"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[22176],{44227:(e,n,s)=>{s.r(n),s.d(n,{default:()=>a});var t=s(74848);const a=[{name:"\u5929\u9e64\u8c31",content:(0,t.jsx)(t.Fragment,{children:"\u5e8f\u5e55 \u3000 \u3000\u3000\u8fd9\u662f\u660e\u6708\u4e4b\u591c\uff0c\u5ce8\u5d4b\u5c71\u91d1\u9876\u5cf0\u4e0a\uff0c\u7aef\u5750\u7740\u4e00\u4e2a\u767d\u9aef\u8fc7\u8179\uff0c\u957f\u7709\u4eba\u9b13\uff0c\u4e00\u88ad\u9752\u886b\u7684\u8001\u4eba\u3002 \u3000\u3000\u91d1\u9876\u5cf0\u50fb\u5904\u4e07\u5cf0\u6df1\u5904\u3001\u5cf0\u8170\u79ef\u96ea\u4e0d\u5316\uff0c\u7ed3\u6210\u4e86\u5343\u5e74\u51b0\u5e26\uff0c\u4e00\u822c\u4eba\u6839\u672c\u65e0\u6cd5\u8d8a\u6e21\u4e00\u7247\u51b0\u5e26\uff0c\u662f\u4ee5\u4eba\u8ff9\u7f55\u81f3\u3002 \u3000\u3000\u7edd\u5cf0\u4e4b\u9876\uff0c\u51b7\u98ce\u51db\u51bd\uff0c\u4f46\u90a3\u8001\u4eba\u53ea\u7a7f\u7740\u4e00\u88ad\u9752\u886b\uff0c\u76d8\u819d\u5750\u5728\u4e00\u5757\u5927\u5ca9\u77f3\u4e0a\u3002 \u3000\u3000\u6708\u534e\u5982\u6c34\uff0c\u7167\u7740\u7edd\u5cf0\u56db\u5468\uff0c\u5343\u5e74\u79ef\u7ed3\u7684\u51b0\u5ca9\uff0c\u95ea\u95ea\u751f\u5149\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u90a3\u9752\u886b\u8001\u4eba\u6323\u52a8\u4e86\u4e00\u4e0b\u8eab\u5b50\u3002\u54cd\u8d77\u4e00\u9635\u6025\u4fc3\u7684\u5598\u54b3\u58f0\uff0c\u5939\u6742\u5728\u76c8\u8033\u677e\u6d9b\u4e4b\u4e2d\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u957f\u957f\u5730\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u538b\u5236\u4e0b\u5f3a\u70c8\u5598\u54b3\u4e4b\u58f0\u3002 \u3000\u3000\u4ed6\u4f3c\u662f\u6781\u5c3d\u80fd\u529b\uff0c\u4f7f\u81ea\u5df1\u4fdd\u6301\u7740\u6f20\u7136\u7684\u5b89\u9759\u3002 \u3000\u3000\u591c\u98ce\u5982\u5200\uff0c\u5439\u98d8\u8d77\u4ed6\u8eab\u4e0a\u7684\u5355\u8584\u7684\u9752\u886b\uff0c\u730e\u730e\u4f5c\u54cd\u3002 \u3000\u3000\u4ed6\u6536\u7f29\u4e86\u4e00\u4e0b\u53cc\u81c2\uff0c\u53cc\u624b\u8f7b\u8f7b\u5730\u4e92\u6413\u4e86\u4e00\u9635\u3002 \u3000\u3000\u4e5f\u8bb8\u5e74\u8fc8\u4e86\uff0c\u679c\u7136\u662f\u6709\u4e9b\u754f\u51b7\uff0c\u4f46\u4ed6\u6781\u529b\u5fcd\u53d7\u3001\u652f\u6491\u7740\u3002 \u3000\u3000\u4ed6\u4e0d\u65f6\u62ac\u8d77\u5934\uff0c\u770b\u7740\u79fb\u5411\u4e2d\u5929\u7684\u660e\u6708\uff0c\u4f3c\u662f\u5728\u76fc\u671b\u7740\u4ec0\u4e48\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u91d1\u9876\u5cf0\u4e0b\uff0c\u4f20\u4e0a\u6765\u4e00\u58f0\u957f\u5578\uff0c\u5212\u7834\u591c\u534a\u7684\u6c89\u5bc2\u3002 \u3000\u3000\u9752\u886b\u5ff5\u4eba\uff0c\u53f3\u624b\u8f7b\u62c2\u4e00\u4e0b\u98d8\u5728\u524d\u80f8\u7684\u767d\u9aef\uff0c\u79fb\u52a8\u4e00\u4e0b\u8eab\u5b50\uff0c\u4f7f\u81ea\u5df1\u5750\u5f97\u66f4\u7aef\u6b63\u4e00\u4e9b\uff0c\u53e3\u4e2d\u5374\u81ea\u8a00\u81ea\u8bed\u7684\u8bf4\u9053\uff1a\u201c\u6765\u4e86\uff0c\u6765\u4e86\uff0c\u5e0c\u671b\u4ed6\u4eec\u90fd\u80fd\u5b88\u7ea6\uff0c\u5168\u6570\u5230\u9f50\u3002\u201d \u3000\u3000\u8fd9\u672c\u662f\u4ed6\u5fc3\u4e2d\u4e4b\u8a00\uff0c\u4f46\u56e0\u76fc\u671b\u5f97\u7126\u6025\uff0c\u5374\u4e0d\u81ea\u89c9\u4ed6\u8bf4\u51fa\u53e3\u6765\u3002 \u3000\u3000\u53ea\u662f\u90a3\u58f0\u97f3\u5f88\u8f7b\u5fae\uff0c\u6709\u5982\u4e00\u7f15\u8f7b\u70df\uff0c\u5728\u591c\u98ce\u4e2d\u5316\u53bb\u3002 \u3000\u3000\u5578\u58f0\u752b\u843d\uff0c\u7d27\u63a5\u7740\u53c8\u54cd\u8d77\u4e00\u9635\u5a49\u8f6c\u7684\u7bab\u58f0\u3002 \u3000\u3000\u90a3\u8427\u58f0\u542c\u6765\u4e0d\u5927\uff0c\u4f46\u5374\u662f\u4e00\u7ebf\u51b2\u5929\uff0c\u4f3c\u662f\u88ab\u4e00\u80a1\u5f3a\u5927\u7684\u529b\u9053\uff0c\u9001\u4e0a\u7edd\u5cf0\u3002 \u3000\u3000\u9752\u886b\u8001\u4eba\u7cbe\u795e\u4e00\u632f\uff0c\u9053\uff1a\u201c\u597d\uff01\u53c8\u4e00\u4e2a\u5982\u7ea6\u8d76\u5230\u3002\u201d \u3000\u3000\u8bed\u58f0\u672a\u843d\uff0c\u53c8\u54cd\u8d77\u4e00\u9635\u91d1\u58f0\u7389\u632f\u822c\u7684\u5927\u7b11\uff0c\u4f20\u5230\u4e86\u7edd\u5cf0\u3002 \u3000\u3000\u9752\u886b\u8001\u4eba\u70b9\u70b9\u5934\uff0c\u8138\u4e0a\u6cdb\u73b0\u8d77\u4e00\u80a1\u51c4\u51c9\u7684\u5fae\u7b11\u3002 \u3000\u3000\u4f46\u95fb\u90a3\u5927\u7b11\u58f0\u4f9d\u7136\u987f\u4f4f\uff0c\u4f59\u97f3\u8885\u8885\uff0c\u6563\u5165\u5929\u9645\u3002 \u3000\u3000\u8fd9\u7b11\u58f0\u6765\u5f97\u5982\u60ca\u96f7\u8fc5\u7535\uff0c\u7a81\u7136\u800c\u6b62\uff0c\u4f46\u6d88\u5931\u5f97\u5374\u662f\u5feb\u901f\u65e0\u6bd4\uff0c\u4f3c\u662f\u4e00\u4e2a\u4eba\u6b63\u5728\u5927\u7b11\u4e4b\u65f6\uff0c\u7a81\u7136\u9047\u4e0a\u4e86\u4ec0\u4e48\u60ca\u5947\u5927\u53d8\uff0c\u987f\u7136\u800c\u4f4f\u3002 \u3000\u3000\u9752\u886b\u8001\u4eba\u5fc3\u4e2d\u6697\u6697\u5730\u5fd6\u9053\uff1a\u201c\u8fd8\u6709\u4e24\u4e2a\uff0c\u5c31\u5230\u9f50\u4e86\u3002\u201d \u3000\u3000\u5fc3\u5ff5\u8f6c\u52a8\u4e4b\u95f4\uff0c\u7a81\u95fb\u4e09\u58f0\u549a\u549a\u549a\u7684\u76ae\u9f13\u58f0\uff0c\u4f20\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u9f13\u58f0\u505c\u4e86\u4e4b\u540e\uff0c\u4e00\u5207\u53c8\u5f52\u6c89\u5bc2\u3002 \u3000\u3000\u9752\u886b\u8001\u4eba\u4fa7\u8033\u9614\u542c\uff0c\u957f\u4e45\u4e4b\u540e\uff0c\u4ecd\u4e0d\u95fb\u6709\u5176\u4ed6\u58f0\u97f3\u4f20\u4e0a\u5cf0\u6765\uff0c\u5fc3\u4e2d\u6697\u6697\u6025\u9053\uff1a\u201c\u600e\u4e48\u5c11\u4e86\u4e00\u4f4d\u5462\uff1f\u201d \u3000\u3000\u5fc3\u4e2d\u7126\u6025\uff0c\u4fc3\u8d77\u75c5\u60c5\u53d1\u4f5c\uff0c\u53c8\u662f\u4e00\u9635\u6025\u4fc3\u7684\u5598\u54b3\u4e4b\u58f0\u3002 \u3000\u3000\u4ed6\u6025\u6025\u5438\u4e00\u53e3\u6c14\uff0c\u628a\u5598\u54b3\u538b\u4e86\u4e0b\u53bb\uff0c\u62c9\u4e00\u4e0b\u88ab\u98ce\u5439\u8d77\u7684\u8863\u89d2\uff0c\u53cc\u624b\u5e73\u653e\u5728\u819d\u524d\uff0c\u8fd0\u8db3\u76ee\u529b\uff0c\u5411\u524d\u770b\u53bb\u3002 \u3000\u3000\u6708\u5149\u4e0b\uff0c\u53ea\u89c1\u90a3\u4e2d\u5e74\u79ef\u7ed3\u7684\u51b0\u5ca9\u5cf0\u8fb9\uff0c\u540c\u65f6\u51fa\u73b0\u4e86\u56db\u6761\u4eba\u5f71\u3002 \u3000\u3000\u9752\u886b\u8001\u4eba\u6697\u81ea\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u538b\u5165\u4e39\u7530\uff0c\u7f13\u7f13\u95ed\u4e0a\u4e86\u53cc\u76ee\u3002 \u3000\u3000\u663e\u7136\uff0c\u4ed6\u60f3\u4f7f\u81ea\u5df1\u5c3d\u91cf\u5730\u9547\u9759\uff0c\u9547\u9759\u5f97\u65e0\u7779\u90a3\u56db\u6761\u8054\u8882\u800c\u6765\u7684\u4eba\u5f71\u3002 \u3000\u3000\u56db\u6761\u4eba\u5f71\uff0c\u5404\u4e0d\u76f8\u8ba9\uff0c\u540c\u65f6\u4ee5\u6781\u5feb\u7684\u901f\u5ea6\uff0c\u5411\u90a3\u9752\u886b\u4eba\u76d8\u5750\u7684\u5de8\u5ca9\u4e4b\u4e0a\u51b2\u6765\u3002 \u3000\u3000\u51e0\u4e4e\u662f\u540c\u4e00\u65f6\u95f4\uff0c\u56db\u4eba\u4e00\u9f50\u5954\u5230\u90a3\u5927\u5ca9\u77f3\u4e4b\u4e0b\u3002 \u3000\u3000\u4ed6\u4eec\u76f8\u4e92\u671b\u4e86\u4e00\u773c\uff0c\u76ee\u5149\u53c8\u8f6c\u5230\u90a3\u9752\u886b\u8001\u4eba\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u6708\u5149\u4e0b\uff0c\u53ea\u89c1\u9752\u886b\u8001\u4eba\u6cd5\u76f8\u5e84\u91cd\uff0c\u9690\u9690\u95f4\u6709\u4e00\u526f\u60b2\u5929\u601c\u4eba\u7684\u6c14\u5ea6\u3002 \u3000\u3000\u56db\u4e2a\u4eba\u516b\u9053\u51b7\u7535\u4e00\u822c\u7684\u76ee\u5149\uff0c\u4e00\u548c\u9752\u886b\u8001\u4eba\u7684\u6cd5\u76f8\u63a5\u89e6\uff0c\u7acb\u65f6\u6536\u56de\u4e86\u90a3\u72c2\u50b2\u4e0d\u53ef\u4e00\u4e16\u7684\u6c14\u7130\uff0c\u9f50\u9f50\u62b1\u62f3\u4f5c\u793c\u3002 \u3000\u3000\u9752\u886b\u8001\u4eba\u7741\u5f00\u53cc\u76ee\uff0c\u6253\u91cf\u4e86\u56db\u4eba\u4e00\u773c\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u54b1\u4f4d\u53c8\u5341\u5e74\u6ca1\u89c1\u9762\u4e86\uff0c\u56db\u4f4d\u4ecd\u7136\u548c\u6614\u5e74\u4e00\u822c\u7684\u5065\u58ee\u5462\u3002\u201d \u3000\u3000\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u76ee\u5149\u5374\u6162\u6162\u8f6c\u52a8\uff0c\u7531\u5de6\u81f3\u53f3\uff0c\u6253\u91cf\u4e86\u56db\u4eba\u4e00\u773c\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u6700\u5de6\u4e00\u4eba\uff0c\u5e74\u7ea6\u4e94\u65ec\uff0c\u5934\u6234\u65b9\u4e2d\uff0c\u9ed1\u4e1d\u5782\u80f8\uff0c\u767d\u9762\u6d53\u7709\uff0c\u6709\u5982\u4e00\u4e2a\u843d\u62d3\u6c5f\u6e56\u7684\u6587\u58eb\u3002 \u3000\u3000\u7b2c\u4e8c\u4e2a\u662f\u4e2d\u5e74\u7f8e\u5987\uff0c\u53f3\u624b\u63d0\u7740\u4e00\u628a\u7389\u7bab\uff0c\u6708\u5149\u4e0b\uff0c\u53ea\u89c1\u5979\u51e4\u7709\u5e26\u715e\uff0c\u674f\u773c\u95ea\u70c1\uff0c\u5634\u89d2\u4e0a\u7fd8\uff0c\u4e00\u526f\u4ee4\u4eba\u83ab\u53ef\u9884\u6d4b\u7684\u5028\u50b2\u4e4b\u6c14\u3002 \u3000\u3000\u7b2c\u4e09\u4eba\u4e94\u77ed\u8eab\u6750\uff0c\u5927\u5934\u73af\u76ee\uff0c\u4e00\u5f20\u5634\u7279\u522b\u9614\u5927\uff0c\u51e0\u4e4e\u5360\u4e86\u4ed6\u534a\u4e2a\u9762\u5b54\u3002 \u3000\u3000\u7b2c\u56db\u4eba\u8eab\u6750\u7ec6\u9ad8\u3002\u4e00\u88ad\u9ed1\u886b\uff0c\u5de6\u624b\u63d0\u7740\u4e00\u5f20\u76f4\u5f84\u76c8\u5c3a\u7684\u76ae\u9f13\uff0c\u53f3\u624b\u63e1\u7740\u4e00\u4e2a\u91d1\u9ec4\u8272\u7684\u9f13\u9524\u3002 \u3000\u3000\u56db\u4eba\u9f50\u9f50\u6b20\u8eab\u5e94\u9053\uff1a\u201c\u6211\u7b49\u6765\u5411\u9648\u5927\u4fa0\u8bf7\u5b89\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u56db\u4f4d\u60f3\u4e0d\u5230\u8001\u592b\u7adf\u7136\u53c8\u6d3b\u4e86\u5341\u5e74\u3002\u201d \u3000\u3000\u90a3\u5de6\u9996\u6587\u58eb\u9053\uff1a\u201c\u9648\u5927\u4fa0\u529f\u53c2\u9020\u5316\uff0c\u518d\u6d3b\u4e00\u767e\u5e74\u4e5f\u4e0d\u4e3a\u602a\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u7f13\u7f13\u5730\u4e3e\u8d77\u53f3\u624b\uff0c\u4e00\u62c2\u957f\u9aef\uff0c\u7b11\u9053\uff1a\u201c\u8001\u673d\u5982\u82e5\u771f\u80fd\u518d\u6d3b\u4e00\u767e\u5e74\uff0c\u56db\u4f4d\u53ea\u6015\u7b49\u4e0d\u53ca\u8001\u673d\u6b7b\u53bb\u4e86\u3002\u201d \u3000\u3000\u53ea\u542c\u90a3\u4e2d\u5e74\u7f8e\u5987\u67d4\u58f0\u7ec6\u8bed\u7684\u63a5\u9053\uff1a\u201c\u9648\u5927\u5939\u6b66\u529f\u7edd\u4f26\uff0c\u5df2\u662f\u91d1\u521a\u4e0d\u574f\u4e4b\u9aa8\uff0c\u54b1\u4eec\u867d\u7136\u662f\u5e74\u5c11\u51e0\u5c81\uff0c\u53ea\u6015\u4e5f\u8981\u6b7b\u5728\u4f60\u7684\u524d\u9762\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\u554a\uff01\u4f46\u613f\u4f60\u4e0d\u5e78\u8a00\u4e2d\u3002\u201d \u3000\u3000\u90a3\u4e94\u77ed\u8eab\u6750\uff0c\u5927\u5934\u73af\u76ee\u5634\u5df4\u7279\u522b\u9614\u5927\u4e4b\u4eba\uff0c\u8bf4\u9053\uff1a\u201c\u5144\u5f1f\u4e00\u5411\u662f\u5fc3\u76f4\u53e3\u5feb\uff0c\u6709\u51e0\u53e5\u8bdd\u8bf4\u51fa\u6765\uff0c\u5e0c\u671b\u4f60\u9648\u5927\u4fa0\u4e0d\u8981\u751f\u6c14\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u70b9\u70b9\u5934\uff0c\u795e\u8272\u4e25\u8083\u4ed6\u8bf4\u9053\uff1a\u201c\u597d\uff01\u4f60\u8bf4\u5427\uff01\u201d \u3000\u3000\u5927\u5634\u77ee\u5b50\u9053\uff1a\u201c\u5144\u5f1f\u542c\u8bf4\uff0c\u4f60\u8001\u60a3\u5f97\u4e00\u79cd\u602a\u75c5\uff0c\u5df2\u7f20\u7ef5\u75c5\u69bb\u6570\u5e74\u4e4b\u4e45\uff0c\u4e0d\u77e5\u662f\u771f\u662f\u5047\uff1f\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8001\u592b\u60a3\u75c5\u4e00\u4e8b\uff0c\u4f60\u662f\u542c\u4f55\u4eba\u6240\u8a00\uff1f\u201d \u3000\u3000\u5927\u5634\u77ee\u5b50\u63a5\u9053\uff1a\u201c\u5899\u65e0\u767e\u65e5\u4e0d\u900f\u98ce\uff0c\u4f60\u9648\u5927\u4fa0\u751f\u75c5\u7684\u4e8b\uff0c\u867d\u7136\u6781\u5c3d\u5c0f\u5fc3\u4fdd\u5bc6\uff0c\u4f46\u4ecd\u7136\u88ab\u533a\u533a\u63a2\u5f97\u2026\u2026\u201d\u8a00\u6bd5\uff0c\u7eb5\u5757\u5927\u7b11\u8d77\u6765\u3002 \u3000\u3000\u9752\u886b\u8001\u4eba\u8138\u8272\u4e00\u6c89\uff0c\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u60f3\u8001\u4eba\u4e00\u75c5\u4e0d\u8d77\u5417\uff1f\u201d \u3000\u3000\u5927\u5634\u77ee\u5b50\u6025\u6025\u8bf4\u9053\uff1a\u201c\u9648\u5927\u4fa0\u8a00\u91cd\u4e86\uff0c\u533a\u533a\u5fc3\u4e2d\u5b9e\u65e0\u6076\u610f\uff0c\u795d\u4f60\u8001\u5bff\u6bd4\u5357\u5c71\uff0c\u4e07\u5e74\u957f\u9752\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u76ee\u5149\u8f6c\u5230\u90a3\u4e00\u8eab\u9ed1\u886b\u3001\u8eab\u6750\u7ec6\u9ad8\u7684\u5927\u6c49\u8eab\u4e0a\uff0c\u95ee\u9053\uff1a\u201c\u5341\u5e74\u4ee5\u6765\uff0c\u4f60\u53c8\u6740\u4e86\u597d\u591a\u4eba\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u6b20\u8eab\u5e94\u9053\uff1a\u201c\u4f60\u9648\u5927\u4fa0\u4e0d\u6b7b\uff0c\u5728\u4e0b\u600e\u6562\u5984\u5f00\u6740\u6212\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u9053\uff1a\u201c\u6211\u6b7b\u4e86\u4e4b\u540e\u5462\uff1f\u201d \u3000\u3000\u9ed1\u591c\u4eba\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u5c31\u5f88\u96be\u8bf4\u4e86\u2026\u2026\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u8138\u8272\u4e00\u6574\uff0c\u63a5\u9053\uff1a\u201c\u8fd9\u4e48\u770b\u8d77\u6765\uff0c\u8001\u592b\u5728\u5916\u4e4b\u524d\uff0c\u5e94\u8be5\u5148\u6740\u4e86\u4f60\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9a87\u7136\u9000\u540e\u4e00\u6b65\uff0c\u9053\uff1a\u201c\u9648\u5927\u4fa0\u65e2\u662f\u4e0d\u8981\u5728\u4e0b\u6740\u4eba\u3001\u4ee5\u540e\u4e0d\u6740\u5c31\u662f\uff0c\u4f60\u8001\u522b\u751f\u6c14\uff0c\u6c14\u574f\u8eab\u5b50\u5728\u4e0b\u53ef\u662f\u7f6a\u8be5\u4e07\u6b7b\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u62ac\u5934\u671b\u671b\u5f53\u7a7a\u7693\u6708\uff0c\u6697\u6697\u5730\u53f9\u606f\u4e00\u58f0\u9053\uff1a\u201c\u54b1\u4eec\u8fd9\u6b21\u7ea6\u4f1a\uff0c\u4f3c\u662f\u5c11\u4e86\u4e00\u4e2a\u4eba\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u56db\u4eba\u9f50\u9f50\u5e94\u58f0\u9053\uff1a\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u76ee\u5149\u8f6c\u52a8\u3001\u626b\u63a0\u4e86\u56db\u4eba\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5929\u9b54\u5973\u4e3a\u4f55\u4e0d\u6765\uff1f\u201d \u3000\u3000\u56db\u4eba\u9762\u9762\u76f8\u89c6\uff0c\u7b54\u4e0d\u4e0a\u6d3b\u3002 \u3000\u3000\u9752\u886b\u8001\u4eba\u7709\u5934\u4e00\u76b1\uff0c\u6295\u6ce8\u5728\u90a3\u4e2d\u5e74\u7f8e\u5987\u7684\u8138\u4e0a\u9053\uff1a\u201c\u4f60\u548c\u5979\u76f8\u5904\u5f97\u6700\u597d\uff0c\u5f53\u77e5\u5979\u4e3a\u4f55\u4e0d\u6765\uff1f\u201d \u3000\u3000\u4e2d\u5e74\u7f8e\u5987\u9053\uff1a\u201c\u5979\u8bf4\uff0c\u5979\u8bf4\u4f60\u2026\u2026\u4f60\u2026\u2026\u201d \u3000\u3000\u5979\u4f3c\u4e4e\u662f\u5fc3\u6709\u6240\u754f\uff0c\u4f60\u4e86\u534a\u5929\uff0c\u4f60\u4e0d\u51fa\u4e2a\u6240\u4ee5\u7136\u6765\u3002 \u3000\u3000\u9752\u886b\u8001\u4eba\u9053\uff1a\u201c\u5929\u9b54\u5973\u9a82\u4e86\u8001\u592b\uff0c\u90a3\u4e5f\u662f\u4e0e\u4f60\u65e0\u5e72\uff0c\u4f60\u5c3d\u7ba1\u76f4\u8bf4\u5c31\u662f\u3002\u201d \u3000\u3000\u4e2d\u5e74\u7f8e\u5987\u9053\uff1a\u201c\u90a3\u5929\u9b54\u5973\u8bf4\uff0c\u4f60\u75c5\u5165\u818f\u80b2\uff0c\u5fc5\u6b7b\u65e0\u7591\uff0c\u4e0d\u7528\u6765\u6b64\u8d74\u7ea6\u4e86\u3002\u201d \u3000\u3000\u8a00\u7f62\uff0c\u53cc\u76ee\u51dd\u795e\uff0c\u76ef\u6ce8\u5728\u90a3\u9752\u8863\u8001\u4eba\u7684\u8eab\u4e0a\uff0c\u795e\u60c5\u4e00\u7247\u60ca\u4ff1\uff0c\u751f\u6050\u90a3\u9752\u7384\u8001\u4eba\u7a81\u7136\u51fa\u624b\uff0c\u53d6\u53bb\u4e86\u81ea\u5df1\u7684\u6027\u547d\u3002 \u3000\u3000\u53ea\u89c1\u9752\u886b\u8001\u8005\u8c08\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u51fa\u4e86\u5979\u610f\u6599\u4e4b\u5916\u2026\u2026\u201d\u76ee\u5149\u626b\u63a0\u4e86\u56db\u4eba\u4e00\u773c\uff0c\u9053\uff1a \u3000\u3000\u201c\u56db\u4f4d\u4f55\u4ee5\u548c\u90a3\u5929\u9b54\u5973\u770b\u6cd5\u4e0d\u540c\u5462\uff1f\u201d \u3000\u3000\u53ea\u542c\u90a3\u5934\u6234\u65b9\u4e2d\u7684\u4e2d\u5e74\u6587\u58eb\u5e94\u9053\uff1a\u201c\u4eba\u4eba\u667a\u6167\u4e0d\u540c\uff0c\u65ad\u4e8b\u6709\u5f02\uff0c\u81ea\u7136\u662f\u7ed3\u679c\u5c31\u4e0d\u540c\u4e86\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u8005\u6de1\u7136\u6ca1\u9053\uff1a\u201c\u4f60\u517c\u901a\u533b\u9053\uff0c\u770b\u8001\u592b\u75c5\u52bf\u5982\u4f55\u5462\uff1f\u201d \u3000\u3000\u4e2d\u5e74\u6587\u58eb\u9053\uff1a\u201c\u9648\u5927\u4fa0\uff0c\u4e0d\u80fd\u4ee5\u5e38\u60c5\u8bba\u65ad\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u8005\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e2d\u5e74\u6587\u58eb\u9053\uff1a\u201c\u636e\u5728\u4e0b\u5f97\u5230\u7684\u6d88\u606f\uff0c\u9648\u5927\u4fa0\u7684\u75c5\u60c5\u751a\u91cd\uff0c\u5982\u662f\u5176\u4ed6\u4eba\u5f97\u6b64\u6076\u75be\uff0c\u53ea\u6015\u96be\u4ee5\u6d3b\u8fc7\u4e00\u5e74\uff0c\u4f46\u4f60\u9648\u5927\u4fa0\u5185\u529f\u7cbe\u7eaf\uff0c\u5df2\u5230\u7089\u706b\u7eaf\u9752\u4e4b\u5883\uff0c\u4e5f\u8bb8\u80fd\u514b\u670d\u4e25\u75be\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8bf4\u5f97\u597d\uff0c\u65e0\u602a\u4eba\u79f0\u4f60\u6076\u5b54\u660e\u548c\u6bd2\u5927\u592b\u3002\u201d \u3000\u3000\u4e2d\u5e74\u6587\u58eb\u9053\uff1a\u201c\u597d\u8bf4\uff0c\u597d\u8bf4\uff0c\u90a3\u662f\u6c5f\u6e56\u4e0a\u670b\u53cb\u4eec\u62ac\u4e3e\u5728\u4e0b\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u8005\u957f\u7709\u4e00\u626c\uff0c\u4f3c\u60f3\u53d1\u4f5c\uff0c\u4f46\u4ed6\u53c8\u5f3a\u81ea\u5fcd\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u6076\u5b54\u660e\u5fae\u5fae\u4e00\u7b11\uff0c\u5927\u6709\u5f97\u610f\u4e4b\u72b6\u3002 \u3000\u3000\u9752\u886b\u8001\u4eba\u51b7\u6f20\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u518d\u4ed4\u7ec6\u77a7\u77a7\u8001\u592b\u3002\u201d \u3000\u3000\u6076\u5b54\u660e\u9053\uff1a\u201c\u5982\u82e5\u9648\u5927\u4fa0\u80fd\u591f\u4fe1\u5f97\u8fc7\u5728\u4e0b\uff0c\u8ba9\u5728\u4e0b\u628a\u4e00\u628a\u4f60\u9648\u5927\u4fa0\u7684\u8109\u7a74\uff0c\u5728\u4e0b\u6216\u8bb8\u53ef\u4f5c\u4e00\u6b66\u65ad\u4e4b\u8a00\u3002\u201d \u3000\u3000\u9752\u8863\u8001\u4eba\u7f13\u7f13\u4f38\u51fa\u5de6\u624b\uff0c\u9053\uff1a\u201c\u597d\uff01\u7ed9\u4f60\u77a7\u77a7\u5427\uff01\u201d \u3000\u3000\u8a00\u7f62\uff0c\u95ed\u4e0a\u53cc\u76ee\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u7edd\u5cf0\u4e4b\u4e0a\uff0c\u5145\u6ee1\u4e86\u4e00\u7247\u7d27\u5f20\uff0c\u53e6\u5916\u4e09\u4eba\uff0c\u516d\u9053\u773c\u795e\uff0c\u4e00\u9f50\u6295\u6ce8\u5728\u90a3\u6076\u5b54\u660e\u8138\u4e0a\u3002 \u3000\u3000\u660e\u6708\u4e4b\u4e0b\uff0c\u53ea\u89c1\u90a3\u6076\u5b54\u660e\u9876\u95e8\u4e0a\u70ed\u6c14\u84b8\u817e\uff0c\u4e00\u9897\u9897\u6c57\u73e0\u513f\uff0c\u6d41\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u90a3\u4e2d\u5e74\u7f8e\u5987\u4e3e\u624b\u7406\u4e00\u4e0b\u5c71\u98ce\u5439\u8d77\u7684\u6563\u53d1\uff0c\u7f13\u7f13\u4ed6\u8bf4\u9053\uff1a\u201c\u5982\u662f\u9648\u5927\u4fa0\u75c5\u52bf\u6c89\u91cd\uff0c\u9700\u8981\u4ec0\u4e48\u836f\u7269\u54b1\u4eec\u90fd\u5e94\u8be5\u5c3d\u529b\u4e3a\u4ed6\u91c7\u96c6\u3002\u201d \u3000\u3000\u6076\u5b54\u660e\u56de\u987e\u4e86\u90a3\u4e2d\u5e74\u7f8e\u5987\u4e00\u773c\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u9648\u5927\u4fa0\u5012\u9700\u4e00\u79cd\u836f\u7269\uff0c\u53ea\u6015\u4f60\u67f3\u5a9a\u5a18\u4e0d\u80af\u62ff\u51fa\u6765\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u626c\u4e86\u626c\u624b\u4e2d\u7389\u7bab\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u836f\u7269\u554a\uff1f\u4f46\u6211\u529b\u80fd\u6240\u53ca\uff0c\u65e0\u4e0d\u5c3d\u529b\u53bb\u627e\u3002\u201d \u3000\u3000\u6076\u5b54\u660e\u9053\uff1a\u201c\u4e0d\u7528\u627e\u4e86\uff0c\u90a3\u836f\u7269\u5c31\u5728\u4f60\u8eab\u4e0a\u5e26\u7740\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u5fae\u5fae\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u6076\u5b54\u660e\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u592a\u767d\u51b0\u6f6d\u4e2d\u90a3\u5c3e\u5343\u5e74\u91d1\u9ca4\u80c6\uff0c\u4e0d\u662f\u5df2\u7ecf\u5728\u4f60\u624b\u4e0a\u4e86\u5417\uff1f\u9648\u5927\u4fa0\u75c5\u52bf\u867d\u91cd\uff0c\u53ea\u8981\u4f60\u5949\u4e0a\u90a3\u91d1\u9ca4\u80c6\uff0c\u7acb\u65f6\u53ef\u4f7f\u9648\u5927\u4fa0\u6c89\u75b4\u5c3d\u9664\u2026\u2026\u201d \u3000\u3000\u53ea\u542c\u90a3\u5927\u5634\u4eba\u548c\u9ed1\u8863\u4eba\u540c\u65f6\u566b\u4e86\u4e00\u58f0\uff0c\u56db\u9053\u76ee\u5149\u4e00\u8d77\u8f6c\u6ce8\u5728\u67f3\u5a9a\u5a18\u7684\u8138\u4e0a\uff0c\u9053\uff1a \u3000\u3000\u201c\u5f53\u771f\u5417\uff1f\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u9053\uff1a\u201c\u542c\u4ed6\u80e1\u8bf4\uff01\u201d \u3000\u3000\u6076\u5b54\u660e\u9053\uff1a\u201c\u90a3\u91d1\u9ca4\u80c6\u4e3a\u4f60\u6240\u53d6\uff0c\u6211\u600e\u662f\u80e1\u8bf4\uff01\u201d \u3000\u3000\u90a3\u5927\u5634\u4eba\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u679c\u771f\u5982\u6b64\uff0c\u54b1\u4eec\u8981\u5411\u59d1\u5a18\u8ba8\u4e0a\u4e00\u676f\u7fb9\u4e86\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u9053\uff1a\u201c\u6076\u5b54\u660e\u5978\u8bc8\u767e\u51fa\uff0c\u4e13\u4f1a\u6311\u62e8\u522b\u4eba\u76f8\u535a\uff0c\u4ed6\u597d\u5750\u6536\u6e14\u4eba\u4e4b\u5229\uff0c\u4e24\u4f4d\u600e\u80fd\u4fe1\u4ed6\u4e4b\u8a00\u5462\u201d \u3000\u3000\u90a3\u9ed1\u8863\u4eba\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u6b64\u4e8b\u592a\u8fc7\u91cd\u5927\uff0c\u54b1\u4eec\u5b81\u53ef\u88ab\u4ed6\u9a97\u4e86\uff0c\u4e5f\u5f97\u76f8\u4fe1\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u4eca\u5bb5\u6765\u6b64\uff0c\u662f\u63a2\u671b\u9648\u5927\u4fa0\u800c\u6765\u7684\uff0c\u4e24\u4f4d\u5982\u662f\u770b\u6211\u67f3\u5a9a\u5a18\u4e0d\u987a\u773c\uff0c\u4e0d\u59a8\u7ea6\u671f\u76f8\u4f1a\uff0c\u627e\u4e2a\u5730\u65b9\u597d\u597d\u5730\u6253\u4e0a\u4e00\u67b6\u3002\u201d \u3000\u3000\u5927\u5634\u4eba\u4ef0\u5929\u5927\u7b11\uff0c\u9053\uff1a\u201c\u7ea6\u671f\u76f8\u4f1a\uff0c\u597d\u4f7f\u4f60\u67f3\u59d1\u5a18\u4ece\u4ece\u5bb9\u5bb9\u5730\u85cf\u597d\u90a3\u91d1\u9ca4\u80c6\uff0c\u5929\u4e0b\u5982\u6b64\u8fbd\u9614\uff0c\u90a3\u91d1\u9ca4\u80c6\u53c8\u662f\u6781\u4e3a\u5fae\u5c0f\u4e4b\u7269\uff0c\u5982\u662f\u88ab\u4f60\u85cf\u8d77\uff0c\u54b1\u4eec\u53ef\u5c31\u96be\u4ee5\u5bfb\u627e\u4e86\u3002\u201d \u3000\u3000\u90a3\u9ed1\u8863\u4eba\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u4ece\u6b64\u65f6\u8d77\uff0c\u54b1\u4eec\u8ddf\u5b9a\u4e86\u4f60\u67f3\u5a9a\u5a18\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\u554a\uff01\u5982\u82e5\u4e24\u4f4d\u6709\u5174\u8ffd\u968f\u5c0f\u59b9\uff0c\u5c0f\u59b9\u662f\u6b22\u8fce\u81f3\u6781\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u6b64\u65f6\u6b64\u523b\uff0c\u5c0f\u59b9\u7684\u4e8b\uff0c\u4f3c\u662f\u6ca1\u6709\u9648\u5927\u4fa0\u7684\u75c5\u52bf\u91cd\u8981\u3002\u201d \u3000\u3000\u90a3\u9ed1\u8863\u4eba\u8f7b\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e0d\u9519\u54b1\u4eec\u5e94\u6cd5\u5148\u770b\u770b\u9648\u5927\u4fa0\u7684\u75c5\u60c5\u3002\u201d \u3000\u3000\u6076\u5b54\u660e\u6296\u52a8\u53f3\u6307\uff0c\u7f13\u7f13\u7684\u642d\u5728\u90a3\u9752\u886b\u8001\u4eba\u7684\u8109\u7a74\u4e4b\u4e0a\u3002\u67f3\u5a9a\u5a18\u3001\u5927\u5634\u4eba\uff0c\u548c\u9ed1\u8863\u7684\u5927\u6c49\uff0c\u516d\u9053\u773c\u5149\uff0c\u4e00\u9f50\u6295\u6ce8\u8fc7\u6765\uff0c\u773c\u5149\u4e2d\u6ee1\u662f\u671f\u671b\u4e4b\u8272\uff0c\u5e0c\u671b\u6076\u5b54\u660e\u80fd\u591f\u501f\u628a\u8109\u7684\u673a\u4f1a\uff0c\u4e00\u4e3e\u95f4\uff0c\u80fd\u6263\u4f4f\u90a3\u9752\u886b\u8001\u4eba\u7684\u7a74\u8109\u3002 \u3000\u3000\u4f46\u90a3\u6076\u5b54\u660e\uff0c\u53ea\u6709\u4e09\u4e2a\u6307\u5934\uff0c\u642d\u5728\u90a3\u9752\u886b\u8001\u4eba\u7684\u8109\u7a74\u4e4b\u4e0a\uff0c\u5782\u9996\u95ed\u76ee\uff0c\u4f3c\u662f\u5f53\u771f\u96c6\u4e2d\u5168\u90e8\u7cbe\u795e\u67e5\u770b\u90a3\u9752\u886b\u8001\u4eba\u7684\u75c5\u52bf\uff0c\u6839\u672c\u4e0d\u77a7\u4e09\u4eba\u4e00\u773c\u3002 \u3000\u3000\u67f3\u5a9a\u5a18\u4e3e\u624b\u7406\u4e00\u4e0b\u9b13\u8fb9\u6563\u53d1\uff0c\u671b\u4e86\u90a3\u5927\u5634\u4eba\u548c\u9ed1\u8863\u4eba\u4e00\u773c\uff0c\u8f7b\u8f7b\u5401\u4e86\u4e00\u53e3\u6c14\u3002 \u3000\u3000\u516d\u76ee\u4ea4\u6295\uff0c\u4e09\u4e2a\u4eba\u540c\u65f6\u6cdb\u8d77\u4e86\u4e00\u8138\u5931\u671b\u795e\u60c5\u3002 \u3000\u3000\u663e\u7136\uff0c\u4e09\u4eba\u5bf9\u90a3\u6076\u5b54\u660e\u4e0d\u80af\u501f\u673a\u6263\u62ff\u9752\u886b\u8001\u4eba\u8109\u7a74\u4e00\u4e8b\uff0c\u5927\u611f\u5931\u671b\u3002 \u3000\u3000\u7edd\u5cf0\u4e0a\u9e26\u96c0\u65e0\u58f0\uff0c\u8fde\u90a3\u547c\u5578\u5c71\u98ce\uff0c\u4e5f\u7a81\u7136\u9759\u6b62\u4e0b\u6765\uff0c\u9759\u5f97\u53ef\u95fb\u82b1\u9488\u843d\u5730\u4e4b\u58f0\u3002 \u3000\u3000\u8db3\u8db3\u8fc7\u4e86\u6709\u4e00\u4e9b\u70ed\u8336\u5de5\u592b\u4e4b\u4e45\uff0c\u90a3\u6076\u5b54\u660e\u7f13\u7f13\u79fb\u5f00\u4e86\u6309\u5728\u9752\u886b\u8001\u4eba\u5728\u8155\u8109\u95e8\u4e0a\u7684\u53f3\u624b\uff0c\u6b20\u8eab\u5411\u540e\u9000\u4e86\u4e24\u6b65\uff0c\u624d\u7741\u5f00\u53cc\u76ee\uff0c\u6ee1\u8138\u60ca\u6115\u4e4b\u8272\uff0c\u671b\u671b\u90a3\u9752\u886b\u8001\u4eba\u3002 \u3000\u3000\u90a3\u9752\u886b\u8001\u4eba\u7f13\u7f13\u6536\u56de\u653e\u5728\u5de8\u5ca9\u4e0a\u7684\u5de6\u8155\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6bd2\u5927\u592b\uff0c\u8001\u592b\u7684\u75c5\u60c5\u5982\u4f55\uff1f\u201d \u3000\u3000\u6076\u5b54\u660e\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u9648\u5927\u4fa0\u5df2\u5b8c\u5168\u5eb7\u590d\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4e00\u53e5\u8bdd\uff0c\u5b57\u5b57\u5982\u94c1\u9524\u51fb\u5ca9\u4e00\u822c\uff0c\u4f7f\u4e09\u4eba\u65e0\u6bd4\u9707\u9a87\u3002 \u3000\u3000\u9752\u886b\u8001\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u77a7\u9519\u5417\uff1f\u8001\u592b\u7684\u75c5\u52bf\u5f88\u6c89\u91cd\u554a\uff01\u201d \u3000\u3000\u6bd2\u5927\u592b\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u6ca1\u6709\u77a7\u9519\uff0c\u5728\u4e0b\u81ea\u4fe1\u533b\u9053\u8fd8\u4e0d\u81f4\u5dee\u5230\u628a\u6709\u75c5\u77a7\u6210\u65e0\u75c5\u3002\u201d \u3000\u3000\u5927\u5634\u4eba\u4e00\u54a7\u5634\uff0c\u9732\u51fa\u4e86\u5947\u5927\u7684\u68ee\u68ee\u767d\u7259\uff0c\u9053\uff1a\u201c\u5144\u5f1f\u4e0d\u4f1a\u770b\u75c5\uff0c\u4f46\u770b\u90a3\u9648\u5927\u4fa0\u7684\u795e\u60c5\uff0c\u4e5f\u4e0d\u4f3c\u65e0\u75c5\u4e4b\u4eba\u554a\uff01\u201d \u3000\u3000\u6bd2\u5927\u592b\u6012\u9053\uff1a\u201c\u9648\u5927\u4fa0\u8109\u606f\u5747\u5300\uff0c\u6c14\u8840\u7545\u901a\uff0c\u54ea\u91cc\u6709\u4ec0\u4e48\u75c5\u4e86\uff0c\u4f60\u5982\u4e0d\u4fe1\uff0c\u81ea\u5df1\u4e0a\u53bb\u8bd5\u8bd5\uff01\u201d \u3000\u3000\u5927\u5634\u4eba\u91cd\u91cd\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u81ea\u7136\uff0c\u5728\u4e0b\u6781\u613f\u9648\u5927\u4fa0\u957f\u547d\u4e07\u5e74\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u51b7\u7b11\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u7ae5\u5b50\u5947\uff0c\u4f60\u8fd9\u8bdd\u53ef\u662f\u7531\u8877\u4e4b\u8a00\u5417\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u6253\u4e86\u4e00\u4e2a\u51b7\u98a4\uff0c\u4e0d\u7531\u81ea\u4e3b\u5730\u9000\u4e86\u4e24\u6b65\uff0c\u9053\uff1a\u201c\u5144\u5f1f\u662f\u5b57\u5b57\u51fa\u81ea\u80ba\u8151\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u8138\u8272\u4e00\u6574\uff0c\u76ee\u5149\u8f6c\u6ce8\u5230\u67f3\u5a9a\u5a18\u7684\u8138\u4e0a\uff0c\u9053\uff1a\u201c\u4f60\u8fc7\u6765\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u5a07\u8273\u7684\u7c89\u8138\u4e0a\uff0c\u767b\u65f6\u6cdb\u8d77\u4e00\u7247\u60e8\u767d\uff0c\u653e\u4e0b\u7389\u7bab\uff0c\u5782\u7740\u53cc\u624b\uff0c\u6162\u6b65\u884c\u4e86\u8fc7\u53bb\uff0c\u9053\uff1a\u201c\u9648\u5927\u4fa0\u6709\u4f55\u5429\u5490\uff1f\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u51b7\u8083\u5730\u9053\uff1a\u201c\u8fd9\u5341\u5e74\u6765\uff0c\u4f60\u505a\u4e86\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u67d4\u961f\u8bf4\u9053\uff1a\u201c\u8d31\u59be\u8fd9\u5341\u5e74\u6765\uff0c\u4e00\u76f4\u6d01\u8eab\u81ea\u4fee\uff0c\u672a\u4f5c\u8fc7\u4e00\u4ef6\u6709\u80cc\u7ea6\u8a00\u7684\u4e8b\u2026\u2026\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u80e1\u8bf4\uff01\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u5012\u9000\u4e86\u4e24\u6b65\uff0c\u7ed3\u7ed3\u5df4\u5df4\u4ed6\u8bf4\u9053\uff1a\u201c\u6211\u53ea\u6740\u4e86\u4e00\u4e2a\u4eba\uff0c\u8fd8\u662f\u90a3\u4eba\u5bf9\u6211\u5fc3\u6000\u4e0d\u8f68\uff0c\u8c03\u620f\u4e8e\u6211\uff0c\u624d\u6fc0\u8d77\u6211\u7684\u6740\u673a\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u9053\uff1a\u201c\u90fd\u662f\u5b9e\u8bdd\u5417\uff1f\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u9053\uff1a\u201c\u5b57\u5b57\u771f\u5b9e\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u4e3e\u624b\u4e00\u6325\uff0c\u9053\uff1a\u201c\u4f60\u9000\u4e0b\u53bb\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u5e94\u4e86\u4e00\u58f0\uff0c\u7f13\u6b65\u9000\u56de\u539f\u4f4d\u3002 \u3000\u3000\u9752\u886b\u8001\u4eba\u4ef0\u8138\u5851\u72c2\u5929\u4e0a\u660e\u6708\uff0c\u9053\uff1a\u201c\u5929\u4e0d\u5982\u4f60\u4eec\u6240\u613f\uff0c\u8001\u592b\u7adf\u7136\u4ece\u75c5\u9b54\u4e2d\u89e3\u8131\u51fa\u6765\uff0c\u770b\u6765\uff0c\u8fd8\u6709\u51e0\u5e74\u597d\u6d3b\u4e86\u3002\u201d \u3000\u3000\u56db\u4e2a\u4eba\u9f50\u9f50\u5723\u9996\u5e94\u9053\uff1a\u201c\u9648\u5927\u4fa0\u884c\u4ec1\u4e3a\u5584\uff0c\u5929\u4e0b\u4ef0\uff0c\u591a\u6d3b\u51e0\u5e74\uff0c\u90a3\u662f\u5929\u4e0b\u6b66\u6797\u4e4b\u798f\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u8bba\u4f60\u4eec\u8868\u9762\u4e0a\u3001\u795e\u6001\u95f4\u5982\u4f55\u7684\u8654\u8bda\uff0c\u4f46\u6211\u77e5\u9053\u4f60\u4eec\u8bf4\u7684\u90fd\u662f\u8fdd\u5fc3\u4e4b\u8a00\u3002\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u8bba\u4f60\u4eec\u5185\u5fc3\u4e2d\u60f3\u4e9b\u4ec0\u4e48\uff1f\u4f46\u4f60\u4eec\u90fd\u8fd8\u8bb0\u7740\u5341\u5e74\u524d\u5728\u6211\u9762\u524d\u7acb\u4e0b\u7684\u8a93\u8a00\uff0c\u8fde\u90a3\u76f8\u4fe1\u6211\u5fc5\u7136\u6b7b\u53bb\u7684\u5929\u9b54\u5973\uff0c\u4e5f\u6ca1\u6709\u660e\u76ee\u5f20\u80c6\u5730\u4f5c\u6076\u592a\u591a\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u8f7b\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u867d\u7136\uff0c\u8fd9\u5341\u5e74\u6765\uff0c\u4f60\u4eec\u6ca1\u6709\u5b8c\u5168\u5730\u542c\u4ece\u8a93\u8a00\uff0c\u4f46\u5927\u81f4\u4e0a\u90fd\u8fd8\u80fd\u81ea\u675f\u81ea\u7231\uff0c\u5bf9\u51e0\u4e2a\u504f\u6fc0\u3001\u56fa\u6267\u3001\u6840\u9a9b\u4e0d\u9a6f\uff0c\u53c8\u8eab\u6000\u7edd\u4e16\u6b66\u529f\u7684\u4eba\uff0c\u80fd\u591f\u8fd9\u6837\uff0c\u90a3\u5df2\u662f\u5f88\u96be\u5f97\u4e86\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u7a81\u7136\u7728\u52a8\u4e86\u4e00\u4e0b\u660e\u4eae\u7684\u5927\u773c\u775b\uff0c\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u9648\u5927\u4fa0\uff0c\u8d31\u59be\u60f3\u8bf7\u6559\u4f60\u4e00\u4ef6\u4e8b\uff0c\u4e0d\u77e5\u53ef\u5426\u89c1\u544a\uff1f\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8bf4\u5427\uff01\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u9053\uff1a\u201c\u4f60\u8fd9\u756a\u5927\u75c5\u4e4b\u540e\uff0c\u4e0d\u77e5\u5bf9\u6b66\u529f\u662f\u5426\u6709\u4e9b\u5f71\u54cd\uff1f\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u9053\uff1a\u201c\u6211\u5982\u8bf4\u6ca1\u6709\u5f71\u54cd\uff0c\u53ea\u6015\u4f60\u4eec\u4e5f\u4e0d\u4f1a\u76f8\u4fe1\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u9053\uff1a\u201c\u8fd9\u5341\u5e74\u4e2d\uff0c\u6211\u4e3a\u4e86\u5b88\u8a93\u8a00\uff0c\u5341\u5206\u82e6\u607c\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u53bb\u4f5c\u51e0\u4ef6\u6709\u76ca\u4eba\u95f4\u7684\u5584\u529f\u5462\uff1f\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8d31\u59be\u6027\u6076\u561b\uff01\u201d\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u8bb0\u5f97\u5341\u5e74\u524d\u8d31\u59be\u548c\u4f60\u52a8\u624b\u76f8\u640f\uff0c\u8d25\u5728\u7b2c\u4e5d\u5341\u62db\u4e4b\u4e0a\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u9053\uff1a\u201c\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u9053\uff1a\u201c\u6211\u5e38\u60f3\uff0c\u5982\u82e5\u6211\u4eec\u4e94\u4eba\u5408\u624b\u5bf9\u4ed8\u4f60\uff0c\u90a3\u5c31\u5f88\u96be\u9884\u6599\u9e7f\u6b7b\u8c01\u624b\u4e86\uff01\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u9053\uff1a\u201c\u4f46\u773c\u4e0b\uff0c\u4f60\u4eec\u53ea\u6709\u56db\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u9053\uff1a\u201c\u4f46\u4f60\u9648\u5927\u4fa0\u5927\u75c5\u4e4b\u540e\uff0c\u6b66\u529f\u4e5f\u6253\u4e86\u6298\u6263\uff0c\u5982\u662f\u6211\u4eec\u4e94\u4e2a\u4eba\u53ef\u4ee5\u80dc\u4f60\uff0c\u6b64\u65f6\uff0c\u6211\u4eec\u56db\u4e2a\u4eba\u4e5f\u5c31\u591f\u4e86\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u9053\uff1a\u201c\u4f60\u4eec\u53ef\u662f\u60f3\u8bd5\u8bd5\u5417\uff1f\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u8d31\u59be\u6ca1\u6709\u8fd9\u4e2a\u80c6\u5b50\uff0c\u800c\u4e14\u4ed6\u4eec\u4e5f\u4e0d\u80af\u548c\u6211\u5408\u4f5c\u3002\u201d \u3000\u3000\u6076\u5b54\u660e\u7f13\u7f13\u9053\uff1a\u201c\u56e0\u6b64\uff0c\u6211\u4eec\u53ea\u6709\u8010\u5fc3\u5730\u7b49\u5f85\u4e00\u9014\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u9053\uff1a\u201c\u7b49\u8001\u592b\u6b7b\u53bb\uff1f\u201d \u3000\u3000\u5927\u5634\u4eba\u4e00\u54a7\u5634\u5df4\uff0c\u5e72\u54b3\u4e86\u4e8c\u58f0\u9053\uff1a\u201c\u9664\u6b64\u4e4b\u5916\uff0c\u5f88\u96be\u60f3\u51fa\u522b\u7684\u826f\u7b56\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u795e\u60c5\u51dd\u91cd\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u4f1a\u6709\u7b49\u5230\u7684\u4e00\u5929\uff0c\u53ef\u60dc\u7684\u662f\u8001\u592b\u73b0\u5728\u8fd8\u6ca1\u6709\u6b7b\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u5e7d\u5e7d\u5730\u9053\uff1a\u201c\u5509\uff01\u4e0d\u77e5\u8fd8\u8981\u7b49\u5f85\u597d\u4e45\uff1f\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u9053\uff1a\u201c\u4e5f\u8bb8\u5f88\u5feb\uff0c\u4e5f\u8bb8\u4f60\u4eec\u8fd8\u8981\u7b49\u4e0a\u4e09\u4e94\u5e74\u3002\u201d \u3000\u3000\u90a3\u9ed1\u8863\u4eba\u957f\u957f\u5730\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5982\u662f\u4e09\u4e94\u5e74\u540e\uff0c\u4f60\u4e00\u5b9a\u6b7b\uff0c\u54b1\u4eec\u81ea\u7136\u8981\u8010\u5fc3\u7684\u7b49\u5f85\uff0c\u4f46\u4f60\u5982\u90a3\u65f6\u4e0d\u6b7b\uff0c\u54b1\u4eec\u5c31\u5c82\u4e0d\u662f\u53c8\u767d\u7b49\u4e94\u5e74\u4e86\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u7f13\u7f13\u4ed6\u8bf4\u9053\uff1a\u201c\u8bf8\u4f4d\u5982\u80fd\u60f3\u4e2a\u529e\u6cd5\uff0c\u4f7f\u8001\u592b\u65e9\u4e9b\u6b7b\u53bb\uff0c\u5c82\u4e0d\u662f\u4f60\u4eec\u5c11\u53bb\u8fd9\u4e9b\u675f\u7f1a\uff1f\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u53f9\u606f\u4e00\u58f0\u9053\uff1a\u201c\u60f3\u6211\u4eec\u662f\u60f3\uff0c\u53ea\u662f\u5f88\u96be\u627e\u51fa\u4e2a\u59a5\u5584\u7684\u6cd5\u5b50\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u4ef0\u671b\u7740\u5f53\u7a7a\u660e\u6708\uff0c\u51b7\u6f20\u4ed6\u8bf4\u9053\uff1a\u201c\u8001\u592b\u6b7b\u4e4b\u524d\uff0c\u4e5f\u5c06\u6709\u4e00\u756a\u5b89\u6392\u2014\u2014\u201d \u3000\u3000\u6076\u5b54\u660e\u7a81\u7136\u54c8\u54c8\u5927\u7b11\u8d77\u6765\uff0c\u6253\u65ad\u4e86\u9752\u886b\u8001\u4eba\u672a\u8bf4\u5b8c\u4e4b\u8a00\u3002 \u3000\u3000\u9752\u886b\u8001\u4eba\u51b7\u547c\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u7b11\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6076\u5b54\u660e\u4e0d\u6562\u518d\u7b11\uff0c\u9661\u7136\u6536\u4f4f\u5927\u7b11\u4e4b\u58f0\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u6709\u51e0\u53e5\u4e0d\u5f53\u4e4b\u8a00\uff0c\u8bf4\u51fa\u6765\uff0c\u5e0c\u671b\u4f60\u5927\u4fa0\u4e0d\u8981\u751f\u6c14\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u9053\uff1a\u201c\u597d\uff01\u4f60\u8bf4\u5427\uff01\u201d \u3000\u3000\u6076\u5b54\u660e\u9053\uff1a\u201c\u9648\u5927\u4fa0\u6240\u8c13\u7684\u8eab\u540e\u5b89\u6392\uff0c\u65e0\u975e\u662f\u627e\u4e00\u4e2a\u5929\u751f\u5947\u624d\uff0c\u7ee7\u627f\u4f60\u7684\u8863\u94b5\uff0c\u65e5\u540e\u5bf9\u4ed8\u6211\u4eec\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u4e25\u5b54\u660e\u7b11\u9053\uff1a\u201c\u5341\u5e74\u524d\uff0c\u6211\u4ea6\u60f3\u5230\u6b64\u4e8b\u4e86\uff0c\u4f46\u8fd9\u662f\u4e0d\u53ef\u80fd\u7684\u4e8b\u554a\uff01\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e25\u5b54\u660e\u9053\uff1a\u201c\u8bf4\u51fa\u6765\uff0c\u5e0c\u671b\u4f60\u9648\u8001\u4e0d\u8981\u751f\u6c14\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u5c3d\u7ba1\u8bf4\u5427\uff01\u201d \u3000\u3000\u6076\u5b54\u660e\u9053\uff1a\u201c\u4f60\u9648\u5927\u4fa0\u6709\u6b64\u7528\u5fc3\uff0c\u53ea\u6015\u5df2\u975e\u4e00\u65e5\uff0c\u4f46\u5728\u4e0b\u7b49\u4ea6\u65e9\u601d\u5ff5\u53ca\u6b64\uff0c\u9664\u975e\u4f60\u9648\u5927\u4fa0\u80fd\u591f\u518d\u6d3b\u4e8c\u5341\u5e74\uff0c\u4f7f\u4f60\u90a3\u5343\u9009\u4e07\u6311\u7684\u5f1f\u5b50\uff0c\u80fd\u53ca\u65f6\u7ee7\u627f\u4f60\u7684\u5168\u90e8\u6b66\u529f\uff0c\u7eb5\u7136\u5982\u6b64\uff0c\u6211\u4eec\u4e5f\u65e9\u6709\u4e86\u5bf9\u4ed8\u4ed6\u7684\u529e\u6cd5\uff01\u4f55\u5144\u4e8c\u5341\u5e74\u4e2d\uff0c\u4e0d\u8bba\u4f55\u7b49\u624d\u6167\u4e4b\u4eba\uff0c\u4e5f\u65e0\u6cd5\u548c\u4f60\u9648\u5927\u4fa0\u6709\u76f8\u540c\u7684\u6210\u5c31\u554a\uff01\u73b0\u5728\u561b\uff0c\u53ea\u6015\u662f\u66f4\u6765\u4e0d\u53ca\u2026\u2026\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u63a5\u9053\uff1a\u201c\u5982\u82e5\u8001\u592b\u80fd\u518d\u6d3b\u4e8c\u5341\u5e74\u5462\uff1f\u201d \u3000\u3000\u6076\u5b54\u660e\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u6211\u77a7\u662f\u6709\u4e9b\u4e0d\u53ef\u80fd\uff0c\u5c31\u7b97\u4f60\u529f\u53c2\u5929\u5730\uff0c\u80fd\u591f\u518d\u6d3b\u4e8c\u5341\u5e74\uff0c\u4f46\u4f60\u4e5f\u65e0\u6cd5\u80fd\u591f\u4fdd\u5f97\u8fd9\u6d88\u606f\u4e0d\u4f20\u626c\u51fa\u6765\uff0c\u518d\u660e\u767d\u4e00\u70b9\u8bf4\uff0c\u6211\u4eec\u5bf9\u4f60\u9648\u5927\u4fa0\u7684\u4e00\u4e3e\u4e00\u52a8\uff0c\u65e9\u5df2\u5341\u5206\u7559\u5fc3\uff0c\u4f60\u5982\u679c\u6536\u4e00\u4e2a\u5f92\u5f1f\uff0c\u51b3\u65e0\u6cd5\u7792\u5f97\u8fc7\u6211\u4eec\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u5fae\u5fae\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u6211\u4eec\u4e94\u4eba\uff0c\u51e0\u756a\u8d25\u5728\u4f60\u9648\u5927\u4fa0\u7684\u624b\u4e2d\uff0c\u5bf9\u4f60\u9648\u5927\u4fa0\uff0c\u56fa\u7136\u5341\u5206\u754f\u60e7\uff0c\u4f46\u4f60\u51e0\u6b21\u4e0d\u80af\u53d6\u6211\u4eec\u7684\u6027\u547d\uff0c\u8fd9\u754f\u4ff1\u4e4b\u4e2d\uff0c\u5b9e\u4e5f\u6709\u7740\u4e00\u756a\u6df1\u631a\u7684\u656c\u610f\uff0c\u6211\u4eec\u4e94\u4eba\u867d\u7136\u4e00\u5411\u5404\u884c\u5176\u662f\uff0c\u4ece\u6765\u4e0d\u80af\u5408\u4f5c\uff0c\u4f46\u6709\u4e00\u4ef6\u4e8b\uff0c\u5374\u5df2\u53d6\u5f97\u534f\u8bae\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u9053\uff1a\u201c\u5bf9\u4ed8\u8001\u592b\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u6076\u5b54\u660e\u9053\uff1a\u201c\u5982\u82e5\u6211\u4eec\u80fd\u591f\u53d6\u5f97\u534f\u8bae\u5bf9\u4ed8\u4f60\u9648\u5927\u4fa0\uff0c\u90a3\u4e5f\u4e0d\u7528\u7b49\u5230\u4eca\u65e5\u4e86\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u9053\uff1a\u201c\u90a3\u662f\u4ec0\u4e48\u534f\u8bae\uff1f\u201d \u3000\u3000\u6076\u5b54\u660e\u9053\uff1a\u201c\u534f\u8bae\u51b3\u4e0d\u5141\u8bb8\u518d\u6709\u7b2c\u4e8c\u4e2a\u9648\u5927\u4fa0\u51fa\u73b0\u6c5f\u6e56\uff0c\u4f60\u9648\u5927\u4fa0\u6d3b\u4e0a\u4e00\u5929\uff0c\u6211\u4eec\u5c31\u86f0\u4f0f\u4e00\u5929\uff0c\u4e0d\u4f5c\u6b66\u6797\u9738\u4e1a\u4e4b\u56fe\uff0c\u4f46\u8981\u5b89\u6392\u5728\u4f60\u6b7b\u4ea1\u4e4b\u540e\uff0c\u8fd8\u6709\u5bf9\u4ed8\u6211\u4eec\u4e4b\u4eba\uff0c\u90a3\u5c31\u4e0d\u6210\u4e86\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u63a5\u9053\uff1a\u201c\u90a3\u65f6\uff0c\u6211\u4eec\u5c06\u4e0d\u62e9\u624b\u6bb5\uff0c\u5bf9\u4ed8\u4f60\u7684\u8863\u94b5\u5f1f\u5b50\u3002\u201d \u3000\u3000\u5927\u5634\u4eba\u4e00\u54a7\u5634\u5df4\uff0c\u63a5\u9053\uff1a\u201c\u6211\u4eec\u5148\u884c\u63b3\u53bb\u4ed6\u7684\u7236\u6bcd\u3001\u5144\u59b9\uff0c\u4ee5\u4f5c\u4eba\u8d28\u3002\u201d \u3000\u3000\u90a3\u9ed1\u8863\u4eba\u63a5\u9053\uff1a\u201c\u6211\u4eec\u5c06\u4e94\u4eba\u5408\u4f5c\uff0c\u56db\u4e2a\u4eba\u5bf9\u4ed8\u9648\u5927\u4fa0\uff0c\u4e00\u4eba\u5168\u529b\u535a\u6740\u4f60\u90a3\u5f1f\u5b50\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u795e\u8272\u8083\u7136\uff0c\u7f13\u7f13\u626b\u63a0\u4e86\u56db\u4eba\u4e00\u773c\uff0c\u53ea\u89c1\u56db\u4eba\u516b\u53cc\u773c\uff0c\u4e5f\u4e00\u9f50\u671b\u7740\u81ea\u5df1\u3002 \u3000\u3000\u679c\u7136\u5728\u8fd9\u4e00\u65b9\u9762\uff0c\u56db\u4eba\u4e0b\u4e86\u5f88\u5927\u7684\u51b3\u5fc3\uff0c\u51b3\u4e0d\u80af\u8f7b\u6613\u8ba9\u6b65\u3002 \u3000\u3000\u4f46\u4ed6\u6b64\u65f6\uff0c\u522b\u8bf4\u548c\u8fd9\u56db\u4e2a\u6b66\u529f\u7edd\u9876\u7684\u9b54\u5934\u52a8\u624b\uff0c\u5c31\u662f\u548c\u4e00\u822c\u6c5f\u6e56\u4e4b\u58eb\u52a8\u624b\u76f8\u535a\uff0c\u4e5f\u662f\u4e1d\u6beb\u65e0\u80dc\u7684\u628a\u63e1\u3002\u5f53\u4e0b\u6545\u4f5c\u5e73\u9759\u5730\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u53c8\u60f3\u548c\u6211\u8d4c\u4e00\u4e0b\u662f\u5417\uff1f\u201d \u3000\u3000\u6076\u5b54\u660e\u9053\uff1a\u201c\u5982\u82e5\u8fd9\u662f\u8d4c\uff0c\u5374\u662f\u4e00\u6b21\u65e0\u9650\u7684\u8d4c\u6ce8\uff0c\u5305\u62ec\u4e86\u673a\u8bc8\u3001\u7fa4\u6bb4\uff0c\u4e00\u5207\u5351\u4e0b\u3001\u6076\u6bd2\u7684\u624b\u6bb5\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u9053\uff1a\u201c\u4f60\u9648\u5927\u4fa0\u662f\u4ec1\u4e49\u541b\u5b50\uff0c\u603b\u4e0d\u4f1a\u4e3a\u4e86\u6536\u4e00\u4e2a\u4f20\u6388\u8863\u94b5\u7684\u5f1f\u5b50\uff0c\u4f7f\u4ed6\u9505\u8fde\u4e5d\u65cf\uff0c\u6240\u6709\u7684\u4eb2\u4eba\uff0c\u90fd\u5c06\u8eab\u906d\u5c60\u622e\u3002\u201d \u3000\u3000\u5927\u5634\u4eba\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u4f60\u9648\u4eba\u4fa0\u5df2\u538b\u5236\u4e86\u6211\u4eec\u6570\u5341\u5e74\uff0c\u6211\u4eec\u51b3\u4e0d\u80fd\u5bb9\u5fcd\u53e6\u4e00\u4e2a\u4f3c\u4f60\u8fd9\u822c\u4eba\u7269\uff0c\u51fa\u73b0\u4e8e\u6c5f\u6e56\u4e4b\u4e0a\u3002\u201d \u3000\u3000\u90a3\u7626\u957f\u7684\u9ed1\u8863\u4eba\u63a5\u9053\uff1a\u201c\u5982\u662f\u60c5\u52bf\u8feb\u4eba\uff0c\u54b1\u4eec\u53ea\u6709\u5b81\u4e3a\u7389\u788e\u4e86\u3002\u201d \u3000\u3000\u90a3\u9752\u886b\u8001\u4eba\u4ef0\u5929\u6253\u4e2a\u54c8\u54c8\uff0c\u9053\uff1a\u201c\u53ef\u60dc\u5440\uff0c\u53ef\u60dc\u3002\u201d \u3000\u3000\u6076\u5b54\u660e\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u53ef\u60dc\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u9053\uff1a\u201c\u4f60\u4eec\u65e2\u7136\u6709\u548c\u6211\u8a93\u4e0d\u4e24\u7acb\u7684\u51b3\u5fc3\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u80af\u4e94\u4eba\u8054\u624b\uff0c\u548c\u6211\u51b3\u4e00\u6b7b\u6218\u5462\uff1f\u90a3\u5929\u9b54\u5973\u4eca\u5bb5\u672a\u6765\uff0c\u4f60\u4eec\u5b9e\u529b\u51cf\u5f31\u4e0d\u5c11\uff0c\u867d\u6709\u6b64\u5fc3\uff0c\u4f46\u6015\u4e5f\u6ca1\u6709\u8fd9\u4efd\u80c6\u91cf\uff0c\u5c82\u4e0d\u662f\u5927\u4e3a\u53ef\u60dc\u7684\u4e8b\u5417\uff1f\u201d \u3000\u3000\u6076\u5b54\u660e\u9053\uff1a\u201c\u7eb5\u7136\u90a3\u5929\u9b54\u5973\u4eca\u591c\u4e5f\u5982\u7ea6\u800c\u6765\uff0c\u53ea\u6015\u4e5f\u4e0d\u6562\u548c\u4f60\u52a8\u624b\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u4eec\u4e94\u4e2a\u4eba\u7eb5\u7136\u80fd\u548c\u4f60\u6253\u4e2a\u79cb\u8272\u5e73\u5206\uff0c\u4f46\u4e5f\u65e0\u6cd5\u5236\u4f60\u4e8e\u6b7b\u5730\uff0c\u518d\u8bf4\u6211\u4eec\u4e94\u4e2a\u4eba\u4e5f\u4e0d\u80fd\u5e38\u8d70\u5728\u4e00\u8d77\u554a\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u5341\u5e74\u524d\uff0c\u6211\u4eec\u4e94\u4eba\u4e5f\u66fe\u8c08\u8fc7\u4f60\u9648\u5927\u4fa0\uff0c\u7814\u5546\u7ed3\u679c\uff0c\u8fd8\u662f\u4e0d\u5b9c\u8054\u624b\u786c\u62da\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u6211\u4eec\u4e94\u4eba\u7b97\u8ba1\u4f60\u9648\u5927\u4fa0\u7684\u529f\u529b\uff0c\u5982\u662f\u6211\u4eec\u786c\u62da\u6b7b\u6597\uff0c\u5728\u6211\u4eec\u4e00\u65b9\uff0c\u81f3\u5c11\u8981\u6709\u4e24\u4e2a\u4eba\u6b7b\u4ea1\uff0c\u4e5f\u8bb8\u4e09\u4e2a\u4eba\uff0c\u624d\u6709\u6740\u6b7b\u4f60\u9648\u5927\u4fa0\u7684\u673a\u4f1a\u2026\u2026\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u9053\uff1a\u201c\u5c31\u7b97\u5982\u6b64\uff0c\u4f60\u4eec\u4e5f\u6709\u4e24\u4e2a\u6216\u4e09\u4e2a\u4eba\u51b6\uff0c\u4f55\u4e50\u4e0d\u4e3a\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u95ee\u9898\u5c31\u662f\u8c01\u8981\u6b7b\uff0c\u5982\u679c\u6211\u4eec\u4e94\u4eba\uff0c\u90fd\u4e0d\u613f\u5148\u6b7b\uff0c\u8fd9\u4e2a\u4ed7\uff0c\u5c31\u65e0\u6cd5\u6253\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u9053\uff1a\u201c\u4f60\u4eec\u5e94\u8be5\u8bd5\u8bd5\uff0c\u4e94\u4eba\u8054\u624b\uff0c\u80dc\u6211\u7684\u673a\u4f1a\u5f88\u5927\u3002\u201d \u3000\u3000\u90a3\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u6211\u4eec\u5df2\u7ecf\u7b97\u8ba1\u4e09\u5929\u4e09\u591c\uff0c\u7ed3\u679c\u662f\u8d25\u591a\u80dc\u5c11\uff0c\u4e00\u4e2a\u4e0d\u597d\uff0c\u8bf4\u4e0d\u5b9a\u4e94\u4e2a\u4eba\u90fd\u6b7b\u5728\u4f60\u9648\u5927\u4fa0\u7684\u624b\u4e2d\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u5927\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u7b97\u5f97\u5f88\u5947\u602a\u5440\uff0c\u7ed3\u8bba\u5404\u6709\u4e0d\u540c\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u7ed3\u8bba\u53ea\u6709\u4e00\u4e2a\uff0c\u53ea\u4e0d\u8fc7\u90a3\u7ae5\u5b50\u5947\u8bf4\u4e0d\u6e05\u695a\u7f62\u4e86\u3002\u201d \u3000\u3000\u7ae5\u5b50\u5947\u6012\u9053\uff1a\u201c\u4f60\u8981\u62a2\u7740\u63a5\u53e3\uff0c\u600e\u602a\u6211\u8bf4\u4e0d\u6e05\u695a\u5462\uff1f\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u7b11\u9053\uff1a\u201c\u6162\u6162\u8bf4\uff0c\u4f60\u4eec\u5982\u662f\u5148\u6253\u8d77\u6765\uff0c\u8001\u592b\u5c31\u5750\u6536\u6e14\u4eba\u4e4b\u5229\u4e86\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5ab3\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8c01\u8bf4\u90fd\u662f\u4e00\u6837\uff0c\u7528\u4e0d\u7740\u4e89\u6267\u4e86\u3002\u201d \u3000\u3000\u90a3\u9ed1\u8863\u4eba\u62a2\u5148\u9053\uff1a\u201c\u9053\u7406\u5f88\u7b80\u5355\uff0c\u5982\u662f\u6211\u4eec\u5404\u51fa\u5168\u529b\uff0c\u6211\u4f60\u786c\u62fc\uff0c\u4f60\u9648\u5927\u4fa0\u5728\u640f\u6740\u6211\u4eec\u4e24\u4eba\u4e4b\u540e\uff0c\u4ea6\u5fc5\u5c06\u8eab\u53d7\u91cd\u4f24\u2026\u2026\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u4f60\u4eec\u7adf\u7136\u662f\u5168\u65e0\u6b64\u7b49\u52c7\u6c14\u4e00\u8bd5\uff0c\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u95ee\u9898\u5c31\u51fa\u5728\u6211\u4eec\u4e94\u4eba\u5404\u6000\u9b3c\u80ce\uff0c\u8c01\u4e5f\u4e0d\u613f\u5728\u51fa\u624b\u65f6\uff0c\u5168\u529b\u98de\u51fb\uff0c\u7eb5\u7136\u662f\u4e94\u4eba\u8054\u624b\u800c\u4e3a\uff0c\u4e5f\u90fd\u5c06\u5404\u81ea\u6697\u7559\u5b9e\u529b\uff0c\u51c6\u5907\u81ea\u4fdd\uff0c\u800c\u4f60\u9635\u5927\u4fa0\uff0c\u4e00\u4e2a\u4eba\u529b\u6597\u4e94\u4eba\uff0c\u5fc5\u5c06\u662f\u5168\u529b\u51fa\u51fb\uff0c\u9891\u4e0b\u715e\u624b\uff0c\u5982\u82e5\u4e8b\u60c5\u5982\u6b64\uff0c\u90a3\u7ed3\u5c40\u5c31\u4e0d\u5728\u6211\u4eec\u9884\u8ba1\u4e4b\u4e2d\u4e86\u3002\u201d \u3000\u3000\u9752\u8863\u8001\u4eba\u6545\u4f5c\u8f7b\u677e\u5730\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u8be5\u662f\u4ec0\u4e48\u6837\u5c40\u9762\uff1f\u201d \u3000\u3000\u9ed1\u6709\u4eba\u9053\uff1a\u201c\u6211\u4eec\u5404\u4fdd\u5b9e\u529b\uff0c\u4f60\u9648\u5927\u4fa0\u5168\u529b\u51fa\u624b\uff0c\u4e00\u6b63\u4e00\u8d1f\u4e4b\u4e0b\uff0c\u6211\u4eec\u8bf4\u4e0d\u5b9a\u90fd\u5c06\u6b7b\u4f5c\u5728\u4f60\u7684\u624b\u4e2d\u4e86\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u4eec\u4e00\u76f4\u4e0d\u6562\u51fa\u624b\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u7a81\u7136\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6709\u4e00\u4ef6\u4e8b\uff0c\u8d31\u59be\u662f\u4e0d\u5f53\u51fa\u53e3\uff0c\u4f46\u6211\u6709\u5982\u6897\u5728\u5589\uff0c\u4e0d\u5410\u4e0d\u5feb\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u9053\uff1a\u201c\u8fd9\u756a\u76f8\u805a\uff0c\u5e0c\u671b\u7545\u6240\u6b32\u8a00\uff0c\u4f60\u5fc3\u4e2d\u6709\u4ec0\u4e48\uff0c\u5c3d\u7ba1\u8bf4\u51fa\u53e3\u5c31\u662f\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u9053\uff1a\u201c\u6709\u4f60\u9648\u5927\u4fa0\u5728\u4e16\uff0c\u6211\u4eec\u624d\u4fdd\u5b58\u4e86\u4e00\u4e2a\u548c\u5e73\u76f8\u5b58\u7684\u5c40\u9762\uff0c\u5982\u8457\u4f60\u9648\u5927\u4fa0\u771f\u7684\u4e0d\u5e78\u800c\u901d\uff0c\u6c5f\u6e56\u4e0a\u7acb\u65f6\u5927\u4e71\uff0c\u5c31\u662f\u6211\u4eec\u4e94\u4eba\uff0c\u5c31\u5c06\u6709\u4e00\u756a\u706b\u5e76\uff0c\u8c01\u80fd\u751f\u5b58\uff0c\u90a3\u5c31\u5f88\u96be\u8bf4\u4e86\u3002\u201d \u3000\u3000\u4e25\u5b54\u660e\u63a5\u9053\uff1a\u201c\u6700\u6bd2\u5987\u4eba\u5fc3\uff0c\u53e4\u6709\u660e\u8bad\uff0c\u4f60\u67f3\u5a9a\u5a18\u548c\u90a3\u5929\u9b54\u5973\uff0c\u5904\u5fc3\u79ef\u8651\u3001\u60f3\u7b97\u8ba1\u6211\u4eec\u4e09\u4eba\u2026\u2026\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u6012\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u6700\u53ef\u6076\u4e86\uff0c\u5230\u5904\u6311\u62d4\u79bb\u95f4\uff0c\u6311\u8d77\u522b\u4eba\u706b\u5e76\uff0c\u81ea\u5df1\u597d\u6536\u6e14\u4eba\u4e4b\u5229\uff0c\u603b\u6709\u4e00\u5929\uff0c\u6211\u8981\u548c\u4f60\u6253\u4e2a\u751f\u6b7b\u51fa\u6765\u3002\u201d \u3000\u3000\u6076\u5b54\u660e\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u533a\u533a\u662f\u968f\u65f6\u5949\u966a\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5982\u662f\u9648\u5927\u4fa0\u80af\u4f5c\u89c1\u8bc1\uff0c\u54b1\u4eec\u5c31\u5728\u8fd9\u91d1\u9876\u5cf0\u4e0a\u52a8\u624b\uff0c\u5206\u4e2a\u80dc\u8d1f\u51fa\u6765\u3002\u201d \u3000\u3000\u6076\u5b54\u660e\u9053\uff1a\u201c\u53ea\u8981\u4f60\u67f3\u5a9a\u5a18\u80fd\u591f\u8bf4\u52a8\u9648\u5927\u4fa0\u7b54\u5e94\uff0c\u533a\u533a\u613f\u8ba9\u4f60\u5148\u673a\u3002\u201d \u3000\u3000\u7ae5\u5b50\u5947\u548c\u90a3\u9ed1\u8863\u4eba\uff0c\u51b7\u6f20\u5730\u7ad9\u5728\u4e00\u4fa7\uff0c\u867d\u672a\u51fa\u8a00\u6311\u6218\uff0c\u4f46\u4e5f\u672a\u51fa\u8a00\u76f8\u529d\u3002 \u3000\u3000\u9752\u886b\u8001\u4eba\u4e3e\u624b\u4e00\u6325\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u7528\u5435\u4e86\u3002\u201d \u3000\u3000\u5176\u5b9e\uff0c\u6076\u5b54\u660e\u548c\u67f3\u5a9a\u5a18\uff0c\u4ea6\u4e0d\u613f\u771f\u7684\u52a8\u624b\u8d77\u6765\uff0c\u53ea\u662f\u53cc\u65b9\u90fd\u628a\u8bdd\u8bf4\u5f97\u592a\u91cd\uff0c\u4e00\u65f6\u65e0\u6cd5\u4e0b\u53f0\u800c\u5df2\u3002 \u3000\u3000\u90a3\u9752\u886b\u8001\u4eba\u4e00\u5f00\u53e3\uff0c\u6b63\u597d\u501f\u673a\u4e0b\u53f0\u3002 \u3000\u3000\u4e24\u4eba\u4e92\u671b\u4e86\u4e00\u773c\uff0c\u90fd\u4e0d\u518d\u8bb2\u8bdd\u3002 \u3000\u3000\u9752\u886b\u8001\u4eba\u62ac\u5934\u5821\u4e86\u671b\u5929\u4e0a\u7684\u660e\u6708\u9053\uff1a\u201c\u8fd9\u6b21\uff0c\u54b1\u4eec\u76f8\u805a\uff0c\u4e3b\u8981\u662f\u8001\u592b\u6709\u51e0\u53e5\u80ba\u8151\u4e4b\u8a00\uff0c\u544a\u8bc9\u4f60\u4eec\u3002\u201d \u3000\u3000\u56db\u4eba\u9f50\u9f50\u9053\uff1a\u201c\u9648\u5927\u4fa0\u6709\u4f55\u5429\u5490\uff0c\u6211\u7b49\u6d17\u8033\u606d\u542c\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u9053\uff1a\u201c\u5305\u62ec\u5929\u9b54\u5973\u5728\u5185\uff0c\u4f60\u4eec\u90fd\u662f\u6c5f\u6e56\u4e0a\u6781\u96be\u4e00\u89c1\u7684\u4e0d\u4e16\u4e4b\u624d\uff0c\u5982\u80fd\u591f\u5408\u529b\u4e3a\u5584\uff0c\u90a3\u5c06\u662f\u5929\u4e0b\u82cd\u751f\u8499\u798f\uff0c\u4f46\u5982\u653e\u624b\u4e3a\u6076\uff0c\u4ea6\u5c06\u662f\u65e0\u6570\u7684\u751f\u7075\u6d82\u70ad\u3002\u201d \u3000\u3000\u56db\u4eba\u9762\u9762\u76f8\u89c6\uff0c\u8bf4\u4e0d\u51fa\u4e00\u53e5\u8bdd\u6765\u3002 \u3000\u3000\u9752\u886b\u8001\u4eba\u957f\u957f\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f7f\u8001\u592b\u60f3\u4e0d\u660e\u767d\u7684\u662f\uff0c\u5584\u6076\u4e4b\u5206\uff0c\u4ea6\u4e0d\u8fc7\u4e00\u5ff5\u4e4b\u95f4\uff0c\u4e3a\u4ec0\u4e48\u4f60\u4eec\u53ef\u4ee5\u4e3a\u6076\u6740\u4eba\uff0c\u4e0d\u53ef\u4ee5\u884c\u4ec1\u81ea\u5584\u5462\uff1f\u201d \u3000\u3000\u6076\u5b54\u660e\u9053\uff1a\u201c\u5728\u4e0b\u4e5f\u60f3\u8fc7\u6b64\u4e8b\uff0c\u56e0\u540d\u5229\u67b7\u9501\uff0c\u4f7f\u4eba\u65e0\u6cd5\u6323\u8131\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u9053\uff1a\u201c\u65e2\u7136\u77e5\u5176\u539f\u56e0\uff0c\u4e3a\u4f55\u53c8\u4e0d\u80af\u6539\u8fc7\u5462\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u6c5f\u6e56\u4e0a\u65e9\u5df2\u628a\u6211\u4eec\u89c6\u4e3a\u574f\u4eba\uff0c\u5c31\u7b97\u6211\u4eec\u60f3\u505a\u597d\u4e8b\uff0c\u522b\u4eba\u8fd8\u662f\u4e0d\u80af\u76f8\u4fe1\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u9053\uff1a\u201c\u5927\u4e08\u592b\u884c\u4e8b\u4e3a\u4eba\uff0c\u53ea\u6c42\u5fc3\u4e4b\u6240\u5b89\uff0c\u4e5f\u5c31\u662f\u8bf4\uff0c\u5927\u53ef\u4e0d\u5fc5\u65a4\u65a4\u8ba1\u8f83\u522b\u4eba\u7684\u770b\u6cd5\u3002\u201d \u3000\u3000\u90a3\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u6b66\u6797\u4e2d\u8c01\u4e0d\u77e5\uff0c\u6211\u4eec\u662f\u88ab\u9648\u5927\u4fa0\u6b66\u529f\u9707\u670d\uff0c\u7acb\u4e0b\u7684\u7ea6\u8a00\uff0c\u4e0d\u8bb8\u968f\u5fc3\u6240\u6b32\uff0c\u8fd9\u4e00\u70b9\u2026\u2026\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u63a5\u9053\uff1a\u201c\u5341\u5e74\u6765\u6211\u4e5f\u66fe\u95fb\u5fc3\u81ea\u95ee\uff0c\u6211\u529d\u4f60\u4eec\u884c\u4ec1\uff0c\u81ea\u5df1\u5374\u7528\u4e86\u9738\u9053\u624b\u6bb5\uff0c\u538c\u670d\u4f60\u4eec\u7acb\u4e0b\u7ea6\u8a00\uff0c\u56e0\u6b64\uff0c\u5fc3\u4e2d\u751a\u662f\u4e0d\u5b89\uff0c\u8fd9\u756a\u548c\u4f60\u4eec\u91cd\u805a\uff0c\u6211\u5df2\u6539\u53d8\u4e86\u4e3b\u610f\uff0c\u5e0c\u671b\u548c\u4f60\u4eec\u597d\u597d\u8c08\u8c08\u3002\u201d \u3000\u3000\u8bed\u58f0\u987f\u4e86\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u6211\u4e5f\u5e0c\u671b\u4f60\u4eec\u80fd\u591f\u7545\u6240\u6b32\u8a00\uff0c\u628a\u4f60\u4eec\u5fc3\u4e2d\u7684\u79ef\u5ff5\uff0c\u503e\u5410\u800c\u51fa\u2026\u2026\u201d \u3000\u3000\u6076\u5b54\u53eb\u63a5\u9053\uff1a\u201c\u662f\u4e86\uff0c\u8fd9\u6b21\u9648\u5927\u4fa0\u60f3\u8bf4\u670d\u6211\u4eec\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u9053\uff1a\u201c\u8001\u592b\u786e\u7136\u6709\u6b64\u7528\u5fc3\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u9053\uff1a\u201c\u6211\u770b\u6b64\u4e8b\u5e0c\u671b\u4e0d\u5927\uff0c\u9648\u5927\u4fa0\u7eb5\u7136\u80fd\u820c\u7ffb\u91d1\u83b2\uff0c\u53ea\u6015\u4e5f\u96be\u4f7f\u987d\u77f3\u70b9\u5934\u3002\u201d \u3000\u3000\u7ae5\u5b50\u5947\u4e00\u54a7\u5927\u5634\u5df4\uff0c\u76ae\u7b11\u8089\u4e0d\u7b11\u4ed6\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u542c\u542c\u4e5f\u597d\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u9ed8\u5bdf\u51e0\u4eba\u795e\u8bf7\uff0c\u53ea\u89c9\u5f97\u51e0\u4eba\u5bf9\u81ea\u5df1\u867d\u7136\u66f2\u610f\u5949\u627f\uff0c\u4f46\u795e\u8272\u5374\u6d41\u9732\u7740\u4e00\u79cd\u91ce\u6027\u96be\u9a6f\u670d\u4e4b\u6c14\uff0c\u4e0d\u7981\u6697\u6697\u53f9\u606f\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u770b\u8bf8\u4f4d\u795e\u60c5\uff0c\u8001\u592b\u662f\u5f88\u96be\u8bf4\u670d\u4f60\u4eec\u4e86\u3002\u201d \u3000\u3000\u90a3\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4eca\u65e5\u4e4b\u4f1a\uff0c\u90a3\u5929\u9b54\u5973\u672a\u80af\u5982\u7ea6\u800c\u6765\uff0c\u5c31\u7b97\u4f60\u9648\u5927\u4fa0\u80fd\u591f\u628a\u6211\u4eec\u56db\u4eba\u8bf4\u670d\uff0c\u53ea\u6015\u4e5f\u662f\u65e0\u7528\uff0c\u56e0\u4e3a\u7b54\u5e94\u9648\u5927\u4fa0\uff0c\u90a3\u662f\u7ed9\u5929\u9b54\u5973\u9020\u6210\u673a\u4f1a\u4e86\u3002\u201d \u3000\u3000\u6076\u5b54\u660e\u9053\uff1a\u201c\u90a3\u5929\u9b54\u5973\u4e0d\u6bd4\u4f60\u9648\u5927\u4fa0\u5ea6\u91cf\uff0c\u5979\u5982\u4e00\u65e6\u7fbd\u7ffc\u4e30\u6ee1\uff0c\u5fc5\u5c06\u5148\u6740\u4e86\u6211\u4eec\u56db\u4eba\u800c\u540e\u5feb\u3002\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u5982\u662f\u4e0d\u5e78\u5343\u53e4\uff0c\u6211\u4eec\u53c8\u88ab\u4f60\u8bf4\u670d\uff0c\u9000\u51fa\u6c5f\u6e56\uff0c\u6574\u4e2a\u6b66\u6797\uff0c\u6210\u4e86\u5929\u9b54\u5973\u7684\u5929\u4e0b\uff0c\u5341\u5e74\u540e\uff0c\u6211\u4eec\u90fd\u5c06\u6b7b\u65e0\u846c\u8eab\u4e4b\u5730\u4e86\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u9053\uff1a\u201c\u5728\u6211\u4eec\u4e94\u4eba\u4e2d\uff0c\u4ee5\u90a3\u5929\u9b54\u5973\u5e74\u4e8b\u6700\u8f7b\uff0c\u4f46\u4e5f\u4ee5\u5979\u7684\u91ce\u5fc3\u6700\u5927\uff0c\u5509\uff01\u5982\u662f\u4f60\u9648\u5927\u4fa0\u65e9\u80fd\u628a\u5979\u6740\u4e86\uff0c\u4e5f\u8bb8\uff0c\u6c5f\u6e56\u4e0a\u5c31\u4e0d\u4f1a\u518d\u6709\u98ce\u6ce2\u4e86\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u9053\uff1a\u201c\u6211\u5e94\u8be5\u628a\u4f60\u4eec\u5168\u90fd\u6740\u6389\u3002\u201d \u3000\u3000\u6076\u5b54\u660e\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6c5f\u6e56\u4e0a\u76ee\u4e0b\u6240\u4ee5\u80fd\u591f\u4fdd\u6301\u7740\u6682\u65f6\u7684\u5e73\u9759\uff0c\u5927\u534a\u662f\u4f60\u9648\u5927\u4fa0\u7684\u5a01\u540d\u6240\u654c\uff0c\u4f46\u4e5f\u6709\u4e00\u534a\u662f\u6211\u7b49\u4e4b\u529f\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u9053\uff1a\u201c\u8fd9\u8bdd\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u6076\u5b54\u660e\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u4eec\u4e94\u4eba\u6076\u540d\u5927\u8457\uff0c\u9ed1\u767d\u4e24\u9053\uff0c\u5168\u4e0d\u7406\u4f1a\uff0c\u8c01\u4e5f\u4e0d\u613f\u626c\u540d\u7acb\u4e07\uff0c\u6015\u7684\u662f\u6811\u5927\u62db\u98ce\u5f15\u8d77\u6211\u4eec\u4e94\u4eba\u7684\u6740\u673a\uff0c\u627e\u4e0a\u95e8\u53bb\uff0c\u5f71\u54cd\u6240\u53ca\uff0c\u6c5f\u6e56\u4e0a\u5404\u95e8\u5404\u6d3e\uff0c\u90fd\u4e0d\u6562\u592a\u8fc7\u6269\u5145\u81ea\u5df1\u7684\u5b9e\u529b\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u6012\u904f\uff1a\u201c\u5f3a\u8bcd\u72e1\u8fa9\uff0c\u51a5\u987d\u4e0d\u7075\u3002\u201d \u3000\u3000\u6076\u5b54\u660e\u4e0d\u518d\u591a\u8a00\uff0c\u7f13\u7f13\u5411\u540e\u9000\u4e86\u4e24\u6b65\u3002 \u3000\u3000\u9752\u886b\u8001\u4eba\u51b7\u8083\u5730\u6280\u9053\uff1a\u201c\u65e2\u7136\u56db\u4f4d\u90fd\u4e0d\u613f\u88ab\u8001\u592b\u8bf4\u670d\uff0c\u8001\u592b\u4e5f\u4e0d\u613f\u591a\u8d39\u65e0\u8c13\u7684\u53e3\u820c\u4e86\uff0c\u4e0d\u8fc7\u8001\u592b\u8981\u8bf4\u660e\u4e00\u4ef6\u4e8b\uff1f\u201d \u3000\u3000\u56db\u4eba\u9f50\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u7b49\u6d17\u8033\u606d\u542c\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u9053\uff1a\u201c\u8fd9\u5341\u5e74\u6765\u4f60\u4eec\u90fd\u8fd8\u80fd\u4fe1\u5b88\u7ea6\u8a00\uff0c\u8001\u592b\u4e0d\u4fbf\u8a00\u800c\u65e0\u4fe1\uff0c\u4f46\u81ea\u4eca\u5bb5\u4e4b\u540e\uff0c\u8001\u592b\u5bf9\u4f60\u4eec\u4e0d\u518d\u5bc4\u6539\u8fc7\u5411\u5584\u4e4b\u671b\uff0c\u54ea\u4e00\u4e2a\u80c6\u5927\u7834\u6212\uff0c\u5148\u884c\u4e3a\u6076\uff0c\u54ea\u4e00\u4e2a\u5c31\u5148\u4e3a\u8001\u592b\u5251\u4e0b\u4e4b\u9b3c\uff0c\u8001\u592b\u8a00\u51fa\u5fc5\u8df5\uff0c\u5c4a\u65f6\u51b3\u4e0d\u5bbd\u5bb9\uff0c\u4f60\u4eec\u53ef\u4ee5\u53bb\u4e86\u3002\u201d \u3000\u3000\u56db\u4e2a\u4eba\u4f3c\u662f\u672a\u6599\u5230\u90a3\u9752\u886b\u8001\u4eba\uff0c\u7a81\u7136\u95f4\u53d8\u8138\u76f8\u5411\uff0c\u4e0d\u7981\u4e00\u5446\u3002 \u3000\u3000\u67f3\u5a9a\u5a18\u7a76\u7136\u4e00\u6b20\u8eab\uff0c\u9053\uff1a\u201c\u9648\u5927\u4fa0\uff1f\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u4e00\u6325\u624b\uff0c\u9053\uff1a\u201c\u8001\u592b\u4e0d\u60f3\u518d\u548c\u4f60\u4eec\u591a\u8d39\u5507\u820c\uff0c\u56db\u4f4d\u53ef\u4ee5\u53bb\u4e86\u2026\u2026\u201d \u3000\u3000\u7a81\u7136\u63d0\u9ad8\u4e86\u58f0\u97f3\uff0c\u63a5\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u628a\u8bdd\u8bf4\u660e\uff0c\u4fe1\u4e0d\u4fe1\u662f\u4f60\u4eec\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u56db\u4eba\u9762\u9762\u76f8\u89c6\uff0c\u65e0\u4e00\u4eba\u6562\u63a5\u8a00\u3002 \u3000\u3000\u9752\u886b\u8001\u4eba\u53ea\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u8d70\uff0c\u7ad9\u5728\u8fd9\u91cc\u7b49\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u56db\u4e2a\u4eba\u679c\u7136\u4e0d\u6562\u518d\u591a\u505c\u7559\uff0c\u6025\u6025\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u4f46\u89c1\u56db\u6761\u4eba\u5f71\uff0c\u53bb\u5982\u6d41\u661f\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u5df2\u7136\u6d88\u5931\u4e0d\u89c1\u3002 \u3000\u3000\u9752\u886b\u8001\u4eba\u76ee\u7779\u56db\u4eba\u8fdc\u53bb\u4e4b\u540e\uff0c\u624d\u7f13\u7f13\u7ad9\u8d77\u8eab\u5b50\u6025\u6025\u7684\u5730\u5598\u606f\u4e86\u4e00\u9635\uff0c\u6b63\u5f85\u79bb\u53bb\uff0c\u7a81\u95fb\u8eab\u540e\u8f6c\u8fc7\u4e00\u4e2a\u5a07\u5a9a\u52a8\u4eba\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u9648\u5927\u4fa0\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u8f6c\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u8eab\u540e\u4e0d\u8fdc\u5904\uff0c\u7ad9\u7740\u4e00\u4e2a\u5168\u8eab\u767d\u8863\uff0c\u957f\u53d1\u5782\u80a9\u7684\u5973\u4eba\u3002\u4e0d\u7981\u5fc3\u5934\u4e00\u9707\uff0c\u9053\uff1a\u201c\u5929\u9b54\u5973\uff1f\u201d \u3000\u3000\u5929\u9b54\u5973\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u7792\u5f97\u8fc7\u4ed6\u4eec\u7792\u4e0d\u8fc7\u6211\u3002\u201d\u4e00\u9762\u8bf4\u4e00\u9762\u4e3e\u6b65\u76f4\u884c\u5305\u6765\u3002 \u3000\u3000\u9752\u886b\u8001\u4eba\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u7ad9\u4f4f\uff01\u201d \u3000\u3000\u5929\u9b54\u5973\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u522b\u8bf4\u4ed6\u4eec\u56db\u4eba\u8054\u624b\u4e86\uff0c\u5c31\u662f\u4e00\u4eba\uff0c\u5728\u5341\u62db\u4e4b\u5185\uff0c\u4e5f\u53ef\u4ee5\u53d6\u4f60\u4e4b\u547d\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u795e\u60c5\u8083\u7136\uff0c\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u4e94\u4eba\u4e4b\u4e2d\u4ee5\u4f60\u6700\u9634\u9669\u6076\u6bd2\uff0c\u8001\u592b\u4eca\u5bb5\u5982\u80fd\u628a\u4f60\u6740\u6b7b\u4e8e\u6b64\uff0c\u90a3\u662f\u6b7b\u4e5f\u7791\u76ee\u4e86\u3002\u201d \u3000\u3000\u5929\u9b54\u5973\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u53ea\u6015\u4f60\u65e9\u5df2\u65e0\u6b64\u80fd\u8010\u4e86\u3002\u201d \u3000\u3000\u5979\u53e3\u4e2d\u867d\u7136\u8bf4\u7684\u8f7b\u677e\uff0c\u4f46\u5374\u505c\u4e0b\u811a\u6b65\uff0c\u4e0d\u6562\u5411\u524d\u8fdb\u884c\u3002 \u3000\u3000\u9752\u886b\u8001\u4eba\u7f13\u6b65\u7531\u5de8\u5ca9\u4e4b\u4e0a\uff0c\u884c\u4e86\u4e0b\u53bb\uff0c\u903c\u8fd1\u4e86\u5929\u9b54\u5973\uff0c\u9053\uff1a\u201c\u4e5f\u8bb8\u8001\u592b\u771f\u7684\u5df2\u65e0\u80fd\u80dc\u4f60\uff0c\u4f60\u4f55\u4e0d\u51fa\u624b\u8bd5\u4e00\u8bd5\u5462\uff1f\u201d \u3000\u3000\u5929\u9b54\u5973\u75be\u5feb\u5730\u5411\u540e\u9000\u4e86\u4e24\u6b65\uff0c\u9053\uff1a\u201c\u6162\u7740\uff01\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u9053\uff1a\u201c\u4f60\u8fd8\u6709\u4ec0\u4e48\u8bdd\u8bf4\uff1f\u201d \u3000\u3000\u5929\u9b54\u5973\u9053\uff1a\u201c\u6211\u786e\u77e5\u4f60\u5df2\u65e0\u80fd\u548c\u6211\u52a8\u624b\uff0c\u4f46\u6211\u4ecd\u4e0d\u613f\u548c\u4f60\u4e00\u62da\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u4ef0\u5929\u6253\u4e2a\u54c8\u54c8\uff0c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5929\u9b54\u5973\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u6211\u5148\u767b\u4e0a\u8fd9\u91d1\u9876\u5cf0\uff0c\u5bf9\u4f60\u7684\u4e00\u8a00\u4e00\u52a8\uff0c\u90fd\u542c\u5341\u5206\u6e05\u695a\uff0c\u800c\u4e14\u4e86\u5982\u6307\u638c\uff0c\u4f46\u6211\u80fd\u77a7\u5f97\u51fa\u6765\uff0c\u90a3\u6076\u5b54\u660e\u66ff\u4f60\u628a\u8109\uff0c\u5c82\u6709\u77a7\u4e0d\u51fa\u4e4b\u7406\uff0c\u4e00\u5219\u4e3a\u4f60\u5a01\u540d\u6240\u9707\uff0c\u4f60\u53c8\u5f3a\u884c\u8fd0\u6c14\uff0c\u8c03\u5300\u4e86\u8109\u640f\uff0c\u4f7f\u4ed6\u5fc3\u4e2d\u6ca1\u6709\u628a\u63e1\uff0c\u4e0d\u6562\u8f7b\u6613\u8bba\u65ad\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u9053\uff1a\u201c\u8fd8\u6709\u5417\uff1f\u201d \u3000\u3000\u5929\u9b54\u5973\u9053\uff1a\u201c\u4e8c\u5219\u4ed6\u4e3a\u4eba\u9634\u9669\uff0c\u5c31\u7b97\u63a2\u51fa\u4e86\u4f60\u786e\u6709\u75c5\uff0c\u4f46\u4ed6\u5374\u6545\u4f5c\u4e0d\u77e5\uff0c\u4f7f\u67f3\u5a9a\u5a18\u3001\u7ae5\u5b50\u5947\u7b49\uff0c\u90fd\u8ba4\u4e3a\u4f60\u75c5\u60c5\u4e0d\u91cd\uff0c\u4e0d\u6562\u8f7b\u634b\u864e\u987b\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u8001\u592b\u60f3\u4e0d\u51fa\u8fd9\u5bf9\u90a3\u6076\u5b54\u660e\u6709\u4f55\u7528\u5904\uff1f\u201d \u3000\u3000\u5929\u9b54\u5973\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u7528\u5904\u592a\u5927\u4e86\u2026\u2026\u201d \u3000\u3000\u4e3e\u624b\u7406\u4e86\u4e00\u4e0b\u88ab\u5c71\u51e4\u5439\u4e71\u7684\u957f\u53d1\uff0c\u63a5\u9053\uff1a\u201c\u5982\u662f\u6211\u6599\u65ad\u4e0d\u9519\uff0c\u4ed6\u5fc5\u5c06\u53bb\u800c\u590d\u8fd4\uff0c\u7136\u540e\u6f5c\u4f0f\u5728\u6697\u4e2d\u89c2\u5bdf\uff0c\u5f85\u4ed6\u51b3\u5b9a\u4f60\u51b3\u65e0\u8fd8\u51fb\u4e4b\u80fd\uff0c\u5fc5\u7136\u91cd\u767b\u5cf0\u4e0a\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u6697\u6697\u9707\u9a87\u9053\uff1a\u201c\u8fd9\u4e2b\u5934\u5e74\u4e8b\u6700\u8f7b\uff0c\u4f46\u5978\u8bc8\u3001\u9634\u6c89\uff0c\u65e0\u4e0d\u8d85\u4eba\u4e00\u7b49\uff0c\u65e5\u540e\u5728\u6c5f\u6e56\u4e0a\u4e3a\u7978\u6700\u70c8\u7684\uff0c\u53ea\u6015\u4e5f\u662f\u5979\u4e86\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u8f6c\u5ff5\uff0c\u53e3\u4e2d\u5374\u51b7\u54fc\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u662f\u8bf4\uff0c\u4ed6\u60f3\u6765\u535a\u6740\u8001\u592b\u5417\uff1f\u201d \u3000\u3000\u5929\u9b54\u5973\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4ed6\u4e0d\u4f1a\u6740\u4f60\uff0c\u4f46\u4ed6\u5c06\u65bd\u7528\u6700\u6b8b\u9177\u7684\u624b\u6bb5\u6298\u78e8\u4f60\uff0c\u4f7f\u4f60\u8bf4\u51fa\u6b66\u529f\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u6697\u9053\uff1a\u8fd9\u4e2b\u5934\u7684\u6599\u4e8b\u4e4b\u80fd\uff0c\u5b9e\u662f\u4f69\u670d\u3002\u53e3\u4e2d\u5374\u6012\u58f0\u559d\u9053\uff1a\u201c\u4e0d\u7528\u62d6\u5ef6\u65f6\u95f4\u4e86\uff0c\u8fd9\u548c\u54b1\u4eec\u640f\u6597\u52a8\u624b\uff0c\u6709\u4f55\u76f8\u5e72\uff1f\u201d \u3000\u3000\u5929\u9b54\u5973\u9053\uff1a\u201c\u81ea\u7136\u6709\u5173\uff0c\u4ed6\u5982\u8981\u6697\u4e2d\u63a2\u89c6\uff0c\u5374\u4e0d\u4f1a\u8ba9\u6211\u6740\u4f60\uff0c\u4f60\u5982\u4e0d\u652f\uff0c\u4ed6\u53ca\u65f6\u51fa\u624b\u52a9\u4f60\uff0c\u5408\u4f60\u4eec\u4e24\u4eba\u4e4b\u529b\uff0c\u6211\u81ea\u7136\u96be\u662f\u654c\u624b\uff0c\u5c82\u4e0d\u662f\u6740\u4f60\u4e0d\u6210\uff0c\u5f04\u5de7\u6210\u62d9\uff1f\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u9053\uff1a\u201c\u8001\u592b\u4e0d\u4fe1\u4f60\u80fd\u4f24\u5f97\u4e86\u6211\u2026\u2026\u201d\u7a81\u7136\u6b3a\u8eab\u800c\u8d77\u3002 \u3000\u3000\u5929\u9b54\u5973\u4e00\u9000\u4e24\u4e08\uff0c\u7b11\u9053\uff1a\u201c\u9648\u5927\u4fa0\u4e0d\u8981\u901e\u5f3a\uff0c\u4f60\u7684\u9a6c\u6b65\uff0c\u5df2\u7ecf\u7ad9\u4e0d\u7a33\u4e86\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u9a87\u7136\u6b62\u6b65\uff0c\u5fc3\u4e2d\u53c8\u6c14\u53c8\u6025\uff0c\u4e0d\u7981\u4e00\u9635\u6025\u5598\u3002 \u3000\u3000\u5929\u9b54\u5973\u51b7\u6f20\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u8981\u7d27\u5f20\uff0c\u6211\u4e0d\u4f1a\u6740\u4f60\uff0c\u4f60\u624b\u4e0b\u7559\u60c5\uff0c\u9976\u8fc7\u6211\u4e09\u6b21\u4e0d\u6b7b\uff0c\u6b64\u523b\uff0c\u6211\u5e94\u8be5\u8fd8\u62a5\u4e00\u6b21\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u6012\u58f0\u63a5\u9053\uff1a\u201c\u8001\u592b\u4e0d\u4fe1\u3002\u201d \u3000\u3000\u5929\u9b54\u5973\u4e3e\u8d77\u767d\u5ae9\u7684\u7389\u638c\uff0c\u8fde\u8fde\u6447\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u4fe1\u4e5f\u4e0d\u8981\u7d27\uff0c\u4e8b\u5b9e\u4e0a\u4e0d\u6740\u4f60\uff0c\u8fd8\u6709\u66f4\u91cd\u8981\u7684\u7406\u7531\uff0c\u6211\u8981\u4f60\u6d3b\u7740\uff0c\u4f7f\u4ed6\u4eec\u4e0d\u6562\u5984\u52a8\uff0c\u7ed9\u6211\u4e00\u4e2a\u4ece\u5bb9\u5e03\u7f72\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u6012\u9053\uff1a\u201c\u8001\u592b\u5982\u82e5\u75c5\u52bf\u80fd\u597d\uff0c\u7b2c\u4e00\u4e2a\u6740\u4f60\u5929\u9b54\u5973\u3002\u201d \u3000\u3000\u5929\u9b54\u5973\u9053\uff1a\u201c\u53ef\u60dc\u4f60\u6ca1\u6709\u673a\u4f1a\u4e86\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u9053\uff1a\u201c\u8001\u592b\u53ef\u4ee5\u628a\u6b64\u8baf\u544a\u8bc9\u90a3\u67f3\u5a9a\u5a18\u3001\u6076\u5b54\u660e\uff0c\u4f7f\u4f60\u5fc3\u613f\u843d\u7a7a\u3002\u201d \u3000\u3000\u5929\u9b54\u5973\u9053\uff1a\u201c\u4ed6\u4eec\u56db\u4e2a\u4eba\uff0c\u96be\u518d\u78b0\u5230\u4e00\u8d77\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u9053\uff1a\u201c\u4ed6\u4eec\u8bf4\u4f60\u5fc3\u673a\u6700\u6df1\uff0c\u770b\u6765\u679c\u7136\u4e0d\u9519\u3002\u201d \u3000\u3000\u5929\u9b54\u5973\u9053\uff1a\u201c\u8fc7\u5956\uff0c\u8fc7\u5956\u2026\u2026\u201d\u63a2\u624b\u4ece\u6000\u4e2d\u6478\u51fa\u4e00\u4e2a\u7ee2\u5305\uff0c\u6295\u5230\u9752\u886b\u8001\u4eba\u7684\u8eab\u524d\uff0c\u5ae3\u7136\u4e00\u7b11\u63a5\u9053\uff1a\u201c\u9648\u5927\u54e5\u554a\u2026\u2026\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u53ea\u58f0\u559d\u9053\uff1a\u201c\u4f4f\u53e3\uff01\u8001\u592b\u662f\u4f55\u7b49\u4eba\uff0c\u5c82\u80af\u548c\u4f60\u8bf4\u7b11\u3002\u201d \u3000\u3000\u5929\u9b54\u5973\u4e3e\u624b\u7406\u7406\u957f\u53d1\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u6b64\u65f6\u7684\u529f\u529b\u5df2\u96be\u518d\u6491\u4e0b\u53bb\uff0c\u53d1\u813e\u6c14\u5bf9\u8eab\u4f53\u5f71\u54cd\u5f88\u5927\uff0c\u4e0d\u8981\u5c0f\u59b9\u8fd9\u822c\u79f0\u547c\uff0c\u4ee5\u540e\u6211\u4e0d\u53eb\u4f60\u5c31\u662f\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u5fc3\u4e2d\u6fc0\u6012\u96be\u8010\uff0c\u4f46\u53c8\u81ea\u77e5\u6b64\u523b\uff0c\u5b9e\u975e\u5929\u9b54\u5973\u4e4b\u654c\uff0c\u53ea\u597d\u5f3a\u81ea\u5fcd\u4e0b\u4e86\uff0c\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u4e0d\u518d\u7406\u4f1a\u5979\u3002 \u3000\u3000\u5929\u9b54\u5973\u683c\u683c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9648\u5927\u4fa0\uff0c\u5982\u662f\u6211\u6599\u65ad\u4e0d\u9519\uff0c\u4f60\u91cd\u75c5\u4e4b\u8eab\uff0c\u53c8\u5728\u8fd9\u91d1\u9876\u5cf0\u4e0a\u51bb\u4e86\u8fd9\u6837\u957f\u4e45\u65f6\u95f4\uff0c\u53ea\u6015\u8fde\u4e0b\u5cf0\u7684\u6c14\u529b\u4e5f\u6ca1\u6709\uff0c\u5c0f\u59b9\u90a3\u7ee2\u888b\u4e4b\u4e2d\u653e\u6709\u725b\u8089\u7c89\u5e72\uff0c\u548c\u4e24\u7c92\u52a9\u957f\u4f60\u6c14\u529b\u7684\u836f\uff0c\u4f60\u5982\u987b\u8981\uff0c\u4e0d\u59a8\u670d\u7528\uff0c\u8fd9\u4e5f\u7b97\u5c0f\u59b9\u62a5\u7b54\u4f60\u6614\u5e74\u6570\u6b21\u624b\u4e0b\u7559\u60c5\u4e4b\u6069\uff0c\u9648\u5927\u4fa0\u591a\u591a\u73cd\u91cd\uff0c\u5c0f\u59b9\u53bb\u4e86\u3002\u201d \u3000\u3000\u8f6c\u8eab\u5411\u5cf0\u4e0b\u5954\u53bb\u3002 \u3000\u3000\u5c06\u8fd1\u7edd\u5cf0\u8fb9\u7f18\u4e4b\u65f6\uff0c\u7f13\u6b65\u8d70\u4e86\u56de\u6765\u3002 \u3000\u3000\u9752\u886b\u8001\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u56de\u6765\u4f5c\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5929\u9b54\u5973\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4e5f\u8bb8\u90a3\u6076\u5b54\u660e\u5df2\u9690\u8eab\u5728\u6697\u4e2d\u77a7\u7740\u54b1\u4eec\u3002\u201d \u3000\u3000\u8bed\u58f0\u987f\u4e86\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u8fd9\u7ee2\u888b\u4e4b\u4e2d\u7684\u836f\u51b3\u7136\u65e0\u6bd2\uff0c\u4f60\u53ef\u4ee5\u653e\u5fc3\u98df\u7528\uff0c\u6211\u4e0d\u662f\u8981\u6551\u4f60\u6027\u547d\u800c\u662f\u4e3a\u4e86\u6211\u5fc5\u9700\u8ba9\u4f60\u6d3b\u4e0b\u53bb\uff0c\u4f55\u51b5\uff0c\u6b64\u60c5\u6b64\u666f\u4e4b\u4e0b\uff0c\u4f60\u4e5f\u4e0d\u7518\u5fc3\u6b7b\u53bb\u662f\u5417\uff1f\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u51b7\u51b7\u9053\uff1a\u201c\u8001\u592b\u7684\u751f\u6b7b\uff0c\u81ea\u6709\u8001\u592b\u51b3\u5b9a\uff0c\u7528\u4e0d\u7740\u4f60\u6765\u8d39\u5fc3\u3002\u201d \u3000\u3000\u5927\u53a6\u5973\u9053\uff1a\u201c\u597d\uff01\u54b1\u4eec\u5c31\u6b64\u522b\u8fc7\uff0c\u5e0c\u671b\u65e5\u540e\u8fd8\u6709\u89c1\u9762\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u8a00\u7f62\uff0c\u9661\u7136\u7ffb\u8eab\uff0c\u4e00\u8dc3\u800c\u8d77\uff0c\u6295\u4e0b\u7edd\u5cf0\u3002 \u3000\u3000\u53ea\u4e00\u7728\u773c\u4e4b\u95f4\uff0c\u5929\u9b54\u5973\u884c\u8e2a\u5df2\u674f\u3002 \u3000\u3000\u9752\u886b\u8001\u4eba\u671b\u7740\u5929\u9b54\u5973\u8fdc\u53bb\u7684\u80cc\u5f71\u5446\u5446\u51fa\u795e\uff0c\u5fc3\u4e2d\u5374\u662f\u6697\u81ea\u9a87\u7136\uff0c\u9053\uff1a\u201c\u770b\u6765\u8fd9\u5973\u9b54\u5934\u7684\u529f\u529b\u4f3c\u4e4e\u5df2\u8d85\u8d8a\u4e86\u6076\u5b54\u660e\u3001\u67f3\u5a9a\u5a18\u7b49\u56db\u4eba\u4e4b\u4e0a\uff0c\u81ea\u5df1\u5982\u82e5\u6b7b\u53bb\uff0c\u6b66\u6797\u4e2d\u7eb7\u4e89\u8d77\u6765\uff0c\u5929\u4e0b\u82cd\u751f\u82e6\u77e3\uff01\u201d\u60f3\u5230\u611f\u4f24\u4e4b\u5904\uff0c\u4e0d\u7981\u80de\u7136\u4e00\u53f9\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u4e00\u58f0\u72ee\u543c\u4f20\u6765\uff0c\u5212\u7834\u4e86\u6df1\u5c71\u7684\u9759\u5bc2\uff0c\u4e5f\u60ca\u9192\u4e86\u9752\u886b\u8001\u4eba\u4f24\u611f\u7684\u5fc3\u60c5\uff0c\u4f7f\u4ed6\u91cd\u56de\u5230\u73b0\u5b9e\u60c5\u666f\u4e2d\u6765\u3002 \u3000\u3000\u4ed6\u4e3e\u624b\u62c9\u4e00\u4e0b\u9752\u886b\u8863\u9886\uff0c\u6253\u4e86\u4e00\u4e2a\u54c6\u55e6\uff0c\u56e0\u75c5\u800c\u8870\u9000\u7684\u4f53\u529b\uff0c\u5df2\u4f7f\u4ed6\u65e0\u6cd5\u5fcd\u53d7\u8fd9\u5c71\u9876\u4e0a\u7684\u5947\u5bd2\u3002 \u3000\u3000\u5929\u9b54\u5973\u8bf4\u7684\u4e0d\u9519\uff0c\u4ed6\u7684\u6c14\u529b\uff0c\u5df2\u65e0\u6cd5\u652f\u6491\u4e0b\u5c71\u4e4b\u884c\uff0c\u7f13\u7f13\u4f38\u51fa\u624b\u53bb\uff0c\u62fe\u8d77\u90a3\u5929\u9b54\u5973\u6295\u4e8e\u5730\u4e0a\u7684\u7ee2\u888b\u3002 \u3000\u3000\u6253\u5f00\u7ee2\u888b\uff0c\u53ea\u89c1\u888b\u4e2d\u9664\u4e86\u5f88\u591a\u7684\u725b\u8089\u7c89\u5e72\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u4e2a\u7389\u74f6\uff0c\u74f6\u4e2d\u653e\u6709\u4e24\u7c92\u767d\u8272\u7684\u4e39\u4e38\u3002 \u3000\u3000\u4e00\u9635\u9965\u997f\u4e4b\u611f\uff0c\u88ad\u4e86\u4e0a\u6765\uff0c\u4f38\u624b\u53d6\u8fc7\u725b\u8089\u7c89\u5e72\uff0c\u5403\u4e86\u51e0\u5757\uff0c\u5c31\u5cf0\u9876\u79ef\u51b0\u4e2d\uff0c\u53d6\u4e0b\u4e00\u5757\uff0c\u653e\u5165\u53e3\u4e2d\uff0c\u6b65\u5c65\u8e52\u8dda\u7684\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u9635\u65f6\u5149\u7684\u5f3a\u884c\u88c5\u4f5c\uff0c\u53c8\u8017\u53bb\u4ed6\u4e0d\u5c11\u7684\u6c14\u529b\uff0c\u91cd\u75c5\u4e4b\u4f53\uff0c\u5df2\u4f7f\u4ed6\u65e0\u80fd\u5fcd\u53d7\u8fd9\u7edd\u5cf0\u4e4b\u9876\u7684\u5947\u5bd2\u3002 \u3000\u3000\u4ed6\u884c\u5230\u5c71\u5cf0\u4e00\u89d2\uff0c\u51c6\u5907\u65e9\u4e9b\u79bb\u5f00\uff0c\u4ed6\u5fc3\u4e2d\u660e\u767d\uff0c\u81ea\u5df1\u53ea\u8981\u591a\u505c\u7559\u4e00\u9635\uff0c\u5f88\u53ef\u80fd\u5c31\u6b64\u5012\u6bd9\u4e8e\u7edd\u5cf0\u4e4b\u4e0a\u3002 \u3000\u3000\u7a81\u7136\u95f4\u811a\u4e0b\u4e00\u6ed1\uff0c\u4e00\u8de4\u6454\u5728\u5730\u4e0a\u3002 \u3000\u3000\u539f\u6765\uff0c\u4ed6\u5fc3\u6709\u6240\u601d\uff0c\u4e00\u811a\u8e0f\u5728\u4e00\u7247\u79ef\u51b0\u4e4b\u4e0a\uff0c\u7ad9\u7acb\u4e0d\u7a33\uff0c\u8dcc\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u8fd9\u4e00\u8de4\uff0c\u867d\u7136\u672a\u4f7f\u4ed6\u808c\u80a4\u53d7\u5230\u591a\u5927\u4f24\u5bb3\uff0c\u4f46\u5728\u5fc3\u7075\u4e0a\uff0c\u5374\u6709\u5982\u88ab\u523a\u4e86\u6570\u5251\u3002 \u3000\u3000\u4ed6\u6323\u624e\u5750\u8d77\u6765\uff0c\u671b\u7740\u5929\u4e0a\u660e\u6708\uff0c\u53e3\u4e2d\u5583\u54fa\u81ea\u8bed\u9053\uff1a\u201c\u5b8c\u4e86\u3002\u201d \u3000\u3000\u957f\u53f9\u4e00\u58f0\uff0c\u62fe\u8d77\u8dcc\u6454\u5728\u5730\u4e0a\u7684\u7ee2\u888b\uff0c\u6253\u5f00\u74f6\u585e\uff0c\u5012\u51fa\u4e86\u4e00\u7c92\u767d\u8272\u7684\u4e39\u4e38\uff0c\u541e\u5165\u53e3\u4e2d\u3002 \u3000\u3000\u4ed6\u5fc5\u987b\u501f\u91cd\u5929\u9b54\u5973\u7684\u4e39\u4e38\uff0c\u4f7f\u81ea\u5df1\u4ece\u7edd\u671b\u4e2d\u632f\u594b\u8d77\u6765\uff0c\u4e0b\u6b64\u7edd\u5cf0\u3002 \u3000 \u7b2c\u4e00\u56de\u3000\u5782\u6b7b\u8001\u4eba \u3000 \u3000\u3000\u5915\u9633\u897f\u4e0b\uff0c\u665a\u971e\u7eda\u70c2\uff0c\u7531\u5ce8\u5d4b\u5230\u5609\u5b9a\u7684\u5b98\u9053\u4e0a\uff0c\u6b63\u5954\u7740\u56db\u5339\u5feb\u9a6c\u3002 \u3000\u3000\u7b2c\u4e00\u5339\u5065\u9a6c\uff0c\u662f\u4e00\u4f4d\u56db\u5341\u5f00\u5916\u7684\u7537\u5b50\uff0c\u65b9\u4e2d\u9752\u886b\uff0c\u957f\u9aef\u5782\u80f8\uff0c\u9a6c\u978d\u6302\u7740\u4e00\u628a\u957f\u5251\u3002 \u3000\u3000\u7b2c\u4e8c\u5339\u5feb\u9a6c\u4e0a\uff0c\u662f\u4e00\u4f4d\u4e2d\u5e74\u5987\u4eba\uff0c\u7709\u76ee\u6e05\u79c0\uff0c\u4eea\u6001\u4e07\u5343\u3002 \u3000\u3000\u7b2c\u4e09\u5339\u9a6c\u4e0a\uff0c\u662f\u4e00\u4e2a\u5341\u56db\u4e94\u5c81\u7684\u7ae5\u5b50\uff0c\u4e00\u8eab\u75be\u670d\uff0c\u5251\u7709\u6717\u76ee\uff0c\u5341\u5206\u4fca\u7f8e\uff0c\u80cc\u4e0a\u63d2\u7740\u957f\u5251\u3002 \u3000\u3000\u7b2c\u56db\u5339\u5feb\u9a6c\u4e0a\uff0c\u4e5f\u662f\u4e00\u4f4d\u7ae5\u5b50\uff0c\u8eab\u7740\u84dd\u886b\uff0c\u5341\u5206\u6e05\u7626\uff0c\u4e00\u8138\u4e66\u751f\u6c14\u3002 \u3000\u3000\u8fd9\u65f6\u56db\u4eba\u6b63\u884c\u5728\u4e00\u5ea7\u5c0f\u5e99\u524d\u9762\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u7531\u90a3\u5c0f\u5e99\u4e2d\u4f20\u51fa\u4e00\u58f0\u6df1\u957f\u53f9\u606f\uff0c\u4e00\u4e2a\u9752\u8863\u8001\u4eba\uff0c\u8e09\u8dc4\u800c\u51fa\uff0c\u4e00\u8de4\u6454\u5728\u5927\u9053\u4e0a\u3002 \u3000\u3000\u524d\u9762\u4e24\u5339\u5feb\u9a6c\u5df2\u7136\u8d8a\u8fc7\u5c0f\u5e99\uff0c\u7b2c\u4e09\u5339\u5feb\u9a6c\uff0c\u75be\u9a70\u800c\u81f3\uff0c\u5411\u90a3\u9752\u886b\u8001\u4eba\u8eab\u4e0a\u649e\u53bb\u3002 \u3000\u3000\u9a6c\u4e0a\u75be\u670d\u7ae5\u5b50\uff0c\u7a81\u7136\u4e00\u52d2\u7f30\u7ef3\uff0c\u5411\u524d\u75be\u51b2\u7684\u5feb\u9a6c\uff0c\u5ffd\u5730\u524d\u8e44\u4e0a\u626c\uff0c\u6253\u4e86\u4e00\u4e2a\u8f6c\u8eab\uff0c\u95ea\u5411\u4e00\u4fa7\u3002 \u3000\u3000\u7b2c\u56db\u5339\u9a6c\u4e0a\u7684\u84dd\u8863\u7ae5\u5b50\uff0c\u5374\u53ca\u65f6\u6536\u4f4f\u7f30\u7ef3\uff0c\u7ffb\u8eab\u4e0b\u9a6c\uff0c\u653e\u5f00\u624b\u4e2d\u7f30\u7ef3\uff0c\u5954\u5230\u90a3\u9752\u8863\u8001\u4eba\u8eab\u4fa7\uff0c\u6276\u8d77\u90a3\u9752\u8863\u8001\u4eba\uff0c\u9053\uff1a\u201c\u8001\u4f2f\u4f2f\u600e\u4e48\u4e86\uff1f\u201d \u3000\u3000\u90a3\u9752\u8863\u8001\u4eba\u8f6c\u773c\u671b\u4e86\u671b\u90a3\u84dd\u8863\u7ae5\u5b50\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u673d\u75c5\u4e86\u3002\u201d \u3000\u3000\u90a3\u84dd\u8863\u7ae5\u5b50\u4f38\u624b\u5728\u90a3\u8001\u4eba\u989d\u5934\u4e0a\u6309\u4e86\u4e00\u4e0b\uff0c\u53ea\u89c9\u5f97\u4ed6\u989d\u4e0a\u5341\u5206\u70eb\u624b\uff0c\u4e0d\u7981\u4e00\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u8001\u4f2f\u4f2f\u4f60\u75c5\u52bf\u4e0d\u8f7b\u5440\uff01\u201d \u3000\u3000\u9752\u8863\u8001\u4eba\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u5509\uff01\u8001\u6c49\u6b7b\u4e0d\u8db3\u501f\uff0c\u53ef\u601c\u5929\u4e0b\u2026\u2026\u201d \u3000\u3000\u5230\u6b64\u5904\uff0c\u795e\u5fd7\u9661\u7136\u4e00\u6e05\uff0c\u4f4f\u53e3\u4e0d\u8a00\u3002 \u3000\u3000\u8fd9\u5f53\u513f\uff0c\u90a3\u9752\u8863\u4e2d\u5e74\u4eba\uff0c\u4ee5\u53ca\u90a3\u5987\u4eba\u548c\u75c5\u670d\u5c11\u5e74\uff0c\u5168\u90fd\u56f4\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u8001\u4f2f\u4f2f\uff0c\u53ef\u6709\u4ec0\u4e48\u5fc3\u613f\u4e22\u4e0d\u4e0b\u5417\uff1f\u201d \u3000\u3000\u9752\u8863\u8001\u4eba\u5931\u53bb\u795e\u91c7\u7684\u53cc\u76ee\uff0c\u7f13\u7f13\u8f6c\u52a8\uff0c\u626b\u63a0\u4e86\u90a3\u9752\u886b\u4eba\u548c\u4e2d\u5e74\u5987\u4eba\u4e00\u773c\u9053\uff1a\u201c\u662f\u7684\uff0c\u8001\u673d\u4e22\u4e0d\u4e0b\u6551\u4e16\u5fc3\u613f\u2026\u2026\u201d \u3000\u3000\u90a3\u9752\u886b\u5927\u6c49\u4e00\u4e0b\u9a6c\uff0c\u4f38\u624b\u5728\u8001\u4eba\u5934\u4e0a\u6309\u4e86\u4e00\u628a\uff0c\u8bf4\u9053\uff1a\u201c\u9601\u4e0b\u70e7\u7684\u5f88\u9ad8\uff0c\u96be\u602a\u662f\u8bed\u8a00\u4e0d\u6e05\u4e86\u3002\u201d \u3000\u3000\u90a3\u4e2d\u5e74\u5987\u4eba\u7a81\u7136\u63a5\u53e3\u9053\uff1a\u201c\u5927\u54e5\u554a\uff0c\u4ed6\u8fd9\u5e74\u7eaa\u3001\u91cd\u75c5\u5728\u8eab\uff0c\u5982\u4e0d\u80fd\u53ca\u65f6\u65bd\u6551\uff0c\u53ea\u6015\u662f\u96be\u4fdd\u4e0d\u6d4b\uff0c\u65e2\u7136\u53eb\u54b1\u4eec\u78b0\u4e0a\u4e86\uff0c\u600e\u80fd\u8896\u624b\u4e0d\u7ba1\uff1f\u201d \u3000\u3000\u90a3\u9752\u886b\u4e2d\u5e74\u4eba\u51dd\u76ee\u5bfb\u601d\u4e86\u7247\u523b\uff0c\u9053\uff1a\u201c\u4ed6\u7684\u75c5\u5f88\u91cd\uff0c\u54b1\u4eec\u53c8\u6709\u6025\u4e8b\u2026\u2026\u201d \u3000\u3000\u90a3\u4e2d\u5e74\u5987\u4eba\u9053\uff1a\u201c\u6551\u4eba\u4e00\u547d\u80dc\u9020\u4e03\u7ea7\u6d6e\u5c60\uff0c\u592b\u541b\u53c8\u7cca\u533b\u7406\uff0c\u7406\u5f53\u6d4e\u4e16\u6551\u4eba\u624d\u662f\u3002\u201d \u3000\u3000\u90a3\u9752\u886b\u4e2d\u5e74\u4eba\u6c89\u541f\u4e86\u7247\u523b\uff0c\u63a5\u9053\uff1a\u201c\u597d\u5427\uff01\u6211\u8bd5\u8bd5\u770b\u3002\u201d \u3000\u3000\u4e2d\u5e74\u5987\u4eba\u559c\u9053\uff1a\u201c\u597d\u6781\u4e86\u2026\u2026\u201d \u3000\u3000\u4fa7\u76ee\u601d\u7d22\u7247\u523b\u9053\uff1a\u201c\u6211\u9ed8\u7b97\u54b1\u4eec\u8d74\u4f1a\u4e4b\u671f\uff0c\u8fd8\u6709\u4e09\u65e5\u53ef\u4ee5\u803d\u6401\uff0c\u5230\u65f6\uff0c\u54b1\u4eec\u8f9b\u82e6\u4e00\u4e9b\uff0c\u5feb\u6b65\u4e00\u4e9b\u5c31\u662f\uff0c\u518d\u8bf4\uff0c\u5c31\u7b97\u54b1\u4eec\u53bb\u665a\u4e86\uff0c\u4e3a\u6551\u4eba\u5ef6\u8bef\u65f6\u95f4\uff0c\u4ed6\u4eec\u4e5f\u4f1a\u539f\u8c05\u7684\u3002\u201d \u3000\u3000\u9752\u886b\u4e2d\u5e74\u4eba\u957f\u957f\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e0d\u7792\u8d24\u59bb\u8bf4\uff0c\u8fd9\u4eba\u75c5\u60c5\u592a\u91cd\uff0c\u6211\u5b9e\u5728\u6ca1\u6709\u628a\u63e1\u80fd\u591f\u628a\u4ed6\u533b\u597d\u3002\u201d \u3000\u3000\u4e2d\u5e74\u5987\u4eba\u9053\uff1a\u201c\u54b3\uff01\u53ea\u8981\u54b1\u4eec\u5c3d\u5230\u4e86\u5168\u90e8\u7684\u5fc3\u529b\uff0c\u6cbb\u4e0d\u597d\u4ed6\u7684\u75c5\u52bf\uff0c\u90a3\u4e5f\u662f\u65e0\u53ef\u5948\u4f55\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u9752\u886b\u4e2d\u5e74\u4eba\u62b1\u8d77\u90a3\u8001\u8005\uff0c\u9053\uff1a\u201c\u6b64\u523b\uff0c\u54b1\u4eec\u5f97\u627e\u4e00\u5ea7\u6709\u836f\u94fa\u7684\u5e02\u9547\uff0c\u6211\u5148\u66ff\u4ed6\u5f00\u4e24\u4ed8\u9000\u70e7\u7684\u836f\u8bd5\u8bd5\u5427\u3002\u201d \u3000\u3000\u4e2d\u5e74\u5987\u4eba\u4f4e\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u90a3\u8001\u8005\u53cc\u76ee\u7d27\u95ed\uff0c\u4f3c\u662f\u5df2\u5230\u96be\u518d\u652f\u6301\u4e4b\u5883\uff0c\u4e0d\u7981\u4e00\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u73b0\u5728\u5f88\u5371\u9669\u5417\uff1f\u201d \u3000\u3000\u9752\u886b\u4e2d\u5e74\u4eba\u9053\uff1a\u201c\u5944\u5944\u4e00\u606f\uff0c\u968f\u65f6\u53ef\u4ee5\u6c14\u7edd\u800c\u901d\u3002\u201d \u3000\u3000\u4e2d\u5e74\u5987\u4eba\u9053\uff1a\u201c\u592b\u541b\u70bc\u6210\u7684\u516b\u5b9d\u91d1\u4e39\uff0c\u53ef\u5426\u7ed9\u4ed6\u670d\u7528\u4e00\u7c92\u5462\uff1f\u201d \u3000\u3000\u9752\u886b\u4e2d\u5e74\u4eba\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u6211\u5341\u5e74\u91c7\u96c6\u7684\u7075\u836f\uff0c\u53ea\u6210\u5341\u7c92\u516b\u5b9d\u91d1\u4e39\u2026\u2026\u201d \u3000\u3000\u5987\u4eba\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u90a3\u516b\u5b9d\u91d1\u4e39\uff0c\u4f5c\u4f55\u7528\u9014\uff1f\u201d \u3000\u3000\u9752\u886b\u4e2d\u5e74\u4eba\u9053\uff1a\u201c\u7528\u6765\u4fdd\u547d\u5ef6\u8eab\u3002\u201d \u3000\u3000\u4e2d\u5e74\u5987\u4eba\u9053\uff1a\u201c\u8fd9\u4f4d\u8001\u4eba\u4e5f\u662f\u4e00\u6761\u547d\u554a\uff01\u4ed6\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u7528\u8fd9\u91d1\u4e39\u5462\uff1f\u201d \u3000\u3000\u9752\u886b\u4e2d\u5e74\u4eba\u9053\uff1a\u201c\u5509\uff01\u8fd9\u91d1\u4e39\u5341\u5206\u73cd\u8d35\u2026\u2026\u201d \u3000\u3000\u4e2d\u5e74\u5987\u4eba\u4e0d\u4fbf\u518d\u8a00\uff0c\u7f13\u6b65\u884c\u5230\u90a3\u8001\u4eba\u8eab\u524d\uff0c\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u4f60\u672c\u6765\u53ef\u4ee5\u4e0d\u6b7b\u7684\u2026\u2026\u201d \u3000\u3000\u9752\u886b\u4e2d\u5e74\u4eba\u7a81\u7136\u653e\u4e0b\u90a3\u9752\u8863\u8001\u4eba\uff0c\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u4e2a\u7389\u74f6\uff0c\u5012\u51fa\u4e00\u7c92\u7ea2\u8272\u7684\u4e39\u4e38\uff0c\u8bf4\u9053\uff1a\u201c\u7ed9\u4ed6\u670d\u4e0b\u53bb\u5427\uff01\u201d \u3000\u3000\u90a3\u4e2d\u5e74\u5987\u4eba\u63a5\u8fc7\u7ea2\u8272\u4e39\u4e38\u60ca\u559c\u4ed6\u8bf4\u9053\uff1a\u201c\u5927\u54e5\u554a\uff01\u4f60\u820d\u5f97\u8fd9\u7c92\u91d1\u4e39\u5417\uff1f\u201d \u3000\u3000\u9752\u886b\u4e2d\u5e74\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u820d\u5f97\u4e5f\u4e0d\u6210\u554a\uff01\u6211\u4e0d\u80fd\u8ba9\u4f60\u56e0\u6b64\u4e8b\u5fc3\u4e2d\u4e0d\u5feb\u4e50\u3002\u201d \u3000\u3000\u4e2d\u5e74\u5987\u4eba\u867d\u5df2\u662f\u4e09\u5341\u51e0\u8bb8\u4eba\uff0c\u4f46\u8138\u4e0a\u4ecd\u662f\u4e00\u7247\u5929\u771f\u3001\u7eaf\u6d01\u7684\u795e\u60c5\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u592b\u541b\u5f85\u6211\u771f\u597d\u3002\u201d \u3000\u3000\u9752\u886b\u4e2d\u5e74\u4eba\u9053\uff1a\u201c\u6211\u8bf4\u8fc7\uff0c\u8fd9\u4e00\u751f\u597d\u597d\u5f85\u4f60\uff0c\u51b3\u4e0d\u8ba9\u4f60\u6709\u4e0d\u5feb\u4e50\u7684\u4e8b\u60c5\u3002\u201d \u3000\u3000\u4e2d\u5e74\u5987\u4eba\u8e72\u4e0b\u8eab\u5b50\uff0c\u4f38\u51fa\u96ea\u767d\u7684\u7389\u624b\uff0c\u634f\u90a3\u8001\u4eba\u7259\u5173\u3002 \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u9752\u8863\u8001\u4eba\u7a81\u7136\u7741\u5f00\u53cc\u76ee\uff0c\u671b\u7740\u90a3\u4e2d\u5e74\u592b\u4eba\uff0c\u5fae\u5fae\u9894\u9996\uff0c\u81ea\u52a8\u5f20\u5f00\u4e86\u5634\u5df4\u3002 \u3000\u3000\u90a3\u4e2d\u5e74\u5987\u4eba\u628a\u4e39\u4e38\u6295\u5165\u4e86\u9752\u8863\u8001\u4eba\u7684\u53e3\u4e2d\uff0c\u9053\uff1a\u201c\u8001\u4f2f\u4f2f\uff0c\u8fd9\u4e00\u7c92\u516b\u5b9d\u91d1\u4e39\uff0c\u4e43\u6211\u4e08\u592b\u91c7\u96c6\u7684\u5404\u79cd\u5947\u836f\u70bc\u6210\uff0c\u529f\u80fd\u56fa\u5143\u4fdd\u547d\uff0c\u4f60\u670d\u7528\u4e86\u4e4b\u540e\uff0c\u53ef\u4ee5\u5b89\u5fc3\u517b\u606f\uff0c\u5f85\u5230\u524d\u9762\u5e02\u9547\u4e4b\u4e0a\uff0c\u518d\u66ff\u4f60\u5f00\u836f\u7597\u75be\u3002\u201d \u3000\u3000\u9752\u8863\u8001\u4eba\u592b\u53bb\u795e\u91c7\u7684\u76ee\u5149\uff0c\u7f13\u7f13\u5730\u8f6c\u5230\u90a3\u9752\u886b\u4eba\u7684\u8eab\u4e0a\uff0c\u5fae\u5fae\u9894\u9996\uff0c\u8868\u793a\u8c22\u610f\u3002 \u3000\u3000\u9752\u886b\u4e2d\u5e74\u4eba\u5927\u7ea6\u5bf9\u90a3\u7c92\u4eba\u5b57\u91d1\u4e39\uff0c\u5fc3\u4e2d\u4ecd\u6709\u4f59\u75bc\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e0d\u7528\u8c22\u6211\uff0c\u5982\u975e\u6211\u90a3\u592b\u4eba\uff0c\u5728\u4e0b\u5b9e\u662f\u820d\u4e0d\u5f97\u8ba9\u4f60\u670d\u7528\u4e00\u7c92\u3002\u201d \u3000\u3000\u9752\u8863\u8001\u4eba\u82e6\u7b11\u4e00\u4e0b\uff0c\u7f13\u7f13\u95ed\u4e0a\u53cc\u76ee\u3002 \u3000\u3000\u90a3\u4e2d\u5e74\u5987\u4eba\u7a81\u7136\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5927\u54e5\u554a\uff01\u4f60\u65e2\u7136\u6551\u4e86\u4ed6\uff0c\u53c8\u4f55\u5fc5\u4f24\u4ed6\u5462\uff1f\u201d \u3000\u3000\u9752\u886b\u4eba\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u8bf4\u7684\u90fd\u662f\u5b9e\u8bdd\uff0c\u6211\u70bc\u8fd9\u516b\u5b9a\u91d1\u4e39\uff0c\u65e9\u5df2\u5b89\u6392\u597d\u4e86\u7528\u5904\uff0c\u5982\u4eca\u5c11\u4e86\u4e00\u7c92\uff0c\u53ea\u597d\u53e6\u60f3\u522b\u7684\u529e\u6cd5\u5f25\u8865\u4e86\u3002\u201d \u3000\u3000\u90a3\u4e2d\u5e74\u5987\u4eba\u9053\uff1a\u201c\u5509\uff01\u5927\u54e5\u4e0d\u8981\u751f\u6c14\uff0c\u6211\u4e0d\u77e5\u4f60\u65e9\u5df2\u5b89\u6392\u4e86\u7528\u9014\u3002\u201d \u3000\u3000\u9752\u886b\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u65e2\u8ba9\u4ed6\u670d\u7528\u4e0b\u4e86\u4e00\u7c92\u516b\u5b9d\u91d1\u4e39\u4e38\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u6211\u4e5f\u8981\u8bbe\u6cd5\u628a\u4ed6\u7684\u75c5\u7597\u597d\u2026\u2026\u201d \u3000\u3000\u8bf4\u7740\u62b1\u8d77\u90a3\u9752\u8863\u8001\u4eba\uff0c\u8df3\u4e0a\u9a6c\u80cc\uff0c\u5411\u524d\u5954\u53bb\u3002 \u3000\u3000\u5929\u5230\u638c\u706f\u65f6\u5206\uff0c\u884c\u5230\u4e00\u5904\u5c0f\u9547\u3002 \u3000\u3000\u9752\u886b\u4e2d\u5e74\u4eba\u627e\u4e86\u4e00\u5904\u5ba2\u6808\uff0c\u628a\u90a3\u9752\u8863\u8001\u4eba\u5b89\u6392\u5728\u4e00\u4e2a\u53a2\u623f\u4e4b\u4e2d\u3002 \u3000\u3000\u90a3\u516b\u5b9d\u91d1\u4e39\u679c\u7136\u6709\u7740\u60ca\u4eba\u7684\u795e\u6548\uff0c\u81ea\u4ece\u8001\u4eba\u670d\u4e0b\u4e4b\u540e\uff0c\u795e\u667a\u7adf\u7136\u4fdd\u6301\u7740\u5b8c\u5168\u7684\u6e05\u9192\uff0c\u4f46\u5f62\u52bf\u5374\u53c8\u8feb\u4ed6\u95ed\u7740\u53cc\u76ee\uff0c\u88c5\u4f5c\u6655\u8ff7\u672a\u9192\u4e4b\u72b6\u3002 \u3000\u3000\u90a3\u9752\u886b\u4e2d\u5e74\u4eba\u5148\u66ff\u9752\u8863\u8001\u4eba\u628a\u8fc7\u8109\u640f\u4e4b\u540e\uff0c\u4e0d\u7981\u4e00\u76b1\u7709\u5934\uff0c\u4f46\u5374\u672a\u53d1\u8a00\uff0c\u5306\u5306\u8d77\u8eab\u800c\u53bb\u3002 \u3000\u3000\u90a3\u4e2d\u5e74\u5987\u4eba\u575a\u6301\u4ee5\u516b\u5b9d\u91d1\u4e39\uff0c\u6551\u90a3\u8001\u4eba\u4e4b\u547d\uff0c\u5df2\u77a7\u51fa\u4e08\u592b\u5fc3\u4e2d\u7684\u4e0d\u4e50\uff0c\u518d\u4e5f\u4e0d\u6562\u591a\u53e3\u3002\u76f4\u5f85\u4e08\u592b\u53bb\u540e\uff0c\u5979\u624d\u671b\u7740\u90a3\u84dd\u8863\u7ae5\u5b50\u8bf4\u9053\uff1a\u201c\u4f60\u7559\u8fd9\u91cc\u7167\u987e\u8fd9\u4f4d\u8001\u4f2f\u4f2f\u3002\u201d \u3000\u3000\u90a3\u84dd\u8863\u7ae5\u5b50\u5e94\u9053\uff1a\u201c\u4f2f\u6bcd\u653e\u5fc3\uff0c\u5c0f\u4f84\u81ea\u4f1a\u5f53\u5fc3\u3002\u201d \u3000\u3000\u90a3\u4e2d\u5e74\u592b\u4eba\u53f9\u606f\u4e00\u58f0\uff0c\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u5c31\u5728\u90a3\u4e2d\u5e74\u5987\u4eba\u53bb\u540e\uff0c\u9752\u8863\u8001\u4eba\u7a81\u7136\u7741\u5f00\u4e86\u53cc\u76ee\u3002 \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u559c\u9053\uff1a\u201c\u8001\u4f2f\u4f2f\uff0c\u4f60\u9192\u4e86\uff1f\u201d \u3000\u3000\u90a3\u9752\u8863\u8001\u4eba\u5728\u90a3\u84dd\u8863\u7ae5\u5b50\u7684\u8138\u4e0a\u6253\u91cf\u4e86\u4e00\u9635\u9053\uff1a\u201c\u6e05\u9192\u4e86\u3002\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u6211\u53bb\u544a\u8bc9\u4f2f\u6bcd\u3002\u201d \u3000\u3000\u9752\u8863\u8001\u4eba\u6025\u6025\u8bf4\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4e0d\u8981\u53bb\u3002\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u505c\u4e0b\u811a\u6b65\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9752\u8863\u8001\u4eba\u82e6\u7b11\u4e00\u4e0b\u9053\uff1a\u201c\u6211\u4eec\u5148\u8c08\u8c08\u5982\u4f55\u3002\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u6c89\u541f\u4e86\u4e00\u9635\u9053\uff1a\u201c\u597d\u5427\uff01\u201d \u3000\u3000\u9752\u8863\u8001\u4eba\u9053\uff1a\u201c\u6211\u60f3\u95ee\u4f60\u51e0\u4ef6\u4e8b\u4e0d\u77e5\u4f60\u53ef\u5426\u544a\u8bc9\u6211\uff1f\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u90a3\u8981\u770b\u4f60\u95ee\u4ec0\u4e48\u4e86\uff1f\u5982\u679c\u6211\u4e0d\u77e5\u9053\u7684\u4e8b\u60c5\u81ea\u7136\u662f\u6ca1\u6709\u6cd5\u5b50\u56de\u7b54\u4f60\u4e86\u3002\u201d \u3000\u3000\u9752\u8863\u8001\u4eba\u9053\uff1a\u201c\u81ea\u7136\u662f\u4f60\u77e5\u6653\u7684\u4e8b\u60c5\u3002\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u90a3\u4f60\u95ee\u5427\uff01\u201d \u3000\u3000\u9752\u8863\u8001\u4eba\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4f60\u90a3\u4f4d\u4f2f\u7236\uff0c\u53ef\u662f\u4e2d\u5dde\u4e09\u4fa0\u4e2d\u65b9\u4e8c\u4fa0\u65b9\u5929\u6210\u5417\uff1f\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u662f\u554a\uff01\u4f60\u8ba4\u8bc6\u4ed6\uff1f\u201d \u3000\u3000\u9752\u8863\u8001\u4eba\u9053\uff1a\u201c\u6211\u4e0d\u8ba4\u8bc6\uff0c\u4f46\u65b9\u4e8c\u4fa0\uff0c\u5927\u540d\u9f0e\u9f0e\uff0c\u5728\u4e0b\u53ea\u4e0d\u8fc7\u542c\u4eba\u8bf4\u8fc7\u4ed6\u7684\u5f62\u8c8c\u7f62\u4e86\u3002\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\u3002\u201d \u3000\u3000\u9752\u8863\u8001\u4eba\u9053\uff1a\u201c\u90a3\u4f4d\u7f8e\u4e3d\u7684\u4e2d\u5e74\u5987\u4eba\u60f3\u5fc5\u662f\u65b9\u4e8c\u4fa0\u7684\u592b\u4eba\uff1f\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u662f\u554a\uff01\u6b63\u662f\u6211\u7684\u4f2f\u6bcd\u3002\u201d \u3000\u3000\u9752\u8863\u8001\u4eba\u9053\uff1a\u201c\u4e45\u95fb\u5979\u7684\u5584\u540d\uff0c\u4eca\u65e5\u4e00\u89c1\u679c\u662f\u4e0d\u9519\u3002\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u6211\u90a3\u4f4d\u65b9\u4f2f\u6bcd\uff0c\u4e3a\u4eba\u6700\u662f\u5584\u826f\u4e86\uff0c\u4e00\u53ea\u9e1f\u513f\u53d7\u4e86\u4f24\uff0c\u5979\u5fc3\u4e2d\u4e5f\u662f\u4e0d\u5fcd\u3002\u201d \u3000\u3000\u9752\u8863\u8001\u4eba\u53f9\u9053\uff1a\u201c\u5979\u7684\u4ec1\u5584\u4e4b\u540d\uff0c\u6b66\u6797\u7686\u95fb\uff0c\u540d\u6c14\u5b9e\u4e0d\u5728\u65b9\u4e8c\u4fa0\u4e4b\u4e0b\u2026\u2026\u201d\u8bed\u58f0\u4e00\u987f\u9053\uff1a\u201c\u90a3\u4f4d\u7a7f\u52b2\u88c5\u7684\u7ae5\u5b50\u53ef\u662f\u65b9\u4e8c\u4fa0\u7684\u516c\u5b50\uff1f\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u662f\u7684\uff0c\u4e5f\u662f\u6211\u7684\u5144\u5f1f\u3002\u201d \u3000\u3000\u9752\u8863\u8001\u4eba\u9053\uff1a\u201c\u4f60\u662f\u8001\u5927\uff1f\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u867d\u662f\u5144\u5f1f\uff0c\u4f46\u5374\u975e\u4e00\u6bcd\u540c\u80de\uff0c\u6211\u662f\u5bc4\u5c45\u5728\u4f2f\u7236\u5bb6\u4e2d\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u9053\uff1a\u201c\u4f60\u4eca\u5e74\u51e0\u5c81\u4e86\uff1f\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u5341\u4e94\u5c81\uff0c\u6211\u90a3\u5144\u5f1f\u4e5f\u662f\u5341\u4e94\u5c81\uff0c\u4e0d\u8fc7\u6211\u5927\u4ed6\u4e09\u4e2a\u6708\u3002\u201d \u3000\u3000\u84dd\u8863\u8001\u4eba\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4ee4\u5c0a\u5462\uff1f\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u5bb6\u7236\u8fc7\u4e16\u5f88\u4e45\u4e86\u3002\u201d \u3000\u3000\u9752\u8863\u8001\u4eba\u9053\uff1a\u201c\u4ed6\u662f\u65b9\u4e8c\u4fa0\u7684\u4eb2\u5144\u5f1f\u5417\uff1f\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u662f\u7684\uff0c\u5bb6\u7236\u5c0f\u4f2f\u7236\u4e94\u5c81\uff0c\u4ed6\u4eec\u662f\u4e00\u6bcd\u540c\u80de\u3002\u201d \u3000\u3000\u9752\u8863\u8001\u4eba\u9053\uff1a\u201c\u4ee4\u5802\u5462\uff1f\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u5bb6\u6bcd\u54c0\u4f24\u8fc7\u751a\uff0c\u4e0d\u613f\u518d\u5728\u5c18\u4e16\u4e4b\u4e0a\uff0c\u672c\u8981\u4ee5\u8eab\u76f8\u6b89\uff0c\u4f46\u53c8\u6015\u6211\u5b64\u82e6\u65e0\u4f9d\uff0c\u56e0\u6b64\u4e94\u5e74\u524d\uff0c\u628a\u6211\u9001\u7ed9\u4f2f\u7236\u6536\u517b\uff0c\u81ea\u5df1\u524a\u53d1\u4e3a\u5c3c\uff0c\u8d85\u7136\u5c18\u5916\u3002\u201d \u3000\u3000\u9752\u8863\u8001\u4eba\u9053\uff1a\u201c\u4ee4\u5c0a\u6b63\u503c\u58ee\u5e74\u6709\u529b\u4e4b\u65f6\uff0c\u600e\u4e48\u4f1a\u7a81\u7136\u800c\u901d\u53bb\uff1f\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u665a\u8f88\u5c31\u4e0d\u6e05\u695a\u4e86\uff0c\u6211\u4e5f\u95ee\u8fc7\u4f2f\u6bcd\uff0c\u4f2f\u6bcd\u8bf4\u5979\u4e0d\u592a\u4e86\u7136\u3002\u201d \u3000\u3000\u9752\u8863\u8001\u4eba\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4f60\u5b66\u8fc7\u6b66\u529f\u6ca1\u6709\uff1f\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u5b66\u8fc7\uff0c\u4e0d\u8fc7\u6211\u559c\u6b22\u8bfb\u4e66\uff0c\u6240\u4ee5\u6b66\u529f\u65b9\u9762\uff0c\u5b66\u7684\u5f88\u5c11\u3002\u201d \u3000\u3000\u9752\u8863\u8001\u4eba\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u559c\u6b22\u5b66\u6b66\u529f\u5417\uff1f\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u8138\u4e0a\u63a0\u8fc7\u4e00\u62b9\u51c4\u51c9\u7684\u5fae\u7b11\uff0c\u9053\uff1a\u201c\u4f2f\u4f2f\u8bf4\u6211\u5929\u751f\u9aa8\u8d28\u5f88\u5f31\uff0c\u7ec3\u6b66\u4e5f\u96be\u767b\u5cf0\u9020\u6781\uff0c\u56e0\u6b64\uff0c\u4ed6\u4e0d\u4e3b\u5f20\u6211\u5b66\u6b66\u529f\uff0c\u5374\u66ff\u6211\u4e70\u4e86\u5f88\u591a\u4e66\u7c4d\uff0c\u8981\u6211\u9605\u8bfb\uff0c\u800c\u4e14\u8fd8\u66ff\u6211\u8bf7\u4e86\u4e24\u4e2a\u901a\u5112\uff0c\u6559\u5bfc\u4e8e\u6211\u3002\u201d \u3000\u3000\u9752\u8863\u8001\u4eba\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u8fd9\u4e5f\u5f88\u96be\u602a\u2026\u2026\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u6781\u4e3a\u806a\u660e\uff0c\u542c\u540e\u6014\u4e86\u4e00\u6014\u9053\uff1a\u201c\u8001\u4f2f\u4f2f\uff0c\u8bf4\u7684\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u9752\u8863\u8001\u4eba\u8f7b\u8f7b\u54b3\u4e86\u4e24\u58f0\u63a5\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u8bfb\u7684\u662f\u4ec0\u4e48\u4e66\uff1f\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u6211\u8bfb\u7684\u5f88\u535a\u6742\uff0c\u661f\u535c\u3001\u533b\u9053\uff0c\u65e0\u6240\u4e0d\u5305\u3002\u201d \u3000\u3000\u9752\u8863\u8001\u4eba\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u4f60\u90a3\u4f2f\u7236\u4e5f\u65e0\u610f\u4f7f\u4f60\u6b65\u5165\u4ec1\u9014\u4e86\uff1f\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u90a3\u6211\u5c31\u4e0d\u6e05\u695a\u4e86\u3002\u201d \u3000\u3000\u9752\u8863\u8001\u4eba\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u89c9\u5f97\u8001\u592b\u7684\u4eba\u5982\u4f55\uff1f\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u4f60\u597d\u50cf\u77e5\u6653\u5f88\u591a\u7684\u4e8b\u60c5\u3002\u201d \u3000\u3000\u9752\u886b\u8001\u4eba\u9053\uff1a\u201c\u4f60\u65e2\u7136\u89c9\u5f97\u8001\u592b\u4e0d\u9519\uff0c\u53ef\u613f\u542c\u4ece\u8001\u592b\u7684\u8bdd\u5417\uff1f\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u6c89\u541f\u4e86\u4e00\u9635\u9053\uff1a\u201c\u6211\u5f88\u60f3\u542c\u4f60\u7684\u8bdd\u3002\u201d \u3000\u3000\u9752\u8863\u8001\u4eba\u9053\uff1a\u201c\u90a3\u5f88\u597d\uff0c\u6211\u5148\u95ee\u4f60\u4e00\u4ef6\u4e8b\uff0c\u4f60\u8981\u786e\u5b9e\u56de\u7b54\u6211\u3002\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u554a\uff1f\u201d \u3000\u3000\u9752\u8863\u8001\u4eba\u9053\uff1a\u201c\u4f60\u662f\u5426\u5f88\u60f3\u5b66\u6b66\u529f\uff1f\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u5706\u7741\u7740\u4e00\u53cc\u773c\u775b\uff0c\u671b\u7740\u90a3\u9752\u8863\u8001\u4eba\uff0c\u826f\u4e45\u4e4b\u540e\uff0c\u624d\u7f13\u7f13\u4ed6\u8bf4\u9053\uff1a\u201c\u8fd8\u662f\u4e0d\u5b66\u7b97\u4e86\u3002\u201d \u3000\u3000\u9752\u8863\u8001\u4eba\u5728\u4ed6\u795e\u60c5\u6115\u7136\u4e4b\u95f4\uff0c\u7a81\u7136\u53d1\u89c9\u90a3\u84dd\u8863\u7ae5\u5b50\u76ee\u5149\u4e2d\uff0c\u9690\u9690\u95f4\u542b\u8574\u7740\u4e00\u80a1\u8083\u715e\u4e4b\u6c14\uff0c\u4e0d\u7981\u5fc3\u5934\u6026\u7136\u4e00\u52a8\uff0c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u5982\u82e5\u6211\u5b66\u4e60\u6b66\u529f\uff0c\u90a3\u5c31\u6c38\u8fdc\u4e5f\u65e0\u6cd5\u8ffd\u4e0a\u6211\u7684\u5144\u5f1f\uff0c\u4e0d\u5982\u5f03\u6b66\u4ece\u6587\uff0c\u5404\u8c0b\u6210\u5c31\u3002\u201d \u3000\u3000\u9752\u8863\u8001\u4eba\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u5916\u8c8c\u67d4\u548c\u5185\u5fc3\u5374\u662f\u5f3a\u5f97\u5f88\u3002\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u51c4\u7136\u4e00\u7b11\uff0c\u6b32\u8a00\u53c8\u6b62\u3002 \u3000\u3000\u9752\u8863\u8001\u4eba\u53f9\u606f\u4e00\u58f0\u63a5\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u5982\u82e5\u6709\u4e00\u4e2a\u4eba\uff0c\u80fd\u628a\u4f60\u9020\u5c31\u6210\u5f53\u4eca\u6b66\u6797\u4e2d\u7b2c\u4e00\u9ad8\u4eba\uff0c\u4f60\u613f\u4e0d\u613f\u610f\u5b66\u6b66\u5462\uff1f\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u90a3\u6211\u81ea\u7136\u613f\u4e86\uff0c\u53ef\u662f\u8fd9\u662f\u4e0d\u53ef\u80fd\u7684\u4e8b\u554a\uff01\u201d \u3000\u3000\u9752\u8863\u8001\u4eba\u7b11\u9053\uff1a\u201c\u8001\u592b\u6709\u4e00\u4f4d\u597d\u53cb\uff0c\u88ab\u5f53\u4eca\u79f0\u4e3a\u7b2c\u4e00\u9ad8\u4eba\uff0c\u65e5\u540e\uff0c\u8001\u592b\u4ecb\u7ecd\u4f60\u53bb\u89c1\u4ed6\uff0c\u4ed6\u5b9a\u4f1a\u6536\u4f60\u4e3a\u5f92\uff0c\u4e0d\u8fc7\uff0c\u6211\u8981\u5148\u4f20\u4f60\u57fa\u672c\u5410\u7eb3\u4e4b\u672f\uff0c\u4e0d\u77e5\u4f60\u613f\u4e0d\u613f\u5b66\uff1f\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u8138\u4e0a\u5448\u73b0\u51fa\u4e00\u4efd\u559c\u60a6\u4e4b\u60c5\uff0c\u4f46\u5374\u53c8\u8fc5\u5feb\u6d88\u5931\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u627e\u6211\u5144\u5f1f\u5462\uff1f\u4ed6\u6bd4\u6211\u5f3a\u5f97\u591a\uff0c\u4e0d\u8bba\u4f53\u80fd\u3001\u8d44\u8d28\u90fd\u662f\u4e0a\u597d\u7684\u7ec3\u6b66\u4eba\u3002\u201d \u3000\u3000\u9752\u8863\u8001\u4eba\u7b11\u9053\uff1a\u201c\u90a3\u662f\u4ed6\u4eec\u7684\u770b\u6cd5\uff0c\u8001\u592b\u7684\u770b\u4e0d\u5219\u4e0d\u540c\u4e86\u3002\u201d \u3000\u3000\u9752\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4f60\u548c\u522b\u4eba\u7684\u770b\u6cd5\u4e0d\u540c\u5462\uff1f\u201d \u3000\u3000\u9752\u8863\u8001\u4eba\u9053\uff1a\u201c\u56e0\u4e3a\u8001\u592b\u770b\u7684\u662f\u7075\u6167\u7980\u8d4b\u548c\u6c14\u5ea6\uff0c\u90a3\u5c31\u4e0d\u662f\u4e00\u822c\u4eba\u80fd\u591f\u770b\u51fa\u6765\u4e86\uff0c\u4ed6\u4eec\u53ea\u80fd\u770b\u9aa8\u9abc\uff0c\u4f53\u8d28\u3002\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u7b11\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\u2026\u2026\u201d\u8bed\u58f0\u5fae\u5fae\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u54b1\u4eec\u8bf4\u4e86\u534a\u5929\uff0c\u6211\u8fd8\u672a\u8bf7\u6559\u4f60\u8001\u4eba\u5bb6\u8d35\u59d3\u3002\u201d \u3000\u3000\u9752\u8863\u8001\u4eba\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u8001\u592b\u59d3\u9648\uff0c\u4f60\u53eb\u6211\u4e1c\u8033\u8001\u4eba\u5c31\u662f\u3002\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u4e1c\u8033\u8001\u4eba\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u201d\u8bed\u58f0\u5fae\u5fae\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u4e0d\u80fd\u628a\u8001\u592b\u7684\u59d3\u540d\uff0c\u544a\u8bc9\u4f60\u7684\u4f2f\u7236\u4f2f\u6bcd\u3002\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u56e0\u4e3a\u8001\u592b\u843d\u96be\u4e8e\u6b64\uff0c\u4e0d\u613f\u4f7f\u59d3\u6c0f\u4e3a\u4eba\u6240\u77e5\u3002\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u5982\u82e5\u4ed6\u4eec\u95ee\u5230\u4f60\u7684\u59d3\u540d\u65f6\uff0c\u4f60\u53c8\u5982\u4f55\u7b54\u590d\u5462\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u90a3\u4f60\u5c31\u8bf4\u6211\u59d3\u4e1c\u5427\uff01\u201d\u957f\u957f\u53f9\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u53ef\u5426\u628a\u4f60\u7684\u540d\u5b57\u544a\u8bc9\u6211\uff1f\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u81ea\u7136\u53ef\u4ee5\u4e86\uff0c\u6211\u53eb\u65b9\u96ea\u5b9c\uff0c\u5b66\u540d\u65b9\u745c\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u8c01\u7ed9\u4f60\u8d77\u7684\u540d\u5b50\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u6211\u6bcd\u4eb2\u8d77\u7684\uff0c\u6211\u7236\u4eb2\u53eb\u65b9\u5929\u5b9c\uff0c\u6bcd\u4eb2\u95fa\u8bb3\u96ea\u83b2\uff0c\u5988\u5988\u7ed9\u6211\u8d77\u7684\u8fd9\u4e2a\u540d\u5b57\uff0c\u4e5f\u8bb8\u542b\u6709\u7eaa\u5ff5\u5bb6\u7236\u4e4b\u610f\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\u3002\u201d \u3000\u3000\u8c08\u8bdd\u4e4b\u95f4\uff0c\u7a81\u95fb\u5f97\u6b65\u5c65\u58f0\u4f20\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4e1c\u8033\u8001\u4eba\u6025\u95ed\u53cc\u76ee\uff0c\u88c5\u4f5c\u6655\u8ff7\u4e4b\u6001\u3002 \u3000\u3000\u53ea\u542c\u6728\u95e8\u5440\u7136\uff0c\u65b9\u5929\u6210\u63a8\u95e8\u800c\u4eba\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u6b20\u8eab\u8bf4\u9053\uff1a\u201c\u89c1\u8fc7\u4f2f\u7236\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u624b\u4e2d\u63d0\u7740\u836f\u7269\uff0c\u9053\uff1a\u201c\u5b69\u5b50\u4f60\u51fa\u53bb\u5427\uff0c\u6211\u8981\u548c\u4ed6\u8c08\u8c08\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5e94\u4e86\u4e00\u58f0\uff0c\u9000\u51fa\u95e8\u5916\u3002 \u3000\u3000\u65b9\u5929\u6210\u4e3e\u624b\u8f7b\u8f7b\u5730\u5728\u4e1c\u8033\u8001\u4eba\u80f8\u524d\u62cd\u4e86\u4e00\u638c\uff0c\u9053\uff1a\u201c\u4f60\u8be5\u6e05\u9192\u4e86\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u7741\u5f00\u53cc\u76ee\uff0c\u9053\uff1a\u201c\u591a\u8c22\u6551\u547d\u4e4b\u6069\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4f60\u7684\u8109\u8c61\u5f88\u4e71\uff0c\u4f7f\u4eba\u77a7\u4e0d\u51fa\u75c5\u4ece\u4f55\u800c\u6765\uff0c\u6211\u5df2\u7ecf\u66ff\u4f60\u6293\u4e86\u51e0\u4ed8\u836f\u6765\uff0c\u4f46\u6211\u4ecd\u5e0c\u671b\u4f60\u80fd\u6e05\u9192\uff0c\u4e5f\u597d\u95ee\u660e\u4f60\u7684\u75c5\uff0c\u518d\u51b3\u5b9a\u7528\u54ea\u4e00\u4ed8\u836f\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u8001\u673d\u4e4b\u75c5\u56e0\u5e74\u8fc8\u4e4b\u6545\uff0c\u53ea\u6015\u5f88\u96be\u533b\u597d\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u63a5\u9053\uff1a\u201c\u4f60\u597d\u50cf\u5f88\u8f7b\u6de1\u81ea\u5df1\u7684\u751f\u6b7b\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u877c\u8681\u4e14\u751f\uff0c\u4f55\u51b5\u5728\u4e0b\u662f\u4eba\u5462\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u90a3\u4f60\u5c31\u4ed4\u7ec6\u5730\u544a\u8bc9\u6211\u75c5\u4ece\u4f55\u6765\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u6c89\u541f\u4e86\u7247\u523b\uff0c\u9053\uff1a\u201c\u8d77\u56e0\u597d\u50cf\u662f\u53d7\u4e86\u98ce\u5bd2\u4fb5\u88ad\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u591f\u4e86\u2026\u2026\u201d\u5c31\u4e09\u5305\u836f\u4e2d\u53d6\u51fa\u4e00\u5305\uff0c\u63a5\u9053\uff1a\u201c\u8fd9\u5305\u836f\u4e2d\uff0c\u9644\u6709\u836f\u5355\uff0c\u4f60\u81ea\u5df1\u7167\u65b9\u518d\u5403\u4e24\u4ed8\uff0c\u5c31\u53ef\u4ee5\u597d\u4e86\uff0c\u6211\u8fd8\u6709\u4e8b\uff0c\u4e0d\u80fd\u7559\u6b64\u4e3a\u4f60\u628a\u8109\u4e86\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u5509\uff01\u4f60\u7559\u8001\u673d\u5728\u6b64\uff0c\u53ea\u6015\u8001\u673d\u4e5f\u65e0\u6cd5\u6d3b\u4e0b\u53bb\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u8001\u673d\u5df2\u5165\u98ce\u70db\u6b8b\u5e74\u4e4b\u5883\uff0c\u65e2\u65e0\u53ef\u5f52\u4e4b\u5bb6\uff0c\u53c8\u65e0\u94f6\u4e24\uff0c\u75c5\u60c5\u968f\u65f6\u53ef\u53d1\uff0c\u5c82\u4e0d\u662f\u96be\u4ee5\u6d3b\u5f97\u4e0b\u53bb\u5417\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u597d\u5427\uff01\u6211\u52a9\u4f60\u5341\u4e24\u94f6\u5b50\uff0c\u8db3\u591f\u4f60\u4e70\u5904\u623f\u5b50\uff0c\u82e6\u5ea6\u6b8b\u5e74\u4e86\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u6447\u6447\u5934\u9053\uff1a\u201c\u8fd9\u529e\u6cd5\u4e5f\u4e0d\u59a5\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u4e00\u76b1\u7709\uff0c\u4e0d\u8010\u70e6\u4ed6\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u5982\u4f55\u624d\u6210\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u8001\u673d\u867d\u7136\u5e38\u75c5\uff0c\u4f46\u4e0d\u75c5\u65f6\u8eab\u4f53\u8fd8\u5f88\u5065\u58ee\uff0c\u5982\u82e5\u8001\u592b\u80fd\u591f\u8ffd\u968f\u5de6\u53f3\uff0c\u6267\u97ad\u968f\u8e6c\u2026\u2026\u201d \u3000\u3000\u65b9\u5929\u6210\u6447\u6447\u5934\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u884c\uff0c\u5728\u4e0b\u5954\u8d70\u6c5f\u6e56\uff0c\u5e2d\u4e0d\u6687\u6696\uff0c\u6709\u65f6\u8fde\u591c\u8d76\u8def\u6570\u65e5\u4e0d\u7720\uff0c\u5c82\u662f\u4f60\u8fd9\u7b49\u8001\u5f31\u7684\u4f53\u80fd\uff0c\u80fd\u591f\u80dc\u4efb\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u9601\u4e0b\u5954\u8d70\u6c5f\u6e56\uff0c\u96be\u9053\u592b\u4eba\u548c\u4ee4\u90ce\uff0c\u4e5f\u8981\u5728\u6c5f\u6e56\u4e4b\u4e0a\u5954\u8d70\u5417\uff0c\u8001\u673d\u7559\u5728\u5e9c\u4e0a\uff0c\u4f5c\u4e00\u8001\u5de5\uff0c\u6df7\u53e3\u996d\u5403\uff0c\u96be\u9053\u9601\u4e0b\u5c31\u4e0d\u80af\u7b54\u5e94\u5417\uff1f\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4f55\u51b5\uff0c\u8001\u673d\u8bfb\u4e86\u4e0d\u5c11\u8bd7\u4e66\uff0c\u5de5\u4f59\u4e4b\u6687\uff0c\u4e5f\u597d\u4e3a\u4ee4\u90ce\u8bb2\u4e9b\u8bd7\u6587\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u5fc3\u4e2d\u4e00\u52a8\uff0c\u9053\uff1a\u201c\u539f\u6765\u4f60\u8fd8\u662f\u4e00\u4f4d\u9971\u5b66\u7684\u901a\u5112\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u4e0d\u6562\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u9601\u4e0b\u8bfb\u7684\u662f\u4ec0\u4e48\u4e66\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u8001\u673d\u65c1\u52a1\u6742\u5b66\uff0c\u4e94\u7ecf\u56db\u4e66\u4e4b\u5916\uff0c\u904d\u53ca\u661f\u535c\u533b\u9053\uff0c\u4f5b\u95e8\u7ecf\u6587\uff0c\u4ea6\u4f1a\u6d89\u730e\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u4f60\u4e5f\u901a\u8fbe\u533b\u7406\u4e86\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u7565\u61c2\u4e00\u4e8c\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u5f88\u597d\uff0c\u5f85\u5728\u4e0b\u548c\u592b\u4eba\u5546\u91cf\u4e00\u4e0b\uff0c\u518d\u7b54\u590d\u8001\u524d\u8f88\u3002\u201d \u3000\u3000\u5c31\u5728\u65b9\u5929\u6210\u53bb\u540e\u4e0d\u4e45\uff0c\u90a3\u84dd\u8863\u7ae5\u5b50\u91cd\u53c8\u884c\u4e86\u56de\u6765\uff0c\u5954\u5230\u4e1c\u8033\u8001\u4eba\u8eab\u524d\uff0c\u9053\uff1a\u201c\u4e1c\u8001\u4f2f\u6015\uff0c\u4f60\u53ef\u662f\u8981\u8d70\u4e86\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u53ea\u8981\u4f60\u90a3\u4f2f\u6bcd\u7b54\u5e94\uff0c\u6211\u5c31\u53ef\u4ee5\u7559\u5728\u8fd9\u91cc\u4e0d\u8d70\u4e86\u3002\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u597d\uff0c\u90a3\u6211\u5c31\u53bb\u544a\u8bc9\u6015\u6bcd\u53bb\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u4e0d\u7528\u4e86\uff0c\u4f60\u90a3\u4f2f\u6bcd\u4e43\u5929\u4e0b\u81f3\u5584\u81f3\u4ec1\u7684\u4eba\uff0c\u5c82\u6709\u4e0d\u5141\uff1f\u5012\u662f\u6709\u4e00\u4ef6\u91cd\u8981\u4e8b\uff0c\u6211\u975e\u5f97\u8be6\u7ec6\u4ed6\u8bf4\u660e\u4e0d\u53ef\u3002\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u4f60\u5b66\u6b66\u7684\u4e8b\u4e0d\u80fd\u6cc4\u9732\u51fa\u53bb\u3002\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u8fde\u6211\u90a3\u4f2f\u6bcd\u4e5f\u4e0d\u80fd\u8bb2\u5417\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u6700\u597d\u522b\u8bb2\uff0c\u56e0\u4e3a\u6b64\u4e8b\u5982\u82e5\u88ab\u4f60\u6015\u7236\u77e5\u9053\uff0c\u8bf4\u4e0d\u5b9a\u4ed6\u8981\u53d6\u8001\u673d\u4e4b\u547d\uff0c\u81f3\u4f4e\u9650\u5ea6\uff0c\u4ed6\u8981\u628a\u6211\u9010\u79bb\u6b64\u5730\uff0c\u81ea\u7136\u4f60\u7684\u6b66\u529f\u4e5f\u5b66\u4e0d\u6210\u4e86\u3002\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u5982\u662f\u4f60\u90a3\u6b66\u529f\u4e0d\u5982\u6211\u4f2f\u7236\uff0c\u90a3\u6211\u5c31\u4e0d\u7528\u5b66\u4e86\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u81ea\u7136\u662f\u6bd4\u4ed6\u9ad8\u660e\u2026\u2026\u201d\u8bed\u58f0\u987f\u4e00\u987f\uff0c\u9053\uff1a\u201c\u4fd7\u8a00\u9053\uff0c\u53ea\u6709\u72b6\u5143\u5b66\u751f\uff0c\u800c\u65e0\u72b6\u5143\u8001\u5e08\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u77aa\u5728\u90a3\u84dd\u8863\u7ae5\u5b50\u7684\u8138\u4e0a\uff0c\u77a7\u4e86\u826f\u4e45\uff0c\u624d\u9053\uff1a\u201c\u5b69\u5b50\u4f60\u5fc5\u987b\u4fe1\u4efb\u6211\uff0c\u624d\u80fd\u591f\u5b66\u5f97\u7b2c\u4e00\u6d41\u7684\u6b66\u529f\u554a\uff01\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u53c8\u4f3c\u660e\u767d\u53c8\u4f3c\u4e0d\u61c2\u5730\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u597d\u5427\uff0c\u6211\u4fe1\u4efb\u4f60\u5c31\u662f\u3002\u201d\u4e1c\u8033\u8001\u4eba\u9053\uff1a \u3000\u3000\u201c\u597d\uff01\u4f60\u5c31\u5148\u51fa\u53bb\u5427\uff01\u4f60\u6015\u7236\u662f\u5426\u7559\u6211\u5728\u6b64\uff0c\u53ea\u6015\u5c31\u8981\u51b3\u5b9a\u4e86\u3002\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u5fae\u5fae\u4e00\u7b11\uff0c\u4f9d\u8a00\u9000\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u679c\u7136\uff0c\u7247\u523b\u4e4b\u540e\uff0c\u65b9\u5929\u6210\u5e26\u7740\u592b\u4eba\uff0c\u4e00\u9f50\u5230\u6b64\u3002 \u3000\u3000\u65b9\u5929\u6210\u8f7b\u8f7b\u5730\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u5df2\u548c\u592b\u4eba\u8c08\u8fc7\uff0c\u5979\u5f88\u5e0c\u671b\u4f60\u7559\u5728\u6b64\u2026\u2026\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u76ee\u5149\u8f6c\u5230\u65b9\u592b\u4eba\u7684\u8eab\u4e0a\uff0c\u9053\uff1a\u201c\u592b\u4eba\u6551\u547d\u4e4b\u6069\uff0c\u8001\u6c49\u8fd8\u672a\u62a5\u7b54\uff0c\u4eca\u53c8\u8499\u6536\u7559\u8001\u6c49\uff0c\u9686\u60c5\u539a\u8c0a\uff0c\u9ad8\u8584\u4e91\u5929\uff0c\u4f46\u5f97\u8001\u6c49\u529b\u80fd\u6240\u5c3d\uff0c\u5fc5\u5c06\u7aed\u5c3d\u7ef5\u8584\uff0c\u62a5\u9686\u60c5\u4e8e\u4e07\u4e00\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u6211\u4e08\u592b\u5728\u6c5f\u6e56\u9887\u6709\u4fa0\u540d\uff0c\u8def\u89c1\u4e0d\u5e73\u62d4\u5200\u76f8\u52a9\uff0c\u4e5f\u662f\u5e73\u5e38\u7684\u4e8b\uff0c\u4f55\u51b5\uff0c\u8001\u4f2f\u4f2f\u5982\u8bb8\u5e74\u7eaa\uff0c\u5c31\u7b97\u6ca1\u6709\u751f\u75c5\uff0c\u6211\u4eec\u4e5f\u8be5\u7167\u987e\u4e00\u4e8c\uff0c\u8001\u4f2f\u4f2f\u5df2\u7ecf\u662f\u5927\u75c5\u5728\u8eab\u7684\u4eba\u4e86\uff0c\u8fd9\u4e00\u70b9\uff0c\u6211\u4eec\u81ea\u662f\u5e94\u8be5\u6548\u52b3\u624d\u662f\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u592b\u4eba\u8a00\u91cd\u4e86\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u9053\uff1a\u201c\u4f60\u73b0\u5728\u75c5\u60c5\u5982\u4f55\u4e86\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u5927\u89c1\u597d\u8f6c\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u9053\uff1a\u201c\u6211\u4eec\u5f88\u60f3\u5e26\u7740\u4f60\u540c\u884c\uff0c\u4f46\u4f60\u7684\u75c5\u60c5\u8fd8\u672a\u5b8c\u5168\u5eb7\u590d\uff0c\u8bf4\u4e0d\u5f97\uff0c\u53ea\u597d\u7559\u4f60\u5728\u6b64\u4e86\u2026\u2026\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u5fae\u5fae\u4e00\u6014\uff0c\u9053\uff1a\u201c\u7559\u6211\u5728\u6b64\uff1f\u201d \u3000\u3000\u65b9\u592b\u4eba\u9053\uff1a\u201c\u662f\u7684\uff0c\u7b49\u6211\u4eec\u56de\u7a0b\u4e4b\u65f6\uff0c\u518d\u6765\u6b64\u5730\u63a5\u4f60\u2026\u2026\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u8001\u6c49\u6597\u80c6\u95ee\u592b\u4eba\u4e00\u53e5\u8bdd\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u9053\uff1a\u201c\u4ec0\u4e48\u8bdd\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u4e24\u4f4d\u53ef\u662f\u8d74\u4ed6\u4eba\u4e4b\u7ea6\u5417\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8001\u4e08\u4f55\u4ee5\u77e5\u6653\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u5728\u4e0b\u9ed8\u5bdf\u60c5\u52bf\u6240\u5f97\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u7a81\u7136\u4e00\u76b1\u53cc\u7709\uff0c\u4f4e\u58f0\u5bf9\u592b\u4eba\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u53bb\u5427\u2026\u2026\u201d\u7275\u7740\u65b9\u592b\u4eba\u6025\u6025\u800c\u53bb\u3002 \u3000\u3000\u7247\u523b\u4e4b\u540e\uff0c\u90a3\u84dd\u8863\u5c11\u5e74\u53c8\u5355\u72ec\u5954\u4eba\u4e1c\u8033\u8001\u4eba\u7684\u623f\u4e2d\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u600e\u4e48\u5f97\u7f6a\u4e86\u6211\u6015\u7236\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u5fae\u5fae\u4e00\u6014\u7b11\u9053\uff1a\u201c\u4e0d\u5f97\u7f6a\u4e5f\u4e0d\u6210\u554a\u2026\u2026\u201d\u8bed\u58f0\u4e00\u987f\uff0c\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u53ef\u66fe\u542c\u8bf4\u4f2f\u7236\u8bf4\u6211\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u6211\u542c\u5230\u4ed6\u8bf4\u4f2f\u6bcd\u2026\u2026\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u6014\u4e86\u4e00\u5f81\uff0c\u9053\uff1a\u201c\u4ed6\u5982\u4f55\u8bf4\u90a3\u65b9\u592b\u4eba\uff1f\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u90a3\u4f2f\u7236\u7d20\u6765\u5bf9\u6211\u4f2f\u6bcd\u656c\u91cd\uff0c\u7eb5\u7136\u6709\u4ec0\u4e48\u4e8b\uff0c\u4e5f\u4e0d\u4f1a\u5bf9\u5979\u5389\u8a00\u58f0\u8272\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u957f\u957f\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5982\u82e5\u65b9\u592b\u4eba\u5e94\u8001\u673d\u53d7\u4e86\u90a3\u65b9\u5927\u4fa0\u7684\u8d23\u9a82\uff0c\u90a3\u5c31\u975e\u8001\u673d\u5fc3\u613f\u4e86\u3002\u201d \u3000\u3000\u8bed\u58f0\u4e00\u9882\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u4f60\u90a3\u4f2f\u7236\u6bcd\uff0c\u5df2\u51b3\u5b9a\u628a\u8001\u673d\u7559\u5728\u957f\u5b89\uff0c\u4f60\u5fc5\u5f97\u60f3\u4e2a\u6cd5\u5b50\uff0c\u4e5f\u7559\u5728\u6b64\u5730\u624d\u6210\u2026\u2026\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u73b0\u5728\u4f60\u4e0d\u7528\u7559\u8fd9\u91cc\u4e86\uff0c\u6211\u90a3\u4f2f\u7236\u8bf4\u4f2f\u6bcd\uff0c\u4e5f\u5c31\u662f\u4e3a\u4e86\u6b64\u4e8b\u3002\u201d \u3000\u3000\u201c\u6211\u90a3\u4f4d\u4ec1\u6148\u7684\u4f2f\u6bcd\uff0c\u89c9\u7740\u628a\u4f60\u7559\u5728\u6b64\u5730\u5f88\u5371\u9669\uff0c\u4e07\u4e00\u4f60\u75c5\u52bf\u53d1\u4f5c\u4e86\uff0c\u65e0\u4eba\u7167\u987e\uff0c\u5c82\u4e0d\u662f\u7b49\u4e8e\u6ca1\u6709\u6551\u4f60\uff0c\u56e0\u6b64\uff0c\u4e3b\u5f20\u5e26\u4f60\u540c\u53bb\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u4f60\u4f2f\u7236\u53ef\u662f\u4e0d\u540c\u610f\u5417\uff1f\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u4f2f\u7236\u4e3a\u6b64\u4e8b\u8d23\u602a\u4f2f\u6bcd\u7ba1\u4e8b\u592a\u591a\uff0c\u4f46\u4ecd\u7136\u7b54\u5e94\u4f2f\u6bcd\u7684\u8bf7\u6c42\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u6c89\u541f\u4e86\u4e00\u9635\u9053\uff1a\u201c\u5176\u5b9e\u53ea\u8981\u4f60\u80fd\u7559\u4e0b\uff0c\u54b1\u4eec\u4e0d\u53bb\u8fd8\u597d\u4e9b\u3002\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4f60\u53ef\u662f\u4e0d\u613f\u53bb\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u7f13\u7f13\u4ed6\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u548c\u4f60\u6015\u7236\u4f2f\u6bcd\u8d70\u5728\u4e00\u8d77\uff0c\u8001\u673d\u5c82\u4e0d\u662f\u65e0\u6cd5\u4f20\u4f60\u6b66\u529f\u4e86\u5417\uff1f\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u6211\u662f\u975e\u5f97\u5b66\u4f60\u7684\u6b66\u529f\u4e0d\u53ef\u4e86\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u4f60\u53ef\u662f\u4e0d\u613f\u610f\u5417\uff1f\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u7b54\u5e94\u4f60\u4e86\uff0c\u5c82\u53ef\u53cd\u6094\uff0c\u5982\u662f\u5728\u9014\u4e2d\u65e0\u6cd5\u4f20\u6211\uff0c\u7b49\u6211\u4eec\u56de\u5230\u6545\u5c45\u4e4b\u540e\uff0c\u4f60\u518d\u4f20\u6211\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u90a3\u4e0d\u6210\uff0c\u6211\u65e2\u8981\u4f20\u4f60\u6b66\u529f\uff0c\u81ea\u7136\u662f\u8d8a\u5feb\u8d8a\u597d\u3002\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u6653\u54b1\u4eec\u8981\u51e0\u65f6\u52a8\u8eab\uff1f\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u4f3c\u4e4e\u662f\u660e\u65e5\u4e00\u65e9\u52a8\u8eab\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u597d\uff01\u4f60\u4eca\u591c\u4e09\u66f4\u4e4b\u540e\uff0c\u5230\u6211\u623f\u4e2d\uff0c\u4f46\u8981\u9690\u79d8\u884c\u52a8\uff0c\u4e0d\u8981\u8ba9\u4f60\u4f2f\u7236\u77e5\u9053\u3002\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u597d\u5427\uff0c\u6211\u4eca\u591c\u4e09\u66f4\u8fc7\u6765\u3002\u201d\u8a00\u7f62\uff0c\u9000\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u591c\u665a\u4e09\u66f4\uff0c\u90a3\u84dd\u8863\u7ae5\u5b50\u679c\u7136\u5982\u7ea6\u800c\u6765\u3002 \u3000\u3000\u4e1c\u8033\u8001\u4eba\u5f00\u59cb\u6559\u4ed6\u9759\u5750\u5410\u7eb3\u4e4b\u6cd5\u3002 \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u4eba\u6781\u806a\u660e\uff0c\u4e1c\u8033\u8001\u4eba\u6240\u6388\u7684\u5410\u7eb3\u672f\uff0c\u4e43\u662f\u4e00\u79cd\u4e0a\u4e58\u5185\u529f\u521d\u6b65\u5960\u57fa\u5de5\u592b\uff0c\u547c\u5438\u4e4b\u95f4\uff0c\u5185\u8179\u52a8\u4f5c\u5341\u5206\u590d\u6742\uff0c\u4f46\u90a3\u7bee\u8863\u7ae5\u5b50\u7adf\u5728\u4e94\u66f4\u4e4b\u524d\uff0c\u5168\u90e8\u5b66\u4f1a\u3002 \u3000\u3000\u4e1c\u8033\u8001\u4eba\u773c\u770b\u90a3\u84dd\u8863\u7ae5\u5b50\uff0c\u5728\u4e24\u4e2a\u66f4\u6b21\u65f6\u95f4\u4e2d\uff0c\u5b66\u4f1a\u4e86\u4e0a\u4e58\u7684\u5185\u529f\u5960\u57fa\u4e4b\u6cd5\uff0c\u5fc3\u4e2d\u5927\u529b\u9ad8\u5174\uff0c\u5f53\u4e0b\u5c31\u8bf4\uff1a\u201c\u5b69\u5b50\uff0c\u8bb0\u4f4f\uff0c\u4ece\u4eca\u65e5\u5f00\u59cb\uff0c\u6bcf\u5929\u5fc5\u9700\u8981\u62bd\u51fa\u4e00\u4e2a\u65f6\u8fb0\u65f6\u95f4\uff0c\u6253\u5750\u8fd0\u529f\uff0c\u6211\u5982\u80fd\u968f\u4f60\u540c\u884c\uff0c\u81ea\u4f1a\u8bbe\u6cd5\u6307\u5bfc\u4f60\uff0c\u5982\u662f\u4e07\u4e00\u4e0d\u80fd\u540c\u884c\uff0c\u4f60\u66f4\u8981\u523b\u82e6\u7528\u529f\uff0c\u4e0d\u8bba\u4f60\u5728\u4f55\u5904\uff0c\u4f55\u5730\uff0c\u6211\u90fd\u4f1a\u627e\u5230\u4f60\uff0c\u4e0d\u8fc7\u6b64\u4e8b\u8981\u5343\u4e07\u9690\u79d8\uff0c\u4e0d\u8981\u8ba9\u522b\u4eba\u77e5\u9053\uff0c\u5305\u62ec\u4f60\u7684\u4f2f\u6bcd\u4f2f\u7236\uff0c\u548c\u4f60\u5144\u5f1f\u3002\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u6c89\u541f\u4e86\u4e00\u9635\u9053\uff1a\u201c\u6211\u5fc3\u4e2d\u6709\u4e00\u70b9\u4e0d\u89e3\u4e4b\u8c1c\uff0c\u4e0d\u77e5\u5f53\u4e0d\u5f53\u95ee\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u53ef\u4ee5\u95ee\uff0c\u4f60\u8bf4\u5427\uff01\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u4f60\u4f20\u6211\u6b66\u529f\u662f\u597d\u4e8b\u8fd8\u662f\u574f\u4e8b\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u81ea\u7136\u4e0d\u662f\u574f\u4e8b\u3002\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u65e2\u7136\u662f\u597d\u4e8b\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u8ba9\u522b\u4eba\u77e5\u9053\u5462\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u56e0\u4e3a\uff0c\u4f60\u5b66\u7684\u662f\u5929\u4e0b\u7b2c\u4e00\u7b49\u7684\u6b66\u529f\uff0c\u4e0d\u8bba\u4f55\u4eba\uff0c\u5c24\u5176\u5b66\u6b66\u7684\u4eba\uff0c\u90fd\u4e0d\u60f3\u4f60\u6bd4\u4ed6\u4eec\u5f3a\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u70b9\u70b9\u5934\u9053\uff1a\u201c\u55ef\uff01\u4e0d\u9519\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u542c\u6211\u7684\u8bdd\u56de\u623f\u7761\u89c9\u53bb\u5427\uff01\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u5fc3\u4e2d\u4ecd\u6709\u7740\u5f88\u591a\u7591\u95ee\uff0c\u4f46\u4ed6\u672a\u6562\u591a\u95ee\uff0c\u56de\u623f\u800c\u53bb\u3002 \u3000\u3000\u5929\u4eae\u4e4b\u540e\uff0c\u65b9\u5929\u6210\u679c\u7136\u6539\u53d8\u4e86\u4e3b\u610f\uff0c\u8981\u4e1c\u8033\u8001\u4eba\u76f8\u968f\u800c\u884c\u3002 \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9664\u4e86\u548c\u84dd\u8863\u7ae5\u5b50\u5355\u72ec\u76f8\u89c1\u4e4b\u65f6\uff0c\u8bf4\u4e0a\u51e0\u53e5\u8bdd\u5916\uff0c\u5e73\u5e38\u4e00\u76f4\u662f\u6c89\u9ed8\u4e0d\u8a00\u3002 \u3000\u3000\u51e0\u4eba\u6653\u884c\u591c\u5bbf\uff0c\u8fd9\u65e5\u5230\u4e86\u5609\u5b9a\u3002 \u3000\u3000\u5609\u5b9a\u7d27\u4e34\u5cb7\u6c5f\u5927\u6e21\u6cb3\u53e3\uff0c\u5546\u8d3e\u7edc\u7ece\uff0c\u5e02\u9762\u7e41\u8363\u3002 \u3000\u3000\u65b9\u5929\u6210\u5e26\u7740\u51e0\u4eba\u5230\u4e86\u4e00\u5ea7\u5ba2\u6808\u4e2d\uff0c\u8981\u4e86\u4e00\u5ea7\u8de8\u9662\uff0c\u5bf9\u90a3\u52b2\u88c5\u7ae5\u5b50\u8bf4\u9053\uff1a\u201c\u4eca\u65e5\u4f60\u4eec\u597d\u597d\u4f11\u606f\u4e00\u591c\uff0c\u660e\u65e5\u5348\u65f6\u4e3a\u7236\u4f1a\u5408\u4f60\u4eec\u5927\u4f2f\u7236\u548c\u4e09\u53d4\u4e4b\u540e\uff0c\u5982\u65e0\u8981\u4e8b\u5c31\u5e26\u4f60\u4eec\u5230\u5609\u5b9a\u73a9\u4e0a\u4e00\u53e3\u3002\u201d \u3000\u3000\u90a3\u52b2\u88c5\u7ae5\u5b50\u9053\uff1a\u201c\u7239\u7239\u548c\u5927\u4f2f\u7236\u5728\u4f55\u5904\u89c1\u9762\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u57ce\u5916\u51cc\u4e91\u5c71\uff1f\u201d \u3000\u3000\u52b2\u88c5\u7ae5\u5b50\u9053\uff1a\u201c\u7239\u7239\u4e0d\u5e26\u6211\u540c\u53bb\u5417\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4e3a\u7236\u548c\u4f60\u5927\u4f2f\u6709\u4e8b\u5546\u8c08\uff0c\u4f60\u548c\u6bcd\u4eb2\uff0c\u54e5\u54e5\u5b88\u5728\u5e97\u4e2d\u4e0d\u7528\u53bb\u4e86\u3002\u201d \u3000\u3000\u52b2\u88c5\u7ae5\u5b50\u9053\uff1a\u201c\u7239\u7239\u51e0\u65f6\u56de\u6765\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u65e5\u843d\u4e4b\u524d\u3002\u201d \u3000\u3000\u90a3\u52b2\u88c5\u6c49\u5b50\u76ee\u5149\u7a81\u7136\u8f6c\u5230\u4e1c\u8033\u8001\u4eba\u8138\u4e0a\uff0c\u9053\uff1a\u201c\u8001\u4e1c\u554a\uff01\u4f60\u5230\u8fc7\u5609\u5b9a\u6ca1\u6709\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u8001\u6c49\u5929\u6daf\u6d41\u6d6a\uff0c\u5230\u8fc7\u6b64\u5730\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u76ee\u5149\u8f6c\u5230\u90a3\u52b2\u88c5\u7ae5\u5b50\u8eab\u4e0a\uff0c\u9053\uff1a\u201c\u73de\u513f\uff0c\u597d\u597d\u5b88\u5728\u5e97\u91cc\uff0c\u4e0d\u8bb8\u4e71\u8dd1\u3002\u201d \u3000\u3000\u65b9\u73de\u70b9\u70b9\u5934\uff0c\u65b9\u5929\u6210\u8d77\u8eab\u81ea\u53bb\u3002 \u3000\u3000\u65b9\u5929\u6210\u53bb\u540e\uff0c\u65b9\u73de\u76ee\u5149\u53c8\u8f6c\u5230\u4e1c\u8033\u8001\u4eba\u8eab\u4e0a\uff0c\u9053\uff1a\u201c\u4f60\u5bf9\u5609\u5b9a\u5f88\u719f\u6089\uff0c\u5e26\u6211\u53bb\u73a9\u73a9\u597d\u5417\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u5e76\u975e\u662f\u8001\u6c49\u4e0d\u80af\uff0c\u53ea\u56e0\u672a\u5f97\u8001\u7237\u592b\u4eba\u9996\u80af\uff0c\u8001\u6c49\u5982\u4f55\u6562\u64c5\u81ea\u4f5c\u4e3b\u3002\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u73de\u5f1f\uff0c\u4e1c\u8033\u8001\u4f2f\u8bf4\u7684\u4e5f\u662f\uff0c\u4ed6\u4e0b\u6562\u81ea\u4f5c\u4e3b\u5f20\uff0c\u4f55\u4e0d\u7b49\u4f2f\u7236\u3001\u4f2f\u6bcd\u56de\u6765\u4e4b\u540e\uff0c\u7980\u660e\u518d\u53bb\uff1f\u201d \u3000\u3000\u65b9\u73de\u4e00\u76b1\u7709\u5934\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u8981\u7b49\u7239\u5a18\u56de\u6765\u5462\uff1f\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u6b64\u523b\uff0c\u4e1c\u8001\u4f2f\u4e0d\u6562\u4f5c\u4e3b\uff0c\u5982\u82e5\u4f60\u4e00\u5b9a\u903c\u4ed6\u540c\u53bb\uff0c\u90a3\u5c82\u4e0d\u662f\u4e3a\u96be\u4ed6\u5417\uff1f\u201d \u3000\u3000\u65b9\u73de\u9053\uff1a\u201c\u5509\uff01\u5927\u54e5\uff0c\u4e0d\u662f\u5c0f\u5f1f\u8bf4\u4f60\uff0c\u8fd9\u8001\u4e1c\u662f\u54b1\u4eec\u7684\u4f63\u4eba\uff0c\u4f60\u600e\u4e48\u80fd\u591f\u53eb\u4ed6\u4e1c\u8001\u4f2f\u5462\uff1f\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u8f7b\u8f7b\u5730\u53f9\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u8bdd\u600e\u4e48\u8fd9\u6837\u8bf4\u5462\uff0c\u4e1c\u8001\u4f2f\u8fd9\u6837\u5927\u5e74\u7eaa\u4e86\uff0c\u6211\u4eec\u8fd8\u4e0d\u53ca\u5f31\u51a0\uff0c\u5982\u4f55\u80fd\u53eb\u4ed6\u8001\u4e1c\u5462\uff1f\u201d \u3000\u3000\u65b9\u73de\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u597d\u5427\uff01\u4f60\u53eb\u4f60\u7684\u4e1c\u8001\u4f2f\uff0c\u6211\u53eb\u6211\u7684\u8001\u4e1c\uff0c\u54b1\u4eec\u5404\u53eb\u5404\u7684\u5c31\u662f\u3002 \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u8fd8\u60f3\u518d\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u4e1c\u8033\u8001\u4eba\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u7528\u4e89\u6267\u4e86\uff0c\u53eb\u6211\u8001\u4e1c\u4e5f\u597d\uff0c\u90fd\u662f\u4e00\u6837\u3002\u201d \u3000\u3000\u65b9\u73de\u7a81\u7136\u8f6c\u8fc7\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u597d\u597d\u5730\u8c08\u5427\uff01\u6211\u8981\u51fa\u53bb\u4e86\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u5927\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u90a3\u600e\u4e48\u6210\uff0c\u5c11\u4e3b\u4eba\u65e0\u8bba\u5982\u4f55\u4e0d\u80fd\u4f7f\u8001\u6c49\u4e3a\u96be\uff01\u201d \u3000\u3000\u6a2a\u8eab\u62e6\u4f4f\u4e86\u53bb\u8def\u3002 \u3000\u3000\u65b9\u73de\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u8981\u7ba1\u675f\u6211\u5417\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u8fd9\u4e2a\u8001\u6c49\u4e0d\u6562\u3002\u201d \u3000\u3000\u65b9\u73de\u9053\uff1a\u201c\u4e0d\u6562\u4f60\u5c31\u95ea\u5f00\u53bb\u3002\u201d\u624b\u4e00\u63a8\u4e1c\u8033\u8001\u4eba\uff0c\u51b2\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4e1c\u8033\u8001\u4eba\u5446\u5446\u5730\u671b\u7740\u65b9\u73de\u7684\u80cc\u5f71\uff0c\u5185\u5fc3\u4e4b\u4e2d\u611f\u6168\u4e07\u5343\uff0c\u534a\u664c\u8bf4\u4e0d\u51fa\u4e00\u53e5\u8bdd\u6765\u3002\u201d \u3000\u3000\u90a3\u84dd\u8863\u7ae5\u5b50\u7f13\u7f13\u5730\u884c\u5230\u90a3\u4e1c\u8033\u8001\u4eba\u7684\u8eab\u524d\uff0c\u4f4e\u58f0\u4ed6\u8bf4\u9053\uff1a\u201c\u8001\u4f2f\u4f2f\uff0c\u4e0d\u7528\u751f\u6c14\uff0c\u6211\u90a3\u4f4d\u5144\u5f1f\uff0c\u751f\u6027\u66b4\u6025\uff0c\u5bf9\u6211\u4e5f\u662f\u5982\u6b64\uff0c\u8001\u4f2f\u4f2f\u4e0d\u8981\u751f\u6c14\u624d\u597d\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u7b11\u9053\uff1a\u201c\u5b69\u5b50\u4f60\u5728\u8fd9\u91cc\u7b49\u6211\uff0c\u6211\u53bb\u77a7\u77a7\u65b9\u516c\u5b50\u3002\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u5927\u6015\u7236\u5bf9\u4ed6\u5341\u5206\u7231\u62a4\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u5b69\u5b50\u4f60\u5b89\u5fc3\u7528\u529f\u53bb\u5427\uff01\u6b64\u5730\u4e8b\u60c5\uff0c\u6211\u81ea\u4f1a\u5bf9\u4ed8\u3002\u201d \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u5fc3\u4e2d\u867d\u60f3\u8bf4\u51e0\u53e5\u5b89\u6170\u4e4b\u8a00\uff0c\u4f46\u53c8\u4e0d\u77e5\u4ece\u4f55\u5f00\u53e3\u3002 \u3000\u3000\u53ea\u89c1\u8001\u4eba\u7f13\u7f13\u5730\u5411\u524d\u884c\u53bb\uff0c\u8eab\u5f71\u6d88\u5931\u5728\u95e8\u5916\u4e0d\u89c1\u3002 \u3000\u3000\u4e14\u8bf4\u65b9\u73de\uff0c\u8d1f\u6c14\u5954\u51fa\u5ba2\u6808\uff0c\u4fe1\u6b65\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u6b64\u65f6\uff0c\u5c06\u8fd1\u5348\u5e02\uff0c\u8857\u4e0a\u884c\u4eba\u6b63\u591a\uff0c\u63a5\u8e35\u64e6\u80a9\uff0c\u62e5\u6324\u5f02\u5e38\u3002 \u3000\u3000\u65b9\u73de\u5730\u5f62\u4e0d\u719f\uff0c\u53c8\u4e0d\u613f\u95ee\u4eba\uff0c\u8ff7\u8ff7\u7cca\u7cca\u5730\u5411\u524d\u8d70\u53bb\u3002 \u3000\u3000\u8d70\u8fc7\u4e24\u4e09\u6761\u8857\uff0c\u5230\u4e86\u4e00\u7247\u5e7f\u573a\u4e4b\u4e2d\u3002 \u3000\u3000\u53ea\u89c1\u4eba\u5934\u6512\u52a8\uff0c\u8fd9\u5e7f\u573a\u4e4b\u4e2d\u5341\u5206\u70ed\u95f9\u3002 \u3000\u3000\u65b9\u73de\u6324\u4eba\u4e00\u5708\u4eba\u7fa4\u4e4b\u4e2d\uff0c\u62ac\u5934\u770b\u53bb\u3002\u53ea\u89c1\u4e24\u7537\u4e24\u5973\uff0c\u6b63\u5728\u4e00\u6761\u94a2\u7d22\u4e4b\u4e0a\uff0c\u7f13\u7f13\u884c\u8d70\uff0c\u65b9\u73de\u5e74\u7eaa\u867d\u5c0f\uff0c\u4f46\u8863\u7740\u5374\u5f88\u534e\u8d35\uff0c\u6324\u5165\u4eba\u7fa4\u4e4b\u4e2d\uff0c\u7acb\u65f6\u6709\u4e00\u4e2a\u8eab\u7740\u9752\u8863\uff0c\u5934\u68b3\u53cc\u8fab\uff0c\u5e74\u7ea6\u5341\u4e09\u56db\u5c81\u7684\u5973\u5b69\uff0c\u884c\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u516c\u5b50\uff0c\u8bf7\u8d4f\u70b9\u94b1\u3002\u201d \u3000\u3000\u65b9\u73de\u62ac\u5934\u77a7\u4e86\u90a3\u94a2\u7d22\u4e00\u773c\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u547c\uff01\u5c31\u51ed\u4f60\u4eec\u8fd9\u70b9\u73a9\u827a\u4e5f\u8981\u5356\u94b1\u5417\uff1f\u201d \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u4f46\u5de6\u624b\u5374\u63a2\u5165\u888b\u4e2d\uff0c\u6478\u51fa\u4e00\u706f\u91d1\u53f6\u5b50\uff0c\u6295\u5165\u5973\u7ae5\u53cc\u624b\u6367\u7740\u7684\u7af9\u7bee\u4e4b\u4e2d\u3002 \u3000\u3000\u90a3\u53cc\u8fab\u5c11\u5973\uff0c\u770b\u4ed6\u51fa\u624b\u5c31\u662f\u4e00\u7247\u91d1\u53f6\u5b50\uff0c\u6015\u6709\u4e8c\u4e24\u591a\u91cd\uff0c\u4e0d\u7981\u554a\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u4ed6\u4e00\u4e2a\u5e74\u8f7b\u5b69\u5b50\uff0c\u51fa\u624b\u5982\u6b64\u5927\u65b9\uff0c\u7acb\u65f6\u5f15\u5f97\u5168\u573a\u6ce8\u76ee\uff0c\u6570\u5341\u9053\u773c\u5149\uff0c\u4e00\u9f50\u6295\u6ce8\u8fc7\u6765\u3002 \u3000\u3000\u65b9\u73de\u50b2\u7136\u4e00\u7b11\u9053\uff1a\u201c\u600e\u4e48\uff1f\u53ef\u662f\u592a\u5c11\u4e86\uff1f\u201d \u3000\u3000\u90a3\u9752\u8863\u5973\u7ae5\u751f\u7684\u5341\u5206\u79c0\u81f4\uff0c\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u592a\u591a\u4e86\u3002\u201d \u3000\u3000\u65b9\u73de\u63a2\u624b\u5165\u6000\uff0c\u53c8\u6478\u51fa\u4e24\u7247\u91d1\u53f6\u5b50\uff0c\u6295\u4eba\u90a3\u7af9\u7bee\u4e4b\u4e2d\u3002 \u3000\u3000\u9752\u8863\u5973\u7ae5\u5446\u4e86\u4e00\u5446\u6b20\u8eab\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u516c\u5b50\u3002\u201d \u3000\u3000\u53ea\u542c\u5f97\u4e00\u4e2a\u51b7\u6f20\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u5973\u5a03\u513f\uff0c\u5230\u8fd9\u8fb9\u6765\uff0c\u54b1\u4eec\u5c11\u7237\u6709\u8d4f\u3002\u201d \u3000\u3000\u65b9\u73de\u8f6c\u773c\u671b\u53bb\uff0c\u53ea\u89c1\u8bf4\u8bdd\u4e4b\u4eba\uff0c\u5934\u6234\u74dc\u76ae\u5e3d\uff0c\u8eab\u7740\u9752\u7ef8\u5b50\u957f\u886b\uff0c\u624b\u4e2d\u63d0\u7740\u4e00\u6839\u6e58\u7af9\u7389\u5634\u7684\u65f1\u70df\u888b\uff0c\u5e74\u7ea6\u4e09\u5341\u4e94\u516d\u5c81\uff0c\u7559\u7740\u516b\u5b57\u80e1\u3002 \u3000\u3000\u5728\u4ed6\u65c1\u4fa7\uff0c\u4e00\u5f20\u6728\u6905\u4e0a\u5750\u7740\u4e00\u4e2a\u4e8c\u5341\u4e0a\u4e0b\uff0c\u767d\u51c0\u9762\u76ae\uff0c\u9ec4\u7f0e\u5b50\u957f\u886b\u7684\u5e74\u8f7b\u4eba\u3002 \u3000\u3000\u9ec4\u8863\u5c11\u5e74\u8eab\u540e\u5e76\u80a9\u513f\u7ad9\u7740\u4e24\u4e2a\u8eab\u8eaf\u9b41\u4f1f\u7684\u5927\u6c49\uff0c\u4e00\u8eab\u9ed1\u8272\u7684\u52b2\u88c5\uff0c\u4e24\u5f20\u5927\u9ebb\u76ae\u8138\uff0c\u6d53\u7709\u864e\u76ee\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5168\u573a\u4e2d\u4eba\uff0c\u90fd\u4e3a\u90a3\u4e00\u58f0\u547c\u559d\u58f0\u60ca\u52a8\uff0c\u9f50\u9f50\u8f6c\u76ee\u76f8\u6ce8\u3002 \u3000\u3000\u90a3\u9752\u8863\u5c11\u5973\u602f\u751f\u751f\u5730\u671b\u4e86\u65b9\u73de\u4e00\u773c\uff0c\u6367\u7740\u7af9\u7bee\uff0c\u884c\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u56db\u4e2a\u8d70\u94a2\u4e1d\u7684\u7537\u5973\uff0c\u4e00\u9f50\u4ece\u4e0a\u9762\u8df3\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u90a3\u9752\u8863\u5973\u7ae5\u884c\u5230\u90a3\u9ec4\u8863\u5c11\u5e74\u7684\u8eab\u524d\uff0c\u6b20\u8eab\u8bf4\u9053\uff1a\u201c\u5927\u53d4\u6709\u4f55\u5429\u5490\uff1f\u201d \u3000\u3000\u9752\u886b\u4eba\u76ee\u5149\u6295\u6ce8\u5728\u90a3\u9ec4\u8863\u5c11\u5e74\u7684\u8eab\u4e0a\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u6211\u5bb6\u516c\u5b50\u7237\uff0c\u5feb\u4e9b\u89c1\u793c\u3002\u201d \u3000\u3000\u9752\u8863\u5973\u7ae5\u671b\u4e86\u90a3\u5bc5\u8863\u5c11\u5e74\u4e00\u9635\u624d\u6b20\u8eab\u8bf4\u9053\uff1a\u201c\u89c1\u8fc7\u516c\u5b50\u3002\u201d \u3000\u3000\u9ec4\u8863\u5c11\u5e74\u8f7b\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e0d\u7528\u591a\u793c\u2026\u2026\u201d\u8bed\u58f0\u4e00\u987f\u9053\uff1a\u201c\u521a\u624d\u90a3\u5c0f\u5b50\u7ed9\u4f60\u597d\u591a\u94f6\u5b50\uff1f\u201d\u9752\u8863\u5973\u7ae5\u9053\uff1a\u201c\u90a3\u4f4d\u516c\u5b50\uff0c\u7ed9\u6211\u4e09\u7247\u91d1\u53f6\u5b50\u3002\u201d \u3000\u3000\u9ec4\u8863\u5c11\u5e74\u671b\u4e86\u671b\u9752\u8863\u5973\u7ae5\u4e00\u773c\uff0c\u9053\uff1a\u201c\u6765\u5440\uff01\u8d4f\u5979\u4e24\u952d\u91d1\u5b50\u3002\u201d\u90a3\u7559\u7740\u516b\u5b57\u80e1\u7684\u9752\u886b\u4eba\uff0c\u4e00\u54c8\u8170\uff0c\u62c9\u5f00\u4e00\u4e2a\u767d\u5e03\u888b\u5b50\uff0c\u6478\u51fa\u4e24\u952d\u91d1\u5b50\uff0c\u653e\u5165\u7af9\u7bee\u4e2d\uff0c\u9053\uff1a\u201c\u8fd8\u4e0d\u5feb\u8c22\u8fc7\u5c11\u7237\u7684\u91cd\u5e38\u3002\u201d \u3000\u3000\u9752\u8863\u5973\u7ae5\u671b\u4e86\u671b\u90a3\u4e24\u952d\u91d1\u5b50\u4e00\u773c\uff0c\u5fc3\u4e2d\u9a87\u4e86\u4e00\u8df3\uff0c\u4f30\u8ba1\u90a3\u4e24\u952d\u9ec4\u91d1\uff0c\u81f3\u5c11\u5728\u4e8c\u5341\u4e24\u4ee5\u4e0a\u3002\u4eba\u7ea6\u5979\u4ece\u6765\u6ca1\u6709\u89c1\u8fc7\u90a3\u6837\u591a\u7684\u9ec4\u91d1\uff0c\u7aef\u7740\u7bee\u5b50\u7684\u53cc\u624b\u6709\u4e9b\u5fae\u5fae\u53d1\u6296\uff0c\u7f13\u7f13\u4ed6\u8bf4\u9053\uff1a \u3000\u3000\u201c\u592a\u591a\u4e86\u3002\u201d \u3000\u3000\u90a3\u9ec4\u8863\u5c11\u5e74\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u8fd9\u70b9\u9ec4\u91d1\uff0c\u7b97\u5f97\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9752\u8863\u5973\u7ae5\u6b20\u8eab\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u516c\u5b50\u3002\u201d \u3000\u3000\u9ec4\u8863\u5c11\u5e74\u4e00\u6325\u624b\u9053\uff1a\u201c\u4e0d\u7528\u8c22\u4e86\u3002\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u6211\u4e0d\u4fe1\u8fd9\u5609\u5b9a\u5730\u9762\u4e0a\uff0c\u8fd8\u6709\u4eba\u624b\u9762\u5927\u8fc7\u672c\u516c\u5b50\u3002\u201d \u3000\u3000\u90a3\u65b9\u73de\u5e74\u8f7b\u6c14\u76db\uff0c\u770b\u7740\u9ec4\u8863\u5c11\u51fa\u624b\uff0c\u4f3c\u662f\u6709\u610f\u8d85\u8d8a\u4e8e\u4ed6\uff0c\u5fc3\u4e2d\u65e9\u5df2\u4e0d\u662f\u6ecb\u5473\uff0c\u542c\u5f97\u6700\u540e\u4e00\u53e5\u8bdd\uff0c\u4e0d\u7981\u6012\u706b\u9ad8\u70e7\uff0c\u5927\u6b65\u884c\u4e86\u8fc7\u53bb\uff0c\u9053\uff1a\u201c\u533a\u533a\u4e8c\u5341\u4e24\u9ec4\u91d1\uff0c\u7b97\u5f97\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u63a2\u624b\u4ece\u6000\u4e2d\u6478\u51fa\u4e00\u53e0\u91d1\u53f6\u5b50\uff0c\u6295\u5165\u90a3\u5973\u7ae5\u7684\u7af9\u7bee\u4e4b\u4e2d\u3002 \u3000\u3000\u8fd9\u4e00\u53e0\u91d1\u53f6\u5b50\u81f3\u5c11\u6709\u5341\u7247\u4ee5\u4e0a\uff0c\u8bf4\u91cd\u91cf\u4e0d\u4e0b\u4e8e\u4e09\u5341\u4e24\u3002 \u3000\u3000\u90a3\u9ec4\u8863\u5c11\u5e74\u51b7\u51b7\u5730\u671b\u4e86\u65b9\u73de\u4e00\u773c\uff0c\u9053\uff1a\u201c\u81ed\u5c0f\u5b50\uff0c\u4f60\u662f\u54ea\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u65b9\u73de\u6012\u9053\uff1a\u201c\u4f60\u9a82\u8c01\u662f\u81ed\u5c0f\u5b50\uff1f\u201d \u3000\u3000\u90a3\u7559\u7740\u516b\u5b57\u80e1\u7684\u9752\u886b\u4eba\u9053\uff1a\u201c\u516c\u5b50\u7237\u4f60\u522b\u751f\u6c14\uff0c\u6559\u8bad\u8fd9\u5c0f\u5b50\u4e00\u987f\u5c31\u662f\u3002\u201d\u4e00\u9762\u4e3e\u8d77\u6e58\u5983\u7af9\u7684\u70df\u888b\uff0c\u6307\u7740\u65b9\u73de\uff0c\u9053\uff1a\u201c\u81ed\u5c0f\u5b50\uff0c\u6211\u77a7\u4f60\u662f\u6d3b\u5f97\u4e0d\u8010\u70e6\u4e86\uff01\u201d \u3000\u3000\u65b9\u73de\u53f3\u624b\u4e00\u4f38\uff0c\u6293\u4f4f\u90a3\u9752\u886b\u4eba\u624b\u4e2d\u7684\u65f1\u70df\u888b\uff0c\u6012\u9053\uff1a\u201c\u4f60\u9a82\u54ea\u4e00\u4e2a\uff1f\u201d\u53f3\u624b\u4e00\u638c\u5288\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u90a3\u9752\u886b\u4eba\u6ca1\u6709\u6599\u5230\u65b9\u73de\u7adf\u7136\u51fa\u624b\uff0c\u800c\u4e14\u51fa\u624b\u662f\u5982\u6b64\u5feb\u901f\uff0c\u95ea\u907f\u5df2\u6765\u4e0d\u53ca\uff0c\u84ec\u7136\u4e00\u58f0\u88ab\u51fb\u5728\u524d\u80f8\u4e4b\u4e0a\u3002 \u3000\u3000\u8fd9\u4e00\u638c\u4f3c\u662f\u5f88\u91cd\uff0c\u90a3\u9752\u8863\u4eba\u5403\u4ed6\u4e00\u638c\u6253\u5f97\u5411\u540e\u9000\u4e86\u4e24\u6b65\uff0c\u5f20\u5f00\u5634\u55b7\u51fa\u4e00\u53e3\u9c9c\u8840\uff0c\u8dcc\u5750\u5730\u4e0a\u3002 \u3000\u3000\u9ec4\u8863\u5c11\u5e74\u970d\u7136\u7ad9\u8d77\u8eab\u5b50\uff0c\u6012\u9053\uff1a\u201c\u4f60\u6562\u52a8\u624b\uff01\u201d \u3000\u3000\u8bed\u58f0\u672a\u843d\uff0c\u8eab\u540e\u4e24\u4e2a\u52b2\u88c5\u5927\u6c49\uff0c\u5df2\u7136\u53cc\u53cc\u6251\u51fa\uff0c\u56db\u53ea\u624b\u4e00\u9f50\u5411\u65b9\u73de\u6293\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u90a3\u9752\u8863\u5973\u7ae5\u7a81\u7136\u653e\u4e0b\u624b\u4e2d\u7684\u7af9\u7bee\u5b50\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u8bf8\u4f4d\u4e0d\u8981\u52a8\u624b\u2026\u2026\u201d \u3000\u3000\u4e24\u4e2a\u5927\u6c49\u54ea\u91cc\u80af\u542c\u5979\u7684\u559d\u6b62\uff0c\u5de6\u9762\u4e00\u4eba\u4e94\u6307\u7b95\u5f20\uff0c\u6263\u4f4f\u65b9\u73de\u7684\u624b\u8155\uff0c\u5de6\u9762\u4e00\u4eba\uff0c\u7d22\u6027\u4e00\u638c\u5288\u4e86\u4e0b\u53bb\uff0c\u62cd\u5411\u65b9\u73de\u7684\u9876\u95e8\u3002 \u3000\u3000\u65b9\u73de\u6781\u5f97\u7236\u6bcd\u7684\u5ba0\u7231\uff0c\u65b9\u5929\u6210\u53c8\u7528\u4e86\u5f88\u5927\u7684\u5fc3\u601d\uff0c\u6765\u57f9\u690d\u7231\u5b50\u6b66\u529f\uff0c\u6240\u4ee5\uff0c\u4ed6\u5e74\u7eaa\u867d\u5c0f\uff0c\u4f46\u6b66\u529f\u4e0a\u5374\u662f\u6709\u7740\u4e0d\u51e1\u7684\u6210\u5c31\uff0c\u773c\u770b\u4e24\u4e2a\u9ad8\u8fc7\u4ed6\u4e00\u500d\u7684\u5927\u6c49\uff0c\u5206\u653b\u5408\u51fb\uff0c\u5fc3\u4e2d\u867d\u7136\u6709\u4e9b\u5bb3\u6015\uff0c\u4f46\u540c\u65f6\uff0c\u4e5f\u6fc0\u8d77\u4e86\u4ed6\u597d\u6597\u7684\u5929\u6027\uff0c\u4e00\u5438\u6c14\uff0c\u7a81\u7136\u4e00\u4e2a\u8f6c\u8eab\uff0c\u907f\u5f00\u4e86\u5de6\u9762\u5927\u6c49\u7684\u4e00\u638c\uff0c\u95ea\u5230\u53f3\u9762\u5927\u6c49\u7684\u8eab\u540e\u98de\u8d77\u4e00\u811a\uff0c\u8e22\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u53f3\u9762\u5927\u6c49\u51b7\u54fc\u4e00\u58f0\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u5f88\u6ed1\u6e9c\u554a\uff01\u201d \u3000\u3000\u8bed\u58f0\u672a\u843d\uff0c\u5c41\u80a1\u4e0a\u4e2d\u4e86\u4e00\u811a\u3002 \u3000\u3000\u8fd9\u4e00\u811a\u8e22\u5f97\u5f88\u91cd\uff0c\u90a3\u5927\u6c49\u9ad8\u5927\u7684\u8eab\u4f53\uff0c\u4e5f\u4e0d\u7981\u5411\u524d\u4e00\u683d\u3002 \u3000\u3000\u8fd9\u4e00\u6765\u4e5f\u540c\u65f6\u6fc0\u6012\u4e86\u90a3\u5927\u6c49\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u56de\u8eab\u6251\u53bb\uff0c\u638c\u5982\u5de8\u65a7\u5f00\u5c71\u4e00\u822c\u5288\u4e0b\u6765\u3002 \u3000\u3000\u65b9\u73de\u4e0d\u6562\u786c\u63a5\u638c\u52bf\uff0c\u7eb5\u8eab\u907f\u5f00\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u5927\u6c49\u7eb5\u8eab\u4ece\u540e\u56f4\u4e0a\uff0c\u6321\u4f4f\u4e86\u65b9\u73de\u540e\u9000\u4e4b\u8def\u3002 \u3000\u3000\u65b9\u73de\u6709\u751f\u4ee5\u6765\uff0c\u4e00\u76f4\u5728\u7236\u6bcd\u5e87\u836b\u4e4b\u4e0b\u957f\u5927\uff0c\u4ece\u672a\u548c\u4eba\u8fd9\u6837\u52a8\u624b\u4e92\u62fc\uff0c\u6beb\u65e0\u5bf9\u654c\u7ecf\u9a8c\uff0c\u5728\u4e24\u4e2a\u5927\u4eba\u56f4\u653b\u4e4b\u4e0b\uff0c\u4e0d\u7981\u6025\u8e81\u8d77\u6765\uff0c\u638c\u6cd5\u6e10\u89c1\u6563\u4e71\u3002 \u3000\u3000\u5176\u5b9e\u4e00\u4e2a\u5927\u6c49\u5341\u4f59\u5408\u4e2d\u672a\u80fd\u64d2\u5f97\u4f4f\u65b9\u73de\uff0c\u5fc3\u4e2d\u4ea6\u662f\u6697\u81ea\u9707\u9a87\uff0c\u5fd6\u9053\uff1a\u8fd9\u5a03\u513f\u5982\u6b64\u5e74\u8f7b\uff0c\u6b66\u529f\u5982\u6b64\u4e86\u5f97\uff0c\u5fc5\u7136\u662f\u51fa\u8eab\u4e8e\u6b66\u6797\u4e16\u5bb6\uff0c\u5982\u662f\u4f24\u5230\u4ed6\uff0c\u52bf\u5fc5\u8981\u5f15\u8d77\u4e00\u573a\u4e89\u7eb7\uff0c\u4ee5\u4ed6\u8eab\u624b\u800c\u8a00\uff0c\u5982\u82e5\u7531\u4ed6\u5bb6\u4eba\u51fa\u9762\uff0c\u6211\u7b49\u51b3\u975e\u5176\u654c\u3002 \u3000\u3000\u90a3\u9752\u8863\u5973\u7ae5\u773c\u770b\u4e24\u4e2a\u6b66\u529f\u9ad8\u5f3a\u7684\u5927\u6c49\uff0c\u535a\u6597\u4e86\u6570\u5341\u5408\uff0c\u4ecd\u672a\u80fd\u4f24\u5230\u65b9\u73de\uff0c\u5fc3\u4e2d\u5927\u662f\u656c\u4f69\uff0c\u6697\u9053\uff1a\u4ed6\u4e43\u6709\u771f\u624d\u5b9e\u5b66\u7684\u4eba\uff0c\u548c\u81ea\u5df1\u5356\u827a\u6c5f\u6e56\u7684\u82b1\u62f3\u7ee3\u817f\uff0c\u5927\u5927\u4e0d\u540c\u4e86\u3002 \u3000\u3000\u5fc3\u8f6c\u5ff5\u52a8\u4e4b\u95f4\uff0c\u7a81\u95fb\u7830\u7136\u4e00\u58f0\u5927\u9707\uff0c\u4f20\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u7d27\u63a5\u7740\uff0c\u54cd\u8d77\u4e86\u4e00\u58f0\u76c8\u8033\u7684\u5c16\u53eb\u3002 \u3000\u3000\u51dd\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u65b9\u73de\u8fde\u9000\u4e86\u6570\u6b65\uff0c\u5012\u5728\u4e86\u5730\u4e0a\u3002 \u3000\u3000\u539f\u6765\uff0c\u4e24\u4e2a\u9ed1\u8863\u5927\u6c49\uff0c\u4e45\u6218\u65b9\u73de\u4e0d\u4e0b\uff0c\u5728\u4f17\u76ee\u777d\u777d\u4e4b\u4e0b\uff0c\u5927\u611f\u96be\u582a\uff0c\u60c5\u52bf\u6240\u8feb\u53ea\u597d\u65bd\u4e0b\u6bd2\u624b\u4e86\u3002 \u3000\u3000\u65b9\u73de\u5bf9\u654c\u7ecf\u9a8c\u4e0d\u8db3\uff0c\u5f3a\u654c\u65bd\u4e0b\u6bd2\u624b\u65f6\uff0c\u9632\u62a4\u4e0d\u53ca\uff0c\u88ab\u4eba\u4e00\u638c\u51fb\u5728\u5de6\u80a9\u4e4b\u4e0a\u3002 \u3000\u3000\u8fd9\u4e00\u51fb\u843d\u638c\u751a\u91cd\uff0c\u53ea\u6253\u5f97\u65b9\u73de\u4e00\u8fde\u5411\u540e\u9000\u4e86\u6570\u6b65\uff0c\u8dcc\u5012\u5728\u5730\u4e0a\u3002 \u3000\u3000\u5de6\u9996\u90a3\u5927\u6c49\u56db\u987e\u4e86\u4e00\u773c\uff0c\u4e0d\u89c1\u6709\u4eba\u51fa\u9762\uff0c\u624d\u51b7\u7b11\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4e0d\u77e5\u5929\u9ad8\u5730\u539a\u7684\u5c0f\u5a03\u513f\u3002\u201d \u3000\u3000\u8bf4\u7740\u8bdd\u4f0f\u8eab\u4e00\u63a2\uff0c\u6293\u8d77\u4e86\u65b9\u73de\u3002 \u3000\u3000\u90a3\u9752\u8863\u5973\u7ae5\u773c\u770b\u65b9\u73de\u4e2d\u638c\u6655\u4e86\u8fc7\u53bb\uff0c\u5fc3\u4e2d\u5927\u662f\u601c\u60dc\uff0c\u518d\u770b\u90a3\u5927\u6c49\u6076\u72e0\u72e0\u5730\u6293\u8d77\u65b9\u73de\uff0c\u5fc3\u4e2d\u5927\u60ca\uff0c\u6025\u6025\u8bf4\u9053\uff1a\u201c\u4e0d\u8981\u52a8\uff01\u201d \u3000\u3000\u5de6\u9996\u90a3\u5927\u6c49\u4e24\u9053\u6076\u72e0\u72e0\u7684\u773c\u5149\uff0c\u76ef\u4f4f\u90a3\u9752\u8863\u5973\u7ae5\u8138\u4e0a\uff0c\u6253\u91cf\u4e86\u4e00\u9635\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a \u3000\u3000\u201c\u600e\u4e48\uff0c\u51ed\u4f60\u8fd9\u5c0f\u6bdb\u4e2b\u5934\u51e0\u62db\u82b1\u62f3\u7ee3\u817f\u60f3\u591a\u7ba1\u95f2\u4e8b\u5417\uff1f\u201d \u3000\u3000\u8fd9\u9752\u8863\u5973\u7ae5\u867d\u7136\u5e74\u7eaa\u4e0d\u5927\uff0c\u4f46\u5979\u5e38\u5e74\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\uff0c\u5904\u7406\u4e8b\u60c5\u4e4b\u80fd\uff0c\u5374\u5927\u5927\u5730\u8d85\u8d8a\u5979\u7684\u5e74\u7eaa\u3002 \u3000\u3000\u53ea\u89c1\u5979\u6b20\u8eab\u4e00\u793c\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u5927\u53d4\u8eab\u624b\u4e0d\u51e1\uff0c\u770b\u6765\u90fd\u662f\u6b66\u6797\u4e2d\u4e00\u6d41\u7684\u9ad8\u624b\uff0c\u5c0f\u5973\u5b50\u5982\u4f55\u4e5f\u4e0d\u4f1a\u662f\u654c\u624b\u3002\u201d \u3000\u3000\u5979\u8bf4\u8bdd\u5a49\u8f6c\u548c\u6c14\uff0c\u5148\u628a\u90a3\u4e24\u4e2a\u9ed1\u8863\u5927\u6c49\u6367\u4e86\u4e00\u9635\uff0c\u4e24\u4eba\u867d\u7136\u4e0d\u8010\u70e6\uff0c\u4e5f\u4e0d\u597d\u53d1\u4f5c\u51fa\u6765\u3002 \u3000\u3000\u5de6\u9762\u90a3\u5927\u6c49\u6c89\u541f\u4e86\u4e00\u9635\u9053\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u4e8b\uff1f\u5feb\u6b64\u8bf4\u51fa\u6765\u5427\uff01\u201d \u3000\u3000\u9752\u8863\u5973\u7ae5\u9053\uff1a\u201c\u4e8c\u4f4d\u548c\u8fd9\u516c\u5b50\u54e5\uff0c\u5e76\u65e0\u4ec0\u4e48\u6df1\u4ec7\u5927\u6068\uff0c\u4ed6\u867d\u7136\u6253\u4f24\u4e86\u4f60\u4eec\u90a3\u4f4d\u5927\u53d4\uff0c\u4f46\u4f60\u4eec\u4e5f\u6253\u4f24\u4e86\u4ed6\uff0c\u5f7c\u6b64\u5df2\u7ecf\u626f\u5e73\uff0c\u6c42\u6c42\u4e24\u4f4d\u5927\u53d4\u653e\u4e86\u4ed6\u5427\uff01\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u770b\u70ed\u95f9\u7684\u4eba\uff0c\u4ecd\u7136\u56f4\u5728\u56db\u5468\uff0c\u6570\u767e\u53ea\u773c\u775b\uff0c\u671b\u7740\u573a\u4e2d\u60c5\u52bf\u53d8\u5316\uff0c\u4f46\u5374\u662f\u4e00\u7247\u5bc2\u7136\uff0c\u9759\u5f97\u542c\u4e0d\u5230\u4e00\u70b9\u58f0\u97f3\u3002 \u3000\u3000\u90a3\u9752\u8863\u5973\u7ae5\u4e0d\u95fb\u4e24\u4e2a\u5927\u6c49\u8bb2\u8bdd\uff0c\u63a5\u9053\uff1a\u201c\u5982\u82e5\u4e8b\u548c\u6211\u4eec\u65e0\u5173\uff0c\u5c0f\u5973\u5b50\u51b3\u7136\u4e0d\u6562\u591a\u53e3\uff0c\u4f46\u4e8b\u4e3a\u6211\u4eec\u800c\u8d77\uff0c\u5c0f\u5973\u5b50\u600e\u80fd\u5750\u89c6\uff1f\u201d \u3000\u3000\u8fd9\u4e24\u4e2a\u9ed1\u8863\u5927\u6c49\uff0c\u4e0d\u4f46\u8eab\u6750\u76f8\u4f3c\uff0c\u800c\u4e14\u90fd\u662f\u4e00\u5f20\u9ebb\u5b50\u8138\uff0c\u5343\u4e07\u4eba\u4e2d\u4e5f\u96be\u627e\u51fa\u548c\u4ed6\u4eec\u76f8\u4f3c\u7684\u4e00\u5bf9\u3002\u53ea\u89c1\u4e24\u4eba\u76f8\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u4ecd\u7531\u5de6\u9762\u90a3\u5927\u6c49\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u8981\u751f\u64d2\u4ed6\u56de\u53bb\uff0c\u5982\u82e5\u6211\u4eec\u90a3\u4f4d\u5f20\u5e08\u7237\uff0c\u80fd\u591f\u4f24\u52bf\u75ca\u6108\uff0c\u6211\u4eec\u81ea\u7136\u4f1a\u9976\u4e86\u4ed6\u7684\u6027\u547d\u3002\u5982\u662f\u5f20\u5e08\u7237\u88ab\u4ed6\u4e00\u638c\u6253\u6b7b\uff0c\u6740\u4eba\u507f\u547d\uff0c\u6b20\u503a\u8fd8\u94b1\uff0c\u81ea\u7136\u7531\u4ed6\u6765\u62b5\u507f\u4e86\u3002\u201d \u3000\u3000\u90a3\u9ec4\u7384\u5c11\u5e74\u51b7\u51b7\u7684\u7ad9\u5728\u4e00\u4fa7\uff0c\u671b\u7740\u90a3\u9752\u8863\u5973\u7ae5\uff0c\u4e00\u8a00\u4e0d\u53d1\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u65b9\u73de\u5df2\u7531\u660f\u8ff7\u4e2d\u6e05\u9192\u8fc7\u6765\uff0c\u6b64\u5b50\u5e74\u7eaa\u867d\u8f7b\uff0c\u4f46\u751f\u6027\u5374\u6781\u9ad8\u505a\uff0c\u6697\u4e2d\u54ac\u7259\uff0c\u5fcd\u75bc\u4e0d\u8a00\u3002 \u3000\u3000\u90a3\u9752\u8863\u5973\u7ae5\u773c\u770b\u82e6\u6c42\u4e24\u4e2a\u5927\u6c49\uff0c\u6beb\u65e0\u4f5c\u7528\uff0c\u8f6c\u5411\u90a3\u9ec4\u8863\u5c11\u5e74\u884c\u53bb\uff0c\u6b20\u8eab\u4e00\u793c\u9053\uff1a \u3000\u3000\u201c\u516c\u5b50\u5728\u4e0a\uff0c\u5c0f\u5a62\u8fd9\u91cc\u6709\u793c\u4e86\u3002\u201d \u3000\u3000\u9ec4\u8863\u5c11\u5e74\u51b7\u7b11\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u9752\u8863\u5973\u7ae5\u9053\uff1a\u201c\u6211\u60f3\u6c42\u4f60\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u9ec4\u8863\u5c11\u5e74\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u9752\u8863\u5973\u7ae5\u9053\uff1a\u201c\u5173\u4e8e\u90a3\u4f4d\u516c\u5b50\u7684\u4e8b\u3002\u201d \u3000\u3000\u9ec4\u8863\u5c11\u5e74\u51b7\u7136\u63a5\u9053\uff1a\u201c\u8fd9\u548c\u4f60\u4f55\u5173\uff1f\u201d \u3000\u3000\u9752\u8863\u5973\u7ae5\u9053\uff1a\u201c\u5982\u82e5\u516c\u5b50\u4e0d\u80af\u653e\u624b\uff0c\u95f9\u51fa\u4eba\u547d\uff0c\u8d31\u59be\u90fd\u96be\u514d\u8eab\u53d7\u7275\u8fde\u3002\u201d \u3000\u3000\u9ec4\u8863\u5c11\u5e74\u671b\u4e86\u4ef0\u5367\u5728\u5730\u4e0a\u7684\u9752\u886b\u4eba\u4e00\u773c\uff0c\u9053\uff1a\u201c\u90a3\u5c0f\u4e8e\u662f\u4f60\u7684\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u9752\u8863\u5973\u7ae5\u9053\uff1a\u201c\u548c\u8d31\u59be\u6beb\u65e0\u74dc\u845b\u3002\u201d \u3000\u3000\u9ec4\u8863\u5c11\u5e74\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4e86\uff0c\u65e2\u7136\u662f\u6beb\u65e0\u74dc\u845b\uff0c\u4f60\u6700\u597d\u522b\u7ba1\u3002\u201d \u3000\u3000\u9752\u8863\u5973\u7ae5\u9053\uff1a\u201c\u516c\u5b50\u2026\u2026\u201d \u3000\u3000\u9ec4\u8863\u5c11\u5e74\u7a81\u7136\u4e3e\u624b\u4e00\u6325\uff0c\u6012\u9053\uff1a\u201c\u6eda\u5f00\u53bb\uff01\u201d \u3000\u3000\u65b9\u73de\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u4e8b\u548c\u4f60\u65e0\u5173\uff0c\u4f60\u4e0d\u8981\u7ba1\uff0c\u6599\u4ed6\u4eec\u4e5f\u4e0d\u6562\u52a8\u6211\u3002\u201d \u3000\u3000\u9ec4\u8863\u5c11\u5e74\u6012\u9053\uff1a\u201c\u738b\u5408\u3001\u738b\u5b9d\uff0c\u6253\u4ed6\u4eec\u4e24\u4e2a\u8033\u5149\uff01\u201d \u3000\u3000\u738b\u5408\u5e94\u58f0\u51fa\u624b\uff0c\u5de6\u53f3\u5f00\u5f13\uff0c\u547c\u547c\u4e24\u4e2a\u8033\u62ec\u5b50\u3002 \u3000\u3000\u90a3\u65b9\u73de\u672c\u5df2\u53d7\u4e86\u5f88\u91cd\u7684\u5185\u4f24\uff0c\u53e3\u4e2d\u8840\u8ff9\u672a\u5e72\uff0c\u738b\u5408\u53c8\u51fa\u624b\u751a\u91cd\uff0c\u4e24\u58f0\u8106\u54cd\u8fc7\u540e\uff0c\u76f4\u6253\u7684\u65b9\u73de\u6ee1\u53e3\u9c9c\u8840\uff0c\u72c2\u55b7\u800c\u51fa\u3002 \u3000\u3000\u5014\u5f3a\u7684\u65b9\u73de\u4ecd\u7136\u662f\u7d27\u54ac\u7259\u5173\uff0c\u4e00\u58f0\u4e0d\u54cd\u3002 \u3000\u3000\u9ec4\u8863\u5c11\u5e74\u7a81\u7136\u4e3e\u6b65\u884c\u4e86\u8fc7\u6765\uff0c\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u5f88\u5014\u5f3a\uff0c\u6211\u5012\u8981\u77a7\u77a7\u4f60\u662f\u4e0d\u662f\u94c1\u6253\u94dc\u6d47\u7684\u7f57\u6c49\u3002\u201d\u7f13\u7f13\u4e3e\u8d77\u53f3\u624b\u3002 \u3000\u3000\u65b9\u73de\u7a76\u7adf\u8fd8\u662f\u4e00\u4e2a\u5341\u4e94\u5c81\u7684\u5b69\u5b50\uff0c\u5728\u7236\u6bcd\u836b\u62a4\u4e4b\u4e0b\u957f\u5927\uff0c\u51e0\u65f6\u5403\u8fc7\u8fd9\u7b49\u82e6\u5934\uff0c\u867d\u7136\u5fcd\u4f4f\u672a\u53eb\uff0c\u4f46\u53cc\u76ee\u4e2d\u65e9\u5df2\u662f\u6ee1\u773c\u6cea\u6c34\u3002 \u3000\u3000\u53ea\u89c1\u9ec4\u8863\u5c11\u5e74\u4ef0\u5929\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u9053\uff1a\u201c\u4f60\u53ef\u662f\u5bb3\u6015\u4e86\uff1f\u201d \u3000\u3000\u65b9\u73de\u9053\uff1a\u201c\u8c01\u6015\u4f60\u4e86\uff1f\u201d \u3000\u3000\u4ed6\u6ee1\u8eab\u662f\u4f24\uff0c\u53cc\u988a\u7ea2\u80bf\uff0c\u8bf4\u6765\u8bed\u7109\u4e0d\u8be6\u3002 \u3000\u3000\u9ec4\u8863\u5c11\u5e74\u6012\u9053\uff1a\u201c\u597d\u786c\u7684\u5c0f\u9aa8\u5934\uff0c\u4eca\u65e5\u6211\u5982\u4e0d\u53eb\u4f60\u544a\u9976\u6c42\u547d\uff0c\u6211\u5c31\u628a\u5b97\u5b57\u5012\u8fc7\u6765\u5199\u3002\u201d \u3000\u3000\u6b63\u5f85\u51fa\u624b\u70b9\u65b9\u73de\u4e94\u9634\u7edd\u7a74\uff0c\u7a81\u7136\u4e00\u4e2a\u51b7\u51b7\u7684\u58f0\u97f3\u559d\u9053\uff1a\u201c\u4f4f\u624b\uff01\u201d \u3000\u3000\u9ec4\u8863\u5c11\u5e74\u4e3e\u76ee\u671b\u53bb\uff0c\u53ea\u4e00\u4e2a\u8eab\u7740\u9752\u886b\uff0c\u5934\u6234\u6be1\u5e16\u5e3d\uff0c\u767d\u9aef\u5782\u80f8\u7684\u8001\u4eba\uff0c\u505c\u5728\u4e09\u5c3a\u4ee5\u5916\u3002 \u3000\u3000\u6b64\u4eba\u6765\u7684\u65e0\u58f0\u65e0\u606f\uff0c\u4e0d\u77e5\u4f55\u4ee5\u7adf\u5230\u4e86\u8eab\u4fa7\u3002 \u3000\u3000\u65b9\u73de\u5df2\u77a7\u51fa\u6765\u4eba\u6b63\u662f\u4e1c\u8033\u8001\u4eba\uff0c\u6025\u6025\u7684\u8bf4\u9053\uff1a\u201c\u8001\u4e1c\uff0c\u4f60\u5feb\u4e9b\u544a\u8bc9\u6211\u7239\u7239\u53bb\uff0c\u8fd9\u4e9b\u4eba\u90fd\u4f1a\u6b66\u529f\uff0c\u4f60\u4e0d\u662f\u4ed6\u4eec\u7684\u654c\u624b\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u7f13\u7f13\u7684\u8bf4\u9053\uff1a\u201c\u8001\u6c49\u65e2\u7136\u6765\u4e86\uff0c\u62fc\u4e86\u8001\u547d\uff0c\u4e5f\u8981\u628a\u5c11\u7237\u6551\u51fa\u6765\u3002\u201d \u3000\u3000\u65b9\u73de\u5f3a\u5fcd\u7740\u75bc\u75db\uff0c\u8bf4\u4e86\u6570\u8a00\uff0c\u518d\u4e5f\u63a5\u4e0d\u4e0b\u53bb\u3002 \u3000\u3000\u9ec4\u8863\u5c11\u5e74\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u628a\u5e74\u7eaa\u4e86\uff0c\u6b7b\u4e5f\u4e0d\u7b97\u592d\u5bff\u554a\uff01\u201d \u3000\u3000\u7a81\u7136\u6325\u624b\u4e00\u638c\uff0c\u62cd\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u5168\u573a\u4eba\u4e2d\uff0c\u65e0\u4e0d\u66ff\u90a3\u8001\u6c49\u62c5\u5fc3\uff0c\u770b\u90a3\u767d\u53d1\u82cd\u82cd\uff0c\u8fd9\u5c11\u5e74\u4e00\u638c\u975e\u8981\u7af9\u65ad\u4ed6\u51e0\u6839\u808b\u9aa8\u4e0d\u53ef\u3002 \u3000\u3000\u4e1c\u8033\u8001\u4eba\u8083\u7136\u4e0d\u52a8\uff0c\u4efb\u90a3\u9ec4\u8863\u5c11\u5e74\u62f3\u52bf\u51fb\u4e2d\u524d\u80f8\u3002 \u3000\u3000\u4f46\u95fb\u4e00\u58f0\u95f7\u54fc\uff0c\u4f20\u5165\u8033\u9645\uff0c\u51e0\u58f0\u6df1\u957f\u7684\u53f9\u606f\u58f0\u4e00\u9f50\u54cd\u8d77\u3002 \u3000\u3000\u90a3\u662f\u4e00\u4e9b\u60cb\u501f\u4e1c\u8033\u8001\u4eba\u7684\u53f9\u606f\uff0c\u4e5f\u662f\u8d5e\u7f8e\u4ed6\u820d\u547d\u6551\u4e3b\u7684\u8c6a\u58ee\u3002 \u3000\u3000\u65b9\u73de\u7406\u662f\u4e0d\u5fcd\u77a7\u770b\uff0c\u95ed\u4e0a\u53cc\u76ee\u3002 \u3000\u3000\u53ea\u542c\u5f97\u4e1c\u8033\u8001\u4eba\u7684\u58f0\u97f3\uff0c\u4f20\u5165\u8033\u9645\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u8fd8\u4e0d\u653e\u624b\u5417\uff1f\u201d \u3000\u3000\u65b9\u73de\u53ea\u89c9\u8eab\u5b50\u4e00\u677e\uff0c\u6293\u5728\u8eab\u4e0a\u7684\u4e24\u6761\u624b\u81c2\u7a81\u7136\u677e\u5f00\uff0c\u5fc3\u4e2d\u5927\u611f\u5947\u602a\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u9ebb\u76ae\u5927\u6c49\uff0c\u51f6\u6076\u6a2a\u86ee\uff0c\u600e\u4f1a\u542c\u4e1c\u8033\u8001\u4eba\u7684\u8bdd\u3002\u201d \u3000\u3000\u7741\u773c\u671b\u53bb\uff0c\u53ea\u89c1\u4e1c\u8033\u8001\u4eba\uff0c\u4ecd\u7136\u7ad9\u7acb\u5728\u539f\u5730\uff0c\u90a3\u9ec4\u8863\u5c11\u5e74\u5374\u62b1\u7740\u5c0f\u8179\uff0c\u8e72\u5728\u5730\u4e0a\uff0c\u4f3c\u662f\u53d7\u4e86\u5f88\u91cd\u7684\u5185\u4f24\u3002 \u3000\u3000\u7389\u5408\u738b\u5ba4\u5446\u5446\u5730\u7ad9\u5728\u4e00\u4fa7\u3002 \u3000\u3000\u4e1c\u8033\u8001\u4eba\u4e3e\u624b\u4e00\u62db\uff0c\u9053\uff1a\u201c\u5c11\u7237\uff0c\u54b1\u4eec\u56de\u53bb\u5427\u3002\u201d \u3000\u3000\u65b9\u73de\u6115\u7136\u671b\u4e86\u738b\u5408\u738b\u5b9d\u4e00\u773c\uff0c\u4ecd\u7136\u7ad9\u7740\u4e00\u52a8\u4e0d\u52a8\u3002 \u3000\u3000\u539f\u6765\u4ed6\u4eec\u4f24\u52bf\u6c89\u91cd\uff0c\u65e0\u6cd5\u4e3e\u6b65\u3002 \u3000\u3000\u4e1c\u8033\u8001\u4eba\u5927\u6b65\u884c\u4e86\u8fc7\u6765\uff0c\u62b1\u8d77\u65b9\u73de\uff0c\u7a7f\u51fa\u4eba\u7fa4\uff0c\u76f4\u56de\u5230\u5ba2\u6808\uff0c\u6d17\u53bb\u65b9\u73de\u8138\u4e0a\u7684\u8840\u8ff9\uff0c\u653e\u5728\u6728\u69bb\u4e4b\u4e0a\uff0c\u53f9\u9053\uff1a\u201c\u8001\u6c49\u665a\u5230\u4e00\u6b65\uff0c\u81f4\u4f7f\u5c11\u7237\u53d7\u4f24\u3002\u201d \u3000\u3000\u90a3\u4e1c\u8033\u8001\u4eba\u5728\u62b1\u7740\u65b9\u73de\u5954\u884c\u4e4b\u65f6\uff0c\u5df2\u6697\u4e2d\u6d3b\u4e86\u4ed6\u8eab\u4e0a\u7ecf\u8109\u3002 \u3000\u3000\u65b9\u73de\u89c9\u7740\u8eab\u4e0a\u8840\u884c\u8212\u7545\u8fde\u4f24\u4e5f\u597d\u4e86\u8bb8\u591a\uff0c\u53ea\u662f\u4ed6\u81ea\u5df1\u5e76\u672a\u89c9\u5f97\u662f\u4e1c\u8033\u8001\u4eba\u6697\u4e2d\u76f8\u52a9\u4e4b\u529b\u3002 \u3000\u3000\u8fd9\u65f6\u90a3\u65b9\u745c\u95fb\u8fc5\u4e5f\u5954\u4e86\u8fdb\u6765\uff0c\u76ee\u7779\u65b9\u73de\u53d7\u4f24\u4e4b\u72b6\uff0c\u4e0d\u7981\u6697\u7136\u6cea\u4e0b\uff0c\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u4ec0\u4e48\u4eba\u6253\u4f24\u4e86\u4f60\u5462\uff1f\u201d \u3000\u3000\u65b9\u73de\u6447\u6447\u5934\u9053\uff1a\u201c\u6211\u4e0d\u8ba4\u8bc6\u90a3\u4e9b\u4eba\u3002\u201d\u76ee\u5149\u8f6c\u5230\u4e1c\u8033\u8001\u4eba\u7684\u8eab\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u8001\u4e1c\u4f60\u4e0d\u7528\u5bb3\u6015\uff0c\u8fd9\u4e8b\u548c\u4f60\u65e0\u5173\uff0c\u7239\u7239\u56de\u6765\u95ee\u8d77\uff0c\u81ea\u6709\u6211\u4e00\u4eba\u627f\u62c5\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u4f38\u51fa\u624b\u53bb\u8f7b\u8f7b\u5728\u65b9\u73de\u8eab\u4e0a\u62cd\u4e86\u4e24\u4e0b\u9053\uff1a\u201c\u5c11\u7237\u5b89\u5fc3\u7761\u5427\uff0c\u8001\u7237\u56de\u6765\uff0c\u8d23\u9a82\u8001\u6c49\u4e24\u53e5\uff0c\u90a3\u4e5f\u662f\u5e94\u8be5\u7684\u4e8b\u3002\u201d \u3000\u3000\u65b9\u73de\u4f3c\u662f\u6781\u4e3a\u542c\u8bdd\uff0c\u95fb\u8a00\u95ed\u4e0a\u53cc\u76ee\uff0c\u5f88\u5feb\u5730\u7761\u719f\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u65b9\u745c\u6447\u6447\u5934\u53f9\u9053\uff1a\u201c\u4e1c\u4f2f\u4f2f\uff0c\u6211\u8fd9\u4f4d\u5144\u5f1f\uff0c\u4ece\u5c0f\u5c31\u88ab\u53d4\u7236\u5ba0\u574f\u4e86\uff0c\u5982\u6709\u5f97\u7f6a\u4f60\u8001\u4eba\u5bb6\u4e4b\u5904\uff0c\u8fd8\u671b\u4f60\u4e0d\u8981\u89c1\u602a\u624d\u597d\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u4ee4\u5f1f\u8bf4\u8bdd\u867d\u7136\u96be\u542c\u4e00\u4e9b\uff0c\u4f46\u4ed6\u5fc3\u5730\u8fd8\u5f88\u5584\u826f\u3002\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u6211\u7684\u5e95\u7ec6\uff0c\u4f60\u4e5f\u8bb8\u5df2\u7ecf\u77e5\u9053\u5f88\u591a\uff0c\u8fd9\u756a\u4e8b\u6545\u4e4b\u540e\uff0c\u4e5f\u8bb8\u6211\u65e0\u6cd5\u5728\u6b64\u591a\u7559\u4e0b\u53bb\uff0c\u5b69\u5b50\uff0c\u5982\u82e5\u6211\u65e0\u6cd5\u5728\u6b64\u591a\u7559\uff0c\u4f60\u613f\u4e0d\u613f\u610f\u8ddf\u7740\u6211\u4e00\u8d77\u8d70\u5462\uff1f\u201d \u3000\u3000\u65b9\u745c\u4f4e\u5934\u6c89\u541f\u4e86\u4e00\u9635\u9053\uff1a\u201c\u54b1\u4eec\u5230\u54ea\u91cc\u53bb\u5462\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u5929\u6daf\u6d77\u89d2\uff0c\u8d8a\u662f\u9690\u79d8\u7684\u5730\u65b9\u8d8a\u597d\u3002\u201d \u3000\u3000\u65b9\u745c\u6c89\u541f\u4e86\u4e00\u9635\u9053\uff1a\u201c\u4f2f\u6bcd\u5f85\u6211\u597d\uff0c\u6709\u5982\u4eb2\u751f\u5b50\u4e00\u822c\u770b\u5f85\uff0c\u5982\u662f\u548c\u4f60\u5077\u8d70\uff0c\u4f2f\u6bcd\u4e00\u5b9a\u5f88\u4f24\u5fc3\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u8001\u592b\u4e5f\u4e0d\u60f3\u5982\u6b64\uff0c\u4f46\u5982\u60c5\u52bf\u903c\u4eba\uff0c\u90a3\u4e5f\u662f\u65e0\u53ef\u5948\u4f55\u7684\u4e8b\u4e86\uff01\u201d \u3000\u3000\u65b9\u745c\u6c89\u541f\u9053\uff1a\u201c\u5982\u662f\u522b\u65e0\u9009\u62e9\uff0c\u665a\u8f88\u81ea\u7136\u8ffd\u968f\u8001\u524d\u8f88\u4e86\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u7a81\u7136\u6d41\u4e0b\u6cea\u6765\uff0c\u63a5\u9053\uff1a\u201c\u4f2f\u6bcd\u53d7\u6211\u9003\u5c18\u907f\u4e16\u7684\u6bcd\u4eb2\u6240\u6258\uff0c\u5bf9\u6211\u4e4b\u597d\uff0c\u5b9e\u662f\u5c24\u80dc\u4eb2\u751f\u4e4b\u5b50\uff0c\u6211\u5982\u82e5\u4e0d\u544a\u800c\u522b\uff0c\u5979\u5fc3\u4e2d\u81ea\u7136\u60b2\u4f24\u65e0\u6bd4\uff0c\u6211\u5b9e\u5728\u4e0d\u613f\u4f7f\u5979\u4f24\u5fc3\uff0c\u5982\u662f\u54b1\u4eec\u80fd\u591f\u4e0d\u8d70\uff0c\u90a3\u662f\u6700\u597d\u4e0d\u8fc7\u4e86\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u7f13\u7f13\u4ed6\u8bf4\u9053\uff1a\u201c\u4f60\u80fd\u6709\u5982\u6b64\u77e5\u6069\u5fc5\u62a5\u7684\u5b5d\u5fc3\u6211\u662f\u66f4\u5b89\u5fc3\u4e86\u3002\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\u9053\uff1a\u201c\u4f60\u53bb\u4f11\u606f\u5427\uff0c\u6211\u5728\u8fd9\u91cc\u5b88\u5019\u4ed6\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u4f2f\u4f2f\u53bb\u5427\uff01\u6211\u5b88\u5019\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u8c08\u8bdd\u4e4b\u95f4\uff0c\u7a81\u7136\u6b65\u5c65\u58f0\u54cd\uff0c\u65b9\u5929\u6210\u4e00\u9a6c\u5f53\u5148\uff0c\u884c\u8fdb\u623f\u4e2d\u3002 \u3000\u3000\u4ed6\u672c\u662f\u8138\u5e26\u7b11\u5bb9\uff0c\u4f46\u8fdb\u5165\u623f\u4e2d\u4e4b\u540e\u8138\u8272\u7a81\u7136\u51b7\u4e86\u4e0b\u6765\uff0c\u76ee\u5149\u6ce8\u5728\u65b9\u7565\u8eab\u4e0a\uff0c\u6253\u91cf\u4e86\u4e00\u9635\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5c11\u7237\u548c\u4eba\u6597\u6bb4\uff0c\u53d7\u4e86\u4f24\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u4e45\u5386\u98ce\u9669\u7684\u4eba\u7269\uff0c\u5fc3\u4e2d\u5c3d\u7ba1\u7126\u8651\u3001\u75db\u75bc\uff0c\u4f46\u4ecd\u7136\u4fdd\u6301\u7740\u9547\u9759\uff0c\u55ef\u4e86\u4e00\u58f0\u9053\uff1a \u3000\u3000\u201c\u4f24\u5f97\u5f88\u91cd\u5417\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u4f24\u5f97\u867d\u7136\u4e0d\u8f7b\uff0c\u4f46\u5374\u4e0d\u788d\u4e8b\u4e86\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u7f13\u6b65\u884c\u8fd1\u6728\u69bb\uff0c\u4ed4\u7ec6\u77a7\u4e86\u7231\u5b50\u4e00\u9635\uff0c\u4f46\u5374\u5e76\u672a\u4f38\u624b\u89e6\u52a8\uff0c\u4e3e\u624b\u4e00\u6325\uff0c\u9053\uff1a \u3000\u3000\u201c\u54b1\u4eec\u5230\u5916\u95f4\u8c08\u8c08\u5427\uff01\u201d \u3000\u3000\u65b9\u745c\u8f6c\u8eab\u671b\u53bb\uff0c\u53ea\u89c1\u65b9\u592b\u4eba\u548c\u4e24\u4e2a\u52b2\u88c5\u5927\u6c49\uff0c\u5e76\u80a9\u7ad9\u5728\u95e8\u53e3\u3002 \u3000\u3000\u5de6\u9762\u4e00\u4e2a\u7ea6\u4e94\u65ec\u4ee5\u4e0a\uff0c\u866c\u9aef\u5982\u621f\uff0c\u8eab\u7740\u6df1\u7070\u52b2\u88c5\uff0c\u5916\u7740\u7384\u8272\u62ab\u51e4\uff0c\u6d53\u7709\u73af\u76ee\uff0c\u795e\u6001\u5341\u5206\u5a01\u731b\u3002 \u3000\u3000\u53f3\u9762\u4e00\u4e2a\u5e74\u7ea6\u4e09\u65ec\u4ee5\u4e0a\uff0c\u767d\u9762\u65e0\u987b\uff0c\u770b\u4e0a\u53bb\u5341\u5206\u5730\u6e05\u4fca\uff0c\u53ea\u662f\u7626\u4e86\u4e9b\u3002 \u3000\u3000\u65b9\u592b\u4eba\u8138\u4e0a\u4e00\u7247\u51c4\u4f24\uff0c\u671b\u4e86\u4ef0\u8eba\u5728\u5e8a\u4e0a\u7684\u7231\u5b50\u4e00\u773c\uff0c\u6b32\u8a00\u53c8\u6b62\u3002 \u3000\u3000\u65b9\u5929\u6210\u4e3e\u6b65\u884c\u5230\u95e8\u53e3\uff0c\u4f4e\u58f0\u5bf9\u90a3\u866c\u9aef\u7684\u5927\u6c49\u9053\uff1a\u201c\u5927\u54e5\uff0c\u73de\u513f\u53d7\u4e86\u70b9\u4f24\uff0c\u6b64\u523b\u7761\u5f97\u5f88\u719f\uff0c\u54b1\u4eec\u5230\u5916\u95f4\u8c08\u8c08\u5427\uff01\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u7f13\u7f13\u4ed6\u8bf4\u9053\uff1a\u201c\u4f24\u5f97\u5f88\u91cd\u5417\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u5c31\u5c0f\u5f1f\u89c2\u5bdf\u6240\u5f97\uff0c\u4f24\u7684\u5e94\u8be5\u4e0d\u8f7b\uff0c\u4f46\u4ed6\u6c14\u606f\u8fd8\u5f88\u5747\u5300\u3002\u201d \u3000\u3000\u90a3\u767d\u9762\u65e0\u987b\u7684\u4e2d\u5e74\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u770b\u6765\u73de\u4f84\u7684\u5185\u529f\uff0c\u8fdb\u5883\u5f88\u5927\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4ee5\u4ed6\u7684\u529f\u529b\u800c\u8bba\uff0c\u53d7\u6b64\u91cd\u4f24\uff0c\u51b3\u96be\u8fd9\u7b49\u719f\u7761\u9547\u5b9a\uff0c\u5176\u95f4\u5fc5\u7136\u6709\u4eba\u76f8\u52a9\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\u9053\uff1a\u201c\u662f\u5426\u662f\u522b\u4eba\u6709\u610f\u4f24\u4ed6\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c0f\u5f1f\u63a8\u60f3\uff0c\u8fd8\u4e0d\u81f4\u4e8e\u6709\u4eba\u6709\u610f\u4f24\u4ed6\u2026\u2026\u3002\u201d\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u8be6\u7ec6\u5185\u60c5\uff0c\u8fd8\u672a\u4e86\u7136\u3002\u201d \u3000\u3000\u7fa4\u8c6a\u7b49\u4e00\u9f50\u9000\u51fa\uff0c\u5230\u5385\u4e2d\u5750\u4e0b\u6765\u3002 \u3000\u3000\u65b9\u5929\u6210\u5c3d\u91cf\u7ef4\u6301\u7740\u5fc3\u60c5\u7684\u5e73\u9759\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\u4e09\u5f1f\u8bf7\u5750\u3002\u201d \u3000\u3000\u90a3\u866c\u9aef\u5927\u6c49\u548c\u767d\u9762\u4e2d\u5e74\u4eba\u7f13\u7f13\u5730\u5750\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u65b9\u592b\u4eba\u6291\u5236\u7740\u7126\u8651\u3001\u60b2\u4f24\uff0c\u4eb2\u624b\u4e3a\u4e09\u4eba\u5949\u4e0a\u9999\u8336\u3002 \u3000\u3000\u65b9\u5929\u6210\u559d\u4e86\u4e00\u53e3\u8336\uff0c\u9053\uff1a\u201c\u745c\u513f\uff0c\u4f60\u5144\u5f1f\u600e\u4e48\u4f1a\u88ab\u4eba\u6253\u4f24\uff1f\u201d \u3000\u3000\u65b9\u745c\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u73de\u5f1f\u8981\u51fa\u53bb\u73a9\u800d\uff0c\u4e1c\u8001\u4f2f\u529d\u4ed6\u4e0d\u8981\u53bb\uff0c\u73de\u5f1f\u4e0d\u80af\u542c\uff0c\u72ec\u81ea\u51fa\u53bb\u73a9\u800d\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4ee5\u540e\u5462\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u63a5\u9053\uff1a\u201c\u8001\u6c49\u529d\u4e0d\u4e0b\u5c11\u7237\uff0c\u53ea\u597d\u8ffd\u51fa\u5bfb\u627e\uff0c\u8001\u6c49\u627e\u5230\u4ed6\u7684\u65f6\u5019\uff0c\u5c11\u7237\u5df2\u7ecf\u8eab\u53d7\u91cd\u4f24\u4e86\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4f60\u6551\u4ed6\u56de\u6765\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u6c89\u541f\u4e86\u4e00\u9635\u9053\uff1a\u201c\u8001\u6c49\u62b1\u4ed6\u56de\u6765\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u6253\u4f24\u4e86\u4ed6\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u8001\u6c49\u4e0d\u8ba4\u8bc6\u90a3\u4e9b\u4eba\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4ed6\u4eec\u7a7f\u7684\u662f\u4ec0\u4e48\u8863\u670d\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u4e00\u4e2a\u7a7f\u7740\u9ec4\u8863\u7684\u5c11\u5e74\uff0c\u4e24\u4e2a\u7a7f\u9ed1\u8863\u7684\u5927\u6c49\uff0c\u4e00\u8138\u5927\u9ebb\u5b50\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u601d\u7d22\u7247\u523b\uff0c\u628a\u7ecf\u8fc7\u4e4b\u60c5\u8bf4\u4e86\u4e00\u904d\u3002 \u3000\u3000\u90a3\u866c\u9aef\u7684\u5927\u6c49\u7f13\u7f13\u4ed6\u8bf4\u9053\uff1a\u201c\u4f60\u62b1\u65b9\u8d24\u4f84\u56de\u6765\u6b64\u5730\uff0c\u9ec4\u8863\u5c11\u5e74\u80af\u653e\u4f60\u4eec\u8d70\u5417\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u6c89\u541f\u4e00\u9635\u9053\uff1a\u201c\u8001\u6c49\u773c\u770b\u5c11\u7237\u5c71\u5f97\u5f88\u91cd\uff0c\u5f53\u4e0b\u5fc3\u4e2d\u5341\u5206\u7d27\u5f20\uff0c\u5306\u5306\u5fd9\u5fd9\u5730\u62b1\u8d77\u5c11\u7237\u5c31\u8dd1\uff0c\u90a3\u4e24\u4e2a\u9ed1\u8863\u5927\u6c49\u867d\u60f3\u62e6\u4f4f\uff0c\u5374\u4e3a\u8001\u6c49\u820d\u547d\u51b2\u4e86\u51fa\u6765\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u4e1c\u8001\u4e08\u8f9b\u82e6\u4e86\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u4e3e\u624b\u4e00\u6325\u9053\uff1a\u201c\u4f60\u53ef\u4ee5\u4f11\u606f\u53bb\u4e86\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u6b20\u8eab\u4e00\u793c\u8f6c\u8eab\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u866c\u9aef\u5927\u6c49\u76ee\u7779\u4e1c\u8033\u8001\u4eba\u80cc\u5f71\u53bb\u540e\uff0c\u624d\u7f13\u7f13\u4ed6\u8bf4\u9053\uff1a\u201c\u4e8c\u5f1f\uff0c\u8fd9\u4f4d\u8001\u4e1c\u662f\u4f55\u8bb8\u4eba\u7269\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u5c0f\u5f1f\u5728\u9014\u4e2d\u6551\u5f97\u7684\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4ed6\u7684\u59d3\u540d\u5417\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4ed6\u81ea\u79f0\u662f\u4e1c\u8033\u8001\u4eba\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u6c89\u541f\u4e86\u4e00\u9635\u9053\uff1a\u201c\u53ea\u6015\u90a3\u4e0d\u662f\u4ed6\u771f\u5b9e\u7684\u59d3\u540d\u3002\u201d \u3000\u3000\u90a3\u767d\u9762\u7626\u5b50\u9053\uff1a\u201c\u65b9\u8d24\u4f84\u88ab\u4eba\u6253\u4f24\uff0c\u96be\u9053\u5c31\u6b64\u7f62\u4f11\u4e0d\u6210\uff1f\u81ea\u7136\u4e0d\u80fd\u5c31\u6b64\u7f62\u4f11\uff0c\u5c0f\u5144\u7acb\u523b\u53bb\u62dc\u4f1a\u7a0b\u5b50\u671b\uff0c\u6253\u542c\u4e00\u4e0b\u90a3\u9ec4\u8863\u4eba\u7684\u6765\u8def\uff0c\u201d \u3000\u3000\u65b9\u5929\u6210\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\u9053\uff1a\u201c\u6211\u770b\u7b97\u4e86\uff01\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u73de\u4f84\u53d7\u6b64\u91cd\u4f24\u54b1\u4eec\u5c31\u8fd9\u822c\u7f62\u4f11\uff0c\u65e5\u540e\u4f20\u5230\u6c5f\u6e56\u4e4b\u4e0a\uff0c\u5c82\u4e0d\u662f\u8981\u4e3a\u4eba\u803b\u7b11\u5417\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u73de\u513f\u8fd9\u70b9\u5e74\u7eaa\uff0c\u81ea\u7136\u4e0d\u4f1a\u548c\u4eba\u7ed3\u4ec7\uff0c\u6211\u53c8\u5f88\u5c11\u5e26\u4ed6\u5728\u6c5f\u6e56\u4e4b\u4e0a\u884c\u8d70\uff0c\u8ba4\u8bc6\u4ed6\u7684\u4eba\u7edd\u65e0\u4ec5\u6709\uff0c\u8fd9\u6b21\uff0c\u4ed6\u88ab\u4eba\u6253\u4f24\uff0c\u53ea\u6015\u662f\u5076\u7136\u53d1\u751f\u7684\u4e8b\u4ef6\uff0c\u800c\u4e14\u8be6\u7ec6\u7ecf\u8fc7\u4e4b\u60c5\uff0c\u76ee\u4e0b\u8fd8\u4e0d\u4e86\u89e3\u2026\u2026\u201d\u4ed6\u8bed\u97f3\u4e00\u987f\uff0c\u53c8\u7f13\u7f13\u4ed6\u8bf4\u9053\uff1a\u201c\u73de\u513f\u7684\u4f24\u52bf\uff0c\u4e5f\u4e0d\u5f88\u91cd\uff0c\u5c0f\u5f1f\u60f3\u5230\u6b64\u4e8b\uff0c\u8ffd\u7a76\u8d77\u6765\uff0c\u4e0d\u77e5\u662f\u5982\u4f55\u4e00\u4e2a\u7ed3\u679c\uff0c\u6b64\u523b\uff0c\u54b1\u4eec\u6b63\u4e8b\u5f88\u591a\uff0c\u4e0d\u7528\u4e3a\u72ac\u5b50\u7684\u5c0f\u4e8b\uff0c\u803d\u8bef\u54b1\u4eec\u7684\u6b63\u4e8b\u4e86\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u8bdd\u867d\u5982\u6b64\uff0c\u4f46\u54b1\u4eec\u4e5f\u4e0d\u80fd\u4e0d\u7ba1\u73de\u4f84\u3002\u201d \u3000\u3000\u90a3\u767d\u9762\u4e2d\u5e74\u4eba\u7a81\u7136\u5f00\u53e3\u8bf4\u9053\uff1a\u201c\u7b97\u65f6\u95f4\u8def\u7a0b\uff0c\u54b1\u4eec\u5728\u5609\u5b9a\u5e9c\u8fd8\u53ef\u4ee5\u9017\u7559\u4e09\u65e5\uff0c\u505c\u4e0a\u4e09\u5929\uff0c\u4e0d\u8bba\u4ec0\u4e48\u4e8b\u90fd\u5e94\u8be5\u529e\u597d\u4e86\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u4e09\u5f1f\u8bf4\u7684\u4e0d\u9519\uff0c\u8fd9\u4e2a\u6c14\u54b1\u4eec\u51b3\u7136\u4e0d\u80fd\u5fcd\u53d7\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u5fc3\u4e2d\u53eb\u7231\u5b50\u53d7\u4f24\u4e00\u4e8b\u5c82\u6709\u4e0d\u62a5\u4e4b\u7406\uff0c\u53ea\u662f\u5f62\u5f0f\u4e0a\u4e0d\u5f97\u4e0d\u4f5c\u51fa\u4e00\u53e6\u4e0d\u5728\u4e4e\u795e\u6001\uff0c\u4f46\u95fb\u90a3\u866c\u9aef\u5927\u6c49\u548c\u767d\u9762\u4e2d\u5e74\u518d\u4e09\u575a\u6301\u4e4b\u540e\uff0c\u4e5f\u5c31\u987a\u6c34\u63a8\u821f\u4ed6\u8bf4\u9053\uff1a\u201c\u5927\u54e5\u3001\u4e09\u5f1f\u65e2\u7136\u575a\u6301\u8981\u67e5\u660e\u5185\u60c5\uff0c\u54b1\u4eec\u53ea\u597d\u5728\u6b64\u505c\u7559\u4e24\u65e5\u4e86\u3002\u201d \u3000\u3000\u767d\u9762\u4e2d\u5e74\u4eba\u7a81\u7136\u8bf4\u9053\uff1a\u201c\u5c0f\u5f1f\u6709\u4e2a\u5f88\u5947\u602a\u7684\u611f\u89c9\uff0c\u4e0d\u77e5\u4e8c\u54e5\u4ee5\u4e3a\u5982\u4f55\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u767d\u9762\u4e2d\u5e74\u4eba\u9053\uff1a\u201c\u5c0f\u5f1f\u603b\u611f\u89c9\u90a3\u4e1c\u8033\u8001\u4eba\u8fd8\u6709\u8bdd\u672a\u8bb2\u5b8c\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u4e5f\u6709\u8fd9\u611f\u89c9\uff0c\u4f46\u4ed6\u65e2\u7136\u4e0d\u613f\u8bf4\uff0c\u5c0f\u5144\u4e5f\u4e0d\u60f3\u8ffd\u95ee\u4e8e\u4ed6\u4e86\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u4e0d\u7528\u8ffd\u95ee\u4ed6\u4e86\uff0c\u5c0f\u5144\u548c\u90a3\u7a0b\u5b50\u671b\u6709\u8fc7\u6570\u9762\u4e4b\u4ea4\uff0c\u4ed6\u66fe\u7ecf\u548c\u5c0f\u5144\u8c08\u8fc7\uff0c\u5609\u5b9a\u5730\u9762\u4e0d\u8bba\u53d1\u751f\u4ec0\u4e48\u4e8b\uff0c\u90fd\u8981\u6211\u627e\u4ed6\u3002\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u76ee\u4e0b\u60f3\u67e5\u660e\u7ecf\u8fc7\u4e4b\u60c5\u5012\u5f88\u5bb9\u6613\uff0c\u53ea\u8981\u627e\u5230\u73b0\u573a\u4e00\u95ee\u4e0d\u5c31\u4e86\u7136\u4e86\uff0c\u4f46\u54b1\u4eec\u8981\u67e5\u660e\u90a3\u9ec4\u8863\u5c11\u5e74\u7684\u5e95\u7ec6\uff0c\u53ea\u6015\u4e0d\u662f\u4e00\u822c\u4eba\u80fd\u591f\u77e5\u6653\u4e86\u3002\u201d \u3000\u3000\u767d\u9762\u4e2d\u5e74\u4eba\u9053\uff1a\u201c\u5927\u54e5\u8bf4\u7684\u662f\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u53cd\u6b63\u6211\u8981\u5230\u7a0b\u5b50\u671b\u90a3\u8fb9\u8d70\u4e00\u8d9f\uff0c\u4ed6\u5728\u5609\u5b9a\u5730\u9762\u4e0a\u5341\u5206\u719f\u6089\uff0c\u5c0f\u5144\u53bb\u95ee\u95ee\u4ed6\u518d\u8bf4\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u5927\u54e5\u5148\u8bf7\u5403\u676f\u8336\uff0c\u4f11\u606f\u7247\u523b\u518d\u53bb\u5982\u4f55\uff1f\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u5c0f\u5144\u53bb\u53bb\u5c31\u6765\u3002\u201d \u3000\u3000\u8bdd\u843d\u53e3\uff0c\u4eba\u5df2\u5230\u6570\u4e08\u4e4b\u5916\u3002 \u3000\u3000\u65b9\u5929\u6210\u76ee\u5149\u8f6c\u5230\u90a3\u767d\u9762\u4e2d\u5e74\u4eba\u7684\u8138\u4e0a\uff0c\u9053\uff1a\u201c\u4fca\u5f1f\uff0c\u5c31\u5c0f\u5144\u89c2\u67e5\u6240\u5f97\uff0c\u5927\u54e5\u8868\u9762\u4e0a\u867d\u7136\u9547\u9759\uff0c\u4f46\u5185\u5fc3\u4e2d\u5b9e\u662f\u5341\u5206\u5730\u7126\u6025\uff0c\u5c0f\u5144\u4e0d\u613f\u4e3a\u72ac\u5b50\u7684\u4e8b\uff0c\u518d\u66ff\u5927\u54e5\u6dfb\u9ebb\u70e6\u3002\u201d \u3000\u3000\u767d\u9762\u4e2d\u5e74\u6c89\u541f\u4e86\u4e00\u9635\u9053\uff1a\u201c\u4e8c\u54e5\u7684\u7528\u5fc3\uff0c\u5c0f\u5f1f\u5341\u5206\u7684\u6e05\u695a\uff0c\u7136\u6865\u5f52\u4fa8\uff0c\u8def\u5f52\u8def\uff0c\u4e0d\u80fd\u8bf4\u660e\u54b1\u4eec\u6709\u8981\u4e8b\uff0c\u73de\u4f84\u88ab\u4eba\u6253\u4f24\u4e00\u4e8b\uff0c\u54b1\u4eec\u5c31\u4e0d\u95fb\u4e0d\u95ee\u4e86\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u7709\u5b87\u95f4\u5fe7\u8651\u91cd\u91cd\uff0c\u534a\u664c\u4e4b\u540e\uff0c\u624d\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u592a\u5927\u4e86\uff0c\u5c0f\u5144\u4e0d\u613f\u518d\u8282\u5916\u751f\u679d\uff0c\u591a\u589e\u5927\u54e5\u70e6\u607c\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u65b9\u592b\u4eba\u5df2\u7ecf\u6084\u7136\u9000\u51fa\uff0c\u56de\u5230\u5185\u5ba4\u63a2\u671b\u7231\u5b50\uff0c\u5385\u623f\u4e2d\u53ea\u5269\u4e0b\u65b9\u5929\u6210\u548c\u90a3\u767d\u9762\u4e2d\u5e74\u4e24\u4e2a\u4eba\u3002 \u3000\u3000\u767d\u9762\u4e2d\u5e74\u56db\u987e\u4e86\u4e00\u773c\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4e8c\u54e5\uff0c\u5c0f\u5f1f\u6709\u51e0\u53e5\u8fc7\u8651\u4e4b\u8a00\uff0c\u8bf4\u51fa\u6765\u4e8c\u54e5\u4e0d\u8981\u591a\u5fc3\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u54b1\u4eec\u516b\u62dc\u91d1\u5170\u60c5\u5171\u751f\u6b7b\uff0c\u4fca\u5f1f\u600e\u7684\u8fd8\u8bf4\u8fd9\u7b49\u8bdd\uff0c\u201d \u3000\u3000\u767d\u9762\u4e2d\u5e74\u9053\uff1a\u201c\u6b64\u756a\u4e8b\u60c5\u7684\u53d8\u5316\uff0c\u786e\u7136\u51fa\u4e86\u6211\u4eec\u7684\u610f\u6599\u4e4b\u5916\uff0c\u4f46\u6211\u65e2\u7136\u9876\u4e86\u4e0b\u6765\uff0c\u81ea\u4e0d\u80fd\u4e2d\u9014\u754f\u7f29\uff0c\u4e2d\u5dde\u4e09\u4fa0\u4e4b\u540d\u4e5f\u4e0d\u80fd\u56e0\u6b64\u800c\u8499\u6c61\uff0c\u6211\u770b\u5927\u54e5\u7684\u610f\u601d\u662f\uff0c\u5b81\u540d\u5b58\u8eab\u4e0d\u5728\u2026\u2026\u201d \u3000\u3000\u58f0\u97f3\u7a81\u7136\u653e\u4f4e\uff0c\u63a5\u9053\uff1a\u201c\u6211\u548c\u5927\u54e5\u90fd\u672a\u5a36\u59bb\u80b2\u5b50\uff0c\u5c06\u6765\u54b1\u4eec\u767e\u5e74\u4e4b\u540e\uff0c\u73de\u513f\u662f\u552f\u4e00\u5230\u54b1\u4eec\u5893\u524d\u70e7\u5316\u7eb8\u94b1\u7684\u4eba\uff0c\u56e0\u6b64\u5ac2\u592b\u4eba\u548c\u73de\u513f\uff0c\u4f3c\u662f\u7528\u4e0d\u7740\u6d89\u8fd9\u6b21\u4e4b\u9669\u2026\u2026\u201d \u3000\u3000\u65b9\u5929\u6210\u82e6\u7b11\u4e00\u4e0b\u9053\uff1a\u201c\u4e09\u5f1f\u7528\u5fc3\uff0c\u5c0f\u5144\u660e\u767d\uff0c\u4e0d\u8fc7\uff0c\u54b1\u4eec\u5144\u5f1f\u5982\u82e5\u6b7b\u53bb\uff0c\u5bf9\u65b9\u51b3\u4e0d\u4f1a\u9976\u4f60\u5ac2\u5ac2\u548c\u73de\u513f\u3002\u201d \u3000\u3000\u767d\u9762\u4e2d\u5e74\u4eba\u9053\uff1a\u201c\u56e0\u6b64\u54b1\u4eec\u5fc5\u5f97\u672a\u96e8\u94dc\u7f28\uff0c\u65e9\u4f5c\u5b89\u6392\u2026\u2026\u201d \u3000\u3000\u957f\u957f\u5410\u4e00\u53e3\u6c14\uff0c\u63a5\u9053\uff1a\u201c\u5929\u4e0b\u4e4b\u5927\uff0c\u4f55\u5904\u4e0d\u53ef\u4ee5\u5b89\u8eab\u7acb\u547d\uff0c\u4e8c\u54e5\u5fc5\u53ef\u4ee5\u60f3\u5230\u4e00\u5904\u5b89\u6392\u5ac2\u5ac2\u53ca\u73de\u513f\u7684\u9690\u79d8\u4e4b\u5730\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4f60\u5ac2\u5ac2\u7ecf\u5e38\u548c\u6211\u4e00\u8d77\u5728\u6c5f\u6e56\u4e4b\u4e0a\u8d70\u52a8\uff0c\u8ba4\u8bc6\u5979\u7684\u4eba\u5f88\u591a\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u6211\u77a7\u6b64\u4e8b\u4e0d\u6613\u5b89\u6392\u3002\u201d \u3000\u3000\u767d\u9762\u4e2d\u5e74\u4eba\u9053\uff1a\u201c\u5982\u662f\u4e8c\u54e5\u5f53\u771f\u60f3\u4e0d\u51fa\u6765\uff0c\u5c0f\u5f1f\u5012\u6709\u4e00\u4e2a\u53bb\u5904\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u767d\u9762\u4e2d\u5e74\u4eba\u63a5\u9053\uff1a\u201c\u5c0f\u5f1f\u6709\u4e00\u53d4\u7236\uff0c\u8fdc\u5728\u6c5f\u5357\u884c\u533b\uff0c\u4ed6\u975e\u6c5f\u6e56\u4e2d\u4eba\uff0c\u4e0d\u59a8\u5148\u628a\u4e8c\u5ac2\u548c\u73de\u4f84\u9001\u5230\u4ed6\u90a3\u91cc\u53bb\u2026\u2026\u201d \u3000\u3000\u65b9\u5929\u6210\u63a5\u9053\uff1a\u201c\u6b64\u4e8b\u4e0d\u59a5\u3002\u201d \u3000\u3000\u767d\u9762\u4e2d\u5e74\u4eba\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u54b1\u4eec\u5982\u906d\u5927\u53d8\uff0c\u5bf9\u65b9\u5fc5\u6709\u65a9\u8349\u9664\u6839\u4e4b\u5fc3\uff0c\u4f60\u5ac2\u5ac2\u548c\u73de\u513f\u5b9a\u7136\u96be\u9003\u4ed6\u4eec\u7684\u8033\u76ee\uff0c\u4ee4\u53d4\u975e\u6b66\u6797\u4e2d\u4eba\uff0c\u5982\u4f55\u80fd\u591f\u5bf9\u4ed8\u5f97\u4e86\u6c5f\u6e56\u4e2d\u4eba\u7269\u7684\u8be1\u8ba1\uff0c\u88ab\u4eba\u67e5\u51fa\uff0c\u53cd\u800c\u8fde\u7d2f\u4ee4\u53d4\u4e86\u3002\u201d \u3000\u3000\u767d\u9762\u4e2d\u5e74\u4eba\u9053\uff1a\u201c\u4e8c\u54e5\u8bf4\u7684\u4e5f\u662f\u3002\u201d \u3000\u3000\u4e24\u4eba\u5bf9\u671b\u4e86\u4e00\u9635\uff0c\u9ed8\u7136\u4e0d\u8bed\u3002 \u3000\u3000\u4e0d\u77e5\u8fc7\u53bb\u4e86\u591a\u5c11\u65f6\u95f4\uff0c\u7a81\u7136\u4e00\u9635\u6b65\u5c65\u4e4b\u58f0\uff0c\u4f20\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u62ac\u5934\u671b\u53bb\u53ea\u89c1\u866c\u9aef\u5927\u6c49\u5f53\u5148\u8fdb\u95e8\uff0c\u9053\u201c\u4e24\u4f4d\u8d24\u5f1f\uff0c\u5c0f\u5144\u66ff\u4e24\u4f4d\u5f15\u89c1\u4e00\u4f4d\u670b\u53cb\u2026\u2026\u201d \u3000\u3000\u56de\u76ee\u671b\u7740\u90a3\u9752\u886b\u8001\u4eba\u63a5\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u7a0b\u5b50\u671b\u5144\uff0c\u4e43\u5609\u5b9a\u5730\u9762\uff0c\u6c34\u65f1\u4e24\u8def\u7684\u9f99\u5934\u8001\u5927\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u3001\u767d\u9762\u4e2d\u5e74\u4eba\u9f50\u9f50\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u4e45\u4ef0\u5927\u540d\uff01\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u4e0d\u6562\uff0c\u4e24\u4f4d\u662f\u65b9\u5927\u4fa0\u548c\u77f3\u5927\u4fa0\u4e86\uff01\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u5144\u5f1f\u65b9\u6210\u3002\u201d \u3000\u3000\u767d\u9762\u4e2d\u5e74\u4eba\u63a5\u9053\uff1a\u201c\u5144\u5f1f\u77f3\u4fca\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u4ef0\u6155\u4e24\u4f4d\u4e45\u77e3\uff01\u4eca\u65e5\u6709\u5e78\u5f97\u4f1a\u2026\u2026\u201d\u56de\u76ee\u671b\u4e86\u90a3\u866c\u9aef\u5927\u6c49\uff0c\u63a5\u9053\uff1a \u3000\u3000\u201c\u5144\u5f1f\u66fe\u548c\u90d1\u5927\u4fa0\u6709\u8fc7\u6570\u9762\u4e4b\u4ea4\u3002\u201d \u3000\u3000\u65b9\u65e0\u6210\u3001\u77f3\u4fca\u9f50\u58f0\u5e94\u9053\uff1a\u201c\u6211\u7b49\u4ea6\u5e38\u542c\u5927\u54e5\u8bf4\u8d77\u7a0b\u5144\uff0c\u5728\u5609\u5b9a\u5730\u9762\u7684\u5a01\u671b\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u54c8\u54c8\u4e00\u7b11\u9053\uff0c\u201c\u5c0f\u5c40\u9762\uff0c\u5728\u4e09\u4f4d\u9762\u524d\uff0c\u5b9e\u53eb\u5144\u5f1f\u60ed\u6127\u3002\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u521a\u624d\u533a\u533a\u5df2\u5f97\u77e5\u90d1\u5927\u5feb\u8bf4\u77e5\u5185\u60c5\uff0c\u65b9\u5927\u4fa0\u7231\u5b50\u88ab\u4eba\u6253\u4f24\uff0c\u800c\u4e14\u53c8\u5728\u5609\u5b9a\u5730\u9762\u4e0a\uff0c\u5144\u5f1f\u5b9e\u662f\u4e0d\u80fd\u5750\u89c6\u3002\u201d \u3000\u3000\u77f3\u4fca\u4e00\u6325\u624b\u9053\uff1a\u201c\u90a3\u5c31\u6709\u52b3\u7a0b\u5144\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u5144\u5f1f\u5df2\u6d3e\u4eba\u624b\uff0c\u67e5\u660e\u5185\u60c5\uff0c\u6211\u76f8\u4fe1\u4eca\u591c\u4e4b\u524d\uff0c\u5b9a\u7136\u80fd\u7ed9\u4e09\u4f4d\u4e00\u4e2a\u4ea4\u4ee3\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u5144\u5f1f\u4e4b\u610f\uff0c\u53ea\u60f3\u67e5\u660e\u7ecf\u8fc7\u4e4b\u60c5\u4e5f\u8bb8\u9519\u5728\u5c0f\u5927\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u4e0d\u7ba1\u5982\u4f55\uff0c\u4ee4\u516c\u5b50\u603b\u662f\u53d7\u4e86\u4f24\uff0c\u5bf9\u65b9\u5c31\u7b97\u662f\u5728\u7406\uff0c\u4e5f\u4e0d\u80fd\u51fa\u624b\u6253\u4eba\uff0c\u4f55\u51b5\uff0c\u6253\u7684\u8fd8\u662f\u4e00\u4f4d\u5b69\u7ae5\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4e0d\u80fd\u8fd9\u6837\u8bf4\uff0c\u5f7c\u6b64\u52a8\u624b\uff0c\u81ea\u7136\u662f\u96be\u7559\u4f59\u5730\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u53f9\u606f\u4e00\u58f0\u9053\uff1a\u201c\u65b9\u4e8c\u4fa0\u7684\u98ce\u5ea6\uff0c\u5b9e\u53eb\u7a0b\u67d0\u4f69\u670d\u2026\u2026\u201d \u3000\u3000\u8c08\u8bdd\u4e4b\u95f4\uff0c\u7a81\u7136\u4e00\u4e2a\u5b8f\u4eae\u7684\u58f0\u97f3\u4f20\u6765\uff0c\u9053\uff1a\u201c\u5feb\u817f\u5f20\u4e09\uff0c\u6c42\u89c1\u5e84\u4e3b\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u5f20\u4e09\u662f\u533a\u533a\u624b\u4e0b\u4e00\u4e2a\u6700\u80fd\u5e72\u7684\u7ba1\u4e8b\uff0c\u6211\u6d3e\u4ed6\u67e5\u660e\u6b64\u4e8b\uff0c\u60f3\u5fc5\u5df2\u6709\u56de\u62a5\u6765\u4e86\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u9053\uff1a\u201c\u4f60\u8fdb\u6765\u3002\u201d \u3000\u3000\u53ea\u89c1\u4eba\u5f71\u4e00\u95ea\uff0c\u4e00\u4e2a\u7a7f\u7070\u8272\u77ed\u8902\uff0c\u817f\u4e0a\u7f20\u7740\u767d\u88f9\u817f\u7684\u6c49\u5b50\uff0c\u5feb\u6b65\u884c\u4e86\u8fc7\u6765\uff0c\u5bf9\u7740\u7a0b\u5b50\u671b\u884c\u4e86\u4e00\u793c\u9053\uff1a\u201c\u89c1\u8fc7\u5e84\u4e3b\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u8fd9\u4e09\u4f4d\u662f\u9f0e\u9f0e\u5927\u540d\u7684\u4e2d\u5dde\u4e09\u4fa0\uff0c\u4eca\u65e5\u4f60\u6709\u5e78\u80fd\u591f\u89c1\u5230\u3002\u201d \u3000\u3000\u5f20\u4e09\u8eac\u8eab\u4e00\u4e2a\u957f\u63d6\u9053\uff1a\u201c\u5c0f\u4eba\u4e45\u95fb\u4e09\u4f4d\u5927\u4fa0\u4e4b\u540d\uff0c\u4eca\u65e5\u5f97\u4f1a\uff0c\u5b9e\u662f\u4e09\u751f\u6709\u5e78\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u3001\u77f3\u4fca\u53ca\u90a3\u866c\u9aef\u5927\u6c49\u4e5f\u90fd\u5fae\u5fae\u4e00\u6b20\u8eab\uff0c\u8fd8\u4e86\u4e00\u793c\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u8f7b\u8f7b\u54b3\u4e86\u4e24\u58f0\uff0c\u9053\uff1a\u201c\u5f20\u4e09\u4f60\u6253\u542c\u7684\u4e8b\u60c5\u5982\u4f55\u4e86\uff1f\u201d \u3000\u3000\u5feb\u817f\u5f20\u4e09\u9053\uff1a\u201c\u5c0f\u7684\u5982\u672a\u529e\u59a5\u600e\u6562\u56de\u5e86\u4e3b\u7684\u8bdd\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u542c\u4ed6\u8bf4\u5df2\u7ecf\u529e\u59a5\uff0c\u8138\u4e0a\u4ea6\u89c9\u5149\u5f69\uff0c\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8bf4\u51fa\u6765\u542c\u542c\u3002\u201d \u3000\u3000\u5f20\u4e09\u9053\uff1a\u201c\u4ed6\u4eec\u4f4f\u5728\u4e00\u4e2a\u753b\u822b\u4e4b\u4e0a\uff0c\u505c\u6cca\u4e8e\u5cb7\u6c5f\u4e4b\u4e2d\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u597d\uff0c\u4f60\u53bb\u9001\u4e2a\u5e16\u5b50\u7ed9\u4ed6\uff0c\u8001\u592b\u8981\u770b\u770b\u4ed6\u4eec\u662f\u4f55\u8bb8\u4eba\u7269\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u8fd9\u4e2a\u600e\u6562\u52b3\u52a8\u7a0b\u5144\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u5609\u5b9a\u5730\u9762\u65b9\u5706\u767e\u91cc\u5185\uff0c\u4e0d\u8bba\u4f55\u8bb8\u4eba\uff0c\u63d0\u8d77\u6211\u7a0b\u67d0\u4eba\uff0c\u90fd\u4f1a\u6253\u4e2a\u62db\u547c\uff0c\u90a3\u4eba\u65e2\u7136\u4e0d\u628a\u6211\u7a0b\u67d0\u4eba\u653e\u5728\u773c\u4e2d\uff0c\u884c\u5ba2\u4e0d\u62dc\u5750\u5ba2\uff0c\u5144\u5f1f\u561b\uff01\u53ea\u597d\u53bb\u62dc\u8bbf\u4e00\u4e0b\u4ed6\u4eec\u4e86\u3002\u201d \u3000\u3000\u5f20\u4e09\u9053\uff1a\u201c\u5e84\u4e3b\u8981\u53bb\u5c0f\u7684\u5c31\u53bb\u51c6\u5907\u5e16\u5b50\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u7a0b\u5144\u4e00\u4e2a\u4eba\u53bb\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u5144\u5f1f\u518d\u5e26\u4e24\u4e2a\u52a9\u624b\u540c\u53bb\u3002\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u5728\u4e0b\u966a\u7a0b\u671b\u5f1f\u4e00\u5757\u53bb\u5982\u4f55\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u77f3\u5144\u5f1f\u8981\u53bb\u90a3\u662f\u6700\u597d\u4e0d\u8fc7\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u56de\u987e\u4e86\u866c\u9aef\u5927\u6c49\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5927\u54e5\uff0c\u5c0f\u5f1f\u662f\u5426\u4e5f\u8981\u53bb\u4e00\u8d9f\u5462\uff1f\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4f60\u53bb\u4e00\u8d9f\u4e5f\u597d\uff0c\u5c0f\u5144\u7559\u5728\u5e97\u4e2d\uff0c\u7b49\u5019\u4f60\u4eec\u6d88\u606f\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u4e00\u8eac\u8eab\u9053\uff1a\u201c\u5927\u54e5\u7a0d\u606f\u98ce\u5c18\uff0c\u5c0f\u5f1f\u4eec\u53bb\u53bb\u5c31\u6765\u3002\u201d\u8a00\u5b8c\uff0c\u5927\u6b65\u800c\u53bb\u3002 \u3000\u3000\u866c\u9aef\u5927\u6c49\u4e5f\u4e0d\u9001\u4ed6\u4eec\u51e0\u4eba\uff0c\u4f38\u624b\u5c31\u63a9\u4e0a\u623f\u95e8\u3002 \u3000\u3000\u5f20\u4e09\u5e26\u7740\u7a0b\u5b50\u671b\u3001\u65b9\u5929\u6210\u7b49\u51e0\u4eba\uff0c\u4e00\u53e3\u6c14\u8dd1\u5230\u57ce\u5916\u5cb7\u6c5f\u5cb8\u7554\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5df2\u662f\u4e8c\u66f4\u65f6\u5206\uff0c\u4f46\u7801\u5934\u65c1\u4ecd\u7136\u5341\u5206\u70ed\u95f9\u3002 \u3000\u3000\u6c5f\u4e2d\u6e14\u706b\u70b9\u70b9\uff0c\u5cb8\u4e0a\u591c\u5e02\u672a\u6563\u3002 \u3000\u3000\u5f20\u4e09\u4f38\u624b\u6307\u7740\u6cca\u5728\u516b\u4e08\u5916\uff0c\u4e00\u8258\u94f6\u8272\u7684\u753b\u7eba\uff0c\u8bf4\u9053\uff1a\u201c\u5c31\u662f\u90a3\u4e00\u8258\u8239\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u7801\u5934\u4e0a\u7684\u54e5\u4eec\uff0c\u5f04\u4e00\u8258\u5feb\u821f\u6765\u3002\u201d \u3000\u3000\u5f20\u4e09\u9053\uff1a\u201c\u4eca\u5df2\u5728\u6c5f\u7554\u7b49\u5019\u3002\u201d \u3000\u3000\u77f3\u4fca\u62ac\u5934\u770b\u53bb\uff0c\u679c\u7136\u6709\u4e00\u6761\u5c0f\u821f\uff0c\u505c\u5728\u8eab\u4fa7\u4e0d\u8fdc\u5904\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u5fae\u5fae\u4e00\u7b11\uff0c\u957f\u63d6\u8083\u5ba2\u3002 \u3000\u3000\u65b9\u5929\u6210\u5f53\u5148\u4e3e\u6b65\u800c\u884c\uff0c\u767b\u4e0a\u5c0f\u821f\u3002 \u3000\u3000\u77f3\u4fca\u7d27\u968f\u7740\u65b9\u5929\u6210\u8eab\u540e\uff0c\u7a0b\u5b50\u671b\u6700\u540e\u767b\u4e0a\u8239\uff0c\u4f4e\u58f0\u5bf9\u5f20\u4e09\u8bf4\uff1a\u201c\u62db\u547c\u4ed6\u4eec\uff0c\u51c6\u5907\u51e0\u8258\u5927\u8239\uff0c\u8fd9\u753b\u822b\u5982\u679c\u8d77\u951a\u9003\u8d70\uff0c\u597d\u5728\u6c5f\u4e2d\u62e6\u622a\u4ed6\u4eec\u3002\u201d\u5f20\u4e09\u5e94\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5e84\u4e3b\u8bf7\u653e\u5fc3\uff0c\u5c0f\u7684\u4e0d\u4f1a\u8bef\u4e8b\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u4e00\u6325\u624b\uff0c\u5c0f\u821f\u79bb\u5cb8\uff0c\u9a76\u5411\u753b\u822b\u3002 \u3000 \u7b2c\u4e8c\u56de\u3000\u5609\u5b9a\u98ce\u4e91 \u3000 \u3000\u3000\u6570\u4e08\u8ddd\u79bb\uff0c\u8f6c\u773c\u5373\u5230\uff0c\u5c0f\u821f\u9a70\u8fd1\u753b\u822b\u4e94\u5c3a\u5de6\u53f3\u65f6\uff0c\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u7ad9\u5728\u8239\u5934\u4e0a\uff0c\u6325\u6325\u624b\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u753b\u822b\u4e0a\u54ea\u4f4d\u6267\u4e8b\uff01\u201d \u3000\u3000\u90a3\u753b\u822b\u4e0a\u8231\u95e8\u5bc6\u95ed\uff0c\u800c\u4e14\u5782\u7740\u539a\u539a\u7684\u5e37\u5b50\uff0c\u867d\u7136\u70b9\u6709\u706f\u706b\uff0c\u4f46\u5728\u5916\u9762\u5374\u77a7\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u547c\u53eb\u4e00\u58f0\u4e4b\u540e\uff0c\u90a3\u822b\u95e8\u7acb\u65f6\u5927\u5f00\uff0c\u706f\u706b\u5c04\u51fa\uff0c\u53ea\u89c1\u4e00\u4e2a\u6ee1\u8138\u9ebb\u5b50\u7684\u5927\u6c49\u6025\u6b65\u800c\u51fa\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u5609\u5b9a\u7a0b\u5b50\u671b\uff0c\u540d\u4e0d\u89c1\u7ecf\u4f20\uff0c\u9601\u4e0b\u53ea\u662f\u4e0d\u8ba4\u8bc6\u3002\u201d \u3000\u3000\u90a3\u9ebb\u8138\u4e00\u6325\u624b\u9053\uff1a\u201c\u4e45\u95fb\u5927\u540d\uff0c\u6709\u5e78\u5f97\u4f1a\uff0c\u4f46\u4e0d\u77e5\u6df1\u591c\u6765\u6b64\uff0c\u6709\u4f55\u89c1\u6559\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u662f\u753b\u822b\u4e3b\u4eba\u5417\uff1f\u201d \u3000\u3000\u8fd9\u9ebb\u8138\u5927\u6c49\u6b63\u662f\u738b\u5408\uff0c\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u4e0d\u6562\u5f53\uff0c\u5144\u5f1f\u53ea\u4e0d\u8fc7\u662f\u4e00\u4e2a\u957f\u968f\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u6309\u4e0b\u5fc3\u5934\u7684\u6012\u706b\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u65e2\u4f5c\u4e0d\u5f97\u4e3b\uff0c\u90a3\u5c31\u6709\u52b3\u901a\u62a5\u4e00\u58f0\uff0c\u5c31\u8bf4\u7a0b\u67d0\u6c42\u89c1\u4e86\u3002\u201d \u3000\u3000\u738b\u5408\u4e00\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u655d\u4e1c\u4e3b\u8eab\u4f53\u4e0d\u9002\uff0c\u4e0d\u4fbf\u89c1\u5ba2\uff0c\u800c\u4e14\u6211\u4eec\u5929\u4eae\u4e4b\u540e\uff0c\u5c31\u8981\u79bb\u6b64\uff0c\u6709\u52b3\u7a0b\u5e84\u4e3b\u5927\u9a7e\uff0c\u5144\u5f1f\u8fd9\u91cc\u4ee3\u4e3b\u4eba\u8c22\u9886\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u6012\u9053\uff1a\u201c\u5728\u5609\u5b9a\u5730\u9762\u4e0a\u6ca1\u88ab\u4eba\u6253\u8fc7\u56de\u7968\uff0c\u656c\u8bf7\u5949\u544a\u4ee4\u4e1c\u4e3b\uff0c\u5728\u4e0b\u65e2\u7136\u6765\u4e86\u51b3\u4e0d\u4f1a\u5c31\u6b64\u56de\u5934\u3002\u201d \u3000\u3000\u738b\u5408\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u5728\u4e0b\u4ee3\u7a0b\u5e84\u4e3b\u901a\u62a5\u4e00\u58f0\u5c31\u662f\u3002\u201d \u3000\u3000\u8a00\u518d\uff0c\u8f6c\u8eab\u5165\u8231\u3002 \u3000\u3000\u7247\u5219\u4e4b\u540e\uff0c\u738b\u5408\u53c8\u8f6c\u4e86\u51fa\u6765\uff0c\u9053\uff1a\u201c\u655d\u4e1c\u6709\u8bf7\u5e84\u4e3b\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u4e00\u63d0\u6c14\uff0c\u98de\u8dc3\u800c\u8d77\uff0c\u767b\u4e0a\u753b\u822b\u3002 \u3000\u3000\u65b9\u5929\u6210\u3001\u77f3\u4fca\u7d27\u968f\u7a0b\u5b50\u671b\u4e00\u8dc3\u800c\u8fc7\u3002 \u3000\u3000\u738b\u5408\u7ad9\u5728\u8231\u95e8\u53e3\u5904\u6253\u91cf\u4e86\u65b9\u5929\u6210\u548c\u77f3\u4fca\u4e24\u773c\uff0c\u9053\uff1a\u201c\u8fd9\u4e24\u4f4d\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u7f13\u7f13\u4ed6\u8bf4\u9053\uff1a\u201c\u8fd9\u4e24\u4f4d\u90fd\u662f\u5728\u4e0b\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u4f46\u95fb\u8231\u4e2d\u4f20\u51fa\u4e00\u4e2a\u6e05\u51b7\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u738b\u5408\uff0c\u8bf7\u4ed6\u4eec\u8fdb\u6765\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u5f53\u5148\u800c\u5165\uff0c\u884c\u5165\u8231\u4e2d\u3002 \u3000\u3000\u65b9\u5929\u6210\u548c\u77f3\u4fca\u9c7c\u8d2f\u76f8\u968f\u8eab\u540e\u3002 \u3000\u3000\u753b\u822b\u4e2d\u5e03\u7f6e\u5f97\u5341\u5206\u8c6a\u534e\uff0c\u56db\u5468\u90fd\u7528\u7d2b\u7eeb\u906e\u4f4f\u4e24\u652f\u513f\u81c2\u7c97\u7ec6\u5de8\u70db\uff0c\u718a\u718a\u71c3\u70e7\uff0c\u8231\u4e2d\u4e00\u7247\u901a\u660e\u3002 \u3000\u3000\u4e00\u4e2a\u8eab\u7740\u9ec4\u8863\u7684\u5c11\u5e74\uff0c\u7aef\u5750\u5728\u4e00\u5f20\u592a\u5e08\u6905\u4e0a\uff0c\u5728\u4ed6\u65c1\u8fb9\u5750\u7740\u4e00\u4f4d\u56db\u65ec\u4ee5\u4e0a\uff0c\u8138\u8272\u60e8\u767d\u7684\u9ed1\u8863\u4eba\u3002 \u3000\u3000\u53e6\u4e00\u4e2a\u548c\u90a3\u95e8\u53e3\u5927\u6c49\u4e00\u822c\u6a21\u6837\u7684\u52b2\u88c5\u5927\u6c49\uff0c\u7ad9\u5728\u90a3\u9ec4\u8863\u5c11\u5e74\u7684\u8eab\u540e\u3002 \u3000\u3000\u53cc\u65b9\u76ee\u5149\u4ea4\u6295\u534a\u664c\u4e0d\u95fb\u58f0\u606f\u3002 \u3000\u3000\u65b9\u5929\u6210\u4e00\u63a0\u90a3\u9ed1\u8863\u4eba\uff0c\u5fae\u5fae\u4e00\u76b1\u7709\u5934\u3002 \u3000\u3000\u4e00\u79cd\u4e0d\u5e94\u6709\u7684\u8083\u9759\uff0c\u4f7f\u8239\u8231\u4e2d\u5145\u5b9e\u7740\u4e00\u79cd\u6c89\u9ed8\u4e0e\u7d27\u5f20\u3002 \u3000\u3000\u826f\u4e45\u4e4b\u540e\uff0c\u90a3\u9ec4\u8863\u5c11\u5e74\u624d\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u7a0b\u5e84\u4e3b\u6df1\u591c\u6765\u8bbf\u4e0d\u77e5\u6709\u4f55\u89c1\u6559\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u91cd\u91cd\u5730\u54b3\u4e86\u4e00\u58f0\uff0c\u7b54\u975e\u6240\u95ee\u5730\u9053\uff1a\u201c\u9601\u4e0b\u8fd9\u7b49\u5f85\u5ba2\u672a\u514d\u592a\u5c0f\u5bb6\u5b50\u6c14\uff0c\u8fde\u4e2a\u5ea7\u4e5f\u4e0d\u80af\u8d50\u5417\uff1f\u201d \u3000\u3000\u9ec4\u8863\u5c11\u5e74\u9053\uff1a\u201c\u597d\uff01\u8bf8\u4f4d\u8bf7\u968f\u4fbf\u5750\u5427\uff01\u201d \u3000\u3000\u7a0b\u5b50\u671b\u5f53\u5148\u5750\u4e0b\uff0c\u65b9\u5929\u6210\uff0c\u77f3\u4fca\u968f\u7740\u4e5f\u5750\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4e09\u4eba\u521a\u521a\u5750\u597d\uff0c\u90a3\u9ec4\u8863\u5c11\u5e74\u5df2\u51b7\u51b7\u5730\u63a5\u9053\uff1a\u201c\u73b0\u5728\u53ef\u4ee5\u8bf4\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u8138\u8272\u4e00\u53d8\u9053\uff1a\u201c\u5728\u5609\u5b9a\u5730\u9762\u4e0a\u8fd8\u65e0\u4eba\u6562\u5bf9\u8001\u592b\u5982\u6b64\u65e0\u793c\u3002\u201d \u3000\u3000\u9ec4\u8863\u5c11\u5e74\u9053\uff1a\u201c\u7a0b\u5e84\u4e3b\u6df1\u591c\u76f8\u8bbf\uff0c\u53ea\u4e3a\u4e86\u8fd9\u51e0\u53e5\u8bdd\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u8001\u592b\u6df1\u591c\u6765\u6b64\uff0c\u81ea\u6709\u539f\u56e0\u3002\u201d \u3000\u3000\u8bed\u58f0\u7a81\u7136\u51b7\u6f20\uff0c\u63a5\u9053\uff1a\u201c\u9601\u4e0b\u4eca\u65e5\u6253\u4f24\u4e00\u4e2a\u7ae5\u5b50\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u9ec4\u8863\u5c11\u5e74\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u90a3\u4f4d\u7ae5\u5b50\u662f\u4f55\u8bb8\u4eba\uff1f\u201d \u3000\u3000\u9ec4\u8863\u5c11\u5e74\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u4e0d\u77e5\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u6682\u65f6\u6487\u5f00\u4ed6\u7684\u8eab\u4efd\u4e0d\u8c08\uff0c\u9601\u4e0b\u770b\u6837\u5b50\u5df2\u8fc7\u5f31\u51a0\u4e4b\u5e74\uff0c\u4e0d\u77e5\u4f55\u4ee5\u8981\u51fa\u624b\u4f24\u4e00\u4e2a\u5341\u51e0\u5c81\u7684\u7ae5\u5b50\u3002\u201d \u3000\u3000\u9ec4\u4e48\u5c11\u5e74\u9053\uff1a\u201c\u9601\u4e0b\u548c\u90a3\u7ae5\u5b50\u662f\u4f55\u5173\u7cfb\uff1f\u90a3\u62b1\u8d70\u4ed6\u7684\u8001\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u8001\u592b\u4eca\u5bb5\u6765\u6b64\uff0c\u5e76\u975e\u662f\u7b54\u4f60\u6240\u95ee\u800c\u6765\u3002\u201d \u3000\u3000\u9ec4\u8863\u5c11\u5e74\u9053\uff1a\u201c\u90a3\u662f\u95ee\u7f6a\u800c\u6765\u4e86\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u9601\u4e0b\u8fd9\u70b9\u5e74\u7eaa\uff0c\u8bf4\u8bdd\u5982\u6b64\u65e0\u793c\uff0c\u5b9a\u7136\u662f\u5927\u6709\u6765\u5934\u7684\u4e86\uff1f\u201d\u8bed\u58f0\u4e00\u987f\uff0c\u9053\uff1a\u201c\u8001\u592b\u5e0c\u671b\u4f60\u80fd\u8bf4\u51fa\u5e08\u95e8\u6765\u5386\uff0c\u514d\u5f97\u95f9\u51fa\u60e8\u5c40\u3002\u201d \u3000\u3000\u9ec4\u8863\u5c11\u5e74\u7a81\u7136\u7eb5\u58f0\u5927\u7b11\u8d77\u6765\uff0c\u54cd\u4eae\u7684\u7b11\u58f0\uff0c\u6253\u65ad\u4e86\u7a0b\u5b50\u671b\u672a\u5b8c\u4e4b\u8a00\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u8001\u8c0b\u6df1\u7b97\uff0c\u770b\u90a3\u9ec4\u8863\u5c11\u5e74\u5982\u6b64\u7684\u72c2\u50b2\uff0c\u4f3c\u662f\u5168\u4e0d\u628a\u81ea\u5df1\u653e\u5728\u5fc3\u4e0a\uff0c\u53cd\u800c\u591a\u7591\u8d77\u6765\uff0c\u6697\u9053\uff1a\u8fd9\u5e74\u8f7b\u4eba\u5982\u6b64\u72c2\u50b2\uff0c\u4f3c\u662f\u6709\u6043\u65e0\u6050\uff0c\u6b64\u4e8b\u5012\u662f\u4e0d\u53ef\u83bd\u649e\u4e86\u3002 \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u5fc3\u4e2d\u7684\u706b\u6c14\uff0c\u53cd\u800c\u5e73\u606f\u4e0b\u53bb\uff0c\u53ea\u5f85\u9ec4\u8863\u5c11\u5e74\u7b11\u58f0\u505c\u4e0b\u6765\u4e4b\u540e\uff0c\u624d\u7f13\u7f13\u5730\u95ee\u9053\uff1a\u201c\u9601\u4e0b\u7b11\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9ec4\u8863\u5c11\u5e74\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u7a0b\u5b50\u671b\uff0c\u5728\u4e0b\u4e0d\u8fc7\u662f\u8def\u8fc7\u6b64\u5730\uff0c\u5e76\u975e\u5728\u5609\u5b9a\u5730\u9762\u4e0a\u95f9\u4e8b\u4e4b\u4eba\u3002\u201d \u3000\u3000\u8bdd\u58f0\u4e00\u987f\u63a5\u9053\uff1a\u201c\u9601\u4e0b\u5982\u82e5\u81ea\u8ba4\u662f\u5609\u5b9a\u5730\u9762\u4e0a\u7684\u9f99\u5934\uff0c\u5e94\u8be5\u5c45\u4e2d\u8c03\u505c\uff0c\u4ee5\u516c\u6b63\u6001\u5ea6\u51fa\u4e4b\uff0c\u4f46\u9601\u4e0b\u5bc5\u591c\u767b\u821f\uff0c\u6765\u52bf\u6c79\u6c79\uff0c\u5012\u4f3c\u5174\u5e08\u95ee\u7f6a\u800c\u6765\uff0c\u8fd9\u54ea\u91cc\u8fd8\u50cf\u662f\u5c45\u4e2d\u8c03\u89e3\u7eb7\u4e89\u7684\u6001\u5ea6\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u8f7b\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u4e4b\u8a00\uff0c\u9aa4\u542c\u8d77\u6765\uff0c\u4f3c\u751a\u6709\u7406\uff0c\u4f46\u9601\u4e0b\u5982\u82e5\u80fd\u4ed4\u7ec6\u60f3\u60f3\uff0c\u53ea\u6015\u5c31\u6709\u4e9b\u4e0d\u901a\u4e86\uff01\u201d \u3000\u3000\u9ec4\u8863\u5c11\u5e74\u9053\uff1a\u201c\u5982\u82e5\u5728\u4e0b\u5728\u5609\u5b9a\u5730\u9762\u88ab\u4eba\u6253\u4f24\uff0c\u7a0b\u5144\u53c8\u5c06\u5982\u4f55\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u4e8b\u5b9e\u4e0a\u9601\u4e0b\u6253\u4f24\u4e86\u4eba\uff0c\u800c\u4e14\u662f\u4e00\u4e2a\u7ae5\u5b50\u3002\u201d \u3000\u3000\u9ec4\u8863\u5c11\u5e74\u9053\uff1a\u201c\u7a0b\u5e84\u4e3b\u786c\u884c\u66ff\u4eba\u51fa\u5934\uff0c\u60f3\u6765\u548c\u90a3\u7ae5\u5b50\u975e\u4eb2\u5fc5\u6545\u4e86\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u7a81\u7136\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u7a0b\u5144\uff0c\u6700\u91cd\u8981\u7684\u662f\u54b1\u4eec\u5148\u8981\u95ee\u660e\u4e8b\u60c5\u8d77\u7aef\u7ecf\u8fc7\u3002\u201d\u7a0b\u5b50\u671b\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u8f6c\u79fb\u5230\u90a3\u9ec4\u8863\u5c11\u5e74\u7684\u8138\u4e0a\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u65e2\u7136\u627f\u8ba4\u4f24\u4e86\u4eba\uff0c\u90a3\u4e5f\u662f\u5927\u4e08\u592b\u7537\u5b50\u6c49\u7684\u884c\u5f84\uff0c\u4f46\u4e0d\u77e5\u53ef\u5426\u628a\u7ecf\u8fc7\u4e4b\u60c5\uff0c\u8bf4\u7ed9\u7a0b\u67d0\u542c\u542c\uff1f\u201d \u3000\u3000\u9ec4\u8863\u5c11\u5e74\u76ee\u5149\u4e00\u63a0\u65b9\u5929\u6210\u548c\u77f3\u4fca\u9053\uff1a\u201c\u90a3\u4e24\u4f4d\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u7a0b\u67d0\u4eba\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u9ec4\u8863\u5c11\u5e74\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u7a0b\u5e84\u4e3b\u8bf4\u5728\u4e0b\u6253\u4f24\u7684\uff0c\u5728\u4e0b\u4e5f\u8ba4\u4e86\uff0c\u7a0b\u5e84\u4e3b\u5982\u8981\u66ff\u4eba\u51fa\u5934\uff0c\u5c3d\u7ba1\u5212\u51fa\u9053\u513f\uff0c\u5982\u662f\u522b\u65e0\u4ed6\u4e8b\uff0c\u5728\u4e0b\u8fd8\u8981\u65e9\u70b9\u4f11\u606f\uff0c\u7a0b\u5e84\u4e3b\u548c\u8d35\u53cb\u4e5f\u597d\u8bf7\u4fbf\u4e86\u3002\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\u8bf4\u7684\u8c6a\u4e0d\u5ba2\u6c14\uff0c\u522b\u8bf4\u7a0b\u5b50\u671b\u4e86\uff0c\u5c31\u662f\u77f3\u4fca\u548c\u65b9\u5929\u6210\uff0c\u4e5f\u542c\u5f97\u6012\u706b\u4e0a\u51b2\u3002 \u3000\u3000\u8001\u8c0b\u6df1\u7b97\uff0c\u5411\u4e0d\u8f7b\u6613\u53d1\u706b\u7684\u7a0b\u5b50\u671b\uff0c\u4e5f\u96be\u4ee5\u53d7\u8fd9\u51e0\u53e5\u8fd1\u4e4e\u6c61\u8fb1\u7684\u9010\u5ba2\u4ee4\uff0c\u970d\u7136\u7ad9\u8d77\u8eab\u5b50\u6012\u9053\uff1a\u201c\u9601\u4e0b\u60f3\u5728\u5609\u5b9a\u5730\u9762\u64a4\u91ce\uff0c\u6211\u7a0b\u67d0\u4eba\u5c31\u7b2c\u4e00\u4e2a\u5bb9\u4f60\u4e0d\u5f97\uff01\u201d \u3000\u3000\u90a3\u9ec4\u8863\u5c11\u5e74\u4e00\u8df3\u800c\u8d77\uff0c\u51c6\u5907\u5411\u7a0b\u5b50\u671b\u51b2\u8fc7\u53bb\uff0c\u5374\u88ab\u90a3\u5750\u5728\u8eab\u4fa7\u7684\u9ed1\u8863\u4eba\u4f38\u624b\u62e6\u4f4f\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u516c\u5b50\u8bf7\u5750\u3002\u201d\u7f13\u7f13\u7ad9\u8d77\u8eab\u5b50\uff0c\u884c\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u6b64\u4eba\u9762\u8272\u60e8\u767d\uff0c\u5168\u65e0\u534a\u70b9\u8840\u8272\uff0c\u504f\u504f\u53c8\u7a7f\u4e0a\u4e86\u4e00\u4ef6\u9ed1\u8272\u7684\u8863\u670d\uff0c\u9ed1\u767d\u5206\u660e\uff0c\u770b\u4e0a\u53bb\u9634\u6c14\u68ee\u68ee\u3002 \u3000\u3000\u4ed6\u6b65\u5c65\u5f88\u6162\uff0c\u4e00\u6b65\u4e0d\u5c3d\u534a\u5c3a\uff0c\u4f46\u7a0b\u5b50\u671b\u5374\u5df2\u63d0\u805a\u771f\u6c14\u6697\u4f5c\u6212\u5907\u3002 \u3000\u3000\u90a3\u9762\u8272\u60e8\u81ea\u7684\u5927\u6c49\u903c\u5230\u4e86\u7a0b\u5b50\u671b\u8eab\u524d\u4e24\u5c3a\u5de6\u53f3\u65f6\uff0c\u505c\u4e86\u4e0b\u6765\uff0c\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u9601\u4e0b\u5bb9\u4e0d\u5f97\u6211\u5bb6\u516c\u5b50\uff0c\u4e0d\u77e5\u8981\u5982\u4f55\u5bf9\u4ed8\u4ed6\u4e86\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u4e45\u5e74\u5728\u6c5f\u6e56\u4e0a\u95ef\u8361\uff0c\u9605\u5389\u4e4b\u4e30\uff0c\u90a3\u662f\u5f88\u5c11\u4eba\u80fd\u591f\u53ca\u5f97\uff0c\u4e00\u770b\u9ed1\u8863\u4eba\uff0c\u5df2\u77e5\u4ed6\u7ec3\u7684\u6b66\u529f\uff0c\u5fc5\u7136\u662f\u5c5e\u4e8e\u9634\u67d4\u3001\u6b79\u6bd2\u7684\u6b66\u529f\uff0c\u53ea\u8981\u88ab\u4ed6\u51fb\u4e2d\u4e00\u638c\uff0c\u4e0d\u6b7b\u4ea6\u5c06\u91cd\u4f24\uff0c\u662f\u4ee5\u5fc3\u4e2d\u5341\u5206\u8b66\u89c9\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u9601\u4e0b\u600e\u4e48\u79f0\u547c\uff1f\u201d \u3000\u3000\u90a3\u9ed1\u8863\u4eba\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u7a0b\u5e84\u4e3b\u4e5f\u4e0d\u4f1a\u548c\u5728\u4e0b\u4ea4\u670b\u53cb\uff0c\u8fd9\u901a\u540d\u62a5\u59d3\u7684\u4e8b\uff0c\u6211\u770b\u514d\u4e86\u2026\u2026\u201d\u8bed\u58f0\u4e00\u987f\uff0c\u7a81\u7136\u8f6c\u58f0\u63a5\u9053\uff1a\u201c\u9601\u4e0b\u7a76\u7adf\u66ff\u4f55\u4eba\u51fa\u5934\uff0c\u5e0c\u671b\u5766\u7136\u8bf4\u51fa\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u5728\u5609\u5b9a\u7684\u5730\u9762\u4e0a\uff0c\u7a0b\u67d0\u4eba\u7684\u8eab\u4efd\uff0c\u96be\u9053\u8fd8\u4e0d\u591f\u5417\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u6709\u4e00\u53e5\u8bdd\u5728\u4e0b\u8bb0\u5f97\u7a0b\u5e84\u4e3b\u8bf4\u8fc7\uff0c\u5982\u662f\u5f7c\u6b64\u9020\u6210\u8bef\u4f1a\uff0c\u96be\u514d\u8981\u95f9\u51fa\u60e8\u5c40\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u5fae\u5fae\u4e00\u987f\u63a5\u9053\uff1a\u201c\u5728\u4e0b\u5e0c\u671b\u80fd\u5982\u5b9e\u8bf4\u51fa\u90a3\u7ae5\u5b50\u7684\u8eab\u4efd\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u8fd9\u4eba\u770b\u4e0a\u53bb\u867d\u7136\u9634\u6c89\u53ef\u6016\uff0c\u4f46\u5374\u4ecd\u662f\u6beb\u65e0\u6c5f\u6e56\u7ecf\u9a8c\uff0c\u5927\u540d\u9f0e\u9f0e\u7684\u4e2d\u5dde\u5927\u4fa0\uff0c\u4e24\u4e2a\u4eba\u5728\u4ed6\u9762\u524d\u7ad9\u7740\uff0c\u4ed6\u7adf\u7136\u8ba4\u4e0d\u51fa\u6765\u3002\u201d \u3000\u3000\u5ff5\u8f6c\u53ca\u6b64\uff0c\u4e0d\u7981\u5931\u58f0\u4e00\u7b11\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u7a0b\u5e84\u4e3b\u7b11\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u7f13\u7f13\u4ed6\u8bf4\u9053\uff1a\u201c\u670b\u53cb\u5f88\u5c11\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\u5417\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u533a\u533a\u7d20\u4e0d\u613f\u8f7b\u6613\u548c\u4eba\u6765\u5f80\u2026\u2026\u201d\u7a81\u7136\u626c\u624b\u4e00\u628a\uff0c\u75be\u5411\u7a0b\u5b50\u671b\u624b\u8155\u4e4b\u4e0a\u6263\u53bb\u3002 \u3000\u3000\u51fa\u624b\u8fc5\u5feb\uff0c\u75be\u5982\u95ea\u7535\uff0c\u7a0b\u5b50\u671b\u5de6\u8155\u4e00\u7f29\u53f3\u624b\u6b63\u5f85\u53cd\u51fb\uff0c\u7a81\u7136\u638c\u5f71\u4e00\u95ea\uff0c\u77f3\u4fca\u5df2\u62a2\u5148\u51fa\u624b\uff0c\u4e00\u638c\u62cd\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u6263\u7a0b\u5b50\u671b\u8155\u8109\u7684\u53f3\u624b\uff0c\u539f\u662f\u534a\u4f38\uff0c\u7a81\u7136\u95f4\u4e94\u6307\u4f38\u76f4\uff0c\u6613\u6293\u4e3a\u638c\uff0c\u786c\u63a5\u4e86\u77f3\u4fca\u4e00\u638c\u3002 \u3000\u3000\u4f46\u95fb\u84ec\u7136\u4e00\u58f0\uff0c\u53cc\u638c\u63a5\u5b9e\u3002 \u3000\u3000\u77f3\u4fca\u548c\u90a3\u9ed1\u8863\u4eba\u5404\u81ea\u5411\u540e\u9000\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u77f3\u4fca\u548c\u90a3\u9ed1\u8863\u4eba\u4f3c\u662f\u90fd\u672a\u6599\u5230\uff0c\u5bf9\u65b9\u6b66\u529f\u5982\u6b64\u7684\u9ad8\u5f3a\u3002\u540c\u65f6\u4e3a\u4e4b\u4e00\u6014\uff0c\u4ed4\u7ec6\u5730\u6253\u91cf\u4e86\u5bf9\u65b9\u4e00\u773c\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u8f7b\u8f7b\u54b3\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u5728\u4e0b\u4e0d\u76f8\u4fe1\u9601\u4e0b\u662f\u7a0b\u5e9c\u4e2d\u7684\u6b66\u5e08\u3002\u201d \u3000\u3000\u77f3\u4fca\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u4e0d\u76f8\u4fe1\u4e5f\u6cd5\u76f8\u4fe1\u4e86\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u9601\u4e0b\u5f88\u72c2\u3002\u201d\u7a81\u7136\u624b\u811a\u9f50\u51fa\uff0c\u8fde\u653b\u56db\u62db\u3002 \u3000\u3000\u77f3\u4fca\u6325\u638c\u5c01\u6321\uff0c\u628a\u56db\u62db\u4e00\u9f50\u5c01\u67b6\u5f00\u53bb\u4eba\u5374\u4ecd\u7ad9\u5728\u539f\u5730\u65e0\u52a8\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u9ed1\u8863\u4eba\u77e5\u5df2\u9047\u4e0a\u4e86\u52b2\u654c\uff0c\u9a84\u4efb\u4e4b\u6c14\u987f\u6d88\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u770b\u7740\u4e24\u4eba\u52a8\u624b\u60c5\u52bf\uff0c\u5fc3\u4e2d\u6697\u53eb\u60ed\u6127\u5fd6\u9053\uff1a\u201c\u5982\u662f\u4e0d\u5e26\u4ed6\u4eec\u540c\u6765\uff0c\u4eca\u591c\u975e\u51fa\u4e11\u4e0d\u53ef\uff0c\u5355\u662f\u90a3\u9ed1\u8863\u4eba\u9002\u624d\u62f3\u811a\u9f50\u51fa\u7684\u5feb\u901f\u56db\u62db\uff0c\u53ea\u6015\u6211\u5c31\u63a5\u4e0d\u4e0b\u6765\u3002\u201d \u3000\u3000\u77f3\u4fca\u76ee\u5149\u4e00\u63a0\u90a3\u9ec4\u8863\u5c11\u5e74\uff0c\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4f60\u5982\u6b64\u72c2\u50b2\uff0c\u539f\u6765\u662f\u6709\u6240\u6043\u3002\u201d \u3000\u3000\u9ec4\u8863\u5c11\u5e74\u505a\u68a6\u4e5f\u6ca1\u6709\u60f3\u5230\u7a0b\u5b50\u671b\u5e26\u6765\u4e4b\u4eba\uff0c\u7adf\u6709\u7740\u5982\u6b64\u9ad8\u5f3a\u7684\u6b66\u529f\uff0c\u4e00\u65f6\u95f4\u5446\u5728\u5f53\u5730\uff0c\u4e0d\u77e5\u6240\u63aa\u3002 \u3000\u3000\u65b9\u5929\u6210\u4e24\u9053\u5a01\u731b\u56db\u5c04\u7684\u76ee\u5149\u4e00\u63a0\u90a3\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u9601\u4e0b\u548c\u5251\u95e8\u4e94\u9b3c\uff0c\u600e\u4e48\u79f0\u547c\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u5148\u662f\u4e00\u6014\uff0c\u7ee7\u800c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5144\u5f1f\u5c31\u662f\u4e94\u9b3c\u8463\u65b9\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4f60\u4eec\u90a3\u5927\u5e08\u5144\u767d\u8863\u540a\u5ba2\u6210\u7384\u901a\uff0c\u6ca1\u6709\u6765\u6b64\u5417\uff1f\u201d \u3000\u3000\u8463\u65b9\u9053\uff1a\u201c\u5251\u95e8\u4e94\u9b3c\u4e2d\u53ea\u6709\u533a\u533a\u4e00\u4eba\u5728\u6b64\u3002\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u56db\u4f4d\u5e08\u5144\uff0c\u4e5f\u8bb8\u5f88\u5feb\u8d76\u6765\u6b64\u5730\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u5403\u4e86\u4e00\u60ca\uff0c\u6697\u9053\uff1a\u201c\u4e94\u9b3c\u9f50\u5230\u5fc5\u6709\u5927\u4e8b\u53d1\u751f\uff0c\u4ed6\u4eec\u6765\u5609\u5b9a\u4e0d\u77e5\u8981\u95f9\u51fa\u4ec0\u4e48\u4e86\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u671b\u4e86\u90a3\u9ec4\u8863\u5c11\u5e74\u4e00\u773c\uff0c\u9053\uff1a\u201c\u8fd9\u4f4d\u5e74\u8f7b\u4eba\uff0c\u548c\u4f60\u4eec\u5251\u95e8\u4e94\u9b3c\u6709\u4f55\u6e0a\u6e90\uff1f\u201d \u3000\u3000\u8463\u65b9\u5bf9\u65b9\u5929\u6210\u4e00\u5f00\u53e3\u5c31\u8bf4\u5251\u95e8\u4e94\u9b3c\uff0c\u53c8\u63d0\u5927\u5e08\u5144\u7684\u59d3\u540d\uff0c\u5fc3\u4e2d\u867d\u7136\u6709\u4e9b\u5f97\u4ff1\uff0c\u4f46\u65e2\u7136\u53eb\u4e86\u540d\u53f7\uff0c\u5374\u53c8\u4e0d\u5f97\u4e0d\u81ea\u6043\u8eab\u4efd\uff0c\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u9601\u4e0b\u7684\u59d3\u540d\uff0c\u8fd8\u672a\u89c1\u544a\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u5728\u4e0b\u59d3\u65b9\u2026\u2026\u201d\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u8fd9\u4f4d\u5e74\u8f7b\u4eba\u6253\u4f24\u7684\u5b69\u7ae5\uff0c\u6b63\u662f\u65b9\u67d0\u7684\u72ac\u5b50\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u5df2\u4e3a\u5251\u95e8\u4e94\u9b3c\u6076\u540d\u6240\u6151\uff0c\u53cd\u800c\u4e0d\u6562\u63d2\u53e3\u4ea4\u8a00\u3002 \u3000\u3000\u8463\u65b9\u671b\u4e86\u9ec4\u8863\u5c11\u5e74\u4e00\u773c\uff0c\u9053\uff1a\u201c\u8fd9\u4f4d\u516c\u5b50\u4e48\u2014\u2014\u59d3\u5b97\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u5b97\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8463\u65b9\u9053\uff1a\u201c\u9601\u4e0b\u7684\u5927\u540d\u8fd8\u6ca1\u8bf4\u51fa\u3002\u201d \u3000\u3000\u539f\u6765\u4ed6\u60f3\u4e86\u5f88\u4e45\uff0c\u4ecd\u662f\u672a\u60f3\u51fa\u65b9\u5929\u6210\u7684\u8eab\u4efd\u3002 \u3000\u3000\u65b9\u5929\u6210\u6de1\u6de1\u7684\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u65b9\u5929\u6210\u3002\u201d \u3000\u3000\u8463\u65b9\u554a\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e2d\u5dde\u4e09\u9655\u7684\u65b9\u4e8c\u4fa0\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4e0d\u6562\u5f53\u3002\u201d \u3000\u3000\u8463\u65b9\u9053\uff1a\u201c\u5144\u5f1f\u5931\u656c\u4e86\u3002\u201d\u62b1\u62f3\u4e00\u63d6\u3002 \u3000\u3000\u65b9\u5929\u6210\u6b20\u8eab\u8fd8\u4e86\u4e00\u793c\u9053\uff1a\u201c\u72ac\u5b50\u88ab\u6253\u4e00\u4e8b\uff0c\u5144\u5f1f\u5e76\u672a\u95ee\u7f6a\u4e4b\u5fc3\uff0c\u6211\u53ea\u662f\u60f3\u67e5\u51fa\u662f\u975e\u66f2\u76f4\uff0c\u5982\u662f\u9517\u5728\u5c0f\u72ac\u6211\u4e5f\u597d\u6559\u8bad\u4ed6\u4e00\u987f\u3002\u201d \u3000\u3000\u9ec4\u8863\u5c11\u5e74\u9053\uff1a\u201c\u5728\u4e0b\u548c\u4ee4\u90ce\u7684\u51b2\u7a81\uff0c\u8bf4\u8d77\u6765\u662f\u8c01\u90fd\u6709\u9519\u3002\u201d \u3000\u3000\u77f3\u4fca\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u5c31\u51ed\u9601\u4e0b\u5bf9\u6211\u4eec\u8fd9\u80a1\u72c2\u50b2\u4e4b\u6c14\uff0c\u90a3\u5c31\u4e0d\u96be\u60f3\u4f60\u5230\u6b3a\u4fae\u4e00\u4e2a\u7ae5\u5b50\u7684\u60c5\u51b5\u3002\u201d \u3000\u3000\u90a3\u9ec4\u8863\u5c11\u5e74\u4f3c\u662f\u4e3a\u4e2d\u5dde\u4e8c\u4fa0\u7684\u5927\u540d\u9707\u6151\uff0c\u6001\u5ea6\u5927\u53d8\uff0c\u867d\u53d7\u90a3\u77f3\u4fca\u8ba5\u8bbd\uff0c\u4ecd\u7136\u5fcd\u4e0b\u672a\u8a00\uff0c\u6253\u91cf\u4e86\u77f3\u4fca\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u548c\u65b9\u5927\u4fa0\u4ee4\u90ce\u51b2\u7a81\u7ecf\u8fc7\uff0c\u5728\u573a\u4e4b\u4eba\uff0c\u4e0d\u4e0b\u6570\u767e\uff0c\u9601\u4e0b\u4e0d\u4fe1\uff0c\u4e0d\u59a8\u53bb\u95ee\u95ee\u90a3\u4e9b\u76ee\u7779\u5b9e\u60c5\u4e4b\u4eba\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u597d\uff01\u9601\u4e0b\u8bf7\u4ed4\u7ec6\u7684\u8bf4\u660e\u7ecf\u8fc7\uff0c\u72ac\u5b50\u5982\u6709\u4e0d\u5bf9\u4e4b\u5904\uff0c\u5728\u4e0b\u51b3\u7136\u4e0d\u541d\u8d23\u7f5a\u3002\u201d \u3000\u3000\u9ec4\u8863\u5c11\u5e74\u5f53\u4e0b\u628a\u7ecf\u8fc7\u4e4b\u60c5\uff0c\u8bf4\u4e86\u4e00\u904d\u3002 \u3000\u3000\u81ea\u7136\uff0c\u8a00\u8bed\u7a0d\u6709\u59d4\u8fc7\u65b9\u73de\u4e4b\u610f\uff0c\u800c\u4e14\u52a0\u91cd\u4e86\u65b9\u73de\u51fa\u624b\u6253\u4f24\u81ea\u5df1\u968f\u884c\u7ba1\u5bb6\u4e00\u4e8b\u3002 \u3000\u3000\u65b9\u5929\u6210\u8fd8\u672a\u6765\u5f97\u53ca\u7b54\u8bdd\uff0c\u77f3\u4fca\u5df2\u51b7\u7b11\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u636e\u5728\u4e0b\u542c\u95fb\u7684\u7ecf\u8fc7\uff0c\u548c\u9601\u4e0b\u6240\u8a00\uff0c\u6709\u751a\u591a\u4e0d\u540c\u4e4b\u5904\u3002\u201d \u3000\u3000\u9ec4\u8863\u5c11\u5e74\u9053\uff1a\u201c\u5728\u4e0b\u8bf4\u7684\u53e5\u53e5\u5b9e\u8a00\u3002\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u90a3\u4f4d\u7ba1\u5bb6\u73b0\u5728\u6b7b\u4e86\u5417\uff1f\u201d \u3000\u3000\u9ec4\u8863\u5c11\u5e74\u6447\u6447\u5934\u9053\uff1a\u201c\u867d\u7136\u672a\u6b7b\uff0c\u4f46\u4ed6\u4f24\u5f97\u5f88\u91cd\u3002\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u4f46\u9601\u4e0b\u5374\u6beb\u987b\u672a\u635f\u3002\u201d \u3000\u3000\u8463\u65b9\u63a5\u9053\uff1a\u201c\u5b97\u516c\u5b50\u548c\u4e24\u4f4d\u957f\u968f\uff0c\u90fd\u88ab\u5185\u5bb6\u53cd\u5f39\u4e4b\u529b\u9707\u4f24\uff0c\u4e0d\u8fc7\uff0c\u90a3\u4eba\u4e0b\u624b\u6781\u6709\u5206\u5bf8\uff0c\u4ed6\u4eec\u4f24\u5f97\u4e0d\u91cd\uff0c\u5144\u5f1f\u53ca\u65f6\u800c\u5230\uff0c\u4ee5\u8eab\u6000\u7075\u4e39\u65bd\u6559\uff0c\u4e09\u4eba\u624d\u5f97\u597d\u8f6c\u3002\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u9053\uff1a\u201c\u8bba\u65b9\u516c\u5b50\u548c\u5b97\u516c\u5b50\u51b2\u7a81\u7ecf\u8fc7\uff0c\u53cc\u65b9\u90fd\u6709\u635f\u6298\uff0c\u65b9\u5927\u4fa0\u4ee4\u90ce\u7684\u4f24\u52bf\u5982\u679c\u81f3\u4eca\u4ecd\u672a\u6076\u5316\uff0c\u4f3c\u662f\u7528\u4e0d\u7740\u518d\u8ffd\u7a76\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u77f3\u4fca\u76ee\u5149\u6295\u6ce8\u5728\u8463\u65b9\u7684\u8138\u4e0a\uff0c\u8083\u7136\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u5b97\u516c\u5b50\u80fd\u548c\u4f60\u4eec\u5251\u95e8\u4e94\u5144\u5f1f\u626f\u4e0a\u5173\u7cfb\uff0c\u8db3\u8bc1\u975e\u5e73\u5e38\u4eba\u7269\u3002\u201d \u3000\u3000\u8463\u65b9\u9053\uff1a\u201c\u8fd9\u4f4d\u5b97\u516c\u5b50\u7684\u5c0a\u7fc1\uff0c\u548c\u54b1\u4eec\u4e94\u5144\u5f1f\u76f8\u4ea4\u751a\u6df1\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u76ee\u5149\u6295\u6ce8\u5230\u5b97\u516c\u5b50\u7684\u8138\u4e0a\uff0c\u9053\uff1a\u201c\u5c0a\u7fc1\u540d\u53f7\u53ef\u5426\u89c1\u544a\uff1f\u201d \u3000\u3000\u9ec4\u8863\u5c11\u5e74\u9053\uff1a\u201c\u5bb6\u7236\u91d1\u67aa\u5b97\u58eb\u4e49\uff0c\u4e0d\u77e5\u65b9\u5927\u4fa0\u662f\u5426\u76f8\u8bc6\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u70b9\u5934\u5e94\u9053\uff1a\u201c\u95fb\u540d\u4e45\u77e3\uff0c\u7f18\u60ad\u4e00\u9762\u3002\u201d \u3000\u3000\u8463\u65b9\u8f7b\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5b97\u8d24\u4f84\u8fd9\u753b\u822b\u4e4b\u4e2d\uff0c\u4e0d\u662f\u5e26\u6709\u56db\u74f6\u9648\u5e74\u674f\u82b1\u917f\u5417\uff1f\u201d \u3000\u3000\u9ec4\u8863\u5c11\u5e74\u9053\uff1a\u201c\u665a\u8f88\u6709\u5fc3\u5949\u9152\uff0c\u4f46\u4e0d\u77e5\u65b9\u5927\u4fa0\u662f\u5426\u80af\u8d4f\u5149\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u540d\u9152\u8bda\u53ef\u8d35\uff0c\u76db\u60c5\u8c0a\u66f4\u9ad8\uff0c\u53ef\u60dc\u65b9\u67d0\u4e0d\u662f\u5584\u996e\u4e4b\u4eba\uff0c\u4e24\u4f4d\u7684\u76db\u60c5\uff0c\u5728\u4e0b\u8fd9\u91cc\u5fc3\u9886\u4e86\u3002\u201d \u3000\u3000\u9ec4\u8863\u5c11\u5e74\u8bf4\u9053\uff1a\u201c\u5b97\u67d0\u5931\u624b\uff0c\u4f24\u4e86\u4ee4\u90ce\uff0c\u539f\u975e\u6709\u5fc3\u94f8\u9519\uff0c\u5e38\u8a00\u9053\u4e0d\u77e5\u8005\u4e0d\u7f6a\uff0c\u60f3\u6765\u65b9\u5927\u4fa0\u5fc5\u9ad8\u62ac\u8d35\u624b\u653e\u8fc7\u665a\u8f88\u4e86\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u5982\u82e5\u5b97\u516c\u5b50\u8bf4\u7684\u4e0d\u9519\uff0c\u5c0f\u72ac\u4e5f\u6709\u9519\u5904\uff0c\u5982\u4f55\u80fd\u591f\u8d23\u602a\u65b9\u516c\u5b50\u4e00\u4eba\u5462\uff1f\u201d \u3000\u3000\u8bed\u58f0\u5fae\u5fae\u4e00\u987f\uff0c\u76ee\u5149\u8f6c\u5230\u8463\u65b9\u8138\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u8463\u5144\uff0c\u65b9\u67d0\u6709\u4e00\u4e8b\u8bf7\u6559\uff0c\u4e0d\u77e5\u8463\u5144\u662f\u5426\u4f1a\u636e\u5b9e\u76f8\u544a\u3002\u201d \u3000\u3000\u8463\u65b9\u9053\uff1a\u201c\u65b9\u5927\u4fa0\u5c3d\u7ba1\u8bf7\u95ee\uff0c\u5728\u4e0b\u662f\u77e5\u65e0\u4e0d\u8a00\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u9002\u624d\u8463\u5144\u66fe\u8a00\uff0c\u4f60\u4eec\u4e94\u5144\u5f1f\uff0c\u8981\u5728\u5609\u5b9a\u96c6\u4f1a\u5417\uff1f\u201d \u3000\u3000\u8463\u65b9\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u9664\u4e86\u6211\u4eec\u4e94\u5144\u5f1f\u4e4b\u5916\uff0c\u8fd8\u6709\u8fd9\u4f4d\u5b97\u4e16\u5144\u7684\u5c0a\u7fc1\uff0c\u91d1\u67aa\u5b97\u58eb\u4e49\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u8bf8\u4f4d\u5343\u91cc\u8fe2\u8fe2\u5230\u6b64\u4f1a\u805a\uff0c\u60f3\u6765\u5b9a\u662f\u6709\u6240\u4e3a\u4e86\uff1f\u201d \u3000\u3000\u8463\u65b9\u9053\uff1a\u201c\u4e00\u70b9\u5c0f\u4e8b\uff0c\u4e0d\u52b3\u65b9\u5927\u4fa0\u6302\u9f7f\u3002\u201d \u3000\u3000\u65b9\u6210\u9053\uff1a\u201c\u4f60\u4eec\u5144\u5f1f\u6b66\u529f\u9ad8\u5f3a\uff0c\u90a3\u91d1\u67aa\u5b97\u58eb\u4e49\u5b97\u5821\u4e3b\uff0c\u4e5f\u662f\u5927\u5927\u6709\u540d\u7684\u4eba\u7269\uff0c\u81ea\u7136\u662f\u7528\u4e0d\u7740\u65b9\u67d0\u62c5\u5fc3\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u8463\u65b9\u9053\uff1a\u201c\u4e0d\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u8fd9\u4f4d\u7a0b\u5144\uff0c\u4e43\u662f\u5728\u4e0b\u7684\u597d\u53cb\uff0c\u5609\u5b9a\u5730\u9762\u4e0a\u4e00\u5411\u5e73\u9759\u65e0\u4e8b\uff0c\u5728\u4e0b\u4e0d\u5e0c\u671b\u5728\u5609\u8d70\u95f9\u51fa\u9ebb\u70e6\u3002\u201d \u3000\u3000\u8463\u65b9\u6c89\u541f\u4e86\u4e00\u9635\u9053\uff1a\u201c\u4e3a\u4f55\u5728\u6b64\u805a\u4f1a\uff0c\u53ea\u6709\u5e08\u5144\u77e5\u6653\u5185\u60c5\uff0c\u5728\u4e0b\u5b9e\u4e0d\u77e5\u4e3a\u4e86\u4f55\u4e8b\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u8463\u5144\u5012\u662f\u63a8\u5f97\u5f88\u5e72\u51c0\u554a\uff01\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5982\u82e5\u65b9\u67d0\u4eca\u591c\u5b9a\u8981\u8ffd\u7a76\u6253\u4f24\u72ac\u5b50\u4e4b\u4e8b\uff0c\u8463\u5144\u53c8\u5c06\u5982\u4f55\uff1f\u201d \u3000\u3000\u8463\u65b9\u53cc\u76ee\u4e2d\u51f6\u5149\u4e00\u95ea\uff0c\u9053\uff1a\u201c\u5b97\u4e16\u5144\u6253\u4f24\u4ee4\u90ce\u4e00\u4e8b\uff0c\u65b9\u5927\u4fa0\u5df2\u7136\u77e5\u6653\u7ecf\u8fc7\uff0c\u5728\u4e0b\u5c40\u5916\u4eba\uff0c\u8bf4\u51fa\u4e00\u53e5\u516c\u5e73\u8bdd\uff0c\u9519\u4e5f\u4e0d\u5728\u5b97\u516c\u5b50\u4e00\u4e2a\u4eba\uff0c\u4ee4\u90ce\u9664\u4e86\u5e74\u7eaa\u5c0f\u5916\uff0c\u4f3c\u662f\u5e76\u65e0\u5927\u591a\u7684\u7406\u7531\uff0c\u4f55\u51b5\uff0c\u8bba\u51fa\u624b\uff0c\u662f\u4ee4\u90ce\u5148\u884c\u4f24\u4eba\uff0c\u4e8b\u540e\uff0c\u8d35\u5e9c\u4e2d\u6709\u4e00\u4f4d\u8001\u6b66\u5e08\uff0c\u6551\u8d70\u4ee4\u90ce\u65f6\uff0c\u53c8\u4f24\u4e86\u5b97\u516c\u5b50\u548c\u4e24\u4f4d\u957f\u968f\u548c\u5b97\u516c\u5b50\u672c\u4eba\uff0c\u5982\u975e\u5144\u5f1f\u5728\u6b64\uff0c\u66ff\u4ed6\u4eec\u4e09\u4f4d\u6cbb\u7597\u4f24\u52bf\uff0c\u6b64\u523b\uff0c\u53ea\u6015\u4ed6\u4eec\u8fd8\u4e0d\u80fd\u4e0b\u5e8a\u884c\u52a8\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u5728\u4e0b\u4e0d\u613f\u4e3a\u72ac\u5b50\u4e4b\u4e8b\u2026\u2026\u4f46\u4e5f\u5e0c\u671b\u4f60\u8463\u5144\u80fd\u7ed9\u5728\u4e0b\u4e00\u4e2a\u9762\u5b50\u3002\u201d \u3000\u3000\u8463\u65b9\u9053\uff1a\u201c\u65b9\u4e8c\u4fa0\u7684\u7528\u5fc3\u5982\u4f55\u5462\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u653e\u5f00\u5609\u5b9a\uff0c\u4f60\u4eec\u5251\u95e8\u5144\u4e94\u5f1f\u548c\u5b97\u5821\u4e3b\u6362\u4e00\u4e2a\u5730\u65b9\u805a\u4f1a\u5982\u4f55\uff1f\u201d \u3000\u3000\u8463\u65b9\u9053\uff1a\u201c\u6b64\u4e8b\u91cd\u5927\uff0c\u5728\u4e0b\u4f5c\u4e0d\u4e86\u4e3b\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u90a3\u662f\u8bf4\u8463\u5144\u4e0d\u80af\u8d4f\u8138\u4e86\uff1f\u201d \u3000\u3000\u8463\u65b9\u9053\uff1a\u201c\u5728\u4e0b\u5f88\u5c0a\u656c\u65b9\u4e8c\u4fa0\uff0c\u867d\u7136\u6211\u4eec\u4ea4\u5f80\u4e0d\u591a\uff0c\u4f46\u4e00\u4ef6\u4e8b\u5f52\u4e00\u4ef6\u4e8b\uff0c\u4e0d\u80fd\u6df7\u4e3a\u4e00\u8c08\uff0c\u4ee4\u90ce\u548c\u5b97\u4e16\u5144\u7684\u4e8b\uff0c\u662f\u975e\u66f2\u76f4\uff0c\u81ea\u6709\u516c\u8bba\uff0c\u5982\u82e5\u65b9\u4e8c\u4fa0\u96be\u6d88\u7231\u5b50\u88ab\u4f24\u4e4b\u6c14\uff0c\u4e0d\u59a8\u53bb\u627e\u91d1\u67aa\u5b97\u58eb\u4e49\u7406\u8bba\uff0c\u5982\u662f\u786c\u628a\u6b64\u4e8b\u548c\u6211\u4eec\u5251\u95e8\u4e94\u5144\u5f1f\u805a\u4f1a\u5728\u5609\u5b9a\u4e00\u4e8b\u626f\u5728\u4e00\u8d77\uff0c\u5c82\u4e0d\u592a\u8fc7\u7275\u5f3a\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u5fc3\u4e2d\u6697\u9053\u4e86\u4e24\u58f0\u60ed\u6127\uff0c\u5fd6\u9053\uff1a\u6211\u672c\u662f\u52a9\u4eba\u800c\u6765\uff0c\u60f3\u4e0d\u5230\u6b64\u523b\u53cd\u8981\u501f\u91cd\u4e2d\u5dde\u4e09\u4fa0\u6765\u52a9\u6211\u3002 \u3000\u3000\u5251\u95e8\u4e94\u9b3c\uff0c\u5728\u6c5f\u6e56\u6076\u540d\u751a\u7740\uff0c\u7a0b\u5b50\u671b\u81ea\u77e5\u60f9\u4e0d\u8d77\u4e94\u9b3c\uff0c\u662f\u4ee5\uff0c\u65e0\u6cd5\u63d2\u8a00\u3002 \u3000\u3000\u4f46\u95fb\u65b9\u5929\u6210\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8463\u5144\u4e4b\u8a00\uff0c\u542c\u8d77\u6765\u4f3c\u662f\u6709\u7406\uff0c\u5176\u5b9e\u72e1\u8fa9\u865a\u6d6e\uff0c\u4e0d\u503c\u8bc6\u8bda\u8005\u4e00\u54c2\u2026\u2026\u201d \u3000\u3000\u8463\u65b9\u8138\u8272\u4e00\u53d8\uff0c\u63a5\u9053\uff1a\u201c\u4f46\u95ee\u65b9\u4e8c\u4fa0\u7684\u9ad8\u8bba\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u5b97\u516c\u5b50\u6253\u4f24\u72ac\u5b50\uff0c\u51f6\u4eba\u5b9e\u72af\uff0c\u73b0\u5728\u8fd9\u753b\u822b\u4e4b\u4e0a\uff0c\u533a\u533a\u4e3a\u4ec0\u4e48\u8981\u820d\u8fd1\u6c42\u8fdc\uff0c\u53bb\u627e\u91d1\u67aa\u5b97\u58eb\u4e49\u5462\uff1f\u201d \u3000\u3000\u8463\u65b9\u9053\uff1a\u201c\u65b9\u5144\u4e4b\u610f\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u5982\u82e5\u8463\u5144\u4e0d\u8d4f\u5149\u7684\u8bdd\uff0c\u65b9\u67d0\u5927\u53ef\u4ee5\u7259\u8fd8\u7259\uff0c\u5148\u7559\u5b97\u516c\u5b50\uff0c\u8463\u5144\u8bf7\u4f20\u8fc5\u91d1\u67aa\u5b97\u58eb\u4e49\uff0c\u8981\u4ed6\u627e\u6211\u65b9\u67d0\u8981\u4eba\u3002\u201d \u3000\u3000\u8463\u65b9\u4ef0\u5929\u6253\u4e2a\u54c8\u54c8\uff0c\u9053\uff1a\u201c\u4e3a\u4e00\u4e2a\u540d\u4e0d\u51fa\u5609\u5b9a\u7684\u7a0b\u5b50\u671b\uff0c\u65b9\u5144\u5f53\u771f\u8981\u548c\u6211\u4eec\u5251\u95e8\u4e94\u9b3c\u4f5c\u5bf9\u5417\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u7559\u4e0b\u5b97\u516c\u5b50\u548c\u4f60\u8463\u65b9\u4f55\u5e72\uff1f\u201d \u3000\u3000\u8463\u65b9\u9053\uff1a\u201c\u5b97\u58eb\u4e49\u548c\u6211\u4eec\u4e94\u5144\u5f1f\u4ea4\u60c5\u6df1\u539a\uff0c\u4e49\u540c\u91d1\u5170\uff0c\u8463\u65b9\u65e2\u7136\u89c1\u5230\u4e86\u8fd9\u6863\u4e8b\uff0c\u5c82\u80fd\u4e0d\u7ba1\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u5de7\u5f97\u5f88\uff0c\u6211\u4eec\u4e2d\u5dde\u4e09\u4fa0\u4e5f\u548c\u7a0b\u5b50\u671b\u4ea4\u60c5\u5f88\u597d\uff0c\u8463\u5144\u5b9a\u8981\u5728\u5609\u5b9a\u5730\u9762\u4e0a\u6380\u8d77\u4e00\u573a\u98ce\u6ce2\uff0c\u65b9\u67d0\u4e5f\u662f\u4e0d\u80fd\u5750\u89c6\u3002\u201d \u3000\u3000\u8463\u65b9\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u662f\u5982\u6b64\uff0c\u65b9\u4e8c\u4fa0\u4e0d\u7eba\u8ba2\u4e2a\u7ea6\u671f\uff0c\u5251\u95e8\u4e94\u9b3c\u51c6\u65f6\u5019\u6559\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u540e\u5929\u4e2d\u5348\u65f6\u5206\uff0c\u4f60\u4eec\u5251\u95e8\u4e94\u9b3c\u5230\u7a0b\u5e9c\u8981\u4eba\u3002\u201d \u3000\u3000\u8463\u65b9\u8bf4\u9053\uff1a\u201c\u4f60\u4eca\u591c\u8981\u5e26\u4eba\u8d70\u5417\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u9664\u975e\u8463\u5144\u80fd\u963b\u62e6\u4f4f\u5728\u4e0b\u3002\u201d \u3000\u3000\u8463\u65b9\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5251\u95e8\u4e94\u9b3c\uff0c\u5728\u6c5f\u6e56\u4e0a\u5e94\u8be5\u662f\u751a\u6709\u4fe1\u7528\u4e4b\u4eba\uff0c\u60f3\u4f60\u65b9\u4e8c\u4fa0\u8fd8\u4fe1\u5f97\u8fc7\uff0c\u65e2\u7136\u5b9a\u7ea6\uff0c\u5c31\u8be5\u4e00\u8a00\u4e3a\u5b9a\uff0c\u5e26\u4eba\u4f5c\u8d28\uff0c\u672a\u514d\u6709\u4e9b\u5927\u5c0f\u89d1\u6211\u4eec\u5251\u95e8\u4e94\u9b3c\u4e86\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u4e5f\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4e0d\u4f1a\u5931\u4fe1\uff0c\u4f46\u95ee\u9898\u662f\u5728\u4e0b\u7b49\u65e0\u6cd5\u5728\u5609\u5b9a\u591a\u7559\uff0c\u56e0\u6b64\uff0c\u9650\u671f\u4e0d\u80fd\u8d85\u8fc7\u540e\u65e5\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u5fae\u5fae\u4e00\u987f\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4eec\u5251\u95e8\u4e94\u9b3c\uff0c\u6709\u4e00\u79cd\u9b3c\u706b\u4f20\u8baf\u4e4b\u6cd5\uff0c\u4e00\u591c\u95f4\u53ef\u53ca\u5343\u91cc\uff0c\u5728\u4e0b\u5982\u82e5\u6263\u4e0b\u5b97\u516c\u5b50\uff0c\u6211\u60f3\u5728\u4f60\u8463\u5144\u9b3c\u706b\u4f20\u8baf\u4e4b\u4e0b\uff0c\u4f60\u90a3\u4e94\u4f4d\u5e08\u5144\u4f1a\u5c3d\u5feb\u8d76\u6765\u6b64\u5730\u3002\u201d \u3000\u3000\u8463\u65b9\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u4eec\u9b3c\u706b\u4f20\u8baf\u4e4b\u6cd5\uff0c\u4e00\u591c\u95f4\u53ef\u53ca\u5343\u91cc\uff0c\u201d\u7a81\u7136\u4f4f\u53e3\uff0c\u6539\u8f6c\u8bdd\u9898\uff0c\u9053\uff1a\u201c\u5982\u82e5\u6211\u4eec\u540e\u5929\u4e2d\u5348\u65e0\u6cd5\u8d76\u5230\u5462\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u533a\u533a\u5e26\u8d70\u5b97\u516c\u5b50\uff0c\u4f60\u4eec\u5251\u95e8\u4e94\u9b3c\u548c\u91d1\u67aa\u5b97\u58eb\u4e49\uff0c\u82e5\u5728\u5609\u5b9a\u95f9\u51fa\u4e86\u4ec0\u4e48\u9ebb\u70e6\uff0c\u533a\u533a\u5c31\u5148\u884c\u5f00\u7f6a\u5b97\u516c\u5b50\u3002\u201d \u3000\u3000\u8463\u65b9\u6012\u9053\uff1a\u201c\u4f60\u8fd9\u7b49\u63b3\u62bc\u4eba\u8d28\u7684\u65b9\u6cd5\uff0c\u7b97\u5f97\u4ec0\u4e48\u4fa0\u4e49\u4e4b\u98ce\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u90a3\u8981\u770b\u4ec0\u4e48\u4e8b\uff0c\u4ec0\u4e48\u4eba\uff01\u201d\u8bed\u58f0\u4e00\u987f\u9053\uff1a\u201c\u5728\u4e0b\u5fc3\u5fd7\u5df2\u51b3\uff0c\u8463\u5144\u9664\u4e86\u52a8\u624b\u4e00\u9014\uff0c\u5df2\u7ecf\u65e0\u6cd5\u8bf4\u670d\u5728\u4e0b\u4e86\u3002\u201d \u3000\u3000\u8463\u65b9\u8138\u4e0a\u795e\u60c5\u5c61\u53d8\uff0c\u663e\u7136\uff0c\u4ed6\u5185\u5fc3\u4e4b\u4e2d\u6fc0\u52a8\u65e0\u6bd4\u3002\u4f46\u4ed6\u7ec8\u4e8e\u5fcd\u4e86\u4e0b\u53bb\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4f60\u4eec\u4e2d\u5dde\u4e09\u4fa0\uff0c\u6b64\u523b\u90fd\u5728\u5609\u5b9a\u5417\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f46\u5982\u6bd4\u8d77\u4f60\u4eec\u5251\u95e8\u4e94\u9b3c\uff0c\u54b1\u4eec\u8fd8\u662f\u5c11\u4e86\u4e24\u4eba\u3002\u201d \u3000\u3000\u8463\u65b9\u9053\uff1a\u201c\u65b9\u5929\u6210\u5e26\u8d70\u5b97\u4e16\u5144\uff0c\u4e0d\u77e5\u8981\u5982\u4f55\u5bf9\u4ed6\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u5728\u540e\u5929\u5348\u65f6\u4e4b\u524d\uff0c\u54b1\u4eec\u4f1a\u5f85\u4ed6\u6709\u5982\u4e0a\u5bbe\u3002\u201d \u3000\u3000\u8463\u65b9\u9053\uff1a\u201c\u8fc7\u4e86\u5348\u65f6\u5462\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u90a3\u5c31\u5f88\u96be\u8bf4\u4e86\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u5fae\u987f\uff0c\u76ee\u5149\u6295\u6ce8\u5230\u90a3\u9ec4\u8863\u5c11\u5e74\u7684\u8138\u4e0a\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5b97\u4e16\u5144\u81ea\u5df1\u8ddf\u7740\u8d70\u5462\uff1f\u8fd8\u662f\u8981\u65b9\u67d0\u51fa\u624b\u76f8\u8bf7\uff1f\u201d \u3000\u3000\u9ec4\u8863\u5c11\u5e74\u56de\u987e\u4e86\u8463\u65b9\u4e00\u773c\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8463\u4e94\u53d4\uff0c\u5c0f\u4f84\u2026\u2026\u201d \u3000\u3000\u8fd9\u4e24\u53e5\u8bdd\uff0c\u5b57\u5b57\u5982\u94c1\u62f3\u4e00\u822c\uff0c\u6253\u5728\u8463\u65b9\u7684\u8138\u4e0a\uff0c\u53ea\u89c1\u8463\u65b9\u6ee1\u8138\u901a\u7ea2\uff0c\u4e0d\u5f85\u9ec4\u8863\u5c11\u5e74\u8bdd\u8bf4\u5b8c\uff0c\u7acb\u65f6\u91cd\u91cd\u54b3\u4e86\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u8d24\u4f84\uff0c\u5927\u4e08\u592b\u80fd\u5c48\u80fd\u4f38\uff0c\u90a3\u65b9\u4e8c\u4fa0\u4e43\u6709\u540d\u4eba\u7269\uff0c\u81ea\u7136\u4e0d\u4f1a\u5bf9\u4f60\u52a0\u5bb3\uff0c\u8981\u4f60\u56de\u53bb\uff0c\u53ea\u4e0d\u8fc7\u8feb\u6211\u4eec\u6309\u65f6\u8d74\u7ea6\u5c31\u662f\u2026\u2026\u201d \u3000\u3000\u5bf9\u7740\u65b9\u5929\u6210\u62f1\u624b\uff0c\u9053\uff1a\u201c\u65b9\u4e8c\u4fa0\uff0c\u8463\u65b9\u8fd8\u6709\u51e0\u53e5\u8bdd\u8bb2\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u65b9\u67d0\u6d17\u8033\u606d\u542c\u3002\u201d \u3000\u3000\u8463\u65b9\u9053\uff1a\u201c\u5728\u4e0b\u56e0\u4e3a\u4e8b\u5148\u6ca1\u6709\u51c6\u5907\uff0c\u5c31\u7b97\u65cb\u5c55\u9b3c\u706b\u4f20\u8baf\u4e4b\u672f\uff0c\u4ed6\u4eec\u4e5f\u672a\u5fc5\u80fd\u4f9d\u65f6\u8d76\u5230\uff0c\u4e07\u4e00\u6211\u8d85\u8fc7\u4e86\u4e2d\u5348\u65f6\u9650\u2026\u2026\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u6211\u7b49\u53ef\u7b49\u5019\u5230\u65e5\u843d\u4e4b\u524d\uff0c\u6b21\u6668\u4e00\u65e9\uff0c\u5c31\u8981\u79bb\u5f00\u5609\u5b9a\u3002\u201d \u3000\u3000\u8463\u65b9\u9053\uff1a\u201c\u6700\u597d\u662f\u8d76\u4e2d\u5348\u4e4b\u7ea6\uff0c\u5728\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u8463\u65b9\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8d24\u4f84\uff0c\u8bc6\u65f6\u52a1\u8005\u4e3a\u4fca\u6770\uff0c\u8ddf\u4ed6\u53bb\u5427\uff01\u201d \u3000\u3000\u9ec4\u8863\u5c11\u5e74\u65e0\u53ef\u5948\u4f55\uff0c\u7f13\u7f13\u7ad9\u8d77\u8eab\u5b50\u3002 \u3000\u3000\u738b\u5408\u3001\u738b\u5b9d\u76f8\u4e92\u671b\u4e86\u4e00\u773c\uff0c\u7a81\u7136\u4e00\u9f50\u6a2a\u8eab\u62e6\u4f4f\u4e86\u65b9\u5929\u6210\u7684\u53bb\u8def\u3002 \u3000\u3000\u65b9\u5929\u6210\u51b7\u51b7\u5730\u671b\u4e86\u4e24\u4eba\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u662f\u4ec0\u4e48\u8eab\u4efd\uff1f\u201d \u3000\u3000\u738b\u5408\u9053\uff1a\u201c\u54b1\u4eec\u5144\u5f1f\u5949\u4e86\u5b97\u5e84\u4e3b\u4e4b\u547d\uff0c\u4fdd\u62a4\u5c11\u5e84\u4e3b\u7684\u957f\u968f\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u5ff5\u4f60\u62a4\u4e3b\u4e4b\u4e49\uff0c\u5728\u4e0b\u4e0d\u548c\u4f60\u4eec\u8ba1\u8f83\uff0c\u4e24\u4f4d\u8bf7\u8ba9\u5f00\u8def\u5427\uff01\u201d \u3000\u3000\u738b\u5408\u6447\u6447\u5934\u9053\uff1a\u201c\u65b9\u4e8c\u4fa0\u6b66\u529f\u9ad8\u5f3a\uff0c\u54b1\u4eec\u5144\u5f1f\u65e9\u6709\u6240\u95fb\uff0c\u54b1\u4eec\u81ea\u77e5\u4e0d\u654c\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4e24\u4f4d\u5fc3\u4e2d\u5982\u6b64\u660e\u767d\uff0c\u600e\u7684\u4e0d\u80af\u8ba9\u8def\u5462\uff1f\u201d \u3000\u3000\u738b\u5408\u9053\uff1a\u201c\u54b1\u4eec\u81ea\u77e5\u975e\u654c\uff0c\u4e5f\u4e0d\u80fd\u773c\u770b\u7740\u5c11\u5e84\u4e3b\u88ab\u4f60\u65b9\u4e8c\u4fa0\u5e26\u8d70\uff0c\u53ea\u597d\u653e\u624b\u4e00\u62fc\u4e86\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u51b7\u7b11\u4e24\u58f0\uff0c\u4e5f\u4e0d\u7b54\u8bdd\u3002 \u3000\u3000\u738b\u5408\u8f7b\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4e2a\u6cd5\u5b50\uff0c\u4e0d\u77e5\u65b9\u4e8c\u4fa0\u80af\u5426\u7b54\u5e94\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u5728\u4e0b\u7d20\u656c\u5fe0\u4e49\u4e4b\u58eb\uff0c\u56e0\u6b64\u4e0d\u5fcd\u4f24\u5bb3\u4f60\u4eec\uff0c\u8fd8\u6709\u4ec0\u4e48\u6cd5\u5b50\uff0c\u4f60\u8bf4\u5427\uff01\u201d \u3000\u3000\u738b\u5408\u9053\uff1a\u201c\u65b9\u4e8c\u4fa0\u5e26\u6211\u7b49\u4e00\u8d77\u8d70\uff0c\u6211\u4eec\u4e5f\u597d\u8ffd\u968f\u5728\u5c11\u5e84\u4e3b\u7684\u8eab\u4fa7\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u76b1\u76b1\u7709\u5934\u9053\uff1a\u201c\u5c31\u51ed\u4f60\u8fd9\u8154\u7231\u62a4\u4e3b\u4eba\u7684\u5fe0\u4e49\u4e4b\u6c14\uff0c\u5728\u4e0b\u4e5f\u8be5\u8bbe\u6cd5\u6210\u5168\uff0c\u4e0d\u8fc7\uff0c\u4f60\u4eec\u7b54\u5e94\u5728\u4e0b\u51e0\u4e2a\u6761\u4ef6\u3002\u201d \u3000\u3000\u7389\u5408\u9053\uff1a\u201c\u4ec0\u4e48\u6761\u4ef6\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u7acb\u523b\u628a\u4f60\u4eec\u8eab\u4e0a\u7684\u6697\u5668\u3001\u5175\u5203\u5168\u90e8\u53d6\u51fa\u6765\uff0c\u7559\u5728\u8fd9\u753b\u822b\u4e4b\u4e0a\u3002\u201d \u3000\u3000\u738b\u5b9d\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u738b\u5408\u9053\uff1a\u201c\u8fd8\u4e0d\u5feb\u4e9b\u5c0a\u7167\u65b9\u4e8c\u4fa0\u7684\u5429\u5490\uff0c\u7b49\u4ec0\u4e48\u5462\uff1f\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u6025\u5fd9\u53d6\u51fa\u4e86\u6000\u4e2d\u7684\u6697\u5668\uff0c\u548c\u4e24\u628a\u624b\u53c9\u5b50\uff0c\u63b7\u5728\u5730\u4e0a\u3002 \u3000\u3000\u738b\u5b9d\u671b\u4e86\u54e5\u54e5\u4e00\u773c\uff0c\u4e5f\u628a\u6000\u4e2d\u7684\u6697\u5668\uff0c\u548c\u4e00\u628a\u5315\u9996\u53d6\u51fa\uff0c\u63b7\u5728\u5730\u4e0a\u3002 \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u6211\u5f88\u4fe1\u4efb\u4e24\u4f4d\uff0c\u8eab\u4e0a\u5df2\u65e0\u5bf8\u94c1\u3002\u201d \u3000\u3000\u738b\u5408\u9053\uff1a\u201c\u65b9\u4e8c\u4fa0\u8fd8\u6709\u4ec0\u4e48\u5429\u5490\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u7b2c\u4e8c\u4ef6\uff0c\u4e24\u4f4d\u8981\u9075\u5b88\u5e9c\u4e2d\u89c4\u77e9\uff0c\u5982\u6709\u8fdd\u72af\uff0c\u51b3\u4e0d\u5bbd\u8d27\uff0c\u522b\u8bf4\u5728\u4e0b\u672a\u5728\u4e8b\u5148\u8bf4\u660e\u3002\u201d \u3000\u3000\u738b\u5408\u9053\uff1a\u201c\u5165\u5883\u968f\u4fd7\uff0c\u6211\u7b49\u65e2\u975e\u62db\u60f9\u662f\u975e\u800c\u53bb\uff0c\u81ea\u7136\u4f1a\u9075\u5b88\u4ed6\u4eec\u7684\u89c4\u77e9\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u70b9\u70b9\u5934\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u8d70\u5427\uff01\u201d\u5f53\u5148\u4e3e\u6b65\u884c\u53bb\u3002 \u3000\u3000\u738b\u5408\u3001\u738b\u5b9d\u548c\u90a3\u9ec4\u8863\u5c11\u5e74\uff0c\u7a0b\u5b50\u671b\uff0c\u77f3\u4fca\u8d2f\u9c7c\u51fa\u8231\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u65b9\u5929\u6210\u7b49\u4e58\u6765\u7684\u5c0f\u821f\uff0c\u4ecd\u7136\u505c\u6cca\u5728\u90a3\u753b\u822b\u65c1\u4fa7\uff0c\u7a0b\u5b50\u671b\u62a2\u6b65\u4e0a\u524d\uff0c\u4e00\u8dc3\u800c\u4e0b\uff0c\u843d\u4e0a\u5c0f\u821f\u3002 \u3000\u3000\u65b9\u5929\u6210\u671b\u4e86\u9ec4\u8863\u5c11\u5e74\u548c\u738b\u5408\u3001\u738b\u5b9d\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4e09\u4f4d\u53ef\u8981\u5728\u4e0b\u76f8\u52a9\u5417\uff1f\u201d \u3000\u3000\u9ec4\u8863\u5c11\u5e74\u9053\uff1a\u201c\u4e0d\u6562\u6709\u52b3\u65b9\u4e8c\u4fa0\u3002\u201d\u7eb5\u8eab\u800c\u4e0b\u3002 \u3000\u3000\u7389\u5408\u3001\u738b\u5b9d\u7d27\u968f\u7740\u8dc3\u843d\u3002 \u3000\u3000\u5c0f\u821f\u5bb9\u91cf\u6709\u9650\uff0c\u843d\u4e0a\u51e0\u4eba\u5df2\u7136\u6324\u6ee1\u3002 \u3000\u3000\u5f20\u4e09\u8fd9\u65f6\u53c8\u9a76\u6765\u4e00\u8258\u5c0f\u821f\uff0c\u63a5\u4e86\u65b9\u5929\u6210\u548c\u77f3\u4fca\u3002 \u3000\u3000\u4e24\u821f\u5e76\u9a70\u8f6c\u773c\u95f4\u9760\u5cb8\u3002 \u3000\u3000\u65b9\u5929\u6210\u53f3\u624b\u6325\u52a8\uff0c\u70b9\u4e86\u738b\u542b\u3001\u7389\u5b9d\u4e24\u5904\u7a74\u9053\uff0c\u63a5\u9053\uff1a\u201c\u7a0b\u5144\u5148\u5e26\u4ed6\u4eec\u56de\u53bb\uff0c\u62db\u96c6\u5bb6\u4e01\uff0c\u51c6\u5907\u4e00\u4e0b\uff0c\u5251\u95e8\u4e94\u9b3c\u975e\u4e00\u822c\u6b66\u6797\u4eba\u7269\u53ef\u6bd4\uff0c\u5982\u4e0d\u65e9\u4f5c\u5b89\u6392\uff0c\u53ea\u6015\u8981\u591a\u4f24\u65e0\u8f9c\u3002\u201d \u3000\u3000\u8a00\u4e0b\u4e4b\u610f\uff0c\u90a3\u65e0\u7591\u662f\u8bf4\uff0c\u5251\u95e8\u4e94\u9b3c\u624b\u6bb5\u6076\u6bd2\uff0c\u8981\u90a3\u7a0b\u5b50\u671b\u65e9\u4e9b\u628a\u5185\u7737\u5a62\u5973\uff0c\u4ee5\u53ca\u8001\u5f31\u5bb6\u5974\uff0c\u9063\u79bb\u7a0b\u5e9c\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u5927\u6069\u4e0d\u8a00\u8c22\uff0c\u5144\u5f1f\u89c9\u7740\u5f88\u60ed\u6127\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u7a0b\u5144\u4e0d\u7528\u5f15\u548e\u592a\u591a\uff0c\u8bf4\u8d77\u6765\u5185\u60c5\u590d\u6742\u5f97\u5f88\uff0c\u660e\u65e5\u5348\u65f6\u6211\u4eec\u81ea\u4f1a\u8d74\u5e9c\u8be6\u8c08\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u4e00\u62b1\u638c\uff0c\u9053\uff1a\u201c\u7a0b\u67d0\u606d\u5019\u4e09\u4f4d\u5927\u9a7e\u3002\u201d \u3000\u3000\u8a00\u5b8c\u5e26\u7740\u5f20\u4e09\u62bc\u7740\u90a3\u9ec4\u8863\u5c11\u5e74\u4e3b\u4ec6\u4e09\u4eba\uff0c\u76f4\u5954\u7a0b\u5e9c\u800c\u53bb\u3002 \u3000\u3000\u65b9\u5929\u6210\u76ee\u7779\u7a0b\u5b50\u671b\u8fdc\u53bb\u4e4b\u540e\uff0c\u8f7b\u8f7b\u4e00\u62c9\u77f3\u4fca\uff0c\u9053\uff1a\u201c\u4e09\u5f1f\uff0c\u4f60\u89c1\u8fc7\u9b3c\u706b\u4f20\u8baf\u4e4b\u6cd5\u5417\uff1f\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u6ca1\u6709\u89c1\u8fc7\u3002 \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u54b1\u4eec\u89c1\u8bc6\u4e00\u4e0b\u5427\uff01\u201d\u5feb\u6b65\u884c\u5230\u50fb\u5df7\u4e4b\u4e2d\uff0c\u7eb5\u8eab\u8dc3\u4e0a\u5c4b\u9762\uff0c\u6674\u4e2d\u6ce8\u89c6\u7740\u90a3\u8258\u753b\u80aa\u3002 \u3000\u3000\u7b49\u5019\u4e86\u8db3\u8db3\u6709\u4e00\u987f\u996d\u5de5\u592b\u4e4b\u540e\uff0c\u624d\u89c1\u90a3\u753b\u822b\u51b2\u8d77\u4e00\u9053\u706b\u5149\uff0c\u76f4\u4e0a\u4e91\u9704\uff0c\u7206\u73b0\u4e94\u6735\u94f6\u661f\uff0c\u597d\u4e45\u59cb\u6563\u3002 \u3000\u3000\u77f3\u4fca\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6b64\u5730\u8ddd\u5251\u95e8\u9065\u9065\u5343\u91cc\uff0c\u8fd9\u51b2\u5929\u706b\u82b1\u5982\u4f55\u80fd\u591f\u53ca\u8fdc\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u6211\u60f3\u8fd9\u51b2\u5929\u706b\u82b1\uff0c\u5fc5\u662f\u4e00\u79cd\u70df\u5e55\uff0c\u771f\u7684\u4f20\u8fc5\u4e4b\u6cd5\uff0c\u5fc5\u7136\u6709\u5185\u60c5\uff0c\u5251\u95e8\u4e94\u5251\u7684\u9b3c\u706b\u4f20\u8fc5\uff0c\u5728\u6c5f\u6e56\u751a\u4e3a\u6709\u540d\uff0c\u5176\u4f0e\u4fe9\u5374\u4e0d\u6b62\u6b64\u3002\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u4e8c\u54e5\u9ad8\u89c1\uff0c\u54b1\u4eec\u518d\u7b49\u5019\u4e0b\u53bb\u3002\u201d \u3000\u3000\u53c8\u8fc7\u4e86\u534a\u70b7\u9999\u7684\u65f6\u5149\uff0c\u7a81\u89c1\u90a3\u753b\u822b\u4e0a\u98de\u8d77\u4e00\u70b9\u706b\u5149\uff0c\u76f4\u5411\u4e1c\u5317\u98de\u53bb\u3002 \u3000\u3000\u77f3\u4fca\u5fae\u5fae\u70b9\u5934\u9053\uff1a\u201c\u662f\u4e86\uff0c\u4ed6\u4eec\u5728\u4fe1\u9e3d\u8eab\u4e0a\uff0c\u52a0\u4e00\u70b9\u71c3\u70e7\u7684\u75d5\u8ff9\uff0c\u53f7\u79f0\u9b3c\u706b\u4f20\u8fc5\u4e4b\u6cd5\uff0c\u770b\u7a7f\u4e86\uff0c\u5b9e\u4e5f\u6ca1\u6709\u4ec0\u4e48\u5e0c\u5947\u4e4b\u5904\u3002\u201d \u3000\u3000\u53ea\u89c1\u90a3\u71c3\u70e7\u7684\u7ea2\u5149\uff0c\u7a81\u7136\u7206\u88c2\u51fa\u6570\u70b9\u94f6\u661f\uff0c\u5f85\u90a3\u94f6\u661f\u6563\u53bb\uff0c\u90a3\u98de\u884c\u7684\u706b\u82b1\uff0c\u5df2\u7136\u6d88\u5931\u4e0d\u89c1\u3002 \u3000\u3000\u65b9\u5929\u6210\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u662f\u4fe1\u9e3d\uff0c\u90a3\u75d5\u8ff9\u4e4b\u95f4\uff0c\u8fd8\u6709\u706b\u82b1\uff0c\u4fe1\u9e3d\u51b3\u65e0\u8fd9\u5927\u529b\u91cf\u3002\u201d \u3000\u3000\u77f3\u4fca\u5927\u611f\u8be7\u5f02\uff0c\u9053\uff1a\u201c\u4e0d\u662f\u4fe1\u9e3d\uff0c\u53c8\u662f\u4f55\u7269\u5462\uff1f\u6211\u4e0d\u4fe1\u5251\u95e8\u4e94\u9b3c\uff0c\u771f\u7684\u4f1a\u9b3c\u706b\u4f20\u8fc5\u7684\u90aa\u672f\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u5e94\u8be5\u662f\u4e00\u79cd\u731b\u79bd\uff0c\u50cf\u82cd\u9e70\u91d1\u773c\u96d5\u4e4b\u7c7b\u3002\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u54b1\u4eec\u56de\u5e97\u53bb\u5427\uff01\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u5df2\u7136\u591c\u6df1\u4eba\u9759\uff0c\u4e24\u4eba\u4e00\u8def\u6025\u5954\uff0c\u56de\u5230\u4e86\u5ba2\u6808\u4e4b\u4e2d\u3002 \u3000\u3000\u53ea\u89c1\u866c\u9aef\u5f97\u5927\u6c49\u80cc\u8d1f\u53cc\u624b\uff0c\u7ad9\u5728\u9662\u4e2d\uff0c\u671b\u4e86\u4e24\u4eba\u4e00\u773c\uff0c\u9053\uff1a\u4f60\u4eec\u56de\u6765\u4e86\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u3001\u77f3\u4fca\u9f50\u9f50\u6b20\u8eab\u4e00\u793c\uff0c\u9053\uff1a\u201c\u5927\u54e5\u8fd8\u672a\u4f11\u606f\u5417\uff1f\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u4f60\u4eec\u89c1\u5230\u4e86\u90a3\u6253\u4f24\u65b9\u73de\u7684\u4eba\u5417\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u89c1\u5230\u4e86\uff0c\u90a3\u4eba\u662f\u91d1\u67aa\u5b97\u58eb\u4e49\u7684\u513f\u5b50\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u91d1\u67aa\u5b97\u58eb\u4e49\uff1f\u201d \u3000\u3000\u77f3\u4fca\u63a5\u9053\uff1a\u201c\u662f\u7684\uff0c\u8fd8\u6709\u5251\u95e8\u4e94\u9b3c\u66ff\u4ed6\u4eec\u6491\u8170\uff0c\u6240\u4ee5\uff0c\u90a3\u4eba\u5f88\u662f\u72c2\u5984\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u5c0f\u5f1f\u89c1\u5230\u4e86\u5251\u95e8\u4e94\u9b3c\u8463\u65b9\uff0c\u636e\u90a3\u8463\u65b9\u8bf4\uff0c\u8fd1\u65e5\u4e4b\u5185\uff0c\u4e94\u9b3c\u548c\u5b97\u58eb\u4e49\u8981\u5728\u5609\u5b9a\u805a\u4f1a\uff0c\u5c0f\u5f1f\u5df2\u4ee3\u7a0b\u5b50\u671b\u63fd\u4e0b\u4e86\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u548c\u4e94\u9b3c\u8ba2\u4e0b\u4f1a\u9762\u4e4b\u7ea6\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u4f46\u54b1\u4eec\u5728\u5609\u5b9a\u4e0d\u80fd\u4e45\u505c\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u5c0f\u5f1f\u5df2\u64c5\u81ea\u4f5c\u4e3b\uff0c\u9650\u4ed6\u4eec\u540e\u65e5\u5348\u65f6\u4f1a\u9762\u3002\u5982\u662f\u8fc7\u4e86\u65f6\u9650\uff0c\u54b1\u4eec\u5c31\u4e0d\u518d\u7b49\u5019\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u4e94\u9b3c\u5e76\u975e\u4fe1\u4e49\u4e4b\u4eba\uff0c\u54b1\u4eec\u8d70\u540e\u4ed6\u4eec\u518d\u6765\uff0c\u7a0b\u5b50\u671b\u52bf\u975e\u906d\u53d7\u4ed6\u4eec\u8bdb\u7edd\u6ee1\u95e8\u4e0d\u53ef\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u5c0f\u5f1f\u5df2\u601d\u8651\u53ca\u6b64\uff0c\u6240\u4ee5\uff0c\u6211\u5df2\u5e26\u6765\u90a3\u5b97\u58eb\u4e49\u7684\u513f\u5b50\uff0c\u4f5c\u4e3a\u4eba\u8d28\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u90a3\u5b97\u516c\u5b50\u73b0\u5728\u4f55\u5904\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u5df2\u4ea4\u7ed9\u7a0b\u5b50\u671b\u5e26\u56de\u7a0b\u5e9c\u4e2d\u53bb\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u9053\uff1a\u201c\u5c0f\u5f1f\u5df2\u903c\u4f7f\u90a3\u4e94\u9b3c\u8463\u65b9\uff0c\u65bd\u5c55\u9b3c\u706b\u4f20\u8fc5\u4e4b\u6cd5\uff0c\u50ac\u4fc3\u4e94\u9b3c\u65e9\u65e5\u5230\u6b64\uff0c\u540c\u65f6\u4e5f\u53ef\u89c1\u8bc6\u90a3\u8a89\u6ee1\u6c5f\u6e56\u7684\u9b3c\u706b\u4f20\u8fc5\u4e4b\u6cd5\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u7b11\u9053\uff1a\u201c\u767d\u8863\u540a\u5ba2\u6210\u7384\u901a\u66fe\u7ecf\u5bf9\u6211\u5938\u53e3\uff0c\u8bf4\u90a3\u9b3c\u706b\u4f20\u8fc5\u4e4b\u6cd5\uff0c\u4e0d\u4f46\u795e\u901f\u65e0\u6bd4\uff0c\u4e00\u65e5\u53ef\u5343\u91cc\uff0c\u5f02\u4e8e\u5e38\u60c5\uff0c\u6211\u542c\u4ed6\u81ea\u5439\u6cd5\u87ba\uff0c\u672a\u7406\u4f1a\u4ed6\uff0c\u6210\u7384\u901a\u4e5f\u5c31\u672a\u8bb2\u4e0b\u53bb\uff0c\u7a76\u7adf\u662f\u4ec0\u4e48\u65b9\u6cd5\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u662f\u4e00\u53cc\u731b\u79bd\uff0c\u8eab\u540e\u5e26\u4e0a\u4e00\u6839\u836f\u7ebf\u548c\u706b\u82b1\uff0c\u8bf4\u7a7f\u4e86\uff0c\u4e5f\u4e0d\u503c\u8bc6\u8005\u4e00\u54c2\uff0c\u548c\u4e00\u822c\u4fe1\u9e3d\u76f8\u4f3c\uff0c\u53ef\u80fd\u6bd4\u4fe1\u9e3d\u98de\u884c\u8f83\u9ad8\u4e00\u4e9b\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6b64\u756a\u548c\u4e94\u9b3c\u8ba2\u7ea6\uff0c\u5c0f\u5f1f\u5168\u662f\u4e3a\u4e86\u7a0b\u5b50\u671b\uff0c\u6b64\u4eba\u5728\u5609\u5b9a\u96c4\u5cd9\u4e00\u65b9\uff0c\u591a\u534a\u662f\u4ed7\u51ed\u4ed6\u4f5c\u4eba\u7684\u4e49\u6c14\uff0c\u9ed1\u3001\u767d\u4e24\u9053\u4e2d\u4eba\uff0c\u90fd\u80af\u66ff\u4ed6\u4fdd\u6301\u9762\u5b50\uff0c\u624d\u4fdd\u6301\u5609\u5b9a\u4e00\u65b9\u7684\u5b89\u9759\uff0c\u4f46\u5982\u9047\u4e0a\u5251\u95e8\u4e94\u9b3c\u8fd9\u7b49\u52b2\u654c\uff0c\u53ea\u6015\u662f\u65e0\u6cd5\u5e94\u4ed8\u4e86\u2026\u2026\u201d \u3000\u3000\u957f\u957f\u5410\u4e86\u4e00\u53e3\u6c14\uff0c\u63a5\u9053\uff1a\u201c\u4ed6\u4e3a\u54b1\u4eec\u5954\u8d70\u4e0d\u505c\uff0c\u60c5\u8c0a\u4e4b\u4e0a\uff0c\u54b1\u4eec\u4e5f\u4e0d\u80fd\u4e0d\u7ba1\uff0c\u53ea\u662f\u54b1\u4eec\u9762\u5bf9\u5927\u53d8\uff0c\u7ed3\u5c40\u96be\u6599\u4e4b\u9645\uff0c\u5c0f\u5f1f\u53c8\u62e6\u4e0b\u4e86\u8fd9\u6863\u5b50\u4e8b\uff0c\u6015\u8981\u589e\u52a0\u5927\u54e5\u5f88\u591a\u9ebb\u70e6\u4e86\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u4e94\u9b3c\u6076\u540d\uff0c\u65e9\u7740\u6c5f\u6e56\uff0c\u53ea\u4e0d\u8fc7\u4ed6\u4eec\u884c\u52a8\u4e00\u5411\u7f1c\u5bc6\uff0c\u6c5f\u6e56\u4e2d\u4eba\u867d\u90fd\u6653\u4ed6\u4eec\u4e3a\u6076\uff0c\u4f46\u53c8\u65e0\u6cd5\u6293\u5230\u4ed6\u4eec\u7684\u628a\u67c4\uff0c\u5c0f\u5144\u65e9\u5df2\u52a8\u8fc7\u8bbf\u67e5\u4ed6\u4eec\u6076\u8ff9\u7684\u5ff5\u5934\uff0c\u4f46\u522b\u4e8b\u5ef6\u8bef\uff0c\u4e00\u62d6\u81f3\u4eca\uff0c\u54b1\u4eec\u5609\u5b9a\u53ef\u505c\u7559\u4e00\u5929\uff0c\u5982\u662f\u8def\u4e0a\u8d76\u7d27\u4e00\u4e9b\uff0c\u53ef\u518d\u52a0\u4e24\u592d\uff0c\u53ea\u8981\u54b1\u4eec\u80fd\u591f\u5728\u4e94\u5929\u4e4b\u5185\uff0c\u529e\u5b8c\u6b64\u4e8b\uff0c\u90a3\u5c31\u884c\u4e86\u2026\u2026\u201d \u3000\u3000\u65b9\u5929\u6210\u4e0d\u89c1\u5927\u54e5\u602a\u8d23\uff0c\u5fc3\u4e2d\u767b\u65f6\u4e00\u5bbd\u3002 \u3000\u3000\u4f46\u95fb\u90a3\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u4e94\u9b3c\u9f50\u805a\uff0c\u5fc5\u6709\u5927\u4e8b\uff0c\u5609\u5b9a\u5730\u9762\u4e0a\uff0c\u5b9a\u7136\u6709\u7740\u503c\u5f97\u4e94\u9b3c\u9f50\u96c6\u7684\u4e8b\u7269\uff0c\u8fd9\u4e00\u70b9\u6211\u60f3\u7a0b\u5b50\u671b\u4e00\u5b9a\u660e\u767d\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u5c0f\u5f1f\u5df2\u7b54\u5141\u4ed6\uff0c\u660e\u65e5\u5348\u65f6\uff0c\u54b1\u4eec\u8fc1\u5c45\u7a0b\u5e9c\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u597d\u5427\uff01\u660e\u65e5\u54b1\u4eec\u642c\u8fc7\u53bb\u5c31\u662f\u3002\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4e24\u4f4d\u8d24\u5f1f\uff0c\u5954\u8d70\u52b3\u7d2f\uff0c\u4e5f\u8be5\u65e9\u4e9b\u4f11\u606f\u4e86\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u6b32\u8a00\u53c8\u6b62\uff0c\u6b20\u8eab\u800c\u9000\u3002 \u3000\u3000\u4e00\u5bb5\u6613\u8fc7\uff0c\u4eba\u65e5\u5929\u8272\u8fd1\u5348\uff0c\u65b9\u5929\u6210\u7ed3\u4e86\u5e97\u5e10\uff0c\u6b65\u51fa\u5e97\u95e8\u3002 \u3000\u3000\u65b9\u73de\u7ecf\u8fc7\u4e00\u65e5\u591c\u7684\u4f11\u606f\uff0c\u53c8\u773c\u7528\u4e86\u7075\u4e39\uff0c\u4eba\u5df2\u53ef\u4ee5\u81ea\u884c\u8d70\u52a8\u3002 \u3000\u3000\u4e2d\u5dde\u4e09\u4fa0\uff0c\u4f9d\u5e8f\u800c\u884c\uff0c\u65b9\u592b\u4eba\u3001\u65b9\u73de\uff0c\u65b9\u745c\u7d27\u968f\u8eab\u540e\uff0c\u6700\u540e\u662f\u4e1c\u8033\u8001\u4eba\u3002\u884c\u51fa\u5e97\u95e8\uff0c\u7a0b\u5b50\u671b\u7acb\u65f6\u8fce\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u866c\u9aef\u5927\u6c49\u4e00\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u7a0b\u5144\u5230\u4e86\u5f88\u4e45\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u7b11\u9053\uff1a\u201c\u65f6\u523b\u672a\u5230\uff0c\u4e0d\u6562\u6253\u6270\u2026\u2026\u201d\u8bed\u58f0\u4e00\u987f\uff0c\u9053\uff1a\u201c\u8f66\u53ea\u90fd\u5df2\u5907\u59a5\uff0c\u592b\u4eba\u3001\u5c11\u7237\u8bf7\u5148\u767b\u8f66\u3002\u201d \u3000\u3000\u4e3e\u624b\u4e00\u62db\uff0c\u4e00\u8f86\u56db\u5339\u5065\u9a6c\u7684\u5927\u7bf7\u8f66\u884c\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u65b9\u592b\u4eba\u3001\u65b9\u745c\u3001\u65b9\u73de\u8fdb\u5165\u8f66\u53a2\uff0c\u4e1c\u8033\u8001\u4eba\u548c\u8d76\u8f66\u4eba\uff0c\u540c\u65f6\u5750\u5728\u8f66\u5b50\u524d\u9762\u3002 \u3000\u3000\u7d27\u63a5\u7740\uff0c\u56db\u4e2a\u9752\u8863\u4eba\uff0c\u7275\u8fc7\u6765\u56db\u5339\u5065\u9a6c\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u65b9\u5144\u9a91\u6765\u7684\u9a6c\u5339\uff0c\u5144\u5f1f\u5df2\u5429\u5490\u4ed6\u4eec\u7275\u56de\u5e84\u4e2d\u591a\u52a0\u8349\u6599\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u591a\u8c22\u7a0b\u5144\u4e86\u3002\u201d \u3000\u3000\u4e2d\u5dde\u4e8c\u4fa0\u53ca\u7a0b\u5b50\u671b\u9f50\u7eb5\u4e0a\u9a6c\u80cc\uff0c\u76f4\u5954\u7a0b\u5bb6\u5e84\u3002 \u3000\u3000\u7a0b\u5bb6\u5e84\u7d27\u4e34\u5609\u5b9a\u57ce\uff0c\u76f8\u8ddd\u4e0d\u8fc7\u4e8c\u4e09\u91cc\uff0c\u4f46\u5374\u662f\u4e00\u4e2a\u56db\u4e0d\u4e34\u6751\u7684\u72ec\u51fa\u9662\uff0c\u56db\u5468\u6ee1\u5e03\u7fe0\u7af9\uff0c\u4e2d\u95f4\u7528\u94c1\u7f51\u8fde\u8d77\uff0c\u770b\u4e0a\u53bb\u7eff\u76f4\u4e34\u98ce\uff0c\u5b9e\u5219\u9632\u5907\u5341\u5206\u68ee\u4e25\u3002 \u3000\u3000\u5927\u95e8\u662f\u7897\u53e3\u7c97\u7ec6\u7684\u677e\u6728\u6392\u6210\uff0c\u4e00\u5ea7\u5341\u5206\u5e7f\u5927\u9662\u843d\uff0c\u5206\u6210\u80b2\u9a6c\u68da\u548c\u7ec3\u6b66\u573a\u3002 \u3000\u3000\u540d\u4e3a\u7a0b\u5bb6\u5e84\uff0c\u5b9e\u5219\u53ea\u4f4f\u7740\u7a0b\u5b50\u671b\u4e00\u5bb6\u4eba\uff0c\u4e0a\u4e0b\u767e\u5341\u53f7\u4eba\uff0c\u9664\u4e86\u7a0b\u5b50\u671b\u3001\u7a0b\u592b\u4eba\u3001\u548c\u4e00\u4f4d\u7a0b\u59d1\u5a18\u5916\uff0c\u5c3d\u90fd\u662f\u5974\u5a62\u6b66\u5e08\u548c\u517b\u9a6c\u9972\u72ac\u7684\u4e0b\u4eba\u3002 \u3000\u3000\u7a7f\u8fc7\u5e7f\u573a\uff0c\u624d\u662f\u5b85\u9662\uff0c\u91cd\u697c\u53e0\u9601\uff0c\u767d\u77f3\u8d70\u5ed3\uff0c\u56db\u8fdb\u9662\u843d\uff0c\u5404\u5177\u5385\u53a2\uff0c\u6c14\u9b44\u5b8f\u4f1f\uff0c\u5e03\u8bbe\u8c6a\u534e\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u5f53\u5148\u4e0b\u9a6c\u5e26\u51e0\u4eba\u76f4\u5165\u7b2c\u4e8c\u8fdb\u9662\u843d\u7684\u5927\u5385\u4e2d\u3002 \u3000\u3000\u7a0b\u592b\u592b\u65e9\u5df2\u5728\u4e8c\u5385\u524d\u76f8\u8fce\uff0c\u5e26\u7740\u65b9\u592b\u4eba\u3001\u65b9\u745c\u3001\u65b9\u73de\u8fdb\u5165\u540e\u5385\u3002 \u3000\u3000\u4e00\u4e2a\u5e10\u623f\u5148\u751f\u6a21\u6837\u7684\u4e2d\u5e74\u4eba\uff0c\u8fce\u7740\u4e1c\u8033\u8001\u4eba\uff0c\u53e6\u884c\u5165\u5ea7\u8de8\u9662\u4e2d\u53bb\u3002\u7a0b\u5b50\u671b\u5e26\u7740\u4e2d\u5dde\u4e09\u4fa0\u8fdb\u5165\u4e86\u4e8c\u5385\u4e4b\u4e2d\u3002 \u3000\u3000\u4e24\u4e2a\u9752\u8863\u5c0f\u7ae5\uff0c\u732e\u4e0a\u8336\uff0c\u53c8\u6084\u7136\u9000\u51fa\u3002 \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u5251\u95e8\u4e94\u9b3c\u7684\u4e8b\uff0c\u4e8c\u5f1f\u5df2\u544a\u8bc9\u6211\u2026\u2026\u201d \u3000\u3000\u7a0b\u5b50\u671b\u63a5\u9053\uff1a\u201c\u7a0b\u67d0\u60ed\u6127\uff0c\u539f\u60f3\u4e3a\u4e09\u4f4d\u7565\u6548\u5fae\u52b3\uff0c\u5374\u4e0d\u6599\u8981\u4e09\u4f4d\u5e2e\u6211\u7a0b\u67d0\u4eba\u4e86\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4e8b\u60c5\u662f\u5728\u4e0b\u63fd\u4e0b\uff0c\u8fd9\u4e2a\u548c\u7a0b\u5144\u65e0\u5e72\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u65b9\u4e8c\u4fa0\u4e0d\u7528\u5411\u7a0b\u67d0\u4eba\u8138\u4e0a\u5e16\u91d1\u5b50\u4e86\uff0c\u5982\u975e\u65b9\u4e8c\u4fa0\u548c\u77f3\u4e09\u4fa0\u5728\u573a\uff0c\u4e94\u9b3c\u8463\u65b9\u5f53\u65f6\u5c31\u4e0d\u80af\u8f7b\u6613\u5730\u9976\u8fc7\u5728\u4e0b\u4e86\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u4e8b\u60c5\u5df2\u7ecf\u53d1\u751f\uff0c\u8981\u7d27\u7684\u662f\u5982\u4f55\u60f3\u6cd5\u5b50\u5bf9\u4ed8\u5f3a\u654c\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u90d1\u5927\u4fa0\u8bf4\u7684\u662f\uff0c\u5728\u4e09\u4f4d\u9762\u524d\uff0c\u7a0b\u5b50\u671b\u4e5f\u4e0d\u4f1a\u5ba2\u6c14\uff0c\u6b64\u756a\u8fce\u654c\u4e4b\u4e8b\uff0c\u8fd8\u671b\u90d1\u5927\u4fa0\u4ece\u4e2d\u4e3b\u6301\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff0c\u201c\u54b1\u4eec\u7814\u5546\u51b3\u5b9a\u5c31\u662f\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5728\u4e0b\u60f3\u5148\u4e86\u89e3\u7a0b\u5144\u8fd9\u65b9\u9762\u7684\u4eba\u4e8b\u60c5\u5f62\uff0c\u6211\u4eec\u4e09\u5144\u5f1f\u4e0d\u80fd\u5728\u6b64\u591a\u7559\uff0c\u5fc5\u9700\u6709\u4e00\u6218\u4e4b\u4e2d\uff0c\u89e3\u51b3\u4e94\u9b3c\u7684\u4e8b\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u8001\u673d\u6b66\u529f\u5e73\u5e73\uff0c\u4e09\u4f4d\u65e9\u5df2\u77e5\u6653\uff0c\u8001\u673d\u4e5f\u4e0d\u7528\u9690\u7792\u4e8c\u4f4d\u4e86\uff0c\u56e0\u4e3a\uff0c\u6211\u8fd9\u7a0b\u5bb6\u5e84\u4e2d\uff0c\u5012\u6709\u51e0\u4f4d\u52a9\u62f3\u4e4b\u4eba\uff0c\u4e00\u822c\u7684\u6c5f\u6e56\u6b66\u5e08\uff0c\u53ef\u4ee5\u5bf9\u4ed8\uff0c\u4f46\u5982\u50cf\u5251\u95e8\u4e94\u9b3c\u8fd9\u7b49\u9ad8\u624b\uff0c\u90a3\u5c31\u65e0\u6cd5\u5bf9\u4ed8\u4e86\uff01\u201d \u3000\u3000\u4ed6\u4f3c\u662f\u81ea\u77e5\u6765\u8bf4\u6e05\u695a\uff0c\u6025\u6025\u63a5\u9053\uff1a\u201c\u80fd\u591f\u548c\u4eba\u52a8\u624b\u7684\u5927\u7ea6\u6709\u5341\u51e0\u4e2a\u4eba\uff0c\u4e8c\u5341\u51e0\u540d\u5f29\u7bad\u624b\uff0c\u65bd\u7528\u8fde\u73e0\u5323\u5f29\uff0c\u4e00\u5323\u5341\u4e8c\u652f\uff0c\u6bcf\u4eba\u5e26\u6709\u5f29\u7bad\u56db\u5341\u516b\u53ea\uff0c\u56db\u5934\u897f\u85cf\u731b\u72ac\uff0c\u53e6\u5916\u4e8c\u5341\u540d\u5b88\u66f4\u54e8\u7684\u5065\u6c49\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u70b9\u5934\u9053\uff1a\u201c\u8fd9\u4e9b\u5b9e\u529b\uff0c\u9632\u5b88\u8fd9\u5ea7\u7a0b\u5bb6\u5e84\u5df2\u7136\u7b97\u5f97\u5f88\u5f3a\uff0c\u4e0d\u8fc7\uff0c\u5fc5\u5f97\u59a5\u4e3a\u5b89\u6392\uff0c\u624d\u4e0d\u81f4\u4e8e\u6709\u5f88\u5927\u7684\u727a\u7272\uff0c\u56e0\u4e3a\u90a3\u5251\u95e8\u4e94\u9b3c\u90fd\u662f\u6697\u5668\u80fd\u624b\uff0c\u5c24\u4ee5\u767d\u8863\u540a\u5ba2\u6210\u7384\u901a\u90a3\u8ffd\u547d\u86c7\u7fbd\u7bad\uff0c\u5728\u6b66\u6797\u4e2d\u4eab\u540d\u751a\u76db\uff0c\u4e8c\u5341\u540d\u5323\u7bad\u624b\uff0c\u5fc5\u9700\u8981\u6709\u7740\u5f88\u597d\u7684\u63a9\u853d\u624d\u6210\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u5728\u4e0b\u5bb6\u4e2d\uff0c\u6536\u6709\u5341\u9762\u76fe\u724c\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u7b11\u9053\uff1a\u201c\u76fe\u724c\u5012\u662f\u4e0d\u9700\u8981\u4e86\uff0c\u8fd9\u4e9b\u7bad\u624b\u548c\u6b66\u5e08\uff0c\u4e5f\u53ea\u4e0d\u8fc7\u662f\u7528\u4f5c\u8b66\u6212\uff0c\u8981\u4ed6\u4eec\u5bf9\u4ed8\u5251\u95e8\u4e94\u9b3c\uff0c\u5f62\u540c\u9001\u547d\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u5927\u54e5\u548c\u767d\u8863\u540a\u5ba2\u6210\u7384\u901a\u5f88\u719f\uff0c\u53ef\u77e5\u4ed6\u6b66\u529f\u5982\u4f55\u5417\uff1f\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u7b11\u9053\uff1a\u201c\u5f88\u9ad8\u5f3a\uff0c\u5176\u4f59\u56db\u9b3c\uff0c\u4e5f\u90fd\u4e0d\u5f31\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u54b1\u4eec\u662f\u5426\u4e5f\u8981\u51c6\u5907\u4e00\u4e0b\u5462\uff1f\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u6b64\u523b\u4e5f\u65e0\u4ece\u51c6\u5907\u8d77\uff0c\u56e0\u4e3a\u54b1\u4eec\u8fd8\u65e0\u6cd5\u9884\u6d4b\u90a3\u5251\u95e8\u4e94\u9b3c\u662f\u660e\u6765\u5462\uff1f\u8fd8\u662f\u6697\u88ad\uff0c\u5982\u662f\u4ed6\u5149\u5195\u5802\u5730\u7684\u6295\u5e16\u6c42\u89c1\uff0c\u54b1\u4eec\u4efb\u4f55\u5e03\u7f6e\u90fd\u5f52\u65e0\u7528\uff0c\u6211\u4eec\u53ea\u597d\u591a\u51c6\u5907\u4e00\u4e0b\uff0c\u5230\u65f6\u968f\u673a\u5e94\u53d8\u5c31\u662f\u3002\u201d \u3000\u3000\u8bed\u58f0\u5fae\u5fae\u4e00\u987f\uff0c\u76ee\u5149\u8f6c\u5230\u7a0b\u5b50\u671b\u7684\u8eab\u4e0a\uff0c\u9053\uff1a\u201c\u7a0b\u5144\uff0c\u5728\u4e0b\u6709\u51e0\u53e5\u8bdd\uff0c\u8bf7\u6559\u7a0b\u5144\uff0c\u5e0c\u671b\u7a0b\u5144\u80fd\u591f\u636e\u5b9e\u800c\u8a00\u3002\u201d \u3000\u3000\u7a0b\u671b\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u5144\u5f1f\u662f\u77e5\u65e0\u4e0d\u8a00\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u5fc5\u6709\u91cd\u5927\u4e8b\u6545\u53d1\u751f\uff0c\u4e94\u9b3c\u8981\u5728\u8fd9\u5609\u5b9a\u5730\u9762\u4f1a\u9f50\uff0c\u8fd9\u5609\u5b9a\u5fc5\u6709\u5438\u5f15\u4ed6\u4eec\u7684\u4e8b\u7269\u4e86\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u6c89\u541f\u4e86\u4e00\u9635\u9053\uff1a\u201c\u5341\u5e74\u6765\uff0c\u5609\u5b9a\u5730\u9762\u4e0a\u4e00\u76f4\u662f\u98ce\u5e73\u6d6a\u9759\uff0c\u867d\u7136\u7535\u6709\u6b66\u6797\u4eba\u5728\u6b64\u95f9\u51fa\u68b0\u6597\u7684\u60e8\u4e8b\uff0c\u4f46\u90fd\u662f\u610f\u6c14\u51b2\u7a81\uff0c\u4e00\u65f6\u4e4b\u5dee\uff0c\u9884\u4e3a\u7b79\u8c0b\u7684\u4e8b\u8fd8\u672a\u53d1\u751f\uff0c\u5144\u5f1f\u51fa\u9762\u8c03\u89e3\uff0c\u5927\u5bb6\u4e5f\u90fd\u5f88\u8d4f\u8138\uff0c\u5e38\u5e38\u662f\u5316\u5e72\u6208\u7684\u4e3a\u7389\u5e1b\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u5728\u4e0b\u4e4b\u610f\uff0c\u662f\u8bf4\u8fd4\u5609\u5b9a\u5730\u9762\uff0c\u6709\u4ec0\u4e48\u8db3\u4ee5\u5f15\u8d77\u4e94\u9b3c\u7684\u4e8b\u7269\uff0c\u4f8b\u5982\u4e00\u79cd\u540d\u8d35\u7684\u5b9d\u7269\uff0c\u6216\u662f\u6b66\u6797\u79d8\u5f55\u4e4b\u7c7b\u7684\u4e1c\u897f\uff0c\u5343\u53e4\u540d\u5251\u7b49\uff0c\u4ef7\u503c\u8fde\u6210\u7684\u5947\u7269\uff0c\u5728\u5609\u5b9a\u51fa\u73b0\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u6c89\u601d\u4e86\u826f\u4e45\uff0c\u9053\uff1a\u201c\u6709\u4e00\u4e2a\u4f20\u8bf4\uff0c\u53d1\u751f\u5728\u4e00\u5e74\u524d\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u4ec0\u4e48\u4f20\u8bf4\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u8ddd\u5609\u5b9a\u5341\u91cc\u5de6\u53f3\uff0c\u6709\u4e00\u5ea7\u5f20\u5bb6\u5e84\uff0c\u6bcf\u591c\u5b50\u65f6\uff0c\u5e38\u73b0\u51fa\u5b9d\u6c14\uff0c\u65f6\u65ad\u65f6\u7eed\uff0c\u7ef5\u5ef6\u4e09\u4e2a\u6708\u4e4b\u4e45\uff0c\u4f46\u4ee5\u540e\u5374\u8bc1\u660e\u4e86\u90a3\u662f\u4e00\u53e3\u706b\u4e95\u3002\u201d \u3000\u3000\u65b9\u5927\u6210\u9053\uff1a\u201c\u7a0b\u5144\uff0c\u53ef\u66fe\u77a7\u5230\u90a3\u4e95\u5347\u8d77\u6765\u5b9d\u6c14\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u90a3\u65f6\uff0c\u5144\u5f1f\u521a\u597d\u6709\u70b9\u4e8b\uff0c\u8d76\u5f80\u6210\u90fd\u53bb\uff0c\u56de\u6765\u65f6\uff0c\u6b64\u8bf4\u5df2\u8fc7\u53bb\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u706b\u4e95\uff0c\u4ece\u672a\u542c\u8bf4\u8fc7\uff0c\u7a0b\u5144\u53bb\u77a7\u8fc7\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u5144\u5f1f\u56de\u6765\u4e4b\u540e\uff0c\u542c\u5230\u8fd9\u4e2a\u4f20\u8bf4\uff0c\u5c31\u4eb2\u81ea\u8d76\u5230\u90a3\u5f20\u5bb6\u5e84\u53bb\u77a7\u4e86\u4e00\u4e0b\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u77a7\u5230\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u4e00\u53e3\u4e95\uff0c\u5e38\u5e38\u6709\u706b\u7130\u5192\u51fa\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u90a3\u53e3\u4e95\u662f\u4ec0\u4e48\u6837\u5b50\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u5f62\u72b6\u548c\u4e00\u822c\u7684\u6c34\u4e95\u65e0\u5f02\uff0c\u53ea\u662f\u6709\u706b\u7130\u5192\u51fa\u800c\u5df2\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u4e00\u5e74\u56db\u5b63\uff0c\u4e0d\u65ad\u6709\u706b\u7130\u5192\u51fa\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u4e0d\u4e00\u5b9a\uff0c\u6709\u65f6\u9694\u4e0a\u4e09\u4e94\u65e5\uff0c\u6709\u65f6\u4e00\u65e5\u4e00\u6b21\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u7a0b\u5144\u5f1f\u95ee\u8fc7\u90a3\u706b\u4e95\u7684\u7ecf\u8fc7\u60c5\u5f62\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u95ee\u8fc7\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u5e0c\u671b\u7a0b\u5144\u80fd\u4ed4\u7ec6\u7684\u8bf4\u660e\u7ecf\u8fc7\uff0c\u6108\u8bf4\u7684\u660e\u767d\u6108\u597d\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u636e\u90a3\u5f20\u5e84\u4e3b\u544a\u8bc9\u5144\u5f1f\uff0c\u8bf4\u662f\u4e00\u65e5\u591c\u95f4\uff0c\u5ffd\u7136\u6709\u706b\u5149\uff0c\u7531\u5730\u4e2d\u6d8c\u51fa\uff0c\u76f4\u5192\u6570\u5341\u4e08\uff0c\u8fdc\u8fdc\u671b\u53bb\u597d\u50cf\u662f\u4e00\u6839\u706b\u67f1\uff0c\u8fd9\u60c5\u5f62\u5e38\u5e38\u5728\u591c\u95f4\u51fa\u73b0\uff0c\u7ef5\u5ef6\u4e86\u6570\u6708\u4e4b\u4e45\uff0c\u4ed6\u624d\u53eb\u5de5\u4eba\u5728\u90a3\u5730\u65b9\u6316\u6398\uff0c\u5f53\u591c\u5c31\u6709\u70c8\u7130\u5192\u51fa\uff0c\u5f62\u6210\u706b\u4e95\uff0c\u8bf4\u8d77\u706b\u4e95\u5c31\u662f\u8fd9\u4e48\u56de\u4e8b\uff0c\u4f46\u90a3\u5f20\u5bb6\u5e84\u6709\u5b9d\u6c14\u51fa\u73b0\uff0c\u4ecd\u7136\u6d41\u4f20\u7740\uff0c\u8bf4\u90a3\u51fa\u73b0\u7684\u5b9d\u6c14\uff0c\u548c\u8fd9\u706b\u4e95\u65e0\u5173\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u7a0b\u5144\u5bf9\u6b64\u4e8b\u7684\u770b\u6cd5\u5982\u4f55\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u867d\u7136\u8a00\u4e4b\u51ff\u51ff\uff0c\u4f46\u5374\u65e0\u771f\u51ed\u636e\u5b9e\u5144\u5f1f\u4e5f\u5c31\u672a\u518d\u67e5\u95ee\u6b64\u4e8b\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u9664\u6b64\u4e4b\u5916\uff0c\u5609\u5b9a\u5730\u9762\uff0c\u8fd8\u6709\u4ec0\u4e48\u503c\u5f97\u90a3\u5251\u95e8\u4e94\u9b3c\u5230\u8fd9\u4e2a\u5730\u65b9\u6765\u7684\u4e8b\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u5609\u5b9a\u5730\u9762\u5341\u5206\u5bcc\u6709\uff0c\u90a3\u5251\u95e8\u4e94\u9b3c\u4e0d\u77e5\u662f\u5426\u60f3\u6d17\u52ab\u5609\u5b9a\uff01\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u6447\u6447\u5934\u9053\uff1a\u201c\u5982\u662f\u5251\u95e8\u4e94\u9b3c\uff0c\u53ea\u662f\u60f3\u52ab\u53d6\u8d22\u7269\uff0c\u7528\u4e0d\u7740\u5230\u5609\u5b9a\u6765\uff0c\u4ed6\u4eec\u968f\u4fbf\u52ab\u53d6\u4e00\u8d9f\u9556\u94f6\uff0c\u5c82\u4e0d\u662f\u5341\u5206\u7b80\u5355\u7684\u4e8b\uff1f\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u8083\u7136\u8bf4\u9053\uff1a\u201c\u7a0b\u5144\u5f1f\u8981\u4ed4\u7ec6\u7684\u60f3\u60f3\uff0c\u6b64\u4e8b\u5173\u7cfb\u91cd\u5927\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u5982\u82e5\u90a3\u5251\u95e8\u4e94\u9b3c\uff0c\u4e0d\u662f\u4e3a\u8d22\u7269\uff0c\u5144\u5f1f\u5b9e\u662f\u60f3\u4e0d\u51fa\uff0c\u4ed6\u4eec\u4f55\u4ee5\u4f1a\u5728\u5609\u5b9a\u5730\u9762\u4e0a\u805a\u4f1a\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u95ed\u76ee\u51dd\u601d\uff0c\u4e0d\u518d\u591a\u95ee\u3002 \u3000\u3000\u65b9\u5929\u6210\u7a81\u7136\u63a5\u53e3\u9053\uff1a\u201c\u5251\u95e8\u4e94\u9b3c\uff0c\u96c6\u4f1a\u4e8e\u65af\u7684\u4e3e\u52a8\uff0c\u5b9e\u662f\u53eb\u4eba\u6000\u7591\uff0c\u5982\u82e5\u54b1\u4eec\u6697\u4e2d\u89c2\u5bdf\uff0c\u81ea\u7136\u96be\u627e\u51fa\u4ed6\u4eec\u7684\u7528\u5fc3\uff0c\u4f46\u6b64\u523b\u7ecf\u54b1\u4eec\u8fd9\u4e00\u95f9\uff0c\u53ea\u6015\u662f\u4ed6\u4eec\u8981\u6539\u53d8\u4e3b\u610f\uff0c\u5148\u8981\u5168\u529b\u5bf9\u4ed8\u54b1\u4eec\u4e86\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u7a81\u7136\u8f6c\u53d8\u8bdd\u9898\uff0c\u9053\uff1a\u201c\u90a3\u4f4d\u5b97\u516c\u5b50\u73b0\u5728\u4f55\u5904\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u73b0\u5728\u540e\u56ed\u4e2d\u7684\u5730\u7a96\u4e2d\u3002\u201d \u3000\u3000\u866c\u9aef\u8ba8\u5927\u6c49\u9053\uff1a\u201c\u627e\u4ed6\u6765\u6b64\u95ee\u95ee\uff0c\u4e5f\u8bb8\u53ef\u4ee5\u95ee\u51fa\u4e00\u70b9\u86db\u4e1d\u9a6c\u8ff9\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u5e94\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8001\u673d\u53bb\u5e26\u4ed6\u6765\u6b64\u3002\u201d\u8d77\u8eab\u79bb\u5f00\u5ba2\u5385\u3002 \u3000\u3000\u7247\u523b\u4e4b\u540e\uff0c\u7a0b\u5b50\u671b\u5e26\u7740\u5b97\u516c\u5b50\u884c\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u90a3\u5b97\u516c\u5b50\u795e\u8272\u6194\u60b4\uff0c\u663e\u7136\uff0c\u6628\u591c\u672a\u66fe\u597d\u597d\u7761\u8fc7\u3002 \u3000\u3000\u866c\u9aef\u5927\u6c49\u6253\u91cf\u4e86\u5b97\u516c\u5b50\u4e00\u773c\uff0c\u9053\uff0c\u201c\u9601\u4e0b\u600e\u6837\u79f0\u547c\uff1f\u201d \u3000\u3000\u5b97\u516c\u5b50\u9053\uff1a\u201c\u533a\u533a\u5b97\u8d35\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u7f13\u7f13\u7684\u8bf4\u9053\uff1a\u201c\u4f60\u548c\u5251\u95e8\u4e94\u9b3c\u5f88\u719f\uff1f\u201d \u3000\u3000\u5b97\u8d35\u9053\uff1a\u201c\u5251\u95e8\u4e94\u4e49\uff0c\u90fd\u662f\u5bb6\u7236\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u5251\u95e8\u4e94\u9b3c\u548c\u4ee4\u5c0a\u805a\u4f1a\u4e8e\u6b64\u4e4b\u4e8b\uff0c\u5b97\u516c\u5b50\u60f3\u5fc5\u5341\u5206\u4e86\u7136\u4e86\uff1f\u201d \u3000\u3000\u5b97\u8d35\u9053\uff1a\u201c\u542c\u5bb6\u7236\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u7528\u5fc3\u4f55\u5728\uff1f\u201d \u3000\u3000\u5b97\u8d35\u9053\uff1a\u201c\u5728\u4e0b\u5e74\u8bb0\u5e7c\u5c0f\uff0c\u4e0d\u53c2\u673a\u8981\u3002\u201d \u3000\u3000\u77f3\u4fca\u51b7\u7b11\u9053\uff1a\u201c\u597d\u5e72\u8106\u7684\u7b54\u590d\u3002\u201d \u3000\u3000\u5b97\u8d35\u9053\uff1a\u201c\u5728\u4e0b\u8a00\u51fa\u8877\u8bda\uff0c\u8bf8\u4f4d\u4e0d\u4fe1\uff0c\u90a3\u4e5f\u662f\u6ca1\u6709\u6cd5\u5b50\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u6211\u7b49\u65e0\u610f\u4f24\u5bb3\u5b97\u516c\u5b50\uff0c\u6545\u800c\u5e0c\u671b\u5b97\u516c\u5b50\u80fd\u548c\u6211\u7b49\u5408\u4f5c\u3002\u201d \u3000\u3000\u5b97\u8d35\u9053\uff1a\u201c\u5728\u4e0b\u77e5\u65e0\u4e0d\u8a00\uff0c\u4f46\u4e0d\u77e5\u9053\u7684\u4e8b\u60c5\uff0c\u5c31\u65e0\u4ece\u8bf4\u8d77\u4e86\u3002\u201d \u3000\u3000\u77f3\u4fca\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u5b97\u516c\u5b50\u4e00\u5411\u5a07\u751f\uff0c\u53ea\u6015\u672a\u5403\u8fc7\u76ae\u8089\u4e4b\u82e6\u3002\u201d \u3000\u3000\u5b97\u8d35\u62ac\u5934\u77a7\u4e86\u77f3\u4fca\u4e00\u773c\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\u7684\u90fd\u8bf4\u4e86\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u5982\u82e5\u4ece\u5176\u4ed6\u4eba\u7684\u53e3\u4e2d\uff0c\u95ee\u51fa\u5185\u60c5\uff0c\u5b97\u516c\u5b50\u53c8\u8be5\u5982\u4f55\uff1f\u201d \u3000\u3000\u5b97\u8d35\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u6307\u738b\u5408\u3001\u738b\u5b9d\u4e8c\u4eba\u5417\uff1f\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u5b97\u8d35\u7f13\u7f13\u4ed6\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u4e0d\u77e5\u9053\u7684\u4e8b\uff0c\u91cf\u4ed6\u4eec\u4e5f\u4e0d\u77e5\u6653\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u4e00\u6325\u624b\uff0c\u9053\uff1a\u201c\u7a0b\u5144\u5f1f\uff0c\u5e26\u8fd9\u4f4d\u5b97\u4e16\u5144\u5230\u524d\u9762\u53bb\uff0c\u8981\u597d\u597d\u5730\u7167\u987e\uff0c\u5206\u522b\u5e26\u4e24\u4f4d\u957f\u968f\u8fdb\u6765\uff0c\u201d \u3000\u3000\u56db\u4e2a\u5e26\u5200\u7684\u5065\u6c49\u5e94\u4e86\u4e00\u58f0\uff0c\u63a8\u7740\u5b97\u8d35\u800c\u53bb\uff0c\u4e0d\u5927\u5de5\u592b\uff0c\u738b\u5408\u5e26\u5230\u3002 \u3000\u3000\u866c\u9aef\u5927\u6c49\u89c1\u90a3\u738b\u5408\u6d53\u7709\u73af\u76ee\uff0c\u4f3c\u6781\u51f6\u608d\uff0c\u5f53\u4e0b\u8138\u8272\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u4f60\u53eb\u738b\u5408\uff1f\u201d \u3000\u3000\u738b\u5408\u76ee\u5149\u8f6c\u52a8\uff0c\u626b\u63a0\u4e86\u866c\u9aef\u5927\u6c49\u548c\u65b9\u5929\u6210\u7b49\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u90a3\u5b97\u8d35\u5df2\u7136\u8bf4\u660e\u5185\u8bf7\uff0c\u6211\u5e0c\u671b\u9601\u4e0b\u4e5f\u80fd\u56de\u7b54\u6211\u7684\u95ee\u8bdd\u3002\u201d \u3000\u3000\u738b\u5408\u9053\uff1a\u201c\u4ec0\u4e48\u5185\u60c5\uff1f\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u5173\u4e8e\u90a3\u5b97\u58eb\u4e49\u548c\u5251\u95e8\u4e94\u9b3c\u4f1a\u805a\u5728\u5609\u5b9a\u7684\u7528\u5fc3\u3002\u201d \u3000\u3000\u738b\u5408\u9053\uff1a\u201c\u8fd9\u7b49\u5927\u4e8b\uff0c\u738b\u67d0\u4e0d\u914d\u53c2\u4e0e\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u5b97\u8d35\u5df2\u7ecf\u8bf4\u660e\u5927\u6982\uff0c\u9601\u4e0b\u5982\u662f\u60f3\u63a8\u8bff\u63a9\u906e\uff0c\u90a3\u662f\u81ea\u6211\u82e6\u5403\u4e86\u3002\u201d \u3000\u3000\u738b\u5408\u534a\u4fe1\u534a\u7591\u4ed6\u8bf4\u9053\uff1a\u201c\u6211\u5bb6\u516c\u5b50\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u4ed6\u8bf4\u90a3\u5251\u95e8\u4e94\u9b3c\uff0c\u4f1a\u96c6\u5609\u5b9a\u4e3a\u4e86\u62a2\u52ab\u4e00\u4ef6\u73cd\u5ba4\u3002\u201d \u3000\u3000\u738b\u5408\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u73cd\u5ba4\uff1f\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u8fd9\u4e2a\u4ed6\u672a\u8bf4\u660e\u3002\u201d \u3000\u3000\u738b\u5408\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u4e2a\u9a97\u672f\uff0c\u4e0d\u8fc7\u738b\u67d0\u4e0d\u5403\u8fd9\u4e00\u5957\u3002\u201d \u3000\u3000\u767d\u4fca\u7a81\u7136\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u4f60\u5403\u8fc7\u4ec0\u4e48\uff1f\u884c\u8840\u56de\u96c6\u7684\u5473\u9053\uff0c\u5c1d\u8fc7\u6ca1\u6709\uff1f\u201d \u3000\u3000\u738b\u5408\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u6211\u8bb0\u5f97\u65b9\u4e8c\u4fa0\u7b54\u5e94\u8fc7\u8463\u4e94\u7237\uff0c\u4e0d\u8bb8\u4f24\u5bb3\u6211\u7b49\u3002\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u90a3\u662f\u8bf4\uff0c\u4f60\u8981\u548c\u6211\u7b49\u5408\u4f5c\u624d\u6210\uff0c\u5982\u662f\u4e0d\u80af\u5408\u4f5c\uff0c\u90a3\u65f6\u53c8\u522b\u8bba\u3002\u201d \u3000\u3000\u738b\u5408\u9ebb\u8138\u4e0a\u808c\u8089\u8df3\u52a8\uff0c\u679c\u7136\uff0c\u4ed6\u5185\u5fc3\u4e2d\u6b63\u6709\u7740\u65e0\u6bd4\u7684\u6fc0\u52a8\u3002 \u3000\u3000\u77f3\u4fca\u7f13\u7f13\u4ed6\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u4fe1\u4f60\u662f\u94c1\u6253\u94dc\u6d47\u7684\u4eba\u201d\uff0c\u53f3\u624b\u7f13\u7f13\u5730\u5411\u738b\u5408\u524d\u80f8\u70b9\u53bb\u3002\u65b9\u5929\u6210\u7a81\u7136\u9053\uff1a\u201c\u4e09\u5f1f\u4f4f\u624b\uff01\u201d \u3000\u3000\u77f3\u4fca\u505c\u4e0b\u624b\uff0c\u9053\uff1a\u201c\u4e8c\u54e5\u6709\u4ec0\u4e48\u5429\u5490\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u80fd\u5931\u4fe1\u4e8e\u4eba\uff0c\u4e0d\u8bba\u4e94\u9b3c\u6b64\u756a\u6765\u610f\u5982\u4f55\uff0c\u4f46\u6211\u4eec\u65e2\u7136\u7ba1\u4e86\u8fd9\u7b49\u4e8b\uff0c\u53c8\u548c\u4ed6\u4eec\u8ba2\u4e0b\u4e86\u7ea6\u4f1a\uff0c\u4ed6\u4eec\u5982\u60f3\u5728\u5609\u5b9a\u6709\u6240\u4f5c\u4e3a\uff0c\u5fc5\u7136\u5148\u8981\u5bf9\u4ed8\u54b1\u4eec\uff0c\u5982\u6817\u54b1\u4eec\u8d25\u4e8e\u4e94\u9b3c\uff0c\u4e0d\u8bba\u4ed6\u4eec\u4f5c\u4e86\u4ec0\u4e48\u4e8b\uff0c\u54b1\u4eec\u4e5f\u662f\u65e0\u6cd5\u5e72\u9884\uff0c\u5982\u662f\u4e94\u9b3c\u8d25\u4e8e\u54b1\u4eec\u4e4b\u624b\uff0c\u4ed6\u4eec\u7eb5\u60f3\u6709\u6240\u4f5c\u4e3a\uff0c\u4e5f\u662f\u6709\u6240\u4e0d\u80fd\u4e86\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u6c89\u541f\u4e86\u4e00\u9635\u9053\uff1a\u201c\u4e8c\u5f1f\u8bf4\u7684\u662f\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u8f6c\u5230\u738b\u5408\u7684\u8138\u4e0a\u9053\uff1a\u201c\u8be5\u653e\u4f60\u7684\u65f6\u5019\uff0c\u6211\u7b49\u81ea\u4f1a\u653e\u4f60\u51fa\u6765\uff0c\u5982\u662f\u672a\u6536\u4e4b\u524d\uff0c\u4f60\u4eec\u81ea\u5df1\u6709\u6240\u52a8\uff0c\u4e5f\u5c31\u522b\u602a\u6211\u51fa\u624b\u72e0\u8fa3\u4e86\u3002\u201d \u3000\u3000\u738b\u5408\u539f\u60f3\u96be\u514d\u4e00\u756a\u76ae\u8089\u4e4b\u82e6\uff0c\u4f46\u5374\u610f\u60f3\u4e0d\u5230\u5bf9\u65b9\u7adf\u672a\u52a0\u5bb3\u3002 \u3000\u3000\u866c\u9aef\u5927\u6c49\u4e00\u6325\u624b\u9053\uff1a\u201c\u5e26\u4ed6\u4e0b\u53bb\u3002\u201d \u3000\u3000\u4e24\u4e2a\u52b2\u88c5\u5927\u6c49\u884c\u4e86\u8fc7\u6765\uff0c\u5e26\u8d70\u738b\u5408\u3002 \u3000\u3000\u77f3\u4fca\u76ee\u5149\u8f6c\u5230\u65b9\u5929\u6210\u7684\u8eab\u4e0a\u9053\uff1a\u201c\u4e8c\u54e5\uff0c\u5c0f\u5f1f\u6709\u4e00\u4ef6\u4e8b\u601d\u89e3\u4e0d\u900f\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u4f55\u4ee5\u4e8c\u54e5\u963b\u6b62\u5c0f\u5f1f\u903c\u95ee\u5185\u60c5\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u7b2c\u4e00\u662f\u4ed6\u672a\u5fc5\u77e5\u9053\uff0c\u7b2c\u4e8c\u662f\u6211\u7b54\u5e94\u8fc7\u672a\u89c1\u4e94\u9b3c\u4e4b\u524d\uff0c\u4e0d\u4f24\u5bb3\u4ed6\u4eec\u3002\u201d \u3000\u3000\u76ee\u5149\u8f6c\u5230\u90a3\u866c\u9aef\u5927\u6c49\u7684\u8138\u4e0a\uff0c\u9053\uff1a\u201c\u5927\u54e5\uff0c\u90a3\u4e94\u9b3c\u4e3a\u4eba\u5982\u4f55\uff1f\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u5fc3\u72e0\u624b\u8fa3\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u8fd9\u4e00\u6218\u54b1\u4eec\u662f\u5426\u8981\u5c3d\u6b7c\u4e94\u9b3c\u3002\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u4e94\u9b3c\u6076\u540d\u5df2\u8457\uff0c\u6740\u4e86\u4ed6\u4eec\u6b63\u597d\u4e3a\u6b66\u6797\u9664\u4e00\u5927\u5bb3\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u640f\u6740\u4e94\u9b3c\uff0c\u53ea\u6015\u4e0d\u662f\u4ef6\u5bb9\u6613\u7684\u4e8b\u3002\u201d \u3000 \u7b2c\u4e09\u56de\u3000\u4e94\u9b3c\u591c\u88ad \u3000 \u3000\u3000\u65b9\u5929\u6210\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u5c0f\u5f1f\u9690\u8ff9\u6df1\u5c71\uff0c\u70bc\u5236\u4e39\u836f\u65f6\uff0c\u66fe\u770b\u5230\u4e00\u53ea\u5de8\u96d5\u548c\u4e00\u6761\u94c1\u7532\u86c7\uff0c\u76f8\u6597\u6df1\u8c37\uff0c\u90a3\u5de8\u96d5\u4e0d\u77e5\u8eab\u606f\u4f55\u5904\uff0c\u5de8\u86c7\u5374\u76d8\u8e1e\u6df1\u8c37\uff0c\u5c0f\u5f1f\u90a3\u4e39\u706b\u7089\u5c31\u5728\u90a3\u6df1\u8c37\u4e4b\u4e0a\uff0c\u4e00\u5ea7\u5de8\u5ca9\u4e4b\u4e0a\uff01\u6bcf\u65e5\u52a0\u8fc7\u6728\u70ad\u4e4b\u540e\uff0c\u6b63\u662f\u65e5\u4e0a\u4e09\u7aff\u65f6\u5206\uff0c\u90a3\u65f6\u5c0f\u5f1f\u6b63\u597d\u95f2\u4e0b\u65e0\u4e8b\uff0c\u90a3\u5de8\u96d5\u4e5f\u5728\u6b64\u65f6\u98de\u4e34\u6df1\u8c37\uff0c\u548c\u94c1\u7532\u86c7\u5c55\u5f00\u6076\u6597\uff0c\u4e00\u6597\u4e24\u4e2a\u65f6\u8fb0\u4ee5\u4e0a\uff0c\u76f4\u6597\u5f97\u7b4b\u75b2\u529b\u5c3d\uff0c\u90a3\u5de8\u96d5\u98de\u4e0a\u5cf0\u9876\u4e00\u682a\u8001\u677e\u4e0a\uff0c\u4f11\u606f\u751a\u4e45\uff0c\u624d\u5c55\u7ffc\u800c\u53bb\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u90a3\u5de8\u96d5\u672c\u662f\u86c7\u7684\u514b\u57a6\uff0c\u4f46\u90a3\u94c1\u7532\u86c7\u9cde\u7247\u575a\u786c\uff0c\u5200\u67aa\u96be\u5165\uff0c\u90a3\u5de8\u96d5\u867d\u7136\u6709\u5229\u53e3\u94dc\u722a\uff0c\u4e5f\u662f\u65e0\u6cd5\u4f24\u5b83\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u5c0f\u5f1f\u521d\u89c1\u4e4b\u65f6\uff0c\u5e76\u672a\u653e\u5728\u5fc3\u4e0a\uff0c\u53ea\u662f\u611f\u5230\u8fd9\u5929\u5730\u4e4b\u95f4\u5f31\u8089\u5f3a\u98df\u7684\u5fae\u5999\u6d88\u957f\u4e4b\u673a\uff0c\u7adf\u4e5f\u65f6\u6709\u53d8\u5316\u3002\u201d \u3000\u3000\u77f3\u4fca\u542c\u5f97\u5927\u611f\u5174\u8da3\uff0c\u95ee\u9053\uff1a\u201c\u4ee5\u540e\u5462\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4ee5\u540e\u6211\u53d1\u89c9\u90a3\u96d5\u548c\u86c7\uff0c\u7adf\u7136\u662f\u79ef\u4e86\u5f88\u4e45\u7684\u4ec7\u4eba\uff0c\u5728\u6211\u4e4b\u524d\uff0c\u8fd9\u4e00\u86c7\u4e00\u96d5\uff0c\u5df2\u4e0d\u77e5\u6597\u4e86\u597d\u4e45\uff0c\u5728\u6211\u5230\u90a3\u60ac\u5d16\u4e4b\u540e\uff0c\u4eb2\u773c\u770b\u5230\u4ed6\u4eec\u52a0\u6597\u4e86\u534a\u5e74\u3002\u2026\u2026\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u534a\u5e74\u4e4b\u4e45\uff0c\u5c31\u672a\u5206\u51fa\u80dc\u8d1f\u5417\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u90a3\u94c1\u7532\u86c7\u8eab\u5728\u4e00\u5ea7\u5ca9\u6d1e\u4e4b\u4e2d\uff0c\u4e0a\u8eab\u63a2\u51fa\uff0c\u6076\u6597\u5de8\u96d5\u4ee5\u9038\u5f85\u52b3\uff0c\u4f53\u529b\u4e0a\u81ea\u7136\u662f\u7a0d\u5360\u4f18\u52bf\uff0c\u4f46\u4e5f\u6709\u5c3d\u51fa\u5ca9\u6d1e\u548c\u90a3\u5de8\u96d5\u640f\u6597\u60c5\u5f62\uff0c\u4e0d\u8fc7\uff0c\u90a3\u5168\u8eab\u5916\u51fa\u7684\u60c5\u5f62\u4e0d\u591a\uff0c\u5341\u6b21\u96be\u5f97\u4e00\u6b21\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u4f3c\u662f\u4e5f\u542c\u51fa\u4e86\u5174\u8da3\uff0c\u95ee\u9053\uff1a\u201c\u96be\u9053\u5b83\u4eec\u5c31\u6c38\u8fdc\u640f\u6597\u4e0b\u53bb\u4e0d\u6210\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u5c0f\u5f1f\u5728\u90a3\u96d5\u86c7\u51fa\u51fb\u76f8\u6597\u4e4b\u4e2d\uff0c\u609f\u51fa\u4e86\u4e0d\u5c11\u6b66\u529f\uff0c\u81ea\u884c\u521b\u51fa\u4e86\u4e00\u79cd\u9e70\u638c\u86c7\u638c\uff0c\u4f46\u5c0f\u5f1f\u624d\u667a\u6709\u9650\uff0c\u5fc3\u60f3\u7559\u5230\u89c1\u5f97\u5927\u54e5\uff0c\u63d0\u51fa\u7814\u7a76\uff0c\u5374\u4e0d\u6599\u5929\u5bab\u65e7\u4e8b\u63d0\u524d\u53d1\u4f5c\uff0c\u5144\u5f1f\u4eec\u4e00\u76f4\u6ca1\u6709\u673a\u4f1a\u8be6\u53d9\u3002\u201d \u3000\u3000\u77f3\u4fca\u4f3c\u4ecd\u5ff5\u5ff5\u5fd8\u4e0d\u90a3\u96d5\u86c7\u76f8\u6597\u4e4b\u4e8b\uff0c\u95ee\u9053\uff1a\u201c\u90a3\u96d5\u548c\u86c7\u4e00\u76f4\u6ca1\u6709\u5206\u51fa\u80dc\u8d1f\u5417\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u5c0f\u5f1f\u4e39\u6210\u4e09\u65e5\u4e4b\u524d\uff0c\u90a3\u5de8\u96d5\u7a81\u7136\u4e0d\u518d\u51fa\u73b0\uff0c\u4e00\u76f4\u5230\u5c0f\u5f1f\u7ec3\u6210\u79bb\u5f00\uff0c\u672a\u518d\u89c1\u8fc7\u86c7\u96d5\u76f8\u6597\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u542c\u5b8c\u6240\u89c1\u86c7\u96d5\u76f8\u6597\u60c5\u5f62\u540e\uff0c\u76ee\u5149\u8f6c\u79fb\u5230\u7a0b\u5b50\u671b\u7684\u8138\u4e0a\uff0c\u9053\uff1a\u201c\u7a0b\u5144\u2026\u2026\u201d \u3000\u3000\u7a0b\u5b50\u671b\u6b20\u8eab\u5e94\u9053\uff1a\u201c\u90d1\u5927\u4fa0\u6709\u4ec0\u4e48\u5429\u5490\uff1f\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u7a0b\u5144\u8bf7\u9009\u6d3e\u51e0\u4e2a\u7cbe\u660e\u4e4b\u4eba\uff0c\u5206\u5e03\u5728\u7801\u5934\u4e0a\uff0c\u89c6\u90a3\u753b\u822b\u4e2d\u4e3e\u52a8\uff0c\u5982\u662f\u5251\u95e8\u4e94\u9b3c\u548c\u5b97\u58eb\u4e49\u5230\u6b64\uff0c\u5b9a\u7136\u5148\u548c\u4e94\u9b3c\u4f1a\u5408\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u5144\u5f1f\u5df2\u7ecf\u6d3e\u4eba\u53bb\u4e86\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u5f88\u597d\u2026\u2026\u201d\u76ee\u5149\u53c8\u8f6c\u5230\u65b9\u5929\u6210\u7684\u8138\u4e0a\uff0c\u9053\uff1a\u201c\u4e8c\u5f1f\uff0c\u54b1\u4eec\u5bf9\u4ed8\u8fc7\u5251\u95e8\u4e94\u9b3c\u4e4b\u540e\uff0c\u518d\u7814\u7a76\u4f60\u609f\u51fa\u7684\u9e70\u638c\u3001\u86c7\u638c\u3002\u201d\u7ad9\u8d77\u8eab\u5b50\uff0c\u53c8\u9053\uff1a\u201c\u54b1\u4eec\u5148\u77a7\u4e00\u77a7\u8fd9\u7a0b\u5bb6\u5e84\u7684\u5f62\u52bf\uff0c\u5206\u6d3e\u4e00\u4e0b\u5f29\u7bad\u624b\u7684\u4f4d\u7f6e\uff0c\u5251\u95e8\u4e94\u9b3c\uff0c\u60ef\u4e8e\u6697\u88ad\uff0c\u54b1\u4eec\u4e0d\u53ef\u5927\u610f\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u62a2\u5728\u524d\u9762\u5e26\u8def\uff0c\u7ed5\u7740\u7a0b\u5bb6\u5e84\u884c\u4e86\u4e00\u5468\uff0c\u9053\uff1a\u201c\u90d1\u5927\u4fa0\u80f8\u7f57\u7384\u673a\uff0c\u8fd8\u671b\u6307\u793a\u4e00\u4e0b\u5e94\u5982\u4f55\u5e03\u7f6e\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u5e84\u4e2d\u5185\u7737\u53ef\u53e6\u6709\u85cf\u8eab\u4e4b\u5904\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u540e\u9662\u4e2d\u6709\u4e00\u5ea7\u79d8\u7a96\uff0c\u8001\u673d\u5df2\u6536\u62fe\u5e72\u51c0\uff0c\u65b9\u592b\u4eba\u7684\u516c\u5b50\u53ef\u5728\u7a96\u4e2d\u4f11\u606f\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u597d\uff0c\u4e94\u9b3c\u624b\u6bb5\u6bd2\u8fa3\uff0c\u54b1\u4eec\u4e0d\u80fd\u4e0d\u9632\uff0c\u7a0b\u592b\u4eba\u548c\u516c\u5b50\u6700\u597d\u4e5f\u5728\u7a96\u4e2d\uff0c\u96c6\u4e2d\u4e00\u5904\uff0c\u5fc5\u8981\u65f6\u4e5f\u597d\u4fdd\u62a4\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u8001\u673d\u606d\u656c\u4e0d\u5982\u4ece\u547d\u4e86\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u4e00\u4e8c\u5341\u540d\u5f13\u7bad\u624b\uff0c\u5728\u4e0b\u51c6\u5907\u96c6\u4e2d\u524d\u5385\u5e7f\u573a\uff0c\u5982\u662f\u4ed6\u4eec\u65bd\u5c55\u6697\u88ad\uff0c\u54b1\u4eec\u5c31\u5728\u5e7f\u573a\u4e0a\u548c\u4ed6\u51b3\u6597\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u4e00\u5207\u6089\u51ed\u90d1\u5927\u4fa0\u7684\u8c03\u5ea6\u3002\u201d \u3000\u3000\u8fd9\u866c\u9aef\u5927\u6c49\u6709\u7740\u4eba\u6240\u96be\u53ca\u7684\u8bb0\u5fc6\uff0c\u770b\u8fc7\u4e00\u904d\uff0c\u5df2\u7136\u719f\u8bb0\u4e86\u7a0b\u5bb6\u5e84\u7684\u6574\u4e2a\u5f62\u52bf\uff0c\u5f53\u4e0b\u4ed4\u7ec6\u8bf4\u660e\u4e86\u5f29\u7bad\u624b\u914d\u7f6e\u7684\u65b9\u4f4d\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u5168\u795e\u8d2f\u6ce8\uff0c\u4e00\u4e00\u9ed8\u8bb0\u4e8e\u5fc3\uff0c\u76f4\u5f85\u866c\u9aef\u5927\u6c49\u8bf4\u660e\u4e86\u5168\u5e84\u5e03\u7f6e\u624d\u7ad9\u8d77\u8eab\u5b50\uff0c\u8bf4\u9053\uff1a \u3000\u3000\u201c\u5728\u4e0b\u7acb\u523b\u8981\u4ed6\u4eec\u5404\u5c31\u5404\u4f4d\u3002\u201d\u8d77\u8eab\u5411\u5916\u8d70\u53bb\u3002 \u3000\u3000\u4e00\u5bb5\u8fc7\u53bb\uff0c\u98ce\u5e73\u6d6a\u9759\u3002 \u3000\u3000\u4e2d\u5348\u65f6\u5206\uff0c\u4e2d\u5dde\u4e09\u4fa0\u548c\u7a0b\u5b50\u671b\u6b63\u5728\u5385\u4e2d\u5c0f\u5ea7\uff0c\u5feb\u817f\u5f20\u4e09\u6025\u6025\u5730\u5954\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u6c89\u58f0\u95ee\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u6d88\u606f\uff1f\u201d \u3000\u3000\u5f20\u4e09\u4e3e\u624b\u64e6\u4e00\u4e0b\u5934\u4e0a\u7684\u6c57\u6c34\uff0c\u8bf4\u9053\uff1a\u201c\u753b\u822b\u9760\u5cb8\uff0c\u4e94\u9b3c\u8463\u65b9\u4e00\u4eba\u767b\u4e0a\u6c5f\u5cb8\uff0c\u4e0d\u77e5\u610f\u56fe\u4f55\u5728\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u600e\u4e0d\u6d3e\u4eba\u76ef\u7740\u4ed6\u5462\uff1f\u201d \u3000\u3000\u5f20\u4e09\u9053\uff1a\u201c\u5c5e\u4e0b\u6d3e\u4eba\u76ef\u68a2\uff0c\u770b\u5230\u4ed6\u884c\u5165\u4e00\u5bb6\u9152\u697c\u4e4b\u4e2d\uff0c\u76f8\u9694\u4e0d\u8fc7\u4e00\u76cf\u8336\u5de5\u592b\uff0c\u5c5e\u4e0b\u767b\u4e0a\u9152\u697c\u65f6\u5df2\u7136\u4e0d\u89c1\u90a3\u8463\u65b9\u884c\u8e2a\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u597d\u65e0\u7528\u7684\u4e1c\u897f\uff01\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u5fae\u5fae\u4e00\u7b11\uff0c\u63a5\u9053\uff0c\u201c\u4f60\u767b\u4e0a\u90a3\u9152\u697c\u4e4b\u540e\uff0c\u53ef\u66fe\u77a7\u5230\u53ef\u7591\u4eba\u7269\uff1f\u201d \u3000\u3000\u5f20\u4e09\u9053\uff1a\u201c\u9664\u4e86\u4e00\u4e2a\u767d\u9aef\u767d\u53d1\uff0c\u6ee1\u8138\u7ea2\u5149\u7684\u8001\u53df\u4e4b\u5916\uff0c\u518d\u65e0\u5176\u4ed6\u4e4b\u4eba\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4f60\u901a\u77e5\u7801\u5934\u4e0a\u7684\u5404\u5904\u8981\u9053\u7684\u54e8\u4e4b\u4eba\uff0c\u5feb\u64a4\u56de\u6765\uff0c\u4e94\u9b3c\u5df2\u7ecf\u5230\u7ecf\u5609\u5b9a\u3002\u201d \u3000\u3000\u5f20\u4e09\u5446\u4e86\u4e00\u5446\u6b20\u8eab\u800c\u9000\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u8f7b\u8f7b\u5730\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u6697\u88ad\u5462\uff1f\u8fd8\u662f\u660e\u6765\uff1f\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u770b\u5f62\u52bf\uff0c\u4ed6\u4eec\u662f\u6295\u5e16\u6c42\u89c1\u7684\u6210\u4efd\u5f88\u5927\u2014\u2014\u201d\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u7a0b\u5144\u8bf7\u5429\u5490\u5c5e\u4e0b\uff0c\u8981\u4ed6\u4eec\u5907\u4e0a\u4e24\u684c\u4e0a\u597d\u7684\u9152\u5e2d\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u8fd9\u4e2a\u65e9\u5df2\u5907\u9f50\u4e86\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u4f20\u4ee4\u4e0b\u53bb\u8981\u5e84\u5916\u5b88\u95e8\u4e4b\u4eba\uff0c\u89e3\u53bb\u4f69\u6234\u5175\u5200\uff0c\u6697\u5e26\u5315\u9996\uff0c\u5916\u7740\u957f\u886b\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u70b9\u70b9\u5429\u5490\u4e86\u957f\u968f\u3002 \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u4ed6\u4eec\u5982\u662f\u6295\u5e16\u6c42\u89c1\uff0c\u5728\u6c14\u5ea6\u4e0a\u54b1\u4eec\u4e0d\u80fd\u8f93\u7ed9\u4ed6\u4eec\u3002\u201d\u8bed\u58f0\u672a\u843d\uff0c\u770b\u89c1\u4e00\u4e2a\u5065\u6c49\uff0c\u624b\u6367\u5927\u7ea2\u5e16\u5b50\u884c\u4e86\u8fdb\u6765\uff0c\u9053\uff1a\u201c\u5251\u95e8\u4e94\u9b3c\u548c\u5b97\u4e3b\u6c42\u89c1\u7a0b\u5e84\u4e3b\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u6697\u6697\u5fd6\u9053\uff1a\u6765\u5f97\u597d\u5feb\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u90d1\u5927\u4fa0\uff0c\u54b1\u4eec\u5e94\u8be5\u5982\u4f55\uff1f\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u8fce\u63a5\u5927\u95e8\u4e4b\u5916\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u5429\u5490\u4e86\u4e00\u58f0\u90a3\u5065\u6c49\uff0c\u9053\uff1a\u201c\u8001\u592b\u4eb2\u5f80\u76f8\u8fce\uff0c\u5148\u884c\u5927\u5f00\u5e84\u95e8\uff0c\u8bf7\u4ed6\u4eec\u8fdb\u6765\u3002\u201d \u3000\u3000\u90a3\u5065\u6c49\u5e94\u4e86\u4e00\u58f0\uff0c\u8d77\u8eab\u5954\u51fa\u3002 \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u7a0b\u5144\u662f\u4e3b\u4eba\uff0c\u4e00\u5207\u7531\u7a0b\u5144\u89c1\u9762\uff0c\u5728\u4e0b\u4ece\u65c1\u76f8\u52a9\u3002\u201d\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a \u3000\u3000\u201c\u54b1\u4eec\u4e5f\u8be5\u53bb\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u3001\u4e2d\u5dde\u4e09\u4fa0\uff0c\u9c7c\u8d2f\u51fa\u8fce\u6392\u5217\u4e8e\u5927\u95e8\u4e4b\u5916\u3002 \u3000\u3000\u56db\u4eba\u4e5f\u4e0d\u8fc7\u521a\u521a\u7ad9\u597d\uff0c\u516d\u6761\u5927\u6c49\u5df2\u6025\u6b65\u884c\u6765\u3002 \u3000\u3000\u65b9\u5929\u6210\u62ac\u5934\u770b\u53bb\u3002\u53ea\u89c1\u90a3\u5f53\u5148\u4e00\u4eba\uff0c\u5168\u8eab\u767d\u8863\uff0c\u4e24\u6761\u4eba\u5b57\u7709\uff0c\u8138\u957f\u5982\u9a6c\uff0c\u60e8\u767d\u7684\u4e0d\u89c1\u4e00\u70b9\u8840\u8272\uff0c\u5934\u4e0a\u6234\u7740\u4e00\u9876\u9ed1\u8272\u7684\u9ad8\u6cbf\u6be1\u5e3d\u3002\u8eab\u6750\u7ec6\u9ad8\uff0c\u679c\u7136\u548c\u540a\u5ba2\u4e00\u822c\u6a21\u6837\u3002 \u3000\u3000\u866c\u9aef\u5927\u6c49\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u90a3\u5f53\u5148\u4e00\u4eba\u6b63\u662f\u4e94\u9b3c\u4e4b\u9996\uff0c\u767d\u8863\u540a\u5ba2\u6210\u7384\u901a\u3002\u201d \u3000\u3000\u7d27\u968f\u5728\u6210\u7384\u901a\u53f3\u9762\u4e00\u4eba\uff0c\u8eab\u6750\u9b41\u68a7\uff0c\u62ab\u7740\u7384\u8272\u82f1\u96c4\u88c5\uff0c\u5185\u7740\u5929\u84dd\u8272\u7d27\u8eab\u52b2\u88c5\uff0c\u6d53\u7709\u864e\u76ee\uff0c\u7d2b\u819b\u8138\uff0c\u8db3\u7740\u9ad8\u8170\u8584\u5e95\u5feb\u9774\uff0c\u795e\u60c5\u4e25\u8083\uff0c\u9690\u9690\u53ef\u89c1\u6012\u5bb9\uff0c\u9ed1\u8272\u957f\u9aef\uff0c\u76f4\u53ca\u5c0f\u8179\u3002 \u3000\u3000\u53e6\u5916\u56db\u4e2a\u4eba\u7a7f\u7740\u9ed1\u8272\u7684\u957f\u886b\uff0c\u4e94\u9b3c\u8463\u65b9\u4e5f\u5728\u5176\u4e2d\uff0c\u56db\u4eba\u9c7c\u8d2f\u968f\u5728\u767d\u8863\u540a\u5ba2\u7684\u540e\u9762\uff0c\u8463\u65b9\u662f\u6700\u672b\u4e00\u4eba\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u9065\u9065\u5730\u62b1\u62f3\u4e00\u793c\uff0c\u9053\uff1a\u201c\u5609\u5b9a\u7a0b\u5b50\u671b\uff0c\u548c\u5404\u4f4d\u89c1\u793c\u3002\u201d \u3000\u3000\u767d\u8863\u540a\u5ba2\u6210\u7384\u901a\u51b7\u773c\u6253\u91cf\u4e86\u9762\u5b50\u671b\u4e00\u9635\uff0c\u624d\u8fd8\u4e86\u4e00\u793c\uff0c\u9053\uff1a\u201c\u4e45\u95fb\u5927\u540d\uff0c\u4eca\u65e5\u5e78\u4f1a\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u8c08\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\u8bf4\uff0c\u597d\u8bf4\uff0c\u51e0\u4f4d\u5927\u9a7e\u5149\u4e34\uff0c\u84ec\u835c\u751f\u8f89\uff0c\u5927\u5385\u5df2\u8bbe\u9152\u4e00\u7b11\uff0c\u66ff\u51e0\u4f4d\u63a5\u98ce\u6d17\u5c18\u3002\u201d \u3000\u3000\u6210\u7384\u901a\u4e0d\u518d\u7406\u4f1a\u7a0b\u5b50\u671b\uff0c\u5374\u5feb\u884c\u4e24\u6b65\uff0c\u5230\u4e86\u90a3\u866c\u9aef\u5927\u6c49\u8eab\u524d\uff0c\u201c\u5927\u521a\u5144\uff0c\u4e45\u8fdd\u4e86\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5609\u5b9a\u53c8\u548c\u6210\u5144\u76f8\u6664\u3002\u201d \u3000\u3000\u6210\u7384\u901a\u9053\uff1a\u201c\u90d1\u5144\u76f8\u53ec\uff0c\u5144\u5f1f\u600e\u6562\u4e0d\u5982\u7ea6\u800c\u6765\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u6b64\u5730\u4e3b\u4eba\u5b50\u671b\u5144\uff0c\u5df2\u4e3a\u6210\u5144\u7b49\u6446\u4e86\u63a5\u98ce\u6d12\u7b75\uff0c\u54b1\u4eec\u5927\u5385\u4e2d\u8c08\u5427\uff01\u201d \u3000\u3000\u6210\u7384\u901a\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u90d1\u5144\u7adf\u4f1a\u548c\u5e02\u4e95\u5c60\u6cbd\u7684\u7a0b\u5b50\u671b\u4ea4\u4e0a\u4e86\u670b\u53cb\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u8f7b\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u5fcd\u4e0b\u672a\u8a00\u3002 \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u5987\u8bf4\u5e02\u4e95\u5c60\u6cbd\uff0c\u54b1\u4eec\u54ea\u4e00\u4f4d\u90fd\u7b97\u51fa\u8eab\u4e8e\u65af\u3002\u201d \u3000\u3000\u6210\u7384\u901a\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5927\u521a\u5144\u548c\u7a0b\u5e84\u4e3b\u7684\u4ea4\u60c5\uff0c\u5982\u6b64\u6df1\u539a\uff0c\u770b\u8d77\u6765\u5728\u4e0b\u662f\u5931\u8a00\u4e86\u3002\u201d \u3000\u3000\u53ea\u542c\u90a3\u8eab\u62ab\u7384\u8272\u82f1\u96c4\u88c5\u7684\u5927\u6c49\u8bf4\u9053\uff1a\u201c\u54ea\u4e00\u4f4d\u662f\u65b9\u4e8c\u4fa0\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u63a5\u9053\uff1a\u201c\u533a\u533a\u4fbf\u662f\uff0c\u9601\u4e0b\u60f3\u662f\u91d1\u67aa\u5b97\u5821\u4e3b\u4e86\u3002\u201d \u3000\u3000\u90a3\u5927\u6c49\u9053\uff1a\u201c\u5b97\u58eb\u4e49\uff0c\u540d\u4e0d\u89c1\u7ecf\u4f20\u65e0\u540d\u5c0f\u5352\uff0c\u5982\u4f55\u653e\u5728\u65b9\u4e8c\u4fa0\u5fc3\u4e0a\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5b97\u5821\u4e3b\u8fd9\u822c\u81ea\u8f7b\uff0c\u672a\u514d\u662f\u592a\u8fc7\u8c26\u865a\u4e86\u3002\u201d \u3000\u3000\u5b97\u58eb\u4e49\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u72ac\u5b50\u60f3\u5fc5\u5df2\u5728\u65b9\u4e8c\u4fa0\u7684\u624b\u4e2d\uff0c\u5403\u8fc7\u4e0d\u5c11\u82e6\u5934\u4e86\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4ee4\u90ce\u4e00\u6839\u6beb\u6bdb\u4e5f\u672a\u635f\u5931\uff0c\u5012\u53eb\u4f60\u5b97\u5821\u4e3b\u5931\u671b\u4e86\u3002\u201d \u3000\u3000\u5b97\u571f\u4e49\u9053\uff1a\u201c\u72ac\u5b50\u73b0\u5728\u4f55\u5904\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u5c31\u5728\u8fd9\u7a0b\u5bb6\u5e84\u4e2d\u3002\u201d \u3000\u3000\u5b97\u4e0a\u4e49\u9053\uff1a\u201c\u53ef\u5426\u8ba9\u72ac\u5b50\u548c\u5728\u4e0b\u89c1\u4e0a\u4e00\u9762\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u6de1\u8c08\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6b64\u4e8b\u4e48\u2026\u2026\u5144\u5f1f\u4f5c\u4e0d\u4e86\u4e3b\uff0c\u5b97\u5e84\u4e3b\uff0c\u4f55\u4ee5\u4e0d\u95ee\u95ee\u6b64\u5730\u4e3b\u4eba\u3002\u201d \u3000\u3000\u5b97\u58eb\u4e49\u51b7\u54fc\u4e00\u58f0\uff0c\u76ee\u5149\u8f6c\u5230\u7a0b\u5b50\u671b\u7684\u8138\u4e0a\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u7a0b\u5e84\u4e3b\uff0c\u5728\u4e0b\u5e0c\u671b\u72ac\u5b50\u771f\u5982\u90a3\u65b9\u4e8c\u4fa0\u6240\u8a00\uff0c\u6beb\u987b\u672a\u635f\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u5b97\u5821\u4e3b\u4e0d\u7528\u6025\uff0c\u4ee4\u90ce\u73b0\u5728\u7a0b\u67d0\u5e9c\u4e2d\uff0c\u6d3b\u7740\u8ba9\u4f60\u89c1\u4eba\uff0c\u6b7b\u4e86\u8ba9\u4f60\u89c1\u5c38\uff0c\u81f4\u4e8e\u7a0b\u67d0\u4eba\u5982\u4f55\u8650\u5f85\u4ee4\u90ce\uff0c\u89c1\u9762\u4e4b\u540e\uff0c\u4ed6\u81ea\u7136\u4f1a\u5bf9\u4f60\u8bf4\u4e2a\u660e\u767d\u3002\u201d \u3000\u3000\u5b97\u58eb\u4e49\u8138\u8272\u4e00\u53d8\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u72ac\u5b50\u5982\u53d7\u4e00\u70b9\u635f\u4f24\uff0c\u7a0b\u5e84\u4e3b\u81ea\u4f1a\u4e3a\u4ed6\u62b5\u507f\u3002\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5df2\u7136\u884c\u4eba\u5927\u5385\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u4e00\u62b1\u62f3\uff0c\u9053\uff1a\u201c\u8bf8\u4f4d\u8fdc\u9053\u800c\u6765\uff0c\u5bb9\u7a0b\u67d0\u7a0d\u5c3d\u5730\u4e3b\u4e4b\u8c0a\u3002\u201d \u3000\u3000\u6210\u7384\u901a\u56db\u987e\u4e86\u4e00\u773c\uff0c\u6602\u7136\u5165\u5ea7\u3002 \u3000\u3000\u5176\u4f59\u56db\u9b3c\u548c\u91d1\u67aa\u5b97\u58eb\u4e49\uff0c\u773c\u770b\u6210\u7384\u901a\u843d\u4e86\u5ea7\u4f4d\uff0c\u4e5f\u968f\u7740\u5750\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5251\u95e8\u4e94\u9b3c\u548c\u5b97\u58eb\u4e49\u6563\u5750\u4e00\u684c\uff0c\u4e2d\u5dde\u4e09\u4fa0\u548c\u7a0b\u5b50\u671b\u53ea\u597d\u5728\u53e6\u4e00\u684c\u4e0a\u5750\u4e0b\u3002 \u3000\u3000\u53cc\u65b9\u5404\u636e\u4e00\u5e2d\uff0c\u58c1\u5792\u5206\u660e\uff0c\u6210\u7384\u901a\u671b\u4e86\u6ee1\u684c\u4f73\u770b\u548c\u9762\u524d\u7f8e\u9152\u4e00\u773c\uff0c\u9053\uff1a\u201c\u90d1\u5144\uff0c\u54b1\u4eec\u5251\u95e8\u4e94\u9b3c\u5728\u6c5f\u6e56\u4e4b\u4e0a\uff0c\u548c\u8d35\u5144\u5f1f\u4e00\u76f4\u662f\u548c\u5e73\u76f8\u5904\uff0c\u8c08\u4e0d\u4e0a\u4e00\u70b9\u8fc7\u8282\uff0c\u60f3\u4e0d\u5230\u90d1\u5144\u7adf\u7136\u4e3a\u4e86\u4e00\u4e2a\u540d\u4e0d\u89c1\u7ecf\u4f20\u7684\u7a0b\u5b50\u671b\uff0c\u4e0d\u60dc\u548c\u6211\u4eec\u4e94\u5f1f\u654c\u5bf9\u76f8\u5904\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e2d\u5dde\u4e09\u4fa0\u5728\u6c5f\u6e56\u4e4b\u4e0a\uff0c\u72af\u8fc7\u5251\u95e8\u4e94\u4e49\u7684\u5730\u76d8\u5417\uff1f\u201d \u3000\u3000\u6210\u7384\u901a\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4e86\uff0c\u54b1\u4eec\u662f\u4e95\u6c34\u4e0d\u72af\u6cb3\u6c34\u3002\u201d \u3000\u3000\u6210\u7384\u901a\u63a5\u9053\uff1a\u201c\u53eb\u6210\u67d0\u60f3\u4e0d\u901a\uff0c\u8fd9\u4e00\u6b21\u8d35\u5144\u5f1f\u4f55\u4ee5\u4f1a\u66ff\u90a3\u7a0b\u5b50\u671b\u6491\u8170\u5462\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u6b32\u52a0\u4e4b\u7f6a\uff0c\u4f55\u60a3\u65e0\u8bcd\uff0c\u90d1\u67d0\uff0c\u4e0e\u7a0b\u5144\u76f8\u8bc6\uff0c\u5c31\u7b97\u662f\u66ff\u4ed6\u6491\u8170\uff0c\u4f46\u5144\u5f1f\u8981\u53cd\u95ee\u4e00\u53e5\uff0c\u8d35\u5144\u5f1f\u53c8\u4e3a\u4ec0\u4e48\u8981\u4e3a\u5b97\u5821\u4e3b\u6491\u8170\u5462\uff1f\u201d \u3000\u3000\u6210\u7384\u901a\u9053\uff1a\u201c\u90a3\u4e0d\u540c\uff0c\u5b97\u5144\u548c\u6211\u4eec\u5251\u95e8\u4e94\u5144\u5f1f\u4ea4\u5f80\u591a\u5e74\uff0c\u60c5\u540c\u9aa8\u8089\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u54b1\u4eec\u4e2d\u5dde\u4e09\u4fa0\u4e5f\u548c\u7a0b\u5e84\u4e3b\u6765\u5f80\u591a\u5e74\uff0c\u4e49\u5171\u751f\u6b7b\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u8f7b\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e8b\u6709\u672c\u672b\uff0c\u8fd9\u4ef6\u4e8b\u672c\u6765\u548c\u4f60\u4eec\u5251\u95e8\u4e94\u5144\u5f1f\u4ee5\u53ca\u7a0b\u5e84\u4e3b\u5168\u90fd\u65e0\u5173\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\u9053\uff0c\u63a5\u9053\uff1a\u201c\u5982\u662f\u518d\u52a0\u6df1\u4e00\u5c42\uff0c\u548c\u533a\u533a\u5927\u54e5\u3001\u4e09\u5f1f\u4e5f\u65e0\u7275\u8fde\uff0c\u8fd9\u4ef6\u4e8b\u662f\u6211\u65b9\u67d0\u4eba\u548c\u5b97\u5821\u4e3b\u7684\u4e8b\u60c5\u3002\u201d \u3000\u3000\u5b97\u58eb\u4e49\u9053\uff1a\u201c\u5728\u4e0b\u548c\u65b9\u4e8c\u4fa0\u7d20\u6627\u5e73\u751f\uff0c\u4e0d\u77e5\u65b9\u4e8c\u4fa0\u4f55\u4ee5\u751f\u64d2\u72ac\u5b50\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5b97\u5927\u5821\u4e3b\u8bf4\u7684\u5f88\u8f7b\u677e\uff0c\u4ee4\u90ce\u5e26\u7740\u8d35\u5821\u4e2d\u4e24\u4e2a\u957f\u968f\uff0c\u6253\u4f24\u4e86\u72ac\u5b50\u7684\u4e8b\uff0c\u5b97\u5821\u4e3b\u5f53\u771f\u662f\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u6210\u7384\u901a\u7a81\u7136\u63a5\u53e3\u8bf4\u9053\uff1a\u201c\u65b9\u4e8c\u4fa0\u53ef\u5426\u5148\u542c\u5728\u4e0b\u4e00\u8a00\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u613f\u542c\u9ad8\u8bba\uff01\u201d \u3000\u3000\u6210\u7384\u901a\u9053\uff1a\u201c\u5c0f\u8f88\u4eec\u5c11\u4e0d\u66f4\u4e8b\uff0c\u9020\u6210\u51b2\u7a81\uff0c\u8c08\u4e0d\u4e0a\u4ec0\u4e48\u4ec7\u6068\uff0c\u4e0d\u8bba\u548e\u5728\u4f55\u65b9\uff0c\u4f3c\u662f\u7528\u4e0d\u7740\u8ffd\u7a76\u4e0b\u53bb\uff0c\u597d\u5728\u53cc\u65b9\u90fd\u65e0\u6b7b\u4ea1\uff0c\u5c1a\u672a\u94f8\u6210\u5927\u9519\uff0c\u65b9\u4e8c\u4fa0\u3001\u5b97\u5821\u4e3b\u90fd\u662f\u82f1\u96c4\u4eba\u7269\uff0c\u60f3\u4e5f\u4e0d\u81f3\u4e8e\u592a\u8fc7\u62a4\u77ed\uff0c\u5144\u5f1f\u8ba4\u4e3a\u4e8b\u60c5\u5df2\u7ecf\u8fc7\u53bb\uff0c\u5927\u5bb6\u676f\u9152\u8a00\u6b22\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u4ece\u6b64\u591a\u4ea4\u4e00\u4e2a\u670b\u53cb\uff0c\u56e0\u4e8c\u5c0f\u51b2\u7a81\uff0c\u4f7f\u4e24\u4f4d\u8bba\u4ea4\uff0c\u5728\u6c5f\u6e56\u4e0a\u4e5f\u7b97\u4e00\u4e2a\u6625\u79cb\u7f8e\u8c08\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u5fc3\u4e2d\u5e76\u672a\u5b58\u5728\u6709\u4e3a\u65b9\u73de\u62a5\u590d\u4e4b\u5fc3\uff0c\u4f46\u8fd9\u662f\u552f\u4e00\u548c\u5251\u95e8\u4e94\u9b3c\u51b2\u7a81\u7684\u501f\u53e3\uff0c\u6210\u7384\u901a\u6597\u5e2d\u8bdd\u628a\u65b9\u5929\u6210\u8bf4\u5f97\u4e0d\u77e5\u5982\u4f55\u7b54\u590d\u624d\u597d\uff0c\u6c89\u541f\u96be\u8a00\u3002 \u3000\u3000\u5b97\u58eb\u4e49\u5374\u54c8\u54c8\u4e00\u7b11\uff0c\u7aef\u8d77\u9152\u676f\uff0c\u9053\uff1a\u201c\u6210\u5144\u8bf4\u5f97\u4e0d\u9519\uff0c\u4e3a\u4e86\u4e24\u4e2a\u5b69\u5b50\u600e\u80fd\u4f7f\u6211\u4eec\u5927\u4eba\u4e5f\u51b2\u7a81\u8d77\u6765\uff1f\u5728\u4e0b\u5148\u656c\u4e8c\u4fa0\u4e00\u676f\u3002\u201d \u3000\u3000\u5904\u6b64\u60c5\u51b5\u4e4b\u4e0b\uff0c\u65b9\u5929\u6210\u6b32\u62d2\u4e0d\u80fd\uff0c\u4e5f\u53ea\u597d\u7aef\u8d77\u9152\u6000\uff0c\u9053\uff1a\u201c\u591a\u8c22\u5b97\u5821\u4e3b\u4e86\u3002\u201d \u3000\u3000\u5b97\u58eb\u4e49\u4e00\u996e\u800c\u5c3d\u3002\u65b9\u5929\u6210\u4e5f\u53ea\u6709\u5e72\u676f\u3002 \u3000\u3000\u5b97\u58eb\u4e49\u7b11\u9053\uff1a\u201c\u65b9\u4e8c\u4fa0\uff0c\u72ac\u5b50\u548c\u4ee4\u90ce\u7684\u4e8b\uff0c\u5c31\u6b64\u4e00\u7b14\u52fe\u6d88\uff0c\u4ece\u6b64\u4e4b\u540e\uff0c\u54b1\u4eec\u90fd\u4e0d\u518d\u63d0\uff0c\u5144\u5f1f\u6536\u85cf\u4e00\u4e9b\u7f8e\u9152\uff0c\u65b9\u5144\u6709\u6687\u65f6\uff0c\u8bf7\u5230\u6211\u5b97\u5bb6\u5821\uff0c\u6211\u8981\u548c\u65b9\u5144\u75db\u996e\u4e00\u756a\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u65e5\u540e\u6709\u6687\uff0c\u5b9a\u5f53\u5230\u5b97\u5821\u4e3b\u5904\u62dc\u8bbf\u3002\u201d \u3000\u3000\u5b97\u58eb\u4e49\u9053\uff1a\u201c\u62dc\u8bbf\u5012\u4e0d\u6562\u5f53\uff0c\u5982\u8499\u5149\u4e34\u5bd2\u820d\uff0c\u5144\u5f1f\u662f\u8363\u5ba0\u4e07\u5206\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u56de\u987e\u7a0b\u5b50\u671b\uff0c\u9053\uff1a\u201c\u7a0b\u5144\uff0c\u90d1\u67d0\u6709\u4e00\u4e2a\u4e0d\u60c5\u4e4b\u6c42\uff0c\u4e0d\u77e5\u7a0b\u5144\u80af\u5426\u7b54\u5141\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u6545\u610f\u4e00\u53e3\u4e00\u4e2a\u7a0b\u5144\uff0c\u53eb\u5f97\u5341\u5206\u4eb2\u70ed\uff0c\u4e5f\u662f\u6709\u610f\u62ac\u4e3e\u90a3\u7a0b\u5b50\u671b\u7684\u8eab\u4efd\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u90d1\u5144\u53ea\u7ba1\u5429\u5490\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u90a3\u5b97\u516c\u5b50\u73b0\u5728\u4f55\u5904\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u73b0\u5728\u7a0b\u5bb6\u5e84\u4e2d\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u5b97\u5821\u4e3b\u5927\u9a7e\u4eb2\u4e34\uff0c\u8db3\u89c1\u5bf9\u7a0b\u5144\u5341\u5206\u7684\u770b\u91cd\uff0c\u5728\u4e0b\u60f3\u8bf7\u7a0b\u5144\u5148\u8ba9\u5b97\u5821\u4e3b\u7236\u5b50\u89c1\u4e0a\u4e00\u9762\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u90d1\u5144\u8bf4\u7684\u662f\u3002\u201d\u56de\u5934\u671b\u7740\u5385\u5916\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u5e26\u4e0a\u5b97\u516c\u5b50\uff01\u201d \u3000\u3000\u7247\u523b\u4e4b\u540e\u3002\u5b97\u516c\u5b50\u884c\u5165\u5385\u4e2d\uff0c\u4e00\u89c1\u5b97\u58eb\u4e49\u7acb\u65f6\u62dc\u4f0f\u9053\uff1a\u201c\u5b69\u513f\u89c1\u8fc7\u7239\u7239\uff01\u201d \u3000\u3000\u5b97\u58eb\u4e49\u8138\u8272\u4e00\u6c89\uff0c\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u6ca1\u6709\u7528\u7684\u4e1c\u897f\uff01\u201d \u3000\u3000\u6210\u7384\u901a\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5b69\u5b50\u5df2\u7ecf\u53d7\u4e86\u59d4\u5c48\uff0c\u4f60\u8fd9\u505a\u7236\u4eb2\u7684\uff0c\u96be\u9053\u8fd8\u8981\u8d23\u7f5a\u4e00\u987f\uff0c\u518d\u8bf4\uff0c\u4ed6\u8fd9\u70b9\u5e74\u7eaa\uff0c\u5982\u4f55\u80fd\u662f\u90a3\u65b9\u4e8c\u4fa0\u7684\u654c\u624b\u3002\u201d \u3000\u3000\u5b97\u58eb\u4e49\u9053\uff1a\u201c\u5982\u975e\u4f60\u4e49\u7236\u4e3a\u4f60\u6c42\u60c5\uff0c\u4e3a\u7236\u4eca\u65e5\u975e\u8981\u6559\u8bad\u4f60\u4e00\u987f\u4e0d\u53ef\uff0c\u8fd8\u4e0d\u5feb\u4e9b\u62dc\u8c22\u8fc7\u4f60\u4e49\u7236\uff01\u201d \u3000\u3000\u5b97\u516c\u5b50\u5bf9\u6210\u7384\u901a\u62dc\u4e86\u4e0b\u53bb\uff0c\u9053\uff1a\u201c\u591a\u8c22\u4e49\u7236\u3002\u201d \u3000\u3000\u6210\u7384\u901a\u7b11\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u5feb\u8d77\u6765\uff0c\u5750\u4e0b\u6765\u5403\u4e00\u676f\u9152\u3002\u201d \u3000\u3000\u5b97\u516c\u5b50\u7ad9\u8d77\u8eab\u5b50\u884c\u5230\u4e86\u6210\u7384\u901a\u7684\u8eab\u4fa7\u5750\u4e0b\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u8f7b\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6210\u5144\uff0c\u7a0b\u5b50\u671b\u6709\u4e00\u4ef6\u4e0d\u660e\u4e4b\u4e8b\uff0c\u60f3\u8bf7\u6559\u6210\u5144\uff1f\u201d \u3000\u3000\u6210\u7384\u901a\u8138\u8272\u4e00\u6c89\uff0c\u51b7\u58f0\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u5251\u95e8\u4e94\u4e49\u7684\u5a01\u540d\uff0c\u7a0b\u67d0\u662f\u4ef0\u6155\u4e45\u77e3\u2026\u2026\u201d \u3000\u3000\u6210\u7384\u901a\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u7a0b\u5e84\u4e3b\u4e0d\u7528\u5ba2\u6c14\uff0c\u6c5f\u6e56\u4e4b\u4e0a\uff0c\u90fd\u79f0\u6211\u4eec\u5251\u95e8\u4e94\u9b3c\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u6de1\u7136\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u4e94\u4e49\u4e5f\u597d\uff0c\u4e94\u9b3c\u4e5f\u597d\uff0c\u4f46\u4e94\u4f4d\u5927\u4fa0\u90fd\u662f\u4e00\u4e24\u4f4d\u5355\u72ec\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\u2026\u2026\u201d \u3000\u3000\u6210\u7384\u901a\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u4f46\u4e94\u4f4d\u4e5f\u6709\u4f1a\u9f50\u7684\u65f6\u5019\uff0c\u90a3\u5fc5\u5c06\u662f\u4e00\u756a\u60ca\u5929\u52a8\u5730\u7684\u5927\u4e8b\u3002\u201d \u3000\u3000\u6210\u7384\u901a\u9053\uff1a\u201c\u770b\u6765\u7a0b\u5e84\u4e3b\u5bf9\u6211\u4eec\u5144\u5f1f\u7684\u4e8b\u60c5\uff0c\u6253\u542c\u5f97\u5f88\u6e05\u695a\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u4e94\u4f4d\u5a01\u540d\u8fdc\u64ad\uff0c\u5929\u4e0b\u6709\u8c01\u4e0d\u77e5\u3002\u201d \u3000\u3000\u6210\u7384\u901a\u9053\uff1a\u201c\u7a0b\u5e84\u4e3b\u6709\u4ec0\u4e48\u4e8b\u660e\u8bf4\u4e86\u5427\uff01\u4e0d\u7528\u541e\u541e\u5410\u5410\uff0c\u6709\u5931\u4e08\u592b\u6c14\u5ea6\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u5609\u5b9a\u5c0f\u5730\u65b9\uff0c\u627f\u6c5f\u6e56\u670b\u53cb\u4eec\u62ac\u7231\uff0c\u7ed9\u7a0b\u67d0\u8d50\u4e0a\u4e00\u5904\u7acb\u8db3\u4e4b\u5730\u3002\u201d\u76ee\u5149\u8f6c\u6ce8\u5230\u8463\u65b9\u7684\u8eab\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u542c\u8fd9\u4f4d\u8463\u5144\u8bf4\uff0c\u8d35\u5144\u5f1f\u770b\u4e0a\u4e86\u5609\u5b9a\uff0c\u8981\u5728\u5609\u5b9a\u5730\u9762\u4e0a\u4f1a\u9f50\uff0c\u60f3\u6765\u5fc5\u7136\u6709\u5927\u8ba1\u5212\u5927\u624b\u7b14\u4e86\u3002\u201d \u3000\u3000\u6210\u7384\u901a\u51b7\u51b7\u5730\u63a5\u9053\uff1a\u201c\u7a0b\u5e84\u4e3b\u662f\u7687\u4e0a\u7684\u547d\u5b98\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u662f\u3002\u201d \u3000\u3000\u6210\u7384\u901a\u9053\uff1a\u201c\u7a0b\u5e84\u4e3b\u548c\u6211\u4eec\u4e94\u5144\u5f1f\u4e00\u6837\uff0c\u4e0d\u8fc7\u662f\u4e00\u4ecb\u8349\u6c11\uff0c\u4e0d\u77e5\u4e3a\u4f55\u8981\u95ee\u6211\u4eec\u5144\u5f1f\u7684\u4e8b\uff1f\u518d\u8bf4\uff0c\u5929\u6daf\u6d77\u89d2\uff0c\u5251\u95e8\u4e94\u9b3c\u4f55\u5904\u4e0d\u80fd\u53bb\uff0c\u7a0b\u5144\u5f1f\u8fd9\u70b9\u9053\u884c\uff0c\u60f3\u5e72\u6d89\u6211\u4eec\u5144\u5f1f\u7684\u4e8b\uff0c\u4e5f\u672a\u514d\u662f\u4e0d\u81ea\u91cf\u529b\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5b50\u785d\u9053\uff1a\u201c\u5e72\u6d89\u5012\u662f\u4e0d\u6562\uff0c\u4e0d\u8fc7\uff0c\u7a0b\u67d0\u5728\u5609\u5b9a\u571f\u751f\u571f\u957f\uff0c\u5609\u5b9a\u5730\u9762\u4e4b\u4eba\uff0c\u4e0d\u662f\u6cbe\u4eb2\u5c31\u662f\u5e26\u6545\uff0c\u5144\u5f1f\u4e0d\u613f\u770b\u5230\u6545\u5c45\u4e4b\u4e61\uff0c\u5806\u5c38\u5982\u5c71\uff0c\u8840\u6d41\u6f02\u6775\u3002\u201d \u3000\u3000\u6210\u7384\u901a\u7eb5\u58f0\u5927\u7b11\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u7a0b\u5b50\u671b\u554a\uff01\u7a0b\u5b50\u671b\uff0c\u5251\u95e8\u4e94\u9b3c\u5982\u60f3\u5728\u5609\u5b9a\u5730\u9762\u4e0a\uff0c\u52a8\u70b9\u624b\u811a\uff0c\u4f60\u53c8\u80fd\u5982\u4f55\u5462\uff1f\u8fd9\u51e0\u53e5\u8bdd\u672a\u514d\u592a\u9ad8\u62ac\u81ea\u5df1\u4e86\u5427\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u7f13\u7f13\u4ed6\u8bf4\u9053\uff1a\u201c\u7a0b\u67d0\u4eba\u5728\u6c5f\u6e56\u56fa\u662f\u540d\u4e0d\u7ecf\u4f20\uff0c\u4f46\u6b66\u6797\u5374\u81ea\u6709\u4e3b\u6301\u516c\u9053\u7684\u4eba\u3002\u201d \u3000\u3000\u6210\u7384\u901a\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u8c01\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u8fdc\u7684\u4e0d\u8bf4\uff0c\u5728\u4e94\u4f4d\u4e4b\u524d\uff0c\u5c31\u6709\u4e09\u4f4d\u5927\u4ec1\u5927\u4e49\uff0c\u9664\u66b4\u5b89\u826f\u7684\u5927\u4fa0\u3002\u201d \u3000\u3000\u6210\u7384\u901a\u9053\uff1a\u201c\u4f60\u662f\u6307\u4e2d\u5dde\u4e09\u4fa0\u4e86\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e2d\u5dde\u4e09\u4fa0\u534a\u4e16\u4e8b\u8ff9\uff0c\u65a9\u597d\u9664\u6076\uff0c\u7eff\u6797\u5bb5\u5c0f\uff0c\u95fb\u540d\u4e27\u80c6\uff0c\u4f60\u4eec\u51e0\u4f4d\u5144\u5f1f\u60f3\u5fc5\u65e9\u6709\u6240\u95fb\u4e86\u3002\u201d \u3000\u3000\u6210\u7384\u901a\u7709\u5934\u4e00\u76b1\uff0c\u51b7\u7136\u8bf4\u9053\uff1a\u201c\u7a0b\u5e84\u4e3b\u81ea\u5df1\u5f80\u8138\u4e0a\u8d34\u91d1\uff0c\u5c31\u51ed\u4f60\u8fd9\u70b9\u540d\u671b\uff0c\u4e5f\u80fd\u4f7f\u4e2d\u5dde\u4e09\u4fa0\u4e3a\u4f60\u6548\u547d\u5417\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u6de1\u6de1\u4e00\u7b11\u3001\u63a5\u9053\uff1a\u201c\u4e2d\u5dde\u4e09\u4fa0\u4ece\u4e0d\u4e3a\u4eba\u6548\u547d\uff0c\u4e5f\u4ece\u4e0d\u53d7\u4eba\u5de7\u8a00\u8499\u6b3a\uff0c\u6211\u4eec\u884c\u4e8b\u5fc3\u4e4b\u6240\u5b89\uff0c\u6210\u8d25\u5411\u6240\u4e0d\u8ba1\u3002\u201d \u3000\u3000\u6210\u7384\u901a\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u548c\u7a0b\u5e84\u4e3b\u4ea4\u8c08\u4e4b\u8a00\uff0c\u90d1\u5144\u5f1f\u90fd\u542c\u5230\u4e86\u5417\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u542c\u5230\u4e86\u3002\u201d \u3000\u3000\u826f\u4e45\u4e4b\u540e\uff0c\u6210\u7384\u901a\u624d\u7f13\u7f13\u63a5\u9053\uff1a\u201c\u6211\u4eec\u5144\u5f1f\u5728\u5609\u5b9a\u805a\u4f1a\uff0c\u77e5\u7a0b\u5e84\u4e3b\u4f55\u6d89\u6cb3\u5e72\uff1f\u4ed6\u7adf\u56fe\u4ece\u4e2d\u6a2a\u52a0\u5e72\u6270\uff0c\u90d1\u5144\u5f1f\u5bf9\u6b64\uff0c\u4e0d\u77e5\u6709\u4f55\u9ad8\u89c1\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u4e4b\u610f\uff0c\u53cc\u65b9\u6700\u597d\u80fd\u5404\u9000\u4e00\u6b65\uff0c\u4e0d\u8981\u4e3a\u6b64\u95f9\u7ffb\u3002\u201d \u3000\u3000\u6210\u7384\u901a\u9053\uff1a\u201c\u6b64\u4e8b\u53ea\u6015\u5f88\u96be\u4e24\u5168\uff0c\u6211\u4eec\u5144\u5f1f\u65e2\u7136\u9009\u4e0a\u4e86\u5609\u5b9a\uff0c\u81ea\u65e0\u66f4\u6539\u4e4b\u7406\uff0c\u5982\u662f\u7a0b\u5b50\u671b\u4ece\u4e2d\u4f5c\u6897\uff0c\u90a3\u53ea\u6709\u5404\u51ed\u6b66\u529f\uff0c\u4e00\u51b3\u80dc\u8d1f\u4e86\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u5fae\u5fae\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u7792\u90d1\u5144\u8bf4\uff0c\u7a0b\u5b50\u671b\u548c\u5ddd\u5357\u9053\u4e0a\u7684\u6b66\u6797\u540c\u9053\uff0c\u4e0d\u8bba\u6709\u597d\u591a\u4eba\u624b\uff0c\u4e5f\u4e0d\u653e\u5728\u5144\u5f1f\u7684\u5fc3\u4e0a\uff0c\u4f46\u5144\u5f1f\u5374\u4e0d\u5e0c\u671b\u4e2d\u5dde\u4e09\u4fa0\u4e5f\u5377\u5165\u8fd9\u573a\u662f\u975e\u4e4b\u4e2d\uff0c\u6211\u6210\u7384\u901a\u5bf9\u670b\u53cb\u4e00\u5411\u662f\u76f4\u6765\u76f4\u5f80\uff0c\u6709\u4e00\u53e5\u5c31\u8bf4\u4e00\u53e5\uff0c\u90d1\u5144\u5982\u679c\u80af\u8d4f\u7ed9\u4e00\u4e2a\u8584\u9762\uff0c\u65e9\u65e5\u79bb\u5f00\u5609\u5b9a\uff0c\u8fd9\u4efd\u4ea4\u60c5\uff0c\u5251\u95e8\u4e94\u9b3c\u548c\u5b97\u5821\u4e3b\u90fd\u4f1a\u9ed8\u8bb0\u4e8e\u5fc3\uff0c\u65e5\u540e\u5fc5\u6709\u4e00\u62a5\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u8f7b\u8f7b\u5730\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6210\u5144\u5982\u82e5\u53ea\u662f\u5728\u5609\u5b9a\u5730\u9762\u4e0a\u805a\u4f1a\u4e00\u756a\uff0c\u6211\u60f3\u5b50\u671b\u5144\u5f1f\u51b3\u4e0d\u4f1a\u6a2a\u52a0\u5e72\u6d89\uff0c\u9664\u975e\u2026\u2026\u201d \u3000\u3000\u6210\u7384\u901a\u9053\uff1a\u201c\u5c31\u7b97\u6211\u4eec\u5144\u5f1f\u522b\u6709\u6240\u56fe\uff0c\u7a0b\u5b50\u671b\u53c8\u4f55\u80fd\u5e72\u6270\u5462\u2026\u2026\u201d\u54c8\u54c8\u4e00\u7b11\uff0c\u63a5\u9053\uff1a \u3000\u3000\u201c\u9664\u975e\u90d1\u5144\u7b54\u5141\u7a0b\u5b50\u671b\u66ff\u4ed6\u6491\u8170\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u8138\u8272\u5fae\u53d8\u9053\uff1a\u201c\u6210\u5144\u8a00\u91cd\u4e86\uff0c\u6c5f\u6e56\u4e0a\u7d20\u6709\u89c4\u77e9\uff0c\u6b63\u50cf\u4f60\u4eec\u4e94\u4e49\u5730\u76d8\uff0c\u4e0d\u8bb8\u522b\u4eba\u4fb5\u5bb3\u4e00\u822c\u3002\u201d \u3000\u3000\u6210\u7384\u901a\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u770b\u8d77\u6765\u8d35\u5144\u5f1f\u63d2\u624b\u6b64\u4e8b\uff0c\u90a3\u662f\u65e0\u6cd5\u66f4\u6539\u7684\u4e86\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u5982\u82e5\u6210\u5144\u53ea\u662f\u6e38\u5bb4\u73a9\u4e50\uff0c\u5144\u5f1f\u60f3\u90a3\u5b50\u671b\u5144\u4e0d\u4f46\u4e0d\u4f1a\u5e72\u6270\uff0c\u4e14\u5fc5\u5c06\u6781\u613f\u610f\u4e00\u5c3d\u5730\u4e3b\u4e4b\u8c0a\u3002\u201d \u3000\u3000\u6210\u7384\u901a\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u4eec\u5251\u95e8\u4e94\u9b3c\u5728\u4f60\u90d1\u5144\u773c\u4e2d\uff0c\u4e0d\u5982\u4e00\u4e2a\u7a0b\u5b50\u671b\uff0c\u5b9e\u53eb\u5144\u5f1f\u60f3\u4e0d\u51fa\u539f\u56e0\u4f55\u5728\u5462\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u795e\u8272\u8083\u7136\u4ed6\u8bf4\u9053\uff1a\u201c\u6709\u4e00\u70b9\u53ea\u6015\u662f\u6210\u5144\u5f53\u771f\u672a\u66fe\u4f1a\u60f3\u5230\u3002\u201d \u3000\u3000\u6210\u7384\u901a\u9053\uff1a\u201c\u613f\u542c\u9ad8\u8bba\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u6b66\u6797\u6b63\u6c14\u3002\u201d \u3000\u3000\u6210\u7384\u901a\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u5251\u95e8\u4e94\u9b3c\u662f\u6b66\u6797\u4e2d\u90aa\u6076\u4e4b\u5f92\u4e86\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u5144\u5f1f\u5c31\u4e8b\u8bba\u4e8b\uff0c\u8d35\u5144\u5f1f\u5728\u6c5f\u6e56\u4e0a\u6240\u4f5c\u6240\u4e3a\uff0c\u7684\u786e\u662f\u65e0\u53eb\u4eba\u606d\u7ef4\u3002\u201d \u3000\u3000\u6210\u7384\u901a\u4ef0\u5929\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u9053\uff1a\u201c\u6709\u4e00\u4ef6\u4e8b\u53ea\u6015\u90d1\u5144\u63a8\u65ad\u6709\u8bef\uff0c\u8fd9\u5c31\u662f\u5251\u95e8\u4e94\u9b3c\uff0c\u53ea\u662f\u662f\u4e0d\u613f\u548c\u4e2d\u5dde\u4e09\u4fa0\u52a8\u624b\uff0c\u4f46\u5374\u5e76\u975e\u662f\u5bb3\u6015\u4f60\u4eec\u4e2d\u5dde\u4e09\u4fa0\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u6210\u5144\u8a00\u91cd\u4e86\uff0c\u54b1\u4eec\u7d20\u5c11\u6765\u5f80\uff0c\u4e5f\u8c08\u4e0d\u5230\u54ea\u4e2a\u6015\u54ea\u4e2a\u4e86\uff0c\u5251\u95e8\u4e94\u9b3c\u4e0d\u6015\u4e2d\u5dde\u4e09\u4fa0\uff0c\u4f46\u4e2d\u5dde\u4e09\u4fa0\u4e00\u6837\u4e0d\u6015\u5251\u95e8\u4e94\u9b3c\u3002\u201d \u3000\u3000\u6210\u7384\u901a\u79bb\u5f00\u5e2d\u4f4d\uff0c\u76ee\u5149\u4e00\u63a0\u56db\u9b3c\u548c\u5b97\u58eb\u4e49\u9053\uff1a\u201c\u54b1\u4eec\u8d70\u5427\uff01\u201d\u5927\u6b65\u5411\u5916\u884c\u53bb\u3002\u56db\u9b3c\u548c\u91d1\u67aa\u5b97\u58eb\u4e49\u9c7c\u8d2f\u968f\u6210\u7384\u901a\u7684\u8eab\u540e\u800c\u884c\u3002 \u3000\u3000\u4e2d\u5dde\u4e09\u4fa0\u5374\u7aef\u5750\u5728\u5e2d\u4f4d\u4e0a\uff0c\u65e2\u672a\u51fa\u8a00\u62e6\u963b\uff0c\u4e5f\u4e0d\u8d77\u8eab\u76f8\u9001\u3002 \u3000\u3000\u6210\u7384\u901a\u8d70\u51fa\u5927\u5385\u4e4b\u540e\uff0c\u7a81\u7136\u8f6c\u8fc7\u8eab\u5b50\uff0c\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u4e2d\u5dde\u4e09\u4fa0\uff0c\u5728\u5609\u5b9a\u8fd8\u6709\u51e0\u65e5\u505c\u7559\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u660e\u5929\u65e5\u843d\u4e4b\u524d\uff0c\u5b9a\u5f97\u79bb\u6b64\u3002\u201d \u3000\u3000\u6210\u7384\u901a\u9053\uff1a\u201c\u6211\u7b49\u4e5f\u4e0d\u613f\u591a\u803d\u8bef\u8d35\u5144\u5f1f\u7684\u65f6\u95f4\uff0c\u4eca\u591c\u4e09\u66f4\u4e4b\u524d\uff0c\u6211\u7b49\u518d\u6765\u62dc\u8bbf\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u6211\u4eec\u606d\u5019\u5927\u9a7e\u3002 \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u5982\u662f\u8bf8\u4f4d\u884c\u5b88\u4fe1\u7ea6\uff0c\u660e\u5929\u6211\u5144\u5f1f\u79bb\u5f00\u6b64\u5730\u65f6\uff0c\u53ea\u597d\u5e26\u7740\u5b97\u4e16\u5144\u4e00\u8d77\u8d70\u4e86\u3002\u201d \u3000\u3000\u5b97\u58eb\u4e49\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u4eca\u5bb5\u518d\u4f1a\u4e4b\u65f6\uff0c\u5728\u4e0b\u5b9a\u7136\u5148\u5411\u65b9\u4e8c\u4fa0\u8ba8\u6559\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u597d\uff01\u5728\u4e0b\u4e5f\u60f3\u89c1\u8bc6\u89c1\u8bc6\u5b97\u5821\u4e3b\u7684\u91d1\u67aa\u62db\u6570\u3002\u201d \u3000\u3000\u6210\u7384\u7231\u4e00\u6325\u624b\uff0c\u9053\uff1a\u201c\u7a0b\u5e84\u4e3b\uff0c\u6b3e\u5bb4\u60c5\u5df2\u9886\uff0c\u5c31\u6b64\u522b\u8fc7\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u4e00\u62b1\u62f3\u9053\uff1a\u201c\u8bf8\u4f4d\u6162\u8d70\uff0c\u7a0b\u67d0\u4e0d\u9001\u4e86\u3002\u201d \u3000\u3000\u6210\u7384\u901a\u9053\uff1a\u201d\u4eca\u5bb5\u91cd\u805a\u4e4b\u65f6\uff0c\u5e0c\u671b\u4f60\u7a0b\u5e84\u4e3b\u80fd\u591f\u5b89\u7136\u65e0\u6059\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u7a0b\u89c1\u4e0d\u7518\u5fc3\u675f\u624b\u5c31\u622e\uff0c\u81f3\u4e8e\u80fd\u5426\u53d6\u7a0b\u67d0\u4e4b\u547d\uff0c\u90a3\u8981\u770b\u8bf8\u4f4d\u7684\u624b\u6bb5\u4e86\u3002\u201d \u3000\u3000\u6210\u7384\u901a\u76ee\u5149\u6ce8\u5230\u90a3\u4ecd\u7136\u72ec\u5750\u5728\u5e2d\u4f4d\u7684\u5b97\u8d35\u4e00\u773c\uff0c\u4e00\u76b1\u7709\u5934\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\uff0c\u4e3a\u4f55\u4e0d\u8d70\uff0c\u4e00\u4e2a\u4eba\u5750\u5728\u90a3\u91cc\u3002\u201d \u3000\u3000\u539f\u6765\u6210\u7384\u901a\u5728\u6c14\u6012\u4e4b\u4e0b\uff0c\u79bb\u4f4d\u800c\u8d77\uff0c\u5927\u6b65\u51fa\u5385\uff0c\u5fd8\u8bb0\u4e86\u5b97\uff0c\u8d35\uff0c\u95fb\u5f97\u65b9\u5929\u6210\u4e4b\u8a00\uff0c\u624d\u60f3\u4e86\u8d77\u6765\uff0c\u8f6c\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u5b97\u8d35\u4ecd\u7136\u7aef\u7aef\u6b63\u6b63\u7684\u5750\u5728\u539f\u4f4d\u4e0a\uff0c\u5fc3\u4e2d\u53c8\u662f\u607c\u6012\uff0c\u53c8\u662f\u597d\u7b11\u3002\u5176\u4f59\u56db\u9b3c\uff0c\u4e5f\u672a\u60f3\u5230\u5b97\u8d35\u7adf\u4e0d\u80af\u8ddf\u5728\u8eab\u540e\u540c\u884c\uff0c\u9f50\u9f50\u4e3a\u4e4b\u6115\u7136\u3002 \u3000\u3000\u5b97\u58eb\u4e49\u66f4\u662f\u607c\u6012\uff0c\u5389\u58f0\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u8d8a\u957f\u8d8a\u5446\u4e86\uff0c\u8001\u5b50\u65e0\u5fb7\u517b\u51fa\u4e86\u4f60\u8fd9\u513f\u6765\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u4ee4\u90ce\u4e0d\u80af\u548c\u4f60\u4eec\u540c\u884c\uff0c\u6b63\u662f\u4ed6\u7684\u806a\u660e\u4e4b\u5904\u3002\u201d \u3000\u3000\u5b97\u58eb\u4e49\u6115\u7136\u9053\uff1a\u201c\u8fd9\u8bdd\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u5b97\u8d35\u7f13\u7f13\u5730\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u5b69\u513f\u773c\u4e0b\u5267\u6bd2\uff0c\u4e00\u4e2a\u65f6\u8fb0\u4ee5\u5185\uff0c\u6bd2\u6027\u53d1\u4f5c\uff0c\u5982\u82e5\u5b69\u513f\u548c\u8bf8\u4f4d\u53d4\u53d4\u4f2f\u4f2f\u540c\u884c\uff0c\u53ea\u6015\u96be\u6709\u6d3b\u547d\u4e4b\u671b\u3002\u201d \u3000\u3000\u6210\u7384\u901a\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u6709\u8fd9\u7b49\u4e8b\uff1f\u201d \u3000\u3000\u76ee\u5149\u8f6c\u6ce8\u5728\u90d1\u5927\u521a\u7684\u8138\u4e0a\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u4eec\u4e2d\u5dde\u4e09\u4fa0\u7684\u624b\u6bb5\u4e86\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u7f13\u7f13\u4ed6\u8bf4\u9053\uff1a\u201c\u6210\u5144\uff0c\u76f8\u4fe1\u5417\uff1f\u201d \u3000\u3000\u6210\u7384\u901a\u9053\uff1a\u201c\u4e8b\u5b9e\u6446\u5728\u773c\u524d\uff0c\u8981\u8001\u592b\u4e0d\u76f8\u4fe1\uff0c\u90a3\u5b9e\u5728\u5f88\u96be\u4e86\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4e2d\u5dde\u4e09\u4fa0\u7d20\u6765\u4e0d\u7528\u6bd2\uff0c\u6210\u53f2\u65e9\u5df2\u77e5\u6653\u4e86\u3002\u201d \u3000\u3000\u6210\u7384\u901a\u9053\uff1a\u201c\u90a3\u662f\u7a0b\u5e84\u4e3b\u7684\u6bd2\u4e86\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u7528\u6bd2\u4e00\u4e8b\uff0c\u7a0b\u67d0\u8fd8\u4e0d\u613f\u4e3a\uff0c\u4e5f\u4e0d\u5c51\u4e3a\u3002\u201d \u3000\u3000\u6210\u7384\u901a\u5927\u58f0\u559d\u9053\uff1a\u201c\u8d35\u513f\uff0c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\u554a\uff01\u201d \u3000\u3000\u5b97\u8d35\u9053\uff1a\u201c\u90a3\u9001\u6211\u8fdb\u5165\u5385\u4e2d\u4e4b\u4eba\u5728\u5e26\u6211\u6765\u6b64\u4e4b\u524d\uff0c\u5148\u903c\u6211\u559d\u4e0b\u4e00\u676f\u6bd2\u9152\uff0c\u5e76\u544a\u8bc9\u6211\u90a3\u9152\u4e2d\u4e4b\u6bd2\uff0c\u5947\u70c8\u65e0\u6bd4\uff0c\u9664\u4e86\u4ed6\u8eab\u4e0a\u5e26\u7684\u89e3\u6bd2\u836f\u4e4b\u5916\uff0c\u518d\u5143\u4ed6\u4eba\u80fd\u591f\u89e3\u5f97\uff0c\u800c\u4e14\u6bd2\u6027\u53d1\u4f5c\u751a\u5feb\uff0c\u5728\u6bd2\u6027\u4e0d\u89e3\u4e4b\u524d\uff0c\u4e0d\u80fd\u79bb\u5f00\u7a0b\u5bb6\u5e84\u4e00\u6b65\uff0c\u5426\u5219\u662f\u53ea\u6709\u6b7b\u8def\u4e00\u6761\u4e86\u3002\u201d \u3000\u3000\u6210\u7384\u901a\u70b9\u70b9\u5934\uff0c\u51b7\u7136\u8bf4\u9053\uff1a\u201c\u90d1\u5927\u521a\u3001\u7a0b\u5b50\u671b\u4f60\u4eec\u90fd\u542c\u5230\u4e86\uff0c\u96be\u9053\u662f\u4ed6\u8bf4\u8c0e\u4e0d\u6210\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u4ee4\u90ce\u8bf4\u7684\u90fd\u662f\u5b9e\u8bdd\u2026\u2026\u201d \u3000\u3000\u6210\u7384\u901a\u63a5\u9053\uff1a\u201c\u90a3\u662f\u4f60\u7a0b\u5b50\u671b\u4e0b\u7684\u6bd2\u4e86\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u4f46\u90a3\u53ea\u662f\u4e00\u676f\u9152\uff0c\u9152\u4e2d\u5e76\u65e0\u6bd2\u836f\uff0c\u5982\u662f\u5b97\u4e16\u5144\u8c6a\u58ee\u4e00\u4e9b\uff0c\u4e0d\u754f\u6b7b\u4ea1\uff0c\u6b64\u523b\u4ed6\u5df2\u7ecf\u968f\u5404\u4f4d\u884c\u51fa\u5821\u5916\u4e86\u3002\u201d \u3000\u3000\u5b97\u58eb\u4e49\u54ac\u7259\u5207\u9f7f\uff0c\u9053\uff1a\u201c\u7a0b\u5b50\u671b\uff0c\u5b97\u58eb\u4e49\u4e0d\u628a\u4f60\u788e\u5c38\u4e07\u6bb5\uff0c\u8a93\u4e0d\u4e3a\u4eba\uff01\u201d \u3000\u3000\u65b9\u5929\u6210\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u5b97\u5821\u4e3b\u4e0d\u7528\u9519\u602a\u7a0b\u5e84\u4e3b\uff0c\u8fd9\u6cd5\u5b50\u662f\u5728\u4e0b\u60f3\u7684\uff0c\u65e8\u5728\u6d4b\u91cf\u4e00\u4e0b\u4ee4\u90ce\u7684\u80c6\u6c14\u7f62\u4e86\uff0c\u60f3\u4e0d\u5230\u4ed6\u7adf\u80af\u8a00\u542c\u8ba1\u4ece\u3002\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8f7b\u63cf\u6de1\u5199\u8ba5\u8bbd\u4e4b\u8a00\uff0c\u53ea\u542c\u5f97\u5b97\u58eb\u4e49\u8138\u4e0a\u706b\u70ed\uff0c\u4f46\u53c8\u65e0\u6cd5\u53d1\u4f5c\u3002\u53ea\u89c1\u5750\u5728\u5ea7\u4e0a\u7684\u5b97\u8d35\u3001\u7a81\u7136\u4e00\u8dc3\u800c\u8d77\uff0c\u76f4\u5411\u5385\u5916\u51b2\u53bb\u3002 \u3000\u3000\u77f3\u4fca\u4e00\u6309\u684c\u9762\uff0c\u4eba\u5f71\u4e00\u95ea\uff0c\u62e6\u4f4f\u4e86\u5b97\u8d35\u7684\u53bb\u8def\uff0c\u9053\uff1a\u201c\u4f60\u5982\u60f3\u591a\u6d3b\u4e00\u4e9b\u65f6\u95f4\uff0c\u90a3\u5c31\u4e0d\u7528\u4f5c\u9003\u51fa\u6b64\u5730\u7684\u6253\u7b97\u3002\u201d \u3000\u3000\u5b97\u8d35\u77e5\u4ed6\u6b66\u529f\u9ad8\u5f3a\uff0c\u81ea\u5df1\u96be\u662f\u654c\u624b\uff0c\u53ea\u597d\u505c\u4e0b\u4e0d\u52a8\u3002 \u3000\u3000\u5b97\u58eb\u4e49\u8868\u9762\u4e0a\u867d\u7136\u5bf9\u90a3\u5b97\u8d35\u53f1\u8d23\u58f0\uff0c\u5176\u5b9e\u7236\u5b50\u8fde\u5fc3\uff0c\u65e9\u5df2\u5fc3\u75bc\u5982\u7ede\uff0c\u770b\u77f3\u4fca\u6321\u4f4f\u4e86\u5b97\u8d35\u7684\u53bb\u8def\uff0c\u7acb\u65f6\u51b7\u7b11\u4e00\u58f0\uff0c\u4eba\u6b65\u5411\u5385\u4e2d\u51b2\u6765\u3002 \u3000\u3000\u65b9\u5929\u6210\u7eb5\u8eab\u800c\u8d77\uff0c\u62e6\u5728\u5385\u95e8\u53e3\u5904\uff0c\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u5b97\u5821\u4e3b\u6025\u4ec0\u4e48\u5462\uff0c\u4eca\u591c\u4e09\u66f4\u4e4b\u7ea6\uff0c\u53ea\u8981\u4f60\u4eec\u80fd\u80dc\uff0c\u8fde\u6211\u4eec\u4e2d\u5dde\u4e09\u4fa0\u4e4b\u547d\uff0c\u90fd\u63e1\u5728\u4f60\u4eec\u624b\u4e2d\uff0c\u6551\u51fa\u4ee4\u90ce\uff0c\u81ea\u7136\u662f\u987a\u7406\u5e94\u7ae0\u4e86\u3002\u201d \u3000\u3000\u5b97\u58eb\u4e49\u6012\u9053\uff1a\u201c\u6211\u4eec\u4e09\u66f4\u6765\u6b64\uff0c\u51b3\u4e0d\u4f1a\u5931\u7ea6\uff0c\u73b0\u5728\u653e\u4e86\u72ac\u5b50\uff0c\u6709\u4f55\u4e0d\u53ef\uff01\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u5bb3\u4eba\u4e4b\u5fc3\u4e0d\u80fd\u6709\uff0c\u9632\u4eba\u4e4b\u5fc3\u4e0d\u53ef\u65e0\uff0c\u4e07\u4e00\u8bf8\u4f4d\uff0c\u5931\u7ea6\u4e0d\u6765\uff0c\u6211\u7b49\u8fd8\u6709\u4eba\u8d28\uff01\u201d \u3000\u3000\u5b97\u58eb\u4e49\u5f3a\u6309\u4e0b\u5fc3\u4e2d\u7684\u60b2\u75db\u6012\u706b\uff0c\u671b\u4e86\u5b97\u8d35\u4e24\u773c\uff0c\u624d\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u65b9\u65e0\u6210\u76ee\u7779\u51e0\u4eba\u5927\u6b65\u800c\u53bb\uff0c\u76f4\u5f85\u80cc\u5f71\u6d88\u592b\uff0c\u624d\u56de\u5230\u5385\u4e2d\u3002 \u3000\u3000\u77f3\u4fca\u62ac\u793a\u70b9\u4e86\u5b97\u70ad\u4e24\u5904\u7a74\u9053\u7b11\u9053\uff1a\u201c\u5b97\u516c\u5b50\uff0c\u6682\u8bf7\u59d4\u66f2\uff0c\u4eca\u591c\u4e09\u66f4\u65f6\u5206\uff0c\u4f60\u90a3\u4e49\u7236\u548c\u4ee4\u5c0a\u90fd\u5c06\u5230\u6b64\u6551\u4f60\uff0c\u5e0c\u671b\u4f60\u80fd\u5b89\u5206\u5b88\u5df2\uff0c\u4f60\u5982\u4e71\u52a8\uff0c\u53d7\u5230\u4f24\u5bb3\uff0c\u90a3\u5c31\u4e0d\u80fd\u602a\u5728\u4e0b\u4e86\u3002\u201d \u3000\u3000\u5b97\u8d35\u867d\u7136\u542c\u5f97\u89c1\uff0c\u53e3\u5374\u662f\u672a\u7b54\u4e00\u8a00\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u4e00\u62db\u624b\uff0c\u4e00\u4e2a\u6b66\u5e08\u884c\u4e86\u8fc7\u6765\uff0c\u5e26\u8d70\u4e86\u5b97\u8d35\u3002 \u3000\u3000\u90d1\u5927\u521a\u671b\u4e86\u65b9\u5929\u6210\u548c\u77f3\u4fca\u4e00\u773c\uff0c\u9053\uff1a\u201c\u6210\u7384\u901a\u542b\u5fff\u800c\u53bb\uff0c\u4eca\u591c\u8fd9\u4e00\u573a\u6076\u6597\uff0c\u5fc5\u7136\u662f\u5341\u5206\u51f6\u9669\uff0c\u54b1\u4eec\u4e0d\u80fd\u6709\u4e1d\u6beb\u5927\u610f\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u5fae\u5fae\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u6b64\u523b\u8d77\u5230\u5165\u591c\u524d\uff0c\u5341\u5206\u5e73\u9759\uff0c\u54b1\u4eec\u8981\u73cd\u60dc\u8fd9\u4e2a\u5b9d\u8d35\u65f6\u5149\uff0c\u597d\u597d\u5730\u4f11\u606f\u4e00\u4e0b\u7cbe\u795e\u3002\u201d \u3000\u3000\u626c\u5b50\u671b\u9053\uff1a\u201c\u65b9\u592b\u4eba\u3001\u516c\u5b50\u90fd\u7559\u5728\u7a0b\u5bb6\u5e84\u4e2d\uff0c\u53ea\u6015\u96be\u514d\u53d7\u60ca\uff0c\u5728\u4e0b\u884c\u5df2\u6697\u4e2d\u6d3e\u4eba\u5907\u4e86\u4e00\u8258\u5927\u8239\uff0c\u628a\u5979\u4eec\u9001\u4e0a\u8239\u53bb\uff0c\u6cca\u4e8e\u6c5f\u5fc3\u2026\u2026\u201d \u3000\u3000\u65b9\u5929\u6210\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u8fd9\u5012\u4e0d\u52b3\u7a0b\u5e84\u4e3b\u8d39\u5fc3\uff0c\u8d31\u5185\u4ea6\u4e60\u8fc7\u6b66\u529f\uff0c\u5fc5\u8981\u65f6\uff0c\u5979\u8fd8\u53ef\u52a9\u54b1\u4eec\u4e00\u81c2\u4e4b\u529b\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u52b3\u8bf7\u7a0b\u5e84\u4e3b\u4f20\u4e0b\u53bb\uff0c\u8981\u5979\u4eec\u5c3d\u91cf\u5229\u7528\u8fd9\u534a\u65e5\u65f6\u5149\u4f11\u606f\uff0c\u5929\u8272\u5165\u591c\u4e4b\u540e\uff0c\u5c31\u8981\u5c0f\u5fc3\u6212\u5907\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u597d\uff0c\u5144\u5f1f\u5c31\u53bb\u3002\u201d\u8d77\u8eab\u79bb\u5f00\u4e86\u5927\u5385\u3002 \u3000\u3000\u534a\u65e5\u65e0\u4e8b\uff0c\u5165\u591c\u4e4b\u65f6\uff0c\u7a0b\u5bb6\u5e84\u7acb\u523b\u7d27\u5f20\u8d77\u6765\uff0c\u5341\u540d\u6b66\u5e08\u548c\u4e8c\u5341\u540d\u5f29\u7bad\u624b\uff0c\u5404\u5c31\u5404\u4f4d\uff0c\u56db\u5934\u85cf\u72ac\uff0c\u4e5f\u89e3\u53bb\u4e86\u94c1\u94fe\uff0c\u7ed5\u7740\u5e84\u9662\u6e38\u8d70\uff0c\u6574\u4e2a\u7a0b\u5bb6\u5e84\u7684\u58c1\u89d2\u5904\uff0c\u90fd\u653e\u7740\u76db\u6ee1\u6e05\u6c34\u7684\u6728\u6876\u3001\u74e6\u76c6\uff0c\u51c6\u5907\u4f5c\u6551\u706b\u4e4b\u7528\u3002 \u3000\u3000\u9664\u4e86\u51c6\u5907\u5bf9\u654c\u7684\u6b66\u5e08\u548c\u5404\u5b88\u65b9\u4f4d\u7684\u5f13\u7bad\u624b\u5916\uff0c\u51e1\u7559\u5728\u7a0b\u5e9c\u4e2d\u7684\u4eba\uff0c\u5404\u6709\u804c\u53f8\uff0c\u8fd9\u4e9b\u4eba\u5927\u90fd\u5728\u7a0b\u5e9c\u4e2d\u5de5\u4f5c\u6570\u5e74\u4e4b\u4e45\uff0c\u5bf9\u6545\u4e3b\u8fd9\u5ea7\u5b85\u9662\uff0c\u90fd\u6709\u7740\u6df1\u6df1\u7684\u4f9d\u604b\uff0c\u867d\u77e5\u591c\u6765\u6076\u6597\uff0c\u51f6\u6076\u65e0\u6bd4\uff0c\u4f46\u5374\u65e0\u4eba\u754f\u4ff1\u3001\u9003\u907f\u3002 \u3000\u3000\u4e1c\u8033\u8001\u4eba\uff0c\u7531\u7a0b\u5e9c\u4e2d\u4e24\u4e2a\u8fd1\u516d\u65ec\u7684\u4ec6\u4eba\u548c\u53a8\u5e08\uff0c\u966a\u540c\u8eb2\u5728\u4e00\u5e84\u575a\u7262\u7684\u4ed3\u5e93\u4e2d\uff0c\u81f3\u4e8e\u5a62\u5973\u3001\u8001\u5a62\uff0c\u90fd\u968f\u7a0b\u592b\u4eba\u7b49\u8eb2\u5165\u4e86\u5730\u7a96\u4e4b\u4e2d\u3002 \u3000\u3000\u5feb\u817f\u5f20\u4e09\u4e00\u8eab\u52b2\u88c5\uff0c\u80cc\u7740\u5355\u5200\uff0c\u817f\u4e0a\u8fd8\u6302\u63d2\u7740\u4e24\u628a\u624b\u53c9\u5b50\uff0c\u5de6\u80c1\u95f4\u8fd8\u7740\u4e00\u888b\u6697\u5668\uff0c\u4ea6\u6b65\u4ea6\u8d8b\u7684\u968f\u5728\u7a0b\u5b50\u671b\u7684\u8eab\u540e\u3002 \u3000\u3000\u4e2d\u5dde\u4e09\u4fa0\u5fc3\u4e2d\u4e5f\u77e5\u9053\u4eca\u5bb5\u4e4b\u6218\uff0c\u654c\u52bf\u6781\u5f3a\uff0c\u4e5f\u90fd\u5404\u81ea\u5e26\u7740\u987a\u624b\u5175\u5203\u3002 \u3000\u3000\u90d1\u5927\u4fa0\u662f\u4e00\u628a\u9ec4\u91d1\u5200\uff0c\u65b9\u5929\u6210\u80cc\u7740\u957f\u5251\uff0c\u77f3\u4fca\u9664\u4e86\u8170\u4e2d\u4e00\u6761\u5341\u4e09\u8282\u4eae\u94f6\u8f6f\u97ad\u4e4b\u5916\uff0c\u8eab\u4e0a\u8fd8\u5e26\u7740\u4e00\u5c3a\u516d\u5bf8\u957f\u7684\u96c1\u7fce\u77ed\u5200\uff0c\u53f3\u808b\u95f4\u6302\u7740\u9e7f\u76ae\u888b\u5b50\uff0c\u85cf\u7740\u6697\u5668\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u4e5f\u662f\u4e00\u8eab\u9ed1\u8272\u52b2\u88c5\uff0c\u624b\u4e2d\u63d0\u7740\u4e00\u6839\u516b\u5c3a\u4e8c\u5bf8\u957f\u7684\u86c7\u5934\u68cd\uff0c\u80cc\u4e0a\u659c\u80cc\u7740\u4e00\u628a\u5355\u5200\uff0c\u5927\u6b65\u884c\u5165\u5385\u4e2d\uff0c\u671b\u7740\u4e2d\u5dde\u4e09\u4fa0\u8bf4\u9053\uff1a\u201c\u6b64\u523b\u521a\u8fc7\u4e8c\u66f4\u5de6\u53f3\uff0c\u4f9d\u7167\u7ea6\u5b9a\uff0c\u4e94\u9b3c\u8fd8\u6709\u4e00\u4e2a\u65f6\u8fb0\u624d\u6765\uff0c\u4e09\u4f4d\u518d\u5750\u5385\u4e2d\u4f11\u606f\u7247\u523b\uff0c\u5728\u4e0b\u5230\u5e84\u5916\u77a7\u77a7\u53bb\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4e0d\u80fd\u53bb\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u5728\u4e0b\u8fd9\u628a\u5e74\u7eaa\uff0c\u6b7b\u800c\u4f55\u61be\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u63a5\u9053\uff1a\u201c\u8bdd\u4e0d\u662f\u8fd9\u4e48\u8bf4\uff0c\u660e\u77e5\u5176\u6709\u51f6\u9669\uff0c\u4f55\u82e6\u5b9a\u8981\u5192\u9669\uff1f\u201d \u3000\u3000\u8c08\u8bdd\u4e4b\u95f4\u7a81\u7136\u4e24\u58f0\u72d7\u53eb\uff0c\u4f20\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4ed6\u4eec\u6765\u8d74\u7ea6\uff0c\u5b9a\u7136\u4f1a\u63d0\u524d\u5230\u6b64\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u75be\u8f6c\u8eab\u5b50\uff0c\u5927\u6b65\u5411\u5385\u5916\u884c\u53bb\u3002 \u3000\u3000\u90d1\u5927\u521a\u547c\u7684\u4e00\u58f0\uff0c\u6b22\u7184\u4e86\u5385\u4e2d\u706b\u70db\uff0c\u9053\uff1a\u201c\u7a0b\u5144\u4e0d\u53ef\u9020\u6b21\uff0c\u8c0b\u5b9a\u800c\u540e\u52a8\uff0c\u54b1\u4eec\u4e0d\u80fd\u65e0\u8c13\u727a\u7272\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u4e00\u5207\u542c\u90d1\u5927\u4fa0\u7684\u8c03\u5ea6\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u90a3\u5f88\u597d\uff0c\u54b1\u4eec\u5148\u552e\u4e2d\u5230\u524d\u9662\u5e7f\u573a\uff0c\u518d\u5ba1\u5ea6\u5f62\u52bf\uff0c\u5206\u5934\u62d2\u654c\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u628a\u5404\u5904\u7684\u5e03\u7f6e\u90fd\u4ee5\u8fd9\u524d\u9662\u4e3a\u4e3b\uff0c\u5e0c\u671b\u80fd\u628a\u5251\u95e8\u4e94\u9b3c\u903c\u5165\u5e7f\u573a\uff0c\u4f5c\u4e00\u51b3\u6218\u3002 \u3000\u3000\u51e0\u4eba\u521a\u521a\u5385\u5165\u5e7f\u573a\uff0c\u7a81\u7136\u4e00\u58f0\u523a\u8033\u7684\u72d7\u568e\uff0c\u4f20\u5165\u8033\u9645\u3002 \u3000\u3000\u8fd9\u58f0\u97f3\u5f02\u5e38\uff0c\u4efb\u4f55\u4eba\u90fd\u53ef\u8fa8\u51fa\uff0c\u662f\u5de8\u72ac\u6b7b\u524d\u7684\u6012\u53eb\u3002 \u3000\u3000\u90d1\u5927\u521a\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5b50\u671b\u5144\uff0c\u53ec\u56de\u90a3\u4f59\u4e0b\u7684\u85cf\u72ac\u548c\u51e0\u4e2a\u9972\u72ac\u4eba\uff0c\u85cf\u72ac\u867d\u7136\u51f6\u731b\uff0c\u4f46\u5374\u65e0\u6cd5\u5bf9\u4ed8\u5251\u95e8\u4e94\u9b3c\u8fd9\u7b49\u9ad8\u624b\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u5e94\u4e86\u4e00\u58f0\uff0c\u63a2\u624b\u4ece\u6000\u4e2d\u6478\u51fa\u4e00\u5341\u7af9\u54e8\uff0c\u5439\u51fa\u4e00\u957f\u4e00\u77ed\u7684\u54e8\u97f3\u3002\u54e8\u58f0\u843d\u540e\u4e0d\u4e45\uff0c\u4e09\u5934\u85cf\u72ac\u548c\u4e00\u4e2a\u9972\u72ac\u4eba\uff0c\u75be\u5954\u800c\u81f3\u3002 \u3000\u3000\u90a3\u4eba\u4e0d\u7b49\u7a0b\u5b50\u671b\u95ee\u8bdd\uff0c\u5c31\u6325\u624b\u8bf4\u9053\uff1a\u201c\u4e00\u4f4d\u9972\u72ac\u4eba\u548c\u4e00\u5934\u72ac\uff0c\u5df2\u6b7b\u4e8e\u6697\u5668\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4f60\u4eec\u770b\u5230\u654c\u4eba\u4e86\u5417\uff1f\u201d \u3000\u3000\u9972\u72ac\u4eba\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u5c5e\u4e0b\u6b63\u5728\u6307\u6325\u85cf\u72ac\uff0c\u95fb\u5f97\u4e86\u5e84\u4e3b\u7684\u54e8\u58f0\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u70b9\u70b9\u5934\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u5e26\u7740\u4e09\u5934\u85cf\u72ac\uff0c\u5954\u5165\u5185\u9662\u3002\u201d \u3000\u3000\u9972\u72ac\u4eba\u5e94\u4e86\u4e00\u58f0\uff0c\u5e26\u7740\u4e09\u5934\u85cf\u72ac\u5954\u5165\u5185\u9662\u3002 \u3000\u3000\u53cc\u65b9\u867d\u6709\u4e86\u521d\u5ea6\u63a5\u89e6\uff0c\u800c\u4e14\u7a0b\u5e9c\u4e2d\u4eba\uff0c\u4e5f\u6709\u4e86\u4f24\u4ea1\uff0c\u4f46\u6574\u4e2a\u7a0b\u5e9c\uff0c\u4ecd\u7136\u4e00\u7247\u9759\u5bc2\uff0c\u4e0d\u89c1\u706f\u706b\uff0c\u4e0d\u95fb\u4eba\u58f0\u3002 \u3000\u3000\u539f\u6765\uff0c\u57cb\u4f0f\u5728\u5404\u5730\u7684\u6b66\u5e08\u548c\u5f29\u7bad\u624b\uff0c\u90fd\u5404\u6709\u4e13\u53f8\uff0c\u5949\u6709\u4e25\u4ee4\uff0c\u9664\u975e\u654c\u4eba\u4fb5\u5165\u4e86\u81ea\u5df1\u7684\u9632\u5b88\u8303\u56f4\uff0c\u4e0d\u5f97\u547c\u559d\u51fa\u624b\uff0c\u81ea\u4e71\u7ae0\u6cd5\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u51e0\u58f0\u5f29\u7bad\u5212\u7a7a\u4e4b\u58f0\uff0c\u4f20\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u4e00\u542c\u4e4b\u4e0b\uff0c\u5df2\u7136\u8fa8\u51fa\u662f\u90a3\u8fde\u73e0\u5323\u5f29\u53d1\u5c04\u51fa\u6765\u7684\u58f0\u97f3\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u7b2c\u516b\u53f7\u7684\u6697\u6869\u4e4b\u4e0a\uff0c\u5df2\u7ecf\u53d1\u51fa\u5323\u7bad\uff0c\u5728\u4e0b\u53bb\u77a7\u77a7\u5982\u4f55\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u7565\u6c89\u541f\uff0c\u9053\uff1a\u201c\u597d\uff01\u65b9\u4e8c\u5f1f\u548c\u4f60\u4e00\u540c\u53bb\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u6b66\u529f\u9ad8\u5f3a\uff0c\u540d\u52a8\u6c5f\u6e56\uff0c\u90d1\u5927\u521a\u8fd9\u756a\u5b89\u6392\uff0c\u90a3\u662f\u9690\u9690\u6709\u4fdd\u62a4\u4e4b\u610f\u4e86\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u5fc3\u5934\u611f\u6fc0\uff0c\u671b\u4e86\u65b9\u5929\u6210\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5144\u5f1f\u5e26\u8def\u3002\u201d\u5927\u6b65\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u5feb\u811a\u5f20\u4e09\uff0c\u4e3e\u6b65\u968f\u884c\uff0c\u5374\u4e3a\u7a0b\u5b50\u671b\u62e6\u4f4f\u9053\uff1a\u201c\u4f60\u7559\u5728\u90d1\u5927\u4fa0\u7684\u8eab\u4fa7\uff0c\u4ee5\u5907\u5782\u8be2\u3002\u201d \u3000\u3000\u5f20\u4e09\u5e94\u4e86\u4e00\u58f0\uff0c\u505c\u5728\u539f\u5730\u3002 \u3000\u3000\u65b9\u5929\u6210\u7d27\u968f\u7a0b\u5b50\u671b\u8eab\u540e\uff0c\u4e00\u9762\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5b50\u671b\u5144\uff0c\u5c0f\u5fc3\u4e94\u9b3c\u6697\u5668\u6076\u6bd2\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u70b9\u70b9\u5934\u5e94\u9053\uff0c\u201c\u591a\u8c22\u5173\u987e\u3002\u201d\u4e24\u4eba\u6cbf\u7740\u5899\u8fb9\uff0c\u7f13\u6b65\u800c\u884c\u3002 \u3000\u3000\u5e84\u4e2d\u4eba\u624b\u57cb\u4f0f\uff0c\u7a0b\u5b50\u671b\u5341\u5206\u7684\u719f\u6089\uff0c\u800c\u4e14\u628a\u4ed6\u4eec\u5206\u6210\u53f7\uff0c\u7528\u54e8\u5e93\u6307\u6325\u8fdb\u9000\u653b\u5b88\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u4e00\u9762\u884c\uff0c\u4e00\u9762\u51dd\u795e\u9759\u542c\uff0c\u90a3\u4e00\u9635\u5f29\u7bad\u8fc7\u540e\uff0c\u7adf\u662f\u518d\u65e0\u58f0\u606f\u3002 \u3000\u3000\u591c\u8272\u4e2d\uff0c\u56db\u5468\u4e00\u7247\u5b81\u9759\uff0c\u9759\u5f97\u542c\u4e0d\u51fa\u6765\u4e00\u70b9\u58f0\u606f\uff0c\u4f46\u7a0b\u5b50\u671b\u5fc3\u4e2d\u660e\u767d\uff0c\u5251\u95e8\u4e94\u9b3c\u548c\u91d1\u67aa\u5b97\u58eb\u4e49\uff0c\u5df2\u7387\u5c5e\u4e0b\uff0c\u552e\u805a\u4e8e\u90a3\u7af9\u7bf1\u4e4b\u5916\uff0c\u5947\u602a\u7684\u662f\uff0c\u5bf9\u65b9\u7adf\u4e0d\u80af\u653b\u5165\u5e84\u4e2d\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u6697\u5f71\u4e2d\u4f38\u51fa\u4e00\u58f0\u8f7b\u53f1\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u4f4e\u58f0\u5e94\u9053\uff1a\u201c\u6211\u2026\u2026\u201d\u6211\u5b57\u51fa\u53e3\uff0c\u4e00\u58f0\u6697\u5668\u7834\u7a7a\u4e4b\u58f0\u4f20\u4e86\u8fc7\u6765\uff0c\u6697\u8272\u4e2d\u6570\u70b9\u5bd2\u8292\uff0c\u7535\u5c04\u800c\u81f3\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u3001\u65b9\u5929\u6210\u540c\u65f6\u4e00\u4f0f\u8eab\u5b50\uff0c\u56db\u4e94\u4ef6\u6697\u5668\uff0c\u5c3d\u5c04\u5728\u5899\u58c1\u4e4b\u4e0a\uff0c\u4e00\u9635\u535c\u535c\u4e4b\u58f0\uff0c\u8dcc\u843d\u5b9e\u5730\u3002 \u3000\u3000\u6562\u60c5\u654c\u4eba\u5df2\u7136\u8fdb\u5165\u7af9\u7bf1\uff0c\u9690\u5728\u54b1\u5904\uff0c\u501f\u673a\u65bd\u653e\u6697\u5668\u3002 \u3000\u3000\u4f46\u95fb\u8fde\u73e0\u7bad\u58f0\uff0c\u4e00\u6392\u5f29\u7bad\uff0c\u8fd8\u51fb\u8fc7\u53bb\uff0c\u4e00\u58f0\u8f7b\u5fae\u7684\u95f7\u54fc\u58f0\uff0c\u679c\u7136\uff0c\u8fd9\u4e00\u6392\u5c04\u51fa\u7684\u5f29\u7bad\uff0c\u51fb\u4f24\u4e86\u5bf9\u65b9\u4e00\u4eba\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u957f\u8eab\u800c\u8d77\u8dc3\u5165\u58c1\u89d2\u3002 \u3000\u3000\u65b9\u5929\u6210\u4e00\u63d0\u6c14\uff0c\u65bd\u51fa\u201c\u71d5\u5b50\u6284\u6c34\u201d\u7684\u8eab\u6cd5\uff0c\u4e00\u8d77\u4e00\u843d\uff0c\u4e5f\u8dc3\u5165\u4e00\u5904\u58c1\u89d2\u4e4b\u5185\u3002 \u3000\u3000\u51dd\u76ee\u671b\u53bb\u53ea\u89c1\u4e00\u9053\u4e94\u5c3a\u9ad8\u77ee\u7684\u9752\u7816\u58c1\u4e4b\u540e\uff0c\u7ad9\u7740\u4e00\u4e2a\u624b\u6267\u82b1\u67aa\u7684\u6b66\u5e08\uff0c\u548c\u624b\u6267\u5323\u5f29\u3001\u6302\u7740\u8170\u5200\u7684\u7bad\u624b\u3002 \u3000\u3000\u539f\u6765\uff0c\u7a0b\u5b50\u671b\u672a\u96e8\u7ef8\u7f28\uff0c\u56db\u9762\u90fd\u5efa\u6709\u4ff1\u90a3\u4e9b\u5f29\u7bad\u624b\u9690\u8eab\u575a\u58c1\u3002 \u3000\u3000\u90a3\u6267\u67aa\u4eba\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u4f24\u4e86\u5bf9\u65b9\u4e00\u4eba\uff0c\u5e84\u4e3b\u542c\u5230\u4e86\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u70b9\u70b9\u5934\uff0c\u8f7b\u8f7b\u4ed6\u8bf4\u9053\uff1a\u201c\u542c\u5230\u4e86\uff0c\u654c\u4eba\u5982\u4f55\uff1f\u201d \u3000\u3000\u90a3\u6267\u67aa\u6b66\u5e08\u9053\uff1a\u201c\u4f3c\u4e4e\u5df2\u6709\u751a\u591a\u654c\u4eba\uff0c\u6df7\u5165\u4e86\u7af9\u7bf1\uff0c\u5e84\u4e3b\u547d\u4ee4\u5c5e\u4e0b\u7b49\u5b88\u4f4f\u6b64\u5730\uff0c\u4e0d\u5f97\u64c5\u81ea\u884c\u52a8\uff0c\u5c5e\u4e0b\u4e5f\u4e0d\u6562\u8d76\u53bb\u67e5\u770b\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u6b63\u5f85\u7b54\u8bdd\uff0c\u7a81\u7136\u706b\u5149\u4e00\u95ea\uff0c\u4e00\u56e2\u706b\u73af\uff0c\u98de\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u90a3\u6267\u67aa\u5de6\u624b\u4e00\u626c\uff0c\u6307\u4e86\u4e00\u4e2a\u65b9\u4f4d\uff0c\u4e24\u4e2a\u5f29\u7bad\u624b\u540c\u65f6\u53d1\u51fa\u4e00\u6392\u5f3a\u52b2\u5f29\u7bad\uff0c\u76f4\u5411\u98de\u5c04\u706b\u73af\u4e4b\u5904\u5c04\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u90a3\u98de\u6765\u706b\u73af\uff0c\u6ce2\u7684\u4e00\u58f0\uff0c\u843d\u5728\u521a\u624d\u7a0b\u5b50\u671b\u505c\u8eab\u7684\u5730\u65b9\uff0c\u718a\u718a\u71c3\u70e7\u8d77\u6765\u3002 \u3000\u3000\u65b9\u5929\u6210\u8fd0\u8db3\u76ee\u529b\uff0c\u501f\u706b\u5149\u4e4b\u7eb3\uff0c\u5411\u524d\u671b\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u4e09\u4e2a\u8eab\u7740\u9ed1\u8272\u7684\u52b2\u88c5\u5927\u6c49\uff0c\u5e76\u80a9\u8e72\u5728\u5730\u4e0a\uff0c\u5927\u534a\u8eab\u9690\u5165\u7af9\u7bf1\u6797\u4e4b\u4e2d\uff0c\u516d\u53cc\u773c\u775b\uff0c\u6b63\u5728\u5411\u8fd9\u9762\u77a7\u7740\uff0c\u5fc3\u4e2d\u597d\u751f\u5947\u602a\uff0c\u6697\u9053\uff1a\u201c\u90a3\u7c97\u7af9\u7ecf\u8fc7\u94c1\u6761\u4e32\u8fde\uff0c\u575a\u786c\u65e0\u6bd4\uff0c\u4e00\u822c\u7684\u5200\u5251\uff0c\u4e5f\u96be\u7834\u5b83\uff0c\u8fd9\u662f\u600e\u4e48\u8fdb\u6765\u7684\u5462\uff1f\u201d \u3000\u3000\u5fc3\u4e2d\u8f6c\u5ff5\uff0c\u53f3\u624b\u5374\u63a2\u5165\u6000\u4e2d\uff0c\u6478\u51fa\u4e86\u4e09\u7c92\u94c1\u8fde\u5b50\uff0c\u6697\u8fd0\u8155\u529b\uff0c\u65bd\u51fa\u201c\u4e09\u5143\u53ca\u7b2c\u201d\u7684\u624b\u6cd5\uff0c\u4e00\u626c\u624b\uff0c\u4e09\u679a\u94c1\u83b2\u5b50\u7834\u7a7a\u98de\u51fa\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u548c\u6267\u67aa\u6b66\u5e08\uff0c\u8fd8\u672a\u77a7\u5230\u654c\u4eba\uff0c\u5374\u5ffd\u89c1\u65b9\u5929\u6210\u6253\u51fa\u6697\u5668\uff0c\u5fc3\u4e2d\u5927\u611f\u5947\u602a\u3002 \u3000\u3000\u4e09\u679a\u94c1\u83b2\u5b50\u7834\u98de\u800c\u51fa\uff0c\u6392\u7a7a\u800c\u81f3\u3002 \u3000\u3000\u53ea\u56e0\u4e09\u4e2a\u654c\u4eba\u534a\u8eab\u9690\u5728\u7af9\u7bf1\u4e4b\u4e2d\uff0c\u884c\u52a8\u4e0d\u4fbf\uff0c\u8eab\u5b50\u8fd8\u672a\u7ad9\u8d77\uff0c\u4e8c\u679a\u94c1\u83b2\u5b50\u5df2\u7ecf\u98de\u53bb\u3002 \u3000\u3000\u53ea\u542c\u5f97\u4e09\u58f0\u5927\u53eb\uff0c\u4e24\u4e2a\u51fb\u4e2d\u8981\u5bb3\uff0c\u5f53\u573a\u5012\u4e86\u4e0b\u53bb\uff0c\u53e6\u4e00\u4e2a\u4f24\u7684\u90e8\u4f4d\u4e0d\u91cd\uff0c\u8d77\u8eab\u5411\u524d\u8dd1\u53bb\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u53eb\u4e86\u4e24\u58f0\u60ed\u6127\uff0c\u5fd6\u9053\uff1a\u201c\u770b\u6765\u773c\u529b\u3001\u8155\u529b\u3001\u624b\u6cd5\u6211\u90fd\u51fa\u8fd9\u4f4d\u65b9\u4e8c\u4fa0\u76f8\u5dee\u751a\u591a\uff0c\u8fd9\u957f\u7684\u8ddd\u79bb\uff0c\u5c31\u7b97\u6211\u80fd\u627e\u5230\u4e86\u4eba\uff0c\u53d1\u51fa\u7684\u6697\u5668\uff0c\u53ea\u6015\u4e5f\u529b\u96be\u6240\u53ca\u3002\u201d \u3000\u3000\u90a3\u6267\u67aa\u6b66\u5e08\u5fc3\u4e2d\u66f4\u662f\u4f69\u670d\u5f97\u4e94\u4f53\u6295\u5730\uff0c\u671b\u4e86\u65b9\u5929\u6210\u4e00\u773c\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u4eba\u5bb6\u80fd\u540d\u626c\u5929\u4e0b\uff0c\u58f0\u52a8\u56db\u6d77\uff0c\u5b9e\u975e\u5e78\u81f4\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u5bd2\u8292\u4e00\u95ea\uff0c\u90a3\u8d77\u8eab\u5954\u8d70\u7684\u5927\u6c49\uff0c\u8eab\u5b50\u4e00\u6447\uff0c\u4e5f\u683d\u5012\u5730\u4e0a\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u5fc3\u4e2d\u660e\u767d\uff0c\u6697\u5668\u5e76\u975e\u5f29\u7bad\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u90d1\u5927\u4fa0\u53d1\u51fa\u7684\u6697\u5668\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u5927\u54e5\u5f88\u5c11\u65bd\u7528\u6697\u5668\uff0c\u90a3\u662f\u77f3\u4e09\u5f1f\u7684\u4eae\u94f6\u68ad\uff0c\u201d\u8bed\u58f0\u5fae\u5fae\u4e00\u987f\uff0c\u9053\uff1a \u3000\u3000\u201c\u5947\u602a\u554a\uff01\u5947\u602a\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4e09\u66f4\u5df2\u5230\uff0c\u4f55\u4ee5\u8fd8\u4e0d\u89c1\u4e94\u9b3c\u73b0\u8eab\uff0c\u53ea\u6709\u8fd9\u4e9b\u4e8c\u4e09\u6d41\u7684\u811a\u8272\uff0c\u5728\u6b64\u65bd\u653e\u6697\u5668\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u4e5f\u8bb8\u4e94\u9b3c\u5148\u8981\u8fd9\u4e9b\u4eba\u6765\u63a2\u63a2\u865a\u5b9e\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4e8b\u60c5\u6709\u4e9b\u4e0d\u5bf9\u3002\u201d\u7a81\u7136\u7ad9\u8d77\u8eab\u5b50\uff0c\u75be\u5982\u98de\u9e1f\uff0c\u4e24\u4e09\u4e2a\u8d77\u843d\u5df2\u5230\u5373\u4e24\u4e2a\u4e3a\u81ea\u5df1\u6697\u5668\u6253\u4f24\u7684\u654c\u4eba\u8eab\u524d\uff0c\u51dd\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u4e24\u4eba\u90fd\u5728\u7728\u52a8\u7740\u773c\u775b\uff0c\u4f24\u52bf\u867d\u7136\u5f88\u91cd\uff0c\u4f46\u8fd8\u672a\u6b7b\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u6025\u6025\u5730\u8d77\u8eab\u8ffd\u4e86\u8fc7\u53bb\uff0c\u9053\uff1a\u201c\u65b9\u4e8c\u4fa0\uff0c\u4e0d\u5fc5\u6d89\u9669\uff0c\u5feb\u9000\u56de\u6765\u3002\u201d \u3000\u3000\u5728\u4ed6\u60f3\u6765\uff0c\u65b9\u5929\u6210\u8d77\u8eab\u5954\u884c\uff0c\u5fc5\u5c06\u662f\u6697\u5668\u5982\u96e8\uff0c\u75be\u5c04\u800c\u6765\uff0c\u54ea\u77e5\u4e8b\u60c5\u5927\u7f2a\u4e0d\u7136\uff0c\u56db\u5468\u4e00\u7247\u9759\u5bc2\uff0c\u4e0d\u89c1\u4e00\u70b9\u53cd\u5e94\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u5954\u5230\u65b9\u5929\u6210\u7684\u8eab\u65c1\uff0c\u4e5f\u5df2\u8b66\u89c9\u5230\u60c5\u52bf\u6709\u4e9b\u53cd\u5e38\uff0c\u7acb\u65f6\u4e0d\u518d\u591a\u8a00\u3002 \u3000\u3000\u65b9\u5929\u6210\u5de6\u624b\u4f38\u51fa\uff0c\u6293\u8d77\u4e86\u4e00\u4e2a\u9ed1\u8863\u5927\u6c49\uff0c\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u4f60\u8fd8\u80fd\u8bf4\u8bdd\u5417\uff1f\u201d \u3000\u3000\u4e00\u9762\u56de\u8bdd\uff0c\u53f3\u624b\u5df2\u7136\u6258\u5728\u90a3\u4eba\u80cc\u5fc3\u4e4b\u4e0a\uff0c\u70ed\u6d41\u6eda\u6eda\uff0c\u6d8c\u5165\u90a3\u4eba\u5185\u8151\u3002 \u3000\u3000\u90a3\u5927\u6c49\u5403\u65b9\u5929\u6210\u94c1\u83b2\u5b50\u51fb\u4e2d\u4e86\u808b\u95f4\u5927\u7a74\uff0c\u53d7\u4f24\u672c\u662f\u5f88\u91cd\uff0c\u4f46\u65b9\u5929\u6210\u6df1\u539a\u7684\u5185\u529b\u76f8\u52a9\uff0c\u987f\u7136\u7cbe\u795e\u4e00\u632f\u9053\uff1a\u201c\u4f60\u8981\u95ee\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u6276\u8d77\u90a3\u5927\u6c49\uff0c\u5feb\u6b65\u5954\u5411\u9662\u5185\uff0c\u53ea\u89c1\u90d1\u5927\u521a\u3001\u77f3\u4fca\u90fd\u5728\u5927\u95e8\u4e4b\u5185\u76f8\u5019\u3002 \u3000\u3000\u539f\u6765\uff0c\u4e24\u4eba\u4e5f\u89c9\u51fa\u60c5\u5f62\u6709\u4e9b\u53ef\u7591\u3002 \u3000\u3000\u65b9\u5929\u6210\u653e\u4e0b\u90a3\u4eba\u6c49\uff0c\u9053\uff1a\u201c\u7b54\u590d\u6211\u7684\u95ee\u8bdd\uff0c\u5c31\u9976\u4f60\u4e0d\u6b7b\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u70b9\u71c3\u4e86\u706b\u647a\u5b50\uff0c\u70b9\u8d77\u4e86\u4e00\u76cf\u98ce\u706f\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u5927\u6c49\u77aa\u7740\u53cc\u76ee\uff0c\u9053\uff1a\u201c\u5f53\u771f\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u63a5\u9053\uff1a\u201c\u8fd9\u4f4d\u65b9\u4e8c\u4fa0\uff0c\u540d\u6ee1\u6c5f\u6e56\uff0c\u4e00\u8a00\u4e5d\u9f0e\uff0c\u5982\u4f55\u4f1a\u9a97\u4f60\u3002\u201d \u3000\u3000\u90a3\u5927\u6c49\u70b9\u70b9\u4eba\uff0c\u9053\uff1a\u201c\u597d\uff01\u6211\u5982\u662f\u77e5\u9053\u7684\u90fd\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4f60\u662f\u5b97\u5bb6\u5821\u4e2d\u4eba\u5462\uff0c\u8fd8\u662f\u8ddf\u968f\u4e94\u9b3c\u7684\u4eba\uff1f\u201d \u3000\u3000\u90a3\u5927\u6c49\u9053\uff1a\u201c\u5b97\u5bb6\u5821\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u91d1\u67aa\u5b97\u58eb\u4e49\u548c\u5251\u95e8\u4e94\u9b3c\u54ea\u91cc\u53bb\u4e86\uff1f\u4f55\u4ee5\u4e0d\u5b88\u4fe1\u7ea6\uff0c\u53ea\u9063\u4f60\u4eec\u51e0\u4e2a\u4eba\u6765\u6b64\u9001\u547d\uff1f\u201d \u3000\u3000\u90a3\u5927\u6c49\u9053\uff1a\u201c\u542c\u8bf4\u4ed6\u4eec\u5148\u5230\u5f20\u5bb6\u5e84\u53bb\u2026\u2026\u201d \u3000\u3000\u90d1\u5927\u521a\u4e00\u8dfa\u811a\u9053\uff1a\u201c\u54b1\u4eec\u8fd8\u662f\u4e2d\u4e86\u4ed6\u4eec\u58f0\u4e1c\u51fb\u897f\u4e4b\u8ba1\uff0c\u4ed6\u4eec\u5148\u6295\u5e16\u6c42\u89c1\uff0c\u548c\u54b1\u4eec\u8ba2\u4e0b\u4e09\u66f4\u4e4b\u7ea6\uff0c\u7a33\u4f4f\u4e86\u54b1\u4eec\uff0c\u7136\u540e\uff0c\u5148\u884c\u591c\u88ad\u5f20\u5bb6\u5e84\uff0c\u662f\u54b1\u4eec\u8651\u4e0d\u53ca\u6b64\uff0c\u5509\uff01\u4eba\u79f0\u90a3\u767d\u8863\u540a\u5ba2\u6210\u7384\u901a\uff0c\u9b3c\u8bc8\u591a\u667a\uff0c\u679c\u662f\u4e0d\u9519\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u54b1\u4eec\u6b64\u523b\u8d76\u63f4\u5f20\u5bb6\u5e84\uff0c\u662f\u5426\u8fd8\u6765\u5f97\u53ca\u5462\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u53ea\u6015\u662f\u6765\u4e0d\u53ca\u4e86\u2026\u2026\u201d \u3000\u3000\u8bdd\u58f0\u5fae\u5fae\u4e00\u987f\uff0c\u76ee\u5149\u8f6c\u5230\u7a0b\u5b50\u671b\u7684\u8138\u4e0a\uff0c\u9053\uff1a\u201c\u7a0b\u5144\uff0c\u5982\u662f\u90a3\u5f20\u5bb6\u5e84\u786e\u672a\u51fa\u4ec0\u4e48\u5947\u5f02\u4e4b\u7269\uff0c\u8fd9\u756a\u60e8\u906d\u4e94\u9b3c\u8840\u6d17\uff0c\u5b9e\u662f\u51a4\u6789\u5f97\u5f88\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u6b63\u5f85\u56de\u7b54\uff0c\u7a81\u7136\u4e00\u58f0\u6025\u4fc3\u7684\u54e8\u58f0\u4f20\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u7d27\u63a5\u7740\u662f\u4e00\u9635\u5f29\u7bad\u7834\u7a7a\u7684\u547c\u5578\uff0c\u5939\u6742\u7740\u4e24\u58f0\u60e8\u53eb\u4f20\u6765\u3002 \u3000\u3000\u90d1\u5927\u521a\u4e00\u628a\u62d6\u8fc7\u90a3\u9ed1\u8863\u5927\u6c49\uff0c\u9053\uff1a\u201c\u597d\u597d\u4fdd\u62a4\u4ed6\u3002\u201d \u3000\u3000\u53cc\u81c2\u4e00\u6296\uff0c\u201c\u4e00\u9e64\u51b2\u5929\u201d\u8dc3\u767b\u5c4b\u9762\u3002\u53cc\u8db3\u5fae\u70b9\u74e6\u820d\uff0c\u98de\u9e1f\u6295\u6797\uff0c\u76f4\u843d\u5b9e\u5730\u3002 \u3000\u3000\u7a81\u7136\u95ee\uff0c\u6570\u70b9\u5bd2\u8292\uff0c\u7834\u7a7a\u800c\u5230\uff0c\u88ad\u5411\u90d1\u5927\u521a\u3002 \u3000\u3000\u90d1\u5927\u521a\u53f3\u624b\u4e00\u7ffb\uff0c\u62d4\u51fa\u7d2b\u91d1\u5200\uff0c\u8fd0\u529b\u4e00\u6325\uff0c\u5200\u5149\u98de\u65cb\uff0c\u6709\u5982\u5339\u7ec3\u7ed5\u4f53\uff0c\u5c3d\u4e3a\u5200\u5149\u51fb\u843d\u3002 \u3000\u3000\u4f46\u95fb\u8863\u8882\u98d8\u98ce\u4e4b\u58f0\uff0c\u65b9\u5929\u6210\u3001\u77f3\u4fca\u5168\u90fd\u8dc3\u5165\u573a\u4e2d\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u5374\u4ece\u5927\u95e8\u4e2d\u95ea\u8eab\u800c\u51fa\u3002 \u3000\u3000\u65b9\u5929\u6210\u7f13\u6b65\u884c\u5230\u90d1\u5927\u521a\u8eab\u524d\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u94c1\u6761\u8d2f\u7a7f\u7684\u7bf1\u5899\uff0c\u5341\u5206\u575a\u786c\uff0c\u4e94\u9b3c\u5c5e\u4e0b\u4f55\u4ee5\u5f97\u5165\u3002\u201d \u3000\u3000\u539f\u6765\u53cc\u65b9\u4f7f\u7528\u6697\u5668\u52a8\u624b\uff0c\u5bf9\u65b9\u6697\u5668\uff0c\u5341\u5206\u590d\u6742\u3002\u98de\u5200\u3001\u8896\u7bad\u3001\u94a2\u9556\u65e0\u6240\u4e0d\u7528\uff0c\u4f46\u7a0b\u5e9c\u4e2d\uff0c\u5374\u5168\u662f\u5f29\u7bad\u8fd8\u51fb\uff0c\u4e8c\u5341\u540d\u5f29\u7bad\u624b\uff0c\u8c03\u914d\u5f97\u5b9c\uff0c\u52a0\u4e0a\u90a3\u8fde\u73e0\u73e0\u5323\u5f29\u7684\u8fde\u53d1\u5f29\u7bad\uff0c\u4ea4\u624b\u4e00\u9635\u4e4b\u540e\uff0c\u5bf9\u65b9\u8fd8\u51fb\u6e10\u5c11\uff0c\u679c\u7136\uff0c\u77e5\u5df2\u65e0\u6cd5\u4ee5\u6697\u5668\u80dc\u8fc7\u8fde\u5f29\u7bad\u3002 \u3000\u3000\u591c\u8272\u5982\u58a8\uff0c\u4e5f\u65e0\u6cd5\u770b\u5230\u53cc\u65b9\u8fd9\u6697\u5668\u4e92\u653e\u4e4b\u540e\u7684\u4f24\u4ea1\u60c5\u5f62\u3002 \u3000\u3000\u90d1\u5927\u521a\u4f4e\u58f0\u5e94\u9053\uff1a\u201c\u4ee5\u4e94\u9b3c\u6b66\u529f\u6216\u53ef\u8d8a\u8fc7\u7af9\u7bf1\u800c\u5165\uff0c\u4f46\u4ed6\u4e00\u822c\u968f\u884c\u5c5e\u4e0b\uff0c\u51b3\u96be\u6709\u6b64\u80fd\u8010\uff0c\u552f\u4e00\u4e4b\u7b56\uff0c\u662f\u4ed6\u4eec\u6316\u5730\u9053\u800c\u5165\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5c0f\u5f1f\u4e5f\u6709\u6b64\u60f3\u3002\u201d \u3000\u3000\u77f3\u4fca\u63a5\u9053\uff1a\u201c\u53ea\u6015\u4e94\u9b3c\u5c1a\u672a\u56de\u6765\uff0c\u4ed6\u4eec\u8fd9\u4e00\u9635\u6df7\u653b\u540e\uff0c\u518d\u65e0\u52a8\u9759\uff0c\u663e\u7136\u662f\u6ca1\u4eba\u4e3b\u6301\u5176\u4e8b\u4e86\u3002\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u7a81\u7136\u4e00\u9635\u7834\u7a7a\u91d1\u98ce\uff0c\u76f4\u88ad\u8fc7\u6765\u3002 \u3000\u3000\u90d1\u5927\u521a\u91d1\u5200\u4e00\u626c\uff0c\u6270\u5f00\u88ad\u6765\u6697\u5668\uff0c\u6709\u5982\u548c\u4eba\u5175\u5200\u76f8\u63a5\u4e00\u822c\uff0c\u7830\u7136\u58f0\u4e2d\uff0c\u624b\u8155\u4e00\u9707\u3002 \u3000\u3000\u65b9\u65e0\u6210\u7ad9\u5728\u77e5\u5927\u521a\u7684\u53f3\u540e\u65b9\uff0c\u90d1\u5927\u521a\u62e8\u5f00\u90a3\u98de\u6765\u51f6\u6697\u5668\uff0c\u7d27\u63a0\u7740\u65b9\u5929\u65f6\u80a9\u5012\u800c\u8fc7\u3002 \u3000\u3000\u90a3\u6697\u5668\u5341\u5206\u957f\u5927\uff0c\u5939\u5e26\u7740\u4e00\u80a1\u5f3a\u70c8\u7684\u5578\u98ce\u4e4b\u58f0\uff0c\u63a0\u8eab\u800c\u8fc7\u3002 \u3000\u3000\u65b9\u5929\u6210\u611f\u89c9\u90a3\u6697\u5668\u548c\u4e00\u822c\u6697\u5668\u4e0d\u540c\uff0c\u5fc3\u4e2d\u5927\u611f\u5947\u602a\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u6697\u5668\uff0c\u5a01\u529b\u5982\u6b64\u5f3a\u5927\u3002\u201d \u3000\u3000\u7a81\u7136\u95f4\uff0c\u51e0\u58f0\u72ac\u5420\uff0c\u7531\u5185\u5b85\u4f20\u4e86\u51fa\u6765\u3002 \u3000\u3000\u663e\u7136\uff0c\u5f3a\u654c\u5df2\u8bbe\u6cd5\u7ed5\u5165\u5b85\u9662\u4e2d\u53bb\u3002 \u3000\u3000\u90d1\u5927\u521a\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4e8c\u5f1f\uff0c\u4f60\u548c\u7a0b\u5821\u4e3b\u5230\u5185\u9662\u770b\u770b\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u5e94\u4e86\u4e00\u58f0\uff0c\u6b63\u5f85\u8f6c\u8eab\uff0c\u7a81\u7136\u706b\u5149\u4e00\u95ea\uff0c\u4e00\u53ea\u7ecf\u6cb9\u6d78\u8fc7\u7684\u5e03\u7403\uff0c\u71c3\u7740\u718a\u718a\u7684\u706b\u7130\uff0c\u7531\u9662\u4e2d\u629b\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5f3a\u70c8\u7684\u706b\u5149\uff0c\u4f7f\u7fa4\u8c6a\u5b8c\u5168\u66b4\u9732\u5728\u6620\u7167\u4e4b\u4e0b\u3002 \u3000\u3000\u90d1\u5927\u521a\u6025\u58f0\u8bf4\u9053\uff1a\u201c\u5c0f\u5fc3\u6697\u5668\uff0c\u6563\u5e03\u5f00\u53bb\uff01\u201d \u3000\u3000\u8bed\u58f0\u987f\u843d\uff0c\u4e24\u70b9\u5bd2\u8292\uff0c\u75be\u5982\u6d41\u661f\u4e00\u822c\uff0c\u5e76\u6392\u98de\u81f3\uff0c\u76f4\u53d6\u90d1\u5927\u521a\u3002 \u3000\u3000\u90d1\u5927\u521a\u7d2b\u91d1\u5200\u5e7b\u8d77\u4e86\u4e00\u7247\u62a4\u8eab\u5200\u5149\uff0c\u51fb\u843d\u4e0b\u4e24\u70b9\u5bd2\u661f\u3002 \u3000\u3000\u706b\u5149\u4e0b\u770b\u5f97\u660e\u767d\u90a3\u51fb\u843d\u7684\u6697\u5668\uff0c\u662f\u4e24\u679a\u7eaf\u94a2\u6253\u7684\u51e4\u5c3e\u9556\uff0c\u9556\u5934\u4e0a\u6cdb\u8d77\u4e86\u4e00\u7247\u84dd\u6c6a\u6c6a\u7684\u989c\u8272\uff0c\u679c\u7136\uff0c\u90a3\u662f\u7ecf\u8fc7\u5267\u6bd2\u6dec\u70bc\u4e4b\u7269\u3002 \u3000\u3000\u770b\u5f97\u90a3\u51e4\u5c3e\u9556\u6253\u7684\u7cbe\u5de7\uff0c\u4ee5\u53ca\u81ea\u5df1\u51fb\u843d\u6697\u5668\u65f6\uff0c\u611f\u89c9\u5230\u7684\u52b2\u9053\uff0c\u5fc3\u4e2d\u5df2\u77e5\u4e94\u9b3c\u8d76\u5230\u3002 \u3000\u3000\u5c31\u5728\u90d1\u5927\u521a\u51fb\u843d\u51e4\u5c3e\u9556\u540c\u65f6\uff0c\u4e00\u679a\u5947\u5927\u7684\u6697\u5668\uff0c\u7834\u7a7a\u800c\u81f3\uff0c\u76f4\u653b\u5411\u65b9\u5929\u6210\u3002 \u3000\u3000\u706b\u5149\u4e0b\uff0c\u53ea\u89c1\u90a3\u6697\u5668\u957f\u8fbe\u4e00\u5c3a\uff0c\u91d1\u5149\u95ea\u95ea\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u5b9a\u662f\u90a3\u5b97\u58eb\u4e49\u7684\u91d1\u67aa\u4e86\uff0c\u8bd5\u8bd5\u529b\u9053\u5982\u4f55\uff1f\u201d \u3000\u3000\u5fc3\u5ff5\u4e00\u52a8\uff0c\u4e0d\u518d\u95ea\u907f\uff0c\u957f\u5251\u63a2\u51fa\uff0c\u6a21\u91cc\u4e00\u5251\uff0c\u62cd\u5411\u91d1\u67aa\u3002 \u3000\u3000\u90a3\u5de8\u5927\u7684\u706b\u7403\uff0c\u4f7f\u51e0\u4eba\u66b4\u73b0\u4e8e\u591c\u6697\u4e4b\u4e2d\uff0c\u6070\u4e5f\u4f7f\u51e0\u4eba\u501f\u52a9\u8fd9\u706b\u5149\uff0c\u770b\u6e05\u695a\u4e86\u6697\u5668\u7684\u6765\u8def\u3002\u65b9\u5929\u6210\u4e00\u5251\u62cd\u5728\u90a3\u91d1\u67aa\u4e4b\u4e0a\uff0c\u624b\u8155\u611f\u89c9\u4e00\u9707\uff0c\u5fc3\u4e2d\u6697\u81ea\u60ca\u9053\uff1a\u201c\u597d\u5f3a\u52b2\u7684\u8155\u529b\uff01\u201d \u3000\u3000\u4f46\u90a3\u91d1\u67aa\u4ecd\u7136\u88ab\u957f\u5251\u9707\u5f97\u504f\u5411\u4e00\u4fa7\u3002 \u3000\u3000\u91d1\u67aa\u7d27\u508d\u7a0b\u5b50\u671b\u7684\u8eab\u4fa7\u800c\u8fc7\uff0c\u4f59\u529b\u4e0d\u8870\uff0c\u84ec\u7136\u4e00\u58f0\uff0c\u9489\u5728\u5899\u58c1\u4e4b\u4e2d\u3002 \u3000\u3000\u65b9\u5929\u6210\u4e00\u5251\u62cd\u5f00\u90a3\u91d1\u67aa\u4e4b\u540e\uff0c\u957f\u5251\u62a4\u8eab\uff0c\u559d\u9053\uff1a\u201c\u8d70\uff01\u201d\u7eb5\u8eab\u8df3\u8d77\uff0c\u76f4\u5411\u9662\u5b85\u4e4b\u5185\u51b2\u53bb\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u7d27\u968f\u65b9\u5929\u6210\u8eab\u540e\uff0c\u5954\u56de\u5b85\u9662\uff0c\u5fc3\u4e2d\u5374\u6697\u6697\u5fd6\u9053\uff1a\u201c\u5251\u95e8\u4e94\u9b3c\uff0c\u5f53\u771f\u662f\u5978\u8bc8\u591a\u667a\uff0c\u6211\u4eec\u8fd9\u7b49\u4e25\u5bc6\u7684\u5e03\u7f6e\uff0c\u4ecd\u662f\u65e0\u6cd5\u62e6\u963b\u4ed6\u4eec\u6df7\u5165\u5185\u5b85\u3002\u201d \u3000\u3000\u77a5\u89c1\u65b9\u5929\u6210\u957f\u5251\u821e\u52a8\uff0c\u767d\u5149\u95ea\u52a8\uff0c\u62a4\u4f4f\u4e86\u8eab\u5b50\u3002 \u3000\u3000\u4e00\u9635\u53ee\u53ee\u549a\u549a\u7684\u91d1\u94c1\u4ea4\u559d\u54cd\u8fc7\uff0c\u5185\u5b85\u4e2d\u5c04\u51fa\u7684\u6570\u70b9\u5bd2\u8292\uff0c\u5c3d\u4e3a\u65b9\u5929\u6210\u5b9d\u5251\u51fb\u843d\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u4e00\u63d0\u771f\u6c14\uff0c\u624b\u4e2d\u86c7\u5934\u68cd\u4e00\u70b9\u5b9e\u5730\uff0c\u8eab\u5b50\u7a81\u7136\u7eb5\u8d77\uff0c\u767b\u4e0a\u5c4b\u9762\u3002 \u3000\u3000\u51dd\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u4e24\u4e2a\u9ed1\u8863\u4eba\u5e76\u6392\u7ad9\u5728\u5c4b\u9762\u4e4b\u4e0a\uff0c\u76f8\u9694\u56db\u4e94\u5c3a\u8fdc\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u624b\u4e2d\u86c7\u5934\u68cd\uff0c\u5a01\u529b\u751a\u5f3a\uff0c\u4ed6\u5728\u86c7\u5934\u68cd\u4e0a\uff0c\u4e0b\u8fc7\u5341\u4f59\u5e74\u5de5\u592b\uff0c\u4f46\u5374\u4e0d\u9002\u5728\u5c4b\u9762\u4e0a\u548c\u4eba\u52a8\u624b\u3002\u4f55\u51b5\uff0c\u4ed6\u8f7b\u8eab\u529f\u592b\u9020\u8be3\u4e0d\u6df1\uff0c\u518d\u62d6\u4e0a\u8fd9\u6837\u4e00\u4ef6\u6c89\u91cd\u7684\u94c1\u68cd\u66f4\u662f\u98de\u8dc3\u4e0d\u52a8\u3002 \u3000\u3000\u4f46\u8fd9\u4f4d\u8001\u6c5f\u6e56\u65e9\u5df2\u6709\u4e86\u51c6\u5907\uff0c\u9664\u4e86\u624b\u4e2d\u7684\u86c7\u5934\u68cd\u5916\uff0c\u8fd8\u5e26\u6709\u4e00\u628a\u5355\u5200\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u65e0\u6cd5\u5224\u660e\u4e24\u4eba\u662f\u8c01\uff0c\u4f46\u4ed6\u60f3\u5230\u4e24\u4eba\u5c31\u7b97\u4e0d\u662f\u4e94\u9b3c\u4e2d\u4eba\uff0c\u4e5f\u5fc5\u662f\u4e94\u9b3c\u4e00\u4e0b\u7684\u7b2c\u4e00\u6d41\u9ad8\u624b\u3002 \u3000\u3000\u6b63\u5f85\u653e\u4e0b\u624b\u4e2d\u86c7\u5934\u68cd\uff0c\u7a81\u7136\u767d\u5149\u95ea\u8f6c\uff0c\u65b9\u5929\u6210\u631f\u5e26\u4e00\u7247\u62a4\u8eab\u5251\u5149\uff0c\u51b2\u5165\u95e8\u5185\u3002 \u3000\u3000\u53ea\u89c1\u767d\u5149\u6536\u655b\uff0c\u4eba\u5f71\u4e00\u95ea\uff0c\u65b9\u5929\u6210\u5df2\u7136\u8dc3\u4e0a\u5c4b\u9762\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u65b9\u5929\u6210\u3001\u4e24\u4f4d\u53ef\u5426\u62a5\u4e0a\u540d\u6765\u3002\u201d \u3000\u3000\u53f3\u9762\u90a3\u9ed1\u8863\u4eba\u63a5\u9053\uff1a\u201c\u4e09\u9b3c\u5305\u6b63\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8bf8\u4f4d\u6765\u665a\u5feb\u8fd1\u4e00\u4e2a\u65f6\u8fb0\u3002\u201d \u3000\u3000\u4e09\u9b3c\u5305\u6b63\u9053\uff1a\u201c\u591a\u8ba9\u4f60\u4eec\u591a\u6d3b\u4e86\u4e00\u4e2a\u65f6\u8fb0\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u9b3c\u4e00\u9f50\u4e0a\u5427\uff01\u201d\u8eab\u5b50\u4e00\u4fa7\uff0c\u9661\u7136\u95ee\u98de\u8dc3\u800c\u8d77\uff0c\u76f4\u5411\u5305\u6b63\u51b2\u53bb\u3002 \u3000\u3000\u539f\u6765\uff0c\u90a3\u65b9\u5929\u6210\u867d\u7136\u8dc3\u4e0a\u4e86\u5c4b\u9762\uff0c\u4ecd\u7136\u548c\u90a3\u4e8c\u9b3c\u6709\u4e00\u4e08\u591a\u8fdc\u7684\u8ddd\u79bb\u3002 \u3000\u3000\u53ea\u542c\u4e09\u9b3c\u5305\u6b63\u5389\u5578\u4e00\u58f0\uff0c\u53f3\u624b\u7a81\u7136\u4e00\u626c\uff0c\u4e94\u9053\u94f6\u8292\uff0c\u7535\u5c04\u800c\u51fa\u3002 \u3000\u3000\u65b9\u5929\u6210\u65e9\u5df2\u601d\u8651\u53ca\u6b64\uff0c\u81ea\u5df1\u5411\u524d\u51b2\u51fb\u4e4b\u65f6\uff0c\u4ed6\u4eec\u53ef\u80fd\u65bd\u5c55\u6697\u5668\uff0c\u6240\u4ee5\uff0c\u957f\u5251\u65e9\u5df2\u63e1\u624b\u6212\u5907\u3002 \u3000\u3000\u773c\u770b\u6697\u5668\u88ad\u6765\uff0c\u7acb\u65f6\u6325\u7740\u957f\u5251\u51fb\u53bb\u3002 \u3000\u3000\u4f46\u95fb\u6ce2\u6ce2\u8f7b\u54cd\uff0c\u5305\u6b63\u6253\u51fa\u7684\u6697\u5668\uff0c\u5c3d\u4e3a\u65b9\u5929\u6210\u5251\u5149\u51fb\u843d\u3002 \u3000\u3000\u65b9\u5929\u6210\u51b2\u5954\u4e4b\u52bf\uff0c\u5feb\u901f\u7edd\u4f26\uff0c\u5305\u6b63\u8fd8\u672a\u6765\u5f97\u53ca\u53d1\u51fa\u7b2c\u4e8c\u9053\u6697\u5668\uff0c\u65b9\u5929\u6210\u5df2\u7136\u51b2\u8fd1\u8eab\u4fa7\uff0c\u4eba\u672a\u843d\u5730\uff0c\u53f3\u624b\u957f\u5219\u201c\u795e\u9f99\u51fa\u585e\u201d\u523a\u5411\u5305\u6b63\u9762\u95e8\u3002 \u3000\u3000\u5305\u6b63\u4e00\u5438\u6c14\uff0c\u9000\u540e\u4e09\u6b65\uff0c\u53f3\u624b\u4e00\u63a2\u3001\u5355\u5200\u5c71\u9798\u3002\u4e00\u62ac\u624b\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u6321\u5f00\u4e86\u65b9\u5929\u6210\u624b\u4e2d\u957f\u5251\uff0c\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u4e2d\u5dde\u4e09\u4fa0\u4e4b\u540d\uff0c\u679c\u975e\u865a\u4f20\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u811a\u843d\u5b9e\u5730\uff0c\u9053\uff1a\u201c\u5938\u5956\u4e86\u3002\u201d\u957f\u5251\u4e00\u7f29\u4e00\u4f38\uff0c\u53c8\u653b\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5305\u6b63\u6325\u5200\u63a5\u67b6\uff0c\u4e24\u4eba\u5c55\u5f00\u4e86\u4e00\u573a\u6076\u6597\u3002 \u3000\u3000\u65b9\u5929\u6210\u4e00\u5f00\u59cb\u5251\u62db\u5c31\u51cc\u5389\u5f02\u5e38\uff0c\u653b\u52bf\u731b\u9510\u3002\u5305\u6b63\u6781\u529b\u60f3\u62a2\u5230\u5148\u673a\uff0c\u5355\u5200\u5c3d\u51fa\u5947\u62db\uff0c\u548c\u65b9\u5929\u6210\u62a2\u653b\u3002 \u3000\u3000\u4f46\u5341\u62db\u8fc7\u540e\uff0c\u5305\u6b63\u59cb\u7ec8\u65e0\u6cd5\u62a2\u5f97\u5148\u673a\uff0c\u624b\u4e2d\u5200\u52bf\u5df2\u7f13\u3002 \u3000\u3000\u65b9\u4e91\u6210\u5de6\u624b\u957f\u5251\u75be\u8fdb\uff0c\u7a81\u7136\u95ea\u8d77\u4e86\u4e24\u6735\u5251\u82b1\uff0c\u523a\u5411\u5305\u6b63\u7684\u6267\u5200\u53f3\u8155\u3002 \u3000\u3000\u539f\u6765\uff0c\u5305\u6b63\u5200\u6cd5\u4e00\u7f13\uff0c\u5df2\u7ed9\u4e86\u65b9\u5929\u6210\u53ef\u4e58\u4e4b\u673a\uff0c\u957f\u5251\u5982\u6c34\u94f6\u6cfb\u5730\uff0c\u4e58\u865a\u800c\u5165\uff0c\u95ea\u8d77\u4e86\u4e24\u6735\u5251\u82b1\uff0c\u5c01\u4f4f\u4e86\u5305\u6b63\u7684\u5200\u62db\u53d8\u5316\uff0c\u5251\u8292\u9002\u65f6\u523a\u5411\u5305\u6b63\u63e1\u5200\u7684\u53f3\u624b\u3002 \u3000\u3000\u5305\u6b63\u5200\u5916\u88ab\u5c01\uff0c\u5c01\u6321\u8fd8\u51fb\u4e24\u7686\u4e0d\u80fd\uff0c\u552f\u4e00\u7684\u529e\u6cd5\uff0c\u5c31\u662f\u5411\u540e\u9000\u8ba9\u3002 \u3000\u3000\u5f62\u52bf\u903c\u4eba\uff0c\u5305\u6b63\u4e0d\u5f97\u4e0d\u5f03\u5200\u9000\u907f\uff0c\u4e94\u6307\u4e00\u677e\uff0c\u5f03\u53bb\u5355\u5200\uff0c\u4eba\u7535\u540c\u65f6\u5411\u540e\u8dc3\u9000\u4e09\u5c3a\u3002 \u3000\u3000\u65b9\u5929\u6210\u53f3\u624b\u957f\u5251\u4e00\u6311\uff0c\u4e0d\u5f85\u5355\u5200\u843d\u5730\uff0c\u6311\u8d77\u5200\u8eab\uff0c\u8155\u4e0a\u52a0\u529b\uff0c\u5185\u5916\u4e00\u63a8\uff0c\u5355\u5200\u76f4\u5411\u738b\u6e05\u98de\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5341\u4f59\u62db\u4e5f\u4e0d\u8fc7\u4e00\u7728\u773c\u5de5\u592b\uff0c\u7a0b\u5b50\u671b\u548c\u56db\u9b3c\u738b\u6e05\uff0c\u90fd\u8fd8\u5728\u505c\u8eab\u89c2\u6218\uff0c\u60f3\u4e0d\u5230\u4e24\u4eba\u5df2\u7136\u5206\u51fa\u80dc\u8d1f\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u773c\u770b\u90a3\u65b9\u5929\u6210\u7247\u523b\u5c31\u903c\u7684\u4e09\u9b3c\u5305\u6b63\u5f03\u5200\uff0c\u5fc3\u4e2d\u8fd9\u80a1\u4f69\u670d\uff0c\u7b80\u76f4\u662f\u65e0\u6cd5\u5f62\u5bb9\uff0c\u6697\u9053\uff1a\u770b\u770b\u65b9\u4e8c\u4fa0\u7684\u6b66\u529f\uff0c\u6211\u7a0b\u67d0\u662f\u5f53\u771f\u8be5\u9000\u9690\u4e86\u3002 \u3000\u3000\u53ea\u89c1\u738b\u6e05\u624b\u4e2d\u5355\u5200\u4e00\u8d77\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u51fb\u5728\u90a3\u65b9\u5929\u6210\u6311\u98de\u8fc7\u6765\u7684\u5355\u5200\u4e0a\uff0c\u9053\uff1a\u201c\u4e09\u54e5\u63a5\u7740\u5200\u3002\u201d \u3000\u3000\u8bdd\u51fa\u53e3\u5355\u5200\u4e5f\u98de\u5230\u4e86\u5305\u6b63\u8eab\u524d\u3002 \u3000\u3000\u5305\u6b63\u53f3\u624b\u4e00\u4f38\u6293\u4f4f\u5355\u5200\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u56db\uff0c\u7559\u70b9\u5fc3\uff0c\u8fd9\u4eba\u5251\u9053\u9ad8\u8fc7\u54b1\u4eec\uff0c\u4f46\u4e5f\u4e0d\u80fd\u5728\u4e09\u4e94\u5341\u62db\u80dc\u54b1\u4eec\uff0c\u6c89\u4f4f\u6c14\uff0c\u5148\u5b88\u540e\u653b\uff0c\u8c61\u6211\uff0c\u6c42\u80dc\u5fc3\u5207\uff0c\u53cd\u800c\u5403\u4e86\u5927\u4e8f\uff0c\u7ed9\u4ed6\u4e2a\u53ef\u4e58\u4e4b\u673a\u3002\u201d \u3000\u3000\u5176\u5b9e\uff0c\u4ed6\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u738b\u6e05\u5df2\u548c\u65b9\u4e91\u6210\u6253\u5f97\u96be\u89e3\u96be\u5206\u3002 \u3000\u3000\u65b9\u5929\u6210\u4e00\u9762\u548c\u738b\u6e05\u52a8\u624b\uff0c\u4f46\u4ecd\u7136\u628a\u5305\u6b63\u7684\u8c08\u8bdd\u3001\u542c\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u4eba\u843d\u8d25\u4e4b\u540e\uff0c\u7acb\u65f6\u80fd\u591f\u770b\u51fa\u843d\u8d25\u539f\u56e0\u4f55\u5728\uff1f\u5b9e\u4e5f\u662f\u975e\u51e1\u4e4b\u624d\u3002\u201d \u3000\u3000\u56db\u9b3c\u738b\u6e05\u4e00\u4e0a\u624b\uff0c\u672c\u4e5f\u662f\u6025\u6025\u62a2\u653b\uff0c\u4f46\u542c\u5f97\u90a3\u5305\u6b63\u4e4b\u8a00\u540e\uff0c\u7a81\u7136\u653b\u52bf\u4e00\u6162\uff0c\u7adf\u7136\u6539\u64cd\u5b88\u52bf\uff0c\u4e00\u628a\u5355\u5200\uff0c\u547c\u547c\u751f\u98ce\uff0c\u628a\u95e8\u6237\u5b88\u5f97\u5341\u5206\u4e25\u7d27\u3002 \u3000\u3000\u65b9\u5929\u6210\u5251\u4e0a\u867d\u7136\u9ad8\u8fc7\u4e8c\u9b3c\uff0c\u4f46\u4e8c\u9b3c\u4e5f\u662f\u6b66\u6797\u9ad8\u624b\uff0c\u5982\u662f\u4e0d\u6025\u4e8e\u6c42\u80dc\uff0c\u53ea\u60f3\u6253\u7684\u65f6\u95f4\u957f\u4e9b\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u65b9\u5929\u6210\u8981\u60f3\u80dc\u4ed6\u4eec\uff0c\u5374\u4e5f\u975e\u6613\u3002 \u3000\u3000\u5305\u6b63\u51b7\u773c\u65c1\u89c2\uff0c\u770b\u738b\u6e05\u6539\u91c7\u5b88\u52bf\u540e\uff0c\u8db3\u53ef\u81ea\u4fdd\u8fd9\u624d\u5927\u559d\u4e00\u58f0\uff0c\u76f4\u5411\u7a0b\u5b50\u671b\u6251\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u5355\u5200\u6325\u52a8\uff0c\u62a2\u5148\u51fa\u624b\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u81ea\u5df1\u6b66\u529f\u51b3\u975e\u4e94\u9b3c\u4e4b\u654c\uff0c\u662f\u4ee5\uff0c\u5148\u62a2\u5148\u673a\uff0c\u800c\u4e14\u4e00\u51fa\u624b\uff0c\u5c31\u662f\u5168\u529b\u731b\u653b\u3002 \u3000\u3000\u5305\u6b63\u62ab\u4ed6\u4e00\u4e0b\u62a2\u53bb\u5148\u673a\uff0c\u800c\u4e14\u653b\u52bf\u53c8\u731b\u6076\u65e0\u6bd4\uff0c\u4e00\u65f6\u95f4\u771f\u8fd8\u4e0d\u6613\u5bf9\u4ed8\uff0c\u53ea\u597d\u6325\u5200\u63a5\u67b6\uff0c\u7acb\u91c7\u5b88\u52bf\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u6b66\u529f\u867d\u7136\u4e0d\u9ad8\uff0c\u4ed6\u5728\u6c5f\u6e56\u4e0a\u7684\u7ecf\u8138\u5374\u662f\u4eba\u6240\u96be\u53ca\uff0c\u4ed6\u5fc3\u77e5\u6b64\u523b\uff0c\u8981\u662f\u88ab\u4e09\u9b3c\u62a2\u53bb\u5148\u673a\uff0c\u6539\u91c7\u653b\u52bf\uff0c\u81ea\u5df1\u843d\u8d25\uff0c\u52bf\u6240\u5fc5\u7136\uff0c\u552f\u4e00\u7684\u529e\u6cd5\uff0c\u5c31\u662f\u4ee5\u653b\u4ee3\u5b88\uff0c\u8fd8\u53ef\u591a\u652f\u6301\u4e00\u4e9b\u65f6\u523b\u3002 \u3000\u3000\u662f\u6545\uff0c\u51fa\u624b\u7684\u5200\u52bf\u5341\u5206\u6076\u6bd2\uff0c\u4e13\u627e\u90a3\u4e09\u9b3c\u81f4\u547d\u6240\u5728\u3002 \u3000\u3000\u6709\u65f6\uff0c\u5305\u6b63\u5200\u6cd5\uff0c\u5b88\u4e2d\u8574\u653b\uff0c\u7a0b\u5b50\u671b\u4e5f\u77a7\u5f97\u660e\u767d\uff0c\u4f46\u4ed6\u5374\u5f03\u7f6e\u4e0d\u987e\uff0c\u624b\u4e2d\u5355\u5200\uff0c\u8fde\u8fde\u5288\u5411\u4e09\u9b3c\u5305\u6b63\u7684\u81f4\u547d\u6240\u5728\u3002 \u3000\u3000\u8fd9\u7b49\u4e24\u8d25\u4ff1\u4f24\u7684\u6253\u6cd5\uff0c\u771f\u8fd8\u628a\u4e09\u9b3c\u5305\u6b63\u5236\u4f4f\uff0c\u7adf\u7136\u4ed6\u4e00\u76f4\u65e0\u6cd5\u62a2\u56de\u5148\u673a\u3002 \u3000\u3000\u539f\u6765\uff0c\u4ed6\u81ea\u77e5\u6b66\u529f\u5f3a\u8fc7\u90a3\u7a0b\u5b50\u671b\u751a\u591a\uff0c\u72af\u4e0d\u7740\u548c\u4ed6\u62fc\u4e2a\u4e24\u8d25\u4ff1\u4f24\u3002 \u3000\u3000\u5b85\u9662\u5185\u5c55\u5f00\u6076\u6597\u7684\u540c\u65f6\uff0c\u6210\u7384\u901a\u5374\u7387\u7740\u4e8c\u9b3c\uff0c\u4e94\u9b3c\u4ee5\u91d1\u67aa\u5b97\u58eb\u4e49\uff0c\u649e\u5f00\u4e86\u7bf1\u95e8\uff0c\u51b2\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u90d1\u5927\u521a\u4f4e\u58f0\u5bf9\u5f20\u4e09\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u5e2e\u5fd9\u4e86\uff0c\u8bbe\u6cd5\u901a\u77e5\u5404\u8def\u57cb\u4f0f\uff0c\u8ba9\u4ed6\u4eec\u56fa\u5b88\u539f\u5730\uff0c\u65bd\u7528\u8fde\u73e0\u5323\u5f29\u62d2\u654c\uff0c\u975e\u4e0d\u5f97\u5df2\uff0c\u4e0d\u8981\u548c\u654c\u4eba\u8fd1\u8eab\u76f8\u640f\u3002\u201d\u5f20\u4e09\u5e94\u4e86\u4e00\u58f0\uff0c\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u90d1\u5927\u521a\u4e00\u6325\u624b\u4e2d\u7d2b\u91d1\u5200\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u6210\u7384\u901a\uff0c\u90d1\u67d0\u5df2\u7ecf\u5019\u9a7e\u591a\u65f6\u4e86\u3002\u201d \u3000\u3000\u6210\u7384\u901a\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u73b0\u5728\u6765\u4e5f\u4e0d\u665a\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u56db\u4f4d\u4e00\u8d77\u4e0a\u5462\uff1f\u8fd8\u662f\u6210\u5144\u548c\u5728\u4e0b\u5148\u884c\u6253\u4e2a\u80dc\u8d1f\uff1f\u201d \u3000\u3000\u6210\u7384\u901a\u9053\uff1a\u201c\u90d1\u5144\u5411\u5144\u5f1f\u6311\u6218\u5417\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4eca\u65e5\u52a8\u624b\uff0c\u5e76\u975e\u662f\u4e00\u822c\u6bd4\u6b66\u5b9a\u540d\uff0c\u4f60\u4eec\u4e94\u9b3c\u8702\u4f63\u800c\u4e0a\u4e5f\u6210\uff0c\u54b1\u4eec\u5355\u6253\u72ec\u6597\u4e5f\u6210\u3002\u201d \u3000\u3000\u5b97\u58eb\u4e49\u7a81\u7136\u5927\u884c\u4e24\u6b65\uff0c\u9053\uff1a\u201c\u5144\u5f1f\u5148\u63a5\u90d1\u5927\u4fa0\u51e0\u62db\u5982\u4f55\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u5144\u5f1f\u6781\u613f\u5949\u966a\u3002\u201d \u3000\u3000\u76ee\u5149\u5230\u5904\uff0c\u53ea\u89c1\u5b97\u58eb\u4e49\u6b64\u523b\u5df2\u7ecf\u6362\u4f5c\u4e00\u8eab\u52b2\u88c5\uff0c\u8170\u4e2d\u4e00\u6761\u56db\u6307\u5bbd\u7684\u76ae\u5e26\uff0c\u5206\u63d2\u7740\u4e00\u5c3a\u4e8c\u5bf8\u957f\u7684\u91d1\u67aa\uff0c\u4f46\u5df2\u6709\u6570\u4e2a\u4f4d\u7f6e\u7a7a\u7740\uff0c\u60f3\u662f\u5df2\u4f5c\u6697\u5668\u6253\u4e86\u51fa\u53bb\u3002\u5fc3\u4e2d\u6697\u89c9\u5947\u602a\uff0c\u5fd6\u9053\uff1a \u3000\u3000\u201c\u4ed6\u8fd9\u91d1\u67aa\u662f\u6697\u5668\uff0c\u8fd8\u5175\u5203\uff1f\u201d \u3000\u3000\u539f\u6765\uff0c\u5b97\u58eb\u4e49\u9664\u4e86\u8170\u4e2d\u7684\u91d1\u67aa\u5916\uff0c\u5de6\u624b\u4e2d\u518d\u65e0\u5175\u5203\u3002 \u3000\u3000\u53ea\u89c1\u5b97\u58eb\u4e49\u4e24\u624b\u4e00\u63a2\u5404\u62d4\u51fa\u4e00\u652f\u91d1\u67aa\uff0c\u9053\uff1a\u201c\u90d1\u5927\u4fa0\u8bf7\u51fa\u624b\u5427\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u539f\u6765\u5b97\u5821\u4e3b\u7684\u91d1\u67aa\uff0c\u53ef\u4f5c\u6697\u5668\uff0c\u4e5f\u53ef\u4f5c\u4e3a\u5175\u5203\u7528\uff0c\u5728\u4e0b\u5012\u662f\u5f00\u6b21\u773c\u754c\u3002\u201d \u3000\u3000\u5b97\u58eb\u4e49\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u90d1\u5927\u4fa0\u5938\u5956\u4e86\uff01\u201d \u3000\u3000\u5de6\u624b\u91d1\u67aa\u4e00\u626c\uff0c\u76f4\u653b\u8fc7\u53bb\uff0c\u53f3\u624b\u91d1\u67aa\u5e73\u80f8\u62a4\u8eab\u3002 \u3000\u3000\u90d1\u5927\u521a\u7d2b\u91d1\u5200\u4e00\u62ac\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u67b6\u5f00\u4e86\u5b97\u58eb\u4e49\u624b\u4e2d\u91d1\u67aa\uff0c\u8155\u52bf\u4e00\u6c89\uff0c\u6a2a\u5411\u5b97\u58eb\u4e49\u624b\u8155\u4e4b\u4e0a\u65a9\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u5200\u56e0\u52bf\u5236\u5b9c\uff0c\u5feb\u8fc5\u65e0\u6bd4\uff0c\u5b97\u58eb\u4e49\u51e0\u4e4e\u5403\u90a3\u4e00\u5200\uff0c\u524a\u4e2d\u4e86\u624b\u8155\uff0c\u4e0d\u7981\u5927\u5403\u4e00\u60ca\u6025\u6025\u9000\u540e\u4e09\u6b65\u3002 \u3000\u3000\u90d1\u5927\u521a\u4e5f\u4e0d\u8feb\u88ad\uff0c\u6536\u5200\u8bf4\u9053\uff1a\u201c\u5b97\u5821\u4e3b\u7684\u91d1\u62a2\u62db\u6cd5\uff0c\u8fd8\u672a\u65bd\u5c55\uff0c\u90d1\u67d0\u8fd9\u91cc\u5019\u6559\u3002\u201d \u3000\u3000\u5b97\u58eb\u4e49\u53ea\u542c\u5f97\u8138\u4e0a\u4e00\u9635\u70ed\u8fa3\u7684\u96be\u53d7\uff0c\u53e3\u4e2d\u5374\u8bf4\u9053\uff1a\u201c\u90d1\u5927\u4fa0\u7684\u5200\u6cd5\uff0c\u679c\u7136\u7cbe\u5947\u3002\u201d \u3000\u3000\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u53cc\u624b\u91d1\u62a2\u5df2\u8fde\u73af\u9012\u51fa\u3002 \u3000\u3000\u4ed6\u548c\u90d1\u5927\u521a\u52a8\u624b\u4e00\u62db\uff0c\u5df2\u77e5\u5229\u5bb3\uff0c\u53cc\u62a2\u9f50\u51fa\uff0c\u653b\u52bf\u867d\u7136\u731b\u9510\uff0c\u4f46\u6bcf\u4e00\u62db\u4e2d\uff0c\u90fd\u7559\u4f59\u52b2\uff0c\u51c6\u5907\u5bf9\u4ed8\u90a3\u90d1\u5927\u521a\u7684\u53cd\u51fb\u3002 \u3000\u3000\u90d1\u5927\u521a\u5fc3\u4e2d\u63a8\u60f3\u7684\u52b2\u654c\uff0c\u4e43\u662f\u4e94\u9b3c\u4e4b\u9996\u7684\u767d\u8863\u540a\u5ba2\u6210\u7384\u901a\uff0c\u5374\u4e0d\u6599\u8fd9\u91d1\u67aa\u5b97\u58eb\u4e49\uff0c\u7adf\u7136\u4e5f\u6709\u7740\u5982\u6b64\u7684\u6b66\u529f\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u4e94\u9b3c\u4e4b\u5916\uff0c\u518d\u52a0\u4e0a\u8fd9\u4e00\u4e2a\u5b97\u58eb\u4e49\uff0c\u654c\u4eba\u5171\u6709\u516d\u4e2a\u9ad8\u624b\uff0c\u6211\u65b9\u9664\u4e86\u4e8c\u5f1f\u3001\u4e09\u5f1f\u4e4b\u5916\uff0c\u7a0b\u5b50\u671b\u53ea\u80fd\u4f5c\u534a\u4e2a\u4eba\u7b97\uff0c\u5176\u4f59\u6b66\u5e08\u51b3\u96be\u548c\u4e94\u9b3c\u7b49\u4e00\u6d41\u9ad8\u624b\u76f8\u535a\uff0c\u53cc\u65b9\u50f5\u6597\u4e0b\u53bb\uff0c\u5bf9\u6211\u5927\u662f\u4e0d\u5229\u3002 \u3000\u3000\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u5200\u6cd5\u5ffd\u7136\u4e00\u53d8\uff0c\u5c55\u5f00\u4e86\u5feb\u901f\u653b\u52bf\u3002 \u3000\u3000\u53ea\u89c1\u5200\u8f6c\u5982\u8f6e\uff0c\u5bd2\u8292\u98de\u65cb\uff0c\u7247\u523b\u95f4\uff0c\u5df2\u7528\u91d1\u67aa\u5b97\u58eb\u4e49\u7684\u653b\u52bf\uff0c\u5168\u90e8\u538b\u5236\uff0c\u6392\u5c71\u5012\u6d77\u822c\u53cd\u51fb\u8fc7\u53bb\u3002 \u3000\u3000\u5b97\u58eb\u4e49\u6599\u4e0d\u5230\u5bf9\u65b9\u5200\u6cd5\u5982\u6b64\u7684\u5229\u5bb3\uff0c\u624b\u4e2d\u53cc\u62a2\uff0c\u5df2\u88ab\u538b\u5236\u5f97\u65bd\u5c55\u4e0d\u5f00\uff0c\u6210\u4e86\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u6ca1\u6709\u4e86\u8fd8\u624b\u4e4b\u529b\u3002 \u3000\u3000\u535a\u6597\u4e4b\u95f4\uff0c\u7a81\u95fb\u5f97\u5b97\u58eb\u4e49\u95f7\u547c\u4e00\u58f0\uff0c\u7a81\u7136\u5411\u540e\u9000\u4e86\u4e24\u6b65\u3002 \u3000\u3000\u51dd\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u90a3\u5b97\u58eb\u4e49\u5de6\u80a9\u4e0a\u9c9c\u8840\u6dcb\u6f13\uff0c\u8863\u5f00\u8089\u7efd\u3002 \u3000\u3000\u539f\u6765\uff0c\u90d1\u5927\u521a\u524a\u51fa\u4e00\u5200\uff0c\u5b97\u58eb\u4e49\u5e94\u63a5\u4e0d\u53ca\uff0c\u88ab\u5212\u4e2d\u5de6\u80a9\u3002 \u3000\u3000\u90d1\u5927\u521a\u672c\u53ef\u8d8b\u52bf\u4e00\u5200\uff0c\u53d6\u90a3\u5b97\u58eb\u4e49\u7684\u6027\u547d\uff0c\u4fbf\u4ed6\u5374\u624b\u4e0b\u7559\u60c5\uff0c\u53ca\u65f6\u6536\u5200\u3002 \u3000\u3000\u6210\u7384\u901a\u7eb5\u8eab\u4e00\u8dc3\uff0c\u6321\u5728\u5b97\u58eb\u4e49\u7684\u8eab\u524d\uff0c\u9053\uff1a\u201c\u90d1\u5927\u4fa0\u5200\u6cd5\u679c\u7136\u9ad8\u5f3a\uff0c\u5728\u4e0b\u9886\u6559\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u4e00\u573a\u51b3\u6218\uff0c\u4e0d\u662f\u4f60\u4eec\u4e94\u9b3c\u8986\u4ea1\uff0c\u5c31\u662f\u6211\u4eec\u4e2d\u5dde\u4e09\u4fa0\u8d25\u706b\uff0c\u6210\u5144\u6709\u4ec0\u4e48\u672c\u9886\u5c3d\u7ba1\u65bd\u5c55\u3002\u201d \u3000\u3000\u6210\u7384\u901a\u9053\uff1a\u201c\u90d1\u5927\u4fa0\u65e2\u662f\u51b3\u5fc3\u548c\u6211\u4eec\u5144\u5f1f\u62da\u4e2a\u6b7b\u6d3b\uff0c\u8bf4\u4e0d\u5f97\u5728\u4e0b\u4e5f\u53ea\u597d\u820d\u547d\u5949\u966a\u4e86\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u9601\u4e0b\u6700\u597d\u662f\u5168\u529b\u65bd\u5c55\uff0c\u4e0d\u8981\u7559\u60c5\u3002\u201d\u56de\u76ee\u4e00\u987e\u77f3\u4fca\uff0c\u9053\uff1a\u201c\u4e09\u5f1f\uff0c\u6211\u548c\u6210\u5144\u52a8\u624b\u76f8\u640f\uff0c\u4e0d\u8bba\u80dc\u8d1f\u5982\u4f55\uff0c\u90fd\u4e0d\u8bb8\u4f60\u51fa\u624b\u76f8\u52a9\u3002\u201d \u3000\u3000\u6210\u7384\u901a\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5251\u95e8\u4e94\u9b3c\uff0c\u7ed3\u4e49\u60c5\u6df1\uff0c\u8a93\u540c\u751f\u6b7b\uff0c\u5982\u82e5\u5728\u4e0b\u771f\u8981\u6b7b\u4f24\u5728\u4f60\u90d1\u5927\u6d4e\u7684\u5200\u4e0b\uff0c\u53ea\u6015\u4ed6\u4eec\u4e5f\u4e0d\u80fd\u72ec\u6d3b\u4e0b\u53bb\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u6210\u5144\u5982\u662f\u4e0d\u613f\u548c\u5728\u4e0b\u5355\u6253\u72ec\u6597\uff0c\u548c\u4f60\u4eec\u4e24\u4f4d\u5144\u5f1f\u8054\u624b\u800c\u4e0a\u4e5f\u597d\u3002\u201d \u3000\u3000\u6210\u7384\u901a\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e0d\u7528\u4e86\uff0c\u4f60\u5148\u80dc\u4e86\u6211\u518d\u8bf4\u3002\u201d \u3000\u3000\u53f3\u624b\u5411\u6000\u4e2d\u4e00\u63a2\uff0c\u7f13\u7f13\u53d6\u51fa\u4e86\u4e00\u4e2a\u5947\u5f62\u5175\u5203\u3002 \u3000\u3000\u90a3\u5175\u5203\u770b\u4e0a\u53bb\uff0c\u6709\u5982\u4e00\u4e2a\u4eba\u81c2\uff0c\u540e\u8fb9\u6709\u4e00\u4e2a\u62a4\u628a\u63e1\u5728\u624b\u4e2d\u3002 \u3000\u3000\u524d\u7aef\u4e94\u4e2a\u5e73\u6307\uff0c\u534a\u4f38\u534a\u5f20\uff0c\u6709\u5982\u624b\u6307\u4e00\u822c\u3002 \u3000\u3000\u4e94\u4e2a\u624b\u6307\uff0c\u4e09\u4e2a\u76f4\u4f38\uff0c\u65e0\u540d\u6307\u548c\u5927\u6307\uff0c\u5374\u6210\u534a\u5f2f\u4e4b\u72b6\u3002 \u3000\u3000\u90d1\u5927\u521a\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e45\u95fb\u6210\u5144\u8fd9\u94c1\u9b3c\u624b\u4e0a\uff0c\u62db\u672f\u5341\u5206\u7cbe\u5947\uff0c\u4eca\u65e5\u6709\u5e78\u4e00\u4f1a\u3002\u201d\u3002 \u3000\u3000\u6210\u7384\u901a\u53f3\u5b50\u4e00\u62db\uff0c\u94c1\u9b3c\u624b\u7a81\u7136\u9012\u51fa\uff0c\u70b9\u5411\u90d1\u5927\u521a\u524d\u80f8\u3002 \u3000\u3000\u90d1\u5927\u521a\u91d1\u5200\u7a81\u7136\u63a8\u51fa\uff0c\u5728\u8eab\u524d\u5e7b\u8d77\u4e00\u7247\u5200\u5149\uff0c\u6321\u5f00\u4e86\u94c1\u9b3c\u624b\u3002 \u3000\u3000\u6210\u7384\u901a\u4e00\u9762\u6325\u52a8\u94c1\u9b3c\u624b\u62a2\u653b\uff0c\u4e00\u800c\u9ad8\u58f0\u9053\uff1a\u201c\u90d1\u5927\u4fa0\uff0c\u65e2\u7136\u8981\u548c\u5144\u5f1f\u4f5c\u751f\u6b7b\u4e4b\u640f\uff0c\u90a3\u54b1\u4eec\u53ea\u597d\u662f\u5404\u65bd\u624b\u6bb5\u4e86\u3002\u201d \u3000 \u7b2c\u56db\u56de\u3000\u6d74\u8840\u9000\u654c \u3000 \u3000\u3000\u90d1\u5927\u521a\u4e00\u5200\u5c01\u5f00\u6210\u7384\u901a\u7684\u94c1\u9b3c\u624b\uff0c\u8fd8\u51fb\u4e86\u4e24\u5200\uff0c\u9053\uff1a\u201c\u6210\u5144\u53ea\u7ba1\u65bd\u5c55\u3002\u201d \u3000\u3000\u6210\u7384\u901a\u9053\uff1a\u201c\u90d1\u5927\u4fa0\u80fd\u591f\u8ba4\u51fa\u5144\u5f1f\u624b\u4e2d\u5175\u5203\uff0c\u60f3\u5fc5\u5b9a\u7136\u77e5\u6653\u5144\u5f1f\u624b\u4e2d\u8fd9\u5175\u5203\u85cf\u6709\u673a\u5173\u4e86\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u5144\u5f1f\u5012\u4e5f\u542c\u4eba\u8bf4\u8fc7\uff0c\u636e\u8bf4\u6709\u751a\u591a\u6b66\u6797\u9ad8\u624b\uff0c\u90fd\u662f\u4f24\u5728\u4f60\u8fd9\u94c1\u9b3c\u624b\u6697\u5668\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u6210\u7384\u901a\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f46\u4e0d\u77e5\u80fd\u5426\u4f24\u4f60\u90d1\u5927\u4fa0\uff01\u201d \u3000\u3000\u4e24\u4eba\u4e00\u9762\u4ea4\u8c08\uff0c\u624b\u4e2d\u5175\u5203\uff0c\u5374\u662f\u4e00\u62db\u7d27\u8fc7\u4e00\u62db\uff0c\u8c08\u4e86\u51e0\u53e5\u6d3b\uff0c\u4e24\u4eba\u5df2\u7ecf\u4ea4\u624b\u4e86\u4e8c\u5341\u4f59\u62db\u3002 \u3000\u3000\u4f46\u95fb\u90d1\u5927\u521a\u63a5\u9053\uff1a\u201c\u6210\u5144\u4f55\u59a8\u8bd5\u4e00\u4e0b\u5462\uff01\u201d \u3000\u3000\u6210\u7384\u901a\u9053\uff1a\u201c\u5144\u5f1f\u5728\u7b49\u673a\u4f1a\uff0c\u90d1\u5144\u8fd9\u8d9f\u5200\u6cd5\u4e2d\u4e0d\u8981\u9732\u51fa\u7834\u7efd\uff0c\u7ed9\u5144\u5f1f\u4ee5\u53ef\u4e58\u4e4b\u673a\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u5200\u6cd5\u4e00\u7d27\uff0c\u5237\u5237\u5237\u53cd\u51fb\u4e09\u5200\uff0c\u8fd9\u4e09\u5200\u52bf\u9053\u5feb\u901f\uff0c\u4e00\u6c14\u5475\u6210\uff0c\u6210\u7384\u901a\u624b\u4e2d\u7684\u94c1\u9b3c\u624b\uff0c\u5c01\u67b6\u4e0d\u53ca\uff0c\u88ab\u8feb\u5f97\u540e\u9000\u4e09\u6b65\u3002 \u3000\u3000\u90d1\u5927\u521a\u7eb5\u58f0\u800c\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u5144\u5f1f\u5982\u4e0d\u8054\u624b\u800c\u51fa\uff0c\u53ea\u6015\u6210\u5144\u53d6\u80dc\u7684\u673a\u4f1a\u4e0d\u5927\u3002\u201d\u7b11\u58f0\u4e2d\uff0c\u5355\u5200\u75be\u8fdb\uff0c\u5947\u62db\u7ef5\u8fde\u800c\u51fa\u3002 \u3000\u3000\u6210\u7384\u901a\u624b\u4e2d\u94c1\u9b3c\u624b\uff0c\u5728\u52a8\u624b\u4e4b\u521d\uff0c\u6709\u653b\u6709\u5b88\uff0c\u4f46\u6b64\u523b\u5374\u5b8c\u5168\u88ab\u8feb\u843d\u4e0b\u98ce\uff0c\u4f46\u89c1\u90d1\u5927\u521a\u624b\u4e2d\u94a2\u5200\u7eb5\u6a2a\uff0c\u98de\u52bf\u6108\u6765\u6108\u89c1\u51cc\u5389\u3002 \u3000\u3000\u4e94\u9b3c\u8463\u65b9\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8001\u4e8c\uff0c\u60c5\u52bf\u6709\u4e9b\u4e0d\u5bf9\uff0c\u54b1\u4eec\u518d\u4e0d\u8fde\u624b\uff0c\u6050\u6015\u5927\u54e5\u6491\u4e0d\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u4e8c\u9b3c\u70b9\u70b9\u5934\uff0c\u7ffb\u8155\u62bd\u51fa\u80cc\u4e0a\u7684\u539a\u80cc\u9b3c\u5934\u5200\uff0c\u7eb5\u8eab\u800c\u4e0a\u3002 \u3000\u3000\u8463\u65b9\u7d27\u968f\u7740\u62d4\u5200\u52a0\u5165\u3002 \u3000\u3000\u4e09\u9b3c\u8054\u624b\uff0c\u5a01\u529b\u5927\u589e\uff0c\u90d1\u5927\u521a\u51cc\u5389\u7684\u653b\u52bf\uff0c\u7acb\u88ab\u963b\u6b62\u3002 \u3000\u3000\u77f3\u4fca\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u4e09\u4eba\u8054\u624b\uff0c\u5728\u4e0b\u51fa\u624b\uff0c\u4e0d\u7b97\u80cc\u7ea6\u5427\uff01\u201d \u3000\u3000\u4f46\u662f\u5b97\u58eb\u4e49\u4e00\u626c\u624b\u4e2d\u7684\u91d1\u67aa\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u867d\u7136\u53d7\u4f24\uff0c\u4f46\u81ea\u8ba4\u8fd8\u80fd\u63a5\u4f60\u77f3\u4e09\u4fa0\u51e0\u62db\u3002\u201d \u3000\u3000\u77f3\u4fca\u53f3\u624b\u4e00\u632f\uff0c\u4eae\u94f6\u97ad\u7a81\u7136\u7ffb\u8d77\uff0c\u6ee1\u81ea\u5b97\u58eb\u4e49\u8170\u56de\u51fb\u53bb\u3002 \u3000\u3000\u5b97\u58eb\u4e49\u5de6\u81c2\u8d1f\u4f24\uff0c\u53f3\u624b\u91d1\u67aa\u4e00\u8f6c\u97ad\u52bf\uff0c\u5de6\u624b\u91d1\u67aa\u53cd\u51fb\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u8001\u5978\u5de8\u6ed1\uff0c\u5fcd\u4f24\u63a5\u6218\uff0c\u5e0c\u671b\u4e09\u9b3c\u5408\u624b\u4e4b\u529b\uff0c\u5728\u81ea\u5df1\u63a5\u6218\u671f\u95f4\uff0c\u80fd\u591f\u4e00\u4e3e\u51fb\u90d1\u5927\u521a\uff0c\u4eca\u65e5\u4e4b\u5c40\uff0c\u5fc5\u64cd\u80dc\u5238\u4e86\u3002 \u3000\u3000\u5b97\u5341\u4e49\u5fc3\u4e2d\u6b63\u6253\u7740\u5982\u610f\u7684\u7b97\u76d8\uff0c\u7a81\u95fb\u4e00\u9635\u51c4\u5389\u7684\u5578\u58f0\uff0c\u4f20\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4ed6\u4e45\u5e74\u548c\u4e94\u9b3c\u8d70\u5728\u4e00\u8d77\uff0c\u5bf9\u4e94\u9b3c\u7684\u751f\u6027\u3001\u6c42\u6551\u3001\u8054\u7edc\u4e4b\u6cd5\uff0c\u77e5\u4e4b\u751a\u8be6\uff0c\u95fb\u90a3\u5389\u5578\uff0c\u6b63\u662f\u56db\u9b3c\u6c42\u6551\u4e4b\u58f0\uff0c\u4e0d\u7981\u4e3a\u4e4b\u4e00\u5446\u3002 \u3000\u3000\u5c31\u5728\u4e00\u758f\u795e\u95f4\uff0c\u77f3\u4fca\u97ad\u4e2d\u52a0\u638c\uff0c\u5288\u8fc7\u4e00\u62db\u3002 \u3000\u3000\u5b97\u58eb\u4e49\u5361\u9632\u8fd9\u4e00\u638c\u6765\u8def\u5947\u5965\uff0c\u6b63\u88ab\u51fb\u4e2d\u80ef\u95f4\u3002 \u3000\u3000\u8fd9\u4e00\u51fb\u867d\u672a\u91cd\u4f24\u5b97\u58eb\u4e49\uff0c\u4f46\u4e5f\u628a\u4ed6\u6253\u5f97\u8fde\u9000\u4e24\u6b65\u624d\u7ad9\u597d\u592e\u5b50\uff0c\u8d76\u5fd9\u96c6\u4e2d\u7cbe\u795e\u63a5\u6218\u3002 \u3000\u3000\u6210\u7384\u901a\u4e5f\u542c\u5230\u56db\u5f1f\u547c\u5578\u6c42\u63f4\u4e4b\u58f0\uff0c\u53ea\u662f\u81ea\u5df1\u5374\u65e0\u6cd5\u5206\u8eab\u8d76\u5f80\u76f8\u63f4\uff0c\u4e00\u9762\u62db\u672f\u52a0\u5feb\uff0c\u63a5\u8fc7\u90d1\u5927\u521a\u653b\u5411\u4e8c\u9b3c\u7684\u5200\u6cd5\uff0c\u4e00\u9762\u8bf4\u9053\uff1a\u201c\u8001\u4e8c\uff0c\u6b64\u5904\u7531\u6211\u5e94\u4ed8\uff0c\u4f60\u53bb\u63a5\u5e94\u56db\u5f1f\u3002\u201d \u3000\u3000\u4e8c\u9b3c\u8fde\u653b\u5341\u62db\uff0c\u90fd\u88ab\u90d1\u5927\u521a\u8f7b\u6613\u5316\u89e3\u5f00\u53bb\uff0c\u5fc3\u4e2d\u5df2\u77e5\u4eca\u5bb5\u80dc\u7b97\u4e0d\u6613\uff0c\u4e00\u4e2a\u662f\u5168\u8eab\u800c\u9000\uff0c\u7acb\u65f6\u64a4\u8d70\uff0c\u8fd8\u6709\u4e00\u9014\uff0c\u53ea\u6709\u65e9\u65bd\u8fa3\u624b\uff0c\u5148\u4f24\u5bf9\u65b9\u4e00\u4e24\u4eba\uff0c\u6216\u53ef\u6709\u53d6\u80dc\u4e4b\uff0c\u662f\u4ee5\uff0c\u542c\u5f97\u6210\u7384\u901a\u4ea4\u5565\u4e4b\u8a00\u540e\uff0c\u5fc3\u4e2d\u5927\u611f\u4e3a\u96be\u3002 \u3000\u3000\u539f\u6765\uff0c\u4ed6\u5df2\u611f\u89c9\u5230\u8fd9\u90d1\u5927\u521a\u7684\u5200\u6cd5\uff0c\u5927\u6709\u6108\u6765\u6108\u731b\u4e4b\u52bf\uff0c\u4e09\u4eba\u8054\u624b\uff0c\u8fd8\u672a\u51fb\u8d25\u90d1\u5927\u521a\uff0c\u5982\u82e5\u81ea\u5df1\u9000\u51fa\uff0c\u5b9e\u529b\u5c82\u4e0d\u662f\u53c8\u6253\u4e86\u4e00\u4e2a\u6298\u6263\uff1f\u4f46\u56db\u5f1f\u547c\u5578\u6c42\u6551\uff0c\u52bf\u53c8\u4e0d\u80fd\u4e0d\u7ba1\u3002 \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u6bd2\u8ba1\u9661\u751f\uff0c\u5200\u6cd5\u4e00\u7d27\uff0c\u5237\u5237\u4e24\u5200\uff0c\u903c\u5f00\u90d1\u5927\u521a\u7684\u5200\u52bf\uff0c\u4eba\u5374\u75be\u5411\u540e\u9762\u9000\u4e86\u4e24\u6b65\u3002 \u3000\u3000\u501f\u540e\u9000\u4e4b\u52bf\uff0c\u5200\u4ea4\u5de6\u624b\uff0c\u53f3\u624b\u63a2\u5165\u6000\u4e2d\uff0c\u6478\u51fa\u4e86\u4e09\u679a\u5b50\u5348\u8ffd\u9b42\u9489\uff0c\u5de6\u624b\u5355\u5200\u4e00\u8d77\uff0c\u4e0d\u6321\u90d1\u5927\u521a\u7684\u5200\u52bf\uff0c\u53cd\u628a\u4e94\u9b3c\u8463\u65b9\u7684\u5200\u52bf\u6321\u5f00\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u90d1\u5927\u521a\u7684\u4e00\u4fa7\uff0c\u9732\u4e86\u884c\u5927\u7684\u4e00\u4e2a\u7a7a\u9699\u3002 \u3000\u3000\u4e8c\u9b3c\u53f3\u8155\u4e00\u632f\uff0c\u4e09\u679a\u5b50\u5348\u8ffd\u7470\u9489\uff0c\u8131\u624b\u98de\u51fa\uff0c\u6253\u5411\u90d1\u5927\u521a\u7684\u5de6\u814b\u3002 \u3000\u3000\u4e94\u9b3c\u4e45\u5e74\u53f0\u4f5c\uff0c\u5fc3\u610f\u76f8\u901a\uff0c\u4e8c\u9b3c\u5200\u5c01\u8463\u65b9\u5200\u52bf\u65f6\uff0c\u6210\u7384\u901a\u5df2\u77e5\u9053\u4ed6\u8981\u65bd\u5c55\u6697\u5668\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u624b\u4e2d\u94c1\u9b3c\u624b\u7a81\u7136\u4e00\u7d27\uff0c\u70b9\u5411\u90d1\u5927\u521a\u7684\u524d\u80f8\u3002 \u3000\u3000\u8fd9\u51e0\u4e0b\u4f73\u5999\u7684\u914d\u5408\uff0c\u987f\u7136\u4f7f\u90d1\u5927\u521a\u5fc3\u795e\u4e00\u5206\u3002\u7d2b\u91d1\u5200\u201c\u6d77\u5e95\u635e\u6708\u201d\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u67b6\u5f00\u4e86\u6210\u7384\u901a\u7684\u94c1\u9b3c\u624b\uff0c\u5374\u65e0\u6cd5\u518d\u59a8\u5230\u4e8c\u9b3c\u8fd1\u8eab\u800c\u53d1\u7684\u4e09\u679a\u5b50\u5348\u8ffd\u7470\u9489\u3002 \u3000\u3000\u53ea\u89c9\u814b\u95ee\u4e00\u9ebb\uff0c\u4e2d\u4e86\u4e24\u679a\u5b50\u5348\u9489\u3002 \u3000\u3000\u90d1\u5927\u521a\u9605\u5386\u4e30\u5bcc\uff0c\u6697\u5668\u4e2d\u8eab\uff0c\u5df2\u77e5\u6697\u5668\u4e0a\u6dec\u6709\u5267\u6bd2\u3002\u5fc3\u4e2d\u6025\u6012\u4ea4\u8ff8\uff0c\u864e\u543c\u4e00\u58f0\uff0c\u7d2b\u91d1\u5200\u5c71\u5d29\u6d77\u5578\u822c\u8fde\u53d1\u4e09\u5200\uff0c\u8feb\u7684\u6210\u7384\u901a\u8fde\u9000\u4e94\u6b65\uff0c\u9661\u7136\u4e00\u4e2a\u8f6c\u8eab\uff0c\u8fde\u4eba\u5e26\u5200\uff0c\u76f4\u5411\u4e8c\u9b3c\u6251\u53bb\u3002 \u3000\u3000\u4e8c\u9b3c\u60f3\u4e0d\u5230\u90d1\u5927\u521a\u8eab\u4e2d\u6697\u5668\u4e4b\u540e\uff0c\u8fd8\u6709\u8fd9\u822c\u51f6\u6076\u7684\u653b\u52bf\uff0c\u4e0d\u7981\u5fc3\u4e2d\u4e00\u5bd2\u3002 \u3000\u3000\u90d1\u5927\u521a\u6765\u52bf\u5947\u5feb\uff0c\u4e8c\u9b3c\u4e00\u6014\u795e\u95f4\uff0c\u7d2b\u91d1\u5200\u5df2\u62e6\u8170\u65a9\u5230\u3002 \u3000\u3000\u4f46\u95fb\u4e00\u58f0\u60e8\u53eb\uff0c\u4e8c\u9b3c\u751f\u751f\u88ab\u62e6\u8170\u65a9\u4f5c\u4e24\u80a1\uff0c\u9c9c\u8840\u6e85\u98de\u4e2d\uff0c\u6c41\u4f53\u5206\u843d\u4e24\u5904\u3002 \u3000\u3000\u8fd9\u4e0d\u8fc7\u662f\u4e00\u5239\u90a3\u95f4\u7684\u5de5\u592b\uff0c\u6210\u7384\u901a\u548c\u4e94\u9b3c\u8463\u65b9\u60f3\u6551\u63f4\uff0c\u5df2\u81ea\u4e0d\u53ca\u3002 \u3000\u3000\u90d1\u5927\u521a\u9f13\u52c7\u65bd\u51fa\u96f7\u9706\u4e00\u5200\uff0c\u65a9\u6bd9\u4e8c\u9b3c\u4e4b\u540e\uff0c\u4ea6\u89c9\u7740\u4f24\u5904\u7684\u9ebb\u6728\uff0c\u8fc5\u901f\u5728\u6269\u5927\u5ef6\u5c55\uff0c\u5fc3\u77e5\u4f24\u4e2d\u8981\u5bb3\uff0c\u51b3\u96be\u4e45\u6301\uff0c\u53ea\u6015\u96be\u518d\u652f\u6301\u5341\u4e2a\u56de\u5408\u4ee5\u4e0a\uff0c\u5fc5\u987b\u5c3d\u91cf\u5728\u5341\u5408\u4e4b\u5185\uff0c\u4f24\u4e86\u90a3\u6210\u7384\u901a\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u660e\u767d\uff0c\u5982\u82e5\u65e0\u6cd5\u5728\u5341\u5408\u5185\u4f24\u5f97\u6210\u7384\u901a\uff0c\u81ea\u5df1\u5982\u6bd2\u53d1\u800c\u6b7b\uff0c\u4eca\u65e5\u4e4b\u5c40\uff0c\u90a3\u5c31\u4e0d\u582a\u60f3\u8c61\u4e86\uff0c\u4e2d\u5dde\u4e09\u4fa0\u53ea\u6015\u90fd\u8981\u6298\u4e27\u4e8e\u7a0b\u5bb6\u5e84\u4e2d\uff0c\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u5389\u58f0\u559d\u9053\uff1a\u201c\u6210\u7384\u901a\uff0c\u6562\u63a5\u6211\u4e09\u5200\u5417\uff1f\u201d \u3000\u3000\u559d\u58f0\u4e2d\u98de\u8dc3\u800c\u8d77\uff0c\u5200\u5149\u7535\u5954\uff0c\u76f4\u5288\u4e0b\u53bb\u3002 \u3000\u3000\u6210\u7384\u901a\u773c\u770b\u5927\u521a\u4e2d\u4e86\u4e8c\u9b3c\u7684\u7edd\u6bd2\u6697\u5668\u4e4b\u540e\uff0c\u4ecd\u80fd\u6325\u5200\u65a9\u4eba\uff0c\u5fc3\u751a\u4e3a\u60ca\u9a87\uff0c\u7eb5\u8eab\u907f\u5f00\u4e0d\u80af\u786c\u62da\u3002 \u3000\u3000\u90d1\u5927\u521a\u5288\u51fa\u4e00\u5200\u4e4b\u540e\uff0c\u7a81\u89c9\u773c\u524d\u4e00\u82b1\uff0c\u5fc3\u4e2d\u6697\u6697\u5403\u60ca\uff0c\u5fd6\u9053\uff1a\u4e94\u9b3c\u6697\u6bef\u4e0a\u4e0d\u77e5\u5550\u7684\u4f55\u7b49\u6bd2\u7269\uff0c\u6bd2\u6027\u5982\u6b64\u4e4b\u70c8\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u77e5\u9053\uff0c\u6b64\u523b\u81ea\u5df1\u5982\u82e5\u5012\u4e86\u4e0b\u53bb\uff0c\u65b9\u5929\u6210\u548c\u77f3\u4fca\u6050\u4e5f\u96be\u652f\u6301\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u5fc5\u9700\u5c3d\u6240\u6709\u4e4b\u529b\uff0c\u652f\u6301\u7740\u81ea\u5df1\u4e0d\u5012\u4e0b\u53bb\u3002\u4e00\u9762\u8fd0\u6c14\u63d0\u795e\uff0c\u4e00\u9762\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6210\u7384\u901a\uff0c\u4e3a\u4f55\u4e0d\u63a5\u90d1\u67d0\u5200\u52bf\uff1f\u201d \u3000\u3000\u6210\u7384\u901a\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u90d1\u5144\u5df2\u4e2d\u4e86\u5b50\u5348\u8ffd\u9b42\u9489\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f46\u90d1\u67d0\u4ecd\u7136\u6709\u518d\u6218\u4e4b\u80fd\u3002\u201d \u3000\u3000\u6210\u7384\u901a\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u591f\u4e86\uff0c\u54b1\u4eec\u4e94\u5144\u5f1f\u65bd\u7528\u7684\u6697\u5668\uff0c\u90fd\u7ecf\u8fc7\u5267\u6bd2\u6dec\u70bc\uff0c\u800c\u4e14\u90fd\u662f\u5404\u79cd\u5947\u6bd2\u6df7\u5408\u4e4b\u6bd2\uff0c\u9664\u4e86\u6211\u4eec\u5144\u5f1f\u8eab\u4e0a\u5e26\u7684\u89e3\u836f\u4e4b\u5916\uff0c\u5929\u4e0b\u7b2c\u4e00\u540d\u533b\uff0c\u4e5f\u65e0\u6cd5\u89e3\u9664\u90a3\u5b50\u5348\u8ffd\u9b42\u9489\u4e0a\u7684\u5947\u6bd2\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u77f3\u4fca\u548c\u5b97\u58eb\u4e49\u6253\u6597\u6b63\u70c8\uff0c\u8f6f\u97ad\u3001\u91d1\u67aa\uff0c\u98de\u821e\u4e00\u56e2\u5bd2\u5149\u3002 \u3000\u3000\u8463\u65b9\u5374\u501f\u673a\u8fd0\u6c14\u8c03\u606f\u3002 \u3000\u3000\u539f\u6765\uff0c\u4ed6\u7ecf\u8fd9\u4e00\u756a\u5267\u70c8\u7684\u6076\u6597\u4e4b\u540e\uff0c\u5df2\u89c9\u7a0d\u5341\u5206\u75b2\u52b3\u3002 \u3000\u3000\u4f46\u95ef\u6210\u7384\u901a\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u5728\u4e0b\u8981\u770b\u4f60\u6bd2\u53d1\u65f6\u6b7b\u4ea1\u524d\u7684\u75db\u82e6\uff0c\u5982\u662f\u4e00\u5200\u628a\u4f60\u6740\u6b7b\uff0c\u90a3\u672a\u514d\u65b9\u4fbf\u5b9c\u4f60\u4e86\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u5fc3\u4e2d\u6697\u9053\uff1a\u5c31\u76ee\u4e0b\u60c5\u5f62\u800c\u8a00\uff0c\u6211\u5df2\u65e0\u6cd5\u63a7\u5236\u8eab\u53d7\u5947\u6bd2\u7684\u53d1\u4f5c\uff0c\u5fc5\u9700\u5728\u4e09\u4e94\u62db\u5185\u548c\u4ed6\u62da\u51fa\u751f\u6b7b\uff0c\u4ed6\u5982\u907f\u4e0d\u76f8\u640f\uff0c\u5012\u662f\u4e00\u4e2a\u6781\u4e3a\u6076\u6bd2\u7684\u529e\u6cd5\uff0c\u600e\u751f\u8bbe\u6cd5\u6fc0\u6012\u4ed6\u51fa\u624b\u786c\u62fc\u624d\u6210\u3002 \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6210\u5144\u4e0d\u60f3\u66ff\u4f60\u8fd9\u4f4d\u5144\u5f1f\u62a5\u4ec7\u5417\uff1f\u201d \u3000\u3000\u6210\u7384\u901a\u9053\uff1a\u201c\u81ea\u7136\u8981\u66ff\u4ed6\u62a5\u4ec7\uff0c\u6240\u4ee5\u624d\u8981\u4f60\u53d7\u4e9b\u75db\u82e6\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6210\u5144\uff0c\u770b\u6765\u662f\u4e0d\u6562\u548c\u5144\u5f1f\u786c\u62fc\u4e86\uff1f\u201d \u3000\u3000\u6210\u7384\u901a\u9053\uff1a\u201c\u4f60\u6bd2\u4f24\u6b63\u5728\u53d1\u4f5c\uff0c\u5927\u7ea6\u81ea\u77e5\u96be\u4ee5\u518d\u652f\u6301\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u88ab\u4ed6\u8bc6\u7a7f\u5fc3\u4e2d\u4e4b\u79d8\uff0c\u5fc3\u4e2d\u6697\u6697\u4e00\u53f9\uff0c\u5fd6\u9053\uff1a\u201c\u770b\u6765\u4ed6\u662f\u4e0d\u4f1a\u4e0a\u94a9\u4e86\uff0c\u4ed6\u5982\u4e0d\u80af\u548c\u6211\u786c\u62fc\uff0c\u5b9e\u65e0\u4f24\u4ed6\u4e4b\u671b\uff0c\u4e94\u9b3c\u8463\u65b9\u6b66\u529f\u3001\u5185\u529b\u90fd\u548c\u4ed6\u76f8\u5dee\u751a\u8fdc\uff0c\u6211\u4f55\u4e0d\u8003\u6b64\u673a\u4f1a\uff0c\u5148\u6740\u4e94\u9b3c\u8463\u65b9\uff1f\u201d \u3000\u3000\u5fc3\u4e2d\u6253\u8d70\u4e86\u4e3b\u610f\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u9601\u4e0b\u65e2\u662f\u4e0d\u80af\u548c\u90d1\u67d0\u529b\u62da\uff0c\u8bf4\u4e0d\u5f97\u90d1\u67d0\u5148\u6740\u4e94\u9b3c\u8463\u65b9\u3002\u201d\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u4eba\u5374\u4e00\u4e2a\u8f6c\u8eab\uff0c\u98de\u8dc3\u800c\u8d77\uff0c\u8fde\u4eba\u5e26\u5200\uff0c\u6a2a\u5411\u8463\u65b9\u65a9\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u51fb\uff0c\u5feb\u901f\u7edd\u4f26\uff0c\u8463\u65b9\u8bba\u907f\u4e0d\u53ca\uff0c\u53ea\u597d\u6325\u5200\u4e00\u6321\u3002 \u3000\u3000\u4f46\u95fb\u6210\u7384\u901a\u5927\u58f0\u559d\u9053\uff1a\u201c\u8001\u4e94\uff0c\u4e0d\u80fd\u548c\u4ed6\u786c\u62da\u3002\u201d \u3000\u3000\u4f46\u8463\u65b9\u5df2\u4e3a\u52bf\u6240\u8feb\uff0c\u8ba9\u907f\u4e0d\u80fd\uff0c\u4f46\u95fb\u5f53\u7684\u4e00\u91d1\u94c1\u4ea4\u9e23\uff0c\u53cc\u5200\u63a5\u5b9e\u4e86\u4e00\u62db\u3002 \u3000\u3000\u8fd9\u4e00\u5200\u4e2d\uff0c\u90d1\u5927\u521a\u51e0\u4e4e\u7528\u51fa\u4e86\u81ea\u5df1\u5168\u8eab\u7684\u6c14\u529b\uff0c\u8463\u65b9\u63a5\u4e0b\u4e00\u5200\uff0c\u53ea\u9707\u7684\u8eab\u4e0d\u7531\u4e3b\u7684\u5411\u540e\u8fde\u9000\u4e86\u4e94\u6b65\uff0c\u864e\u53e3\u7834\u88c2\uff0c\u5355\u5200\u8131\u624b\u98de\u51fa\u3002 \u3000\u3000\u6210\u7384\u901a\u5927\u559d\u4e00\u58f0\uff0c\u75be\u8dc3\u800c\u4e0a\uff0c\u624b\u4e2d\u94c1\u9b3c\u624b\u201c\u91d1\u9e21\u5544\u7c73\u201d\uff0c\u70b9\u5411\u90d1\u5927\u521a\u7684\u80cc\u5fc3\u3002 \u3000\u3000\u90d1\u5927\u521a\u8fd9\u4e00\u62db\u5a01\u731b\u7684\u5200\u52bf\uff0c\u867d\u7136\u8463\u65b9\u5927\u53d7\u632b\u6298\uff0c\u4f46\u4ed6\u81ea\u5df1\u5288\u5c71\u8fd9\u4e00\u5200\u4e4b\u540e\uff0c\u4e5f\u4fc3\u4ed6\u5267\u6bd2\u53d1\u4f5c\uff0c\u65e5\u89c9\u4ece\u91cd\u5343\u65a4\uff0c\u773c\u524d\u9ed1\u5f71\u4e71\u7a9c\uff0c\u6b65\u5c65\u8e09\u8dc4\uff0c\u7ad9\u7acb\u4e0d\u7a33\u3002 \u3000\u3000\u6210\u7384\u901a\u70b9\u51fa\u7684\u4e00\u8bb0\u94c1\u9b3c\u624b\uff0c\u5df2\u7136\u65e0\u6cd5\u95ea\u907f\u3002 \u3000\u3000\u773c\u770b\u90a3\u90d1\u5927\u521a\u5c31\u8981\u6b7b\u4f24\u5728\u6210\u7384\u901a\u7684\u94c1\u9b3c\u624b\u4e0b\uff0c\u7a81\u7136\u4eba\u5f71\u4e00\u95ea\u95ea\uff0c\u4e00\u6761\u9ed1\u5f71\u75be\u98de\u800c\u81f3\uff0c\u62ac\u624b\u4e00\u6284\uff0c\u6293\u4f4f\u4e86\u6210\u7384\u901a\u7684\u94c1\u9b3c\u624b\uff0c\u7528\u529b\u4e00\u5e26\uff0c\u6210\u7384\u901a\u8eab\u4e0d\u7531\u5df2\u7684\u5411\u524d\u51b2\u53bb\u3002 \u3000\u3000\u90a3\u4eba\u8eab\u7740\u957f\u886b\uff0c\u6574\u4e2a\u7684\u5934\u8138\uff0c\u90fd\u7528\u5e03\u5305\u4e86\u8d77\u6765\uff0c\u53ea\u9732\u51fa\u4e00\u5bf9\u795e\u5149\u70af\u70af\u7684\u773c\u775b\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u53f3\u624b\u6293\u4f4f\u94c1\u9b3c\u624b\uff0c\u5de6\u624b\u4e00\u62ac\u4e00\u638c\u62cd\u5411\u90a3\u6210\u7384\u901a\u7684\u524d\u80f8\u3002 \u3000\u3000\u6210\u7384\u901a\u8eab\u4e0d\u7531\u5df1\u5730\u5411\u524d\u503e\u52a8\uff0c\u5bf9\u65b9\u7684\u62f3\u52bf\u53c8\u662f\u76f4\u8fce\u4e0a\u6765\uff0c\u4e00\u6765\u4e00\u8fce\u4e4b\u95f4\uff0c\u52bf\u9053\u5947\u5feb\u3002 \u3000\u3000\u4f46\u6210\u7384\u901a\u7adf\u662f\u4e45\u7ecf\u5927\u654c\u7684\u4eba\u7269\uff0c\u773c\u770b\u638c\u52bf\u5c31\u8981\u8fd1\u8eab\uff0c\u7acb\u65f6\u4e00\u5438\u6c14\u5411\u524d\u503e\u52a8\u7684\u8eab\u5b50\u7acb\u65f6\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u90a3\u957f\u886b\u4eba\u53f3\u624b\u4e00\u4f38\uff0c\u7a81\u7136\u5411\u524d\u4e00\u9001\uff0c\u638c\u52bf\u5370\u5728\u6210\u7384\u901a\u7684\u524d\u8111\u4e4b\u4e0a\u3002\u6210\u7384\u901a\u8eab\u5b50\u4e00\u98a4\uff0c\u5f20\u5634\u5410\u51fa\u4e86\u4e00\u53e3\u9c9c\u8840\u3002 \u3000\u3000\u90a3\u957f\u886b\u4eba\u53f3\u624b\u52a0\u52b2\uff0c\u593a\u8fc7\u4e86\u6210\u7384\u901a\u624b\u4e2d\u94c1\u9b3c\u624b\uff0c\u4e00\u6b3a\u8eab\uff0c\u6325\u624b\u6321\u5f00\u4e86\u5b97\u58eb\u4e49\u624b\u4e2d\u91d1\u67aa\uff0c\u4e00\u62ac\u817f\uff0c\u8e22\u5728\u5b97\u58eb\u4e49\u7684\u5de6\u819d\u4e4b\u4e0a\u3002 \u3000\u3000\u5b97\u58eb\u4e49\u8eab\u4e0d\u7531\u5df1\u7684\u5411\u540e\u9000\u4e86\u4e09\u6b65\uff0c\u4e00\u8de4\u8dcc\u5750\u5728\u5730\u4e0a\u3002 \u3000\u3000\u77f3\u4fca\u89c1\u8fd9\u52a9\u62f3\u4eba\uff0c\u4e00\u62ac\u624b\u5c31\u4f24\u4e86\u5b97\u58eb\u4e49\uff0c\u6b66\u529f\u4e4b\u9ad8\uff0c\u7b80\u76f4\u662f\u4e0d\u53ef\u601d\u4eea\uff0c\u4e0d\u7981\u5fae\u5fae\u4e00\u6014\u3002 \u3000\u3000\u76f4\u5f85\u90a3\u957f\u886b\u4eba\u6253\u5012\u5b97\u58eb\u4e49\uff0c\u4ed6\u624d\u8bb0\u8d77\u8fd8\u4e0d\u77e5\u6765\u4eba\u662f\u8c01\uff0c\u5f53\u4e0b\u95ee\u9053\uff1a\u201c\u9601\u4e0b\u662f\u4f55\u8bb8\u4eba\uff1f\u201d \u3000\u3000\u90a3\u957f\u886b\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5feb\u4e9b\u6551\u4eba\uff0c\u90d1\u5927\u4fa0\u4f24\u5f97\u5f88\u91cd\uff0c\u518d\u665a\uff0c\u5c31\u6765\u53ca\u4e86\u3002\u201d \u3000\u3000\u77f3\u4fca\u8f6c\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u90d1\u5927\u521a\u8eab\u5b50\u6447\u6447\u6b32\u5012\uff0c\u5f53\u4e0b\u6025\u6b65\u5954\u4e86\u8fc7\u53bb\uff0c\u6276\u4f4f\u90d1\u5927\u521a\uff0c\u9053\uff1a \u3000\u3000\u201c\u5927\u54e5\u4f24\u5f97\u5f88\u91cd\u5417\uff1f\u201d \u3000\u3000\u5c31\u5728\u77f3\u4fca\u5954\u53bb\u6400\u6276\u90d1\u5927\u521a\u7684\u540c\u65f6\uff0c\u4e94\u9b3c\u8463\u65b9\u4e5f\u5954\u4e86\u8fc7\u6765\uff0c\u62b1\u8d77\u4e86\u6210\u7384\u901a\u3002 \u3000\u3000\u539f\u6765\uff0c\u6210\u7384\u901a\u6328\u7684\u4e00\u638c\u5f88\u91cd\uff0c\u8840\u6d6e\u6c14\u52a8\uff0c\u76f4\u51b2\u9876\u95e8\uff0c\u7ad9\u7acb\u4e0d\u7a33\uff0c\u5750\u5728\u4e86\u5730\u4e0a\u3002 \u3000\u3000\u8463\u65b9\u62b1\u8d77\u6210\u7384\u901a\u65f6\uff0c\u5b97\u571f\u4e49\u4e5f\u6323\u624e\u800c\u8d77\uff0c\u95ee\u9053\uff1a\u201c\u6210\u5144\u600e\u6837\u4e86\uff1f\u201d \u3000\u3000\u8463\u65b9\u9eef\u7136\u5e94\u9053\uff1a\u201c\u4f24\u5f97\u5f88\u91cd\u3002\u201d \u3000\u3000\u5b97\u58eb\u4e49\u9053\uff1a\u201c\u54b1\u4eec\u9000\u5427\uff01\u201d\u8f6c\u8eab\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u8463\u65b9\u62b1\u7740\u6210\u7384\u901a\uff0c\u7d27\u968f\u5728\u5b97\u58eb\u4e49\u7684\u8eab\u540e\uff0c\u884c\u51fa\u4e86\u7a0b\u5bb6\u5e84\u3002 \u3000\u3000\u77f3\u4fca\u56e0\u90d1\u5927\u521a\u4f24\u52bf\u8fc7\u91cd\uff0c\u65e9\u5df2\u65e0\u5fc3\u7559\u610f\u654c\u60c5\uff0c\u90a3\u957f\u886b\u4eba\u7adf\u4e5f\u672a\u7559\u96be\u4e94\u9b3c\uff0c\u773c\u770b\u7740\u4e24\u4eba\u8d70\u53bb\u3002 \u3000\u3000\u90a3\u957f\u886b\u4eba\u76ee\u7779\u4e94\u9b3c\u53bb\u8fdc\uff0c\u7f13\u6b65\u884c\u5230\u90d1\u5927\u521a\u8eab\u4fa7\uff0c\u4f4e\u5934\u77a7\u4e86\u4e24\u773c\uff0c\u6084\u7136\u800c\u9000\u3002 \u3000\u3000\u77f3\u4fca\u6b63\u5168\u795e\u8d2f\u6ce8\u4ee5\u672c\u8eab\u529f\u529b\uff0c\u653b\u5165\u90a3\u90d1\u5927\u521a\u7684\u5185\u8151\u4e4b\u4e2d\uff0c\u50ac\u52a8\u4ed6\u90a3\u5185\u8151\u771f\u6c14\uff0c\u7adf\u4e0d\u77e5\u4e94\u9b3c\u548c\u90a3\u957f\u886b\u4eba\u79bb\u53bb\u3002 \u3000\u3000\u90d1\u5927\u521a\u91cd\u4f24\u4e4b\u4f59\uff0c\u4ecd\u7136\u62fc\u5c3d\u4f59\u529b\uff0c\u8fde\u4f24\u5f3a\u6545\uff0c\u4f7f\u5267\u6bd2\u8fc5\u5feb\u7684\u6563\u5e03\u5f00\u53bb\u3002 \u3000\u3000\u77f3\u4fca\u8fde\u756a\u65bd\u6551\uff0c\u4f46\u90d1\u5927\u521a\u4e00\u76f4\u662f\u660f\u8ff7\u4e0d\u9192\uff0c\u4e0d\u7981\u5927\u6025\uff0c\u53ea\u597d\u653e\u5f00\u624b\u7ad9\u8d77\u8eab\u5b50\u3002 \u3000\u3000\u8f6c\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u5f3a\u654c\u548c\u90a3\u957f\u886b\u4eba\u90fd\u5df2\u8d70\u53bb\uff0c\u6574\u4e2a\u9662\u843d\u4e2d\uff0c\u663e\u5f97\u4e00\u7247\u51c4\u6e05\uff0c\u53ea\u4f59\u4e0b\u4e8c\u9b3c\u7684\u4e00\u5177\u5c38\u4f53\u3002 \u3000\u3000\u4fa7\u8eab\u9759\u542c\uff0c\u5df2\u4e0d\u95fb\u6253\u6597\u4e4b\u58f0\u3002 \u3000\u3000\u77f3\u4fca\u8f7b\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u7a0b\u5e84\u4e3b\u3002\u201d \u3000\u3000\u8bdd\u58f0\u752b\u843d\uff0c\u77a5\u89c1\u7a0b\u5b50\u671b\u6267\u7740\u4e00\u76cf\u706f\u7b3c\uff0c\u4e00\u8ddb\u4e00\u8ddb\u7684\u8d70\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u6562\u60c5\u7a0b\u5b50\u671b\u4e5f\u53d7\u4e86\u4f24\u3002 \u3000\u3000\u77f3\u4fca\u5fc3\u4e2d\u672c\u6765\u5145\u6ee1\u7740\u6012\u706b\uff0c\u4f46\u89c1\u7a0b\u5b50\u671b\u62b1\u4f24\u6267\u706f\u800c\u6765\uff0c\u5fc3\u4e2d\u6012\u706b\u518d\u4e5f\u53d1\u4e0d\u51fa\u53bb\uff0c\u4e00\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u7a0b\u5e84\u4e3b\u4e5f\u53d7\u4e86\u4f24\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u533a\u533a\u4e00\u4e9b\u5c0f\u4f24\uff0c\u5df2\u4e0d\u788d\u4e8b\uff0c\u90d1\u5927\u4fa0\u4f24\u7684\u5f88\u91cd\u5417\uff1f\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u4f24\u5f97\u5f88\u91cd\u2026\u2026\u201d\u5ffd\u7136\u60f3\u8d77\u65b9\u5929\u6210\u6765\uff0c\u63a5\u53e3\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u7684\u4e8c\u5144\u957f\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u65b9\u4e8c\u4fa0\u4e5f\u53d7\u4e86\u4e00\u70b9\u4f24\uff0c\u6b63\u5728\u8fd0\u6c14\u8c03\u606f\u3002\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u654c\u4eba\u5462\uff1f\u90fd\u8dd1\u4e86\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u90fd\u4f24\u5728\u4e86\u4e8c\u4fa0\u7684\u5251\u4e0b\uff0c\u4e00\u4e2a\u4f24\u91cd\u800c\u6b7b\uff0c\u53e6\u4e00\u4e2a\u88ab\u56da\u7981\u5ba4\u4e2d\u3002\u201d \u3000\u3000\u77f3\u4fca\u5fc3\u4e2d\u867d\u6025\uff0c\u5374\u4e5f\u65e0\u53ef\u8010\u4f55\uff0c\u957f\u957f\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e8c\u54e5\u672c\u7cbe\u533b\u9053\uff0c\u60f3\u4e0d\u5230\u4ed6\u4e5f\u53d7\u4e86\u4f24\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u4e8b\u5df2\u81f3\u6b64\uff0c\u77f3\u4e09\u4fa0\u6025\u4e5f\u65e0\u7528\uff0c\u8fd8\u662f\u5148\u628a\u90d1\u5927\u4fa0\u62b1\u5165\u9662\u5185\u518d\u8bf4\u3002\u201d\u77f3\u4fca\u62b1\u8d77\u90d1\u5927\u521a\uff0c\u968f\u5728\u7a0b\u5b50\u671b\u8eab\u540e\uff0c\u884c\u5165\u9662\u5185\uff0c\u4e00\u9762\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u7a0b\u5144\uff0c\u5e26\u6211\u53bb\u770b\u770b\u4e8c\u54e5\u7684\u4f24\u52bf\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u5e26\u7740\u77f3\u4fca\u884c\u5165\u5385\u4e2d\uff0c\u53ea\u89c1\u65b9\u5929\u6210\u6b63\u5728\u95ed\u76ee\u8fd0\u6c14\u8c03\u606f\u3002 \u3000\u3000\u77f3\u4fca\u89c1\u4ed6\u6b63\u5728\u8fd0\u6c14\uff0c\u4e0d\u6562\u60ca\u6270\uff0c\u518d\u671b\u671b\u91cd\u4f24\u7684\u90d1\u5927\u521a\uff0c\u4e0d\u7981\u6697\u7136\u6cea\u4e0b\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u51dd\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u90d1\u5927\u521a\u53cc\u76ee\u7d27\u95ed\uff0c\u5f62\u52bf\u5371\u6b86\uff0c\u5944\u5944\u4e00\u606f\uff0c\u4e0d\u7981\u4e00\u58f0\u957f\u53f9\u3002 \u3000\u3000\u76d8\u5750\u5728\u8fd0\u606f\u7684\u65b9\u5929\u6210\uff0c\u7a81\u7136\u7741\u5f00\u53cc\u76ee\uff0c\u671b\u4e86\u77f3\u4fca\u4e00\u773c\uff0c\u9053\uff1a\u201c\u8001\u4e09\uff0c\u5927\u54e5\u53d7\u4e86\u4f24\uff1f\u201d \u3000\u3000\u77f3\u4fca\u6697\u7136\u8bf4\u9053\uff1a\u201c\u662f\u7684\uff0c\u800c\u4e14\u4f24\u5f97\u5f88\u91cd\uff0c\u4e8c\u54e5\u6b63\u5728\u8fd0\u6c14\u8c03\u606f\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u63a5\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u600e\u4e48\u4e0d\u53eb\u6211\uff1f\u201d \u3000\u3000\u77f3\u4fca\u63a5\u9053\uff1a\u201c\u5c0f\u5f1f\u4e0d\u6562\u60ca\u6270\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u51b7\u54fc\u4e00\u58f0\uff0c\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u7cca\u6d82\u201d\uff0c\u5927\u884c\u4e24\u6b65\uff0c\u5230\u4e86\u77f3\u4fca\u8eab\u65c1\uff0c\u6301\u9053\uff1a \u3000\u3000\u201c\u5927\u54e5\u4f24\u5728\u4f55\u5904\uff1f\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u4f24\u5728\u808b\u95f4\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u5feb\u653e\u4ed6\u4e0b\u6765\u3002\u201d \u3000\u3000\u76ee\u5149\u8f6c\u5230\u7a0b\u5b50\u671b\u8eab\u4e0a\uff0c\u9053\uff1a\u201c\u6709\u52b3\u7a0b\u5927\u4fa0\uff0c\u5429\u5490\u4ed6\u4eec\u591a\u71c3\u51e0\u679d\u706f\u706b\uff0c\u6108\u4eae\u6108\u597d\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u5e94\u4e86\u4e00\u58f0\uff0c\u6025\u6b65\u9000\u4e86\u56de\u53bb\u3002 \u3000\u3000\u7247\u523b\u4e4b\u540e\u4e24\u4e2a\u5973\u5a62\u5404\u6367\u7740\u4e24\u652f\u5de8\u706f\u800c\u5165\u3002 \u3000\u3000\u56db\u652f\u5de8\u5927\u7684\u706b\u70db\uff0c\u4e00\u9f50\u71c3\u8d77\uff0c\u7167\u7684\u5385\u4e2d\u4e00\u7247\u901a\u660e\u3002 \u3000\u3000\u65b9\u5929\u6210\u8f6c\u8fc7\u90d1\u5927\u521a\u7684\u8eab\u5b50\uff0c\u627e\u7740\u4f24\u53e3\uff0c\u63a2\u624b\u5f80\u6000\u4e2d\u53d6\u51fa\u4e00\u628a\u94f6\u8272\u7684\u94b3\u5b50\uff0c\u5939\u4e86\u4e8c\u679a\u5b50\u5348\u8ffd\u9b42\u9489\u51fa\u6765\u3002\u9ad8\u9ad8\u4e3e\u8d77\uff0c\u5728\u70db\u706b\u4e0b\u7167\u4e86\u4e00\u8f6c\uff0c\u9053\uff1a\u201c\u5267\u6bd2\u6dec\u70bc\u7684\u6697\u5668\u3002\u201d \u3000\u3000\u653e\u4e0b\u5b50\u5348\u8ffd\u9b42\u9489\uff0c\u4ece\u6000\u4e2d\u6478\u51fa\u4e00\u4e2a\u7389\u74f6\uff0c\u62d4\u5f00\u74f6\u53e3\uff0c\u5012\u4e86\u4e00\u7c92\u4e39\u4e38\uff0c\u9001\u5165\u90d1\u5927\u521a\u7684\u53e3\u4e2d\uff0c\u9053\uff1a\u201c\u6709\u5f00\u6c34\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u53cc\u624b\u6367\u7740\u4e00\u676f\u5f00\u6c34\uff0c\u9012\u4e86\u8fc7\u6765\u9053\uff1a\u201c\u65e9\u5df2\u5907\u597d\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u63a5\u8fc7\u5f00\u6c34\uff0c\u634f\u5f00\u90d1\u5927\u521a\u7684\u7259\u5173\uff0c\u51b2\u4e0b\u4e39\u836f\u3002 \u3000\u3000\u77f3\u4fca\u8138\u7684\u60f6\u6025\u4e4b\u60c5\uff0c\u5fd9\u95ee\u9053\uff1a\u201c\u4e8c\u54e5\uff0c\u5927\u54e5\u7684\u4f24\u52bf\u6709\u6551\u5417\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u76ee\u4e0b\u5c0f\u5144\u4e5f\u4e0d\u6562\u65ad\u8a00\u2026\u2026\u201d\u8f7b\u8f7b\u5730\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6210\u7384\u901a\u5462\uff1f\u201d \u3000\u3000\u77f3\u5b50\u4fca\u9053\uff1a\u201c\u9000\u8d70\u4e86\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u53ef\u662f\u5927\u54e5\u8d1f\u4f24\u4e4b\u540e\uff0c\u5f3a\u5fcd\u4f24\u75be\uff0c\u51fb\u9000\u5f3a\u654c\u5417\uff1f\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u6709\u4e00\u4f4d\u8499\u9762\u4eba\uff0c\u9661\u7136\u51fa\u73b0\u76f8\u52a9\uff0c\u51fb\u9000\u4e86\u6210\u7384\u901a\u548c\u5b97\u58eb\u4e49\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u63a5\u9053\uff1a\u201c\u5728\u4e0b\u770b\u5230\u4e00\u5177\u6b7b\u5c38\uff0c\u4f3c\u662f\u4e94\u9b3c\u7684\u8001\u4e8c\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u90a3\u5f88\u597d\uff0c\u5feb\u4e9b\u628a\u4ed6\u7684\u5c38\u4f53\u62b1\u6765\u3002\u201d\u77f3\u4fca\u8f6c\u8eab\u4e00\u8dc3\uff0c\u98de\u51fa\u5927\u5385\uff0c\u7247\u523b\u4e4b\u540e\uff0c\u62b1\u6765\u4e86\u4e8c\u9b3c\u7684\u5c38\u4f53\u3002 \u3000\u3000\u65b9\u5929\u6210\u5728\u4e8c\u9b3c\u5c38\u4f53\u4e4b\u4e0a\uff0c\u4ed4\u7ec6\u641c\u67e5\u4e00\u9635\uff0c\u627e\u51fa\u4e00\u4e2a\u7389\u74f6\u548c\u5341\u4f59\u679a\u5b50\u5348\u8ffd\u9b42\u9489\u6765\uff0c\u70b9\u5934\u8bf4\u9053\uff1a\u201c\u5927\u54e5\u8eab\u4e0a\u94b3\u51fa\u7684\u5b50\u5348\u8ffd\u9b42\u9489\uff0c\u548c\u4e8c\u9b3c\u65bd\u7684\u6697\u5668\u4e00\u822c\u6a21\u6837\uff0c\u663e\u7136\u5927\u54e5\u662f\u4f24\u5728\u6b64\u4eba\u7684\u624b\u4e2d\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u4ed6\u65e2\u4f7f\u7528\u8fd9\u7b49\u6697\u5668\uff0c\u60f3\u5fc5\u5e26\u6709\u89e3\u836f\u4e86\uff01\u201d \u3000\u3000\u4f38\u624b\u53d6\u8fc7\u4e24\u4e2a\u7389\u74f6\uff0c\u62e8\u5f00\u74f6\u53e3\uff0c\u4e00\u4e2a\u74f6\u4e2d\uff0c\u5012\u51fa\u4e00\u7c92\u4e39\u4e38\uff0c\u653e\u5728\u624b\u5fc3\u4e4b\u4e0a\u3002 \u3000\u3000\u706b\u4e0b\u53ea\u89c1\u90a3\u4e24\u7c92\u4e39\uff0c\u989c\u8272\u4e0d\u540c\uff0c\u4e00\u548c\u7ea2\uff0c\u4e00\u4e2a\u96ea\u767d\uff0c\u4f46\u5927\u5c0f\u5374\u662f\u4e00\u822c\u3002 \u3000\u3000\u65b9\u5929\u6210\u4e3e\u8d77\u624b\u4e2d\u4e24\u79cd\u836f\u7269\uff0c\u4ed4\u7ec6\u77a7\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u8fd9\u4e24\u79cd\u989c\u8272\u4e0d\u540c\u7684\u836f\u4e38\uff0c\u4e00\u7c92\u53ef\u80fd\u662f\u6bd2\u4e38\uff0c\u4e00\u7c92\u53ef\u80fd\u662f\u89e3\u836f\uff0c\u4f46\u5b83\u5916\u9762\u5374\u7528\u814a\u5305\u8d77\uff0c\u4e00\u65f6\u95f4\u5f88\u96be\u5206\u8fa9\uff0c\u4e07\u4e00\u7528\u9519\uff0c\u90a3\u624d\u662f\u5927\u6068\u5927\u61be\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u4f46\u5927\u54e5\u7684\u4f24\u52bf\u5f88\u91cd\uff0c\u53ea\u6015\u6ca1\u6709\u5f88\u591a\u65f6\u95f4\u2026\u2026\u201d \u3000\u3000\u8bdd\u5230\u6b64\u5904\uff0c\u7a81\u7136\u89c9\u5230\u4e0b\u9762\u4e4b\u8a00\uff0c\u5f88\u4e0d\u7965\uff0c\u4f4f\u53e3\u4e0d\u8a00\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u6c89\u541f\u4e86\u4e00\u9635\u9053\uff1a\u201c\u4e3a\u4e86\u6551\u90d1\u5927\u4fa0\u6027\u547d\uff0c\u8001\u673d\u5012\u6709\u4e00\u4e2a\u6cd5\u5b50\uff0c\u4f46\u4e0d\u77e5\u6b64\u6cd5\u662f\u5426\u592a\u8fc7\u6b8b\u5fcd\u3002\u201d \u3000\u3000\u77f3\u5b50\u4fca\u9053\uff1a\u201c\u4ec0\u4e48\u6cd5\u5b50\uff1f\u5feb\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u8001\u673d\u4e4b\u610f\uff0c\u63d0\u51fa\u5b97\u516c\u5b50\uff0c\u8981\u4ed6\u5206\u8fa8\u51fa\u8fd9\u4e24\u79cd\u836f\u7269\uff0c\u90a3\u662f\u6bd2\u4e38\uff0c\u90a3\u662f\u89e3\u836f\uff0c\u4f46\u5982\u5b58\u5fc3\u76f8\u5bb3\uff0c\u5148\u8981\u81ea\u8eab\u4e2d\u6bd2\u3002\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u8fd9\u6cd5\u5b50\u5f88\u597d\uff0c\u5feb\u53bb\u5e26\u4ed6\u51fa\u6765\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u5306\u5306\u800c\u53bb\uff0c\u7247\u523b\u540e\u5e26\u4e86\u5b97\u8d35\u800c\u81f3\u3002 \u3000\u3000\u5b97\u8d35\u88ab\u56da\u5c45\u5730\u4e0b\u5bc6\u5ba4\u4e4b\u4e2d\uff0c\u5bf9\u5916\u9762\u7684\u5267\u70c8\u6076\u6597\u5e76\u672a\u89c9\u5230\u3002\u4f46\u4ed6\u76ee\u5149\u77a7\u5230\u4e8c\u9b3c\u6b7b\u5c38\uff0c\u548c\u4ef0\u5728\u5730\u4e0a\u7684\u90d1\u5927\u521a\uff0c\u5df2\u89c9\u60c5\u5f62\u4e0d\u5bf9\uff0c\u662f\u4ee5\u62a2\u5148\u95ee\u9053\uff1a\u201c\u5bb6\u7236\u6765\u8fc7\u4e86\uff1f\u201d \u3000\u3000\u77f3\u4fca\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u6765\u8fc7\u4e86\uff0c\u4f46\u4ed6\u4eec\u8d25\u9000\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u90d1\u5927\u4fa0\u53d7\u4e86\u4e94\u9b3c\u6dec\u6bd2\u6697\u5668\u6240\u7b97\uff0c\u54b1\u4eec\u8bf7\u5b97\u516c\u5b50\u5e2e\u4e00\u4e2a\u5fd9\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4e8c\u3001\u4e09\u3001\u56db\u9b3c\u90fd\u5df2\u6b7b\u4e8e\u6b64\uff0c\u53ea\u9003\u4e86\u6210\u7384\u901a\u3001\u8463\u65b9\u548c\u4ee4\u5c0a\u4e09\u4eba\uff0c\u5b97\u516c\u5b50\u5982\u82e5\u4e0d\u4fe1\uff0c\u73b0\u6709\u6b7b\u5c38\u4e3a\u8bc1\u3002\u201d \u3000\u3000\u5b97\u516c\u5b50\u76ee\u7779\u4e8c\u9b3c\u6b7b\u5c38\uff0c\u5fc3\u4e2d\u80c6\u5b50\u5df2\u5bd2\uff0c\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u4e0d\u77e5\u8981\u5728\u4e0b\u5e2e\u4ec0\u4e48\u5fd9\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u8fd9\u836f\u4e38\u662f\u4e8c\u9b3c\u8eab\u4e0a\u641c\u51fa\uff0c\u6211\u60f3\u8fd9\u4e24\u79cd\u989c\u8272\u4e4b\u4e2d\uff0c\u6709\u4e00\u79cd\u89e3\u836f\uff0c\u4e00\u79cd\u6bd2\u836f\uff0c\u5b97\u516c\u5b50\u5e38\u5e74\u548c\u4e94\u9b3c\u5728\u4e00\u8d77\uff0c\u5b9a\u7136\u53ef\u4ee5\u6653\u5f97\u4e86\uff1f\u201d \u3000\u3000\u77f3\u4fca\u63a5\u9053\uff1a\u201c\u6709\u4e00\u4ef6\u4e8b\uff0c\u5728\u4e0b\u5fc5\u5148\u8bf4\u660e\u767d\uff0c\u5982\u82e5\u5b97\u516c\u5b50\u60f3\u6b3a\u9a97\u6211\u7b49\uff0c\u53ea\u6015\u662f\u81ea\u6211\u82e6\u5403\uff0c\u56e0\u6b64\uff0c\u5b97\u516c\u5b50\u6307\u660e\u89e3\u836f\u4e4b\u540e\uff0c\u54b1\u4eec\u8981\u5b97\u516c\u5b50\u5148\u7528\u4e00\u7c92\u3002\u201d \u3000\u3000\u5b97\u8d35\u76ee\u5149\u671b\u7740\u90a3\u4e24\u7c92\u4e39\u4e38\uff0c\u77a7\u770b\u4e86\u826f\u4e45\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u6240\u77e5\uff0c\u7ea2\u8272\u7684\u662f\u89e3\u6bd2\u836f\u7269\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4f9d\u636e\u5e38\u60c5\uff0c\u767d\u8272\u624d\u662f\u89e3\u836f\uff0c\u4f46\u4e94\u9b3c\u4e3a\u4eba\u9634\u9669\uff0c\u53eb\u4eba\u5f88\u96be\u63e3\u6d4b\u4e86\u3002\u201d \u3000\u3000\u77f3\u4fca\u968f\u624b\u62ff\u8d77\u4e86\u4e00\u7c92\u7ea2\u8272\u7684\u4e39\u4e38\uff0c\u9053\uff1a\u201c\u5b97\u516c\u5b50\u65e2\u7136\u8ba4\u4e3a\u8fd9\u7ea2\u8272\u7684\u4e39\u4e38\u662f\u89e3\u836f\uff0c\u90a3\u5c31\u8bf7\u5148\u670d\u7528\u4e00\u7c92\u5982\u4f55\uff1f\u201d \u3000\u3000\u5b97\u8d35\u9053\uff1a\u201c\u5728\u4e0b\u5982\u4e0d\u670d\u7528\uff0c\u90a3\u4e5f\u662f\u4e0d\u6210\u4e86\u3002\u201d\u5f53\u4e0b\u5f20\u53e3\u4ee5\u5f85\u3002 \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u5982\u82e5\u8fd9\u7ea2\u8272\u7684\u836f\u4e38\u662f\u6bd2\u836f\uff0c\u4f60\u5c31\u53bb\u627e\u4f60\u90a3\u4f4d\u9b3c\u53d4\u7236\u7b97\u8d26\u3002\u201d\u53f3\u4e0b\u4e00\u63a8\uff0c\u628a\u836f\u4e38\u63a8\u5165\u4e86\u5b97\u8d35\u7684\u53e3\u4e2d\u3002 \u3000\u3000\u5b97\u8d35\u5927\u7ea6\u5fc3\u4e2d\u77e5\u6653\u90a3\u7ea2\u8272\u4e38\u836f\u4e0d\u662f\u6bd2\u7269\uff0c\u7acb\u65f6\u54bd\u4e86\u4e0b\u53bb\uff0c\u95ed\u4e0a\u53cc\u76ee\uff0c\u76d8\u819d\u800c\u5750\u3002 \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u4e0d\u8bb8\u8fd0\u6c14\u62d2\u6297\uff0c\u8981\u90a3\u6bd2\u836f\u5c3d\u5feb\u53d1\u4f5c\u3002\u201d \u3000\u3000\u5927\u7ea6\u8fc7\u4e86\u4e00\u676f\u8336\u7684\u5de5\u592b\uff0c\u77f3\u4fca\u5fcd\u4e0d\u4f4f\u5f00\u53e3\u8bf4\u9053\uff1a\u201c\u4f60\u6709\u4f55\u611f\u89c9\uff1f\u201d \u3000\u3000\u5b97\u8d35\u7741\u5f00\u773c\u775b\uff0c\u9053\uff1a\u201c\u6211\u5f88\u597d\u3002\u201d \u3000\u3000\u4e00\u76f4\u51b7\u773c\u65c1\u89c2\u7684\u65b9\u5929\u6210\uff0c\u9053\uff1a\u201c\u5927\u6982\u662f\u4e0d\u4f1a\u9519\u4e86\uff0c\u53ef\u4ee5\u7ed9\u5927\u54e5\u670d\u7528\u4e00\u7c92\u8bd5\u8bd5\uff1f\u201d\u5012\u51fa\u4e00\u7c92\u7ea2\u8272\u7684\u4e39\u4e38\uff0c\u9001\u5165\u5373\u5927\u521a\u7684\u53e3\u4e2d\u3002 \u3000\u3000\u77f3\u4fca\u53d6\u8fc7\u4e00\u676f\u5f00\u6c34\uff0c\u51b2\u4e0b\u90d1\u5927\u521a\u53e3\u4e2d\u4e39\u4e38\uff0c\u653e\u4e0b\u74f6\u7897\uff0c\u9053\uff1a\u201c\u4e8c\u54e5\uff0c\u73b0\u5728\u5e94\u8be5\u5982\u4f55\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u7b49\u7b49\u770b\uff0c\u5982\u82e5\u5927\u54e5\u670d\u836f\u4e0d\u9519\uff0c\u4ee5\u5927\u54e5\u7684\u6df1\u539a\u7684\u529f\u529b\u800c\u8a00\uff0c\u53ef\u80fd\u4f1a\u6e05\u9192\u8fc7\u6765\uff0c\u5982\u662f\u670d\u9519\u836f\u7269\uff0c\u4ea6\u6709\u6fc0\u70c8\u7684\u53cd\u5e94\u3002\u201d \u3000\u3000\u539f\u6765\uff0c\u65b9\u5929\u6210\u7ec6\u67e5\u90d1\u5927\u521a\u7684\u75c5\u52bf\uff0c\u89c9\u7740\u4ed6\u867d\u7136\u6c14\u606f\u5fae\u5f31\uff0c\u4f46\u5fc3\u810f\u4e00\u76f4\u8df3\u52a8\u5f97\u5341\u5206\u5747\u5300\uff0c\u77e5\u4ed6\u5728\u91cd\u4f24\u4e4b\u540e\uff0c\u7528\u4e00\u53e3\u771f\u5143\u4e4b\u6c14\u62a4\u4f4f\u4e86\u5185\u810f\uff0c\u4e0d\u8ba9\u6bd2\u6027\u4fb5\u5165\uff0c\u53ea\u8981\u8fd9\u836f\u7269\u7528\u5f97\u4e0d\u9519\uff0c\u5f88\u5feb\u5c31\u4f1a\u6e05\u9192\u8fc7\u6765\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u77f3\u4fca\u3001\u65b9\u5929\u6210\u3001\u7a0b\u5b50\u671b\u7b49\u516d\u9053\u76ee\u5149\uff0c\u4e00\u76f4\u76ef\u6ce8\u5728\u90d1\u5927\u521a\u8138\u4e0a\u770b\u7740\u4ed6\u7684\u53cd\u5e94\uff0c\u5374\u4e0d\u77e5\u5385\u95e8\u53e3\u5904\uff0c\u6b63\u7ad9\u7740\u4e00\u4e2a\u8eab\u7740\u957f\u886b\uff0c\u9ed1\u5e03\u5305\u8138\u7684\u4eba\uff0c\u4e24\u9053\u70af\u70af\u7684\u76ee\u5149\uff0c\u4e5f\u51dd\u6ce8\u5728\u90d1\u5927\u521a\u8138\u4e0a\u77a7\u7740\u3002 \u3000\u3000\u53ea\u89c1\u90d1\u5927\u521a\u53e3\u89d2\u6380\u52a8\uff0c\u8f7b\u8f7b\u5730\u5410\u4e00\u53e3\u6c14\uff0c\u7f13\u7f13\u7741\u5f00\u4e86\u53cc\u76ee\u3002 \u3000\u3000\u77f3\u4fca\u60c5\u7eea\u6fc0\u52a8\u5730\u53eb\u9053\uff1a\u201c\u5927\u54e5\u9192\u8fc7\u6765\u4e86\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u5634\u89d2\u5fae\u52a8\uff0c\u6de1\u6de1\u4e00\u7b11\u3002 \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4e0d\u8981\u60ca\u6270\u5927\u54e5\u3002\u201d \u3000\u3000\u77f3\u4fca\u8f7b\u8f7b\u5e94\u4e86\u4e00\u58f0\uff0c\u4e0d\u518d\u8a00\u8bed\u3002 \u3000\u3000\u8fd9\u5f53\u513f\uff0c\u90a3\u7ad9\u5728\u5385\u95e8\u53e3\u5904\u7684\u4eba\u5f71\uff0c\u7a81\u7136\u8f6c\u8fc7\u8eab\u5b50\uff0c\u6084\u7136\u800c\u4ed6\u7684\u6b65\u5c65\uff0c\u8f7b\u82e5\u65e0\u7269\uff0c\u672a\u53d1\u51fa\u4e00\u70b9\u58f0\u606f\uff0c\u5ba4\u4e2d\u7684\u4eba\u5bf9\u4ed6\u7684\u51fa\u73b0\u3001\u79bb\u53bb\uff0c\u5168\u65e0\u6240\u89c9\u3002 \u3000\u3000\u65b9\u5929\u6210\u8f7b\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u8bf4\uff1a\u201c\u5927\u54e5\uff0c\u662f\u5426\u8981\u5c0f\u5f1f\u52a9\u4f60\u8fd0\u6c14\u884c\u529f\uff1f\u201d\u90d1\u5927\u540c\u6447\u6447\u5934\uff0c\u53e3\u9f7f\u542f\u52a8\uff0c\u4f3c\u662f\u60f3\u8bf4\u8bdd\u3002 \u3000\u3000\u65b9\u5929\u6210\u6025\u6025\u7684\u63a5\u9053\uff1a\u201c\u5927\u54e5\u4e0d\u7528\u8bf4\u8bdd\uff0c\u5c0f\u5f1f\u5df2\u77e5\u5927\u54e5\u4e0d\u7528\u6211\u4eec\u5e2e\u52a9\u4e86\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u957f\u957f\u7684\u53f9\u4e86\u53e3\u6c14\uff0c\u95ed\u4e0a\u53cc\u76ee\u3002 \u3000\u3000\u65b9\u5929\u6210\u56de\u5934\u671b\u4e86\u5b97\u8d35\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5b97\u4e16\u5144\uff0c\u611f\u89c9\u5982\u4f55\uff1f\u201d \u3000\u3000\u5b97\u8d35\u9053\uff1a\u201c\u6211\u5f88\u597d\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u770b\u6765\u4f60\u5fc3\u4e2d\u6709\u4e9b\u628a\u63e1\uff0c\u5e76\u975e\u662f\u968f\u53e3\u80e1\u8a00\u3002\u201d \u3000\u3000\u5b97\u8d35\u9053\uff1a\u201c\u53ea\u80fd\u8bf4\u90d1\u5927\u4fa0\u7684\u547d\u5927\uff0c\u5728\u4e0b\u4e5f\u4e0d\u8be5\u6b7b\uff0c\u6240\u4ee5\u88ab\u5728\u4e0b\u731c\u5bf9\u4e86\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u4e86\u89e3\u90a3\u5251\u95e8\u4e94\u9b3c\u7684\u4e3a\u4eba\u3002\u201d \u3000\u3000\u5b97\u8d35\u9053\uff1a\u201c\u90a3\u4e5f\u4e0d\u662f\uff0c\u5f53\u4eca\u4e4b\u4e16\uff0c\u6ca1\u6709\u4e00\u4e2a\u80fd\u591f\u731c\u5230\u5251\u95e8\u4e94\u4e49\u7684\u5904\u4e16\u4e4b\u6cd5\u3002\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u5251\u95e8\u4e94\u9b3c\u5c31\u662f\u5251\u95e8\u4e94\u9b3c\uff0c\u79f0\u4e94\u4e49\u592a\u9ad8\u62ac\u4ed6\u4eec\u4e86\uff0c\u4f55\u51b5\u4e94\u9b3c\u53ea\u4f59\u4e0b\u4e86\u4e24\u9b3c\uff0c\u6b64\u540e\u5e94\u8be5\u6539\u79f0\u5251\u95e8\u4e8c\u9b3c\u4e86\u3002\u201d \u3000\u3000\u5b97\u8d35\u5fc3\u5934\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u77e5\u4ee5\u4e2d\u5dde\u4e09\u4fa0\u7684\u8eab\u4efd\uff0c\u51b3\u7136\u4e0d\u4f1a\u8bf4\u8c0e\uff0c\u4f46\u8868\u9762\u4e0a\u5374\u53c8\u4e0d\u5f97\u4e0d\u4fdd\u6301\u9547\u9759\uff0c\u7f13\u7f13\u5730\u8bf4\u9053\uff1a\u201c\u5bb6\u7236\u5462\uff1f\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u4ee4\u5c0a\u4e5f\u53d7\u4f24\u800c\u53bb\u3002\u201d \u3000\u3000\u5b97\u8d35\u5446\u4e86\u4e00\u5446\u9053\uff1a\u201c\u8bf8\u4f4d\u51c6\u5907\u5982\u4f55\u5904\u7f6e\u5728\u4e0b\uff1f\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u73b0\u5728\u5f88\u96be\u8bf4\u4e86\u3002\u201d \u3000\u3000\u4f46\u95fb\u90d1\u5927\u521a\u957f\u957f\u5410\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u90a3\u4f4d\u76f8\u6551\u54b1\u4eec\u7684\u9ad8\u4eba\u4f55\u5728\uff1f\u201d\u77f3\u4fca\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u5185\u529f\u6df1\u539a\uff0c\u5148\u5f97\u65b9\u5929\u6210\u7ec3\u6210\u7684\u4e39\u4e38\uff0c\u6536\u96c6\u4e86\u5143\u6c14\uff0c\u53c8\u5f97\u4e8c\u9b3c\u8eab\u4e0a\u7684\u89e3\u6bd2\u836f\u7269\u670d\u7528\uff0c\u6b63\u662f\u5bf9\u75c7\u7528\u836f\uff0c\u5947\u6548\u7acb\u89c1\uff0c\u6bd2\u4f24\u5927\u529b\u51cf\u8f7b\uff0c\u5df2\u53ef\u52c9\u5f3a\u8bf4\u8bdd\u3002 \u3000\u3000\u77f3\u4fca\u6025\u6025\u63a5\u9053\uff1a\u201c\u90a3\u6551\u54b1\u4eec\u7684\u8499\u9762\u4eba\u5417\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e0d\u662f\u4ed6\u53ca\u65f6\u73b0\u8eab\uff0c\u6168\u63f4\u65bd\u624b\uff0c\u4eca\u65e5\u4e4b\u5c40\uff0c\u54b1\u4eec\u5144\u5f1f\u7686\u8981\u57cb\u9aa8\u4e8e\u6b64\u3002\u201d \u3000\u3000\u4f46\u95fb\u77f3\u4fca\u5e94\u9053\uff1a\u201c\u4ed6\u8d70\u4e86\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u95ee\u9053\uff1a\u201c\u4ed6\u5230\u4e86\u4f55\u5904\u3002\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u4ed6\u6084\u7136\u800c\u53bb\uff0c\u5c0f\u5f1f\u53c8\u5168\u5fc3\u6ce8\u4e8e\u5927\u54e5\u8eab\u4e0a\uff0c\u6ca1\u6709\u6765\u5f97\u53ca\u95ee\u4ed6\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u4e0d\u518d\u8a00\u8bed\u3002 \u3000\u3000\u65b9\u5929\u6210\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u4e09\u5f1f\uff0c\u662f\u4e00\u4f4d\u4ec0\u4e48\u6837\u7684\u4eba\u7269\uff1f\u201d\u77f3\u4fca\u9053\uff1a\u201c\u8eab\u7740\u957f\u886b\uff0c\u9762\u5305\u9ed1\u7eb1\uff0c\u65e0\u6cd5\u770b\u6e05\u695a\u4ed6\u7684\u771f\u6b63\u9762\u76ee\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4ed6\u4e00\u76f4\u90fd\u672a\u8bf4\u8fc7\u4e00\u53e5\u8bdd\u5417\uff1f\u201d\u77f3\u4fca\u9053\uff1a\u201c\u8bf4\u8fc7\uff0c\u4ed6\u8981\u5c0f\u5f1f\u6551\u4eba\u8981\u7d27\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u6c89\u541f\u4e00\u9635\uff0c\u9053\uff1a\u201c\u5927\u54e5\u5b89\u5fc3\u517b\u4f24\uff0c\u54b1\u4eec\u6162\u6162\u5730\u518d\u8bbf\u67e5\u90a3\u4eba\u7684\u4e0b\u843d\u5c31\u662f\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u7a81\u7136\u53c8\u7741\u5f00\u53cc\u76ee\uff0c\u9053\uff1a\u201c\u7a0b\u5e84\u4e3b\uff0c\u5e84\u4e2d\u4f24\u4ea1\u5f88\u91cd\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u6447\u6447\u5934\u9053\uff1a\u201c\u6709\u4e09\u4f4d\u795e\u5a01\u76f8\u62a4\uff0c\u53ea\u6709\u4e09\u6b7b\u56db\u4f24\uff0c\u5b9e\u5728\u6258\u5929\u4e4b\u5e78\u4e86\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u5fae\u5fae\u4e00\u7b11\u53c8\u95ed\u4e0a\u53cc\u76ee\u3002 \u3000\u3000\u65b9\u5929\u6210\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u7a0b\u5144\uff0c\u8bf7\u51c6\u5907\u4e00\u95f4\u9759\u5ba4\u2026\u2026\u201d \u3000\u3000\u7a0b\u5b50\u671b\u63a5\u9053\uff1a\u201c\u65e9\u5df2\u6253\u626b\u9f50\u5907\uff0c\u5144\u5f1f\u53c8\u6d3e\u4eba\u53bb\u8bf7\u4e86\u4e00\u4f4d\u5927\u592b\u7167\u987e\u90d1\u5927\u4fa0\u7684\u4f24\u52bf\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u6709\u52b3\u7a0b\u5e84\u4e3b\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u4e3e\u624b\u4e00\u6325\uff0c\u53ec\u6765\u4e24\u4e2a\u58ee\u6c49\uff0c\u628a\u90d1\u5927\u521a\u62ac\u5165\u9759\u5ba4\uff0c\u4f4e\u58f0\u5bf9\u65b9\u5929\u6210\u9053\uff1a\u201c\u65b9\u4e8c\u4fa0\uff0c\u8fd8\u6709\u4ec0\u4e48\u5429\u5490\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u770b\u5927\u54e5\u4f24\u52bf\uff0c\u660e\u65e5\u52a8\u8eab\u7684\u884c\u671f\uff0c\u4e0d\u5f97\u4e0d\u7a0d\u4f5c\u5ef6\u957f\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u5982\u662f\u8bf8\u4f4d\u6ca1\u6709\u7d27\u8981\u7684\u4e8b\uff0c\u8fd8\u671b\u80fd\u591a\u7559\u5609\u5b9a\u51e0\u65e5\uff0c\u4e5f\u5bb9\u8001\u673d\u5584\u5c3d\u4e00\u70b9\u5730\u4e3b\u4e4b\u8c0a\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4e8b\u60c5\u867d\u7d27\u6025\uff0c\u4f46\u4e5f\u662f\u4e0d\u5f97\u4e0d\u884c\uff0c\u770b\u5927\u54e5\u4f24\u52bf\u53d8\u5316\u5982\u4f55\uff0c\u518d\u51b3\u5b9a\u884c\u671f\u4e86\u2014\u2014\u201d \u3000\u3000\u76ee\u5149\u4e00\u63a0\u5b97\u8d35\u63a5\u9053\uff1a\u201c\u6682\u65f6\u59d4\u5c48\u9601\u4e0b\uff0c\u518d\u7559\u6b64\u51e0\u65e5\uff0c\u53ea\u8981\u4f60\u80fd\u5b89\u4efd\u5b88\u5df2\uff0c\u6211\u7b49\u51b3\u4e0d\u4f1a\u52a0\u5bb3\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u4e00\u6325\u624b\uff0c\u9053\uff1a\u201c\u5e26\u51fa\u53bb\u3002\u201d \u3000\u3000\u4e24\u4e2a\u6b66\u5e08\u884c\u4e86\u8fdb\u6765\uff0c\u5e26\u8d70\u5b97\u8d35\u3002 \u3000\u3000\u7a0b\u5b50\u4e9b\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5144\u5f1f\u5df2\u5728\u90d1\u5927\u4fa0\u56db\u5468\uff0c\u5e03\u4e0b\u4e25\u5bc6\u7684\u8b66\u6212\uff0c\u4e00\u6709\u98ce\u5439\u8349\u52a8\uff0c\u7acb\u65f6\u4f20\u51fa\u8b66\u8fc5\uff0c\u4e24\u4f4d\u534a\u5bb5\u82e6\u6218\uff0c\u4e5f\u8be5\u4f11\u606f\u4e00\u4e0b\u4e86\uff0c\u6709\u4ec0\u4e48\u4e8b\uff0c\u660e\u5929\u518d\u8c08\u5982\u4f55\uff01\u201d \u3000\u3000\u65b9\u5929\u6210\u6447\u6447\u5934\u9053\uff1a\u201c\u4e0d\u6210\uff0c\u6211\u8fd8\u8981\u770b\u7740\u5927\u54e5\uff0c\u4e07\u4e00\u4ed6\u4f24\u52bf\u6709\u4e86\u53d8\u620f\uff0c\u4e5f\u597d\u53ca\u65f6\u8bbe\u6cd5\u62a2\u6551\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u65b9\u4e8c\u4fa0\u4e5f\u53d7\u4f24\u4e86\u2026\u2026\u201d \u3000\u3000\u65b9\u5929\u6210\u7ad9\u8d77\u8eab\u5b50\u63a5\u9053\uff1a\u201c\u6709\u52b3\u7a0b\u5144\u5e26\u6211\u5230\u5927\u54e5\u7684\u623f\u4e2d\uff0c\u5728\u4e0b\u5c31\u5728\u75c5\u5ba4\u4e2d\u5750\u606f\uff0c\u5982\u662f\u660e\u65e5\u4e2d\u5348\u65f6\u5206\uff0c\u5927\u54e5\u7684\u4f24\u52bf\u8fd8\u65e0\u53d8\u5316\uff0c\u90a3\u5c31\u4e0d\u4f1a\u518d\u6709\u5371\u9669\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u7f13\u7f13\u5730\u8bf4\u9053\uff1a\u201c\u4e09\u4f4d\u7ed3\u4e49\u60c5\u6df1\uff0c\u4e0d\u8ba9\u6843\u56ed\u5144\u5f1f\u4e13\u7f8e\u4e8e\u524d\uff0c\u5144\u5f1f\u662f\u606d\u656c\u4e0d\u5982\u4ece\u547d\u4e86\u3002\u201d\u4e3e\u6b65\u5e26\u8def\u3002\u65b9\u5929\u6210\u3001\u77f3\u4fca\u9c7c\u8d2f\u76f8\u968f\u8eab\u540e\uff0c\u884c\u5165\u4e86\u90d1\u5927\u521a\u7684\u517b\u4f24\u9759\u5ba4\u3002 \u3000\u3000\u65b9\u5929\u6210\u884c\u5165\u9759\u5ba4\uff0c\u56de\u987e\u4e86\u77f3\u4fca\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4e09\u5f1f\u4f60\u53bb\u4f11\u606f\u5427\uff01\u5c0f\u5144\u5728\u6b64\u5df2\u53ef\u5e94\u4ed8\u53d8\u6545\u3002\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u5c0f\u5f1f\u79bb\u6b64\u4e5f\u662f\u96be\u5b89\u6795\u5e2d\uff0c\u5012\u4e0d\u5982\u5728\u6b64\u5949\u966a\u5927\u54e5\u4e86\u3002\u201d \u3000\u3000\u5728\u5c0f\u5385\u4e2d\u5e2d\u5730\u5750\u4e0b\uff0c\u9053\uff1a\u201c\u597d\uff01\u4f60\u65e2\u7136\u60f3\u7559\u5728\u6b64\uff0c\u90a3\u5c31\u5728\u6b64\u5750\u606f\uff0c\u5927\u54e5\u4f24\u52bf\u672a\u6108\u4e4b\u524d\uff0c\u4efb\u4f55\u4e8b\u60c5\u90fd\u5f97\u54b1\u4eec\u81ea\u5df1\u4f5c\u4e3b\u5f20\u5e94\u4ed8\uff0c\u5fc5\u8981\u65f6\u4fdd\u6301\u4f53\u529b\u624d\u6210\u2026\u2026\u201d \u3000\u3000\u6325\u6325\u624b\u65f6\u5bf9\u4e8e\u671b\u9053\uff1a\u201c\u7a0b\u5144\u81ea\u53bb\u6b47\u606f\u5427\uff01\u4e0d\u7528\u7559\u6b64\u7167\u987e\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u4e00\u62b1\u62f3\uff0c\u9053\uff1a\u201c\u8001\u673d\u660e\u65e5\u518d\u6765\u63a2\u770b\u90d1\u5927\u4fa0\u4f24\u52bf\u3002\u201d\u6084\u7136\u9000\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u65b9\u5929\u6210\u3001\u77f3\u4fca\u5c31\u5728\u5c0f\u5385\u4e2d\u76d8\u5750\u8c03\u606f\u3002 \u3000\u3000\u4e00\u5bb5\u65e0\u8bdd\uff0c\u4ea6\u65e0\u4e8b\u6545\u53d1\u751f\uff0c\u6b21\u6668\u5929\u4eae\u65f6\u5206\uff0c\u4e24\u4e2a\u6e05\u79c0\u5973\u5a62\uff0c\u6367\u7740\u65e9\u70b9\u800c\u5165\u3002 \u3000\u3000\u65b9\u5929\u6210\u3001\u77f3\u4fca\u7565\u4e00\u68b3\u6d17\uff0c\u8fdb\u5165\u5185\u5ba4\uff0c\u53ea\u89c1\u90d1\u5927\u521a\u62e5\u88ab\u800c\u7761\uff0c\u7761\u5f97\u5341\u5206\u5730\u9999\u751c\uff0c\u6c14\u606f\u5747\u5300\u3001\u4f24\u52bf\u4f3c\u5df2\u89c1\u597d\u8f6c\u3002 \u3000\u3000\u77f3\u4fca\u867d\u7136\u4e0d\u89e3\u533b\u9053\uff0c\u4f46\u4ed6\u770b\u90a3\u5927\u54e5\u7684\u60c5\u5f62\uff0c\u4f3c\u662f\u6b63\u5728\u5feb\u901f\u590d\u539f\u4e4b\u4e2d\uff0c\u4f4e\u58f0\u95ee\u9053\uff1a \u3000\u3000\u201c\u4e8c\u54e5\uff0c\u5927\u54e5\u7684\u60c5\u5f62\u5f97\u597d\u5427\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u70b9\u70b9\u5934\uff0c\u4f4e\u58f0\u4ed6\u8bf4\u9053\uff1a\u201c\u4ee5\u5927\u54e5\u6df1\u539a\u7684\u529f\u529b\u800c\u8bba\uff0c\u5feb\u5219\u4e24\u65e5\uff0c\u6162\u5219\u4e94\u5929\uff0c\u54b1\u4eec\u5c31\u53ef\u4ee5\u4e0a\u8def\u4e86\uff0c\u53ea\u662f\u2026\u2026\u201d \u3000\u3000\u8fd9\u5f53\u513f\uff0c\u7a81\u7136\u4e00\u9635\u6025\u4fc3\u7684\u6b65\u5c65\u4e4b\u58f0\uff0c\u4f20\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u5b88\u597d\u5927\u54e5\u3002\u201d\u7a81\u7136\u8f6c\u8eab\u4e00\u8dc3\uff0c\u5954\u51fa\u5185\u5ba4\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u3002\u201d \u3000\u3000\u53ea\u542c\u5916\u9762\u6709\u4eba\u5e94\u9053\uff1a\u201c\u5728\u4e0b\u7a0b\u5b50\u671b\u3002\u201d \u3000\u3000\u95e8\u5e18\u542f\u52a8\uff0c\u7a0b\u5b50\u671b\u7f13\u6b65\u884c\u4e86\u8fdb\u6765\uff0c\u624b\u4e2d\u62ff\u7740\u4e00\u4e2a\u5927\u7ea2\u5e16\u5b50\uff0c\u6ee1\u8138\u60f6\u6025\u4e4b\u8272\u3002 \u3000\u3000\u4f46\u4ed6\u4ecd\u7136\u4fdd\u6301\u7740\u9547\u9759\uff0c\u7f13\u7f13\u4ed6\u8bf4\u9053\uff1a\u201c\u90d1\u5927\u4fa0\u7684\u4f24\u52bf\u5982\u4f55\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u5927\u89c1\u597d\u8f6c\u2014\u2014\u201d\uff0c\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u7a0b\u5144\u624b\u4e0a\u62ff\u7684\u4f55\u7269\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u5f88\u5947\u602a\u2014\u2014\u201d \u3000\u3000\u77f3\u4fca\u5927\u6b65\u884c\u4e86\u51fa\u6765\uff0c\u63a5\u9053\uff1a\u201c\u5947\u602a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u4e09\u4f4d\u5230\u559c\u5b9a\u6765\uff0c\u77e5\u6653\u7684\u4eba\u5f88\u591a\u5417\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u6211\u4eec\u5230\u6b64\u4e4b\u884c\uff0c\u65e2\u672a\u62db\u6447\uff0c\u4e5f\u672a\u4fdd\u5bc6\uff0c\u53ef\u662f\u6709\u4eba\u6765\u627e\u6211\u4eec\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u6b63\u662f\u5982\u6b64\uff0c\u8fd9\u91cc\u6709\u5f20\u5e16\u5b50\uff0c\u656c\u8bf7\u65b9\u4e8c\u4fa0\u8fc7\u76ee\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u4f38\u624b\u63a5\u8fc7\u5e16\u5b50\uff0c\u53e3\u4e2d\u5374\u8bf4\u9053\uff1a\u201c\u90a3\u4eba\u6307\u540d\u627e\u6211\u4eec\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6307\u540d\u8981\u627e\u4e2d\u5dde\u4e09\u4fa0\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u5c55\u5f00\u5e16\u5b50\u770b\u4e0a\u53bb\uff0c\u53ea\u89c1\u4e0a\u9762\u5199\u9053\uff1a\u201c\u8fb0\u5dde\u638c\u95e8\uff0c\u8a00\u51e4\u537f\u62dc\u3002\u201d \u3000\u3000\u4e0d\u7981\u76b1\u7709\u5934\u9053\uff1a\u201c\u8a00\u5bb6\u95e8\u7684\u638c\u95e8\u4eba\u3002\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u5947\u602a\u554a\uff0c\u54b1\u4eec\u548c\u8a00\u5bb6\u7d20\u65e0\u5f80\u6765\uff0c\u4ed6\u600e\u4e48\u4f1a\u5343\u91cc\u8fe2\u8fe2\u7684\u6258\u6765\u5609\u5b9a\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4e0d\u8bba\u4ed6\u6765\u610f\u5982\u4f55\uff0c\u4f46\u5927\u54e5\u6b64\u65f6\u6b63\u5728\u517b\u606f\u4f24\u52bf\uff0c\u54b1\u4eec\u4e0d\u80fd\u5728\u6b64\u89c1\u4ed6\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u70b9\u70b9\u5934\u9053\uff1a\u201c\u6709\u52b3\u7a0b\u5e84\u4e3b\u4ee3\u4e3a\u4f20\u8fbe\u4e00\u58f0\uff0c\u8bf7\u90a3\u8a00\u638c\u95e8\u4eba\u7a0d\u5019\u7247\u523b\uff0c\u5c31\u8bf4\u6211\u7b49\u66f4\u8863\u4e4b\u540e\uff0c\u7acb\u65f6\u606d\u8fce\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u8fd9\u4e2a\u5144\u5f1f\u81ea\u4f1a\u5e94\u4ed8\uff0c\u4e24\u4f4d\u4e0d\u7528\u8d39\u5fc3\u3002\u201d \u3000\u3000\u6b63\u5f85\u8f6c\u8eab\u800c\u53bb\uff0c\u7a81\u95fb\u90a3\u5185\u5ba4\u4e4b\u4e2d\uff0c\u4f20\u51fa\u90d1\u5927\u521a\u7ec6\u7ec6\u7684\u58f0\u97f3\u9053\uff1a\u201c\u4e0d\u7528\u4e86\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u6380\u5e18\u8fdb\u5165\u5185\u5ba4\uff0c\u9053\uff1a\u201c\u5927\u54e5\u597d\u597d\u517b\u606f\u4f24\u52bf\uff0c\u4e00\u4e9b\u7410\u5c0f\u4e4b\u4e8b\uff0c\u4e0d\u52b3\u5927\u54e5\u5206\u5fc3\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u6211\u90fd\u542c\u5230\u4f60\u4eec\u8c08\u7684\u8bdd\u4e86\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u54b1\u4eec\u548c\u8fb0\u5dde\u8a00\u5bb6\u7d20\u65e0\u6069\u6028\uff0c\u4e5f\u8bb8\u90a3\u8a00\u51e4\u537f\u6e38\u8e2a\u5230\u6b64\uff0c\u95fb\u5f97\u54b1\u4eec\u4e5f\u5728\u6b64\u5730\uff0c\u987a\u9053\u4e00\u4f1a\uff0c\u60f3\u6765\u4e0d\u4f1a\u6709\u4ec0\u4e48\u4e8b\u4e86\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u6447\u6447\u5934\u63a5\u9053\uff1a\u201c\u8a00\u5bb6\u95e8\uff0c\u8fd1\u4e94\u5341\u5e74\u6765\uff0c\u4e03\u6613\u638c\u95e8\uff0c\u4f46\u59cb\u7ec8\u65e0\u6cd5\u91cd\u632f\u6614\u5e74\u5728\u6b66\u6797\u7684\u96c4\u98ce\uff0c\u8fd9\u4f4d\u8a00\u51e4\u537f\u662f\u4e94\u5341\u5e74\u4e2d\u7b49\u516b\u4f4d\u638c\u95e8\u4eba\u4e86\uff0c\u4e5f\u662f\u4e94\u5341\u5e74\u6765\uff0c\u8a00\u5bb6\u4e00\u4f4d\u6770\u51fa\u7684\u4eba\u624d\uff0c\u4ed6\u5e74\u4e8b\u751a\u8f7b\uff0c\u4eca\u5e74\u4e0d\u8fc7\u4e8c\u5341\u56db\u4e94\uff0c\u4f46\u5df2\u5c3d\u5f97\u8a00\u5bb6\u95e8\u7684\u6b66\u529f\u795e\u9ad3\u2026\u2026\u201d \u3000\u3000\u65b9\u5929\u6210\u63a5\u9053\uff1a\u201c\u5927\u54e5\u548c\u4ed6\u8ba4\u8bc6\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u548c\u4ed6\u4eec\u8ba2\u4ea4\u4e24\u5e74\u4e86\uff0c\u4f46\u5374\u4e00\u76f4\u6ca1\u6709\u5bf9\u4f60\u4eec\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u77f3\u4fca\u5fc3\u4e2d\u5927\u611f\u5947\u602a\uff0c\u6697\u9053\uff1a\u201c\u5927\u54e5\u4ec0\u4e48\u4e8b\u90fd\u672a\u6b3a\u7792\u8fc7\u6211\u4eec\uff0c\u600e\u7684\u6b64\u4e8b\u6211\u4eec\u5374\u4e00\u76f4\u4e0d\u77e5\u3002\u201d \u3000\u3000\u4ed6\u5f88\u60f3\u95ee\u4e2a\u660e\u767d\uff0c\u4f46\u8bdd\u5230\u53e3\u8fb9\u65f6\uff0c\u5374\u53c8\u5fcd\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4f46\u95fb\u90d1\u5927\u521a\u9053\uff1a\u201c\u6709\u52b3\u7a0b\u5e84\u4e3b\uff0c\u8bf7\u4ed6\u8fdb\u6765\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u5e94\u4e86\u4e00\u58f0\uff0c\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u65b9\u5929\u6210\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5927\u54e5\u4f24\u52bf\u8fd8\u672a\u5168\u597d\uff0c\u4e0d\u5b9c\u8017\u8d39\u7cbe\u795e\uff0c\u89c1\u90a3\u4f4d\u8a00\u638c\u95e8\u65f6\uff0c\u4e5f\u4e0d\u5ba3\u8c08\u8bdd\u592a\u591a\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u70b9\u70b9\u5934\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u5feb\u4e9b\u6276\u6211\u8d77\u6765\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u5e94\u4e86\u4e00\u58f0\uff0c\u4f38\u51fa\u53cc\u624b\uff0c\u6276\u8d77\u90d1\u5927\u521a\u62e5\u88ab\u800c\u5750\uff0c\u7f13\u7f13\u5730\u8bf4\u9053\uff1a\u201c\u90a3\u8a00\u51e4\u537f\u6765\u4e86\u4e4b\u540e\uff0c\u4f60\u4e5f\u7559\u5728\u6b64\u5730\u3002\u201d \u3000\u3000\u8c08\u8bdd\u4e4b\u95f4\uff0c\u7a0b\u5b50\u671b\u5df2\u5e26\u7740\u8a00\u51e4\u537f\u884c\u5230\u5ba4\u5916\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u8a00\u638c\u95e8\u4eba\u5230\u8bbf\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u8a00\u5144\u5f1f\uff0c\u5feb\u8bf7\u8fdb\u6765\u3002\u201d \u3000\u3000\u53ea\u89c1\u73e0\u5e18\u542f\u52a8\uff0c\u4e00\u4e2a\u8eab\u7740\u9752\u886b\u7684\u5e74\u8f7b\u4eba\uff0c\u7f13\u6b65\u884c\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u65b9\u5929\u6210\u76ee\u5149\u8f6c\u52a8\uff0c\u53ea\u89c1\u6765\u4eba\u5e74\u7ea6\u4e8c\u5341\u56db\u4e94\u5c81\uff0c\u751f\u5f97\u6781\u662f\u82f1\u4fca\uff0c\u5251\u7709\u6717\u76ee\uff0c\u795e\u6e05\u6c14\u95f2\u3002 \u3000\u3000\u4f46\u89c1\u90a3\u4eba\u62a2\u524d\u4e00\u6b65\uff0c\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u90d1\u5144\u53ef\u662f\u4f24\u5728\u4e94\u9b3c\u6697\u5668\u4e4b\u4e0b\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u5fae\u5fae\u9894\u9996\uff0c\u7b11\u9053\uff1a\u201c\u4f24\u52bf\u5df2\u4e0d\u8981\u7d27\uff0c\u4e0d\u7528\u5144\u5f1f\u6302\u5ff5\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u542c\u5f97\u5fc3\u4e2d\u5927\u4e3a\u5947\u602a\uff0c\u6697\u9053\uff1a\u201c\u90a3\u7a0b\u5b50\u671b\u662f\u8001\u4e8e\u4e16\u6545\u4e4b\u4eba\uff0c\u51b3\u7136\u4e0d\u4f1a\u544a\u8bc9\u8a00\u51e4\u537f\u6b64\u7b49\u4e8b\u60c5\uff0c\u4ed6\u53c8\u600e\u4e48\u77e5\u6653\u5462\uff1f\u201d \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u8a00\u638c\u95e8\u4eba\u600e\u77e5\u6211\u90d1\u5927\u54e5\u662f\u4f24\u5728\u4e94\u9b3c\u6697\u5668\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u4e24\u9053\u70af\u70af\u7684\u76ee\u5149\uff0c\u8f6c\u5230\u65b9\u5929\u6210\u7684\u8138\u4e0a\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\uff1f\u2014\u2014\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u5728\u4e0b\u65b9\u4e8c\u5f1f\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u4e00\u62b1\u624b\uff0c\u9053\uff1a\u201c\u95fb\u540d\u4e45\u77e3\uff01\u4eca\u65e5\u6709\u5e78\u4e00\u4f1a\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4e0d\u6562\u5f53\uff0c\u8a00\u5144\u5f1f\u4e00\u6d3e\u638c\u95e8\u4e4b\u5c0a\uff0c\u5728\u4e0b\u7b49\u9ad8\u6500\u4e86\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u90d1\u5144\u5200\u6cd5\uff0c\u7edd\u4e16\u65e0\u4f26\uff0c\u4e94\u9b3c\u51b3\u96be\u4e0e\u4e4b\u5339\u654c\uff0c\u4ed6\u4eec\u552f\u4e00\u80fd\u4f24\u5230\u90d1\u5144\u5f1f\u7684\uff0c\u81ea\u7136\u662f\u6697\u5668\u4e86\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u90fd\u662f\u81ea\u5df1\u670b\u53cb\uff0c\u4e0d\u7528\u5ba2\u5957\u4e86\uff0c\u8a00\u5144\u5f1f\u8fb0\u5dde\u4e8b\u52a1\u7e41\u5fd9\uff0c\u600e\u4f1a\u6709\u6687\u6765\u5609\u5b9a\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u5144\u5f1f\u4e8e\u534a\u5e74\u4e4b\u524d\uff0c\u5df2\u9063\u6d3e\u51fa\u8a00\u5bb6\u95e8\u5341\u4e2a\u7cbe\u660e\u80fd\u5e72\u7684\u5f1f\u5b50\uff0c\u67e5\u8bbf\u90d1\u5144\u5f1f\u884c\u8e2a\uff0c\u597d\u5728\u4f60\u4eec\u4e2d\u5dde\u4e09\u4fa0\u7684\u884c\u8e2a\uff0c\u4e00\u5411\u4e5f\u4e0d\u4fdd\u5bc6\uff0c\u67e5\u8bbf\u5e76\u4e0d\u56f0\u96be\uff0c\u4f46\u90d1\u5144\u7684\u884c\u8e2a\uff0c\u5374\u662f\u5306\u6025\u65e0\u6bd4\uff0c\u4eca\u65e5\u5728\u6b64\u660e\u65e5\u5728\u5f7c\uff0c\u5144\u5f1f\u63a5\u62a5\u4e4b\u540e\uff0c\u4ece\u8fb0\u5dde\u52a8\u8eab\uff0c\u6cbf\u9014\u8ffd\u8e2a\u5230\u6b64\uff0c\u540c\u65f6\u4e5f\u542c\u5f97\u4e94\u9b3c\u5230\u6b64\u6d88\u606f\uff0c\u4f46\u5144\u5f1f\u60f3\u4e0d\u5230\u4e94\u9b3c\u8d76\u6765\u5609\u5b9a\uff0c\u7adf\u662f\u548c\u90d1\u5144\u6709\u5173\uff0c\u5509\uff01\u65e9\u77e5\u5982\u6b64\uff0c\u5144\u5f1f\u4e5f\u53ef\u4e8e\u6628\u5bb5\u8d76\u6765\uff0c\u52a9\u90d1\u5144\u4e00\u81c2\u4e4b\u529b\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8a00\u5bb6\u95e8\u6b63\u5728\u57cb\u9996\u82e6\u7ec3\u6b66\u529f\u4e4b\u671f\uff0c\u5b9e\u4e0d\u5b9c\u548c\u6c5f\u6e56\u4eba\u7ed3\u6028\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u5c0f\u5f1f\u5f88\u5c11\u5728\u6c5f\u6e56\u4e0a\u9732\u9762\uff0c\u8bc6\u6211\u4e4b\u4eba\u4e0d\u591a\uff0c\u60f3\u90a3\u5251\u95e8\u4e94\u9b3c\u4e5f\u672a\u5fc5\u80fd\u8bc6\u5f97\u6211\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4e94\u9b3c\u8bc6\u5e7f\uff0c\u8fb0\u5dde\u8a00\u5bb6\u62f3\u6cd5\uff0c\u53ea\u6015\u662f\u7792\u4ed6\u4e0d\u8fc7\uff0c\u5e78\u597d\u4f60\u6ca1\u6709\u8d76\u6765\u52a9\u62f3\u2026\u2026\u201d \u3000\u3000\u8bed\u5fae\u5fae\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u8a00\u5144\u5f1f\u5343\u91cc\u8ffd\u8e2a\uff0c\u60f3\u5fc5\u6709\u8981\u4e8b\u89c1\u6559\u4e86\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u671b\u671b\u90d1\u5927\u521a\uff0c\u9053\uff1a\u201c\u5144\u5f1f\u88ab\u51e0\u4f4d\u53d4\u4f2f\u4eec\u63a8\u4e3e\u51fa\u6765\uff0c\u638c\u7406\u8a00\u5bb6\u95e8\u6237\uff0c\u5fc3\u4e2d\u65e2\u662f\u60f6\u6050\uff0c\u53c8\u662f\u5174\u594b\uff0c\u60f6\u6050\u7684\u662f\u5144\u5f1f\u5c11\u4e0d\u66f4\u662f\uff0c\u8a00\u5bb6\u95e8\u6ca1\u843d\u4e86\u6570\u5341\u5e74\uff0c\u6211\u6709\u4f55\u80fd\uff0c\u91cd\u632f\u8a00\u5bb6\u95e8\u7684\u96c4\u98ce\uff1f\u5174\u594b\u7684\u662f\uff0c\u8a00\u5bb6\u95e8\u4e2d\u6240\u6709\u7684\u4eba\uff0c\u90fd\u5df2\u7ecf\u5b8c\u5168\u89c9\u9192\uff0c\u8a00\u5bb6\u95e8\u5982\u518d\u8fd9\u6837\u6ca1\u843d\u4e0b\u53bb\uff0c\u53ea\u6015\u8981\u4ece\u6b64\u5728\u6c5f\u6e56\u4e4b\u4e0a\u6d88\u6c89\u4e0d\u89c1\u3002\u56e0\u6b64\u5728\u6211\u5c31\u4efb\u638c\u95e8\u4e4b\u4f4d\u65f6\uff0c\u6388\u6211\u4e00\u79cd\u7279\u6b8a\u7684\u6743\u9650\uff0c\u7ed9\u6211\u5341\u5e74\u65f6\u95f4\uff0c\u8981\u6211\u4f7f\u8a00\u5bb6\u95e8\u8d77\u6b7b\u56de\u751f\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u60f3\u5144\u5f1f\u6709\u6b64\u80fd\u529b\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u82e6\u7b11\u4e00\u4e0b\u9053\uff1a\u201c\u6211\u80a9\u6b64\u91cd\u4efb\uff0c\u5185\u5fc3\u867d\u7136\u60f6\u6050\uff0c\u4f46\u4e5f\u53ea\u597d\u5168\u529b\u4ee5\u8d74\u4e86\u3002\u6211\u81ea\u5df1\u8ba2\u4e86\u4e00\u4e2a\u8ba1\u5212\uff0c\u5148\u627e\u51fa\u539f\u56e0\u4f55\u5728\uff0c\u7136\u540e\u9010\u6b65\u6d88\u9664\u8fd9\u79cd\u539f\u56e0\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u5144\u5f1f\u627e\u51fa\u4e86\u539f\u56e0\u5417\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u627e\u51fa\u6765\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u90a3\u5f88\u597d\uff0c\u539f\u56e0\u4f55\u5728\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u8fd9\uff1a\u201c\u7b2c\u4e00\u4e2a\u539f\u56e0\u662f\uff0c\u6211\u4eec\u8a00\u5bb6\u7684\u7edd\u6280\u5168\u90e8\u5931\u4f20\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u901a\uff1a\u201c\u5168\u90e8\u5931\u4f20\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8a00\u5bb6\u95e8\u7684\u7edd\u6280\u5168\u90e8\u5931\u4f20\uff0c\u800c\u4e14\u90a3\u5931\u4f20\u7684\u6b66\u529f\uff0c\u5df2\u4e0d\u5728\u8a00\u5bb6\u95e8\u4e2d\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u8fd9\u4e2a\u5012\u53eb\u5728\u4e0b\u4e0d\u89e3\u4e86\uff0c\u4f60\u4eec\u8a00\u5bb6\u95e8\u7684\u7edd\u6280\uff0c\u5168\u90e8\u5931\u4f20\uff0c\u800c\u4e14\u53c8\u5168\u4e0d\u5728\u8a00\u5bb6\u95e8\uff0c\u90a3\u6b66\u529f\u73b0\u5728\u4f55\u5904\u5462\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u5947\u602a\u4e8b\u4e5f\u5c31\u5728\u6b64\uff0c\u5144\u5f1f\u6574\u7406\u8a00\u5bb6\u95e8\u6240\u6709\u7684\u6b66\u529f\u5178\u7c4d\uff0c\u627e\u4e0d\u51fa\u8a00\u5bb6\u7edd\u6280\u4f55\u5728\u2026\u2026\u201d \u3000\u3000\u65b9\u5929\u6210\u63a5\u9053\uff1a\u201c\u8a00\u5144\u65e2\u65e0\u6240\u672c\uff0c\u53c8\u5982\u4f55\u77e5\u6653\u8a00\u5bb6\u95e8\u8fd8\u6709\u7edd\u6280\u5931\u4f20\u5462\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u5144\u5f1f\u4ed4\u7ec6\u7814\u7a76\u8a00\u5bb6\u7684\u62f3\u638c\u6b66\u529f\uff0c\u53d1\u89c9\u5176\u95f4\u6bcf\u5230\u53d8\u5316\u7cbe\u5947\u4e4b\u5916\uff0c\u5c31\u6709\u5931\u7f3a\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u90a3\u662f\u4f55\u6545\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u8a00\u5bb6\u95e8\u6570\u5341\u5e74\u65e5\u8d8b\u6ca1\u843d\u7684\u6700\u4e3a\u4e3b\u8981\u539f\u56e0\uff0c\u5f53\u65f6\u5144\u5f1f\u4e5f\u89c9\u5f97\u5341\u5206\u832b\u7136\uff0c\u4f46\u7ecf\u8fc7\u4e09\u4e2a\u6708\u7684\u82e6\u601d\u7814\u7a76\uff0c\u548c\u4e24\u4ee3\u957f\u8f88\u4eec\u7684\u5e2e\u5fd9\uff0c\u8ffd\u672c\u6eaf\u6e90\uff0c\u624d\u67e5\u51fa\u6b64\u4e2d\u539f\u56e0\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u548c\u65b9\u5f00\u6210\uff0c\u8fde\u90a3\u7ad9\u5728\u4e00\u4fa7\u7684\u7a0b\u5b50\u671b\uff0c\u90fd\u542c\u5f97\u4e00\u8138\u832b\u7136\u4e4b\u8272\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u539f\u56e0\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u90a3\u539f\u56e0\u662f\u4e94\u5341\u5e74\u524d\uff0c\u6211\u8a00\u5bb6\u95e8\u6709\u4e00\u4f4d\u638c\u95e8\u4eba\uff0c\u6e38\u8e2a\u6c5f\u6e56\uff0c\u4e09\u5e74\u672a\u5f52\uff0c\u90a3\u65f6\u56e0\u4e3a\u8a00\u5bb6\u95e8\u5728\u6c5f\u6e56\u8fd8\u6709\u4f59\u5a01\uff0c\u638c\u95e8\u4eba\u4e09\u5e74\u95f4\u97f3\u8baf\u5168\u65e0\uff0c\u5b9e\u662f\u4e00\u4ef6\u5341\u5206\u91cd\u5927\u7684\u4e8b\uff0c\u7531\u4ee3\u7406\u638c\u95e8\u4e4b\u4f4d\u7684\u4e00\u4f4d\u957f\u8f88\uff0c\u4f20\u4e0b\u4ee4\u8c15\uff0c\u4e8c\u5341\u540d\u7cbe\u5e72\u5f1f\u5b50\u4e00\u9f50\u51fa\u52a8\uff0c\u627e\u5bfb\u638c\u95e8\u4eba\u7684\u4e0b\u843d\u3002\u201d \u3000\u3000\u8bed\u58f0\u5fae\u5fae\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5c31\u5728\u4e8c\u5341\u4f4d\u5f1f\u5b50\u51fa\u53d1\u540e\u7684\u7b2c\u4e8c\u5929\uff0c\u638c\u95e8\u4eba\u7a81\u7136\u81ea\u884c\u800c\u5f52\u2026\u2026\u201d \u3000\u3000\u65b9\u5929\u6210\u63a5\u9053\uff1a\u201c\u8fd9\u548c\u8d35\u6d3e\u58f0\u8a89\u6709\u4f55\u5173\u7cfb\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u672c\u6d3e\u638c\u95e8\u4eba\u56de\u5230\u6545\u5c45\u4e4b\u540e\uff0c\u5306\u5306\u8349\u4e66\u4e00\u4fe1\uff0c\u5c31\u5306\u5306\u56de\u5230\u623f\u5185\uff0c\u95ed\u95e8\u800c\u5367\uff0c\u76f4\u5230\u7b2c\u4e8c\u5929\u7684\u5348\u65f6\u8fc7\u540e\uff0c\u4ecd\u7136\u6ca1\u6709\u8d77\u6765\uff0c\u90a3\u65f6\u867d\u7136\u6709\u7740\u4e24\u4f4d\u957f\u8001\u5fc3\u4e2d\u6000\u7591\uff0c\u4f46\u8fd8\u672a\u60f3\u5230\u6709\u4ec0\u4e48\u53d8\u5316\uff0c\u76f4\u5230\u5929\u8272\u5165\u591c\uff0c\u672a\u89c1\u5f00\u95e8\uff0c\u624d\u5f15\u8d77\u5927\u5bb6\u5fc3\u4e2d\u4e4b\u7591\uff0c\u7834\u95e8\u800c\u5165\uff0c\u624d\u53d1\u89c9\u638c\u95e8\u5df2\u7ecf\u6b7b\u53bb\u2026\u2026\u201d \u3000\u3000\u65b9\u5929\u6210\u63a5\u9053\uff1a\u201c\u88ab\u4eba\u6697\u4e2d\u6740\u5bb3\u4e86\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u95e8\u7a97\u672a\u52a8\uff0c\u627e\u4e0d\u51fa\u4e00\u4e1d\u88ab\u4eba\u6f5c\u5165\u7684\u75d5\u8ff9\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u90a3\u5c31\u662f\u8bf4\u638c\u95e8\u4eba\u56de\u6765\u4e4b\u65f6\uff0c\u5df2\u7136\u67d3\u4e0a\u91cd\u75c5\uff0c\u96be\u652f\u800c\u901d\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u4e0d\u662f\u67d3\u4e86\u91cd\u75c5\uff0c\u800c\u4e2d\u4e86\u6bd2\uff0c\u548c\u53d7\u4e86\u5185\u4f24\u2026\u2026\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u6740\u5bb3\u4e86\u4ed6\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u4ed6\u7559\u4e0b\u7684\u9057\u4e66\u4e2d\u96be\u9053\u672a\u8bf4\u660e\u767d\u5417\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u90a3\u9057\u4e66\u4e0a\u53ea\u753b\u4e86\u4e00\u5ea7\u6df1\u5c71\uff0c\u4e00\u7247\u6f6d\u6c34\uff0c\u5199\u4e86\u6bd2\u9f99\u6f6d\u4e09\u4e2a\u5b57\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u90a3\u662f\u4ec0\u4e48\u7528\u610f\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u5f53\u65f6\u5927\u5bb6\u4e00\u7247\u5fd9\u4e71\uff0c\u53ea\u89c9\u5f97\u8fd9\u5e45\u56fe\u753b\u5c71\u6c34\uff0c\u662f\u4ed6\u5728\u795e\u5fd7\u8ff7\u4e71\u4e4b\u4e0b\u968f\u624b\u5199\u6765\uff0c\u4e5f\u65e0\u4eba\u7528\u5fc3\u7814\u7a76\uff0c\u5c31\u628a\u5b83\u5b58\u4e86\u8d77\u6765\u2026\u2026\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u90a3\u5e45\u753b\u4e66\uff0c\u5e94\u8be5\u5927\u6709\u7814\u7a76\u7684\u5fc5\u8981\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5728\u4e0b\u8ffd\u6eaf\u5f80\u4e8b\uff0c\u627e\u5230\u4e86\u8fd9\u4ef6\u4e8b\uff0c\u4ed4\u7ec6\u5730\u7814\u7a76\u90a3\u5e45\u56fe\u753b\uff0c\u624d\u89c9\u5f97\u51fa\u8fd9\u4ef6\u4e8b\uff0c\u5bd3\u610f\u6df1\u523b\uff0c\u5e76\u975e\u662f\u968f\u624b\u5199\u6765\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u6bd2\u9f99\u6f6d\u53ef\u80fd\u662f\u4e00\u4e2a\u5730\u540d\u3002\u201d \u3000\u3000\u65b9\u51e4\u537f\u9053\uff1a\u201c\u65b9\u5144\u8bf4\u7684\u4e0d\u9519\uff0c\u90a3\u6bd2\u9f99\u6f6d\u786e\u6709\u6b64\u5730\uff0c\u800c\u4e14\u5144\u5f1f\u5df2\u7ecf\u627e\u5230\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u90a3\u6bd2\u9f99\u6f6d\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u6cf0\u5c71\u6df1\u5904\uff0c\u4e00\u4e2a\u4eba\u8ff9\u7f55\u5230\u4e4b\u5730\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u8a00\u5144\u5f1f\u80fd\u591f\u627e\u5230\u90a3\u7b49\u50fb\u9759\u6240\u5728\uff0c\u5b9e\u4e5f\u975e\u6613\u4e8b\u4e86\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u5144\u5f1f\u4e3a\u627e\u90a3\u6bd2\u9f99\u6f6d\uff0c\u8db3\u8db3\u82b1\u4e86\u534a\u5e74\u65f6\u95f4\uff0c\u624d\u9047\u4e0a\u4e86\u4e00\u4e2a\u77e5\u6653\u90a3\u6bd2\u9f99\u6f6d\u7684\u4eba\uff0c\u5144\u5f1f\u627e\u4e0a\u4e86\u6bd2\u9f99\u6f6d\u7ec6\u770b\u56db\u4e0b\u7684\u666f\u7269\uff0c\u5927\u81f4\u76f8\u540c\uff0c\u4f46\u641c\u67e5\u56db\u5468\uff0c\u65b9\u5706\u5341\u91cc\u5185\u672a\u4f4f\u4eba\u5bb6\uff0c\u5144\u5f1f\u53ea\u597d\u91cd\u56de\u8fb0\u5dde\uff0c\u82e6\u601d\u751a\u4e45\uff0c\u4ecd\u60f3\u4e0d\u51fa\u539f\u56e0\u4f55\u5728\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u8fd9\u548c\u8d35\u95e8\u4e2d\u6b66\u529f\uff0c\u6709\u4f55\u5173\u8fde\uff1f\u201d \u3000\u3000\u65b9\u51e4\u537f\u9053\uff1a\u201c\u636e\u5144\u5f1f\u67e5\u627e\u8a00\u5bb6\u95e8\u4e2d\u6863\u6848\u3001\u8d44\u6599\uff0c\u53d1\u89c9\u6211\u8a00\u5bb6\u95e8\u4f20\u4e0b\u7edd\u6280\u5bc6\u5f55\uff0c\u53ef\u80fd\u4e3a\u90a3\u4e00\u4ee3\u638c\u95e8\u4eba\u5e26\u4e86\u51fa\u53bb\uff0c\u4ee5\u81f4\u6240\u6709\u7684\u6b66\u529f\u4e2d\u7cbe\u534e\u4e4b\u5b66\uff0c\u5168\u90fd\u6d88\u5931\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u6b64\u4e8b\u91cd\u5927\uff0c\u4f60\u627e\u51fa\u4ec0\u4e48\u7ebf\u7d22\u5417\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u5144\u5f1f\u7684\u82e6\u8877\u4e86\uff0c\u6211\u67e5\u51fa\u5341\u4ee3\u4ee5\u524d\u7684\u638c\u95e8\u4eba\uff0c\u7559\u4e0b\u8fd9\u5e45\u56fe\u6848\uff0c\u4e5f\u67e5\u51fa\u4ece\u4ed6\u6b7b\u53bb\u4e4b\u540e\uff0c\u8a00\u5bb6\u95e8\u5f00\u59cb\u6ca1\u843d\u2026\u2026\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u5c31\u7b97\u4f60\u4eec\u8a00\u5bb6\u95e8\u7684\u7cbe\u534e\u6b66\u529f\uff0c\u88ab\u90a3\u4f4d\u638c\u95e8\u4eba\u5e26\u8d70\uff0c\u4f46\u4f60\u4eec\u8a00\u5bb6\u95e8\u96be\u9053\u5c31\u6ca1\u6709\u7559\u4e0b\u5e74\u9ad8\u4f4d\u5c0a\u7684\u957f\u8005\u5417\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u56db\u4f4d\u6b66\u529f\u6700\u9ad8\u5f3a\u7684\u9ad8\u624b\u90fd\u8ffd\u968f\u638c\u95e8\u4eba\u79bb\u5f00\u4e86\u8fb0\u5dde\uff0c\u56de\u6765\u65f6\u53ea\u6709\u638c\u95e8\u4eba\u4e00\u4e2a\u5f52\u6765\uff0c\u56db\u5927\u9ad8\u624b\u7684\u751f\u6b7b\uff0c\u6709\u5982\u6c89\u6d77\u6c99\u77f3\uff0c\u63a2\u4e0d\u51fa\u4e00\u70b9\u6d88\u606f\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u8fd9\u786e\u5b9e\u662f\u6709\u4e9b\u602a\uff0c\u9664\u4e86\u56db\u4eba\u4e4b\u5916\uff0c\u96be\u9053\u518d\u65e0\u522b\u4eba\u5417\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u66f4\u602a\u7684\u662f\uff0c\u638c\u95e8\u4e27\u4e8b\u521a\u521a\u529e\u5b8c\uff0c\u51e0\u4f4d\u957f\u8001\u4e5f\u76f8\u7ee7\u75c5\u901d\uff0c\u7167\u6211\u4eec\u8a00\u5bb6\u95e8\u7684\u4f20\u8bf4\uff0c\u90a3\u662f\u5929\u8981\u4ea1\u8a00\u5bb6\u95e8\uff0c\u624d\u4f7f\u51e0\u4e2a\u5e73\u65e5\u5341\u5206\u96c4\u58ee\u7684\u957f\u8001\uff0c\u76f8\u7ee7\u5728\u4e09\u65e5\u4e4b\u5185\u6b7b\u4ea1\uff0c\u800c\u4e14\u4e00\u75c5\u5168\u8eab\u62bd\u52a8\u53e3\u4e0d\u80fd\u8a00\uff0c\u6240\u4ee5\u51e0\u4f4d\u957f\u8005\u6b7b\u65f6\uff0c\u7adf\u672a\u7559\u4e0b\u4e00\u53e5\u9057\u8a00\uff0c\u4e5f\u672a\u4ea4\u5f85\u8fc7\u4e00\u4ef6\u4e8b\u60c5\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u8a00\u5144\u5f1f\u7684\u770b\u6cd5\u5462\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u5728\u4e0b\u7684\u770b\u6cd5\uff0c\u8fd9\u662f\u4e00\u79cd\u8ba1\u5212\u7cbe\u5bc6\u7684\u8c0b\u6740\uff0c\u5fd7\u5728\u4f7f\u8a00\u5bb6\u95e8\u81ea\u52a8\u5728\u6b66\u6797\u4e2d\u6d88\u6c89\u3001\u6ca1\u843d\uff0c\u638c\u95e8\u4eba\u79bb\u5f00\u524d\uff0c\u8fd9\u9634\u8c0b\u4e00\u76f4\u5728\u8fdb\u884c\u4e2d\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u8a00\u5144\u5f1f\u4e4b\u610f\u53ef\u662f\u8bf4\u8d35\u95e8\u4e2d\u4eba\uff0c\u90fd\u662f\u88ab\u4eba\u6240\u7b97\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u7269\u6700\u53ef\u7591\uff0c\u8a00\u5144\u66fe\u60f3\u5230\u5426\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u5144\u5f1f\u60f3\u4e0d\u51fa\u6765\u3002\u201d \u3000\u3000\u65b9\u5927\u6210\u9053\uff1a\u201c\u8a00\u5144\uff0c\u53ef\u5426\u628a\u8d35\u638c\u95e8\u7559\u4e0b\u6765\u7684\u56fe\u753b\u7ed9\u5728\u4e0b\u77a7\u77a7\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u7565\u4e00\u72b9\u50cf\uff0c\u7f13\u7f13\u5730\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u5e45\u56fe\u753b\u9012\u4e86\u8fc7\u53bb\uff0c\u9053\uff1a\u201c\u65b9\u4e8c\u4fa0\u8bf7\u770b\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u5c55\u5f00\u56fe\uff0c\u53ea\u89c1\u90a3\u4e0a\u9762\u56fe\u753b\u7559\u5b57\u679c\u548c\u8a00\u51e4\u537f\u6240\u8bf4\u4e00\u822c\uff0c\u53ea\u662f\u5e74\u4ee3\u5341\u5206\u4e45\u8fdc\uff0c\u770b\u4e0a\u53bb\u6709\u4e9b\u6a21\u7cca\uff0c\u4f46\u5b57\u8ff9\u7b14\u52bf\uff0c\u90fd\u8fd8\u53ef\u8fa9\u8ba4\u3002 \u3000\u3000\u90d1\u5927\u521a\u4fa7\u8fc7\u5934\u6765\uff0c\u77a7\u4e86\u90a3\u56fe\u753b\u4e00\u773c\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u4e00\u5e45\u7b80\u660e\u7684\u56fe\u753b\uff0c\u5176\u4e2d\u4e0d\u4f3c\u6709\u9690\u79d8\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u5144\u5f1f\u4e5f\u8fd9\u6837\u60f3\uff0c\u6839\u636e\u6211\u4eec\u8a00\u5bb6\u95e8\u4e2d\u7684\u8d44\u6599\u8bb0\u8f7d\uff0c\u6211\u4eec\u8fd9\u4f4d\u5341\u4ee3\u524d\u7684\u638c\u95e8\u4eba\uff0c\u6709\u5f88\u591a\u65f6\u95f4\uff0c\u4e5f\u4e0d\u5584\u4e66\u753b\uff0c\u600e\u4e48\u4f1a\u753b\u4e86\u8fd9\u4e00\u5e45\u753b\u5462\uff1f\u5982\u82e5\u8fd9\u753b\u4e2d\u7684\u610f\u601d\u6df1\u5c71\u73af\u7ed5\u7740\u4e00\u7247\u6f6d\u6c34\uff0c\u5929\u4e0b\u53ef\u80fd\u627e\u51fa\u51e0\u767e\u5904\u76f8\u540c\u4e4b\u5730\uff0c\u6240\u4ee5\uff0c\u4ed6\u624d\u5199\u4e0b\u4e86\u6bd2\u9f99\u6f6d\u4e09\u4e2a\u5b57\uff0c\u4f46\u90a3\u6bd2\u9f99\u6f6d\u5df2\u4e3a\u5728\u4e0b\u627e\u5230\uff0c\u904d\u67e5\u4e86\u65b9\u5706\u5341\u91cc\uff0c\u672a\u627e\u5230\u4e00\u5904\u4eba\u5bb6\uff0c\u96be\u9053\u8fd8\u53e6\u6709\u4e00\u4e2a\u6bd2\u9f99\u6f6d\u5417\uff1f\u201d \u3000\u3000\u65b9\u5f00\u6210\u7ee9\u7f13\u5730\u628a\u56fe\u6848\u6298\u597d\uff0c\u4ea4\u7ed9\u8a00\u51e4\u537f\u9053\uff1a\u201c\u4e5f\u8bb8\u6709\u4e09\u4e2a\u3001\u4e94\u4e2a\u6bd2\u9f99\u6f6d\uff0c\u4f46\u5176\u5f62\u6001\u5374\u4e0d\u4f1a\u548c\u4f60\u4eec\u90a3\u4f4d\u638c\u95e8\u4eba\u753b\u7684\u4e00\u822c\uff0c\u4f60\u65e2\u7136\u6211\u5230\u4e86\u6bd2\u9f99\u6f6d\uff0c\u800c\u4e14\u56db\u5468\u666f\u7269\u53c8\u548c\u8fd9\u753b\u4e0a\u60c5\u5f62\u76f8\u5408\uff0c\u81ea\u7136\u662f\u4e0d\u4f1a\u9519\u4e86\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u5144\u5f1f\u60f3\u4e0d\u660e\u767d\u4e86\uff0c\u4ed6\u7559\u4e0b\u8fd9\u5e45\u753b\u7684\u7528\u5fc3\u4f55\u5728\uff1f\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u6b64\u4e8b\u5173\u7cfb\u66f9\u6211\u8a00\u5bb6\u95e8\u7684\u751f\u6b7b\u5b58\u4ea1\uff0c\u5144\u5f1f\u53c8\u4e0d\u613f\u5f20\u626c\u51fa\u53bb\uff0c\u4e5f\u4e0d\u60f3\u60ca\u52a8\u6211\u8a00\u5bb6\u4e2d\u4eba\uff0c\u5176\u5b9e\u8a00\u5bb6\u95e8\u4e5f\u65e0\u4eba\u624d\u53ef\u7528\uff0c\u8fd9\u4e94\u5341\u5e74\u6765\uff0c\u4e0d\u8fdb\u5219\u9000\uff0c\u8a00\u5bb6\u95e8\u5b9e\u65e0\u53ef\u7528\u4e4b\u624d\uff0c\u5144\u5f1f\u51e0\u756a\u601d\u7d22\uff0c\u89c9\u7740\u53ea\u6709\u627e\u90d1\u5144\u5e2e\u5fd9\u4e86\uff0c\u60f3\u4e0d\u5230\u5144\u5f1f\u665a\u6765\u4e86\u4e00\u6b65\uff0c\u90d1\u5144\u7adf\u4f24\u5728\u4e86\u4e94\u9b3c\u624b\u4e0b\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u5144\u5f1f\u8fd9\u70b9\u4f24\u52bf\uff0c\u5e76\u4e0d\u788d\u4e8b\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u8a00\u98ce\u537f\u9053\uff1a\u201c\u4e0d\u8fc7\u4ec0\u4e48\uff0c\u96be\u9053\u8fd8\u6709\u4eba\u5148\u7ea6\u4e86\u51e0\u4f4d\u4e0d\u6210\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u6b63\u662f\u5982\u6b64\uff0c\u8fd9\u7ea6\u4f1a\u5df2\u7ecf\u8ba2\u5728\u4e00\u5e74\u4e4b\u524d\uff0c\u771f\u4e5f\u65e0\u6cd5\u63a8\u62d6\u4e86\u2026\u2026\u201d\u76ee\u5149\u4e00\u63a0\u65b9\u5929\u6210\u9053\uff1a\u201c\u4e8c\u5f1f\uff0c\u54b1\u4eec\u90a3\u7ea6\u4f1a\u8fd8\u6709\u51e0\u65e5\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4f1a\u671f\u662f\u4e5d\u6708\u5341\u4e94\u65e5\uff0c\u7b97\u8ba1\u8def\u7a0b\uff0c\u4e00\u8def\u6025\u8d76\uff0c\u4e5f\u8981\u516b\u4e5d\u5929\uff0c\u5927\u54e5\u518d\u517b\u606f\u51e0\u5929\u4f24\u52bf\uff0c\u5b9e\u662f\u96be\u6709\u7a7a\u4e86\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u76ee\u5149\u79fb\u6ce8\u5230\u8a00\u51e4\u537f\u7684\u9669\u4e0a\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u8a00\u5144\u5f1f\uff0c\u770b\u6765\u5c0f\u5144\u5f1f\u662f\u65e0\u6cd5\u52a9\u4f60\u4e86\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u4e09\u4f4d\u6709\u4e8b\uff0c\u81ea\u7136\u5148\u529e\u81ea\u5df1\u7684\u4e8b\u60c5\u8981\u7d27\u2026\u2026\u201d\u8bed\u58f0\u4e00\u987f\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u6709\u4e00\u53e5\u4e0d\u5f53\u4e4b\u8a00\u4e0d\u77e5\u662f\u5426\u8be5\u95ee\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u8a00\u5144\u5f1f\u53ea\u7ba1\u8bf4\u660e\u5c31\u662f\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u4e09\u4f4d\u548c\u4f55\u4eba\u7ea6\u4f1a\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u6b66\u6797\u4e2d\u4e00\u4e2a\u5f88\u6709\u540d\u7684\u9b54\u5934\uff0c\u8fd9\u4e00\u6b21\u4f1a\u5408\uff0c\u751f\u6b7b\u96be\u535c\uff0c\u4f46\u6211\u548c\u4e24\u4f4d\u5144\u5f1f\u90fd\u5df2\u7ecf\u6709\u4e86\u51c6\u5907\uff0c\u65e9\u5df2\u628a\u751f\u6b7b\u7f6e\u4e4b\u5ea6\u5916\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u5947\u602a\u9053\uff1a\u201c\u5144\u5f1f\u672a\u8d76\u4e0a\u4e09\u4f4d\u4f1a\u6597\u4e94\u9b3c\u4e00\u4e8b\uff0c\u8fd9\u6b21\u5e0c\u671b\u80fd\u52a9\u90d1\u5144\u7b49\u4e00\u81c2\u4e4b\u529b\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u6447\u6447\u5934\u7b11\u9053\uff1a\u201c\u8a00\u5144\u5f1f\u7684\u597d\u610f\u5728\u4e0b\u7b49\u5fc3\u9886\u4e86\uff0c\u6211\u4eec\u5144\u5f1f\u8fd9\u4e00\u6b21\u4f1a\u6664\u5f3a\u654c\uff0c\u751f\u673a\u5f88\u5c11\uff0c\u8a00\u5144\u5f1f\u53d7\u8a00\u5bb6\u4e0a\u4e0b\u4e24\u4ee3\u91cd\u6258\uff0c\u5b9e\u4e5f\u7528\u4e0d\u7740\u548c\u6211\u4eec\u5192\u8fd9\u7b49\u51f6\u9669\uff01\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\u9053\uff1a\u201c\u660e\u65e5\uff0c\u5c0f\u5144\u7684\u4f5c\u52bf\u53ef\u80fd\u4f1a\u597d\u8f6c\u4e00\u4e9b\uff0c\u6211\u4eec\u4ed4\u7ec6\u5730\u7814\u7a76\u4e00\u4e0b\uff0c\u4f60\u8fd9\u56fe\u753b\u5185\u60c5\uff0c\u4e5f\u8bb8\u6211\u7b49\u53ef\u63d0\u4f9b\u8a00\u5144\u5f1f\u4e00\u4e9b\u7ebf\u7d22\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\u9053\uff1a\u201c\u4e94\u5341\u5e74\uff0c\u5c71\u5ddd\u6cb3\u6d41\u90fd\u53ef\u80fd\u6709\u4e86\u6539\u53d8\uff0c\u4f55\u51b5\u4eba\u5462\uff1f\u4e0d\u8fc7\uff0c\u5144\u5f1f\u5947\u602a\u7684\u662f\u8a00\u5bb6\u95e8\u51e0\u79cd\u7279\u6b8a\u7684\u6b66\u529f\uff0c\u59cb\u7ec8\u672a\u66fe\u51fa\u73b0\u4e8e\u6c5f\u6e56\u4e4b\u4e0a\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u4f60\u63a8\u60f3\u90a3\u8a00\u5bb6\u6b66\u529f\u79d8\u5f55\uff0c\u53ef\u80fd\u85cf\u5728\u90a3\u6bd2\u9f99\u6f6d\u9644\u8fd1\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u8d77\u521d\u4e4b\u65f6\uff0c\u5728\u4e0b\u786e\u66fe\u8fd9\u6837\u60f3\u8fc7\uff0c\u4f46\u540e\u6765\uff0c\u53c8\u89c9\u7740\u8fd9\u60f3\u6cd5\u672a\u514d\u592a\u5982\u610f\u4e86\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5927\u54e5\u8bf7\u95ed\u76ee\u517b\u606f\u4e00\u4e0b\uff0c\u5c0f\u5f1f\u548c\u8fd9\u4f4d\u8a00\u5144\u5f1f\u5230\u5385\u4e2d\u53bb\u8c08\u8c08\uff0c\u54b1\u4eec\u65f6\u95f4\u4e0d\u591a\uff0c\u65e0\u6cd5\u51fa\u529b\u76f8\u52a9\u4ed6\u5bfb\u56de\u8a00\u5bb6\u95e8\u4e2d\u5931\u843d\u7684\u6b66\u529f\uff0c\u4f46\u54b1\u4eec\u5e94\u4ea5\u5c3d\u529b\u52a9\u4ed6\u89e3\u51b3\u4e00\u4e9b\u7591\u96be\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4eec\u5230\u5385\u4e2d\u8c08\u8bba\u5427\uff01\u201d \u3000\u3000\u4ed6\u4f3c\u662f\u7761\u610f\u751a\u6d53\uff0c\u8a00\u7f62\uff0c\u95ed\u4e0a\u53cc\u76ee\u3002 \u3000\u3000\u65b9\u5929\u6210\u7ad9\u8d77\u8eab\u5b50\uff0c\u8f7b\u6b65\u884c\u51fa\u5ba2\u5385\u3002 \u3000\u3000\u8a00\u51e4\u537f\u3001\u7a0b\u5b50\u671b\u3001\u77f3\u4fca\u7b49\u7d27\u968f\u65b9\u5929\u6210\u9000\u51fa\u5185\u5ba4\uff0c\u884c\u5165\u5385\u4e2d\u3002 \u3000\u3000\u65b9\u5929\u6210\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u7a0b\u5e84\u4e3b\uff0c\u54b1\u4eec\u5230\u5bf9\u9762\u623f\u4e2d\u5750\u5750\u5982\u4f55\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u597d\uff01\u5728\u4e0b\u7acb\u65f6\u5bfb\u4ed6\u4eec\u6253\u626b\u4e00\u4e0b\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4e0d\u7528\u4e86\uff0c\u54b1\u4eec\u5750\u4e0d\u4e86\u591a\u4e45\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u5f53\u5148\u5e26\u8def\uff0c\u884c\u5165\u5ba4\u4e2d\u3002 \u3000\u3000\u8fd9\u623f\u820d\u5efa\u7b51\u548c\u90d1\u5927\u521a\u7684\u517b\u4f24\u5fc3\u5c4b\u4e00\u822c\uff0c\u5916\u9762\u4e00\u5385\uff0c\u91cc\u9762\u4e00\u623f\u3002 \u3000\u3000\u8fd9\u623f\u4e2d\u867d\u672a\u4f4f\u4eba\u4f46\u5374\u7ecf\u5e38\u6253\u626b\uff0c\u5ba4\u4e2d\u5341\u5206\u5e72\u51c0\u3002 \u3000\u3000\u5385\u4e2d\u653e\u7740\u4e00\u5f20\u516b\u4ed9\u684c\uff0c\u548c\u51e0\u5f20\u6728\u6905\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u8ba9\u51e0\u4eba\u5750\u4e0b\u4e4b\u540e\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u51e0\u4f4d\u8c08\u8c08\uff0c\u5728\u4e0b\u53bb\u53bb\u5c31\u6765\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u7a0b\u5e84\u4e3b\u5982\u82e5\u65e0\u4e8b\uff0c\u4e0d\u59a8\u7559\u6b64\uff0c\u5982\u662f\u771f\u7684\u6709\u4e8b\uff0c\u5728\u4e0b\u7b49\u4e5f\u52c9\u5f3a\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6628\u591c\u4e00\u573a\u640f\u6597\uff0c\u6709\u5f88\u591a\u4e8b\u60c5\u8981\u529e\uff0c\u5728\u4e0b\u4e0d\u80fd\u591a\u7559\u4e86\u3002\u201d\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u8a00\u98ce\u537f\u671b\u7740\u7a0b\u5b50\u671b\u7684\u80cc\u5f71\uff0c\u9053\uff1a\u201c\u8fd9\u4f4d\u7a0b\u5e84\u4e3b\u7684\u4e3a\u4eba\u5982\u4f55\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u5bf9\u4e8e\u7a0b\u5e84\u4e3b\u7684\u4e3a\u4eba\uff0c\u5728\u4e0b\u4e5f\u4e0d\u592a\u4e86\u7136\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u6211\u4eec\u8a00\u5bb6\u95e8\u4e2d\u7684\u79c1\u4e8b\uff0c\u4e0d\u5e0c\u671b\u6cc4\u9732\u4e8e\u6c5f\u6e56\u4e4b\u4e0a\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u7a0b\u5b50\u671b\u4e3a\u4eba\u5f88\u6301\u91cd\uff0c\u6b64\u7b49\u91cd\u5927\u4e4b\u4e8b\uff0c\u6211\u60f3\u4ed6\u4e0d\u81f4\u4e8e\u8f7b\u6613\u6cc4\u9732\u5427\uff01\u201d \u3000\u3000\u8a00\u98ce\u537f\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5e0c\u671b\u5982\u6b64\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u54b1\u4eec\u5927\u54e5\u4f24\u52bf\u5c1a\u672a\u75ca\u6108\uff0c\u4e0d\u4fbf\u591a\u8017\u7cbe\u795e\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u5144\u5f1f\u5df2\u89c9\u7740\u9002\u624d\u8bf4\u8bdd\u592a\u591a\uff0c\u8017\u53bb\u4e86\u90d1\u5144\u4e0d\u5c11\u7cbe\u795e\uff0c\u751a\u611f\u6127\u759a\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u8981\u7d27\uff0c\u8a00\u5144\u4e0d\u7528\u4e3a\u6b64\u591a\u5fc3\u2026\u2026\u201d\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u8a00\u5144\u4f3c\u662f\u5bf9\u8d35\u95e8\u4e2d\u4e8b\uff0c\u5df2\u7136\u627e\u51fa\u4e9b\u5934\u7eea\uff0c\u5982\u662f\u5144\u5f1f\u63a8\u60f3\u7684\u4e0d\u9519\uff0c\u8a00\u5144\u80f8\u4e2d\u6240\u77e5\uff0c\u51b3\u4e0d\u6b62\u9002\u624d\u6240\u8a00\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u8fd9\u662f\u6211\u8a00\u5bb6\u95e8\u79c1\u4e8b\uff0c\u672c\u4e5f\u4e0d\u5e94\u8be5\u60ca\u6270\u4ed6\u4eba\uff0c\u53ea\u662f\u5144\u5f1f\u548c\u90d1\u5144\u4e00\u89c1\u5982\u6545\uff0c\u5f7c\u6b64\u60c5\u6295\u610f\u53f0\uff0c\u56e0\u6b64\u60f3\u501f\u91cd\u4e09\u4f4d\u624d\u6167\u3001\u6b66\u529f\uff0c\u52a9\u6211\u4e00\u81c2\u4e4b\u529b\uff0c\u4f46\u4e09\u4f4d\u4e8b\u52a1\u5306\u5fd9\uff0c\u90d1\u5144\u53c8\u53d7\u4e86\u91cd\u4f24\uff0c\u8fd9\u5012\u4f7f\u5144\u5f1f\u4e0d\u4fbf\u5f00\u53e3\u4e86\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u6211\u4eec\u8fd8\u6709\u4e24\u4e09\u5929\u65f6\u95f4\uff0c\u8a00\u5144\u7684\u4e8b\uff0c\u6050\u975e\u4e09\u4e94\u65e5\u80fd\u591f\u89e3\u51b3\u2026\u2026\u201d \u3000\u3000\u8a00\u51e4\u537f\u63a5\u9053\uff1b\u201c\u51e0\u4f4d\u4e8b\u5fd9\uff0c\u5144\u5f1f\u7684\u4e8b\uff0c\u5df2\u8fc7\u4e86\u6570\u5341\u5e74\uff0c\u518d\u665a\u4e0a\u4e00\u5e74\u534a\u8f7d\u4e5f\u4e0d\u8981\u7d27\uff0c\u4f46\u5144\u5f1f\u548c\u90d1\u5144\u4ea4\u5f80\u4e86\u4e00\u573a\uff0c\u5e0c\u671b\u80fd\u52a9\u51e0\u4f4d\u4e00\u81c2\u4e4b\u529b\u2026\u2026\u201d \u3000\u3000\u65b9\u5929\u6210\u6447\u6447\u5934\u53f9\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u8a00\u5144\u548c\u6211\u4eec\u5927\u54e5\u8bba\u4ea4\uff0c\u5728\u4e0b\u4e5f\u4e0d\u628a\u8a00\u5144\u5f53\u5916\u4eba\u770b\u4e86\uff0c\u5144\u5f1f\u6709\u4ec0\u4e48\u8bdd\uff0c\u76f4\u8bdd\u76f4\u8bf4\u4e86\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u65b9\u5144\u6709\u4f55\u6307\u6559\uff0c\u4f46\u7ba1\u76f4\u8bf4\u4e0d\u59a8\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u6211\u4eec\u5144\u5f1f\u548c\u4eba\u8fd9\u756a\u7ea6\u4f1a\uff0c\u5b9e\u5728\u662f\u4e00\u573a\u6b7b\u4ea1\u4e4b\u7ea6\uff0c\u8a00\u5144\u5f1f\u7528\u4e0d\u7740\u773c\u6211\u4eec\u6dcc\u8fd9\u6b21\u6d51\u6c34\uff01\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u8a00\u5bb6\u7684\u7edd\u6280\uff0c\u867d\u7136\u6d41\u5931\u6c5f\u6e56\uff0c\u4f46\u5144\u5f1f\u82e6\u7ec3\u6570\u5e74\uff0c\u5c0f\u6709\u6536\u83b7\uff0c\u53ea\u8981\u8bf8\u4f4d\u80fd\u591f\u4fdd\u6301\u53cc\u65b9\u5747\u8861\uff0c\u5144\u5f1f\u52a0\u5165\uff0c\u53ef\u5c0f\u589e\u51e0\u5206\u80dc\u7b97\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u4e2d\u5dde\u4e09\u4fa0\uff0c\u5341\u4f59\u5e74\u6765\u884c\u8d70\u6c5f\u6e56\uff0c\u7ecf\u8fc7\u4e86\u65e0\u6570\u7684\u98ce\u6d6a\uff0c\u90fd\u80fd\u5c65\u9669\u4e3a\u5937\uff0c\u56fa\u7136\u8981\u51ed\u673a\u667a\uff0c\u4f46\u4e8b\u524d\u7684\u6599\u654c\u673a\u5148\uff0c\u80dc\u8d1f\u8ba1\u7b97\uff0c\u90fd\u6709\u7740\u7cbe\u5bc6\u7684\u7b97\u8ba1\uff0c\u8fd9\u4e00\u6b21\u4e0d\u540c\u4e86\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u6709\u4f55\u4e0d\u540c\u4e4b\u5904\u4e86\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u8fd9\u4e00\u6b21\u6211\u4eec\u5168\u65e0\u80dc\u7b97\uff0c\u8981\u5b8c\u5168\u78b0\u8fd0\u6c14\u4e86\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u8d35\u5144\u5f1f\u8fd9\u6b21\u7ea6\u4f1a\uff0c\u51e0\u65f6\u8ba2\u4e0b\u7684\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u8fd9\u7ea6\u4f1a\u8ba2\u4e0b\u7684\u65f6\u95f4\u5f88\u4e45\u4e86\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u65e2\u975e\u5076\u53d1\u5bdf\u4ef6\uff0c\u8d35\u5144\u5f1f\u53c8\u65e0\u80dc\u654c\u628a\u63e1\uff0c\u4f55\u4ee5\u4e0d\u80af\u65e9\u4f5c\u51c6\u5907\u5462\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8a00\u5144\u6240\u8c13\u7684\u51c6\u5907\uff0c\u65e0\u975e\u662f\u9080\u8bf7\u52a9\u62f3\u4e4b\u4eba\u4e86\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u9080\u4eba\u52a9\u62f3\uff0c\u81ea\u7136\u662f\u6700\u91cd\u8981\u7684\u4e00\u73af\uff0c\u5177\u4ed6\u65b9\u9762\uff0c\u8d35\u5144\u5f1f\u4e5f\u8be5\u6709\u4e2a\u8ba1\u5212\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u5144\u5f1f\u672c\u6765\u662f\u60f3\u8c08\u8c08\u8a00\u5144\u7684\u4e8b\uff0c\u63d0\u4f9b\u4e00\u4e9b\u611a\u89c1\uff0c\u60f3\u4e0d\u5230\u7adf\u662f\u8c08\u8d77\u6211\u4eec\u7684\u4e8b\u4e86\u2026\u2026\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u4eba\u65e0\u8fdc\u8651\uff0c\u5fc5\u6709\u8fd1\u5fe7\uff0c\u8d35\u5144\u5f1f\u7684\u4e8b\u6bd4\u5728\u4e0b\u91cd\u8981\uff0c\u81ea\u7136\u8981\u5148\u8c08\u6de1\u4f60\u4eec\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\uff0c\u6211\u4eec\u4e00\u76f4\u6ca1\u6709\u544a\u8bc9\u8fc7\u4eba\uff0c\u56e0\u4e3a\u5f88\u5c11\u4eba\u80fd\u5e2e\u4e0a\u5fd9\uff0c\u4e5f\u5f97\u5c11\u4eba\u613f\u610f\u5e2e\u5fd9\uff0c\u4eca\u65e5\u544a\u8bc9\u8a00\u5144\uff0c\u4e2d\u5dde\u4e09\u4fa0\u4ece\u6b64\u7edd\u8ff9\u6c5f\u6e56\u4e4b\u540e\uff0c\u4e5f\u7b97\u6709\u4e00\u4e2a\u4eba\u77e5\u6653\u6211\u4eec\u54ea\u91cc\u53bb\u4e86\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u542c\u5f97\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u8fd9\u7b49\u4e25\u91cd\u5417\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u795e\u60c5\u8083\u7136\u5730\u7b54\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5f80\u597d\u4e00\u65b9\u9762\u8bf4\uff0c\u6211\u4eec\u4e09\u4eba\u4e2d\uff0c\u8d38\u4e00\u4e24\u4e2a\u6b8b\u5e9f\uff0c\u4f46\u4e5f\u662f\u78b0\u8fd0\u6c14\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u5bf9\u65b9\u662f\u4ec0\u4e48\u4eba\uff1f\u7adf\u4f7f\u5927\u540d\u9f0e\u9f0e\u7684\u4e2d\u5dde\u4e09\u4fa0\u5982\u6b64\u6c14\u9981\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8a00\u5144\u77e5\u6653\u7530\u4e00\u5200\u5417\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u53ef\u662f\u4eba\u79f0\u9b54\u5200\u7684\u7530\u8fdc\u5417\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8a00\u5144\u53ef\u662f\u542c\u8d35\u95e8\u4e2d\u957f\u8001\u63d0\u8fc7\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u8fd1\u4e24\u5e74\u4e2d\uff0c\u5144\u5f1f\u8d2f\u6ce8\u4e8e\u7814\u7a76\u6c5f\u6e56\u60c5\u52bf\uff0c\u662f\u4ee5\uff0c\u6b63\u90aa\u4e24\u9053\u4e2d\u9ad8\u624b\uff0c\u90fd\u80fd\u77e5\u6653\u51e0\u4eba\u3002\u4e0d\u8fc7\uff0c\u636e\u5144\u5f1f\u6240\u77e5\uff0c\u8fd9\u4eba\u5df2\u6709\u8fd1\u5341\u5e74\u672a\u5728\u6c5f\u6e56\u9732\u9762\u4e86\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4ed6\u867d\u672a\u9732\u9762\uff0c\u4f46\u5e76\u975e\u9000\u51fa\u6c5f\u6e56\uff0c\u800c\u4ecd\u7136\u957f\u5e74\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\uff0c\u53ea\u662f\u4e3e\u52a8\u66f4\u4e3a\u9690\u79d8\u4e86\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u5144\u5f1f\u5c31\u4e0d\u6e05\u695a\u4e86\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u81ea\u4ece\u5251\u795e\u9648\u5927\u4fa0\u626b\u8361\u6c5f\u6e56\uff0c\u903c\u5f97\u4e94\u9b54\u4e3b\u7acb\u4e0b\u8a93\u8a00\uff0c\u9648\u5927\u4fa0\u5728\u4e16\u4e4b\u65e5\uff0c\u51b3\u4e0d\u91cd\u51fa\u6c5f\u6e56\uff0c\u8fd9\u9b54\u5200\u7530\u8fdc\u53ef\u7b97\u662f\u7eff\u6797\u9053\u4e0a\u6570\u4e00\u6570\u4e8c\u7684\u4eba\u7269\u4e86\uff0c\u4f46\u4ed6\u6bd4\u8d77\u4e94\u4f4d\u9b54\u4e3b\u7684\u6b66\u529f\uff0c\u81ea\u7136\u76f8\u5dee\u6781\u8fdc\uff0c\u4e94\u4f4d\u9b54\u4e3b\u81e3\u4f0f\u9648\u5927\u4fa0\u795e\u5251\u4e4b\u4e0b\uff0c\u7530\u8fdc\u7b49\u81ea\u7136\u8981\u907f\u98ce\u5934\uff0c\u6545\u800c\u4e0d\u6562\u518d\u5927\u6447\u5927\u6446\u5730\u51fa\u73b0\u4e8e\u6c5f\u6e56\u4e4b\u4e0a\u4e86\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u5251\u795e\u9648\u5927\u4fa0\u7684\u4e8b\u8ff9\uff0c\u5728\u4e0b\u4e5f\u542c\u8bf4\u8fc7\u4e00\u4e9b\uff0c\u4f46\u4f7f\u5728\u4e0b\u89c9\u5f97\u5947\u602a\u7684\u662f\uff0c\u90a3\u9648\u5927\u4fa0\u65e2\u7136\u538b\u670d\u4e86\u4e94\u4f4d\u9b54\u4e3b\uff0c\u4e3a\u4f55\u4e0d\u80af\u628a\u4ed6\u4eec\u4e00\u4e00\u535a\u6740\uff0c\u65a9\u8349\u9664\u6839\uff0c\u4ee5\u7edd\u540e\u60a3\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u8fd9\u5927\u6982\u662f\u6b66\u6797\u4e00\u6869\u5947\u5927\u7684\u9690\u79d8\uff0c\u5e73\u5e38\u4e4b\u4eba\u81ea\u7136\u662f\u5f88\u96be\u4e86\u89e3\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u5176\u4e2d\u5b9a\u7136\u6709\u5f88\u4eba\u7684\u539f\u56e0\uff0c\u4f46\u8fd9\u539f\u56e0\uff0c\u9664\u4e86\u5251\u795e\u548c\u4e94\u5927\u9b54\u4e3b\u4e4b\u5916\uff0c\u53ea\u6015\u662f\u5f88\u5c11\u6709\u4eba\u77e5\u6653\u4e86\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u9b54\u5200\u7530\u8fdc\u6bd4\u8d77\u4e94\u4e2a\u9b54\u4e3b\u81ea\u4e0d\u53ef\u540c\u65e5\u800c\u8bba\uff0c\u96be\u9053\u6b66\u6797\u4e4b\u4e2d\uff0c\u5c31\u65e0\u4eba\u80fd\u591f\u5236\u670d\u4ed6\u5417\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u5c11\u6797\u7684\u5165\u51e1\u5927\u5e08\uff0c\u6b66\u5f53\u7684\u767d\u62c2\u9053\u957f\u7b49\uff0c\u90fd\u6709\u80fd\u529b\u5236\u670d\u9b54\u5200\u7530\u8fdc\u2026\u2026\u201d \u3000 \u7b2c\u4e94\u56de\u3000\u79d8\u4f20\u7edd\u5b66 \u3000 \u3000\u3000\u8a00\u51e4\u537f\u5411\u65b9\u5929\u6210\u7591\u60d1\u5730\u95ee\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u5c11\u6797\u6b66\u5f53\u90fd\u662f\u6b63\u5927\u95e8\u6d3e\uff0c\u8fd9\u4e9b\u4eba\u53c8\u4e3a\u4f55\u4e0d\u80af\u51fa\u6765\u5236\u670d\u90a3\u9b54\u5200\u7530\u8fdc\uff0c\u800c\u8ba9\u4ed6\u7eb5\u6a2a\u6c5f\u6e56\uff0c\u4e3a\u5bb3\u4eba\u95f4\u5462\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u5c11\u6797\u5165\u51e1\u5927\u5e08\u548c\u6b66\u5f53\u767d\u62c2\u9053\u957f\u90fd\u8d25\u5728\u5929\u9b54\u5973\u7684\u624b\u4e2d\uff0c\u4e24\u4eba\u53d7\u632b\u4e4b\u540e\uff0c\u5c31\u5f88\u5c11\u518d\u5728\u6c5f\u6e56\u51fa\u73b0\u2026\u2026\u201d \u3000\u3000\u8a00\u51e4\u537f\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u53ef\u60dc\u8a00\u67d0\u4eba\u6b66\u529f\u6709\u9650\uff0c\u5982\u82e5\u6211\u6709\u5251\u795e\u4e4b\u80fd\uff0c\u5fc5\u5c06\u5c3d\u6b7c\u7fa4\u9b54\uff0c\u4e3a\u6b66\u6797\u6e05\u9664\u5996\u6c14\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u63a5\u9053\uff1a\u201c\u4e5f\u8bb8\u9b54\u5200\u7530\u8fdc\u7684\u4e3a\u6076\u4e0d\u8457\uff0c\u4ed6\u4e00\u76f4\u9690\u5728\u5e55\u540e\uff0c\u6307\u6325\u722a\u7259\u6d3b\u52a8\uff0c\u6240\u4ee5\uff0c\u4e16\u4eba\u90fd\u5df2\u628a\u4ed6\u5fd8\u6000\u4e86\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u4f46\u8d35\u5144\u5f1f\u53c8\u4e3a\u4f55\u548c\u9b54\u5200\u7530\u8fdc\u7ed3\u4e0b\u6881\u5b50\u5462\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u90a3\u662f\u6211\u4eec\u4f24\u4e86\u4ed6\u7684\u722a\u7259\uff0c\u7530\u8fdc\u624d\u51fa\u9762\uff0c\u548c\u6211\u4eec\u8ba2\u4e0b\u7ea6\u671f\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u8d35\u5144\u5f1f\u65e2\u77e5\u975e\u90a3\u9b47\u5200\u7530\u8fdc\u4e4b\u654c\uff0c\u4e0d\u77e5\u4f55\u4ee5\u4e0d\u80af\u7ea6\u4eba\u52a9\u62f3\u5462\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u7ea6\u8c01\u5462\u3002\u201d\u8bed\u58f0\u5fae\u4ee5\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u672c\u6765\u6211\u4eec\u8d76\u6765\u5609\u5b9a\uff0c\u4e43\u662f\u548c\u4e00\u4f4d\u5c11\u6797\u9ad8\u50e7\u6709\u7ea6\u2026\u2026\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u5165\u51e1\u5927\u5e08\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u4e0d\u8fc7\u4e5f\u662f\u5927\u6709\u540d\u9ad8\u671b\u7684\u9ad8\u50e7\uff0c\u90a3\u4f4d\u5927\u5e08\u548c\u90d1\u5927\u54e5\u662f\u4e16\u5916\u4e4b\u4ea4\uff0c\u7b54\u5141\u4e86\u5c4a\u65f6\u52a9\u6211\u4eec\u4e00\u81c2\u4e4b\u529b\uff0c\u7ea6\u6211\u4eec\u5728\u5609\u5b9a\u4f1a\u9762\uff0c\u4ed6\u56de\u5c11\u6797\u5bfa\u53bb\uff0c\u518d\u7ea6\u4e24\u4f4d\u5e08\u5144\u76f8\u52a9\uff0c\u51c6\u5907\u4e00\u4e3e\u6b7c\u706d\u9b54\u5200\u7530\u8fdc\uff01\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u90a3\u4f4d\u5927\u5e08\u5462\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4ed6\u662f\u6709\u9053\u9ad8\u50e7\uff0c\u5931\u7ea6\u4e0d\u6765\uff0c\u4e5f\u8bb8\u662f\u522b\u6709\u539f\u56e0\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u5509\uff01\u51e0\u4f4d\u679c\u7136\u90fd\u6709\u7740\u4e49\u4fa0\u80f8\u6000\uff0c\u660e\u77e5\u6b64\u53bb\uff0c\u96be\u662f\u4eba\u654c\uff0c\u4f46\u4ecd\u7136\u8981\u6177\u6168\u8d74\u4e49\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8a00\u5144\u4e0d\u77e5\u9b54\u5200\u7530\u8fdc\u7684\u4e3a\u4eba\uff0c\u6211\u4eec\u5982\u82e5\u4e0d\u6309\u65f6\u8d74\u7ea6\uff0c\u4ed6\u4ec0\u4e48\u624b\u6bb5\u90fd\u505a\u5f97\u51fa\u6765\uff0c\u8fd9\u51e0\u6708\u4e2d\uff0c\u6211\u4eec\u5df2\u9690\u9690\u89c9\u5230\uff0c\u6709\u4eba\u5728\u6697\u4e2d\u76d1\u89c6\u7740\u6211\u4eec\u3002\u201d \u3000\u3000\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u63a5\u9053\uff1a\u201c\u5728\u4e0b\u5df2\u7ecf\u8bf4\u7684\u592a\u591a\uff0c\u5927\u6982\u60c5\u5f62\u5c31\u662f\u5982\u6b64\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u5c0f\u5f1f\u548c\u90d1\u5144\u5b9a\u4ea4\u65f6\uff0c\u65b9\u5144\u9002\u4e0d\u5728\u573a\uff0c\u4f46\u5144\u5f1f\u5bf9\u65b9\u5144\u7684\u4e3a\u4eba\uff0c\u6781\u662f\u656c\u670d\uff0c\u4eca\u4e0e\u65b9\u4e8c\u4fa0\u4e00\u5e2d\u8c08\u8bdd\uff0c\u66f4\u89c9\u4e2d\u5dde\u4e09\u4fa0\u540d\u4e0d\u865a\u4f20\u2026\u2026\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u8a00\u5144\u8fc7\u5956\u4e86\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u5144\u5f1f\u6709\u4e00\u4e0d\u60c5\u4e4b\u6c42\uff0c\u4e0d\u77e5\u65b9\u4e8c\u4fa0\u662f\u5426\u5e94\u5141\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u8a00\u5144\u6709\u4f55\u5429\u5490\uff0c\u53ea\u7ba1\u8bf7\u8bf4\uff0c\u8a00\u5144\u62b1\u6ee1\u6000\u5e0c\u671b\u800c\u6765\uff0c\u6211\u7b49\u4e0d\u80fd\u76f8\u52a9\uff0c\u4f7f\u8a00\u5144\u5927\u611f\u5931\u671b\uff0c\u5fc3\u4e2d\u5df2\u6781\u4e3a\u4e0d\u5b89\u4e86\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4f46\u6211\u7b49\u5fc5\u5c06\u5728\u8fd9\u4e24\u4e09\u65e5\u4e4b\u5185\uff0c\u7aed\u5c3d\u6240\u77e5\u7684\u52a9\u8a00\u5144\u63a2\u7d22\u4f60\u4eec\u8a00\u5bb6\u95e8\u5931\u53bb\u7684\u6b66\u529f\uff0c\u5e0c\u671b\u80fd\u5f04\u51fa\u4e00\u70b9\u7ebf\u7d22\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u6447\u5934\u63a5\u9053\uff1a\u201c\u8a00\u5bb6\u95e8\u7684\u6b66\u529f\uff0c\u5df2\u6d41\u5931\u4e94\u5341\u5e74\uff0c\u6025\u4e5f\u4e0d\u5728\u8fd9\u4e00\u65f6\uff0c\u5012\u662f\u8d35\u5144\u5f1f\u7684\u4e8b\uff0c\u6700\u4e3a\u7d27\u6025\uff0c\u5144\u5f1f\u81ea\u77e5\u6b66\u529f\u4e0d\u8db3\u76f8\u52a9\u4e09\u4f4d\u2026\u2026\u201d \u3000\u3000\u65b9\u5929\u6210\u63a5\u9053\uff1a\u201c\u7530\u8fdc\u624b\u4e2d\u4e00\u628a\u5200\uff0c\u5df2\u5230\u4e86\u51fa\u795e\u5165\u5316\u4e4b\u5883\uff0c\u6545\u6709\u9b54\u5200\u4e4b\u8a89\uff0c\u6c5f\u6e56\u4e0a\u4e5f\u4ed6\u654c\u624b\u4e4b\u4eba\u5c48\u6307\u53ef\u6570\uff0c\u6211\u4eec\u4e5f\u672a\u5b58\u9080\u4eba\u52a9\u62f3\u4e4b\u5fc3\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u5144\u5f1f\u8bf7\u6c42\u65b9\u4e8c\u4fa0\u7b54\u5141\u5144\u5f1f\uff0c\u4f7f\u6211\u80fd\u4e00\u540c\u524d\u5f80\uff0c\u4ece\u65c1\u89c2\u6218\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u5fae\u5fae\u4e00\u6014\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u4e0d\u59a5\u5427\uff01\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u5144\u5f1f\u5fc3\u610f\u5df2\u51b3\uff0c\u5c31\u662f\u65b9\u4e8c\u4fa0\u4e0d\u7b54\u5e94\uff0c\u5728\u4e0b\u4e5f\u8981\u8ddf\u53bb\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u76b1\u76b1\u76fe\u5934\uff0c\u9053\uff1a\u201c\u8a00\u5144\u4e3a\u4f55\u4e00\u5b9a\u8981\u8ddf\u7740\u6211\u7b49\u6dcc\u8fd9\u6b21\u6d51\u6c34\u5462\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u65b9\u4e8c\u4fa0\u53ef\u4ee5\u4e0d\u5141\u5144\u5f1f\u6240\u8bf7\uff0c\u4f46\u4f60\u603b\u65e0\u6cd5\u6321\u4f4f\u5144\u5f1f\u8ffd\u8e2a\u8ddf\u5f80\u5427\uff01\u201d\u7a81\u95fb\u4e00\u8f7b\u788e\u7684\u6b65\u5c65\u58f0\uff0c\u4f20\u4e86\u8fc7\u6765\uff0c\u6253\u65ad\u4e86\u4e24\u4eba\u7684\u8c08\u8bdd\u3002 \u3000\u3000\u8a00\u51e4\u537f\u62ac\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u4e00\u4e2a\u8eab\u7740\u7eff\u8863\u7684\u7f8e\u4e3d\u5987\u4eba\uff0c\u5e26\u7740\u4e24\u4e2a\u7ae5\u5b50\u884c\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u90a3\u5987\u4eba\u4e00\u8f6c\u76ee\uff0c\u77a7\u5230\u4e86\u65b9\u5929\u6210\uff0c\u76f4\u5411\u5ba4\u4e2d\u884c\u6765\u3002 \u3000\u3000\u884c\u5230\u95e8\u53e3\u5904\uff0c\u5927\u7ea6\u662f\u77a7\u89c1\u4e86\u8a00\u51e4\u537f\uff0c\u7acb\u65f6\u505c\u4e0b\u4e86\uff0c\u9053\uff1a\u201c\u8d31\u59be\u4e0d\u77e5\u592b\u541b\u4f1a\u5ba2\u2026\u2026\u201d \u3000\u3000\u65b9\u5929\u6210\u8d77\u8eab\u63a5\u9053\uff1a\u201c\u8a00\u5144\u4e0d\u662f\u5916\u4eba\uff0c\u592b\u4eba\u8bf7\u8fdb\u6765\u5427\uff01\u201d \u3000\u3000\u7eff\u8863\u5987\u4eba\u7565\u4e00\u72b9\u8c6b\uff0c\u7f13\u6b65\u884c\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u65b9\u5929\u6210\u6307\u7740\u8a00\u51e4\u537f\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u8fb0\u5dde\u8a00\u5bb6\u7684\u638c\u95e8\u4eba\u3002\u201d \u3000\u3000\u7eff\u8863\u5987\u4eba\u4e00\u6b20\u8eab\u9053\uff1a\u201c\u89c1\u8fc7\u8a00\u638c\u95e8\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u671b\u7740\u7eff\u8863\u5987\u4eba\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u8d31\u5185\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u4e00\u62b1\u62f3\uff0c\u9053\uff1a\u201c\u89c1\u8fc7\u5ac2\u592b\u4eba\u3002\u201d \u3000\u3000\u77f3\u4fca\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u4e8c\u5ac2\u8bf7\u5750\u3002\u201d \u3000\u3000\u7eff\u8863\u5987\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u6b20\u8eab\u5750\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u8a00\u98ce\u537f\u76ee\u5149\u4e00\u8f6c\uff0c\u53ea\u89c1\u4e24\u4e2a\u968f\u8eab\u7ae5\u5b50\uff0c\u90fd\u7a7f\u7740\u4e00\u8eab\u9752\u886b\uff0c\u5de6\u9762\u4e00\u4eba\u770b\u4e0a\u53bb\u8f83\u4e3a\u6587\u9759\u4e00\u4e9b\uff0c\u4e00\u8138\u4e66\u751f\u6c14\uff0c\u53f3\u9762\u4e00\u4e2a\u4f3c\u8f83\u6d3b\u6cfc\uff0c\u5e38\u5e38\u8f6c\u76ee\u56db\u987e\u3002 \u3000\u3000\u53ea\u542c\u65b9\u592b\u4eba\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u8d31\u59be\u542c\u8bf4\u5927\u54e5\u53d7\u4f24\u5f88\u91cd\uff0c\u7279\u5730\u5e26\u7740\u745c\u513f\u73de\u513f\uff0c\u770b\u770b\u5927\u4f2f\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u5927\u54e5\u521a\u521a\u7761\u7740\uff0c\u4e0d\u8981\u53bb\u60ca\u52a8\u4ed6\u4e86\u2026\u2026\u201d\u76ee\u5149\u4e00\u63a0\u4e24\u4e2a\u9752\u8863\u7ae5\u5b50\uff0c\u9053\uff1a \u3000\u3000\u201c\u4f60\u4eec\u89c1\u8fc7\u8a00\u53d4\u7236\u3002\u201d \u3000\u3000\u65b9\u745c\u3001\u65b9\u73de\u9f50\u9f50\u4e00\u4e2a\u957f\u63d6\uff0c\u9053\uff1a\u201c\u62dc\u89c1\u8a00\u53d4\u7236\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u6b20\u8eab\u8fd8\u4e86\u4e00\u793c\uff0c\u7b11\u9053\uff1a\u201c\u597d\u806a\u660e\u7684\u5b69\u5b50\uff0c\u4eca\u5e74\u51e0\u5c81\u4e86\uff1f\u201d \u3000\u3000\u65b9\u745c\u3001\u65b9\u73de\u9f50\u58f0\u5e94\u9053\uff1a\u201c\u5341\u4e94\u5c81\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u5fc3\u4e2d\u6697\u9053\uff1a\u8fd9\u4e24\u4eba\u867d\u90fd\u6e05\u79c0\u53ef\u7231\uff0c\u4f46\u5de6\u9762\u4e00\u4e2a\uff0c\u9aa8\u683c\u6c14\u5ea6\uff0c\u6e05\u5385\u51fa\u5c18\uff0c\u4e0d\u4f3c\u4e00\u6bcd\u53cc\u751f\uff0c\u600e\u7684\u4e24\u4eba\u90fd\u5341\u4e94\u5c81\u5462\uff1f \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u91cc\u5374\u542b\u542b\u7cca\u7cca\u7684\u5e94\u9053\uff1a\u201c\u4e24\u4f4d\u516c\u5b50\u4eba\u54c1\u4e0d\u4fd7\uff0c\u65e5\u540e\u5fc5\u6709\u5927\u6210\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u53cc\u76ee\u51dd\u6ce8\u5728\u90a3\u7eff\u8863\u5987\u4eba\u8138\u4e0a\uff0c\u77a7\u4e86\u4e00\u9635\uff0c\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8a00\u5144\u5982\u662f\u60f3\u52a9\u6211\u4eec\uff0c\u5144\u5f1f\u5012\u6709\u4e00\u4e8b\u76f8\u6258\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u65b9\u5144\u6709\u4e8b\uff0c\u53ea\u7ba1\u5429\u5490\uff0c\u5144\u5f1f\u529b\u80fd\u6240\u53ca\uff0c\u65e0\u4e0d\u5168\u529b\u4ee5\u8d74\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u671b\u4e86\u671b\u7231\u59bb\u548c\u65b9\u745c\u3001\u65b9\u73de\u4e00\u773c\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u8d24\u59bb\u5e26\u745c\u513f\u3001\u73de\u513f\u597d\u597d\u4f11\u606f\u4e24\u5929\uff0c\u54b1\u4eec\u8fd8\u5f97\u4f4f\u4e24\u5929\u624d\u6210\u3002\u201d \u3000\u3000\u7eff\u8863\u5987\u4eba\u5341\u5206\u67d4\u987a\u7684\u7ad9\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u8c08\u8c08\u5427\uff01\u6211\u5148\u544a\u9000\u3002\u201d\u5fae\u5fae\u6b20\u8eab\u5bf9\u8a00\u51e4\u537f\u4e00\u793c\uff0c\u7275\u7740\u4e24\u4e2a\u7ae5\u5b50\uff0c\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u65b9\u5929\u6210\u76ee\u6ce8\u4e24\u4eba\u53bb\u8fdc\u4e4b\u540e\uff0c\u624d\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5144\u5f1f\u8fd9\u4e9b\u8bdd\uff0c\u6709\u4e9b\u8a00\u6df1\u4ea4\u6d45\u4e86\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u5947\u9053\uff1a\u201c\u65b9\u4e8c\u4fa0\u4f46\u8bf7\u5429\u5490\uff0c\u53ea\u8981\u5c0f\u5f1f\u529b\u80fd\u6240\u53ca\uff0c\u51b3\u4e0d\u63a8\u8f9e\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u6211\u4eec\u548c\u9b54\u5200\u7530\u8fdc\u4e4b\u7ea6\uff0c\u51f6\u591a\u5409\u5c11\uff0c\u5df2\u65e0\u7591\u95ee\uff0c\u8a00\u5144\u4e00\u8154\u70ed\u60c5\uff0c\u5144\u5f1f\u5c31\u6709\u8bdd\u76f4\u8bf4\u4e86\uff0c\u6211\u4eec\u6b64\u756a\u524d\u53bb\u8d74\u7ea6\uff0c\u552f\u4e00\u7684\u613f\u671b\uff0c\u5c31\u662f\u5e0c\u671b\u6211\u4eec\u4e09\u4eba\u4e4b\u4e2d\uff0c\u6709\u4e00\u4e2a\u6216\u4e24\u4e2a\u80fd\u4fdd\u4e0b\u6027\u547d\uff0c\u5144\u5f1f\u4e3a\u6b64\uff0c\u7279\u5730\u5728\u6df1\u5c71\u4e4b\u4e2d\uff0c\u7ec3\u6210\u4e86\u5341\u7c92\u4fdd\u547d\u91d1\u4e39\uff0c\u4e0d\u7ba1\u4f24\u52bf\u5982\u4f55\u60e8\u91cd\uff0c\u53ea\u8981\u80fd\u591f\u5f53\u573a\u4e0d\u6b7b\uff0c\u6211\u4eec\u5c31\u6709\u4e86\u4fdd\u547d\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u5947\u9053\uff1a\u201c\u65b9\u5144\u4e4b\u8a00\uff0c\u7528\u610f\u4f55\u5728\uff0c\u5144\u5f1f\u6709\u4e9b\u542c\u4e0d\u660e\u767d\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4e8b\u60c5\u5f88\u7b80\u5355\uff0c\u8a00\u5144\u6765\u7684\u51d1\u5de7\uff0c\u5144\u5f1f\u6069\u628a\u62d9\u8346\u548c\u4e24\u4f4d\u5b64\u513f\uff0c\u5949\u6258\u8a00\u5144\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u4e00\u76b1\u76fe\u5934\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u8d8a\u542c\u8d8a\u7cca\u6d82\u4e86\u3002\u201d \u3000\u3000\u77f3\u4fca\u63a5\u9053\uff1a\u201c\u4e8c\u54e5\u8bf4\u6162\u4e00\u4e9b\uff0c\u8a00\u5144\u4e0d\u77e5\u5185\u60c5\uff0c\u4f60\u8fd9\u7b49\u8bf4\u6cd5\uff0c\u81ea\u7136\u662f\u542c\u4e0d\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u8bf4\u7684\u592a\u7b80\u7565\u4e86\u2026\u2026\u201d\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u90a3\u9b54\u5200\u7530\u8fdc\uff0c\u6709\u4e00\u4e2a\u602a\u7656\uff0c\u53ea\u8981\u4e2d\u4eba\u4e00\u5200\uff0c\u4e0d\u7ba1\u4f60\u751f\u6b7b\uff0c\u5c31\u4e0d\u518d\u4f24\u4eba\u7b2c\u4e8c\u5200\u4e86\uff0c\u6240\u4ee5\uff0c\u6211\u4eec\u51c6\u5907\u53d7\u4ed6\u4e00\u5200\u2026\u2026\u201d \u3000\u3000\u77f3\u4fca\u63a5\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u90a3\u7530\u8fdc\u4e00\u5200\u4e4b\u4e0b\uff0c\u5f88\u5c11\u5e78\u5b58\u4e4b\u4eba\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\u2026\u2026\u201d\u957f\u957f\u53f9\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u4e09\u4f4d\u80fd\u65e9\u7ec3\u91d1\u4e39\uff0c\u4ee5\u4fdd\u6027\u547d\uff0c\u96be\u9053\u5c31\u4e0d\u80fd\u8bbe\u6cd5\u9080\u8bf7\u4e2a\u5bf9\u4ed8\u7530\u8fdc\u7684\u9ad8\u624b\u5417\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u90a3\u4f4d\u5c11\u6797\u9ad8\u50e7\u5931\u7ea6\u672a\u6765\uff0c\u4e00\u65f6\u95f4\u6211\u4eec\u53c8\u5f80\u4f55\u5904\u9080\u4eba\u5462\uff1f\u6b66\u529f\u4e0d\u53ca\u7530\u8fdc\u7684\u7eb5\u6709\u76f8\u52a9\u6211\u4eec\u4e4b\u5fc3\uff0c\u6211\u4eec\u4e5f\u4e0d\u4f1a\u63a5\u53d7\uff0c\u6b66\u529f\u9ad8\u8fc7\u7530\u8fdc\u4e4b\u4eba\uff0c\u5c48\u6307\u53ef\u6570\uff0c\u5927\u90fd\u672a\u66fe\u6664\u9762\uff0c\u8a00\u5144\u81ea\u5df1\u4eba\uff0c\u5144\u5f1f\u4e5f\u4e0d\u63a9\u9970\u4e86\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u65b9\u5144\u4e4b\u610f\uff0c\u53ef\u662f\u8981\u5144\u5f1f\u7167\u987e\u592b\u4eba\u548c\u4e24\u4f4d\u516c\u5b50\u5417\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u672c\u6765\u6211\u4eec\u8fd8\u6709\u5145\u5206\u7684\u65f6\u95f4\u5b89\u6392\u62d9\u8346\uff0c\u60f3\u4e0d\u5230\u4e3a\u4e94\u9b3c\u7684\u4e8b\uff0c\u5728\u5609\u5b9a\u4f4f\u4e86\u8fd9\u591a\u65f6\u95f4\uff0c\u8a00\u5144\u5982\u82e5\u968f\u540c\u6211\u7b49\u524d\u5f80\u52a9\u62f3\uff0c\u90a3\u4e0d\u8fc7\u662f\u591a\u9001\u7ed9\u7530\u8fdc\u4e00\u6761\u547d\uff0c\u5b9e\u662f\u4e0e\u4e8b\u65e0\u8865\uff0c\u8a00\u5144\u4e3a\u8ffd\u5bfb\u8a00\u5bb6\u95e8\u5931\u53bb\u6b66\u529f\u7684\u4e8b\uff0c\u5954\u8d70\u5929\u6daf\uff0c\u6258\u4ed8\u59bb\u513f\u4e4b\u4e8b\uff0c\u5728\u4e0b\u4e5f\u4e0d\u4fbf\u51fa\u53e3\uff0c\u53ea\u671b\u8a00\u5144\u62bd\u51fa\u51e0\u65e5\u65f6\u95f4\u628a\u62d9\u8346\u72ac\u5b50\u9001\u5f80\u4e00\u4e2a\u53bb\u5904\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u9001\u5f80\u4f55\u5730\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u5728\u4e0b\u4fee\u597d\u4e86\u4e00\u5c01\u4e66\u4fe1\uff0c\u4e0a\u9762\u5199\u6709\u53bb\u5904\uff0c\u6211\u4eec\u4e0d\u65e5\u52a8\u8eab\uff0c\u800c\u4e14\u4e5f\u4e0d\u7528\u5bf9\u5979\u4eec\u8bf4\u660e\uff0c\u4e0d\u77e5\u8a00\u5144\u53ef\u5426\u5e94\u5141\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u542c\u4ed6\u4e86\u534a\u5929\uff0c\u4ecd\u672a\u8bf4\u51fa\u53bb\u5904\uff0c\u77e5\u4ed6\u4f5c\u4e8b\u7f1c\u5bc6\uff0c\u4e0d\u5230\u8981\u7d27\u4e4b\u65f6\uff0c\u4e0d\u613f\u8bf4\u51fa\uff0c\u4e5f\u5c31\u4e0d\u518d\u591a\u95ee\uff0c\u70b9\u70b9\u5934\u9053\uff1a\u201c\u597d\uff01\u5144\u5f1f\u5c3d\u6211\u4e4b\u80fd\uff0c\u628a\u592b\u4eba\u548c\u4e24\u4f4d\u4ee4\u90ce\u9001\u5230\u5c31\u662f\uff0c\u9664\u975e\u5144\u5f1f\u6b7b\u53bb\u4e4b\u5916\uff0c\u51b3\u4e0d\u8bef\u65b9\u5144\u4e4b\u4e8b\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u62b1\u62f3\u4e00\u793c\uff0c\u9053\uff1a\u201c\u5144\u5f1f\u8fd9\u91cc\u5148\u884c\u62dc\u8c22\u4e86\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u8fd8\u4e86\u4e00\u793c\uff0c\u9053\uff1a\u201c\u533a\u533a\u5fae\u52b3\uff0c\u4f55\u6562\u5f53\u6b64\u4e00\u793c\uff01\u201d \u3000\u3000\u77f3\u4fca\u8d77\u8eab\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u8fd9\u91cc\u4e5f\u4ee3\u4e8c\u54e5\u4e8c\u5ac2\uff0c\u62dc\u8c22\u4e00\u793c\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u53f9\u9053\uff1a\u201c\u8bf8\u4f4d\u8fd9\u822c\u591a\u793c\uff0c\u53cd\u53eb\u5144\u5f1f\u4e0d\u5b89\u4e86\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8a00\u5144\u4e00\u8bfa\u5343\u91d1\uff0c\u54b1\u4f4d\u4e0d\u8c08\u6b64\u4e8b\u2026\u2026\u201d\u8bed\u58f0\u4e00\u987f\uff0c\u9053\uff1a\u201c\u6765\uff01 \u3000\u3000\u8a00\u5144\u8bf7\u53d6\u51fa\u90a3\u56fe\u753b\u6765\uff0c\u54b1\u4eec\u4ed4\u7ec6\u7814\u7a76\u4e00\u4e0b\u90a3\u6bd2\u9f99\u6f6d\uff0c\u4e5f\u8bb8\u5144\u5f1f\u80fd\u591f\u63d0\u4f9b\u4e00\u70b9\u7ebf\u7d22\u3002\u201d \u3000\u3000\u4e14\u8bf4\u65b9\u592b\u4eba\u5e26\u7740\u65b9\u745c\u3001\u65b9\u73de\u76f4\u5411\u540e\u5b85\uff0c\u7a0b\u592b\u4eba\u8fce\u4e8e\u5385\u5916\uff0c\u9053\uff1a\u201c\u90d1\u5927\u4fa0\u7684\u4f24\u52bf\u5982\u4f55\uff1f\u201d \u3000\u3000\u65b9\u592b\u4eba\u53f9\u9053\uff1a\u201c\u5927\u54e5\u7761\u719f\u4e86\uff0c\u5c0f\u59b9\u672a\u80fd\u5f97\u89c1\u3002\u201d \u3000\u3000\u7a0b\u592b\u4eba\u9053\uff1a\u201c\u542c\u8d31\u59be\u592b\u541b\u6240\u8a00\uff0c\u90d1\u5927\u4fa0\u4f24\u52bf\u5df2\u7136\u5927\u89c1\u597d\u8f6c\uff0c\u65b9\u592b\u4eba\u4e0d\u7528\u62c5\u5fc3\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u56de\u987e\u4e86\u65b9\u745c\u3001\u65b9\u73de\u4e24\u773c\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u81ea\u56de\u623f\u4e2d\u53bb\u5427\uff01\u201d \u3000\u3000\u4e24\u4eba\u5e94\u4e86\u4e00\u58f0\uff0c\u6084\u7136\u9000\u51fa\u3002 \u3000\u3000\u65b9\u745c\u5fc3\u4e2d\u6302\u5ff5\u90a3\u4e1c\u8033\u8001\u4eba\uff0c\u4e0d\u77e5\u8fd9\u4e00\u756a\u640f\u6597\u4e4b\u4e2d\uff0c\u4ed6\u662f\u5426\u53d7\u5230\u4f24\u5bb3\uff0c\u4f4e\u58f0\u5bf9\u65b9\u73de\u8bf4\u9053\uff1a\u201c\u5144\u5f1f\u5148\u56de\u623f\u53bb\uff0c\u6211\u6709\u70b9\u4e8b\uff0c\u5c31\u56de\u6765\u3002\u201d \u3000\u3000\u65b9\u73de\u81ea\u95f9\u51fa\u8fd9\u573a\u7eb7\u7eb7\u4e4b\u540e\uff0c\u81ea\u77e5\u8fd9\u573a\u6076\u6597\u5168\u7531\u81ea\u8eab\u5f15\u8d77\uff0c\u4f3c\u662f\u6210\u719f\u4e86\u5f88\u591a\uff0c\u70b9\u70b9\u5934\u9053\uff1a\u201c\u5927\u54e5\u65e9\u4e9b\u56de\u6765\uff0c\u6211\u8fd8\u6709\u4e8b\u95ee\u4f60\u3002\u201d \u3000\u3000\u65b9\u745c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff0c\u73b0\u5728\u8bf4\u4e5f\u662f\u4e00\u6837\u3002\u201d \u3000\u3000\u65b9\u73de\u9053\uff1a\u201c\u4f60\u5148\u53bb\u529e\u4e8b\u5427\uff01\u7b49\u4f60\u56de\u6765\u4e86\uff0c\u54b1\u4eec\u518d\u8c08\u3002\u201d \u3000\u3000\u8a00\u7f62\uff0c\u5f84\u81ea\u56de\u623f\u800c\u53bb\u3002 \u3000\u3000\u65b9\u745c\u95ee\u51fa\u4e86\u4e1c\u8033\u8001\u4eba\u7684\u505c\u8eab\u4e4b\u5904\uff0c\u90a3\u662f\u4e00\u5ea7\u5f88\u5927\u7684\u6808\u623f\uff0c\u7528\u4f5c\u5806\u5b58\u8d27\u7269\u4e4b\u5904\u3002\u65b9\u745c\u8f7b\u7740\u811a\u6b65\uff0c\u884c\u5230\u4e1c\u8033\u8001\u4eba\u8eab\u524d\uff0c\u4e0a\u4e0b\u77a7\u4e86\u4e00\u9635\uff0c\u770b\u4ed6\u5b8c\u597d\u65e0\u6059\uff0c\u53c8\u6084\u7136\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u884c\u7ea6\u4e24\u6b65\uff0c\u7a81\u542c\u4e1c\u8033\u8001\u4eba\u7684\u58f0\u97f3\uff0c\u4f20\u5165\u8033\u9645\uff0c\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u6709\u4e8b\u5417\uff1f\u201d \u3000\u3000\u65b9\u745c\u970d\u7136\u8f6c\u8fc7\u8eab\u5b50\uff0c\u53ea\u89c1\u4e1c\u8033\u8001\u4eba\u8138\u4e0a\u5e26\u7740\u6148\u853c\u7684\u5fae\u7b11\uff0c\u671b\u7740\u81ea\u5df1\uff0c\u5f53\u4e0b\u6025\u6b65\u884c\u4e86\u8fc7\u53bb\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u745c\u513f\u6302\u5ff5\u4f60\uff0c\u7279\u6765\u77a7\u77a7\uff0c\u89c1\u8001\u524d\u8f88\u719f\u7761\u65e0\u6059\uff0c\u4e0d\u6562\u60ca\u6270\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u4e3e\u624b\u4e00\u62db\uff0c\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u8fc7\u6765\uff0c\u6211\u4e5f\u6b63\u8981\u627e\u4f60\u3002\u201d \u3000\u3000\u65b9\u745c\u7f13\u6b65\u884c\u4e86\u8fc7\u53bb\uff0c\u606d\u606d\u656c\u656c\u884c\u4e86\u4e00\u793c\uff0c\u9053\uff1a\u201c\u8001\u524d\u8f88\u6709\u4f55\u5490\u5490\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u987a\u624b\u62c9\u8fc7\u4e00\u5f20\u6728\u6905\uff0c\u9053\uff1a\u201c\u5750\u4e0b\u6765\u3002\u201d \u3000\u3000\u65b9\u745c\u4f9d\u8a00\u5750\u4e0b\uff0c\u9053\uff1a\u201c\u665a\u8f88\u548c\u5927\u5a18\u4e00\u76f4\u90fd\u8eb2\u5728\u5730\u7a96\u4e4b\u4e2d\uff0c\u4e0d\u77e5\u5916\u9762\u60c5\u5f62\uff0c\u4eca\u6668\u51fa\u6765\uff0c\u624d\u77e5\u6653\u90d1\u5927\u4f2f\u7236\u6076\u6597\u53d7\u4f24\uff0c\u7a0b\u5bb6\u5e84\u6628\u5bb5\u5251\u5149\u5200\u5f71\uff0c\u6253\u5f97\u5341\u5206\u6fc0\u70c8\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5982\u4eca\u6b64\u4e8b\u5df2\u7ecf\u8fc7\u53bb\uff0c\u4e94\u9b3c\u6b7b\u53bb\u4e09\u4eba\uff0c\u54b1\u4eec\u5927\u83b7\u5168\u80dc\u4e86\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u5fae\u5fae\u4e00\u987f\uff0c\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u8fd9\u51e0\u65e5\u4f60\u4e00\u76f4\u5b88\u5728\u4f60\u5927\u5a18\u8eab\u8fb9\uff0c\u53ef\u66fe\u6309\u65f6\u7528\u529f\uff1f\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u8001\u524d\u8f88\u544a\u8bc9\u6211\uff0c\u4e0d\u80fd\u8ba9\u522b\u4eba\u77e5\u6653\u6b64\u4e8b\uff0c\u665a\u8f88\u81ea\u662f\u4e0d\u6562\u6cc4\u6f0f\u51fa\u53bb\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4f60\u5f88\u806a\u660e\u2026\u2026\u201d\u8bed\u58f0\u5fae\u5fae\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u6709\u4e00\u4ef6\u4e8b\u8001\u592b\u60f3\u4e0d\u660e\u767d\uff0c\u5e0c\u671b\u4f60\u636e\u5b9e\u56de\u7b54\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u8001\u592b\u95ee\u8fc7\u4f60\u51e0\u6b21\u59d3\u540d\u4e86\uff1f\u201d \u3000\u3000\u65b9\u745c\u6014\u4e86\u4e00\u6014\uff0c\u6c89\u601d\u4e86\u826f\u4e45\uff0c\u9053\uff1a\u201c\u597d\u50cf\u662f\u4e24\u6b21\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f46\u4f60\u4e24\u6b21\u544a\u8bc9\u8001\u592b\u7684\u5374\u662f\u5927\u4e0d\u76f8\u540c\uff0c\u7b2c\u4e00\u6b21\u4f60\u544a\u8bc9\u8001\u592b\u53eb\u65b9\u96ea\u5b9c\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u90a3\u662f\u6bcd\u4eb2\u7559\u7ed9\u6211\u4e00\u9762\u7389\u724c\u4e0a\u7684\u540d\u5b57\uff0c\u4f46\u4f2f\u7236\u5374\u66ff\u6211\u6539\u540d\u65b9\u745c\uff0c\u5176\u5b9e\u65b9\u745c\u548c\u65b9\u96ea\u5b9c\uff0c\u90fd\u662f\u6211\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u8f7b\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u5df2\u7ecf\u5f88\u5927\u4e86\uff0c\u4f60\u4f2f\u7236\u53ef\u6709\u548c\u4f60\u8c08\u8fc7\u4ee4\u5c0a\u7684\u4e8b\uff1f\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u5012\u662f\u5927\u5a18\u5076\u5c14\u8c08\u5230\u3002\u201d \u3000\u3000\u957f\u957f\u5401\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u7389\u724c\u4e0a\u5c1a\u6709\u6211\u7236\u6bcd\u66ff\u6211\u53d6\u7684\u540d\u5b57\uff0c\u6211\u5e38\u5e38\u53d6\u51fa\u7389\u724c\u628a\u73a9\uff0c\u90a3\u96ea\u5b9c\u4e8c\u5b57\u5df2\u7136\u6df1\u5165\u6211\u5fc3\uff0c\u4f46\u6211\u53c8\u89c9\u7740\u4f2f\u7236\u3001\u5927\u5a18\u517b\u6211\u957f\u5927\uff0c\u4ed6\u66ff\u6211\u6539\u7684\u540d\u5b57\uff0c\u5c82\u80fd\u4e0d\u7528\uff0c\u56e0\u6b64\u6211\u6709\u65f6\u89c9\u770b\u81ea\u5df1\u8be5\u53eb\u65b9\u96ea\u5b9c\uff0c\u6709\u65f6\u53c8\u89c9\u7740\u8be5\u53eb\u65b9\u745c\u624d\u5bf9\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u95ed\u76ee\u6c89\u601d\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4e0d\u7528\u4e3a\u8fd9\u4e8b\u56f0\u6270\u4e86\uff0c\u65b9\u745c\uff0c\u65b9\u96ea\u5b9c\u90fd\u65e0\u5173\u7d27\u8981\u3002\u201d \u3000\u3000\u65b9\u745c\u62ac\u8d77\u8138\u6765\uff0c\u671b\u7740\u4e1c\u8033\u8001\u4eba\uff0c\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u8fd9\u51e0\u65e5\u4e2d\uff0c\u665a\u8f88\u6df1\u5c45\u5730\u7a96\u4e4b\u4e2d\uff0c\u65e0\u4e66\u53ef\u8bfb\uff0c\u4e5f\u65e0\u6cd5\u8fd0\u52a8\uff0c\u56e0\u6b64\u60f3\u4e86\u5f88\u591a\u4e8b\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u4f60\u60f3\u7684\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u60f3\u5230\u8001\u524d\u8f88\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u7b11\u9053\uff1a\u201c\u60f3\u5230\u8001\u592b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u665a\u8f88\u89c9\u7740\u8001\u524d\u8f88\u5e76\u975e\u662f\u5c48\u5c45\u4eba\u4e0b\u7684\u4eba\uff0c\u4e0d\u77e5\u4f55\u4ee5\u7518\u613f\u5c48\u5c31\u4eba\u4ec6\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53ea\u6709\u8fd9\u4e9b\u5417\uff1f\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u8fd8\u6709\u5f88\u591a\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u5427\uff01\u628a\u4f60\u5fc3\u4e2d\u6240\u601d\u6240\u60f3\u5168\u90fd\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u665a\u8f88\u9690\u9690\u89c9\u7740\u8001\u524d\u8f88\u662f\u4e00\u7edd\u4ee3\u9ad8\u4eba\uff0c\u5c48\u4ece\u4eba\u4ec6\uff0c\u662f\u6709\u4e3a\u800c\u6765\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u62a5\u7b54\u6211\u65b9\u4f2f\u7236\u8d60\u836f\u76f8\u6551\u4e4b\u6069\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u8fd8\u6709\u5417\uff1f\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u4e3a\u4e86\u665a\u8f88\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u5148\u662f\u4e00\u6014\uff0c\u7ee7\u800c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u679c\u7136\u806a\u660e\uff0c\u8001\u592b\u672a\u770b\u9519\u4eba\u3002\u201d \u3000\u3000\u65b9\u745c\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u665a\u8f88\u5148\u5929\u4f53\u8d28\u4e0d\u597d\uff0c\u53ea\u6015\u6709\u8d1f\u8001\u524d\u8f88\u7684\u5e0c\u671b\u4e86\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u8c01\u8bf4\u7684\uff1f\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u6211\u4f2f\u7236\u8bf4\u7684\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u4ed6\u4f20\u8fc7\u4f60\u6b66\u529f\uff1f\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u90a3\u53ea\u662f\u4e00\u4e9b\u7528\u4f5c\u5f3a\u8eab\u7684\u6b66\u529f\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u4e5f\u8bb8\u4ed6\u770b\u4e0d\u51fa\u6765\u2026\u2026\u201d\u8bed\u58f0\u5fae\u5fae\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4e0a\u4e58\u7684\u6b66\u529f\uff0c\u9700\u8981\u7075\u8d28\u6167\u5fc3\uff0c\u4f53\u80fd\u4e0a\u7684\u7f3a\u61be\uff0c\u5c1a\u53ef\u501f\u91cd\u836f\u7269\u5f25\u8865\uff0c\u4f46\u624d\u534e\u5374\u65e0\u6cd5\u7528\u836f\u589e\u8fdb\u3002\u201d \u3000\u3000\u4ef0\u8d77\u8138\u6765\uff0c\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u9057\u61be\u7684\u662f\uff0c\u8001\u592b\u9047\u5230\u4f60\u592a\u665a\u4e86\u4e00\u4e9b\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u53ef\u662f\u56e0\u4e3a\u665a\u8f88\u7684\u5e74\u9f84\u592a\u5927\u4e86\uff0c\u5df2\u4e0d\u9002\u5408\u4e60\u7ec3\u4e0a\u4e58\u6b66\u529f\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u4f60\u867d\u7136\u5e74\u9f84\u7a0d\u957f\u4e00\u4e9b\uff0c\u4f46\u90a3\u5e76\u975e\u662f\u65e0\u6cd5\u633d\u56de\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u9664\u6b64\u4e4b\u5916\uff0c\u665a\u8f88\u6211\u60f3\u4e0d\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u90a3\u662f\u56e0\u4e3a\u8001\u592b\u5728\u4e16\u96be\u4e45\u4e86\u3002\u201d \u3000\u3000\u65b9\u745c\u4f3c\u662f\u4e0d\u76f8\u4fe1\u81ea\u5df1\u7684\u8033\u6735\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8001\u524d\u8f88\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u795e\u60c5\u5e73\u9759\u5730\u7b11\u9053\uff1a\u201c\u8001\u592b\u5728\u4e16\u4e0d\u4e45\u4e86\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u8001\u524d\u8f88\u4e0d\u662f\u5f88\u597d\u5417\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u8001\u592b\u5df2\u7ecf\u8ba1\u7b97\u8fc7\u6b7b\u4ea1\u7684\u65f6\u95f4\uff0c\u4e00\u5207\u987a\u5229\uff0c\u518d\u501f\u52a9\u836f\u7269\u76f8\u52a9\uff0c\u6700\u591a\u6211\u8fd8\u80fd\u6d3b\u4e0a\u4e00\u5e74\uff0c\u5982\u662f\u518d\u548c\u4eba\u52a8\u624b\uff0c\u591a\u8017\u5143\u6c14\uff0c\u90a3\u5c31\u5f88\u96be\u8bf4\uff0c\u4e5f\u8bb8\u534a\u5e74\uff0c\u4e5f\u8bb8\u53ea\u6709\u4e09\u4e2a\u6708\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u8fd9\u8bdd\u5f53\u771f\u5417\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u9a97\u4f60\uff1f\u8001\u592b\u5df2\u7ecf\u8017\u8d39\u4e86\u4e94\u5e74\u65f6\u5149\uff0c\u5e0c\u671b\u627e\u4e00\u4e2a\u4f20\u6211\u8863\u94b5\u4e4b\u4eba\uff0c\u5728\u8fd9\u4e94\u5e74\u4e2d\uff0c\u6211\u8db3\u8ff9\u8d70\u904d\u4e86\u5927\u6c5f\u5357\u5317\uff0c\u9605\u4eba\u5343\u4e07\u2014\u2014\u201d \u3000\u3000\u65b9\u745c\u63a5\u9053\uff1a\u201c\u4e94\u5e74\u4e2d\uff0c\u8001\u524d\u8f88\u5c31\u6ca1\u6709\u627e\u5230\u4e00\u4e2a\u4eba\u5417\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u826f\u6750\u96be\u6c42\uff0c\u8c08\u4f55\u5bb9\u6613\uff0c\u5c31\u5728\u6211\u5fc3\u7070\u610f\u61d2\u4e4b\u9645\uff0c\u9047\u5230\u4e86\u4f60\u2026\u2026\u201d\u957f\u957f\u53f9\u606f\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u54b1\u4eec\u65f6\u95f4\u4e0d\u591a\u4e86\uff0c\u53c8\u4e0d\u80fd\u7acb\u65f6\u7acb\u523b\u5e26\u4f60\u79bb\u6b64\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u5927\u5a18\u4e00\u5411\u5f85\u6211\u751a\u597d\uff0c\u6211\u53bb\u7980\u544a\u5927\u5a18\u4e00\u58f0\u2026\u2026\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u4e0d\u884c\uff0c\u4f60\u53d7\u90a3\u65b9\u5929\u6210\u592b\u5987\u517b\u80b2\u4e4b\u6069\uff0c\u5728\u4ed6\u4eec\u5927\u96be\u4e34\u5934\u4e4b\u9645\uff0c\u4f60\u5982\u4f55\u80fd\u5f03\u4ed6\u4eec\u800c\u53bb\uff1f\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u6211\u5927\u4f2f\u5927\u96be\u4e34\u5934\u2026\u2026\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u662f\u7684\uff0c\u5b69\u5b50\uff0c\u4ed6\u4eec\u8fd9\u6b21\u8d74\u9b54\u5200\u7530\u8fdc\u4e4b\u7ea6\uff0c\u6050\u6015\u662f\u51f6\u591a\u5409\u5c11\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u90a3\u7530\u8fdc\u6b66\u529f\u5f88\u9ad8\u5417\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u9b54\u5200\u7530\u8fdc\u6b66\u529f\u867d\u7136\u4e0d\u9519\uff0c\u4f46\u5982\u6bd4\u8d77\u90a3\u4e94\u5927\u9b54\u4e3b\uff0c\u90a3\u53c8\u662f\u5c0f\u5deb\u89c1\u5927\u5deb\u4e86\uff0c\u4f46\u4e2d\u5dde\u4e09\u4fa0\u5374\u8fdc\u975e\u90a3\u9b54\u5200\u7530\u8fdc\u4e4b\u654c\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u5927\u4f2f\u3001\u5927\u5a18\u90fd\u5f85\u6211\u6069\u60c5\u5982\u5c71\uff0c\u8fd8\u671b\u8001\u524d\u8f88\u770b\u5728\u665a\u8f88\u9762\u4e0a\uff0c\u6551\u4ed6\u4eec\u4e00\u6b21\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5982\u662f\u8001\u592b\u6ca1\u6709\u6551\u52a9\u4ed6\u4eec\u4e4b\u5fc3\uff0c\u4e5f\u4e0d\u4f1a\u544a\u8bc9\u4f60\u4e86\uff0c\u4e0d\u8fc7\uff0c\u8981\u4f60\u51fa\u624b\u6551\u4ed6\u4eec\u4e86\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u665a\u8f88\u5417\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u665a\u8f88\u8fd9\u70b9\u6b66\u529f\uff0c\u8fde\u6211\u90a3\u5144\u5f1f\u4e5f\u6253\u4e0d\u8fc7\uff0c\u5982\u4f55\u80fd\u591f\u6551\u4f2f\u7236\u3001\u5927\u5a18\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u4f60\u8981\u7ec3\u554a\uff01\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u6211\u90a3\u4f2f\u7236\u4f55\u65f6\u8d74\u7ea6\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u5c31\u5728\u6700\u8fd1\u51e0\u65e5\uff0c\u5927\u7ea6\u4e0d\u4f1a\u8d85\u8fc7\u5341\u65e5\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u5341\u65e5\u65f6\u5149\uff0c\u8f6c\u773c\u5373\u5c4a\uff0c\u665a\u8f88\u5c31\u7b97\u4e00\u523b\u4e5f\u4e0d\u4f11\u606f\uff0c\u53c8\u80fd\u7ec3\u6210\u4ec0\u4e48\u6b66\u529f\u5462\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u7b11\u9053\uff1a\u201c\u5982\u82e5\u4f60\u80af\u5168\u795e\u8d2f\u6ce8\u53bb\u5b66\uff0c\u5341\u65e5\u5de5\u592b\uff0c\u4e5f\u5c31\u591f\u4e86\u3002\u201d \u3000\u3000\u65b9\u745c\u534a\u4fe1\u534a\u7591\u4ed6\u8bf4\u9053\uff1a\u201c\u665a\u8f88\u8981\u5b66\u4ec0\u4e48\u6b66\u529f\uff0c\u80fd\u5728\u5341\u65e5\u4e4b\u5185\u901f\u6210\uff0c\u5bf9\u4ed8\u90a3\u9b54\u5200\u7530\u8fdc\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u5251\u6cd5\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u5251\u4e43\u5175\u5203\u4e4b\u7956\uff0c\u665a\u8f88\u542c\u4f2f\u7236\u8bf4\u8fc7\uff0c\u4e09\u5e74\u5200\u6cd5\u4e94\u5e74\u5251\uff0c\u81f3\u5c11\u8981\u4e94\u5e74\u624d\u80fd\u6709\u6210\uff0c\u533a\u533a\u5341\u65e5\uff0c\u80fd\u5b66\u51fa\u4ec0\u4e48\u5462\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u8001\u592b\u53ea\u4f20\u4f60\u4e09\u62db\u5251\uff0c\u4f60\u80fd\u591f\u8bb0\u719f\uff0c\u5c31\u8db3\u5413\u9000\u9b54\u5200\u7530\u8fdc\u4e86\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u53ea\u5b66\u4e09\u5251\u5417\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u662f\u7684\uff0c\u53ea\u5b66\u4e09\u5251\uff0c\u8fd9\u4e09\u5251\u4e43\u5251\u6cd5\u4e2d\u7cbe\u7cb9\u4e4b\u5b66\uff0c\u4e0d\u8fc7\uff0c\u4f60\u529f\u529b\u4e0d\u8db3\u4ee5\u9a6d\u5251\uff0c\u8eab\u624b\u96be\u4ee5\u548c\u5251\u6cd5\u914d\u5408\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u90a3\u662f\u8bf4\u5b66\u4f1a\u4e86\uff0c\u4e5f\u662f\u65e0\u7528\u5566\uff01\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u8fd9\u5c31\u8981\u770b\u4f60\u7684\u80c6\u6c14\u4e86\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u665a\u8f88\u4e0d\u660e\u767d\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u4f60\u53ea\u6709\u4e09\u5251\uff0c\u800c\u4e14\u529f\u529b\u4e0d\u5230\uff0c\u867d\u6709\u5947\u62db\uff0c\u4e5f\u96be\u4f24\u4eba\uff0c\u66f4\u65e0\u6cd5\u5728\u4e2d\u5dde\u4e09\u4fa0\u8eab\u9677\u5371\u5883\u4e4b\u65f6\uff0c\u8dc3\u5165\u573a\u4e2d\u89e3\u6551\uff0c\u552f\u4e00\u7684\u529e\u6cd5\uff0c\u5c31\u662f\u51ed\u4ed7\u7740\u4f60\u7684\u80c6\u6c14\uff0c\u4ece\u5bb9\u884c\u5165\u573a\u4e2d\uff0c\u7136\u540e\u7528\u51fa\u5251\u62db\uff0c\u60ca\u9000\u7530\u8fdc\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u4e00\u5b9a\u80fd\u591f\u5413\u9000\u4ed6\u5417\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5982\u662f\u5413\u4ed6\u4e0d\u9000\uff0c\u4f60\u53c8\u975e\u4ed6\u7684\u654c\u624b\uff0c\u53ea\u6709\u88ab\u4eba\u6740\u6b7b\u4e00\u9014\u4e86\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u8001\u524d\u8f88\u4e3a\u4f55\u4e0d\u80af\u51fa\u624b\u5462\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u8001\u592b\u4e0d\u662f\u8bf4\u8fc7\u4e86\u561b\uff0c\u6211\u5982\u591a\u6d88\u8017\u4e00\u4efd\u771f\u5143\u4e4b\u6c14\uff0c\u5c31\u63a5\u8fd1\u6b7b\u4ea1\u4e00\u6b65\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u9664\u6b64\u4e4b\u5916\uff0c\u522b\u65e0\u4ed6\u6cd5\u4e86\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u4f60\u5982\u65e0\u6b64\u80c6\u6c14\uff0c\u4e2d\u5dde\u4e09\u4fa0\u548c\u4f60\u90a3\u5927\u5a18\u5c31\u96be\u514d\u8981\u8eab\u906d\u6bd2\u624b\u3002\u201d \u3000\u3000\u65b9\u745c\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u62ac\u8d77\u5934\uff0c\u4e00\u8138\u575a\u51b3\u4e4b\u8272\uff0c\u9053\uff1a\u201c\u597d\uff0c\u665a\u8f88\u51e0\u65f6\u5b66\u90a3\u4e09\u5251\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u7acb\u65f6\u5c31\u5b66\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u73b0\u5728\u5417\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8001\u592b\u5148\u66ff\u4f60\u8bb2\u89e3\u90a3\u5251\u6cd5\u7684\u7a8d\u8981\u3002\u201d \u3000\u3000\u5f53\u4e0b\u5f88\u4ed4\u7ec6\u7684\u8ff0\u8bf4\u51fa\u4e09\u62db\u5251\u6cd5\u7684\u5999\u7528\uff0c\u4e00\u9762\u7528\u624b\u52bf\u8865\u52a9\u3002 \u3000\u3000\u65b9\u745c\u7684\u624d\u6167\uff0c\u867d\u7136\u8d85\u5f02\u5e38\u4eba\uff0c\u4f46\u4ed6\u6b66\u529f\u7684\u57fa\u7840\uff0c\u8ddd\u79bb\u8fd9\u7b49\u6df1\u5965\u7684\u5251\u672f\u592a\u8fdc\u4e86\uff0c\u867d\u7136\u7528\u5fc3\u53bb\u542c\uff0c\u4ecd\u662f\u65e0\u6cd5\u9886\u609f\u3002 \u3000\u3000\u4e1c\u8033\u8001\u4eba\u4e0d\u538c\u5176\u70e6\u5730\u4e00\u8fde\u8bb2\u8bf4\u6570\u904d\uff0c\u6bcf\u6b21\u90fd\u548c\u989c\u60a6\u8272\uff0c\u6beb\u65e0\u538c\u5026\u4e4b\u5bb9\u3002 \u3000\u3000\u65b9\u745c\u957f\u957f\u53f9\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u770b\u6765\u6211\u5f88\u7b28\uff0c\u53ea\u6015\u6709\u8d1f\u8001\u524d\u8f88\u7684\u5e0c\u671b\u4e86\u3002\u201d \u3000\u3000\u8001\u8033\u8001\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u7b97\u5f88\u806a\u660e\u4e86\uff0c\u4f60\u7684\u624d\u6167\u5df2\u8d85\u51fa\u6211\u5e0c\u671b\u4e4b\u5916\u3002\u201d \u3000\u3000\u65b9\u745c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8001\u524d\u8f88\u4e0d\u662f\u8ddf\u6211\u8bf4\u7b11\u8bdd\u5427\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u8001\u592b\u662f\u8bda\u5fc3\u8bda\u610f\u5730\u79f0\u8d5e\u4f60\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u6211\u5b66\u4e86\u8fd9\u6837\u4e45\uff0c\u4f60\u8bf4\u4e86\u5341\u6570\u904d\uff0c\u6211\u8fd8\u662f\u6709\u4e9b\u4e0d\u660e\u767d\uff0c\u90a3\u8fd8\u80fd\u7b97\u806a\u660e\u5417\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u6211\u4f20\u6388\u4f60\u7684\u5251\u672f\uff0c\u4e43\u662f\u5251\u6cd5\u4e2d\u81f3\u9ad8\u4e4b\u5b66\uff0c\u4ee5\u4f60\u6b64\u523b\u7684\u6b66\u529f\u57fa\u7840\u800c\u8bba\uff0c\u4f60\u662f\u6839\u672c\u4e0d\u4f1a\u4e86\u7136\uff0c\u522b\u8bf4\u6211\u8bf4\u5341\u904d\uff0c\u5c31\u662f\u8bf4\u4e0a\u4e00\u5343\u904d\u3001\u4e00\u4e07\u904d\uff0c\u4f60\u4e5f\u4e0d\u4f1a\u4e86\u89e3\uff0c\u4f46\u4f60\u5374\u4f3c\u6709\u4e9b\u9886\u609f\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u6211\u9886\u609f\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u9886\u609f\u4e86\u2026\u2026\u201d\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u73b0\u5728\u53ef\u4ee5\u56de\u53bb\uff0c\u505c\u7559\u592a\u4e45\uff0c\u4f1a\u5f15\u8d77\u4f60\u5927\u5a18\u7684\u7591\u5fc3\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u665a\u8f88\u5c1a\u672a\u5b66\u4f1a\uff0c\u5982\u4f55\u80fd\u8d70\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u4f60\u56de\u53bb\u4e4b\u540e\uff0c\u627e\u65f6\u95f4\u60f3\u60f3\u8001\u592b\u6240\u6388\u4f60\u7684\u5251\u6cd5\uff0c\u660e\u5929\u518d\u6765\u544a\u8bc9\u8001\u592b\u3002\u201d \u3000\u3000\u65b9\u745c\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u665a\u8f88\u9075\u547d\u3002\u201d\u606d\u606d\u656c\u656c\u5bf9\u90a3\u8001\u4eba\u62dc\u4e86\u4e00\u62dc\uff0c\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u4e1c\u8033\u8001\u4eba\u4e00\u76f4\u95ed\u76ee\u9759\u5750\u7740\u672a\u52a8\uff0c\u4e5f\u672a\u8fd8\u793c\uff0c\u76f4\u5f85\u90a3\u65b9\u745c\u6084\u7136\u79bb\u53bb\uff0c\u624d\u7741\u5f00\u53cc\u76ee\uff0c\u671b\u4e86\u65b9\u745c\u7684\u80cc\u5f71\u4e00\u773c\uff0c\u5173\u4e0a\u95e8\uff0c\u9759\u5750\u8c03\u606f\u3002 \u3000\u3000\u4e14\u8bf4\u65b9\u745c\u56de\u5230\u5185\u5b85\uff0c\u8111\u9645\u4e4b\u4e2d\uff0c\u4e00\u76f4\u76d8\u65cb\u7740\u90a3\u4e1c\u8033\u8001\u4eba\u7684\u8bb2\u89e3\uff0c\u4e00\u4e2a\u4eba\u5750\u5728\u623f\u4e2d\u51fa\u795e\u3002 \u3000\u3000\u4e0d\u77e5\u4e0d\u89c9\u8fc7\u53bb\u4e86\u591a\u5c11\u65f6\u95f4\uff0c\u65b9\u592b\u4eba\u4ece\u5bb9\u800c\u5165\u3002 \u3000\u3000\u65b9\u745c\u6b63\u60f3\u5f97\u5fc3\u795e\u8d2f\u6ce8\uff0c\u7adf\u7136\u4e0d\u77e5\u90a3\u65b9\u592b\u4eba\u51e0\u65f6\u884c\u5165\u4e86\u623f\u4e2d\u3002 \u3000\u3000\u65b9\u592b\u4eba\u7f13\u7f13\u884c\u5230\u65b9\u745c\u8eab\u524d\uff0c\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u5728\u60f3\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u745c\u5982\u68a6\u521d\u9192\uff0c\u554a\u4e86\u4e00\u58f0\uff0c\u624d\u77a7\u6e05\u695a\u6765\u4eba\u662f\u8c01\uff0c\u6025\u6025\u884c\u4e86\u4e00\u793c\uff0c\u9053\uff1a\u201c\u89c1\u8fc7\u5927\u5a18\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u9053\uff1a\u201c\u4f60\u60f3\u4ec0\u4e48\uff0c\u60f3\u7684\u90a3\u7b49\u5165\u795e\uff1f\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u4f84\u513f\u5728\u60f3\u2026\u2026\u5728\u60f3\u2026\u2026\u201d \u3000\u3000\u65b9\u592b\u4eba\u9053\uff1a\u201c\u5728\u60f3\u4f60\u6bcd\u4eb2\u662f\u5417\uff1f\u201d \u3000\u3000\u65b9\u745c\u5fae\u5fae\u4e00\u7b11\uff0c\u5782\u4e0b\u5934\u53bb\u3002 \u3000\u3000\u65b9\u592b\u4eba\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u8ddf\u4f60\u5927\u4f2f\u8bf4\u8fc7\uff0c\u6211\u4eec\u5f85\u4f60\u600e\u4e48\u597d\uff0c\u90fd\u65e0\u6cd5\u6d88\u53bb\u4f60\u601d\u5ff5\u6bcd\u4eb2\u4e4b\u5fc3\uff0c\u51e0\u6b21\u6211\u90fd\u60f3\u628a\u4f60\u9001\u5230\u4f60\u6bcd\u4eb2\u90a3\u91cc\u4f4f\u4e9b\u65f6\u65e5\uff0c\u4f46\u5374\u4e00\u76f4\u65e0\u6cd5\u627e\u5230\u4f60\u6bcd\u4eb2\u7684\u56fa\u5b9a\u4f4f\u5740\uff0c\u4ee5\u81f4\u8fdf\u8fdf\u65e0\u6cd5\u628a\u4f60\u9001\u53bb\u4e86\u3002\u201d \u3000\u3000\u65b9\u745c\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5927\u5a18\u5f85\u6211\u8d5b\u8fc7\u6211\u4eb2\u751f\u6bcd\u4eb2\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u5fc3\u91cc\u6709\u4e9b\u611f\u89c9\uff0c\u90a3\u5c31\u597d\u4e86\u2026\u2026\u201d \u3000\u3000\u8c08\u8bdd\u4e4b\u95f4\uff0c\u7a81\u89c1\u4eba\u666f\u4e00\u95ea\uff0c\u65b9\u73de\u884c\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u65b9\u592b\u4eba\u56de\u987e\u4e86\u65b9\u73de\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u65b9\u73de\u9053\uff1a\u201c\u5b69\u513f\u53bb\u7ec3\u4e86\u4e00\u904d\u5251\u6cd5\uff0c\u8fd9\u6b21\u548c\u4eba\u51b2\u649e\u4e4b\u540e\uff0c\u5b69\u513f\u89c9\u7740\u81ea\u5df1\u6b66\u529f\u592a\u574f\u4e86\uff0c\u8fc7\u53bb\uff0c\u6211\u4e00\u76f4\u4e0d\u80af\u7528\u5fc3\u5b66\u6b66\uff0c\u6b64\u540e\u8981\u597d\u597d\u5730\u7528\u5fc3\u7ec3\u4e60\u4e86\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u5f88\u597d\uff0c\u4f60\u4eec\u4f11\u606f\u4e00\u4e0b\u5427\uff01\u201d\u8f6c\u8eab\u7f13\u6b65\u800c\u4e00\u5411\u67d4\u548c\uff0c\u7ec8\u65e5\u9762\u5e26\u8005\u5fae\u7b11\u7684\u65b9\u592b\u4eba\uff0c\u6b64\u523b\uff0c\u5374\u663e\u7136\u6709\u7740\u5f88\u6c89\u91cd\u7684\u5fc3\u4e8b\uff0c\u5979\u867d\u7136\u4ecd\u548c\u5f80\u5e38\u4e00\u822c\u7684\u5e26\u7740\u5fae\u7b11\uff0c\u4f46\u76fe\u5b87\u95f4\u5374\u6d41\u73b0\u51fa\u4e00\u7f15\u6de1\u6de1\u7684\u5fe7\u82e6\u3002 \u3000\u3000\u65b9\u745c\u5fc3\u4e2d\u660e\u767d\uff0c\u65b9\u592b\u4eba\u5df2\u7136\u77e5\u6653\u65b9\u5929\u6210\u7b49\u8fd9\u6b21\u8d76\u8d74\u7684\u7ea6\u4f1a\uff0c\u51f6\u591a\u5409\u5c11\uff0c\u6545\u800c\u5185\u5fc3\u4e2d\u5fe7\u8651\u91cd\u91cd\u3002 \u3000\u3000\u65b9\u592b\u4eba\u5f62\u8bf8\u4e8e\u8272\u7684\u6101\u82e6\uff0c\u65e0\u5f62\u4e2d\u589e\u52a0\u65b9\u745c\u7684\u8d23\u4efb\uff0c\u53ea\u89c9\u8fd9\u756a\u548c\u9b54\u5200\u7530\u8fdc\u4e4b\u4f1a\uff0c\u80fd\u5426\u4fdd\u5f97\u65b9\u5929\u6210\u7684\u6027\u547d\uff0c\u5168\u8981\u770b\u81ea\u5df1\u80fd\u5426\u628a\u4e09\u62db\u5251\u6cd5\u7ec3\u7684\u719f\u6089\u4e86\u3002\u6b64\u5ff5\u65e2\u751f\uff0c\u6c42\u8fdb\u4e4b\u5fc3\uff0c\u66f4\u662f\u6bb7\u5207\u3002 \u3000\u3000\u65b9\u73de\u76ee\u7779\u6bcd\u4eb2\u80cc\u5f71\u6d88\u5931\uff0c\u4f4e\u58f0\u5bf9\u65b9\u745c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u77e5\u9053\u5417\uff1f\u90a3\u4e1c\u8033\u8001\u4eba\u662f\u4e00\u4f4d\u6b66\u529f\u5f88\u5f3a\u7684\u9ad8\u54e9\u2026\u2026\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u65b9\u73de\u9053\uff1a\u201c\u6211\u56de\u60f3\u4ed6\u6551\u6211\u7ecf\u8fc7\uff0c\u5982\u8457\u4e0d\u662f\u6b66\u529f\u5f88\u9ad8\u5f3a\uff0c\u5c82\u80fd\u8f7b\u6613\u628a\u6211\u6551\u56de\u6765\uff1f\u201d \u3000\u3000\u65b9\u745c\u5fc3\u4e2d\u6697\u9053\uff1a\u8fde\u4f60\u8fd9\u6761\u5c0f\u547d\uff0c\u4e5f\u662f\u4ed6\u6551\u56de\u6765\u7684\uff1f\u53e3\u4e2d\u5374\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u5144\u5f1f\u8bf4\u7684\u662f\u3002\u201d \u3000\u3000\u65b9\u73de\u9053\uff1a\u201c\u4ed6\u65e2\u662f\u6b66\u6797\u9ad8\u4eba\uff0c\u4e0d\u77e5\u4e3a\u4ec0\u4e48\u8981\u4e3a\u4eba\u4ece\u4ec6\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u8fd9\u4e2a\u5417\uff0c\u6211\u4e5f\u4e0d\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u65b9\u73de\u9053\uff1a\u201c\u6211\u60f3\u628a\u6b64\u4e8b\u544a\u8bc9\u7239\u7239\uff0c\u5927\u54e5\u610f\u4e0b\u5982\u4f55\uff1f\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u5927\u4f2f\u4e45\u5e74\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\uff0c\u5c82\u6709\u4e0d\u77e5\u4e4b\u7406\uff1f\u6211\u60f3\u4ed6\u65e9\u5df2\u77a7\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u65b9\u73de\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u5927\u54e5\u8bf4\u7684\u662f\u2026\u2026\u201d\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u54b3\uff01\u6211\u77a7\u7239\u3001\u5a18\u4f3c\u90fd\u6709\u5fc3\u4e8b\u7792\u7740\u6211\u4eec\u3002\u201d \u3000\u3000\u65b9\u745c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u90a3\u4e1c\u8033\u8001\u524d\u8f88\u4e0d\u8ba9\u6211\u6cc4\u9732\u4f20\u6211\u6b66\u529f\u4e4b\u4e8b\uff0c\u6b64\u4e8b\u4e07\u4e07\u4e0d\u80fd\u8bf4\u51fa\u53bb\uff0c\u73de\u5f1f\u5e73\u65e5\u7c97\u5fc3\u5927\u610f\uff0c\u8fd9\u6b21\u5403\u4e86\u4e00\u756a\u82e6\u5934\u4e4b\u540e\uff0c\u4f3c\u662f\u53d8\u7684\u7ec6\u5fc3\u8d77\u6765\uff0c\u65e2\u5bf9\u4e1c\u8033\u8001\u4eba\u52a8\u7591\uff0c\u53c8\u77a7\u51fa\u5927\u5a18\u82e5\u6709\u5fc3\u4e8b\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u5374\u5e94\u9053\uff1a\u201c\u662f\u554a\uff01\u6211\u4e5f\u77a7\u51fa\u5927\u5a18\u795e\u60c5\u6709\u4e9b\u4e0d\u5bf9\uff0c\u4f46\u662f\u5927\u4eba\u7684\u4e8b\uff0c\u54b1\u4eec\u5c0f\u5b69\u5b50\u53ea\u6015\u4e0d\u80fd\u591a\u95ee\u3002\u201d \u3000\u3000\u65b9\u73de\u9053\uff1a\u201c\u6bcd\u4eb2\u4e00\u5411\u4ec1\u6148\u548c\u853c\uff0c\u5728\u627e\u8bb0\u5fc6\u4e4b\u4e2d\uff0c\u4ece\u6765\u6ca1\u6709\u89c1\u8fc7\u5979\u6709\u6101\u82e6\u4e4b\u5bb9\uff0c\u4f46\u8fd9\u51e0\u5929\u6211\u5374\u611f\u89c9\u5230\u8981\u4f3c\u6709\u7740\u91cd\u91cd\u7684\u5fc3\u4e8b\uff0c\u4eca\u65e5\u66f4\u662f\u660e\u663e\uff0c\u5b9a\u7136\u6709\u7740\u5f88\u91cd\u5927\u7684\u4e8b\u4e86\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u9053\uff1a\u201c\u542c\u8bf4\u90d1\u5927\u4f2f\u53d7\u4f24\u5f88\u91cd\uff0c\u6211\u5fc3\u4e2d\u6781\u662f\u4e0d\u5b89\uff0c\u5982\u975e\u6211\u95ef\u51fa\u8fd9\u573a\u5927\u7978\uff0c\u54b1\u4eec\u4e5f\u4e0d\u4f1a\u548c\u5251\u95e8\u4e94\u9b3c\u7ed3\u4ec7\u4e86\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u95f9\u51fa\u8fd9\u756a\u7eb7\u4e89\uff0c\u8868\u9762\u4e0a\u867d\u7136\u662f\u4f60\u60f9\u51fa\u7684\u7978\uff0c\u5b9e\u5219\u6b63\u90aa\u4e0d\u4e24\u7acb\uff0c\u4f2f\u7236\u8fd8\u662f\u4e3a\u4e86\u5e2e\u52a9\u7a0b\u5b50\u671b\u7a0b\u5e84\u4e3b\u3002\u201d \u3000\u3000\u65b9\u73de\u9053\uff1a\u201c\u5509\uff01\u4e0d\u7ba1\u5982\u4f55\uff0c\u603b\u662f\u5c0f\u5f1f\u71c3\u8d77\u4e86\u8fd9\u573a\u7eb7\u4e89\u4e4b\u706b\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u5144\u5f1f\u4e0d\u7528\u6101\u82e6\uff0c\u5fe7\u5fc3\u70e6\u607c\uff0c\u4e8e\u4e8b\u65e0\u8865\uff0c\u54b1\u4eec\u501f\u8fd9\u673a\u4f1a\uff0c\u5750\u606f\u4e00\u9635\u5982\u4f55\uff1f\u201d \u3000\u3000\u65b9\u73de\u5e94\u4e86\u4e00\u58f0\uff0c\u5144\u5f1f\u4e24\u4eba\u5404\u81ea\u76d8\u5750\u8fd0\u52a8\u3002 \u3000\u3000\u65f6\u5149\u5306\u5306\uff0c\u4e09\u65e5\u95f4\u5f39\u6307\u800c\u8fc7\u3002\u5728\u8fd9\u4e09\u65e5\u4e4b\u4e2d\uff0c\u65b9\u745c\u672a\u6d6a\u8d39\u8fc7\u4e00\u523b\u65f6\u5149\uff0c\u4e0d\u662f\u6253\u5750\u8c03\u606f\uff0c\u5c31\u662f\u601d\u7d22\u90a3\u4e09\u62db\u5251\u6cd5\u3002 \u3000\u3000\u4e1c\u8033\u8001\u4eba\u4e5f\u4e00\u76f4\u8010\u5fc3\u7684\u89e3\u8bf4\u90a3\u4e09\u62db\u5251\u6cd5\u7a8d\u8981\u3002 \u3000\u3000\u7b2c\u56db\u5929\u65e5\u51fa\u65f6\u5206\uff0c\u4e2d\u5dde\u4e09\u4fa0\u79bb\u5f00\u4e86\u7a0b\u5bb6\u5821\u3002 \u3000\u3000\u90d1\u5927\u521a\u7ecf\u8fc7\u6570\u65e5\u8c03\u606f\uff0c\u4f53\u80fd\u5927\u590d\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u4e3a\u51e0\u4eba\u5907\u4e86\u56db\u5339\u957f\u7a0b\u5065\u9a6c\uff0c\u4e24\u8f86\u575a\u7262\u7bf7\u8f66\u3002 \u3000\u3000\u65b9\u73de\u4f4e\u58f0\u5bf9\u65b9\u745c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\u554a\uff01\u4e3a\u4ec0\u4e48\u8981\u4e24\u8f86\u7bf7\u8f66\u5462\uff1f\u201d \u3000\u3000\u65b9\u745c\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u4e5f\u89c9\u7740\u6709\u4e9b\u5947\u602a\u2026\u2026\u201d\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4e5f\u8bb8\u90d1\u5927\u4f2f\u8eab\u4f53\u672a\u590d\uff0c\u4e0d\u4fbf\u9a91\u9a6c\uff0c\u4e5f\u5750\u7bf7\u8f66\u8d76\u8def\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u6307\u7740\u7b2c\u4e00\u8f86\u7bf7\u8f66\uff0c\u4f4e\u58f0\u5bf9\u65b9\u592b\u4eba\uff0c\u9053\uff1a\u201c\u4f60\u5e26\u7740\u745c\u513f\u3001\u73de\u513f\u5750\u7b2c\u4e00\u8f86\u8f66\uff0c\u8f66\u540e\u4e24\u53ea\u6728\u7bb1\u5185\u88c5\u6709\u73e0\u5b9d\u3001\u9ec4\u91d1\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u6b32\u8a00\u53c8\u6b62\uff0c\u4f38\u624b\u7275\u8d77\u65b9\u745c\uff0c\u65b9\u73de\uff0c\u884c\u5411\u7b2c\u4e00\u8f86\u7bf7\u8f66\u4e4b\u4e0a\u3002 \u3000\u3000\u90a3\u4e1c\u8033\u8001\u4eba\u65e9\u5df2\u5750\u5728\u8f66\u524d\u7b49\u5019\u3002 \u3000\u3000\u65b9\u5929\u6210\u5927\u6b65\u884c\u5230\u90d1\u5927\u521a\u8eab\u524d\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5927\u54e5\u4f24\u4f53\u672a\u6108\uff0c\u5750\u8f66\u8d74\u8def\u5982\u4f55\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u54c8\u54c8\u4e00\u7b11\uff0c\u7eb5\u8eab\u8dc3\u4e0a\u9a6c\u80cc\uff0c\u9053\uff1a\u201c\u6211\u5df2\u5927\u90e8\u590d\u539f\u4e86\uff01\u201d \u3000\u3000\u65b9\u5929\u6210\u3001\u77f3\u4fca\uff0c\u8a00\u51e4\u537f\u773c\u770b\u90d1\u5927\u521a\u8dc3\u4e0a\u9a6c\u80cc\uff0c\u53ea\u597d\u968f\u540e\u4e0a\u9a6c\u3002 \u3000\u3000\u90d1\u5927\u521a\u4e00\u52d2\u9a6c\u7f30\uff0c\u56de\u5934\u8bf4\u9053\uff1a\u201c\u7a0b\u5144\u8bf7\u56de\u5427\uff01\u6211\u7b49\u5c31\u6b64\u522b\u8fc7\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u4e00\u62b1\u62f3\u9053\uff1a\u201c\u8bf8\u4f4d\u4e00\u8def\u987a\u98ce\uff0c\u518d\u8fc7\u5609\u5b9a\u65f6\uff0c\u5e0c\u671b\u80fd\u7ed9\u7a0b\u67d0\u4e00\u4e2a\u4fe1\uff0c\u5927\u6069\u4e0d\u8a00\u8c22\uff0c\u7a0b\u67d0\u4eba\u6b64\u523b\u7eb5\u6709\u5343\u8a00\u4e07\u8bed\uff0c\u4e5f\u89c9\u7740\u65e0\u4ece\u8bf4\u8d77\u4e86\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u8f7b\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u7a0b\u5144\uff0c\u5144\u5f1f\u60f3\u5949\u529d\u7a0b\u5144\u4e00\u2026\u2026\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u7a0b\u67d0\u4eba\u6d17\u8033\u606d\u542c\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4e94\u9b3c\u6f0f\u7f51\u4e8c\u9b3c\uff0c\u52a0\u4e0a\u90a3\u91d1\u67aa\u5b97\u58eb\u4e49\uff0c\u5b9e\u529b\u4ecd\u6781\u5f3a\u5927\uff0c\u5bf9\u6b64\u756a\u4e4b\u8fb1\uff0c\u5b9a\u7136\u8bb0\u4e8e\u5fc3\u4e2d\uff0c\u4fdf\u673a\u62a5\u590d\uff0c\u4f46\u4ed6\u4eec\u5927\u521b\u4e4b\u540e\uff0c\u5143\u6c14\u672a\u590d\uff0c\u51b3\u4e0d\u6562\u4e8e\u8fd1\u65e5\u4e4b\u5185\uff0c\u518d\u6765\u751f\u4e8b\uff0c\u7a0b\u5144\u6709\u8db3\u591f\u7684\u65f6\u95f4\uff0c\u7ed3\u675f\u6b64\u95f4\u4e8b\u52a1\uff0c\u907f\u7978\u8fdc\u8d70\u3002\u201d \u3000\u3000\u9648\u5b50\u671b\u884c\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u65b9\u4e8c\u4fa0\u7684\u597d\u610f\uff0c\u5144\u5f1f\u5448\u611f\u6f88\u4e0d\u5c3d\uff0c\u4f46\u7a0b\u67d0\u751f\u4e8e\u65af\uff0c\u957f\u4e8e\u65af\uff0c\u57cb\u9aa8\u6851\u6893\uff0c\u6b7b\u800c\u4f55\u61be\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u7a0b\u67d0\u5f53\u5148\u9063\u62d9\u8346\u5c0f\u5973\uff0c\u907f\u7978\u8fdc\u8d70\uff0c\u8001\u673d\u4e5f\u5c06\u4ece\u4eca\u65e5\u8d77\uff0c\u52a0\u5f3a\u6212\u5907\uff0c\u4ee5\u5f85\u5f3a\u654c\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u7a0b\u5144\u8c6a\u6c14\uff0c\u5144\u5f1f\u5341\u5206\u656c\u4f69\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u4e94\u9b3c\u8033\u76ee\u904d\u5929\u4e0b\uff0c\u5144\u5f1f\u5c31\u7b97\u60f3\u9003\uff0c\u53c8\u80fd\u9003\u5411\u4f55\u5904\u5462\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u8c6a\u653e\u7684\u8bf4\u9053\uff1a\u201c\u4e94\u9b3c\u4f5c\u6076\u591a\u7aef\uff0c\u6b7b\u6709\u4f59\u8f9c\uff0c\u4f46\u671b\u7a0b\u5144\u80fd\u591f\u526a\u9664\u4f59\u5b7d\u3002\u201d\u8a00\u7f62\uff0c\u4e00\u6296\u7f30\u7ef3\uff0c\u5065\u9a6c\u7a81\u7136\u5411\u524d\u5954\u53bb\u3002 \u3000\u3000\u65b9\u5929\u6210\u3001\u77f3\u4fca\u3001\u8a00\u51e4\u537f\u4e09\u9a91\u9a6c\u9c7c\u8d2f\u968f\u884c\u3002 \u3000\u3000\u4e1c\u8033\u8001\u4eba\u957f\u97ad\u4e00\u6325\uff0c\u8f6e\u58f0\u8f98\u8f98\uff0c\u968f\u5728\u56db\u5339\u5065\u9a6c\u4e4b\u540e\u3002 \u3000\u3000\u53e6\u4e00\u8f86\u7a7a\u7740\u7684\u9a6c\u8f66\uff0c\u7adf\u7136\u4e5f\u626c\u97ad\u968f\u884c\u3002 \u3000\u3000\u65b9\u592b\u4eba\u4f3c\u662f\u5bf9\u90a3\u8f86\u7a7a\u7740\u968f\u884c\u628a\u8f66\u7279\u522b\u6ce8\u610f\uff0c\u4e0d\u65f6\u62e8\u5f00\u540e\u9762\u7684\u5782\u5e18\uff0c\u671b\u7740\u90a3\u9a6c\u8f66\u51fa\u795e\uff0c\u795e\u8272\u95f4\u65e0\u9650\u51c4\u60f6\u3001\u9eef\u7136\u3002 \u3000\u3000\u65b9\u73de\u5927\u4e3a\u5947\u602a\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5988\u5988\u554a\uff01\u90a3\u8f86\u7a7a\u8f66\uff0c\u6709\u4ec0\u4e48\u597d\u770b\u7684\uff1f\u201d \u3000\u3000\u65b9\u592b\u4eba\u51c4\u51c9\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u4eca\u5e74\u5341\u4e94\u5c81\u4e86\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u65b9\u73de\u9053\uff1a\u201c\u662f\u554a\uff01\u201d \u3000\u3000\u65b9\u592b\u4eba\u9053\uff1a\u201c\u5341\u4e94\u5c81\u5f88\u5927\u5566\uff0c\u4ee5\u540e\uff0c\u4f60\u8981\u5b66\u4f1a\u81ea\u5df1\u7167\u987e\u81ea\u5df1\uff0c\u7239\u5988\u90fd\u4e0d\u80fd\u8ddf\u4f60\u4e00\u8f88\u5b50\u4e86\uff01\u201d \u3000\u3000\u65b9\u73de\u6709\u4e9b\u660e\u767d\uff0c\u4f46\u5374\u4e0d\u5c3d\u4e86\u7136\uff0c\u8fd8\u60f3\u518d\u95ee\uff0c\u5374\u4e3a\u65b9\u745c\u62c9\u4f4f\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u5927\u5a18\u5fc3\u60c5\u4e0d\u597d\uff0c\u54b1\u4f4d\u522b\u518d\u70e6\u5979\u4e86\u3002\u201d \u3000\u3000\u4f46\u95fb\u8f66\u5916\u4f20\u6765\u4f4e\u6c89\u7684\u6b4c\u58f0\uff0c\u9053\uff1a\u201c\u4e3a\u5584\u5168\u4ed7\u4e00\u7247\u5fc3\uff0c\u5409\u4eba\u81ea\u6709\u5929\u76f8\u4f51\u2026\u2026\u201d \u3000\u3000\u65b9\u592b\u4eba\u4f38\u7267\u6311\u5f00\u8f66\u5e18\uff0c\u53ea\u89c1\u90a3\u5531\u6b4c\u7684\u6b63\u662f\u4e1c\u8033\u8001\u4eba\u3002 \u3000\u3000\u4e1c\u8033\u8001\u4eba\u662f\u5df2\u77e5\u6653\u8eab\u540e\u7684\u8f66\u5e18\u88ab\u4eba\u6380\u5f00\uff0c\u7acb\u65f6\u4f4f\u53e3\u3002 \u3000\u3000\u65b9\u592b\u4eba\u8f7b\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e1c\u8001\u4e08\uff01\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u56de\u8fc7\u5934\u6765\uff0c\u62d8\u8c28\u7684\u8bf4\u9053\uff1a\u201c\u592b\u4eba\u6709\u4f55\u5429\u5490\uff1f\u201d \u3000\u3000\u65b9\u592b\u4eba\u9053\uff1a\u201c\u521a\u624d\u662f\u4f60\u5728\u5531\u6b4c\u5417\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8001\u6c49\u60f3\u8d77\u4e86\u4e24\u53e5\u683c\u8a00\uff0c\u968f\u53e3\u54fc\u4e86\u51fa\u6765\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e1c\u8001\u4e08\uff0c\u4f60\u6709\u513f\u5973\u5417\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u8001\u6c49\u5b51\u7136\u4e00\u8eab\uff0c\u5929\u6daf\u98d8\u96f6\uff1f\u201d \u3000\u3000\u65b9\u592b\u4eba\u9053\uff1a\u201c\u4ee5\u540e\u4f60\u5c31\u8ddf\u7740\u6211\u4eec\u6bcd\u5b50\uff0c\u597d\u5417\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u8001\u6c49\u5e74\u7eaa\u8001\u8fc8\u8033\u76ee\u5df2\u6e10\u5931\u7075\uff0c\u53ea\u6015\u96be\u4ee5\u80dc\u4efb\u7e41\u91cd\u5de5\u4f5c\u4e86\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u4e0d\u518d\u591a\u8a00\uff0c\u7f13\u7f13\u653e\u4e0b\u5782\u5e18\u3002 \u3000\u3000\u65b9\u745c\u79fb\u52a8\u4e86\u4e00\u4e0b\u8eab\u8eaf\uff0c\u62e8\u5f00\u8f66\u5e18\u671b\u53bb\uff0c\u53ea\u89c1\u4e2d\u5dde\u4e09\u4fa0\u548c\u8a00\u51e4\u537f\u5e76\u9a91\u800c\u9a70\uff0c\u4f3c\u662f\u5728\u5546\u91cf\u7740\u4ec0\u4e48\u4e8b\u60c5\u3002\u5fc3\u4e2d\u6697\u9053\uff1a\u5927\u7ea6\u4ed6\u4eec\u5728\u8a00\u5546\u5bf9\u4ed8\u90a3\u9b54\u5200\u7530\u8fdc\u4e4b\u7b56\u4e86\u3002\u5fc3\u5ff5\u53ca\u6b64\uff0c\u60f3\u5230\u81ea\u8eab\u7684\u8d23\u4efb\u91cd\u5927\uff0c\u7acb\u65f6\u95ed\u4e0a\u53cc\u76ee\uff0c\u63a8\u6572\u90a3\u4e09\u62db\u5251\u672f\u3002 \u3000\u3000\u4e0d\u77e5\u8fc7\u53bb\u4e86\u591a\u5c11\u65f6\u95f4\uff0c\u7bf7\u8f66\u7a81\u7136\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u53ea\u89c1\u5782\u5e18\u9ad8\u5377\uff0c\u4e2d\u5dde\u4e09\u4fa0\u548c\u8a00\u51e4\u537f\u5e76\u80a9\u505c\u5728\u8f66\u524d\u3002 \u3000\u3000\u65b9\u745c\u76ee\u5149\u8f6c\u52a8\uff0c\u53ea\u89c1\u505c\u8eab\u4e4b\u5730\uff0c\u662f\u4e00\u7247\u56db\u65e0\u4eba\u8ff9\u7684\u90ca\u91ce\uff0c\u773c\u524d\u662f\u4e00\u5904\u8d2b\u9053\u3002 \u3000\u3000\u53ea\u542c\u65b9\u5929\u6210\u8f7b\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8d24\u59bb\u2026\u2026\u201d\u98ce\u89c9\u4e00\u9635\u51c4\u695a\u6d8c\u4e0a\u5fc3\u5934\uff0c\u7adf\u81ea\u8bf4\u4e0d\u51fa\u4e0b\u9762\u4e4b\u8a00\u3002 \u3000\u3000\u65b9\u592b\u4eba\u4e3e\u624b\u7406\u4e00\u4e0b\u9b13\u524d\u6563\u53d1\uff0c\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u592b\u541b\u53ea\u7ba1\u8bf4\u5427\uff01\u8981\u6765\u7684\u603b\u662f\u8981\u6765\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u548c\u5927\u54e5\uff0c\u4e09\u5f1f\u8981\u8d74\u4e00\u4e2a\u7ea6\u4f1a\uff01\u2026\u2026\u201d \u3000\u3000\u65b9\u592b\u4eba\u9053\uff1a\u201c\u90a3\u7ea6\u4f1a\u5f88\u5371\u9669\uff0c\u4e0d\u80fd\u5e26\u6211\u540c\u53bb\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u56e0\u6b64\uff0c\u6211\u6258\u4ed8\u4e86\u8fd9\u4f4d\u8a00\u5144\uff0c\u7531\u4ed6\u5e26\u4f60\u4eec\u5230\u5b89\u9759\u7684\u73af\u5883\u4e4b\u4e2d\uff0c\u6682\u4f4f\u51e0\u65e5\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u6d41\u4e0b\u6cea\u6765\uff0c\u4f46\u5979\u4ecd\u7136\u67d4\u987a\u5730\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u8981\u56de\u6765\u3002\u201d\u8fd9\u6de1\u6de1\u4e00\u53e5\u8bdd\uff0c\u80dc\u8fc7\u5343\u8a00\u4e07\u8bed\uff0c\u5305\u542b\u4e86\u65e0\u9650\u7684\u60c5\u610f\u3002 \u3000\u3000\u65b9\u5929\u6210\u5f3a\u5fcd\u7740\u5fc3\u4e2d\u9178\u695a\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c31\u7b97\u4e3a\u592b\u4e0d\u56de\u5bb6\u53bb\uff0c\u6211\u4e5f\u6839\u4fe1\u8d24\u59bb\uff0c\u80fd\u591f\u59a5\u4e3a\u7167\u987e\u4e24\u4e2a\u5b69\u5b50\u3002\u201d \u3000\u3000\u56de\u7b54\u7684\u4e5f\u5f88\u8f7b\u6de1\uff0c\u4f46\u8a00\u8bed\u4e4b\u4e2d\uff0c\u5374\u9690\u9690\u6709\u6258\u5b64\u4e4b\u610f\u3002 \u3000\u3000\u65b9\u592b\u4eba\u9053\uff1a\u201c\u592b\u541b\u4e4b\u8a00\uff0c\u8d31\u59be\u51e0\u65f6\u4e0d\u542c\u4e86\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u4f38\u51fa\u624b\u53bb\uff0c\u63e1\u4f4f\u4e86\u65b9\u592b\u4eba\u7684\u67d4\u8351\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u8981\u5584\u81ea\u73cd\u91cd\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u51c4\u82e6\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u592b\u541b\u4fdd\u91cd\uff0c\u6211\u5c06\u5f88\u8010\u5fc3\u5730\u7b49\u4f60\u56de\u6765\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u56de\u8fc7\u5934\u53bb\uff0c\u671b\u7740\u8a00\u51e4\u537f\u4e00\u62b1\u62f3\uff0c\u9053\uff1a\u201c\u4e07\u4e8b\u62dc\u6258\u4e86\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u5c0f\u5f1f\u5c3d\u6211\u5fc3\u529b\u5c31\u662f\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u4e00\u52d2\u9a6c\u7f30\uff0c\u5f53\u5148\u5411\u524d\u5954\u53bb\u3002 \u3000\u3000\u77f3\u4fca\u4e00\u62b1\u62f3\uff0c\u9053\uff1a\u201c\u5ac2\u5ac2\u73cd\u91cd\uff0c\u4f46\u613f\u5c0f\u5f1f\u8fd8\u80fd\u5403\u5230\u5ac2\u5ac2\u4eb2\u624b\u70e7\u5236\u7684\u51e0\u5473\u4f73\u80b4\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u9053\uff1a\u201c\u6211\u4f1a\u70e7\u7ed9\u4f60\u5403\u7684\uff0c\u5144\u5f1f\uff0c\u548c\u4f60\u4e8c\u54e5\u4e00\u8d77\u56de\u6765\u3002\u201d \u3000\u3000\u77f3\u4fca\u7eb5\u58f0\u957f\u5578\u4e00\u58f0\uff0c\u5e26\u8f6c\u9a6c\u5934\u800c\u53bb\u3002 \u3000\u3000\u90d1\u5927\u521a\u4e00\u76f4\u51b7\u773c\u65c1\u89c2\uff0c\u89c1\u77f3\u4fca\u7eb5\u9a6c\u800c\u53bb\uff0c\u624d\u6325\u6325\u624b\uff0c\u9053\uff1a\u201c\u5f1f\u59b9\u8d24\u6dd1\u7f8e\u4e3d\uff0c\u67d4\u4e2d\u8574\u521a\uff0c\u5fc5\u80fd\u5584\u81ea\u7167\u987e\u4e24\u4e2a\u8d24\u4f84\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u5c31\u8f66\u4e0a\u68c0\u4efb\u4e00\u798f\uff0c\u9053\uff1a\u201c\u5927\u4f2f\u653e\u5fc3\uff0c\u5929\u53ef\u8001\uff0c\u5730\u53ef\u8352\uff0c\u6211\u5bf9\u65b9\u90ce\u60c5\u4e49\u4e0d\u53d8\u3001\u4e07\u4e00\u6709\u4e86\u4ec0\u4e48\u4e0d\u5e78\uff0c\u6211\u4e5f\u4f1a\u5584\u4e3a\u7167\u770b\u73de\u513f\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4eec\u4e09\u5144\u5f1f\uff0c\u540c\u8d74\u9b3c\u95e8\u5173\uff0c\u9ec4\u6cc9\u8def\u4e0a\uff0c\u4e5f\u4e0d\u4f1a\u5bc2\u5bde\u4e86\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u9053\uff1a\u201c\u52b3\u5927\u4f2f\u8f6c\u544a\u65b9\u90ce\uff0c\u8981\u4ed6\u4e00\u5207\u653e\u5fc3\uff0c\u73de\u513f\u52a0\u51a0\u4e4b\u5e74\uff0c\u6211\u81ea\u4f1a\u5230\u9634\u66f9\u5730\u5e9c\u627e\u4ed6\uff0c\u8981\u4ed6\u597d\u597d\u5730\u7b49\u6211\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u65b9\u5144\u5f1f\u6709\u6b64\u8d24\u5987\uff0c\u6211\u4eec\u4f5c\u5144\u5f1f\u4e0e\u6709\u8363\u852b\uff0c\u5f1f\u5987\u8bf7\u53d7\u5c0f\u5144\u4e00\u793c\u3002\u201d\u62b1\u62f3\u4e00\u793c\uff0c\u62e8\u8f6c\u9a6c\u5934\uff0c\u6025\u5954\u800c\u53bb\u3002 \u3000\u3000\u65b9\u592b\u4eba\u6025\u6025\u8fd8\u793c\uff0c\u9053\uff1a\u201c\u600e\u6562\u5f53\u5927\u4f2f\u4e4b\u793c\u3002\u201d \u3000\u3000\u8bdd\u843d\u53e3\uff0c\u90d1\u5927\u521a\u5df2\u7136\u5954\u51fa\u53bb\u6570\u4e08\u4e4b\u5916\u3002 \u3000\u3000\u65b9\u592b\u4eba\u76ee\u7779\u4e2d\u5dde\u4e09\u4fa0\u53bb\u8fdc\uff0c\u624d\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u62c2\u53bb\u8138\u4e0a\u6cea\u75d5\uff0c\u56de\u5934\u671b\u7740\u51e4\u537f\uff0c\u8bf4\u9053\uff1a \u3000\u3000\u201c\u6709\u52b3\u8a00\u5927\u4fa0\u4e86\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u5fc3\u4e2d\u6697\u6697\u8d5e\u9053\uff1a\u201c\u6b64\u5973\u5929\u751f\u4e3d\u8d28\uff0c\u867d\u7136\u4e09\u5341\u8fc7\u5934\uff0c\u4f46\u770b\u4e0a\u53bb\uff0c\u4f46\u770b\u4e0a\u53bb\uff0c\u5374\u82e5\u4e8c\u5341\u8bb8\u4eba\uff0c\u5979\u67d4\u5f31\u7684\u4ee4\u4eba\u601c\u60dc\uff0c\u4f46\u4e5f\u575a\u5f3a\u7684\u4ee4\u4eba\u656c\u4f69\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u5374\u8bf4\u9053\uff1a\u201c\u592b\u4eba\u4f46\u8bf7\u653e\u5fc3\uff0c\u4e2d\u5dde\u4e09\u4fa0\u6b64\u756a\u8d74\u7ea6\uff0c\u867d\u7136\u6709\u4e9b\u60ca\u9669\u9645\u9047\uff0c\u4f46\u5409\u4eba\u5929\u76f8\uff0c\u5728\u4e0b\u76f8\u4fe1\u4e09\u4f4d\u5fc5\u7136\u4f1a\u65e0\u6059\u5f52\u6765\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u6309\u637a\u4e0b\u5fc3\u5934\u65e0\u5c3d\u7684\u60b2\u4f24\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8a00\u5927\u4fa0\u51c6\u5907\u5e26\u6211\u4eec\u6bcd\u5b50\u5230\u4f55\u5904\u5c45\u4f4f\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u65b9\u4e8c\u4fa0\u8c06\u8c06\u544a\u5631\u5728\u4e0b\uff0c\u672a\u5230\u4e4b\u524d\uff0c\u4e0d\u4fbf\u8a00\u660e\u53bb\u5904\uff0c\u8fd8\u8bf7\u592b\u4eba\u539f\u8c05\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u9053\uff1a\u201c\u592b\u541b\u65e2\u6709\u5631\uff0c\u8d31\u59be\u81ea\u662f\u4e0d\u4fbf\u591a\u95ee\u4e86\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u592b\u4eba\u8bf7\u56de\u8f66\u4e2d\uff0c\u54b1\u4f4d\u7acb\u65f6\u52a8\u8eab\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u5e94\u4e86\u4e00\u58f0\uff0c\u9000\u56de\u8f66\u4e2d\uff0c\u653e\u4e0b\u5782\u5e18\u3002 \u3000\u3000\u8a00\u51e4\u537f\u76ee\u5149\u8f6c\u6ce8\u5230\u4e1c\u8033\u8001\u4eba\u7684\u8eab\u4e0a\uff0c\u9053\uff1a\u201c\u8001\u4e08\u8bf7\u968f\u5728\u4e0b\u9a6c\u540e\u8d76\u8def\u3002\u201d\u4e1c\u8033\u8001\u4eba\u5e94\u4e86\u4e00\u58f0\uff0c\u529d\u957f\u97ad\uff0c\u8f6e\u58f0\u8f98\u8f98\uff0c\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u65b9\u745c\u8eb2\u5728\u8f66\u4e2d\uff0c\u773c\u770b\u90a3\u4e2d\u5dde\u4e09\u4fa0\u62cd\u9a6c\u800c\u53bb\uff0c\u5fc3\u4e2d\u5927\u662f\u56e0\u60d1\uff0c\u6697\u6697\u5fd6\u9053\uff1a\u201c\u4ed6\u4eec\u8fd9\u4e00\u8d70\uff0c\u6211\u53c8\u5f80\u4f55\u5904\u627e\u5bfb\u4ed6\u4eec\u5462\uff1f\u201d \u3000\u3000\u4f46\u89c1\u4e1c\u8033\u8001\u4eba\u7aef\u5750\u8f66\u524d\uff0c\u6709\u5982\u4e00\u5c0a\u6ce5\u5851\u6728\u96d5\u7684\u795e\u50cf\u4e00\u822c\uff0c\u6beb\u65e0\u4e00\u70b9\u8868\u60c5\u3002\u8fd9\u5c31\u66f4\u4f7f\u65b9\u745c\u516d\u795e\u65e0\u4e3b\uff0c\u4e0d\u77e5\u5982\u4f55\u624d\u597d\u3002 \u3000\u3000\u4ed6\u60f3\u8df3\u4e0b\u8f66\uff0c\u76f4\u8ffd\u4e2d\u5dde\u4e09\u4fa0\u800c\u53bb\uff0c\u4f46\u4ed6\u77e5\u9053\uff0c\u5982\u6b64\u4e00\u6765\uff0c\u5fc5\u5c06\u4e3a\u5927\u4f2f\u6321\u56de\uff0c\u800c\u4e14\u4e5f\u5c06\u5f15\u8d77\u90a3\u65b9\u592b\u4eba\u7684\u6ce8\u610f\uff0c\u6b64\u540e\u518d\u60f3\u9003\u8d70\uff0c\u53ea\u6015\u518d\u96be\u6709\u673a\u4f1a\u4e86\u3002 \u3000\u3000\u56e0\u6b64\uff0c\u4ed6\u4e0d\u6562\u5984\u52a8\uff0c\u5e0c\u671b\u90a3\u4e1c\u8033\u8001\u4eba\u4f1a\u53ca\u65f6\u544a\u8bc9\u4ed6\u5982\u4f55\u51fa\u8d70\u3002 \u3000\u3000\u4f46\u90a3\u4e1c\u8033\u8001\u4eba\u5374\u6df1\u4f3c\u4e0d\u89c9\u4e00\u822c\uff0c\u522b\u8bf4\u5e2e\u60f3\u529e\u6cd5\uff0c\u5c31\u662f\u8fde\u56de\u5934\u77a7\u4ed6\u4e00\u773c\uff0c\u4e5f\u662f\u6ca1\u6709\u3002 \u3000\u3000\u53ea\u542c\u8f6e\u58f0\u8fc5\u6025\uff0c\u60f3\u662f\u7bf7\u8f66\u6b63\u4ee5\u6781\u5feb\u7684\u901f\u5ea6\u5411\u524d\u5954\u9a70\u3002 \u3000\u3000\u65b9\u73de\u4f3c\u662f\u4e5f\u4ece\u7236\u6bcd\u4e00\u756a\u5bf9\u8bdd\u4e2d\u542c\u51fa\u4e86\u8fd9\u4e00\u6b21\u5206\u79bb\uff0c\u9690\u9690\u6709\u51b3\u522b\u4e4b\u610f\uff0c\u5185\u5fc3\u4e2d\u4e5f\u611f\u67d3\u4e86\u6df1\u6c89\u7684\u60b2\u4f24\uff0c\u5446\u5446\u5730\u671b\u7740\u6bcd\u4eb2\u51fa\u795e\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u6709\u7740\u5343\u8a00\u4e07\u8bed\uff0c\u4f46\u5374\u53c8\u4e0d\u77e5\u4ece\u4f55\u8bf4\u8d77\u3002 \u3000\u3000\u65b9\u592b\u4eba\u60ca\u89c9\u5230\uff0c\u81ea\u5df1\u6df1\u91cd\u7684\u4f24\u611f\uff0c\u6b63\u81ea\u611f\u67d3\u5230\u4e24\u4e2a\u5b69\u5b50\u8eab\u4e0a\uff0c\u5f53\u4e0b\u5f3a\u81ea\u5fcd\u4f4f\u5fc3\u4e2d\u7684\u51c4\u82e6\uff0c\u56de\u76ee\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u73de\u513f\uff0c\u4f60\u5728\u60f3\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u73de\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u5728\u60f3\u7239\u7239\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u9053\uff1a\u201c\u4f60\u7239\u7239\u4e00\u751f\u4e2d\u6ca1\u6709\u4f5c\u8fc7\u574f\u4e8b\uff0c\u6211\u60f3\u4ed6\u4e00\u5b9a\u80fd\u5e73\u5b89\u5730\u56de\u6765\u3002\u201d \u3000\u3000\u65b9\u73de\u9053\uff1a\u201c\u7239\u7239\u8fd9\u6b21\u8d74\u4eba\u4e4b\u7ea6\uff0c\u53ef\u662f\u5341\u5206\u51f6\u9669\u5417\uff1f\u201d \u3000\u3000\u65b9\u592b\u4eba\u9053\uff1a\u201c\u662f\u7684\uff0c\u90a3\u7ea6\u4ed6\u4eec\u7684\u4eba\uff0c\u6b66\u529f\u5341\u5206\u9ad8\u5f3a\uff0c\u4f46\u6211\u76f8\u4fe1\u4f60\u7239\u80fd\u591f\u5e94\u4ed8\u3002\u201d \u3000\u3000\u65b9\u745c\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5927\u5a18\uff0c\u745c\u513f\u5fc3\u4e2d\u60f3\u5230\u4e00\u4ef6\u4e8b\uff0c\u60f3\u6c42\u5927\u5a18\u7b54\u5e94\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u7728\u52a8\u4e86\u4e00\u4e0b\u773c\u775b\uff0c\u9053\uff1a\u201c\u745c\u513f\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u6211\u4e00\u76f4\u628a\u4f60\u5f53\u81ea\u5df1\u7684\u5b69\u5b50\u770b\u5f85\uff0c\u5728\u6211\u5fc3\u76ee\u4e2d\u4f60\u548c\u73de\u513f\u4e00\u822c\u2026\u2026\u201d \u3000\u3000\u65b9\u745c\u63a5\u9053\uff1a\u201c\u8fd9\u4e2a\u745c\u513f\u77e5\u9053\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u9053\uff1a\u201c\u90a3\u5f88\u597d\uff0c\u4f60\u6709\u4ec0\u4e48\u4e8b\uff0c\u5c3d\u7ba1\u8bf4\u5427\uff01\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u6211\u60f3\u8ddf\u5927\u4f2f\u4e00\u8d77\u53bb\u2026\u2026\u201d \u3000\u3000\u65b9\u592b\u4eba\u5947\u9053\uff1a\u201c\u4f60\u53bb\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u4e5f\u8bb8\u745c\u513f\u53ef\u52a9\u5927\u4f2f\u7236\u4e00\u81c2\u4e4b\u529b\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u6447\u6447\u5934\uff0c\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u5728\u8bf4\u68a6\u8bdd\u4e86\uff01\u4f60\u5927\u4f2f\u6b66\u529f\uff0c\u5f3a\u4f60\u4f55\u6b62\u5341\u500d\uff0c\u4ed6\u5982\u975e\u4eba\u4e4b\u654c\uff0c\u4f60\u600e\u4f1a\u662f\u4ed6\u654c\u624b\uff1f\u201d \u3000\u3000\u65b9\u745c\u5fc3\u4e2d\u6697\u9053\uff1a\u6211\u5982\u8bf4\u51fa\u4e1c\u8033\u8001\u4eba\u4f20\u6211\u5251\u6cd5\u4e4b\u4e8b\uff0c\u6216\u53ef\u9080\u5f97\u5927\u5a18\u4e4b\u5141\uff0c\u4f46\u4ed6\u53c8\u544a\u8bc9\u6211\u4e0d\u80fd\u968f\u4fbf\u8bf4\u51fa\uff0c\u53ea\u6015\u96be\u83b7\u5927\u5a18\u4e4b\u5141\u4e86\u3002 \u3000\u3000\u5fc3\u4e2d\u4e00\u6025\uff0c\u8131\u53e3\u8bf4\u9053\uff1a\u201c\u745c\u513f\u4e4b\u89c1\uff0c\u8ba4\u4e3a\u6c5f\u6e56\u4e0a\u6709\u5f88\u591a\u4e8b\uff0c\u4f3c\u662f\u4e0d\u4e00\u5b9a\u8981\u7528\u6b66\u529f\uff0c\u745c\u513f\u6b66\u529f\u867d\u7136\u4e0d\u6210\uff0c\u4f46\u6216\u53ef\u8bf4\u670d\u5bf9\u65b9\uff0c\u6d88\u5f25\u4e00\u573a\u6740\u52ab\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u53f9\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u4f60\u786e\u7136\u8bfb\u4e86\u5f88\u591a\u4e66\uff0c\u4f46\u6b66\u6797\u4e2d\u6709\u5f88\u591a\u4e8b\uff0c\u5374\u4e0d\u662f\u4f60\u60f3\u7684\u90a3\u822c\u7b80\u5355\uff0c\u4e0d\u7528\u80e1\u601d\u4e71\u60f3\u4e86\u3002\u201d \u3000\u3000\u65b9\u73de\u8c6a\u58ee\u4ed6\u8bf4\u9053\uff1a\u201c\u7978\u7531\u5b69\u513f\u5f15\u8d77\uff0c\u6211\u8981\u53bb\u52a9\u7239\u7239\u4e00\u81c2\u4e4b\u529b\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u6447\u6447\u5934\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u5982\u662f\u6709\u5fd7\u6c14\u7684\u7537\u513f\uff0c\u5c31\u8be5\u4ece\u4eca\u65e5\u8d77\uff0c\u5fc3\u4e0d\u65c1\u9a9b\uff0c\u82e6\u82e6\u4e60\u7ec3\u6b66\u529f\uff0c\u65e5\u540e\u80fd\u591f\u627f\u7ee7\u4f60\u7236\u5a01\u540d\uff0c\u6b64\u65f6\uff0c\u4f60\u5e74\u672a\u957f\u6210\uff0c\u6b66\u672a\u4e60\u597d\uff0c\u5982\u4f55\u53bb\u52a9\u4f60\u7236\u4eb2\uff1f\u201d \u3000\u3000\u65b9\u73de\u5e94\u9053\uff1a\u201c\u6bcd\u4eb2\u6559\u8bad\u7684\u662f\u3002\u201d\u5782\u9996\u4e0d\u518d\u591a\u8a00\u3002 \u3000\u3000\u65b9\u745c\u7a81\u7136\u60f3\u8d77\u90a3\u7b2c\u4e8c\u8f86\u7bf7\u8f66\uff0c\u662f\u5426\u968f\u540c\u800c\u6765\uff0c\u4f38\u624b\u6380\u5f00\u5782\u5e18\u5411\u540e\u671b\u53bb\u3002\u4f46\u89c1\u56db\u90ca\u8352\u51c9\uff0c\u90a3\u7b2c\u4e8c\u8f86\u7bf7\u8f66\u5e76\u672a\u968f\u540c\u800c\u6765\u3002 \u3000\u3000\u65b9\u745c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6697\u9053\uff1a\u201c\u662f\u4e86\uff0c\u90a3\u8f86\u7bf7\u8f66\uff0c\u539f\u6765\u662f\u4f5c\u4f2a\u88c5\u4e4b\u7528\u2026\u2026\u201d \u3000\u3000\u5fd6\u601d\u4e4b\u95f4\uff0c\u7a81\u89c1\u70df\u5c18\u6eda\u6eda\uff0c\u51e0\u5339\u5feb\u9a6c\uff0c\u96f7\u5954\u7535\u95ea\u4e00\u822c\uff0c\u6025\u9a70\u800c\u6765\u3002 \u3000\u3000\u53ea\u542c\u8a00\u51e4\u537f\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u8001\u4e08\uff0c\u628a\u7bf7\u8f66\u8f6c\u5165\u8def\u65c1\u90a3\u7247\u8349\u5730\u4e0a\u53bb\u3002\u201d\u76ee\u5149\u4e00\u63a0\u65b9\u745c\uff0c\u63a5\u9053\uff1a\u201c\u4e16\u5144\u8bf7\u8eb2\u5165\u8f66\u7bf7\u4e2d\u3002\u201d \u3000\u3000\u65b9\u745c\u6025\u6025\u7f29\u56de\u8eab\u5b50\uff0c\u653e\u4e0b\u5782\u5e18\u3002 \u3000\u3000\u4e1c\u8033\u8001\u4eba\u957f\u97ad\u4e00\u6325\uff0c\u7bf7\u8f66\u6253\u4e2a\u6025\u8f6c\uff0c\u6298\u5165\u9053\u65c1\u4e00\u7247\u9752\u8349\u5730\u4e0a\u3002 \u3000\u3000\u65b9\u592b\u4eba\u671b\u4e86\u65b9\u745c\u4e00\u773c\uff0c\u9053\uff1a\u201c\u745c\u513f\uff0c\u53ef\u662f\u6709\u4eba\u8ffd\u54b1\u4eec\u6765\u4e86\uff1f\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u51e0\u5339\u5feb\u9a6c\u5954\u4e86\u8fc7\u6765\uff0c\u662f\u5426\u8ffd\u54b1\u4eec\u7684\uff0c\u90a3\u5c31\u4e0d\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u9053\uff1a\u201c\u4e0d\u7ba1\u53d1\u751f\u4ec0\u4e48\u4e8b\uff0c\u672a\u5f97\u547c\u53eb\uff0c\u4f60\u4eec\u90fd\u4e0d\u8bb8\u51fa\u53bb\u3002\u201d \u3000\u3000\u65b9\u745c\uff0c\u65b9\u73de\u9f50\u9f50\u70b9\u5934\u3002 \u3000\u3000\u65b9\u592b\u4eba\u4f38\u624b\u6478\u8fc7\u4e00\u67c4\u5b9d\u5251\uff0c\u653e\u5728\u8eab\u524d\uff0c\u95ed\u76ee\u517b\u606f\u3002 \u3000\u3000\u65b9\u73de\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u5927\u54e5\uff0c\u6765\u7684\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u65b9\u745c\u6447\u6447\u5934\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u8ba4\u8bc6\u3002\u201d \u3000\u3000\u65b9\u73de\u9053\uff1a\u201c\u6709\u51e0\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u6ca1\u6709\u770b\u6e05\u695a\uff0c\u4f46\u81f3\u5c11\u5728\u4e24\u4eba\u4ee5\u4e0a\u3002\u201d \u3000\u3000\u8c08\u8bdd\u4e4b\u95f4\uff0c\u9a6c\u8f66\u5df2\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u53ea\u542c\u8e44\u58f0\u5f97\u3001\u76f4\u8fbe\u8f66\u524d\uff0c\u4f3c\u662f\u6709\u51e0\u5339\u9a6c\uff0c\u884c\u8fd1\u4e86\u7bf7\u8f66\u505c\u4e0b\u3002 \u3000\u3000\u65b9\u73de\u5fcd\u4e0d\u4e0b\u597d\u5947\u4e4b\u5fc3\uff0c\u4f38\u624b\u53bb\u6380\u5782\u5e18\u3002 \u3000\u3000\u4e00\u53ea\u7389\u624b\uff0c\u4f38\u4e86\u8fc7\u6765\uff0c\u633d\u4f4f\u4e86\u65b9\u73de\u7684\u624b\u8155\u3002 \u3000\u3000\u65b9\u73de\u8f6c\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u90a3\u62e6\u4f4f\u81ea\u5df1\u4e4b\u4eba\uff0c\u6b63\u662f\u6bcd\u4eb2\uff0c\u6025\u6025\u7f29\u56de\u624b\u53bb\u3002 \u3000\u3000\u4f46\u95fb\u4e00\u4e2a\u7c97\u8c6a\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u8fd9\u8f86\u7bf7\u8f66\uff0c\u53ef\u662f\u5750\u7684\u65b9\u592b\u4eba\uff1f\u201d \u3000\u3000\u53ea\u542c\u8a00\u51e4\u537f\u7684\u58f0\u97f3\u5e94\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u59d3\u65b9\uff0c\u8bf8\u4f4d\u627e\u9519\u4eba\u4e86\u3002\u201d \u3000\u3000\u4e00\u4e2a\u51b7\u51b0\u7684\u58f0\u97f3\u63a5\u9053\uff1a\u201c\u4f60\u670b\u53cb\u8d35\u59d3\uff1f\u4e2d\u5dde\u4e09\u4fa0\u4ea4\u5f80\u7684\u670b\u53cb\u4e2d\uff0c\u54b1\u4eec\u5927\u90fd\u8ba4\u8bc6\uff0c\u5c31\u7b97\u53eb\u4e0d\u51fa\u540d\u5b57\uff0c\u90a3\u9762\u5b54\u4e5f\u719f\u5f97\u5f88\uff0c\u4f46\u5374\u4ece\u672a\u89c1\u8fc7\u9601\u4e0b\uff01\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u90a3\u662f\u56e0\u4e3a\u5728\u4e0b\u5e76\u975e\u4e2d\u5dde\u4e09\u4fa0\u7684\u660e\u53cb\uff0c\u81f3\u4e8e\u5c0f\u59d3\u561b\u2026\u2026\u201d\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4f3c\u4e4e\u6ca1\u6709\u544a\u8bc9\u8bf8\u4f4d\u7684\u5fc5\u8981\u3002\u201d \u3000\u3000\u4f46\u95fb\u4e00\u58f0\u51b7\u7b11\uff0c\u53e6\u4e00\u4e2a\u9634\u68ee\u7684\u58f0\u97f3\u63a5\u9053\uff1a\u201c\u670b\u53cb\u65e2\u975e\u5c40\u4e2d\u4eba\uff0c\u597d\u50cf\u4e0d\u7528\u6dcc\u8fd9\u6b21\u6d51\u6c34\uff0c\u9700\u77e5\u5200\u67aa\u65e0\u773c\uff0c\u9601\u4e0b\u2026\u2026\u201d \u3000\u3000\u8a00\u51e4\u537f\u63a5\u9053\uff1a\u201c\u4f4f\u53e3\uff0c\u4e09\u4f4d\u548c\u6211\u7d22\u4e0d\u76f8\u8bc6\uff0c\u5728\u4e0b\u7b49\u6025\u7740\u8d76\u8def\uff0c\u65e0\u6687\u548c\u8bf8\u4f4d\u591a\u8c08\uff0c\u8bf8\u4f4d\u8bf7\u8ba9\u8ba9\u8def\u4e86\u3002\u201d \u3000\u3000\u4ed6\u4e5f\u4e0d\u613f\u548c\u6765\u4eba\u95f9\u7ffb\u52a8\u624b\uff0c\u662f\u4ee5\u540e\u4e24\u53e5\u8bdd\u63aa\u8bcd\u5f88\u67d4\u548c\u3002 \u3000\u3000\u4f46\u95fb\u4e00\u9635\u54c8\u54c8\u5927\u7b11\uff0c\u4f20\u5165\u8033\u9645\uff0c\u9053\uff1a\u201c\u597d\u670b\u53cb\uff0c\u4f60\u8bf4\u5f97\u5f88\u8f7b\u677e\u554a\u2026\u2026\u201d \u3000\u3000\u7a81\u7136\u8dc3\u884c\u9a6c\u80cc\uff0c\u80cc\u4e0a\u5355\u5200\u4e5f\u540c\u65f6\u51fa\u624b\uff0c\u4f46\u624b\u5411\u4e0a\u5782\u5e18\u6311\u53bb\uff0c\u53e3\u4e2d\u5374\u8bf4\u9053\uff1a\u201c\u8fd9\u8f66\u4e2d\u65e2\u975e\u65b9\u592b\u4eba\uff0c\u54b1\u4eec\u6311\u5f00\u8f66\u5e18\u77a7\u77a7\u5982\u4f55\uff1f\u201d \u3000\u3000\u53ea\u542c\u5f53\u7684\u4e00\u58f0\uff0c\u8a00\u51e4\u537f\u957f\u5251\u51fa\u9798\uff0c\u6321\u5f00\u4e86\u90a3\u6311\u5e18\u5355\u5200\u3002 \u3000\u3000\u65b9\u592b\u4eba\u6293\u8d77\u957f\u5251\uff0c\u62c9\u5f00\u5782\u5e18\uff0c\u884c\u51fa\u7bf7\u8f66\u3002 \u3000\u3000\u62ac\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u4e24\u4e2a\u52b2\u88c5\u4f69\u5200\u5927\u6c49\uff0c\u7aef\u5750\u9a6c\u4e0a\uff0c\u53e6\u4e00\u4e2a\u624b\u63e1\u5355\u5200\uff0c\u7ad9\u5728\u8f66\u8fb9\uff0c\u8a00\u51e4\u537f\u5e73\u4e3e\u957f\u5251\uff0c\u6321\u5728\u90a3\u5927\u6c49\u8eab\u524d\u3002 \u3000\u3000\u65b9\u592b\u4eba\u53f3\u624b\u4e00\u6309\u673a\u7c27\uff0c\u545b\u7684\u4e00\u58f0\uff0c\u957f\u5251\u51fa\u9798\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4e09\u4f4d\u627e\u6211\u5417\uff1f\u201d \u3000\u3000\u5979\u867d\u5df2\u5e74\u8fc7\u4e09\u5341\uff0c\u4f46\u770b\u4e0a\u53bb\uff0c\u4ecd\u662f\u5a07\u7f8e\u4e0d\u53ef\u903c\u89c6\u3002 \u3000\u3000\u8a00\u51e4\u537f\u76ee\u7779\u65b9\u592b\u4eba\u4ed7\u5251\u800c\u51fa\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u770b\u6765\u4eca\u65e5\u96be\u514d\u4e00\u756a\u6076\u6597\u4e86\u3002\u201d \u3000\u3000\u65b9\u745c\u76ee\u5149\u8f6c\u52a8\uff0c\u53ea\u89c1\u90a3\u4e1c\u8033\u8001\u4eba\u7aef\u5750\u5728\u8f66\u524d\uff0c\u624b\u4e2d\u6267\u7740\u957f\u97ad\uff0c\u795e\u60c5\u5e73\u9759\uff0c\u5bf9\u8eab\u4fa7\u7684\u4e89\u6267\u3001\u5435\u95f9\uff0c\u6d51\u5982\u4e0d\u89c9\u3002 \u3000\u3000\u5de6\u9996\u5927\u6c49\u7a81\u7136\u4e00\u62b1\u62f3\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u662f\u65b9\u592b\u4eba\uff1f\u201d \u3000\u3000\u65b9\u592b\u4eba\u795e\u8272\u4e25\u8083\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u4eec\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u90a3\u5de6\u9996\u5927\u6c49\uff0c\u4f3c\u662f\u4e09\u4eba\u4e2d\u7684\u9996\u8111\uff0c\u5fae\u5fae\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u5949\u547d\u8fce\u63a5\u65b9\u592b\u4eba\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u9053\uff1a\u201c\u5949\u8c01\u4e4b\u547d\uff1f\u201d \u3000\u3000\u90a3\u5927\u6c49\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u8bf7\u6055\u5728\u4e0b\u4e0d\u4fbf\u5949\u544a\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u90a3\u4f60\u53bb\u544a\u8bc9\u90a3\u4eba\uff0c\u6211\u4e0d\u53bb\u3002\u201d \u3000\u3000\u90a3\u5927\u6c49\u8138\u8272\u7a81\u7136\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u592b\u4eba\u9519\u4e86\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u6012\u9053\uff1a\u201c\u6211\u54ea\u91cc\u9519\u4e86\uff1f\u201d \u3000\u3000\u90a3\u5927\u6c49\u9053\uff1a\u201c\u592b\u4eba\u53bb\u4e5f\u5f97\u53bb\uff0c\u4e0d\u53bb\u4e5f\u5f97\u53bb\uff0c\u54b1\u4eec\u65e2\u7136\u5949\u547d\u800c\u6765\uff0c\u975e\u8bf7\u592b\u4eba\u53bb\u4e00\u8d9f\u4e0d\u53ef\uff01\u201d \u3000\u3000\u65b9\u592b\u4eba\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5982\u662f\u6211\u4e00\u5b9a\u4e0d\u53bb\u5462\uff1f\u201d \u3000\u3000\u90a3\u5927\u6c49\u9053\uff1a\u201c\u60c5\u975e\u5f97\u5df2\uff0c\u54b1\u4eec\u53ea\u597d\u52a8\u5f3a\u903c\u8feb\u592b\u4eba\u800c\u53bb\u4e86\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u770b\u9519\u4eba\u4e86\uff0c\u6211\u53ef\u4ee5\u6218\u6b7b\u6b64\u5730\uff0c\u6a2a\u5c38\u8352\u90ca\uff0c\u4f46\u4e5f\u4e0d\u4f1a\u8ddf\u4f60\u4eec\u53bb\u3002\u201d \u3000\u3000\u5979\u5929\u6027\u67d4\u548c\uff0c\u867d\u662f\u6c14\u6012\u4e4b\u8a00\uff0c\u4e5f\u8bf4\u7684\u5a49\u8f6c\u5f02\u5e38\u3002 \u3000\u3000\u90a3\u5de6\u9996\u5927\u6c49\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u5949\u547d\u8981\u64d2\u6d3b\u7684\uff0c\u5982\u4f55\u80fd\u8ba9\u4f60\u6b7b\u53bb\uff1f\u201d \u3000\u3000\u7aef\u5750\u5728\u8f66\u524d\u7684\u4e1c\u8033\u8001\u4eba\uff0c\u7a81\u7136\u56de\u5934\u671b\u4e86\u4e09\u4e2a\u5927\u6c49\u4e00\u773c\uff0c\u4f46\u4ed6\u5374\u5fcd\u4e86\u4e0b\u53bb\uff0c\u672a\u591a\u63a5\u8a00\u3002 \u3000\u3000\u65b9\u592b\u4eba\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u8981\u5984\u60f3\u6211\u4f1a\u7b54\u5e94\u3002\u201d \u3000\u3000\u90a3\u5de6\u9996\u5927\u6c49\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8001\u6881\uff0c\u4f60\u51fa\u624b\u8bd5\u8bd5\u65b9\u592b\u4eba\u7684\u5251\u6cd5\u5982\u4f55\uff1f\u201d \u3000\u3000\u90a3\u7ad9\u5728\u5730\u4e0a\u7684\u5927\u6c49\u5e94\u4e86\u4e00\u58f0\uff0c\u624b\u4e2d\u5355\u5200\u4e00\u5241\uff0c\u523a\u5411\u65b9\u592b\u4eba\u3002 \u3000\u3000\u5ffd\u89c1\u4eba\u5f71\u4e00\u95ea\uff0c\u4e00\u9053\u5bd2\u5149\u98de\u4e86\u8fc7\u6765\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u67b6\u5f00\u4e86\u90a3\u5927\u6c49\u624b\u4e2d\u7684\u5355\u5200\u3002 \u3000\u3000\u65b9\u592b\u4eba\u56de\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u8a00\u51e4\u537f\u624b\u6267\u957f\u5251\uff0c\u6321\u5728\u81ea\u5df1\u8eab\u524d\u3002 \u3000\u3000\u90a3\u6267\u5200\u5927\u6c49\uff0c\u88ab\u8a00\u51e4\u537f\u4e00\u5251\uff0c\u9707\u5f97\u5411\u540e\u9000\u4e86\u4e00\u6b65\uff0c\u5fc3\u4e2d\u53c8\u60ca\u53c8\u6012\uff0c\u51b7\u51b7\u559d\u9053\uff1a\u201c\u770b\u9601\u4e0b\u8eab\u624b\uff0c\u4e0d\u4f3c\u4ec6\u4ece\u2026\u2026\u201d \u3000\u3000\u8a00\u51e4\u537f\u63a5\u9053\uff1a\u201c\u54b1\u4eec\u4f3c\u662f\u7528\u4e0d\u7740\u901a\u540d\u62a5\u59d3\uff0c\u9601\u4e0b\u5200\u4e0a\u6b66\u529f\uff0c\u53ea\u7ba1\u65bd\u5c55\u5c31\u662f\u3002\u201d \u3000\u3000\u90a3\u5de6\u9996\u5927\u6c49\u4ecd\u7136\u7aef\u5750\u5728\u9a6c\u4e0a\uff0c\u51b7\u51b7\u5730\u671b\u4e86\u8a00\u51e4\u537f\u4e00\u773c\uff0c\u9053\uff1a\u201c\u8fd9\u4f4d\u670b\u53cb\u65e2\u662f\u4e00\u5b9a\u8981\u6dcc\u6d51\u6c34\uff0c\u60f3\u5fc5\u662f\u6709\u540d\u6709\u59d3\u7684\u9ad8\u4eba\u4e86\uff0c\u4e0d\u77e5\u5927\u540d\u600e\u4e48\u79f0\u547c\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u6d41\u76ee\u56db\u987e\u4e86\u4e00\u773c\uff0c\u4e0d\u7406\u90a3\u5927\u6c49\u7684\u95ee\u8bdd\uff0c\u76ee\u5149\u8f6c\u5230\u65b9\u592b\u4eba\u7684\u8138\u4e0a\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a \u3000\u3000\u201c\u592b\u4eba\u8bf7\u5148\u884c\u4e00\u6b65\uff0c\u6b64\u5730\u8fd9\u51e0\u4e2a\u65e0\u540d\u5c0f\u5352\uff0c\u6709\u5728\u4e0b\u5bf9\u4ed8\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u90a3\u5c31\u504f\u52b3\u4e86\u3002\u201d\u56de\u8eab\u5411\u8f66\u4e0a\u8d70\u53bb\u3002 \u3000\u3000\u90a3\u5de6\u9996\u5927\u6c49\u5fc3\u4e2d\u5927\u6025\uff0c\u5389\u58f0\u559d\u9053\uff1a\u201c\u52a8\u624b\u3002\u201d\u5f53\u5148\u7531\u9a6c\u80cc\u4e0a\u98de\u8dc3\u800c\u4e0b\uff0c\u76f4\u5411\u65b9\u592b\u4eba\u6251\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8a00\u51e4\u537f\u5fc3\u4e2d\u65e9\u5df2\u7559\u610f\u6b64\u4eba\uff0c\u4ed6\u65e2\u5728\u4e09\u4eba\u4e2d\u53d1\u53f7\u65bd\u4ee4\uff0c\u81ea\u7136\u662f\u6b66\u529f\u8f83\u9ad8\u3002\u64d2\u8d3c\u64d2\u738b\uff0c\u6253\u86c7\u6253\u5934\uff0c\u5982\u80fd\u5148\u6536\u62fe\u4e86\u4ed6\u4eec\u7684\u4e3a\u9996\u4e4b\u4eba\uff0c\u4f59\u4e0b\u4e24\u4eba\uff0c\u4e5f\u5c31\u5bb9\u6613\u5bf9\u4ed8\u4e86\u3002 \u3000 \u7b2c\u516d\u56de\u3000\u4e00\u5251\u594f\u529f \u3000 \u3000\u3000\u90a3\u5de6\u9996\u5927\u6c49\u98de\u6251\u65b9\u592b\u4eba\u7684\u540c\u65f6\uff0c\u8a00\u51e4\u537f\u4e5f\u540c\u65f6\u98de\u8eab\u800c\u8d77\u3002 \u3000\u3000\u8a00\u51e4\u537f\u957f\u5251\u95ea\u95ea\uff0c\u523a\u5411\u90a3\u5927\u6c49\u524d\u80f8\u3002 \u3000\u3000\u90a3\u5927\u6c49\u98de\u6251\u65b9\u592b\u4eba\u65f6\uff0c\u53f3\u624b\u5df2\u62e8\u51fa\u4e86\u5355\u5200\uff0c\u5200\u52bf\u4e00\u6a2a\uff0c\u75be\u5411\u957f\u5251\u4e4b\u4e0a\u524a\u53bb\u3002 \u3000\u3000\u53ea\u542c\u4e00\u9635\u91d1\u94c1\u4ea4\u545c\uff0c\u5200\u5251\u76f8\u89e6\uff0c\u786c\u62fc\u4e86\u4e00\u62db\u3002 \u3000\u3000\u8fd9\u4e00\u62db\uff0c\u53cc\u65b9\u90fd\u7528\u4e0a\u4e86\u5f88\u5f3a\u7684\u529b\u9053\uff0c\u91d1\u94c1\u4ea4\u9e23\u58f0\u4e2d\uff0c\u4e24\u4eba\u540c\u65f6\u7531\u7a7a\u4e2d\u843d\u5728\u5b9e\u5730\u3002 \u3000\u3000\u8a00\u51e4\u537f\u5fc3\u4e2d\u6697\u9053\uff1a\u6b64\u4eba\u5185\u529b\u751a\u5f3a\uff0c\u4e0d\u53ef\u548c\u4ed6\u786c\u62da\u86ee\u529b\uff01\u957f\u5251\u4e00\u8d77\uff0c\u62e6\u8170\u524a\u53bb\u3002 \u3000\u3000\u90a3\u5927\u6c49\u6325\u5200\u8fce\u6218\uff0c\u5c55\u5f00\u4e86\u4e00\u573a\u6076\u6597\u3002 \u3000\u3000\u53e6\u5916\u4e24\u4e2a\u5927\u6c49\uff0c\u5374\u53cc\u53cc\u6251\u5411\u65b9\u592b\u4eba\u3002 \u3000\u3000\u8a00\u51e4\u537f\u867d\u7136\u770b\u5728\u773c\u4e2d\uff0c\u4f46\u56e0\u548c\u90a3\u5927\u6c49\u6076\u6597\u6b63\u70c8\uff0c\u65e0\u6cd5\u5206\u8eab\u62e6\u4f4f\u3002 \u3000\u3000\u65b9\u592b\u4eba\u4f3c\u662f\u65e9\u5df2\u60f3\u5230\u4e86\u8a00\u51e4\u537f\u65e0\u6cd5\u963b\u62e6\u4e09\u4e2a\u4eba\u7684\u540c\u65f6\u653b\u51fb\uff0c\u5e76\u975e\u8dc3\u767b\u9022\u8f66\uff0c\u53cd\u800c\u7ad9\u5728\u8f66\u524d\u7b49\u5019\u3002 \u3000\u3000\u4e24\u4eba\u5206\u5de6\u53f3\u540c\u65f6\u6251\u5230\u3002 \u3000\u3000\u5927\u7ea6\u4ed6\u4eec\u5949\u6709\u4e25\u547d\uff0c\u4e0d\u80fd\u4f24\u5bb3\u65b9\u592b\u4eba\uff0c\u4e24\u4eba\u624b\u4e2d\u867d\u7136\u90fd\u62ff\u7740\u5355\u5200\uff0c\u4f46\u5e76\u672a\u7528\u5200\u65bd\u88ad\uff0c\u540c\u65f6\u4f38\u51fa\u5de6\u624b\uff0c\u6293\u5411\u65b9\u592b\u4eba\u3002 \u3000\u3000\u65b9\u592b\u4eba\u957f\u5251\u6325\u52a8\uff0c\u6a2a\u626b\u800c\u51fa\u3002 \u3000\u3000\u4e24\u4eba\u867d\u672a\u7528\u5200\u65bd\u88ad\uff0c\u4f46\u5374\u6325\u5200\u67b6\u5f00\u4e86\u65b9\u592b\u4eba\u7684\u5251\u52bf\uff0c\u5de6\u624b\u53cc\u540c\u65f6\u653b\u5411\u4e86\u65b9\u592b\u4eba\u3002 \u3000\u3000\u65b9\u592b\u4eba\u7eb5\u8eab\u907f\u5f00\uff0c\u957f\u5251\u95ea\u52a8\uff0c\u5c55\u5f00\u4e86\u4e00\u8f6e\u5feb\u653b\u3002 \u3000\u3000\u5979\u5fc3\u4e2d\u660e\u767d\uff0c\u5982\u82e5\u4e0d\u5e78\u88ab\u4e24\u4eba\u64d2\u53bb\uff0c\u5fc5\u5c06\u53d7\u5c3d\u51cc\u8fb1\uff0c\u5b81\u53ef\u6218\u6b7b\uff0c\u4ea6\u4e0d\u80fd\u4e3a\u4e24\u4eba\u751f\u64d2\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u5251\u62db\u6781\u662f\u51cc\u5389\uff0c\u62db\u62db\u90fd\u653b\u5411\u4e24\u4e2a\u5927\u6c49\u7684\u8981\u5bb3\uff0c\u4e03\u6210\u6b66\u529f\uff0c\u53d1\u6325\u4e86\u5341\u6210\u5a01\u529b\u3002 \u3000\u3000\u4e24\u4e2a\u5927\u6c49\u5374\u4e0d\u6562\u5bf9\u90a3\u65b9\u592b\u4eba\u65bd\u4e0b\u6bd2\u624b\uff0c\u8fd9\u4e00\u6765\u4f7f\u65b9\u592b\u4eba\u5360\u4e86\u4e0d\u5c11\u4fbf\u5b9c\u3002 \u3000\u3000\u65b9\u745c\u3001\u65b9\u73de\u5df2\u9ad8\u5377\u8f66\u5e18\uff0c\u5e76\u80a9\u89c2\u6218\u3002 \u3000\u3000\u4f46\u662f\u51e0\u4eba\u7684\u6076\u6597\uff0c\u6108\u6253\u6108\u70c8\uff0c\u90a3\u4e24\u4e2a\u5927\u6c49\u5728\u65b9\u592b\u4eba\u5251\u52bf\u8feb\u653b\u4e4b\u4e0b\uff0c\u4e3a\u6c42\u81ea\u4fdd\uff0c\u4e5f\u9010\u6e10\u5c55\u5f00\u4e86\u53cd\u51fb\u3002 \u3000\u3000\u65b9\u592b\u4eba\u672c\u975e\u4e24\u4eba\u8054\u624b\u4e4b\u654c\uff0c\u5728\u4e24\u4eba\u8054\u653b\u53cd\u51fb\u4e4b\u4e0b\uff0c\u5251\u52bf\u9010\u6e10\u53d7\u5236\uff0c\u65bd\u5c55\u4e0d\u5f00\u3002 \u3000\u3000\u65b9\u745c\u53ea\u77a7\u7684\u5fc3\u4e2d\u5927\u6025\uff0c\u56de\u5934\u671b\u53bb\uff0c\u53ea\u89c1\u4e1c\u8033\u8001\u4eba\uff0c\u95ed\u76ee\u800c\u5750\uff0c\u5bf9\u773c\u524d\u7684\u6076\u6597\uff0c\u4f3c\u662f\u6beb\u4e0d\u5173\u5fc3\uff0c\u5fcd\u4e0d\u4f4f\u8bf4\u9053\uff1a\u201c\u8001\u524d\u8f88\u2026\u2026\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u7a81\u7136\u56de\u8fc7\u5934\u6765\uff0c\u4e24\u9053\u70af\u70af\u7684\u76ee\u5149\uff0c\u4e00\u63a0\u65b9\u745c\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u600e\u4e0d\u53bb\u52a9\u4f60\u5927\u5a18\u4e00\u81c2\u4e4b\u529b\u5462\uff1f\u201d \u3000\u3000\u65b9\u745c\u6014\u4e86\u4e00\u6014\u9053\uff1a\u201c\u6211\u2026\u2026\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u6de1\u6de1\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u5fc3\u4e2d\u5bb3\u6015\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u65b9\u745c\u6447\u6447\u5934\u9053\uff1a\u201c\u4e0d\u662f\u2026\u2026\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u63a5\u9053\uff1a\u201c\u65e2\u7136\u4e0d\u662f\u5bb3\u6015\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u51fa\u624b\u8bd5\u8bd5\u5462\uff1f\u201d \u3000\u3000\u65b9\u745c\u7a81\u7136\u4e00\u8dc3\u4e0b\u8f66\uff0c\u5237\u7684\u4e00\u58f0\uff0c\u62bd\u51fa\u4e86\u957f\u5251\u3002 \u3000\u3000\u4f46\u89c1\u4eba\u5f71\u4e00\u95ea\uff0c\u65b9\u73de\u5374\u62a2\u5728\u4e86\u65b9\u745c\u524d\u9762\uff0c\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u4e66\u8bfb\u7684\u6bd4\u6211\u591a\uff0c\u4f46\u6b66\u529f\u5374\u4e0d\u5982\u6211\uff0c\u5feb\u4e0a\u8f66\uff0c\u6211\u53bb\u52a9\u6bcd\u4eb2\u4e00\u81c2\u4e4b\u529b\u3002\u201d \u3000\u3000\u53ea\u89c1\u90a3\u59d3\u7cb1\u7684\u5927\u6c49\u8bf4\u9053\uff1a\u201c\u674e\u5144\uff0c\u4f60\u5bf9\u4ed8\u65b9\u592b\u4eba\uff0c\u6211\u53bb\u6536\u62fe\u90a3\u4e2a\u5a03\u513f\u3002\u201d \u3000\u3000\u53e6\u4e00\u4e2a\u5927\u6c49\u5e94\u4e86\u4e00\u58f0\uff0c\u5200\u6cd5\u4e00\u7d27\uff0c\u7c91\u65b9\u592b\u4eba\u7684\u5251\u62db\uff0c\u5c3d\u6570\u63a5\u8fc7\u3002 \u3000\u3000\u90a3\u59d3\u6881\u7684\u5927\u6c49\uff0c\u7a81\u7136\u75be\u9000\u4e24\u6b65\uff0c\u62bd\u8eab\u800c\u51fa\uff0c\u53f3\u624b\u5355\u5200\u4e00\u7d27\uff0c\u76f4\u5411\u65b9\u73de\u7b49\u6251\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u65b9\u745c\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u5144\u5f1f\u95ea\u5f00\uff01\u201d \u3000\u3000\u65b9\u73de\u54ea\u91cc\u80af\u5e94\uff0c\u75be\u4e0a\u4e24\u6b65\uff0c\u957f\u5251\u4e00\u63a2\uff0c\u523a\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u6881\u59d3\u5927\u6c49\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4e2a\u4e00\u8d77\u4e0a\u5427\uff01\u4e5f\u514d\u4e86\u6881\u5927\u7237\u591a\u8d39\u624b\u811a\uff0c\u6211\u4eec\u4e0d\u4f24\u4f60\u5988\u5988\uff0c\u4f46\u5bf9\u4f60\u4eec\u5374\u662f\u4e0d\u7528\u624b\u4e0b\u7559\u60c5\u4e86\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5df2\u7136\u6321\u5f00\u4e86\u65b9\u73de\u624b\u4e2d\u957f\u5251\uff0c\u6325\u5200\u8fd8\u51fb\u3002 \u3000\u3000\u65b9\u73de\u867d\u7136\u5df2\u5f97\u65b9\u5929\u6210\u7684\u771f\u4f20\uff0c\u7ec8\u662f\u5e74\u9f84\u5e7c\u5c0f\uff0c\u53c8\u7f3a\u4e4f\u5bf9\u654c\u7ecf\u9a8c\uff0c\u88ab\u90a3\u5927\u6c49\u4e00\u8fde\u4e09\u5200\uff0c\u8feb\u5f97\u5411\u540e\u8fde\u9000\u6570\u6b65\u3002 \u3000\u3000\u65b9\u745c\u62a2\u4e0a\u4e24\u6b65\uff0c\u559d\u9053\uff1a\u201c\u5144\u5f1f\u9000\u5f00\uff01\u201d\u5237\u7684\u4e00\u5251\uff0c\u523a\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u90a3\u6881\u59d3\u5927\u6c49\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u662f\u54e5\u54e5\uff0c\u5148\u6740\u4f60\u4e5f\u662f\u4e00\u6837\u3002\u201d\u4e3e\u8d77\u624b\u4e2d\u5355\u5200\u4e00\u64a9\u3002 \u3000\u3000\u54ea\u77e5\u65b9\u745c\u624b\u4e2d\u5251\u52bf\uff0c\u4e0d\u5f85\u4ed6\u5355\u5200\u51fa\u624b\uff0c\u5df2\u7136\u81ea\u52a8\u5411\u4e0a\u8f6c\u523a\u3002 \u3000\u3000\u8fd9\u4e00\u53d8\uff0c\u5927\u51fa\u610f\u5916\uff0c\u90a3\u5927\u6c49\u5fae\u5fae\u4e00\u6014\u4e4b\u65f6\uff0c\u957f\u5251\u5df2\u7136\u523a\u4e2d\u524d\u80f8\uff0c\u76f4\u5165\u5185\u8151\u3002\u5251\u52bf\u523a\u4e2d\u8981\u5bb3\uff0c\u90a3\u6881\u59d3\u5927\u6c49\u8fde\u5782\u6b7b\u53cd\u51fb\u4e4b\u80fd\u4e5f\u5931\u53bb\uff0c\u8eab\u5b50\u4e00\u98a4\uff0c\u5012\u5730\u6b7b\u53bb\u3002 \u3000\u3000\u65b9\u745c\u6709\u751f\u4ee5\u6765\uff0c\u521d\u6b21\u6740\u4eba\uff0c\u8fde\u5251\u4e5f\u4e0d\u77e5\u62d4\u51fa\uff0c\u8fd8\u662f\u5c38\u4f53\u683d\u5012\u65f6\uff0c\u5b9d\u5251\u81ea\u52a8\u8131\u4e0b\u3002 \u3000\u3000\u65b9\u73de\u770b\u4ed6\u5251\u523a\u4e2d\u4e86\u654c\u4eba\u8981\u5bb3\uff0c\u5f53\u573a\u6c14\u7edd\u800c\u901d\uff0c\u5fc3\u4e2d\u5927\u611f\u5947\u602a\uff0c\u6697\u6697\u5fd6\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\u5462\uff1f\u5927\u54e5\u6b66\u529f\u5411\u4e0d\u5982\u6211\uff0c\u600e\u7684\u7a81\u7136\u95f4\uff0c\u7adf\u6709\u4e86\u8fd9\u5927\u8fdb\u5883\uff1f\u201d \u3000\u3000\u4e14\u8bf4\u65b9\u745c\uff0c\u4e00\u5251\u6bd9\u654c\u4e4b\u540e\uff0c\u8c6a\u6c14\u9661\u751f\uff0c\u5fc3\u4e2d\u4e5f\u6709\u4e86\u5f3a\u70c8\u7684\u4fe1\u5fc3\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u5251\u52bf\u5982\u6b64\u5229\u5bb3\uff0c\u6211\u53ea\u6309\u5e8f\u4f7f\u7528\u51fa\u6765\uff0c\u4e00\u5251\u4e0d\u6210\uff0c\u518d\u7528\u51fa\u7b2c\u4e8c\u5251\u5c31\u662f\u4e86\u3002 \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u4eba\u5374\u5927\u6b65\u884c\u5411\u65b9\u592b\u4eba\uff0c\u53e3\u4e2d\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u5927\u5a18\u8bf7\u9000\u4e0b\u4f11\u606f\uff0c\u8fd9\u6076\u5f92\u4ea4\u7ed9\u5c0f\u4f84\u5c31\u662f\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u56de\u76ee\u4e00\u987e\uff0c\u53ea\u89c1\u90a3\u6881\u59d3\u5927\u6c49\u65e9\u5df2\u6a2a\u5c38\u5f53\u573a\uff0c\u5fc3\u4e2d\u5927\u662f\u5947\u602a\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\u554a\uff0c\u745c\u513f\u3001\u73de\u513f\u90fd\u65e0\u6740\u6b7b\u90a3\u5927\u6c49\u7684\u80fd\u529b\uff0c\u96be\u9053\u662f\u90a3\u4e1c\u8033\u8001\u4eba\u51fa\u624b\u4e0d\u6210\u201d\u539f\u6765\uff0c\u5979\u5fc3\u4e2d\u65e9\u5df2\u6000\u7591\u5230\u90a3\u4e1c\u8033\u8001\u4eba\uff0c\u662f\u4e00\u4f4d\u8eab\u8d1f\u7edd\u6280\u7684\u9ad8\u624b\u3002 \u3000\u3000\u5979\u8fd9\u91cc\u5fc3\u795e\u4e00\u5206\uff0c\u624b\u4e2d\u5251\u6cd5\u7565\u6162\uff0c\u88ab\u90a3\u5927\u6c49\u5237\u3001\u5237\u4e24\u5200\uff0c\u8feb\u9000\u4e86\u4e09\u6b65\u3002 \u3000\u3000\u65b9\u745c\u501f\u52bf\u5927\u884c\u4e00\u6b65\uff0c\u957f\u5251\u4e00\u6325\u800c\u51fa\u3002 \u3000\u3000\u4f46\u95fb\u4e00\u58f0\u60e8\u53eb\uff0c\u90a3\u6267\u5200\u5927\u6c49\uff0c\u5f03\u53bb\u624b\u4e2d\u5355\u5200\uff0c\u53cc\u624b\u62b1\u8179\uff0c\u8e72\u4e86\u4e0b\u53bb\u3002\u539f\u6765\uff0c\u65b9\u745c\u8fd9\u4e00\u5251\u5212\u5230\u4e86\u5bf9\u65b9\u7684\u5c0f\u8179\uff0c\u80a0\u809a\u5b8c\u5168\u6d41\u4e86\u51fa\u6765\u3002 \u3000\u3000\u90a3\u5927\u6c49\u4f24\u52bf\u5947\u91cd\uff0c\u6323\u624e\u4e86\u4e00\u9635\uff0c\u5012\u5730\u6b7b\u53bb\u3002 \u3000\u3000\u65b9\u592b\u4eba\u76ee\u7779\u65b9\u745c\u4e00\u5251\u4f24\u654c\uff0c\u5fc3\u4e2d\u65e2\u662f\u9707\u60ca\uff0c\u53c8\u662f\u5947\u602a\uff0c\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u5b69\u5b50\u4f60\u51e0\u65f6\u5b66\u4e86\u6b66\u529f\uff0c\u600e\u4e48\u5927\u5a18\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\u5462\uff1f\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u745c\u513f\uff0c\u745c\u513f\u2026\u2026\u201d\u53ea\u89c9\u5fc3\u4e2d\u4e4b\u8a00\uff0c\u96be\u4ee5\u8bf4\u51fa\u53e3\u6765\uff0c\u4e0d\u77e5\u5982\u4f55\u56de\u7b54\u624d\u597d\u3002 \u3000\u3000\u65b9\u592b\u4eba\u671b\u4e86\u4e1c\u8033\u8001\u4eba\u4e00\u773c\uff0c\u9053\uff1a\u201c\u662f\u90a3\u4f4d\u4e1c\u8001\u4e08\u4f20\u4f60\u7684\u6b66\u529f\u5417\uff1f\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u65e9\u8be5\u60f3\u5230\u7684\u3002\u201d\u7f13\u6b65\u5bf9\u4e1c\u8033\u8001\u4eba\u884c\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u539f\u6765\uff0c\u90a3\u548c\u8a00\u51e4\u537f\u52a8\u624b\u7684\u5927\u6c49\uff0c\u773c\u770b\u4e24\u4e2a\u540c\u4f34\uff0c\u5c3d\u90fd\u6b7b\u5728\u5bf9\u65b9\u5251\u4e0b\uff0c\u5fc3\u4e2d\u4e00\u614c\uff0c\u88ab\u90a3\u8a00\u51e4\u537f\u4e00\u5251\u523a\u4e2d\u53f3\u81c2\u3002 \u3000\u3000\u8a00\u51e4\u537f\u6740\u673a\u5df2\u52a8\uff0c\u90a3\u8fd8\u5bb9\u4ed6\u9003\u51fa\u5251\u4e0b\uff0c\u4e00\u5251\u5f97\u624b\uff0c\u7b2c\u4e8c\u5251\u7d27\u968f\u5288\u51fa\u3002 \u3000\u3000\u90a3\u5927\u6c49\u53f3\u81c2\u4e2d\u5251\uff0c\u624b\u4e2d\u5355\u5200\uff0c\u5df2\u7ecf\u8fd0\u8f6c\u4e0d\u7075\uff0c\u773c\u770b\u8a00\u51e4\u537f\u4e00\u5251\u5288\u6765\uff0c\u7adf\u662f\u65e0\u6cd5\u8ba9\u907f\uff0c\u5bd2\u8292\u62e6\u8170\u800c\u8fc7\uff0c\u751f\u751f\u88ab\u65a9\u6210\u4e24\u622a\u3002 \u3000\u3000\u4e09\u4e2a\u8ffd\u6765\u5927\u6c49\uff0c\u7247\u523b\u95f4\uff0c\u5c3d\u4e3a\u65a9\u6bd9\u5f53\u573a\u3002 \u3000\u3000\u8a00\u98ce\u537f\u7f13\u6b65\u884c\u4e86\u8fc7\u6765\uff0c\u671b\u4e86\u4e24\u5177\u5c38\u4f53\u4e00\u773c\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u592b\u4eba\u6b66\u529f\u9ad8\u5f3a\uff0c\u529b\u6bd9\u4e8c\u654c\uff0c\u5728\u4e0b\u60ed\u6127\u5f97\u62a5\u3002\u201d \u3000\u3000\u539f\u6765\uff0c\u4ed6\u548c\u90a3\u5927\u6c49\u5168\u529b\u76f8\u640f\uff0c\u7adf\u7136\u4e0d\u77e5\u8fd9\u4e24\u4eba\u662f\u8a00\u9053\u6740\u6b7b\u3002 \u3000\u3000\u90a3\u65b9\u592b\u4eba\u5df2\u884c\u8fd1\u8f66\u524d\uff0c\u95fb\u58f0\u8f6c\u8fc7\u8138\u6765\uff0c\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u662f\u6211\u6740\u6b7b\u7684\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4e0d\u662f\u592b\u4eba\uff0c\u90a3\u662f\u4ec0\u4e48\u4eba\uff0c\u8fde\u6bd9\u4e24\u654c\uff1f\u201d \u3000\u3000\u65b9\u73de\u4f38\u624b\u6307\u7740\u65b9\u745c\uff0c\u9053\uff1a\u201c\u662f\u6211\u5927\u54e5\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u671b\u4e86\u65b9\u745c\u4e00\u773c\uff0c\u9053\uff1a\u201c\u662f\u65b9\u4e16\u4f84\uff1f\u201d \u3000\u3000\u65b9\u745c\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5c0f\u4f84\u53ea\u662f\u78b0\u5de7\u7f62\u4e86\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u65b9\u5bb6\u5251\u6cd5\uff0c\u5982\u6b64\u4e86\u5f97\uff0c\u5728\u4e0b\u957f\u4e86\u4e0d\u5c11\u89c1\u8bc6\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u95ee\u9053\uff1a\u201c\u4ed6\u4e0d\u662f\u7528\u7684\u65b9\u5bb6\u5251\uff1f\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u4e0d\u662f\u65b9\u5bb6\u5251\uff0c\u90a3\u662f\u2026\u2026\u201d \u3000\u3000\u65b9\u745c\u53ea\u89c9\u5fc3\u4e2d\u4e4b\u8a00\uff0c\u65e0\u6cd5\u51fa\u53e3\uff0c\u6ee1\u8138\u901a\u7ea2\u3002 \u3000\u3000\u8a00\u51e4\u537f\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4e16\u4f84\uff0c\u4e0d\u7528\u4e3a\u96be\uff0c\u5728\u4e0b\u5e76\u65e0\u8ffd\u6839\u76d8\u5e95\u4e4b\u610f\u3002\u201d \u3000\u3000\u53ea\u89c1\u65b9\u592b\u4eba\u884c\u5230\u90a3\u8d76\u8f66\u8001\u4eba\u7684\u8eab\u524d\uff0c\u798f\u4e86\u4e00\u798f\uff0c\u9053\uff1a\u201c\u8001\u524d\u8f88\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u7aef\u5750\u672a\u52a8\uff0c\u4f3c\u662f\u672a\u95fb\u5f97\u65b9\u592b\u4eba\u547c\u53eb\u4e4b\u8a00\u3002 \u3000\u3000\u8a00\u51e4\u537f\u5fc3\u4e2d\u5927\u611f\u5947\u602a\uff0c\u6697\u9053\uff1a\u201c\u96be\u9053\u8fd9\u4f4d\u8d76\u8f66\u7684\u8001\u4eba\uff0c\u624d\u662f\u771f\u6b63\u6740\u6b7b\u4e8c\u4f4d\u51f6\u624b\u4e4b\u4eba\u4e0d\u6210\uff1f\u201d \u3000\u3000\u4f46\u89c1\u65b9\u592b\u4eba\u76c8\u76c8\u62dc\u5012\uff0c\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u8d31\u59be\u8fd9\u91cc\u4e0b\u62dc\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u90a3\u4e1c\u8033\u8001\u4eba\u96be\u518d\u88c5\u4f5c\u4e0d\u95fb\uff0c\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u592b\u4eba\u8bf7\u8d77\uff0c\u6709\u8bdd\u597d\u8bf4\u2026\u2026\u201d \u3000\u3000\u65b9\u592b\u4eba\u62ac\u8d77\u5934\u6765\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u8001\u524d\u8f88\u5982\u80af\u7b54\u5141\uff0c\u6551\u52a9\u59be\u592b\u4e4b\u547d\uff0c\u8d31\u5984\u624d\u80fd\u7ad9\u8d77\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u795e\u60c5\u51b7\u8083\u4ed6\u8bf4\u9053\uff1a\u201c\u592b\u4eba\u8d77\u6765\uff0c\u8001\u673d\u5982\u662f\u4e0d\u80af\u7b54\u5e94\u7684\u4e8b\uff0c\u592b\u4eba\u54c0\u6c42\u4ea6\u662f\u65e0\u7528\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u8bdd\u7684\u58f0\u97f3\u867d\u7136\u5f88\u4f4e\u6c89\uff0c\u4f46\u8bed\u6c14\u4e4b\u4e2d\uff0c\u5374\u81ea\u6709\u4e00\u80a1\u9707\u6151\u4eba\u5fc3\u7684\u5a01\u4e25\u3002 \u3000\u3000\u65b9\u592b\u4eba\u7f13\u7f13\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u656c\u671b\u8001\u524d\u8f88\u5927\u53d1\u6148\u60b2\uff0c\u5984\u592b\u2026\u2026\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u63a5\u9053\uff1a\u201c\u53eb\u745c\u513f\u53bb\u5427\uff01\u201d \u3000\u3000\u65b9\u592b\u4eba\u6014\u4e86\u4e00\u5f81\uff0c\u9053\uff1a\u201c\u59be\u592b\u7684\u654c\u624b\uff0c\u6b66\u529f\u5341\u5206\u9ad8\u5f3a\uff0c\u59be\u592b\u867d\u672a\u660e\u8a00\uff0c\u8d31\u59be\u5df2\u4e86\u7136\u4ed6\u6258\u5b64\u4e4b\u610f\uff0c\u745c\u513f\u4e60\u6b66\u65f6\u95f4\u4e0d\u957f\uff0c\u5982\u4f55\u80fd\u662f\u90a3\u4eba\u4e4b\u654c\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u7f13\u7f13\u63a5\u9053\uff1a\u201c\u8981\u4ed6\u53bb\u7f62\uff0c\u5982\u679c\u4f60\u4fe1\u4efb\u8001\u592b\uff0c\u90a3\u5c31\u8981\u4ed6\u53bb\uff0c\u5426\u5219\u90a3\u5c31\u4e0d\u7528\u548c\u8001\u592b\u8c08\u8bdd\u4e86\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u8d31\u59be\u540c\u53bb\u5417\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u4e0d\u7528\u4e86\uff0c\u7531\u8fd9\u4f4d\u8a00\u638c\u95e8\u5e26\u4ed6\u540c\u53bb\u5c31\u662f\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u60f3\u7b54\u8bdd\uff0c\u90a3\u4e1c\u8033\u8001\u4eba\u53c8\u62a2\u5148\u8bf4\u9053\uff1a\u201c\u4e5f\u4e0d\u80fd\u8ba9\u4f60\u767d\u53bb\uff0c\u65e5\u540e\u7531\u745c\u513f\u5e2e\u4f60\u5bfb\u56de\u8a00\u5bb6\u95e8\u6b66\u529f\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u67d4\u58f0\u63a5\u9053\uff1a\u201c\u8a00\u638c\u95e8\uff0c\u542c\u8fd9\u4f4d\u8001\u524d\u8f88\u7684\u8bdd\u5427\uff01\u7b54\u5e94\u9001\u745c\u513f\u4e00\u884c\uff0c\u8d31\u59be\u611f\u6fc0\u4e07\u5206\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u70b9\u70b9\u5934\uff0c\u76ee\u5149\u671b\u7740\u4e1c\u8033\u8001\u4eba\uff0c\u4e00\u62b1\u62f3\uff0c\u9053\uff1a\u201c\u8001\u524d\u8f88\u5927\u540d\u5982\u4f55\u79f0\u547c\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u8981\u95ee\u6211\u59d3\u540d\uff0c\u4e5f\u4e0d\u7528\u5bf9\u4e2d\u5dde\u4e09\u4fa0\u8bf4\u660e\u5185\u60c5\uff0c\u53ea\u8bf4\u65b9\u592b\u4eba\u8981\u4f60\u548c\u745c\u513f\u63f4\u52a9\u4ed6\u4eec\u5c31\u662f\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u4e2d\u5dde\u4e09\u4fa0\u4f55\u7b49\u4eba\u7269\uff0c\u5c82\u80af\u76f8\u4fe1\u6b64\u8a00\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u4f60\u53ea\u8bf4\u8fd9\u4e9b\u5c31\u591f\u4e86\u2026\u2026\u201d\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u8d74\u8fc7\u9b54\u5200\u7530\u8fdc\u4e4b\u7ea6\uff0c\u8981\u4ed6\u4eec\u91cd\u56de\u5609\u5b9a\uff0c\u627e\u90a3\u7a0b\u5b50\u671b\uff0c\u81ea\u4f1a\u89c1\u5230\u65b9\u592b\u4eba\uff0c\u5feb\u4e9b\u53bb\u5427\uff01\u201d \u3000\u3000\u8a00\u51e4\u537f\u8fd8\u5f85\u518d\u95ee\uff0c\u4e1c\u8033\u8001\u4eba\u5df2\u95ed\u4e0a\u53cc\u76ee\uff0c\u53e3\u4e2d\u5374\u51b7\u51b7\u9053\uff1a\u201c\u4e0d\u8981\u518d\u95ee\u4e86\uff0c\u5982\u82e5\u4f60\u4eec\u77e5\u6653\u8001\u592b\u59d3\u540d\uff0c\u7acb\u65f6\u6709\u6740\u8eab\u4e4b\u7978\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u8f6c\u76ee\u671b\u7740\u65b9\u745c\u9053\uff1a\u201c\u4e16\u5144\u4f1a\u9a91\u9a6c\u5417\uff1f\u201d \u3000\u3000\u65b9\u745c\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u665a\u8f88\u4f1a\u9a91\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u4e00\u8dc3\u4e0a\u9a6c\uff0c\u9053\uff1a\u201c\u597d\uff01\u54b1\u4eec\u8d70\u5427\uff01\u201d \u3000\u3000\u8a00\u745c\u5c31\u90a3\u4e09\u4e2a\u5927\u6c49\u9a91\u6765\u5065\u9a6c\u4e2d\uff0c\u9009\u4e86\u4e00\u5339\uff0c\u642c\u978d\u8e0f\u9563\u800c\u4e0a\u3002 \u3000\u3000\u8a00\u51e4\u537f\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u8fde\u9a6c\u80cc\u90fd\u8df3\u4e0d\u4e0a\uff0c\u600e\u80fd\u6325\u5251\u6740\u6b7b\u5f3a\u654c\uff0c\u63f4\u6551\u4e2d\u5dde\u4e09\u4fa0\uff0c\u8fd9\u5b9e\u662f\u4e0d\u53ef\u601d\u8bae\u7684\u4e8b\u3002\u201d \u3000\u3000\u53ea\u89c1\u65b9\u745c\u4e00\u52d2\u7f30\u7ef3\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u8d70\u5427\uff01\u201d\u653e\u9a6c\u5411\u524d\u5954\u53bb\u3002 \u3000\u3000\u65b9\u592b\u4eba\u76ee\u6ce8\u4e24\u4eba\u80cc\u5f71\u6d88\u5931\u4e4b\u540e\u4f4e\u58f0\u5bf9\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u54b1\u4eec\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u56de\u5609\u8d70\u7a0b\u5e84\u4e3b\u90a3\u91cc\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u9053\uff1a\u201c\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u7b49\u4ed6\u4eec\u7684\u6d88\u606f\u3002\u201d\u5e26\u8f6c\u9a6c\u5934\uff0c\u539f\u8def\u800c\u8fd4\u3002 \u3000\u3000\u4e14\u8bf4\u65b9\u745c\u5f53\u5148\u800c\u884c\uff0c\u4e00\u53e3\u6c14\u5954\u884c\u4e86\u5341\u4f59\u91cc\u8fdc\uff0c\u624d\u505c\u4e0b\u9a6c\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8a00\u53d4\u7236\uff0c\u54b1\u4eec\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u52d2\u4f4f\u7f30\u7ef3\uff0c\u9053\uff1a\u201c\u90a3\u8d74\u7ea6\u4e4b\u5730\uff0c\u6211\u4e5f\u4e0d\u77e5\uff0c\u4e0d\u8fc7\uff0c\u4ee4\u5c0a\u7b49\u521a\u521a\u884c\u8fc7\u4e0d\u4e45\uff0c\u54b1\u4eec\u4e00\u8def\u6253\u542c\u4e0d\u96be\u95ee\u5230\uff0c\u8d24\u4f84\u4e0d\u7528\u4e3a\u6b64\u53d1\u6101\u3002\u201d\u8bed\u58f0\u5fae\u5fae\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u90a3\u4f4d\u8001\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u4ed6\u7684\u771f\u6b63\u59d3\u540d\uff0c\u53ea\u77e5\u9053\u4ed6\u662f\u4e1c\u8033\u8001\u4eba\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u4f4e\u58f0\u81ea\u8bed\u9053\uff1a\u201c\u4e1c\u8033\u8001\u4eba\uff0c\u4e1c\u8033\u8001\u4eba\uff0c\u4e0d\u4f3c\u4e2a\u4eba\u7684\u540d\u5b57\u554a\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u5fae\u5fae\u4e00\u8f6c\uff0c\u9053\uff1a\u201c\u4e16\u5144\u7684\u5251\u6cd5\uff0c\u53ef\u662f\u90a3\u4e1c\u8033\u8001\u4eba\u4f20\u6388\u7684\u5417\uff1f\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u90a3\u4e24\u4e2a\u6b79\u5f92\uff0c\u90fd\u662f\u6b7b\u4e8e\u4e16\u5144\u7684\u5251\u4e0b\u5417\uff1f\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u662f\u7684\uff0c\u90fd\u662f\u6b7b\u4e8e\u5c0f\u4f84\u7684\u5251\u4e0b\u3002\u201d \u3000\u3000\u8a00\u98ce\u537f\u9053\uff1a\u201c\u5728\u4e0b\u77a7\u4e16\u4f84\uff0c\u4e0d\u4f3c\u7ec3\u8fc7\u5f88\u591a\u5e74\u6b66\u529f\u7684\u4eba\uff0c\u6055\u6211\u8bf4\u4e00\u53e5\u5192\u6627\u4e4b\u8a00\uff0c\u4e16\u5144\u8fde\u8dc3\u9a6c\u80cc\u7684\u80fd\u529b\u4e5f\u662f\u65e0\u6709\uff0c\u4e0d\u77e5\u4f55\u4ee5\u80fd\u5728\u6781\u77ed\u65f6\u523b\u4e2d\uff0c\u640f\u6740\u4e24\u540d\u76d7\u5f92\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u6211\u53ea\u51fa\u624b\u4e00\u5251\uff0c\u5c31\u6740\u6b7b\u4e86\u4ed6\u4eec\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u53ea\u662f\u62d4\u5251\u4e00\u5468\u5417\uff1f\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u662f\u554a\uff01\u4f3c\u4e4e\u662f\u4ed6\u4eec\u6545\u610f\u5411\u6211\u5251\u4e0a\u78b0\u561b\uff01\u6211\u8fde\u51fb\u51fa\u4e24\u5251\uff0c\u5c31\u6740\u4e86\u4e24\u4eba\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u597d\u6076\u6bd2\u7684\u5251\u6cd5\uff0c\u4f46\u4e0d\u77e5\u90a3\u4e1c\u8033\u8001\u4eba\u4e00\u5171\u4f20\u4f60\u51e0\u5251\uff1f\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u4e09\u62db\u5251\u6cd5\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u55ef\u4e86\u4e00\u58f0\uff0c\u4e0d\u518d\u591a\u95ee\uff0c\u7eb5\u9a6c\u5411\u524d\u5954\u53bb\u3002 \u3000\u3000\u4f46\u4ed6\u7684\u5fc3\u4e2d\uff0c\u5374\u7559\u7740\u65e0\u6570\u7684\u7591\u95ee\uff0c\u4ed6\u60f3\u4e86\u7136\u7684\u81ea\u7136\u662f\u90a3\u4e1c\u8033\u8001\u4eba\u7684\u771f\u6b63\u8eab\u4efd\uff0c\u4ed6\u65e2\u7136\u80af\u4f20\u6388\u65b9\u745c\u7684\u5251\u6cd5\uff0c\u4e3a\u4ec0\u4e48\u53c8\u53ea\u4f20\u4ed6\u4e09\u62db\uff1f \u3000\u3000\u8fd9\u756a\u65b9\u745c\u548c\u81ea\u5df1\u524d\u6765\uff0c\u81ea\u7136\u662f\u90a3\u4e1c\u8033\u8001\u4eba\u7684\u6388\u610f\u4e86\u3002 \u3000\u3000\u4e09\u62db\u5251\u6cd5\uff0c\u4e0d\u7ba1\u5982\u4f55\u7cbe\u5947\uff0c\u4f46\u4e5f\u65e0\u6cd5\u5bf9\u4ed8\u9b54\u5200\u7530\u8fdc\u8fd9\u7b49\u9ad8\u624b\u3002 \u3000\u3000\u4f46\u90a3\u4e1c\u8033\u8001\u4eba\u65e2\u7136\u4f20\u4e86\u4ed6\u7684\u5251\u6cd5\uff0c\u51b3\u4e0d\u81f4\u6545\u610f\u8ba9\u4ed6\u6765\u6b64\u9001\u6b7b\u3002 \u3000\u3000\u90a3\u662f\u8bf4\uff0c\u4ed6\u5fc3\u4e2d\u5f88\u6709\u628a\u63e1\u51ed\u6b64\u4e09\u5251\uff0c\u80fd\u591f\u80dc\u5f97\u9b54\u5200\u7530\u8fdc\u4e86\u3002 \u3000\u3000\u4e00\u8def\u4e0a\u6253\u542c\u4e2d\u5dde\u4e09\u4fa0\u7684\u53bb\u5411\uff0c\u5feb\u9a6c\u8ffd\u8e2a\u3002 \u3000\u3000\u4e00\u5219\uff0c\u4e24\u65b9\u76f8\u9694\u65f6\u95f4\u5e76\u4e0d\u592a\u4e45\uff0c\u4e8c\u5219\u4e2d\u5dde\u4e09\u4fa0\u8fd9\u756a\u8d74\u7ea6\uff0c\u4e5f\u4e0d\u7528\u9690\u853d\u8eab\u4efd\uff0c\u6240\u4ee5\u6253\u542c\u8d77\u4e09\u4eba\u884c\u8e2a\uff0c\u5e76\u975e\u96be\u4e8b\u3002 \u3000\u3000\u8a00\u51e4\u537f\u5e0c\u671b\u5728\u672a\u4f1a\u6664\u9b54\u5200\u7530\u8fdc\u4e4b\u524d\uff0c\u80fd\u591f\u8ffd\u4e0a\u4e2d\u5dde\u4e09\u4fa0\uff0c\u518d\u4f5c\u4e00\u756a\u8ba1\u8bae\uff0c\u6240\u4ee5\uff0c\u4e00\u76f4\u662f\u5feb\u9a6c\u517c\u7a0b\u5954\u884c\uff0c\u6025\u6025\u8ffd\u8d76\uff0c\u5374\u82e6\u4e86\u4e24\u5339\u9a6c\uff0c\u90fd\u8dd1\u5f97\u904d\u4f53\u5927\u6c57\u3002 \u3000\u3000\u76f4\u5230\u5929\u8272\u5165\u591c\u65f6\u5206\uff0c\u4e24\u4eba\u867d\u7136\u8fd8\u53ef\u652f\u6301\uff0c\u4f46\u80ef\u4e0b\u4e4b\u9a6c\uff0c\u5df2\u7136\u65e0\u6cd5\u518d\u652f\u6301\u4e86\uff0c\u53ea\u597d\u627e\u4e86\u4e00\u5904\u5ba2\u6808\u3002 \u3000\u3000\u5929\u8272\u5fae\u660e\uff0c\u7acb\u65f6\u767b\u7a0b\u3002 \u3000\u3000\u5927\u7ea6\u662f\u4e2d\u5dde\u4e09\u4fa0\u6240\u4e58\u4e4b\u9a6c\uff0c\u548c\u4e24\u4eba\u5982\u4e0b\u4e4b\u9a6c\uff0c\u4f53\u80fd\u76f8\u82e5\uff0c\u90fd\u5df2\u7ecf\u53d1\u6325\u5230\u5065\u9a6c\u6781\u5927\u4f53\u529b\uff0c\u56e0\u6b64\uff0c\u5f53\u4e24\u4eba\u518d\u63a2\u95ee\u4e2d\u5dde\u4e09\u4fa0\u884c\u8e2a\u65f6\uff0c\u4ecd\u7136\u548c\u6628\u5929\u4e00\u822c\u7684\u4fdd\u6301\u76f8\u5dee\u7684\u8ddd\u79bb\u3002 \u3000\u3000\u5c31\u8fd9\u822c\uff0c\u4e8c\u4eba\u4e00\u76f4\u65e0\u6cd5\u8ffd\u4e0a\u4e2d\u5dde\u4e09\u4fa0\uff0c\u4e00\u65e5\u6025\u8d76\uff0c\u81f3\u5929\u5c06\u5165\u591c\u65f6\u5206\uff0c\u5230\u4e86\u4e00\u5904\u4e09\u5c94\u8def\u53e3\u6240\u5728\u3002 \u3000\u3000\u8a00\u51e4\u537f\u4e00\u52d2\u9a6c\uff0c\u6536\u4f4f\u5954\u884c\u4e4b\u52bf\uff0c\u6d41\u76ee\u56db\u987e\u3002 \u3000\u3000\u672c\u5730\u662f\u4e00\u5904\u5b98\u9053\uff0c\u884c\u4eba\u751a\u591a\uff0c\u56e0\u5929\u8272\u5df2\u665a\uff0c\u6240\u4ee5\u4e0d\u89c1\u884c\u4eba\u3002 \u3000\u3000\u65b9\u745c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u8fc7\u53bb\u4e0d\u8fc7\u4e00\u4e2a\u65f6\u8fb0\uff0c\u54b1\u4eec\u627e\u627e\u5730\u4e0a\u9a6c\u8e44\u5982\u4f55\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u8fd9\u5012\u4e5f\u662f\u4e2a\u6ca1\u6709\u529e\u6cd5\u4e2d\u7684\u529e\u6cd5\u4e86\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u7ffb\u8eab\u4e0b\u9a6c\uff0c\u4ed4\u7ec6\u67e5\u770b\u4e4b\u4e0b\uff0c\u53ea\u89c1\u4e00\u7247\u8e44\u75d5\uff0c\u6298\u5411\u5de6\u9762\u4e00\u6761\u5c0f\u5f84\u4e4b\u4e0a\u3002 \u3000\u3000\u62ac\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u665a\u971e\u5982\u5929\uff0c\u5de6\u9762\u65b9\u5411\uff0c\u5c71\u5cf0\u9690\u9690\uff0c\u5df2\u662f\u524d\u65e0\u53bb\u8def\u3002\u8a00\u51e4\u537f\u5f88\u60f3\u627e\u4e2a\u4eba\u6765\u95ee\u95ee\uff0c\u6b64\u5730\u662f\u4f55\u6240\u5728\uff0c\u4f46\u6b64\u523b\uff0c\u56db\u5468\u7adf\u662f\u4e00\u4e2a\u4eba\u5f71\u4e5f\u770b\u4e0d\u5230\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u65b9\u745c\u4e5f\u4ece\u9a6c\u4e0a\u8df3\u4e0b\uff0c\u5411\u5730\u4e0a\u67e5\u770b\u3002 \u3000\u3000\u8a00\u51e4\u537f\u8f7b\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e16\u4f84\u77a7\u51fa\u4ec0\u4e48\u6ca1\u6709\uff1f\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u77a7\u51fa\u6765\u4e86\uff0c\u8fd9\u9a6c\u8e44\u6298\u5411\u5de6\u8fb9\u800c\u6765\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u524d\u9762\u548c\u53f3\u9762\u5462\uff1f\u201d \u3000\u3000\u4ed6\u867d\u5df2\u67e5\u8fc7\uff0c\u4e0d\u662f\u9a6c\u8e44\u75d5\u8ff9\uff0c\u4f46\u5fc3\u4e2d\u5374\u4ecd\u662f\u6709\u4e9b\u653e\u5fc3\u4e0d\u4e0b\u3002 \u3000\u3000\u539f\u6765\uff0c\u524d\u9762\u548c\u53f3\u9762\u5927\u9053\uff0c\u5206\u522b\u901a\u81ea\u6210\u90fd\uff0c\u54b8\u9633\uff0c\u53ea\u6709\u5de6\u9762\u8fd9\u6761\u8def\uff0c\u5374\u4f3c\u662f\u4e00\u6761\u6b7b\u8def\uff0c\u4f3c\u662f\u901a\u5411\u4e00\u5ea7\u5c71\u6751\u4e2d\u53bb\u3002 \u3000\u3000\u4f46\u95fb\u65b9\u745c\u5e94\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u77a7\u8fc7\uff0c\u4e24\u6761\u8def\u4e0a\uff0c\u90fd\u4e0d\u89c1\u9a6c\u8e44\u75d5\u8ff9\uff0c\u4ed6\u4eec\u5b9a\u7136\u8d70\u5728\u5de6\u9762\u4e00\u6761\u8def\u4e0a\u4e86\u3002\u201d \u3000\u3000\u5982\u82e5\u90a3\u65b9\u745c\u5e74\u7eaa\u957f\u4e9b\uff0c\u7ecf\u9a8c\u591a\u4e9b\uff0c\u89c2\u5bdf\u8fc7\u5730\u52bf\u5f62\u6001\u4e4b\u540e\uff0c\u51b3\u7136\u4e0d\u6562\u8bf4\u7684\u5982\u6b64\u8f7b\u677e\uff0c\u6b63\u56e0\u4ed6\u5fc3\u65e0\u591a\u8651\uff0c\u80f8\u65e0\u6742\u5ff5\uff0c\u662f\u4ee5\uff0c\u8bf4\u7684\u65a9\u9489\u622a\u94c1\u3002 \u3000\u3000\u8a00\u51e4\u537f\u8f7b\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e16\u4f84\uff0c\u8fd9\u6761\u8def\u524d\u65e0\u53bb\u5904\uff0c\u4ed6\u4eec\u884c\u5411\u4f55\u5904\uff1f\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u4e0d\u7ba1\u4ed6\u4eec\u884c\u5411\u4f55\u5904\uff0c\u4f46\u8d70\u7684\u662f\u8fd9\u6761\u8def\uff0c\u5b9a\u7136\u4e0d\u4f1a\u9517\u4e86\u3002\u201d\u7ffb\u8eab\u8dc3\u4e0a\u9a6c\u80cc\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u8d70\u5427\uff01\u201d\u4e00\u6296\u7f30\u7ef3\uff0c\u5065\u9a6c\u5411\u524d\u5954\u53bb\u3002 \u3000\u3000\u8a00\u51e4\u537f\u82e6\u7b11\u4e00\u4e0b\uff0c\u8df3\u4e0a\u9a6c\u80cc\uff0c\u7d27\u968f\u65b9\u745c\u8eab\u540e\u800c\u884c\u3002 \u3000\u3000\u5fc3\u4e2d\u5374\u4e0d\u505c\u7684\u6253\u8f6c\uff0c\u6697\u6697\u5fd6\u9053\uff1a\u201c\u4e2d\u5dde\u4e09\u4fa0\u8fd9\u756a\u8d74\u7ea6\uff0c\u81ea\u662f\u7528\u4e0d\u7740\u6545\u5e03\u7591\u9635\uff0c\u770b\u6765\u8fd9\u9a6c\u8e44\u8ff9\u5927\u7ea6\u662f\u4e0d\u4f1a\u9519\u4e86\u3002\u201d \u3000\u3000\u5fd6\u601d\u4e4b\u95f4\uff0c\u7a81\u89c1\u65b9\u745c\u4e00\u52d2\u9a6c\u7f30\uff0c\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u4e16\u5144\u600e\u4e48\u4e0d\u8d70\u4e86\uff1f\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u90a3\u6728\u724c\u662f\u4f55\u7528\u610f\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u62ac\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u8ddf\u524d\u4e08\u4f59\u5904\uff0c\u4e00\u4e1b\u9752\u8349\u4e4b\u7533\uff0c\u63d2\u7740\u4e00\u4e2a\u5c0f\u5c0f\u7684\u6807\u724c\uff0c\u6807\u724c\u7528\u5229\u5668\u523b\u4e86\u56db\u4e2a\u5b57\uff1a\u201c\u884c\u4eba\u6b62\u6b65\u3002\u201d\u5f53\u4e0b\u8dc3\u4e0b\u9a6c\u80cc\uff0c\u4f38\u624b\u53d6\u4e0b\u6728\u724c\u3002 \u3000\u3000\u90a3\u6728\u724c\u663e\u662f\u521a\u521a\u524a\u6210\u4e0d\u4e45\uff0c\u75d5\u8ff9\u72b9\u65b0\uff0c\u4e0d\u7981\u559c\u9053\uff1a\u201c\u54b1\u4eec\u8d70\u5bf9\u4e86\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u4e2d\u5dde\u4e09\u4fa0\uff0c\u4e49\u80c6\u4ec1\u5fc3\uff0c\u751f\u6050\u4ed6\u4eba\u8bef\u95ef\u5165\u6b64\uff0c\u906d\u4e86\u8bef\u4f24\uff0c\u6240\u4ee5\u7559\u4e0b\u6b64\u724c\uff0c\u5949\u544a\u8def\u4eba\u3002\u201d \u3000\u3000\u65b9\u745c\u7f13\u7f13\u9053\uff1a\u201c\u6709\u4f55\u8bc1\u660e\uff0c\u80fd\u80af\u5b9a\u8fd9\u6728\u724c\u662f\u6211\u5927\u4f2f\u7b49\u6240\u7559\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u5fae\u5fae\u4e00\u6014\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u53ea\u662f\u8fd9\u4e48\u63a8\u60f3\u3002\u201d \u3000\u3000\u65b9\u745c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u63a8\u65ad\u7684\u4e00\u70b9\u4e0d\u9519\uff0c\u54b1\u4eec\u8d70\u5427\uff01\u201d\u7eb5\u8eab\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u8a00\u51e4\u537f\u7a81\u7136\u4e00\u63d0\u7f30\u7ef3\uff0c\u5feb\u9a6c\u8d85\u8fc7\u65b9\u745c\uff0c\u9053\uff1a\u201c\u4e16\u4f84\uff0c\u5c71\u5165\u9669\u5730\uff0c\u5728\u4e0b\u5e26\u8def\u3002\u201d \u3000\u3000\u65b9\u745c\u5fc3\u4e2d\u660e\u767d\uff0c\u81ea\u5df1\u4e00\u8eab\u672c\u9886\uff0c\u5c31\u662f\u4e1c\u8033\u8001\u4eba\u4f20\u7684\u4e09\u5251\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u518d\u65e0\u522b\u7684\u80fd\u8010\uff0c\u5bf9\u65b9\u5982\u662f\u5c04\u6765\u6697\u5668\uff0c\u81ea\u5df1\u5c31\u65e0\u80fd\u5e94\u4ed8\uff0c\u8a00\u51e4\u537f\u8d70\u524d\u9762\uff0c\u90a3\u662f\u6700\u597d\u4e0d\u8fc7\u4e86\u3002 \u3000\u3000\u8a00\u51e4\u537f\u7eb5\u9a91\u800c\u884c\uff0c\u6df1\u5165\u4e94\u5341\u4e08\u540e\uff0c\u5f62\u52bf\u7a81\u7136\u4e00\u53d8\u3002 \u3000\u3000\u53ea\u89c1\u4e24\u6761\u5c0f\u5f84\uff0c\u5411\u5de6\u53f3\u5206\u53bb\uff0c\u6ee1\u5730\u6742\u8349\u4e71\u77f3\uff0c\u5df2\u6b7b\u53bb\u8def\u3002 \u3000\u3000\u8fd9\u65f6\u843d\u65e5\u4f59\u6656\u5df2\u5c3d\uff0c\u4e94\u8272\u4e00\u7247\u6726\u80e7\u3002 \u3000\u3000\u8a00\u51e4\u537f\u52d2\u9a6c\u6c89\u601d\uff0c\u826f\u4e45\uff0c\u624d\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u4e0b\u9a6c\u6b65\u884c\u5427\uff01\u201d\u5f53\u5148\u8dc3\u4e0b\u9a6c\u6765\u3002 \u3000\u3000\u65b9\u745c\u4e5f\u89c9\u7740\u773c\u524d\u5f62\u52bf\uff0c\u5341\u5206\u5947\u602a\uff0c\u90a3\u4e24\u6761\u5c0f\u5f84\uff0c\u5206\u522b\u901a\u5411\u4e24\u5904\u5c71\u6751\uff0c\u90a3\u7530\u8fdc\u81ea\u7136\u4e0d\u4f1a\u7ea6\u4e2d\u5dde\u4e09\u4fa0\u5728\u90a3\u6751\u5e84\u4e4b\u4e2d\u4f1a\u9762\u4e86\uff0c\u552f\u4e00\u7684\u53ef\u80fd\uff0c\u5c31\u662f\u5728\u4e71\u77f3\u6742\u8349\u6df1\u5904\u3002 \u3000\u3000\u4e0d\u89e3\u7684\u662f\uff0c\u90a3\u7530\u8fdc\u4e3a\u4f55\u8981\u7ea6\u4e2d\u5dde\u4e09\u4fa0\u5728\u8fd9\u6837\u4e00\u5904\u8352\u51c9\u7684\u6240\u5728\u6bd4\u6b66\uff1f \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u4eba\u4e5f\u8dc3\u4e0b\u9a6c\u6765\u3002 \u3000\u3000\u8a00\u51e4\u537f\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4e16\u5144\uff0c\u91cd\u8981\u4e4b\u7269\uff0c\u5e26\u5728\u8eab\u4e0a\uff0c\u4e5f\u8bb8\u54b1\u4eec\u65e0\u6cd5\u518d\u9a91\u8fd9\u4e24\u5339\u9a6c\u4e86\u3002\u201d \u3000\u3000\u65b9\u745c\u53d6\u4e0b\u9a6c\u978d\u7684\u957f\u5251\uff0c\u80cc\u5728\u8eab\u4e0a\uff0c\u628a\u9a6c\u513f\u62f4\u5728\u5927\u6811\u4e0a\u3002 \u3000\u3000\u8a00\u51e4\u537f\u4e5f\u53d6\u4e0b\u5175\u5203\uff0c\u62f4\u597d\u5065\u9a6c\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u8fdb\u53bb\u77a7\u77a7\u5427\uff01\u201d \u3000\u3000\u5fc3\u4e2d\u5374\u6697\u6697\u5fd6\u9053\uff1a\u201c\u5982\u662f\u5f53\u771f\u7684\u627e\u5230\u4e86\u9b54\u5200\u7530\u8fdc\uff0c\u54b1\u4eec\u53ea\u6015\u662f\u5f88\u96be\u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u539f\u6765\uff0c\u4e24\u4eba\u62f4\u9a6c\u4e4b\u6811\uff0c\u90fd\u5f88\u7ec6\u5c0f\uff0c\u53ea\u8981\u4e24\u5339\u5065\u9a6c\u7528\u529b\u4e00\u6323\uff0c\u5c31\u53ef\u4ee5\u6323\u8131\u7f30\u7ef3\u800c\u53bb\u3002 \u3000\u3000\u8a00\u51e4\u537f\u624b\u63e1\u5251\u628a\uff0c\u5f53\u5148\u5e26\u8def\uff0c\u65b9\u745c\u7d27\u968f\u8eab\u540e\u800c\u884c\u3002 \u3000\u3000\u5893\u5730\u8352\u51c9\uff0c\u591c\u8272\u8ff7\u8499\uff0c\u671b\u7740\u90a3\u7d2f\u8d77\u9752\u575f\uff0c\u6c14\u6c1b\u9634\u6c89\u4e2d\uff0c\u5e26\u7740\u6050\u6016\u3002\u4e24\u4eba\u53c8\u6df1\u5165\u6570\u5341\u4e08\uff0c\u53ea\u89c1\u90a3\u6742\u8349\u6108\u6765\u6108\u6df1\uff0c\u5f62\u52bf\u4e5f\u66f4\u89c1\u8352\u51c9\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u54cd\u8d77\u4e86\u4e00\u4e2a\u9634\u6c89\u7684\u58f0\u97f3\uff0c\u559d\u9053\uff1a\u201c\u7ad9\u4f4f\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u62ac\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u4e24\u4e2a\u8eab\u7740\u9ed1\u8272\u52b2\u88c5\u7684\u5927\u6c49\uff0c\u6bcf\u4eba\u624b\u4e2d\u6267\u7740\u4e00\u67c4\u9b3c\u5934\u5200\uff0c\u5e76\u80a9\u800c\u7acb\uff0c\u62e6\u4f4f\u4e86\u53bb\u8def\u3002 \u3000\u3000\u8a00\u51e4\u537f\u8f7b\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6709\u52b3\u4e24\u4f4d\u53bb\u901a\u62a5\u90a3\u9b54\u5200\u7530\u8fdc\u4e00\u58f0\uff0c\u5c31\u8bf4\u5728\u4e0b\u4e24\u4eba\u662f\u4e2d\u5dde\u4e09\u4fa0\u9080\u6765\u7684\u52a9\u62f3\u4eba\u3002\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u5355\u5200\u76f4\u5165\uff0c\u542c\u5f97\u4e24\u4e2a\u6267\u5200\u4eba\uff0c\u4e3a\u4e4b\u4e00\u6014\uff0c\u4e24\u4eba\u76f8\u4e92\u671b\u4e86\u4e00\u773c\uff0c\u624d\u7531\u5de6\u9996\u90a3\u5927\u6c49\u5e94\u9053\uff1a\u201c\u9601\u4e0b\u4f55\u8bb8\u4eba\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u8bf4\u9053\uff1a\u201c\u89c1\u5230\u7530\u8fdc\u4e4b\u65f6\uff0c\u4ed6\u81ea\u4f1a\u8bc6\u5f97\u5728\u4e0b\u3002\u201d \u3000\u3000\u4e24\u4e2a\u4eba\u6253\u91cf\u4e86\u8a00\u51e4\u537f\u548c\u65b9\u745c\u4e00\u773c\uff0c\u770b\u4e24\u4eba\u7684\u5e74\u7eaa\u751a\u5c0f\uff0c\u7d20\u4e0d\u76f8\u8bc6\uff0c\u7adf\u88ab\u8a00\u51e4\u537f\u5927\u8a00\u552c\u4f4f\uff0c\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u4e24\u4f4d\u8bf7\u7a0d\u5019\u4e00\u4e0b\uff0c\u5728\u4e0b\u7acb\u65f6\u4e3a\u4e24\u4f4d\u901a\u62a5\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u4e0d\u8010\u4e45\u5019\uff0c\u4e24\u4f4d\u8981\u901f\u53bb\u901f\u56de\uff01\u201d \u3000\u3000\u90a3\u5de6\u9996\u5927\u6c49\u5e94\u9053\uff1a\u201c\u5728\u4e0b\u5c3d\u5feb\u5c31\u662f\u3002\u201d\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u7b49\u7ea6\u987f\u996d\u5de5\u592b\u4e4b\u4e45\uff0c\u90a3\u5927\u6c49\u6025\u6025\u5954\u4e86\u56de\u6765\uff0c\u9053\uff1a\u201c\u7530\u603b\u74e2\u628a\u5b50\u6709\u8bf7\u4e24\u4f4d\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u4e00\u6325\u624b\uff0c\u9053\uff1a\u201c\u5e26\u8def\u3002\u201d \u3000\u3000\u90a3\u5927\u6c49\u4f3c\u5df2\u4e3a\u8a00\u51e4\u537f\u6c14\u52bf\u9738\u6151\uff0c\u8f6c\u8eab\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u8a00\u51e4\u537f\u624b\u6309\u5251\u628a\uff0c\u968f\u540e\u800c\u884c\u3002 \u3000\u3000\u65b9\u745c\u7d27\u8ffd\u5728\u8a00\u51e4\u537f\u7684\u8eab\u540e\uff0c\u76ee\u5149\u8f6c\u52a8\uff0c\u6253\u91cf\u7740\u56db\u9762\u7684\u666f\u7269\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u6f2b\u5730\u8352\u8349\uff0c\u6108\u6765\u6108\u6df1\uff0c\u90a3\u5e26\u8def\u5927\u6c49\uff0c\u884c\u8d70\u5728\u6df1\u8349\u4e4b\u4e2d\uff0c\u4ec5\u53ef\u9732\u51fa\u4e00\u4e2a\u8111\u888b\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u8fd9\u5730\u65b9\u5f53\u771f\u662f\u9690\u79d8\u5f97\u5f88\u3002\u201d \u3000\u3000\u90a3\u5e26\u8def\u5927\u6c49\u5728\u6df1\u8349\u4e2d\u8f6c\u4e86\u4e00\u9635\uff0c\u5728\u4e00\u5e62\u8305\u820d\u524d\u9762\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u8fd9\u5ea7\u8305\u820d\uff0c\u5efa\u5728\u8352\u8349\u6742\u6797\u4e4b\u4e2d\uff0c\u867d\u7136\u5ba4\u5185\u71c3\u6709\u706f\u5149\uff0c\u4f46\u5982\u4e0d\u5230\u8fd1\u524d\uff0c\u4e5f\u65e0\u6cd5\u77a7\u5f97\u51fa\u6765\u3002 \u3000\u3000\u4e09\u4eba\u884c\u8fd1\u8305\u820d\uff0c\u90a3\u5927\u6c49\u6b63\u5f85\u4e3e\u624b\u53e9\u95e8\uff0c\u6728\u95e8\u5df2\u5440\u7136\u800c\u5f00\u3002 \u3000\u3000\u62ac\u5934\u770b\u53bb\uff0c\u4e24\u4e2a\u8eab\u7740\u9ed1\u8863\u7684\u5927\u6c49\uff0c\u5e76\u80a9\u800c\u7acb\uff0c\u624b\u63e1\u9b3c\u5934\u5200\u3002 \u3000\u3000\u90a3\u5e26\u8def\u5927\u6c49\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u4e24\u4f4d\u5c31\u662f\u4e2d\u5dde\u4e09\u4fa0\u7684\u63f4\u624b\u3002\u201d \u3000\u3000\u4e24\u4e2a\u5b88\u95e8\u5927\u6c49\uff0c\u9f50\u9f50\u62ac\u5934\uff0c\u56db\u9053\u76ee\u5149\uff0c\u4e00\u9f50\u6295\u6ce8\u5728\u8a00\u51e4\u537f\u548c\u65b9\u745c\u7684\u8138\u4e0a\uff0c\u4ed4\u7ec6\u6253\u91cf\u4e86\u4e24\u4eba\u4e00\u9635\uff0c\u95ea\u5411\u65c1\u8fb9\uff0c\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\u8bf7\u8fdb\u5427\uff01\u201d \u3000\u3000\u4e8c\u4eba\u4e00\u633a\u80f8\uff0c\u5927\u6b65\u5411\u524d\u884c\u53bb\u3002\u8fdb\u4e86\u5927\u95e8\uff0c\u662f\u4e00\u6761\u957f\u957f\u7684\u752c\u9053\u3002 \u3000\u3000\u8fd9\u867d\u662f\u4e00\u5e62\u8305\u820d\uff0c\u4f46\u5374\u76d6\u7684\u5341\u5206\u5177\u6709\u89c4\u8303\u3002 \u3000\u3000\u8a00\u51e4\u537f\u5f53\u5148\u800c\u884c\uff0c\u4e5f\u65e0\u4eba\u66ff\u4ed6\u4eec\u5e26\u8def\u3002 \u3000\u3000\u884c\u5b8c\u4e86\u4e00\u6bb5\u752c\u9053\uff0c\u5230\u4e86\u4e00\u5ea7\u5927\u5385\u524d\u9762\u3002\u53ea\u89c1\u5385\u95e8\u6d1e\u5f00\uff0c\u5385\u5185\u706b\u70db\u8f89\u714c\u3002 \u3000\u3000\u65b9\u745c\u76ee\u5149\u8f6c\u52a8\uff0c\u53ea\u89c1\u5385\u4e2d\u6446\u6ee1\u4e86\u4e24\u684c\u9152\u9760\uff0c\u4e00\u684c\u4e0a\u5750\u7684\u4e2d\u5dde\u4e09\u4fa0\uff0c\u4e00\u684c\u4e0a\u5750\u7740\u4e00\u4e2a\u8eab\u7740\u9ed1\u8863\uff0c\u80f8\u524d\u98d8\u5782\u7740\u82b1\u767d\u957f\u9aef\uff0c\u795e\u60c5\u5341\u5206\u8be1\u5f02\u7684\u4eba\u3002 \u3000\u3000\u8a00\u51e4\u537f\u8f7b\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u5927\u6b65\u5411\u5ba4\u4e2d\u884c\u53bb\u3002 \u3000\u3000\u65b9\u745c\u968f\u7740\u8a00\u51e4\u537f\uff0c\u6602\u9996\u800c\u884c\u3002 \u3000\u3000\u4e2d\u5dde\u4e09\u4fa0\u76ee\u5149\u4e00\u8f6c\uff0c\u770b\u6765\u4eba\u7adf\u662f\u8a00\u51e4\u537f\u548c\u65b9\u745c\uff0c\u90fd\u4e0d\u7981\u4e3a\u4e4b\u4e00\u6014\u3002 \u3000\u3000\u8a00\u51e4\u537f\u751f\u6050\u4e2d\u5dde\u4e09\u4fa0\u547c\u53eb\u51fa\u81ea\u5df1\u7684\u540d\u5b57\uff0c\u6025\u6025\u8bf4\u9053\uff1a\u201c\u54ea\u4e00\u4f4d\u662f\u9b54\u5200\u7530\u603b\u74e2\u628a\u5b50\uff1f\u201d \u3000\u3000\u90a3\u72ec\u5750\u4e00\u684c\u795e\u60c5\u8be1\u5f02\u7684\u9ed1\u8863\u4eba\uff0c\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u533a\u533a\u4fbf\u662f\uff0c\u9601\u4e0b\u662f\u4f55\u8bb8\u4eba\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u95fb\u77e5\u7530\u603b\u74e2\u628a\u5b50\uff0c\u7ea6\u6597\u4e2d\u5dde\u4e09\u4fa0\uff0c\u60f3\u6765\u8fd9\u662f\u4e00\u573a\u76db\u4f1a\uff0c\u7279\u5730\u6765\u6b64\u89c2\u8d4f\u4e00\u756a\u3002\u201d \u3000\u3000\u9b54\u5200\u7530\u8fdc\u4e24\u9053\u76ee\u5149\u76ef\u6ce8\u5728\u8a00\u51e4\u537f\u7684\u8eab\u4e0a\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u8bf7\u6559\u5927\u540d\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u5f7c\u6b64\u65e2\u975e\u6500\u4ea4\u800c\u6765\uff0c\u4e0d\u901a\u59d3\u540d\u4e5f\u7f62\u3002\u201d \u3000\u3000\u7530\u8fdc\u9053\uff1a\u201c\u54b1\u4eec\u6ca1\u6709\u89c1\u8fc7\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u6ca1\u6709\u89c1\u8fc7\uff0c\u4f46\u5728\u4e0b\u5374\u95fb\u540d\u5df2\u4e45\u4e86\u3002\u201d \u3000\u3000\u7530\u8fdc\u5f04\u4e0d\u6e05\u8a00\u51e4\u537f\u7684\u6765\u5386\uff0c\u53e3\u4e2d\u8c26\u900a\u9053\uff1a\u201c\u597d\u8bf4\u597d\u8bf4\u2026\u2026\u201d \u3000\u3000\u8a00\u51e4\u537f\u5fc3\u77e5\u6b64\u523b\uff0c\u5df2\u7136\u9a91\u4e0a\u4e86\u864e\u80cc\uff0c\u4e0d\u80fd\u6d41\u9732\u51fa\u4e00\u70b9\u4ff1\u602f\u4e4b\u610f\uff0c\u5f53\u4e0b\u4e00\u633a\u80f8\uff0c\u9053\uff1a \u3000\u3000\u201c\u7530\u603b\u74e2\u628a\u5b50\u7684\u5f85\u5ba2\u4e4b\u9053\uff0c\u672a\u514d\u662f\u592a\u8fc7\u5931\u793c\u4e86\u5427\uff01\u201d \u3000\u3000\u7530\u8fdc\u9053\uff1a\u201c\u8001\u592b\u54ea\u91cc\u5931\u793c\u4e86\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u5e7f\u5927\u7684\u655e\u5385\u4e4b\u4e2d\uff0c\u96be\u9053\u8fde\u5728\u4e0b\u7b49\u4e00\u4e2a\u5ea7\u4f4d\uff0c\u4e5f\u6ca1\u6709\u5417\uff1f\u201d \u3000\u3000\u7530\u8fdc\u53cc\u76ee\u76ef\u5728\u8a00\u98ce\u537f\u8138\u4e0a\u77a7\u7740\uff0c\u770b\u6765\u770b\u53bb\uff0c\u60f3\u4e0d\u51fa\u5728\u4f55\u5904\u89c1\u8fc7\uff0c\u53c8\u89c1\u4ed6\u6c14\u5b9a\u795e\u95f2\uff0c\u770b\u4e0d\u51fa\u4e00\u4e1d\u754f\u602f\u4e4b\u60c5\uff0c\u5fc3\u4e2d\u5bf9\u4ed6\uff0c\u66f4\u662f\u4f30\u8ba1\u4e0d\u900f\uff0c\u5f53\u4e0b\u4e00\u6325\u624b\uff0c\u9053\uff1a\u201c\u66ff\u8fd9\u4f4d\u5c11\u5e74\u4fa0\u58eb\uff0c\u52a0\u4e0a\u4e00\u684c\u9152\u5e2d\u3002\u201d \u3000\u3000\u53ea\u89c1\u62ac\u684c\u5b50\u6367\u9152\u7684\u5927\u6c49\uff0c\u9c7c\u8d2f\u800c\u4e0a\uff0c\u7247\u523b\u95f4\uff0c\u53c8\u6446\u4e0a\u4e00\u5f20\u684c\u5b50\u548c\u4e24\u5f20\u51f3\u5b50\uff0c\u7f8e\u80b4\u7f57\u5217\u70ed\u6c14\u84b8\u817e\u3002 \u3000\u3000\u8a00\u51e4\u537f\u6545\u793a\u5927\u65b9\uff0c\u4e3e\u8d77\u4e00\u676f\u9152\uff0c\u4e00\u996e\u800c\u5c3d\uff0c\u9053\uff1a\u201c\u597d\u9152\u554a\uff01\u597d\u9152\u3002\u201d \u3000\u3000\u9b54\u5200\u7530\u8fdc\u4f3c\u662f\u5df2\u88ab\u8fd9\u7a81\u5982\u5176\u6765\u7684\u4e24\u4e2a\u5c11\u5e74\u4eba\u9707\u4f4f\uff0c\u5e0c\u671b\u80fd\u4ece\u8a00\u8bcd\uff0c\u795e\u60c5\u540c\uff0c\u627e\u51fa\u4ed6\u4eec\u4e00\u4e9b\u5e95\u7ec6\uff0c\u54ea\u77e5\u8a00\u51e4\u537f\u72c2\u653e\u81ea\u5982\uff0c\u7adf\u662f\u77a7\u4e0d\u51fa\u4e00\u70b9\u53ef\u7591\u51f6\u8ff9\u8c61\u3002 \u3000\u3000\u9b54\u5200\u7530\u8fdc\u91cd\u91cd\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u4e0d\u6015\u90a3\u9152\u4e2d\u6709\u6bd2\u5417\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4ee5\u4f60\u9b54\u5200\u7530\u8fdc\u7684\u8eab\u4efd\uff0c\u5927\u7ea6\u8fd8\u4e0d\u81f4\u4e8e\u65bd\u5c55\u8fd9\u7b49\u5351\u4e0b\u7684\u624b\u6cd5\u3002\u201d \u3000\u3000\u7530\u8fdc\u4e00\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u4e0d\u80af\u901a\u62a5\u59d3\u540d\uff0c\u90a3\u4e5f\u7f62\u4e86\uff0c\u4f46\u9601\u4e0b\u7b49\u6765\u6b64\u7528\u5fc3\u4f55\u5728\uff1f\u53ef\u4ee5\u76f4\u8bf4\u51fa\u6765\u4e86\u5427\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u5728\u4e0b\u5df2\u7ecf\u8bf4\u660e\uff0c\u6765\u6b64\u4e4b\u610f\u662f\u60f3\u770b\u770b\u70ed\u95f9\u800c\u5df2\u3002\u201d \u3000\u3000\u9b54\u5200\u7530\u8fdc\u76ee\u5149\u8f6c\u6ce8\u5230\u4e2d\u5dde\u4e09\u4fa0\u8eab\u4e0a\uff0c\u5389\u58f0\u559d\u9053\uff1a\u201c\u8fd9\u4e24\u4f4d\u53ef\u662f\u4f60\u4eec\u7ea6\u6765\u7684\u5e2e\u624b\u5417\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u4e0d\u613f\u56de\u7b54\u3002\u201d \u3000\u3000\u7530\u8fdc\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8001\u592b\u539f\u60f3\u8ba9\u4f60\u4eec\u4e2d\u5dde\u4e09\u4fa0\uff0c\u8054\u624b\u51fa\u654c\uff0c\u591a\u7ed9\u4f60\u4eec\u4e00\u4e2a\u673a\u4f1a\uff0c\u4f46\u65b9\u7b49\u65e2\u7136\u6446\u6765\u4e86\u52a9\u62f3\u4e4b\u4eba\uff0c\u60c5\u52bf\u5c31\u5927\u4e0d\u76f8\u540c\u4e86\u2026\u2026\u201d\u53cc\u624b\u4e3e\u8d77\uff0c\u4e92\u51fb\u4e09\u638c\u3002\u53ea\u542c\u4e00\u9635\u6b65\u5c65\u4e4b\u58f0\uff0c\u5927\u95e8\u4e4b\u5916\uff0c\u7a81\u7136\u6d8c\u8fdb\u6765\u516b\u4e2a\u8eab\u7740\u9ed1\u8863\uff0c\u80cc\u8d1f\u9b3c\u5934\u5200\u7684\u5927\u6c49\u3002 \u3000\u3000\u7530\u8fdc\u76ee\u5149\u8f6c\u52a8\uff0c\u626b\u63a0\u4e86\u8a00\u51e4\u537f\u7b49\u4e00\u773c\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u8001\u592b\u82e6\u5fc3\u8bad\u7ec3\u800c\u6210\u7684\u516b\u9b3c\u5200\u9635\uff0c\u5982\u82e5\u9601\u4e0b\u7b49\u51fa\u624b\uff0c\u8001\u592b\u5c31\u7528\u516b\u9b3c\u5200\u9635\u5bf9\u4ed8\u2026\u2026\u201d\u76ee\u5149\u53c8\u8f6c\u5230\u4e2d\u5dde\u4e09\u4fa0\u8138\u4e0a\uff0c\u63a5\uff1a\u201c\u4e09\u4f4d\u60f3\u597d\u4e86\u6ca1\u6709\uff0c\u8001\u592b\u5df2\u7ecf\u8bf4\u660e\u4e86\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u53ea\u6709\u4e00\u51b3\u751f\u6b7b\u4e86\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u7b49\u60f3\u901a\u4e86\u3002\u201d \u3000\u3000\u9b54\u5200\u7530\u8fdc\u70b9\u5934\u5e94\u9053\uff1a\u201c\u90a3\u5f88\u597d\uff0c\u5982\u82e5\u8bf8\u4f4d\u80af\u4ece\u8001\u592b\u4e4b\u89c1\uff0c\u8001\u592b\u81ea\u5f53\u9075\u5b88\u8bfa\u8a00\u3002\u2026\u2026\u201d \u3000\u3000\u90d1\u5927\u521a\u4ef0\u5929\u6253\u4e2a\u54c8\u54c8\uff0c\u63a5\u9053\uff1a\u201c\u4e2d\u5dde\u4e09\u4fa0\uff0c\u5802\u5802\u52c7\u5b50\uff0c\u5c82\u80af\u4ece\u5c14\u4e3a\u8d3c\uff0c\u5927\u4e08\u592b\u5c82\u4e3a\u5a01\u6b66\u6240\u5c48\uff0c\u90d1\u67d0\u6216\u975e\u9601\u4e0b\u4e4b\u654c\uff0c\u4f46\u5374\u613f\u548c\u9601\u4e0b\u4f5c\u4e00\u6b21\u751f\u6b7b\u4e4b\u640f\u3002\u201d\u5237\u7684\u4e00\u58f0\uff0c\u62bd\u51fa\u7d2b\u91d1\u5200\uff0c\u884c\u5165\u573a\u4e2d\uff0c\u63a5\u9053\uff1a\u201c\u90d1\u67d0\u9886\u6559\u9601\u4e0b\u5200\u6cd5\u3002\u201d \u3000\u3000\u9b54\u5200\u7530\u8fdc\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u90d1\u5927\u521a\uff0c\u8fd9\u662f\u4f60\u4e00\u4eba\u4e4b\u610f\u5462\uff1f\u8fd8\u662f\u4f60\u4eec\u4e09\u5144\u5f1f\u6240\u540c\u610f\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u3001\u77f3\u4fca\u9f50\u58f0\u63a5\u9053\uff1a\u201c\u4e2d\u5dde\u4e09\u4fa0\uff0c\u8a93\u540c\u751f\u6b7b\uff0c\u81ea\u7136\u662f\u6211\u4eec\u4e09\u4eba\u4e4b\u610f\u4e86\u3002\u201d \u3000\u3000\u7530\u8fdc\u51b7\u6f20\u4e00\u7b11\uff0c\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u8001\u592b\u5e0c\u671b\u4e09\u4f4d\u80fd\u591f\u5982\u613f\u4ee5\u507f\uff0c\u57cb\u9aa8\u4e8e\u65af\uff0c\u4e0d\u8fc7\uff0c\u5200\u5251\u65e0\u773c\uff0c\u4e5f\u8bb8\u65e0\u6cd5\u628a\u4e09\u4f4d\u4e00\u5200\u6740\u6b7b\uff0c\u90a3\u65f6\u8bf8\u4f4d\u6c42\u751f\u4e0d\u80fd\uff0c\u6c42\u6b7b\u4e0d\u5f97\uff0c\u60f3\u7b54\u5141\u8001\u592b\u6761\u4ef6\uff0c\u53ea\u6015\u4e3a\u65f6\u5df2\u665a\u4e86\uff01\u201d \u3000\u3000\u4ed6\u8a00\u4e0b\u4e4b\u610f\uff0c\u5e76\u65e0\u6740\u6b7b\u4e09\u4eba\u4e4b\u5fc3\uff0c\u4f46\u5374\u8981\u4e09\u4eba\u91cd\u4f24\u5200\u4e0b\uff0c\u53d7\u5c3d\u6d3b\u7f6a\u3002 \u3000\u3000\u90d1\u5927\u521a\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5343\u53e4\u8270\u96be\u552f\u4e00\u6b7b\uff0c\u90d1\u67d0\u7b49\u65e2\u8fde\u6b7b\u90fd\u4e0d\u6015\uff0c\u4f55\u4ff1\u4e4b\u6709\uff01\u201d \u3000\u3000\u7530\u8fdc\u7f13\u7f13\u4f38\u624b\u4e00\u62db\uff0c\u4e00\u4e2a\u9ed1\u8863\u7ae5\u5b50\uff0c\u5954\u4e86\u8fc7\u6765\uff0c\u53cc\u624b\u6367\u7740\u4e00\u628a\u9798\u4e0a\u6ee1\u9576\u5b9d\u77f3\u7684\u53e4\u5200\uff0c\u606d\u606d\u656c\u656c\uff0c\u9012\u7ed9\u4e86\u7530\u8fdc\u3002 \u3000\u3000\u65b9\u745c\u51e0\u6b21\u8dc3\u8dc3\u6b32\u8bd5\uff0c\u90fd\u88ab\u90a3\u8a00\u51e4\u537f\u6697\u4e2d\u963b\u6b62\u3002 \u3000\u3000\u65b9\u5929\u6210\u7709\u5934\u6df1\u9501\uff0c\u671b\u4e86\u8a00\u51e4\u537f\u548c\u65b9\u745c\u4e00\u773c\uff0c\u6b32\u8a00\u53c8\u6b62\u3002 \u3000\u3000\u7530\u8fdc\u884c\u82e5\u65e0\u4e8b\u4e00\u822c\uff0c\u6162\u6b65\u884c\u5230\u90d1\u5927\u521a\u7684\u8eab\u524d\uff0c\u51b7\u6f20\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u90d1\u5927\u521a\uff0c\u4f60\u4e00\u4eba\u548c\u7530\u67d0\u52a8\u624b\uff0c\u5168\u65e0\u673a\u4f1a\u2026\u2026\u201d\u76ee\u5149\u4e00\u63a0\u65b9\u5929\u6210\u548c\u77f3\u4fca\uff0c\u63a5\u9053\uff1a\u201c\u4e24\u4f4d\u4f55\u4e0d\u4e00\u8d77\u4e0a\u6765\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u548c\u77f3\u4fca\uff0c\u90fd\u77e5\u7530\u8fdc\u5e76\u975e\u5938\u53e3\u4e4b\u8a00\uff0c\u5982\u662f\u4e09\u4eba\u8054\u624b\u62d2\u654c\uff0c\u6216\u53ef\u63a5\u4ed6\u51e0\u5200\uff0c\u4e24\u4eba\u4e00\u822c\u5fc3\u610f\uff0c\u76f8\u4e92\u671b\u4e86\u4e00\u773c\uff0c\u9f50\u9f50\u8d77\u8eab\u884c\u5165\u573a\u4e2d\u3002 \u3000\u3000\u7530\u8fdc\u795e\u60c5\u60a0\u95f2\uff0c\u76f4\u5f85\u65b9\u5929\u6210\u548c\u77f3\u4fca\u7ad9\u597d\u8eab\u5b50\u4e4b\u540e\uff0c\u624d\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u65b9\u4e8c\u4fa0\uff0c\u5728\u4e0b\u5df2\u9063\u4eba\u8bf7\u5c0a\u592b\u4eba\u548c\u4e24\u4f4d\u4ee4\u90ce\u540c\u6765\uff0c\u7b97\u8ba1\u65f6\u523b\uff0c\u5373\u5c06\u8d76\u5230\uff0c\u5728\u4e0b\u4e4b\u610f\uff0c\u8fd8\u60f3\u8ba9\u4f60\u4eec\u592b\u59bb\uff0c\u7236\u5b50\u89c1\u4e0a\u4e00\u9762\uff0c\u4f46\u6b64\u523b\u770b\u6765\uff0c\u5c0a\u592b\u4eba\u5230\u6b64\u4e4b\u65f6\uff0c\u65b9\u4e8c\u4fa0\u5c31\u7b97\u4e0d\u6b7b\uff0c\u4e5f\u5df2\u91cd\u4f24\u96be\u652f\u4e86\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u6697\u6697\u5fd6\u9053\uff1a\u201c\u5947\u602a\u554a\uff01\u8fd9\u8a00\u51e4\u537f\u5982\u82e5\u662f\u968f\u62a4\u6211\u59bb\u540c\u884c\uff0c\u600e\u4f1a\u5e26\u4e86\u745c\u513f\u5230\u6b64\uff0c\u770b\u4e24\u4eba\u5f62\u6001\uff0c\u5168\u65e0\u72fc\u72c8\u4e4b\u72b6\uff0c\u53c8\u4e0d\u548c\u4eba\u6076\u6597\u4e4b\u540e\u5230\u6b64\uff1f\u201d\u53ea\u89c9\u91cd\u91cd\u7591\u95ee\u6cdb\u4e0a\u5fc3\u5934\uff0c\u4f46\u5374\u53c8\u4e0d\u4fbf\u5f53\u573a\u8be2\u95ee\uff0c\u53ea\u597d\u5f3a\u884c\u5fcd\u4e0b\u4e0d\u8a00\u3002 \u3000\u3000\u4f46\u95fb\u7530\u8fdc\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u636e\u5728\u4e0b\u6240\u77e5\uff0c\u65b9\u4e8c\u4fa0\u4f09\u4fea\u60c5\u6df1\uff0c\u5728\u4e0b\u5982\u4ee5\u65b9\u4e8c\u4fa0\u548c\u4e24\u4f4d\u4ee4\u90ce\u4e4b\u547d\uff0c\u80c1\u8feb\u4e8e\u5979\uff0c\u4efb\u5979\u4e09\u8d1e\u4e5d\u70c8\uff0c\u4e5f\u5c06\u5c31\u8303\u3002\u201d \u3000\u3000\u65b9\u745c\u542c\u7530\u8fdc\u8fb1\u53ca\u5927\u5a18\uff0c\u767b\u65f6\u5fc3\u5934\u706b\u8d77\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u8001\u5339\u592b\uff0c\u4f4f\u53e3\uff01\u201d \u3000\u3000\u7530\u8fdc\u56de\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u90a3\u559d\u9a82\u81ea\u5df1\u4e4b\u4eba\uff0c\u7adf\u662f\u4e00\u4e2a\u7ae5\u5b50\uff0c\u4e0d\u7981\u5fae\u5fae\u4e00\u6014\uff0c\u9053\uff1a\u201c\u662f\u4f60\u9a82\u8001\u592b\u5417\uff1f\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u7530\u8fdc\u6c14\u6781\u53cd\u7b11\uff0c\u5475\u5475\u4e00\u9635\u5927\u7b11\u4e4b\u540e\uff0c\u9053\uff1a\u201c\u4f60\u80c6\u5b50\u5f88\u5927\u554a\uff01\u201d \u3000\u3000\u65b9\u745c\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u90a3\u4e1c\u8033\u8001\u4eba\u544a\u8bc9\u6211\u8d8a\u662f\u9547\u9759\u8d8a\u597d\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u4e0d\u80fd\u614c\u5f20\uff0c\u60f3\u6765\u5b9a\u7136\u4e0d\u80fd\u53d1\u6012\u4e86\u3002\u201d\u5f53\u4e0b\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u7ad9\u8d77\u8eab\u5b50\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u4f46\u8981\u9a82\u4f60\uff0c\u800c\u4e14\u8fd8\u8981\u6740\u4e86\u4f60\uff0c\u66ff\u6b66\u6797\u9664\u5bb3\u3002\u201d \u3000\u3000\u62bd\u51fa\u957f\u5251\uff0c\u4e3e\u6b65\u884c\u6765\u3002 \u3000\u3000\u90d1\u5927\u521a\u773c\u770b\u65b9\u745c\u5982\u6b64\u53d1\u72c2\uff0c\u5fc3\u4e2d\u5927\u6025\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u5982\u4f55\u80fd\u6321\u53d7\u7530\u8fdc\u4e00\u51fb\uff0c\u5c82\u4e0d\u662f\u4e0a\u6765\u9001\u6b7b\u5417\uff1f\u201d\u6b63\u60f3\u62a2\u5148\u51fa\u624b\uff0c\u4f46\u89c1\u8a00\u51e4\u537f\u9891\u4ee5\u76ee\u793a\u610f\uff0c\u963b\u62e6\u81ea\u5df1\uff0c\u5fc3\u4e2d\u5927\u662f\u7eb3\u95f7\u3002 \u3000\u3000\u65b9\u745c\u4e00\u9762\u4e3e\u6b65\u800c\u884c\uff0c\u4e00\u9762\u9ed8\u8bf5\u90a3\u4e1c\u8033\u8001\u4eba\u4f20\u6388\u7684\u5251\u6cd5\u771f\u8bc0\uff0c\u6c14\u5b9a\u795e\u95f2\u5730\u8d70\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u516b\u4e2a\u8eab\u8d1f\u9b3c\u5934\u5200\u7684\u5927\u6c49\uff0c\u773c\u770b\u65b9\u745c\u4ed7\u5251\u884c\u5165\u5385\u4e2d\uff0c\u9f50\u9f50\u62bd\u51fa\u9b3c\u5934\u5200\uff0c\u51c6\u5907\u62e6\u4f4f\u5374\u4e3a\u7530\u8fdc\u6447\u624b\u963b\u6b62\u3002 \u3000\u3000\u539f\u6765\u90a3\u7530\u8fdc\u770b\u6765\u770b\u53bb\uff0c\u770b\u4e0d\u51fa\u65b9\u745c\u6709\u4ec0\u4e48\u7279\u522b\u4e4b\u5904\uff0c\u4e0d\u89c9\u95f4\u52a8\u4e86\u597d\u5947\u4e4b\u5ff5\uff0c\u6697\u9053\uff1a \u3000\u3000\u201c\u8fd9\u5a03\u513f\u4e0d\u77e5\u6709\u4f55\u4ed7\u6043\uff0c\u5982\u6b64\u80c6\u5927\uff0c\u5012\u8981\u770b\u4e2a\u660e\u767d\u3002\u201d \u3000\u3000\u65b9\u745c\u9ed8\u8bf5\u5251\u6cd5\uff0c\u5fc3\u795e\u8d2f\u6ce8\uff0c\u4e0d\u89c9\u95f4\u76f4\u5411\u7530\u8fdc\u8eab\u4e0a\u649e\u53bb\u3002 \u3000\u3000\u7530\u8fdc\u9000\u540e\u4e00\u6b65\uff0c\u51b7\u51b7\u559d\u9053\uff1a\u201c\u5c0f\u5a03\u513f\uff01\u201d \u3000\u3000\u65b9\u745c\u9661\u7136\u505c\u6b65\uff0c\u62ac\u5934\u671b\u4e86\u7530\u8fdc\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u7530\u8fdc\u770b\u4ed6\u76f4\u5411\u81ea\u5df1\u649e\u6765\uff0c\u4f3c\u662f\u5168\u4e0d\u628a\u81ea\u5df1\u653e\u5728\u773c\u4e2d\uff0c\u5fc3\u4e2d\u65e2\u662f\u9707\u9a87\uff0c\u53c8\u89c9\u5947\u602a\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u5a03\u513f\u5982\u662f\u5fc3\u65e0\u6240\u6043\uff0c\u600e\u4f1a\u5982\u6b64\u80c6\u5927\uff1f\u201d \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u95ee\u9053\uff1a\u201c\u4f60\u8981\u6740\u8001\u592b\uff0c\u4f55\u4ee5\u4e0d\u80af\u51fa\u624b\uff1f\u201d \u3000\u3000\u65b9\u745c\u957f\u5251\u5e73\u4e3e\u8bf4\u9053\uff1a\u201c\u6211\u5982\u4e00\u5251\u628a\u4f60\u6740\u6b7b\uff0c\u90a3\u5c31\u7f62\u4e86\uff0c\u5982\u662f\u6211\u4e00\u5251\u628a\u4f60\u6740\u4f24\u5462\uff1f\u201d \u3000\u3000\u7530\u8fdc\u5f81\u4e86\u4e00\u5f81\u5fd6\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u8bdd\uff1f\u53eb\u4eba\u542c\u4e0d\u660e\u767d\u3002\u201d\u51b7\u7b11\u4e00\u58f0\uff0c\u5e94\u9053\uff1a\u201c\u4f60\u5982\u771f\u80fd\u6740\u4f24\u8001\u592b\uff0c\u4efb\u51ed\u4f60\u5429\u5490\u5c31\u662f\u3002\u201d \u3000\u3000\u65b9\u745c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u662f\u771f\u7684\uff1f\u201d \u3000\u3000\u7530\u8fdc\u9053\uff1a\u201c\u8001\u592b\u4ece\u6765\u4e0d\u8bf4\u8c0e\u8a00\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u597d\uff01\u54b1\u4eec\u4e00\u8a00\u4e3a\u5b9a\uff0c\u5f53\u7740\u8fd9\u591a\u5c5e\u4e0b\u4e4b\u9762\uff0c\u4f60\u5982\u8bf4\u4e86\u4e0d\u7b97\uff0c\u65e5\u540e\u4f60\u4e5f\u65e0\u989c\u89c1\u4eba\u3002\u201d \u3000\u3000\u7530\u8fdc\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8001\u592b\u51fa\u53e3\u4e4b\u8a00\uff0c\u5c82\u6709\u4e0d\u7b97\u4e4b\u7406\uff01\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u597d\uff01\u90a3\u4f60\u5c31\u5c0f\u5fc3\u4e86\u3002\u201d \u3000\u3000\u7530\u8fdc\u770b\u4ed6\u8138\u4e0a\u8bf4\u8bdd\u795e\u60c5\uff0c\u5145\u6ee1\u7740\u5f3a\u70c8\u7684\u81ea\u4fe1\u4e4b\u8272\uff0c\u5fc3\u4e2d\u7a81\u7136\u751f\u51fa\u4e00\u79cd\u754f\u602f\u4e4b\u611f\uff0c\u6697\u5fd6\u9053\uff1a\u201c\u5f53\u4eca\u6b66\u6797\u4e4b\u4e16\uff0c\u4e0d\u5c11\u6210\u540d\u82f1\u96c4\u95fb\u6211\u4e4b\u540d\uff0c\u5927\u90fd\u9000\u907f\u4e09\u820d\uff0c\u8fd9\u5b69\u5b50\u600e\u7684\u5982\u6b64\u5927\u80c6\uff1f \u3000\u3000\u96be\u9053\u4ed6\u771f\u7684\u8eab\u6000\u7edd\u6280\u4e0d\u6210\uff01\u201d \u3000\u3000\u4ed6\u751f\u6027\u6df1\u8651\u591a\u7591\uff0c\u770b\u5230\u90a3\u65b9\u745c\u82e5\u6709\u6240\u6043\u7684\u6c14\u52bf\uff0c\u5f53\u4e0b\u5927\u58f0\u559d\u9053\uff1a\u201c\u5c0f\u5a03\u513f\uff0c\u8001\u592b\u8fd8\u6709\u51e0\u53e5\u8bdd\u95ee\u4f60\u3002\u201d \u3000\u3000\u65b9\u745c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u597d\uff01\u4f60\u95ee\u5427\uff01\u4e0d\u8fc7\uff0c\u4f60\u8981\u95ee\u7684\u6108\u662f\u7b80\u5355\u6108\u597d\u3002\u201d \u3000\u3000\u7530\u8fdc\u9053\uff1a\u201c\u4f60\u59d3\u4ec0\u4e48\uff1f\u5e08\u627f\u4f55\u4eba\uff1f\u201d \u3000\u3000\u65b9\u745c\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u80fd\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u8fd9\u7b49\u76f4\u622a\u4e86\u5f53\u7684\u7b54\u590d\uff0c\u53ea\u542c\u5f97\u7530\u8fdc\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u548c\u4e2d\u5dde\u4e09\u4fa0\u6709\u4f55\u5173\u7cfb\uff1f\u4e3a\u4f55\u8981\u4e3a\u4ed6\u4eec\u51fa\u9762\uff1f\u201d \u3000\u3000\u65b9\u745c\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u4f60\u95ee\u7684\u5168\u662f\u6211\u4e0d\u613f\u56de\u7b54\u7684\u4e8b\u3002\u201d \u3000\u3000\u7530\u8fdc\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u8001\u592b\u5200\u4e0b\u7d20\u6765\u4e0d\u4f24\u65e0\u540d\u4e4b\u4eba\uff0c\u4f60\u62a5\u4e0a\u59d3\u540d\u6765\uff0c\u54b1\u518d\u52a8\u624b\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u4e00\u5b9a\u8981\u62a5\u59d3\u540d\u5417\uff1f\u201d \u3000\u3000\u7530\u8fdc\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u597d\uff01\u6211\u53eb\u65b9\u745c\u3002\u201d \u3000\u3000\u7530\u8fdc\u5583\u5583\u81ea\u8bed\uff0c\u9053\uff1a\u201c\u65b9\u745c\uff0c\u8fd9\u540d\u5b57\u4ece\u672a\u542c\u4eba\u8bf4\u8fc7\u2026\u2026\u201d \u3000\u3000\u65b9\u745c\u957f\u5251\u4e00\u63a2\uff0c\u523a\u4e86\u8fc7\u53bb\uff0c\u9053\uff1a\u201c\u63a5\u6211\u7b2c\u4e00\u5251\u3002\u201d \u3000\u3000\u7530\u8fdc\u8eab\u5b50\u7eb9\u98ce\u4e0d\u52a8\uff0c\u53cc\u76ee\u5374\u76ef\u6ce8\u5728\u65b9\u745c\u7684\u8138\u4e0a\uff0c\u770b\u4ed6\u5982\u4f55\u53d8\u5316\u3002 \u3000\u3000\u65b9\u745c\u53ea\u8bb0\u5f97\u4e09\u62db\u5251\u6cd5\uff0c\u867d\u7136\uff0c\u90a3\u4e09\u5251\u90fd\u662f\u4e16\u95f4\u7cbe\u4e13\u65e0\u4f26\u7684\u5251\u62db\uff0c\u5404\u8574\u53d8\u5316\uff0c\u4f46\u4ed6\u8fd8\u65e0\u6cd5\u878d\u4f1a\u8d2f\u901a\uff0c\u7075\u6d3b\u8fd0\u7528\uff0c\u8fd9\u4e00\u5251\u523a\u51fa\u4e4b\u540e\uff0c\u90a3\u7530\u8fdc\u5e94\u8be5\u4e3e\u5200\u5c01\u6321\uff0c\u65b9\u745c\u624d\u80fd\u8f6c\u5251\u659c\u64a9\uff0c\u518d\u6c42\u53d8\u5316\u3002 \u3000\u3000\u4ed6\u6599\u60f3\u90a3\u7530\u8fdc\u5fc5\u7136\u4f1a\u62d4\u5200\u5c01\u67b6\uff0c\u5251\u4e0a\u53d8\u5316\uff0c\u5c31\u987a\u7406\u6210\u7ae0\u7684\u65bd\u7528\u51fa\u6765\u3002\u54ea\u77e5\uff0c\u9b54\u5200\u7530\u8fdc\u5b58\u5fc3\u8981\u770b\u4ed6\u5251\u6cd5\u6765\u8def\uff0c\u662f\u6545\u5e76\u672a\u51fa\u5200\u5c01\u67b6\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u65b9\u745c\u6709\u5982\u5931\u53bb\u6307\u5f15\uff0c\u5251\u52bf\u523a\u51fa\uff0c\u4e0d\u77e5\u8be5\u5982\u4f55\u624d\u597d\uff0c\u523a\u51fa\u7684\u5251\u62db\uff0c\u7adf\u7136\u5728\u4e2d\u9014\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u7530\u8fdc\u770b\u4ed6\u5251\u672a\u53ca\u8eab\uff0c\u9661\u7136\u800c\u4f4f\uff0c\u4ea6\u662f\u5927\u611f\u60f6\u60d1\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\u554a\uff01\u201d\u4ed6\u4e00\u751f\u548c\u4eba\u640f\u6597\uff0c\u4e0d\u4e0b\u5343\u767e\u9635\uff0c\u4f46\u5374\u4ece\u672a\u9047\u4e0a\u8fc7\u8fd9\u7b49\u602a\u5f02\u7684\u5251\u52bf\uff0c\u4e0d\u7981\u4e00\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u4f60\u4e3a\u4f55\u4f4f\u624b\u4e0d\u523a\u8fc7\u6765\uff1f\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u4f60\u4e3a\u4f55\u4e0d\u62d4\u5200\u5c01\u6211\u5251\u52bf\uff1f\u201d \u3000\u3000\u7530\u8fdc\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8001\u592b\u4ee5\u5feb\u5200\u9a70\u540d\u6b66\u6797\uff0c\u5982\u662f\u62d4\u5200\u4e00\u51fb\uff0c\u90a3\u8fd8\u6709\u4f60\u7684\u547d\u5728\u3002\u201d \u3000\u3000\u65b9\u745c\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u80af\u62d4\u5200\u5e94\u6218\uff0c\u6211\u8fd9\u4e00\u5251\u51b3\u4e0d\u523a\u4f60\uff1f\u201d \u3000\u3000\u53e3\u4e2d\u5728\u548c\u7530\u8fdc\u5bf9\u8bdd\uff0c\u5fc3\u4e2d\u5374\u5728\u9ed8\u8bf5\u90a3\u4e1c\u8033\u8001\u4eba\u6388\u4e88\u7684\u5251\u8bc0\uff0c\u5fc3\u4e2d\u628a\u5251\u8bc0\u9ed8\u5ff5\u4e86\u4e00\u904d\uff0c\u4ecd\u662f\u4e0d\u77e5\u8fd9\u4e00\u5251\u8be5\u5982\u4f55\u53d8\u5316\u3002 \u3000\u3000\u9b54\u5200\u7530\u8fdc\u4e3a\u4eba\u9634\u6c89\uff0c\u770b\u65b9\u745c\u7ec6\u52bf\u4e0d\u52a8\uff0c\u4e5f\u5c31\u4e0d\u80af\u51fa\u624b\uff0c\u53cc\u76ee\u5374\u76ef\u4f4f\u5728\u65b9\u745c\u624b\u4e0a\u77a7\u7740\uff0c\u51c6\u5907\u77a7\u51fa\u4ed6\u5251\u8def\u53d8\u5316\u3002 \u3000\u3000\u4e00\u65f6\u95f4\uff0c\u4e24\u4eba\u5bf9\u5cd9\u800c\u7acb\u3002 \u3000\u3000\u4e2d\u5dde\u4e09\u4fa0\u4e00\u4fa7\u89c2\u6218\uff0c\u53ea\u77a7\u5f97\u5fc3\u4e2d\u5927\u4e3a\u7126\u6025\u3002 \u3000\u3000\u90d1\u5927\u521a\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4e8c\u5f1f\uff0c\u8fd9\u662f\u4ec0\u4e48\u5251\u6cd5\uff1f\u592a\u6c89\u9759\u4e86\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u82e6\u7b11\u4e00\u4e0b\uff0c\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u5c0f\u5f1f\u4e5f\u4e0d\u592a\u660e\u767d\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u7530\u8fdc\u62d4\u5200\u4e00\u51fb\uff0c\u77f3\u7834\u5929\u60ca\uff0c\u6b66\u6797\u4e2d\u5f88\u5c11\u6709\u4eba\u80fd\u591f\u8eb2\u8fc7\uff0c\u54b1\u4eec\u4e0d\u80fd\u770b\u7740\u8fd9\u5b69\u5b50\u4f24\u5728\u9b54\u5200\u4e4b\u4e0b\uff0c\u81f3\u4f4e\u9650\u5ea6\uff0c\u4e0d\u80fd\u8ba9\u4ed6\u5148\u54b1\u4eec\u6b7b\u4e8e\u5200\u4e0b\uff0c\u4e3a\u5144\u53bb\u66ff\u4ed6\u4e0b\u6765\u3002\u201d \u3000\u3000\u6b63\u5f85\u7eb5\u8eab\u800c\u51fa\uff0c\u7a81\u95fb\u7530\u8fdc\u6012\u58f0\u559d\u9053\uff1a\u201c\u4e0d\u77e5\u6b7b\u6d3b\u7684\u5c0f\u5a03\u513f\uff0c\u8001\u592b\u51e0\u4e4e\u8981\u88ab\u4f60\u8499\u9a97\u8fc7\u4e86\u3002\u201d \u3000\u3000\u559d\u58f0\u4e2d\uff0c\u53f3\u624b\u4e00\u6325\uff0c\u4e00\u9053\u5bd2\u8292\u75be\u65a9\u8fc7\u6765\u3002 \u3000\u3000\u65b9\u745c\u5fc3\u4e2d\u6b63\u5728\u7126\u6025\uff0c\u6697\u9053\uff1a\u201c\u4ed6\u5982\u4e0d\u80af\u6325\u5200\u51fb\u51fa\uff0c\u6211\u5374\u4e0d\u80fd\u6c38\u8fdc\u6267\u5251\u7ad9\u5728\u8fd9\u91cc\u554a\uff01\u201d \u3000\u3000\u662f\u4ee5\uff0c\u5168\u795e\u8d2f\u6ce8\u7530\u8fdc\u53f3\u624b\u4e4b\u4e0a\u3002\u201d \u3000\u3000\u76f4\u5f85\u770b\u4ed6\u53f3\u624b\u6325\u5200\u51fb\u51fa\uff0c\u7acb\u65f6\u633a\u5251\u5411\u4e0a\u64a9\u53bb\u3002 \u3000\u3000\u4ee5\u9b54\u5200\u7530\u8fdc\u5200\u52bf\u4e4b\u5feb\uff0c\u8fd9\u4e00\u51fb\u5982\u662f\u76f4\u5288\u65b9\u745c\uff0c\u65b9\u745c\u7eb5\u7136\u4ee5\u7edd\u4e16\u5251\u62db\uff0c\u56de\u624b\u53cd\u51fb\uff0c\u4e5f\u5c06\u843d\u4e0b\u4e2a\u4e24\u8d25\u4ff1\u4f24\u4e4b\u5c40\u3002 \u3000\u3000\u4f46\u7530\u8fdc\u592a\u591a\u8651\u4e86\uff0c\u8fd9\u4e00\u5200\u76f4\u5288\u5411\u65b9\u745c\u7684\u5251\u4e0a\uff0c\u5e0c\u671b\u4ee5\u81ea\u5df1\u6df1\u539a\u7684\u5185\u529b\uff0c\u51fb\u98de\u65b9\u745c\u624b\u4e2d\u7684\u957f\u5251\uff0c\u518d\u597d\u597d\u620f\u5f04\u4ed6\u4e00\u756a\u3002 \u3000\u3000\u90a3\u77e5\u4ed6\u5feb\u5982\u95ea\u7535\u7684\u4e00\u5200\u5288\u51fa\uff0c\u65b9\u745c\u5251\u52bf\u5df2\u53d8\uff0c\u5200\u5251\u4e00\u9519\u800c\u8fc7\u3002 \u3000\u3000\u7530\u8fdc\u5fc3\u4e2d\u4e00\u9707\uff0c\u4e0d\u6562\u518d\u884c\u653b\u51fb\uff0c\u6025\u6025\u7684\u4e00\u5438\u6c14\uff0c\u9000\u540e\u4e09\u5c3a\u3002 \u3000\u3000\u53ea\u89c9\u524d\u80f8\u4e00\u51c9\uff0c\u7adf\u88ab\u65b9\u745c\u4e00\u5251\u5212\u8fc7\uff0c\u8863\u88c2\u7834\u7efd\uff0c\u9c9c\u8840\u6d41\u51fa\u3002 \u3000\u3000\u4e2d\u5dde\u4e09\u4fa0\uff0c\u773c\u770b\u9b54\u5200\u7530\u8fdc\u6325\u5200\u4e00\u51fb\uff0c\u4e0d\u5fcd\u89c2\u770b\uff0c\u9f50\u9f50\u4e00\u95ed\u773c\u775b\uff0c\u8111\u9645\u95f4\u5374\u6d6e\u73b0\u51fa\u65b9\u745c\u60e8\u6b7b\u5200\u4e0b\u7684\u4e00\u5e45\u60b2\u60e8\u666f\u8c61\uff0c\u8eab\u9996\u5f02\u5904\u6a21\u5c38\u5f53\u573a\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\u4e00\u77ac\u95f4\u7684\u5de5\u592b\uff0c\u5f85\u4e09\u4eba\u7741\u773c\u77a7\u770b\u65f6\uff0c\u4e0d\u7981\u90fd\u4e3a\u4e4b\u4e00\u5446\u3002 \u3000\u3000\u4e07\u745c\u7adf\u5b8c\u597d\u65e0\u6059\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u9b54\u5200\u7530\u8fdc\u5374\u72fc\u72c8\u4e0d\u582a\uff0c\u8863\u886b\u7834\u88c2\uff0c\u9c9c\u8840\u67d3\u80f8\u3002 \u3000\u3000\u8fd9\u4e00\u5251\uff0c\u4f7f\u4e2d\u5dde\u4e09\u4fa0\u60ca\u5947\uff0c\u7530\u8fdc\u5fc3\u5934\u9a87\u7136\u3002 \u3000\u3000\u7530\u8fdc\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u597d\u5251\u6cd5\uff01\u201d \u3000\u3000\u65b9\u745c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u670d\u4e0d\u670d\u6c14\u3002\u201d \u3000\u3000\u7530\u8fdc\u4f24\u7684\u5927\u6982\u5f88\u91cd\uff0c\u80f8\u524d\u9c9c\u8840\u4e0d\u65ad\u7684\u6d8c\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4f46\u4ed6\u4e0d\u6127\u4e3a\u4e00\u4ee3\u9b54\u5934\uff0c\u4ecd\u7136\u7ad9\u5728\u539f\u5730\u4e0d\u52a8\uff0c\u5e76\u672a\u7acb\u523b\u5305\u624e\u4f24\u52bf\u3002 \u3000\u3000\u826f\u4e45\u4e4b\u540e\uff0c\u624d\u8fd8\u5200\u5165\u9798\uff0c\u9053\uff1a\u201c\u8001\u592b\u60f3\u4e0d\u51fa\u9601\u4e0b\u8fd9\u4e00\u5251\u7684\u6765\u8def\u3002\u201d \u3000\u3000\u65b9\u745c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4f60\u5982\u5fc3\u4e2d\u4e0d\u670d\uff0c\u90a3\u5c31\u4e0d\u59a8\u518d\u51fa\u624b\u4e00\u62db\u8bd5\u8bd5\uff1f\u201d \u3000\u3000\u7530\u8fdc\u9053\uff1a\u201c\u8001\u592b\u786e\u5b9e\u8d25\u5f97\u5fc3\u4e2d\u6709\u4e9b\u4e0d\u670d\uff0c\u4f46\u6211\u8bf4\u8fc7\u7684\u8bdd\uff0c\u4e0d\u80fd\u4e0d\u7b97\uff01\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u8fd8\u662f\u4e00\u4e2a\u5f88\u8bb2\u4fe1\u7528\u7684\u4eba\u3002\u201d \u3000\u3000\u7530\u8fdc\u51b7\u7b11\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u6709\u751a\u4e48\u6761\u4ef6\uff0c\u53ef\u4ee5\u8bf4\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u65b9\u745c\u76ee\u5149\u8f6c\u52a8\uff0c\u56db\u987e\u4e86\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u5148\u64a4\u53bb\u5385\u4e2d\u5c5e\u4e0b\u3002\u201d \u3000\u3000\u7530\u8fdc\u4e00\u6325\u624b\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u9000\u4e0b\u53bb\uff01\u201d \u3000\u3000\u5385\u4e2d\u516b\u9b3c\u5e94\u4e86\u4e00\u58f0\uff0c\u5f53\u5148\u9000\u51fa\u5927\u5385\uff0c\u7d27\u63a5\u7740\u6240\u6709\u4e4b\u4eba\uff0c\u9c7c\u8d2f\u800c\u51fa\u3002 \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u6c5f\u6e56\u4e0a\u4f20\u8bf4\u4f60\u4e3a\u4eba\u51f6\u6b8b\u72e0\u6bd2\uff0c\u4f46\u4f60\u8fd8\u6709\u4e00\u6837\u53ef\u53d6\uff0c\u8a00\u800c\u6709\u4fe1\u3002\u201d \u3000\u3000\u7530\u8fdc\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8001\u592b\u603b\u6709\u67e5\u51fa\u4f60\u51fa\u8eab\u7684\u4e00\u5929\uff0c\u8a93\u62a5\u6b64\u4ec7\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u4f60\u80fd\u5426\u62a5\u4ec7\uff0c\u548c\u6211\u51fa\u8eab\u65e0\u5173\uff0c\u53ea\u8981\u80fd\u80dc\u5f97\u6211\u624b\u4e2d\u4e4b\u5251\u2026\u2026\u201d\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u7ea6\u4e2d\u5dde\u4e09\u4fa0\u800c\u6765\uff0c\u8981\u606d\u9001\u4ed6\u4eec\u79bb\u6b64\uff0c\u800c\u4e14\u4ee5\u540e\u4e5f\u4e0d\u80fd\u518d\u627e\u4e2d\u5dde\u4e09\u4fa0\u62a5\u4ec7\uff0c\u6240\u6709\u6069\u6028\uff0c\u4ece\u6b64\u4e00\u7b14\u52fe\u9500\u3002\u201d\u9b54\u5200\u7530\u8fdc\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u96be\u9053\u8001\u592b\u6c38\u8fdc\u4e0d\u80fd\u62a5\u4eca\u65e5\u4e4b\u4ec7\u3002\u201d \u3000\u3000\u65b9\u745c\u957f\u5251\u4e00\u6296\uff0c\u9053\uff1a\u201c\u627e\u6211\uff0c\u4f60\u5982\u80fd\u6740\u4e86\u6211\uff0c\u518d\u627e\u4e2d\u5dde\u4e09\u4fa0\u62a5\u4ec7\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u7530\u8fdc\u9053\uff1a\u201c\u597d\uff01\u5c31\u6b64\u4e00\u8a00\u4e3a\u5b9a\uff0c\u8001\u592b\u6740\u4f60\u4e4b\u540e\uff0c\u518d\u627e\u4e2d\u5dde\u4e09\u4fa0\uff0c\u90a3\u4e5f\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u8001\u5978\u5de8\u733e\uff0c\u8bf4\u4e86\u4e0d\u7b97\uff0c\u5f88\u96be\u4f7f\u4eba\u76f8\u4fe1\u3002\u201d \u3000\u3000\u7530\u8fdc\u9053\uff1a\u201c\u5982\u662f\u8001\u592b\u662f\u8a00\u800c\u65e0\u4fe1\u4e4b\u4eba\uff0c\u6b64\u523b\u4e5f\u4e0d\u4f1a\u8ba4\u8f93\u4e86\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u4f60\u5982\u4e0d\u6015\u6b7b\uff0c\u4e0d\u59a8\u518d\u8bd5\u4e00\u5251\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u7530\u8fdc\u6240\u6709\u7684\u5c5e\u4e0b\uff0c\u90fd\u5df2\u4ece\u5385\u4e2d\u64a4\u8d70\uff0c\u81ea\u5df1\u53c8\u53d7\u4e86\u91cd\u4f24\uff0c\u522b\u8bf4\u65b9\u745c\u7684\u5251\u6cd5\u795e\u5947\u83ab\u6d4b\uff0c\u5c31\u662f\u4e2d\u5dde\u4e09\u4fa0\u51fa\u624b\uff0c\u81ea\u5df1\u4e5f\u96be\u662f\u654c\u624b\uff0c\u53ea\u597d\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u8981\u8001\u592b\u5982\u4f55\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u6211\u8981\u4f60\u7acb\u4e0b\u91cd\u8a93\u3002\u201d \u3000\u3000\u7530\u8fdc\u4e00\u76b1\u7709\uff0c\u9053\uff1a\u201c\u8001\u592b\u4ece\u6765\u6ca1\u6709\u88ab\u4eba\u5f3a\u8feb\u7acb\u8fc7\u91cd\u8a93\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u9601\u4e0b\u5982\u6b64\u60dc\u547d\uff0c\u90a3\u5c31\u7834\u4f8b\u4e00\u6b21\u5427\u3002\u201d \u3000\u3000\u7530\u8fdc\u76ee\u4e2d\u795e\u5149\u66b4\u95ea\uff0c\u663e\u662f\u5fc3\u4e2d\u6012\u6781\uff0c\u4f46\u4ed6\u4ecd\u7136\u5f3a\u81ea\u5fcd\u4e0b\u6012\u706b\uff0c\u9053\uff1a\u201c\u8001\u592b\u5728\u672a\u6740\u2026\u2026\u201d \u3000\u3000\u4ed6\u671b\u7740\u65b9\u745c\u63a5\u9053\uff1a\u201c\u4f60\u53eb\u65b9\u745c\uff1f\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u5df2\u544a\u8bc9\u4f60\u4e86\u3002\u201d \u3000\u3000\u7530\u8fdc\u9053\uff1a\u201c\u8001\u592b\u5728\u672a\u6740\u4f60\u65b9\u745c\u4e4b\u524d\uff0c\u51b3\u4e0d\u627e\u4e2d\u5dde\u4e09\u4fa0\u62a5\u590d\uff0c\u5929\u4eba\u5171\u76d1\uff0c\u7acb\u6b64\u8a93\u8a00\uff0c\u5982\u82e5\u6709\u8fdd\u6b64\u8a93\uff0c\u5929\u8bdb\u5730\u706d\u3002\u201d \u3000\u3000\u65b9\u745c\u8fd8\u5251\u5165\u9798\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4f60\u4f24\u5f97\u4e0d\u8f7b\uff0c\u53ef\u4ee5\u5305\u624e\u4e00\u4e0b\u4f24\u53e3\uff0c\u9001\u6211\u4eec\u79bb\u5f00\u4e86\u3002\u201d \u3000\u3000\u7530\u8fdc\u9053\uff1a\u201c\u533a\u533a\u4e00\u70b9\u5fae\u4f24\uff0c\u8001\u592b\u8fd8\u6491\u5f97\u4f4f\u3002\u201d \u3000\u3000\u65b9\u745c\u671b\u4e86\u4e2d\u5dde\u4e09\u4fa0\u4e00\u773c\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u8d70\u5427\uff01\u201d \u3000\u3000\u4e2d\u5dde\u4e09\u4fa0\u60ca\u5947\u4e0d\u5df2\uff0c\u4f46\u4e09\u4eba\u90fd\u5fcd\u8010\u4e86\u672a\u518d\u591a\u8a00\u3002\u7f13\u6b65\u5411\u5385\u5916\u884c\u53bb\u3002 \u3000\u3000\u65b9\u745c\u671b\u4e86\u7530\u8fdc\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5e0c\u671b\u4f60\u7684\u5c5e\u4e0b\u4e0d\u4f1a\u81ea\u5bfb\u6b7b\u8def\u62e6\u963b\u6211\u7b49\u3002\u201d \u3000\u3000\u7530\u8fdc\u7a81\u7136\u653e\u6b65\u800c\u884c\uff0c\u62a2\u5728\u51e0\u4eba\u524d\u9762\u3002 \u3000\u3000\u4e00\u76f4\u9001\u51e0\u4eba\u51fa\u4e86\u5927\u95e8\uff0c\u7530\u8fdc\u624d\u505c\u4e0b\u811a\u6b65\uff0c\u9053\uff1a\u201c\u8001\u592b\u4e0d\u9001\u4e86\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u9601\u4e0b\u5185\u529f\u867d\u7136\u7cbe\u6df1\uff0c\u4f46\u4e5f\u4e0d\u5b9c\u5931\u8840\u8fc7\u591a\uff0c\u65e9\u4e9b\u6cbb\u7597\u4f24\u52bf\u3002\u201d \u3000\u3000\u7530\u8fdc\u9053\uff1a\u201c\u8001\u592b\u5fc5\u62a5\u6b64\u4ec7\uff0c\u81ea\u4f1a\u73cd\u91cd\uff0c\u4e0d\u52b3\u8d39\u5fc3\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u54b1\u4eec\u540e\u4f1a\u6709\u671f\u3002\u201d\u4e00\u62b1\u62f3\uff0c\u8f6c\u8eab\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u4e2d\u5dde\u4e09\u4fa0\u9c7c\u8d2f\u968f\u5728\u65b9\u745c\u548c\u8a00\u51e4\u537f\u7684\u8eab\u540e\u3002 \u3000\u3000\u7a7f\u8fc7\u90a3\u53ca\u4eba\u6df1\u8349\uff0c\u884c\u5165\u5893\u5730\uff0c\u53ea\u89c1\u4e24\u5339\u5065\u9a6c\uff0c\u4ecd\u505c\u5728\u6811\u4e0b\u3002 \u3000\u3000\u65b9\u5929\u6210\u518d\u4e5f\u5fcd\u4e0d\u4f4f\uff0c\u957f\u957f\u53ee\u4e00\u53e3\u5927\u6c14\uff0c\u9053\uff1a\u201c\u745c\u513f\uff0c\u4f60\u51e0\u65f6\u5b66\u4f1a\u4e86\u8fd9\u7b49\u6839\u6df1\u7684\u5251\u9053\uff0c\u600e\u4e48\u4e3a\u53d4\u7684\u4e00\u70b9\u4e5f\u4e0d\u77e5\u6653\uff1f\u201d \u3000\u3000\u65b9\u745c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u745c\u513f\u53ea\u4f1a\u2026\u2026\u201d \u3000\u3000\u8a00\u51e4\u537f\u7eb5\u8eab\u4e0a\u9a6c\uff0c\u4e00\u6296\u7f30\u7ef3\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u8d70\u5427\uff01\u201d \u3000\u3000\u65b9\u5929\u6210\u671b\u4e86\u90d1\u5927\u521a\u4e00\u773c\uff0c\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u5339\u5065\u9a6c\uff0c\u5927\u54e5\u9a91\u4e0a\u8d76\u8def\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u5c0f\u5144\u4f53\u80fd\u5df2\u590d\uff0c\u8fd8\u662f\u7531\u745c\u513f\u9a91\u5427\uff01\u201d \u3000\u3000\u65b9\u745c\u8fd8\u5f85\u63a8\u8f9e\uff0c\u77f3\u4fca\u5df2\u7136\u62b1\u8d77\u65b9\u745c\uff0c\u653e\u4e0a\u9a6c\u80cc\uff0c\u4e00\u638c\u62cd\u5728\u9a6c\u80cc\u4e0a\u3002\u5065\u9a6c\u957f\u5636\u4e00\u58f0\uff0c\u6025\u8ffd\u8a00\u51e4\u537f\u800c\u53bb\u3002 \u3000\u3000\u90d1\u5927\u521a\u3001\u65b9\u5929\u6210\uff0c\u77f3\u4fca\u7b49\u4e00\u8d77\u653e\u5f00\u5927\u6b65\uff0c\u8ffd\u5728\u4e24\u5339\u80dc\u9a6c\u8eab\u540e\u800c\u884c\u3002 \u3000\u3000\u4e00\u53e3\u6c14\u5954\u884c\u51fa\u516d\u4e03\u91cc\u8def\uff0c\u8a00\u51e4\u537f\u624d\u4e00\u6536\u7f30\uff0c\u505c\u4e0b\u811a\u6b65\uff0c\u9053\uff1a\u201c\u65b9\u4e16\u4f84\uff0c\u73b0\u5728\u53ef\u4ee5\u8bb2\u51fa\u5176\u4e2d\u5185\u60c5\u9690\u79d8\u4e86\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u7b49\u867d\u7136\u672a\u95ee\u90a3\u8a00\u51e4\u537f\u4f55\u6545\u8981\u653e\u9a6c\u5954\u884c\uff0c\u4f46\u51e0\u4eba\u5fc3\u4e2d\u90fd\u660e\u767d\uff0c\u90a3\u8a00\u51e4\u537f\u6015\u65b9\u745c\u6cc4\u6f0f\u51fa\u4e86\u9690\u79d8\u4e4b\u60c5\u3002 \u3000\u3000\u65b9\u745c\u671b\u4e86\u65b9\u5929\u6210\u7b49\u4e00\u773c\uff0c\u7a81\u7136\u8df3\u4e0b\u9a6c\u6765\uff0c\u62dc\u4f0f\u4e8e\u5730\uff0c\u9053\uff1a\u201c\u745c\u513f\u62dc\u89c1\u5927\u4f2f\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u4f38\u624b\u6276\u8d77\u65b9\u745c\uff0c\u9053\uff1a\u201c\u8be5\u5b50\uff0c\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\u3002\u201d\u4ed6\u987f\u4e86\u987f\uff0c\u63a5\u9053\uff1a\u201c\u6162\u6162\u8bf4\u5427\uff01\u201d \u3000\u3000\u65b9\u745c\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u745c\u513f\u53ea\u4f1a\u4e09\u5251\uff0c\u90a3\u9b54\u5200\u7530\u8fdc\u5982\u662f\u77e5\u6653\u5185\u60c5\uff0c\u51b3\u7136\u4e0d\u80af\u8ba4\u8d25\u4e86\uff01\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u4f60\u4e00\u5251\u5c31\u6740\u4f24\u4e86\u7530\u8fdc\uff0c\u4e09\u5251\u4e4b\u591a\uff0c\u90a3\u662f\u8db3\u8db3\u591f\u7528\u4e86\uff0c\u4e0d\u77e5\u4f55\u4eba\u4f20\u6388\u4f60\u5251\u6cd5\uff0c\u5982\u6b64\u5229\u5bb3\u3002\u201d\u8bf4\u5b8c\u8bdd\uff0c\u53cc\u76ee\u5374\u8f6c\u6ce8\u5728\u65b9\u5929\u6210\u7684\u8138\u4e0a\uff0c\u4f3c\u662f\u8981\u7b49\u65b9\u5929\u6210\u7684\u7b54\u590d\u3002 \u3000\u3000\u65b9\u5929\u6210\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u4f20\u4e86\u4f60\u7684\u5251\u6cd5\uff0c\u4ece\u5b9e\u8bf4\u6765\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u600e\u4e48\u4e86\uff1f\u541e\u541e\u5410\u5410\u7684\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u90a3\u6388\u6211\u5251\u6cd5\u4e4b\u4eba\uff0c\u544a\u8bc9\u745c\u513f\uff0c\u4e0d\u8bb8\u544a\u8bc9\u522b\u4eba\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u6b64\u523b\u5df2\u4e8b\u8fc7\u5883\u8fc1\uff0c\u8bf4\u51fa\u65e0\u59a8\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u65b9\u592b\u4eba\u548c\u5728\u4e0b\u90fd\u5df2\u77e5\u6653\uff0c\u4e16\u4f84\u8fd8\u6709\u4ec0\u4e48\u53ef\u4fdd\u4e4b\u5bc6\u5462\uff1f\u201d \u3000\u3000\u65b9\u745c\u60f3\u4e86\u4e00\u60f3\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u8bdd\u5012\u4e5f\u4e0d\u9519\uff0c\u6211\u5982\u4e0d\u8bf4\uff0c\u4ed6\u4eec\u95ee\u4ed6\u4e5f\u662f\u4e00\u6837\u3002\u201d\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u4e1c\u8033\u8001\u524d\u8f88\uff0c\u4f20\u6388\u6211\u7684\u5251\u6cd5\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u745c\u513f\uff0c\u4ed6\u544a\u8bc9\u4f60\u59d3\u540d\u6ca1\u6709\uff1f\u201d \u3000\u3000\u65b9\u745c\u6447\u6447\u5934\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u4ed6\u544a\u8bc9\u6211\u7684\uff0c\u4e5f\u662f\u4e1c\u8033\u8001\u4eba\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4e1c\u8033\uff0c\u4e1c\u8033\uff0c\u8fd9\u4ee3\u8868\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u4e5f\u5583\u5583\u81ea\u8bed\u9053\uff1a\u201c\u4e1c\u8033\uff0c\u4e1c\u8033\u2026\u2026\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u63a5\u9053\uff1a\u201c\u8033\u4e1c\u662f\u9648\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u4e00\u4e0b\u5b50\u8df3\u8d77\u6765\u9053\uff1a\u201c\u83ab\u975e\u662f\u4ed6\uff1f\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u5251\u795e\u9648\u5927\u4fa0\u3002\u201d \u3000\u3000\u53ea\u89c1\u65b9\u5929\u6210\u795e\u60c5\u51dd\u5446\uff0c\u4ef0\u5929\u671b\u7740\u591c\u7a7a\uff0c\u4e00\u8bed\u4e0d\u53d1\u3002 \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4e8c\u5f1f\uff0c\u600e\u4e48\u4e86\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u5982\u68a6\u521d\u9192\uff0c\u53e3\u4e2d\u554a\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5982\u82e5\u662f\u4ed6\uff0c\u90a3\u5c31\uff0c\u90a3\u5c31\u2026\u2026\u201d \u3000\u3000\u90d1\u5927\u521a\u4e00\u76b1\u76fe\u5934\uff0c\u9053\uff1a\u201c\u90a3\u5c31\u600e\u6837\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u5c0f\u5f1f\u521d\u89c1\u4ed6\u65f6\uff0c\u5bf9\u4ed6\u4e5f\u5f88\u4e0d\u5ba2\u6c14\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u9053\u4ec0\u4e48\u4e8b\u5462\uff1f\u539f\u6765\u662f\u8fd9\u70b9\u5c0f\u4e8b\uff0c\u9648\u5927\u4fa0\u662f\u4f55\u7b49\u98ce\u5ea6\u7684\u4eba\u7269\uff0c\u5c82\u80af\u548c\u54b1\u4eec\u4e00\u822c\u89c1\u8bc6\uff1f\u201d \u3000\u3000\u77f3\u4fca\u63a5\u9053\uff1a\u201c\u4ed6\u5982\u8bb0\u6068\u4e8c\u54e5\uff0c\u4e5f\u4e0d\u4f1a\u4f20\u6388\u745c\u513f\u4e09\u5251\uff0c\u8981\u4ed6\u6765\u63a5\u5e94\u54b1\u4eec\u4e86\u2026\u2026\u201d \u3000\u3000\u7a81\u7136\u4e3e\u624b\u4e00\u62cd\u8111\u888b\uff0c\u9053\uff1a\u201c\u554a\uff01\u5c0f\u5f1f\u60f3\u8d77\u4e00\u4ef6\u4e8b\u6765\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u5728\u5609\u5b9a\u7a0b\u5bb6\u5e84\u4e2d\uff0c\u4f3c\u4e4e\u6709\u4e00\u4e2a\u4eba\u9010\u9000\u5f3a\u654c\u6551\u4e86\u5927\u54e5\u2026\u2026\u201d \u3000\u3000\u90d1\u5927\u521a\u63a5\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u77a7\u5230\u90a3\u4eba\u5417\uff1f\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u5f53\u65f6\u5c0f\u5f1f\u6b63\u5728\u5305\u624e\u5927\u54e5\u7684\u4f24\u52bf\uff0c\u672a\u7559\u5fc3\u5230\u90a3\u4eba\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\uff1a\u201c\u9664\u4e86\u4e1c\u8033\u8001\u4eba\u4e4b\u5916\uff0c\u53ea\u6015\u518d\u65e0\u522b\u4eba\u80fd\u591f\u6551\u6211\u4e86\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u8f7b\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u5c0a\u592b\u4eba\u5df2\u91cd\u53c8\u56de\u8f6c\u7a0b\u5bb6\u5e84\u4e2d\uff0c\u7b49\u5019\u8bf8\u4f4d\uff0c\u54b1\u4eec\u65e9\u4e9b\u56de\u53bb\uff0c\u4e5f\u514d\u5f97\u5c0a\u592b\u4eba\u6302\u5ff5\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u4f3c\u662f\u7a81\u7136\u6709\u4e86\u5f88\u6df1\u91cd\u7684\u5fc3\u4e8b\uff0c\u832b\u832b\u7136\u7136\u5730\u5e94\u9053\uff1a\u201c\u8a00\u5144\u8bf4\u7684\u662f\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u867d\u7136\u5df2\u77a7\u51fa\u4e86\u65b9\u5929\u6210\u6709\u4e9b\u53cd\u5e38\uff0c\u4f46\u4ed6\u5374\u672a\u591a\u8ffd\u95ee\u3002 \u3000\u3000\u4e00\u884c\u7fa4\u8c6a\uff0c\u53c8\u5411\u5609\u5b9a\u8d76\u53bb\u3002 \u3000\u3000\u65b9\u5929\u6210\u4e0d\u77e5\u662f\u6709\u5fc3\u5462\uff0c\u8fd8\u662f\u65e0\u610f\uff0c\u6545\u610f\u884c\u5728\u6700\u540e\uff0c\u548c\u51e0\u4eba\u4fdd\u6301\u4e86\u4e09\u4e08\u4ee5\u4e0a\u7684\u8ddd\u79bb\u3002 \u3000\u3000\u65b9\u745c\u773c\u770b\u65b9\u5929\u6210\u843d\u540e\u751a\u591a\uff0c\u5f53\u4e0b\u4e00\u52d2\u9a6c\u7f30\uff0c\u8f6c\u8fc7\u5934\u53bb\uff0c\u884c\u5230\u65b9\u5929\u6210\u8eab\u524d\uff0c\u9053\uff1a\u201c\u5927\u4f2f\uff0c\u53ef\u662f\u6709\u4e9b\u75b2\u7d2f\u5417\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6709\u4e9b\u8eab\u4f53\u4e0d\u9002\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u5c0f\u4f84\u628a\u5750\u9a91\u8ba9\u7ed9\u5927\u6015\uff0c\u597d\u5417\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4e0d\u7528\u4e86\uff0c\u54b1\u4eec\u5408\u4e58\u4e00\u9a91\u5c31\u662f\u3002\u201d \u3000\u3000\u7eb5\u8eab\u8dc3\u4e0a\u9a6c\u80cc\uff0c\u4e00\u6296\u7f30\u7ef3\uff0c\u5065\u9a6c\u53c8\u5411\u524d\u5954\u53bb\u3002\u4e00\u9762\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u745c\u513f\uff0c\u6211\u60f3\u95ee\u4f60\u4e24\u4ef6\u4e8b\uff0c\u4f60\u8981\u636e\u5b9e\u56de\u7b54\u3002\u201d \u3000\u3000\u65b9\u745c\u5947\u9053\uff1a\u201c\u5927\u4f2f\u95ee\u8bdd\uff0c\u745c\u513f\u600e\u6562\u4e0d\u636e\u5b9e\u56de\u7b54\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u4f3c\u662f\u81ea\u77e5\u8bf4\u9519\u4e86\u8bdd\uff0c\u4e0d\u7981\u5c34\u5c2c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u4f4d\u4e1c\u8033\u8001\u4eba\u7a76\u7adf\u662f\u8c01\uff1f\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u4ed6\u6ca1\u6709\u544a\u8bc9\u8fc7\u745c\u513f\u4ed6\u662f\u4ec0\u4e48\u4eba\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4ed6\u662f\u4e0d\u662f\u5251\u795e\u9648\u5927\u4fa0\u5462\uff1f\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u745c\u513f\u4e0d\u77e5\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4f60\u6253\u8d25\u90a3\u9b54\u5200\u7530\u8fdc\u7684\u5251\u62db\uff0c\u53ef\u662f\u4e1c\u8033\u8001\u4eba\u4f20\u6388\u7684\u5417\uff1f\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u662f\u4ed6\u6240\u6388\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u81ea\u8a00\u81ea\u8bed\uff0c\u9053\uff1a\u201c\u5f53\u4eca\u6b66\u6797\u4e4b\u4e16\uff0c\u9664\u4e86\u5251\u795e\u9648\u5927\u4fa0\u4e4b\u5916\uff0c\u8fd8\u6709\u4f55\u4eba\u6709\u90a3\u7b49\u7cbe\u5947\u7684\u5251\u6cd5\u5462\uff1f\u201d \u3000\u3000\u65b9\u745c\u4f3c\u662f\u542c\u4e0d\u61c2\u65b9\u5929\u6210\u7684\u8bdd\u4e2d\u4e4b\u610f\uff0c\u56de\u8fc7\u5934\u53bb\uff0c\u671b\u7740\u65b9\u5929\u6210\uff0c\u8138\u4e0a\u4e00\u7247\u832b\u7136\u4e4b\u8272\u3002 \u3000\u3000\u65b9\u5929\u6210\u4f3c\u662f\u60ca\u89c9\u5230\u81ea\u5df1\u5931\u5e38\u7684\u795e\u6001\uff0c\u6025\u6025\u8bf4\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u90a3\u4e1c\u8033\u8001\u4eba\uff0c\u53ef\u66fe\u95ee\u8fc7\u4f60\u7684\u8eab\u4e16\u5417\uff1f\u201d \u3000\u3000\u65b9\u745c\u70b9\u70b9\u5934\u9053\uff1a\u201c\u95ee\u8fc7\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u8f7b\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u600e\u4e48\u544a\u8bc9\u4ed6\uff1f\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u745c\u513f\u636e\u5b9e\u4ee5\u544a\u201d\u3002 \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u554a\uff01\u4ed6\u542c\u8fc7\u4e4b\u540e\uff0c\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u745c\u4eba\u672c\u662f\u806a\u660e\u5f02\u5e38\uff0c\u5fc3\u4e2d\u5ffd\u7136\u52a8\u4e86\u6000\u7591\uff0c\u6697\u9053\uff1a\u201c\u542c\u4ed6\u53e3\u6c14\uff0c\u4f3c\u662f\u5f88\u6015\u6211\u628a\u5bb6\u4e16\u5185\u60c5\uff0c\u544a\u8bc9\u90a3\u4e1c\u8033\u8001\u4eba\u2026\u2026\u201d\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u90a3\u4e1c\u8033\u8001\u4eba\uff0c\u53ef\u662f\u5251\u795e\u9648\u5927\u4fa0\u5417\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u55ef\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5927\u7ea6\u662f\u4e0d\u4f1a\u9519\u5427\uff01\u9664\u4e86\u5251\u795e\u4e4b\u5916\uff0c\u53c8\u6709\u8c01\u80fd\u591f\u4f20\u4f60\u4e09\u62db\u5251\u6cd5\uff0c\u4f7f\u4f60\u80dc\u4e86\u90a3\u9b54\u5200\u7530\u8fdc\uff1f\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u90a3\u9648\u5927\u4fa0\u662f\u597d\u4eba\u8fd8\u662f\u574f\u4eba\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4ed6\u83b7\u5f97\u6b66\u6797\u540c\u9053\u63a8\u5d07\u4e3a\u5251\u795e\u4e4b\u8a89\uff0c\u81ea\u7136\u4e0d\u662f\u574f\u4eba\u4e86\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u5982\u82e5\u90a3\u4e1c\u8033\u8001\u4eba\u662f\u4e2a\u597d\u4eba\uff0c\u544a\u8bc9\u4ed6\u745c\u513f\u7684\u4e8b\uff0c\u4e5f\u4e0d\u8981\u7d27\u554a\uff01\u201d \u3000\u3000\u5982\u662f\u5f80\u65e5\uff0c\u65b9\u745c\u8fd9\u822c\u9876\u649e\u65b9\u5929\u6210\uff0c\u5fc5\u5c06\u8981\u53d7\u90a3\u65b9\u5929\u6210\u4e00\u987f\u8d23\u9a82\uff0c\u4f46\u6b64\u523b\u65b9\u5929\u6210\u5374\u4e0d\u80fd\u53d1\u4f5c\uff0c\u800c\u4e14\uff0c\u4ed6\u4e5f\u60ca\u89c9\u5230\uff0c\u8c08\u8bba\u7684\u4e8b\uff0c\u4f3c\u662f\u5f15\u8d77\u4e86\u65b9\u745c\u7684\u6000\u7591\uff0c\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5982\u82e5\u662f\u9648\u5927\u4fa0\uff0c\u81ea\u7136\u662f\u4e0d\u8981\u7d27\u4e86\uff0c\u5982\u82e5\u90a3\u4eba\u4e0d\u662f\u5251\u795e\u9648\u5927\u4fa0\uff0c\u90a3\u5c31\u6709\u4e9b\u4e0d\u59a5\u4e86\uff01\u201d \u3000\u3000\u65b9\u745c\u76b1\u4e86\u76b1\u7709\u5934\uff0c\u4e0d\u518d\u591a\u95ee\uff0c\u5fc3\u4e2d\u5374\u662f\u6697\u6697\u5fd6\u9053\uff1a\u6211\u8fd9\u4f4d\u5927\u4f2f\u7236\uff0c\u4eca\u65e5\u4f3c\u662f\u6709\u4e9b\u9b42\u4e0d\u5b88\u820d\uff0c\u90a3\u4eba\u5c31\u7b97\u4e0d\u662f\u9648\u5927\u4fa0\uff0c\u4f46\u4ed6\u5bf9\u54b1\u4eec\u5e76\u65e0\u6076\u610f\uff0c\u544a\u8bc9\u4ed6\uff0c\u90a3\u4e5f\u4e0d\u7b97\u9519\u554a\uff01 \u3000\u3000\u53ea\u89c1\u65b9\u5929\u6210\u4e00\u6296\u9a6c\u7f30\uff0c\u5065\u9a6c\u653e\u817f\u5411\u524d\u5954\u53bb\uff0c\u8ffd\u4e0a\u4e86\u90d1\u5927\u521a\u7b49\u4e00\u884c\u3002 \u3000\u3000\u51e0\u4eba\u4e00\u8def\u8d76\u5954\uff0c\u53c8\u56de\u5609\u5b9a\u3002 \u3000\u3000\u4e00\u8def\u4e0a\uff0c\u65b9\u5929\u6210\u4e00\u76f4\u6c89\u9ed8\u4e0d\u8bed\uff0c\u9664\u4e86\u90d1\u5927\u521a\u7b49\u95ee\u4ed6\u4e4b\u8a00\uff0c\u4ed6\u4e0d\u80fd\u4e0d\u56de\u7b54\u4e4b\u5916\uff0c\u4ece\u4e0d\u591a\u8bf4\u4e00\u53e5\u8bdd\u3002 \u3000\u3000\u8fd9\u65e5\uff0c\u4e2d\u5348\u65f6\u5206\uff0c\u5230\u4e86\u7a0b\u5bb6\u5e84\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u65e9\u5df2\u606d\u8fce\u95e8\u5916\uff0c\u957f\u63d6\u8bf4\u9053\uff1a\u201c\u8ba1\u7b97\u884c\u7a0b\uff0c\u8bf8\u4f4d\u4eca\u5929\u8be5\u5230\u4e86\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u6025\u6025\u95ee\u9053\uff1a\u201c\u90a3\u4f4d\u4e1c\u8033\u8001\u4eba\u5728\u5bb6\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u65b9\u4e8c\u4fa0\u7684\u90a3\u4f4d\u8001\u4ec6\u4eba\u5417\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4e0d\u9519\u4e4b\uff0c\u6b63\u662f\u90a3\u4f4d\u4e1c\u8001\u4e08\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u65e5\u524d\u6765\u6b64\u4e4b\u65f6\uff0c\u5144\u5f1f\u89c1\u8fc7\u90a3\u4f4d\u8001\u4e08\u4e00\u6b21\uff0c\u8fd9\u51e0\u65e5\u4e2d\uff0c\u5144\u5f1f\u5c31\u672a\u518d\u89c1\u8fc7\u2026\u2026\u201d \u3000\u3000\u7a81\u7136\u4e3e\u624b\u4e00\u62db\uff0c\u4e00\u4e2a\u6b66\u5e08\u5954\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u5e84\u4e3b\u6709\u4f55\u5429\u5490\uff1f\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u90a3\u4f4d\u8d76\u8f66\u7684\u4e1c\u8001\u4e08\uff0c\u8fd8\u5728\u5417\uff1f\u201d \u3000\u3000\u90a3\u6b66\u5e08\u5e94\u9053\uff1a\u201c\u90a3\u4f4d\u4e1c\u8033\u8001\u4e08\uff0c\u6628\u65e5\u5348\u591c\uff0c\u9a91\u9a6c\u4ed6\u5f80\uff0c\u8fc4\u4eca\u672a\u56de\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u8138\u8272\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u6d3e\u4eba\u53bb\u627e\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u63a5\u9053\uff1a\u201c\u4e0d\u7528\u627e\u4e86\uff0c\u5728\u4e0b\u53ea\u4e0d\u8fc7\u968f\u53e3\u95ee\u95ee\u800c\u5df2\u3002\u201d \u3000 \u7b2c\u4e03\u56de\u3000\u6674\u5929\u9739\u96f3 \u3000 \u3000\u3000\u7a0b\u5b50\u671b\u5f15\u7740\u8def\uff0c\u884c\u5165\u5385\u4e2d\uff0c\u53ea\u89c1\u65b9\u592b\u4eba\u5e26\u7740\u65b9\u73de\uff0c\u65e9\u5df2\u5728\u5385\u7b49\u5019\u3002 \u3000\u3000\u65b9\u5929\u6210\u6025\u884c\u4e24\u6b65\uff0c\u5954\u5230\u59bb\u5b50\u8eab\u4fa7\uff0c\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u4e1c\u8033\u8001\u4eba\u5462\uff1f\u201d \u3000\u3000\u65b9\u592b\u4eba\u9053\uff1a\u201c\u4ed6\u8d70\u4e86\uff0c\u7559\u4e0b\u4e00\u5c01\u4fe1\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4fe1\u5728\u4f55\u5904\uff1f\u201d \u3000\u3000\u65b9\u592b\u4eba\u63a2\u624b\u5165\u6000\uff0c\u53d6\u51fa\u4e00\u4e2a\u5c01\u7b80\u9053\uff1a\u201c\u8fd9\u5c01\u4fe1\u662f\u7559\u7ed9\u745c\u513f\u7684\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u63a5\u8fc7\u4e66\u4fe1\uff0c\u51dd\u81ea\u671b\u53bb\uff0c\u53ea\u89c1\u4e0a\u9762\u5199\u9053\uff1a\u7559\u4ea4\u65b9\u96ea\u5b9c\u4eb2\u62c6\u3002 \u3000\u3000\u4e0d\u7981\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6697\u9053\uff1a\u201c\u745c\u513f\u628a\u96ea\u5b9c\u4e4b\u540d\uff0c\u90fd\u544a\u8bc9\u4e86\u4ed6\uff0c\u60f3\u6765\uff0c\u5b9a\u7136\u662f\u8bb2\u8fc7\u5f88\u591a\u4e8b\u4e86\uff01\u201d\u5fc3\u6709\u6240\u601d\uff0c\u5446\u5446\u5730\u62ff\u7740\u90a3\u4e66\u4fe1\u51fa\u795e\u3002 \u3000\u3000\u65b9\u5929\u6210\u8fd9\u7b49\u53cd\u5e38\u7684\u4e3e\u52a8\uff0c\u53ea\u77a7\u5f97\u90d1\u5927\u521a\u5927\u4e3a\u5947\u602a\uff0c\u4f46\u4ed6\u5374\u9690\u5fcd\u672a\u95ee\u3002 \u3000\u3000\u65b9\u592b\u4eba\u8f7b\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8fd9\u5c01\u4fe1\u7559\u7ed9\u745c\u513f\u770b\u5427\uff01\u201d \u3000\u3000\u65b9\u5929\u6210\u554a\u4e86\u4e00\u58f0\uff0c\u628a\u624b\u4e2d\u4e66\u7b80\u4ea4\u7ed9\u65b9\u745c\uff0c\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u770b\u770b\u8fd9\u4fe1\u4e0a\u5199\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u745c\u6253\u5f00\u5c01\u7b80\uff0c\u8fc5\u901f\u7684\u77a7\u8fc7\uff0c\u9053\uff1a\u201c\u4fe1\u4e0a\u8981\u745c\u513f\u7acb\u65f6\u52a8\u8eab\uff0c\u8d76\u53bb\u548c\u4ed6\u76f8\u4f1a\uff0c\u5e76\u8981\u745c\u513f\u5411\u5927\u4f2f\u5927\u5a18\u81f4\u610f\uff0c\u591a\u5219\u4e94\u5e74\uff0c\u5c11\u5219\u4e09\u5e74\uff0c\u745c\u513f\u5373\u53ef\u91cd\u548c\u5927\u4f2f\u5927\u5a18\u76f8\u89c1\u4e86\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u53ea\u5199\u4e86\u8fd9\u4e9b\u5417\uff1f\u201d\u3002 \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u662f\u7684\uff0c\u5927\u4f2f\u77a7\u77a7\u5427\uff01\u201d \u3000\u3000\u65b9\u5929\u6210\u4e00\u6325\u624b\uff0c\u9053\uff1a\u201c\u4e0d\u7528\u770b\u4e86\uff0c\u4f60\u8981\u5230\u4f55\u5904\u548c\u4ed6\u76f8\u4f1a\uff1f\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u8fd9\u4fe1\u4e0a\u4e66\u6709\u53bb\u8def\uff0c\u745c\u513f\u53ea\u8981\u6309\u56fe\u7d22\u9aa5\u5c31\u6210\u4e86\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4f60\u51c6\u5907\u51e0\u65f6\u52a8\u8eab\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u4fe1\u4e0a\u8981\u745c\u513f\u62c6\u9605\u8fc7\u4e66\u4fe1\u4e4b\u540e\uff0c\u7acb\u65f6\u52a8\u8eab\uff0c\u4e0d\u77e5\u5927\u4f2f\uff0c\u5927\u5a18\u7684\u610f\u4e0b\u5982\u4f55\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u5403\u8fc7\u996d\u8d70\u5427\uff01\u6211\u9001\u4f60\u4e00\u7a0b\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u63a5\u9053\uff1a\u201c\u9152\u996d\u90fd\u5df2\u5907\u597d\uff0c\u8bf8\u4f4d\u7a0d\u7a0d\u4f11\u606f\u4e00\u4e0b\u5c31\u53ef\u5403\u996d\u4e0b\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u671b\u4e86\u77f3\u4fca\u4e00\u773c\uff0c\u968f\u5728\u7a0b\u5b50\u671b\u8eab\u540e\uff0c\u9000\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u77f3\u4fca\u7d27\u968f\u90d1\u5927\u521a\u8eab\u540e\uff0c\u4e5f\u9000\u51fa\u4e86\u5927\u5385\u3002 \u3000\u3000\u4e24\u4eba\u884c\u8fc7\u4e00\u5ea7\u8de8\u9662\u4e4b\u540e\uff0c\u90d1\u5927\u521a\u7a81\u7136\u56de\u8fc7\u5934\u6765\uff0c\u9053\uff1a\u201c\u4e09\u5f1f\uff0c\u4f60\u77a7\u51fa\u6765\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u4e8c\u54e5\u7684\u795e\u60c5\uff0c\u6709\u4e9b\u4e0d\u5bf9\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5728\u5c0f\u5144\u8bb0\u5fc6\u4e4b\u4e2d\uff0c\u54b1\u4eec\u5144\u5f1f\u5386\u7ecf\u751f\u6b7b\uff0c\u65e0\u6570\u51b3\u6218\u4e4b\u4e2d\uff0c\u90fd\u672a\u77a7\u5230\u4ed6\u8fd9\u7b49\u5931\u5e38\u7684\u795e\u60c5\uff0c\u56e0\u6b64\uff0c\u5c0f\u5144\u5fc3\u4e2d\u5f88\u662f\u6000\u7591\u3002\u201d\xb7\u77f3\u4fca\u4e3e\u624b\u6293\u6293\u8111\u888b\uff0c\u9053\uff1a\u201c\u5c0f\u5f1f\u4e5f\u662f\u89c9\u7740\u5947\u602a\uff0c\u53ea\u662f\u60f3\u4e0d\u901a\u539f\u56e0\u4f55\u5728\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u8fd9\u4e2a\u4e48\uff0c\u8fd9\u4e2a\u2026\u2026\u201d\u957f\u53f9\u4e00\u58f0\uff0c\u6539\u53e3\u8bf4\u9053\uff1a\u201c\u7b49\u4e00\u4f1a\uff0c\u4ed6\u8981\u9001\u745c\u513f\u4e0a\u8def\uff0c\u5c0f\u5144\u60f3\u6697\u4e2d\u8ffd\u8e2a\u2026\u2026\u201d\u77f3\u4fca\u53cc\u76ee\u4e00\u77aa\uff0c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u6ca1\u6709\u4ec0\u4e48\uff0c\u5c0f\u5144\u60f3\u8ddf\u53bb\u77a7\u77a7\u800c\u5df2\u2026\u2026\u201d\u8bed\u58f0\u4e00\u987f\uff0c\u9053\uff1a\u201c\u53bb\u4e4b\u540e\uff0c\u4f60\u8981\u597d\u597d\u5730\u7167\u987e\u4f60\u4e8c\u5ac2\u3002\u201d \u3000\u3000\u77f3\u4fca\u4e00\u6014\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u662f\u4f55\u610f\uff1f\u201d\u5fc3\u4e2d\u867d\u7136\u7591\u7aa6\u91cd\u91cd\uff0c\u4f46\u5374\u5fcd\u4e0b\u672a\u95ee\u3002 \u3000\u3000\u4f46\u95fb\u90d1\u5927\u521a\u63a5\u9053\uff1a\u201c\u5982\u662f\u6211\u548c\u4e8c\u54e5\uff0c\u660e\u5929\u65e9\u6668\u8fd8\u4e0d\u56de\u6765\uff0c\u4f60\u4e5f\u4e0d\u7528\u5728\u8fd9\u91cc\u7b49\u5019\u4e86\uff0c\u628a\u4f60\u4e8c\u5ac2\u9001\u56de\u6545\u5c45\u3002\u201d \u3000\u3000\u77f3\u4fca\u4e00\u76b1\u62f3\u5934\uff0c\u9053\uff1a\u201c\u5927\u54e5\u7684\u8bdd\uff0c\u5c0f\u5f1f\u542c\u4e0d\u660e\u767d\uff0c\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\u5462\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u6ca1\u6709\u4ec0\u4e48\uff0c\u4e5f\u8bb8\u6211\u4eec\u7247\u523b\u4e4b\u540e\uff0c\u5c31\u4e00\u540c\u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u77f3\u4fca\u867d\u7136\u89c9\u51fa\u90a3\u90d1\u5927\u521a\u8bdd\u4e2d\u6709\u8bdd\uff0c\u4f46\u90d1\u5927\u521a\u65e2\u662f\u4e0d\u613f\u8bb2\u51fa\u6765\uff0c\u4ed6\u4e5f\u65e0\u6cd5\u591a\u95ee\uff0c\u53ea\u597d\u70b9\u5934\u5e94\u9053\uff1a\u201c\u597d\u5427\uff01\u5c0f\u5f1f\u7167\u5927\u54e5\u5429\u5490\u5c31\u662f\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u4e00\u4e2a\u5e84\u6c49\u884c\u4e86\u8fdb\u6765\uff0c\u8bf7\u4e24\u4eba\u524d\u53bb\u5403\u996d\u3002 \u3000\u3000\u65b9\u5929\u6210\u4f3c\u662f\u6709\u7740\u5f88\u6df1\u91cd\u7684\u5fc3\u4e8b\uff0c\u624b\u4e0d\u505c\u7b77\uff0c\u5306\u5306\u98df\u7528\u3002 \u3000\u3000\u90d1\u5927\u521a\u4e00\u76f4\u5f88\u7559\u5fc3\u4ed6\uff0c\u770b\u51fa\u4ed6\u5feb\u901f\u8fdb\u98df\u7684\u7528\u5fc3\uff0c\u662f\u60f3\u501f\u6545\u63a9\u9970\u4ed6\u5fc3\u4e2d\u7684\u60f6\u60d1\u4e0d\u5b89\u3002 \u3000\u3000\u4e00\u9910\u996d\u5306\u5306\u98df\u6bd5\u3002 \u3000\u3000\u65b9\u5929\u6210\u7ad9\u8d77\u8eab\u5b50\uff0c\u4e00\u62f1\u624b\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u8981\u9001\u745c\u4f84\u767b\u7a0b\uff0c\u53bb\u53bb\u5c31\u6765\uff0c\u5148\u884c\u544a\u9000\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u65b9\u4e8c\u4fa0\u5c3d\u7ba1\u8bf7\u4fbf\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u56de\u987e\u4e86\u65b9\u592b\u4eba\u4e00\u773c\uff0c\u9053\uff1a\u201c\u90a3\u4e1c\u8033\u8001\u524d\u8f88\u7559\u4e66\u4e4b\u4e0a\uff0c\u65e2\u7136\u8bf4\u660e\u4e86\u8981\u745c\u513f\u7acb\u65f6\u52a8\u8eab\uff0c\u54b1\u4eec\u4e0d\u4fbf\u803d\u8bef\u4ed6\uff0c\u6211\u53bb\u9001\u4ed6\u4e00\u7a0b\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u5c0f\u4f84\u6000\u6709\u56fe\u6848\uff0c\u4e0d\u7528\u6709\u52b3\u5927\u4f2f\u4e86\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u8fd9\u4e00\u522b\uff0c\u4e0d\u77e5\u8981\u591a\u4e45\u624d\u80fd\u89c1\u9762\uff0c\u5927\u4f2f\u9001\u4f60\u4e00\u7a0b\uff0c\u5c82\u4e0d\u5e94\u8be5\uff1f\u201d \u3000\u3000\u65b9\u745c\u7ad9\u8d77\u8eab\u5b50\uff0c\u884c\u5230\u65b9\u592b\u4eba\u7684\u9762\u524d\uff0c\u606d\u606d\u656c\u656c\u62dc\u4e86\u4e09\u62dc\uff0c\u9053\uff1a\u201c\u5927\u5a18\u4fdd\u91cd\uff0c\u745c\u513f\u8fd9\u4e9b\u5e74\u6765\uff0c\u627f\u5927\u5a18\u629a\u517b\u957f\u5927\uff0c\u4eb2\u60c5\u5982\u6d77\uff0c\u745c\u513f\u65e5\u540e\u5982\u6709\u5bf8\u8fdb\uff0c\u5fc5\u4e0d\u5fd8\u5927\u5a18\u517b\u80b2\u4e4b\u6069\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u70ed\u6cea\u76c8\u776b\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u5e0c\u671b\u4f60\u6b64\u53bb\u5b66\u6210\u7edd\u6280\uff0c\u65e5\u540e\u540d\u626c\u5929\u4e0b\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u745c\u513f\u81ea\u4f1a\u5c3d\u6211\u5fc3\u529b\uff0c\u827a\u6210\u4e4b\u66f0\uff0c\u5148\u56de\u6545\u5c45\uff0c\u5411\u5927\u5a18\u8bf7\u5b89\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u9053\uff1a\u201c\u4f60\u5f88\u5b5d\u987a\u3002\u201d \u3000\u3000\u4e3e\u624b\u62ed\u53bb\u773c\u4e2d\u540e\u6c34\uff0c\u8f6c\u5bf9\u65b9\u73de\u9053\uff1a\u201c\u73de\u513f\uff0c\u9001\u4f60\u54e5\u54e5\u4e00\u7a0b\u5427\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4e0d\u7528\u73de\u513f\u53bb\u4e86\u3002\u201d \u3000\u3000\u65b9\u73de\u5df2\u79bb\u5f00\u5e2d\u4f4d\uff0c\u95fb\u8a00\u53ea\u597d\u505c\u4e0b\uff0c\u62b1\u62f3\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u5927\u54e5\u4e00\u8def\u987a\u98ce\u3002\u201d \u3000\u3000\u65b9\u745c\u8fd8\u4e86\u4e00\u793c\uff0c\u9053\uff1a\u201c\u6211\u53bb\u4e86\uff0c\u4f60\u8981\u591a\u5b5d\u656c\u5927\u5a18\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u7275\u7740\u65b9\u745c\u7684\u624b\uff0c\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u54b1\u4eec\u8d70\u5427\uff01\u201d\u5927\u6b65\u800c\u53bb\u3002 \u3000\u3000\u90d1\u5927\u521a\u76ee\u7779\u65b9\u5929\u6210\u80cc\u5f71\u6d88\u5931\uff0c\u4e5f\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u7a0b\u5e84\u4e3b\uff0c\u5728\u4e0b\u4f24\u4f53\u5c1a\u672a\u5168\u590d\uff0c\u60f3\u53bb\u5750\u606f\u4e00\u4e0b\uff0c\u5982\u6761\u6709\u4e8b\uff0c\u627e\u5728\u4e0b\u77f3\u5144\u5f1f\u8c08\u8c08\u4e5f\u662f\u4e00\u6837\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u90d1\u5927\u4fa0\u8bf7\u4fbf\uff0c\u5728\u4e0b\u5429\u5490\u4ed6\u4eec\uff0c\u4e0d\u51c6\u60ca\u6270\u5c31\u662f\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u53c8\u5bf9\u8a00\u51e4\u537f\u4e00\u62f1\u624b\uff0c\u9053\uff1a\u201c\u8a00\u5144\uff0c\u5f80\u8fd4\u5954\u9a70\uff0c\u4e5f\u8bb8\u4f11\u606f\u4e00\u4e0b\u4e86\u3002\u201d\u4e5f\u4e0d\u5f85\u8a00\u51e4\u537f\u7b54\u8bdd\uff0c\u4eba\u5df2\u51fa\u5385\u800c\u53bb\u3002 \u3000\u3000\u4ed6\u4e3a\u4eba\u7cbe\u7ec6\uff0c\u56de\u5230\u81ea\u5df1\u7684\u5367\u5ba4\uff0c\u63a9\u4e0a\u5ba4\u95e8\uff0c\u6362\u4e86\u4e00\u8eab\u8863\u7740\uff0c\u624d\u63a8\u5f00\u540e\u7a97\uff0c\u7ed5\u51fa\u7a0b\u5bb6\u5e84\uff0c\u76f4\u8ffd\u65b9\u5929\u6210\u548c\u65b9\u745c\u800c\u53bb\u3002 \u3000\u3000\u4e24\u4eba\u884c\u4e0d\u591a\u4e45\uff0c\u90d1\u5927\u521a\u7d27\u8d76\u4e00\u9635\uff0c\u5df2\u7136\u77a7\u5230\u4e86\u4e24\u4eba\u80cc\u5f71\u3002 \u3000\u3000\u53ea\u89c1\u4e24\u4eba\u5404\u9a91\u7740\u4e00\u5339\u5e62\u9a6c\uff0c\u5e76\u9a91\u659c\u884c\uff0c\u4f3c\u662f\u8c08\u8bba\u4ec0\u4e48\u3002 \u3000\u3000\u90d1\u5927\u521a\u627e\u5230\u4e00\u4e2a\u519c\u4eba\uff0c\u8ba8\u4e86\u4e00\u9876\u8349\u5e3d\uff0c\u4f4e\u538b\u7709\u9645\uff0c\u63a9\u4f4f\u9762\u76ee\uff0c\u968f\u540e\u800c\u884c\u3002\u4ed6\u6709\u7740\u4e00\u79cd\u4e0d\u7965\u7684\u9884\u611f\uff0c\u89c9\u7740\u65b9\u5929\u6210\u81ea\u5df1\u9001\u65b9\u745c\u540c\u884c\uff0c\u4f3c\u662f\u5b58\u4e0b\u4e86\u4e0d\u826f\u5fc3\u610f\u3002\u540c\u65f6\uff0c\u4e5f\u89c9\u65b9\u5929\u6210\u6709\u7740\u4e00\u4ef6\u672a\u66fe\u5bf9\u81ea\u5df1\u8c08\u8fc7\u7684\u9690\u79d8\u3002 \u3000\u3000\u4e14\u8bf4\u65b9\u5929\u6210\u5e26\u7740\u65b9\u745c\uff0c\u884c\u7ea6\u5341\u51e0\u91cc\u8def\uff0c\u5230\u4e86\u8352\u91ce\u4e4b\u4e2d\uff0c\u7a81\u7136\u4e00\u5e26\u9a6c\u5934\uff0c\u884c\u5165\u9053\u65c1\u4e00\u5ea7\u6742\u6797\u4e4b\u5185\uff0c\u53e3\u4e2d\u5374\u8bf4\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4e3a\u53d4\u4e0d\u8981\u9001\u4f60\u4e86\uff0c\u54b1\u4eec\u53d9\u4f84\u5728\u6b64\u5c0f\u5750\u7247\u523b\uff0c\u5c31\u8981\u5206\u9053\u626c\u9573\u4e86\u3002\u201d \u3000\u3000\u65b9\u745c\u5e94\u4e86\u4e00\u58f0\u5e26\u8f6c\u9a6c\u5934\uff0c\u884c\u5165\u6797\u5185\u3002 \u3000\u3000\u65b9\u5929\u6210\u7ffb\u8eab\u4e0b\u9a6c\uff0c\u65b9\u745c\u4e5f\u968f\u7740\u8dc3\u4e0b\u9a6c\u978d\u3002 \u3000\u3000\u65b9\u5929\u6210\u4f38\u624b\u53bb\u63a5\u65b9\u745c\u624b\u4e2d\u7684\u7f30\u7ef3\uff0c\u53e3\u4e2d\u5374\u7b11\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u90a3\u4e1c\u8033\u8001\u4eba\u4f20\u4e86\u4f60\u4e09\u62db\u5251\u6cd5\u4e4b\u5916\uff0c\u8fd8\u4f20\u4e86\u4f60\u4ec0\u4e48\u6b66\u529f\u3002\u201d \u3000\u3000\u65b9\u745c\u6447\u6447\u5934\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u4ed6\u53ea\u4f20\u745c\u513f\u4e09\u62db\u5251\u6cd5\uff0c\u5509\uff01\u5982\u82e5\u90a3\u9b54\u5200\u7530\u8fdc\u4e0d\u548c\u6211\u6bd4\u5251\uff0c\u5c0f\u4f84\u5c31\u65e0\u6cd5\u5bf9\u4ed8\u4ed6\u4e86\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u6b64\u8bdd\u5f53\u771f\u5417\uff1f\u201d\u7a81\u7136\u4e00\u756a\u53f3\u8155\uff0c\u70b9\u4e86\u65b9\u745c\u7684\u7a74\u9053\u3002 \u3000\u3000\u65b9\u745c\u8fd8\u672a\u6765\u5f97\u53ca\u7b54\u8bdd\uff0c\u7a74\u9053\u5df2\u7136\u88ab\u70b9\uff0c\u554a\u54df\u4e00\u58f0\uff0c\u6454\u5012\u5728\u5730\u4e0a\u3002 \u3000\u3000\u65b9\u5929\u6210\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u6289\u8d77\u4e86\u65b9\u745c\uff0c\u9760\u5728\u4e00\u682a\u5927\u6811\u4e0a\u5750\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u65b9\u745c\u53cc\u76ee\u4e2d\u5c3d\u90fd\u662f\u56f0\u60d1\u7684\u5149\u8292\uff0c\u671b\u7740\u65b9\u5929\u6210\u5446\u5446\u58f0\u795e\u3002 \u3000\u3000\u826f\u4e45\u4e4b\u540e\u624d\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5927\u4f2f\uff0c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u5fc3\u4e2d\u89c9\u7740\u5f88\u5947\u602a\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u662f\u7684\uff0c\u745c\u513f\u60f3\u4e0d\u901a\u5927\u6015\u4e3a\u4f55\u70b9\u6211\u7a74\u9053\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u6211\u4e0d\u4f46\u8981\u70b9\u4f60\u7a74\u9053\uff0c\u800c\u4e14\u8fd8\u8981\u53d6\u4f60\u4e4b\u547d\uff0c\u4f60\u6c38\u8fdc\u65e0\u6cd5\u53bb\u627e\u90a3\u4e1c\u8033\u8001\u4eba\uff0c\u8ddf\u4ed6\u5b66\u4e60\u6b66\u529f\u4e86\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u8138\u4e0a\u63a0\u8fc7\u4e00\u62b9\u51f6\u5149\uff0c\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u539f\u56e0\u5f88\u590d\u6742\uff0c\u4e00\u65f6\u95f4\u4e5f\u65e0\u6cd5\u8ddf\u4f60\u8bf4\u660e\u767d\uff0c\u800c\u4e14\uff0c\u6211\u4e5f\u6ca1\u6709\u5927\u591a\u65f6\u95f4\u3002\u201d\u4ef0\u5929\u6253\u4e2a\u54c8\u54c8\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u54b1\u4eec\u76f8\u5904\u5341\u4f59\u5e74\uff0c\u5176\u95f4\u81ea\u7136\u6709\u4e9b\u60c5\u611f\uff0c\u5927\u4f2f\u81ea\u4f1a\u5584\u4e8e\u5904\u7406\u4f60\u7684\u5c38\u4f53\uff0c\u6211\u8981\u4e3a\u4f60\u5907\u7f6e\u4e00\u53e3\u68fa\u6728\uff0c\u6536\u6b93\u8d77\u4f60\u7684\u5c38\u4f53\uff0c\u57cb\u846c\u8d77\u6765\uff0c\u62b1\u6b49\u7684\u662f\uff0c\u65e0\u6cd5\u66ff\u4f60\u7acb\u7891\u8bf4\u660e\u4f60\u7684\u8eab\u4e16\u4e86\u3002\u201d \u3000\u3000\u65b9\u745c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u6211\u6b7b\u7684\u6108\u662f\u9690\u79d8\uff0c\u5bf9\u4f60\u6765\u8bf4\uff0c\u90a3\u662f\u6108\u597d\u4e86\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u8fd9\u4e9b\u5e74\u6765\uff0c\u4f60\u8bfb\u4e86\u5f88\u591a\u7684\u4e66\uff0c\u679c\u7136\u662f\u806a\u660e\u8fc7\u4eba\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u4e8b\u5230\u6b64\u523b\u5c0f\u4f84\u662f\u6b7b\u5b9a\u4e86\uff0c\u4f46\u6211\u4e0d\u613f\u6b7b\u5f97\u592a\u7cca\u6d82\uff0c\u6211\u5e0c\u671b\u80fd\u77e5\u9053\u4e00\u4ef6\u4e8b\u60c5\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u4f60\u7684\u771f\u6b63\u8eab\u4efd\u662f\u8c01\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u95ee\u7684\u597d\u6781\u4e86\u2026\u2026\u201d \u3000\u3000\u65b9\u745c\u5947\u9053\uff1a\u201c\u600e\u4e48\uff1f\u6211\u95ee\u7684\u4e0d\u5bf9\u5417\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4f60\u95ee\u7684\u5f88\u5947\u602a\uff0c\u5927\u51fa\u4e86\u6211\u610f\u6599\u4e4b\u5916\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u90a3\u8981\u6211\u5982\u4f55\u95ee\u5462\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4f60\u95ee\u6211\u771f\u6b63\u7684\u8eab\u4efd\u662f\u8c01\uff0c\u4f3c\u662f\u6211\u4e0d\u662f\u4f60\u7684\u5927\u6015\u4e86\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u5947\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u5982\u82e5\u4f60\u662f\u6211\u5927\u6015\uff0c\u548c\u6211\u90a3\u7236\u4eb2\u662f\u4e00\u6bcd\u540c\u80de\u7684\u5144\u5f1f\uff0c\u5982\u4f55\u80fd\u5fcd\u4e0b\u5fc3\u5bf9\u6211\u65bd\u4e0b\u6bd2\u624b\uff1f\u4f55\u51b5\uff0c\u6211\u8fd8\u6551\u8fc7\u4f60\u7684\u6027\u547d\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u8f7b\u8f7b\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u5f88\u6709\u9053\u7406\uff0c\u4e0d\u8fc7\uff0c\u6211\u4e0d\u60f3\u8ba9\u4f60\u6b7b\u5f97\u592a\u660e\u767d\u3002\u201d \u3000\u3000\u65b9\u745c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u53cd\u6b63\u6211\u5c31\u8981\u6b7b\u4e86\uff0c\u4e5f\u4e0d\u4f1a\u627e\u4f60\u62a5\u4ec7\uff0c\u4f60\u53c8\u4e3a\u4ec0\u4e48\u4e0d\u8ba9\u6211\u6b7b\u5f97\u660e\u660e\u767d\u767d\u5462\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u5c31\u8981\u6b7b\u4e86\uff0c\u660e\u767d\u771f\u8c61\uff0c\u53c8\u4e8e\u4e8b\u4f55\u8865\uff1f\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u4f60\u4e0d\u80af\u8bf4\u660e\u5185\u60c5\uff0c\u6211\u6b7b\u4e86\u4e5f\u4e0d\u7791\u76ee\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u626c\u8d77\u53f3\u638c\uff0c\u7b54\u975e\u6240\u95ee\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u76f8\u5904\u4e86\u5341\u4f59\u5e74\uff0c\u5f7c\u6b64\u4e4b\u95f4\uff0c\u4e0d\u80fd\u8bf4\u6beb\u65e0\u60c5\u610f\uff0c\u6211\u8ba9\u4f60\u6b7b\u5f97\u4e0d\u53d7\u75db\u82e6\u5c31\u662f\u3002\u201d\u638c\u52bf\u7f13\u843d\uff0c\u51fb\u5411\u65b9\u745c\u7684\u5929\u7075\u8981\u7a74\u3002 \u3000\u3000\u65b9\u745c\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u770b\u6765\uff0c\u4f60\u5b9a\u662f\u5bb3\u6b7b\u6211\u7236\u4eb2\u7684\u51f6\u624b\uff01\u60f3\u4e0d\u5230\uff0c\u6211\u4eec\u7236\u5b50\u4e24\u4ee3\uff0c\u90fd\u6b7b\u5728\u4f60\u8fd9\u4e07\u6076\u51f6\u5f92\u7684\u624b\u4e2d\u3002\u201d \u3000\u3000\u8a00\u7f62\uff0c\u95ed\u4e0a\u53cc\u76ee\u7b49\u6b7b\u3002 \u3000\u3000\u65b9\u5929\u6210\u7f13\u7f13\u5411\u4e0b\u62cd\u843d\u7684\u638c\u52bf\uff0c\u7a81\u7136\u6536\u4f4f\uff0c\u9053\uff1a\u201c\u4ee5\u4f60\u7684\u5e74\u9f84\uff0c\u5b9e\u4e0d\u5f97\u4e0d\u53eb\u4eba\u4f69\u670d\u4f60\u65ad\u4e8b\u4e4b\u80fd\u3002\u201d \u3000\u3000\u65b9\u745c\u9661\u7136\u7741\u5f00\u53cc\u76ee\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6709\u4e00\u4e8b\uff0c\u53ea\u6015\u5927\u6015\u5fd8\u8bb0\u4e86\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u5bb6\u6bcd\u8fd8\u6d3b\u5728\u4e16\u4e0a\uff0c\u6709\u4e00\u5929\uff0c\u5979\u4f1a\u67e5\u51fa\u5185\u60c5\uff0c\u627e\u4f60\u62a5\u4ec7\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u7b11\u9053\uff1a\u201c\u5979\u5982\u6709\u8fd9\u4efd\u80fd\u8010\uff0c\u60f3\u5230\u6211\u662f\u5bb3\u6b7b\u5979\u4e08\u592b\u7684\u51f6\u624b\uff0c\u65e9\u5c31\u627e\u6211\u4e86\uff0c\u8fd8\u80fd\u7b49\u5230\u73b0\u5728\uff1f\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u8fd8\u6709\u4e1c\u8033\u8001\u4eba\uff0c\u51b3\u7136\u4e0d\u4f1a\u9976\u4f60\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u6740\u4f60\u4e4b\u540e\uff0c\u6b7b\u65e0\u5bf9\u8bc1\uff0c\u4e1c\u8033\u8001\u4eba\u867d\u7136\u6709\u7edd\u4e16\u6b66\u529f\uff0c\u4e5f\u627e\u4e0d\u5230\u6211\u7684\u5934\u4e0a\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u5927\u5a18\u4e0d\u662f\u574f\u4eba\uff0c\u5979\u4e5f\u4f1a\u4e0d\u9f7f\u4f60\u7684\u6240\u4e3a\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u8fd9\u4e9b\u4e8b\uff0c\u6211\u81ea\u4f1a\u5904\u7406\u5e94\u4ed8\uff0c\u7528\u4e0d\u5230\u4f60\u8d39\u5fc3\u4e86\u3002\u201d \u3000\u3000\u65b9\u745c\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u4f60\u5982\u4f55\u535a\u5f97\u4fa0\u540d\uff0c\u4e5f\u4e0d\u77e5\u4e2d\u5dde\u4e09\u4fa0\u4e2d\uff0c\u5982\u4f55\u80fd\u591f\u5bb9\u5f97\u4f60\u8fd9\u7b49\u9634\u9669\u6076\u6bd2\u7684\u4eba\u7269\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u53cc\u76ee\u4e2d\u7684\u51f6\u5149\u4e00\u95ea\uff0c\u9053\uff1a\u201c\u591f\u4e86\u3002\u201d\u638c\u52bf\u4e00\u6c89\uff0c\u76f4\u5411\u65b9\u745c\u5929\u7075\u8981\u7a74\u4e4b\u4e0a\u51fb\u53bb\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u5bd2\u5149\u4e00\u95ea\uff0c\u659c\u91cc\u98de\u6765\uff0c\u76f4\u5288\u5411\u65b9\u5929\u6210\u7684\u5de6\u81c2\u3002 \u3000\u3000\u65b9\u5929\u6210\u5982\u82e5\u4e0d\u53ca\u65f6\u907f\u5f00\uff0c\u56fa\u7136\u4e00\u638c\u53ef\u4ee5\u628a\u65b9\u745c\u5288\u6b7b\uff0c\u4f46\u4ed6\u4e00\u6761\u53f3\u81c2\uff0c\u4ea6\u5c06\u4f24\u4e8e\u5bd2\u5149\u4e4b\u4e0b\u3002 \u3000\u3000\u4e00\u79cd\u81ea\u4fdd\u7684\u672c\u80fd\uff0c\u4f7f\u65b9\u5929\u6210\u7a81\u7136\u6536\u56de\u4e86\u53f3\u81c2\u3002 \u3000\u3000\u8f6c\u76ee\u770b\u53bb\uff0c\u53ea\u89c1\u6765\u4eba\u6b63\u662f\u90d1\u5927\u521a\uff0c\u4e0d\u7981\u4e3a\u4e4b\u4e00\u5446\u3002 \u3000\u3000\u90d1\u5927\u521a\u671b\u7740\u65b9\u5929\u6210\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5927\u54e5\u90fd\u542c\u5230\u4e86\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u542c\u5230\u4e86\u4e00\u90e8\u5206\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u671b\u671b\u65b9\u745c\uff0c\u9053\uff1a\u201c\u5927\u54e5\u53ca\u65f6\u8d76\u6765\uff0c\u770b\u6765\u8fd9\u5b69\u5b50\u4e0d\u8be5\u6b7b\u4e86\uff0c\u6211\u53bb\u89e3\u4e86\u4ed6\u7684\u7a74\u9053\uff0c\u54b1\u4eec\u518d\u8c08\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f4f\u624b\u3002\u201d\u540c\u65f6\uff0c\u5288\u51fa\u4e00\u5200\uff0c\u62e6\u4f4f\u4e86\u65b9\u5929\u6210\u7684\u53bb\u8def\u3002 \u3000\u3000\u65b9\u5929\u6210\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5927\u54e5\u963b\u6b62\u5c0f\u5f1f\uff0c\u662f\u4f55\u7528\u610f\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u6211\u6015\u4f60\u6740\u4e86\u4ed6\uff01\u201d\u53f3\u624b\u6267\u5200\uff0c\u5927\u6b65\u884c\u5411\u65b9\u745c\u3002 \u3000\u3000\u65b9\u5929\u6210\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u5927\u54e5\u5bf9\u5c0f\u5f1f\u7684\u8bef\u4f1a\u5f88\u6df1\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u4e0d\u7406\u65b9\u5929\u6210\uff0c\u5374\u4f38\u624b\u62cd\u6d3b\u4e86\u65b9\u745c\u8eab\u4e0a\u7684\u7a74\u9053\u3002 \u3000\u3000\u65b9\u5929\u6210\u53cc\u76ee\u4e2d\u795e\u5149\u8fde\u95ea\uff0c\u4e00\u76f4\u5728\u65b9\u745c\u8eab\u4e0a\u6253\u8f6c\u3002 \u3000\u3000\u90a3\u662f\u4e00\u79cd\u5fc5\u6b32\u6740\u4e4b\u800c\u7518\u5fc3\u7684\u76ee\u5149\uff0c\u5145\u6ee1\u7740\u6076\u6bd2\u7684\u6740\u673a\u3002 \u3000\u3000\u90d1\u5927\u521a\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4e0d\u8981\u4f2f\uff0c\u6709\u90d1\u4f2f\u4f2f\u66ff\u4f60\u4f5c\u4e3b\uff0c\u4f60\u5927\u4f2f\u4e3a\u4ec0\u4e48\u8981\u6740\u4f60\uff0c\u636e\u5b9e\u544a\u8bc9\u6211\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u539f\u60f3\u90a3\u90d1\u5927\u521a\u542c\u53bb\u5927\u90e8\u9690\u79d8\uff0c\u542c\u4ed6\u5982\u6b64\u76f8\u95ee\uff0c\u4e4b\u77e5\u4ed6\u786e\u672a\u542c\u5230\u5185\u60c5\uff0c\u5f53\u4e0b\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4e8b\u60c5\u662f\u2026\u2026\u201d \u3000\u3000\u90d1\u5927\u521a\u6447\u624b\u8bf4\u9053\uff1a\u201c\u4e0d\u7528\u4f60\u8bf4\uff0c\u6211\u8981\u95ee\u745c\u513f\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u8f7b\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u73e0\u513f\uff0c\u544a\u8bc9\u4f60\u90d1\u4f2f\u4f2f\uff0c\u5927\u4f2f\u5e76\u65e0\u771f\u7684\u6740\u4f60\u4e4b\u610f\u3002\u201d \u3000\u3000\u65b9\u745c\u5c0f\u8138\u4e0a\u4e00\u7247\u4e25\u8083\uff0c\u671b\u4e86\u65b9\u5929\u6210\u4e00\u773c\uff0c\u53c8\u671b\u671b\u90d1\u5927\u521a\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u90d1\u4f2f\u4f2f\uff0c\u5c0f\u4f84\u60f3\u95ee\u4e00\u4e8b\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u597d\uff01\u4f60\u8bf4\u5427\uff01\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u90d1\u4f2f\u4f2f\u548c\u6211\u5927\u4f2f\uff0c\u76f8\u8bc6\u597d\u4e45\u4e86\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4e8c\u5341\u5e74\u4ee5\u4e0a\u4e86\u3002\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u90d1\u4f2f\u4f2f\u8ba4\u8bc6\u6211\u5927\u4f2f\u6709\u4e00\u6bb5\u5f88\u957f\u7684\u5206\u624b\u65f6\u95f4\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u7709\u5934\u8038\u626c\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u548c\u4f60\u5927\u6015\u6709\u4e00\u6bb5\u5f88\u957f\u5206\u624b\u65f6\u95f4\uff0c\u4f60\u5927\u4f2f\u8981\u7ec3\u4e00\u79cd\u4e39\u836f\uff0c\u72ec\u81ea\u9690\u5c45\u4e8e\u5ce8\u5d4b\u5c71\u4e2d\u3002\u201d \u3000\u3000\u65b9\u745c\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u864e\u6bd2\u4e0d\u98df\u5b50\uff0c\u5927\u4f2f\u5b9e\u65e0\u6740\u5bb3\u5c0f\u4f84\u7684\u7406\u7531\uff0c\u8fd9\u4e2d\u95f4\u5fc5\u6709\u7740\u4ee4\u4eba\u96be\u6d4b\u7684\u53d8\u5316\u4e86\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4f60\u662f\u8bf4\uff1f\u2026\u2026\u201d \u3000\u3000\u65b9\u745c\u63a5\u9053\uff1a\u201c\u665a\u8f88\u672a\u4e86\u7136\u771f\u8c61\u4e4b\u524d\uff0c\u4e0d\u613f\u5984\u4f5c\u8bba\u65ad\uff0c\u8840\u53e3\u55b7\u4eba\uff0c\u4f46\u665a\u8f88\u4e34\u53bb\uff0c\u6709\u4e00\u4e8b\u5949\u544a\u90d1\u4f2f\u4f2f\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u65b9\u745c\u9053\uff1a\u201c\u8bf7\u90d1\u4f2f\u4f2f\u548c\u77f3\u53d4\u53d4\uff0c\u5584\u4e3a\u7167\u987e\u6211\u90a3\u4f4d\u5584\u826f\u7684\u5927\u5a18\uff0c\u548c\u5144\u5f1f\u2026\u2026\u201d \u3000\u3000\u4f38\u624b\u6361\u8d77\u957f\u5251\uff0c\u884c\u5230\u65b9\u5929\u6210\u7684\u8eab\u524d\uff0c\u63a5\u9053\uff1a\u201c\u4e8b\u5230\u5982\u4eca\uff0c\u5927\u4f2f\u60f3\u4e5f\u65e0\u6cd5\u63a9\u9970\u4e86\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u8f7b\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u65b9\u745c\u53f3\u624b\u4e00\u6325\uff0c\u524a\u53bb\u4e86\u5934\u4e0a\u7684\u9752\u4e1d\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e0d\u8bba\u4f60\u662f\u4ec0\u4e48\u4eba\uff0c\u4f46\u4f60\u5bf9\u6211\u6709\u7740\u4e00\u756a\u517b\u80b2\u4e4b\u6069\uff0c\u4eca\u65e5\u6211\u5272\u53d1\u4ee3\u9996\uff0c\u4ee5\u916c\u517b\u80b2\u4e4b\u6069\uff0c\u4ece\u6b64\u523b\u8d77\uff0c\u65b9\u745c\u5df2\u6b7b\uff0c\u6d3b\u7740\u7684\u662f\u65b9\u96ea\u5b9c\u2026\u2026\u201d \u3000\u3000\u56de\u5934\u5bf9\u90d1\u5927\u521a\u62b1\u62f3\u793c\uff0c\u9053\uff1a\u201c\u90d1\u4f2f\u4f2f\u76f8\u6551\u4e4b\u60c5\uff0c\u665a\u8f88\u65e5\u540e\u5fc5\u6709\u4e00\u62a5\uff0c\u665a\u8f88\u5c31\u6b64\u544a\u522b\u4e86\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u653e\u5fc3\u5730\u8d70\u5427\uff01\u5e0c\u671b\u65e5\u540e\uff0c\u54b1\u4eec\u8fd8\u80fd\u5728\u6c5f\u6e56\u4e4b\u4e0a\u76f8\u89c1\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7ffb\u8eab\u4e0a\u9a6c\uff0c\u7eb5\u9a91\u800c\u53bb\u3002 \u3000\u3000\u90d1\u5927\u521a\u624b\u6267\u7d2b\u91d1\u5200\uff0c\u4e00\u76f4\u76d1\u89c6\u7740\u65b9\u5929\u6210\uff0c\u76f4\u5f85\u90a3\u65b9\u96ea\u5b9c\u80cc\u5f71\u6d88\u5931\uff0c\u624d\u6536\u5200\u5165\u9798\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u65b9\u4e8c\u5f1f\uff0c\u4f60\u4e3a\u4f55\u8981\u6740\u65b9\u745c\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u795e\u60c5\u51b7\u6f20\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u6211\u4eec\u5bb6\u65cf\u4e8b\uff0c\u5927\u54e5\u6700\u597d\u4e0d\u8981\u591a\u7ba1\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5c0f\u5144\u9047\u4e0a\u4e86\uff0c\u5c82\u6709\u4e0d\u7ba1\u4e4b\u7406\uff1f\u2026\u2026\u201d \u3000\u3000\u65b9\u5929\u6210\u7a81\u7136\u4e00\u8dc3\u800c\u8d77\uff0c\u7eb5\u8eab\u76f4\u5411\u9a6c\u80cc\u4e0a\u8dc3\u53bb\u3002 \u3000\u3000\u90d1\u5927\u521a\u51b7\u54fc\u4e00\u58f0\uff0c\u53f3\u624b\u4e00\u62ac\uff0c\u7d2b\u91d1\u5200\u91cd\u53c8\u51fa\u9798\uff0c\u95ea\u7535\u4e00\u822c\u5288\u4e86\u8fc7\u53bb\u3002\u5200\u52bf\u5947\u901f\uff0c\u8feb\u5f97\u65b9\u5929\u6210\u4e0d\u5f97\u4e0d\u7eb5\u8eab\u8ba9\u907f\u5f00\u53bb\u3002 \u3000\u3000\u5200\u5149\u8fc7\u5904\uff0c\u5065\u9a6c\u60b2\u5636\uff0c\u4e00\u5339\u957f\u7a0b\u5065\u9a6c\uff0c\u751f\u751f\u88ab\u4ed6\u5288\u6210\u4e24\u65ad\u3002 \u3000\u3000\u65b9\u5929\u6210\u671b\u7740\u90a3\u6a2a\u9648\u9a6c\u5c38\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f3c\u662f\u5f53\u771f\u7684\u6709\u6740\u5bb3\u5c0f\u5f1f\u4e4b\u5fc3\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4e8c\u5f1f\u4e3e\u52a8\uff0c\u5927\u632c\u5e38\u7406\uff0c\u5c82\u80fd\u8ba9\u5c0f\u5144\u4e0d\u52a8\u7591\u5fc3\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u662f\u60c5\u91cd\u751f\u6b7b\u7684\u5144\u5f1f\uff0c\u4e8c\u5341\u5e74\u751f\u6b7b\u4e0e\u5171\uff0c\u60a3\u96be\u76f8\u6276\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f46\u54b1\u4eec\u5144\u5f1f\u4e4b\u95f4\uff0c\u4e00\u5411\u662f\u5766\u5766\u8bda\u8bda\uff0c\u4ece\u4e0d\u76f8\u8f7b\u76f8\u6b3a\uff0c\u4e8c\u5f1f\u7adf\u7136\u8981\u6740\u5bb3\u81ea\u5df1\u7684\u4f84\u513f\uff0c\u4f55\u51b5\uff0c\u4ed6\u53c8\u662f\u521a\u521a\u6551\u8fc7\u4f60\u6027\u547d\u4e4b\u4eba\uff0c\u6b64\u7b49\u6096\u60c5\u80cc\u7406\u7684\u4e8b\uff0c\u96be\u9053\u5c0f\u5144\u80fd\u4e0d\u7ba1\u5417\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5982\u662f\u6ca1\u6709\u4e86\u5144\u5f1f\u4e4b\u60c5\uff0c\u5927\u54e5\u662f\u5426\u8fd8\u8981\u7ba1\u6211\u4eec\u5bb6\u52a1\u4e4b\u4e8b\u5462\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u90a3\u8981\u770b\u60c5\u5f62\u5982\u4f55\u4e86\uff01\u54b1\u4eec\u4ee5\u4fa0\u4e49\u81ea\u4efb\uff0c\u8def\u89c1\u4e0d\u5e73\uff0c\u8fd8\u8981\u62d4\u5200\u76f8\u52a9\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5927\u54e5\u5bf9\u5c0f\u5f1f\u8fde\u4e0b\u8fa3\u624b\uff0c\u4f3c\u662f\u60c5\u4e49\u5df2\u7edd\uff0c\u54b1\u4eec\u5144\u5f1f\u4e4b\u60c5\uff0c\u4e5f\u8be5\u5c31\u6b64\u544a\u7ec8\u4e86\u2026\u2026\u201d \u3000\u3000\u90d1\u5927\u521a\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u65b9\u4e8c\u5f1f\u5982\u7740\u5b9a\u8981\u548c\u5c0f\u5144\u7edd\u4ea4\uff0c\u5c0f\u5144\u51b3\u4e0d\u52c9\u5f3a\uff0c\u4e0d\u8fc7\uff0c\u6865\u5f52\u6865\uff0c\u8def\u5f52\u8def\uff0c\u54b1\u4eec\u5144\u5f1f\u867d\u7136\u60c5\u5c3d\u4e49\u7edd\uff0c\u4ecd\u7136\u4e0d\u59a8\u788d\u5c0f\u5144\u67e5\u95ee\u65b9\u745c\u7684\u4e8b\u60c5\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u5982\u662f\u54b1\u4eec\u5144\u5f1f\uff0c\u5df2\u7136\u5212\u5730\u7edd\u4ea4\uff0c\u5272\u888d\u65ad\u4e49\uff0c\u5728\u4e0b\u53ef\u4ee5\u62d2\u7edd\u7b54\u590d\u90d1\u5927\u54e5\u4efb\u4f55\u95ee\u9898\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e8c\u5f1f\u53ef\u662f\u60f3\u4ee5\u7edd\u4ea4\u624b\u6bb5\uff0c\u5a01\u80c1\u5c0f\u5144\u5417\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u7a81\u7136\u62d4\u51fa\u957f\u5251\uff0c\u5212\u4e86\u4e00\u9053\u571f\u6c9f\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u5144\u5f1f\u4ece\u6b64\u523b\u8d77\uff0c\u60c5\u7edd\u4e49\u7edd\uff0c\u4ee5\u540e\u5f7c\u6b64\u4e0d\u518d\u5f80\u6765\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u7a81\u7136\u4ef0\u8138\u54c8\u54c8\u5927\u7b11\u4e00\u9635\uff0c\u9053\uff1a\u201c\u65b9\u5929\u6210\uff0c\u4f60\u5982\u60f3\u4f7f\u6211\u8d1f\u6c14\u800c\u53bb\uff0c\u4e0d\u518d\u591a\u95ee\u4f60\u65b9\u5929\u6210\u7684\u4e8b\uff0c\u90a3\u662f\u9601\u4e0b\u6253\u9519\u4e3b\u610f\u4e86\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5982\u662f\u5728\u4e0b\u4e0d\u544a\u8bc9\u4f60\u90d1\u5927\u521a\u5462\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u542c\u4ed6\u76f4\u63a5\u53eb\u51fa\u4e86\u81ea\u5df1\u7684\u59d3\u540d\uff0c\u4e0d\u7981\u8138\u8272\u4e00\u53d8\uff0c\u6012\u58f0\u559d\u9053\uff1a\u201c\u65b9\u5929\u6210\uff0c\u4f60\u5982\u771f\u7684\u8fd9\u822c\u5be1\u60c5\u8584\u4e49\uff0c\u53ea\u6709\u4e00\u4e2a\u529e\u6cd5\uff0c\u53ef\u4f7f\u90d1\u67d0\u4eba\u4e0d\u518d\u591a\u95ee\u6b64\u4e8b\uff01\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u90d1\u5144\u53ef\u662f\u8981\u548c\u5728\u4e0b\u52a8\u624b\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u53ea\u8981\u80dc\u4e86\u6211\u624b\u4e2d\u4e4b\u5200\uff0c\u90d1\u67d0\u4eba\u56de\u5934\u5c31\u8d70\uff0c\u51b3\u4e0d\u95ee\u4f60\u7684\u4e8b\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u9053\uff1a\u201c\u5982\u82e5\u90d1\u5144\u8feb\u65b9\u67d0\u51fa\u624b\uff0c\u5728\u4e0b\u662f\u53ea\u597d\u5949\u966a\u4e86\uff0c\u4e0d\u8fc7\u5175\u5203\u65e0\u773c\uff0c\u5982\u662f\u4f24\u4e86\u4f60\u90d1\u5927\u4fa0\uff0c\u90a3\u5c31\u4e0d\u8981\u602a\u5728\u4e0b\u4e86\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u6012\u9053\uff1a\u201c\u4f60\u6709\u672c\u9886\u53ea\u7ba1\u65bd\u5c55\u5c31\u662f\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u957f\u5251\u5e73\u80f8\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5ff5\u4f60\u5e74\u957f\u51e0\u5c81\uff0c\u5728\u4e0b\u5949\u8ba9\u5148\u673a\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u6c14\u5f97\u8138\u8272\u94c1\u9752\uff0c\u6b63\u5f85\u53cd\u5507\u76f8\u8ba5\uff0c\u7a81\u7136\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6697\u9053\uff1a\u201c\u4ed6\u8fd9\u7b49\u4e3e\u52a8\uff0c\u5206\u660e\u662f\u60f3\u6fc0\u6012\u4e8e\u6211\uff0c\u51c6\u5907\u5728\u8fd9\u4e00\u6218\u4e4b\u4e2d\uff0c\u6740\u6211\u706d\u53e3\u3002\u201d \u3000\u3000\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u80f8\u4e2d\u6012\u6c14\u987f\u6d88\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u65b9\u4e8c\u4fa0\uff0c\u65b9\u5929\u6210\uff0c\u4f60\u53ef\u662f\u60f3\u6fc0\u6012\u4e8e\u6211\u5417\uff1f\u201d \u3000\u3000\u65b9\u5929\u6210\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u90d1\u5927\u4fa0\u53ef\u4ee5\u51fa\u624b\u4e86\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u6de1\u8c08\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u55ef\uff01\u4f60\u65b9\u4e8c\u4fa0\u5148\u51fa\u624b\u5427\uff01\u5728\u4e0b\u75f4\u957f\u51e0\u5c81\u5e94\u8be5\u8ba9\u4f60\u4e24\u62db\u3002\u201d \u3000\u3000\u65b9\u5929\u6210\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4eca\u65e5\u4e0d\u662f\u4f60\u90d1\u5927\u521a\u6b7b\uff0c\u5c31\u662f\u65b9\u67d0\u4eba\u4ea1\u3002\u201d \u3000\u3000\u559d\u58f0\u4e2d\u632f\u5251\u800c\u8d77\uff0c\u523a\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u90d1\u5927\u521a\u4e3e\u5200\u5c01\u6321\uff0c\u67b6\u5f00\u4e86\u65b9\u5929\u6210\u7684\u957f\u5251\uff0c\u56de\u624b\u53cd\u51fb\uff0c\u5288\u51fa\u4e24\u5200\u3002 \u3000\u3000\u65b9\u5929\u6210\u6321\u5f00\u4e24\u5200\u4e4b\u540e\uff0c\u5251\u52bf\u7acb\u53d8\uff0c\u62db\u62db\u90fd\u662f\u81f4\u547d\u7684\u62db\u6570\u3002 \u3000\u3000\u90d1\u5927\u521a\u5fc3\u4e2d\u8fd8\u6709\u4e9b\u5144\u5f1f\u4e4b\u60c5\uff0c\u4f46\u76ee\u7779\u65b9\u5929\u6210\u624b\u4e2d\u5251\u52bf\u7684\u6076\u6bd2\uff0c\u53ea\u8981\u88ab\u4ed6\u523a\u4e2d\u4e00\u5251\uff0c\u7acb\u65f6\u5c31\u8981\u547d\u4e27\u5f53\u573a\uff0c\u5fc3\u4e2d\u5ffd\u7136\u4e00\u51db\uff0c\u6697\u9053\uff1a\u201c\u6211\u7b49\u7ed3\u4e49\u4e4b\u521d\u65b9\u4e8c\u5f1f\u4fa0\u809d\u4e49\u80c6\uff0c\u6b64\u65f6\u6b64\u523b\uff0c\u5168\u7136\u4f3c\u53d8\u4e86\u4e00\u4e2a\u4eba\u822c\u3002\u201d \u3000\u3000\u53ea\u89c9\u7740\u8111\u9645\u4e2d\u7075\u5149\u8fde\u95ea\uff0c\u7a81\u7136\u60f3\u8d77\u4e86\u65b9\u745c\u4e34\u53bb\u4e4b\u8a00\uff0c\u5fd6\u9053\uff1a\u201c\u96be\u9053\u4ed6\u5df2\u4e0d\u662f\u771f\u7684\u65b9\u5929\u6210\u5417\uff1f\u201d \u3000\u3000\u5fc3\u4e2d\u5ff5\u5934\u8f6c\u52a8\uff0c\u4e0d\u89c9\u7cbe\u795e\u4e00\u5206\uff0c\u624b\u4e2d\u5200\u52bf\u4e00\u7f13\uff0c\u88ab\u90a3\u65b9\u5929\u6210\u4e00\u5251\u523a\u6765\uff0c\u4f24\u53ca\u5de6\u81c2\uff0c\u8863\u670d\u7834\u88c2\uff0c\u9c9c\u8840\u6c69\u6c69\u800c\u51fa\u3002 \u3000\u3000\u90d1\u5927\u521a\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u8d76\u5fd9\u51dd\u795e\u5bf9\u654c\uff0c\u5200\u6cd5\u4e00\u53d8\uff0c\u5c55\u5f00\u53cd\u51fb\u3002 \u3000\u3000\u4e2d\u5dde\u4e09\u4fa0\u4e2d\uff0c\u539f\u672c\u4ee5\u90d1\u5927\u521a\u6b66\u529f\u6700\u5f3a\uff0c\u6b64\u523b\u5fc3\u4e2d\u7591\u7aa6\u91cd\u91cd\uff0c\u89c9\u7740\u53ea\u6709\u751f\u64d2\u8fd9\u65b9\u5929\u6210\uff0c\u624d\u80fd\u95ee\u51fa\u4e2a\u4e2d\u60c5\u7531\uff0c\u7eb5\u7136\u662f\u4f7f\u4ed6\u843d\u4e2a\u4f24\u6b8b\uff0c\u90a3\u4e5f\u662f\u6ca1\u6709\u6cd5\u5b50\u7684\u4e8b\u4e86\u3002\u4ed6\u5fc3\u4e2d\u6697\u5b9a\u4e3b\u610f\uff0c\u5168\u529b\u6325\u5200\u8fd8\u51fb\u3002 \u3000\u3000\u53ea\u89c1\u5200\u5149\u5982\u7535\uff0c\u6392\u5c71\u5012\u6d77\u4e00\u822c\uff0c\u76f4\u538b\u8fc7\u53bb\u3002 \u3000\u3000\u65b9\u5929\u6210\u624b\u4e2d\u5251\u52bf\uff0c\u9010\u6e10\u88ab\u538b\u5236\u4e86\u4e0b\u6765\uff0c\u6709\u4e9b\u65bd\u5c55\u4e0d\u5f00\u3002 \u3000\u3000\u640f\u6597\u4e2d\uff0c\u90d1\u5927\u521a\u5947\u62db\u7a81\u51fa\uff0c\u624b\u4e2d\u7d2b\u91d1\u5200\u903c\u5f00\u4e86\u65b9\u5929\u6210\u624b\u4e2d\u4e4b\u5251\uff0c\u5200\u52bf\u4e00\u8f6c\uff0c\u84ec\u7136\u4e00\u58f0\uff0c\u7528\u5200\u80cc\u51fb\u5728\u65b9\u5929\u6210\u7684\u53f3\u8098\u4e4b\u4e0a\u3002 \u3000\u3000\u8fd9\u4e00\u51fb\u52bf\u9053\u5f88\u91cd\uff0c\u65b9\u5929\u6210\u957f\u5251\u8131\u624b\u843d\u5730\uff0c\u4eba\u4e5f\u88ab\u6253\u5f97\u4e00\u4e2a\u8ddf\u6597\uff0c\u683d\u5012\u5730\u4e0a\u3002 \u3000\u3000\u90d1\u5927\u521a\u5982\u82e5\u8d81\u52bf\u4e00\u5200\u5288\u843d\uff0c\u5fc5\u53ef\u628a\u65b9\u5929\u6210\u4f24\u5728\u5200\u4e0b\u3002 \u3000\u3000\u4f46\u4ed6\u5fc3\u5ff5\u65e7\u60c5\uff0c\u624b\u4e2d\u7d2b\u91d1\u5200\u4e0d\u5fcd\u843d\u4e0b\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u5fae\u4e00\u72b9\u8c6b\u4e4b\u95f4\uff0c\u65b9\u5929\u6210\u7a81\u7136\u501f\u52bf\u6253\u4e86\u4e24\u4e2a\u7ffb\u6eda\uff0c\u4eba\u5df2\u5230\u4e86\u4e08\u4f59\u5f00\u5916\u3002 \u3000\u3000\u90d1\u5927\u521a\u5927\u58f0\u559d\u9053\uff1a\u201c\u5feb\u4e9b\u7ad9\u4f4f\u2026\u2026\u201d\u559d\u58f0\u4e2d\u5927\u6b65\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u65b9\u5929\u6210\u53f3\u624b\u4e00\u62ac\uff0c\u4e09\u70b9\u5bd2\u661f\uff0c\u8131\u624b\u800c\u51fa\uff0c\u54c1\u5b57\u5f62\u4e00\u9f50\u5c04\u5230\u3002 \u3000\u3000\u90d1\u5927\u521a\u91d1\u5200\u6325\u52a8\uff0c\u5e7b\u8d77\u4e00\u7247\u62a4\u8eab\u5200\u5149\u3002\u3002 \u3000\u3000\u53ea\u542c\u4e00\u9635\u53ee\u53ee\u549a\u549a\u4e4b\u58f0\uff0c\u4e09\u679a\u767d\u864e\u9489\uff0c\u5c3d\u4e3a\u90d1\u5927\u521a\u5200\u5149\u9707\u843d\u3002 \u3000\u3000\u4f46\u90a3\u65b9\u5929\u6210\u5374\u501f\u90d1\u5927\u521a\u5c01\u6321\u6697\u5668\u4e4b\u65f6\uff0c\u75be\u5feb\u7684\u5954\u5165\u6797\u4e2d\uff0c\u9690\u5931\u4e0d\u89c1\u3002 \u3000\u3000\u90d1\u5927\u521a\u5728\u6797\u8fb9\u8db3\u8db3\u7ad9\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u6599\u60f3\u90a3\u65b9\u745c\u5df2\u7ecf\u8d70\u8fdc\uff0c\u624d\u8f6c\u56de\u7a0b\u5bb6\u5e84\u53bb\u3002 \u3000\u3000\u4e14\u8bf4\u77f3\u4fca\u56de\u5230\u623f\u4e2d\uff0c\u8d8a\u60f3\u8d8a\u89c9\u7740\u4e8b\u60c5\u4e0d\u5bf9\uff0c\u7acb\u65f6\u8d76\u5230\u90d1\u5927\u521a\u7684\u623f\u4e2d\u67e5\u770b\uff0c\u4ed6\u63a8\u95e8\u4e0d\u5f00\uff0c\u7ed5\u5230\u540e\u7a97\uff0c\u679c\u7136\u53d1\u73b0\u90d1\u5927\u521a\u5df2\u7ecf\u79bb\u53bb\u3002 \u3000\u3000\u4e00\u79cd\u4e0d\u7965\u7684\u9884\u611f\uff0c\u6cdb\u4e0a\u5fc3\u5934\uff0c\u611f\u89c9\u5230\u76f8\u4ea4\u6570\u5341\u5e74\u7684\u4e2d\u5dde\u4e09\u4fa0\uff0c\u7acb\u65f6\u95f4\u5c06\u6709\u6781\u5927\u53d8\u5316\u3002 \u3000\u3000\u5fc3\u4e2d\u7126\u6025\uff0c\u72ec\u81ea\u884c\u51fa\u5e84\u5916\uff0c\u5f98\u5f8a\uff0c\u7b49\u5f85\u3002 \u3000\u3000\u8db3\u8db3\u7b49\u4e86\u4e24\u4e2a\u65f6\u8fb0\uff0c\u624d\u89c1\u90d1\u5927\u521a\uff0c\u72ec\u81ea\u8f6c\u4e86\u56de\u6765\u3002 \u3000\u3000\u77f3\u4fca\u6025\u6025\u8fce\u4e86\u51fa\u53bb\u9053\uff1a\u201c\u5927\u54e5\uff0c\u89c1\u8fc7\u4e8c\u54e5\u4e86\u5417\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u70b9\u70b9\u5934\u9053\uff1a\u201c\u544a\u8bc9\u7a0b\u5e84\u4e3b\uff0c\u54b1\u4eec\u7acb\u523b\u52a8\u8eab\u3002\u201d \u3000\u3000\u77f3\u4fca\u5947\u9053\uff1a\u201c\u4e8c\u54e5\u6ca1\u6709\u540c\u6765\u5417\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u54b1\u4eec\u5144\u5f1f\u7684\u4e8b\u4e0d\u8981\u60ca\u52a8\u522b\u4eba\uff0c\u54b1\u4eec\u5148\u79bb\u5f00\u6b64\u5730\u518d\u8c08\u3002\u201d \u3000\u3000\u4ece\u90d1\u5927\u521a\u4e25\u8083\u7684\u795e\u8272\u4e2d\uff0c\u77f3\u4fca\u5df2\u8b66\u89c9\u5230\u4e8b\u60c5\u4e25\u91cd\uff0c\u4e0d\u518d\u591a\u95ee\uff0c\u4f4e\u58f0\u95ee\u9053\uff0c\u201c\u5927\u54e5\u4ecd\u8bf7\u7531\u540e\u7a97\u7ed5\u56de\u5ba4\u4e2d\uff0c\u5c0f\u5f1f\u8fd9\u5c31\u53bb\u901a\u77e5\u7a0b\u5e84\u4e3b\u548c\u4e8c\u5ac2\uff0c\u51c6\u5907\u4e0a\u8def\u4e00\u5207\u5c31\u7eea\uff0c\u5c0f\u5f1f\u518d\u8bf7\u5927\u54e5\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u597d\uff01\u4f60\u53bb\u5427\uff01\u201d\u884c\u5165\u5e84\u4e2d\uff0c\u4ecd\u7136\u7531\u540e\u7a97\u884c\u5165\u5ba4\u4e2d\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u4e5f\u660e\u767d\uff0c\u81ea\u5df1\u79bb\u5e84\u548c\u56de\u5e84\u7684\u4e3e\u52a8\uff0c\u51b3\u7136\u65e0\u6cd5\u907f\u8fc7\u7a0b\u5bb6\u5e84\u4eba\u7684\u8033\u76ee\uff0c\u53ea\u662f\uff0c\u5bf9\u65b9\u4e0d\u4fbf\u591a\u95ee\uff0c\u770b\u89c1\u4e5f\u88c5\u6ca1\u6709\u770b\u89c1\u7f62\u4e86\u3002 \u3000\u3000\u90d1\u5927\u521a\u56de\u5165\u5ba4\u4e2d\uff0c\u89e3\u4e0b\u4f69\u5200\uff0c\u53ea\u89c9\u5f80\u4e8b\u91cd\u91cd\u6cdb\u4e0a\u5fc3\u5934\uff0c\u4f46\u5343\u601d\u4e07\u60f3\uff0c\u5374\u662f\u60f3\u4e0d\u660e\u767d\uff0c\u8fd9\u65b9\u5929\u6210\u5982\u4f55\u4f1a\u53d8\u5f97\u8fd9\u822c\u6076\u6bd2\u3002 \u3000\u3000\u4e0d\u77e5\u9053\u8fc7\u4e86\u591a\u5c0f\u65f6\u5149\uff0c\u5ba4\u5916\u4f20\u6765\u77f3\u4fca\u7684\u58f0\u97f3\uff1a\u9053\uff1a\u201c\u5927\u54e5\u9192\u4e86\u5417\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u53d6\u8fc7\u7d2b\u91d1\u5200\uff0c\u4f69\u5e26\u8eab\u4e0a\uff0c\u6253\u5f00\u623f\u95e8\u3002 \u3000\u3000\u53ea\u89c1\u77f3\u4fca\u548c\u7a0b\u5b50\u671b\u5e76\u80a9\u7ad9\u5728\u5ba4\u5916\uff0c\u7a0b\u5b50\u671b\u4e00\u62b1\u62f3\uff0c\u9053\uff1a\u201c\u77f3\u4e09\u4fa0\u544a\u8bc9\u533a\u533a\uff0c\u8bf8\u4f4d\u8981\u7acb\u523b\u4e0a\u8def\u2026\u2026\u201d \u3000\u3000\u90d1\u5927\u521a\u63a5\u7740\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u54b1\u4eec\u5144\u5f1f\u8fd8\u6709\u8981\u4e8b\uff0c\u5fc5\u5f97\u7acb\u523b\u767b\u7a0b\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u4e2d\u5dde\u4e09\u4fa0\uff0c\u4e3a\u4f38\u5f20\u6b66\u6797\u6b63\u4e49\uff0c\u7ec8\u65e5\u5e2d\u4e0d\u6687\u6696\uff0c\u5954\u8d70\u4e8e\u6c5f\u6e56\u4e4b\u4e0a\uff0c\u4f46\u8bf8\u4f4d\u7535\u8be5\u7a0d\u6709\u51e0\u65e5\u4f11\u6687\u624d\u5bf9\uff0c\u5144\u5f1f\u5df2\u7ecf\u4e3a\u8bf8\u4f4d\u51c6\u5907\u4e86\u4e00\u8258\u753b\u822b\uff0c\u660e\u65e5\u7531\u5144\u5f1f\u966a\u8bf8\u4f4d\u4e00\u6e38\u5cb7\u6c5f\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u7a0b\u5e84\u4e3b\u76db\u60c5\u5fc3\u9886\uff0c\u5f02\u65e5\u6709\u7a7a\u54b1\u4eec\u518d\u6765\u53e8\u6270\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u65e0\u53ef\u5948\u4f55\u4ed6\u8bf4\u9053\uff1a\u201c\u90d1\u5144\u5f53\u771f\u8981\u8d70\u5417\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd8\u671b\u7a0b\u5144\u5429\u5490\u4e0b\u53bb\uff0c\u4ee3\u6211\u4eec\u51c6\u5907\u4e24\u5339\u9a6c\uff0c\u4e00\u8f86\u7bf7\u8f66\uff0c\u6211\u7b49\u5c31\u611f\u6fc0\u4e0d\u5c3d\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5b50\u671b\u9053\uff1a\u201c\u90d1\u5144\u8a00\u91cd\u4e86\uff0c\u7a0b\u5b50\u671b\u4e00\u5bb6\u4eba\u7684\u751f\u6b7b\uff0c\u90fd\u662f\u4e2d\u5dde\u4e09\u4fa0\u6240\u8d50\uff0c\u533a\u533a\u51e0\u5339\u9a6c\u90d1\u5144\u600e\u80fd\u8bf4\u51fa\u611f\u6fc0\u4e4b\u8a00\uff1f\u5728\u4e0b\u7acb\u65f6\u53bb\u4e3a\u8bf8\u4f4d\u51c6\u5907\u3002\u201d\u7247\u523b\u4e4b\u540e\uff0c\u7a0b\u5b50\u671b\u91cd\u53c8\u884c\u56de\u9053\uff1a \u3000\u3000\u201c\u5065\u9a6c\uff0c\u84ec\u8f66\u90fd\u5df2\u5907\u59a5\uff0c\u5385\u4e2d\u4e5f\u6446\u4e86\u9152\u996d\uff0c\u8bf8\u4f4d\u8bf7\u5c3d\u4e00\u676f\u6c34\u9152\u518d\u8d70\u5982\u4f55\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u9152\u996d\u8c22\u4e86\uff0c\u5728\u4e0b\u7b49\u5373\u523b\u4e0a\u8def\u3002\u201d \u3000\u3000\u5927\u6b65\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u7a0b\u5b50\u671b\u662f\u4f55\u7b49\u4eba\u7269\uff0c\u5df2\u7136\u77a7\u51fa\u4e86\u90d1\u5927\u521a\u5fc3\u4e8b\u6c89\u91cd\uff0c\u5012\u4e5f\u4e0d\u518d\u529d\u9152\u996d\uff0c\u9001\u4e24\u4eba\u884c\u51fa\u5e84\u5916\u3002\u679c\u7136\u5e84\u5916\u65e9\u5df2\u5907\u597d\u4e86\u5065\u9a6c\uff0c\u7bf7\u8f66\u3002 \u3000\u3000\u65b9\u592b\u4eba\u5e26\u7740\u65b9\u73de\u7f13\u7f13\u884c\u51fa\u5e84\u5916\uff0c\u767b\u4e0a\u7bf7\u5352\u3002 \u3000\u3000\u90d1\u5927\u521a\u98de\u8eab\u4e0a\u9a6c\uff0c\u56de\u8eab\u5bf9\u8a00\u51e4\u537f\u4e00\u62b1\u62f3\uff0c\u9053\uff1a\u201c\u8a00\u5144\u5f1f\uff0c\u5728\u4e0b\u6709\u70b9\u79c1\u4e8b\u5148\u8d70\u4e00\u6b65\uff0c\u65e5\u540e\uff0c\u5144\u5f1f\u81ea\u4f1a\u5230\u8fb0\u5dde\u62dc\u8bbf\uff0c\u4ee5\u8c22\u76f8\u52a9\u4e4b\u60c5\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u4e0d\u89c1\u65b9\u5927\u6210\u4e00\u8d77\u5f52\u6765\uff0c\u5df2\u60f3\u5230\u4e86\u4ed6\u4eec\u5144\u5f1f\u4e4b\u95f4\uff0c\u53ef\u80fd\u6709\u4e86\u7ea0\u7eb7\uff0c\u5012\u4e5f\u4e0d\u4fbf\u591a\u95ee\uff0c\u62b1\u62f3\u8fd8\u4e86\u4e00\u793c\uff0c\u9053\uff1a\u201c\u5144\u5f1f\u5728\u8fb0\u5dde\u5019\u9a7e\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u56de\u987e\u4e86\u77f3\u4fca\u4e00\u773c\uff0c\u9053\uff1a\u201c\u6709\u52b3\u4e09\u5144\u8d76\u8f66\uff0c\u54b1\u4eec\u6b64\u756a\u524d\u53bb\uff0c\u4e0d\u77e5\u4f55\u65f6\u518d\u6765\uff0c\u4e0d\u7528\u7a0b\u5e9c\u4e2d\u4eba\u4e86\u3002\u201d \u3000\u3000\u539f\u6765\uff0c\u7a0b\u5b50\u671b\u5907\u4e86\u4e00\u8f86\u8f66\u4e4b\u540e\uff0c\u53c8\u9063\u6d3e\u4e86\u4e00\u540d\u8f66\u592b\u8d76\u6765\u3002 \u3000\u3000\u77f3\u4fca\u5e94\u4e86\u4e00\u58f0\uff0c\u767b\u4e0a\u8f66\u8f95\uff0c\u4f4e\u58f0\u5bf9\u90a3\u8d76\u8f66\u4eba\u9053\uff1a\u201c\u4e0d\u6562\u6709\u52b3\uff0c\u9601\u4e0b\u8bf7\u56de\u5427\uff01\u201d\u90a3\u8d76\u8f66\u4eba\u5e94\u4e86\u4e00\u58f0\uff0c\u7f13\u7f13\u4e0b\u4e86\u7bf7\u8f66\u3002 \u3000\u3000\u90d1\u5927\u521a\u80f8\u6709\u6210\u7af9\uff0c\u4e00\u53e3\u6c14\u5954\u4eba\u90a3\u68ee\u6797\u4e4b\u4e2d\uff0c\u624d\u4e00\u52d2\u9a6c\u7f30\uff0c\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u77f3\u4fca\u505c\u4e0b\u7bf7\u8f66\u6d41\u76ee\u56db\u987e\uff0c\u770b\u505c\u8eab\u4e4b\u5904\uff0c\u5341\u5206\u8352\u51c9\uff0c\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u5927\u54e5\uff0c\u600e\u4e48\u4e0d\u8d70\u4e86\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5982\u82e5\u54b1\u4eec\u4e09\u5144\u5f1f\uff0c\u90fd\u6b7b\u5e72\u90a3\u9b54\u5200\u7530\u8fdc\u4e4b\u624b\uff0c\u597d\u843d\u5f97\u4fa0\u540d\u5343\u53e4\uff0c\u4e0d\u8ba9\u6311\u56ed\u4e09\u7ed3\u4e49\uff0c\u5218\u3001\u5173\u3001\u5f20\u4e13\u7f8e\u4e8e\u524d\u4e86\u3002\u201d \u3000\u3000\u53ea\u89c1\u5782\u5e18\u542f\u52a8\uff0c\u65b9\u592b\u4eba\u63a2\u51fa\u5934\u6765\uff0c\u9053\uff1a\u201c\u5927\u54e5\uff0c\u5929\u6210\u6709\u4ec0\u4e48\u5bf9\u4e0d\u8d77\u5927\u54e5\u7684\u4e8b\u5417\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u5f1f\u59b9\u65e2\u7136\u5982\u6b64\u76f8\u8be2\uff0c\u5c0f\u5144\u5012\u4e5f\u4e0d\u4fbf\u518d\u9690\u7792\u4e86\u3002\u201d\u96e8\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u8fd9\u4ef6\u4e8b\u4e5f\u65e0\u6cd5\u518d\u9690\u7792\u4e0b\u53bb\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u9053\uff1a\u201c\u4e0d\u89c1\u5929\u6210\u540c\u5927\u54e5\u5f52\u6765\uff0c\u5f1f\u5ab3\u5df2\u6709\u9884\u611f\u4e86\u2026\u2026\u201d \u3000\u3000\u77f3\u4fca\u5fc3\u4e2d\u7126\u6025\uff0c\u5fcd\u4e0d\u4f4f\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u4e8c\u54e5\u73b0\u5728\u4f55\u5904\uff1f\u7a76\u7adf\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u76ee\u5149\u8f6c\u6ce8\u5230\u65b9\u592b\u4eba\u7684\u8138\u4e0a\uff0c\u7f13\u7f13\u4ed6\u8bf4\u9053\uff1a\u201c\u4e8c\u5f1f\u7d20\u6709\u8d24\u540d\uff0c\u8fd9\u4e0d\u4f46\u6211\u548c\u4e09\u5f1f\u77e5\u6653\uff0c\u548c\u54b1\u4eec\u4e2d\u5dde\u4e09\u4fa0\u6765\u5f80\u7684\u670b\u53cb\uff0c\u4e2a\u4e2a\u90fd\u6709\u611f\u89c9\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u7f13\u7f13\u4ed6\u8bf4\u9053\uff1a\u201c\u5509\uff01\u5927\u54e5\u6709\u4ec0\u4e48\u8bdd\uff0c\u53ea\u7ba1\u8bb2\uff0c\u5f1f\u5ab3\u6d17\u8033\u606d\u542c\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4e8c\u5f1f\u8981\u6740\u65b9\u745c\uff01\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\uff0c\u6709\u5982\u6625\u96f7\u4e4d\u5c55\uff0c\u53ea\u542c\u5f97\u77f3\u4fca\u3001\u65b9\u592b\u4eba\u540c\u65f6\u60ca\u53eb\u5931\u58f0\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u65b9\u4e8c\u5f1f\u8981\u6740\u65b9\u745c\uff0c\u5728\u4e0b\u4eb2\u773c\u6240\u89c1\uff0c\u51fa\u624b\u76f8\u6551\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u6447\u6447\u5934\u9053\uff1a\u201c\u4e0d\u53ef\u80fd\u554a\uff01\u4ed6\u4e3a\u4ec0\u4e48\u8981\u6740\u745c\u513f\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u8f7b\u8f7b\u5730\u53f9\u606f\u4e00\u58f0\u9053\uff1a\u201c\u5982\u975e\u5728\u4e0b\u4eb2\u773c\u770b\u5230\uff0c\u522b\u4eba\u8bf4\u7ed9\u6211\u542c\uff0c\u6211\u4e5f\u4e00\u6837\u4e0d\u4fe1\u3002\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u745c\u513f\u6551\u4e86\u6211\u4eec\u624d\u4e0d\u8fc7\u6570\u65e5\uff0c\u4e8c\u54e5\u600e\u80fd\u4e0b\u6b64\u6bd2\u624b\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4ed6\u65bd\u7528\u9a97\u672f\uff0c\u70b9\u4e86\u745c\u513f\u7684\u7a74\u9053\uff0c\u5426\u5219\u4ee5\u745c\u513f\u6740\u4f24\u9b54\u5200\u7530\u8fdc\u7684\u5251\u6cd5\uff0c\u4ed6\u5c82\u80fd\u662f\u5176\u654c\u624b\uff1f\u2026\u2026\u201d\u957f\u53f9\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u4f46\u4ed6\u5374\u4e0d\u77e5\u87b3\u8782\u6355\u8749\uff0c\u9ec4\u96c0\u5728\u540e\uff0c\u6211\u5374\u8ffd\u968f\u4ed6\u8eab\u540e\u800c\u6765\u2026\u2026\u201d \u3000\u3000\u5f53\u4e0b\uff0c\u628a\u8be6\u7ec6\u7ecf\u8fc7\u4e4b\u60c5\uff0c\u5f88\u4ed4\u7ec6\u4ed6\u8bf4\u4e86\u4e00\u904d\u3002 \u3000\u3000\u65b9\u592b\u4eba\u5446\u5446\u5730\u542c\u7740\uff0c\u5979\u4f3c\u662f\u542c\u5f97\u5f88\u4ed4\u7ec6\uff0c\u53c8\u4f3c\u662f\u6839\u672c\u6ca1\u6709\u5fc3\u5728\u542c\u3002\u76f4\u5f85\u90d1\u5927\u521a\u8bf4\u660e\u4e86\u7ecf\u8fc7\u4e4b\u60c5\u826f\u4e45\uff0c\u65b9\u592b\u4eba\u624d\u957f\u957f\u53f9\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5929\u6210\u5462\uff1f\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u77f3\u4fca\u63a5\u9053\uff1a\u201c\u5927\u54e5\u8bf4\u7684\u5341\u5206\u4ed4\u7ec6\uff0c\u81ea\u7136\uff0c\u662f\u4e0d\u4f1a\u9519\u4e86\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u7728\u52a8\u4e86\u4e00\u4e0b\u5706\u5706\u7684\u5927\u773c\u775b\uff0c\u4e24\u884c\u6e05\u6cea\uff0c\u6eda\u4e0b\u53cc\u988a\uff0c\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u6211\u4e00\u5b9a\u8981\u627e\u5230\u4ed6\uff0c\u95ee\u95ee\u4ed6\uff0c\u4e3a\u4ec0\u4e48\u8981\u6740\u745c\u513f\uff0c\u5509\uff01\u6211\u641c\u5c3d\u4e86\u67af\u80a0\uff0c\u5b9e\u5728\u60f3\u4e0d\u51fa\u539f\u56e0\u4f55\u5728\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u9762\u73b0\u96be\u8272\uff0c\u6c89\u541f\u4e86\u826f\u4e45\uff0c\u624d\u9053\uff1a\u201c\u5f1f\u59b9\u548c\u4e8c\u5f1f\u662f\u5426\u6709\u4e00\u6bb5\u5f88\u957f\u65f6\u95f4\u7684\u5206\u79bb\uff1f\u201d \u3000\u3000\u65b9\u592b\u4eba\u5446\u4e86\u4e00\u5446\u9053\uff1a\u201c\u5f88\u957f\u65f6\u95f4\u7684\u5206\u79bb\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u65b9\u4e8c\u5f1f\u8c6a\u4fa0\u80f8\u6000\uff0c\u600e\u4f1a\u505a\u51fa\u6b64\u7b49\u4e8b\uff01\u201d \u3000\u3000\u65b9\u592b\u4eba\u795e\u60c5\u832b\u7136\uff0c\u82e5\u6709\u6240\u601d\u4e00\u822c\uff0c\u5446\u5446\u671b\u7740\u5929\u9645\u4e00\u7247\u767d\u4e91\u51fa\u795e\u3002 \u3000\u3000\u77f3\u4fca\uff0c\u7a81\u7136\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4e0d\u80fd\u554a\uff01\u5982\u82e5\u4ed6\u4e0d\u662f\u4e8c\u54e5\uff0c\u600e\u7684\u4ed6\u58f0\u97f3\u795e\u60c5\uff0c\u9762\u8c8c\u3001\u8eab\u6750\u5168\u90fd\u80fd\u7ef4\u5999\u96be\u8096\uff1f\u54b1\u4eec\u548c\u4ed6\u76f8\u4ea4\u4e86\u4e8c\u5341\u5e74\uff0c\u5c82\u6709\u770b\u4e0d\u51fa\u7684\u9053\u7406\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5f53\u6211\u53d1\u73b0\u4ed6\u8981\u6740\u5bb3\u745c\u513f\u65f6\uff0c\u5185\u5fc3\u4e4b\u4e2d\u7684\u60ca\u6115\uff0c\u5c24\u8fc7\u4e09\u5f1f\uff0c\u6211\u4e5f\u60f3\u4e0d\u901a\u4ed6\u4f55\u4ee5\u8981\u5982\u6b64\uff0c\u4f46\u4e8b\u60c5\u6bd5\u7adf\u662f\u53d1\u751f\u4e86\uff0c\u5982\u662f\u5c0f\u5144\u665a\u53bb\u4e00\u6b65\uff0c\u6b64\u523b\u745c\u513f\u65e9\u5df2\u6c14\u7edd\u591a\u65f6\u4e86\u3002\u201d \u3000\u3000\u5927\u7ea6\u662f\u8fd9\u6d88\u606f\u592a\u8fc7\u7a81\u7136\uff0c\u77f3\u4fca\u3001\u65b9\u592b\u4eba\u6709\u4e9b\u832b\u7136\u65e0\u63aa\uff0c\u90d1\u5927\u521a\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u4ed6\u4eec\u4f3c\u4e4e\u662f\u90fd\u672a\u542c\u660e\u767d\u3002 \u3000\u3000\u90d1\u5927\u521a\u5fc3\u4e2d\u4e86\u7136\uff0c\u662f\u4ee5\u4e0d\u7ba1\u77f3\u4fca\u3001\u5bf9\u592b\u4eba\u95ee\u4ec0\u4e48\uff0c\u4ed6\u603b\u662f\u4e0d\u538c\u5176\u70e6\uff0c\u518d\u4e09\u89e3\u8bf4\uff0c\u5e0c\u671b\u4f7f\u4e24\u4f4d\u660e\u767d\u4e3a\u6b62\u3002 \u3000\u3000\u65b9\u592b\u4eba\u6295\u6ce8\u65e0\u9645\u7684\u76ee\u5149\uff0c\u7f13\u7f13\u5730\u6536\u4e86\u56de\u6765\uff0c\u9053\uff1a\u201c\u6211\u548c\u4ed6\u6709\u8fc7\u4e00\u6bb5\u65f6\u95f4\u5206\u79bb\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u597d\u957f\u65f6\u95f4\uff1f\u201d \u3000\u3000\u65b9\u592b\u4eba\u9053\uff1a\u201c\u5927\u7ea6\u534a\u5e74\u4ee5\u4e0a\u3002\u90a3\u65f6\uff0c\u6211\u521a\u521a\u6000\u7740\u73de\u513f\u2026\u2026\u201d \u3000\u3000\u77f3\u4fca\u63a5\u9053\uff1a\u201c\u90a3\u662f\u8bf4\uff0c\u8fd9\u4ef6\u4e8b\u81f3\u5c11\u6709\u5341\u4e94\u5e74\u4e4b\u4e45\u4e86\uff1f\u201d \u3000\u3000\u65b9\u592b\u4eba\u9053\uff1a\u201c\u9664\u4e86\u90a3\u4e00\u6b21\u7684\u5206\u79bb\u4e4b\u5916\uff0c\u6211\u4eec\u4e00\u76f4\u518d\u6ca1\u6709\u8f83\u957f\u7684\u5206\u79bb\u8fc7\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u90a3\u662f\u8bf4\uff0c\u5982\u82e5\u65b9\u5929\u6210\u771f\u7684\u6709\u4eba\u5192\u5145\uff0c\u5df2\u7ecf\u5192\u5145\u4e86\u5341\u51e0\u5e74\uff0c\u90a3\u65f6\uff0c\u65b9\u73de\u6216\u672a\u751f\u51fa\uff0c\u65b9\u745c\u4e5f\u4e0d\u8fc7\u5728\u6000\u62b1\u4e4b\u4e2d\u3002\u201d \u3000\u3000\u53ea\u542c\u65b9\u592b\u4eba\u957f\u957f\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u4e0d\u53ef\u80fd\u7684\u4e8b\u554a\uff01\u201d \u3000\u3000\u77f3\u4fca\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5341\u4e94\u5e74\u524d\u4e8c\u5ac2\u5ac1\u7ed9\u4e8c\u54e5\u4e0d\u4e45\u554a\uff01\u201d \u3000\u3000\u65b9\u592b\u4eba\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4e24\u5e74\u591a\u5427\u2026\u2026\u201d\u7a81\u7136\u8f6c\u8fc7\u8eab\u5b50\uff0c\u5bf9\u7740\u90d1\u5927\u521a\u76c8\u76c8\u62dc\u5012\uff0c\u9053\uff1a \u3000\u3000\u201c\u5927\u54e5\uff0c\u6c42\u4f60\u4e00\u4ef6\u4e8b\u597d\u5417\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u65b9\u592b\u4eba\u9053\uff1a\u201c\u6211\u4e0d\u76f8\u4fe1\u5929\u6210\u662f\u90a3\u7b49\u8584\u60c5\u5be1\u4e49\u7684\u4eba\uff0c\u6211\u8981\u4eb2\u53e3\u95ee\u4ed6\u4e00\u4e2a\u660e\u767d\uff0c\u5927\u54e5\uff0c\u4e8c\u5f1f\u8bf7\u770b\u5728\u6211\u4efd\u4e0a\uff0c\u5e2e\u6211\u627e\u5230\u4ed6\u3002\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u90a3\u662f\u5f53\u7136\uff0c\u4e8c\u5ac2\u5429\u5490\uff0c\u6211\u4eec\u4e49\u4e0d\u5bb9\u8f9e\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5927\u54e5\u5462\uff1f\u6211\u77e5\u9053\u4f60\u5bf9\u4ed6\u5f88\u6c14\u5fff\uff0c\u4e0d\u613f\u518d\u770b\u5230\u4ed6\uff0c\u4f46\u5e0c\u671b\u770b\u5728\u5b69\u5b50\u548c\u6211\u7684\u9762\u4e0a\uff0c\u7b54\u5e94\u6211\u4eec\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u70b9\u70b9\u5934\u9053\uff1a\u201c\u597d\uff01\u6211\u4e5f\u8981\u628a\u4e8b\u60c5\u67e5\u6e05\u695a\uff0c\u5982\u82e5\u4ed6\u771f\u662f\u65b9\u4e8c\u5f1f\uff0c\u5176\u95ee\u5fc5\u6709\u5185\u60c5\u2026\u2026\u201d \u3000\u3000\u7a81\u95fb\u65b9\u592b\u4eba\u5624\u54fc\u4e00\u58f0\uff0c\u5012\u6454\u5728\u5730\u4e0a\u3002 \u3000\u3000\u90d1\u5927\u521a\u4f38\u624b\u53bb\u6276\uff0c\u4f46\u5b57\u6307\u5c06\u8981\u63a5\u89e6\u5230\u65b9\u592b\u4eba\u7684\u8863\u670d\u65f6\uff0c\u7a81\u7136\u53c8\u7f29\u4e86\u56de\u6765\uff0c\u9053\uff1a\u201c\u4e09\u5f1f\uff0c\u5feb\u6276\u8d77\u4f60\u4e8c\u5ac2\u3002\u201d \u3000\u3000\u5176\u5b9e\uff0c\u65b9\u592b\u4eba\u6655\u5012\uff0c\u65b9\u73de\u5df2\u6025\u5954\u4e86\u8fc7\u6765\uff0c\u4f38\u624b\u6276\u8d77\u65b9\u592b\u4eba\u3002 \u3000\u3000\u90d1\u5927\u521a\u8f7b\u8f7b\u4e00\u638c\uff0c\u62cd\u5728\u65b9\u592b\u4eba\u80cc\u5fc3\u4e4b\u4e0a\u3002 \u3000\u3000\u65b9\u592b\u4eba\u957f\u957f\u5410\u4e86\u4e00\u53e3\u6c14\uff0c\u5410\u51fa\u4e00\u53e3\u8840\u6765\u3002 \u3000\u3000\u90d1\u5927\u521a\u4f4e\u58f0\u9053\uff1a\u201c\u73de\u513f\uff0c\u628a\u4f60\u6bcd\u4eb2\u6276\u53bb\u4e00\u4fa7\uff0c\u63a8\u62ff\u5979\u7684\u7a74\u8109\u3002\u201d \u3000\u3000\u65b9\u73de\u671b\u7740\u6bcd\u4eb2\u5410\u51fa\u7684\u9c9c\u8840\uff0c\u9053\uff1a\u201c\u5927\u4f2f\u7236\uff0c\u5988\u5988\u53d7\u4e86\u4f24\u5417\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u5979\u5fc3\u4e2d\u4f24\u75db\u5230\u4e86\u6781\u5904\uff0c\u8840\u6c14\u7ffb\u6d8c\uff0c\u521a\u624d\u88ab\u4e00\u53e3\u6c14\u7ffb\u8d77\u7684\u8840\u5757\uff0c\u5835\u4f4f\u4e86\u54bd\u5589\uff0c\u6655\u4e86\u8fc7\u53bb\uff0c\u8fd9\u53e3\u8840\u5757\u624d\u5410\u51fa\uff0c\u5df2\u7ecf\u4e0d\u788d\u4e8b\u4e86\uff0c\u73b0\u5728\u8ba9\u5979\u597d\u597d\u5730\u4f11\u606f\u4e00\u4e0b\u3002\u201d \u3000\u3000\u65b9\u73de\u5e94\u4e86\u4e00\u58f0\u3002\u628a\u6bcd\u4eb2\u6276\u5230\u4e00\u682a\u5927\u6811\u65c1\uff0c\u5750\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u77f3\u4fca\u671b\u7740\u65b9\u592b\u4eba\u82cd\u767d\u7684\u8138\u8272\uff0c\u6447\u6447\u5934\uff0c\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5927\u54e5\uff0c\u8fd9\u771f\u6bd4\u88ab\u4eba\u6740\u4e0a\u5343\u5200\u8fd8\u8981\u96be\u8fc7\uff0c\u65e0\u602a\u4e8c\u5ac2\u53d7\u4e0d\u5728\u4e86\uff0c\u5c0f\u5f1f\u4e5f\u5feb\u6491\u4e0d\u4f4f\u4e86\u201d \u3000\u3000\u90d1\u5927\u521a\u91cd\u91cd\u54b3\u4e86\u4e00\u58f0\uff0c\u9075\uff1a\u201c\u4e09\u5f1f\uff0c\u54b1\u4eec\u5144\u5f1f\u60c5\u540c\u624b\u8db3\uff0c\u5c82\u662f\u4f60\u4e8c\u54e5\u51e0\u53e5\u8d1f\u6c14\u8bdd\uff0c\u771f\u80fd\u4f7f\u5c0f\u5144\u64a4\u624b\u4e0d\u7ba1\uff01\u2026\u2026\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u6fc0\u52a8\u4e4b\u60c5\uff0c\u5b9e\u4e0d\u5728\u77f3\u4fca\u4e4b\u4e0b\uff0c\u53ea\u662f\u4ed6\u751f\u6027\u6c89\u7740\uff0c\u4e0d\u8ba9\u90a3\u788e\u5fc3\u88c2\u80c6\u7684\u75db\u82e6\uff0c\u5f62\u8bf8\u4e8e\u795e\u8272\u4e4b\u95f4\u3002 \u3000\u3000\u540c\u65f6\u4ed6\u5fc3\u4e2d\u4e5f\u660e\u767d\uff0c\u6b64\u65f6\u6b64\u60c5\u4e4b\u4e0b\uff0c\u81ea\u5df1\u5982\u4e0d\u80fd\u9547\u9759\u4ece\u4e8b\uff0c\u5fc5\u5c06\u4f7f\u5927\u5c40\u66f4\u4e3a\u7eb7\u4e71\u3002 \u3000\u3000\u5f53\u4e0b\u957f\u957f\u53f9\u4e00\u53e3\u6c14\uff0c\u538b\u5236\u4e0b\u6fc0\u52a8\u7684\u5fc3\u60c5\uff0c\u7f13\u7f13\u5730\u63a5\u9053\uff1a\u201c\u4e09\u5f1f\uff0c\u54b1\u4eec\u5144\u5f1f\uff0c\u51fa\u751f\u5165\u6b7b\uff0c\u7ecf\u8fc7\u4e86\u65e0\u6570\u9669\u6076\u9635\u4ed7\uff0c\u4e8c\u5f1f\u6709\u4e86\u4e8b\u6545\uff0c\u4e3a\u5144\u600e\u80fd\u5750\u89c6\uff1f\u53ea\u662f\u6b64\u523b\u7684\u5c40\u52bf\uff0c\u662f\u7528\u667a\u4e4b\u65f6\uff0c\u7528\u667a\u975e\u540c\u8f83\u529b\uff0c\u7b2c\u4e00\u8981\u9547\u9759\uff0c\u7b2c\u4e8c\u8fd8\u8981\u9547\u9759\uff0c\u624d\u80fd\u591f\u6d1e\u5bdf\u7ec6\u5fae\uff0c\u4f60\u4e8c\u5ac2\u662f\u5987\u9053\u4eba\u5bb6\uff0c\u96be\u602a\u5979\u60c5\u96be\u81ea\u7981\uff0c\u4f46\u4e09\u5f1f\u5fc5\u987b\u4fdd\u6301\u51b7\u9759\u3002\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u5927\u54e5\u8bf4\u7684\u662f\u3002\u201d\u8bed\u58f0\u5fae\u5fae\u4e00\u987f\u63a5\u9053\uff1a\u201c\u5927\u54e5\uff0c\u5982\u82e5\u4e8c\u54e5\u88ab\u4eba\u8c0b\u5bb3\u4e86\uff0c\u5192\u540d\u9876\u66ff\uff0c\u90a3\u5e94\u8be5\u662f\u5341\u4e94\u5e74\u524d\u7684\u4e8b\u4e86\uff0c\u8fd9\u5341\u51e0\u5e74\uff0c\u54b1\u4eec\u5c31\u6ca1\u6709\u53d1\u73b0\uff0c\u8fd9\u672a\u514d\u662f\u592a\u4e0d\u53ef\u601d\u4e49\u4e86\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u6211\u4e5f\u611f\u5230\u6b64\u4e8b\u51e0\u8fd1\u7384\u5947\uff0c\u4f46\u9664\u6b64\u4e4b\u5916\uff0c\u5c0f\u5144\u5b9e\u4e5f\u60f3\u4e0d\u51fa\u522b\u7684\u539f\u56e0\u4e86\u3002\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u4f1a\u4e0d\u4f1a\u662f\u4e8c\u54e5\u6709\u4e86\u4ec0\u4e48\u60b2\u60e8\u7684\u9645\u9047\uff0c\u4f7f\u4ed6\u96be\u518d\u81ea\u4e3b\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u5947\u602a\u7684\u662f\uff0c\u4ed6\u5bf9\u54b1\u4eec\u5e76\u65e0\u52a0\u5bb3\u4e4b\u610f\uff0c\u800c\u4e14\u60c5\u4e49\u5982\u521d\uff0c\u5982\u975e\u4ed6\u52a8\u4e86\u6740\u5bb3\u745c\u513f\u7684\u5ff5\u5934\uff0c\u54b1\u4eec\u6c38\u8fdc\u65e0\u6cd5\u5bf9\u4ed6\u52a8\u7591\u3002\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u4ed6\u53c8\u4e3a\u4ec0\u4e48\u8981\u6740\u745c\u513f\u5462\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u6c89\u541f\u4e86\u4e00\u9635\u9053\uff1a\u201c\u8fd9\u548c\u4e1c\u8033\u8001\u4eba\u6709\u5173\u3002\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u662f\u56e0\u4e3a\u90a3\u4e1c\u8033\u8001\u4eba\u8981\u6536\u745c\u513f\u4e3a\u5f92\uff0c\u624d\u5f15\u8d77\u4e8c\u54e5\u7684\u6740\u673a\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u5c0f\u5144\u51e0\u7ecf\u5fd6\u601d\uff0c\u63a8\u60f3\u90a3\u4e1c\u8033\u8001\u4eba\uff0c\u5c31\u662f\u5251\u795e\u9648\u5927\u4fa0\u7684\u5316\u8eab\uff0c\u9664\u4e86\u5251\u795e\u4e4b\u5916\uff0c\u5f53\u4eca\u4e4b\u4e16\uff0c\u51b3\u65e0\u7b2c\u4e8c\u4eba\u80fd\u591f\u5728\u6781\u77ed\u7684\u65f6\u65e5\u4e2d\uff0c\u4f20\u6388\u51fa\u90a3\u7b49\u7cbe\u7edd\u65e0\u4f26\u7684\u5251\u62db\uff0c\u4f7f\u745c\u513f\u5236\u670d\u4e86\u9b54\u5200\u7530\u8fdc\u2026\u2026\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u4e1c\u8033\u5408\u4e3a\u9648\uff0c\u4e0d\u4f1a\u9519\u4e86\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u5927\u7ea6\u662f\u90a3\u4f4d\u9648\u5927\u4fa0\u77a7\u51fa\u65b9\u4e8c\u5f1f\u53ef\u7591\u4e4b\u5904\uff0c\u544a\u8bc9\u4e86\u745c\u513f\uff0c\u745c\u513f\u4e0d\u5c0f\u5fc3\u9732\u51fa\u53e3\uff0c\u65b9\u4e8c\u5f1f\u624d\u52a8\u4e86\u6740\u4eba\u706d\u53e3\u4e4b\u5fc3\u2026\u2026\u201d \u3000\u3000\u4ef0\u8d77\u8138\u6765\uff0c\u957f\u957f\u5730\u5410\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5982\u82e5\u8fd9\u5176\u95f4\uff0c\u679c\u6709\u5185\u60c5\uff0c\u745c\u513f\u4ece\u5251\u795e\u5b66\u5f97\u6b66\u529f\uff0c\u5fc5\u7136\u4f1a\u67e5\u660e\u6b64\u4e8b\u3002\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e0d\u8fc7\uff0c\u745c\u513f\u867d\u4ece\u826f\u5e08\uff0c\u4f46\u4e5f\u975e\u4e09\u4e24\u5e74\u53ef\u5f97\u771f\u4f20\uff0c\u54b1\u4eec\u4e0d\u80fd\u8ba9\u745c\u513f\u4e60\u5f97\u7edd\u6280\u4e4b\u540e\u518d\u6765\u8ffd\u67e5\u6b64\u4e8b\u3002\u201d \u3000\u3000\u90d1\u5927\u4fa0\u9053\uff1a\u201c\u5c0f\u5144\u4e5f\u662f\u8fd9\u822c\u60f3\u6cd5\uff0c\u6211\u4eec\u4e2d\u5dde\u4e09\u4fa0\uff0c\u884c\u4fa0\u6c5f\u6e56\uff0c\u4e3a\u4eba\u5356\u547d\uff0c\u5982\u4eca\u4e8b\u5230\u4e86\u54b1\u4eec\u81ea\u5df1\u5144\u5f1f\u5934\u4e0a\uff0c\u81ea\u7136\u8981\u67e5\u4e00\u4e2a\u6c34\u843d\u77f3\u51fa\u3002\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u6b64\u4e8b\u5982\u4e0d\u67e5\u660e\uff0c\u5c0f\u5f1f\u66f4\u6709\u7740\u5e2d\u96be\u5b89\u6795\uff0c\u98df\u4e0d\u7518\u5473\u7684\u611f\u89c9\uff0c\u8981\u67e5\uff0c\u54b1\u4eec\u5c31\u7acb\u65f6\u52a8\u624b\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u67e5\u660e\u6b64\u4e8b\uff0c\u4e0d\u5916\u4e24\u9014\uff0c\u4e00\u662f\u627e\u5230\u65b9\u5144\u5f1f\uff0c\u76f4\u63a5\u95ee\u4ed6\u5185\u60c5\uff0c\u81ea\u7136\u8fd9\u662f\u6700\u4e3a\u7b80\u4fbf\u7684\u6cd5\u5b50\uff0c\u4e0d\u8fc7\u8fd9\u6cd5\u5b50\u770b\u6765\u5bb9\u6613\uff0c\u884c\u6765\u751a\u96be\u3002\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4f60\u4e8c\u54e5\u65e2\u7136\u5f53\u9762\u548c\u6211\u7edd\u4ea4\uff0c\u5df2\u7ecf\u662f\u6beb\u65e0\u60c5\u610f\uff0c\u4ed6\u5fc3\u4e2d\u5b9a\u4e5f\u6653\u5f97\u54b1\u4eec\u4e0d\u4f1a\u8f7b\u6613\u653e\u8fc7\u6b64\u4e8b\uff0c\u5fc5\u7136\u8981\u5168\u529b\u8ffd\u67e5\uff0c\u5929\u6daf\u6d77\u89d2\uff0c\u4ed6\u5982\u6709\u5fc3\u9003\u907f\u54b1\u4eec\uff0c\u54b1\u4eec\u53c8\u5230\u4f55\u5904\u80fd\u5bfb\u5230\u4ed6\u5462\uff1f\u201d \u3000\u3000\u957f\u957f\u53f9\u4e00\u53e3\u6c14\uff0c\u63a5\u9053\uff1a\u201c\u5c31\u7b97\u627e\u5230\u4e86\u4ed6\uff0c\u4ed6\u4e5f\u4e0d\u80af\u8bf4\u51fa\u5185\u60c5\u3002\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u8fd9\u4e9b\u5e74\u6765\uff0c\u4e8c\u54e5\u4e8c\u5ac2\u4e00\u76f4\u662f\u4e00\u5f80\u60c5\u6df1\uff0c\u6211\u60f3\u4ed6\u5b9a\u7136\u4e0d\u4f1a\u5f03\u4e8c\u5ac2\u4e8e\u4e0d\u987e\uff0c\u5982\u82e5\u54b1\u4eec\u80fd\u5546\u5f97\u4e8c\u5ac2\u540c\u610f\uff0c\u4ee5\u4e8c\u5ac2\u4f5c\u9975\uff0c\u6216\u4f7f\u4e8c\u54e5\u4e0a\u94a9\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u77f3\u4fca\u4e0d\u95ee\u90d1\u5927\u521a\u6709\u8bdd\uff0c\u5fcd\u4e0d\u4f4f\u63a5\u9053\uff1a\u201c\u5927\u54e5\u8ba4\u4e3a\u5c0f\u5f1f\u4e4b\u7b56\u5982\u4f55\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u56de\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u65b9\u592b\u4eba\u9760\u5728\u5927\u6811\u4e4b\u4e0a\uff0c\u4f3c\u662f\u5728\u8fd0\u6c14\u8c03\u606f\uff0c\u65b9\u73de\u7d27\u504e\u5728\u6bcd\u4eb2\u8eab\u4fa7\uff0c\u795e\u60c5\u95f4\u4e00\u7247\u51dd\u91cd\u3002 \u3000\u3000\u679c\u7136\uff0c\u4ed6\u7ae5\u7a1a\u7684\u5fc3\u7075\u4e4b\u4e2d\uff0c\u5df2\u7136\u53d7\u4e86\u6781\u5927\u7684\u521b\u4f24\u3002 \u3000\u3000\u90d1\u5927\u521a\u7f13\u7f13\u5730\u56de\u8fc7\u5934\u6765\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4e09\u5f1f\uff0c\u5c0f\u5144\uff0c\u60f3\u95ee\u4f60\u4e00\u4ef6\u4e8b\uff0c\u4f60\u8981\u636e\u5b9e\u56de\u7b54\u3002\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4f60\u4e8c\u5ac2\u957f\u5f97\u5982\u4f55\uff1f\u4e0d\u8981\u7d27\uff0c\u4f60\u636e\u5b9e\u800c\u8a00\u2026\u2026\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u4e8c\u5ac2\u4e4b\u7f8e\uff0c\u53ef\u5f53\u5f97\u7edd\u4e16\u7389\u4eba\u4e4b\u79f0\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u5e73\u5e38\u4e4b\u65f6\uff0c\u5c0f\u5144\u672a\u66fe\u7559\u5fc3\uff0c\u53ea\u89c9\u5f97\u4f60\u4e8c\u5ac2\u5f88\u7f8e\uff0c\u4f46\u4eca\u65e5\u4ed4\u7ec6\u77a7\u770b\u4e4b\u4e0b\uff0c\u5c0f\u5144\u624d\u53d1\u89c9\u4f60\u4e8c\u5ac2\u4e0d\u4f46\u5f88\u7f8e\uff0c\u800c\u4e14\u7f8e\u5f97\u52a8\u4eba\u3002\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u5927\u54e5\u4e4b\u610f\u2026\u2026\u201d \u3000\u3000\u90d1\u5927\u521a\u7f13\u7f13\u4ed6\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u636e\u5b9e\u800c\u8a00\uff0c\u4e0d\u7528\u987e\u8651\uff0c\u5982\u82e5\u662f\u4f60\uff0c\u662f\u5426\u4f1a\u4e3a\u4f60\u4e8c\u5ac2\u4e4b\u7f8e\u52a8\u5fc3\uff1f\u201d \u3000\u3000\u77f3\u4fca\u6da8\u5f97\u6ee1\u8138\u901a\u7ea2\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u8fd9\u4e2a\uff0c\u5c0f\u5f1f\u5b9e\u5728\u5f88\u96be\u8bf4\u5f97\u51fa\u53e3\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4e0d\u59a8\uff0c\u5c0f\u5144\u5728\u6c42\u8bc1\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u5c0f\u5f1f\u5c31\u76f4\u8bf4\u4e86\u2026\u2026\u201d\u8bed\u58f0\u5fae\u5fae\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5982\u82e5\u65b9\u592b\u4eba\u4e0d\u662f\u4e8c\u54e5\u7684\u592b\u4eba\uff0c\u5c0f\u5f1f\u5c06\u4e3a\u4e8c\u5ac2\u7684\u7f8e\u8272\u6240\u52a8\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u795e\u8272\u8083\u7136\u4ed6\u8bf4\u9053\uff1a\u201c\u5c0f\u5144\u4eca\u65e5\u4ed4\u7ec6\u77a7\u4e86\u4f60\u4e8c\u5ac2\u7684\u59ff\u8272\uff0c\u4e5f\u4e0d\u7981\u6709\u6b64\u611f\u89c9\u3002\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u5927\u54e5\u4e5f\u6709\u6b64\u611f\uff1f\u201d\u90d1\u5927\u521a\u9053\uff1a\u201c\u54b1\u4eec\u660e\u77e5\u5979\u662f\u4f60\u4e8c\u54e5\u7684\u592b\u4eba\uff0c\u5fc3\u4e2d\u4ea6\u96be\u514d\u4e3a\u5979\u7684\u59ff\u8272\u6240\u611f\uff0c\u5982\u662f\u548c\u5979\u6beb\u65e0\u60c5\u4e49\u4e4b\u4eba\uff0c\u81ea\u7136\u662f\u96be\u514d\u4e3a\u5979\u7f8e\u8272\u6240\u52a8\u4e86\u3002\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u5927\u54e5\u4e4b\u610f\uff0c\u53ef\u662f\u8bf4\u6709\u4eba\u4e3a\u4e86\u4e8c\u5ac2\u7684\u7f8e\u8272\u5bb3\u4e86\u4e8c\u54e5\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u7ea2\u989c\u8584\u547d\uff0c\u7f8e\u8272\u7978\u6c34\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u5c0f\u5144\u5b9e\u662f\u60f3\u4e0d\u51fa\uff0c\u522b\u4eba\u4e3a\u4ec0\u4e48\u8981\u5bb3\u4e8c\u5f1f\uff0c\u800c\u4e14\u5bb3\u4ed6\u4e4b\u540e\uff0c\u5192\u4ed6\u4e4b\u540d\uff0c\u53c8\u4ecd\u7136\u7ef4\u6301\u548c\u54b1\u4eec\u7684\u4ea4\u5f80\u3002\u201d \u3000\u3000\u77f3\u4fca\u70b9\u70b9\u5934\u9053\uff1a\u201c\u5927\u54e5\u8bf4\u7684\u6709\u7406\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u6b64\u523b\uff0c\u4f60\u4e8c\u5a5a\u60c5\u7eea\u672a\u5c1a\u5e73\u590d\uff0c\u5f85\u5979\u60c5\u7eea\u6062\u590d\u6b63\u5e38\u4e4b\u540e\uff0c\u54b1\u4eec\u518d\u4ed4\u7ec6\u5730\u95ee\u5979\u3002\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u6211\u770b\u4ed6\u4eec\u592b\u59bb\u60c5\u6df1\uff0c\u5982\u6b64\u5927\u53d8\u4e4b\u4e0b\uff0c\u53ea\u4f2f\u4e8c\u5ac2\u7684\u60c5\u7eea\u4e00\u65f6\u95f4\u4e0d\u6613\u5e73\u590d\u8fc7\u6765\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4e5f\u8bb8\u4f60\u4e8c\u5ac2\u53d7\u6b64\u6253\u51fb\u4e4b\u540e\uff0c\u80fd\u56de\u60f3\u8d77\u5f88\u591a\u4e8b\u60c5\u6765\u3002\u201d \u3000\u3000\u8c08\u8bdd\u4e4b\u95f4\u770b\u89c1\u65b9\u592b\u4eba\u7f13\u7f13\u5730\u7ad9\u8d77\u8eab\u5b50\u3002 \u3000\u3000\u8f6c\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u65b9\u592b\u4eba\u4e00\u624b\u6309\u7740\u989d\u89d2\uff0c\u8885\u8885\u5a1c\u5a1c\uff0c\u5730\u884c\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u65e5\u5149\u7167\u5c04\u4e4b\u4e0b\uff0c\u53ea\u89c1\u5979\u8138\u4e0a\u6cea\u75d5\u72b9\u5b58\uff0c\u4e00\u526f\u695a\u695a\u53ef\u601c\u7684\u795e\u6001\u3002 \u3000\u3000\u51c4\u51c9\u795e\u60c5\u4e2d\uff0c\u522b\u6709\u4e00\u756a\u52a8\u4eba\u7684\u98ce\u59ff\u3002 \u3000\u3000\u90d1\u5927\u521a\u8f7b\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5f1f\u59b9\uff0c\u597d\u4e00\u4e9b\u5417\uff1f\u201d \u3000\u3000\u65b9\u592b\u4eba\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u597d\u4e9b\u4e86\uff0c\u591a\u8c22\u5927\u4f2f\u5173\u6000\uff0c\u2026\u2026\u201d\u9eef\u7136\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u73b0\u5728\uff0c\u5927\u4f2f\u548c\u4e09\u5f1f\u6709\u4f55\u51c6\u5907\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u54b1\u4eec\u5148\u627e\u4e00\u5904\u4f4f\u5bbf\u4e4b\u5730\uff0c\u5f1f\u59b9\u517b\u606f\u4e24\u65e5\uff0c\u518d\u8bbe\u6cd5\u627e\u5bfb\u65b9\u4e8c\u5f1f\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u5e0c\u671b\u80fd\u65e9\u4e9b\u627e\u5230\u4ed6\uff0c\u95ee\u4e2a\u660e\u2026\u2026\u201d\u5979\u4f3c\u662f\u8a00\u672a\u5c3d\u610f\uff0c\u4f46\u5374\u7a81\u7136\u4f4f\u53e3\u3002 \u3000\u3000\u90d1\u5927\u521a\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5c0f\u5144\u4e86\u89e3\u5f1f\u59b9\u6b64\u523b\u7684\u5fc3\u60c5\uff0c\u4f46\u4e8b\u60c5\u5df2\u7ecf\u53d1\u751f\uff0c\u5f1f\u59b9\u5fc5\u5f97\u9547\u9759\uff0c\u9759\u800c\u8651\uff0c\u8651\u800c\u5f97\uff0c\u65b9\u4e8c\u5f1f\u8fd9\u756a\u51fa\u4eba\u610f\u5916\u7684\u884c\u5f84\u51b3\u4e0d\u7b80\u5355\uff0c\u5929\u6daf\u832b\u832b\uff0c\u56db\u6d77\u8fbd\u9614\uff0c\u4ed6\u5982\u6709\u5fc3\u9003\u907f\u54b1\u4eec\uff0c\u54b1\u4eec\u53c8\u5982\u4f55\u80fd\u591f\u627e\u5230\u4ed6\u5462\uff1f\u201d \u3000\u3000\u65b9\u592b\u4eba\u9053\uff1a\u201c\u7167\u5927\u54e5\u8bf4\u6cd5\uff0c\u54b1\u4eec\u65e0\u671b\u627e\u5230\u4ed6\u4e86\u3002\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u5e0c\u671b\u5012\u6709\uff0c\u4f46\u8981\u4e8c\u5ac2\u5e2e\u5fd9\u4e86\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u548c\u6211\u5173\u7cfb\u6700\u5927\uff0c\u7c89\u8eab\u788e\u9aa8\u5728\u6240\u4e0d\u60dc\uff0c\u4e09\u5f1f\u8fd8\u5ba2\u6c14\u4ec0\u4e48\u3002\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u521a\u624d\u5c0f\u5f1f\u548c\u5927\u54e5\u5546\u91cf\uff0c\u89c9\u5f97\u8ffd\u5bfb\u4e8c\u54e5\u5927\u6e3a\u832b\uff0c\u56e0\u6b64\uff0c\u4e0d\u5982\u8ba9\u4ed6\u627e\u6211\u4eec\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u6ceb\u7136\u8bf4\u9053\uff1a\u201c\u4ed6\u5fff\u800c\u79bb\u53bb\uff0c\u5bf9\u6211\u4eec\u552f\u6050\u907f\u4e4b\u4e0d\u53ca\uff0c\u600e\u4f1a\u518d\u6765\u627e\u6211\u4eec\u5462\uff1f\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u4ed6\u80fd\u548c\u5927\u54e5\u548c\u5c0f\u5f1f\u65ad\u4e49\uff0c\u4f46\u5374\u4e0d\u80fd\u5bf9\u4e8c\u5ac2\u5fd8\u60c5\uff0c\u53ea\u8981\u4ed6\u77e5\u6653\u4e8c\u5ac2\u7684\u53bb\u5904\uff0c\u53c8\u77e5\u9053\u6211\u548c\u5927\u54e5\u4e0d\u5728\uff0c\u5b9a\u7136\u4f1a\u6765\u770b\u4e8c\u5ac2\u7684\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u6c89\u541f\u4e86\u4e00\u9635\u9053\uff1a\u201c\u8fd9\u4e9b\u5e74\u6765\uff0c\u4ed6\u5bf9\u6211\u4e00\u76f4\u662f\u7231\u62a4\u5907\u81f3\uff0c\u8fd9\u6cd5\u5b50\u4e5f\u8bb8\u6709\u4e9b\u5e0c\u671b\u2026\u2026\u201d\u957f\u957f\u5410\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u95ee\u9898\u662f\u8981\u4ed6\u5982\u4f55\u76f8\u4fe1\u5927\u54e5\u548c\u4e09\u5f1f\u5df2\u7ecf\u79bb\u5f00\u4e86\u6211\u4eec\u6bcd\u5b50\u5462\uff1f\u201d \u3000\u3000\u77f3\u4fca\u9053\uff1a\u201c\u4e8c\u5ac2\u65e2\u7136\u540c\u610f\uff0c\u4f59\u4e0b\u7684\u4e8b\u60c5\uff0c\u5c31\u597d\u529e\u4e86\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u9053\uff1a\u201c\u4e09\u5f1f\u548c\u5927\u54e5\u5b89\u6392\u5427\uff0c\u53ea\u8981\u80fd\u89c1\u5230\u5929\u6210\uff0c\u59be\u8eab\u65e0\u4e0d\u4ece\u547d\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u5f1f\u59b9\u65e2\u80af\u5408\u4f5c\uff0c\u54b1\u4eec\u7684\u6210\u529f\u5e0c\u671b\u5c31\u5f88\u5927\u4e86\uff0c\u5f1f\u59b9\u5e26\u7740\u73de\u513f\u56de\u6545\u5c45\uff0c\u5c0f\u5144\u548c\u4e09\u5f1f\u6613\u5bb9\u6697\u4e2d\u76f8\u968f\uff0c\u4ed6\u5b64\u72ec\u4e00\u4eba\uff0c\u8033\u76ee\u4e0d\u591a\uff0c\u7eb5\u7136\u5fc3\u4e2d\u6709\u7591\uff0c\u4e5f\u662f\u65e0\u6cd5\u786e\u5b9a\uff0c\u65f6\u65e5\u4e00\u4e45\uff0c\u81ea\u7136\u96be\u514d\u73b0\u8eab\u548c\u5f1f\u59b9\u76f8\u4f1a\u4e86\u3002\u201d \u3000\u3000\u65b9\u592b\u4eba\u9053\uff1a\u201c\u5f1f\u59b9\u4e00\u5207\u5c0a\u547d\u3002\u201d\u5e26\u7740\u65b9\u73de\uff0c\u767b\u8f66\u8d76\u8def\uff0c\u76f4\u5954\u6545\u5c45\u3002 \u3000\u3000\u90d1\u5927\u521a\u548c\u77f3\u4fca\u6613\u5bb9\u6539\u88c5\uff0c\u6697\u4e2d\u76f8\u968f\u800c\u884c\u3002 \u3000\u3000\u4e14\u8bf4\u65b9\u96ea\u5b9c\u6ee1\u6000\u60b2\u75db\uff0c\u7eb5\u9a91\u800c\u884c\uff0c\u4f9d\u7167\u5730\u56fe\u4e0a\u5212\u51fa\u7684\u7ebf\u8def\uff0c\u63a2\u9053\u884c\u8fdb\u3002\u5f85\u5929\u8fd1\u9ec4\u660f\u65f6\u5206\uff0c\u5df2\u7136\u5230\u4e86\u4e00\u9053\u6025\u6d41\u4e4b\u524d\u3002 \u3000\u3000\u4ed6\u5c0a\u7167\u90a3\u8001\u4eba\u51fd\u4e2d\u7684\u5429\u5490\uff0c\u53d6\u51fa\u56fe\u6848\uff0c\u53c8\u4ed4\u7ec6\u77a7\u4e86\u4e00\u9635\uff0c\u7262\u8bb0\u4e8e\u5fc3\uff0c\u7136\u540e\uff0c\u6643\u7740\u706b\u647a\u5b50\uff0c\u70e7\u4e86\u56fe\u6848\uff0c\u6295\u5165\u90a3\u6025\u6d41\u4e4b\u4e2d\u3002 \u3000\u3000\u8fd9\u662f\u7fa4\u5c71\u4e2d\u4e07\u9053\u9752\u6cc9\u6c47\u96c6\u7684\u4e00\u9053\u6eaa\u6d41\uff0c\u6c34\u52bf\u6e4d\u6025\uff0c\u4f46\u5374\u6e05\u53ef\u89c1\u5e95\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u884c\u8fd1\u6eaa\u65c1\uff0c\u4ed4\u7ec6\u5730\u6253\u91cf\u8fc7\u56db\u5468\u5f62\u52bf\u548c\u56fe\u6848\u4e0a\u6240\u793a\uff0c\u5b8c\u5168\u662f\u543b\u5408\u7684\uff0c\u624d\u62d4\u51fa\u4f69\u5251\uff0c\u5c31\u5728\u6eaa\u6d41\u5cb8\u7554\uff0c\u65a9\u4e86\u5f88\u591a\u6811\u5e72\uff0c\u626f\u7834\u968f\u8eab\u5e26\u6765\u7684\u8863\u670d\uff0c\u628a\u6811\u5e72\u7262\u7262\u6346\u8d77\u3002\u7b49\u4ed6\u4f5c\u6210\u4e86\u4e00\u5f20\u957f\u53ef\u5bb9\u8eab\u7684\u6728\u6392\u65f6\uff0c\u5df2\u7136\u662f\u843d\u65e5\u897f\u4e0b\u7e41\u661f\u6ee1\u5929\u4e86\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u89e3\u4e0b\u9a6c\u978d\uff0c\u8f7b\u8f7b\u5728\u9a6c\u80cc\u4e0a\u62cd\u4e86\u4e00\u638c\uff0c\u591c\u8272\u4e2d\u5065\u9a6c\u5982\u98de\u800c\u53bb\u3002 \u3000\u3000\u653e\u8d70\u4e86\u5065\u9a6c\uff0c\u65b9\u96ea\u5b9c\u53c8\u628a\u9a6c\u978d\u4e4b\u7269\u6295\u5165\u6c34\u4e2d\uff0c\u987a\u6d41\u9010\u6ce2\u800c\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e22\u4e86\u6240\u6709\u53ef\u80fd\u7559\u4e0b\u75d5\u8ff9\u4e4b\u7269\uff0c\u7136\u540e\u6574\u6574\u8863\u670d\uff0c\u63a8\u52a8\u81ea\u5df1\u6346\u624e\u7684\u6728\u6392\uff0c\u6295\u5165\u6c34\u4e2d\u7eb5\u8eab\u800c\u4e0a\u3002\u6025\u6d41\u8fc5\u5feb\uff0c\u4ed6\u8fd8\u672a\u5750\u597d\u8eab\u5b50\uff0c\u90a3\u6728\u6392\u5df2\u7136\u4e3a\u6025\u6d41\u51b2\u51fa\u4e86\u4e24\u4e08\u5f00\u5916\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u6b21\u65b0\u5947\u7684\u884c\u7a0b\uff0c\u671b\u7740\u5728\u6fc0\u6d41\u4e0a\u7684\u6728\u6392\uff0c\u65b9\u96ea\u5b9c\u6709\u7740\u832b\u7136\u65e0\u63aa\u7684\u611f\u89c9\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u5e0c\u671b\u6211\u8fd9\u6728\u6392\u80fd\u591f\u7262\u56fa\u4e00\u4e9b\uff0c\u4e0d\u8981\u88ab\u6fc0\u6d41\u51b2\u6563\u3002\u201d\u53ea\u89c9\u5f97\u90a3\u6fc0\u6d41\u6108\u6765\u6108\u5feb\uff0c\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u4e5f\u6108\u6765\u6108\u7d27\u3002 \u3000\u3000\u7a81\u7136\u95f4\u773c\u524d\u4e00\u9ed1\uff0c\u8fde\u661f\u5149\u4e5f\u9690\u5931\u4e0d\u89c1\u3002\u539f\u6765\uff0c\u90a3\u6728\u6392\u968f\u7740\u6fc0\u6d41\uff0c\u8fdb\u5165\u4e86\u4e00\u9053\u5c71\u8c37\u4e4b\u4e2d\uff0c\u8c37\u4e2d\u6797\u6728\u53c2\u5929\uff0c\u63a9\u906e\u53bb\u4e86\u5929\u4e0a\u7684\u661f\u5149\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u6781\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u90a3\u8c37\u4e2d\u7684\u6c34\u9053\uff0c\u53c8\u7a84\u4e86\u5f88\u591a\uff0c\u53ea\u6709\u4e00\u4e08\u591a\u5bbd\uff0c\u6c34\u52bf\u4e5f\u66f4\u89c1\u6e4d\u6025\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4fef\u5728\u6728\u6392\u4e4b\u4e0a\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u6c34\u52bf\u5982\u6b64\u5feb\u901f\uff0c\u82e5\u9047\u4e0a\u4e00\u4e2a\u9661\u5f2f\uff0c\u8fd9\u6728\u6392\u52bf\u5fc5\u8981\u649e\u4e0a\u77f3\u58c1\u4e0d\u53ef\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u5ff5\u5934\u8fd8\u672a\u8f6c\u5b8c\uff0c\u8033\u9645\u95f4\u5df2\u54cd\u8d77\u4e86\u4e00\u58f0\u84ec\u7136\u5927\u9707\u3002\u53ea\u89c9\u8eab\u4e0a\u4e00\u51c9\uff0c\u5168\u8eab\u6d78\u5165\u6c34\u4e2d\u3002 \u3000\u3000\u6562\u60c5\u6b63\u5982\u4ed6\u5fc3\u4e2d\u6240\u754f\uff0c\u6728\u6392\u649e\u4e0a\u4e86\u77f3\u58c1\uff0c\u9707\u65ad\u5e03\u7d22\uff0c\u6728\u6392\u788e\u5f00\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4f38\u624b\u4e00\u6293\uff0c\u62b1\u4f4f\u4e86\u4e24\u6839\u6728\u5e72\uff0c\u7d27\u7d27\u4e0d\u653e\uff0c\u5168\u8eab\u4fb5\u5165\u6c34\u4e2d\uff0c\u53ea\u9732\u51fa\u4e00\u4e2a\u8111\u888b\uff0c\u987a\u7740\u6fc0\u6d41\u800c\u4e0b\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u6c34\u52bf\u4e00\u7f13\uff0c\u90a3\u5954\u817e\u4e4b\u52bf\uff0c\u4e5f\u7a81\u7136\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u6447\u5934\uff0c\u7741\u773c\u671b\u53bb\uff0c\u53ea\u89c1\u6c34\u5149\u6d6e\u52a8\uff0c\u4e00\u6c60\u7e41\u661f\uff0c\u98d8\u6765\u8361\u53bb\uff0c\u6562\u60c5\uff0c\u6025\u6d41\u5728\u6b64\u805a\u6210\u4e86\u4e00\u4e2a\u5927\u6f6d\u3002 \u3000\u3000\u534a\u8f6e\u660e\u6708\uff0c\u8131\u51fa\u6d6e\u4e91\uff0c\u6e05\u8f89\u7167\u8000\u7740\u6e56\u9762\uff0c\u78a7\u6f6d\u5370\u6708\uff0c\u5149\u6ce2\u7cbc\u7cbc\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u6d41\u901f\u5f88\u5fae\uff0c\u65b9\u96ea\u5b9c\u6323\u624e\u7740\u9a91\u4e0a\u6728\u5e72\uff0c\u4ef0\u5929\u957f\u5578\u4e00\u58f0\u3002 \u3000\u3000\u5578\u58f0\u843d\u540e\u4e0d\u4e45\uff0c\u7a81\u7136\u89c1\u706b\u5149\u95ea\u52a8\uff0c\u4e00\u8258\u5c0f\u821f\uff0c\u76f4\u9a70\u800c\u6765\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e0d\u65f6\u53d1\u51fa\u5578\u58f0\uff0c\u544a\u8bc9\u90a3\u821f\u65b9\u4f4d\u3002\u4e0d\u5927\u5de5\u592b\uff0c\u5c0f\u8239\u9a70\u8fd1\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e0d\u7981\u5403\u4e86\u60ca\u3002\u539f\u6765\uff0c\u90a3\u5c0f\u821f\u4e0a\uff0c\u7ad9\u7740\u4e00\u5934\u5f62\u5982\u4eba\u9ad8\u7684\u5de8\u733f\uff0c\u53cc\u624b\u628a\u6a79\uff0c\u77aa\u7740\u4e00\u53cc\u706b\u773c\uff0c\u671b\u524d\u65b9\u96ea\u5b9c\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u5c0f\u821f\u4e0a\u53d1\u51fa\u4e86\u4e00\u58f0\u6e05\u8106\u7684\u547c\u53eb\uff0c\u9053\uff1a\u201c\u4e0a\u8239\u6765\u5427\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5403\u4e86\u4e00\u60ca\uff0c\u6697\u9053\uff1a\u201c\u96be\u9053\u90a3\u5de8\u733f\u4e5f\u4f1a\u53e3\u5410\u4eba\u8a00\u4e0d\u6210\uff1f\u201d \u3000\u3000\u4ed4\u7ec6\u7684\u770b\u53bb\uff0c\u53ea\u89c1\u90a3\u5de8\u733f\u8eab\u540e\uff0c\u4e00\u5f20\u67b6\u7af9\u4e4b\u4e0a\uff0c\u7ad9\u7740\u4e00\u53cc\u901a\u4f53\u96ea\u767d\u7684\u9e66\u9e49\uff0c\u90a3\u5de8\u733f\u77aa\u7740\u4e00\u5bf9\u706b\u773c\u91d1\u775b\uff0c\u671b\u7740\u65b9\u96ea\u5b9c\u51fa\u795e\u826f\u4e45\u3001\u7a81\u7136\u4f38\u51fa\u6bdb\u8338\u8338\u7684\u624b\u6765\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u770b\u4ed6\u4f3c\u65e0\u6076\u610f\uff0c\u4e5f\u4f38\u51fa\u624b\u53bb\u3002 \u3000\u3000\u90a3\u5de8\u733f\u529b\u6c14\u5f88\u5927\uff0c\u4e00\u62ac\u624b\uff0c\u751f\u751f\u628a\u65b9\u96ea\u5b9c\u63d0\u4e0a\u5c0f\u821f\uff0c\u653e\u4e0b\u65b9\u96ea\u5b9c\uff0c\u6447\u6a79\u8f6c\u8239\u5934\uff0c\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5728\u6c34\u4e2d\u6ce1\u4e86\u5927\u534a\u591c\uff0c\u6323\u624e\u5728\u751f\u6b7b\u8fb9\u7f18\u65f6\uff0c\u751f\u547d\u66ff\u529b\u987f\u53d1\uff0c\u8fd8\u4e0d\u89c9\u600e\u6837\uff0c\u767b\u4e0a\u5c0f\u821f\uff0c\u5df2\u8fbe\u5b89\u5168\u4e4b\u5883\uff0c\u7d27\u5f20\u7684\u7cbe\u795e\uff0c\u4ea6\u4e3a\u4e4b\u677e\u61c8\u4e0b\u6765\uff0c\u987f\u89c9\u4e00\u9635\u5bd2\u610f\uff0c\u7531\u5fc3\u5e95\u76f4\u8fbe\u5168\u8eab\uff0c\u4e0d\u81ea\u4e3b\u6253\u4e86\u51e0\u4e2a\u51b7\u6218\u3002 \u3000\u3000\u4f46\u4ed6\u4ecd\u7136\u88ab\u4e00\u80a1\u597d\u5947\u4e4b\u5fc3\u5438\u5f15\u7740\uff0c\u7559\u5fc3\u7740\u90a3\u5c0f\u821f\u7684\u884c\u7ecf\u4e4b\u5730\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u5c0f\u821f\u884c\u8fd1\u60ac\u5d16\u4e4b\u540e\uff0c\u7a81\u7136\u8f6c\u5165\u4e00\u9053\u5f88\u5c0f\u7684\u5c71\u8c37\u4e4b\u4e2d\u3002 \u3000\u3000\u90a3\u5c71\u8c37\u53ea\u53ef\u5bb9\u8fd9\u5c0f\u821f\u901a\u884c\uff0c\u4e24\u9762\u90fd\u662f\u9ad8\u8038\u4e91\u9704\u7684\u77f3\u58c1\u3002 \u3000\u3000\u5de8\u733f\u64cd\u4f5c\u6280\u672f\u5341\u5206\u9ad8\u660e\uff0c\u5c0f\u821f\u5728\u72ed\u9053\u4e2d\u5212\u884c\uff0c\u7adf\u5f88\u987a\u5229\u901a\u8fc7\u3002 \u3000\u3000\u8d70\u5b8c\u72ed\u9053\u5f62\u52bf\u4e00\u53d8\uff0c\u6c34\u9762\u53c8\u5448\u5f00\u9614\u3002 \u3000\u3000\u62ac\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u6708\u5149\u76f4\u5c04\u800c\u5165\uff0c\u8fd8\u662f\u6708\u6302\u4e2d\u5929\u65f6\u5206\u3002 \u3000\u3000\u539f\u6765\uff0c\u8fd9\u6c34\u9053\u5c3d\u5904\uff0c\u5c71\u52bf\u5f62\u6210\u4e86\u4e00\u4e2a\u5706\u5468\uff0c\u7eb5\u6a2a\u56db\u4e08\uff0c\u9664\u4e86\u90a3\u6c34\u9053\u53e3\u5916\uff0c\u90fd\u662f\u5ced\u7acb\u7684\u5c71\u58c1\u3002 \u3000\u3000\u5de8\u733f\u628a\u5c0f\u821f\u505c\u5728\u4e00\u9762\u5c0f\u58c1\u95ee\uff0c\u58c1\u4e0a\u5782\u4e0b\u7684\u8001\u85e4\u7cfb\u597d\uff0c\u4f38\u624b\u6307\u7740\u5c71\u58c1\u3002 \u3000\u3000\u53ea\u542c\u90a3\u7ffc\u5212\u7a7a\uff0c\u90a3\u767d\u9e66\u9e49\u5df2\u5f53\u5148\u98de\u4e86\u8d77\u6765\uff0c\u76f4\u5411\u4e0a\u9762\u98de\u53bb\uff0c\u53e3\u4e2d\u5374\u4f20\u4e0b\u6e05\u8106\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u4e0a\u5c71\u53bb\uff0c\u4e0a\u5c71\u53bb\u3002\u201d \u3000\u3000\u4ed6\u867d\u80fd\u53e3\u5410\u4eba\u8a00\uff0c\u4f46\u7a76\u7adf\u975e\u4eba\uff0c\u65e0\u6cd5\u628a\u5fc3\u610f\u5b8c\u5168\u8868\u8fbe\u51fa\u6765\u3002 \u3000\u3000\u5728\u5de8\u733f\u624b\u52bf\uff0c\u767d\u9e66\u9e49\u5410\u8a00\u4e4b\u4e0b\uff0c\u65b9\u96ea\u5b9c\u4e8e\u662f\u660e\u767d\u4e86\u5b83\u4eec\u7684\u610f\u601d\u3002 \u3000\u3000\u51dd\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u90a3\u6cca\u821f\u6240\u5728\uff0c\u9690\u9690\u53ef\u89c1\u4e00\u6761\u53ef\u884c\u5c0f\u5f84\u3002 \u3000\u3000\u53ea\u542c\u547c\u7684\u4e00\u58f0\uff0c\u90a3\u5de8\u733f\u7a81\u7136\u8dc3\u4e0a\u77f3\u58c1\uff0c\u624b\u6500\u8db3\u767b\uff0c\u5411\u4e0a\u884c\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u770b\u90a3\u5ced\u58c1\u867d\u7136\u9669\u6076\u4f46\u53c8\u624b\u5374\u6709\u53ef\u6500\u4e4b\u5904\uff0c\u5f53\u4e0b\u632f\u8d77\u7cbe\u795e\uff0c\u8dc3\u4e0a\u77f3\u58c1\uff0c\u624b\u8db3\u5e76\u7528\uff0c\u5411\u4e0a\u884c\u53bb\u3002 \u3000\u3000\u4ed6\u624b\u8db3\u90fd\u5df2\u51bb\u6728\uff0c\u6500\u8e0f\u77f3\u800c\u4e0a\uff0c\u5b9e\u6709\u529b\u4e0d\u4ece\u5fc3\u4e4b\u611f\uff0c\u51e0\u6b21\u5931\u8db3\uff0c\u4f46\u5e78\u597d\u624b\u4e2d\u6293\u4f4f\u7684\u8001\u85e4\u751a\u7262\uff0c\u624d\u672a\u6454\u4e0b\u5ced\u58c1\u3002 \u3000\u3000\u62ac\u5934\u770b\u53bb\uff0c\u90a3\u9e66\u9e49\uff0c\u5de8\u733f\u90fd\u5df2\u8d70\u5f97\u4e0d\u77e5\u53bb\u5411\uff0c\u5bc2\u9759\u5ced\u7acb\u7684\u77f3\u58c1\u95f4\uff0c\u53ea\u6709\u81ea\u5df1\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u957f\u957f\u5410\u4e86\u4e00\u65e5\u6c14\uff0c\u9f13\u4f59\u52c7\u53c8\u5411\u4e0a\u9762\u722c\u53bb\u3002 \u3000\u3000\u8fd9\u9762\u5ced\u58c1\uff0c\u662f\u56db\u9762\u8038\u7acb\u7684\u5c71\u5cf0\u4e2d\u8f83\u4f4e\u7684\u4e00\u9762\uff0c\u4f46\u4e5f\u5728\u767e\u4e08\u4ee5\u4e0a\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u7528\u4e0a\u4e86\u5168\u8eab\u7684\u6c14\u529b\uff0c\u6500\u4e0a\u5c71\u5cf0\uff0c\u5df2\u7136\u7d2f\u7684\u7b4b\u75b2\u529b\u5c3d\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u6211\u4e14\u4f0f\u5728\u5730\u4e0a\u4f11\u606f\u4e00\u9635\uff0c\u518d\u627e\u8def\u800c\u884c\u3002\u201d \u3000\u3000\u54ea\u77e5\u4ed6\u75b2\u7d2f\u8fc7\u751a\uff0c\u8fd9\u4e00\u8d9f\u4e0b\uff0c\u7adf\u4e0d\u77e5\u4e0d\u89c9\u5730\u7761\u719f\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u9192\u6765\u65f6\uff0c\u53d1\u89c9\u81ea\u5df1\u6b63\u8eba\u5728\u4e00\u5f20\u6728\u69bb\u4e4b\u4e0a\uff0c\u4e00\u9635\u82ac\u82b3\u82b1\u6c14\uff0c\u6251\u5165\u9f3b\u4e2d\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u633a\u8eab\u800c\u8d77\uff0c\u56db\u4e0b\u6253\u91cf\u4e86\u4e00\u9635\uff0c\u53ea\u89c1\u81ea\u5df1\u505c\u8eab\u4e4b\u5730\uff0c\u662f\u4e00\u5ea7\u6253\u626b\u5f97\u5341\u5206\u5e72\u51c0\u7684\u8305\u820d\uff0c\u6728\u69bb\u4e0a\u94fa\u7740\u8f6f\u8349\uff0c\u8349\u4e0a\u94fa\u7740\u4e00\u5f20\u864e\u76ae\u8925\u5b50\u3002 \u3000\u3000\u5ba4\u4e2d\u7af9\u51e0\u7af9\u6905\uff0c\u4e00\u5c18\u4e0d\u67d3\u3002 \u3000\u3000\u7a97\u5916\u9633\u5149\u7167\uff0c\u5fae\u98ce\u5f90\u5f90\uff0c\u7a7f\u7a97\u800c\u5165\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u6b63\u5f85\u8d77\u8eab\u884c\u5411\u7a97\u5916\uff0c\u770b\u4e2a\u542c\u767d\uff0c\u7a81\u7136\u4e00\u9635\u8f7b\u5fae\u7684\u6b65\u5c65\u58f0\uff0c\u4f20\u5165\u8033\u9645\u3002 \u3000\u3000\u62ac\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u4e1c\u8033\u8001\u4eba\uff0c\u624b\u6267\u6756\uff0c\u7f13\u7f13\u884c\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u6025\u6025\u8dea\u4e86\u4e0b\u53bb\uff0c\u9053\uff1a\u201c\u53e9\u89c1\u5e08\u7236\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u70b9\u5934\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u8d77\u6765\uff0c\u54b1\u4eec\u5750\u4e0b\u8bf4\u5427\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u62dc\u7f62\u4e4b\u540e\uff0c\u7ad9\u8d77\u8eab\u5b50\uff0c\u4ed4\u7ec6\u5730\u770b\u4e86\u4e1c\u8033\u8001\u4eba\u4e00\u773c\uff0c\u4e0d\u7981\u5403\u4e86\u4e00\u60ca\u3002 \u3000\u3000\u53ea\u89c1\u4e1c\u8033\u8001\u4eba\u9762\u8272\u82cd\u767d\uff0c\u6570\u65e5\u4e0d\u89c1\uff0c\u4eba\u4f3c\u53c8\u6d88\u7626\u4e86\u8bb8\u591a\u3002 \u3000\u3000\u679c\u7136\uff0c\u4e1c\u8033\u8001\u4eba\u8fd9\u6570\u65e5\u4e2d\u6b63\u53d7\u75c5\u9b54\u56f0\u6270\u3002 \u3000\u3000\u4e1c\u8033\u8001\u4eba\u7af9\u6756\u70b9\u5730\uff0c\u7f13\u7f13\u884c\u5230\u4e00\u5f20\u6905\u4e0a\u5750\u4e86\u4e0b\u53bb\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4e3a\u5e08\u7684\u8fd9\u4e24\u65e5\u75c5\u52bf\u5927\u53d8\uff0c\u80fd\u591f\u89c1\u5230\u4f60\uff0c\u5f88\u662f\u610f\u5916\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6025\u9053\uff1a\u201c\u5e08\u7236\u73b0\u5728\u75c5\u52bf\u5982\u4f55\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u4e0d\u8981\u7d27\u4e86\uff0c\u53c8\u88ab\u6491\u4e86\u8fc7\u53bb\uff0c\u53ea\u8981\u4e0d\u518d\u5954\u8d70\u52b3\u52a8\uff0c\u8fd8\u53ef\u4ee5\u6d3b\u4e9b\u65e5\u5b50\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u53ea\u89c9\u5f97\u5fc3\u4e2d\u4e00\u9635\u9178\u695a\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u7236\uff0c\u5f1f\u5b50\u8fd8\u672a\u884c\u62dc\u5e08\u5927\u793c\u2026\u2026\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u6447\u6447\u5934\u9053\uff1a\u201c\u4e0d\u7528\u4e86\uff0c\u6211\u6536\u4f60\u4e3a\u5f92\uff0c\u53ea\u662f\u4e3a\u4e86\u4f20\u6211\u8863\u94b5\uff0c\u62dc\u5e08\u5927\u793c\u7684\u5f62\u5f0f\u4e3a\u5e08\u5e76\u4e0d\u653e\u5728\u5fc3\u4e0a\uff0c\u91cd\u8981\u7684\u662f\uff0c\u4f60\u8981\u5c3d\u4f60\u4e4b\u80fd\uff0c\u5b8c\u6210\u6211\u7684\u5fc3\u613f\uff0c\u5982\u4f60\u80fd\u591f\u7b54\u5141\uff0c\u6211\u5c31\u8ba4\u4f60\u4e3a\u5f92\uff0c\u5982\u662f\u4e0d\u80fd\u7b54\u5141\uff0c\u6211\u4e5f\u4e0d\u52c9\u5f3a\u4f60\uff0c\u4f60\u53ef\u4ee5\u5728\u8fd9\u91cc\u5c0f\u606f\u4e24\u65e5\uff0c\u6211\u9063\u7075\u733f\u9001\u4f60\u79bb\u6b64\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7f13\u7f13\u4ed6\u8bf4\u9053\uff1a\u201c\u5e08\u7236\u6709\u4f55\u5fc3\u613f\uff0c\u4e0d\u77e5\u53ef\u5426\u5148\u884c\u544a\u8bc9\u5f92\u513f\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u70b9\u70b9\u5934\u9053\uff1a\u201c\u95ee\u5f97\u597d\uff0c\u6211\u6ca1\u6709\u770b\u9519\u4f60\u2026\u2026\u201d\u8bed\u58f0\u987f\u4e86\u4e00\u987f\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u6211\u90a3\u5fc3\u613f\u975e\u5e38\u5b8f\u5927\uff0c\u4f60\u8981\u503e\u5c3d\u6bd5\u751f\u7684\u7cbe\u529b\uff0c\u4f7f\u5176\u5b9e\u73b0\uff0c\u4e00\u822c\u4eba\u90fd\u65e0\u6b64\u7b49\u5b9a\u529b\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7a81\u7136\u63a5\u53e3\u9053\uff1a\u201c\u5e08\u7236\u770b\u5f92\u513f\u884c\u5417\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u4e3a\u5e08\u4e5f\u96be\u80af\u5b9a\uff0c\u4e0d\u8fc7\uff0c\u4f60\u662f\u6211\u6240\u89c1\u4e4b\u4eba\u4e2d\uff0c\u552f\u4e00\u6709\u6b64\u53ef\u80fd\u7684\u4eba\uff0c\u4f46\u9053\u9ad8\u4e00\u5c3a\uff0c\u9b54\u9ad8\u4e00\u4e08\uff0c\u6211\u5982\u6d3b\u5728\u4e16\u4e0a\uff0c\u8fd8\u6709\u4eba\u53ca\u65f6\u63d0\u9192\u7ea0\u6b63\u4f60\uff0c\u53ef\u662f\u4e3a\u5e08\u7684\u65e0\u6cd5\u7b49\u4f60\u7ec3\u6210\uff0c\u5c31\u8981\u6b7b\u53bb\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u5e08\u7236\u75c5\u4e86\u5f88\u4e45\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u5341\u51e0\u5e74\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e08\u7236\u529f\u53c2\u9020\u5316\uff0c\u63a7\u5236\u751f\u6b7b\uff0c\u80fd\u62d6\u8fc7\u5341\u51e0\u5e74\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u518d\u591a\u62d6\u5ef6\u5341\u51e0\u5e74\u5462\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u54d1\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u8fd9\u5c82\u662f\u4eba\u529b\u80fd\u591f\u633d\u56de\u7684\u5417\uff1f\u751f\u8001\u75c5\u6b7b\uff0c\u5927\u81ea\u7136\u4e2d\u5fae\u5999\u7684\u6d88\u957f\u4e4b\u673a\uff0c\u4e3a\u5e08\u6709\u4f55\u667a\u80fd\uff0c\u80fd\u591f\u4e0e\u8fd9\u5927\u81ea\u7136\u4e2d\u6d88\u957f\u4e4b\u673a\u6297\u8861\uff1f\u201d \u3000\u3000\u8bed\u58f0\u7a81\u8f6c\u6c89\u91cd\uff0c\u51c4\u51c9\u5730\u63a5\u9053\uff1a\u201c\u4f46\u4e3a\u5e08\u80fd\u505a\u7684\u4e8b\u662f\uff0c\u627e\u4e00\u4e2a\u8d44\u8d28\u597d\u7684\u4eba\uff0c\u4f20\u6211\u7684\u8863\u94b5\uff0c\u80fd\u591f\u5c31\u4e0b\u4ee3\u65b0\u751f\u7684\u529b\u91cf\uff0c\u627e\u4e00\u4e2a\u66ff\u6211\u7684\u4eba\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u53ef\u662f\u5e08\u7236\u4e0d\u662f\u5e73\u5e38\u7684\u4eba\u554a\uff01\u4f60\u5df2\u62d6\u5ef6\u4e86\u5341\u51e0\u5e74\uff0c\u4e3a\u4f55\u4e0d\u80fd\u518d\u62d6\u4e9b\u65f6\u95f4\u5462\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u5341\u51e0\u5e74\u6765\uff0c\u6211\u5df2\u7ecf\u8017\u5c3d\u4e86\u6240\u6709\u7684\u5fc3\u795e\uff0c\u5df2\u5230\u6cb9\u5c3d\u706f\u67af\u4e4b\u5883\uff0c\u5b9e\u65e0\u6cd5\u518d\u62d6\u5ef6\u4e0b\u53bb\u4e86\uff0c\u5b69\u5b50\uff0c\u4f46\u4f60\u53ef\u4ee5\u7ee7\u8d77\u6211\u7684\u751f\u547d\uff0c\u6211\u6b7b\u4e86\u4e4b\u540e\uff0c\u4ecd\u6709\u4e00\u4e2a\u5251\u795e\u6d3b\u5728\u4eba\u95f4\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u5e08\u7236\uff0c\u8bf7\u8bf4\u4f60\u7acb\u4e0b\u7684\u5fc3\u613f\u5427\uff0c\u5f1f\u5b50\u4e5f\u8981\u60f3\u4e00\u60f3\uff0c\u662f\u5426\u80fd\u591f\u7b54\u5e94\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u795e\u8272\u9eef\u7136\u4ed6\u8bf4\u9053\uff1a\u201c\u6211\u8981\u4f60\u627f\u6211\u9057\u5fd7\uff0c\u7ec8\u751f\u4e00\u4e16\uff0c\u4e3a\u7ef4\u62a4\u6b66\u6797\u6b63\u4e49\u594b\u6597\uff0c\u4e3a\u5e08\u627f\u53d7\u4f60\u5e08\u7956\u7684\u795e\u5251\uff0c\u4e00\u751f\u4e2d\u867d\u65e0\u6b92\u8d8a\uff0c\u4f46\u6211\u4ee5\u672a\u5b8c\u6210\u4ed6\u8001\u4eba\u5bb6\u7684\u9057\u5fd7\u4e3a\u61be\uff0c\u5982\u4eca\u8981\u628a\u8fd9\u5e72\u65a4\u91cd\u4efb\u652f\u4ed8\u7ed9\u4f60\uff0c\u5e0c\u671b\u4f60\u80fd\u591f\u627f\u62c5\u91cd\u4efb\uff0c\u5b8c\u6210\u6211\u672a\u5b8c\u9057\u5fd7\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f1f\u5b50\u613f\u5c3d\u529b\uff0c\u4f46\u53ea\u6015\u65e0\u6b64\u80fd\u8010\uff0c\u6709\u8d1f\u5e08\u7236\u671f\u671b\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u53ea\u8981\u4f60\u7ec8\u8eab\u5949\u884c\uff0c\u80fd\u5426\u5b8c\u6210\uff0c\u90a3\u5c31\u542c\u547d\u4e8e\u5929\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f1f\u5b50\u5df2\u7136\u5e74\u8fc7\u5341\u4e94\uff0c\u4e60\u6b66\u53ea\u6015\u665a\u4e86\u4e00\u4e9b\uff0c\u5960\u57fa\u672a\u56fa\uff0c\u5982\u4f55\u80fd\u671f\u5927\u6210\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u4f60\u6709\u8db3\u591f\u7684\u624d\u6167\uff0c\u5177\u5907\u4e86\u7b2c\u4e00\u4e2a\u8981\u6c42\uff0c\u5f53\u4f60\u5e08\u7956\u62e9\u6211\u7ee7\u4ed6\u8863\u94b5\u65f6\uff0c\u8bf4\u4e86\u4e00\u53e5\u8bdd\uff0c\u4ed6\u8bf4\uff0c\u6211\u867d\u662f\u5f88\u597d\u7684\u7ec3\u6b66\u4e4b\u6750\uff0c\u5374\u6ca1\u6709\u8db3\u591f\u5e94\u53d8\u673a\u667a\uff0c\u5982\u4eca\u521a\u597d\u4f60\u548c\u6211\u6614\u5e74\u7684\u673a\u9047\u76f8\u53cd\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u7ef4\u62a4\u6b66\u6797\u6b63\u4e49\uff0c\u9996\u9700\u6b66\u529f\uff0c\u5f1f\u5b50\u7eb5\u6709\u673a\u667a\uff0c\u4f46\u65e0\u6cd5\u7ec3\u6210\u7edd\u6280\u5982\u4f55\u80fd\u5f53\u5927\u4efb\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u7f13\u7f13\u4ed6\u8bf4\u9053\uff1a\u201c\u8001\u592b\u5c06\u5c3d\u6211\u4e4b\u80fd\uff0c\u8bd5\u884c\u521b\u9020\u5947\u8ff9\uff0c\u5b69\u5b50\uff01\u4eba\u867d\u65e0\u6cd5\u548c\u5927\u81ea\u7136\u6297\u62d2\uff0c\u4f46\u5728\u7cbe\u5bc6\u7684\u8ba1\u7b97\u4e4b\u4e0b\uff0c\u53ef\u80fd\u4f1a\u521b\u51fa\u8d85\u4e4e\u5e38\u89c4\u7684\u5947\u8ff9\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5982\u4f55\u4e00\u4e2a\u521b\u9020\u4e4b\u6cd5\u5462\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u7528\u836f\u7269\u548c\u4f5b\u95e8\u4e2d\u4f20\u85aa\u5927\u6cd5\uff0c\u5b69\u5b50\u4f60\u591f\u806a\u660e\uff0c\u6240\u4ee5\uff0c\u5bf9\u90a3\u795e\u5947\u7684\u5251\u62db\u7e41\u4e71\u7684\u62f3\u811a\u4e4b\u672f\uff0c\u4f60\u5b9a\u53ef\u4ee5\u5b66\u6709\u6240\u6210\uff0c\u5dee\u7684\u662f\u4f60\u7684\u5185\u529f\uff0c\u4e5f\u8bb8\u5960\u57fa\u7684\u5de5\u592b\uff0c\u6211\u8981\u7528\u4eba\u7b97\u80dc\u5929\u7684\u529e\u6cd5\uff0c\u4f7f\u4f60\u5728\u6781\u77ed\u7684\u6570\u5e74\uff0c\u8865\u4e0d\u8db3\u6210\u6709\u4f59\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u53ea\u6015\u6211\u6ca1\u6709\u8fd9\u4efd\u80fd\u8010\uff0c\u800c\u4f7f\u5e08\u7236\u5927\u5931\u6240\u671b\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u5e08\u7236\u6211\u4e5f\u6ca1\u6709\u7edd\u5bf9\u7684\u628a\u63e1\u2026\u2026\u201d \u3000\u3000\u8138\u4e0a\u6cdb\u73b0\u51fa\u4e00\u79cd\u6005\u7136\u82e5\u5931\u4e4b\u60c5\uff0c\u7f13\u7f13\u63a5\u9053\uff1a\u201c\u8bf4\u662f\u4e00\u573a\u8c6a\u8d4c\uff0c\u7528\u6b66\u6797\u6b63\u4e49\u4f5c\u7ecf\uff0c\u4f60\u7684\u751f\u547d\u4f5c\u7eac\uff0c\u8d4c\u8f93\u4e86\uff0c\u8fde\u5e26\u6240\u53ca\uff0c\u5c06\u6709\u591a\u6570\u7684\u751f\u547d\uff0c\u966a\u7740\u4f60\u540c\u6ca6\u5165\u5927\u52ab\uff0c\u56e0\u6b64\uff0c\u6211\u4eec\u5fc5\u5c06\u5c3d\u5176\u6240\u80fd\u6c42\u8d62\uff0c\u5b69\u5b50\u4f60\u542c\u5f97\u61c2\u4e3a\u5e08\u7684\u8bdd\u4e86\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u6211\u6709\u70b9\u61c2\uff0c\u5e08\u7236\u4e4b\u610f\uff0c\u662f\u8981\u6211\u503e\u5c3d\u6240\u6709\u7684\u667a\u6167\u3001\u529b\u91cf\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u70b9\u5934\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e3a\u5e08\u5728\u4f60\u7684\u5e74\u9f84\u65f6\uff0c\u667a\u6167\u65b9\u9762\uff0c\u786e\u7136\u548c\u4f60\u6709\u7740\u4e00\u6bb5\u5f88\u5927\u7684\u8ddd\u79bb\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8bd7\u4e66\u884c\u6587\u4e00\u9053\uff0c\u5f92\u513f\u81ea\u4fe1\u53ef\u4ee5\u5e94\u4ed8\uff0c\u4f46\u5728\u4e60\u6b66\u65b9\u9762\u5f92\u513f\u53ea\u6015\u96be\u5f97\u5e08\u671b\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u53ef\u4ee5\uff0c\u5251\u62db\u4e0a\u7684\u5947\u53d8\u548c\u8bd7\u4e66\u6c42\u89e3\uff0c\u662f\u5f02\u66f2\u540c\u5de5\uff0c\u8fd9\u65b9\u9762\u4f60\u5df2\u7ecf\u6709\u4e86\u597d\u7684\u8868\u73b0\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f92\u513f\u54ea\u91cc\u6709\u8868\u73b0\u4e86\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u597d\uff01\u6211\u4e00\u4ef6\u4ef6\u7684\u544a\u8bc9\u4f60\uff0c\u4e0d\u8fc7\uff0c\u4f60\u5148\u4e86\u89e3\u5e08\u7236\u7684\u82e6\u8877\uff0c\u624d\u4e0d\u4f1a\u89c9\u51fa\u6211\u5bf9\u4f60\u4f7f\u7528\u6743\u8c0b\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6115\u7136\u8bf4\u9053\uff1a\u201c\u5e08\u7236\u51e0\u65f6\u5bf9\u6211\u4f7f\u8fc7\u6743\u8c0b\u4e86\uff0c\u600e\u7684\u5f92\u513f\u4e00\u70b9\u4e5f\u770b\u4e0d\u51fa\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u6709\u7684\u56e0\u4e3a\u4f60\u4e0d\u591f\u8001\u7ec3\uff0c\u6240\u4ee5\u89c9\u4e0d\u51fa\u6765\uff0c\u4f46\u4e3a\u5e08\u5fc5\u5f97\u8bf4\u660e\u767d\uff0c\u4e0d\u7136\uff0c\u4f60\u4e00\u65e6\u609f\u5f97\u65f6\uff0c\u5bf9\u6b64\u4e8b\u4e00\u5b9a\u6781\u4e3a\u4e0d\u6ee1\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\u63a5\u9053\uff1a\u201c\u5e08\u7236\u75c5\u5165\u818f\u76f2\uff0c\u5df2\u5230\u4e86\u4e0d\u6cbb\u4e4b\u5883\uff0c\u6211\u5fc5\u987b\u5584\u542f\u73cd\u5f97\u6b8b\u4f59\u7684\u5143\u6c14\uff0c\u4ee5\u6c42\u5c3d\u53ef\u80fd\u5ef6\u7eed\u6211\u7684\u751f\u547d\uff0c\u56e0\u4e3a\u6211\u5b9e\u5728\u6ca1\u6709\u673a\u4f1a\u4e86\uff0c\u8fd9\u4e00\u70b9\u4f60\u8981\u8c05\u89e3\u2014\u2014\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e08\u5f92\u5982\u7236\u5b50\uff0c\u5e08\u7236\u5bf9\u5f1f\u5b50\u8bf4\u8bdd\uff0c\u7528\u4e0d\u7740\u8fd9\u6837\u5ba2\u6c14\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u5728\u4f60\u672a\u5bf9\u7956\u5e08\u795e\u50cf\u8dea\u62dc\u4e4b\u524d\uff0c\u54b1\u4eec\u53ea\u7b97\u662f\u4e00\u5bf9\u5fd8\u5e74\u4e4b\u4ea4\uff0c\u4f60\u53eb\u6211\u5e08\u7236\uff0c\u90a3\u4e5f\u4e0d\u8fc7\u662f\u5f92\u5177\u865a\u540d\u800c\u5df2\uff0c\u670b\u53cb\u76f8\u4ea4\uff0c\u8d35\u4e4b\u4ee5\u8bda\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u70b9\u70b9\u5934\u9053\uff1a\u201c\u597d\u5427\uff01\u6211\u8c05\u89e3\uff0c\u5e08\u7236\u5bf9\u6211\u7528\u4e86\u4ec0\u4e48\u6743\u8c0b\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u6211\u4f20\u4f60\u5251\u62db\uff0c\u8ba9\u4f60\u53bb\u89e3\u6551\u4e2d\u5dde\u4e09\u4fa0\u4e4b\u5371\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u90a3\u662f\u6743\u8c0b\u5417\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u5982\u662f\u667a\u529b\u4e0d\u591f\uff0c\u51b3\u65e0\u6cd5\u5728\u77ed\u77ed\u7684\u51e0\u65e5\u4e4b\u5185\uff0c\u9886\u53d7\u90a3\u5947\u7edd\u4e00\u4ee3\u7684\u5251\u62db\uff0c\u6b64\u53bb\u5192\u9669\uff0c\u4e0d\u4f46\u65e0\u80fd\u6551\u5f97\u51fa\u4e2d\u5dde\u4e09\u4fa0\uff0c\u8fde\u81ea\u5df1\u7684\u6027\u547d\uff0c\u4e5f\u5c06\u4f24\u5728\u9b54\u5200\u7530\u8fdc\u7684\u624b\u4e2d\uff0c\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7565\u4e00\u6c89\u601d\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u5982\u82e5\u4e0d\u7528\u8c0b\uff0c\u5e94\u8be5\u6697\u4e2d\u8d76\u53bb\u63a5\u5e94\u4f60\uff0c\u4f46\u6211\u5374\u6ca1\u6709\u5982\u6b64\uff0c\u90a3\u662f\u8d4c\uff0c\u7f6e\u4f60\u7684\u751f\u6b7b\u4e8e\u4e0d\u987e\u4e86\uff0c\u4f60\u5982\u624d\u6167\u4e0d\u8db3\u4ee5\u9886\u53d7\u90a3\u7edd\u5947\u7684\u6b66\u529f\uff0c\u53ea\u6709\u6b7b\u4ea1\u4e00\u9014\u53ef\u5faa\uff0c\u4f46\u4f60\u8fd8\u662f\u4e00\u4e2a\u7eaf\u6d01\u7684\u5b69\u5b50\uff0c\u6211\u5374\u628a\u4f60\u63a8\u8fdb\u5230\u751f\u6b7b\u4e2d\u53bb\u5192\u9669\uff0c\u8fd9\u662f\u6743\u8c0b\u4e4b\u4e00\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u70b9\u70b9\u5934\u9053\uff1a\u201c\u5e08\u7236\u9009\u624d\u8c28\u4e25\uff0c\u8fd9\u4e5f\u662f\u6ca1\u6cd5\u5b50\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4e2d\u9009\u4e86\uff0c\u5374\u8981\u5148\u5192\u4e00\u573a\u5947\u9669\u3002\u201d \u3000\u3000\u8f7b\u8f7b\u5730\u54b3\u4e86\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u6211\u660e\u77e5\u4f60\u6ca1\u6709\u5f88\u597d\u7684\u6b66\u529f\u57fa\u7840\uff0c\u5374\u7559\u4e66\u53eb\u4f60\u4e58\u6728\u6392\u5230\u6b64\u5730\u6765\uff0c\u8fd9\u5176\u95f4\u7ecf\u8fc7\u4e86\u65e0\u6570\u7684\u51f6\u9669\uff0c\u800c\u4e14\u4e5f\u6709\u6b7b\u4ea1\u7684\u673a\u4f1a\uff0c\u56fa\u7136\u662f\u901a\u6b64\u4e4b\u8def\uff0c\u4ee5\u90a3\u6c34\u9053\u6700\u6613\uff0c\u4f46\u6211\u5374\u672a\u63a5\u8fce\u4f60\uff0c\u5982\u679c\u4f60\u610f\u5fd7\u4e0d\u575a\uff0c\u6015\u6b7b\u754f\u9669\uff0c\u81ea\u7136\u4f1a\u671b\u800c\u5374\u6b65\uff0c\u4e0d\u4f1a\u5230\u6b64\u5730\u6765\u4e86\uff0c\u6b64\u4e3a\u6743\u8c0b\u4e4b\u4e8c\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u7b11\u9053\uff1a\u201c\u6613\u5883\u800c\u5904\uff0c\u6211\u4e5f\u60f3\u4e0d\u51fa\u66f4\u597d\u7684\u529e\u6cd5\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u4f60\u80fd\u8c05\u89e3\u6700\u597d\u2026\u2026\u201d\u957f\u957f\u5410\u4e00\u53e3\u6c14\uff0c\u63a5\u9053\uff1a\u201c\u73b0\u5728\uff0c\u4f60\u7ec8\u4e8e\u5230\u4e86\u6b64\u5730\uff0c\u624d\u6167\u3001\u610f\u5fd7\u90fd\u5df2\u7ecf\u8bc1\u660e\u4e3a\u5e08\u7684\u9009\u624d\u4e0d\u9519\uff0c\u4f59\u4e0b\u7684\u8be5\u662f\u4f60\u7684\u5b9a\u529b\u4e86\uff0c\u5509\uff01\u5b69\u5b50\uff0c\u5982\u662f\u4f5c\u597d\u4eba\uff0c\u4ed6\u7684\u6b66\u529f\u3001\u624d\u6167\u6108\u9ad8\u6108\u597d\u3002\u5982\u662f\u8981\u4f5c\u574f\u4eba\uff0c\u4ed6\u7684\u624d\u6167\uff0c\u6b66\u529f\u6108\u4f4e\u8d8a\u597d\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u6211\u660e\u767d\uff0c\u4e0d\u77e5\u5e08\u7236\u8fd8\u8981\u5f92\u513f\u5982\u4f55\u901a\u8fc7\u90a3\u5b9a\u529b\u4e00\u5173\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u795e\u60c5\u8083\u7a46\u4ed6\u8bf4\u9053\uff1a\u201c\u4eba\u4e4b\u521d\uff0c\u6027\u672c\u5584\uff0c\u98ce\u56e0\u4e3a\u53d7\u4e0d\u4e86\u90a3\u82b1\u82b1\u4e16\u754c\u7684\u4e07\u5343\u8bf1\u60d1\uff0c\u6ca6\u5165\u9b54\u9053\uff0c\u800c\u4e14\u4e00\u6b65\u5931\u63aa\uff0c\u56de\u9996\u767e\u5e74\uff0c\u518d\u60f3\u4ece\u9b54\u9053\u4e2d\u91cd\u65b0\u56de\u5934\uff0c\u53ea\u6015\u662f\u4e07\u5143\u4e00\u6210\u7684\u673a\u4f1a\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f92\u513f\u81ea\u4fe1\u6709\u6b64\u5b9a\u529b\uff0c\u4e0d\u81f4\u4e3a\u9b54\u9053\u6240\u61a8\u2026\u2026\u201d\u7a81\u7136\u957f\u53f9\u4e00\u58f0\uff0c\u63a5\u9053\uff1a \u3000\u3000\u201c\u8bf4\u6765\u53ea\u6015\u5e08\u7236\u4e5f\u662f\u4e0d\u80af\u76f8\u4fe1\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u5b9a\u529b\u7684\u4e00\u5173\uff0c\u4e8b\u5173\u91cd\u8981\uff0c\u4e3a\u5e08\u65e5\u540e\u81ea\u6709\u8003\u9a8c\u4f60\u7684\u529e\u6cd5\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f92\u513f\u968f\u65f6\u5019\u547d\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u73b0\u5728\u4f60\u53ef\u4ee5\u4ed4\u7ec6\u5730\u60f3\u60f3\u4e86\uff0c\u662f\u5426\u7b54\u5141\u8001\u592b\u8fd9\u4e9b\u6761\u4ef6\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f92\u513f\u7b54\u5141\uff01\u201d\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u597d\uff01\u4e3a\u5e08\u8bf7\u51fa\u4f60\u5e08\u7956\u7684\u9057\u50cf\uff0c\u4f60\u8981\u5728\u9057\u50cf\u4e4b\u524d\uff0c\u7acb\u4e0b\u91cd\u8a93\uff0c\u6211\u624d\u80fd\u6b63\u5f0f\u6536\u4f60\u4e3a\u5f92\uff0c\u6388\u4f60\u6b66\u529f\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e08\u7956\u50cf\u5728\u4f55\u5904\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u4f60\u5728\u6b64\u7a0d\u5019\u7247\u523b\uff0c\u4e3a\u5e08\u53bb\u8bf7\u4f60\u5e08\u7956\u9057\u50cf\u3002\u201d\u8a00\u7f62\uff0c\u8d77\u8eab\u7b56\u6756\u800c\u53bb\u3002 \u3000\u3000\u7247\u523b\u4e4b\u540e\uff0c\u4e1c\u8033\u8001\u4eba\u53bb\u800c\u590d\u8fd4\uff0c\u53f3\u624b\u4e2d\u6367\u7740\u4e00\u5e45\u767d\u7ee2\u3002 \u3000\u3000\u4e1c\u8033\u8001\u4eba\u7f13\u7f13\u653e\u4e0b\u5de6\u624b\u4e2d\u63d0\u7740\u7684\u6756\uff0c\u606d\u606d\u656c\u656c\uff0c\u628a\u624b\u4e2d\u767d\u7ee2\u6253\u5f00\uff0c\u6302\u5728\u58c1\u4e0a\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u51dd\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u90a3\u767d\u7ee2\u4e4b\u4e0a\uff0c\u753b\u7740\u4e00\u4e2a\u8eab\u7740\u9053\u88c5\uff0c\u957f\u9aef\u98d8\u62c2\u7684\u8001\u4eba\uff0c\u4e00\u6d3e\u4ed9\u98ce\u9053\u9aa8\u3002 \u3000\u3000\u4e1c\u8033\u8001\u4eba\u5148\u5bf9\u7740\u753b\u50cf\u62dc\u4e86\u4e09\u62dc\uff0c\u7ad9\u8d77\u8eab\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u5148\u5728\u4f60\u5e08\u7956\u50cf\u524d\u7acb\u4e0b\u91cd\u8a93\uff0c\u518d\u62dc\u4ed6\u8001\u4eba\u5bb6\u7684\u9057\u50cf\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5e94\u4e86\u4e00\u58f0\uff0c\u5148\u5bf9\u90a3\u753b\u50cf\u5927\u62dc\u4e09\u62dc\uff0c\u7136\u540e\u884c\u5230\u753b\u50cf\u9762\u524d\uff0c\u606d\u606d\u656c\u656c\u4ed6\u8bf4\u9053\uff1a \u3000\u3000\u201c\u5e08\u7956\u795e\u7075\u5728\u4e0a\uff0c\u5f92\u513f\u65b9\u96ea\u5b9c\uff0c\u5982\u8499\u6069\u5e08\u6536\u7559\u4e3a\u5f92\uff0c\u5f53\u7ec8\u8eab\u4e00\u4e16\uff0c\u5b88\u4e49\u884c\u4fa0\uff0c\u5982\u82e5\u4e0d\u5b88\u5e08\u8bad\uff0c\u4e3a\u975e\u505a\u6b79\u5929\u8bdb\u5730\u706d\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u597d\uff01\u201d\u65b9\u96ea\u5b9c\u5bf9\u90a3\u9057\u50cf\u62dc\u4e86\u4e00\u62dc\uff0c\u56de\u5934\u671b\u7740\u4e1c\u8033\u8001\u4eba\uff0c\u9053\uff1a \u3000\u3000\u201c\u5e08\u7236\uff0c\u5f1f\u5b50\u518d\u62dc\u5e08\u5417\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u597d\uff01\u4f60\u65e2\u6709\u5982\u6b64\u7684\u5b5d\u5fc3\uff0c\u4e3a\u5e08\u5982\u575a\u6301\uff0c\u4f60\u5fc3\u4e2d\u53cd\u800c\u4e0d\u5b89\u4e86\u3002\u201d \u3000\u3000\u5f53\u4e0b\u7aef\u5750\u5728\u7af9\u6905\u4e4b\u4e0a\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u606d\u606d\u656c\u656c\uff0c\u8dea\u4e86\u4e0b\u53bb\uff0c\u884c\u4e86\u5927\u793c\uff0c\u7ad9\u8d77\u8eab\u5b50\u3002 \u3000\u3000\u4e1c\u8033\u8001\u4eba\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4ece\u6b64\u5f00\u59cb\uff0c\u4f60\u624d\u7b97\u4e3a\u5e08\u7684\u5f92\u5f1f\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u5e08\u7236\uff0c\u6b64\u523b\uff0c\u5f1f\u5b50\u5fc3\u4e2d\u6709\u51e0\u4ef6\u4e0d\u89e3\u4e4b\u4e8b\uff0c\u73b0\u5728\u53ef\u4ee5\u95ee\u95ee\u5427\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u597d\uff0c\u4f60\u95ee\u5427\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e08\u7236\u4e0d\u662f\u59d3\u4e1c\u5427\uff01\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u6211\u59d3\u9648\u2014\u2014\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5251\u795e\u9648\u5927\u4fa0\uff0c\u53ef\u662f\u5e08\u7236\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u542c\u4f55\u4eba\u8bf4\u8fc7\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u6211\u90a3\u4f4d\u90d1\u5927\u4f2f\u7236\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u7565\u4e00\u6c89\u541f\uff0c\u9053\uff1a\u201c\u90d1\u5927\u521a\u4e00\u8eab\u6b63\u6c14\uff0c\u786e\u662f\u4e00\u4f4d\u4fa0\u4e49\u4eba\u7269\uff0c\u4f60\u90a3\u4e09\u53d4\u7236\u4e5f\u4e0d\u9519\uff0c\u53ea\u662f\u4f60\u90a3\u4f4d\u4e8c\u6015\uff0c\u5c31\u6709\u4e9b\u4e0d\u5bf9\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u5e08\u7236\u600e\u77e5\u6653\uff1f\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u6211\u53ea\u662f\u89c2\u5bdf\u6240\u5f97\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9eef\u7136\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5f1f\u5b50\u5fc3\u4e2d\uff0c\u6709\u4e00\u6869\u767e\u601d\u4e0d\u89e3\u7684\u4e8b\uff0c\u6b63\u60f3\u5411\u5e08\u7236\u8bf7\u6559\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5728\u5f1f\u5b50\u770b\u8fc7\u5e08\u7236\u7559\u8a00\u4e4b\u540e\uff0c\u51c6\u5907\u6765\u6b64\u4e4b\u65f6\uff0c\u6211\u90a3\u4f4d\u5927\u4f2f\u7236\u575a\u6301\u8981\u9001\u6211\u4e00\u7a0b\u3002\u884c\u81f3\u4e2d\u9014\uff0c\u7a81\u7136\u51fa\u624b\u70b9\u4e86\u6211\u7684\u7a74\u9053\uff0c\u8981\u53d6\u6211\u4e4b\u547d\uff0c\u5e78\u5f97\u90d1\u5927\u4f2f\u53ca\u65f6\u8d76\u5230\uff0c\u6551\u4e86\u5f92\u513f\u4e4b\u547d\uff0c\u5f92\u513f\u5272\u53d1\u4ee3\u9996\uff0c\u8fd8\u4e86\u4ed6\u517b\u80b2\u4e4b\u6069\u2014\u2014\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u63a5\u9053\uff1a\u201c\u4ed6\u4e3a\u4ec0\u4e48\u6740\u4f60\uff1f\u201d\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u5f1f\u5b50\u767e\u601d\u4e0d\u89e3\u4e4b\u5904\u4e86\uff0c\u5e73\u5e38\u4e4b\u65f6\uff0c\u4ed6\u5bf9\u6211\u548c\u73de\u5f1f\uff0c\u867d\u7136\u7a0d\u6709\u5206\u522b\uff0c\u4f46\u8fd8\u7b97\u6148\u7231\uff0c\u60f3\u4e0d\u5230\u4e00\u4e0b\u53d8\u8138\uff0c\u7adf\u7136\u8981\u53d6\u6211\u4e4b\u547d\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u7f13\u7f13\u4ed6\u8bf4\u9053\uff1a\u201c\u4e3a\u5e08\u672a\u75c5\u4e4b\u524d\uff0c\u5728\u6c5f\u6e56\u4e4b\u4e0a\u8d70\u52a8\uff0c\u51e1\u662f\u529b\u6076\u4e4b\u5f92\uff0c\u53ea\u8981\u770b\u5230\u4e3a\u5e08\uff0c\u65e0\u4e0d\u9000\u907f\u4e09\u820d\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u6b66\u6797\u4e4b\u4e2d\uff0c\u624d\u9001\u5e08\u7236\u4e00\u4e2a\u5251\u795e\u4e4b\u53f7\uff0c\u795e\u4e4b\u6240\u81f3\uff0c\u81ea\u7136\u662f\u5bb5\u5c0f\u56de\u907f\u4e86\u3002\u201d \u3000\u3000\u4e1c\u8033\u8001\u4eba\u9053\uff1a\u201c\u4e3a\u5e08\u7684\u672c\u540d\u9648\u5e0c\u6b63\uff0c\u8fd9\u5251\u795e\u4e4b\u53f7\u8fd8\u662f\u4e8c\u5341\u5e74\u524d\u4e3a\u5e08\u5251\u670d\u6c5f\u6e56\u4e94\u9b54\u4e4b\u540e\uff0c\u6b66\u6797\u4e2d\u4eba\u9001\u7ed9\u4e3a\u5e08\u7684\uff0c\u5509\uff01\u4f46\u8fd9\u5251\u795e\u4e8c\u5b57\uff0c\u592a\u96be\u5f53\u4e86\uff0c\u81ea\u4ece\u4ed6\u4eec\u9001\u4e86\u4e3a\u5e08\u6b64\u53f7\u4e4b\u540e\uff0c\u4e3a\u5e08\u5f97\u4e86\u4e00\u79cd\u4e0d\u6cbb\u7684\u7edd\u75c7\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u63a5\u9053\uff1a\u201c\u5e08\u7236\u6b66\u529f\u5df2\u8fbe\u7384\u901a\u4e4b\u5883\uff0c\u800c\u4e14\u5e74\u5c81\u4ea6\u76db\u58ee\uff0c\u4f1a\u5f97\u4ec0\u4e48\u4e0d\u6cbb\u4e4b\u75c7\u5462\uff1f\u201d \u3000\u3000\u9648\u5e0c\u6b63\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u4e16\u4e0a\u6ca1\u6709\u4e0d\u6b7b\u7684\u4eba\uff0c\u4e3a\u5e08\u4e5f\u4e0d\u628a\u4ec0\u4e48\u4e0d\u6cbb\u4e4b\u75c7\u653e\u5728\u5fc3\u4e0a\uff0c\u91cd\u8981\u7684\u662f\uff0c\u4e3a\u5e08\u5fc5\u987b\u627e\u4e00\u4e2a\u4eba\uff0c\u80fd\u591f\u627f\u7ee7\u4f60\u5e08\u7956\u7559\u4e0b\u6765\u7684\u8863\u94b5\uff0c\u5982\u4eca\u603b\u7b97\u88ab\u4e3a\u5e08\u5bfb\u5230\u4e86\uff0c\u6b64\u540e\uff0c\u4e3a\u5e08\u6700\u91cd\u8981\u7684\u4e00\u4ef6\u4e8b\uff0c\u5c31\u662f\u8bbe\u6cd5\u4f7f\u4f60\u80fd\u5b66\u5f97\u4f60\u5e08\u7956\u7559\u4e0b\u7684\u5168\u90e8\u6b66\u529f\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u63a5\u9053\uff1a\u201c\u5f1f\u5b50\u81ea\u77e5\u611a\u62d9\uff0c\u6050\u53eb\u5e08\u7236\u5931\u671b\u4e86\u3002\u201d \u3000\u3000\u9635\u5e0c\u6b63\u7b11\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u662f\uff0c\u8bba\u4f60\u624d\u667a\uff0c\u5c24\u5728\u4e3a\u5e08\u4e4b\u4e0a\uff0c\u4f46\u6050\u65e0\u6cd5\u8d76\u4e0a\u4f60\u90a3\u5e08\u7956\uff0c\u987b\u77e5\u4f60\u90a3\u5e08\u7956\u662f\u5343\u767e\u5e74\u4e2d\u96be\u5f97\u4e00\u89c1\u7684\u4eba\u624d\uff0c\u4e3a\u5e08\u7684\u53d7\u4e86\u5341\u5e74\u82e6\u5fc3\u6559\u8bf2\uff0c\u4e5f\u4e0d\u8fc7\u5f97\u4ed6\u6b66\u529f\u5341\u4e4b\u4e94\u516d\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u70b9\u70b9\u5934\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\u3002\u201d \u3000\u3000\u9648\u5e0c\u6b63\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u5929\u4e0b\u6ca1\u6709\u72b6\u5143\u5e08\u7236\uff0c\u4e5f\u8bb8\u4f60\u80fd\u591f\u9752\u51fa\u4e8e\u84dd\uff0c\u5f3a\u8fc7\u4e3a\u5e08\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f1f\u5b50\u600e\u6709\u6b64\u80fd\uff01\u201d \u3000\u3000\u9648\u5e0c\u6b63\u9053\uff1a\u201c\u4f60\u5e08\u7956\u7559\u4e0b\u4e86\u65e0\u4e0a\u5999\u6cd5\uff0c\u770b\u770b\u54b1\u4eec\u80fd\u5426\u8fd0\u7528\uff0c\u4eba\u5b9a\u80dc\u5929\u2014\u2014\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u81f3\u4e8e\u4e3a\u5e08\u7684\u75c5\uff0c\u6b64\u523b\u8bf4\u7ed9\u4f60\u542c\u4f60\u4e5f\u4e0d\u660e\u767d\uff0c\u65e5\u540e\uff0c\u4e3a\u5e08\u6162\u6162\u5730\u544a\u8bc9\u4f60\u5427\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u70b9\u70b9\u5934\u9053\uff1a\u201c\u5e0c\u671b\u5409\u4eba\u6709\u5929\u76f8\uff0c\u5e08\u7236\u4e4b\u75c5\uff0c\u80fd\u591f\u4e0d\u836f\u800c\u6108\u3002\u201d \u3000\u3000\u9648\u5e0c\u6b63\u9053\uff1a\u201c\u4e3a\u5e08\u7684\u4e8b\uff0c\u4e0d\u7528\u4f60\u8d39\u5fc3\uff0c\u5012\u662f\u4f60\u81ea\u5df1\u8981\u8bbe\u6cd5\u653e\u5f00\u80f8\u6000\uff0c\u4e0d\u8981\u518d\u628a\u4f60\u5927\u4f2f\u4e4b\u4e8b\uff0c\u653e\u5728\u5fc3\u4e0a\uff0c\u9700\u77e5\u4f60\u7956\u5e08\u9057\u7559\u4eba\u95f4\u5251\u9053\uff0c\u4e43\u662f\u6b66\u4e2d\u4e4b\u7cbe\uff0c\u5e73\u5e38\u4e4b\u4eba\uff0c\u65e0\u6cd5\u5b66\u4e60\uff0c\u7eb5\u7136\u662f\u624d\u667a\u4e4b\u58eb\uff0c\u4e5f\u5f97\u4e13\u5fc3\u4e00\u5fd7\u624d\u80fd\u671f\u671b\u6709\u6210\uff0c\u4f60\u5982\u5206\u5fc3\u522b\u7528\uff0c\u90a3\u5c31\u5f88\u96be\u6709\u6210\u5c31\u4e4b\u671b\uff0c\u5c24\u5176\u662f\u5728\u6253\u57fa\u7840\u4e4b\u65f6\uff0c\u4f60\u90a3\u4f4d\u5927\u6015\uff0c\u786e\u7136\u6709\u4e9b\u4e0d\u5bf9\uff0c\u4e3a\u5e08\u521d\u89c1\u4ed6\u65f6\uff0c\u5c31\u6709\u6b64\u611f\uff0c\u5947\u602a\u7684\u662f\u4f60\u90a3\u5927\u5a18\u65b9\u592b\u4eba\uff0c\u5374\u662f\u4e00\u4f4d\u5929\u4f7f\u822c\u7684\u5584\u826f\u4eba\u7269\uff0c\u4e0d\u77e5\u4ed6\u4eec\u5982\u4f55\u76f8\u5904\u4e86\u8fd9\u4e9b\u5e74\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f92\u5f1f\u5fc3\u4e2d\u6000\u7591\u6211\u7684\u5927\u4f2f\uff0c\u548c\u6211\u751f\u7236\u6b7b\u4ea1\u6709\u5173\u3002\u201d \u3000\u3000\u9648\u5e0c\u6b63\u9053\uff1a\u201c\u4e3a\u5e08\u7684\u53ea\u80fd\u8bf4\u4f60\u90a3\u5927\u4f2f\u662f\u4e2a\u6781\u4e3a\u53ef\u7591\u7684\u4eba\uff0c\u5728\u672a\u627e\u51fa\u8bc1\u660e\u4e4b\u524d\uff0c\u4e0d\u80fd\u8f7b\u6613\u5730\u51fa\u53e3\u8bec\u4eba\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f1f\u5b50\u77e5\u9053\uff0c\u54b1\u4eec\u672a\u80fd\u627e\u51fa\u4e00\u4e2a\u4eba\u7684\u786e\u5b9e\u7f6a\u8bc1\u4e4b\u524d\uff0c\u4e0d\u80fd\u8f7b\u6613\u8bec\u4eba\u4e4b\u7f6a\u3002\u201d \u3000\u3000\u9648\u5e0c\u6b63\u7b11\u9053\uff1a\u201c\u5982\u6b64\u624d\u7b97\u5f97\u4fa0\u4e49\u82f1\u96c4\u4eba\u7269\u2026\u2026\u201d\u957f\u957f\u5410\u4e86\u4e00\u53e3\u6c14\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u5e08\u7956\u6709\u8fc7\u4e00\u4e2a\u5fc3\u613f\uff0c\u90a3\u5c31\u662f\u6211\u4eec\u5b81\u53ef\u653e\u8fc7\u4e86\u4e00\u4e2a\u574f\u4eba\uff0c\u4e5f\u4e0d\u53ef\u8bef\u4f24\u4e00\u4e2a\u597d\u4eba\uff0c\u56e0\u6b64\uff0c\u4e3a\u5e08\u4e00\u751f\u4e2d\u683c\u5b88\u7740\u4f60\u5e08\u7956\u9057\u8bad\uff0c\u4e0d\u6562\u8f7b\u6613\u4f24\u4eba\uff0c\u6709\u65f6\u867d\u7136\u660e\u77e5\u5bf9\u65b9\u662f\u4e2a\u5341\u6076\u4e0d\u8d66\u4e4b\u5f92\uff0c\u4f46\u56e0\u4e00\u76f4\u672a\u80fd\u627e\u5230\u5bf9\u65b9\u7684\u786e\u5b9e\u7f6a\u8bc1\uff0c\u4e0d\u80fd\u65a9\u8349\u9664\u6839\uff0c\u53d6\u4ed6\u6027\u547d\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e08\u7236\u5c0a\u5b88\u5e08\u7956\u7684\u9057\u8bad\uff0c\u90a3\u4e5f\u662f\u6ca1\u6709\u6cd5\u5b50\u7684\u4e8b\u4e86\uff01\u201d \u3000\u3000\u9648\u5e0c\u6b63\u9053\uff1a\u201c\u4f46\u8fd9\u6570\u5341\u5e74\u6765\uff0c\u4e3a\u5e08\u518d\u4e09\u60f3\u8fc7\u4e4b\u540e\uff0c\u89c9\u7740\u4f60\u7956\u5e08\u7684\u9057\u8bad\uff0c\u6709\u4e9b\u95ee\u9898\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4ec0\u4e48\u95ee\u9898\uff1f\u201d \u3000\u3000\u9648\u5e0c\u6b63\u9053\uff1a\u201c\u4e3a\u5e08\u60f3\u4e86\u5f88\u591a\u5e74\u76f4\u5230\u4eca\u65e5\u624d\u60f3\u901a\u4e86\u4e2a\u4e2d\u7684\u9053\u7406\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4ec0\u4e48\u9053\u7406\uff1f\u201d \u3000\u3000\u9648\u5e0c\u6b63\u9053\uff1a\u201c\u4f60\u5e08\u7956\u624d\u534e\u7edd\u4e16\uff0c\u77e5\u6709\u6240\u4e0d\u77e5\uff0c\u4e3a\u4eba\u6240\u4e0d\u4e3a\uff0c\u4e0d\u8bba\u4ec0\u4e48\u4e3a\u96be\u4e4b\u4e8b\uff0c\u5728\u4f60\u5e08\u7956\u773c\u4e2d\uff0c\u90fd\u96be\u4e0d\u5012\u4ed6\uff0c\u4f46\u4e3a\u5e08\u5c31\u4e0d\u540c\u4e86\uff0c\u8fd9\u6570\u5341\u5e74\u6765\uff0c\u4e3a\u5e08\u9047\u5230\u68d8\u624b\u7684\u4e8b\uff0c\u5374\u5e38\u5e38\u65e0\u80fd\u89e3\u51b3\uff0c\u56e0\u6b64\uff0c\u6211\u60f3\u5230\u4f60\u5e08\u7956\u7684\u9057\u8bad\u5b9e\u6709\u4fee\u6b63\u4e4b\u5fc5\u8981\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9648\u5e0c\u6b63\u9053\uff1a\u201c\u56e0\u4e3a\uff0c\u6b64\u523b\u54b1\u4eec\u4e0d\u53ef\u80fd\u518d\u6709\u4f60\u5e08\u7956\u90a3\u7b49\u624d\u667a\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u5e08\u7236\u8bf4\u7684\u662f\u3002\u201d \u3000 \u7b2c\u516b\u56de\u3000\u62dc\u5e08\u7ec3\u5251 \u3000 \u3000\u3000\u9648\u5e0c\u6b63\u9053\uff1a\u201c\u4f60\u5e08\u7956\u4e3a\u4eba\uff0c\u4e00\u5411\u662f\u72ec\u6765\u72ec\u5f80\uff0c\u4e3a\u5e08\u7684\u5c31\u5403\u4e86\u8fd9\u4e2a\u5927\u4e8f\uff0c\u6548\u6cd5\u4f60\u5e08\u7956\u4e3a\u4eba\uff0c\u72ec\u884c\u5176\u662f\uff0c\u4ee5\u81f4\u4e8e\u65e0\u6cd5\u548c\u6b66\u6797\u540c\u9053\u7684\u6b63\u4e49\u536b\u9053\u4e4b\u58eb\uff0c\u7ed3\u5408\u4e00\u4f53\uff0c\u624d\u81f4\u9b51\u9b45\u6a2a\u884c\uff0c\u65e0\u6cd5\u8bdb\u6536\u3002\u4e3a\u5e08\u5728\u6c5f\u6e56\u4e4b\u4e0a\u884c\u8d70\uff0c\u90a3\u4e9b\u9b54\u5934\u4eec\u8fd8\u6709\u51e0\u5206\u754f\u4ff1\uff0c\u4f46\u4e3a\u5e08\u81ea\u60a3\u91cd\u75c5\u4e4b\u540e\uff0c\u6b66\u4f11\u4e4b\u4e2d\u5c31\u96be\u518d\u6709\u4e00\u80a1\u529b\u91cf\uff0c\u9547\u538b\u90a3\u4e9b\u9b51\u9b45\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u662f\u7684\uff0c\u541b\u5b50\u53ef\u6b3a\u4e4b\u4ee5\u65b9\uff0c\u4f46\u90a3\u9b51\u9b45\u4e4b\u8f88\uff0c\u4e0d\u62e9\u624b\u6bb5\u5bf9\u4ed8\u6b66\u6797\u4e2d\u6b63\u4e49\u4eba\u58eb\uff0c\u6216\u4e88\u65a9\u9664\uff0c\u6216\u4e88\u6536\u7528\uff0c\u800c\u4e14\u7fa4\u6076\u76f8\u6d4e\uff0c\u9020\u6210\u82cd\u751f\u52ab\u96be\uff0c\u6211\u4eec\u65e2\u65e0\u5e08\u7956\u7684\u7edd\u4e16\u624d\u534e\uff0c\u6d88\u5f25\u5927\u7978\u4e8e\u65e0\u5f62\uff0c\u53ea\u6709\u8bbe\u6cd5\u8054\u7edc\u4ec1\u4eba\u5fd7\u58eb\uff0c\u540c\u4e3a\u6b66\u6797\u6b63\u4e49\u6548\u529b\uff0c\u6b66\u6797\u4e2d\u516c\u7406\u4f38\u957f\uff0c\u624d\u80fd\u4f7f\u5996\u90aa\u65e0\u6240\u9041\u5f62\uff01\u201d \u3000\u3000\u9648\u5e0c\u6b63\u542c\u4e86\u65b9\u96ea\u5b9c\u8fd9\u756a\u8a00\u8bed\uff0c\u5fae\u73b0\u8bb6\u5f02\u4e4b\u8272\uff0c\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u7b11\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u53ea\u542c\u4f60\u8fd9\u756a\u8a00\u8bed\uff0c\u5c31\u4f7f\u6211\u653e\u5fc3\u4e86\u4e0d\u5c11\uff0c\u8fd9\u4e5f\u662f\u4f60\u5e08\u7956\u5728\u5929\u4e4b\u7075\u76f8\u4f51\uff0c\u624d\u4f7f\u4e3a\u5e08\u5728\u5c06\u8981\u5931\u671b\u4e4b\u4e2d\u9047\u4e0a\u4e86\u4f60\u2026\u2026\u201d \u3000\u3000\u5377\u8d77\u90a3\u8001\u4eba\u753b\u50cf\uff0c\u63a5\u9053\uff1a\u201c\u6211\u7acb\u523b\u4f20\u4f60\u5960\u57fa\u6253\u5750\u4e4b\u6cd5\uff0c\u4f60\u8981\u5168\u5fc3\u4e00\u610f\u5730\u7ec3\u4e60\uff0c\u8fd9\u5c71\u4e0a\u7410\u4e8b\uff0c\u90fd\u4e0d\u7528\u4f60\u7ba1\u4e86\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u90a3\u600e\u4e48\u6210\uff0c\u6709\u4e8b\u5f1f\u5b50\u670d\u5176\u52b3\u2026\u2026\u201d \u3000\u3000\u9648\u5e0c\u6b63\u6447\u5934\u63a5\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u54b1\u4eec\u7684\u65f6\u95f4\u6709\u9650\uff0c\u4f60\u8eab\u62c5\u91cd\u4efb\uff0c\u65e0\u6687\u5584\u5c3d\u5f1f\u5b50\u4e4b\u804c\uff0c\u4ece\u660e\u5929\u8d77\uff0c\u4f60\u7684\u996e\u98df\uff0c\u90fd\u7531\u4e3a\u5e08\u8c03\u7406\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u4e2a\u53eb\u5f1f\u5b50\u5982\u4f55\u6562\u5f53\uff01\u201d \u3000\u3000\u9648\u5e0c\u6b63\u9053\uff1a\u201c\u4f60\u8981\u77e5\u9053\uff0c\u90a3\u5e76\u975e\u4e3a\u5e08\u4f3a\u5019\u4e8e\u4f60\uff0c\u800c\u662f\u4e3a\u4e86\u8981\u4f60\u65e9\u62c5\u5927\u4efb\u2026\u2026\u201d \u3000\u3000\u957f\u957f\u5401\u4e86\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u4f60\u5e08\u7956\u4f3c\u662f\u65e9\u5df2\u659c\u60f3\u5230\u6570\u5341\u5e74\u540e\u7684\u4e8b\uff0c\u4ed6\u66ff\u6211\u7559\u4e86\u6570\u5341\u795e\u836f\u7269\uff0c\u6211\u81ea\u5df1\u8fd9\u4e9b\u5e74\u6765\uff0c\u8d70\u904d\u4e86\u5929\u6daf\u6d77\u89d2\uff0c\u4e5f\u91c7\u53d6\u4e86\u6570\u5341\u79cd\u836f\uff0c\u8fd9\u4e9b\u836f\u7269\uff0c\u5bf9\u4e00\u4e2a\u4eba\u7684\u8eab\uff0c\u672c\u5927\u6709\u5e2e\u52a9\uff0c\u90a3\u5c31\u662f\u8bf4\uff0c\u660e\u65e5\u5f00\u59cb\uff0c\u4f60\u5c31\u8981\u98df\u7528\u836f\u7269\uff0c\u4ee5\u5907\u65e5\u540e\u4e60\u7ec3\u4e0a\u4e58\u6b66\u529f\u4e4b\u7528\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u5f1f\u5b50\u5c31\u606d\u656c\u4e0d\u5982\u4ece\u547d\u4e86\u3002\u201d \u3000\u3000\u9648\u5e0c\u6b63\u7f13\u7f13\u7ad9\u8d77\u8eab\u5b50\uff0c\u5927\u6b65\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u5927\u7ea6\u8fc7\u4e86\u6709\u4e00\u76cf\u70ed\u8336\u5de5\u592b\u4e4b\u4e45\uff0c\u91cd\u53c8\u884c\u4e86\u56de\u6765\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u54b1\u4eec\u73b0\u5728\u5c31\u5f00\u59cb\u4e60\u7ec3\u6b66\u529f\u3002\u201d \u3000\u3000\u5f53\u4e0b\u4f20\u6388\u4e86\u65b9\u96ea\u5b9c\u5410\u7eb3\u4e4b\u672f\u7684\u53e3\u8bc0\u3001\u8bc0\u7a8d\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u5f00\u59cb\u8bd5\u884c\u8c03\u606f\u3002 \u3000\u3000\u4e00\u65f6\u95f4\uff0c\u7adf\u65e0\u6cd5\u4f7f\u80f8\u4e2d\u4e4b\u6c14\uff0c\u5e73\u590d\u4e0b\u6765\u3002 \u3000\u3000\u5e78\u597d\uff0c\u65b9\u96ea\u5b9c\u6709\u7740\u4eba\u6240\u96be\u53ca\u7684\u8010\u5fc3\uff0c\u4e0d\u538c\u5176\u70e6\uff0c\u4e00\u6b21\u4e0d\u6210\uff0c\u518d\u884c\u6765\u8fc7\uff0c\u4e00\u8fde\u8bd5\u4e86\u6570\u5341\u6b21\uff0c\u4ecd\u4e0d\u6c14\u9981\uff0c\u76f4\u5230\u4e86\u6df1\u591c\u56db\u66f4\uff0c\u624d\u7b97\u627e\u5bf9\u4e86\u8def\u5b50\uff0c\u5f00\u59cb\u4f9d\u7167\u5410\u7eb3\u4e4b\u672f\uff0c\u8c03\u606f\u8fd0\u6c14\u3002 \u3000\u3000\u4ed6\u8bd5\u884c\u4e86\u516d\u4e03\u4e2a\u65f6\u8fb0\u4e4b\u4e45\uff0c\u76f4\u5230\u4e94\u66f4\u8fc7\u540e\uff0c\u4eba\u5df2\u75b2\u4e4f\u4e0d\u582a\uff0c\u4f46\u4ed6\u4ecd\u7136\u4e0d\u505c\u5730\u8fd0\u6c14\u8c03\u606f\uff0c\u76f4\u5230\u5fc3\u9886\u795e\u4f1a\uff0c\u5b8c\u5168\u5b66\u6210\u3002 \u3000\u3000\u8fd9\u7b49\u4e0a\u4e58\u5185\u529f\uff0c\u4e00\u7ecf\u5b66\u6210\uff0c\u7acb\u65f6\u6c14\u8d70\u7ecf\u8109\uff0c\u7cbe\u795e\u5927\u89c1\u597d\u8f6c\uff0c\u75b2\u52b3\u6e10\u6d88\uff0c\u8fdb\u5165\u4e86\u65e0\u6211\u4e4b\u5883\u3002 \u3000\u3000\u4e00\u6b21\u5750\u606f\u9192\u6765\uff0c\u5df2\u662f\u65e5\u5347\u4e09\u7aff\u65f6\u5206\uff0c\u62ac\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u9633\u5149\u6ee1\u7a97\uff0c\u9648\u5e0c\u6b63\u6ee1\u542b\u5fae\u7b11\uff0c\u7ad9\u5728\u8eab\u524d\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5403\u4e86\u4e00\u60ca\uff0c\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u5e08\u7236\uff0c\u5f1f\u5b50\u2026\u2026\u201d \u3000\u3000\u9648\u5e0c\u6b63\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u5750\u7740\uff0c\u4e0d\u8981\u7d27\u5f20\uff0c\u6211\u5df2\u7ecf\u66ff\u4f60\u51c6\u5907\u4e86\u5403\u7684\u4e1c\u897f\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8f6c\u8138\u770b\u53bb\uff0c\u53ea\u89c1\u8eab\u4fa7\u653e\u7740\u4e00\u4e2a\u74f7\u7897\uff0c\u4e0a\u9762\u8fd8\u7528\u4e00\u4e2a\u74f7\u76d8\u76d6\u7740\u3002 \u3000\u3000\u4f46\u95fb\u9648\u5e0c\u6b63\u4f4e\u5934\u8bf4\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u5148\u628a\u5b83\u5403\u4e0b\u53bb\uff0c\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u600e\u6562\u52b3\u5e08\u52a8\u624b\uff1f\u201d \u3000\u3000\u4f38\u624b\u63ed\u5f00\u74f7\u76d8\uff0c\u51dd\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u90a3\u74f7\u76d8\u4e4b\u4e0b\u7684\u767d\u7897\u4e4b\u4e2d\uff0c\u76db\u4e86\u6ee1\u6ee1\u4e00\u7897\u767d\u8272\u7684\u6c34\u6c41\uff0c\u4e0d\u77e5\u662f\u4ec0\u4e48\uff1f\u4e00\u80a1\u6d53\u91cd\u7684\u6e05\u9999\uff0c\u7531\u90a3\u767d\u7897\u4e2d\u76f4\u5192\u4e0a\u6765\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u671b\u4e86\u90a3\u767d\u8272\u6d53\u6db2\u4e00\u773c\uff0c\u4f38\u624b\u6765\uff0c\u4e00\u53e3\u6c14\u559d\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u9648\u5e0c\u6b63\u63a5\u8fc7\u74f7\u7897\uff0c\u5fae\u5fae\u4e00\u7b11\uff1b\u9053\uff1a\u201c\u597d\uff01\u4f60\u73b0\u5728\u51fa\u5ba4\u8d70\u8d70\uff0c\u6563\u6563\u6b65\uff0c\u7136\u540e\u518d\u56de\u5230\u5ba4\u4e2d\u6765\u6309\u7167\u53e3\u8bc0\u5750\u606f\uff0c\u5176\u4ed6\u4e4b\u4e8b\uff0c\u4f60\u5c31\u4e0d\u7528\u7ba1\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u5f1f\u5b50\u5982\u4f55\u2014\u2014\u201d \u3000\u3000\u9648\u5e0c\u6b63\u8138\u8272\u4e00\u6574\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u8981\u542c\u4e3a\u5e08\u7684\u8bdd\uff0c\u4e0d\u8981\u591a\u8a00\uff0c\u51fa\u623f\u53bb\u5427\uff01\u201d\u65b9\u96ea\u5b9c\u4e0d\u6562\u518d\u8a00\uff0c\u8d77\u8eab\u884c\u81f3\u623f\u5916\u3002 \u3000\u3000\u8fd9\u662f\u7edd\u5cf0\u4e4b\u9876\uff0c\u5efa\u7b51\u7684\u4e00\u5ea7\u8305\u820d\uff0c\u5ba4\u5916\u662f\u4e00\u7247\u5e7f\u573a\uff0c\u904d\u5730\u5c71\u82b1\uff0c\u9752\u8349\u5982\u8335\uff0c\u4e00\u80a1\u5e7d\u5e7d\u6e05\u9999\u8349\u6728\u6c14\u606f\uff0c\u6251\u5165\u9f3b\u4e2d\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4f38\u5f20\u4e00\u4e0b\u53cc\u81c2\uff0c\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u6cbf\u7740\u8349\u576a\u4e0a\u884c\u4e86\u4e00\u5468\uff0c\u91cd\u53c8\u884c\u56de\u8305\u820d\u3002 \u3000\u3000\u5348\u9910\u4e4b\u65f6\uff0c\u9648\u5e0c\u6b63\u53c8\u9001\u4e00\u7897\u9ed1\u8272\u7684\u6db2\u6c41\uff0c\u5176\u5473\u751a\u82e6\uff0c\u548c\u65e9\u4e0a\u90a3\u7897\u767d\u8272\u6db2\u6c41\uff0c\u5927\u4e0d\u76f8\u540c\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u90b9\u76b1\u7709\u5934\uff0c\u7aef\u8d77\u74f7\u7897\u3002 \u3000\u3000\u9648\u5e0c\u6b63\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u826f\u836f\u82e6\u53e3\uff0c\u8fd9\u662f\u4f60\u5e08\u7956\u7559\u4e0b\u7684\u4ed9\u65b9\u5999\u6cd5\uff0c\u4e3a\u5e08\u8981\u51ed\u4ed7\u6b64\u6cd5\uff0c\u52a9\u4f60\u901f\u957f\u5185\u529b\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e00\u7897\u82e6\u6c41\uff0c\u7b97\u5f97\u4ec0\u4e48\uff1f\u201d\u4e3e\u7897\u5c31\u53e3\uff0c\u4e00\u6c14\u559d\u5b8c\u3002 \u3000\u3000\u65f6\u5149\u5306\u52ff\uff0c\u8f6c\u773c\u534a\u5e74\u3002 \u3000\u3000\u5728\u8fd9\u534a\u5e74\u65f6\u5149\u4e4b\u4e2d\uff0c\u65b9\u96ea\u5b9c\u4e00\u76f4\u90fd\u5728\u6253\u5750\u8c03\u606f\uff0c\u5b66\u4e60\u5410\u7eb3\u4e4b\u672f\uff0c\u9664\u4e86\u6bcf\u5929\u4e00\u6b21\u7684\u5ba4\u5916\u6563\u6b65\u4e4b\u5916\uff0c\u5f88\u5c11\u79bb\u5f00\u90a3\u95f4\u8305\u820d\u4e00\u6b65\u3002 \u3000\u3000\u9648\u5e0c\u6b63\u6bcf\u65e5\u90fd\u66ff\u90a3\u65b9\u96ea\u5b9c\u9001\u4e0a\u714e\u71ac\u7684\u836f\u6c41\uff0c\u6bcf\u65e5\u4e09\u6b21\uff0c\u4ece\u672a\u95f4\u65ad\u3002 \u3000\u3000\u8fd9\u65e5\uff0c\u5929\u8272\u5ffd\u53d8\uff0c\u5927\u96ea\u98d8\u98de\uff0c\u6574\u4e2a\u7684\u5c71\u5cf0\uff0c\u8305\u820d\uff0c\u90fd\u4e3a\u5927\u96ea\u63a9\u76d6\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u505a\u5b8c\u5348\u8bfe\uff0c\u671b\u7740\u5ba4\u5916\u79ef\u96ea\uff0c\u7f13\u6b65\u884c\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u6d41\u76ee\u56db\u987e\uff0c\u53ea\u89c1\u96ea\u5982\u9e45\u6bdb\uff0c\u4e0d\u505c\u98d8\u843d\uff0c\u6574\u4e2a\u4e16\u754c\u4e00\u7247\u7691\u767d\u3002 \u3000\u3000\u4e00\u9635\u5f3a\u52b2\u7684\u5317\u98ce\u5439\u6765\uff0c\u522e\u8d77\u5730\u4e0a\u79ef\u96ea\uff0c\u98d8\u843d\u5cf0\u4e0b\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4ef0\u8138\u671b\u5929\uff0c\u5ffd\u7136\u60f3\u8d77\u4e86\u5927\u5a18\uff0c\u4e0d\u7981\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u6697\u9053\uff1a\u201c\u5927\u5a18\u5982\u82e5\u77e5\u6653\u4e86\u5927\u4f2f\u6709\u6740\u6211\u4e4b\u5fc3\uff0c\u4e0d\u77e5\u8981\u4f5c\u4f55\u611f\u60f3\u3002\u201d \u3000\u3000\u539f\u6765\uff0c\u4ed6\u8fd9\u4e9b\u65e5\u5b50\u4e4b\u4e2d\uff0c\u805a\u7cbe\u4f1a\u795e\u7684\u4e60\u7ec3\u5410\u7eb3\u4e4b\u672f\uff0c\u5fc3\u65e0\u65c1\u9a9b\uff0c\u5fd8\u53bb\u4e86\u4eba\u95f4\u8bf8\u822c\u70e6\u607c\u3002 \u3000\u3000\u53ea\u542c\u4e00\u4e2a\u6148\u853c\u7684\u58f0\u97f3\uff0c\u81ea\u8eab\u540e\u4f20\u6765\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u53f9\u4ec0\u4e48\u6c14\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8f6c\u8138\u671b\u53bb\uff0c\u53ea\u89c1\u9648\u5e0c\u6b63\u624b\u6276\u7af9\u6756\uff0c\u8eab\u7740\u68c9\u888d\uff0c\u5934\u6234\u6be1\u5e3d\uff0c\u7ad9\u5728\u53e6\u4e00\u95f4\u8305\u820d\u95e8\u53e3\uff0c\u5f53\u4e0b\u6025\u6025\u62b1\u62f3\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u5e08\u7236\u3002\u201d \u3000\u3000\u9648\u5e0c\u6b63\u8e0f\u96ea\u800c\u6765\uff0c\u4e00\u9762\u7b11\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u53ef\u77e5\u6653\u4f60\u5230\u8fd9\u91cc\u597d\u4e45\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u7ea6\u7565\u7b97\u6765\uff0c\u603b\u5728\u534a\u5e74\u4ee5\u4e0a\u4e86\u3002\u201d \u3000\u3000\u9648\u5e0c\u6b63\u9053\uff1a\u201c\u7b97\u4e0a\u6b64\u65e5\uff0c\u4f60\u5df2\u5728\u8fd9\u5cf0\u9876\u4e4b\u4e0a\uff0c\u4f4f\u4e86\u516d\u4e2a\u6708\u53e6\u4e03\u5929\u4e86\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u534a\u5e74\u6765\u4f60\u6709\u4f55\u611f\u89c9\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u832b\u7136\u5e94\u9053\uff1a\u201c\u6ca1\u6709\u554a\uff01\u201d \u3000\u3000\u9648\u5e0c\u6b63\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u89c9\u7740\u81ea\u5df1\u548c\u8fc7\u53bb\u6709\u4f55\u4e0d\u540c\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f1f\u5b50\u89c9\u4e0d\u51fa\u6765\uff0c\u4f46\u5f1f\u5b50\u5374\u62c5\u5fc3\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u9648\u5e0c\u6b63\u5947\u9053\uff1a\u201c\u4f60\u62c5\u5fc3\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f1f\u5b50\u62c5\u5fc3\u751f\u6027\u611a\u52a3\uff0c\u96be\u767b\u6b66\u529f\u5965\u79d8\u4e4b\u5802\uff0c\u6709\u8d1f\u5e08\u7236\u4e00\u756a\u82e6\u5fc3\u3002\u201d \u3000\u3000\u9648\u5e0c\u6b63\u9053\uff1a\u201c\u90a3\u662f\u4e3a\u5e08\u62c5\u5fc3\u7684\u4e8b\u4e86\uff0c\u4f60\u53ea\u8981\u5168\u5fc3\u53bb\u5b66\u5c31\u662f\uff0c\u5176\u5b9e\uff0c\u4f60\u8fd9\u534a\u5e74\u6765\uff0c\u8fdb\u5883\u5f88\u5927\uff0c\u56fa\u7136\u662f\u4f60\u5e08\u7956\u9057\u7559\u7684\u5999\u6cd5\u795e\u5947\uff0c\u4f46\u4f60\u7684\u8fc7\u4eba\u6bc5\u529b\uff0c\u4e5f\u662f\u539f\u56e0\u4e4b\u4e00\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f1f\u5b50\u6709\u4f55\u6210\u5c31\uff0c\u6211\u600e\u4e48\u4e00\u70b9\u4e5f\u89c9\u4e0d\u51fa\u6765\uff1f\u201d \u3000\u3000\u9648\u5e0c\u6b63\u9053\uff1a\u201c\u54b1\u4eec\u8fd9\u5ea7\u5c71\u5cf0\uff0c\u56db\u9762\u9ad8\u5c71\u73af\u62b1\uff0c\u5f88\u96be\u5f97\u6709\u8fd9\u6837\u7684\u5bd2\u51b7\u5929\u6c14\uff0c\u4f46\u4eca\u65e5\u5374\u5bd2\u98ce\u523a\u9aa8\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u82e5\u6709\u6240\u609f\u5730\u554a\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u662f\u4e86\uff01\u5f1f\u5b50\u8863\u7740\u5355\u8584\uff0c\u4f46\u5374\u672a\u89c9\u5bd2\u51b7\uff0c\u90a3\u5c31\u662f\uff0c\u6211\u5df2\u6709\u7740\u5f88\u5f3a\u7684\u8010\u5bd2\u4e4b\u529b\u4e86\u3002\u201d \u3000\u3000\u9648\u5e0c\u6b63\u70b9\u70b9\u5934\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u8fd9\u534a\u5e74\u6765\u7684\u5410\u7eb3\u4e4b\u672f\uff0c\u5df2\u7136\u66ff\u4f60\u624e\u4e0b\u4e86\u4e60\u7ec3\u4e0a\u4e58\u6b66\u529f\u7684\u6839\u57fa\uff0c\u4ee5\u4f60\u773c\u4e0b\u4f53\u529b\u4e4b\u5f3a\uff0c\u4f11\u8bf4\u8fd9\u523a\u9aa8\u7684\u5bd2\u98ce\uff0c\u4f60\u5df2\u65e0\u4ece\u611f\u89c9\uff0c\u7eb5\u7136\u9677\u8eab\u96ea\u5c71\u51b0\u7a9f\u4e4b\u4e2d\uff0c\u4f60\u4e5f\u4e0d\u4f1a\u89c9\u7740\u6709\u4ec0\u4e48\u51c9\u610f\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u4e86\u4e00\u5446\u9053\uff1a\u201c\u5e08\u7236\uff0c\u5f1f\u5b50\u53ef\u662f\u5df2\u7ecf\u6709\u7740\u5bd2\u6691\u4e0d\u4fb5\u7684\u80fd\u8010\u4e86\u5417\uff1f\u201d \u3000\u3000\u9648\u5e0c\u6b63\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u6b64\u523b\u786e\u5df2\u662f\u5230\u4e86\u5bd2\u6691\u96be\u4fb5\u7684\u5730\u6b65\u3002\u201d\u62ac\u5934\u770b\u4e86\u4e00\u770b\u5929\u8272\uff0c\u957f\u957f\u4e00\u53f9\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4ece\u660e\u5929\u8d77\uff0c\u54b1\u4eec\u5e94\u8be5\u5f00\u59cb\u4f20\u5251\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u77a7\u7740\u8001\u4eba\u82cd\u767d\u7684\u8138\u8272\uff0c\u5728\u5bd2\u98ce\u4e4b\u4e2d\uff0c\u5df2\u6709\u51b7\u610f\uff0c\u4e0d\u7981\u5fc3\u4e2d\u5927\u751f\u4e0d\u5fcd\u4e4b\u5fc3\uff0c\u5fd9\u9053\uff1a\u201c\u4f60\u8001\u4eba\u5bb6\u8eab\u4f53\u4e0d\u9002\uff0c\u5f1f\u5b50\u8fd9\u7ec3\u5251\u4e4b\u4e8b\uff0c\u53ef\u5426\u5ef6\u540e\u51e0\u65e5\u3002\u201d \u3000\u3000\u9648\u5e0c\u6b63\u6148\u7709\u9661\u7136\u66b4\u8038\uff0c\u4e24\u773c\u795e\u5149\u5ffd\u73b0\uff0c\u76ef\u7740\u65b9\u96ea\u5b9c\uff0c\u4e00\u5b57\u5b57\u8bf4\u9053\uff1a\u201c\u8be5\u5b50\uff0c\u4f60\u53ef\u662f\u6709\u7740\u5077\u61d2\u4e4b\u5fc3\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f1f\u5b50\u4e0d\u6562\uff0c\u5f1f\u4e8e\u662f\u6015\u5e08\u7236\u8eab\u4f53\u652f\u6301\u4e0d\u4e86\uff1f\u201d \u3000\u3000\u9648\u5e0c\u6b63\u8138\u8272\u7565\u89c1\u7f13\u548c\u5730\u53f9\u606f\u9053\uff1a\u201c\u4e3a\u5e08\u867d\u7136\u8eab\u60a3\u4e0d\u6cbb\u4e4b\u75c5\uff0c\u76f8\u4fe1\u5728\u4e09\u4e94\u5e74\u5185\uff0c\u5c1a\u4e0d\u81f4\u6492\u624b\u5c18\u5bf0\uff0c\u5b69\u5b50\uff0c\u4f60\u4e0d\u5fc5\u62c5\u5fc3\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u606d\u58f0\u5e94\u9053\uff1a\u201c\u5f1f\u5b50\u9075\u547d\u4ece\u660e\u65e5\u8d77\u7ec3\u5251\uff0c\u53ea\u671b\u5e08\u7236\u83ab\u518d\u96be\u8fc7\uff01\u201d \u3000\u3000\u9648\u5e0c\u6b63\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u4e3a\u5e08\u7ec8\u65e5\u5f3a\u5fcd\u75c5\u9b54\u6298\u78e8\u4e4b\u82e6\uff0c\u4e3a\u7684\u5c31\u662f\u8981\u8fbe\u6210\u4f60\u5e08\u7956\u9057\u547d\uff0c\u626b\u9664\u6c5f\u6e56\u4e0a\u9b51\u9b45\u9b4d\u9b49\uff0c\u4e3a\u5e08\u597d\u4e0d\u5bb9\u6613\u624d\u62e8\u4e91\u89c1\u65e5\uff0c\u53d1\u73b0\u4f60\u8fd9\u4e00\u5757\u672a\u7ecf\u7422\u78e8\u7684\u7f8e\u7389\uff0c\u4e3a\u5e08\u5982\u4e0d\u5c3d\u6211\u6709\u751f\u4e4b\u5e74\uff0c\u9020\u5c31\u4e8e\u4f60\uff0c\u4e0d\u4f46\u5bf9\u4e0d\u8d77\u5e08\u7956\u5728\u5929\u4e4b\u7075\uff0c\u5c31\u662f\u4e3a\u5e08\u81ea\u5df1\uff0c\u4e5f\u8981\u542b\u6068\u4e5d\u6cc9\uff0c\u6b7b\u96be\u7791\u76ee\u4e86\u3002\u201d \u3000\u3000\u8bed\u97f3\u81f3\u6b64\uff0c\u9648\u5e0c\u6b63\u4f3c\u662f\u8010\u4e0d\u4e86\u5c71\u9876\u5bd2\u98ce\uff0c\u8fde\u58f0\u545b\u54b3\u4e0d\u6b62\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u5927\u4e3a\u7126\u6025\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5e08\u7236\uff0c\u5c71\u4e0a\u98ce\u5927\uff0c\u54b1\u4eec\u5230\u5c4b\u5b50\u91cc\u53bb\u8bf4\u5427\uff01\u201d \u3000\u3000\u9648\u5e0c\u6b63\u6148\u7965\u5730\u7b11\u4e86\u4e00\u7b11\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4e3a\u5e08\u7684\u867d\u7136\u96be\u8010\u523a\u9aa8\u5bd2\u98ce\uff0c\u4f46\u7ad9\u4e2a\u628a\u65f6\u8fb0\uff0c\u5c1a\u65e0\u5927\u788d\u3002\u201d \u3000\u3000\u987f\u4e86\u4e00\u987f\uff0c\u5ffd\u7136\u957f\u53f9\u4e00\u58f0\u9053\uff1a\u201c\u82cd\u5929\u65e0\u773c\uff0c\u7adf\u4ee4\u8001\u592b\u60a3\u6b64\u75c5\u75be\uff0c\u82e5\u975e\u904f\u4e0a\u4f60\u5927\u4f2f\u592b\u5987\uff0c\u771f\u4e0d\u77e5\u8001\u592b\u6709\u4f55\u9762\u76ee\u5bf9\u4f60\u5e08\u7956\u5728\u5929\u4e4b\u7075\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u5f97\u4e5f\u5927\u611f\u5fc3\u5934\u6014\u5fe1\uff0c\u5927\u4f2f\u7236\u7684\u4e3a\u4eba\uff0c\u5f80\u65e5\u5728\u4ed6\u5fc3\u4e2d\uff0c\u53ea\u662f\u7a0d\u5acc\u51b7\u9177\u65e0\u60c5\uff0c\u4f46\u81ea\u7ecf\u90d1\u5927\u4f2f\u6551\u4e86\u81ea\u5df1\u4e00\u6761\u6027\u547d\u4e4b\u540e\uff0c\u4ed6\u4e00\u76f4\u672a\u66fe\u5fd8\u6000\u5927\u4f2f\u7236\u90a3\u5239\u90a3\u95f4\u7684\u6076\u6bd2\u773c\u795e\uff0c\u534a\u5e74\u5c71\u5c45\uff0c\u7adf\u7136\u672a\u80fd\u62b9\u53bb\u5fc3\u5934\u8fd9\u4e2a\u7591\u9898\uff0c\u4f7f\u4ed6\u542c\u5230\u5e08\u7236\u63d0\u53ca\u5927\u4f2f\u7236\u4e4b\u4f59\uff0c\u7acb\u5373\u5fcd\u4e0d\u4f4f\u8131\u53e3\u9053\uff1a\u201c\u5e08\u7236\uff0c\u5f1f\u5b50\u6b64\u523b\u4ecd\u7136\u5947\u602a\uff0c\u5927\u4f2f\u7236\u56e0\u4f55\u8d77\u4e86\u8981\u6740\u6211\u4e4b\u5fc3\u5462\uff1f\u201d \u3000\u3000\u9648\u5e0c\u6b63\u6599\u4e0d\u5230\u65b9\u96ea\u5b9c\u4f1a\u7a81\u7136\u6709\u6b64\u4e00\u95ee\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u5012\u4e5f\u5446\u4e86\u4e00\u5446\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u5b69\u5b50\uff0c\u4f60\u6b66\u529f\u7ec3\u6210\u4ee5\u540e\uff0c\u4e0b\u5c71\u4e4b\u65e5\uff0c\u4e0d\u5c31\u5f88\u5feb\u53ef\u4ee5\u67e5\u660e\u767d\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u60f3\uff1a\u5e08\u7236\u8bf4\u7684\u4e0d\u9519\uff0c\u770b\u6765\u6b64\u4e8b\u53ea\u6709\u7b49\u5230\u81ea\u5df1\u6b66\u529f\u5927\u6210\uff0c\u4e0b\u5c71\u80fd\u6551\u4e2d\u5dde\u4e09\u4fa0\u4e4b\u5371\u65f6\uff0c\u65b9\u80fd\u660e\u767d\u4e86\u3002\u5f53\u4e0b\u5e94\u58f0\u9053\uff1a\u201c\u5e08\u7236\u6559\u8bad\u7684\u662f\uff01\u201d \u3000\u3000\u9648\u5e0c\u6b63\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4e3a\u5e08\u987b\u56de\u5230\u5c4b\u91cc\u53bb\u7a0d\u4f5c\u51c6\u5907\uff0c\u4f60\u4e14\u5728\u8fd9\u513f\u8fce\u7740\u8fd9\u523a\u9aa8\u5bd2\u98ce\uff0c\u91cd\u628a\u5410\u7eb3\u4e4b\u672f\u4ed4\u7ec6\u5730\u6e29\u4e60\u4e24\u904d\uff0c\u672a\u6765\u7684\u65e5\u5b50\u4e4b\u5185\uff0c\u5251\u672f\u80fd\u5426\u5927\u6210\uff0c\u5c31\u5728\u4f60\u8fd9\u534a\u5e74\u7684\u6210\u5c31\u9ad8\u4f4e\u800c\u8bba\u4e86\u3002\u201d \u3000\u3000\u8bdd\u58f0\u4e00\u843d\uff0c\u7f13\u7f13\u7b56\u6756\u800c\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u53ea\u89c9\u5f97\u5e08\u7236\u7684\u53e3\u6c14\uff0c\u867d\u7136\u6e29\u548c\uff0c\u4f46\u8a00\u4e0b\u4e4b\u610f\uff0c\u5374\u5341\u5206\u6c89\u91cd\uff0c\u81ea\u5df1\u80a9\u8d1f\u8d23\u4efb\u4e4b\u5927\uff0c\u4ee4\u4ed6\u7a81\u7136\u751f\u51fa\u4e86\u51db\u6000\u4e4b\u5fc3\uff0c\u773c\u770b\u5e08\u7236\u9f99\u949f\u7684\u80cc\u5f71\u6d88\u5931\uff0c\u7acb\u5373\u51dd\u795e\u4e00\u5fd7\uff0c\u8fce\u7740\u90a3\u547c\u5578\u7684\u5bd2\u98ce\uff0c\u56e2\u76ee\u76d8\u5750\uff0c\u4f9d\u7167\u5e08\u7236\u6240\u6559\uff0c\u5341\u5206\u4ed4\u7ec6\u5730\u4f5c\u7740\u90a3\u5410\u7eb3\u5de5\u592b\u3002 \u3000\u3000\u7247\u523b\u4e4b\u95f4\uff0c\u5df2\u5165\u4eba\u6211\u4e24\u5fd8\u5883\u754c\u3002 \u3000\u3000\u6253\u7b2c\u4e8c\u5929\u8d77\uff0c\u9648\u5e0c\u6b63\u679c\u7136\u5f00\u59cb\u6307\u70b9\u4ed6\u4e60\u5251\u672f\u638c\u6cd5\uff0c\u65b9\u96ea\u5b9c\u539f\u672c\u542c\u8bf4\u5251\u638c\u4e24\u95e8\u6b66\u529f\uff0c\u8981\u8d39\u65f6\u4e09\u5e74\uff0c\u65b9\u80fd\u6709\u6240\u5c0f\u6210\u4e4b\u65f6\uff0c\u8fd8\u662f\u6709\u4e9b\u4e0d\u5927\u76f8\u4fe1\uff0c\u4f46\u5728\u4ed6\u7ec3\u4e86\u51e0\u5929\u4e4b\u540e\uff0c\u624d\u53d1\u73b0\u5e08\u7236\u6240\u8bf4\u4e0d\u5047\uff0c\u8fd9\u5957\u5251\u6cd5\u4e4b\u7cbe\u6df1\u7384\u5965\uff0c\u7eb5\u7136\u662f\u4e00\u4e2a\u624d\u667a\u7edd\u4ee3\u7684\u4e4b\u4eba\u4e60\u7ec3\uff0c\u4e5f\u975e\u6709\u4e09\u5e74\u5de5\u592b\u4e0d\u80fd\u5f97\u5176\u7cbe\u9ad3\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u60f3\u662f\u81ea\u77e5\u81ea\u5df1\u7684\u624d\u534e\uff0c\u79bb\u90a3\u624d\u667a\u7edd\u4ee3\u56db\u5b57\u751a\u8fdc\uff0c\u662f\u4ee5\u5e08\u7236\u6240\u6559\u7684\u4e00\u8a00\u4e00\u8bed\uff0c\u4e00\u62db\u4e00\u5f0f\uff0c\u4ed6\u65e0\u4e0d\u5168\u795e\u8d2f\u6ce8\uff0c\u4ed4\u7ec6\u80dc\u542c\uff0c\u800c\u4e14\u9664\u4e86\u65e5\u98df\u4e09\u9910\uff0c\u591c\u7720\u4e00\u5bbf\u4e4b\u5916\uff0c\u65e0\u65f6\u65e0\u523b\u4e0d\u5728\u82e6\u7ec3\u5251\u62db\u548c\u638c\u6cd5\u3002 \u3000\u3000\u5c81\u6708\u4e0d\u5c45\uff0c\u7531\u4e2d\u5df2\u89c1\u7ea2\u53f6\u6ee1\u8c37\u3002 \u3000\u3000\u8fd9\u4e00\u5929\uff0c\u65b9\u96ea\u5b9c\u521a\u81ea\u7ec3\u5b8c\u5251\u672f\u4e2d\u6700\u540e\u7684\u4e00\u62db\u201c\u4e07\u6d41\u5f52\u5b97\u201d\uff0c\u9648\u5e0c\u6b63\u7adf\u7136\u957f\u957f\u5730\u5411\u7740\u90a3\u6ee1\u5c71\u7ea2\u53f6\uff0c\u5410\u51fa\u4e00\u53e3\u5927\u6c14\u3002 \u3000\u3000\u663e\u7136\uff0c\u8fd9\u4f4d\u4e00\u4ee3\u5251\u795e\u8001\u4eba\uff0c\u4eff\u4f5b\u653e\u4e0b\u4e86\u4e00\u526f\u91cd\u62c5\u822c\u5730\u677e\u4e86\u4e00\u53e3\u6c14\u3002 \u3000\u3000\u591a\u5c11\u5e74\u6765\u7684\u5fc3\u613f\uff0c\u8fd9\u4e00\u5239\u90a3\u4e4b\u95f4\uff0c\u603b\u7b97\u6709\u4e86\u4e2a\u4ea4\u4ee3\u3002 \u3000\u3000\u4ed6\u77a7\u7740\u65b9\u96ea\u5b9c\u90a3\u98de\u8dc3\u7684\u8eab\u5f71\uff0c\u82cd\u767d\u7684\u8138\u4e0a\uff0c\u9732\u51fa\u4e00\u4e1d\u5b89\u6170\u7684\u5fae\u7b11\uff0c\u8fd9\u7b11\u5bb9\u5341\u5206\u51c4\u51c9\uff0c\u4e5f\u975e\u5e38\u75db\u82e6\uff0c\u90a3\u7709\u68a2\u773c\u89d2\uff0c\u7adf\u662f\u51fa\u73b0\u4e86\u4e24\u70b9\u67af\u7aed\u7684\u8001\u6cea\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u65b9\u96ea\u5b9c\u6b63\u5728\u5168\u795e\u7ec3\u5251\uff0c\u672a\u80fd\u53d1\u73b0\u5e08\u7236\u7684\u795e\u60c5\uff0c\u5426\u5219\uff0c\u4ed6\u4e00\u5b9a\u4f1a\u4e22\u4e0b\u624b\u4e2d\u957f\u5251\uff0c\u5954\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u6562\u60c5\u9648\u5e0c\u6b63\u8fd9\u65f6\u5df2\u6655\u5012\u5728\u90a3\u5757\u957f\u5f62\u5de8\u77f3\u4e4b\u4e0a\u3002 \u3000\u3000\u5f85\u5f97\u65b9\u96ea\u5b9c\u81ea\u8ba4\u5df2\u628a\u8fd9\u62db\u201c\u4e07\u6d41\u5f52\u5b97\u201d\u7684\u4e94\u4e2a\u53d8\u5316\u5168\u5df2\u8bb0\u5728\u5fc3\u5934\uff0c\u65b9\u59cb\u6536\u62db\u4f4f\u624b\uff0c\u7b11\u9053\uff1a\u201c\u5e08\u7236\uff0c\u2026\u2026\u201d\u4ed6\u8bdd\u97f3\u752b\u51fa\uff0c\u7acb\u5373\u8138\u8272\u5927\u53d8\uff0c\u4e00\u8dc3\u5411\u524d\uff0c\u6276\u8d77\u9648\u5e0c\u6b63\u8eba\u5728\u5de8\u77f3\u4e4b\u4e0a\u7684\u67af\u7626\u8eab\u5b50\uff0c\u8fde\u8fde\u53eb\u7740\u5e08\u7236\uff0c\u53cc\u624b\u66f4\u4e0d\u505c\u5730\u5728\u8001\u4eba\u5bb6\u8eab\u4e0a\u63a8\u62ff\u3002\u534a\u664c\uff0c\u9648\u5e0c\u6b63\u65b9\u59cb\u7f13\u7f13\u7741\u5f00\u53cc\u773c\uff0c\u4e4f\u529b\u5730\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u70ed\u6cea\u76c8\u7736\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u8001\u600e\u4e48\u4e86\uff1f\u5f1f\u5b50\u62b1\u4f60\u56de\u53bb\u5427\uff01\u201d \u3000\u3000\u9648\u5e0c\u6b63\u6447\u4e86\u6447\u5934\uff0c\u6709\u6c14\u65e0\u529b\u5730\u95ee\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u8fd9\u62db\u2018\u4e07\u6d41\u5f52\u5b97\u2019\u4f60\u53ef\u8bb0\u719f\u4e86\uff1f\u201d \u3000\u3000\u8fd9\u7b49\u65f6\u523b\uff0c\u8001\u4eba\u8fd8\u5728\u60e6\u8bb0\u7740\u90a3\u62db\u5251\u6cd5\uff0c\u53ea\u628a\u65b9\u96ea\u5b9c\u542c\u5f97\u5fc3\u4e2d\u5927\u611f\u9707\u60b8\uff0c\u5f53\u4e0b\u8fde\u5fd9\u5e94\u58f0\u9053\uff1a\u201c\u5f1f\u5b50\u8bb0\u719f\u4e86\uff0c\u5e08\u7236\uff0c\u5f1f\u5b50\u80cc\u4f60\u8fdb\u5c4b\u91cc\u53bb\u3002\u201d \u3000\u3000\u9648\u5e0c\u6b63\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u518d\u7ec3\u4e00\u904d\u7ed9\u6211\u770b\u770b\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u660e\u77e5\u5e08\u7236\u5df2\u662f\u4f53\u529b\u4e0d\u7acb\uff0c\u4e0d\u5b9c\u5728\u8fd9\u5c71\u4e0a\u98ce\u5927\u4e4b\u5904\u505c\u7559\uff0c\u4f46\u5e08\u7236\u6240\u547d\uff0c\u5374\u53c8\u4e0d\u80fd\u4e0d\u9075\uff0c\u53ea\u597d\u6276\u7740\u8001\u4eba\u5750\u597d\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5f1f\u5b50\u9075\u547d\uff01\u201d\u8f6c\u8eab\u62fe\u8d77\u957f\u5251\uff0c\u628a\u90a3\u62db\u201c\u4e07\u6d41\u5f52\u5b97\u201d\u7684\u4e94\u5f0f\u53d8\u62db\uff0c\u4e00\u6c14\u5475\u6210\u5730\u65bd\u5c55\u4e86\u4e00\u904d\u3002 \u3000\u3000\u9648\u5e0c\u6b63\u65e0\u65b9\u5730\u70b9\u4e86\u70b9\u5934\u9053\uff1a\u201c\u5f88\u597d\uff0c\u5f88\u597d\u2026\u2026\u201d \u3000\u3000\u8001\u4eba\u8fde\u8bf4\u4e24\u4e2a\u5f97\u597d\u4ee5\u540e\uff0c\u4f3c\u662f\u5df2\u7136\u76f8\u5f53\u5403\u529b\uff0c\u63a2\u624b\u81ea\u6000\u4e2d\u53d6\u51fa\u4e00\u5c0f\u5c0f\u7684\u7531\u7389\u78c1\u74f6\uff0c\u5012\u4e86\u534a\u5929\uff0c\u53ea\u5012\u51fa\u4e00\u9897\u7eff\u8c46\u5927\u5c0f\u7684\u9ed1\u8272\u836f\u4e38\uff0c\u795e\u60c5\u6709\u4e9b\u60b2\u58ee\u7684\u7eb3\u5165\u53e3\u4e2d\u3002 \u3000\u3000\u4ed6\u8fd9\u756a\u4e3e\u52a8\uff0c\u5341\u5206\u5feb\u6377\uff0c\u662f\u4ee5\u65b9\u96ea\u5b9c\u5e76\u672a\u89c1\u5230\uff0c\u836f\u4e38\u4e00\u65e6\u5165\u53e3\uff0c\u8001\u4eba\u795e\u8272\u5927\u89c1\u632f\u594b\uff0c\u90a3\u672c\u5df2\u9eef\u7136\u65e0\u8272\u7684\u773c\u795e\uff0c\u5ffd\u7136\u53d8\u5f97\u70af\u70af\u53d1\u5149\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u6536\u4f4f\u4e86\u5251\u52bf\uff0c\u8d70\u5230\u8001\u4eba\u8eab\u524d\u5ffd\u7136\u4e00\u6014\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u8001\u4eba\u5bb6\u597d\u4e9b\u4e86\u5417\uff1f\u201d\u9648\u5e0c\u6b63\u5475\u5475\u4e00\u7b11\u9053\uff1a\u201c\u4e3a\u5e08\u53ea\u662f\u6c14\u8840\u653b\u5fc3\uff0c\u4ee5\u81f4\u6655\u53a5\u77f3\u4e0a\uff0c\u7ecf\u4f60\u4e00\u9635\u63a8\u62ff\uff0c\u5df2\u662f\u5927\u4e3a\u597d\u8f6c\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u89c1\u5e08\u7236\u7cbe\u795e\u5955\u5955\uff0c\u5fc3\u4e2d\u4e5f\u5341\u5206\u9ad8\u5174\uff0c\u7b11\u9053\uff1a\u201c\u5e08\u7236\uff0c\u8981\u4e0d\u8981\u5f1f\u5b50\u518d\u7ec3\u4e00\u904d\u7ed9\u4f60\u8001\u4eba\u5bb6\u770b\uff1f\u201d \u3000\u3000\u9648\u5e0c\u6b63\u7b11\u9053\uff1a\u201c\u4e0d\u5fc5\u4e86\uff0c\u8fd9\u62db\u5251\u6cd5\u4e94\u79cd\u53d8\u62db\uff0c\u4f60\u679c\u7136\u5df2\u7ecf\u8bb0\u719f\uff0c\u53ea\u8981\u4f60\u80fd\u5728\u4e0e\u4eba\u52a8\u624b\u4e4b\u65f6\uff0c\u518d\u4f53\u4f1a\u4e00\u4e0b\u5e94\u53d8\u7684\u673a\u667a\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u8bdd\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u638c\u6cd5\u53ef\u90fd\u8bb0\u719f\u5728\u80f8\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f1f\u5b50\u89c9\u51fa\u638c\u6cd5\u4f3c\u662f\u6bd4\u5251\u6cd5\u8981\u5bb9\u6613\u8bb0\u5f97\u591a\uff0c\u5e08\u7236\u8981\u4e0d\u8981\u5f1f\u5b50\u7ec3\u4e0a\u4e00\u6b21\u3002\u201d \u3000\u3000\u9648\u5e0c\u6b63\u7b11\u9053\uff1a\u201c\u4e0d\u5fc5\u7ec3\u4e86\uff0c\u4e3a\u5e08\u76f8\u4fe1\u4f60\u5fc5\u7136\u8bb0\u5f97\uff0c\u5b69\u5b50\uff0c\u4f60\u8be5\u77e5\u9053\uff0c\u4e3a\u5e08\u6240\u4ee5\u80fd\u5236\u670d\u90a3\u4e94\u9b54\u7684\u9053\u7406\uff0c\u5c31\u662f\u56e0\u4e3a\u4e3a\u5e08\u7684\u5251\u6cd5\u5df2\u5230\u65e0\u654c\u5883\u754c\uff0c\u4e5f\u6b63\u56e0\u6b64\uff0c\u4e3a\u5e08\u624d\u6709\u4e86\u5251\u795e\u4e4b\u79f0\uff0c\u8bd5\u60f3\u8fd9\u5957\u5251\u6cd5\u5982\u662f\u90a3\u7b49\u5e73\u51e1\u6613\u5b66\uff0c\u53c8\u600e\u80fd\u7ed9\u4e3a\u5e08\u535a\u6765\u5251\u795e\u7684\u540d\u671b\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5931\u7b11\u9053\uff1a\u201c\u5e08\u7236\u8bf4\u7684\u662f\uff0c\u5f1f\u5b50\u53ea\u56e0\u7ec8\u65e5\u8ddf\u968f\u5e08\u7236\uff0c\u5fc3\u76ee\u4e2d\u53ea\u6709\u5e08\u7236\u662f\u6211\u7684\u4eb2\u4eba\uff0c\u76f4\u4f3c\u4e25\u7236\u6148\u6bcd\u4e00\u822c\uff0c\u6545\u800c\u628a\u5e08\u7236\u7684\u5251\u795e\u540d\u53f7\u65e9\u5c31\u5fd8\u4e86\uff01\u201d \u3000\u3000\u9648\u5e0c\u6b63\u7b11\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u8fd9\u5012\u662f\u4e3a\u5e08\u6ca1\u6709\u60f3\u5230\u7684\u4e8b\uff0c\u4e0d\u9519\uff0c\u4e00\u4e2a\u4eba\u5bf9\u81ea\u5df1\u7684\u4eb2\u4eba\uff0c\u662f\u4e0d\u5bb9\u6613\u8bb0\u5f97\u4ed6\u7684\u5a01\u671b\u540d\u58f0\u7684\uff0c\u5426\u5219\uff0c\u90a3\u4e9b\u8fbe\u5b98\u8d35\u4eba\u7684\u59bb\u5b50\u513f\u5973\uff0c\u53ef\u5c31\u4e00\u5929\u5230\u665a\u60f3\u8ddf\u81ea\u5df1\u7684\u7236\u7956\u592b\u541b\u4eec\uff0c\u8bf4\u4e0a\u4e00\u53e5\u4f53\u5df1\u8bdd\u90fd\u4e0d\u53ef\u80fd\u4e86\u2026\u2026\u201d\u4ed6\u7565\u4e3a\u6b47\u4e86\u6b47\uff0c\u5fae\u7b11\u63a5\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u81ea\u4ece\u4e3a\u5e08\u8981\u4f60\u7ec3\u4e60\u5251\u638c\u4e24\u95e8\u6b66\u529f\u4ee5\u6765\uff0c\u5230\u5982\u4eca\u5df2\u6709\u591a\u4e45\uff0c\u4f60\u53ef\u8bb0\u5f97\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u82b1\u5f00\u53f6\u843d\uff0c\u597d\u50cf\u5df2\u5386\u4e09\u6b21\u4e86\uff0c\u5e08\u7236\uff0c\u662f\u4e0d\u662f\u5df2\u7ecf\u4e09\u5e74\u5566\uff1f\u201d \u3000\u3000\u9648\u5e0c\u6b63\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4ee5\u4f60\u773c\u4e0b\u4e4b\u80fd\uff0c\u5982\u679c\u8e0f\u5165\u6c5f\u6e56\uff0c\u5df2\u53ef\u5217\u5165\u4e00\u6d41\u9ad8\u624b\u4e4b\u5217\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e08\u7236\uff0c\u5f1f\u5b50\u5012\u771f\u7684\u4e0d\u4f1a\u89c9\u7740\u6709\u4f55\u7cbe\u8fdb\u4e4b\u5904\uff0c\u4f60\u8001\u8fd9\u4e48\u8bf4\u53ef\u6559\u5f1f\u5b50\u6709\u4e9b\u4e0d\u89e3\u2026\u2026\u201d \u3000\u3000\u9648\u5e0c\u6b63\u7b11\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u4e14\u51dd\u805a\u5341\u6210\u529f\u529b\uff0c\u5bf9\u7740\u90a3\u68f5\u8eab\u524d\u7684\u67af\u677e\uff0c\u53d1\u51fa\u4e00\u5251\u8bd5\u8bd5\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u77a7\u4e86\u90a3\u68f5\u67af\u677e\u4e00\u773c\uff0c\u89c9\u51fa\u79bb\u5f00\u81ea\u5df1\u7ea6\u6709\u4e08\u8bb8\u4e4b\u8fdc\uff0c\u5f53\u4e0b\u53f3\u624b\u6301\u5251\uff0c\u6697\u6697\u8c03\u5747\u4e00\u53e3\u771f\u6c14\uff0c\u731b\u7136\u6325\u51fa\u4e00\u5251\u3002\u4f46\u95fb\u9510\u5578\u751f\u98ce\u5251\u5f71\u4e00\u53d1\u5373\u6536\uff0c\u90a3\u68f5\u67af\u677e\u4f9d\u7136\u633a\u7acb\u539f\u5904\uff0c\u8fde\u52a8\u90fd\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u9893\u7136\u7684\u5782\u4e0b\u957f\u5251\uff0c\u5931\u58f0\u9053\uff1a\u201c\u5e08\u7236\uff0c\u5f1f\u5b50\u53ea\u6015\u6709\u8d1f\u6559\u8bf2\u4e86\u3002\u201d \u3000\u3000\u9648\u5e0c\u6b63\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e09\u5e74\u82e6\u7ec3\uff0c\u8fde\u4e00\u4e08\u4ee5\u5916\u7684\u67af\u677e\uff0c\u90fd\u65e0\u6cd5\u8fd0\u5251\u9707\u53ca\uff0c\u5c82\u975e\u662f\u5f1f\u5b50\u8d44\u8d28\u592a\u4ee5\u9c81\u949d\uff0c\u7a7a\u8d39\u4e86\u5e08\u7236\u7684\u4e00\u756a\u5fc3\u8840\u5417\uff1f\u201d \u3000\u3000\u9648\u5e0c\u6b63\u7b11\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u53bb\u63a8\u4e00\u4e0b\u90a3\u68f5\u67af\u677e\u8bd5\u8bd5\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u4e86\u4e00\u5446\u9053\uff1a\u201c\u5e08\u7236\u8981\u6211\u2026\u2026\u201d \u3000\u3000\u9648\u5e0c\u6b63\u9053\uff1a\u201c\u4f60\u53bb\u8f7b\u8f7b\u5730\u63a8\u4e00\u4e0b\u8bd5\u8bd5\u5427\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7565\u4e00\u72b9\u8c6b\uff0c\u7ec8\u4e8e\u8fd8\u662f\u4f9d\u8a00\u8d70\u4e86\u8fc7\u53bb\uff0c\u4f38\u624b\u5411\u90a3\u68f5\u67af\u677e\u6309\u53bb\u3002 \u3000\u3000\u53ea\u542c\u5f97\u201c\u54af\u201d\u4e00\u58f0\u8f7b\u54cd\u5165\u8033\uff0c\u987f\u65f6\u679d\u53f6\u6a2a\u98de\uff0c\u90a3\u68f5\u67af\u677e\u7adf\u7136\u5e94\u624b\u800c\u5012\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u60ca\u5f97\u5012\u8df3\u800c\u56de\uff0c\u6014\u6014\u5730\u9053\uff1a\u201c\u5e08\u7236\uff0c\u8fd9\u682a\u677e\u6811\u600e\u4e48\u65e9\u5c31\u6298\u65ad\u4e86\u5417\uff1f\u201d \u3000\u3000\u9648\u5e0c\u6b63\u5bf9\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u521a\u624d\u8fd0\u5251\u4e00\u6325\uff0c\u5df2\u5c06\u6b64\u6811\u524a\u65ad\uff0c\u98ce\u56e0\u5251\u5203\u592a\u8fc7\u7280\u5229\uff0c\u800c\u53c8\u5feb\u901f\u65e0\u6bd4\uff0c\u6240\u4ee5\u6811\u8eab\u867d\u65ad\uff0c\u5374\u4f9d\u7136\u672a\u66fe\u5012\u4e0b\u53bb\uff0c\u5b69\u5b50\uff0c\u4f60\u6b64\u523b\u603b\u76f8\u4fe1\u4e3a\u5e08\u6240\u8bf4\uff0c\u5e76\u4e0d\u662f\u9a97\u4f60\u7684\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4ece\u60ca\u695e\u4e4b\u4e2d\u9192\u89c9\uff0c\u767b\u65f6\u773c\u4e2d\u6ee1\u7736\u70ed\u6cea\u7684\u6251\u5411\u8001\u4eba\u8eab\u524d\uff0c\u5927\u58f0\u9053\uff1a\u201c\u5e08\u7236\u2026\u2026\u201d \u3000\u3000\u4ed6\u6fc0\u52a8\u8fc7\u751a\uff0c\u4e00\u65f6\u4e4b\u95f4\u7adf\u7136\u8fde\u8bdd\u90fd\u8bf4\u4e0d\u51fa\u6765\u4e86\uff0c\u6562\u60c5\u4ed6\u4ece\u672a\u60f3\u5230\u81ea\u5df1\u7684\u6b66\u529f\uff0c\u5728\u77ed\u77ed\u7684\u4e09\u5e74\u516d\u6708\u4e4b\u4e2d\uff0c\u5c45\u7136\u7cbe\u8fdb\u5230\u8fd9\u7b49\u795e\u5947\u5730\u6b65\uff0c\u5e08\u7236\u6559\u8bf2\u4e4b\u6069\uff0c\u5b9b\u4f3c\u4e00\u80a1\u6c79\u6d8c\u6d77\u6d9b\uff0c\u4ece\u4ed6\u5fc3\u5e95\u5347\u8d77\uff0c\u4f7f\u4ed6\u6df1\u6df1\u5730\u4f53\u4f1a\u5230\u8fd9\u7b49\u6069\u540c\u518d\u9020\u7684\u5927\u5fb7\uff0c\u81ea\u5df1\u7eb5\u662f\u7c89\u8eab\u788e\u9aa8\uff0c\u4e5f\u96be\u4ee5\u4e3a\u62a5\u3002 \u3000\u3000\u9648\u5e0c\u6b63\u4f38\u624b\u8f7b\u629a\u7740\u65b9\u96ea\u5b9c\uff0c\u7b11\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u83ab\u8981\u6fc0\u52a8\uff0c\u4f60\u7684\u6b66\u529f\u867d\u7136\u5df2\u53ef\u5217\u5165\u4e00\u6d41\u9ad8\u624b\uff0c\u4f46\u60f3\u91cd\u632f\u5251\u795e\u96c4\u98ce\uff0c\u8fd8\u662f\u5acc\u7740\u4e0d\u8db3\u3002\u201d \u3000\u3000\u4ef0\u5929\u4e00\u53f9\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u4e3a\u5e08\u5df2\u5c3d\u6240\u80fd\uff0c\u4f60\u90a3\u5e08\u7956\u5251\u6cd5\u4e4b\u4e2d\u7684\u51e0\u5904\u6781\u4e3a\u7384\u5999\u4e4b\u62db\uff0c\u8fde\u4e3a\u5e08\u4e5f\u672a\u80fd\u9886\u609f\uff0c\u5b9e\u662f\u65e0\u6cd5\u518d\u9020\u5c31\u4e8e\u4f60\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u6d51\u8eab\u4e00\u9707\uff0c\u84e6\u5730\u4ef0\u5934\u9053\uff1a\u201c\u5e08\u7236\uff0c\u5f1f\u5b50\u8499\u4f60\u8001\u4eba\u5bb6\u6559\u8bf2\u4e4b\u6069\uff0c\u5df2\u662f\u7ec8\u751f\u96be\u62a5\u4e86\u2026\u2026\u201d \u3000\u3000\u9648\u5e0c\u6b63\u5ffd\u7136\u76ee\u5149\u4e00\u9eef\uff0c\u5341\u5206\u4e25\u8083\u4ed6\u8bf4\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u5e08\u7956\u7684\u9057\u5fd7\uff0c\u4e3a\u5e08\u7ec8\u65e5\u803f\u803f\u4e8e\u6000\uff0c\u4eca\u540e\u8fd9\u5927\u8d23\u91cd\u4efb\uff0c\u5df2\u843d\u5728\u4f60\u7684\u8eab\u4e0a\uff0c\u4ee5\u4f60\u773c\u4e0b\u6b66\u529f\uff0c\u5374\u53c8\u4ecd\u5acc\u4e0d\u8db3\uff0c\u4e3a\u5e08\u60f3\u6765\u60f3\u53bb\uff0c\u53ea\u6709\u8ba9\u4f60\u4e0b\u5c71\uff0c\u53bb\u89c1\u4e00\u4e2a\u4eba\uff0c\u5f53\u4eca\u4e4b\u4e16\uff0c\u4e5f\u53ea\u6709\u4ed6\u624d\u80fd\u4f7f\u4f60\u628a\u5e08\u7956\u795e\u5251\u7684\u7cbe\u5965\u4e4b\u5904\uff0c\u5b8c\u5168\u9886\u4f1a\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e08\u7236\uff0c\u5f1f\u5b50\u4e0d\u60f3\u4e0b\u5c71\u3002\u201d \u3000\u3000\u9648\u5e0c\u6b63\u4e00\u6014\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f1f\u5b50\u8981\u5728\u8fd9\u513f\u968f\u4f8d\u5e08\u7236\u554a\uff01\u201d \u3000\u3000\u9648\u5e0c\u6b63\u9661\u5730\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u4e16\u4e0a\u6c38\u65e0\u4e0d\u6563\u7684\u5bb4\u5e2d\uff0c\u5b69\u5b50\uff0c\u4f60\u522b\u50bb\u4e86\uff0c\u4e3a\u4e86\u5e08\u7956\u9057\u5fd7\uff0c\u4e3a\u5e08\u5df2\u8d39\u5c3d\u4e86\u5fc3\u8840\uff0c\u773c\u770b\u5927\u529f\u5df2\u8fd1\u544a\u6210\u4e4b\u65e5\uff0c\u5b69\u5b50\uff0c\u4f60\u5c82\u53ef\u4e3a\u4e86\u4e0d\u653e\u5fc3\u4e3a\u5e08\u4e00\u4eba\u5728\u6b64\uff0c\u800c\u7f6e\u5e08\u7956\u9057\u5fd7\u4e8e\u4e0d\u987e\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6014\u4e86\u4e00\u6014\u4f4e\u5934\u4e0d\u6562\u8bf4\u8bdd\u3002 \u3000\u3000\u9648\u5e0c\u6b63\u6df1\u6df1\u5730\u5438\u4e86\u53e3\u6c14\uff0c\u53c8\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u8fd9\u5c31\u56de\u53bb\u6536\u62fe\u8863\u7269\uff0c\u4e3a\u5e08\u7acb\u5373\u4fee\u4e66\u4e00\u5c01\uff0c\u4ea4\u4f60\u5e26\u7ed9\u90a3\u4f4d\u53bb\u89c1\u4e4b\u4eba\uff0c\u5979\u89c1\u5230\u4e3a\u5e08\u7684\u4e66\u4fe1\uff0c\u5fc5\u7136\u4f1a\u6307\u70b9\u4e8e\u4f60\uff0c\u4f7f\u4f60\u6210\u4e3a\u4e3e\u4e16\u65e0\u654c\u7684\u9ad8\u624b\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u867d\u662f\u5145\u6ee1\u4e86\u4e0d\u613f\u79bb\u5c71\u4e4b\u610f\uff0c\u4f46\u5374\u534a\u53e5\u4e5f\u4e0d\u6562\u8bf4\u51fa\u6765\uff0c\u53ea\u597d\u9ed8\u9ed8\u5730\u968f\u5728\u5e08\u7236\u8eab\u540e\uff0c\u5411\u5c4b\u5185\u884c\u53bb\u3002 \u3000\u3000\u9ec4\u660f\u65f6\u5206\uff0c\u65b9\u96ea\u5b9c\u521a\u521a\u6253\u70b9\u597d\u4e86\u8863\u7269\uff0c\u9648\u5e0c\u6b63\u624b\u4e2d\u5df2\u62ff\u4e86\u4e00\u4e2a\u5bbd\u5927\u7684\u4fe1\u5c01\uff0c\u7f13\u6b65\u8d70\u6765\uff0c\u7b11\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u8863\u7269\u51c6\u5907\u597d\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f1f\u5b50\u6b64\u53bb\u4e0d\u4e45\uff0c\u6240\u4ee5\u65e9\u5df2\u51c6\u5907\u597d\u4e86\u3002\u201d \u3000\u3000\u9648\u5e0c\u6b63\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u8981\u53bb\u89c1\u7684\u4eba\uff0c\u4e43\u662f\u4e3a\u5e08\u7684\u4e00\u4f4d\u540c\u95e8\u5e08\u59b9\uff0c\u4e5f\u7b97\u662f\u4f60\u7684\u5e08\u53d4\u4e86\uff0c\u8fd9\u5c01\u4fe1\u7684\u80cc\u540e\uff0c\u4e3a\u5e08\u5df2\u66ff\u4f60\u753b\u4e0b\u4e86\u5979\u90a3\u4f4f\u5904\u7684\u5165\u5c71\u9014\u5f84\uff0c\u5929\u5df2\u4e0d\u65e9\uff0c\u4f60\u8fd9\u5c31\u4e0b\u5c71\u53bb\u5427\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u606d\u656c\u7684\u63a5\u8fc7\u4e86\u4fe1\u5c01\uff0c\u76ee\u5149\u4e00\u77a5\uff0c\u53ea\u89c1\u90a3\u4fe1\u5c01\u4e0a\u5206\u4f5c\u4e09\u884c\u7aef\u7aef\u6b63\u6b63\u5730\u5199\u7740\uff1a \u3000\u3000\u201c\u656c\u91d1\u9876\u795e\u5c3c\u4f5b\u9a7e\uff0c\u9648\u5e0c\u6b63\u624b\u6258\u201d\u5341\u4e09\u4e2a\u5927\u5b57\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u8fd9\u4f4d\u5e08\u53d4\u539f\u6765\u662f\u4e2a\u51fa\u5bb6\u4eba\uff1f\u2026\u2026\u201d\u4f46\u53e3\u4e2d\u5374\u606d\u656c\u5730\u5e94\u9053\uff1a\u201c\u5f1f\u5b50\u9075\u547d\uff01\u201d \u3000\u3000\u4ed6\u63e3\u597d\u4e86\u4fe1\u5c01\uff0c\u80cc\u8d77\u8863\u7269\u5411\u9648\u5e0c\u6b63\u62dc\u4e86\u4e00\u62dc\u9053\uff1a\u201c\u5e08\u7236\uff0c\u5f1f\u5b50\u89c1\u5230\u5e08\u53d4\u4e4b\u540e\uff0c\u4e0d\u77e5\u80fd\u4e0d\u80fd\u7acb\u5373\u56de\u6765\u53e9\u89c1\u5e08\u7236\u5462\uff1f\u201d \u3000\u3000\u9648\u5e0c\u6b63\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u90a3\u5e08\u53d4\u89c1\u5230\u4e3a\u5e08\u4e66\u4fe1\u4e4b\u540e\uff0c\u81ea\u4f1a\u7acb\u5373\u4f20\u4f60\u6b66\u529f\uff0c\u662f\u5426\u80fd\u5f88\u5feb\u56de\u6765\u6b64\u5904\uff0c\u8fd9\u5c31\u8981\u770b\u4f60\u7684\u609f\u6027\u4e86\u2026\u2026\u201d \u3000\u3000\u8bdd\u97f3\u672a\u5df2\uff0c\u7a81\u7136\u76ee\u5149\u4e4b\u4e2d\u73b0\u51fa\u6cea\u75d5\uff0c\u4f46\u4ed6\u8fc5\u901f\u7684\u4fa7\u8f6c\u8eab\u8eaf\uff0c\u4e0d\u8ba9\u65b9\u96ea\u5b9c\u770b\u89c1\uff0c\u54fc\u4e00\u58f0\u9053\uff1a\u201c\u65e9\u4e9b\u52a8\u8eab\u5427\uff0c\u4e3a\u5e08\u4f46\u613f\u4f60\u56de\u6765\u89c1\u6211\u4e4b\u65f6\uff0c\u5df2\u662f\u6b66\u529f\u5927\u6210\u4e4b\u65e5\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u604b\u604b\u4e0d\u820d\u5730\u53c8\u62dc\u4e86\u4e00\u62dc\uff0c\u8fd9\u624d\u545c\u54bd\u9053\uff1a\u201c\u5e08\u7236\uff0c\u5f1f\u5b50\u53bb\u4e86\u2026\u2026\u201d\u5f3a\u5fcd\u7740\u5c31\u8981\u6ef4\u51fa\u7684\u773c\u6cea\uff0c\u5927\u6b65\u5411\u540e\u5c71\u884c\u53bb\u3002 \u3000\u3000\u9648\u5e0c\u6b63\u4e00\u624b\u6276\u7740\u7af9\u6756\uff0c\u6ee1\u8138\u5df2\u662f\u8001\u6cea\u7eb5\u6a2a\uff0c\u4ed6\u90a3\u82cd\u767d\u7684\u8138\u8272\uff0c\u5239\u90a3\u4e4b\u95f4\uff0c\u53d8\u6210\u91d1\u9ec4\u4e4b\u8272\uff0c\u800c\u4e14\u4e24\u773c\u4e4b\u4e2d\u795e\u5149\u75ea\u6563\uff0c\u957f\u957f\u7684\u5730\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u8eab\u5b50\u7adf\u662f\u6643\u4e86\u6643\uff0c\u51e0\u4e4e\u8fde\u8d70\u8def\u7684\u529b\u6c14\u90fd\u6ca1\u6709\u4e86\u3002 \u3000\u3000\u4f46\u662f\uff0c\u4ed6\u5374\u5f3a\u81ea\u6491\u6301\uff0c\u8f6c\u8eab\u8d70\u8fdb\u4e86\u5c4b\u5185\uff0c\u9690\u7ea6\u4e2d\u542c\u5f97\u65ad\u7eed\u7684\u53f9\u9053\uff1a\u201c\u6069\u5e08\u5728\u5929\u4e4b\u7075\u660e\u9274\uff0c\u5f1f\u5b50\u9648\u5e0c\u6b63\u603b\u7b97\u672a\u8d1f\u4f60\u8001\u9057\u5fd7\u2026\u2026\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u65b9\u96ea\u5b9c\u6b63\u9075\u7167\u6069\u5e08\u6240\u793a\uff0c\u987a\u7740\u540e\u5c71\u7684\u4e00\u6761\u5ced\u58c1\uff0c\u81ea\u5c71\u4e0b\u5954\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5954\u884c\u4e4b\u52bf\uff0c\u5e76\u4e0d\u56e0\u5c71\u5f62\u9661\u5ced\u800c\u6709\u6240\u8fdf\u6ede\uff0c\u53ea\u89c1\u4ed6\u8df3\u5954\u4e8e\u60ac\u5ca9\u7edd\u58c1\uff0c\u9752\u82d4\u85e4\u8513\u4e4b\u95f4\uff0c\u76f4\u4f3c\u4e00\u5934\u767d\u733f\u822c\u7075\u5de7\uff0c\u534a\u4e2a\u65f6\u8fb0\u4e0d\u5230\uff0c\u4e1a\u5df2\u4e0b\u5230\u5c71\u8170\u4ee5\u4e0b\u3002 \u3000\u3000\u6b64\u523b\u65e5\u8272\u5c06\u5c3d\uff0c\u5c71\u4e2d\u4e00\u7247\u9edd\u6697\uff0c\u65b9\u96ea\u5b9c\u505c\u8eab\u5728\u4e00\u5904\u65ad\u5ca9\u4e4b\u95f4\uff0c\u5fcd\u4e0d\u4f4f\u56de\u5934\u770b\u4e86\u770b\u5c71\u4e0a\u4e00\u773c\uff0c\u5fc3\u4e2d\u6697\u9053\u5e08\u7236\u8eab\u6000\u75fc\u75be\u72ec\u81ea\u4e00\u4e2a\u7559\u5728\u5c71\u4e0a\uff0c\u867d\u6709\u733f\uff0c\u9e1f\u76f8\u4f34\uff0c\u603b\u4e0d\u662f\u957f\u4e45\u4e4b\u7b56\uff0c\u6b64\u756a\u81ea\u5df1\u89c1\u5230\u5e08\u53d4\u4e4b\u540e\uff0c\u4e00\u5b9a\u8981\u5f88\u5feb\u56de\u5c71\u6765\u4f8d\u5949\u5e08\u7236\uff0c\u95ee\u660e\u75c5\u60c5\uff0c\u4e5f\u597d\u8bbe\u6cd5\u5bfb\u627e\u836f\u7269\uff0c\u66ff\u5e08\u7236\u6cbb\u75c5\u2026\u2026 \u3000\u3000\u4ed6\u672c\u6709\u7740\u8fd9\u7b49\u5b5d\u5fc3\uff0c\u672c\u5c5e\u4eba\u60c5\u4e4b\u5e38\uff0c\u4f46\u4ed6\u5374\u5fd8\u4e86\u4ee5\u9648\u5e0c\u6b63\u4e4b\u80fd\uff0c\u65e2\u53ef\u4ee4\u4ed6\u4f10\u6bdb\u6d17\u9ad3\u3001\u8131\u80ce\u6362\u9aa8\uff0c\u5728\u77ed\u77ed\u4e09\u5e74\u96f6\u516d\u5341\u6708\u4e0d\u5230\u7684\u65f6\u95f4\u91cc\uff0c\u7531\u4e00\u4e2a\u6b66\u529f\u5e73\u5e38\u7684\u5b69\u5b50\uff0c\u664b\u5165\u6b66\u6797\u7b2c\u4e00\u6d41\u9ad8\u624b\u4e4b\u5217\uff0c\u4e3a\u4f55\u5bf9\u81ea\u5df1\u957f\u5e74\u75c5\u75db\uff0c\u5374\u5bfb\u4e0d\u51fa\u533b\u6cbb\u4e4b\u65b9\uff1f\u53ef\u89c1\u9648\u5e0c\u6b63\u7684\u75c5\uff0c\u51b3\u975e\u4ed6\u4ec5\u51ed\u7740\u90a3\u4efd\u5b5d\u5fc3\u6240\u80fd\u533b\u6cbb\u7684\u4e86\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u5446\u5730\u5728\u65ad\u5ca9\u4e4b\u524d\u7ad9\u4e86\u534a\u664c\uff0c\u5ffd\u7136\u957f\u53f9\u4e00\u58f0\uff0c\u6b63\u5f85\u4e3e\u6b65\u4e0b\u5c71\uff0c\u9661\u7136\u89c9\u51fa\u4e0d\u8fdc\u5904\u4f20\u6765\u4e00\u9635\u54bb\u54bb\u7684\u547c\u5438\u4e4b\u58f0\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u9a87\u7136\u5374\u6b65\uff0c\u6697\u6697\u5bfb\u601d\u9053\uff1a\u201c\u8fd9\u540e\u5c71\u8352\u50fb\u65e0\u6bd4\uff0c\u96be\u9053\u8fd8\u6709\u4ec0\u4e48\u4eba\u80c6\u6562\u5728\u8fd9\u7b49\u591c\u8272\u832b\u832b\u4e4b\u65f6\uff0c\u524d\u6765\u6500\u767b\u5417\uff1f\u201d \u3000\u3000\u4ed6\u5fc3\u5ff5\u8f6c\u52a8\uff0c\u540c\u65f6\u4e0d\u81ea\u89c9\u5730\u4f38\u624b\u5411\u80a9\u4e0a\u7684\u5251\u67c4\u63a2\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u4f38\u624b\u4e0d\u6253\u7d27\uff0c\u65b9\u96ea\u5b9c\u5ffd\u7136\u53d1\u73b0\uff0c\u81ea\u5df1\u7adf\u7136\u628a\u5e08\u7236\u4ea4\u7ed9\u4ed6\u7684\u90a3\u4e00\u628a\u957f\u5251\uff0c\u5fd8\u8bb0\u5728\u5ba4\u5185\u672a\u66fe\u5e26\u6765\uff0c\u65b9\u96ea\u5b9c\u51e0\u4e4e\u5931\u58f0\u60f3\u7b11\uff0c\u81ea\u5df1\u5949\u4e86\u5e08\u7236\u4e4b\u547d\uff0c\u524d\u53bb\u5bfb\u627e\u90a3\u91d1\u9876\u795e\u5c3c\u5e08\u53d4\u7ee7\u7eed\u7ec3\u5251\uff0c\u7adf\u4f1a\u5fd8\u4e86\u5e26\u5251\uff0c\u8fd9\u8be5\u662f\u591a\u5927\u7684\u7b11\u8bdd\u3002 \u3000\u3000\u4e0d\u8fc7\u90a3\u6c89\u91cd\u7684\u54bb\u54bb\u4e4b\u58f0\uff0c\u5df2\u8d8a\u6765\u8d8a\u8fd1\uff0c\u6253\u65ad\u4e86\u4ed6\u5fc3\u4e2d\u6240\u601d\uff0c\u65e2\u7136\u8eab\u65e0\u5bf8\u94c1\uff0c\u4ed6\u53ea\u597d\u51ed\u7740\u4e00\u53cc\u8089\u638c\uff0c\u9759\u5019\u5728\u8fd9\u591c\u8272\u7b3c\u7f69\u4e0b\uff0c\u6765\u6b64\u767b\u5c71\u4e4b\u4eba\uff0c\u662f\u5f3a\u654c\u8fd8\u662f\u6e38\u5ba2\uff01 \u3000\u3000\u4ed6\u5fc3\u5ff5\u752b\u5b9a\uff0c\u5341\u4e08\u4e4b\u5916\u7684\u4e00\u5904\u5ca9\u77f3\u4e4b\u540e\uff0c\u5df2\u7f13\u7f13\u7684\u8f6c\u51fa\u4e00\u56e2\u5e9e\u5927\u7684\u9ed1\u5f71\uff0c\u8fdc\u8fdc\u671b\u53bb\uff0c\u539f\u6765\u662f\u4e00\u53ea\u6591\u6593\u731b\u864e\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u867d\u662f\u6b66\u529f\u4e0d\u5f31\uff0c\u4f46\u7a76\u7adf\u662f\u5e74\u7eaa\u4e0d\u5927\uff0c\u6b64\u65f6\u6b64\u523b\uff0c\u5728\u56db\u4e0b\u65e0\u4eba\u7684\u6df1\u5c71\u4e2d\u9047\u4e0a\u4e86\u8fd9\u7b49\u51f6\u731b\u7684\u5de8\u517d\uff0c\u5fc3\u5934\u4e5f\u4e0d\u7981\u6251\u901a\u4e71\u8df3\u3002 \u3000\u3000\u4ed6\u6697\u6697\u54ac\u7259\u5fd6\u9053\uff1a\u201c\u8fd9\u7b49\u51f6\u731b\u4e4b\u7269\uff0c\u81ea\u5df1\u8d64\u624b\u7a7a\u62f3\u600e\u4e48\u80fd\u5bf9\u4ed8\u5f97\u4e86\uff1f\u201d\u4f46\u65f6\u4e0d\u6211\u4e88\uff0c\u65b9\u96ea\u5b9c\u5df2\u662f\u522b\u65e0\u9009\u62e9\u4f59\u5730\uff0c\u53ea\u89c1\u90a3\u53ea\u8db3\u8db3\u6709\u5c0f\u725b\u5927\u5c0f\u7684\u5de8\u864e\uff0c\u5df2\u5411\u4ed6\u505c\u8eab\u4e4b\u5904\u7f13\u6b65\u884c\u6765\uff0c\u522b\u77a7\u8fd9\u53ea\u5927\u866b\u91cd\u903e\u5343\u65a4\uff0c\u4f46\u5b83\u811a\u722a\u843d\u5730\uff0c\u5374\u53c8\u8f7b\u76c8\u5f97\u5b9b\u4f3c\u4e00\u53ea\u72f8\u732b\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u5934\u5fae\u5fae\u53d1\u6bdb\uff0c\u624b\u5fc3\u4e4b\u4e2d\uff0c\u4e5f\u76f4\u51fa\u51b7\u6c57\uff0c\u4e24\u773c\u7d27\u76ef\u7740\u8fd9\u51f6\u72e0\u7684\u5c71\u91ce\u4e4b\u7389\u6df1\u6df1\u5730\u5438\u4e86\u4e00\u53e3\u6c14\u3002\u90a3\u5de8\u864e\u81f3\u4ed6\u8eab\u524d\u4e24\u4e08\u4e4b\u5904\uff0c\u5ffd\u7136\u505c\u4e0b\u6765\u3002 \u3000\u3000\u53ea\u89c1\u5b83\u56db\u8db3\u8e1e\u5730\u77aa\u7740\u90a3\u5c0f\u706f\u4f3c\u7684\u4e24\u773c\uff0c\u76ef\u7740\u65b9\u96ea\u5b9c\u77a7\u770b\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u76ee\u7779\u731b\u864e\u7b95\u8e1e\u5730\u4e0a\u4e0d\u52a8\uff0c\u5fc3\u4e2d\u4e5f\u5728\u5343\u56de\u767e\u8f6c\u3002 \u3000\u3000\u6253\u4ed6\u8bb0\u4e8b\u65f6\u8d77\uff0c\u53ef\u8fd8\u6ca1\u6709\u4eba\u6559\u8fc7\u4ed6\u5982\u4f55\u4ee5\u8d64\u624b\u7a7a\u62f3\u53bb\u640f\u6740\u4e00\u53ea\u731b\u864e\uff0c\u773c\u4e0b\u9047\u5230\u8fd9\u7b49\u666f\u8c61\uff0c\u5374\u4e5f\u4e0d\u77e5\u5982\u4f55\u4e0b\u624b\u624d\u5bf9\uff1f \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u4eba\u864e\u5bf9\u7acb\uff0c\u4e45\u4e45\u6ca1\u6709\u52a8\u9759\u3002 \u3000\u3000\u591c\u98ce\u51db\u51db\uff0c\u4eba\u517d\u76f8\u5bf9\u7684\u5947\u666f\uff0c\u6301\u7eed\u4e86\u5e76\u4e0d\u5f88\u4e45\uff0c\u90a3\u5934\u5403\u4eba\u65e0\u6570\u7684\u731b\u864e\uff0c\u7a81\u7136\u4f4e\u543c\u4e00\u58f0\uff0c\u4e00\u8dc3\u800c\u8d77\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u867d\u65e0\u640f\u864e\u7684\u7ecf\u9a8c\uff0c\u4f46\u5374\u672c\u80fd\u5730\u95ea\u8eab\u4e00\u8ba9\uff0c\u659c\u659c\u98d8\u5f00\u516b\u5c3a\uff0c\u8f6c\u5934\u77a7\u53bb\uff0c\u53ea\u89c1\u90a3\u731b\u864e\u5df2\u5728\u81ea\u5df1\u505c\u8eab\u4e4b\u5916\u843d\u5730\uff0c\u5229\u722a\u6240\u53ca\uff0c\u5c71\u77f3\u5212\u8d77\u51e0\u4e1d\u706b\u661f\u3002\u5982\u662f\u4ed6\u4e0d\u66fe\u8ba9\u5f00\uff0c\u731b\u864e\u5229\u722a\u4e00\u65e6\u6cbe\u8eab\uff0c\u4e0d\u6b7b\u4e5f\u8981\u91cd\u4f24\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u5bfb\u601d\u9053\uff1a\u201c\u6211\u5982\u662f\u8fde\u8fd9\u4e2a\u755c\u7272\u90fd\u6597\u4e0d\u8fc7\uff0c\u8fd8\u600e\u80fd\u627f\u7ee7\u5e08\u7236\u7684\u8863\u4f53\uff0c\u4ed7\u5251\u6c5f\u6e56\uff0c\u91cd\u632f\u5251\u795e\u5a01\u671b\u4e86\u3002\u201d \u3000\u3000\u4e00\u5ff5\u53ca\u6b64\uff0c\u9661\u7136\u96c4\u5fc3\u5927\u594b\uff0c\u8c6a\u6c14\u84e6\u751f\u3002 \u3000\u3000\u4f46\u89c1\u4ed6\u6df1\u6df1\u5730\u5438\u4e86\u53e3\u6c14\uff0c\u76ee\u6ce8\u731b\u864e\uff0c\u529f\u51dd\u53cc\u81c2\u957f\u5578\u4e00\u58f0\uff0c\u6325\u624b\u731b\u5411\u516b\u5c3a\u4e4b\u5916\u7684\u5de8\u864e\u51fb\u51fa\u4e00\u638c\u3002\u65b9\u96ea\u5b9c\u7684\u5288\u7a7a\u638c\u529b\uff0c\u53ea\u626b\u5230\u4e86\u731b\u864e\u7684\u540e\u80ef\u90e8\u4f4d\uff0c\u5373\u4f7f\u5982\u6b64\uff0c\u90a3\u731b\u864e\u5df2\u7ecf\u627f\u53d7\u4e0d\u8d77\uff0c\u817e\u8eab\u800c\u8d77\uff0c\u53ea\u542c\u5f97\u4e00\u58f0\u95f7\u543c\u5165\u8033\uff0c\u90a3\u91cd\u903e\u5343\u65a4\u7684\u5de8\u864e\uff0c\u5ffd\u5730\u51cc\u7a7a\u4e0b\u5760\uff0c\u5728\u5730\u4e0a\u7ffb\u6eda\u6293\u6251\u4e2a\u4e0d\u505c\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4f3c\u662f\u672a\u66fe\u6599\u5230\u81ea\u5df1\u638c\u529b\u4e4b\u5f3a\uff0c\u7adf\u7136\u4e00\u51fb\u4e4b\u4e0b\uff0c\u5c31\u4f7f\u8fd9\u7b49\u5de8\u864e\u8d1f\u4e86\u91cd\u4f24\uff0c\u5fc3\u4e2d\u4e00\u9ad8\u5174\u6325\u624b\u53c8\u662f\u4e00\u638c\u62cd\u51fa\u3002 \u3000\u3000\u8fd9\u4e00\u638c\u4ed6\u51fb\u5411\u4e86\u6eda\u52a8\u4e2d\u5de8\u864e\u7684\u524d\u989d\u3002 \u3000\u3000\u4f46\u542c\u5f97\u90a3\u731b\u864e\u725f\u725f\u7684\u54c0\u53f9\u4e86\u4e24\u58f0\uff0c\u5239\u90a3\u95f4\u72c2\u8dc3\u800c\u8d77\uff0c\u51cc\u7a7a\u9ad8\u8fbe\u4e08\u4e94\u4e0a\u4e0b\uff0c\u864e\u53e3\u4e2d\u9c9c\u8840\u55b7\u5c04\uff0c\u4e00\u5934\u683d\u5411\u4e86\u60ac\u5ca9\u4e4b\u4e0b\u7684\u4e07\u4e08\u6df1\u6e0a\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u8d76\u5230\u60ac\u5ca9\u4e4b\u524d\uff0c\u81ea\u4e0b\u671b\u53bb\uff0c\u867d\u6709\u96ea\u8272\u63a9\u6620\uff0c\u4f46\u5ca9\u4e0b\u52a0\u9ed1\u96fe\u6c89\u6c89\uff0c\u90a3\u91cc\u8fd8\u80fd\u770b\u5f97\u89c1\u534a\u4e1d\u5de8\u864e\u7684\u8eab\u5f71\u3002 \u3000\u3000\u4e0d\u7981\u6447\u5934\u81ea\u4ed8\u9053\uff1a\u539f\u6765\u8fd9\u4f0f\u864e\u4e4b\u80fd\uff0c\u4e5f\u4e0d\u8fc7\u5982\u6b64\u800c\u5df2\uff0c\u53ea\u53ef\u60dc\u672a\u66fe\u5265\u4e0b\u8fd9\u5f20\u864e\u76ae\uff0c\u5426\u5219\u7ed9\u5e08\u7236\u5236\u4e00\u4ef6\u76ae\u888d\uff0c\u4e5f\u53ef\u5fa1\u8fd9\u4e25\u51ac\u7684\u5bd2\u610f\u3002\u6298\u817e\u4e86\u534a\u5929\uff0c\u5929\u8272\u5df2\u8fc7\u521d\u66f4\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4ef0\u5934\u770b\u4e86\u770b\u5929\u8272\uff0c\u5ffd\u7136\u60f3\u8d77\uff0c\u81ea\u5df1\u5e94\u8be5\u56de\u53bb\u53d6\u4e86\u957f\u5251\u518d\u53bb\u5bfb\u627e\u5e08\u53d4\u624d\u5bf9\uff0c\u5426\u5219\u5c06\u8981\u88ab\u5e08\u53d4\u8f7b\u770b\uff0c\u8ba4\u4e3a\u81ea\u5df1\u662f\u4e2a\u7c97\u5fc3\u5927\u610f\u7684\u5b69\u5b50\u4e86\uff0c\u4ed6\u5ff5\u52a8\u5373\u884c\uff0c\u53cd\u8eab\u76f4\u5954\u5c71\u4e0a\u3002 \u3000\u3000\u8fd9\u56de\u867d\u662f\u4e0a\u5c71\uff0c\u4ed6\u6500\u767b\u7684\u901f\u5ea6\uff0c\u5374\u6bd4\u4ed6\u4e0b\u5c71\u4e4b\u65f6\u8fd8\u8981\u5feb\u4e86\u8bb8\u591a\uff0c\u534a\u4e2a\u65f6\u8fb0\u4e0d\u5230\uff0c\u5c31\u5df2\u56de\u5230\u4e86\u8305\u820d\u4e4b\u524d\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u8db3\u521a\u8de8\u8fdb\u95e8\u91cc\uff0c\u90a3\u4e00\u58f0\u5e08\u7236\u5c1a\u672a\u51fa\u53e3\u3002\u76ee\u5149\u6240\u53ca\u53ea\u628a\u4ed6\u60ca\u5f97\u9b42\u98de\u5929\u5916\uff0c\u5934\u76ae\u53d1\u70b8\uff0c\u5446\u5728\u95e8\u4e2d\uff0c\u534a\u5929\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u660f\u9ed1\u4e4b\u4e2d\uff0c\u53ea\u89c1\u90a3\u4e09\u5e74\u591a\u4e0e\u81ea\u5df1\u671d\u5915\u76f8\u5904\uff0c\u6069\u6bd4\u7236\u6bcd\u66f4\u6df1\u7684\u5e08\u7236\uff0c\u6a2a\u8eab\u5012\u5367\u5728\u5385\u4e2d\u7684\u84b2\u56e2\u4e4b\u65c1\uff0c\u90a3\u6839\u7ec8\u65e5\u4e0d\u66fe\u79bb\u624b\u7684\u7af9\u6756\uff0c\u8dcc\u843d\u5728\u4e09\u5c3a\u4e4b\u5916\u3002 \u3000\u3000\u4ed6\u5446\u4e86\u4e00\u9635\u4e4b\u540e\uff0c\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u5e08\u7236\u554a\u2026\u2026\u201d\u8dc3\u8eab\u6251\u8fc7\u53bb\u3002 \u3000\u3000\u53cc\u624b\u62b1\u8d77\u9648\u5e0c\u6b63\uff0c\u6cea\u6c34\u6cc9\u6d8c\u800c\u51fa\u3002\u4e00\u9635\u54c0\u75db\u8fc7\u540e\uff0c\u5fc3\u60c5\u9010\u6e10\u5e73\u590d\uff0c\u987f\u65f6\u5174\u8d77\u4e86\u4e07\u4e00\u4e4b\u60f3\uff0c\u6697\u9053\uff1a\u201c\u5e08\u7236\u6b66\u529f\u76d6\u4e16\uff0c\u90a3\u4e0d\u6cbb\u4e4b\u75c7\uff0c\u867d\u7136\u96be\u4ee5\u533b\u6cbb\uff0c\u4f46\u4e5f\u4e0d\u4f1a\u5728\u81ea\u5df1\u521a\u521a\u79bb\u53bb\uff0c\u4ed6\u8001\u4eba\u5bb6\u5c31\u75c5\u53d1\u8eab\u6b7b\uff0c\u4e5f\u8bb8\u5e08\u7236\u53ea\u662f\u4e00\u65f6\u6655\u53a5\uff0c\u800c\u4e0d\u662f\u771f\u6b63\u7684\u8fc7\u4e16\u4e86\u5427\uff1f\u201d \u3000\u3000\u5fc3\u5ff5\u4e00\u52a8\uff0c\u63a2\u624b\u5411\u5e08\u7236\u7684\u80f8\u53e3\u629a\u53bb\uff0c\u4f46\u89c9\u89e6\u624b\u51b0\u51c9\u6beb\u65e0\u8df3\u52a8\u8ff9\u8c61\uff0c\u663e\u7136\u5e08\u7236\u5df2\u7ecf\u6b7b\u53bb\u6709\u4e00\u4e2a\u65f6\u8fb0\u4ee5\u4e0a\u4e86\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u53ea\u89c9\u4e00\u80a1\u70ed\u8840\u7531\u5fc3\u5934\u76f4\u51b2\u4e0a\u6765\uff0c\u518d\u4e5f\u96be\u4ee5\u63a7\u5236\u90a3\u60b2\u4f24\u6fc0\u52a8\u7684\u60c5\u7eea\uff0c\u72c2\u53eb\u4e00\u58f0\uff0c\u55b7\u51fa\u4e00\u53e3\u9c9c\u8840\uff0c\u62b1\u7740\u9648\u5e0c\u6b63\u7684\u5c38\u4f53\uff0c\u8dea\u5728\u5730\u4e0a\uff0c\u653e\u58f0\u5927\u54ed\u8d77\u6765\u3002 \u3000\u3000\u4e09\u5e74\u591a\u6765\uff0c\u5e08\u7236\u90a3\u6148\u7965\u601c\u608c\u7684\u7b11\u5bb9\uff0c\u5df2\u662f\u81ea\u6b64\u6c38\u7edd\uff0c\u8c06\u8c06\u6559\u8bf2\uff0c\u8033\u63d0\u9762\u547d\u7684\u6e29\u58f0\u56de\u5fc6\uff0c\u73b0\u5728\u90fd\u5df2\u5316\u4f5c\u4e86\u60b2\u82e6\u7684\u6000\u5ff5\u3002\u2026\u2026 \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u4e00\u54ed\uff0c\u5f53\u771f\u662f\u54c0\u75db\u6b32\u7edd\uff0c\u8840\u6cea\u4ea4\u8fdb\uff0c\u8c01\u8bf4\u4e08\u592b\u6709\u6cea\u4e0d\u8f7b\u5f39\uff0c\u53ea\u4e3a\u672a\u5230\u4f24\u5fc3\u5904\u554a\uff01 \u3000\u3000\u4e0d\u77e5\u8fc7\u4e86\u591a\u5c11\u65f6\u95f4\uff0c\u65b9\u96ea\u5b9c\u65e9\u5df2\u54ed\u5f97\u6cea\u5c3d\u773c\u67af\uff0c\u603b\u7b97\u8fd9\u4e00\u573a\u5927\u54ed\uff0c\u6682\u65f6\u53d1\u6cc4\u4e86\u4ed6\u62e5\u585e\u5728\u80f8\u4e2d\u7684\u54c0\u4f24\u60c5\u6000\uff0c\u5fc3\u795e\u6162\u6162\u5730\u5b89\u9759\u4e0b\u6765\u3002 \u3000\u3000\u4ed6\u5b9a\u4e86\u5b9a\u795e\uff0c\u7b2c\u4e00\u4e2a\u60f3\u5230\u7684\uff0c\u5c31\u662f\u600e\u751f\u5b89\u987f\u5e08\u7236\u7684\u9057\u4f53\uff0c\u4e0d\u4f7f\u4ed6\u8150\u574f\u3002 \u3000\u3000\u4ed6\u603b\u89c9\u5f97\u5e08\u7236\u6240\u5f97\u7684\u8fd9\u4e2a\u602a\u75c5\uff0c\u4ee4\u4eba\u83ab\u6d4b\u9ad8\u6df1\uff0c\u7591\u5fc3\u662f\u53d7\u4e86\u4ec0\u4e48\u4eba\u7684\u6697\u7b97\uff0c\u65b9\u81f4\u65e0\u6cd5\u6cbb\u7597\uff0c\u4f46\u5e08\u7236\u65e2\u672a\u544a\u8bc9\u81ea\u5df1\uff0c\u5c06\u6765\u53ea\u6709\u9760\u81ea\u5df1\u6765\u8bbe\u6cd5\u67e5\u660e\uff0c\u5018\u82e5\u81ea\u5df1\u731c\u60f3\u7684\u4e0d\u9519\uff0c\u90a3\u6697\u7b97\u5e08\u7236\u7684\u4eba\uff0c\u662f\u6740\u5e08\u7684\u5927\u4ec7\u4eba\u4e86\uff0c\u8fd9\u7b49\u4ec7\u6068\uff0c\u81ea\u5df1\u53ef\u5c31\u8d23\u65e0\u65c1\u8d37\u5730\u8981\u66ff\u6069\u5e08\u62a5\u590d\u3002 \u3000\u3000\u81f3\u4e8e\u8981\u600e\u751f\u624d\u80fd\u67e5\u63a2\u660e\u767d\uff0c\u90a3\u53ef\u662f\u4ee5\u540e\u7684\u4e8b\u4e86\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u62b1\u7740\u9648\u5e0c\u6b63\u7684\u5c38\u4f53\uff0c\u832b\u7136\u5730\u5728\u5ba4\u5185\u8f6c\u6765\u8f6c\u53bb\u3002\u4e5f\u4e0d\u77e5\u8d70\u4e86\u591a\u5c11\u5708\uff0c\u5ffd\u7136\u4ed6\u53d1\u73b0\u81ea\u5df1\u5df2\u7136\u8d70\u5230\u5e08\u7236\u7684\u90a3\u95f4\u5367\u5ba4\u4e4b\u5185\u3002 \u3000\u3000\u53ea\u89c1\u5e08\u7956\u7684\u90a3\u5e45\u753b\u50cf\uff0c\u6b63\u60ac\u6302\u5728\u5c0f\u5ba4\u4e4b\u4fa7\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4f46\u611f\u5fc3\u4e2d\u4e00\u9707\uff0c\u6697\u9053\uff1a\u6211\u600e\u4e48\u5982\u6b64\u516d\u795e\u65e0\u4e3b\uff0c\u7ecf\u4e0d\u8d77\u4e1d\u6beb\u53d8\u6545\u5462\uff1f\u5e08\u7956\u7684\u7edd\u5b66\uff0c\u5e08\u7236\u7684\u5fc3\u613f\uff0c\u4ee5\u53ca\u4eca\u540e\u6b66\u6797\u7684\u5927\u52ab\uff0c\u5168\u90fd\u5bc4\u671b\u5728\u81ea\u5df1\u8eab\u4e0a\uff0c\u5018\u82e5\u81ea\u5df1\u8fd9\u7b49\u7ecf\u4e0d\u8d77\u4e00\u70b9\u6ce2\u6298\uff0c\u5e08\u7236\u8d39\u5c3d\u5fc3\u8840\u8c03\u6559\u81ea\u5df1\u7684\u82e6\u5fc3\uff0c\u5c82\u4e0d\u662f\u5b8c\u5168\u767d\u8d39\u4e86\u5417\uff1f\u800c\u4e14\uff0c\u5e08\u7236\u5728\u81ea\u5df1\u79bb\u53bb\u4e0d\u8db3\u4e24\u4e2a\u65f6\u8fb0\uff0c\u5c31\u5df2\u65ad\u6c14\u8fc7\u4e16\uff0c\u663e\u89c1\u5f97\u5e08\u7236\u786e\u662f\u4e3a\u4e86\u7763\u4fc3\u81ea\u5df1\u52e4\u7ec3\u6b66\u529f\uff0c\u64cd\u5fc3\u8fc7\u751a\uff0c\u65b9\u4f1a\u63d0\u524d\u6570\u5e74\u800c\u6492\u624b\u5c18\u5bf0\u3002 \u3000\u3000\u52a8\u5ff5\u81f3\u6b64\uff0c\u5fc3\u4e2d\u53c8\u9690\u9690\u5730\u4f5c\u75db\uff0c\u4f46\u4ed6\u5374\u9762\u5bf9\u5e08\u7956\u9057\u50cf\uff0c\u5f3a\u81ea\u5fcd\u4e0b\u4e86\u4f24\u75db\u4e4b\u5fc3\uff0c\u5c06\u5e08\u7236\u7684\u9057\u4f53\uff0c\u653e\u5728\u90a3\u5f20\u77f3\u5e8a\u4e0a\uff0c\u670d\u529b\u6240\u53ca\uff0c\u5ffd\u7136\u53d1\u73b0\u90a3\u7af9\u6795\u4e4b\u4e0b\uff0c\u7adf\u662f\u538b\u4e86\u4e00\u5f20\u767d\u7b3a\u4e4b\u7c7b\u7684\u4e8b\u7269\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u63a2\u624b\u53d6\u51fa\u90a3\u5f20\u767d\u7b3a\uff0c\u4ed6\u8fc5\u5feb\u5730\u638f\u51fa\u706b\u647a\u5b50\u6643\u7136\uff0c\u70b9\u4eae\u4e86\u5e8a\u524d\u4e66\u684c\u4e4b\u4e0a\u7684\u706f\u6cb9\uff0c\u51dd\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u90a3\u767d\u7b3a\u4e4b\u4e0a\u5199\u7740\uff1a\u201c\u5b57\u8c15\u96ea\u513f\uff0c\u6211\u5df2\u75c5\u5165\u818f\u5bb5\uff0c\u968f\u65f6\u5747\u6709\u65ad\u6c14\u53ef\u80fd\uff0c\u6211\u6b7b\u4e4b\u540e\uff0c\u53ef\u5c06\u5c38\u4f53\u85cf\u5728\u8fd9\u77f3\u5e8a\u4e4b\u4e0b\u7684\u51b0\u7a96\u4e4b\u4e2d\uff0c\u5bd2\u6c14\u6240\u79ef\uff0c\u5f53\u53ef\u4fdd\u6211\u5c38\u4f53\u4e0d\u70c2\uff0c\u4e66\u684c\u5de6\u6d4b\u62bd\u5c49\u4e4b\u4e2d\uff0c\u6709\u6211\u5199\u7ed9\u4f60\u5e08\u53d4\u91d1\u9876\u795e\u5c3c\u4e66\u4fe1\u4e00\u5c01\uff0c\u53ef\u7acb\u5373\u6309\u7167\u4fe1\u5c01\u80cc\u540e\u7684\u56fe\u5740\uff0c\u524d\u53bb\u6c42\u89c1\uff0c\u4f60\u4e0a\u4e58\u5251\u9053\u672a\u7aa5\u5802\u5965\u8fd9\u524d\uff0c\u5343\u4e07\u4e0d\u53ef\u4e0b\u5c71\uff0c\u4e3a\u5e08\u8eab\u53d7\u4e4b\u82e6\uff0c\u5373\u662f\u524d\u8f66\u4e4b\u9274\uff0c\u5207\u8bb0\u5207\u8bb0\uff01\u5176\u4f59\u4e4b\u4e8b\uff0c\u81ea\u6709\u4f60\u5e08\u53d4\u5b89\u6392\uff0c\u4f60\u83ab\u8981\u591a\u7ba1\uff0c\u5e08\u7956\u9057\u5fd7\uff0c\u4e0d\u53ef\u4e00\u65e5\u6216\u60d1\uff0c\u5584\u81ea\u73cd\u91cd\uff0c\u4e3a\u5e08\u6b7b\u4e5f\u7791\u76ee\u3002\u201d\u4e0b\u9762\u7684\u843d\u6b3e\uff0c\u53ea\u662f\u201c\u5e08\u5b57\u201d\u4e24\u4e2a\u5b57\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u770b\u5b8c\u4e86\u8fd9\u5f20\u4e0d\u77e5\u662f\u5e08\u7236\u51e0\u65f6\u7559\u4e0b\u7684\u9057\u8a00\uff0c\u4e0d\u7981\u60b2\u4ece\u4e2d\u6765\uff0c\u67af\u7aed\u7684\u6c5f\u6c34\uff0c\u987f\u65f6\u76c8\u7736\u3002\u6ca1\u60f3\u5230\u5e08\u7236\u65e9\u5df2\u77e5\u9053\u81ea\u5df1\u4e0d\u4e45\u4eba\u4e16\uff0c\u5374\u4e00\u76f4\u672a\u66fe\u5411\u81ea\u5df1\u8bf4\u51fa\uff0c\u8fd9\u5f20\u9057\u8a00\uff0c\u4e5f\u8bb8\u662f\u51e0\u4e2a\u6708\u524d\u5199\u4e0b\uff0c\u4f46\u4e5f\u53ef\u80fd\u65e9\u5728\u4e24\u5e74\u524d\u5c31\u5df2\u4e66\u5c31\uff0c\u770b\u90a3\u767d\u7b3a\u7684\u989c\u8272\uff0c\u5df2\u7136\u53d8\u9ec4\uff0c\u5c31\u7b97\u65f6\u65e5\u4e0d\u591a\uff0c\u81f3\u5c11\u4e5f\u5728\u534a\u5e74\u4ee5\u4e0a\u4e86\u3002 \u3000\u3000\u4ed6\u606d\u656c\u63e3\u597d\u8fd9\u5f20\u5e08\u7236\u552f\u4e00\u7559\u7ed9\u81ea\u5df1\u7684\u624b\u6cfd\uff0c\u4e0d\u7981\u6697\u9053\uff1a\u5e08\u7236\u8fd9\u7b49\u51d1\u5de7\u7684\u5728\u4eca\u65e5\u4f20\u5b8c\u4e86\u6700\u540e\u4e00\u62db\u5c31\u53eb\u81ea\u5df1\u4e0b\u5c71\uff0c\u5fc5\u662f\u77e5\u5927\u9650\u5df2\u5c3d\uff0c\u4e0d\u613f\u8ba9\u81ea\u5df1\u770b\u5230\u4ed6\u5fc3\u8840\u8017\u5c3d\u800c\u6b7b\uff0c\u4e00\u65f6\u5fcd\u4e0d\u4f4f\u60b2\u75db\uff0c\u800c\u5f71\u54cd\u4e86\u6b66\u529f\u7684\u7cbe\u8fdb\uff0c\u81ea\u5df1\u53c8\u600e\u80fd\u8f9c\u8d1f\u5e08\u7236\u8fd9\u756a\u7528\u5fc3\u5462\uff1f\u53ea\u662f\u51a5\u51a5\u4e4b\u4e2d\uff0c\u4e5f\u6709\u5b9a\u6570\uff0c\u5e08\u7236\u5b89\u6392\u7684\u672c\u662f\u6781\u4e3a\u9690\u79d8\u59a5\u8d34\uff0c\u4f7f\u81ea\u5df1\u5728\u5251\u9053\u672a\u6210\u4e4b\u524d\uff0c\u4e0d\u4f1a\u56e0\u4e27\u5e08\u4e4b\u75db\uff0c\u800c\u5206\u6563\u7528\u529f\u4e4b\u5fc3\uff0c\u8c01\u53c8\u60f3\u5230\u81ea\u5df1\u4e00\u65f6\u5306\u5fd9\u4e4b\u4e0b\uff0c\u7adf\u5fd8\u4e86\u628a\u957f\u5251\u5e26\u4e0b\u5c71\u53bb\u3002\u66f4\u60f3\u4e0d\u5230\u7684\u662f\uff0c\u4e0b\u5c71\u884c\u81f3\u534a\u9014\uff0c\u4f1a\u9047\u4e0a\u90a3\u53ea\u5927\u866b\uff0c\u8fd9\u624d\u4f7f\u81ea\u5df1\u60f3\u8d77\u5e94\u8be5\u56de\u6765\u53d6\u5251\uff0c\u7ec8\u4e8e\u53d1\u73b0\u4e86\u5e08\u7236\u5df2\u6b7b\u7684\u60e8\u75db\u5c40\u9762\uff0c\u800c\u4e14\uff0c\u5e08\u7236\u660e\u660e\u5df2\u628a\u5448\u9001\u5e08\u53d4\u7684\u4fe1\u4ef6\uff0c\u4ea4\u7ed9\u4e86\u81ea\u5df1\uff0c\u5374\u53c8\u5fd8\u8bb0\u628a\u538b\u5728\u6795\u4e0b\u7684\u9057\u8a00\u6495\u6bc1\uff0c\u663e\u5f97\u4eba\u4e16\u7684\u8bb8\u591a\u5b89\u6392\uff0c\u603b\u5728\u51a5\u51a5\u5b9a\u6570\u4e4b\u4e2d\uff0c\u5f3a\u4ed6\u4e0d\u5f97\u2026\u2026\u3002 \u3000\u3000\u4ed6\u601d\u5fd6\u4e45\u4e45\uff0c\u4e0d\u7981\u957f\u53f9\u51fa\u58f0\uff0c\u60f3\u5230\u5e08\u7236\u4ea4\u4ee3\u5b89\u7f6e\u9057\u4f53\u4e4b\u6cd5\uff0c\u4ed6\u7acb\u5373\u8d70\u5230\u77f3\u5e8a\u4e4b\u524d\uff0c\u5bfb\u627e\u5230\u4e86\u79fb\u52a8\u77f3\u5e8a\u7684\u673a\u94ae\uff0c\u8f7b\u8f7b\u7684\u6309\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4f46\u542c\u5f97\u4e00\u9635\u54d7\u5566\u5566\u8f6e\u8f74\u8f6c\u52a8\u4e4b\u58f0\u5165\u8033\uff0c\u90a3\u77f3\u5e8a\u5df2\u5411\u5de6\u4fa7\u6a2a\u79fb\u4e86\u4e09\u5c3a\uff0c\u9732\u51fa\u4e00\u4e2a\u53ef\u5bb9\u4e00\u4eba\u4e0a\u4e0b\u7684\u5730\u9053\uff0c\u957f\u957f\u7684\u77f3\u7ea7\uff0c\u76f4\u5f80\u5730\u4e0b\u5ef6\u4f38\u8fc7\u53bb\u3002\u53ea\u89c9\u4e00\u80a1\u5176\u7262\u5f7b\u9aa8\u7684\u51b7\u98ce\uff0c\u6253\u90a3\u5730\u9053\u5165\u53e3\u5192\u4e0a\u6765\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u62b1\u8d77\u5e08\u7236\u9057\u4f53\uff0c\u56e2\u4e0a\u4e86\u53cc\u76ee\uff0c\u4e00\u6b65\u4e00\u6b65\u987a\u7740\u77f3\u7ea7\u5411\u4e0b\u884c\u53bb\uff0c\u7ea6\u83ab\u6df1\u5165\u7ea6\u6709\u4e24\u4e08\uff0c\u65b9\u59cb\u62b5\u8fbe\u5e73\u5730\u3002 \u3000\u3000\u4ed6\u7f13\u7f13\u5730\u7741\u5f00\u4e24\u773c\uff0c\u9ed1\u6697\u4e4b\u4e2d\uff0c\u4e5f\u53ef\u77a7\u6e05\u695a\u8fd9\u5730\u4e0b\u51b0\u7a96\u7684\u4e00\u5207\uff0c\u6562\u60c5\u4ed6\u77e5\u9053\u8fd9\u4e0b\u9762\u5bd2\u6c14\u6781\u91cd\uff0c\u53ea\u6015\u706f\u706b\u4e0d\u6613\u70b9\u71c3\uff0c\u662f\u4ee5\u5148\u884c\u95ed\u4e0a\u53cc\u76ee\uff0c\u4ee5\u4fbf\u9002\u5e94\u6697\u5904\u7684\u5149\u7ebf\u3002 \u3000\u3000\u51dd\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u8fd9\u5730\u7a96\u4e4b\u4e2d\u5927\u5c0f\u53ea\u6709\u4e24\u4e08\u65b9\u5706\uff0c\u5f53\u4e2d\u7684\u5730\u4e0a\uff0c\u6709\u4e00\u5757\u5927\u5c0f\u7ea6\u516b\u5c3a\u89c1\u65b9\u7684\u3001\u633a\u8d77\u79bb\u5730\u4e09\u5c3a\u591a\u9ad8\u7eaf\u7531\u5bd2\u51b0\u7ed3\u6210\u7684\u51b0\u77f3\u3002\u51b0\u77f3\u4e4b\u4e0a\uff0c\u5411\u4e1c\u7684\u4e00\u5934\uff0c\u62f1\u8d77\u4e00\u6761\u51b0\u6795\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u77e5\u9053\uff0c\u8fd9\u53ef\u80fd\u5c31\u662f\u5e08\u7236\u81ea\u5df2\u7ecf\u8425\u7684\u51b0\u7a96\u5893\u7a74\u3002 \u3000\u3000\u5f53\u4e0b\u62b1\u7740\u5e08\u7236\u9057\u4f53\uff0c\u5934\u4e1c\u811a\u897f\u653e\u7f6e\u5728\u90a3\u51b0\u77f3\u4e4b\u4e0a\uff0c\u9000\u540e\u4e24\u6b65\uff0c\u606d\u606d\u656c\u656c\u5730\u62dc\u4e86\u4e09\u62dc\uff0c\u542b\u6cea\u8bf4\u9053\uff1a\u201c\u5e08\u7236\uff0c\u5f1f\u5b50\u6c38\u8fdc\u4e0d\u4f1a\u5fd8\u8bb0\u4f60\u8001\u7684\u4e00\u8a00\u4e00\u8bed\uff0c\u4f46\u613f\u4f60\u8001\u5728\u5929\u4e4b\u7075\uff0c\u65e9\u83b7\u5b89\u606f\u2026\u2026\u201d \u3000\u3000\u62dc\u7f62\u8d77\u8eab\uff0c\u4f9d\u4f9d\u4e0d\u820d\u5730\u542b\u6cea\u9000\u51fa\u51b0\u7a96\u3002\u4ed6\u6309\u52a8\u673a\u94ae\uff0c\u56de\u590d\u4e86\u77f3\u5e8a\u539f\u4f4d\uff0c\u8f6c\u8eab\u8d70\u5230\u5e08\u7956\u9057\u50cf\u4e4b\u524d\uff0c\u62dc\u4e86\u4e09\u62dc\uff0c\u606d\u656c\u7684\u53d6\u4e0b\u90a3\u5e45\u767d\u7ee2\u9057\u50cf\uff0c\u5c0f\u5fc3\u5730\u5377\u4e86\u8d77\u6765\uff0c\u8d34\u5728\u80f8\u524d\u85cf\u597d\u3002 \u3000\u3000\u4ed6\u4ed4\u7ec6\u5730\u5728\u5ba4\u5185\u5de1\u89c6\u4e00\u756a\uff0c\u56e2\u7d27\u7a97\u6237\uff0c\u7f13\u6b65\u9000\u51fa\u95e8\u5916\uff0c\u53c8\u671b\u95e8\u4e00\u62dc\uff0c\u65b9\u59cb\u6263\u4e0a\u4e86\u6728\u95e8\uff0c\u5feb\u6b65\u56de\u5230\u81ea\u5df1\u623f\u4e2d\u53d6\u4e86\u957f\u5251\u659c\u63d2\u80a9\u5934\uff0c\u8fd9\u624d\u4e00\u6b65\u4e00\u56de\u5934\u5730\u51fa\u5ba4\uff0c\u5f80\u540e\u5c71\u800c\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u7f13\u6b65\u884c\u81f3\u540e\u5c71\uff0c\u8033\u4e2d\u542c\u5f97\u8fdc\u5904\u4f20\u6765\u6570\u58f0\u733f\u557c\uff0c\u5fc3\u4e2d\u7a81\u7136\u60f3\u8d77\u90a3\u4ecd\u5728\u524d\u5c71\u5b88\u536b\u7684\u767d\u733f\u548c\u9e66\u9e49\uff0c\u773c\u4e0b\u4e0d\u77e5\u7a76\u7adf\u5982\u4f55\u4e86\uff1f \u3000\u3000\u4ed6\u81ea\u4ece\u4e0a\u5c71\u4ee5\u540e\uff0c\u5c31\u6ca1\u89c1\u8fc7\u8fd9\u4e00\u5bf9\u4ed9\u79bd\u7075\u517d\uff0c\u5e08\u7236\u4e5f\u672a\u518d\u5411\u81ea\u5df1\u63d0\u53ca\uff0c\u663e\u7136\u662f\u5b83\u4eec\u81ea\u6709\u6c42\u751f\u4e4b\u9053\uff0c\u4e0d\u5fc5\u70e6\u4eba\u7167\u5e94\uff0c\u4f46\u81ea\u5df1\u5373\u5c06\u79bb\u5c71\u8fdc\u884c\uff0c\u662f\u5426\u5e94\u8be5\u5230\u524d\u5c71\u53bb\u77a7\u4e00\u77a7\uff0c\u4e00\u65f6\u4e4b\u95f4\u5012\u62ff\u4e0d\u5b9a\u4e3b\u610f\u3002 \u3000\u3000\u53ea\u662f\u4ed6\u53c8\u60f3\u5230\uff0c\u81ea\u5df1\u4e0e\u8fd9\u4ed9\u79bd\u7075\u517d\u53ea\u89c1\u8fc7\u90a3\u4e48\u4e00\u6b21\u9762\uff0c\u5b83\u4eec\u662f\u5426\u8fd8\u8bb0\u5f97\u81ea\u5df1\uff0c\u4e0d\u628a\u6211\u5f53\u4f5c\u5916\u6765\u4e4b\u654c\u5462\uff1f \u3000\u3000\u5bfb\u601d\u53ca\u6b64\uff0c\u987f\u7136\u89c9\u51fa\u8fd8\u662f\u6682\u65f6\u83ab\u53bb\u524d\u5c71\u4e3a\u5999\uff0c\u8fd9\u4e00\u5207\u7684\u540e\u4e8b\uff0c\u5c31\u9075\u4ece\u5e08\u7236\u9057\u8a00\uff0c\u89c1\u5230\u5e08\u53d9\u4e4b\u540e\uff0c\u518d\u7531\u5e08\u53d4\u53bb\u5904\u7406\u5427\uff01 \u3000\u3000\u5fc3\u5ff5\u4e00\u5b9a\uff0c\u4ed6\u7acb\u5373\u4e00\u63d0\u771f\u6c14\uff0c\u5faa\u7740\u5148\u524d\u7684\u8def\u5f84\uff0c\u5728\u591c\u8272\u832b\u832b\u4e4b\u4e2d\uff0c\u6251\u5954\u5c71\u4e0b\u800c\u53bb\u3002 \u3000\u3000\u76f4\u5230\u7b2c\u4e8c\u5929\u7684\u4e2d\u5348\u65f6\u5206\uff0c\u65b9\u96ea\u5b9c\u5df2\u9010\u6e10\u8d70\u51fa\u4e86\u7a77\u5c71\u6076\u5cad\uff0c\u901a\u5230\u4e86\u4eba\u8e2a\uff0c\u4e00\u6253\u542c\uff0c\u8fd9\u513f\u5730\u5c5e\u56db\u5ddd\u61cb\u529f\u53bf\u5883\uff0c\u4ed6\u8fd9\u624d\u660e\u767d\uff0c\u81ea\u5df1\u968f\u7740\u5e08\u7236\u5c45\u4e86\u4e09\u5e74\u516d\u4e2a\u591a\u6708\u7684\u5730\u65b9\uff0c\u6b63\u662f\u5ddd\u85cf\u63a5\u754c\u4e4b\u5904\u7684\u909b\u5d03\u5c71\u4e3b\u8109\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5f53\u665a\u8d76\u5230\u4e86\u61cb\u529f\u53bf\u57ce\uff0c\u7167\u7740\u5e08\u7236\u5728\u90a3\u4fe1\u5c01\u80cc\u540e\u6240\u793a\uff0c\u5411\u5e97\u5bb6\u6253\u63a2\u660e\u767d\u524d\u5f80\u5ce8\u5d4b\u7684\u8def\u5f84\uff0c\u6b47\u5bbf\u4e00\u5bb5\uff0c\u7b2c\u4e8c\u5929\u4e00\u65e9\uff0c\u5c31\u6cbf\u7740\u5ddd\u85cf\u8fb9\u754c\uff0c\u76f4\u5411\u5ce8\u5d4b\u8fdb\u53d1\u3002\u5929\u5bd2\u5730\u51bb\uff0c\u8fd9\u4e00\u5e26\u5c71\u8def\u5341\u5206\u96be\u8d70\uff0c\u4efb\u4ee4\u65b9\u96ea\u5b9c\u8eab\u5177\u7edd\u9876\u6b66\u529f\uff0c\u4ecd\u7136\u82b1\u4e86\u4e09\u5929\u7684\u65f6\u5149\uff0c\u624d\u8d70\u5b8c\u8fd9\u56db\u767e\u91cc\u4e0d\u5230\u7684\u8def\u7a0b\uff0c\u62b5\u8fbe\u4e86\u5ce8\u5d4b\u53bf\u57ce\u3002 \u3000\u3000\u6b21\u65e5\u9ec4\u660f\u65f6\u5206\uff0c\u65b9\u96ea\u5b9c\u603b\u7b97\u6309\u56fe\u7d22\u9aa5\u5728\u5ce8\u5d4b\u7edd\u9876\uff0c\u4e3b\u5cf0\u7684\u91d1\u5149\u660e\u9876\u53f3\u4fa7\u4e00\u5904\u5ced\u58c1\u4e4b\u4e0b\uff0c\u627e\u5230\u4e86\u5367\u4e91\u576a\u548c\u90a3\u4e09\u95f4\u8305\u5eb5\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4e09\u95f4\u7531\u8305\u8349\u5de8\u7af9\u642d\u5efa\u7684\u5eb5\u5b87\uff0c\u80cc\u501a\u91d1\u9876\u5ced\u58c1\uff0c\u524d\u4e34\u4e07\u4e08\u6df1\u58d1\uff0c\u4e91\u96fe\u56de\u7ed5\uff0c\u662f\u4ee5\u5f62\u52bf\u5341\u5206\u9669\u8981\uff0c\u4f46\u56e0\u8fd9\u5904\u5c71\u576a\u5e76\u975e\u5168\u7cfb\u5ca9\u77f3\u7ec4\u6210\uff0c\u6cbf\u7740\u5eb5\u5b57\u56db\u5468\uff0c\u4fbf\u957f\u6ee1\u4e86\u82cd\u677e\u7fe0\u67cf\uff0c\u5728\u90a3\u7eff\u836b\u8986\u76d6\u4e4b\u4e0b\uff0c\u5982\u662f\u4e0d\u8d70\u5230\u8fd1\u524d\uff0c\u51b3\u96be\u53d1\u73b0\u6b64\u95f4\u5c1a\u6709\u623f\u820d\uff0c\u65b9\u96ea\u5b9c\u82e5\u975e\u6709\u7740\u5e08\u7236\u6240\u4e66\u7684\u8be6\u56fe\uff0c\u53ea\u6015\u7eb5\u7136\u5bfb\u5230\u91d1\u9876\uff0c\u4e5f\u96be\u53d1\u73b0\u5e08\u53d4\u7684\u6cd5\u9a7e\u9a7b\u9a7f\u4e4b\u6240\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u6cbf\u7740\u90a3\u7edd\u9876\u5ced\u58c1\u800c\u884c\uff0c\u4e00\u8fde\u8f6c\u8fc7\u4e09\u5904\u5371\u5761\uff0c\u98de\u6e21\u4e24\u5904\u5bbd\u8fbe\u6570\u4e08\u7684\u65ad\u5d16\uff0c\u59cb\u884c\u8dc3\u843d\u90a3\u51e0\u4e0e\u4eba\u4e16\u9694\u7edd\u7684\u5367\u4e91\u576a\u3002 \u3000\u3000\u4e3e\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u89e6\u76ee\u6240\u53ca\uff0c\u82cd\u677e\u7fe0\u5e15\uff0c\u904d\u5e03\u576a\u4e0a\uff0c\u51e0\u682a\u5de8\u67ab\u53e4\u67cf\uff0c\u5c1a\u8fd8\u6b8b\u7559\u534a\u6811\u7ea2\u53f6\uff0c\u4e00\u7247\u58a8\u78a7\u4e4b\u4e2d\uff0c\u70b9\u7f00\u7740\u8fd9\u6570\u70b9\u5ae3\u7ea2\uff0c\u522b\u6709\u4e00\u756a\u8bf4\u4e0d\u51fa\u6765\u7684\u98ce\u5473\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u65e0\u5fc3\u8d2a\u770b\u8fd9\u4eba\u95f4\u4ed9\u5883\u7684\u666f\u8272\uff0c\u4e3e\u6b65\u671d\u63a9\u6620\u5728\u6797\u836b\u6df1\u5904\u7684\u4e09\u95f4\u8305\u5eb5\u884c\u53bb\u3002 \u3000\u3000\u4e34\u8fd1\u5eb5\u524d\uff0c\u8fd9\u624d\u770b\u5230\u5eb5\u95e8\u4e4b\u4e0a\uff0c\u8305\u8349\u8986\u76d6\u7740\u5c4b\u6cbf\uff0c\u4e0b\u9762\u6a2a\u6302\u7740\u4e00\u5757\u6728\u533e\uff0c\u96b6\u4e66\u7740\u201c\u5367\u4e91\u5eb5\u201d\u4e09\u4e2a\u5927\u5b57\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u7565\u4e00\u72b9\u8c6b\uff0c\u624d\u4f38\u624b\u5411\u95e8\u4e0a\u7684\u6b23\u73af\u8f7b\u8f7b\u6263\u53bb\u3002 \u3000\u3000\u7b49\u4e86\u7ea6\u83ab\u76cf\u8336\u4e4b\u4e45\uff0c\u90a3\u5eb5\u95e8\u65b9\u59cb\u5440\u7136\u6253\u5f00\uff0c\u4e00\u4f4d\u5168\u8eab\u767d\u8863\u7684\u5c0f\u5c3c\u59d1\uff0c\u5f53\u95e8\u800c\u7acb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u4e86\u4e00\u5446\uff0c\u4ed6\u53ef\u6ca1\u60f3\u5230\u5e08\u53d4\u9690\u8eab\u7684\u5eb5\u5802\uff0c\u5f00\u95e8\u6709\u8fd9\u7b49\u5e74\u7eaa\u8f7b\u8f7b\u7684\u5c0f\u5c3c\u59d1\uff0c\u662f\u4ee5\u4e00\u6123\u4e4b\u4e0b\uff0c\u7adf\u7136\u5fd8\u8bb0\u4e86\u5411\u5bf9\u65b9\u8bf4\u51fa\u81ea\u5df1\u7684\u6765\u610f\u3002 \u3000\u3000\u90a3\u767d\u8863\u5973\u5c3c\uff0c\u5e74\u7eaa\u5927\u7ea6\u53ea\u6709\u5341\u4e94\u516d\u5c81\u5de6\u53f3\uff0c\u867d\u7136\u53d7\u6212\u843d\u53d1\uff0c\u770b\u4e0a\u53bb\u4f9d\u65e7\u6e05\u79c0\u7edd\u4fd7\uff0c\u5bb9\u5149\u7167\u4eba\uff0c\u6b64\u523b\u53ef\u80fd\u662f\u4e4d\u89c1\u751f\u4eba\u4e4b\u6545\uff0c\u8138\u4e0a\u73b0\u51fa\u4e86\u4e24\u6735\u7ea2\u6655\uff0c\u4eff\u4f5b\u4e0d\u80dc\u5a07\u7f9e\u3002 \u3000\u3000\u5979\u7b49\u4e86\u4e00\u4f1a\u513f\uff0c\u6ca1\u542c\u89c1\u65b9\u96ea\u5b9c\u8bf4\u8bdd\uff0c\u7adf\u7136\u7f13\u7f13\u5730\u9000\u4e86\u4e00\u6b65\uff0c\u4f38\u51fa\u4e24\u53ea\u7ea4\u7ea4\u7389\u624b\uff0c\u5c31\u5f85\u5c06\u5eb5\u95e8\u63a9\u4e0a\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u624d\u5fc3\u5934\u6026\u7136\u4e00\u52a8\uff0c\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u5c0f\u5e08\u7236\u2026\u2026\u201d \u3000\u3000\u5c0f\u5c3c\u59d1\u542c\u5f97\u65b9\u96ea\u5b9c\u53e3\u4e2d\u547c\u5524\uff0c\u6ca1\u518d\u63a9\u95e8\uff0c\u4f46\u5374\u4e5f\u6ca1\u6709\u56de\u8bdd\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u6025\u4e4b\u4e0b\uff0c\u53ea\u597d\u539a\u7740\u8138\uff0c\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u5c0f\u5e08\u7236\u2026\u2026\u8bf7\u95ee\u91d1\u9876\u795e\u5c3c\u2026\u2026\u524d\u8f88\u53ef\u5728\u5eb5\u4e2d\u6e05\u4fee\uff1f\u201d \u3000\u3000\u5c0f\u5c3c\u59d1\u95fb\u8a00\uff0c\u8138\u4e0a\u867d\u662f\u7ea2\u4e91\u6ee1\u5e03\uff0c\u4f46\u4ecd\u7136\u6709\u4e9b\u60ca\u8bb6\u4e4b\u8272\uff0c\u5979\u81ea\u4ece\u8ddf\u968f\u5e08\u7236\u5728\u5367\u4e91\u5eb5\u4fee\u884c\u4ee5\u6765\uff0c\u5341\u591a\u5e74\u4e2d\uff0c\u9664\u4e86\u90a3\u6bcf\u6708\u9001\u67f4\u3001\u7c73\u3001\u6cb9\u3001\u76d0\u7684\u8001\u5934\u513f\u6309\u65f6\u6765\u5230\u5eb5\u4e2d\uff0c\u77e5\u9053\u5e08\u7236\u7684\u6cd5\u53f7\u4ee5\u5916\uff0c\u5176\u4ed6\u5076\u7136\u4e5f\u6709\u4e00\u4e24\u4f4d\u6765\u6b64\u6e38\u8bbf\u7684\u6b66\u6797\u4eba\u7269\uff0c\u53ef\u5374\u4ece\u6765\u65e0\u4eba\u77e5\u6653\u5e08\u7236\u7684\u6cd5\u53f7\uff0c\u773c\u524d\u8fd9\u5e74\u8f7b\u4eba\u7adf\u4e00\u53e3\u5c31\u53eb\u51fa\u5e08\u7236\u7684\u6cd5\u53f7\uff0c\u600e\u4e0d\u4ee4\u8fd9\u4f4d\u5c0f\u5c3c\u59d1\u5927\u611f\u5947\u602a\u5462\uff0c\u5c0f\u5c3c\u59d1\u5fc3\u4e2d\u56fa\u7136\u662f\u975e\u5e38\u7684\u60ca\u8bb6\uff0c\u4f46\u53e3\u4e2d\u5374\u4e0d\u80fd\u518d\u4e0d\u56de\u7b54\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u65bd\u4e3b\u8ba4\u8bc6\u5bb6\u5e08\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u60f3\uff0c\u539f\u6765\u662f\u5e08\u53d4\u7684\u5f92\u513f\uff0c\u7b97\u6765\u8be5\u662f\u79f0\u547c\u5979\u4e00\u58f0\u5e08\u59b9\u624d\u662f\u2026\u2026\u8f6c\u5ff5\u4e4b\u95f4\uff0c\u6539\u53e3\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u5e08\u59b9\u2026\u2026\u5c0f\u5e08\u7236\u3002\u201d \u3000\u3000\u6562\u60c5\u65b9\u96ea\u5b9c\u65e2\u4e0d\u77e5\u9053\u8fd9\u4f4d\u5bc4\u8eab\u65b9\u5916\u7684\u5e08\u59b9\u540d\u5b57\uff0c\u53c8\u4e0d\u61c2\u662f\u5426\u53ef\u4ee5\u76f4\u63a5\u79f0\u547c\u53eb\u5979\u4e00\u58f0\u5e08\u59b9\uff0c\u800c\u5979\u4f1a\u5426\u89c1\u602a\uff0c\u6240\u4ee5\uff0c\u5e08\u59b9\u4e24\u4e2a\u5b57\u8bf4\u51fa\uff0c\u60f3\u5230\u8fd8\u5e94\u8be5\u5ba2\u6c14\u4e00\u4e9b\uff0c\u5c31\u53c8\u52a0\u4e86\u201c\u5c0f\u5e08\u7236\u201d\u4e09\u4e2a\u5b57\u4e0a\u53bb\u3002 \u3000\u3000\u6b8a\u4e0d\u77e5\u4ed6\u8fd9\u4e48\u4e00\u52a0\uff0c\u5374\u52a0\u7684\u6709\u4e9b\u4e0d\u4f26\u4e0d\u7c7b\u3002 \u3000\u3000\u5c0f\u5c3c\u59d1\u5148\u662f\u4e00\u6123\uff0c\u7ee7\u800c\u5fcd\u4e0d\u4f4f\u60f3\u7b11\u51fa\u58f0\u6765\uff0c\u4f46\u5979\u5e78\u800c\u9a6c\u4e0a\u60f3\u8d77\u4e86\u5e08\u95e8\u7684\u6e05\u89c4\uff0c\u4fbf\u5f3a\u81ea\u5fcd\u4e0b\u7b11\u610f\uff0c\u9053\uff1a\u201c\u65bd\u4e3b\u4e5f\u662f\u5e08\u7236\u7684\u5f1f\u5b50\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e0d\u7981\u53c8\u662f\u4e00\u6014\uff0c\u663e\u7136\u51b2\u7740\u8fd9\u4f4d\u5c3c\u59d1\u5e08\u59b9\u7684\u8fd9\u4e00\u53e5\u8bdd\uff0c\u4ed6\u5df2\u60f3\u5230\u5979\u8fd8\u4e0d\u77e5\u9053\u81ea\u5df1\u6709\u4f4d\u5251\u795e\u5e08\u4f2f\u54e9\u3002 \u3000\u3000\u5f53\u4e0b\u8fde\u5fd9\u6447\u5934\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u795e\u5c3c\u662f\u6211\u5e08\u53d4\u2026\u2026\u6211\u5e08\u7236\u662f\u4e16\u4eba\u5c0a\u8a89\u7684\u5251\u795e\u5927\u4fa0\uff01\u201d \u3000\u3000\u5c0f\u5c3c\u59d1\u60f3\u5fc5\u4ece\u672a\u542c\u5230\u8fc7\u5251\u795e\u4e4b\u4e8b\uff0c\u95fb\u8a00\u5374\u9053\uff1a\u201c\u65bd\u4e3b\u53ef\u662f\u8981\u89c1\u6211\u5e08\u7236\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5949\u4e86\u5e08\u7236\u4e4b\u547d\uff0c\u7279\u6765\u53e9\u89c1\u795e\u5c3c\u5e08\u53d4\uff0c\u5448\u4e0a\u4e00\u5c01\u4e66\u4fe1\u3002\u201d \u3000\u3000\u5c0f\u5c3c\u7ad9\u4f4e\u5934\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u65bd\u4e3b\uff0c\u4f60\u7b49\u4e00\u4f1a\u513f\u597d\u5417\uff1f\u6211\u2026\u2026\u8d2b\u5c3c\u53bb\u7980\u62a5\u5e08\u7236\uff0c\u5e08\u7236\u5982\u662f\u8ba9\u4f60\u8fdb\u6765\uff0c\u6211\u518d\u6765\u7ed9\u4f60\u5f15\u8def\u5427\uff01\u201d\u7f13\u7f13\u8d70\u5411\u5de6\u624b\u7684\u4e00\u95f4\u4e91\u623f\u4e4b\u4e2d\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u77a7\u7740\u5979\u4e3e\u6b65\u4e4b\u95f4\uff0c\u8eab\u5f62\u5a40\u5a1c\uff0c\u867d\u662f\u88f9\u5728\u5bbd\u5927\u7684\u8863\u670d\u4e2d\uff0c\u4f46\u662f\u63a9\u4e0d\u4f4f\u5a09\u5a77\u4e4b\u6001\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u4e0d\u6562\u591a\u77a7\uff0c\u751f\u6050\u81ea\u5df1\u8fd9\u4e00\u5ff5\u5c18\u601d\uff0c\u8981\u4eb5\u6e0e\u4e86\u8fd9\u5757\u4f5b\u95e8\u51c0\u571f\uff0c\u4e0d\u7531\u81ea\u4e3b\u5730\u5411\u540e\u9000\u4e86\u4e24\u6b65\u3002 \u3000\u3000\u8fc7\u4e0d\u4e86\u4e00\u4f1a\uff0c\u90a3\u767d\u8863\u5c0f\u5c3c\u59d1\u8f7b\u5feb\u5730\u8d70\u4e86\u8fc7\u6765\uff0c\u5a07\u58f0\u9053\uff1a\u201c\u5e08\u5144\uff0c\u5e08\u7236\u8981\u4f60\u8fdb\u6765\u54e9\u3002\u201d \u3000\u3000\u58f0\u97f3\u4e4b\u4e2d\uff0c\u900f\u51fa\u65e0\u6bd4\u7684\u559c\u60a6\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u8d76\u5fd9\u62b1\u62f3\u9053\uff1a\u201c\u6709\u52b3\u5e08\u59b9\u5f15\u8def\u3002\u201d \u3000\u3000\u5b9e\u5219\uff0c\u8305\u5eb5\u4ec5\u53ea\u4e09\u95f4\u6b63\u5c4b\uff0c\u4e0d\u987b\u5f15\u8def\uff0c\u4e5f\u4e0d\u987b\u51e0\u6b65\uff0c\u65b9\u96ea\u5b9c\u5c31\u5df2\u89c1\u5230\u81ea\u5df1\u8981\u89c1\u7684\u5e08\u53d4\u91d1\u9876\u795e\u5c3c\u3002 \u3000\u3000\u4ed6\u8de8\u8fdb\u90a3\u5de6\u624b\u4e91\u623f\u7684\u6728\u95e8\uff0c\u62ac\u5934\u671b\u53bb\uff0c\u53ea\u89c1\u4e00\u4f4d\u5e74\u7ea6\u4e94\u5341\u51fa\u5934\uff0c\u6148\u76fe\u5584\u76ee\uff0c\u8138\u8272\u7a0d\u73b0\u6fc0\u52a8\u7684\u7070\u8863\u8001\u5c3c\uff0c\u6b63\u76d8\u5750\u5728\u9760\u7a97\u4e00\u9762\u7684\u7985\u5e8a\u4e4b\u4e0a\uff0c\u671b\u7740\u81ea\u5df1\u5fae\u7b11\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u77e5\u8fd9\u5b9a\u7136\u662f\u5e08\u53d4\u91d1\u9876\u795e\u5c3c\u4e86\uff0c\u6025\u884c\u6570\u6b65\uff0c\u66f2\u819d\u62dc\u5012\u5728\u5730\uff0c\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u5f1f\u5b50\u65b9\u96ea\u5b9c\u53e9\u95ee\u5e08\u53d4\u91d1\u5b89\u2026\u2026\u201d \u3000\u3000\u795e\u5c3c\u62ac\u4e86\u62ac\u624b\uff0c\u9053\uff1a\u201c\u8d77\u6765\u8bb2\u8bdd\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u606d\u606d\u656c\u656c\u5730\u5e94\u4e86\u58f0\uff1a\u201c\u5f1f\u5b50\u9075\u547d\uff0c\u2026\u2026\u201d\u53cc\u624b\u638f\u51fa\u6069\u5e08\u7684\u4e66\u4fe1\uff0c\u5448\u9012\u4e0a\u53bb\u9053\uff1a \u3000\u3000\u201c\u5e08\u7236\u6709\u4e00\u5c01\u4e66\u4fe1\uff0c\u8981\u5f1f\u5b50\u5448\u4ea4\u5e08\u53d4\u2026\u2026\u201d \u3000\u3000\u795e\u5c3c\u63a5\u8fc7\u4e66\u4fe1\uff0c\u7a81\u7136\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e0d\u89c1\u5e08\u5144\uff0c\u7b97\u6765\u5df2\u6709\u5341\u4e94\u5e74\u4e86\uff0c\u60f3\u4e0d\u5230\u4eca\u65e5\u7adf\u4f1a\u9063\u4f60\u9001\u4fe1\u6765\u6b64\u2026\u2026\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u76ee\u5149\u5728\u65b9\u96ea\u5b9c\u8eab\u4e0a\u4e00\u8f6c\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u5e08\u7236\u597d\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9012\u4e0a\u4e86\u4e66\u4fe1\uff0c\u9000\u540e\u4e24\u6b65\uff0c\u5782\u624b\u8083\u7acb\uff0c\u8138\u4e0a\u4e00\u6d3e\u606d\u8c28\u795e\u8272\uff0c\u4f46\u5ffd\u7136\u542c\u5230\u795e\u5c3c\u95ee\u53ca\u5e08\u7236\uff0c\u4e0d\u7981\u987f\u65f6\u60b2\u4ece\u4e2d\u6765\uff0c\u98a4\u58f0\u5e94\u9053\uff1a\u201c\u5e08\u7236\u2026\u2026\u4ed6\u2026\u2026\u8001\u4eba\u5bb6\u2026\u2026\u5df2\u7ecf\u4ed9\u901d\u4e86\u2026\u2026\u201d \u3000\u3000\u4e24\u9897\u70ed\u6cea\uff0c\u593a\u7736\u800c\u51fa\u3002 \u3000\u3000\u91d1\u9876\u795e\u5c3c\u5bb9\u989c\u5927\u53d8\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u84e6\u5730\u66f2\u819d\u8dea\u5012\uff0c\u6251\u62dc\u5728\u5730\uff0c\u63a5\u9053\uff1a\u201c\u5f1f\u5b50\u4e0d\u5b5d\uff0c\u672a\u80fd\u597d\u597d\u4f8d\u5949\u5e08\u7236\uff0c\u4ed6\u8001\u4eba\u5bb6\u4e27\u8eab\u5728\u909b\u5d03\u5c71\u4e2d\u3002 \u3000\u3000\u91d1\u9876\u795e\u5c3c\u5140\u6709\u4e9b\u4e0d\u4fe1\uff0c\u4e5f\u6709\u4e9b\u6025\u8e81\u5730\u95ee\u9053\uff1a\u201c\u4f60\u5e08\u7236\u6b66\u529f\u9ad8\u7edd\uff0c\u88ab\u6b66\u6797\u4e2d\u4eba\u7269\u5c0a\u79f0\u4e3a\u5251\u795e\uff0c\u4ed6\u600e\u4e48\u4f1a\u7a81\u7136\u6b7b\u53bb\uff1f\u662f\u4ec0\u4e48\u4eba\u5bb3\u4e86\u4ed6\uff1f\u5feb\u8bf4\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4fef\u9996\u6447\u5934\u9053\uff1a\u201c\u5e08\u7236\u662f\u75c5\u6b7b\u7684\u554a\uff01\u2026\u2026\u201d \u3000\u3000\u91d1\u9876\u795e\u5c3c\u6c89\u541f\u9053\uff1a\u201c\u75c5\u6b7b\u7684\u5417\uff1f\u8fd9\u66f4\u96be\u8ba9\u4eba\u76f8\u4fe1\u4e86\uff0c\u4f60\u8d77\u6765\u5427\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5e94\u4e86\u58f0\uff1a\u201c\u5f1f\u5b50\u9075\u547d\uff01\u201d\u8d77\u8eab\u8083\u7acb\u4e00\u65c1\uff0c\u8fde\u8138\u4e0a\u7684\u6cea\u75d5\u4e5f\u5fd8\u8bb0\u62c2\u62ed\u3002 \u3000\u3000\u795e\u5c3c\u6b64\u523b\u5df2\u7f13\u7f13\u62c6\u5f00\u4e86\u90a3\u5c01\u4fe1\u3002 \u3000\u3000\u4f46\u89c1\u5979\u5c55\u5f00\u4fe1\u7b3a\u4e4b\u540e\uff0c\u9661\u7136\u95f4\u795e\u60c5\u6728\u8bb7\uff0c\u534a\u664c\u4e00\u52a8\u672a\u52a8\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u89c9\u5f97\u6709\u4e9b\u610f\u5916\uff0c\u4f46\u4ed6\u52a8\u4e0d\u6562\u51fa\u58f0\u3002 \u3000\u3000\u90a3\u767d\u8863\u5c0f\u5c3c\u59d1\u672c\u662f\u7ad9\u5728\u4e91\u623f\u7684\u95e8\u53e3\uff0c\u773c\u89c1\u5e08\u7236\u5c55\u5f00\u4fe1\u7b3a\u4e4b\u540e\uff0c\u7adf\u662f\u4e24\u773c\u53d1\u76f4\uff0c\u534a\u664c\u4e0d\u52a8\uff0c\u5fc3\u4e2d\u5927\u4e3a\u60ca\u9a87\uff0c\u8fde\u5fd9\u5954\u4e86\u8fc7\u6765\uff0c\u4f38\u51fa\u7389\u624b\uff0c\u62c9\u7740\u795e\u5c3c\u7684\u8863\u8896\u8fde\u8fde\u626f\u52a8\uff0c\u53e3\u4e2d\u4e5f\u4e0d\u505c\u5730\u53eb\u9053\uff1a\u201c\u5e08\u7236\uff0c\u2026\u2026\u4f60\u8001\u600e\u4e48\u4e86\u2026\u2026\u201d \u3000 \u7b2c\u4e5d\u56de\u3000\u91d1\u9876\u795e\u5c3c \u3000 \u3000\u3000\u7ecf\u5c0f\u5c3c\u59d1\u8fd9\u4e48\u8fde\u63a8\u5e26\u62c9\uff0c\u795e\u5c3c\u90a3\u5b9a\u4e86\u795e\u7684\u76ee\u5149\uff0c\u8fd9\u624d\u7565\u89c1\u8f6c\u52a8\uff0c\u6ee1\u8138\u5177\u662f\u60b2\u82e6\u4e4b\u8272\uff0c\u671b\u7740\u65b9\u96ea\u5b9c\u8f7b\u8f7b\u4e00\u53f9\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u4fe1\u662f\u5e08\u7236\u5144\u4eb2\u624b\u4ea4\u7ed9\u4f60\u7684\uff0c\u8fd8\u662f\u6b7b\u540e\u4f60\u81ea\u5df1\u627e\u5230\u7684\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u662f\u5e08\u4eb2\u624b\u4ea4\u7ed9\u5f1f\u5b50\uff0c\u8981\u5f1f\u5b50\u4f9d\u7167\u56fe\u793a\u7684\u8def\u5f84\u7acb\u5373\u524d\u6765\u53e9\u89c1\u5e08\u53d4\u3002\u201d \u3000\u3000\u795e\u5c3c\u6148\u7709\u5ffd\u7136\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u65e2\u662f\u5e08\u5144\u5e08\u5144\u4eb2\u624b\u4ea4\u4ed8\u4e88\u4f60\uff0c\u663e\u7136\u4f60\u79bb\u5f00\u4e4b\u65f6\uff0c\u5e08\u5144\u5c1a\u672a\u8fc7\u4e16\u7684\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f1f\u5b50\u79bb\u5c71\u4e4b\u65f6\uff0c\u5e08\u7236\u786e\u662f\u5c1a\u672a\u4ed9\u53bb\u3002\u201d \u3000\u3000\u795e\u5c3c\u9661\u5730\u559d\u9053\uff1a\u201c\u4f60\u5e08\u7236\u65e2\u662f\u4f60\u79bb\u5c71\u65f6\u5c1a\u672a\u4ed9\u53bb\uff0c\u4f60\u53c8\u600e\u5730\u77e5\u9053\u4ed6\u56e0\u75c5\u4ea1\u6545\u4e4b\u4e8b\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4f3c\u662f\u542c\u51fa\u5e08\u53d4\u8bdd\u4e2d\u731c\u7591\u4e4b\u610f\uff0c\u5fc3\u4e2d\u4e00\u60ca\uff0c\u8fde\u5fd9\u63a5\u9053\uff1a\u201c\u5f1f\u5b50\u672c\u662f\u5728\u9ec4\u660f\u65f6\u5206\u5306\u5306\u4e0b\u5c71\uff0c\u4f46\u884c\u5230\u534a\u9014\uff0c\u7a81\u7136\u60f3\u8d77\u5fd8\u8bb0\u4f69\u5e26\u968f\u8eab\u5175\u5203\uff0c\u8fd9\u624d\u91cd\u65b0\u8d76\u56de\u5c71\u4e0a\u53bb\u53d6\uff0c\u4e0d\u60f3\u752b\u8fdb\u5927\u95e8\uff0c\u5c31\u77a7\u5230\u5e08\u7236\u4e1a\u5df2\u65ad\u6c14\u591a\u65f6\u4e86\u3002\u201d \u3000\u3000\u795e\u5c3c\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u4f60\u4e14\u6162\u6162\u7684\u544a\u8bc9\u6211\u7ecf\u8fc7\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9eef\u7136\u795e\u4f24\uff0c\u628a\u81ea\u5df1\u5982\u4f55\u968f\u5e08\u4e60\u7ec3\u6b66\u529f\uff0c\u5e08\u7236\u5982\u4f55\u5728\u6559\u5b8c\u4e86\u5251\u6cd5\u4ee5\u540e\uff0c\u8981\u81ea\u5df1\u4e0b\u5c71\u6765\u89c1\u5e08\u53d4\uff0c\u4ee5\u53ca\u884c\u5230\u534a\u5c71\u5ffd\u7136\u9047\u89c1\u731b\u864e\uff0c\u8fd9\u624d\u60f3\u8d77\u4f69\u5251\u672a\u5e26\uff0c\u56de\u53bb\u53bb\u53d6\u5251\u800c\u53d1\u73b0\u5e08\u7236\u4e1a\u5df2\u4ed9\u901d\u4e4b\u4e8b\uff0c\u8be6\u7ec6\u7684\u8bf4\u4e86\u904d\u3002 \u3000\u3000\u91d1\u9876\u795e\u5c3c\u4e00\u76f4\u95ed\u76ee\u8046\u542c\uff0c\u5bb9\u5f97\u65b9\u96ea\u4e14\u8bf4\u51fa\u5168\u90e8\u7ecf\u8fc7\uff0c\u8fd9\u624d\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u9519\u602a\u4f60\u4e86\u2026\u2026\u201d\u8bed\u58f0\u4e00\u987f\uff0c\u4e00\u5f39\u624b\u4e2d\u4fe1\u7b3a\uff0c\u63a5\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u53ef\u77e5\u9053\u4f60\u5e08\u7236\u5728\u8fd9\u5c01\u4fe1\u4e2d\uff0c\u5199\u4e86\u4ec0\u4e48\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u6447\u5934\u9053\uff1a\u201c\u5f1f\u5b50\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u795e\u5c3c\u5e7d\u5e7d\u4e00\u53f9\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u6765\u77a7\u77a7\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u6014\u5fe1\u4e0d\u5df2\uff0c\u4ed8\u9053\uff1a\u83ab\u975e\u5e08\u7236\u5728\u8fd9\u5c01\u4fe1\u4e0a\u9a82\u4e86\u6211\u4e00\u987f\u5417\uff1f\u4f46\u4ed6\u4ecd\u7136\u8d70\u5230\u795e\u5c3c\u9762\u524d\u5411\u90a3\u5f20\u767d\u7eb8\u7b3a\u77a7\u53bb\uff0c\u5165\u76ee\u60ca\u5fc3\uff0c\u65b9\u96ea\u5b9c\u4e0d\u89c9\u5730\u5446\u4e86\uff0c\u795e\u5c3c\u624b\u4e2d\u7684\u90a3\u5f20\u767d\u7b3a\uff0c\u7adf\u662f\u4e00\u5f20\u7a7a\u767d\u4fe1\u7eb8\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u6709\u4e9b\u4e0d\u4fe1\uff0c\u62ac\u624b\u63c9\u4e86\u63c9\u773c\u775b\uff0c\u518d\u4ed4\u7ec6\u5730\u770b\u53bb\uff0c\u90a3\u4f9d\u65e7\u8fd8\u662f\u4e00\u5f20\u4e0d\u89c1\u4e00\u5b57\u7684\u767d\u7eb8\uff0c\u4e0d\u7981\u671f\u671f\u4ed6\u8bf4\u9053\uff1a\u201c\u5e08\u53d4\u554a\u2026\u2026\u5e08\u7236\u600e\u4e48\u53eb\u6211\u9001\u4e00\u5f20\u767d\u7eb8\u6765\u5462\uff1f\u5f1f\u5b50\u4e00\u8def\u4e4b\u4e0a\uff0c\u4e66\u4fe1\u4ece\u672a\u79bb\u8eab\uff0c\u90a3\u4e5f\u4e0d\u4f1a\u6709\u4eba\u6389\u6362\u5f97\u4e86\uff0c\u5e08\u53d4\uff0c\u8fd9\u7a76\u7adf\u2026\u2026\u201d \u3000\u3000\u795e\u5c3c\u9eef\u7136\u4e00\u53f9\u9053\uff1a\u201c\u4e66\u4fe1\u5e76\u672a\u88ab\u4eba\u6389\u6362\uff0c\u5b69\u5b50\uff0c\u53ea\u662f\u8fd9\u5f20\u767d\u7b3a\u4e4b\u4e0a\uff0c\u4f60\u5e08\u7236\u5e76\u672a\u5199\u5b57\u800c\u5df2\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u5f1f\u5b50\u7cca\u6d82\u4e86\u3002\u201d \u3000\u3000\u8fd9\u65f6\u90a3\u4f4d\u5c0f\u5c3c\u59d1\u4e5f\u542c\u5f97\u6709\u4e9b\u5947\u602a\uff0c\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u5e08\u7236\uff0c\u8fd9\u771f\u662f\u5e08\u4f2f\u5199\u6765\u7684\u767d\u7eb8\u5417\uff1f \u3000\u3000\u4f60\u8001\u4eba\u5bb6\u600e\u4e48\u4ece\u6765\u6ca1\u8ddf\u5f92\u513f\u63d0\u8fc7\u5e08\u4f2f\u8fd9\u4e2a\u4eba\u5462\uff1f\u201d \u3000\u3000\u795e\u5c3c\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u5e08\u4f2f\u4e43\u662f\u6b66\u6797\u4e2d\u7684\u5927\u6709\u6765\u5934\u4eba\u7269\uff0c\u4f60\u6211\u65e2\u7136\u4e0d\u60f3\u8eab\u5165\u6b66\u6797\uff0c\u53c8\u4f55\u5fc5\u8ba9\u4f60\u77e5\u9053\u8fd9\u4e9b\u4e8b\u5462\uff1f\u201d \u3000\u3000\u5c0f\u5c3c\u7ad9\u5a07\u58f0\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u8001\u65e2\u4e0d\u8981\u5f92\u513f\u8dfb\u8eab\u6b66\u6797\uff0c\u53c8\u53eb\u5f92\u513f\u7ec3\u90a3\u6b66\u529f\uff0c\u6709\u4f55\u7528\u5904\u5462\uff1f\u201d \u3000\u3000\u795e\u5c3c\u6148\u7965\u7684\u9762\u5bb9\uff0c\u5ffd\u7136\u4e00\u53d8\uff0c\u4f46\u7ec8\u4e8e\u5fae\u5fae\u4e00\u53f9\u9053\uff1a\u201c\u4f5b\u95e8\u5f1f\u5b50\uff0c\u96be\u514d\u6709\u6cbf\u95e8\u6258\u94b5\uff0c\u5c71\u5c45\u91ce\u5bbf\u4e4b\u65f6\uff0c\u5982\u82e5\u6ca1\u6709\u9632\u8eab\u4e4b\u80fd\uff0c\u5c82\u4e0d\u662f\u8fde\u81ea\u8eab\u4e5f\u65e0\u6cd5\u4fdd\u5168\u4e86\u5417\uff1f\u96ea\u513f\uff0c\u4f60\u83ab\u8981\u9519\u4f1a\u4e86\u4e3a\u5e08\u8981\u4f60\u4e60\u7ec3\u6b66\u529f\u7684\u7528\u610f\u3002\u201d \u3000\u3000\u90a3\u88ab\u5524\u4f5c\u96ea\u513f\u7684\u5c0f\u5c3c\u7ad9\uff0c\u561f\u7740\u5634\u9053\uff1a\u201c\u5e08\u7236\u554a\uff0c\u4f60\u8001\u4eba\u5bb6\u5f53\u771f\u4e0d\u8be5\u8981\u5f1f\u5b50\u7ec3\u6b66\u5462\u3002\u201d \u3000\u3000\u91d1\u9876\u795e\u5c3c\u542c\u5f97\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u96ea\u513f\uff0c\u4f60\u8fd9\u53e5\u6d3b\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u96ea\u513f\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u60f3\u5462\uff01\u5982\u82e5\u4f60\u8001\u4e0d\u6559\u6211\u6b66\u529f\uff0c\u5c82\u662f\u4e0d\u653e\u5fc3\u6211\u4e0b\u5c71\u79ef\u4fee\u5916\u529f\u5417\uff1f \u3000\u3000\u8fd9\u6837\u7684\u8bdd\uff0c\u5f92\u513f\u5c31\u53ef\u4ee5\u4e00\u76f4\u8ddf\u5728\u5e08\u7236\u8eab\u8fb9\u5566\uff01\u201d \u3000\u3000\u795e\u5c3c\u4f3c\u662f\u4e0d\u66fe\u60f3\u5230\u5979\u662f\u8fd9\u7b49\u7528\u5fc3\uff0c\u4e0d\u7981\u54e7\u7684\u4e00\u7b11\u9053\uff1a\u201c\u75f4\u513f\uff0c\u4e3a\u5e08\u4e5f\u4e0d\u80fd\u4f34\u4f60\u4e00\u8f88\u5b50\u5440\u2026\u2026\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u4fa7\u8138\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u4eca\u5e74\u591a\u5927\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f1f\u5b50\u4eca\u5e74\u5341\u4e5d\u4e86\u3002\u201d \u3000\u3000\u91d1\u9876\u795e\u5c3c\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4f60\u5e08\u7236\u4e00\u751f\u4e4b\u4e2d\uff0c\u6ca1\u6709\u505a\u597d\u4e00\u4ef6\u4e8b\uff0c\u4f46\u80fd\u5728\u4e34\u6b7b\u4e4b\u524d\uff0c\u6536\u5230\u4e86\u50cf\u4f60\u8fd9\u7b49\u8d44\u8d28\u7684\u4e00\u4e2a\u5f92\u513f\uff0c\u5012\u4e5f\u662f\u4e00\u6869\u83ab\u5927\u7684\u529f\u679c\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u5f97\u5fc3\u4e2d\u6697\u51db\uff0c\u5fd6\u9053\uff1a\u8fd9\u4f4d\u5e08\u53d4\u600e\u7684\u628a\u5e08\u7236\u7684\u4e00\u751f\u884c\u4e8b\uff0c\u5168\u7ed9\u8bf4\u7684\u4e00\u65e0\u662f\u5904\uff0c\u5e08\u7236\u80fd\u591f\u535a\u5f97\u5251\u795e\u7684\u7f8e\u540d\uff0c\u5c31\u662f\u6c5f\u6e56\u4e0a\u7684\u6b66\u6797\u4eba\u7269\uff0c\u5d07\u656c\u5e08\u7236\u884c\u4e8b\u5149\u660e\u6b63\u5927\uff0c\u9020\u798f\u6b66\u6797\u7684\u94c1\u8bc1\uff0c\u7ed3\u679c\u7adf\u5728\u5e08\u53d4\u53e3\u4e2d\u843d\u4e0b\u4e86\u4e00\u4e2a\u767e\u65e0\u4e00\u662f\u7684\u8a00\u8be0\uff0c\u786e\u662f\u53eb\u4eba\u4e0d\u89e3\u4e86\uff01 \u3000\u3000\u4ed6\u8fd9\u4e48\u7565\u4e00\u6c89\u541f\uff0c\u795e\u5c3c\u4f3c\u662f\u5df2\u7136\u77a7\u51fa\u4ed6\u5fc3\u4e2d\u6240\u60f3\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u5fc3\u4e2d\u53ef\u662f\u6709\u4e9b\u4e0d\u670d\u6211\u5bf9\u4f60\u5e08\u7236\u7684\u6307\u8d23\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fd9\u9053\uff1a\u201c\u5f1f\u5b50\u4e0d\u6562\u3002\u201d \u3000\u3000\u795e\u5c3c\u5ffd\u7136\u5e7d\u5e7d\u4e00\u53f9\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u6211\u4e5f\u77e5\u9053\u5e08\u5144\u5728\u4e8c\u5341\u5e74\u524d\uff0c\u5c31\u5df2\u83b7\u5f97\u4e86\u5251\u795e\u7684\u540d\u53f7\uff0c\u53d7\u5c3d\u4e86\u6b66\u6797\u4eba\u7269\u7684\u5c0a\u656c\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u624d\u6562\u7b11\u9053\uff1a\u201c\u5e08\u53d4\u8bf4\u7684\u4e0d\u9519\uff0c\u6c5f\u6e56\u4e0a\u90aa\u9b54\u5916\u9053\uff0c\u89c1\u4e86\u5e08\u7236\uff0c\u65e0\u4e0d\u9000\u907f\u4e09\u820d\u3002\u201d \u3000\u3000\u795e\u5c3c\u51b7\u7b11\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u7528\uff0c\u4ed6\u81ea\u5df1\u8fd8\u4e0d\u662f\u6bd4\u90a3\u4e9b\u90aa\u9b54\u5916\u9053\u5148\u6b7b\u5417\uff1f\u4e00\u4e16\u7a7a\u540d\uff0c\u4e0d\u503c\u5f97\u70ab\u8000\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u987f\u65f6\u5446\u4e86\u4e00\u5446\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u8fd9\u4e0d\u662f\u5947\u602a\u5417\uff1f\u4eba\u751f\u767e\u5c81\uff0c\u4e5f\u96be\u9003\u4e00\u6b7b\uff0c\u96be\u9053\u8fd8\u8981\u5e08\u7236\u6c38\u8fdc\u6d3b\u5728\u4e16\u4e0a\uff0c\u624d\u503c\u5f97\u70ab\u8000\uff1f\u4f55\u51b5\u4ee5\u4e00\u4e2a\u4eba\u7684\u8840\u8089\u4e4b\u8eaf\uff0c\u80fd\u591f\u6297\u62d2\u5fc5\u6b7b\u4e4b\u75c5\uff0c\u8db3\u8fbe\u5341\u5e74\u4e4b\u4e45\uff0c\u4f7f\u6b66\u6797\u4e2d\u7a0d\u83b7\u5598\u606f\u751f\u673a\uff0c\u8fd9\u53c8\u6709\u4ec0\u4e48\u4e0d\u5bf9\uff1f\u5e08\u53d4\u8fd9\u4e48\u82db\u6c42\u5e08\u7236\uff0c\u5c82\u975e\u592a\u8fc7\u504f\u6fc0\uff1f \u3000\u3000\u4ed6\u5fc3\u4e2d\u867d\u4f5c\u5982\u662f\u60f3\uff0c\u53e3\u4e2d\u53ef\u6ca1\u6562\u8bf4\u51fa\u6765\uff0c\u5374\u662f\u606d\u606d\u656c\u656c\u7684\u4f4e\u58f0\u5e94\u9053\uff1a\u201c\u5e08\u53d4\u8d23\u602a\u7684\u662f\u3002\u201d \u3000\u3000\u91d1\u9876\u795e\u5c3c\u53cc\u76ee\u4e4b\u4e2d\uff0c\u9690\u9690\u5730\u73b0\u51fa\u4e00\u4e1d\u6028\u610f\uff0c\u9eef\u7136\u53f9\u606f\u9053\uff1a\u201c\u4ed6\u82e5\u662f\u80fd\u591f\u7a0d\u7a0d\u4e3a\u81ea\u5df1\u7740\u60f3\uff0c\u4e5f\u5c31\u4e0d\u4f1a\u9020\u6210\u4eca\u5929\u8fd9\u7b49\u60b2\u60e8\u7684\u5c40\u9762\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fd6\u9053\uff1a\u820d\u5df1\u4e3a\u4eba\uff0c\u6b63\u662f\u4e00\u4ee3\u4ec1\u4fa0\u7684\u80f8\u895f\uff0c\u5e08\u53d4\u662f\u8fd9\u822c\u602a\u8d23\u5e08\u7236\uff0c\u90a3\u662f\u5979\u7684\u5987\u4eba\u4e4b\u89c1\u4e86\u3002 \u3000\u3000\u4e00\u5ff5\u53ca\u6b64\uff0c\u4ed6\u539f\u5df2\u6e10\u6e10\u751f\u51fa\u5bf9\u8fd9\u4f4d\u5e08\u53d9\u7684\u7591\u8651\u4e0d\u6ee1\u4e4b\u5fc3\uff0c\u5239\u90a3\u95f4\u4e5f\u4e00\u626b\u800c\u5c3d\u3002 \u3000\u3000\u4ed6\u6697\u6697\u7684\u62ac\u76ee\u5411\u5e08\u53d4\u77a7\u53bb\uff0c\u84e6\u7136\u5fc3\u5934\u4e00\u9707\uff0c\u53ea\u89c1\u91d1\u9876\u795e\u5c3c\u90a3\u5e84\u4e25\u7684\u8138\u4e0a\uff0c\u6b63\u6302\u7740\u4e24\u884c\u6cea\u73e0\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4f4e\u5934\u5bfb\u601d\u9053\uff1a\u201c\u539f\u6765\u5e08\u53d4\u53e3\u4e2d\u5bf9\u5e08\u7236\u662f\u4e0d\u6ee1\uff0c\u53ea\u4e0d\u8fc7\u662f\u6c42\u5168\u4e4b\u8d23\uff0c\u5979\u7684\u5fc3\u4e2d\uff0c\u5374\u4e5f\u4e3a\u5e08\u7236\u7684\u65e9\u6b7b\uff0c\u5927\u4e3a\u60b2\u6078\u554a\uff01\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u90a3\u96ea\u513f\u5ffd\u7136\u5931\u58f0\u9053\uff1a\u201c\u5e08\u7236\u554a\uff0c\u4f60\u8001\u600e\u5730\u54ed\u8d77\u6765\u4e86\uff1f\u201d \u3000\u3000\u957f\u957f\u4e00\u53f9\uff0c\u5408\u5341\u4f4e\u8bed\u63a5\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4e8c\u5341\u5e74\u9752\u706f\u7ea2\u78d0\u7684\u82e6\u82e6\u6e05\u4fee\uff0c\u4ecd\u7136\u96be\u4ee5\u8df3\u51fa\u4e03\u60c5\u56db\u76f8\u4e4b\u5916\uff0c\u4f5b\u7956\u6709\u77e5\uff0c\u8bf7\u6055\u5f1f\u5b50\u611a\u6627\u554a\uff01\u201d \u3000\u3000\u4e00\u9635\u4f5b\u53f7\uff0c\u7f13\u7f13\u5730\u81ea\u795e\u5c3c\u53e3\u4e2d\u4f20\u51fa\uff0c\u53ea\u542c\u5f97\u65b9\u96ea\u5b9c\u7247\u523b\u4e4b\u95f4\uff0c\u5c18\u5fc3\u5c3d\u6ee4\uff0c\u667a\u73e0\u6717\u6717\uff0c\u4e16\u4fd7\u4e4b\u60c5\uff0c\u970d\u7136\u5168\u5931\u3002 \u3000\u3000\u5c0f\u5c3c\u96ea\u513f\u4e5f\u53cc\u624b\u5408\u5341\uff0c\u8dea\u4f0f\u5728\u795e\u5c3c\u8eab\u524d\uff0c\u9762\u542b\u5fae\u7b11\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u5c11\u65f6\u95f4\uff0c\u5bfa\u4e2d\u7684\u5348\u8bfe\u949f\u58f0\uff0c\u5e7d\u5e7d\u5730\u81ea\u5c71\u8170\u4f20\u6765\uff0c\u65b9\u96ea\u5b9c\u8fd9\u624d\u8d6b\u7136\u4e00\u9707\uff0c\u4ece\u5fd8\u6211\u7684\u5929\u5730\u4e4b\u4e2d\u56de\u5230\u4e86\u773c\u524d\u7684\u4e16\u754c\u3002 \u3000\u3000\u4ed6\u62ac\u5934\u671b\u53bb\uff0c\u53ea\u89c1\u795e\u5c3c\u90a3\u4e00\u53cc\u6e05\u6f88\u7684\u773c\u795e\uff0c\u6b63\u7d27\u76ef\u7740\u81ea\u5df1\uff0c\u5f53\u4e0b\u614c\u4e71\u7684\u62b1\u62f3\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5e08\u53d9\uff0c\u5f1f\u5b50\u4e00\u65f6\u5fd8\u5f62\uff0c\u4f60\u8001\u83ab\u8981\u89c1\u602a\u3002\u201d \u3000\u3000\u91d1\u9876\u795e\u5c3c\u7b11\u9053\uff1a\u201c\u5e08\u5144\u679c\u7136\u5f88\u6709\u773c\u529b\uff0c\u5b69\u5b50\uff0c\u4f60\u5927\u6982\u997f\u4e86\u5427\uff01\u201d \u3000\u3000\u4e0d\u7b49\u65b9\u96ea\u5b9c\u8bf4\u8bdd\uff0c\u4fbf\u5411\u90a3\u96ea\u513f\u9053\uff0c\u201c\u5feb\u53bb\uff0c\u658b\u5802\u5907\u996d\uff0c\u4f60\u90a3\u65b9\u5e08\u5144\u4e00\u5b9a\u997f\u574f\u4e86\u3002\u201d \u3000\u3000\u96ea\u513f\u5a07\u58f0\u4e00\u7b11\uff0c\u626d\u5934\u8d70\u53bb\u3002 \u3000\u3000\u91d1\u9876\u795e\u5c3c\u77a7\u7740\u96ea\u513f\u7684\u80cc\u5f71\uff0c\u6447\u5934\u4e00\u53f9\u9053\uff1a\u201c\u5b7d\u969c\uff0c\u53ea\u6015\u4e3a\u5e08\u8981\u88ab\u4f60\u803d\u8bef\u5341\u5e74\u529f\u679c\u4e86\u2026\u2026\u201d\u76ee\u5149\u4e00\u8f6c\uff0c\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u5e08\u7236\u9063\u4f60\u4e0b\u5c71\u4e4b\u65f6\uff0c\u53ef\u8fd8\u6709\u4ec0\u4e48\u4ea4\u4ee3\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e08\u7236\u8981\u5f1f\u5b50\u6765\u89c1\u5e08\u53d4\uff0c\u4e43\u662f\u60f3\u8bf7\u5e08\u53d4\u6307\u70b9\u5f1f\u5b50\uff0c\u5e08\u7956\u5251\u6cd5\u4e2d\u7684\u7384\u5965\u62db\u5f0f\u3002\u201d \u3000\u3000\u795e\u5c3c\u8138\u4e0a\u4f3c\u662f\u6709\u4e9b\u5931\u671b\u7684\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u6ca1\u6709\u8bf4\u522b\u7684\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u795e\u5c3c\u95fb\u8a00\uff0c\u8138\u4e0a\u795e\u60c5\u7adf\u662f\u9634\u6697\u4e0d\u5b9a\uff0c\u4e45\u4e45\u4e0d\u51fa\u4e00\u58f0\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u9053\uff1a\u201c\u83ab\u975e\u6211\u8bf4\u9519\u4e86\u8bdd\u5417\uff1f\u5426\u5219\u5e08\u53d4\u600e\u4f1a\u4e0d\u9ad8\u5174\u5462\uff1f\u201d\u5ffd\u7136\u95f4\u76ee\u5149\u89e6\u53ca\u653e\u5728\u4e91\u5e8a\u4e4b\u4e0a\u7684\u4e66\u4fe1\uff0c\u987f\u65f6\u7075\u673a\u4e00\u52a8\uff0c\u5fd9\u9053\uff1a\u201c\u5e08\u7236\u544a\u8bc9\u5f1f\u5b50\uff0c\u5e08\u53d4\u89c1\u5230\u4e66\u4fe1\uff0c\u5c31\u660e\u767d\u4ed6\u8001\u4eba\u5bb6\u7684\u4e00\u5207\u4e86\u3002\u201d\u8bdd\u4e00\u51fa\u53e3\uff0c\u4ed6\u624d\u60f3\u8d77\uff0c\u5e08\u7236\u8fd9\u5c01\u4fe1\u4e43\u662f\u4e00\u5f20\u767d\u7eb8\uff0c\u81ea\u5df1\u8fd9\u7b49\u8bf4\u6cd5\uff0c\u5c82\u975e\u753b\u86c7\u6dfb\u8db3\uff0c\u8d8a\u63cf\u8d8a\u9ed1\u4e86\u3002 \u3000\u3000\u795e\u5c3c\u8bf4\u5979\u660e\u767d\u4e86\uff0c\u65b9\u96ea\u5b9c\u5374\u53cd\u5012\u7cca\u6d82\u4e86\uff0c\u5e08\u7236\u660e\u660e\u6ca1\u6709\u5199\u4e0a\u4e00\u53e5\u8bdd\uff0c\u5e08\u53d4\u53c8\u6253\u4ece\u4f55\u5904\u660e\u767d\u5462\uff0c\u542c\u5e08\u53d4\u90a3\u51b7\u51b0\u51b0\u7684\u53e3\u6c14\uff0c\u4f3c\u662f\u5728\u751f\u6c14\uff0c\u60f3\u5230\u81ea\u5df1\u6765\u6b64\uff0c\u4e43\u662f\u5411\u5e08\u53d4\u6c42\u6559\u5e08\u7956\u9057\u5fd7\uff0c\u90a3\u53ef\u5c31\u7f6a\u5927\u4e86\uff0c\u8d8a\u60f3\u8d8a\u89c9\u5fc3\u60ca\uff0c\u5fcd\u4e0d\u4f4f\u6697\u6697\u5411\u5e08\u53d4\u671b\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5374\u89c1\u5e08\u53d4\u8138\u4e0a\u5e76\u65e0\u4e0d\u6109\u4e4b\u8272\u3002 \u3000\u3000\u76f8\u53cd\u7684\uff0c\u5e08\u53d4\u90a3\u5e84\u4e25\u7684\u6cd5\u76f8\uff0c\u6b64\u523b\u7adf\u6362\u4e0a\u4e86\u4e00\u526f\u8109\u8109\u542b\u60c5\uff0c\u795e\u601d\u77b3\u77b3\u7684\u6df1\u601d\u4e4b\u6001\uff0c\u90a3\u660e\u4eae\u7684\u53cc\u773c\uff0c\u6b63\u671b\u7740\u5e08\u7236\u8981\u81ea\u5df1\u9001\u6765\u7684\u65e0\u5b57\u767d\u7b3a\uff0c\u4eff\u4f5b\u90a3\u767d\u7b3a\u4e4b\u4e0a\uff0c\u5df2\u7136\u91cd\u65b0\u5199\u4e0a\u4e86\u6570\u4e0d\u5c3d\u7684\u5b57\u8ff9\u822c\u5438\u5f15\u4eba\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u611f\u5947\u602a\uff0c\u4f46\u5374\u4e0d\u6562\u95ee\u3002\u751a\u81f3\u4ed6\u6b64\u523b\u8fde\u5927\u58f0\u547c\u5438\u90fd\u6709\u4e9b\u4e0d\u6562\uff0c\u6df1\u6050\u60ca\u7834\u4e86\u5e08\u53d4\u7684\u6c89\u601d\u56de\u5fc6\u3002 \u3000\u3000\u76f4\u5230\u96ea\u513f\u5192\u5931\u7684\u649e\u4e86\u8fdb\u6765\u53eb\u9053\uff1a\u201c\u5e08\u7236\u8bf7\u7528\u658b\u2026\u2026\u201d\u65b9\u96ea\u5b9c\u624d\u6df1\u6df1\u5730\u542c\u4e86\u4e00\u53e3\u5927\u6c14\u3002 \u3000\u3000\u90a3\u96ea\u513f\u672c\u662f\u6ee1\u8138\u6b22\u6109\u7684\u8dd1\u6765\uff0c\u4f46\u5979\u77a7\u89c1\u795e\u5c3c\u8138\u4e0a\u6ee1\u5e03\u7ea2\u6655\u7684\u795e\u60c5\u4ee5\u540e\uff0c\u6123\u4e86\u4e00\u6123\uff0c\u8bdd\u97f3\u5ffd\u6b62\u3002 \u3000\u3000\u5979\u4e0d\u89e3\u7684\u6389\u5934\u770b\u7740\u65b9\u96ea\u5b9c\uff0c\u6b63\u60f3\u95ee\u4ed6\uff0c\u5e08\u7236\u600e\u4f1a\u5982\u6b64\u5931\u795e\uff0c\u4e24\u4eba\u8033\u4e2d\u5df2\u4f20\u6765\u795e\u5c3c\u6df1\u6c89\u7684\u53f9\u606f\u9053\uff1a\u201c\u5e08\u5144\u554a\uff01\u8fd9\u4e0d\u662f\u6211\u7684\u9519\u2026\u2026\u201d \u3000\u3000\u795e\u5c3c\u867d\u662f\u5583\u54fa\u81ea\u8bed\uff0c\u4f46\u65b9\u96ea\u5b9c\u548c\u96ea\u513f\u5df2\u5f97\u6e05\u6e05\u695a\u695a\u3002 \u3000\u3000\u96ea\u513f\u5012\u8fd8\u4e0d\u600e\u4e48\u6837\uff0c\u65b9\u96ea\u5b9c\u5374\u88ab\u8fd9\u53e5\u770b\u6765\u4f3c\u662f\u65e0\u5173\u7d27\u8981\u7684\u53f9\u606f\uff0c\u60ca\u7684\u5fc3\u5934\u6251\u901a\u76f4\u8df3\uff0c\u4ed6\u8fc5\u5feb\u5730\u8054\u60f3\u5230\u5e08\u53d4\u90a3\u79cd\u5bf9\u5e08\u7236\u4e4b\u6b7b\uff0c\u65f6\u51b7\u65f6\u70ed\u7684\u8bf4\u8bc4\uff0c\u4f3c\u5173\u6000\u4f24\u611f\uff0c\u53c8\u4f3c\u65e0\u52a8\u4e8e\u8877\u7684\u8a00\u8bed\u4e3e\u6b62\uff0c\u5168\u662f\u56e0\u4e3a\u4ed6\u4eec\u4e4b\u95f4\uff0c\u65e9\u5df2\u5b58\u4e86\u8bef\u89e3\u548c\u6069\u6028\uff0c\u4ed6\u53ea\u6709\u5728\u5fc3\u4e2d\u6697\u6697\u7948\u6c42\uff0c\u5e08\u53d4\u5343\u4e07\u522b\u628a\u8fd9\u4efd\u6069\u6028\u7275\u8fde\u5230\u81ea\u5df1\u8eab\u4e0a\u3002 \u3000\u3000\u5426\u5219\uff0c\u5e08\u53d4\u53c8\u5c82\u80af\u5c3d\u529b\u6307\u5bfc\u81ea\u5df1\u7684\u6b66\u529f\u3002 \u3000\u3000\u4ed6\u6697\u4e2d\u7977\u5ff5\u672a\u5df2\uff0c\u795e\u5c3c\u5df2\u95ea\u8eab\u4e0b\u4e86\u4e91\u5e8a\uff0c\u6148\u7965\u5730\u671b\u7740\u65b9\u96ea\u5b9c\u4e00\u7b11\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u6709\u4ec0\u4e48\u8bdd\u7528\u8fc7\u658b\u518d\u8bf4\u5427\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u786e\u4e5f\u5f88\u997f\u4e86\uff0c\u6240\u4ee5\uff0c\u4ed6\u8fd9\u4e00\u987f\u7d20\u658b\u5403\u4e0b\u6765\uff0c\u8db3\u8db3\u591f\u91d1\u9876\u795e\u5c3c\u548c\u96ea\u513f\u4e24\u4eba\u5403\u4e0a\u4e09\u5929\u3002 \u3000\u3000\u795e\u5c3c\u4e00\u76f4\u5f88\u6148\u853c\u7684\u77a7\u7740\u4ed6\u7b11\uff0c\u96ea\u513f\u5219\u6709\u4e9b\u60ca\u5947\u5730\u77aa\u7740\u5927\u773c\uff0c\u770b\u4ed6\u90a3\u4ed8\u72fc\u541e\u864e\u54bd\u7684\u5403\u76f8\u5fc3\u4e2d\u76f4\u60f3\u7b11\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u60f3\u662f\u88ab\u5979\u4eec\u77a7\u5f97\u6709\u4e9b\u5c34\u5c2c\uff0c\u53ea\u5403\u4e86\u4e2a\u516b\u6210\u9971\uff0c\u5c31\u518d\u4e5f\u4e0d\u597d\u610f\u601d\u6dfb\u996d\u4e86\uff0c\u8baa\u8baa\u5730\u8bf4\u9053\uff1a\u201c\u5f1f\u5b50\u5403\u9971\u4e86\u3002\u201d \u3000\u3000\u795e\u5c3c\u53ea\u662f\u7b11\u4e86\u4e00\u7b11\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4ee5\u540e\u4f60\u5929\u5929\u90fd\u8981\u8fd9\u7b49\u996d\u83dc\uff0c\u5927\u53ef\u4e0d\u5fc5\u5ba2\u6c14\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u5f97\u5fc3\u4e2d\u5927\u559c\uff0c\u4e00\u76f4\u6014\u5fe1\u4e0d\u5b89\u7684\u5fc3\u60c5\uff0c\u4e5f\u6709\u4e86\u7740\u843d\uff0c\u663e\u7136\u795e\u5c3c\u8fd9\u8bdd\uff0c\u5df2\u6697\u793a\u7559\u4ed6\u5728\u6b64\uff0c\u6307\u5bfc\u4ed6\u4e60\u7ec3\u6b66\u529f\u4e86\u3002 \u3000\u3000\u56de\u5230\u7985\u623f\uff0c\u795e\u5c3c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u5e08\u7236\u90a3\u513f\u5b58\u6709\u4e00\u5e45\u5e08\u7956\u7684\u9057\u50cf\uff0c\u4e0d\u77e5\u9053\u4f60\u89c1\u5230\u6ca1\u6709\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5e94\u58f0\u9053\uff1a\u201c\u5f1f\u5b50\u5df2\u5c06\u5e08\u7956\u9057\u50cf\u5e26\u6765\u4e86\uff0c\u5f85\u5f1f\u5b50\u53bb\u53d6\u6765\u2026\u2026\u201d\u8f6c\u8eab\u8d70\u5230\u5916\u95ee\u4f5b\u5802\uff0c\u89e3\u5f00\u90a3\u653e\u5728\u89d2\u843d\u7684\u5305\u88b1\uff0c\u5c0f\u5fc3\u7684\u53d6\u51fa\u5e08\u7956\u9057\u50cf\uff0c\u5448\u9001\u5230\u795e\u5c3c\u624b\u4e2d\u3002 \u3000\u3000\u795e\u5c3c\u8138\u4e0a\u4e00\u8138\u8083\u7a46\uff0c\u8d70\u4e0b\u4e91\u5e8a\uff0c\u606d\u606d\u656c\u656c\u5730\u4ece\u65b9\u96ea\u5b9c\u624b\u4e2d\u63a5\u8fc7\u90a3\u5e45\u767d\u7ee2\uff0c\u5f53\u5373\u6302\u5728\u5899\u4e0a\u62dc\u4e86\u4e09\u62dc\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u968f\u7740\u5e08\u53d4\u62dc\u8fc7\u5e08\u7956\uff0c\u5374\u8dea\u5728\u5730\u4e0a\u6ca1\u6562\u8d77\u8eab\u3002 \u3000\u3000\u91d1\u9876\u795e\u5c3c\u770b\u5f97\u6697\u6697\u70b9\u5934\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u8d77\u6765\u8bb2\u8bdd\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u624d\u7ad9\u8d77\u8eab\u5b50\uff0c\u8083\u7acb\u4e00\u65c1\u3002 \u3000\u3000\u795e\u5c3c\u770b\u4e86\u5e08\u7236\u9057\u50cf\u4e00\u773c\uff0c\u56de\u5934\u5bf9\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u5e08\u7956\u4e43\u662f\u5343\u767e\u5e74\u6765\u96be\u5f97\u4e00\u89c1\u7684\u5947\u624d\uff0c\u80f8\u7f57\u4e4b\u5e7f\uff0c\u6b66\u529f\u4e4b\u9ad8\uff0c\u88ab\u8a89\u4e3a\u81ea\u5c11\u6797\u8fbe\u6469\u7956\u5e08\u4ee5\u6765\u7684\u7b2c\u4e00\u5947\u4eba\uff0c\u4f60\u5e08\u7236\u53ef\u66fe\u5bf9\u4f60\u8bf4\u8fc7\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e08\u7236\u786e\u4e5f\u662f\u8fd9\u4e48\u8bf4\u7684\u3002\u201d \u3000\u3000\u795e\u5c3c\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4f60\u5e08\u7236\u6bd4\u6211\u65e9\u5165\u6069\u5e08\u95e8\u4e0b\u4e00\u5e74\uff0c\u4f46\u56e0\u5949\u547d\u884c\u9053\u6c5f\u6e56\uff0c\u6240\u4ee5\u53ea\u5728\u6069\u5e08\u8eab\u8fb9\u5446\u4e86\u5341\u5e74\uff0c\u6211\u867d\u5165\u95e8\u8f83\u665a\uff0c\u5374\u4e00\u76f4\u4f8d\u5949\u6069\u5e08\u8eab\u8fb9\uff0c\u76f4\u5230\u4ed6\u8001\u4eba\u5bb6\u4ed9\u53bb\u2026\u2026\u201d\u5ffd\u7136\u4f4e\u58f0\u4e00\u53f9\u9053\uff1a\u201c\u5341\u56db\u5e74\u8033\u63d0\u9762\u547d\uff0c\u8c06\u8c06\u6559\u8bf2\u7684\u5e08\u6069\uff0c\u771f\u6bd4\u5927\u6d77\u8fd8\u6df1\uff0c\u53ef\u60dc\u672a\u5bb9\u6211\u7b49\u62a5\u7b54\uff0c\u6069\u5e08\u5c31\u5df2\u64a4\u624b\u800c\u53bb\uff0c\u4eca\u65e5\u601d\u53ca\uff0c\u771f\u662f\u7f6a\u5b7d\u6df1\u91cd\uff0c\u65e0\u4ee5\u81ea\u5b89\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u5f97\u5fc3\u4e2d\u5927\u662f\u60ca\u51db\uff0c\u5e08\u53d4\u8fd9\u8bdd\uff0c\u51e0\u4e4e\u7b49\u4e8e\u5728\u6570\u8bf4\u81ea\u5df1\uff0c\u5982\u679c\u81ea\u5df1\u4e0d\u4e89\u6c14\uff0c\u90a3\u53ef\u771f\u662f\u4e07\u6b7b\u4e5f\u96be\u4ee5\u8865\u507f\u5e08\u6069\u4e8e\u4e07\u4e00\u4e86\u3002 \u3000\u3000\u795e\u5c3c\u8fd9\u65f6\u8bed\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u53c8\u770b\u4e86\u5e08\u7236\u9057\u50cf\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u5e08\u7236\u7684\u624d\u667a\uff0c\u867d\u4e5f\u7b97\u5f97\u5343\u4e2d\u9009\u4e00\uff0c\u5f53\u4ee3\u96be\u6c42\u7684\u4eba\u6750\uff0c\u4f46\u8981\u60f3\u5c3d\u4f20\u5e08\u7956\u8863\u94b5\uff0c\u81f3\u5c11\u8fd8\u5dee\u4e86\u56db\u4e94\u6210\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u60f3\uff0c\u5e08\u7236\u679c\u7136\u4e5f\u662f\u8fd9\u4e48\u8bf4\uff0c\u5f53\u4e0b\u7b54\u9053\uff1a\u201c\u5e08\u7236\u544a\u8bc9\u5f1f\u5b50\uff0c\u4ed6\u8001\u4eba\u5bb6\u4e5f\u53ea\u5b66\u4f1a\u4e86\u5e08\u7956\u6b66\u529f\u7684\u5341\u4e4b\u4e94\u516d\u2014\u2014\u201d \u3000\u3000\u795e\u5c3c\u54fc\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u603b\u7b97\u4ed6\u867d\u7136\u5f97\u4e86\u5251\u795e\u96c5\u8a89\uff0c\u8fd8\u6ca1\u6709\u5fd8\u8bb0\u81ea\u5df1\u7a76\u7adf\u6709\u591a\u5c11\u80fd\u8010\uff0c\u5b69\u5b50\uff0c\u4e0d\u662f\u6211\u6709\u5fc3\u5728\u4f53\u9762\u524d\u8bf4\u4f60\u90a3\u5e08\u7236\u7684\u8fc7\u9519\uff0c\u4ed6\u90a3\u79cd\u8fc2\u8150\u7684\u4f5c\u6cd5\uff0c\u6211\u5c31\u4e00\u76f4\u89c9\u5f97\u4ed6\u7ec8\u5fc5\u81ea\u8499\u5176\u5bb3\u3002\u201d \u3000\u3000\u8bba\u5230\u957f\u8005\u7684\u662f\u975e\uff0c\u65b9\u96ea\u5b9c\u81ea\u662f\u4e0d\u4fbf\u63d2\u8a00\u3002 \u3000\u3000\u795e\u5c3c\u76b1\u4e86\u76b1\u7709\u5934\uff0c\u6c89\u541f\u4e86\u4e00\u4f1a\u513f\uff0c\u624d\u63a5\u9053\uff1a\u201c\u4ed6\u4ee5\u4e2d\u9a77\u4e4b\u624d\uff0c\u5904\u5904\u884c\u4e8b\uff0c\u90fd\u8981\u5b66\u7740\u4f60\u5e08\u7956\u4e00\u822c\uff0c\u5b64\u4ecb\u803f\u76f4\uff0c\u9047\u4e8b\u5b81\u6298\u4e0d\u5c48\u800c\u72ec\u884c\u5176\u662f\uff0c\u4e00\u5fc3\u60f3\u51ed\u4e2a\u4eba\u7684\u80fd\u529b\uff0c\u633d\u56de\u72c2\u6f9c\u6740\u52ab\uff0c\u4e0d\u77e5\u7eb5\u6709\u5c0f\u6210\uff0c\u5374\u96be\u5f25\u5927\u7978\uff0c\u867d\u7136\u6709\u4ed6\u4e00\u65e5\uff0c\u53ef\u4f7f\u6c5f\u6e56\u9b54\u5934\u538b\u4f0f\u4e8e\u4e00\u65f6\uff0c\u4f46\u5929\u957f\u65e5\u4e45\uff0c\u4ed6\u5c31\u53d8\u6210\u4e86\u654c\u6697\u6211\u660e\u7684\u52a3\u52bf\uff0c\u4e00\u65e6\u81ea\u5df1\u529b\u6709\u4e0d\u902e\u4e4b\u65f6\uff0c\u6c5f\u6e56\u4e0a\u5c31\u66f4\u95f9\u5f97\u4e0d\u53ef\u6536\u62fe\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e08\u53d4\u8bf4\u7684\u751a\u662f\uff0c\u5e08\u7236\u8981\u5f1f\u5b50\u91cd\u884c\u62dc\u5e08\u5927\u793c\u4e4b\u65f6\uff0c\u5c31\u66fe\u5bf9\u5f1f\u5b50\u8bf4\u8fc7\u8fd9\u4e00\u756a\u8bdd\u3002\u201d \u3000\u3000\u795e\u5c3c\u51b7\u7b11\u9053\uff1a\u201c\u4ed6\u5230\u5e95\u8fd8\u6709\u81ea\u77e5\u4e4b\u660e\u3002\u201d\u8bdd\u97f3\u4e00\u987f\uff0c\u7adf\u662f\u957f\u957f\u4e00\u53f9\u9053\uff1a\u201c\u5e08\u5144\u554a\uff0c\u4f60\u65e2\u7136\u80fd\u6709\u4eca\u65e5\u4e4b\u660e\uff0c\u4e3a\u4f55\u504f\u8981\u94f8\u4e0b\u90a3\u60dc\u65e5\u4e4b\u9519\u5462\uff1f\u201d \u3000\u3000\u7a81\u7136\u6cea\u6c34\u6c6a\u6c6a\uff0c\u6324\u6ee1\u4e86\u795e\u5c3c\u53cc\u76ee\uff0c\u65b9\u96ea\u5b9c\u8fde\u5fd9\u4f4e\u4e0b\u5934\u53bb\uff0c\u4e0d\u6562\u8ba9\u795e\u5c3c\u89c9\u51fa\u81ea\u5df1\u5728\u6697\u4e2d\u7aa5\u5979\u4f24\u5fc3\u7684\u60c5\u666f\u3002 \u3000\u3000\u9ed8\u9ed8\u826f\u4e45\uff0c\u795e\u5c3c\u624d\u8f7b\u54b3\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u5e08\u7236\u7684\u9057\u4f53\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e08\u7236\u4f3c\u662f\u65e9\u77e5\u81ea\u5df1\u75c5\u5165\u818f\u80b2\uff0c\u5728\u90a3\u909b\u5d03\u5c71\u7684\u8305\u820d\u4e4b\u4e2d\uff0c\u5148\u884c\u51c6\u5907\u4e86\u4e00\u5ea7\u51b0\u7a96\uff0c\u5f1f\u5b50\u5df2\u9075\u5e08\u7236\u9057\u547d\uff0c\u4ed6\u8001\u4eba\u5bb6\u7684\u6cd5\u4f53\uff0c\u653e\u5728\u51b0\u7a96\u4e4b\u4e2d\uff0c\u6570\u5341\u5e74\u4e2d\uff0c\u5927\u7ea6\u4e0d\u4f1a\u635f\u574f\u3002\u201d \u3000\u3000\u795e\u5c3c\u70b9\u5934\u9053\uff1a\u201c\u5f88\u597d\uff0c\u5b69\u5b50\uff0c\u4f60\u505a\u5f97\u5f88\u597d\u3002\u201d\u65b9\u96ea\u5b9c\u6697\u9053\uff1a\u8fd9\u53ea\u662f\u5e08\u7236\u7684\u9057\u6108\uff0c\u5e08\u53d4\u4e3a\u4f55\u7adf\u7136\u8981\u5938\u8d5e\u6211\uff1f \u3000\u3000\u4ed6\u6b63\u5728\u4e0d\u89e3\uff0c\u795e\u5c3c\u5df2\u7136\u95ee\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u5e08\u7236\u8eab\u540e\u8fd8\u7559\u4e0b\u4ec0\u4e48\u9057\u7269\u6ca1\u6709\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u60f3\u4e86\u4e00\u60f3\uff0c\u6447\u5934\u9053\uff1a\u201c\u90a3\u8305\u820d\u4e4b\u4e2d\uff0c\u6beb\u65e0\u8d35\u91cd\u7269\u54c1\uff0c\u5e08\u7236\u6240\u7528\u7684\u4f69\u5251\uff0c\u5df2\u7136\u4ea4\u7ed9\u5f1f\u5b50\u5e26\u6765\u4e86\u3002\u201d \u3000\u3000\u795e\u5c3c\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u4f60\u518d\u60f3\u60f3\u770b\uff0c\u662f\u4e0d\u662f\u8fd8\u6709\u4ec0\u4e48\u4f60\u6ca1\u6ce8\u610f\u7684\u4e8b\u7269\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5f53\u771f\u7528\u5fc3\u5730\u60f3\u4e86\u534a\u664c\uff0c\u7b11\u9053\uff1a\u201c\u5e08\u7236\u517b\u6709\u4e00\u53cc\u9e66\u9e49\u548c\u4e00\u53ea\u5de8\u733f\uff0c\u4f46\u5f1f\u5b50\u9664\u4e86\u5728\u4e0a\u5c71\u4e4b\u65f6\uff0c\u8499\u8fd9\u4e00\u79bd\u4e00\u733f\u63a5\u5f15\uff0c\u89c1\u8fc7\u4e00\u6b21\u4e4b\u5916\uff0c\u6b64\u540e\u4e09\u5e74\u96f6\u516d\u4e2a\u6708\uff0c\u90fd\u6ca1\u6709\u89c1\u8fc7\u5b83\u4eec\uff0c\u4e0d\u77e5\u8fd9\u7b97\u4e0d\u7b97\u662f\u5e08\u4ece\u7684\u9057\u7269\uff1f\u201d \u3000\u3000\u795e\u5c3c\u7b11\u9053\uff1a\u201c\u81ea\u662f\u7b97\u5f97\u2026\u2026\u4e0d\u8fc7\u2026\u2026\u6211\u6015\u4f60\u8fd8\u5fd8\u4e86\u4e9b\u4ec0\u4e48\u6ca1\u6709\u60f3\u5f97\u8d77\u6765\u5427\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6b64\u523b\u4e1a\u5df2\u89c9\u51fa\uff0c\u5e08\u53d4\u5bf9\u5e08\u7236\u8eab\u540e\u7559\u4e0b\u4e4b\u7269\u751a\u662f\u6ce8\u610f\uff0c\u77e5\u9053\u5fc5\u6709\u539f\u56e0\uff0c\u4f46\u5e08\u53d4\u4e0d\u8bf4\u51fa\u6765\uff0c\u4ed6\u5f53\u7136\u4e0d\u4fbf\u8fc7\u95ee\uff0c\u53ea\u662f\u606d\u656c\u7684\u5e94\u9053\uff1a\u201c\u5f1f\u5b50\u521d\u7ecf\u5927\u53d8\uff0c\u5bb9\u6216\u6709\u4e9b\u8bb0\u5fc6\u4e0d\u6e05\uff0c\u5e08\u53d4\u5982\u662f\u6709\u6687\uff0c\u5f1f\u5b50\u81ea\u5f53\u4e3a\u5e08\u53d4\u5e26\u8def\uff0c\u5230\u5e08\u7236\u9690\u5c45\u4e4b\u5904\u67e5\u770b\u4e00\u4e0b\u2026\u2026\u201d \u3000\u3000\u795e\u5c3c\u7b11\u9053\uff1a\u201c\u522b\u90a3\u4e48\u5fd9\uff0c\u6211\u8fdf\u65e9\u4e00\u5b9a\u8981\u53bb\u4e00\u8d9f\u7684\uff0c\u800c\u4e14\u2026\u2026\u201d\u4f3c\u662f\u60f3\u8d77\u4ec0\u4e48\u5927\u4e8b\uff0c\u8bdd\u97f3\u4e8c\u987f\uff0c\u76ee\u5149\u7535\u5c04\u65b9\u96ea\u5b9c\u8138\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u8bf4\u4f60\u5e08\u7236\u662f\u75c5\u6b7b\u7684\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e08\u7236\u4e43\u662f\u75c5\u6b7b\u7684\u3002\u201d \u3000\u3000\u795e\u5c3c\u9053\uff1a\u201c\u800c\u4e14\u75c5\u4e86\u5f88\u4e45\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e08\u7236\u81ea\u5df1\u8bf4\u5df2\u6709\u4e8c\u5341\u5e74\u4e4b\u4e45\u3002\u201d \u3000\u3000\u795e\u5c3c\u6447\u5934\u9053\uff1a\u201c\u5947\u602a\u554a\uff01\u8fd9\u600e\u4e48\u53ef\u80fd\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u5f1f\u5b50\u8fd9\u4e09\u5e74\u591a\u6765\uff0c\u786e\u662f\u5929\u592d\u77a7\u5230\u5e08\u7236\u5403\u836f\u2026\u2026\u201d \u3000\u3000\u795e\u5c3c\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u5e08\u7236\u7cbe\u901a\u6b67\u9ec4\uff0c\u6d3b\u4eba\u65e0\u6570\uff0c\u4ed6\u600e\u4f1a\u6cbb\u4e0d\u4e86\u81ea\u5df1\u7684\u75c5\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\u4e00\u6014\uff0c\u9053\uff1a\u201c\u662f\u554a\uff0c\u8fd9\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u795e\u5c3c\u76ee\u5149\u4e00\u8f6c\uff0c\u559d\u9053\uff1a\u201c\u4f60\u5e08\u7236\u544a\u8bc9\u4f60\uff0c\u4ed6\u5f97\u7684\u662f\u4ec0\u4e48\u75c5\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u5934\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u795e\u5c3c\u9053\uff1a\u201c\u4ed6\u662f\u600e\u4e48\u8bf4\u7684\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e08\u7236\u6bcf\u6b21\u63d0\u5230\u6b64\u4e8b\uff0c\u53ea\u662f\u8bf4\u8fd9\u4e43\u662f\u4e00\u79cd\u4e0d\u6cbb\u4e4b\u75be\uff0c\u4ece\u6765\u4e0d\u66fe\u591a\u8c08\u3002\u201d \u3000\u3000\u795e\u5c3c\u6c89\u541f\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u5e08\u7236\u75c5\u52bf\u53d1\u4f5c\u4e4b\u65f6\uff0c\u662f\u4f55\u7b49\u75c7\u72b6\uff0c\u4f60\u89c1\u8fc7\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f1f\u5b50\u89c1\u8fc7\u3002\u201d\u4ed6\u60f3\u4e86\u4e00\u60f3\uff0c\u63a5\u9053\uff1a\u201c\u5982\u4e0d\u662f\u5e08\u7236\u75c5\u52bf\u7a81\u53d1\uff0c\u6655\u5012\u5728\u5730\u4e0a\u88ab\u6211\u90a3\u5927\u5a18\u53d1\u73b0\uff0c\u903c\u6211\u5927\u4f2f\u6089\u5fc3\u533b\u6cbb\uff0c\u6551\u9192\u4e86\u5e08\u7236\uff0c\u5f1f\u5b50\u53ea\u6015\u4e5f\u4e0d\u53ef\u80fd\u6709\u4eca\u65e5\u8fd9\u7b49\u9020\u5316\u3002\u201d \u3000\u3000\u795e\u5c3c\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u4f60\u5e08\u7236\u75c5\u53d1\u4f5c\u4e4b\u65f6\uff0c\u60c5\u5f62\u662f\u5f88\u4e25\u91cd\u7684\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u786e\u662f\u5341\u5206\u5371\u9669\u3002\u201d \u3000\u3000\u795e\u5c3c\u5ffd\u7136\u4ef0\u5934\u9ed8\u7136\u4e0d\u8bed\uff0c\u8138\u4e0a\u7684\u795e\u8272\u51b0\u51b7\uff0c\u9887\u4f3c\u6b63\u5728\u601d\u7d22\u7740\u4ec0\u4e48\u7591\u96be\u4e4b\u4e8b\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u9759\u9759\u5730\u5782\u624b\u8083\u7acb\u4e8c\u65c1\uff0c\u4e0d\u4f46\u4e0d\u6562\u8bf4\u8bdd\uff0c\u8fde\u547c\u5438\u90fd\u4e0d\u6562\u592a\u91cd\uff0c\u751f\u6015\u6253\u6270\u4e86\u795e\u5c3c\u5e08\u53d4\u7684\u601d\u7eea\u3002 \u3000\u3000\u7ea6\u83ab\u8fc7\u4e86\u76cf\u8336\u4e4b\u4e45\uff0c\u795e\u5c3c\u624d\u957f\u957f\u4e00\u53f9\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u5e08\u7236\u7684\u6b7b\u56e0\uff0c\u6211\u5df2\u60f3\u5230\u4e00\u70b9\u5934\u7eea\uff0c\u53ea\u662f\u2026\u2026\u201d\u795e\u5c3c\u8bdd\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u7adf\u7136\u53e3\u4e2d\u54fa\u5583\u5982\u540c\u81ea\u8bed\u822c\u63a5\u9053\uff1a\u201c\u5e08\u5144\u554a\uff01\u4f60\u8fd9\u4e48\u591a\u53ef\u80fd\u81f4\u6b7b\u4e4b\u56e0\uff0c\u53eb\u6211\u600e\u80fd\u786e\u5b9a\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u7684\u5fc3\u5934\u72c2\u9707\uff0c\u6697\u9053\uff1a\u5e08\u7236\u96be\u9053\u4e0d\u662f\u56e0\u75c5\u81f4\u547d\u6b7b\u7684\u5417\uff1f\u4ed6\u5fc3\u4e2d\u7591\u5ff5\u867d\u8d77\uff0c\u4f46\u5374\u4e0d\u6562\u968f\u4fbf\u7b54\u8bdd\u3002 \u3000\u3000\u91d1\u9876\u795e\u5c3c\u8bed\u97f3\u4f4e\u6c89\u5730\u559f\u53f9\u826f\u4e45\uff0c\u7a81\u7136\u76ee\u5149\u4e00\u8f6c\uff0c\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u5e08\u7236\u7684\u6b7b\u56e0\uff0c\u5982\u7167\u8d2b\u5c3c\u63a8\u65ad\uff0c\u81f3\u5c11\u6709\u4e94\u79cd\u4ee5\u4e0a\u53ef\u80fd\uff0c\u8d2b\u5c3c\u672a\u66fe\u5bdf\u770b\u4ed6\u7684\u9057\u4f53\u4e4b\u524d\uff0c\u5b9e\u662f\u96be\u4e0b\u5b9a\u8bba\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u606d\u58f0\u5e94\u9053\uff1a\u201c\u5e08\u53d4\u662f\u5426\u6253\u7b97\u53bb\u4e00\u8d9f\u909b\u5d03\u5c71\uff1f\u201d \u3000\u3000\u91d1\u9876\u795e\u5c3c\u9053\uff1a\u201c\u8981\u60f3\u67e5\u51fa\u4f60\u5e08\u7236\u81f4\u6b7b\u4e4b\u7531\uff0c\u8d2b\u5c3c\u81ea\u662f\u8981\u53bb\u4e00\u8d9f\u7684\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u559c\u9053\uff1a\u201c\u5e08\u53d4\u51e0\u65f6\u52a8\u8eab\uff1f\u201d \u3000\u3000\u795e\u5c3c\u6447\u5934\u9053\uff1a\u201c\u4e0d\u5fd9\uff0c\u7b49\u4f60\u628a\u5e08\u7956\u7559\u4e0b\u6765\u7684\u5251\u9053\u4e60\u5c31\uff0c\u8d2b\u5c3c\u518d\u53bb\u4e0b\u8fdf\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6709\u4e9b\u5931\u671b\u5730\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u5e08\u7236\u5982\u662f\u88ab\u4eba\u6240\u5bb3\uff0c\u8eab\u4e3a\u5f1f\u5b50\u800c\u5c1a\u4e0d\u5f97\u77e5\uff0c\u5c82\u975e\u662f\u6709\u5982\u79bd\u517d\u4e00\u822c\u5417\uff1f\u201d \u3000\u3000\u795e\u5c3c\u5fae\u4e00\u6c89\u541f\uff0c\u9053\uff1a\u201c\u5ff5\u5728\u4f60\u8fd9\u4e00\u7247\u5b5d\u5fc3\uff0c\u8d2b\u5c3c\u5e94\u8be5\u6210\u5168\u4e8e\u4f60\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u957f\u957f\u4e00\u63d6\u9053\uff1a\u201c\u591a\u8c22\u5e08\u53d4\u6069\u5178\u3002\u201d \u3000\u3000\u795e\u5c3c\u6447\u5934\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u4e0d\u5fc5\u6fc0\u6211\u4e86\uff0c\u4f60\u5e08\u7236\u4e43\u662f\u8d2b\u5c3c\u5e08\u5144\uff0c\u4ed6\u5982\u771f\u662f\u88ab\u4eba\u6240\u5bb3\uff0c\u8d2b\u5c3c\u81ea\u4e5f\u4e0d\u80fd\u8896\u624b\u4e0d\u95ee\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u9661\u7136\u95f4\u8138\u4e0a\u73b0\u51fa\u4e00\u6d3e\u538c\u6076\u4e4b\u8272\uff0c\u8fde\u8fde\u6447\u5934\u63a5\u9053\uff1a\u201c\u8d2b\u5c3c\u5728\u4f5b\u7956\u9762\u524d\u7acb\u4e0b\u5b8f\u8a93\uff0c\u6b64\u751f\u6b64\u4e16\uff0c\u4e0d\u518d\u52a8\u624b\u8bba\u6b66\uff0c\u5e08\u5144\u4e4b\u6068\uff0c\u53ea\u6015\u8d2b\u5c3c\u4f9d\u7136\u662f\u5fc3\u4f59\u529b\u62d9\uff0c\u96be\u4ee5\u4eb2\u81ea\u51fa\u9762\u53c2\u4e0e\u7684\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u624d\u6709\u4e9b\u660e\u767d\uff0c\u4e3a\u4f55\u4e00\u65e6\u63d0\u53ca\u6b66\u529f\uff0c\u5e08\u53d4\u7684\u8138\u4e0a\u5c31\u4f1a\u51fa\u73b0\u538c\u6076\u795e\u60c5\u7684\u539f\u56e0\uff0c\u6562\u60c5\u8fd9\u4f4d\u5bc4\u8ff9\u7a7a\u95e8\u7684\u957f\u8f88\uff0c\u66fe\u5728\u4f5b\u7956\u4e4b\u524d\uff0c\u53d1\u4e0b\u4e86\u5b8f\u8a93\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u81ea\u662f\u5927\u5927\u4e0d\u4ee5\u4e3a\u7136\uff0c\u89c9\u5f97\u4f5b\u7956\u4e5f\u66fe\u6709\u8fc7\u964d\u9b54\u672f\u9053\uff0c\u4ee5\u6740\u6b62\u6740\u4e4b\u4e3e\uff0c\u5e08\u53d4\u53c8\u4f55\u5fc5\u5982\u6b64\u8fc2\u9614\u820d\u672c\u9010\u672a\u5462\uff1f \u3000\u3000\u4ed6\u5fc3\u4e2d\u867d\u7136\u8fd9\u4e48\u60f3\uff0c\u4f46\u53e3\u4e2d\u53ef\u6ca1\u6562\u8bf4\u5c06\u51fa\u6765\u3002 \u3000\u3000\u795e\u5c3c\u77a7\u7740\u4ed6\u8138\u4e0a\u7684\u795e\u8272\uff0c\u4f3c\u662f\u5df2\u7136\u660e\u767d\u4e86\u65b9\u96ea\u5b9c\u7684\u5fc3\u610f\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u5408\u5341\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u53ef\u662f\u4e0d\u5927\u540c\u610f\u8d2b\u5c3c\u7684\u8fd9\u8a93\u4e0d\u518d\u8c08\u6b66\u529f\u7684\u4e3e\u63aa\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f1f\u5b50\u4e0d\u6562\uff01\u201d \u3000\u3000\u795e\u5c3c\u6447\u5934\u4e00\u53f9\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u5929\u4e0b\u6709\u5f88\u591a\u4e8b\u4e0d\u662f\u4f60\u8fd9\u7b49\u5e74\u7eaa\u6240\u80fd\u987f\u609f\uff0c\u4e8c\u5341\u5e74\u4e4b\u540e\uff0c\u4e5f\u8bb8\u4f60\u5c31\u80fd\u61c2\u5f97\u8d2b\u5c3c\u7684\u7528\u5fc3\u4e86\u3002\u201d \u3000\u3000\u8bdd\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u4f60\u4e5f\u4e0d\u5fc5\u6101\u6025\uff0c\u8d2b\u5c3c\u5df2\u7136\u51b3\u5b9a\uff0c\u8fd1\u65e5\u5373\u5c06\u524d\u53bb\u909b\u5d03\u4e00\u884c\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8131\u53e3\u559c\u9053\uff1a\u201c\u771f\u7684\uff01\u201d \u3000\u3000\u795e\u5c3c\u5e7d\u5e7d\u5730\u8981\u7b11\u4e0d\u7b11\u9053\uff1a\u201c\u8d2b\u5c3c\u5411\u6765\u8a00\u51fa\u5fc5\u884c\uff0c\u5b69\u5b50\u4f60\u8be5\u76f8\u4fe1\u624d\u662f\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f1f\u5b50\u600e\u6562\u4e0d\u4fe1\u5e08\u53d4\u4e4b\u8a00\uff0c\u53ea\u662f\u5f1f\u5b50\u89c9\u5f97\u6709\u4e9b\u60ca\u559c\u8fc7\u751a\u2026\u2026\u201d \u3000\u3000\u795e\u5c3c\u9053\uff1a\u201c\u5e08\u6069\u53ef\u6bd4\u7236\u6069\uff0c\u4f60\u4e00\u65f6\u60ca\u559c\u8fc7\u751a\uff0c\u8fd9\u4e5f\u4e0d\u80fd\u8d23\u602a\u4e8e\u4f60\uff0c\u8fde\u65e5\u5954\u6ce2\uff0c\u4f60\u4e00\u5b9a\u5f88\u7d2f\u4e86\uff0c\u5b69\u5b50\uff0c\u5148\u53bb\u4f11\u606f\u534a\u5929\uff0c\u660e\u513f\u8d77\u4f60\u5c31\u5f97\u6709\u4e00\u9635\u5b50\u8f9b\u82e6\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f1f\u5b50\u81ea\u7ecf\u5e08\u7236\u5728\u90a3\u909b\u5d03\u7edd\u9876\u7763\u4fc3\u4e60\u7ec3\u5e08\u7956\u7684\u5410\u7eb3\u7ec3\u6c14\u4ee5\u6765\uff0c\u867d\u662f\u4e00\u884c\u5343\u91cc\uff0c\u4e5f\u4e0d\u4f1a\u89c9\u51fa\u52b3\u7d2f\uff0c\u5e08\u53d9\u4e0d\u7528\u4e3a\u5f1f\u5b50\u803d\u5fc3\u4e86\u3002\u201d \u3000\u3000\u795e\u5c3c\u7b11\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u8fd8\u662f\u4f11\u606f\u4e00\u5bbf\u7684\u597d\uff0c\u4f60\u5e08\u7236\u6240\u4f20\u6388\u7684\u6b66\u529f\u867d\u7136\u5df2\u975e\u4e00\u822c\u7684\u6b66\u5b66\uff0c\u4f46\u4ecd\u975e\u5e08\u7956\u5251\u672f\u7684\u5fc3\u8840\u673a\u81f4\uff0c\u56e0\u6b64\u4f60\u867d\u662f\u5728\u909b\u5d03\u4e60\u827a\u5e76\u4e0d\u5341\u5206\u8f9b\u82e6\uff0c\u4f46\u6253\u660e\u513f\u5f00\u59cb\uff0c\u6240\u4e60\u7684\u4e00\u70b9\u4e00\u6ef4\uff0c\u65e0\u4e0d\u662f\u6b66\u529f\u4e4b\u4e2d\u7684\u7cbe\u534e\uff0c\u7eb5\u7136\u662f\u624d\u667a\u6781\u9ad8\u4e4b\u58eb\uff0c\u4e5f\u5f88\u96be\u5728\u6781\u77ed\u65f6\u95f4\u5185\uff0c\u5f97\u5176\u795e\u9ad3\uff0c\u8981\u662f\u4f60\u7cbe\u795e\u4e0d\u591f\uff0c\u522b\u8bf4\u8981\u4e8b\u500d\u529f\u534a\uff0c\u5984\u8d39\u4e86\u5fc3\u8840\uff0c\u66f4\u574f\u7684\u7ed3\u679c\uff0c\u4e43\u662f\u6015\u4f60\u529b\u4e0d\u4ece\u5fc3\uff0c\u5c94\u6c14\u4f24\u8109\uff0c\u90a3\u5c31\u4e0d\u5982\u4e0d\u7ec3\u4e86\u3002\u201d \u3000\u3000\u795e\u5c3c\u8fd9\u51e0\u53e5\u8bdd\u8bf4\u5f97\u5341\u5206\u6e29\u548c\uff0c\u4f46\u542c\u5728\u65b9\u96ea\u5b9c\u8033\u4e2d\uff0c\u5374\u4f7f\u4ed6\u5927\u4e3a\u60ca\u51db\uff0c\u5f53\u4e0b\u8fde\u5fd9\u5e94\u58f0\u9053\uff1a\u201c\u5e08\u53d4\u5982\u6b64\u5173\u6000\uff0c\u5f1f\u5b50\u6562\u4e0d\u9075\u547d\uff01\u201d \u3000\u3000\u8f6c\u8eab\u5c31\u5f85\u9000\u51fa\u795e\u5c3c\u7684\u7985\u623f\u3002 \u3000\u3000\u795e\u5c3c\u7b11\u9053\uff1a\u201c\u5b69\u5b50\u4f60\u4e14\u7b49\u7b49\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u5e08\u53d4\u8fd8\u6709\u8bad\u793a\u5417\uff1f\u201d \u3000\u3000\u795e\u5c3c\u9053\uff1a\u201c\u8d2b\u5c3c\u8fd9\u5367\u4e91\u5eb5\u53ea\u6709\u4e09\u95f4\u6b63\u5c4b\uff0c\u4f60\u6765\u6b64\u4e60\u827a\uff0c\u5e76\u4e0d\u662f\u4e00\u5929\u534a\u65e5\u4e4b\u671f\uff0c\u90a3\u658b\u5802\u548c\u81b3\u623f\u53c8\u975e\u53ef\u4f4f\u4e4b\u5904\uff0c\u5982\u679c\u4f60\u4e0d\u5acc\u8d2b\u5c3c\u5eb5\u4e2d\u7b80\u964b\uff0c\u4f60\u5c31\u5728\u8fd9\u4e2d\u95f4\u7684\u4f5b\u5802\u89d2\u843d\uff0c\u5377\u4e0a\u4e00\u4e2a\u4e34\u65f6\u7684\u5367\u606f\u4e4b\u5904\u5427\uff01\u5982\u679c\u7f3a\u5c11\u4ec0\u4e48\u9700\u7528\u4e4b\u7269\uff0c\u53ef\u5411\u4f60\u90a3\u96ea\u6d9b\u5e08\u59b9\u7d22\u53d6\u4fbf\u662f\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u542c\uff0c\u539f\u6765\u5e08\u53d4\u4e43\u662f\u4e3a\u4e86\u81ea\u5df1\u8fd9\u5bbf\u4f4f\u4e4b\u5904\u8d39\u795e\uff0c\u5fc3\u4e2d\u987f\u611f\u4e0d\u5b89\uff0c\u5fd9\u9053\uff1a\u201c\u5f1f\u5b50\u968f\u4fbf\u54ea\u513f\u90fd\u53ef\u5b89\u6b47\uff0c\u5e08\u53d4\u4e0d\u5fc5\u8d39\u795e\u4e86\u3002\u201d \u3000\u3000\u4fef\u8eab\u957f\u957f\u4e00\u62dc\uff0c\u4e3e\u6b65\u9000\u51fa\u7985\u623f\u3002 \u3000\u3000\u4ed6\u521a\u521a\u8f6c\u8fc7\u8eab\u6765\uff0c\u7adf\u7136\u53d1\u73b0\u5728\u90a3\u4f5b\u5802\u9760\u5916\u7684\u5c0f\u5c0f\u56f4\u5899\u4e0b\uff0c\u5df2\u7136\u642d\u8d77\u4e86\u4e00\u5f20\u5c0f\u5c0f\u7684\u5e8a\u94fa\uff0c\u90a3\u96ea\u6d9b\u5e08\u59b9\u6b63\u6253\u5f00\u4e86\u81ea\u5df1\u7684\u5305\u88b1\uff0c\u5728\u66ff\u81ea\u5df1\u62fe\u6387\u7740\u8863\u7269\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u867d\u7136\u5e74\u7eaa\u4e0d\u5927\uff0c\u4f46\u5374\u5f88\u61c2\u4e8b\uff0c\u96ea\u6d9b\u5c0f\u5c3c\u59d1\u8fd9\u7b49\u66ff\u4ed6\u6574\u7406\u8863\u886b\u7b49\u7269\uff0c\u7adf\u662f\u4ee4\u4ed6\u5fc3\u4e2d\u5927\u4e3a\u9707\u52a8\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u8fd9\u624d\u6162\u6162\u5730\u8d70\u4e86\u8fc7\u53bb\uff0c\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6709\u52b3\u5e08\u59b9\u2026\u2026\u201d \u3000\u3000\u96ea\u6d9b\u6839\u672c\u6ca1\u6599\u5230\u65b9\u96ea\u5b9c\u4f1a\u8fd9\u4e48\u5feb\u5c31\u51fa\u6765\u4e86\uff0c\u95fb\u8a00\u4f3c\u662f\u5403\u4e86\u4e00\u60ca\uff0c\u90a3\u6e05\u79c0\u7684\u8138\u4e0a\uff0c\u987f\u65f6\u98de\u4e0a\u4e00\u5c42\u7ea2\u4e91\uff0c\u4f4f\u624b\u4f4e\u5934\u9053\uff1a\u201c\u5e08\u5144\u4e43\u662f\u5ba2\u4eba\u561b\uff0c\u5e08\u7236\u5462\uff1f\u5165\u5b9a\u4e86\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e08\u53d4\u8981\u6211\u5148\u884c\u8c03\u606f\uff0c\u660e\u5929\u597d\u7ec3\u5251\u3002\u201d \u3000\u3000\u96ea\u6d9b\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5e08\u5144\uff0c\u4f60\u5c06\u5c31\u7740\u8c03\u606f\u5427\uff0c\u6211\u4e5f\u8981\u505a\u5348\u8bfe\u53bb\u4e86\u3002\u201d\u8bdd\u97f3\u4e00\u843d\uff0c\u98d8\u8eab\u95ea\u5165\u5de6\u4fa7\u7684\u90a3\u95f4\u7985\u623f\u800c\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u867d\u662f\u5e94\u4e86\u4e00\u58f0\u201c\u591a\u8c22\u5e08\u59b9\uff01\u201d\u4f46\u96ea\u6d9b\u4f3c\u662f\u6ca1\u6709\u542c\u89c1\u4e00\u822c\uff0c\u8fdb\u4e86\u90a3\u95f4\u7985\u623f\uff0c\u5c31\u518d\u4e5f\u4e0d\u95fb\u58f0\u606f\uff0c \u3000\u3000\u65b9\u96ea\u5b9c\u7565\u7565\u6536\u62fe\u4e86\u4e00\u4e0b\u81ea\u5df2\u7684\u8863\u7269\uff0c\u4fbf\u5750\u5728\u90a3\u6728\u677f\u642d\u6210\u7684\u77ee\u94fa\u4e4b\u4e0a\uff0c\u505a\u8d77\u90a3\u8c03\u606f\u5410\u7eb3\u529f\u592b\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u4fbf\u5df2\u8fdb\u5165\u4eba\u6211\u4e24\u5fd8\u4e4b\u5883\u3002 \u3000\u3000\u5f85\u4ed6\u884c\u529f\u9192\u6765\uff0c\u5df2\u662f\u9ec4\u660f\u65f6\u5206\uff0c\u4e3e\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u4f5b\u5802\u4e4b\u4e2d\uff0c\u706f\u5149\u5df2\u660e\uff0c\u795e\u5c3c\u5e08\u53d4\u6ee1\u9762\u542b\u7b11\u5730\u7ad9\u5728\u8eab\u524d\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5403\u4e86\u4e00\u60ca\uff0c\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u5f1f\u5b50\u5931\u793c\u4e86\u3002\u201d \u3000\u3000\u795e\u5c3c\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u7684\u5185\u529f\uff0c\u6839\u57fa\u679c\u7136\u624e\u7684\u5f88\u6df1\u539a\uff0c\u8d2b\u5c3c\u5148\u524d\u5012\u662f\u803d\u5fc3\u7684\u591a\u4f59\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e08\u7236\u7ec8\u65e5\u7763\u4fc3\u4e4b\u4e0b\uff0c\u5f1f\u5b50\u81ea\u662f\u4e0d\u6562\u5077\u61d2\uff0c\u4f46\u5f1f\u5b50\u8d44\u8d28\u9c81\u949d\uff0c\u5e08\u53d4\u5982\u6b64\u8d5e\u8a89\u5012\u53eb\u5f1f\u5b50\u5fc3\u4e2d\u597d\u751f\u96be\u8fc7\u2026\u2026\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u60f3\u8d77\u5e08\u7236\u7684\u8c03\u6559\u4e4b\u6069\uff0c\u4e0d\u89c9\u5730\u9ed8\u7136\u6cea\u4e0b\u3002 \u3000\u3000\u795e\u5c3c\u7b11\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u83ab\u4f24\u5fc3\u4e86\uff0c\u53bb\u7528\u658b\u5427\uff0c\u5982\u662f\u665a\u95f4\u6ca1\u6709\u4ec0\u4e48\u91cd\u8981\u4e4b\u4e8b\uff0c\u4f60\u4e0d\u59a8\u65e9\u4e9b\u5b89\u6b47\uff0c\u660e\u65e5\u4e94\u66f4\uff0c\u6211\u518d\u6765\u53eb\u4f60\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f1f\u5b50\u9075\u547d\u3002\u201d\u5f53\u5373\u53bb\u81f3\u658b\u5802\u7528\u996d\uff0c\u90a3\u96ea\u6d9b\u5e08\u59b9\uff0c\u65e9\u5df2\u5728\u658b\u5802\u76f8\u5019\uff0c\u89c1\u4ed6\u8d70\u4e86\u8fdb\u6765\uff0c\u4fbf\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u65b9\u5e08\u5144\uff0c\u5feb\u4e9b\u5403\u5427\uff01\u8fd9\u53ef\u662f\u5e08\u7236\u81ea\u5df1\u4e3a\u4f60\u8c03\u5236\u7684\u5462\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u6014\u9053\uff1a\u201c\u8fd9\u2026\u2026\u600e\u4e48\u53ef\u4ee5\u5462\uff1f\u201d \u3000\u3000\u96ea\u6d9b\u7b11\u9053\uff1a\u201c\u65b9\u5e08\u5144\uff0c\u5e08\u7236\u8bf4\u8fd9\u662f\u4e3a\u4f60\u660e\u5929\u5c31\u8981\u5f00\u59cb\u4e60\u7ec3\u90a3\u4e0a\u4e58\u5251\u9053\uff0c\u7279\u522b\u4e3a\u4f60\u8c03\u5236\u4e86\u4e00\u7897\u96ea\u83b2\u7fb9\uff0c\u8fd9\u662f\u5979\u8001\u4eba\u5bb6\u7684\u4e00\u7247\u7231\u62a4\u4e4b\u610f\uff0c\u4f60\u53ef\u83ab\u8981\u8f9c\u8d1f\u4e86\uff0c\u5feb\u5403\u5427\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u51dd\u76ee\u671b\u53bb\uff0c\u679c\u7136\u90a3\u6728\u684c\u4e4b\u4e0a\uff0c\u9664\u4e86\u996d\u83dc\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u4e2a\u76d6\u7897\u653e\u5728\u65c1\u8fb9\uff0c\u60f3\u5fc5\u90a3\u76d6\u7897\u4e4b\u5185\uff0c\u5c31\u662f\u90a3\u96ea\u83b2\u7fb9\u4e86\uff0c\u4ed6\u9053\u4e86\u4e00\u58f0\u8c22\uff0c\u8fd9\u624d\u8fc7\u53bb\u5750\u4e0b\u7528\u81b3\u3002 \u3000\u3000\u5f53\u591c\u65e0\u8bdd\uff0c\u6b21\u65e5\u5929\u8272\u6709\u660e\uff0c\u795e\u5c3c\u5c31\u53eb\u9192\u4e86\u65b9\u96ea\u5b9c\uff0c\u53bb\u81f3\u5eb5\u524d\u7684\u77f3\u576a\u4e4b\u4e0a\uff0c\u8981\u4ed6\u628a\u6240\u6709\u7684\u5251\u638c\u5de5\u592b\uff0c\u4e00\u4e00\u5730\u6f14\u4e86\u4e00\u904d\u7ed9\u5979\u770b\u3002 \u3000\u3000\u795e\u5c3c\u77a7\u7684\u5fae\u7b11\u4e0d\u8bed\uff0c\u76f4\u5230\u65b9\u96ea\u5b9c\u5c06\u90a3\u5957\u638c\u6cd5\u548c\u5251\u6cd5\u5df1\u5747\u4f7f\u5b8c\uff0c\u5411\u795e\u5c3c\u8dea\u62dc\u8bf7\u6559\uff0c\u5979\u8fd9\u624d\u6447\u5934\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u5982\u662f\u4f60\u53ea\u60f3\u5728\u6b66\u6797\u4e4b\u4e2d\u626c\u540d\u7acb\u4e07\uff0c\u4ee5\u4f60\u773c\u4e0b\u7684\u6b66\u529f\uff0c\u5012\u4e5f\u53ef\u4ee5\u5217\u5165\u90a3\u7b2c\u4e00\u6d41\u9ad8\u624b\u4e4b\u5185\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f1f\u5b50\u867d\u6709\u826f\u5e08\uff0c\u4f46\u81ea\u77e5\u624d\u667a\u4e0d\u582a\uff0c\u96be\u6709\u5927\u6210\uff0c\u5c1a\u6c42\u5e08\u53d4\u8bad\u8bf2\u624d\u662f\u3002\u201d \u3000\u3000\u795e\u5c3c\u7b11\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u8fd9\u4e0d\u662f\u4f60\u4e2a\u4eba\u7684\u624d\u667a\u95ee\u9898\uff0c\u4f60\u5e08\u7236\u80fd\u591f\u628a\u4f60\u6559\u5230\u8fd9\u7b49\u7a0b\u5ea6\uff0c\u5b9e\u5728\u5df2\u5c3d\u4e86\u4ed6\u6240\u80fd\uff0c\u5982\u662f\u6211\u731c\u60f3\u7684\u4e0d\u9519\uff0c\u4f60\u5e08\u7236\u4e00\u5b9a\u7528\u8fc7\u5f88\u591a\u5e08\u7956\u7559\u4e0b\u7684\u4ed9\u65b9\uff0c\u66ff\u4f60\u589e\u957f\u4e86\u5185\u529f\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e08\u7236\u786e\u662f\u4e3a\u5f1f\u5b50\u8c03\u5236\u4e86\u8bb8\u591a\u836f\u7269\uff0c\u52a9\u6211\u589e\u957f\u4e86\u5185\u529b\u3002\u201d \u3000\u3000\u795e\u5c3c\u8bdd\u97f3\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u8fd9\u5957\u5251\u6cd5\uff0c\u4f60\u5e08\u7236\u53ef\u66fe\u544a\u8bc9\u8fc7\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\u6ca1\u6709\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u5934\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u795e\u5c3c\u7b11\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u53ef\u77e5\u9053\u4f60\u5e08\u7236\u4e3a\u4ec0\u4e48\u6559\u4f60\u7ec3\u5251\u800c\u672a\u66fe\u544a\u77e5\u4f60\u7684\u5251\u540d\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u5f1f\u5b50\u5b9e\u5728\u4e0d\u660e\u767d\u3002\u201d \u3000\u3000\u795e\u5c3c\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u5e08\u7956\u5f53\u5e74\u521b\u7814\u8fd9\u5957\u5251\u6cd5\u4e4b\u65f6\uff0c\u672c\u662f\u4e00\u5341\u4e8c\u62db\uff0c\u5b9a\u540d\u4e3a\u9f99\u884c\u5341\u4e8c\u5251\uff0c\u56e0\u4e3a\u8fd9\u5957\u5251\u6cd5\u4e00\u65e6\u65bd\u5c55\uff0c\u76f4\u5982\u9f99\u8dc3\u5929\u91ce\uff0c\u77eb\u9038\u98de\u817e\uff0c\u6545\u800c\u53d6\u4e86\u9f99\u884c\u4e24\u5b57\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\u3002\u201d \u3000\u3000\u795e\u5c3c\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u4f60\u5e08\u7956\u5728\u884c\u9053\u6c5f\u6e56\u6570\u5e74\u4ee5\u540e\uff0c\u7ec8\u4e8e\u53c8\u53d1\u73b0\u4e86\u8fd9\u5341\u4e8c\u62db\u5251\u6cd5\uff0c\u867d\u7136\u5df2\u53ef\u7b97\u5f97\u96c6\u5251\u9053\u4e4b\u5927\u6210\uff0c\u4f46\u4ecd\u7136\u6709\u4e0d\u5c11\u53ef\u5f85\u6539\u8fdb\u4e4b\u5904\uff0c\u8d39\u4e86\u4e94\u5e74\u5fc3\u8840\uff0c\u53bb\u829c\u8350\u83c1\uff0c\u5c06\u5341\u4e8c\u62db\u5251\u6cd5\u91cd\u65b0\u7ec4\u5408\u6d53\u7f29\u6210\u4e86\u516b\u62db\uff0c\u53eb\u9f99\u884c\u516b\u5251\uff0c\u5a01\u529b\u8f83\u524d\u589e\u52a0\u4e86\u4e0d\u4e0b\u4e00\u500d\u4e4b\u591a\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e08\u7956\u4e00\u4ee3\u5947\u4eba\uff0c\u81ea\u662f\u6709\u7740\u4eba\u6240\u96be\u53ca\u7684\u80fd\u8010\u3002\u201d \u3000\u3000\u795e\u5c3c\u70b9\u5934\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u5e08\u7956\u679c\u7136\u662f\u4e00\u4ee3\u4eba\u6770\uff0c\u9648\u5e08\u5144\u968f\u5e08\u7956\u4e60\u827a\u4e0b\u5c71\u4ee5\u540e\uff0c\u5e08\u7956\u53c8\u5c06\u9f99\u884c\u516b\u5251\u7cbe\u7814\u6539\u8fdb\uff0c\u589e\u6dfb\u4e86\u4e00\u62db\u6bd4\u90a3\u201c\u4e07\u6d41\u5f52\u5b97\u201d\u66f4\u5177\u6709\u5a01\u529b\u7684\u62db\u5f0f\uff0c\u4f60\u5e08\u7236\u8981\u4f60\u524d\u6765\u5367\u4e91\u5eb5\u89c1\u6211\uff0c\u4e3b\u8981\u7684\u5c31\u662f\u8981\u4f60\u524d\u6765\u5b66\u4f1a\u5e08\u7956\u6240\u7559\u4e0b\u7684\u6700\u540e\u8fd9\u62db\u5251\u6cd5\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u5c31\u5f1f\u5b50\u6240\u77e5\uff0c\u90a3\u4e00\u62db\u2018\u4e07\u6d41\u5f52\u5b97\u2019\u7684\u4e94\u4e2a\u53d8\u5316\uff0c\u5b9e\u662f\u7cbe\u5965\u65e0\u6bd4\uff0c\u5f1f\u5b50\u4ec5\u4ec5\u8fd9\u4e00\u62db\uff0c\u5c31\u8db3\u8db3\u7684\u4f7f\u4e86\u4e03\u4e2a\u591a\u6708\u624d\u80fd\u5b8c\u5168\u968f\u5fc3\u8fd0\u7528\uff0c\u4f46\u4e0d\u77e5\u5e08\u7956\u8fd9\u6700\u540e\u7814\u521b\u7684\u4e00\u62db\uff0c\u4f1a\u4e0d\u4f1a\u6bd4\u2018\u4e07\u6d41\u5f52\u5b97\u2019\u66f4\u4e3a\u96be\u8bb0\u5462\uff1f\u201d \u3000\u3000\u795e\u5c3c\u7b11\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u89c1\u5f97\uff0c\u5b69\u5b50\uff0c\u4f60\u53ef\u662f\u5f88\u6025\u7740\u60f3\u628a\u8fd9\u62db\u5251\u6cd5\u7ec3\u719f\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7684\u5fc3\u4e8b\u88ab\u5e08\u53d4\u770b\u7a7f\uff0c\u4e0d\u7981\u8138\u4e0a\u8baa\u8baa\u5730\u9053\uff1a\u201c\u5f1f\u5b50\u53ea\u662f\u6709\u4e9b\u803d\u5fc3\u3002\u201d \u3000\u3000\u795e\u5c3c\u6de1\u6de1\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u522b\u8bf4\u4e86\uff0c\u5176\u5b9e\uff0c\u5e08\u7956\u8fd9\u65b0\u521b\u7684\u4e00\u62db\uff0c\u5e76\u4e0d\u6bd4\u2018\u4e07\u6d41\u5f52\u5b97\u2019\u96be\u8bb0\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6709\u4e9b\u96be\u4ee5\u76f8\u4fe1\u7684\u7b11\u9053\uff1a\u201c\u5e08\u53d9\uff0c\u8fd9\u662f\u771f\u7684\u5417\uff1f\u201d \u3000\u3000\u795e\u5c3c\u9053\uff1a\u201c\u6211\u600e\u4f1a\u54c4\u4f60\u5462\uff1f\u5b69\u5b50\uff0c\u6211\u53ef\u4ee5\u544a\u8bc9\u4f60\uff0c\u534a\u5e74\u4e4b\u5185\uff0c\u4f60\u81ea\u7136\u80fd\u591f\u628a\u5e08\u7956\u7684\u5251\u9053\u5b8c\u5168\u9886\u4f1a\u8d2f\u901a\uff0c\u4e0d\u8fc7\u2026\u2026\u201d\u7a81\u7136\u4f4f\u53e3\uff0c\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u4e0d\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u795e\u5c3c\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u957f\u53f9\u9053\uff1a\u201c\u4f60\u5982\u679c\u60f3\u771f\u80fd\u5b8c\u6210\u5e08\u7956\u7684\u9057\u5fd7\uff0c\u6050\u6015\u8fd8\u8981\u770b\u4f60\u4eca\u540e\u7684\u9020\u5316\u54e9\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u5f97\u9887\u4e3a\u60f6\u7136\uff0c\u90a3\u9020\u5316\u4e24\u5b57\uff0c\u4e0d\u557b\u5728\u4ed6\u5fc3\u4e2d\u6253\u4e86\u4e2a\u4e0d\u5927\u4e0d\u5c0f\u7684\u7ed3\uff0c\u4ed6\u660e\u767d\u5e08\u53d4\u8a00\u4e0b\u4e4b\u610f\uff0c\u4eff\u4f5b\u81ea\u5df2\u4ec5\u51ed\u4e2a\u4eba\u4e4b\u529b\uff0c\u51b3\u7136\u65e0\u6cd5\u4f7f\u6b66\u6797\u4e4b\u4e2d\u957f\u89c1\u592a\u5e73\u76db\u5174\uff0c\u8fd9\u4e0e\u5e08\u7236\u6388\u81ea\u5df1\u6b66\u529f\u4e4b\u524d\u6240\u8bf4\uff0c\u6b63\u662f\u4e0d\u8c0b\u800c\u5408\uff0c\u5f53\u4e0b\u8083\u5bb9\u5411\u795e\u5c3c\u4e00\u62dc\uff0c\u9053\uff1a\u201c\u5e08\u53d4\u653e\u5fc3\uff0c\u5f1f\u5b50\u7eb5\u7136\u7c89\u8eab\u788e\u9aa8\uff0c\u4e5f\u8981\u4e0d\u4f7f\u5e08\u7956\u548c\u5e08\u7236\u5728\u5929\u4e4b\u7075\u5931\u671b\u554a\uff01\u201d \u3000\u3000\u795e\u5c3c\u7b11\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u5e08\u53d4\u5938\u8d5e\u4e8e\u4f60\uff0c\u5b69\u5b50\uff0c\u7b49\u4f60\u628a\u5e08\u7956\u540e\u6765\u6240\u6dfb\u521b\u7684\u4e00\u62db\u5251\u6cd5\u5b66\u4f1a\uff0c\u4f60\u786e\u662f\u8981\u6bd4\u4f60\u90a3\u6b7b\u53bb\u7684\u5e08\u7236\u8981\u5f3a\u8fc7\u4e0d\u5c11\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u606d\u656c\u5730\u5e94\u9053\uff1a\u201c\u5f1f\u5b50\u771f\u6709\u6b64\u65e5\uff0c\u90a3\u5168\u662f\u5e08\u53d4\u6240\u8d50\u3002\u201d \u3000\u3000\u795e\u5c3c\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u8c22\u6211\uff0c\u8bf4\u7a7f\u4e86\u6211\u53ea\u662f\u4ee3\u4f60\u5e08\u7956\u4f20\u827a\u800c\u5df2\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u4f46\u5e08\u53d4\u5374\u4e0d\u80fd\u4e0d\u5bf9\u4f60\u8bf4\u660e\uff0c\u9f99\u884c\u4e5d\u5251\uff0c\u5e76\u4e0d\u7b97\u5f97\u6b66\u6797\u4e2d\u7684\u552f\u4e00\u7edd\u5b66\uff0c\u5f3a\u8fc7\u9f99\u884c\u4e5d\u5251\u7684\u6b66\u529f\uff0c\u5e76\u4e0d\u662f\u6ca1\u6709\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u6b66\u6797\u4e4b\u4e2d\u8fd8\u6709\u4eba\u80fd\u591f\u5f3a\u5f97\u8fc7\u5e08\u7956\u5417\uff1f\u201d \u3000\u3000\u795e\u5c3c\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u90a3\u5e08\u7956\u65e2\u662f\u6b66\u6797\u4e2d\u5343\u767e\u5e74\u96be\u5f97\u4e00\u89c1\u7684\u624d\u4eba\uff0c\u81ea\u7136\u662f\u4e0d\u4f1a\u6709\u4eba\u5f3a\u5f97\u8fc7\u4ed6\u7684\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u65e2\u662f\u65e0\u4eba\u5f3a\u8fc7\u5e08\u7956\uff0c\u5e08\u53d4\u53c8\u600e\u7684\u8bf4\u5e08\u7956\u7684\u6b66\u529f\uff0c\u7b97\u4e0d\u5f97\u552f\u4e00\u7edd\u5b66\u5462\uff1f\u201d \u3000\u3000\u795e\u5c3c\u7b11\u9053\uff1a\u201c\u6b66\u6797\u867d\u7136\u65e0\u4eba\u5f3a\u8fc7\u5e08\u7956\uff0c\u5374\u5b9e\u5b9e\u5728\u5728\u7684\u6709\u4eba\u5f3a\u8fc7\u4f60\u5e08\u7236\u548c\u4f60\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5374\u662f\u6709\u4e9b\u4e0d\u89e3\u7684\u95ee\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u8fd9\u662f\u4ec0\u4e48\u9053\u7406\u5462\uff1f\u201d \u3000\u3000\u795e\u5c3c\u9053\uff1a\u201c\u5e08\u53d4\u5982\u4e0d\u8bf4\u51fa\uff0c\u8c05\u4f60\u6c38\u8fdc\u4e5f\u731c\u4e0d\u8feb\u5176\u4e2d\u9053\u7406\u3002\u201d \u3000\u3000\u5ffd\u7136\u957f\u957f\u7684\u5401\u4e86\u53e3\u6c14\uff0c\u63a5\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u5929\u751f\u4e07\u7269\uff0c\u5747\u6709\u76f8\u751f\u76f8\u514b\u4e4b\u80fd\uff0c\u8fd9\u6b66\u529f\u4e0a\u7684\u6210\u5c31\uff0c\u4e5f\u4e0d\u4f1a\u8131\u51fa\u8fd9\u4e00\u7269\u514b\u5236\u4e00\u7269\u7684\u5de2\u81fc\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5ffd\u7136\u81ea\u4f5c\u806a\u660e\u5730\u7b11\u9053\uff1a\u201c\u5f1f\u5b50\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u795e\u5c3c\u7b11\u9053\uff1a\u201c\u4f60\u660e\u767d\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u53ef\u662f\u6b66\u6797\u4e2d\u6709\u4e00\u79cd\u6b66\u529f\uff0c\u6b63\u597d\u662f\u9f99\u884c\u4e5d\u5251\u7684\u514b\u661f\u5417\uff1f\u201d \u3000\u3000\u795e\u5c3c\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u5f88\u806a\u660e\uff0c\u679c\u7136\u662f\u88ab\u4f60\u731c\u5bf9\u4e86\u5341\u4e4b\u516d\u516b\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6014\u4e86\u6014\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u600e\u4f1a\u53ea\u662f\u5341\u4e4b\u516d\u516b\u5462\uff1f\u65e2\u662f\u514b\u661f\uff0c\u90a3\u5c31\u8be5\u5168\u5bf9\u624d\u662f\u5440\uff01\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u867d\u662f\u8fd9\u4e48\u60f3\uff0c\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u5e08\u53d9\uff0c\u8c01\u6709\u6b64\u80fd\u8010\uff0c\u521b\u51fa\u514b\u5236\u5e08\u7956\u5251\u6cd5\u7684\u6b66\u529f\u5462\uff1f\u201d \u3000\u3000\u795e\u5c3c\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u8fd9\u8bdd\u8bf4\u8d77\u6765\u53ef\u5c31\u957f\u4e86\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u65f6\u9664\u4e86\u5fc3\u4e2d\u5403\u60ca\uff0c\u53ef\u66f4\u662f\u5341\u5206\u597d\u5947\uff0c\u8131\u53e3\u63d2\u8bdd\u8bf4\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u65e2\u662f\u5f1f\u5b50\u6240\u4e60\u7684\u6b66\u529f\u6709\u4e86\u514b\u661f\uff0c\u5982\u662f\u5f1f\u5b50\u4f9d\u65e7\u61f5\u7136\u65e0\u77e5\uff0c\u5c06\u6765\u5c82\u4e0d\u662f\u8981\u5403\u4eba\u5bb6\u7684\u4e8f\u5417\uff1f\u201d \u3000\u3000\u4ed6\u660e\u660e\u60f3\u8bf7\u5e08\u53d4\u628a\u90a3\u80fd\u514b\u5236\u201c\u9f99\u884c\u4e5d\u5251\u201d\u7684\u6b66\u529f\uff0c\u662f\u4ec0\u4e48\u4eba\u6240\u7834\u7684\u6545\u4e8b\u8bf4\u51fa\uff0c\u5374\u662f\u4e0d\u6562\u660e\u91cc\u50ac\u8bf7\uff0c\u6545\u610f\u515c\u5708\u5b50\u8bf4\u81ea\u5df1\u5c06\u6765\u53ef\u80fd\u8981\u5403\u4eba\u5bb6\u7684\u4e8f\uff0c\u800c\u8ba9\u5e08\u53d4\u4e0d\u80fd\u4e0d\u628a\u5176\u4e2d\u7684\u8be6\u60c5\u8bf4\u660e\uff0c\u5012\u771f\u662f\u5f88\u6709\u5fc3\u673a\u3002 \u3000\u3000\u795e\u5c3c\u542c\u5f97\u5fae\u6559\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u8ddf\u5e08\u53d4\u4e5f\u7528\u4e0a\u4e86\u5fc3\u673a\u4e86\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8138\u4e0a\u4e00\u7ea2\uff0c\u5fd9\u9053\uff1a\u201c\u5f1f\u5b50\u4e0d\u6562\u3002\u201d \u3000\u3000\u795e\u5c3c\u7b11\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u4e0d\u8981\u7d27\u5f20\uff0c\u4e00\u4e2a\u4eba\u5728\u5fc5\u8981\u65f6\u7528\u4e00\u70b9\u5fc3\u673a\uff0c\u90a3\u4e5f\u4e0d\u662f\u574f\u4e8b\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4f4e\u5934\u9053\uff1a\u201c\u8bf7\u5e08\u53d4\u6055\u5f1f\u5b50\u4e0d\u656c\u4e4b\u7f6a\u3002\u201d \u3000\u3000\u795e\u5c3c\u9053\uff1a\u201c\u5e08\u53d4\u5e76\u6ca1\u602a\u4f60\u554a\uff0c\u5b69\u5b50\uff0c\u8fd9\u4e8b\u8bf4\u6765\u786e\u662f\u8bdd\u957f\uff0c\u4f46\u773c\u4e0b\u5982\u4e0d\u544a\u8bc9\u4f60\uff0c\u53ea\u6015\u4f60\u4e5f\u4e13\u4e0d\u4e0b\u5fc3\u6765\u4e60\u7ec3\u5251\u6cd5\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5931\u7b11\u9053\uff1a\u201c\u5e08\u53d4\u53ef\u771f\u8bf4\u5230\u5f1f\u5b50\u5fc3\u4e2d\u4e86\u3002\u201d \u3000\u3000\u795e\u5c3c\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u5176\u4e2d\u8be6\u5c3d\u7684\u7ec6\u8282\uff0c\u5e08\u53d4\u65e0\u6cd5\u4e00\u4e00\u8bf4\u51fa\uff0c\u53ea\u80fd\u5220\u7e41\u4ece\u7b80\uff0c\u957f\u8bdd\u77ed\u53d9\u4e86\u3002\u201d \u3000\u3000\u8bdd\u97f3\u7565\u7565\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u5e08\u7236\u53ef\u66fe\u544a\u8bc9\u8fc7\u4f60\u5e08\u7956\u7684\u540d\u53f7\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u795e\u5c3c\u7a81\u7136\u6c89\u8138\u9053\uff1a\u201c\u4f60\u5e08\u7236\u8fd9\u53ef\u5c31\u4e0d\u5bf9\u4e86\uff0c\u4ed6\u600e\u53ef\u5982\u6b64\u8352\u5510\uff0c\u8fde\u5e08\u7956\u7684\u540d\u53f7\u90fd\u4e0d\u5bf9\u4f60\u8bf4\u660e\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fae\u5fae\u4e00\u60ca\uff0c\u5fd9\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\uff0c\u4e5f\u8bb8\u5e08\u7236\u65e9\u6709\u5b89\u6392\uff0c\u8981\u7b49\u5e08\u53d4\u544a\u77e5\u5f1f\u5b50\u3002\u201d \u3000\u3000\u795e\u5c3c\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u662f\u5728\u4e3a\u4ed6\u8bb2\u60c5\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5ffd\u7136\u8dea\u62dc\u5728\u5730\u4e0a\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5f1f\u5b50\u53ea\u671b\u5e08\u53d4\u83ab\u8981\u518d\u8d23\u602a\u5e08\u7236\u5c31\u662f\u3002\u201d \u3000\u3000\u795e\u5c3c\u77a7\u7740\u65b9\u96ea\u5b9c\u60f6\u6025\u7684\u795e\u8272\uff0c\u7ec8\u4e8e\u957f\u53f9\u9053\uff1a\u201c\u4f60\u8d77\u6765\uff0c\u5e08\u53d4\u4e0d\u518d\u602a\u4f60\u5e08\u7236\u5c31\u662f\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u53c8\u62dc\u4e86\u4e00\u62dc\uff0c\u9053\uff1a\u201c\u591a\u8c22\u5e08\u53d4\u2026\u2026\u201d\u7ad9\u8d77\u8eab\u5b50\uff0c\u5728\u65c1\u7adf\u662f\u4e0d\u6562\u4ef0\u89c6\u3002 \u3000\u3000\u795e\u5c3c\u5fcd\u4e0d\u4f4f\u7b11\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u53ef\u662f\u5f88\u6015\u5e08\u53d4\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u679c\u771f\u662f\u6709\u4e9b\u6015\u5979\uff0c\u4f46\u53e3\u4e2d\u5374\u4e0d\u80fd\u4e0d\u5e94\u9053\uff1a\u201c\u5e08\u53d4\u4e43\u662f\u957f\u8f88\uff0c\u5f1f\u5b50\u53ea\u6709\u606d\u656c\u4ef0\u6155\u4e4b\u5fc3\uff0c\u600e\u4f1a\u6709\u90a3\u5bb3\u6015\u4e4b\u610f\u5462\uff1f\u201d \u3000\u3000\u795e\u5c3c\u7b11\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u5e08\u53d4\u5927\u6982\u771f\u662f\u5413\u7740\u4f60\u4e86\u2026\u2026\u201d \u3000\u3000\u5fae\u5fae\u7684\u53f9\u606f\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u5e08\u7956\u5728\u6b66\u6797\u4e4b\u4e2d\uff0c\u88ab\u4eba\u5c0a\u4e3a\u5251\u5723\uff0c\u4ed6\u8001\u4eba\u5bb6\u7684\u540d\u53f7\u662f\u9f99\u8d85\u7fa4\uff0c\u4f60\u53ef\u8981\u7d27\u7d27\u8bb0\u7262\u624d\u662f\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f1f\u5b50\u8bb0\u4e0b\u4e86\u3002\u201d \u3000\u3000\u795e\u5c3c\u76ee\u5149\u4e00\u8f6c\uff0c\u9053\uff1a\u201c\u4e03\u5341\u5e74\u524d\uff0c\u5e08\u7956\u4ee5\u7fbd\u51a0\u4e4b\u5e74\uff0c\u521d\u5165\u6c5f\u6e56\uff0c\u56e0\u4e3a\u5e08\u7956\u4e0d\u4f46\u6b66\u529f\u8d85\u51e1\u5165\u5723\uff0c\u4e13\u7ba1\u4e0d\u5e73\u4e4b\u4e8b\uff0c\u800c\u4e14\u4ece\u6765\u4e0d\u66fe\u4f24\u4e00\u4e2a\u597d\u4eba\uff0c\u662f\u4ee5\u4e00\u5e74\u4e0d\u5230\uff0c\u5c31\u5df2\u58f0\u8a89\u9e4a\u8d77\uff0c\u88ab\u9ed1\u767d\u4e24\u9053\u7684\u4eba\u7269\uff0c\u89c6\u4f5c\u4e07\u5bb6\u751f\u4f5b\u4e00\u822c\uff0c\u6070\u5728\u6b64\u65f6\uff0c\u4e2d\u539f\u9053\u4e0a\uff0c\u5ffd\u7136\u51fa\u73b0\u4e86\u4e24\u4f4d\u6b66\u529f\u9ad8\u5f97\u51fa\u5947\uff0c\u5374\u53c8\u5fc3\u72e0\u624b\u8fa3\u7684\u8499\u9762\u602a\u4fa0\u3002\u4ed6\u4eec\u4e3a\u4e86\u8981\u5728\u4e2d\u539f\u6b66\u6797\u4e0a\u7acb\u5a01\uff0c\u7adf\u7136\u884c\u4e8b\u5927\u5927\u4e0d\u62e9\u624b\u6bb5\uff0c\u4e0d\u4f46\u5728\u4e00\u6708\u4e4b\u5185\uff0c\u8fde\u6311\u6c5f\u5357\u6c5f\u5317\u5341\u4e03\u5904\u7eff\u6797\u5802\u5be8\uff0c\u66f4\u5728\u534a\u5e74\u4e4b\u5185\uff0c\u628a\u6b66\u6797\u516d\u5927\u95e8\u6d3e\u7684\u638c\u95e8\u4eba\u5168\u90e8\u63b3\u8d70\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u5f97\u5fc3\u4e2d\u5927\u9707\uff0c\u8131\u53e3\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u8fd9\u4e24\u4eba\u662f\u4ec0\u4e48\u6765\u8def\uff0c\u7adf\u6562\u5c06\u516d\u5927\u95e8\u6d3e\u638c\u95e8\u63b3\u8d70\uff1f\u201d \u3000\u3000\u795e\u5c3c\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u6765\u81ea\u4e1c\u6d77\uff0c\u8fd9\u4e24\u4eba\u6b66\u529f\uff0c\u786e\u5b9e\u4e0d\u51e1\uff0c\u516d\u5927\u95e8\u6d3e\u638c\u95e8\u88ab\u4fd8\uff0c\u81ea\u662f\u6fc0\u8d77\u95e8\u4e0b\u5b50\u5f1f\u7684\u607c\u6012\uff0c\u9042\u7531\u5c11\u6797\u5bfa\u4e2d\u7684\u4e24\u4f4d\u957f\u8001\u51fa\u9762\uff0c\u8054\u5408\u4e86\u516d\u95e8\u6d3e\u7684\u5341\u5927\u9ad8\u624b\uff0c\u5728\u5929\u53f0\u5c71\u7684\u4e00\u5904\u6df1\u8c37\u4e4b\u4e2d\uff0c\u7ea6\u6597\u8fd9\u4e24\u4f4d\u8499\u9762\u602a\u4fa0\uff0c\u8d28\u95ee\u8fd9\u4e24\u4eba\uff0c\u63b3\u8d70\u5404\u6d3e\u638c\u95e8\uff0c\u7528\u5fc3\u4f55\u5728\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u77e5\u90a3\u5929\u53f0\u6df1\u8c37\u4e4b\u6218\uff0c\u516d\u5927\u95e8\u6d3e\u5341\u5927\u9ad8\u624b\u662f\u80dc\u662f\u8d25\uff1f\u201d \u3000\u3000\u795e\u5c3c\u7b11\u9053\uff1a\u201c\u90a3\u5341\u5927\u9ad8\u624b\u81ea\u7136\u662f\u8d25\u4e86\uff0c\u5426\u5219\uff0c\u4e5f\u4e0d\u5fc5\u52b3\u52a8\u5e08\u7956\u9010\u9000\u4e24\u4eba\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u604d\u7136\u9053\uff1a\u201c\u539f\u6765\u662f\u5e08\u7956\u628a\u4ed6\u4eec\u64b5\u8d70\u7684\u5417\uff1f\u201d \u3000\u3000\u795e\u5c3c\u9053\uff1a\u201c\u5929\u53f0\u6df1\u8c37\u4e4b\u6218\uff0c\u516d\u5927\u95e8\u6d3e\u7684\u5341\u5927\u9ad8\u624b\uff0c\u7adf\u7136\u65e0\u4e00\u751f\u8fd8\uff0c\u7ecf\u6b64\u4e00\u5f79\uff0c\u4e2d\u539f\u6b66\u6797\u5df2\u5168\u90e8\u6ca6\u5165\u8fd9\u4e24\u4eba\u7684\u638c\u63e1\u4e4b\u4e2d\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u5e08\u7956\u4e3a\u4f55\u4e0d\u5728\u90a3\u5341\u5927\u9ad8\u624b\u4e27\u751f\u4e4b\u524d\uff0c\u5c06\u90a3\u5bf9\u602a\u4eba\u64b5\u8d70\u5462\uff1f\u201d \u3000\u3000\u795e\u5c3c\u7b11\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u95ee\u5f97\u597d\uff0c\u8db3\u89c1\u4f60\u5206\u6790\u4e8b\u7406\uff0c\u5341\u5206\u6709\u5c42\u6b21\uff0c\u6bd4\u4f60\u5e08\u7236\u5f3a\u5f97\u591a\u4e86\u3002\u201d\u8bdd\u97f3\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u8fd9\u4e24\u4eba\u8fdb\u5165\u4e2d\u539f\u80e1\u95f9\u4e4b\u65f6\uff0c\u4f60\u5e08\u7956\u6b63\u5de7\u5e94\u7ea6\u5230\u897f\u85cf\uff0c\u4e0e\u5bc6\u5b97\u638c\u6559\u7684\u5587\u561b\u8f83\u91cf\u795e\u529f\u53bb\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u539f\u6765\u5e08\u7956\u4e0d\u5728\u4e2d\u539f\uff1f\u8fd9\u5c31\u96be\u602a\u4e86\u3002\u201d \u3000\u3000\u795e\u5c3c\u63a5\u9053\uff1a\u201c\u7b49\u5230\u5e08\u7956\u6253\u897f\u85cf\u8fd4\u56de\u4e2d\u539f\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u5f1f\u5b50\u6253\u5c94\u597d\u5417\uff1f\u201d \u3000\u3000\u795e\u5c3c\u7b11\u9053\uff1a\u201c\u4f60\u60f3\u8d77\u4ec0\u4e48\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f1f\u5b50\u542c\u8bf4\u5bc6\u5b97\u6b66\u5b66\u5341\u5206\u602a\u5f02\uff0c\u8f83\u4e4b\u4e2d\u571f\u8981\u5f3a\u8fc7\u4e0d\u5c11\uff0c\u4e0d\u77e5\u5e08\u7956\u53bb\u8ddf\u90a3\u5bc6\u5b97\u638c\u6559\u5587\u561b\u8f83\u91cf\u795e\u529f\u7684\u7ed3\u679c\uff0c\u7a76\u7adf\u662f\u8c01\u5360\u4e86\u80dc\u673a\u3002\u201d \u3000\u3000\u795e\u5c3c\u7b11\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u5176\u5b9e\u4f60\u4e0d\u5fc5\u95ee\u5c31\u8be5\u77e5\u9053\u7684\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u662f\u5e08\u7956\u8d62\u4e86\uff1f\u201d \u3000\u3000\u795e\u5c3c\u9053\uff1a\u201c\u5e08\u7956\u5982\u662f\u843d\u8d25\uff0c\u53ea\u6015\u4e5f\u5c31\u65e0\u6cd5\u518d\u56de\u4e2d\u571f\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u662f\u554a\uff01\u5f1f\u5b50\u4e5f\u66fe\u8fd9\u4e48\u60f3\uff0c\u53ea\u662f\u4e0d\u4e5f\u6709\u4e03\u5206\u628a\u63e1\u3002\u201d \u3000\u3000\u795e\u5c3c\u7b11\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u90a3\u662f\u4f60\u5148\u5165\u4e3a\u4e3b\u7684\u770b\u6cd5\u5728\u4f5c\u602a\uff0c\u5b9e\u5219\u90a3\u5bc6\u5b97\u6b66\u5b66\uff0c\u6bd4\u4e4b\u5e08\u7956\u7684\u6b66\u529f\uff0c\u90a3\u8fd8\u5dee\u5f97\u5f88\u591a\u3002\u201d \u3000\u3000\u795e\u5c3c\u63a5\u9053\uff1a\u201c\u4f60\u5e08\u7956\u6253\u897f\u85cf\u91cd\u8fd4\u4e2d\u571f\u4e4b\u540e\uff0c\u8fd9\u624d\u53d1\u73b0\u4e2d\u539f\u6b66\u6797\u4e1a\u5df2\u6ca6\u5165\u4e86\u9b54\u638c\u4e4b\u4e2d\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e08\u7956\u53ef\u662f\u7acb\u5373\u5c31\u53bb\u627e\u5230\u90a3\u4e24\u4e2a\u8499\u9762\u4e4b\u4eba\uff0c\u5c06\u4ed6\u4eec\u9010\u56de\u4e86\u4e1c\u6d77\uff1f\u201d \u3000\u3000\u795e\u5c3c\u6447\u5934\u9053\uff1a\u201c\u5e08\u7956\u884c\u4e8b\uff0c\u5411\u6765\u8c28\u614e\uff0c\u4ed6\u5982\u4e0d\u662f\u4eb2\u81ea\u89c1\u5230\u5bf9\u65b9\u4e3a\u6076\u4e4b\u4e8b\uff0c\u51b3\u4e0d\u80af\u8f7b\u6613\u4f24\u5bb3\u4e00\u4f4d\u597d\u4eba\u7684\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e08\u7236\u4e5f\u8fd9\u4e48\u5bf9\u5f1f\u5b50\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u795e\u5c3c\u7b11\u9053\uff1a\u201c\u4f60\u5e08\u7956\u5728\u89c9\u51fa\u6709\u5f02\u4e4b\u540e\uff0c\u4f7f\u5148\u81ea\u6697\u4e2d\u67e5\u63a2\uff0c\u53d1\u73b0\u516d\u5927\u95e8\u6d3e\u7684\u65b0\u4efb\u638c\u95e8\u4eba\uff0c\u679c\u771f\u4e0d\u80fd\u9075\u5b88\u4fa0\u4e49\u884c\u9053\u7684\u7cbe\u795e\uff0c\u4e00\u6d3e\u80e1\u4f5c\u975e\u4e3a\uff0c\u4f7f\u6b66\u6797\u540c\u9053\uff0c\u4eba\u4eba\u9677\u4e8e\u81ea\u5371\u4e4b\u5883\uff0c\u5e08\u7956\u4e00\u6012\u4e4b\u4e0b\uff0c\u4fbf\u5148\u5c06\u8fd9\u516d\u4f4d\u6df7\u5e10\u638c\u95e8\uff0c\u5168\u6570\u902e\u4f4f\uff0c\u8fd9\u4e48\u4e00\u6765\uff0c\u90a3\u4e24\u4f4d\u8499\u9762\u602a\u4fa0\u5c31\u81ea\u52a8\u7684\u627e\u4e0a\u4f60\u5e08\u7956\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e08\u7956\u624d\u667a\uff0c\u679c\u7136\u4eba\u6240\u96be\u53ca\uff0c\u8fd9\u7b49\u4ee5\u9038\u5f85\u52b3\uff0c\u4e14\u53c8\u65a9\u9664\u4e86\u5bf9\u65b9\u7fbd\u7ffc\u7684\u4f5c\u6cd5\uff0c\u786e\u662f\u4ee4\u4eba\u94a6\u4f69\u65e0\u5df2\u3002\u201d \u3000\u3000\u795e\u5c3c\u7b11\u9053\uff1a\u201c\u5e08\u7956\u667a\u6167\u8fc7\u4eba\uff0c\u8fd9\u7b49\u505a\u6cd5\uff0c\u4e5f\u53ea\u6709\u5e08\u7956\u80fd\u591f\u6c89\u5f97\u4f4f\u6c14\uff0c\u505a\u5f97\u51fa\u6765\u3002\u201d \u3000\u3000\u8bdd\u97f3\u81f3\u6b64\u4e00\u987f\uff0c\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u5e08\u7956\u902e\u4f4f\u4e86\u90a3\u516d\u4f4d\u65b0\u4efb\u638c\u95e8\u4e4b\u540e\u7b2c\u4e09\u5929\uff0c\u90a3\u4e24\u4f4d\u8499\u9762\u602a\u5ba2\uff0c\u679c\u771f\u5c31\u627e\u4e0a\u4e86\u95e8\u6765\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e08\u7956\u90a3\u65f6\u4e0d\u77e5\u5728\u4f55\u5904\u7b49\u7740\u4ed6\u4eec\u3002\u201d \u3000\u3000\u795e\u5c3c\u7b11\u9053\uff1a\u201c\u5728\u5d69\u5c71\u5c11\u5ba4\u5cf0\u9e93\u7684\u5c11\u6797\u5bfa\u4e2d\uff0c\u636e\u5e08\u7956\u8bf4\uff0c\u90a3\u662f\u7b2c\u4e09\u5929\u7684\u5348\u591c\u65f6\u5206\uff0c\u5e08\u7956\u6b63\u5728\u5c11\u6797\u65b9\u4e08\u5ba4\u5185\u4e0e\u516d\u5927\u95e8\u6d3e\u51e0\u4f4d\u5e78\u5b58\u7684\u957f\u8001\u5546\u8ba8\u5982\u4f55\u91cd\u5efa\u5404\u5927\u95e8\u6d3e\u4e4b\u4e8b\uff0c\u90a3\u4e24\u4f4d\u8499\u9762\u602a\u5ba2\u7adf\u662f\u7a81\u7136\u964d\u4e34\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u5e08\u7956\u90a3\u65f6\u53ef\u662f\u5df2\u7ecf\u7814\u521b\u51fa\u4e86\u9f99\u884c\u5341\u4e8c\u5251\u3002\u201d \u3000\u3000\u795e\u5c3c\u9053\uff1a\u201c\u5e08\u7956\u4e0b\u5c71\u4e4b\u65f6\uff0c\u9f99\u884c\u5341\u4e8c\u5251\u7684\u62db\u5f0f\u5df2\u7136\u7814\u521b\u6210\u719f\uff0c\u4e0d\u8fc7\uff0c\u5a01\u529b\u8fd8\u4e0d\u8db3\u4ee5\u60ca\u4e16\u9a87\u4fd7\uff0c\u4f46\u5bf9\u4ed8\u90a3\u4e24\u4f4d\u8499\u9762\u602a\u5ba2\uff0c\u5374\u5df2\u7ef0\u7ef0\u6709\u4f59\uff0c\u4f60\u5e08\u7956\u90a3\u6b21\u5728\u5c11\u6797\u4e00\u5171\u53ea\u51fa\u5251\u4e03\u6b21\uff0c\u4fbf\u5c06\u90a3\u4e24\u4f4d\u8499\u9762\u602a\u5ba2\u51fb\u8d25\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e08\u7956\u60f3\u662f\u5e76\u6ca1\u6709\u53d6\u4ed6\u4eec\u7684\u6027\u547d\u3002\u201d \u3000\u3000\u795e\u5c3c\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u5e08\u7956\u53ea\u662f\u4e0d\u8bb8\u4ed6\u4eec\u518d\u5165\u4e2d\u539f\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e08\u7956\u4e00\u7247\u4ec1\u5fc3\uff0c\u5b9e\u662f\u6559\u4eba\u94a6\u4f69\u3002\u201d \u3000\u3000\u795e\u5c3c\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u5f53\u65e5\u662f\u7531\u5e08\u7956\u4eb2\u81ea\u966a\u540c\u8499\u9762\u602a\u5ba2\u56de\u81f3\u4e1c\u6d77\u7684\uff0c\u636e\u5e08\u7956\u540e\u6765\u544a\u77e5\u4f60\u5e08\u7236\u548c\u6211\u66fe\u8bf4\u90a3\u4e24\u4eba\u4e43\u662f\u4e00\u5bf9\u592b\u5987\u3002\u4e00\u8eab\u6b66\u529f\uff0c\u786e\u662f\u5341\u5206\u9ad8\u660e\uff0c\u5e08\u7956\u5728\u4e1c\u6d77\u7684\u90a3\u5ea7\u5b64\u5c9b\u4e0a\uff0c\u66fe\u4e0e\u90a3\u5bf9\u592b\u5987\u76d8\u6052\u4e86\u5f88\u4e45\uff0c\u5c31\u5e08\u53d4\u6211\u540e\u6765\u6240\u89c1\uff0c\u663e\u7136\u90a3\u4e00\u5bf9\u592b\u5987\u8ddf\u5e08\u7956\u5df2\u7136\u6210\u4e86\u9053\u4e49\u4e4b\u4ea4\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u80fd\u4f7f\u6709\u6076\u4e4b\u4eba\uff0c\u6d17\u9762\u9769\u5fc3\uff0c\u91cd\u65b0\u5411\u5584\uff0c\u4e5f\u53ea\u6709\u5e08\u7956\u8fd9\u7b49\u5927\u667a\u6167\u65b9\u53ef\u611f\u5316\u5f97\u4e86\u4ed6\u4eec\uff0c\u5982\u662f\u6362\u4e86\u5f1f\u5b50\uff0c\u53ea\u6015\u5f53\u65f6\u90a3\u5bf9\u592b\u5987\u5c31\u5df2\u6b7b\u5728\u5f1f\u5b50\u5251\u4e0b\u4e86\u3002\u201d \u3000\u3000\u795e\u5c3c\u9053\uff1a\u201c\u5e08\u53d4\u5f53\u65e5\u4e5f\u8ddf\u4f60\u7684\u60f3\u6cd5\u4e00\u6837\uff0c\u53ea\u662f\u4e00\u4e2a\u4eba\u5982\u662f\u5e74\u9f84\u589e\u957f\uff0c\u5fc3\u6027\u5df2\u5b9a\uff0c\u90a3\u5c31\u4e0d\u4f1a\u968f\u610f\u51fa\u624b\u4f24\u4eba\uff0c\u9020\u90a3\u65e0\u8fb9\u6740\u52ab\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u5e08\u7956\u90a3\u65f6\u4e0d\u662f\u4e5f\u5f88\u5e74\u8f7b\u5417\uff1f\u201d \u3000\u3000\u795e\u5c3c\u9053\uff1a\u201c\u5927\u7ea6\u53ea\u6709\u4e8c\u5341\u516d\u516b\u5c81\uff0c\u4f46\u5e08\u7956\u4e43\u662f\u975e\u5e38\u4e4b\u4eba\uff0c\u81ea\u662f\u4e0d\u53ef\u4ee5\u5e38\u60c5\u8861\u91cf\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e08\u53d4\u8bf4\u6b66\u6797\u4e4b\u4e2d\uff0c\u6709\u4eba\u80fd\u591f\u514b\u5236\u9f99\u884c\u4e5d\u5251\uff0c\u60f3\u5fc5\u5c31\u662f\u8fd9\u4e00\u5bf9\u592b\u5987\u4e86\uff1f\u201d \u3000\u3000\u795e\u5c3c\u7b11\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u662f\uff0c\u5e08\u7956\u4ee5\u540e\u544a\u8bc9\u4f60\u5e08\u7236\u548c\u6211\u8bf4\uff0c\u80fd\u591f\u514b\u5236\u9f99\u884c\u4e5d\u5251\u6b66\u529f\u7684\u4eba\uff0c\u4e43\u662f\u90a3\u5bf9\u592b\u5987\u7684\u540e\u4ee3\u5b50\u5973\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u5f97\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u53ef\u80fd\u5462\uff1f\u201d \u3000\u3000\u795e\u5c3c\u9053\uff1a\u201c\u5e08\u7956\u65e2\u662f\u8fd9\u4e48\u8bf4\u4e86\uff0c\u90a3\u5f53\u7136\u4e0d\u4f1a\u9519\u7684\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4ecd\u7136\u4e0d\u4fe1\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u90a3\u5bf9\u592b\u5987\u672c\u4eba\u90fd\u4e0d\u662f\u5e08\u7956\u4e4b\u654c\uff0c\u4ed6\u4eec\u7684\u5b50\u5973\u53c8\u600e\u80fd\u66f4\u5f3a\u5f97\u8fc7\u4ed6\u4eec\u7684\u7236\u6bcd\u7684\u5462\uff1f\u201d \u3000\u3000\u795e\u5c3c\u9053\uff1a\u201c\u5f53\u65f6\u5e08\u53d4\u4e5f\u66fe\u767e\u601d\u4e0d\u5f97\u5176\u89e3\uff0c\u4f46\u4eca\u65e5\u60f3\u6765\uff0c\u5374\u53c8\u4e0d\u503c\u5f97\u5947\u602a\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f1f\u5b50\u53ef\u771f\u662f\u4e0d\u61c2\u8fd9\u662f\u4ec0\u4e48\u9053\u7406\u54e9\uff01\u201d \u3000\u3000\u795e\u5c3c\u7b11\u9053\uff1a\u201c\u4e5f\u8bb8\u90a3\u5bf9\u592b\u5987\u5b50\u5973\u7684\u6b66\u529f\uff0c\u4e0d\u662f\u8ddf\u4ed6\u4eec\u7236\u6bcd\u6240\u4e60\uff0c\u5b69\u5b50\uff0c\u4f60\u96be\u9053\u8fd8\u4e0d\u660e\u767d\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u624d\u604d\u7136\u9053\uff1a\u201c\u662f\u4e86\uff0c\u7ecf\u5e08\u53d4\u8fd9\u4e48\u4e00\u8bf4\uff0c\u5f1f\u5b50\u5012\u662f\u660e\u767d\u4e86\uff01\u201d\u4ed6\u987f\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u90a3\u79cd\u6b66\u529f\uff0c\u5e08\u7956\u77e5\u9053\u4ec0\u4e48\u540d\u79f0\u5417\uff1f\u201d \u3000\u3000\u795e\u5c3c\u7b11\u9053\uff1a\u201c\u5e08\u7956\u66fe\u8bf4\uff0c\u90a3\u5957\u6b66\u529f\u53eb\u505a\u2018\u5929\u9e64\u65a9\u2019\uff0c\u56e0\u4e3a\u4ed6\u5728\u8eab\u6cd5\u548c\u62db\u5f0f\u4e0a\u4f3c\u4e4e\u5904\u5904\u90fd\u6bd4\u9f99\u884c\u795e\u5251\u8981\u5feb\u4e0a\u4e00\u4e9b\uff0c\u6240\u4ee5\u4ed6\u80fd\u514b\u5236\u5e08\u7956\u7684\u5251\u6cd5\uff0c\u5b69\u5b50\uff0c\u4f60\u6b66\u529f\u7ec3\u6210\uff0c\u4e0b\u5c71\u4ee5\u540e\uff0c\u5982\u679c\u9047\u5230\u5251\u6cd5\u4e0a\u6bd4\u4f60\u65bd\u5c55\u7684\u9f99\u884c\u4e5d\u5251\u66f4\u5feb\uff0c\u800c\u4e14\u8896\u4e2d\u6697\u85cf\u4e00\u6839\u767d\u8272\u7684\u8f6f\u7d22\uff0c\u53ef\u4ee5\u5377\u4eba\u5175\u5203\uff0c\u90a3\u4e2a\u4eba\u5c31\u662f\u8eab\u5177\u2018\u5929\u9e64\u65a9\u2019\u6b66\u529f\u4e4b\u4eba\uff0c\u5343\u4e07\u83ab\u8981\u5927\u610f\uff0c\u81f4\u906d\u4e0d\u5e78\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u5f97\u5fc3\u4e2d\u81ea\u662f\u6709\u4e9b\u4e0d\u670d\uff0c\u4f46\u4ed6\u6b64\u65f6\u6b64\u523b\uff0c\u5374\u53c8\u4e0d\u6562\u8bf4\u51fa\u5fc3\u4e2d\u6240\u601d\uff0c\u53ea\u662f\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u5f1f\u5b50\u8bb0\u4e0b\u4e86\u3002\u201d \u3000\u3000\u795e\u5c3c\u4f3c\u662f\u65e9\u5df2\u770b\u900f\u4e86\u65b9\u96ea\u5b9c\u5fc3\u610f\u4e00\u822c\uff0c\u7b11\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u5fc3\u4e2d\u662f\u5426\u6709\u4e9b\u4e0d\u670d\u6c14\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fd9\u9053\uff1a\u201c\u5f1f\u5b50\u4e0d\u6562\u8fd9\u7b49\u60f3\u2026\u2026\u201d \u3000\u3000\u795e\u5c3c\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u5e74\u8f7b\u4eba\u80fd\u591f\u521a\u505a\u4e00\u70b9\u4e5f\u4e0d\u4e3a\u8fc7\uff0c\u4f46\u6b64\u4e8b\u4e43\u662f\u5e08\u7956\u9057\u8bad\uff0c\u4f60\u5374\u4e0d\u53ef\u4e0d\u5f53\u5fc3\u4e00\u4e9b\uff0c\u5426\u5219\u4e00\u65e6\u94f8\u6210\u5927\u9519\uff0c\u90a3\u5c31\u6094\u4e4b\u665a\u77e3\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u7684\u5fc3\u4e2d\u6697\u51db\uff0c\u5e08\u53d4\u90a3\u6c89\u91cd\u7684\u8138\u8272\uff0c\u4f7f\u4ed6\u4e0d\u6562\u518d\u4e0d\u4fe1\u5929\u9e64\u65a9\u6b66\u529f\u4e0d\u6bd4\u9f99\u884c\u516b\u5251\u9ad8\u660e\uff0c\u5f53\u4e0b\u63a5\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u5f1f\u5b50\u771f\u7684\u8bb0\u4e0b\u4e86\u3002\u201d \u3000\u3000\u795e\u5c3c\u70b9\u70b9\u5934\u9053\uff1a\u201c\u5e08\u53d4\u4e5f\u76f8\u4fe1\u4f60\u4f1a\u8bb0\u4e0b\u7684\u3002\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u5e08\u7236\u6559\u7684\u9f99\u884c\u516b\u5251\uff0c\u4f60\u518d\u7ec3\u4e00\u904d\u7ed9\u5e08\u53d4\u770b\u770b\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5e94\u58f0\u9053\uff1a\u201c\u5f1f\u5b50\u9075\u547d\u3002\u201d\u5f53\u4e0b\u8d70\u5230\u77f3\u576a\u7684\u4e2d\u592e\uff0c\u6447\u5251\u4e00\u6325\uff0c\u5341\u5206\u4ed4\u7ec6\u7684\u628a\u90a3\u5957\u5251\u6cd5\u91cd\u65b0\u6f14\u7ec3\u4e86\u4e00\u756a\u3002 \u3000\u3000\u795e\u5c3c\u5728\u65c1\u77a7\u5f97\u975e\u5e38\u8ba4\u771f\uff0c\u65b9\u96ea\u5b9c\u5251\u52bf\u4e00\u6536\uff0c\u7acb\u5373\u957f\u957f\u5401\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u5e08\u7236\u867d\u6709\u5251\u795e\u4e4b\u540d\uff0c\u4f46\u4ed6\u4f20\u6388\u4f60\u5251\u62db\u4e4b\u65f6\uff0c\u663e\u7136\u5df1\u6709\u529b\u4e0d\u4ece\u5fc3\u4e4b\u611f\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u62b1\u5251\u8083\u7acb\uff0c\u95fb\u8a00\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u8fd9\u6050\u662f\u5f1f\u5b50\u592a\u7b28\u7684\u7f18\u6545\u5427\uff01\u201d \u3000\u3000\u795e\u5c3c\u6447\u5934\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u77a7\u4f60\u51fa\u5251\u4e4b\u52bf\uff0c\u6bcf\u4e00\u62db\u5251\u6cd5\u90fd\u53ea\u53d1\u6325\u516b\u6210\u5a01\u529b\uff0c\u4f46\u5374\u5728\u53d8\u62db\u4e4b\u9645\uff0c\u4e0d\u66fe\u610f\u5230\u5251\u5230\uff0c\u8fd9\u4fbf\u662f\u4f60\u5e08\u7236\u5728\u6559\u4f60\u4e4b\u65f6\uff0c\u56e0\u4e3a\u4ed6\u81ea\u5df1\u5df2\u529b\u4e0d\u4ece\u5fc3\uff0c\u624d\u4f1a\u96be\u4ee5\u5c06\u523d\u52bf\u63a7\u5236\u7684\u541e\u5410\u81ea\u5982\u4e4b\u6545\u3002\u201d \u3000\u3000\u8d77\u8eab\u63a5\u8fc7\u65b9\u96ea\u5b9c\u624b\u4e2d\u957f\u5251\uff0c\u63a5\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u5e08\u53d4\u9041\u5165\u7a7a\u95e8\u4ee5\u6765\uff0c\u5c31\u5f88\u5c11\u821e\u5200\u5f04\u5251\uff0c\u4f60\u90a3\u96ea\u6d9b\u5e08\u59b9\u867d\u7136\u4e5f\u5b66\u4e86\u4e00\u4e9b\u6b66\u529f\uff0c\u4f46\u90a3\u90fd\u662f\u5065\u4f53\u9632\u8eab\u7684\u62f3\u638c\u5de5\u592b\uff0c\u2026\u2026\u5509\uff01\u4e3a\u4e86\u5e08\u7956\u9057\u5fd7\uff0c\u5e08\u53d4\u6211\u5374\u4e0d\u80fd\u4e0d\u518d\u52a8\u5175\u5203\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f1f\u5b50\u4f1a\u6c38\u8fdc\u8bb0\u5f97\u5e08\u53d4\u7684\u5927\u6069\u2026\u2026\u201d \u3000\u3000\u795e\u5c3c\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u5e08\u53d4\u53c8\u5c82\u662f\u8981\u4f60\u8bb0\u6069\u624d\u6307\u5bfc\u4f60\u7ec3\u5251\u4e4b\u4eba\uff1f\u201d\u7b11\u5bb9\u4e00\u655b\uff0c\u5ffd\u7136\u4f4e\u58f0\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u7559\u5fc3\u4e86\u2026\u2026\u201d \u3000\u3000\u4f46\u89c1\u5979\u767d\u8863\u98d8\u98d8\uff0c\u6f2b\u6b65\u884c\u81f3\u77f3\u576a\u5f53\u4e2d\uff0c\u53f3\u81c2\u4e00\u4f38\uff0c\u5374\u662f\u6f2b\u5929\u5bd2\u5149\u4e4d\u73b0\uff0c\u5251\u5149\u8ff7\u8499\u4e2d\u5df2\u7136\u96be\u89c1\u8eab\u5f62\u4f55\u5728\uff01 \u3000 \u7b2c\u5341\u56de\u3000\u521d\u8bd5\u557c\u58f0 \u3000 \u3000\u3000\u65b9\u96ea\u5b9c\u773c\u89c1\u540c\u6837\u7684\u5251\u6cd5\uff0c\u5728\u5e08\u53d4\u624b\u4e2d\u65bd\u5c55\uff0c\u987f\u65f6\u4e0e\u81ea\u5df1\u6f14\u7ec3\u4e4b\u65f6\u5927\u4e0d\u76f8\u540c\uff0c\u4ed6\u4ed4\u7ec6\u5730\u7559\u5fc3\u7740\u5e08\u53d4\u6bcf\u4e00\u62db\uff0c\u6bcf\u4e00\u5f0f\uff0c\u679c\u771f\u6bd4\u81ea\u5df1\u6240\u4f7f\u7684\u8981\u5b8c\u6574\u5f97\u591a\u591a\u3002 \u3000\u3000\u4ed6\u5df2\u7136\u89c9\u51fa\uff0c\u867d\u4ec5\u662f\u90a3\u4e48\u6beb\u5398\u4e4b\u5dee\uff0c\u4f46\u5251\u52bf\u6240\u53ca\uff0c\u7adf\u662f\u6bd4\u81ea\u5df1\u968f\u5e08\u7236\u6240\u5b66\u7684\u8981\u7ef5\u5bc6\u751a\u591a\uff0c\u663e\u7136\u5e08\u53d4\u5e76\u672a\u8bf4\u9519\uff0c\u5e08\u7236\u56e0\u75c5\u4e4b\u4e0b\uff0c\u786e\u5c5e\u5df2\u96be\u610f\u5728\u5ff5\u751f\uff0c\u53d1\u6325\u5e08\u7956\u6240\u7559\u5251\u6cd5\u7684\u7cbe\u534e\u5965\u5999\u4e86\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u9762\u5728\u6697\u4e2d\u5bfb\u601d\uff0c\u4e00\u9762\u5374\u76ee\u5149\u70af\u70af\uff0c\u4e00\u77ac\u4e0d\u77ac\u7684\u76ef\u7740\u5e08\u53d4\u90a3\u817e\u8dc3\u6709\u5982\u9f99\u884c\u7684\u5251\u52bf\uff0c\u5239\u90a3\u4e4b\u95f4\uff0c\u5374\u662f\u9886\u609f\u4e86\u4e0d\u5c11\u7cbe\u5999\u7684\u53d8\u5316\uff0c\u5927\u611f\u53d7\u76ca\u532a\u6d45\u3002\u65b9\u96ea\u5b9c\u77a7\u5f97\u610f\u72b9\u672a\u5c3d\uff0c\u795e\u5c3c\u4e1a\u5df2\u6536\u62db\u62b1\u5251\uff0c\u5411\u4ed6\u8d70\u6765\uff0c\u542b\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u770b\u660e\u767d\u4e86\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u606d\u656c\u7684\u5e94\u9053\uff1a\u201c\u5f1f\u5b50\u77a7\u5f97\u867d\u662f\u5341\u5206\u4ed4\u7ec6\uff0c\u4f46\u5374\u65e0\u6cd5\u8bb0\u4e0b\u5251\u6cd5\u4e2d\u7cbe\u5965\u53d8\u5316\u2026\u2026\u201d \u3000\u3000\u795e\u5c3c\u7b11\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u5982\u80fd\u77a7\u4e0a\u4e00\u904d\u5c31\u53ef\u8bb0\u4e0b\uff0c\u90a3\u4f60\u7684\u624d\u667a\uff0c\u5c82\u4e0d\u662f\u8d85\u8fc7\u5e08\u7956\u4e86\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u51db\u7136\u8bf4\u9053\uff1a\u201c\u5e08\u7956\u4e43\u662f\u6b66\u6797\u81f3\u5723\uff0c\u5f1f\u5b50\u600e\u53ef\u5984\u81ea\u6bd4\u62df\uff0c\u5e08\u53d4\u8a00\u91cd\u4e86\u3002\u201d \u3000\u3000\u795e\u5c3c\u9053\uff1a\u201c\u773c\u4e0b\u4f60\u8bf4\u4f60\u786e\u662f\u6bd4\u4e0d\u5f97\u4f60\u5e08\u7956\uff0c\u5b69\u5b50\uff0c\u5e08\u53d4\u8bf4\u53e5\u4e0d\u5ba2\u6c14\u9b44\u8bdd\uff0c\u4f60\u8fde\u4f60\u5e08\u7236\uff0c\u4e5f\u8fd8\u4e0d\u80fd\u76f8\u63d0\u5e76\u8bba\u5462\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f1f\u5b50\u77e5\u9053\uff0c\u5e08\u7236\u4e43\u662f\u4e00\u4ee3\u5251\u795e\uff0c\u5f1f\u5b50\u53c8\u5c82\u53ef\u4e0e\u4ed6\u8001\u4eba\u5bb6\u76f8\u6bd4\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7684\u76f8\u5f53\u60f6\u6050\uff0c\u4e0d\u7981\u4f7f\u795e\u5c3c\u770b\u5f97\u7b11\u8d77\u6765\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u4f60\u4e5f\u4e0d\u7528\u5984\u81ea\u532a\u8584\uff0c\u5047\u4ee5\u65f6\u65e5\uff0c\u4f60\u8981\u8d76\u4e0a\u4f60\u5e08\u7236\uff0c\u90a3\u4e5f\u5e76\u975e\u4ec0\u4e48\u96be\u4e8b\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4f46\u51ed\u5e08\u53d4\u683d\u57f9\u4e86\u3002\u201d \u3000\u3000\u795e\u5c3c\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u5e08\u53d4\u81ea\u7136\u5c3d\u5fc3\u57f9\u690d\u4e8e\u4f60\uff0c\u4f46\u6210\u5c31\u5982\u4f55\uff0c\u53ef\u5f97\u8d41\u4f60\u81ea\u5df1\u52aa\u529b\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u7684\u609a\u7136\u4e00\u9707\uff0c\u8fde\u58f0\u5e94\u9053\uff1a\u201c\u5f1f\u5b50\u8bb0\u4e0b\u4e86\u3002\u201d \u3000\u3000\u795e\u5c3c\u70b9\u4e86\u70b9\u5934\uff0c\u7b11\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u54b1\u4eec\u8fd9\u5c31\u5f00\u59cb\u5427\uff01\u201d\u8bdd\u97f3\u4e00\u843d\uff0c\u987a\u624b\u628a\u5b9d\u5251\u4ea4\u7ed9\u4e86\u65b9\u96ea\u5b9c\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u4e14\u4f9d\u7167\u5e08\u7236\u6240\u6388\u7684\u516b\u62db\u5251\u6cd5\uff0c\u4e00\u62db\u4e00\u5f0f\u7684\u6f14\u7ec3\uff0c\u6211\u5982\u53d1\u73b0\u6709\u4e0d\u59a5\u4e4b\u5916\uff0c\u81ea\u4f1a\u6307\u6b63\u4e8e\u4f60\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u53cc\u624b\u540c\u4f38\uff0c\u606d\u656c\u5730\u63a5\u8fc7\u957f\u5251\uff0c\u9053\uff1a\u201c\u5f1f\u5b50\u4e00\u5207\u9075\u547d\u3002\u201d \u3000\u3000\u5f53\u4e0b\u4fbf\u4f9d\u7167\u5e08\u7236\u4f20\u6388\uff0c\u7f13\u6162\u5730\u523a\u51fa\u4e00\u5251\u3002 \u3000\u3000\u8fd9\u6b63\u662f\u201c\u9f99\u884c\u516b\u5251\u201d\u7684\u8d77\u624b\u4e4b\u5f0f\u201c\u6392\u4e91\u5410\u96fe\u201d\uff0c\u5251\u5149\u4e00\u95ea\uff0c\u4e08\u8bb8\u65b9\u5706\u4e4b\u5185\uff0c\u5177\u662f\u88ad\u4eba\u7684\u5bd2\u98ce\uff0c\u5341\u6b65\u4e4b\u4e2d\uff0c\u903c\u4eba\u96be\u4ee5\u7acb\u8db3\uff0c\u679c\u7136\u662f\u65f7\u4ee3\u7684\u7edd\u5b66\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u5171\u6709\u4e09\u5f0f\u53d8\u5316\uff0c\u65b9\u96ea\u5b9c\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u65bd\u5c55\uff0c\u81ea\u662f\u66f4\u89c1\u529f\u592b\uff0c\u53ea\u77a7\u5f97\u795e\u5c3c\u70b9\u5934\u7b11\u9053\uff1a\u201c\u5f88\u597d\uff0c\u8fd9\u4e00\u62db\u4e4b\u4e2d\uff0c\u4f60\u5df2\u5b66\u4f1a\u4e86\u5341\u4e4b\u516b\u4e5d\uff0c\u4f46\u7b2c\u4e09\u5f0f\u53d8\u5316\uff0c\u5374\u7a0d\u5acc\u4e0d\u5168\uff0c\u5982\u662f\u5728\u6a2a\u5251\u4e0a\u64a9\u4e4b\u9645\uff0c\u80fd\u591f\u98de\u8eab\u8dc3\u8d77\u516b\u5c3a\uff0c\u5219\u8fd9\u5f0f\u5a01\u529b\u8981\u6bd4\u4f60\u773c\u4e0b\u6240\u65bd\u5c55\u7684\u589e\u5f3a\u4e24\u500d\u6709\u4f59\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u62b1\u5251\u7b11\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u5251\u8eab\u6a2a\u626b\u4e4b\u9645\uff0c\u6301\u5251\u4e4b\u4eba\u53ea\u6015\u5f88\u96be\u518d\u98de\u8eab\u8dc3\u8d77\u554a\u2026\u2026\u201d \u3000\u3000\u795e\u5c3c\u7b11\u9053\uff1a\u201c\u4e3a\u4eba\u6240\u4e0d\u80fd\u4e4b\u4e3a\uff0c\u6b63\u662f\u4f60\u5e08\u7956\u8fc7\u4eba\u4e4b\u5904\uff0c\u5b69\u5b50\uff0c\u4f60\u628a\u957f\u5251\u7ed9\u6211\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5927\u6b65\u4e0a\u524d\uff0c\u9012\u8fc7\u957f\u5251\uff0c\u795e\u5c3c\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u5c0f\u5fc3\u770b\u660e\u767d\u4e86\u2026\u2026\u201d\u53f3\u624b\u5251\u52bf\u4e00\u632f\uff0c\u7167\u6837\u628a\u90a3\u62db\u201c\u6392\u4e91\u5410\u96fe\u201d\u65bd\u5c55\u4e86\u4e00\u904d\uff0c\u53ea\u89c1\u5251\u5149\u5bd2\u8292\u56db\u5c04\uff0c\u8f83\u4e4b\u65b9\u96ea\u5b9c\u65bd\u5c55\u4e4b\u9645\uff0c\u4e5f\u5e76\u4e0d\u7280\u5229\uff0c\u4e0d\u8fc7\u5728\u7b2c\u4e09\u5f0f\u53d8\u5316\u53d1\u51fa\u4e4b\u65f6\uff0c\u795e\u5c3c\u679c\u771f\u5728\u957f\u5251\u6a2a\u7740\u4e0a\u64a9\u7684\u540c\u65f6\uff0c\u8eab\u5f62\u5ffd\u5730\u5012\u62d4\u800c\u8d77\uff0c\u79bb\u5730\u7ea6\u6709\u4e03\u5c3a\uff0c\u767b\u65f6\u4f7f\u5f97\u90a3\u5251\u52bf\u6bd4\u65b9\u96ea\u5b9c\u8981\u5f3a\u8fc7\u591a\u591a\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u53ea\u77a7\u5f97\u8131\u53e3\u53eb\u9053\uff1a\u201c\u679c\u7136\u662f\u6bd4\u5f1f\u5b50\u5f3a\u7684\u591a\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bdd\u97f3\u672a\u5df2\uff0c\u795e\u5c3c\u5df2\u6536\u5251\u7b11\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u770b\u660e\u767d\u8fd9\u5f0f\u7684\u8bc0\u7a8d\u6ca1\u6709\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f1f\u5b50\u77a7\u662f\u77a7\u660e\u767d\u4e86\uff0c\u53ea\u4e0d\u77e5\u80fd\u4e0d\u80fd\u4f9d\u5f0f\u5b66\u6b65\uff1f\u201d \u3000\u3000\u795e\u5c3c\u9012\u8fc7\u5b9d\u5251\u7b11\u9053\uff1a\u201c\u8bd5\u8bd5\u770b\u5427\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u63a5\u5251\u5728\u624b\uff0c\u51dd\u795e\u4e00\u5fd7\uff0c\u628a\u5e08\u53d4\u9002\u624d\u65bd\u5c55\u7684\u8eab\u6cd5\uff0c\u8fc5\u5feb\u5730\u5728\u5fc3\u4e2d\u60f3\u4e86\u4e00\u904d\uff0c\u8fd9\u624d\u4f9d\u6837\u5730\u6f14\u7ec3\u4e00\u756a\u3002 \u3000\u3000\u522b\u770b\u795e\u5c3c\u6a2a\u5251\u817e\u8eab\uff0c\u5341\u5206\u8f7b\u6613\uff0c\u4f46\u5f53\u65b9\u96ea\u5b9c\u7167\u6837\u7684\u65bd\u5c55\u4e4b\u9645\uff0c\u5374\u89c9\u7740\u4e00\u53e3\u771f\u6c14\u96be\u4ee5\u63d0\u805a\uff0c\u5251\u867d\u6a2a\u64a9\u800c\u51fa\uff0c\u4eba\u5374\u79bb\u5730\u4e0d\u53ca\u4e00\u5c3a\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u8fde\u8bd5\u4e86\u4e09\u6b21\uff0c\u90fd\u662f\u5982\u6b64\uff0c\u4e0d\u7981\u5fc3\u4e2d\u5927\u4e3a\u96be\u8fc7\uff0c\u5446\u5446\u5730\u62b1\u7740\u5b9d\u5251\uff0c\u5411\u795e\u5c3c\u9eef\u7136\u53f9\u606f\u9053\uff1a\u201c\u5e08\u53d9\uff0c\u5f1f\u5b50\u592a\u7b28\u4e86\u3002\u201d \u3000\u3000\u795e\u5c3c\u6de1\u6de1\u4e00\u7b11\uff0c\u6447\u5934\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u600e\u4e48\u4e86\uff0c\u53ef\u662f\u6709\u4e9b\u7070\u5fc3\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f1f\u5b50\u624d\u8bc6\u4e0d\u591f\uff0c\u53ea\u6015\u6709\u8d1f\u5e08\u53d4\u7684\u8fd9\u756a\u82e6\u5fc3\u4e86\u3002\u201d \u3000\u3000\u795e\u5c3c\u7a81\u7136\u53eb\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u65b9\u96ea\u5b9c\uff0c\u5e08\u95e8\u5b8f\u613f\uff0c\u5168\u5728\u4f60\u53cc\u80a9\u4e4b\u4e0a\uff0c\u4f60\u5982\u7ecf\u6b64\u5c0f\u632b\uff0c\u5c31\u7070\u5fc3\u5931\u610f\uff0c\u4f60\u5e08\u7236\u8eab\u5728\u4e5d\u6cc9\uff0c\u5c82\u80fd\u7791\u76ee\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u53ea\u542c\u5f97\u989d\u9645\u5192\u6c57\uff0c\u53cc\u819d\u4e00\u66f2\uff0c\u6251\u901a\u8dea\u5012\u5728\u5730\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u5f1f\u5b50\u4e0d\u6562\u3002\u201d \u3000\u3000\u795e\u5c3c\u6697\u81ea\u5728\u8138\u4e0a\u63a0\u8fc7\u4e00\u4e1d\u7b11\u610f\uff0c\u4f46\u53e3\u4e2d\u5374\u662f\u51b7\u51b7\u559d\u9053\uff1a\u201c\u8d77\u6765\uff0c\u91cd\u65b0\u7ec3\u8fc7\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6bd5\u606d\u6bd5\u656c\u5730\u62dc\u4e86\u4e00\u62dc\uff0c\u8fd9\u624d\u7ad9\u8d77\u8eab\u5b50\uff0c\u62b1\u5251\u5f53\u80f8\uff0c\u62ac\u5934\u9ed8\u5ff5\u9053\uff1a\u201c\u5e08\u7236\u554a\uff01 \u3000\u3000\u6c42\u4f60\u8001\u5728\u5929\u4e4b\u7075\u6697\u52a9\u5f1f\u5b50\uff0c\u7ec3\u6210\u5e08\u7956\u5251\u6cd5\uff0c\u4e5f\u597d\u5b8c\u6210\u4f60\u8001\u9057\u5fd7\u2026\u2026\u201d\u4ed6\u53f3\u81c2\u4e00\u6325\uff0c\u5251\u5149\u4e4d\u8d77\uff0c\u90a3\u4e00\u62db\u201c\u6392\u4e91\u5410\u96fe\u201d\u7684\u4e09\u5f0f\u53d8\u5316\uff0c\u7adf\u662f\u4e00\u6c14\u5475\u6210\u7684\u65bd\u5c55\u51fa\u6765\u3002 \u3000\u3000\u53ea\u662f\uff0c\u5f53\u4ed6\u4eec\u7167\u5e08\u53d4\u6307\u70b9\uff0c\u6a2a\u5251\u817e\u8eab\u4e4b\u8d77\uff0c\u4f9d\u65e7\u89c9\u7740\u6c14\u673a\u4e0d\u7eaf\uff0c\u90a3\u4f7f\u5251\u53f3\u81c2\u6325\u51fa\u4e4b\u540e\uff0c\u65e0\u8bba\u5982\u4f55\u4e5f\u65e0\u6cd5\u518d\u8fd0\u771f\u6c14\uff0c\u62d4\u8db3\u817e\u8eab\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u9893\u7136\u957f\u53f9\u4e00\u58f0\uff0c\u4e24\u773c\u5ffd\u7136\u6cea\u5982\u6cc9\u6d8c\u800c\u51fa\u3002 \u3000\u3000\u4e00\u65f6\u95f4\u4f24\u5fc3\u96be\u7981\uff0c\u4e0d\u89c9\u5f97\u54ed\u51fa\u58f0\u6765\u3002 \u3000\u3000\u795e\u5c3c\u5728\u65c1\u5374\u53cd\u5012\u77a7\u5f97\u7b11\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u54ed\u4ec0\u4e48\u554a\uff1f\u8fd9\u5f0f\u53d8\u5316\u7684\u8bc0\u7a8d\u4f60\u6839\u672c\u672a\u66fe\u770b\u660e\u767d\u561b\u2026\u2026\u201d\u8bdd\u97f3\u4e00\u843d\uff0c\u5ffd\u7136\u53d6\u8fc7\u65b9\u96ea\u5b9c\u624b\u4e2d\u5b9d\u5251\uff0c\u63a5\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u518d\u77a7\u77a7\u3002\u201d \u3000\u3000\u5251\u5149\u4e00\u95ea\uff0c\u8fd9\u56de\u5374\u662f\u975e\u5e38\u7f13\u6162\u5730\u628a\u8fd9\u62db\u53d8\u5316\u65bd\u5c55\u51fa\u6765\uff0c\u5e76\u4e14\u5f88\u8be6\u5c3d\u4ed6\u8bf4\u51fa\u5982\u4f55\u8fd0\u6c14\uff0c\u5982\u4f55\u8fd0\u5251\u7684\u65b9\u6cd5\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7741\u5927\u7740\u6cea\u773c\uff0c\u4e00\u77ac\u4e0d\u77ac\u5730\u671b\u7740\u795e\u5c3c\u7684\u8eab\u6cd5\uff0c\u7ec8\u4e8e\u604d\u7136\u5927\u609f\uff0c\u5fd6\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\u2026\u2026\u201d \u3000\u3000\u6562\u60c5\u9002\u624d\u4ed6\u6240\u4ee5\u4e0d\u80fd\u817e\u8eab\u800c\u8d77\uff0c\u4e43\u662f\u9a6d\u6c14\u8fd0\u5251\u7684\u65b9\u6cd5\u4e0d\u5bf9\uff0c\u624d\u4f1a\u5251\u52bf\u64a9\u51fa\uff0c\u5df2\u544a\u65e0\u529b\u8fd0\u6c14\u817e\u8eab\u3002 \u3000\u3000\u8fd9\u65f6\u4ed6\u660e\u767d\u4e86\u5176\u4e2d\u9053\u7406\uff0c\u987f\u65f6\u7834\u6d95\u4e3a\u7b11\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u5f1f\u5b50\u660e\u767d\u5566\uff01\u201d \u3000\u3000\u795e\u5c3c\u7b11\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u518d\u8bd5\u8bd5\u770b\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4f9d\u8a00\u63a5\u8fc7\u957f\u5251\uff0c\u7167\u7740\u795e\u5c3c\u6240\u793a\uff0c\u4f9d\u4f6f\u65bd\u5c55\u4e86\u4e00\u904d\uff0c\u8fd9\u56de\u4ed6\u7565\u4e00\u8fd0\u6c14\uff0c\u5c31\u5df2\u6325\u5251\u51cc\u7a7a\uff0c\u8dc3\u8d77\u4e86\u56db\u5c3a\u6709\u4f59\uff0c\u800c\u4e14\u5e76\u4e0d\u600e\u4e48\u8d39\u529b\u3002 \u3000\u3000\u795e\u5c3c\u7b11\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u5c31\u7167\u7740\u8fd9\u4e48\u7ec3\uff0c\u53ea\u8981\u4f60\u80fd\u591f\u817e\u8eab\u8d85\u51fa\u4e00\u4e08\uff0c\u8fd9\u4e00\u62db\u5251\u6cd5\u5c31\u7b97\u662f\u7ec3\u5230\u6781\u81f4\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u5f1f\u5b50\u9075\u547d\u2026\u2026\u201d \u3000\u3000\u795e\u5c3c\u5728\u65c1\u4e00\u76f4\u77a7\u5230\u65b9\u96ea\u5b9c\u7ec3\u5230\u5df2\u80fd\u591f\u5728\u51fa\u5251\u540c\u65f6\u817e\u8eab\u516d\u5c3a\u5de6\u53f3\uff0c\u65b9\u59cb\u56de\u8f6c\u5eb5\u5185\u3002 \u3000\u3000\u4f46\u65b9\u96ea\u5b9c\u5374\u4e00\u76f4\u4e0d\u505c\uff0c\u76f4\u5230\u90a3\u96ea\u513f\u5e08\u59b9\u524d\u6765\u53eb\u4ed6\u7528\u9910\uff0c\u4ed6\u65b9\u624d\u4f4f\u624b\u64e6\u6c57\u3002 \u3000\u3000\u65f6\u65e5\u5306\u5306\uff0c\u8f6c\u773c\u5df2\u662f\u4e00\u4e2a\u591a\u6708\u8fc7\u53bb\u3002 \u3000\u3000\u4e00\u4e2a\u6708\u65f6\u5149\uff0c\u51ed\u7740\u65b9\u96ea\u5b9c\u7684\u5b66\u8bc6\u548c\u6bc5\u529b\uff0c\u4ed6\u4e5f\u53ea\u52c9\u52c9\u5f3a\u5f3a\u7684\u628a\u9f99\u884c\u516b\u5251\u7684\u524d\u4e24\u62db\u5404\u5f0f\u53d8\u5316\uff0c\u5b8c\u5168\u8bb0\u4f4f\u3002 \u3000\u3000\u4fbf\u4ed6\u5374\u4ecd\u672a\u5230\u8fbe\u8fd0\u7528\u968f\u5fc3\u4e4b\u5730\u3002 \u3000\u3000\u8fd9\u4e00\u5929\uff0c\u5c71\u4e2d\u96ea\u8272\u5ffd\u6d88\uff0c\u65b9\u96ea\u5b9c\u4e00\u5927\u65e9\u5c31\u88ab\u5eb5\u5916\u6797\u4e2d\u7684\u96c0\u9e1f\u5431\u55a7\u4e4b\u58f0\u5435\u9192\uff0c\u4ed6\u62ab\u8863\u800c\u8d77\uff0c\u6253\u5f00\u4e86\u5eb5\u95e8\uff0c\u4e0d\u89c9\u7684\u957f\u957f\u5410\u4e86\u4e00\u53e3\u5927\u6c14\u3002\u84dd\u5929\u6df1\u8fdc\uff0c\u867d\u7136\u8fd8\u6709\u51e0\u7247\u767d\u4e91\u7ed5\u7740\u5c71\u5ce6\u98d8\u98de\uff0c\u4f46\u90a3\u53ea\u662f\u4e00\u95f4\u5c71\u98ce\u96fe\u6c14\uff0c\u770b\u6765\u8fd9\u662f\u4ed6\u4e0a\u5f97\u91d1\u9876\u4ee5\u6765\u7684\u7b2c\u4e00\u4e2a\u5927\u6674\u7684\u5929\u6c14\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5927\u4e3a\u9ad8\u5174\uff0c\u56de\u53bb\u53d6\u4e86\u957f\u5251\uff0c\u4fbf\u8dc3\u5230\u5eb5\u524d\u77f3\u576a\u4e4b\u4e0a\uff0c\u5411\u7740\u4e1c\u65b9\u5148\u6df1\u6df1\u5730\u4f5c\u4e86\u4e00\u756a\u5410\u7eb3\u5de5\u592b\uff0c\u8fd9\u624d\u62d4\u51fa\u957f\u5251\uff0c\u628a\u5e08\u53d4\u6307\u70b9\u7684\u62db\u5f0f\u4e00\u4e00\u5730\u6f14\u7ec3\u4e86\u4e00\u904d\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u6536\u5251\u5165\u9798\u4e4b\u9645\uff0c\u76ee\u5149\u8fc7\u5904\uff0c\u53ea\u89c1\u795e\u5c3c\u5e08\u53d4\u5df2\u4e0d\u77e5\u4f55\u65f6\u65e0\u58f0\u65e0\u606f\u5730\u6765\u5230\u5eb5\u5916\uff0c\u6ee1\u8138\u542b\u7b11\uff0c\u671b\u7740\u81ea\u5df1\u4e0d\u4f4f\u5730\u70b9\u5934\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5403\u4e00\u60ca\uff0c\u53cc\u624b\u62b1\u5251\uff0c\u9053\uff1a\u201c\u5e08\u53d4\u2026\u2026\u201d \u3000\u3000\u795e\u5c3c\u6148\u7965\u7684\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u9f99\u884c\u516b\u5251\u7684\u524d\u4e24\u62db\uff0c\u4f60\u5df2\u7ecf\u7ec3\u5f97\u5f88\u719f\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u90fd\u662f\u5e08\u53d4\u6307\u70b9\u4e4b\u529f\u3002\u201d \u3000\u3000\u795e\u5c3c\u7b11\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u5c31\u770b\u4f60\u8fd9\u4efd\u8010\u5fc3\u6bc5\u529b\uff0c\u6211\u76f8\u4fe1\u4f60\u5e08\u7956\u7684\u5168\u90e8\u6b66\u5b66\uff0c\u4e00\u5b9a\u80fd\u591f\u5728\u4f60\u8eab\u4e0a\u53d1\u626c\u5149\u5927\u4e86\u2026\u2026\u201d\u8bdd\u97f3\u4e00\u987f\uff0c\u770b\u4e86\u770b\u5929\u6c14\uff0c\u63a5\u9053\uff1a\u201c\u6625\u6c14\u5df2\u52a8\uff0c\u6b64\u540e\u7684\u5929\u6c14\u53ef\u80fd\u8981\u8f6c\u597d\u4e86\uff0c\u5b69\u5b50\uff0c\u4eca\u5929\u6211\u8981\u6307\u5bfc\u4f60\u7ec3\u90a3\u7b2c\u4e09\u62db\u5251\u6cd5\u7684\u5404\u5f0f\u53d8\u5316\uff0c\u4f60\u53ef\u8981\u683c\u5916\u7559\u795e\u4e9b\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u606d\u656c\u5730\u9001\u4e0a\u957f\u5251\uff0c\u9053\uff1a\u201c\u5f1f\u5b50\u660e\u767d\u2026\u2026\u201d \u3000\u3000\u795e\u5c3c\u63a5\u8fc7\u957f\u5251\uff0c\u6162\u6162\u5730\u628a\u9f99\u884c\u516b\u5251\u7684\u7b2c\u4e09\u62db\u201c\u98de\u9f99\u5728\u5929\u201d\u7684\u4e03\u79cd\u53d8\u5316\uff0c\u4e00\u4e00\u6f14\u7ec3\u4e00\u904d\uff0c\u53ea\u628a\u65b9\u96ea\u5b9c\u770b\u5f97\u5446\u4e86\u534a\u54cd\uff0c\u65b9\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u8fd9\u4e00\u62db\u600e\u5730\u6bd4\u5e08\u7236\u4f20\u6388\u7ed9\u5f1f\u4e86\u7684\u591a\u4e86\u56db\u4e2a\u53d8\u5316\u5462\uff1f\u201d \u3000\u3000\u795e\u5c3c\u6536\u5251\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u5e08\u7956\u662f\u5728\u9648\u5e08\u5144\u4e0b\u5c71\u4ee5\u540e\uff0c\u624d\u60f3\u51fa\u8fd9\u62db\u5251\u6cd5\u7684\u53e6\u5916\u4e09\u5f0f\u53d8\u5316\uff0c\u4f60\u5e08\u7236\u53ea\u6559\u4e86\u4e09\u62db\uff0c\u5728\u4ed6\u6765\u8bf4\uff0c\u5df2\u662f\u672a\u66fe\u85cf\u79c1\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e08\u53d9\uff0c\u8fd9\u56db\u5f0f\u53d8\u5316\u597d\u50cf\u2026\u2026\u597d\u50cf\u2026\u2026\u201d \u3000\u3000\u795e\u5c3c\u7b11\u9053\uff1a\u201c\u597d\u50cf\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8138\u4e0a\u5fae\u73b0\u8ff7\u60d1\u4e4b\u5bb9\uff0c\u6447\u5934\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u5f1f\u5b50\u53ef\u80fd\u662f\u8bb0\u9519\u4e86\u3002\u201d \u3000\u3000\u795e\u5c3c\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u5982\u662f\u6709\u4ec0\u4e48\u8bdd\uff0c\u5728\u5e08\u53d4\u9762\u524d\u5927\u53ef\u4e0d\u5fc5\u9690\u7792\uff0c\u8bf4\u51fa\u6765\u5427\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u8fd9\u62db\u5251\u6cd5\u7684\u540e\u56db\u5f0f\u53d8\u5316\uff0c\u5f1f\u5b50\u770b\u6765\uff0c\u5f88\u50cf\u662f\u5e08\u7236\u6559\u6211\u638c\u6cd5\u4e2d\u90a3\u62db\u2018\u5929\u9a6c\u884c\u4e91\u2019\u7684\u56db\u79cd\u53d8\u5316\u54e9\uff01\u201d \u3000\u3000\u795e\u5c3c\u95fb\u8a00\u4e00\u6014\u9053\uff1a\u201c\u771f\u7684\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f1f\u5b50\u600e\u6562\u73ed\u9a97\u5e08\u53d4\uff1f\u201d \u3000\u3000\u795e\u5c3c\u4fee\u7709\u5fae\u9501\u9053\uff1a\u201c\u4f60\u628a\u90a3\u62db\u638c\u6cd5\u65bd\u5c55\u4e00\u904d\u7ed9\u6211\u770b\u770b\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\u4e86\u4e00\u58f0\uff1a\u201c\u9075\u547d\uff01\u201d\u8eab\u5f62\u66b4\u95ea\uff0c\u9661\u5730\u51cc\u7a7a\u98de\u8d77\u4e24\u4e08\uff0c\u53cc\u638c\u5e76\u51fa\uff0c\u5728\u7a7a\u4e2d\u7ed5\u884c\u4e86\u4e00\u5708\uff0c\u653b\u51fa\u4e86\u4e94\u638c\u3002 \u3000\u3000\u5bb9\u5f97\u65b9\u96ea\u5b9c\u53cc\u8db3\u843d\u5730\uff0c\u638c\u52bf\u4e00\u6536\uff0c\u6b63\u5f85\u5411\u5e08\u53d9\u8bf4\u8bdd\uff0c\u76ee\u5149\u6240\u53ca\uff0c\u4ed6\u4e0d\u89c9\u5446\u4e86\u3002\u53ea\u89c1\u795e\u5c3c\u6728\u7136\u8083\u7acb\uff0c\u4e24\u773c\u4e4b\u4e2d\uff0c\u7adf\u662f\u6cea\u5149\u95ea\u95ea\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5403\u4e86\u4e00\u60ca\uff0c\u8f7b\u58f0\u8bf4\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u4f60\u8001\u600e\u4e48\u5566\u3002\u201d \u3000\u3000\u795e\u5c3c\u6cea\u773c\u5ffd\u95ed\uff0c\u4e24\u9897\u6676\u83b9\u7684\u6cea\u73e0\uff0c\u987a\u816e\u6ef4\u843d\u5730\u4e0a\uff0c\u5979\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u6211\u5f80\u65e5\u5c0f\u770b\u4e86\u9648\u5e08\u5144\u4e86\u2026\u2026\u201d\u8bdd\u97f3\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5e08\u7956\u60f3\u51fa\u7684\u62db\u5f0f\uff0c\u5e08\u5144\u7adf\u7136\u4e5f\u80fd\u60f3\u5f97\u51fa\u6765\uff0c\u53ef\u60dc\u554a\uff0c\u53ef\u60dc\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6014\u6014\u5730\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u4f60\u8001\u53ef\u60dc\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u795e\u5c3c\u4f4e\u53f9\u9053\uff1a\u201c\u53ef\u60dc\u4f60\u5e08\u7236\u906d\u4eba\u6697\u7b97\uff0c\u8eab\u6000\u4e0d\u6cbb\u4e4b\u75c7\uff0c\u4ee5\u81f4\u8e49\u8dce\u4e86\u6570\u5341\u5e74\u5c81\u6708\uff0c\u7a7a\u628a\u5168\u8eab\u7cbe\u529b\uff0c\u90fd\u4ed8\u8bf8\u5bf9\u6297\u75c5\u9b54\uff0c\u8c03\u7406\u751f\u673a\u4e4b\u4e0a\uff0c\u5426\u5219\uff0c\u4f60\u5e08\u7236\u7684\u6210\u5c31\uff0c\u53ea\u6015\u4e0d\u4f1a\u5728\u4f60\u5e08\u7956\u4e4b\u4e0b\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e5f\u4f3c\u662f\u6709\u4e9b\u660e\u767d\u5730\u5e94\u58f0\u9053\uff1a\u201c\u662f\u554a\uff01\u5e08\u7236\u82e5\u975e\u8eab\u6000\u6b8b\u75be\uff0c\u5e08\u7956\u7684\u6b66\u5b66\uff0c\u53ea\u6015\u65e9\u88ab\u5e08\u7236\u60f3\u901a\u4e86\u3002\u201d \u3000\u3000\u795e\u5c3c\u7565\u4e00\u6c89\u541f\u63a5\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u8fd9\u7b2c\u4e09\u62db\u5251\u6cd5\u7684\u540e\u9762\u897f\u5f0f\u53d8\u5316\uff0c\u679c\u7136\u4e0e\u90a3\u638c\u6cd5\u5927\u540c\u5c0f\u5f02\uff0c\u53ea\u662f\u5728\u8fd0\u6c14\u51fa\u62db\u4e4b\u65f6\uff0c\u7a0d\u6709\u4e0d\u540c\uff0c\u4f60\u5148\u8bd5\u8bd5\uff0c\u4ee5\u5251\u6362\u638c\uff0c\u662f\u5426\u4e5f\u80fd\u5f97\u5fc3\u5e94\u624b\uff1f\u201d\u8bf4\u7740\uff0c\u628a\u624b\u4e2d\u957f\u5251\u9012\u8fc7\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u63a5\u5251\u5728\u624b\uff0c\u4f9d\u7740\u5e08\u53d4\u5148\u524d\u7684\u8eab\u6cd5\uff0c\u7167\u6837\u6f14\u7ec3\u4e00\u904d\uff0c\u7ed3\u679c\u5012\u4e5f\u76f8\u5dee\u65e0\u51e0\uff0c\u795e\u5c3c\u77a7\u5f97\u8fde\u8fde\u70b9\u5934\uff1a\u201c\u5b69\u5b50\uff0c\u672c\u6765\u6211\u803d\u5fc3\u8fd9\u4e00\u62db\u8981\u82b1\u8d39\u4e00\u4e2a\u6708\u7684\u65f6\u5149\uff0c\u6599\u4e0d\u5230\u8fd9\u62db\u53d8\u5316\uff0c\u7adf\u662f\u4e0d\u9700\u4e00\u65e5\uff0c\u4f60\u4fbf\u53ef\u4ee5\u7ec3\u597d\uff0c\u5e08\u53d4\u771f\u662f\u4e3a\u4f60\u9ad8\u5174\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u81ea\u4e5f\u9ad8\u5174\u975e\u5e38\uff0c\u4f46\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u5f1f\u5b50\u5374\u89c9\u5f97\u7528\u5251\u523a\u51fa\uff0c\u4e0d\u5982\u7528\u638c\u65f6\u6325\u6d12\u81ea\u5982\uff0c\u5e08\u53d4\uff0c\u662f\u4e0d\u662f\u5f1f\u5b50\u6709\u4ec0\u4e48\u5730\u65b9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u795e\u5c3c\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u662f\uff0c\u4f60\u53ea\u4e0d\u8fc7\u5c1a\u672a\u7eaf\u719f\u800c\u5df2\u2026\u2026\u201d\u8bdd\u97f3\u987f\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u5e08\u53d4\u5ffd\u7136\u60f3\u8d77\u4e00\u4ef6\u91cd\u5927\u4e4b\u4e8b\uff0c\u5fc5\u987b\u4e0b\u5c71\u4e00\u884c\uff0c\u4e5f\u8bb8\u8981\u5341\u5929\u65b9\u59cb\u8d76\u5f97\u56de\u6765\uff0c\u4f60\u8ddf\u96ea\u513f\u5e08\u59b9\u7559\u5728\u5eb5\u4e2d\uff0c\u4e0d\u4f1a\u5bb3\u6015\u5427\uff1f\u201d \u3000\u3000\u795e\u5c3c\u8fd9\u7b49\u95ee\u4ed6\uff0c\u4e0d\u557b\u628a\u4ed6\u5f53\u6210\u4e86\u4e00\u4e2a\u666e\u901a\u7684\u5b69\u5b50\uff0c\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u5927\u611f\u4e0d\u670d\uff0c\u5f53\u4e0b\u4e00\u633a\u80f8\u819b\uff0c\u5927\u58f0\u5e94\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u5f1f\u5b50\u8fd9\u4e48\u5927\u4e86\uff0c\u81ea\u662f\u4e0d\u4f1a\u5bb3\u6015\uff0c\u5e08\u53d4\u5c3d\u7ba1\u653e\u5fc3\u4e0b\u5c71\u53bb\u5427\uff01\u201d \u3000\u3000\u795e\u5c3c\u77a7\u7740\u4ed6\u90a3\u4efd\u6545\u4f5c\u5927\u4eba\u7684\u8c6a\u52c7\u795e\u60c5\uff0c\u4e0d\u7981\u7b11\u9053\uff1a\u201c\u662f\u554a\uff01\u4f60\u5df2\u7ecf\u8eab\u5177\u5251\u795e\u6b66\u529f\uff0c\u81ea\u662f\u4e0d\u4f1a\u5bb3\u6015\u7684\u4e86\uff0c\u5e08\u53d4\u5012\u662f\u591a\u66ff\u4f60\u803d\u5fc3\u5566\uff01\u201d \u3000\u3000\u5ffd\u7136\u4f38\u624b\u628a\u65b9\u96ea\u5b9c\u624b\u4e2d\u957f\u5251\u53d6\u8fc7\uff0c\u63a5\u9053\uff1a\u201c\u6765\uff0c\u5b69\u5b50\uff0c\u5e08\u53d4\u518d\u628a\u5269\u4e0b\u51e0\u62db\u5251\u6cd5\u4e2d\u7684\u53d8\u5316\u6559\u4f60\uff0c\u5e08\u53d4\u4e0b\u5c71\u8fd9\u51e0\u5929\uff0c\u4f60\u5c31\u5168\u5fc3\u5168\u610f\u628a\u8fd9\u51e0\u62db\u5251\u6cd5\u7ec3\u719f\u2026\u2026\u7559\u5fc3\u5730\u77a7\u7740\uff01\u201d\u767d\u8863\u98d8\u62c2\uff0c\u6b65\u5c65\u4ece\u5bb9\uff0c\u9f99\u884c\u516b\u5251\u7b2c\u56db\u62db\u201c\u6012\u9f99\u51fa\u5cab\u201d\uff0c\u5df2\u7136\u8fc5\u5feb\u5730\u5728\u795e\u5c3c\u7684\u624b\u4e0b\u65bd\u5c55\u5f00\u6765\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e0d\u6562\u5927\u610f\uff0c\u76ef\u7740\u795e\u5c3c\u7684\u4e3e\u624b\u6295\u8db3\uff0c\u8fde\u7728\u773c\u90fd\u4e0d\u6562\uff0c\u5c4f\u6c14\u51dd\u795e\uff0c\u770b\u5f97\u679c\u662f\u5341\u5206\u7ec6\u5fc3\u3002 \u3000\u3000\u8fd9\u4e94\u62db\u5251\u6cd5\uff0c\u5171\u6709\u5341\u56db\u5f0f\u53d8\u62db\uff0c\u65b9\u96ea\u5b9c\u867d\u662f\u5168\u795e\u8d2f\u6ce8\u7684\u5f3a\u8bb0\uff0c\u4f46\u4e5f\u672a\u80fd\u5b8c\u5168\u8bb0\u4e0b\uff0c\u5bb9\u5f97\u795e\u5c3c\u7b2c\u4e8c\u65e5\u4e0b\u5c71\u800c\u53bb\uff0c\u4ed6\u4e00\u5171\u53ea\u8bb0\u4e0b\u4e86\u5341\u5f0f\uff0c\u4e03\u3001\u516b\u4e24\u62db\u7684\u56db\u5f0f\u53d8\u5316\uff0c\u4ed6\u600e\u4e48\u4e5f\u60f3\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u65e0\u53ef\u5948\u4f55\u4e4b\u4e0b\uff0c\u53ea\u597d\u5229\u7528\u5e08\u53d4\u4e0b\u5c71\u7684\u8fd9\u51e0\u5929\uff0c\u5168\u5fc3\u4e60\u7ec3\u90a3\u8bb0\u4e0b\u6765\u7684\u5341\u62db\u5251\u5f0f\uff0c\u4ed6\u5f88\u60f3\u5728\u795e\u5c3c\u56de\u5c71\u4e4b\u524d\uff0c\u628a\u8fd9\u5341\u62db\u5251\u5f0f\u7ec3\u719f\u3002 \u3000\u3000\u8fd9\u6b63\u662f\u5e08\u53d4\u79bb\u5e2d\u7684\u7b2c\u516b\u5929\u6df1\u591c\uff0c\u65b9\u96ea\u5b9c\u5728\u90a3\u5c0f\u5c0f\u7684\u4f5b\u5802\u4e4b\u4e2d\uff0c\u6b63\u5c31\u7740\u90a3\u660f\u6697\u706f\u5149\uff0c\u8bfb\u795e\u5c3c\u4e34\u53bb\u4e4b\u65f6\uff0c\u4ea4\u7ed9\u4ed6\u7684\u4e00\u672c\u624b\u518c\uff0c\u8fd9\u672c\u624b\u518c\u6240\u8bb0\uff0c\u5747\u662f\u5e08\u7956\u6614\u5e74\u884c\u9053\u6c5f\u6e56\u4e4b\u65e5\u6240\u9047\u5230\u7684\u5404\u79cd\u7591\u96be\u6742\u75c7\u533b\u6cbb\u7684\u7ecf\u8fc7\uff0c\u5c24\u5176\u5bf9\u4e8e\u6b66\u6797\u4eba\u7269\u7684\u7528\u6bd2\u624b\u6bb5\uff0c\u548c\u5404\u79cd\u89e3\u6bd2\u65b9\u6cd5\uff0c\u66f4\u662f\u8bb0\u8f7d\u5f97\u5341\u5206\u8be6\u5c3d\uff0c\u4f7f\u65b9\u96ea\u5b9c\u770b\u5f97\u5927\u4e3a\u51fa\u795e\uff0c\u6b32\u7f62\u4e0d\u5fcd\u91ca\u624b\u3002 \u3000\u3000\u7ea6\u83ab\u5df2\u662f\u4e09\u66f4\u65f6\u5206\uff0c\u5ce8\u5d4b\u4f0f\u864e\u7985\u5bfa\u7684\u591c\u8bfe\u949f\u58f0\u5df2\u6c93\uff0c\u65b9\u96ea\u5b9c\u5026\u6020\u6b32\u7720\u7684\u4f38\u4e00\u4e2a\u61d2\u8170\uff0c\u6b63\u6253\u7b97\u63a8\u4e66\u800c\u5367\uff0c\u9661\u7136\u4e4b\u95f4\uff0c\u4ed6\u5fc3\u7075\u4e0a\u8d77\u4e86\u4e00\u9635\u6014\u5fe1\u4e0d\u5b89\u7684\u8b66\u5146\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u7709\u5934\u4e00\u76b1\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u4e8b\u53ef\u6709\u4e9b\u6eaa\u8df7\uff0c\u8fd9\u5367\u4e91\u5eb5\u6570\u5341\u5e74\u5e73\u9759\u65e0\u4e8b\uff0c\u600e\u5730\u5e08\u53d4\u521a\u521a\u79bb\u5f00\u51e0\u5929\uff0c\u5c31\u5ffd\u7136\u2026\u2026\u201d \u3000\u3000\u4ed6\u8f6c\u5ff5\u672a\u5df2\uff0c\u8033\u4e2d\u5df2\u4f20\u6765\u4e00\u9635\u7ec6\u788e\u7684\u6b65\u5c65\u4e4b\u58f0\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u9a87\u7136\u4e00\u9707\uff0c\u8fc5\u5feb\u5730\u8df3\u843d\u5730\u4e0a\uff0c\u987a\u624b\u53d6\u8fc7\u6795\u4e0b\u7684\u5b9d\u5251\uff0c\u8f7b\u624b\u8f7b\u811a\u7684\u7a7f\u4e0a\u4e86\u8863\u886b\uff0c\u8fd9\u624d\u6253\u5f00\u4e86\u90a3\u5c0f\u5c0f\u7684\u5706\u7a97\uff0c\u5411\u5916\u671b\u53bb\u3002 \u3000\u3000\u76ee\u5149\u6240\u53ca\uff0c\u65b9\u96ea\u5b9c\u4e0d\u7981\u5446\u4e86\u3002 \u3000\u3000\u53ea\u89c1\u5728\u90a3\u5eb5\u5916\u7684\u77f3\u576a\u4e0a\uff0c\u7ad9\u7740\u4e00\u7537\u4e00\u5973\uff0c\u4f3c\u662f\u6b63\u5728\u5546\u8ba8\u7740\u4ec0\u4e48\uff1f\u800c\u90a3\u4f4d\u5973\u4eba\uff0c\u7adf\u7136\u5c31\u662f\u96ea\u6d9b\u5e08\u59b9\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u4e00\u60ca\uff0c\u5f53\u771f\u4e0d\u5c0f\uff0c\u5982\u4ed6\u6240\u60f3\uff0c\u8fd9\u4f4d\u96ea\u6d9b\u5c0f\u5c3c\u59d1\u662f\u4e0d\u5e94\u8be5\u8ba4\u8bc6\u4ec0\u4e48\u751f\u4eba\u7684\uff0c\u60f3\u4e0d\u5230\u5979\u6b64\u65f6\u6b64\u523b\uff0c\u5e08\u53d4\u4e0b\u5c71\u4e0d\u4e45\uff0c\u5c31\u4f1a\u6709\u4e2a\u5927\u7537\u4eba\u627e\u4e0a\u5c71\u6765\uff0c\u83ab\u975e\u8fd9\u4f4d\u5c0f\u5c3c\u59d1\u7adf\u7136\u662f\u4f4d\u53ef\u7591\u7684\u4eba\u7269\u5417\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u5ff5\u53ca\u6b64\uff0c\u5fc3\u4e2d\u7acb\u611f\u4e0d\u5999\uff0c\u4e00\u7d27\u624b\u4e2d\u957f\u5251\uff0c\u6781\u4e3a\u5192\u5931\u7684\u62c9\u5f00\u4e86\u5eb5\u95e8\uff0c\u5927\u6b65\u5411\u4e24\u4eba\u8d70\u53bb\u3002 \u3000\u3000\u90a3\u96ea\u6d9b\u5973\u5c3c\u542c\u5f97\u811a\u6b65\u4e4b\u58f0\uff0c\u4f3c\u4e1d\u6beb\u4e0d\u7ecf\u4e3a\u610f\u5730\u56de\u5934\u770b\u4e86\u65b9\u96ea\u5b9c\u4e00\u670d\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u65b9\u5e08\u5144\uff0c\u5feb\u6765\u89c1\u8fc7\u7533\u5927\u4fa0\uff01\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u90a3\u4f4d\u8eab\u9ad8\u7ea6\u6709\u516b\u5c3a\uff0c\u6ee1\u9762\u866c\u9aef\u621f\u5f20\uff0c\u6c14\u52bf\u5a01\u731b\u7684\u7070\u8863\u5927\u6c49\uff0c\u5df2\u7136\u53cc\u624b\u62b1\u62f3\uff0c\u5148\u884c\u53d1\u8bdd\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\u5fc5\u5c31\u662f\u5251\u795e\u9648\u5927\u4fa0\u7684\u4f20\u4eba\u65b9\u516c\u5b50\u4e86\uff0c\u5728\u4e0b\u7533\u884c\u65f6\uff0c\u6df1\u591c\u9020\u8bbf\uff0c\u6253\u6270\u4e86\u516c\u5b50\u6e05\u68a6\uff0c\u7f6a\u8fc7\u5f97\u5f88\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5927\u51fa\u610f\u6599\u4e4b\u5916\u7684\u5446\u4e86\u5446\uff0c\u6123\u6123\u5730\u5411\u96ea\u6d9b\u5e08\u59b9\u9053\uff1a\u201c\u96ea\u5e08\u59b9\uff0c\u8fd9\u4f4d\u7533\u5927\u4fa0\u5e38\u6765\u5417\uff1f\u201d \u3000\u3000\u96ea\u6d9b\u6251\u55e4\u4e00\u7b11\u9053\uff1a\u201c\u65b9\u5e08\u5144\uff0c\u8d2b\u5c3c\u4e00\u65f6\u672a\u80fd\u628a\u8bdd\u8bf4\u660e\uff0c\u60f3\u5fc5\u5e08\u5144\u4e00\u5b9a\u5fc3\u4e2d\u5f88\u662f\u8be7\u5f02\u4e86\u3002\u201d\u8bdd\u97f3\u987f\u4e86\u4e00\u987f\u9053\uff1a\u201c\u7533\u5927\u4fa0\u5c31\u4f4f\u5728\u4f0f\u864e\u5bfa\u4e2d\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u8d2b\u5c3c\u8ddf\u5e08\u7236\u867d\u7136\u672a\u79bb\u5f00\u5367\u4e91\u5eb5\u4e00\u6b65\uff0c\u4f46\u5eb5\u4e2d\u6240\u9700\u7684\u4e00\u5207\uff0c\u65e0\u8651\u532e\u4e4f\uff0c\u5168\u90fd\u662f\u4ef0\u4ed7\u7533\u5927\u4fa0\u7684\u70ed\u5fc3\u5e2e\u5fd9\u2026\u2026\u201d \u3000\u3000\u96ea\u6d9b\u8bdd\u97f3\u752b\u6b47\uff0c\u65b9\u96ea\u5b9c\u7acb\u5373\u604d\u7136\uff0c\u6562\u60c5\u81ea\u5df2\u90a3\u7b49\u7591\u5fc3\uff0c\u786e\u662f\u592a\u8fc7\u4ee5\u5c0f\u4eba\u4e4b\u5fc3\uff0c\u5ea6\u541b\u5b50\u4e4b\u8179\u4e86\u3002\u5f53\u4e0b\u8fde\u5fd9\u6487\u4e0b\u624b\u4e2d\u957f\u5251\uff0c\u7ea2\u7740\u8138\u7b11\u9053\uff1a\u201c\u665a\u8f88\u4e0d\u77e5\u7533\u5927\u4fa0\u4e43\u662f\u5e08\u53d4\u65e7\u53cb\uff0c\u4e00\u76f4\u5728\u6b64\u7167\u987e\uff0c\u5931\u793c\u4e4b\u5904\uff0c\u5c1a\u7948\u89c1\u8c05\uff01\u201d\u8bf4\u7740\uff0c\u957f\u957f\u4e00\u63d6\u5230\u5730\u3002 \u3000\u3000\u90a3\u7533\u884c\u65f6\u4e0d\u4f46\u8c8c\u76f8\u8c6a\u8fbe\u8fc7\u4eba\uff0c\u800c\u4e14\u4e3a\u4eba\u5927\u6982\u4e5f\u662f\u4e00\u4f4d\u8c6a\u6c14\u8fc7\u4eba\u4e4b\u58eb\uff0c\u95fb\u8a00\u7adf\u662f\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u8fd9\u4e48\u8bf4\u53ef\u53eb\u7533\u67d0\u6127\u6c57\u65e0\u5730\u81ea\u5bb9\u4e86\uff0c\u9648\u5927\u4fa0\u4e8e\u6211\u6709\u6d3b\u547d\u518d\u9020\u4e4b\u6069\uff0c\u533a\u533a\u5949\u9648\u5927\u4fa0\u4e4b\u547d\uff0c\u6548\u52b3\u5954\u9a70\uff0c\u4e43\u662f\u7406\u6240\u5f53\u7136\uff0c\u5c0f\u5144\u5f1f\uff0c\u8fd9\u4e00\u70b9\u5fae\u52b3\u6bd4\u4e4b\u9648\u5927\u4fa0\u6d3b\u547d\u4e4b\u6069\uff0c\u7b80\u76f4\u662f\u6ca7\u6d77\u4e00\u7c9f\u3002\u201d\u5ffd\u7136\u957f\u53f9\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u7533\u67d0\u60f3\u4e0d\u5230\u9648\u5927\u4fa0\u7adf\u5df2\u4f5c\u4e86\u53e4\u4eba\uff0c\u7533\u67d0\u4eca\u65e5\u65b9\u59cb\u627f\u8499\u5c0f\u5e08\u7236\u544a\u77e5\uff0c\u5fc3\u4e2d\u60b2\u75db\u96be\u7981\uff0c\u4f3c\u8fd9\u7b49\u518d\u9020\u5927\u6069\uff0c\u53eb\u7533\u67d0\u8fd9\u4f59\u751f\u4e4b\u5e74\uff0c\u5df2\u7136\u62a5\u7b54\u673a\u4f1a\uff0c\u601d\u60f3\u8d77\u6765\uff0c\u771f\u4ee4\u4eba\u6709\u82cd\u5929\u65e0\u773c\u4e4b\u6068\uff01\u201d\u8a00\u7f62\uff0c\u53ea\u662f\u4e00\u58f0\u51c4\u7136\u957f\u53f9\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u6697\u9053\uff0c\u539f\u6765\u8fd9\u59d3\u7533\u7684\u4e43\u662f\u53d7\u4e86\u5e08\u7236\u4e4b\u6258\uff0c\u5728\u6b64\u7167\u987e\u5e08\u53d4\u7684\uff0c\u77a7\u6b64\u4eba\u8a00\u8c08\uff0c\u5fc5\u7136\u4e5f\u662f\u4e00\u4f4d\u6b66\u6797\u5927\u4fa0\uff0c\u4e3a\u4e86\u62a5\u7b54\u5e08\u7236\u5bf9\u4ed6\u7684\u6551\u547d\u4e4b\u6069\uff0c\u5c45\u7136\u5bc4\u8eab\u4f5b\u5bfa\uff0c\u6570\u5341\u5e74\u5982\u4e00\u65e5\uff0c\u628a\u540d\u5229\u4e8c\u5b57\uff0c\u6de1\u6de1\u5fd8\u5374\uff0c\u82e5\u975e\u5927\u52c7\u4e4b\u58eb\uff0c\u53c8\u4f55\u80fd\u81fb\u6b64\uff1f\u8fd9\u7b49\u4eba\u624d\u662f\u81ea\u5df1\u5e94\u5f53\u656c\u4f69\u7684\u82f1\u96c4\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5ff5\u5934\u8f6c\u7684\u6781\u5feb\uff0c\u90a3\u7533\u884c\u65f6\u53f9\u606f\u4e4b\u58f0\u752b\u843d\uff0c\u4ed6\u7acb\u5373\u5e94\u58f0\u9053\uff1a\u201c\u7533\u5927\u4fa0\u7684\u5fc3\u610f\uff0c\u5148\u5e08\u7eb5\u5728\u6cc9\u4e0b\uff0c\u5b9a\u7136\u4e5f\u662f\u611f\u6fc0\u4e0d\u5c3d\u7684\u2026\u2026\u201d \u3000\u3000\u7533\u884c\u65f6\u6447\u4e86\u6447\u5934\uff0c\u611f\u6168\u4e07\u5343\u5730\u53f9\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u9648\u5927\u4fa0\u4e88\u6211\u518d\u9020\u4e4b\u6069\uff0c\u7533\u67d0\u5b9e\u662f\u7c89\u8eab\u788e\u9aa8\uff0c\u96be\u4ee5\u4e3a\u62a5\uff0c\u5c0f\u5144\u5f1f\u65e2\u662f\u9648\u5927\u4fa0\u8863\u94b5\u4f20\u4eba\uff0c\u5728\u7533\u67d0\u773c\u4e2d\uff0c\u4f60\u5c0f\u5144\u5f1f\u5df2\u4e0d\u557b\u9648\u5927\u4fa0\u5316\u8eab\uff0c\u4eca\u540e\u5c0f\u5144\u5f1f\u4ed7\u5251\u6c5f\u6e56\uff0c\u8bdb\u9b54\u884c\u9053\u4e4b\u65f6\uff0c\u5982\u6709\u7528\u5f97\u7740\u7533\u67d0\u4e4b\u65f6\uff0c\u53ea\u8981\u5c0f\u5144\u5f1f\u4e00\u7eb8\u76f8\u62db\uff0c\u7533\u67d0\u4e07\u6b7b\u4e0d\u8f9e\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u5f97\u5927\u4e3a\u611f\u52a8\uff0c\u5fd9\u9053\uff1a\u201c\u7533\u5927\u4fa0\u9686\u60c5\u9ad8\u8c0a\uff0c\u665a\u8f88\u8bb0\u4e0b\u4e86\u3002\u201d \u3000\u3000\u7533\u884c\u65f6\u6599\u4e0d\u5230\u65b9\u96ea\u5b9c\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u7adf\u80fd\u5982\u6b64\u8c6a\u723d\uff0c\u5f53\u4e0b\u653e\u58f0\u5927\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u54b1\u4eec\u5c31\u6b64\u4e00\u8a00\u4e3a\u5b9a\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u957f\u957f\u4e00\u63d6\uff0c\u7b11\u9053\uff1a\u201c\u7533\u5927\u4fa0\u76db\u60c5\uff0c\u665a\u8f88\u5374\u4e4b\u4e0d\u606d\uff0c\u81ea\u662f\u611f\u5230\u6127\u6c57\u2026\u2026\u201d\u4f59\u97f3\u672a\u7edd\uff0c\u7a81\u7136\u95f4\u795e\u8272\u5927\u53d8\uff0c\u7adf\u662f\u4fef\u8eab\u62fe\u8d77\u957f\u5251\uff0c\u77aa\u7740\u7533\u884c\u65f6\uff0c\u4f4e\u559d\u9053\uff1a\u201c\u7533\u5927\u4fa0\uff0c\u4f60\u662f\u51e0\u4e2a\u4eba\u4e00\u9053\u800c\u6765\uff1f\u201d \u3000\u3000\u7533\u884c\u65f6\u5446\u4e86\u4e00\u5446\u9053\uff1a\u201c\u7533\u67d0\u5411\u6765\u72ec\u5f80\uff0c\u5c0f\u5144\u5f1f\u4f55\u51fa\u6b64\u8a00\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u51b7\u51b7\u5730\u7b11\u4e86\u4e00\u7b11\u9053\uff1a\u201c\u6709\u4eba\u6765\u4e86\uff1f\u201d \u3000\u3000\u90a3\u96ea\u6d9b\u7389\u989c\u5927\u5927\u5931\u8272\u5730\u95ee\u9053\uff1a\u201c\u65b9\u5e08\u5144\uff0c\u771f\u7684\u6709\u4eba\u6765\u4e86\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u6765\u4eba\u5df2\u5728\u5ca9\u4e0b\uff0c\u5e08\u59b9\u7a0d\u65f6\u81ea\u6709\u53d1\u73b0\uff01\u201d \u3000\u3000\u90a3\u7533\u884c\u65f6\u6b64\u523b\u53ef\u662f\u5927\u611f\u4e0d\u5b89\u5730\u76b1\u8d77\u4e86\u6d53\u7709\uff0c\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u6ca1\u6709\u542c\u9519\uff0c\u5f53\u771f\u662f\u6709\u4eba\u6765\u4e86\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u7533\u5927\u4fa0\u83ab\u975e\u4e0d\u4fe1\u5417\uff1f\u201d \u3000\u3000\u7533\u884c\u65f6\u4f4e\u53f9\u9053\uff1a\u201c\u4e0d\u662f\u7533\u67d0\u4e0d\u4fe1\uff0c\u800c\u662f\u8fd9\u7b49\u6df1\u5c71\u534a\u591c\uff0c\u5c0f\u5144\u5f1f\u53ef\u80fd\u7591\u5c06\u91ce\u517d\u5f53\u4f5c\u4eba\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u5934\u9053\uff1a\u201c\u4e0d\u53ef\u80fd\uff0c\u665a\u8f88\u8033\u76ee\u867d\u4e0d\u751a\u7075\u654f\uff0c\u4f46\u4eba\u517d\u4e4b\u5206\uff0c\u5f53\u4f1a\u7262\u8bb0\u5fc3\u5934\u3002\u201d \u3000\u3000\u663e\u7136\uff0c\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u5df2\u7ecf\u5bf9\u8fd9\u4f4d\u7533\u884c\u65f6\u8d77\u4e86\u7591\u5ff5\uff0c\u662f\u4ee5\uff0c\u8a00\u8bcd\u4e4b\u4e0b\uff0c\u5c31\u4e0d\u514d\u6697\u6697\u5e26\u4e86\u8ba5\u8bbd\u4e4b\u610f\u3002 \u3000\u3000\u90a3\u7533\u884c\u65f6\u95ef\u8361\u6c5f\u6e56\u751a\u4e45\uff0c\u7109\u6709\u542c\u4e0d\u51fa\u65b9\u96ea\u5b9c\u5728\u6697\u9a82\u81ea\u5df1\u7684\u9053\u7406\u3002 \u3000\u3000\u4f46\u8eab\u53d7\u5251\u795e\u9648\u5e0c\u6b63\u6d3b\u547d\u4e4b\u5fb7\uff0c\u65b9\u96ea\u5b9c\u7eb5\u7136\u5bf9\u4ed6\u518d\u8fc7\u5206\u4e9b\uff0c\u4ed6\u4e5f\u4e0d\u4f1a\u52a8\u6012\u7684\u3002 \u3000\u3000\u662f\u4ee5\uff0c\u4ed6\u95fb\u8a00\u4e4b\u540e\uff0c\u5374\u662f\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\u8bf4\u7684\u662f\uff0c\u7533\u67d0\u5374\u8981\u7b49\u7740\u77a7\u77a7\u8fd9\u6765\u6b64\u4e4b\u4eba\uff0c\u7a76\u7adf\u662f\u4e2a\u4ec0\u4e48\u4e1c\u897f\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u51b7\u51b7\u9053\uff1a\u201c\u5728\u4e0b\u4f46\u613f\u7533\u5927\u4fa0\u6ca1\u6709\u8bf4\u9519\uff0c\u5426\u5219\uff0c\u53ea\u6015\u665a\u8f88\u5e08\u53d4\u56de\u6765\uff0c\u7533\u5927\u4fa0\u5c31\u65e0\u8a00\u4ee5\u5bf9\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8bdd\u97f3\u752b\u843d\uff0c\u53ea\u89c1\u90a3\u77f3\u576a\u524d\u4fa7\u7684\u6797\u6728\u4e4b\u4e2d\uff0c\u5df2\u53d1\u89c9\u5927\u6b65\u8d70\u6765\u4e24\u540d\u6b66\u6797\u4eba\u7269\u3002 \u3000\u3000\u7533\u884c\u65f6\u5168\u8eab\u4e00\u9707\uff0c\u8f6c\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u8fd9\u73b0\u8eab\u5367\u4e91\u5eb5\u524d\u7684\u4e24\u4eba\uff0c\u4e00\u9ad8\u4e00\u77ee\uff0c\u4ff1\u662f\u81ea\u5df1\u4ece\u672a\u89c1\u8fc7\u4e4b\u58eb\u3002\u4e0d\u7981\u73af\u76ee\u66b4\u7741\uff0c\u5b8f\u58f0\u5927\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u80c6\u6562\u591c\u95ef\u5367\u4e91\u5eb5\u4f5b\u95e8\u5723\u5730\uff0c\u8fd8\u4e0d\u5feb\u5feb\u62a5\u4e0a\u540d\u6765\u9886\u8d23\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u65f6\u4e5f\u51b7\u559d\u9053\uff1a\u201c\u4f60\u4eec\u662f\u5e72\u4ec0\u4e48\u7684\uff1f\u8fd9\u5ce8\u5d4b\u91d1\u9876\u5c82\u662f\u64c5\u95ef\u4e4b\u5730\uff01\u201d \u3000\u3000\u4e24\u4eba\u8bdd\u97f3\u752b\u6b47\uff0c\u53ea\u89c1\u90a3\u8eab\u6750\u9ad8\u5927\uff0c\u4f46\u53c8\u7626\u5f97\u5b9b\u5982\u4e00\u62a5\u7af9\u7aff\u7acb\u5728\u5730\u4e0a\u7684\u9053\u88c5\u8001\u4eba\uff0c\u5ffd\u7136\u602a\u7b11\u9053\uff1a\u201c\u6797\u84dd\u4ed9\u53ef\u5728\u5ea7\u4e2d\u5417\uff1f\u8fd8\u4e0d\u53eb\u5979\u51fa\u6765\u53e9\u89c1\u5357\u5929\u4e8c\u5723\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u5f97\u5fc3\u4e2d\u4e00\u5446\u6697\u9053\uff1a\u201c\u6797\u82b7\u4ed9\u662f\u6dee\uff0c\u96be\u9053\u662f\u795e\u5c3c\u4fd7\u5bb6\u7684\u540d\u59d3\u5417\uff1f\u201d \u3000\u3000\u4ed6\u5bfb\u601d\u672a\u5b9a\uff0c\u7533\u884c\u65f6\u5df2\u7136\u51b7\u7b11\u9053\uff1a\u201c\u7533\u67d0\u6614\u5e74\u53ef\u66fe\u542c\u8bf4\u3002\u5728\u90a3\u70b9\u82cd\u5c71\u4e2d\u51fa\u4e86\u4e24\u4e2a\u602a\u7269\uff0c\u4eba\u79f0\u2018\u5357\u5929\u4e8c\u602a\u2019\u4e00\u4e2a\u53eb\u505a\u7af9\u9053\u77bf\u9ad8\u51e4\uff0c\u4e00\u4e2a\u53eb\u505a\u846b\u53df\u8096\u65e0\u75d5\uff0c\u770b\u6765\u5c31\u662f\u4e24\u4f4d\u4e86\uff0c\u4f46\u7533\u67d0\u4e0d\u89e3\u7684\u662f\uff0c\u4e8c\u602a\u53c8\u51e0\u65f6\u5f97\u9053\u5347\u5929\uff0c\u6210\u4e86\u4e8c\u5723\u4e86\uff1f\u201d \u3000\u3000\u90a3\u9ad8\u7626\u7684\u9053\u4eba\u542c\u5f97\u7533\u884c\u65f6\u4e4b\u8a00\uff0c\u4e0d\u7981\u8138\u8272\u4e00\u53d8\u3002\u6b63\u5f85\u51fa\u53e3\u559d\u9a82\u4f46\u90a3\u7ad9\u5728\u4ed6\u8eab\u8fb9\uff0c\u9ad8\u4e0d\u53ca\u4e09\u5c3a\uff0c\u7c97\u5374\u4e0d\u6b62\u4e09\u5c3a\uff0c\u770b\u4e0a\u53bb\u771f\u5982\u540c\u4e00\u4e2a\u5927\u846b\u82a6\u822c\u7684\u79c3\u9876\u8001\u4eba\uff0c\u5f92\u7136\u5f20\u5634\u5927\u7b11\u9053\uff1a \u3000\u3000\u201c\u5927\u4e2a\u513f\uff0c\u4f60\u5982\u77e5\u9053\u8001\u592b\u4e8c\u5723\u540d\u53f7\uff0c\u8db3\u89c1\u4f60\u4e5f\u662f\u6b66\u6797\u4e2d\u6709\u540d\u5934\u7684\u4eba\u7269\u3002\u201d \u3000\u3000\u7533\u5f97\u65f6\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u533a\u533a\u7533\u884c\u65f6\u3002\u201d \u3000\u3000\u90a3\u79c3\u9876\u8001\u4eba\u4e24\u53ea\u5c0f\u773c\u4e00\u523b\uff0c\u5927\u7b11\u9053\uff1a\u201c\u7533\u884c\u65f6\u5417\uff1f\u8001\u592b\u5012\u4e5f\u4eff\u4f5b\u542c\u8bf4\u8fc7\u8fd9\u53f7\u540d\u5b57\uff0c\u53ea\u662f\u2026\u2026\u53ea\u662f\uff0c\u6b64\u65f6\u5374\u60f3\u4ed6\u4e0d\u8d77\u4e86\u3002\u201d \u3000\u3000\u77ee\u8001\u4eba\u81ea\u8bed\u672a\u6b47\uff0c\u90a3\u7af9\u9053\u77bf\u9ad8\u51e4\u5df2\u51b7\u51b7\u5730\u4e00\u7b11\uff0c\u5411\u7533\u884c\u65f6\u9053\uff1a\u201c\u5357\u5317\u9053\u4e0a\u6614\u5e74\u6709\u4e2a\u72ec\u884c\u4fa0\u76d7\uff0c\u4eba\u79f0\u91d1\u5200\u4e8c\u90ce\u7684\uff0c\u60f3\u5fc5\u5c31\u662f\u4f60\u8fd9\u83bd\u592b\u4e86\uff1f\u201d \u3000\u3000\u7533\u884c\u65f6\u6d53\u7709\u5ffd\u8f69\uff0c\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u7533\u67d0\u6b63\u662f\u6614\u65e5\u7684\u91d1\u5200\u4e8c\u90ce\u3002\u201d \u3000\u3000\u6562\u60c5\u4ed6\u5012\u4f3c\u662f\u4e3a\u6b64\u5927\u611f\u5feb\u6170\u3002 \u3000\u3000\u53c8\u77ee\u53c8\u7c97\u7684\u79c3\u9876\u8001\u4eba\u201c\u846b\u82a6\u53df\u201d\u8096\u65e0\u75d5\uff0c\u4e3e\u624b\u91cd\u91cd\u5730\u62cd\u4e86\u4e00\u4e0b\u79c3\u9876\uff0c\u5927\u7b11\u9053\uff0c\u201c\u662f\u4e86\uff01\u91d1\u5200\u4e8c\u90ce\u7533\u884c\u65f6\uff0c\u8001\u592b\u60f3\u8d77\u6765\u5566\u2026\u2026\u201d \u3000\u3000\u7b11\u58f0\u4e00\u655b\uff0c\u9661\u7136\u4e00\u6643\u79c3\u9876\uff0c\u4eba\u5df2\u6b3a\u5230\u7533\u884c\u65f6\u8eab\u524d\u5c3a\u8bb8\uff0c\u63a2\u624b\u6263\u5411\u7533\u884c\u65f6\u7684\u5de6\u624b\u8155\u8109\uff0c\u63a5\u9053\uff1a\u201c\u7533\u8001\u5f1f\uff0c\u8001\u592b\u8981\u8bd5\u8bd5\u4f60\u7684\u5de5\u592b\uff0c\u914d\u4e0d\u914d\u88ab\u4eba\u79f0\u4f5c\u4fa0\u76d7\uff01\u201d \u3000\u3000\u7533\u884c\u65f6\u4f46\u611f\u773c\u524d\u4eba\u5f71\u4e00\u82b1\uff0c\u8fde\u8f6c\u5ff5\u90fd\u672a\u6765\u5f97\u53ca\uff0c\u5de6\u624b\u4e00\u9707\uff0c\u90a3\u8096\u65e0\u75d5\u7684\u4e94\u6307\uff0c\u5df2\u5982\u94c1\u7b8d\u4e00\u822c\uff0c\u6263\u7262\u4e86\u81ea\u5df1\uff0c\u7533\u884c\u65f6\u5fc3\u4e2d\u9a87\u7136\u5927\u9707\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u8001\u602a\uff0c\u679c\u7136\u4e0d\u662f\u6613\u4e0e\u4e4b\u8f88\u2026\u2026\u201d \u3000\u3000\u53e3\u4e2d\u5374\u662f\u6012\u58f0\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u4f5c\u4ec0\u4e48\uff1f\u8fd8\u4e0d\u7ed9\u6211\u6492\u624b\uff01\u201d \u3000\u3000\u529b\u8d2f\u5de6\u81c2\uff0c\u594b\u529b\u5f80\u5916\u4e00\u5e26\uff0c\u5de6\u638c\u66f4\u662f\u515c\u80f8\u4e00\u62f3\uff0c\u51fb\u5411\u8096\u65e0\u75d5\u80f8\u8179\u4e4b\u95f4\u3002 \u3000\u3000\u8096\u65e0\u75d5\u8089\u56e2\u56e2\u7684\u80d6\u8138\u4e4b\u4e0a\uff0c\u7565\u73b0\u8bb6\u5f02\u4e4b\u8272\uff0c\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u4e0d\u574f\uff0c\u4f60\u8001\u5f1f\u679c\u7136\u6709\u4e00\u624b\u2026\u2026\u201d\u53f3\u624b\u4e00\u677e\uff0c\u5de6\u81c2\u5374\u5411\u4e0a\u4e00\u632f\uff0c\u7533\u884c\u65f6\u53ea\u89c9\u5f97\u5de6\u8155\u88ab\u634f\u4e4b\u5904\u5df2\u677e\uff0c\u4f46\u81ea\u5df1\u653b\u51fa\u7684\u5de6\u62f3\uff0c\u5374\u88ab\u8096\u65e0\u75d5\u7684\u540e\u90e8\u649e\u5f97\u5982\u906d\u94c1\u9525\u731b\u51fb\u4e00\u822c\u7684\u75bc\u75db\uff0c\u5927\u60ca\u4e4b\u4e0b\uff0c\u609a\u7136\u6536\u624b\uff0c\u8dc3\u9000\u4e09\u5c3a\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u77bf\u9ad8\u98ce\u540a\u7709\u4e00\u8038\u7b11\u9053\uff1a\u201c\u5c0f\u5a03\u513f\uff0c\u90a3\u6797\u8292\u4ed9\u53ef\u5728\u5eb5\u4e2d\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u6325\u624b\u4e2d\u957f\u5251\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u5367\u4e91\u5eb5\u4e3b\uff0c\u4e43\u662f\u91d1\u9876\u795e\u5c3c\uff0c\u9601\u4e0b\u8981\u6211\u7684\u4eba\uff0c\u53ea\u6015\u4e0d\u5728\u6bd4\u5904\uff01\u201d \u3000\u3000\u77bf\u9ad8\u51e4\u5c16\u58f0\u5389\u559d\u9053\uff1a\u201c\u90a3\u6797\u82b7\u4ed9\u5c31\u662f\u91d1\u9876\u795e\u5c3c\uff0c\u5c0f\u5a03\u513f\u8fd8\u4e0d\u5feb\u5feb\u4e0e\u6211\u53eb\u5979\u51fa\u6765\u2026\u2026\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u6697\u9053\uff1a\u8fd9\u5c31\u662f\u4e86\uff0c\u8fd9\u4e24\u4e2a\u8001\u602a\u679c\u771f\u662f\u6765\u627e\u5e08\u53d4\u7684\uff0c\u4e0d\u77e5\u4ed6\u4eec\u7a76\u7adf\u4e3a\u4e86\u4f55\u6545\uff0c\u6211\u603b\u5f97\u95ee\u660e\u767d\u624d\u597d\u51b3\u5b9a\u600e\u751f\u5e94\u5bf9\u2026\u2026 \u3000\u3000\u4ed6\u8f6c\u5ff5\u53ca\u6b64\uff0c\u7acb\u5373\u4e00\u62b1\u957f\u5251\uff0c\u65cb\u793c\u9053\uff1a\u201c\u795e\u5c3c\u4e0d\u5728\u5eb5\u4e2d\uff0c\u4e0d\u77e5\u4e24\u4f4d\u627e\u5979\u8001\u4eba\u5bb6\u4f5c\u751a\uff1f\u201d \u3000\u3000\u90a3\u77bf\u9ad8\u98ce\u95fb\u8a00\u8138\u4e0a\u795e\u8272\u53d8\u5f97\u5341\u5206\u7684\u51c4\u5389\uff0c\u6012\u559d\u9053\uff1a\u201c\u5c0f\u5a03\u513f\uff0c\u90a3\u6797\u82b7\u4ed9\u679c\u771f\u4e0d\u5728\u5eb5\u4e2d\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u795e\u5c3c\u4e0b\u5c71\uff0c\u5df2\u6709\u516b\u4e5d\u5929\u4e86\u3002\u201d \u3000\u3000\u8096\u65e0\u75d5\u8fd9\u65f6\u6b63\u4e3e\u6b65\u5411\u65b9\u96ea\u5b9c\u8d70\u6765\uff0c\u95fb\u8a00\u5927\u7b11\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u6562\u7528\u8c0e\u8a00\u9a97\u4eba\u5417\uff1f\u8001\u592b\u65e9\u5df2\u6253\u542c\u660e\u767d\uff0c\u90a3\u6797\u82b7\u4ed9\u81ea\u4ece\u5931\u610f\u60c5\u573a\uff0c\u9690\u5c45\u8fd9\u5367\u4e91\u5eb5\u4e2d\uff0c\u6570\u5341\u5e74\u672a\u51fa\u5eb5\u95e8\u4e00\u6b65\uff0c\u600e\u5730\u5bb9\u8001\u592b\u95fb\u8baf\u8ffd\u6765\uff0c\u5979\u5374\u6b63\u597d\u4e0b\u5c71\u53bb\u4e86\u5462\uff0c\u5c0f\u5b50\uff0c\u4f60\u8fd9\u756a\u8c0e\u6d3b\uff0c\u5c82\u975e\u7f16\u5f97\u592a\u4e0d\u9ad8\u660e\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u5fc3\u4e2d\u6674\u6697\u5403\u60ca\uff0c\u5fd6\u9053\uff1a\u201c\u542c\u8fd9\u4e24\u4e2a\u602a\u7269\u53e3\u6c14\uff0c\u679c\u7136\u662f\u5411\u5e08\u53d4\u5bfb\u4ec7\u7684\uff0c\u81ea\u5df1\u53ef\u5f97\u5c0f\u5fc3\u5e94\u4ed8\u624d\u597d\u2026\u2026\u201d \u3000\u3000\u5ff5\u5934\u4e00\u5b9a\uff0c\u987f\u65f6\u6717\u58f0\u9053\uff1a\u201c\u795e\u5c3c\u4e43\u662f\u533a\u533a\u5e08\u53d4\uff0c\u773c\u4e0b\u786e\u5b9e\u4e0d\u5728\u5eb5\u4e2d\uff0c\u4e24\u4f4d\u5982\u662f\u6709\u4ec0\u4e48\u91cd\u8981\u4e4b\u4e8b\uff0c\u544a\u8bc9\u533a\u533a\u6216\u662f\u96ea\u6d9b\u5e08\u59b9\u4e5f\u884c\u3002\u201d \u3000\u3000\u8096\u65e0\u75d5\u77a7\u7740\u65b9\u96ea\u5b9c\u534a\u54cd\uff0c\u5927\u6982\u89c9\u51fa\u65b9\u96ea\u5b9c\u6b64\u8bf4\uff0c\u4f3c\u662f\u5b9e\u60c5\uff0c\u5f53\u4e0b\u56de\u5934\u5411\u77bf\u9ad8\u51e4\u9053\uff1a \u3000\u3000\u201c\u77bf\u5144\uff0c\u8fd9\u5c0f\u5b50\u4e00\u8138\u771f\u8bda\uff0c\u4e0d\u4f3c\u8bf4\u8c0e\u4e4b\u4eba\uff0c\u53ea\u6015\u90a3\u6797\u82b7\u4ed9\u679c\u771f\u4e0d\u5728\u5eb5\u5185\u5462\uff1f\u201d \u3000\u3000\u77bf\u9ad8\u51e4\u51cc\u5389\u7684\u76ee\u5149\u5728\u65b9\u96ea\u5b9c\u8eab\u4e0a\u6a2a\u626b\u4e86\u4e00\u904d\uff0c\u5ffd\u5730\u602a\u7b11\u9053\uff1a\u201c\u8096\u5144\uff0c\u8001\u7684\u4e0d\u5728\uff0c\u54b1\u4eec\u5c31\u5148\u62ff\u4e0b\u5c0f\u7684\uff0c\u4f60\u770b\u5982\u4f55\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u60f3\uff1a\u201c\u8fd9\u59d3\u7fdf\u7684\u771f\u662f\u4e2a\u6df7\u5e10\uff0c\u7a0d\u65f6\u5982\u662f\u514d\u4e0d\u4e86\u8981\u52a8\u624b\uff0c\u81ea\u5df1\u5c31\u8981\u8ba9\u4ed6\u5148\u5c1d\u5c1d\u6211\u5e08\u7956\u9f99\u884c\u516b\u5251\u7684\u5229\u5bb3\u3002\u201d \u3000\u3000\u90a3\u8096\u65e0\u75d5\u542c\u5f97\u77bf\u9ad8\u51e4\u7684\u8bdd\u97f3\u4ee5\u540e\uff0c\u7adf\u662f\u6c89\u541f\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u77bf\u5144\uff0c\u4ee5\u5927\u538b\u5c0f\uff0c\u80dc\u4e4b\u4e0d\u6b66\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u9053\uff1a\u201c\u8fd9\u59d3\u8096\u7684\u4e3a\u4eba\u8fd8\u7b97\u4e0d\u9519\u2026\u2026\u201d \u3000\u3000\u77bf\u9ad8\u51e4\u5ffd\u7136\u5389\u58f0\u9053\uff1a\u201c\u8096\u5144\uff0c\u4f60\u83ab\u975e\u5fd8\u4e86\u65ad\u5d14\u4e4b\u6068\u5417\uff1f\u201d \u3000\u3000\u8096\u65e0\u75d5\u84e6\u5730\u76ee\u5149\u4e00\u4eae\uff0c\u62ac\u8d77\u5de6\u624b\uff0c\u5728\u8ff7\u8499\u7684\u6708\u8272\u4e0b\uff0c\u770b\u4e86\u4e00\u773c\uff0c\u957f\u957f\u4e00\u53f9\u9053\uff1a\u201c\u8096\u65e0\u75d5\u6ca1\u6709\u5fd8\u8bb0\u8fd9\u6839\u5c0f\u6307\u88ab\u524a\u4e4b\u6068\uff0c\u4f46\u662f\u5f53\u5e74\u65ad\u6307\u4e4b\u4eba\uff0c\u4e43\u662f\u6797\u82b7\u4ed9\uff0c\u4e0d\u662f\u773c\u524d\u7684\u8fd9\u4e2a\u5c0f\u5b50\uff0c\u8fc1\u6012\u4e8e\u540e\u751f\u665a\u8f88\uff0c\u5374\u662f\u8096\u67d0\u4e0d\u5c51\u4e3a\u3002\u201d \u3000\u3000\u77bf\u9ad8\u51e4\u95fb\u8a00\u5389\u58f0\u9053\uff1a\u201c\u8096\u5144\uff0c\u6797\u82b7\u4ed9\u65ad\u4f60\u4e00\u6307\uff0c\u4f60\u8981\u627e\u4ed6\u672c\u4eba\uff0c\u77bf\u67d0\u81ea\u4e0d\u53cd\u5bf9\uff0c\u4f46\u5979\u6bc1\u6211\u7231\u5f92\u4e4b\u4ec7\uff0c\u77bf\u67d0\u5374\u4e0d\u613f\u53ea\u53ca\u4e8e\u5979\u81ea\u8eab\u800c\u5df2\u2026\u2026\u201d\u8bdd\u97f3\u4e00\u987f\uff0c\u76ee\u5149\u9661\u7136\u540e\u5411\u65b9\u96ea\u5b9c\u548c\u96ea\u6d9b\u8eab\u4e0a\uff0c\u51b7\u7b11\u63a5\u9053\uff1a\u201c\u8001\u592b\u5c31\u5148\u62ff\u5979\u8fd9\u4e24\u4e2a\u665a\u8f88\u8bd5\u624b\uff0c\u653e\u5979\u4e5f\u77e5\u9053\u5931\u5f92\u4e4b\u6068\uff0c\u7a76\u7adf\u662f\u4f55\u7b49\u6ecb\u5473\uff01\u201d \u3000\u3000\u8096\u65e0\u75d5\u542c\u5f97\u4f3c\u662f\u5446\u4e86\u4e00\u5446\u9053\uff1a\u201c\u77bf\u5144\uff0c\u4f60\u5c82\u53ef\u4e0d\u987e\u5357\u5929\u4e8c\u5723\u7684\u540d\u671b\uff0c\u51fa\u624b\u5bf9\u4ed8\u540e\u751f\u4e4b\u8f88\u5462\uff1f\u4f9d\u8001\u5148\u4e4b\u89c1\uff0c\u54b1\u4eec\u4e0d\u5982\u7b49\u90a3\u6797\u82b7\u4ed9\u56de\u5c71\u518d\u6765\u2026\u2026\u201d \u3000\u3000\u77bf\u9ad8\u98ce\u51b7\u7b11\u9053\uff1a\u201c\u8096\u5144\uff0c\u8fd9\u6740\u5f92\u4e4b\u6068\uff0c\u77bf\u67d0\u5df2\u5fcd\u75db\u7b49\u5f85\u4e86\u4e8c\u5341\u591a\u5e74\uff0c\u4eca\u65e5\u6709\u6b64\u673a\u4f1a\uff0c\u53eb\u77bf\u67d0\u600e\u80af\u653e\u8fc7\uff1f\u8096\u5144\u4e0d\u5fc5\u591a\u8bf4\u2026\u2026\u201d \u3000\u3000\u8bdd\u97f3\u672a\u5df2\uff0c\u53f3\u624b\u5df2\u5411\u65b9\u96ea\u5b9c\u80a9\u5934\u6293\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u65f6\u5df2\u7ecf\u64a9\u7136\u773c\u524d\u5c40\u52bf\uff0c\u53ea\u6015\u4e0d\u89c1\u4e2a\u771f\u7ae0\uff0c\u8fd9\u59d3\u77bf\u7684\u4e00\u5b9a\u4f1a\u4e0d\u6b7b\u5fc3\u4e86\u3002\u77bf\u9ad8\u51e4\u638c\u52bf\u4e00\u53d1\uff0c\u4ed6\u7acb\u5373\u53cd\u624b\u5411\u4e0a\u8fce\u53bb\uff0c\u53e3\u4e2d\u559d\u9053\uff1a\u201c\u59d3\u77bf\u7684\uff0c\u4f60\u5f88\u65e0\u803b\u2026\u2026\u201d \u3000\u3000\u84e6\u7136\uff0c\u4e00\u6761\u4eba\u5f71\u7535\u5c04\u800c\u6765\uff0c\u65b9\u96ea\u5b9c\u8033\u4e2d\u540c\u65f6\u4f20\u5165\u90a3\u91d1\u5200\u4e8c\u90ce\u7533\u884c\u65f6\u7684\u66b4\u559d\u4e4b\u58f0\u9053\uff1a \u3000\u3000\u201c\u5357\u5929\u4e8c\u602a\u539f\u6765\u53ea\u662f\u4ed7\u52bf\u6b3a\u4eba\uff0c\u4e0d\u77e5\u81ea\u91cd\u7684\u5c0f\u4eba\uff0c\u5012\u53eb\u7533\u67d0\u597d\u7b11\u4e86\u3002\u201d \u3000\u3000\u7830\u7136\u4e00\u58f0\u5de8\u9707\u4f20\u6765\uff0c\u90a3\u77bf\u9ad8\u51e4\u9661\u5730\u9000\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u4f46\u90a3\u7533\u884c\u65f6\u5374\u88ab\u77bf\u9ad8\u51e4\u9707\u5f97\u51d1\u7a7a\u98de\u843d\u4e08\u8bb8\u4e4b\u5916\u3002 \u3000\u3000\u663e\u7136\u662f\u7533\u884c\u65f6\u5728\u5185\u529b\u4fee\u4e3a\u4e4b\u4e0a\uff0c\u5dee\u7684\u5f88\u591a\u3002 \u3000\u3000\u77bf\u9ad8\u51e4\u602a\u58f0\uff0c\u559d\u9053\uff1a\u201c\u59d3\u7533\u7684\uff0c\u5c31\u51b2\u4f60\u8fd9\u53e5\u8bdd\uff0c\u8001\u592b\u4eca\u591c\u5c31\u653e\u4f60\u4e0d\u5f97\u2026\u2026\u201d \u3000\u3000\u9ec4\u8863\u4eba\u5f71\u4e00\u6643\uff0c\u7535\u4f3c\u6251\u5411\u7533\u884c\u65f6\u800c\u4f1a\uff0c\u5f3a\u52b2\u7684\u638c\u529b\uff0c\u631f\u7740\u96f7\u9706\u4e07\u94a9\u4e4b\u52bf\uff0c\u72c2\u626b\u800c\u53bb\u3002 \u3000\u3000\u7533\u884c\u65f6\u81ea\u77e5\u5185\u529b\u4e0d\u5982\u5bf9\u65b9\u751a\u8fdc\uff0c\u77bf\u9ad8\u98ce\u4f5c\u52bf\u5916\u6765\uff0c\u4ed6\u81ea\u662f\u4e0d\u6562\u786c\u63a5\uff0c\u4e00\u65cb\u8eab\u5c31\u6e9c\u5f00\u4e86\u4e24\u4e08\uff0c\u4f46\u53e3\u4e2d\u5374\u5475\u5475\u7b11\u9053\uff1a\u201c\u7533\u67d0\u4e00\u751f\u5200\u5934\u8214\u8840\uff0c\u5012\u4e5f\u6d3b\u4e86\u56db\u5341\u591a\u5c81\uff0c\u5c0a\u9a7e\u8fd9\u53e5\u653e\u7533\u67d0\u4e4b\u8a00\uff0c\u4e0d\u77e5\u7528\u610f\u4f55\u5728\uff1f\u201d \u3000\u3000\u8f70\u7136\u4e24\u58f0\u5de8\u9707\u4f20\u6765\uff0c\u6253\u65ad\u4e86\u7533\u884c\u65f6\u7684\u8bdd\u8bed\uff0c\u65b9\u96ea\u5b9c\u62ac\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u90a3\u7533\u884c\u65f6\u5148\u524d\u7acb\u8eab\u4e4b\u5904\uff0c\u5df2\u73b0\u51fa\u4e86\u4e24\u4e2a\u5c3a\u8bb8\u5927\u5751\uff0c\u788e\u77f3\u7eb7\u98de\uff0c\u843d\u7247\u672a\u5b9a\u3002\u8fd9\u77bf\u9ad8\u51e4\u7684\u638c\u529b\uff0c\u679c\u7136\u5341\u5206\u60ca\u4eba\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u77a7\u5f97\u53cc\u7709\u4e00\u5254\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u5357\u5929\u4e8c\u602a\u7684\u6b66\u529f\uff0c\u663e\u7136\u8981\u6bd4\u7533\u884c\u65f6\u9ad8\u660e\u751a\u591a\uff0c\u81ea\u5df1\u518d\u4e0d\u51fa\u624b\uff0c\u53ea\u6015\u7533\u884c\u65f6\u5b9a\u7136\u96be\u9003\u5bf9\u65b9\u8fa3\u624b\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u60f3\u5230\u6b64\u5904\uff0c\u7acb\u5373\u4e0d\u518d\u72b9\u8c6b\uff0c\u957f\u5251\u4e00\u632f\uff0c\u5927\u6b65\u8d70\u5230\u77bf\u9ad8\u51e4\u8eab\u524d\uff0c\u51b7\u51b7\u5730\u559d\u9053\uff1a \u3000\u3000\u201c\u9601\u4e0b\u65e2\u662f\u4e3a\u4e86\u62a5\u90a3\u6740\u5f92\u4e4b\u4ec7\u800c\u6765\uff0c\u533a\u533a\u65b9\u96ea\u5b9c\u76ee\u4e0b\u5df2\u53ef\u7b97\u5f97\u5e08\u53d4\u7684\u5f1f\u5b50\uff0c\u5982\u662f\u9601\u4e0b\u80dc\u5f97\u4e86\u65b9\u67d0\u4eba\uff0c\u4f60\u5c3d\u53ef\u62a5\u590d\u4e86\u3002\u201d \u3000\u3000\u90a3\u77bf\u9ad8\u51e4\u672c\u5c31\u4e0d\u613f\u8ddf\u7533\u884c\u65f6\u52a8\u624b\uff0c\u95fb\u8a00\u81ea\u662f\u6b63\u5408\u5fc3\u610f\uff0c\u6840\u6840\u957f\u7b11\u58f0\u4e2d\uff0c\u6389\u5934\u5411\u8eab\u540e\u7684\u8096\u65e0\u75d5\u9053\uff1a\u201c\u8096\u5144\uff0c\u8fd9\u53ef\u662f\u4eba\u5bb6\u627e\u4e0a\u77bf\u67d0\u7684\u2026\u2026\u201d \u3000\u3000\u8096\u65e0\u75d5\u4e0e\u4ed6\u5408\u79f0\u5357\u5929\u4e8c\u602a\uff0c\u5e38\u65e5\u76f8\u5904\uff0c\u81ea\u7136\u5f88\u4e86\u89e3\u77bf\u9ad8\u51e4\u7684\u4e3a\u4eba\uff0c\u95fb\u8a00\u4e4b\u4e0b\uff0c\u5df2\u77e5\u77bf\u9ad8\u98ce\u52a8\u4e86\u6740\u673a\uff0c\u60cb\u501f\u5730\u770b\u4e86\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u6447\u5934\u82e6\u7b11\u9053\uff1a\u201c\u77bf\u5144\uff0c\u5f97\u653e\u624b\u65f6\u4e14\u653e\u624b\uff0c\u80fd\u9976\u4eba\u5904\u6682\u9976\u4eba\uff0c\u8fd9\u4f4d\u5c0f\u5144\u5f1f\u9aa8\u9abc\u6e05\u5947\uff0c\u4e43\u662f\u5927\u5bff\u4e4b\u5f81\uff0c\u77bf\u5144\u83ab\u8981\u505a\u7684\u592a\u8fc7\u4efd\uff0c\u6298\u4e86\u81ea\u5df1\u7684\u9634\u5fb7\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4ed8\u9053\uff1a\u201c\u542c\u8fd9\u8096\u65e0\u75d5\u8bf4\u8bdd\uff0c\u5012\u4f3c\u4e0d\u662f\u5927\u51f6\u5927\u6076\u4e4b\u4eba\uff0c\u83ab\u975e\u8fd9\u5357\u5929\u4e8c\u602a\u7adf\u662f\u4e00\u6076\u4e00\u5584\u5417\uff1f\u201d \u3000\u3000\u5ffd\u542c\u90a3\u77bf\u9ad8\u51e4\u51b7\u51b7\u5730\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8096\u5144\uff0c\u77bf\u67d0\u4e00\u751f\u884c\u4e8b\uff0c\u6700\u6068\u90a3\u5987\u4eba\u4e4b\u4ec1\uff0c\u4e8c\u5341\u591a\u5e74\u82e6\u5fc3\uff0c\u8096\u5144\u83ab\u975e\u5fd8\u6000\u4e86\u5417\uff1f\u5144\u5f1f\u6b64\u523b\u5df2\u662f\u7bad\u5728\u5f26\u4e0a\uff0c\u6b32\u7f62\u4e0d\u80fd\u4e86\u2026\u2026\u201d\u8bdd\u97f3\u4e00\u987f\uff0c\u6389\u5934\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5c0f\u5a03\u513f\uff0c\u5982\u662f\u4f60\u6b7b\u5728\u4e5d\u6cc9\uff0c\u5fc3\u6709\u4e0d\u7518\uff0c\u4e0d\u59a8\u7b49\u4f60\u90a3\u5e08\u53d4\u62b5\u8fbe\u9634\u90fd\u4e4b\u65e5\uff0c\u5411\u5979\u7d22\u547d\u53bb\u5427\uff01\u201d\u542c\u4ed6\u53e3\u6c14\uff0c\u65b9\u96ea\u5b9c\u4f3c\u5df2\u6b7b\u5b9a\u3002 \u3000\u3000\u4f46\u65b9\u96ea\u5b9c\u5374\u4e0d\u5927\u5584\u4e8e\u8f9e\u4ee4\uff0c\u95fb\u8a00\u53ea\u662f\u6de1\u6de1\u5e94\u9053\uff1a\u201c\u9601\u4e0b\u597d\u50cf\u6709\u628a\u63e1\u5f97\u5f88\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u77bf\u9ad8\u51e4\u563f\u563f\u4e00\u7b11\u9053\uff1a\u201c\u8001\u592b\u770b\u4f60\u5e74\u5e7c\uff0c\u5148\u8ba9\u4f60\u4e09\u62db\u673a\u5148\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8138\u8272\u4e00\u53d8\uff0c\u53f3\u624b\u5b9d\u5251\u6b63\u5f85\u5e73\u80f8\u523a\u51fa\uff0c\u7a81\u7136\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6697\u9053\uff1a\u4ed6\u4e43\u662f\u7a7a\u7740\u4e24\u624b\u4e4b\u4eba\uff0c\u6211\u5c82\u53ef\u7528\u5251\u535a\u6740\u624b\u65e0\u5bf8\u94c1\u4e4b\u4eba\uff1f \u3000\u3000\u5f53\u4e0b\u5fae\u5fae\u4e00\u7b11\uff0c\u6296\u624b\u5c06\u5ba4\u5251\u629b\u5411\u4e08\u8bb8\u4e4b\u5916\u7684\u96ea\u6d9b\u5973\u5c3c\uff0c\u9053\uff1a\u201c\u96ea\u5e08\u59b9\uff0c\u8bf7\u4ee3\u5c0f\u5144\u4ed7\u5251\u63a0\u9635\u2026\u2026\u201d\u53f3\u638c\u5f53\u80f8\u4e00\u7acb\uff0c\u5411\u77bf\u9ad8\u51e4\u9053\uff1a\u201c\u9601\u4e0b\u5e74\u9f84\u9ad8\u51fa\u5728\u4e0b\u751a\u591a\uff0c\u65b9\u67d0\u7406\u5e94\u5949\u8ba9\u4e24\u62db\u624d\u662f\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8bdd\u97f3\u752b\u843d\uff0c\u77bf\u9ad8\u51e4\u5df2\u7136\u6c14\u5f97\u8138\u8272\u94c1\u9752\uff0c\u5389\u543c\u4e00\u58f0\u9053\uff1a\u201c\u8001\u592b\u77a7\u4f60\u80fd\u518d\u72c2\u591a\u4e45\u2026\u2026\u201d\u543c\u53eb\u58f0\u4e2d\uff0c\u5927\u8896\u4e00\u6325\uff0c\u76f4\u5411\u65b9\u96ea\u5b9c\u5377\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u672c\u5c31\u5728\u9ed8\u60f3\u7740\u5e08\u7236\u4f20\u6388\u7684\u638c\u6cd5\uff0c\u5982\u4f55\u65b9\u80fd\u6070\u5230\u597d\u5904\u7684\u5e94\u4ed8\u5bf9\u65b9\u7684\u653b\u52bf\uff0c\u6562\u60c5\u4ed6\u867d\u7136\u4e60\u827a\u4e09\u5e74\u591a\uff0c\u4f46\u5374\u4ece\u65e0\u4e0e\u4eba\u5bf9\u624b\u76f8\u535a\u7684\u7ecf\u9a8c\uff0c\u662f\u4ee5\uff0c\u4ed6\u7eb5\u7136\u77e5\u9053\u81ea\u5df1\u7684\u6b66\u529f\u4e0d\u4f1a\u6bd4\u522b\u4eba\u5dee\uff0c\u4f46\u5374\u4e0d\u514d\u5728\u5fc3\u4e2d\u6709\u4e9b\u5600\u5495\u3002 \u3000\u3000\u8fd9\u65f6\u77bf\u9ad8\u51e4\u9661\u7136\u6325\u8896\u5377\u6765\uff0c\u8fd9\u4e0e\u4ed6\u4e8b\u5148\u6240\u6599\u60f3\u7684\u5927\u4e3a\u4e0d\u540c\uff0c\u5251\u7709\u4e00\u63d0\uff0c\u5df2\u65e0\u6687\u601d\u7d22\u4ec0\u4e48\uff0c\u53f3\u81c2\u4e00\u62ac\uff0c\u4eae\u638c\u56de\u51fb\u800c\u53bb\u3002 \u3000\u3000\u4ed6\u81ea\u5df1\u5e76\u4e0d\u77e5\u9053\uff0c\u9648\u5e0c\u6b63\u82b1\u8d39\u4e86\u4e09\u5e74\u516d\u4e2a\u6708\u7684\u65f6\u95f4\uff0c\u5916\u501f\u836f\u529b\u4e4b\u529f\uff0c\u5185\u501f\u65b9\u96ea\u5b9c\u575a\u6bc5\u5fc3\u6027\uff0c\u65e9\u5df2\u4f7f\u4ed6\u8131\u80ce\u6362\u9aa8\uff0c\u7531\u4e00\u4e2a\u5929\u751f\u4f53\u8d28\u4e0d\u5f3a\u7684\u5b7a\u5b50\uff0c\u53d8\u6210\u4e86\u4e00\u4f4d\u8eab\u5177\u65e0\u4e0a\u5185\u5bb6\u5de5\u592b\u7684\u4e00\u6d41\u9ad8\u624b\uff0c\u8fd9\u4e00\u65e6\u4eae\u638c\u53cd\u51fb\uff0c\u5176\u52b2\u9053\u4e4b\u5f3a\uff0c\u5df2\u7ecf\u4e0d\u6bd4\u9648\u5e0c\u6b63\u51fa\u9053\u6c5f\u6e56\u4e4b\u65f6\u7a0d\u900a\uff0c\u662f\u6545\u90a3\u77bf\u9ad8\u51e4\u6240\u53d1\u7684\u4e03\u6210\u771f\u529b\uff0c\u4e00\u65e6\u78b0\u5230\u4e86\u65b9\u96ea\u5b9c\u7684\u638c\u529b\uff0c\u970e\u65f6\u88ab\u5012\u5377\u800c\u56de\uff0c\u6084\u65e0\u58f0\u606f\u5730\u649e\u5411\u81ea\u5df1\u80f8\u524d\u3002 \u3000\u3000\u77bf\u9ad8\u51e4\u4f5c\u68a6\u4e5f\u6ca1\u60f3\u5230\u773c\u524d\u8fd9\u4f4d\u5b7a\u5b50\u7684\u6b66\u529f\u5982\u6b64\u9ad8\u660e\uff0c\u9a87\u7136\u5927\u60ca\u4e4b\u4e0b\uff0c\u9519\u975e\u4ed6\u5e74\u8001\u6210\u7cbe\uff0c\u5bf9\u654c\u7ecf\u9a8c\u53c8\u591a\uff0c\u4e00\u65e6\u89c9\u51fa\u4e0d\u5bf9\uff0c\u7acb\u5373\u98d8\u8eab\u659c\u5f15\u516b\u5c3a\uff0c\u9003\u5f00\u4e86\u638c\u950b\uff0c\u53ea\u6015\u8fd9\u4f4d\u7af9\u9053\u5f53\u573a\u5c31\u5df2\u843d\u8d25\u3002 \u3000\u3000\u8096\u65e0\u75d5\u5728\u65c1\u53ea\u77a7\u5f97\u8131\u53e3\u5927\u7b11\u9053\uff1a\u201c\u5b7a\u5b50\u53ef\u6559\uff0c\u5c0f\u4f19\u5b50\uff0c\u4f60\u4e0d\u542b\u7cca\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u773c\u89c1\u77bf\u9ad8\u98ce\u72fc\u72c8\u795e\u60c5\uff0c\u5c31\u77e5\u9053\u81ea\u5df1\u4e00\u638c\u5df2\u7ecf\u594f\u6548\uff0c\u663e\u7136\u662f\u5bf9\u65b9\u7684\u529f\u529b\u4e0d\u6bd4\u81ea\u5df1\u5f3a\uff0c\u987f\u65f6\u8c6a\u6c14\u5927\u53d1\uff0c\u542c\u5f97\u8096\u65e0\u75d5\u8bdd\u97f3\u4e4b\u540e\uff0c\u4e0d\u81ea\u89c9\u7684\u8131\u53e3\u7b11\u9053\uff1a\u201c\u591a\u627f\u8096\u8001\u5938\u5956\uff0c\u65b9\u67d0\u611f\u6fc0\u5f97\u5f88\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5728\u8bd5\u51fa\u81ea\u5df1\u529f\u529b\u4ee5\u540e\uff0c\u5f53\u7136\u5df2\u65e0\u602f\u573a\u4e4b\u610f\uff0c\u6717\u7b11\u4e00\u58f0\uff0c\u6325\u638c\u53cd\u51fb\u8fc7\u53bb\u3002 \u3000\u3000\u77bf\u9ad8\u51e4\u653b\u51fa\u5341\u62db\u4ee5\u540e\uff0c\u638c\u52bf\u4e00\u53d8\uff0c\u62db\u62d3\u90fd\u662f\u81f4\u547d\u7684\u6253\u6cd5\uff0c\u638c\u51e4\u6307\u5f71\uff0c\u5f0f\u5f0f\u4e0d\u79bb\u65b9\u96ea\u5b9c\u5168\u8eab\u5927\u7a74\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u521d\u65f6\u591a\u5c11\u8fd8\u6709\u7740\u62ff\u5bf9\u65b9\u8bd5\u62db\u4e4b\u610f\uff0c\u6545\u800c\u51fa\u624b\u7684\u638c\u52bf\uff0c\u5e76\u4e0d\u600e\u4e48\u7280\u5229\uff0c\u4f46\u76ee\u7779\u77bf\u9ad8\u51e4\u7684\u6076\u6bd2\u638c\u529b\uff0c\u7adf\u662f\u968f\u65f6\u90fd\u6709\u81f4\u81ea\u5df1\u4e8e\u6b7b\u547d\u7684\u53ef\u80fd\u4e4b\u540e\uff0c\u5fc3\u4e2d\u4e0d\u7981\u6709\u6c14\uff0c\u6697\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4e2a\u8001\u6df7\u5e10\u5b8c\u5168\u4e0d\u9886\u6211\u624b\u4e0b\u7559\u60c5\u4e4b\u610f\uff0c\u6211\u53c8\u4f55\u5fc5\u5b58\u90a3\u539a\u9053\u4e4b\u5fc3\u5462\uff1f\u201d \u3000\u3000\u6562\u60c5\u4ed6\u6b64\u523b\u5ffd\u7136\u60f3\u8d77\uff0c\u6240\u8c13\u8bdb\u9b54\u9053\u7684\u5927\u4e1a\uff0c\u6b63\u8be5\u7531\u6d93\u6d93\u6ef4\u6ef4\u7684\u505a\u8d77\uff0c\u80fd\u591f\u9664\u53bb\u4e00\u4e2a\u6076\u4eba\uff0c\u5c31\u4e0d\u59a8\u9664\u53bb\u4e00\u4e2a\u4eba\uff0c\u8fd9\u77bf\u9ad8\u51e4\u662f\u6076\u4eba\u4e4b\u4e00\uff0c\u81ea\u5df1\u53c8\u4f55\u987b\u4e0d\u6562\u653e\u624b\u4f24\u4eba\uff1f \u3000\u3000\u5fc3\u5ff5\u53ca\u6b64\uff0c\u987f\u65f6\u638c\u52bf\u4e00\u53d8\uff0c\u4e0d\u518d\u50cf\u8bd5\u62db\u822c\u6e38\u95e8\uff0c\u4e00\u53e3\u6c14\u62cd\u51fa\u4e09\u62db\u5e08\u7236\u638c\u6cd5\uff0c\u5c06\u77bf\u9ad8\u51e4\u8feb\u5f97\u9000\u4e86\u4e94\u6b65\u3002 \u3000\u3000\u77bf\u9ad8\u51e4\u8d8a\u6253\u8d8a\u5fc3\u60ca\uff0c\u4ed6\u5df2\u7136\u662f\u51fa\u5c3d\u4e86\u5168\u529b\uff0c\u5374\u628a\u773c\u524d\u8fd9\u4e2a\u5927\u5b69\u5b50\u65e0\u53ef\u5948\u4f55\uff0c\u8d8a\u60f3\u8d8a\u89c9\u5f97\u4e0d\u5bf9\uff0c\u67af\u76fe\u4e00\u9635\u95ea\u52a8\uff0c\u4e0d\u7981\u5fc3\u4e2d\u751f\u4e86\u6076\u5ff5\u3002\u4f46\u89c1\u4ed6\u6fc0\u6597\u4e2d\u5ffd\u7136\u66b4\u559d\u4e00\u58f0\u9053\uff1a\u201c\u5b7a\u5b50\uff0c\u4f60\u518d\u8bd5\u8bd5\u8001\u592b\u8fd9\u62db\u2026\u2026\u201d\u7acb\u638c\u5982\u5200\uff0c\u5f53\u5934\u5288\u4e0b\u3002 \u3000\u3000\u8fd9\u4e00\u638c\u9887\u4f3c\u77bf\u9ad8\u51e4\u5168\u8eab\u771f\u529b\u6240\u805a\uff0c\u4e0d\u4f46\u6765\u52bf\u6781\u731b\uff0c\u800c\u4e14\u8fd8\u6697\u85cf\u4e86\u4e09\u5f0f\u53d8\u5316\uff0c\u4ee4\u4eba\u96be\u4ee5\u6349\u6478\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u7a76\u7adf\u5bf9\u654c\u7ecf\u9a8c\u4e0d\u8db3\uff0c\u7779\u72b6\u4e4b\u4e0b\uff0c\u5fc3\u4e2d\u6697\u611f\u9a87\u7136\uff0c\u5fd8\u4e86\u81ea\u5df1\u5728\u5185\u529b\u4e0a\u5e76\u4e0d\u8f93\u7ed9\u77bf\u9ad8\u51e4\uff0c\u672c\u53ef\u632f\u81c2\u786c\u6321\u4ed6\u8fd9\u4e00\u62db\uff0c\u53cd\u5012\u7565\u73b0\u602f\u610f\uff0c\u95ea\u8eab\u5411\u540e\u9000\u4e86\u4e94\u5c3a\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u81ea\u662f\u672a\u66fe\u6599\u5230\u77bf\u9ad8\u51e4\u8fd9\u4e00\u62db\u72c2\u5288\u800c\u4e0b\u7684\u76ee\u7684\uff0c\u5c31\u662f\u5728\u903c\u4ed6\u8dc3\u9000\uff0c\u597d\u8ba9\u81ea\u5df1\u6709\u65f6\u95f4\u6697\u4e0b\u5176\u6bd2\u624b\uff0c\u6b64\u65f6\u65b9\u96ea\u5b9c\u4e0d\u5e78\u4e2d\u8ba1\uff0c\u77bf\u9ad8\u51e4\u54ea\u80af\u653e\u5f03\u8fd9\u5343\u8f7d\u96be\u9022\u7684\u826f\u673a\uff0c\u53f3\u624b\u5411\u6000\u4e2d\u4e00\u63a2\uff0c\u5ffd\u7136\u5927\u58f0\u559d\u9053\uff1a\u201c\u5a03\u513f\uff0c\u4f60\u6562\u786c\u63a5\u8001\u592b\u4e00\u638c\u5417\uff1f\u201d \u3000\u3000\u8bdd\u97f3\u672a\u843d\uff0c\u4eba\u5df2\u6251\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u8eab\u5f62\u672c\u6765\u53ea\u662f\u7a0d\u7a0d\u4e00\u9000\u5373\u6b62\uff0c\u95fb\u8a00\u5fc3\u4e2d\u6709\u6c14\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u5341\u638c\u53c8\u6709\u4f55\u59a8\uff01\u201d \u3000\u3000\u53f3\u81c2\u4e00\u63a2\uff0c\u5f53\u771f\u4eae\u638c\u8fce\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u5c31\u5728\u53cc\u638c\u5373\u5c06\u63a5\u5b9e\u7684\u5239\u90a3\uff0c\u5ffd\u5730\u8033\u4e2d\u4f20\u6765\u8096\u65e0\u75d5\u7684\u66b4\u559d\uff1a\u201c\u77bf\u5144\u624b\u4e0b\u7559\u60c5\uff0c\u6bd2\u9488\u6697\u7b97\uff0c\u4e0d\u662f\u82f1\u96c4\u884c\u5f84\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u867d\u7136\u7f3a\u5c11\u5bf9\u654c\u7ecf\u9a8c\uff0c\u4f46\u4ed6\u65e2\u7136\u88ab\u5251\u795e\u89c6\u4e3a\u552f\u4e00\u53ef\u4f20\u8863\u94b5\u4e4b\u4eba\uff0c\u81ea\u7136\u6709\u5176\u8fc7\u4eba\u4e4b\u5904\uff0c\u8096\u65e0\u75d5\u8bdd\u97f3\u5165\u8033\uff0c\u4ed6\u987f\u65f6\u660e\u767d\u4e86\u77bf\u9ad8\u51e4\u60ca\u9000\u81ea\u5df1\u7684\u7528\u5fc3\u4f55\u5728\uff0c\u94a2\u7259\u6697\u54ac\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u8fd9\u59d3\u7fdf\u7684\u5f53\u771f\u5982\u6b64\u6076\u6bd2\uff0c\u7adf\u60f3\u5728\u638c\u4e2d\u6697\u85cf\u6bd2\u8ba1\u7b97\u8ba1\u6211\uff0c\u6740\u4e4b\u4e5f\u4e0d\u4e3a\u8fc7\u3002\u201d \u3000\u3000\u4ed6\u5fc3\u5ff5\u8f6c\u52a8\uff0c\u672c\u662f\u5239\u90a3\u95f4\u4e8b\uff0c\u53f3\u624b\u773c\u770b\u4e0e\u77bf\u9ad8\u51e4\u7684\u53f3\u638c\u76f8\u8ddd\u4e0d\u53ca\u5bf8\u8bb8\uff0c\u5ffd\u7136\u80a9\u5934\u4e00\u63d0\uff0c\u53cc\u638c\u4e3a\u6307\uff0c\u4e0d\u4e0e\u77bf\u9ad8\u51e4\u6765\u638c\u76f8\u649e\u5fae\u5fae\u4e0b\u6c89\u5bf8\u8bb8\uff0c\u76f4\u53d6\u5bf9\u65b9\u671f\u95e8\u5927\u7a74\u3002 \u3000\u3000\u5bb9\u5f97\u77bf\u9ad8\u51e4\u53d1\u73b0\u65b9\u96ea\u5b9c\u51e0\u62db\u653b\u6765\uff0c\u5306\u5fd9\u4e4b\u4e0b\uff0c\u6b32\u5f85\u4fa7\u8eab\u95ea\u907f\uff0c\u4f46\u611f\u5de6\u8fb9\u671f\u95e8\u7a74\u4e00\u9707\u4eba\u5df2\u6454\u5012\u5730\u4e0a\u3002 \u3000\u3000\u4ed6\u5168\u8eab\u771f\u529b\u867d\u7136\u6563\u53bb\uff0c\u4f46\u90a3\u53cc\u9e70\u773c\uff0c\u5374\u72e0\u72e0\u5730\u76ef\u7740\u8096\u65e0\u75d5\uff0c\u5636\u58f0\u9a82\u9053\uff1a\u201c\u8096\u77ee\u5b50\uff0c\u4f60\u8fd9\u4eba\u9762\u517d\u5fc3\uff0c\u5403\u91cc\u6252\u5916\u7684\u72d7\u8d3c\uff0c\u7fdf\u67d0\u7b97\u662f\u8ba4\u5f97\u4f60\u4e86\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5982\u82e5\u8d81\u52bf\u8865\u4e0a\u4e00\u62db\u534a\u5f0f\uff0c\u77bf\u9ad8\u51e4\u5fc5\u5c06\u547d\u4e27\u8fd9\u5367\u4e91\u5eb5\u524d\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u8fdf\u7591\u4e86\u4e00\u4e0b\uff0c\u672a\u66fe\u6325\u638c\u4e0b\u51fb\u3002 \u3000\u3000\u8fd9\u6b63\u662f\u4ed6\u5929\u6027\u539a\u9053\u4e4b\u5904\uff0c\u7eb5\u7136\u522b\u4eba\u6709\u4e86\u6740\u4ed6\u4e4b\u5fc3\uff0c\u4ed6\u5374\u5c1a\u672a\u611f\u5230\u5fc5\u5c06\u6740\u4eba\u800c\u540e\u5feb\u7684\u4e88\u4ee5\u62a5\u590d\u3002 \u3000\u3000\u7533\u884c\u65f6\u5374\u4e00\u8dc3\u800c\u4e0a\uff0c\u4f4e\u58f0\u5bf9\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u6b64\u4eba\u7559\u4ed6\u4e0d\u5f97\uff0c\u5982\u80fd\u8d81\u673a\u9664\u53bb\uff0c\u5b9e\u662f\u4e00\u5927\u5584\u4e3e\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u521a\u81ea\u6447\u5934\u4e00\u7b11\uff0c\u8096\u65e0\u75d5\u5df2\u5927\u58f0\u9053\uff1a\u201c\u5c0f\u4f19\u5b50\uff0c\u4f60\u6b66\u529f\u5df2\u5f97\u5251\u795e\u771f\u4f20\uff0c\u4e3e\u624b\u6295\u8db3\u95f4\uff0c\u8db3\u89c1\u5927\u5c06\u98ce\u8303\uff0c\u8001\u592b\u6b23\u89c1\u9648\u5927\u4fa0\u6709\u5f92\u5982\u4f60\uff0c\u7740\u5b9e\u9ad8\u5174\u5f97\u5f88\u2026\u2026\u201d \u3000\u3000\u8bdd\u97f3\u672a\u843d\uff0c\u5df2\u7136\u63a2\u624b\u6273\u5f00\u77bf\u9ad8\u51e4\u7684\u53f3\u638c\uff0c\u6361\u51fa\u4e00\u6839\u901a\u4f53\u96ea\u4eae\uff0c\u957f\u8fbe\u5bf8\u534a\u7684\u94a2\u9488\uff0c\u5411\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u8fd9\u6839\u94a2\u9488\u4f60\u53ef\u66fe\u542c\u4eba\u8bf4\u8fc7\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5bf9\u6b66\u6797\u4e2d\u7684\u4e00\u5207\uff0c\u53ef\u8c13\u77e5\u9053\u7684\u5c11\u4e4b\u53c8\u5c11\uff0c\u56e0\u6b64\u8096\u65e0\u75d5\u4e3e\u9488\u76f8\u8be2\uff0c\u4ed6\u53ea\u6709\u77a0\u76ee\u6447\u5934\u4e0d\u7b54\u3002 \u3000\u3000\u4f46\u7533\u884c\u65f6\u5374\u6d53\u7709\u4e00\u76b1\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u8096\u8001\u5f53\u5bb6\u7684\uff0c\u7533\u67d0\u4f3c\u662f\u542c\u4eba\u8bf4\u8fc7\u6b64\u9488\u6765\u5386\u3002\u201d \u3000\u3000\u8096\u65e0\u75d5\u7b11\u9053\uff1a\u201c\u7533\u5144\u5f1f\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u7533\u884c\u65f6\u9053\uff1a\u201c\u5357\u5929\u4e8c\u602a\u4e4b\u4e2d\uff0c\u7af9\u9053\u7d20\u6709\u4e00\u638c\u8ffd\u9b42\u4e4b\u540d\uff0c\u636e\u4f20\u4e43\u4ee5\u638c\u4e2d\u5251\u4f24\u4eba\u81f4\u6b7b\uff0c\u4f46\u4eca\u65e5\u770b\u6765\uff0c\u8fd9\u638c\u4e2d\u5251\u4e09\u5b57\uff0c\u5927\u6982\u662f\u8981\u6539\u6210\u638c\u4e2d\u9488\u4e86\u3002\u201d \u3000\u3000\u8096\u65e0\u75d5\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u5012\u662f\u5f88\u6709\u89c1\u8bc6\uff0c\u8fd9\u4f4d\u77bf\u5144\u679c\u7136\u662f\u7528\u8fd9\u6839\u94a2\u9488\uff0c\u4f24\u8fc7\u6b66\u6797\u4e0d\u5c11\u8c6a\u6770\u7684\u6027\u547d\uff0c\u8001\u592b\u867d\u7136\u51e0\u756a\u529d\u963b\uff0c\u7ec8\u56e0\u5404\u4eba\u4e60\u6027\u4e0d\u540c\uff0c\u65e0\u6cd5\u529d\u5176\u6c38\u4e0d\u518d\u7528\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u53ea\u89c1\u4ed6\u84e6\u7136\u968f\u624b\u4e00\u6325\uff0c\u5c06\u90a3\u62a5\u94a2\u9488\u629b\u843d\u4e07\u4e08\u60ac\u5ca9\u4e4b\u4e0b\uff0c\u63a5\u7740\u9053\uff1a\u201c\u4ece\u4eca\u5929\u8d77\uff0c\u77bf\u9ad8\u51e4\u8fd9\u638c\u4e2d\u85cf\u9488\u7684\u4f24\u4eba\u7edd\u679d\uff0c\u53ea\u80fd\u6c38\u8fdc\u7559\u4f5c\u6614\u65e5\u7684\u7f8e\u8c08\u4e86\u3002\u201d\u8bf4\u7f62\uff0c\u7adf\u7136\u54c8\u54c8\u5927\u7b11\u4e0d\u6b62\u3002 \u3000\u3000\u7533\u884c\u65f6\u77a7\u7740\u65b9\u96ea\u5b9c\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u8fd9\u4f4d\u8096\u8001\u5f53\u5bb6\u7684\uff0c\u5012\u662f\u4e2a\u6709\u5fc3\u4e4b\u4eba\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u6253\u4e00\u5f00\u5934\u5c31\u6ca1\u5bf9\u8096\u65e0\u75d5\u6000\u6709\u4f55\u7b49\u654c\u610f\uff0c\u6b64\u65f6\u89c1\u4ed6\u8fd9\u7b49\u4e3e\u63aa\uff0c\u81ea\u7136\u66f4\u662f\u89c9\u51fa\u8fd9\u4e2a\u53c8\u77ee\u53c8\u80d6\u7684\u8001\u4eba\u5e76\u975e\u90aa\u6076\u4e4b\u58eb\uff0c\u7533\u884c\u65f6\u8bdd\u97f3\u4e00\u843d\uff0c\u4ed6\u7acb\u5373\u7b11\u9053\uff1a\u201c\u7533\u5927\u4fa0\u8bf4\u7684\u662f\uff0c\u665a\u8f88\u4e5f\u89c9\u51fa\u8fd9\u4f4d\u8001\u4eba\u5bb6\u4e0d\u662f\u5fc3\u5730\u9669\u6076\u4e4b\u4eba\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bf4\u8bdd\u7684\u58f0\u97f3\u4e0d\u4f4e\uff0c\u90a3\u8096\u65e0\u75d5\u81ea\u662f\u542c\u5f97\u660e\u660e\u767d\u767d\uff0c\u76ee\u5149\u4e00\u8f6c\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5c0f\u4f19\u5b50\uff0c\u4f60\u8fd9\u53e5\u5949\u627f\u8bdd\u5f88\u4e2d\u542c\u2026\u2026\u201d \u3000\u3000\u8bdd\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u8d35\u59d3\u554a\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5ffd\u7136\u95f4\u89c9\u51fa\uff0c\u8fd9\u8096\u65e0\u75d5\u7684\u4e2a\u6027\uff0c\u4f3c\u662f\u5728\u5e73\u6613\u4e4b\u4e2d\u8fd8\u5e26\u51e0\u6210\u6ed1\u7a3d\uff0c\u5f53\u4e0b\u51e0\u4e4e\u5931\u7b11\u51fa\u58f0\u5730\u5e94\u9053\uff1a\u201c\u665a\u8f88\u65b9\u96ea\u5b9c\uff01\u201d \u3000\u3000\u8096\u65e0\u75d5\u79c3\u9876\u4e00\u8fde\u70b9\u4e86\u51e0\u70b9\uff0c\u9053\uff1a\u201c\u65b9\u96ea\u5b9c\u5417\uff1f\u8001\u592b\u8bb0\u4e0b\u4e86\u3002\u201d \u3000\u3000\u4ed6\u4e00\u9762\u8bf4\uff0c\u4e00\u9762\u4fef\u4e0b\u8eab\u53bb\uff0c\u628a\u90a3\u7af9\u9053\u77bf\u9ad8\u51e4\u6276\u5750\u8d77\u6765\uff0c\u53f3\u624b\u8f7b\u8f7b\u5730\u5411\u4ed6\u540e\u5fc3\u95e8\u7a74\u6309\u53bb\uff0c\u53e3\u4e2d\u53c8\u9063\uff1a\u201c\u65b9\u8001\u5f1f\uff0c\u8001\u673d\u5982\u662f\u89e3\u5f00\u4ed6\u7684\u7a74\u9053\uff0c\u8001\u5f1f\u4f1a\u4e0d\u4f1a\u89c1\u602a\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u89c1\u4ed6\u638c\u5fc3\u5df2\u7136\u8d34\u5728\u77bf\u9ad8\u51e4\u7684\u547d\u95e8\uff0c\u5374\u662f\u771f\u529b\u84c4\u800c\u4e0d\u53d1\uff0c\u5148\u5411\u81ea\u5df1\u6253\u62db\u547c\u8be2\u95ee\uff0c\u5fc3\u4e2d\u5bf9\u8096\u65e0\u75d5\u7684\u4e3a\u4eba\uff0c\u53c8\u589e\u591a\u4e86\u4e00\u4efd\u656c\u610f\uff0c\u5f53\u4e0b\u7b11\u9053\uff1a\u201c\u665a\u8f88\u5982\u6709\u4f24\u4ed6\u4e4b\u5fc3\uff0c\u9002\u624d\u4e5f\u4e0d\u4f1a\u5728\u70b9\u5012\u4ed6\u4ee5\u540e\u8fd9\u4e45\u672a\u4e0b\u624b\u4e86\uff01\u8096\u524d\u8f88\u5982\u8981\u89e3\u5f00\u4ed6\u7684\u7a74\u9053\uff0c\u5c3d\u7ba1\u52a8\u624b\u4fbf\u662f\u3002\u201d \u3000\u3000\u8096\u65e0\u75d5\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u8001\u5f1f\u6177\u6168\u5f97\u5f88\u554a\uff01\u201d \u3000\u3000\u7b11\u58f0\u4e00\u6b47\uff0c\u638c\u529b\u5ffd\u53d1\uff0c\u4f46\u542c\u5f97\u90a3\u77bf\u9ad8\u51e4\u9661\u7136\u5389\u543c\u4e00\u58f0\uff0c\u5410\u51fa\u4e86\u4e00\u5927\u53e3\u9c9c\u8840\uff0c\u6389\u5934\u5411\u8096\u65e0\u75d5\u9a82\u9053\uff1a\u201c\u59d3\u8096\u7684\uff0c\u4e09\u5341\u591a\u5e74\u7684\u4ea4\u60c5\uff0c\u4f60\u5168\u5fd8\u4e86\u5417\uff1f\u4f60\u600e\u80fd\u4e0b\u5f97\u4e86\u8fd9\u7b49\u8fa3\u624b\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u867d\u7136\u89c9\u51fa\u8096\u65e0\u75d5\u62cd\u5f00\u7a74\u9053\u4e4b\u4e3e\uff0c\u7adf\u7136\u4f7f\u5f97\u77bf\u9ad8\u98ce\u5410\u51fa\u5927\u53e3\u9c9c\u8840\uff0c\u5b9e\u662f\u5927\u51fa\u5e38\u89c4\u4e4b\u5916\uff0c\u4f46\u53ef\u5e76\u672a\u770b\u51fa\u6709\u4ec0\u4e48\u4e0d\u5bf9\uff0c\u751a\u81f3\u542c\u5f97\u77bf\u9ad8\u51e4\u8d23\u9a7e\u8096\u65e0\u75d5\u4ee5\u540e\uff0c\u4ecd\u7136\u6709\u4e9b\u4e0d\u660e\u767d\uff0c\u6b63\u5f85\u76b1\u7709\u8be2\u95ee\u8096\u65e0\u75d5\uff0c\u8fd9\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u8033\u4e2d\u5ffd\u7136\u542c\u5f97\u8096\u65e0\u75d5\u5927\u7b11\u4e4b\u58f0\u9053\uff1a\u201c\u77bf\u9ad8\u51e4\uff0c\u5982\u679c\u6211\u8096\u67d0\u843d\u5230\u4eca\u5929\u5730\u6b65\uff0c\u521a\u624d\u90a3\u4e00\u638c\u6539\u7531\u4f60\u7fdf\u5144\u6765\u66ff\u8096\u67d0\u6d3b\u7a74\u7684\u8bdd\uff0c\u53ea\u6015\u540e\u679c\u6bd4\u773c\u4e0b\u8981\u4e25\u91cd\u5f97\u591a\u4e86\u3002\u201d \u3000\u3000\u77bf\u9ad8\u51e4\u4e24\u773c\u4e4b\u4e2d\u5145\u6ee1\u4e86\u6028\u6bd2\u4e4b\u8272\uff0c\u6068\u6068\u5730\u5207\u9f7f\u9053\uff1a\u201c\u8096\u65e0\u75d5\uff0c\u4f60\u6bd4\u8001\u592b\u6076\u6bd2\u4e86\u5343\u500d\uff0c\u4e0d\u9519\uff0c\u8001\u592b\u5982\u662f\u6709\u673a\u4f1a\u4e3a\u4f60\u62cd\u5f00\u7a74\u9053\uff0c\u786e\u662f\u53ef\u80fd\u501f\u6b64\u826f\u673a\uff0c\u53d6\u4f60\u4e00\u547d\uff0c\u4f46\u603b\u6bd4\u4f60\u6563\u53bb\u6211\u4e00\u8eab\u529f\u529b\uff0c\u843d\u4e2a\u957f\u75db\u4e0d\u5982\u77ed\u75db\u7684\u597d\u554a\uff01\u201d\u8bdd\u97f3\u672a\u5df2\uff0c\u6028\u6bd2\u7684\u773c\u8272\uff0c\u5df2\u7ecf\u88ab\u6cea\u5149\u6240\u7f69\u6ca1\uff0c\u77bf\u9ad8\u51e4\u867d\u7136\u4e00\u751f\u4e3a\u6076\uff0c\u4f46\u843d\u5230\u4e00\u8eab\u771f\u529b\u6563\u53bb\uff0c\u7a7a\u6709\u6ee1\u809a\u5b50\u7edd\u62db\uff0c\u518d\u4e5f\u65e0\u6cd5\u65bd\u5c55\uff0c\u786e\u662f\u4f7f\u4eba\u6709\u82f1\u96c4\u672b\u8def\u4e4b\u53f9\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u65f6\u624d\u660e\u767d\u8096\u65e0\u75d5\u501f\u7740\u89e3\u7a74\u7684\u673a\u4f1a\uff0c\u5df2\u7136\u9707\u6563\u4e86\u77bf\u9ad8\u98ce\u7684\u5168\u8eab\u771f\u6c14\uff0c\u4f7f\u4ed6\u7531\u6b64\u800c\u540e\uff0c\u518d\u4e5f\u4e0d\u80fd\u8bba\u6b66\uff0c\u65b9\u624d\u5f15\u8d77\u77bf\u9ad8\u98ce\u90a3\u7b49\u6012\u6bd2\u6124\u6068\u3002 \u3000\u3000\u8096\u65e0\u75d5\u54c8\u54c8\u4e00\u7b11\uff0c\u7adf\u662f\u5c06\u90a3\u77bf\u9ad8\u51e4\u625b\u5728\u80a9\u4e0a\uff0c\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u89c1\u5230\u5251\u795e\u9648\u5927\u4fa0\u4e4b\u65f6\uff0c\u5c31\u8bf4\u6614\u65e5\u6545\u4ea4\u8096\u4f2f\u5ef6\u5411\u4ed6\u95ee\u5019\u3002\u201d \u3000\u3000\u4ed6\u8bdd\u97f3\u672a\u5df2\uff0c\u7533\u884c\u65f6\u8131\u53e3\u53eb\u9053\uff1a\u201c\u539f\u6765\u4f60\u8001\u662f\u70b9\u82cd\u6d3e\u7684\u90a3\u4f4d\u5931\u8e2a\u591a\u5e74\u7684\u957f\u8001\u77ee\u5251\u5ba2\u8096\u4f2f\u5ef6\u8001\u524d\u8f88\u5417\uff1f\u665a\u8f88\u771f\u6b63\u662f\u5931\u656c\u4e86\uff01\u201d \u3000\u3000\u5927\u6b65\u4e0a\u524d\uff0c\u957f\u63d6\u5230\u5730\u3002 \u3000\u3000\u8096\u67d0\u79c3\u9876\u4e00\u6447\uff0c\u7b11\u9053\uff1a\u201c\u4e0d\u6562\u5f53\uff0c\u7533\u8001\u5f1f\u7684\u4fa0\u540d\uff0c\u8001\u673d\u65e9\u5df2\u95fb\u540d\u3002\u201d \u3000\u3000\u7533\u884c\u65f6\u9053\uff1a\u201c\u665a\u8f88\u6614\u65e5\u542c\u8fc7\u5f88\u591a\u4f60\u8001\u7684\u8c6a\u4fa0\u4e8b\u8ff9\uff0c\u70b9\u82cd\u5251\u6d3e\u4f20\u8bf4\u4f60\u8001\u5931\u8e2a\uff0c\u60f3\u4e0d\u5230\u4f60\u8001\u7adf\u662f\u6447\u8eab\u4e00\u53d8\uff0c\u6210\u4e86\u5357\u5929\u4e8c\u602a\u4e4b\u4e00\u3002\u201d \u3000\u3000\u8096\u65e0\u75d5\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u7533\u8001\u5f1f\uff0c\u8001\u673d\u8fd9\u8096\u4f2f\u5ef7\u7684\u540d\u53f7\uff0c\u9664\u4e86\u5411\u5251\u795e\u9648\u5927\u4fa0\u5c1a\u6562\u63d0\u4e0a\u4e00\u63d0\uff0c\u5bf9\u6b66\u6797\u670b\u53cb\u800c\u8a00\uff0c\u8096\u4f2f\u5ef7\u5e94\u662f\u65e9\u5df2\u4e0d\u5728\u4eba\u95f4\u4e86\uff0c\u5c1a\u76fc\u8001\u5f1f\u5305\u6db5\uff0c\u83ab\u5c06\u6b64\u8baf\u900f\u9732\u51fa\u53bb\u3002\u201d \u3000\u3000\u8bdd\u97f3\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u8001\u673d\u8fd9\u4e09\u5341\u5e74\u6765\uff0c\u4e0d\u60dc\u6df7\u8ff9\u4e8c\u602a\u9b54\u9053\u4e2d\u7684\u5fc3\u613f\uff0c\u4eca\u65e5\u603b\u7b97\u5047\u65b9\u8001\u5f1f\u4e4b\u624b\u6210\u529f\uff0c\u4ece\u4eca\u800c\u540e\uff0c\u8001\u673d\u4e5f\u8bb8\u4e0d\u518d\u4e0b\u5c71\u4e86\u2026\u2026\u201d \u3000\u3000\u7533\u884c\u65f6\u95fb\u8a00\uff1a\u987f\u65f6\u660e\u767d\u8fd9\u4f4d\u6614\u5e74\u7684\u70b9\u82cd\u957f\u8001\uff0c\u5c45\u7136\u662f\u4e3a\u4e86\u60f3\u7275\u5236\u7af9\u9053\u77bf\u9ad8\u51e4\uff0c\u4e0d\u8ba9\u4ed6\u8fc7\u5206\u4e3a\u6076\u624d\u9690\u59d3\u57cb\u540d\uff0c\u548c\u77bf\u9ad8\u51e4\u7ed3\u6210\u5144\u5f1f\uff0c\u8fd9\u7b49\u4e0d\u60dc\u8eab\u5165\u5730\u72f1\u7684\u4e49\u884c\uff0c\u611f\u52a8\u7684\u7533\u884c\u65f6\u864e\u76ee\u4e4b\u4e2d\uff0c\u9971\u5b55\u7740\u6cea\u5149\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8001\u524d\u8f88\u820d\u8eab\u9972\u864e\u4e4b\u5fb7\uff0c\u5b9e\u662f\u4eba\u5929\u5171\u9274\u2026\u2026\u201d \u3000\u3000\u8096\u65e0\u75d5\u4e0d\u5bb9\u7533\u884c\u65f6\u591a\u8bf4\uff0c\u6717\u58f0\u4e00\u7b11\u9053\uff1a\u201c\u8001\u5144\u53f0\uff0c\u8001\u673d\u867d\u6709\u4ee5\u8eab\u5556\u864e\u4e4b\u5fc3\uff0c\u4f46\u5374\u662f\u65b9\u8001\u5f1f\u6210\u5c31\u4e86\u8fd9\u756a\u529f\u5fb7\uff0c\u4e09\u5341\u5e74\u6765\uff0c\u77bf\u9ad8\u51e4\u9632\u6211\u4e4b\u751a\uff0c\u8fdc\u8fc7\u4e8e\u9632\u8303\u4ed6\u4eba\uff0c\u5426\u5219\uff0c\u8001\u673d\u4e5f\u4e0d\u4f1a\u7b49\u4e86\u8fd9\u4e48\u591a\u5e74\uff0c\u90fd\u6ca1\u6709\u673a\u4f1a\u4e0b\u624b\u4e86\u2026\u2026\u201d\u8bed\u97f3\u4e00\u6b47\uff0c\u6389\u5934\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u65b9\u8001\u5f1f\uff0c\u591a\u8c22\u4f60\u52a9\u4e86\u8001\u673d\u6210\u529f\uff0c\u522b\u5fd8\u8bb0\u89c1\u5230\u4eca\u5e08\u4e4b\u65e5\uff0c\u4ee3\u6545\u4eba\u5411\u4ed6\u95ee\u5019\u4e00\u58f0\u5b89\u597d\u554a\uff01\u201d\u4fa7\u8f6c\u8eab\u8eaf\uff0c\u5c31\u5f85\u4e3e\u6b65\u79bb\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u6253\u7533\u884c\u65f6\u7684\u4e00\u756a\u8c08\u8bdd\u4e4b\u4e2d\uff0c\u5df2\u7ecf\u4e86\u7136\u8096\u65e0\u75d5\u5fc5\u662f\u4e00\u4f4d\u767d\u9053\u5927\u4fa0\uff0c\u800c\u4e14\u4ed6\u4ecd\u662f\u4e0d\u501f\u81ea\u6bc1\u4fa0\u540d\u659c\u8dfb\u8eab\u9b54\u9053\u4e4b\u4e2d\uff0c\u8096\u65e0\u75d5\u7684\u8fd9\u7b49\u4e49\u98ce\u4fa0\u884c\uff0c\u81ea\u662f\u4ee4\u4ed6\u5927\u4e3a\u94a6\u4ef0\uff0c\u5f53\u4e0b\u8fde\u5fd9\u62b1\u62f3\u9053\uff1a\u201c\u665a\u8f88\u8bb0\u4e0b\u4e86\u2026\u2026\u4e0d\u8fc7\uff0c\u8001\u524d\u8f88\u7684\u76db\u60c5\uff0c\u5148\u5e08\u5df2\u65e0\u6cd5\u63a5\u53d7\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8bdd\u97f3\u752b\u843d\uff0c\u8096\u65e0\u75d5\u521a\u521a\u8e0f\u51fa\u7684\u5de6\u8db3\uff0c\u9661\u5730\u6536\u4e86\u56de\u6765\uff0c\u8fde\u6253\u5728\u80a9\u5934\u7684\u77bf\u9ad8\u98ce\uff0c\u4e5f\u88ab\u4ed6\u677e\u624b\u8dcc\u843d\u5728\u5730\u4e0a\uff0c\u72c2\u7741\u7740\u4e24\u773c\uff0c\u559d\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u8bf4\u5251\u795e\u9648\u5927\u4fa0\u600e\u4e48\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u51c4\u7136\u9053\uff1a\u201c\u5148\u5e08\u5df2\u4e8e\u6708\u524d\u901d\u4e16\u4e86\u3002\u201d \u3000\u3000\u8096\u65e0\u75d5\u5982\u96f7\u51fb\u822c\u7684\u5446\u7acb\u5f53\u5730\uff0c\u4e45\u4e45\u672a\u51fa\u4e00\u58f0\u3002\u8c46\u5927\u7684\u6cea\u73e0\uff0c\u593a\u7736\u800c\u51fa\uff0c\u987a\u7740\u90a3\u5706\u5706\u7684\u8138\u4e0a\uff0c\u6d41\u6ee1\u4e86\u9894\u4e0b\u7684\u80e1\u987b\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u7779\u72b6\uff0c\u5fc3\u4e2d\u5927\u4e3a\u60b2\u75db\uff0c\u4e00\u65f6\u7adf\u7136\u63a9\u9762\u54ed\u51fa\u58f0\u6765\u3002 \u3000\u3000\u591c\u51e4\u98d8\u98d8\uff0c\u5bd2\u6708\u6726\u80e7\uff0c\u8352\u5c71\u5076\u6709\u733f\u557c\uff0c\u76ca\u53d1\u589e\u52a0\u4e86\u8fd9\u5eb5\u524d\u7684\u51c4\u51c9\u6c14\u6c1b\u3002 \u3000\u3000\u5927\u7ea6\u8fc7\u4e86\u8db3\u8db3\u987f\u996d\u4e4b\u4e45\uff0c\u8096\u65e0\u75d5\u65b9\u59cb\u4ef0\u5929\u957f\u957f\u4e00\u53f9\uff0c\u9ad8\u58f0\u9053\uff1a\u201c\u82cd\u5929\u554a\uff01\u4e3a\u4ec0\u4e48\u597d\u4eba\u5c31\u4e0d\u4f1a\u957f\u5bff\u5462\uff1f\u6b66\u6797\u4e4b\u4e2d\uff0c\u53ef\u4ee5\u6b7b\u53bb\u5343\u4e07\u4e2a\u50cf\u8096\u67d0\u8fd9\u7b49\u65e0\u7528\u4e4b\u4eba\uff0c\u5374\u4e0d\u80fd\u6ca1\u6709\u5251\u795e\u9648\u5927\u4fa0\uff0c\u800c\u8001\u5929\u4f60\u65e2\u80fd\u5bb9\u5f97\u4e0b\u8096\u67d0\u8fd9\u7b49\u4eba\u6d3b\u5728\u4e16\u95f4\uff0c\u4e3a\u4ec0\u4e48\u53c8\u5bb9\u4e0d\u4e0b\u9648\u5927\u4fa0\u4e00\u4eba\uff1f\u5929\u554a\u2026\u2026 \u3000\u3000\u4f60\u4e5f\u592a\u4e0d\u957f\u773c\u775b\u2026\u2026\u201d \u3000\u3000\u6c89\u75db\u7684\u547c\u5524\uff0c\u5b57\u5b57\u6263\u4eba\u5fc3\u5f26\uff0c\u8fde\u7533\u884c\u65f6\u4e5f\u5fcd\u4e0d\u4f4f\u6cea\u5982\u6cc9\u6d8c\u5730\u5927\u53eb\u9053\uff1a\u201c\u8001\u524d\u8f88\u95ee\u5f97\u597d\uff0c\u7533\u67d0\u5fc3\u4e2d\u8981\u8bf4\u7684\u8bdd\uff0c\u4f60\u8001\u7ec8\u4e8e\u4ee3\u6211\u8bf4\u51fa\u6765\u4e86\u2026\u2026\u201d \u3000\u3000\u8096\u65e0\u75d5\u5ffd\u7136\u4e3e\u624b\u62b9\u53bb\u4e86\u6cea\u75d5\uff0c\u5927\u58f0\u9053\uff1a\u201c\u7533\u8001\u5f1f\uff0c\u542c\u4f60\u4e4b\u8a00\uff0c\u83ab\u975e\u53d7\u8fc7\u9648\u5927\u4fa0\u7684\u6069\u60e0\u5417\uff1f\u201d \u3000\u3000\u7533\u884c\u65f6\u9053\uff1a\u201c\u6d3b\u547d\u4e4b\u6069\uff0c\u6709\u5982\u91cd\u751f\u7236\u6bcd\u3002\u201d \u3000\u3000\u8096\u65e0\u75d5\u9661\u7136\u5927\u7b11\u9053\uff1a\u201c\u662f\u4e86\uff0c\u9648\u5927\u4fa0\u4e00\u6b7b\uff0c\u4f60\u8001\u5f1f\u6c38\u6ca1\u62a5\u6069\u7684\u673a\u7f18\uff0c\u6240\u4ee5\u5982\u6b64\u4f24\u5fc3\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u7533\u884c\u65f6\u545c\u54bd\u9053\uff1a\u201c\u4e00\u4ee3\u4ec1\u4fa0\u5251\u795e\uff0c\u7adf\u7136\u76db\u5e74\u4ed9\u53bb\uff0c\u6b66\u6797\u540c\u9053\uff0c\u5931\u6b64\u680b\u6881\uff0c\u81ea\u5e94\u4e3a\u4e4b\u540c\u58f0\u4e00\u54ed\uff0c\u7533\u67d0\u6d3b\u547d\u4e4b\u6069\uff0c\u7eb5\u662f\u9648\u5927\u4fa0\u672a\u6b7b\uff0c\u4ee5\u9648\u5927\u4fa0\u6240\u80fd\uff0c\u7533\u67d0\u53c8\u4f55\u80fd\u62a5\u7b54\uff1f\u8001\u524d\u8f88\u4f3c\u662f\u5e94\u6bd4\u665a\u8f88\u660e\u767d\u554a\uff01\u201d \u3000\u3000\u8096\u65e0\u75d5\u79c3\u5934\u8fde\u70b9\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u62a5\u6069\u4e24\u5b57\uff0c\u4f60\u679c\u662f\u8c08\u4e0d\u5230\u2026\u2026\u201d\u8bdd\u97f3\u4e00\u987f\uff0c\u5ffd\u7136\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u65b9\u8001\u5f1f\uff0c\u9648\u5927\u4fa0\u662f\u9053\u6210\u5347\u5929\u7684\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542b\u6cea\u9053\uff1a\u201c\u5148\u5e08\u662f\u56e0\u75c5\u901d\u4e16\u3002\u201d \u3000\u3000\u8096\u65e0\u75d5\u6014\u4e86\u4e00\u6014\u9053\uff1a\u201c\u9648\u5927\u4fa0\u4e5f\u4f1a\u88ab\u75c5\u9b54\u593a\u53bb\u6027\u547d\u5417\uff1f\u8fd9\u2026\u2026\u8fd9\u53eb\u8001\u673d\u600e\u80af\u76f8\u4fe1\uff1f\u201d \u3000\u3000\u4f46\u4ed6\u5ffd\u7136\u4e0d\u518d\u8bf4\u8bdd\uff0c\u6253\u5730\u4e0a\u6293\u8d77\u90a3\u5944\u5944\u4e00\u606f\u7684\u77bf\u9ad8\u51e4\u6253\u5728\u80a9\u5934\uff0c\u6b32\u8a00\u53c8\u6b62\u5730\u5411\u65b9\u96ea\u5b9c\u6253\u4e86\u4e2a\u95ee\u8baf\uff0c\u5f84\u81ea\u6389\u5934\u5411\u6797\u5916\u884c\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u53ea\u9053\u4ed6\u4e34\u53bb\u4e4b\u65f6\uff0c\u5fc5\u5c06\u6709\u51e0\u53e5\u8bdd\u4ea4\u4ee3\uff0c\u5b70\u6599\u8fd9\u8001\u4eba\u5c45\u7136\u8bf4\u8d70\u5c31\u8d70\uff0c\u5fc3\u4e2d\u4e00\u6123\uff0c\u8131\u53e3\u9053\uff1a\u201c\u8096\u524d\u8f88\u8fd9\u5c31\u8981\u8d70\u4e86\u5417\uff1f\u201d \u3000\u3000\u8096\u65e0\u75d5\u5df2\u5728\u4e09\u4e08\u4ee5\u5916\u7684\u6797\u6728\u4e4b\u4e2d\uff0c\u95fb\u8a00\u957f\u53f9\u4e00\u58f0\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u77bf\u9ad8\u51e4\u6b66\u529f\u5df2\u5931\uff0c\u8001\u673d\u4e0e\u4ed6\u76f8\u5904\u4e09\u5341\u5e74\uff0c\u603b\u4e0d\u80fd\u773c\u770b\u4ed6\u65e0\u5904\u5b89\u606f\u7acb\u547d\uff0c\u8001\u673d\u6b64\u53bb\uff0c\u6253\u7b97\u5b89\u987f\u597d\u4e86\u8fd9\u4f4d\u8001\u53cb\uff0c\u7136\u540e\u518d\u5bfb\u627e\u8001\u5f1f\u2026\u2026\u81f3\u4e8e\u8001\u673d\u4e0e\u795e\u5c3c\u65ad\u6307\u4e4b\u4ec7\uff0c\u8bf4\u7a7f\u4e86\u53ea\u4e0d\u8fc7\u662f\u82e6\u8089\u8ba1\u800c\u5df2\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u62b1\u62f3\u4e00\u63d6\u9053\uff1a\u201c\u8001\u524d\u8f88\u4e0d\u662f\u8bf4\u4e0d\u518d\u4e0b\u5c71\u4e86\u5417\uff1f\u201d \u3000\u3000\u8096\u65e0\u75d5\u51c4\u7136\u72c2\u7b11\u4e09\u58f0\uff0c\u9053\uff1a\u201c\u9648\u5927\u4fa0\u5982\u662f\u4ecd\u5728\u4e16\u95f4\uff0c\u8001\u673d\u81ea\u662f\u4e0d\u5fc5\u518d\u604b\u7ea2\u5c18\uff0c\u788d\u624b\u788d\u811a\u7684\uff0c\u4f46\u9648\u5927\u4fa0\u8fc7\u65e9\u4ed9\u53bb\uff0c\u8001\u5f1f\u4f60\u53c8\u5982\u6b64\u5e74\u8f7b\uff0c\u8001\u673d\u5018\u82e5\u4ecd\u7136\u72ec\u5584\u5176\u8eab\uff0c\u9ad8\u8e48\u81ea\u9690\uff0c\u90a3\u5c82\u4e0d\u662f\u6210\u4e86\u771f\u6b63\u7684\u6076\u4eba\u4e86\uff1f\u201d\u8bdd\u97f3\u672a\u843d\uff0c\u4eba\u5df2\u8dc3\u4e0a\u5d16\u53bb\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u957f\u957f\u5730\u5401\u4e86\u53e3\u6c14\uff0c\u5446\u671b\u7740\u8096\u65e0\u75d5\u53bb\u5904\u9053\uff1a\u201c\u98ce\u5c18\u5947\u58eb\uff0c\u4fa0\u9053\u9ad8\u4eba\uff0c\u679c\u662f\u6709\u4ed6\u8fc7\u4eba\u4e4b\u5904\u2026\u2026\u201d \u3000\u3000\u90a3\u4e00\u76f4\u5728\u65c1\u672a\u51fa\u4e00\u58f0\u7684\u96ea\u6d9b\uff0c\u6b64\u523b\u5ffd\u7136\u4f4e\u58f0\u9053\uff1a\u201c\u5929\u8fc7\u56db\u9f13\uff0c\u7533\u5927\u4fa0\u53ef\u8981\u5230\u5eb5\u5185\u5750\u4e0a\u4e00\u4f1a\uff0c\u5f85\u8d2b\u5c3c\u4e3a\u5927\u4fa0\u51c6\u5907\u4e00\u70b9\u513f\u70ed\u8336\uff0c\u6696\u6696\u8eab\u5b50\uff1f\u201d \u3000\u3000\u7533\u884c\u65f6\u95f4\u8a00\uff0c\u62ac\u5934\u6253\u91cf\u4e86\u4e00\u4e0b\u5929\u8272\uff0c\u6447\u5934\u9053\uff1a\u201c\u4e0d\u5fc5\u4e86\uff0c\u5c0f\u5e08\u7236\uff0c\u7533\u67d0\u4e5f\u8be5\u56de\u5bfa\u4e86\u3002\u201d \u3000\u3000\u8bdd\u97f3\u987f\u4e86\u987f\uff0c\u5411\u65b9\u96ea\u5b9c\u62b1\u62f3\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4eca\u5bb5\u4e4b\u4e8b\uff0c\u53ef\u80fd\u662f\u7531\u5144\u5f1f\u884c\u8e2a\u4e0d\u591f\u9690\u79d8\u5f15\u8d77\uff0c\u5e78\u800c\u90a3\u8096\u602a\u4e43\u662f\u70b9\u82cd\u957f\u8001\uff0c\u8001\u5f1f\u53c8\u6b66\u529f\u9ad8\u5f3a\uff0c\u5426\u5219\uff0c\u7533\u67d0\u53ea\u6015\u8981\u94f8\u4e0b\u7ec8\u8eab\u96be\u4ee5\u5f25\u8865\u7684\u5927\u61be\u4e86\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u65e9\u5df2\u6599\u5230\u90a3\u5357\u5929\u4e8c\u602a\u5fc5\u662f\u8ddf\u7533\u884c\u65f6\u800c\u6765\uff0c\u4f46\u7533\u884c\u65f6\u81ea\u5df1\u4e0d\u8bf4\uff0c\u4ed6\u53ef\u672a\u4fbf\u8bf4\u51fa\uff0c\u6b64\u65f6\u95fb\u8a00\uff0c\u66f4\u662f\u6447\u5934\u9053\uff1a\u201c\u7533\u5927\u4fa0\u4e0d\u5fc5\u81ea\u8d23\uff0c\u6b64\u4e8b\u5df2\u6210\u8fc7\u53bb\uff0c\u83ab\u8981\u6302\u5728\u5fc3\u4e0a\u4e86\uff01\u201d \u3000\u3000\u7533\u884c\u65f6\u4f3c\u662f\u60f3\u518d\u8bf4\u8bdd\uff0c\u4f46\u4ed6\u7ec8\u4e8e\u5f3a\u5fcd\u5728\u5fc3\uff0c\u62b1\u62f3\u4e00\u63d6\u9053\uff1a\u201c\u6539\u65e5\u5144\u5f1f\u518d\u6765\u95ee\u5019\uff01\u201d \u3000\u3000\u8f6c\u8eab\u5927\u6b65\u800c\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e5f\u7565\u4e00\u62b1\u62f3\uff0c\u8fd9\u624d\u548c\u96ea\u6d9b\u5e08\u59b9\u56de\u8f6c\u5eb5\u4e2d\u3002 \u3000\u3000\u7ecf\u6b64\u4e00\u6765\uff0c\u76f4\u5230\u795e\u5c3c\u5728\u7b2c\u5341\u5929\u56de\u5eb5\uff0c\u5367\u4e91\u5ca9\u4e0a\u5e76\u65e0\u5176\u4ed6\u4e8b\u6545\u53d1\u751f\u3002\u5f53\u65b9\u96ea\u5b9c\u628a\u5357\u5929\u4e8c\u602a\u88ad\u51fb\u4e4b\u4e8b\u5411\u795e\u5c3c\u8bf4\u660e\u4ee5\u540e\uff0c\u795e\u5c3c\u53ea\u662f\u90b9\u7709\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u8bdd\u97f3\u4e00\u987f\uff0c\u5ffd\u7136\u95f4\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u7684\u5251\u6cd5\u7ec3\u5f97\u600e\u6837\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8138\u8272\u5fae\u6cdb\u7ea2\u6655\uff0c\u9053\uff1a\u201c\u5f1f\u5b50\u611a\u8822\uff0c\u5e08\u53d4\u6240\u6388\u5341\u56db\u5f0f\u53d8\u5316\u5f1f\u5b50\u53ea\u7ec3\u4f1a\u4e86\u5341\u5f0f\u2026\u2026\u201d \u3000\u3000\u795e\u5c3c\u70b9\u70b9\u9053\uff1a\u201c\u90a3\u53e6\u5916\u7684\u56db\u5f0f\uff0c\u4f60\u60f3\u5fc5\u662f\u5f53\u65f6\u672a\u80fd\u8bb0\u4e0b\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f1f\u5b50\u786e\u662f\u672a\u80fd\u8bb0\u7262\u3002\u201d \u3000\u3000\u795e\u5c3c\u5fae\u7b11\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u83ab\u8981\u6cc4\u6c14\uff0c\u5341\u5929\u4e4b\u5185\uff0c\u4f60\u80fd\u7ec3\u719f\u5341\u5f0f\u53d8\u5316\uff0c\u5df2\u975e\u5e38\u4eba\u6240\u53ca\u4e86\u3002\u201d\u8bed\u97f3\u7565\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u5e08\u53d4\u539f\u8981\u4f60\u7559\u5728\u5c71\u4e0a\u534a\u5e74\uff0c\u628a\u4f60\u5e08\u7956\u5168\u90e8\u6b66\u529f\u7ec3\u6210\uff0c\u4f46\u773c\u4e0b\u5374\u4e0d\u80fd\u4e0d\u7a0d\u4f5c\u6539\u53d8\uff0c\u5b69\u5b50\uff0c\u7531\u4eca\u65e5\u8d77\u5e08\u53d4\u8981\u5728\u5341\u5929\u4e4b\u5185\uff0c\u628a\u4f60\u5e08\u7956\u5168\u90e8\u6b66\u529f\u4f20\u6388\u4e8e\u4f60\uff0c\u534a\u6708\u4e4b\u540e\uff0c\u4f60\u5c31\u8be5\u4e0b\u5c71\u53bb\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u4e86\u4e00\u5446\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u6211\u600e\u80fd\u4e00\u4e0b\u5b50\u8bb0\u5f97\u90a3\u4e48\u591a\u5462\uff1f\u201d \u3000\u3000\u4f46\u4ed6\u53e3\u4e2d\u5374\u672a\u628a\u5fc3\u4e2d\u6240\u60f3\u8bf4\u51fa\uff0c\u53ea\u76b1\u7709\u9053\uff1a\u201c\u5e08\u53d4\u8981\u5f1f\u5b50\u63d0\u524d\u4e0b\u5c71\u5417\uff1f\u201d \u3000\u3000\u795e\u5c3c\u4f4e\u53f9\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4f4e\u53f9\u9053\uff1a\u201c\u5f1f\u5b50\u5341\u5206\u611a\u7b28\uff0c\u770b\u6765\u8fd9\u53ea\u6709\u5341\u591a\u5929\u7684\u65f6\u5149\uff0c\u662f\u65e0\u6cd5\u628a\u5e08\u7956\u6b66\u529f\u5168\u90e8\u5b66\u6210\u4e86\u3002\u201d \u3000\u3000\u795e\u5c3c\u70b9\u5934\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u5e08\u53d4\u4e5f\u77e5\u9053\u4f60\u51b3\u65e0\u53ef\u80fd\u5728\u5341\u5929\u4e2d\u5b66\u4f1a\u8bb8\u591a\uff0c\u4f46\u5e08\u53d4\u53ea\u8981\u628a\u53e3\u8bc0\u548c\u53d8\u5316\u544a\u77e5\u4e8e\u4f60\uff0c\u4e0b\u5c71\u4ee5\u540e\uff0c\u4f60\u4ecd\u53ef\u4ee5\u7ec3\u6210\u529f\u7684\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u77e5\u5e08\u53d4\u65e2\u5df2\u51b3\u5b9a\uff0c\u5fc5\u7136\u662f\u6709\u4e86\u91cd\u5927\u539f\u56e0\uff0c\u5f53\u4e0b\u606d\u656c\u4ed6\u8bf4\u9053\uff1a\u201c\u5f1f\u5b50\u9075\u547d\uff01\u201d \u3000\u3000\u795e\u5c3c\u53f9\u606f\u4e00\u58f0\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u6211\u77e5\u9053\u4f60\u5fc3\u4e2d\u4e00\u5b9a\u6709\u5f88\u591a\u7684\u8bdd\u8981\u8bf4\uff0c\u4f46\u65f6\u65e5\u5df2\u7136\u65e0\u591a\uff0c\u5e08\u53d4\u4e0d\u60f3\u518d\u6d6a\u8d39\u8fd9\u534a\u4e2a\u6708\u7684\u5b9d\u8d35\u65f6\u5149\uff0c\u4e00\u5207\u4e8b\u7b49\u4f60\u4e0b\u5c71\u4e4b\u540e\uff0c\u4f60\u81ea\u7136\u5c31\u4f1a\u660e\u767d\uff0c\u5e08\u53d4\u8fd8\u662f\u5148\u628a\u5e08\u7956\u7684\u6b66\u529f\u4f20\u6388\u7ed9\u4f60\uff0c\u5b69\u5b50\uff0c\u4eca\u540e\u4f60\u80fd\u5426\u8d76\u5f97\u4e0a\u4f60\u5e08\u7236\uff0c\u7ee7\u627f\u5e08\u7956\u9057\u5fd7\uff0c\u5149\u5927\u5251\u795e\u7684\u540d\u671b\uff0c\u90a3\u53ef\u5c31\u9760\u4f60\u81ea\u5df1\u7684\u52aa\u529b\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u51db\u7136\u606d\u5e94\u9053\uff1a\u201c\u5f1f\u5b50\u77e5\u9053\uff01\u201d \u3000\u3000\u795e\u5c3c\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u4eca\u5929\u4f60\u5148\u628a\u90a3\u56db\u5f0f\u53d8\u5316\u7ec3\u719f\uff0c\u660e\u5929\u8d77\uff0c\u5e08\u53d4\u5c31\u8981\u4f20\u4f60\u90a3\u5251\u6cd5\u4e2d\u7684\u6700\u540e\u4e00\u62db\u4e86\u2026\u2026\u201d\u8bdd\u97f3\u7565\u987f\u53c8\u9053\uff1a\u201c\u9f99\u884c\u4e5d\u5251\u8bb0\u719f\u4e4b\u540e\uff0c\u4f60\u5df2\u53ef\u7b97\u4e60\u6210\u4e86\u5e08\u7956\u6b66\u529f\u7684\u4e5d\u6210\uff0c\u8fd8\u6709\u4e00\u6210\u5c31\u8981\u9760\u4f60\u81ea\u5df1\u53bb\u9886\u609f\u4e86\uff0c\u5e08\u53d4\u5df2\u7136\u6559\u4e0d\u4e86\u4f60\u5566\uff01\u201d\u65b9\u96ea\u5b9c\u542c\u5f97\u6709\u4e9b\u4e0d\u89e3\u5730\u7b11\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u90a3\u53e6\u5916\u4e00\u6210\u6b66\u529f\uff0c\u53ef\u662f\u5e08\u7956\u5e76\u672a\u7559\u4e0b\u9057\u7b08\u79d8\u672c\u5417\uff1f\u201d \u3000\u3000\u795e\u5c3c\u5931\u58f0\u7b11\u9053\uff1a\u201c\u4e0d\u662f\uff01\u201d \u3000\u3000\u77a7\u7740\u65b9\u96ea\u5b9c\u53d1\u5446\u7684\u795e\u6001\uff0c\u795e\u5c3c\u63a5\u4e0a\u53c8\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u5341\u6210\u6b66\u529f\uff0c\u5c31\u4e00\u822c\u4eba\u800c\u8a00\uff0c\u7ecf\u9a8c\u5e94\u5360\u4e09\u6210\uff0c\u4f46\u56e0\u4e3a\u4f60\u5e08\u7956\u6240\u4f20\u7684\u6b66\u529f\uff0c\u535a\u5927\u7cbe\u6df1\uff0c\u6d1e\u70db\u673a\u5148\u4e8e\u654c\u65b9\u672a\u52a8\u4e4b\u524d\uff0c\u6545\u800c\u4f60\u53ea\u8981\u6709\u4e00\u6210\u52a8\u624b\u7684\u7ecf\u9a8c\u5c31\u591f\u4e86\uff0c\u6240\u4ee5\uff0c\u8fd9\u6700\u540e\u4e00\u6210\u6b66\u529f\uff0c\u5c31\u662f\u6307\u7684\u5bf9\u654c\u7ecf\u9a8c\u800c\u8a00\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u7a81\u7136\u60f3\u8d77\u81ea\u5df1\u5728\u90a3\u8352\u91ce\u8305\u820d\uff0c\u4e00\u5251\u523a\u4f24\u9b54\u5200\u7530\u8fdc\u4e4b\u65f6\uff0c\u7530\u8fdc\u5200\u52bf\u4e0d\u51fa\uff0c\u81ea\u5df1\u7adf\u7136\u4e0d\u77e5\u5982\u4f55\u4e13\u62db\u4f24\u654c\u4e4b\u4e8b\uff0c\u987f\u65f6\u4e86\u7136\u5e08\u53d4\u6240\u8bf4\u5e08\u7956\u7559\u4e0b\u7684\u6b66\u529f\uff0c\u5df2\u80fd\u6599\u654c\u673a\u5148\uff0c\u679c\u7136\u4e0d\u5047\u3002 \u3000\u3000\u800c\u4e14\u65b9\u96ea\u5b9c\u4e5f\u660e\u767d\u5e08\u53d4\u628a\u52a8\u624b\u7ecf\u9a8c\uff0c\u5217\u4e3a\u6b66\u529f\u4e2d\u7684\u4e00\u6210\uff0c\u786e\u662f\u5b9e\u60c5\uff0c\u56e0\u4e3a\u5f53\u65e5\u81ea\u5df1\u5018\u4f7f\u6709\u4e86\u52a8\u624b\u7ecf\u9a8c\uff0c\u90a3\u5200\u9b54\u7530\u8fdc\u5c31\u7b97\u4e0d\u80af\u62bd\u5200\u51fa\u9798\uff0c\u81ea\u5df1\u4e5f\u80fd\u591f\u51ed\u7740\u5bf9\u654c\u7684\u7ecf\u9a8c\uff0c\u8bf1\u4f7f\u5bf9\u65b9\u79fb\u52a8\u8eab\u5f62\uff0c\u7136\u540e\u5c31\u52bf\u51fa\u5251\u4f24\u654c\u3002\u8f6c\u5ff5\u81f3\u6b64\uff0c\u4ed6\u604d\u7136\u7b11\u9053\uff1a\u201c\u5f1f\u5b50\u660e\u767d\u4e86\uff01\u201d \u3000\u3000\u795e\u5c3c\u7b11\u9053\uff1a\u201c\u4f60\u660e\u767d\u5c31\u597d\uff0c\u5b69\u5b50\uff0c\u54b1\u4eec\u8be5\u53bb\u7ec3\u90a3\u56db\u5f0f\u5251\u62db\u5566\uff01\u201d \u3000\u3000\u5341\u5929\u7684\u65f6\u95f4\uff0c\u5f39\u6307\u5373\u8fc7\uff0c\u65b9\u96ea\u5b9c\u51e0\u4e4e\u662f\u663c\u591c\u4e0d\u606f\u5730\u9ed8\u5ff5\u7740\u90a3\u5e08\u7956\u6700\u540e\u6dfb\u521b\u7684\u4e00\u62db\u5251\u6cd5\uff0c\u4efb\u51ed\u5982\u6b64\uff0c\u4ed6\u4ecd\u7136\u672a\u80fd\u7aa5\u5176\u5168\u8c8c\uff0c\u53ea\u7ea6\u7565\u5730\u628a\u8fd9\u4e00\u62db\u7684\u4e03\u5927\u53d8\u5316\uff0c\u8bb0\u4e0b\u4e86\u4e00\u4e2a\u5927\u6982\u3002 \u3000\u3000\u6562\u60c5\u5f53\u5e74\u5251\u5723\u9f99\u8d85\u7fa4\u624b\u521b\u6b64\u62db\u5251\u6cd5\u4e4b\u65e5\uff0c\u51e0\u4e4e\u82b1\u8d39\u4e86\u4e94\u5e74\u65f6\u5149\uff0c\u65b9\u96ea\u5b9c\u80fd\u591f\u5728\u77ed\u77ed\u7684\u5341\u5929\u4e4b\u4e2d\uff0c\u8bb0\u4e0b\u4e86\u4e03\u5f0f\u53d8\u5316\u7684\u5927\u6982\uff0c\u5b9e\u5219\u662f\u4e3a\u4eba\u6240\u4e0d\u80fd\u4e3a\u7684\u4e86\u3002 \u3000\u3000\u6253\u7b2c\u5341\u4e00\u5929\u8d77\uff0c\u91d1\u9876\u795e\u5c3c\u7adf\u662f\u8981\u65b9\u96ea\u5b9c\u4ece\u5934\u81f3\u5c3e\u7684\u628a\u6240\u6709\u6b66\u529f\uff0c\u5faa\u5e8f\u6e10\u8fdb\u5730\u4e00\u4e00\u4ece\u5934\u6f14\u7ec3\uff0c\u5230\u7b2c\u5341\u56db\u5929\u7684\u9ec4\u660f\u4e3a\u6b62\uff0c\u6b63\u597d\u628a\u81ea\u5df1\u8fd9\u4e09\u5e74\u591a\u6240\u4e60\uff0c\u5168\u90e8\u6f14\u7ec3\u5b8c\u6bd5\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u81f3\u6b64\u65b9\u59cb\u660e\u767d\uff0c\u5e08\u53d9\u4e3a\u4f55\u8981\u81ea\u5df1\u5728\u7b2c\u5341\u4e94\u5929\u4e0b\u5c71\u7684\u539f\u6545\u4e86\uff0c\u539f\u6765\u5979\u8001\u4eba\u5bb6\u8981\u7559\u4e0b\u56db\u5929\u65f6\u95f4\uff0c\u5f7b\u5934\u5f7b\u5c3e\u5730\u8003\u91cf\u81ea\u5df1\u4e00\u756a\u3002 \u3000\u3000\u5f53\u591c\uff0c\u795e\u5c3c\u628a\u65b9\u96ea\u5b9c\u5524\u5230\u7985\u623f\u4e4b\u4e2d\uff0c\u5bf9\u52b3\u5e08\u7956\u7684\u9057\u50cf\uff0c\u53c8\u8c06\u8c06\u544a\u54b8\u4e86\u4e00\u904d\uff0c\u6700\u540e\uff0c\u7adf\u7136\u957f\u957f\u4e00\u53f9\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u6df1\u5c71\u5b66\u827a\uff0c\u5df2\u6709\u4e09\u5e74\u96f6\u4e5d\u4e2a\u6708\u4e4b\u4e45\uff0c\u6b64\u756a\u4e0b\u5f97\u5c71\u53bb\uff0c\u6b66\u6797\u4e4b\u4e2d\uff0c\u5fc5\u7136\u6709\u4e86\u53d8\u52a8\uff0c\u4f60\u5343\u4e07\u83ab\u8981\u5fd8\u8bb0\uff0c\u90a3\u4e94\u5927\u9b54\u4e3b\u770b\u4f3c\u4e00\u5207\u52a8\u4e71\u7684\u6839\u6e90\uff0c\u4f46\u5b9e\u9645\u4e0a\uff0c\u53ea\u6015\u8fd8\u6709\u9690\u8eab\u5e55\u540e\u7684\u6076\u4eba\uff0c\u4f60\u5e08\u7236\u7684\u7f20\u7ef5\u75c5\u69bb\u4e4b\u4e8b\uff0c\u4f60\u4e43\u662f\u4eb2\u76ee\u6240\u7779\uff0c\u5b69\u5b50\uff0c\u4f60\u53ef\u8981\u5f53\u5fc3\u4e9b\u624d\u662f\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u867d\u662f\u660e\u767d\u5e08\u53d4\u7a81\u7136\u8981\u81ea\u5df1\u63d0\u524d\u4e0b\u5c71\u56fa\u6709\u6df1\u610f\uff0c\u4f46\u5374\u4e00\u76f4\u4e0d\u6562\u8be2\u95ee\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u81ea\u5df1\u660e\u65e5\u5c31\u8981\u79bb\u53bb\uff0c\u5982\u679c\u81ea\u5df1\u4ecd\u4e0d\u542f\u9f7f\uff0c\u4e0b\u5c71\u4ee5\u540e\uff0c\u53c8\u8981\u5230\u51e0\u65f6\u624d\u80fd\u518d\u56de\u5c71\u53e9\u95ee\uff1f\u201d\u5f53\u4e0b\u7565\u4e00\u6c89\u541f\uff0c\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u5f1f\u5b50\u6709\u4e00\u4ef6\u4e8b\uff0c\u4e0d\u77e5\u5f53\u4e0d\u5f53\u95ee\uff1f\u201d \u3000\u3000\u795e\u5c3c\u9053\uff1a\u201c\u660e\u65e5\u4f60\u5c31\u8981\u4e0b\u5c71\u4e86\uff0c\u5fc3\u4e2d\u5982\u6709\u7591\u96be\u4e4b\u4e8b\uff0c\u5c31\u8d76\u5feb\u8bf4\u51fa\u6765\u5427\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e08\u53d4\u672c\u662f\u8981\u5f1f\u5b50\u7559\u5728\u5eb5\u4e2d\u534a\u5e74\uff0c\u73b0\u5728\u4e09\u4e2a\u6708\u4e0d\u5230\uff0c\u5e08\u53d4\u7a81\u7136\u8981\u5f1f\u5b50\u63d0\u524d\u4e0b\u5c71\uff0c\u662f\u4e0d\u662f\u5e08\u53d4\u56e0\u4e3a\u6709\u4ec0\u4e48\u91cd\u5927\u7684\u4e8b\u6545\u53d1\u751f\uff0c\u624d\u547d\u5f1f\u5b50\u8d76\u53bb\u89e3\u51b3\u3002\u201d \u3000\u3000\u795e\u5c3c\u6447\u5934\u9053\uff1a\u201c\u9519\u4e86\uff01\u5b69\u5b50\uff0c\u5982\u662f\u5e08\u53d4\u6709\u4e8b\u8981\u4f60\u53bb\u529e\uff0c\u5c82\u4f1a\u5230\u6b64\u523b\u5c1a\u4e0d\u544a\u8bc9\u4f60\u4e4b\u7406\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u6014\u9053\uff1a\u201c\u662f\u554a\uff0c\u5f1f\u5b50\u60f3\u9519\u4e86\u3002\u201d \u3000\u3000\u795e\u5c3c\u5ffd\u7136\u51c4\u51c9\u5730\u4e00\u7b11\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u5e08\u53d4\u4e5f\u4e0d\u60f3\u7792\u4f60\uff0c\u4f60\u660e\u65e5\u4e0b\u5c71\u4e4b\u540e\uff0c\u5e08\u53d4\u4e5f\u5c06\u4e8e\u4e94\u65e5\u4e4b\u540e\uff0c\u79bb\u5c71\u4ed6\u53bb\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u4e86\u4e00\u5446\u9053\uff1a\u201c\u5e08\u53d4\u4e5f\u8981\u4e0b\u5c71\u5417\uff1f\u201d \u3000\u3000\u795e\u5c3c\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e08\u53d4\u51e0\u65f6\u91cd\u56de\u91d1\u9876\uff1f\u201d \u3000\u3000\u795e\u5c3c\u6447\u5934\u9053\uff1a\u201c\u4e0d\u77e5\u9053\uff0c\u4e5f\u8bb8\u2026\u2026\u4e5f\u8bb8\u2026\u2026\u201d\u7a81\u7136\u76ee\u5149\u4e00\u9eef\uff0c\u4f4f\u53e3\u4e0d\u8bed\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u60f6\u7136\u5931\u58f0\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u5f1f\u5b50\u5982\u662f\u60f3\u5411\u5e08\u53d4\u53e9\u95ee\u91d1\u5b89\uff0c\u83ab\u975e\u56de\u5230\u91d1\u9876\u4e5f\u89c1\u4e0d\u5230\u4f60\u8001\u5417\uff1f\u201d \u3000\u3000\u795e\u5c3c\u957f\u957f\u4e00\u53f9\uff0c\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u8fd9\u756a\u5b5d\u5fc3\uff0c\u5e08\u53d4\u8bb0\u4f4f\u4e86\uff0c\u5176\u5b9e\uff0c\u5e08\u53d4\u6b64\u884c\u5982\u662f\u987a\u5229\uff0c\u534a\u5e74\u4e4b\u4e2d\uff0c\u4e5f\u8bb8\u5c31\u4f1a\u56de\u8f6c\u5ce8\u5d4b\uff0c\u5426\u5219\uff0c\u53ea\u6015\u54b1\u4eec\u5df2\u96be\u6709\u518d\u89c1\u7684\u673a\u7f18\u2026\u2026\u201d \u3000\u3000\u8bdd\u97f3\u4e00\u987f\uff0c\u5ba4\u4e86\u5e08\u7956\u9057\u50cf\u4e00\u773c\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u4e00\u5e74\u4e4b\u540e\u4f60\u5982\u6709\u6687\uff0c\u6700\u597d\u80fd\u6765\u6b64\u4e00\u884c\uff0c\u4e5f\u597d\u53d6\u53bb\u5e08\u7956\u7684\u9057\u50cf\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5ffd\u7136\u89c9\u51fa\u5e08\u53d4\u8fd9\u8bed\u7109\u4e0d\u8be6\u7684\u8bdd\u53e5\u4e4b\u4e2d\uff0c\u4eff\u4f5b\u5145\u6ee1\u4e86\u4e0d\u7965\u4e4b\u610f\uff0c\u5fc3\u4e2d\u5927\u4e3a\u9707\u60b8\uff0c\u8131\u53e3\u9053\uff1a\u201c\u5e08\u53d4\u8a00\u4e0b\u4e4b\u610f\uff0c\u83ab\u975e\u6b64\u884c\u5341\u5206\u51f6\u9669\uff1f\u201d \u3000\u3000\u795e\u5c3c\u9053\uff1a\u201c\u4e0d\u4e00\u5b9a\uff0c\u5728\u6211\u672a\u89c1\u90a3\u4eba\u4e4b\u524d\uff0c\u5409\u51f6\u5584\u4e25\u5747\u65e0\u4ece\u5148\u77e5\uff0c\u5b69\u5b50\uff0c\u8fd9\u662f\u5e08\u53d4\u7684\u79c1\u4e8b\uff0c\u4f60\u4e5f\u4e0d\u5fc5\u591a\u95ee\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u63a5\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u6709\u4e8b\u5f1f\u5b50\u670d\u5176\u52b3\uff0c\u8fd9\u4e8b\u80fd\u5426\u7531\u5f1f\u5b50\u4ee3\u5e08\u53d4\u524d\u53bb\u5462\uff1f\u201d \u3000\u3000\u795e\u5c3c\u8fde\u8fde\u6447\u6447\u5934\uff1a\u201c\u4e0d\u53ef\u80fd\u3002\u201d \u3000\u3000\u8fd9\u65a9\u9489\u622a\u94c1\u822c\u7684\u56de\u7b54\uff0c\u53ea\u542c\u5f97\u65b9\u96ea\u5b9c\u5446\u4e86\u534a\u664c\u3002 \u3000\u3000\u795e\u5c3c\u77a7\u7740\u65b9\u96ea\u5b9c\u53cc\u76ee\u4e4b\u4e2d\uff0c\u6cea\u5149\u95ea\u95ea\uff0c\u5fc3\u4e2d\u5927\u4e3a\u4e0d\u5fcd\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u522b\u4e3a\u5e08\u53d4\u803d\u5fc3\uff0c\u5982\u8bba\u6b66\u529f\uff0c\u5e08\u53d4\u6216\u8bb8\u8fde\u4f60\u4e5f\u5f3a\u4e0d\u8fc7\uff0c\u4f46\u5982\u82e5\u8bba\u8d77\u884c\u4e8b\u7684\u673a\u667a\u548c\u8c28\u614e\uff0c\u65f6\u4e0b\u5c1a\u65e0\u9ad8\u8fc7\u5e08\u53d4\u4e4b\u4eba\uff0c\u5b69\u5b50\uff0c\u4f60\u5927\u53ef\u653e\u5fc3\uff0c\u5728\u4f60\u5e08\u5085\u6b7b\u56e0\u672a\u660e\u4e4b\u524d\uff0c\u5e08\u53d4\u4e0d\u4f1a\u907d\u800c\u8f7b\u751f\u7684\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u62ed\u7740\u70ed\u6cea\u8bf4\u9053\uff1a\u201c\u5f1f\u5b50\u51e0\u65f6\u518d\u95ee\u4f60\u8001\u8bf7\u5b89\u5462\uff1f\u201d \u3000\u3000\u795e\u5c3c\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u53f9\u606f\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u627e\u6211\uff0c\u7531\u4eca\u65e5\u7b97\u8d77\uff0c\u4e00\u5e74\u4e4b\u5185\uff0c\u5e08\u53d4\u81ea\u4f1a\u524d\u53bb\u5bfb\u4f60\u2026\u2026\u5b69\u5b50\uff0c\u4f60\u8be5\u53bb\u6536\u62fe\u884c\u88c5\uff0c\u4e5f\u597d\u987a\u4fbf\u5411\u96ea\u513f\u5e08\u59b9\u9053\u522b\u5566\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u606d\u5c31\u5e94\u4e00\u58f0\uff1a\u201c\u5f1f\u5b50\u9075\u547d\uff01\u201d\u8fd9\u624d\u6697\u6697\u5730\u62ed\u7740\u6cea\uff0c\u8f6c\u8eab\u9000\u51fa\u4e86\u7985\u623f\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u4e00\u65e9\uff0c\u65b9\u96ea\u5b9c\u6362\u4e0a\u4e86\u96ea\u6d9b\u5e08\u59b9\u4e3a\u4ed6\u88c1\u5236\u7684\u4e00\u88ad\u5d2d\u65b0\u7684\u5929\u84dd\u957f\u886b\uff0c\u52a9\u4e0b\u6302\u7740\u90a3\u652f\u5251\u795e\u9057\u7559\u7684\u5b9d\u5251\uff0c\u5de6\u80a9\u7ff1\u4e86\u4e2a\u5c0f\u5c0f\u7684\u5730\u9ec4\u5e03\u5305\uff0c\u4e00\u6b65\u4e09\u56de\u5934\u7684\u8d70\u4e0b\u4e86\u91d1\u9876\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u6709\u7740\u6325\u4e0d\u53bb\u7684\u4f9d\u604b\u4e4b\u60c5\uff0c\u4e5f\u6709\u7740\u8bf4\u4e0d\u51fa\u7684\u4e0d\u5b89\u7684\u9884\u5146\uff0c\u4eff\u4f5b\u4e00\u65e6\u79bb\u5f00\u5367\u4e91\u5eb5\uff0c\u5c31\u518d\u4e5f\u6ca1\u6709\u673a\u4f1a\u89c1\u5f97\u5230\u5e08\u53d4\u548c\u96ea\u6d9b\u5e08\u59b9\u4e00\u822c\u7684\u96be\u8fc7\uff0c\u4f46\u7a76\u7adf\u5df2\u7ecf\u957f\u5927\u4e86\uff0c\u7eb5\u7136\u5fc3\u4e2d\u90a3\u4e48\u4e0d\u5b89\uff0c\u96be\u8fc7\uff0c\u53e3\u4e2d\u5374\u662f\u4e00\u5b57\u4e5f\u672a\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u7ecf\u8fc7\u4f0f\u864e\u7985\u5bfa\u65f6\uff0c\u786e\u66fe\u60f3\u5230\u53bb\u770b\u770b\u7533\u884c\u65f6\uff0c\u4f46\u4ed6\u5ffd\u7136\u60f3\u8d77\u4e86\u5927\u4f2f\u6bcd\u548c\u65b9\u73de\uff0c\u987f\u65f6\u6709\u7740\u5f52\u5fc3\u4f3c\u7bad\u4e4b\u601d\uff0c\u6068\u4e0d\u5f97\u63d2\u7fc5\u98de\u5230\u5609\u5b9a\u5e9c\uff0c\u53bb\u627e\u90a3\u7a0b\u5b50\u671b\u6253\u542c\u4e00\u4e0b\u5927\u5a18\u4e0b\u843d\uff0c\u56e0\u6b64\uff0c\u4ed6\u5728\u4f0f\u864e\u5bfa\u524d\u7565\u4e00\u5f20\u671b\uff0c\u7adf\u7136\u5fcd\u4f4f\u4e86\u5165\u5bfa\u5fc3\u613f\uff0c\u5927\u6b65\u5954\u5411\u5ce8\u5d4b\u53bf\u57ce\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5728\u57ce\u4e2d\u6253\u4e2a\u5c16\uff0c\u4ece\u5e08\u7236\u7559\u7ed9\u81ea\u5df1\u7684\u51e0\u5341\u7247\u91d1\u53f6\u5b50\u4e4b\u4e2d\uff0c\u53d6\u51fa\u4e00\u7247\uff0c\u5728\u9a6c\u5e02\u573a\u9009\u4e86\u4e00\u5339\u5feb\u9a6c\uff0c\u987a\u7740\u5b98\u9053\uff0c\u76f4\u5411\u5609\u5b9a\u9a76\u53bb\u3002 \u3000\u3000\u9ec4\u660f\u65f6\u5206\uff0c\u65b9\u96ea\u5b9c\u5df2\u7ecf\u6253\u9a6c\u5954\u8fdb\u4e86\u5609\u5b9a\u5e9c\u7684\u57ce\u95e8\u3002 \u3000\u3000\u56db\u5e74\u4e0d\u5230\uff0c\u65e7\u5730\u91cd\u6e38\uff0c\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u4e0d\u7981\u6709\u4eba\u4e8b\u5168\u975e\u4e4b\u611f\uff0c\u5f53\u5e74\u7684\u5609\u5b9a\uff0c\u4ec5\u4ec5\u662f\u6cef\u6c5f\u548c\u5927\u6e21\u6cb3\u4e24\u6761\u6c34\u8def\u7684\u670b\u53cb\uff0c\u5c31\u4e0d\u4e0b\u5343\u4eba\u4e4b\u591a\uff0c\u4ed6\u56db\u5e74\u524d\u968f\u7740\u4e2d\u6d32\u4e09\u4fa0\u6765\u6b64\u4e4b\u65e5\uff0c\u7ecf\u8fc7\u8857\u4e0a\u4e4b\u65f6\uff0c\u4ed6\u66fe\u77a7\u5230\u4e0d\u5c11\u7cbe\u795e\u6296\u64de\u7684\u6b66\u6797\u548c\u8c6a\u6c14\u8fc7\u4eba\u7684\u6c5f\u6e56\u597d\u6c49\uff0c\u6bd4\u80a9\u63a5\u8e35\uff0c\u7199\u6765\u6518\u5f80\uff0c\u4f46\u4eca\u65e5\u8e0f\u8fdb\u57ce\u95e8\uff0c\u5374\u89c1\u4e0d\u5230\u4e00\u4e2a\u8fd9\u7c7b\u4eba\u7269\uff0c\u5927\u8857\u4e4b\u4e0a\uff0c\u663e\u5f97\u51b7\u51b7\u6e05\u6e05\uff0c\u5076\u5c14\u89c1\u5230\u51e0\u4f4d\u52b2\u88c5\u6c49\u5b50\uff0c\u4e5f\u90fd\u662f\u957f\u7684\u6a21\u7709\u6012\u76ee\uff0c\u9762\u5bb9\u53ef\u618e\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u77e5\u9053\u6709\u4e9b\u4e0d\u5bf9\uff0c\u4f46\u4ed6\u5728\u6ca1\u6709\u89c1\u5230\u7a0b\u5b50\u671b\u4ee5\u524d\uff0c\u53ef\u4e0d\u60f3\u5984\u4e0b\u8bc4\u65ad\uff0c\u4e00\u63d0\u7f30\u7ef3\uff0c\u7adf\u662f\u7a7f\u57ce\u800c\u8fc7\uff0c\u76f4\u5411\u7a0b\u5bb6\u5e84\u9a70\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5728\u79bb\u5f00\u5609\u5b9a\u4e4b\u65f6\uff0c\u5e76\u672a\u6ce8\u610f\u5230\u90a3\u7a0b\u5bb6\u5e84\u7a76\u7adf\u5728\u57ce\u5317\u8fd8\u662f\u57ce\u897f\uff0c\u5317\u523b\u7a7f\u57ce\u800c\u53bb\uff0c\u8fce\u9762\u89c1\u5230\u9a84\u9633\u7531\u5de6\u624b\u65b9\u4f4d\u5c04\u6765\uff0c\u65b9\u59cb\u660e\u767d\uff0c\u90a3\u7a0b\u5bb6\u5e84\u4e43\u662f\u5728\u5317\u95e8\u4e4b\u5916\uff0c\u7fe0\u7af9\u4f9d\u65e7\u8fce\u98ce\u800c\u7acb\uff0c\u4f46\u7fe0\u7af9\u7684\u91cc\u9762\uff0c\u5374\u5df2\u7136\u89c1\u4e0d\u5230\u534a\u95f4\u5b8c\u6574\u7684\u623f\u820d\u3002 \u3000\u3000\u5f53\u5e74\u90a3\u4f4f\u4e86\u7a0b\u5b50\u671b\u4e00\u5bb6\u4e0a\u4e0a\u4e0b\u4e0b\u767e\u5341\u53f7\u4eba\u53e3\u7684\u7a0b\u5e84\u5bb6\uff0c\u6b64\u523b\u5374\u53ea\u5269\u4e0b\u4e86\u4e00\u7247\u74e6\u783e\uff0c\u6b8b\u7816\u65ad\u5899\uff0c\u5c4b\u572e\u5899\u5012\uff0c\u4ece\u90a3\u6ecb\u751f\u5f97\u5341\u5206\u8302\u5bc6\u7684\u5df2\u67af\u91ce\u8349\u770b\u6765\uff0c\u7a0b\u5bb6\u5e84\u5e94\u662f\u53bb\u4e86\u5f88\u4e45\u7684\u4e8b\u4e86\u3002 \u3000 \u7b2c\u5341\u4e00\u56de\u3000\u4eba\u4e8b\u5168\u975e \u3000 \u3000\u3000\u65b9\u96ea\u5b9c\u7275\u7740\u9a6c\u513f\uff0c\u5446\u7acb\u5728\u7af9\u6797\u8fb9\u6cbf\uff0c\u4e45\u4e45\u4e0d\u77e5\u6240\u63aa\u3002 \u3000\u3000\u4ed6\u5411\u56db\u5468\u671b\u4e86\u534a\u5929\uff0c\u5374\u4e0d\u89c1\u4e00\u4e2a\u4eba\u5f71\u3002 \u3000\u3000\u770b\u6765\u8fd9\u7a0b\u5bb6\u5e84\u4e2d\u7684\u4eba\uff0c\u82e5\u975e\u906d\u9047\u4e86\u4e00\u628a\u5929\u706b\uff0c\u70e7\u5f97\u7247\u74e6\u4e0d\u5b58\uff0c\u90a3\u5c31\u662f\u9047\u4e0a\u4e86\u6c5f\u6e56\u4ec7\u6740\u4e4b\u4e8b\uff0c\u5168\u5e84\u4eba\u53e3\uff0c\u4ff1\u6bd5\u88ab\u4eba\u6240\u5bb3\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u601d\u6765\u60f3\u53bb\uff0c\u7ec8\u4e8e\u62c9\u7740\u9a6c\u513f\uff0c\u5728\u8fd9\u7247\u783e\u573a\u4e2d\u8f6c\u4e86\u534a\u54cd\uff0c\u679c\u7136\uff0c\u4ed6\u53d1\u73b0\u5728\u90a3\u65ad\u74e6\u6b8b\u57a3\u4e4b\u4e2d\uff0c\u786e\u662f\u7559\u6709\u4e0d\u5c11\u62d3\u7126\u7684\u9aa8\u9abc\uff0c\u663e\u7136\u8fd9\u7a0b\u5e84\u5bb6\u662f\u51fa\u4e8b\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u77ac\u4e4b\u95f4\uff0c\u65b9\u96ea\u5b9c\u5927\u6709\u4eba\u6d77\u832b\u832b\uff0c\u4eca\u540e\u4f55\u53bb\u4f55\u4ece\u4e4b\u611f\u3002 \u3000\u3000\u5728\u4ed6\u7684\u60f3\u8c61\uff0c\u5927\u5a18\u548c\u4e2d\u5dde\u4e09\u4fa0\u7684\u53bb\u5904\uff0c\u53ea\u6709\u7a0b\u5b50\u671b\u660e\u767d\uff0c\u800c\u7a0b\u5b50\u671b\u7684\u5168\u5e84\u4eba\u53e3\uff0c\u5f88\u53ef\u80fd\u5168\u88ab\u4ec7\u5bb6\u6240\u5bb3\uff0c\u5219\u5929\u5730\u4f55\u5176\u4e4b\u5927\uff0c\u8981\u81ea\u5df1\u53bb\u5bfb\u5927\u5a18\u548c\u73de\u5f1f\uff0c\u5c82\u975e\u96be\u4f3c\u5927\u6d77\u635e\u9488\u4e00\u822c\u3002 \u3000\u3000\u6c89\u6c89\u7684\u591c\u8272\u4e4b\u4e2d\uff0c\u6253\u56db\u9762\u516b\u65b9\u6d8c\u4e86\u8fc7\u6765\uff0c\u9635\u9635\u585e\u9e26\uff0c\u6b63\u7ed5\u7740\u8fd9\u7247\u7af9\u6797\u98de\u566a\uff0c\u65b9\u96ea\u5b9c\u5fcd\u4e0d\u4f4f\u957f\u53f9\u4e00\u58f0\uff0c\u62c9\u9a6c\u6389\u5934\uff0c\u5411\u5609\u5b9a\u57ce\u4e2d\u8d76\u56de\u53bb\u3002\u6562\u60c5\u4ed6\u7a81\u7136\u60f3\u8d77\uff0c\u5f53\u5e74\u6240\u4f4f\u7684\u90a3\u5bb6\u5404\u6808\u4e4b\u4e2d\uff0c\u6216\u8bb8\u80fd\u6253\u542c\u5f97\u51fa\u7a0b\u5b50\u671b\u7684\u4e00\u5207\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u6253\u7b97\u7684\u672c\u662f\u4e0d\u9519\uff0c\u4f46\u662f\uff0c\u5bb9\u5f97\u4ed6\u627e\u5230\u90a3\u6761\u5927\u8857\u4ee5\u540e\uff0c\u8fd9\u624d\u53d1\u73b0\u90a3\u5bb6\u5ba2\u6808\uff0c\u65e9\u5df2\u6539\u6210\u4e86\u4e09\u95f4\u6742\u8d27\u94fa\u4e86\u3002 \u3000\u3000\u987f\u65f6\uff0c\u4ed6\u8fde\u4e00\u70b9\u7ebf\u7d22\u4e5f\u627e\u4e0d\u51fa\u4e86\u3002 \u3000\u3000\u671b\u4e86\u671b\u5929\u8272\uff0c\u5df2\u7136\u5feb\u5230\u521d\u66f4\u65f6\u5206\uff0c\u4ed6\u94a2\u7259\u6697\u54ac\uff0c\u60f3\u9053\uff1a\u201c\u597d\u6b79\u4e5f\u5f97\u5148\u6253\u4e2a\u5bfa\u65b9\u4f4f\u4e0b\u518d\u8bb2\uff01\u201d\u5fc3\u5ff5\u4e00\u5b9a\uff0c\u4fbf\u987a\u7740\u8fd9\u6761\u5927\u8857\u5f80\u4e1c\u8d70\u53bb\uff0c\u7ec8\u4e8e\u5728\u4e0d\u8fdc\u4e4b\u5904\uff0c\u627e\u5230\u4e86\u4e00\u5bb6\u540d\u53eb\u201c\u60a6\u6765\u5ba2\u6808\u201d\u7684\u4f4f\u5904\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u968f\u4fbf\u7528\u4e86\u70b9\u665a\u81b3\uff0c\u7f13\u6b65\u8d70\u5230\u90a3\u5bb6\u5ba2\u6808\u7684\u8d26\u623f\uff0c\u6253\u7740\u90a3\u4f4d\u770b\u4e0a\u53bb\u9887\u4e3a\u5fe0\u539a\uff0c\u5e74\u7ea6\u4e94\u5341\u51fa\u5934\u7684\u8d26\u623f\u5148\u751f\u95f2\u804a\u3002 \u3000\u3000\u90a3\u4f4d\u8d26\u623f\u5148\u751f\u5012\u4e5f\u5341\u5206\u8001\u5b9e\uff0c\u65b9\u96ea\u5b9c\u90a3\u53e3\u88ab\u5f53\u5730\u4eba\u8ba4\u4f5c\u4e0b\u6c5f\u53e3\u97f3\u7684\u5b98\u8bdd\uff0c\u4f7f\u4ed6\u8bef\u628a\u8fd9\u4f4d\u5341\u4e5d\u5c81\u4e0d\u5230\u7684\u5c11\u5e74\uff0c\u5f53\u4f5c\u8fbe\u5b98\u8d35\u4eba\u7684\u516c\u5b50\u4e00\u822c\u5949\u627f\uff0c\u51e0\u4e4e\u662f\u6709\u70b9\u5df4\u7ed3\u5f97\u6709\u95ee\u5fc5\u7b54\uff0c\u800c\u4e14\u8bf4\u7684\u5341\u5206\u8be6\u5c3d\u3002\u4e0d\u8fc7\uff0c\u5c31\u5728\u65b9\u96ea\u5b9c\u5076\u7136\u8be2\u53ca\u7a0b\u5b50\u671b\u4e4b\u540e\uff0c\u8fd9\u4f4d\u5e10\u623f\u5148\u751f\u7adf\u7136\u8138\u8272\u5927\u53d8\uff0c\u4e45\u4e45\u6c89\u541f\uff0c\u4e0d\u7f6e\u4e00\u8bed\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u7acb\u5373\u611f\u5230\u6709\u4e9b\u4e0d\u5bf9\uff0c\u5f88\u663e\u5730\u53ef\u4ee5\u770b\u51fa\uff0c\u8fd9\u4f4d\u5e10\u623f\u8001\u4eba\u5bf9\u7a0b\u5b50\u671b\u7684\u4e8b\uff0c\u5b9a\u5f97\u77e5\u4e4b\u751a\u8be6\uff0c\u5426\u5219\uff0c\u4ed6\u662f\u4e0d\u4f1a\u53d8\u8272\u4e0d\u8bed\u7684\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u5fc3\u4e2d\u76d8\u7b97\u9053\uff1a\u201c\u77a7\u8fd9\u8001\u5e10\u623f\u7684\u795e\u6001\uff0c\u4f3c\u662f\u6709\u7740\u96be\u8a00\u4e4b\u9690\uff0c\u81ea\u5df1\u603b\u5f97\u60f3\u4e0a\u4e00\u4e2a\u85c9\u53e3\uff0c\u5957\u51fa\u4ed6\u51e0\u53e5\u8bdd\u624d\u597d\u2026\u2026\u201d \u3000\u3000\u5f53\u4e0b\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8001\u4e08\uff0c\u90a3\u7a0b\u671b\u7a0b\u5e84\u4e3b\u4e0e\u5728\u4e0b\u5bb6\u95e8\u4e43\u662f\u4e16\u4ea4\uff0c\u5728\u4e0b\u5949\u4e86\u5bb6\u6148\u4e4b\u547d\u987a\u9053\u62dc\u671b\u7a0b\u592b\u4eba\u53e9\u5173\u5b89\u597d\uff0c\u4e0d\u60f3\u5728\u4e0b\u53bb\u90a3\u7a0b\u5bb6\u5e84\u4e4b\u65f6\uff0c\u51b5\u7136\u53ea\u5269\u4e0b\u65ad\u74e6\u6b8b\u57a3\uff0c\u4f7f\u5728\u4e0b\u5927\u611f\u4e0d\u5b89\uff0c\u4e0d\u77e5\u8001\u4e08\u53ef\u5426\u544a\u77e5\u5728\u4e0b\uff0c\u7a0b\u5e84\u4e3b\u5bb6\u4e2d\uff0c\u662f\u5426\u51fa\u4e86\u4ec0\u4e48\u4e0d\u5e78\u4e4b\u4e8b\uff0c\u5728\u4e0b\u56de\u5bb6\u4e4b\u65f6\uff0c\u4e5f\u597d\u5411\u5bb6\u6bcd\u2026\u2026\u201d \u3000\u3000\u5e10\u623f\u8001\u4eba\u76ee\u5149\u5728\u65b9\u96ea\u5b9c\u8eab\u4e0a\u6253\u91cf\u4e86\u534a\u664c\uff0c\u65b9\u624d\u957f\u957f\u4e00\u53f9\uff1a\u201c\u516c\u5b50\uff0c\u4f60\u4e5f\u662f\u6c5f\u6e56\u4e2d\u4eba\u5417\uff1f\u201d \u3000\u3000\u65b9\u5b9c\u5b9c\u672c\u8981\u70b9\u5934\u5e94\u662f\uff0c\u4f46\u8bdd\u5230\u53e3\u8fb9\uff0c\u5374\u5fc3\u4e2d\u7075\u673a\u4e00\u52a8\uff0c\u6447\u5934\u9053\uff1a\u201c\u5728\u4e0b\u81ea\u5e7c\u5c31\u7ec8\u65e5\u57cb\u9996\u5bd2\u7a97\uff0c\u4ece\u672a\u5728\u6c5f\u6e56\u4e0a\u884c\u8d70\u3002\u201d \u3000\u3000\u5e10\u623f\u8001\u4eba\u4f3c\u662f\u6709\u4e9b\u4e0d\u4fe1\u7684\u7b11\u9053\uff1a\u201c\u516c\u5b50\uff0c\u77a7\u4f60\u8eab\u4f69\u5b9d\u5251\uff0c\u53c8\u80fd\u4e58\u9a91\u90a3\u7b49\u9a8f\u9a6c\uff0c\u5012\u4e0d\u50cf\u662f\u4e2a\u6587\u5f31\u4e66\u751f\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8001\u4e08\u8bf4\u7684\u4e0d\u9519\uff0c\u5728\u4e0b\u9664\u4e86\u4e60\u8bf5\u8bd7\u6587\u4e4b\u5904\uff0c\u5bb6\u4e25\u5ff5\u6211\u81ea\u5c0f\u8eab\u4f53\u592a\u5f31\uff0c\u53c8\u8bf7\u4e86\u51e0\u4f4d\u6559\u5e08\u7237\uff0c\u6559\u5728\u4e0b\u7ec3\u4e86\u4e00\u4e9b\u62f3\u811a\uff0c\u7528\u4f5c\u9632\u8eab\u5065\u4f53\uff0c\u4e0e\u90a3\u884c\u8d70\u6c5f\u6e56\u7684\u597d\u6c49\u76f8\u8f83\uff0c\u90a3\u53ef\u5c31\u5dee\u5f97\u8fdc\u5566\uff01\u201d \u3000\u3000\u5e10\u623f\u8001\u4eba\u56e0\u662f\u770b\u4ed6\u7709\u6e05\u76ee\u79c0\uff0c\u4e00\u6d3e\u6587\u8d28\u5f6c\u5f6c\u7684\u795e\u6001\uff0c\u5fc3\u4e2d\u5012\u4e5f\u6709\u4e86\u516b\u6210\u76f8\u4fe1\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u7b11\u9053\uff1a\u201c\u516c\u5b50\u679c\u662f\u4e0d\u50cf\u6c5f\u6e56\u4eba\u7269\uff0c\u8001\u673d\u76f8\u4fe1\u4e86\u2026\u2026\u201d\u8bdd\u97f3\u5fae\u5fae\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u8001\u673d\u5374\u8ba4\u4e3a\u516c\u5b50\u65e2\u7136\u4e0d\u662f\u6c5f\u6e56\u4e2d\u4eba\uff0c\u53c8\u4f55\u5fc5\u8981\u6cbe\u60f9\u8fd9\u4e9b\u6c5f\u6e56\u4eba\u7269\u5462\uff0c\u90a3\u7a0b\u5e84\u4e3b\u5728\u5609\u5b9a\u5730\u9762\u4e4b\u4e0a\uff0c\u786e\u4e5f\u7b97\u5f97\u4e0a\u4e00\u4e2a\u4fa0\u4e49\u4eba\u7269\uff0c\u4f46\u7a76\u7adf\u4e0d\u6bd4\u5b98\u5ba6\u4e4b\u5bb6\uff0c\u6240\u4ea4\u7684\u670b\u53cb\uff0c\u53ef\u8c13\u4e09\u6559\u4e5d\u6d41\u5747\u6709\uff0c\u516c\u5b50\u5982\u662f\u60f3\u56fe\u4e2a\u6e05\u9759\uff0c\u8001\u673d\u5012\u5e0c\u671b\u516c\u5b50\u4e0d\u8981\u6253\u542c\u8fd9\u4e9b\u4e8b\u6700\u597d\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u5934\u9053\uff1a\u201c\u6b64\u4e43\u5bb6\u6bcd\u6148\u547d\uff0c\u5728\u4e0b\u7eb5\u7136\u4e3a\u6b64\u62db\u6765\u4eca\u540e\u5f88\u591a\u56f0\u6270\uff0c\u5728\u4e0b\u4e5f\u4e0d\u5bb9\u63a8\u8f9e\uff01\u201d \u3000\u3000\u5e10\u623f\u8001\u4eba\u4f3c\u662f\u5bf9\u65b9\u96ea\u5b9c\u8fd9\u7b49\u5b5d\u5fc3\uff0c\u751a\u4e3a\u94a6\u4f69\uff0c\u5f53\u4e0b\u7b11\u9053\uff1a\u201c\u516c\u5b50\uff0c\u5b5d\u5fc3\u53ef\u5609\uff0c\u8001\u673d\u611f\u4f69\u5f97\u5f88\u2026\u2026\u201d \u3000\u3000\u7a81\u7136\u4ed6\u56db\u4e0b\u91cc\u5f20\u671b\u4e86\u4e00\u773c\uff0c\u4f4e\u58f0\u63a5\u9053\uff1a\u201c\u516c\u5b50\u201d\uff0c\u65b9\u96ea\u5b9c\u539f\u6765\u5c31\u5df2\u60f3\u5230\u7a0b\u5b50\u671b\u4e00\u5bb6\uff0c\u53ef\u80fd\u5df2\u7ecf\u906d\u4e86\u6bd2\u624b\uff0c\u4f46\u6b64\u523b\u95fb\u8a00\u4e4b\u540e\uff0c\u4ecd\u7136\u514d\u4e0d\u4e86\u5fc3\u5934\u5927\u4e3a\u9707\u60b8\uff0c\u8131\u53e3\u8bf4\u9053\uff1a\u201c\u7a0b\u5e84\u4e3b\u4e00\u5bb6\u90fd\u906d\u5bb3\u4e86\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u76b1\u7709\u9053\uff1a\u201c\u8001\u4e08\uff0c\u90a3\u7a0b\u5bb6\u53ef\u6709\u751f\u8fd8\u4e4b\u4eba\uff1f\u201d \u3000\u3000\u5e10\u623f\u8001\u4eba\u6447\u5934\u9053\uff1a\u201c\u636e\u8001\u673d\u6240\u77e5\uff0c\u53ea\u6015\u7a0b\u5e84\u4e3b\u7684\u4e00\u5bb6\uff0c\u5e76\u65e0\u4e00\u4eba\u751f\u8fd8\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8001\u4e08\uff0c\u6b64\u4e8b\u4f55\u65f6\u53d1\u751f\u7684\u3002\u201d \u3000\u3000\u5e10\u65c1\u8001\u4eba\u60f3\u4e86\u4e00\u60f3\uff0c\u9053\uff1a\u201c\u7b97\u6765\u5df2\u6709\u4e00\u5e74\u591a\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u6697\u9053\uff1a\u4e00\u5e74\u591a\u4ee5\u524d\uff0c\u90a3\u662f\u8bf4\u6211\u8fd8\u5728\u909b\u5d03\u5c71\u4e2d\u8fd9\u65f6\uff0c\u8fd9\u7a0b\u5bb6\u5e84\u5c31\u5df2\u906d\u4e86\u522b\u4eba\u7684\u6bd2\u624b\u4e86\u2026\u2026 \u3000\u3000\u4ed6\u5fc3\u4e2d\u5bfb\u601d\u672a\u5df2\uff0c\u5e10\u623f\u8001\u4eba\u5df2\u63a5\u7740\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4f60\u4f3c\u662f\u6765\u8fdf\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9aa4\u7136\u9053\uff1a\u201c\u662f\u554a\uff01\u5728\u4e0b\u679c\u771f\u662f\u6765\u8fdf\u4e86\u2026\u2026\u201d\u8bed\u58f0\u7565\u987f\uff0c\u4f4e\u58f0\u63a5\u9053\uff1a\u201c\u8001\u4e08\uff0c\u90a3\u7a0b\u5e84\u4e3b\u7684\u4ec7\u5bb6\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5e10\u623f\u8001\u4eba\u8138\u4e0a\u795e\u8272\u53c8\u662f\u4e00\u53d8\uff0c\u6447\u5934\u6025\u9053\uff1a\u201c\u516c\u5b50\uff0c\u8fd9\u4e9b\u4e8b\u4f60\u6700\u597d\u83ab\u8981\u8fc7\u95ee\uff0c\u6c5f\u6e56\u4e0a\u7684\u6069\u6028\u4ec7\u6740\uff0c\u4e0d\u662f\u4f60\u4eec\u8bfb\u4e66\u505a\u5b98\u7684\u4eba\u8be5\u95ee\u7684\uff0c\u516c\u5b50\uff0c\u542c\u8001\u673d\u7684\u529d\u544a\uff0c\u4f60\u8fd8\u662f\u65e9\u4e9b\u79bb\u5f00\u5609\u5b9a\u5427\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8001\u4e08\u597d\u610f\uff0c\u5728\u4e0b\u4e0d\u662f\u4e0d\u77e5\uff0c\u53ea\u662f\u5728\u4e0b\u5018\u82e5\u4e0d\u80fd\u67e5\u660e\u7a0b\u5e84\u4e3b\u4e00\u5bb6\u4e27\u751f\u4f55\u4eba\u4e4b\u624b\uff0c\u56de\u53bb\u53ea\u6015\u8fd8\u8981\u53d7\u5230\u5bb6\u6148\u7684\u8d23\u9a82\uff0c\u8ba4\u4e3a\u5728\u4e0b\u529e\u4e8b\u65e0\u80fd\uff01\u201d \u3000\u3000\u5e10\u623f\u8001\u4eba\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u516c\u5b50\uff0c\u8001\u673d\u6709\u53e5\u8bdd\uff0c\u4e0d\u77e5\u5f53\u4e0d\u5f53\u95ee\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8001\u4e08\u6709\u8bdd\u8bf7\u8bb2\uff01\u201d \u3000\u3000\u5e10\u623f\u8001\u4eba\u9053\uff1a\u201c\u516c\u5b50\u4e00\u5b9a\u8981\u60f3\u77e5\u9053\u6740\u5bb3\u90a3\u7a0b\u5e84\u4e3b\u7684\u4ec7\u5bb6\u662f\u8c01\uff0c\u53ef\u662f\u6709\u7740\u60f3\u66ff\u7a0b\u5e84\u4e3b\u590d\u4ec7\u4e4b\u5fc3\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u5012\u4e0d\u4e00\u5b9a\uff01\u201d \u3000\u3000\u5e10\u623f\u8001\u4eba\u70b9\u5934\u9053\uff1a\u201c\u516c\u5b50\u8bf4\u7684\u4e0d\u9519\uff0c\u5e74\u8f7b\u4eba\u6700\u5927\u7684\u6bdb\u75c5\uff0c\u5c31\u662f\u8840\u6c14\u65b9\u521a\uff0c\u6613\u4e8e\u51b2\u52a8\uff0c\u516c\u5b50\u672a\u53ca\u5f31\u51a0\u4e4b\u9f84\uff0c\u5c31\u80fd\u6709\u8fd9\u7b49\u517b\u6c14\u7684\u5de5\u592b\uff0c\u8001\u673d\u5012\u662f\u653e\u5fc3\u4e0d\u5c11\u3002\u201d \u3000\u3000\u8bdd\u97f3\u81f3\u6b64\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u516c\u5b50\uff0c\u5176\u5b9e\u8001\u673d\u4e5f\u4e0d\u77e5\u9053\u5176\u4e2d\u8be6\u60c5\uff0c\u7eb5\u7136\u8001\u673d\u544a\u8bc9\u516c\u5b50\uff0c\u4e5f\u8bb8\u5bf9\u516c\u5b50\u5e2e\u52a9\u4e0d\u5927\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u8001\u4e08\u4f46\u80fd\u544a\u77e5\u90a3\u7a0b\u5e84\u4e3b\u7684\u4ec7\u4eba\u662f\u8c01\uff0c\u5728\u4e0b\u5c31\u5341\u5206\u611f\u6fc0\u4e86\u3002\u201d \u3000\u3000\u5e10\u623f\u8001\u4eba\u70b9\u4e86\u70b9\u5934\uff0c\u6781\u76ee\u626b\u4e86\u90a3\u5750\u5728\u524d\u5385\u4e2d\u7684\u51e0\u4f4d\u5ba2\u4eba\u4e00\u773c\uff0c\u8fd9\u624d\u9644\u8033\u5411\u65b9\u96ea\u5b9c\u9053\uff1a \u3000\u3000\u201c\u516c\u5b50\uff0c\u7a0b\u5e84\u4e3b\u7684\u4ec7\u5bb6\uff0c\u636e\u8bf4\u662f\u5251\u95e8\u4e94\u9b3c\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5251\u76fe\u9661\u5730\u4e00\u626c\uff0c\u8131\u53e3\u9053\uff1a\u201c\u679c\u7136\u5c31\u662f\u4ed6\u4eec\u2026\u2026\u201d \u3000\u3000\u5e10\u623f\u8001\u4eba\u5446\u4e86\u4e00\u5446\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u600e\u4e48\uff1f\u516c\u5b50\u53ef\u662f\u65e9\u5df2\u77e5\u9053\u90a3\u7a0b\u5e84\u4e3b\u7684\u4ec7\u4eba\u662f\u5251\u95e8\u4e94\u9b3c\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u624d\u89c9\u51fa\u81ea\u5df1\u4e00\u65f6\u5fd8\u60c5\uff0c\u8bf4\u6f0f\u4e86\u5634\uff0c\u5fd9\u9053\uff1a\u201c\u5728\u4e0b\u53ea\u662f\u5982\u6b64\u731c\u60f3\u800c\u5df2\u3002\u201d \u3000\u3000\u5e10\u623f\u8001\u4eba\u5ffd\u7136\u9644\u8033\u8bf4\u9053\uff1a\u201c\u516c\u5b50\uff0c\u8fd9\u5609\u5b9a\u81ea\u4ece\u7a0b\u5e84\u4e3b\u4e00\u5bb6\u88ab\u5bb3\u4ee5\u540e\uff0c\u65e9\u5df2\u6210\u4e3a\u5251\u95e8\u4e94\u9b3c\u7684\u5f92\u5b50\u5f92\u5b59\u4eec\u7684\u5929\u4e0b\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u611f\u6fc0\u5730\u7b11\u4e86\u4e00\u7b11\u9053\uff1a\u201c\u8001\u4e08\uff0c\u6253\u6270\u4f60\u8001\u5566\u2026\u2026\u201d\u7565\u4e00\u62b1\u62f3\uff0c\u4fbf\u8d77\u8eab\u79bb\u5ea7\uff0c\u6b32\u5f85\u5367\u5230\u81ea\u5df1\u623f\u4e2d\u3002 \u3000\u3000\u90a3\u5e10\u623f\u8001\u4eba\u5ffd\u7136\u6697\u4e2d\u4f38\u624b\uff0c\u62c9\u4e86\u4ed6\u8863\u89d2\u4e00\u628a\uff0c\u4eba\u58f0\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4f60\u5982\u662f\u5acc\u5c0f\u5e97\u8336\u996d\u4e0d\u597d\uff0c\u8001\u673d\u8fd9\u5c31\u5dee\u4eba\u53bb\u4e3a\u516c\u5b50\u5230\u5916\u9762\u53eb\u4e0a\u51e0\u5473\u53ef\u53e3\u7684\u83b1\u80b4\u9001\u6765\u3002\u201d\u4e00\u9762\u7b11\u7740\u8bf4\u8bdd\uff0c\u5374\u4e00\u9762\u4e0d\u505c\u5730\u5411\u65b9\u96ea\u5b9c\u7728\u773c\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u731c\u51fa\u8fd9\u4f4d\u8001\u4eba\u6b64\u4e3e\u5fc5\u6709\u6df1\u610f\uff0c\u5f53\u4e0b\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u4e0d\u8010\u4e45\u7b49\uff0c\u4f60\u8981\u4ed6\u4eec\u624b\u811a\u653e\u5feb\u4e9b\u3002\u201d\u8bdd\u97f3\u4e00\u843d\uff0c\u5934\u4e5f\u4e0d\u56de\u5730\u8d70\u51fa\u5e10\u623f\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u53ea\u89c1\u4e00\u4e2a\u7350\u5934\u9f20\u76ee\uff0c\u5f62\u8c8c\u72e0\u7410\u7684\u6c49\u5b50\uff0c\u6b63\u6253\u81ea\u5df2\u8eab\u65c1\u6328\u8eab\u800c\u8fc7\uff0c\u5411\u90a3\u5e10\u623f\u5148\u751f\u6253\u7740\u62db\u547c\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u60f3\uff1a\u201c\u8fd9\u5bb6\u4f19\u516b\u6210\u5c31\u662f\u90a3\u6210\u7384\u901a\u548c\u8463\u65b9\u7684\u624b\u4e0b\u4e86\uff0c\u6211\u6b64\u523b\u8fd8\u662f\u6682\u65f6\u5fcd\u8010\u4e00\u4e0b\u2026\u2026\uff0c\u8f6c\u5ff5\u4e4b\u95f4\uff0c\u4eba\u5df2\u56de\u5230\u81ea\u5df1\u7684\u5ba2\u623f\u4e4b\u5185\u3002 \u3000\u3000\u4ed6\u5bbd\u53bb\u5916\u7f69\u957f\u886b\uff0c\u6b63\u60f3\u7565\u4e8b\u8c03\u606f\uff0c\u76ee\u5149\u8fc7\u5904\uff0c\u9661\u7136\u5fc3\u5934\u6012\u610f\u5ffd\u751f\u3002\u6562\u60c5\u81ea\u5df1\u90a3\u9ec4\u5e03\u7684\u5305\u88b1\uff0c\u5df2\u7136\u88ab\u4eba\u6296\u5f00\uff0c\u5305\u88b1\u4e2d\u7684\u51e0\u4ef6\u6362\u6d17\u7684\u8863\u886b\u548c\u4e00\u4e9b\u91d1\u94f6\uff0c\u5168\u90e8\u6563\u843d\u5728\u5730\u4e0a\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u4e00\u9707\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u53ef\u597d\uff0c\u6211\u8fd8\u6ca1\u53bb\u627e\u4ed6\u4eec\uff0c\u4ed6\u4eec\u5012\u5148\u627e\u4e0a\u4e86\u6211\uff0c\u6211\u5c31\u4e0d\u76f8\u4fe1\u4f60\u4eec\u5251\u95e8\u4e94\u9b3c\u4ec5\u5269\u7684\u4e24\u9b3c\uff0c\u53c8\u80fd\u6709\u591a\u5927\u6c14\u52bf\u2026\u2026\uff0c\u8f6c\u5ff5\u4e4b\u95f4\uff0c\u65b9\u96ea\u5b9c\u91cd\u65b0\u62ab\u4e0a\u8863\u886b\uff0c\u9694\u7a97\u5411\u5916\uff0c\u5927\u58f0\u5730\u53eb\u7740\u5e97\u5bb6\u3002 \u3000\u3000\u5f53\u623f\u95e8\u6253\u5f00\u4e4b\u9645\uff0c\u8d6b\u7136\u662f\u90a3\u5e10\u623f\u8001\u4eba\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u76fe\u5934\u4e00\u76b1\u8131\u53e3\u9053\uff1a\u201c\u600e\u4e48\u8001\u4e08\u81ea\u5df1\u6765\u4e86\uff1f\u201d \u3000\u3000\u5e10\u623f\u8001\u4eba\u7b11\u9053\uff0c\u201c\u516c\u5b50\uff0c\u4f19\u8ba1\u4eec\u6b63\u5728\u5fd9\u7740\uff0c\u6709\u4ec0\u4e48\u4e8b\u4ea4\u4ee3\u8001\u673d\u4e5f\u662f\u4e00\u6837\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u53d1\u73b0\u5e10\u623f\u8001\u4eba\u5df2\u7136\u987a\u624b\u63a9\u4e0a\u623f\u95e8\uff0c\u5fc3\u4e2d\u5ffd\u7136\u4e00\u52a8\uff0c\u4ed8\u9053\uff1a\u201c\u83ab\u975e\u4ed6\u4e5f\u6709\u4e8b\u627e\u6211\uff1f\u201d\u4ed6\u7565\u4e00\u6c89\u541f\uff0c\u6307\u7740\u5730\u4e0a\u7684\u9ec4\u8272\u5305\u88b1\u9053\uff1a\u201c\u8001\u4e08\uff0c\u533a\u533a\u7684\u8863\u7269\u88ab\u4eba\u6253\u5f00\u641c\u67e5\u8fc7\u4e86\u3002\u201d \u3000\u3000\u5e10\u623f\u8001\u4eba\u7a00\u758f\u7684\u7709\u5934\u4e00\u76b1\uff0c\u987f\u8db3\u9053\uff1a\u201c\u516c\u5b50\uff0c\u8fd9\u4e0d\u662f\u4f19\u8ba1\u4eec\u5e72\u7684\u5427\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u51b7\u7b11\u9053\uff1a\u201c\u8001\u4e08\u4f3c\u662f\u5e94\u8be5\u6bd4\u5728\u4e0b\u66f4\u660e\u767d\u5440\uff01\u201d \u3000\u3000\u5e10\u623f\u8001\u4eba\u5ffd\u7136\u4f4e\u58f0\u4e00\u53f9\uff0c\u6447\u5934\u9053\uff1a\u201c\u516c\u5b50\uff0c\u8001\u673d\u6709\u53e5\u8bdd\u8bf4\u51fa\u6765\uff0c\u53ea\u6015\u516c\u5b50\u4e0d\u4e2d\u542c\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4ec0\u4e48\u8bdd\uff1f\u201d \u3000\u3000\u5e10\u623f\u8001\u4eba\u9053\uff1a\u201c\u8001\u673d\u4e3a\u516c\u5b50\u8bbe\u60f3\uff0c\u5e0c\u671b\u516c\u5b50\u80fd\u591f\u65e9\u4e9b\u79bb\u5f00\u5609\u5b9a\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5931\u7b11\u9053\uff1a\u201c\u8001\u4e08\u662f\u8981\u5728\u4e0b\u7acb\u5373\u79bb\u53bb\u5417\uff1f\u201d \u3000\u3000\u5e10\u623f\u8001\u4eba\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8d8a\u5feb\u8d8a\u597d\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u6014\uff0c\u9053\uff1a\u201c\u8001\u4e08\uff0c\u6b64\u523b\u5df2\u7136\u5feb\u5230\u4e09\u66f4\uff0c\u5728\u4e0b\u65e2\u4e0d\u60ef\u591c\u884c\uff0c\u53c8\u4f55\u51b5\u57ce\u95e8\u5df2\u7ecf\u4e0a\u9501\uff0c\u5728\u4e0b\u7eb5\u662f\u6709\u5fc3\u79bb\u57ce\uff0c\u53ea\u6015\u4e5f\u51fa\u4e0d\u4e86\u6b64\u57ce\u554a\uff01\u201d \u3000\u3000\u5e10\u623f\u8001\u4eba\u4f4e\u58f0\u9053\uff1a\u201c\u516c\u5b50\uff0c\u8001\u673d\u867d\u7136\u4e0d\u662f\u6b66\u6797\u4e2d\u4eba\uff0c\u4f46\u5728\u8fd9\u5609\u5b9a\u5730\u9762\u4e4b\u4e0a\uff0c\u4e5f\u6df7\u4e86\u4e09\u5341\u5e74\u591a\uff0c\u8033\u95fb\u76ee\u6fe1\uff0c\u770b\u7684\u597d\u6c49\u4eec\u786e\u5df2\u4e0d\u5c11\uff0c\u516c\u5b50\u4e00\u8eab\u529f\u529b\uff0c\u5df2\u662f\u6b66\u6797\u4e2d\u4f7c\u4f7c\u4eba\u7269\uff0c\u8001\u673d\u77e5\u9053\uff0c\u516c\u5b50\u5982\u662f\u6682\u65f6\u628a\u9a6c\u5339\u5bc4\u5728\u6b64\u5904\uff0c\u72ec\u81ea\u7ffb\u57ce\u51fa\u53bb\uff0c\u5fc5\u7136\u4e0d\u4f1a\u56f0\u96be\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5728\u4e0b\u8981\u8d70\uff0c\u4e5f\u4e0d\u4f1a\u5728\u4eca\u591c\uff01\u201d \u3000\u3000\u5e10\u623f\u8001\u4eba\u9893\u7136\u4e00\u53f9\u9053\uff1a\u201c\u516c\u5b50\uff0c\u8001\u673d\u662f\u4e3a\u4f60\u597d\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u8001\u4e08\u76db\u60c5\uff0c\u5728\u4e0b\u5fc3\u9886\u4e86\u3002\u201d\u8bdd\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u8001\u4e08\uff0c\u4f60\u66ff\u4ee3\u90a3\u4f19\u8ba1\u4eec\u6765\u6b64\u89c1\u6211\uff0c\u53ef\u662f\u6709\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5e10\u623f\u8001\u4eba\u70b9\u4e86\u70b9\u5934\uff0c\u4fef\u8eab\u62fe\u8d77\u5730\u4e0a\u7684\u8863\u7269\uff0c\u957f\u957f\u5730\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u516c\u5b50\uff0c\u8001\u673d\u679c\u7136\u6709\u4e8b\u627e\u4f60\u2026\u2026\u201d\u8bdd\u97f3\u672a\u5df2\uff0c\u4eba\u5374\u95ea\u5411\u623f\u95e8\u65c1\u8fb9\uff0c\u62c9\u5f00\u4e86\u95e8\u5411\u5916\u5f20\u671b\u4e86\u4e00\u4e0b\uff0c\u8fd9\u624d\u4e0a\u4e86\u95e8\u6813\uff0c\u5411\u65b9\u96ea\u5b9c\u4f4e\u58f0\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4ed6\u4eec\u6709\u4eba\u4f4f\u5728\u5e97\u4e2d\u5462\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u65e9\u5df2\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u5e10\u623f\u8001\u4eba\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4f60\u2026\u2026\u4f60\u65e9\u5c31\u77e5\u9053\u90a3\u5251\u95e8\u4e94\u9b3c\u7684\u624b\u4e0b\uff0c\u4f4f\u5728\u6b64\u5904\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6307\u7740\u90a3\u5305\u62fe\u8d77\u7684\u8863\u7269\uff0c\u9053\uff1a\u201c\u82e5\u975e\u4ed6\u4eec\u624b\u4e0b\uff0c\u8c01\u6562\u641c\u67e5\u5728\u4e0b\u8863\u7269\u3002\u201d\u4ed6\u5ffd\u7136\u8bdd\u97f3\u4e00\u987f\uff0c\u51b7\u51b7\u5730\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u6211\u5374\u6709\u4e9b\u4e0d\u5927\u660e\u767d\uff0c\u4ed6\u4eec\u662f\u600e\u6837\u77e5\u9053\u6211\u2026\u2026\u201d \u3000\u3000\u5e10\u623f\u8001\u4eba\u95fb\u8a00\uff0c\u5fd9\u4f4e\u58f0\u9053\uff1a\u201c\u516c\u5b50\uff0c\u8001\u673d\u5012\u662f\u77e5\u9053\u4ed6\u4eec\u600e\u751f\u76ef\u4e0a\u4e86\u516c\u5b50\u7684\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4ec0\u4e48\u539f\u56e0\uff1f\u201d \u3000\u3000\u5e10\u623f\u8001\u4eba\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4f60\u5728\u6765\u6b64\u4e4b\u524d\uff0c\u53ef\u662f\u53bb\u8fc7\u90a3\u7a0b\u5bb6\u5e84\u7684\u6545\u5c45\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5728\u4e0b\u679c\u662f\u53bb\u8fc7\uff0c\u2026\u2026\u201d\u5ffd\u7136\uff0c\u4ed6\u604d\u7136\u800c\u7b11\u9053\uff1a\u201c\u662f\u4e86\uff0c\u60f3\u5fc5\u90a3\u6210\u7384\u901a\u548c\u5b97\u58eb\u4e49\u4e00\u5b9a\u6d3e\u6709\u4eba\u4f4f\u5728\u90a3\u7a0b\u5bb6\u5e84\u9644\u8fd1\u76d1\u89c6\uff0c\u662f\u5417\uff0c\u201d \u3000\u3000\u5e10\u623f\u8001\u4eba\u9053\uff1a\u201c\u516c\u5b50\u731c\u7684\u4e0d\u5dee\uff0c\u8001\u673d\u9002\u624d\u5c31\u662f\u542c\u5f97\u90a3\u4e24\u4f4d\u5ba2\u4eba\u8ff0\u53ca\uff0c\u4eca\u65e5\u9ec4\u660f\u6d3e\u5728\u7a0b\u5bb6\u5e84\u7684\u773c\u7ebf\uff0c\u53d1\u73b0\u4e86\u516c\u5b50\uff0c\u6240\u4ee5\u4ed6\u4eec\u5c31\u8ddf\u4e0a\u4e86\u516c\u5b50\uff0c\u800c\u4e14\u6253\u7b97\u2026\u2026\u6253\u7b97\u2026\u2026\u201d\u4ed6\u4e00\u8fde\u8bf4\u4e86\u51e0\u4e2a\u6253\u7b97\uff0c\u5374\u6ca1\u5f80\u4e0b\u8bf4\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u6253\u7b97\u4ec0\u4e48\uff0c\u8001\u4e08\u4e3a\u4f55\u4e0d\u8bf4\uff1f\u201d \u3000\u3000\u5e10\u623f\u8001\u4eba\u5f85\u8d77\u8eab\u53bb\u95e8\u524d\u5f20\u671b\uff0c\u65b9\u96ea\u5b9c\u5374\u4f38\u624b\u62c9\u4f4f\u4e86\u4ed6\uff0c\u6447\u5934\u9053\uff1a\u201c\u8001\u4e08\uff0c\u5341\u4e08\u4e4b\u5185\u65b9\u65e0\u4e00\u4eba\uff0c\u6709\u4ec0\u4e48\u8bdd\u5c3d\u8bf4\u65e0\u59a8\uff01\u201d \u3000\u3000\u5e10\u623f\u8001\u4eba\u6123\u4e86\u4e00\u6123\uff0c\u9053\uff1a\u201c\u5341\u4e08\u4ee5\u5185\u65e0\u4eba\u5417\uff1f\u516c\u5b50\u600e\u4e48\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u8001\u4e08\uff0c\u4e0d\u7792\u4f60\u8bf4\uff0c\u5728\u4e0b\u81ea\u5e7c\u4e60\u8fc7\u5929\u8033\u901a\uff0c\u5341\u4e08\u4e4b\u5185\uff0c\u53ef\u4ee5\u542c\u89c1\u98de\u82b1\u843d\u53f6\u4e4b\u58f0\u3002\u201d \u3000\u3000\u5e10\u623f\u8001\u4eba\u4f3c\u662f\u6709\u4e9b\u4e0d\u4fe1\uff0c\u4f46\u5374\u53c8\u4e0d\u80fd\u4e0d\u4fe1\u5730\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4f60\u53ef\u6ca1\u9a97\u6211\u5427\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u8001\u4e08\u4e43\u662f\u4e3a\u4e86\u5728\u4e0b\u4e4b\u4e8b\uff0c\u6211\u53c8\u600e\u4f1a\u9a97\u4f60\u5462\uff1f\u201d \u3000\u3000\u5e10\u623f\u8001\u4eba\u4ecd\u7136\u6709\u4e9b\u4e0d\u5b89\u5730\u5f20\u671b\u4e86\u7a97\u5916\u4e00\u773c\uff0c\u65b9\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4ed6\u4eec\u8ba1\u8bae\u8981\u5728\u4eca\u591c\u7b49\u4f60\u7761\u719f\u4e4b\u540e\u52a8\u624b\u6697\u7b97\u4e8e\u4f60\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9661\u7136\u53cc\u76ee\u5bd2\u5149\u66b4\u5c04\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u597d\u5f97\u5f88\uff0c\u591a\u8c22\u8001\u4e08\u4f20\u8baf\u4e4b\u60c5\u2026\u2026\u201d\u53cc\u624b\u62b1\u62f3\uff0c\u957f\u957f\u4e00\u63d6\u3002 \u3000\u3000\u5e10\u623f\u8001\u4eba\u76b1\u7709\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4f60\u5355\u8eab\u4e00\u4eba\u5728\u5916\uff0c\u8fd8\u662f\u5c0f\u5fc3\u4e9b\u4e3a\u662f\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u81ea\u662f\u4e0d\u4fbf\u5bf9\u4ed6\u591a\u4f5c\u89e3\u8bf4\uff0c\u53ea\u597d\u62b1\u62f3\u7b11\u9053\uff1a\u201c\u8001\u4e08\u76db\u60c5\uff0c\u5728\u4e0b\u5341\u5206\u611f\u6fc0\uff0c\u4f46\u8bf7\u8001\u4e08\u653e\u5fc3\uff0c\u5728\u4e0b\u5982\u65e0\u81ea\u4fdd\u4e4b\u80fd\uff0c\u4e5f\u51b3\u4e0d\u4f1a\u5192\u8fd9\u7b49\u751f\u6b7b\u7684\u5927\u9669\u4e86\u3002\u201d \u3000\u3000\u5e10\u623f\u8001\u4eba\u4f3c\u662f\u8fd8\u5f85\u591a\u529d\u51e0\u53e5\uff0c\u4f46\u4ed6\u77a7\u5230\u65b9\u96ea\u5b9c\u90a3\u575a\u51b3\u7684\u8138\u8272\uff0c\u53ea\u597d\u628a\u8981\u8bf4\u7684\u8bdd\u54bd\u4e86\u56de\u53bb\uff0c\u4f4e\u53f9\u4e00\u58f0\uff0c\u542f\u95e8\u800c\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5341\u5206\u793c\u8c8c\u5730\u9001\u51fa\u95e8\u5916\uff0c\u7b11\u9053\uff1a\u201c\u8001\u4e08\uff0c\u5728\u4e0b\u4e0d\u9001\u4e86\uff01\u201d \u3000\u3000\u76f4\u5f85\u90a3\u5e10\u623f\u8001\u4eba\u4f5d\u502d\u7684\u8eab\u5f62\uff0c\u5728\u9ed1\u6697\u4e2d\u6d88\u5931\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u8f6c\u8eab\u56de\u623f\uff0c\u63d2\u4e0a\u4e86\u95e8\u6813\uff0c\u628a\u8863\u7269\u5305\u62ac\u6387\u6574\u9f50\uff0c\u7184\u53bb\u706f\u706b\uff0c\u8fd9\u624d\u548c\u8863\u8eba\u5728\u5e8a\u4e0a\u5047\u5bd0\uff0c\u7b49\u7740\u90a3\u6210\u7384\u901a\u7684\u624b\u4e0b\u524d\u6765\u3002 \u3000\u3000\u7ea6\u83ab\u4e09\u66f4\u752b\u8fc7\uff0c\u95e8\u5916\u679c\u7136\u6709\u4e86\u5e94\u58f0\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u6697\u5730\u51b7\u7b11\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u53ea\u6015\u4f60\u4eec\u4e0b\u6765\u2026\u2026\u201d \u3000\u3000\u4ed6\u601d\u5fd6\u672a\u5df2\uff0c\u90a3\u95e8\u6813\u5df2\u88ab\u6765\u4eba\u4ee5\u94a2\u5200\u62e8\u5f00\uff0c\u4e00\u58f0\u6781\u5176\u8f7b\u5fae\u7684\u5440\u7136\u4e4b\u58f0\u4f20\u6765\uff0c\u623f\u95e8\u7a81\u7136\u542f\u5f00\u4e86\u4e00\u6247\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u4e00\u77ac\uff0c\u624d\u6709\u4e00\u4e2a\u8111\u888b\u63a2\u5165\u623f\u5185\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u76ee\u529b\u4e4b\u5f3a\uff0c\u773c\u4e0b\u53ef\u900f\u89c6\u91cd\u96fe\uff0c\u90a3\u4eba\u8111\u888b\u521a\u521a\u4f38\u5165\u623f\u5185\uff0c\u65b9\u96ea\u5b9c\u5c31\u5df2\u77a7\u51fa\u6b64\u4eba\u6b63\u662f\u5148\u524d\u5728\u5e10\u623f\u5916\u9762\u4fa7\u8eab\u76f8\u9047\u7684\u90a3\u4e2a\u7350\u5934\u9f20\u76ee\u7684\u6c49\u5b50\u3002 \u3000\u3000\u90a3\u4eba\u8fd9\u65f6\u5ffd\u7136\u53c8\u7f29\u56de\u5934\u53bb\uff0c\u663e\u7136\u662f\u5728\u8ddf\u95e8\u5916\u7684\u53e6\u4e00\u4e2a\u4f19\u4f34\u5728\u5546\u91cf\u7740\u4ec0\u4e48\uff0c\u4e45\u4e45\u672a\u518d\u5165\u5185\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e0d\u89c9\u6709\u4e9b\u8be7\u5f02\uff0c\u6697\u9053\uff1a\u201c\u4ed6\u4eec\u600e\u5730\u5f04\u5f00\u4e86\u623f\u95e8\uff0c\u5374\u53c8\u4e0d\u8fdb\u6765\u4e86\u5462\u2014\u2014\u201d \u3000\u3000\u7a81\u7136\u95f4\uff0c\u4e00\u4e1d\u706b\u5149\u5728\u95e8\u7f1d\u5916\u4e00\u95ea\u800c\u706d\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u5927\u9707\uff0c\u5bfb\u601d\u9053\uff1a\u201c\u83ab\u975e\u4ed6\u4eec\u7adf\u7136\u5982\u6b64\u4e0b\u4f5c\uff0c\u60f3\u7528\u4ec0\u4e48\u6bd2\u9999\u4e4b\u7c7b\u7684\u6bd2\u7269\uff0c\u6765\u5bf9\u4ed8\u6211\u5417\uff1f\u201d\u8f6c\u5ff5\u53ca\u6b64\uff0c\u4e0d\u7981\u5fc3\u4e2d\u5927\u6012\uff0c\u6b63\u5f85\u8dc3\u8eab\u800c\u8d77\uff0c\u51fa\u623f\u5c06\u4e24\u4eba\u5236\u4f4f\uff0c\u9661\u7136\u4e00\u7f15\u5f02\u9999\u8bf1\u9f3b\u800c\u5165\uff0c\u82e5\u975e\u4ed6\u89c9\u51fa\u6709\u5f02\uff0c\u7acb\u5373\u5c4f\u4f4f\u547c\u5438\uff0c\u540e\u679c\u53ef\u5c31\u4e0d\u582a\u8bbe\u60f3\u4e86\u3002 \u3000\u3000\u7a76\u7adf\u662f\u4ed6\u7684\u6c5f\u6e56\u7ecf\u9a8c\u4e0d\u591f\uff0c\u5426\u5219\uff0c\u4ed6\u5927\u53ef\u5728\u53d1\u73b0\u95e8\u5916\u706b\u628a\u5b50\u6643\u71c3\u4e4b\u9645\uff0c\u5c31\u5148\u95ed\u4f4f\u547c\u5438\uff0c\u6539\u7528\u5e08\u95e8\u9f9f\u606f\u4e4b\u6cd5\u6362\u6c14\uff0c\u5c82\u4e0d\u8fde\u8fd9\u4e00\u70b9\u513f\u9669\u4e5f\u4e0d\u5fc5\u5192\u4e86\u3002 \u3000\u3000\u5c31\u5728\u65b9\u96ea\u5b9c\u5c4f\u4f4f\u547c\u5438\u7684\u540c\u65f6\uff0c\u90a3\u4e24\u4eba\u4e1a\u5df2\u63a8\u95e8\u800c\u5165\u3002 \u3000\u3000\u5982\u6b64\u4e00\u6765\uff0c\u65b9\u96ea\u5b9c\u7d22\u6027\u5c31\u4e0d\u518d\u6709\u52a8\u9759\uff0c\u53ea\u662f\u76d8\u819d\u5750\u5728\u5e8a\u4e0a\uff0c\u51b7\u773c\u77a7\u7740\u4e24\u4eba\u7684\u4e0b\u4e00\u6b65\u884c\u52a8\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u5bb6\u4f19\u7684\u80c6\u5b50\u53ef\u771f\u4e0d\u5c0f\uff0c\u4f46\u89c1\u4ed6\u8de8\u5165\u623f\u5185\u4ee5\u540e\uff0c\u7adf\u7136\u7531\u5176\u4e2d\u4e00\u4eba\u5e4c\u4eae\u4e86\u706b\u647a\u5b50\u70b9\u71c3\u684c\u4e0a\u7684\u6cb9\u706f\u3002 \u3000\u3000\u706f\u5149\u4e00\u4eae\uff0c\u4e24\u4eba\u7acb\u5373\u8f6c\u8eab\u5411\u5e8a\u4e0a\u671b\u53bb\uff0c\u767b\u65f6\uff0c\u4e24\u4eba\u5982\u906d\u96f7\u51fb\u4e00\u822c\u6123\u4f4f\u4e86\u3002 \u3000\u3000\u90a3\u7350\u5934\u9f20\u76ee\u7684\u4e00\u4f4d\uff0c\u4e00\u6296\u624b\u4e2d\u5355\u5200\uff0c\u98a4\u58f0\u559d\u9053\uff1a\u201c\u4f60\u2026\u2026\u6ca1\u6709\u6655\u5012\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u773c\u89c1\u8fd9\u4e24\u4eba\u9f3b\u5b54\u4e4b\u4e2d\uff0c\u585e\u6709\u4e24\u4e2a\u9ed1\u8272\u836f\u4e38\uff0c\u77e5\u9053\u5fc5\u662f\u7528\u6765\u795b\u6bd2\u4e4b\u7269\uff0c\u5f53\u4e0b\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u201c\u6211\u4f55\u4e0d\u5148\u5236\u4f4f\u5176\u4e2d\u4e00\u4eba\uff0c\u5148\u884c\u53d6\u5230\u795b\u6bd2\u836f\u4e38\u518d\u884c\u5f00\u53e3\u8bf4\u8bdd\uff0c\u4e5f\u514d\u5f97\u4e00\u4e0d\u5c0f\u5fc3\uff0c\u4e2d\u4e86\u4ed6\u4eec\u7684\u9053\u513f\u2026\u2026\u201d\u4e3b\u610f\u65e2\u5b9a\uff0c\u65b9\u96ea\u5b9c\u51b7\u54fc\u4e00\u58f0\uff0c\u9661\u7136\u53cc\u624b\u4e00\u62cd\u5e8a\u6cbf\uff0c\u5168\u8eab\u51cc\u7a7a\u800c\u8d77\uff0c\u76f4\u5411\u4e24\u4eba\u63a0\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u81ea\u5df1\u5e76\u4e0d\u77e5\u9053\u773c\u4e0b\u4ed6\u7684\u6b66\u529f\u4e4b\u9ad8\uff0c\u5df2\u5c5e\u9876\u5c16\u9ad8\u624b\uff0c\u6b64\u523b\u5fc3\u4e2d\u6025\u60f3\u53d6\u5f97\u5bf9\u65b9\u7684\u6cd5\u6bd2\u836f\u7269\uff0c\u662f\u4ee5\u884c\u52a8\u4e4b\u95f4\uff0c\u81ea\u662f\u7528\u4e0a\u5168\u529b\uff0c\u53bb\u52bf\u4e4b\u5feb\uff0c\u7b80\u76f4\u5df2\u6709\u4ee4\u4eba\u76ee\u4e0d\u6687\u63a5\u4e4b\u611f\uff0c\u4f46\u89c1\u4eba\u5f71\u4e00\u82b1\uff0c\u7350\u5934\u9f20\u76ee\u7684\u6c49\u5b50\uff0c\u624b\u4e2d\u5355\u5200\u5c1a\u672a\u626c\u8d77\uff0c\u4eba\u5df2\u5012\u5728\u5730\u4e0a\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5de6\u624b\u4e00\u635e\uff0c\u5c06\u90a3\u4eba\u6293\u5728\u624b\u4e2d\uff0c\u540c\u65f6\u53f3\u624b\u4e00\u63a2\uff0c\u5df2\u628a\u90a3\u6c49\u5b50\u9f3b\u5b54\u4e4b\u4e2d\u7684\u4e24\u7c92\u836f\u4e38\u53d6\u51fa\uff0c\u585e\u5728\u81ea\u5df1\u9f3b\u7aef\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u90a3\u53e6\u5916\u7684\u4e00\u4f4d\u6c49\u5b50\uff0c\u4f3c\u662f\u5df2\u7ecf\u5413\u5f97\u5446\u4e86\uff0c\u624b\u4e2d\u867d\u7136\u6709\u4e00\u67c4\u5315\u9996\uff0c\u5374\u662f\u6728\u6123\u6123\u7684\u7ad9\u5728\u90a3\u513f\uff0c\u52a8\u90fd\u4e0d\u6562\u52a8\u4e00\u4e0b\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u56de\u624b\u5c06\u90a3\u7350\u5934\u9f20\u76ee\u6c49\u5b50\u6401\u5728\u5730\u4e0a\uff0c\u4e24\u773c\u7728\u4e86\u4e00\u7728\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u53ef\u662f\u6210\u7384\u901a\u7684\u624b\u4e0b\uff1f\u201d \u3000\u3000\u8fd9\u624b\u63e1\u5315\u9996\u7684\u6c49\u5b50\uff0c\u957f\u5f97\u6bd4\u90a3\u7350\u5934\u9f20\u76ee\u4eba\u8981\u9b41\u68a7\u5f97\u591a\uff0c\u4f46\u6b64\u65f6\u5374\u4e5f\u50cf\u72d7\u718a\u4e00\u822c\u5730\u7f29\u5728\u4e00\u8d77\uff0c\u5bf9\u65b9\u96ea\u5b9c\u6240\u95ee\u4e4b\u8bdd\uff0c\u5b9b\u5982\u5145\u8033\u4e0d\u95fb\u4e00\u822c\uff0c\u4e45\u4e45\u672a\u66fe\u56de\u7b54\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u77a7\u7740\u4ed6\u8fd9\u4efd\u7a9d\u56ca\u76f8\uff0c\u5fc3\u4e2d\u5b9e\u662f\u751f\u6c14\uff0c\u5251\u7709\u4e00\u76b1\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4f60\u662f\u54d1\u5df4\u5417\uff1f\u201d \u3000\u3000\u90a3\u4eba\u88ab\u65b9\u96ea\u5b9c\u7280\u5229\u7684\u76ee\u5149\uff0c\u77a7\u5f97\u4e0d\u7531\u81ea\u4e3b\u5730\u5411\u540e\u9000\u4e86\u4e24\u6b65\uff0c\u6447\u7740\u5934\u98a4\u58f0\u9053\uff1a\u201c\u5c0f\u2026\u2026 \u3000\u3000\u5c0f\u7684\u4f1a\u2026\u2026\u8bb2\u8bdd\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f88\u597d\uff0c\u4f60\u7528\u5fc3\u542c\u7740\uff0c\u4e00\u53e5\u4e00\u53e5\u5730\u7167\u5b9e\u56de\u7b54\uff01\u201d\u8bdd\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u90a3\u4eba\u5e94\u9053\uff1a\u201c\u5c0f\u7684\u540d\u53eb\u8d75\u4e09\u62f3\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5931\u58f0\u9053\uff1a\u201c\u8d75\u4e09\u62f3\uff0c\u8fd9\u540d\u53f7\u4e0d\u9519\uff01\u201d \u3000\u3000\u8d75\u4e09\u62f3\u77a7\u89c1\u65b9\u96ea\u5b9c\u7684\u8138\u4e0a\u7b11\u5bb9\uff0c\u4e0d\u7981\u80c6\u5b50\u5927\u4e86\u4e0d\u5c11\uff0c\u626c\u4e86\u626c\u53f3\u62f3\uff0c\u9053\uff1a\u201c\u5c0f\u7684\u5929\u751f\u795e\u529b\uff0c\u624b\u4e0b\u5f88\u5c11\u8d70\u8fc7\u4e09\u62f3\u4e4b\u4eba\uff0c\u6240\u4ee5\uff0c\u4f19\u8ba1\u4eec\u90fd\u53eb\u6211\u8d75\u4e09\u62f3\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u81ea\u4e00\u7b11\uff0c\u4ed8\u9053\uff1a\u201c\u770b\u6b64\u4eba\u8bf4\u8bdd\uff0c\u5012\u50cf\u662f\u803f\u76f4\u7684\u8001\u7c97\uff0c\u7a0d\u65f6\u5012\u662f\u4e0d\u5fc5\u4e3a\u96be\u4ed6\u4e86\u3002\u201d\u5f53\u4e0b\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u662f\u5251\u95e8\u4e94\u9b3c\u95e8\u4e0b\u3002\u201d \u3000\u3000\u8d75\u4e09\u62f3\u9053\uff1a\u201c\u516c\u5b50\uff0c\u5c0f\u7684\u4e3b\u4eba\u5df2\u7136\u6539\u79f0\u5251\u95e8\u53cc\u4fa0\u4e86\uff0c\u4f60\u8001\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5251\u7709\u8038\u52a8\uff0c\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e94\u9b3c\u5df2\u4e27\u5176\u4e09\uff0c\u81ea\u7136\u4e0d\u80fd\u518d\u7528\u6614\u5e74\u7684\u540d\u53f7\u6df7\u4e16\u2026\u2026\u201d\u5fae\u5fae\u4e00\u987f\u8bdd\u97f3\uff0c\u63a5\u9053\uff0c\u201c\u4e0d\u8bba\u662f\u4e8c\u9b3c\u4e5f\u597d\uff0c\u8fd8\u662f\u53cc\u4fa0\u4e5f\u597d\uff0c\u4f60\u7684\u4e3b\u4eba\u53ef\u662f\u6210\u7384\u901a\u548c\u8463\u65b9\uff1f\u201d \u3000\u3000\u8d75\u4e09\u62f3\u9053\uff1a\u201c\u5c0f\u7684\u4e3b\u4eba\u6b63\u662f\u6210\u3001\u8463\u53cc\u4fa0\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u51b7\u51b7\u7b11\u4e86\u4e00\u58f0\uff0c\u7a81\u7136\u559d\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4eba\u591c\u5165\u6211\u7684\u623f\u95f4\uff0c\u6697\u7528\u6bd2\u9999\u8ff7\u4eba\uff0c\u53ef\u662f\u8ba4\u8bc6\u6211\u662f\u8c01\uff1f\u201d \u3000\u3000\u8d75\u4e09\u62f3\u9053\uff1a\u201c\u8fd9\u2026\u2026\u8fd9\u4e2a\u2026\u2026\u5c0f\u7684\u53ea\u662f\u5949\u547d\u884c\u4e8b\u800c\u5df2\uff0c\u516c\u5b50\u83ab\u8981\u89c1\u602a\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u60f3\uff1a\u4f60\u4eec\u60f3\u8981\u6211\u7684\u547d\uff0c\u8fd8\u8981\u53eb\u6211\u83ab\u89c1\u602a\u5417\uff1f\u4e0d\u8fc7\u2026\u2026\u53e3\u4e2d\u5374\u6de1\u6de1\u7684\u8bf4\u9053\uff1a \u3000\u3000\u201c\u5949\u4ec0\u4e48\u4eba\u547d\uff1f\u201d \u3000\u3000\u8d75\u4e09\u62f3\u9053\uff1a\u201c\u81ea\u7136\u662f\u4e3b\u4eba\u4e4b\u547d\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u6210\u7384\u901a\u73b0\u5728\u4f55\u5904\uff1f\u201d \u3000\u3000\u8d75\u4e09\u62f3\u9053\uff1a\u201c\u5c0f\u7684\u4e0d\u77e5\u9053\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u77a7\u90a3\u8d75\u4e09\u62f3\u7684\u795e\u6001\uff0c\u4f3c\u662f\u5e76\u672a\u8bf4\u8c0e\uff0c\u5f53\u4e0b\u76b1\u7709\u9053\uff1a\u201c\u4f60\u5949\u4e86\u4e3b\u4eba\u4e4b\u547d\uff0c\u524d\u6765\u6697\u7b97\u5728\u4e0b\uff0c\u53c8\u600e\u4f1a\u4e0d\u77e5\u9053\u4e3b\u4eba\u4f55\u5728\u5462\uff1f\u201d \u3000\u3000\u8d75\u4e09\u62f3\u9053\uff1a\u201c\u516c\u5b50\uff0c\u5c0f\u7684\u8bf4\u7684\u662f\u5b9e\u8bdd\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u4eec\u4e0d\u662f\u76f4\u63a5\u53d7\u547d\u800c\u6765\u4e86\uff1f\u201d \u3000\u3000\u8d75\u4e09\u62f3\u7b11\u9053\uff1a\u201c\u516c\u5b50\u8bf4\u5bf9\u4e86\uff0c\u5c0f\u7684\u4eec\u5f53\u7136\u662f\u4e0d\u591f\u8d44\u683c\u76f4\u63a5\u542c\u547d\u4e3b\u4eba\u5566\uff01\u201d\u6b64\u4eba\u5df2\u7136\u6e10\u6e10\u5730\u4e0d\u518d\u80c6\u602f\u4e86\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4f60\u5949\u4f55\u4eba\u6240\u5dee\u6765\u6b64\uff1f\u201d \u3000\u3000\u8d75\u4e09\u62f3\u9053\uff1a\u201c\u5c0f\u7684\u2026\u2026\u662f\u5949\u4e86\u5b97\u5802\u4e3b\u4e4b\u547d\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u672a\u7b49\u4ed6\u8bf4\u5b8c\uff0c\u5fcd\u4e0d\u4f4f\u8131\u53e3\u9053\uff1a\u201c\u54ea\u4e00\u4f4d\u5b97\u5802\u4e3b\uff1f\u53ef\u662f\u5b97\u58eb\u4e49\u5417\uff1f\u201d \u3000\u3000\u8d75\u4e09\u62f3\u9053\uff1a\u201c\u4e0d\u662f\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4e0d\u662f\u91d1\u67aa\u5b97\u58eb\u4e49\uff1f\u201d \u3000\u3000\u8d75\u4e09\u62f3\u9053\uff1a\u201c\u516c\u5b50\uff0c\u8fd9\u5b97\u5802\u4e3b\u4e43\u662f\u5b97\u5821\u4e3b\u7684\u516c\u5b50\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u604d\u7136\u9053\uff1a\u201c\u662f\u90a3\u5b97\u8d35\u5417\uff1f\u201d \u3000\u3000\u8d75\u4e09\u62f3\u9053\uff1a\u201c\u6b63\u662f\u5b97\u5927\u516c\u5b50\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u51b7\u54fc\u9053\uff1a\u201c\u5b97\u8d35\u73b0\u5728\u4f55\u5904\uff1f\u201d \u3000\u3000\u8d75\u4e09\u62f3\u9053\uff1a\u201c\u5b97\u5802\u4e3b\u5c31\u4f4f\u5728\u5609\u5b9a\u57ce\u4e4b\u5185\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5ffd\u7136\u6c89\u58f0\u9053\uff1a\u201c\u8d75\u4e09\u62f3\uff0c\u4f60\u60f3\u4e0d\u60f3\u6d3b\uff1f\u201d \u3000\u3000\u8d75\u4e09\u62f3\u4e0d\u81ea\u4e3b\u5730\u6478\u4e86\u6478\u8111\u888b\uff0c\u9053\uff1a\u201c\u516c\u5b50\uff0c\u8682\u8681\u5c1a\u4e14\u8d2a\u751f\uff0c\u5c0f\u7684\u5f53\u7136\u6015\u6b7b\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4f60\u65e2\u662f\u6015\u6b7b\uff0c\u6211\u5012\u662f\u6709\u4e2a\u4f7f\u4f60\u6c42\u751f\u4e4b\u8def\uff0c\u4f60\u613f\u4e0d\u613f\u610f\u542c\u547d\uff1f\u201d \u3000\u3000\u8d75\u4e09\u62f3\u9053\uff1a\u201c\u613f\u610f\uff0c\u5c0f\u7684\u4e00\u5207\u90fd\u613f\u9075\u547d\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8d75\u4e09\u62f3\uff0c\u4f60\u53ef\u4e0d\u8bb8\u540e\u6094\uff01\u201d \u3000\u3000\u8d75\u4e09\u62f3\u9053\uff1a\u201c\u5c0f\u7684\u51b3\u4e0d\u540e\u6094\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u5982\u6b64\u751a\u597d\uff0c\u4f60\u53bb\u53eb\u5b97\u8d35\u6765\u89c1\u6211\u3002\u201d \u3000\u3000\u8d75\u4e09\u62f3\u6123\u4e86\u4e00\u6123\uff0c\u9053\uff1a\u201c\u4f60\u8001\u2026\u2026\u516c\u5b50\uff0c\u4f60\u662f\u8981\u6211\u628a\u5b97\u5802\u4e3b\u627e\u6765\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4f60\u53ef\u662f\u4e0d\u613f\uff1f\u201d \u3000\u3000\u8d75\u4e09\u62f3\u9053\uff1a\u201c\u5c0f\u7684\u613f\u610f\u2026\u2026\u201d\u4ed6\u8bdd\u6ca1\u8bf4\u5b8c\uff0c\u8f6c\u8eab\u5c31\u5411\u95e8\u5916\u8dd1\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u7a81\u7136\u4f38\u624b\u4e00\u62e6\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d \u3000\u3000\u8d75\u4e09\u62f3\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u516c\u5b50\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4f60\u5982\u662f\u4e00\u53bb\u4e0d\u56de\uff0c\u6211\u53c8\u5c06\u5230\u5982\u4f55\u5904\u5bfb\u627e\u4f60\u5462\uff1f\u201d \u3000\u3000\u8d75\u4e09\u62f3\u9053\uff1a\u201c\u5c0f\u7684\u600e\u6562\uff0c\u516c\u5b50\u653e\u5fc3\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u51b7\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u672c\u662f\u5bf9\u4f60\u6781\u4e3a\u653e\u5fc3\uff0c\u5426\u5219\uff0c\u4e5f\u4e0d\u4f1a\u8981\u4f60\u53bb\u53eb\u90a3\u5b97\u8d35\u7684\u4e86\u2026\u2026\u201d \u3000\u3000\u53f3\u624b\u8fc5\u5feb\u5730\u62cd\u51fa\u4e09\u638c\uff0c\u8f7b\u51fb\u5728\u8d75\u4e09\u62f3\u80a9\u808b\u7b49\u5904\uff0c\u559d\u9053\uff1a\u201c\u5feb\u53bb\u5feb\u6765\uff0c\u5728\u4e0b\u5728\u6b64\u7b49\u4f60\u4e00\u4e2a\u65f6\u8fb0\uff01\u201d \u3000\u3000\u8d75\u4e09\u62f3\u6328\u4e86\u4e09\u638c\uff0c\u867d\u662f\u4e0d\u91cd\uff0c\u4f46\u5374\u89c9\u51fa\u4e2d\u638c\u4e4b\u5904\uff0c\u6709\u4e9b\u9178\u75bc\uff0c\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4f60\u53ef\u662f\u5c01\u4e86\u5c0f\u4eba\u7684\u7a74\u9053\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u51b7\u54fc\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e00\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u4f60\u5982\u662f\u8d76\u4e0d\u56de\u6765\uff0c\u5c31\u5c06\u5168\u8eab\u7ecf\u8109\u786c\u5316\u800c\u6b7b\uff0c\u4f60\u5982\u662f\u4e0d\u60f3\u65e9\u6b7b\uff0c\u6700\u597d\u662f\u8ddf\u90a3\u5b97\u8d35\u540c\u6765\uff01\u201d \u3000\u3000\u8d75\u4e09\u62f3\u8138\u8272\u987f\u65f6\u53d8\u5f97\u715e\u767d\uff0c\u5446\u5446\u5730\u77a7\u7740\u65b9\u96ea\u5b9c\uff0c\u53e3\u4e2d\u8fde\u58f0\u8bf4\u9053\uff1a\u201c\u5c0f\u4eba\u2026\u2026\u5c0f\u4eba\u2026\u2026 \u3000\u3000\u8fd9\u5c31\u53bb\u2026\u2026\u201d\u4ed6\u867d\u7136\u53e3\u4e2d\u8bf4\u8d70\uff0c\u4eba\u5374\u6839\u672c\u672a\u52a8\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u8be7\u5f02\u7684\u7b11\u9053\uff1a\u201c\u4f60\u4e3a\u4f55\u8fd8\u4e0d\u52a8\u8eab\uff1f\u201d \u3000\u3000\u8d75\u4e09\u62f3\u51e0\u4e4e\u662f\u75db\u82e6\u5931\u58f0\u5730\u5e94\u9053\uff1a\u201c\u516c\u5b50\uff0c\u5c0f\u7684\u8be5\u6b7b\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u5f97\u5446\u4e86\u4e00\u5446\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u600e\u4e48\u4e86\uff1f\u201d \u3000\u3000\u8d75\u4e09\u62f3\u7a81\u7136\u8dea\u5012\u5728\u5730\uff0c\u78d5\u5934\u9053\uff1a\u201c\u516c\u5b50\uff0c\u5c0f\u4eba\u65e0\u6cd5\u89c1\u5230\u5b97\u5802\u4e3b\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u5fc3\u4e2d\u5df2\u7136\u6709\u4e9b\u660e\u767d\uff0c\u4f46\u5374\u559d\u9053\uff1a\u201c\u90a3\u4f60\u662f\u4e0d\u60f3\u6d3b\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u8d75\u4e09\u62f3\u987f\u65f6\u53e9\u5934\u4e0d\u6b62\uff0c\u54c0\u58f0\u54ed\u6c42\u9053\uff1a\u201c\u516c\u5b50\uff0c\u5c0f\u4eba\u7684\u8eab\u4efd\u5351\u5fae\uff0c\u65e0\u6cd5\u89c1\u5f97\u5230\u5802\u4e3b\u554a\u2026\u2026\uff0c\u671b\u4f60\u8001\u5f00\u6069\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u770b\u5f97\u51fa\u8fd9\u8d75\u4e09\u62f3\u5e76\u975e\u505a\u5047\uff0c\u5f53\u4e0b\u5fcd\u4e0d\u4f4f\u5931\u58f0\u7b11\u9053\uff1a\u201c\u521a\u624d\u4f60\u4e3a\u4f55\u4e00\u53e3\u7b54\u5e94\u4e0b\u6765\uff0c\u81ea\u613f\u524d\u53bb\u8bf7\u90a3\u5b97\u5802\u4e3b\u6765\u6b64\uff1f\u770b\u4f60\u8fd9\u7b49\u53cd\u53cd\u590d\u590d\u7684\u8a00\u8c08\uff0c\u8db3\u8bc1\u4e0d\u662f\u4e2a\u597d\u4eba\u3002\u201d \u3000\u3000\u8d75\u4e09\u62f3\u53ea\u5413\u5f97\u53e9\u5934\u5982\u6363\u849c\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5c0f\u4eba\u4e00\u65f6\u7cca\u6d82\uff0c\u671b\u6c42\u4f60\u8001\u9ad8\u62ac\u8d35\u624b\uff0c\u653e\u8fc7\u5c0f\u4eba\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u90a3\u4e48\u65b9\u4fbf\uff1f\u4f60\u53ef\u662f\u628a\u5728\u4e0b\u5f53\u4f5c\u4e86\u5bb9\u6613\u6b3a\u9a97\u7684\u4eba\u5417\uff1f\u201d\u8bed\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u5728\u4e0b\u5012\u662f\u6709\u4e2a\u529e\u6cd5\uff0c\u53ea\u8981\u4f60\u7b54\u5e94\uff0c\u5728\u4e0b\u4fbf\u53ef\u89e3\u5f00\u4f60\u88ab\u5236\u7684\u7a74\u9053\uff01\u201d \u3000\u3000\u8d75\u4e09\u62f3\u5fd9\u9053\uff1a\u201c\u4ec0\u4e48\u529e\u6cd5\uff1f\u4f60\u8001\u5feb\u8bb2\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4f60\u65e2\u662f\u89c1\u4e0d\u7740\u90a3\u5b97\u8d35\u4e4b\u9762\uff0c\u4f46\u603b\u77e5\u9053\u4ed6\u4f4f\u5728\u4f55\u5904\u7684\u4e86\uff1f\u201d \u3000\u3000\u8d75\u4e09\u62f3\u9053\uff1a\u201c\u8fd9\u4e2a\u5c0f\u4eba\u5012\u662f\u77e5\u9053\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f88\u597d\uff0c\u4f60\u9886\u7740\u5728\u4e0b\u524d\u53bb\uff0c\u89c1\u5230\u5b97\u8d35\u4ee5\u540e\uff0c\u5728\u4e0b\u5c31\u89e3\u53bb\u4f60\u88ab\u5236\u7684\u7a74\u9053\uff01\u201d \u3000\u3000\u8d75\u4e09\u62f3\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u8fd9\u2026\u2026\u5c0f\u4eba\u5982\u662f\u9886\u4e86\u4f60\u8001\u53bb\u89c1\u5b97\u5802\u4e3b\uff0c\u53ea\u6015\u5c0f\u4eba\u4f9d\u7136\u4e5f\u6d3b\u4e0d\u6210\u5566\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u65e2\u662f\u4e0d\u6562\u9886\u6211\u524d\u53bb\uff0c\u6211\u5c31\u62cd\u9192\u4f60\u7684\u540c\u4f34\uff0c\u8981\u4ed6\u5e26\u8def\u524d\u53bb\u4fbf\u4e86\uff0c\u8d75\u4e09\u62f3\uff0c\u6211\u4e5f\u4e0d\u518d\u4e3a\u96be\u4e8e\u4f60\uff0c\u4f60\u8bf7\u5427\uff01\u201d \u3000\u3000\u8d75\u4e09\u62f3\u4e00\u542c\uff0c\u767b\u65f6\u51c9\u4e86\u534a\u622a\uff0c\u4ed6\u4e00\u8fde\u53c8\u78d5\u4e86\u4e09\u4e2a\u54cd\u5934\uff0c\u53eb\u9053\uff1a\u201c\u516c\u5b50\u9976\u547d\u2026\u2026\u5c0f\u4eba\u613f\u610f\u7ed9\u4f60\u8001\u5e26\u8def\u2026\u2026\u201d \u3000\u3000\u6562\u60c5\u8fd9\u8d75\u4e09\u62f3\u4f3c\u662f\u60f3\u901a\u4e86\uff0c\u5982\u662f\u4e0d\u5e26\u8def\u53bb\u89c1\u5b97\u8d35\uff0c\u4ed6\u773c\u4e0b\u4e0d\u51fa\u4e00\u4e2a\u65f6\u8fb0\uff0c\u5c31\u5f97\u5168\u8eab\u7a74\u8109\u50f5\u786c\u800c\u6b7b\uff0c\u5018\u82e5\u81ea\u5df1\u5e26\u4ed6\u53bb\u89c1\u5b97\u8d35\u8bf4\u4e0d\u5b9a\u8fd9\u59d3\u65b9\u7684\u88ab\u5b97\u5802\u4e3b\u62ff\u4e0b\uff0c\u81ea\u5df1\u4e0d\u4f46\u4e0d\u4f1a\u4e27\u547d\uff0c\u6216\u8bb8\u8fd8\u5f97\u7740\u4e00\u6869\u5927\u529f\u54e9\uff01\u201d\u4ed6\u8f6c\u5ff5\u81f3\u6b64\uff0c\u4fbf\u8fde\u5fd9\u5e94\u5141\u5e26\u8def\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5374\u5728\u5fc3\u4e2d\u6697\u9053\uff1a\u4e0d\u6015\u4f60\u4e0d\u80af\u5e26\u8def\u2026\u2026\u53e3\u4e2d\u53ef\u4f9d\u65e7\u51b7\u51b7\u5730\u559d\u9053\uff1a\u201c\u625b\u8d77\u4f60\u7684\u4f19\u4f34\uff0c\u54b1\u4eec\u8fd9\u5c31\u524d\u53bb\u89c1\u5b97\u8d35\u3002\u201d \u3000\u3000\u90a3\u8d75\u4e09\u62f3\u82e6\u7b11\u4e00\u58f0\uff0c\u6293\u8d77\u8eba\u5728\u5730\u4e0a\u7684\u6c49\u5b50\uff0c\u625b\u5728\u80a9\u5934\uff0c\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u516c\u5b50\uff0c\u54b1\u4eec\u8fd9\u5c31\u8d70\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u524d\u5934\u9886\u8def\u2026\u2026\u201d \u3000\u3000\u90a3\u8d75\u4e09\u62f3\u5f20\u4e86\u5f20\u5634\uff0c\u4f3c\u662f\u60f3\u8bf4\u4ec0\u4e48\uff0c\u4f46\u77a7\u7740\u65b9\u96ea\u5b9c\u90a3\u5bd2\u51b0\u4f3c\u7684\u8138\u5b54\uff0c\u5374\u8fde\u5fd9\u5e94\u4e86\u4e00\u58f0\uff1a\u201c\u5c0f\u4eba\u9075\u547d\u2026\u2026\u201d\u8f6c\u8eab\u4fbf\u5411\u95e8\u5916\u884c\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u7d27\u597d\u4f69\u5251\uff0c\u63a9\u4e0a\u4e86\u623f\u95e8\uff0c\u968f\u5728\u8d75\u4e09\u62f3\u8eab\u540e\uff0c\u51fa\u4e86\u60a6\u6765\u5ba2\u627e\u3002 \u3000\u3000\u7ea6\u83ab\u8f6c\u8fc7\u4e86\u56db\u4e94\u6761\u5927\u8857\uff0c\u8d75\u4e09\u62f3\u5ffd\u7136\u6b62\u6b65\u4e0d\u8fdb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u501f\u7740\u6de1\u6de1\u7684\u6708\u5149\uff0c\u6253\u91cf\u4e86\u4e00\u4e0b\u56db\u5468\u666f\u8272\uff0c\u5374\u89c1\u505c\u8eab\u4e4b\u5904\uff0c\u4e43\u662f\u4e00\u6240\u751a\u4e3a\u5e7d\u9759\u7684\u82b1\u56ed\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5251\u76fe\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u4e00\u9053\u4fa7\u95e8\u5417\uff1f\u201d \u3000\u3000\u8d75\u4e09\u62f3\u9053\uff1a\u201c\u5c0f\u4eba\u7b49\u8fdb\u51fa\uff0c\u90fd\u662f\u8d70\u7684\u8fd9\u9053\u4fa7\u95e8\u3002\u201d \u3000\u3000\u8a00\u96ea\u5b9c\u7565\u4e00\u6c89\u541f\uff0c\u9053\uff1a\u201c\u4e0a\u524d\u53eb\u95e8\u3002\u201d \u3000\u3000\u8d75\u4e09\u62f3\u8fdf\u7591\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u516c\u5b50\uff0c\u8fd9\u7b49\u4e09\u66f4\u534a\u591c\u53eb\u95e8\uff0c\u53ea\u6015\u5b97\u5802\u4e3b\u4e5f\u4e0d\u80af\u63a5\u89c1\u4f60\u8001\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u7684\u5fc3\u4e2d\u5927\u4e3a\u5fff\u6012\uff0c\u6697\u9053\uff1a\u201c\u6b64\u4eba\u5916\u8868\u770b\u6765\uff0c\u751a\u4f3c\u803f\u76f4\u7684\u6c49\u5b50\uff0c\u600e\u4e48\u884c\u4e8b\u5374\u53c8\u5982\u6b64\u62d6\u6ce5\u5e26\u6c34\u3002\u201d\u5f53\u4e0b\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u90a3\u5b97\u8d35\u4e0d\u89c1\u6211\uff0c\u4e0d\u5173\u4f60\u7684\u4e8b\uff0c\u4f60\u5982\u60f3\u6d3b\u4e0b\u53bb\uff0c\u5c31\u65e9\u4e00\u70b9\u4e0a\u524d\u53eb\u95e8\uff01\u201d \u3000\u3000\u90a3\u8d75\u4e09\u62f3\u5413\u5f97\u8fde\u9000\u4e24\u6b65\uff0c\u4e00\u8f6c\u8eab\uff0c\u5927\u6b65\u5411\u90a3\u82b1\u56ed\u7684\u4fa7\u95e8\u8d70\u53bb\uff0c\u65e5\u89c1\u4ed6\u4f38\u624b\u5728\u95e8\u4e0a\u8f7b\u8f7b\u5730\u6572\u4e86\u4e09\u4e0b\uff0c\u4fbf\u653e\u4e0b\u625b\u5728\u80a9\u5934\u7684\u540c\u4f34\uff0c\u5782\u624b\u8083\u7acb\u95e8\u5916\u3002\u4e0d\u591a\u65f6\u90a3\u9053\u4fa7\u95e8\u5440\u7684\u4e00\u58f0\uff0c\u5411\u5185\u62c9\u5f00\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u7531\u6697\u5904\u671b\u53bb\uff0c\u53ea\u89c1\u4e00\u4f4d\u5341\u5206\u9b41\u68a7\u7684\u52b2\u88c5\u5730\u6c49\u5b50\uff0c\u624b\u91cc\u4e3e\u7740\u4e00\u76cf\u6c14\u6b7b\u98ce\u706f\uff0c\u63a2\u5934\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u90a3\u8d75\u4e09\u62f3\u8fde\u5fd9\u4e0a\u524d\uff0c\u4f4e\u58f0\u4f5c\u63d6\u9053\uff1a\u201c\u4e8c\u7237\uff0c\u662f\u5c0f\u7684\u8d75\u4e09\u2026\u2026\u201d \u3000\u3000\u8fd9\u53eb\u505a\u4e8c\u7237\u7684\u4eba\uff0c\u662f\u4f3c\u6123\u4e00\u6123\uff0c\u559d\u9053\uff1a\u201c\u8fd9\u534a\u591c\u4e09\u66f4\u7684\uff0c\u4f60\u8dd1\u6765\u5e72\u5417\uff1f\u201d \u3000\u3000\u8d75\u4e09\u62f3\u6307\u7740\u5730\u4e0a\u7684\u6c49\u5b50\uff0c\u9053\uff1a\u201c\u4e8c\u7237\uff0c\u5c0f\u7684\u662f\u5949\u4e86\u526f\u5802\u4e3b\u4e4b\u547d\u548c\u51af\u4e03\u529e\u5dee\u4e8b\u7684\u2026\u2026\u201d \u3000\u3000\u6b64\u523b\uff0c\u90a3\u95e8\u5185\u7684\u4e8c\u7237\uff0c\u5927\u6982\u5df2\u77a7\u51fa\u8eba\u5728\u5730\u4e0a\u7684\u51af\u4e03\uff0c\u7a81\u7136\u8de8\u51fa\u95e8\u5916\uff0c\u5411\u8d75\u4e09\u62f3\u559d\u9053\uff1a \u3000\u3000\u201c\u8fd9\u662f\u600e\u4e48\u4e86\uff1f\u51af\u4e03\u6b7b\u4e86\u5417\uff1f\u201d \u3000\u3000\u8d75\u4e09\u62f3\u6447\u5934\u9053\uff1a\u201c\u6ca1\u6709\u2026\u2026\u53ea\u4e0d\u8fc7\u88ab\u70b9\u4e86\u7a74\u9053\u800c\u5df2\uff0c\u4e8c\u7237\uff0c\u70e6\u4f60\u8001\u901a\u7980\u526f\u5802\u4e3b\u4e00\u58f0\uff0c\u5c31\u8bf4\u5c0f\u7684\u6c42\u89c1\u3002\u201d \u3000\u3000\u90a3\u53eb\u505a\u4e8c\u7237\u7684\u4eba\uff0c\u6c89\u541f\u4e86\u4e00\u4f1a\u513f\uff0c\u65b9\u9053\uff1a\u201c\u4f60\u7b49\u7740\uff0c\u6211\u53bb\u77a7\u77a7\u526f\u5802\u4e3b\u662f\u5426\u672a\u66fe\u5b89\u6b47\u2026\u2026\u201d\u8bdd\u97f3\u672a\u5df2\uff0c\u4fbf\u81ea\u8f6c\u8eab\u8de8\u5165\u95e8\u5185\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u7f13\u6b65\u8d70\u5230\u8d75\u4e09\u62f3\u8eab\u524d\uff0c\u7b11\u9053\uff1a\u201c\u5f88\u597d\uff0c\u4e0d\u77e5\u9053\u8fd9\u4f4d\u526f\u5802\u4e3b\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u8d75\u4e09\u62f3\u9053\uff1a\u201c\u526f\u5802\u4e3b\u59d3\u5e84\uff0c\u5355\u540d\u4e00\u4e2a\u6377\u5b57\uff0c\u516c\u5b50\uff0c\u4f60\u8001\u53ef\u5f97\u5c0f\u5fc3\u4e00\u4e9b\uff0c\u636e\u8bf4\u2026\u2026\u636e\u8bf4\u2026\u2026\u201d \u3000\u3000\u77a7\u4ed6\u541e\u541e\u5410\u5410\uff0c\u6b32\u8a00\u4e0d\u6562\u7684\u795e\u6001\uff0c\u65b9\u96ea\u5b9c\u4e0d\u7981\u597d\u7b11\uff0c\u559d\u9053\uff1a\u201c\u636e\u8bf4\u4ec0\u4e48\uff0c\u6b64\u5904\u522b\u65e0\u4ed6\u4eba\uff0c\u6709\u4ec0\u4e48\u4e0d\u6562\u8bf4\u7684\uff1f\u201d \u3000\u3000\u8d75\u4e09\u62f3\u9053\uff1a\u201c\u516c\u5b50\uff0c\u636e\u8bf4\u8fd9\u5e84\u526f\u5802\u4e3b\u7684\u6b66\u529f\uff0c\u8fd8\u5728\u5b97\u5802\u4e3b\u4e4b\u4e0a\uff0c\u516c\u5b50\u6700\u597d\u83ab\u8981\u8ddf\u4ed6\u5435\u7ffb\uff0c\u5426\u5219\uff0c\u5403\u4e8f\u7684\u53ea\u6015\u5c31\u662f\u516c\u5b50\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\u6697\u9053\uff1a\u8fd9\u59d3\u8d75\u7684\u7a76\u7adf\u8fd8\u4e0d\u7b97\u5f97\u574f\u4eba\u2026\u2026\u52a8\u5ff5\u53ca\u6b64\uff0c\u4e0d\u7981\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u8c22\u8c22\u4f60\u7684\u597d\u610f\uff0c\u533a\u533a\u8bb0\u4e0b\u4fbf\u662f\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u53ea\u89c1\u4e00\u7f15\u706f\u5149\uff0c\u6253\u56ed\u5185\u5c04\u4e86\u51fa\u6765\uff0c\u90a3\u5148\u524d\u5165\u5185\u7684\u52b2\u88c5\u5927\u6c49\uff0c\u5df2\u6025\u6b65\u8d76\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4f46\u4ed6\u4e00\u773c\u770b\u5230\u65b9\u96ea\u5b9c\u4e4b\u540e\uff0c\u4e0d\u7981\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8d75\u4e09\uff0c\u8fd9\u662f\u4f55\u4eba\uff1f\u201d \u3000\u3000\u8d75\u4e09\u62f3\u9053\uff1a\u201c\u4e8c\u7237\uff0c\u8fd9\u5c31\u662f\u526f\u5802\u4e3b\u8981\u5c0f\u7684\u53bb\u6697\u7b97\u7684\u90a3\u4f4d\u516c\u5b50\uff0c\u201d \u3000\u3000\u90a3\u4f4d\u4e8c\u7237\u663e\u7136\u662f\u5403\u60ca\u4e0d\u5c0f\uff0c\u6d53\u7709\u8fde\u8fde\u95ea\u52a8\uff0c\u5411\u8d75\u4e09\u62f3\u559d\u9053\uff1a\u201c\u8d75\u4e09\uff0c\u4f60\u600e\u4e48\u628a\u654c\u4eba\u7ed9\u9886\u6765\u4e86\uff1f\u201d \u3000\u3000\u8d75\u4e09\u62f3\u5c1a\u672a\u7b54\u8bdd\uff0c\u65b9\u96ea\u5b9c\u5df2\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u4e8c\u7237\uff0c\u533a\u533a\u548c\u5b97\u5802\u4e3b\u3001\u5e84\u526f\u5802\u4e3b\u5747\u5c5e\u6545\u4ea4\uff0c\u4f60\u8fd9\u654c\u4eba\u4e8c\u5b57\uff0c\u4e0d\u5acc\u7528\u5f97\u592a\u8fc7\u5206\u4e86\u5417\uff1f\u201d \u3000\u3000\u4ed6\u8fd9\u4e48\u8bf4\u8bdd\uff0c\u5012\u628a\u8fd9\u4f4d\u4e8c\u7237\u542c\u7684\u5446\u4e86\u534a\u664c\uff0c\u66b4\u7741\u73af\u76ee\u7684\u4ed4\u7ec6\u6253\u91cf\u4e86\u65b9\u96ea\u5b9c\u534a\u5929\uff0c\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u516c\u5b50\uff0c\u771f\u7684\u8ddf\u526f\u5802\u4e3b\u662f\u6545\u4ea4\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e8c\u7237\u53ef\u662f\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u90a3\u4eba\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u5c0f\u4eba\u540d\u53eb\u5f90\u5927\u5316\uff0c\u516c\u5b50\u53ea\u7ba1\u53eb\u5c0f\u4eba\u540d\u53f7\u4fbf\u662f\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5feb\u5f15\u6211\u53bb\u89c1\u5e84\u526f\u5802\u4e3b\uff01\u201d \u3000\u3000\u5f90\u5927\u5316\u8eac\u8170\u5e94\u9053\uff1a\u201c\u5c0f\u4eba\u9075\u547d\uff01\u201d \u3000\u3000\u9ad8\u9ad8\u4e3e\u8d77\u98ce\u706f\uff0c\u9886\u7740\u65b9\u96ea\u5b9c\u5411\u56ed\u5185\u884c\u53bb\u3002 \u3000\u3000\u8d75\u4e09\u62f3\u625b\u8d77\u4e86\u51af\u4e03\uff0c\u8ddf\u7740\u65b9\u96ea\u5b9c\u8eab\u540e\uff0c\u6562\u60c5\u4ed6\u53ef\u662f\u60e6\u8bb0\u7740\u81ea\u5df1\u88ab\u5c01\u7684\u7a74\u9053\u672a\u89e3\uff0c\u4e0d\u6562\u4e0d\u4ea6\u8d9f\u4ea6\u8d8b\u5730\u968f\u5728\u65b9\u96ea\u5b9c\u5de6\u53f3\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5f90\u5927\u5316\u9886\u7740\u65b9\u96ea\u5b9c\u5728\u82b1\u5f84\u6797\u6728\u4e4b\u95f4\uff0c\u5de6\u8f6c\u53f3\u6298\uff0c\u8d70\u4e86\u7ea6\u83ab\u6709\u76cf\u8336\u4e4b\u4e45\uff0c\u6765\u5230\u4e00\u5904\u655e\u8f69\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e3e\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u90a3\u655d\u8f69\u4e4b\u4e2d\uff0c\u706f\u5149\u901a\u660e\uff0c\u800c\u4e14\u4eba\u5f71\u6643\u52a8\uff0c\u4f3c\u662f\u6709\u7740\u4e0d\u5c11\u4eba\u5728\u5185\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u5fae\u5fae\u4e00\u52a8\uff0c\u7acb\u597d\u653e\u6162\u811a\u6b65\uff0c\u5411\u8eab\u540e\u7684\u8d75\u4e09\u62f3\u62db\u547c\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u5148\u53bb\u7980\u62a5\uff01\u201d \u3000\u3000\u90a3\u8d75\u4e09\u62f3\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u5c0f\u4eba\uff01\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u90a3\u4f4d\u8d70\u5728\u524d\u5934\u7684\u5f90\u5927\u5316\uff0c\u5df2\u7136\u8fdb\u4e86\u90a3\u5ea7\u655e\u8f69\uff0c\u4f3c\u662f\u6b63\u5728\u5411\u90a3\u5750\u5728\u655e\u8f69\u4e2d\u7684\u4e00\u4f4d\u4e2d\u5e74\u6587\u58eb\u8bb2\u8bdd\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u76ee\u5149\u4e00\u8f6c\uff0c\u4f4e\u559d\u9053\uff1a\u201c\u8d75\u4e09\u62f3\uff0c\u4f60\u5982\u4e0d\u53bb\u8bf4\u660e\uff0c\u90a3\u5e84\u526f\u5802\u4e3b\u600e\u4f1a\u77e5\u9053\u4f60\u4eec\u529e\u5dee\u4e8b\u7684\u7ed3\u679c\u5462\uff1f\u800c\u4e14\uff0c\u533a\u533a\u82e5\u4e0d\u5f53\u7740\u4ed6\u9762\u524d\u62cd\u5f00\u4f60\u7684\u7a74\u9053\uff0c\u53ea\u6015\u90a3\u5e84\u526f\u5802\u4e3b\u4e5f\u4e0d\u4f1a\u76f8\u4fe1\u4f60\u88ab\u4eba\u6240\u5236\u5427\uff01\u201d \u3000\u3000\u8d75\u4e09\u62f3\u5927\u6982\u4e5f\u5f88\u660e\u767d\u65b9\u96ea\u5b9c\u6240\u8bf4\u4e0d\u5047\uff0c\u5f53\u4e0b\u5012\u662f\u4e0d\u518d\u8fa9\u8bf1\uff0c\u625b\u7740\u51af\u4e03\uff0c\u5927\u6b65\u5411\u8f69\u5185\u8d70\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u968f\u5728\u4ed6\u8eab\u540e\uff0c\u4f46\u884c\u5230\u655e\u8f69\u7684\u77f3\u9636\u4e4b\u4e0b\uff0c\u4fbf\u505c\u4e0b\u8eab\u5b50\uff0c\u8d1f\u624b\u800c\u7acb\u3002\u6b64\u523b\u90a3\u5750\u5728\u655d\u8f69\u4e2d\u7684\u84dd\u886b\u4e2d\u5e74\u6587\u58eb\uff0c\u76ee\u7779\u8d75\u4e09\u62f3\u625b\u7740\u51af\u4e03\u800c\u6765\uff0c\u8138\u4e0a\u795e\u8272\u679c\u662f\u5fae\u5fae\u4e00\u53d8\uff0c\u6389\u5934\u5411\u5f90\u5927\u5316\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff1f\u201d \u3000\u3000\u90a3\u5f90\u5927\u5316\u663e\u7136\u672a\u66fe\u6599\u5230\u90a3\u4f4d\u81ea\u79f0\u4e0e\u5e84\u526f\u5802\u4e3b\u4e43\u662f\u6545\u65e7\u4e4b\u4ea4\u7684\u516c\u5b50\uff0c\u5c45\u7136\u7ad9\u5728\u8f69\u5916\u4e0d\u66fe\u5165\u5185\uff0c\u987f\u65f6\u4fef\u9996\u5e94\u9053\uff1a\u201c\u5c5e\u4e0b\u2026\u2026\u5c5e\u4e0b\u4e5f\u4e0d\u61c2\u4e3a\u4e86\u4f55\u6545\uff01\u201d \u3000\u3000\u8d75\u4e09\u62f3\u653e\u4e0b\u4e86\u51af\u4e03\uff0c\u8dea\u4f0f\u5728\u5730\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u5c5e\u4e0b\u8d75\u4e09\u62f3\uff0c\u53e9\u89c1\u526f\u5802\u4e3b\u3002\u201d \u3000\u3000\u539f\u6765\u8fd9\u4f4d\u8eab\u7740\u84dd\u886b\uff0c\u795e\u60c5\u51b7\u9177\u7684\u4e2d\u5e74\u6587\u58eb\uff0c\u6b63\u662f\u5e84\u6377\uff0c\u5e84\u526f\u5802\u4e3b\u3002 \u3000\u3000\u8d75\u4e09\u62f3\u8bdd\u97f3\u752b\u843d\uff0c\u5e84\u6377\u5df2\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u8d75\u4e09\uff0c\u51af\u4e03\u600e\u4e48\u4e86\uff1f\u201d \u3000\u3000\u8d75\u4e09\u62f3\u50cf\u662f\u6328\u4e86\u4e00\u97ad\u5b50\u822c\uff0c\u6d51\u8eab\u53d1\u6296\u5730\u9053\uff1a\u201c\u4ed6\u2026\u2026\u51af\u8001\u4e03\uff0c\u88ab\u90a3\u516c\u5b50\u70b9\u4e86\u6b7b\u7a74\u2026\u2026\u201d \u3000\u3000\u5e84\u6377\u7709\u5934\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u4f60\u5462\uff0c\u4e3a\u4ec0\u4e48\u597d\u597d\u7684\uff1f\u201d \u3000\u3000\u8d75\u4e09\u62f3\u9053\uff1a\u201c\u5c0f\u4eba\u88ab\u5236\u4f4f\u4e86\u4e09\u5904\u5927\u7a74\uff0c\u90a3\u4f4d\u516c\u5b50\u8bf4\uff0c\u4e00\u4e2a\u65f6\u8fb0\u4e0d\u89e3\uff0c\u5c5e\u4e0b\u5c31\u5168\u8eab\u7a74\u8109\u50f5\u786c\u800c\u6b7b\u3002\u201d \u3000\u3000\u5e84\u6377\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6709\u8fd9\u7b49\u4e8b\uff1f\u201d\u7a81\u7136\u7ad9\u8d77\u8eab\u5b50\uff0c\u5411\u8d75\u4e09\u62f3\u8d70\u4e86\u8fc7\u6765\uff0c\u63a2\u624b\u6293\u4f4f\u8d75\u4e09\u62f3\u7684\u53f3\u8109\uff0c\u7791\u76ee\u4e0d\u8bed\uff0c\u8138\u4e0a\u4e00\u6d3e\u8083\u7136\u795e\u8272\u3002 \u3000\u3000\u8d75\u4e09\u62f3\u5374\u611f\u5230\u4e00\u80a1\u5f3a\u5927\u7684\u52b2\u9053\uff0c\u6b63\u4ece\u5e84\u526f\u5802\u4e3b\u7684\u638c\u5fc3\uff0c\u903c\u5411\u81ea\u5df1\u4f53\u5185\uff0c\u4f46\u56e0\u81ea\u5df1\u6709\u4e09\u5904\u7a74\u9053\u88ab\u5236\uff0c\u662f\u4ee5\u90a3\u80a1\u771f\u529b\u53ea\u80fd\u5728\u53f3\u81c2\u4e4b\u4e2d\u731b\u649e\uff0c\u75db\u5f97\u4ed6\u773c\u6cea\u90fd\u6d41\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5e84\u6377\u5927\u6982\u89c9\u51fa\u8d75\u4e09\u62f3\u6ca1\u8bf4\u5047\u8bdd\uff0c\u5ffd\u7136\u677e\u624b\u559d\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u679c\u7136\u88ab\u4eba\u5c01\u4e86\u4e09\u5904\u7a74\u9053\u2026\u2026\u201d\u8bed\u58f0\u4e00\u987f\uff0c\u6389\u5934\u5411\u8f69\u5916\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u9601\u4e0b\u4e43\u662f\u4e00\u4f4d\u9ad8\u4eba\uff0c\u5e84\u67d0\u771f\u662f\u5931\u656c\u4e86\u3002\u201d \u3000\u3000\u6562\u60c5\uff0c\u4ed6\u5e76\u975e\u4e0d\u66fe\u89c1\u5230\u65b9\u96ea\u5b9c\uff0c\u800c\u662f\u6709\u5fc3\u5728\u95ee\u660e\u4e00\u5207\u4ee5\u540e\uff0c\u65b9\u51fa\u5236\u62db\u547c\u5bf9\u65b9\uff1f \u3000\u3000\u65b9\u96ea\u5b9c\u5e94\u58f0\u4e00\u7b11\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u533a\u533a\u82e5\u662f\u4e0d\u7565\u4e3a\u732e\u4e11\uff0c\u6050\u6015\u5f97\u5b9a\u89c1\u5230\u526f\u5802\u4e3b\u7684\u91d1\u9762\u5427\uff01\u201d\u8bdd\u97f3\u672a\u5df2\u4eba\u5df2\u884c\u8fdb\u655e\u8f69\u4e4b\u5185\u3002 \u3000\u3000\u5e84\u6377\u9002\u624d\u5df2\u7136\u8bd5\u8d75\u4e09\u62f3\u88ab\u5c01\u7684\u4e09\u5904\u7a74\u9053\uff0c\u624b\u6cd5\u975e\u5e38\u602a\u5f02\uff0c\u81ea\u5df1\u65e0\u80fd\u89e3\u7a74\uff0c\u5fc3\u4e2d\u5bf9\u65b9\u96ea\u5b9c\u4e0d\u4f46\u6709\u4e94\u5206\u754f\u4ff1\u4e4b\u611f\uff0c\u800c\u4e14\u6709\u7740\u4e94\u5206\u656c\u610f\uff0c\u65b9\u96ea\u5b9c\u8e0f\u8fdb\u655e\u8f69\uff0c\u4ed6\u7acb\u5373\u62b1\u62f3\u76f8\u8fce\uff0c\u5e76\u4e14\u8ba9\u4e0a\u5ea7\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5750\u5b9a\u4ee5\u540e\uff0c\u76ee\u5149\u6253\u91cf\u4e86\u56db\u5468\u4e00\u773c\uff0c\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u5e84\u526f\u5802\u4e3b\uff0c\u5728\u4e0b\u4e0e\u526f\u5802\u4e3b\u4f3c\u662f\u7d20\u6627\u4e3b\u5e73\uff0c\u672a\u66fe\u89c1\u8fc7\u4e00\u9762\u5427\uff01\u201d \u3000\u3000\u5e84\u6377\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5206\u5b50\u4e0e\u5e84\u67d0\u679c\u771f\u7d20\u672a\u8c0b\u9762\uff0c\u4e0d\u77e5\u516c\u5b50\u4e0a\u59d3\u5927\u540d\u3002\u201d \u3000\u3000\u4ed6\u5bfb\u601d\u672a\u5df2\uff0c\u8033\u4e2d\u5df2\u542c\u5f97\u65b9\u96ea\u5b9c\u51b7\u51b7\u5730\u5e94\u9053\uff1a\u201c\u5728\u4e0b\u59d3\u65b9\uff0c\u8349\u5b57\u96ea\u5b9c\u3002\u201d \u3000\u3000\u5e84\u6377\u7b11\u9053\uff1a\u201c\u65b9\u516c\u5b50\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u65b9\u67d0\u4eca\u65e5\u62dc\u8bbf\uff0c\u6709\u4e24\u4ef6\u4e8b\uff0c\u60f3\u5411\u526f\u5802\u4e3b\u8bf7\u6559\uff01\u201d \u3000\u3000\u5e84\u6377\u7b11\u9053\uff1a\u201c\u8bf7\u6559\u4e24\u5b57\uff0c\u5e84\u67d0\u4e0d\u6562\u627f\u53d7\uff0c\u516c\u5b50\u6709\u4ec0\u4e48\u8bdd\uff0c\u5c3d\u7ba1\u5429\u5490\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5251\u7709\u8f69\u52a8\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u526f\u5802\u4e3b\u65e2\u4e0e\u65b9\u67d0\u4e0d\u8bc6\uff0c\u4e3a\u4f55\u6d3e\u4eba\u8ffd\u8e2a\u65b9\u67d0\uff0c\u610f\u56fe\u6697\u7b97\uff1f\u201d \u3000\u3000\u5e84\u6377\u95ee\u8a00\u7adf\u7136\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u516c\u5b50\u8a00\u91cd\u4e86\uff0c\u5e84\u67d0\u51b3\u65e0\u610f\u56fe\u6697\u7b97\u516c\u5b50\u4e4b\u610f\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u51b7\u7b11\u9053\uff1a\u201c\u526f\u4e16\u4e3b\uff0c\u5c0a\u9a7e\u6d3e\u51fa\u7684\u5c5e\u4e0b\uff0c\u5df2\u88ab\u65b9\u67d0\u64d2\u6765\uff0c\u5c0a\u9a7e\u7adf\u8fd8\u4e0d\u80af\u627f\u8ba4\u5417\uff1f\u201d \u3000\u3000\u5e84\u6377\u9762\u4e0d\u6539\u8272\u7684\u7b11\u9053\uff1a\u201c\u516c\u5b50\uff0c\u5e84\u67d0\u5982\u662f\u8bf4\u51fa\u5176\u4e2d\u9053\u7406\uff0c\u76f8\u4fe1\u65b9\u516c\u5b50\u5c31\u4e0d\u4f1a\u8bef\u4f1a\u4e86\u3002\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u7a81\u7136\u5411\u8d75\u4e09\u62f3\u9053\uff1a\u201c\u8d75\u4e09\uff0c\u672c\u5ea7\u4ea4\u4ee3\u4f60\u4eec\u8ffd\u8e2a\u65b9\u516c\u5b50\u65f6\uff0c\u53ef\u66fe\u8981\u4f60\u4eec\u6697\u7b97\u65b9\u516c\u5b50\u6765\u7740\uff1f\u201d \u3000\u3000\u90a3\u8d75\u4e09\u62f3\u663e\u7136\u662f\u5341\u5206\u754f\u602f\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u5e84\u6377\u5ffd\u7136\u6012\u54fc\u4e00\u58f0\uff0c\u559d\u9053\uff1a\u201c\u65e2\u7136\u672c\u5ea7\u4e0d\u66fe\u547d\u5c14\u7b49\u6697\u7b97\u65b9\u516c\u5b50\uff0c\u4f60\u4eec\u4e3a\u4f55\u80c6\u5927\u5984\u4e3a\uff0c\u64c5\u4f5c\u4e3b\u5f20\uff1f\u201d \u3000\u3000\u8d75\u4e09\u62f3\u542c\u5f97\u8dea\u5730\u8fde\u8fde\u53e9\u5934\u9053\uff1a\u201c\u8fd9\u2026\u2026\u8fd9\u662f\u51af\u8001\u4e03\u51fa\u7684\u4e3b\u610f\u3002\u201d \u3000\u3000\u5e86\u6377\u8138\u8272\u5fae\u9701\uff0c\u7b11\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u65b9\u516c\u5b50\uff0c\u53ef\u5426\u8bf7\u4f60\u89e3\u5f00\u8fd9\u51af\u8001\u4e03\u7684\u7a74\u9053\uff0c\u5bb9\u4ed6\u7b54\u8bdd\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u51b7\u773c\u65c1\u89c2\uff0c\u6697\u9053\uff1a\u201c\u7eb5\u7136\u4f60\u5728\u505a\u620f\uff0c\u6211\u4e5f\u8981\u77a7\u4e2a\u6e05\u695a\u3002\u201d\u5f53\u4e0b\u8c08\u6de1\u4e00\u5956\uff0c\u63a5\u9053\uff1a\u201c\u65b9\u67d0\u9075\u547d\uff01\u201d\u5927\u6b65\u4e0a\u524d\uff0c\u4fef\u4e0b\u8eab\u5b50\uff0c\u5728\u51af\u8001\u4e03\u547d\u95e8\u4e4b\u4e0a\uff0c\u8f7b\u51fb\u4e00\u638c\uff0c\u53ea\u89c1\u90a3\u51af\u8001\u4e03\u957f\u53f9\u4e00\u58f0\uff0c\u970d\u7136\u5750\u8d77\u8eab\u6765\u3002 \u3000\u3000\u4f46\u4ed6\u4e00\u773c\u53d1\u73b0\u5e84\u6377\u6b63\u6012\u76ee\u76f8\u5411\uff0c\u53ea\u5413\u5f97\u8fde\u8eab\u5b50\u8fd8\u6ca1\u6709\u5750\u7a33\uff0c\u5c31\u8fde\u5fd9\u7ffb\u8eab\u8dea\u5012\u5728\u5730\uff0c\u53e3\u4e2d\u53eb\u9053\uff1a\u201c\u5c5e\u4e0b\u51af\u4e03\uff0c\u53e9\u89c1\u526f\u5802\u4e3b\uff01\u201d \u3000\u3000\u5e84\u6377\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u51af\u4e03\uff0c\u4f60\u597d\u5927\u72d7\u80c6\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u65f6\u5df2\u7f13\u6b65\u56de\u5230\u81ea\u5df1\u5ea7\u4f4d\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u6211\u4e14\u77a7\u77a7\u4f60\u4eec\u80fd\u8981\u51fa\u4ec0\u4e48\u9b3c\u95e8\u9053\u6765\u3002\u201d \u3000\u3000\u90a3\u51af\u4e03\u88ab\u5e84\u6377\u9a82\u5f97\u5148\u662f\u4e00\u6014\uff0c\u4f46\u4ed6\u76ee\u4fa7\u4f59\u5149\uff0c\u5374\u77a7\u89c1\u4e86\u65b9\u96ea\u5b9c\u7684\u5f62\u8c8c\uff0c\u987f\u65f6\u76f4\u5413\u5f97\u5168\u8eab\u53d1\u6296\uff0c\u8fde\u8fde\u53e9\u5934\uff0c\u9053\uff1a\u201c\u5c5e\u4e0b\u4e0d\u6562\u2026\u2026\u5c5e\u4e0b\u2026\u2026\uff0c\u6b66\u529f\u4e0d\u6d4e\uff0c\u8fd8\u6c42\u526f\u5802\u4e3b\u5f00\u6069\u3002\u201d \u3000\u3000\u5e84\u6377\u51b7\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u679c\u7136\u662f\u6b66\u529f\u4e0d\u6d4e\uff0c\u5426\u5219\uff0c\u4f60\u4e5f\u4e0d\u4f1a\u7528\u90a3\u79cd\u4e0b\u4e94\u95e8\u7684\u624b\u6cd5\u5bf9\u5f85\u65b9\u516c\u5b50\u4e86\u3002\u201d \u3000\u3000\u51af\u4e03\u663e\u7136\u662f\u5e76\u672a\u542c\u51fa\u6765\u5e86\u6377\u7684\u8bdd\u9898\u771f\u6b63\u7528\u610f\u4f55\u5728\uff0c\u5f53\u4e0b\u7b54\u9053\uff1a\u201c\u5c5e\u4e0b\u5949\u547d\u524d\u53bb\u67e5\u770b\u8fd9\u65b9\u516c\u5b50\u5f62\u8ff9\uff0c\u65e0\u610f\u4e4b\u4e2d\uff0c\u53d1\u73b0\u4ed6\u679c\u7136\u6b63\u662f\u5802\u4e3b\u7684\u5927\u2026\u2026\u201d \u3000\u3000\u5e84\u6377\u9661\u7136\u66b4\u559d\u4e00\u58f0\u9053\uff1a\u201c\u4f4f\u53e3\u3002\u201d \u3000\u3000\u8fd9\u51af\u4e03\u5012\u4e5f\u542c\u8bdd\uff0c\u7acb\u5373\u7f29\u56de\u4e86\u820c\u5934\uff0c\u95ed\u53e3\u4e0d\u8bed\u3002 \u3000\u3000\u5e84\u6377\u8138\u8272\u94c1\u9752\uff0c\u5411\u7ad9\u5728\u8f69\u5916\u7684\u51e0\u540d\u5927\u6c49\u559d\u9053\uff1a\u201c\u62d6\u4e0b\u53bb\uff0c\u4f9d\u7167\u8fdd\u5f8b\u884c\u4e8b\u8bba\u5904\uff01\u201d \u3000\u3000\u987f\u65f6\uff0c\u8fdb\u6765\u4e86\u4e24\u540d\u5927\u6c49\uff0c\u4e00\u5de6\u4e00\u53f3\uff0c\u5c06\u51af\u4e03\u67b6\u4e86\u8d77\u6765\uff0c\u76f4\u5f80\u655e\u8f69\u4e4b\u5916\u8fde\u62c9\u5e26\u62ac\u7684\u62d6\u8d70\u3002 \u3000\u3000\u51af\u4e03\u62ab\u62d6\u8d70\u4ee5\u540e\uff0c\u5e84\u6377\u7adf\u662f\u5341\u5206\u5ba2\u6c14\u7684\u5411\u65b9\u96ea\u5b9c\u62b1\u62f3\u9053\uff1a\u201c\u65b9\u516c\u5b50\uff0c\u51af\u4e03\u4e0d\u542c\u53f7\u4ee4\uff0c\u81ea\u4f5c\u4e3b\u5f20\uff0c\u5144\u5f1f\u5df2\u6309\u5802\u89c4\u8bba\u5904\uff0c\u5f97\u7f6a\u4e86\u516c\u5b50\u4e4b\u5904\uff0c\u5c1a\u671b\u65b9\u516c\u5b50\u83ab\u518d\u8bb0\u5728\u5fc3\u4e0a\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u660e\u77e5\u5e84\u6377\u8fd9\u4e00\u756a\u505a\u4f5c\u7684\u7528\u5fc3\u4f55\u5728\uff0c\u4f46\u4ed6\u5374\u4e5f\u4e0d\u8bf4\u7a7f\uff0c\u7adf\u7136\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u5e84\u5144\u597d\u8bf4\uff0c\u8d35\u5c5e\u5176\u5b9e\u4e5f\u5e76\u672a\u6cbe\u5230\u65b9\u67d0\u4eba\u7684\u5149\uff0c\u5e94\u5144\u5982\u6b64\u91cd\u7f5a\u4e8e\u4ed6\uff0c\u5012\u53eb\u65b9\u67d0\u6709\u70b9\u8fc7\u610f\u4e0d\u53bb\u4e86\u3002\u201d \u3000\u3000\u5e84\u6377\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u65b9\u516c\u5b50\u5bbd\u5b8f\u5927\u91cf\uff0c\u5144\u5f1f\u5341\u5206\u656c\u4f69\uff01\u201d\u8bed\u97f3\u7565\u987f\uff0c\u6389\u5934\u5411\u8d75\u4e09\u62f3\u559d\u9053\uff1a\u201c\u5ff5\u5728\u4f60\u5f15\u5bfc\u65b9\u516c\u5b50\u6765\u6b64\u4e4b\u52b3\uff0c\u672c\u5ea7\u4e0d\u518d\u5904\u7f5a\uff0c\u8fd8\u4e0d\u7ed9\u6211\u4e0b\u53bb\u3002\u201d \u3000\u3000\u90a3\u8d75\u4e09\u62f3\u767b\u65f6\u5982\u9022\u5927\u8d66\uff0c\u8fde\u58f0\u5e94\u9053\uff1a\u201c\u5c5e\u4e0b\u9075\u547d\uff0c\u2026\u2026\u591a\u8c22\u526f\u5802\u4e3b\u6069\u5178\u2026\u2026\u201d\u51e0\u4e4e\u662f\u8fde\u6eda\u5e26\u722c\u7684\u9000\u4e86\u4e0b\u53bb\u3002\u5e84\u6377\u76ee\u5149\u4e00\u8f6c\uff0c\u6295\u6ce8\u5728\u65b9\u96ea\u5b9c\u8eab\u4e0a\uff0c\u7b11\u9053\uff1a\u201c\u65b9\u516c\u5b50\u4ed9\u4e61\u4f55\u5904\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u60f3\uff1a\u201c\u8fd9\u662f\u5728\u76d8\u95ee\u6211\u7684\u6765\u5386\u4e86\u3002\u201d\u53e3\u4e2d\u5374\u4e00\u7b11\uff0c\u7b54\u9053\uff1a\u201c\u65b9\u67d0\u4e16\u4ee3\u5c45\u4f4f\u6d1b\u9633\u3002\u201d \u3000\u3000\u5e84\u6377\u8138\u4e0a\u63a0\u8fc7\u4e00\u4e1d\u5f02\u91c7\uff0c\u62b1\u62f3\u9053\uff1a\u201c\u6d1b\u9633\u65b9\u5bb6\uff0c\u5728\u6b66\u6797\u4e4b\u4e2d\uff0c\u5927\u6709\u540d\u671b\uff0c\u90a3\u4e2d\u5dde\u4e09\u4fa0\u4e2d\u7684\u65b9\u4e8c\u4fa0\uff0c\u4e0d\u77e5\u662f\u516c\u5b50\u7684\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u5931\u7b11\u9053\uff1a\u201c\u679c\u7136\u5f88\u723d\u7094\uff0c\u4e09\u53e5\u8bdd\u4e0d\u5230\uff0c\u5c31\u8f6c\u4e0a\u4e86\u6b63\u9898\uff0c\u6211\u5982\u662f\u4e0d\u6562\u63ed\u660e\u8eab\u4efd\uff0c\u53ea\u6015\u53cd\u4f1a\u60f9\u8d77\u4ed6\u4eec\u7684\u8f7b\u89c6\u3002\u201d\u5f53\u4e0b\u8083\u5bb9\u9053\uff1a\u201c\u6b63\u662f\u5728\u4e0b\u7684\u5927\u4f2f\u7236\uff01\u201d \u3000\u3000\u5e84\u6377\u4f3c\u662f\u88ab\u65b9\u96ea\u5b9c\u8fd9\u53e5\u8bdd\u7b54\u5f97\u6d51\u8eab\u4e00\u9707\uff0c\u4f46\u4ed6\u5341\u5206\u673a\u8b66\uff0c\u8d81\u7740\u90a3\u5fc3\u8840\u4e00\u9707\uff0c\u795e\u60c5\u5fae\u53d8\u4e4b\u9645\uff0c\u970d\u5730\u7ad9\u8d77\u8eab\u5b50\uff0c\u53cc\u624b\u62b1\u62f3\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u539f\u6765\u662f\u65b9\u4e8c\u4fa0\u7684\u4f84\u516c\u5b50\uff0c\u5e84\u67d0\u5931\u656c\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7565\u4e00\u6b20\u8eab\uff0c\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u65b9\u67d0\u6df1\u591c\u5192\u6627\u9020\u8bbf\uff0c\u6253\u6270\u4e4b\u5904\uff0c\u5c1a\u7948\u5e84\u526f\u5802\u4e3b\u5305\u6db5\u3002\u201d \u3000\u3000\u5e84\u6377\u5927\u7b11\u9053\uff1a\u201c\u516c\u5b50\u592a\u5ba2\u6c14\u4e86\uff0c\u4e0d\u77e5\u65b9\u516c\u5b50\u6b64\u756a\u524d\u6765\u5609\u5b9a\uff0c\u6709\u4ec0\u4e48\u8d35\u5e72\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5251\u7709\u4e00\u76b1\uff0c\u5fc3\u4e2d\u5bfb\u601d\u9053\uff1a\u201c\u8fd9\u5bb6\u4f19\u4e00\u518d\u660e\u77e5\u6545\u95ee\uff0c\u6ee1\u53e3\u4ff1\u662f\u4e0d\u7740\u8fb9\u9645\u7684\u5ba2\u5957\uff0c\u5b9e\u5219\u53e5\u53e5\u90fd\u662f\u76d8\u6839\u7a76\u5e95\u7684\u9677\u9631\uff0c\u679c\u7136\u662f\u4e2a\u5341\u5206\u96be\u7f20\u7684\u4eba\u7269\uff0c\u4e0d\u77e5\u8fd9\u91d1\u67aa\u5b97\u58eb\u4e49\u7236\u5b50\uff0c\u600e\u4f1a\u627e\u5230\u8fd9\u7b49\u6781\u5bcc\u5fc3\u673a\u7684\u4e0b\u624b\uff0c\u96be\u9053\u8fd9\u5609\u5b9a\u5730\u9762\uff0c\u53c8\u6709\u4e86\u4ec0\u4e48\u65b0\u7684\u5e2e\u4f1a\u52bf\u529b\u4e0d\u6210\u3002\u201d \u3000\u3000\u4ed6\u4e00\u5ff5\u53ca\u6b64\uff0c\u4e0d\u7981\u6709\u4e9b\u604d\u7136\u89c9\u609f\u5230\uff0c\u8fd9\u6240\u8c13\u5609\u5b9a\u5206\u5802\uff0c\u660e\u660e\u662f\u4e00\u4e2a\u5e2e\u4f1a\u7ec4\u7ec7\u7684\u5206\u673a\u6784\uff0c\u81ea\u5df1\u5012\u662f\u8981\u8bbe\u6cd5\u5148\u4ece\u5bf9\u65b9\u53e3\u4e2d\u63a2\u51fa\u4e00\u70b9\u7ebf\u7d22\u624d\u662f\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u867d\u7136\u60f3\u7684\u8bf8\u822c\u60c5\u4e8b\u4e0d\u5c11\uff0c\u4f46\u5ff5\u5934\u8f6c\u52a8\u7684\u5374\u4e5f\u98de\u5feb\uff0c\u5728\u5e84\u6377\u773c\u4e2d\uff0c\u4ed6\u53ea\u662f\u7565\u7565\u7684\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u4fbf\u9053\uff1a\u201c\u5144\u5f1f\u6b64\u6b21\u4e43\u662f\u987a\u9053\u7ecf\u8fc7\u5609\u5b9a\u3002\u201d \u3000\u3000\u7167\u5e38\u60c5\u800c\u8bba\uff0c\u65b9\u96ea\u5b9c\u8fd9\u7b49\u8bf4\u6cd5\uff0c\u5e84\u6377\u5e94\u662f\u51b3\u7136\u4e0d\u4f1a\u76f8\u4fe1\uff0c\u4f46\u4ed6\u5374\u4f3c\u662f\u76f8\u4fe1\u4e86\u65b9\u96ea\u5b9c\u4e4b\u8a00\uff0c\u7b11\u9053\uff1a\u201c\u516c\u5b50\u53ef\u662f\u6253\u7b97\u5728\u5609\u5b9a\u76d8\u4f46\u4e9b\u65f6\u65e5\u5417\uff1f\u5e84\u67d0\u4e0d\u624d\uff0c\u751a\u671b\u80fd\u591f\u7a0d\u5c3d\u5730\u4e3b\u4e4b\u8c0a\uff0c\u8ffd\u968f\u516c\u5b50\u7545\u6e38\u6b64\u95f4\u51e0\u5904\u540d\u80dc\u53e4\u8ff9\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e5f\u660e\u77e5\u8fd9\u5e84\u6377\u8a00\u4e0d\u7531\u8877\uff0c\u4f46\u4ed6\u5374\u4e0d\u5927\u5584\u4e8e\u5fc3\u673a\uff0c\u5e84\u6377\u8bed\u97f3\u672a\u7adf\uff0c\u4ed6\u5df2\u7136\u5fcd\u4e0d\u4f4f\u6447\u624b\u9053\uff1a\u201c\u4e0d\u6562\u591a\u52b3\u5e84\u5144\u8d39\u795e\uff0c\u5144\u5f1f\u660e\u65e5\u5c31\u5c06\u79bb\u6b64\u4ed6\u53bb\u3002\u201d \u3000\u3000\u5e84\u6377\u987f\u65f6\u9732\u51fa\u4e86\u4e00\u8138\u5931\u671b\u4e4b\u8272\uff0c\u53f9\u606f\u9053\uff1a\u201c\u516c\u5b50\u4e43\u662f\u540d\u95e8\u5b50\u5f1f\uff0c\u5e84\u67d0\u81ea\u77e5\u9ad8\u6500\u4e0d\u4e0a\uff0c\u53ea\u662f\u2026\u2026\u53ea\u662f\u2026\u2026\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u53ea\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5e84\u6377\u795e\u6001\u771f\u8bda\u7684\u62b1\u62f3\u9053\uff1a\u201c\u53ea\u662f\u5e84\u67d0\u8fd9\u756a\u8bda\u610f\uff0c\u5c1a\u671b\u65b9\u516c\u5b50\u660e\u767d\u5c31\u597d\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7ec8\u7a76\u662f\u6d89\u4e16\u4e0d\u6df1\uff0c\u4ed6\u53ea\u89c1\u5230\u5e84\u6377\u8fd9\u6ee1\u8138\u5fe0\u8bda\u7684\u795e\u60c5\uff0c\u5fc3\u4e2d\u7adf\u662f\u5341\u5206\u611f\u52a8\uff0c\u5bf9\u4ed6\u7591\u60d1\u4e4b\u5fc3\u5927\u51cf\uff0c\u7b11\u9053\uff1a\u201c\u5e84\u5144\u539a\u5f85\u4e4b\u60c5\uff0c\u5144\u5f1f\u6c38\u8fdc\u4e0d\u4f1a\u8bb0\u5fc6\uff01\u201d \u3000\u3000\u8bed\u97f3\u5fae\u5fae\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5144\u5f1f\u539f\u672c\u60f3\u5728\u5609\u5b9a\u9017\u7559\u4e00\u4e24\u5929\u2026\u2026\u201d \u3000\u3000\u5e84\u6377\u8131\u53e3\u6b22\u547c\u9053\uff1a\u201c\u516c\u5b50\u7b54\u5e94\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u77a7\u4ed6\u90a3\u4e00\u6d3e\u6b22\u6109\u4e4b\u8272\uff0c\u5b8c\u5168\u4e0d\u4f3c\u505a\u4f5c\uff0c\u51e0\u4e4e\u4e0d\u5fcd\u8bf4\u51fa\u90a3\u4e0d\u5b57\uff0c\u4f46\u4ed6\u4ecd\u7136\u54ac\u4e86\u54ac\u7259\uff0c\u6447\u5934\u9053\uff1a\u201c\u5e84\u5144\u8bda\u610f\uff0c\u5144\u5f1f\u5fc3\u9886\u4e86\uff0c\u65e5\u540e\u6709\u6687\uff0c\u5144\u5f1f\u81ea\u4f1a\u91cd\u6765\u62dc\u671b\u5e84\u53f2\uff0c\u5171\u4f5c\u4e94\u65e5\u4e4b\u6e38\u3002 \u3000\u3000\u5e84\u6377\u7b11\u5bb9\u5ffd\u544a\u6d88\u5931\uff0c\u4e0d\u4f4f\u7684\u6447\u5934\u53f9\u606f\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4f60\u2026\u2026\u5509\uff01\u4f60\u8bf4\u539f\u672c\u8981\u5728\u6b64\u9017\u7559\u51e0\u65e5\u5417\uff0c\u4e3a\u4f55\u53c8\u6539\u4e86\u4e3b\u610f\u5462\uff1f\u83ab\u975e\u662f\u4e3a\u4e86\u5e84\u67d0\u90a3\u6df7\u5e10\u7684\u4e0b\u5c5e\u60ca\u52a8\u4e86\u516c\u5b50\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u662f\u3002\u201d\u5e84\u6377\u76b1\u76fe\u9053\uff1a\u201c\u516c\u5b50\u2026\u2026\u201d \u3000\u3000\u5e84\u6377\u6014\u4e86\u4e00\u6014\u9053\uff1a\u201c\u516c\u5b50\u2026\u2026\u65b9\u5144\uff0c\u5144\u5f1f\u606d\u656c\u4e0d\u5982\u4ece\u547d\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e84\u5144\uff0c\u5144\u5f1f\u6709\u4e00\u53e5\u8bdd\uff0c\u4e0d\u77e5\u8be5\u4e0d\u8be5\u95ee\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u65b9\u5144\u6709\u8bdd\uff0c\u5c3d\u7ba1\u5429\u5490\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e84\u5144\u8eab\u4e3a\u5609\u5b9a\u5206\u5802\u5802\u4e3b\uff0c\u4e0d\u77e5\u5e84\u5144\u8fd9\u5206\u5802\u662f\u5c5e\u4e8e\u4ec0\u4e48\u95e8\u6d3e\u6240\u8f96\uff1f\u201d \u3000\u3000\u5e84\u6377\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e09\u4e49\u95e8\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4e09\u4e49\u95e8\uff1f\u5144\u5f1f\u600e\u5730\u672a\u66fe\u542c\u8bf4\u8fc7\uff1f\u201d \u3000\u3000\u5e84\u6377\u7b11\u9053\uff1a\u201c\u6211\u4e09\u4e49\u95e8\u6210\u7acb\u672a\u53ca\u4e00\u5e74\uff0c\u5c1a\u672a\u6b63\u5f0f\u5411\u6c5f\u6e56\u7acb\u4e07\uff0c\u65b9\u5144\u4e0d\u77e5\uff0c\u90a3\u4e5f\u4e0d\u8db3\u4e3a\u602a\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u679c\u7136\u88ab\u6211\u6599\u4e2d\uff0c\u8fd9\u5609\u5b9a\u4e00\u5e26\uff0c\u6b63\u662f\u6709\u4e86\u65b0\u7684\u5e2e\u4f1a\u7ec4\u6210\u4e86\u3002\u201d\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u5e84\u5144\uff0c\u8d35\u95e8\u65e2\u79f0\u4e09\u4e49\uff0c\u5fc5\u662f\u7531\u4e09\u4f4d\u4ec1\u4e49\u5927\u4fa0\u6240\u624b\u521b\u4e86\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u65b9\u5144\u8bf4\u7684\u4e0d\u9519\uff0c\u4e09\u4e49\u95e8\u6b63\u662f\u7531\u5ddd\u897f\u4e09\u4e49\u624b\u521b\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u60f3\uff1a\u201c\u8fd9\u513f\u51e0\u65f6\u53c8\u51fa\u4e86\u5ddd\u897f\u4e09\u4e49\u95e8\uff1f\u5012\u662f\u8981\u95ee\u4e2a\u660e\u767d\u201d\u5f53\u4e0b\u62b1\u62f3\u9053\uff1a\u201c\u5e84\u5144\uff0c\u7f14\u521b\u8d35\u95e8\u7684\u4e09\u4f4d\u4e49\u4fa0\uff0c\u4e0d\u77e5\u600e\u4e48\u79f0\u547c\uff1f\u8fd9\u638c\u95e8\u4eba\u662f\u5426\u4e5f\u7531\u4e09\u4eba\u5171\u540c\u63d0\u4efb\uff1f\u201d \u3000\u3000\u5e86\u6377\u76ee\u5149\u4e00\u8f6c\uff0c\u6c89\u541f\u4e00\u4f1a\u513f\uff0c\u65b9\u9053\uff1a\u201c\u6b64\u4e8b\u5982\u662f\u522b\u4eba\u76f8\u8be2\uff0c\u5144\u5f1f\u4e00\u5b9a\u4e0d\u4f1a\u8bf4\u51fa\uff0c\u4f46\u4f60\u65b9\u5144\u4e43\u662f\u4e2d\u5dde\u4e09\u4fa0\u5f1f\u5b50\uff0c\u5144\u5f1f\u81ea\u7136\u76f8\u4fe1\u5f97\u5f88\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u56fa\u7136\u5341\u5206\u5e0c\u671b\u5e84\u6377\u80fd\u591f\u8bf4\u51fa\u4e09\u4e49\u95e8\u7684\u673a\u5bc6\uff0c\u4f46\u4ed6\u5374\u79c9\u6027\u5fe0\u539a\uff0c\u4e0d\u6562\u5f3a\u4eba\u6240\u96be\uff0c\u76ee\u7779\u5e84\u6377\u8fd9\u7b49\u795e\u60c5\uff0c\u7acb\u5373\u63a5\u53e3\u9053\uff1a\u201c\u5e84\u5144\u5982\u662f\u4e0d\u4fbf\uff0c\u8fd9\u4e0d\u5fc5\u8bf4\u4e86\uff01\u201d\u5e84\u6377\u6168\u7136\u4e00\u7b11\u9053\uff1a\u201c\u5144\u5f1f\u4e0e\u65b9\u5144\u4ea4\u5f80\uff0c\u51fa\u81ea\u4e00\u7247\u81f3\u8bda\uff0c\u5982\u662f\u8fd9\u70b9\u5c0f\u4e8b\u5144\u5f1f\u90fd\u8981\u9690\u7792\uff0c\u90a3\u6211\u5e84\u6377\u8fd8\u80fd\u7b97\u662f\u4f60\u65b9\u5144\u7684\u670b\u53cb\u5417\uff1f\u201d \u3000\u3000\u4ed6\u4e00\u8138\u7686\u662f\u8bda\u6073\u4e4b\u6001\uff0c\u8bf4\u7684\u53e5\u53e5\u5982\u51fa\u80ba\u8151\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5927\u4e3a\u611f\u52a8\u5730\u9053\uff1a\u201c\u5e84\u5144\u5982\u6b64\u770b\u91cd\u5144\u5f1f\uff0c\u771f\u4f7f\u5144\u5f1f\u53d7\u5ba0\u82e5\u60ca\uff01\u201d\u6562\u60c5\uff0c\u65b9\u96ea\u5b9c\u5b8c\u5b8c\u5168\u5168\u5730\u628a\u5e84\u6377\u5f53\u6210\u670b\u53cb\u4e86\u3002 \u3000\u3000\u5e86\u6377\u5ffd\u7136\u957f\u957f\u7684\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u65b9\u5144\u8499\u4f60\u4e0d\u5f03\uff0c\u6298\u8282\u4e0b\u4ea4\uff0c\u5144\u5f1f\u81ea\u5df1\u662f\u4e0d\u80fd\u4e0d\u8bda\u5fc3\u8bda\u610f\u7684\u8ddf\u65b9\u5144\u8bf4\u8bdd\u4e86\u2026\u2026\u201d \u3000\u3000\u8bed\u97f3\u5fae\u5fae\u4e00\u987f\uff0c\u4f4e\u58f0\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5ddd\u897f\u4e09\u4e49\uff0c\u5c31\u662f\u6614\u5e74\u7684\uff0c\u5251\u95e8\u4e94\u9b3c\u4e2d\u7684\u6210\u7384\u901a\u3001\u8463\u65b9\u53ca\u5b97\u5821\u4e3b\u91d1\u67aa\u5b97\u58eb\u4e49\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e5f\u66fe\u60f3\u5230\u5f88\u591a\u4eba\uff0c\u751a\u81f3\u9b54\u5200\u7530\u8fdc\uff0c\u90fd\u5728\u8fd9\u4e09\u4e49\u4e4b\u4e2d\uff0c\u4f46\u662f\u5374\u51b3\u672a\u60f3\u5230\u8fd9\u4e09\u4e49\u95e8\u7684\u4e09\u4e49\uff0c\u7adf\u4f1a\u662f\u6614\u5e74\u4e0d\u66fe\u6b7b\u5728\u4e2d\u5dde\u4e09\u4fa0\u5251\u4e0b\u7684\u5251\u95e8\u4e94\u9b3c\u4e2d\u7684\u8001\u5927\u548c\u8001\u4e94\uff0c\u81ea\u7136\uff0c\u8fd9\u5b97\u58eb\u4e49\u5c45\u7136\u548c\u4ed6\u4eec\u5408\u79f0\u4e09\u4e49\uff0c\u66f4\u662f\u51fa\u4e4e\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u4ed6\u6123\u4e86\u534a\u54cd\uff0c\u65b9\u9053\uff1a\u201c\u4f1a\u662f\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u65b9\u5144\u53ef\u8ba4\u5f97\u672c\u95e8\u7684\u4e09\u4f4d\u638c\u95e8\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u53c8\u4e00\u884c\u9053\uff1a\u201c\u4ed6\u4eec\u90fd\u7b97\u662f\u638c\u95e8\u4eba\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u8fd9\u4e43\u672c\u95e8\u4e0e\u5176\u4ed6\u95e8\u6d3e\u4e0d\u540c\u4e4b\u70b9\uff0c\u65b9\u5144\u96be\u514d\u89c9\u5f97\u5947\u602a\uff0c\u5176\u5b9e\uff0c\u8fd9\u4e5f\u662f\u4e09\u4e49\u95e8\u7684\u4f5c\u98ce\u5f00\u660e\u4e4b\u5904\u2026\u2026\u201d\u65b9\u96ea\u5b9c\u542c\u4ed6\u6b64\u8bdd\uff0c\u5fc3\u4e2d\u9661\u7136\u60f3\u8d77\u4ed6\u5bf9\u5f85\u51af\u4e03\u7684\u7ecf\u8fc7\uff0c\u5fc3\u60f3\uff0c\u8fd9\u4e5f\u7b97\u662f\u5f00\u660e\u4e48\uff1f\u4e0d\u89c9\u5f97\u8131\u53e3\u9053\uff1a\u201c\u5e84\u5144\uff0c\u8d35\u95e8\u60f3\u5fc5\u8fd9\u5f00\u660e\u7684\u4f5c\u98ce\uff0c\u5f88\u662f\u5f15\u4ee5\u4e3a\u81ea\u5bb6\u7684\u4e86\u3002\u201d \u3000\u3000\u5e84\u6377\u7b11\u9053\uff1a\u201c\u90a3\u662f\u81ea\u7136\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u6bd4\u5982\u5e84\u5144\u5bf9\u5f85\u51af\u4e03\u7684\u4e3e\u63aa\uff0c\u4e5f\u662f\u8d35\u95e8\u7684\u5f00\u660e\u3002\u201d \u3000\u3000\u5e84\u6377\u8138\u8272\u5927\u53d8\uff0c\u5fd9\u9053\uff1a\u201c\u65b9\u5144\uff0c\u6b64\u4e8b\u5144\u5f1f\u786e\u5b9e\u505a\u5f97\u6709\u8fdd\u672c\u95e8\u89c4\u6212\uff0c\u65b9\u5144\u5343\u4e07\u83ab\u8981\u5f20\u626c\u2026\u2026\u201d\u4ed6\u8fd9\u79cd\u795e\u79d8\u60ca\u614c\u7684\u795e\u8272\uff0c\u5012\u4f7f\u65b9\u96ea\u5b9c\u89c9\u5f97\u4e0d\u5927\u5bfb\u5e38\uff0c\u6b63\u60f3\u95ee\u4ed6\u662f\u4f55\u539f\u6545\uff0c\u5e84\u6377\u5df2\u7136\u9644\u8033\u63a5\u9053\uff1a\u201c\u65b9\u5144\uff0c\u90a3\u51af\u4e03\u672c\u662f\u8fd9\u5609\u5b9a\u5730\u9762\u7684\u4e00\u4e2a\u6d41\u6c13\uff0c\u53ea\u56e0\u4ed6\u8ddf\u5b97\u5802\u4e3b\u6709\u7740\u8fdc\u4eb2\u7684\u5173\u7cfb\uff0c\u5144\u5f1f\u867d\u6709\u715e\u71ac\u4ed6\u9a84\u7eb5\u4e4b\u5fc3\uff0c\u5374\u4e00\u76f4\u82e6\u65e0\u673a\u4f1a\u4e0b\u624b\uff0c\u4eca\u5929\u96be\u5f97\u89c1\u4e0a\u5b97\u5802\u4e3b\u4e0d\u5728\u5609\u5b9a\uff0c\u4ed6\u53c8\u81ea\u4f5c\u4e3b\u5f20\uff0c\u4e0d\u542c\u53f7\u4ee4\u7ea6\u675f\uff0c\u6b63\u662f\u5929\u8d50\u673a\u4f1a\uff0c\u5144\u5f1f\u5f88\u60f3\u5c31\u6b64\u5c06\u4ed6\u9664\u53bb\uff0c\u6240\u4ee5\u624d\u4f1a\u6709\u7740\u521a\u624d\u90a3\u7b49\u63aa\u65bd\u2026\u2026\u201d \u3000\u3000\u5e84\u6377\u8fd9\u4e00\u756a\u8bdd\uff0c\u53ea\u8bf4\u7684\u5408\u60c5\u5408\u7406\uff0c\u65b9\u96ea\u5b9c\u5012\u4e5f\u672a\u66fe\u542c\u51fa\u4ec0\u4e48\u6f0f\u6d1e\uff0c\u7b11\u9053\uff1a\u201c\u5e84\u5144\u8fd9\u4e09\u4e49\u95e8\u7684\u603b\u575b\uff0c\u8bbe\u5728\u4f55\u5904\uff1f\u201d \u3000\u3000\u5e84\u51b2\u9053\uff1a\u201c\u5c31\u5728\u6210\u90fd\u5357\u57ce\u5916\u7684\u9f99\u6cc9\u9a7f\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8bb0\u4e0b\u4e86\u9f99\u6cc9\u5f13\u8fd9\u4e2a\u5730\u540d\uff0c\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u58ee\u5144\uff0c\u5144\u5f1f\u8fd8\u6709\u4e00\u4ef6\u4e8b\uff0c\u60f3\u5411\u5e84\u5144\u8bf7\u6559\u3002\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u65b9\u5144\u4e0d\u5fc5\u5ba2\u6c14\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5144\u5f1f\u8fd9\u6b21\u9053\u7ecf\u5609\u5b9a\uff0c\u672c\u662f\u8981\u63a2\u671b\u5927\u4f2f\u7684\u6614\u5e74\u4e00\u4f4d\u597d\u53cb\uff0c\u4e0d\u6599\u4e8b\u9694\u56db\u5e74\u4e0d\u5230\uff0c\u5609\u5b9a\u5374\u5df2\u4eba\u4e8b\u5168\u975e\uff0c\u5144\u5f1f\u7adf\u662f\u627e\u4e0d\u5230\u90a3\u4f4d\u8001\u4eba\u5bb6\u7684\u4e0b\u843d\uff0c\u4e0d\u77e5\u5e84\u5144\u662f\u5426\u53ef\u4ee5\u6307\u660e\u5144\u5f1f\u4e00\u6761\u660e\u8def\uff01\u201d \u3000\u3000\u5e84\u6377\u7b11\u9053\uff1a\u201c\u65b9\u5144\u597d\u8bf4\uff0c\u5144\u5f1f\u867d\u662f\u524d\u6765\u5609\u5b9a\u4e0d\u8fc7\u4e24\u5e74\uff0c\u4f46\u53ea\u662f\u5609\u5b9a\u5730\u9762\u4e0a\u7a0d\u6709\u5934\u8138\u4e4b\u4eba\uff0c\u5144\u5f1f\u591a\u5c11\u603b\u662f\u77e5\u9053\u4e00\u70b9\uff0c\u4f46\u4e0d\u77e5\u65b9\u5144\u5f1f\u8981\u627e\u7684\u662f\u8c01\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5c31\u662f\u4e1c\u95e8\u5916\u7a0b\u5b50\u671b\u7a0b\u5e84\u4e3b\u3002\u201d \u3000\u3000\u5e84\u6377\u5ffd\u7136\u4e00\u6014\u9053\uff1a\u201c\u7a0b\u5b50\u671b\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e84\u5144\u53ef\u77e5\u7a0b\u5e84\u4e3b\u7684\u4e0b\u843d\uff1f\u201d \u3000\u3000\u5e84\u6377\u76ee\u5149\u4e00\u6697\uff0c\u53f9\u606f\u4e00\u58f0\u9053\uff1a\u201c\u65b9\u5144\uff0c\u5982\u662f\u4f60\u8981\u6211\u7684\u662f\u90a3\u7a0b\u5e84\u4e3b\uff0c\u53ea\u6015\u5144\u5f1f\u65e0\u6cd5\u5e2e\u5fd9\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5e84\u6377\u957f\u53f9\u9053\uff1a\u201c\u7a0b\u5e84\u4e3b\u4e00\u5bb6\u767e\u53e3\uff0c\u5df2\u5728\u4e09\u5e74\u524d\u88ab\u4eba\u4e8e\u4e00\u591c\u4e4b\u95f4\uff0c\u65a9\u5c3d\u6740\u7edd\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u867d\u662f\u65e9\u5df2\u77e5\u9053\u4e86\u8fd9\u79cd\u7ed3\u679c\uff0c\u4f46\u6b64\u523b\u4f9d\u7136\u5728\u5f00\u8a00\u4e4b\u9645\uff0c\u621a\u7136\u826f\u4e45\uff0c\u6c89\u58f0\u95ee\u9053\uff1a\u201c\u5e84\u5144\uff0c\u7a0b\u5e84\u4e3b\u5728\u5609\u5b9a\u5e9c\u4e00\u5e26\uff0c\u7b97\u5f97\u4e0a\u662f\u4f4d\u6b63\u76f4\u7684\u6b66\u6797\u540c\u9053\uff0c\u4ed6\u6ee1\u95e8\u9047\u5bb3\u4e4b\u4e8b\uff0c\u4e0d\u77e5\u5e84\u5144\u53ef\u77e5\u90a3\u51f6\u624b\u662f\u8c01\uff1f\u201d \u3000\u3000\u5e84\u6377\u6447\u5934\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u5144\u5f1f\u867d\u4e5f\u5728\u62b5\u8fbe\u5609\u5b9a\u4ee5\u540e\uff0c\u591a\u65b9\u6253\u63a2\uff0c\u4f46\u5374\u5e76\u65e0\u80af\u5b9a\u7684\u7ed3\u679c\uff01\u201d \u3000\u3000\u6562\u8bf7\u8fd9\u5e84\u6377\u8bdd\u4e2d\u6709\u8bdd\uff0c\u65b9\u96ea\u5b9c\u63a5\u9053\uff1a\u201c\u83ab\u975e\u5e84\u5144\u5df2\u7136\u6709\u51fa\u4e86\u4ec0\u4e48\u7709\u76ee\uff1f\u201d \u3000\u3000\u5e84\u6377\u5ffd\u7136\u6c89\u541f\u9053\uff1a\u201c\u65b9\u5144\uff0c\u6b64\u4e8b\u2026\u2026\u201d\u8bed\u58f0\u4e00\u987f\uff0c\u671b\u7740\u65b9\u96ea\u5b9c\u597d\u534a\u5929\uff0c\u8fd9\u624d\u63a5\u9053\uff1a \u3000\u3000\u201c\u65b9\u5144\uff0c\u6b66\u6797\u4e2d\u6709\u4f4d\u53eb\u7530\u4e00\u5200\u7684\u4eba\uff0c\u4f60\u53ef\u66fe\u542c\u8bf4\u8fc7\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u9053\uff1a\u4f55\u6b62\u542c\u8fc7\uff0c\u53ea\u6015\u90a3\u7530\u4e00\u5200\u89c1\u4e86\u6211\uff0c\u8fd8\u5f97\u5c0a\u79f0\u6211\u4e00\u58f0\u65b9\u4e00\u5251\u5462\uff1f\u4f46\u4ed6\u81ea\u662f\u4e0d\u4f1a\u628a\u8fd9\u7b49\u8bdd\u544a\u77e5\u5e84\u6377\uff0c\u53cd\u5012\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u4e0d\u592a\u660e\u767d\uff0c\u53ea\u662f\u90a3\u7530\u4e00\u5200\u53ef\u662f\u88ab\u4eba\u5524\u4f5c\u9b54\u5200\u7530\u8fdc\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u6b63\u662f\u6b64\u4eba\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4ed6\u600e\u4e48\u6837\uff1f\u90a3\u7a0b\u5e84\u4e3b\u4e0d\u4f1a\u662f\u4ed6\u6240\u5bb3\u5427\uff1f\u201d \u3000\u3000\u5e84\u6377\u53f9\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u65b9\u5144\u4e0d\u5e78\u8a00\u4e2d\uff0c\u6b63\u662f\u8fd9\u4f4d\u9b54\u5200\u7530\u8fdc\u5bb3\u4e86\u7a0b\u5e84\u4e3b\u4e00\u5bb6\u5927\u5c0f\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8131\u53e3\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u53ef\u80fd\uff1f\u201d \u3000\u3000\u5e84\u6377\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u636e\u5144\u5f1f\u8fd9\u4e00\u5e74\u591a\u6765\u6240\u542c\u6240\u95fb\uff0c\u90a3\u7a0b\u5e84\u4e3b\u5341\u4e4b\u516b\u4e5d\uff0c\u662f\u6b7b\u5728\u7530\u4e00\u5200\u7684\u624b\u4e2d\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5251\u7709\u4e00\u626c\uff0c\u9053\uff1a\u201c\u5e84\u5144\u4f60\u53ef\u662f\u627e\u51fa\u4e86\u4ec0\u4e48\u771f\u5b9e\u8bc1\u636e\u5417\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u60f3\u90a3\u9b54\u5200\u7530\u8fdc\u7684\u4e3a\u4eba\uff0c\u7d20\u4ee5\u72e0\u6bd2\u8457\u79f0\uff0c\u4ed6\u5982\u662f\u5b58\u5fc3\u4e0d\u7559\u75d5\u8ff9\uff0c\u522b\u4eba\u81ea\u662f\u96be\u4ee5\u53d1\u89c9\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u65e2\u7136\u672a\u7559\u75d5\u8ff9\uff0c\u5e84\u5144\u53c8\u4f55\u4ee5\u4f1a\u60f3\u5230\u4e86\u7a0b\u5e9c\u767e\u53e3\uff0c\u4ff1\u6b7b\u5728\u7530\u8fdc\u4e4b\u624b\u5462\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u8fd9\u2026\u2026\u5144\u5f1f\u66fe\u542c\u5609\u5b9a\u7684\u670b\u53cb\u63d0\u8fc7\u90a3\u7a0b\u5e84\u4e3b\u5bb6\u4eba\u4e2d\u6709\u672a\u88ab\u711a\u6bc1\u7684\u6b7b\u9aa8\uff0c\u5c31\u4f24\u75a4\u770b\u6765\uff0c\u4ff1\u662f\u4e00\u5200\u81f4\u547d\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u51b7\u51b7\u5730\u5e94\u9053\uff1a\u201c\u5c31\u53ea\u6709\u8fd9\u4e00\u70b9\u5417\uff1f\u201d \u3000\u3000\u5e84\u6377\u7b11\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4eba\u4eba\u5982\u662f\u800c\u8a00\uff0c\u672a\u66fe\u627e\u5230\u9b54\u5200\u7530\u8fdc\u4e4b\u524d\uff0c\u5144\u5f1f\u4e5f\u4e0d\u6562\u5341\u5206\u76f8\u4fe1\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6c89\u541f\u5bfb\u601d\u9053\uff1a\u77a7\u4ed6\u8bf4\u8bdd\u7684\u795e\u6001\uff0c\u4f3c\u662f\u65e0\u6709\u4f5c\u4f2a\u4e4b\u60c5\uff0c\u540e\u6765\u90a3\u8d26\u623f\u8001\u4eba\u6240\u8bf4\uff0c\u7a0b\u5bb6\u5bb3\u5728\u6210\u7384\u901a\u7b49\u4eba\u624b\u4e2d\u4e4b\u4e8b\uff0c\u53ea\u6015\u6210\u7384\u901a\u5e76\u672a\u8981\u624b\u4e0b\u4e4b\u4eba\u53c2\u4e0e\uff0c\u81ea\u5df2\u60f3\u5728\u5e84\u6377\u53e3\u4e2d\u5957\u51fa\u5185\u60c5\uff0c\u6050\u6015\u5df2\u662f\u65e0\u671b\u4e86\u3002\u5f53\u4e0b\u62b1\u62f3\u4e00\u7b11\u9053\uff1a\u201c\u591a\u8c22\u5e84\u5144\u5f1f\u6307\u70b9\uff0c\u5144\u5f1f\u6253\u6270\u751a\u4e45\u5fc3\u4e2d\u5f88\u662f\u4e0d\u5b89\uff0c\u544a\u8f9e\u4e86\u3002\u201d \u3000\u3000\u5e84\u6377\u5ffd\u7136\u4f38\u624b\u62c9\u4f4f\u65b9\u96ea\u5b9c\u7684\u624b\u7b11\u9053\uff1a\u201c\u65b9\u5144\u5929\u8272\u5df2\u8fd1\u56db\u66f4\uff0c\u6b64\u523b\u56de\u81f3\u5ba2\u6808\uff0c\u5b9a\u7136\u60ca\u52a8\u522b\u4eba\uff0c\u4f55\u4e0d\u5c31\u5728\u5144\u5f1f\u8fd9\u655e\u8f69\u4e4b\u4e2d\u5c0f\u5750\uff0c\u5bb9\u5144\u5f1f\u53eb\u4ed6\u4eec\u9001\u4e0a\u4e00\u70b9\u9152\u83dc\uff0c\u526a\u70db\u5171\u8bdd\uff0c\u4e5f\u5411\u65b9\u5144\u8bf7\u6559\u8bf7\u6559\u554a\uff01\u201d \u3000\u3000\u4ed6\u8bf4\u6765\u60c5\u771f\u8f9e\u5207\uff0c\u65b9\u96ea\u5b9c\u5012\u4e5f\u4e0d\u4fbf\u575a\u6301\uff0c\u53ea\u597d\u7b11\u9053\uff1a\u201c\u8fd9\u7b49\u9ebb\u70e6\u5e84\u5144\uff0c\u5144\u5f1f\u600e\u80fd\u5b89\u5fc3\u2026\u2026 \u3000\u3000\u5e84\u6377\u5927\u7b11\u9053\uff1a\u201c\u65b9\u5144\u4f60\u8fd9\u6837\u60f3\u53ef\u5c31\u662f\u89c1\u5916\u4e86\uff01\uff0c\u8bdd\u97f3\u4e00\u987f\uff0c\u5411\u95e8\u5916\u4f8d\u7acb\u7684\u52b2\u88c5\u5927\u6c49\u559d\u9053\uff1a\u201c\u53eb\u4ed6\u4eec\u51c6\u5907\u4e00\u70b9\u9152\u83dc\u9001\u6765\u3002\u201d \u3000\u3000\u4f8d\u7acb\u8f69\u5916\u7684\u4e24\u540d\u5927\u6c49\uff0c\u5e94\u58f0\u800c\u8d70\uff0c\u5e84\u6377\u8fd9\u624d\u4f4e\u58f0\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u6709\u4e00\u4ef6\u4e8b\uff0c\u8d81\u4ed6\u4eec\u4e0d\u5728\u6b64\u5904\uff0c\u5144\u5f1f\u5fc5\u987b\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u770b\u4ed6\u795e\u8272\u90d1\u91cd\uff0c\u5fc3\u4e2d\u6697\u6697\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u5e84\u5144\uff0c\u4ec0\u4e48\u4e8b\u8fd9\u822c\u91cd\u8981\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u6b66\u6797\u4e71\u8c61\u5df2\u73b0\uff0c\u5ddd\u4e2d\u66f4\u975e\u5584\u5730\uff0c\u65b9\u5144\u6700\u597d\u80fd\u65e9\u65e5\u79bb\u53bb\u4e3a\u5999\uff01\u201d\u8fd9\u53e5\u8bdd\u58f0\u97f3\u867d\u7136\u5f88\u4f4e\uff0c\u4f46\u5374\u9707\u7684\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u6251\u6251\u4e71\u8df3\uff0c\u5239\u90a3\u4e4b\u95f4\uff0c\u65b9\u96ea\u5b9c\u5ffd\u7136\u89c9\u51fa\u8fd9\u4f4d\u5e84\u6377\u5927\u4e0d\u7b80\u5355\uff0c\u751a\u81f3\u8fde\u4ed6\u7684\u6765\u5386\u4e5f\u5927\u4e3a\u53ef\u7591\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u65f6\u771f\u731c\u4e0d\u900f\u8fd9\u4f4d\u770b\u6765\u7cbe\u660e\u800c\u53c8\u4e0d\u5e73\u51e1\u7684\u5e84\u6377\uff0c\u5230\u5e95\u662f\u4e3a\u4e86\u4f55\u6545\uff0c\u8981\u5c48\u8eab\u505a\u5b97\u8d35\u7684\u526f\u624b\uff0c\u4f46\u4ed6\u5374\u5f88\u611f\u6fc0\u5e84\u6377\u7684\u5173\u62a4\u4e4b\u60c5\uff0c\u5f53\u4e0b\u7b11\u9053\uff1a\u201c\u5e84\u5144\u5982\u6b64\u5475\u62a4\u5144\u5f1f\uff0c\u771f\u53eb\u5144\u5f1f\u611f\u6fc0\u5f88\u2026\u2026\u201d \u3000\u3000\u5e84\u6377\u5ffd\u7136\u5927\u707e\u9053\uff1a\u201c\u5728\u5bb6\u5343\u65e5\u597d\uff0c\u51fa\u5916\u65f6\u65f6\u96be\uff0c\u65b9\u5144\u8fd9\u6837\u5e74\u7eaa\uff0c\u60f3\u5fc5\u662f\u521d\u6b21\u79bb\u5bb6\uff0c\u8fdc\u884c\u5728\u5916\uff0c\u5144\u5f1f\u80fd\u4e0e\u65b9\u5144\u76f8\u8bc6\uff0c\u81ea\u5e94\u5c3d\u6211\u4e4b\u529b\u7167\u987e\u65b9\u5144\u2026\u2026\u4f55\u51b5\uff0c\u53ea\u56e0\u5144\u5f1f\u9a6d\u4e0b\u4e0d\u4e25\uff0c\u7ed9\u65b9\u5144\u5f15\u6765\u8bb8\u591a\u9ebb\u70e6\uff0c\u8fd9\u70b9\u5fc3\u610f\uff0c\u4e0d\u8fc7\u662f\u5144\u5f1f\u5411\u65b9\u5144\u8d4e\u7f6a\u800c\u5df2\u2026\u2026\u201d \u3000\u3000\u8bed\u97f3\u672a\u5df2\uff0c\u53ea\u89c1\u4e00\u4f4d\u5f62\u5bb9\u7325\u7410\u7684\u8001\u4eba\uff0c\u5e26\u770b\u4e09\u540d\u52b2\u88c5\u5927\u6c49\u548c\u4e24\u540d\u5c0f\u53ae\uff0c\u9001\u4e0a\u4e86\u4e00\u684c\u70ed\u817e\u817e\u7684\u9152\u83b1\u6765\u3002 \u3000\u3000\u90a3\u5e84\u6377\u4f3c\u662f\u5bf9\u8fd9\u4f4d\u8001\u4eba\u5341\u5206\u5c0a\u656c\uff0c\u4e00\u89c1\u8001\u4eba\u9001\u6765\u9152\u83dc\uff0c\u7adf\u7136\u8d77\u8eab\u7b11\u9053\uff1a\u201c\u6709\u52b3\u5b8b\u5e08\u5085\u4e86\uff0c\u8fd9\u534a\u591c\u4e09\u66f4\u53eb\u4ed6\u4eec\u968f\u4fbf\u5f04\u51e0\u6837\u5c0f\u83dc\u5c31\u53ef\u4ee5\u4e86\uff0c\u4f55\u5fc5\u4f60\u81ea\u5df1\u4e0b\u53a8\u2026\u2026\u201d \u3000\u3000\u90a3\u8001\u4eba\u4e00\u5bf9\u6597\u9e21\u773c\u5728\u65b9\u96ea\u5b9c\u8eab\u4e0a\u77a7\u4e86\u534a\u664c\uff0c\u65b9\u59cb\u7b11\u9053\uff1a\u201c\u526f\u5802\u4e3b\u6b3e\u5f85\u4f73\u5bbe\uff0c\u8001\u673d\u81ea\u5e94\u534a\u81ea\u4e0b\u53a8\u3002\u201d\u8bdd\u8bed\u4e4b\u95f4\uff0c\u9152\u83dc\u4e1a\u5df2\u6446\u59a5\uff0c\u90a3\u5b8b\u59d3\u8001\u4eba\u5728\u79bb\u53bb\u4e4b\u9645\uff0c\u5374\u4f3c\u65e0\u610f\u95f4\u5730\u5728\u65b9\u96ea\u5b9c\u8eab\u4e0a\u649e\u4e86\u4e00\u4e0b\uff0c\u65b9\u96ea\u5b9c\u521a\u81ea\u4e00\u6014\uff0c\u84e6\u7136\u53d1\u73b0\u81ea\u5df1\u7684\u53f3\u638c\u5fc3\uff0c\u5df2\u88ab\u4eba\u585e\u8fdb\u4e86\u4e00\u56e2\u7269\u4ef6\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u6697\u5730\u5403\u4e86\u4e00\u60ca\uff0c\u76ee\u5149\u8fc5\u4f0f\u5730\u6295\u5411\u5e84\u6377\uff0c\u53ea\u89c1\u5e84\u6377\u6b63\u4f4e\u58f0\u5411\u90a3\u4e09\u4f4d\u52b2\u88c5\u5927\u6c49\u5728\u5429\u5490\u4ec0\u4e48\uff0c\u5e76\u672a\u77a7\u51fa\u90a3\u5b8b\u59d3\u8001\u4eba\u649e\u81ea\u5df1\uff0c\u5fc3\u4e2d\u624d\u677e\u4e86\u4e00\u53e3\u6c14\u3002 \u3000\u3000\u4f46\u5f53\u4ed6\u60f3\u8d77\u5e94\u8be5\u5411\u90a3\u4f4d\u5b8b\u59d3\u8001\u4eba\u6253\u4e2a\u62db\u547c\u65f6\uff0c\u62ac\u76ee\u671b\u53bb\uff0c\u90a3\u5b8b\u59d3\u8001\u4eba\u7684\u80cc\u5f71\uff0c\u5df2\u5728\u82b1\u6811\u4e4b\u95f4\u6d88\u5931\u4e86\u3002 \u3000\u3000\u4f46\u56e0\u5e84\u6377\u5df2\u5728\u6270\u547c\u81ea\u5df1\u5165\u5e2d\uff0c\u53ea\u597d\u538b\u4e0b\u4e86\u597d\u5947\u4e4b\u5fc3\uff0c\u79fb\u5230\u9152\u7b75\u5e2d\u524d\u5750\u5b9a\u3002 \u3000\u3000\u867d\u4ec5\u4e24\u4eba\u5bf9\u996e\uff0c\u4f46\u684c\u4e0a\u7684\u83dc\u770b\u5374\u6709\u516b\u5473\u4e4b\u591a\uff0c\u5e84\u6377\u7684\u4e00\u756a\u76db\u60c5\uff0c\u4f7f\u5f97\u65b9\u96ea\u5b9c\u5b8c\u5168\u6cef\u9664\u4e86\u5bf9\u4ed6\u7684\u6212\u5907\u4e4b\u5fc3\uff0c\u4e24\u4eba\u5c0f\u914c\u6e05\u8c08\uff0c\u5012\u4f3c\u6545\u53cb\u91cd\u9022\u4e00\u822c\u70ed\u60c5\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u5374\u5e76\u672a\u5fd8\u8bb0\u90a3\u585e\u5728\u8863\u888b\u4e2d\u7684\u7eb8\u56e2\uff0c\u8fd9\u5176\u95f4\uff0c\u4ed6\u4e00\u76f4\u5728\u60f3\u627e\u4e00\u4e2a\u673a\u4f1a\u5077\u5077\u770b\u90a3\u7eb8\u4e0a\u6240\u4e66\u4f55\u4e8b\uff1f \u3000\u3000\u4f46\u56e0\u5e84\u6377\u59cb\u7ec8\u672a\u66fe\u79bb\u5ea7\uff0c\u800c\u65e0\u6cd5\u6253\u5f00\u77a7\u770b\uff0c\u76f4\u5230\u5929\u8272\u5df2\u7136\u53d1\u767d\uff0c\u7a81\u7136\u6709\u4e00\u540d\u52b2\u88c5\u4eba\u6c49\u5306\u52ff\u800c\u6765\uff0c\u5728\u5e84\u6377\u8033\u8fb9\u4f4e\u8bed\u4e86\u51e0\u53e5\uff0c\u5e84\u6377\u987f\u65f6\u8138\u8272\u5927\u53d8\uff0c\u5411\u65b9\u96ea\u5b9c\u62b1\u62f3\u9053\uff1a\u201c\u65b9\u5144\uff0c\u5144\u5f1f\u6709\u70b9\u8981\u4e8b\u5f85\u529e\uff0c\u4f60\u4e14\u5728\u6b64\u7a0d\u5750\u4e00\u4f1a\u513f\uff0c\u5144\u5f1f\u53bb\u53bb\u5c31\u6765\u3002\u201d\u7ad9\u8d77\u8eab\u5b50\uff0c\u968f\u5728\u8fd9\u52b2\u88c5\u5927\u6c49\u8eab\u540e\uff0c\u5927\u6b65\u79bb\u53bb\u3002 \u3000\u3000\u90a3\u672c\u662f\u4f8d\u7acb\u655e\u8f69\u4e4b\u5916\u7684\u51e0\u540d\u58ee\u6c49\uff0c\u8fd9\u65f6\u4e5f\u8ddf\u5728\u5e84\u6377\u8eab\u540e\u8d70\u53bb\u3002 \u3000 \u7b2c\u5341\u4e8c\u56de\u3000\u9b54\u5200\u6388\u9996 \u3000 \u3000\u3000\u65b9\u96ea\u5b9c\u867d\u662f\u89c9\u5f97\u6709\u4e9b\u4e8b\u51fa\u7a81\u7136\uff0c\u4f46\u5fc3\u4e2d\u5ffd\u7136\u7075\u5149\u4e00\u95ea\uff0c\u4ed4\u7ec6\u7684\u5bdf\u770b\u4e86\u4e00\u4e0b\u56db\u5468\uff0c\u786e\u5b9a\u5e84\u6377\u624b\u4e0b\u4e4b\u4eba\uff0c\u679c\u771f\u4ff1\u5df2\u4e0d\u5728\uff0c\u4ed6\u8fd9\u624d\u638f\u51fa\u90a3\u5c0f\u5c0f\u7eb8\u56e2\uff0c\u5f20\u76ee\u671b\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u647a\u76b1\u7684\u7eb8\u4e0a\uff0c\u53ea\u5199\u4e86\u201c\u8fb0\u65f6\u4ee5\u524d\u901f\u79bb\u5609\u5b9a\u201d\u516b\u4e2a\u6f66\u8349\u7684\u5b57\u8ff9\uff0c\u7eb8\u89d2\u4e4b\u4e0b\uff0c\u5374\u53c8\u753b\u4e86\u4e00\u53cc\u67af\u7626\u5982\u67f4\u7684\u4eba\u624b\uff0c\u663e\u7136\u4f3c\u662f\u4ee3\u8868\u90a3\u4e66\u5199\u5b57\u6761\u7684\u4eba\u7684\u8eab\u4efd\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5bf9\u6c5f\u6e56\u4eba\u7269\u77e5\u4e4b\u4e0d\u591a\uff0c\u8fd9\u53ea\u7626\u624b\u4ee3\u8868\u4e86\u4ec0\u4e48\u4eba\uff0c\u4ed6\u5b8c\u5168\u4e0d\u77e5\uff0c\u81f3\u4e8e\u662f\u5426\u5c31\u662f\u90a3\u4f4d\u5f62\u8c8c\u72e0\u7410\u7684\u5b8b\u8001\u4eba\u81ea\u5df1\uff0c\u4ed6\u4e5f\u65e0\u6cd5\u786e\u5b9a\uff0c\u6b64\u523b\u4ed6\u5fc3\u4e2d\u6240\u60f3\uff0c\u5c31\u662f\u8fd9\u8fb0\u65f6\u4ee5\u524d\u901f\u79bb\u5609\u5b9a\u516b\u4e2a\u5b57\uff0c\u5230\u5e95\u662f\u4e3a\u4e86\u4f55\u6545\uff1f \u3000\u3000\u5728\u4ed6\u60f3\u6765\u3002\u81ea\u5df1\u752b\u51fa\u5e08\u95e8\uff0c\u81ea\u7136\u4e0d\u4f1a\u6709\u4eba\u8ba4\u5f97\u81ea\u5df1\uff0c\u800c\u773c\u4e0b\u5374\u6709\u4eba\u8b66\u544a\u81ea\u5df1\u901f\u79bb\u5609\u5b9a\uff0c\u5176\u6545\u5b89\u5728\uff1f \u3000\u3000\u65b9\u96ea\u5b9c\u6c89\u541f\u826f\u4e45\uff0c\u5374\u6ca1\u6709\u60f3\u51fa\u4e1d\u6beb\u5934\u7eea\uff0c\u773c\u89c1\u5929\u5df2\u7136\u5927\u4eae\uff0c\u5e84\u6377\u4ecd\u672a\u5f52\u6765\uff0c\u4ed6\u5ffd\u7136\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6697\u6697\u5fd6\u9053\uff1a\u201c\u83ab\u975e\u6709\u4eba\u6545\u610f\u7f20\u4f4f\u4e86\u5e84\u6377\uff0c\u597d\u53eb\u6211\u79bb\u5f00\u6b64\u5904\u2026\u2026\u201d\u4ed6\u4e00\u5ff5\u53ca\u6b64\uff0c\u9661\u7136\u89c9\u5f97\u8fd9\u5e84\u6377\u7684\u4e3a\u4eba\uff0c\u6df1\u6c89\u5f97\u4e0d\u53ef\u601d\u8bae\uff0c\u540c\u65f6\u4e5f\u60f3\u5230\u90a3\u793a\u8b66\u4e4b\u4eba\u7684\u4e00\u7247\u82e6\u5fc3\uff0c\u81ea\u5df1\u786e\u662f\u4e0d\u53ef\u8f9c\u8d1f\uff0c\u5fc5\u7136\u662f\u90a3\u6210\u7384\u901a\u7b49\u4eba\u4e1a\u5df2\u77e5\u9053\u4e86\u81ea\u5df1\u7684\u8eab\u4efd\uff0c\u8981\u5e84\u6377\u5957\u4f4f\u81ea\u5df1\uff0c\u7136\u540e\u518d\u6765\u7b97\u8ba1\u81ea\u5df1\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u53ea\u89c9\u5f97\u80f8\u5934\u6012\u706b\u4e2d\u70e7\uff0c\u6068\u6068\u5730\u4e00\u638c\u62cd\u5728\u90a3\u6728\u684c\u4e4b\u4e0a\uff0c\u51b7\u54fc\u4e00\u58f0\uff0c\u81ea\u8bed\u9053\uff1a \u3000\u3000\u201c\u6211\u5012\u662f\u8981\u77a7\u77a7\u4f60\u4eec\u53c8\u80fd\u5bf9\u6211\u600e\u6837\uff1f\u201d \u3000\u3000\u4f59\u97f3\u672a\u7edd\uff0c\u8033\u4e2d\u53ea\u542c\u5f97\u5e84\u6377\u5927\u7b11\u4e4b\u58f0\u9053\uff1a\u201c\u65b9\u5144\uff0c\u53eb\u4f60\u4e45\u7b49\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5403\u4e86\u4e00\u60ca\uff0c\u62ac\u5934\u671b\u53bb\uff0c\u53ea\u89c1\u5e84\u6377\u5df2\u7136\u72ec\u81ea\u4e00\u4eba\uff0c\u65e0\u58f0\u65e0\u606f\u5730\u7ad9\u5728\u81ea\u5df1\u8eab\u524d\uff0c\u5f53\u4e0b\u5fd9\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u5e84\u5144\u7684\u4e8b\u60c5\u529e\u5b8c\u4e86\u5417\uff1f\u201d \u3000\u3000\u5e84\u6377\u7b11\u9053\uff1a\u201c\u529e\u5b8c\u4e86\uff0c\u771f\u60f3\u4e0d\u5230\u8fd9\u5609\u5b9a\u57ce\u4e2d\uff0c\u5c45\u7136\u6709\u4e86\u8fd9\u7b49\u9ad8\u624b\uff0c\u672c\u5802\u4e09\u4f4d\u62a4\u6cd5\u8054\u624b\uff0c\u7adf\u4e5f\u4e0d\u66fe\u652f\u6301\u5341\u62db\uff0c\u5c31\u91cd\u4f24\u4e86\u4e24\u4f4d\uff0c\u82e5\u975e\u5144\u5f1f\u7387\u4eba\u53bb\u5f97\u6b63\u662f\u65f6\u5019\uff0c\u6050\u6015\u4e09\u4f4d\u62a4\u6cd5\u90fd\u8981\u4e27\u751f\u5bf9\u65b9\u624b\u4e0b\u4e86\u3002\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u76ee\u5149\u5728\u65b9\u96ea\u5b9c\u8eab\u524d\u7684\u684c\u4e0a\u4e00\u8f6c\uff0c\u63a5\u9053\uff1a\u201c\u65b9\u5144\uff0c\u8fd9\u662f\u4ec0\u4e48\uff1f\u2026\u2026\u201d\u4f38\u624b\u4fbf\u5c06\u90a3\u6446\u5728\u684c\u4e0a\u7684\u7eb8\u56e2\uff0c\u53d6\u5230\u4e86\u624b\u4e2d\u3002 \u3000\u3000\u6562\u60c5\u65b9\u96ea\u5b9c\u9002\u624d\u6309\u684c\u81ea\u8bed\u65f6\uff0c\u628a\u634f\u5728\u638c\u5fc3\u4e2d\u7684\u90a3\u5f20\u6761\u7ed9\u7559\u5728\u684c\u4e0a\uff0c\u5e84\u6377\u5ffd\u7136\u73b0\u8eab\uff0c\u5403\u60ca\u4e4b\u4e0b\uff0c\u4fbf\u5fd8\u4e86\u5c06\u90a3\u5f20\u7eb8\u6761\u987a\u624b\u62bd\u56de\uff0c\u4e00\u65f6\u5927\u610f\uff0c\u7adf\u7136\u843d\u5230\u5e84\u6377\u624b\u4e2d\uff0c\u5fc3\u4e2d\u53ef\u662f\u5927\u4e3a\u7740\u6025\uff0c\u4e5f\u89c9\u7740\u5927\u5927\u7684\u5bf9\u4e0d\u8d77\u90a3\u4f4d\u7559\u5b57\u793a\u8b66\u4e4b\u4eba\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u8131\u53e3\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u3002\u5e84\u5144\u79bb\u53bb\u4e4b\u540e\uff0c\u5144\u5f1f\u5728\u655e\u8f69\u7684\u77f3\u9636\u4e4b\u4e0a\uff0c\u53d1\u73b0\u8fd9\u5f20\u5b57\u6761\uff0c\u662f\u4ee5\u987a\u624b\u62ac\u8d77\u2026\u2026\u201d \u3000\u3000\u4ed6\u8fd9\u7b49\u89e3\u8bf4\uff0c\u4efb\u8c01\u4e5f\u542c\u5f97\u51fa\u4e43\u662f\u865a\u4f2a\u63a8\u6258\u4e4b\u8f9e\uff0c\u4f46\u602a\u7684\u662f\u5e84\u6377\u5728\u770b\u4e86\u90a3\u7eb8\u4e0a\u7684\u5b57\u8ff9\u4ee5\u540e\uff0c\u7adf\u7136\u5c06\u5b83\u9012\u8fd8\u7ed9\u4e86\u65b9\u96ea\u5b9c\uff0c\u70b9\u5934\u4e00\u7b11\u9053\uff1a\u201c\u65b9\u5144\u62fe\u5230\u4e4b\u7269\uff0c\u8fd8\u662f\u7531\u65b9\u5144\u7559\u4e0b\u5427\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6123\u4e86\u4e00\u6123\uff0c\u6697\u9053\uff1a\u8fd9\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u4ed6\u600e\u4f1a\u4e0d\u518d\u6df1\u7a76\uff0c\u2026\u2026\u4f46\u4ed6\u53e3\u4e2d\u5374\u5fd9\u7740\u9053\uff1a \u3000\u3000\u201c\u591a\u8c22\u5e84\u5144\u4e86\uff01\u201d\u4f38\u624b\u63a5\u8fc7\u5b57\u6761\uff0c\u63e3\u5165\u8863\u886b\u4e4b\u5185\u3002 \u3000\u3000\u5e84\u6377\u76ee\u5149\u4e00\u8f6c\uff0c\u63a2\u624b\u6293\u8d77\u9152\u58f6\uff0c\u91cd\u65b0\u659f\u4e86\u4e24\u676f\u9152\uff0c\u7b11\u9053\uff1a\u201c\u65b9\u5144\uff0c\u8fd8\u6709\u534a\u4e2a\u65f6\u8fb0\uff0c\u5c31\u8be5\u662f\u4f60\u79bb\u53bb\u7684\u65f6\u523b\uff0c\u5144\u5f1f\u518d\u656c\u4f60\u4e00\u676f\u5982\u4f55\uff1f\u201d \u3000\u3000\u8bf4\u7f62\uff0c\u4e3e\u676f\u4e00\u996e\u800c\u5c3d\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u867d\u662f\u7591\u5ff5\u6742\u4e3b\uff0c\u4f46\u5e84\u6377\u65e2\u4e0d\u591a\u95ee\uff0c\u4ed6\u4e5f\u4e50\u5f97\u88c5\u4f6f\uff0c\u7acb\u5373\u53d6\u8fc7\u4e00\u676f\u9152\uff0c\u4ef0\u5934\u559d\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5e84\u6377\u54c8\u54c8\u4e00\u7b11\uff0c\u653e\u4e0b\u624b\u4e2d\u9152\u676f\uff0c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u90a3\u5b57\u6761\u4e4b\u4e0a\u7684\u9b3c\u624b\u6807\u5fd7\uff0c\u4f60\u53ef\u8ba4\u5f97\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u539f\u4ee5\u4e3a\u5e84\u6377\u5e76\u4e0d\u5173\u5fc3\u90a3\u5f20\u5b57\u6761\uff0c\u8fd9\u65f6\u542c\u4ed6\u4e00\u95ee\uff0c\u5fc3\u4e2d\u65b9\u660e\u767d\uff0c\u6b64\u4eba\u884c\u4e8b\uff0c\u9547\u9759\u6c89\u7a33\u9ad8\u4eba\u4e00\u7b79\uff0c\u660e\u660e\u5341\u5206\u5173\u5fc3\u4e4b\u4e8b\uff0c\u5374\u80fd\u6545\u4f5c\u4ece\u5bb9\uff0c\u51fa\u8bf8\u4e8e\u7b11\u8bed\u95f9\u8c08\u4e4b\u95f4\uff0c\u4f7f\u4f60\u4e8e\u4e0d\u77e5\u4e0d\u515a\u4e2d\u8bf4\u51fa\u5b9e\u8bdd\u6765\u3002\u5f53\u4e0b\u5fae\u5fae\u4e00\u7b11\uff0c\u6447\u5934\u9053\uff1a\u201c\u5144\u5f1f\u751a\u5c11\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\uff0c\u5bf9\u8fd9\u4e9b\u4ee3\u8868\u8eab\u4efd\u7684\u6807\u5fd7\uff0c\u53ef\u8c13\u4e00\u7a8d\u4e0d\u901a\u3002\u201d \u3000\u3000\u5e84\u6377\u7b11\u9053\uff1a\u201c\u5144\u5f1f\u77e5\u9053\u65b9\u5144\u4e0d\u4f1a\u77e5\u6653\u2026\u2026\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u585e\u5916\u5927\u6f20\u4e4b\u4e2d\uff0c\u6709\u4e00\u4f4d\u540d\u5077\u9ad8\u624b\uff0c\u4e0d\u4f46\u5077\u6280\u9ad8\u660e\u7684\u795e\u51fa\u9b3c\u6ca1\uff0c\u800c\u4e14\u4e00\u8eab\u6b66\u529f\uff0c\u4e5f\u662f\u4eba\u95f4\u7f55\u89c1\uff0c\u56e0\u6b64\uff0c\u88ab\u4eba\u9001\u4e86\u4e2a\u96c5\u53f7\u53eb\u505a\u201c\u5927\u6f20\u9b3c\u624b\u201d\uff0c\u4f46\u51e1\u88ab\u4ed6\u5149\u987e\u4e4b\u540e\uff0c\u5fc5\u7136\u4f1a\u7559\u4e0b\u4e00\u7acb\u7eaf\u91d1\u6253\u5c31\uff0c\u5bf8\u8bb8\u5927\u5c0f\u7684\u9b3c\u624b\uff0c\u6240\u4ee5\u8fd9\u9b3c\u624b\u4e5f\u5c31\u6210\u4e3a\u8fd9\u4f4d\u8001\u4eba\u7684\u6807\u5fd7\u4e86\uff0c\u770b\u6765\uff0c\u65b9\u5144\u6240\u62fe\u7684\u5b57\u6761\uff0c\u516b\u6210\u662f\u6b64\u8001\u6240\u7559\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5bf9\u5927\u6f20\u9b3c\u624b\u4e4b\u540d\uff0c\u53ef\u8c13\u5b8c\u5168\u964c\u751f\uff0c\u542c\u5f97\u5e84\u6377\u8bf4\u660e\u4e4b\u540e\uff0c\u5012\u4e0d\u89c9\u7740\u6709\u4f55\u53ef\u60ca\u53ef\u559c\u4e4b\u5904\uff0c\u53ea\u662f\u50bb\u50bb\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u591a\u8c22\u5e84\u5144\u6307\u6559\u3002\u201d \u3000\u3000\u5e84\u6377\u89c1\u4ed6\u542c\u5230\u5927\u6f20\u9b3c\u624b\u4e4b\u540d\uff0c\u7adf\u7136\u6beb\u4e0d\u52a8\u5bb9\uff0c\u4e0d\u7981\u6697\u6697\u76b1\u7709\u5934\u8ba8\u9053\uff1a\u201c\u83ab\u975e\u4ed6\u771f\u7684\u4e0d\u8ba4\u8fd9\u4f4d\u6b66\u6797\u9ad8\u4eba\uff1f\u201d\u7565\u4e00\u6c89\u541f\uff0c\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u65b9\u5144\uff0c\u6562\u60c5\u4f60\u771f\u7684\u6ca1\u542c\u8bf4\u8fc7\u8fd9\u4f4d\u9b3c\u624b\u524d\u8f88\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u6014\u9053\uff1a\u201c\u5e84\u5144\u600e\u5730\u4f3c\u662f\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u5e84\u6377\u4f4e\u58f0\u4e00\u53f9\u9053\uff1a\u201c\u65b9\u5144\uff0c\u5e76\u975e\u5143\u5f1f\u4e0d\u4fe1\uff0c\u800c\u662f\u9b3c\u624b\u524d\u8f88\u7684\u4e3a\u4eba\uff0c\u5411\u6765\u72ec\u884c\u5176\u662f\uff0c\u5018\u82e5\u65b9\u5144\u4e0e\u4ed6\u771f\u4e2a\u4e0d\u66fe\u76f8\u8bc6\uff0c\u8fd9\u5f20\u5b57\u6761\uff0c\u5c82\u975e\u51fa\u73b0\u5f97\u5927\u4e3a\u5947\u602a\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u5144\u5f1f\u4e5f\u89c9\u7740\u5176\u4e2d\u96be\u89e3\u4e4b\u5904\u751a\u591a\uff0c\u4e5f\u8bb8\uff0c\u5c06\u6765\u5982\u80fd\u89c1\u5230\u9b3c\u624b\u524d\u8f88\uff0c\u5c31\u53ef\u660e\u767d\u9053\u7406\u5411\u5728\u4e86\u3002\u201d \u3000\u3000\u5e84\u6377\u5fae\u5fae\u4e00\u7b11\uff0c\u62ac\u5934\u770b\u4e86\u770b\u8f69\u5916\u5929\u8272\uff0c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u5929\u8272\u4e0d\u65e9\u4e86\uff0c\u5144\u5f1f\u9001\u65b9\u5144\u51fa\u57ce\u5982\u4f55\uff1f\u201d \u3000\u3000\u5982\u5c31\u65b9\u96ea\u5b9c\u539f\u610f\uff0c\u4ed6\u771f\u60f3\u7559\u5728\u5609\u5b9a\uff0c\u7b49\u7740\u77a7\u77a7\u90a3\u5b97\u58eb\u4e49\u7b49\u4eba\uff0c\u53ef\u80fd\u4f1a\u5bf9\u81ea\u5df1\u8981\u51fa\u4ec0\u4e48\u82b1\u6837\uff0c\u4f46\u5f53\u5e84\u6377\u8bf4\u660e\u90a3\u7559\u5b57\u4e4b\u4eba\uff0c\u4e43\u662f\u4e00\u4f4d\u6b66\u6797\u9ad8\u4eba\u5927\u6f20\u9b3c\u624b\u4ee5\u540e\uff0c\u65b9\u96ea\u5b9c\u4fbf\u5df2\u89c9\u51fa\uff0c\u5176\u4e2d\u5fc5\u6709\u9053\u7406\uff0c\u4e8e\u662f\uff0c\u4f9d\u7167\u9b54\u624b\u7559\u5b57\u6307\u793a\uff0c\u5728\u8fb0\u65f6\u4ee5\u524d\u79bb\u5f00\u5609\u5b9a\uff0c\u53cd\u6b63\u81ea\u5df1\u4eca\u540e\u884c\u8d70\u6c5f\u6e56\uff0c\u4e0d\u6015\u67e5\u4e0d\u51fa\u8fd9\u4e09\u4e49\u95e8\u7684\u6839\u5e95\u3002 \u3000\u3000\u5fc3\u5ff5\u4e00\u5b9a\uff0c\u5f53\u5373\u7b11\u9053\uff1a\u201c\u4e0d\u6562\u5f53\u5f97\u5e84\u5144\u76db\u610f\uff0c\u5144\u5f1f\u8fd9\u5c31\u544a\u8f9e\u3002\u201d \u3000\u3000\u7ad9\u8d77\u8eab\u5b50\uff0c\u5bf9\u5e84\u6377\u62b1\u62f3\u4e00\u63d6\uff0c\u5927\u6b65\u81ea\u5916\u884c\u53bb\u3002 \u3000\u3000\u5e84\u6377\u4e3e\u6b65\u76f8\u968f\uff0c\u7b11\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u6211\u867d\u662f\u840d\u6c34\u76f8\u8fde\uff0c\u521d\u6b21\u4f26\u4ea4\uff0c\u4f46\u5144\u5f1f\u5bf9\u65b9\u5374\u662f\u4ef0\u6155\u65e0\u5df2\uff0c\u5c1a\u671b\u65b9\u5144\u80fd\u591f\u6210\u5168\u5144\u5f1f\u8fd9\u756a\u5fc3\u610f\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u4e24\u4eba\u5df2\u7136\u8d70\u51fa\u82b1\u56ed\u4fa7\u95e8\u4e4b\u5916\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5728\u76db\u60c5\u96be\u5374\u4e4b\u4e0b\uff0c\u53ea\u597d\u4efb\u7531\u90a3\u5e84\u6377\u966a\u540c\u81ea\u5df1\uff0c\u8d76\u5230\u60a6\u6765\u5ba2\u5f7c\uff0c\u53d6\u8fc7\u884c\u674e\u9a6c\u5339\uff0c\u7f13\u6b65\u8d70\u51fa\u5609\u5b9a\u4e1c\u5173\u57ce\u5916\u3002 \u3000\u3000\u76f4\u5230\u79bb\u57ce\u4e09\u91cc\u8fdc\u8fd1\uff0c\u5e84\u6377\u65b9\u59cb\u957f\u957f\u5401\u4e86\u4e00\u53e3\u5927\u6c14\uff0c\u62b1\u62f3\u9053\uff1a\u201c\u65b9\u5144\uff0c\u9001\u7fa4\u5343\u91cc\uff0c\u7ec8\u987b\u4e00\u522b\uff0c\u6055\u5144\u5f1f\u4e0d\u518d\u8fdc\u9001\u5144\u53f0\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fd9\u9053\uff1a\u201c\u5e84\u5144\u8bf7\u8f6c\uff0c\u5144\u5f1f\u8fd9\u53a2\u62dc\u8c22\u76db\u60c5\u2026\u2026\u201d\u5f53\u4e0b\u62b1\u62f3\uff0c\u957f\u62dc\u5728\u5730\u3002 \u3000\u3000\u5e84\u6377\u8fc5\u5feb\u7684\u4f38\u624b\u6276\u4f4f\u4e86\u65b9\u96ea\u5b9c\uff0c\u5de6\u624b\u5374\u8d81\u52bf\u9012\u8fc7\u53bb\u4e00\u79cd\u7269\u4e8b\uff0c\u53e3\u4e2d\u8fde\u58f0\u8bf4\uff1a\u201c\u4e0d\u6562\u2026\u2026\u4e0d\u6562\u2026\u2026\u65b9\u5144\u524d\u9014\u73cd\u91cd\u4e86\u3002\u201d \u3000\u3000\u8bdd\u97f3\u4e00\u843d\uff0c\u677e\u5f00\u6276\u4f4f\u65b9\u96ea\u5b9c\u7684\u53cc\u624b\uff0c\u5f80\u540e\u9000\u4e86\u4e00\u6b65\uff0c\u518d\u6b21\u62b1\u62f3\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u65b9\u5144\u4fdd\u91cd\u2026\u2026\u201d\u7a81\u7136\u8f6c\u8eab\uff0c\u4e3e\u6b65\u75be\u9a76\u800c\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u4e86\u4e00\u5446\uff0c\u76f4\u77a7\u5f97\u5e84\u6377\u7684\u8eab\u5f71\u5728\u8fdc\u5904\u6d88\u5931\uff0c\u8fd9\u624d\u8f6c\u8eab\u4e0a\u9a6c\uff0c\u6cbf\u7740\u901a\u5f80\u6210\u90fd\u7684\u5b98\u9053\u884c\u53bb\u3002 \u3000\u3000\u4ed6\u4e00\u9635\u517c\u7a0b\u75be\u9a76\uff0c\u534a\u4e2a\u65f6\u8fb0\u4e0d\u5230\uff0c\u5df2\u79bb\u5f00\u5609\u5b9a\u8db3\u8db3\u6709\u4e09\u5341\u4f59\u91cc\uff0c\u4e3e\u76ee\u671b\u5411\u524d\u9762\uff0c\u53ea\u89c1\u4e0d\u8fdc\u5904\uff0c\u6709\u51e0\u5bb6\u5c0f\u5e97\u5f00\u5728\u8def\u65c1\uff0c\u65b9\u96ea\u5b9c\u6b64\u523b\u53ea\u89c9\u53e3\u4e2d\u751a\u611f\u7126\u6e34\uff0c\u4fbf\u50ac\u9a6c\u76f4\u8d8b\u8def\u5934\u7684\u4e00\u5bb6\u8336\u9986\uff0c\u8981\u4e86\u4e00\u58f6\u6e05\u8336\uff0c\u987a\u4fbf\u8ba9\u90a3\u7ea2\u9a6c\u4e5f\u597d\u4f11\u606f\u4e00\u756a\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u53e3\u8fde\u5e72\u4e86\u4e09\u58f6\u8336\uff0c\u65b9\u59cb\u89c9\u7740\u53e3\u4e2d\u6b62\u6e34\u4e3b\u6d25\uff0c\u8fd9\u65f6\u624d\u60f3\u5e84\u6377\u4ea4\u7ed9\u81ea\u5df1\u4e4b\u7269\uff0c\u8fde\u5fd9\u638f\u4e86\u51fa\u6765\uff0c\u539f\u6765\u4e5f\u662f\u4e00\u4e2a\u56e2\u6210\u9f99\u773c\u5927\u5c0f\u7684\u7eb8\u56e2\u3002\u4ed6\u62e8\u5f00\u7eb8\u56e2\uff0c\u987f\u65f6\u6709\u4e00\u7c92\u8272\u6cdb\u91d1\u9ec4\u7684\u4e39\u4e38\u843d\u5728\u638c\u5fc3\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u6014\u4e86\u6014\uff0c\u8fde\u5fd9\u644a\u5f00\u7eb8\u56e2\uff0c\u53ea\u89c1\u4e0a\u9762\u5199\u7740\uff1a\u543e\u5144\u5df2\u4e2d\u9152\u5185\u5267\u6bd2\uff0c\u8fb0\u65f6\u4e00\u8fc7\uff0c\u5fc5\u5c06\u53d1\u4f5c\uff0c\u901f\u670d\u6b64\u4e39\uff0c\u53ef\u4fdd\u65e0\u8651\u3002\u201d\u5e95\u4e0b\u7684\u7f72\u540d\uff0c\u7adf\u662f\u5e84\u6377\u4e8c\u5b57\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u53ea\u77a7\u7684\u5251\u76fe\u4e00\u76b1\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u4f4d\u5e84\u5144\u884c\u4e8b\uff0c\u600e\u4f1a\u5982\u6b64\u98a0\u5012\uff1f\u65e2\u7136\u9152\u4e2d\u6709\u6bd2\uff0c\u4e3a\u4f55\u529d\u6211\u996e\u4e0b\uff1f\u65e2\u662f\u8981\u6211\u996e\u9152\u4e2d\u6bd2\uff0c\u5374\u4e3a\u4f55\u53c8\u504f\u504f\u8fd9\u7b49\u9b3c\u9b3c\u795f\u795f\u5730\u7ed9\u6211\u89e3\u836f\uff1f\u51fa\u5c14\u53cd\u5c14\uff0c\u5176\u4e2d\u9053\u7406\u5b89\u5728\uff1f\u201d \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u65b9\u96ea\u5b9c\u8d8a\u60f3\u8d8a\u89c9\u96be\u4ee5\u91ca\u6000\uff0c\u4f55\u51b5\uff0c\u6b64\u523b\u65e9\u5df2\u5230\u4e86\u8fb0\u65f6\u4e09\u523b\u5de6\u53f3\uff0c\u81ea\u5df1\u5018\u662f\u771f\u4e2a\u4e2d\u6bd2\uff0c\u600e\u5730\u53c8\u4e0d\u89c1\u4e1d\u6beb\u53d1\u4f5c\u8ff9\u8c61\uff1f \u3000\u3000\u5ff5\u5934\u8f6c\u52a8\u4e4b\u9645\uff0c\u4e0d\u77e5\u4e0d\u89c9\u6697\u63d0\u4e00\u53e3\u771f\u6c14\uff0c\u5faa\u7740\u4e94\u7ecf\u516b\u8109\uff0c\u9ed8\u67e5\u4e00\u5468\uff0c\u611f\u4f53\u5185\u5929\u673a\u8212\u7545\uff0c\u65e0\u4e1d\u6beb\u4e0d\u9002\u4e4b\u8c61\uff0c\u51d1\u5728\u9f3b\u5c16\u4e4b\u4e0a\uff0c\u95fb\u4e86\u534a\u5929\uff0c\u5374\u4e5f\u77a7\u4e0d\u5230\u3001\u95fb\u4e0d\u51fa\u4ec0\u4e48\u7a76\u7adf\u6765\uff0c\u53ea\u597d\u72e0\u72e0\u5730\u626c\u4e86\u4e00\u626c\u521b\u7709\uff0c\u51b3\u5fc3\u5148\u6536\u8d77\u8fd9\u7c92\u4e39\u4e38\uff0c\u5bb9\u5f85\u4ee5\u540e\u518d\u6765\u67e5\u9a8c\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u6253\u7b97\u5305\u8d77\u90a3\u7c92\u91d1\u4e39\u4e4b\u9645\uff0c\u7a81\u7136\u8033\u4e2d\u54cd\u8d77\u4e00\u4e1d\u868a\u868b\u822c\u7684\u8bed\u97f3\u9053\uff1a\u201c\u5929\u4e0b\u7adf\u7136\u4f1a\u6709\u62ff\u7740\u6b66\u5f53\u89e3\u6bd2\u81f3\u54c1\u592a\u6e05\u91d1\u4e39\u53d1\u6123\u7684\u50bb\u5c0f\u5b50\uff0c\u6211\u5b8b\u8001\u90aa\u5012\u662f\u5934\u4e00\u6b21\u77a7\u5230\u2026\u2026\u201d \u3000\u3000\u8bdd\u97f3\u5165\u8033\uff0c\u65b9\u96ea\u5b9c\u987f\u65f6\u5927\u5403\u4e00\u60ca\uff0c\u4ed6\u77e5\u9053\u8fd9\u4e43\u662f\u6b66\u6797\u4e4b\u4e2d\uff0c\u9a6d\u6c14\u4f20\u97f3\u4e4b\u672f\uff0c\u663e\u7136\u8fd9\u8bf4\u8bdd\u4e4b\u4eba\uff0c\u5fc5\u662f\u4e00\u4f4d\u6b66\u6797\u7f55\u89c1\u7684\u9ad8\u624b\uff0c\u800c\u4e14\uff1a\u8fd9\u8bdd\u4e2d\u542b\u610f\uff0c\u4e5f\u9887\u4f3c\u5bf9\u81ea\u5df1\u800c\u53d1\uff0c\u4e0d\u7981\u5fd8\u4e86\u63e3\u8d77\u90a3\u9897\u91d1\u4e39\uff0c\u832b\u7136\u4e3e\u76ee\u5411\u56db\u5468\u671b\u53bb\u3002\u53ea\u89c1\u8fd9\u5c0f\u5c0f\u7684\u8336\u9986\u4e4b\u5185\uff0c\u9664\u4e86\u81ea\u5df1\u5916\uff0c\u4e03\u5f20\u65b9\u684c\uff0c\u4e00\u5171\u53ea\u6709\u56db\u4f4d\u8336\u5ba2\uff0c\u5176\u4e2d\u6709\u4e09\u4f4d\u662f\u5e26\u5200\u4f69\u5251\u7684\u52b2\u88c5\u6c49\u5b50\u3002\u4f46\u5374\u5206\u5750\u5728\u9760\u5411\u95e8\u53e3\u7684\u4e09\u5f20\u6728\u684c\uff0c\u5168\u795e\u8d2f\u6ce8\u5730\u77aa\u7740\u5b98\u9053\u4e0a\u7684\u884c\u4eba\uff0c\u5012\u4f3c\u4e0d\u66fe\u56de\u5934\u77a7\u8fc7\u81ea\u5df1\u3002 \u3000\u3000\u90a3\u53e6\u5916\u7684\u4e00\u4f4d\u8336\u5ba2\uff0c\u5219\u662f\u4f4d\u5e74\u7ea6\u4e03\u5341\u51fa\u5934\uff0c\u4e00\u8eab\u6750\u7fc1\u5b66\u7a76\u822c\u6253\u626e\u7684\u4eba\uff0c\u7a7f\u7740\u4e00\u88ad\u7070\u65e7\u7684\u84dd\u5e02\u957f\u886b\uff0c\u8170\u95f4\u7cfb\u4e86\u4e00\u6839\u53d8\u9ec4\u7684\u767d\u5e26\u5b50\uff0c\u6ee1\u5934\u767d\u53d1\uff0c\u957f\u7ea6\u5bf8\u8bb8\uff0c\u4f46\u9760\u8fd1\u9876\u95e8\u4e4b\u5904\uff0c\u5374\u53c8\u8131\u7684\u7cbe\u5149\uff0c\u662f\u4ee5\u8fdc\u8fdc\u77a7\u8fc7\u53bb\uff0c\u5012\u50cf\u662f\u6234\u4e86\u4e00\u9876\u767d\u8fb9\u8089\u9876\u7684\u5c0f\u5e3d\u4e00\u822c\u3002 \u3000\u3000\u6b64\u523b\uff0c\u90a3\u8001\u4eba\u6b63\u634f\u7740\u5c71\u7f8a\u80e1\u9aef\uff0c\u671b\u7740\u65b9\u96ea\u5b9c\u76f4\u7b11\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6697\u9053\uff1a\u201c\u96be\u9053\u8fd9\u8bf4\u8bdd\u4e4b\u4eba\uff0c\u4f1a\u662f\u4ed6\u5417\uff1f\u201d \u3000\u3000\u5fc3\u5ff5\u8f6c\u52a8\u4e4b\u95f4\uff0c\u4e0d\u81ea\u89c9\u7684\u81ea\u8001\u4eba\u4e00\u7b11\u3002 \u3000\u3000\u90a3\u8001\u4eba\u539f\u672c\u671b\u7740\u65b9\u96ea\u5b9c\u5728\u7b11\uff0c\u5bb9\u5f97\u65b9\u96ea\u5b9c\u4e5f\u5bf9\u4ed6\u62a5\u4ee5\u56de\u7b11\u4e4b\u9645\uff0c\u4ed6\u7adf\u7136\u9669\u8272\u4e00\u6c89\uff0c\u6389\u5934\u4e0d\u770b\u65b9\u96ea\u5b9c\uff0c\u5927\u58f0\u559d\u53eb\u8336\u9986\u4f19\u8ba1\u9053\uff1a\u201c\u5802\u500c\uff0c\u6ce1\u6eda\u6c34\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u53ea\u89c9\u5f97\u8fd9\u4f4d\u8001\u5b66\u7a76\u7684\u795e\u6001\uff0c\u5927\u4e0d\u5408\u5e38\u60c5\uff0c\u5bb9\u5f97\u90a3\u5802\u500c\u66ff\u4ed6\u780c\u4e0a\u4e86\u5f00\u6c34\uff0c\u65b9\u96ea\u5b9c\u6b63\u60f3\u4e0a\u524d\u53bb\u5411\u8fd9\u8001\u5b66\u7a76\u642d\u4ed9\u51e0\u53e5\uff0c\u5ffd\u7136\u8033\u4e2d\u53c8\u4f20\u6765\u4e86\u5148\u524d\u6709\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u4f60\u5982\u679c\u60f3\u6d3b\u4e0b\u53bb\u6765\u8ddf\u6211\u5b8b\u8001\u90aa\u8bb2\u8bdd\uff0c\u6700\u597d\u5148\u628a\u90a3\u9897\u592a\u6e05\u91d1\u4e39\u541e\u4e0b\u809a\u53bb\uff0c\u522b\u4ee5\u4e3a\u4f60\u4ed7\u6043\u7740\u4e00\u53e3\u771f\u6c14\uff0c\u6682\u65f6\u538b\u5236\u5267\u6bd2\u4e0d\u4f1a\u53d1\u4f5c\uff0c\u4f46\u65f6\u95f4\u4e00\u4e45\uff0c\u7eb5\u662f\u518d\u670d\u4e0b\u91d1\u4e39\uff0c\u5c31\u6765\u4e0d\u53ca\u5566\uff01\u201d\u65b9\u96ea\u5b9c\u542c\u5f97\u5927\u4e3a\u60d1\u7136\uff0c\u539f\u6765\u4ed6\u5728\u4e00\u9762\u542c\u8bdd\u4e4b\u65f6\uff0c\u4e00\u800c\u5df2\u6697\u4e2d\u518d\u5ea6\u8fd0\u6c14\u9ed8\u5bdf\uff0c\u81ea\u5df1\u7a76\u7adf\u662f\u5426\u6709\u90a3\u4e2d\u6bd2\u73b0\u8c61\uff0c\u7b49\u5f97\u5bf9\u65b9\u8bdd\u97f3\u4e00\u843d\uff0c\u4ed6\u4e1a\u5df2\u89c9\u51fa\u81ea\u5df1\u786e\u662f\u5e76\u672a\u4e2d\u6bd2\uff0c\u662f\u4ee5\uff0c\u5fc3\u4e2d\u5b8c\u5168\u4e0d\u89e3\uff0c\u77aa\u7740\u4e24\u773c\uff0c\u671b\u7740\u90a3\u4f4d\u5b66\u7a76\u822c\u7684\u8001\u4eba\u53d1\u5446\u3002 \u3000\u3000\u90a3\u8001\u4eba\u8fd9\u65f6\u5ffd\u7136\u957f\u957f\u4e00\u53f9\uff0c\u7ad9\u8d77\u8eab\u6765\u3002 \u3000\u3000\u5bf9\u96ea\u5b9c\u5251\u7709\u4e00\u76b1\uff0c\u5fc3\u60f3\uff1a\u201c\u83ab\u975e\u8fd9\u8001\u5b66\u7a76\u8981\u8d70\u4e86\u5417\uff1f\u201d\u63a2\u624b\u5165\u6000\uff0c\u6b63\u5f85\u53d6\u51fa\u8336\u94b1\uff0c\u53ea\u89c1\u90a3\u84dd\u5e03\u886b\u8001\u4eba\u7adf\u662f\u76f4\u5411\u81ea\u5df1\u684c\u4e0a\u8d70\u8fc7\u6765\uff0c\u5f53\u4e0b\u8fde\u5fd9\u8d77\u8eab\u62b1\u62f3\uff0c\u9053\uff1a\u201c\u8001\u4e08\u2026\u2026\u201d \u3000\u3000\u90a3\u8001\u4eba\u4e00\u6325\u624b\uff0c\u559d\u9053\uff1a\u201c\u514d\u793c\uff0c\u5750\u4e0b\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u5f97\u4e00\u6014\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u8001\u513f\u597d\u5927\u7684\u67b6\u5b50\uff01\u201d\u4f46\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u8001\u4e08\u8bf7\u5148\u5750\u4e0b\u5427\uff01\u201d \u3000\u3000\u8001\u4eba\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5e9f\u8bdd\uff01\u201d\u4e00\u626c\u79c3\u9876\uff0c\u5c31\u5728\u65b9\u96ea\u5b9c\u8eab\u8fb9\u7684\u6728\u51f3\u4e2d\u5750\u4e0b\u6765\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8001\u4e08\u7a81\u7136\u79fb\u9a7e\u800c\u6765\uff0c\u4e0d\u77e5\u6709\u4f55\u89c1\u6559\u3002\u201d \u3000\u3000\u8001\u4eba\u76ee\u5149\u4e00\u95ea\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4f60\u8eab\u4e2d\u5267\u6bd2\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u5446\u9053\uff1a\u201c\u662f\u5417\uff1f\u521a\u624d\u90a3\u4f20\u97f3\u793a\u8b66\uff0c\u539f\u6765\u662f\u8001\u524d\u8f88\uff01\u201d \u3000\u3000\u8001\u4eba\u5ffd\u7136\u5927\u58f0\u9053\uff1a\u201c\u4ec0\u4e48\u8001\u524d\u8f88\uff0c\u5c0f\u524d\u8f88\uff0c\u6211\u5b8b\u8001\u90aa\u8ba8\u538c\u522b\u4eba\u5949\u627f\u2026\u2026\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u7a81\u5730\u51b7\u51b7\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u4e2d\u4e86\u5267\u6bd2\uff0c\u53ef\u662f\u4e00\u70b9\u90fd\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u5934\u9053\uff1a\u201c\u6ca1\u6709\u554a\uff01\u665a\u8f88\u9002\u624d\u5df2\u7136\u8fd0\u6c14\u9ed8\u5bdf\u5947\u7ecf\u516b\u8109\uff0c\u6839\u672c\u5c31\u4e0d\u66fe\u6709\u4e2d\u6bd2\u4e4b\u5146\u3002\u201d \u3000\u3000\u79c3\u9876\u8001\u4eba\u542c\u5f97\u4e00\u5f81\uff0c\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u9a97\u6211\u5b8b\u8001\u90aa\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u665a\u8f88\u5f88\u597d\u554a\uff0c\u81ea\u662f\u4e0d\u66fe\u54c4\u9a97\u4f60\u8001\u4e86\u3002\u201d \u3000\u3000\u79c3\u9876\u8001\u4eba\u4f3c\u4fe1\u975e\u4fe1\u5730\u9053\uff1a\u201c\u8fd9\u5c31\u6ca1\u4e86\u2026\u2026\u201d\u8bed\u97f3\u987f\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u90a3\u5e84\u6377\u660e\u660e\u5728\u9152\u4e2d\u4e0b\u4e86\u7edd\u6bd2\u4e4b\u7269\uff0c\u600e\u5730\u4f60\u4f1a\u4e0d\u66fe\u4e2d\u6bd2\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u8bf4\u4e43\u662f\u5e84\u6377\u5728\u9152\u4e2d\u4e0b\u6bd2\uff0c\u987f\u65f6\u5931\u58f0\u7b11\u9053\uff0c\u201c\u8001\u4e08\uff0c\u8fd9\u4e8b\u53ea\u6015\u6709\u4e86\u8bef\u4f1a\uff0c\u60f3\u90a3\u5e84\u6377\u2026\u2026\u201d\u8bdd\u97f3\u672a\u5df2\uff0c\u53ea\u89c1\u90a3\u5750\u5728\u95e8\u53e3\u7684\u4e09\u540d\u5927\u6c49\uff0c\u7a81\u7136\u4e00\u9f50\u8d70\u4e86\u8fc7\u6765\uff0c\u5bf9\u65b9\u96ea\u5b9c\u6253\u91cf\u4e86\u4e00\u773c\uff0c\u5176\u4e2d\u4e00\u4eba\u5927\u559d\u9053\uff1a\u201c\u4f60\u53ef\u662f\u59d3\u65b9\u3002\u201d\u65b9\u96ea\u5b9c\u76b1\u7709\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u533a\u533a\u6b63\u662f\u59d3\u65b9\u3002\u201d \u3000\u3000\u90a3\u4e09\u4e2a\u5927\u6c49\u4e00\u542c\u65b9\u96ea\u5b9c\u679c\u771f\u59d3\u65b9\uff0c\u7acb\u5373\u8138\u73b0\u6b22\u6109\u4e4b\u8272\uff0c\u540c\u65f6\u62d4\u51fa\u4e86\u80cc\u540e\u5355\u5200\uff0c\u7531\u5148\u8bf4\u8bdd\u7684\u90a3\u4eba\u53eb\u9053\uff1a\u201c\u59d3\u65b9\u7684\uff0c\u54b1\u4eec\u7b49\u4f60\u5f88\u4e45\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5947\u9053\uff1a\u201c\u65b9\u67d0\u4e0e\u4e09\u4f4d\u7d20\u4e0d\u76f8\u8bc6\uff0c\u4e09\u4f4d\u600e\u4f1a\u5728\u6b64\u7b49\u6211\u5f88\u4e45\uff1f\u201d \u3000\u3000\u90a3\u5927\u6c49\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u5149\u68cd\u773c\u91cc\u63c9\u4e0d\u8fdb\u7802\u5b50\uff0c\u4f60\u522b\u8ddf\u54b1\u4eec\u88c5\u849c\u5566\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8d8a\u542c\u8d8a\u7cca\u6d82\uff0c\u7b11\u9053\uff1a\u201c\u4e09\u4f4d\u53ef\u5426\u628a\u4e8b\u60c5\u8bf4\u660e\u767d\u5462\uff1f\u533a\u533a\u5012\u662f\u5982\u5760\u4e94\u91cc\u96fe\u4e2d\u2026\u2026\u201d \u3000\u3000\u90a3\u5927\u6c49\u5ffd\u7136\u4e00\u632f\u624b\u4e2d\u5355\u5200\uff0c\u559d\u9053\uff1a\u201c\u59d3\u65b9\u7684\uff0c\u4f60\u6b64\u523b\u5df2\u7136\u6bd2\u4fb5\u80ba\u8151\uff0c\u6b7b\u5728\u773c\u524d\uff0c\u54b1\u4eec\u4e09\u4f4d\u5c31\u662f\u5949\u4e86\u5b97\u5802\u4e3b\u4e4b\u547d\uff0c\u5230\u6b64\u7ed9\u4f60\u6536\u5c38\u6765\u7684\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u6697\u6697\u4e00\u60ca\uff0c\u76ee\u5149\u4e0d\u7531\u81ea\u4e3b\u5730\u4ece\u90a3\u8001\u4eba\u8138\u4e0a\u4e00\u8f6c\uff0c\u53ea\u89c1\u90a3\u8001\u4eba\u7adf\u7136\u95ed\u4e0a\u4e86\u53cc\u76ee\uff0c\u4e00\u526f\u88c5\u804b\u4f5c\u54d1\u7684\u795e\u6001\uff0c\u5fc3\u4e2d\u6697\u89c9\u4e0d\u5feb\uff0c\u8ba8\u9053\uff1a\u4f60\u4ee5\u4e3a\u6211\u5bf9\u4ed8\u4e0d\u4e86\u4ed6\u4eec\u5417\uff1f\u6211\u5012\u8981\u53eb\u4f60\u77a7\u77a7\u65b9\u67d0\u624b\u6bb5\u3002 \u3000\u3000\u5f53\u4e0b\u7adf\u7136\u653e\u58f0\u4e00\u7b11\uff0c\u5411\u90a3\u5927\u6c49\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u4eec\u662f\u5949\u90a3\u5b97\u8d35\u4e4b\u547d\u800c\u6765\u7684\u4e86\u3002\u201d \u3000\u3000\u90a3\u540d\u5927\u6c49\u4e00\u632f\u624b\u4e2d\u5355\u5200\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u662f\u53c8\u5982\u4f55\uff01\u59d3\u65b9\u7684\uff0c\u4f60\u7eb3\u547d\u5427\uff01\u201d\u5bd2\u5149\u4e00\u95ea\uff0c\u4e3e\u5200\u5c31\u5288\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u8fd9\u5b97\u8d35\u7684\u624b\u4e0b\uff0c\u600e\u4e48\u5168\u662f\u4e00\u4e9b\u83bd\u649e\u5192\u5931\u4e4b\u5f92\uff1f\u770b\u6765\u4ed6\u4eec\u5728\u8fd9\u5ddd\u897f\u4e00\u5e26\uff0c\u771f\u4e2a\u662f\u62ff\u4eba\u547d\u5f53\u4f5c\u513f\u620f\u4e00\u822c\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8f6c\u5ff5\u4e4b\u95f4\uff0c\u6740\u673a\u6697\u8d77\uff0c\u53f3\u624b\u4e00\u62ac\uff0c\u8fc5\u5feb\u7684\u5411\u90a3\u62d4\u5200\u5927\u6c49\u7684\u624b\u8155\u7ffb\u7f20\u8fc7\u53bb\u3002 \u3000\u3000\u90a3\u540d\u5927\u6c49\u6ee1\u5fc3\u4ee5\u4e3a\u4e00\u5200\u5c31\u53ef\u5c06\u8eab\u4e2d\u5de8\u6bd2\u7684\u65b9\u96ea\u5b9c\u89e3\u51b3\uff0c\u662f\u4ee5\u5200\u52bf\u5288\u51fa\uff0c\u4e00\u8138\u4ff1\u662f\u5fd7\u5f97\u610f\u6ee1\u7684\u795e\u60c5\uff0c\u773c\u770b\u5200\u950b\u5df2\u53ca\u65b9\u96ea\u5b9c\uff0c\u90a3\u5927\u6c49\u9661\u611f\u8155\u8109\u4e00\u9707\uff0c\u624b\u4e2d\u4e00\u8f7b\uff0c\u5355\u5200\u5df2\u8dcc\u843d\u5728\u684c\u9762\u4e0a\u3002 \u3000\u3000\u53ea\u542c\u201c\u5494\u5f53\u201d\u4e00\u58f0\uff0c\u5200\u67c4\u7838\u788e\u4e86\u684c\u4e0a\u7684\u74f7\u58f6\uff0c\u7834\u7247\u3001\u8336\u6c34\uff0c\u6e85\u7684\u65b9\u96ea\u5b9c\u548c\u90a3\u8001\u4eba\u4e00\u8eab\u3002\u90a3\u5927\u6c49\u60ca\u547c\u4e00\u58f0\uff0c\u8f6c\u8eab\u8fde\u8fde\u9000\u4e86\u4e09\u6b65\uff0c\u8131\u53e3\u9053\uff1a\u201c\u83ab\u975e\u4f60\u5e76\u672a\u4e2d\u6bd2\uff1f\u201d \u3000\u3000\u53e6\u5916\u4e24\u540d\u6301\u5200\u5927\u6c49\uff0c\u4e92\u65bd\u773c\u8272\uff0c\u5ffd\u7136\u540c\u65f6\u4e3e\u5200\uff0c\u4e00\u5de6\u4e00\u53f3\uff0c\u6253\u4e24\u4fa7\u4e0d\u8a00\u4e0d\u54cd\u76f4\u5411\u65b9\u96ea\u5b9c\u6b3a\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u51b7\u7b11\u4e00\u58f0\uff0c\u53ea\u5f53\u6ca1\u6709\u77a7\u89c1\uff0c\u6307\u7740\u5931\u5200\u7684\u6c49\u5b50\u559d\u9053\uff1a\u201c\u65b9\u67d0\u7eb5\u7136\u4e2d\u6bd2\uff0c\u5c31\u51ed\u4f60\u4eec\u4e09\u4e2a\u8d27\u8272\uff0c\u5927\u6982\u8fd8\u5948\u4f55\u4e0d\u4e86\u6211\u2026\u2026\u201d \u3000\u3000\u8bed\u97f3\u672a\u7edd\uff0c\u7a81\u7136\u53cc\u80a9\u4e00\u632f\uff0c\u4eae\u638c\u5411\u5de6\u53f3\u6a2a\u51fb\u800c\u53bb\u3002 \u3000\u3000\u90a3\u4e24\u540d\u4e3e\u5200\u6b3a\u8eab\u800c\u6765\u7684\u5927\u6c49\uff0c\u53ea\u89c9\u80f8\u5934\u4e00\u9707\uff0c\u5982\u906d\u5343\u65a4\u91cd\u51fb\uff0c\u95f7\u54fc\u4e00\u58f0\uff0c\u64a4\u624b\u4e22\u5200\u5f80\u540e\u683d\u5012\u5728\u5730\u3002\u5634\u89d2\u542b\u8840\uff0c\u53cc\u76ee\u7a81\u51fa\uff0c\u663e\u7136\u65b9\u96ea\u8fd9\u4e14\u542b\u6012\u51fa\u624b\uff0c\u4e00\u638c\u4e4b\u4e0b\uff0c\u5df2\u5c06\u8fd9\u4e24\u540d\u6c49\u5b50\u51fb\u6b7b\u5f53\u573a\u3002 \u3000\u3000\u672c\u662f\u95ed\u76ee\u800c\u5750\u7684\u8001\u4eba\uff0c\u5ffd\u7136\u4e24\u773c\u4e00\u7741\uff0c\u5927\u7b11\u9053\uff1a\u201c\u597d\u5f3a\u7684\u638c\u529b\uff0c\u5c0f\u5144\u53f0\uff0c\u6253\u6b7b\u4eba\u53ef\u4e0d\u662f\u95f9\u7740\u73a9\u7684\uff0c\u8fd9\u5269\u4e0b\u7684\u4e00\u4e2a\uff0c\u4f60\u6253\u7b97\u600e\u4e48\u529e\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5e76\u672a\u6599\u5230\uff0c\u81ea\u5df1\u8fd9\u4e00\u638c\uff0c\u7adf\u7136\u9707\u4e24\u4e86\u4e24\u540d\u58ee\u6c49\uff0c\u5fc3\u4e2d\u4e5f\u4e0d\u7981\u5446\u4e86\u4e00\u5446\uff0c\u6697\u5fd6\u9053\uff1a\u201c\u6211\u5e76\u4e0d\u60f3\u6740\u6b7b\u4ed6\u4eec\u554a\uff01\u4e3a\u4ec0\u4e48\u8fd9\u4e48\u8f7b\u8f7b\u7684\u4e00\u638c\uff0c\u4ed6\u4eec\u5c31\u627f\u53d7\u4e0d\u4e86\u5462\uff1f \u3000\u3000\u65b9\u96ea\u5b9c\u6709\u8fd9\u7b49\u60f3\u6cd5\uff0c\u5728\u4ed6\u800c\u8a00\uff0c\u672c\u662f\u5408\u60c5\u5408\u7406\u4e4b\u601d\uff0c\u4ed6\u751f\u5177\u81f3\u6027\uff0c\u8fd9\u6b21\u6325\u638c\u4e4b\u95f4\uff0c\u7adf\u8fde\u51fb\u4e24\u4eba\uff0c\u4e0d\u4f46\u4f7f\u4ed6\u5bf9\u81ea\u5df1\u4e00\u8eab\u529f\u529b\u6709\u76f8\u5f53\u7684\u4e86\u89e3\uff0c\u4e5f\u4f7f\u4ed6\u9886\u609f\u4e86\u4e00\u79cd\u975e\u5e38\u91cd\u8981\u7684\u6c5f\u6e56\u7ecf\u9a8c\uff0c\u5bf9\u654c\u4e4b\u9645\uff0c\u5982\u662f\u4f60\u4f24\u4e0d\u4e86\u5bf9\u65b9\uff0c\u90a3\u5c31\u662f\u5bf9\u65b9\u4f24\u4e86\u4f60\u3002\u5fc3\u5ff5\u8f6c\u52a8\uff0c\u4e0d\u7981\u9887\u4e3a\u6b49\u759a\u7684\u770b\u4e86\u6a2a\u5c38\u5730\u4e0a\u7684\u4e24\u540d\u5927\u6c49\u4e00\u773c\uff0c\u4f4e\u58f0\u5e94\u9053\uff1a\u201c\u4f9d\u8001\u4e08\u4e4b\u89c1\u5462\uff1f\u201d \u3000\u3000\u8001\u4eba\u4e00\u7b11\u9053\uff1a\u201c\u65a9\u8349\u9664\u6839\u53ef\u7edd\u540e\u60a3\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u76ee\u5149\u8f6c\u5230\u90a3\u5931\u5200\u5927\u6c49\u8eab\u4e0a\uff0c\u7a0d\u4e00\u6c89\u541f\uff0c\u6447\u5934\u9053\uff1a\u201c\u4e0d\u5fc5\u4e86\uff0c\u4ed6\u4eec\u4e43\u662f\u53d7\u4eba\u6307\u4f7f\u4e4b\u8f88\uff0c\u6740\u4e4b\u4e0d\u6b66\u3002\u201d \u3000\u3000\u7ad9\u8d77\u8eab\u5b50\uff0c\u76f4\u5411\u90a3\u5927\u6c49\u884c\u53bb\uff0c\u53e3\u4e2d\u63a5\u9053\uff1a\u201c\u65b9\u67d0\u4e00\u65f6\u5931\u624b\uff0c\u6bd9\u4e86\u4f60\u4e24\u540d\u4f19\u4f34\uff0c\u5fc3\u4e2d\u751a\u611f\u4e0d\u5b89\uff0c\u8fd9\u91cc\u6709\u9ec4\u91d1\u4e00\u952d\uff0c\u4f60\u53ef\u62ff\u53bb\u66ff\u4ed6\u4eec\u529e\u7406\u540e\u4e8b\u2026\u2026\u201d\u4f38\u624b\u638f\u51fa\u4e00\u952d\u9ec4\u91d1\uff0c\u9012\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u90a3\u5927\u6c49\u6218\u6218\u5162\u5162\u5730\u63a5\u8fc7\u9ec4\u91d1\uff0c\u8dea\u5012\u5730\u4e0a\uff0c\u8fde\u8fde\u53e9\u5934\u9053\uff1a\u201c\u591a\u8c22\u65b9\u516c\u5b50\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u4e0d\u5fc5\u8c22\u6211\u4e86\uff0c\u65b9\u67d0\u5df2\u65e0\u6740\u4f60\u4e4b\u5fc3\uff0c\u81f3\u4e8e\u4f60\u56de\u5609\u5b9a\u600e\u4e48\u5411\u5b97\u8d35\u56de\u62a5\uff0c\u6211\u4e5f\u4e0d\u4f1a\u89c1\u602a\u4e8e\u4f60\u3002\u201d \u3000\u3000\u8f6c\u8eab\u770b\u4e86\u90a3\u8001\u4eba\u4e00\u773c\uff0c\u63a5\u9053\uff1a\u201c\u8001\u4e08\u6307\u70b9\u4e4b\u8bf7\uff0c\u665a\u751f\u6781\u4e3a\u611f\u8c22\uff0c\u4ed6\u65e5\u6709\u7f18\uff0c\u518d\u884c\u56fe\u62a5\u2026\u2026\u201d \u3000\u3000\u7565\u4e00\u62b1\u62f3\uff0c\u4e22\u4e86\u4e00\u952d\u767d\u94f6\u5b50\u7ed9\u5e97\u5bb6\uff0c\u5927\u6b65\u51fa\u95e8\u800c\u53bb\u3002 \u3000\u3000\u90a3\u8001\u4eba\u539f\u60f3\u65b9\u96ea\u5b9c\u4f1a\u7ed9\u81ea\u5df1\u89e3\u8bf4\u8bef\u4f1a\u5e84\u6377\u4e4b\u4e8b\uff0c\u4e0d\u6599\u65b9\u96ea\u5b9c\u5c45\u7136\u5c31\u6b64\u8d77\u8eab\u51fa\u95e8\uff0c\u4e00\u76b1\u7709\u5934\uff0c\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u8ba8\u5e86\u6211\u8001\u4eba\u5bb6\uff0c\u6211\u8001\u4eba\u5bb6\u975e\u8ddf\u5b9a\u4f60\u4e0d\u53ef\u3002\u201d\u8d77\u8eab\u6253\u8336\u94fa\u5b50\u540e\u9762\u6e9c\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4e14\u8bf4\u65b9\u96ea\u5b9c\u51fa\u5e97\u4ee5\u540e\uff0c\u4e00\u50ac\u5750\u9a91\u3002\u6cbf\u7740\u5b98\u9053\uff0c\u76f4\u5f80\u6210\u90fd\u65b9\u5411\u517c\u7a0b\u75be\u8fdb\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u8def\u884c\u6765\uff0c\u4ff1\u662f\u5b98\u9053\uff0c\u6653\u884c\u591c\u5bbf\uff0c\u5012\u4e5f\u65e0\u751a\u4e8b\u6545\u3002 \u3000\u3000\u7b2c\u56db\u5929\u5348\u540e\uff0c\u6765\u81f3\u4e00\u5904\u4e09\u5c94\u8def\u53e3\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u52d2\u9a6c\u7f30\uff0c\u6d41\u76ee\u56db\u987e\uff0c\u53ea\u89c9\u773c\u524d\u7684\u666f\u8272\u5341\u5206\u719f\u6089\uff0c\u6b63\u662f\u6614\u5e74\u5728\u8a00\u51e4\u537f\u966a\u540c\u4e0b\uff0c\u8ffd\u5bfb\u4e2d\u5dde\u4e09\u4fa0\u65f6\u8ff7\u9014\uff0c\u7ed3\u679c\u5faa\u9a6c\u8e44\u75d5\u8e2a\uff0c\u624d\u67e5\u51fa\u4e86\u9b54\u5200\u7530\u8fdc\u4f4f\u5904\u7684\u5b98\u9053\u3002 \u3000\u3000\u4ed6\u7565\u4e00\u6253\u91cf\u53bb\u8def\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5ffd\u7136\u6709\u4e86\u518d\u53bb\u627e\u90a3\u9b54\u5200\u7530\u8fdc\u7684\u5ff5\u5934\u3002 \u3000\u3000\u539f\u6765\uff0c\u4ed6\u89c9\u7740\u5f53\u5e74\u80dc\u4e86\u7530\u8fdc\uff0c\u53ef\u8bf4\u5168\u662f\u5728\u6c14\u52bf\u4e4b\u4e0a\u9a97\u4eba\uff0c\u8feb\u5f97\u7530\u8fdc\u8ba4\u8f93\u670d\u8d25\u3002\u8fd9\u4e00\u56de\uff0c\u4ed6\u53ef\u662f\u60f3\u51ed\u7740\u4e09\u5e74\u7684\u82e6\u5b66\u800c\u6765\u7684\u771f\u5b9e\u5de5\u592b\uff0c\u6597\u6597\u90a3\u9b54\u5200\u7530\u8fdc\uff0c\u4e5f\u597d\u8bd5\u51fa\u81ea\u5df1\u76ee\u524d\u5728\u6b66\u6797\u4e4b\u4e2d\uff0c\u7a76\u7adf\u7b97\u5f97\u4e0a\u662f\u51e0\u6d41\u89d2\u8272\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u7b49\u5ff5\u5934\u4e00\u8d77\uff0c\u8eab\u4e0d\u7531\u4e3b\u5730\u4e00\u987a\u4e1d\u7f30\uff0c\u659c\u523a\u91cc\u62c9\u9a6c\u5411\u5de6\u6d4b\u5c0f\u5f84\u884c\u53bb\u3002\u4e00\u8def\u666f\u8272\uff0c\u4e86\u65e0\u53d8\u5f02\uff0c\u751a\u81f3\u8fde\u5f53\u5e74\u4e2d\u5dde\u4e09\u4fa0\u7ad6\u7acb\u5728\u8349\u4ece\u4e2d\u7684\u6728\u724c\uff0c\u867d\u7136\u5df2\u7ecf\u88ab\u98ce\u96e8\u5265\u8680\u7684\u7834\u65e7\u8150\u70c2\uff0c\u4f46\u5374\u63d2\u5728\u539f\u5730\uff0c\u672a\u66fe\u62d4\u8d70\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u8dc3\u4e0b\u9a6c\u6765\uff0c\u987a\u7740\u90a3\u6761\u5c71\u8def\uff0c\u7275\u9a6c\u75be\u884c\u3002 \u3000\u3000\u76cf\u8336\u4e0d\u5230\uff0c\u4e1a\u5df2\u884c\u62b5\u90a3\u4e00\u5806\u4e71\u77f3\u9752\u8349\u4e4b\u4e2d\uff0c\u8fd9\u6b63\u662f\u5f53\u5e74\u4ed6\u548c\u8a00\u51e4\u537f\u9047\u5230\u7530\u8fdc\u624b\u4e0b\u559d\u963b\u4e4b\u5904\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u60c5\u4e0d\u81ea\u7981\u7684\u505c\u4e86\u4e0b\u6765\uff0c\u4e3e\u76ee\u5411\u56db\u5468\u671b\u53bb\u3002 \u3000\u3000\u4f46\u89c1\u8352\u8349\u8513\u8513\uff0c\u6c14\u6c1b\u9634\u6c89\u3002\u4f11\u8bf4\u4e0d\u95fb\u4eba\u58f0\u9e1f\u8bed\uff0c\u51e0\u4e4e\u8fde\u5730\u4e0a\u7684\u866b\u58f0\uff0c\u90fd\u96be\u5f97\u4e00\u89c1\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u8038\u4e86\u8038\u5251\u76fe\uff0c\u6697\u9053\uff1a\u201c\u83ab\u975e\u90a3\u9b54\u5200\u7530\u8fdc\uff0c\u5df2\u7136\u5f03\u53bb\u4e86\u8fd9\u4e2a\u6240\u5728\u5417\uff1f\u201d\u5bfb\u601d\u4e4b\u95f4\uff0c\u4e0d\u89c9\u5730\u4e3e\u6b65\u800c\u884c\u3002 \u3000\u3000\u4ed6\u4eec\u7a00\u8fa8\u8ba4\u51fa\u5f53\u5e74\u7684\u8def\u5f84\uff0c\u4e0d\u4e00\u4f1a\u5de5\u592b\uff0c\u679c\u7136\u89c1\u5230\u4e86\u90a3\u5e62\u8305\u820d\uff0c\u63a9\u6620\u5728\u6742\u8349\u6df1\u6797\u4e4b\u4e2d\u3002 \u3000\u3000\u4ed6\u5de6\u624b\u7d27\u62c9\u7740\u9a8f\u9a6c\uff0c\u53f3\u624b\u4e00\u62ac\uff0c\u6b63\u5f85\u5411\u90a3\u67f4\u95e8\u6263\u53bb\u3002 \u3000\u3000\u7a81\u7136\uff0c\u4e00\u58f0\u4f4e\u559d\uff0c\u6253\u5de6\u4fa7\u4f20\u6765\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff0c\u7adf\u6562\u64c5\u95ef\u672c\u6559\u91cd\u5730\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fae\u5fae\u4e00\u60ca\uff0c\u8f6c\u5934\u671b\u53bb\uff0c\u53ea\u89c1\u4e00\u4f4d\u8eab\u9ad8\u51e0\u8fbe\u4e08\u8bb8\u7684\u9ed1\u8863\u52b2\u88c5\u5927\u6c49\uff0c\u5bd2\u7740\u8138\u7ad9\u5728\u4e0d\u8fdc\u4e4b\u5904\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5728\u4e0b\u7279\u6765\u63a2\u671b\u4e00\u4f4d\u65e7\u8bc6\u9b54\u5200\u7530\u8fdc\uff0c\u7530\u603b\u74e2\u628a\u5b50\u3002\u201d \u3000\u3000\u90a3\u94c1\u5854\u822c\u7684\u5927\u6c49\uff0c\u6df1\u7709\u4e00\u626c\uff0c\u4f3c\u662f\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u662f\u90a3\u7530\u8fdc\u7684\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u8ba8\u9053\uff1a\u201c\u542c\u6b64\u4eba\u53e3\u6c14\uff0c\u4f3c\u662f\u7530\u8fdc\u4ecd\u5728\u6b64\u95f4\uff0c\u53ea\u4e0d\u77e5\u4ed6\u53e3\u4e2d\u6240\u79f0\u672c\u6559\uff0c\u53c8\u662f\u4e00\u4e2a\u4ec0\u4e48\u7ec4\u6210\uff0c\u6211\u4f55\u4e0d\u7ed9\u4ed6\u4e00\u4e2a\u83ab\u6d4b\u9ad8\u6df1\uff0c\u4e5f\u8bb8\u4f1a\u67e5\u51fa\u4e00\u70b9\u610f\u5916\u7684\u76fe\u76ee\u3002\u201d\u5fc3\u5ff5\u8f6c\u52a8\uff0c\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u89c1\u7740\u90a3\u7530\u8fdc\u4e4b\u65f6\uff0c\u4ed6\u81ea\u4f1a\u77e5\u9053\u5728\u4e0b\u662f\u8c01\uff01\u201d \u3000\u3000\u5927\u6c49\u95fb\u8a00\uff0c\u5ffd\u7136\u51b7\u51b7\u5730\u770b\u4e86\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u9053\uff1a\u201c\u90a3\u7530\u8fdc\u5df2\u662f\u672c\u6559\u7684\u4e00\u4f4d\u62a4\u6cd5\uff0c\u5982\u679c\u4f60\u4e0d\u80af\u62a5\u51fa\u59d3\u540d\uff0c\u60f3\u89c1\u672c\u6559\u62a4\u6cd5\uff0c\u90a3\u53ef\u662f\u5728\u505a\u68a6\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u4e00\u60ca\uff0c\u6697\u9053\uff1a\u201c\u679c\u7136\u8fd9\u5ddd\u897f\u5730\u9762\uff0c\u53c8\u6709\u65b0\u7684\u6b66\u6797\u4eba\u7269\u5d1b\u8d77\u4e86\u3002\u201d\u53e3\u4e2d\u5374\u8bf4\u9053\uff1a\u201c\u5982\u662f\u5728\u4e0b\u575a\u6301\u4e0d\u8bf4\u59d3\u540d\uff0c\u83ab\u975e\u5c31\u771f\u7684\u65e0\u6cd5\u89c1\u5230\u8d35\u6559\u62a4\u6cd5\u5417\uff1f\u201d \u3000\u3000\u5927\u6c49\u51b7\u7b11\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6446\u51fa\u4e00\u526f\u5947\u5f02\u4e4b\u8272\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8fd9\u5c31\u602a\u4e86\uff0c\u8fd9\u5ddd\u897f\u5730\u5e26\u51e0\u65f6\u6709\u8fc7\u8fd9\u7b49\u6559\u6d3e\uff1f \u3000\u3000\u60f3\u90a3\u7530\u8fdc\u4e0e\u6211\u4e43\u662f\u5fd8\u5e74\u4e4b\u4ea4\uff0c\u6bcf\u6b21\u76f8\u89c1\uff0c\u4ece\u4e0d\u8981\u6211\u901a\u540d\u2026\u2026\u201d \u3000\u3000\u90a3\u5927\u6c49\u9661\u5730\u559d\u9053\uff1a\u201c\u4f60\u80e1\u8bf4\u516b\u9053\u4e9b\u4ec0\u4e48\uff1f\u987b\u77e5\u6211\u56db\u7075\u6559\u4e2d\u7684\u89c4\u77e9\u6781\u4e25\uff0c\u7eb5\u662f\u6559\u4e3b\u672c\u4eba\uff0c\u4e5f\u5f97\u9075\u884c\u4e0d\u6e1d\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u5f97\u5fc3\u4e2d\u6697\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u56db\u7075\u6559\uff1f\u4f46\u4e0d\u77e5\u90a3\u6559\u4e3b\u53c8\u662f\u4f55\u4eba\uff1f\u6211\u5012\u662f\u8be5\u60f3\u4e2a\u6cd5\u5b50\uff0c\u591a\u5957\u4ed6\u4e00\u4e9b\u8bdd\u624d\u597d\u3002\u201d\u5f53\u4e0b\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5373\u662f\u8d35\u6559\u89c4\u77e9\u5982\u6b64\uff0c\u70e6\u8bf7\u9601\u4e0b\u4ee3\u4e3a\u901a\u62a5\u7530\u62a4\u6cd5\uff0c\u5c31\u8bf4\u533a\u533a\u65b9\u96ea\u5b9c\u5728\u6b64\u6c42\u89c1\u3002\u201d \u3000\u3000\u4ed6\u77e5\u9053\u81ea\u5df1\u7684\u59d3\u540d\uff0c\u6b66\u6797\u4e2d\u5c1a\u4e0d\u89c1\u7ecf\u4f20\uff0c\u8bf4\u4e86\u51fa\u6765\uff0c\u4e5f\u4e0d\u4f1a\u6709\u4ec0\u4e48\u4e0d\u59a5\uff0c\u662f\u4ee5\u4ed6\u4e5f\u5c31\u4e0d\u5fc5\u9690\u7792\u4e86\u3002 \u3000\u3000\u90a3\u5927\u6c49\u563f\u563f\u4e00\u7b11\uff1a\u9053\uff1a\u201c\u4f60\u8981\u6211\u66ff\u4f60\u901a\u62a5\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u6709\u52b3\u5c0a\u9a7e\u4e86\u3002\u201d \u3000\u3000\u90a3\u5927\u6c49\u4e00\u7741\u90a3\u94dc\u73af\u822c\u7684\u53cc\u773c\uff0c\u4ef0\u5929\u5927\u7b11\u9053\uff1a\u201c\u4f60\u597d\u5927\u7684\u53e3\u6c14\u554a\uff01\u201d\u7b11\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a \u3000\u3000\u201c\u5c0f\u5b50\uff0c\u4f60\u77e5\u9053\u672c\u5ea7\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5c1a\u672a\u8bf7\u6559\uff01\u201d \u3000\u3000\u90a3\u5927\u6c49\u9053\uff1a\u201c\u672c\u5ea7\u4e43\u662f\u56db\u7075\u6559\u4e2d\u7684\u56db\u7075\u4e4b\u9996\uff0c\u9664\u4e86\u6559\u4e3b\uff0c\u5c31\u4ee5\u672c\u5ea7\u4e3a\u5c0a\uff0c\u4f60\u5c45\u7136\u8981\u672c\u5ea7\u66ff\u4f60\u901a\u62a5\uff0c\u5c82\u975e\u4ee4\u4eba\u53ef\u7b11\u5417\uff1f\u201d \u3000\u3000\u8bed\u97f3\u672a\u5df2\uff0c\u53c8\u662f\u5927\u7b11\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e0d\u7981\u4e00\u5446\uff0c\u6697\u9053\uff1a\u201c\u539f\u6765\u4ed6\u6bd4\u7530\u8fdc\u5730\u4f4d\u9ad8\u51fa\u4e0d\u5c11\uff0c\u6bcb\u602a\u4ed6\u53e3\u4e2d\u5bf9\u9b54\u5200\u7530\u8fdc\u6beb\u65e0\u5c0a\u656c\u4e4b\u610f\u5462\uff01\u201d\u4ed6\u5fc3\u4e2d\u867d\u60f3\u7740\u8fd9\u5927\u6c49\u7684\u8eab\u4efd\uff0c\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u5c0a\u9a7e\u539f\u6765\u662f\u56db\u7075\u4e4b\u9996\uff0c\u533a\u533a\u5931\u656c\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u5c0a\u9a7e\u600e\u4e48\u79f0\u547c\uff1f\u201d \u3000\u3000\u5927\u6c49\u9053\uff1a\u201c\u672c\u5ea7\u59d3\u9ad8\uff0c\u5927\u540d\u65e0\u654c\uff0c\u6b66\u6797\u670b\u53cb\u5c0a\u5949\u672c\u5ea7\u662f\u201c\u795e\u529b\u5929\u738b\u201d\uff0c\u5c0f\u5b50\uff0c\u4f60\u542c\u8fc7\u6ca1\u6709\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u7b11\u9053\uff1a\u201c\u8fd9\u4eba\u83ab\u975e\u6709\u4e9b\u6d51\u61a8\u5417\uff1f\u201d\u53e3\u4e2d\u5e94\u9053\uff1a\u201c\u533a\u533a\u4e45\u4ef0\u5927\u540d\uff0c\u4eca\u65e5\u4e00\u89c1\uff0c\u5b9e\u662f\u4e09\u751f\u6709\u5e78\u2026\u2026\u201d \u3000\u3000\u90a3\u9ad8\u65e0\u654c\u663e\u7136\u88ab\u65b9\u96ea\u5b9c\u6367\u5f97\u5341\u5206\u5f00\u5fc3\uff0c\u6717\u58f0\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u4e0d\u6562\u2026\u2026\u4f60\u8001\u5f1f\u8981\u89c1\u90a3\u7530\u8fdc\uff0c\u6709\u4ec0\u4e48\u5927\u4e8b\u6ca1\u6709\uff1f\u201d \u3000\u3000\u65b9\u59bb\u5b9c\u6b64\u523b\u5df2\u7136\u786e\u4fe1\uff0c\u8fd9\u9ad8\u65e0\u6545\u4f3c\u662f\u4e00\u4f4d\u4e0d\u901a\u5fc3\u673a\u7684\u4eba\uff0c\u5f53\u4e0b\u4e00\u7b11\u9053\uff1a\u201c\u533a\u533a\u4e0e\u4ed6\u4e43\u662f\u65e7\u8bc6\uff0c\u7279\u6765\u62dc\u671b\u4e8e\u4ed6\uff0c\u4e0d\u77e5\u9ad8\u5929\u738b\u53ef\u5426\u4f20\u4eba\u53eb\u4ed6\u4e00\u58f0\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u77a7\u4e86\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u4eca\u5e74\u591a\u5927\u5e74\u7eaa\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u533a\u533a\u884c\u5e74\u5341\u4e5d\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u5341\u4e5d\u5c81\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u5ffd\u7136\u6447\u5934\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u5728\u9a97\u4eba\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u867d\u7136\u89c9\u51fa\u4ed6\u8bf4\u7684\u6709\u4e9b\u5947\u602a\uff0c\u4f46\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u533a\u533a\u8bf4\u7684\u53e5\u53e5\u5b9e\u60c5\uff0c\u9ad8\u5929\u738b\u4e3a\u4f55\u4e0d\u4fe1\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u7a81\u9053\uff1a\u201c\u60f3\u90a3\u9b54\u5200\u7530\u8fdc\uff0c\u5728\u7eff\u6797\u9053\u4e0a\uff0c\u4e43\u662f\u5ddd\u6ec7\u4e00\u5e26\u7684\u9996\u5ea7\uff0c\u4ed6\u600e\u4f1a\u6709\u4f60\u8fd9\u7b49\u5e74\u8f7b\u7684\u670b\u53cb\u3002\u8001\u5f1f\u8fd9\u4e0d\u662f\u9a97\u4eba\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u539f\u6765\u4ed6\u4e0d\u76f8\u4fe1\u6211\u8ba4\u8bc6\u7530\u8fdc\u2014\u2014\u201d\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u9ad8\u5929\u738b\u5982\u662f\u4e0d\u4fe1\uff0c\u4f55\u4e0d\u53eb\u90a3\u7530\u8fdc\u524d\u6765\u4e00\u89c1\uff1f\u201d \u3000\u3000\u9ad8\u65e0\u654c\u60f3\u4e86\u4e00\u60f3\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u672c\u5ea7\u4f55\u5fc5\u4e3a\u8fd9\u7b49\u4e8b\u70e6\u5fc3\u5462\uff1f\u4f60\u8001\u5f1f\u8bf4\u7684\u5bf9\uff0c\u53eb\u4ed6\u51fa\u6765\u4e00\u89c1\u5c31\u660e\u767d\u7684\u4e86\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u770b\u4e86\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u5411\u4ed6\u7684\u5c0f\u53ae\u9053\uff1a\u201c\u53bb\u53eb\u90a3\u7530\u62a4\u6cd5\u6765\u6b64\u89c1\u6211\u3002\u201d \u3000\u3000\u90a3\u540d\u5c0f\u53ae\uff0c\u770b\u4e0a\u53bb\u5341\u5206\u6e05\u79c0\uff0c\u95fb\u8a00\u6e9c\u4e86\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u8f6c\u8eab\u8d70\u8fdb\u4e86\u8305\u820d\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u6697\u6697\u4e00\u9707\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u8305\u820d\u7684\u56db\u5468\uff0c\u53ea\u6015\u85cf\u6709\u4e0d\u5c11\u6697\u6869\uff0c\u600e\u5730\u81ea\u5df1\u4e00\u8def\u884c\u6765\uff0c\u7adf\u7136\u672a\u66fe\u53d1\u73b0\u3002\u201d\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u542c\u5f97\u9ad8\u65e0\u654c\u5927\u58f0\u9053\uff1a\u201c\u65b9\u8001\u5f1f\uff0c\u4f60\u4e5f\u662f\u6b66\u6797\u4e2d\u4eba\u5417\uff1f\u201d \u3000\u3000\u6b64\u4eba\u5ffd\u51fa\u6b64\u8a00\uff0c\u5012\u662f\u628a\u65b9\u96ea\u5b9c\u95ee\u5f97\u6014\u4f4f\u4e86\uff0c\u4ed6\u731c\u4e0d\u900f\u9ad8\u65e0\u654c\u8fd9\u79cd\u7528\u5fc3\u4f55\u5728\uff0c\u53ea\u597d\u542b\u7cca\u5e94\u9053\uff1a\u201c\u533a\u533a\u53ea\u4e0d\u8fc7\u5b66\u4e86\u51e0\u624b\u5f3a\u8eab\u58ee\u4f53\u7684\u5e84\u7a3c\u628a\u5f0f\uff0c\u7b97\u4e0d\u5f97\u6b66\u6797\u4eba\u7269\u3002\u201d \u3000\u3000\u90a3\u9ad8\u65e0\u654c\u6709\u4e9b\u76f8\u4fe1\uff0c\u4f46\u5374\u53c8\u627e\u4e0d\u51fa\u4ec0\u4e48\u8bdd\u53ef\u4ee5\u53cd\u95ee\uff0c\u7adf\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u80fd\u8ddf\u9b54\u5200\u7530\u8fdc\u76f8\u4ea4\uff0c\u672c\u5ea7\u770b\u5f97\u51fa\u4f60\u8001\u5f1f\u5c06\u6765\u5fc5\u7136\u5927\u6709\u51fa\u606f\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u8bdd\uff0c\u96be\u9053\u4e0d\u8ddf\u7530\u8fdc\u4ea4\u5f80\uff0c\u5c31\u6ca1\u6709\u51fa\u606f\u4e86\u5417\uff1f\u770b\u6765\u8fd9\u56db\u7075\u6559\u4e5f\u8bb8\u662f\u4e2a\u6781\u6709\u91ce\u5fc3\u7684\u7ec4\u7ec7\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5929\u8d4b\u6781\u9ad8\uff0c\u662f\u4ee5\u9ad8\u65e0\u654c\u8fd9\u7b49\u65e0\u610f\u4e2d\u6f0f\u51fa\u7684\u8bdd\u5934\uff0c\u5374\u4f7f\u4ed6\u654f\u611f\u5230\u6b64\u4eba\u8bdd\u4e2d\u5fc5\u6709\u6240\u6307\uff0c\u56e0\u6b64\uff0c\u4ed6\u53c8\u5174\u8d77\u4e86\u591a\u5957\u5bf9\u65b9\u53e3\u98ce\u7684\u4e3b\u610f\u3002 \u3000\u3000\u5f53\u4e0b\u7b11\u9053\uff1a\u201c\u533a\u533a\u5e74\u5e7c\u65e0\u77e5\uff0c\u4eca\u540e\u5c1a\u7948\u9ad8\u5929\u738b\u591a\u591a\u63d0\u62d4\u624d\u597d\u3002\u201d \u3000\u3000\u9ad8\u65e0\u6545\u542c\u5f97\u5927\u4e3a\u53d7\u7528\uff0c\u7b11\u9053\uff1a\u201c\u54ea\u91cc\uff0c\u54ea\u91cc\uff0c\u529e\u8981\u672c\u5ea7\u4e00\u53e5\u8bdd\uff0c\u4f60\u65b9\u8001\u5f1f\u5b9a\u53ef\u5927\u5927\u51fa\u4eba\u5934\u5730\u2026\u2026\u201d \u3000\u3000\u8bdd\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u4f3c\u662f\u60f3\u8d77\u4e86\u4e00\u4ef6\u5927\u4e8b\u822c\uff0c\u63a5\u9053\uff1a\u201c\u8001\u5f1f\u4f60\u662f\u4ec0\u4e48\u95e8\u4e0b\uff0c\u5982\u679c\u672c\u5ea7\u5c06\u4f60\u5f15\u5165\u56db\u7075\u6559\u4e2d\uff0c\u4f60\u7684\u957f\u8f88\u4f1a\u4e0d\u4f1a\u53cd\u5bf9\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u8fc5\u5feb\u5730\u8f6c\u52a8\u5ff5\u5934\uff0c\u5fd6\u9053\uff1a\u201c\u6211\u82e5\u662f\u8bf4\u51fa\u6069\u5e08\u59d3\u540d\uff0c\u53ea\u6015\u4ed6\u4eec\u5c31\u8981\u5927\u5403\u4e00\u60ca\u4e86\uff0c\u4f46\u5982\u634f\u9020\u4e00\u4e2a\u5047\u7684\uff0c\u5374\u53c8\u4e8e\u5fc3\u4e0d\u5b89\u3002\u201d\u5f53\u4e0b\u6c89\u541f\u4e86\u4e00\u4f1a\uff0c\u9053\uff1a\u201c\u533a\u533a\u7684\u6b66\u529f\uff0c\u5f97\u81ea\u4e00\u4f4d\u4e1c\u8033\u8001\u4eba\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8bdd\u97f3\u4e00\u843d\uff0c\u9ad8\u65e0\u654c\u8138\u4e0a\u5c31\u6709\u4e9b\u96be\u770b\uff0c\u4ed6\u6014\u6014\u7684\u9053\uff1a\u201c\u4e1c\u8033\u8001\u4eba\uff0c\u4ed6\u662f\u8c01\uff1f\u5728\u6b66\u6797\u4e2d\uff0c\u53ef\u662f\u6709\u5f88\u9ad8\u7684\u5730\u4f4d\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u77a7\u7684\u6697\u6697\u5931\u7b11\uff0c\u4f46\u53e3\u4e2d\u5374\u4e00\u672c\u6b63\u7ecf\u4ed6\u8bf4\u9053\uff1a\u201c\u4e1c\u8033\u8001\u4eba\u662f\u5426\u5728\u6b66\u6797\u4e2d\u5927\u6709\u540d\u671b\uff0c\u533a\u533a\u5e76\u4e0d\u6e05\u695a\uff0c\u5f53\u5e74\uff0c\u8001\u4eba\u4f20\u6211\u6b66\u529f\u4e4b\u65f6\uff0c\u53ea\u662f\u533a\u533a\u5bb6\u4e2d\u4e00\u540d\u8001\u4ec6\u3002\u201d \u3000\u3000\u90a3\u9ad8\u65e0\u654c\u5b8c\u5168\u88ab\u65b9\u96ea\u5b9c\u552c\u4f4f\uff0c\u5446\u4e86\u4e00\u5446\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u8001\u5f1f\u679c\u7136\u53ea\u5b66\u4f1a\u51e0\u624b\u5e84\u7a3c\u628a\u5f0f\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u533a\u533a\u7d20\u65e0\u5927\u5fd7\uff0c\u4f46\u6c42\u5f3a\u8eab\u58ee\u9aa8\uff0c\u8fd9\u5df2\u7ecf\u5f88\u591f\u4e86\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u70b9\u4e86\u70b9\u5934\uff0c\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u8fd9\u7b49\u60f3\u6cd5\uff0c\u672c\u5ea7\u5012\u4e5f\u76f8\u4fe1\uff0c\u4e0d\u8fc7\u2026\u2026\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u671b\u7740\u65b9\u96ea\u5b9c\u54a7\u5634\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u8001\u5f1f\u5982\u60f3\u51fa\u5165\u5934\u5730\uff0c\u5728\u6c5f\u6e56\u4e0a\u95ef\u95ef\uff0c\u672c\u5ea7\u4e5f\u8bb8\u53ef\u52a9\u4f60\u4e00\u81c2\u4e4b\u529b\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u9053\uff1a\u201c\u4f60\u8fd9\u50bb\u74dc\uff0c\u5012\u662f\u60f3\u81ea\u5165\u9677\u9631\u4e86\uff0c\u5c11\u65f6\u90a3\u7530\u8fdc\u6765\u6b64\u4ee5\u540e\uff0c\u53ea\u6015\u4f60\u51c6\u4f1a\u5927\u5403\u4e00\u60ca\u2026\u2026\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u5c0a\u9a7e\u76db\u60c5\uff0c\u533a\u533a\u5fc3\u9886\u4e86\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u6562\u60c5\u4e0d\u60f3\u8981\u672c\u5ea7\u5e2e\u5fd9\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u533a\u533a\u4e0a\u6709\u8001\u6bcd\u5728\u5802\uff0c\u4e0b\u65e0\u5144\u5f1f\u59d0\u59b9\uff0c\u5982\u662f\u6d89\u8eab\u6c5f\u6e56\uff0c\u5c31\u5c06\u62db\u81f4\u4e0d\u5b5d\u4e4b\u8ba5\uff0c\u662f\u4ee5\u2026\u2026\u662f\u4ee5\u2026\u2026\u201d\u4f59\u97f3\u672a\u7edd\uff0c\u53ea\u89c1\u90a3\u8305\u820d\u4e4b\u4e2d\uff0c\u8d70\u51fa\u4e00\u4f4d\u795e\u60c5\u8be1\u5f02\uff0c\u76ee\u5149\u95ea\u70c1\u4e0d\u5b9a\u7684\u767d\u9aef\u8001\u4eba\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u987f\u65f6\u5207\u65ad\u8bdd\u5934\uff0c\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u4f4f\u53e3\u4e0d\u8bed\u3002 \u3000\u3000\u539f\u6765\uff0c\u8fd9\u4f4d\u767d\u9aef\u8001\u4eba\uff0c\u6b63\u662f\u9b54\u5200\u7530\u8fdc\u3002\u53ea\u89c1\u4ed6\u770b\u90fd\u672a\u770b\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u5927\u6b65\u5411\u90a3\u9ad8\u65e0\u654c\u8d70\u53bb\uff0c\u53cc\u624b\u62b1\u62f3\uff0c\u795e\u60c5\u606d\u656c\u5730\u7b11\u9053\uff1a\u201c\u9996\u5ea7\u53ec\u5524\u5c5e\u4e0b\uff0c\u4e0d\u77e5\u6709\u4f55\u5429\u5490\uff1f\u201d \u3000\u3000\u90a3\u9ad8\u65e0\u654c\u76ee\u5149\u4e00\u8f6c\uff0c\u7b11\u9053\uff1a\u201c\u7530\u62a4\u6cd5\uff0c\u4f60\u6709\u4f4d\u8001\u670b\u53cb\u8981\u89c1\u4f60\u3002\u201d \u3000\u3000\u7530\u8fdc\u95fb\u8a00\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u5c5e\u4e0b\u7684\u670b\u53cb\uff0c\u5728\u54ea\u513f\uff1f\u201d \u3000\u3000\u9ad8\u65e0\u654c\u6307\u7740\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u4e0d\u5c31\u662f\u5417\uff1f\u201d \u3000\u3000\u7530\u8fdc\u76ee\u5149\u6295\u6ce8\u5728\u65b9\u96ea\u5b9c\u8eab\u4e0a\u77a7\u4e86\u534a\u54cd\uff0c\u65b9\u9053\uff1a\u201c\u9996\u5ea7\uff0c\u662f\u5426\u8fd9\u4f4d\u5c0f\u5144\u5f1f\uff0c\u4e43\u662f\u5c5e\u4e0b\u7684\u670b\u53cb\u5417\uff1f\u201d \u3000\u3000\u9ad8\u65e0\u654c\u7b11\u9053\uff1a\u201c\u7530\u62a4\u6cd5\uff0c\u8fd9\u53ef\u4e0d\u662f\u672c\u5ea7\u8bf4\u7684\uff0c\u8fd9\u4f4d\u5c0f\u5144\u5f1f\uff0c\u72ec\u81ea\u627e\u4e0a\u95e8\u6765\u8981\u89c1\u4f60\u554a\uff01\u201d \u3000\u3000\u9b54\u536b\u7530\u8fdc\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5947\u602a\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u7530\u603b\u74e2\u628a\u5b50\uff0c\u4f60\u53ef\u662f\u8d35\u4eba\u591a\u5fd8\u4e8b\uff0c\u533a\u533a\u65b9\u96ea\u5b9c\uff0c\u4ec5\u53ea\u4e09\u5e74\u591a\u672a\u89c1\uff0c\u5c0a\u9a7e\u5c31\u5fd8\u8bb0\u4e86\u5417\uff1f\u201d \u3000\u3000\u9b54\u5200\u7530\u8fdc\u6014\u6014\u5730\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u2026\u2026\u771f\u7684\u662f\u524d\u6765\u5bfb\u627e\u8001\u592b\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5927\u7b11\u9053\uff1a\u201c\u603b\u74e2\u628a\u5b50\u53ef\u8bb0\u5f97\u5f53\u5e74\u4e0e\u4e2d\u5dde\u4e09\u4fa0\u7684\u90a3\u573a\u7ea6\u4f1a\u5417\uff1f\u201d \u3000\u3000\u7530\u8fdc\u9661\u7136\u8138\u8272\u5927\u53d8\uff0c\u6c89\u58f0\u9053\uff0c\u201c\u4f60\u662f\u65b9\u745c\uff1f\u201d \u3000\u3000\u6562\u60c5\u9b54\u5200\u7530\u8fdc\uff0c\u5f53\u5e74\u88ab\u65b9\u96ea\u5b9c\u4e00\u5251\u523a\u4f24\uff0c\u8feb\u5f97\u4ed6\u4e0d\u5f97\u4e0d\u653e\u8d70\u4e2d\u5dde\u4e09\u4fa0\uff0c\u5e76\u4e14\u53d1\u4e0b\u8a93\u8a00\uff0c\u4e0d\u6740\u65b9\u745c\u4e4b\u524d\uff0c\u51b3\u4e0d\u627e\u4e2d\u5dde\u4e09\u4fa0\u62a5\u4ec7\uff0c\u662f\u4ee5\uff0c\u65b9\u745c\u7684\u540d\u5b57\uff0c\u5728\u4ed6\u53e3\u4e2d\u76f4\u5982\u751f\u6839\u4e00\u822c\uff0c\u7262\u7262\u8bb0\u4f4f\uff0c\u65b9\u96ea\u5b9c\u4e00\u63d0\u4e2d\u5dde\u4e09\u4fa0\uff0c\u4ed6\u81ea\u662f\u7acb\u5373\u60f3\u8d77\u6765\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u6de1\u6de1\u7b11\u9053\uff1a\u201c\u4e0d\u9519\u554a\uff01\u65b9\u67d0\u773c\u4e0b\u5df2\u6539\u540d\u65b9\u96ea\u5b9c\uff0c\u603b\u74e2\u628a\u5b50\u6700\u597d\u8bb0\u4f4f\u3002\u201d \u3000\u3000\u9b54\u5200\u7530\u8fdc\u51b7\u54fc\u4e86\u4e00\u54fc\uff0c\u9053\uff1a\u201c\u8001\u592b\u6b64\u751f\u662f\u4e0d\u4f1a\u5fd8\u8bb0\u4f60\u8fd9\u5a03\u513f\u7684\u4e86\u3002\u201d\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5a03\u513f\uff0c\u4f60\u4e09\u5e74\u524d\u7684\u90a3\u4e00\u5251\uff0c\u867d\u7136\u6551\u4e86\u4e2d\u5dde\u4e09\u4fa0\u4e00\u547d\uff0c\u4f46\u662f\uff0c\u4f60\u5a03\u513f\u53ef\u77e5\u9053\uff0c\u8fd9\u6b63\u4e0d\u557b\u66ff\u4f60\u81ea\u5df1\u6572\u54cd\u4e86\u4e27\u949f\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u662f\u5417\uff1f\u533a\u533a\u4f3c\u662f\u4e0d\u5927\u76f8\u4fe1\u3002\u201d \u3000\u3000\u7530\u8fdc\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u5c0f\u5a03\u513f\uff0c\u4f60\u7acb\u5373\u5c31\u4f1a\u77e5\u9053\u8001\u592b\u8bf4\u7684\u4e0d\u5047\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u603b\u74e2\u628a\u5b50\u53ef\u662f\u8981\u8bd5\u8bd5\u533a\u533a\u5251\u6cd5\u5417\uff1f\u201d \u3000\u3000\u9b54\u5200\u7530\u8fdc\u8f69\u7709\u9053\uff1a\u201c\u5a03\u513f\uff0c\u5f53\u5e74\u8001\u592b\u4e00\u5ff5\u8f7b\u654c\uff0c\u4e2d\u4e86\u4f60\u7684\u8be1\u8ba1\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u8001\u592b\u5012\u662f\u60f3\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5bab\u7b11\u9053\uff1a\u201c\u5c0a\u9a7e\u5f53\u771f\u60f3\u660e\u767d\u4e86\uff0c\u4f46\u4e0d\u77e5\u533a\u533a\u90a3\u4e00\u5251\u7684\u6765\u5389\uff0c\u4f60\u60f3\u51fa\u6765\u6ca1\u6709\uff1f\u201d \u3000\u3000\u9b54\u5200\u7530\u8fdc\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u8001\u592b\u867d\u672a\u60f3\u51fa\u90a3\u4e00\u5251\u6765\u5386\uff0c\u4f46\u8001\u592b\u5374\u5df1\u60f3\u4e86\u7834\u89e3\u7684\u62db\u6cd5\u2026\u2026\u201d \u3000\u3000\u8bed\u97f3\u672a\u5df2\uff0c\u90a3\u9ad8\u65e0\u654c\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u7530\u62a4\u6cd5\uff0c\u4f60\u8ddf\u8fd9\u4f4d\u8001\u5f1f\u6709\u8fc7\u4ec0\u4e48\u8282\u5417\uff1f\u201d \u3000\u3000\u7530\u8fdc\u795e\u8272\u4e25\u8083\u5730\u70b9\u5934\uff0c\u62b1\u62f3\u9053\uff1a\u201c\u5c5e\u4e0b\u5f53\u5e74\u66fe\u8d25\u5728\u8fd9\u5c0f\u5a03\u513f\u624b\u4e0b\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u5fc3\u4e2d\u4e00\u9707\uff0c\u8131\u53e3\u9053\uff1a\u201c\u9b54\u5200\u7530\u8fdc\u4f1a\u8d25\u5728\u8fd9\u5c0f\u5a03\u624b\u4e0b\u5417\uff1f\u201d \u3000\u3000\u7530\u8fdc\u4f4e\u5934\u9053\uff1a\u201c\u5c5e\u4e0b\u4e00\u65f6\u4e0a\u4e86\u4ed6\u7684\u5f53\u2026\u2026\u201d \u3000\u3000\u9ad8\u65e0\u654c\u77a7\u4e86\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u5ffd\u7136\u5927\u7b11\u9053\uff1a\u201c\u672c\u5ea7\u4e0d\u4fe1\uff0c\u7530\u62a4\u6cd5\uff0c\u4f60\u662f\u600e\u6837\u5403\u4e86\u8d25\u4ed7\u3002\u201d \u3000\u3000\u7530\u8fdc\u72e0\u72e0\u5730\u76ef\u4e86\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u8fd9\u624d\u628a\u5f53\u5e74\u4e4b\u4e8b\uff0c\u7b80\u7565\u4ed6\u8bf4\u4e86\u4e00\u904d\u3002 \u3000\u3000\u9ad8\u65e0\u654c\u6c89\u541f\u4e86\u4e00\u4f1a\uff0c\u6389\u5934\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u8fd9\u7530\u8fdc\u8bf4\u7684\u53ef\u662f\u771f\u7684\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u53e5\u53e5\u771f\u5b9e\uff0c\u4e0d\u8fc7\uff0c\u6709\u4e00\u70b9\u4ed6\u5374\u672a\u66fe\u8bf4\u5bf9\uff0c\u201d \u3000\u3000\u9ad8\u65e0\u654c\u5fae\u7b11\u9053\uff1a\u201c\u54ea\u4e00\u70b9\u4e0d\u5bf9\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u533a\u533a\u5e76\u672a\u884c\u9669\u53d6\u5de7\uff0c\u4e5f\u672a\u5982\u4ed6\u6240\u8bf4\uff0c\u7528\u8be1\u8ba1\u6218\u80dc\u4e8e\u4ed6\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u7b11\u9053\uff1a\u201c\u672c\u5ea7\u77e5\u9053\uff0c\u8001\u5f1f\uff0c\u4ee5\u9b54\u5200\u7530\u8fdc\u7684\u6b66\u529f\uff0c\u4f11\u8bf4\u662f\u4f60\uff0c\u5c31\u662f\u529f\u529b\u5f3a\u8fc7\u672c\u5ea7\u4e4b\u4eba\uff0c\u8981\u60f3\u4f7f\u7528\u8be1\u8ba1\u80fd\u591f\u4e00\u5251\u523a\u4f24\u4e8e\u4ed6\uff0c\u53ea\u6015\u4e5f\u4e0d\u53ef\u80fd\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u6697\u9053\uff1a\u8fd9\u59d3\u9ad8\u7684\u5012\u5f88\u901a\u60c5\u8fbe\u7406\uff01\u53e3\u4e2d\u5374\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5c0a\u9a7e\u8bf4\u7684\u4e5f\u662f\u5b9e\u60c5\u3002 \u3000\u3000\u9ad8\u65e0\u654c\u671b\u7740\u7530\u8fdc\u7b11\u9053\uff1a\u201c\u7530\u62a4\u6cd5\uff0c\u8fd9\u4f4d\u8001\u5f1f\u4eca\u5929\u627e\u4e0a\u95e8\u6765\uff0c\u7530\u62a4\u6cd5\u53ef\u6709\u4ec0\u4e48\u6253\u7b97\uff1f \u3000\u3000\u6614\u5e74\u7684\u90a3\u4e00\u5251\u4e4b\u4ec7\uff0c\u4f60\u662f\u5426\u8fd8\u8981\u62a5\u590d\u3002\u201d \u3000\u3000\u7530\u8fdc\u76ee\u4e2d\u9661\u5c04\u51f6\u5149\uff0c\u63a5\u9053\uff1a\u201c\u5c5e\u4e0b\u6614\u65e5\u5df1\u6709\u8a93\u8a00\uff0c\u7eb5\u7136\u8fd9\u5a03\u513f\u4e0d\u80af\u627e\u4e0a\u95e8\u6765\uff0c\u5c5e\u4e0b\u4e5f\u4f1a\u6709\u4e00\u5929\u524d\u53bb\u627e\u4ed6\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u4e0d\u5fc5\u4e86\uff0c\u533a\u533a\u4eca\u513f\u6b63\u662f\u81ea\u5df1\u9001\u4e0a\u95e8\u6765\u5566\uff01\u201d \u3000\u3000\u7530\u8fdc\u6068\u6068\u5730\u6012\u9053\uff1a\u201c\u5a03\u513f\uff0c\u4eca\u5929\u4e0d\u662f\u4f60\u6b7b\uff0c\u90a3\u5c31\u662f\u8001\u592b\u81ea\u520e\u5200\u4e0b\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5927\u7b11\u9053\uff1a\u201c\u592a\u4e25\u91cd\u4e86\uff0c\u533a\u533a\u5e76\u65e0\u4ece\u4f60\u4e4b\u5fc3\uff0c\u4f60\u4f55\u5fc5\u81ea\u520e\u201d\uff1f\u201d \u3000\u3000\u9ad8\u65e0\u6545\u5ffd\u7136\u63a5\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u4eca\u65e5\u6765\u6b64\uff0c\u662f\u5426\u8981\u627e\u8fd9\u7530\u62a4\u6cd5\u8f83\u91cf\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u533a\u533a\u5e76\u65e0\u6240\u8c13\uff0c\u5982\u662f\u7530\u8fdc\u4e0d\u670d\u6c14\uff0c\u533a\u533a\u81ea\u662f\u613f\u610f\u5949\u966a\u4ed6\u8d70\u4e0a\u51e0\u62db\u3002\u201d \u3000\u3000\u9ad8\u65e0\u6545\u7b11\u9053\uff1a\u201c\u8001\u5f1f\u5012\u662f\u8c6a\u723d\u5f97\u5f88\u3002\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u5411\u7530\u8fdc\u559d\u9053\uff1a\u201c\u7530\u62a4\u6cd5\uff0c\u4f60\u8bf4\u5f53\u5e74\u4e4b\u8d25\uff0c\u4e43\u662f\u4e00\u65f6\u8f7b\u654c\uff0c\u4eca\u65e5\u672c\u5ea7\u5728\u65c1\u4f5c\u8bc1\uff0c\u4f60\u4e0d\u59a8\u518d\u8bd5\u8bd5\u8fd9\u4f4d\u65b9\u8001\u5f1f\u7684\u5251\u6cd5\uff0c\u5230\u5e95\u662f\u4f60\u8f7b\u654c\u843d\u8d25\uff0c\u8fd8\u662f\u65b9\u8001\u5f1f\u5251\u6cd5\u9ad8\u660e\u3002\u201d \u3000\u3000\u7530\u8fdc\u9053\uff1a\u201c\u5c5e\u4e0b\u9075\u547d\uff01\u201d\u8f6c\u8eab\u7adf\u5411\u8305\u820d\u5185\u884c\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u77a7\u5f97\u5fc3\u4e2d\u4e00\u884c\uff0c\u4ed8\u9053\uff1a\u201c\u4ed6\u600e\u4e48\u6e9c\u4e86\uff1f\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u9ad8\u65e0\u654c\u5df2\u63a5\u53e3\u9053\uff1a\u201c\u65b9\u8001\u5f1f\uff0c\u7530\u62a4\u6cd5\u5165\u5185\u53d6\u5200\uff0c\u4f60\u53ef\u662f\u8981\u5c0f\u5fc3\u4e9b\u624d\u597d\u3002\u201d\u6b64\u4eba\u5230\u66ff\u65b9\u96ea\u5b9c\u803d\u5fc3\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u751a\u662f\u611f\u6fc0\uff0c\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u4e0d\u52b3\u5c0a\u9a7e\u8d39\u5fc3\uff0c\u65b9\u67d0\u81f3\u5c11\u5c1a\u6709\u80dc\u4ed6\u4e4b\u80fd\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u7530\u8fdc\u5df2\u68d2\u7740\u90a3\u628a\u53e4\u5200\uff0c\u5927\u6b65\u800c\u6765\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u63a2\u624b\uff0c\u62bd\u51fa\u808b\u4e0b\u5b9d\u5251\uff0c\u7b11\u9053\uff1a\u201c\u9b54\u5200\u7530\u8fdc\u7684\u62d4\u5200\u4e00\u51fb\uff0c\u636e\u8bf4\u6b66\u6797\u4e4b\u4e2d\uff0c\u751a\u5c11\u6709\u5165\u80fd\u591f\u8eb2\u8fc7\uff0c\u533a\u533a\u5f53\u5e74\u4fa5\u80dc\u4e00\u62db\uff0c\u8fd9\u51e0\u5e74\u6765\uff0c\u9601\u4e0b\u5b9a\u7136\u7686\u7ec3\u4e0d\u4f11\uff0c\u770b\u6765\u4eca\u65e5\u4f60\u9b54\u5200\u51fa\u624b\uff0c\u5fc5\u662f\u77f3\u7834\u5929\u60ca\uff0c\u5341\u5206\u5413\u4eba\u7684\u4e86\u3002\u201d \u3000\u3000\u9b54\u5200\u53e3\u8fdc\u6b65\u5c65\u6c89\u91cd\u7684\u8d70\u6765\uff0c\u95fb\u8a00\u5927\u559d\u9053\uff1a\u201c\u5a03\u513f\uff0c\u4f60\u80fd\u77e5\u9053\u8001\u592b\u8fd9\u51e0\u5e74\u82e6\u7ec3\u4e0d\u4f11\uff0c\u4f60\u5c31\u4e0d\u8be5\u81ea\u5df1\u524d\u6765\u9001\u6b7b\u3002\u201d \u3000\u3000\u53e4\u5200\u5ffd\u5730\u4e00\u6446\uff0c\u63a5\u9053\uff1a\u201c\u5f53\u5e74\u8001\u592b\u5148\u4f60\u51fa\u5200\uff0c\u5374\u4f24\u5728\u4f60\u7684\u5251\u4e0b\uff0c\u8001\u592b\u4eca\u65e5\u5374\u8981\u6258\u5927\u4e9b\uff0c\u8ba9\u4f60\u4e00\u4e2a\u5148\u673a\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u5e74\u7eaa\u5927\u7684\u591a\uff0c\u81ea\u662f\u5e94\u8be5\u8ba9\u5e74\u8f7b\u4eba\u5148\u52a8\u624b\u624d\u662f\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u8fd9\u7530\u8fdc\u660e\u660e\u662f\u5c1a\u672a\u60f3\u51fa\u5f53\u5e74\u81ea\u5df1\u4e00\u5251\uff0c\u7a76\u7adf\u662f\u5982\u4f55\u4f24\u4e86\u4ed6\uff0c\u8fd8\u8fd9\u7b49\u6545\u793a\u5927\u65b9\u3002\u201d \u3000\u3000\u5f53\u4e0b\u5fae\u5fae\u4e00\u7b11\uff0c\u5411\u7530\u8fdc\u9053\uff1a\u201c\u9601\u4e0b\u76db\u610f\uff0c\u533a\u533a\u5fc3\u9886\u4e86\u2026\u2026\u201d \u3000\u3000\u76ee\u524d\u7684\u65b9\u96ea\u5b9c\uff0c\u5df2\u975e\u6614\u65e5\u5434\u4e0b\u963f\u8499\uff0c\u5251\u795e\u7684\u5168\u5957\u5251\u6cd5\uff0c\u4ed6\u5df2\u64a9\u7136\u5fc3\u4e2d\uff0c\u7530\u8fdc\u7eb5\u4e0d\u51fa\u5200\uff0c\u4ed6\u4e5f\u4e0d\u4f1a\u50cf\u5f53\u5e74\u90a3\u822c\uff0c\u4e3e\u5251\u96be\u4ee5\u653b\u654c\u4e86\u3002 \u3000\u3000\u56e0\u6b64\uff0c\u8bdd\u97f3\u4e00\u987f\uff0c\u4e00\u6325\u957f\u5251\uff0c\u8f7b\u8f7b\u5730\u5411\u7530\u8fdc\u523a\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u4ed6\u7528\u7684\u4e43\u662f\u865a\u62db\uff0c\u65e2\u65e0\u4f24\u654c\u4e4b\u610f\uff0c\u4e5f\u4e0d\u662f\u5251\u795e\u7684\u5251\u6cd5\uff0c\u8bf4\u7a7f\u4e86\uff0c\u53ea\u4e0d\u8fc7\u662f\u505a\u4e2a\u6837\u5b50\u800c\u5df2\u3002 \u3000\u3000\u7530\u8fdc\u5e74\u8001\u6210\u7cbe\uff0c\u800c\u53c8\u662f\u7591\u5fc3\u7279\u91cd\u7684\u4eba\uff0c\u65b9\u96ea\u5b9c\u865a\u6643\u4e00\u5251\uff0c\u4ed6\u81ea\u662f\u77a7\u5f97\u51fa\u6765\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u7adf\u7136\u659c\u8eab\u5012\u9000\u4e86\u4e09\u6b65\uff0c\u559d\u9053\uff1a\u201c\u5a03\u513f\uff0c\u4f60\u4e0d\u5fc5\u5f04\u9b3c\uff0c\u8001\u592b\u4e0d\u4f1a\u518d\u4e0a\u4f60\u7684\u5f53\u4e86\u3002\u201d\u539f\u6765\uff0c\u4ed6\u9519\u4ee5\u4e3a\u65b9\u96ea\u5b9c\u8fd9\u865a\u6643\u4e00\u62db\uff0c\u4e43\u662f\u8bf1\u4ed6\u51fa\u5200\uff0c\u597d\u518d\u8e48\u6614\u5e74\u8986\u8f99\u3002 \u3000\u3000\u7530\u8fdc\u95ea\u8eab\u540e\u9000\uff0c\u76ee\u5149\u76ef\u5728\u65b9\u96ea\u5b9c\u957f\u5251\u4e4b\u4e0a\uff0c\u77a7\u4ed6\u662f\u4e0d\u662f\u53c8\u50cf\u6614\u5e74\u4e00\u6837\uff0c\u957f\u5251\u523a\u51fa\u4e4b\u540e\uff0c\u7adf\u5728\u4e2d\u9014\u505c\u4e0b\u3002 \u3000\u3000\u4ed6\u5f53\u7136\u4e0d\u66fe\u6599\u5230\uff0c\u65b9\u96ea\u5b9c\u5df2\u7136\u662f\u7b2c\u4e8c\u4e2a\u5251\u795e\u51fa\u4e16\uff0c\u8ddf\u4e09\u5e74\u524d\u76f8\u8f83\uff0c\u7b80\u76f4\u662f\u5929\u58e4\u4e4b\u522b\u4e86\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u865a\u6643\u4e00\u62db\uff0c\u7acb\u5373\u6536\u5251\uff0c\u7b11\u9053\uff1a\u201c\u9601\u4e0b\u4e3a\u4f55\u4e0d\u8fdb\u53cd\u9000\uff1f\u201d \u3000\u3000\u7530\u8fdc\u51b7\u7b11\u9053\uff1a\u201c\u5c0f\u5a03\u513f\uff0c\u8001\u592b\u4e0a\u5f53\uff0c\u4e00\u751f\u4e5f\u53ea\u80fd\u4e0a\u4e00\u6b21\uff0c\u4f60\u60f3\u8981\u8001\u592b\u518d\u5148\u884c\u51fa\u5200\uff0c\u53ea\u6015\u4f60\u2026\u2026\u53ea\u6015\u2026\u2026\u201d \u3000\u3000\u9ad8\u65e0\u654c\u559d\u9053\uff1a\u201c\u53ea\u6015\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9b54\u5200\u7530\u8fdc\u9053\uff1a\u201c\u5c5e\u4e0b\u6614\u5e74\u662f\u8d25\u5728\u8fd9\u7b49\u624b\u6cd5\u4e4b\u4e0b\uff0c\u56e0\u6b64\u5c5e\u4e0b\u4e0d\u5f97\u4e0d\u5c0f\u5fc3\u4e00\u4e9b\uff0c\u201d \u3000\u3000\u9ad8\u65e0\u654c\u51b7\u54fc\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u7530\u62a4\u6cd5\uff0c\u4ee5\u4f60\u7684\u58f0\u671b\uff0c\u5e74\u7eaa\u4f3c\u4e4e\u4e0d\u5e94\u5982\u6b64\u5c0f\u5fc3\u624d\u662f\u3002\u201d \u3000\u3000\u9b54\u5200\u7530\u8fdc\u8138\u4e0a\u795e\u60c5\u5927\u4e3a\u96be\u582a\uff0c\u76b1\u7709\u9053\uff1a\u201c\u9996\u5ea7\uff0c\u5c5e\u4e0b\u5f53\u5e74\u66fe\u7ecf\u88ab\u4ed6\u4e00\u5251\u523a\u4f24\u80f8\u8179\uff0c\u6b64\u60c5\u6b64\u666f\uff0c\u5982\u5728\u76ee\u524d\uff0c\u8fd9\u5a03\u513f\u6545\u6280\u91cd\u65bd\uff0c\u5c5e\u4e0b\u81ea\u662f\u4e0d\u613f\u4e0a\u5f53\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5ffd\u7136\u5927\u7b11\u9053\uff1a\u201c\u7530\u8fdc\uff0c\u542c\u4f60\u8bdd\u4e2d\u4e4b\u610f\uff0c\u4eff\u4f5b\u533a\u533a\u5fc5\u987b\u7b49\u4f60\u51fa\u5200\u4e4b\u65f6\uff0c\u65b9\u59cb\u65bd\u5c55\u4ec0\u4e48\u8be1\u8ba1\u80dc\u4f60\u4e86\u3002\u201d \u3000\u3000\u7530\u8fdc\u6012\u9053\uff1a\u201c\u4f60\u672c\u6765\u5c31\u662f\u8fd9\u7b49\u7528\u5fc3\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u5934\u4e00\u7b11\u9053\uff1a\u201c\u770b\u6765\u533a\u533a\u5982\u4e0d\u5f53\u771f\u5148\u884c\u653b\u4f60\uff0c\u4f60\u662f\u4e0d\u4f1a\u76f8\u4fe1\u7684\u4e86\u3002\u201d \u3000\u3000\u7530\u8fdc\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u597d\uff01\u533a\u533a\u5c31\u5148\u653b\u51fa\u4e00\u5251\uff0c\u8981\u4f60\u89c1\u8bc6\u89c1\u8bc6\u65b9\u67d0\u7684\u5251\u6cd5\u3002\u201d\u957f\u5251\u4e00\u6cd5\uff0c\u5237\u7684\u4e00\u58f0\u523a\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u7530\u8fdc\u76ee\u5149\u4e00\u4eae\u5168\u795e\u8d2f\u6ce8\u5730\u77a7\u7740\u65b9\u96ea\u5b9c\uff0c\u76f4\u5230\u65b9\u96ea\u5b9c\u7684\u5251\u5c16\u5df2\u7136\u8feb\u523a\u5230\u81ea\u5df1\u80f8\u524d\uff0c\u8fd9\u624d\u89c9\u51fa\u4e0d\u5bf9\uff0c\u8fde\u5fd9\u53f3\u624b\u6325\u5200\uff0c\u76f4\u5f80\u65b9\u96ea\u5b9c\u5251\u4e0a\u7f20\u53bb\u3002 \u3000\u3000\u539f\u6765\u7530\u8fdc\u5148\u524d\u53ea\u9053\u65b9\u96ea\u5b9c\u5251\u5230\u4e2d\u9014\uff0c\u4e5f\u8bb8\u53c8\u4f1a\u505c\u987f\u4e0b\u6765\uff0c\u50cf\u5f53\u5e74\u4e00\u6837\uff0c\u4e0d\u518d\u5f80\u524d\u523a\u51fa\uff0c\u4f46\u5c31\u5728\u4ed6\u7565\u4e00\u72b9\u7591\u95ee\uff0c\u60f3\u4e0d\u5230\u65b9\u96ea\u5b9c\u7684\u957f\u5251\uff0c\u5374\u5f53\u771f\u7684\u523a\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u5bb9\u5f97\u9b54\u5200\u7530\u8fdc\u51fa\u5200\u963b\u6321\uff0c\u4e3a\u65f6\u5df2\u665a\uff0c\u4f46\u542c\u5f97\u65b9\u96ea\u5b9c\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u7530\u8fc7\uff0c\u533a\u533a\u8fd9\u4e00\u5251\u53ea\u662f\u53eb\u4f60\u660e\u767d\uff0c\u65b9\u67d0\u5e76\u975e\u5982\u4f60\u6240\u60f3\u7684\u90a3\u7b49\u4f7f\u8bc8\u6c42\u80dc\u7f62\u4e86\u3002\u201d \u3000\u3000\u7530\u8fdc\u9661\u611f\u53f3\u81c2\u4e00\u51c9\uff0c\u534a\u622a\u8863\u8896\uff0c\u4e1a\u5df2\u8dcc\u843d\u5730\u4e0a\u3002 \u3000\u3000\u9b54\u5200\u7530\u8fdc\u5012\u62bd\u4e86\u4e00\u53e3\u51b7\u6c14\uff0c\u5446\u5446\u5730\u77a7\u7740\u65b9\u96ea\u5b9c\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u5251\u6cd5\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u4e0d\u544a\u8bc9\u4f60\u3002\u201d\u8bdd\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u7530\u8fdc\uff0c\u4f60\u5982\u662f\u5fc3\u4e2d\u4e0d\u773c\uff0c\u54b1\u4eec\u4e0d\u59a8\u91cd\u6765\u4e00\u6b21\u3002\u201d \u3000\u3000\u7530\u8fdc\u6ce8\u89c6\u4e86\u81ea\u5df1\u5de6\u81c2\uff0c\u5e76\u672a\u88ab\u65b9\u96ea\u5b9c\u6240\u4f24\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u8fd9\u5a03\u513f\u5230\u5e95\u529f\u529b\u4e0d\u8db3\uff0c\u7ecf\u9a8c\u4e0d\u591f\uff0c\u8fd9\u4e00\u5251\u5018\u82e5\u6362\u4e86\u6211\u51fa\u624b\uff0c\u8fd9\u6761\u5de6\u81c2\u5e94\u662f\u8fde\u7740\u8863\u8896\uff0c\u4e00\u9053\u88ab\u5272\u4e0b\u6765\u4e86\u3002\u201d\u53e3\u4e2d\u5374\u5e94\u9053\uff1a\u201c\u5a03\u513f\uff0c\u8001\u592b\u4e0d\u662f\u8ddf\u4f60\u6597\u6c14\uff0c\u670d\u4e0e\u4e0d\u670d\uff0c\u6839\u672c\u6bcb\u5eb8\u591a\u8c08\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4f3c\u672a\u66fe\u660e\u767d\u9b54\u5200\u7530\u8fdc\u8bdd\u4e2d\u542b\u610f\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u6253\u7b97\u518d\u6597\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u7530\u8fdc\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u8001\u592b\u4e00\u5251\u4e4b\u4ec7\u6765\u62a5\uff0c\u5c82\u80af\u5c31\u6b64\u4f4f\u624b\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u6014\u9053\uff1a\u201c\u4f60\u8fd8\u8981\u62fc\u4e00\u573a\uff1f\u201d \u3000\u3000\u7530\u8fdc\u9053\uff1a\u201c\u5a03\u513f\uff0c\u8001\u592b\u5982\u662f\u4e0d\u80fd\u4f24\u4f60\u4e00\u5200\uff0c\u8fd9\u53e3\u6c14\u53c8\u600e\u80fd\u51fa\u5f97\u4e86\u2026\u2026\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u84e6\u7136\u5927\u559d\u4e00\u58f0\uff0c\u6325\u5200\u65a9\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4ed6\u6df1\u6df1\u5730\u8bb0\u4f4f\u5f53\u5e74\u4e00\u5251\u4e4b\u8fb1\uff0c\u56e0\u6b64\uff0c\u4ed6\u5200\u52bf\u65a9\u51fa\uff0c\u7528\u5fc3\u5728\u8bf1\u4f7f\u65b9\u96ea\u5b9c\u51fa\u5251\uff0c\u81ea\u5df1\u5c31\u53ef\u51ed\u4ed7\u7740\u6570\u5341\u5e74\u6df1\u539a\u7684\u529f\u529b\uff0c\u5c06\u5bf9\u65b9\u5b9d\u5251\u9707\u98de\u3002 \u3000\u3000\u4ed6\u8fd9\u60f3\u6cd5\u672c\u662f\u987a\u7406\u6210\u7ae0\u4e4b\u4e8b\uff0c\u5c31\u5e38\u60c5\u800c\u8bba\uff0c\u65b9\u96ea\u5b9c\u8fd9\u822c\u5e74\u7eaa\u5728\u529f\u529b\u4e0a\uff0c\u600e\u53ef\u4e0e\u9b54\u5200\u7530\u8fdc\u76f8\u8f83\uff1f \u3000\u3000\u4f46\u6b64\u523b\u4ed6\u53ef\u5927\u5927\u7684\u9519\u4f30\u4e86\u5bf9\u65b9\u7684\u529f\u529b\u4e86\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5f97\u5251\u795e\u9635\u5e0c\u6b63\u6240\u7ed9\u836f\u7269\u4e4b\u52a9\uff0c\u77ed\u77ed\u4e09\u5e74\u4e2d\uff0c\u4e0d\u4ec5\u66ff\u4ed6\u7af9\u4e01\u4e86\u6df1\u539a\u7684\u7ec3\u6b66\u57fa\u7840\uff0c\u4f20\u6388\u4ed6\u65e0\u4e0a\u7684\u5251\u6cd5\uff0c\u66f4\u5728\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u4e2d\uff0c\u4f7f\u65b9\u96ea\u5b9c\u5728\u5185\u529b\u65b9\u9762\uff0c\u5df2\u4e0d\u6bd4\u9b54\u5200\u7530\u8fdc\u7a0d\u6709\u900a\u8272\u3002 \u3000\u3000\u56e0\u6b64\uff0c\u7530\u8fdc\u5200\u52bf\u5288\u51fa\uff0c\u65b9\u96ea\u5b9c\u4e0d\u518d\u50cf\u6614\u5e74\u90a3\u6837\uff0c\u4ec5\u51ed\u4ed7\u7740\u5251\u62db\u7684\u795e\u5947\uff0c\u9519\u8fc7\u5200\u52bf\uff0c\u4e00\u5251\u523a\u4e2d\u7530\u8fdc\uff0c\u800c\u662f\u632f\u81c2\u4e00\u5251\uff0c\u76f4\u5f80\u7530\u8fdc\u7684\u53e4\u5200\u4e0a\u649e\u53bb\u3002\u4f46\u95fb\u5f53\u7684\u4e00\u58f0\uff0c\u7530\u8fdc\u7684\u53e4\u5200\uff0c\u5df2\u88ab\u65b9\u96ea\u5b9c\u4e00\u5251\u5c01\u5f00\u3002 \u3000\u3000\u4e24\u4eba\u4e00\u4e2a\u9519\u8eab\uff0c\u4ea4\u95ea\u800c\u8fc7\u3002 \u3000\u3000\u9b54\u5200\u7530\u8fdc\u5fc3\u5934\u5927\u9707\uff0c\u8131\u53e3\u559d\u9053\uff1a\u201c\u597d\u5f3a\u7684\u5185\u529b\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u8fc7\u5956\u4e86\u3002\u201d \u3000\u3000\u5251\u52bf\u7a81\u7136\u4e00\u53d8\uff0c\u53cd\u624b\u653b\u4e86\u51fa\u53bb\uff0c\u5bd2\u8292\u4e00\u95ea\uff0c\u6307\u5411\u9b54\u5200\u7530\u8fdc\u7684\u80cc\u5fc3\u3002 \u3000\u3000\u7530\u8fdc\u6012\u54fc\u4e00\u58f0\uff0c\u65cb\u8eab\u51fa\u5200\uff0c\u7adf\u7136\u4e5f\u6a2a\u780d\u65b9\u96ea\u5b9c\u7684\u53f3\u81c2\u90e8\u4f4d\u3002 \u3000\u3000\u663e\u7136\uff0c\u65b9\u96ea\u5b9c\u5982\u679c\u4e0d\u64a4\u56de\u957f\u5251\uff0c\u7eb5\u7136\u53ef\u7f6e\u7530\u8fdc\u4e8e\u6b7b\u547d\uff0c\u4f46\u81ea\u5df1\u7684\u53f3\u81c2\uff0c\u4e5f\u5fc5\u96be\u4fdd\u5168\u3002 \u3000\u3000\u4f3c\u8fd9\u7b49\u4e0d\u987e\u6b7b\u6d3b\u7684\u6253\u6cd5\uff0c\u65b9\u96ea\u5b9c\u5f53\u7136\u4e0d\u53d6\u3002 \u3000\u3000\u5f53\u4e0b\u51b7\u7b11\u4e00\u58f0\uff0c\u79fb\u8eab\u659c\u63a0\u5c3a\u8bb8\uff0c\u559d\u9053\uff1a\u201c\u8001\u5339\u592b\uff0c\u4f60\u60f3\u800d\u65e0\u8d56\u5417\uff1f\u201d \u3000\u3000\u7530\u8fdc\u5200\u52bf\u843d\u7a7a\uff0c\u4f46\u5374\u563f\u563f\u9b3c\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8001\u592b\u53ea\u8981\u6bc1\u5f97\u4e86\u4f60\uff0c\u7eb5\u7136\u8eab\u6b7b\u5251\u4e0b\uff0c\u4e5f\u662f\u5212\u5f97\u6765\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6012\u9053\uff1a\u201c\u65e0\u803b\u4e4b\u5c24\u3002\u201d \u3000\u3000\u7530\u8fdc\u9053\uff1a\u201c\u8001\u592b\u6bd4\u4f60\u591a\u6d3b\u4e86\u6570\u5341\u5e74\uff0c\u4eba\u4e16\u4e00\u5207\uff0c\u5168\u90e8\u4eab\u7528\u591f\u4e86\uff0c\u7528\u8001\u592b\u5373\u5c06\u5165\u571f\u4e4b\u8eaf\uff0c\u6362\u4f60\u8fd9\u6bdb\u5934\u5b7a\u5b50\u4e4b\u547d\uff0c\u6709\u4f55\u4e0d\u53ef\u3002\u201d \u3000\u3000\u8bdd\u97f3\u672a\u843d\uff0c\u5bd2\u8292\u7535\u95ea\uff0c\u5237\u7684\u4e00\u58f0\uff0c\u6325\u5200\u72c2\u5288\u800c\u4e0b\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u987f\u65f6\u5fc3\u4e2d\u5927\u6012\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u79cd\u4eba\u9634\u9669\u4e4b\u81f3\uff0c\u662f\u4e0d\u5fc5\u4e0e\u4ed6\u8bb2\u4ec0\u4e48\u9053\u4e49\u4e86\u3002\u201d\u5fc3\u5ff5\u4e00\u5b9a\uff0c\u53f3\u624b\u5251\u52bf\u5ffd\u8d77\uff0c\u53ea\u89c1\u4e00\u7247\u5149\u5f71\u65cb\u8f6c\u4e4b\u4e2d\uff0c\u5939\u7740\u4e00\u9635\u9635\u91d1\u94c1\u4ea4\u545c\u4e4b\u58f0\uff0c\u4e24\u4eba\u5200\u5251\u4ea4\u51fb\u4e4b\u4e0b\uff0c\u4eba\u5f71\u4e00\u95ea\u800c\u5206\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u9000\u5f00\u4e94\u6b65\uff0c\u957f\u5251\u5df2\u7136\u5165\u9798\u3002 \u3000\u3000\u4ed6\u8138\u4e0a\u4e00\u7247\u8083\u7a46\u4e4b\u5bb9\uff0c\u76ee\u5149\u6295\u5f80\u5728\u7530\u8fdc\u8eab\u4e0a\u3002 \u3000\u3000\u90a3\u9b54\u5200\u7530\u8fdc\u53f3\u624b\u4e3e\u7740\u53e4\u5200\uff0c\u4f9d\u7136\u9ad8\u8fc7\u80a9\u5934\uff0c\u53cc\u76ee\u7d20\u76b1\u4e00\u8d77\uff0c\u8138\u4e0a\u795e\u8272\u4e00\u7247\u82cd\u767d\uff0c\u5de6\u80f8\u90e8\u4f4d\uff0c\u5374\u662f\u6c69\u6c69\u5730\u6d41\u7740\u9c9c\u8840\u3002 \u3000\u3000\u534a\u664c\uff0c\u65b9\u59cb\u542c\u5f97\u7530\u8fdc\u5410\u51fa\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u4f7f\u7684\u662f\u2026\u2026\u5251\u795e\u4e4b\u5251\u2026\u2026\u201d\u8bed\u97f3\u672a\u7edd\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u8fde\u4eba\u5e26\u5200\uff0c\u4ec6\u5012\u5728\u5730\u3002 \u3000\u3000\u9ad8\u65e0\u6545\u5927\u5403\u4e00\u60ca\uff0c\u8d70\u4e86\u8fc7\u53bb\uff0c\u53eb\u9053\uff1a\u201c\u7530\u62a4\u6cd5\uff0c\u4f60\u4f24\u7684\u5f88\u91cd\u5417\uff1f\u201d \u3000\u3000\u9b54\u5200\u7530\u8fdc\uff0c\u53cc\u76ee\u5df2\u7136\u95ed\u4e0a\uff0c\u6562\u60c5\u65b9\u96ea\u5b9c\u8fd9\u4e00\u5251\uff0c\u6b63\u597d\u523a\u4e2d\u4e86\u4ed6\u7684\u5fc3\u810f\u3002 \u3000\u3000\u4e00\u4ee3\u9b54\u5200\uff0c\u7adf\u7136\u5c31\u8fd9\u4e48\u6b7b\u53bb\u3002 \u3000\u3000\u9ad8\u65e0\u654c\u8138\u8272\u5927\u53d8\uff0c\u8fde\u5fd9\u4fef\u8eab\u63a2\u89c6\uff0c\u65b9\u96ea\u5b9c\u5fae\u5fbd\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u5fc5\u770b\u4e86\uff0c\u4ed6\u5df2\u7ecf\u6b7b\u4e86\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u6012\u5f62\u4e8e\u8272\uff0c\u8f6c\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4f60\u521a\u624d\u7684\u8bdd\uff0c\u90fd\u662f\u9a97\u6211\u7684\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u9022\u4eba\u4e14\u8bf4\u4e09\u5206\u8bdd\u7684\u9053\u7406\uff0c\u5c0a\u9a7e\u83ab\u975e\u90fd\u4e0d\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u9ad8\u65e0\u654c\u770b\u4e86\u90a3\u4e1a\u5df2\u6c14\u7edd\u7684\u7530\u8fdc\u4e00\u773c\uff0c\u51b7\u54fc\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u4f3c\u4e4e\u662f\u4e13\u4e3a\u6740\u4ed6\u800c\u6765\u7684\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u4e00\u5b9a\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8fd8\u6709\u522b\u7684\u4e8b\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u867d\u7136\u6709\u4e8b\uff0c\u7530\u8fdc\u5df2\u6b7b\uff0c\u90a3\u4e5f\u4e0d\u7528\u8bf4\u4e86\u3002\u201d \u3000\u3000\u9ad8\u65e0\u6545\u76ee\u5149\u4e2d\uff0c\u9732\u51fa\u4e00\u4efd\u5947\u5f02\u7684\u795e\u8272\uff0c\u9053\uff1a\u201c\u8001\u5f1f\u5982\u679c\u771f\u662f\u6709\u4e8b\u627e\u4ed6\uff0c\u4e3a\u4f55\u4f60\u7adf\u4e00\u5251\u53d6\u4e86\u4ed6\u7684\u6027\u547d\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u533a\u533a\u5e76\u672a\u6599\u5230\uff0c\u6b66\u6797\u4e4b\u4e2d\u5927\u5927\u6709\u540d\u7684\u9b54\u5200\u7530\u8fdc\uff0c\u5c45\u7136\u8fd9\u822c\u4e0d\u957f\u8fdb\u2026\u2026\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5c0a\u9a7e\u5728\u56db\u7075\u6559\u4e2d\uff0c\u4f4d\u5145\u9996\u5ea7\uff0c\u4e0d\u77e5\u9053\u9996\u5ea7\u53ef\u5c31\u662f\u6559\u4e3b\uff1f\u201d \u3000\u3000\u9ad8\u65e0\u654c\u6447\u6447\u5934\u9053\uff1a\u201c\u4e0d\u662f\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u90a3\u662f\u8fd8\u6709\u4e00\u4f4d\u6559\u4e3b\uff0c\u5728\u4f60\u4e4b\u4e0a\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u5fd6\u9053\uff1a\u4e0d\u77e5\u8fd9\u56db\u7075\u6559\u7684\u6559\u4e3b\u662f\u4ec0\u4e48\u4eba\u3002\u5fc3\u5ff5\u4e00\u8d77\uff0c\u8131\u53e3\u95ee\u9053\uff1a\u201c\u8d35\u6559\u7684\u6559\u4e3b\uff0c\u4e0d\u77e5\u662f\u6b66\u6797\u7684\u54ea\u4e00\u4f4d\u9ad8\u4eba\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u6055\u96be\u5949\u544a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8138\u8272\u5f88\u5e73\u9759\u5730\u6253\u91cf\u4e86\u9ad8\u65e0\u654c\u4e00\u773c\uff0c\u7b11\u9053\uff1a\u201c\u5c0a\u9a7e\u65e2\u7136\u4e0d\u613f\u8bf4\uff0c\u533a\u533a\u7b97\u662f\u591a\u6b64\u4e00\u95ee\u4e86\u3002\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u62b1\u62f3\u9053\uff1a\u201c\u544a\u8f9e\u4e86\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u4e00\u6014\u9053\uff1a\u201c\u600e\u4e48\u4f60\u8981\u8d70\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u7530\u8fdc\u5df2\u6b7b\uff0c\u65b9\u67d0\u7559\u6b64\u5df2\u7136\u65e0\u4e8b\u2026\u2026\u201d \u3000\u3000\u8bed\u97f3\u672a\u843d\uff0c\u9ad8\u65e0\u654c\u5ffd\u7136\u51b7\u54fc\u9053\uff1a\u201c\u4f60\u4e0d\u80fd\u8d70\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u90a3\u9b54\u5200\u7530\u8fdc\uff0c\u4e43\u662f\u672c\u6559\u62a4\u6cd5\u4e4b\u4e00\uff0c\u4f60\u60f3\u5fc5\u77e5\u9053\u5427\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5c0a\u9a7e\u5df2\u7ecf\u8bf4\u8fc7\uff01\u201d \u3000\u3000\u9ad8\u65e0\u654c\u53cc\u7709\u4e00\u8038\uff0c\u9053\uff1a\u201c\u8001\u5f1f\u65e2\u7136\u542c\u672c\u5ea7\u8bf4\u8fc7\uff0c\u5c31\u8be5\u660e\u767d\uff0c\u4f60\u773c\u4e0b\u4e0d\u80fd\u79bb\u5f00\u6b64\u5904\u7684\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u201c\u4ed6\u83ab\u975e\u8981\u4e3a\u7530\u8fdc\u62a5\u4ec7\u5417\uff1f\u201d \u3000\u3000\u8f6c\u5ff5\u4e4b\u95f4\uff0c\u63a5\u9053\uff1a\u201c\u533a\u533a\u7231\u6765\u5219\u6765\uff0c\u8981\u53bb\u5c31\u53bb\uff0c\u5c0a\u9a7e\u53ea\u6015\u65e0\u6743\u8fc7\u95ee\uff01\u201d \u3000\u3000\u9ad8\u65e0\u654c\u84e6\u5730\u4ef0\u5929\u5927\u7b11\u9053\uff0c\u201c\u597d\u72c2\u7684\u5c0f\u4f19\u5b50\uff0c\u9ad8\u67d0\u82e5\u662f\u65e0\u6743\u8fc7\u95ee\uff0c\u90a3\u5c82\u4e0d\u662f\u6210\u4e86\u7b11\u8bdd\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4f3c\u662f\u5df2\u7ecf\u542c\u51fa\u4e86\u9ad8\u65e0\u654c\u7684\u8bdd\u4e2d\u542b\u610f\uff0c\u90a3\u662f\u8bf4\uff0c\u4ed6\u65e2\u7136\u6765\u5230\u56db\u7075\u6559\u4e2d\uff0c\u60f3\u8d70\uff0c\u53ef\u5c31\u4e0d\u6613\u3002 \u3000\u3000\u5f53\u4e0b\u5fae\u6fc0\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f9d\u5c0a\u5bfc\u9a7e\u4e4b\u89c1\uff0c\u65b9\u67d0\u7684\u53bb\u7559\uff0c\u4f3c\u662f\u5176\u6743\u5728\u4f60\u4e86\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u7b97\u662f\u4f60\u8bf4\u5bf9\u4e86\uff0c\u56db\u7075\u6559\u7684\u5ddd\u897f\u91cd\u5730\uff0c\u53c8\u5c82\u662f\u5bb9\u4eba\u968f\u610f\u8fdb\u51fa\u7684\u6240\u5728\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u51b7\u7b11\u9053\uff1a\u201c\u5c0a\u9a7e\u53ef\u662f\u8981\u52a8\u5f3a\u5417\uff1f\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u4f60\u6740\u4e86\u672c\u6559\u62a4\u6cd5\uff0c\u5982\u662f\u56db\u7075\u6559\u4e2d\u65e0\u4eba\u8fc7\u95ee\uff0c\u4f20\u8bf4\u51fa\u6765\uff0c\u672c\u6559\u5c82\u4e0d\u88ab\u5929\u4e0b\u803b\u7b11\uff1f\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u5c11\u4e0d\u5f97\u672c\u5ea7\u8981\u5c06\u4f60\u7559\u5728\u6b64\u95f4\uff0c\u7b49\u5019\u6559\u4e3b\u56de\u6765\u5904\u7f6e\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u8bdd\u662f\u6709\u9053\u7406\uff0c\u65b9\u67d0\u6740\u4e86\u8d35\u6559\u62a4\u6cd5\uff0c\u4f60\u4eec\u8981\u62ff\u4e0b\u65b9\u67d0\uff0c\u90a3\u672c\u4e5f\u65e0\u53ef\u539a\u975e\uff0c\u53ea\u662f\u2026\u2026\u53ea\u662f\u2026\u2026\u201d\u4ed6\u5ffd\u800c\u6c89\u541f\u4e0d\u5f80\u4e0b\u8bf4\uff0c\u77a7\u7740\u9ad8\u65e0\u654c\u76f4\u7b11\u3002 \u3000\u3000\u9ad8\u65e0\u6545\u77aa\u76ee\u9053\uff1a\u201c\u53ea\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u53ea\u662f\u4f60\u4eec\u5c11\u7b97\u4e86\u4e00\u6869\u4e8b\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u4f60\u5c11\u7b97\u4e86\u65b9\u67d0\u7684\u5fc3\u610f\uff01\u201d \u3000\u3000\u9ad8\u65e0\u654c\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u8fd9\u4e0e\u4f60\u8001\u5f1f\u7684\u5fc3\u610f\uff0c\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f53\u7136\u6709\u5173\u7cfb\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u53eb\u9053\uff1a\u201c\u5feb\u8bf4\u51fa\u6765\uff0c\u5230\u5e95\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u65b9\u67d0\u7684\u5fc3\u610f\uff0c\u4e43\u662f\u4e3b\u5bb0\u65b9\u67d0\u4eca\u540e\u7684\u884c\u52a8\uff0c\u7559\u4e0e\u4e0d\u7559\uff0c\u81f3\u5c11\u8be5\u7531\u65b9\u67d0\u81ea\u5df1\u51b3\u5b9a\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u81f3\u6b64\uff0c\u65b9\u59cb\u660e\u767d\u65b9\u96ea\u5b9c\u7684\u8bdd\u4e2d\u610f\uff0c\u539f\u6765\u662f\u4e0d\u80af\u7559\u4e0b\u3002 \u3000\u3000\u8bd5\u60f3\u4ee5\u97f5\u5728\u56db\u7075\u6559\u4e2d\u4e00\u5411\u9ad8\u9ad8\u5728\u4e0a\uff0c\u81e3\u6307\u6c14\u4f7f\u60ef\u4e86\u7684\u4eba\uff0c\u5c82\u80af\u53d7\u8fd9\u65b9\u96ea\u5b9c\u7684\u595a\u843d\uff1f \u3000\u3000\u987f\u65f6\u8138\u8272\u5927\u53d8\uff0c\u559d\u9053\uff1a\u201c\u5c0f\u5a03\u513f\uff0c\u4f60\u662f\u4e0d\u613f\u7559\u4e0b\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u662f\u53c8\u5982\u4f55\uff1f\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u5a03\u513f\uff0c\u4f60\u81ea\u4ee5\u4e3a\u6b66\u529f\u5f88\u5f3a\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u6562\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u51b7\u58f0\u9053\uff1a\u201c\u6740\u4e86\u533a\u533a\u4e00\u4e2a\u9b54\u5200\u7530\u8fdc\uff0c\u90a3\u4e5f\u7b97\u4e0d\u5f97\u4ec0\u4e48\u9ad8\u660e\uff0c\u9ad8\u67d0\u4e0d\u5fc5\u52a8\u5251\uff0c\u53ea\u987b\u4e00\u6307\uff0c\u5c31\u53ef\u53d6\u4ed6\u6027\u547d\u4e8e\u5239\u90a3\u4e4b\u95f4\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u63a5\u9053\uff1a\u201c\u4f60\u4e5f\u81ea\u8ba4\u6b66\u529f\u5f88\u5f3a\u7684\u4e86\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u4e0d\u6562\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5c0a\u9a7e\u5982\u662f\u60f3\u7559\u4e0b\u65b9\u67d0\uff0c\u4eca\u5929\u53ea\u6015\u5f88\u96be\u529e\u5f97\u5230\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u4f60\u5a03\u513f\u5982\u60f3\u79bb\u53bb\uff0c\u53ea\u6015\u66f4\u96be\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fcd\u4e0d\u4f4f\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u662f\u8c08\u4e0d\u62e2\u4e86\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u4e0d\u4e00\u5b9a\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u60f3\uff0c\u6211\u4f55\u4e0d\u9017\u4ed6\u4e00\u9017\uff1f \u3000\u3000\u5f53\u4e0b\u88c5\u51fa\u4e00\u526f\u671f\u5f85\u4e4b\u8272\uff0c\u9053\uff1a\u201c\u90a3\u662f\u8bf4\u5c0a\u9a7e\u5c1a\u6709\u6761\u4ef6\u53ef\u8c08\u4e86\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u6761\u4ef6\u5341\u5206\u7b80\u5355\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6545\u610f\u8138\u9732\u559c\u8272\uff0c\u6025\u6025\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u6761\u4ef6\uff0c\u5c0a\u9a7e\u5feb\u8bf4\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u7b11\u9053\uff1a\u201c\u4f60\u7559\u6b64\u5341\u65e5\uff0c\u7b49\u5019\u6559\u4e3b\u56de\u6765\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u660e\u660e\u77e5\u9053\u4ed6\u4f1a\u8bf4\u51fa\u4ec0\u4e48\uff0c\u4f46\u4ed6\u4f9d\u65e7\u8fd9\u4e48\u53d1\u95ee\uff0c\u663e\u7136\u662f\u5728\u8c03\u4f83\u4ed6\uff0c\u53ea\u56e0\u8fd9\u4f4d\u751f\u6765\u5b9b\u5982\u9738\u738b\u4e00\u822c\u7684\u795e\u529b\u5929\u738b\u592a\u8fc7\u611a\u8822\uff0c\u624d\u4f1a\u4e0a\u4e86\u65b9\u96ea\u5b9c\u7684\u5f53\uff0c\u53ea\u9053\u4ed6\u5df2\u7ecf\u540c\u610f\u81ea\u5df1\u7684\u60f3\u6cd5\uff0c\u8131\u53e3\u8bf4\u51fa\uff0c\u8981\u4ed6\u5728\u6b64\u7b49\u5019\u5341\u5929\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5bb9\u4ed6\u8bf4\u51fa\u771f\u6b63\u7528\u5fc3\u6240\u5728\uff0c\u7acb\u5373\u7b11\u9053\uff1a\u201c\u5341\u5929\u5417\uff1f\u592a\u957f\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5143\u654c\u6c89\u541f\u9053\uff1a\u201c\u4e94\u5929\u5982\u4f55\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u4e94\u5929\u4e86\u8d35\u6559\u4e3b\u4e94\u5929\u4e4b\u5185\uff0c\u80fd\u591f\u56de\u6765\u5f97\u4e86\uff1f\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u53ef\u4e0d\u6562\u4e00\u5b9a\uff0c\u8bf4\u4e0d\u5b9a\u4f60\u5a03\u513f\u8fd0\u6c14\u597d\uff0c\u6559\u4e3b\u5728\u4eca\u5929\u5c31\u56de\u6765\u4e86\uff0c\u4e0d\u7136\u7684\u8bdd\uff0c\u4e5f\u8bb8\u5341\u5929\u534a\u6708\u4e5f\u4e0d\u4f1a\u56de\u6765\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u542c\u5c0a\u9a7e\u53e3\u6c14\uff0c\u6839\u672c\u4e0d\u77e5\u6559\u4e3b\u4f55\u65e5\u4f1a\u6765\u6b64\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u6559\u4e3b\u7684\u884c\u52a8\uff0c\u672c\u5ea7\u4e0d\u80fd\u8fc7\u95ee\uff0c\u4f46\u672c\u5ea7\u76f8\u4fe1\uff0c\u4e09\u4e94\u65e5\u5185\uff0c\u6559\u4e3b\u4e00\u5b9a\u4f1a\u6765\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e0d\u5f85\u4ed6\u7a0d\u6709\u601d\u8651\u4e4b\u673a\uff0c\u63a5\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u56e0\u4e3a\u4e09\u65e5\u4e4b\u5185\uff0c\u54b1\u4eec\u6709\u4e2a\u5f3a\u654c\u8981\u6765\uff0c\u5982\u662f\u6559\u4e3b\u4e0d\u4eb2\u81ea\u62b5\u6b64\uff0c\u672c\u5ea7\u5c06\u4f1a\u5e94\u4ed8\u4e0d\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff01\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u8001\u5f1f\u53ef\u80af\u7559\u4e0b\uff1f\u201d\u8bf4\u8bdd\u53e3\u6c14\u53d8\u5f97\u5341\u5206\u5ba2\u6c14\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u795e\u529b\u5927\u738b\u4e5f\u6709\u5bf9\u4ed8\u4e0d\u4e86\u7684\u4eba\u7269\uff0c\u770b\u6765\u4f60\u90a3\u5f3a\u654c\uff0c\u4e00\u5b9a\u5f88\u51f6\u4e86\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u4e94\u5927\u9b54\u4e3b\u4e2d\u4eba\uff0c\u81ea\u7136\u5f88\u51f6\u7684\u4e86\u3002\u201d \u3000\u3000\u4e94\u5927\u9b54\u4e3b\u56db\u5b57\u5165\u8033\uff0c\u65b9\u96ea\u5b9c\u4e0d\u7981\u5fc3\u4e2d\u4e00\u9707\u3002 \u3000\u3000\u7eba\u60f3\u8d77\u4e86\u6069\u5e08\u7684\u9057\u547d\uff0c\u8fd9\u4e94\u5927\u9b54\u4e3b\uff0c\u4e0d\u6b63\u662f\u5e08\u7236\u6240\u6700\u6700\u4e0d\u80fd\u653e\u5fc3\u7684\u6b66\u6797\u5f3a\u4eba\u5417\uff1f\u60f3\u4e0d\u5230\u81ea\u5df1\u8bef\u6253\u8bef\u649e\uff0c\u5374\u5728\u6b64\u95f4\u9047\u4e0a\uff0c\u90a3\u662f\u5343\u4e07\u4e0d\u80fd\u653e\u8fc7\u7684\u4e86\u3002\u201d\u5fc3\u5ff5\u4e00\u5b9a\uff0c\u6168\u7136\u9053\uff1a \u3000\u3000\u201c\u597d\uff0c\u6211\u7559\u4e0b\u4e09\u65e5\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u6709\u4e9b\u610f\u5916\u5730\u559c\u9053\uff1a\u201c\u4f60\u2026\u2026\u8001\u5f1f\u7b54\u5e94\u7559\u4e0b\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u65b9\u67d0\u8bf4\u51fa\u8fd9\u8bdd\uff0c\u81ea\u662f\u4e0d\u4f1a\u66f4\u6539\uff0c\u4e0d\u8fc7\uff0c\u6709\u4e00\u4ef6\u4e8b\uff0c\u5728\u4e0b\u53ef\u8981\u5148\u8bf4\u660e\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u8001\u5f1f\u8bf7\u8bb2\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8d35\u6559\u4e3b\u5982\u4e09\u65e5\u4e4b\u5185\uff0c\u4ecd\u672a\u62b5\u6b64\uff0c\u5728\u4e0b\u53ef\u5c31\u96be\u4ee5\u4e45\u5019\u4e86\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u90a3\u662f\u81ea\u7136\u2026\u2026\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5c4b\u5916\u4e0d\u662f\u5f85\u5ba2\u7684\u6240\u5728\uff0c\u8001\u5f1f\u65e2\u613f\u7559\u4e0b\uff0c\u54b1\u4eec\u5165\u5185\u8bf4\u8bdd\u5427\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u5fc5\u4e86\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u95fb\u8a00\u4e00\u6014\uff0c\u9053\uff1a\u201c\u8001\u5f1f\u53c8\u6539\u53d8\u4e86\u5fc3\u610f\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u6ca1\u6709\u554a\uff01\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u8001\u5f1f\u65e2\u662f\u672a\u66fe\u6539\u53d8\u5fc3\u610f\uff0c\u4e3a\u4f55\u4e0d\u80af\u5165\u5185\u7a0d\u53d9\uff0c\u800c\u4e14\u2026\u2026\uff0c\u4e5f\u597d\u8ba9\u672c\u5ea7\u4e3a\u8001\u5f1f\u5907\u9152\u6d17\u5c18\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u533a\u533a\u867d\u7136\u5e74\u7eaa\u4e0d\u5927\uff0c\u5012\u4e5f\u559c\u6b22\u5c71\u5c45\u91ce\u5bbf\uff0c\u5c0a\u9a7e\u7528\u4e0d\u7740\u4e3a\u6211\u5f20\u7f57\u4e86\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u663e\u7136\u731c\u4e0d\u51fa\u65b9\u96ea\u5b9c\u7528\u610f\u4f55\u5728\uff0c\u63a5\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u65e2\u7136\u7559\u4e0b\u4e86\uff0c\u600e\u5730\u2014\u2014\u600e\u5730\u2014\u2014\u201d \u3000\u3000\u4ed6\u4e00\u8fde\u8bf4\u51fa\u4e86\u51e0\u4e2a\u600e\u5730\uff0c\u90fd\u672a\u80fd\u8bb2\u660e\u8981\u95ee\u7684\u8bdd\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5929\u4e3a\u7fe0\u5e5b\u5730\u4e3a\u5e8a\uff0c\u5c0a\u9a7e\u7528\u4e0d\u7740\u591a\u4e3a\u5728\u4e0b\u98df\u5bbf\u62c5\u5fe7\u3002\u201d \u3000\u3000\u6562\u8bf7\u65b9\u96ea\u5b9c\u5df2\u7136\u731c\u51fa\u4e86\u4ed6\u7684\u5fc3\u610f\uff0c\u4e0d\u5f85\u518d\u95ee\uff0c\u5c31\u5148\u884c\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u9ad8\u65e0\u654c\u8baa\u6c55\u5730\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u672c\u5ea7\u6b63\u662f\u60f3\u4e0d\u51fa\u8001\u5f1f\u65e2\u5df2\u613f\u610f\u7559\u6b64\u4e09\u65e5\uff0c\u5374\u53c8\u4e3a\u4f55\u4e0d\u5165\u672c\u5ea7\u7684\u5c45\u4f4f\u4e4b\u6240\u2026\u2026\u201d \u3000\u3000\u957f\u957f\u7684\u5401\u4e86\u53e3\u6c14\uff0c\u63a5\u9053\uff1a\u201c\u4f46\u8001\u5f1f\u9732\u5bbf\u5c4b\u5916\u8349\u83bd\uff0c\u4e0d\u662f\u672c\u6559\u5f85\u5ba2\u4e4b\u9053\u554a\uff01\u5018\u662f\u6559\u4e3b\u62b5\u6b64\u77e5\u9053\uff0c\u672c\u5ea7\u5c11\u4e0d\u5f97\u8981\u6328\u4e00\u987f\u9a82\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8d35\u6559\u4e3b\u5982\u6709\u8d23\u95ee\uff0c\u533a\u533a\u81ea\u4f1a\u4e3a\u4f60\u62c5\u4ee3\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u4e0d\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u9002\u624d\u5c0a\u9a7e\u8a00\u4e0b\u4e4b\u610f\uff0c\u8d35\u6559\u4e3b\u4e00\u65e6\u6765\u6b64\uff0c\u5341\u4e4b\u4e03\u516b\uff0c\u8981\u62ff\u533a\u533a\u6cbb\u7f6a\uff0c\u4e0d\u77e5\u5c0a\u9a7e\u53c8\u4e3a\u4f55\u4ee5\u5bbe\u5ba2\u8eab\u4efd\u7559\u4f4f\u5728\u4e0b\uff0c\u83ab\u975e\u90a3\u7530\u8fdc\u65e9\u5c31\u8be5\u6b7b\u4e86\u5417\uff1f\u201d \u3000\u3000\u5982\u6b64\u4e00\u95ee\uff0c\u628a\u9ad8\u65e0\u654c\u95ee\u5f97\u534a\u664c\u65e0\u8a00\u76f8\u5bf9\u3002 \u3000\u3000\u539f\u6765\uff0c\u8fd9\u795e\u529b\u5929\u738b\u9ad8\u65e0\u654c\u6240\u4ee5\u8981\u7559\u4e0b\u65b9\u96ea\u5b9c\uff0c\u4e43\u662f\u6709\u4e86\u4e00\u7247\u79c1\u5fc3\uff0c\u4ed6\u76ee\u7779\u65b9\u96ea\u5b9c\u51fa\u624b\u7684\u5251\u6cd5\uff0c\u7b80\u76f4\u662f\u9ad8\u660e\u5f97\u81ea\u5df1\u610f\u60f3\u4e0d\u5230\uff0c\u5bb9\u5f97\u9b54\u5200\u7530\u8fdc\u88ab\u4ed6\u4e00\u5251\u7a7f\u80f8\u81f4\u6b7b\uff0c\u66f4\u4f7f\u4ed6\u60ca\u51db\u83ab\u540d\uff0c\u9ad8\u65e0\u654c\u5fc3\u4e2d\u6709\u6570\uff0c\u65b9\u96ea\u5b9c\u8ddf\u4ed6\u8981\u53cd\u8138\u4e4b\u65f6\uff0c\u4e5f\u53ef\u771f\u7684\u6697\u6697\u803d\u5fc3\uff0c\u867d\u7136\u4ed6\u6bd4\u7530\u8fdc\u5f3a\u4e86\u4e00\u4e9b\uff0c\u53ef\u662f\uff0c\u7edd\u5bf9\u65e0\u6cd5\u90a3\u4e48\u5bb9\u6613\u6740\u6b7b\u5bf9\u65b9\u3002 \u3000\u3000\u867d\u7136\u8868\u800c\u4e0a\u770b\uff0c\u9ad8\u65e0\u654c\u4f3c\u662f\u4e2a\u4e0d\u61c2\u5fc3\u673a\u7684\u83bd\u592b\uff0c\u5b9e\u5219\uff0c\u8fd9\u7b49\u6b66\u529f\u9ad8\u4f4e\uff0c\u53ef\u5224\u751f\u6b7b\u7684\u5927\u4e8b\uff0c\u4ed6\u4e5f\u4f1a\u7c97\u4e2d\u6709\u7ec6\uff0c\u4e0d\u6562\u5192\u5931\uff0c\u56e0\u6b64\uff0c\u65b9\u96ea\u5b9c\u5018\u5982\u771f\u8981\u8ddf\u4ed6\u52a8\u624b\uff0c\u4ed6\u7edd\u5bf9\u4e0d\u6562\u5e94\u5141\u3002 \u3000\u3000\u5e78\u800c\u65b9\u96ea\u5b9c\u6b63\u60f3\u5957\u4ed6\u7684\u8bdd\uff0c\u800c\u7f13\u548c\u4e86\u53e3\u6c14\uff0c\u4e8e\u662f\u4e88\u4ed6\u4ee5\u501f\u53e3\u4e4b\u673a\uff0c\u4e0d\u501f\u9053\u5c3d\u81ea\u5df1\u6559\u4e2d\u5f3a\u654c\u538b\u5883\u4e4b\u4e8b\uff0c\u60f3\u535a\u53d6\u65b9\u96ea\u5b9c\u7684\u76f8\u4fe1\uff0c\u7a76\u5176\u79c1\u5fc3\u6240\u5728\uff0c\u4e0d\u8fc7\u662f\u60f3\u7559\u4e0b\u65b9\u96ea\u5b9c\u6765\uff0c\u5171\u5fa1\u5f3a\u654c\uff0c\u4e5f\u53ea\u6709\u4ed6\u660e\u767d\uff0c\u6559\u4e3b\u77ed\u671f\u4e4b\u5185\uff0c\u4e0d\u4f1a\u6765\u6b64\uff0c\u653e\u7740\u8fd9\u7b49\u5927\u597d\u5e2e\u624b\uff0c\u7528\u6765\u5bf9\u4ed8\u90a3\u4e94\u5927\u9b54\u4e3b\u4e2d\u7684\u4e00\u4f4d\uff0c\u4ee4\u81ea\u5df1\u501f\u4ed6\u4e4b\u529b\uff0c\u60f3\u5fc5\u662f\u53ef\u4ee5\u7a33\u64cd\u80dc\u7b97\u7684\u4e86\u3002 \u3000\u3000\u56e0\u6b64\uff0c\u4ed6\u624d\u4e00\u518d\u633d\u7559\u65b9\u96ea\u5b9c\u3002 \u3000\u3000\u4f46\u65b9\u96ea\u5b9c\u8fd9\u4e48\u4e00\u95ee\uff0c\u5374\u53c8\u628a\u4ed6\u7ed9\u95ee\u4f4f\u4e86\uff0c\u4e00\u65f6\u95f4\uff0c\u65e0\u8a00\u4ee5\u5bf9\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u89c1\u4ed6\u6c89\u541f\u4e0d\u8bed\uff0c\u7b11\u9053\uff1a\u201c\u5c0a\u9a7e\u4e3a\u4f55\u6c89\u541f\uff0c\u83ab\u975e\u533a\u533a\u8bf4\u5bf9\u4e86\u5417\uff1f\u201d \u3000 \u7b2c\u5341\u4e09\u56de\u3000\u6743\u5b9c\u4e4b\u8ba1 \u3000 \u3000\u3000\u9ad8\u65e0\u654c\u65e0\u53ef\u5948\u4f55\u5730\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8001\u5f1f\u867d\u7136\u4e0d\u4f1a\u5b8c\u5168\u8bf4\u5bf9\uff0c\u4f46\u9ad8\u67d0\u4eba\u51b3\u65e0\u5411\u4f60\u62a5\u590d\u4e4b\u5fc3\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u4e00\u70b9\u6211\u65e9\u5df2\u77a7\u51fa\u6765\u3002\u201d\u987f\u4e86\u4e00\u987f\uff0c\u76b1\u7709\u63a5\u9053\uff1a\u201c\u4f46\u5728\u4e0b\u4e0d\u4e86\u89e3\u7684\u662f\uff0c\u4f60\u5c06\u5982\u4f55\u5411\u6559\u4e3b\u89e3\u8bf4\u7530\u8fdc\u81f4\u6b7b\u4e4b\u56e0\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u7b11\u9053\uff1a\u201c\u8001\u5f1f\u4e0d\u5fc5\u4e3a\u6b64\u803d\u5fc3\uff0c\u672c\u5ea7\u81ea\u6709\u9053\u7406\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u77a7\u4ed6\u8fd9\u79cd\u795e\u6001\uff0c\u5fc3\u4e2d\u5ffd\u7136\u7075\u5149\u4e00\u73b0\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u4e00\u6123\u9053\uff1a\u201c\u8001\u5f1f\u660e\u767d\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4f60\u4eec\u6559\u4e3b\uff0c\u53ef\u662f\u6700\u8fd1\u4e0d\u4f1a\u6765\u6b64\u5417\uff1f\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u8fd9\u4e8b\u54b1\u5df2\u5411\u8001\u5f1f\u8bf4\u8fc7\u4e86\uff0c\u6559\u4e3b\u884c\u8e2a\u98d8\u5ffd\uff0c\u672c\u5ea7\u600e\u6562\u65ad\u8a00\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4e86\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u987f\u65f6\u8138\u8272\u5927\u53d8\uff0c\u55eb\u5685\u5730\u9053\uff1a\u201c\u8fd9\u2026\u2026\u672c\u5ea7\u2026\u2026\u672c\u5ea7\u2026\u2026\u201d\u6562\u60c5\u4ed6\u7528\u5c3d\u4e86\u5fc3\u601d\uff0c\u4e5f\u60f3\u4e0d\u51fa\u65b9\u96ea\u5b9c\u56e0\u4f55\u731c\u51fa\u4e86\u81ea\u5df1\u7684\u79c1\u5fc3\uff1f\u4e00\u65f6\u95f4\uff0c\u4ed6\u7b54\u4e0d\u51fa\u9053\u7406\u6765\u4e86\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4f60\u7528\u4e0d\u7740\u9690\u7792\uff0c\u65b9\u67d0\u867d\u7136\u5e74\u8f7b\uff0c\u4f46\u5374\u5411\u6765\u4e0d\u5bb9\u6613\u4e0a\u5f53\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u95fb\u8a00\uff0c\u65e2\u89c9\u60ed\u6127\u81ea\u5df1\u7684\u9a97\u672f\u4f4e\u52a3\uff0c\u53c8\u5fc3\u4e2d\u5927\u4e3a\u7740\u6025\u7684\u53eb\u9053\uff1a\u201c\u8001\u5f1f\u53ef\u662f\u4e0d\u80af\u7559\u6b64\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e25\u8083\u4ed6\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u672c\u6765\u5728\u8bc6\u7834\u4f60\u7684\u7528\u5fc3\u4ee5\u540e\uff0c\u4e0d\u6253\u7b97\u518d\u7559\u4e0b\u6765\u4e86\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u5931\u671b\u5730\u53f9\u606f\u9053\uff1a\u201c\u672c\u5ea7\u4e0d\u6562\u5f3a\u7559\u8001\u5f1f\u2026\u2026\u201d\u8a00\u4e0b\u4e4b\u610f\u663e\u7136\u5df2\u5728\u8ba4\u9519\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u51b7\u7b11\u9053\uff1a\u201c\u53ea\u662f\uff0c\u533a\u533a\u5148\u524d\u5df2\u7136\u8bf4\u8fc7\uff0c\u5728\u4e0b\u884c\u4e8b\u8a00\u51fa\u5fc5\u884c\uff0c\u6240\u4ee5\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u4f59\u97f3\u672a\u7edd\uff0c\u9ad8\u65e0\u654c\u5df2\u5927\u58f0\u9053\uff1a\u201c\u8001\u5f1f\u8fd8\u80af\u7559\u4e0b\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8c6a\u723d\u7684\u9053\uff1a\u201c\u4f60\u8fd9\u7b49\u60f3\u5957\u6211\u7559\u4e0b\u7684\u7528\u5fc3\uff0c\u679c\u771f\u5341\u5206\u53ef\u6076\uff0c\u4f46\u76ee\u4e0b\u4f60\u5904\u5883\u53c8\u53ef\u601c\uff0c\u56e0\u6b64\uff0c\u6211\u4f9d\u7136\u5982\u7ea6\u7559\u6b64\u4e09\u65e5\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u559c\u9053\uff1a\u201c\u591a\u8c22\u8001\u5f1f\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u77a7\u4ed6\u4e00\u7247\u771f\u8bda\u4e4b\u610f\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u4eba\u5012\u8fd8\u4e0d\u4f1a\u574f\u5230\u4e0d\u53ef\u6536\u62fe\uff0c\u5982\u662f\u81ea\u5df1\u80fd\u591f\u65bd\u6069\u4e8e\u4ed6\uff0c\u89e3\u4ed6\u76ee\u4e0b\u4e4b\u5371\uff0c\u7109\u77e5\u4e0d\u662f\u66ff\u6b66\u6797\u6b63\u9053\u529b\u91cf\uff0c\u589e\u4e00\u52a9\u76ca\u3002\u201d \u3000\u3000\u539f\u6765\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u5df2\u7136\u6697\u6697\u89c9\u51fa\uff0c\u8fd9\u56db\u7075\u6559\u65e2\u6709\u7530\u8fdc\u4f5c\u4e3a\u62a4\u6cd5\uff0c\u90a3\u81ea\u7136\u4e5f\u662f\u5c31\u9b54\u9053\u4e2d\u4eba\u6240\u521b\uff0c\u8fd9\u4f4d\u795e\u529b\u5929\u738b\u65e2\u662f\u9996\u5ea7\uff0c\u5728\u6559\u4e2d\u5e94\u662f\u6709\u7740\u76f8\u5f53\u6743\u52bf\uff0c\u81ea\u5df1\u5018\u80fd\u670d\u6b64\u4e00\u4eba\uff0c\u8bf4\u4e0d\u5b9a\u5c31\u7b49\u4e8e\u6697\u6697\u62e2\u7edc\u4e86\u56db\u7075\u6559\u56db\u5206\u4e4b\u4e00\u7684\u654c\u4eba\u3002\u4e00\u5ff5\u53ca\u6b64\uff0c\u5f53\u4e0b\u5927\u7b11\u9053\uff1a\u201c\u9ad8\u5144\u4e0d\u5fc5\u5ba2\u6c14\uff0c\u5144\u5f1f\u51b3\u5fc3\u7559\u4e0b\uff0c\u52a9\u4f60\u7834\u5f3a\u654c\u3002\u201d \u3000\u3000\u53e3\u6c14\u4e00\u53d8\uff0c\u4f7f\u90a3\u9ad8\u65e0\u654c\u5927\u611f\u53d7\u7528\uff0c\u5fd9\u9053\uff1a\u201c\u65b9\u5144\u5982\u80af\u7559\u4e0b\uff0c\u6211\u9ad8\u65e0\u654c\u4e00\u8f88\u5b50\u90fd\u611f\u6fc0\u4f60\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u5934\u7b11\u9053\uff1a\u201c\u9ad8\u5144\u8a00\u91cd\u4e86\u3002\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u65e5\u5185\u6765\u6b64\u7684\u5f3a\u654c\uff0c\u9ad8\u5144\u53ef\u77e5\u4e94\u5927\u9b54\u4e3b\u4e2d\u7684\u54ea\u4e00\u4f4d\uff1f\u201d \u3000\u3000\u9ad8\u65e0\u654c\u5c34\u5c2c\u4ed6\u8bf4\u9053\uff1a\u201c\u8fd9\u2026\u2026\u8fd9\u8fde\u5728\u4e0b\u4e5f\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u5f97\u5927\u611f\u8be7\u5f02\uff0c\u8131\u53e3\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u77e5\u9053\u662f\u54ea\u4e00\u4f4d\u5417\uff1f\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u65b9\u5144\u5f1f\u662f\u5426\u4e0d\u4fe1\uff0c\u5176\u5b9e\uff0c\u672c\u6559\u6559\u4e3b\u4e5f\u662f\u4e94\u9b54\u4e4b\u4e00\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u76ee\u5149\u4e00\u4eae\uff0c\u5fd6\u9053\uff1a\u201c\u679c\u7136\u4e0d\u9519\uff0c\u8fd9\u56db\u7075\u6559\u4e5f\u662f\u4e94\u9b54\u6559\u4e2d\u4eba\u6240\u624b\u521b\uff0c\u5947\u602a\u7684\u662f\uff0c\u4ed6\u4eec\u4e4b\u95f4\uff0c\u96be\u9053\u4e5f\u6709\u4ec0\u4e48\u8fc7\u4e0d\u53bb\u4e4b\u5904\uff0c\u5f7c\u6b64\u7ed3\u6709\u4ec7\u6028\u545c\uff1f\u201d\u679c\u771f\u5982\u6b64\uff0c\u90a3\u5012\u662f\u4e00\u4e2a\u5f88\u503c\u5f97\u9ad8\u5174\u7684\u6d88\u606f\u4e86\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u6545\u4f5c\u8be7\u5f02\u4e0d\u5b89\u7684\u795e\u8272\uff0c\u95ee\u9053\uff1a\u201c\u8d35\u6559\u4e3b\u4e5f\u662f\u4e94\u9b54\u4e4b\u4e00\u5417\uff1f\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u4f60\u89c1\u8fc7\u4e94\u5927\u9b54\u4e3b\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u6ca1\u6709\u89c1\u8fc7\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u4ee5\u5144\u5f1f\u8fd9\u7b49\u5e74\u7eaa\uff0c\u7167\u8bf4\u4e5f\u5e94\u662f\u4e0d\u66fe\u89c1\u8fc7\u4e94\u5927\u9b54\u4e3b\u3002\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u5927\u4e3a\u611f\u6168\u5730\u957f\u957f\u5401\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u81ea\u4ece\u90a3\u88ab\u4eba\u79f0\u4f5c\u4e3a\u5251\u795e\u7684\u9648\u5e0c\u6b63\u5927\u4fa0\u4ee5\u6b66\u529f\u6151\u773c\u4e94\u5927\u9b54\u4e3b\u4ee5\u6765\uff0c\u54b1\u4eec\u5144\u5f1f\u53ef\u771f\u662f\u82e6\u4e0d\u582a\u8a00\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6697\u9053\uff1a\u201c\u4e94\u9b54\u7b49\u4eba\u5ffd\u7136\u5927\u63d0\u7ec4\u5e2e\uff0c\u83ab\u975e\u4ed6\u4eec\u5df2\u7ecf\u77e5\u9053\u4e86\u5e08\u7236\u7684\u6b7b\u8baf\u4e86\u5417\uff1f\u6211\u4f55\u4e0d\u5957\u5957\u4ed6\u7684\u53e3\u6c14\uff1f\u201d\u5f53\u4e0b\u653e\u9732\u540c\u60c5\u4e4b\u8272\uff0c\u9053\uff1a\u201c\u542c\u5c0a\u9a7e\u4e4b\u8a00\uff0c\u90a3\u9648\u5927\u4fa0\u53ef\u662f\u4f60\u4eec\u7684\u4ec7\u5bb6\u5417\uff1f\u201d \u3000\u3000\u9ad8\u65e0\u654c\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e0d\u7792\u4f60\u8001\u5f1f\u8bf4\uff0c\u5251\u795e\u9648\u5e0c\u6b63\uff0c\u6b63\u662f\u6211\u4eec\u7684\u6b7b\u654c\uff0c\u4e94\u5927\u9b54\u4e3b\u9000\u51fa\u6c5f\u6e56\u6570\u5341\u4f59\u8f7d\uff0c\u4e3b\u8981\u7684\u5c31\u662f\u88ab\u5251\u795e\u6240\u8d25\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u7b11\u9053\uff1a\u201c\u8fd9\u8fd8\u8981\u4f60\u6765\u8bf4\u5417\uff1f\u201d\u4f46\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u4e94\u5927\u9b54\u4e3b\u518d\u73b0\u6b66\u6797\uff0c\u83ab\u975e\u662f\u5df2\u7ecf\u7ec3\u5c31\u4e00\u8eab\u6b66\u529f\uff0c\u8db3\u4e0e\u5251\u795e\u9648\u5927\u4fa0\u76f8\u6297\u5417\uff1f\u201d \u3000\u3000\u9ad8\u65e0\u654c\u6447\u5934\u9053\uff1a\u201c\u8fd9\u4e2a\u5144\u5f1f\u5e76\u4e0d\u660e\u767d\uff0c\u4f46\u672c\u6559\u6559\u4e3b\u516c\u5f00\u521b\u7acb\u56db\u7075\u6559\uff0c\u5fc5\u7136\u662f\u65e0\u987b\u518d\u6015\u90a3\u5251\u795e\u4e86\u3002\u201d\u6562\u60c5\uff0c\u4ed6\u5e76\u4e0d\u77e5\u9053\u9648\u5e0c\u6b63\u5df2\u6b7b\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u77e5\u9053\u8fd9\u4f4d\u9996\u5ea7\u5e76\u4e0d\u5982\u81ea\u5df1\u60f3\u8c61\u90a3\u4e48\u806a\u660e\uff0c\u767b\u65f6\u4e0d\u518d\u591a\u95ee\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u9ad8\u5144\u8bf7\u5feb\u6d3e\u4eba\u5b89\u7f6e\u8fd9\u9b54\u5200\u7530\u8fdc\u7684\u5c38\u4f53\uff0c\u5728\u4e0b\u544a\u9000\u4e86\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u5144\u5f1f\u9075\u547d\u2026\u2026\u201d\u8bdd\u51fa\u53e3\uff0c\u624d\u60f3\u8d77\u65b9\u96ea\u5b9c\u90a3\u53e5\u544a\u9000\u7684\u8bdd\uff0c\u8fde\u5fd9\u53c8\u63a5\u9053\uff1a \u3000\u3000\u201c\u65b9\u8001\u5f1f\uff0c\u4f60\u8981\u8d70\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u5e76\u975e\u8fdc\u53bb\uff0c\u5c31\u5728\u8fd9\u5c71\u540e\u6253\u5750\u4e00\u523b\uff0c\u4e09\u4e2a\u65f6\u8fb0\u4e4b\u540e\uff0c\u518d\u6765\u4e0e\u9ad8\u5144\u628a\u6664\u2026\u2026\u201d\u7565\u4e00\u62b1\u62f3\uff0c\u4fbf\u81ea\u8f6c\u8eab\u884c\u53bb\u3002 \u3000\u3000\u9ad8\u65e0\u654c\u76ee\u9001\u65b9\u96ea\u5b9c\u80cc\u5f71\u6d88\u5931\u8349\u4e1b\u4e4b\u4e2d\uff0c\u8fd9\u624d\u62db\u547c\u624b\u4e0b\u4eba\uff0c\u63a9\u57cb\u9b54\u5200\u7530\u8fdc\u7684\u5c38\u4f53\uff0c\u5e76\u5c06\u90a3\u67c4\u6210\u540d\u7684\u53e4\u5200\uff0c\u4e00\u8d77\u846c\u5728\u7530\u8fdc\u575f\u83b9\u4e4b\u5185\u3002 \u3000\u3000\u4e14\u8bf4\u65b9\u96ea\u5b9c\u8fc5\u5feb\u7684\u8f6c\u5165\u8349\u4e1b\u4e4b\u4e2d\uff0c\u7ea6\u884c\u5341\u4e08\u5de6\u53f3\uff0c\u5ffd\u7136\u6298\u5411\u53f3\u4fa7\u7684\u4e00\u5e26\u5c71\u4e18\u4e4b\u540e\uff0c\u4e3e\u76ee\u671b\u53bb\uff0c\u679c\u7136\u77a7\u89c1\u4e00\u4f4d\u8001\u4eba\uff0c\u5750\u5728\u4e00\u5757\u9752\u77f3\u4e0a\uff0c\u77aa\u7740\u4e24\u773c\uff0c\u5411\u81ea\u5df1\u9f87\u7259\u88c2\u5634\uff0c\u76f4\u4f5c\u602a\u7b11\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u4e00\u9707\uff0c\u4ed8\u9053\uff1a\u201c\u5f53\u771f\u662f\u4ed6\uff01\u201d \u3000\u3000\u539f\u6765\u8fd9\u53d1\u7b11\u4e4b\u4eba\uff0c\u6b63\u662f\u5728\u90a3\u8336\u9986\u4e2d\uff0c\u81ea\u79f0\u5b8b\u8001\u90aa\u7684\u8001\u4eba\uff0c\u65b9\u96ea\u5b9c\u5728\u62b5\u8fbe\u8305\u820d\u65f6\uff0c\u5c31\u5df2\u89c9\u5f97\u6709\u4eba\u8ffd\u5728\u81ea\u5df1\u8eab\u540e\u800c\u6765\uff0c\u53ea\u9053\u8fd9\u8ffd\u8e2a\u4e4b\u4eba\uff0c\u4e43\u662f\u5b97\u58eb\u4e49\u7684\u5c5e\u4e0b\uff0c\u5b70\u6599\u7adf\u662f\u8fd9\u4f4d\u9ad8\u4eba\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u5012\u53eb\u4ed6\u6728\u7acb\u5f53\u5730\uff0c\u6014\u6014\u5730\u53d1\u5446\u4e0d\u5df2\u3002 \u3000\u3000\u90a3\u5b8b\u8001\u90aa\u77a7\u7740\u65b9\u96ea\u5b9c\u7684\u5446\u76f8\uff0c\u4e0d\u7981\u5927\u58f0\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u771f\u662f\u4eba\u751f\u4f55\u5904\u4e0d\u76f8\u9002\uff0c\u54b1\u4eec\u4e00\u65e5\u4e4b\u95f4\uff0c\u5c45\u7136\u4e09\u6b21\u78b0\u5728\u4e00\u8d77\uff0c\u8bf4\u6765\u5e94\u662f\u7f18\u4efd\u4e0d\u6d45\u554a\uff01\u201d\u8bed\u97f3\u672a\u5df2\uff0c\u84e6\u5730\u4ef0\u5929\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5251\u7709\u4e00\u76b1\uff0c\u6697\u9053\uff1a\u201c\u6211\u660e\u660e\u624d\u8ddf\u4f60\u78b0\u7b2c\u4e8c\u9762\uff0c\u4f60\u600e\u4ed6\u8bf4\u662f\u4e00\u65e5\u95f4\uff0c\u4e09\u6b21\u78b0\u5728\u4e00\u8d77\uff1f\u663e\u7136\u8fd9\u5b8b\u8001\u90aa\u4e86\u4e0a\u5e74\u7eaa\uff0c\u8bb0\u4e8b\u6709\u4e9b\u98a0\u4e09\u5012\u56db\u4e86\u5427\uff01\u201d\u65b9\u96ea\u5b9c\u7a76\u7adf\u4e0d\u5931\u81f3\u8bda\u541b\u5b50\uff0c\u867d\u7136\u5fc3\u4e2d\u89c9\u5f97\u8fd9\u4f4d\u8001\u4eba\u6709\u4e9b\u8a00\u8bed\u98a0\u5012\uff0c\u53e3\u4e2d\u5374\u5341\u5206\u5fe0\u539a\uff0c\u53cc\u624b\u4e00\u62f1\uff0c\u7b11\u9053\uff1a\u201c\u8001\u4e08\u8bf4\u7b11\u4e86\u3002\u201d \u3000\u3000\u90a3\u5b8b\u8001\u90aa\u76ee\u5149\u4e00\u8f6c\uff0c\u5ffd\u7136\u4f4e\u559d\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u597d\u5927\u7684\u80c6\u5b50\uff0c\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u4e00\u9519\u5c82\u53ef\u518d\u9519\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u5446\u9053\uff1a\u201c\u8001\u4e08\u4f55\u51fa\u6b64\u8a00\uff1f\u665a\u8f88\u51e0\u65f6\u505a\u9519\u4e86\u4ec0\u4e48\u4e8b\u6765\uff1f\u201d \u3000\u3000\u5b8b\u8001\u90aa\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u6ca1\u6709\u4f5c\u9519\u4e8b\uff0c\u5251\u6740\u9b54\u5200\u7530\u8fdc\uff0c\u4e0d\u5c31\u662f\u4e00\u6869\u5927\u4e0d\u5e94\u8be5\u7684\u9519\u4e8b\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u60f3\uff1a\u201c\u539f\u6765\u662f\u4e3a\u4e86\u8fd9\u4e2a\uff1f\u60f3\u5fc5\u8fd9\u8001\u4eba\u4e0d\u77e5\u9053\u6211\u8ddf\u7530\u8fdc\u4e4b\u95f4\u7684\u8fc7\u8282\uff0c\u81ea\u662f\u4e0d\u80fd\u602a\u4ed6\u4e86\u3002\u201d \u3000\u3000\u5f53\u4e0b\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u7530\u8fdc\u4e0e\u665a\u8f88\u65e9\u6709\u8fc7\u8282\uff0c\u6211\u5982\u4e0d\u5c06\u4ed6\u6740\u6b7b\uff0c\u5219\u6b7b\u7684\u5fc5\u5c06\u662f\u6211\u4e86\u3002\u201d \u3000\u3000\u5b8b\u8001\u90aa\u4e24\u773c\u4e00\u7ffb\uff0c\u6012\u9053\uff1a\u201c\u80e1\u8bf4\uff0c\u4f60\u5f53\u8001\u592b\u662f\u778e\u5b50\u5417\uff1f\u4f11\u8bf4\u4f60\u70b9\u5e74\u7eaa\uff0c\u6839\u672c\u4e0d\u53ef\u80fd\u8ddf\u90a3\u9b54\u5200\u7530\u8fdc\u7ed3\u4e0b\u6740\u8eab\u5927\u4ec7\uff0c\u5c31\u4ece\u4f60\u4e3e\u624b\u4e4b\u95f4\uff0c\u5373\u53ef\u7f6e\u4ed6\u4e8e\u6b7b\u5730\u7684\u6b66\u529f\u800c\u8a00\uff0c\u7eb5\u7136\u90a3\u7530\u8fdc\u6709\u5929\u5927\u7684\u80c6\u5b50\uff0c\u4ed6\u4e5f\u4e0d\u4f1a\u8ddf\u4f60\u7ed3\u4ec7\u5427\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u88ab\u4ed6\u559d\u65a5\u5f97\u4e00\u6014\uff0c\u8be7\u5f02\u5730\u95ee\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u8001\u4e08\u8ba4\u4e3a\u90a3\u7530\u8fdc\u4e0d\u6562\u4e0e\u665a\u8f88\u7ed3\u4ec7\u5462\uff1f\u201d \u3000\u3000\u5b8b\u8001\u90aa\u51b7\u7b11\u9053\uff1a\u201c\u8001\u592b\u5982\u4e0d\u8bf4\u51fa\uff0c\u4f60\u5b9a\u7136\u65e0\u6cd5\u4e86\u89e3\u5176\u4e2d\u9053\u7406\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u6b63\u8981\u8bf7\u6559\u6307\u70b9\uff1f\u201d \u3000\u3000\u5b8b\u8001\u90aa\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u8001\u592b\u8bf4\u51fa\u6765\uff0c\u5374\u4e5f\u5e73\u5e38\u5f97\u5f88\uff0c\u53ea\u56e0\u90a3\u8eab\u5728\u9b54\u9053\u7684\u90aa\u6076\u4eba\u7269\uff0c\u4e2a\u4e2a\u90fd\u662f\u8d2b\u751f\u6015\u6b7b\u4e4b\u5f92\uff0c\u8bd5\u60f3\u4e00\u4e2a\u6015\u6b7b\u4e4b\u4eba\uff0c\u53c8\u54ea\u6709\u80c6\u91cf\u8ddf\u6b66\u529f\u9ad8\u8fc7\u81ea\u5df1\u592a\u591a\u4e4b\u4eba\u7ed3\u4ec7\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u8001\u4e08\u679c\u7136\u6709\u4e9b\u89c1\u5730\u3002\u201d \u3000\u3000\u5b8b\u8001\u90aa\u6447\u5934\u63a5\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u6b32\u77e5\u6b63\u90aa\u4e4b\u5206\uff0c\u5c31\u5f97\u770b\u4ed6\u5728\u751f\u6b7b\u5229\u4e49\u4e4b\u95f4\u7684\u9009\u62e9\uff0c\u4ed6\u89c1\u9053\u4e49\u6240\u5728\uff0c\u867d\u660e\u77e5\u4e0d\u53ef\u4e3a\u800c\u4ecd\u7136\u4e0d\u7a0d\u5374\u6b65\uff0c\u8fd9\u7b49\u4eba\u5c31\u662f\u4fa0\u4e49\u4e4b\u58eb\uff0c\u53cd\u4e4b\uff0c\u53ea\u61c2\u56e0\u65f6\u5c31\u52bf\uff0c\u81ea\u79c1\u4fdd\u547d\uff0c\u4e43\u81f3\u89c6\u540d\u8282\u5982\u513f\u620f\u7684\u5bb6\u4f19\uff0c\u5c31\u662f\u90aa\u9b54\u4e4b\u8f88\u2026\u2026\u201d \u3000\u3000\u8fd9\u4e00\u756a\u8bdd\uff0c\u53ea\u628a\u65b9\u96ea\u5b9c\u542c\u5f97\u5fc3\u4e2d\u5927\u9707\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u4f4d\u8001\u4e08\u662f\u4f55\u8bb8\u4eba\u7269\uff0c\u7adf\u7136\u5224\u65ad\u4eba\u5fc3\u5584\u6076\uff0c\u5927\u6709\u4e00\u9488\u89c1\u8840\uff0c\u5165\u6728\u4e09\u5206\u4e4b\u6982\uff1f\u770b\u6765\u6b64\u4eba\u662f\u5927\u53ef\u4ea4\u5f80\u7684\u9ad8\u4eba\u3002\u201d\u5fc3\u5ff5\u4e00\u5b9a\uff0c\u7acb\u5373\u7b11\u9053\uff1a\u201c\u665a\u8f88\u53d7\u6559\u4e86\u3002\u201d \u3000\u3000\u5b8b\u8001\u90aa\u8fd9\u624d\u8f6c\u6012\u4e3a\u559c\u5730\u7b11\u9053\uff1a\u201c\u4e0d\u5fc5\u5ba2\u6c14\uff0c\u4f60\u65e2\u7136\u627f\u8ba4\u8001\u592b\u6240\u8bf4\u4e4b\u540e\uff0c\u4f60\u6740\u4e86\u7530\u8fdc\uff0c\u5c82\u975e\u662f\u9519\u4e86\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u539f\u53ea\u9053\u8fd9\u4f4d\u5b8b\u8001\u90aa\u8bf4\u660e\u4e2a\u4e2d\u9053\u7406\uff0c\u5c31\u4e0d\u4f1a\u518d\u63d0\u53ca\u5230\u81ea\u5df1\u6740\u90a3\u7530\u8fdc\u662f\u5426\u6709\u9519\u7684\u95ee\u9898\uff0c\u800c\u53ef\u80fd\u662f\u4f1a\u8be2\u53ca\u81ea\u5df2\u7684\u51fa\u8eab\u3001\u6765\u5386\uff0c\u600e\u4e48\u8001\u90aa\u504f\u53c8\u8f6c\u56de\u4e86\u7530\u8fdc\u8eab\u4e0a\uff0c\u4ee4\u4ed6\u89c9\u5f97\u4e0d\u5c06\u6614\u65e5\u4e4b\u4e8b\u8bf4\u51fa\uff0c\u5fc5\u96be\u4ece\u5f97\u6b64\u8001\u8c05\u89e3\u4e86\u3002\u4ed6\u7565\u4e00\u6c89\u541f\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u8001\u4e08\u53ef\u662f\u4ecd\u7136\u4e0d\u4fe1\u665a\u8f88\u65e9\u4e0e\u7530\u8fdc\u7ed3\u6709\u6028\u6068\u5417\uff1f\u201d \u3000\u3000\u5b8b\u8001\u90aa\u51b7\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u8001\u592b\u4e0d\u662f\u9002\u624d\u5df2\u7ecf\u9610\u660e\u6b63\u90aa\u4e4b\u5206\u7684\u901a\u7406\u7ed9\u4f60\u542c\u4e86\u5417\uff1f\u90a3\u7530\u8fdc\u4e43\u662f\u9b54\u5200\uff0c\u6cbe\u4e86\u4e00\u4e2a\u9b54\u5b57\uff0c\u81ea\u662f\u7b97\u4e0d\u5f97\u6b63\u9053\u4eba\u7269\uff0c\u4e00\u4e2a\u6015\u6b7b\u7684\u9b54\u5f92\u51e0\u65f6\u4f1a\u62ff\u81ea\u5df1\u6027\u547d\u53bb\u4e89\u6c14\u6765\u7740\uff1f\u8001\u5f1f\uff0c\u4f60\u660e\u660e\u5728\u54c4\u9a97\u8001\u592b\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e0d\u89c9\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u8001\u4e08\uff0c\u665a\u8f88\u6ca1\u6709\u9a97\u4f60\u554a\uff01\u201d \u3000\u3000\u5b8b\u8001\u90aa\u9053\uff1a\u201c\u8001\u592b\u4e0d\u4fe1\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u82e6\u7b11\u8fde\u58f0\u9053\uff1a\u201c\u8001\u4e08\uff0c\u665a\u8f88\u4e0e\u7530\u8fdc\u4e4b\u95f4\u4ec7\u6028\uff0c\u4e43\u662f\u7ed3\u5728\u4e0a\u4e00\u4ee3\u4eba\u7269\u8eab\u4e0a\u2026\u2026\u201d \u3000\u3000\u4e0d\u7b49\u65b9\u96ea\u5b9c\u628a\u8bdd\u8bf4\u5b8c\uff0c\u5b8b\u8001\u90aa\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u597d\u5566\uff01\u8001\u592b\u660e\u767d\u4e86\uff01\u201d\u7b11\u58f0\u4e00\u655b\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u8001\u5f1f\u554a\uff01\u4f60\u8be5\u77e5\u9053\uff0c\u5982\u4eca\u7684\u7530\u8fdc\uff0c\u5df2\u7ecf\u6709\u4e86\u9760\u5c71\uff0c\u6740\u4e86\u4ed6\u4e0d\u5927\u8981\u7d27\uff0c\u5f15\u51fa\u4e86\u4ed6\u90a3\u9760\u5c71\uff0c\u4f60\u8001\u5f1f\u53c8\u600e\u751f\u5bf9\u4ed8\u7684\u5f97\u4e86\uff1f\u8bf4\u6765\u8bf4\u53bb\uff0c\u4f60\u8fd8\u662f\u56e0\u4e3a\u6740\u4e86\u7530\u8fdc\uff0c\u624d\u72af\u4e86\u7b2c\u4e00\u9519\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u4ed6\u81ea\u79f0\u660e\u767d\u4e4b\u540e\uff0c\u4f9d\u7136\u54ac\u5b9a\u81ea\u5df1\u5251\u8bdb\u7530\u8fdc\uff0c\u4e43\u662f\u4e00\u9519\uff0c\u4e0d\u89c9\u5fae\u5fae\u4e00\u5f81\uff0c\u4ed8\u9053\uff1a\u201c\u4ed6\u786c\u8981\u6307\u8d23\u6211\u9519\uff0c\u5373\u4f7f\u591a\u8d39\u5507\u820c\uff0c\u6050\u6015\u4e5f\u6ca1\u6709\u7528\u5904\uff0c\u4f55\u4e0d\u5148\u95ee\u95ee\u4ed6\u6240\u8c13\u518d\u9519\u4f55\u6307\uff0c\u4e5f\u8bb8\u80fd\u627e\u51fa\u4ed6\u7684\u6f0f\u6d1e\u2026\u2026\u201d\u8f6c\u5ff5\u53ca\u6b64\uff0c\u7b11\u9053\uff1a\u201c\u8001\u4e08\u5173\u5fc3\u665a\u8f88\uff0c\u5b9e\u4ee4\u4eba\u611f\u6fc0\uff0c\u4f46\u4e0d\u77e5\u665a\u8f88\u53c8\u9519\u4e86\u54ea\u4e00\u6869\u4e8b\uff1f\u201d \u3000\u3000\u5b8b\u8001\u90aa\u77aa\u773c\u9053\uff1a\u201c\u7b54\u5e94\u90a3\u5927\u5757\u5934\uff0c\u7559\u5728\u8fd9\u513f\u4e09\u65e5\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u4e5f\u7b97\u4e0d\u5f97\u4ec0\u4e48\u9519\uff0c\u8001\u4e08\u8bef\u4f1a\u4e86\uff0c\u665a\u8f88\u7b54\u5e94\u90a3\u9ad8\u65e0\u654c\u7559\u4e0b\uff0c\u4e43\u662f\uff0c\u4e43\u662f\u2026\u2026\u201d\u5ffd\u7136\u60f3\u5230\uff0c\u6211\u4e0e\u8fd9\u8001\u4eba\u5e76\u975e\u6df1\u4ea4\uff0c\u81ea\u5df1\u7684\u5fc3\u4e8b\uff0c\u53c8\u600e\u4e48\u53ef\u4ee5\u968f\u4fbf\u8bf4\u51fa\u6765\u5462\uff1f\u8bdd\u5230\u5634\u8fb9\u54bd\u4e86\u56de\u53bb\u3002 \u3000\u3000\u5b8b\u8001\u90aa\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4e43\u662f\u4ec0\u4e48\uff0c\u5927\u4e0d\u4e86\u4f60\u8001\u5f1f\u60f3\u4ed7\u7740\u4e00\u8eab\u6b66\u529f\uff0c\u626c\u540d\u95ef\u4e07\u72ec\u6597\u90a3\u4e2a\u5927\u9b54\u4e3b\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u65b9\u96ea\u76f4\u63a5\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u665a\u8f88\u4e0d\u4f1a\u5982\u6b64\u5192\u5931\uff1f\u5e94\u5141\u4e8e\u4ed6\uff0c\u4e0d\u8fc7\u662f\u60f3\u8ddf\u4ed6\u4ea4\u670b\u53cb\u800c\u5df2\uff01\u201d \u3000\u3000\u5b8b\u8001\u90aa\u5927\u7b11\u9053\uff1a\u201c\u4f60\u60f3\u4ea4\u8fd9\u7b49\u670b\u53cb\u5417\uff0c\u8001\u5f1f\uff0c\u4ed6\u53ef\u4e5f\u662f\u4e94\u5927\u9b54\u4e3b\u624b\u4e0b\u4e4b\u4eba\u5475\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u70b9\u5934\u9053\uff1a\u201c\u8fd9\u4e00\u70b9\u665a\u8f88\u6ca1\u6709\u5fd8\u8bb0\u3002\u201d \u3000\u3000\u5b8b\u8001\u90aa\u63a5\u9053\uff1a\u201c\u65e2\u662f\u660e\u767d\uff0c\u5c31\u4e0d\u8be5\u4e0e\u4ed6\u4ea4\u5f80\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5ffd\u7136\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8001\u4e08\uff0c\u4e5f\u8bb8\u665a\u8f88\u662f\u4e0d\u5f53\u4e0e\u4ed6\u7ed3\u4ea4\uff0c\u4f46\u662f\uff0c\u665a\u8f88\u5374\u53d1\u73b0\uff0c\u5176\u4eba\u826f\u77e5\u672a\u6cef\uff0c\u5c1a\u975e\u65e0\u53ef\u6551\u836f\u4e4b\u8f88\u3002\u201d \u3000\u3000\u5b8b\u8001\u90aa\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4f60\u2026\u2026\u201d\u5ffd\u7136\u54c8\u54c8\u5927\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u554a\uff01\u539f\u6765\u4f60\u8001\u5f1f\u4e43\u662f\u4e00\u4f4d\u6709\u5fc3\u4e4b\u4eba\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u62f1\u624b\u7b11\u9053\uff1a\u201c\u665a\u8f88\u4eba\u5fae\u624d\u8f7b\uff0c\u53ea\u6015\u96be\u4ee5\u4f7f\u4ed6\u5e61\u7136\u89c9\u609f\uff0c\u672c\u8d39\u5de5\u592b\uff0c\u8001\u4e08\u83ab\u8981\u89c1\u7b11\u3002\u201d \u3000\u3000\u5b8b\u8001\u90aa\u9053\uff1a\u201c\u4e0d\u4f1a\uff0c\u8001\u592b\u4e00\u4e07\u4e2a\u4e0d\u4f1a\u3002\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u8001\u5f1f\u8fd9\u7247\u4ec1\u4e49\u4e4b\u5fc3\uff0c\u8001\u592b\u591a\u5c11\u5df2\u7ecf\u660e\u767d\uff0c\u5982\u679c\u8001\u5f1f\u4e0d\u5f03\uff0c\u8001\u673d\u6df1\u613f\u52a9\u4f60\u5b8c\u6b64\u529f\u5fb7\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u6697\u9053\uff1a\u4ed6\u5012\u786c\u662f\u627e\u4e0a\u95e8\u6765\u4e86\uff0c\u6211\u5982\u662f\u62d2\u7edd\u4e8e\u4ed6\uff0c\u5b9a\u4f1a\u5f15\u8d77\u4ed6\u7684\u4e0d\u5feb\uff0c\u53cd\u6b63\u5e08\u7236\u8bf4\u8fc7\uff0c\u6276\u6301\u6b66\u6797\u6b63\u4e49\uff0c\u5fc5\u5f97\u591a\u7ed3\u5408\u51e0\u4e2a\u56de\u9053\uff0c\u6211\u4f55\u4e0d\u7b54\u5e94\u4e8e\u4ed6\u5462\uff1f\u5fc3\u5ff5\u4e00\u5b9a\uff0c\u7acb\u5373\u7b11\u9053\uff1a\u201c\u591a\u8c22\u8001\u4e08\u6210\u5168\u76f8\u52a9\u4e4b\u610f\uff0c\u4f46\u4e0d\u77e5\u8001\u8f88\u600e\u4e48\u79f0\u547c\uff1f\u201d \u3000\u3000\u5b8b\u8001\u90aa\u5927\u7b11\u9053\uff1a\u201c\u8001\u592b\u7684\u540d\u53f7\uff0c\u5e84\u6377\u4e0d\u662f\u544a\u8bc9\u8fc7\u4f60\u4e86\u5417\uff0c\u600e\u5730\u4f60\u8fd8\u4e0d\u66fe\u731c\u51fa\u6765\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u5927\u9707\uff0c\u8131\u53e3\u9053\uff1a\u201c\u539f\u6765\u4f60\u8001\u5c31\u662f\u5927\u6f20\u9b3c\u2014\u2014\u795e\u624b\u5b8b\u8001\u524d\u8f88\u5440\uff01\u201d\u4ed6\u4e3a\u4eba\u5fe0\u539a\uff0c\u53ea\u89c9\u90a3\u5927\u6f20\u9b3c\u624b\u7684\u9b3c\u5b57\u51fa\u53e3\u5341\u5206\u4e0d\u96c5\uff0c\u662f\u4ee5\u5373\u65f6\u7075\u673a\u4e00\u52a8\uff0c\u628a\u4e2a\u9b3c\u6539\u6210\u4e86\u795e\u5b57\u3002 \u3000\u3000\u5927\u6f20\u9b3c\u624b\u5b8b\u5b5a\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u59a8\u4e8b\uff0c\u6211\u8001\u90aa\u540d\u53f7\uff0c\u5411\u6765\u662f\u6ca1\u6709\u4ec0\u4e48\u5fcc\u8bb3\u53ef\u8a00\u7684\uff0c\u8001\u5f1f\u53f0\u83ab\u8981\u4e0d\u5b89\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u89c1\u4ed6\u8fd9\u7b49\u8bf4\u8bdd\uff0c\u77e5\u9053\u4ed6\u4e43\u662f\u6b66\u6797\u5947\u4eba\u4e00\u6d41\uff0c\u4e0d\u62d8\u5c0f\u8282\uff0c\u5f53\u4e0b\u53ea\u5f97\u62b1\u62f3\u9053\uff1a \u3000\u3000\u201c\u8001\u524d\u8f88\u8c6a\u723d\u8fc7\u4eba\uff0c\u665a\u8f88\u4ece\u547d\u4fbf\u662f\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u8d5e\u8bb8\u7684\u70b9\u4e86\u70b9\u5934\uff0c\u63a5\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u7adf\u6562\u7b54\u5141\u90a3\u9ad8\u65e0\u654c\uff0c\u7559\u4e0b\u4e09\u65e5\uff0c\u53ef\u662f\u4f60\u6709\u7740\u5fc5\u80fd\u80dc\u8fc7\u4e94\u5927\u9b54\u4e3b\u7684\u628a\u63e1\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u665a\u8f88\u5c1a\u4e0d\u6562\u8fd9\u7b49\u6258\u5927\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u5fae\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u65e2\u77e5\u90a3\u4e94\u5927\u9b54\u4e3b\u4e0d\u597d\u5bf9\u4ed8\uff0c\u4f60\u600e\u53ef\u968f\u53e3\u5e94\u5141\u4e0b\u6765\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8001\u524d\u8f88\u6709\u6240\u4e0d\u77e5\uff0c\u665a\u8f88\u6b63\u662f\u8981\u89c1\u90a3\u4e94\u5927\u9b54\u4e3b\uff0c\u6709\u6b64\u673a\u7f18\uff0c\u600e\u80fd\u653e\u8fc7\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u95f4\u8a00\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4f60\u8981\u89c1\u90a3\u4e94\u5927\u9b54\u4e3b\uff1f\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7565\u4e00\u6c89\u541f\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u542c\u8bf4\u8bf4\u51fa\u8981\u89c1\u4e94\u5927\u9b54\u4e3b\uff0c\u5fc3\u4e2d\u672c\u662f\u72af\u7591\uff0c\u6b64\u523b\u89c1\u4ed6\u6c89\u541f\u4e0d\u8bed\uff0c\u66f4\u662f\u89c9\u5f97\u4e0d\u5bf9\uff0c\u8131\u53e3\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u4e0e\u90a3\u4e94\u5927\u9b54\u4e3b\u6709\u4ec7\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e5f\u8bf4\u4e0d\u4e0a\u6709\u4ec7\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e0d\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8a93\u4e0d\u4e24\u7acb\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u5927\u529b\u610f\u5916\u5730\u4e00\u6014\u9053\uff1a\u201c\u4f60\u2026\u2026\u8fd9\u822c\u5e74\u7eaa\uff0c\u600e\u4f1a\u4e0e\u90a3\u4e94\u5927\u9b54\u4e3b\u6709\u4e86\u4ec7\u6028\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u665a\u8f88\u672c\u8eab\u4e0e\u4ed6\u4eec\u5e76\u65e0\u4ec7\u6028\uff0c\u4f46\u665a\u8f88\u5374\u4e0d\u5bb9\u4ed6\u4eec\u4e3a\u5bb3\u6b66\u6797\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u4e24\u773c\u66b4\u7741\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u7ad6\u8d77\u62c7\u6307\uff0c\u9053\uff1a\u201c\u597d\uff01\u8001\u5f1f\u7adf\u7136\u6709\u7740\u8fd9\u7b49\u96c4\u5fc3\u58ee\u5fd7\uff0c\u53eb\u8001\u673d\u597d\u751f\u656c\u4f69\uff01\u201d\u8bdd\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4e94\u5927\u9b54\u4e3b\u7684\u6b66\u529f\uff0c\u5404\u6709\u6240\u957f\uff0c\u636e\u8001\u673d\u6240\u77e5\uff0c\u5f53\u4e16\u6b66\u6797\u4e4b\u4e2d\uff0c\u9664\u4e86\u5251\u795e\u9648\u5927\u4fa0\u800c\u5916\uff0c\u5c1a\u65e0\u4e00\u4eba\u80fd\u591f\u80dc\u4e86\u4ed6\u4eec\uff0c\u8001\u5f1f\u7684\u96c4\u5fc3\u56fa\u7136\u53ef\u5609\uff0c\u4f46\u5982\u662f\u4ec5\u51ed\u4e00\u8154\u70ed\u8840\uff0c\u5f92\u901e\u5339\u592b\u4e4b\u52c7\uff0c\u5374\u662f\u667a\u8005\u6240\u4e0d\u53d6\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u4e5f\u4e0d\u8bf4\u660e\uff0c\u53ea\u662f\u4e00\u7b11\u9053\uff1a\u201c\u8001\u524d\u8f88\u91d1\u7389\u826f\u8a00\uff0c\u665a\u8f88\u8bb0\u4e0b\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u7709\u68a2\u5fae\u73b0\u559c\u8272\uff0c\u4f46\u53c8\u751a\u662f\u4e0d\u89e3\u7684\u95ee\u9053\uff1a\u201c\u542c\u8001\u5f1f\u4e4b\u8a00\uff0c\u83ab\u975e\u4ecd\u8981\u5728\u6b64\u7b49\u4e09\u5929\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e08\u592b\u65e0\u5fd7\u4e0d\u7acb\uff0c\u8a00\u800c\u65e0\u4fe1\u4e0d\u884c\uff0c\u665a\u8f88\u65e2\u5df2\u7b54\u5e94\u4e86\u9ad8\u65e0\u654c\uff0c\u81ea\u662f\u4e0d\u53ef\u53cd\u6094\u7684\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u76ee\u5149\u6295\u6ce8\u5728\u65b9\u96ea\u5b9c\u8eab\u4e0a\uff0c\u4e00\u77ac\u4e0d\u77ac\u7684\u534a\u664c\uff0c\u7ec8\u4e8e\u6447\u5934\u4e00\u53f9\u9053\uff1a\u201c\u8001\u5f1f\u51b3\u5fc3\u5192\u9669\uff0c\u8001\u673d\u8fd8\u6709\u4ec0\u4e48\u597d\u8bf4\u7684\uff1f\u201d\u5ffd\u7136\u957f\u957f\u5730\u5401\u4e86\u53e3\u6c14\uff0c\u63a5\u9053\uff1a\u201c\u8fd9\u4e48\u529e\uff0c\u8001\u673d\u4e5f\u5728\u8fd9\u513f\u5c0f\u4f4f\u4e09\u5929\u5982\u4f55\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u5f97\u5fc3\u4e2d\u5927\u4e3a\u611f\u6fc0\uff0c\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u6210\uff0c\u8001\u524d\u8f88\u4e0d\u5fc5\u4e3a\u665a\u8f88\u803d\u5fc3\uff0c\u4ed6\u4eec\u4f24\u5bb3\u4e0d\u4e86\u665a\u8f88\u7684\u3002\u201d \u3000\u3000\u8fd9\u7b49\u8bdd\u8bf4\u51fa\uff0c\u5982\u662f\u65b9\u96ea\u5b9c\u80fd\u591f\u518d\u9053\u660e\u81ea\u5df1\u7684\u5e08\u627f\u6765\u5386\uff0c\u5b8b\u5b5a\u60f3\u5fc5\u4e5f\u4e0d\u4f1a\u4e0d\u4fe1\uff0c\u53ea\u56e0\u4ed6\u5e76\u672a\u544a\u8bc9\u5b8b\u5b5a\uff0c\u81ea\u5df1\u7684\u5e08\u7236\u662f\u4ec0\u4e48\u4eba\uff0c\u662f\u4ee5\uff0c\u90a3\u5b8b\u8001\u90aa\u7adf\u662f\u8fde\u8fde\u6447\u5934\uff0c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4e8b\u60c5\u5c31\u8fd9\u6837\u51b3\u5b9a\u4e86\uff0c\u5982\u662f\u90a3\u4e94\u5927\u9b54\u4e3b\u4e2d\u65e0\u4eba\u6765\u6b64\u5219\u5df2\uff0c\u4e0d\u7136\uff0c\u8001\u673d\u62da\u4e86\u4e00\u6b7b\uff0c\u4e5f\u4e0d\u80fd\u8ba9\u4ed6\u4f24\u4f60\u3002\u201d \u3000\u3000\u5b57\u5b57\u9524\u9535\uff0c\u53e5\u53e5\u6709\u529b\uff0c\u4f7f\u5f97\u65b9\u96ea\u5b9c\u65e0\u6cd5\u518d\u8bf4\u62d2\u7edd\u4e4b\u8a00\uff0c\u5f53\u4e0b\u53ea\u597d\u62b1\u62f3\u957f\u63d6\uff0c\u9053\uff1a \u3000\u3000\u201c\u8001\u524d\u8f88\u8fd9\u822c\u5173\u62a4\u665a\u8f88\uff0c\u771f\u53eb\u4eba\u4e07\u5206\u611f\u6fc0\u3002\u201d \u3000\u3000\u4f46\u4ed6\u53c8\u89c9\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u4ed6\u5982\u6b64\u5173\u6000\u4e8e\u6211\uff0c\u7a76\u7adf\u4e3a\u7684\u4ec0\u4e48\uff0c\u800c\u4e14\uff0c\u4ed6\u4f3c\u662f\u81ea\u77e5\u6b66\u529f\u4e0d\u5982\u90a3\u4e94\u5927\u9b54\u4e3b\uff0c\u83ab\u975e\u4ed6\u5f53\u5e74\u5403\u8fc7\u4e94\u9b54\u7684\u82e6\u5934\u5417\uff1f \u3000\u3000\u4ed6\u52a8\u5ff5\u4e4b\u95f4\uff0c\u672c\u662f\u60f3\u5f00\u81ea\u8be2\u95ee\uff0c\u4f46\u5ffd\u7136\u89c9\u5f97\uff0c\u5373\u4f7f\u4ed6\u4e0e\u90a3\u4e94\u9b54\u6709\u6028\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u4e0d\u5f53\uff0c\u81ea\u5df1\u82e5\u662f\u8ffd\u6839\u7a76\u5e95\uff0c\u5c82\u4e0d\u662f\u4e0d\u591f\u541b\u5b50\u98ce\u8303\u4e86\u5417\uff1f \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u51b7\u6c57\u6697\u6d41\uff0c\u8fde\u8fde\u6697\u8d23\u81ea\u5df1\uff0c\u4e00\u9762\u5411\u5b8b\u5b5a\u8bf4\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u665a\u8f88\u6709\u4e00\u4ef6\u4e8b\uff0c\u5fc5\u987b\u5411\u4f60\u8001\u8bf4\u660e\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e09\u5929\u4e4b\u5185\uff0c\u5982\u662f\u4e94\u9b54\u4e4b\u4e2d\uff0c\u6709\u4eba\u6765\u6b64\uff0c\u665a\u8f88\u53ef\u80fd\u8981\u4e0e\u4ed6\u4eec\u52a8\u624b\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8fd9\u4e2a\u8001\u673d\u731c\u5230\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u56e0\u6b64\uff0c\u665a\u8f88\u8981\u4e0e\u4f60\u8001\u7ea6\u6cd5\u4e00\u7ae0\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u90a3\u4e00\u7ae0\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u665a\u8f88\u5018\u662f\u4e0e\u90a3\u4e94\u5927\u9b54\u4e3b\u4e2d\u4eba\u52a8\u624b\uff0c\u672a\u9732\u8d25\u8c61\u4e4b\u65f6\uff0c\u4f60\u8001\u5343\u4e07\u4e0d\u53ef\u51fa\u624b\u76f8\u52a9\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u4e00\u6014\u9053\uff1a\u201c\u8fd9\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8138\u8272\u5341\u5206\u6c89\u91cd\u4ed6\u8bf4\u9053\uff1a\u201c\u4f60\u8001\u975e\u5f97\u7b54\u5e94\u4e0d\u53ef\uff0c\u5426\u5219\uff0c\u574f\u4e86\u665a\u8f88\u7684\u5e08\u95e8\u540d\u671b\uff0c\u4e0d\u557b\u903c\u6211\u81ea\u6215\u4e4b\u5883\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4ee4\u5e08\u662f\u8c01\uff1f\u201d \u3000\u3000\u6562\u60c5\uff0c\u4ed6\u8fd9\u624d\u60f3\u8d77\uff0c\u5c1a\u672a\u95ee\u8fc7\u65b9\u96ea\u5b9c\u7684\u5e08\u95e8\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u5934\u9053\uff1a\u201c\u8001\u524d\u8f88\u5c1a\u672a\u7b54\u5e94\u6211\u554a\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u5fd6\u9053\uff1a\u201c\u6211\u5982\u662f\u4e0d\u7b54\u5e94\u4ed6\uff0c\u53ea\u6015\u8fd9\u5b69\u5b50\u8bf4\u5f97\u51fa\uff0c\u5c31\u771f\u505a\u5f97\u51fa\uff0c\u5018\u662f\u4ed6\u4e00\u65e6\u81ea\u6215\uff0c\u81ea\u5df1\u53ef\u5c31\u7f6a\u8fc7\u5927\u4e86\u3002\u201d \u3000\u3000\u4e00\u5ff5\u53ca\u6b64\uff0c\u5f53\u4e0b\u63a5\u9053\uff1a\u201c\u8001\u673d\u5e94\u5141\u4f60\u8001\u5f1f\u4fbf\u662f\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u62b1\u62f3\u9053\uff1a\u201c\u591a\u8c22\u4f60\u8001\u6210\u5168\u3002\u201d\u7f13\u7f13\u5730\u5750\u4e86\u4e0b\u53bb\uff0c\u7adf\u662f\u95ed\u76ee\u4e0d\u8bed\u3002 \u3000\u3000\u5b8b\u5b5a\u7b49\u4e86\u4e00\u4f1a\uff0c\u5e76\u672a\u89c1\u4ed6\u8bf4\u8bdd\uff0c\u5fc3\u4e2d\u5927\u611f\u5947\u602a\uff0c\u7a81\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u600e\u4e48\u4e0d\u8bf4\u8bdd\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u660e\u77e5\u4ed6\u60f3\u95ee\u4ec0\u4e48\uff0c\u4f46\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u8001\u524d\u8f88\u8fd8\u6709\u4ec0\u4e48\u6307\u6559\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u597d\u554a\uff0c\u8001\u5f1f\u5012\u5f88\u4f1a\u88c5\u4f6f\u3002\u201d \u3000\u3000\u8bed\u97f3\u7565\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4ee4\u5e08\u662f\u8c01\uff0c\u600e\u5730\u4e0d\u80af\u8bf4\u51fa\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u5ff5\u7535\u8f6c\uff0c\u5374\u662f\u60f3\u4e0d\u51fa\u4e00\u4e2a\u4ec0\u4e48\u6700\u597d\u63aa\u8f9e\u6765\u63a9\u9970\u81ea\u5df1\u4e0d\u8bf4\u7684\u9053\u7406\uff0c\u5f53\u4e0b\u82e6\u7b11\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u4e0d\u662f\u665a\u8f88\u4e0d\u8bf4\uff0c\u9664\u975e\u90a3\u4e94\u5927\u9b54\u4e3b\u4e2d\u4eba\u4e0d\u6765\uff0c\u76f8\u4fe1\u4f60\u4e00\u5b9a\u80fd\u81ea\u665a\u8f88\u6b66\u529f\u4e4b\u4e2d\uff0c\u77a7\u51fa\u7a76\u7adf\u6765\u7684\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u76b1\u4e86\u76b1\u7709\uff0c\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u4ee4\u5e08\u5b9a\u662f\u4e00\u4f4d\u5927\u6709\u540d\u671b\u7684\u4eba\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u4f60\u997f\u4e0d\u997f\uff1f\u201d\u5b8b\u5b5a\u95fb\u8a00\uff0c\u7b11\u9053\uff1a\u201c\u662f\u554a\uff0c\u5929\u5df2\u4e0d\u65e9\uff0c\u82e5\u975e\u8001\u5f1f\u63d0\u8d77\uff0c\u8001\u673d\u5012\u5fd8\u4e86\u8179\u4e2d\u9965\u997f\u4e86\u3002\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u7ad9\u8d77\u8eab\u5b50\uff0c\u63a5\u9053\uff1a\u201c\u54b1\u4eec\u4e14\u4e0b\u5c71\u53bb\u9971\u9910\u4e00\u987f\uff0c\u56de\u6765\u518d\u7b49\u90a3\u5927\u9b54\u4e3b\u5427\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5c31\u4f9d\u4f60\u8001\u3002\u201d \u3000\u3000\u4e24\u4eba\u9c7c\u8d2f\u4e0b\u5f97\u5c71\u53bb\uff0c\u56e0\u662f\u5730\u5904\u8352\u90ca\uff0c\u662f\u4ee5\u5c55\u5f00\u75be\u884c\u8eab\u6cd5\uff0c\u76cf\u8336\u4e0d\u5230\uff0c\u4fbf\u5df1\u5954\u62b5\u4e00\u5904\u9547\u5e97\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u968f\u5728\u5b8b\u5b5a\u8eab\u540e\uff0c\u627e\u4e86\u4e00\u5bb6\u996d\u9986\uff0c\u53eb\u4e86\u51e0\u5473\u83dc\u80b4\uff0c\u80e1\u4e71\u5730\u5403\u4e86\u4e00\u4e9b\uff0c\u53c8\u5207\u4e86\u51e0\u7247\u8089\u812f\uff0c\u5305\u4e86\u51e0\u4e2a\u5927\u997c\uff0c\u4fbf\u5954\u56de\u5230\u90a3\u9ad8\u65e0\u654c\u6240\u4f4f\u7684\u8305\u820d\u8fd1\u65c1\u3002 \u3000\u3000\u4e00\u5bbf\u65e0\u8bdd\uff0c\u7b2c\u4e8c\u5929\u6e05\u6668\uff0c\u90a3\u9ad8\u65e0\u654c\u4fbf\u627e\u5230\u4e24\u4eba\u9690\u8eab\u4e4b\u5904\uff0c\u4ed6\u77a7\u89c1\u4e86\u5927\u6f20\u9b3c\u624b\u5b8b\u5b5a\uff0c\u663e\u7136\u662f\u5403\u4e86\u4e00\u60ca\uff0c\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u65b9\u5144\u5f1f\uff0c\u8fd9\u4f4d\u8001\u4e08\u662f\u4ec0\u4e48\u4eba\uff1f\u53ef\u662f\u5c0f\u5144\u5f1f\u4f60\u7684\u5e08\u7236\u5417\uff1f\u201d\u8bf4\u7740\u62f1\u624b\u4e00\u793c\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u9ad8\u5144\u5f88\u65e9\u554a\uff01\u8fd9\u4f4d\u8001\u524d\u8f88\u4e0d\u662f\u5144\u5f1f\u7684\u5e08\u7236\uff0c\u4ed6\u8001\u4eba\u5bb6\u4e43\u662f\u6b66\u6797\u4e2d\u5927\u5927\u6709\u540d\u7684\u4eba\u7269\uff0c\u8bf4\u51fa\u6765\uff0c\u9ad8\u5144\u5b9a\u7136\u77e5\u6653\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u76ee\u5149\u6295\u6ce8\u5728\u5b8b\u5b5a\u8eab\u4e0a\uff0c\u6c89\u601d\u9053\uff1a\u201c\u8fd9\u4f4d\u8001\u4e08\u7684\u5f62\u8c8c\uff0c\u5144\u5f1f\u5012\u4f3c\u542c\u4eba\u8bf4\u8fc7\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u5ffd\u7136\u5927\u7b11\u9053\uff1a\u201c\u8001\u592b\u9ad8\u5174\u5f97\u5f88\uff0c\u60f3\u4e0d\u5230\u4e94\u5927\u9b54\u4e3b\u7684\u624b\u4e0b\uff0c\u4e5f\u6709\u542c\u8fc7\u8001\u592b\u5f62\u8c8c\u2026\u2026\u201d \u3000\u3000\u7b11\u58f0\u987f\u4e86\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u9601\u4e0b\u5728\u54ea\u4f4d\u9b54\u4e3b\u624b\u4e0b\uff0c\u5927\u53f7\u600e\u4e48\u79f0\u547c\uff1f\u201d \u3000\u3000\u9ad8\u65e0\u654c\u5fc3\u60f3\uff0c\u4f60\u81ea\u5df1\u4e0d\u8bf4\uff0c\u53cd\u5012\u95ee\u8d77\u6211\u6765\u4e86\uff1f\u4ed6\u4e3a\u4eba\u867d\u662f\u5341\u5206\u7c97\u653e\uff0c\u4f46\u4e5f\u6709\u4ed6\u7ec6\u5fc3\u4e4b\u5904\uff0c\u8fd9\u65f6\u5374\u88c5\u7684\u975e\u5e38\u65e0\u53ef\u5948\u4f55\u7684\u8bf4\u9053\uff1a\u201c\u54b1\u53eb\u9ad8\u65e0\u6545\uff0c\u4e43\u662f\u56db\u7075\u6559\u7684\u9996\u5ea7\uff0c\u81f3\u4e8e\u662f\u54ea\u4e00\u4f4d\u9b54\u4e3b\u624b\u4e0b\uff0c\u9ad8\u67d0\u4e0d\u6562\u76f8\u544a\uff0c\u5c1a\u8bf7\u8001\u4e08\u89c1\u8c05\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u76ee\u5149\u4e00\u8f6c\uff0c\u7b11\u9053\uff1a\u201c\u56db\u7075\u6559\u5728\u6c5f\u6e56\u4e2d\u5f88\u5c11\u542c\u8bf4\uff0c\u5fc5\u7136\u662f\u65b0\u8fd1\u7ec4\u5e2e\u5f00\u6d3e\u7684\u4e86\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u672c\u6559\u5f00\u6d3e\uff0c\u53ea\u6709\u4e00\u5e74\u5de6\u53f3\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u679c\u7136\u662f\u65b0\u6210\u7acb\u7684\u3002\u201d \u3000\u3000\u9ad8\u65e0\u6545\u62b1\u62f3\u63a5\u9053\uff1a\u201c\u8001\u4e08\u5927\u540d\uff0c\u53ef\u5426\u89c1\u793a\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u6709\u4f55\u4e0d\u53ef\uff1f\u8001\u592b\u5b8b\u5b5a\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u5012\u771f\u662f\u5403\u4e86\u4e00\u60ca\uff0c\u6123\u4e86\u4e00\u6123\uff0c\u62f1\u624b\u9053\uff1a\u201c\u539f\u6765\u662f\u5927\u6f20\u9b3c\u624b\u5b8b\u8001\u524d\u8f88\uff0c\u5931\u656c\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u4e0d\u7528\u5ba2\u6c14\uff01\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u90a3\u9b54\u5200\u7530\u8fdc\uff0c\u53ef\u662f\u4f60\u7684\u5c5e\u4e0b\uff1f\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u7b97\u5f97\u4e0a\u662f\u4e00\u540d\u5c5e\u4e0b\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u5ffd\u7136\u6c89\u58f0\u9053\uff1a\u201c\u6709\u4e00\u4ef6\u4e8b\uff0c\u8001\u592b\u53ea\u597d\u5411\u4f60\u8bf7\u6559\uff0c\u6253\u542c\u4e86\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u542c\u4ed6\u53e3\u6c14\u6709\u4e9b\u4e0d\u5584\uff0c\u4e0d\u7981\u4e00\u6014\u9053\uff1a\u201c\u5b8b\u8001\u6709\u4ec0\u4e48\u4e8b\u89c1\u793a\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u90a3\u9b54\u5200\u7530\u8fdc\uff0c\u65e2\u662f\u4f60\u7684\u5c5e\u4e0b\uff0c\u4ed6\u6240\u4f5c\u6240\u4e3a\u4e4b\u4e8b\uff0c\u4f60\u5e94\u662f\u77e5\u9053\u7684\u4e86\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u8981\u770b\u662f\u4ec0\u4e48\u4e8b\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u5609\u5b9a\u7a0b\u5bb6\u5e84\u767e\u53e3\u6027\u547d\uff0c\u53ef\u662f\u4e27\u5728\u90a3\u9b54\u5200\u7530\u8fdc\u7684\u624b\u4e2d\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u672c\u662f\u65c1\u503e\u542c\uff0c\u6b64\u523b\u4e00\u89c1\u5b8b\u5b5a\u95ee\u7684\u7adf\u662f\u7a0b\u5b50\u671b\u4e00\u5bb6\u88ab\u5bb3\u4e4b\u4e8b\uff0c\u4e0d\u89c9\u5931\u58f0\u9053\uff1a \u3000\u3000\u201c\u5b8b\u8001\u4e0e\u7a0b\u5e84\u4e3b\u8ba4\u8bc6\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u957f\u53f9\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u513f\u5973\u4eb2\u5bb6\u600e\u4f1a\u4e0d\u76f8\u8bc6\uff1f\u8001\u673d\u60f3\u4e0d\u5230\u4e09\u5e74\u591a\u672a\u89c1\uff0c\u7adf\u7136\u5929\u4eba\u8fdc\u9694\uff0c\u9057\u61be\u4eca\u751f\u4e86\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9eef\u7136\u9053\uff1a\u201c\u5982\u6b64\u770b\u6765\uff0c\u665a\u8f88\u6b64\u6765\uff0c\u7adf\u4e0e\u8001\u4e08\u662f\u540c\u4ec7\u654c\u6c7d\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4f60\u6740\u90a3\u7530\u8fdc\uff0c\u662f\u4e3a\u4e86\u8001\u673d\u90a3\u4eb2\u5bb6\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u5168\u662f\uff0c\u665a\u8f88\u7684\u4e09\u4f4d\u4f2f\u53d4\uff0c\u4e0e\u90a3\u7530\u8fdc\u6709\u4ec7\uff0c\u6240\u4ee5\uff0c\u665a\u8f88\u6740\u4ed6\uff0c\u4e3a\u7a0b\u5e84\u4e3b\u5bb6\u62a5\u4ec7\uff0c\u53ea\u662f\u987a\u4fbf\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u76ee\u5149\u5149\u4e00\u4eae\uff0c\u9053\uff1a\u201c\u8001\u5f1f\u7684\u4e09\u4f4d\u4f2f\u53d4\u662f\u8c01\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u4e2d\u5dde\u4e09\u4fa0\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u662f\u65b9\u5929\u6210\u7684\u4f84\u5b50\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4f60\u8001\u8ddf\u6211\u4f2f\u53d4\u89c1\u8fc7\uff1f\u201d\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u4e2d\u5dde\u4e09\u4fa0\uff0c\u4e0e\u8001\u592b\u4ff1\u662f\u6545\u4ea4\uff0c\u53ea\u662f\u8fd1\u51e0\u5e74\u6765\uff0c\u5374\u662f\u672a\u66fe\u89c1\u8fc7\u3002\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u8fd9\u4e00\u8eab\u6b66\u529f\uff0c\u53ef\u662f\u5f97\u81ea\u4e2d\u5dde\u4e09\u4fa0\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u6211\u90a3\u4e09\u4f4d\u53d4\u4f2f\uff0c\u5e76\u672a\u4ef0\u6280\u665a\u8f88\u6b66\u529f\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u9887\u4e3a\u610f\u5916\u5730\u9053\uff1a\u201c\u4f60\u53e6\u6709\u5e08\u627f\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u665a\u8f88\u6b63\u662f\u53e6\u6709\u5e08\u627f\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u662f\u4e86\uff0c\u60f3\u90a3\u4e2d\u5dde\u4e09\u4fa0\u7684\u6b66\u529f\u867d\u597d\u4f46\u53ea\u6015\u5e76\u975e\u9b54\u5200\u7530\u8fdc\u4e4b\u654c\uff0c\u4f60\u65e2\u80fd\u4e00\u5251\u6740\u6b7b\u7530\u8fdc\uff0c\u8db3\u89c1\u4f60\u7684\u6b66\u529f\u6bd4\u90a3\u7530\u8fdc\u9ad8\u660e\u751a\u591a\uff0c\u4e2d\u5dde\u4e09\u4fa0\u81ea\u7136\u8c03\u6559\u4e0d\u51fa\u50cf\u4f60\u8fd9\u822c\u9ad8\u624b\u4e86\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u5ffd\u7136\u63d2\u53e3\u9053\uff1a\u201c\u4f11\u8bf4\u4e2d\u5dde\u4e09\u9655\uff0c\u4ee5\u65b9\u5144\u7684\u5251\u6cd5\u800c\u8a00\uff0c\u53ea\u6015\u8fde\u7d20\u6709\u6b66\u6797\u6cf0\u5c71\u5317\u6597\u4e4b\u8a89\u7684\u5c11\u6797\u8fbe\u6469\u4e09\u5251\uff0c\u4e5f\u65e0\u8fd9\u7b49\u5feb\u901f\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8001\u592b\u77a7\u5230\u4ed6\u51fa\u5251\u4e4b\u52bf\uff0c\u679c\u7136\u795e\u5947\u5df2\u6781\uff0c\u5f53\u4eca\u4e4b\u4e16\uff0c\u9664\u4e86\u5251\u795e\u9648\u5927\u4fa0\uff0c\u53c8\u6709\u4f55\u4eba\u7684\u5251\u6cd5\uff0c\u80fd\u591f\u6bd4\u5f97\u4e0a\u2026\u2026\u201d\u4ed6\u5ffd\u7136\u4e00\u626c\u5934\u540e\uff0c\u5927\u7b11\u9053\uff1a\u201c\u662f\u4e86\uff0c\u8001\u592b\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u6014\u9053\uff0c\u201c\u4f60\u8001\u660e\u767d\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4f60\u662f\u5251\u795e\u7684\u4f20\u4eba\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u9ad8\u65e0\u654c\u8138\u4e0a\u795e\u8272\u5927\u53d8\uff0c\u8131\u53e3\u9053\uff1a\u201c\u65b9\u5144\u5f1f\uff0c\u4f60\u771f\u662f\u5251\u795e\u9648\u5e0c\u6b63\u7684\u5f1f\u5b50\u5417\uff1f\u201d\u6562\u60c5\uff0c\u4ed6\u5bf9\u9648\u5e0c\u6b63\u4f59\u60b8\u72b9\u5728\uff0c\u4e00\u95fb\u5251\u795e\u5927\u540d\uff0c\u987f\u65f6\u4e0d\u7531\u81ea\u4e3b\u5730\u5fc3\u4e2d\u5927\u611f\u60ca\u60e7\u4e0d\u5b89\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u8861\u91cf\u773c\u4e0b\u5c40\u52bf\uff0c\u81ea\u5df1\u5982\u60f3\u89c1\u89c1\u90a3\u4e94\u5927\u9b54\u4e3b\uff0c\u4ee5\u53ca\u8bd5\u8bd5\u4ed6\u4eec\u7684\u6b66\u529f\uff0c\u5c31\u7edd\u5bf9\u4e0d\u80fd\u627f\u8ba4\u662f\u5251\u795e\u4f20\u4eba\uff0c\u5426\u5219\uff0c\u90a3\u9ad8\u65e0\u654c\u4e00\u5b9a\u4e0d\u80af\u5bb9\u81ea\u5df1\u518d\u5728\u6b64\u95f4\u505c\u7559\u3002\u4f46\u4ed6\u5374\u53c8\u89c9\u7740\uff0c\u8eab\u4e3a\u5251\u795e\u9648\u5e0c\u6b63\u7684\u5f1f\u5b50\uff0c\u603b\u4e0d\u80fd\u5f53\u7740\u522b\u4eba\uff0c\u4e0d\u627f\u8ba4\u5e08\u5c0a\u662f\u8c01\uff0c\u5426\u5219\uff0c\u5373\u662f\u4e0d\u5b5d\u3002\u201d \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u4ed6\u5341\u5206\u4e3a\u96be\u7684\u6c89\u541f\u672a\u8bed\u3002 \u3000\u3000\u9ad8\u65e0\u654c\u5927\u4e3a\u7d27\u5f20\u5730\u77a7\u7740\u4ed6\uff0c\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u65b9\u5144\u5f1f\uff0c\u4f60\u600e\u4e48\u4e0d\u8bf4\u8bdd\uff1f\u5230\u5e95\u4f60\u662f\u4e0d\u662f\u5251\u795e\u4f20\u4eba\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u6025\u4e4b\u4e0b\uff0c\u5ffd\u7136\u8111\u9645\u7075\u5149\u4e00\u73b0\uff0c\u8131\u53e3\u9053\uff1a\u201c\u5144\u5f1f\u7684\u6069\u5e08\uff0c\u81ea\u79f0\u4e1c\u8033\u8001\u4eba\uff0c\u662f\u4e0d\u662f\u5251\u795e\u5927\u4fa0\uff0c\u5144\u5f1f\u5374\u662f\u4e0d\u66fe\u95ee\u8fc7\uff01\u201d \u3000\u3000\u4ed6\u8fd9\u7b49\u56de\u7b54\uff0c\u679c\u771f\u4e24\u9762\u90fd\u5df2\u987e\u5230\uff0c\u5373\u4e0d\u4f7f\u9ad8\u65e0\u654c\u803d\u5fc3\uff0c\u4e0b\u90a3\u9010\u5ba2\u4e4b\u4ee4\uff0c\u4e5f\u4e0d\u4f1a\u5728\u81ea\u5df1\u5fc3\u4e2d\uff0c\u72af\u535e\u90a3\u4e0d\u8ba4\u5e08\u957f\u7684\u4e0d\u6559\u4e4b\u7f6a\uff0c\u56e0\u4e3a\u8fd9\u4e1c\u8033\u8001\u4eba\u56db\u5b57\uff0c\u672c\u662f\u51fa\u8bf8\u5e08\u7236\u81ea\u5df1\u4e4b\u53e3\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u8bdd\u97f3\u4e00\u843d\uff0c\u53ea\u542c\u5f97\u9ad8\u65e0\u6545\u5927\u5927\u7684\u5598\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5c31\u597d\u4e86\u2026\u2026\u201d\u539f\u6765\u90a3\u5b9b\u5982\u534a\u622a\u94c1\u5854\u822c\u7684\u9ad8\u65e0\u654c\uff0c\u8fd9\u9877\u523b\u4e4b\u95f4\uff0c\u5df2\u56e0\u8fc7\u5ea6\u7d27\u5f20\u5c4f\u606f\u76f8\u5f85\uff0c\u8fde\u8138\u8272\u90fd\u8e69\u5f97\u6cdb\u6210\u6df1\u7d2b\u3002 \u3000\u3000\u5b8b\u5b5a\u5219\u76b1\u7709\u5583\u5583\u8bf4\u9053\uff1a\u201c\u4e1c\u8033\u8001\u4eba\u56db\u5b57\uff0c\u8001\u673d\u53ef\u662f\u964c\u751f\u5f97\u7d27\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u7b11\u9053\uff1a\u5982\u4e0d\u964c\u751f\uff0c\u6211\u53c8\u600e\u4f1a\u8bf4\u51fa\u6765\uff0c\u4f46\u4ed6\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u5bb6\u5e08\u9690\u5c45\u9041\u4e16\u6570\u5341\u5e74\uff0c\u6b66\u6797\u4e4b\u4e2d\uff0c\u81ea\u662f\u65e0\u4eba\u8ba4\u8bc6\u4ed6\u8001\u4eba\u5bb6\u7684\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u6ce8\u89c6\u4e86\u65b9\u96ea\u5b9c\u6709\u9877\uff0c\u63a5\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u90a3\u4e2d\u5dde\u4e09\u4fa0\u73b0\u5728\u4f55\u5904\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u665a\u8f88\u8fd9\u51e0\u5e74\u6df1\u5c71\u5b66\u827a\uff0c\u4e0a\u4e2a\u6708\u65b9\u59cb\u4e0b\u5c71\uff0c\u5c1a\u672a\u56de\u5230\u4e2d\u539f\uff0c\u62dc\u89c1\u51e0\u4f4d\u53d4\u4f2f\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u4e5f\u6ca1\u6709\u89c1\u5230\u4ed6\u4eec\u3002\u201d \u3000\u3000\u8bdd\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u8001\u673d\u8fd9\u756a\u7531\u5927\u6f20\u4e1c\u6765\uff0c\u672c\u60f3\u4e0e\u90d1\u5927\u521a\u8001\u5f1f\u7b49\u5546\u91cf\u4e00\u6869\u5927\u4e8b\uff0c\u5b70\u6599\u8d70\u904d\u4e2d\u5dde\u4e00\u5e26\uff0c\u5374\u672a\u89c1\u5230\u4ed6\u4eec\u4e8c\u4eba\uff0c\u4f20\u95fb\u4e2d\uff0c\u53ea\u8bf4\u4ed6\u4eec\u4e1a\u5df2\u5e94\u90a3\u9b54\u5200\u7530\u8fdc\u4e4b\u7ea6\u524d\u6765\u897f\u8700\uff0c\u4e0d\u6599\u5f85\u8001\u592b\u62b5\u8fbe\u5ddd\u4e2d\uff0c\u4e0d\u4f46\u672a\u66fe\u6211\u5230\u4e2d\u5dde\u4e09\u4fa0\uff0c\u66f4\u53c8\u9047\u4e0a\u8001\u673d\u513f\u5973\u4eb2\u5bb6\u7a0b\u5b50\u671b\u6ee1\u95e8\u906d\u4eba\u6697\u7b97\u2026\u2026\u201d\u957f\u957f\u4e00\u53f9\uff0c\u4f4f\u53e3\u4e0d\u8bed\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u90a3\u9ad8\u65e0\u654c\u5374\u662f\u7ad9\u5728\u4e00\u65c1\uff0c\u8138\u8272\u9634\u775b\u4e0d\u5b9a\uff0c\u77a7\u77a7\u65b9\u96ea\u5b9c\uff0c\u53c8\u77a7\u77a7\u5927\u6f20\u9b3c\u624b\uff0c\u7ec8\u4e8e\u53cc\u624b\u4e00\u62b1\u62f3\uff0c\u6717\u58f0\u5411\u5b8b\u5b5a\u8bf4\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u90a3\u7a0b\u5bb6\u5e84\u7684\u8840\u6848\uff0c\u9ad8\u67d0\u5012\u662f\u77e5\u9053\u4e00\u4e9b\u6765\u9f99\u53bb\u8109\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u95fb\u8a00\uff0c\u4f3c\u662f\u65e9\u5728\u610f\u6599\u4e4b\u4e2d\uff0c\u63a5\u9053\uff1a\u201c\u9601\u4e0b\u65b9\u4fbf\u8bf4\u51fa\u6765\u5417\uff1f\u201d \u3000\u3000\u9ad8\u65e0\u6545\u6168\u7136\u9053\uff1a\u201c\u82e5\u662f\u4e0d\u4fbf\u8bf4\u51fa\uff0c\u9ad8\u67d0\u5927\u53ef\u63a8\u8bf4\u4e0d\u77e5\u6b64\u4e8b\u554a\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8001\u673d\u656c\u5019\u660e\u793a\uff01\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u90a3\u7a0b\u5b50\u671b\u4e00\u5bb6\uff0c\u4e43\u662f\u5728\u5251\u95e8\u4e94\u9b3c\u4e3b\u4f7f\u4e4b\u4e0b\uff0c\u6002\u607f\u9b54\u5200\u7530\u8fdc\u548c\u4ed6\u4eec\u8054\u624b\u6240\u4e3a\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u90a3\u5206\u62ab\u8033\u9645\u7684\u767d\u53d1\uff0c\u5ffd\u7136\u65e0\u98ce\u81ea\u52a8\uff0c\u54ac\u7259\u9053\uff1a\u201c\u679c\u7136\u662f\u4ed6\u4eec\u6240\u4e3a\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e5f\u5927\u4e3a\u6fc0\u52a8\uff0c\u8131\u53e3\u9053\uff1a\u201c\u767d\u8863\u540a\u5ba2\u6210\u7384\u901a\uff0c\u4e43\u662f\u4e3a\u4e86\u6211\u4eec\u4e09\u4f4d\u53d4\u4f2f\u4e4b\u4e8b\uff0c\u624d\u4e0e\u7a0b\u5e84\u4e3b\u7ed3\u4e0b\u4ec7\u6028\uff0c\u60f3\u4e0d\u5230\u4ed6\u4eec\u7adf\u7528\u8fd9\u7b49\u5351\u52a3\u7684\u624b\u6bb5\uff0c\u5bf9\u4ed8\u7a0b\u8001\u4e00\u5bb6\uff0c\u6b64\u4e8b\u665a\u8f88\u51b3\u4e0d\u80fd\u5750\u89c6\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u4e0d\u77e5\u6614\u5e74\u4e2d\u5dde\u4e09\u4fa0\u56e0\u4e3a\u5bc4\u4f4f\u7a0b\u5bb6\u5e84\u4e2d\uff0c\u4e0e\u5251\u95e8\u4e94\u9b3c\u7ed3\u6028\u7ecf\u8fc7\uff0c\u95fb\u8a00\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u6b64\u4e8b\u4e0e\u4f60\u4e09\u4f4d\u53d4\u4f2f\u4f55\u5173\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5f53\u4e0b\u5f88\u7b80\u7565\u5730\u628a\u5f53\u5e74\u4e4b\u4e8b\uff0c\u8bf4\u4e86\u4e00\u904d\uff0c\u53ea\u662f\u628a\u5176\u4e2d\u6709\u5173\u5251\u795e\u7684\u4e00\u5207\uff0c\u9690\u7792\u672a\u8bb2\u3002 \u3000\u3000\u5b8b\u5b5a\u5927\u4e3a\u610f\u5916\u5730\u4e00\u6014\u9053\uff1a\u201c\u539f\u6765\u8fd9\u5176\u4e2d\u8fd8\u6709\u5982\u8bb8\u4e4b\u591a\u7684\u6ce2\u6298\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u665a\u8f88\u9002\u9022\u5176\u4f1a\uff0c\u56e0\u6b64\u4e00\u4e00\u5747\u4e3a\u76ee\u7779\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u957f\u957f\u4e00\u53f9\u9053\uff1a\u201c\u7530\u8fdc\u5df2\u6b7b\u8001\u5f1f\u5251\u4e0b\uff0c\u90a3\u5251\u95e8\u4e94\u9b3c\uff0c\u4e94\u6b7b\u5176\u4e09\uff0c\u5269\u4e0b\u4e24\u4e2a\uff0c\u5373\u548c\u5b97\u58eb\u4e49\u7ec4\u6210\u4e09\u4e49\u95e8\uff0c\u60f3\u4ed6\u4eec\u5fc5\u662f\u6709\u4e86\u4ec0\u4e48\u9760\u5c71\u5728\u80cc\u540e\u652f\u6301\u7684\u4e86\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u5ffd\u7136\u63a5\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u9ad8\u67d0\u5012\u662f\u77e5\u5f97\u4e00\u4e9b\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u9ad8\u5144\u53ef\u613f\u8bf4\u51fa\uff1f\u201d \u3000\u3000\u9ad8\u65e0\u6545\u9053\uff1a\u201c\u65b9\u5144\u5f1f\uff0c\u4e94\u4eba\u9b54\u4e3b\u4e4b\u4e2d\uff0c\u6709\u4e00\u4f4d\u8c8c\u6bd4\u6625\u82b1\uff0c\u5fc3\u5982\u86c7\u874e\u7684\u5973\u4eba\uff0c\u4f60\u53ef\u77e5\u9053\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u867d\u662f\u542c\u5e08\u7236\u8bf4\u8fc7\u4e94\u9b54\u4e3b\u7684\u540d\u53f7\uff0c\u4f46\u5374\u5e76\u4e0d\u5341\u5206\u4e86\u7136\u662f\u4ee5\uff0c\u95fb\u8a00\u4e00\u6014\u9053\uff1a\u201c\u662f\u5929\u9b54\u5973\uff1f\u201d \u3000\u3000\u539f\u6765\u5251\u795e\u9648\u5e0c\u6b63\u5bf9\u4e94\u5927\u9b54\u4e3b\u4e4b\u4e2d\u7684\u5929\u9b54\u5973\u6700\u662f\u4e0d\u653e\u5fc3\uff0c\u56e0\u800c\u6709\u544a\u8bc9\u65b9\u96ea\u5b9c\u4e4b\u65f6\uff0c\u4e5f\u5c31\u7279\u522b\u4ea4\u5f85\uff0c\u8981\u4ed6\u7559\u5fc3\u90a3\u5fc3\u72e0\u624b\u8fa3\uff0c\u8be1\u8ba1\u591a\u7aef\u7684\u5973\u4eba\uff0c\u662f\u4ee5\uff0c\u9ad8\u65e0\u654c\u8bdd\u4e00\u843d\uff0c\u4ed6\u7acb\u5373\u5c31\u60f3\u5230\u4e86\u5929\u9b54\u5973\u2026\u2026\u201d \u3000\u3000\u600e\u77e5\u9ad8\u65e0\u654c\u6447\u5934\u9053\uff1a\u201c\u4e0d\u662f\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6b63\u89c9\u5f97\u4e0d\u89e3\uff0c\u5b8b\u5b5a\u5df2\u4f4e\u58f0\u9053\uff1a\u201c\u662f\u90a3\u7389\u5170\u5996\u5973\u67f3\u5a9a\u5a18\u5417\uff1f\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b63\u662f\u90a3\u67f3\u5a9a\u5a18\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u76b1\u7709\u9053\uff1a\u201c\u9ad8\u5144\uff0c\u4e09\u4e49\u95e8\u53ef\u662f\u7531\u67f3\u5a9a\u5a18\u5728\u6697\u4e2d\u652f\u6301\u7684\u5417\uff1f\u201d \u3000\u3000\u9ad8\u65e0\u6545\u9053\uff1a\u201c\u5144\u5f1f\u4e0d\u6562\u5341\u5206\u80af\u5b9a\uff0c\u4f46\u636e\u5144\u5f1f\u6240\u77e5\uff0c\u90a3\u6210\u7384\u901a\u5df2\u7136\u62dc\u5728\u67f3\u5a9a\u5a18\u7684\u95e8\u4e0b\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u70b9\u5934\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u90a3\u4e09\u4e49\u95e8\u679c\u662f\u67f3\u5a9a\u5a18\u6240\u4e00\u624b\u4e3b\u4f7f\u7684\u4e86\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u5927\u6982\u4e0d\u9519\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u5ffd\u7136\u5927\u58f0\u9053\uff1a\u201c\u8001\u592b\u5bc4\u8eab\u4e09\u4e49\u95e8\u957f\u8fbe\u534a\u5e74\uff0c\u5374\u662f\u672a\u66fe\u67e5\u51fa\u7a0b\u4eb2\u5bb6\u7684\u4ec7\u4eba\uff0c\u8fd9\u6210\u7384\u901a\u5012\u662f\u771f\u662f\u7cbe\u660e\u5f97\u5f88\u2026\u2026\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u4ef0\u5728\u4e00\u53f9\uff0c\u63a5\u9053\uff1a\u201c\u4eb2\u5bb6\u7fc1\u82f1\u7075\u4e0d\u8fdc\uff0c\u5144\u5f1f\u5fc5\u5c06\u624b\u5203\u6210\u8d3c\uff0c\u4e3a\u4f60\u62a5\u4ec7\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u6697\u5fd6\u9053\uff1a\u201c\u4e0d\u77e5\u4ed6\u8ddf\u7a0b\u5e84\u4e3b\u662f\u600e\u6837\u7684\u513f\u5973\u4eb2\u5bb6\uff0c\u77a7\u4ed6\u795e\u60c5\uff0c\u679c\u662f\u4f24\u5fc3\u5f97\u5f88\u3002\u201d \u3000\u3000\u8f6c\u5ff5\u95f4\u8131\u53e3\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u54b1\u4eec\u5148\u53bb\u627e\u90a3\u6210\u7384\u901a\u5982\u4f55\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u4e00\u6123\u9053\uff1a\u201c\u6b64\u95f4\u4e09\u65e5\u4e4b\u7ea6\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u6b64\u53bb\u5609\u5b9a\uff0c\u4e00\u65e5\u53ef\u56de\uff0c\u60f3\u5fc5\u4e0d\u4f1a\u8bef\u4e86\u9ad8\u5144\u4e4b\u4e8b\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u6c89\u541f\u9053\uff1a\u201c\u4e8b\u5206\u7f13\u6025\u5148\u540e\uff0c\u8001\u5f1f\uff0c\u90a3\u6210\u7384\u901a\u4e5f\u4e0d\u4f1a\u6025\u75c5\u800c\u6b7b\uff0c\u54b1\u4eec\u7559\u4ed6\u591a\u6d3b\u4e09\u5929\u4e5f\u4e0d\u8981\u7d27\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u5728\u65b9\u96ea\u5b9c\u63d0\u5230\u5148\u627e\u6210\u7384\u901a\u4e4b\u65f6\uff0c\u8138\u4e0a\u9887\u73b0\u4e0d\u5b89\u4e4b\u610f\uff0c\u542c\u5f97\u5b8b\u5b5a\u6240\u8bf4\u4e4b\u540e\uff0c\u7acb\u5373\u5927\u559c\u9053\uff1a\u201c\u662f\u554a\uff01\u65b9\u5144\u5f1f\u4f55\u4e0d\u4e09\u65e5\u4e4b\u540e\u518d\u53bb\u5462\uff1f\u5144\u5f1f\u4e0d\u624d\uff0c\u4ea6\u613f\u968f\u5f80\uff0c\u7a0d\u5c3d\u4e00\u4efd\u5fc3\u610f\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u51b7\u54fc\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u5fc5\u4e86\u3002\u201d \u3000\u3000\u663e\u7136\uff0c\u9ad8\u65e0\u654c\u78b0\u4e86\u4e2a\u4e0d\u5927\u4e0d\u5c0f\u7684\u9489\u5b50\uff0c\u4ed6\u8baa\u8baa\u4e00\u7b11\u9053\uff1a\u201c\u5373\u7136\u65b9\u5144\u5f1f\u529b\u80fd\u80dc\u4efb\uff0c\u5144\u5f1f\u4e0d\u53bb\u5c31\u662f\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5251\u7709\u4e00\u626c\uff0c\u7b11\u9053\uff1a\u201c\u9ad8\u5144\u76db\u60c5\uff0c\u5144\u5f1f\u5fc3\u9886\u4e86\u3002\u201d\u8bed\u97f3\u987f\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u9ad8\u5144\uff0c\u5144\u5f1f\u6709\u4e00\u6869\u4e0d\u60c5\u4e4b\u8bf7\uff0c\u4e0d\u77e5\u9ad8\u5144\u53ef\u5426\u540c\u610f\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u5b8f\u58f0\u4e00\u7b11\u9053\uff1a\u201c\u65b9\u5144\u5f1f\u6709\u4ec0\u4e48\u4e8b\uff0c\u8bf7\u8bf4\u65e0\u59a8\uff0c\u53ea\u8981\u5144\u5f1f\u529b\u6240\u80fd\u53ca\uff0c\u5b9a\u5c06\u5168\u529b\u4ee5\u8d74\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5144\u5f1f\u5982\u5e78\u80fd\u52a9\u4e86\u9ad8\u5144\u51fb\u9000\u5f3a\u654c\uff0c\u5c1a\u76fc\u9ad8\u5144\u66ff\u5144\u5f1f\u89e3\u7b54\u4e00\u4e8b\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u5148\u524d\u867d\u662f\u8bf4\u7684\u5341\u5206\u5927\u65b9\uff0c\u4f46\u5fc3\u4e2d\u5374\u4f9d\u7136\u4e0d\u505c\u5fd0\u5fd1\uff0c\u56e0\u4e3a\u4ed6\u4e5f\u60f3\u5230\u4e00\u70b9\uff0c\u90a3\u5c31\u662f\u4ee5\u65b9\u96ea\u5b9c\u4e00\u8eab\u80fd\u8010\uff0c\u5982\u679c\u6709\u4e8b\u6c42\u4eba\uff0c\u90a3\u5fc5\u662f\u975e\u5e38\u96be\u529e\u4e4b\u4e8b\uff0c\u81ea\u5df1\u80fd\u5426\u62c5\u5f53\u5f97\u4e86\uff0c\u8fd8\u4e0d\u6562\u4e00\u5b9a\uff0c\u6b64\u523b\u542c\u5f97\u65b9\u96ea\u5b9c\u5f00\u53e3\uff0c\u539f\u6765\u53ea\u662f\u8981\u66ff\u4ed6\u91ca\u4e00\u96be\u9898\uff0c\u4e0d\u7981\u5f00\u5fc3\u5730\u5927\u7b11\u9053\uff1a\u201c\u5bb9\u6613\u5f97\u5f88\uff01\u65b9\u5144\u5f1f\u5feb\u5feb\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u8fd9\u4e8b\u5728\u9ad8\u5144\u800c\u8a00\uff0c\u53ea\u4e0d\u8fc7\u542f\u9f7f\u4e4b\u52b3\u800c\u5df2\u2026\u2026\u201d\u8bed\u97f3\u5ffd\u7136\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5144\u5f1f\u53ea\u662f\u5e0c\u671b\u9ad8\u5144\u80fd\u5c06\u56db\u7075\u6559\u7684\u4e00\u5207\u544a\u77e5\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5c31\u662f\u8fd9\u4e2a\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5c31\u662f\u8fd9\u4e2a\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9661\u5730\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u65b9\u5144\u5f1f\uff0c\u9ad8\u67d0\u4e0d\u662f\u592a\u5360\u4fbf\u5b9c\u4e86\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u662f\uff0c\u8981\u77e5\u56db\u7075\u6559\u4e2d\u4e00\u5207\uff0c\u4e0d\u662f\u6559\u4e2d\u5f1f\u5b50\uff0c\u60f3\u5fc5\u662f\u4e0d\u53ef\u80fd\u77e5\u9053\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u8fd9\u5012\u4e0d\u5047\uff0c\u5c31\u7b97\u662f\u6559\u4e2d\u5f1f\u5b50\uff0c\u4e5f\u8981\u5206\u6210\u82e5\u5e72\u7b49\u7ea7\uff0c\u8001\u5b9e\u8bf4\uff0c\u9664\u4e86\u50cf\u5144\u5f1f\u6211\u8fd9\u7b49\u8eab\u4efd\uff0c\u60f3\u8981\u77e5\u9053\u56db\u7075\u6559\u4e2d\u7684\u4e00\u5207\uff0c\u53ea\u6015\u5f53\u771f\u96be\u6bd4\u767b\u5929\u4e00\u822c\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u4e00\u65c1\u51b7\u54fc\u9053\uff1a\u201c\u5927\u8a00\u4e0d\u60ed\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u77e5\u9053\u5b8b\u8001\u4eba\u5bf9\u4ed6\u5370\u8c61\u4e0d\u597d\uff0c\u5f53\u4e0b\u5fd9\u9053\uff1a\u201c\u9ad8\u5144\uff0c\u82e5\u975e\u5144\u5f1f\u77e5\u6653\u53ea\u6709\u9ad8\u5144\u80fd\u591f\u4e86\u89e3\u6559\u4e2d\u4e00\u5207\uff0c\u5144\u5f1f\u4e5f\u5c31\u4e0d\u4f1a\u8bf7\u6559\u4e86\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u5927\u7b11\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f11\u8bf4\u6b64\u4e8b\u8fd9\u822c\u5bb9\u6613\uff0c\u5c31\u662f\u518d\u96be\u4e4b\u4e8b\uff0c\u5144\u5f1f\u7b54\u5e94\u5728\u5148\uff0c\u90a3\u4e5f\u8981\u4e3a\u65b9\u5144\u505a\u5230\u624d\u662f\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u8fd9\u65f6\u5ffd\u7136\u51b7\u7b11\u9053\uff1a\u201c\u5e9f\u8bdd\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u8138\u8272\u5fae\u5fae\u4e00\u53d8\uff0c\u51e0\u4e4e\u5c31\u8981\u53d1\u4f5c\uff0c\u5ffd\u7136\u542c\u5230\u65b9\u96ea\u5b9c\u4f4e\u58f0\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u6709\u4eba\u6765\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u8fd9\u65f6\u4e5f\u5df2\u89c9\u51fa\uff0c\u6b63\u6709\u4e00\u4eba\u5411\u81ea\u5df1\u7b49\u4eba\u505c\u8eab\u4e4b\u5904\u884c\u6765\uff0c\u542c\u4ed6\u811a\u6b65\u7684\u8f7b\u6d6e\uff0c\u5341\u4e08\u4e4b\u5185\uff0c\u7adf\u7136\u4f7f\u4ed6\u4e0d\u6613\u89c9\u5bdf\uff0c\u8db3\u53ef\u89c1\u51fa\u6b64\u4eba\u529f\u529b\uff0c\u4f3c\u662f\u4e0d\u5728\u81ea\u5df1\u4e4b\u4e0b\u3002 \u3000\u3000\u5f53\u4e0b\u6545\u610f\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u8fd9\u4eba\u6b66\u529f\u4e0d\u5f31\u54e9\uff01\u201d \u3000\u3000\u9ad8\u65e0\u654c\u76f4\u5230\u6b64\u523b\uff0c\u65b9\u59cb\u89c9\u51fa\u6709\u4eba\u8d70\u6765\uff0c\u5fc3\u4e2d\u81ea\u662f\u5927\u5403\u4e00\u60ca\uff0c\u8131\u53e3\u9053\uff1a\u201c\u65b9\u5144\u5f1f\uff0c\u4f1a\u4e0d\u4f1a\u662f\u2026\u2026\u201d \u3000\u3000\u8bed\u97f3\u672a\u5b8c\uff0c\u53ea\u542c\u5f97\u6709\u4eba\u5927\u559d\u9053\uff1a\u201c\u9ad8\u5144\u53ef\u5728\u6797\u4e2d\uff1f\u201d \u3000\u3000\u987f\u65f6\uff0c\u9ad8\u65e0\u654c\u8138\u8272\u4e00\u677e\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u662f\u672c\u6559\u4e2d\u4eba\u3002\u201d\u53ea\u89c1\u4ed6\u626d\u8f6c\u5934\uff0c\u9ad8\u58f0\u9053\uff1a\u201c\u672c\u5ea7\u5728\u6b64\u3002\u201d \u3000\u3000\u4e00\u4f4d\u516c\u9ad8\u4e0d\u53ca\u4e09\u5c3a\uff0c\u79c3\u9876\u540a\u76fe\uff0c\u7a7f\u7740\u4e00\u8eab\u84dd\u8863\u886b\u88e4\u7684\u8001\u4eba\uff0c\u6643\u5f97\u4e00\u6643\uff0c\u4fbf\u81ea\u6797\u6797\u800c\u5165\u3002\u8eab\u6cd5\u4e4b\u5feb\uff0c\u4f7f\u5f97\u65b9\u96ea\u5b9c\u6697\u6697\u5403\u4e86\u4e00\u60ca\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u9ad8\u65e0\u654c\u5df2\u5927\u7b11\u9053\uff1a\u201c\u827e\u5144\u56de\u6765\u4e86\uff0c\u5144\u5f1f\u8fd9\u53ef\u653e\u5fc3\u4e86\u3002\u201d \u3000\u3000\u77ee\u8001\u4eba\u4e00\u5165\u6797\u4e2d\uff0c\u76ee\u5149\u4fbf\u5728\u5b8b\u5b5a\u548c\u65b9\u96ea\u5b9c\u8eab\u4e0a\u8f6c\u52a8\u4e0d\u505c\uff0c\u9ad8\u65e0\u654c\u8bdd\u97f3\u4e00\u987f\uff0c\u4ed6\u7acb\u5373\u63a5\u9053\uff1a\u201c\u9ad8\u5144\uff0c\u8fd9\u4e24\u4f4d\u5c11\u89c1\uff0c\u90fd\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u9ad8\u65e0\u654c\u5475\u5475\u5927\u7b11\u9053\uff1a\u201c\u827e\u5144\uff0c\u5144\u5f1f\u6b63\u8981\u4e3a\u4f60\u4ecb\u7ecd\u3002\u201d \u3000\u3000\u6307\u770b\u5b8b\u8001\u4eba\u9053\uff1a\u201c\u8fd9\u4f4d\u540d\u626c\u6f20\u5317\uff0c\u5374\u53c8\u8a89\u6ee1\u4e2d\u539f\u7684\u4fa0\u5ba2\uff0c\u5927\u6f20\u9b3c\u624b\u5b8b\u8001\u3002\u201d \u3000\u3000\u90a3\u77ee\u8001\u4eba\u987f\u65f6\u6014\u4e86\u4e00\u6014\uff0c\u9887\u4e3a\u610f\u5916\u5730\u62b1\u62f3\u9053\uff1a\u201c\u539f\u6765\u662f\u5b8b\u795e\u5077\uff0c\u5c0f\u8001\u513f\u5931\u656c\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u634f\u7740\u5c71\u7f8a\u80e1\u5b50\uff0c\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u5c0a\u9a7e\u5927\u540d\uff0c\u600e\u4e48\u79f0\u547c\uff1f\u201d \u3000\u3000\u90a3\u77ee\u8001\u4eba\u63a5\u9053\uff1a\u201c\u5c0f\u8001\u513f\u827e\u4e1c\u6d77\u3002\u201d \u3000\u3000\u5927\u6f20\u9b3c\u624b\u5b8b\u5b5a\uff0c\u8138\u4e0a\u795e\u60c5\u4e00\u53d8\uff0c\u4f46\u77ac\u5373\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u8e0f\u6ce2\u77ee\u53df\u5417\uff1f\u602a\u4e0d\u5f97\u6709\u8fd9\u597d\u7684\u8f7b\u529f\uff01\u201d \u3000\u3000\u827e\u4e1c\u6d77\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\u8bf4\uff0c\u5b8b\u5144\u7684\u8f7b\u529f\uff0c\u7d20\u6709\u5b87\u5185\u7b2c\u4e00\u4e4b\u79f0\uff0c\u5c0f\u8001\u513f\u8fd9\u4e00\u70b9\u624b\u811a\uff0c\u600e\u5f53\u5f97\u5b8b\u5144\u6cd5\u773c\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u4e00\u626c\u767d\u7709\uff0c\u7b11\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u827e\u5144\u62ac\u4e3e\u8001\u673d\u4e86\u3002\u201d \u3000\u3000\u6562\u60c5\u8fd9\u4e24\u4eba\u90fd\u662f\u6b66\u6797\u4e2d\u4ee5\u8f7b\u529f\u4eab\u8a89\u4e4b\u58eb\uff0c\u662f\u4ee5\u5f7c\u6b64\u4e45\u5df2\u95fb\u540d\uff0c\u4e00\u65e6\u76f8\u89c1\uff0c\u81ea\u662f\u514d\u4e0d\u4e86\u6709\u7740\u60fa\u60fa\u76f8\u60dc\u4e4b\u610f\u3002 \u3000\u3000\u9ad8\u65e0\u654c\u867d\u7136\u5bf9\u5927\u6f20\u9b3c\u624b\u7684\u540d\u6c14\u4e5f\u5f88\u94a6\u4f69\uff0c\u4f46\u5982\u4e0e\u4ed6\u5fc3\u4e2d\u5bf9\u65b9\u96ea\u5b9c\u7684\u656c\u610f\u76f8\u8f83\uff0c\u90a3\u53ef\u5dee\u7684\u5f88\u8fdc\u3002\u662f\u4ee5\uff0c\u4ed6\u5bb9\u5f97\u4e24\u4eba\u8bdd\u97f3\u4e00\u987f\uff0c\u7acb\u5373\u5411\u827e\u4e1c\u6d77\u9053\uff1a\u201c\u827e\u5144\uff0c\u8fd9\u4f4d\u662f\u65b9\u96ea\u5b9c\u65b9\u5144\u5f1f\uff0c\u827e\u5144\u5feb\u4e9b\u89c1\u8fc7\u3002\u201d \u3000\u3000\u827e\u4e1c\u6d77\u542c\u5230\u9ad8\u65e0\u654c\u8fd9\u822c\u53e3\u6c14\uff0c\u4e0d\u7981\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u65b9\u96ea\u5b9c\uff1f\u8001\u592b\u4f3c\u662f\u5c1a\u672a\u542c\u8fc7\u8fd9\u4f4d\u8001\u5f1f\u53f0\u7684\u540d\u53f7\u54e9\uff01\u201d \u3000\u3000\u9ad8\u65e0\u654c\u89c1\u4ed6\u8fd9\u7b49\u8bf4\u8bdd\uff0c\u5fc3\u4e2d\u8001\u5927\u4e0d\u9ad8\u5174\uff0c\u8138\u8272\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u827e\u5144\uff0c\u6709\u5fd7\u4e0d\u5728\u5e74\u9ad8\uff0c\u5144\u5f1f\u6bd4\u827e\u5144\u4e5f\u5e74\u8f7b\u4e0d\u5c11\uff0c\u4f46\u5728\u56db\u7075\u6559\u4e2d\uff0c\u597d\u50cf\u5144\u5f1f\u7684\u8eab\u4efd\u4e0d\u6bd4\u4f60\u827e\u5144\u4e3a\u4f4e\u3002\u201d \u3000\u3000\u827e\u4e1c\u6d77\u5bf9\u8fd9\u8c8c\u50cf\u6148\u539a\u7684\u9ad8\u65e0\u654c\uff0c\u4f3c\u662f\u6709\u7740\u51e0\u5206\u6212\u610f\uff0c\u95fb\u8a00\u5fd9\u9053\uff1a\u201c\u9ad8\u5144\u8bf4\u54ea\u91cc\u8bdd\uff0c\u5144\u5f1f\u51b3\u65e0\u8fd9\u7b49\u5fc3\u610f\u3002\u201d \u3000\u3000\u8bdd\u97f3\u7565\u4e3a\u4e00\u987f\uff0c\u5411\u65b9\u96ea\u5b9c\u62f1\u624b\u9053\uff1a\u201c\u65b9\u5144\u5f1f\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u76f4\u5728\u5fc3\u4e2d\u6697\u7b11\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u65b9\u96ea\u5b9c\uff0c\u89c1\u8fc7\u827e\u8001\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u8fd9\u624d\u8138\u8272\u4e00\u53d8\uff0c\u5475\u5475\u7b11\u9053\uff1a\u201c\u827e\u5144\uff0c\u8fd9\u4f4d\u65b9\u5144\u5f1f\u7684\u5e74\u7eaa\u867d\u4e0d\u5927\uff0c\u4f46\u4e00\u8eab\u529f\u529b\u4e4b\u9ad8\uff0c\u53ef\u662f\u5144\u5f1f\u6709\u751f\u4ee5\u6765\u6240\u4ec5\u89c1\uff0c\u53ea\u6015\u6559\u4e3b\u4e5f\u4e0d\u2026\u2026\u201d\u9ad8\u65e0\u654c\u4f3c\u662f\u53d1\u89c2\u81ea\u5df1\u4e00\u65f6\u8bf4\u6f0f\u4e86\u5634\uff0c\u8fde\u5fd9\u4f4f\u53e3\u4e0d\u8bed\u3002 \u3000\u3000\u827e\u4e1c\u6d77\u8138\u8272\u7a81\u7136\u4e00\u53d8\uff0c\u8131\u53e3\u9053\uff1a\u201c\u9ad8\u5144\uff0c\u8fd9\u4f4d\u65b9\u5144\u5f1f\u662f\u4ec0\u4e48\u4eba\u95e8\u4e0b\uff1f\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u65b9\u5144\u5f1f\u5e76\u672a\u8bf4\u51fa\uff0c\u4e0d\u8fc7\uff0c\u65b9\u5144\u5f1f\u7684\u6b66\u529f\uff0c\u5144\u5f1f\u5374\u662f\u4eb2\u773c\u89c1\u8bc6\u8fc7\u4e86\u3002\u201d \u3000\u3000\u827e\u4e1c\u6d77\u63a5\u9053\uff1a\u201c\u4ec0\u4e48\u5730\u65b9\uff1f\u201d\u663e\u7136\uff0c\u827e\u4e1c\u6d77\u6709\u4e9b\u4e0d\u76f8\u4fe1\u3002 \u3000\u3000\u9ad8\u65e0\u654c\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u5c31\u5728\u6b64\u5904\uff01\u201d \u3000\u3000\u827e\u4e1c\u6d77\u76ee\u5149\u5728\u9ad8\u65e0\u654c\u8eab\u4e0a\u8f6c\u4e86\u51e0\u8f6c\uff0c\u7b11\u9053\uff1a\u201c\u9ad8\u5144\uff0c\u8fd9\u4e48\u8bf4\u662f\u4f60\u81ea\u5df1\u4f38\u624b\u8f83\u91cf\u4e86\u8fd9\u4f4d\u8001\u5f1f\u7684\u6b66\u529f\u4e86\u5417\uff1f\u201d \u3000\u3000\u9ad8\u65e0\u654c\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u662f\u3002\u201d \u3000\u3000\u827e\u4e1c\u6d77\u53c8\u662f\u4e00\u6014\uff0c\u63a5\u9053\uff1a\u201c\u9ad8\u5144\u6ca1\u6709\u52a8\u624b\uff0c\u600e\u4f1a\u77e5\u9053\u65b9\u5144\u5f1f\u529f\u529b\u65e0\u654c\uff1f\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u827e\u5144\uff0c\u662f\u90a3\u7530\u62a4\u6cd5\u3002\u201d \u3000\u3000\u827e\u4e1c\u6d77\u9053\uff1a\u201c\u7530\u8fdc\uff1f\u201d\u63a5\u9053\uff1a\u201c\u4ed6\u5230\u54ea\u91cc\u53bb\u4e86\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u51b7\u7b11\u9053\uff1a\u201c\u4ed6\u5230\u9634\u90fd\u57ce\u4e2d\u4f5c\u5ba2\u53bb\u4e86\uff0c\u827e\u5144\uff0c\u90a3\u9b54\u5200\u7530\u8fdc\u7684\u5200\u6cd5\uff0c\u5728\u4f60\u773c\u4e2d\u5982\u4f55\uff1f\u201d \u3000\u3000\u827e\u4e1c\u6d77\u542c\u5f97\u7530\u8fdc\u5230\u4e86\u9634\u90fd\u57ce\uff0c\u81ea\u662f\u5403\u4e86\u4e00\u60ca\uff0c\u518d\u542c\u5f97\u9ad8\u65e0\u654c\u95ee\u53ca\u81ea\u5df1\u5bf9\u9b54\u5200\u7530\u8fdc\u7684\u5200\u6cd5\u8bc4\u4ef7\uff0c\u5f53\u884c\u6beb\u4e0d\u601d\u7d22\u7684\u63a5\u9053\uff1a\u201c\u5e94\u662f\u4e00\u6d41\u9ad8\u624b\u4e86\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u5012\u662f\u827e\u5144\u4e0e\u7530\u8fdc\u52a8\u624b\uff0c\u80fd\u5426\u6709\u7f6e\u4ed6\u4e8e\u6b7b\u5730\u7684\u628a\u63e1\u3002\u201d \u3000\u3000\u827e\u4e1c\u6d77\u5fd6\u9053\uff1a\u201c\u7530\u8fdc\u65e2\u7136\u5df2\u6b7b\u4e8e\u8fd9\u59d3\u65b9\u7684\u624b\u4e2d\uff0c\u81ea\u5df1\u7eb5\u7136\u5439\u4e0a\u51e0\u53e5\u5927\u8bdd\uff0c\u4e5f\u4e0d\u6015\u4ed6\u4eec\u63ed\u7a7f\u3002\u201d\u5f53\u4e0b\u51b7\u7b11\u9053\uff1a\u201c\u4e94\u5341\u62db\u4e4b\u5185\u5144\u5f1f\u5fc5\u53ef\u83b7\u80dc\u3002\u201d \u3000\u3000\u5728\u4ed6\u7684\u60f3\u8c61\u4e4b\u4e2d\uff0c\u65b9\u96ea\u5b9c\u8fd9\u70b9\u5e74\u7eaa\uff0c\u5373\u4ee4\u679c\u771f\u80fd\u591f\u80dc\u4e86\u9b54\u5200\u7530\u8fdc\uff0c\u90a3\u5fc5\u7136\u8981\u7ecf\u8fc7\u4e00\u756a\u82e6\u6218\uff0c\u8bf4\u4e0d\u5b9a\u8981\u6253\u5230\u53cc\u65b9\u7b4b\u75b2\u529b\u7aed\u7684\u5730\u6b65\uff0c\u81ea\u5df1\u8bf4\u5728\u4e94\u5341\u62db\u5185\u5fc5\u53ef\u83b7\u80dc\uff0c\u5b9a\u7136\u53ef\u4ee5\u5728\u65b9\u96ea\u5b9c\u9762\u524d\u4e89\u4e89\u5a01\u98ce\u7684\u4e86\u3002\u4f46\u4ed6\u600e\u80fd\u6599\u60f3\u4e0d\u5230\uff0c\u65b9\u96ea\u5b9c\u7684\u5251\u6cd5\uff0c\u9ad8\u660e\u5f97\u53eb\u4ed6\u8fde\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u3002 \u3000\u3000\u9ad8\u65e0\u654c\u5e94\u58f0\u5927\u7b11\u9053\uff1a\u201c\u827e\u5144\uff0c\u4f60\u8981\u4e94\u5341\u62db\u53ef\u53d6\u7530\u8fdc\u6027\u547d\uff0c\u4f46\u65b9\u5144\u5f1f\u4e48\uff0c\u4ed6\u53ea\u7528\u4e86\u5c1a\u672a\u53d8\u5316\u5b8c\u5168\u7684\u4e00\u62db\uff0c\u5c31\u5c06\u672c\u6559\u7530\u62a4\u6cd5\u9001\u5165\u4e86\u9b3c\u95e8\u5173\u4e2d\u3002\u201d \u3000\u3000\u827e\u4e1c\u6d77\u95fb\u8a00\u6123\u4e86\u534a\u664c\uff0c\u65b9\u9053\uff1a\u201c\u6709\u8fd9\u79cd\u4e8b\uff1f\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u5144\u5f1f\u4eb2\u76ee\u6240\u89c1\uff0c\u81ea\u662f\u4e0d\u9519\u3002\u201d \u3000\u3000\u827e\u4e1c\u6d77\u987f\u65f6\u6362\u4e86\u4e00\u526f\u795e\u60c5\uff0c\u888d\u62f3\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u65b9\u8001\u5f1f\uff0c\u5c0f\u8001\u513f\u8fd9\u53a2\u91cd\u65b0\u89c1\u793c\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u6562\u5f53\uff0c\u9ad8\u5144\u5982\u6b64\u63a8\u8bb8\u5728\u4e0b\uff0c\u5b9e\u5728\u6709\u4e9b\u8fc7\u5206\u4e86\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u7b11\u9053\uff1a\u201c\u4e0d\u8fc7\u5206\uff0c\u65b9\u5144\u5f1f\u6b66\u529f\u4e4b\u9ad8\uff0c\u5144\u5f1f\u77a7\u5f97\u5341\u5206\u6e05\u695a\uff0c\u827e\u5144\u4e5f\u4e0d\u5fc5\u4ee5\u4e3a\u5144\u5f1f\u66ff\u4ed6\u4eba\u5439\u5618\u4e86\u3002\u201d \u3000\u3000\u827e\u4e1c\u6d77\u663e\u7136\u5df1\u662f\u771f\u6709\u4e9b\u76f8\u4fe1\uff0c\u95fb\u8a00\u7b11\u9053\uff1a\u201c\u9ad8\u5144\uff0c\u8001\u673d\u731c\u60f3\uff0c\u90a3\u7530\u62a4\u6cd5\u5fc5\u6709\u81ea\u53d6\u6b7b\u8def\u4e4b\u6545\u2026\u2026\u201d \u3000\u3000\u9ad8\u65e0\u654c\u5927\u7b11\u9053\uff1a\u201c\u5f53\u7136\uff0c\u5426\u5219\u7684\u8bdd\uff0c\u5144\u5f1f\u4e5f\u4e0d\u4f1a\u5bb9\u8bb8\u65b9\u5144\u5f1f\u5728\u672c\u6559\u91cd\u5730\uff0c\u4f24\u6211\u6559\u4e2d\u62a4\u6cd5\uff0c\u201d \u3000\u3000\u827e\u4e1c\u6d77\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u9ad8\u5144\u8bf4\u5f97\u4e0d\u9519\uff0c\u4f46\u4e0d\u77e5\u65b9\u5144\u548c\u5b8b\u8001\u54e5\u600e\u5730\u4e0d\u5728\u5ba4\u5185\u8bf4\u8bdd\uff0c\u5374\u5728\u8fd9\u5ba4\u5916\u2026\u2026\u201d\u6562\u60c5\u4ed6\u8001\u4e8e\u4e16\u6545\uff0c\u5bf9\u4e8e\u65b9\u96ea\u5b9c\u7b49\u7684\u773c\u524d\u60c5\u51b5\uff0c\u4e00\u773c\u5c31\u770b\u51fa\u4ed6\u4eec\u5e76\u672a\u8fdb\u5165\u5ba4\u5185\u3002 \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u5144\u5f1f\u672c\u8981\u8bf7\u4ed6\u4eec\u4eba\u5185\uff0c\u4f46\u65b9\u5144\u5f1f\u548c\u5b8b\u8001\u5374\u5b81\u613f\u6682\u65f6\u9732\u5bbf\u5c4b\u5916\uff0c\u60f3\u6765\u771f\u662f\u5931\u793c\u5f97\u5f88\u2026\u2026\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u7b49\u4e8e\u6697\u6697\u544a\u8bc9\u4e86\u827e\u4e1c\u6d77\uff0c\u53cc\u65b9\u7684\u4ea4\u60c5\uff0c\u5e76\u4e0d\u600e\u4e48\u6df1\uff0c\u8bf4\u8bdd\u53ef\u8981\u5f53\u5fc3\u4e00\u4e9b\u624d\u597d\u3002 \u3000\u3000\u90a3\u827e\u4e1c\u6d77\u81ea\u7136\u4e00\u70b9\u5373\u900f\uff0c\u5f53\u4e0b\u7b11\u9053\uff1a\u201c\u9ad8\u5144\u89c9\u5f97\u5931\u793c\uff0c\u5144\u5f1f\u4e5f\u89c9\u4e0d\u5b89\uff0c\u2026\u2026\u201d\u8bdd\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u4f4f\u53e3\u4e0d\u8bed\u3002 \u3000\u3000\u9ad8\u65e0\u654c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u827e\u5144\uff0c\u4f60\u9002\u624d\u4f3c\u662f\u6709\u4ec0\u4e48\u6025\u4e8b\uff0c\u4e0d\u77e5\u662f\u5426\u6709\u5173\u5bf9\u5934\u65b9\u9762\u7684\u6d88\u606f\uff1f\u201d \u3000\u3000\u827e\u4e1c\u6d77\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u201d\u4ed6\u7565\u4e00\u6c89\u541f\uff0c\u53c8\u63a5\u9053\uff1a\u201c\u8001\u673d\u542c\u8bf4\uff0c\u90a3\u4f4d\u5996\u5973\u6628\u65e5\u5df2\u5728\u5609\u5b9a\u73b0\u8eab\u4e86\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u5144\u5f1f\u5df2\u7136\u63a5\u83b7\u4ee4\u8c15\uff0c\u6559\u4e3b\u5341\u65e5\u5185\u65e0\u6687\u62bd\u8eab\u6765\u6b64\u3002\u201d \u3000\u3000\u827e\u4e1c\u6d77\u5ffd\u7136\u4e00\u5446\uff0c\u9053\uff1a\u201c\u9ad8\u5144\uff0c\u54b1\u4eec\u2026\u2026\u201d \u3000\u3000\u9ad8\u65e0\u654c\u4e0d\u7b49\u827e\u4e1c\u6d77\u518d\u8bf4\uff0c\u7adf\u662f\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u827e\u5144\u4e0d\u5fc5\u803d\u5fc3\u4e86\uff0c\u6b64\u4e8b\u5144\u5f1f\u5df2\u6709\u4e86\u5b89\u6392\u3002\u201d\u76ee\u5149\u5411\u65b9\u96ea\u5b9c\u8eab\u4e0a\u4e00\u8f6c\uff0c\u7b11\u9053\uff1a\u201c\u65b9\u5144\u5f1f\u7559\u6b64\u4e0d\u53bb\uff0c\u5c31\u662f\u4e3a\u4e86\u66ff\u5144\u5f1f\u89e3\u6b64\u5927\u96be\uff01\u201d \u3000\u3000\u827e\u4e1c\u6d77\u4e5f\u66fe\u60f3\uff0c\u8fd9\u5b8b\u8001\u90aa\u548c\u65b9\u96ea\u5b9c\u7559\u6b64\u4e0d\u53bb\uff0c\u5fc5\u662f\u6709\u7740\u76f8\u5f53\u7684\u539f\u56e0\uff0c\u4f46\u4ed6\u5374\u65e0\u8bba\u5982\u4f55\uff0c\u4e5f\u60f3\u4e0d\u5230\u4ed6\u4eec\u7adf\u4f1a\u662f\u4e3a\u4e86\u5bf9\u4ed8\u90a3\u5bf9\u5934\u4e4b\u4eba\uff0c\u624d\u7559\u4e0b\u672a\u53bb\u3002\u56e0\u6b64\uff0c\u95fb\u8a00\u4e4b\u540e\uff0c\u7adf\u4e00\u8bed\u672a\u53d1\u7684\u5446\u4e86\u3002 \u3000\u3000\u9ad8\u65e0\u654c\u5fcd\u4e0d\u4f4f\u7b11\u9053\uff1a\u201c\u827e\u5144\u600e\u4e48\u4e86\uff1f\u201d \u3000\u3000\u827e\u4e1c\u6d77\u904f\uff1a\u201c\u8fd9\u4e8b\u4f3c\u662f\u6709\u4e9b\u2026\u2026\u6709\u4e9b\u5192\u9669\u554a\uff01\u201d \u3000\u3000\u539f\u6765\u4ed6\u867d\u7136\u76f8\u4fe1\u65b9\u96ea\u5b9c\u6b66\u529f\u4e0d\u5f31\uff0c\u4f46\u4ed6\u5374\u4e0d\u6562\u60f3\u50cf\uff0c\u90a3\u65b9\u96ea\u5b98\u7684\u6b66\u529f\uff0c\u8db3\u4e0e\u90a3\u4e94\u5927\u9b54\u4e3b\u4e2d\u4eba\u4e3a\u654c\u3002 \u3000\u3000\u5b8b\u5b5a\u9661\u7136\u5927\u7b11\u9053\uff1a\u201c\u827e\u5144\u4e0d\u76f8\u4fe1\u65b9\u8001\u5f1f\u5417\uff1f\u201d \u3000\u3000\u827e\u4e1c\u6d77\u9053\uff1a\u201c\u5144\u5f1f\u89c9\u5f97\u65b9\u5144\u5f1f\u5e74\u4e8b\u5982\u6b64\u4e4b\u8f7b\uff0c\u600e\u53ef\u4e0e\u90a3\u4e94\u5927\u9b54\u4e3b\u4e2d\u4eba\u76f8\u63d0\u5e76\u8bba\uff1f\u672c\u6559\u4e0e\u4ed6\u4eec\u6709\u4ec7\u6709\u6028\uff0c\u53d7\u5230\u5bf9\u65b9\u4f24\u5bb3\uff0c\u672c\u662f\u65e0\u8bdd\u53ef\u8bf4\uff0c\u4f46\u65b9\u5144\u5f1f\u4ed6\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u5ffd\u7136\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4eba\u5bb6\u4f3c\u662f\u4e0d\u5927\u76f8\u4fe1\u54b1\u4eec\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u76f4\u5728\u65c1\u5fae\u7b11\uff0c\u95fb\u8a00\u63a5\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u665a\u8f88\u65e2\u5df2\u7b54\u5e94\u4e86\u9ad8\u5144\uff0c\u597d\u6b79\u4e5f\u5f97\u7559\u6b64\u4e09\u65e5\u554a\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4eba\u5bb6\u4f3c\u662f\u4e0b\u4e86\u9010\u5ba2\u4ee4\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u4f1a\u5427\uff01\u9ad8\u5144\uff0c\u4f60\u8bf4\u662f\u5417\uff1f\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u90a3\u662f\u81ea\u7136\u7684\u4e86\uff0c\u5144\u5f1f\u627f\u8499\u65b9\u5144\u5f1f\u5e94\u5141\u7559\u4e0b\uff0c\u53c8\u600e\u4f1a\u8bf7\u65b9\u5144\u5f1f\u79bb\u53bb\u3002\u201d \u3000\u3000\u8f6c\u5934\u5411\u827e\u4e1c\u6d77\u9053\uff1a\u201c\u827e\u5144\uff0c\u65b9\u5144\u4e00\u8eab\u6b66\u529f\uff0c\u5144\u5f1f\u5df2\u7136\u89c1\u8bc6\u8fc7\u4e86\uff0c\u827e\u5144\u5927\u53ef\u4e0d\u7528\u803d\u5fc3\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5251\u7709\u626c\u4e86\u4e00\u626c\uff0c\u9053\uff1a\u201c\u9ad8\u5144\uff0c\u4f60\u548c\u827e\u5927\u4fa0\u5982\u662f\u6709\u4e8b\uff0c\u4e0d\u59a8\u5148\u56de\u53bb\u5427\uff01\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u90a3\u600e\u4e48\u884c\uff0c\u4e24\u4f4d\u4e43\u662f\u672c\u6559\u8d35\u5bbe\u2026\u2026\u201d \u3000\u3000\u4f59\u97f3\u672a\u7edd\uff0c\u7a81\u7136\u6797\u5916\u4f20\u6765\u4e00\u58f0\u5927\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u8fd9\u58f0\u66b4\u559d\uff0c\u6765\u7684\u5341\u5206\u7a81\u7136\uff0c\u9ad8\u65e0\u654c\u4e0d\u7981\u8138\u751f\u5927\u53d8\uff0c\u9053\uff1a\u201c\u827e\u5144\uff0c\u8fd9\u662f\u4f55\u4eba\u5728\u6797\u5916\u559d\u53eb\uff1f\u201d \u3000\u3000\u827e\u4e1c\u6d77\u5931\u58f0\u9053\uff1a\u201c\u8001\u592b\u7684\u624b\u4e0b\u4eba\u2026\u2026\u83ab\u975e\u4ed6\u4eec\u9047\u4e0a\u4e86\u654c\u624b\u5417\uff1f\u201d \u3000\u3000\u8fd9\u65f6\u6797\u5916\u5ffd\u7136\u53c8\u662f\u4e00\u58f0\u60e8\u53eb\u4f20\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u827e\u4e1c\u6d77\u8bdd\u97f3\u9661\u7136\u4e00\u987f\uff0c\u626d\u5934\u4fbf\u5411\u6797\u5916\u5954\u53bb\u3002 \u3000\u3000\u9ad8\u65e0\u654c\u4e00\u8a00\u672a\u53d1\uff0c\u8ddf\u5728\u827e\u4e1c\u6d77\u8eab\u540e\uff0c\u4e5f\u5306\u5306\u5fd9\u5fd9\u5730\u7a7f\u6797\u800c\u51fa\u3002 \u3000\u3000\u5b8b\u5b5a\u4f4e\u58f0\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u516b\u6210\u662f\u90a3\u8bdd\u513f\u6765\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u6014\u9053\uff1a\u201c\u662f\u90a3\u4e94\u9b54\u4e3b\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u5982\u662f\u8001\u673d\u6ca1\u6709\u6599\u9519\uff0c\u827e\u4e1c\u6d77\u90a3\u53e5\u5996\u5973\uff0c\u5e94\u662f\u8bf4\u660e\u6765\u6b64\u4e4b\u4eba\uff0c\u4e0d\u662f\u5929\u9b54\u5973\uff0c\u4fbf\u662f\u67f3\u5a9a\u5a18\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u54b1\u4eec\u8be5\u53bb\u770b\u770b\u3002\u201d \u3000\u3000\u4e24\u4eba\u5927\u6b65\u5411\u5916\u8d70\u53bb\u3002\u4e1b\u6797\u4e4b\u5916\uff0c\u53ea\u89c1\u90a3\u9ad8\u65e0\u654c\uff0c\u827e\u4e1c\u6d77\u4e24\u4eba\uff0c\u6b63\u5728\u8ddf\u4e00\u4f4d\u8eab\u6750\u751f\u5f97\u5341\u5206\u7ec6\u957f\uff0c\u7a7f\u7740\u4e00\u88ad\u9ed1\u8272\u7f57\u886b\uff0c\u5934\u633d\u5bab\u9afb\u7684\u5973\u4eba\u8bb2\u8bdd\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e0d\u77e5\u6b64\u662f\u8c01\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u8fd9\u5973\u4eba\u662f\u4e94\u9b54\u4e4b\u4e00\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u5e94\u8be5\u662f\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u4e86\u5446\uff0c\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u4f60\u600e\u4e48\u4f1a\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u7167\u6b64\u5973\u5f62\u8c8c\u800c\u8a00\uff0c\u5979\u5e94\u662f\u90a3\u4f4d\u59d3\u67f3\u7684\u5973\u9b54\uff0c\u4f46\u5979\u8eab\u4e0a\u672a\u5e26\u5979\u90a3\u4e00\u652f\u7389\u7bab\uff0c\u662f\u4ee5\u8001\u592b\u4e0d\u6562\u786e\u5b9a\u5979\u662f\u67f3\u5a9a\u5a18\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u54b1\u4eec\u8fc7\u53bb\u95ee\u95ee\u5427\uff01\u201d\u4e3e\u6b65\u76f4\u5411\u90a3\u9ed1\u8863\u5973\u4eba\u8d70\u53bb\u3002 \u3000\u3000\u5b8b\u5b5a\u62c5\u5fc3\u4ed6\u5e74\u8f7b\u6c14\u76db\uff0c\u4e0d\u77e5\u5229\u5bb3\uff0c\u4fbf\u4e5f\u8ddf\u7740\u79fb\u6b65\u4e0a\u524d\uff0c\u5e76\u4e14\u4f4e\u58f0\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u53ef\u83ab\u8981\u5192\u5931\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u665a\u8f88\u8bb0\u5f97\u3002\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u4e24\u4eba\u5df2\u8d70\u81f3\u9ad8\u65e0\u654c\u8eab\u65c1\u3002 \u3000\u3000\u6b64\u523b\uff0c\u90a3\u9ad8\u65e0\u654c\u6b63\u5728\u5341\u5206\u606d\u656c\u5730\u5411\u90a3\u5973\u4eba\u8bf4\u9053\uff1a\u201c\u8001\u524d\u8f88\u4e0e\u665a\u8f88\u6559\u4e3b\uff0c\u4e43\u662f\u9053\u5ea6\u4e4b\u4ea4\uff0c\u4eca\u65e5\u6765\u6b64\uff0c\u665a\u8f88\u9002\u56e0\u53e6\u6709\u4f73\u5bbe\u5728\u5ea7\uff0c\u672a\u66fe\u8fdc\u81f3\u5c71\u4e0b\u606d\u5019\uff0c\u5931\u793c\u4e4b\u5904\uff0c\u5c1a\u8bf7\u8001\u524d\u8f88\u4f11\u8981\u89c1\u602a\u3002\u201d \u3000\u3000\u9ed1\u8863\u5987\u4eba\u770b\u4e0a\u53bb\u4e0d\u8fc7\u4e09\u5341\u8bb8\u4eba\uff0c\u8fd9\u9ad8\u65e0\u654c\u5374\u5bf9\u5979\u8c26\u79f0\u665a\u8f88\uff0c\u5f88\u663e\u7136\u5df2\u662f\u8bf4\u53eb\u90a3\u5987\u4eba\u5fc5\u662f\u4e94\u5927\u9b54\u4e3b\u4e2d\u7684\u4e00\u4f4d\u4e86\u3002 \u3000\u3000\u827e\u4e1c\u6d77\u7709\u5934\u76b1\u4e86\u76b1\uff0c\u62b1\u62f3\u9053\uff1a\u201c\u67f3\u59d1\u5a18\uff0c\u8001\u673d\u4e5f\u4e0d\u8fc7\u662f\u9002\u624d\u65b9\u4ece\u5609\u5b9a\u8d76\u56de\uff0c\u56e0\u662f\u542c\u8bf4\u59d1\u5a18\u8981\u6765\uff0c\u4f46\u60f3\u4e0d\u5230\u59d1\u5a18\u7adf\u7136\u6765\u5f97\u5982\u6b64\u4e4b\u5feb\u3002\u201d\u6562\u60c5\uff0c\u8fd9\u9ed1\u8863\u5c11\u5987\uff0c\u6b63\u662f\u90a3\u67f3\u5a9a\u5a18\u3002 \u3000\u3000\u67f3\u5a9a\u5a18\u674f\u773c\u5bd2\u5149\u4e4d\u95ea\uff0c\u5a07\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u827e\u5927\u4fa0\u597d\u8bf4\uff0c\u90a3\u7ae5\u5b50\u5947\u76ee\u4e0b\u662f\u5426\u5728\u8305\u820d\u5185\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u95fb\u8a00\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u6697\u9053\uff1a\u201c\u539f\u6765\u8fd9\u56db\u7075\u6559\u7684\u6559\u4e3b\uff0c\u7adf\u7136\u662f\u5927\u5634\u9b42\u7ae5\u5b50\u5947\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u4e5f\u5728\u5bfb\u601d\uff1a\u201c\u8fd9\u56db\u7075\u6559\u65e2\u662f\u90a3\u4e94\u9b54\u4e2d\u7684\u7ae5\u5b50\u5947\u6240\u624b\u521b\uff0c\u4f46\u4e0b\u77e5\u5373\u4e09\u4e49\u53c8\u662f\u4f55\u4eba\u6240\u4e3b\u6301\u3002\u201d \u3000\u3000\u4e24\u4eba\u5bfb\u601d\u4e4b\u9645\uff0c\u67f3\u5a9a\u5a18\u5ffd\u7136\u683c\u683c\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u5982\u662f\u542c\u7ae5\u5b50\u5947\u4e0d\u5728\uff0c\u5c31\u51ed\u4f60\u4eec\u4e24\u4f4d\uff0c\u53ea\u6015\u4fdd\u4e0d\u4f4f\u8fd9\u5ea7\u56db\u7075\u6559\u7684\u5ddd\u897f\u5206\u5802\u4e86\u3002\u201d \u3000\u3000\u8a00\u4e0b\u4e4b\u610f\uff0c\u6b63\u662f\u627e\u78b4\u6765\u7684\u3002 \u3000\u3000\u9ad8\u65e0\u654c\u6d53\u7709\u4e00\u626c\uff0c\u9053\uff1a\u201c\u67f3\u524d\u8f88\uff0c\u655d\u6559\u4e3b\u4e0d\u5728\uff0c\u4f60\u8001\u7eb5\u7136\u6363\u6bc1\u9ad8\u67d0\u4eba\u5206\u5802\uff0c\u4e0d\u77e5\u4e0e\u4f60\u53c8\u6709\u4ec0\u4e48\u597d\u5904\uff1f\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u5996\u77e5\u9053\uff1a\u201c\u77a7\u4f60\u957f\u7684\u8fd9\u4e00\u8eab\u6a2a\u8089\uff0c\u5927\u6982\u5c31\u662f\u7ae5\u5b50\u5947\u624b\u4e0b\u90a3\u9ad8\u2014\u77ee\u2014\u5478\u2014\u7626\u56db\u5927\u9ad8\u624b\u4e2d\u7684\u9ad8\u65e0\u654c\u4e86\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u4e0d\u6562\uff01\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u9053\uff1a\u201c\u542c\u8bf4\u4f60\u6709\u4e2a\u795e\u529b\u5929\u738b\u7684\u79f0\u53f7\uff0c\u5b9a\u7136\u662f\u5929\u751f\u795e\u529b\u8fc7\u4eba\u4e86\u201d \u3000\u3000\u9ad8\u65e0\u654c\u7b11\u9053\uff1a\u201c\u9ad8\u67d0\u751f\u7684\u5341\u5206\u5f3a\u58ee\uff0c\u6709\u4e86\u51e0\u65a4\u86ee\u529b\uff0c\u90a3\u4e5f\u4e0d\u7b97\u4ec0\u4e48\uff0c\u5728\u67f3\u524d\u8f88\u773c\u4e2d\uff0c\u66f4\u662f\u4e0d\u503c\u5f97\u4e00\u7b11\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u7b11\u9053\uff1a\u201c\u4e0d\u8981\u7d27\uff0c\u53cd\u6b63\u7ae5\u5b50\u5947\u602a\u4e0d\u5230\u4f60\u7684\u4e86\uff0c\u4f60\u5927\u53ef\u4e0d\u7528\u62c5\u5fc3\u2026\u2026\u201d \u3000\u3000\u9ad8\u65e0\u654c\u4e43\u662f\u4e2a\u76f4\u5fc3\u773c\uff0c\u4e0d\u4f1a\u542c\u51fa\u67f3\u5a9a\u5a18\u8bdd\u4e2d\u4e4b\u610f\uff0c\u95fb\u8a00\u81ea\u662f\u5927\u611f\u610f\u5916\uff0c\u9053\uff1a\u201c\u67f3\u524d\u8f88\u53ef\u662f\u8981\u4ee3\u9ad8\u67d0\u5411\u6211\u6559\u4e3b\u7f13\u988a\u5417\uff1f\u201d \u3000\u3000\u67f3\u5a9a\u5fcd\u4e0d\u4f4f\u7b11\u7684\u5f2f\u4e86\u8170\u53bb\u3002 \u3000\u3000\u827e\u4e1c\u6d77\u76b1\u7709\u9053\uff1a\u201c\u9ad8\u5144\uff0c\u59d1\u5a18\u662f\u8bf4\u2026\u2026\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u5ffd\u7136\u5a9a\u773c\u4e00\u6311\uff0c\u5f3a\u5fcd\u4e86\u7b11\u610f\uff0c\u5a07\u53f1\u9053\uff1a\u201c\u59d3\u827e\u7684\uff0c\u4f60\u4ee5\u4e3a\u4f60\u6bd4\u4ed6\u806a\u660e\u662f\u5417\uff1f \u3000\u3000\u59d1\u5a18\u4eca\u513f\u5148\u6536\u62fe\u4f60\u4e5f\u884c\u3002\u201d \u3000\u3000\u8bdd\u97f3\u4e00\u843d\uff0c\u827e\u4e1c\u6d77\u53ea\u89c9\u773c\u524d\u4eba\u5f71\u4e00\u82b1\uff0c\u53f3\u988a\u4e4b\u4e0a\uff0c\u5df2\u7ecf\u62cd\u7684\u4e00\u58f0\uff0c\u6328\u4e86\u5bf9\u65b9\u4e00\u638c\uff0c\u4e94\u6761\u7ea4\u7ea4\u624b\u5370\u9f13\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5a9a\u5a18\u6253\u4e86\u827e\u4e1c\u6d77\u4e00\u4e2a\u8033\u5149\uff0c\u9ad8\u65e0\u654c\u4f3c\u662f\u8fd9\u624d\u60f3\u51fa\u4e86\u9002\u624d\u95ee\u67f3\u5a9a\u5a18\u7684\u8bdd\u4e2d\u4e4b\u610f\uff0c\u7a81\u7136\u4e24\u773c\u4e00\u77aa\uff0c\u5927\u559d\u9053\uff1a\u201c\u67f3\u5a9a\u5a18\uff0c\u4f60\u521a\u624d\u662f\u8bf4\u9ad8\u67d0\u6d3b\u4e0d\u5230\u6559\u4e3b\u56de\u6765\u5417\uff1f\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u867d\u7136\u95ea\u8eab\u6253\u4e86\u90a3\u827e\u4e1c\u6d77\u4e00\u638c\uff0c\u4f46\u56e0\u5979\u8eab\u5f62\u6781\u5feb\uff0c\u662f\u4ee5\u65e9\u5df2\u9000\u56de\u5230\u539f\u6765\u7684\u7acb\u8eab\u4e4b\u5904\uff0c\u9ad8\u65e0\u654c\u559d\u95ee\u752b\u6b47\uff0c\u5979\u7adf\u7136\u683c\u683c\u4e00\u7b11\u9053\uff1a\u201c\u8822\u725b\uff0c\u4f60\u624d\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u9ad8\u65e0\u6545\u867d\u7136\u660e\u660e\u77e5\u9053\u81ea\u5df1\u4e0d\u662f\u67f3\u5a9a\u5a18\u4e4b\u654c\uff0c\u4f46\u4ed6\u5374\u53d7\u4e0d\u4e86\u8fd9\u7b49\u8bbd\u523a\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u67f3\u5a9a\u5a18\uff0c\u4f60\u4e5f\u592a\u5c0f\u770b\u4e86\u9ad8\u67d0\u4e86\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4f60\u53ef\u662f\u4e0d\u670d\u6c14\u5417\uff1f\u201d \u3000\u3000\u9ad8\u65e0\u654c\u4e00\u6012\u4e4b\u4e0b\uff0c\u4f3c\u662f\u5fd8\u4e86\u5229\u5bb3\uff0c\u5927\u559d\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u9ad8\u67d0\u6b63\u8981\u5411\u4f60\u8ba8\u6559\u51e0\u624b\u9ad8\u660e\u6b66\u529f\u2026\u2026\u201d\u6325\u81c2\u4e00\u62f3\uff0c\u76f4\u6363\u67f3\u5a9a\u5a18\u5de6\u80a9\u3002 \u3000\u3000\u539f\u6765\u4ed6\u8fd9\u4e00\u62f3\u672c\u662f\u51fb\u5411\u5979\u7684\u80f8\u8179\uff0c\u4f46\u5ffd\u7136\u60f3\u8d77\u5bf9\u65b9\u4e43\u662f\u4e00\u4e2a\u5973\u6d41\u4e4b\u8f88\uff0c\u81ea\u5df1\u51fa\u62f3\u51fb\u5411\u5979\u7684\u80f8\u524d\uff0c\u5c82\u975e\u662f\u88ab\u4eba\u7b11\u4e3a\u8f7b\u8584\u4e4b\u5f92\u5417\uff1f\u62f3\u52bf\u5df2\u53d1\uff0c\u5374\u53c8\u4e34\u65f6\u53d8\u4e86\u65b9\u4f4d\u3002 \u3000\u3000\u67f3\u5a9a\u5a18\u76f4\u77a7\u5230\u9ad8\u65e0\u654c\u51fa\u62f3\u4e4b\u9645\uff0c\u674f\u773c\u500f\u7136\u95f4\u6740\u673a\u9690\u73b0\uff0c\u5927\u6709\u4e3e\u624b\u4e4b\u95f4\uff0c\u4fbf\u8981\u5236\u9ad8\u65e0\u654c\u4e8e\u6b7b\u5730\u4e4b\u610f\u3002\u4f46\u5f53\u67f3\u5a9a\u5a18\u53d1\u89c9\u9ad8\u65e0\u654c\u62f3\u52bf\u5728\u4e2d\u9014\u6362\u4e86\u4f4d\u7f6e\u4ee5\u540e\uff0c\u90a3\u51e4\u7709\u7565\u8212\uff0c\u672c\u5df2\u66f2\u8d77\u7684\u7389\u624b\u7ea4\u6307\uff0c\u7adf\u7136\u53d8\u4e3a\u7acb\u638c\u5982\u5200\u4e00\u822c\uff0c\u659c\u659c\u5730\u780d\u5411\u9ad8\u65e0\u654c\u3002 \u3000\u3000\u5b8b\u5b5a\u5728\u65c1\u77a7\u5f97\u5341\u5206\u6e05\u695a\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u65b9\u8001\u5f1f\uff0c\u90a3\u9ad8\u65e0\u654c\u4e00\u5ff5\u4e4b\u95f4\uff0c\u5c45\u7136\u8eb2\u8fc7\u4e00\u6b7b\u52ab\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u539f\u4e5f\u672a\u66fe\u77a7\u51fa\u5176\u4e2d\u539f\u6545\uff0c\u95fb\u8a00\u65b9\u59cb\u604d\u7136\uff0c\u7b11\u9053\uff1a\u201c\u4e0d\u9519\u554a\uff01\u8fd9\u9ad8\u5144\u5012\u4e5f\u4e0d\u5931\u662f\u541b\u5b50\u2026\u2026\u201d \u3000\u3000\u8fd9\u65f6\u5ffd\u7136\u542c\u5f97\u67f3\u5a9a\u5a18\u5a07\u559d\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u9ad8\u65e0\u654c\uff0c\u4f60\u8981\u60f3\u8ddf\u6211\u8d70\u4e0a\u5341\u62db\u516b\u62db\uff0c\u90a3\u8fd8\u5dee\u5f97\u5f88\u8fdc\uff0c\u5ff5\u5728\u4f60\u4e43\u662f\u4e00\u4e2a\u4e0d\u7518\u505a\u90a3\u8f7b\u8584\u4e4b\u4e8b\u7684\u83bd\u592b\uff0c\u6211\u53ea\u8981\u4f60\u6539\u6295\u6211\u67f3\u5a9a\u5a18\u7684\u624b\u4e0b\uff0c\u4fbf\u514d\u4f60\u4e00\u6b7b\uff0c\u4f60\u80af\u4e0d\u80af\uff1f\u201d \u3000\u3000\u4e24\u4eba\u4e43\u5728\u6fc0\u6597\u4e4b\u4e2d\uff0c\u67f3\u5a9a\u5a18\u4f9d\u7136\u80fd\u8fd9\u7b49\u4ece\u5bb9\u8bf4\u8bdd\uff0c\u8db3\u89c1\u5979\u6b66\u529f\u6bd4\u90a3\u9ad8\u65e0\u654c\u5f3a\u8fc7\u751a\u591a\u3002 \u3000\u3000\u9ad8\u65e0\u654c\u6012\u54fc\u4e86\u4e00\u58f0\uff0c\u4e00\u8fde\u53d1\u51fa\u4e03\u62f3\uff0c\u7b97\u662f\u7b54\u590d\u3002 \u3000\u3000\u67f3\u5a9a\u5a18\u8138\u8272\u5fae\u5fae\u4e00\u53d8\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u53ef\u662f\u4e0d\u8bc6\u62ac\u4e3e\u5417\uff1f\u60f9\u5f97\u6211\u6027\u8d77\uff0c\u4f60\u5c31\u4f11\u60f3\u6d3b\u547d\u4e86\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u5927\u559d\u9053\uff1a\u201c\u9ad8\u67d0\u53ea\u61c2\u5f97\u751f\u6b7b\uff0c\u4e0d\u61c2\u5f97\u6295\u964d\uff01\u201d \u3000\u3000\u549a\u7684\u4e00\u62f3\uff0c\u6b63\u597d\u51fb\u4e2d\u4e86\u67f3\u5a9a\u5a18\u7684\u638c\u5fc3\u3002 \u3000\u3000\u7167\u7406\u4ed6\u53f7\u79f0\u795e\u529b\u5929\u738b\uff0c\u8fd9\u4e00\u62f3\u81f3\u5c11\u4e5f\u6709\u51e0\u767e\u65a4\u529b\u9053\uff0c\u90a3\u4e48\u7ea4\u7626\u7684\u67f3\u5a9a\u5a18\uff0c\u53c8\u600e\u751f\u7981\u53d7\u5f97\u8d77\u4ed6\u8fd9\u4e00\u51fb\u3002 \u3000\u3000\u4f46\u65b9\u96ea\u5b9c\u5374\u77a7\u5f97\u51fa\uff0c\u9ad8\u65e0\u654c\u8fd9\u4e00\u62f3\u867d\u7136\u51fb\u4e2d\u4e86\u67f3\u5a9a\u5a18\u7684\u638c\u5fc3\uff0c\u5374\u4f3c\u662f\u6839\u672c\u6ca1\u6709\u7740\u529b\u4e4b\u5904\u4e00\u822c\uff0c\u672a\u66fe\u4f7f\u5f97\u4e0a\u52b2\u3002 \u3000\u3000\u987f\u65f6\uff0c\u65b9\u96ea\u5b9c\u611f\u5230\u5982\u662f\u518d\u8ba9\u4ed6\u8ddf\u90a3\u9b54\u5973\u62fc\u5c06\u4e0b\u53bb\uff0c\u7528\u4e0d\u7740\u67f3\u5a9a\u5a18\u8fd8\u624b\uff0c\u9ad8\u65e0\u654c\u5c31\u8981\u6d3b\u6d3b\u7d2f\u6b7b\u3002 \u3000\u3000\u4e00\u5ff5\u53ca\u6b64\uff0c\u4ed6\u7adf\u7136\u4e00\u8dc3\u800c\u524d\uff0c\u559d\u9053\uff1a\u201c\u9ad8\u5144\u5feb\u5feb\u9000\u4e0b\uff0c\u5bb9\u5144\u5f1f\u524d\u6765\u89c1\u8bc6\u89c1\u8bc6\u4e94\u5927\u9b54\u4e3b\u7684\u6b66\u529f\u2026\u2026\u201d\u4e3e\u624b\u4e00\u638c\uff0c\u5411\u90a3\u67f3\u5a9a\u5a18\u62cd\u53bb\u3002 \u3000\u3000\u67f3\u5a9a\u5a18\u683c\u683c\u5a07\u7b11\u3002\u8fde\u58f0\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4e2a\u5168\u4e0a\uff0c\u53ea\u6015\u4e5f\u652f\u6301\u4e0d\u4e86\u51e0\u5408\u3002\u201d\u7693\u8155\u4e00\u7ffb\uff0c\u5c31\u5f85\u53cd\u7f20\u65b9\u96ea\u5b9c\u624b\u638c\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u638c\u52bf\u5ffd\u7136\u4e00\u5e26\uff0c\u53d8\u638c\u4e3a\u6307\uff0c\u70b9\u5411\u67f3\u5a9a\u5a18\u66f2\u6c60\u7a74\uff0c\u53e3\u4e2d\u5374\u5411\u4ecd\u672a\u9000\u4e0b\u7684\u9ad8\u65e0\u654c\u8bf4\u9053\uff1a\u201c\u9ad8\u5144\uff0c\u54b1\u4eec\u6709\u7ea6\u5728\u5148\uff0c\u4f60\u4e0d\u5fc5\u518d\u5939\u5728\u5176\u95f4\u7684\u4e86\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u76ee\u7779\u65b9\u96ea\u5b9c\u53d8\u62db\u4e4b\u5feb\uff0c\u5b9e\u662f\u751f\u5e73\u4ec5\u89c1\uff0c\u4e0d\u7981\u8138\u4e0a\u53d8\u8272\uff0c\u60ca\u54a6\u4e86\u4e00\u58f0\uff0c\u98de\u8eab\u63a0\u9000\u4e08\u4e94\u3002 \u3000\u3000\u674f\u773c\u5706\u7741\uff0c\u51b7\u51b7\u5730\u559d\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff0c\u7ae5\u5b50\u5947\u624b\u4e0b\uff0c\u51e0\u65f6\u6709\u4e86\u8fd9\u7b49\u9ad8\u624b\uff1f\u201d \u3000 \u7b2c\u5341\u56db\u56de\u3000\u521d\u6597\u5973\u9b54 \u3000 \u3000\u3000\u65b9\u96ea\u5b9c\u672a\u66fe\u7b54\u8bdd\uff0c\u5374\u662f\u56de\u987e\u4e86\u9ad8\u65e0\u654c\u4e00\u773c\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u9ad8\u5144\uff0c\u4f60\u9002\u624d\u90a3\u4e00\u62f3\u6328\u5230\u90a3\u5973\u9b54\u7684\u638c\u5fc3\uff0c\u663e\u7136\u52b2\u9053\u5168\u88ab\u9b54\u5973\u5378\u6389\uff0c\u4e0d\u77e5\u9ad8\u5144\u53ef\u66fe\u8fd0\u6c14\u8bd5\u8fc7\uff0c\u5bf9\u65b9\u638c\u5fc3\u4e4b\u4e2d\uff0c\u662f\u5426\u8574\u6709\u4ec0\u4e48\u5947\u6bd2\u4e4b\u7269\uff1f\u201d \u3000\u3000\u9ad8\u65e0\u654c\u5446\u4e86\u4e00\u5446\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u5144\u5f1f\u4e0d\u66fe\u89c9\u51fa\u6709\u4f55\u5f02\u6837\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u90a3\u5c31\u597d\uff01\u9ad8\u5144\u5feb\u5feb\u9000\u5728\u4e00\u8fb9\uff0c\u67f3\u5a9a\u5a18\u4ea4\u7ed9\u5144\u5f1f\u4fbf\u662f\u4e86\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u611f\u6fc0\u5730\u5411\u65b9\u96ea\u5b9c\u62b1\u62f3\u9053\uff1a\u201c\u6709\u52b3\u65b9\u5144\uff01\u2026\u2026\u201d\u5927\u6b65\u9000\u5230\u4e08\u8bb8\u4e4b\u5916\u3002 \u3000\u3000\u67f3\u5a9a\u5a18\u77a7\u7740\u65b9\u96ea\u5b9c\u8fd9\u7b49\u4ece\u5bb9\u795e\u6001\uff0c\u5fc3\u4e2d\u5927\u611f\u5947\u602a\uff0c\u6697\u9053\uff1a\u8fd9\u4eba\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u600e\u4f1a\u5982\u6b64\u9547\u5b9a\uff0c\u542c\u4ed6\u53e3\u6c14\uff0c\u5012\u597d\u50cf\u4e0d\u662f\u56db\u7075\u6559\u4e2d\u4e4b\u4eba\u2026\u2026 \u3000\u3000\u5979\u5bfb\u601d\u4e4b\u95f4\uff0c\u65b9\u96ea\u5b9c\u5df2\u5fae\u5fae\u4e00\u7b11\uff0c\u62b1\u62f3\u9053\uff1a\u201c\u4e94\u5927\u9b54\u4e3b\u4e4b\u540d\uff0c\u533a\u533a\u65e9\u5df2\u8033\u95fb\uff0c\u4eca\u65e5\u5e78\u4f1a\uff0c\u679c\u771f\u4e0d\u51e1\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u7709\u5934\u626c\u4e00\u626c\uff0c\u683c\u683c\u5730\u7b11\u4e86\u51e0\u58f0\uff0c\u9053\uff1a\u201c\u5c0f\u8001\u5f1f\uff0c\u6211\u521a\u624d\u95ee\u4f60\u7684\u8bdd\uff0c\u4f60\u600e\u4e48\u4e0d\u7b54\u554a\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u9b54\u4e3b\u53ef\u662f\u95ee\u7684\u533a\u533a\u59d3\u540d\u5417\uff1f\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u9053\uff1a\u201c\u4f60\u80af\u4e0d\u80af\u8bf4\uff1f\u201d\u542c\u5979\u7684\u53e3\u6c14\uff0c\u5012\u662f\u5341\u5206\u5ba2\u6c14\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5728\u4e0b\u59d3\u65b9\uff0c\u8349\u5b57\u96ea\u5b9c\uff0c\u4e43\u662f\u4e2d\u5dde\u4eba\u6c0f\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u7b11\u9053\uff1a\u201c\u4f60\u5e08\u7236\u5462\uff1f\u5728\u6c5f\u6e56\u4e0a\u5b9a\u7136\u5f88\u6709\u540d\u6c14\u7684\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff0c\u201c\u5bb6\u5e08\u5c71\u91ce\u9690\u58eb\uff0c\u4e0d\u8bf4\u4e5f\u7f62\uff01\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u4e00\u6014\u9053\uff1a\u201c\u77a7\u4f60\u7684\u51fa\u624b\u6b66\u529f\uff0c\u597d\u50cf\u5f97\u81ea\u540d\u5bb6\u6307\u70b9\uff0c\u6211\u4e0d\u4fe1\u5c71\u91ce\u9690\u58eb\uff0c\u80fd\u6559\u51fa\u4f60\u8fd9\u7b49\u597d\u5f92\u5f1f\u6765\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5728\u4e0b\u8bf4\u7684\u5b9e\u8bdd\uff0c\u9b54\u4e3b\u4e0d\u4fe1\uff0c\u4e5f\u662f\u6ca1\u6cd5\u53ef\u60f3\u4e86\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u683c\u683c\u4e00\u7b11\u9053\uff1a\u201c\u597d\u554a\uff0c\u4f60\u8fd9\u4e48\u4e00\u70b9\u5e74\u7eaa\uff0c\u5012\u662f\u5b66\u5f97\u56fa\u6267\u5f97\u5f88\u2026\u2026\u201d\u7b11\u58f0\u4e00\u655b\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u6211\u4e0d\u6562\u4f24\u4f60\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u5e76\u65e0\u6b64\u610f\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u9053\uff1a\u201c\u4f60\u660e\u767d\u5c31\u597d\u3002\u201d\u5ffd\u7136\u6389\u5934\u5411\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u7ae5\u5b50\u5947\u81ea\u5df1\u4e0d\u5728\uff0c\u7559\u4e0b\u4e86\u4f60\u4eec\u8fd9\u9ad8\u77ee\u4e24\u4e2a\u8113\u5305\uff0c\u81ea\u5df1\u4e0d\u6562\u51fa\u5934\uff0c\u5374\u627e\u4e86\u8fd9\u4e48\u4e00\u4e2a\u9ec4\u53e3\u4e73\u5b50\u4ee3\u4f60\u4eec\u51fa\u5934\u3002\u77a7\u4f60\u4eec\u4e5f\u4e0d\u5bb3\u81ca\u5417\uff1f\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\uff0c\u53ea\u628a\u9ad8\u65e0\u654c\u548c\u827e\u4e1c\u6d77\u8bbd\u523a\u7684\u65e0\u5730\u5bb9\u8eab\uff0c\u6da8\u7ea2\u7740\u8138\u534a\u664c\u8bf4\u4e0d\u51fa\u4e00\u4e2a\u5b57\u6765\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u9b54\u4e3b\u4e0d\u5fc5\u8bbd\u523a\u4ed6\u4eec\uff0c\u8bf4\u6765\u8fd9\u4e8b\u4e43\u662f\u5728\u4e0b\u81ea\u5df1\u627e\u4e0a\u8eab\u6765\u7684\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u6709\u591a\u5927\u7684\u80c6\u5b50\uff1f\u7adf\u6562\u628a\u8fd9\u7b49\u5927\u4e8b\u63fd\u5728\u8eab\u4e0a\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u589e\u957f\u89c1\u8bc6\uff0c\u6709\u4f55\u4e0d\u53ef\uff1f\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u5e74\u7eaa\u8fd8\u8f7b\uff0c\u5047\u4ee5\u65f6\u65e5\uff0c\u5982\u80fd\u597d\u597d\u529b\u6c42\u4e0a\u8fdb\uff0c\u4e5f\u8bb8\u4f1a\u6709\u51fa\u4eba\u5934\u5730\u4e4b\u66f0\uff0c\u4f46\u82e5\u50cf\u773c\u524d\u8fd9\u7b49\u5192\u5931\uff0c\u53ea\u6015\u5c31\u96be\u6709\u5584\u7ec8\u4e86\u3002\u201d\u542c\u5979\u8bf4\u8bdd\uff0c\u5012\u4e5f\u4e0d\u4f3c\u4ec0\u4e48\u51f6\u6076\u4e4b\u4eba\u3002 \u3000\u3000\u4f46\u65b9\u96ea\u5b9c\u660e\u767d\uff0c\u67f3\u5a9a\u5a18\u8fd9\u4e48\u5fcd\u8ba9\uff0c\u53ea\u6015\u4e5f\u6709\u539f\u56e0\u3002 \u3000\u3000\u5f53\u4e0b\u5fae\u5fae\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u5728\u4e0b\u6709\u5426\u51fa\u4eba\u5934\u5730\u4e4b\u65e5\uff0c\u4e0d\u52b3\u9b54\u4e3b\u6302\u5fc3\uff0c\u4e5f\u8bb8\u4e00\u65e5\u4e4b\u95f4\uff0c\u5c31\u53ef\u540d\u626c\u5929\u4e0b\u4e86\u54e9\uff01\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u95fb\u8a00\u4e00\u6014\u9053\uff1a\u201c\u4f60\u2026\u2026\u597d\u554a\uff0c\u6562\u60c5\u4f60\u5c0f\u5144\u5f1f\u4f9d\u65e7\u662f\u96c4\u5fc3\u4e07\u4e08\uff0c\u60f3\u8981\u5728\u4e94\u5927\u9b54\u4e3b\u8eab\u4e0a\u626c\u540d\u95ef\u4e07\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5c0a\u9a7e\u5012\u662f\u660e\u767d\u4eba\u2026\u2026\u201d \u3000\u3000\u4f59\u97f3\u672a\u5df2\uff0c\u53ea\u542c\u5f97\u67f3\u5a9a\u5a18\u5a07\u7b11\u8fde\u58f0\uff0c\u9053\uff1a\u201c\u672c\u5ea7\u51fa\u9053\u4ee5\u6765\uff0c\u8fd9\u662f\u5934\u4e00\u6b21\u78b0\u5230\u50cf\u4f60\u5c0f\u5144\u5f1f\u8fd9\u7b49\u72c2\u4eba\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7684\u7528\u610f\uff0c\u5c31\u662f\u60f3\u89e6\u6012\u90a3\u67f3\u5a9a\u5a18\uff0c\u4ee5\u8bd5\u4e94\u5927\u9b54\u4e3b\u529f\u529b\uff0c\u7a76\u7adf\u6709\u591a\u4e48\u9ad8\u660e\uff0c\u56e0\u6b64\uff0c\u67f3\u5a9a\u5a18\u8bdd\u97f3\u4e00\u843d\uff0c\u4ed6\u7acb\u5373\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u4e0e\u9ad8\u5144\u5f1f\u6709\u7ea6\u5728\u5148\uff0c\u8fd9\u4e09\u5929\u4e4b\u5185\uff0c\u4e0d\u8bba\u6765\u7684\u662f\u4ec0\u4e48\u4eba\uff0c\u90fd\u4e0d\u8bb8\u4ed6\u5728\u56db\u7075\u6559\u5ddd\u897f\u5206\u5802\u4e4b\u524d\u64a4\u91ce\uff0c\u5927\u4e08\u592b\u4e00\u8bfa\u5343\u91d1\uff0c\u81ea\u662f\u4e0d\u80fd\u8bf4\u8fc7\u4e0d\u7b97\u7684\u4e86\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\u679c\u7136\u8981\u4ee3\u56db\u7075\u6559\u51fa\u5934\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u65e2\u5df2\u7ea6\u8d70\uff0c\u5728\u4e0b\u81ea\u5e94\u52c9\u529b\u4ee5\u8d74\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u5ffd\u7136\u5a07\u7b11\u9053\uff1a\u201c\u597d\u554a\uff01\u6211\u5c31\u77a7\u77a7\u4f60\u7684\u80fd\u8010\u3002\u201d \u3000\u3000\u7389\u624b\u4e00\u6325\uff0c\u8f7b\u98d8\u98d8\u5730\u62cd\u51fa\u4e00\u638c\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u53e3\u4e2d\u867d\u7136\u8bf4\u7684\u5341\u5206\u81ea\u5927\uff0c\u4f46\u5fc3\u4e2d\u53ef\u662f\u8c28\u614e\u5f97\u5f88\uff0c\u4e94\u5927\u9b54\u4e3b\u65e2\u80fd\u5728\u6b66\u6797\u4e4b\u4e2d\u83b7\u5f97\u90a3\u4e48\u9ad8\u7684\u58f0\u8a89\uff0c\u5c82\u975e\u5e78\u5f97\u800c\u6765\uff0c\u4f55\u51b5\uff0c\u8fde\u8eab\u4e3a\u5251\u795e\u7684\u5e08\u7236\uff0c\u4e5f\u53ea\u80fd\u5236\u670d\u4ed6\u4eec\uff0c\u7a0d\u655b\u4ed6\u4eec\u7684\u51f6\u7130\uff0c\u800c\u65e0\u4ece\u4e00\u4e00\u4e0b\u624b\u8bdb\u9664\uff0c\u81ea\u5df1\u8f83\u6069\u5e08\uff0c\u76f8\u5dee\u751a\u8fdc\u3002\u5982\u4eca\u9762\u5bf9\u4e94\u5927\u9b54\u4e3b\u4e4b\u4e00\uff0c\u5f53\u7136\u4e0d\u6562\u5927\u610f\u3002 \u3000\u3000\u67f3\u5a9a\u5a18\u7389\u624b\u4e00\u6325\uff0c\u4ed6\u7acb\u5373\u64a4\u5251\u6a2a\u5728\u80f8\u524d\uff0c\u63a5\u9053\uff1a\u201c\u5728\u4e0b\u6b63\u8981\u9886\u6559\u2026\u2026\u201d\u5bd2\u5149\u4e4d\u95ea\uff0c\u5b9d\u5251\u5e73\u5e73\u524a\u51fa\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u4ed6\u7528\u7684\u662f\u91d1\u9876\u795e\u5c3c\u4f20\u6388\u7684\u5251\u6cd5\uff0c\u662f\u4ee5\u67f3\u5a9a\u5a18\u867d\u7136\u770b\u6765\u5341\u5206\u773c\u719f\uff0c\u800c\u4e14\u66f4\u662f\u7280\u5229\u65e0\u5339\uff0c\u8feb\u5f97\u5979\u8fde\u5fd9\u7f29\u624b\u540e\u9000\uff0c\u4f46\u5374\u672a\u66fe\u77a7\u51fa\u5251\u6cd5\u7684\u6765\u5386\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u51fa\u624b\u4e00\u5251\uff0c\u8feb\u9000\u4e86\u4e94\u5927\u9b54\u4e3b\u7684\u67f3\u5a9a\u5a18\uff0c\u4e0d\u7981\u96c4\u5fc3\u5927\u53d1\uff0c\u957f\u5578\u4e00\u58f0\uff0c\u6325\u5251\u62a2\u653b\u8fc7\u53bb\u3002 \u3000\u3000\u67f3\u5a9a\u5a18\u674f\u773c\u4e2d\u63a0\u8fc7\u4e00\u4e1d\u60ca\u8bb6\u7684\u773c\u8272\uff0c\u5a07\u7b11\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u597d\u5251\u6cd5\u3002\u201d\u7693\u8155\u8f7b\u8212\uff0c\u66f2\u6307\u5982\u94a9\uff0c\u7adf\u7136\u7528\u90a3\u7a7a\u624b\u593a\u767d\u5203\u7684\u62db\u672f\uff0c\u6263\u5411\u65b9\u96ea\u5b9c\u624b\u4e2d\u5b9d\u5251\u3002 \u3000\u3000\u67f3\u5a9a\u5a18\u662f\u4e94\u5927\u9b54\u4e3b\u4e4b\u4e00\uff0c\u529f\u529b\u8fd0\u7528\u7531\u5fc3\uff0c\u867d\u662f\u4e00\u53cc\u7389\u624b\uff0c\u4e00\u65e6\u65bd\u5c55\u5f00\u6765\uff0c\u6bd4\u90a3\u7cbe\u94a2\u5b9d\u5251\uff0c\u4e5f\u4e0d\u7a0d\u900a\u8272\u3002 \u3000\u3000\u5728\u5979\u5fc3\u5ff5\u4e4b\u4e2d\uff0c\u50cf\u65b9\u96ea\u5b9c\u8fd9\u7b49\u521d\u51fa\u8305\u5e90\u7684\u5c0f\u8f88\uff0c\u7eb5\u7136\u5f97\u8fc7\u540d\u5e08\u6307\u70b9\uff0c\u5b66\u4f1a\u4e86\u51e0\u62db\u5947\u5999\u7684\u5251\u6cd5\uff0c\u90a3\u53ea\u80fd\u5bf9\u4ed8\u4e00\u822c\u6b66\u6797\u89d2\u8272\uff0c\u9047\u5230\u81ea\u5df1\uff0c\u5e94\u662f\u4e0d\u51fa\u4e09\u62db\uff0c\u5c31\u5c06\u675f\u624b\u675f\u811a\u624d\u5bf9\u3002 \u3000\u3000\u6b8a\u4e0d\u6599\u4e8b\u5b9e\u5374\u662f\u5927\u51fa\u67f3\u5a9a\u5a18\u7684\u610f\u6599\u4e4b\u5916\uff0c\u5979\u90a3\u4e00\u62db\u7a7a\u624b\u5165\u767d\u5203\u7684\u624b\u52bf\u5c1a\u672a\u5c55\u5f00\uff0c\u65b9\u96ea\u5b9c\u624b\u4e2d\u5b9d\u5251\uff0c\u7adf\u662f\u5341\u5206\u8f7b\u7075\u7684\u4e00\u95ea\uff0c\u9519\u8fc7\u4e86\u5979\u7684\u4e94\u6307\uff0c\u6572\u5411\u5979\u8098\u8282\u7a74\u9053\u3002 \u3000\u3000\u67f3\u5a9a\u5a18\u5fc3\u4e2d\u9a87\u7136\u9053\uff1a\u201c\u6b64\u4eba\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u600e\u7684\u7ec3\u5c31\u4e86\u8fd9\u7b49\u60ca\u4eba\u7684\u5251\u672f\uff1f\u201d\u5979\u90a3\u9a84\u654c\u4e4b\u5fc3\uff0c\u5ffd\u7136\u4e00\u626b\u800c\u7a7a\uff0c\u8fc5\u5feb\u65e0\u6bd4\u7684\u5411\u540e\u9000\u8dc3\u4e86\u4e09\u6b65\uff0c\u7389\u8155\u4e00\u626c\uff0c\u624b\u4e2d\u5df2\u591a\u51fa\u4e00\u6839\u7fe0\u7389\u8427\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u65e9\u8be5\u53d6\u51fa\u7389\u8427\u7684\u4e86\u3002\u201d \u3000\u3000\u8bdd\u97f3\u4e00\u987f\uff0c\u5b9d\u5251\u4e00\u6643\uff0c\u6b3a\u8eab\u62a2\u653b\u8fc7\u53bb\u3002 \u3000\u3000\u67f3\u5a9a\u5a18\u7389\u7bab\u5728\u624b\uff0c\u8138\u4e0a\u6740\u673a\u9661\u73b0\uff0c\u51b7\u51b7\u5730\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u65e0\u77e5\u5c0f\u8f88\uff0c\u8001\u5a18\u7eb5\u6709\u60dc\u624d\u4e4b\u5fc3\uff0c\u4e5f\u65e0\u6cd5\u5bb9\u4f60\u591a\u6d3b\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u9752\u8292\u78a7\u5f71\u66b4\u73b0\uff0c\u4f46\u95fb\u5f97\u4e00\u9635\u4f4e\u5578\u4e4b\u58f0\u5165\u8033\uff0c\u65b9\u96ea\u5b9c\u653b\u51fa\u7684\u5251\u52bf\uff0c\u7adf\u7136\u9047\u4e0a\u4e86\u6781\u5927\u7684\u963b\u529b\uff0c\u538b\u5f97\u53f3\u81c2\u51e0\u4e4e\u4f38\u4e0d\u5f00\u53bb\u3002 \u3000\u3000\u5927\u60ca\u4e4b\u4e0b\uff0c\u8fc5\u5feb\u7684\u64a4\u5251\u540e\u9000\u4e09\u5c3a\uff0c\u5de6\u624b\u4e00\u6325\uff0c\u5168\u529b\u4e00\u638c\uff0c\u62cd\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u7a76\u7adf\u5e74\u8f7b\uff0c\u5bf9\u654c\u7ecf\u9a8c\u53c8\u5acc\u4e0d\u591f\uff0c\u9047\u5230\u50cf\u67f3\u5a9a\u5a18\u8fd9\u7b49\u9ad8\u624b\uff0c\u4e00\u8eab\u6b66\u529f\uff0c\u5341\u6210\u53ea\u4f7f\u5f97\u51fa\u4e94\u6210\uff0c\u65e0\u5f62\u4e4b\u4e2d\uff0c\u5c31\u5403\u4e86\u5927\u4e8f\u3002 \u3000\u3000\u603b\u7b97\u4ed6\u4e34\u5371\u4e0d\u4e71\uff0c\u53ca\u65f6\u4e00\u638c\uff0c\u6321\u4f4f\u4e86\u67f3\u5a9a\u5a18\u524d\u51b2\u4e4b\u52bf\uff0c\u7f13\u4e86\u4e00\u53e3\u6c14\uff0c\u957f\u5251\u4e00\u9886\uff0c\u4e00\u5f0f\u9f99\u884c\u516b\u5251\u4e2d\u7684\u201c\u6392\u4e91\u5410\u96fe\u201d\u5b9e\u5219\u5316\u6210\u5343\u4e07\u6735\u94f6\u82b1\uff0c\u7f69\u4f4f\u4e86\u8eab\u524d\u4e08\u8bb8\u65b9\u56ed\u3002 \u3000\u3000\u7ad9\u5728\u4e00\u65c1\u7684\u9ad8\u65e0\u654c\u3001\u827e\u4e1c\u6d77\uff0c\u53ea\u611f\u5230\u90a3\u68ee\u68ee\u7684\u5251\u6c14\uff0c\u5b9b\u5982\u6392\u5c71\u5012\u6d77\u4e00\u822c\u88ad\u6765\uff0c\u9a87\u7136\u4e4b\u4e0b\uff0c\u5404\u81ea\u98de\u8eab\u9000\u4e86\u4e03\u5c3a\u3002 \u3000\u3000\u5b8b\u5b5a\u5219\u77a7\u5f97\u5fcd\u4e0d\u4f4f\u559d\u9053\uff1a\u201c\u9f99\u884c\u516b\u5251\u3002\u201d\u6562\u60c5\uff0c\u4ed6\u53ea\u77e5\u9053\u9648\u5e0c\u6b63\u7684\u8fd9\u4e00\u5957\u5251\u6cd5\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u4e0d\u77e5\u9053\u7684\u662f\u773c\u4e0b\u65b9\u96ea\u5b9c\u65bd\u5c55\u7684\u4e43\u662f\u201c\u9f99\u884c\u4e5d\u5251\u201d\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u67f3\u5a9a\u5a18\u4e5f\u5df2\u77a7\u51fa\u4e86\u65b9\u96ea\u5b9c\u7684\u5251\u6cd5\uff0c\u5fc3\u4e2d\u5927\u5927\u4e00\u9707\uff0c\u5a07\u53f1\u4e86\u4e00\u58f0\uff0c\u559d\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\u4f4f\u624b\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e5f\u77e5\u9053\u4e00\u65e6\u6b64\u5f0f\u5251\u6cd5\u51fa\u624b\uff0c\u90a3\u67f3\u5a9a\u5a18\u5fc5\u7136\u4f1a\u8ba4\u51fa\u81ea\u5df1\u6765\u5386\uff0c\u6545\u800c\u4ed6\u4e5f\u5e76\u672a\u5b58\u4e0b\u4f24\u654c\u4e4b\u5fc3\uff0c\u95fb\u8a00\u7acb\u5373\u64a4\u5251\u6536\u62db\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9b54\u4e3b\u8fd8\u6709\u4ec0\u4e48\u89c1\u6559\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u76ee\u5149\u51dd\u6ce8\u5728\u65b9\u96ea\u5b9c\u8138\u4e0a\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u90a3\u5251\u6cd5\u53ef\u662f\u9648\u5e0c\u6b63\u8001\u4eba\u6240\u6388\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u201d\u4ed6\u5c1a\u672a\u51b3\u5b9a\u662f\u5426\u628a\u81ea\u5df1\u5e08\u7236\u5df2\u6b7b\u7684\u4e8b\u8ba9\u4e94\u5927\u9b54\u4e3b\u77e5\u9053\uff0c\u662f\u4ee5\u67f3\u5a9a\u5a18\u6307\u540d\u76f8\u8be2\uff0c\u4ed6\u4e0d\u7981\u611f\u5230\u4e86\u72b9\u8c6b\u3002 \u3000\u3000\u67f3\u5a9a\u5a18\u77a7\u4ed6\u6c89\u541f\u4e0d\u8bed\uff0c\u63a5\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u5251\u795e\u9648\u5927\u4fa0\u662f\u4f60\u7684\u5e08\u7236\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fd6\u9053\uff1a\u201c\u6211\u5982\u82e5\u5e94\u662f\uff0c\u53ea\u6015\u5979\u8fd8\u8981\u95ee\u4e0a\u66f4\u591a\u7684\u4e8b\u4e86\uff0c\u5230\u65f6\u81ea\u5df1\u5c31\u65e0\u6cd5\u4e0d\u628a\u5e08\u7236\u5df2\u6b7b\u7684\u4e8b\u8bf4\u5c06\u51fa\u6765\uff0c\u4e94\u5927\u9b54\u4e3b\u5982\u662f\u77e5\u9053\u4e86\u5e08\u7236\u7684\u6b7b\u8baf\uff0c\u8fd9\u4e16\u95f4\u4e4b\u4e0a\uff0c\u5bf9\u4ed6\u4eec\u5df2\u65e0\u5177\u6709\u5a01\u80c1\u4e4b\u4eba\uff0c\u7728\u773c\u4e4b\u95f4\uff0c\u5c31\u53ef\u80fd\u9020\u6210\u65e0\u8fb9\u7684\u6740\u52ab\u4e86\u2026\u2026\u201d \u3000\u3000\u539f\u6765\u4ed6\u60f3\u5230\u4e86\u4e00\u70b9\uff0c\u4ee5\u81ea\u5df1\u773c\u4e0b\u4e4b\u529b\uff0c\u4e00\u5bf9\u4e00\u81ea\u53ef\u4e0e\u4e94\u5927\u9b54\u4e3b\u4e00\u8f83\u957f\u77ed\uff0c\u4f46\u5982\u4ed6\u4eec\u5408\u529b\u5bf9\u4ed8\u81ea\u5df1\uff0c\u5219\u80dc\u8d1f\u4e4b\u673a\uff0c\u5c31\u5341\u5206\u96be\u6d4b\u4e86\u3002\u5bfb\u601d\u4e4b\u95f4\uff0c\u7acb\u5373\u51b3\u5b9a\u8fd8\u662f\u88c5\u4f6f\u7684\u597d\u3002 \u3000\u3000\u5f53\u4e0b\u6447\u5934\u4e00\u7b11\u9053\uff1a\u201c\u5bb6\u5e08\u81ea\u79f0\u4e1c\u8033\u8001\u4eba\uff0c\u662f\u5426\u5c31\u662f\u5251\u795e\uff0c\u5728\u4e0b\u4ece\u672a\u56de\u8fc7\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4e1c\u8033\u8001\u4eba\uff0c\u672a\u66fe\u542c\u8bf4\u8fc7\u554a\uff01\u201d \u3000\u3000\u8bed\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u521a\u624d\u90a3\u4e00\u62db\u5251\u6cd5\uff0c\u53ef\u662f\u9f99\u884c\u516b\u5251\u4e2d\u7684\u7b2c\u4e00\u62db\u201c\u6392\u4e91\u5410\u96fe\u201d\uff1f \u3000\u3000\u65b9\u96ea\u5b9c\u77e5\u9053\u4e0d\u627f\u8ba4\u4e5f\u4e0d\u884c\uff0c\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u53ea\u662f\u8fd9\u5957\u5251\u6cd5\u5e76\u975e\u53eb\u505a\u9f99\u884c\u516b\u5251\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u9053\uff1a\u201c\u53eb\u4ec0\u4e48\uff0c\u5929\u4e0b\u54ea\u6709\u90a3\u7b49\u76f8\u4f3c\u7684\u62db\u5f0f\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u9f99\u884c\u4e5d\u5251\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u4e00\u6014\uff0c\u9053\uff1a\u201c\u516b\u5251\u3001\u4e5d\u5251\uff0c\u90a3\u662f\u540c\u6837\u7684\u5251\u6cd5\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u5728\u4e0b\u4e0d\u77e5\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u5927\u4e3a\u4e0d\u89e3\u5730\u4e00\u6123\uff0c\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u8fd9\u5957\u5251\u6cd5\u5168\u662f\u4e1c\u8033\u8001\u4eba\u6240\u6388\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u4e0d\u5168\u662f\u3002\u201d \u3000\u3000\u4ed6\u8d8a\u662f\u8fd9\u7b49\u56de\u7b54\uff0c\u8d8a\u662f\u5f15\u8d77\u4e86\u67f3\u5a9a\u5a18\u7684\u7591\u8651\uff0c\u4e5f\u540c\u65f6\u8d8a\u4f7f\u5979\u5982\u5760\u4e94\u91cc\u96fe\u4e2d\uff0c\u5fc5\u987b\u67e5\u95ee\u4e00\u4e2a\u660e\u767d\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u8bdd\u97f3\u58f0\u843d\uff0c\u5979\u7acb\u5373\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u62f3\u8bb2\u8d8a\u79bb\u4e86\u8c31\u4e86\u3002\u8fd9\u5251\u62db\u660e\u660e\u662f\u5251\u795e\u9648\u5927\u4fa0\u7684\u9f99\u884c\u516b\u5251\u4e2d\u8d77\u624b\u7684\u7b2c\u4e00\u5f0f\uff0c\u4f60\u504f\u504f\u8bf4\u662f\u9f99\u884c\u4e5d\u5251\uff0c\u800c\u4e14\u2026\u2026\u800c\u4e14\u2026\u2026\u8fd8\u8bf4\u4e0d\u662f\u4e00\u4eba\u6240\u6388\uff0c\u8fd9\u8bdd\u53eb\u4eba\u600e\u751f\u76f8\u4fe1\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4f60\u5982\u4e0d\u4fe1\uff0c\u90a3\u4e5f\u65e0\u6cd5\u4e86\uff0c\u5728\u4e0b\u786e\u662f\u53e5\u53e5\u5b9e\u8a00\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u6014\u4e86\u6014\uff0c\u5ffd\u7136\u683c\u683c\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u9664\u4e86\u4e1c\u8033\u8001\u4eba\uff0c\u8fd8\u6709\u8c01\u6559\u4e86\u4f60\u8fd9\u4e00\u5957\u5251\u6cd5\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5bfb\u601d\u9053\uff1a\u201c\u6211\u5982\u4e0d\u8bf4\u51fa\u5e08\u53d4\u4e4b\u540d\uff0c\u53ea\u6015\u8fde\u81ea\u5df1\u4e5f\u626f\u4e0d\u5706\u8fd9\u756a\u8c0e\u8a00\u4e86\u3002\u201d\u5f53\u4e0b\u63a5\u9053\uff1a\u201c\u91d1\u9876\u795e\u5c3c\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u6c89\u541f\u9053\uff1a\u201c\u91d1\u9876\u795e\u5c3c\uff1f\u8fd9\u53c8\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u539f\u6765\u8fd9\u9b54\u5973\u672a\u66fe\u542c\u8bf4\u8fc7\u795e\u5c3c\u4e4b\u540d\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u91d1\u9876\u795e\u5c3c\u4e43\u662f\u4e16\u5916\u9ad8\u4eba\uff0c\u5411\u4e0d\u4e0e\u95fb\u95ee\u6b66\u6797\u4e2d\u7684\u4e8b\uff0c\u9b54\u4e3b\u6ca1\u6709\u542c\u8bf4\u8fc7\uff0c\u90a3\u4e5f\u4e0d\u8db3\u4e3a\u602a\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u5fae\u5fae\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u6211\u6709\u4e00\u53e5\u8bdd\uff0c\u4e0d\u77e5\u9053\u4f60\u613f\u4e0d\u613f\u542c\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u60f3\uff0c\u8fd9\u4e0d\u662f\u5947\u4e86\uff1f\u4f60\u8981\u8bf4\u7684\u8bdd\uff0c\u7eb5\u7136\u6211\u4e0d\u60f3\u542c\uff0c\u53ea\u6015\u4f60\u4e5f\u4e0d\u4f1a\u4e0d\u8bf4\u51fa\u6765\u7684\u554a\uff01\u4f46\u4ed6\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u4ec0\u4e48\u8bdd\uff1f\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u9053\uff1a\u201c\u5251\u795e\u9648\u5927\u4fa0\uff0c\u4e0e\u6211\u6709\u7740\u5927\u6069\uff0c\u5982\u82e5\u5c0f\u5144\u5f1f\u4e43\u662f\u4ed6\u7684\u5f1f\u5b50\uff0c\u773c\u4e0b\u7684\u60c5\u52bf\uff0c\u5c31\u5927\u5927\u5730\u4e0d\u540c\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u5201\u6076\u7684\u9b54\u5987\uff0c\u4f60\u5c45\u7136\u60f3\u9a97\u6211\u5417\uff0c\u660e\u660e\u4f60\u4eec\u5bf9\u5e08\u7236\u65e2\u754f\u53c8\u6068\uff0c\u53cd\u5012\u8bf4\u4ed6\u8001\u4eba\u5bb6\u4e0e\u4f60\u6709\u6069\uff0c\u60f3\u4ece\u6211\u53e3\u4e2d\u5957\u8bdd\uff0c\u90a3\u53ef\u529e\u4e0d\u5230\u3002\u201d\u52a8\u5ff5\u4e4b\u95f4\uff0c\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u4e1c\u8033\u8001\u4eba\u662f\u4e0d\u662f\u5251\u795e\uff0c\u5728\u4e0b\u4e0d\u77e5\uff0c\u9b54\u4e3b\u5927\u53ef\u4e0d\u5fc5\u5fc3\u4e2d\u4e0d\u5b89\u3002\u201d \u3000\u3000\u663e\u7136\uff0c\u65b9\u96ea\u5b9c\u8fd8\u6709\u7740\u8ddf\u5979\u52a8\u624b\u4e4b\u610f\u3002 \u3000\u3000\u67f3\u5a9a\u5a18\u95fb\u8a00\uff0c\u6c89\u541f\u4e86\u826f\u4e45\uff0c\u9053\uff1a\u201c\u9664\u4e86\u5251\u795e\u9648\u5927\u4fa0\uff0c\u6211\u5b9e\u5728\u60f3\u4e0d\u51fa\u6b66\u6797\u4e2d\uff0c\u8fd8\u6709\u4ec0\u4e48\u4eba\u80fd\u4f1a\u9f99\u884c\u516b\u5251\u2026\u2026\u201d\u5ffd\u7136\u95f4\u67f3\u7709\u4e00\u626c\uff0c\u5a07\u7b11\u9053\uff1a\u201c\u662f\u4e86\uff0c\u6211\u51e0\u4e4e\u88ab\u4ed6\u7792\u4e86\u8fc7\u53bb\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u6697\u5403\u4e86\u4e00\u60ca\uff0c\u4ed8\u9053\uff1a\u5979\u83ab\u975e\u5df2\u7136\u77a7\u51fa\u4e86\u5176\u4e2d\u539f\u6545\u5417\uff1f \u3000\u3000\u53e3\u4e2d\u5374\u662f\u63a5\u9053\uff1a\u201c\u9648\u5927\u4fa0\u9a97\u4e86\u9b54\u4e3b\uff1f\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u51b7\u51b7\u5730\u5e94\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4e0d\u4f46\u6211\u53d7\u4e86\u9a97\uff0c\u8fde\u4f60\u4e5f\u4e0a\u4e86\u4ed6\u7684\u5f53\u4e86\u5566\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u5931\u7b11\uff0c\u6697\u9053\uff1a\u201c\u6211\u4f1a\u4e0a\u5f53\u5417\uff1f\u201d \u3000\u3000\u8868\u9762\u4e4b\u4e0a\uff0c\u4ed6\u5374\u505a\u4f5c\u7684\u5341\u5206\u6728\u8bb7\uff0c\u5446\u5446\u5730\u95ee\u9053\uff1a\u201c\u6211\u4e0a\u4e86\u5f53\uff1f\u5728\u4e0b\u4ece\u672a\u89c1\u8fc7\u9648\u5927\u4fa0\u4e4b\u9762\u554a\uff01\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u5ffd\u7136\u4f3c\u662f\u5341\u5206\u5f97\u610f\u5730\u683c\u683c\u7b11\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u5c0f\u5144\u5f1f\u65e9\u5c31\u89c1\u8fc7\u5251\u795e\u5566\uff01\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5c0f\u5144\uff0c\u4f60\u4e0d\u59a8\u518d\u7ec6\u5fc3\u5730\u60f3\u4e0a\u4e00\u60f3\uff0c\u90a3\u4e1c\u8033\u4e24\u5b57\uff0c\u51d1\u5728\u4e00\u8d77\uff0c\u662f\u4e0d\u662f\u4e00\u4e2a\u9648\u5b57\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fae\u5fae\u4e00\u7b11\uff0c\u4ed8\u9053\uff1a\u201c\u8fd9\u9b54\u5934\u679c\u7136\u731c\u60f3\u51fa\u6765\u4e86\u3002\u201d\u5f53\u4e0b\u63a5\u9053\uff1a\u201c\u5728\u4e0b\u51e0\u65f6\u89c1\u8fc7\u5251\u795e\uff0c\u600e\u7684\u81ea\u5df1\u7adf\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u90a3\u4e1c\u8033\u8001\u4eba\uff0c\u5c31\u662f\u5251\u795e\uff0c\u4ed6\u59d3\u9648\uff0c\u540d\u53eb\u9648\u5e0c\u6b63\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u771f\u7684\uff01\u201d \u3000\u3000\u4ed6\u88c5\u7684\u5341\u5206\u50cf\uff0c\u662f\u4ee5\u67f3\u5a9a\u5a18\u5012\u4e5f\u672a\u77a7\u51fa\u7834\u7efd\uff0c\u63a5\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u6b64\u523b\u5df2\u662f\u5251\u795e\u7684\u4f20\u4eba\u4e86\uff0c\u5728\u6b66\u6797\u4e4b\u4e2d\uff0c\u4f60\u7684\u8eab\u4efd\u53ef\u4e0d\u4f4e\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u5728\u4e0b\u89c9\u7740\u5e76\u65e0\u4e0d\u540c\u4e4b\u5904\uff01\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u674f\u76ee\u7cbe\u5149\u6d41\u8f6c\uff0c\u5728\u65b9\u96ea\u5b9c\u8eab\u4e0a\u98d8\u6765\u98d8\u53bb\uff0c\u9661\u7136\u5a07\u7b11\u4e86\u4e09\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u5e08\u7236\u73b0\u5728\u4f55\u5904\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u77e5\u5979\u8fdf\u65e9\u5fc5\u6709\u8fd9\u7b49\u95ee\u6cd5\uff0c\u65e9\u5728\u5fc3\u4e2d\u6253\u5b9a\u4e86\u4e3b\u610f\uff0c\u95fb\u8a00\u7b11\u9053\uff1a\u201c\u5bb6\u5e08\u6d6a\u8ff9\u5929\u6daf\uff0c\u5728\u4e0b\u65e0\u6cd5\u77e5\u6653\u4ed6\u8001\u4eba\u5bb6\u4f55\u5728\uff01\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u9887\u4e3a\u610f\u5916\u5730\u4e00\u6014\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5bb6\u5e08\u6388\u6211\u6b66\u529f\uff0c\u4e43\u662f\u5c48\u8eab\u5974\u4ec6\u884c\u4e2d\uff0c\u5929\u5e74\u4e4b\u524d\uff0c\u4fbf\u5df2\u79bb\u53bb\uff0c\u5728\u4e0b\u8fd9\u51e0\u5e74\u884c\u8d70\u6c5f\u6e56\uff0c\u4e5f\u6b63\u662f\u4e3a\u4e86\u5bfb\u627e\u5bb6\u5e08\u2026\u2026\u201d \u3000\u3000\u4ed6\u4e00\u65f6\u798f\u81f3\u5fc3\u7075\uff0c\u8bf4\u6765\u7adf\u662f\u5934\u5934\u662f\u9053\u3002 \u3000\u3000\u67f3\u5a9a\u5a18\u77a7\u4ed6\u4e0d\u4f3c\u8bf4\u8c0e\uff0c\u5f53\u4e0b\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u5728\u4f55\u5904\u4e0e\u5251\u795e\u5206\u624b\u7684\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5c31\u5728\u5609\u5b9a\u57ce\u4e2d\uff01\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u9661\u7136\u8138\u8272\u4e00\u53d8\uff0c\u663e\u7136\uff0c\u5979\u5927\u5927\u5403\u4e86\u4e00\u60ca\u3002 \u3000\u3000\u987b\u77e5\u4e94\u5927\u9b54\u4e3b\u80c6\u5b50\u518d\u5927\uff0c\u5bf9\u4e8e\u5251\u795e\u9648\u5927\u4fa0\uff0c\u8fd8\u662f\u754f\u4e4b\u5982\u540c\u5c0f\u9b3c\u89c1\u4e86\u5224\u5b98\u4e00\u822c\uff0c\u867d\u7136\uff0c\u8fd9\u51e0\u5e74\u4e4b\u4e2d\uff0c\u672a\u66fe\u89c1\u5230\u9648\u5e0c\u6b63\u9732\u9762\uff0c\u90fd\u77e5\u9053\u4ed6\u5df2\u6bd2\u4f24\u53d1\u4f5c\u6b7b\u53bb\uff0c\u4f46\u6b64\u523b\u5ffd\u7136\u542c\u5f97\u65b9\u96ea\u5b9c\u8bf4\u4ed6\u4e0e\u5251\u795e\u4e43\u662f\u5728\u5609\u5b9a\u5206\u624b\uff0c\u6b63\u662f\u5728\u81ea\u5df1\u7684\u5367\u69bb\u4e4b\u65c1\uff0c\u67f3\u5a9a\u5a18\u518d\u9547\u5b9a\uff0c\u4e5f\u4e0d\u514d\u5fc3\u4e2d\u5927\u4e3a\u9707\u9a87\u3002 \u3000\u3000\u5446\u5446\u5730\u77a7\u7740\u65b9\u96ea\u5b9c\u597d\u4e45\uff0c\u65b9\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u8fd9\u8bdd\u4e0d\u662f\u9a97\u4eba\u5427\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5728\u5fc3\u4e2d\u5fcd\u4e0d\u4f4f\u60f3\u7b11\uff0c\u4f46\u4ed6\u77e5\u9053\uff0c\u81ea\u5df1\u5982\u662f\u4e00\u65e6\u5931\u7b11\uff0c\u8fd9\u5148\u593a\u5bf9\u65b9\u4e4b\u6c14\u7684\u8ba1\u5212\u5c31\u96be\u4ee5\u5151\u73b0\u4e86\u3002 \u3000\u3000\u56e0\u6b64\uff0c\u4ed6\u6168\u7136\u5e94\u9053\uff1a\u201c\u5728\u4e0b\u6ca1\u6709\u8bf4\u8c0e\u7684\u5fc5\u8981\u554a\uff01\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u5012\u662f\u76f8\u4fe1\u4e86\u4ed6\uff0c\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u63a5\u9053\uff1a\u201c\u4ee4\u5e08\u53ef\u66fe\u544a\u8bc9\u8fc7\u4f60\uff0c\u4ed6\u4eca\u540e\u884c\u6b62\u4f55\u65b9\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u5934\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u5bb6\u5e08\u672a\u66fe\u63d0\u53ca\uff01\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u9053\uff1a\u201c\u4ee4\u5e08\u4e5f\u672a\u7ea6\u5b9a\u548c\u4f60\u89c1\u9762\u7684\u5730\u70b9\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4ed8\u9053\uff1a\u201c\u770b\u6765\u6211\u5f97\u7ed9\u5979\u5fc3\u4e2d\u518d\u627e\u4e00\u70b9\u82e6\u5934\u5403\u5403\u624d\u662f\u2026\u2026\u201d\u7565\u4e00\u6c89\u541f\uff0c\u9053\uff1a \u3000\u3000\u201c\u6709\u7684\u5730\u70b9\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u9053\uff1a\u201c\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5ce8\u5d4b\u91d1\u9876\uff01\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u8138\u8272\u500f\u7136\u518d\u53d8\uff0c\u6025\u6025\u559d\u9053\uff1a\u201c\u4f60\u53bb\u8fc7\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u53bb\u8fc7\u4e86\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u5927\u6709\u8feb\u4e0d\u53ca\u5f85\u4e4b\u52bf\u63a5\u9053\uff1a\u201c\u89c1\u5230\u4e86\u4ee4\u5e08\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u5934\u82e6\u7b11\u9053\uff1a\u201c\u6ca1\u6709\u2026\u2026\u201d \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u4ed6\u60f3\u8d77\u4e86\u6069\u5e08\u5df2\u7136\u9648\u5c38\u909b\u5cf0\uff0c\u5fc3\u4e2d\u4e00\u60e8\uff0c\u82e6\u7b11\u7684\u8138\u8272\uff0c\u5239\u90a3\u95f4\u53d8\u6210\u4e86\u4e00\u7247\u60b2\u5bc2\u4e4b\u5bb9\u3002 \u3000\u3000\u67f3\u5a9a\u5a18\u4e0d\u77e5\u4ed6\u5fc3\u4e2d\u60f3\u7684\u4e43\u662f\u5e08\u7236\u5df2\u6b7b\u4e4b\u4e8b\uff0c\u53ea\u9053\u4ed6\u672a\u66fe\u89c1\u522b\u9648\u5e0c\u6b63\u800c\u96be\u8fc7\uff0c\u5f53\u4e0b\u7565\u4e3a\u653e\u5fc3\uff0c\u559d\u9053\uff1a\u201c\u90a3\u662f\u8bf4\u5251\u795e\u4e0d\u5728\u5ce8\u5d4b\u5c71\u4e2d\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u2026\u2026\u5728\u4e0b\u4e0d\u77e5\u9053\u4e86\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u4e00\u6014\u9053\uff1a\u201c\u4e0d\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u8fc7\u5728\u4e0b\u5374\u89c1\u7740\u4e86\u91d1\u9876\u795e\u5c3c\uff01\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u7565\u4e3a\u677e\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u90a3\u4f4d\u91d1\u9876\u795e\u5c3c\uff0c\u53ef\u5c31\u662f\u4f20\u4f60\u9f99\u884c\u4e5d\u5251\u4e4b\u4eba\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u6c89\u541f\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u8fd9\u4e48\u770b\u6765\uff0c\u90a3\u91d1\u9876\u795e\u5c3c\u4e0e\u5251\u795e\u9648\u5927\u4fa0\u662f\u6709\u76f8\u5f53\u6e0a\u6e90\u7684\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5728\u4e0b\u6ca1\u6709\u95ee\u8fc7\uff01\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u9053\uff1a\u201c\u90a3\u662f\u4e0d\u4f1a\u9517\u7684\u5566\uff01\u8fd9\u9f99\u884c\u4e5d\u5251\uff0c\u4e0e\u5251\u795e\u9648\u5927\u4fa0\u7684\u5251\u6cd5\u5982\u51fa\u4e00\u8f99\uff0c\u82e5\u6ca1\u6709\u6e0a\u6e90\uff0c\u600e\u4f1a\u8fd9\u4e48\u76f8\u4f3c\u3002\u201d \u3000\u3000\u8bed\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u6211\u672c\u65e0\u4e0e\u4f60\u4e3a\u96be\u4e4b\u5fc3\uff0c\u4f46\u662f\uff0c\u773c\u4e0b\u56e0\u4e3a\u4f60\u662f\u5251\u795e\u7684\u4f20\u4eba\uff0c\u6211\u53ef\u4e0d\u5f97\u4e0d\u5c06\u4f60\u7559\u4e0b\u6765\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u9b54\u4e3b\u4e0e\u5251\u795e\u6709\u4ec7\uff1f\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u6709\u4ec7\uff1f\u5c0f\u5144\u5f1f\uff0c\u4f60\u5fd8\u4e86\u5417\uff1f\u6211\u66fe\u8bf4\u5230\u5251\u795e\u4e8e\u6211\u6709\u6069\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u63a5\u9053\uff1a\u201c\u65e2\u662f\u5251\u795e\u6709\u6069\u4e8e\u9b54\u4e3b\uff0c\u4e3a\u4f55\u9b54\u4e3b\u7adf\u8981\u5c06\u5728\u4e0b\u7559\u4e0b\uff1f\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u7709\u68a2\u5ffd\u7136\u4e00\u626c\uff0c\u9053\uff1a\u201c\u5176\u4e2d\u9053\u7406\uff0c\u5bb9\u5f97\u9648\u5e0c\u6b63\u6765\u5bfb\u4f60\u4e4b\u65f6\uff0c\u4f60\u81ea\u7136\u5c31\u4f1a\u660e\u767d\u3002\u201d\u8bed\u97f3\u5ffd\u7136\u4e00\u987f\uff0c\u6389\u5934\u5411\u9ad8\u65e0\u654c\u559d\u9053\uff1a\u201c\u9ad8\u65e0\u654c\uff0c\u4f60\u5e72\u7684\u597d\u4e8b\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u6ca1\u6709\u554a\uff01\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u9053\uff1a\u201c\u90a3\u4f60\u5c31\u7ed9\u6211\u542c\u7740\uff01\u201d\u5ffd\u7136\u683c\u683c\u4e00\u7b11\uff0c\u6307\u7740\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u4f4d\u5c0f\u5144\u5f1f\u4e43\u662f\u5251\u795e\u7684\u4f20\u4eba\uff0c\u4f60\u5148\u524d\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u4e0d\u77e5\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u7b11\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u4e0d\u77e5\u9053\uff0c\u9ad8\u65e0\u654c\uff0c\u7ae5\u5b50\u5947\u56de\u6765\u4e4b\u540e\uff0c\u53ea\u6015\u4f60\u8fd9\u56db\u7075\u6559\u7684\u9996\u5ea7\u4e4b\u5e2d\uff0c\u8981\u642c\u4e00\u6b21\u5bb6\u4e86\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u6014\u6014\u5730\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u9053\uff1a\u201c\u5f15\u72fc\u5165\u5ba4\uff0c\u8fd8\u4e0d\u591f\u4f60\u6740\u8eab\u800c\u6709\u4f59\u5417\uff1f\u4e0d\u8fc7\uff0c\u4f60\u5982\u542c\u6211\u4e4b\u547d\u884c\u4e8b\uff0c\u6211\u5374\u53ef\u4ee5\u4e3a\u4f60\u5f00\u8131\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u8fd9\u4f1a\u513f\u4e5f\u660e\u767d\u4e86\u67f3\u5a9a\u5a18\u6240\u6307\u4f55\u4e8b\u7684\u4e86\u3002 \u3000\u3000\u6562\u60c5\u9648\u5e0c\u6b63\u4e43\u662f\u4e94\u5927\u9b54\u4e3b\u4e0d\u4e16\u4e4b\u4ec7\u7684\u4e8b\uff0c\u4ed6\u4eec\u81ea\u662f\u77e5\u6653\uff0c\u9002\u624d\u81ea\u5df1\u8fd8\u4e0e\u827e\u4e1c\u6d77\u4e89\u6267\uff0c\u827e\u4e1c\u6d77\u8bf4\u8981\u4e0e\u90a3\u67f3\u5a9a\u5a18\u8054\u624b\uff0c\u62ff\u4e0b\u65b9\u96ea\u5b9c\uff0c\u4e00\u6765\u53ef\u4ee5\u4e0e\u67f3\u5a9a\u5a18\u91ca\u6028\uff0c\u4e8c\u6765\u4e5f\u597d\u5411\u6559\u4e3b\u9080\u529f\u3002 \u3000\u3000\u4f46\u9ad8\u65e0\u654c\u7684\u4e3a\u4eba\uff0c\u5374\u662f\u4e2a\u5730\u9053\u7684\u8840\u6027\u6c49\u5b50\uff0c\u4ed6\u5bf9\u65b9\u96ea\u5b9c\u53ef\u662f\u771f\u5fc3\u4ea4\u5f80\uff0c\u8ba4\u4f5c\u4e86\u670b\u53cb\u8981\u4ed6\u4f5c\u90a3\u5356\u53cb\u4e4b\u4e8b\uff0c\u4ed6\u53ef\u662f\u4e0d\u5e72\u3002 \u3000\u3000\u6b64\u523b\uff0c\u4ed6\u542c\u5f97\u67f3\u5a9a\u5a18\u4e4b\u8a00\uff0c\u5fc3\u4e2d\u4e0d\u7981\u5927\u5403\u4e00\u60ca\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u4e8b\u679c\u7136\u662f\u81ea\u5df1\u5931\u7b56\uff0c\u4e0d\u8be5\u628a\u4ec7\u4eba\u5f1f\u5b50\uff0c\u5f53\u4f5c\u4e86\u81f3\u53cb\uff0c\u82e5\u662f\u6559\u4e3b\u77e5\u6653\uff0c\u81ea\u5df1\u5f53\u771f\u662f\u96be\u9003\u6bd2\u5211\u52a0\u8eab\u4e4b\u60e8\u3002\u201d\u4e00\u65f6\u4e4b\u95f4\uff0c\u9ad8\u65e0\u6545\u5374\u6ca1\u4e86\u4e3b\u610f\uff0c\u5446\u5446\u5730\u671b\u7740\u67f3\u5a9a\u5a18\u9053\uff1a\u201c\u4f9d\u9b54\u4e3b\u4e4b\u89c1\uff0c\u9ad8\u67d0\u8be5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u9053\uff1a\u201c\u5f88\u5bb9\u6613\uff0c\u5c06\u529f\u6298\u7f6a\u800c\u5df2\uff01\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u600e\u751f\u5c06\u529f\u6298\u7f6a\uff1f\u201d \u3000\u3000\u67f3\u5a9a\u5a07\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u5316\u654c\u4e3a\u53cb\uff0c\u540c\u4ec7\u654c\u6c7d\u4fbf\u662f\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u76ee\u5149\u5728\u65b9\u96ea\u5b9c\u8eab\u4e0a\u4e00\u8f6c\uff0c\u9053\uff1a\u201c\u9b54\u4e3b\uff0c\u4f60\u8001\u662f\u8981\u9ad8\u67d0\u4e0e\u65b9\u8001\u5f1f\u4e3a\u654c\u5417\uff1f\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u53ea\u602a\u4ed6\u4e0d\u8be5\u662f\u90a3\u5251\u795e\u7684\u5f1f\u5b50\uff01\u201d \u3000\u3000\u9ad8\u65e0\u654c\u6014\u6014\u5730\u9053\uff1a\u201c\u8fd9\u2026\u2026\u9ad8\u65e0\u654c\u4e0d\u662f\u5356\u53cb\u4e4b\u4eba\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u9053\uff1a\u201c\u597d\u554a\uff01\u4f60\u5f88\u8bb2\u4e49\u6c14\u554a\uff01\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u65b9\u5144\u5f1f\u89c1\u5371\u76f8\u52a9\uff0c\u7b54\u5e94\u7559\u6b64\u4e09\u65e5\u4e4b\u60c5\uff0c\u5728\u9ad8\u67d0\u5fc3\u4e2d\uff0c\u4e91\u5929\u9ad8\u8c0a\uff0c\u867d\u6740\u6211\u8eab\uff0c\u4e5f\u662f\u6beb\u65e0\u6028\u5c24\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5728\u65c1\u542c\u5f97\u5fc3\u4e2d\u5927\u4e3a\u611f\u6fc0\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u4f4d\u9ad8\u5144\u5012\u662f\u4e00\u4f4d\u6c49\u5b50\uff0c\u597d\u6b79\u6211\u5f97\u6551\u4ed6\u4e00\u6551\u2026\u2026\u201d \u3000\u3000\u4ed6\u660e\u77e5\u6b64\u523b\u81ea\u5df1\u5df2\u7136\u9677\u5165\u6781\u7aef\u5371\u673a\u4e4b\u4e2d\uff0c\u4f46\u5374\u4ecd\u5728\u60f3\u7740\u5982\u4f55\u633d\u90a3\u9ad8\u65e0\u654c\u4e8e\u9b54\u9053\u4e4b\u4e2d\uff0c\u8fd9\u7b49\u820d\u5df1\u5fd8\u79c1\u4e4b\u5fc3\uff0c\u6b63\u662f\u5927\u4ec1\u4fa0\uff0c\u5927\u8c6a\u6770\u7684\u6210\u529f\u4e4b\u57fa\u3002 \u3000\u3000\u8fd9\u65f6\u90a3\u5b8b\u5b5a\u5fc3\u4e2d\uff0c\u53ef\u662f\u60ca\u51db\u4e0d\u5df2\uff0c\u4e00\u65e6\u827e\u4e1c\u6d77\u3001\u9ad8\u65e0\u654c\u771f\u4e2a\u4e0e\u67f3\u5a9a\u5a18\u5316\u654c\u4e3a\u53cb\uff0c\u5408\u529b\u5bf9\u4ed8\u65b9\u96ea\u5b9c\uff0c\u7eb5\u7136\u65b9\u96ea\u5b9c\u662f\u90a3\u5251\u795e\u4f20\u4eba\uff0c\u53ea\u6050\u6015\u4e5f\u96be\u4ee5\u5e94\u4ed8\u3002\u5f53\u4e0b\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u4e2d\uff0c\u5411\u524d\u79fb\u8fdb\u4e86\u4e09\u6b65\u3002 \u3000\u3000\u67f3\u5a9a\u5a18\u76ee\u5149\u95ea\u70c1\u4e0d\u5b9a\u5730\u5728\u9ad8\u65e0\u654c\u8eab\u4e0b\u8f6c\u52a8\uff0c\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u9ad8\u65e0\u654c\uff0c\u4f60\u8be5\u77e5\u9053\u81ea\u5df1\u7684\u8eab\u4efd\u554a\uff01\u201d \u3000\u3000\u9ad8\u65e0\u654c\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u9ad8\u67d0\u81ea\u7136\u662f\u77e5\u9053\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u51b7\u54fc\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u56db\u7075\u6559\u9996\u5ea7\uff0c\u5982\u662f\u72af\u4e86\u90a3\u53db\u6559\u4e4b\u884c\uff0c\u7ae5\u5b50\u5947\u4f1a\u6709\u4f55\u7b49\u6076\u6bd2\u624b\u6bb5\u76f8\u5f85\uff0c\u4f60\u603b\u8be5\u5fc3\u5934\u660e\u767d\u5427\uff01\u201d \u3000\u3000\u9ad8\u65e0\u654c\u4f3c\u662f\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u9ad8\u67d0\u5e76\u65e0\u53db\u6559\u4e4b\u5fc3\uff0c\u9b54\u4e3b\u4e0d\u5fc5\u8fd9\u7b49\u5371\u8a00\u8038\u542c\u554a\uff01\u201d \u3000\u3000\u827e\u4e1c\u6d77\u5ffd\u7136\u4e00\u7b11\u9053\uff1a\u201c\u9ad8\u5144\uff0c\u9b54\u4e3b\u771f\u6b63\u7684\u7528\u5fc3\uff0c\u4f60\u96be\u9053\u8fd8\u4e0d\u66fe\u660e\u767d\u5417\uff1f\u201d \u3000\u3000\u9ad8\u65e0\u654c\u9053\uff1a\u201c\u827e\u5144\u4e4b\u610f\uff0c\u53ef\u662f\u8981\u5144\u5f1f\u4e0e\u9b54\u4e3b\u8054\u624b\u5bf9\u4ed8\u8fd9\u65b9\u8001\u5f1f\u5417\uff1f\u201d \u3000\u3000\u827e\u4e1c\u6d77\u9053\uff1a\u201c\u9ad8\u5144\u660e\u767d\u5c31\u597d\uff01\u201d\u8bed\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u957f\u957f\u5730\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u63a5\u9053\uff1a\u201c\u773c\u4e0b\u7684\u5c40\u52bf\uff0c\u4e0d\u5fc5\u5144\u5f1f\u8be6\u8bf4\uff0c\u5251\u795e\u65e2\u6709\u4f20\u4eba\uff0c\u54b1\u4eec\u4e94\u5927\u9b54\u4e3b\u7684\u5c5e\u4e0b\uff0c\u81ea\u5e94\u540c\u4ec7\u654c\u6c7d\u624d\u662f\u554a\uff01\u201d \u3000\u3000\u9ad8\u65e0\u654c\u5e76\u975e\u4e0d\u77e5\u827e\u4e1c\u6d77\u4e4b\u8a00\u6709\u7406\uff0c\u4f46\u4ed6\u5fc3\u4e2d\u5374\u662f\u611f\u5ff5\u65b9\u96ea\u5b9c\uff0c\u4e0d\u613f\u4e0e\u4e4b\u4e3a\u654c\u3002\u56e0\u6b64\uff0c\u4ed6\u6014\u4e86\u4e00\u6014\u9053\uff1a\u201c\u827e\u5144\uff0c\u5144\u5f1f\u2026\u2026\u201d \u3000\u3000\u827e\u4e1c\u6d77\u4e0d\u5bb9\u4ed6\u518d\u8bf4\uff0c\u6b63\u8272\u9053\uff1a\u201c\u9ad8\u5144\uff0c\u9648\u5e0c\u6b63\u4e43\u662f\u6211\u7b49\u5927\u654c\uff0c\u9ad8\u5144\u600e\u53ef\u8fd9\u7b49\u654c\u53cb\u4e0d\u5206\u5462\uff1f\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u662f\u8bf4\u9ad8\u65e0\u654c\u4e0d\u660e\u662f\u975e\u654c\u6211\u7684\u4e0b\u67f3\u5a9a\u5a18\u4e5f\u683c\u683c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u9519\u554a\uff01\u4f60\u9ad8\u65e0\u654c\u65e2\u65e0\u53db\u6559\u4e4b\u5fc3\uff0c\u5c31\u8be5\u4e25\u8fa8\u654c\u6211\u624d\u662f\u3002\u201d \u3000\u3000\u9ad8\u65e0\u654c\u76ee\u5149\u5728\u65b9\u96ea\u5b9c\u8eab\u4e0a\u4e00\u8f6c\uff0c\u4f3c\u662f\u4e0b\u4e86\u6781\u5927\u7684\u51b3\u5fc3\uff0c\u5927\u58f0\u9053\uff1a\u201c\u827e\u5144\u4e0d\u5fc5\u591a\u8bf4\u4e86\uff0c\u5144\u5f1f\u5df2\u7ecf\u4f5c\u4e86\u51b3\u5b9a\uff0c\u7eb5\u7136\u9ad8\u67d0\u80cc\u4e0b\u90a3\u53db\u6559\u4e4b\u540d\uff0c\u4e5f\u4e0d\u80fd\u5bf9\u65b9\u5144\u5f1f\u51fa\u624b\u3002\u201d \u3000\u3000\u4ed6\u77a7\u7740\u827e\u4e1c\u6d77\u8be7\u7136\u7684\u795e\u60c5\uff0c\u7b11\u4e86\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u827e\u5144\u83ab\u8981\u5947\u602a\uff0c\u5144\u5f1f\u611f\u5ff5\u65b9\u5144\u7684\u4e34\u5371\u4ed7\u4e49\u4e4b\u5fb7\uff0c\u81ea\u662f\u4e0d\u4fbf\u5bf9\u4ed6\u51fa\u624b\uff0c\u4f46\u5144\u5f1f\u4e5f\u4e0d\u52a9\u65b9\u5144\u5f1f\u4fbf\u662f\u4e86\u3002\u201d\u6562\u60c5\u4ed6\u5728\u65e0\u53ef\u5948\u4f55\u4e4b\u4e0b\uff0c\u53ea\u597d\u7f6e\u8eab\u4e8b\u5916\u4e86\u3002 \u3000\u3000\u827e\u4e1c\u6d77\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u9ad8\u5144\u8fd9\u7b49\u505a\u6cd5\uff0c\u53ea\u6015\u751a\u662f\u4e0d\u667a\u5427\uff01\u6559\u4e3b\u4ed6\u2026\u2026\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u5ffd\u7136\u5a07\u7b11\u4e86\u4e09\u58f0\uff0c\u9053\uff1a\u201c\u7b97\u4e86\uff0c\u4f60\u59d3\u9ad8\u7684\u91cd\u4e49\u6c14\uff0c\u672c\u5ea7\u662f\u52c9\u5f3a\u4e0d\u5f97\u4f60\u7684\u4e86\u3002\u201d \u3000\u3000\u56de\u987e\u4e86\u827e\u4e1c\u6d77\u4e00\u773c\uff0c\u9053\uff1a\u201c\u827e\u4e1c\u6d77\uff0c\u4f60\u5462\uff1f\u60f3\u5fc5\u4e0d\u4f1a\u4e5f\u8896\u624b\u65c1\u89c2\u5427\uff01\u201d \u3000\u3000\u827e\u4e1c\u6d77\u5fd9\u9053\uff1a\u201c\u90a3\u662f\u81ea\u7136\uff0c\u4f46\u51ed\u9b54\u4e3b\u5429\u5490\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u70b9\u5934\u9053\uff1a\u201c\u5f88\u597d\uff0c\u54b1\u4eec\u5c31\u5148\u5c06\u8fd9\u59d3\u65b9\u7684\u62ff\u4e0b\uff0c\u7136\u540e\u518d\u53bb\u627e\u90a3\u9648\u5e0c\u6b63\u3002\u201d \u3000\u3000\u7a81\u7136\uff0c\u5b8b\u5b5a\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u67f3\u5a9a\u5a18\u4f60\u8981\u62ff\u4e0b\u65b9\u8001\u5f1f\uff0c\u53ea\u6015\u8fd8\u5f97\u5148\u8981\u8d39\u4e0a\u4e00\u756a\u624b\u811a\uff01\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u674f\u773c\u4e00\u95ea\uff0c\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f\u80c6\u6562\u5bf9\u672c\u5ea7\u5982\u6b64\u8bf4\u8bdd\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8001\u592b\u5b8b\u5b5a\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u5fae\u5fae\u4e00\u6014\u9053\uff1a\u201c\u5927\u6f20\u9b3c\u624b\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u90a3\u6b63\u662f\u8001\u592b\u540d\u53f7\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u5ffd\u7136\u683c\u683c\u5a07\u7b11\u9053\uff1a\u201c\u5927\u6f20\u9b3c\u624b\u56db\u5b57\uff0c\u5413\u4e0d\u5012\u672c\u5ea7\u554a\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u5f53\u7136\uff0c\u8001\u592b\u4e5f\u6709\u81ea\u77e5\u4e4b\u660e\u201d\u8bdd\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4f46\u8001\u592b\u76f8\u4fe1\uff0c\u773c\u4e0b\u5c40\u52bf\uff0c\u6709\u4e86\u8001\u592b\u4e00\u4eba\uff0c\u5b9a\u53ef\u4f7f\u4f60\u96be\u5f97\u5982\u613f\u4ee5\u507f\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u7a81\u7136\u53d8\u8272\u9053\uff1a\u201c\u4f60\u60f3\u600e\u4e48\u6837\uff1f\u51ed\u4f60\u5927\u6f20\u9b3c\u624b\uff0c\u4e5f\u60f3\u8ddf\u672c\u5ea7\u4f5c\u5bf9\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u8001\u592b\u867d\u975e\u67f3\u59d1\u5a18\u4e4b\u654c\uff0c\u4f55\u5982\u7275\u5236\u827e\u4e1c\u6d77\u5219\u81ea\u4fe1\u7ef0\u7ef0\u6709\u4f59\u3002\u201d \u3000\u3000\u90a3\u67f3\u5a9a\u5a18\u867d\u5df2\u5e74\u8fc7\u4e09\u65ec\uff0c\u4f46\u56e0\u5979\u4e00\u76f4\u6b63\u5f0f\u672a\u66fe\u5ac1\u4eba\uff0c\u56e0\u6b64\u5b8b\u5b5a\u79f0\u5979\u4e00\u58f0\u59d1\u5a18\u3002 \u3000\u3000\u67f3\u5a9a\u5a18\u95fb\u8a00\uff0c\u5fc3\u4e2d\u7535\u8f6c\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u8001\u9b3c\u679c\u7136\u53ef\u6076\u5f97\u5f88\uff0c\u81ea\u5df1\u4e00\u4eba\u5bf9\u4ed8\u65b9\u96ea\u5b9c\u8fd8\u771f\u62ff\u4e0d\u7a33\u8f93\u8d62\u5462\u3002\u201d \u3000\u3000\u539f\u6765\u5979\u4eec\u4e94\u5927\u9b54\u4e3b\u65e9\u88ab\u5251\u795e\u5413\u7834\u4e86\u80c6\uff0c\u5bf9\u4e8e\u9648\u5e0c\u6b63\u90a3\u4e00\u5957\u9f99\u884c\u516b\u5251\uff0c\u82b1\u4e86\u591a\u5c11\u5e74\u5c81\u6708\uff0c\u4ecd\u7136\u60f3\u4e0d\u51fa\u514b\u5236\u4e4b\u6cd5\uff0c\u56e0\u6b64\uff0c\u8981\u5979\u4e00\u4eba\u4e0e\u65b9\u96ea\u5b9c\u535a\u6740\uff0c\u5979\u8fd8\u771f\u6ca1\u6709\u4fe1\u5fc3\u3002\u662f\u4ee5\uff0c\u5b8b\u5b5a\u8bdd\u97f3\u4e00\u843d\uff0c\u5979\u4e0d\u7981\u5927\u4e3a\u72b9\u8c6b\u3002 \u3000\u3000\u5b8b\u5b5a\u54c8\u54c8\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u67f3\u59d1\u5a18\uff0c\u8001\u592b\u53ef\u662f\u6ca1\u6709\u8bf4\u9519\u5427\uff01\u76ee\u4e0b\u7684\u5c40\u9762\uff0c\u59d1\u5a18\u5e94\u662f\u660e\u767d\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u5f53\u7136\u660e\u767d\uff0c\u4f46\u6b64\u523b\u5979\u4e5f\u611f\u89c9\u5230\uff0c\u5982\u662f\u81ea\u5df1\u4e0d\u8d81\u6b64\u673a\u4f1a\u5c06\u8fd9\u5251\u795e\u7684\u4f20\u4eba\u62ff\u4e0b\uff0c\u6b64\u540e\u5c31\u6015\u8d8a\u6765\u8d8a\u4e0d\u5bb9\u6613\uff0c\u5f53\u4e0b\u5fc3\u5ff5\u4e00\u6a2a\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u5b8b\u8001\u54e5\uff0c\u4f60\u81ea\u89c6\u5f88\u9ad8\u554a\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u8001\u592b\u82e5\u4e0e\u59d1\u5a18\u76f8\u8f83\uff0c\u4e5f\u8bb8\u6709\u6240\u4e0d\u53ca\uff0c\u4f55\u5982\u4e0e\u827e\u8001\u5f1f\u76f8\u6bd4\uff0c\u5012\u53ef\u4e89\u90a3\u4e00\u65e5\u4e4b\u957f\uff01\u201d \u3000\u3000\u827e\u4e1c\u6d77\u4e24\u773c\u4e00\u7ffb\uff0c\u9053\uff1a\u201c\u5b8b\u5144\u5982\u6b64\u5c0f\u89c6\u827e\u67d0\uff0c\u5b9e\u662f\u53eb\u4eba\u4e0d\u670d\u5f97\u5f88\u3002\u201d\u6562\u60c5\u8fd9\u827e\u4e1c\u6d77\u5fc3\u4e2d\u4e5f\u6709\u9b3c\uff0c\u4ed6\u77e5\u9053\u81ea\u5df1\u5018\u4e0d\u662f\u4e0e\u90a3\u65b9\u96ea\u5b9c\u52a8\u624b\uff0c\u51c6\u5b9a\u96be\u4ee5\u9003\u8fc7\u4e00\u5251\u4e4b\u5384\uff0c\u6545\u800c\uff0c\u4ed6\u5b81\u53ef\u4e0e\u5927\u6f20\u9b3c\u624b\u4e00\u62fc\u3002 \u3000\u3000\u5b8b\u5b5a\u95fb\u8a00\uff0c\u5927\u7b11\u9053\uff1a\u201c\u827e\u8001\u5f1f\uff0c\u4f60\u65e2\u7136\u4e0d\u670d\uff0c\u54b1\u4eec\u4f55\u4e0d\u5206\u4e00\u4e2a\u9ad8\u4f4e\u5462\uff1f\u201d\u8bdd\u97f3\u752b\u6b47\uff0c\u6325\u624b\u4e00\u638c\uff0c\u62cd\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u827e\u4e1c\u6d77\u53cc\u76ee\u4e00\u77aa\uff0c\u56de\u624b\u5c31\u662f\u4e00\u62f3\u6363\u51fa\u3002 \u3000\u3000\u4f46\u95fb\u84ec\u84ec\u4e4b\u58f0\u4f20\u6765\uff0c\u663e\u7136\u4e24\u4eba\u5df2\u786c\u63a5\u4e86\u51e0\u638c\uff0c\u4e00\u65f6\u95f4\u56db\u5468\u52b2\u6c14\u904d\u5e03\uff0c\u5012\u4e5f\u662f\u4e2a\u52bf\u5747\u529b\u654c\u4e4b\u6218\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u89c1\u90a3\u5b8b\u5b5a\u7684\u529f\u529b\uff0c\u4e0d\u5728\u827e\u4e1c\u6d77\u4e4b\u4e0b\uff0c\u5f53\u4e0b\u5927\u4e3a\u653e\u5fc3\uff0c\u8f6c\u8eab\u5411\u67f3\u5a9a\u5a18\u9053\uff1a\u201c\u9b54\u4e3b\uff0c\u54b1\u4eec\u662f\u5426\u4e5f\u8981\u5206\u51fa\u4e00\u4e2a\u9ad8\u4e0b\u5462\uff1f\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u5a07\u7b11\u9053\uff1a\u201c\u672c\u5ea7\u6b63\u8981\u5c06\u4f60\u64d2\u4e0b\uff0c\u7136\u540e\u518d\u627e\u4f60\u90a3\u8001\u9b3c\u5e08\u7236\u7b97\u5e10\u5462\uff01\u201d\u7389\u624b\u4e00\u626c\uff0c\u8896\u4e2d\u7684\u7fe0\u7389\u6d1e\u7bab\uff0c\u4e00\u95ea\u800c\u51fa\uff0c\u4f46\u95fb\u5f97\u51e4\u9e23\u4e4b\u58f0\u4e0d\u7edd\uff0c\u65b9\u96ea\u5b9c\u524d\u80f8\u4e94\u5904\u5927\u7a74\uff0c\u5747\u7f69\u5728\u7bab\u62db\u4e4b\u5185\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e5f\u66fe\u542c\u5f97\u5e08\u7236\u8bf4\u8fc7\u4e94\u5927\u9b54\u4e3b\u7684\u6b66\u529f\uff0c\u8fd9\u67f3\u5a9a\u5979\u7684\u4e09\u5341\u516d\u8def\u5f15\u98ce\u7bab\u6cd5\uff0c\u9664\u4e86\u53ef\u70b9\u7a74\u9053\u4e4b\u5916\uff0c\u90a3\u7bab\u5b54\u5212\u7a7a\u6240\u9707\u52a8\u7684\u5f02\u58f0\uff0c\u66f4\u53ef\u4e71\u4eba\u5fc3\u795e\u3002\u6b64\u523b\u4e00\u89c1\uff0c\u679c\u7136\u4e0d\u5047\uff0c\u82e5\u975e\u4ed6\u65e9\u5df2\u8fd0\u8d77\u4e00\u53e3\u771f\u6c14\uff0c\u62a4\u4f4f\u4e86\u5fc3\u7075\uff0c\u5c31\u662f\u90a3\u9635\u523a\u8033\u7684\u602a\u58f0\uff0c\u5c31\u4f1a\u4ee4\u4eba\u627f\u53d7\u4e0d\u4e86\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u51dd\u795e\u62b1\u5251\uff0c\u5bb9\u5f97\u67f3\u5a9a\u5a18\u7684\u7389\u7bab\u70b9\u5411\u80f8\u524d\u201c\u534e\u76d6\u7a74\u201d\u4e0d\u53ca\u4e09\u5bf8\u4e4b\u5730\uff0c\u8fd9\u624d\u6325\u5251\u51fb\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u4ed6\u7528\u7684\u662f\u9f99\u884c\u4e5d\u5251\u4e2d\u7684\u7b2c\u56db\u5f0f\u201c\u6012\u9f99\u51fa\u868b\u201d\u52bf\u9053\u4e4b\u731b\uff0c\u5177\u6709\u77f3\u7834\u5929\u60ca\u4e4b\u52bf\uff0c\u53ea\u89c1\u6676\u5149\u7535\u95ea\uff0c\u5251\u6c14\u817e\u4e91\uff0c\u67f3\u5a9a\u5a18\u4f46\u611f\u624b\u4e2d\u4e00\u9707\uff0c\u78a7\u7389\u7bab\u51e0\u4e4e\u8131\u624b\u98de\u53bb\u3002\u5927\u60ca\u4e4b\u4e0b\uff0c\u987e\u4e0d\u5f97\u4f24\u4eba\uff0c\u98d8\u8eab\u659c\u63a0\u4e94\u5c3a\uff0c\u5389\u559d\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u65b9\u96ea\u5b9c\uff0c\u4f60\u7684\u5185\u529b\u4e5f\u4e0d\u5dee\u554a\uff01\u201d \u3000\u3000\u8bdd\u97f3\u672a\u5df2\u5ffd\u7136\u7bab\u653b\u6307\u70b9\uff0c\u4e00\u8fde\u53d1\u51fa\u4e94\u62db\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5fae\u5fae\u4e00\u60ca\uff0c\u6697\u9053\uff1a\u201c\u4e94\u5927\u9b54\u4e3b\u4e4b\u4e2d\uff0c\u5c31\u7b97\u6b64\u5973\u529f\u529b\u6700\u5dee\uff0c\u4f46\u5979\u51fa\u624b\u529b\u9053\u4e4b\u5f3a\uff0c\u81ea\u5df1\u5374\u4f9d\u7136\u4e0d\u80fd\u76f8\u6bd4\uff0c\u90a3\u53e6\u5916\u56db\u4f4d\uff0c\u81ea\u662f\u66f4\u8981\u96be\u7f20\u7684\u4e86\u2026\u2026\u201d\u601d\u8651\u4e4b\u95f4\uff0c\u624b\u4e2d\u5251\u52bf\u4e00\u53d8\uff0c\u4e00\u62db\u201c\u4e07\u6d41\u5f52\u5b97\u201d\uff0c\u6697\u542b\u516b\u5f0f\u53d8\u5316\uff0c\u7535\u75be\u4e00\u822c\u8fd8\u51fb\u8fc7\u53bb\u3002 \u3000\u3000\u67f3\u5a9a\u5a18\u7389\u7bab\u4e00\u62a1\uff0c\u5ffd\u7136\u6362\u5230\u5de6\u624b\u4e4b\u4e2d\uff0c\u53f3\u624b\u5341\u6307\u7ea4\u7ea4\uff0c\u8fc5\u5feb\u5730\u6263\u5411\u65b9\u96ea\u5b9c\u6267\u5251\u7684\u624b\u8155\u90e8\u4f4d\u3002 \u3000\u3000\u663e\u7136\uff0c\u5979\u5bf9\u65b9\u96ea\u5b9c\u8fd9\u62db\u201c\u4e07\u6d41\u5f52\u5b97\u201d\uff0c\u6709\u7740\u6781\u5927\u7684\u8ba4\u8bc6\uff0c\u662f\u6545\u5979\u624d\u6562\u8fd9\u7b49\u80c6\u5927\u7684\u653b\u53d6\u4ed6\u624b\u8155\u90e8\u4f4d\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u6697\u5403\u4e86\u4e00\u60ca\uff0c\u53ea\u89c9\u67f3\u5a9a\u5a18\u6307\u4e0a\u6f5c\u529b\u6cc9\u6d8c\uff0c\u8feb\u5f97\u81ea\u5df1\u9887\u6709\u6267\u5251\u4e0d\u7262\u4e4b\u52bf\u3002\u5306\u5fd9\u4e4b\u4e0b\uff0c\u65b9\u96ea\u5b9c\u5de6\u624b\u62cd\u51fa\u4e00\u638c\uff0c\u501f\u7740\u90a3\u5f39\u9707\u4e4b\u529b\uff0c\u98d8\u5411\u540e\u9000\u4e09\u5c3a\uff0c\u53f3\u624b\u957f\u5251\u4e00\u6447\uff0c\u65bd\u5c55\u4e86\u90a3\u62db\u7531\u5e08\u53d4\u8865\u6388\u7684\u7b2c\u4e5d\u62db\u5251\u6cd5\u201c\u7fa4\u9f99\u671d\u5929\u201d\uff0c\u53ea\u89c1\u4e00\u7247\u6f29\u7ed5\u7684\u6676\u5149\uff0c\u5b9b\u5982\u6012\u6d77\u626c\u6ce2\uff0c\u76f4\u5411\u90a3\u67f3\u5a9a\u5a18\u6d8c\u53bb\u3002 \u3000\u3000\u90a3\u67f3\u5a9a\u5a18\u5728\u65b9\u96ea\u5b9c\u7528\u51fa\u201c\u4e07\u6d41\u5f52\u5b97\u201d\u4e00\u62db\u4e4b\u65f6\uff0c\u5fc3\u4e2d\u7a83\u7a83\u6697\u559c\uff0c\u6562\u60c5\u5979\u5f53\u5e74\u5c31\u662f\u5728\u8fd9\u4e00\u62db\u5251\u6cd5\u4e4b\u4e0b\uff0c\u5403\u4e86\u9648\u5e0c\u6b63\u7684\u5927\u4e8f\uff0c\u6545\u800c\u4e8c\u5341\u5e74\u6765\uff0c\u5979\u4e0d\u7720\u4e0d\u4f11\u7684\u90fd\u5728\u63e3\u6469\u7740\u8fd9\u4e00\u62db\u5251\u6cd5\uff0c\u601d\u7d22\u7740\u5982\u4f55\u7834\u89e3\u8fd9\u62db\u5251\u6cd5\uff0c\u603b\u7b97\u5979\u624d\u667a\u4e0d\u4f4e\uff0c\u7ec8\u4e8e\u627e\u51fa\u4e86\u5176\u4e2d\u7834\u7efd\uff0c\u4e0d\u8bba\u8fd9\u4e00\u62db\u5251\u6cd5\u865a\u5b9e\u76f8\u751f\u7684\u53d8\u5316\uff0c\u53ea\u53d6\u5bf9\u65b9\u8155\u8109\uff0c\u679c\u7136\u8feb\u7684\u65b9\u96ea\u5b9c\u9000\u907f\u4e0d\u53ca\u3002 \u3000\u3000\u8bd5\u60f3\uff0c\u5979\u5c45\u7136\u80fd\u591f\u7834\u89e3\u4e86\u5251\u795e\u8fd9\u795e\u5947\u5251\u6cd5\uff0c\u5fc3\u4e2d\u9ad8\u5174\uff0c\u81ea\u662f\u53ef\u60f3\u800c\u77e5\uff0c\u4f46\u5979\u5374\u672a\u6599\u5230\uff0c\u65b9\u96ea\u5b9c\u8fd9\u62db\u201c\u7fa4\u9f99\u671d\u5929\u201d\u6bd4\u90a3\u201c\u4e07\u6d41\u5f52\u5b97\u201d\u66f4\u8981\u9ad8\u660e\u591a\u591a\u3002\u5979\u72c2\u559c\u4e4b\u4f59\uff0c\u4e00\u65f6\u4e0d\u514d\u5931\u795e\uff0c\u7b49\u5230\u53d1\u73b0\u65b9\u96ea\u5b9c\u5251\u5149\u4f3c\u96ea\uff0c\u72c2\u6d8c\u800c\u6765\uff0c\u5fc3\u4e2d\u90a3\u4efd\u60ca\u51db\uff0c\u53ef\u5c31\u975e\u7b14\u58a8\u6240\u80fd\u5f62\u5bb9\u3002 \u3000\u3000\u6025\u8feb\u95f4\u5df2\u65e0\u601d\u5fd6\u4f59\u5730\uff0c\u7389\u7bab\u771f\u529b\u8d2f\u6ce8\uff0c\u4e00\u5f0f\u201c\u5929\u5916\u6765\u9e3f\u201d\u5e26\u8d77\u9635\u9635\u5c16\u5578\u4e4b\u58f0\uff0c\u62a4\u5411\u81ea\u5df1\u7684\u80f8\u8179\u3002 \u3000\u3000\u540c\u65f6\uff0c\u67f3\u5a9a\u5a18\u6740\u673a\u9661\u73b0\uff0c\u53f3\u624b\u6697\u84c4\u771f\u529b\uff0c\u53ea\u7b49\u65b9\u96ea\u5b9c\u5251\u52bf\u4e00\u655b\uff0c\u5979\u90a3\u85cf\u5728\u6307\u7532\u4e4b\u5185\u7684\u7edd\u6bd2\u8ff7\u795e\u836f\u7c89\uff0c\u4fbf\u53ef\u5f39\u5411\u5bf9\u65b9\u9762\u76ee\uff0c\u5c06\u65b9\u96ea\u5b9c\u8ff7\u5012\u5f53\u5730\u3002 \u3000\u3000\u67f3\u5a9a\u5a18\u7684\u7b97\u76d8\u6253\u5f97\u4e0d\u8c13\u4e0d\u7cbe\uff0c\u4f46\u5979\u5374\u5ffd\u7565\u4e86\u4e00\u4ef6\u4e8b\uff0c\u90a3\u4fbf\u662f\u65b9\u96ea\u5b9c\u5df2\u7136\u4e0d\u5bb9\u5979\u518d\u6709\u5598\u606f\u7684\u673a\u4f1a\u3002 \u3000\u3000\u9f99\u884c\u4e5d\u5251\u4e2d\u7684\u8fd9\u4e00\u62db\u201c\u7fa4\u9f99\u671d\u5929\u201d\u4e43\u662f\u65b9\u96ea\u5b9c\u7684\u5e08\u7956\uff0c\u5251\u5723\u9f99\u8d85\u7fa4\u4ed9\u53bb\u4e4b\u524d\u6240\u609f\u51fa\u6765\u7684\u4e00\u62db\u5251\u6cd5\uff0c\u867d\u7136\u773c\u4e0b\u65b9\u96ea\u5b9c\u771f\u529b\u4e0d\u8db3\uff0c\u96be\u4ee5\u53d1\u6325\u5176\u4e2d\u8bf8\u822c\u5965\u5999\uff0c\u4f46\u4e5f\u4e0d\u662f\u67f3\u5a9a\u5a18\u6240\u80fd\u6298\u5176\u950b\u9510\u4e86\u3002 \u3000\u3000\u67f3\u5a9a\u5a18\u7389\u7bab\u65b9\u81ea\u653b\u4e86\u51fa\u53bb\uff0c\u53ea\u89c9\u4e94\u6307\u4e00\u9707\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u7bab\u5251\u5df2\u7136\u78b0\u5728\u4e00\u8d77\u3002 \u3000\u3000\u5206\u96ea\u5b9c\u9661\u7136\u5927\u559d\u4e00\u58f0\u9053\uff1a\u201c\u64a4\u624b\uff01\u201d\u4f46\u89c1\u4e00\u7f15\u78a7\u7eff\u7684\u5149\u534e\u98de\u95ea\u800c\u8d77\uff0c\u67f3\u5a9a\u5a18\u7684\u90a3\u652f\u78a7\u7389\u7bab\u4e1a\u5df2\u8131\u624b\u8dcc\u5411\u4e08\u5916\u8349\u4e1b\u4e4b\u4e2d\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u957f\u5251\u5bd2\u8292\u4e4d\u5410\uff0c\u62b5\u5728\u5a9a\u5a18\u54bd\u5589\u4e4b\u524d\uff0c\u53ea\u8981\u518d\u5411\u524d\u5206\u5bf8\uff0c\u67f3\u5a9a\u5a18\u90a3\u6839\u5589\u7ba1\u5fc5\u5c06\u523a\u7a7f\u3002 \u3000\u3000\u67f3\u5a9a\u5a18\u53ea\u5413\u5f97\u9762\u65e0\u4eba\u8272\uff0c\u7c89\u6c57\u5982\u96e8\uff0c\u662f\u4ee5\u5979\u90a3\u6697\u85cf\u6307\u7532\u4e4b\u4e2d\u7684\u6bd2\u7c89\uff0c\u4e5f\u5fd8\u4e86\u4f7f\u7528\u4e86\u3002 \u3000\u3000\u5b8b\u5b5a\u5728\u65c1\u53ea\u77a7\u7684\u9f13\u638c\u5927\u7b11\u9053\uff1a\u201c\u597d\u5251\u6cd5\uff01\u679c\u7136\u4e0d\u6127\u5251\u5723\u4f20\u4eba\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u5b8b\u8001\u8fc7\u5956\uff0c\u8fd9\u4e00\u62db\u5251\u6cd5\u4e0d\u662f\u6069\u5e08\u6240\u6388\u2026\u2026\u201d\u4ed6\u8bdd\u97f3\u4e00\u987f\uff0c\u7a81\u7136\u5411\u67f3\u5a9a\u5a18\u559d\u9053\uff1a\u201c\u4e94\u5927\u9b54\u4e3b\u4e5f\u4e0d\u8fc7\u5c14\u5c14\uff0c\u5012\u53eb\u5728\u4e0b\u5931\u7b11\u4e86\u3002\u201d\u5ffd\u5730\u4e00\u6536\u957f\u5251\uff0c\u5411\u540e\u9000\u4e86\u4e94\u6b65\u3002 \u3000\u3000\u5b8b\u5b5a\u6697\u72b6\u5927\u4e3a\u60cb\u501f\uff0c\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u653e\u4e86\u8fd9\u5973\u9b54\u5934\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5927\u7b11\u9053\uff1a\u201c\u65e2\u975e\u5728\u4e0b\u4e09\u5251\u4e4b\u654c\uff0c\u6740\u4e86\u5c82\u4e0d\u662f\u6709\u4e9b\u4e0d\u503c\u5417\uff1f\u201d \u3000\u3000\u8fd9\u7b49\u8bdd\uff0c\u539f\u4e5f\u65e0\u53ef\u539a\u975e\uff0c\u53ea\u662f\u773c\u4e0b\u7684\u654c\u624b\uff0c\u5e76\u975e\u5e73\u51e1\u4e4b\u8f88\uff0c\u5728\u5b8b\u5b5a\u5fc3\u76ee\u4e2d\u8a00\uff0c\u4e94\u5927\u9b54\u4e3b\u662f\u4f55\u7b49\u4eba\u7269\uff1f\u81ea\u7136\u4e0d\u53ef\u7b49\u95f2\u89c6\u4e4b\u7684\u4e86\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u65b9\u96ea\u5b9c\u8bdd\u97f3\u4e00\u843d\uff0c\u4ed6\u53ea\u597d\u82e6\u7b11\u9053\uff1a\u201c\u8001\u5f1f\u8c6a\u723d\u4e4b\u5fc3\uff0c\u56fa\u662f\u53ef\u5609\uff0c\u4f46\u64d2\u864e\u5bb9\u6613\u7eb5\u864e\u96be\u7684\u9053\u7406\uff0c\u8001\u5f1f\u5e94\u662f\u77e5\u6653\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u665a\u8f88\u672c\u65e0\u4f24\u4eba\u4e4b\u5fc3\uff0c\u81ea\u662f\u8c08\u4e0d\u5230\u64d2\u864e\u3001\u7eb5\u864e\u7684\u5229\u5bb3\u5f97\u5931\uff0c\u4f60\u8001\u4e5f\u4e0d\u5fc5\u4e3a\u6b64\u803d\u5fc3\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u5927\u6982\u77e5\u9053\u62d7\u4ed6\u4e0d\u8fc7\uff0c\u53ea\u597d\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u770b\u6765\u662f\u8001\u673d\u591a\u8bdd\u4e86\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u9661\u7136\u5a07\u7b11\u9053\uff1a\u201c\u662f\u554a\uff01\u4f60\u5b8b\u8001\u90aa\u5982\u662f\u4e0d\u670d\uff0c\u59d1\u5a18\u548c\u4f60\u4e00\u51b3\u80dc\u8d1f\u5982\u4f55\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u53ea\u542c\u5f97\u5927\u4e3a\u6c14\u607c\uff0c\u559d\u9053\uff1a\u201c\u67f3\u5a9a\u5a18\uff0c\u4f60\u5f53\u771f\u662f\u8d8a\u6765\u8d8a\u76ee\u4e2d\u65e0\u4eba\u4e86\u3002\u5b8b\u67d0\u867d\u7136\u6bd4\u4e0d\u4e0a\u4f60\u4eec\u4e94\u5927\u9b54\u4e3b\u7684\u58f0\u52bf\u55a7\u8d6b\uff0c\u4f46\u4e5f\u4e0d\u662f\u6328\u4eba\u6307\u70b9\u7b11\u8bba\u4e4b\u58eb\u554a\uff01\u4f60\u65e2\u662f\u6709\u5174\u5728\u65b0\u8d25\u4e4b\u4f59\uff0c\u9f13\u52c7\u518d\u6218\uff0c\u5b8b\u67d0\u8bf4\u4e0d\u5f97\u53ea\u597d\u5949\u966a\u2026\u2026\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u633d\u8d77\u8863\u8896\uff0c\u5f53\u771f\u5c31\u8981\u52a8\u624b\u3002 \u3000\u3000\u4f46\u662f\u90a3\u67f3\u5a9a\u5a18\u5374\u88ab\u4ed6\u8fd9\u51e0\u53e5\u8bdd\u8bf4\u7684\u7c89\u8138\u970d\u7136\u53d8\u8272\uff0c\u90a3\u4e00\u53cc\u674f\u773c\uff0c\u5728\u65b9\u96ea\u5b9c\u8eab\u4e0a\u8f6c\u4e86\u51e0\u8f6c\u3002\u5ffd\u7136\u53c8\u6251\u54e7\u4e00\u7b11\u3002 \u3000\u3000\u5b8b\u5b5a\u76b1\u7709\u9053\uff1a\u201c\u4f60\u7b11\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u5a07\u58f0\u63a5\u9053\uff1a\u201c\u5b8b\u8001\u90aa\uff0c\u4f60\u8fd9\u4e3b\u610f\u6253\u7684\u5012\u662f\u4e0d\u9519\uff0c\u53ea\u53ef\u60dc\u4f60\u627e\u9519\u4e86\u4eba\u54ea\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u67f3\u5a9a\u5a18\uff0c\u4f60\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u683c\u683c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5b8b\u8001\u90aa\u3002\u4f60\u5c11\u6253\u5982\u610f\u7b97\u76d8\uff0c\u6211\u5018\u662f\u8ddf\u4f60\u52a8\u624b\uff0c\u90a3\u5c82\u4e0d\u662f\u4e0a\u4e86\u4f60\u7684\u5927\u5f53\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u5446\u4e86\u4e00\u5446\uff0c\u663e\u7136\u5e76\u672a\u60f3\u51fa\u67f3\u5a9a\u5a18\u8a00\u4e0b\u4e4b\u610f\uff0c\u6545\u800c\u53ea\u770b\u7740\u67f3\u5a9a\u5a18\u53d1\u6123\uff0c\u4e0d\u77e5\u600e\u4e48\u56de\u7b54\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u76ee\u6697\u5b8b\u5b5a\u8fd9\u7b49\u795e\u60c5\uff0c\u5fcd\u4e0d\u4f4f\u7b11\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u4f60\u8001\u4e0d\u5fc5\u5947\u602a\uff0c\u67f3\u9b54\u4e3b\u53ea\u662f\u4e0d\u60f3\u8ddf\u4f60\u52a8\u624b\u800c\u5df2\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u4f3c\u662f\u4e0d\u4fe1\uff0c\u4f46\u53c8\u4e0d\u5f97\u4e0d\u4fe1\u7684\u7b11\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u8001\u673d\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e0d\u7b49\u5b8b\u5b5a\u8bf4\u5b8c\uff0c\u63a5\u9053\uff1a\u201c\u8fd9\u4e8b\u5341\u5206\u7b80\u5355\uff0c\u5982\u662f\u5728\u4e0b\u8f6c\u8eab\u800c\u53bb\uff0c\u5979\u5c31\u4f1a\u7b54\u5e94\u4e0e\u4f60\u4e00\u62fc\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u5373\u4ee4\u518d\u8ff7\u7cca\uff0c\u8fd9\u65f6\u4e5f\u660e\u767d\u65b9\u96ea\u5b9c\u8bdd\u4e2d\u4e4b\u610f\u4e86\uff0c\u6562\u60c5\u90a3\u67f3\u5a9a\u5a18\u4e43\u662f\u62c5\u5fc3\u65b9\u96ea\u5b9c\u63d2\u624b\u5176\u95f4\u554a\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u8bdd\u97f3\u4e00\u843d\uff0c\u5b8b\u5b5a\u9661\u7136\u4ef0\u5929\u5927\u7b11\u9053\uff1a\u201c\u8001\u5f1f\u53f0\uff0c\u8fd9\u4e48\u8bf4\uff0c\u662f\u8001\u673d\u6cbe\u4e86\u4f60\u7684\u5149\u7f57\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u8fd9\u53eb\u665a\u8f88\u54ea\u5f53\u5f97\u8d77\u5566\uff01\u201d \u3000\u3000\u8bdd\u97f3\u4e00\u987f\uff0c\u63a5\uff1a\u201c\u4e0d\u8fc7\uff0c\u5b8b\u8001\u4e5f\u4e0d\u5fc5\u4e3a\u6b64\u52a8\u6012\uff0c\u4e94\u5927\u9b54\u4e3b\u4e0d\u6562\u5e94\u4f60\u8001\u6311\u6218\u7684\u8bdd\uff0c\u4e00\u65e6\u4f20\u4e86\u51fa\u53bb\uff0c\u4e5f\u591f\u4f60\u8001\u5149\u5f69\u7684\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u672c\u662f\u5fc3\u4e2d\u5927\u5927\u4e0d\u4e50\uff0c\u82e5\u975e\u65b9\u96ea\u5b9c\u63a5\u8bdd\u751a\u5feb\uff0c\u5b8b\u5b5a\u65e9\u5df2\u5c31\u6325\u8d77\u8001\u62f3\uff0c\u5411\u67f3\u5a9a\u5a18\u6572\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u4f38\u51fa\u7684\u624b\u81c2\u521a\u5230\u4e00\u534a\uff0c\u7adf\u7136\u6536\u4e86\u56de\u6765\uff0c\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8001\u5f1f\u8bf4\u7684\u5bf9\uff0c\u8001\u592b\u679c\u7136\u591f\u5149\u5f69\u7684\u4e86\u2026\u2026\u201d\u8bdd\u97f3\u4e00\u987f\uff0c\u76ee\u5149\u5728\u67f3\u5a9a\u5a18\u8eab\u4e0a\u4e00\u626b\uff0c\u559d\u9053\uff1a\u201c\u67f3\u5a9a\u5a18\uff0c\u8001\u592b\u6210\u5168\u4f60\u4e86\uff0c\u4f60\u8bf7\u5427\uff01\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u4e5f\u4e0d\u7981\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4f60\u662f\u5728\u4e0b\u90a3\u9010\u5ba2\u4ee4\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u51b7\u7b11\uff1a\u201c\u662f\u53c8\u5982\u4f55\uff1f\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u51b7\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5b8b\u8001\u90aa\uff0c\u4f60\u60f3\u652f\u4f7f\u6211\uff0c\u53ea\u6015\u8fd8\u65e9\u5f97\u5f88\u5462\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u8bf4\u5f97\u4e0d\u9519\uff0c\u8001\u592b\u5e76\u65e0\u652f\u4f7f\u59d1\u5a18\u4e4b\u610f\uff0c\u4f46\u65b9\u5144\u5f1f\u773c\u4e0b\u5df2\u662f\u5251\u795e\u4f20\u4eba\uff0c\u4e94\u5927\u9b54\u4e3b\u8981\u60f3\u5728\u6b66\u6797\u5174\u98ce\u4f5c\u6d6a\uff0c\u7eb5\u7136\u9648\u5927\u4fa0\u4e0d\u518d\u8fc7\u95ee\uff0c\u65b9\u8001\u5f1f\u53ef\u5c31\u4e0d\u4f1a\u4e0d\u7ba1\uff0c\u8001\u592b\u8981\u4f60\u65e9\u8d70\uff0c\u662f\u8981\u4f60\u53bb\u901a\u77e5\u90a3\u51e0\u4f4d\u9b54\u4e3b\uff0c\u6700\u597d\u662f\u7acb\u5373\u9500\u58f0\u655b\u8ff9\uff0c\u83ab\u8981\u81ea\u5bfb\u82e6\u5934\u3002\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u53ea\u542c\u5f97\u8138\u4e0a\u9634\u6674\u4e0d\u5b9a\uff0c\u4e45\u4e45\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u5b8b\u8001\u8bf4\u7684\u5012\u662f\u5b9e\u8bdd\uff0c\u5728\u4e0b\u79c9\u627f\u5e08\u547d\uff0c\u53ea\u6015\u4e0d\u5f97\u4e0d\u5f97\u7f6a\u4e94\u5927\u9b54\u4e3b\u7684\u4e86\u3002\u201d \u3000\u3000\u827e\u4e1c\u6d77\u5728\u65c1\u5e72\u54b3\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u65b9\u8001\u5f1f\uff0c\u5982\u6b64\u8bf4\u6765\uff0c\u56db\u7075\u6559\u4e5f\u662f\u8001\u5f1f\u7684\u654c\u4eba\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u4e0d\u4e00\u5b9a\uff01\u201d \u3000\u3000\u827e\u4e1c\u6d77\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u56db\u7075\u6559\u4e5f\u662f\u4e94\u5927\u9b54\u4e3b\u4e2d\u4eba\uff0c\u8001\u5f1f\u8fd9\u8bdd\uff0c\u4e0d\u662f\u6709\u4e9b\u81ea\u76f8\u77db\u76fe\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u654c\u53cb\u4e4b\u5206\uff0c\u53ea\u5728\u4e00\u5ff5\u4e4b\u95f4\uff0c\u8d35\u6559\u638c\u6559\u5982\u662f\u80fd\u591f\u9020\u798f\u6b66\u6797\uff0c\u81ea\u7136\u5c31\u4e0d\u4f1a\u662f\u5728\u4e0b\u7684\u654c\u4eba\u4e86\u3002\u201d \u3000\u3000\u827e\u4e1c\u6d77\u6697\u9053\uff1a\u541b\u5b50\u53ef\u4ee5\u6b3a\u4e4b\u4ee5\u65b9\uff0c\u56db\u7075\u6559\u53ea\u8981\u8c28\u614e\u5c06\u4e8b\uff0c\u4e0d\u6015\u4f60\u8fd9\u9ec4\u6bdb\u4e73\u5b50\u4e0d\u4e0a\u5f53\u2026\u2026\uff0c\u5f53\u4e0b\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff01\u201d \u3000\u3000\u9ad8\u65e0\u654c\u4e3a\u4eba\u803f\u76f4\uff0c\u95fb\u8a00\u63a5\u9053\uff1a\u201c\u65b9\u5144\u5f1f\uff0c\u56db\u7075\u6559\u4e2d\u7684\u522b\u4eba\uff0c\u5144\u5f1f\u4e5f\u8bb8\u65e0\u529b\u517c\u987e\uff0c\u4f46\u5144\u5f1f\u6240\u5c5e\u624b\u4e0b\uff0c\u81ea\u4eca\u800c\u540e\uff0c\u5b9a\u5c06\u4ee5\u6b66\u6797\u6b63\u4e49\u4e3a\u524d\u63d0\uff0c\u4f5c\u4e0a\u4e00\u756a\u8f70\u8f70\u70c8\u70c8\u7684\u4e8b\u60c5\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u9ad8\u5144\u8c6a\u4eba\u5feb\u8bed\u5728\u4e0b\u611f\u6fc0\u5f97\u5f88\uff01\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u5411\u67f3\u5a9a\u5a18\u9053\uff1a\u201c\u4f60\u8ddf\u56db\u7075\u6559\u7684\u8fc7\u8282\uff0c\u53ef\u5426\u770b\u5728\u533a\u533a\u9762\u4e0a\uff0c\u4f11\u8981\u63d0\u8d77\u5462\uff1f\u201d \u3000\u3000\u67f3\u5a9a\u5a18\u683c\u683c\u4e00\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u51b2\u7740\u4f60\u8fd9\u4f4d\u5251\u795e\u4f20\u4eba\u7684\u9762\u5b50\uff0c\u6211\u4e0d\u7b54\u5e94\u884c\u5417\uff1f\u597d\u5427\uff01 \u3000\u3000\u4eca\u65e5\u5c31\u7b97\u6211\u6ca1\u6765\u2026\u2026\u201d\u7c89\u8138\u5bb9\u8272\u5ffd\u7136\u4e00\u5bd2\uff0c\u5411\u9ad8\u65e0\u654c\u559d\u9053\uff1a\u201c\u59d3\u9ad8\u7684\uff0c\u4f60\u5f80\u540e\u53ef\u5f97\u7ed9\u6211\u5f53\u5fc3\u4e00\u4e9b\u3002\u201d\u4f46\u89c1\u5979\u957f\u5578\u4e00\u58f0\uff0c\u4e00\u626d\u5a07\u8eaf\uff0c\u8d70\u4e86\u4e2a\u6ca1\u6709\u8e2a\u5f71\u3002 \u3000\u3000\u67f3\u5a9a\u5a18\u6765\u7684\u7a81\u7136\uff0c\u53bb\u7684\u66f4\u662f\u4e0d\u7740\u75d5\u8ff9\uff0c\u5b8b\u5b5a\u53ea\u770b\u5f97\u957f\u957f\u4e00\u53f9\u9053\uff1a\u201c\u8fd9\u5973\u9b54\u5934\u679c\u7136\u5389\u5bb3\uff0c\u4eca\u65e5\u5bb9\u5979\u79bb\u53bb\uff0c\u5b9e\u662f\u5927\u5927\u4e0d\u667a\u4e4b\u4e3e\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6de1\u8c08\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u4e94\u5927\u9b54\u4e3b\u86f0\u5c45\u6570\u5341\u5e74\uff0c\u5982\u4eca\u516c\u7136\u9732\u9762\uff0c\u5fc5\u6709\u6781\u5927\u9634\u8c0b\uff0c\u4eca\u65e5\u82e5\u662f\u7559\u4e0b\u67f3\u5a9a\u5a18\uff0c\u4e5f\u4e0d\u89c1\u5f97\u6709\u591a\u5927\u7528\u5904\uff0c\u5018\u662f\u80fd\u56e0\u5979\u4e4b\u53bb\uff0c\u628a\u4e94\u5927\u9b54\u4e3b\u805a\u5728\u4e00\u5904\uff0c\u7136\u540e\u5bb9\u665a\u8f88\u8d76\u53bb\uff0c\u4e00\u9f13\u800c\u6b7c\uff0c\u5c82\u4e0d\u662f\u5927\u5927\u7684\u514d\u53bb\u8bb8\u591a\u624b\u811a\u5417\uff1f\u201d\u4ed6\u8bf4\u7684\u4e5f\u7b97\u5fc3\u5e73\u6c14\u548c\uff0c\u4f46\u90a3\u575a\u5fcd\u8c6a\u723d\u7684\u52c7\u6c14\uff0c\u5374\u662f\u6ea2\u4e8e\u8a00\u8868\u4e4b\u5916\u3002 \u3000\u3000\u5b8b\u5b5a\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u7684\u786e\u5fc3\u4e0d\u5c0f\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e08\u7236\u9057\u547d\uff0c\u505a\u5f1f\u5b50\u7684\u81ea\u7136\u8981\u5949\u884c\u552f\u8c28\u7684\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u9a87\u7136\u4e00\u9707\uff0c\u8131\u53e3\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u2026\u2026\u4ee4\u5e08\u9648\u5927\u4fa0\u53ef\u662f\u5df2\u7136\u4ed9\u53bb\u4e86\u5417\uff1f\u201d \u3000\u3000\u6562\u60c5\u90a3\u5e08\u7236\u9057\u547d\u56db\u5b57\uff0c\u65e0\u610f\u4e4b\u4e2d\u88ab\u65b9\u96ea\u5b9c\u8bf4\u51fa\u6765\uff0c\u5251\u795e\u7684\u6b7b\u8baf\uff0c\u4e5f\u5c31\u7b49\u4e8e\u6b63\u5f0f\u5411\u6b66\u6797\u5ba3\u5e03\u4e86\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u867d\u7136\u5927\u611f\u61ca\u607c\uff0c\u6697\u602a\u81ea\u5df1\u4e0d\u8be5\u5982\u6b64\u5927\u610f\uff0c\u4f46\u6b64\u523b\u5374\u5df2\u65e0\u6cd5\u6539\u53e3\uff0c\u53ea\u9eef\u7136\u63a5\u9053\uff1a\u201c\u5148\u5e08\u5df2\u5728\u6708\u524d\uff0c\u9053\u6210\u98de\u5347\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u8138\u4e0a\u795e\u8272\u60e8\u6de1\uff0c\u957f\u957f\u5730\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u987f\u8db3\u9053\uff1a\u201c\u8001\u5929\u4e3a\u4f55\u65e0\u773c\uff0c\u597d\u4eba\u600e\u5730\u5c31\u96be\u4eab\u957f\u5bff\u554a\u2026\u2026\u201d \u3000\u3000\u827e\u4e1c\u6d77\u5374\u662f\u5fc3\u4e2d\u6697\u559c\uff0c\u5927\u58f0\u9053\uff1a\u201c\u65b9\u5144\u5f1f\uff0c\u5251\u795e\u9648\u5927\u4fa0\u4ed9\u5f52\u9053\u5c71\u4e4b\u4e8b\uff0c\u600e\u5730\u6b66\u6797\u4e2d\u5e76\u4e0d\u77e5\u6653\uff0c\u8001\u5f1f\u4e0d\u662f\u8bc8\u4f5c\u7591\u5175\u4e4b\u8ba1\u5427\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\u5fc3\u4e2d\u5927\u662f\u4e0d\u5feb\uff0c\u51b7\u54fc\u9053\uff1a\u201c\u6587\u5144\u53ef\u662f\u4e0d\u4fe1\u5417\uff1f\u201d \u3000\u3000\u827e\u4e1c\u6d77\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u8001\u673d\u77e5\u9053\u4ee5\u9648\u5927\u4fa0\u7684\u6b66\u529f\uff0c\u82b1\u7532\u521d\u5ea6\u4e4b\u4e2d\uff0c\u6b63\u662f\u5982\u65e5\u4e2d\u5929\u4e4b\u65f6\uff0c\u8001\u5f1f\u5ffd\u8bf4\u4ee4\u5e08\u4ed9\u901d\uff0c\u786e\u662f\u53eb\u4eba\u96be\u4ee5\u76f8\u4fe1\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u9053\uff1a\u201c\u4ed6\u8fd9\u7b49\u4e0d\u76f8\u4fe1\u5e08\u7236\u5df2\u6b7b\uff0c\u7a76\u7adf\u53c8\u662f\u4e3a\u4e86\u4ec0\u4e48\u5462\uff1f\u65e2\u662f\u4f60\u4e0d\u4fe1\uff0c\u6211\u4f55\u4e0d\u5c06\u8ba1\u5c31\u8ba1\uff0c\u53eb\u4ed6\u4eec\u5728\u5fc3\u4e2d\u7591\u795e\u7591\u9b3c\uff1f\u201d \u3000\u3000\u5fc3\u5ff5\u65e2\u5b9a\uff0c\u7adf\u7136\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5148\u5e08\u6b66\u529f\uff0c\u786e\u5df2\u8d85\u51e1\u5165\u5723\uff0c\u5982\u5c31\u5e38\u60c5\u800c\u8a00\uff0c\u4ed6\u8001\u4eba\u5bb6\u6d3b\u4e0a\u4e2a\u767e\u5c81\u51fa\u5934\uff0c\u5e94\u662f\u4e0d\u4f1a\u6709\u95ee\u9898\u2026\u2026\u53ea\u662f\u2026\u2026\u201d\u4ed6\u6545\u610f\u987f\u4f4f\u8bdd\u97f3\uff0c\u4e0d\u5f80\u4e0b\u8bf4\u3002 \u3000\u3000\u827e\u4e1c\u6d77\u679c\u7136\u672a\u66fe\u60f3\u5230\u65b9\u96ea\u5b9c\u8fd9\u7b49\u4e73\u5b50\uff0c\u4e5f\u4f1a\u5bf9\u4ed6\u4f7f\u4e0a\u8fd9\u7b49\u7591\u5175\u4e4b\u8ba1\uff0c\u662f\u4ee5\u8131\u53e3\u6025\u6025\u95ee\u9053\uff1a\u201c\u53ea\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u53ea\u662f\u5148\u5e08\u4e5f\u8bb8\u538c\u5026\u4e86\u7ea2\u5c18\u4e16\u4e8b\uff0c\u65e9\u6c42\u89e3\u8131\uff0c\u90a3\u5c31\u53e6\u5f53\u522b\u8bba\u4e86\u3002\u201d \u3000\u3000\u827e\u4e1c\u6d77\u9053\uff1a\u201c\u4e0d\u9519\u4ee5\u9648\u5927\u4fa0\u90a3\u7b49\u8d85\u51e1\u8131\u4fd7\u7684\u6027\u60c5\uff0c\u538c\u6076\u7ea2\u5c18\uff0c\u4e5f\u662f\u610f\u6599\u4e2d\u4e8b\u2026\u2026\u201d \u3000\u3000\u7565\u4e00\u6c89\u541f\uff0c\u5ffd\u7136\u81ea\u4f5c\u806a\u660e\u5730\u5927\u7b11\u9053\uff1a\u201c\u662f\u4e86\uff0c\u8001\u673d\u660e\u767d\u5566\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e0d\u7981\u4e00\u6014\u9053\uff1a\u201c\u5c0a\u9a7e\u660e\u767d\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u827e\u4e1c\u6d77\u9762\u8272\u51dd\u91cd\u5730\u95ee\u9053\uff1a\u201c\u65b9\u5144\u5f1f\uff0c\u4ee4\u5e08\u4ed9\u53bb\u4e4b\u65f6\uff0c\u4f60\u53ef\u662f\u5b88\u5019\u5728\u4fa7\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4ed8\u9053\uff0c\u4ed6\u8fd9\u4e48\u614e\u91cd\u7684\u95ee\u53ca\u6b64\u4e8b\uff0c\u90a3\u5bf9\u4ed6\u5fc5\u662f\u5341\u5206\u91cd\u8981\u7684\u4e86\uff0c\u6211\u5982\u662f\u76f4\u8bf4\uff0c\u6050\u6015\u4ed6\u5c31\u80fd\u770b\u7a7f\u6211\u7684\u5fc3\u610f\u4e86\u3002\u8f6c\u5ff5\u4e4b\u9645\uff0c\u4e0d\u89c9\u5730\u6545\u4f5c\u6c89\u601d\uff0c\u9053\uff1a\u201c\u8fd9\u2026\u2026\u533a\u533a\u53ea\u662f\u89c1\u5230\u5148\u5e08\u7559\u4e0b\u7684\u9057\u547d\uff01\u201d \u3000\u3000\u827e\u4e1c\u6d77\u63a5\u9053\uff1a\u201c\u9648\u5927\u4fa0\u7684\u6cd5\u4f53\uff0c\u53ef\u662f\u8001\u5f1f\u5b89\u7684\u846c\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u5934\u9053\uff1a\u201c\u5148\u5e08\u9057\u547d\uff0c\u5bf9\u81ea\u5df1\u7684\u4e00\u5207\uff0c\u4ff1\u5df2\u4f5c\u4e86\u9002\u5f53\u5b89\u6392\uff0c\u81ea\u662f\u7528\u4e0d\u7740\u5728\u4e0b\u64cd\u5fc3\u7684\u4e86\u3002\u201d \u3000\u3000\u827e\u4e1c\u6d77\u5ffd\u7136\u5927\u7b11\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u5728\u65c1\u503e\u542c\uff0c\u76f4\u5230\u6b64\u523b\uff0c\u65b9\u59cb\u63a5\u9053\uff1a\u201c\u8001\u5f1f\u53f0\uff0c\u4ee4\u5e08\u53ea\u662f\u7ed9\u4f60\u7559\u4e0b\u4e00\u7eb8\u9057\u547d\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff0c\u201c\u6b63\u662f\u5982\u6b64\uff0c\u665a\u8f88\u5bf9\u5148\u5e08\u4ed9\u53bb\uff0c\u7adf\u4e0d\u5bb9\u5f1f\u5b50\u89c1\u4ed6\u6700\u540e\u4e00\u9762\uff0c\u5b9e\u662f\u5fc3\u4e2d\u4e0d\u5b89\u5f97\u5f88\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u6709\u4e00\u4ef6\u4e8b\uff0c\u8001\u673d\u4e0d\u77e5\u5f53\u4e0d\u5f53\u95ee\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5b8b\u8001\u6709\u8bdd\u8bf7\u8bb2\u65e0\u59a8\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8001\u673d\u6000\u7591\u4ee4\u5e08\u9648\u5927\u4fa0\u5c1a\u5728\u4eba\u95f4\u3002\u201d \u3000\u3000\u4ed6\u8fd9\u7b49\u60f3\u6cd5\uff0c\u6b63\u548c\u827e\u4e1c\u6d77\u4e0d\u8c0b\u800c\u53f0\uff0c\u4e5f\u4e0e\u65b9\u96ea\u5b9c\u7591\u5175\u4e4b\u8ba1\u76f8\u5951\uff0c\u662f\u6545\uff0c\u65b9\u96ea\u5b9c\u6545\u610f\u76b1\u7709\u9053\uff1a\u201c\u665a\u8f88\u5bf9\u4e8e\u957f\u8005\u4e4b\u4e8b\uff0c\u672a\u66fe\u76ee\u7779\uff0c\u4e0d\u4fbf\u5984\u4f5c\u63a8\u6d4b\uff0c\u5148\u5e08\u65e2\u7136\u7559\u4e0b\u9057\u547d\uff0c\u5f1f\u5b50\u81ea\u662f\u8981\u7a7f\u5b5d\u53d1\u5f15\u7684\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8001\u5f1f\u6709\u8fd9\u7b49\u5b5d\u5fc3\uff0c\u81ea\u65e0\u4e0d\u5f53\u3002\u201d \u3000\u3000\u827e\u4e1c\u6d77\u63a5\u9053\uff1a\u201c\u65b9\u5144\u5f1f\uff0c\u5b8b\u5144\u8bf4\u7684\u751a\u662f\u5408\u7406\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5ba3\u6014\u6014\u4ed6\u8bf4\u9053\uff1a\u201c\u827e\u5144\u662f\u8bf4\u5148\u5e08\u679c\u7136\u672a\u66fe\u4ed9\u53bb\u5417\uff1f\u201d \u3000\u3000\u827e\u4e1c\u6d77\u9053\uff1a\u201c\u4ee5\u4ee4\u5e08\u9648\u5927\u4fa0\u7684\u6b66\u529f\u800c\u8a00\uff0c\u6b32\u6c42\u957f\u751f\uff0c\u4e43\u662f\u6613\u6263\u53cd\u638c\u4e4b\u4e8b\uff0c\u9648\u5927\u4fa0\u5ffd\u7136\u4ed9\u53bb\uff0c\u81ea\u662f\u4e0d\u80fd\u4e0d\u53eb\u4eba\u5fc3\u4e2d\u72af\u7591\u7684\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4ed8\u9053\uff1a\u4e0d\u6015\u4f60\u4e0d\u4e0a\u5f53\u3002\u4f46\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u827e\u5144\u4e4b\u8a00\uff0c\u5144\u5f1f\u4e5f\u89c9\u6709\u7406\uff0c\u4e0d\u8fc7\uff0c\u5148\u5e08\u7559\u6709\u9057\u547d\uff0c\u5144\u5f1f\u7eb5\u7136\u7591\u8651\uff0c\u4e5f\u4e0d\u4fbf\u5ba3\u8bf8\u4e8e\u53e3\u3002\u201d \u3000\u3000\u8bdd\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u827e\u5144\uff0c\u6b64\u4e8b\u5c1a\u76fc\u4e24\u4f4d\u83ab\u8981\u628a\u5fc3\u4e2d\u72af\u7591\u4e4b\u4e8b\uff0c\u4f20\u8bf4\u51fa\u53bb\u624d\u597d\u3002\u201d \u3000\u3000\u827e\u4e1c\u6d77\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4f3c\u662f\u65e0\u53ef\u5948\u4f55\u5730\u957f\u957f\u4e00\u53f9\uff0c\u9053\uff1a\u201c\u5e08\u7236\u5982\u662f\u672a\u66fe\u4ed9\u53bb\uff0c\u6b64\u4e3e\u5fc5\u6709\u91cd\u5927\u7684\u610f\u4e49\uff0c\u4e24\u4f4d\u5982\u662f\u5c06\u8fd9\u4e8b\u63ed\u7a7f\uff0c\u90a3\u53ea\u6015\u8981\u8bef\u4e86\u5148\u5e08\u7684\u5927\u4e8b\u4e86\u3002\u201d \u3000\u3000\u827e\u4e1c\u6d77\u63a5\u9053\uff1a\u201c\u65b9\u5144\u5f1f\u653e\u5fc3\uff0c\u8001\u673d\u4e0d\u4f1a\u4e71\u8bf4\u7684\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u6211\u8001\u90aa\u4e00\u5411\u5b88\u53e3\u5982\u74f6\uff0c\u8001\u5f1f\u53f0\u66f4\u662f\u7528\u4e0d\u7740\u62c5\u5fc3\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u76ee\u5149\u4e00\u8f6c\uff0c\u62b1\u62f3\u9053\uff1a\u201c\u8c22\u4e24\u4f4d\u6210\u5168\uff01\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u9ad8\u65e0\u654c\u5ffd\u7136\u5927\u7b11\u9053\uff1a\u201c\u65b9\u5144\u5f1f\uff0c\u627f\u4f60\u4e00\u7247\u4fa0\u4e49\u4e4b\u5fc3\uff0c\u89e3\u4e86\u9ad8\u67d0\u5927\u5371\uff0c\u6b64\u523b\u90a3\u67f3\u5a9a\u5a18\u5df2\u7136\u79bb\u53bb\uff0c\u65b9\u5144\u5f1f\u53ef\u5426\u8bf7\u5165\u8305\u820d\u4e4b\u5185\u5c0f\u5750\uff0c\u5bb9\u5144\u5f1f\u7a0d\u5c3d\u5730\u4e3b\u4e4b\u8c0a\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u5fc3\u4e2d\u6697\u9053\uff0c\u8fd9\u9ad8\u65e0\u654c\uff0c\u5c1a\u53ef\u633d\u6551\uff0c\u4f46\u5982\u5f53\u7740\u827e\u4e1c\u6d77\u4e4b\u9762\uff0c\u53ea\u6015\u6709\u8bb8\u591a\u8bdd\u4e5f\u4e0d\u4fbf\u8bf4\u51fa\uff0c\u7559\u5728\u6b64\u95f4\uff0c\u5012\u4e0d\u5982\u79bb\u53bb\u7684\u597d\u4e86\u3002\u5fc3\u5ff5\u4e00\u5b9a\uff0c\u7acb\u5373\u7b11\u9053\uff1a\u201c\u4e0d\u7528\u4e86\uff0c\u9ad8\u5144\u76db\u60c5\uff0c\u5728\u4e0b\u5fc3\u9886\u4fbf\u662f\u3002\u201d \u3000\u3000\u8bdd\u97f3\u7565\u987f\uff0c\u5411\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u665a\u8f88\u6025\u6b32\u5bfb\u627e\u5927\u4f2f\u548c\u5927\u5a18\uff0c\u4e0d\u80fd\u518d\u5728\u5ddd\u4e2d\u9017\u7559\uff0c\u4f60\u8001\u5173\u7167\u4e4b\u60c5\uff0c\u665a\u8f88\u53ea\u597d\u5bb9\u56fe\u540e\u62a5\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u8001\u5f1f\u8bf4\u54ea\u91cc\u8bdd\u6765\uff0c\u8001\u673d\u8fd9\u70b9\u80fd\u8010\uff0c\u53ef\u771f\u662f\u4e00\u70b9\u5fd9\u90fd\u5e2e\u4e0d\u4e0a\u554a\uff01\u201d \u3000\u3000\u8bdd\u97f3\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u8001\u5f1f\u7a76\u7adf\u5e74\u8f7b\uff0c\u4eca\u540e\u884c\u8d70\u6c5f\u6e56\uff0c\u96be\u514d\u8981\u9047\u4e0a\u8bb8\u591a\u5978\u8bc8\u8be1\u795f\u4e4b\u5f92\uff0c\u8001\u673d\u591a\u5c11\u603b\u65e9\u751f\u4e86\u51e0\u5e74\uff0c\u6b66\u529f\u4e0a\u8c08\u4e0d\u5230\u4ec0\u4e48\u7528\uff0c\u4f46\u5bf9\u4ed8\u90a3\u725b\u9b3c\u86c7\u795e\u4e4b\u8f88\u7684\u597d\u8ba1\uff0c\u5374\u662f\u5c0f\u6709\u7ecf\u9a8c\uff0c\u5018\u662f\u8001\u5f1f\u4e0d\u5acc\u8001\u673d\u62d6\u7d2f\uff0c\u5c31\u5bb9\u5f97\u8001\u673d\u4f34\u4f60\u53bb\u81f3\u4e2d\u5dde\uff0c\u7a0d\u5c3d\u7ef5\u8584\u5982\u4f55\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u5f97\u5fc3\u4e2d\u5927\u4e3a\u611f\u6fc0\uff0c\u4f46\u53e3\u4e2d\u5374\u8fde\u8fde\u8c22\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u6562\u5f53\uff0c\u4f60\u8001\u4e0d\u5fc5\u4e3a\u665a\u8f88\u4e4b\u4e8b\u5954\u6ce2\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u8001\u5f1f\u554a\uff01\u770b\u6765\u8001\u673d\u8981\u8bf4\u771f\u8bdd\u624d\u6210\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u6014\u9053\uff1a\u201c\u8001\u524d\u8f88\u83ab\u975e\u522b\u6709\u610f\u56fe\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8001\u673d\u6b63\u662f\u522b\u6709\u6240\u6c42\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8bf7\u8bb2\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8001\u673d\u6b66\u529f\u6709\u9650\uff0c\u4eca\u65e5\u5f97\u7f6a\u4e86\u90a3\u4f4d\u67f3\u59d1\u5a18\uff0c\u8bf4\u4e0d\u5b9a\u5979\u5c31\u5728\u9644\u8fd1\u7b49\u7740\u8001\u673d\uff0c\u6240\u4ee5\u8001\u673d\u8ffd\u968f\u8001\u5f1f\uff0c\u4e0d\u8fc7\u60f3\u6258\u4f60\u4e4b\u5a01\uff0c\u8eb2\u8eb2\u7978\u800c\u5df2\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u7b11\u51fa\u58f0\u6765\u3002 \u3000\u3000\u4ed6\u51b3\u672a\u60f3\u5230\u6765\u5b5a\u4f1a\u5982\u6b64\u6ed1\u7a3d\uff0c\u5f3a\u5fcd\u7740\u7b11\u610f\uff0c\u63a5\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u4f60\u592a\u77a7\u5f97\u8d77\u665a\u8f88\u4e86\uff0c\u8fd9\u7b49\u77e5\u9047\u4e4b\u5fb7\uff0c\u665a\u8f88\u771f\u662f\u611f\u6fc0\u5f97\u5f88\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u8001\u5f1f\u5e94\u5141\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6168\u7136\u9053\uff1a\u201c\u4f60\u8001\u8fd9\u7b49\u81ea\u8d2c\u58f0\u8a89\uff0c\u6298\u8282\u4e0b\u4ea4\u4e4b\u8bf7\uff0c\u665a\u8f88\u5982\u662f\u518d\u4e0d\u7b54\u5e94\uff0c\u5c82\u4e0d\u662f\u8981\u88ab\u4eba\u803b\u7b11\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u8001\u673d\u77e5\u9053\u8001\u5f1f\u4e0d\u4f1a\u62d2\u7edd\u7684\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u6b63\u662f\uff01\u201d\u8f6c\u8eab\u5411\u827e\u4e1c\u6d77\u3001\u9ad8\u65e0\u654c\u4e00\u62b1\u62f3\uff0c\u9053\uff1a\u201c\u540e\u4f1a\u6709\u671f\u3001\u5c31\u6b64\u544a\u8f9e\u4e86\uff01\u201d\u5927\u6b65\u5411\u5c0f\u5f84\u4e4b\u4e0a\u884c\u53bb\u3002 \u3000\u3000\u5b8b\u8001\u90aa\u8ddf\u5728\u4ed6\u8eab\u540e\uff0c\u8fde\u62db\u547c\u4e5f\u4e0d\u5411\u827e\u3001\u9ad8\u4e24\u4eba\u62db\u547c\uff0c\u597d\u4f3c\u5bf9\u4ed6\u4eec\u4f9d\u7136\u6000\u6709\u654c\u89c6\u4e4b\u5fc3\u3002 \u3000\u3000\u827e\u4e1c\u6d77\u5fc3\u8ba1\u6781\u6df1\uff0c\u7779\u72b6\u5374\u662f\u5927\u7b11\u9053\uff1a\u201c\u4e24\u4f4d\u6162\u8d70\uff0c\u6055\u8001\u673d\u4e0d\u9001\u4e86\uff01\u201d \u3000\u3000\u4f46\u9ad8\u65e0\u654c\u5374\u5927\u6b65\u8d76\u4e86\u4e0a\u53bb\uff0c\u8d70\u5728\u65b9\u96ea\u5b9c\u8eab\u7554\uff0c\u7b11\u9053\uff1a\u201c\u65b9\u5144\u5f1f\uff0c\u5bb9\u9ad8\u67d0\u606d\u9001\u4e00\u7a0b\u5427\uff01 \u3000\u3000\u5509\uff01\u4eca\u65e5\u4e00\u522b\uff0c\u4e0d\u77e5\u4f55\u65e5\u518d\u89c1\uff0c\u5144\u5f1f\u53ef\u771f\u662f\u60f3\u8ddf\u4f60\u4e00\u8d77\u79bb\u53bb\u554a\uff01\u201d\u8fd9\u7b49\u58ee\u6c49\uff0c\u5ffd\u7136\u8bf4\u51fa\u8fd9\u7b49\u4f24\u522b\u7684\u8a00\u8bed\uff0c\u770b\u6765\u53eb\u4eba\u683c\u5916\u611f\u53d7\u6b8a\u6df1\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u7f13\u7f13\u63a5\u9053\uff1a\u201c\u9ad8\u5144\uff0c\u9752\u5c71\u4e0d\u6539\uff0c\u7eff\u6c34\u957f\u6d41\uff0c\u53ea\u8981\u5144\u5f1f\u8eab\u5728\u6c5f\u6e56\uff0c\u54b1\u4eec\u5c06\u6765\u89c1\u9762\u7684\u65e5\u5b50\u8fd8\u591a\u7740\u5632\uff01\u53ea\u662f\u5c0f\u5f1f\u5e0c\u671b\u9ad8\u5144\u522b\u540e\u80fd\u5e38\u4fdd\u8d64\u5b50\u4e4b\u5fc3\uff0c\u4ed6\u65e5\u76f8\u805a\uff0c\u5c31\u66f4\u4e3a\u98ce\u5149\u52a8\u4eba\u4e86\u3002\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u4e0d\u8bf4\u51fa\u6765\uff0c\u9ad8\u65e0\u654c\u4e5f\u6709\u4e9b\u660e\u767d\uff0c\u5f53\u4e0b\u70b9\u5934\u9053\uff1a\u201c\u9ad8\u67d0\u4e3a\u4eba\u867d\u662f\u7c97\u9c81\uff0c\u4f46\u81ea\u627f\u5144\u5f1f\u4f60\u4e0d\u5f03\uff0c\u53e6\u773c\u76f8\u5f85\uff0c\u5584\u610f\u597d\u6b79\uff0c\u4e5f\u5206\u522b\u5f97\u51fa\u6765\uff0c\u5144\u5f1f\u8fd9\u756a\u91d1\u7389\u826f\u8a00\uff0c\u9ad8\u67d0\u81ea\u5f53\u6c38\u5b88\u4e0d\u6e1d\uff0c\u65b9\u5144\u5f1f\u653e\u5fc3\u5427\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u559f\u7136\u53f9\u606f\u9053\uff1a\u201c\u6b66\u6797\u4e2d\u4e0d\u4e4f\u50cf\u9ad8\u5144\u8fd9\u7b49\u4eba\u7269\uff0c\u5144\u5f1f\u4eca\u540e\u5012\u8981\u5904\u5904\u7559\u5fc3\uff0c\u591a\u591a\u4e3a\u6b66\u6797\u4fdd\u7559\u4e0b\u4e00\u4e9b\u8c6a\u6770\u4e4b\u58eb\u2026\u2026\u201d \u3000\u3000\u8bdd\u97f3\u4e00\u987f\uff0c\u5ffd\u7136\u7ad9\u5b9a\uff0c\u7b11\u9053\uff1a\u201c\u9ad8\u5144\uff0c\u8bf7\u56de\u53bb\u5427\uff01\u201d \u3000\u3000\u539f\u6765\u4e09\u4eba\u811a\u7a0b\u6781\u5feb\uff0c\u5c31\u53ea\u8fd9\u4e48\u51e0\u53e5\u8bdd\u4e4b\u95f4\uff0c\u5df2\u7136\u8d70\u5230\u65b9\u96ea\u5b9c\u62f4\u9a6c\u4e4b\u5904\u3002 \u3000\u3000\u9ad8\u65e0\u654c\u6709\u4e9b\u9eef\u7136\uff0c\u4f46\u5374\u6545\u4f5c\u8c6a\u58ee\u5730\u5927\u7b11\u9053\uff1a\u201c\u9001\u541b\u5343\u91cc\u7ec8\u987b\u522b\uff0c\u5144\u5f1f\u4fdd\u91cd\u4e86\u3002\u201d\u53cc\u624b\u62b1\u62f3\u4e00\u63d6\uff0c\u6389\u5934\u98de\u5954\u800c\u53bb\u3002 \u3000\u3000\u6562\u60c5\u4ed6\u53cc\u76ee\u6cea\u5149\u5df2\u73b0\uff0c\u5982\u4e0d\u8fc5\u5feb\u56de\u8f6c\uff0c\u5b9a\u8981\u843d\u4e0b\u6cea\u6765\uff0c\u8bd5\u60f3\u4f3c\u4ed6\u8fd9\u7b49\u94c1\u6c49\uff0c\u600e\u613f\u8ba9\u4eba\u77a7\u89c1\u6d41\u6cea\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u6014\u6014\u5730\u77a7\u7740\u9ad8\u65e0\u654c\u9ad8\u5927\u7684\u8eab\u5f71\u5728\u8349\u4e1b\u4e2d\u6d88\u5931\uff0c\u957f\u957f\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u4f4e\u58f0\u9053\uff1a \u3000\u3000\u201c\u597d\u4e00\u4e2a\u58ee\u58eb\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b64\u4eba\u679c\u7136\u662f\u4e00\u6761\u597d\u6c49\uff0c\u826f\u77e5\u672a\u6cef\uff0c\u662f\u975e\u4e4b\u5ff5\u5c1a\u5728\uff0c\u8001\u5f1f\u4ed7\u4e49\u52a9\u4ed6\u8131\u5371\u4e4b\u5fb7\uff0c\u867d\u7136\u662f\u4e00\u7740\u95f2\u68cb\uff0c\u4eca\u540e\u5fc5\u7136\u6709\u90a3\u5f00\u82b1\u7ed3\u679c\u4e4b\u65e5\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u665a\u8f88\u5e76\u65e0\u5229\u7528\u9ad8\u65e0\u654c\u4e4b\u5fc3\uff0c\u4ed6\u65e5\u631f\u6069\u56fe\u62a5\uff0c\u5e76\u975e\u665a\u8f88\u672c\u610f\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u8001\u5f1f\u8bef\u4f1a\u4e86\uff0c\u8001\u673d\u600e\u4f1a\u8bf4\u4f60\u5bf9\u4ed6\u65bd\u6069\u4e86\uff0c\u53ea\u662f\uff0c\u4e00\u65e6\u4f60\u8001\u5f1f\u4e0e\u90a3\u7ae5\u5b50\u5947\u6709\u4e86\u6b63\u9762\u51b2\u7a81\u4e4b\u65f6\uff0c\u8001\u673d\u76f8\u4fe1\uff0c\u9ad8\u67d0\u4eba\u5fc5\u53ef\u5bf9\u4f60\u5927\u6709\u52a9\u76ca\uff01\u201d\u8bdd\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a \u3000\u3000\u201c\u4f55\u51b5\uff0c\u6b63\u9053\u6b66\u6797\u4e4b\u58eb\uff0c\u591a\u662f\u4fdd\u8eab\u81ea\u9690\uff0c\u65e0\u5f62\u4e4b\u4e2d\uff0c\u5927\u5927\u524a\u5f31\u4e86\u6b66\u6797\u6b63\u4e49\uff0c\u8001\u5f1f\u82e5\u80fd\u591a\u5728\u9ed1\u9053\u6851\u96c4\u8eab\u4e0a\u6316\u6765\u51e0\u4f4d\u5fc3\u8179\u4e4b\u58eb\uff0c\u6b64\u957f\u5f7c\u6d88\uff0c\u4e0d\u4e5f\u7b49\u4e8e\u66ff\u6211\u6b63\u9053\u9635\u8425\u4e4b\u4e2d\uff0c\u591a\u52a0\u4e86\u4e00\u4efd\u529b\u91cf\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u665a\u8f88\u62c5\u5fc3\u7684\u662f\uff0c\u4eba\u5fae\u8a00\u8f7b\uff0c\u96be\u4ee5\u6709\u591a\u5927\u7684\u6210\u5c31\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4e0d\u662f\u8001\u673d\u6367\u4f60\uff0c\u9664\u975e\u4f60\u8001\u5f1f\u65e0\u5fc3\u5e72\u6b64\uff0c\u5426\u5219\uff0c\u4ee5\u4f60\u8001\u5f1f\u7684\u8fd9\u7b49\u5fc3\u80f8\u6210\u4e8b\u5f53\u662f\u6613\u5982\u53cd\u638c\u4e00\u822c\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8001\u524d\u8f88\u8fc7\u5956\u4e86\u3002\u201d \u3000\u3000\u8bdd\u97f3\u4e00\u987f\uff0c\u7adf\u7136\u6c89\u541f\u63a5\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u665a\u8f88\u6709\u5fc3\u524d\u53bb\u4e2d\u539f\u5bfb\u627e\u5728\u4e0b\u7684\u90d1\u5927\u4f2f\u7236\uff0c\u4e0d\u77e5\u5e94\u8be5\u600e\u4e48\u8d70\u6cd5\u624d\u5bf9\uff1f\u201d \u3000\u3000\u6562\u60c5\u4ed6\u521d\u6b21\u51fa\u5916\uff0c\u5bf9\u4e8e\u5404\u5904\u9053\u8def\uff0c\u90fd\u8fd8\u4e0d\u719f\u3002 \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8001\u5f1f\u662f\u8981\u5bfb\u627e\u4e2d\u5dde\u4e09\u4fa0\u4e2d\u7684\u90d1\u5927\u521a\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u6b63\u662f\u90d1\u5927\u4f2f\u7236\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u5982\u6b64\u54b1\u4eec\u5e94\u53d6\u9053\u8944\u6a0a\uff0c\u51fa\u6c49\u4e2d\uff0c\u8f6c\u8d8b\u4e2d\u5dde\u4e86\u3002\u201d \u3000\u3000\u7b11\u9053\uff1a\u201c\u665a\u8f88\u8fd9\u8fd8\u662f\u5934\u4e00\u6b21\u72ec\u81ea\u5728\u5916\u884c\u8d70\uff0c\u4e00\u5207\u90fd\u8981\u4f60\u8001\u8d39\u5fc3\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u54b1\u4eec\u8fd9\u5c31\u56de\u5934\u5411\u4e1c\uff0c\u8fc7\u4e86\u4e07\u53bf\uff0c\u54b1\u4eec\u4e70\u821f\u987a\u6c5f\u800c\u4e0b\uff0c\u987a\u9053\u4e5f\u8ba9\u4f60\u770b\u770b\u4e09\u5ce1\u98ce\u5149\u5982\u4f55\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u665a\u8f88\u4e00\u5207\u542c\u547d\u2026\u2026\u201d\u4e24\u4eba\u51fa\u4e86\u90a3\u4e00\u5e26\u5c71\u533a\uff0c\u4fbf\u987a\u7740\u9a7f\u9053\uff0c\u76f4\u5f03\u4e07\u53bf\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u548c\u5b8b\u5b5a\u4e24\u4eba\uff0c\u56e0\u65e0\u6025\u4e8b\uff0c\u56e0\u6b64\uff0c\u7b2c\u516d\u5929\u6b63\u5348\uff0c\u65b9\u59cb\u62b5\u8fbe\u4e07\u53bf\u7801\u5934\u3002 \u3000\u3000\u5b8b\u5b5a\u672a\u5728\u8fd9\u4e00\u5e26\uff0c\u4f3c\u662f\u4e5f\u751a\u719f\u6089\uff0c\u672a\u8d39\u4e1d\u6beb\u5468\u7ae0\uff0c\u5c31\u96c7\u5230\u4e86\u4e00\u53ea\u72ec\u6845\u6c5f\u8239\uff0c\u672a\u65f6\u752b\u8fc7\uff0c\u4e24\u4eba\u4fbf\u5df2\u987a\u6c5f\u800c\u4e0b\u3002 \u3000\u3000\u4e8c\u66f4\u65f6\u5206\uff0c\u8239\u8fc7\u767d\u5e1d\u57ce\uff0c\u5373\u5165\u77bf\u5858\u5ce1\uff0c\u7167\u8239\u5bb6\u7684\u89c4\u77e9\uff0c\u7edd\u5c11\u591c\u95f4\u5728\u5ce1\u4e2d\u884c\u821f\uff0c\u6240\u4ee5\uff0c\u5f53\u665a\u4fbf\u8981\u5728\u767d\u5e1d\u57ce\u6cca\u8239\uff0c\u4f46\u5b8b\u5b5a\u5374\u529b\u8a00\u77bf\u5858\u6c34\u8def\u751a\u77ed\uff0c\u4e0d\u59a8\u8d76\u5230\u5deb\u5c71\u57ce\u4e0b\uff0c\u518d\u884c\u9760\u5cb8\uff0c\u8239\u5bb6\u65e0\u5948\uff0c\u53ea\u597d\u5192\u9669\u64cd\u821f\uff0c\u6cbf\u7740\u77f3\u58c1\u60ac\u5ca9\u4e0b\u9a76\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u51e0\u66fe\u89c1\u8fc7\u8fd9\u7b49\u58ee\u4e3d\u7684\u5c71\u5149\u6c34\u8272\uff0c\u6253\u6728\u8239\u5165\u4e86\u5ce1\u53e3\uff0c\u4ed6\u5c31\u7ad9\u5728\u8239\u5934\uff0c\u4ef0\u671b\u4e24\u8fb9\u5c71\u77f3\uff0c\u548c\u90a3\u65f6\u800c\u5f53\u5934\uff0c\u65f6\u800c\u88ab\u5c71\u5ce6\u906e\u65ad\u7684\u4e00\u5f2f\u65b0\u6708\uff0c\u53e3\u4e2d\u4e0d\u4f4f\u8d5e\u53f9\u9020\u7269\u795e\u5947\uff0c\u5b9e\u975e\u4eba\u4e4b\u624d\u667a\u6240\u80fd\u60f3\u8c61\u3002 \u3000\u3000\u5b8b\u5b5a\u4f3c\u662f\u5bf9\u4e09\u5ce1\u666f\u8272\u89c1\u8fc7\u751a\u591a\uff0c\u8eab\u867d\u505c\u7acb\u822c\u9996\uff0c\u4f46\u5374\u5168\u795e\u8d2f\u6ce8\u5730\u5728\u77a7\u7740\u6c5f\u9762\uff0c\u5e2e\u7740\u90a3\u8239\u5bb6\uff0c\u7559\u795e\u7a81\u51fa\u6c5f\u8fb9\u7684\u5ca9\u77f3\uff0c\u83ab\u8ba9\u8239\u8eab\u649e\u4e0a\u53bb\u3002 \u3000\u3000\u4e09\u66f4\u752b\u8fc7\uff0c\u5341\u591a\u91cc\u7684\u77bf\u5858\u5ce1\u5df2\u7136\u6e21\u8fc7\uff0c\u8239\u5bb6\u4e00\u9762\u6325\u6c57\uff0c\u4e00\u9762\u5c06\u7f06\u7ef3\u7cfb\u5411\u5deb\u5c71\u57ce\u7684\u5cb8\u8fb9\u7801\u5934\u4e4b\u4e0a\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u957f\u957f\u5730\u53f9\u4e86\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u9020\u7269\u795e\u5947\uff0c\u771f\u4e0d\u6127\u9b3c\u65a7\u795e\u5de5\u4e4b\u8a89\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u957f\u6c5f\u4e09\u5ce1\uff0c\u6700\u5947\u6700\u9669\u4e4b\u5904\uff0c\u8fd8\u8981\u5230\u5175\u4e66\u5b9d\u5251\u5ce1\u4e2d\u7684\u9752\u6ee9\uff0c\u660e\u65e5\u6b63\u5348\uff0c\u8001\u5f1f\u5c31\u53ef\u89c1\u5230\u6c34\u52bf\u4e4b\u6025\uff0c\u6c5f\u6d9b\u4e4b\u9669\uff0c\u821f\u884c\u5176\u4e2d\uff0c\u5b9b\u5982\u98d8\u53f6\u4e00\u822c\uff0c\u77ac\u773c\u4e4b\u95f4\uff0c\u76f4\u6cfb\u5343\u91cc\uff0c\u8001\u673d\u5f80\u8fd4\u5ddd\u4e2d\uff0c\u4e0d\u4e0b\u6570\u5341\u6b21\u4e4b\u591a\uff0c\u6bcf\u5230\u65af\u5904\uff0c\u4ecd\u7136\u60ca\u5fc3\u52a8\u9b44\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u660e\u65e5\u665a\u8f88\u5012\u8981\u4ed4\u7ec6\u5730\u77a7\u4e0a\u4e00\u77a7\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u5148\u517b\u517b\u7cbe\u795e\uff0c\u660e\u65e5\u4e5f\u597d\u4e00\u9971\u773c\u798f\u3002\u201d \u3000\u3000\u4e24\u4eba\u8bf4\u7b11\u5165\u8231\uff0c\u7565\u4f5c\u8c03\u606f\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u5c11\u65f6\u95f4\uff0c\u65b9\u96ea\u5b9c\u5ffd\u7136\u4e00\u60ca\u800c\u9192\u3002"}),showOnHomepage:!0},{name:"\u7b2c\u5341\u4e94\u56de\u3000\u795e\u79d8\u7ea6\u4f1a",content:(0,t.jsx)(t.Fragment,{children:"\u8231\u5185\u4e00\u7247\u9eef\u7136\uff0c\u62ac\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u5b8b\u5b5a\u4ecd\u7136\u95ed\u76ee\u6253\u5750\u672a\u9192\uff0c\u56db\u91ce\u9664\u4e86\u6d6a\u6d9b\u62cd\u5cb8\u733f\u557c\u72fc\u55e5\u4e4b\u58f0\uff0c\u522b\u65e0\u4e00\u4e1d\u52a8\u9759\u3002 \u3000\u3000\u4f46\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u5374\u6697\u611f\u4e0d\u5bf9\uff0c\u9002\u624d\u4ed6\u4e00\u60ca\u800c\u9192\uff0c\u4e43\u662f\u89c9\u7740\u8239\u8eab\u5ffd\u7136\u4e00\u9707\uff0c\u6b64\u523b\u5374\u662f\u4e0d\u89c1\u4eba\u58f0\uff0c\u5c82\u975e\u5341\u5206\u5947\u602a\u5417\uff1f \u3000\u3000\u65b9\u96ea\u5b9c\u7565\u4e00\u6c89\u541f\uff0c\u8f7b\u8f7b\u7684\u8d70\u51fa\u8231\u5916\uff0c\u8239\u5934\u4e0a\u7684\u706f\u5149\u5df2\u7184\uff0c\u53ea\u5269\u4e0b\u90a3\u6845\u6746\u7684\u534a\u8170\u4e4b\u65c1\uff0c\u4eae\u7740\u4e00\u4e9b\u6c14\u6b7b\u98ce\u706f\uff0c\u5728\u6c5f\u9762\u817e\u8d77\u7684\u96fe\u8272\u4e2d\uff0c\u6643\u6765\u6643\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e0d\u4fe1\u81ea\u5df1\u4f1a\u542c\u9519\uff0c\u662f\u4ee5\u4ed6\u8fc5\u5feb\u7684\u7ed5\u8fc7\u5168\u8239\u4e4b\u540e\uff0c\u7acb\u5373\u7eb5\u8eab\u8dc3\u4e0a\u90a3\u6845\u9876\uff0c\u6b32\u5f85\u4e3e\u76ee\u5411\u56db\u5468\u5bdf\u770b\u3002\u9661\u7136\u95f4\u5fc3\u4e2d\u4e00\u9707\uff0c\u53ea\u89c1\u90a3\u6845\u9876\u4e4b\u4e0a\uff0c\u7528\u4e00\u6839\u94f6\u7c2a\uff0c\u63d2\u4e86\u4e00\u65b9\u767d\u7ee2\uff0c\u6b63\u5728\u591c\u98ce\u4e2d\uff0c\u8361\u6765\u8361\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u63a2\u624b\u62d4\u4e0b\u94f6\u7c2a\uff0c\u53ea\u89c9\u4e00\u80a1\u6e05\u96c5\u7684\u9999\u6c14\uff0c\u9009\u9f3b\u800c\u5165\uff0c\u4e00\u60ca\u4e4b\u4e0b\uff0c\u8fde\u5fd9\u95ed\u4f4f\u547c\u5438\uff0c\u6697\u4e2d\u8fd0\u6c14\u9ed8\u5bdf\uff0c\u4f53\u5185\u6709\u65e0\u5f02\u72b6\uff0c\u53cc\u76ee\u5374\u6ce8\u89c6\u767d\u7ee2\u4e4b\u4e0a\u7684\u5b57\u8ff9\u3002\u53ea\u89c1\u90a3\u5c0f\u7ea6\u6709\u5c3a\u8bb8\u89c1\u65b9\u7684\u767d\u7ee2\u4e4b\u4e0a\uff0c\u53ea\u5199\u4e86\u5be5\u5be5\u51e0\u4e2a\u5c0f\u5b57\uff1a\u201c\u6211\u5bb6\u7ad9\u5a18\u8981\u4f60\u6765\u671d\u4e91\u5cf0\uff0c\u4f60\u6562\u4e0d\u6562\u6765\uff1f\u201d \u3000\u3000\u65e2\u65e0\u4e0a\u6b3e\uff0c\u4ea6\u65e0\u7f72\u540d\uff0c\u663e\u7136\u8fd9\u662f\u51b2\u7740\u81ea\u5df1\u6216\u662f\u5b8b\u5b5a\u9001\u6765\u7684\u4e86\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u5df2\u786e\u5b9a\u90a3\u9635\u9999\u5473\uff0c\u53ea\u662f\u5973\u5b69\u513f\u5bb6\u7528\u7684\u9999\u7c89\u6c14\u5473\uff0c\u5e76\u975e\u4ec0\u4e48\u6bd2\u7269\uff0c\u5f53\u4e0b\u95ea\u8eab\u8dc3\u843d\u8239\u68a2\uff0c\u76f4\u8fdb\u623f\uff0c\u5c31\u7740\u706f\u5149\uff0c\u91cd\u65b0\u6253\u91cf\u90a3\u5c0f\u5c0f\u94f6\u7c2a\u3002\u8fd9\u4e00\u51dd\u89c6\uff0c\u7a81\u7136\u4f7f\u5f97\u65b9\u96ea\u5b9c\u5927\u5927\u7684\u5403\u4e86\u4e00\u60ca\u3002 \u3000\u3000\u6562\u60c5\uff0c\u8fd9\u53ea\u94f6\u7c2a\uff0c\u867d\u7136\u957f\u4ec5\u4e09\u5bf8\uff0c\u4f46\u90a3\u7c2a\u5934\u4e4b\u4e0a\uff0c\u5374\u96d5\u94f8\u4e86\u4e00\u53ea\u6829\u6829\u5982\u751f\u7684\u4ed9\u9e64\uff0c\u4f5c\u632f\u7ffc\u9ad8\u98de\u4e4b\u72b6\u3002 \u3000\u3000\u4ed6\u8bb0\u5f97\u5e08\u53d4\u66fe\u7ecf\u4ea4\u5f85\u8fc7\u81ea\u5df1\uff0c\u9f99\u884c\u5251\u6cd5\u5982\u6709\u514b\u5236\u7684\u5bf9\u624b\uff0c\u90a3\u5c31\u662f\u201c\u5929\u9e64\u65a9\u201d\u7684\u5f80\u4e8b\uff0c\u662f\u6545\u4ed6\u4e00\u773c\u770b\u5230\u9e64\u5f62\u6807\u5fd7\uff0c\u5c31\u4e0d\u7981\u5fc3\u5934\u5927\u9707\u3002 \u3000\u3000\u6b63\u5728\u4ed6\u624b\u6301\u94f6\u7c2a\u53d1\u6123\u4e4b\u95f4\uff0c\u8033\u4e2d\u5ffd\u7136\u542c\u5f97\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u8fd9\u94f6\u7c2a\u662f\u4f55\u4eba\u4e4b\u7269\uff1f \u3000\u3000\u8001\u5f1f\u4e3a\u4f55\u624b\u6267\u6b64\u7269\u53d1\u5446\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u970d\u5730\u8f6c\u8eab\uff0c\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u4f60\u4e5f\u77a7\u77a7\u2026\u2026\u201d\u8bf4\u7740\uff0c\u5c06\u94f6\u7c2a\u9012\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5b8b\u5b5a\u63a5\u8fc7\u8fd9\u652f\u94f6\u7c2a\uff0c\u4ed4\u7ec6\u5730\u770b\u4e86\u4e00\u904d\uff0c\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u8fd9\u662f\u5728\u54ea\u513f\u53d1\u73b0\u7684\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e0d\u7b54\u5b8b\u5b5a\u6240\u95ee\uff0c\u5374\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u8fd9\u94f6\u7c2a\u4f60\u8001\u4ee5\u5f80\u89c1\u8fc7\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u539f\u4ee5\u4e3a\u5b8b\u5b5a\u6c5f\u6e56\u7ecf\u9a8c\u6781\u4e30\uff0c\u81ea\u7136\u89c1\u95fb\u751a\u535a\uff0c\u5bf9\u4e8e\u8fd9\u652f\u96d5\u6709\u4ed9\u9e64\u7684\u94f6\u7c2a\uff0c\u53ef\u80fd\u4f1a\u77e5\u6653\u4e00\u4e9b\u6765\u5386\uff0c\u6b64\u523b\u95fb\u4ed6\u56de\u7b54\u4e0d\u77e5\uff0c\u4e0d\u7981\u5927\u5931\u6240\u671b\u3002 \u3000\u3000\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u8fd9\u7acb\u94f6\u7c2a\uff0c\u4e43\u662f\u81ea\u8239\u6845\u4e4b\u4e0a\u53d6\u6765\uff0c\u800c\u4e14\uff0c\u8fd8\u6709\u4e00\u65b9\u767d\u7ee2\u2026\u2026\u201d\uff0c\u9012\u8fc7\u767d\u7ee2\uff0c\u63a5\u9053\uff1a\u201c\u8fd9\u4e8b\u4f60\u8001\u6709\u4f5d\u9ad8\u89c1\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u63a5\u8fc7\u767d\u7ee2\uff0c\u770b\u8fc7\u4e0a\u9762\u6240\u5199\u7684\u5a1f\u79c0\u5b57\u8ff9\uff0c\u4e0d\u89c9\u5730\u9501\u8d77\u53cc\u7709\uff0c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u8fd9\u4f4d\u59d1\u5a18\uff0c\u53ef\u662f\u51b2\u7740\u4f60\u6765\u7684\uff1f\u201d \u3000\u3000\u6562\u60c5\u8fd9\u4f4d\u5927\u6f20\u9b3c\u624b\u4e5f\u5760\u5165\u4e86\u4e94\u91cc\u96fe\u4e2d\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u957f\u957f\u5730\u4e00\u53f9\u9053\uff1a\u201c\u4e0d\u77e5\u9053\uff0c\u665a\u8f88\u539f\u4ee5\u4e3a\u8fd9\u4e8b\u53ef\u80fd\u662f\u9488\u5bf9\u4f60\u8001\u800c\u6765\uff0c\u773c\u4e0b\u770b\u6765\uff0c\u90a3\u662f\u665a\u8f88\u6599\u9519\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u8001\u673d\u4e00\u751f\uff0c\u6700\u662f\u4e0d\u559c\u4e0e\u5973\u4eba\u4ea4\u5f80\uff0c\u8fd9\u7559\u7ee2\u4e66\u7684\u5bf9\u8c61\uff0c\u516b\u6210\u662f\u6211\u7684\u4f60\u8001\u5f1f\u53f0\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u665a\u8f88\u521d\u79bb\u5e08\u95e8\uff0c\u5728\u6b66\u6797\u4e2d\u4e43\u662f\u85c9\u85c9\u4e4b\u8f88\u554a\u2026\u2026\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u662f\u81ea\u5df1\u4e0d\u53ef\u80fd\u4e0e\u4ec0\u4e48\u4eba\u7ed3\u4e0b\u6069\u6028\u7684\u4e86\u3002 \u3000\u3000\u5b8b\u5b5a\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f1a\u4e0d\u4f1a\u662f\u90a3\u67f3\u5a9a\u5a18\uff0c\u5979\u4e0d\u4e5f\u662f\u5973\u4eba\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6c89\u541f\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u4e0d\u50cf\u5427\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u4e00\u6014\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u56fa\u662f\u5fc3\u4e2d\u6000\u7591\u8fd9\u94f6\u7c2a\u4e4b\u4e0a\u7684\u4ed9\u9e64\u96d5\u82b1\uff0c\u53ef\u80fd\u6b63\u662f\u5e08\u7236\u6240\u8bf4\u7684\u90a3\u4e00\u53cc\u4e1c\u6d77\u5947\u4eba\u7684\u6807\u5fd7\uff0c\u65b9\u624d\u8ba4\u4e3a\u6b64\u4e8b\u4e0d\u662f\u67f3\u5a9a\u5a18\u6240\u4e3a\uff0c\u542c\u5f97\u5b8b\u5b5a\u53cd\u95ee\uff0c\u53ea\u597d\u5e94\u58f0\u9053\uff1a\u201c\u90a3\u67f3\u5a9a\u5a18\u5982\u662f\u8981\u627e\u665a\u8f88\uff0c\u8c05\u4e5f\u4e0d\u4f1a\u82b1\u8d39\u8fd9\u4e9b\u624b\u811a\u2026\u2026\u201d \u3000\u3000\u8bed\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u8fd9\u94f6\u7c2a\u4e0a\u7684\u4ed9\u9e64\u6807\u5fd7\uff0c\u4f60\u8001\u5f80\u65e5\u53ef\u66fe\u542c\u8bf4\uff0c\u6b66\u6797\u4e4b\u4e2d\uff0c\u662f\u5426\u6709\u4eba\u4f7f\u7528\u8fc7\u8fd9\u7b49\u94f6\u7c2a\u4f20\u8baf\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u6293\u5934\u6c89\u601d\u826f\u4e45\uff0c\u7a81\u7136\u4e00\u53f9\u9053\uff1a\u201c\u8001\u673d\u5b9e\u662f\u60f3\u4e0d\u8d77\u6b66\u6797\u4e4b\u4e2d\uff0c\u6709\u4ec0\u4e48\u4eba\u7528\u8fc7\u8fd9\u7b49\u6807\u5fd7\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5251\u7709\u8fde\u8fde\u626c\u52a8\uff0c\u663e\u7136\u662f\u5fc3\u60c5\u751a\u4e3a\u6fc0\u52a8\uff0c\u8131\u53e3\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u54b1\u4eec\u8be5\u4e0d\u8be5\u53bb\u90a3\u671d\u4e91\u5cf0\u4e00\u884c\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u91cd\u65b0\u628a\u90a3\u65b9\u767d\u7ee2\u770b\u4e86\u4e00\u773c\uff0c\u5931\u7b11\u9053\uff1a\u201c\u662f\u4e86\uff0c\u54b1\u4eec\u5982\u8981\u67e5\u51fa\u6b64\u4e8b\u5e95\u7ec6\uff0c\u90a3\u53ea\u6709\u5e94\u7ea6\u524d\u53bb\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u90a3\u671d\u4e91\u5cf0\u4e0d\u77e5\u79bb\u6b64\u591a\u8fdc\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e0d\u592a\u8fdc\u53ea\u662f\u8fd9\u8239\u5bb6\u2026\u2026\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u60f3\u5230\u4e24\u4eba\u8fd9\u4e00\u79bb\u53bb\uff0c\u4e94\u66f4\u65f6\u5206\u662f\u5426\u53ef\u4ee5\u8d76\u5f97\u56de\u6765\uff0c\u5018\u82e5\u8239\u5bb6\u9192\u6765\u4e0d\u89c1\u4e86\u5ba2\u4eba\uff0c\u4e0a\u5cb8\u60ca\u52a8\u4e86\u5b98\u5e9c\uff0c\u53cd\u800c\u4e0d\u597d\u3002 \u3000\u3000\u5f53\u4e0b\u8bdd\u97f3\u4e00\u987f\uff0c\u6c89\u541f\u6709\u9877\u63a5\u9053\uff1a\u201c\u8fd9\u4e48\u529e\uff0c\u8001\u673d\u524d\u53bb\u544a\u8bc9\u8239\u5bb6\uff0c\u54b1\u4eec\u8d81\u591c\u8bbf\u53cb\uff0c\u5728\u672a\u66fe\u56de\u6765\u4e4b\u524d\uff0c\u4e0d\u8981\u5f00\u8239\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u53ea\u597d\u5982\u6b64\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u8f6c\u8eab\u51fa\u4e86\u8231\u95e8\uff0c\u53eb\u9192\u4e86\u8239\u8001\u5927\uff0c\u4e00\u5207\u4ea4\u5f85\u6e05\u695a\uff0c\u8fd9\u624d\u548c\u65b9\u96ea\u5b9c\u53cc\u53cc\u4e0a\u5f97\u5cb8\u6765\uff0c\u76f4\u5954\u5deb\u5c71\u4e4b\u5cb8\u800c\u53bb\u3002 \u3000\u3000\u90a3\u5b8b\u4e4e\u5bf9\u5deb\u5c71\u7684\u5341\u4e8c\u540d\u5cf0\uff0c\u7684\u786e\u5341\u5206\u719f\u6089\uff0c\u4e00\u8def\u4e4b\u4e0a\uff0c\u4e0d\u505c\u7684\u6307\u70b9\u65b9\u96ea\u5b9c\uff0c\u4f55\u5904\u662f\u671b\u971e\u5cf0\uff0c\u4f55\u5904\u662f\u98de\u51e4\u5cf0\uff0c\u4f55\u5904\u662f\u7fe0\u5c4f\u5cf0\uff0c\u4f55\u5904\u662f\u805a\u9e64\u5cf0\uff0c\u6562\u60c5\u90a3\u5341\u4e8c\u5cf0\u6240\u5728\uff0c\u5728\u5b8b\u5b5a\u53e3\u4e2d\uff0c\u76f4\u662f\u52a0\u6570\u5bb6\u73cd\u4e00\u822c\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4e24\u4eba\u6cbf\u7740\u5927\u6c5f\u5317\u5cf0\uff0c\u5f80\u4e1c\u800c\u884c\uff0c\u7ea6\u83ab\u5728\u5947\u5cf0\u5cfb\u5dc5\u4e4b\u95f4\uff0c\u884c\u8d70\u4e86\u5f97\u4e00\u4e2a\u66f4\u6b21\uff0c\u5ffd\u7136\u6765\u5230\u4e00\u5ea7\u9ad8\u8038\u5165\u4e91\u7684\u5de8\u5927\u7684\u5927\u5c71\u5cf0\u4e4b\u524d\u3002 \u3000\u3000\u5b8b\u5b5a\u5373\u671b\u5cf0\u5934\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u8fd9\u5c31\u662f\u671d\u4e91\u5cf0\u4e86\uff0c\u4e5f\u6b63\u662f\u6b64\u95f4\u767e\u59d3\uff0c\u4f20\u79f0\u7684\u795e\u5973\u5cf0\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u62ac\u5934\u671b\u53bb\u53ea\u89c1\u6b64\u5cf0\u8fdb\u51fa\u4e91\u8868\uff0c\u679c\u7136\u6bd4\u5176\u4ed6\u8fdc\u8fd1\u7684\u5c71\u5ce6\uff0c\u9ad8\u51fa\u4e0d\u5c11\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a \u3000\u3000\u8fd9\u7b49\u633a\u62d4\u7684\u5c71\u52bf\uff0c\u4e0d\u77e5\u5cf0\u9876\u662f\u5426\u6709\u5bb9\u4eba\u7acb\u8db3\u4e4b\u5904\uff1f\u5fc3\u4e2d\u867d\u4f5c\u5982\u662f\u4e4b\u60f3\uff0c\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u54b1\u4eec\u6253\u90a3\u513f\u4e0a\u53bb\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u95fb\u8a00\uff0c\u84e6\u7136\u5931\u7b11\u9053\uff1a\u201c\u662f\u554a\uff01\u8001\u673d\u73b0\u5c31\u9886\u8def\u4e86\u3002\u201d \u3000\u3000\u7a81\u7136\u4e3e\u6b65\u6447\u80a9\uff0c\u76f4\u5411\u5c71\u4e0a\u6500\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u7d27\u7d27\u773c\u5728\u4ed6\u8eab\u540e\uff0c\u4e0d\u6d88\u591a\u4e45\uff0c\u4fbf\u5df2\u5230\u8fbe\u5cf0\u9876\u3002 \u3000\u3000\u679c\u7136\uff0c\u8fd9\u671d\u4e91\u5cf0\u9876\uff0c\u4e43\u662f\u4e00\u5ea7\u5e73\u53f0\u3002\u56db\u5468\u5de8\u77f3\u6797\u7acb\uff0c\u5f62\u52bf\u5341\u5947\u5140\uff0c\u4e5f\u76f8\u5f53\u53e4\u62d9\u3002 \u3000\u3000\u4e24\u4eba\u8eab\u884c\u521a\u81ea\u7ad9\u5b9a\uff0c\u53ea\u89c9\u773c\u524d\u4eba\u5f71\u4e00\u82b1\uff0c\u4e00\u4e2a\u8eab\u8eaf\u7ea4\u7ec6\u5934\u633d\u53cc\u9afb\u7684\u5c11\u5973\uff0c\u4e0d\u77e5\u4ece\u4f55\u5904\u5192\u4e86\u51fa\u6765\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u6014\u4e86\u4e00\u6014\uff0c\u90a3\u5c11\u5973\u4e1a\u5df2\u683c\u683c\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u53ef\u662f\u59d3\u65b9\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5403\u4e86\u4e00\u60ca\uff0c\u6697\u9053\uff1a\u201c\u5979\u600e\u4f1a\u77e5\u9053\u6211\u59d3\u65b9\uff1f\u201d\u4f46\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5728\u4e0b\u6b63\u662f\u59d3\u65b9\uff0c\u53ef\u662f\u59d1\u5a18\u7559\u7ee2\u7ea6\u6211\u6765\u6b64\u7684\u5417\uff1f\u201d \u3000\u3000\u5c11\u5973\u5ffd\u7136\u7b11\u5bb9\u4e00\u655b\uff0c\u76ee\u5149\u5c04\u5411\u4ed6\u8eab\u540e\u7684\u5b8b\u5b5a\u9053\uff1a\u201c\u4ed6\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5728\u4e0b\u7684\u4e00\u4f4d\u957f\u8f88\u3002\u201d \u3000\u3000\u5c11\u5973\u4f3c\u662f\u5446\u4e86\u4e00\u5446\uff0c\u65b9\u9053\uff1a\u201c\u4f60\u2026\u2026\u4e3a\u4f55\u8ddf\u4ed6\u4e00\u9053\u524d\u6765\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fd6\u9053\uff1a\u201c\u4f60\u90a3\u7559\u5b57\u4e4b\u4e0a\uff0c\u5e76\u672a\u8bf4\u51fa\u53ea\u8981\u6211\u4e00\u4e2a\u4eba\u524d\u6765\u554a\uff01\u4e3a\u4ec0\u4e48\u522b\u4eba\u5c31\u4e0d\u80fd\u6765\u5462\uff0c\u201d\u4f46\u4ed6\u4e3a\u4eba\u5fe0\u539a\uff0c\u8fd9\u7b49\u8bdd\u81ea\u4e0d\u4f1a\u8bf4\u51fa\u53e3\u6765\uff0c\u53ea\u6de1\u6de1\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u59d1\u5a18\u5728\u90a3\u7559\u4e0b\u7684\u7ee2\u4e2d\u4e4b\u4e0a\uff0c\u5e76\u672a\u6307\u660e\u7ea6\u5b9a\u4f55\u4eba\uff0c\u5728\u4e0b\u4e0e\u5b8b\u8001\u540c\u6765\uff0c\u4e5f\u6ca1\u6709\u9519\u554a\uff01\u201d \u3000\u3000\u5c11\u5973\u5ffd\u7136\u53c8\u683c\u683c\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u771f\u53ef\u7b11\uff0c\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u59d1\u5a18\u8ddf\u5728\u4e0b\u7d20\u4e0d\u76f8\u8bc6\uff0c\u4e0d\u77e5\u600e\u4f1a\u77e5\u6653\u5728\u4e0b\u59d3\u65b9\uff1f\u201d \u3000\u3000\u8bdd\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u59d1\u5a18\u600e\u4e48\u79f0\u547c\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u5927\u773c\u7728\u4e86\u4e00\u7728\uff0c\u9053\uff1a\u201c\u6211\u53eb\u5e73\u513f\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u539f\u6765\u662f\u5e73\u513f\u59d1\u5a18\uff01\u2026\u2026\u201d \u3000\u3000\u4ed6\u53e3\u4e2d\u867d\u7136\u8fd9\u4e48\u8bf4\uff0c\u4f46\u5fc3\u4e2d\u5374\u5728\u6697\u9053\uff1a\u542c\u5979\u8fd9\u7b49\u53ea\u6709\u540d\u5b57\uff0c\u4e0d\u62a5\u59d3\u6c0f\uff0c\u663e\u7136\u53ea\u662f\u4e00\u4e2a\u4e2b\u5934\u8eab\u4efd\uff0c\u96be\u9053\u90a3\u7559\u5b57\u4e4b\u4eba\uff0c\u4e0d\u662f\u5979\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u8fd9\u65f6\u5ffd\u7136\u5e72\u54b3\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u5e73\u513f\u59d1\u5a18\uff01\u201d \u3000\u3000\u90a3\u5e73\u513f\u7b11\u9053\uff1a\u201c\u8001\u4eba\u5bb6\uff0c\u4f60\u6709\u4ec0\u4e48\u4e8b\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u542c\u59d1\u5a18\u53e3\u6c14\uff0c\u90a3\u7ea6\u8bf7\u6211\u7b49\u6765\u6b64\u4e4b\u4eba\uff0c\u53ef\u662f\u59d1\u5a18\u7684\u4e3b\u4eba\u5417\uff1f\u201d\u5230\u5e95\u662f\u5e74\u7eaa\u5927\u4e9b\uff0c\u6709\u4e9b\u8bdd\u65b9\u4fbf\u8bf4\u51fa\u53e3\u6765\u3002 \u3000\u3000\u90a3\u5e73\u513f\u5ffd\u5730\u683c\u683c\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8001\u4eba\u5bb6\u731c\u5bf9\u4e86\uff0c\u7ea6\u4f60\u4eec\u6765\u6b64\u7684\u4eba\uff0c\u6b63\u662f\u6211\u5bb6\u5c0f\u59d0\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u6697\u5730\u5401\u4e86\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u679c\u7136\u4e0d\u662f\u4f60\uff1f\u201d \u3000\u3000\u5e73\u513f\u4e24\u773c\u4e00\u8f6c\uff0c\u63a5\u9053\uff1a\u201c\u65b9\u516c\u5b50\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\u6211\u5bb6\u5c0f\u59d0\u662f\u8c01\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u9053\uff1a\u8fd9\u5c31\u5947\u4e86\uff0c\u4f60\u5bb6\u5c0f\u59d0\u662f\u6dee\u4f60\u90fd\u8bf4\u4e0d\u51fa\u6765\uff0c\u6211\u53c8\u600e\u4f1a\u77e5\u6653\uff1f\u5f53\u4e0b\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u65b9\u67d0\u5c11\u5728\u6c5f\u6e56\u8d70\u52a8\uff0c\u59d1\u5a18\u7684\u4e3b\u4eba\u662f\u8c01\uff0c\u6055\u5728\u4e0b\u5be1\u95fb\u4e0d\u77e5\uff01\u201d \u3000\u3000\u5e72\u513f\u7b11\u9053\uff1a\u201c\u4f60\u5f88\u8001\u5b9e\u554a\uff01\u6211\u731c\u5f97\u51fa\u4f60\u4e0d\u77e5\u9053\uff01\u201d \u3000\u3000\u8bdd\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u65b9\u516c\u5b50\uff0c\u4f60\u5728\u6c5f\u6e56\u4e0a\u53ef\u662f\u5f88\u6709\u540d\u6c14\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8baa\u8baa\u5730\u4e00\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u5728\u4e0b\u521d\u51fa\u8305\u5e90\uff0c\u8c08\u4e0d\u4e0a\u6709\u4f55\u540d\u6c14\u3002\u201d \u3000\u3000\u5e73\u513f\u5ffd\u7136\u6c89\u541f\u9053\uff1a\u201c\u8fd9\u5c31\u5947\u602a\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u5947\u602a\uff1f\u201d \u3000\u3000\u5e73\u513f\u9053\uff1a\u201c\u4f60\u65e2\u7136\u521d\u51fa\u8305\u5e90\uff0c\u60f3\u5fc5\u662f\u4e2a\u65e0\u540d\u4e4b\u8f88\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fd6\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u660e\u660e\u5728\u9a82\u4eba\u5417\uff1f\u201d\u4f46\u4ed6\u5374\u4e0d\u80fd\u4e0d\u63a5\u9053\uff1a\u201c\u4e0d\u9519\uff01\u5728\u4e0b\u5728\u6b66\u6797\u4e4b\u4e2d\uff0c\u53ef\u8c13\u7c4d\u85c9\u65e0\u540d\u3002\u201d \u3000\u3000\u5e73\u513f\u9053\uff1a\u201c\u6211\u5bb6\u5c0f\u59d0\u4e3a\u4ec0\u4e48\u8981\u7ea6\u4f60\u6765\u6b64\u5462\uff1f\u8fd9\u4e0d\u662f\u5f88\u5947\u602a\u5417\uff1f\u201d \u3000\u3000\u7a76\u7adf\u5947\u4e0d\u5947\u602a\uff0c\u8fde\u65b9\u96ea\u5b9c\u81ea\u5df1\u4e5f\u5f04\u4e0d\u6e05\u695a\u3002 \u3000\u3000\u4ed6\u82e6\u7b11\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u5728\u4e0b\u6b63\u662f\u6709\u4e9b\u4e0d\u89e3\uff0c\u59d1\u5a18\u53ef\u77e5\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5e73\u513f\u6447\u5934\u9053\uff1a\u201c\u6211\u5bb6\u5c0f\u59d0\u884c\u4e8b\uff0c\u6211\u600e\u4f1a\u77e5\u9053\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u5bb6\u5c0f\u59d0\u73b0\u5728\u4f55\u5904\uff1f\u5979\u65e2\u7136\u7559\u5b57\u76f8\u9080\uff0c\u54b1\u4eec\u5df2\u7136\u5e94\u7ea6\u800c\u6765\uff0c\u4e0d\u77e5\u59d1\u5a18\u53ef\u5426\u4ee3\u4e3a\u5f15\u89c1\u8d35\u4e0a\uff1f\u201d \u3000\u3000\u5e73\u513f\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u6709\u4f55\u4e0d\u53ef\uff0c\u6211\u8fd9\u5c31\u53bb\u7980\u544a\u5c0f\u59d0\u4fbf\u4e86\u3002\u201d \u3000\u3000\u4e00\u626d\u5934\uff0c\u5c31\u5f85\u79bb\u53bb\u3002 \u3000\u3000\u5ffd\u7136\uff0c\u65b9\u96ea\u5b9c\u4f3c\u662f\u60f3\u8d77\u4e86\u4ec0\u4e48\uff0c\u559d\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d \u3000\u3000\u5e73\u513f\u5a07\u8eaf\u4e00\u8f6c\uff0c\u7b11\u9053\uff1a\u201c\u65b9\u516c\u5b50\uff0c\u6709\u4f55\u4e8b\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u59d1\u5a18\u7684\u5c0f\u59d0\uff0c\u662f\u54ea\u4e00\u95e8\u6d3e\u7684\u9ad8\u4eba\u5b50\u5973\uff0c\u4e0d\u77e5\u53ef\u5426\u5148\u884c\u89c1\u544a\u3002\u201d \u3000\u3000\u5e73\u513f\u683c\u683c\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u95ee\u8fd9\u4e2a\u5e72\u5417\uff1f\u5f85\u4f1a\u513f\u89c1\u5230\u6211\u5bb6\u5c0f\u59d0\uff0c\u6015\u5979\u4e0d\u4f1a\u544a\u8bc9\u4f60\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u82e6\u7b11\u4e86\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u5728\u4e0b\u5e94\u7ea6\u800c\u6765\uff0c\u5982\u662f\u4e0d\u77e5\u4f60\u5bb6\u5c0f\u59d0\u8d35\u59d3\uff0c\u5c82\u975e\u5927\u4e3a\u5931\u793c\u5417\uff1f\u201d \u3000\u3000\u90a3\u5e73\u513f\u7565\u4e00\u6c89\u541f\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u4e5f\u6709\u9053\u7406\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u4f3c\u662f\u6015\u5979\u4e0d\u8bf4\uff0c\u8fde\u5fd9\u7b11\u9053\uff1a\u201c\u8d35\u4e0a\u600e\u4e48\u79f0\u547c\uff1f\u201d \u3000\u3000\u5e73\u513f\u9053\uff1a\u201c\u6211\u5bb6\u5c0f\u59d0\u59d3\u5b89\u201d\uff0c\u5ffd\u7136\u4e00\u65cb\u5a07\u8eaf\uff0c\u8d70\u5f97\u6ca1\u4e86\u5f71\u5b50\u3002 \u3000\u3000\u5b8b\u5b5a\u6123\u4e86\u4e00\u6123\uff0c\u9053\uff1a\u201c\u597d\u5feb\u7684\u8eab\u6cd5\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u77a7\u90a3\u5e73\u59d1\u5a18\u5ffd\u7136\u884c\u53bb\uff0c\u5fc3\u4e2d\u5927\u5927\u4e00\u9707\uff0c\u5fd6\u9053\uff1a\u201c\u770b\u6b64\u5973\u8eab\u6cd5\u4e4b\u5feb\uff0c\u5df2\u7136\u4e0d\u5728\u6c5f\u6e56\u4e00\u6d41\u9ad8\u624b\u4e4b\u4e0b\uff0c\u5979\u7684\u4e3b\u4eba\uff0c\u81ea\u7136\u662f\u66f4\u9ad8\u660e\u4e86\u3002\u201d\u4e00\u65f6\u4e4b\u95f4\uff0c\u4ed6\u60f3\u4e0d\u51fa\u4eca\u591c\u4e4b\u4e8b\u662f\u7978\u662f\u798f\uff0c\u867d\u7136\uff0c\u90a3\u5e73\u513f\u5e76\u65e0\u4e00\u53e5\u6076\u8a00\u76f8\u5411\uff0c\u4f46\u4e8b\u51fa\u7a81\u7136\uff0c\u603b\u662f\u53eb\u4eba\u89c9\u5f97\u4e0d\u592a\u5e73\u51e1\u2026\u2026 \u3000\u3000\u52a8\u5ff5\u4e4b\u95f4\uff0c\u4e0d\u7981\u4f4e\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u773c\u4e0b\u7684\u60c5\u666f\uff0c\u53eb\u665a\u751f\u4e0d\u5b89\u5f97\u5f88\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u4e5f\u795e\u8272\u51dd\u91cd\u5730\u63a5\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u7978\u662f\u9505\uff0c\u662f\u559c\u662f\u5fe7\uff0c\u672a\u89c1\u90a3\u4f4d\u5b89\u59d1\u5a18\u4e4b\u524d\uff0c\u679c\u771f\u96be\u4ee5\u731c\u5ea6\uff0c\u4e0d\u8fc7\uff0c\u636e\u8001\u673d\u770b\u6765\uff0c\u8001\u5f1f\u4e5f\u7528\u4e0d\u7740\u62c5\u5fc3\u592a\u751a\uff0c\u5bf9\u65b9\u5018\u82e5\u771f\u6709\u751a\u6076\u610f\uff0c\u90a3\u4e5f\u7528\u4e0d\u7740\u8d39\u4e0a\u8fd9\u4e48\u591a\u624b\u811a\uff0c\u628a\u4f60\u6211\u8bf7\u6765\u6b64\u5904\u4e86\u3002\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u4f3c\u662f\u5bf9\u65b9\u82e5\u662f\u60f3\u4e3a\u5bb3\u4e8e\u81ea\u5df1\u4e24\u4eba\uff0c\u5927\u53ef\u8d81\u7559\u4e66\u8239\u6845\u4e4b\u9645\uff0c\u6697\u4e2d\u4e0b\u6bd2\u624b\u4e86\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u81ea\u4e5f\u60f3\u5230\u8fc7\u8fd9\u4e00\u70b9\uff0c\u5f53\u4e0b\u76b1\u7709\u4e00\u53f9\uff0c\u9053\uff1a\u201c\u5b8b\u8001\u8bf4\u5f97\u4e0d\u9519\uff0c\u665a\u751f\u53ea\u6015\u4e8b\u51fa\u610f\u6599\u4e4b\u5916\u7f62\u4e86\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u77a7\u4ed6\u8fd9\u7b49\u4e0d\u5b89\uff0c\u5fc3\u4e2d\u4e0d\u7981\u6697\u9053\uff1a\u201c\u6211\u597d\u6b79\u4e5f\u5f97\u7a33\u4f4f\u4ed6\u7684\u60c5\u7eea\u624d\u597d\u3002\u201d\u5f53\u4e0b\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u65e2\u6765\u4e4b\uff0c\u5219\u5b89\u4e4b\uff0c\u5927\u4e08\u592b\u4e3a\u4eba\u5904\u4e16\uff0c\u4f46\u6c42\u5fc3\u5b89\uff0c\u4f55\u5fc5\u7ec8\u65e5\u60f4\u60f4\u5462\uff1f\u201d \u3000\u3000\u7b11\u58f0\u867d\u7136\u6709\u4e9b\u52c9\u5f3a\uff0c\u4f46\u8fd9\u51e0\u53e5\u8bdd\uff0c\u5374\u662f\u76f8\u5f53\u6709\u529b\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e0d\u89c9\u5730\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u662f\u554a\uff01\u54b1\u4eec\u4e5f\u6ca1\u6709\u505a\u8fc7\u4ec0\u4e48\u4e8f\u5fc3\u4e4b\u4e8b\uff0c\u6015\u7740\u522b\u4eba\u4f5c\u751a\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u6b63\u662f\uff0c\u6b63\u662f\uff0c\u8001\u5f1f\u679c\u7136\u662f\u4e2a\u660e\u767d\u4eba\u3002\u201d \u3000\u3000\u4e24\u4eba\u7b11\u58f0\u672a\u5df2\uff0c\u90a3\u5e73\u513f\u5df2\u518d\u5ea6\u5192\u4e86\u51fa\u6765\u3002 \u3000\u3000\u6562\u60c5\u5728\u90a3\u5deb\u5c71\u7edd\u9876\u7684\u8944\u738b\u53f0\u4e0a\uff0c\u7adf\u7136\u8fd8\u6709\u7740\u4e00\u5904\u5341\u5206\u9690\u79d8\u7684\u79d8\u9053\uff0c\u901a\u5f80\u4e00\u5904\u4e0d\u4e3a\u4eba\u77e5\u7684\u77f3\u6d1e\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u90a3\u5e73\u513f\u6765\u53bb\u4e4b\u95f4\uff0c\u5c31\u663e\u5f97\u5341\u5206\u5feb\u6377\u4e86\u3002 \u3000\u3000\u5b8b\u5b5a\u76ee\u7779\u90a3\u5e73\u513f\u73b0\u8eab\u3002\u8fde\u5fd9\u7b11\u9053\uff1a\u201c\u5e73\u59d1\u5a18\uff0c\u8d35\u4e0a\u600e\u4e48\u8bb2\u4e86\uff1f\u201d \u3000\u3000\u5e73\u513f\u7b11\u9053\uff1a\u201c\u6211\u5bb6\u5c0f\u59d0\u65e2\u7136\u7559\u5b57\u7ea6\u4e86\u4f60\u4eec\uff0c\u5f53\u7136\u662f\u89c1\u4f60\u4eec\u7684\u561b\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u5982\u6b64\uff0c\u8001\u673d\u7b49\u8fd9\u5c31\u524d\u53bb\u5417\uff1f\u201d \u3000\u3000\u5e73\u513f\u4e00\u7b11\u9053\uff1a\u201c\u5f53\u7136\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fae\u5fae\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u6709\u52b3\u59d1\u5a18\u5f15\u8def\uff0c\u5728\u4e0b\u2026\u2026\u201d \u3000\u3000\u5e73\u513f\u672a\u5bb9\u4ed6\u518d\u8bf4\uff0c\u683c\u683c\u5730\u5a07\u7b11\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5c11\u8bf4\u90a3\u4e9b\u4fd7\u793c\u5ba2\u5957\uff0c\u8ddf\u6211\u6765\u5427\uff01\u201d\u8f6c\u8eab\u5411\u4e24\u4e08\u4ee5\u5916\u7684\u4e00\u6839\u5de8\u5927\u77f3\u7b0b\uff0c\u884c\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u662f\uff01\u662f\u2026\u2026\u201d \u3000\u3000\u5f53\u4e0b\u548c\u5b8b\u5b5a\u968f\u7740\u5e73\u513f\u8eab\u540e\u8d70\u53bb\uff0c\u5230\u5f97\u77f3\u7b0b\u4e4b\u524d\uff0c\u8fd9\u624d\u53d1\u73b0\uff0c\u90a3\u513f\u6709\u7740\u4e00\u9053\u5bbd\u53ef\u5bb9\u4eba\u8fdb\u51fa\u7684\u77f3\u95e8\u3002 \u3000\u3000\u5e73\u513f\u6b63\u7ad9\u5728\u90a3\u77f3\u95e8\u4eba\u53e3\u4e4b\u5904\u76f8\u5f85\u3002 \u3000\u3000\u5b8b\u5b5a\u767d\u7709\u4e00\u626c\uff0c\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u671d\u4e91\u5cf0\u4e0a\uff0c\u8fd8\u6709\u4e00\u5904\u79d8\u5bc6\u3002\u201d \u3000\u3000\u5e73\u513f\u5c0f\u5634\u4e00\u9f13\uff0c\u9053\uff1a\u201c\u8fd9\u6709\u4ec0\u4e48\u7a00\u5947\uff1f\u4f60\u4e0d\u77e5\u9053\u7684\u4e8b\u8fd8\u591a\u7740\u5462\uff01\u5feb\u8ddf\u6211\u8fdb\u6765\u5427\uff01\u201d \u3000\u3000\u4e00\u8f6c\u8eab\uff0c\u4fbf\u6253\u90a3\u77f3\u95e8\u4e4b\u4e2d\uff0c\u5411\u5730\u4e0b\u94bb\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u56de\u987e\u4e86\u5b8b\u5b5a\u4e00\u773c\uff0c\u9053\uff1a\u201c\u665a\u8f88\u524d\u5934\u5e26\u8def\u4e86\u3002\u201d \u3000\u3000\u4e24\u4eba\u9c7c\u8d2f\u800c\u884c\uff0c\u987a\u7740\u90a3\u95e8\u5185\u5411\u4e0b\u4f38\u5c55\u7684\u79d8\u5f84\uff0c\u7ea6\u83ab\u8d70\u4e86\u4e8c\u5341\u4f59\u7ea7\uff0c\u8bc6\u89c9\u773c\u524d\u5ffd\u7136\u4e00\u4eae\uff0c\u970d\u7136\u4f3c\u5b58\u8eab\u4e00\u5904\u6781\u5927\u7684\u6bbf\u5802\u4e4b\u4e2d\u4e00\u822c\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u6014\u5f97\u4e00\u6014\uff0c\u4e3e\u76ee\u6253\u91cf\u8fd9\u7b49\u6240\u5728\uff0c\u53ea\u89c1\u8fd9\u56db\u9762\u4ff1\u662f\u77f3\u58c1\u7684\u5927\u5385\uff0c\u7adf\u6709\u4e94\u4e08\u65b9\u5706\uff0c\u5385\u5185\u9648\u8bbe\uff0c\u6781\u5176\u534e\u4e3d\uff0c\u867d\u65e0\u5b57\u753b\u60ac\u6302\u58c1\u4e0a\uff0c\u4f46\u6cbf\u7740\u4e09\u9762\u77f3\u58c1\u4e4b\u4e0b\uff0c\u5374\u6446\u6ee1\u4e86\u5404\u79cd\u53e4\u73a9\uff0c\u5176\u4e2d\u4ec5\u662f\u94dc\u74f7\u74f6\u4e4b\u7c7b\uff0c\u5c31\u4e0d\u4e0b\u4e8c\u5341\u591a\u4ef6\u3002\u800c\u4e14\uff0c\u4e00\u534a\u4ee5\u4e0a\uff0c\u4f7f\u65b9\u96ea\u5b9c\u53eb\u4e0d\u51fa\u540d\u5b57\u3002 \u3000\u3000\u5927\u5385\u7684\u6b63\u9762\uff0c\u8bbe\u6709\u4e00\u5f20\u864e\u76ae\u592a\u5e08\u6905\uff0c\u7531\u592a\u5e08\u8f6c\u5411\u4e24\u4fa7\u524d\u4f38\uff0c\u662f\u4e24\u5217\u9526\u51f3\uff0c\u6bcf\u8fb9\u5404\u6709\u56db\u53ea\u4e4b\u591a\u3002 \u3000\u3000\u592a\u5e08\u6905\u7684\u80cc\u540e\uff0c\u7ad6\u7740\u4e00\u9762\u7389\u77f3\u5c4f\u98ce\uff0c\u9ad8\u6709\u4e08\u4f59\uff0c\u5bbd\u8fbe\u56db\u5c3a\uff0c\u7389\u8272\u9752\u4e2d\u542b\u7d2b\uff0c\u8fdc\u8fdc\u671b\u53bb\uff0c\u771f\u4f3c\u4e00\u7247\u4e91\u70df\u6d41\u8f6c\uff0c\u6eda\u6eda\u7ffb\u817e\u4e0d\u5df2\u3002 \u3000\u3000\u592a\u5e08\u6905\u7684\u524d\u65b9\u4e94\u5c3a\u5de6\u53f3\uff0c\u6446\u7740\u4e00\u4e2a\u91d1\u9f0e\uff0c\u7f15\u7f15\u9752\u70df\uff0c\u6b63\u7531\u9f0e\u76d6\u4e4b\u4e2d\uff0c\u67ad\u67ad\u5347\u8d77\u3002 \u3000\u3000\u5b8b\u5b5a\u8eab\u5165\u5927\u5385\uff0c\u7adf\u7136\u7acb\u5373\u5954\u5411\u90a3\u4e9b\u53e4\u73a9\u53e4\u74f6\uff0c\u8fde\u58f0\u8d5e\u597d\u7684\u6478\u6765\u6478\u53bb\uff0c\u663e\u7136\u4ed6\u662f\u5f88\u8bc6\u8d27\u7684\u4e86\u3002 \u3000\u3000\u5728\u4e24\u4eba\u540c\u65f6\u88ab\u5927\u5385\u666f\u8272\u6240\u593a\u4e4b\u9645\uff0c\u8033\u4e2d\u5ffd\u7136\u542c\u5f97\u5e73\u513f\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u600e\u4e48\u5566\uff01\u662f\u6765\u770b\u8fd9\u4e2a\u5927\u5385\uff1f\u8fd8\u662f\u6765\u62dc\u89c1\u6211\u5bb6\u5c0f\u59d0\u5462\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u3001\u5b8b\u5b5a\u95fb\u8a00\u4e0d\u7981\u8d6b\u7136\uff0c\u6697\u9053\uff1a\u201c\u53ef\u4e0d\u662f\uff1f\u54b1\u4eec\u771f\u662f\u7cca\u6d82\u7684\u5f88\u2026\u2026\u201d \u3000\u3000\u8f6c\u5ff5\u4e4b\u95f4\uff0c\u65b9\u96ea\u5b9c\u8131\u53e3\u9053\uff1a\u201c\u81ea\u662f\u62dc\u89c1\u5b89\u5c0f\u59d0\u7684\u4e86\uff0c\u5e73\u59d1\u5a18\u5feb\u4e9b\u7980\u544a\uff0c\u5c31\u8bf4\u65b9\u96ea\u5b9c\u966a\u540c\u5927\u6f20\u9b3c\u624b\u5b8b\u8001\u524d\u6765\u62dc\u8c12\u3002\u201d \u3000\u3000\u5e73\u513f\u683c\u683c\u4e00\u7b11\u9053\uff1a\u201c\u5e9f\u8bdd\uff01\u201d\u7b11\u58f0\u4e00\u987f\uff0c\u5ffd\u7136\u63a5\u9053\uff1a\u201c\u8ddf\u6211\u6765\u5427\uff01\u5c0f\u59d0\u65e9\u5c31\u5728\u540e\u5385\u7b49\u7740\u4f60\u4eec\u54e9\uff01\u201d \u3000\u3000\u4e24\u4eba\u867d\u5df2\u6599\u5230\u8fd9\u5730\u4e0b\u7684\u5927\u5385\uff0c\u7edd\u975e\u6b64\u95f4\u552f\u4e00\u7684\u77f3\u5ba4\uff0c\u4f46\u5374\u5e76\u672a\u60f3\u5230\u8fd8\u6709\u5185\u5385\uff0c\u5982\u679c\u7167\u5e73\u513f\u8fd9\u7b49\u8bf4\u6cd5\uff0c\u90a3\u9664\u4e86\u5185\u5385\u4e4b\u5904\uff0c\u53ea\u6015\u8fd8\u6709\u7740\u53a2\u623f\uff0c\u4e0a\u623f\u548c\u5185\u5ba4\u7684\u4e86\u3002 \u3000\u3000\u679c\u771f\u5982\u6b64\uff0c\u8fd9\u671d\u4e91\u5cf0\u4e0b\u7684\u77f3\u6d1e\uff0c\u5c82\u4e0d\u662f\u4e00\u5904\u5f88\u5927\u7684\u5c71\u8179\u4e4b\u4e2d\u7684\u77f3\u5e9c\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5ba3\u5fc3\u4e2d\u867d\u8bf4\u65e2\u60ca\u53c8\u5947\uff0c\u8868\u9762\u4e4b\u4e0a\uff0c\u81ea\u7136\u672a\u4fbf\u9732\u51fa\uff0c\u5f53\u4e0b\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u8bf7\uff01\u2026\u2026\u201d \u3000\u3000\u4e24\u4eba\u968f\u5728\u90a3\u5e73\u513f\u8eab\u540e\uff0c\u8f6c\u8fc7\u4e86\u7389\u77f3\u5c4f\u98ce\uff0c\u53c8\u8fc7\u4e86\u4e00\u9053\u95e8\u6237\uff0c\u6cbf\u7740\u4e00\u6761\u5bbd\u6709\u4e94\u5c3a\uff0c\u9ad8\u8fbe\u4e08\u8bb8\u7684\u957f\u957f\u752c\u9053\u884c\u53bb\u3002 \u3000\u3000\u752c\u9053\u7684\u4e24\u4fa7\uff0c\u6bcf\u9694\u4e09\u4e08\uff0c\u5c31\u6709\u4e00\u9053\u95e8\u6237\uff0c\u6bcf\u4e00\u95e8\u6237\u4e4b\u4e0a\uff0c\u4ff1\u7686\u5d4c\u6709\u4e00\u9897\u660e\u73e0\uff0c\u662f\u4ee5\u8fd9\u96e8\u9053\u4e4b\u5185\uff0c\u5012\u4e5f\u5341\u5206\u660e\u4eae\u3002 \u3000\u3000\u7ea6\u83ab\u8d70\u4e86\u5341\u4e03\u516b\u4e08\u4e4b\u8fdc\uff0c\u8fce\u9762\u53c8\u662f\u4e00\u9053\u77f3\u95e8\u3002 \u3000\u3000\u5e73\u513f\u56de\u5934\u5411\u4e24\u4eba\u62db\u4e86\u62db\u624b\uff0c\u4fbf\u8de8\u8fdb\u77f3\u95e8\u4e4b\u5185\u3002 \u3000\u3000\u65b9\u3001\u5b8b\u4e24\u4eba\u81ea\u662f\u4e0d\u6562\u6020\u9992\uff0c\u7acb\u5373\u8ddf\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8fdb\u5f97\u8fd9\u5ea7\u77f3\u95e8\uff0c\u679c\u7136\u53c8\u662f\u4e00\u95f4\u5927\u5385\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u8fd9\u95f4\u4f3c\u662f\u6bd4\u5916\u95f4\u8981\u5c0f\u4e86\u4e09\u5206\u4e4b\u4e00\uff0c\u9648\u8bbe\u6446\u8bbe\uff0c\u4e5f\u7b80\u5355\u5f97\u591a\uff0c\u9664\u4e86\u5f53\u4e2d\u4e00\u53cc\u7389\u77f3\u4e66\u6848\uff0c\u548c\u4e00\u53ea\u77ee\u77ee\u7684\u80e1\u5e8a\u4e4b\u5916\uff0c\u4e09\u4e08\u89c1\u65b9\u7684\u5185\u5385\uff0c\u53ea\u53e6\u5916\u6446\u4e86\u4e24\u53ea\u84b2\u56e2\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u62ac\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u90a3\u7389\u6848\u4e4b\u540e\u7684\u80e1\u5e8a\u4e4b\u4e0a\uff0c\u76d8\u819d\u5750\u4e86\u4e00\u4f4d\u8eab\u7740\u6de1\u9752\u7f57\u886b\uff0c\u957f\u53d1\u5782\u80a9\uff0c\u9762\u8c8c\u59e3\u7f8e\u79c0\u59a9\u7684\u5c11\u5973\u3002 \u3000\u3000\u90a3\u5e73\u513f\u8fd9\u65f6\u6b63\u5728\u5979\u8eab\u524d\u5581\u5581\u4f4e\u8bed\u3002 \u3000\u3000\u90a3\u9752\u8863\u5c11\u5973\u53cc\u76ee\u4f4e\u5782\uff0c\u53ea\u662f\u4e0d\u4f4f\u5730\u8f7b\u8f7b\u70b9\u5934\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\u60f3\u5fc5\u5c31\u662f\u90a3\u7ea6\u6211\u6765\u6b64\u7684\u5b89\u5c0f\u59d0\u4e86\u3002\u201d \u3000\u3000\u5fc3\u5ff5\u4e00\u52a8\uff0c\u6b63\u5f85\u51fa\u58f0\u62a5\u540d\u95ee\u8bdd\uff0c\u5ffd\u7136\u542c\u5f97\u5b8b\u5b5a\u7528\u4f20\u97f3\u4e4b\u672f\uff0c\u5411\u81ea\u5df1\u8bf4\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u6b64\u5973\u53cc\u7709\u5e26\u715e\uff0c\u4e0d\u4f3c\u6b63\u8def\u4eba\u7269\uff0c\u54b1\u4eec\u53ef\u8981\u5c0f\u5fc3\u4e86\u3002\u201d \u3000\u3000\u5b9e\u5219\uff0c\u4e0d\u7528\u5b8b\u5b5a\u4ea4\u4ee3\uff0c\u65b9\u96ea\u5b9c\u4e5f\u5df2\u77a7\u51fa\u6b64\u5973\u7684\u76ee\u5149\u4e0d\u5927\u5bf9\u52b2\uff0c\u662f\u4ee5\uff0c\u5fc3\u4e2d\u5df2\u7136\u6709\u4e86\u8b66\u60d5\u4e4b\u610f\u3002\u5f53\u4e0b\u4e5f\u7528\u4f20\u97f3\u5e94\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u665a\u8f88\u77a7\u5979\u4f3c\u662f\u4e0d\u6000\u597d\u610f\uff0c\u5982\u662f\u60c5\u52bf\u6240\u8feb\uff0c\u54b1\u4eec\u8981\u4e0d\u8981\u8ddf\u5979\u52a8\u624b\u5462\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8fd9\u4e2a\u5417\uff1f\u6700\u597d\u4e0d\u8981\u52a8\u6b66\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u4e00\u6123\uff0c\u6b63\u60f3\u95ee\u90a3\u5b8b\u5b5a\uff0c\u4e3a\u4f55\u6700\u597d\u4e0d\u8981\u8ddf\u5979\u52a8\u6b66\uff0c\u8033\u4e2d\u5374\u5df2\u542c\u5f97\u90a3\u9752\u886b\u5c11\u5973\u51b7\u51b7\u559d\u9053\uff1a\u201c\u4f60\u53ef\u662f\u65b9\u96ea\u5b9c\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5403\u4e86\u4e00\u60ca\uff0c\u8131\u53e3\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u533a\u533a\u6b63\u662f\u65b9\u96ea\u5b9c\uff0c\u4e0d\u77e5\u59d1\u5a18\u600e\u4e48\u79f0\u547c\uff1f\u201d \u3000\u3000\u9752\u886b\u5c11\u5973\u9053\uff1a\u201c\u6211\u59d3\u5b89\uff0c\u5355\u540d\u4e00\u4e2a\u5029\u5b57\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u63a5\u9053\uff1a\u201c\u539f\u6765\u662f\u5b89\u59d1\u5a18\uff0c\u5931\u656c\u4e86\uff01\u201d\u8bdd\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5b89\u59d1\u5a18\uff0c\u662f\u600e\u751f\u77e5\u9053\u5728\u4e0b\u59d3\u540d\uff1f\u201d \u3000\u3000\u90a3\u5b89\u60c5\u683c\u683c\uff0c\u4e00\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u4f60\u53ef\u7ba1\u4e0d\u7740\u3002\u201d \u3000\u3000\u5ffd\u7136\u76ee\u5149\u5411\u5b8b\u5b5a\u4e00\u8f6c\u9053\uff1a\u201c\u8fd9\u4f4d\u60f3\u5fc5\u662f\u5927\u540d\u9f0e\u9f0e\u7684\u5b8b\u795e\u5077\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u5fae\u5fae\u6014\u4e86\u4e00\u6014\uff0c\u5e72\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6b63\u662f\uff0c\u6b63\u662f\uff01\u8001\u673d\u4e0e\u59d1\u5a18\u7d20\u6627\u5e73\u751f\uff0c\u4e0d\u77e5\u59d1\u5a18\u662f\u600e\u751f\u77e5\u9053\u4e86\u8001\u673d\u8fd9\u5077\u513f\u7684\u6765\u5386\uff01\u201d \u3000\u3000\u5b89\u60c5\u9053\uff1a\u201c\u5927\u6f20\u9b3c\u624b\u4e4b\u540d\uff0c\u5728\u6b66\u6797\u4e2d\u81ea\u7136\u662f\u4eba\u4eba\u7686\u77e5\uff0c\u6211\u80fd\u6653\u5f97\u51fa\u6765\uff0c\u90a3\u4e5f\u4e0d\u8db3\u4e3a\u5947\u7684\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u76f4\u63a5\u9053\uff1a\u201c\u5b89\u59d1\u5a18\uff0c\u5728\u4e0b\u6614\u65e5\u548c\u59d1\u5a18\u53ef\u662f\u6ca1\u6709\u89c1\u8fc7\u9762\u5440\uff1f\u201d \u3000\u3000\u5b89\u60c5\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u4eec\u6ca1\u6709\u89c1\u8fc7\u9762\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u65e2\u7136\u5f7c\u6b64\u4e5f\u5e76\u4e0d\u76f8\u8bc6\uff0c\u59d1\u5a18\u5ffd\u7136\u7559\u67ec\u5ba0\u53ec\uff0c\u4e0d\u77e5\u4e3a\u4e86\u4f55\u6545\uff1f\u201d \u3000\u3000\u5b89\u60c5\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u201d\u5ffd\u7136\u683c\u683c\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u6211\u5411\u6765\u884c\u4e8b\u53ea\u7ba1\u81ea\u5df1\u9ad8\u4e0d\u9ad8\u5174\uff0c\u6240\u4ee5\uff0c\u6211\u89c9\u5f97\u4f60\u8fd9\u4eba\u5f88\u597d\u73a9\uff0c\u5c31\u7559\u4e0b\u51e0\u4e2a\u5b57\u7ea6\u4f60\u6765\u6b64\uff0c\u96be\u9053\u6709\u4ec0\u4e48\u4e0d\u5bf9\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u6697\u9053\uff1a\u8fd9\u662f\u4ec0\u4e48\u6b6a\u9053\u7406\u5462\uff1f\u54b1\u4eec\u4e5f\u4e0d\u662f\u4e13\u4e3a\u4f9b\u4f60\u5f00\u5fc3\u7684\u554a\uff01 \u3000\u3000\u4f46\u4ed6\u53e3\u4e2d\u5374\u51b7\u51b7\u5730\u63a5\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u53ef\u77e5\u9053\u65b9\u67d0\u548c\u5b8b\u8001\u5c1a\u6709\u8981\u4e8b\u5728\u8eab\u5417\uff1f\u59d1\u5a18\u4eec\u5ffd\u7136\u76f8\u9080\uff0c\u867d\u7136\u5f80\u8fd4\u5e76\u4e0d\u7b97\u8fdc\uff0c\u81f3\u5c11\u4e5f\u8981\u803d\u6401\u4e86\u65b9\u67d0\u7684\u4e00\u6bb5\u884c\u7a0b\u3002\u201d \u3000\u3000\u5b89\u60c5\u9053\uff1a\u201c\u4f60\u5f88\u4e0d\u9ad8\u5174\u662f\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u59d1\u5a18\u8fd9\u7b49\u73a9\u7b11\uff0c\u4f3c\u662f\u5f00\u5f97\u8fc7\u5206\u4e86\u3002\u201d \u3000\u3000\u5176\u5b9e\uff0c\u8fd9\u5b89\u5029\u521a\u624d\u5df2\u7ecf\u8bf4\u6f0f\u4e86\u4e00\u53e5\u8bdd\uff0c\u53ea\u53ef\u60dc\u65b9\u96ea\u5b9c\u5e76\u672a\u542c\u5f97\u51fa\u6765\uff0c\u90a3\u767d\u7ee2\u7559\u5b57\uff0c\u660e\u660e\u7528\u7684\u662f\u4e0b\u4eba\u53e3\u6c14\uff0c\u6b64\u523b\u5b89\u5029\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u5374\u53c8\u8bf4\u662f\u81ea\u5df1\u4e00\u65f6\u9ad8\u5174\u7559\u4e0b\uff0c\u663e\u7136\u4e0e\u90a3\u4e8b\u5b9e\u5df2\u662f\u4e0d\u7b26\u4e86\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u8bdd\u4e00\u843d\uff0c\u5b89\u5029\u5ae3\u7136\u7b11\u9053\uff1a\u201c\u4f60\u771f\u7684\u8eab\u6709\u6025\u4e8b\u5f85\u529e\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5728\u4e0b\u6b63\u662f\u8eab\u6709\u6025\u4e8b\uff0c\u5426\u5219\uff0c\u90a3\u4e5f\u7528\u4e0d\u7740\u8fde\u591c\u8d76\u8def\uff0c\u5192\u90a3\u649e\u4e0a\u5c71\u77f3\u7684\u5371\u9669\u4e86\u3002\u201d \u3000\u3000\u5b89\u5029\u9661\u7136\u683c\u683c\u5a07\u7b11\u9053\uff1a\u201c\u65b9\u516c\u5b50\uff0c\u4f60\u770b\u4e0a\u53bb\u5f88\u8001\u5b9e\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u5728\u4e0b\u5411\u6765\u8a00\u5fc5\u7531\u8877\uff01\u201d \u3000\u3000\u5b89\u60c5\u5ffd\u7136\u8138\u8272\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u8d81\u90a3\u591c\u8272\uff0c\u884c\u8239\u4e8e\u77bf\u5858\u5ce1\u4e2d\uff0c\u5f53\u771f\u662f\u4e3a\u4e86\u8d76\u8def\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6014\u5f97\u4e00\u6014\uff0c\u9053\uff1a\u201c\u96be\u9053\u4f1a\u4e0d\u662f\u5417\uff1f\u201d\u8bdd\u5df2\u51fa\u53e3\uff0c\u65b9\u96ea\u5b9c\u7acb\u5373\u89c9\u51fa\u81ea\u5df1\u4e0d\u5bf9\uff0c\u5fd9\u9053\uff1a\u201c\u5728\u4e0b\u4e4b\u4e8b\uff0c\u59d1\u5a18\u53c8\u600e\u77e5\u4e0d\u662f\uff1f\u201d \u3000\u3000\u5b89\u5029\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u77a7\u4e0d\u51fa\u50cf\u4f60\u8fd9\u7b49\u5e74\u7eaa\uff0c\u5c31\u5b66\u5f97\u8fd9\u4e48\u6cb9\u6ed1\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u5fc3\u4e2d\u5927\u4e0d\u53d7\u7528\uff0c\u6012\u9053\uff1a\u201c\u59d1\u5a18\u6700\u597d\u522b\u6fc0\u6012\u5728\u4e0b\uff01\u201d \u3000\u3000\u5b89\u60c5\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u660e\u660e\u8981\u89c2\u8d4f\u90a3\u77bf\u5858\u5ce1\u591c\u666f\u5417\uff1f\u4e3a\u4f55\u53c8\u9a97\u4eba\u8bf4\u662f\u6709\u6025\u4e8b\u8d76\u8def\u5462\uff1f\u201d \u3000\u3000\u8bed\u58f0\u7565\u987f\uff0c\u51b7\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u8fd9\u8fd8\u602a\u522b\u4eba\u6fc0\u6012\u4f60\u5440\uff1f\u8c01\u53eb\u4f60\u5148\u8bf4\u5047\u8bdd\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u5f97\u5446\u4e86\u534a\u664c\uff0c\u6ca1\u6709\u8bf4\u8bdd\uff0c\u6562\u60c5\uff0c\u4ed6\u60f3\u4e0d\u51fa\u8fd9\u5c11\u5973\u600e\u77e5\u4ed6\u4eec\u8fde\u591c\u884c\u821f\uff0c\u4e43\u662f\u4e3a\u4e86\u6b23\u8d4f\u4e09\u5ce1\u98ce\u5149\u4e4b\u4e8b\u3002 \u3000\u3000\u5b8b\u5b5a\u8fd9\u65f6\u5ffd\u7136\u76b1\u8d77\u4e86\u7709\u5934\uff0c\u9053\uff1a\u201c\u5b89\u59d1\u5a18\uff0c\u8001\u673d\u6709\u4e00\u53e5\u8bdd\uff0c\u4e0d\u77e5\u8be5\u4e0d\u8be5\u5411\u59d1\u5a18\u8bf7\u6559\uff1f\u201d \u3000\u3000\u5b89\u5029\u9053\uff1a\u201c\u4ec0\u4e48\u8bdd\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u59d1\u5a18\u53ef\u662f\u4e00\u76f4\u8ddf\u5728\u6211\u4eec\u8eab\u540e\u5417\uff1f\u201d \u3000\u3000\u5b89\u5029\u9053\uff1a\u201c\u5c0a\u9a7e\u8ba4\u4e3a\u6709\u6b64\u53ef\u80fd\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u5f88\u96be\u8bf4\uff0c\u81f3\u5c11\u8001\u673d\u4e0d\u5927\u76f8\u4fe1\uff0c\u59d1\u5a18\u80fd\u8ddf\u5728\u8001\u673d\u8eab\u540e\u3002\u4e0d\u88ab\u53d1\u73b0\u3002\u201d \u3000\u3000\u5b89\u5029\u683c\u683c\u7b11\u9053\uff1a\u201c\u90a3\u4e0d\u5c31\u5f97\u5566\uff1f\u4f60\u4f55\u5fc5\u518d\u95ee\u5462\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4f46\u7ad9\u5a18\u53c8\u600e\u77e5\u65b9\u8001\u5f1f\u6b64\u756a\u8fde\u591c\u884c\u821f\uff0c\u4e43\u662f\u4e3a\u4e86\u8d4f\u73a9\u4e09\u5ce1\u5c71\u5149\u6c34\u8272\u5462\uff1f\u9664\u975e\u2026\u2026\u201d \u3000\u3000\u8bed\u97f3\u987f\u4e00\u987f\uff0c\u7a81\u7136\u5927\u609f\u9053\uff1a\u201c\u662f\u4e86\uff01\u8001\u673d\u660e\u767d\u4e86\uff01\u201d \u3000\u3000\u5b89\u5029\u4e00\u6014\u9053\uff1a\u201c\u4f60\u660e\u767d\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u90a3\u9a76\u8239\u7684\u4eba\uff0c\u662f\u4f60\u4e70\u901a\u7684\uff0c\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u5b89\u5029\u4e24\u53ea\u674f\u773c\u4e00\u9f13\uff0c\u9053\uff1a\u201c\u80e1\u8bf4\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u4e00\u5446\u9053\uff1a\u201c\u6ca1\u6709\u5417\uff1f\u201d \u3000\u3000\u5b89\u5029\u9053\uff1a\u201c\u6211\u4f55\u5fc5\u53bb\u4e70\u901a\u4ed6\u4eec\uff1f\u4f55\u51b5\uff0c\u6211\u53c8\u6839\u672c\u4e0d\u6653\u5f97\u4f60\u4eec\u8981\u5750\u90a3\u4e00\u53ea\u8239\u554a\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u6697\u9053\uff1a\u201c\u8fd9\u5012\u4e0d\u662f\u5047\u8bdd\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u65f6\u5ffd\u7136\u798f\u81f3\u5fc3\u7075\uff0c\u7b11\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u5728\u4e0b\u5012\u662f\u771f\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u5b89\u5029\u5bf9\u4ed6\u770b\u4e86\u4e00\u773c\uff0c\u5c1a\u672a\u8bf4\u8bdd\uff0c\u90a3\u4f8d\u7acb\u4e00\u65c1\u7684\u5e73\u513f\u59d1\u5a18\uff0c\u7a81\u7136\u5a07\u7b11\u4e86\u4e00\u58f0\uff0c\u63a5\u9053\uff1a \u3000\u3000\u201c\u4f60\u660e\u767d\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e24\u4f4d\u59d1\u5a18\u4e4b\u4e2d\uff0c\u5fc5\u662f\u6709\u4eba\u4e5f\u642d\u4e86\u6211\u4eec\u7684\u8239\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u5e73\u513f\u95fb\u8a00\uff0c\u5fcd\u4e0d\u4f4f\u7b11\u4e2a\u4e0d\u505c\u3002 \u3000\u3000\u5b89\u5029\u663e\u7136\u662f\u5979\u7684\u4e3b\u4eba\uff0c\u4f46\u5374\u5e76\u672a\u963b\u6b62\u5979\u8fd9\u7b49\u5927\u4e3a\u5931\u793c\u7684\u4e3e\u52a8\uff0c\u53ea\u662f\u5411\u65b9\u96ea\u5b9c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u56de\u5012\u662f\u88ab\u4f60\u731c\u7740\u5566\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u5343\u8fe5\u767e\u8f6c\uff0c\u5374\u4e5f\u60f3\u4e0d\u5230\u5979\u4eec\u6b64\u4e3e\u7528\u5fc3\u4f55\u5728\uff0c\u662f\u4ee5\uff0c\u5728\u77e5\u9053\u4e86\u5979\u4eec\u4f55\u4ee5\u660e\u767d\u81ea\u5df2\u5e95\u7ec6\u7684\u539f\u56e0\u4ee5\u540e\uff0c\u4e5f\u60f3\u4e0d\u51fa\u518d\u8bf4\u4ec0\u4e48\u624d\u662f\u3002 \u3000\u3000\u5b8b\u5b5a\u7a76\u7adf\u8001\u7ec3\u4e16\u6545\u7684\u591a\uff0c\u5e72\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u9080\u7ea6\u6211\u7b49\u6765\u6b64\uff0c\u7528\u610f\u4f55\u5728\uff0c\u53ef\u5426\u660e\u544a\uff1f\u201d \u3000\u3000\u90a3\u5b89\u5029\u6de1\u6de1\u7684\u770b\u4e86\u5b8b\u5b5a\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5982\u679c\u6211\u8bf4\u6ca1\u6709\u4ec0\u4e48\u4e8b\uff0c\u90a3\u4f60\u4e00\u5b9a\u4e0d\u4f1a\u76f8\u4fe1\u7684\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u5b89\u5029\u5ffd\u7136\u795e\u79d8\u5730\u4e00\u7b11\u9053\uff1a\u201c\u8fd9\u4e48\u770b\u6765\uff0c\u6211\u662f\u4e00\u5b9a\u8981\u8bf4\u51fa\u4e00\u4e9b\u7406\u7531\u624d\u884c\u7684\u4e86\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u4f9d\u7136\u51b7\u51b7\u5e94\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u5b89\u60c5\u9661\u7136\u8106\u58f0\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5982\u679c\u6211\u4e0d\u60f3\u8bf4\u5462\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u5446\u4e86\u4e00\u5446\uff0c\u5374\u9053\uff1a\u201c\u6055\u8001\u673d\u4e0e\u65b9\u8001\u5f1f\u4e0d\u4fbf\u518d\u591a\u7559\u4e86\u3002\u201d \u3000\u3000\u5b89\u60c5\u9053\uff1a\u201c\u4f60\u4eec\u8981\u8d70\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u4ed6\u4e00\u8fde\u7528\u4e86\u4e09\u6b21\u4e0d\u9519\uff0c\u53ea\u628a\u90a3\u5e73\u513f\u542c\u5f97\u521a\u521a\u4f38\u76f4\u4e86\u7684\u67f3\u8170\uff0c\u53c8\u7b11\u5f97\u5f2f\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5b89\u60c5\u6c89\u541f\u9053\uff1a\u201c\u90a3\u600e\u4e48\u884c\u2026\u2026\u6211\u4e0d\u662f\u7279\u5730\u628a\u4f60\u4eec\u9080\u8bf7\u6765\u7684\u5417\uff1f\u6ca1\u89c1\u5c0f\u59d0\uff0c\u600e\u4e48\u2026\u2026\u201d \u3000\u3000\u5979\u5ffd\u7136\u609a\u7136\u4e00\u9707\uff0c\u4f4f\u53e3\u4e0d\u5f80\u4e0b\u8bf4\u3002 \u3000\u3000\u8bd5\u60f3\uff0c\u5b8b\u5b5a\u662f\u4f55\u7b49\u4eba\u7269\uff0c\u5979\u867d\u7136\u5341\u5206\u673a\u8b66\uff0c\u81ea\u5df1\u53d1\u73b0\u8bf4\u6f0f\u4e86\u5634\uff0c\u7acb\u5373\u4f4f\u53e3\u4e0d\u8a00\uff0c\u4f46\u5b8b\u5b5a\u5df2\u7136\u542c\u51fa\u5979\u8bdd\u4e2d\u7684\u6f0f\u6d1e\u4e86\u3002 \u3000\u3000\u5f53\u4e0b\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u4e0d\u662f\u4e3b\u4eba\uff1f\u201d \u3000\u3000\u5b89\u5029\u7c89\u8138\u5927\u5927\u53d8\u8272\uff0c\u6025\u9053\uff1a\u201c\u8c01\u8bf4\u6211\u4e0d\u662f\uff1f\u4f60\u8fd9\u4eba\u771f\u662f\u8001\u5f97\u6709\u4e9b\u7cca\u6d82\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u4e0d\u9519\u554a\uff01\u8001\u673d\u679c\u7136\u662f\u8001\u5f97\u6709\u4e9b\u7cca\u6d82\uff0c\u4f46\u59d1\u5a18\u81ea\u5df1\u53ea\u6015\u6bd4\u8001\u673d\u66f4\u7cca\u6d82\u5f97\u591a\u591a\u5427\uff01\u201d \u3000\u3000\u5b89\u5029\u6012\u9053\uff1a\u201c\u4f60\u6562\u9a82\u6211\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e0d\u6562\uff01\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u5b89\u5029\u9053\uff1a\u201c\u4e0d\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u59d1\u5a18\u660e\u660e\u81ea\u5df1\u5df2\u7ecf\u8bf4\u51fa\uff0c\u8001\u673d\u7b49\u6765\u6b64\u5c1a\u672a\u89c1\u5230\u4f60\u4eec\u5c0f\u59d0\u4e4b\u524d\uff0c\u6240\u4ee5\u4e0d\u80fd\u79bb\u53bb\uff0c\u53ef\u89c1\u5f97\u59d1\u5a18\u4e4b\u4e0a\uff0c\u8fd8\u53e6\u6709\u4e00\u4f4d\u4e3b\u4e8b\u4e4b\u4eba\u7684\u4e86\u3002\u201d \u3000\u3000\u5b89\u60c5\u663e\u7136\u662f\u5904\u4e8b\u6599\u4e8b\u7684\u7ecf\u9a8c\u90fd\u5f88\u6b20\u7f3a\uff0c\u95fb\u8a00\u5c16\u53eb\u9053\uff1a\u201c\u662f\u53c8\u600e\u6837\uff0c\u4f60\u4eec\u60f3\u8d70\uff0c\u53ef\u6ca1\u6709\u90a3\u4e48\u5bb9\u6613\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u5230\u5e95\u8bf4\u4e86\u5b9e\u8bdd\u4e86\u3002\u201d\u76ee\u5149\u4e00\u8f6c\uff0c\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u54b1\u4eec\u5dee\u70b9\u513f\u4e0a\u4e86\u8fd9\u4f4d\u59d1\u5a18\u7684\u5f53\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u5bf9\u5b8b\u5b5a\u7684\u8001\u7ec3\uff0c\u5341\u5206\u94a6\u4f69\uff0c\u95fb\u8a00\u7b11\u9053\uff1a\u201c\u82e5\u975e\u5b8b\u8001\u6167\u773c\u8fc7\u4eba\uff0c\u6362\u4e86\u665a\u8f88\uff0c\u53ef\u5c31\u77a7\u4e0d\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u597d\u8bf4\uff01\u8001\u5f1f\uff0c\u54b1\u4eec\u8981\u4e0d\u8981\u89c1\u89c1\u6b64\u95f4\u4e3b\u4eba\u518d\u8d70\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4f9d\u4f60\u8001\u4e4b\u89c1\u5462\uff1f\u201d\u4ed6\u672c\u6765\u60f3\u8bf4\uff0c\u65e2\u7136\u6765\u4e86\uff0c\u4e0d\u89c1\u4e3b\u4eba\uff0c\u600e\u53ef\u4fbf\u53bb\uff1f\u4f46\u4ed6\u5ffd\u7136\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u7adf\u7136\u4e0d\u4f5c\u4e3b\u5f20\uff0c\u53cd\u53bb\u5f81\u6c42\u610f\u89c1\u3002 \u3000\u3000\u5b8b\u5b5a\u54c8\u54c8\u9053\uff1a\u201c\u5982\u662f\u8001\u673d\u4e4b\u89c1\u5417\uff0c\u90a3\u81ea\u7136\u662f\u8981\u89c1\u5230\u4e3b\u4eba\u4e4b\u540e\u518d\u8d70\u7684\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u665a\u8f88\u9075\u547d\u4fbf\u662f\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u8fd9\u624d\u56de\u987e\u4e86\u5b89\u5029\u4e00\u773c\uff0c\u9053\uff1a\u201c\u7ad9\u5a18\uff0c\u5feb\u8bf7\u901a\u62a5\u8d35\u4e0a\uff0c\u522b\u518d\u9017\u8001\u673d\u5f00\u5fc3\u4e86\u3002\u201d\u6562\u60c5\uff0c\u4ed6\u4fe9\u4e00\u9635\u4ea4\u8c08\u4e4b\u540e\uff0c\u89c1\u90a3\u5b89\u5029\u5e76\u672a\u63d2\u8bdd\uff0c\u4fbf\u76ca\u53d1\u5730\u8ba4\u5b9a\uff0c\u8fd9\u5b89\u5029\u51b3\u975e\u4e3b\u4eba\u3002 \u3000\u3000\u5b89\u5029\u76b1\u8d77\u4e86\u67f3\u7709\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u8001\u7cca\u6d82\u771f\u96be\u7f20\u2026\u2026\u201d \u3000\u3000\u8bdd\u97f3\u672a\u5df2\uff0c\u5e73\u513f\u5df2\u7ecf\u5a07\u7b11\u9053\uff1a\u201c\u5029\u513f\u59d0\u59d0\uff0c\u6211\u7684\u8d4c\u6ce8\u53ef\u7b97\u8d62\u5566\uff01\u201d \u3000\u3000\u90a3\u5b89\u5029\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u8d62\u4e86\u53c8\u600e\u4e48\u529e\uff1f\u771f\u662f\u6211\u628a\u90a3\u4e00\u6735\u91d1\u82b1\u8f93\u7ed9\u4f60\u5417\uff0c\u597d\u72e0\u5fc3\u554a\uff01\u201d \u3000\u3000\u5e73\u513f\u9053\uff1a\u201c\u8fd9\u53ef\u662f\u4f60\u81ea\u5df1\u8d4c\u7684\u5440\uff01\u4f60\u8bf4\u4eba\u5bb6\u8ba4\u4e0d\u51fa\u6765\u561b\uff01\u8fd9\u56de\u8f93\u4e86\uff0c\u5374\u8981\u8d56\u5e10\uff01\u201d \u3000\u3000\u5b89\u5029\u4f3c\u662f\u5341\u5206\u4e0d\u613f\uff0c\u5374\u53c8\u4e0d\u5f97\u4e0d\u628a\u9b13\u7554\u4e00\u6735\u91d1\u82b1\u53d6\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4f46\u90a3\u5b8b\u5b5a\u5728\u770b\u5230\u90a3\u6735\u91d1\u82b1\u4e4b\u540e\uff0c\u4e0d\u7981\u8138\u8272\u4e00\u53d8\u3002 \u3000\u3000\u5e73\u513f\u6447\u5934\u9053\uff1a\u201c\u4e0d\u8981\uff0c\u6211\u53ea\u8981\u8fd9\u4e00\u6735\u91d1\u82b1\uff01\u201d \u3000\u3000\u5b89\u5029\u5927\u5927\u4e0d\u820d\u5730\u5c06\u91d1\u82b1\u4ea4\u5230\u5e73\u513f\u624b\u4e2d\u9053\uff1a\u201c\u597d\u554a\uff01\u4ee5\u540e\u6211\u518d\u4e5f\u4e0d\u8ddf\u4f60\u8fd9\u9b3c\u7cbe\u7075\u6253\u8d4c\u4e86\uff01\u201d \u3000\u3000\u5e73\u513f\u5a07\u7b11\u4e0d\u5df2\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u5df2\u7ecf\u6ca1\u6709\u597d\u4e1c\u897f\u53ef\u4ee5\u4f5c\u8d4c\u6ce8\uff0c\u518d\u627e\u6211\u6253\u8d4c\uff0c\u6211\u5df2\u4e0d\u5e72\u5566\uff01\u201d \u3000\u3000\u4e8c\u4eba\u4e00\u9635\u8c08\u7b11\uff0c\u4f3c\u4e4e\u628a\u8eab\u8fb9\u7684\u5916\u4eba\u5168\u90fd\u5fd8\u4e86\u4e00\u822c\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5012\u4e0d\u89c9\u5f97\u600e\u4e48\u6837\uff0c\u5b8b\u5b5a\u53ef\u5c31\u4e00\u77ac\u4e0d\u77ac\u7684\u76ef\u7740\u90a3\u6735\u5728\u5e73\u513f\u624b\u4e2d\u628a\u73a9\u7684\u91d1\u82b1\uff0c\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u5029\u59d1\u5a18\uff0c\u8fd9\u6735\u91d1\u82b1\uff0c\u53ef\u662f\u4f60\u81ea\u5df1\u4e4b\u7269\uff1f\u201d \u3000\u3000\u5b89\u5029\u5927\u6982\u6b63\u56e0\u90a3\u6735\u81ea\u5df1\u5fc3\u7231\u7684\u91d1\u82b1\uff0c\u6253\u8d4c\u8f93\u4e86\u51fa\u53bb\uff0c\u5fc3\u4e2d\u5927\u662f\u4e0d\u5feb\uff0c\u95fb\u8a00\u674f\u773c\u4e00\u77aa\u9053\uff1a\u201c\u4f60\u7ba1\u5f97\u7740\u5417\uff0c\u662f\u6211\u7684\u600e\u6837\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u5ffd\u7136\u8138\u8272\u4e00\u6c89\uff0c\u559d\u9053\uff1a\u201c\u90a3\u4e94\u5927\u9b54\u4e3b\u4e4b\u4e2d\u7684\u5929\u9b54\u5973\uff0c\u8ddf\u4f60\u600e\u6837\u79f0\u547c\uff01\u201d \u3000\u3000\u5b89\u5029\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u8c01\u662f\u5929\u9b54\u5973\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u6700\u597d\u83ab\u8981\u5728\u8001\u592b\u9762\u524d\u88c5\u50bb\uff0c\u987b\u77e5\u6b66\u6797\u4e4b\u4e2d\uff0c\u9664\u4e86\u5929\u9b54\u5973\u800c\u5916\uff0c\u522b\u4eba\u4e0d\u4f1a\u518d\u6709\u8fd9\u7b49\u8d64\u91d1\u6253\u7684\u7389\u5170\u91d1\u82b1\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u6697\u6697\u5403\u4e86\u4e00\u60ca\uff0c\u5fd6\u9053\uff1a\u201c\u539f\u6765\u8fd9\u91d1\u82b1\u662f\u90a3\u5929\u9b54\u5973\u7684\u72ec\u6709\u8868\u8bb0\uff0c\u96be\u602a\u5b8b\u8001\u521a\u624d\u5403\u60ca\u4e86\u3002\u201d \u3000\u3000\u4f46\u90a3\u5b89\u5029\u5374\u662f\u832b\u7136\u5e94\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u6211\u4e0d\u61c2\u554a\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8fd9\u7389\u5170\u91d1\u82b1\uff0c\u4e43\u662f\u5929\u9b54\u5973\u884c\u8d70\u6c5f\u6e56\u7684\u8868\u8bb0\uff0c\u59d1\u5a18\u8eab\u6709\u6b64\u7269\uff0c\u90a3\u5fc5\u7136\u662f\u5929\u9b54\u5973\u7684\u95e8\u4e0b\u4e86\u3002\u201d \u3000\u3000\u5b89\u5029\u5ffd\u7136\u5a07\u7b11\u9053\uff1a\u201c\u4f60\u771f\u662f\u4e00\u4e2a\u8001\u7cca\u6d82\uff0c\u8fd9\u91d1\u82b1\u4e0d\u8fc7\u662f\u4eba\u5bb6\u9001\u7ed9\u6211\u7684\uff0c\u4f60\u5c45\u7136\u628a\u6211\u8ba4\u4f5c\u4ec0\u4e48\u5929\u9b54\u5973\u95e8\u4e0b\uff0c\u4e0d\u53eb\u4eba\u7b11\u6389\u7259\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u77a7\u5979\u8bf4\u51fa\u6b64\u8bdd\u4e4b\u65f6\uff0c\u4e00\u6d3e\u5929\u771f\uff0c\u4e0d\u4f3c\u505a\u4f5c\u4e4b\u6001\uff0c\u5fc3\u4e2d\u5927\u5728\u5947\u602a\uff0c\u6014\u5f97\u4e00\u6014\uff0c\u65b9\u9053\uff1a\u201c\u53ef\u662f\u4e00\u4e2a\u957f\u53d1\u62ab\u80a9\u7684\u767d\u8863\u5c11\u5973\u9001\u7ed9\u4f60\u7684\u5417\uff1f\u201d \u3000\u3000\u5b89\u5029\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u8ba4\u5f97\u5979\u5417\uff1f\u201d \u3000\u3000\u5e73\u513f\u5728\u65c1\u63a5\u53e3\u9053\uff1a\u201c\u8001\u7cca\u6d82\uff0c\u516b\u6210\u4f60\u662f\u5979\u8001\u76f8\u597d\u4e86\uff0c\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u8fd9\u7b49\u8bdd\u51fa\u81ea\u4e00\u540d\u5929\u771f\u65e0\u90aa\u7684\u5c11\u5973\u53e3\u4e2d\uff0c\u771f\u4ee4\u5b8b\u5b5a\u5927\u4e3a\u5c34\u5c2c\uff0c\u4f46\u4ed6\u76ee\u7779\u90a3\u5e73\u513f\u8bf4\u8bdd\u65f6\u7684\u795e\u6001\uff0c\u4f3c\u662f\u6839\u672c\u4e0d\u4f46\u8fd9\u53e5\u8bdd\u4e2d\u7684\u90aa\u610f\uff0c\u53c8\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u5931\u58f0\u800c\u7b11\u3002 \u3000\u3000\u5f53\u4e0b\u6447\u5934\u9053\uff1a\u201c\u59d1\u5a18\u771f\u6b63\u8bf4\u9519\u4e86\uff0c\u8001\u673d\u4e0e\u90a3\u5929\u9b54\u5973\u662f\u654c\u975e\u53cb\u3002\u201d \u3000\u3000\u5e73\u513f\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4f60\u8ddf\u5979\u6709\u4ec7\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u5e73\u513f\u770b\u4e86\u5029\u513f\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5029\u59d0\u59d0\uff0c\u4ed6\u8ddf\u90a3\u4f4d\u59d1\u5a18\u6709\u4ec7\uff0c\u6211\u4eec\u5374\u8ddf\u5979\u662f\u670b\u53cb\uff0c\u4f9d\u4f60\u770b\uff0c\u8fd9\u8001\u7cca\u6d82\u6211\u4eec\u7b97\u662f\u670b\u53cb\uff0c\u8fd8\u662f\u4ec7\u654c\u5462\uff1f\u201d \u3000\u3000\u5b89\u5029\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u8fd9\u5f88\u96be\u8bb2\uff01\u201d \u3000\u3000\u5e73\u513f\u76b1\u7709\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b89\u5029\u9053\uff1a\u201c\u4ed6\u4eec\u4e0d\u662f\u5c0f\u59d0\u7ea6\u6765\u7684\u8d35\u5ba2\u5417\uff0c\u5728\u4ed6\u4eec\u6ca1\u6709\u8ddf\u5c0f\u59d0\u53cd\u8138\u4e4b\u65f6\uff0c\u6211\u4eec\u53ea\u6015\u4e0d\u80fd\u628a\u4ed6\u4eec\u5f53\u4f5c\u4ec7\u654c\u5566\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u9053\uff1a\u8fd9\u4e2b\u5934\u60f3\u7684\u5012\u4e5f\u4e0d\u9519\uff0c\u5c11\u65f6\u5982\u662f\u89c1\u5230\u5979\u4eec\u7684\u4e3b\u4eba\uff0c\u6700\u597d\u4e0d\u53cd\u8138\uff0c\u522b\u8ba9\u8fd9\u4e24\u4e2a\u4e2b\u5934\u6709\u673a\u4f1a\u66ff\u5929\u9b54\u5973\u5356\u529b\u3002 \u3000\u3000\u5b8b\u5b5a\u95fb\u8a00\uff0c\u5374\u662f\u6c89\u58f0\u9053\uff1a\u201c\u4e24\u4f4d\u7ad9\u5a18\uff0c\u4f60\u4eec\u53ef\u662f\u521d\u6b21\u5728\u6c5f\u6e56\u4e0a\u884c\u8d70\u5417\uff1f\u201d \u3000\u3000\u5029\u513f\u9053\uff1a\u201c\u662f\u554a\uff01\u54b1\u4eec\u521a\u521a\u8fdb\u5165\u4e2d\u539f\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e24\u4f4d\u6765\u81ea\u4f55\u5904\uff1f\u201d \u3000\u3000\u4ed6\u95ee\u5f97\u867d\u7136\u5f88\u5feb\uff0c\u4f46\u90a3\u5029\u513f\u5374\u4e5f\u673a\u7075\u5f97\u5f88\uff0c\u683c\u683c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u53cc\u7709\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u5201\u86ee\u5f97\u5f88\u3002\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u59d1\u5a18\u53ef\u77e5\u9053\uff0c\u90a3\u5929\u9b54\u5973\u4e43\u662f\u4e2a\u5f88\u5927\u7684\u574f\u4eba\u3002\u201d \u3000\u3000\u5e73\u513f\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u8c01\u76f8\u4fe1\uff1f\u6211\u770b\u4f60\u8fd9\u4e2a\u957f\u76f8\uff0c\u5012\u771f\u50cf\u662f\u4e00\u4e2a\u574f\u4eba\u5462\uff1f\u201d \u3000\u3000\u5b89\u5029\u5374\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u8001\u7cca\u6d82\uff0c\u6211\u5982\u4e0d\u662f\u770b\u5728\u4f60\u662f\u6211\u5bb6\u5c0f\u59d0\u8bf7\u6765\u7684\u5ba2\u4eba\u4efd\u4e0a\uff0c\u5c31\u51b2\u4f60\u80c6\u6562\u5f97\u7f6a\u4e86\u6211\u7684\u670b\u53cb\u4efd\u4e0a\uff0c\u4f60\u5c31\u6709\u5f97\u82e6\u5934\u597d\u5403\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u957f\u957f\u5730\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u4e24\u4f4d\u6d89\u4e16\u4e0d\u6df1\uff0c\u5bf9\u4e8e\u4e00\u4e2a\u4eba\u7684\u597d\u574f\uff0c\u81ea\u662f\u96be\u4ee5\u5206\u522b\u5f97\u6e05\u695a\uff0c\u5f88\u591a\u4eba\u5728\u5916\u8868\u4e0a\u770b\u6765\u6148\u5584\u53ef\u4eb2\uff0c\u4f46\u5374\u5185\u5fc3\u6076\u6bd2\u65e0\u6bd4\uff0c\u90a3\u5929\u9b54\u5973\u8c8c\u7f8e\u5982\u82b1\uff0c\u5fc3\u5982\u86c7\u874e\uff0c\u4e24\u4f4d\u59d1\u5a18\u548c\u5979\u4ea4\u5f80\uff0c\u53ea\u6015\u4eca\u540e\u5c11\u4e0d\u4e86\u8981\u5403\u5927\u4e8f\u7684\u4e86\u3002\u201d \u3000\u3000\u5e73\u513f\u4e24\u773c\u4e00\u7ffb\u9053\uff1a\u201c\u4e0d\u8981\u4f60\u7ba1\uff0c\u591a\u4e8b\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u8138\u8272\u4e00\u53d8\uff0c\u4f3c\u662f\u5c31\u5f85\u53d1\u4f5c\uff0c\u4f46\u4ed6\u5374\u53c8\u6447\u4e86\u6447\u5934\uff0c\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5fe0\u8a00\u9006\u8033\uff0c\u826f\u836f\u82e6\u53e3\uff0c\u8001\u673d\u8fd9\u756a\u771f\u662f\u591a\u4e8b\u4e86\u3002\u201d \u3000\u3000\u5e73\u513f\u9053\uff1a\u201c\u4f60\u660e\u767d\u5c31\u597d\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u672c\u662f\u8f6c\u52a8\u7740\u5ff5\u5934\uff0c\u60f3\u5728\u4e8c\u5973\u53e3\u4e2d\u95ee\u51fa\u90a3\u5929\u9b54\u5973\u4e0b\u843d\uff0c\u4f46\u542c\u5f97\u5b8b\u5b5a\u4e0e\u4ed6\u4eec\u5bf9\u7b54\u4e4b\u8a00\u4ee5\u540e\uff0c\u5ffd\u7136\u53d8\u4e86\u4e3b\u610f\uff0c\u5251\u7709\u4e00\u626c\uff0c\u9053\uff1a\u201c\u5b89\u59d1\u5a18\uff0c\u8d35\u4e3b\u4eba\u73b0\u5728\u4f55\u5904\uff1f\u201d \u3000\u3000\u5b89\u60c5\u8138\u8272\u6df1\u6c89\u5730\u9053\uff1a\u201c\u4f60\u8981\u89c1\u6211\u5bb6\u5c0f\u59d0\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8d35\u4e0a\u7559\u5b57\u76f8\u7ea6\uff0c\u65b9\u67d0\u4eba\u5df2\u7136\u5e94\u7ea6\u800c\u6765\uff0c\u4e3a\u4f55\u8fdf\u8fdf\u4e0d\u80af\u51fa\u73b0\uff1f\u201d \u3000\u3000\u5b89\u5029\u5ffd\u7136\u683c\u683c\u4e00\u7b11\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u8c01\u8bf4\u6211\u5bb6\u5c0f\u59d0\u8fdf\u8fdf\u4e0d\u80af\u51fa\u6765\u7740\uff0c\u53ea\u4e0d\u8fc7\u8fd8\u6ca1\u5230\u65f6\u5019\u7f62\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u76ee\u5149\u4e00\u8f6c\uff0c\u9053\uff1a\u201c\u96be\u9053\u8d35\u4e0a\u89c1\u4eba\uff0c\u8fd8\u6709\u4e00\u5b9a\u7684\u65f6\u523b\u5417\uff1f\u201d \u3000\u3000\u5b89\u5029\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u5bb6\u5c0f\u59d0\u89c1\u4eba\uff0c\u6b63\u662f\u6709\u4e00\u5b9a\u7684\u65f6\u8fb0\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4ec0\u4e48\u65f6\u5019\uff0c\u8d35\u4e0a\u65b9\u80af\u51fa\u89c1\u3002\u201d \u3000\u3000\u5b89\u5029\u9053\uff1a\u201c\u773c\u4e0b\u662f\u4ec0\u4e48\u65f6\u8fb0\u4e86\u3002\u201d \u3000\u3000\u5e73\u513f\u9053\uff1a\u201c\u4e11\u65f6\u5df2\u8fc7\u3002\u201d \u3000\u3000\u5b89\u5029\u9053\uff1a\u201c\u5feb\u4e86\uff0c\u8fd8\u6709\u4e00\u523b\u65f6\u5149\uff0c\u6211\u5bb6\u5c0f\u59d0\u6253\u5750\u9192\u6765\uff0c\u5c31\u4f1a\u63a5\u89c1\u4e24\u4f4d\u7684\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u672c\u60f3\uff0c\u4f46\u56e0\u5bf9\u65b9\u8bf4\u662f\u53ea\u6709\u4e00\u523b\u65f6\u5149\uff0c\u5c31\u53ef\u51fa\u89c1\uff0c\u5f53\u4e0b\u5c31\u5fcd\u4f4f\u4e86\u8fd9\u4e00\u53e3\u6c14\uff0c\u9053\uff1a \u3000\u3000\u201c\u597d\u5427\uff01\u65b9\u67d0\u5c31\u518d\u7b49\u4e00\u523b\u65f6\u5149\u3002\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u56de\u6597\u5411\u5b8b\u5b5a\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u54b1\u4eec\u518d\u7b49\u4e0a\u4e00\u523b\u65f6\u5149\u5427\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u5012\u65e0\u6240\u8c13\uff0c\u4ed6\u6b64\u523b\u5341\u5206\u5173\u5fc3\u90a3\u6735\u4f5c\u4e3a\u5929\u9b54\u5973\u8868\u8bb0\u7684\u91d1\u82b1\uff0c\u56e0\u4e3a\uff0c\u5982\u662f\u8fd9\u4e24\u4e2a\u4e2b\u5934\u7684\u4e3b\u4eba\u5e76\u975e\u662f\u5929\u9b54\u5973\u5c5e\u4e0b\uff0c\u5219\u5929\u9b54\u5973\u5ffd\u7136\u7528\u91d1\u82b1\u548c\u8fd9\u4e24\u540d\u4e2b\u5934\u7ed3\u4ea4\uff0c\u5fc5\u6709\u7740\u62c9\u62e2\u5979\u4eec\u4e4b\u5fc3\u3002 \u3000\u3000\u5b8b\u5b5a\u660e\u767d\u90a3\u5929\u9b54\u5973\u7684\u4e3a\u4eba\uff0c\u5982\u975e\u8fd9\u4e24\u4e2a\u4e2b\u5934\u7684\u4e3b\u4eba\u5341\u5206\u4e86\u5f97\uff0c\u5979\u53c8\u4f55\u5fc5\u6765\u7ed3\u4ea4\u8fd9\u4e24\u4e2a\u5c0f\u4e2b\u5934\uff1f\u8f6c\u5ff5\u81f3\u6b64\uff0c\u5374\u662f\u4e00\u7b11\u9053\uff1a\u201c\u6b63\u662f\uff01\u8001\u5f1f\uff0c\u54b1\u4eec\u5c31\u591a\u7b49\u4e0a\u4e00\u4f1a\uff0c\u90a3\u4e5f\u662f\u6ca1\u6709\u5173\u7cfb\u7684\u4e86\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5ffd\u7136\u7528\u4f20\u97f3\u4e4b\u672f\uff0c\u63a5\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u8fd9\u4e2b\u5934\u7684\u4e3b\u4eba\uff0c\u53ef\u80fd\u662f\u4f4d\u6b66\u6797\u5c11\u89c1\u7684\u9ad8\u624b\uff0c\u5426\u5219\u90a3\u5929\u9b54\u5973\u4e5f\u4e0d\u4f1a\u5df4\u7ed3\u5979\u7684\u4e0b\u4eba\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e5f\u7528\u4f20\u97f3\u4e4b\u672f\u5e94\u9053\uff1a\u201c\u665a\u8f88\u4e5f\u6709\u8fd9\u7b49\u60f3\u6cd5\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u63a5\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u867d\u7136\u53ea\u5269\u4e0b\u4e00\u523b\u65f6\u5149\uff0c\u4f46\u8001\u673d\u751a\u671b\u8001\u5f1f\u4e58\u7740\u8fd9\u70b9\u65f6\u523b\uff0c\u8c03\u8fd0\u4e00\u4e0b\u4f53\u5185\u771f\u6c14\uff0c\u5012\u662f\u7a0d\u65f6\u771f\u8981\u52a8\u624b\uff0c\u4e5f\u53ef\u591a\u5360\u51e0\u5206\u80dc\u673a\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u665a\u8f88\u9075\u547d\u2026\u2026\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u90a3\u5b89\u5029\u5ffd\u7136\u51b7\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u5728\u8bf4\u4ec0\u4e48\u554a\uff01\u201d \u3000\u3000\u6562\u60c5\u4e24\u4eba\u56e0\u662f\u7528\u7684\u4f20\u97f3\u4e4b\u672f\uff0c\u5b89\u5029\u53ea\u80fd\u770b\u5230\u4ed6\u4eec\u5634\u5507\u5fae\u52a8\uff0c\u5374\u542c\u4e0d\u5230\u4e24\u4eba\u8bf4\u8bdd\uff0c\u5fcd\u4e0d\u4f4f\u5c31\u95ee\u51fa\u53e3\u6765\u3002 \u3000\u3000\u5b8b\u5b5a\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4e0d\u662f\u8001\u673d\u548c\u65b9\u8001\u5f1f\u4e0d\u80af\u8bf4\u51fa\u8981\u8bf4\u7684\u8bdd\uff0c\u53ea\u56e0\u6b64\u4e8b\u4e43\u662f\u7537\u4eba\u4e4b\u4e8b\uff0c\u4f60\u4eec\u5973\u5b69\u513f\u5bb6\u662f\u542c\u4e0d\u5f97\u7684\u2026\u2026\u201d \u3000\u3000\u8bf4\u7f62\uff0c\u53c8\u662f\u4e00\u9635\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u5230\u5e95\u5979\u6bd4\u90a3\u5e73\u513f\u5927\u4e86\u51e0\u5c81\uff0c\u6240\u4ee5\u542c\u5f97\u5b8b\u8001\u4e4b\u8a00\uff0c\u82b3\u5fc3\u4e4b\u4e2d\u5c31\u60f3\u5230\u4e86\u90a3\u7537\u5973\u4e4b\u4e8b\u4e0a\u53bb\uff0c\u987f\u65f6\u7f9e\u5f97\u4f4e\u4e0b\u5934\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u8138\u4e0a\uff0c\u5374\u4e5f\u4ed9\u4ed9\u5730\u6709\u4e9b\u53d1\u70ed\uff0c\u4ed6\u89c9\u5f97\u5b8b\u8001\u8fd9\u4e2a\u73a9\u7b11\uff0c\u5f00\u5f97\u592a\u8fc7\u5206\uff0c\u4f46\u53c8\u96be\u4ee5\u89e3\u8bf4\uff0c\u5f53\u4e0b\u53ea\u597d\u95ed\u4e0a\u53cc\u76ee\uff0c\u88c5\u4f5c\u4e86\u6b63\u5728\u8fd0\u6c14\u8c03\u606f\u4e4b\u72b6\u3002 \u3000\u3000\u4e00\u65f6\u95f4\uff0c\u5927\u5385\u4e4b\u5185\uff0c\u4e00\u7247\u5bc2\u7136\u3002 \u3000\u3000\u90a3\u5e73\u513f\u4f3c\u662f\u4e0d\u8010\u8fd9\u7b49\u60c5\u666f\uff0c\u661f\u76ee\u4e00\u8f6c\uff0c\u683c\u683c\u4e00\u7b11\uff0c\u5411\u5b89\u5029\u9053\uff1a\u201c\u5029\u59d0\u59d0\uff0c\u6211\u8fdb\u53bb\u77a7\u77a7\u59d1\u5a18\u597d\u5417\uff1f\u201d \u3000\u3000\u5b89\u5029\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u5982\u662f\u59d1\u5a18\u5df2\u7ecf\u8d77\u6765\u4e86\uff0c\u5c31\u8bf4\u4ed6\u4eec\u4fe9\u4e1a\u5df2\u5e94\u7ea6\u6765\u6b64\u4e86\u3002\u201d \u3000\u3000\u5e73\u513f\u7b11\u9053\uff1a\u201c\u6211\u6653\u5f97\u4e86\u2026\u2026\u201d\u4e00\u7529\u5934\uff0c\u8df3\u5440\u8df3\u7684\u5411\u5927\u5385\u540e\u4fa7\u7684\u4e00\u6247\u95e8\u4e2d\u8dd1\u53bb\u3002 \u3000\u3000\u8fc7\u4e86\u4e0d\u4e45\uff0c\u53ea\u89c1\u90a3\u5e73\u513f\u6253\u90a3\u7389\u77f3\u5c4f\u98ce\u4e4b\u540e\uff0c\u8f6c\u4e86\u8fc7\u6765\uff0c\u5a07\u58f0\u9053\uff1a\u201c\u5029\u59d0\uff0c\u59d1\u5a18\u6765\u5566\u3002\u201d \u3000\u3000\u5b89\u5029\u95fb\u8a00\uff0c\u7acb\u5373\u7ad9\u8d77\u8eab\u5b50\uff0c\u79fb\u6b65\u8d70\u5230\u7389\u77f3\u5c4f\u98ce\u8fb9\uff0c\u795e\u60c5\u751a\u662f\u606d\u8c28\u5730\u4f8d\u7acb\u7740\uff0c\u4e00\u53cc\u5999\u76ee\uff0c\u5374\u5c04\u5411\u5c4f\u98ce\u4e4b\u540e\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u65f6\u4e5f\u7741\u5f00\u4e86\u53cc\u773c\uff0c\u5411\u90a3\u7389\u77f3\u5c4f\u98ce\u4e4b\u540e\u671b\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u5728\u90a3\u5e73\u513f\u8eab\u540e\uff0c\u679c\u7136\u51fa\u73b0\u4e00\u4f4d\u957f\u53d1\u5782\u80a9\uff0c\u8eab\u6001\u52a8\u4eba\uff0c\u9762\u8c8c\u6781\u4e3a\u6e05\u79c0\uff0c\u6c14\u8d28\u9ad8\u96c5\u7684\u767d\u8863\u5c11\u5973\uff0c\u770b\u5979\u7684\u5e74\u7eaa\uff0c\u5927\u7ea6\u5728\u5341\u4e03\u5c81\u5de6\u53f3\uff0c\u4f46\u4e3e\u6b62\u4e4b\u95f4\uff0c\u5374\u662f\u5341\u5206\u5e84\u91cd\uff0c\u662f\u4ee5\u4ee4\u4eba\u671b\u53bb\u6709\u7740\u9ad8\u4e0d\u53ef\u6500\u4e4b\u611f\u3002 \u3000\u3000\u6b64\u523b\uff0c\u5979\u5df2\u7f13\u7f13\u5730\u8d70\u5230\u7389\u6848\u4e4b\u540e\uff0c\u5750\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u90a3\u5029\u513f\u79fb\u6b65\u8d70\u5230\u5979\u8eab\u524d\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u59d1\u5a18\u2026\u2026\u201d \u3000\u3000\u90a3\u767d\u8863\u5c11\u5973\u4e00\u76f4\u662f\u7f8e\u76ee\u4f4e\u5782\uff0c\u7c89\u8138\u4e0a\u6ca1\u6709\u4e1d\u6beb\u8868\u60c5\uff0c\u8fd9\u65f6\u5ffd\u7136\u70b9\u4e86\u70b9\u5934\uff0c\u4f4e\u58f0\u9053\uff1a \u3000\u3000\u201c\u6211\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u867d\u7136\u5979\u8bf4\u8bdd\u7684\u58f0\u97f3\u5f88\u4f4e\uff0c\u4f46\u542c\u6765\u6709\u5982\u9ec4\u83ba\u51fa\u8c37\uff0c\u60a6\u8033\u5f97\u5f88\u3002 \u3000\u3000\u5029\u513f\u95fb\u8a00\uff0c\u9000\u4e86\u534a\u6b65\uff0c\u4e0d\u518d\u8bf4\u8bdd\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u65f6\u5ffd\u7136\u5251\u7709\u4e00\u626c\uff0c\u7ad9\u8d77\u8eab\u6765\uff0c\u62b1\u62f3\u9053\uff1a\u201c\u5728\u4e0b\u65b9\u96ea\u5b9c\uff0c\u8fd9\u53a2\u6709\u793c\u4e86\u3002\u201d \u3000\u3000\u90a3\u957f\u53d1\u5c11\u5973\u5fae\u62ac\u661f\u7738\uff0c\u5ae3\u7136\u4e00\u7b11\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u6709\u52b3\u65b9\u516c\u5b50\u4e45\u5019\u3002\u201d \u3000\u3000\u5979\u4eba\u751f\u7684\u5f88\u7f8e\uff0c\u8fd9\u65f6\u4e00\u7b11\uff0c\u66f4\u662f\u5b9b\u82e5\u6625\u82b1\u4e4d\u653e\uff0c\u53ea\u628a\u65b9\u96ea\u5b9c\u77a7\u7684\u5446\u4e86\u4e00\u5446\uff0c\u6697\u9053\uff1a \u3000\u3000\u201c\u5979\u7b11\u5f97\u771f\u52a8\u4eba\u2026\u2026\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u5b8b\u5b5a\u4e5f\u8d77\u8eab\u5927\u58f0\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u8001\u673d\u895f\u5b5a\u89c1\u59d1\u5a18\u3002\u201d \u3000\u3000\u957f\u53d1\u5c11\u5973\u6b20\u4e86\u6b20\u8eab\uff0c\u9053\uff1a\u201c\u4e0d\u6562\u5f53\uff0c\u5b8b\u8001\u4e43\u662f\u524d\u8f88\u9ad8\u4eba\uff0c\u5c0f\u5973\u5b50\u600e\u6562\u5f53\u5f97\u4f60\u8001\u8fd9\u822c\u79f0\u8c13\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u4e0d\u7b49\u5979\u8bf4\u5b8c\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u59d1\u5a18\u662f\u54ea\u4f4d\u9ad8\u4eba\u5b50\u5f1f\uff1f\u624d\u673d\u773c\u62d9\u8bc6\u6d45\uff0c\u5c1a\u76fc\u59d1\u5a18\u83ab\u8981\u89c1\u7b11\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4ece\u4e00\u5f00\u53e3\uff0c\u5c31\u60f3\u95ee\u660e\u8fd9\u4f4d\u5c11\u5973\u7684\u8eab\u5bb6\u59d3\u540d\uff0c\u4f46\u5374\u4e00\u76f4\u672a\u80fd\u60f3\u51fa\u5e94\u8be5\u600e\u751f\u8bf4\u6cd5\uff0c\u624d\u4e0d\u4f1a\u5510\u7a81\u4f73\u4eba\uff0c\u6b64\u523b\u542c\u5230\u5b8b\u8001\u8fd9\u4e48\u4e00\u95ee\uff0c\u4e0d\u7981\u6697\u81ea\u5fd6\u9053\uff1a\u201c\u5230\u5e95\u59dc\u662f\u8001\u7684\u8fa3\uff0c\u5982\u662f\u6362\u4e86\u6211\uff0c\u53ef\u5c31\u60f3\u4e0d\u51fa\u7528\u4e0a\u8fd9\u7b49\u501f\u8f9e\u4e86\u3002\u201d \u3000\u3000\u90a3\u957f\u53d1\u767d\u8863\u5c11\u5973\u76ee\u5149\u5728\u65b9\u96ea\u5b9c\u8eab\u4e00\u8f6c\uff0c\u8fd9\u624d\u5411\u5b8b\u8001\u9053\uff1a\u201c\u6211\u53eb\u5b89\u5c0f\u840d\uff0c\u4e43\u4e1c\u6d77\u5929\u5fc3\u5c9b\u4eba\u6c0f\uff0c\u6d77\u9685\u9057\u6c11\uff0c\u751a\u5c11\u8fdb\u5165\u4e2d\u539f\uff0c\u5b8b\u8001\u4e0d\u77e5\uff0c\u90a3\u4e5f\u7b97\u4e0d\u5f97\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u539f\u6765\u662f\u5b89\u59d1\u5a18\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8033\u542c\u5f97\u4e1c\u6d77\u4e24\u5b57\uff0c\u5fc3\u4e2d\u987f\u65f6\u4e00\u60ca\uff0c\u6697\u9053\uff1a\u201c\u516b\u6210\u662f\u4e86\u2026\u2026\u201d\u4f46\u4ed6\u53e3\u4e2d\u5374\u8131\u53e3\u8bf4\u9053\uff1a\u201c\u5b89\u59d1\u5a18\u662f\u4e1c\u6d77\u4eba\u6c0f\u5417\uff1f\u201d \u3000\u3000\u90a3\u5b89\u5c0f\u840d\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u4e0d\u9519\u554a\uff0c\u8d31\u59be\u6b63\u662f\u81ea\u5e7c\u751f\u957f\u5728\u4e1c\u6d77\u5929\u5fc3\u5c9b\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u90a3\u201c\u4e1c\u6d77\u53cc\u4ed9\u201d\u56db\u5b57\uff0c\u5dee\u4e00\u70b9\u5c31\u4ece\u4ed6\u53e3\u4e2d\u6e9c\u51fa\u6765\uff0c\u4f46\u4ed6\u8fd9\u4e9b\u65e5\u6765\uff0c\u5728\u5fc3\u673a\u4e0a\u5df2\u7136\u5927\u6709\u8fdb\u6b65\uff0c\u662f\u4ee5\uff0c\u4ed6\u867d\u7136\u5341\u5206\u6000\u7591\u8fd9\u5973\u5b50\u5c31\u662f\u201c\u5929\u9e64\u65a9\u201d\u6b66\u529f\u7684\u4f20\u4eba\uff0c\u4e1c\u6d77\u53cc\u4ed9\u7684\u540e\u4ee3\uff0c\u53ef\u662f\uff0c\u8bdd\u5230\u53e3\u8fb9\uff0c\u5374\u53c8\u5fcd\u4f4f\uff0c\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\u4ee4\u5c5e\u4e0b\u4e4b\u4eba\uff0c\u7559\u5b57\u76f8\u9080\u5728\u4e0b\uff0c\u4e0d\u77e5\u4e3a\u4e86\u4f55\u6545\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u8138\u4e0a\u5fae\u73b0\u6655\uff0c\u9053\uff1a\u201c\u65b9\u516c\u5b50\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u4e0d\u5fc5\u8fd9\u7b49\u5ba2\u6c14\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u65b9\u516c\u5b50\u53ef\u662f\u90a3\u5251\u795e\u9648\u5927\u4fa0\u7684\u5f1f\u5b50\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u6014\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5251\u795e\u6b63\u662f\u5bb6\u5e08\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u8fd9\u5c31\u6ca1\u6709\u9519\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u9053\uff1a\u201c\u597d\u554a\uff01\u5979\u679c\u7136\u662f\u8bda\u5fc3\u5728\u627e\u6211\u2026\u2026\u201d\u4f46\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u59d1\u5a18\u5bfb\u627e\u5728\u4e0b\uff0c\u53ef\u662f\u6709\u4ec0\u4e48\u4e8b\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u59be\u8eab\u7559\u5b57\u76f8\u7ea6\uff0c\u90a3\u81ea\u7136\u662f\u6709\u4e8b\u7684\u4e86\u3002\u201d\u8bdd\u97f3\u987f\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5251\u795e\u9648\u5927\u4fa0\uff0c\u4e0d\u77e5\u73b0\u5728\u4f55\u5904\u9690\u5c45\uff0c\u516c\u5b50\u53ef\u5426\u89c1\u544a\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u7591\u8651\u91cd\u91cd\uff0c\u5fd6\u9053\uff1a\u201c\u5979\u539f\u6765\u662f\u627e\u6211\u6069\u5e08\u7684\u5417\uff1f\u201d \u3000\u3000\u8f6c\u5ff5\u4e4b\u95f4\uff0c\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u5bb6\u5e08\u5df2\u7136\u4ed9\u53bb\u4e86\u3002\u201d \u3000\u3000\u90a3\u957f\u53d1\u5c11\u5973\u5b89\u5c0f\u840d\u5ffd\u7136\u4e00\u5446\uff0c\u9053\uff1a\u201c\u9648\u5927\u4fa0\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5148\u5e08\u53bb\u4e16\u4e4b\u524d\uff0c\u5728\u4e0b\u5b88\u5019\u5728\u4fa7\uff0c\u4eb2\u624b\u529e\u7406\u4e00\u5207\u846c\u793c\uff0c\u81ea\u662f\u4e0d\u4f1a\u6709\u9519\u7684\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6c89\u541f\u9053\uff1a\u201c\u9648\u5927\u4fa0\u6709\u51e0\u4f4d\u5f1f\u5b50\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u9053\uff1a\u5979\u4e3a\u4f55\u95ee\u8fd9\u4e2a\u5462\uff0c\u4ed6\u53e3\u4e2d\u5374\u63a5\u9053\uff1a\u201c\u53ea\u6709\u65b9\u67d0\u4e00\u4eba\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u53ea\u6709\u65b9\u516c\u5b50\u4e00\u4f4d\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u636e\u5148\u5e08\u544a\u77e5\uff0c\u5b9e\u662f\u53ea\u6709\u5728\u4e0b\u4e00\u4eba\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u674f\u773c\u5706\u7741\uff0c\u77aa\u7740\u65b9\u96ea\u5b9c\u534a\u664c\uff0c\u65b9\u59cb\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u65b9\u516c\u5b50\u5df2\u7136\u627f\u53d7\u4e86\u5251\u795e\u9648\u5927\u4fa0\u7684\u4e00\u8eab\u6b66\u529f\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u867d\u662f\u6df1\u6df1\u4e3a\u4e4b\u56f0\u6270\uff0c\u4f46\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u65b9\u67d0\u5e74\u8f7b\u624d\u5f31\uff0c\u6210\u5c31\u96be\u6bd4\u5148\u5e08\u767e\u5206\u4e4b\u4e00\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u516c\u5b50\u5f88\u8c26\u865a\u554a\uff01\u201d\u8bdd\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u90a3\u9b54\u5200\u7530\u8fdc\u5728\u6b66\u6797\u4e2d\u4e5f\u5f88\u6709\u540d\u671b\uff0c\u5374\u5728\u516c\u5b50\u4e00\u5251\u4e4b\u4e0b\u4e27\u547d\uff0c\u8db3\u89c1\u516c\u5b50\u4e00\u8eab\u6b66\u529f\uff0c\u5df2\u5f97\u9648\u5927\u4fa0\u7684\u771f\u4f20\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u4e86\u4e00\u5446\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u7b49\u4e8b\u5979\u600e\u4e48\u77e5\u9053\u7684\u5462\uff1f\u201d \u3000\u3000\u4f46\u4ed6\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u59d1\u5a18\u8fc7\u5956\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5ffd\u7136\u4e00\u7b11\u9053\uff1a\u201c\u8fd8\u6709\u90a3\u67f3\u5a9a\u5a18\uff0c\u5979\u7684\u6b66\u529f\u6bd4\u90a3\u7530\u8fdc\u9ad8\u660e\u591a\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u65b9\u516c\u5b50\u4e0d\u4e5f\u662f\u80dc\u4e86\u5979\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4fa5\u5e78\u800c\u5df2\uff0c\u59d1\u5a18\u5c82\u80fd\u8ba4\u771f\uff1f\u5018\u662f\u90a3\u67f3\u59d1\u5a18\u5168\u529b\u4e00\u62fc\uff0c\u5728\u4e0b\u53ef\u5c31\u4e0d\u662f\u5979\u7684\u654c\u624b\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u516c\u5b50\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u59d1\u5a18\u8fd8\u6709\u4ec0\u4e48\u4e8b\u95ee\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u5f53\u7136\u6709\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5728\u4e0b\u662f\u5e94\u9080\u800c\u6765\uff0c\u59d1\u5a18\u6709\u4ec0\u4e48\u8bdd\uff0c\u4f55\u4e0d\u5feb\u8bf4\uff1f\u201d \u3000\u3000\u201c\u5728\u4e0b\u2014\u2014\u201d\u4ed6\u8bed\u97f3\u987f\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5b8b\u8001\u4e0e\u5728\u4e0b\u5c1a\u6709\u8981\u4e8b\u5728\u8eab\uff0c\u4e0d\u4fbf\u4e45\u7559\u6b64\u95f4\u5462\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7565\u4e00\u6c89\u541f\uff0c\u9053\uff1a\u201c\u59be\u8eab\u2026\u2026\u6709\u4e00\u4ef6\u4e8b\uff0c\u60f3\u5411\u516c\u5b50\u8bf7\u6559\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u6697\u9053\uff1a\u8fd9\u4f4d\u59d1\u5a18\u597d\u751f\u5947\u602a\uff0c\u6709\u4ec0\u4e48\u4e8b\u600e\u4e0d\u75db\u5feb\u7684\u8bf4\u51fa\u6765\uff0c\u5374\u8fd9\u7b49\u541e\u541e\u5410\u5410\u4f5c\u751a\uff1f \u3000\u3000\u8f6c\u5ff5\u4e4b\u95f4\uff0c\u53e3\u4e2d\u63a5\u9053\uff1a\u201c\u59d1\u5a18\u8bf7\u6559\u4e0d\u6562\uff0c\u6709\u4ec0\u4e48\u8bdd\uff0c\u8bf7\u8bb2\u5f53\u9762\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4ee4\u5e08\u4ed9\u53bb\u4e4b\u524d\uff0c\u4e0d\u77e5\u662f\u5426\u544a\u8bc9\u8fc7\u4f60\uff0c\u5728\u90a3\u4e1c\u6d77\u4e4b\u6ee8\uff0c\u6709\u4e00\u4f4d\u6545\u4ea4\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u6709\u5fc3\u4e2d\u4ed8\u9053\uff1a\u4e1c\u6d77\u4e4b\u6ee8\uff0c\u53ea\u6015\u6ca1\u6709\u6545\u4ea4\uff0c\u800c\u662f\u6709\u7740\u514b\u5236\u9f99\u884c\u516b\u5251\u6b66\u529f\u7684\u5bf9\u5934\u5427\uff01 \u3000\u3000\u4f46\u4ed6\u53e3\u4e2d\u81ea\u662f\u4e0d\u597d\u8fd9\u7b49\u8bf4\u5c06\u51fa\u6765\uff0c\u6447\u5934\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u6ca1\u6709\u554a\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u95fb\u8a00\uff0c\u4f3c\u662f\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5251\u795e\u9648\u5927\u4fa0\u5f53\u771f\u6ca1\u6709\u63d0\u8d77\u8fc7\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u786e\u5b9e\u6ca1\u6709\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5ffd\u7136\u957f\u957f\u7684\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u662f\u4e86\uff0c\u9648\u5927\u4fa0\u6216\u8bb8\u662f\u65e9\u5df2\u5fd8\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5148\u5e08\u5982\u662f\u4e0d\u66fe\u5fd8\u8bb0\uff0c\u5e94\u662f\u65e9\u5df2\u544a\u77e5\u5728\u4e0b\u6b64\u4e8b\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8bdd\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u59d1\u5a18\u53ef\u8ba4\u8bc6\u90a3\u4e94\u9b54\u4e3b\u4e2d\u7684\u5929\u9b54\u5973\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u67f3\u7709\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u5929\u9b54\u5973\u5417\uff0c\u59be\u8eab\u89c1\u8fc7\u4e00\u9762\uff0c\u5e76\u65e0\u6df1\u4ea4\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u5979\u4e0e\u5929\u9b54\u5973\u5e76\u65e0\u6df1\u4ea4\uff0c\u987f\u65f6\u653e\u5fc3\u4e0d\u5c11\uff0c\u8131\u53e3\u95ee\u9053\uff1a\u201c\u59d1\u5a18\u53ef\u77e5\u90a3\u5929\u9b54\u5973\u73b0\u5728\u4f55\u5904\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u59be\u8eab\u662f\u5728\u96c1\u8361\u5c71\u9047\u5230\u5979\uff0c\u800c\u4eca\u7b97\u6765\u5df2\u6709\u4e00\u4e2a\u591a\u6708\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5979\u8fd8\u5728\u96c1\u8361\u5c71\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6447\u5934\u9053\uff1a\u201c\u8fd9\u4e2a\u53ef\u5c31\u4e0d\u77e5\u9053\u4e86\u3002\u201d\u4f46\u5979\u8bed\u97f3\u7565\u987f\uff0c\u7acb\u5373\u53c8\u63a5\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u90a3\u5929\u9b54\u5973\u59d0\u59d0\u5bf9\u59be\u8eab\u63d0\u8fc7\uff0c\u5979\u4e0d\u4e45\u8981\u53bb\u5d69\u5c71\u5c11\u6797\u4e00\u884c\uff0c\u516c\u5b50\u5982\u679c\u8981\u627e\u5979\uff0c\u4f55\u4e0d\u53bb\u81f3\u5c11\u6797\u8d70\u4e0a\u4e00\u8d9f\u5462\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u95fb\u8a00\uff0c\u5fc3\u4e2d\u5927\u5927\u5403\u4e86\u4e00\u60ca\uff1b\u5fd6\u9053\uff1a\u201c\u83ab\u975e\u8fd9\u5929\u9b54\u5973\u8981\u4e0d\u5229\u4e8e\u5c11\u6797\u7684\u548c\u5c1a\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e5f\u6123\u4e86\u4e00\u6123\uff0c\u9053\uff1a\u201c\u90a3\u5929\u9b54\u5973\u8981\u53bb\u5c11\u6797\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u662f\u5979\u4eb2\u53e3\u544a\u77e5\u59be\u8eab\u7684\uff0c\u60f3\u5fc5\u4e0d\u4f1a\u9a97\u4eba\u7684\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u56de\u987e\u4e86\u5b8b\u5b5a\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u5979\u2026\u2026\u8fd9\u5929\u9b54\u5973\u53ef\u662f\u8981\u5bf9\u90a3\u5c11\u6797\u5bfa\u4e2d\u7684\u548c\u5c1a\u4eec\u4e0d\u5229\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5ffd\u7136\u4e00\u7b11\u9053\uff1a\u201c\u65b9\u516c\u5b50\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000 \u7b2c\u5341\u516d\u56de\u3000\u4e1c\u6d77\u5a07\u5a03 \u3000 \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u516c\u5b50\u8ddf\u5c11\u6797\u5bfa\u4e2d\u7684\u50e7\u4fa3\u5f88\u6709\u4ea4\u60c5\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u5934\u9053\uff1a\u201c\u7d20\u65e0\u76f8\u8bc6\u4e4b\u4eba\uff0c\u81ea\u662f\u8c08\u4e0d\u4e0a\u4ec0\u4e48\u4ea4\u60c5\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u65e2\u65e0\u76f8\u8bc6\u4e4b\u4eba\uff0c\u516c\u5b50\u4e3a\u4f55\u4f3c\u662f\u5f88\u5173\u5fc3\u4ed6\u4eec\u7684\u4e8b\u60c5\u5462\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u76b1\u7709\u9053\uff1a\u201c\u5c11\u6797\u4e00\u6d3e\uff0c\u7d20\u4e3a\u6b66\u6797\u540c\u9053\u94a6\u4ef0\uff0c\u4e3a\u6b66\u6797\u4e2d\u7684\u6cf0\u5c71\u5317\u6597\uff0c\u51e1\u662f\u8eab\u4e3a\u6b66\u6797\u7684\u4eba\u7269\uff0c\u90a3\u81ea\u7136\u662f\u5341\u5206\u5173\u5fc3\u5c11\u6797\u7684\u76db\u8870\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u5c11\u6797\u7684\u76db\u8870\uff0c\u5bf9\u6b66\u6797\u4e2d\u7adf\u662f\u6709\u7740\u8fd9\u7b49\u91cd\u8981\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u592a\u91cd\u8981\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u63a5\u9053\uff1a\u201c\u5b89\u59d1\u5a18\uff0c\u4f60\u53ef\u662f\u5f88\u5c11\u5728\u6b66\u6797\u4e2d\u8d70\u52a8\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u70b9\u5934\u9053\uff1a\u201c\u8fd9\u5c31\u96be\u602a\u7684\u4e86\u3002\u201d\u957f\u957f\u5730\u5401\u4e86\u53e3\u6c14\uff0c\u63a5\u9053\uff1a\u201c\u6b66\u6797\u4e4b\u4e2d\uff0c\u9b54\u9053\u548c\u9ed1\u9053\u4eba\u7269\uff0c\u4e00\u76f4\u4e0d\u80fd\u5c06\u767d\u9053\u670b\u53cb\u51fb\u6e83\u6740\u7edd\uff0c\u5c11\u6797\u4e00\u6d3e\uff0c\u53ef\u662f\u6709\u7740\u6781\u5927\u7684\u529f\u52b3\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4ed6\u4eec\u7684\u6b66\u529f\u5f88\u597d\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u5c11\u6797\u5bfa\u6709\u7740\u4e03\u5341\u4e8c\u79cd\u7edd\u6280\uff0c\u6b66\u529f\u5f88\u597d\uff0c\u90a3\u662f\u4e0d\u8db3\u4ee5\u5f62\u5bb9\u7684\u4e86\u3002\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u662f\u5c11\u6797\u6b66\u529f\uff0c\u6bd4\u5979\u81ea\u4e2d\u6240\u8bf4\u7684\u5f88\u597d\u4e24\u5b57\uff0c\u4e0d\u77e5\u8981\u9ad8\u8fc7\u591a\u5c11\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u5ae3\u7136\u4e00\u7b11\u9053\uff1a\u201c\u65b9\u516c\u5b50\uff0c\u90a3\u5c11\u6797\u6b66\u5b66\uff0c\u6bd4\u4e4b\u5251\u795e\u9648\u5927\u4fa0\u600e\u6837\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u8fd9\u4e8b\u5728\u4e0b\u53ef\u5c31\u4e0d\u660e\u767d\u4e86\uff0c\u800c\u4e14\uff0c\u5148\u5e08\u4f3c\u662f\u5e76\u672a\u4e0e\u5c11\u6797\u548c\u5c1a\u52a8\u8fc7\u624b\uff0c\u81ea\u662f\u4e0d\u77e5\u9053\u4f18\u52a3\u7684\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5ffd\u7136\u5411\u5b8b\u5b5a\u9053\uff1a\u201c\u5b8b\u8001\u89c1\u591a\u8bc6\u5e7f\uff0c\u60f3\u5fc5\u662f\u77e5\u9053\u7684\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u6c89\u541f\u9053\uff1a\u201c\u8fd9\u4e5f\u5f88\u96be\u8bb2\uff0c\u9648\u5927\u4fa0\u4e43\u662f\u6570\u5341\u5e74\u6765\u552f\u4e00\u7684\u5c11\u89c1\u7684\u5947\u624d\u5f02\u80fd\u4e4b\u4eba\uff0c\u5c11\u6797\u5bfa\u73b0\u6709\u7684\u50e7\u4fa3\u4e4b\u4e2d\uff0c\u81ea\u662f\u65e0\u4eba\u80fd\u6709\u90a3\u7b49\u6210\u5c31\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u5b8b\u8001\u53ef\u662f\u8bf4\u9648\u5927\u4fa0\u6bd4\u4ed6\u4eec\u9ad8\u660e\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4ee5\u4e2a\u4eba\u6b66\u529f\u800c\u8a00\uff0c\u5f53\u4e16\u4e4b\u4e2d\uff0c\u5e94\u6570\u9648\u5927\u4fa0\u5217\u540d\u7b2c\u4e00\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6251\u54e7\u4e00\u58f0\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u4fe1\u3002\u201d \u3000\u3000\u4f46\u5979\u7acb\u5373\u63a5\u9053\uff1a\u201c\u5b8b\u8001\u65b9\u624d\u4e4b\u610f\uff0c\u597d\u50cf\u662f\u8bf4\u5c11\u6797\u5bfa\u4e2d\u65e0\u4eba\u80dc\u5f97\u8fc7\u9648\u5927\u4fa0\uff0c\u4f46\u5374\u53c8\u4e0d\u627f\u8ba4\u9648\u5927\u4fa0\u7684\u6b66\u529f\uff0c\u9ad8\u8fc7\u90a3\u5c11\u6797\u4e00\u6d3e\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u5c11\u6797\u4e03\u5341\u4e8c\u79cd\u7edd\u6280\uff0c\u5176\u4e2d\u4e8c\u5341\u51e0\u79cd\uff0c\u4ece\u6765\u65e0\u4eba\u7ec3\u6210\uff0c\u5426\u5219\uff0c\u6c5f\u6e56\u4e0a\u7684\u725b\u9b3c\u86c7\u795e\uff0c\u65e9\u5df2\u9500\u58f0\u655b\u8ff9\uff0c\u4e0d\u6562\u4e3a\u975e\u505a\u6b79\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u60f3\u4e86\u4e00\u60f3\uff0c\u5fae\u54ac\u7740\u6a31\u5507\uff0c\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u5982\u662f\u6709\u4eba\u7ec3\u6210\u5c11\u6797\u90a3\u4e8c\u5341\u591a\u79cd\u7edd\u5b66\uff0c\u5c31\u53ef\u80dc\u8fc7\u9648\u5927\u4fa0\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u53ef\u60dc\u5c11\u6797\u81ea\u4ece\u7b2c\u5341\u516d\u4ee3\u638c\u95e8\u4ee5\u540e\uff0c\u5c31\u65e0\u4eba\u6709\u8fc7\u90a3\u7b49\u6210\u5c31\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u7167\u4f60\u8fd9\u4e48\u8bb2\uff0c\u5c11\u6797\u7edd\u5b66\u5931\u4f20\uff0c\u5929\u9b54\u5973\u662f\u5426\u5bf9\u4ed6\u4eec\u4e0d\u5229\uff0c\u5e94\u662f\u5173\u7cfb\u6b66\u6797\u52ab\u8fd0\u4e0d\u5927\u554a\uff01\u201d\u663e\u7136\uff0c\u5979\u515c\u4e86\u4e2a\u5708\u5b50\uff0c\u9a73\u5012\u4e86\u5b8b\u5b5a\u90a3\u5f3a\u8c03\u5c11\u6797\u5bfa\u5341\u5206\u91cd\u8981\u7684\u8bdd\u9898\u4e86\u3002 \u3000\u3000\u5b8b\u5b5a\u6447\u5934\u4e00\u7b11\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u90a3\u5c11\u6797\u5bfa\u867d\u7136\u773c\u4e0b\u5e76\u65e0\u51fa\u7c7b\u62d4\u8403\u4e4b\u4eba\uff0c\u4f46\u5c11\u6797\u50e7\u4fa3\u4f17\u591a\uff0c\u4eba\u4eba\u7686\u6709\u4e00\u79cd\u6b66\u529f\uff0c\u53ea\u8981\u5c11\u6797\u5bfa\u4e00\u65e5\u4e0d\u6ca1\uff0c\u6b66\u6797\u6b63\u4e49\uff0c\u5c31\u7b49\u4e8e\u4e00\u65e5\u5c1a\u5728\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u90a3\u5c11\u6797\u5bfa\u5728\u6b66\u6797\u4e2d\u7684\u58f0\u5a01\uff0c\u5012\u662f\u4e0d\u5c0f\u554a\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u7ef4\u7cfb\u6b66\u6797\u540c\u9053\u5411\u5584\u4e4b\u5fc3\uff0c\u5c11\u6797\u50e7\u4fa3\u679c\u7136\u79ef\u4e86\u4e0d\u5c11\u529f\u5fb7\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u65b9\u516c\u5b50\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u59d1\u5a18\u6709\u4f3a\u6307\u6559\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u90a3\u5929\u9b54\u5973\u5982\u662f\u771f\u6709\u5bf9\u5c11\u6797\u4e0d\u5229\u4e4b\u5fc3\uff0c\u516c\u5b50\u662f\u5426\u8981\u53bb\u89e3\u6551\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u201d\u4ed6\u5ffd\u7136\u60f3\u8d77\uff0c\u81ea\u5df1\u672c\u8eab\u7684\u4e8b\u5df2\u7136\u7275\u626f\u4e0d\u6e05\uff0c\u662f\u5426\u5c1a\u6709\u4f59\u529b\u53bb\u89e3\u6551\u5c11\u6797\u6839\u672c\u6beb\u65e0\u628a\u63e1\u3002\u662f\u4ee5\uff0c\u8bdd\u5230\u53e3\u8fb9\uff0c\u7591\u800c\u4e0d\u51b3\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u516c\u5b50\u83ab\u975e\u4e0d\u60f3\u53bb\u89e3\u6551\u5c11\u6797\u4e4b\u56f0\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6c89\u541f\u9053\uff1a\u201c\u5728\u4e0b\u679c\u6709\u6b64\u5fc3\uff0c\u53ea\u662f\u773c\u4e0b\u5374\u53c8\u5206\u8eab\u4e0d\u5f97\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4f60\u662f\u4e0d\u662f\u771f\u5fc3\u60f3\u53bb\u89e3\u90a3\u5c11\u6797\u4e4b\u56f4\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5728\u4e0b\u81ea\u7136\u662f\u771f\u5fc3\u7684\u4e86\uff0c\u53ea\u662f\u2026\u2026\u53ea\u662f\u2026\u2026\u5728\u4e0b\u4e00\u65f6\u522b\u6709\u8981\u4e8b\u5728\u8eab\uff0c\u4f55\u51b5\uff0c\u90a3\u5929\u9b54\u5973\u7684\u6b66\u529f\uff0c\u53c8\u5341\u5206\u9ad8\u5f3a\uff0c\u5728\u4e0b\u5373\u4f7f\u80fd\u53bb\uff0c\u4e5f\u4e0d\u77e5\u9053\u80fd\u5426\u963b\u6321\u5f97\u4e86\u3002\u201d\u4f46\u4ed6\u8bed\u97f3\u7565\u987f\uff0c\u7acb\u5373\u63a5\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u5728\u4e0b\u4e5f\u8bb8\u4e0d\u8ba1\u6210\u8d25\uff0c\u5148\u53bb\u5c11\u6797\u4e00\u8d9f\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u90a3\u5f88\u597d\u554a\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u65b9\u8001\u5f1f\uff0c\u8001\u673d\u5012\u662f\u89c9\u51fa\uff0c\u8fd9\u4e8b\u4e0e\u4f60\u6211\u6b64\u884c\uff0c\u5e76\u65e0\u51b2\u7a81\u4e4b\u5904\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8001\u524d\u8f88\u6709\u4f55\u9ad8\u89c1\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4f60\u6211\u524d\u5f80\u4e2d\u5dde\uff0c\u4e0d\u4e5f\u662f\u5728\u90a3\u6cb3\u5357\u5883\u5185\u5417\uff0c\u7ed5\u9053\u5d69\u5c71\u4e00\u884c\uff0c\u4e5f\u4e0d\u4f1a\u803d\u6401\u591a\u4e45\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u987a\u9053\u5417\uff1f\u201d\u6562\u60c5\u4ed6\u5bf9\u90a3\u4e2d\u5dde\u7684\u8def\u5f84\uff0c\u5341\u5206\u964c\u751f\u3002 \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u867d\u7136\u4e0d\u7b97\u987a\u8def\uff0c\u4f46\u4e5f\u7ed5\u4e0d\u591a\u8fdc\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6168\u7136\u9053\uff1a\u201c\u90a3\u5c31\u8fd9\u4e48\u51b3\u5b9a\u4e86\u3002\u201d\u8bdd\u97f3\u987f\u4e00\u987f\uff0c\u5ffd\u7136\u8f6c\u5411\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u627f\u8499\u76f8\u544a\u5929\u9b54\u5973\u4e4b\u4e8b\uff0c\u5728\u4e0b\u5341\u5206\u611f\u6fc0\uff0c\u65e5\u540e\u76f8\u89c1\uff0c\u5728\u4e0b\u518d\u5f53\u56fe\u62a5\u2026\u2026\u201d\u53cc\u624b\u62b1\u62f3\u4e00\u63d6\uff0c\u5c31\u5c06\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u5ffd\u7136\u4e00\u7b11\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4f60\u8981\u8d70\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5728\u4e0b\u6025\u4e8e\u60f3\u53bb\u90a3\u5d69\u5c71\uff0c\u81ea\u7136\u4e0d\u4fbf\u5728\u6b64\u95f4\u9017\u7559\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u59be\u8eab\u8fd8\u6709\u51e0\u53e5\u8bdd\u60f3\u8bf4\uff0c\u4e0d\u77e5\u516c\u5b50\u53ef\u5426\u518d\u7a0d\u7559\u7247\u523b\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u65e0\u53ef\u5948\u4f55\u5730\u82e6\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u606d\u5019\u6307\u6559\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u516c\u5b50\u81ea\u4fe1\u53ef\u80dc\u5f97\u4e86\u5929\u9b54\u5973\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5728\u4e0b\u65e2\u7136\u51b3\u5fc3\u524d\u53bb\uff0c\u90a3\u81ea\u7136\u662f\u4e0d\u8bba\u6210\u8d25\u5f97\u5931\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u516c\u5b50\u679c\u7136\u5f88\u662f\u8c6a\u6c14\u554a\uff01\u201d\u5979\u5ffd\u7136\u4f4e\u58f0\u4e00\u53f9\u9053\uff1a\u201c\u516c\u5b50\uff0c\u59be\u8eab\u5012\u6709\u4e00\u4e2a\u529e\u6cd5\uff0c\u53ef\u514d\u53bb\u516c\u5b50\u8dcb\u6d89\u5c11\u6797\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u6014\u9053\uff1a\u201c\u59d1\u5a18\u6709\u529e\u6cd5\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u516c\u5b50\u613f\u4e0d\u613f\u63a5\u53d7\u5462\uff1f\u5982\u662f\u516c\u5b50\u4e0d\u613f\u63a5\u53d7\uff0c\u59be\u8eab\u5012\u4e0d\u5982\u4e0d\u8bf4\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5927\u4e3a\u611f\u6fc0\u5730\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u76db\u60c5\uff0c\u53eb\u5728\u4e0b\u5341\u5206\u611f\u6fc0\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u516c\u5b50\u613f\u610f\u542c\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u8bf7\u8bb2\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u90a3\u5929\u9b54\u5973\u4f3c\u662f\u5bf9\u59be\u8eab\u5341\u5206\u5df4\u7ed3\uff0c\u5979\u53bb\u90a3\u5c11\u6797\u5bfa\u5bfb\u4e8b\uff0c\u5982\u662f\u59be\u8eab\u51fa\u9762\u963b\u6b62\u4e8e\u5979\uff0c\u5fc5\u7136\u53ef\u4ee5\u53eb\u5979\u5373\u65f6\u800c\u9000\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u59d1\u5a18\u6b64\u8a00\u5f53\u771f\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u5982\u4e0d\u5f53\u771f\uff0c\u59be\u8eab\u4e5f\u4e0d\u4f1a\u8bf4\u5c06\u51fa\u6765\u7684\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u56de\u987e\u4e86\u5b8b\u5b5a\u4e00\u773c\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u5b89\u59d1\u5a18\u4e4b\u7b56\uff0c\u53ef\u884c\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u53ef\u884c\uff01\u5f53\u7136\u53ef\u884c\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u5b8b\u8001\u540c\u610f\u665a\u8f88\u6b64\u4e3e\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u540c\u610f\uff01\u540c\u610f\uff01\u201d\u8bdd\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u53ea\u662f\u2026\u2026\u90a3\u5929\u9b54\u5973\u4f1a\u4e0d\u4f1a\u63a5\u53d7\u59d1\u5a18\u7684\u529d\u963b\u5462\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5fbd\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u8fd9\u5012\u4e0d\u5fc5\u5b8b\u8001\u5fc3\u70e6\u4e86\uff0c\u665a\u8f88\u76f8\u4fe1\u5979\u4e0d\u4f1a\u4e0d\u4e70\u6211\u8fd9\u70b9\u8584\u9762\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u62b1\u62f3\u9053\uff1a\u201c\u5982\u6b64\u5c31\u6709\u52b3\u59d1\u5a18\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u516c\u5b50\uff0c\u59be\u5206\u65e2\u7136\u8fdb\u5165\u4e86\u4e2d\u539f\uff0c\u662f\u4e0d\u662f\u4e5f\u7b97\u5f97\u6b66\u6797\u4e2d\u7684\u4e00\u5206\u5b50\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f53\u7136\u7b97\u5f97\u554a\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u65e2\u7136\u662f\u6b66\u6797\u5c0f\u5352\uff0c\u59be\u8eab\u4e3a\u6b66\u6797\u670b\u53cb\u8d39\u70b9\u5fc3\u529b\uff0c\u90a3\u4e5f\u662f\u7406\u6240\u5e94\u8be5\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u59d1\u5a18\u65e2\u6709\u6b64\u610f\uff0c\u8001\u673d\u5012\u4e50\u89c2\u5176\u6210\uff0c\u4f55\u4e0d\u77e5\u59d1\u5a18\u51e0\u65f6\u53bb\u90a3\u5c11\u6797\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u6211\u4f55\u5fc5\u53bb\uff0c\u2026\u2026\u201d\u4f46\u5979\u8bdd\u867d\u51fa\u53e3\uff0c\u5374\u53c8\u5f88\u5feb\u7684\u6447\u4e86\u6447\u957f\u53d1\uff0c\u9053\uff1a\u201c\u5b8b\u8001\u548c\u65b9\u516c\u5b50\u51e0\u65f6\u52a8\u8eab\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u8fd9\u5c31\u5947\u4e86\uff0c\u6211\u4eec\u51e0\u65f6\u52a8\u8eab\uff0c\u4e0e\u4f60\u53c8\u6709\u4ec0\u4e48\u5173\u8fde\u5417\uff1f\u201d\u53ea\u662f\uff0c\u4ed6\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u5728\u4e0b\u4e0e\u5b8b\u8001\u5373\u65e5\u5c31\u8981\u524d\u53bb\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5982\u662f\u59be\u8eab\u8ddf\u516c\u5b50\u4e00\u9053\u5462\uff0c\u4e0d\u77e5\u65b9\u4fbf\u5426\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5927\u611f\u610f\u5916\u5730\u6014\u4e86\u6014\uff0c\u6697\u9053\uff1a\u201c\u5979\u600e\u4f1a\u6709\u4e86\u8fd9\u7b49\u5ff5\u5934\u3002\u201d \u3000\u3000\u6562\u60c5\u65b9\u96ea\u5b9c\u89c9\u51fa\u6709\u4e86\u5979\u4eec\u8fd9\u822c\u5e74\u8f7b\u7684\u5c11\u5973\u540c\u884c\uff0c\u5fc5\u5c06\u6dfb\u751a\u591a\u7684\u9ebb\u70e6\uff0c\u662f\u4ee5\uff0c\u4e00\u65f6\u4e0d\u77e5\u5982\u4f55\u4f5c\u7b54\u3002 \u3000\u3000\u5b8b\u5b5a\u77a7\u4e86\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u53ef\u662f\u8981\u642d\u8001\u673d\u7684\u6728\u8239\u540c\u884c\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u662f\u554a\uff01\u53ea\u4e0d\u77e5\u4f60\u8001\u662f\u5426\u540c\u610f\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6709\u4f55\u4e0d\u53ef\uff1f\u201d\u56de\u987e\u4e86\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u63a5\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u610f\u4e0b\u5982\u4f55\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fd6\u9053\uff1a\u201c\u4f60\u90fd\u7b54\u5e94\u4e86\uff0c\u6211\u8fd8\u80fd\u8bf4\u4ec0\u4e48\u5462\uff1f\u201d\u5f53\u4e0b\u7b11\u9053\uff1a\u201c\u4e00\u5207\u7531\u4f60\u8001\u4f5c\u4e3b\u4fbf\u662f\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u8138\u4e0a\u63a0\u8fc7\u4e00\u4e1d\u559c\u8272\uff0c\u5411\u8eab\u8fb9\u7684\u5029\u513f\u9053\uff1a\u201c\u4f60\u4eec\u6536\u62fe\u6536\u62fe\uff0c\u56de\u8f6c\u4e1c\u6d77\u53bb\u5427\uff01\u201d \u3000\u3000\u90a3\u5029\u513f\u3001\u5e73\u513f\u4e8c\u5973\u95fb\u8a00\uff0c\u540c\u65f6\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5a62\u5b50\u7b49\u56de\u53bb\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u8981\u8ddf\u65b9\u516c\u5b50\u548c\u5b8b\u8001\u4eba\u8fdc\u53bb\u6cb3\u5357\uff0c\u4f60\u4eec\u5f53\u7136\u8981\u56de\u5bb6\u4e86\u3002\u201d \u3000\u3000\u5029\u513f\u9053\uff1a\u201c\u59d1\u5a18\u4e0d\u8981\u5c0f\u5a62\u7b49\u5f85\u5949\u5417\uff1f\u201d \u3000\u3000\u5e73\u513f\u5374\u9053\uff1a\u201c\u8fd9\u5deb\u5c71\u7684\u522b\u5e9c\uff0c\u8981\u4e0d\u8981\u770b\u5b88\u5462\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u4e0d\u8981\u4e86\uff0c\u6211\u6b64\u756a\u524d\u5f80\u6cb3\u5357\uff0c\u53ef\u80fd\u8981\u6539\u88c5\u6613\u5bb9\uff0c\u4f60\u8ddf\u6211\u4e00\u9053\uff0c\u53cd\u591a\u4e0d\u4fbf\u3002\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u81f3\u4e8e\u8fd9\u5deb\u5c71\u522b\u5e9c\uff0c\u4f60\u4eec\u56de\u53bb\u4ee5\u540e\uff0c\u518d\u8981\u4ed6\u4eec\u6d3e\u4eba\u524d\u6765\u7167\u6599\u5427\uff01\u201d \u3000\u3000\u90a3\u5029\u513f\u4f3c\u662f\u4e0d\u613f\u610f\u5730\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u4e00\u4e2a\u4eba\u600e\u53ef\u884c\u8d70\u6c5f\u6e56\u554a\uff01\u201d \u3000\u3000\u5e73\u513f\u4e5f\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u5982\u679c\u6211\u4eec\u56de\u53bb\u4e86\uff0c\u53ea\u6015\u8001\u592b\u4eba\u4e0d\u4f1a\u9976\u8fc7\u6211\u4eec\u554a\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6012\u9053\uff1a\u201c\u7f57\u55e6\uff01\u6211\u81ea\u5df1\u4e0d\u61c2\uff0c\u8981\u4f60\u4eec\u6765\u8bb2\u5417\uff1f\u54fc\uff01\u5a18\u8981\u95ee\u8d77\u6765\uff0c\u5c31\u8bf4\u6211\u53eb\u4f60\u4eec\u56de\u53bb\u7684\uff0c\u4e0d\u5c31\u5f97\u5566\uff01\u201d \u3000\u3000\u5029\u513f\u548c\u5e73\u513f\u8fd8\u60f3\u518d\u8bf4\uff0c\u4f46\u5b89\u5c0f\u840d\u7684\u8138\u8272\u5341\u5206\u96be\u770b\uff0c\u7ec8\u4e8e\u5c06\u8981\u51fa\u53e3\u7684\u8bdd\u513f\uff0c\u54bd\u56de\u4e86\u809a\u91cc\u3002 \u3000\u3000\u5b8b\u5b5a\u8fd9\u65f6\u4e00\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u679c\u7136\u662f\u51b3\u5b9a\u72ec\u81ea\u548c\u8001\u673d\u7b49\u524d\u53bb\u4e48\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4f60\u8001\u83ab\u975e\u8ba4\u4e3a\u4e0d\u5f53\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u662f\uff0c\u8001\u673d\u53ea\u662f\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u8001\u524d\u8f88\u60f3\u5fc5\u8ba4\u5b9a\u665a\u8f88\u4ecd\u662f\u5973\u513f\u4e4b\u8eab\uff0c\u5982\u662f\u72ec\u81ea\u4e0e\u4f60\u4eec\u540c\u53bb\uff0c\u8def\u4e0a\u4e0d\u5927\u65b9\u4fbf\uff0c\u5bf9\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u59d1\u5a18\u771f\u662f\u806a\u660e\u4eba\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u5982\u662f\u6211\u6539\u7740\u7537\u5b50\u88c5\u675f\u5462\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u4e00\u6014\uff1a\u201c\u6613\u94a1\u800c\u5f01\u5417\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u662f\u554a\uff0c\u884c\u5f97\u901a\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u5f53\u7136\u884c\u5f97\u901a\u3002\u201d\u5ffd\u7136\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u610f\u4e0b\u5982\u4f55\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u665a\u8f88\u8fd8\u6709\u4ec0\u4e48\u597d\u8bf4\uff0c\u4f60\u8001\u65e2\u8bf4\u53ef\u884c\uff0c\u90a3\u5f53\u7136\u662f\u53ef\u884c\u7684\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u76ee\u5149\u4e00\u8f6c\uff0c\u5411\u4e24\u5a62\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u8fd8\u7b49\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u663e\u7136\uff0c\u5df2\u5728\u50ac\u5979\u4eec\u56de\u53bb\u4e86\u3002 \u3000\u3000\u5029\u513f\u548c\u5e73\u513f\u65e0\u53ef\u5948\u4f55\uff0c\u5374\u53c8\u4f9d\u4f9d\u4e0d\u820d\u5730\u770b\u4e86\u5b89\u5c0f\u840d\u4e00\u773c\uff0c\u8fd9\u624d\u8f6c\u8eab\u5411\u540e\u6d1e\u6b65\u53bb\u3002 \u3000\u3000\u4e0d\u5927\u4e00\u4f1a\uff0c\u4e8c\u5973\u5404\u81ea\u63d0\u4e86\u4e00\u4e2a\u5c0f\u5c0f\u7684\u5305\u88b1\uff0c\u8d70\u51fa\u95e8\u6765\uff0c\u5411\u5b89\u5c0f\u840d\u76c8\u76c8\u62dc\u5012\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u4eec\u8d70\u5427\uff01\u201d\u58f0\u97f3\u6709\u4e9b\u545c\u54bd\uff0c\u6562\u60c5\u5979\u4e5f\u6709\u4e9b\u4e0d\u820d\u554a\uff01 \u3000\u3000\u5029\u513f\u3001\u5e73\u513f\u542b\u6cea\u7ad9\u4e86\u8d77\u6765\uff0c\u4e24\u4eba\u4f3c\u4e4e\u6709\u7740\u6ee1\u8179\u8bdd\u8981\u8bf4\uff0c\u5374\u662f\u4e00\u4e2a\u5b57\u4e5f\u6ca1\u6709\u8bf4\u51fa\u53e3\u6765\u3002 \u3000\u3000\u7ec8\u4e8e\uff0c\u63a9\u9762\u8f7b\u4f4d\u800c\u53bb\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u65b9\u81ea\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5979\u4eec\u4ece\u5c0f\u5c31\u8ddf\u6211\u5728\u4e00\u8d77\uff0c\u4ece\u672a\u8fdc\u79bb\uff0c\u8fd9\u7b49\u4f9d\u604b\u4e8e\u6211\uff0c\u4e5f\u662f\u4eba\u4e4b\u5e38\u60c5\u3002\u6211\u4e5f\u2026\u2026\u201d \u3000\u3000\u5ffd\u7136\uff0c\u90a3\u5029\u513f\u53c8\u6298\u4e86\u56de\u6765\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u5446\u4e86\u4e00\u5446\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u600e\u4e48\u53c8\u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u5029\u513f\u9053\uff1a\u201c\u5a62\u5b50\u60f3\u5411\u8fd9\u4f4d\u5b8b\u8001\u4eba\u8bf4\u4e00\u53e5\u8bdd\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u4e00\u6014\u4e4b\u95f4\uff0c\u5b8b\u5b5a\u5df2\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u6709\u4f55\u89c1\u6559\uff0c\u5feb\u5feb\u8bf7\u8bb2\u3002\u201d \u3000\u3000\u5029\u513f\u9053\uff1a\u201c\u5a62\u5b50\u7b49\u5f85\u5949\u59d1\u5a18\u591a\u5e74\uff0c\u4ece\u672a\u79bb\u5f00\uff0c\u521d\u6b21\u522b\u79bb\uff0c\u662f\u6709\u4e9b\u653e\u5fc3\u4e0d\u4e0b\uff0c\u4f60\u8001\u4eba\u5bb6\u4e43\u662f\u6b66\u6797\u524d\u8f88\uff0c\u53c8\u662f\u4e0a\u4e86\u5e74\u7eaa\u7684\u4eba\uff0c\u6211\u5bb6\u59d1\u5a18\u4eca\u540e\u7684\u4e00\u5207\uff0c\u8fd8\u5f97\u4f60\u8001\u591a\u591a\u7167\u987e\u554a\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u4eba\u5df2\u62dc\u5012\u5728\u5730\u3002 \u3000\u3000\u5b8b\u5b5a\u5927\u4e3a\u611f\u52a8\u5730\u6276\u8d77\u90a3\u5029\u513f\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u4e0d\u987b\u6302\u5728\u5fc3\u4e0a\uff0c\u8001\u673d\u81ea\u662f\u7b54\u5e94\u59d1\u5a18\u4fbf\u662f\u3002\u201d \u3000\u3000\u5029\u513f\u8fd9\u624d\u653e\u5fc3\u5730\u9eef\u7136\u4e00\u7b11\uff0c\u8f6c\u8eab\u5411\u5b89\u5c0f\u840d\u4e00\u62dc\u9053\uff1a\u201c\u59d1\u5a18\u73cd\u91cd\uff0c\u5c0f\u5a62\u7b49\u56de\u8f6c\u4e1c\u6d77\u53bb\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u4f4e\u58f0\u9053\uff1a\u201c\u56de\u53bb\u89c1\u5230\u5a18\u65f6\uff0c\u5c31\u8bf4\u6211\u4e2d\u539f\u7684\u4e8b\u60c5\u529e\u5b8c\uff0c\u5c31\u4f1a\u56de\u6765\u2026\u2026\u201d \u3000\u3000\u5029\u513f\u5e94\u4e86\u58f0\u662f\uff0c\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u5b8b\u5b5a\u5fcd\u4e0d\u4f4f\u957f\u53f9\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u5b89\u59d1\u5a18\u7eb5\u7136\u5e26\u5979\u4eec\u56de\u53bb\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u4e0d\u59a5\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6447\u5934\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u4e0d\u662f\u6211\u4e0d\u5e26\u5979\u4eec\u56de\u53bb\uff0c\u53ea\u4e0d\u8fc7\u6015\u5979\u4eec\u65e0\u6545\u751f\u4e8b\uff0c\u60f9\u6765\u66f4\u591a\u7684\u9ebb\u70e6\u800c\u5df2\u2026\u2026\u201d \u3000\u3000\u8bdd\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u4f3c\u662f\u6015\u4ed6\u4eec\u542c\u4e0d\u660e\u767d\u81ea\u5df1\u8a00\u4e0b\u4e4b\u610f\uff0c\u63a5\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u4e2b\u5934\u4e00\u5411\u5728\u4e1c\u6d77\u88ab\u5a07\u5ba0\u60ef\u4e86\u3002\u9664\u4e86\u5bf9\u6211\u548c\u6211\u5a18\u53ca\u51e0\u4f4d\u957f\u8005\u5916\uff0c\u522b\u4eba\u51e0\u4e4e\u90fd\u4e0d\u653e\u5728\u5979\u4eec\u773c\u4e2d\uff0c\u6211\u662f\u6015\u5979\u4eec\u8ddf\u4e86\u524d\u53bb\uff0c\u4f1a\u7ed9\u4e2d\u539f\u6b66\u6797\u670b\u53cb\uff0c\u5e26\u6765\u4e0d\u5c11\u4e8b\u6545\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u604d\u7136\u4e00\u7b11\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u59d1\u5a18\u8bf4\u7684\u5bf9\uff0c\u4e0d\u5e26\u5979\u4eec\u53bb\u4e5f\u597d\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u65f6\u4e00\u7b11\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u54b1\u4eec\u53ef\u597d\u52a8\u8eab\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u2026\u2026\u201d\u76ee\u5149\u8f6c\u5411\u5b89\u5c0f\u840d\uff0c\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u8981\u4e0d\u8981\u7acb\u5373\u6539\u626e\u4e00\u4e0b\u5462\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6251\u54e7\u4e00\u7b11\u9053\uff1a\u201c\u662f\u554a\uff01\u6211\u600e\u5730\u5fd8\u4e86\uff01\u4e24\u4f4d\u8bf7\u7a0d\u7b49\u4e00\u4f1a\u513f\u5427\uff01\u201d \u3000\u3000\u5a07\u8eaf\u4e00\u62e7\uff0c\u8d70\u5230\u90a3\u7389\u77f3\u5c4f\u98ce\u4e4b\u540e\u53bb\u4e86\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5ffd\u7136\u4f4e\u58f0\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u8fd9\u4e48\u505a\u59a5\u5f53\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u5979\u4f3c\u4e4e\u662f\u4e1c\u6d77\u7684\u4e00\u4f4d\u6b66\u6797\u4e16\u5bb6\u5b50\u5973\uff0c\u8001\u673d\u8ba4\u4e3a\u8981\u5979\u56de\u53bb\uff0c\u5e76\u65e0\u4e0d\u5f53\u3002\u201d \u3000\u3000\u4ed6\u56e0\u4e3a\u4e0d\u77e5\u9053\u90a3\u201c\u5929\u9e64\u65a9\u201d\u6b66\u529f\u4e4b\u4e8b\uff0c\u81ea\u7136\u4e5f\u4e0d\u77e5\u9053\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u803d\u5fc3\u7684\u662f\u4ec0\u4e48\u4e86\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u4ed6\u624d\u8fd9\u4e48\u56de\u7b54\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u665a\u8f88\u603b\u662f\u89c9\u5f97\u5979\u4eec\u51fa\u73b0\u7684\u592a\u8fc7\u4e8e\u79bb\u5947\u4e86\u4e00\u4e9b\uff0c\u9080\u5979\u540c\u53bb\u5c11\u6797\uff0c\u53ea\u6015\u5f97\u4e0d\u507f\u5931\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u600e\u4e48\u4f1a\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5982\u662f\u60f3\u60ca\u9000\u5929\u9b54\u5973\uff0c\u6709\u665a\u8f88\u4e00\u4e2a\u5c31\u5f88\u591f\u4e86\uff0c\u6240\u4ee5\u8fd9\u4f4d\u5b89\u59d1\u5a18\u53bb\u4e0d\u53bb\u60f3\u6765\u662f\u6ca1\u6709\u751a\u4e48\u5173\u7cfb\u5427\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u5979\u5982\u679c\u56de\u53bb\uff0c\u54b1\u4eec\u5c31\u4e0d\u5fc5\u8ddf\u5929\u9b54\u5973\u52a8\u624b\u4e86\uff0c\u8fd9\u7b49\u5175\u4e0d\u8840\u5203\u800c\u9000\u654c\u4e4b\u4e8b\uff0c\u8001\u5f1f\u4f55\u4e50\u4e0d\u4e3a\uff1f\u201d\u8bdd\u97f3\u4e00\u987f\uff0c\u795e\u79d8\u4f38\u53e4\u9053\uff1a\u201c\u4f55\u51b5\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4f55\u51b5\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u5979\u4e0d\u662f\u5bf9\u4f60\u8001\u5f1f\u5f88\u6709\u60c5\u610f\u4e86\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u987f\u65f6\u6ee1\u8138\u901a\u7ea2\uff0c\u8fde\u5fd9\u6447\u624b\u9053\uff1a\u201c\u8001\u524d\u8f88\u5343\u4e07\u4e0d\u53ef\u5f00\u8fd9\u7b49\u73a9\u7b11\u2026\u2026\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u7a81\u7136\u542c\u5f97\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u5b8b\u8001\u548c\u65b9\u516c\u5b50\u4ec0\u4e48\u73a9\u7b11\uff0c\u6211\u53ef\u4ee5\u542c\u5417\uff1f\u201d \u3000\u3000\u4e24\u4eba\u4e00\u60ca\u4e4b\u95f4\uff0c\u53ea\u89c1\u90a3\u5b89\u5c0f\u840d\u5df2\u6253\u626e\u6210\u4e00\u4f4d\u4fd7\u4e16\u7c97\u7c97\u7684\u4f73\u516c\u5b50\uff0c\u7ad9\u5728\u4e24\u4eba\u8eab\u540e\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u53ef\u771f\u6015\u5b8b\u8001\u7684\u8fd9\u4e00\u53e5\u73a9\u7b11\u88ab\u5979\u542c\u5230\uff0c\u8fde\u5fd9\u7b11\u9053\uff1a\u201c\u6ca1\u6709\u4ec0\u4e48\u554a\u2026\u2026\u201d\u4f46\u4ed6\u8138\u4e0a\u7684\u795e\u60c5\uff0c\u5374\u5b8c\u5168\u4e0d\u662f\u90a3\u4e48\u4e00\u56de\u4e8b\uff0c\u867d\u975e\u7ea2\u900f\uff0c\u4e5f\u591f\u706b\u8fa3\u8fa3\u7684\u96be\u53d7\u4e86\u3002 \u3000\u3000\u5b8b\u5b5a\u5230\u5e95\u8001\u7ec3\u5f97\u591a\uff0c\u54c8\u54c8\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u8001\u673d\u662f\u8bf4\u8fd9\u4f4d\u65b9\u8001\u5f1f\u4e00\u5fc3\u53ea\u60f3\u7ba1\u90a3\u4e94\u5927\u9b54\u4e3b\u4e4b\u4e8b\uff0c\u653e\u4e0b\u81ea\u5df1\u7684\u6b63\u4e8b\u4e0d\u95ee\uff0c\u5374\u60f3\u4e0d\u5230\u4e8b\u5230\u5934\u6765\uff0c\u8fd8\u662f\u9760\u522b\u4eba\u5e2e\u5fd9\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u4f60\u8001\u662f\u6307\u665a\u8f88\u524d\u5f80\u5c11\u6797\uff0c\u529d\u963b\u90a3\u5929\u9b54\u5973\u7684\u4e8b\u60c5\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u53ef\u4e0d\u662f\uff1f\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4e0d\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u6b66\u6797\u4e2d\u4eba\uff0c\u7ba1\u8fd9\u6b66\u6797\u4e2d\u4e8b\uff0c\u4e5f\u539f\u662f\u5929\u7ecf\u5730\u4e49\u4e4b\u8bba\uff0c\u59d1\u5a18\u5ffd\u7136\u81ea\u544a\u594b\u52c7\uff0c\u672c\u4e5f\u65e0\u53ef\u539a\u975e\uff0c\u6240\u4ee5\u8001\u673d\u624d\u4f1a\u5f00\u4ed6\u4e00\u4e2a\u73a9\u7b11\uff0c\u8bf4\u4ed6\u5982\u662f\u65e5\u540e\u5e38\u80fd\u5982\u6b64\u5f97\u4eba\u76f8\u52a9\uff0c\u5fc5\u53ef\u6210\u4e3a\u6b66\u6797\u4e2d\u7684\u4e00\u65b9\u9738\u4e3b\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u597d\u554a\uff01\u65b9\u5144\u5982\u82e5\u6709\u6b64\u5fc3\uff0c\u59be\u8eab\u5b9a\u7136\u5168\u529b\u76f8\u52a9\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u542c\u5230\u4e86\u5417\uff1f\u8001\u5f1f\uff1f\u201d \u3000\u3000\u4ed6\u4e0d\u7b49\u65b9\u96ea\u5b9c\u8bf4\u8bdd\uff0c\u5ffd\u7136\u4e00\u8f6c\u8eab\uff0c\u5927\u6b65\u5411\u5916\u884c\u53bb\uff0c\u63a5\u9053\uff1a\u201c\u54b1\u4eec\u5982\u60f3\u5728\u65e5\u51fa\u524d\u8d76\u56de\u8239\u4e0a\uff0c\u54b1\u4eec\u53ef\u5c31\u4e0d\u80fd\u518d\u6709\u6240\u803d\u6401\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8baa\u8baa\u5730\u4e00\u7b11\uff0c\u5411\u5b89\u5c0f\u840d\u4e00\u62b1\u62f3\uff0c\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u8bf7\u554a\uff01\u201d\u5b89\u5c0f\u840d\u4e00\u8bed\u4e0d\u53d1\uff0c\u968f\u5728\u5b8b\u5b5a\u8eab\u540e\u5411\u5916\u8d70\u53bb\u3002 \u3000\u3000\u4e09\u4eba\u51fa\u5f97\u90a3\u8944\u738b\u53f0\u4e2d\u7684\u77f3\u5e9c\uff0c\u770b\u4e86\u770b\u5929\u8272\uff0c\u679c\u7136\u5df2\u8fd1\u4e94\u66f4\u65f6\u5206\u4e86\u3002\u5f53\u4e0b\u4e0d\u518d\u72b9\u8c6b\uff0c\u5404\u5c55\u8eab\u6cd5\uff0c\u8fc5\u5feb\u5730\u5411\u9760\u5728\u5deb\u5c71\u57ce\u5916\u7684\u6728\u8239\u884c\u53bb\u3002 \u3000\u3000\u679c\u7136\u5728\u65e5\u51fa\u4e4b\u524d\uff0c\u6728\u8239\u4e1a\u5df2\u89e3\u7f06\u542f\u822a\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u501a\u7acb\u8239\u5934\uff0c\u76ee\u5149\u88ab\u773c\u524d\u7684\u5947\u5999\u666f\u8272\u5438\u5f15\uff0c\u4f46\u89c1\u4e24\u5cb8\u8fde\u5c71\uff0c\u91cd\u5ce6\u53e0\u5d82\uff0c\u8fdc\u770b\u6c5f\u6c34\u5982\u5230\u5c3d\u5934\uff0c\u8239\u5230\u5c71\u8fb9\uff0c\u5374\u53c8\u4e00\u6298\u800c\u4e1c\uff0c\u524d\u8def\u53c8\u662f\u4e00\u756a\u96c4\u4f1f\u666f\u8c61\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u76ee\u5c01\u7729\u9a70\uff0c\u795e\u4e3a\u6240\u593a\uff0c\u53ea\u89c9\u821f\u884c\u4e4b\u8fdc\uff0c\u6709\u5982\u5feb\u9a6c\u98de\u9a76\uff0c\u77ac\u606f\u4e07\u4e08\uff0c\u987f\u65f6\u60f3\u5230\u674e\u767d\u90a3\u9996\u201c\u4e0b\u6c5f\u9675\u201d\u70e9\u7099\u4eba\u53e3\u7684\u8bd7\u53e5\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u4e0d\u7531\u81ea\u4e3b\u7684\u8131\u53e3\u5ff5\u51fa\u3002 \u3000\u3000\u5ffd\u7136\uff0c\u4ed6\u542c\u5f97\u4e00\u58f0\u8f7b\u7b11\u8d77\u81ea\u8eab\u540e\uff0c\u5fae\u5fae\u4e00\u60ca\uff0c\u626d\u5934\u770b\u53bb\uff0c\u89c1\u5b89\u5c0f\u840d\u542b\u7b11\u8bf4\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u4e24\u5cb8\u733f\u58f0\u557c\u4e0d\u4f4f\uff0c\u8f7b\u821f\u5df2\u8fc7\u4e07\u91cd\u5c71\uff0c\u516c\u5b50\uff0c\u542c\u4f60\u60c5\u4e0d\u81ea\u5df1\uff0c\u4f4e\u58f0\u56de\u60f3\uff0c\u83ab\u975e\u6709\u4e86\u90a3\u601d\u53e4\u7684\u5e7d\u60c5\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u53ea\u56e0\u4e00\u65f6\u5fc3\u795e\u4ff1\u88ab\u773c\u524d\u5947\u666f\u6240\u593a\uff0c\u4ee5\u81f4\u8fde\u5b89\u5c0f\u840d\u6765\u5230\u8eab\u540e\uff0c\u5747\u6240\u4e0d\u77e5\uff0c\u95fb\u8a00\u8baa\u8baa\u4e00\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u51e0\u65f6\u4e5f\u51fa\u8231\u6765\u4e86\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u5f88\u4e45\u2026\u2026\u201d\u5ffd\u7136\u4e00\u6014\uff0c\u63a5\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4f60\u79f0\u547c\u6211\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5b89\u59d1\u5a18\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5ffd\u7136\u6307\u7740\u81ea\u5df1\u90a3\u5934\u4e0a\u7684\u6587\u751f\u5dfe\uff0c\u8eab\u4e0a\u7684\u5b66\u58eb\u886b\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u5acc\u5192\u72af\u65af\u6587\u5417\uff1f \u3000\u3000\u4f60\u770b\uff0c\u59be\u8eab\u5df2\u7136\u6539\u88c5\u6613\u5bb9\uff0c\u4f60\u8be5\u53eb\u6211\u4e00\u58f0\u5b89\u5144\u5f1f\u624d\u662f\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u90a3\u2026\u2026\u600e\u4e48\u884c\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u63a9\u4eba\u8033\u76ee\uff0c\u6709\u4f55\u4e0d\u53ef\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6014\u4e86\u4e00\u6014\u9053\uff1a\u201c\u5b89\u5144\u5f1f\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u683c\u683c\u4e00\u7b11\u9053\uff1a\u201c\u65b9\u516c\u5b50\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9661\u7136\u89c9\u5f97\u8fd9\u516c\u5b50\u4e24\u5b57\uff0c\u975e\u5e38\u523a\u8033\uff0c\u5fd9\u9053\uff1a\u201c\u5b89\u5144\u5f1f\uff0c\u6709\u4e00\u4ef6\u4e8b\uff0c\u5728\u4e0b\u4f3c\u4e4e\u4e5f\u8981\u8bf4\u660e\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u554a\uff01\u8fd9\u7b49\u4e25\u91cd\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4f60\u4e0d\u53ef\u518d\u53eb\u6211\u65b9\u516c\u5b50\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4e0b\u53ef\u662f\u5b89\u5c0f\u840d\u5446\u4e86\u4e00\u5446\u9053\uff1a\u201c\u53eb\u4f60\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u59d1\u5a18\u2026\u2026\u201d \u3000\u3000\u773c\u89c1\u5b89\u5c0f\u840d\u8138\u8272\u4e00\u53d8\uff0c\u624d\u77e5\u81ea\u5df1\u53c8\u53eb\u6f0f\u4e86\u53e3\uff0c\u5fd9\u6539\u53e3\u9053\uff1a\u201c\u5b89\u5144\u5f1f\uff0c\u4f60\u4eca\u5e74\u591a\u5927\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5ffd\u7136\u7fd8\u9996\u4f4e\u58f0\u5e94\u9053\uff1a\u201c\u59be\u8eab\u5341\u4e03\u5c81\u2026\u2026\u201d \u3000\u3000\u6562\u60c5\u5973\u513f\u5bb6\u4e00\u63d0\u5230\u5e74\u9f84\uff0c\u603b\u8fd8\u662f\u6709\u4e9b\u5bb3\u7f9e\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u663e\u7136\u5e76\u672a\u7559\u610f\u53ca\u6b64\uff0c\u53ea\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u865a\u5ea6\u4e8c\u5341\u5566\uff01\u5b89\u5144\u5f1f\uff0c\u4f60\u4e0d\u5982\u6539\u53e3\u53eb\u6211\u4e00\u58f0\u65b9\u5144\u5427\uff01\u201d \u3000\u3000\u8fd9\u7b49\u79f0\u547c\uff0c\u672c\u662f\u5341\u5206\u5408\u7406\u4e4b\u4e8b\uff0c\u5728\u65b9\u96ea\u5b9c\u7684\u610f\u60f3\u4e4b\u4e2d\uff0c\u5e94\u662f\u4e0d\u4f1a\u6709\u9519\u7684\u4e86\u3002 \u3000\u3000\u4f46\u5b89\u5c0f\u840d\u95fb\u8a00\uff0c\u5374\u534a\u664c\u6ca1\u6709\u4f5c\u58f0\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u89c1\u5979\u4e45\u4e45\u4e0d\u7b54\uff0c\u4e0d\u7981\u8be7\u5f02\u5730\u7b11\u9053\uff1a\u201c\u5b89\u5144\u5f1f\uff0c\u4f60\u53ef\u662f\u4e0d\u613f\u5144\u5f1f\u9ad8\u6500\u4f60\u4e86\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6697\u9053\uff1a\u201c\u4ed6\u600e\u77e5\u5973\u5b69\u513f\u5bb6\u7684\u5fc3\u4e2d\u6240\u60f3\u5462\uff1f\u770b\u6765\u5982\u8981\u4ed6\u4e0d\u8bef\u4f1a\uff0c\u53ea\u597d\u79f0\u547c\u4ed6\u65b9\u5144\u4e86\u2026\u2026\u201d\u5f53\u4e0b\u4f4e\u4f4e\u5730\u63a5\u9053\uff1a\u201c\u65b9\u5144\u8bef\u4f1a\u4e86\uff0c\u8fd9\u9ad8\u6500\u7684\u8be5\u662f\u59be\u8eab\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u5b89\u5144\u5f1f\uff0c\u4f60\u592a\u8c26\u4e86\u3002\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5b89\u5144\u5f1f\uff0c\u4f60\u8fd9\u81ea\u79f0\u6709\u4e9b\u4e0d\u4f26\u4e0d\u7c7b\u5427\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5446\u4e86\u4e00\u5446\uff0c\u5ffd\u7136\u5931\u7b11\u9053\uff1a\u201c\u662f\u554a\uff0c\u6211\u679c\u7136\u8bb0\u9519\u4e86\u3002\u4ee5\u540e\u5c0f\u5f1f\u6539\u8fc7\u4fbf\u662f\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u8d24\u5f1f\u8bb0\u4e0b\u5c31\u597d\u3002\u201d \u3000\u3000\u4ed6\u4ece\u8bb0\u4e8b\u4ee5\u6765\uff0c\u6240\u89c1\u4e4b\u4eba\uff0c\u90fd\u6bd4\u4ed6\u5e74\u7eaa\u4e3a\u5927\uff0c\u4e00\u65e6\u5b89\u5c0f\u840d\u6210\u4e86\u4ed6\u7684\u5144\u5f1f\uff0c\u5728\u4ed6\u800c\u8a00\uff0c\u5012\u662f\u5341\u5206\u65b0\u5947\uff0c\u800c\u4e14\uff0c\u4e5f\u65e0\u610f\u4e4b\u95f4\uff0c\u89e6\u53d1\u4e86\u4ed6\u90a3\u4ec1\u6148\u7684\u5929\u6027\uff0c\u89c9\u5f97\u4eca\u540e\u5012\u771f\u8981\u597d\u597d\u5730\u7167\u987e\u8fd9\u4f4d\u8d24\u5f1f\uff0c\u4e5f\u597d\u5c3d\u4e00\u4efd\u4f5c\u90a3\u5144\u957f\u7684\u8d23\u4efb\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u81ea\u662f\u4e0d\u77e5\u4ed6\u5fc3\u4e2d\u6240\u60f3\uff0c\u76ee\u5149\u5728\u90a3\u4e24\u5cb8\u5947\u5c71\u5cfb\u5cad\u7684\u96c4\u4f1f\u666f\u8272\u4e2d\u8f6c\u6765\u8f6c\u53bb\uff0c\u7b11\u9053\uff1a \u3000\u3000\u201c\u65b9\u5144\uff0c\u4f60\u5f80\u65e5\u6ca1\u6709\u6765\u8fc7\u4e09\u5ce1\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6014\u6014\u5730\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u89c1\u4ed6\u5fc3\u795e\u4e0d\u5c5e\uff0c\u4e0d\u7981\u7b11\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u600e\u4e48\u5566\uff01\u6709\u4ec0\u4e48\u5fc3\u4e8b\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u6ca1\u6709\u554a\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u90a3\u4f60\u4e3a\u4f55\u4e0d\u66fe\u542c\u660e\u767d\u5c0f\u5f1f\u6240\u95ee\u4e4b\u8bdd\u5462\uff0c\u9664\u975e\u4f60\u2026\u2026\u4e0d\u613f\u8ddf\u6211\u8bb2\u8bdd\u5440\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u4f1a\uff0c\u8d24\u5f1f\u83ab\u8981\u8bef\u4f1a\u2026\u2026\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u8d24\u5f1f\u521a\u624d\u8bf4\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u84e6\u5730\u4e00\u7b11\u9053\uff1a\u201c\u6211\u2026\u2026\u65b9\u5144\uff0c\u5c0f\u5f1f\u4e0d\u5fc5\u518d\u95ee\uff0c\u4e5f\u53ef\u4ee5\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u660e\u767d\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4f60\u5f80\u65e5\u672a\u66fe\u6765\u8fc7\u4e09\u5ce1\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5144\u5f1f\u679c\u7136\u6ca1\u6709\u6765\u8fc7\u3002\u201d \u3000\u3000\u8bdd\u97f3\u4e00\u987f\uff0c\u8131\u53e3\u63a5\u9053\uff1a\u201c\u8d24\u5f1f\u8fdc\u5c45\u4e1c\u6d77\uff0c\u600e\u4f1a\u5728\u90a3\u671d\u4e91\u5cf0\u5934\uff0c\u6709\u4e00\u5ea7\u77f3\u5e9c\uff1f\u5144\u5f1f\u60f3\u4e86\u597d\u4e45\uff0c\u90fd\u60f3\u4e0d\u901a\u5176\u4e2d\u9053\u7406\u4f55\u5728\u3002\u83ab\u975e\u90a3\u662f\u8d24\u5f1f\u5c0a\u957f\u6e38\u620f\u4e2d\u539f\u4e4b\u65f6\u6240\u5efa\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u90a3\u6d1e\u5e9c\u662f\u539f\u6765\u5c31\u6709\u7684\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u5fd6\u9053\uff1a\u201c\u81ea\u53e4\u5373\u6709\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u8be6\u60c5\u5c0f\u5f1f\u4e0d\u77e5\u3002\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u53d1\u73b0\u8fd9\u5ea7\u6d1e\u5e9c\u7684\u4eba\uff0c\u5374\u662f\u6211\u7237\u7237\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4ee4\u7956\u5417\uff1f\u53ef\u662f\u4e1c\u6d77\u53cc\u4ed9\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u662f\u554a\uff01\u201d\u8fd9\u65f6\uff0c\u8239\u5df2\u8fdb\u4e86\u201c\u5175\u4e66\u5b9d\u5251\u5ce1\u201d\u4e2d\uff0c\u53ea\u89c1\u6c34\u6d41\u6e4d\u6025\uff0c\u5947\u6ee9\u602a\u77f3\uff0c\u4e5f\u8d8a\u53d1\u7684\u52a0\u591a\uff0c\u7a0d\u4e0d\u5c0f\u5fc3\uff0c\u5c31\u6709\u90a3\u7c89\u8eab\u788e\u9aa8\u4e4b\u8651\u3002 \u3000\u3000\u4f46\u8fd9\u51e0\u540d\u8239\u5bb6\uff0c\u5012\u662f\u9a76\u821f\u7684\u8001\u624b\uff0c\u770b\u4e0a\u53bb\u867d\u7136\u5371\u9669\uff0c\u5374\u6bcf\u6b21\u5747\u80fd\u5316\u9669\u4e3a\u5937\uff0c\u5e73\u5b89\u6e21\u8fc7\u3002 \u3000\u3000\u5bb9\u5f97\u821f\u884c\u6e10\u7f13\uff0c\u65b9\u96ea\u5b9c\u65b9\u59cb\u957f\u957f\u7684\u5473\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4ee4\u7956\u7684\u6b66\u529f\uff0c\u542c\u8bf4\u9ad8\u660e\u5f97\u5f88\u554a\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6d45\u7b11\u9053\uff1a\u201c\u65b9\u5144\u77e5\u9053\u6211\u7237\u7237\u5417\uff1f\u201d \u3000\u3000\u65b9\u5b66\u5b9c\u7b11\u9053\uff1a\u201c\u4e1c\u6d77\u53cc\u4ed9\u4e4b\u540d\u81ea\u662f\u77e5\u9053\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u9648\u5927\u4fa0\u544a\u8bc9\u4f60\u7684\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u6b63\u662f\u5148\u5e08\u6240\u8bf4\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u9648\u5927\u4fa0\u662f\u600e\u4e48\u63d0\u8d77\u7684\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5148\u5e08\u662f\u5728\u8c08\u53ca\u5e08\u7956\u4e4b\u65f6\uff0c\u63d0\u5230\u4e86\u4ee4\u7956\u4e1c\u6d77\u53cc\u4ed9\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5a07\u9762\u4e4b\u4e0a\uff0c\u5ffd\u7136\u7b11\u5bb9\u500f\u655b\uff0c\u76b1\u7709\u9053\uff1a\u201c\u65b9\u5144\u53ef\u77e5\u9053\u4f60\u90a3\u5e08\u7956\u7684\u5f80\u4e8b\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u6240\u77e5\u4e0d\u591a\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6c89\u541f\u9053\uff1a\u201c\u4ee4\u5e08\u53f7\u79f0\u5251\u795e\uff0c\u4f60\u5e08\u7956\u7684\u6b66\u529f\uff0c\u5fc5\u7136\u6bd4\u4ee4\u5e08\u66f4\u9ad8\u660e\u7684\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8131\u53e3\u9053\uff1a\u201c\u90a3\u5012\u4e5f\u4e0d\u4e00\u5b9a\uff0c\u5929\u4e0b\u591a\u7684\u662f\u72b6\u5143\u5f92\u5f1f\uff0c\u5374\u62a5\u5c11\u72b6\u5143\u5e08\u7236\u554a\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u9648\u5927\u4fa0\u624d\u667a\u8fc7\u4eba\uff0c\u9752\u51fa\u4e8e\u84dd\uff0c\u800c\u80dc\u4e8e\u84dd\uff0c\u90a3\u4e5f\u4e0d\u8db3\u4e3a\u5947\u7684\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6014\u4e86\u4e00\u6014\u9053\uff1a\u201c\u8d24\u5f1f\u8bf4\u7684\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u6211\u8bf4\u4ee4\u5e08\u5728\u5251\u6cd5\u4e0a\u7684\u9020\u8be3\uff0c\u53ef\u80fd\u5f3a\u8fc7\u4f60\u5e08\u7956\u4e0d\u5c11\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u6447\u5934\u9053\uff1a\u201c\u4e0d\u5bf9\uff01\u5148\u5e08\u6b66\u529f\uff0c\u53ea\u5f97\u5e08\u7956\u5168\u90e8\u6b66\u529f\u5341\u4e4b\u516d\u4e03\u800c\u5df2\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6697\u6697\u7b11\u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u6211\u5417\uff1f\u8d24\u5f1f\u6307\u7684\u54ea\u4e00\u65b9\u9762\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u5f53\u7136\u662f\u6307\u6b66\u529f\u561b\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6c89\u541f\u5fd6\u9053\uff1a\u6211\u5982\u662f\u8bf4\u5df2\u7136\u5c3d\u5f97\u5e08\u7236\u7684\u771f\u4f20\uff0c\u53ea\u6015\u5979\u4e0d\u4f1a\u76f8\u4fe1\uff0c\u4f55\u51b5\uff0c\u81ea\u5df1\u8fd8\u53e6\u5916\u5f97\u5230\u5e08\u53d4\u4f20\u6388\u5462\uff0c\u8fd9\u4e9b\u4e8b\u4e00\u65e6\u8bf4\u8d77\u6765\uff0c\u53ef\u5c31\u5341\u5206\u9ebb\u70e6\uff0c\u4f55\u5982\u542b\u7cca\u5e94\u5bf9\uff0c\u514d\u5f97\u5979\u7ea0\u7f20\u53d1\u95ee\u4e0d\u5df2\u3002\u2026\u2026 \u3000\u3000\u5fc3\u5ff5\u4e00\u5b9a\uff0c\u7acb\u5373\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5dee\u5f97\u66f4\u8fdc\u4e86\uff0c\u5e08\u7236\u6240\u6388\uff0c\u6211\u9876\u591a\u53ea\u5f97\u5176\u5341\u4e4b\u4e8c\u4e09\u800c\u5df2\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5ffd\u7136\u626c\u7709\u4e00\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u4fe1\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4f60\u4e0d\u4fe1\uff0c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4f60\u80fd\u4e00\u5251\u523a\u6740\u9b54\u5200\u7530\u8fdc\uff0c\u53c8\u80fd\u60ca\u9000\u90a3\u67f3\u5a9a\u5a18\uff0c\u8db3\u89c1\u4f60\u5f97\u81ea\u9648\u5927\u4fa0\u4f20\u7684\u6b66\u529f\uff0c\u5fc5\u7136\u4e0d\u53ea\u5341\u4e4b\u4e8c\u4e09\uff0c\u800c\u662f\u5c3d\u5f97\u9648\u5927\u4fa0\u7684\u4e0d\u4e8c\u5fc3\u6cd5\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u4e00\u9707\uff0c\u9053\uff1a\u201c\u8fd9\u2026\u2026\u8d24\u5f1f\u600e\u77e5\u8fd9\u4e0d\u662f\u4ed6\u4eec\u7684\u6b66\u529f\u592a\u5dee\u5462\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u6709\u4e00\u4ef6\u4e8b\u6211\u5982\u679c\u4e0d\u8bf4\u5c06\u51fa\u6765\uff0c\u4f60\u4e00\u5b9a\u8fd8\u4f1a\u5f3a\u8fa9\u4e0d\u5df2\u7684\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff0c\u8d24\u5f1f\u5feb\u5feb\u8bf4\u6765\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u773c\u4e0b\u5373\u4f7f\u662f\u5251\u795e\u9648\u5927\u4fa0\u590d\u751f\uff0c\u53ea\u6015\u4e5f\u60ca\u4e0d\u9000\u67f3\u5a9a\u5a18\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u5927\u5927\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u90a3\u67f3\u5a9a\u5a18\u4e3a\u4e86\u4e0d\u518d\u53d7\u5236\u4e8e\u4ee4\u5e08\u9648\u5927\u4fa0\uff0c\u8fd1\u51e0\u5e74\u6765\uff0c\u66fe\u7ecf\u8fdc\u8d70\u5929\u6daf\uff0c\u5bfb\u6c42\u7edd\u827a\uff0c\u4f46\u5979\u5374\u88ab\u4f60\u4ee5\u4e00\u624b\u524d\u6240\u672a\u66fe\u89c1\u8fc7\u7684\u5251\u62db\u5413\u9000\uff0c\u8db3\u89c1\u4f60\u5728\u5251\u6cd5\u4e4b\u4e0a\uff0c\u5df2\u7136\u6bd4\u5251\u795e\u9648\u5927\u4fa0\u66f4\u4e3a\u9ad8\u660e\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u5230\u6b64\u5904\uff0c\u4e0d\u7981\u751a\u4e3a\u8be7\u5f02\uff0c\u8131\u53e3\u9053\uff1a\u201c\u90a3\u67f3\u5a9a\u5a18\u53ef\u66fe\u627e\u5230\u4ec0\u4e48\u7edd\u827a\u4e86\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u5f53\u7136\u627e\u5230\u4e86\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u5979\u5ffd\u7136\u6c89\u541f\u4e0d\u8bed\uff0c\u5012\u4f7f\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u4e00\u6014\uff0c\u95ee\u9053\uff1a\u201c\u4e0d\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u6b64\u4e8b\u6211\u672c\u662f\u4e0d\u6539\u5bf9\u4f60\u8bf4\u7684\uff0c\u4f46\u662f\u2026\u2026\u59be\u8eab\u5374\u53c8\u4e0d\u7531\u81ea\u4e3b\u4ed6\u8bf4\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u5979\u8fd9\u7b49\u7b54\u975e\u6240\u95ee\uff0c\u8d8a\u53d1\u5730\u4f7f\u5f97\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u7591\u4e91\u4e4d\u8d77\uff0c\u5fcd\u4e0d\u4f4f\u76b1\u7709\u9053\uff1a\u201c\u8d24\u5f1f\u4e3a\u4f55\u8fd9\u822c\u541e\u541e\u5410\u5410\u5462\uff0c\u6709\u4ec0\u4e48\u8bdd\u5c3d\u7ba1\u8bf4\u660e\u4fbf\u662f\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u773c\u4e2d\u63a0\u8fc7\u4e00\u4e1d\u832b\u7136\u7684\u795e\u8272\uff0c\u4f4e\u4f4e\u5730\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u65b9\u5144\uff0c\u6709\u5173\u4e1c\u6d77\u53cc\u4ed9\u548c\u4f60\u5e08\u7956\u6240\u4ea4\u5f80\u7ecf\u8fc7\uff0c\u9648\u5927\u4fa0\u53ef\u66fe\u5bf9\u4f60\u8c08\u8fc7\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5ffd\u7136\u5fc3\u4e2d\u5927\u9707\uff0c\u6697\u9053\uff1a\u679c\u7136\u662f\u4e86\uff0c\u5979\u8bf4\u4e86\u8bb8\u4e45\uff0c\u6700\u540e\u7684\u76ee\u7684\uff0c\u8fd8\u662f\u5728\u6211\u5e08\u7956\u7684\u8eab\u4e0a\u554a\u2026\u2026 \u3000\u3000\u4f46\u4ed6\u53e3\u4e2d\u5374\u63a5\u9053\uff1a\u201c\u5148\u5e08\u4e5f\u66fe\u7ea6\u7565\u63d0\u8fc7\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6447\u9996\u51dd\u6ce8\u4e86\u65b9\u96ea\u5b9c\u534a\u664c\uff0c\u8fd9\u624d\u8bf4\u9053\uff1a\u201c\u9648\u5927\u4fa0\u65e2\u7136\u8bf4\u8fc7\uff0c\u65b9\u5144\u4e00\u5b9a\u662f\u5f88\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u660e\u767d\u4ec0\u4e48\uff1f\u201d\u4ed6\u7a76\u7adf\u662f\u7cca\u6d82\uff0c\u8fd8\u662f\u771f\u7684\u4e0d\u61c2\u5b89\u5c0f\u840d\u6240\u95ee\u7684\u8bdd\u4e2d\u4e4b\u610f\uff0c\u53ea\u6015\u8fde\u81ea\u5df1\u6b64\u523b\u4e5f\u5206\u8fa8\u4e0d\u592a\u660e\u767d\u4e86\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u65b9\u5144\uff0c\u6211\u7237\u7237\u626c\u5a01\u4e2d\u539f\u7684\u4e8b\uff0c\u96be\u9053\u9648\u5927\u4fa0\u672a\u66fe\u63d0\u53ca\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u63d0\u8fc7\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u5e08\u7956\u5251\u521b\u6211\u7237\u7237\u4e4b\u4e8b\uff0c\u4f60\u662f\u4e0d\u77e5\u9053\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u76b1\u7709\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u5c0f\u5144\u5f1f\u672a\u542c\u8bf4\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u6ca1\u542c\u8bf4\uff1f\u4e3a\u4ec0\u4e48\uff1f\u9648\u5e0c\u6b63\u96be\u9053\u5fd8\u4e86\u5417\uff1f\u201d \u3000\u3000\u5979\u7a81\u7136\u76f4\u547c\u65b9\u96ea\u5b9c\u7684\u5e08\u7236\u540d\u8bb3\uff0c\u4e0d\u89c9\u5730\u4f7f\u5f97\u65b9\u96ea\u5b9c\u5927\u4e3a\u5403\u60ca\uff0c\u800c\u4e14\uff0c\u4e5f\u6ecb\u751f\u4e86\u4e00\u80a1\u6012\u610f\u3002 \u3000\u3000\u5f53\u4e0b\u6c89\u58f0\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u4e0d\u5f97\u5bf9\u5728\u4e0b\u5148\u5e08\u65e0\u793c\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u89c1\u4ed6\u5ffd\u7136\u6c89\u4e0b\u4e86\u8138\u6765\uff0c\u82b3\u5fc3\u4e00\u9635\u5fd0\u5fd1\u4e71\u8df3\uff0c\u4f4e\u5782\u7c89\u988a\uff1a\u201c\u59be\u8eab\u4e00\u65f6\u5fd8\u60c5\uff0c\u65b9\u5144\u5343\u4e07\u83ab\u602a\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u77a7\u5979\u90a3\u526f\u695a\u695a\u53ef\u601c\u7684\u795e\u60c5\uff0c\u53c8\u89c9\u5fc3\u4e2d\u4e0d\u5fcd\uff0c\u6447\u5934\u4e00\u53f9\u9053\uff1a\u201c\u8d24\u5f1f\u4e0b\u56de\u8bb0\u4f4f\u5c31\u662f\u3002\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5148\u5e08\u7956\u4e0e\u4f60\u7237\u7237\u4e4b\u95f4\u7684\u6069\u6028\uff0c\u5144\u5f1f\u4e5f\u77e5\u9053\u4e00\u4e9b\uff0c\u4e0d\u8fc7\uff0c\u636e\u6211\u6240\u95fb\uff0c\u5148\u5e08\u7956\u786e\u5b9e\u5e76\u672a\u4f24\u5bb3\u8fc7\u4e1c\u6d77\u53cc\u4ed9\uff0c\u8d24\u5f1f\u9002\u624d\u8a00\u4e0b\u9887\u6709\u6012\u610f\uff0c\u5176\u4e2d\u5fc5\u7136\u6709\u7740\u4ec0\u4e48\u8bef\u4f1a\u4e4b\u5916\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6447\u5934\uff0c\u4f46\u5374\u53c8\u70b9\u5934\uff0c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f20\u95fb\u4e4b\u8a00\uff0c\u4e0d\u89c1\u5f97\u90fd\u662f\u771f\u7684\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5239\u90a3\u4e4b\u95f4\uff0c\u5ffd\u7136\u89c9\u51fa\uff0c\u8fd9\u4f4d\u5341\u5206\u7f8e\u4e3d\u7684\u59d1\u5a18\uff0c\u53ea\u6015\u5c31\u662f\u81ea\u5df1\u7684\u5bf9\u5934\u4e4b\u4eba\uff0c\u7a0d\u4e00\u6c89\u541f\uff0c\u63a5\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u5018\u82e5\u8fd9\u5e76\u975e\u4f20\u95fb\u4e4b\u8a00\uff0c\u800c\u662f\u51fa\u81ea\u5148\u5e08\u7956\u4e4b\u53e3\u5462\uff1f\u8d24\u5f1f\u662f\u5426\u4f9d\u7136\u4e0d\u80af\u76f8\u4fe1\u5462\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6123\u6123\u5730\u9053\uff1a\u201c\u5251\u5723\u81ea\u5df1\u8bf4\u7684\uff1f\u201d \u3000\u3000\u6562\u60c5\u5979\u77e5\u9053\u65b9\u96ea\u5b9c\u7684\u4e00\u5207\uff0c\u8fdc\u8d85\u8fc7\u65b9\u96ea\u5b9c\u6240\u60f3\u8c61\u7684\u591a\uff0c\u662f\u4ee5\uff0c\u8fd9\u5251\u5723\u4e24\u5b57\u51fa\u53e3\uff0c\u65b9\u96ea\u5b9c\u7acb\u5373\u63a5\u9053\uff1a\u201c\u8d24\u5f1f\u77e5\u9053\u7684\u4e8b\u4e0d\u5c11\u554a\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u53f9\u606f\u9053\uff1a\u201c\u77e5\u9053\u592a\u591a\uff0c\u771f\u4e0d\u662f\u798f\u6c14\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u867d\u7136\u5e74\u7eaa\u4e0d\u592a\u5927\uff0c\u4f46\u4ed6\u4e5f\u66fe\u5386\u7ecf\u4e86\u751a\u591a\u7684\u8270\u56f0\u98a0\u6c9b\uff0c\u5c24\u5176\u662f\u5609\u5b9a\u57ce\u5916\uff0c\u5927\u4f2f\u7236\u7adf\u7136\u8981\u638c\u51fb\u81ea\u5df1\u4e4b\u4e8b\u4ee5\u540e\uff0c\u4ed6\u90a3\u5fc3\u7075\u4e4b\u4e2d\uff0c\u4e00\u76f4\u9690\u9690\u7ea6\u7ea6\u5730\u6709\u4e86\u4e00\u4e1d\u538c\u70e6\u5c18\u56a3\u4e4b\u5fc3\uff0c\u6b64\u523b\u542c\u5f97\u5b89\u5c0f\u840d\u8fd9\u53e5\u8bdd\uff0c\u4e0d\u7981\u52fe\u8d77\u4ed6\u90a3\u6f5c\u4f0f\u7684\u51fa\u4e16\u4e4b\u601d\uff0c\u9893\u7136\u957f\u53f9\u9053\uff1a\u201c\u8d24\u5f1f\u8bf4\u7684\u6709\u7406\uff0c\u54b1\u4eec\u5c11\u77e5\u9053\u4e00\u4e9b\u4e8b\uff0c\u6b63\u662f\u591a\u4e00\u5206\u798f\u6c14\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6014\u6014\u5730\u77a7\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u8eab\u4f20\u5251\u5723\u3001\u5251\u795e\u7684\u4e00\u8109\u6b66\u529f\uff0c\u5c55\u7fc5\u5343\u91cc\uff0c\u9e4f\u7a0b\u672a\u53ef\u9650\u91cf\uff0c\u600e\u4e48\u5ffd\u7136\u4e5f\u8bf4\u51fa\u8fd9\u7b49\u60b2\u89c2\u7684\u8bdd\u6765\u5462\uff1f\u96be\u9053\u2026\u2026\u201d\u5979\u5ffd\u7136\u987f\u4f4f\u8bdd\u97f3\uff0c\u9ed8\u7136\u5782\u9996\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4ef0\u89c6\u90a3\u4e24\u5cb8\u7fa4\u5c71\uff0c\u6005\u6005\u7136\u63a5\u9053\uff1a\u201c\u8d24\u5f1f\u4f55\u4e0d\u8bf4\u4e0b\u53bb\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u59be\u8eab\u5ffd\u7136\u89c9\u51fa\uff0c\u65b9\u5144\u4e0d\u50cf\u90a3\u5177\u6709\u4f24\u5fc3\u6000\u62b1\u4e4b\u4eba\uff0c\u662f\u4ee5\u4f4f\u53e3\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4f24\u5fc3\u6000\u62b1\uff0c\u5c0f\u5144\u5012\u662f\u6ca1\u6709\uff0c\u53ea\u662f\u2026\u2026\u53ea\u662f\u2026\u2026\u5c0f\u5144\u5374\u6709\u51e0\u6869\u6069\u4ec7\uff0c\u5fc5\u987b\u5728\u6709\u751f\u4e4b\u5e74\u4e00\u4e00\u89e3\u51b3\uff0c\u65b9\u80fd\u5b89\u5fc3\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u65b9\u5144\u8fd9\u4e48\u5e74\u8f7b\uff0c\u600e\u4f1a\u7275\u6d89\u4e86\u8bb8\u591a\u6069\u6028\u5728\u8eab\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0a\u4e00\u4ee3\u7684\u6069\u6028\uff0c\u6211\u7b49\u4f5c\u4e3a\u4e0b\u4e00\u4ee3\u4e4b\u4eba\uff0c\u603b\u4e0d\u80fd\u8896\u624b\u4e0d\u95ee\u5427\uff1f\u8d24\u5f1f\uff0c\u4f60\u5462\uff0c\u6709\u6ca1\u6709\u4ec0\u4e48\u7a9d\u5fc3\u4e4b\u4e8b\uff0c\u53eb\u4f60\u70e6\u607c\u3002\u201d \u3000\u3000\u4e24\u4eba\u7684\u8bdd\u5934\uff0c\u7adf\u7136\u8d8a\u626f\u8d8a\u8fdc\u4e86\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u63a5\u9053\uff1a\u201c\u59be\u8eab\u5c1a\u65e0\u6c5f\u6e56\u6069\u6028\u7f20\u8eab\uff01\u201d \u3000\u3000\u4f46\u5979\u53c8\u5ffd\u7136\u4f4e\u5934\u4e00\u53f9\u9053\uff1a\u201c\u65b9\u5144\uff0c\u59be\u8eab\u6709\u4e00\u4ef6\u4e8b\u751a\u662f\u4e0d\u89e3\uff0c\u4e0d\u77e5\u65b9\u5144\u53ef\u5426\u6307\u70b9\u8ff7\u6d25\uff1f\u201d \u3000\u3000\u65b9\u96ea\u6709\u4e00\u6014\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u4e60\u7ec3\u6b66\u529f\uff0c\u662f\u5426\u5c31\u662f\u4e3a\u4e86\u4ec7\u6740\u62a5\u590d\uff0c\u6063\u9020\u8840\u8165\u4e8b\u4ef6\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u90a3\u600e\u4e48\u4f1a\uff1f\u201d\u957f\u957f\u5730\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u63a5\u9053\uff1a\u201c\u6211\u7b49\u7ec3\u6b66\uff0c\u4ece\u5c0f\u5904\u770b\uff0c\u4e43\u662f\u5f3a\u8eab\u4fdd\u8eaf\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u5927\u5904\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u884c\u9053\u964d\u9b54\uff0c\u4e3a\u5929\u4e0b\u82cd\u751f\u7acb\u547d\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5ae3\u7136\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u53d7\u6559\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5251\u76fe\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u8d24\u5f1f\u600e\u5730\u5ffd\u7136\u95ee\u8d77\u8fd9\u7b49\u4e8b\u6765\u4e86\uff0c\u83ab\u975e\u4f60\u4e5f\u4e0d\u66fe\u771f\u6b63\u6446\u8131\u90a3\u6069\u6028\u4ec7\u6068\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u59be\u8eab\u53ea\u662f\u4e00\u65f6\u597d\u5947\uff0c\u65b9\u5144\u4e0d\u5fc5\u591a\u5fc3\uff01\u201d \u3000\u3000\u5979\u53e3\u4e2d\u8bf4\u5f97\u5341\u5206\u8f7b\u677e\uff0c\u4f46\u5fc3\u5934\u5374\u662f\u4e00\u7247\u6c89\u91cd\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u672a\u66fe\u591a\u5fc3\uff0c\u81ea\u7136\u4e0d\u77e5\u8eab\u65c1\u7684\u8fd9\u4f4d\u5c11\u5973\u5fc3\u5883\uff0c\u95fb\u8a00\u4e4b\u540e\uff0c\u63a5\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u6614\u5e74\u5c0f\u5144\u7684\u5e08\u7956\u5728\u5929\u53f0\u4e00\u6218\uff0c\u867d\u7136\u632b\u8d25\u4e86\u4ee4\u7956\u4e1c\u6d77\u53cc\u4ed9\uff0c\u5b9e\u9645\u4e0a\u5e76\u672a\u4f24\u5bb3\u8fd9\u4e24\u4f4d\u8001\u4eba\u2026\u2026\u201d \u3000\u3000\u4ed6\u5ffd\u53c8\u56de\u5230\u672c\u9898\uff0c\u5012\u662f\u5927\u51fa\u5b89\u5c0f\u840d\u610f\u6599\u4e4b\u5916\uff0c\u5f53\u4e0b\u4f4e\u58f0\u9053\uff1a\u201c\u65b9\u5144\u8fd9\u8bdd\u662f\u4ee4\u5e08\u8bf4\u7684\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d\u8bdd\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u636e\u5148\u5e08\u544a\u77e5\uff0c\u8fd9\u4e8b\u7ecf\u8fc7\uff0c\u5e08\u7956\u66fe\u5f88\u8be6\u7ec6\u5730\u544a\u8bc9\u8fc7\u5148\u5e08\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5ffd\u7136\u4f4e\u4f4e\u4e00\u53f9\u9053\uff1a\u201c\u9648\u5927\u4fa0\u771f\u662f\u5f88\u6709\u773c\u5149\uff0c\u770b\u6765\u5251\u5723\u7edd\u5b66\uff0c\u5fc5\u5c06\u5f88\u5feb\u518d\u5728\u6b66\u6797\u4e4b\u4e2d\u626c\u540d\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8d24\u5f1f\u8fc7\u5956\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u76ee\u5149\u4e00\u8f6c\uff0c\u7adf\u7136\u5a07\u6ef4\u6ef4\u5730\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u5018\u82e5\u7167\u4f60\u6240\u8bf4\uff0c\u957f\u8f88\u4e4b\u8a00\uff0c\u5e94\u8be5\u7ec8\u8eab\u5949\u884c\u4e0d\u6613\uff0c\u59be\u8eab\u53ea\u6015\u6709\u4e00\u4ef6\u4e8b\u6b63\u597d\u4e0e\u65b9\u5144\u7684\u770b\u6cd5\u76f8\u53cd\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u6014\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u8ddf\u6211\u76f8\u53cd\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u6709\u5173\u6211\u7237\u7237\u7684\u4e8b\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u987f\u65f6\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4ee4\u7956\u662f\u600e\u6837\u8bb2\u7684\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u6211\u7237\u7237\u5012\u6ca1\u6709\u8bf4\u4ec0\u4e48\uff0c\u662f\u6211\u5a18\u8bb2\u7684\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4ee4\u5802\u600e\u4e48\u8bb2\u6cd5\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6c89\u541f\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u62ac\u8d77\u5934\u6765\uff0c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u59be\u8eab\u8fd8\u662f\u4e0d\u8bf4\u7684\u597d\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u6211\u2026\u2026\u6015\u4f60\u751f\u6c14\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u8d24\u5f1f\u53ea\u7ba1\u8bf4\uff0c\u6211\u4e0d\u751f\u6c14\u4fbf\u662f\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u4f4e\u58f0\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u65b9\u5144\u5f53\u771f\u4e0d\u751f\u6c14\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u611a\u5144\u4e0d\u662f\u90a3\u7b49\u5fc3\u80f8\u72ed\u7a84\u4e4b\u4eba\uff0c\u6709\u4ec0\u4e48\u8bdd\uff0c\u5c3d\u7ba1\u8bf4\u51fa\u4fbf\u662f\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5ffd\u7136\u957f\u957f\u5730\u53f9\u606f\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u672c\u6765\uff0c\u59be\u8eab\u662f\u4e0d\u5e94\u5c06\u8fd9\u7b49\u8bdd\u8bf4\u51fa\u6765\u7684\uff0c\u53ea\u662f\u2026\u2026\u201d\u5979\u987f\u4e86\u4e00\u987f\u8bdd\u97f3\uff0c\u62ac\u5934\u770b\u4e86\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u5e7d\u5e7d\u4ed6\u8bf4\u9053\uff1a\u201c\u53ea\u662f\u65b9\u5144\u8fd9\u7b49\u80f8\u65e0\u57ce\u5e9c\uff0c\u5f85\u6211\u4e00\u7247\u81f3\u8bda\uff0c\u59be\u8eab\u5982\u662f\u5b58\u5fc3\u6b3a\u9a97\u65b9\u5144\uff0c\u5c82\u4e0d\u662f\u53eb\u6211\u5fc3\u4e2d\u4e0d\u5b89\u5f97\u5f88\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u5fc3\u4e2d\u6697\u6697\u5403\u4e86\u4e00\u60ca\u3002 \u3000\u3000\u5bfb\u601d\u9053\uff1a\u5979\u8fd9\u7b49\u53e3\u6c14\uff0c\u83ab\u975e\u5f53\u771f\u662f\u5bf9\u6211\u4e0d\u5229\u3002 \u3000\u3000\u4f46\u4ed6\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4ec0\u4e48\u4e8b\u6b3a\u9a97\u4e86\u6211\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6ca1\u6709\u56de\u7b54\u65b9\u96ea\u5b9c\u7684\u6240\u95ee\uff0c\u53cd\u800c\u95ee\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u53ea\u77e5\u9053\u59be\u8eab\u6b64\u6b21\u524d\u6765\u4e2d\u539f\uff0c\u5f53\u771f\u662f\u4e3a\u4e86\u89c2\u8d4f\u540d\u5c71\u5927\u5ddd\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6014\u4e86\u4e00\u6014\uff0c\u63a5\u9053\uff1a\u201c\u83ab\u975e\u4e0d\u662f\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u5f53\u7136\u4e0d\u662f\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7eb5\u7136\u6728\u8bb7\u5fe0\u539a\uff0c\u4f46\u6b64\u523b\u4e5f\u542c\u5f97\u51fa\u5b89\u5c0f\u840d\u8bdd\u4e2d\u4e4b\u610f\uff0c\u5f26\u5916\u4e4b\u97f3\uff0c\u90fd\u662f\u4e3a\u7740\u81ea\u5df1\u800c\u6765\u7684\u4e86\u3002\u5f53\u4e0b\u76b1\u7709\u4e00\u7b11\uff0c\u6545\u4f5c\u4e0d\u89e3\u5730\u95ee\u9053\uff1a\u201c\u8d24\u5f1f\u82e5\u975e\u4e3a\u7545\u6e38\u4e2d\u539f\u5c71\u6c34\uff0c\u90a3\u53c8\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5fc3\u4e2d\u4e00\u6d3e\u82e6\u6da9\uff0c\u5fd6\u9053\uff1a\u4e5f\u4e0d\u77e5\u9053\u4ed6\u662f\u771f\u7684\u4e0d\u89e3\uff0c\u8fd8\u662f\u6709\u5fc3\u505a\u4f5c\uff1f\u2026\u2026\u7565\u4e00\u6c89\u541f\uff0c\u63a5\u9053\uff1a\u201c\u65b9\u5144\uff0c\u59be\u8eab\u8bf4\u5c06\u51fa\u6765\uff0c\u5374\u6015\u4f60\u4e0d\u4f1a\u4fe1\uff0c\u6211\u6b63\u662f\u4e3a\u4e86\u65b9\u5144\u800c\u6765\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u4e00\u60ca\uff0c\u6697\u9053\uff1a\u679c\u7136\u662f\u4e86\u3002\u4f46\u4ed6\u5374\u672a\u66fe\u70b9\u660e\uff0c\u8138\u4e0a\u4e00\u7247\u8be7\u5f02\u4e4b\u8272\uff0c\u95ee\u9053\uff1a \u3000\u3000\u201c\u8d24\u5f1f\u4e3a\u7740\u611a\u5144\u800c\u6765\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5ffd\u7136\u6251\u54e7\u4e00\u7b11\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u771f\u7684\u4e0d\u660e\u767d\uff1f\u8fd8\u662f\u6545\u610f\u88c5\u4f6f\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6b64\u523b\u53ea\u597d\u88c5\u7cca\u6d82\u5230\u5e95\uff0c\u6447\u5934\u82e6\u7b11\u9053\uff1a\u201c\u6211\u672c\u6765\u4e0d\u77e5\u9053\u554a\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u59be\u8eab\u6b64\u756a\u8fdb\u5165\u4e2d\u539f\uff0c\u4e43\u662f\u5949\u4e86\u5bb6\u6bcd\u4e4b\u547d\uff0c\u5bfb\u627e\u90a3\u5251\u795e\u9648\u5927\u4fa0\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5148\u5e08\u5417\uff1f\u201d\u5ffd\u7136\u91cd\u91cd\u5730\u5e72\u54b3\u4e86\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4ee4\u5802\u8981\u4f60\u5bfb\u627e\u5148\u5e08\uff0c\u4e3a\u7684\u4f55\u6545\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u88ab\u4ed6\u8fd9\u4e25\u8083\u7684\u8bed\u6c14\uff0c\u5f04\u5f97\u4e00\u6014\uff0c\u8f7b\u58f0\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u751f\u6c14\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u987f\u65f6\u89c9\u51fa\u81ea\u5df1\u7684\u8bed\u6c14\uff0c\u786e\u5b9e\u662f\u592a\u91cd\u4e86\u4e00\u4e9b\uff0c\u5f53\u4e0b\u6de1\u6de1\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4ee4\u5802\u8ba4\u8bc6\u5148\u5e08\u5417\uff1f\u201d \u3000\u3000\u4ed6\u6ca1\u6709\u89e3\u8bf4\u81ea\u5df1\u662f\u5426\u751f\u6c14\uff0c\u4f46\u90a3\u6de1\u6de1\u4e00\u7b11\uff0c\u5df2\u7136\u4f5c\u4e86\u6700\u597d\u7684\u89e3\u8bf4\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u56e0\u89c1\u4ed6\u542b\u7b11\u76f8\u7b54\uff0c\u82b3\u5fc3\u7565\u611f\u5bbd\u8212\uff0c\u5ae3\u7136\u5730\u9053\uff1a\u201c\u6211\u5a18\u8ddf\u9648\u5927\u4fa0\u6ca1\u6709\u89c1\u8fc7\u9762\uff0c\u81ea\u7136\u4e0d\u8ba4\u8bc6\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4ee4\u5802\u65e2\u7136\u4e0d\u8ba4\u8bc6\u5148\u5e08\u4e3a\u4f55\u8981\u8d24\u5f1f\u76f8\u9694\u5343\u91cc\uff0c\u5bfb\u627e\u4e8e\u4ed6\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u65b9\u5144\uff0c\u8fd9\u4e8b\u53ef\u662f\u6211\u7237\u7237\u8ddf\u4f60\u5e08\u7956\u7559\u4e0b\u6765\u7684\u8fc7\u8282\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u56de\u771f\u7684\u6709\u4e9b\u8ff7\u7cca\u4e86\u3002 \u3000\u3000\u4ed6\u4f9d\u7a00\u8bb0\u5f97\uff0c\u5e08\u7236\u5728\u8ff0\u53ca\u5e08\u7956\u4e0e\u4e1c\u6d77\u53cc\u4ed9\u52a8\u624b\u548c\u4ea4\u5f80\u7ecf\u8fc7\uff0c\u5374\u672a\u63d0\u53ca\u8fc7\u4ed6\u4eec\u4e4b\u95f4\u66fe\u6709\u4ec0\u4e48\u6069\u6028\uff0c\u4f46\u4ece\u5b89\u5c0f\u840d\u7684\u8bed\u610f\u4e4b\u4e2d\uff0c\u5374\u4eff\u4f5b\u9690\u7ea6\u5730\u900f\u9732\u51fa\uff0c\u5e08\u7956\u548c\u4ed6\u4eec\u4e4b\u95f4\u786c\u662f\u6709\u4e86\u8fc7\u8282\u2026\u2026 \u3000\u3000\u987f\u65f6\uff0c\u4ed6\u5fcd\u4e0d\u4f4f\u8131\u53e3\u95ee\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u6211\u90a3\u5e08\u7956\u51e0\u65f6\u7559\u4e0b\u8fd9\u4e00\u6bb5\u8fc7\u8282\uff0c\u5148\u5e08\u600e\u7684\u4ece\u672a\u8bf4\u8fc7\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u5176\u4e2d\u8be6\u60c5\uff0c\u59be\u8eab\u4e5f\u4e0d\u77e5\u6653\uff0c\u5bb6\u6bcd\u8981\u59be\u8eab\u524d\u6765\u4e2d\u539f\uff0c\u5bfb\u627e\u9648\u5927\u4fa0\u65f6\uff0c\u7279\u522b\u4ea4\u4ee3\u4e86\u4e00\u53e5\u8bdd\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4ec0\u4e48\u8bdd\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u8fdf\u7591\u4e86\u4e00\u4e0b\uff0c\u63a5\u9053\uff1a\u201c\u6253\u8d25\u9648\u5927\u4fa0\uff0c\u6bc1\u53bb\u9648\u5927\u4fa0\u5251\u795e\u7684\u5c0a\u53f7\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6d51\u8eab\u4e00\u9707\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u77a7\u7740\u4ed6\u6fc0\u52a8\u4e4b\u60c5\uff0c\u5fc3\u4e2d\u5927\u611f\u4e0d\u5b89\uff0c\u4f4e\u58f0\u5e94\u9053\uff1a\u201c\u6211\u5a18\u8fd9\u822c\u4ea4\u4ee3\uff0c\u4e3a\u4e86\u4ec0\u4e48\uff0c\u59be\u8eab\u4e5f\u4e0d\u77e5\u9053\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5bf9\u5e08\u7236\u4e00\u5411\u656c\u5982\u5929\u4eba\uff0c\u5b89\u5c0f\u840d\u7adf\u7136\u662f\u4e3a\u4e86\u8981\u51fb\u8d25\u4ed6\u5e08\u7236\uff0c\u6bc1\u53bb\u4ed6\u5e08\u7236\u5728\u6b66\u6797\u4e4b\u4e2d\u7684\u6e05\u8a89\u800c\u8fdb\u5165\u4e2d\u539f\uff0c\u786e\u5b9e\u53eb\u4ed6\u4e0d\u80fd\u5fcd\u53d7\uff0c\u5239\u90a3\u4e4b\u95f4\uff0c\u5168\u8eab\u8840\u6c14\u7ffb\u6d8c\uff0c\u6012\u6c14\u96be\u7981\uff0c\u5251\u7709\u66b4\u626c\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u6709\u4e00\u4ef6\u4e8b\uff0c\u4f60\u786e\u662f\u660e\u767d\u7684\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u542c\u4ed6\u5ffd\u7136\u6539\u53e3\u79f0\u8c13\uff0c\u5fc3\u4e2d\u5927\u5927\u4f24\u611f\uff0c\u5fd6\u9053\uff1a\u201c\u4f60\u751f\u6211\u4ec0\u4e48\u6c14\u5462\uff1f\u2026\u2026\u8fd9\u4e5f\u4e0d\u662f\u6211\u7684\u9519\u2026\u2026\u201d\u53ea\u662f\uff0c\u5979\u53e3\u4e2d\u5374\u662f\u5f88\u5a49\u8f6c\u7684\u63a5\u9053\uff1a\u201c\u65b9\u5144\u6240\u6307\u4f55\u4e8b\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5bb6\u5e08\u5df2\u7136\u4ed9\u53bb\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u95fb\u8a00\u4e00\u7b11\u9053\uff1a\u201c\u65b9\u5144\u5148\u524d\u5df2\u7136\u544a\u8bc9\u8fc7\u59be\u8eab\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u51b7\u51b7\u5730\u9053\uff1a\u201c\u4ee4\u5802\u8981\u59d1\u5a18\u8fdb\u5165\u4e2d\u539f\uff0c\u672c\u662f\u4e3a\u4e86\u5148\u5e08\uff0c\u773c\u4e0b\u5148\u5e08\u5df2\u7136\u9a7e\u8fd4\u9053\u5c71\uff0c\u59d1\u5a18\u8fd8\u6709\u4ec0\u4e48\u6253\u7b97\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u5979\u6c89\u541f\u826f\u4e45\u672a\u66fe\u8bf4\u51fa\u4e0b\u9762\u4e4b\u8a00\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5ffd\u7136\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5982\u82e5\u65b9\u67d0\u4e0d\u66fe\u6599\u9519\uff0c\u59d1\u5a18\u6d3e\u4eba\u7ea6\u6211\uff0c\u90a3\u5b9a\u662f\u522b\u6709\u6240\u56fe\u7684\u4e86\u3002\u201d\u58f0\u97f3\u51b7\u9177\uff0c\u8a00\u8bcd\u66f4\u662f\u5484\u5484\u903c\u4eba\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u4e00\u65f6\u4e4b\u95f4\uff0c\u53ea\u89c9\u65b9\u96ea\u5b9c\u5b9b\u5982\u53e6\u5916\u4e00\u4e2a\u4eba\u4f3c\u7684\uff0c\u4e0e\u4ed6\u5148\u524d\u90a3\u7b49\u548c\u5584\u5fe0\u539a\u7684\u795e\u60c5\uff0c\u6709\u7740\u5929\u58e4\u4e4b\u522b\u3002 \u3000\u3000\u5979\u67f3\u7709\u53cc\u9501\uff0c\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u65b9\u5144\u554a\uff01\u59be\u8eab\u5982\u662f\u5426\u8ba4\uff0c\u53ea\u6015\u4f60\u4e5f\u4e0d\u4f1a\u76f8\u4fe1\u7684\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u59d1\u5a18\u672c\u7528\u4e0d\u7740\u5426\u8ba4\uff0c\u8239\u6845\u7559\u5b57\uff0c\u5df2\u662f\u4e0d\u4e89\u4e4b\u4e8b\uff0c\u65b9\u67d0\u4e0d\u61c2\u7684\u5374\u662f\u59d1\u5a18\u5fc3\u673a\u4e4b\u6df1\uff0c\u5927\u5927\u51fa\u4e4e\u672c\u4eba\u610f\u6599\u800c\u5df2\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5fc3\u4e2d\u4e5f\u6697\u6697\u751f\u6c14\uff0c\u63a5\u9053\uff1a\u201c\u6211\u51e0\u65f6\u7528\u4e86\u5fc3\u673a\u4e86\uff0c\u4f60\u522b\u51a4\u6789\u597d\u4eba\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u51b7\u7b11\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u8fd9\u8bdd\u600e\u4f1a\u9a97\u5f97\u65b9\u67d0\uff1f\u201d\u8bed\u8a00\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u4f60\u5927\u53ef\u4e0d\u5fc5\u591a\u8d39\u8fd9\u4e9b\u624b\u811a\uff0c\u53ea\u8981\u5411\u65b9\u67d0\u8a00\u660e\uff0c\u6211\u65b9\u96ea\u5b9c\u867d\u7136\u6b66\u529f\u4e0d\u53ca\u6069\u5e08\u4e07\u4e00\uff0c\u4f46\u5e08\u95e8\u6069\u6028\uff0c\u5374\u662f\u6709\u80c6\u91cf\u627f\u62c5\u3002\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u5927\u6709\u4e00\u5207\u51b2\u7740\u6211\u6765\u4fbf\u662f\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u5446\u5446\u5730\u770b\u4e86\u65b9\u96ea\u5b9c\u534a\u664c\uff0c\u4e00\u8a00\u672a\u53d1\uff0c\u626d\u5934\u8d70\u8fdb\u4e86\u8239\u8231\u4e4b\u5185\u3002 \u3000\u3000\u5982\u6b64\u4e00\u8d70\uff0c\u5012\u53eb\u65b9\u96ea\u5b9c\u6123\u4e86\u534a\u5929\u3002 \u3000\u3000\u4e0d\u77e5\u8fc7\u4e86\u591a\u5c11\u65f6\u5149\uff0c\u4ed6\u957f\u957f\u5730\u53f9\u4e86\u53e3\u6c14\uff0c\u81ea\u8bed\u9053\uff1a\u201c\u5973\u5b69\u513f\u5bb6\uff0c\u679c\u771f\u662f\u4e0d\u53ef\u7406\u55bb\u5f97\u5f88\u3002\u201d \u3000\u3000\u4f59\u97f3\u672a\u5df2\uff0c\u5ffd\u7136\u8eab\u540e\u4f20\u6765\u4e00\u58f0\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u76b1\u7709\u8f6c\u6765\uff0c\u53ea\u89c1\u90a3\u5927\u6f20\u795e\u624b\u5b8b\u5b5a\uff0c\u4e0d\u77e5\u4f55\u65f6\u6765\u5230\u81ea\u5df1\u8eab\u540e\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u8fde\u5fd9\u62b1\u62f3\u9053\uff1a\u201c\u4f60\u8001\u9192\u6765\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u65e9\u5c31\u9192\u6765\u4e86\uff0c\u53ea\u56e0\u4e0d\u60f3\u6253\u6270\u4f60\u4eec\u4e24\u4f4d\u5531\u5531\u79c1\u8bed\uff0c\u6240\u4ee5\u8001\u673d\u53ea\u597d\u88c5\u7761\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u53ea\u89c9\u5f97\u8138\u4e0a\u5fae\u5fae\u4e00\u70ed\uff0c\u9053\uff1a\u201c\u4f60\u8001\u73a9\u7b11\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u5bb9\u4e00\u655b\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u8ddf\u5979\u5435\u67b6\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u5934\u9053\uff1a\u201c\u6ca1\u6709\u554a\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u6ca1\u6709\uff1f\u90a3\u2026\u2026\u5b89\u59d1\u5a18\u4e3a\u4ec0\u4e48\u4f24\u5fc3\u7684\u4f0f\u5e8a\u75db\u54ed\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5979\u5728\u54ed\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u53ef\u4e0d\u662f\uff1f\u5979\u9002\u624d\u5954\u8fdb\u5185\u8231\uff0c\u4f0f\u5728\u5e8a\u4e0a\u75db\u54ed\u5931\u58f0\uff0c\u8001\u673d\u9694\u8231\u8be2\u95ee\uff0c\u5979\u53ef\u4e00\u53e5\u8bdd\u4e5f\u4e0d\u613f\u8bf4\uff0c\u8001\u5f1f\uff0c\u5230\u5e95\u662f\u4ec0\u4e48\u4e8b\u60f9\u4e86\u5979\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u6211\u51e0\u65f6\u60f9\u4e86\u5979\uff1f\u53ea\u8981\u5979\u4e0d\u62db\u60f9\u6211\u5c31\u597d\u4e86\u2026\u2026\u201d \u3000\u3000\u4f46\u8fd9\u7b49\u8bdd\u5374\u53c8\u600e\u80fd\u51fa\u53e3\uff0c\u53ea\u597d\u82e6\u7b11\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u4f59\u524d\u8f88\uff0c\u6211\u5e76\u672a\u62db\u60f9\u5979\u554a\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u6447\u5934\u9053\uff1a\u201c\u4e0d\u5bf9\uff0c\u5b89\u59d1\u5a18\u4f3c\u662f\u5341\u5206\u4f24\u5fc3\uff0c\u50cf\u5979\u8fd9\u7b49\u8eab\u5177\u7edd\u4e16\u6b66\u529f\u7684\u5c11\u5973\uff0c\u82e5\u65e0\u771f\u6b63\u75db\u5fc3\u4e4b\u4e8b\uff0c\u662f\u4e0d\u4f1a\u8ba9\u4eba\u542c\u89c1\u54ed\u6ce3\u7684\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6025\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u665a\u751f\u771f\u7684\u6ca1\u6709\u60f9\u5979\u561b\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u5982\u679c\u4f60\u6ca1\u6709\u60f9\u5979\uff0c\u83ab\u4e0d\u6210\u662f\u5979\u60f9\u4e86\u4f60\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u9519\uff01\u6b63\u662f\u5979\u7ed9\u4e86\u665a\u8f88\u7684\u9ebb\u70e6\uff01\u201d \u3000 \u7b2c\u5341\u4e03\u56de\u3000\u4fa0\u9aa8\u67d4\u60c5 \u3000 \u3000\u3000\u5b8b\u5b5a\u90a3\u7b49\u53cd\u95ee\uff0c\u53ea\u662f\u6c14\u5fff\u4e4b\u8be2\uff0c\u60f3\u4e0d\u5230\u65b9\u96ea\u5b9c\u7adf\u7136\u56de\u79f0\u4e0d\u9519\uff0c\u5373\u4f7f\u5b8b\u5b5a\u89c1\u591a\u8bc6\u5e7f\uff0c\u4e5f\u4e0d\u89c9\u5730\u5446\u3002 \u3000\u3000\u4ed6\u832b\u7136\u5730\u6307\u7740\u8231\u5185\uff0c\u95ee\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u5979\u2026\u2026\u5b89\u59d1\u5a18\u4ec0\u4e48\u4e8b\u5f97\u7f6a\u4e86\u4f60\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5979\u5c45\u5fc3\u53f5\u6d4b\uff0c\u8be1\u8ba1\u591a\u7aef\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u542c\u4ed6\u7adf\u662f\u5728\u4e5d\u4e2a\u5b57\u7684\u4e00\u53e5\u8bdd\u4e2d\uff0c\u52a0\u8bf8\u5b89\u5c0f\u840d\u8eab\u4e0a\u516b\u4e2a\u5b57\u7684\u6076\u8bc4\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u4e0d\u5feb\uff0c\u6c89\u58f0\u559d\u9053\uff1a\u201c\u8001\u9053\uff0c\u6bcb\u602a\u5b89\u59d1\u5a18\u54ed\u5f97\u90a3\u7b49\u4f24\u5fc3\u4e86\uff0c\u539f\u6765\u4f60\u8001\u5f1f\u53e3\u9f7f\u8fd9\u822c\u6076\u6bd2\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u88ab\u4ed6\u559d\u5f97\u4e00\u6014\uff0c\u9053\uff1a\u201c\u6211\u6076\u6bd2\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4f60\u8001\u5f1f\u8fd8\u4e0d\u627f\u8ba4\u5417\uff1f\u8fd9\u5c45\u5fc3\u53f5\u6d4b\uff0c\u8be1\u8ba1\u591a\u7aef\u516b\u4e2a\u5b57\uff0c\u52a0\u5728\u4efb\u4f55\u4eba\u8eab\u4e0a\uff0c\u8c01\u4e5f\u4e0d\u80fd\u5fcd\u53d7\u2026\u2026\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u957f\u5401\u4e00\u53e3\u5927\u6c14\uff0c\u63a5\u9053\uff1a\u201c\u4f55\u51b5\uff0c\u4f60\u7adf\u7136\u628a\u8fd9\u7b49\u8bdd\u7528\u4e4b\u4e8e\u4e00\u4e2a\u6709\u5fc3\u52a9\u4f60\u7684\u4eba\u8eab\u4e0a\uff0c\u8001\u5f1f\u4f60\u4eca\u5929\u83ab\u4e0d\u662f\u6709\u4e9b\u795e\u4e0d\u5b88\u820d\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u5446\u9053\uff1a\u201c\u90a3\u600e\u4e48\u4f1a\uff1f\u201d \u3000\u3000\u4f46\u4ed6\u5ffd\u7136\u60f3\u5230\u5b8b\u5b5a\u8fd9\u53e5\u201c\u6709\u5fc3\u52a9\u4f60\u201d\u4e4b\u8a00\uff0c\u4e0d\u89c9\u5fc3\u4e2d\u5bfb\u601d\u9053\uff1a\u201c\u4e0d\u9519\u554a\uff0c\u5979\u679c\u7136\u662f\u4e3a\u4e86\u52a9\u6211\uff0c\u624d\u8981\u53bb\u90a3\u5c11\u6797\uff0c\u6211\u8fd9\u7b49\u7591\u5fc3\u4e8e\u5979\uff0c\u81ea\u7136\u662f\u4e0d\u5bf9\u7684\u4e86\u2026\u2026\u201d\u53ea\u662f\uff0c\u4ed6\u5ffd\u7136\u53c8\u60f3\u8d77\uff0c\u5979\u5a18\u65e2\u662f\u8981\u5979\u8fdb\u5165\u4e2d\u539f\uff0c\u5bfb\u627e\u81ea\u5df1\u5e08\u7236\uff0c\u663e\u7136\u5c31\u662f\u81ea\u5df1\u7684\u4ec7\u654c\u4e86\uff0c\u5f7c\u6b64\u65e2\u662f\u4ec7\u654c\uff0c\u5979\u53c8\u600e\u4f1a\u771f\u5fc3\u52a9\u6211\uff1f\u9664\u975e\uff0c\u5979\u4e3a\u4e86\u63a9\u85cf\u67d0\u79cd\u7978\u5fc3\uff0c\u624d\u4f1a\u5148\u884c\u793a\u4ee5\u5c0f\u6069\u5c0f\u60e0\u2026\u2026 \u3000\u3000\u65b9\u96ea\u5b9c\u8f6c\u5ff5\u81f3\u6b64\uff0c\u4e0d\u7981\u5fc3\u4e2d\u5927\u4e3a\u77db\u76fe\uff0c\u4e00\u65f6\u95f4\uff0c\u5bf9\u4e8e\u8fd9\u4f4d\u5b89\u5c0f\u840d\u7684\u771f\u6b63\u7528\u5fc3\u4f55\u5728\uff0c\u8d8a\u53d1\u89c9\u5f97\u8ff7\u7cca\u4e86\u3002 \u3000\u3000\u5b8b\u5b5a\u77a7\u4ed6\u8138\u4e0a\u795e\u8272\u4e00\u8fde\u6570\u53d8\uff0c\u77e5\u9053\u6b64\u523b\u4ed6\u5fc3\u4e2d\u5fc5\u7136\u662f\u5728\u540e\u6094\u9002\u624d\u7684\u8a00\u884c\u8fc7\u5206\uff0c\u662f\u4ee5\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u8be5\u8fdb\u53bb\u77a7\u77a7\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6014\u6014\u5730\u9053\uff1a\u201c\u8fdb\u53bb\u77a7\u77a7\uff1f\u77a7\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u723d\u7136\u5927\u7b11\u9053\uff1a\u201c\u77a7\u4ec0\u4e48\uff1f\u4f60\u8001\u5f1f\u5f53\u771f\u662f\u5728\u88c5\u7cca\u6d82\u5417\uff1f\u4f60\u628a\u5b89\u59d1\u5a18\u6c14\u5f97\u5927\u54ed\uff0c\u96be\u9053\u4e0d\u5e94\u8be5\u8fdb\u53bb\u529d\u4e0a\u4e00\u529d\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u76b1\u7709\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u5979\u2026\u2026\u201d\u5ffd\u7136\u95f4\uff0c\u4ed6\u4f4f\u53e3\u4e0d\u8bed\u3002\u82e6\u7b11\u7740\u5411\u5b8b\u5b5a\u770b\u4e86\u4e00\u773c\uff0c\u63a5\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u665a\u8f88\u5e76\u4e0d\u662f\u6709\u5fc3\u60f9\u5979\u7684\uff0c\u8fd8\u662f\u4f60\u8001\u53bb\u529d\u529d\u5b89\u59d1\u5a18\u5427\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u53ea\u542c\u5f97\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u6211\uff1f\u4f60\u8981\u8001\u673d\u53bb\u529d\u5979\u6709\u4ec0\u4e48\u7528\uff1f\u8001\u5f1f\uff0c\u89e3\u94c3\u8fd8\u987b\u7cfb\u94c3\u4eba\u7684\u9053\u7406\uff0c\u4f60\u6ca1\u542c\u8fc7\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5251\u7709\u53cc\u9501\uff0c\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u6211\u2026\u2026\u5509\uff01\u8be5\u600e\u4e48\u8bf4\u624d\u662f\u5462\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u5f88\u5bb9\u6613\uff0c\u53ea\u8981\u4f60\u81ea\u5df1\u8ba4\u9519\u5c31\u6210\uff01\u201d \u3000\u3000\u8bed\u97f3\u5fae\u5fae\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u90a3\u4e94\u5927\u9b54\u4e3b\uff0c\u4e2a\u4e2a\u90fd\u4e0d\u662f\u597d\u60f9\u4e4b\u4eba\uff0c\u5982\u679c\u4f60\u771f\u8981\u9075\u4ece\u4ee4\u5e08\u5251\u795e\u7684\u9057\u8a00\uff0c\u4e00\u4e00\u5c06\u4ed6\u4eec\u5236\u670d\uff0c\u51ed\u4f60\u8001\u5f1f\u4e00\u4eba\u4e4b\u529b\uff0c\u6050\u6015\u5f88\u96be\u594f\u6548\u554a\uff01\u201d\u8a00\u5916\u4e4b\u610f\uff0c\u5341\u5206\u660e\u663e\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5e76\u975e\u4e0d\u77e5\uff0c\u8fd9\u4f4d\u6765\u81ea\u4e1c\u6d77\u7684\u5c11\u5973\uff0c\u5728\u6b66\u529f\u4e0a\u5f88\u53ef\u80fd\u4e0d\u7a0d\u900a\u4e8e\u81ea\u5df1\uff0c\u5c06\u6765\u964d\u9b54\u536b\u9053\uff0c\u5982\u80fd\u5f97\u5979\u4e4b\u52a9\uff0c\u679c\u7136\u5927\u6709\u52a9\u76ca\uff0c\u53ea\u662f\uff0c\u5979\u660e\u660e\u662f\u5949\u4e86\u6bcd\u4eb2\u4e4b\u547d\u524d\u6765\u5411\u81ea\u5df1\u5e08\u7236\u5bfb\u4ec7\uff0c\u65e2\u5c5e\u4e16\u4ec7\uff0c\u5979\u53c8\u600e\u4f1a\u5e2e\u5f97\u4e0a\u81ea\u5df1\u4e4b\u5fd9\uff1f \u3000\u3000\u60f3\u6765\u60f3\u53bb\uff0c\u4ed6\u8fd8\u662f\u5f04\u4e0d\u61c2\u5b8b\u5b5a\u7a76\u7adf\u662f\u8981\u81ea\u5df1\u529d\u5979\u4ec0\u4e48\u3002 \u3000\u3000\u5b8b\u5b5a\u76ee\u7779\u65b9\u96ea\u5b9c\u53d1\u5446\u4e0d\u8bed\uff0c\u4e5f\u731c\u51fa\u4e86\u4ed6\u7684\u4e3a\u96be\u4e4b\u5904\uff0c\u5f53\u4e0b\u4e00\u7b11\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u6709\u4e00\u4ef6\u4e8b\uff0c\u53ea\u6015\u4f60\u8001\u5f1f\u5e76\u672a\u77a7\u51fa\u6765\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6014\u4e86\u4e00\u6014\uff0c\u6697\u9053\uff1a\u201c\u8fd8\u6709\u4ec0\u4e48\u4e8b\u6ca1\u77a7\u51fa\u6765\uff1f\u8fd9\u9ebb\u70e6\u83ab\u975e\u8fd8\u4e0d\u591f\u5417\uff1f\u201d\u4e0d\u8fc7\uff0c\u4ed6\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u90a3\u5b89\u59d1\u5a18\u5bf9\u4f60\uff0c\u4f3c\u662f\u751a\u6709\u60c5\u610f\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5ffd\u7136\u95f4\u5446\u4e86\u534a\u664c\uff0c\u4e00\u8bed\u672a\u53d1\u5730\u671b\u7740\u5b8b\u5b5a\u53d1\u6123\u3002 \u3000\u3000\u5b8b\u5b5a\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u600e\u4e48\u4e86\uff1f\u53ef\u662f\u4e0d\u4fe1\u8001\u673d\u4e4b\u8a00\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u4e86\u6447\u5934\u3002\u4ed6\u65e2\u672a\u627f\u8ba4\uff0c\u4e5f\u672a\u53cd\u5bf9\u3002 \u3000\u3000\u5b8b\u5b5a\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u5feb\u53bb\u5427\uff01\u773c\u4e0b\u54b1\u4eec\u4e0d\u53ef\u6b3a\u4fae\u5979\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u76b1\u7709\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u6ca1\u4eba\u6b3a\u4fae\u8fc7\u5979\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u8ba4\u4e3a\u6ca1\u6709\u4eba\u6b3a\u4fae\u5979\uff0c\u53ef\u662f\u5728\u5979\u800c\u8a00\uff0c\u786e\u662f\u53d7\u4e86\u59d4\u5c48\u3002\u201d \u3000\u3000\u8bed\u97f3\u7565\u987f\uff0c\u63a5\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u8bf4\u5979\u4e43\u662f\u5949\u4e86\u6bcd\u4eb2\u4e4b\u547d\uff0c\u524d\u6765\u5bfb\u4f60\u5e08\u7236\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4e86\uff0c\u4f60\u4e0d\u60f3\u60f3\uff0c\u5018\u82e5\u5979\u771f\u7684\u6709\u5411\u4f60\u5bfb\u4ec7\u4e4b\u610f\uff0c\u5979\u5e72\u5417\u53c8\u8981\u8fdc\u53bb\u5c11\u6797\uff0c\u52a9\u4f60\u9000\u5374\u5f3a\u654c\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u2026\u2026\u6211\u600e\u4e48\u6653\u5f97\u5462\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u5fae\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u4f60\u4e0d\u5fc5\u6653\u5f97\uff0c\u53ea\u8981\u731c\u4e0a\u4e00\u731c\uff0c\u5c31\u8be5\u660e\u767d\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6c89\u541f\u9053\uff1a\u201c\u90a3\u6211\u771f\u7684\u5e94\u8be5\u53bb\u5411\u5979\u966a\u7f6a\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u53eb\u8d54\u7f6a\uff0c\u8001\u5f1f\uff0c\u4f60\u53ea\u987b\u53bb\u597d\u8a00\u5b89\u6170\uff0c\u81ea\u5df1\u627f\u8ba4\u9519\u602a\u4e86\u5979\u800c\u5df2\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u89c1\u5b8b\u5b5a\u8981\u81ea\u5df1\u53bb\u5411\u5b89\u5c0f\u840d\u966a\u7f6a\uff0c\u4f3c\u4e4e\u65e0\u53ef\u5948\u4f55\u5730\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u665a\u8f88\u771f\u662f\u6709\u4e9b\u2026\u2026\u201d\u4f46\u4ed6\u8bed\u97f3\u672a\u5df2\uff0c\u4eba\u5df2\u5411\u90a3\u8231\u5185\u884c\u53bb\u3002 \u3000\u3000\u5b8b\u5b5a\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u8001\u673d\u5c31\u5728\u8fd9\u8231\u5934\u770b\u770b\u5c71\u5149\u6c34\u8272\uff0c\u4f60\u597d\u597d\u5730\u529d\u6170\u5b89\u59d1\u5a18\u5427\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u5f97\u5fc3\u4e2d\u867d\u662f\u4e0d\u5927\u8212\u670d\uff0c\u4f46\u4ed6\u4eba\u5df2\u8fdb\u4e86\u8231\u5185\uff0c\u4fbf\u4e5f\u61d2\u5f97\u518d\u51fa\u6765\u8bf4\u8bdd\u3002 \u3000\u3000\u4ed6\u7a7f\u8fc7\u4e86\u5ba2\u8231\uff0c\u521a\u521a\u6380\u5f00\u90a3\u4f5c\u4e3a\u5b89\u5c0f\u840d\u5367\u5904\u7684\u5185\u8231\u5e03\u5e18\uff0c\u4e0d\u7981\u5fc3\u4e2d\u6251\u901a\u4e00\u8df3\u3002 \u3000\u3000\u539f\u6765\u76ee\u5149\u6240\u53ca\uff0c\u90a3\u5b89\u5c0f\u840d\u7adf\u7136\u6062\u590d\u4e86\u5973\u513f\u88c5\u626e\uff0c\u659c\u8eba\u5728\u6728\u5e8a\u4e4b\u4e0a\u3002\u5b89\u5c0f\u840d\u672c\u6765\u751f\u7684\u5341\u5206\u7f8e\u8273\uff0c\u6b64\u523b\u659c\u5367\u5e8a\u4e0a\uff0c\u53ea\u662f\u63a9\u76d6\u4e86\u4e00\u89d2\u7eeb\u88ab\uff0c\u5a07\u9765\u6cea\u75d5\u72b9\u5728\uff0c\u770b\u6765\u66f4\u662f\u52a8\u4eba\u3002 \u3000\u3000\u8fd9\u4e00\u77ac\u4e4b\u95f4\uff0c\u65b9\u96ea\u5b9c\u53ef\u5c31\u4f5c\u96be\u4e86\u3002\u4ed6\u4e0d\u77e5\u9053\u81ea\u5df1\u662f\u5165\u5185\u597d\uff0c\u8fd8\u662f\u9000\u51fa\u7684\u597d\uff0c\u662f\u4ee5\uff0c\u4e00\u624b\u6380\u5e18\uff0c\u4e00\u624b\u6276\u5728\u90a3\u6728\u677f\u4e4b\u4e0a\uff0c\u5446\u5728\u5f53\u5730\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u4e0d\u77e5\u662f\u5426\u771f\u7684\u5df2\u7136\u719f\u7761\uff0c\u4f46\u5979\u80f8\u524d\u8d77\u4f0f\u4e0d\u5b9a\uff0c\u53ef\u4e5f\u663e\u793a\u4e86\u5979\u7eb5\u7136\u5728\u7761\uff0c\u4e5f\u4e0d\u592a\u5b81\u9759\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u72b9\u8c6b\u4e86\u8db3\u6709\u76cf\u8336\u65f6\u5149\uff0c\u8fd9\u624d\u91cd\u91cd\u5730\u5e72\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5b89\u8d24\u5f1f\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bed\u97f3\u752b\u843d\uff0c\u5b89\u5c0f\u840d\u5ffd\u7136\u4e00\u8dc3\u5750\u8d77\u3002 \u3000\u3000\u5979\u5446\u5446\u5730\u770b\u7740\u65b9\u96ea\u5b9c\uff0c\u534a\u664c\u63a5\u9053\uff1a\u201c\u65b9\u5144\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u77a7\u5979\u6709\u4e9b\u5931\u795e\uff0c\u5fc3\u4e2d\u987f\u65f6\u6709\u4e86\u601c\u60dc\u4e4b\u610f\uff0c\u63a5\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u6253\u6270\u4f60\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6da9\u6da9\u5730\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u54ea\u91cc\uff0c\u65b9\u5144\u6709\u4e8b\u5417\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u9053\uff1a\u201c\u6211\u5982\u8bf4\u6ca1\u4e8b\uff0c\u53ea\u662f\u6765\u770b\u770b\u5979\uff0c\u90a3\u4e00\u5b9a\u4f1a\u5f15\u8d77\u5979\u7684\u8bef\u4f1a\uff0c\u628a\u6211\u5f53\u4f5c\u90a3\u7eab\u80ef\u5b50\u5f1f\u4e86\u2026\u2026\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u5144\u5f1f\u7279\u6765\u770b\u770b\u8d24\u5f1f\uff0c\u662f\u5426\u4ecd\u5728\u751f\u6c14\u3002\u201d \u3000\u3000\u663e\u7136\uff0c\u65b9\u96ea\u5b9c\u5df2\u7ecf\u5728\u8ba4\u9519\u4e86\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u5fae\u5fae\u5730\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u6211\u600e\u6562\u751f\u4f60\u7684\u6c14\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u5f97\u5979\u8fd9\u8bdd\u4e2d\u542b\u610f\uff0c\u5f53\u4e0b\u7ea2\u7740\u8138\uff0c\u63a5\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u9002\u624d\u662f\u6211\u591a\u5fc3\u4e86\u3002\u201d\u5ffd\u7136\u62b1\u62f3\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u5c0f\u5144\u8fd9\u513f\u5148\u7ed9\u4f60\u966a\u4e2a\u4e0d\u662f\uff0c\u5c1a\u7948\u8d24\u5f1f\u83ab\u8981\u89c1\u602a\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7779\u72b6\uff0c\u5fc3\u4e2d\u5148\u524d\u7684\u59d4\u5c48\uff0c\u987f\u65f6\u4e00\u626b\u800c\u5149\u3002 \u3000\u3000\u5979\u7ea4\u7ea4\u7389\u624b\u4e00\u4f38\uff0c\u6380\u8d77\u4e86\u534a\u89d2\u7ea2\u7eeb\u7ef5\u88ab\uff0c\u672c\u5f85\u8d77\u8eab\uff0c\u4f46\u5979\u5ffd\u7136\u5a07\u9765\u7ea2\u6655\u6d8c\u8d77\uff0c\u8fc5\u901f\u5730\u53c8\u5c06\u88ab\u63a9\u5411\u8eab\u4e0a\uff0c\u4f4e\u4e0b\u7c89\u9888\uff0c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u5148\u51fa\u53bb\uff0c\u5bb9\u6211\u8d77\u6765\u554a\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5728\u5b89\u5c0f\u840d\u6253\u7b97\u8d77\u8eab\u4e4b\u9645\uff0c\u4e5f\u5df2\u53d1\u73b0\u5b89\u5c0f\u840d\u5e76\u975e\u662f\u7a7f\u7740\u5973\u513f\u88c5\u675f\uff0c\u53ea\u662f\u53ea\u7a7f\u4e86\u4e2d\u8863\u3002\u6240\u4ee5\uff0c\u5979\u4e00\u6380\u9526\u88ab\uff0c\u7acb\u5373\u7f9e\u7ea2\u4e86\u8138\uff0c\u8fde\u5fd9\u9000\u51fa\u5185\u8231\uff0c\u597d\u8ba9\u5979\u7a7f\u8863\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5728\u76ee\u775b\u5979\u53ea\u7a7f\u4e86\u4e2d\u8863\uff0c\u8eba\u5728\u5e8a\u4e0a\u4e4b\u9645\uff0c\u5176\u5b9e\u4e0d\u7b49\u5b89\u5c0f\u840d\u50ac\u8bf4\uff0c\u4eba\u5df2\u8f6c\u8fc7\u4e86\u8eab\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u542c\u5f97\u5b89\u5c0f\u840d\u8981\u81ea\u5df1\u51fa\u53bb\uff0c\u5f53\u4e0b\u7b11\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4f60\u4e0d\u5fc5\u7740\u6025\uff0c\u5982\u662f\u56f0\u5026\uff0c\u4e0d\u59a8\u591a\u7761\u4e00\u4f1a\u2026\u2026\u201d\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u4eba\u5df1\u9000\u51fa\u8231\u5916\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u5f88\u5feb\u5730\u7740\u4e0a\u957f\u886b\uff0c\u6253\u626e\u6574\u9f50\u7684\u8d70\u4e86\u51fa\u6765\u3002\u5148\u524d\u7684\u59d4\u5c48\u548c\u4e0d\u5feb\uff0c\u5239\u90a3\u95f4\u4f3c\u5df2\u5fd8\u5f97\u5e72\u5e72\u51c0\u51c0\u3002 \u3000\u3000\u5b8b\u5b5a\u773c\u89c1\u4e8c\u4eba\u51fa\u6765\uff0c\u4e0d\u7981\u7b11\u9053\uff1a\u201c\u5b89\u59d1\u5a18\uff0c\u518d\u6709\u534a\u5929\uff0c\u54b1\u4eec\u5c31\u5df2\u51fa\u4e86\u957f\u6c5f\u4e09\u5ce1\u4e86\uff0c\u4f60\u4eec\u5982\u662f\u60f3\u591a\u6b23\u8d4f\u8fd9\u4e09\u5ce1\u666f\u8272\uff0c\u53ef\u8981\u591a\u5728\u8239\u5934\u4e0a\u7ad9\u7ad9\u5566\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7ea2\u7740\u8138\uff0c\u7b11\u4e86\u4e00\u7b11\uff0c\u4e3e\u6b65\u5411\u8239\u5934\u884c\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e0d\u7531\u81ea\u4e3b\u5730\u4e5f\u8d70\u5411\u8239\u5934\uff0c\u4f46\u89c1\u6708\u8272\u6e10\u73b0\u7684\u957f\u6c5f\u4e09\u5ce1\uff0c\u679c\u7136\u522b\u6709\u4e00\u756a\u60c5\u8da3\u3002 \u3000\u3000\u5b8b\u5b5a\u76ee\u7779\u4ed6\u4eec\u5e76\u80a9\u7ad9\u5728\u8239\u9996\uff0c\u5341\u5206\u8bc6\u8da3\u5730\u5411\u8231\u5185\u9000\u53bb\uff0c\u8fd9\u65f6\u53ea\u542c\u5f97\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u6709\u4e00\u4ef6\u4e8b\uff0c\u5144\u5f1f\u53ef\u662f\u5341\u5206\u4e0d\u89e3\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u65b9\u5144\u597d\u4f3c\u5fc3\u91cc\u85cf\u7684\u4e8b\u4e0d\u5c11\u561b\uff0c\u8001\u662f\u95ee\u6765\u95ee\u53bb\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u8d24\u5f1f\u7684\u4ee4\u5802\uff0c\u4e3a\u4ec0\u4e48\u8981\u4e0e\u5148\u5e08\u4e3a\u4ec7\u5462\uff1f\u201d \u3000\u3000\u90a3\u5b89\u5c0f\u840d\u95fb\u8a00\u4e4b\u540e\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u65b9\u5144\u554a\uff0c\u4f60\u600e\u4e48\u8fd8\u662f\u5728\u60f3\u8fd9\u4ef6\u4e8b\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e8b\u5173\u5148\u5e08\u4e3a\u4eba\uff0c\u5144\u5f1f\u81ea\u7136\u653e\u5fc3\u4e0d\u4e0b\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u6211\u5df2\u7ecf\u8bf4\u8fc7\u4e86\uff0c\u90a3\u662f\u957f\u8f88\u4e4b\u95f4\u7684\u4e8b\uff0c\u6211\u4eec\u53c8\u4f55\u5fc5\u7275\u626f\u5165\u5185\u54e9\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u770b\u4e86\u5b89\u5c0f\u840d\u4e00\u773c\uff0c\u6b63\u8272\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u8fd9\u4ef6\u4e8b\u5728\u5144\u5f1f\u8a00\uff0c\u53ef\u662f\u4e0d\u80fd\u7b49\u95f2\u89c2\u4e4b\u5462\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u6697\u60f3\uff0c\u81ea\u5df1\u79c9\u627f\u5e08\u7236\u9057\u5fd7\uff0c\u4e0d\u557b\u662f\u5e08\u7236\u7684\u518d\u751f\u4e4b\u8eab\uff0c\u5e08\u7236\u7684\u4e00\u5207\u6069\u6028\uff0c\u81ea\u5df1\u5c31\u5e94\u6beb\u4e0d\u8fdf\u7591\u5730\u627f\u53d7\u4e0b\u6765\uff0c\u662f\u4ee5\uff0c\u5b89\u5c0f\u840d\u7684\u6bcd\u4eb2\u8981\u627e\u5e08\u7236\uff0c\u773c\u4e0b\u6765\u8bf4\uff0c\u7b49\u4e8e\u5c31\u5728\u627e\u81ea\u5df1\u4e00\u822c\u7684\u4e86\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u4ed6\u5fc3\u4e2d\u867d\u7136\u5728\u8fd9\u4e48\u60f3\uff0c\u53ef\u662f\u53e3\u4e2d\u5374\u5e76\u672a\u8fd9\u4e48\u8bf4\u51fa\u6765\uff0c\u53ea\u662f\u957f\u957f\u4e00\u53f9\uff0c\u9053\uff1a \u3000\u3000\u201c\u8d24\u5f1f\u4f60\u4e3a\u4f55\u4e0d\u5411\u5144\u5f1f\u660e\u8bf4\u5462\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5fc3\u4e2d\u5fd6\u9053\uff1a\u6211\u4e0d\u65e9\u5c31\u8bf4\u660e\u767d\u4e86\u4e48\uff1f\u4ed6\u4e3a\u4f55\u4e00\u518d\u8ffd\u95ee\u4e0d\u4f11\uff1f\u5bfb\u601d\u540e\uff0c\u63a5\u9053\uff1a \u3000\u3000\u201c\u65b9\u5144\uff0c\u6211\u5df2\u7ecf\u8bf4\u8fc7\u5566\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4ee4\u5802\u53ea\u662f\u8981\u4f60\u5728\u6b66\u529f\u4e0a\u51fb\u8d25\u5148\u5e08\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u53ef\u60dc\u5148\u5e08\u6b7b\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u6211\u8bf4\u8fd9\u4e8b\u5df2\u7ecf\u7b97\u5566\uff0c\u6211\u5a18\u5927\u6982\u4e5f\u4e0d\u4f1a\u518d\u751f\u6c14\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u5ffd\u7136\u4f4e\u58f0\u9053\uff1a\u201c\u4e0d\u89c1\u5f97\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u4e00\u6014\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u8d24\u5f1f\u5e94\u662f\u6bd4\u6211\u66f4\u660e\u767d\u624d\u5bf9\uff0c\u4ee4\u5802\u7684\u7528\u5fc3\uff0c\u53ea\u6015\u4e0d\u6b62\u4e8e\u6b64\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5ffd\u7136\u8138\u8272\u4e00\u53d8\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u600e\u4e48\u8fd9\u6837\u7684\u8bf4\u6211\u5a18\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u6309\u7406\u800c\u8a00\uff0c\u4f60\u6211\u65e2\u7136\u5144\u5f1f\u76f8\u79f0\uff0c\u4ee4\u5802\u4e0d\u557b\u5373\u6211\u4e4b\u6bcd\uff0c\u6211\u672c\u5e94\u5341\u5206\u5c0a\u656c\u624d\u662f\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u70b9\u5934\u4e00\u7b11\u9053\uff1a\u201c\u65b9\u5144\u5ba2\u6c14\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e5f\u81ea\u4e00\u53f9\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u4e8b\u4e0e\u613f\u8fdd\uff0c\u5144\u5f1f\u4e5f\u65e0\u53ef\u5948\u4f55\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u4f3c\u662f\u5bf9\u65b9\u96ea\u5b9c\u7684\u8fd9\u4e00\u53e5\u8bdd\uff0c\u6709\u4e9b\u4e0d\u61c2\u3002 \u3000\u3000\u5979\u6014\u6014\u5730\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u8bf4\u6211\u5a18\u4f1a\u4ec7\u89c6\u4e8e\u4f60\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f88\u53ef\u80fd\uff01\u201d\u4ed6\u987f\u4e86\u4e00\u987f\u8bdd\u97f3\uff0c\u63a5\u9053\uff1a\u201c\u4ee4\u5802\u5982\u662f\u542c\u5230\u5148\u5e08\u901d\u53bb\u4e4b\u4e8b\uff0c\u4e0b\u4e00\u6b65\u5fc5\u7136\u662f\u8981\u8d24\u5f1f\u627e\u6211\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u82b3\u5fc3\u4e00\u9707\uff0c\u9053\uff1a\u201c\u6211\u600e\u4e48\u4f1a\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6b63\u8272\u9053\uff1a\u201c\u4f1a\uff01\u4ee4\u5802\u5fc5\u5c06\u4f1a\u8fd9\u4e48\u505a\uff01\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5018\u82e5\u4ee4\u5802\u4e0d\u8fd9\u4e48\u505a\uff0c\u5979\u4e5f\u4e0d\u4f1a\u8981\u8d24\u5f1f\u8dcb\u6d89\u4e07\u91cc\u5730\u8fdb\u5165\u4e2d\u539f\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5ffd\u7136\u89c9\u5f97\uff0c\u65b9\u96ea\u5b9c\u8fd9\u756a\u4e5f\u6709\u4ed6\u7684\u9053\u7406\u3002 \u3000\u3000\u5979\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u5982\u679c\u6211\u5a18\u771f\u8fd9\u4e48\u505a\uff0c\u6211\u53c8\u8be5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6599\u4e0d\u5230\u5979\u4f1a\u6709\u6b64\u4e00\u95ee\uff0c\u5f53\u4e0b\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u53ef\u8981\u8d24\u5f1f\u81ea\u5df1\u4f5c\u4e3b\u4e86\u3002\u201d \u3000\u3000\u4ed6\u7565\u4e3a\u987f\u4e86\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u8fc7\u2014\u2014\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4e0d\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4e00\u4e2a\u4eba\u4e3a\u4eba\u5904\u4e16\uff0c\u6700\u6700\u4e0d\u53ef\u5fd8\u5374\u4e86\u601d\u8003\uff0c\u4ee4\u5802\u4e4b\u547d\uff0c\u8d24\u5f1f\u5fc5\u8981\u9075\u5b88\u624d\u662f\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6123\u4e86\u534a\u5929\uff0c\u63a5\u9053\uff1a\u201c\u65b9\u5144\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u96be\u9053\u4f60\u662f\u8981\u8ddf\u6211\u53cd\u8138\u76f8\u5bf9\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u90a3\u4e5f\u4e0d\u662f\uff0c\u6211\u53ea\u662f\u8ba4\u4e3a\uff0c\u8d24\u5f1f\u6bcd\u547d\u4e0d\u53ef\u8fdd\uff0c\u6709\u671d\u4e00\u65e5\uff0c\u4f60\u5fc5\u5c06\u4f1a\u5949\u547d\u5bfb\u627e\u5251\u795e\u7684\u4f20\u4eba\uff0c\u5230\u90a3\u65f6\u5019\uff0c\u54b1\u4eec\u53ef\u80fd\u5c31\u8981\u2026\u2026\u201d\u4ed6\u628a\u90a3\u53cd\u76ee\u76f8\u5411\u56db\u5b57\uff0c\u6ca1\u6709\u8bf4\u51fa\u53e3\u6765\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u65b9\u5144\uff0c\u8fd9\u4e8b\u53eb\u6211\u597d\u751f\u4e3a\u96be\u4e86\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4f60\u4e5f\u4e0d\u5fc5\u4e3a\u96be\uff0c\u4e3a\u4eba\u53ea\u8981\u61c2\u5f97\u4e00\u4e2a\u5b5d\u5b57\uff0c\u5c31\u53ef\u65e0\u6127\u4e8e\u5fc3\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u600e\u80fd\u6709\u4ed6\u8fd9\u7b49\u8c41\u8fbe\uff1f\u987f\u65f6\u4f4e\u4e0b\u5934\u53bb\uff0c\u534a\u664c\u6ca1\u6709\u7b54\u8bdd\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u957f\u957f\u5730\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u6211\u5982\u679c\u6ca1\u6709\u6599\u9519\uff0c\u4ee4\u5802\u81ea\u5df1\u4e0d\u5165\u4e2d\u539f\u5bfb\u627e\u5148\u5e08\u5176\u4e2d\u5fc5\u6709\u7f18\u6545\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u672c\u662f\u82b3\u5fc3\u5927\u4e3a\u61ca\u6068\uff0c\u6b63\u5728\u60f3\u7740\u4f55\u7b49\u65b9\u6cd5\uff0c\u65b9\u53ef\u907f\u514d\u4e0e\u65b9\u96ea\u5b9c\u53d1\u751f\u51b2\u7a81\uff0c\u6b64\u65f6\u95f4\u8a00\uff0c\u4e0d\u7981\u5fc3\u5934\u7075\u5149\u4e00\u73b0\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u9519\uff01\u6211\u5a18\u679c\u7136\u6709\u7f18\u6545\u624d\u4e0d\u66fe\u81ea\u5df1\u524d\u6765\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5176\u4e2d\u56e0\u679c\uff0c\u8d24\u5f1f\u53ef\u65b9\u4fbf\u8bf4\u51fa\u6765\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u5f53\u7136\u53ef\u4ee5\uff01\u201d\u5979\u6de1\u6de1\u5730\u4e00\u7b11\u9053\uff1a\u201c\u65b9\u5144\uff0c\u6211\u5a18\u7684\u6b66\u529f\uff0c\u88ab\u4eba\u5e9f\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u4e86\u4e00\u5446\u9053\uff1a\u201c\u4ee4\u5802\u5931\u53bb\u5168\u8eab\u6b66\u529f\u4e86\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u4e0b\u624b\u7684\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u6211\u7237\u7237\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u66f4\u662f\u5927\u4e3a\u5403\u60ca\uff0c\u63a5\u9053\uff1a\u201c\u4ee4\u7956\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u65b9\u5144\u83ab\u975e\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5144\u5f1f\u679c\u771f\u6709\u4e9b\u96be\u4ee5\u76f8\u4fe1\uff0c\u4ee4\u7956\u4e3a\u4f55\u8981\u5e9f\u53bb\u4ee4\u5802\u6b66\u529f\u5462\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u53f9\u606f\u9053\uff1a\u201c\u6211\u7237\u7237\u8bf4\uff0c\u6211\u5a18\u7684\u5fc3\u592a\u72e0\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u76b1\u7709\u9053\uff1a\u201c\u8fd9\u662f\u4ee4\u7956\u8bf4\u7684\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u662f\u6211\u7237\u7237\u8bf4\u7684\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u5934\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u5144\u5f1f\u6709\u4e00\u53e5\u4e0d\u5f53\u4e4b\u8a00\uff0c\u8bf4\u51fa\u6765\uff0c\u8d24\u5f1f\u83ab\u8981\u89c1\u602a\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u65b9\u5144\u5c3d\u7ba1\u8bf4\uff0c\u6211\u4e0d\u4f1a\u89c1\u602a\u7684\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u4e9b\u8bdd\u7eb5\u7136\u662f\u4ee4\u7956\u8bf4\uff0c\u8d24\u5f1f\u4e5f\u5343\u4e07\u4e0d\u53ef\u518d\u8bf4\u51fa\u53e3\uff0c\u987b\u77e5\u4f5c\u4e3a\u665a\u8f88\u4e4b\u4eba\uff0c\u51b3\u4e0d\u53ef\u5bf9\u957f\u8f88\u53e3\u51fa\u8ba5\u8bc4\u4e4b\u8a00\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6014\u6014\u63a5\u9053\uff1a\u201c\u6211\u8bb0\u4e0b\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4ee4\u5802\u4e0e\u4ee4\u7956\u95f4\uff0c\u5fc5\u7136\u6709\u4e86\u4ec0\u4e48\u8bef\u4f1a\uff0c\u5426\u5219\u4ee4\u7956\u4e5f\u4e0d\u4f1a\u5e9f\u53bb\u4ee4\u5802\u7684\u6b66\u529f\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6447\u5934\u9053\uff1a\u201c\u8fd9\u4e2a\u6211\u53ef\u4e0d\u6653\u5f97\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u76f4\u63a5\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4f60\u7684\u4e00\u8eab\u6b66\u529f\uff0c\u662f\u4f20\u81ea\u4ee4\u5802\u8fd8\u662f\u4ee4\u7956\u5462\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u662f\u7239\u7239\u6559\u7684\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5251\u7709\u4e00\u626c\uff0c\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4ee4\u5802\u8981\u5bfb\u5148\u5e08\u4e4b\u4e8b\uff0c\u4ee4\u7956\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u6211\u7237\u7237\u6b7b\u4e86\uff0c\u4ed6\u8001\u4eba\u5bb6\u5f53\u7136\u4e0d\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5ffd\u7136\u54e6\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5144\u5f1f\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u4e00\u6014\u9053\uff1a\u201c\u4f60\u660e\u767d\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5ba3\u9053\uff1a\u201c\u4ee4\u7956\u5f88\u53ef\u80fd\u662f\u56e0\u4ee4\u5802\u4e00\u5fc3\u8981\u627e\u5148\u5e08\u4e4b\u6545\uff0c\u624d\u5e9f\u4e86\u5979\u7684\u6b66\u529f\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u90a3\u2026\u2026\u4e3a\u4e86\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u6697\u60f3\uff1a\u8fd9\u672c\u662f\u5e94\u8be5\u7531\u5979\u8bf4\u51fa\u6765\uff0c\u544a\u8bc9\u6211\u7684\u4e8b\uff0c\u5374\u53d8\u6210\u4e86\u6211\u6765\u89e3\u91ca\u7ed9\u5979\u542c\uff0c\u5c82\u975e\u7b11\u8bdd\uff1f\u4f46\u4ed6\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u8fd9\u4e2a\u4e2d\u7684\u9053\u7406\uff0c\u53ea\u6015\u5168\u5728\u4ee4\u5802\u4e00\u4eba\u8eab\u4e0a\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u5e72\u8106\u8bf4\u660e\u767d\u5417\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7565\u4e00\u6c89\u541f\uff0c\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u6211\u5982\u662f\u6599\u60f3\u4e0d\u9519\uff0c\u5fc5\u662f\u4ee4\u5802\u4e0e\u5148\u5e08\u4e4b\u95f4\uff0c\u6709\u7740\u4ec0\u4e48\u8fc7\u8282\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6447\u5934\u9053\uff1a\u201c\u4e0d\u89c1\u5f97\uff0c\u6211\u2026\u2026\u5a18\u5982\u679c\u771f\u4e0e\u5251\u795e\u9648\u5927\u4fa0\u6709\u4ec0\u4e48\u8fc7\u8282\uff0c\u5979\u8001\u4eba\u5bb6\u600e\u4f1a\u53ea\u8981\u6bc1\u4ed6\u4e4b\u540d\uff0c\u800c\u4e0d\u8981\u6211\u8fdb\u5165\u4e2d\u539f\u4ee5\u540e\uff0c\u53d6\u4ed6\u7684\u6027\u547d\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u957f\u957f\u5730\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u6709\u4e00\u6869\u4e8b\uff0c\u53ea\u6015\u8d24\u5f1f\u77e5\u9053\u5f97\u4e0d\u591a\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u5728\u6b66\u6797\u4e4b\u4e2d\uff0c\u6709\u4e86\u50cf\u5148\u5e08\u90a3\u7b49\u8eab\u4efd\u548c\u540d\u671b\uff0c\u4ed6\u5982\u6218\u6b7b\u4e8e\u7586\u573a\uff0c\u867d\u7136\u5931\u53bb\u4e86\u6027\u547d\uff0c\u5374\u4e5f\u4e0d\u4f1a\u6709\u4ec0\u4e48\u62b1\u61be\u4e4b\u5904\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u8fd9\u5c31\u53eb\u89c6\u6b7b\u5982\u5f52\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7a81\u7136\u63a5\u9053\uff1a\u201c\u65b9\u5144\u8fd8\u6709\u522b\u7684\u89e3\u8bf4\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u82e6\u7b11\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4f60\u542c\u8fc7\u5934\u53ef\u65ad\uff0c\u8840\u53ef\u6d41\uff0c\u6b64\u8eab\u4e0d\u53ef\u8fb1\u8fd9\u53e5\u8bdd\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u597d\u50cf\u542c\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4e86\uff0c\u8bd5\u60f3\u5148\u5e08\u5728\u6b66\u6797\u4e4b\u4e2d\uff0c\u88ab\u4eba\u5c0a\u4e3a\u5251\u795e\uff0c\u4ed6\u5982\u662f\u88ab\u4e00\u4e2a\u5e74\u7eaa\u8f7b\u8f7b\u7684\u5c11\u5973\u6253\u8d25\u3001\u53d7\u8fb1\uff0c\u90a3\u5c82\u4e0d\u662f\u6bd4\u6b7b\u4e86\u66f4\u96be\u53d7\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6014\u6014\u5730\u671b\u7740\u65b9\u96ea\u5b9c\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u65b9\u5144\uff0c\u8fd9\u6837\u8bb2\uff0c\u6211\u5a18\u7684\u7528\u5fc3\uff0c\u6bd4\u6740\u6b7b\u5251\u795e\u9648\u5927\u4fa0\u8fd8\u8981\u72e0\u6bd2\u7684\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e5f\u8bb8\u4ee4\u5802\u7684\u7528\u610f\uff0c\u6b63\u662f\u5982\u6b64\uff0c\u53ea\u662f\u5148\u5e08\u65e2\u5df2\u4ed9\u901d\u53bb\uff0c\u4ee4\u5802\u8fd9\u756a\u7528\u5fc3\uff0c\u81ea\u4e5f\u5931\u5374\u4f5c\u7528\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5ffd\u7136\u95f4\u6c89\u826f\u4e45\uff0c\u76f4\u5230\u90a3\u8258\u6728\u8239\u5df2\u7136\u9a76\u51fa\u4e86\u5deb\u5ce1\uff0c\u8fc7\u4e86\u5df4\u4e1c\uff0c\u5979\u624d\u62ac\u8d77\u5934\u6765\uff0c\u53eb\u9053\uff1a\u201c\u65b9\u5144\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u770b\u5979\u4f4e\u5934\u6c89\u541f\uff0c\u77e5\u9053\u5fc5\u662f\u5728\u601d\u7d22\u4ec0\u4e48\u91cd\u5927\u7684\u4e8b\u4f53\uff0c\u662f\u4ee5\u4ed6\u4e5f\u9ed8\u9ed8\u4e0d\u8a00\uff0c\u5047\u4f5c\u89c2\u8d4f\u4e24\u5cb8\u7684\u5c71\u8272\uff0c\u8ba9\u5979\u597d\u4ed4\u7ec6\u7684\u601d\u5fd6\u5fc3\u4e2d\u8981\u60f3\u7684\u4e8b\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5b89\u5c0f\u840d\u5ffd\u7136\u53eb\u4e86\u4ed6\u4e00\u58f0\uff0c\u4ed6\u81ea\u662f\u4e0d\u80fd\u4e0d\u5e94\uff0c\u5f53\u4e0b\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u8d24\u5f1f\u6709\u4f55\u89c1\u6559\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u65b9\u5144\uff0c\u6211\u628a\u4f60\u521a\u624d\u7684\u8bdd\u5168\u90e8\u60f3\u4e86\u4e00\u904d\u4e86\uff0c\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u53ef\u662f\u5144\u8bf4\u9519\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u90a3\u5012\u6ca1\u6709\uff0c\u53ea\u662f\u2026\u2026\u5509\uff0c\u6211\u5a18\u600e\u4f1a\u8fd9\u4e48\u72e0\u6bd2\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\u5fd9\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u5148\u5e08\u4e5f\u5df2\u4ed9\u53bb\uff0c\u4ee4\u5802\u7528\u5fc3\u662f\u72e0\u662f\u5584\uff0c\u90a3\u4e5f\u6ca1\u6709\u5173\u7cfb\u4e86\uff0c\u4f60\u4f55\u5fc5\u803f\u803f\u4e8e\u6000\u5462\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6447\u5934\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4e8b\u60c5\u6050\u6015\u4e0d\u90a3\u4e48\u5355\u7eaf\u54e9\uff0c\u6211\u5a18\u5979\uff0c\u5509\uff01\u2026\u2026\u201d\u500f\u7136\u4f4f\u53e3\uff0c\u957f\u957f\u7684\u53d1\u51fa\u4e00\u58f0\u53f9\u606f\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5251\u7709\u5fae\u5fae\u4e00\u626c\uff0c\u7b11\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4f60\u2026\u2026\u53ef\u662f\uff0c\u62c5\u5fc3\u4ee4\u5802\u53c8\u8981\u51fa\u522b\u7684\u82b1\u6837\uff0c\u4ee4\u4f60\u53bb\u627e\u5251\u795e\u7684\u4f20\u4eba\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u4e0d\u5b89\u5730\u770b\u4e86\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u66fe\u8bf4\u5251\u4fa0\u9648\u5927\u4fa0\u53ea\u6709\u4f60\u4e00\u4e2a\u5f92\u5f1f\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8d24\u5f1f\u8981\u627e\u5251\u795e\u4f20\u4eba\uff0c\u90a3\u5c31\u662f\u6307\u7684\u5144\u5f1f\u6211\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6447\u5934\u9053\uff1a\u201c\u6211\u4e0d\u4f1a\u7684\u3002\u201d\u4f46\u5979\u8bdd\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u65b9\u5144\uff0c\u5047\u82e5\u771f\u6709\u8fd9\u4e48\u4e00\u5929\uff0c\u54b1\u4eec\u8be5\u600e\u4e48\u529e\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6014\u6014\u5730\u9053\uff1a\u201c\u8fd9\u2026\u2026\u770b\u6765\u53ea\u6709\u5404\u51ed\u6b66\u529f\u51b3\u4e00\u9ad8\u4f4e\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u8138\u4e0a\u7684\u795e\u60c5\uff0c\u4e00\u8fde\u53d8\u4e86\u51e0\u53d8\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u89c1\u5979\u795e\u6001\u4e0d\u5bf9\uff0c\u7b11\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u600e\u4e48\u4e86\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u4e00\u4f38\u624b\uff0c\u6276\u4f4f\u4e86\u53f3\u4fa7\u7684\u8239\u8237\uff0c\u6447\u5934\u9053\uff1a\u201c\u6211\u5f88\u597d\uff0c\u65b9\u5144\uff0c\u4f60\u771f\u8981\u8ddf\u6211\u6bd4\u5251\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u76b1\u7709\u5fd6\u9053\uff1a\u6211\u5f53\u7136\u4e0d\u613f\uff0c\u53ef\u662f\u5e08\u95e8\u7684\u5a01\u671b\uff0c\u5374\u53c8\u903c\u6211\u975e\u6bd4\u4e0d\u53ef\u3002\u4ed6\u5fc3\u4e2d\u8fd9\u4e48\u60f3\uff0c\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u8fd9\u4e8b\u8fd8\u65e9\u5462\uff0c\u81f3\u5c11\u5728\u4f60\u672a\u66fe\u56de\u8f6c\u4e1c\u6d77\uff0c\u544a\u77e5\u4ee4\u5802\u5251\u795e\u5df2\u6b7b\u7684\u8baf\u606f\u4e4b\u524d\uff0c\u54b1\u4eec\u8fd8\u4e0d\u4f1a\u5175\u5203\u76f8\u5411\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8bdd\u97f3\u752b\u843d\uff0c\u5b89\u5c0f\u840d\u5ffd\u7136\u7c89\u9762\u751f\u6625\uff0c\u7cbe\u795e\u5927\u632f\uff0c\u6276\u5728\u8239\u8237\u7684\u7ea4\u7ea4\u7389\u624b\uff0c\u5df2\u7f29\u4e86\u56de\u6765\u3002 \u3000\u3000\u5979\u671b\u7740\u65b9\u96ea\u5b9c\uff0c\u5ae3\u7136\u4f4e\u8bed\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u771f\u806a\u660e\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u5446\u9053\uff1a\u201c\u6211\u806a\u660e\u4ec0\u4e48\uff1f\u8d24\u5f1f\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u683c\u683c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u54b1\u4eec\u6c38\u8fdc\u4e0d\u4f1a\u6bd4\u5251\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u53ea\u542c\u5f97\u5446\u4e86\u4e00\u5446\uff0c\u63a5\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u53ea\u8981\u6211\u4e0d\u56de\u4e1c\u6d77\uff0c\u54b1\u4eec\u4e0d\u5c31\u662f\u7528\u4e0d\u7740\u6bd4\u5251\u4e86\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u6709\u4e9b\u4e0d\u4e3a\u4ee5\u7136\u5730\u6447\u5934\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u8fd9\u6837\u505a\u4e0d\u59a5\u5f53\uff0c\u4ee4\u5802\u8981\u4f60\u524d\u6765\u4e2d\u539f\uff0c\u81ea\u662f\u5341\u5206\u671f\u671b\u4f60\u80fd\u6253\u8d25\u5148\u5e08\uff0c\u65e9\u65e5\u5f52\u53bb\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u53ef\u662f\u5251\u795e\u5df2\u7136\u4ed9\u53bb\uff0c\u53cd\u6b63\u6211\u5a18\u7684\u613f\u671b\u5df2\u7ecf\u8fbe\u5230\u4e86\uff0c\u6211\u53c8\u4f55\u5fc5\u6025\u4e8e\u56de\u53bb\u5462\uff1f\u65b9\u5144\uff0c\u4f60\u8bf4\u6211\u4e0d\u5bf9\uff0c\u6211\u5012\u8ba4\u4e3a\u5f88\u6709\u9053\u7406\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u5934\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4ee4\u5802\u501a\u95fe\u800c\u671b\uff0c\u76fc\u4f60\u56de\u53bb\u4e4b\u5fc3\uff0c\u4f60\u53ef\u66fe\u60f3\u53ca\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5931\u58f0\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u4ee5\u4e3a\u6211\u5a18\u4f1a\u501a\u95fe\u800c\u671b\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u6bcd\u5973\u4e4b\u60c5\u975e\u6bd4\u5bfb\u5e38\uff0c\u4ee4\u5802\u53ea\u6015\u6b63\u65e5\u591c\u8ba1\u8f83\u7740\u8d24\u5f1f\u7684\u5f52\u671f\u54e9\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5ffd\u7136\u8138\u8272\u4e00\u6697\uff0c\u957f\u53f9\u63a5\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u8fd9\u4e00\u56de\u53ef\u662f\u5b8c\u5168\u6599\u9519\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u76b1\u7709\u9053\uff1a\u201c\u9519\u4e86\uff1f\u83ab\u975e\u4ee4\u5802\u65e0\u7231\u4f60\u4e4b\u5fc3\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u90a3\u4e5f\u4e0d\u662f\uff0c\u4e0d\u8fc7\uff0c\u6211\u5a18\u5f88\u602a\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6c89\u58f0\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4f60\u5fd8\u4e86\u5144\u5f1f\u521a\u624d\u544a\u8beb\u4e8e\u4f60\uff0c\u5b50\u5973\u4e0d\u53ef\u8bc4\u6bd4\u957f\u8005\u4e4b\u8a00\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u89c1\u4ed6\u5341\u5206\u8ba4\u771f\uff0c\u53ea\u597d\u4f4e\u58f0\u9053\uff1a\u201c\u65b9\u5144\uff0c\u6211\u518d\u4e5f\u4e0d\u63d0\u6211\u5a18\u7684\u4e8b\u4e86\u3002\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4e14\u7b49\u5c11\u6797\u4e8b\u6bd5\uff0c\u6211\u966a\u7740\u4f60\u53bb\u628a\u51e0\u4f4d\u9b54\u5934\u4e00\u4e00\u5236\u670d\uff0c\u7136\u540e\u6211\u518d\u56de\u53bb\u4e1c\u6d77\uff0c\u4f60\u8bf4\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u5fc3\u4e2d\u5927\u4e3a\u611f\u52a8\uff0c\u90a3\u4e25\u5389\u7684\u8138\u8272\uff0c\u987f\u65f6\u7f13\u548c\uff0c\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4f60\u8fd9\u756a\u76db\u610f\uff0c\u5144\u5f1f\u5fc3\u9886\u4e86\u3002\u201d \u3000\u3000\u6562\u60c5\uff0c\u4ed6\u62d2\u7edd\u4e86\u5b89\u5c0f\u840d\u52a9\u4ed6\u6536\u670d\u4e94\u5927\u9b54\u4e3b\u4e4b\u5ff5\uff0c\u8fd9\u53ef\u4f7f\u5f97\u5b89\u5c0f\u840d\u5927\u611f\u8be7\u5f02\uff0c\u6123\u6123\u5730\u63a5\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u53ef\u662f\u8ba8\u538c\u8ddf\u6211\u5728\u4e00\u8d77\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u5934\u9053\uff1a\u201c\u4e0d\u662f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u542c\u4ed6\u8bf4\u51fa\u4e0d\u662f\u8ba8\u538c\u81ea\u5df1\uff0c\u82b3\u5fc3\u4e4b\u4e2d\uff0c\u5927\u611f\u6b22\u6109\uff0c\u4f46\u4ecd\u7136\u4e0d\u89e3\uff0c\u4e3a\u4f55\u4ed6\u4f1a\u62d2\u7edd\u81ea\u5df1\u8fd9\u4efd\u52a9\u4ed6\u964d\u670d\u4e94\u5927\u9b54\u4e3b\u4e4b\u610f\uff0c\u5f53\u4e0b\u63a5\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u8981\u6211\u52a9\u4f60\u554a\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u6211\u5728\u968f\u5e08\u4e60\u827a\u4e4b\u65f6\uff0c\u66fe\u7ecf\u79c1\u81ea\u8bb8\u4e0b\u4e86\u4e00\u4e2a\u5fc3\u613f\uff0c\u6709\u751f\u4e4b\u5e74\uff0c\u5fc5\u5c06\u4ee5\u4e2a\u4eba\u4e4b\u529b\uff0c\u5c06\u90a3\u4e9b\u9b54\u5934\u6536\u670d\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6014\u4e86\u4e00\u6014\u9053\uff1a\u201c\u771f\u7684\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u81ea\u7136\u662f\u771f\u7684\uff0c\u5144\u5f1f\u66fe\u60f3\u5f53\u5e74\u5148\u5e08\u65e2\u80fd\u4e00\u4e00\u5c06\u4ed6\u4eec\u5236\u670d\uff0c\u4e3a\u4ec0\u4e48\u6211\u5c31\u4e0d\u80fd\u5462\uff1f\u6240\u4ee5\uff0c\u8d24\u5f1f\u76f8\u52a9\u4e4b\u5fc3\uff0c\u5144\u5f1f\u53ea\u597d\u5fc3\u9886\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5401\u4e86\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u679c\u771f\u662f\u5982\u6b64\uff0c\u6211\u5012\u771f\u7684\u4e0d\u4fbf\u63d2\u624b\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u9664\u4e86\u8fd9\u4e94\u5927\u9b54\u4e3b\u800c\u5916\uff0c\u5321\u6276\u6b66\u6797\u6b63\u4e49\uff0c\u8981\u505a\u7684\u4e8b\u8fd8\u5f88\u591a\uff0c\u53ea\u6015\u5230\u90a3\u65f6\uff0c\u5144\u5f1f\u8fd8\u5f97\u501f\u91cd\u8d24\u5f1f\u5462\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u95fb\u8a00\uff0c\u5a07\u7b11\u4e86\u4e00\u58f0\uff1a\u201c\u65b9\u5144\u2026\u2026\u201d \u3000\u3000\u5979\u4f3c\u4e4e\u6709\u4e9b\u5a07\u7f9e\uff0c\u4e5f\u4f3c\u4e4e\u6709\u4e9b\u5931\u671b\u540e\u7684\u60ca\u559c\uff0c\u662f\u4ee5\uff0c\u65b9\u5144\u4e24\u5b57\u51fa\u53e3\uff0c\u4e0b\u9762\u7684\u8bdd\uff0c\u5c31\u6709\u4e9b\u8bf4\u4e0d\u51fa\u6765\u4e86\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u867d\u7136\u4e0d\u80fd\u5b8c\u5168\u4e86\u89e3\u5979\u523b\u4e0b\u7684\u5fc3\u60c5\uff0c\u4f46\u7ea6\u7565\u7684\u4e5f\u4f53\u4f1a\u5230\u5979\u7684\u5a07\u7f9e\u4e4b\u610f\uff0c\u5f53\u4e0b\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u5728\u4e0b\u6709\u4e00\u4ef6\u4e8b\u4e00\u76f4\u60f3\u8bf4\uff0c\u4e0d\u77e5\u5f53\u4e5f\u4e0d\u5f53\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9ed8\u9ed8\u5730\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u63a5\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u5148\u5e08\u66fe\u7ecf\u8bf4\u8fc7\uff0c\u8d24\u5f1f\u5bb6\u4f20\u6b66\u529f\uff0c\u6b63\u597d\u514b\u5236\u4e86\u9f99\u884c\u516b\u5251\uff0c\u4e0d\u77e5\u662f\u5426\u5c5e\u5b9e\uff1f\u201d \u3000\u3000\u4ed6\u7a81\u7136\u6709\u6b64\u4e00\u95ee\u5b89\u5c0f\u840d\u4f3c\u662f\u51fa\u4e4e\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u5979\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u4e0d\u4fe1\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u9053\uff1a\u6211\u600e\u4f1a\u4e0d\u4fe1\u5462\uff1f\u4f46\u4ed6\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u5728\u4e0b\u679c\u7136\u6709\u4e9b\u5947\u602a\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u65b9\u5144\u679c\u7136\u4e0d\u60c5\u7684\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u70b9\u4e86\u70b9\u5934\uff0c\u63a5\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u5728\u4e0b\u6240\u8c13\u5e76\u975e\u7eaf\u7136\u4e0d\u4fe1\uff0c\u800c\u662f\u6709\u4e9b\u5947\u602a\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f53\u5e74\u8d24\u5f1f\u7684\u7956\u7236\u8fdb\u5165\u4e2d\u539f\uff0c\u5a01\u9547\u6b66\u6797\u4e4b\u4e8b\uff0c\u8d24\u5f1f\u53ef\u66fe\u77e5\u9053\uff1f \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u5f53\u7136\u77e5\u9053\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6c89\u541f\u9053\uff1a\u201c\u4ee4\u7956\u9000\u51fa\u4e2d\u539f\u4e4b\u6545\uff0c\u4e0d\u7528\u8bf4\uff0c\u8d24\u5f1f\u4e5f\u542c\u5230\u8fc7\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u5f53\u7136\u561b\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4e86\uff0c\u4ee4\u7956\u5f53\u5e74\u9000\u51fa\u4e2d\u539f\uff0c\u636e\u8bf4\u4e43\u662f\u56e0\u4e3a\u2026\u2026\u56e0\u4e3a\u2026\u2026 \u3000\u3000\u5b89\u5c0f\u840d\u77a7\u4ed6\u4e0d\u597d\u610f\u601d\u8bf4\u51fa\u81ea\u5df1\u7956\u7236\u4e43\u662f\u8d25\u5728\u9f99\u884c\u516b\u5251\u4e4b\u4e0b\uff0c\u82b3\u5fc3\u4e4b\u4e2d\u5927\u4e3a\u9ad8\u5174\uff0c\u77e5\u9053\u65b9\u96ea\u5b9c\u5bf9\u81ea\u5df1\u679c\u7136\u5341\u5206\u5c0a\u656c\uff0c\u5f53\u4e0b\u63a5\u9053\uff1a\u201c\u65b9\u5144\uff0c\u6211\u7237\u7237\u8d25\u5728\u4f60\u5e08\u7956\u5251\u4e0b\uff0c\u624d\u9000\u51fa\u4e2d\u539f\u7684\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6014\u6014\u5730\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5148\u5e08\u4e5f\u662f\u8fd9\u4e48\u544a\u8bc9\u6211\uff0c\u6240\u4ee5\u6211\u624d\u89c9\u5f97\u5176\u4e2d\u751a\u662f\u77db\u76fe\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u77db\u76fe\uff1f\u65b9\u5144\uff0c\u8fd9\u4e8b\u6211\u662f\u542c\u6211\u7237\u7237\u4eb2\u53e3\u8bb2\u7684\u5462\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u65e2\u7136\u4ee4\u7956\u53e3\u8ff0\uff0c\u90a3\u662f\u4e0d\u4f1a\u9519\u7684\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u672c\u6765\u4e0d\u9519\u5440\uff0c\u65b9\u5144\uff0c\u4f60\u53ef\u662f\u2026\u2026\u201d\u5979\u5ffd\u7136\u4f4f\u53e3\uff0c\u4e0d\u5f80\u4e0b\u8bf4\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u6211\u5e76\u975e\u4e0d\u76f8\u4fe1\u51e0\u4f4d\u8001\u4eba\u5bb6\u4e4b\u8a00\uff0c\u53ea\u4e0d\u8fc7\u2026\u2026\u201d\u4ed6\u987f\u4e00\u987f\u8bed\u97f3\uff0c\u957f\u957f\u4e00\u53f9\u6c14\uff0c\u9053\uff1a\u201c\u662f\u4e86\uff01\u4ee4\u7956\u8fd4\u56de\u4e1c\u6d77\u4e4b\u540e\uff0c\u91cd\u65b0\u7814\u521b\u4e86\u8fd9\u95e8\u6b66\u529f\uff0c\u624d\u4f1a\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u4e0d\u7b49\u4ed6\u8bf4\u5b8c\uff0c\u4fbf\u81ea\u683c\u683c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u54b1\u4eec\u4e0d\u8c08\u8fd9\u4ef6\u4e8b\u597d\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u6123\uff0c\u559d\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u8bdd\u5df2\u51fa\u53e3\uff0c\u624d\u89c9\u5f97\u81ea\u5df1\u7684\u58f0\u97f3\u592a\u5927\uff0c\u8fde\u5fd9\u7b11\u4e86\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u8fd9\u4e8b\u975e\u8981\u5f04\u660e\u767d\u4e0d\u53ef\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u89c1\u4ed6\u5ffd\u7136\u8fd9\u4e48\u56fa\u6267\uff0c\u5012\u4e5f\u5927\u611f\u610f\u5916\uff0c\u8038\u80a9\u9053\uff1a\u201c\u65b9\u5144\uff0c\u5176\u5b9e\u4f60\u6839\u672c\u4e0d\u7528\u62c5\u5fc3\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fd6\u9053\uff1a\u201c\u6211\u5e76\u672a\u6709\u4ec0\u4e48\u5fc3\u53ef\u62c5\u554a\u2026\u2026\u201d\u53ea\u662f\uff0c\u4ed6\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u5728\u4e0b\u5411\u6765\u4e0d\u559c\u6b22\u505a\u90a3\u534a\u540a\u5b50\u7684\u4e8b\uff0c\u6b64\u4e8b\u82e5\u4e0d\u5f04\u660e\u767d\uff0c\u6211\u6015\u8981\u5bdd\u98df\u96be\u5b89\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u771f\u86ee\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5931\u58f0\u7b11\u9053\uff1a\u201c\u662f\u5417\uff1f\u8d24\u5f1f\uff0c\u5148\u5e08\u5728\u544a\u77e5\u6211\u8fd9\u4e8b\u4e4b\u65f6\uff0c\u9690\u7ea6\u63d0\u8fc7\uff0c\u5148\u5e08\u66fe\u5728\u4e1c\u6d77\u4f4f\u4e86\u5f88\u4e45\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u6014\u9053\uff1a\u201c\u77e5\u9053\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4f60\u5e08\u7956\u4f20\u4e86\u6211\u7239\u7684\u6b66\u529f\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u5fc3\u4e2d\u4e00\u9707\uff0c\u5fd6\u9053\uff1a\u201c\u5e08\u7956\u539f\u6765\u4f20\u6388\u4e86\u5979\u7239\u7239\u6b66\u529f\uff01\u201d \u3000\u3000\u5ffd\u7136\u95ee\uff0c\u4ed6\u8111\u9645\u7075\u5149\u4e00\u73b0\u60f3\u5230\u5b89\u5c0f\u840d\u6240\u8bf4\uff0c\u5979\u5a18\u8981\u5979\u51fb\u8d25\u5251\u795e\u4e4b\u6545\uff0c\u4f1a\u4e0d\u4f1a\u4e0e\u5e08\u7956\u4f20\u6388\u6b66\u529f\u6709\u5173\uff1f \u3000\u3000\u4e00\u5ff5\u53ca\u6b64\uff0c\u4e0d\u7531\u81ea\u4e3b\u5730\u8131\u53e3\u9053\uff1a\u201c\u6211\u53c8\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u4e00\u6123\uff0c\u7b11\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u53c8\u660e\u767d\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u90a3\u5929\u9e64\u65a9\u6b66\u529f\uff0c\u4ee4\u5802\u5b66\u8fc7\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u6ca1\u6709\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u8d8a\u53d1\u5730\u6709\u4e86\u7740\u843d\uff0c\u4f46\u4ecd\u7136\u88c5\u4f5c\u4e0d\u77e5\u9053\u5730\u95ee\uff1a\u201c\u4e3a\u4ec0\u4e48\u4ee4\u5802\u4e0d\u5b66\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u8fd9\u2026\u2026\u56e0\u4e3a\u6ca1\u6709\u4eba\u6559\u5979\u561b\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4ee4\u5c0a\u4e0d\u66fe\u4f20\u6388\u4e8e\u5979\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d\u8bdd\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u65b9\u5144\uff0c\u8fd9\u5929\u9e64\u65a9\u6b66\u529f\u4e0d\u4f46\u6211\u5a18\u4e0d\u4f1a\uff0c\u5c31\u662f\u6211\u7237\u7237\u4e5f\u6ca1\u6709\u7ec3\u5230\u516b\u6210\u706b\u5019\u5462\uff01\u53ea\u6709\u6211\u7239\u7239\u4e00\u4eba\u5b8c\u5168\u8bb0\u4e0b\u4e86\u8fd9\u5957\u6b66\u529f\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u70b9\u5934\u4e00\u7b11\u9053\uff1a\u201c\u662f\u4e86\uff01\u4ee4\u5c0a\u53ea\u4f20\u4e86\u4f60\u4e00\u4eba\uff0c\u8fd9\u624d\u5f15\u8d77\u4ee4\u5802\u7684\u4e0d\u5feb\uff0c\u800c\u4e14\u6000\u6068\u5230\u5bb6\u5e08\u8eab\u4e0a\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u95fb\u8a00\uff0c\u7741\u5927\u4e86\u773c\uff0c\u9053\uff1a\u201c\u4f60\u600e\u4f1a\u77e5\u9053\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u731c\u51fa\u6765\u7684\uff01\u800c\u4e14\u2014\u2014\u201d \u3000\u3000\u5b89\u5c0f\u840d\u4e0d\u4fe1\u5730\u6447\u5934\u9053\uff1a\u201c\u6211\u4e0d\u4fe1\u4f60\u731c\u5f97\u51fa\u6765\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u4e86\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u5f53\u7136\u731c\u5f97\u5230\uff0c\u5148\u5e08\u7559\u5728\u4e1c\u6d77\u90a3\u4e48\u4e45\uff0c\u5fc5\u7136\u662f\u6709\u4ec0\u4e48\u91cd\u8981\u7684\u4e8b\u5f85\u529e\uff0c\u800c\u4f60\u4e1c\u6d77\u6b66\u529f\u53c8\u53ef\u4ee5\u514b\u5236\u9f99\u884c\u516b\u5251\uff0c\u8fd9\u5176\u4e2d\u53ea\u6709\u4e00\u4e2a\u9053\u7406\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u63a5\u9053\uff1a\u201c\u4ec0\u4e48\u9053\u7406\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5148\u5e08\u7956\u548c\u4ee4\u7956\u7684\u624d\u667a\uff0c\u521b\u4e0b\u4e86\u4e00\u95e8\u65b0\u7684\u6b66\u529f\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u53f9\u4e86\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u65b9\u5144\uff0c\u4f60\u5bf9\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u679c\u7136\u8fd9\u5929\u9e64\u65a9\u662f\u51fa\u81ea\u5148\u5e08\u7956\u7684\u4f20\u6388\u4e86\uff0c\u53ea\u662f\uff0c\u4ed6\u4eec\u4e24\u4f4d\u8001\u4eba\u5bb6\u53c8\u600e\u4e48\u60f3\u5230\uff0c\u4ee4\u5802\u5c45\u7136\u56e0\u4e3a\u5148\u5e08\u7956\u4e0d\u8bb8\u5979\u4e60\u6b66\u529f\uff0c\u800c\u6000\u6068\u5728\u5fc3\u5462\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u795e\u60c5\u4e00\u9eef\uff0c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u5bb6\u6bcd\u2026\u2026\u5979\u5df2\u7ecf\u88ab\u5148\u7956\u5e9f\u53bb\u4e00\u8eab\u6b66\u529f\uff0c\u4f60\u53c8\u4f55\u5fc5\u602a\u5979\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u6211\u5e76\u672a\u771f\u7684\u8d23\u602a\u4ee4\u5802\u554a\uff01\u4e0d\u8fc7\u5728\u4e0b\u8fd8\u6709\u4e00\u4e8b\u4e0d\u89e3\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u65b9\u5144\u4e0d\u660e\u767d\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4ee4\u5c0a\u6b63\u503c\u76db\u5e74\uff0c\u4e3a\u4f55\u7a81\u7136\u8c22\u4e16\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u4e24\u53ea\u5927\u773c\u4e2d\u5ffd\u7136\u51fa\u73b0\u4e86\u6cea\u5149\uff0c\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u65b9\u5144\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u77a7\u5979\u4f3c\u662f\u6709\u7740\u96be\u8a00\u4e4b\u9690\uff0c\u5fd9\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u5982\u679c\u4e0d\u65b9\u4fbf\u8bf4\uff0c\u90a3\u5c31\u7b97\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6447\u5934\u9053\uff1a\u201c\u65b9\u5144\uff0c\u8fd9\u4e8b\u4e5f\u6ca1\u6709\u4ec0\u4e48\u4e0d\u53ef\u544a\u4eba\u4e4b\u5904\uff0c\u6211\u7239\u662f\u56e0\u4e3a\u6211\u5a18\u6d3b\u6d3b\u6c14\u6b7b\u7684\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6123\u4e86\u534a\u5929\uff0c\u9053\uff1a\u201c\u6709\u8fd9\u7b49\u4e8b\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u8fd9\u4e8b\u5f52\u6839\u7ed3\u5e95\uff0c\u8fd8\u662f\u4e3a\u4e86\u4f60\u5e08\u7956\u9f99\u8001\u524d\u8f88\u4f20\u6388\u4e86\u6211\u7239\u6b66\u529f\uff0c\u6211\u5a18\u5929\u5929\u903c\u7740\u6211\u7239\u8981\u4ed6\u4f20\u6388\uff0c\u4f46\u662f\uff0c\u6211\u7239\u56e0\u4e3a\u7b54\u5e94\u8fc7\u9f99\u8001\u524d\u8f88\uff0c\u5929\u9e64\u65a9\u6b66\u529f\uff0c\u4f20\u5973\u4f20\u5b50\uff0c\u5374\u4e0d\u4f20\u59bb\u59be\u4e4b\u6212\uff0c\u6240\u4ee5\u4e00\u76f4\u4e0d\u66fe\u7b54\u5e94\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u90a3\u4e5f\u7528\u4e0d\u7740\u6b7b\u554a\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u65b9\u5144\uff0c\u6211\u5a18\u7684\u6027\u60c5\uff0c\u539f\u6765\u5c31\u5341\u5206\u4e56\u50fb\uff0c\u81ea\u4ece\u5979\u672a\u80fd\u4e60\u7ec3\u5929\u9e64\u65a9\u6b66\u529f\u4ee5\u540e\uff0c\u53d8\u5f97\u66f4\u662f\u4e0d\u8fd1\u60c5\u7406\uff0c\u636e\u8bf4\u5979\u66fe\u7ecf\u51e0\u6b21\u60f3\u6697\u4e2d\u5728\u4f60\u5e08\u7956\u8eab\u4e0a\u4e0b\u6bd2\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u60ca\u9053\uff1a\u201c\u8fd9\u2026\u2026\u592a\u8fc7\u5206\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u53ef\u4e0d\u662f\uff1f\u5426\u5219\u6211\u7237\u7237\u4e5f\u4e0d\u4f1a\u6c14\u5f97\u5e9f\u53bb\u5979\u7684\u6b66\u529f\u4e86\u3002\u201d\u8bdd\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a \u3000\u3000\u201c\u6211\u5a18\u6b66\u529f\u88ab\u5e9f\u4ee5\u540e\uff0c\u9f99\u8001\u524d\u8f88\u4e5f\u5df2\u56de\u8f6c\u4e2d\u539f\uff0c\u4e0d\u4e45\u6211\u5c31\u51fa\u4e16\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4ee4\u5802\u6709\u4e86\u9aa8\u8089\uff0c\u6027\u60c5\u5e94\u662f\u53d8\u597d\u4e86\u5427\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4ee4\u5c0a\u5230\u5e95\u662f\u600e\u4e48\u6b7b\u53bb\u7684\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u6c14\u6b7b\u7684\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u81ea\u662f\u4e0d\u5927\u76f8\u4fe1\uff0c\u76b1\u7709\u9053\uff1a\u201c\u5c0a\u5927\u4eba\u4e43\u662f\u8eab\u5177\u6781\u9ad8\u6b66\u529f\u4e4b\u4eba\uff0c\u600e\u4f1a\u88ab\u4ee4\u5802\u6c14\u6b7b\u5462\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u957f\u957f\u4e00\u53f9\u9053\uff1a\u201c\u65b9\u5144\uff0c\u6b64\u4e8b\u8bf4\u6765\u4e22\u4eba\u5f97\u5f88\uff0c\u4f55\u51b5\u53c8\u5173\u7cfb\u5230\u5bb6\u6bcd\u7684\u540d\u8282\uff0c\u6211\u5b9e\u5728\u96be\u4ee5\u542f\u9f7f\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u6014\u9053\uff1a\u201c\u539f\u6765\u2026\u2026\u5982\u6b64\u2026\u2026\u201d\u8bdd\u97f3\u4e00\u987f\uff0c\u7acb\u5373\u63a5\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u65e2\u7136\u4f60\u4e0d\u4fbf\u542f\u53e3\uff0c\u90a3\u5c31\u4e0d\u8981\u8bf4\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u611f\u6fc0\u5730\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u5173\u4e8e\u5bb6\u6bcd\u4e4b\u547d\uff0c\u59be\u8eab\u662f\u4e0d\u4f1a\u5949\u884c\u7684\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u90a3\u2026\u2026\u59d1\u5a18\u4e0d\u662f\u4e0d\u5b5d\u4e86\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6447\u5934\u9053\uff1a\u201c\u957f\u8005\u4e71\u547d\uff0c\u505a\u665a\u8f88\u7684\u53ef\u4ee5\u4e0d\u53d7\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fae\u5fae\u70b9\u5934\uff0c\u63a5\u9053\uff1a\u201c\u59d1\u5a18\u8bf4\u5f97\u4e5f\u6709\u9053\u7406\u3002\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u4f55\u51b5\u4ee4\u7956\u548c\u4ee4\u5c0a\u90fd\u4e0d\u8bb8\u5979\u8fd9\u4e48\u505a\uff0c\u59d1\u5a18\u4e0d\u9075\u6bcd\u547d\uff0c\u90a3\u4e5f\u4e0d\u7b97\u4ec0\u4e48\u5927\u9519\u7684\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u53ef\u4e0d\u662f\uff1f\u59be\u8eab\u662f\u9075\u884c\u7237\u7237\u548c\u7239\u7239\u7684\u9057\u547d\u561b\uff01\u201d \u3000\u3000\u5ffd\u7136\u4e4b\u95f4\uff0c\u5979\u663e\u5f97\u5341\u5206\u9ad8\u5174\uff0c\u90a3\u672c\u6765\u4f3c\u559c\u5b9c\u55d4\u7684\u7c89\u9762\uff0c\u8d8a\u53d1\u51fa\u843d\u5f97\u5341\u5206\u6807\u81f4\uff0c\u53ea\u628a\u65b9\u96ea\u5b9c\u770b\u5f97\u5446\u4e86\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u4e3e\u624b\u63a0\u4e86\u63a0\u9b13\u8fb9\u957f\u53d1\uff0c\u7b11\u9053\uff1a\u201c\u65b9\u5144\uff0c\u6211\u2026\u2026\u201d\u7a81\u7136\uff0c\u5979\u4f4f\u53e3\u4e0d\u8bed\uff0c\u8fc5\u5feb\u5730\u4f4e\u4e0b\u5934\u53bb\u3002 \u3000\u3000\u6562\u60c5\uff0c\u65b9\u96ea\u5b9c\u90a3\u53d1\u5446\u7684\u795e\u60c5\uff0c\u4f7f\u5979\u5927\u611f\u7f9e\u6da9\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5ffd\u7136\u4e0d\u95fb\u8bdd\u8bed\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u7565\u4e00\u5b9a\u795e\uff0c\u8fd9\u624d\u89c9\u51fa\u81ea\u5df1\u8fd9\u822c\u5446\u770b\u7740\u4e00\u4f4d\u5973\u5b69\u513f\u5bb6\uff0c\u5b9e\u6709\u4e9b\u5931\u793c\u3002 \u3000\u3000\u5f53\u4e0b\u8fde\u5fd9\u79fb\u5f00\u4e86\u534a\u6b65\uff0c\u957f\u957f\u5730\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u671b\u7740\u6c5f\u4e2d\u6d4a\u6c34\uff0c\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u54b1\u4eec\u5df2\u7136\u51fa\u4e86\u4e09\u5ce1\u5929\u9669\u4e86\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u867d\u7136\u4e00\u8eab\u6587\u58eb\u6253\u626e\uff0c\u4f46\u5979\u4e3e\u52a8\u4e4b\u95f4\uff0c\u4f9d\u7136\u4e0d\u8131\u5973\u513f\u8154\u6001\uff0c\u8fd9\u65f6\uff0c\u4e0d\u7531\u81ea\u4e3b\u5730\u4e3e\u8896\u4ffa\u53e3\uff0c\u7ea2\u7740\u8138\u62ac\u8d77\u5934\u6765\uff0c\u4f4e\u5e94\u9053\uff1a\u201c\u662f\u554a\uff0c\u54b1\u4eec\u53ea\u987e\u8bf4\u8bdd\uff0c\u628a\u8fd9\u4e00\u5e26\u7684\u98ce\u5149\uff0c\u5168\u90e8\u7ed9\u9519\u8fc7\u4e86\u2026\u2026\u201d \u3000\u3000\u5ffd\u7136\uff0c\u5b8b\u59a5\u6253\u8231\u4e2d\u5927\u6b65\u884c\u6765\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u53ea\u77e5\u804a\u4e2a\u4e0d\u505c\uff0c\u53ef\u77e5\u6b64\u523b\u8eab\u5728\u4e86\u4f55\u5904\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u3001\u5b89\u5c0f\u840d\u540c\u65f6\u8138\u4e0a\u53d1\u70e7\uff0c\u8baa\u8baa\u4e00\u7b11\uff0c\u4e0d\u66fe\u7b54\u8bdd\u3002 \u3000\u3000\u5b8b\u59a5\u6307\u7740\u90a3\u53f3\u624b\u7684\u4e00\u5e26\u5c71\u52bf\uff0c\u7b11\u9053\uff1a\u201c\u90a3\u5c16\u5c16\u7684\u79c3\u5cf0\uff0c\u5df1\u7136\u662f\u8346\u95e8\u5c71\u4e4b\u5cf0\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u95fb\u8a00\uff0c\u9053\uff1a\u201c\u8346\u95e8\u5c71\uff1f\u83ab\u975e\u5df2\u7ecf\u8fc7\u4e86\u5b9c\u660c\u4e86\u5417\uff1f\u201d \u3000\u3000\u5b8b\u59a5\u6447\u5934\u9053\uff1a\u201c\u8fd8\u6ca1\u6709\u90a3\u4e48\u5feb\uff0c\u4e0d\u8fc7\u518d\u6709\u534a\u4e2a\u65f6\u8fb0\uff0c\u4e5f\u5c31\u5230\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u76b1\u7740\u7709\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u54b1\u4eec\u5e94\u8be5\u8d77\u65f1\u4e86\u3002\u201d \u3000\u3000\u5b8b\u59a5\u9053\uff1a\u201c\u5230\u4e86\u5b9c\u660c\uff0c\u54b1\u4eec\u5c31\u7531\u65f1\u8def\u8d70\u5f53\u9633\uff0c\u8fc7\u5ba3\u57ce\uff0c\u8d8a\u8944\u9633\uff0c\u76f4\u5954\u6cb3\u5357\u7701\u5883\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5251\u7709\u8038\u52a8\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u4e0b\u6c34\u8239\u53ef\u771f\u5feb\u2026\u2026\u602a\u4e0d\u5f97\u674e\u767d\u6709\u90a3\u5343\u91cc\u6c5f\u9675\u4e00\u65e5\u8fd8\u4e4b\u53e5\u5462\uff01\u201d \u3000\u3000\u5b8b\u59a5\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u8fd9\u4e00\u6bb5\u6c34\u7a0b\uff0c\u6c5f\u6d41\u4f3c\u7bad\uff0c\u4e00\u65e5\u5343\u91cc\uff0c\u4e5f\u662f\u610f\u6599\u4e2d\u4e8b\uff0c\u4f55\u51b5\u54b1\u4eec\u8fd9\u4e00\u6761\u5feb\u821f\uff0c\u53c8\u6b63\u5982\u9752\u83b2\u5c45\u58eb\u6240\u8c13\u7684\u8f7b\u821f\u54e9\u2026\u2026\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5b9c\u660c\u57ce\u5df2\u7136\u5728\u671b\u3002 \u3000\u3000\u5b8b\u59a5\u56de\u987e\u4e86\u821f\u5b50\u4e00\u773c\uff0c\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u548c\u5b89\u59d1\u5a18\u6253\u70b9\u4e00\u4e0b\u8863\u7269\uff0c\u8001\u673d\u5c31\u53bb\u5f00\u53d1\u8239\u8d44\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6325\u624b\u6253\u6000\u4e2d\u53d6\u51fa\u4e00\u7247\u91d1\u53f6\uff0c\u9012\u4e86\u8fc7\u53bb\uff0c\u7b11\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u665a\u8f88\u8fd9\u513f\u6709\u94b1\u3002\u201d\u5b8b\u59a5\u4e24\u773c\u4e00\u77aa\uff0c\u6447\u5934\u9053\uff1a\u201c\u4f60\u6015\u8001\u673d\u4ed8\u4e0d\u8d77\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6014\u4e86\u4e00\u6014\u9053\uff1a\u201c\u665a\u8f88\u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\u2026\u2026\u201d \u3000\u3000\u5b8b\u59a5\u9053\uff1a\u201c\u65e2\u7136\u4e0d\u6015\u8001\u673d\u4ed8\u4e0d\u8d77\uff0c\u4f60\u4e0d\u5c06\u8fd9\u91d1\u53f6\u6536\u56de\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u65e0\u53ef\u5948\u4f55\u5730\u6447\u5934\u9053\uff1a\u201c\u8fd9\u672c\u662f\u4e3a\u4e86\u665a\u8f88\u4e4b\u4e8b\uff0c\u600e\u53ef\u8981\u4f60\u8001\u7834\u8d39\u5462\uff1f\u201d\u4ed6\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5b8b\u59a5\u5df2\u5411\u8231\u5c3e\u884c\u53bb\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u77a7\u7740\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u5b8b\u8001\u53e4\u9053\u70ed\u80a0\uff0c\u65b9\u5144\uff0c\u4f60\u83ab\u8981\u4e3a\u8fd9\u70b9\u5c0f\u4e8b\u4e0d\u5b89\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u82e6\u7b11\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8fd9\u4f4d\u8001\u524d\u8f88\u771f\u662f\u4e00\u4f4d\u5947\u4eba\u2026\u2026\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5df2\u5c06\u90a3\u91d1\u53f6\u63e3\u5165\u6000\u4e2d\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u5728\u8231\u5934\u77a7\u7740\u4ed6\u4eec\u62e2\u5cb8\uff0c\u59be\u8eab\u53bb\u6536\u62fe\u8863\u7269\u2026\u2026\u201d \u3000\u3000\u4e0d\u7b49\u65b9\u96ea\u5b9c\u591a\u8bf4\uff0c\u4e00\u626d\u5934\u8fdb\u4e86\u8231\u5185\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u8239\u5df2\u7f13\u7f13\u6cca\u5411\u5cb8\u8fb9\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e3e\u76ee\u6253\u91cf\u90a3\u5b9c\u660c\u5cb8\u4e0a\uff0c\u53ea\u89c1\u5f80\u6765\u884c\u4eba\u8f90\u51d1\uff0c\u663e\u5f97\u5341\u5206\u70ed\u95f9\uff0c\u800c\u4e14\u6cbf\u5cb8\u4e00\u5e26\uff0c\u821f\u63d6\u6797\u7acb\uff0c\u4e00\u7247\u7199\u6518\u5608\u6742\u4e4b\u58f0\uff0c\u4e0d\u7edd\u4e8e\u8033\u3002 \u3000\u3000\u62e2\u5cb8\u4ee5\u540e\uff0c\u4e09\u4eba\u5c1a\u672a\u8e0f\u4e0a\u8df3\u677f\uff0c\u90a3\u5b9c\u660c\u57ce\u4e2d\u7684\u5ba2\u6808\u4f19\u8ba1\uff0c\u5df2\u7ecf\u62a2\u5148\u8dd1\u4e0a\u8239\u6765\u515c\u63fd\u751f\u610f\u4e86\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u7709\u5934\u4e00\u76b1\uff0c\u6b63\u81ea\u5bfb\u601d\u5982\u4f55\u5e94\u5bf9\uff0c\u53ea\u89c1\u5b8b\u5b5a\u5927\u6b65\u884c\u6765\uff0c\u5411\u90a3\u4e94\u516d\u540d\u4f19\u8ba1\u626b\u4e86\u4e00\u773c\uff0c\u559d\u9053\uff1a\u201c\u54b1\u4eec\u662f\u4e13\u7a0b\u524d\u6765\u62dc\u5019\u8d35\u5730\u98de\u9f99\u5e9c\u6731\u5927\u5b98\u4eba\uff0c\u4f60\u4eec\u8fd9\u7b49\u62c9\u62c9\u626f\u626f\uff0c\u4e0d\u6015\u6731\u5927\u5b98\u4eba\u602a\u7f6a\u5417\uff1f\u201d \u3000\u3000\u90a3\u56db\u4e94\u540d\u4f19\u8ba1\u95fb\u8a00\uff0c\u987f\u65f6\u8138\u8272\u4e00\u53d8\uff0c\u6362\u4e86\u4e00\u526f\u6bd5\u606d\u6bd5\u656c\u7684\u795e\u60c5\uff0c\u62f1\u624b\u4e3a\u793c\uff0c\u4e00\u8bed\u672a\u53d1\uff0c\u6084\u6084\u9000\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u6731\u5927\u5b98\u4eba\u662f\u8c01\uff1f\u54b1\u4eec\u771f\u8981\u53bb\u89c1\u4ed6\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u6731\u5927\u5b98\u4eba\u4e43\u662f\u957f\u6c5f\u4e09\u9f99\u4e4b\u9996\uff0c\u5728\u6c5f\u6e56\u4e0a\u5012\u4e5f\u5f88\u6709\u540d\u6c14\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4ece\u672a\u542c\u8bf4\u8fc7\u957f\u6c5f\u4e09\u9f99\uff0c\u6b64\u65f6\u5b8b\u5b5a\u8bf4\u51fa\u53e3\u6765\uff0c\u5012\u53eb\u4ed6\u96be\u8fa8\u771f\u5047\uff0c\u4e0d\u77e5\u662f\u5426\u771f\u6709\u6731\u5927\u5b98\u4eba\u4e86\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u95fb\u8a00\uff0c\u5374\u662f\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u90a3\u957f\u6c5f\u4e09\u9f99\uff0c\u53ef\u662f\u6c34\u4e0a\u5de5\u592b\u751a\u662f\u4e86\u5f97\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u542c\u4f60\u4e4b\u610f\uff0c\u83ab\u975e\u54b1\u4eec\u771f\u8981\u53bb\u770b\u90a3\u957f\u6c5f\u4e09\u9f99\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u63a5\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u54b1\u4eec\u5982\u662f\u8981\u53bb\uff0c\u4f60\u8001\u603b\u5f97\u5148\u7ed9\u6211\u4eec\u8bf4\u4e00\u8bf4\u8fd9\u4e09\u4e2a\u4eba\u5427\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u8981\u8bf4\uff0c\u5f53\u7136\u8981\u8bf4\u2026\u2026\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u8fd9\u957f\u6c5f\u4e09\u9f99\u4e43\u662f\u7ed3\u62dc\u5144\u5f1f\uff0c\u4e3a\u9996\u4e4b\u4eba\uff0c\u540d\u5524\u6731\u7693\uff0c\u4eba\u79f0\u98de\u9f99\u65e0\u5f71\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u98de\u9f99\u65e0\u5f71\uff1f\u90a3\u662f\u8bf4\u4ed6\u7684\u8f7b\u529f\u8eab\u6cd5\uff0c\u4e00\u5b9a\u5341\u5206\u4e86\u5f97\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e09\u9f99\u4e4b\u4e2d\uff0c\u4ee5\u4ed6\u7684\u529f\u529b\u6700\u9ad8\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u53e6\u5916\u4e24\u4f4d\u5462\uff1f\u4ed6\u4eec\u4e5f\u53eb\u4ec0\u4e48\u9f99\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8001\u4e8c\u662f\u795e\u9f99\u592a\u5c81\u8d75\u4e0d\u51e1\uff0c\u8001\u4e09\u4eba\u79f0\u7389\u9f99\u4e3b\u5251\u5d14\u5c11\u534e\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7565\u4e00\u6c89\u541f\uff0c\u63a5\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u4ed6\u4eec\u4e09\u4eba\u5728\u6c5f\u6e56\u4e0a\u662f\u597d\u662f\u574f\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u82e5\u8bba\u4ed6\u4eec\u4e09\u5144\u5f1f\u7684\u4e3a\u4eba\uff0c\u5012\u4e5f\u7b97\u4e0d\u5f97\u662f\u4ec0\u4e48\u574f\u4eba\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u6b66\u6797\u4e2d\u7684\u51e0\u5927\u95e8\u6d3e\uff0c\u5bf9\u4ed6\u4eec\u4e09\u5144\u5f1f\u53ef\u4ece\u6765\u4e0d\u80af\u4ea4\u5f80\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u90a3\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u5f53\u4eca\u6b66\u6797\u5404\u5927\u95e8\u6d3e\u7684\u5b50\u5f1f\uff0c\u90fd\u81ea\u89c6\u592a\u9ad8\uff0c\u50cf\u4ed6\u4eec\u8fd9\u4e9b\u72ec\u884c\u72ec\u5f80\u7684\u8349\u83bd\u82f1\u96c4\uff0c\u5c31\u4e0d\u4e3a\u4ed6\u4eec\u6240\u559c\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u7167\u4f60\u8fd9\u4e48\u8bf4\uff0c\u8fd9\u4e09\u5144\u5f1f\u4e5f\u4e0d\u662f\u4ec0\u4e48\u574f\u4eba\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u957f\u6c5f\u4e09\u9f99\u5728\u6b66\u6797\u4e4b\u4e2d\uff0c\u5012\u4e5f\u7b97\u5f97\u662f\u51e0\u6761\u94c1\u6323\u94ee\u7684\u597d\u6c49\uff0c\u54b1\u4eec\u65e2\u7136\u6765\u5230\u5b9c\u660c\uff0c\u4f55\u4e0d\u501f\u6b64\u673a\u4f1a\u8ba9\u4ed6\u4eec\u4f1a\u89c1\u4e00\u4e0b\u4e24\u4f4d\u5c11\u5e74\u82f1\u8c6a\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u4f60\u8fd9\u662f\u8bb2\u7684\u4f55\u7b49\u9053\u7406\uff1f\u5e94\u8be5\u662f\u54b1\u4eec\u53bb\u62dc\u89c1\u4ed6\u4eec\u624d\u662f\u5440\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u90fd\u662f\u4e00\u6837\u2026\u2026\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u7adf\u7136\u4e0a\u5cb8\u800c\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u3001\u5b89\u5c0f\u840d\u76f8\u4e92\u4e00\u7b11\uff0c\u968f\u5728\u5b8b\u5b5a\u8eab\u540e\u4e0a\u5f97\u5cb8\u6765\u3002 \u3000\u3000\u5b8b\u5b5a\u9886\u7740\u4e24\u4eba\uff0c\u7a7f\u8fc7\u4e86\u51e0\u6761\u70ed\u95f9\u5927\u8857\uff0c\u6765\u5230\u4e00\u5904\u5e9c\u7b2c\u4e4b\u524d\uff0c\u7b11\u9053\uff1a\u201c\u5230\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u62ac\u76ee\u6253\u91cf\uff0c\u53ea\u89c1\u8fd9\u5ea7\u5e9c\u7b2c\u7684\u6c14\u6d3e\uff0c\u5341\u5206\u4e0d\u5c0f\uff0c\u8fce\u95e8\u7684\u4e00\u5757\u9ed1\u6f06\u91d1\u533e\uff0c\u7adf\u7136\u662f\u5927\u4e66\u201c\u5b9c\u660c\u7b2c\u4e00\u5bb6\u201d\u4e94\u4e2a\u5b57\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fd6\u9053\uff1a\u201c\u8fd9\u7b49\u53e3\u6c14\uff0c\u4e0d\u5acc\u592a\u5927\u4e86\u5417\uff1f\u5230\u5e95\u662f\u6c5f\u6e56\u4eba\u7269\uff0c\u96be\u8131\u6840\u9a9c\u4e4b\u6c14\u2026\u2026\u201d \u3000\u3000\u5bfb\u601d\u4e4b\u95f4\uff0c\u5b8b\u5b5a\u5df2\u4e0a\u524d\u53e9\u95e8\u3002 \u3000\u3000\u4e0d\u7a0d\u65f6\uff0c\u9ed1\u6f06\u5927\u95e8\u5440\u7136\u5f00\u542f\uff0c\u4e00\u540d\u5e74\u7eaa\u4e94\u5341\u671b\u5916\u767d\u82cd\u53d1\uff0c\u5f53\u95e8\u9762\u7acb\uff0c\u4ed6\u4e00\u773c\u77a7\u89c1\u4e86\u5b8b\u5b5a\uff0c\u7acb\u5373\u5806\u4e0a\u4e86\u7b11\u5bb9\uff0c\u8fde\u8fde\u4f5c\u63d6\u9053\uff1a\u201c\u8001\u5974\u53ea\u9053\u662f\u8c01\uff0c\u539f\u6765\u662f\u5b8b\u5927\u4fa0\uff0c\u5feb\u5feb\u8bf7\u8fdb\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u8083\u5ba2\u5165\u5185\u3002 \u3000\u3000\u5b8b\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u6731\u5927\u4fa0\u53ef\u5728\u5e9c\u4e2d\u5417\uff1f\u5feb\u5feb\u901a\u62a5\uff0c\u5c31\u8bf4\u5b8b\u67d0\u4e3a\u4ed6\u8bf7\u6765\u4e24\u4f4d\u670b\u53cb\u2026\u2026\u201d \u3000\u3000\u8001\u82cd\u5934\u95fb\u8a00\uff0c\u76ee\u5149\u5728\u65b9\u96ea\u5b9c\u3001\u5b89\u5c0f\u840d\u8eab\u4e0a\u4e00\u8f6c\u7b11\u9053\uff1a\u201c\u53ef\u662f\u8bf4\u4e24\u4f4d\u516c\u5b50\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u8001\u82cd\u5934\u9053\uff1a\u201c\u5b8b\u8001\u8bf7\u7a0d\u5f85\uff0c\u8001\u5974\u8fd9\u5c31\u7980\u544a\u4e1c\u4e3b\uff01\u201d\u8f6c\u8eab\u5165\u5185\u800c\u53bb\u3002 \u3000\u3000\u5b8b\u5b5a\u5fae\u5fae\u4e00\u7b11\uff0c\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u770b\u6765\u8fd9\u957f\u6c5f\u4e09\u9f99\u90fd\u5728\u5bb6\u4e2d\u7684\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u90a3\u8001\u82cd\u5934\u5e76\u6765\u8bf4\u660e\uff0c\u600e\u89c1\u5f97\u4e09\u4eba\u90fd\u5728\u5bb6\u4e2d\u5462\uff1f\u201d \u3000\u3000\u4ed6\u8f6c\u5ff5\u4e4b\u95f4\uff0c\u5ffd\u7136\u542c\u5f97\u95e8\u5185\u4f20\u6765\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u4ec0\u4e48\u98ce\u628a\u4f60\u8001\u5439\u6765\u4e86\uff1f\u771f\u662f\u60f3\u715e\u5144\u5f1f\u4e86\u2026\u2026\u201d\u8bdd\u97f3\u672a\u5df2\uff0c\u53ea\u89c1\u4e00\u4f4d\u767d\u53d1\u767d\u987b\u8c8c\u76f8\u6e05\u7626\u7684\u4e2d\u8001\u5e74\u4eba\uff0c\u5feb\u6b65\u6253\u95e8\u5185\u884c\u6765\u3002 \u3000\u3000\u5b8b\u5b5a\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6731\u5144\uff0c\u4f60\u597d\u5440\uff01\u201d \u3000\u3000\u6562\u60c5\u8fd9\u4f4d\u5e74\u7ea7\u4e03\u65ec\u7684\u8001\u4eba\uff0c\u6b63\u662f\u201c\u98de\u9f99\u65e0\u5f71\u201d\u6731\u7693\u3002 \u3000\u3000\u6731\u7693\u62b1\u62f3\u4e00\u7b11\u9053\uff1a\u201c\u6258\u5b8b\u5144\u7684\u798f\uff0c\u8fd9\u4e00\u5411\u987d\u8eaf\u5012\u4e5f\u7c97\u5065\u5730\u5f88\u3002\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u76ee\u5149\u8f6c\u5411\u4e86\u4e8c\u5c0f\uff0c\u9053\uff1a\u201c\u9002\u624d\u8001\u5974\u6731\u798f\u8a00\u9053\u5b8b\u8001\u4e3a\u5144\u5f1f\u9886\u6765\u4e8c\u4f4d\u5c11\u5e74\u4f73\u5bbe\uff0c\u83ab\u975e\u5c31\u662f\u8fd9\u4e24\u4f4d\u8001\u5f1f\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u6731\u7693\u76ee\u5149\u5728\u4e24\u4eba\u8eab\u4e0a\u4e0d\u505c\u7684\u8f6c\u6765\u8f6c\u53bb\uff0c\u5ffd\u7136\u5927\u58f0\u7b11\u9053\uff1a\u201c\u597d\u8d44\u8d28\uff0c\u597d\u7980\u8d4b\u2026\u2026\u5b8b\u5144\uff0c\u8fd9\u4e24\u4f4d\u8001\u5f1f\u600e\u4e48\u79f0\u547c\uff1f\u600e\u7684\u4e0d\u4e3a\u5144\u5f1f\u4ecb\u7ecd\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u6731\u5144\uff0c\u54b1\u4eec\u5165\u5185\u8bb2\u8bdd\u884c\u5417\uff1f\u201d \u3000\u3000\u6731\u7693\u95fb\u8a00\u5931\u7b11\u9053\uff1a\u201c\u662f\u554a\uff0c\u5144\u5f1f\u771f\u662f\u7cca\u6d82\u4e86\uff0c\u54ea\u6709\u8981\u5ba2\u4eba\u5728\u95e8\u53e3\u8bf4\u8bdd\u7684\u9053\u7406\uff1f\u6765\uff0c\u6765\uff0c\u5feb\u8bf7\u5165\u5185\u2026\u2026\u201d\u8f6c\u8eab\u8083\u5ba2\u5165\u5185\u3002 \u3000\u3000\u4e09\u4eba\u5165\u5f97\u5927\u5385\uff0c\u5206\u5bbe\u4e3b\u5750\u5b9a\uff0c\u7acb\u5373\u6709\u9752\u8863\u5c0f\u5a62\u9001\u4e0a\u9999\u8317\u3002 \u3000\u3000\u6731\u7693\u5bb9\u5f97\u4e09\u4eba\u6da6\u8fc7\u4e86\u5507\u5589\uff0c\u7b11\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u8fd9\u4e24\u4f4d\u8001\u5f1f\uff0c\u82f1\u534e\u5185\u8574\uff0c\u663e\u7136\u662f\u5e74\u8f7b\u4e00\u4ee3\u4e2d\u7684\u9ad8\u624b\uff0c\u4e0d\u77e5\u662f\u54ea\u5566\u9ad8\u4eba\u5b50\u5f1f\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u5fae\u5fae\u4e00\u7b11\uff0c\u66ff\u6731\u7693\u4ecb\u7ecd\u4e86\u4e8c\u5c0f\u6765\u5386\u3002 \u3000\u3000\u6731\u7693\u53ea\u542c\u5f97\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8fd9\u4f4d\u65b9\u8001\u5f1f\u4e43\u662f\u5251\u795e\u7684\u4f20\u4eba\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fde\u8fde\u62b1\u62f3\u9053\uff1a\u201c\u6731\u8001\u591a\u591a\u6307\u6559\uff01\u201d \u3000\u3000\u6731\u7693\u7a81\u7136\u957f\u957f\u4e00\u53f9\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u4e0d\u6562\uff0c\u5251\u795e\u9648\u5927\u4fa0\u5927\u540d\uff0c\u8001\u673d\u8033\u95fb\u5df2\u4e45\uff0c\u53ea\u662f\u7f18\u632b\u4e00\u9762\uff0c\u5f15\u4e3a\u7ec8\u8eab\u61be\u4e8b\u2026\u2026\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4ee4\u5e08\u8fd1\u6765\u53ef\u597d\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9eef\u7136\u5e94\u9053\uff1a\u201c\u6731\u8001\uff0c\u5bb6\u5e08\u4e5f\u5df2\u8fc7\u4e16\u4e86\u3002\u201d \u3000\u3000\u6731\u7693\u6014\u5f97\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u5251\u795e\u9648\u5927\u4fa0\u4e5f\u5df2\u5f52\u9053\u5c71\u4e86\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u6b63\u662f\uff0c\u5148\u5e08\u5df2\u5728\u6708\u524d\u5750\u5316\u4e86\u3002\u201d \u3000\u3000\u6731\u7693\u4e0d\u7981\u987f\u8db3\u957f\u53f9\u9053\uff1a\u201c\u597d\u4eba\u4e0d\u80fd\u957f\u5bff\uff0c\u771f\u662f\u5929\u9053\u4f55\u5728\u554a\uff01\u201d\u8bdd\u97f3\u4e00\u987f\uff0c\u56de\u5934\u5411\u5b8b\u5b5a\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u9648\u5927\u4fa0\u4ed9\u53bb\u4e4b\u4e8b\uff0c\u90a3\u4e94\u5927\u9b54\u4e3b\u53ef\u66fe\u77e5\u6653\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u6731\u7693\u8138\u8272\u5927\u53d8\uff0c\u9053\uff1a\u201c\u7cdf\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u7cdf\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6731\u7693\u9053\uff1a\u201c\u60f3\u90a3\u4e94\u5927\u9b54\u4e3b\u4e00\u76f4\u4e0d\u6562\u660e\u76ee\u5f20\u80c6\u5730\u4e3a\u5bb3\u6b66\u6797\uff0c\u5c31\u56e0\u4e3a\u6709\u7740\u5251\u795e\u9648\u5927\u4fa0\u5728\u4e16\u4e4b\u6545\uff0c\u5982\u4eca\u9648\u5927\u4fa0\u4e00\u6b7b\uff0c\u53ea\u6015\u90a3\u4e94\u5927\u9b54\u4e3b\uff0c\u5c31\u8981\u5174\u98ce\u4f5c\u6d6a\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5ffd\u7136\u4e00\u7b11\u9053\uff1a\u201c\u6731\u8001\uff0c\u5728\u4e0b\u8ba4\u4e3a\u4ed6\u4eec\u4e0d\u6562\u3002\u201d \u3000\u3000\u6731\u7693\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u4e0d\u77e5\u9053\u90a3\u4e94\u9b54\u7684\u4e3a\u4eba\uff0c\u4ed6\u4eec\u4e00\u751f\u4e00\u4e16\uff0c\u6240\u6015\u7684\u4e5f\u53ea\u6709\u4ee4\u5e08\u4e00\u4eba\u800c\u5df2\uff0c\u800c\u4eca\u5251\u795e\u7adf\u7136\u76db\u6240\u65e9\u901d\uff0c\u4ed6\u4eec\u5df2\u7136\u522b\u65e0\u6240\u60e7\uff0c\u53ea\u6015\u8fd9\u6b66\u6797\u4e4b\u4e2d\uff0c\u9a6c\u4e0a\u5c31\u8981\u51fa\u73b0\u8165\u98ce\u8840\u96e8\u7684\u5c40\u9762\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u95fb\u5927\u7b11\u9053\uff1a\u201c\u6731\u5144\uff0c\u4f60\u4e5f\u5927\u80c6\u5c0f\u4e86\uff0c\u5251\u795e\u5927\u4fa0\u867d\u7136\u4ed9\u53bb\uff0c\u4f46\u4ed6\u8fd8\u6709\u4f20\u4eba\u5728\u4e16\uff0c\u8c05\u90a3\u4e94\u5927\u9b54\u4e3b\u4e5f\u4e0d\u6562\u5f20\u72c2\u5230\u54ea\u53bb\u4e86\uff01\u201d \u3000\u3000\u6731\u7693\u95fb\u8a00\u4e00\u5446\u9053\uff1a\u201c\u5b8b\u5144\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4f60\u83ab\u975e\u4e0d\u4fe1\u5144\u5f1f\u4e4b\u8a00\uff1f\u201d \u3000\u3000\u6731\u7693\u63a5\u9053\uff1a\u201c\u4e0d\u662f\u4e0d\u4fe1\uff0c\u53ea\u662f\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u53ea\u662f\u4ec0\u4e48\uff1f\u4f60\u8ba4\u4e3a\u65b9\u8001\u5f1f\u592a\u5e74\u8f7b\uff0c\u4e0d\u662f\u4e94\u9b54\u5bf9\u624b\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u6731\u7693\u82e6\u7b11\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u679c\u7136\u8bf4\u5bf9\u4e86\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u6731\u5144\uff0c\u6709\u5173\u4e94\u5927\u9b54\u5934\u7684\u4e8b\uff0c\u4f60\u6731\u5144\u5927\u53ef\u4e0d\u7528\u6302\u5728\u5fc3\u4e0a\uff0c\u65b9\u8001\u5f1f\u65e2\u662f\u9648\u5927\u4fa0\u5f1f\u5b50\uff0c\u81ea\u7136\u4e0d\u4f1a\u653e\u8fc7\u4e94\u9b54\u7684\u4e86\u3002\u201d\u8bed\u97f3\u4e00\u987f\u6307\u7740\u5b89\u5c0f\u840d\uff1a\u201c\u6731\u5144\uff0c\u8fd9\u4f4d\u5b89\u8001\u5f1f\u7684\u5148\u4eba\uff0c\u6731\u5144\u53ef\u80fd\u4e5f\u66fe\u6709\u8fc7\u8033\u95fb\u3002\u201d \u3000\u3000\u6731\u7693\u76b1\u7709\u9053\uff1a\u201c\u4f46\u4e0d\u77e5\u662f\u54ea\u4e00\u4f4d\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u6570\u5341\u5e74\u524d\uff0c\u4e1c\u6d77\u6709\u4e00\u53cc\u5947\u4eba\u592b\u5987\uff0c\u8fdb\u5165\u4e2d\u539f\uff0c\u5927\u8d25\u4e5d\u95e8\u6d3e\u4e4b\u4e8b\u4f60\u53ef\u8bb0\u5f97\uff1f\u201d \u3000\u3000\u6731\u7693\u9053\uff1a\u201c\u8bb0\u5f97\u2026\u2026\u201d\u5ffd\u7136\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u83ab\u975e\u8fd9\u4f4d\u8001\u5f1f\uff0c\u5c31\u662f\u90a3\u53cc\u5947\u4eba\u592b\u5987\u7684\u540e\u4ee3\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u90a3\u4e00\u53cc\u5947\u4eba\uff0c\u6b63\u662f\u8fd9\u4f4d\u8001\u5f1f\u53f0\u7684\u7956\u7236\u6bcd\u3002\u201d \u3000\u3000\u6731\u7693\u6c89\u541f\u4e86\u4e00\u9635\u9053\uff1a\u201c\u5947\u4eba\u5947\u4e8b\uff0c\u5144\u5f1f\u5e38\u5e38\u6094\u6068\u5f53\u65e5\u5e74\u5e7c\uff0c\u672a\u80fd\u76ee\u7779\u90a3\u4e24\u4f4d\u5947\u4eba\u7684\u82f1\u6b66\uff0c\u4e0d\u60f3\u6570\u5341\u5e74\u540e\uff0c\u5374\u80fd\u89c1\u5230\u4ed6\u4eec\u7684\u5b50\u5f1f\uff0c\u771f\u662f\u5e73\u751f\u83ab\u5927\u5feb\u4e8b\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u8fd9\u65f6\u7565\u4e00\u6b20\u8eab\uff0c\u7b11\u9053\uff1a\u201c\u6731\u8001\u8fc7\u5956\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u773c\u5149\u4e00\u8f6c\uff0c\u7b11\u9053\uff1a\u201c\u6731\u5144\uff0c\u8d75\u3001\u5d14\u4e24\u4f4d\u8001\u5f1f\u5462\uff1f\u600e\u5730\u4e0d\u66fe\u89c1\u5230\uff1f\u201d \u3000\u3000\u6731\u7693\u5ffd\u7136\u4e00\u53f9\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u8d75\u3001\u5d14\u4e24\u4f4d\u8d24\u5f1f\u56e0\u4e8b\u53bb\u4e86\u4e2d\u5cb3\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u4e00\u6014\u9053\uff1a\u201c\u5d69\u5c71\u5417\uff1f\u201d \u3000\u3000\u6731\u7693\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4ed6\u4eec\u53bb\u90a3\u5d69\u5c71\u4f5c\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6731\u7693\u9053\uff1a\u201c\u5c11\u6797\u5bfa\u65b9\u4e08\u5165\u4e91\u5927\u5e08\u6709\u4f5b\u8d34\u5230\u6b64\uff0c\u9080\u7ea6\u6211\u7b49\u524d\u5f80\u76f8\u52a9\uff0c\u8001\u673d\u56e0\u4e3a\u4e0e\u90a3\u5bfa\u4e2d\u51e0\u4f4d\u957f\u8001\u66fe\u6709\u6069\u6028\uff0c\u6240\u4ee5\u4e0d\u66fe\u524d\u53bb\uff0c\u53ea\u7531\u8d75\u3001\u5d14\u4e8c\u4f4d\u8d24\u5f1f\u5e94\u7ea6\u800c\u5f80\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u7565\u4e00\u6c89\u541f\uff0c\u7b11\u9053\uff1a\u201c\u6731\u5144\uff0c\u90a3\u5c11\u6797\u5bfa\u53ef\u662f\u6709\u4ec0\u4e48\u9ebb\u70e6\uff1f\u201d \u3000\u3000\u6731\u7693\u76ee\u5149\u5728\u65b9\u96ea\u5b9c\u8eab\u4e0a\u4e00\u8f6c\uff0c\u9053\uff1a\u201c\u636e\u90a3\u5165\u4e91\u638c\u95e8\u4eba\uff0c\u5728\u4f20\u67ec\u4e4b\u4e0a\u4f20\u8a00\uff0c\u4e43\u662f\u4e94\u9b54\u4e4b\u4e2d\u6709\u4eba\u8981\u9738\u5360\u5c11\u6797\uff0c\u4f5c\u4e3a\u53f7\u4ee4\u4e2d\u539f\u7684\u603b\u575b\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u53ea\u542c\u5f97\u5251\u7709\u4e00\u626c\uff0c\u5931\u58f0\u9053\uff1a\u201c\u4ed6\u4eec\u597d\u5927\u7684\u80c6\u5b50\uff0c\u4f46\u4e0d\u77e5\u662f\u4e94\u9b54\u4e2d\u7684\u54ea\u4e00\u4f4d\uff1f\u201d \u3000\u3000\u6731\u7693\u6447\u5934\u9053\uff1a\u201c\u4f20\u67ec\u4e4b\u4e0a\u5e76\u672a\u8bf4\u660e\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u76b1\u7709\u6c89\u541f\u9053\uff1a\u201c\u770b\u6765\u8fd9\u5c11\u6797\u4e4b\u4e8b\uff0c\u679c\u662f\u5341\u5206\u7d27\u8feb\u7684\u4e86\u3002\u201d \u3000\u3000\u6731\u7693\u63a5\u9053\uff1a\u201c\u5982\u4e0d\u7d27\u8feb\uff0c\u90a3\u5165\u4e91\u5927\u5e08\u4e5f\u4e0d\u4f1a\u4f20\u4e0b\u4f5b\u8d34\uff0c\u9080\u4eba\u76f8\u52a9\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5ffd\u7136\u4e00\u7b11\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u665a\u8f88\u5148\u524d\u6ca1\u6709\u8bf4\u9519\u5427\uff1f\u516b\u6210\u662f\u90a3\u5929\u9b54\u5973\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5b9a\u7136\u662f\u5929\u9b54\u5973\u4f5c\u602a\u2026\u2026\u201d \u3000\u3000\u6731\u7693\u95fb\u8a00\uff0c\u8138\u8272\u5ffd\u7136\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u5929\u9b54\u5973\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u6b63\u662f\u6b64\u59b9\u3002\u201d \u3000\u3000\u6731\u7693\u957f\u53f9\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u4e94\u9b54\u4e4b\u4e2d\uff0c\u4ee5\u90a3\u5929\u9b54\u5973\u6700\u4e3a\u72e1\u733e\u96be\u6597\uff0c\u5018\u82e5\u8fd9\u4e3a\u7978\u5c11\u6797\u4e4b\u4eba\uff0c\u679c\u771f\u662f\u5979\uff0c\u4e2d\u539f\u6b66\u6797\u9053\u4e0a\uff0c\u53ea\u6015\u7ec8\u5c06\u6ca6\u5165\u9b54\u9053\u4e86\u2026\u2026\u201d\u5fe7\u8651\u4e4b\u60c5\uff0c\u6ea2\u4e8e\u8a00\u8868\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u53ea\u770b\u5f97\u5fc3\u4e2d\u5927\u4e3a\u611f\u52a8\uff0c\u6697\u9053\uff1a\u5b8b\u8001\u6ca1\u6709\u8bf4\u9519\uff0c\u8fd9\u957f\u6c5f\u4e09\u9f99\u5144\u5f1f\uff0c\u5012\u4e5f\u662f\u6b63\u4eba\u541b\u5b50\uff0c\u4ec5\u4ec5\u4ece\u4ed6\u8fd9\u7b49\u5fe7\u6101\u6b66\u6797\u540c\u9053\u6ca6\u843d\u4e4b\u5fc3\uff0c\u5373\u53ef\u770b\u51fa\u4ed6\u4eec\u5f53\u975e\u79bb\u4eb2\u53db\u9053\u4e4b\u4eba\u3002 \u3000\u3000\u4ed6\u601d\u91cf\u4e4b\u9645\uff0c\u5ffd\u7136\u542c\u5f97\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u6731\u5927\u4fa0\uff0c\u90a3\u5929\u9b54\u5973\u867d\u7136\u72e1\u733e\u9634\u72e0\uff0c\u4f46\u5979\u5982\u60f3\u6b8b\u5bb3\u6b66\u6797\uff0c\u53ea\u6015\u8fd8\u6ca1\u6709\u90a3\u7b49\u6c14\u5019\uff01\u201d \u3000\u3000\u6731\u7693\u95fb\u8a00\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5b89\u5c11\u4fa0\u6b64\u8a00\uff0c\u53ef\u662f\u8bf4\u90a3\u5929\u9b54\u5973\u4e0d\u8db3\u4e3a\u5bb3\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u5979\u672c\u5c31\u4e0d\u8db3\u4e3a\u5bb3\u561b\uff01\u201d \u3000\u3000\u6731\u7693\u6014\u6014\u5730\u770b\u770b\u5b8b\u5b5a\uff0c\u82e6\u7b11\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u521d\u751f\u4e4b\u728a\u4e0d\u754f\u864e\uff0c\u8fd9\u4f4d\u8001\u5f1f\uff0c\u53ef\u662f\u8c6a\u723d\u5f97\u5f88\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u6de1\u6de1\u4e86\u7b11\u9053\uff1a\u201c\u5b89\u8001\u5f1f\u8bf4\u5f97\u4e0d\u5dee\uff0c\u90a3\u5929\u9b54\u5973\u771f\u60f3\u72ec\u9738\u6b66\u6797\uff0c\u5927\u6982\u8fd8\u5dee\u5f97\u5f88\u8fdc\u3002\u201d \u3000\u3000\u6731\u7693\u4e4b\u610f\uff0c\u672c\u662f\u8bbd\u523a\u5b89\u5c0f\u840d\u53e3\u51fa\u72c2\u8a00\uff0c\u788d\u4e8e\u521d\u89c1\uff0c\u4e0d\u4fbf\u5b9c\u6307\uff0c\u8fd9\u65f6\u542c\u5f97\u5b8b\u5b5a\u7adf\u7136\u4e5f\u5e2e\u7740\u5b89\u5c0f\u840d\u8bb2\u8bdd\uff0c\u5fc3\u4e2d\u4e0d\u6536\u7531\u5f97\u5927\u4e3a\u4e0d\u89e3\uff0c\u76b1\u7709\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u522b\u4eba\u7684\u80fd\u8010\uff0c\u4f60\u53ef\u4ee5\u63a8\u8bf4\u4e0d\u77e5\uff0c\u4f46\u8fd9\u5929\u9b54\u5973\u7684\u4e00\u8eab\u6b66\u529f\uff0c\u4f60\u8fd9\u4f4d\u5927\u6f20\u795e\u624b\u5e94\u662f\u660e\u767d\u5f97\u5f88\uff0c\u5982\u662f\u5979\u84c4\u610f\u4e3a\u7978\uff0c\u9648\u5927\u4fa0\u800c\u540e\uff0c\u53c8\u6709\u8c01\u80fd\u963b\u6b62\u5f97\u4e86\u5979\uff1f\u5b8b\u5144\uff0c\u5b89\u8001\u5f1f\u5e74\u8f7b\u6c14\u76db\uff0c\u4e0d\u628a\u90a3\u9b54\u5934\u770b\u5728\u773c\u4e2d\uff0c\u4e43\u662f\u53ef\u559c\u4e4b\u5146\uff0c\u4f46\u5b8b\u5144\u4e5f\u8fd9\u7b49\u60f3\uff0c\u5c82\u975e\u6709\u70b9\u513f\u4e0d\u660e\u767d\u4e86\u5417\uff1f\u201d\u7ec8\u4e8e\uff0c\u4ed6\u5fcd\u4e0d\u4f4f\u76f4\u622a\u4ed6\u8bf4\u4e86\u5fc3\u4e2d\u6240\u601d\u3002 \u3000\u3000\u5b8b\u5b5a\u95fb\u8a00\u5927\u7b11\u9053\uff1a\u201c\u6731\u5144\uff0c\u4f60\u89c1\u8fc7\u5929\u9b54\u5973\uff1f\u201d \u3000\u3000\u6731\u7693\u9053\uff1a\u201c\u89c1\u8fc7\u4e00\u6b21\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u52a8\u8fc7\u624b\u5417\uff1f\u201d \u3000\u3000\u6731\u7693\u9053\uff1a\u201c\u4e0d\u6ee1\u5341\u62db\uff0c\u5373\u906d\u60e8\u8d25\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u8fd9\u5c31\u96be\u602a\u4e86\uff0c\u6731\u5144\u679c\u7136\u662f\u88ab\u90a3\u5929\u9b54\u5973\u5413\u7834\u4e86\u80c6\u5b50\u3002\u201d \u3000\u3000\u6731\u7693\u8001\u8138\u5fae\u5fae\u4e00\u7ea2\uff0c\u8baa\u8baa\u63a5\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u8d25\u5175\u4e4b\u5c06\uff0c\u4e0d\u6562\u8a00\u52c7\uff0c\u5b8b\u5144\u53d6\u7b11\uff0c\u5144\u5f1f\u4e5f\u53ea\u597d\u8eab\u53d7\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u6731\u5144\u83ab\u8981\u6cc4\u6c14\uff0c\u4e0d\u662f\u5b8b\u67d0\u5728\u6b64\u72c2\u8a00\uff0c\u5929\u9b54\u5973\u6b66\u529f\u56fa\u7136\u9ad8\u660e\uff0c\u4f46\u5f53\u6b64\u4e4b\u4e16\uff0c\u81f3\u5c11\u6709\u4e24\u4f4d\u6bd4\u5979\u66f4\u4e3a\u9ad8\u660e\u4e4b\u58eb\u3002\u201d \u3000\u3000\u6731\u7693\u6014\u6014\u5730\u9053\uff1a\u201c\u54ea\u4e24\u4f4d\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8fdc\u5728\u5929\u8fb9\u2026\u2026\u201d \u3000\u3000\u6731\u7693\u76ee\u5149\u4e00\u8f6c\uff0c\u5446\u5446\u5730\u9053\uff1a\u201c\u90a3\u662f\u8bf4\u8fd1\u5728\u773c\u524d\u4e86\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u6731\u7693\u6307\u7740\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5251\u795e\u4f20\u4eba\u65b9\u8001\u5f1f\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u7b97\u5f97\u4e0a\u4e00\u4f4d\uff01\u201d \u3000\u3000\u6731\u7693\u53c8\u77a7\u4e86\u5b89\u5c0f\u840d\u4e00\u773c\u9053\uff1a\u201c\u8fd9\u4f4d\u2026\u2026\u5b89\u8001\u5f1f\u4e5f\u7b97\u662f\u4e00\u4f4d\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u600e\u6837\uff1f\u4f60\u4f3c\u662f\u4e0d\u76f8\u4fe1\u5417\uff1f\u201d \u3000\u3000\u6731\u7693\u5fc3\u4e2d\u679c\u771f\u4e0d\u76f8\u4fe1\u3002\u4f46\u4ed6\u53e3\u4e2d\u5374\u600e\u597d\u8bf4\u51fa\uff1f\u53ea\u80fd\u76b1\u7709\u9053\uff1a\u201c\u8fd9\u2026\u2026\u59d4\u5b9e\u5927\u610f\u5916\u4e86\u4e00\u4e9b\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u6731\u5144\uff0c\u4f60\u4e0d\u5fc5\u89c9\u5f97\u610f\u5916\uff0c\u8fd9\u4f4d\u5b89\u8001\u5f1f\u7684\u8eab\u4e16\uff0c\u4f60\u5df2\u7136\u77e5\u9053\uff0c\u4f55\u5fc5\u8fd8\u8981\u5947\u602a\uff0c\u4f11\u8bf4\u90a3\u5929\u9b54\u5973\u4e0d\u8db3\u754f\uff0c\u7eb5\u7136\u4e94\u9b54\u8054\u624b\uff0c\u8001\u592b\u76f8\u4fe1\u4ed6\u4eec\u4e5f\u65e0\u53ef\u4f5c\u4e3a\u7684\u4e86\u3002\u201d \u3000\u3000\u6731\u7693\u4f3c\u662f\u88ab\u8feb\uff0c\u4e0d\u5f97\u4e0d\u76f8\u4fe1\uff0c\u5f53\u4e0b\u957f\u53f9\u9053\uff1a\u201c\u5b8b\u5144\u8fd9\u4e48\u8bf4\uff0c\u5144\u5f1f\u81ea\u662f\u53ea\u597d\u76f8\u4fe1\u4e86\u3002\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u7b11\u9053\uff1a\u201c\u540e\u5385\u6446\u9152\uff0c\u4e14\u5bb9\u8001\u673d\u7a0d\u5c3d\u5730\u4e3b\u4e4b\u8c0a\u5982\u4f55\uff1f\u201d\u8bf4\u8bdd\u95f4\uff0c\u5c31\u5f85\u8083\u5ba2\u5165\u5185\u3002 \u3000\u3000\u5b8b\u5b5a\u6447\u5934\u9053\uff1a\u201c\u6731\u5144\uff0c\u9152\u996d\u5012\u662f\u4e0d\u5fc5\uff0c\u5982\u6709\u90a3\u957f\u7a0b\u5065\u9a6c\uff0c\u80fd\u4ee3\u4e3a\u4e70\u4e0a\u4e09\u5339\uff0c\u54b1\u4eec\u5c31\u5341\u5206\u611f\u6fc0\u4e86\u3002\u201d \u3000\u3000\u6731\u7693\u7b11\u9053\uff1a\u201c\u820d\u4e0b\u9a6c\u68da\u4e4b\u4e2d\uff0c\u591a\u7684\u662f\u4e0a\u4e58\u9f99\u9a79\uff0c\u7a0d\u7528\u9152\u83dc\u4ee5\u540e\uff0c\u5144\u5f1f\u53eb\u4ed6\u4eec\u51c6\u5907\u6253\u70b9\u4fbf\u662f\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u5982\u6b64\u53e8\u6270\u4e86\u2026\u2026\u201d \u3000\u3000\u6731\u7693\u4f3c\u662f\u5341\u5206\u5feb\u6170\uff0c\u5f15\u7740\u4e09\u4eba\u8fdb\u5165\u5185\u5385\uff0c\u8fd9\u4e00\u5e2d\u9152\u996d\uff0c\u76f4\u5230\u5348\u591c\u65b9\u7f62\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u60ac\u90a3\u5929\u9b54\u5973\u7684\u4e3e\u63aa\uff0c\u9152\u996d\u4e00\u7f62\uff0c\u7acb\u5373\u5411\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u54b1\u4eec\u8fd8\u662f\u65e9\u4e9b\u4e0a\u8def\u8981\u7d27\u554a\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u7531\u6b64\u524d\u5f80\u4e2d\u5cb3\uff0c\u6700\u5feb\u4e5f\u5f97\u6570\u65e5\uff0c\u54b1\u4eec\u65e9\u4e00\u65f6\u662f\u4e00\u65f6\u2026\u2026\u201d\u6389\u5934\u5411\u6731\u7693\u9053\uff1a\u201c\u6731\u5144\uff0c\u4f60\u60f3\u4e0d\u60f3\u53bb\u770b\u770b\u70ed\u95f9\uff1f\u201d \u3000\u3000\u6731\u7693\u4e00\u6014\u9053\uff1a\u201c\u4ec0\u4e48\u70ed\u95f9\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u5c11\u6797\u5bfa\u4e2d\u6597\u5929\u9b54\u5440\uff1f\u201d \u3000\u3000\u6731\u7693\u9053\uff1a\u201c\u4e0d\u7528\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u77a7\u6731\u7693\u6709\u4e9b\u610f\u5174\u7d22\u7136\uff0c\u4e0d\u89c9\u8be7\u5f02\u9053\uff1a\u201c\u6731\u5144\uff0c\u4f60\u600e\u4e48\u5ffd\u7136\u53d8\u4e86\uff1f\u201d \u3000\u3000\u6731\u7693\u9053\uff1a\u201c\u5144\u5f1f\u51e0\u65f6\u53d8\u4e86\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u6614\u65e5\u7684\u6731\u5144\uff0c\u4e0d\u4f3c\u8fd9\u822c\u6d88\u6c89\u554a\uff01\u201d \u3000\u3000\u6731\u7693\u95fb\u8a00\uff0c\u5ffd\u7136\u957f\u957f\u4e00\u53f9\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u6b64\u4e8b\u5144\u5f1f\u672c\u6765\u4e0d\u60f3\u8bf4\u5c06\u51fa\u53bb\uff0c\u4f46\u4e3a\u4e0d\u6559\u5b8b\u5144\u8bef\u4f1a\uff0c\u5144\u5f1f\u53ea\u597d\u548c\u76d8\u6258\u51fa\uff0c\u4f46\u671b\u5b8b\u5144\u548c\u4e24\u4f4d\u8001\u5f1f\u83ab\u8981\u89c1\u7b11\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u5fc3\u4e2d\u6697\u9053\uff1a\u8fd9\u7b49\u53e3\u6c14\uff0c\u4f3c\u662f\u4e8b\u60c5\u5341\u5206\u4e25\u91cd\uff0c\u4f46\u4e0d\u77e5\u8fd9\u8001\u513f\u78b0\u4e0a\u4e86\u4f55\u7b49\u9ebb\u70e6\uff1f\u2026\u2026 \u3000\u3000\u52a8\u5ff5\u4e4b\u95f4\uff0c\u53e3\u4e2d\u63a5\u9053\uff1a\u201c\u6731\u5144\u4f60\u83ab\u975e\u9047\u4e0a\u4e86\u4ec0\u4e48\u82e6\u607c\u4e4b\u4e8b\u5417\uff1f\u201d \u3000\u3000\u6731\u7693\u70b9\u4e86\u70b9\u5934\uff0c\u957f\u957f\u5730\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5144\u5f1f\u6b63\u662f\u9047\u4e0a\u4e86\u4e00\u6869\u6781\u5176\u9ebb\u70e6\u4e4b\u4e8b\u2026\u2026\u201d \u3000\u3000\u8bed\u8a00\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u81f3\u4e8e\u5144\u5f1f\u4e0d\u53bb\u5c11\u6797\uff0c\u8bf4\u662f\u56e0\u5bfa\u4e2d\u7684\u51e0\u4f4d\u957f\u8001\u66fe\u6709\u8fc7\u8282\uff0c\u90a3\u4e0d\u8fc7\u662f\u9a97\u4eba\u4e4b\u8a00\u800c\u5df2\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4ec0\u4e48\u9ebb\u70e6\u4e8b\u5462\uff1f\u6731\u5144\u4e0d\u59a8\u5feb\u8bf4\uff0c\u5144\u5f1f\u5982\u80fd\u6548\u52b3\uff0c\u5012\u662f\u751a\u60f3\u52a9\u4f60\u4e00\u81c2\u4e4b\u529b\uff01\u201d \u3000\u3000\u6731\u7693\u6c89\u541f\u9053\uff1a\u201c\u8fd9\u4e8b\u8bf4\u6765\u8bdd\u957f\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u6731\u5144\uff0c\u957f\u8bdd\u77ed\u53d9\uff0c\u53ea\u628a\u5176\u4e2d\u7684\u8981\u70b9\u8bb2\u660e\u5427\uff01\u201d \u3000\u3000\u6731\u7693\u9053\uff1a\u201c\u4e8b\u60c5\u8d77\u6e90\u4e8e\u4e2d\u5dde\u4e09\u4fa0\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bdd\u97f3\u672a\u5df2\uff0c\u65b9\u96ea\u5b9c\u5ffd\u7136\u4e00\u60ca\uff0c\u8131\u53e3\u9053\uff1a\u201c\u6731\u8001\u4e0e\u4e2d\u5dde\u4e09\u4fa0\u76f8\u719f\uff1f\u201d \u3000\u3000\u6731\u7693\u9053\uff1a\u201c\u4f55\u6b62\u76f8\u8bc6\uff1f\u4e5f\u7b97\u5f97\u662f\u9053\u4e49\u4e4b\u4ea4\uff01\u201d \u3000\u3000\u5ffd\u7136\u76ee\u5149\u4e00\u8f6c\uff0c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u2026\u2026\u89c1\u8fc7\u4e2d\u5dde\u4e09\u4fa0\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fde\u5fd9\u8d77\u8eab\uff0c\u91cd\u65b0\u89c1\u793c\u9053\uff1a\u201c\u4e2d\u5dde\u4e09\u4fa0\uff0c\u4ff1\u662f\u665a\u8f88\u7684\u957f\u8f88\u3002\u3002 \u3000\u3000\u6731\u7693\u6014\u5f97\u4e00\u6014\uff0c\u5ffd\u7136\u5927\u609f\u9053\uff1a\u201c\u4f60\u59d3\u65b9\uff0c\u83ab\u975e\u662f\u90a3\u65b9\u5929\u6210\u4e8c\u4fa0\u7684\u5b50\u5f1f\uff1f\u201d \u3000 \u7b2c\u5341\u516b\u56de\u3000\u8dc3\u9a6c\u4e2d\u539f \u3000 \u3000\u3000\u65b9\u96ea\u5b9c\u9eef\u7136\u9053\uff1a\u201c\u4e0d\u9519\u2026\u2026\u65b9\u4e8c\u4fa0\u6b63\u662f\u665a\u751f\u4f2f\u7236\uff01\u201d \u3000\u3000\u6731\u7693\u5ffd\u7136\u6380\u9aef\u4e00\u7b11\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u7b97\u4e0d\u5f97\u662f\u5916\u4eba\u4e86\uff0c\u65b9\u4e16\u5144\uff0c\u6700\u8fd1\u4f60\u89c1\u5230\u4f60\u4f2f\u7236\u6ca1\u6709\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u5934\u9053\uff1a\u201c\u6ca1\u6709\uff01\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u6211\u4f2f\u7236\uff0c\u53ef\u662f\u7559\u4e0b\u4ec0\u4e48\u9ebb\u70e6\u7ed9\u4e86\u4f60\u8001\uff1f\u201d \u3000\u3000\u6731\u7693\u76b1\u7709\u9053\uff1a\u201c\u9ebb\u70e6\u8c08\u4e0d\u4e0a\uff0c\u53ea\u662f\u2026\u2026\u53ea\u662f\u2026\u2026\u8fd9\u4e8b\u53eb\u4eba\u5341\u5206\u68d8\u624b\u800c\u5df2\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u4f60\u4f55\u4e0d\u5feb\u4e00\u4e9b\u8bf4\u51fa\u6765\uff1f\u201d \u3000\u3000\u6731\u7693\u957f\u53f9\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u4ed6\u770b\u770b\u65b9\u96ea\u5b9c\uff0c\u63a5\u9053\uff1a\u201c\u65b9\u4e8c\u4fa0\u5931\u8e2a\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u795e\u60c5\u5341\u5206\u9547\u5b9a\uff0c\u70b9\u5934\u9053\uff1a\u201c\u665a\u8f88\u731c\u5f97\u5230\uff01\u201d \u3000\u3000\u6731\u7693\u89c1\u4ed6\u4e1d\u6beb\u6ca1\u6709\u60ca\u8bb6\u60b2\u4f24\u4e4b\u8272\uff0c\u4e0d\u7531\u5f97\u5927\u4e3a\u5947\u602a\uff0c\u63a5\u9053\uff1a\u201c\u65b9\u4e16\u5144\uff0c\u4f60\u53ef\u65e9\u5df2\u77e5\u9053\u4e86\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u5934\u9053\uff1a\u201c\u665a\u8f88\u4e0d\u77e5\uff01\u201d \u3000\u3000\u6731\u7693\u76b1\u7709\u9053\uff1a\u201c\u4e16\u5144\u4e0d\u89c9\u5f97\u96be\u8fc7\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u672c\u60f3\u8bf4\u90a3\u65b9\u5929\u6210\u65e2\u6709\u6740\u6211\u4e4b\u5fc3\uff0c\u4ed6\u5931\u8e2a\u4e0e\u5426\uff0c\u6211\u96be\u8fc7\u4ec0\u4e48\uff1f \u3000\u3000\u4f46\u4ed6\u7ee7\u800c\u60f3\u5230\u90a3\u591a\u5e74\u6276\u517b\u81ea\u5df1\u7684\u4f2f\u6bcd\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8fd9\u7b49\u610f\u6c14\u4e4b\u8a00\uff0c\u5c31\u54bd\u56de\u53e3\u4e2d\u3002 \u3000\u3000\u5f53\u4e0b\u6de1\u6de1\u5e94\u9053\uff1a\u201c\u96be\u8fc7\u4e5f\u6ca1\u6709\u4ec0\u4e48\u529e\u6cd5\u554a\uff01\u201d \u3000\u3000\u6731\u7693\u542c\u5f97\u5446\u4e86\u4e00\u5446\u3002 \u3000\u3000\u4ed6\u5b8c\u5168\u5f04\u4e0d\u660e\u767d\uff0c\u8fd9\u65b9\u96ea\u5b9c\u600e\u4f1a\u8fd9\u7b49\u7edd\u60c5\u5be1\u4e49\uff1f \u3000\u3000\u5b8b\u5b5a\u8fd9\u65f6\u5374\u5927\u58f0\u9053\uff1a\u201c\u6731\u5144\uff0c\u5173\u4e8e\u65b9\u8001\u5f1f\u7684\u5bb6\u4e2d\u4e4b\u4e8b\uff0c\u4f60\u6700\u597d\u5c11\u7ba1\uff0c\u5012\u662f\u4f60\u81ea\u5df1\u773c\u4e0b\u6240\u8bb2\u96be\u9898\uff0c\u7a76\u7adf\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u4f60\u518d\u8981\u4e0d\u8bf4\uff0c\u54b1\u4eec\u53ef\u5c31\u61d2\u5f97\u542c\u4e86\uff01\u201d \u3000\u3000\u6731\u7693\u6014\u6014\u5730\u9053\uff1a\u201c\u5b8b\u5144\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u6731\u7693\u6307\u7740\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4ed6\u2026\u2026\u771f\u662f\u65b9\u5929\u6210\u7684\u4f84\u513f\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u600e\u4e48\uff1f\u5929\u4e0b\u8fd8\u6709\u5192\u5145\u522b\u4eba\u665a\u8f88\u7684\u4eba\u5417\uff1f\u4f60\u5230\u5e95\u600e\u4e48\u5566\uff1f\u8fd9\u7b49\u5947\u5947\u602a\u602a\u7684\u2026\u2026\u201d \u3000\u3000\u6731\u7693\u9053\uff1a\u201c\u5b8b\u5144\u554a\uff0c\u4ed6\u5982\u662f\u65b9\u5929\u6210\u7684\u4f84\u5b50\uff0c\u542c\u5230\u65b9\u5929\u6210\u5931\u8e2a\u4e4b\u4e8b\uff0c\u4ed6\u600e\u4e1d\u6beb\u65e0\u52a8\u4e8e\u8877\u5462\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8fd9\u662f\u4ed6\u4eec\u65b9\u5bb6\u7684\u5bb6\u52a1\u4e8b\uff0c\u4f60\u7ba1\u4ed6\u8fd9\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u6731\u5144\uff0c\u5feb\u8bf4\u4f60\u81ea\u5df1\u7684\u4e8b\u5427\uff01\u201d \u3000\u3000\u6731\u7693\u65e0\u53ef\u5948\u4f55\u5730\u957f\u53f9\u9053\uff1a\u201c\u8fd9\u771f\u662f\u602a\u4e8b\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5ffd\u7136\u4e00\u7b11\u9053\uff1a\u201c\u6731\u8001\uff0c\u5929\u4e0b\u6709\u8bb8\u591a\u4e8b\u672c\u662f\u4e0d\u8db3\u4e3a\u5916\u4eba\u9053\uff0c\u65b9\u5144\u7684\u906d\u9047\uff0c\u6b63\u662f\u90a3\u4e0d\u8db3\u4e3a\u4eba\u9053\u7684\u4e00\u4f8b\uff0c\u4f60\u8001\u6700\u597d\u522b\u518d\u4e3a\u8fd9\u4e2a\u8d39\u5fc3\u4e86\uff01\u201d \u3000\u3000\u6731\u7693\u6c89\u541f\u4e86\u4e00\u9635\u9053\uff1a\u201c\u8001\u5f1f\u8bf4\u5f97\u4e5f\u5bf9\uff0c\u8001\u673d\u5b9e\u5728\u591a\u4e8b\u4e86\uff01\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u534a\u6708\u4e4b\u524d\uff0c\u8001\u673d\u5ffd\u7136\u63a5\u5230\u5251\u95e8\u4e94\u9b3c\u4e2d\u6210\u7384\u901a\u7684\u4e00\u7eb8\u4e66\u4fe1\uff0c\u8981\u8001\u673d\u5728\u672c\u6708\u5341\u516d\u65e5\uff0c\u524d\u53bb\u5251\u95e8\u8d74\u4f1a\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u8fd9\u4e5f\u6ca1\u6709\u4ec0\u4e48\u5927\u4e0d\u4e86\u554a\uff01\u6700\u591a\u53bb\u53e8\u6270\u4ed6\u4e00\u987f\u800c\u5df2\uff0c\u4e5f\u503c\u5f97\u8fd9\u7b49\u70e6\u95f7\u5417\uff1f\u201d \u3000\u3000\u6731\u7693\u6447\u5934\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u4f60\u53ea\u77e5\u5176\u4e00\uff0c\u4e0d\u77e5\u5176\u4e8c\uff0c\u6210\u7384\u901a\u4e66\u4fe1\u4e4b\u4e2d\u63d0\u5230\uff0c\u8d74\u5bb4\u4e4b\u65f6\uff0c\u5fc5\u987b\u643a\u5e26\u4e00\u6869\u793c\u7269\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u793c\u7269\uff1f\u9601\u4e0b\u4e70\u4e00\u70b9\u82b1\u7ea2\u7eeb\u7f0e\u4e0d\u5c31\u6210\u4e86\uff1f\u201d \u3000\u3000\u6731\u7693\u82e6\u7b11\u9053\uff1a\u201c\u5982\u662f\u8fd9\u822c\u7b80\u5355\uff0c\u90a3\u5012\u7528\u4e0d\u7740\u5144\u5f1f\u8fd9\u7b49\u4f24\u795e\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u6210\u7384\u901a\u6307\u5b9a\u8981\u4f60\u643a\u5e26\u4f55\u79cd\u793c\u7269\u524d\u53bb\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u6731\u7693\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4ec0\u4e48\u6837\u7684\u793c\u7269\uff0c\u96be\u5012\u4e86\u5c0a\u9a7e\uff1f\u201d \u3000\u3000\u6731\u7693\u9053\uff1a\u201c\u6210\u7384\u901a\u8981\u5144\u5f1f\u643a\u5e26\u4e2d\u5dde\u4e09\u4fa0\u7684\u9996\u7ea7\u524d\u53bb\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u5f97\u51e0\u4e4e\u8df3\u4e86\u8d77\u6765\uff0c\u8131\u53e3\u9053\uff1a\u201c\u6709\u8fd9\u7b49\u4e8b\uff1f\u201d \u3000\u3000\u6731\u7693\u9053\uff1a\u201c\u90a3\u6210\u7384\u901a\u6b63\u662f\u8fd9\u7b49\u8bf4\u7684\uff0c\u8bd5\u60f3\u6211\u4e0e\u90a3\u4e2d\u5dde\u4e09\u4fa0\u7b97\u5f97\u4e0a\u662f\u9053\u4e49\u4e4b\u4ea4\uff0c\u600e\u80fd\u505a\u51fa\u8fd9\u79cd\u4e8b\u60c5\u6765\u5462\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u5374\u7b11\u9053\uff1a\u201c\u4e0d\u53bb\u7b97\u4e86\uff01\u201d \u3000\u3000\u6731\u7693\u9eef\u7136\u4e00\u53f9\u9053\uff1a\u201c\u4e0d\u53bb\u600e\u4e48\u884c\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u884c\uff1f\u201d \u3000\u3000\u6731\u7693\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u5144\u5f1f\u773c\u4e0b\u53ef\u771f\u60f9\u4e0d\u8d77\u90a3\u5251\u95e8\u4e94\u9b3c\u4e2d\u7684\u6210\u8001\u5927\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u6210\u7384\u901a\u6b66\u529f\u4e0d\u9519\uff0c\u4f46\u4ed6\u4e5f\u4e0d\u89c1\u5f97\u6bd4\u4f60\u8001\u5144\u9ad8\u660e\u591a\u5c11\u5427\uff01\u201d \u3000\u3000\u6731\u7693\u9053\uff1a\u201c\u5982\u679c\u53ea\u662f\u6210\u7384\u901a\uff0c\u5144\u5f1f\u81ea\u7136\u662f\u4e0d\u6015\u7684\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u600e\u4e48\uff1f\u90a3\u6210\u7384\u901a\u6709\u4e86\u9760\u5c71\uff1f\u201d \u3000\u3000\u5176\u5b9e\uff0c\u5b8b\u5b5a\u65e9\u5c31\u77e5\u9053\u90a3\u6210\u7384\u901a\u7684\u5e95\u7ec6\uff0c\u4f46\u4ed6\u6b64\u523b\u6545\u4f5c\u4e0d\u77e5\u5730\u53cd\u95ee\uff0c\u4e43\u8981\u6731\u7693\u4eb2\u53e3\u8bf4\u51fa\u6765\uff0c\u597d\u8ba9\u65b9\u96ea\u5b9c\u548c\u5b89\u5c0f\u840d\u77e5\u9053\u3002 \u3000\u3000\u6731\u7693\u679c\u7136\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u90a3\u6210\u7384\u901a\u6b63\u662f\u6709\u4e86\u6700\u597d\u7684\u9760\u5c71\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u65f6\u56e0\u4e3a\u542c\u8bf4\u90a3\u6210\u7384\u901a\u5c45\u7136\u8981\u6731\u7693\u5bfb\u627e\u4e2d\u5dde\u4e09\u4fa0\uff0c\u53d6\u4ed6\u4eec\u9996\u7ea7\u524d\u53bb\u8d74\u5bb4\uff0c\u5fc3\u4e2d\u81ea\u662f\u5341\u5206\u5173\u5207\uff0c\u5f53\u4e0b\u8131\u53e3\u9053\uff1a\u201c\u90a3\u59d3\u6210\u7684\u9760\u5c71\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u6731\u7693\u957f\u53f9\u4e00\u58f0\u9053\uff1a\u201c\u4e94\u9b54\u4e2d\u4eba\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u600e\u4e48\uff1f\u53c8\u662f\u4e94\u9b54\u4f5c\u602a\uff1f\u201d \u3000\u3000\u6731\u7693\u9053\uff1a\u201c\u90a3\u6210\u7384\u901a\u548c\u8463\u65b9\u4e24\u4eba\uff0c\u4f1a\u5408\u4e86\u91d1\u67aa\u5b97\u58eb\u4e49\u4e09\u4eba\uff0c\u5728\u5ddd\u4e2d\u5408\u521b\u4e86\u4e09\u4e49\u95e8\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u5728\u4e0b\u77e5\u9053\u4e86\uff01\u201d \u3000\u3000\u6731\u7693\u9053\uff1a\u201c\u8001\u5f1f\u53ef\u77e5\u9053\u8fd9\u4e09\u4e49\u95e8\u662f\u4ec0\u4e48\u4eba\u5728\u90a3\u5e55\u540e\u4e3b\u6301\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u6b63\u8981\u8bf7\u6559\uff01\u201d \u3000\u3000\u6731\u7693\u9053\uff1a\u201c\u67f3\u5a9a\u5a18\u548c\u91d1\u957f\u5e9a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u6014\u9053\uff1a\u201c\u8c01\u662f\u91d1\u957f\u5e9a\uff01\u201d \u3000\u3000\u6731\u7693\u9053\uff1a\u201c\u4e94\u9b54\u4e2d\u7684\u9f13\u9b54\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u9053\uff1a\u201c\u539f\u6765\u9f13\u9b54\u53eb\u505a\u91d1\u957f\u5e9a\u3002\u201d \u3000\u3000\u4f46\u4ed6\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u90a3\u67f3\u5a9a\u5a18\u5728\u4e0b\u5df2\u7ecf\u89c1\u8fc7\u4e86\u201d \u3000\u3000\u6731\u7693\u8fd9\u56de\u53ef\u662f\u6014\u5f97\u4e00\u6014\uff0c\u9053\uff1a\u201c\u8001\u5f1f\u89c1\u8fc7\u90a3\u67f3\u5a9a\u5a18\u9b54\u5973\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u89c1\u8fc7\u4e86\u3002\u201d \u3000\u3000\u6731\u7693\u9053\uff1a\u201c\u5979\u2026\u2026\u5979\u6ca1\u6709\u8ddf\u4f60\u52a8\u624b\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u52a8\u8fc7\u624b\u4e86\uff01\u201d \u3000\u3000\u6731\u7693\u9053\uff1a\u201c\u7ed3\u679c\u5982\u4f55\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u67f3\u5a9a\u5a18\u5413\u8dd1\u4e86\u3002\u201d \u3000\u3000\u6731\u7693\u9053\uff1a\u201c\u5413\u8dd1\u4e86\uff1f\u8001\u673d\u2026\u2026\u201d \u3000\u3000\u663e\u7136\uff0c\u4ed6\u5341\u5206\u96be\u4ee5\u76f8\u4fe1\u3002 \u3000\u3000\u5b8b\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u8fd8\u6709\u4ec0\u4e48\u53ef\u5947\u602a\uff1f\u65b9\u8001\u5f1f\u773c\u4e0b\u7684\u529f\u529b\uff0c\u53ea\u6015\u6bd4\u5251\u795e\u9648\u5927\u4fa0\u5728\u4e16\u4e4b\u65e5\uff0c\u8fd8\u8981\u9ad8\u660e\u4e0d\u5c11\u5462\uff01\u201d \u3000\u3000\u6731\u7693\u9053\uff1a\u201c\u5b8b\u5144\u6b64\u8a00\u5f53\u771f\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u5f53\u7136\u662f\u771f\u7684\uff01\u201d \u3000\u3000\u6731\u7693\u5ffd\u7136\u957f\u957f\u5730\u547c\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u53ef\u5c31\u597d\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8001\u5f1f\u53f0\u83ab\u975e\u88ab\u4e94\u9b54\u5413\u7834\u4e86\u80c6\u4e86\u5417\uff1f\u201d \u3000\u3000\u6731\u7693\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u5144\u5f1f\u4e00\u4eba\u751f\u547d\uff0c\u53c8\u7b97\u5f97\u4e86\u4ec0\u4e48\uff1f\u6b66\u6797\u4e4b\u4e2d\uff0c\u5982\u662f\u65e0\u4eba\u80fd\u591f\u5f3a\u8fc7\u90a3\u4e94\u5927\u9b54\u4e3b\uff0c\u5c82\u4e0d\u662f\u773c\u770b\u5c31\u8981\u8840\u8165\u56db\u8d77\u4e86\u5417\uff1f\u4e3a\u6b66\u6797\u540c\u9053\u5343\u4e07\u751f\u7075\uff0c\u5144\u5f1f\u600e\u4e0d\u5fc3\u6025\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u5f88\u597d\u554a\uff0c\u9601\u4e0b\u5012\u662f\u6211\u4f5b\u5fc3\u80a0\uff0c\u6148\u60b2\u4e3a\u672c\uff01\u201d \u3000\u3000\u6731\u7693\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u4f60\u5462\uff1f\u96be\u9053\u5f53\u771f\u4e0d\u628a\u6b64\u4e8b\u653e\u5728\u5fc3\u4e0a\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u6731\u5144\uff0c\u8001\u592b\u53ea\u662f\u8ddf\u4f60\u73a9\u7b11\u800c\u5df2\uff01\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u90a3\u6210\u7384\u901a\u7684\u9e3f\u95e8\u5927\u5bb4\uff0c\u8fd8\u6709\u4e94\u65e5\u4e0d\u77e5\u4f60\u6731\u5144\u6709\u4f55\u6253\u7b97\uff1f\u201d \u3000\u3000\u6731\u7693\u9053\uff1a\u201c\u6ca1\u6709\u4ec0\u4e48\u6253\u7b97\uff0c\u4e0d\u53bb\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e0d\u53bb\u662f\u53ef\u4ee5\uff0c\u4f46\u5982\u4ed6\u4eec\u627e\u4e0a\u95e8\u6765\u600e\u4e48\u529e\u5462\uff1f\u4f60\u9003\u5f97\u4e86\u5417\uff1f\u201d \u3000\u3000\u6731\u7693\u957f\u53f9\u9053\uff1a\u201c\u6731\u67d0\u4e00\u5bb6\u8001\u5c11\u5747\u5728\u5b9c\u660c\uff0c\u4ed6\u4eec\u4e0d\u6765\u5219\u5df2\uff0c\u6765\u4e86\uff0c\u6731\u67d0\u5f53\u5168\u529b\u4e00\u6218\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e3b\u610f\u4e0d\u9519\uff0c\u5982\u4ec5\u662f\u6210\u7384\u901a\u4e09\u4eba\u524d\u6765\uff0c\u4f60\u56fa\u7136\u53ef\u4ee5\u4e00\u6218\uff0c\u4fbf\u82e5\u67f3\u5a9a\u5a18\u548c\u91d1\u957f\u5e9a\u4e24\u4eba\u4e4b\u4e2d\u6765\u4e86\u4e00\u4f4d\uff0c\u4f60\u6731\u5144\u53c8\u600e\u4f1a\u662f\u4ed6\u4eec\u4e4b\u654c\uff1f\u201d \u3000\u3000\u6731\u7693\u51c4\u7136\u4e00\u7b11\u9053\uff1a\u201c\u6731\u5144\uff0c\u5927\u4e0d\u4e86\u4e00\u6b7b\u800c\u5df2\uff0c\u5144\u5f1f\u9002\u624d\u5df2\u7136\u8bf4\u8fc7\u4e86\uff0c\u5144\u5f1f\u4e00\u4eba\u751f\u6b7b\u672c\u4e0d\u8db3\u60dc\u554a\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u8001\u5144\uff0c\u4f60\u9519\u4e86\uff01\u5339\u592b\u4e4b\u52c7\uff0c\u6839\u672c\u4e8e\u4e8b\u65e0\u8865\u554a\uff01\u201d \u3000\u3000\u6731\u7693\u9053\uff1a\u201c\u5144\u5f1f\u660e\u767d\uff0c\u53ea\u662f\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u53ea\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6731\u7693\u9053\uff1a\u201c\u5144\u5f1f\u4e0d\u80fd\u56e0\u4e3a\u6015\u6b7b\uff0c\u5f31\u4e86\u957f\u6c5f\u4e09\u9f99\u7684\u540d\u53f7\u5440\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u547d\u5df2\u4e0d\u5b58\uff0c\u8fd8\u8981\u4ec0\u4e48\u540d\uff1f\u9601\u4e0b\u7b28\u5f97\u5f88\uff01\u201d \u3000\u3000\u6731\u7693\u6447\u5934\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u8fd9\u5c31\u662f\u4f60\u7684\u9519\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8001\u592b\u9519\u5728\u4f55\u5904\uff1f\u201d \u3000\u3000\u6731\u7693\u9053\uff1a\u201c\u5b8b\u5144\u5f53\u77e5\u8c79\u6b7b\u7559\u76ae\uff0c\u4eba\u6b7b\u7559\u540d\u90a3\u540d\u8bdd\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8001\u751f\u5e38\u8c08\uff0c\u4fd7\u4e0d\u53ef\u8010\uff0c\u6731\u5144\uff0c\u4eba\u6b7b\u7559\u540d\u672c\u6765\u4e0d\u9519\uff0c\u4e5f\u5f97\u770b\u770b\u600e\u4e2a\u6b7b\u6cd5\u624d\u5bf9\uff01\u201d \u3000\u3000\u6731\u7693\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u6731\u5144\uff0c\u6b7b\u6709\u8f7b\u5982\u9e3f\u6bdb\uff0c\u4e5f\u6709\u91cd\u4e8e\u6cf0\u5c71\uff0c\u4f60\u5982\u662f\u5750\u5b88\u5b9c\u660c\uff0c\u7b49\u90a3\u67f3\u5a9a\u5a18\u8d76\u6765\u6740\u4f60\uff0c\u53c8\u4f55\u4e0d\u6682\u65f6\u9000\u4e00\u6b65\u800c\u60f3\uff0c\u7559\u4e0b\u6709\u7528\u4e4b\u8eaf\u5462\uff1f\u201d \u3000\u3000\u6731\u7693\u9053\uff1a\u201c\u5144\u5f1f\u8fd9\u4e48\u505a\uff0c\u81ea\u89c9\u5e76\u65e0\u4e0d\u5f53\uff0c\u4eba\u6b7b\u53ea\u6c42\u95ee\u5fc3\u65e0\u6127\u800c\u5df2\uff0c\u5144\u5f1f\u5982\u662f\u6218\u6b7b\u4e94\u9b54\u5200\u4e0b\uff0c\u81ea\u89c9\u751a\u662f\u5fc3\u5b89\u7406\u5f97\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u6012\u9053\uff1a\u201c\u9601\u4e0b\u8fc2\u5f97\u5f88\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u65f6\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u6731\u8001\uff0c\u5b8b\u8001\u6ca1\u6709\u8bf4\u9519\uff0c\u4f60\u8001\u4e0d\u5fc5\u8ddf\u4ed6\u4eec\u62da\u4e86\u3002\u201d \u3000\u3000\u6731\u7693\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u8fd8\u5e74\u8f7b\uff0c\u4e0d\u61c2\u5f97\u6b66\u6797\u6210\u540d\u7684\u4eba\u6c42\u751f\u4e0d\u6613\uff0c\u6c42\u6b7b\u4e5f\u5f88\u96be\u7684\u9053\u7406\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5927\u7b11\u9053\uff1a\u201c\u6731\u8001\uff0c\u541b\u5b50\u62a5\u4ec7\u5341\u5e74\u4e0d\u665a\uff0c\u5fcd\u4e00\u65f6\u4e4b\u6c14\uff0c\u514d\u767e\u5e74\u4e4b\u5fe7\uff0c\u8fd9\u6761\u7b80\u5355\u7684\u9053\u7406\uff0c\u4f60\u8001\u90fd\u4e0d\u660e\u767d\u4e86\u5417\uff1f\u665a\u751f\u53ea\u4e0d\u8fc7\u8981\u6c42\u6731\u8001\u4f60\u6682\u65f6\u907f\u907f\u4ed6\u4eec\u7684\u950b\u9510\uff0c\u7b49\u5f85\u65f6\u673a\u5230\u6765\uff0c\u518d\u884c\u4e0e\u4ed6\u4eec\u89c1\u4e2a\u9ad8\u4f4e\u800c\u5df2\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u8fd9\u65f6\u5ffd\u7136\u63a5\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u65b9\u5144\u91d1\u7389\u826f\u8a00\uff0c\u6731\u8001\u4f55\u4e0d\u4e09\u601d\uff1f\u201d \u3000\u3000\u6731\u7693\u95fb\u8a00\uff0c\u679c\u771f\u6c89\u541f\u826f\u4e45\uff0c\u672a\u66fe\u8bf4\u8bdd\u3002 \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u6731\u5144\uff0c\u4f60\u5fc5\u662f\u89c9\u5f97\u8fdc\u8d70\u907f\u7978\uff0c\u5f31\u4e86\u957f\u6c5f\u4e09\u9f99\u7684\u540d\u671b\uff0c\u8001\u592b\u5230\u6709\u4e00\u6761\u4e24\u5168\u4e4b\u7b56\u3002\u201d \u3000\u3000\u6731\u7693\u7eb5\u7709\u9053\uff1a\u201c\u4ec0\u4e48\u4e24\u5168\u4e4b\u7b56\uff1f\u201d \u3000\u3000\u663e\u7136\uff0c\u4ed6\u6709\u4e9b\u5fc3\u52a8\u4e86\u3002 \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8bf4\u6765\u4e5f\u5341\u5206\u7b80\u5355\uff0c\u53ea\u8981\u4f60\u6731\u5144\u4e5f\u53bb\u5d69\u5c71\u4e00\u884c\uff0c\u4e00\u6765\u53ef\u7b97\u907f\u7978\uff0c\u4e00\u6765\u4e5f\u53ef\u4e3a\u6b66\u6797\u6b63\u4e49\u6548\u52b3\uff0c\u6731\u5144\u4ed4\u7ec6\u60f3\u60f3\uff0c\u8fd9\u5c82\u4e0d\u662f\u5f88\u597d\u7684\u65b9\u5f0f\u5417\uff1f\u201d \u3000\u3000\u6731\u7693\u957f\u957f\u4e00\u53f9\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u5144\u5f1f\u5982\u53bb\u5c11\u6797\uff0c\u8fd9\u4e00\u5bb6\u5927\u5c0f\u5462\uff1f\u90a3\u67f3\u5a9a\u5a18\u53c8\u5c82\u80af\u653e\u8fc7\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u8fd9\u592a\u5bb9\u6613\u4e86\uff01\u201d \u3000\u3000\u6731\u7693\u82e6\u7b11\u9053\uff1a\u201c\u5012\u8981\u8bf7\u6559\u5b8b\u5144\u5176\u4e2d\u5de7\u5999\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u6682\u65f6\u79fb\u5c45\u4ed6\u5904\uff01\u201d \u3000\u3000\u6731\u7693\u5446\u4e86\u4e00\u5446\u9053\uff1a\u201c\u79fb\u5c45\u4ed6\u5904\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u82b1\u4e0d\u4e86\u4e00\u767e\u4e24\u94f6\u5b50\uff0c\u4f60\u6731\u5144\u83ab\u975e\u8fd8\u820d\u4e0d\u5f97\u5417\uff1f\u201d \u3000\u3000\u6731\u7693\u53ea\u542c\u5f97\u82e6\u7b11\u8fde\u8fde\uff0c\u63a5\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u5144\u5f1f\u867d\u7136\u5bb6\u9053\u4e0d\u88d5\uff0c\u5728\u8fd9\u5b9c\u660c\u5730\u9762\u4e4b\u4e0a\uff0c\u4e5f\u7b97\u662f\u6570\u4e00\u6570\u4e8c\u7684\u9996\u5bcc\u4e4b\u5bb6\uff0c\u4f11\u8bf4\u4e00\u767e\u4e24\u7eb9\u94f6\uff0c\u7eb5\u662f\u5341\u500d\u6b64\u6570\uff0c\u5144\u5f1f\u4e5f\u51fa\u5f97\u8d77\u554a\uff01\u53ea\u662f\u2026\u2026 \u3000\u3000\u5509\uff0c\u5144\u5f1f\u975e\u4e0d\u80fd\u4e5f\uff0c\u5b9e\u4e0d\u4e3a\u4e5f\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u6731\u5144\uff0c\u8001\u592b\u770b\u4f60\u662f\u5b58\u5fc3\u627e\u6b7b\u4e86\u3002\u201d \u3000\u3000\u6731\u7693\u5ffd\u7136\u6b63\u8272\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u5144\u5f1f\u7eb5\u7136\u4e00\u6b7b\uff0c\u81f3\u5c11\u4e5f\u8981\u90a3\u6210\u7384\u901a\u548c\u8463\u65b9\u4e4b\u4e2d\u7684\u4e00\u4f4d\u966a\u846c\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u51b7\u7b11\u9053\uff1a\u201c\u597d\u6c14\u8282\uff0c\u8001\u592b\u4f69\u670d\u5f97\u5f88\uff01\u201d \u3000\u3000\u7b11\u58f0\u4e00\u987f\uff0c\u6389\u5934\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u54b1\u4eec\u518d\u591a\u8bf4\u4e5f\u4e0d\u4f1a\u6709\u7528\u4e86\uff0c\u8fd8\u662f\u544a\u8f9e\u4e3a\u5999\u2026\u2026\u201d \u3000\u3000\u7ad9\u8d77\u8eab\u5b50\uff0c\u5c31\u5f85\u79bb\u53bb\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u6014\u9053\uff1a\u201c\u8fd9\u5c31\u8d70\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e0d\u8d70\u5e72\u4e48\uff1f\u8ddf\u8fd9\u7b49\u4e8e\u6b7b\u4e86\u4e00\u5927\u534a\u7684\u4eba\u8bf4\u8bdd\uff0c\u53eb\u4eba\u5fc3\u4e2d\u70e6\u5f97\u8981\u547d\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5927\u7b11\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u4e14\u6162\u8d77\u8eab\uff0c\u665a\u8f88\u8fd8\u6709\u51e0\u53e5\u8bdd\u8981\u8bf4\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u672c\u5df2\u7ad9\u8d77\u7684\u8eab\u5b50\uff0c\u53c8\u6162\u6162\u5730\u5750\u4e0b\u6765\uff0c\u63a5\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u53ea\u6015\u8fd8\u662f\u767d\u8d39\u5507\u820c\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u4e00\u5b9a\uff01\u201d \u3000\u3000\u56de\u5934\u5411\u6731\u7693\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u6731\u8001\uff0c\u90a3\u4e2d\u5dde\u4e09\u4fa0\u4e43\u662f\u5728\u4e0b\u7684\u957f\u8f88\uff0c\u4f60\u5e94\u8be5\u77e5\u9053\u7684\u4e86\uff1f\u201d \u3000\u3000\u6731\u7693\u9053\uff1a\u201c\u8001\u5f1f\u5df2\u7136\u8bf4\u8fc7\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u90a3\u6210\u7384\u901a\u65e2\u7136\u8981\u4f60\u5e26\u4e86\u4e2d\u5dde\u4e09\u4fa0\u4eba\u5934\u524d\u53bb\u8d74\u4f1a\uff0c\u90a3\u663e\u7136\u5c31\u662f\u5728\u4e0b\u957f\u8f88\u7684\u4ec7\u5bb6\u4e86\uff1f\u201d \u3000\u3000\u6731\u7693\u9053\uff1a\u201c\u7167\u7406\u7b97\u5f97\u4e0a\u662f\u4ee4\u5927\u4f2f\u7684\u4ec7\u5bb6\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4e86\uff0c\u4e3a\u4e86\u5728\u4e0b\u7684\u4ec7\u5bb6\uff0c\u800c\u8fde\u7d2f\u4e86\u6731\u5927\u4fa0\uff0c\u5728\u4e0b\u5c82\u80fd\u4e0d\u95ee\uff1f\u201d \u3000\u3000\u6731\u7693\u4e00\u6014\u9053\uff1a\u201c\u8001\u5f1f\u6709\u4f55\u6253\u7b97\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u5728\u4e0b\u53ea\u597d\u653e\u5f03\u5d69\u5c71\u4e4b\u884c\uff0c\u5148\u53bb\u627e\u90a3\u6210\u7384\u901a\u548c\u9f13\u738b\u91d1\u957f\u5e9a\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4f7f\u4e0d\u5f97\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u4f60\u8001\u5f1f\u4e00\u56de\u5ddd\u897f\uff0c\u5927\u6982\u5b89\u8001\u5f1f\u4e5f\u8981\u8ddf\u4f60\u4e00\u9053\u524d\u53bb\u7684\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5b89\u8d24\u5f1f\u4e48\uff1f\u8fd9\u53ef\u4e0d\u662f\u665a\u751f\u4f5c\u5f97\u4e86\u4e3b\uff0c\u4e5f\u8bb8\u5b89\u8d24\u5f1f\u4e0d\u53bb\u2026\u2026\u201d \u3000\u3000\u56de\u987e\u4e86\u5b89\u5c0f\u840d\u4e00\u773c\uff0c\u53ea\u89c1\u5979\u5927\u773c\u4e00\u7728\uff0c\u7b11\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u6211\u4e0d\u80fd\u53bb\u5462\uff1f\u65b9\u5144\uff0c\u4f60\u5230\u54ea\u91cc\uff0c\u6211\u5c31\u8981\u8ddf\u5230\u54ea\u91cc\uff0c\u65e2\u662f\u4f60\u4e0d\u53bb\u5d69\u5c71\uff0c\u6211\u5f53\u7136\u4e5f\u4e0d\u53bb\u7684\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u76b1\u4e86\u4e00\u76b1\u7709\u5934\uff0c\u5b8b\u5b5a\u63a5\u9053\uff1a\u201c\u65b9\u8001\u5f1f\uff0c\u6709\u4e00\u4ef6\u4e8b\uff0c\u4f60\u83ab\u8981\u5fd8\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u6b66\u6797\u5927\u4e49\uff0c\u5e94\u662f\u91cd\u8fc7\u79c1\u4eba\u4ec7\u6068\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u800c\u4eca\u52a0\u4e0a\u4e86\u6731\u5927\u4fa0\u4e00\u5bb6\u7684\u751f\u547d\u8d22\u5b9d\uff0c\u53ef\u5c31\u7b97\u4e0d\u5f97\u662f\u65b9\u5144\u4e00\u4eba\u7684\u79c1\u4ec7\u4e86\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8d24\u5f1f\u5012\u662f\u6df1\u5f97\u6211\u5fc3\uff01\u201d \u3000\u3000\u4ed6\u672c\u662f\u4e00\u53e5\u65e0\u5fc3\u4e4b\u8a00\uff0c\u4f46\u5374\u53eb\u5b89\u5c0f\u840d\u542c\u5728\u8033\u4e2d\uff0c\u559c\u5728\u5fc3\u4e2d\uff0c\u7f9e\u5728\u8138\u4e0a\uff0c\u987f\u65f6\uff0c\u7ea2\u7740\u5a07\u9765\uff0c\u4f4e\u4e0b\u5934\u53bb\u3002 \u3000\u3000\u5b8b\u5b5a\u5475\u5475\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4f4d\u8001\u5f1f\u5012\u5f88\u6709\u610f\u601d\uff0c\u4e00\u62c9\u4e00\u5531\uff0c\u914d\u5408\u5f97\u86ee\u597d\u554a\uff01\u53ef\u60dc\u7684\u662f\uff0c\u4f60\u4eec\u8fd8\u662f\u9519\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u9519\u5728\u4f55\u5904\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e00\u4e24\u5bb6\u4eba\uff0c\u66ff\u4ee3\u4e0d\u4e86\u6574\u4e2a\u6b66\u6797\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6545\u610f\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u90a3\u2026\u2026\u8be5\u600e\u4e48\u529e\u624d\u7b97\u4e0d\u9519\u5462\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u5148\u53bb\u5d69\u5c71\uff0c\u518d\u51b3\u79c1\u4ec7\uff01\u201d \u3000\u3000\u6731\u7693\u8fd9\u4e00\u4f1a\u513f\u4e00\u76f4\u5728\u65c1\u4f4e\u5934\u6c89\u601d\uff0c\u6b64\u523b\u5ffd\u7136\u5927\u58f0\u9053\uff1a\u201c\u5b8b\u5144\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u5fc3\u60f3\uff0c\u4f60\u4e5f\u8be5\u901a\u7a83\u8bf4\u8bdd\u4e86\uff0c\u4f46\u4ed6\u53e3\u4e2d\u5374\u662f\u63a5\u9053\uff1a\u201c\u5b8b\u5144\u6709\u4f55\u9ad8\u89c1\uff1f\u201d \u3000\u3000\u6731\u7693\u9053\uff1a\u201c\u5144\u5f1f\u60f3\u660e\u767d\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u6545\u4f5c\u5341\u5206\u4e0d\u89e3\u5730\u7b11\u9053\uff1a\u201c\u6731\u5144\u660e\u767d\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6731\u7693\u9053\uff1a\u201c\u5144\u5f1f\u90a3\u7b49\u987e\u5fcc\u79c1\u4eba\u58f0\u671b\u7684\u60f3\u6cd5\uff0c\u679c\u7136\u662f\u592a\u8fc2\u8150\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u6731\u5144\u83ab\u975e\u662f\u6253\u7b97\u60f3\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u6731\u7693\u957f\u957f\u5730\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u5144\u5f1f\u867d\u7136\u611a\u8822\uff0c\u4f46\u4e5f\u4e0d\u662f\u90a3\u4e0d\u901a\u60c5\u7406\u4e4b\u4eba\uff0c\u4e09\u4f4d\u8fd9\u7b49\u82e6\u53e3\u5a46\u5fc3\u76f8\u529d\u76f8\u8bbd\uff0c\u5144\u5f1f\u5982\u662f\u518d\u4e0d\u7701\u609f\uff0c\u90a3\u771f\u662f\u79bd\u517d\u4e0d\u5982\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u6731\u5144\u4e4b\u610f\uff0c\u662f\u4e0d\u518d\u604b\u6808\u4e8e\u5b9c\u660c\u57ce\u4e2d\u7684\u4ea7\u4e1a\u4e86\uff01\u201d \u3000\u3000\u6731\u7693\u76b1\u7709\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u5144\u5f1f\u2026\u2026\u5509\uff0c\u5144\u5f1f\u8fd9\u5c31\u53bb\u4ea4\u4ee3\u4ed6\u4eec\u5b89\u6392\u4e00\u5207\uff0c\u5144\u5f1f\u672c\u4eba\uff0c\u5219\u51b3\u5b9a\u8ffd\u968f\u4e09\u4f4d\u524d\u5f80\u5c11\u6797\uff01\u201d \u3000\u3000\u8bdd\u97f3\u4e00\u843d\uff0c\u7acb\u5373\u8d77\u8eab\u5411\u540e\u5802\u884c\u53bb\u3002 \u3000\u3000\u5b8b\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u6731\u5144\u4e0d\u5931\u4e3a\u660e\u54f2\u4e4b\u58eb\u2026\u2026\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u5df2\u6709\u51e0\u540d\u5c0f\u53ae\u9001\u4e0a\u4e86\u9152\u83dc\u3002 \u3000\u3000\u5b8b\u5b5a\u5012\u662f\u8001\u5b9e\u5f97\u5f88\uff0c\u6743\u5145\u4e3b\u4eba\uff0c\u8981\u65b9\u96ea\u5b9c\u548c\u5b89\u5c0f\u840d\u4e24\u4eba\u9971\u9910\u4e00\u987f\u3002 \u3000\u3000\u4e09\u4eba\u521a\u521a\u5403\u4e86\u516b\u6210\uff0c\u6731\u7693\u5df2\u6362\u4e0a\u4e00\u8eab\u77ed\u6253\uff0c\u4ece\u90a3\u540e\u5802\u8d70\u4e86\u51fa\u6765\u3002\u53cc\u624b\u62b1\u62f3\uff0c\u8fde\u8fde\u544a\u7f6a\u3002 \u3000\u3000\u5b8b\u5b5a\u653e\u4e0b\u4e86\u624b\u4e2d\u7259\u7b77\uff0c\u62b9\u4e86\u62b9\u5634\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u8fd9\u5c31\u8d70\u5427\uff01\u201d \u3000\u3000\u6731\u7693\u7b11\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u9a6c\u5339\u5747\u5df2\u5907\u59a5\uff0c\u968f\u65f6\u7686\u53ef\u52a8\u8eab\uff0c\u4f60\u53c8\u4f55\u987b\u6025\u6b64\u4e00\u65f6\uff1f\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5df2\u7136\u62ff\u8d77\u684c\u4e0a\u7684\u7389\u76cf\uff0c\u659f\u6ee1\u4e86\u4e00\u676f\u9152\uff0c\u5411\u4e09\u4eba\u7167\u4e86\u7167\uff0c\u4ef0\u5934\u4e00\u996e\u800c\u5c3d\uff0c\u63a5\u9053\uff1a\u201c\u6731\u67d0\u82e5\u975e\u9047\u7740\u4e09\u4f4d\uff0c\u53ea\u6015\u8fd9\u4e00\u5bb6\u5927\u5c0f\uff0c\u90fd\u5c06\u6210\u4e3a\u4e94\u9b54\u5200\u4e0b\u4e4b\u9b3c\u4e86\uff01\u6731\u67d0\u6709\u5fc3\u8981\u5185\u5ba4\u5987\u5b7a\u524d\u6765\u53e9\u8c22\uff0c\u53ea\u6015\u4e09\u4f4d\u5acc\u6211\u5eb8\u4fd7\uff0c\u5c31\u7531\u6731\u67d0\u501f\u8fd9\u4e00\u676f\u6c34\u9152\uff0c\u5411\u4e09\u4f4d\u804a\u81f4\u656c\u610f\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fde\u5fd9\u62f1\u624b\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u6731\u8001\u8a00\u91cd\u4e86\uff01\u201d \u3000\u3000\u6731\u7693\u5927\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u5927\u6069\u4e0d\u6562\u8a00\u8c22\uff0c\u65e5\u540e\u8001\u5f1f\u4f46\u6709\u7528\u5f97\u5230\u6731\u67d0\u4e4b\u5904\uff0c\u6731\u67d0\u8fd9\u6761\u8001\u547d\uff0c\u5168\u4ea4\u7ed9\u4f60\u8001\u5f1f\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u66f4\u662f\u8fde\u8fde\u900a\u8c22\u4e0d\u8fed\u3002 \u3000\u3000\u5b8b\u5b5a\u76ee\u5149\u8f6c\u4e86\u4e00\u8f6c\uff0c\u9053\uff1a\u201c\u6731\u5144\uff0c\u4f60\u4e0d\u7528\u591a\u8bf4\u8fd9\u4e9b\u5e9f\u8bdd\u4e86\uff0c\u5f80\u540e\u7684\u65e5\u5b50\u8fd8\u957f\u7740\u5f88\uff0c\u54b1\u4eec\u8d76\u5feb\u4e9b\u5427\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u65e9\u5c31\u505c\u8457\u4e0d\u7528\u4e86\uff0c\u65b9\u96ea\u5b9c\u95fb\u8a00\u7b11\u9053\uff1a\u201c\u6731\u8001\uff0c\u54b1\u4eec\u627f\u8499\u76db\u5f85\uff0c\u5df2\u7136\u9152\u8db3\u996d\u9971\uff0c\u5012\u662f\u6731\u8001\u81ea\u5df1\u672a\u66fe\u8fdb\u98df\u2026\u2026\u201d \u3000\u3000\u6731\u7693\u672a\u7b49\u65b9\u96ea\u5b9c\u518d\u4f4f\u4e0b\u8bf4\uff0c\u63a5\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u8001\u673d\u4e0d\u997f\uff0c\u8981\u8d70\u5c31\u8d70\uff0c\u53ea\u662f\u6162\u6020\u4e4b\u7f6a\u5c1a\u76fc\u83ab\u8981\u89c1\u602a\u624d\u597d\u554a\uff01\u201d \u3000\u3000\u663e\u7136\uff0c\u6211\u8fd9\u8001\u4eba\u53c8\u72af\u4e86\u5ba2\u5957\u6bdb\u75c5\u4e86\u3002 \u3000\u3000\u5b8b\u5b5a\u5927\u559d\u4e00\u58f0\u9053\uff1a\u201c\u7b97\u5566\uff0c\u54b1\u4eec\u8d70\uff01\u201d \u3000\u3000\u8f6c\u8eab\u5927\u6b65\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u6731\u7693\u5446\u5f97\u4e00\u5446\uff0c\u4f46\u77ac\u5373\u5927\u7b11\u9053\uff1a\u201c\u662f\u554a\uff0c\u54b1\u4eec\u679c\u7136\u662f\u5e94\u8be5\u8d70\u4e86\uff01\u201d \u3000\u3000\u56db\u4eba\u9c7c\u8d2f\u51fa\u4e86\u5e9c\u95e8\uff0c\u53ea\u89c1\u56db\u540d\u5065\u4ec6\uff0c\u62c9\u4e86\u56db\u5339\u957f\u7a0b\u5065\u9a6c\uff0c\u7b49\u5728\u5927\u95e8\u5916\u7684\u9a6c\u77f3\u65c1\u3002 \u3000\u3000\u6731\u7693\u770b\u5f97\u4e00\u6014\u9053\uff1a\u201c\u600e\u4e48\uff1f\u8fd8\u8981\u5e26\u4ed6\u4eec\u53bb\u5417\uff0c\u4e0d\u5fc5\u4e86\uff01\u201d \u3000\u3000\u95ea\u8eab\u4e00\u8dc3\uff0c\u8df3\u4e0a\u4e86\u7b2c\u4e00\u5339\u9a6c\u978d\uff0c\u4e00\u6325\u624b\uff0c\u63a5\u9053\uff1a\u201c\u6731\u5144\uff0c\u54b1\u4eec\u4e00\u65e6\u653e\u9a6c\u75be\u884c\uff0c\u4ed6\u4eec\u5c31\u8ddf\u4e0d\u4e0a\u4e86\uff0c\u5012\u4e0d\u5982\u53eb\u4ed6\u4eec\u7559\u4e0b\u7167\u987e\u5bb6\u5c0f\u4e3a\u5999\u3002\u201d \u3000\u3000\u6731\u7693\u95fb\u8a00\uff0c\u7acb\u5373\u5411\u90a3\u56db\u540d\u5065\u4ec6\u4ea4\u4ee3\u4e86\u51e0\u53e5\uff0c\u8fd9\u624d\u7ffb\u8eab\u4e0a\u9a6c\uff0c\u4e00\u6296\u4e1d\u7f30\uff0c\u5411\u90a3\u4e5f\u5df2\u653e\u8e44\u800c\u53bb\u7684\u5b8b\u5b5a\u3001\u65b9\u96ea\u5b9c\u3001\u5b89\u5c0f\u840d\u4e09\u4eba\u8ffd\u4e0a\u3002 \u3000\u3000\u51fa\u4e86\u5b9c\u660c\u5317\u95e8\uff0c\u56db\u4eba\u987f\u65f6\u653e\u8e44\u75be\u9a70\uff0c\u6cbf\u7740\u8346\u5c71\uff0c\u76f4\u5411\u8944\u9633\u65b9\u5411\u800c\u53bb\u3002 \u3000\u3000\u7b2c\u56db\u5929\uff0c\u4ed6\u4eec\u6e21\u8fc7\u4e86\u6c49\u6c34\uff0c\u5f53\u591c\u8fdb\u5165\u6cb3\u5357\u7701\u5883\u3002 \u3000\u3000\u6b64\u884c\u867d\u662f\u5341\u5206\u987a\u5229\uff0c\u4f46\u8fdb\u5165\u4e86\u6cb3\u5357\u5883\u5185\u4e4b\u540e\uff0c\u5c31\u53d1\u73b0\u90a3\u8346\u6d1b\u5b98\u9053\u4e4b\u4e0a\uff0c\u5f80\u6765\u7684\u884c\u4eba\uff0c\u4e0e\u90a3\u6e56\u5317\u5927\u5927\u4e0d\u540c\u3002 \u3000\u3000\u4ed6\u4eec\u524d\u56db\u5929\u5f88\u5c11\u4f1a\u9047\u5230\u6b66\u6797\u4eba\u7269\uff0c\u4f46\u4e00\u8fc7\u5357\u9633\u4e0d\u4e45\uff0c\u5b98\u9053\u4e4b\u4e0a\uff0c\u5c31\u4e0d\u65f6\u51fa\u73b0\u5947\u88c5\u5f02\u670d\u7684\u6b66\u6797\u4eba\u7269\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u4e2a\u4e2a\u90fd\u662f\u9762\u5bb9\u6c89\u8083\uff0c\u8ddf\u4ed6\u4eec\u4e00\u822c\u7684\u8d76\u7a0b\u75be\u884c\uff0c\u770b\u6837\u5b50\uff0c\u8fd9\u4e9b\u4eba\u4e2d\uff0c\u5927\u591a\u4e5f\u90fd\u662f\u8d76\u5411\u5d69\u5c71\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u51b7\u773c\u65c1\u89c2\uff0c\u53d1\u73b0\u8fd9\u4e00\u6279\u6279\u7684\u6b66\u6797\u4eba\u7269\u4e4b\u4e2d\uff0c\u53ef\u8c13\u662f\u5404\u8272\u4eba\u7b49\u7686\u6709\uff0c\u50e7\u535c\u533b\u9053\u4fd7\uff0c\u8001\u5c11\u5987\u5c3c\uff0c\u5939\u6742\u5176\u95f4\uff0c\u770b\u6765\u5012\u4e5f\u5341\u5206\u70ed\u95f9\u3002 \u3000\u3000\u5b8b\u5b5a\u8eab\u5728\u9a6c\u4e0a\uff0c\u5374\u662f\u9547\u9759\u5f97\u5f88\uff0c\u4f46\u90a3\u6731\u7693\u5219\u663e\u5f97\u6709\u4e86\u7d27\u5f20\u548c\u4e0d\u5b89\u4e4b\u8272\uff01 \u3000\u3000\u5b89\u5c0f\u840d\u4e00\u6d3e\u5929\u771f\uff0c\u4f3c\u662f\u5bf9\u8fd9\u8def\u4e0a\u7684\u5404\u8272\u4eba\u7b49\uff0c\u89c9\u5f97\u76f8\u5f53\u597d\u73a9\uff0c\u4e0d\u65f6\u6307\u4e1c\u5212\u897f\u5730\u5411\u65b9\u96ea\u5b9c\u8bf4\u7b11\u3002 \u3000\u3000\u4ed6\u4eec\u4fe9\u662f\u5e76\u9a91\u8d70\u5728\u4e24\u4f4d\u8001\u4eba\u8eab\u540e\uff0c\u56e0\u6b64\uff0c\u4ed6\u4eec\u4e24\u4eba\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u8d70\u5728\u524d\u9762\u7684\u5b8b\u5b5a\u548c\u6731\u7693\u90fd\u4e0d\u592a\u660e\u4e86\u3002 \u3000\u3000\u4e00\u8def\u4e4b\u4e0a\uff0c\u603b\u7b97\u5e73\u5b89\u5730\u5ea6\u8fc7\uff0c\u6ca1\u6709\u51fa\u4ec0\u4e48\u9ebb\u70e6\u3002 \u3000\u3000\u8fd9\u4e00\u5929\uff0c\u4e00\u884c\u56db\u4eba\u5df2\u7136\u62b5\u8fbe\u4e86\u6c5d\u5dde\u3002 \u3000\u3000\u7531\u6b64\u53bb\u81f3\u5d69\u5c71\uff0c\u5df2\u4e0d\u8db3\u4e00\u65e5\u884c\u7a0b\uff0c\u4f9d\u7740\u6731\u7693\u4e4b\u610f\uff0c\u5c31\u4e0d\u5982\u8fde\u591c\u8d76\u8d74\u5d69\u5c71\uff0c\u5348\u591c\u65f6\u5206\uff0c\u5f53\u53ef\u62b5\u8fbe\u3002 \u3000\u3000\u4f46\u5b8b\u5b5a\u5374\u4e0d\u540c\u610f\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u4e5f\u4e0d\u540c\u610f\uff0c\u5979\u8ba4\u4e3a\u4e0d\u5982\u5728\u8fd9\u6c5d\u5dde\u57ce\u7a0d\u4f5c\u4f11\u606f\uff0c\u660e\u5929\u4e5f\u597d\u5728\u5348\u65f6\u5de6\u53f3\uff0c\u518d\u8d76\u5230\u5c11\u6797\u5bfa\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u5012\u65e0\u6240\u8c13\uff0c\u867d\u7136\u6b64\u5904\u79bb\u4ed6\u6545\u56ed\u4e0d\u8fdc\uff0c\u4ed6\u53cd\u800c\u663e\u5f97\u683c\u5916\u7684\u5e73\u9759\uff0c\u6ca1\u6709\u4e1d\u6beb\u4e61\u60c5\u602f\u7684\u8868\u60c5\u3002\u56e0\u4e4b\uff0c\u4ed6\u5bf9\u53bb\u7559\u4e4b\u4e8b\uff0c\u6839\u672c\u6ca1\u6709\u610f\u89c1\u3002 \u3000\u3000\u6731\u7693\u663e\u7136\u5f3a\u4e0d\u8fc7\u5b8b\u5b5a\uff0c\u53ea\u597d\u5728\u6c5d\u5dde\u57ce\u4e2d\u4f4f\u4e0b\u3002 \u3000\u3000\u4ed6\u4eec\u62b5\u8fbe\u6c5d\u5dde\uff0c\u5927\u7ea6\u5728\u7533\u897f\u4e4b\u95f4\uff0c\u5bb9\u5f97\u4ed6\u4eec\u597d\u4e0d\u5bb9\u6613\u5730\u627e\u5230\u4e86\u4e00\u5bb6\u5ba2\u6808\u4f4f\u4e0b\uff0c\u5df2\u7136\u662f\u638c\u706f\u65f6\u5206\u4e86\u3002 \u3000\u3000\u56db\u4eba\u7565\u4e00\u6f31\u6d17\uff0c\u968f\u4fbf\u5730\u53eb\u4e86\u4e00\u4e9b\u9152\u83dc\u679c\u8179\uff0c\u5b89\u5c0f\u840d\u4fbf\u72ec\u81ea\u4e00\u4eba\u8fdb\u4e86\u5185\u95f4\u6b47\u606f\u3002 \u3000\u3000\u5b8b\u5b5a\u3001\u6731\u7693\u3001\u65b9\u96ea\u5b9c\u7b49\u4e09\u4eba\u5219\u5171\u540c\u5728\u5916\u95f4\u7684\u4e00\u5904\u5927\u5ba2\u623f\u4e4b\u4e2d\u3002 \u3000\u3000\u8fd9\u5bb6\u5ba2\u6808\uff0c\u5f00\u5728\u4e00\u4e2a\u5341\u5206\u50fb\u9759\u7684\u8857\u574a\uff0c\u5b8b\u5b5a\u7b49\u4eba\u80fd\u591f\u627e\u5230\u53ef\u4ee5\u5b58\u8eab\u4e4b\u5904\uff0c\u5c31\u56e0\u4e3a\u8fd9\u95f4\u5ba2\u6808\u5ea7\u843d\u5728\u50fb\u9759\u4e4b\u5904\u3002 \u3000\u3000\u5373\u4f7f\u8fd9\u5bb6\u5ba2\u6808\u4e0d\u5927\uff0c\u4f46\u6b64\u523b\u4e5f\u5df2\u7136\u5ba2\u6ee1\u4e86\u3002 \u3000\u3000\u4e8c\u66f4\u8fc7\u540e\uff0c\u5b8b\u5b5a\u7a81\u7136\u6084\u6084\u8d77\u8eab\u53eb\u9192\u4e86\u65b9\u96ea\u5b9c\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u53ef\u77e5\u9053\u8001\u673d\u4eca\u591c\u8981\u7559\u5728\u6c5d\u5dde\u4e4b\u610f\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u731c\u4e0d\u51fa\u6765\uff0c\u4f46\u665a\u8f88\u5374\u77e5\u9053\uff0c\u4f60\u8001\u6b64\u4e3e\uff0c\u5fc5\u6709\u6df1\u610f\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u6df1\u610f\u5012\u4e0d\u89c1\u5f97\u6709\uff0c\u53ea\u662f\u8001\u673d\u5374\u53d1\u73b0\u4e86\u4e00\u6869\u503c\u5f97\u6ce8\u610f\u4e4b\u4e8b\uff0c\u6240\u4ee5\u624d\u8981\u7559\u5728\u6c5d\u5dde\u67e5\u4e0a\u4e00\u67e5\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6014\u4e86\u4e00\u6014\u9053\uff1a\u201c\u4ec0\u4e48\u5947\u602a\u4e4b\u4e8b\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u90a3\u4e5f\u4e0d\u89c1\u5f97\u662f\u4ec0\u4e48\u5947\u602a\u4e4b\u4e8b\uff0c\u53ea\u4e0d\u8fc7\u662f\u6c5f\u6e56\u4e0a\u4eba\u7269\u7559\u4e0b\u7684\u6697\u8bb0\u800c\u5df2\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u7559\u4e0b\u7684\u6697\u8bb0\u554a\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e10\u5e2e\u5f1f\u5b50\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4e10\u5e2e\u4e5f\u6709\u4eba\u5377\u4eba\u8fd9\u573a\u662f\u975e\u4e4b\u4e2d\u4e86\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e10\u5e2e\u5f1f\u5b50\u904d\u5e03\u5929\u4e0b\uff0c\u4e94\u9b54\u5982\u60f3\u9010\u9e7f\u4e2d\u539f\uff0c\u60f3\u5fc5\u662f\u653e\u4e0d\u8fc7\u4ed6\u4eec\u7684\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u4f60\u6253\u7b97\u600e\u4e48\u67e5\u8bbf\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u77a7\u90a3\u7559\u4e0b\u7684\u6697\u8bb0\uff0c\u660e\u660e\u6307\u51fa\u4e10\u5e2e\u5df2\u6709\u91cd\u8981\u4eba\u7269\u5728\u4eca\u591c\u62b5\u8fbe\u6c5d\u5dde\uff0c\u5e76\u4e14\u7ea6\u5b9a\u8981\u4e10\u5e2e\u5f1f\u5b50\u4eec\u4e09\u66f4\u65f6\u5206\uff0c\u5728\u57ce\u5317\u7684\u5173\u738b\u5e99\u4e2d\u805a\u4f1a\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u4f60\u8001\u53ef\u4e5f\u662f\u4e10\u5e2e\u4e2d\u4eba\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u4e0d\u662f\u554a\uff01\u8001\u5f1f\uff0c\u4f60\u600e\u4f1a\u53d1\u6b64\u5947\u60f3\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u665a\u8f88\u77e5\u9053\uff0c\u6b66\u6797\u5404\u5927\u95e8\u6d3e\u7684\u6697\u8bb0\u9664\u4e86\u4ed6\u4eec\u95e8\u4e0b\u5b50\u5f1f\uff0c\u522b\u4eba\u662f\u4e0d\u4f1a\u77e5\u9053\uff0c\u4f60\u8001\u82e5\u975e\u4e10\u5e2e\u4e2d\u4eba\uff0c\u4e0d\u77e5\u600e\u4f1a\u77e5\u6653\u4e86\u4ed6\u4eec\u7684\u6697\u8bb0\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u83ab\u5fd8\u4e86\u8001\u673d\u90a3\u5927\u6f20\u9b3c\u624b\u7684\u532a\u53f7\u554a\uff01\u5018\u82e5\u8001\u673d\u51b3\u5fc3\u8981\u60f3\u77e5\u6653\u6b66\u6797\u4e2d\u54ea\u4e00\u6d3e\u7684\u79d8\u5bc6\uff0c\u5927\u6982\u4ed6\u4eec\u8fd8\u65e0\u6cd5\u7792\u5f97\u4e86\u8001\u673d\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u8001\u662f\u6109\u542c\u6765\u7684\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u5dee\u4e0d\u4e86\u591a\u5c11\u2026\u2026\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u6731\u7693\u5ffd\u7136\u7741\u5f00\u4e86\u773c\u6765\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u600e\u4e48\u53c8\u8d77\u6765\u4e86\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u7761\u4e0d\u7740\uff0c\u5f53\u7136\u8981\u8d77\u6765\u804a\u804a\u3002\u201d \u3000\u3000\u6731\u7693\u9053\uff1a\u201c\u770b\u5b8b\u5144\u7684\u795e\u8272\uff0c\u53ea\u6015\u6709\u4ec0\u4e48\u4e8b\u60c5\u53d1\u751f\uff0c\u4e0d\u77e5\u5b8b\u5144\u662f\u5426\u613f\u610f\u89c1\u544a\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u6731\u5144\u679c\u7136\u8001\u7ec3\u8fc7\u4eba\uff0c\u8001\u592b\u867d\u662f\u4e0e\u65b9\u8001\u5f1f\u6709\u70b9\u4e8b\u8981\u51fa\u53bb\u4e00\u8d9f\uff0c\u6731\u5144\u53ef\u8981\u5c0f\u5fc3\u7167\u987e\u7740\u91cc\u95f4\u7684\u5b89\u59d1\u5a18\u2026\u2026\u201d \u3000\u3000\u6731\u7693\u95fb\u8a00\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5b89\u59d1\u5a18\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5b89\u8001\u5f1f\u4e43\u662f\u4f4d\u5973\u513f\u4e4b\u8eab\uff01\u201d \u3000\u3000\u6731\u7693\u8138\u4e0a\u7684\u795e\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u5b8b\u5144\u53ef\u662f\u8981\u5144\u5f1f\u7559\u5728\u6b64\u95f4\uff0c\u7167\u987e\u5b89\u59d1\u5a18\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u6731\u7693\u4f3c\u662f\u9887\u4e3a\u4e0d\u5b89\u5730\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u5144\u5f1f\u7684\u4e00\u8eab\u80fd\u8010\uff0c\u5b8b\u5144\u5e94\u662f\u660e\u767d\uff0c\u5b89\u59d1\u5a18\u65e2\u662f\u5973\u513f\u4e4b\u8eab\uff0c\u5982\u662f\u771f\u6709\u4ec0\u4e48\u4eba\u524d\u6765\u6ecb\u4e8b\uff0c\u5144\u5f1f\u53ea\u6015\u529b\u96be\u72ec\u652f\u554a\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u95fb\u8a00\uff0c\u5927\u7b11\u9053\uff1a\u201c\u9601\u4e0b\u653e\u5fc3\uff0c\u4e0d\u4f1a\u6709\u4eba\u80c6\u6562\u524d\u6765\u751f\u4e8b\u7684\uff01\u201d \u3000\u3000\u6731\u7693\u65e0\u53ef\u5948\u4f55\u5730\u82e6\u7b11\u9053\uff1a\u201c\u5144\u5f1f\u53ea\u597d\u52c9\u529b\u4ee5\u8d74\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u5475\u5475\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6709\u52b3\u6731\u5144\u4e86\u3002\u201d \u3000\u3000\u5411\u65b9\u96ea\u5b9c\u4e00\u62db\u624b\uff0c\u4e24\u4eba\u95ea\u8eab\u7531\u7a97\u4e2d\u7a7f\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u6c5d\u5dde\u5730\u5f62\uff0c\u5b8b\u5b5a\u4e5f\u4f3c\u5341\u5206\u719f\u6089\uff0c\u968f\u7740\u65b9\u96ea\u5b9c\u7a7f\u623f\u8d8a\u5c4b\uff0c\u8fc5\u5feb\u5730\u5411\u5317\u95e8\u57ce\u5916\u5954\u53bb\u3002 \u3000\u3000\u76cf\u8336\u65f6\u5149\u4e0d\u5230\uff0c\u4e24\u4eba\u5df2\u51fa\u4e86\u5317\u95e8\u3002 \u3000\u3000\u5b8b\u5b5a\u7565\u4e00\u6253\u91cf\uff0c\u7a81\u7136\u6307\u7740\u53f3\u624b\u4e00\u7247\u4e1b\u6797\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u90a3\u5173\u738b\u5e99\u5c31\u5728\u90a3\u7247\u7af9\u6797\u4e4b\u540e\uff0c\u54b1\u4eec\u524d\u53bb\uff0c\u53ef\u5f97\u5c0f\u5fc3\u4e9b\u624d\u662f\uff0c\u83ab\u8ba9\u4e10\u5e2e\u5f1f\u5b50\u53d1\u73b0\uff0c\u591a\u60f9\u7ea0\u7eb7\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u665a\u8f88\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u4e24\u4eba\u4ed4\u7ec6\u5730\u5ea6\u91cf\u4e86\u4e00\u756a\u5730\u52bf\uff0c\u53d1\u89c9\u8981\u8fdb\u5165\u8fd9\u7247\u7af9\u6797\uff0c\u5982\u662f\u6797\u4e2d\u6709\u4e86\u5b88\u671b\u4e4b\u535c\uff0c\u7eb5\u662f\u4e24\u4eba\u8eab\u6cd5\u518d\u5feb\uff0c\u4e5f\u65e0\u6cd5\u9003\u5f97\u8fc7\u5bf9\u65b9\u8033\u76ee\uff0c\u662f\u4ee5\uff0c\u4f9d\u65b9\u96ea\u5b9c\u7684\u89c1\u89e3\uff0c\u83ab\u5982\u7ed5\u8fc7\u90a3\u7247\u7af9\u6797\u524d\u53bb\u3002 \u3000\u3000\u4f46\u672a\u5b5a\u5374\u6301\u76f8\u53cd\u4e4b\u89c1 \u3000\u3000\u65b9\u96ea\u5b9c\u76b1\u76b1\u7709\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u54b1\u4eec\u5982\u4ece\u6797\u4e2d\u7a7f\u8fc7\uff0c\u542c\u6015\u4e00\u5b9a\u7792\u4e0d\u8fc7\u4ed6\u4eec\u8033\u76ee\u554a\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u4f4e\u58f0\u7b11\u9053\uff1a\u201c\u4e0d\u4e00\u5b9a\uff0c\u8001\u5f1f\uff0c\u5982\u662f\u8001\u673d\u731c\u60f3\u5f97\u4e0d\u9519\uff0c\u8fd9\u7af9\u6797\u4e4b\u4e2d\u7684\u6697\u6869\uff0c\u5fc5\u7136\u6bd4\u522b\u7684\u65b9\u4f4d\u8981\u5c11\uff0c\u54b1\u4eec\u5018\u80fd\u8fc5\u5feb\u903c\u8fd1\u7af9\u6797\uff0c\u518d\u4ee5\u6781\u5feb\u7684\u8eab\u6cd5\u8dc3\u767b\u6797\u68a2\uff0c\u5219\u4ed6\u4eec\u5f88\u96be\u89c9\u51fa\u7684\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4ed6\u4eec\u4e0d\u4f1a\u60f3\u5230\u6709\u4eba\u80c6\u6562\u4ece\u6b63\u9762\u800c\u6765\uff0c\u662f\u4ee5\uff0c\u5373\u4ee4\u4ed6\u4eec\u6709\u6b64\u53d1\u73b0\uff0c\u5982\u662f\u6211\u4eec\u7a0d\u5feb\u4e00\u70b9\uff0c\u90a3\u4e5f\u5f88\u5bb9\u6613\u5c06\u4ed6\u4eec\u7792\u8fc7\uff0c\u53ea\u9053\u662f\u4ec0\u4e48\u98de\u9e1f\u9e70\u4e4b\u7c7b\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u5012\u4e5f\u6709\u9053\u7406\uff0c\u4f46\u4e0d\u77e5\u54b1\u4eec\u5e94\u8be5\u600e\u6837\u903c\u8fd1\u6811\u6797\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u4f60\u8001\u5f1f\u8f7b\u529f\u8eab\u6cd5\uff0c\u4e00\u53e3\u6c14\u53ef\u4ee5\u8dc3\u8fdb\u591a\u8fdc\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e94\u4e08\u4ee5\u5185\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u591f\u4e86\uff0c\u8001\u5f1f\u7d27\u7d27\u8ddf\u5728\u6211\u8eab\u540e\uff0c\u54b1\u4eec\u8fd9\u5c31\u5411\u90a3\u7247\u7af9\u6797\u63a9\u8fdb\u2026\u2026\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u7acb\u5373\u817e\u8eab\u5411\u524d\u5954\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4f46\u89c1\u4ed6\u5b9b\u5982\u4e00\u7f15\u8f7b\u70df\uff0c\u7728\u773c\u4e4b\u95f4\uff0c\u5df2\u53bb\u4e86\u5341\u4e08\u4e4b\u5916\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u4eec\u5b58\u8eab\u4e4b\u5904\uff0c\u79bb\u5f00\u7af9\u6797\u7ea6\u6709\u4e09\u5341\u4f59\u4e08\uff0c\u5b8b\u5b5a\u8fd9\u4e00\u5954\u884c\uff0c\u4e5f\u4e0d\u8fc7\u4e00\u8f6c\u77ac\u4e4b\u95f4\uff0c\u4fbf\u5df2\u98de\u4e0a\u6797\u68a2\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e0d\u6562\u6020\u6162\uff0c\u4e00\u8f6c\u8eab\uff0c\u4e5f\u81ea\u8d76\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4e24\u4eba\u7a7f\u8fc7\u4e86\u7af9\u6797\uff0c\u5c45\u7136\u6ca1\u6709\u4e1d\u6beb\u52a8\u9759\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u603b\u89c9\u5f97\u6709\u4e9b\u5947\u602a\uff0c\u4f46\u5b8b\u5b5a\u5374\u89c6\u4f5c\u7406\u6240\u5f53\u7136\uff0c\u9886\u7740\u4ed6\u4ece\u7af9\u6797\u7684\u8fb9\u7f18\uff0c\u7ed5\u5411\u5e99\u540e\u7684\u5929\u4e95\u4e4b\u4e2d\u3002 \u3000\u3000\u4ed6\u4eec\u521a\u81ea\u5728\u90a3\u6b63\u6bbf\u5de6\u4fa7\u7684\u4e00\u682a\u6811\u4e4b\u4e0a\uff0c\u9690\u597d\u4e86\u8eab\u5f62\uff0c\u53ea\u89c1\u5173\u738b\u5e99\u4e2d\uff0c\u5ffd\u7136\u706f\u706b\u5927\u660e\u3002 \u3000\u3000\u6562\u60c5\uff0c\u4ed6\u4eec\u6765\u5f97\u751a\u65e9\uff0c\u4e10\u5e2e\u4e2d\u7684\u9996\u8981\u4eba\u7269\u4e0d\u4f46\u672a\u6765\uff0c\u90a3\u8d1f\u8d23\u5148\u884c\u529e\u7406\u4e00\u5207\u7684\u5f1f\u5b50\u4e5f\u4e0d\u8fc7\u521a\u521a\u62b5\u8fbe\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u604d\u7136\u5931\u7b11\u9053\uff1a\u201c\u539f\u6765\u4ed6\u4eec\u6839\u672c\u6ca1\u6709\u4eba\u6765\uff0c\u6211\u4eec\u771f\u7684\u62c5\u5fc3\u5f97\u591a\u4e86\u3002\u201d \u3000\u3000\u5bfb\u601d\u4e4b\u95f4\uff0c\u51dd\u76ee\u5411\u5927\u6bbf\u4e4b\u4e2d\u671b\u53bb\uff0c\u53ea\u89c1\u5341\u591a\u540d\u82b1\u5b50\uff0c\u6b63\u5728\u6bbf\u4e2d\u5fd9\u6765\u5fd9\u53bb\u5f20\u7f57\u7740\u9152\u83dc\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u9053\uff1a\u201c\u4e10\u5e2e\u5b50\u5f1f\uff0c\u4e0d\u8bb2\u7a76\u7a7f\uff0c\u5012\u662f\u8bb2\u7a76\u5403\uff0c\u602a\u4e0d\u5f97\u4ed6\u4eec\u4f1a\u8fd9\u4e48\u7a77\u554a\u2026\u2026\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u8033\u4e2d\u5ffd\u7136\u4f20\u6765\u4e00\u9635\u5306\u5fd9\u7684\u6b65\u5c65\u4e4b\u58f0\u3002 \u3000\u3000\u8f6c\u773c\u671b\u53bb\uff0c\u90a3\u5c71\u95e8\u4e4b\u5916\uff0c\u5df2\u8fc5\u5feb\u5730\u6765\u4e86\u56db\u4f4d\u8001\u4eba\uff0c\u867d\u7136\u662f\u5728\u591c\u8272\u4e4b\u4e2d\uff0c\u4f46\u90a3\u56db\u4f4d\u8001\u4eba\u7684\u773c\u795e\uff0c\u5374\u4f9d\u7136\u5728\u706f\u5149\u7167\u5c04\u4e4b\u4eba\uff0c\u95ea\u95ea\u751f\u8f89\uff0c\u663e\u7136\uff0c\u8fd9\u56db\u4f4d\u8001\u4eba\u90fd\u662f\u8eab\u5177\u7edd\u9876\u5185\u529f\u7684\u9ad8\u624b\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e0d\u8ba4\u8bc6\u8fd9\u56db\u4f4d\u8001\u4eba\u662f\u8c01\uff0c\u6b63\u60f3\u7528\u4f20\u58f0\u4e4b\u6cd5\u5411\u8eab\u7554\u7684\u5b8b\u5b5a\u63a2\u8be2\uff0c\u5b8b\u5b5a\u5df2\u62a2\u5148\u7528\u4f20\u97f3\u5411\u4ed6\u8bf4\u5230\uff1a\u201c\u8001\u5f1f\uff0c\u8fd9\u56db\u4f4d\u8001\u4eba\uff0c\u4e43\u662f\u4e10\u5e2e\u4e4b\u4e2d\u7684\u56db\u5927\u957f\u8001\uff0c\u4eca\u65e5\u5ffd\u7136\u540c\u5728\u8fd9\u6c5d\u5dde\u57ce\u5916\u7684\u5173\u738b\u5e99\u4e2d\u51fa\u73b0\uff0c\u516b\u6210\u662f\u4e10\u5e2e\u7684\u5e2e\u4e3b\u5230\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u6014\uff0c\u4f20\u97f3\u63a5\u9053\uff1a\u201c\u4e10\u5e2e\u5e2e\u4e3b\u662f\u8c01\uff1f\u201d \u3000\u3000\u539f\u6765\u4ed6\u8fde\u4e10\u5e2e\u5e2e\u4e3b\u90fd\u4e0d\u77e5\u9053\u662f\u8c01\u3002 \u3000\u3000\u5b8b\u5b5a\u4f20\u97f3\u9053\uff1a\u201c\u4e10\u5e2e\u5e2e\u4e3b\u59d3\u970d\uff0c\u53eb\u9e23\u98ce\uff0c\u6b66\u6797\u4e4b\u4e2d\uff0c\u5c0a\u79f0\u4ed6\u4e00\u58f0\u5357\u534e\u795e\u4e5e\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u56db\u957f\u8001\uff0c\u4f60\u4e5f\u8ba4\u8bc6\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u6709\u8fc7\u6570\u9762\u4e4b\u7f18\u3002\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\u4e4b\u95f4\uff0c\u90a3\u56db\u4f4d\u957f\u8001\u5df2\u8fdb\u5165\u5927\u6bbf\uff0c\u4eff\u4f5b\u5747\u5df2\u5165\u5b9a\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u4ed6\u4eec\u600e\u7684\u90fd\u4e0d\u795e\u8bdd\uff0c\u8fde\u90a3\u4e9b\u6267\u4e8b\u7684\u5f1f\u5b50\uff0c\u600e\u4e48\u90fd\u5664\u82e5\u5bd2\u8749\u554a\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e10\u5e2e\u7684\u95e8\u89c4\uff0c\u5728\u6b66\u6797\u4e4b\u4e2d\uff0c\u5217\u4e3a\u6700\u6700\u4e25\u5389\uff0c\u957f\u5e7c\u4e4b\u5206\uff0c\u53ef\u662f\u4e1d\u6beb\u5dee\u9519\u4e0d\u5f97\uff0c\u8fd9\u56db\u4f4d\u957f\u8001\u7684\u8eab\u4efd\uff0c\u5728\u5e2e\u4e2d\u4ec5\u6b21\u4e8e\u5e2e\u4e3b\u4e00\u4eba\uff0c\u8fd9\u4e9b\u5f1f\u5b50\u81ea\u7136\u4e0d\u6562\u8bf4\u8bdd\u7684\u4e86\u3002\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u987b\u77e5\u9053\u4e10\u5e2e\u5b50\u5f1f\uff0c\u904d\u5e03\u5929\u4e0b\uff0c\u53f7\u79f0\u6b66\u6797\u7b2c\u4e00\u5927\u5e2e\uff0c\u5982\u662f\u4ed6\u4eec\u95e8\u89c4\u4e0d\u4e25\uff0c\u6b66\u6797\u4e4b\u4e2d\u7684\u5176\u4ed6\u95e8\u6d3e\uff0c\u53c8\u600e\u80fd\u5bb9\u5f97\u4e0b\u4ed6\u4eec\uff1f\u4f55\u51b5\uff0c\u8fd9\u4e9b\u4eba\u65e2\u7136\u5728\u4e5e\u8ba8\u4e4b\u4e2d\uff0c\u4e5f\u5fc5\u6709\u5176\u4ed6\u7684\u539f\u56e0\uff0c\u50cf\u8fd9\u7b49\u4eba\u82e5\u975e\u5168\u51ed\u95e8\u89c4\u6240\u5236\uff0c\u4efb\u7531\u4ed6\u4eec\u80e1\u4e3a\uff0c\u5c82\u4e0d\u662f\u8981\u60f9\u8d77\u5929\u4e0b\u5927\u4e71\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u9053\uff1a\u201c\u8fd9\u5012\u662f\u4e0d\u5047\uff0c\u4e00\u4e2a\u4eba\u6ca1\u51fa\u606f\u5230\u53bb\u8981\u996d\uff0c\u679c\u7136\u662f\u5bb9\u6613\u5e72\u5f97\u51fa\u574f\u4e8b\u6765\u7684\u4e86\uff01\u201d\u4ed6\u5bfb\u601d\u4e4b\u95f4\uff0c\u4e0d\u7981\u63a5\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u8fd9\u4e48\u770b\u6765\uff0c\u8fd9\u4f4d\u4e10\u5e2e\u5e2e\u4e3b\uff0c\u5fc5\u662f\u96c4\u624d\u5927\u7565\u4e4b\u58eb\u4e86\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u970d\u9e23\u98ce\u679c\u7136\u5f53\u5f97\u8fd9\u56db\u5b57\u3002\u201d \u3000\u3000\u4ed6\u4eec\u4e24\u4eba\u8c08\u8bdd\uff0c\u4ecd\u662f\u7528\u7684\u4f20\u97f3\u4e4b\u672f\uff0c\u81ea\u662f\u4e0d\u4f1a\u88ab\u5916\u4eba\u542c\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u65f6\u6307\u7740\u90a3\u56db\u8001\u4e2d\u6709\u4e00\u4f4d\uff0c\u767d\u9aef\u8fc7\u80f8\uff0c\u795e\u60c5\u5341\u5206\u6e05\u9038\u7684\u9ed1\u8863\u8001\u4eba\uff0c\u9053\uff1a\u201c\u5b8b\u8001\u3001\u8fd9\u4f4d\u957f\u8001\u597d\u4e00\u6d3e\u4ed9\u98ce\u9053\u9aa8\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8fd9\u662f\u4e10\u5e2e\u56db\u8001\u4e4b\u9996\uff0c\u4e4c\u8863\u795e\u53df\u5d14\u5927\u516c\uff0c\u5728\u6c5f\u6e56\u4e0a\u53ef\u662f\u5927\u6709\u540d\u671b\u5462\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u53e6\u5916\u4e09\u4f4d\u5462\uff1f\u6bd4\u4ed6\u5dee\u5f97\u5f88\u591a\u5417\uff1f\u201d \u3000\u3000\u5230\u5e95\u4ed6\u5e74\u8f7b\u5f97\u591a\uff0c\u8131\u53e3\u5c31\u95ee\u51fa\u8fd9\u7b49\u5341\u5206\u53ef\u7b11\u7684\u8bdd\u6765\u3002 \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u65e2\u662f\u56db\u8001\uff0c\u5f53\u7136\u76f8\u5dee\u4e0d\u8fdc\u7684\u4e86\uff01\u201d \u3000\u3000\u8bdd\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u90a3\u4f4d\u8eab\u8eaf\u6700\u77ee\uff0c\u5706\u8138\u77ed\u9aef\u7684\u8001\u4eba\uff0c\u53eb\u505a\u77ee\u65b9\u6714\u7fc1\u6606\u4ed1\uff0c\u6b64\u4eba\u4e00\u8eab\u6c14\u529f\uff0c\u4f20\u8bf4\u5f53\u4eca\u4e4b\u4e16\uff0c\u65e0\u4eba\u5339\u654c\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u770b\u4e86\u90a3\u4f4d\u7fc1\u6606\u4ed1\u4e00\u773c\uff0c\u9053\uff1a\u201c\u8fd9\u4f4d\u8001\u4eba\u4e00\u8138\u90fd\u662f\u6ed1\u7a3d\u4e4b\u76f8\uff0c\u60f3\u5fc5\u4e3a\u4eba\u4e5f\u5f88\u6ed1\u7a3d\u7684\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u7fc1\u6606\u4ed1\u7ef0\u53f7\u77ee\u65b9\u6714\uff0c\u6b63\u662f\u56e0\u4e3a\u4ed6\u4e3a\u4eba\u5341\u5206\u6ed1\u7a3d\u7a81\u68af\u4e4b\u6545\uff0c\u8001\u5f1f\u5c06\u6765\u5982\u6709\u673a\u4f1a\uff0c\u5230\u4e0d\u59a8\u8ddf\u8fd9\u4f4d\u77ee\u8001\u4eba\u6253\u6253\u4ea4\u9053\uff0c\u4fdd\u51c6\u4f60\u5927\u4e3a\u5f00\u5fc3\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u665a\u8f88\u751a\u613f\u6709\u6b64\u673a\u7f18\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u8fd9\u65f6\u6307\u7740\u5750\u5728\u53f3\u624b\u7684\u4e24\u4f4d\u8001\u4eba\uff0c\u9053\uff1a\u201c\u90a3\u53f3\u4fa7\u7684\u4e24\u4f4d\u8001\u4eba\uff0c\u8eab\u7740\u77ed\u6253\u7684\u662f\u64d2\u9f99\u624b\u9a86\u5947\uff0c\u9a86\u5947\u8eab\u65c1\u7684\u90a3\u4f4d\u8138\u8272\u9634\u6c89\u7684\u7626\u8001\u4eba\uff0c\u540d\u53eb\u9634\u9633\u624b\u5409\u5a01\uff0c\u6b64\u516c\u4e3a\u4eba\uff0c\u751a\u662f\u673a\u8bc8\uff0c\u662f\u4ee5\u6b66\u6797\u4e2d\u4eba\uff0c\u90fd\u5bf9\u4ed6\u656c\u800c\u8fdc\u4e4b\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u5fc3\u672f\u4e0d\u6b63\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u90a3\u4e5f\u4e0d\u662f\uff0c\u4e0d\u8fc7\uff0c\u53ea\u56e0\u4ed6\u57ce\u5e9c\u592a\u6df1\uff0c\u4eba\u5bb6\u8ddf\u4ed6\u6253\u4ea4\u9053\uff0c\u51b3\u5360\u4e0d\u4e86\u4fbf\u5b9c\uff0c\u6240\u4ee5\u5c31\u6ca1\u6709\u4eba\u6562\u8ddf\u4ed6\u591a\u6240\u4ea4\u5f80\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u6697\u6697\u5730\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u4e00\u4e2a\u4eba\u53ea\u8981\u5fc3\u672f\u4e0d\u574f\uff0c\u7eb5\u7136\u673a\u8bc8\u4e00\u4e9b\uff0c\u90a3\u4e5f\u4e0d\u8db3\u4e3a\u8fc7\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8001\u5f1f\u8bf4\u5f97\u56fa\u7136\u4e0d\u9519\uff0c\u4f46\u6b66\u6797\u4e2d\u7684\u670b\u53cb\uff0c\u90fd\u662f\u5fc3\u76f4\u53e3\u5feb\u7684\u70ed\u8840\u6c49\u5b50\uff0c\u9047\u5230\u8fd9\u7b49\u5fc3\u673a\u8fc7\u4eba\u4e4b\u5f92\uff0c\u4ed6\u4eec\u907f\u800c\u8fdc\u4e4b\uff0c\u90a3\u4e5f\u662f\u5f88\u81ea\u7136\u7684\u4e8b\u2026\u2026\u201d \u3000\u3000\u4f59\u97f3\u672a\u5df2\uff0c\u53ea\u89c1\u6253\u90a3\u5c71\u95e8\u4e4b\u5916\uff0c\u8fc5\u5feb\u5730\u8fdb\u6765\u4e00\u540d\u5f6a\u5f62\u5927\u6c49\uff0c\u76f4\u8d8b\u56db\u8001\uff0c\u62b1\u62f3\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u5e2e\u4e3b\u9a7e\u5230\uff01\u201d \u3000\u3000\u9759\u5750\u4e2d\u7684\u56db\u4f4d\u957f\u8001\uff0c\u5ffd\u7136\u4e00\u8dc3\u800c\u8d77\uff0c\u5782\u624b\u8083\u7acb\u5728\u539f\u5730\uff0c\u4e00\u52a8\u4e5f\u4e0d\u52a8\u3002 \u3000\u3000\u90a3\u540d\u5f6a\u5f62\u5927\u6c49\uff0c\u8fd9\u65f6\u5374\u7f13\u7f13\u5730\u9000\u5230\u6bbf\u5916\uff0c\u8fde\u90a3\u6bbf\u4e2d\u6267\u4e8b\u7684\u5f1f\u5b50\uff0c\u4e5f\u5168\u90fd\u9000\u5411\u6bbf\u89d2\u4e4b\u5904\uff0c\u5782\u624b\u800c\u7acb\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u4e3e\u76ee\u5411\u5c71\u95e8\u671b\u53bb\uff0c\u53ea\u89c1\u4e00\u4f4d\u8eab\u8eaf\u7626\u957f\uff0c\u795e\u60c5\u751a\u662f\u6d12\u8131\u7684\u4e2d\u5e74\u767d\u8863\u6587\u58eb\uff0c\u5927\u6b65\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u6b64\u4eba\u8fdb\u5165\u6bbf\u4e2d\uff0c\u7acb\u5373\u5411\u56db\u4f4d\u957f\u8001\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6709\u52b3\u56db\u4f4d\u5e08\u53d4\u4e45\u5019\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6014\u5f97\u4e00\u6014\uff0c\u4f20\u97f3\u9053\uff1a\u201c\u600e\u4e48\uff1f\u8fd9\u4f4d\u5e2e\u4e3b\u6bd4\u90a3\u56db\u4f4d\u957f\u8001\u8fd8\u4f4e\u4e0a\u4e00\u8f88\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u8fd9\u4eba\u4e43\u662f\u970d\u545c\u98ce\u7684\u9996\u5ea7\u5927\u5f1f\u5b50\u5c39\u6556\uff0c\u6c5f\u6e56\u4e0a\u79f0\u4ed6\u767e\u8872\u5251\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u9053\uff1a\u201c\u539f\u6765\u4ed6\u4e0d\u662f\u539f\u4e3b\u2026\u2026\u201d \u3000\u3000\u5bfb\u601d\u4e4b\u95f4\uff0c\u53ea\u89c1\u5c71\u95e8\u4e4b\u5916\uff0c\u53c8\u8d70\u6765\u4e00\u540d\u8001\u4eba\u3002 \u3000\u3000\u8fd9\u8001\u4eba\u8eab\u9ad8\u4e03\u5c3a\u5f00\u5916\uff0c\u7a7f\u4e00\u4ef6\u957f\u4ec5\u53ca\u819d\u7684\u957f\u886b\uff0c\u8d64\u7740\u4e00\u53cc\u811a\uff0c\u84ec\u7740\u4e00\u5934\u4e71\u53d1\uff0c\u8138\u4e0a\u4e00\u7247\u98ce\u5c18\u4e4b\u8272\uff0c\u625b\u7740\u4e00\u6839\u9752\u8272\u7af9\u6756\uff0c\u5feb\u6b65\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u5c39\u6556\u8f6c\u8eab\u8fce\u4e86\u8fc7\u53bb\uff0c\u4f4e\u58f0\u8bf4\u4e86\u4e00\u53e5\u8bdd\uff01 \u3000\u3000\u53ea\u89c1\u90a3\u84ec\u5934\u8001\u4eba\u5475\u5475\u4e00\u7b11\u9053\uff1a\u201c\u77e5\u9053\u4e86\uff01\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u90a3\u56db\u540d\u8001\u4eba\u5df2\u5404\u81ea\u62b1\u62f3\u4e00\u793c\uff0c\u540c\u65f6\u8bf4\u9053\uff1a\u201c\u53c2\u89c1\u5e2e\u4e3b\uff01\u201d \u3000\u3000\u84ec\u5934\u8001\u4eba\u4e00\u6446\u5de6\u624b\uff0c\u9053\uff1a\u201c\u5404\u4f4d\u8f9b\u82e6\u4e86\u3002\u201d \u3000\u3000\u76ee\u5149\u56db\u4e0b\u91cc\u4e00\u8f6c\uff0c\u5ffd\u7136\u5927\u58f0\u9053\uff1a\u201c\u4e2d\u5dde\u5206\u5802\u7684\u5415\u5802\u4e3b\u6765\u4e86\u5417\uff1f\u201d \u3000\u3000\u8bdd\u97f3\u752b\u843d\uff0c\u5374\u89c1\u90a3\u5927\u6bbf\u5de6\u4fa7\u7684\u504f\u95e8\u4e4b\u4e2d\uff0c\u95ea\u51fa\u4e86\u4e00\u4f4d\u4e2d\u5e74\u5927\u6c49\uff0c\u6025\u6b65\u4e0a\u524d\uff0c\u8dea\u62dc\u5728\u5730\uff0c\u9053\uff1a\u201c\u5c5e\u4e0b\u5415\u5764\uff0c\u4e5f\u5df2\u606d\u5019\u5e2e\u4e3b\u5f88\u4e45\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u9053\uff1a\u201c\u539f\u6765\u4e10\u5e2e\u5728\u4e2d\u5dde\u8fd8\u6709\u5206\u5802\u5417\uff1f\u5982\u662f\u4ed6\u4eec\u5404\u5730\u5747\u6709\u5206\u5802\uff0c\u8fd9\u4e10\u5e2e\u4e4b\u5927\uff0c\u679c\u771f\u662f\u79f0\u5f97\u4e0a\u5929\u4e0b\u7b2c\u4e00\u5927\u5e2e\u4e86\uff01\u201d \u3000\u3000\u4ed6\u5bfb\u601d\u4e4b\u9645\uff0c\u53ea\u89c1\u90a3\u4e10\u5e2e\u5e2e\u4e3b\u5357\u534e\u795e\u4e5e\u970d\u9e23\u98ce\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8d77\u6765\u8bf4\u8bdd\uff01\u201d \u3000\u3000\u81ea\u5df1\u5374\u4e00\u77ee\u8eab\uff0c\u76d8\u819d\u5750\u5728\u5f53\u4e2d\u7684\u84b2\u56e2\u4e4b\u4e2d\u3002 \u3000\u3000\u4e24\u65c1\u7684\u56db\u4f4d\u957f\u8001\uff0c\u4e5f\u5750\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5415\u5764\u53c8\u62dc\u4e86\u4e00\u62dc\uff0c\u8fd9\u624d\u7ad9\u8d77\u8eab\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u5c5e\u4e0b\u9075\u547d\u2026\u2026\u201d\u9000\u540e\u4e24\u6b65\uff0c\u5782\u624b\u800c\u7acb\u3002 \u3000 \u7b2c\u5341\u4e5d\u56de\u3000\u5251\u9707\u4e10\u5e2e \u3000 \u3000\u3000\u970d\u9e23\u98ce\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u5415\u5802\u4e3b\u89c1\u5230\u90d1\u5927\u4fa0\u4e86\uff1f\u201d \u3000\u3000\u5415\u5764\u9053\uff1a\u201c\u5c5e\u4e0b\u4e8e\u534a\u6708\u4e4b\u524d\uff0c\u89c1\u8fc7\u4e86\u90d1\u5927\u4fa0\u4e00\u9762\u3002\u201d \u3000\u3000\u970d\u9e23\u98ce\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u90d1\u5927\u4fa0\u53ef\u6709\u4ec0\u4e48\u8bdd\u8981\u4f60\u8f6c\u544a\u672c\u5ea7\uff1f\u201d \u3000\u3000\u5415\u5764\u9053\uff1a\u201c\u90d1\u5927\u4fa0\u8a00\u9053\u5e2e\u4e3b\u5982\u53bb\u5c11\u6797\uff0c\u6700\u597d\u4e0d\u8981\u660e\u5730\u91cc\u4e0e\u90a3\u5929\u9b54\u5973\u76f8\u89c1\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u5ffd\u7136\u53cc\u76ee\u7cbe\u5149\u66b4\u5c04\uff0c\u559d\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u90a3\u90d1\u5927\u4fa0\u53ef\u66fe\u5bf9\u4f60\u8bf4\u51fa\u5176\u4e2d\u9053\u7406\uff1f\u201d \u3000\u3000\u5415\u5764\u6447\u5934\u9053\uff1a\u201c\u6ca1\u6709\uff01\u5f53\u65f6\u5c5e\u4e0b\u4e5f\u66fe\u5411\u90d1\u5927\u4fa0\u8bf7\uff0c\u4e3a\u4f55\u5e2e\u4e3b\u4e0d\u5b9c\u51fa\u9762\u4e0e\u5929\u9b54\u5973\u76f8\u89c1\uff0c\u4f46\u90d1\u5927\u4fa0\u53ea\u662f\u82e6\u7b11\u4e86\u4e00\u7b11\uff0c\u4e0d\u80af\u4f5c\u7b54\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u6c89\u541f\u9053\uff1a\u201c\u4e0d\u5bf9\u554a\uff01\u201d \u3000\u3000\u5415\u5764\u6014\u5f97\u4e00\u6014\uff0c\u9053\uff1a\u201c\u5c5e\u4e0b\u8bf4\u7684\u53e5\u53e5\u5b9e\u8bdd\uff0c\u5e2e\u4e3b\u4e0d\u4fe1\uff0c\u65e5\u540e\u89c1\u5230\u90d1\u5927\u4fa0\u4e4b\u65f6\uff0c\u81ea\u53ef\u67e5\u8be2\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u6447\u5934\u5931\u7b11\u9053\uff1a\u201c\u5415\u5802\u4e3b\uff0c\u672c\u5ea7\u4e0d\u662f\u8bf4\u4f60\u6ca1\u6709\u8bb2\u5b9e\u8bdd\uff0c\u800c\u662f\u89c9\u5f97\uff0c\u4ee5\u90d1\u5927\u521a\u7684\u4e3a\u4eba\uff0c\u4e0d\u5e94\u8be5\u6709\u8bdd\u85cf\u5728\u5fc3\uff0c\u4e0d\u8bf4\u51fa\u6765\uff01\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u9690\u8eab\u6750\u4e0a\u7684\u65b9\u96ea\u5b9c\u4e0d\u7981\u6d51\u8eab\u4e00\u9707\uff0c\u6697\u9053\uff1a\u201c\u539f\u6765\u4ed6\u4eec\u53e3\u4e2d\u7684\u90d1\u5927\u4fa0\uff0c\u679c\u7136\u8bf4\u7684\u662f\u6211\u90a3\u90d1\u5927\u4f2f\u7236\u2026\u2026\u201d \u3000\u3000\u6562\u60c5\uff0c\u5728\u970d\u9e23\u98ce\u95ee\u90a3\u5415\u5764\u53ef\u66fe\u5728\u4e2d\u5dde\u89c1\u5230\u5927\u4fa0\u4e4b\u65f6\uff0c\u4ed6\u5c31\u6709\u4e9b\u513f\u7591\u5fc3\uff0c\u4ed6\u4eec\u6240\u6307\u4e4b\u4eba\uff0c\u4f1a\u662f\u90d1\u5927\u521a\uff0c\u6b64\u523b\u542c\u5f97\u970d\u5e2e\u4e3b\u63d0\u53ca\u90d1\u5927\u521a\u4e4b\u540d\uff0c\u81ea\u662f\u5fc3\u60c5\u5927\u4e3a\u6fc0\u52a8\u7684\u4e86\uff01 \u3000\u3000\u5b8b\u5b5a\u5bf9\u4e8e\u65b9\u96ea\u5b9c\u7684\u6fc0\u52a8\u4e4b\u60c5\uff0c\u77a7\u5f97\u5341\u5206\u660e\u767d\uff0c\u5f53\u4e0b\u5fd9\u7528\u4f20\u97f3\u8bf4\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u7a0d\u5b89\u6bcb\u8e81\uff0c\u5982\u662f\u4f60\u8981\u60f3\u89c1\u90d1\u5927\u4f2f\u7236\uff0c\u6700\u597d\u5f85\u4f1a\u513f\u4e0a\u53bb\u627e\uff0c\u90a3\u5415\u5764\u5c31\u884c\u4e86\uff0c\u6b64\u65f6\u5374\u83ab\u8981\u60ca\u52a8\u4e86\u970d\u9e23\u98ce\uff0c\u522b\u751f\u679d\u8282\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6709\u4e9b\u4e0d\u4ee5\u4e3a\u7136\u5730\u4f20\u97f3\u63a5\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u54b1\u4eec\u4e0d\u80fd\u53bb\u89c1\u89c1\u90a3\u970d\u5e2e\u4e3b\u5462\uff1f\u4ed6\u4e0d\u662f\u4e5f\u5e94\u9080\u800c\u6765\u5c11\u6797\u7684\u5417\uff1f\u201d\u4ed6\u541d\u4e8e\u6b66\u6797\u4e16\u6545\uff0c\u6709\u4e86\u8fd9\u7b49\u60f3\u6cd5\uff0c\u672c\u4e5f\u65e0\u53ef\u539a\u975e\uff0c\u4f46\u5b8b\u5b5a\u5374\u4e0d\u7136\uff0c\u4ed6\u77e5\u9053\u6b66\u6797\u5404\u5927\u5e2e\u6d3e\uff0c\u5747\u6709\u4e00\u6869\u6781\u5927\u7684\u5fcc\u8bb3\uff0c\u90a3\u5c31\u662f\u4ed6\u4eec\u7684\u9996\u8111\u4eba\u7269\uff0c\u5982\u6709\u4ec0\u4e48\u5927\u4e8b\u76f8\u5546\u4e4b\u65f6\uff0c\u5207\u5fcc\u6709\u90a3\u5916\u4eba\u5077\u542c\uff0c\u7eb5\u7136\u662f\u81f3\u4ea4\u597d\u53cb\uff0c\u4e5f\u4e0d\u80fd\u7a0d\u4e8b\u901a\u878d\uff01 \u3000\u3000\u662f\u4ee5\u65b9\u96ea\u5b9c\u8bdd\u97f3\u4e00\u843d\uff0c\u5b8b\u5b5a\u7acb\u5373\u4f20\u97f3\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u54b1\u4eec\u6b64\u4e3e\uff0c\u72af\u4e86\u4ed6\u4eec\u6781\u5927\u7684\u7981\u5fcc\uff0c\u5982\u679c\u6b64\u523b\u73b0\u8eab\u76f8\u89c1\uff0c\u970d\u9e23\u98ce\u5c06\u4e0e\u8001\u673d\u53cd\u8138\u6210\u4ec7\u7684\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u4e86\u4e00\u5446\uff0c\u4f20\u97f3\u9053\uff1a\u201c\u54b1\u4eec\u4e0d\u80fd\u4e0b\u53bb\u4e86\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e0d\u80fd\uff01\u8001\u5f1f\uff0c\u54b1\u4eec\u8fd8\u662f\u8010\u5fc3\u542c\u4e00\u4f1a\u513f\u5427\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u60c5\u6fc0\u52a8\uff0c\u54ea\u91cc\u518d\u80fd\u5b89\u5f97\u4e0b\u5fc3\u6765\uff0c\u7741\u7740\u4e24\u773c\uff0c\u76f4\u76ef\u7740\u90a3\u4f4d\u4e10\u5e2e\u4e2d\u5dde\u5206\u575b\u5415\u5764\uff0c\u4e00\u8111\u888b\u5168\u662f\u60f3\u7740\u90d1\u5927\u4f2f\uff01 \u3000\u3000\u4f46\u5b8b\u5b5a\u5374\u6ce8\u89c6\u7740\u56db\u4f4d\u957f\u8001\u5bf9\u4e8e\u773c\u524d\u4e4b\u4e8b\uff0c\u604d\u82e5\u672a\u95fb\uff0c\u4f9d\u65e7\u95ed\u76ee\u76d8\u5750\u5728\u84b2\u56e2\u4e0a\uff0c\u76f4\u5230\u5415\u5764\u4e5f\u5df2\u9000\u4e0b\uff0c\u56db\u4eba\u624d\u540c\u65f6\u7741\u5f00\u4e86\u53cc\u76ee\u3002 \u3000\u3000\u4e4c\u8863\u795e\u53df\u5d14\u5927\u516c\u5e72\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5e2e\u4e3b\u5bf9\u90d1\u5927\u521a\u4e4b\u8a00\uff0c\u53ef\u662f\u6709\u4e86\u51b3\u5b9a\u4e86\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u5c0f\u5f1f\u5bf9\u90d1\u5927\u521a\u7684\u4f20\u8a00\uff0c\u6df1\u4e3a\u56f0\u60d1\uff0c\u5d14\u5e08\u5144\u6709\u4f55\u9ad8\u89c1\uff0c\u4f55\u4e0d\u8bf4\u5c06\u51fa\u6765\uff1f\u201d \u3000\u3000\u5d14\u5927\u516c\u957f\u957f\u4e00\u53f9\u9053\uff1a\u201c\u5e2e\u4e3b\u624d\u667a\u8fc7\u4eba\uff0c\u5c0f\u5144\u54ea\u91cc\u8fd8\u6709\u4ec0\u4e48\u66f4\u597d\u7684\u4e3b\u610f\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u4e00\u987f\u8bdd\u97f3\uff0c\u6c89\u541f\u4e0d\u8bed\uff01 \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u5e08\u5144\u6709\u8bdd\uff0c\u8bf7\u8bf4\u5143\u59a8\uff01\u201d \u3000\u3000\u5d14\u5927\u516c\u63a5\u9053\uff1a\u201c\u5e2e\u4e3b\u65e2\u77e5\u90d1\u5927\u521a\u4e3a\u4eba\uff0c\u4e00\u5411\u5149\u660e\u78ca\u843d\uff0c\u6b64\u523b\u7adf\u7136\u8981\u5415\u5802\u4e3b\u4f20\u8a00\uff0c\u8bf7\u5e2e\u4e3b\u83ab\u4e0e\u5929\u9b54\u5973\u6b63\u9762\u76f8\u5bf9\uff0c\u5c0f\u5144\u603b\u89c9\u5f97\u5176\u4e2d\u5fc5\u6709\u9053\u7406\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u6b63\u662f\u5c0f\u5f1f\u4e0d\u89e3\u4e4b\u5904\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u5728\u90a3\u9634\u9633\u624b\u5409\u5a01\u8eab\u4e0a\u4e00\u8f6c\uff0c\u7b11\u9053\uff1a\u201c\u5409\u5e08\u5f1f\uff0c\u8d24\u5f1f\u624d\u601d\u654f\u6377\uff0c\u6599\u4e8b\u4e00\u5411\u5982\u795e\uff0c\u90d1\u5927\u521a\u8fd9\u756a\u7528\u5fc3\uff0c\u8d24\u5f1f\u60f3\u5fc5\u6d1e\u7136\u4e8e\u80f8\u4e86\uff01\u201d \u3000\u3000\u5409\u5a01\u795e\u60c5\u51b7\u6f20\u5730\u63a5\u9053\uff1a\u201c\u5c0f\u5f1f\u731c\u4ed6\u4e0d\u5916\u4e24\u79cd\u7528\u5fc3\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u8138\u8272\u5927\u9701\uff0c\u7b11\u9053\uff1a\u201c\u54ea\u4e24\u79cd\u7528\u5fc3\uff1f\u201d \u3000\u3000\u5409\u5a01\u9053\uff1a\u201c\u5176\u4e00\uff0c\u662f\u5929\u9b54\u5973\u6b66\u529f\u5df2\u7136\u9ad8\u51fa\u5e2e\u4e3b\uff0c\u5e2e\u4e3b\u4e0e\u5979\u6b63\u9762\u76f8\u5bf9\uff0c\u96be\u514d\u4e00\u6218\uff0c\u90d1\u5927\u521a\u4e0d\u5fcd\u591a\u5e74\u6545\u4ea4\uff0c\u5728\u4f17\u76ee\u777d\u777d\u4e4b\u4e0b\uff0c\u8d25\u5728\u9b54\u5973\u624b\u4e2d\uff0c\u6545\u800c\u963b\u6b62\u5e2e\u4e3b\u51fa\u9762\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u7709\u5934\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u53e6\u4e00\u79cd\u5462\uff1f\u201d \u3000\u3000\u5409\u5a01\u9053\uff1a\u201c\u9690\u8eab\u4e00\u4fa7\uff0c\u76f8\u673a\u884c\u4e8b\uff0c\u5229\u7528\u7fa4\u96c4\u6bd5\u96c6\u5c11\u6797\u4e4b\u9645\uff0c\u6697\u4e2d\u51fa\u624b\uff0c\u9664\u53bb\u6b64\u9b54\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u5ffd\u7136\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u8d24\u5f1f\u7684\u770b\u6cd5\uff0c\u60f3\u5fc5\u662f\u4e0d\u4f1a\u6709\u9519\u7684\u4e86\uff01\u201d\u8138\u7136\u84e6\u5730\u4e00\u6c89\uff0c\u7b11\u5bb9\u5c3d\u655b\uff0c\u63a5\u9053\uff1a\u201c\u4f46\u90a3\u90d1\u5927\u521a\u5374\u662f\u770b\u9519\u4e86\u4eba\u4e86\uff01\u201d \u3000\u3000\u5409\u5a01\u672c\u662f\u6728\u8bb7\u65e0\u60c5\u7684\u9762\u5b54\uff0c\u8fd9\u65f6\u5ffd\u7136\u73b0\u51fa\u4e00\u4e1d\u6fc0\u52a8\u4e4b\u8272\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5e2e\u4e3b\uff0c\u90a3\u90d1\u5927\u521a\u4e43\u662f\u4e2a\u8840\u6027\u7537\u5b50\uff0c\u4ed6\u5982\u662f\u6ca1\u6709\u4ec0\u4e48\u7279\u522b\u7684\u53d1\u73b0\uff0c\u53ea\u6015\u4e5f\u4e0d\u4f1a\u8981\u90a3\u5415\u5802\u4e3b\u5bf9\u5e2e\u4e3b\u8bf4\u51fa\u8fd9\u7b49\u8bdd\u6765\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u51b7\u7b11\u9053\uff1a\u201c\u5409\u5e08\u5f1f\uff0c\u4f60\u8ba4\u4e3a\u672c\u5ea7\u8be5\u600e\u4e48\u529e\uff1f\u542c\u4fe1\u90a3\u90d1\u5927\u521a\u7684\u529d\u544a\uff0c\u505a\u90a3\u7f29\u5934\u7684\u4e4c\u9f9f\u5417\uff1f\u201d \u3000\u3000\u5409\u5a01\u8fdf\u7591\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u5c0f\u5f1f\u4e0d\u6562\u5984\u4f5c\u4e3b\u5f20\uff0c\u53ea\u662f\uff0c\u5c1a\u671b\u5e2e\u4e3b\u4e3a\u5168\u5e2e\u5b50\u5f1f\u4fdd\u91cd\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u4f3c\u662f\u6014\u4e86\u4e00\u6014\uff0c\u770b\u4e86\u5409\u5a01\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u4f60\u4e5f\u8981\u5b66\u90a3\u90d1\u5927\u521a\uff0c\u9677\u4e3a\u5144\u4e8e\u4e0d\u4e49\u4e4b\u5883\u5417\uff1f\u201d \u3000\u3000\u5409\u5a01\u51b7\u51b7\u5e94\u9053\uff1a\u201c\u5c0f\u5f1f\u4e0d\u6562\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u5ffd\u7136\u4e00\u6325\u624b\u4e2d\u7af9\u6756\uff0c\u5927\u58f0\u9053\uff1a\u201c\u6211\u4e10\u5e2e\u7acb\u8eab\u6b66\u6797\uff0c\u5411\u6765\u5c31\u4ee5\u4e00\u4e2a\u4e49\u5b57\u4e3a\u672c\uff0c\u5982\u662f\u672c\u5ea7\u7adf\u662f\u5f53\u771f\u4e0d\u6562\u660e\u5730\u91cc\u51fa\u9762\uff0c\u5bf9\u4ed8\u90a3\u5929\u9b54\u5973\u4e00\u5e72\u90aa\u6076\u4e4b\u5f92\uff0c\u6b66\u6797\u4e4b\u4e2d\uff0c\u4eca\u540e\u54ea\u91cc\u8fd8\u6709\u672c\u5e2e\u7acb\u8db3\u4e4b\u5730\uff1f\u5927\u4e08\u592b\u751f\u800c\u4f55\u6b22\uff0c\u6b7b\u800c\u4f55\u60e7\uff1f\u4f46\u6c42\u6b7b\u5f97\u5176\u6240\uff0c\u867d\u6b7b\u4e4b\u65e5\uff0c\u5b81\u975e\u72b9\u751f\u4e4b\u5e74\uff1f\u201d\u4e00\u6d3e\u8c6a\u58ee\u4e4b\u6c14\uff0c\u4f7f\u4eba\u4e0d\u6562\u903c\u89c6\uff01 \u3000\u3000\u5d14\u5927\u516c\u767d\u7709\u626c\u4e86\u4e00\u626c\uff0c\u7b11\u9053\uff1a\u201c\u5e2e\u4e3b\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u56de\u5934\u63a5\u9053\uff1a\u201c\u5e08\u5144\u6709\u8bdd\u8981\u8bb2\u5417\uff1f\u201d \u3000\u3000\u5d14\u5927\u516c\u9053\uff1a\u201c\u5e2e\u4e3b\u9002\u95f4\u6240\u8a00\uff0c\u5c0f\u5144\u5341\u5206\u611f\u52a8\uff0c\u4f46\u5e2e\u4e3b\u8eab\u88ad\u4e00\u5e2e\u547d\u8109\uff0c\u4e0d\u53ef\u5f92\u901e\u5339\u592b\u4e4b\u52c7\uff0c\u8fd9\u4e8b\u4ee5\u5c0f\u5144\u611a\u89c1\uff0c\u8fd8\u5f97\u4ed4\u7ec6\u5546\u91cf\u4e00\u756a\u624d\u597d\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5e08\u5144\uff0c\u4f60\u4e5f\u5e2e\u7740\u5409\u5e08\u5f1f\u548c\u90d1\u5927\u521a\u8bf4\u8bdd\u5417\uff1f\u201d \u3000\u3000\u5d14\u5927\u516c\u6447\u5934\u957f\u53f9\u9053\uff1a\u201c\u5e2e\u4e3b\u8bef\u4f1a\u4e86\uff01\u60f3\u90a3\u90d1\u5927\u521a\u4f20\u8a00\u4e4b\u610f\uff0c\u4e43\u662f\u7528\u5fc3\u826f\u82e6\uff0c\u4ed6\u4e0d\u613f\u5bf9\u5415\u5764\u8bf4\u51fa\u539f\u56e0\uff0c\u662f\u4e3a\u4e86\u987e\u5168\u5e2e\u4e3b\u548c\u672c\u5e2e\u7684\u58f0\u671b\uff0c\u5e2e\u4e3b\u5982\u80fd\u5e73\u5fc3\u9759\u6c14\u5730\u60f3\u4e00\u60f3\uff0c\u5176\u4e2d\u9053\u7406\uff0c\u5c31\u4e0d\u96be\u660e\u767d\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u5475\u5475\u4e00\u7b11\u9053\uff1a\u201c\u4e0d\u5fc5\u60f3\u4e86\uff01\u201d\u7b11\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u672c\u5e2e\u4eba\u624d\u6d4e\u6d4e\uff0c\u7d20\u6709\u6b66\u6797\u7b2c\u4e00\u5927\u5e2e\u4e4b\u79f0\uff0c\u5c0f\u5f1f\u7eb5\u7136\u6218\u6b7b\u5728\u5929\u9b54\u5973\u5996\u5251\u4e4b\u4e0b\uff0c\u672c\u5e2e\u4e5f\u4e0d\u6101\u540e\u7ee7\u65e0\u4eba\uff0c\u5e08\u5144\u987e\u8651\u5c0f\u5f1f\u5b89\u5371\uff0c\u90a3\u662f\u591a\u4f59\u7684\u4e86\uff01\u201d \u3000\u3000\u663e\u7136\uff0c\u970d\u9e23\u98ce\u5df2\u7136\u4e0b\u5b9a\u4e86\u51b3\u5fc3\uff0c\u8981\u4e0e\u90a3\u5929\u9b54\u5973\u5168\u529b\u4e00\u62da\uff01 \u3000\u3000\u5d14\u5927\u516c\u8138\u8272\u9eef\u7136\uff0c\u957f\u957f\u5730\u5401\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5e2e\u4e3b\u5df2\u7136\u4e0b\u5b9a\u51b3\u5fc3\u4e86\u5417\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u5e08\u5144\u83ab\u518d\u591a\u529f\uff0c\u5c0f\u5144\u5982\u662f\u4e0d\u80fd\u5728\u5c11\u6797\u624b\u6b7c\u5996\u5973\uff0c\u90a3\u5c31\u662f\u5c0f\u5f1f\u4e3a\u6b66\u6797\u540c\u9053\uff0c\u6210\u4ec1\u53d6\u4e49\u4e4b\u65f6\uff01\u201d \u3000\u3000\u5d14\u5927\u516c\u70b9\u5934\u9053\uff1a\u201c\u5e2e\u4e3b\u65e2\u5df2\u51b3\u5b9a\uff0c\u5c0f\u5144\u5f53\u7136\u4e0d\u4fbf\u591a\u529d\uff0c\u4e0d\u8fc7\uff0c\u5c0f\u5144\u5374\u6709\u4e00\u4e2a\u8bf7\u6c42\uff0c\u5c1a\u671b\u5e2e\u4e3b\u89c1\u5141\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5d14\u5927\u516c\u9053\uff1a\u201c\u5bb9\u5c0f\u5144\u4e0e\u5e2e\u4e3b\u8054\u624b\u4e0e\u90a3\u5996\u5973\u4e00\u640f\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u95fb\u8a00\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u90a3\u600e\u4e48\u53ef\u4ee5\uff1f\u201d \u3000\u3000\u5409\u5a01\u51b7\u7b11\u63a5\u9053\uff1a\u201c\u4e3a\u4f55\u4e0d\u53ef\uff1f\u5e2e\u4e3b\u83ab\u8981\u5fd8\u8bb0\uff0c\u6b64\u6218\u4e0d\u72ec\u5173\u7cfb\u5e2e\u4e3b\u4e2a\u4eba\u58f0\u671b\uff0c\u4e5f\u5173\u7cfb\u5230\u672c\u5e2e\u4eca\u540e\u7684\u58f0\u671b\uff01\u4f55\u51b5\uff0c\u4ee5\u90d1\u5927\u521a\u7684\u4e2a\u6027\uff0c\u5c45\u7136\u4f1a\u529d\u963b\u5e2e\u4e3b\u51fa\u9762\uff0c\u8db3\u89c1\u90a3\u5929\u9b54\u5973\u5df2\u7136\u65e9\u6709\u4e86\u5236\u80dc\u4e4b\u673a\u3002\u201d \u3000\u3000\u970d\u9e23\u98ce\u95fb\u8a00\uff0c\u52c3\u7136\u4f5c\u8272\u9053\uff1a\u201c\u5409\u5e08\u5f1f\uff0c\u4f60\u7109\u77e5\u4e0d\u662f\u90a3\u90d1\u5927\u521a\u5df2\u88ab\u5929\u9b54\u5973\u6deb\u5a01\u6240\u80c1\uff0c\u6545\u610f\u5371\u8a00\u8038\u542c\uff0c\u60d1\u6211\u519b\u5fc3\u5462\uff1f\u201d \u3000\u3000\u5409\u5a01\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u5e2e\u4e3b\u5bf9\u90d1\u5927\u521a\u8d77\u4e86\u7591\u5fc3\u5417\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u672c\u5ea7\u4e0e\u4ed6\u4ea4\u5f80\u6570\u5341\u5e74\uff0c\u591a\u5c11\u6b21\u5200\u4e34\u9888\u5934\u4e4b\u95f4\uff0c\u90d1\u5927\u521a\u4e5f\u672a\u76b1\u8fc7\u4e00\u6b21\u7709\u5934\uff0c\u4e3a\u4f55\u8fd9\u56de\u7adf\u4f1a\u4e3a\u5b50\u4e00\u4e2a\u5973\u4eba\uff0c\u53d8\u5f97\u754f\u7f29\u8d77\u6765\uff1f\u5176\u4e2d\u9053\u7406\uff0c\u5e08\u5f1f\u4e5f\u5f53\u60f3\u60f3\u5427\uff01\u201d \u3000\u3000\u5409\u5a01\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u5c0f\u5f1f\u8ba4\u4e3a\u8fd8\u4e0d\u81f4\u4e8e\uff01\u4e2d\u5dde\u4e09\u4fa0\u867d\u7136\u6b66\u529f\u4e0d\u9ad8\uff0c\u4f46\u4ed6\u4eec\u4fa0\u4e49\u4e4b\u5fc3\uff0c\u5374\u662f\u53e3\u7891\u8f7d\u9053\uff01\u50cf\u90a3\u90d1\u5927\u521a\u4e0d\u4f1a\u8fd9\u7b49\u4e0b\u6d41\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5409\u5e08\u5f1f\uff0c\u4f60\u662f\u5f88\u76f8\u4fe1\u90d1\u5927\u521a\u7684\u4e86\uff01\u201d \u3000\u3000\u5409\u5a01\u9053\uff1a\u201c\u8840\u6027\u6c49\u5b50\uff0c\u5fe0\u4e49\u7537\u513f\uff0c\u81ea\u662f\u5e94\u5f97\u4eba\u4eba\u76f8\u4fe1\u656c\u7231\u554a\u201d \u3000\u3000\u5409\u5a01\u8f6c\u5934\u770b\u4e86\u8eab\u8fb9\u7684\u64d2\u9f99\u624b\u9a86\u5947\u4e00\u773c\uff0c\u63a5\u9053\uff1a\u201c\u5e2e\u4e3b\uff0c\u90d1\u5927\u521a\u4e0e\u6211\u7b49\u76f8\u8bc6\uff0c\u4e43\u662f\u9a86\u5e08\u5144\u5f15\u89c1\uff0c\u5982\u662f\u90d1\u5927\u521a\u4e3a\u4eba\u771f\u6709\u53ef\u7591\u4e4b\u5904\uff0c\u9a86\u5e08\u5144\u5e94\u660e\u660e\u767d\u767d\u2026\u2026\u201d \u3000\u3000\u9a86\u5947\u8fd9\u65f6\u5e94\u58f0\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5409\u5e08\u5f1f\uff0c\u8fd9\u4e9b\u5df2\u662f\u8fc7\u53bb\u7684\u4e8b\u4e86\uff01\u90d1\u5927\u521a\u76ee\u524d\u5982\u4f55\uff0c\u5c0f\u5144\u600e\u4f1a\u77e5\u9053\uff1f\u201d \u3000\u3000\u5409\u5a01\u539f\u610f\uff0c\u662f\u8981\u4ed6\u5e2e\u7740\u8bf4\u51e0\u53e5\u90d1\u5927\u521a\u597d\u8bdd\uff0c\u6765\u529d\u963b\u5e2e\u4e3b\uff0c\u8c01\u6599\u4ed6\u5c45\u7136\u4e00\u53e3\u63a8\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u663e\u5f97\u5706\u6ed1\u5230\u9876\uff01\u987f\u65f6\uff0c\u4f7f\u5f97\u5409\u5a01\u5446\u4e86\u534a\u664c\uff0c\u4e0d\u77e5\u5982\u4f55\u5e94\u5bf9\uff01 \u3000\u3000\u970d\u9e23\u98ce\u5374\u6717\u6717\u7b11\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4f60\u542c\u5230\u4e86\u5427\uff01\u8fde\u9a86\u5e08\u5f1f\u4e5f\u4e0d\u6562\u8bf4\u90a3\u90d1\u5927\u521a\u4e0d\u66fe\u88ab\u5929\u9b54\u5973\u6240\u7b3c\u7edc\u800c\u79f0\u81e3\u88d9\u4e0b\u4e86\uff01\u201d \u3000\u3000\u53e4\u5a01\u51b7\u6f20\u7684\u8138\u8272\uff0c\u8d77\u4e86\u6781\u5927\u7684\u53d8\u5316\uff0c\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u9a86\u5e08\u5144\uff0c\u4f60\u53d8\u5f97\u8fd9\u822c\u5706\u6ed1\uff0c\u771f\u662f\u53eb\u4eba\u96be\u8fc7\u5f97\u5f88\uff01\u201d \u3000\u3000\u9a86\u5947\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5409\u5e08\u5f1f\uff0c\u8bdd\u4e0d\u662f\u8fd9\u4e48\u8bf4\uff0c\u90a3\u90d1\u5927\u521a\u867d\u7136\u662f\u6301\u6b63\u4e0d\u963f\u4e4b\u58eb\uff0c\u4f46\u4e00\u4e2a\u4eba\u5230\u4e86\u9762\u4e34\u751f\u6b7b\u5173\u5934\u4e4b\u65f6\uff0c\u6240\u8c13\u6c14\u8282\u4e24\u5b57\uff0c\u5c31\u5f97\u6253\u4e0a\u4e00\u70b9\u6298\u6263\u4e86\uff01\u201d \u3000\u3000\u5409\u5a01\u53ea\u542c\u5f97\u51b7\u7b11\u8fde\u8fde\u9053\uff1a\u201c\u9a86\u5e08\u5f1f\uff0c\u8fd9\u662f\u4f60\u7684\u770b\u6cd5\u5417\uff1f\u201d \u3000\u3000\u9a86\u5947\u4f3c\u662f\u5e76\u672a\u60f3\u51fa\u5409\u5a01\u8bdd\u4e2d\u522b\u7684\u542b\u610f\uff0c\u70b9\u5934\u7b11\u9053\uff1a\u201c\u611a\u5144\u8ba4\u4e3a\u8fd9\u4e43\u4eba\u60c5\u4e4b\u5e38\u2026\u2026\u201d \u3000\u3000\u5409\u5a01\u9053\uff1a\u201c\u9a86\u5e08\u5144\u7684\u8bdd\uff0c\u5e2e\u4e3b\u662f\u4eb2\u8033\u542c\u5230\u7684\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u542c\u5230\u4e86\uff01\u201d \u3000\u3000\u5409\u5a01\u9661\u7136\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4e10\u5e2e\u81ea\u4ece\u7956\u5e08\u5efa\u7acb\u5e2e\u89c4\u4ee5\u6765\uff0c\u4e00\u5411\u6807\u699c\u7684\uff0c\u4fbf\u662f\u5fe0\u70c8\u3001\u8282\u4e49\u4e4b\u65e8\uff0c\u9002\u624d\u9a86\u5e08\u5144\u7684\u8fd9\u756a\u6d3b\uff0c\u72af\u4e86\u672c\u5e2e\u5927\u5fcc\uff0c\u5e2e\u4e3b\u600e\u53ef\u5bb9\u5fcd\u4e0d\u8a00\u554a\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u9a86\u5947\u72af\u4e86\u4ec0\u4e48\u5927\u5fcc\uff1f\u600e\u5730\u5c0f\u5144\u6ca1\u6709\u542c\u51fa\u6765\uff1f\u201d \u3000\u3000\u5409\u5a01\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u5e2e\u4e3b\u5e08\u5144\u4e0e\u4ed6\u60f3\u6cd5\u5dee\u4e0d\u4e86\u591a\u5c11\uff0c\u81ea\u7136\u662f\u542c\u4e0d\u51fa\u9a86\u5e08\u5144\u8bdd\u4e2d\u7684\u7528\u610f\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u76b1\u7709\u6c89\u541f\u9053\uff1a\u201c\u5409\u5e08\u5f1f\uff0c\u5728\u5c0f\u5144\u9762\u524d\uff0c\u6709\u4ec0\u4e48\u8bdd\uff0c\u4f60\u8fd8\u662f\u76f4\u8bf4\u5730\u624d\u662f\uff01\u201d \u3000\u3000\u5409\u5a01\u9053\uff1a\u201c\u5e2e\u4e3b\u8981\u5c0f\u5f1f\u76f4\u8bf4\uff0c\u5c0f\u5f1f\u81ea\u5f53\u9075\u547d\uff01\u201d \u3000\u3000\u8bed\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u9a86\u5e08\u5144\u8bf4\u8fc7\uff0c\u4e00\u4e2a\u4eba\u5230\u4e86\u9762\u4e34\u751f\u6b7b\u5173\u5934\uff0c\u90a3\u6c14\u8282\u4e24\u5b57\uff0c\u5c31\u8981\u5927\u5927\u5730\u6253\u4e0a\u4e00\u70b9\u6298\u6263\uff0c\u5176\u4e2d\u542b\u84c4\uff0c\u4e0e\u672c\u5e2e\u90a3\u5a01\u6b66\u4e0d\u80fd\u5c48\u7684\u6212\u6761\uff0c\u5c82\u975e\u5927\u4e0d\u548c\u8c10\u5417\uff1f\u518d\u8bf4\uff0c\u8fd9\u4e5f\u663e\u9732\u9a86\u5e08\u5144\u4e00\u65e6\u88ab\u4eba\u6240\u6267\uff0c\u5341\u4e4b\u4e03\u516b\u5c31\u4f1a\u53d8\u8282\u53db\u5e2e\uff0c\u6295\u654c\u4fdd\u547d\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u542c\u5f97\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u9a86\u5947\u600e\u4e48\u4f1a\u53db\u5e2e\uff1f\u8fd9\u2026\u2026\u5c0f\u5144\u4e0d\u6562\u76f8\u4fe1\u2026\u2026\u201d \u3000\u3000\u5409\u5a01\u5ffd\u7136\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8bdd\u662f\u9a86\u5e08\u5144\u4eb2\u53e3\u6240\u8bf4\uff0c\u5e2e\u4e3b\u8fd8\u60f3\u4ee3\u4ed6\u63a9\u8fc7\u9970\u975e\uff0c\u672a\u514d\u592a\u4e0d\u516c\u5e73\u4e86\uff01\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u9a86\u5947\u9661\u7136\u4e00\u8dc3\u800c\u8d77\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u5409\u5e08\u5f1f\uff0c\u4f60\u8fd9\u7b49\u5c45\u5fc3\u6311\u62e8\uff0c\u79bb\u95f4\u5e2e\u4e3b\u4e0e\u9a86\u67d0\u60c5\u5206\uff0c\u4f9d\u8001\u592b\u770b\u6765\uff0c\u4f60\u5df2\u7136\u62a2\u5148\u4e00\u6b65\uff0c\u4f5c\u4e86\u672c\u5e2e\u7684\u53db\u5f92\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u6b63\u5728\u6293\u7740\u84ec\u5934\uff0c\u8fd8\u6ca1\u60f3\u51fa\u600e\u4e48\u5904\u7406\u773c\u524d\u4e4b\u4e8b\uff0c\u5409\u5a01\u4e5f\u5df2\u51b7\u51b7\u4e00\u7b11\uff0c\u6307\u7740\u9a86\u5947\u9053\uff1a\u201c\u9a86\u5e08\u5144\uff0c\u90a3\u4e9b\u8bdd\u4e43\u662f\u4f60\u81ea\u5df1\u8bf4\u51fa\u53e3\u6765\uff0c\u5c0f\u5f1f\u53ea\u4e0d\u8fc7\u628a\u4f60\u7684\u7528\u5fc3\uff0c\u5411\u5e2e\u4e3b\u89e3\u8bf4\uff0c\u600e\u4f1a\u6210\u4e86\u6311\u62e8\u79bb\u95f4\uff1f\u5230\u662f\u9a86\u5e08\u5144\u4e0d\u8bc6\u5927\u4f53\uff0c\u53ea\u77e5\u81ea\u8f9e\u5f15\u89c1\u4e4b\u548e\uff0c\u9f13\u52a8\u5e2e\u4e3b\u901e\u90a3\u5339\u592b\u4e4b\u52c7\uff0c\u629b\u5374\u597d\u53cb\u51c0\u8c0f\u4e4b\u8a00\uff0c\u5982\u6b64\u884c\u5f84\uff0c\u771f\u662f\u6709\u5fc3\u9677\u5e2e\u4e3b\u4e8e\u4e0d\u4e49\u4e0d\u667a\u4e4b\u5730\uff0c\u7a76\u7adf\u8c01\u662f\u53db\u5e2e\u4e4b\u5f92\uff0c\u5c0f\u5f1f\u76f8\u4fe1\u5e2e\u4e3b\u548c\u5d14\u5e08\u5144\u5fc5\u6709\u660e\u9274\u2026\u2026 \u3000\u3000\u4ed6\u4e0d\u4f46\u53e3\u9f7f\u7280\u5229\uff0c\u800c\u4e14\u7406\u7531\u5149\u660e\u6b63\u5927\uff0c\u4e0d\u7531\u5f97\u4e0d\u4f7f\u90a3\u5728\u65c1\u8fb9\u542c\u5230\uff0c\u5927\u611f\u6709\u7406\uff01 \u3000\u3000\u6240\u4ee5\uff0c\u9a86\u5947\u5c1a\u672a\u5f00\u53e3\uff0c\u970d\u9e23\u98ce\u5df2\u7136\u6325\u624b\u5411\u4ed6\u8bf4\u9053\uff1a\u201c\u9a86\u5e08\u5f1f\uff0c\u4f60\u4e0d\u5fc5\u518d\u8bf4\u4e86\uff01\u201d\u56de\u5934\u5411\u5409\u5a01\u5927\u7b11\u63a5\u9053\uff1a\u201c\u5409\u5e08\u5f1f\uff0c\u5c0f\u5144\u5230\u4eca\u5929\u624d\u77e5\u9053\uff0c\u4f60\u4ecd\u662f\u4e00\u4e2a\u5916\u51b7\u5185\u70ed\uff0c\u8c8c\u9177\u5fc3\u5584\u7684\u597d\u5f1f\u5144\uff01\u8fd9\u591a\u5e74\u6765\u5c0f\u5144\u77e5\u4eba\u4e0d\u660e\uff0c\u771f\u662f\u8be5\u6b7b\u5f97\u5f88\u2026\u2026\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u7adf\u7136\u62b1\u62f3\u5411\u5409\u5a01\u4e00\u63d6\uff01 \u3000\u3000\u5e2e\u4e3b\u957f\u63d6\u4e4b\u793c\uff0c\u5bf9\u5409\u5a01\u800c\u8a00\uff0c\u786e\u662f\u6709\u4e9b\u592a\u91cd\uff0c\u4ed6\u8fde\u5fd9\u5355\u819d\u4e00\u66f2\uff0c\u8fd8\u4e86\u4e00\u793c\uff0c\u9053\uff1a \u3000\u3000\u201c\u5e2e\u4e3b\u5e08\u5144\u8fd9\u822c\u62ac\u4e3e\uff0c\u5c0f\u5f1f\u5fc3\u4e2d\u60f6\u6050\u5f97\u5f88\uff01\u201d \u3000\u3000\u970d\u545c\u98ce\u4e00\u7b11\u9053\uff1a\u201c\u4e0d\u662f\u5c0f\u5144\u62ac\u4e3e\u4e0e\u4f60\uff0c\u5b9e\u5728\u662f\u5c0f\u5144\u7ec8\u4e8e\u660e\u767d\u4e86\u5409\u5e08\u5f1f\u7684\u5fc3\u6027\uff0c\u4e0d\u7531\u5f97\u4e0d\u6cb9\u7136\u751f\u51fa\u656c\u4f69\u4e4b\u5fc3\u3002\u201d\u8bdd\u97f3\u4e00\u987f\uff0c\u5411\u9a86\u5947\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u9a86\u5e08\u5f1f\uff0c\u4f60\u8ba4\u4e3a\u90a3\u90d1\u5927\u521a\u679c\u771f\u4f1a\u53d8\u8282\u6295\u5411\u9b54\u5973\u5417\uff1f\u201d \u3000\u3000\u9a86\u5947\u8138\u8272\u4f9d\u7136\u5fff\u5fff\uff0c\u867d\u8bf4\u5e2e\u4e3b\u5e76\u672a\u8d28\u8baf\u4ed6\u662f\u5426\u5728\u751f\u6b7b\u5173\u5934\uff0c\u4f1a\u53d8\u8282\u4e8b\u6545\uff0c\u4f46\u4ed6\u5374\u5927\u58f0\u63a5\u9053\uff1a\u201c\u5e2e\u4e3b\uff0c\u9a86\u5947\u4e0d\u662f\u90d1\u5927\u521a\uff0c\u90d1\u5927\u521a\u53ef\u4ee5\u8d2a\u751f\u4fdd\u547d\uff0c\u6295\u9760\u9b54\u5973\uff0c\u4f46\u5c0f\u5f1f\u5374\u5934\u53ef\u65ad\uff0c\u9aa8\u53ef\u6467\uff0c\u5fe0\u4e8e\u672c\u5e2e\u4e4b\u5fd7\uff0c\u51b3\u4e0d\u53ef\u79fb\u2026\u2026\u201d \u3000\u3000\u970d\u545c\u98ce\u95fb\u8a00\uff0c\u76b1\u7709\u9053\uff1a\u201c\u5c0f\u5144\u77e5\u9053\u4f60\uff01\u4f60\u4e0d\u5fc5\u89e3\u8bf4\u4e86\uff01\u90a3\u90d1\u5927\u521a\u662f\u5426\u5982\u4f60\u6240\u731c\uff0c\u4f1a\u6295\u5411\u9b54\u5973\u5462\uff1f\u201d \u3000\u3000\u9a86\u5947\u95fb\u8a00\uff0c\u62f1\u4e86\u62f1\u624b\u9053\uff1a\u201c\u591a\u8c22\u5e2e\u4e3b\u660e\u89c1\u2026\u2026\u81f3\u4e8e\u90d1\u5927\u521a\u662f\u5426\u4f1a\u53d8\u8282\u6295\u9b54\u4e00\u4e8b\uff0c\u5c0f\u5f1f\u4ee5\u4e3a\u2026\u2026\u4ee5\u4e3a\u4ed6\u2026\u2026\u201d\u4ed6\u5ffd\u7136\u6c89\u541f\uff0c\u4e0d\u5f80\u4e0b\u8bf4\uff01 \u3000\u3000\u6b64\u523b\uff0c\u9690\u8eab\u6811\u4e0a\u7684\u65b9\u96ea\u5b9c\uff0c\u53ea\u611f\u5fc3\u60c5\u6fc0\u52a8\u65e0\u6bd4\uff0c\u82e5\u975e\u5b8b\u5b5a\u4e24\u6b21\u4e09\u756a\u62c9\u4f4f\u4ed6\u4e0d\u653e\uff0c\u65e9\u5728\u9a86\u5947\u7b2c\u4e00\u6b21\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u4ed6\u5c31\u8981\u73b0\u8eab\u800c\u51fa\uff0c\u72e0\u72e0\u5730\u6559\u8bad\u8fd9\u4f4d\u4e10\u5e2e\u957f\u8001\u4e00\u756a\u4e86\uff01\u5f53\u90a3\u9a86\u5947\u5ffd\u800c\u6c89\u541f\u4e0d\u8bed\u4e4b\u9645\uff0c\u65b9\u96ea\u5b9c\u5fcd\u4e0d\u4f4f\u7528\u4f20\u97f3\u4e4b\u672f\uff0c\u5411\u5b8b\u5b5a\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u5982\u662f\u8fd9\u59d3\u9a86\u7684\u518d\u6562\u6709\u534a\u53e5\u8bdd\u539a\u53ca\u6211\u90a3\u5927\u4f2f\u7236\u7684\u8bed\u53e5\uff0c\u665a\u8f88\u662f\u518d\u4e5f\u4e0d\u80fd\u5fcd\u8010\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u542c\u5f97\u76b1\u7709\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u5c0f\u4e0d\u5fcd\u5219\u4e71\u5927\u8c0b\uff0c\u4f60\u4f55\u5fc5\u4e0e\u4e10\u5e2e\u7ed3\u4ec7\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u665a\u8f88\u53ea\u662f\u627e\u90a3\u59d3\u9a86\u7684\u4e00\u4e2a\u4eba\u7406\u8bba\uff0c\u800c\u4e14\uff0c\u4e5f\u4ec5\u4ec5\u662f\u8981\u4e3a\u90d1\u5927\u4f2f\u7236\u6d17\u5237\u6e05\u8a89\uff0c\u600e\u4f1a\u4e0e\u90a3\u4e10\u5e2e\u4e3a\u654c\uff1f\u4f55\u51b5\uff0c\u90a3\u5409\u5a01\u7b49\u4e09\u4f4d\u957f\u8001\u548c\u970d\u5e2e\u4e3b\u672c\u4eba\uff0c\u4e0d\u4e5f\u662f\u8ba4\u4e3a\u59d3\u9a86\u7684\u9519\u4e86\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u6447\u5934\u957f\u53f9\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u53ea\u6015\u4f60\u8fd9\u4e48\u60f3\uff0c\u7ec8\u4f1a\u94f8\u6210\u5927\u9519\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4ec0\u4e48\u5927\u9519\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u6b66\u6797\u5404\u5927\u95e8\u6d3e\uff0c\u5e08\u5144\u5f1f\u4e4b\u95f4\u7eb5\u7136\u6709\u4ec0\u4e48\u4e89\u6267\uff0c\u5411\u4f8b\u662f\u4e0d\u5bb9\u5916\u4eba\u63d2\u624b\u5176\u95f4\uff0c\u8001\u5f1f\u5982\u662f\u8fc7\u95ee\u4e86\u4ed6\u4eec\u5e08\u5144\u5f1f\u4e4b\u95f4\u7684\u4e8b\uff0c\u53ea\u6015\u4ed6\u4eec\u4f1a\u8054\u6210\u4e00\u6c14\uff0c\u5bf9\u4ed8\u4e8e\u4f60\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u76b1\u7709\u9053\uff1a\u201c\u5929\u4e0b\u7b2c\u4e00\u5927\u5e2e\uff0c\u83ab\u975e\u4e5f\u4e0d\u8bb2\u6c42\u4e00\u4e2a\u662f\u975e\u660e\u767d\u5417\uff1f\u665a\u8f88\u6709\u4e9b\u4e0d\u4fe1\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u5ffd\u7136\u957f\u957f\u7684\u53f9\u606f\uff0c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u770b\u6765\u4f60\u662f\u51b3\u5fc3\u8981\u4e3a\u90d1\u5927\u521a\u51fa\u9762\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u9519\uff01\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e0d\u8fc7\u600e\u6837\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5982\u662f\u90a3\u9a86\u5947\u4e0d\u4f5c\u8fdd\u5fc3\u4e4b\u8bba\uff0c\u4e0d\u635f\u90d1\u5927\u4f2f\u7236\u5fe0\u4e49\u4e4b\u540d\uff0c\u665a\u8f88\u5c31\u4e0d\u7528\u73b0\u8eab\u7684\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u82e6\u7b11\u9053\uff1a\u201c\u597d\u5427\uff01\u54b1\u4eec\u5c31\u542c\u90a3\u59d3\u9a86\u7684\u600e\u4e48\u8bb2\u6cd5\u5427\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u970d\u9e23\u98ce\u7b49\u90a3\u9a86\u5947\u534a\u664c\uff0c\u672a\u89c1\u4ed6\u518d\u8bf4\u4ec0\u4e48\uff0c\u4e0d\u7981\u8138\u8272\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u9a86\u5e08\u5f1f\uff0c\u4f60\u5230\u5e95\u4ee5\u4e3a\u90a3\u90d1\u5927\u521a\u7684\u4e3a\u4eba\u5982\u4f55\uff1f\u5feb\u8bf4\uff01\u201d \u3000\u3000\u8bed\u6c14\u5341\u5206\u4e25\u5389\uff0c\u663e\u7136\u5fc3\u4e2d\u5df2\u662f\u4e0d\u5feb\uff01 \u3000\u3000\u9a86\u5947\u76ee\u5149\u5728\u6bbf\u5185\u51e0\u4eba\u8eab\u4e0a\u4e00\u8f6c\uff0c\u795e\u60c5\u751a\u662f\u9547\u5b9a\u5730\u63a5\u9053\uff1a\u201c\u4f9d\u5c0f\u5f1f\u4e4b\u89c1\uff0c\u90d1\u5927\u521a\u4e0d\u5931\u662f\u4e00\u4f4d\u8840\u6027\u597d\u6c49\uff0c\u4f46\u4ed6\u5374\u4ecd\u7136\u672a\u5230\u52d8\u7834\u751f\u6b7b\uff0c\u8d85\u7136\u7269\u5916\u7684\u540d\u5bb6\u5883\u754c\uff0c\u662f\u4ee5\uff0c\u4e00\u65e6\u771f\u88ab\u9b54\u5973\u6240\u6267\uff0c\u5fc5\u7136\u96be\u514d\u6709\u90a3\u8d2a\u751f\u6015\u6b7b\u4e4b\u5fc3\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u95fb\u8a00\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4f60\u4f9d\u65e7\u4e0d\u5927\u76f8\u4fe1\u90d1\u5927\u521a\u5417\uff1f\u201d \u3000\u3000\u9a86\u5947\u9053\uff1a\u201c\u5c0f\u5f1f\u786e\u662f\u4e0d\u6562\u592a\u8fc7\u4fe1\u4efb\u2026\u2026\u201d\u4f59\u97f3\u672a\u7edd\uff0c\u7a81\u7136\u4e00\u58f0\u6012\u559d\u4f20\u6765\uff0c\u53ea\u89c1\u4e00\u9053\u4eba\u5f71\uff0c\u5b9b\u5982\u5929\u795e\u81ea\u7a7a\u800c\u964d\uff0c\u52df\u7136\u51fa\u73b0\u5728\u90a3\u5927\u6bbf\u4e4b\u5185\uff01 \u3000\u3000\u53cc\u8db3\u6cbe\u5730\uff0c\u8fd9\u4eba\u5df2\u6012\u58f0\u559d\u9053\uff1a\u201c\u59d3\u9a86\u7684\uff0c\u4f60\u81ea\u5df1\u8d2a\u6015\u6b7b\u4e0d\u8bf4\uff0c\u5374\u8981\u786c\u8bf4\u90d1\u5927\u4fa0\u4e0d\u53ef\u4fe1\u4efb\uff0c\u4e10\u5e2e\u957f\u8001\u4e4b\u4e2d\uff0c\u5c45\u7136\u6709\u4e86\u4f60\u8fd9\u7b49\u65e0\u803b\u4e4b\u8f88\uff0c\u771f\u6b63\u53eb\u4eba\u5bd2\u5fc3\u5f97\u5f88\uff01\u201d \u3000\u3000\u6bbf\u5185\u7684\u90a3\u4f4d\u4e10\u5e2e\u5e2e\u4e3b\u548c\u51e0\u4f4d\u957f\u8001\uff0c\u4ff1\u662f\u5728\u5927\u60ca\u4e4b\u4e0b\uff0c\u4e3e\u76ee\u671b\u53bb\uff0c\u5374\u89c1\u8fd9\u4f4d\u7a81\u5982\u800c\u6765\u7684\u4eba\u5f71\uff0c\u7adf\u662f\u4e00\u4f4d\u5341\u516b\u4e5d\u5c81\u7684\u5c11\u5e74\u6587\u751f\u6253\u626e\u4eba\u7269\uff0c\u4e0d\u7531\u5f97\u5927\u611f\u610f\u5916\u5730\u6123\u5728\u5f53\u5730\uff01\u6562\u60c5\uff0c\u65b9\u96ea\u5b9c\u7ec8\u4e8e\u5728\u542c\u5230\u9a86\u5947\u8fd9\u756a\u8bdd\u4e4b\u540e\uff0c\u5fcd\u4e0d\u4f4f\u8df3\u5165\u5927\u6bbf\u4e4b\u4e2d\u6765\u4e86\uff01 \u3000\u3000\u9a86\u5947\u5728\u672a\u66fe\u770b\u6e05\u6765\u4eba\u662f\u8c01\u4e4b\u524d\uff0c\u5fc3\u4e2d\u751a\u662f\u60ca\u51db\uff0c\u4f46\u5f53\u4ed6\u770b\u5230\u8fd9\u7ad9\u5728\u81ea\u5df1\u8eab\u524d\u4e0d\u8fdc\u4e4b\u4eba\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e2a\u5341\u516b\u4e5d\u5c81\u7684\u5927\u5a03\u5a03\uff0c\u4e0d\u7981\u60ca\u51db\u4e4b\u5fc3\u5927\u51cf\uff0c\u4ee3\u4e4b\u800c\u8d77\uff0c\u4e43\u662f\u5931\u7b11\u4e4b\u58f0\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u8bdd\u58f0\u752b\u843d\uff0c\u9a86\u5947\u7acb\u5373\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u80c6\u6562\u5077\u95ef\u672c\u5e2e\u957f\u8001\u805a\u4f1a\u7684\u7981\u5730\uff1f \u3000\u3000\u5feb\u5feb\u62a5\u4e0a\u540d\u6765\uff0c\u4e5f\u597d\u9886\u53d7\u672c\u5e2e\u6212\u8d23\uff01\u201d \u3000\u3000\u522b\u77a7\u9a86\u5947\u6597\u4e0d\u8fc7\u5409\u5a01\uff0c\u4f46\u4ed6\u8fd9\u51e0\u53e5\u8bdd\uff0c\u53ef\u662f\u5389\u5bb3\u5f97\u5f88\uff0c\u8f7b\u63cf\u6de1\u5199\u5730\u4e00\u4e3e\uff0c\u5c31\u628a\u65b9\u96ea\u5b9c\u5f53\u4f5c\u4e86\u5168\u5e2e\u4e4b\u654c\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u827a\u9ad8\u4eba\u80c6\u5927\uff0c\u4f55\u51b5\u4ed6\u5df2\u7136\u62b1\u4e86\u7834\u91dc\u6c89\u821f\u4e4b\u5fc3\u800c\u6765\uff0c\u95fb\u8a00\u5374\u662f\u677f\u7740\u8138\uff0c\u559d\u9053\uff1a \u3000\u3000\u201c\u59d3\u9a86\u7684\uff0c\u5728\u4e0b\u53ea\u662f\u4e3a\u4e86\u66ff\u90d1\u5927\u4fa0\u7684\u6e05\u8a89\u800c\u6765\uff0c\u4f60\u7529\u4e0d\u7740\u626f\u4e0a\u4e10\u5e2e\u5168\u4f53\uff01\u201d \u3000\u3000\u539f\u6765\u65b9\u96ea\u5b9c\u5728\u542c\u5230\u5b8b\u5b6a\u6240\u8bf4\u4e4b\u540e\uff0c\u5fc3\u4e2d\u5df2\u7136\u6709\u4e86\u6253\u7b97\uff0c\u662f\u4ee5\u4ed6\u4e00\u542c\u5230\u9a86\u5947\u679c\u771f\u628a\u81ea\u5df1\u5f15\u4e3a\u5192\u95ef\u4e10\u5e2e\u957f\u8001\u805a\u4f1a\u7981\u5730\u7684\u5168\u5e2e\u4e4b\u654c\uff0c\u4e5f\u7acb\u5373\u9488\u950b\u76f8\u5bf9\u5730\u5c06\u6765\u610f\u63ed\u660e\uff0c\u4f7f\u5f97\u9a86\u5947\u7684\u9634\u8c0b\uff0c\u65e0\u6240\u9041\u8ff9\uff01 \u3000\u3000\u4e24\u4eba\u4e00\u95ee\u4e00\u7b54\u4e4b\u9645\uff0c\u4e10\u5e2e\u5e2e\u4e3b\u5df2\u7ecf\u8d77\u8eab\u5927\u6b65\u8d70\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u970d\u9e23\u98ce\u4ed4\u7ec6\u5730\u6253\u91cf\u4e86\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u987f\u4e86\u987f\u624b\u4e2d\u90a3\u6839\u9752\u7af9\u6756\uff0c\u5475\u5475\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u7684\u8eab\u6cd5\u5f88\u5feb\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u89c1\u4ed6\u542b\u7b11\u800c\u8a00\uff0c\u8fde\u5fd9\u62b1\u62f3\u9053\uff1a\u201c\u5e2e\u4e3b\u8fc7\u5956\u4e86\uff01\u96d5\u866b\u5c0f\u6280\uff0c\u600e\u5165\u65b9\u5bb6\u6cd5\u773c\u2026\u2026\u201d \u3000\u3000\u970d\u9e23\u98ce\u542c\u4ed6\u8c08\u5410\u4e0d\u4fd7\uff0c\u76ca\u53d1\u4e0d\u6562\u5927\u610f\uff0c\u5f53\u4e0b\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u770b\u4f60\u4eea\u8868\u4e0d\u51e1\uff0c\u8eab\u624b\u4e0d\u5179\uff0c\u60f3\u5fc5\u662f\u54ea\u4f4d\u9ad8\u4eba\u7684\u5b50\u5f1f\u4e86\uff01\u4e0d\u77e5\u5c0f\u5144\u5f1f\u53ef\u5426\u5c06\u5927\u540d\u5949\u544a\uff1f\u5c0a\u5e08\u4f55\u4eba\uff0c\u4e5f\u8bf7\u4e00\u5e76\u8bf4\u51fa\uff01\u201d \u3000\u3000\u970d\u545c\u98ce\u5728\u65b9\u96ea\u5b9c\u73b0\u8eab\u4e4b\u9645\uff0c\u5c31\u5df2\u77a7\u51fa\u4ed6\u90a3\u98de\u8eab\u63a0\u5730\u7684\u8f7b\u529f\u8eab\u6cd5\uff0c\u4f3c\u662f\u6709\u4e9b\u4e0e\u4f17\u4e0d\u540c\uff0c\u56e0\u4e4b\u7279\u522b\u5c0f\u5fc3\uff0c\u60f3\u4ece\u65b9\u96ea\u5b9c\u7684\u5e08\u95e8\uff0c\u67e5\u51fa\u4ed6\u6765\u6b64\u7528\u610f\uff0c\u662f\u53cb\u662f\u654c\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u65f6\u5374\u62b1\u62f3\u9053\uff1a\u201c\u5728\u4e0b\u59d3\u65b9\uff0c\u8349\u5b57\u96ea\u5b9c\uff0c\u6b66\u6797\u672a\u5b66\u540e\u8fdb\uff0c\u5e2e\u4e3b\u4e0d\u987b\u8fd9\u822c\u5ba2\u6c14\uff01\u201d \u3000\u3000\u59d3\u540d\u4ed6\u867d\u7136\u62a5\u4e86\u51fa\u6765\uff0c\u4f46\u5e08\u95e8\u6765\u5386\uff0c\u5374\u662f\u53ea\u5b57\u672a\u5410\uff0c\u8fd9\u4e0d\u7981\u4f7f\u5f97\u970d\u9e23\u98ce\u6014\u4e86\u4e00\u6014\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u5a03\u513f\u5f88\u6ed1\u5934\u2026\u2026\u201d \u3000\u3000\u5c31\u5728\u970d\u9e23\u98ce\u5bfb\u601d\u4e4b\u95f4\uff0c\u5d14\u5927\u516c\u5ffd\u7136\u5927\u7b11\u9053\uff1a\u201c\u6811\u4e0a\u7684\u670b\u53cb\u662f\u54ea\u4e00\u4f4d\u9ad8\u4eba\uff1f\u4f55\u4e0d\u73b0\u8eab\u4eba\u6bbf\u4e00\u53d9\uff1f\u201d \u3000\u3000\u6562\u60c5\u90a3\u5d14\u5927\u516c\u4e0d\u4f46\u6b66\u529f\u9ad8\u5f3a\uff0c\u4e3a\u4eba\u4e5f\u751a\u662f\u7ec6\u5fc3\uff0c\u65b9\u96ea\u5b9c\u98de\u8eab\u800c\u6765\uff0c\u4ed6\u7acb\u5373\u9ed8\u8fd0\u795e\u529f\uff0c\u6697\u81ea\u5bdf\u770b\u90a3\u6811\u4e0a\u662f\u5426\u5c1a\u6709\u65b9\u96ea\u5b9c\u7684\u540c\u515a\uff0c\u7531\u4e8e\u5b8b\u5b5a\u5e76\u672a\u6ce8\u610f\u5230\u5d14\u5927\u516c\u6709\u6b64\u4e00\u7740\uff0c\u662f\u4ee5\u672a\u7528\u9f9f\u606f\u4e4b\u6cd5\u547c\u606f\uff0c\u867d\u7136\u90a3\u547c\u606f\u4e4b\u97f3\u7ec6\u5c0f\uff0c\u4f46\u4ecd\u7136\u7792\u4e0d\u8fc7\u50cf\u5d14\u5927\u516c\u8fd9\u7b49\u6b66\u6797\u9ad8\u624b\uff01 \u3000\u3000\u5b8b\u5b5a\u542c\u5f97\u5d14\u5927\u516c\u4e4b\u8a00\uff0c\u4e0d\u7981\u5fc3\u4e2d\u4e00\u60ca\uff0c\u4f46\u8fd9\u65f6\u5374\u518d\u4e5f\u4e0d\u80fd\u8d56\u5728\u6811\u4e0a\u4e0d\u4e0b\u6765\u4e86\uff0c\u53ea\u597d\u7eb5\u8eab\u4e00\u7b11\uff0c\u7eb5\u843d\u5927\u6bbf\u4e4b\u4e2d\u3002 \u3000\u3000\u970d\u545c\u98ce\u5728\u5d14\u5927\u516c\u8bf4\u8bdd\u4e4b\u9645\uff0c\u672c\u662f\u4e00\u60ca\uff0c\u4f46\u5b8b\u5b5a\u73b0\u8eab\u4ee5\u540e\uff0c\u4e0d\u7531\u5f97\u53c8\u662f\u4e00\u6014\uff0c\u8131\u53e3\u53eb\u9053\uff1a\u201c\u539f\u6765\u662f\u5b8b\u5144\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u8fde\u5fd9\u62b1\u62f3\u9053\uff1a\u201c\u4e0d\u6562\uff01\u970d\u5e2e\u4e3b\u522b\u6765\u65e0\u6059\uff0c\u5b8b\u67d0\u5192\u5931\u4e86\uff01\u201d \u3000\u3000\u5d14\u5927\u516c\u8fd9\u65f6\u4e5f\u8d70\u4e86\u8fc7\u6765\uff0c\u7b11\u9053\uff1a\u201c\u8001\u5077\u513f\uff0c\u54b1\u4eec\u8fd9\u4e00\u5e2e\u5b50\u4eba\uff0c\u7686\u662f\u7a77\u58eb\uff0c\u4f60\u8981\u5077\uff0c\u4e5f\u72af\u4e0d\u7740\u8bf4\u8bdd\u4e4b\u95f4\u3002\u201d\u5b8b\u5b5a\u8bf4\u9053\uff1a\u201c\u5c0f\u5f1f\u8bf4\u7b11\u4e86\uff01\u201d \u3000\u3000\u53c8\u5411\u5409\u5a01\u3001\u7fc1\u6606\u4ed1\uff0c\u9a86\u5947\u7b49\u4e09\u4eba\u7565\u4e00\u62b1\u62f3\u89c1\u793c\uff01 \u3000\u3000\u970d\u9e23\u98ce\u5fae\u5fae\u4e00\u7b11\uff0c\u6307\u7740\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u8fd9\u4f4d\u8001\u5f1f\uff0c\u53ef\u662f\u4e0e\u4f60\u540c\u6765\u7684\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u6253\u6270\u970d\u5144\u4e4b\u7f6a\uff0c\u5144\u5f1f\u8fd9\u53a2\u613f\u9886\u8d23\u7f5a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\u4e00\u6014\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u8fd8\u8981\u53d7\u7f5a\uff1f\u4e8b\u60c5\u7531\u6211\u73b0\u8eab\u800c\u8d77\uff0c\u600e\u80fd\u8ba9\u4ed6\u4e00\u4eba\u9886\u8d23\u5462\uff1f \u3000\u3000\u52a8\u5ff5\u4e4b\u95f4\uff0c\u53e3\u4e2d\u5927\u58f0\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u8fd9\u4e8b\u7686\u7531\u665a\u8f88\u4e00\u65f6\u4e0d\u5fcd\u5f15\u8d77\uff0c\u5982\u82e5\u970d\u5e2e\u4e3b\u8981\u7f5a\uff0c\u90a3\u4e5f\u5e94\u8be5\u7531\u665a\u8f88\u4e00\u4eba\u9886\u8d23\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u522b\u5fd8\u4e86\uff0c\u53d1\u73b0\u4e10\u5e2e\u6697\u8bb0\uff0c\u51fa\u4e3b\u610f\u6765\u6b64\u7684\uff0c\u53ef\u662f\u8001\u673d\u554a\uff01\u6240\u4ee5\uff0c\u5982\u8981\u9886\u8d23\uff0c\u90a3\u662f\u8f6e\u4e0d\u5230\u4f60\u8001\u5f1f\u7684\u4e86\uff01\u201d \u3000\u3000\u4e00\u8001\u4e00\u5c11\uff0c\u5c45\u7136\u62a2\u7740\u9886\u7f5a\uff0c\u800c\u970d\u9e23\u98ce\u6839\u672c\u5c31\u6ca1\u6709\u8bf4\u51fa\u534a\u4e2a\u8981\u4ed6\u4eec\u53d7\u7f5a\u7684\u5b57\uff0c\u53eb\u4eba\u542c\u6765\uff0c\u672a\u514d\u597d\u7b11\uff01 \u3000\u3000\u5d14\u5927\u516c\u76ee\u5149\u5728\u65b9\u96ea\u5b9c\u8eab\u4e0a\u8f6c\u6765\u8f6c\u53bb\uff0c\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u8fd9\u4f4d\u5c0f\u5144\u5f1f\u662f\u54ea\u4e00\u4f4d\u9ad8\u4eba\u7684\u95e8\u4e0b\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u770b\u4e86\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u201d\u53ea\u56e0\u65b9\u96ea\u5b9c\u9002\u624d\u81ea\u5df1\u5e76\u672a\u62a5\u51fa\u5e08\u95e8\uff0c\u4ed6\u5982\u662f\u4ee3\u4ed6\u8bf4\u4e86\u51fa\u6765\uff0c\u672a\u514d\u6709\u7740\u8d8a\u4fce\u4ee3\u5e96\u4e4b\u5acc\uff0c\u6545\u800c\u8bdd\u5230\u53e3\u8fb9\uff0c\u53c8\u54bd\u4e86\u56de\u53bb\u3002 \u3000\u3000\u5d14\u5927\u516c\u7709\u5934\u4e00\u76b1\uff0c\u970d\u9e23\u98ce\u5df2\u7136\u7b11\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u8fd9\u8d23\u7f5a\u4e24\u5b57\uff0c\u5b8b\u5144\u662f\u4e0d\u5fc5\u518d\u8bf4\u4e86\uff01\u5230\u662f\u8fd9\u4f4d\u65b9\u8001\u5f1f\u7684\u51fa\u8eab\u6765\u5386\uff0c\u5144\u5f1f\u5fc5\u5f97\u5f04\u4e2a\u660e\u767d\uff01\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u90a3\u662f\u8981\u4ed6\u4eec\u975e\u5f97\u8bf4\u660e\u4e0d\u53ef\u4e86\uff01 \u3000\u3000\u5b8b\u5b5a\u5fc3\u4e2d\u660e\u767d\uff0c\u9664\u975e\u4ed6\u77e5\u9053\u4e86\u65b9\u96ea\u5b9c\u7684\u5e08\u95e8\u662f\u8c01\uff0c\u5426\u5219\uff0c\u81ea\u5df1\u4e5f\u8bb8\u4ed6\u4eec\u4e0d\u4fbf\u5982\u4f55\uff0c\u4f46\u5bf9\u65b9\u96ea\u5b9c\u53ea\u6015\u5c31\u4e0d\u4f1a\u5ba2\u6c14\u7684\u4e86\uff01 \u3000\u3000\u5f53\u4e0b\u8861\u91cf\u5f97\u5931\uff0c\u6389\u5934\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u970d\u5e2e\u4e3b\u4e43\u662f\u6b63\u76f4\u4e4b\u4eba\uff0c\u8001\u5f1f\u4f3c\u4e5f\u4e0d\u7528\u592a\u4e8e\u89c1\u5916\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7565\u4e00\u6c89\u541f\uff0c\u9053\uff1a\u201c\u8001\u524d\u8f88\u8bf4\u7684\u662f\uff01\u201d\u5ffd\u7136\u62b1\u62f3\u5411\u970d\u9e23\u98ce\u4e00\u63d6\uff0c\u63a5\u9053\uff1a\u201c\u5148\u5e08\u9648\u5e0c\u6b63\uff0c\u60f3\u5fc5\u970d\u5e2e\u4e3b\u4e0d\u4f1a\u89c1\u7591\u5728\u4e0b\u4e86\u5427\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u95fb\u8a00\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5251\u795e\u9648\u5927\u4fa0\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u970d\u5144\uff0c\u4f60\u8be5\u653e\u5fc3\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u76ee\u5149\u5982\u7535\uff0c\u51dd\u5f80\u5728\u65b9\u96ea\u5b9c\u8eab\u4e0a\uff0c\u957f\u957f\u5730\u5401\u4e86\u4e00\u53e3\u5927\u6c14\u3002 \u3000\u3000\u4e10\u5e2e\u56db\u4f4d\u957f\u8001\uff0c\u4e5f\u9f50\u9f50\u89c9\u5f97\u6709\u4e9b\u610f\u5916\uff0c\u4e00\u77ac\u4e0d\u77ac\u5730\u628a\u76ee\u5149\u6295\u6ce8\u5728\u65b9\u96ea\u5b9c\u8eab\u4e0a\uff0c\u6ca1\u6709\u4e00\u4e2a\u4eba\u8bf4\u8bdd\u3002 \u3000\u3000\u5239\u90a3\u4e4b\u95f4\uff0c\u5927\u6bbf\u5185\u9759\u5f97\u4e0d\u95fb\u4e00\u4e1d\u58f0\u606f\uff01 \u3000\u3000\u5b8b\u5b5a\u6123\u6123\u5730\u770b\u4e86\u970d\u9e23\u98ce\u548c\u56db\u4f4d\u957f\u8001\u4e00\u773c\uff0c\u84e6\u5730\u5475\u5475\u5927\u7b11\u9053\uff1a\u201c\u970d\u5144\uff0c\u4f60\u4eec\u53ef\u662f\u4e0d\u4fe1\u5417\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u4f38\u624b\u6293\u7740\u84ec\u53d1\uff0c\u4e00\u987f\u7af9\u6756\uff0c\u6717\u58f0\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u5144\u5f1f\u7b49\u4eba\u53ea\u6709\u89c9\u5f97\u592a\u8fc7\u610f\u5916\uff0c\u54ea\u6709\u4e0d\u4fe1\u4e4b\u7406\uff1f\u201d\u8bed\u97f3\u7565\u987f\uff0c\u76b1\u7709\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u65b9\u8001\u5f1f\uff0c\u4f60\u53e3\u79f0\u5251\u795e\u9648\u5927\u4fa0\u4e3a\u5148\u5e08\uff0c\u83ab\u975e\u9648\u5927\u4fa0\u5df2\u7136\u9a7e\u8fd4\u9053\u5c71\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9eef\u7136\u63a5\u9053\uff1a\u201c\u5148\u5e08\u5df2\u4e8e\u4e24\u6708\u4e4b\u524d\u8c22\u4e16\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u5ffd\u7136\u5927\u559d\u4e00\u58f0\uff0c\u8dcc\u8db3\u957f\u53f9\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u4e0d\u516c\u5e73\u7684\u5929\u9053\u554a\uff01\u50cf\u9648\u5927\u4fa0\u8fd9\u7b49\u6b63\u4eba\u541b\u5b50\uff0c\u4e3a\u4ec0\u4e48\u4e0a\u5929\u5c31\u4e0d\u8ba9\u4ed6\u591a\u6d3b\u51e0\u5e74\u5462\uff1f\u201d \u3000\u3000\u60b2\u6124\u4e4b\u60c5\u6ea2\u4e8e\u8a00\u8877\uff0c\u53ea\u770b\u5f97\u65b9\u96ea\u5b9c\u5927\u4e3a\u611f\u52a8\u3002 \u3000\u3000\u5d14\u5927\u516c\u4e5f\u8fde\u58f0\u53f9\u606f\uff0c\u4f3c\u662f\u60c5\u4e0d\u81ea\u5df1\uff0c\u4f46\u9634\u9633\u624b\u5409\u5a01\u5374\u53cc\u7709\u4e00\u626c\uff0c\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u65b9\u8001\u5f1f\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u56de\u987e\u63a5\u9053\uff1a\u201c\u5409\u8001\u6709\u4ec0\u4e48\u6307\u6559\uff1f\u201d \u3000\u3000\u5409\u5a01\u6c89\u58f0\u9053\uff1a\u201c\u5c0a\u5e08\u9648\u5927\u4fa0\u662f\u600e\u6837\u8fc7\u4e16\u7684\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u4e0d\u6127\u9634\u9633\u624b\u4e4b\u540d\uff0c\u4ed6\u80fd\u5728\u8fd9\u7b49\u65f6\u523b\uff0c\u7559\u5fc3\u5230\u5e08\u7236\u600e\u4e48\u6b7b\u53bb\u4e4b\u4e8b\uff0c\u53ef\u89c1\u5f97\u4ed6\u7684\u4e3a\u4eba\u548c\u5fc3\u601d\u4ed4\u7ec6\u4e4b\u5904\u3002\u201d \u3000\u3000\u8f6c\u5ff5\u4e4b\u95f4\uff0c\u8083\u5bb9\u63a5\u9053\uff1a\u201c\u5148\u5e08\u4e43\u662f\u75c5\u6b7b\u909b\u5ce1\u5c71\u4e2d\uff01\u201d \u3000\u3000\u5409\u5a01\u76b1\u7709\u6c89\u541f\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u53ef\u80fd\u2026\u2026\u201d \u3000\u3000\u970d\u9e23\u98ce\u5374\u662f\u5927\u53eb\u9053\uff1a\u201c\u8fd9\u5c31\u4e0d\u516c\u7406\u4e86\uff01\u50cf\u9648\u5927\u4fa0\u8fd9\u7b49\u6b66\u6797\u602a\u6770\uff0c\u4e00\u4ee3\u5947\u624d\uff0c\u8001\u5929\u600e\u53ef\u8981\u4ed6\u8f97\u8f6c\u75c5\u69bb\uff0c\u62b1\u6068\u4ee5\u7ec8\uff1f\u8001\u5f1f\uff0c\u4f60\u6ca1\u6709\u8bf4\u9519\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5148\u5e08\u8fc7\u4e16\u4e4b\u65f6\uff0c\u5728\u4e0b\u5b88\u5728\u4e00\u65c1\u4eb2\u81ea\u89c6\u6b93\uff0c\u81ea\u7136\u4e0d\u4f1a\u9519\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u5782\u4e0b\u84ec\u9996\uff0c\u5583\u5583\u81ea\u8bed\u9053\uff1a\u201c\u82cd\u5927\u65e0\u773c\uff0c\u53eb\u4eba\u597d\u4e0d\u607c\u6068\u3002\u201d \u3000\u3000\u5409\u5a01\u4e5f\u53cc\u7709\u7d27\u9501\uff0c\u6c89\u541f\u9053\uff1a\u201c\u65b9\u8001\u5f1f\uff0c\u50cf\u5c0a\u5e08\u8fd9\u7b49\u6b66\u529f\u4e4b\u4eba\uff0c\u600e\u4f1a\u88ab\u75c5\u9b54\u903c\u5012\u5e8a\u69bb\u7684\u5462\uff1f\u8fd9\u4e8b\u53eb\u5409\u67d0\u597d\u751f\u4e0d\u89e3\uff0c\u4e5f\u65e0\u6cd5\u76f8\u4fe1\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u957f\u957f\u4e00\u53f9\u9053\uff1a\u201c\u5409\u8001\uff0c\u5148\u5e08\u4e4b\u75c5\uff0c\u7531\u6765\u5df2\u4e45\uff0c\u4e8c\u5341\u5e74\u524d\uff0c\u5c31\u79cd\u4e0b\u7978\u56e0\u4e86\uff01\u201d \u3000\u3000\u5409\u5a01\u9661\u7136\u53cc\u76ee\u4e00\u4eae\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4e8c\u5341\u5e74\u7684\u8001\u75c5\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5148\u5e08\u662f\u8fd9\u4e48\u544a\u8bc9\u5728\u4e0b\u7684\uff01\u201d \u3000\u3000\u5409\u5a01\u70b9\u5934\u9053\uff1a\u201c\u5982\u6b64\u770b\u6765\uff0c\u4ee4\u5e08\u679c\u7136\u4e0d\u662f\u771f\u6b63\u75c5\u6b7b\u7684\u4e86\uff01\u201d \u3000\u3000\u5d14\u5927\u516c\u8138\u8272\u4e00\u6c89\uff0c\u63a5\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u4f60\u8bf4\u9648\u5927\u4fa0\u4e0d\u662f\u75c5\u6b7b\u7684\u5417\uff1f\u201d \u3000\u3000\u5409\u5a01\u9053\uff1a\u201c\u5927\u5e08\u5144\uff0c\u4f11\u8bf4\u5251\u795e\u9648\u5927\u4fa0\u90a3\u7b49\u6b66\u529f\u4e4b\u4eba\uff0c\u5df2\u5230\u767e\u75c5\u96be\u4fb5\u5883\u754c\uff0c\u5c31\u7b97\u5927\u5e08\u5144\u548c\u5c0f\u5f1f\u5427\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u53c8\u4f55\u66fe\u6709\u8fc7\u4e00\u65e5\u4e4b\u75c5\uff1f\u201d \u3000\u3000\u5d14\u5927\u516c\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4efb\u4f55\u75c5\u75db\uff0c\u679c\u7136\u4f24\u5bb3\u4e0d\u4e86\u6211\u4eec\u2026\u2026\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u8fdf\u7591\u4e86\u4e00\u4e0b\uff0c\u63a5\u7740\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u9648\u5927\u4fa0\u7684\u6b7b\u56e0\uff0c\u5fc5\u7136\u522b\u6709\u7f18\u6545\u7684\u4e86\uff1f\u201d \u3000\u3000\u5409\u5a01\u9053\uff1a\u201c\u5982\u662f\u5c0f\u5f1f\u731c\u60f3\u7684\u4e0d\u9519\uff0c\u5251\u795e\u9648\u5927\u4fa0\u7684\u81f4\u6b7b\u4e4b\u56e0\uff0c\u516b\u6210\u662f\u53d7\u4e86\u4ed6\u4eba\u7684\u6697\u7b97\u3002\u201d \u3000\u3000\u970d\u9e23\u98ce\u84e6\u5730\u62ac\u5934\u63a5\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u6697\u7b97\u4e86\u4ed6\uff1f\u201d \u3000\u3000\u5409\u5a01\u76ee\u5149\u8f6c\u5411\u65b9\u96ea\u5b9c\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u53ef\u8981\u95ee\u65b9\u8001\u5f1f\u624d\u80fd\u77e5\u6653\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u76b1\u7709\uff1a\u201c\u665a\u8f88\u4e0d\u77e5\u9053\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u5ffd\u7136\u8138\u8272\u4e00\u6c89\uff0c\u559d\u9053\uff1a\u201c\u4e0d\u77e5\u9053\uff01\u4f60\u600e\u4e48\u80fd\u591f\u8fde\u5e08\u7236\u7684\u6b7b\u56e0\uff0c\u90fd\u4e0d\u77e5\u9053\u5462\uff1f\u201d \u3000\u3000\u4ed6\u8fd9\u7b49\u8d23\u9a82\uff0c\u53eb\u65b9\u96ea\u5b9c\u5927\u4e3a\u5c34\u5c2c\uff01\u5c31\u7406\u800c\u8a00\uff0c\u81ea\u5df1\u8eab\u4e3a\u5f1f\u5b50\u5bf9\u4e8e\u957f\u8005\u7684\u6b7b\u56e0\uff0c\u6b63\u5e94\u8be5\u660e\u767d\u624d\u5bf9\uff01\u4f46\u5e08\u7236\u81ea\u5df1\u4e0d\u8bf4\uff0c\u53eb\u4ed6\u53c8\u6709\u4ec0\u4e48\u529e\u6cd5\u5462\uff1f\u201d \u3000\u3000\u5f53\u4e0b\u9eef\u7136\u53f9\u606f\u9053\uff1a\u201c\u665a\u8f88\u77e5\u9053\u81ea\u5df1\u4e0d\u8be5\uff0c\u4f46\u5148\u5e08\u4e0d\u80af\u900f\u9732\u53e3\u98ce\uff0c\u665a\u8f88\u4e5f\u65e0\u53ef\u5948\u4f55\u2026\u2026\u201d \u3000\u3000\u5d14\u5927\u516c\u5ffd\u7136\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u8be5\u53bb\u67e5\uff01\u5426\u5219\uff0c\u5251\u795e\u53c8\u4f55\u5fc5\u6536\u4f60\u4f5c\u4e3a\u4f20\u4eba\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8083\u5bb9\u9053\uff1a\u201c\u5d14\u8001\u524d\u8f88\uff0c\u665a\u8f88\u8fd9\u756a\u884c\u8d70\u6c5f\u6e56\uff0c\u6b63\u662f\u4e3a\u4e86\u8fd9\u4e8b\uff01\u201d \u3000\u3000\u970d\u5927\u516c\u95fb\u8a00\uff0c\u8138\u8272\u4ee5\u5927\u89c1\u7f13\u5408\uff0c\u70b9\u5934\u9053\uff1a\u201c\u8fd9\u8fd8\u5dee\u4e0d\u591a\uff01\u53ea\u4e0d\u77e5\u53ef\u6709\u4ec0\u4e48\u7709\u76ee\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u5934\u9053\uff1a\u201c\u5c1a\u65e0\u4e1d\u6beb\u8ff9\u8c61\u53ef\u5bfb\u2026\u2026\u201d \u3000\u3000\u5409\u5a01\u5ffd\u7136\u5e72\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u65b9\u8001\u5f1f\uff0c\u5251\u795e\u9648\u5927\u4fa0\u5782\u5371\u4e4b\u9645\uff0c\u53ef\u6709\u4ec0\u4e48\u7279\u522b\u7684\u75c7\u5146\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4f53\u529b\u5927\u51cf\uff0c\u65f6\u800c\u6655\u53a5\uff0c\u5168\u51ed\u4e00\u79cd\u4e39\u4e38\u652f\u6301\uff0c\u624d\u591a\u6d3b\u4e86\u6570\u6708\uff01\u201d \u3000\u3000\u5409\u5a01\u70b9\u5934\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4e86\uff01\u5251\u795e\u6b63\u662f\u53d7\u4e86\u4eba\u5bb6\u7684\u6697\u7b97\uff0c\u4ed7\u7740\u7cbe\u7eaf\u7684\u5185\u529f\u548c\u836f\u529b\uff0c\u7ef4\u6301\u4e86\u4e8c\u5341\u5e74\u7684\u751f\u547d\u2026\u2026\u201d \u3000\u3000\u970d\u9e23\u98ce\u8fd9\u65f6\u4e5f\u6012\u610f\u5c3d\u655b\uff0c\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u65b9\u8001\u5f1f\uff0c\u8001\u592b\u751f\u6027\uff0c\u5411\u6765\u6025\u8e81\uff0c\u9002\u624d\u5192\u72af\u8001\u5f1f\uff0c\u53ef\u522b\u6302\u5728\u5fc3\u4e0a\uff01\u201d \u3000\u3000\u6b64\u4eba\u679c\u7136\u4e0e\u4f17\u4e0d\u540c\uff0c\u662f\u975e\u4e4b\u5ff5\uff0c\u5206\u5f97\u6781\u660e\uff01 \u3000\u3000\u65b9\u96ea\u5b9a\u8eac\u8eab\u8083\u5bb9\u9053\uff1a\u201c\u665a\u8f88\u7406\u4f1a\u5f97\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u5fae\u5fae\u4e00\u7b11\uff0c\u8f6c\u5411\u5b8b\u5b5a\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u4f60\u548c\u65b9\u8001\u5f1f\u540c\u884c\uff0c\u773c\u4e0b\u610f\u6b32\u4f55\u5f80\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u970d\u5144\u53ef\u613f\u731c\u4e0a\u4e00\u731c\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u4e00\u6014\u9053\uff1a\u201c\u8981\u5144\u5f1f\u731c\u5417\uff1f\u90a3\u53ef\u662f\u96be\u5012\u5144\u5f1f\u4e86\u2026\u2026\u201d \u3000\u3000\u5ffd\u7136\u4e00\u6307\u5409\u5a01\uff0c\u63a5\u9053\uff1a\u201c\u6709\u4e86\uff0c\u5144\u5f1f\u8bf7\u5409\u5e08\u5f1f\u4ee3\u731c\u4e00\u756a\u5982\u4f55\uff1f\u201d \u3000\u3000\u5409\u5a01\u95fb\u8a00\uff0c\u672a\u7b49\u5b8b\u5b5a\u8868\u793a\u53ef\u5426\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u4f60\u4eec\u6b64\u884c\uff0c\u5927\u6982\u53ea\u6709\u4e24\u5904\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u54ea\u4e24\u5904\uff1f\u201d \u3000\u3000\u5409\u5a01\u9053\uff1a\u201c\u4e00\u662f\u5d69\u5c71\u5c11\u6797\uff0c\u4e8c\u662f\u4e2d\u5dde\u5927\u4fa0\u90d1\u5927\u521a\u7684\u4f4f\u5904\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u542c\u5f97\u4e00\u6014\uff0c\u9053\uff1a\u201c\u5409\u5144\u4f55\u4ee5\u89c1\u5f97\u54b1\u4eec\u8981\u53bb\u627e\u90a3\u90d1\u5927\u521a\uff1f\u201d \u3000\u3000\u5409\u5a01\u671b\u7740\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u4f4d\u65b9\u8001\u5f1f\u6240\u4ee5\u73b0\u8eab\uff0c\u4e0d\u5c31\u662f\u4e3a\u4e86\u9a86\u5e08\u5144\u5bf9\u90a3\u90d1\u5927\u521a\u53e3\u51fa\u4e0d\u900a\u4e4b\u6545\u5417\uff1f\u5982\u679c\u65b9\u8001\u5f1f\u4e0e\u90a3\u90d1\u5927\u521a\u6beb\u65e0\u7275\u8fde\uff0c\u4ed6\u53c8\u600e\u4f1a\u633a\u8eab\u800c\u51fa\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u7b97\u4f60\u6709\u9053\u7406\uff01\u4e0d\u8fc7\u8001\u673d\u8981\u544a\u8bc9\u4f60\uff0c\u54b1\u4eec\u6b64\u6765\uff0c\u4e3b\u8981\u7684\u8fd8\u662f\u53bb\u4f1a\u4f1a\u90a3\u5929\u9b54\u5973\uff01\u201d \u3000\u3000\u5409\u5a01\u95fb\u8a00\u4e00\u6123\uff0c\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u597d\u51e0\u5e74\u4e0d\u89c1\uff0c\u60f3\u4e0d\u5230\u9601\u4e0b\u7adf\u7136\u53d8\u5f97\u5982\u6b64\u8c6a\u8fc8\u4e86\uff0c\u53eb\u5144\u5f1f\u597d\u751f\u656c\u4f69\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u4e5f\u5927\u7b11\u9053\uff1a\u201c\u662f\u554a\uff01\u5b8b\u5144\u65e2\u6709\u8fd9\u7b49\u8c6a\u6c14\u4f1a\u4f1a\u90a3\u5929\u9b54\u5973\uff0c\u5144\u5f1f\u5219\u66f4\u662f\u4e0d\u80fd\u542c\u90a3\u90d1\u5927\u521a\u4e4b\u529d\uff0c\u754f\u7f29\u4e0d\u524d\u7684\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8981\u4f1a\u5929\u9b54\u5973\u7684\uff0c\u4e0d\u662f\u6211\u5b8b\u5b5a\uff01\u201d \u3000\u3000\u5409\u5a01\u6c89\u58f0\u63a5\u9053\uff1a\u201c\u4e0d\u662f\u5b8b\u5144\uff0c\u90a3\u4f1a\u662f\u8c01\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u662f\u8fd9\u4f4d\u65b9\u8001\u5f1f\uff01\u201d \u3000\u3000\u51e0\u4f4d\u8001\u4eba\u95fb\u8a00\uff0c\u540c\u65f6\u4e00\u6014\u3002 \u3000\u3000\u970d\u9e23\u98ce\u76b1\u7709\u9053\uff1a\u201c\u65b9\u8001\u5f1f\u8981\u89c1\u90a3\u9b54\u5973\u4f5c\u751a\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u5fc3\u4e2d\u6697\u6697\u5931\u7b11\u9053\uff1a\u201c\u8fd9\u8001\u513f\u95ee\u7684\u771f\u53eb\u591a\u4f59\u2026\u2026\u201d \u3000\u3000\u4f46\u4ed6\u4e5f\u660e\u767d\uff0c\u4ed6\u4eec\u5bf9\u65b9\u96ea\u5b9c\u7684\u6b66\u529f\uff0c\u4f30\u91cf\u5f97\u592a\u4f4e\uff0c\u6240\u4ee5\uff0c\u624d\u5728\u4e00\u542c\u5230\u627e\u90a3\u5929\u9b54\u5973\u4e4b\u4eba\u4e43\u662f\u65b9\u96ea\u5b9c\uff0c\u81ea\u7136\u514d\u4e0d\u4e86\u8981\u5403\u4e86\u4e00\u60ca\uff0c\u591a\u6b64\u4e00\u95ee\u4e86\u3002\u5ff5\u52a8\u4e4b\u9645\uff0c\u53e3\u4e2d\u5e94\u9053\uff1a\u201c\u5251\u795e\u5927\u4fa0\u5728\u4e16\u4e4b\u65e5\uff0c\u4e94\u9b54\u4e0d\u6562\u8822\u52a8\uff0c\u800c\u4eca\u5927\u4fa0\u5df2\u7136\u4f5c\u53e4\uff0c\u8fd9\u963b\u6b62\u4e94\u9b54\u4f5c\u6076\u4e3a\u975e\u7684\u5927\u8d23\uff0c\u81ea\u662f\u843d\u5728\u65b9\u8001\u5f1f\u8eab\u4e0a\u4e86\uff01\u201d \u3000\u3000\u5d14\u5927\u516c\u76ee\u5149\u8d2e\u5728\u65b9\u96ea\u5b9c\u8eab\u4e0a\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u8fd9\u4f4d\u8001\u5f1f\u5df2\u5f97\u9648\u5927\u4fa0\u51e0\u5206\u771f\u4f20\uff1f\u201d\u679c\u7136\u5b8b\u5b5a\u6ca1\u6709\u6599\u9519\uff0c\u4ed6\u4eec\u6b63\u662f\u4e0d\u653e\u5fc3\u65b9\u96ea\u5b9c\u7684\u6b66\u529f\u3002 \u3000\u3000\u5b8b\u5b5a\u6de1\u6de1\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u5341\u6210\u5de6\u53f3\uff01\u201d \u3000\u3000\u5d14\u5927\u516c\u542c\u5f97\u4e00\u6014\uff0c\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u8fd9\u7b49\u65f6\u523b\uff0c\u5c82\u662f\u73a9\u7b11\u4e4b\u65f6\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u5144\u5f1f\u51e0\u65f6\u73a9\u7b11\u4e86\uff1f\u65b9\u8001\u5f1f\u4e0d\u4f46\u5df2\u5f97\u5230\u5251\u795e\u9648\u5927\u4fa0\u7684\u5168\u90e8\u771f\u4f20\uff0c\u751a\u81f3\u8fde\u4ed6\u5e08\u7956\u7559\u4e0b\u7684\u6b66\u529f\uff0c\u4e5f\u5df2\u5168\u90e8\u4e60\u5c31\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u5927\u58f0\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u4f60\u8fd9\u8bdd\u53ef\u662f\u771f\u7684\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u5e76\u65e0\u534a\u540d\u865a\u8a00\uff0c\u970d\u5144\u5018\u662f\u4e0d\u4fe1\uff0c\u4e0d\u59a8\u51fa\u624b\u8bd5\u4e0a\u4e00\u8bd5\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u4f3c\u662f\u672a\u66fe\u6599\u5230\u5b8b\u5b5a\u4f1a\u8bf4\u51fa\u8fd9\u7b49\u8bdd\u6765\uff0c\u5f53\u4e0b\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u201d\u663e\u7136\uff0c\u8981\u4ed6\u4ee5\u4e10\u5e2e\u5e2e\u4e3b\u7684\u8eab\u4efd\uff0c\u6765\u4e0e\u65b9\u96ea\u5b9c\u52a8\u624b\uff0c\u5373\u4fbf\u65b9\u96ea\u5b9c\u662f\u5251\u795e\u9648\u5927\u4fa0\u7684\u8863\u94b5\u4f20\u4eba\uff0c\u90a3\u4e5f\u662f\u5341\u5206\u4e0d\u6070\u5f53\u7684\u4e8b\uff0c\u662f\u6545\u4ed6\u65e0\u4ece\u56de\u7b54\u53ef\u5426\u2026\u2026\u3002 \u3000\u3000\u5409\u5a01\u8fd9\u65f6\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u5e2e\u4e3b\u600e\u53ef\u4e0e\u65b9\u5f1f\u52a8\u624b\uff1f\u4f60\u8fd9\u662f\u5f3a\u4eba\u6240\u96be\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u90a3\u5c31\u4e0d\u5fc5\u8bd5\u4e86\uff01\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5404\u4f4d\u4e4b\u4e2d\uff0c\u4e0d\u77e5\u53ef\u6709\u4eba\u4e0e\u90a3\u67f3\u5a9a\u5a18\u52a8\u8fc7\u624b\uff1f\u201d \u3000\u3000\u77ee\u65b9\u6714\u7fc1\u6606\u4ed1\u5ffd\u5730\u54c8\u5927\u7b11\u9053\uff1a\u201c\u5b8b\u8001\u54e5\uff0c\u90a3\u67f3\u5a9a\u5a18\u7684\u6b66\u529f\uff0c\u5144\u5f1f\u5012\u662f\u89c1\u5230\u8fc7\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u80dc\u8d1f\u5982\u4f55\uff1f\u201d \u3000\u3000\u7fc1\u6606\u4ed1\u5927\u7b11\u9053\uff1a\u201c\u529b\u6218\u767e\u62db\uff0c\u8f93\u4e86\u4e00\u5f0f\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u7fc1\u5144\u4e0d\u662f\u5979\u7684\u5bf9\u624b\u4e86\uff01\u201d \u3000\u3000\u7fc1\u6606\u4ed1\u5475\u5475\u7b11\u9053\uff1a\u201c\u5018\u662f\u62fc\u547d\uff0c\u4e5f\u8bb8\u80fd\u591f\u6323\u5f97\u4e2a\u4e24\u8d25\u4ff1\u4f24\uff01\u201d \u3000\u3000\u6b64\u4eba\u8bf4\u8bdd\u751a\u662f\u98ce\u8da3\uff0c\u5f15\u5f97\u5b8b\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u5144\u5f1f\u76f8\u4fe1\u7fc1\u5144\u6709\u6b64\u80fd\u8010\u3002\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5144\u5f1f\u8bf4\u5c06\u51fa\u6765\uff0c\u53ea\u6015\u51e0\u4f4d\u5144\u53f0\u4e0d\u4fe1\uff0c\u5144\u5f1f\u5728\u90a3\u5609\u5b9a\u5e9c\u9644\u8fd1\uff0c\u4eb2\u773c\u5230\u65b9\u8001\u5f1f\u7684\u4e09\u62db\u4e4b\u5185\uff0c\u5c06\u90a3\u67f3\u5a9a\u5a18\u51fb\u8d25\u9041\u53bb\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u9661\u5730\u5927\u7b11\u9053\uff1a\u201c\u8db3\u8bc1\u82f1\u96c4\u51fa\u5c11\u5e74\uff0c\u65b9\u8001\u5f1f\uff0c\u8001\u673d\u4e3a\u4f60\u9ad8\u5174\uff01\u201d\u6562\u60c5\u970d\u545c\u98ce\u5e2e\u4e3b\u662f\u76f8\u4fe1\u4e86\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\u5fd9\u9053\uff1a\u201c\u4e0d\u6562\u5f53\u5f97\u5e2e\u4e3b\u8d5e\u8a89\uff0c\u665a\u8f88\u4e0d\u8fc7\u662f\u4e00\u65f6\u4fa5\u5e78\u800c\u5df2\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u8fd9\u65f6\u63a5\u9053\uff1a\u201c\u970d\u5144\uff0c\u90a3\u9b54\u5200\u7530\u8fdc\u7684\u6b66\u529f\uff0c\u5144\u5f1f\u76f8\u4fe1\u970d\u5144\u4e00\u5b9a\u77e5\u6653\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u70b9\u70b9\u5934\u9053\uff1a\u201c\u9b54\u5200\u7530\u8fdc\u7684\u5200\u6cd5\u4e4b\u5feb\uff0c\u7d20\u6709\u6b66\u6797\u7b2c\u4e00\u5feb\u5200\u4e4b\u8a89\uff0c\u5144\u5f1f\u4e5f\u66fe\u89c1\u8bc6\u8fc7\u7684\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u4ed6\u7a7a\u6709\u7b2c\u4e00\u5feb\u5200\u4e4b\u8a89\u4e86\uff01\u65b9\u5144\u5f1f\u51fa\u624b\u4e24\u5251\uff0c\u5c31\u5c06\u4ed6\u5f00\u80a0\u7834\u809a\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u542c\u5f97\u4e00\u5446\uff0c\u8033\u4e2d\u5374\u542c\u5f97\u5409\u5a01\u6c89\u58f0\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u90a3\u7530\u8fdc\u5df2\u7136\u6b7b\u5728\u65b9\u8001\u5f1f\u7684\u5251\u4e0b\u4e86\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u8001\u592b\u4eb2\u773c\u6240\u89c1\uff0c\u81ea\u7136\u9519\u4e0d\u4e86\u7684\uff01\u201d \u3000\u3000\u5409\u5a01\u957f\u957f\u5730\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u671b\u7740\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u65b9\u8001\u5f1f\u7684\u5251\u6cd5\u5df2\u5f97\u9648\u5927\u4fa0\u7684\u5341\u6210\u771f\u4f20\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u5b8b\u67d0\u51e0\u65f6\u5bf9\u4eba\u8bf4\u8fc7\u5047\u8bdd\u6765\u7740\uff1f\u5409\u5144\u7b49\u5018\u82e5\u4ecd\u7136\u4e0d\u4fe1\uff0c\u4f55\u4e0d\u8054\u624b\u4e00\u8bd5\uff1f\u201d \u3000\u3000\u60f3\u4e0d\u5230\u5b8b\u5b5a\u8bf4\u8bdd\u8d8a\u6765\u8d8a\u53d1\u96be\u542c\uff0c\u8bd5\u60f3\u4ed6\u4eec\u4e4b\u4e2d\u4efb\u8c01\u4e00\u4eba\u51fa\u9762\u8bd5\u62db\uff0c\u90fd\u5df2\u89c9\u5f97\u4e0d\u59a5\uff0c\u4f55\u51b5\u4ed6\u4eec\u8054\u62db\u4e00\u8bd5\uff1f \u3000\u3000\u56db\u4f4d\u8001\u4eba\u8138\u4e0a\u7684\u795e\u60c5\uff0c\u9f50\u9f50\u4e00\u53d8\uff0c\u5fc3\u4e2d\u90a3\u4efd\u7a9d\u56ca\uff0c\u53ef\u771f\u975e\u8a00\u975e\u8bed\u6240\u53ef\u5f62\u5bb9\u3002 \u3000\u3000\u5d14\u5927\u516c\uff0c\u5409\u5a01\u867d\u7136\u8138\u4e0a\u53d8\u8272\uff0c\u4f46\u5374\u5f3a\u81ea\u5fcd\u8010\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u7fc1\u6606\u4ed1\u5219\u5475\u5475\u4e00\u7b11\uff0c\u81ea\u884c\u89e3\u5632\uff0c\u552f\u72ec\u90a3\u64d2\u9f99\u624b\u9a86\u5947\u5ffd\u7136\u5927\u559d\u4e00\u58f0\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u4f60\u8fd9\u822c\u4e00\u518d\u8bbd\u6fc0\u5144\u5f1f\u7b49\u4eba\uff0c\u4e0d\u77e5\u5c45\u5fc3\u4f55\u5728\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u4e0d\u5230\u9ec4\u6cb3\u5fc3\u4e0d\u6b7b\uff0c\u5982\u662f\u4f60\u4eec\u4eb2\u81ea\u51fa\u624b\u4e00\u8bd5\uff0c\u90a3\u80dc\u8fc7\u5144\u5f1f\u5343\u8a00\u4e07\u8bed\u4e86\uff01\u201d \u3000\u3000\u9a86\u5947\u51b7\u51b7\u5730\u770b\u4e86\u5b8b\u5b5a\u4e00\u773c\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u662f\u6709\u5fc3\u8981\u54b1\u4eec\u51fa\u624b\u4e00\u8bd5\u7684\u4e86\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u5144\u5f1f\u51b3\u975e\u6709\u5fc3\u5982\u6b64\uff0c\u53ea\u8981\u51e0\u4f4d\u4fe1\u5f97\u8fc7\u5b8b\u67d0\u4eba\u81ea\u7136\u53ef\u4ee5\u514d\u53bb\u52a8\u624b\u5566\uff01\u201d \u3000\u3000\u9a86\u5947\u5ffd\u7136\u5927\u559d\u4e00\u58f0\u9053\uff1a\u201c\u522b\u4eba\u4fe1\u5f97\u8fc7\u4f60\uff0c\u5144\u5f1f\u5374\u6709\u4e9b\u4e0d\u613f\u2026\u2026\u201d \u3000\u3000\u6389\u5934\u5411\u65b9\u96ea\u5b9c\u51b7\u7b11\u9053\uff1a\u201c\u9a86\u67d0\u4e0d\u624d\uff0c\u5012\u8981\u8bf7\u6559\u4e00\u4e0b\u8001\u5f1f\u7684\u5251\u4e0a\u5de5\u592b\uff01\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5df2\u7136\u6253\u8170\u9645\u53d6\u51fa\u4e00\u6839\u5c3a\u8bb8\u957f\u77ed\u5176\u7c97\u5f84\u5bf8\u7684\u94dc\u68d2\uff0c\u8fce\u98ce\u4e00\u6296\uff0c\u7adf\u7136\u957f\u4e86\u4e09\u500d\u6709\u4f59\uff0c\u53d8\u6210\u4e00\u6839\u94dc\u6756\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u672c\u65e0\u52a8\u624b\u4e4b\u5fc3\uff0c\u8fd9\u65f6\u89c1\u4ed6\u4eae\u51fa\u4e10\u5e2e\u72ec\u95e8\u5175\u5203\u4f38\u7f29\u9752\u94dc\u6253\u72d7\u68d2\uff0c\u4e0d\u7531\u5f97\u5251\u7709\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u9a86\u957f\u8001\uff0c\u665a\u8f88\u6240\u5b66\uff0c\u4e0d\u53ca\u5e08\u7236\u4e07\u4e00\uff0c\u54b1\u4eec\u7528\u4e0d\u7740\u6bd4\u8bd5\u4e86\uff01\u201d \u3000\u3000\u9a86\u5947\u95fb\u8a00\uff0c\u5374\u662f\u4e0d\u4e88\u7406\u4f1a\uff0c\u4e00\u6325\u624b\u4e2d\u9752\u94dc\u68d2\uff0c\u51b7\u51b7\u559d\u9053\uff1a\u201c\u8001\u5f1f\u4e0d\u5fc5\u81ea\u8c26\uff0c\u9a86\u67d0\u4eca\u5929\u4e00\u5b9a\u8981\u9886\u6559\u4e00\u756a\uff01\u201d\u8bdd\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u8001\u5f1f\u8d76\u5feb\u64a4\u5251\uff0c\u9a86\u67d0\u53ef\u8981\u52a8\u624b\uff01\u201d\u94dc\u68d2\u4e00\u626c\uff0c\u5f53\u80f8\u70b9\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u4ed6\u5982\u6b64\u76f8\u903c\u4e8e\u6211\uff0c\u8bf4\u4e0d\u5f97\u53ea\u597d\u8ba9\u4ed6\u4eec\u89c1\u53ea\u89c1\u8bc6\u4e86\uff01\u201d\u5fc3\u5ff5\u4e00\u5b9a\uff0c\u95ea\u8eab\u8ba9\u8fc7\u9a86\u5947\u7684\u653b\u52bf\uff0c\u53f3\u624b\u4e00\u6309\u5251\u67c4\uff0c\u545b\u5577\u4e00\u58f0\uff0c\u957f\u5251\u5df2\u7136\u51fa\u9798\u3002\u4e00\u5251\u5728\u624b\uff0c\u65b9\u96ea\u5b9c\u7684\u6c14\u52bf\u5927\u4e0d\u76f8\u540c\uff01 \u3000\u3000\u53ea\u89c1\u4ed6\u51dd\u795e\u4e00\u5fd7\uff0c\u76ee\u5149\u6ce8\u89c6\u7740\u9a86\u5947\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9a86\u957f\u8001\uff0c\u8bf7\u6055\u665a\u8f88\u653e\u8086\u4e86\uff01\u201d \u3000\u3000\u957f\u5251\u4e00\u4e3e\uff0c\u6676\u5149\u7a81\u73b0\uff0c\u5e73\u76f4\u76f4\u5411\u9a86\u5947\u523a\u53bb\u3002 \u3000\u3000\u9a86\u5947\u653b\u51fa\u4e00\u68d2\u4e4b\u540e\uff0c\u5e76\u672a\u518d\u8fdb\uff0c\u4ed6\u62a2\u5148\u52a8\u624b\u7684\u7528\u5fc3\uff0c\u53ea\u4e0d\u8fc7\u8981\u903c\u5f97\u65b9\u96ea\u5b9c\u62d4\u5251\uff0c\u5bf9\u65b9\u65e2\u5df2\u4eae\u5251\uff0c\u4ee5\u4ed6\u7684\u8eab\u4efd\uff0c\u81ea\u7136\u4e0d\u4fbf\u518d\u62a2\u5360\u5148\u673a\u4e86\u3002 \u3000\u3000\u5bb9\u5f97\u65b9\u96ea\u5b9c\u633a\u5251\u523a\u6765\uff0c\u9a86\u5947\u4e0d\u7981\u6697\u6697\u5931\u7b11\u5fd6\u9053\uff1a\u201c\u8fd9\u822c\u7a00\u677e\u5e73\u5e38\u7684\u5251\u62db\uff0c\u4e5f\u80fd\u4f24\u5f97\u4e86\u9b54\u5200\u7530\u8fdc\uff1f\u663e\u7136\u90a3\u5b8b\u5b5a\u662f\u5728\u80e1\u8bf4\u516b\u9053\u4e86\uff01\u201d \u3000\u3000\u8f6c\u5ff5\u4e4b\u95f4\uff0c\u968f\u624b\u4e00\u632f\u94dc\u68d2\uff0c\u5411\u65b9\u96ea\u5b9c\u5251\u8eab\u649e\u53bb\u3002 \u3000\u3000\u4ed6\u4e00\u5ff5\u8f7b\u654c\uff0c\u7ec8\u4e8e\u62db\u6765\u83ab\u5927\u7684\u7f9e\u8fb1\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u4e00\u62db\u5251\u6cd5\u770b\u6765\u5341\u5206\u5e73\u6de1\u65e0\u5947\uff0c\u4f46\u5305\u542b\u7684\u53d8\u5316\u4e4b\u591a\uff0c\u51b3\u975e\u9a86\u5947\u6240\u80fd\u60f3\u8c61\uff0c\u773c\u89c1\u94dc\u68d2\u5373\u5c06\u51fb\u4e2d\u5251\u8eab\u7684\u5239\u90a3\uff0c\u65b9\u96ea\u5b9c\u9661\u7136\u5251\u52bf\u4e00\u6c89\uff0c\u6781\u5176\u8f7b\u7075\u5730\u4e00\u8f6c\uff0c\u5251\u5c16\u5df2\u7136\u6307\u5411\u9a86\u5947\u80f8\u524d\u534e\u76d6\u7a74\u90e8\u4f4d\u3002 \u3000\u3000\u9a86\u5947\u5fc3\u4e2d\u4e00\u9707\uff0c\u6025\u6025\u7684\u4e00\u5438\u6c14\uff0c\u5012\u9000\u4e00\u6b65\u3002\u4f46\u89c1\u773c\u524d\u54c1\u5149\u66b4\u957f\uff0c\u6f2b\u5929\u5251\u5f71\uff0c\u5239\u90a3\u95f4\u5b9b\u5982\u4e00\u9762\u5de8\u7f51\uff0c\u5c06\u81ea\u5df1\u8eab\u8eaf\uff0c\u56e2\u56e2\u7f69\u4f4f\u3002 \u3000\u3000\u90a3\u9a86\u5947\u5728\u4e10\u90a6\u4e4b\u4e2d\uff0c\u8eab\u4e3a\u957f\u8001\uff0c\u6b66\u529f\u672c\u662f\u4e0d\u51e1\uff0c\u4ed6\u5982\u662f\u5728\u52a8\u624b\u4e4b\u9645\uff0c\u4e0d\u5b58\u8f7b\u654c\u4e4b\u5fc3\uff0c\u4e00\u5f00\u59cb\u4fbf\u80fd\u5168\u529b\u5e94\u6218\uff0c\u65b9\u96ea\u5b9c\u7eb5\u7136\u9ad8\u660e\uff0c\u4e5f\u96be\u4ee5\u4e00\u62db\u5c31\u53ef\u5c06\u4ed6\u51fb\u8d25\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u662f\u5fc3\u751f\u8f7b\u654c\u4e4b\u610f\uff0c\u5c0f\u770b\u4e86\u65b9\u96ea\u5b9c\u7684\u7edd\u4e16\u5251\u6cd5\uff0c\u53ea\u9053\u50cf\u90a3\u822c\u7a00\u677e\u5e73\u5e38\u7684\u5251\u62db\uff0c\u53ea\u8981\u81ea\u5df1\u94dc\u68d2\u4e00\u4e3e\uff0c\u5c31\u53ef\u5c06\u5bf9\u65b9\u957f\u5251\u9707\u5f97\u8131\u624b\u800c\u53bb\u3002\u600e\u6599\u4ed6\u94dc\u68d2\u521a\u521a\u626b\u51fa\uff0c\u65b9\u96ea\u5b9c\u5251\u52bf\u5df2\u53d8\uff0c\u5251\u68d2\u4e00\u9519\u800c\u8fc7\uff0c\u518d\u60f3\u62a2\u5236\u5148\u673a\uff0c\u4e5f\u5df2\u5b8c\u5168\u65e0\u671b\u4e86\u3002 \u3000\u3000\u53ea\u89c9\u5de6\u80f8\u8188\u9aa8\u4e00\u51c9\uff0c\u89c9\u5f97\u5728\u65b9\u96ea\u5b9c\u90a3\u91cd\u91cd\u5251\u5f71\u4e4b\u4e2d\uff0c\u4e0d\u8fa8\u865a\u5b9e\u7684\u6328\u4e86\u4e00\u5251\u3002\u8fd9\u4e00\u5251\u523a\u5f97\u867d\u7136\u4e0d\u6df1\uff0c\u4f46\u5df2\u8863\u88c2\u76ae\u7efd\uff0c\u9c9c\u8840\u6ea2\u51fa\u3002 \u3000\u3000\u4e10\u5e2e\u7684\u51e0\u4f4d\u957f\u8001\uff0c\u5728\u9a86\u5947\u53eb\u6218\u4e4b\u65f6\uff0c\u5305\u62ec\u5e2e\u4e3b\u970d\u9e23\u98ce\u5728\u5185\u5747\u672a\u51fa\u58f0\u963b\u6b62\uff0c\u4ed6\u4eec\u7684\u7528\u5fc3\uff0c\u4e5f\u5c31\u662f\u60f3\u77a7\u77a7\u90a3\u65b9\u96ea\u5b9c\u7a76\u7adf\u6709\u591a\u5c11\u80fd\u8010\u3002 \u3000\u3000\u5f53\u65b9\u96ea\u5b9c\u51fa\u5251\u653b\u51fb\u7684\u5239\u90a3\uff0c\u4ed6\u4eec\u5fc3\u4e2d\uff0c\u4e5f\u6709\u7740\u9a86\u5947\u4e00\u822c\u7684\u60f3\u6cd5\uff0c\u8ba4\u4e3a\u65b9\u96ea\u5b9c\u8fd9\u4e00\u62db\u5251\u6cd5\uff0c\u592a\u8fc7\u5e73\u5e38\uff0c\u5b8b\u5b5a\u4e4b\u8a00\uff0c\u6d6e\u5938\u4e0d\u5b9e\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\u77ac\u773c\u4e4b\u95f4\uff0c\u56db\u4eba\u5374\u53c8\u540c\u65f6\u516b\u76ee\u72c2\u7741\uff0c\u9f50\u9f50\u4e3a\u4e4b\u4e00\u5446\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u7a00\u677e\u7684\u5251\u6cd5\uff0c\u5ffd\u800c\u7ef5\u7ef5\u6d12\u51fa\uff0c\u8fde\u5ff5\u5934\u90fd\u672a\u6765\u5f97\u53ca\u8f6c\uff0c\u9a86\u5947\u5df2\u7136\u8863\u886b\u7834\u88c2\uff0c\u9c9c\u8840\u67d3\u80f8\u3002 \u3000\u3000\u8fd9\u4e00\u5251\uff0c\u53ea\u4f7f\u5f97\u9a86\u5947\u72fc\u72c8\u4e0d\u582a\uff0c\u4e10\u5e2e\u5e2e\u4e3b\u5fc3\u751f\u9a87\u7136\u3002 \u3000\u3000\u5d14\u5927\u516c\u5931\u58f0\u5927\u53eb\u9053\uff1a\u201c\u597d\u5251\u6cd5\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u5251\u5f97\u624b\uff0c\u5e76\u672a\u518d\u8fdb\uff0c\u62b1\u5251\u5f53\u80f8\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u5411\u9a86\u5947\u62f1\u624b\u9053\uff1a\u201c\u5728\u4e0b\u4e00\u65f6\u5931\u624b\uff0c\u8bf7\u6055\u7f6a\uff01\u201d \u3000\u3000\u9a86\u5947\u95ed\u76ee\u4e0d\u8bed\uff0c\u4efb\u7531\u80f8\u524d\u70ed\u8840\u987a\u957f\u886b\u6ef4\u843d\u5730\u4e0a\uff0c\u53cc\u624b\u4e00\u677e\uff0c\u94dc\u68d2\u5f53\u7684\u4e00\u58f0\uff0c\u8dcc\u843d\u9752\u77f3\u5730\u9762\u4e4b\u4e0a\u3002 \u3000\u3000\u970d\u9e23\u98ce\u6b64\u65f6\u5982\u540c\u5728\u5927\u68a6\u4e4b\u4e2d\u60ca\u9192\uff0c\u957f\u957f\u5730\u5401\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5251\u795e\u4f20\u4eba\uff0c\u679c\u7136\u4e0d\u540c\u51e1\u54cd\uff0c\u970d\u67d0\u4eca\u65e5\u65b9\u77e5\uff0c\u9648\u5927\u4fa0\u4e00\u751f\u4ee4\u8a89\uff0c\u51b3\u975e\u5e78\u81f4\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u5fae\u5fae\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u65b9\u8001\u5f1f\u627f\u7ee7\u8863\u94b5\uff0c\u4e0d\u8d1f\u4ee4\u5e08\u58f0\u5a01\uff0c\u773c\u89c1\u6b66\u6797\u4e4b\u4e2d\uff0c\u53c8\u51fa\u5c11\u5e74\u5947\u58eb\uff0c\u8001\u673d\u5fe7\u4e16\u4e4b\u5fc3\uff0c\u4e5f\u8981\u7a0d\u5b89\u4e86\uff01\u201d \u3000\u3000\u4e00\u7247\u8bda\u610f\uff0c\u4f7f\u4eba\u611f\u52a8\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\u8fde\u5fd9\u7b11\u9053\uff1a\u201c\u5e2e\u4e3b\u8fc7\u5956\uff0c\u533a\u533a\u5fc3\u4e2d\u60ed\u6127\u5f97\u5f88\uff01\u201d \u3000\u3000\u5409\u5a01\u4e24\u773c\u4e4b\u4e2d\uff0c\u6cdb\u73b0\u7740\u903c\u4eba\u7684\u5149\u5f69\uff0c\u5ffd\u7136\u4ef0\u5929\u5927\u7b11\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u4f60\u8bf4\u5f97\u4e0d\u9519\uff0c\u90a3\u67f3\u5a9a\u5a18\u80fd\u591f\u5168\u8eab\u800c\u9000\uff0c\u90a3\u53ea\u662f\u65b9\u8001\u5f1f\u4e0d\u66fe\u8d77\u4e0b\u6740\u5979\u4e4b\u5fc3\uff0c\u5426\u5219\uff0c\u53ea\u6015\u5341\u62db\u4e0d\u51fa\uff0c\u9b54\u5973\u5b9a\u5c06\u6388\u9996\u3002\u201d \u3000\u3000\u6253\u4ece\u73b0\u8eab\u8d77\uff0c\u5409\u5a01\u5c31\u5f88\u5c11\u6709\u8fc7\u7b11\u5bb9\uff0c\u8fd9\u65f6\u5ffd\u800c\u5927\u7b11\uff0c\u90a3\u7b49\u8877\u5fc3\u5feb\u6170\u4e4b\u60c5\uff0c\u8d8a\u53d1\u53eb\u4eba\u770b\u6765\u8212\u7545\u3002 \u3000\u3000\u5b8b\u5b5a\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u65b9\u8001\u5f1f\u5982\u679c\u5b58\u4e0b\u8bdb\u6740\u4e4b\u5fc3\uff0c\u90a3\u67f3\u5a9a\u5a18\u81ea\u7136\u662f\u8d70\u4e0d\u8131\u7684\u4e86\uff0c\u4e0d\u8fc7\u2026\u2026\u201d\u4ed6\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u5929\u9b54\u5973\u8f83\u4e4b\u67f3\u5a9a\u5a18\uff0c\u90a3\u53c8\u4e0d\u53ef\u540c\u65e5\u800c\u8bed\u4e86\uff01\u201d \u3000\u3000\u5409\u5a01\u95fb\u8a00\uff0c\u7b11\u5bb9\u5ffd\u655b\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u5144\u5f1f\u4e0d\u4fe1\u5929\u9b54\u5973\u80fd\u6bd4\u67f3\u5a9a\u5a18\u5f3a\u51fa\u591a\u5c11\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u5144\u5f1f\u4e5f\u53ea\u662f\u542c\u4eba\u4f20\u8a00\uff0c\u5929\u9b54\u5973\u5728\u4e94\u9b54\u4e4b\u4e2d\u6700\u4e3a\u96be\u6597\uff0c\u81f3\u4e8e\u662f\u5426\u771f\u4e2a\u8981\u6bd4\u67f3\u5a9a\u5a18\u9ad8\u660e\u591a\u5c11\uff0c\u5144\u5f1f\u4e5f\u4e0d\u6562\u65ad\u8a00\uff01\u53ea\u662f\u2026\u2026\u201d \u3000\u3000\u5409\u5a01\u9053\uff1a\u201c\u53ea\u662f\u600e\u6837\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u53ea\u662f\u90a3\u5929\u9b54\u5973\u65e2\u7136\u4eab\u540d\u5982\u6b64\u4e4b\u76db\uff0c\u60f3\u5fc5\u5b9a\u6709\u90a3\u8fc7\u4eba\u4e4b\u5904\uff01\u201d\u5409\u5a01\u5140\u81ea\u5bf9\u4e8e\u5b8b\u5b5a\u4e4b\u8a00\uff0c\u6709\u4e9b\u4e0d\u6ee1\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u4f60\u4f3c\u4e4e\u88ab\u5929\u9b54\u5973\u5413\u7834\u4e86\u80c6\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u5ffd\u5730\u8138\u4e0a\u5fae\u9732\u5c34\u5c2c\u4e4b\u8272\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u5409\u5144\uff0c\u4f60\u6ca1\u6709\u8ddf\u5979\u78b0\u8fc7\u9762\u5427\uff1f\u201d \u3000\u3000\u5409\u5a01\u51b7\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u81ea\u7136\u662f\u6ca1\u6709\u7684\u4e86\uff01\u4e0d\u7136\uff0c\u8fd9\u4e16\u95f4\u4e4b\u4e0a\uff0c\u6709\u4e86\u5144\u5f1f\uff0c\u5c31\u6ca1\u6709\u4e86\u5929\u9b54\u5973\u4e86\uff01\u201d\u4ed6\u91cd\u65b0\u6062\u590d\u4e86\u90a3\u51b0\u51b7\u7684\u795e\u60c5\uff0c\u8bed\u8a00\u6c89\u5389\uff0c\u4f7f\u4eba\u542c\u6765\uff0c\u4eff\u4f5b\u6709\u4e9b\u4e0d\u5bd2\u9762\u800c\u6817\u3002 \u3000\u3000\u5b8b\u5b5a\u7709\u5934\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u5409\u5144\u5f88\u8c6a\u6c14\u554a\uff01\u201d \u3000\u3000\u5409\u5a01\u9661\u7136\u8138\u8272\u5927\u53d8\uff0c\u6012\u9053\uff1a\u201c\u5b8b\u5144\u83ab\u975e\u4e0d\u60c5\u5144\u5f1f\u6709\u90a3\u7b49\u51b3\u5b9a\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u5ffd\u800c\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u5144\u5f1f\u7b97\u662f\u4fe1\u5f97\u8fc7\u2026\u2026\u201d \u3000\u3000\u663e\u7136\uff0c\u4ed6\u8a00\u72b9\u672a\u5c3d\uff0c\u8a00\u4e0b\u4f9d\u7136\u5145\u6ee1\u4e86\u4e0d\u4fe1\u4e4b\u610f\u3002 \u3000\u3000\u5409\u5a01\u542c\u5f97\u5fc3\u4e2d\u5927\u4e0d\u597d\u53d7\uff0c\u6b63\u5f85\u53d1\u4f5c\uff0c\u970d\u9e23\u98ce\u4e5f\u5df2\u77a7\u51fa\u5409\u5a01\u7684\u7a98\u8feb\u4e4b\u60c5\uff0c\u4ed6\u4e0d\u60f3\u518d\u751f\u679d\u8282\uff0c\u5f53\u4e0b\u5475\u5475\u4e00\u7b11\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u4f60\u4eec\u4f3c\u662f\u7528\u4e0d\u7740\u5728\u6b64\u6597\u53e3\uff0c\u54b1\u4eec\u8fd8\u6709\u5f88\u591a\u91cd\u8981\u4e8b\u4f53\uff0c\u4e9f\u5f85\u5546\u91cf\u5462\uff01\u201d \u3000\u3000\u8bed\u97f3\u5fae\u5fae\u4e00\u987f\uff0c\u53c8\u5411\u5409\u5a01\u7b11\u9053\uff1a\u201c\u5409\u5e08\u5f1f\uff0c\u6709\u4e00\u4ef6\u4e8b\uff0c\u611a\u5144\u8981\u501f\u91cd\u5e08\u5f1f\u7684\u667a\u2026\u2026\u201d \u3000\u3000\u5409\u5a01\u542c\u5f97\u5e2e\u4e3b\u6709\u4e8b\u501f\u91cd\uff0c\u8fde\u5fd9\u8083\u5bb9\u9053\uff1a\u201c\u5e2e\u4e3b\u6709\u4f55\u5dee\u9063\uff0c\u5c0f\u5f1f\u656c\u5019\u6559\u8c15\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u7b11\u9053\uff1a\u201c\u773c\u524d\u5c40\u52bf\uff0c\u6709\u4e86\u65b9\u8001\u5f1f\u8fd9\u7b49\u9ad8\u624b\uff0c\u54b1\u4eec\u53bb\u90a3\u5c11\u6797\u4e4b\u65e5\uff0c\u90d1\u5927\u4fa0\u7684\u4f20\u8a00\uff0c\u4f3c\u4e4e\u4e0d\u7528\u8003\u8651\u7684\u4e86\uff01\u201d \u3000\u3000\u5409\u5a01\u542c\u5f97\u76b1\u7709\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u4ed6\u8fd9\u822c\u6c89\u541f\uff0c\u663e\u7136\u4f9d\u65e7\u6709\u4e9b\u4e0d\u540c\u610f\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u90a3\u5e2e\u7740\u9a86\u5947\u88f9\u4f24\u4e0a\u836f\u7684\u7fc1\u6606\u4ed1\u9661\u5730\u6389\u5934\u6765\uff0c\u5411\u5409\u5a01\u7b11\u9053\uff1a\u201c\u5409\u5e08\u5f1f\uff0c\u5c0f\u5144\u6709\u4e00\u53e5\u8bdd\uff0c\u4e0d\u77e5\u8bf4\u5c06\u51fa\u6765\uff0c\u5e08\u5f1f\u613f\u4e0d\u613f\u542c\uff1f\u201d \u3000\u3000\u5409\u5a01\u9053\uff1a\u201c\u5e08\u5144\u6709\u8bdd\uff0c\u4e0d\u59a8\u89c1\u793a\uff01\u201d \u3000\u3000\u7fc1\u6606\u4ed1\u9053\uff1a\u201c\u5e08\u5f1f\u4f55\u4e0d\u95ee\u95ee\u65b9\u5144\u5f1f\u548c\u90d1\u5927\u4fa0\u7684\u5173\u7cfb\u5462\uff1f\u201d \u3000\u3000\u4ed6\u8fd9\u53e5\u8bdd\uff0c\u5012\u662f\u63d0\u9192\u4e86\u5409\u5a01\uff0c\u65b9\u96ea\u5b9c\u65e2\u662f\u4e3a\u4e86\u90d1\u5927\u521a\u88ab\u9a86\u5e08\u5144\u8a00\u8bcd\u6240\u8fb1\uff0c\u65b9\u59cb\u633a\u8eab\u800c\u6765\uff0c\u53ea\u6015\u4ed6\u4e0e\u90d1\u5927\u521a\u4e4b\u95f4\uff0c\u5fc5\u7136\u6709\u7740\u6781\u5176\u6df1\u539a\u7684\u5173\u7cfb\uff0c\u662f\u4ee5\uff0c\u7fc1\u6606\u4ed1\u8bdd\u97f3\u4e00\u843d\uff0c\u4ed6\u7acb\u5373\u70b9\u5934\u9053\uff1a\u201c\u5e08\u5144\u8bf4\u7684\u662f\uff0c\u5c0f\u5f1f\u679c\u7136\u5ffd\u7565\u4e86\u2026\u2026\u201d \u3000\u3000\u5ffd\u7136\u5411\u65b9\u96ea\u5b9c\u62b1\u62f3\u9053\uff1a\u201c\u65b9\u5144\u5f1f\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u89c1\u4ed6\u795e\u8272\u4e4b\u95f4\uff0c\u4e00\u7247\u8bda\u8c28\uff0c\u8fde\u5fd9\u62b1\u62f3\u8fd8\u793c\uff0c\u7b11\u9053\uff1a\u201c\u8001\u524d\u8f88\u6709\u4f55\u6307\u6559\uff1f\u201d \u3000\u3000\u5409\u5a01\u9053\uff1a\u201c\u60f3\u90a3\u4e2d\u5dde\u4e09\u4fa0\uff0c\u4e43\u4fa0\u4e49\u4e4b\u58eb\uff0c\u65b9\u5144\u5f1f\u4e3a\u4e86\u90d1\u5927\u521a\u4e4b\u4e8b\uff0c\u4e0d\u60dc\u73b0\u8eab\u6426\u6218\uff0c\u60f3\u5fc5\u65b9\u5144\u5f1f\u4e0e\u90a3\u90d1\u5927\u4fa0\u4e4b\u95f4\uff0c\u5fc5\u7136\u6709\u7740\u6781\u4e3a\u6df1\u8fdc\u7684\u6e0a\u6e90\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8083\u5bb9\u63a5\u9053\uff1a\u201c\u4ed6\u4e43\u662f\u533a\u533a\u7684\u5927\u4f2f\u7236\uff01\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u970d\u9e23\u98ce\u5ffd\u7136\u5927\u58f0\u9053\uff1a\u201c\u90d1\u5927\u521a\u662f\u4f60\u8001\u5f1f\u7684\u5927\u4f2f\u7236\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u6014\u5f97\u4e00\u6014\uff0c\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u4f60\u4e0e\u65b9\u4e8c\u4fa0\u65b9\u5929\u6210\u662f\u4e00\u5bb6\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fdf\u7591\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u4ed6\u4e00\u65e6\u542c\u5230\u65b9\u5929\u6210\u4e4b\u540d\uff0c\u8111\u9645\u5c31\u51fa\u73b0\u5728\u90a3\u5609\u5b9a\u57ce\u5916\uff0c\u81ea\u5df1\u9669\u4e9b\u4e27\u547d\u7684\u666f\u8c61\uff0c\u662f\u4ee5\uff0c\u4ed6\u72b9\u8c6b\u4e86\u4e00\u9635\uff0c\u65b9\u59cb\u56de\u7b54\u970d\u9e23\u98ce\u6240\u95ee\u3002 \u3000\u3000\u5409\u5a01\u76ee\u7779\u8fd9\u7b49\u666f\uff0c\u5fc3\u4e2d\u4e0d\u7531\u5f97\u4e00\u52a8\uff0c\u8131\u53e3\u9053\uff1a\u201c\u65b9\u5144\u5f1f\uff0c\u90a3\u4e8c\u4fa0\u662f\u4f60\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u5341\u5206\u4e0d\u613f\u5730\u63a5\u9053\uff1a\u201c\u5927\u4f2f\uff01\u201d \u3000\u3000\u5409\u5a01\u89c1\u4ed6\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u795e\u60c5\u4e0a\u6bd4\u4ed6\u53eb\u51fa\u90d1\u5927\u521a\u662f\u4ed6\u5927\u4f2f\u7236\u4e4b\u65f6\uff0c\u6709\u7740\u6781\u5927\u7684\u4e0d\u540c\uff0c\u5fc3\u4e2d\u66f4\u662f\u7591\u8651\u5927\u8d77\uff0c\u6709\u5fc3\u95ee\u4e2a\u660e\u767d\uff0c\u5374\u53c8\u89c9\u7740\u4e0d\u4fbf\u542f\u9f7f\uff0c\u53ea\u56e0\u4e3a\u8fd9\u4e8b\u5b9e\u662f\u8d8a\u51fa\u4e00\u822c\u4f26\u7406\u5e38\u60c5\uff0c\u4e00\u8a00\u4e0d\u614e\uff0c\u5c31\u8bb8\u62db\u6765\u65e0\u8c13\u7684\u9ebb\u70e6\uff1b\u4f55\u51b5\uff0c\u65b9\u96ea\u5b9c\u65e2\u80fd\u4e3a\u90d1\u5927\u521a\u8fb1\u800c\u633a\u8eab\uff0c\u81ea\u7136\u4e0d\u662f\u4e00\u4e2a\u4e56\u60bb\u4f26\u5e38\u7684\u4e0d\u8096\u4e4b\u5f92\uff0c\u4ed6\u5bf9\u65b9\u5929\u6210\u8868\u793a\u51b7\u6f20\uff0c\u53ea\u6015\u53e6\u6709\u9053\u7406\u2026\u2026 \u3000\u3000\u601d\u5ff5\u4e4b\u95f4\uff0c\u8033\u4e2d\u5ffd\u7136\u542c\u5f97\u970d\u9e23\u98ce\u5927\u7b11\u9053\uff1a\u201c\u65b9\u5144\u5f1f\u4f60\u662f\u65b9\u5929\u6210\u7684\u4f84\u4e8e\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u76b1\u7709\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u663e\u7136\uff0c\u4ed6\u5bf9\u4e8e\u65b9\u5929\u6210\u4e09\u5b57\uff0c\u6709\u7740\u65e0\u6bd4\u7684\u53cd\u611f\uff0c\u6545\u800c\u5bf9\u7b54\u4e4b\u95f4\uff0c\u4e0d\u80af\u591a\u8bf4\u4e00\u8a00\u534a\u8bed\u3002 \u3000\u3000\u5982\u6b64\u51b7\u6de1\u7684\u8868\u60c5\uff0c\u4f7f\u5f97\u970d\u5e2e\u4e3b\u4e3a\u4e4b\u4e00\u5446\u3002 \u3000\u3000\u4f46\u4ed6\u4e5f\u672a\u4fbf\u8be2\u95ee\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u600e\u4e48\u4e86\uff1f\u600e\u5730\u5bf9\u81ea\u5df1\u5927\u4f2f\uff0c\u53cd\u4e0d\u5982\u4e00\u4f4d\u5f02\u59d3\u7684\u4f2f\u7236\u6765\u5f97\u4eb2\u5207\u554a\uff01\u201d \u3000\u3000\u5409\u5a01\u5df2\u7136\u5728\u5fc3\u4e2d\u6709\u4e86\u6253\u7b97\uff0c\u4ed6\u77e5\u9053\u5173\u4e8e\u65b9\u5929\u6210\u4e3a\u4f55\u88ab\u8fd9\u4f4d\u4f84\u513f\u6240\u51b7\u6de1\uff0c\u5fc5\u6709\u5f88\u5927\u7684\u6765\u5386\uff0c\u6025\u5207\u4e4b\u95f4\uff0c\u60f3\u8981\u67e5\u95ee\u660e\u767d\uff0c\u90a3\u4e00\u5b9a\u662f\u5f88\u96be\u7684\u4e86\uff01\u50cf\u8fd9\u7b49\u53d4\u4f84\u95f4\u7684\u6069\u6028\uff0c\u65b9\u96ea\u5b9c\u516b\u6210\u4e5f\u4e0d\u4f1a\u5bf9\u5916\u4eba\u8a00\u660e\uff0c\u4e00\u65e6\u95ee\u5f97\u592a\u6025\uff0c\u5341\u4e4b\u516b\u4e5d\uff0c\u4fbf\u8981\u591a\u4e8b\u4e86\uff01 \u3000\u3000\u5f53\u4e0b\u5fae\u5fae\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u65b9\u5144\u5f1f\uff0c\u90a3\u90d1\u5927\u4fa0\u65e2\u662f\u4f60\u7684\u5927\u6015\u7236\uff0c\u5bf9\u4ed6\u7684\u4e3a\u4eba\uff0c\u65b9\u5144\u5f1f\u81ea\u662f\u5341\u5206\u660e\u767d\u7684\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u533a\u533a\u4e0e\u5927\u4f2f\u7236\u76f8\u805a\u4e4b\u65f6\uff0c\u867d\u7136\u5e74\u5e7c\uff0c\u4f46\u5927\u4f2f\u7236\u53e4\u9053\u70ed\u80a0\uff0c\u5fe0\u4e49\u51db\u70c8\u4e4b\u6c14\uff0c\u533a\u533a\u4e07\u5206\u656c\u4ef0\uff01\u201d \u3000\u3000\u5409\u5a01\u7b11\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u65b9\u5144\u5f1f\u5f88\u540c\u610f\u5409\u67d0\u5bf9\u90d1\u5927\u4fa0\u7684\u770b\u6cd5\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u70b9\u5934\u9053\uff1a\u201c\u8001\u524d\u8f88\u6240\u89c1\uff0c\u679c\u662f\u4e0d\u5dee\uff01\u60f3\u6211\u90a3\u90d1\u4f2f\u7236\u7684\u4e3a\u4eba\uff0c\u7eb5\u7136\u5200\u65a7\u52a0\u8eab\uff0c\u4e5f\u4e0d\u4f1a\u4e27\u540d\u6c42\u751f\u7684\uff01\u201d \u3000\u3000\u5409\u5a01\u5927\u611f\u9ad8\u5174\uff0c\u5411\u970d\u9e23\u98ce\u62b1\u62f3\u9053\uff1a\u201c\u5e2e\u4e3b\uff0c\u65b9\u5144\u5f1f\u6240\u8bf4\uff0c\u5e94\u53ef\u8bc1\u660e\u90a3\u90d1\u5927\u4fa0\u4e0d\u4f1a\u5f92\u51fa\u7a7a\u8a00\uff0c\u5c1a\u671b\u5e2e\u4e3b\u6765\u65e5\u81f3\u5c11\u6797\uff0c\u83ab\u8981\u901e\u90a3\u4e00\u65f6\u4e4b\u4ec7\u5fff\u2026\u2026\u201d\u4ed6\u8eab\u4e3a\u5c5e\u4e0b\uff0c\u8a00\u8bcd\u4e4b\u95f4\uff0c\u53ea\u53ef\u8bf4\u5230\u8fd9\u7b49\u5730\u6b65\u4e86\u3002 \u3000\u3000\u970d\u9e23\u98ce\u6d53\u7709\u8038\u52a8\uff0c\u5927\u58f0\u7b11\u9053\uff1a\u201c\u5409\u5e08\u5f1f\uff0c\u4f60\u8fd9\u756a\u597d\u5fc3\uff0c\u611a\u5144\u656c\u9886\u4e86\u2026\u2026\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u5ffd\u800c\u957f\u957f\u4e00\u53f9\u9053\uff1a\u201c\u4e10\u5e2e\u4e4b\u4e2d\uff0c\u4ece\u65e0\u5371\u96be\u9000\u7f29\u4e4b\u4eba\uff0c\u4f46\u4e09\u4f4d\u5144\u5f1f\u5982\u6b64\u76f8\u529d\uff0c\u6211\u53c8\u600e\u80fd\u4e0d\u542c\uff1f\u8fd9\u7b49\u4e8b\u5b9e\u5728\u53eb\u4eba\u4e3a\u96be\u5f97\u5f88\u554a\uff01\u201d \u3000\u3000\u7fc1\u6606\u4ed1\u5ffd\u7136\u5475\u5475\u5927\u7b11\u9053\uff1a\u201c\u5e2e\u4e3b\uff0c\u5c0f\u5f1f\u6709\u4e00\u53e5\u8bdd\uff0c\u53ea\u6015\u8bf4\u5c06\u51fa\u6765\uff0c\u5e2e\u4e3b\u89c1\u602a\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u6709\u8bdd\u76f4\u8bb2\u5143\u59a8\uff0c\u611a\u5144\u600e\u4f1a\u89c1\u602a\u5462\uff1f\u201d \u3000\u3000\u7fc1\u6606\u4ed1\u9053\uff1a\u201c\u5c11\u6797\u4e4b\u4e8b\uff0c\u5e2e\u4e3b\u5e94\u8be5\u89c1\u673a\u800c\u4e3a\u624d\u662f\uff0c\u5e08\u5144\u4e43\u662f\u4e00\u5e2e\u4e4b\u4e3b\uff0c\u81ea\u662f\u4e0d\u53ef\u9661\u901e\u5339\u592b\u4e4b\u52c7\u554a\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u6c89\u4e86\u4e00\u9635\uff0c\u63a5\u9053\uff1a\u201c\u770b\u6765\u611a\u5144\u7eb5\u7136\u4e0d\u5e94\u5141\uff0c\u90a3\u4e5f\u662f\u884c\u4e0d\u901a\u7684\uff01\u201d \u3000\u3000\u5d14\u5927\u516c\u8fd9\u65f6\u5ffd\u7136\u62b1\u62f3\u5411\u970d\u9e23\u98ce\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u5e2e\u4e3b\u6709\u6b64\u660e\u89c1\uff0c\u5b9e\u662f\u672c\u5e2e\u4e4b\u798f\u2026\u2026\u201d \u3000\u3000\u970d\u9e23\u98ce\u795e\u60c5\u751a\u662f\u6fc0\u52a8\uff0c\u76ee\u5149\u5728\u51e0\u4eba\u8eab\u4e0a\u4e00\u8f6c\uff0c\u957f\u957f\u5730\u5401\u4e86\u4e00\u53e3\u5927\u6c14\uff0c\u9053\uff1a\u201c\u5c0f\u5f1f\u8499\u5927\u5e08\u5144\u8fd9\u822c\u7231\u62a4\uff0c\u771f\u6b63\u53eb\u4eba\u611f\u6fc0\u65e0\u5df2\uff0c\u5982\u662f\u6211\u518d\u4e0d\u77e5\u81ea\u91cd\uff0c\u90a3\u5c82\u975e\u662f\u592a\u4e0d\u901a\u7406\u4e86\u5417\uff01\u201d \u3000\u3000\u5d14\u5927\u516c\u76ee\u5149\u5728\u5b8b\u5b5a\u548c\u65b9\u96ea\u5b9c\u8eab\u4e0a\u4e00\u8f6c\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u5e2e\u4e3b\u6709\u6ca1\u6709\u6307\u793a\uff1f\u201d \u3000\u3000\u4ed6\u8fd9\u822c\u8bed\u6c14\uff0c\u53ea\u542c\u5f97\u970d\u9e23\u98ce\u4e00\u6014\uff0c\u9053\uff1a\u201c\u5e08\u5144\u6709\u4ec0\u4e48\u8bdd\u8981\u8bb2\u5417\uff1f\u201d \u3000\u3000\u5d14\u5927\u516c\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u5ffd\u7136\u6307\u7740\u5b8b\u5b5a\u548c\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4ed6\u4eec\u4e24\u4f4d\u72af\u4e86\u672c\u5e2e\u5927\u5fcc\uff0c\u5077\u7aa5\u672c\u5e2e\u957f\u8001\u805a\u4f1a\uff0c\u6309\u5f8b\u5e94\u8be5\u60e9\u6cbb\uff0c\u4e0d\u77e5\u5e2e\u4e3b\u53ef\u6709\u4ec0\u4e48\u6253\u7b97\uff1f\u201d \u3000\u3000\u4ed6\u7adf\u7136\u63d0\u51fa\u8fd9\u7b49\u96be\u9898\uff0c\u4e0d\u7981\u5f04\u5f97\u970d\u9e23\u98ce\u6123\u5728\u5f53\u5730\uff0c\u534a\u664c\u6ca1\u6709\u8bf4\u51fa\u8bdd\u6765\uff01 \u3000\u3000\u5d14\u5927\u516c\u8bf4\u7684\u4e0d\u9519\uff0c\u5b8b\u5b5a\u548c\u65b9\u96ea\u5b9c\u679c\u7136\u662f\u89e6\u72af\u4e86\u4e10\u5e2e\u5927\u5fcc\uff0c\u4f9d\u7167\u5e2e\u4e2d\u89c4\u5f8b\uff0c\u81f3\u5c11\u5e94\u4eb2\u81ea\u5230\u4e10\u5e2e\u603b\u575b\u5411\u7956\u5e08\u7237\u4e3b\u8c22\u7f6a\u3002 \u3000\u3000\u4f46\u970d\u9e23\u98ce\u5728\u77e5\u6653\u4e86\u65b9\u96ea\u5b9c\u4e43\u662f\u5251\u795e\u9648\u5e0c\u6b63\u7684\u4f20\u4eba\u4ee5\u540e\uff0c\u8fd9\u7b49\u8bdd\u53c8\u600e\u80fd\u8bf4\u51fa\u53e3\u6765\uff1f\u662f\u4ee5\uff0c\u5239\u90a3\u4e4b\u95f4\u4ed6\u5012\u60f3\u4e0d\u51fa\u4e00\u4e2a\u4e24\u5168\u4e4b\u7b56\u3002 \u3000\u3000\u5b8b\u5b5a\u5728\u542c\u5230\u5d14\u5927\u516c\u6240\u8a00\uff0c\u53ea\u4e0d\u8fc7\u6de1\u6de1\u5730\u7b11\u4e86\u4e00\u7b11\uff0c\u65b9\u96ea\u5b9c\u5c31\u6709\u4e9b\u4e0d\u7136\uff0c\u4ed6\u5446\u4e86\u4e00\u5446\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u8001\u82b1\u5b50\u600e\u4f1a\u5ffd\u7136\u95f4\u60f3\u8d77\u8fd9\u4e8b\u6765\u4e86\uff1f\u83ab\u975e\u4ed6\u4eec\u60f3\u85c9\u53e3\u8054\u624b\u5bf9\u4ed8\u4e8e\u6211\uff1f\u2026\u2026\u201d \u3000\u3000\u6562\u60c5\uff0c\u65b9\u96ea\u5b9c\u8bef\u4f1a\u5d14\u5927\u516c\u4e43\u662f\u60f3\u8892\u62a4\u9a86\u5947\uff0c\u5e76\u4e14\u4e3a\u4e86\u633d\u56de\u4e10\u5e2e\u58f0\u8a89\uff0c\u624d\u63d0\u51fa\u6b64\u8a00\u3002 \u3000\u3000\u601d\u5ff5\u4e4b\u95f4\uff0c\u8033\u4e2d\u5ffd\u7136\u542c\u5f97\u5409\u5a01\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5e2e\u4e3b\uff0c\u6b64\u89c4\u4e43\u662f\u5386\u4ee3\u7956\u5e08\u624b\u8ba2\uff0c\u54b1\u4eec\u53ef\u4e0d\u80fd\u8fdd\u80cc\u554a\uff01\u201d\u663e\u7136\uff0c\u4ed6\u662f\u706b\u4e0a\u52a0\u6cb9\u4e86\u3002 \u3000\u3000\u970d\u9e23\u98ce\u7709\u5934\u8fde\u76b1\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u5927\u5e08\u5144\u548c\u5409\u5e08\u5f1f\u8bf4\u7684\u90fd\u4e0d\u9519\uff0c\u53ea\u662f\u2026\u2026\u201d \u3000\u3000\u7ad9\u5728\u8fdc\u5904\u7684\u9a86\u5947\u7a81\u7136\u7b11\u9053\uff1a\u201c\u5e2e\u4e3b\uff0c\u8fd9\u7b49\u89c4\u77e9\u4e0d\u4f1a\u4ece\u4f60\u800c\u65ad\u5427\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u56e0\u662f\u4e0d\u77e5\u89e6\u72af\u4e86\u6b64\u7b49\u89c4\u6212\uff0c\u8981\u53d7\u5230\u4f55\u7b49\u60e9\u7f5a\uff0c\u662f\u4ee5\u5fc3\u4e2d\u6709\u4e9b\u4e0d\u5feb\uff0c\u95fb\u8a00\u5927\u58f0\u9053\uff1a\u201c\u970d\u5e2e\u4e3b\uff0c\u8fd9\u4f4d\u9a86\u5927\u4fa0\u8bf4\u7684\u4e0d\u9519\uff0c\u4f60\u4e0d\u53ef\u65ad\u9001\u4e86\u4e10\u5e2e\u7684\u5386\u4ee3\u58f0\u671b\uff0c\u65b9\u67d0\u5192\u72af\u4e4b\u7f6a\uff0c\u4e5f\u4e0d\u60f3\u63a8\u5378\uff0c\u53ea\u6d88\u5e2e\u4e3b\u8bf4\u5c06\u51fa\u6765\uff0c\u5bb9\u65b9\u67d0\u601d\u91cf\u601d\u91cf\u5982\u4f55\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u5e72\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u65b9\u8001\u5f1f\uff0c\u6b64\u4e8b\u53eb\u970d\u67d0\u8d8a\u53d1\u4e3a\u96be\u4e86\uff01\u60f3\u90a3\u9648\u5927\u4fa0\u4ecd\u662f\u672c\u5e2e\u7684\u5927\u6069\u4eba\uff0c\u5bf9\u4f60\u8001\u5f1f\uff0c\u970d\u67d0\u600e\u597d\u63d0\u60e9\u7f5a\u4e8c\u5b57\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\u51b7\u7b11\u9053\uff1a\u201c\u5e2e\u4e3b\u76db\u60c5\uff0c\u533a\u533a\u5fc3\u9886\u4e86\uff01\u5982\u82e5\u4e3a\u4e86\u5728\u4e0b\uff0c\u4e71\u4e86\u8d35\u5e2e\u5bb6\u6cd5\uff0c\u5c82\u4e0d\u662f\u4f7f\u5f97\u5e2e\u4e3b\u6210\u4e86\u53db\u5e2e\u7684\u7f6a\u4eba\u4e86\u5417\uff1f\u4f55\u51b5\uff0c\u8fd9\u51e0\u4f4d\u957f\u8001\u5c31\u4e0d\u4f1a\u653e\u8fc7\u5e2e\u4e3b\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5df2\u7136\u52a8\u6012\uff0c\u970d\u9e23\u98ce\u54ea\u6709\u542c\u4e0d\u51fa\u6765\u7684\u9053\u7406\uff1f\u4f46\u4e8b\u5728\u4e24\u96be\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u53eb\u4ed6\u5927\u6709\u65e0\u4ece\u6289\u62e9\u4e4b\u82e6\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5b8b\u5b5a\u5728\u65c1\u5230\u662f\u663e\u5f97\u8f7b\u677e\u5730\u4e00\u7b11\u9053\uff1a\u201c\u65b9\u8001\u5f1f\uff0c\u4f60\u8fd9\u822c\u81ea\u627e\u7f6a\u53d7\uff0c\u8001\u673d\u53ef\u4e0d\u5927\u613f\u610f\u5462\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u54b1\u4eec\u72af\u4e86\u4eba\u5bb6\u5e2e\u89c4\uff0c\u5982\u679c\u4e0d\u613f\u53d7\u7f5a\uff0c\u4e0d\u77e5\u662f\u5426\u6709\u522b\u7684\u529e\u6cd5\u53ef\u60f3\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u5f53\u7136\u6709\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4f60\u8001\u8bf4\u51fa\u6765\u542c\u542c\u770b\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u8001\u673d\u8bf4\u51fa\u6765\uff0c\u53ea\u6015\u4f60\u4e5f\u4e0d\u613f\u610f\u505a\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u665a\u8f88\u884c\u4e0d\u884c\u5f97\uff0c\u4f60\u8001\u673d\u662f\u4e0d\u7528\u8a00\u4e4b\u8fc7\u65e9\uff0c\u6216\u8bb8\u665a\u8f88\u613f\u610f\u4e00\u8bd5\u90a3\u4e5f\u8bf4\u4e0d\u5b9a\u5462\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u76ee\u5149\u5728\u970d\u9e23\u98ce\u8eab\u4e0a\u4e00\u6e9c\u800c\u8fc7\uff0c\u63a5\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u53ea\u8981\u4f60\u8001\u5f1f\u6b66\u529f\u8db3\u4ee5\u51fb\u8d25\u4ed6\u4eec\u5e2e\u4e2d\u957f\u8001\u4ee5\u4e0a\u7684\u4eba\u7269\uff0c\u8fd9\u7b49\u60e9\u7f5a\u4e4b\u4e8b\uff0c\u5e94\u662f\u53ef\u4ee5\u514d\u53bb\u7684\u4e86\uff01\u8001\u5f1f\uff0c\u53ea\u770b\u4f60\u80af\u4e0d\u80af\u8ddf\u4ed6\u4eec\u52a8\u624b\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u7565\u4e00\u6c89\u541f\uff0c\u5411\u970d\u9e23\u98ce\u9053\uff1a\u201c\u970d\u5e2e\u4e3b\uff0c\u5b8b\u8001\u4e4b\u8a00\uff0c\u5f53\u771f\u5417\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u672c\u5e2e\u7956\u5e08\u5728\u8ba2\u5b9a\u5e2e\u89c4\u4e4b\u65f6\u4e5f\u66fe\u8003\u8651\u5230\uff0c\u5982\u679c\u662f\u6709\u90a3\u6b66\u529f\u5353\u7edd\u4e4b\u58eb\uff0c\u72af\u4e86\u7981\u5fcc\uff0c\u603b\u4e0d\u80fd\u4e3a\u4e86\u8fd9\u7b49\u5c0f\u4e8b\uff0c\u503e\u5168\u5e2e\u4e4b\u529b\uff0c\u4f5c\u7ec6\u5fae\u672a\u8282\u4e4b\u4e8b\uff0c\u751a\u81f3\u5371\u53ca\u672c\u5e2e\u5168\u4f53\u5b50\u5f1f\uff0c\u662f\u4ee5\u7279\u522b\u89c4\u5b9a\uff0c\u51e1\u662f\u6b66\u529f\u80dc\u8fc7\u672c\u5e2e\u957f\u8001\u4e4b\u4eba\uff0c\u4e00\u65e6\u72af\u7981\uff0c\u90a3\u4f4d\u88ab\u51fb\u8d25\u7684\u957f\u8001\uff0c\u5c31\u5e94\u4ee3\u90a3\u72af\u7981\u4e4b\u4eba\u53d7\u7f5a\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fd6\u9053\uff1a\u201c\u5982\u662f\u4f9d\u4ed6\u6240\u8bf4\uff0c\u4e10\u5e2e\u771f\u6709\u8fd9\u7b49\u89c4\u77e9\uff0c\u5d14\u5927\u516c\u53c8\u4f55\u5fc5\u5f53\u9762\u63d0\u4e86\u51fa\u6765\u5462\uff1f \u3000\u3000\u60f3\u90a3\u9a86\u5947\u4e43\u662f\u4ed6\u4eec\u5e2e\u4e2d\u957f\u8001\u8eab\u4efd\uff0c\u5df2\u7136\u8d25\u5728\u6211\u7684\u624b\u4e2d\uff0c\u7eb5\u7136\u8981\u7f5a\uff0c\u90a3\u4e5f\u662f\u8981\u90a3\u9a86\u5947\u53bb\u4ee3\u6211\u53d7\u7f5a\u597d\u4e86\uff01\u201d \u3000\u3000\u5fd6\u5ff5\u4e4b\u95f4\uff0c\u4ed6\u5374\u6ca1\u6709\u60f3\u5230\uff0c\u5d14\u5927\u516c\u6240\u4ee5\u63d0\u51fa\u8fd9\u7b49\u5e2e\u89c4\uff0c\u6b63\u662f\u4e3a\u4e86\u5bf9\u4ed8\u9a86\u5947\u3002 \u3000\u3000\u970d\u9e23\u98ce\u8bdd\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u4f3c\u662f\u8fd9\u624d\u60f3\u8d77\uff0c\u90a3\u65b9\u96ea\u5b9c\u66fe\u7ecf\u51fb\u8d25\u4e86\u9a86\u5947\uff0c\u4e24\u773c\u795e\u5149\u66b4\u73b0\uff0c\u51dd\u89c6\u7740\u9a86\u5947\uff0c\u559d\u9053\uff1a\u201c\u9a86\u5e08\u5f1f\uff01\u201d \u3000\u3000\u9a86\u5947\u6697\u6697\u4e00\u60ca\uff0c\u62b1\u62f3\u9053\uff1a\u201c\u5e2e\u4e3b\u6709\u4f55\u6559\u8c15\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u9002\u624d\u4f60\u4e0e\u65b9\u8001\u5f1f\u4e4b\u6218\uff0c\u662f\u4f60\u8f93\u4e86\uff01\u201d \u3000\u3000\u9a86\u5947\u6014\u5f97\u4e00\u6014\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u5c0f\u5f1f\u8f93\u662f\u8f93\u4e86\uff0c\u4f46\u5374\u8f93\u5f97\u6709\u4e9b\u4e0d\u670d\u3002\u201d \u3000\u3000\u5d14\u5927\u516c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u600e\u7684\u4e0d\u670d\uff1f\u83ab\u975e\u4f60\u8fd8\u60f3\u518d\u6253\u4e00\u573a\u5417\uff1f\u201d \u3000\u3000\u9a86\u5947\u9634\u6c89\u5730\u626b\u4e86\u5d14\u5927\u516c\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5c0f\u5f1f\u5982\u77e5\u8fd9\u4e00\u6218\u4e43\u662f\u5173\u7cfb\u5230\u72af\u7981\u4e4b\u4e8b\uff0c\u5c0f\u5f1f\u5c31\u4e0d\u4f1a\u90a3\u7b49\u8f7b\u5fc3\u5927\u610f\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u76b1\u7709\u9053\uff1a\u201c\u9a86\u5e08\u5f1f\uff0c\u4eba\u8d35\u6709\u81ea\u77e5\u4e4b\u660e\uff0c\u9648\u5927\u4fa0\u7684\u5251\u6cd5\uff0c\u5c82\u662f\u4f60\u6211\u6240\u80fd\u7834\u89e3\u2026\u2026\u201d\u957f\u53f9\u4e00\u58f0\uff0c\u621b\u7136\u800c\u6b62\u3002 \u3000\u3000\u4ed6\u8a00\u4e0b\u4e4b\u610f\uff0c\u662f\u8981\u90a3\u9a86\u5947\u83ab\u518d\u4e22\u4eba\u4e86\uff01 \u3000\u3000\u9a86\u5947\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u5e2e\u4e3b\uff0c\u5c0f\u5f1f\u613f\u4e3a\u672c\u5e2e\u58f0\u671b\uff0c\u4e0e\u65b9\u5c11\u4fa0\u4f5c\u4e00\u6b7b\u6218\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u95fb\u8a00\uff0c\u4e24\u773c\u4e00\u77aa\uff0c\u6b63\u5f85\u8bf4\u8bdd\uff0c\u5374\u542c\u5f97\u65b9\u96ea\u5b9c\u5927\u58f0\u9053\uff1a\u201c\u5c0a\u9a7e\u4e0d\u670d\uff0c\u90a3\u5c31\u4e0d\u59a8\u518d\u51fa\u624b\u8bd5\u4e0a\u4e00\u8bd5\u4e86\uff01\u201d \u3000\u3000\u9a86\u5947\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u8001\u592b\u6b63\u8981\u4e0e\u4f60\u51b3\u4e00\u6b7b\u6218\uff0c\u4e0d\u77e5\u9601\u4e0b\u53ef\u6562\u653e\u624b\u4e00\u62fc\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u56de\u987e\u4e86\u970d\u9e23\u98ce\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5e2e\u4e3b\uff0c\u8bf7\u6055\u665a\u8f88\u653e\u8086\u4e86\uff01\u201d \u3000\u3000\u4e00\u632f\u957f\u5251\uff0c\u6307\u7740\u9a86\u5947\u9053\uff1a\u201c\u5c0a\u9a7e\u53ef\u4ee5\u51fa\u624b\u4e86\uff01\u201d \u3000\u3000\u9a86\u5947\u63a3\u52a8\u94dc\u68d2\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u6709\u5982\u6cf0\u5c71\u538b\u9876\u4e00\u822c\uff0c\u76f4\u5411\u65b9\u96ea\u5b9c\u5288\u6765\u3002\u9a86\u5947\u4f3c\u662f\u5b58\u5fc3\u8fd9\u4e00\u68d2\u5c31\u8981\u7f6e\u65b9\u96ea\u5b9c\u4e8e\u6b7b\u5730\uff0c\u662f\u6545\u6765\u52bf\u4e4b\u731b\uff0c\u4f7f\u4eba\u770b\u5f97\u72ec\u76ee\u60ca\u5fc3\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5374\u662f\u795e\u60c5\u5341\u5206\u4ece\u5bb9\uff0c\u8fd0\u5251\u4e00\u64a9\uff0c\u7531\u4e0b\u5f80\u4e0a\u53cd\u6321\u5f00\u53bb\u3002 \u3000\u3000\u970d\u9e23\u98ce\u5fc3\u4e2d\u4e00\u9707\uff0c\u6697\u53eb\u4e0d\u597d\uff0c\u53ea\u56e0\u9a86\u5947\u8fd9\u4e00\u68d2\u7684\u529b\u9053\uff0c\u4e0d\u4e0b\u6570\u767e\u65a4\uff0c\u65b9\u96ea\u5b9c\u6240\u7528\u7684\u5b9d\u5251\uff0c\u4e43\u662f\u8f7b\u7684\u5175\u5668\uff0c\u786c\u6321\u94dc\u68d2\uff0c\u5b9e\u662f\u5341\u5206\u4e0d\u667a\u4e4b\u4e3e\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u662f\u4e0d\u80fd\u8bf4\u51fa\u6765\uff0c\u4e00\u56e0\u81ea\u5df1\u8eab\u4e3a\u5e2e\u4e3b\uff0c\u4e8c\u6765\u65b9\u96ea\u5b9c\u4e0d\u662f\u5916\u4eba\uff0c\u81ea\u5df1\u5018\u662f\u53ea\u52a9\u5916\u4eba\uff0c\u90a3\u5c82\u4e0d\u53eb\u5e2e\u4e2d\u957f\u8001\u4eec\u7b11\u8bdd\u5417\uff1f \u3000\u3000\u8fd9\u65f6\uff0c\u4e10\u5e2e\u4e2d\u7684\u51e0\u4f4d\u957f\u8001\uff0c\u90fd\u6709\u4e9b\u513f\u7d27\u5f20\uff0c\u53ea\u6709\u5b8b\u5b5a\u77e5\u9053\u65b9\u96ea\u5b9c\u6df1\u6d45\uff0c\u6ee1\u8138\u542b\u7b11\uff0c\u89c6\u5982\u672a\u89c1\u4e00\u822c\uff0c\u5927\u58f0\u5411\u970d\u9e23\u98ce\u9053\uff1a\u201c\u970d\u5e2e\u4e3b\uff0c\u4f60\u8fd9\u4f4d\u9a86\u5e08\u5f1f\u7684\u68d2\u6cd5\u867d\u662f\u5f88\u731b\uff0c\u4f46\u5374\u4e0d\u61c2\u53d6\u5de7\u4e4b\u9053\uff0c\u4ec5\u6b64\u4e00\u62db\uff0c\u5c31\u6015\u65b9\u8001\u5f1f\u5df2\u7136\u5360\u5c3d\u673a\u5148\u7684\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u95fb\u8a00\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8fd8\u6ca1\u6709\u8f6c\u8fc7\u5ff5\u5934\u6765\uff0c\u8033\u4e2d\u5df2\u542c\u5f97\u65b9\u96ea\u5b9c\u5927\u7b11\u4e4b\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u67d4\u80fd\u514b\u5236\uff0c\u9a86\u5927\u4fa0\u8fd9\u4e00\u62db\u8d25\u5f97\u60f3\u5fc5\u4f9d\u7136\u4e0d\u670d\u7684\u4e86\uff01\u201d \u3000\u3000\u4e3e\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u9a86\u5947\u90a3\u6839\u94dc\u68d2\uff0c\u7adf\u7136\u518d\u5ea6\u8131\u624b\uff01 \u3000\u3000\u539f\u6765\u65b9\u96ea\u5b9c\u5251\u8d70\u8f7b\u7075\uff0c\u7528\u4e86\u4e00\u624b\u56db\u4e24\u62e8\u5343\u94a9\u7684\u5de7\u52b2\uff0c\u987a\u52bf\u4e00\u5f15\uff0c\u5c06\u9a86\u5947\u7684\u94dc\u68d2\u62e8\u5f97\u98de\u51fa\u4e94\u5c3a\uff01 \u3000\u3000\u9a86\u5947\u8138\u8272\u5927\u53d8\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u79fb\u6b65\u62fe\u8d77\u94dc\u68d2\uff0c\u7f13\u7f13\u5411\u7740\u65b9\u96ea\u5b9c\u8d70\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u9a86\u5927\u4fa0\uff0c\u8fd9\u56de\u4f60\u53ef\u8981\u5c0f\u5fc3\u4e86\uff0c\u5728\u4e0b\u8ba9\u4e86\u4f60\u5148\u653b\u4e09\u62db\uff0c\u773c\u4e0b\u53ef\u8981\u56de\u653b\u4f60\u4e00\u5f0f\u7684\u4e86\uff01\u201d\u8bed\u97f3\u752b\u843d\uff0c\u6325\u624b\u8fd0\u5251\uff0c\u731b\u523a\u800c\u53bb\u3002 \u3000\u3000\u9a86\u5947\u77e5\u9053\uff0c\u65b9\u96ea\u5b9c\u5251\u62db\u53d8\u5316\u4e4b\u591a\uff0c\u62db\u5f0f\u4e4b\u7cbe\uff0c\u7edd\u975e\u81ea\u5df1\u6240\u53ef\u4ee5\u62df\uff0c\u552f\u4e00\u53ef\u4ee5\u81f4\u80dc\u4e4b\u673a\uff0c\u4e43\u5728\u81ea\u5df1\u4ed7\u6043\u529f\u529b\u6df1\u539a\uff0c\u5c06\u5bf9\u65b9\u51fb\u9000\u3002 \u3000\u3000\u662f\u4ee5\uff0c\u65b9\u96ea\u5b9c\u5251\u52bf\u4e00\u53d1\uff0c\u4ed6\u7acb\u5373\u53cc\u624b\u62a2\u68d2\uff0c\u731b\u7136\u5411\u524d\u64a9\u53bb\u3002 \u3000\u3000\u773c\u770b\u5251\u68d2\u5373\u5c06\u76f8\u89e6\uff0c\u65b9\u96ea\u5b9c\u9661\u7136\u8eab\u5f62\u4e00\u8f6c\uff0c\u8fc5\u5feb\u65e0\u6bd4\u5730\u8e0f\u51fa\u534a\u6b65\uff0c\u5b9d\u5251\u4e00\u504f\uff0c\u7a7f\u900f\u9a86\u5947\u90a3\u6df1\u91cd\u7684\u68d2\u5f71\uff0c\u53ea\u542c\u5f97\u55e4\u7684\u4e00\u58f0\u9a86\u5947\u524d\u8863\u886b\u7834\u88c2\uff0c\u9c9c\u8840\u70b9\u70b9\u6d41\u51fa\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u82e5\u975e\u65b9\u96ea\u5b9c\u5fc3\u5b58\u5fe0\u539a\uff0c\u9a86\u5947\u65e9\u5df2\u547d\u4e27\u5f53\u5730\u4e86\u3002 \u3000\u3000\u970d\u9e23\u98ce\u7779\u72b6\u5927\u5403\u4e00\u60ca\uff0c\u8131\u53e3\u9053\uff1a\u201c\u65b9\u8001\u5f1f\uff0c\u624b\u4e0b\u7559\u60c5\u2026\u2026\u201d \u3000\u3000\u6562\u60c5\uff0c\u4ed6\u6015\u65b9\u96ea\u5b9c\u5e74\u8f7b\u6c14\u76db\uff0c\u4e00\u6012\u4e4b\u4e0b\uff0c\u5c31\u53d6\u4e86\u9a86\u5947\u7684\u6027\u547d\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u5e2e\u4e3b\u653e\u5fc3\uff0c\u5728\u4e0b\u867d\u662f\u5364\u83bd\uff0c\u4f46\u4e5f\u4e0d\u662f\u90a3\u822c\u8349\u83c5\u4eba\u547d\u4e4b\u4eba\u3002\u201d \u3000\u3000\u8bdd\u97f3\u4e00\u987f\uff0c\u7acb\u5373\u5411\u5b8b\u5b5a\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u665a\u8f88\u5df2\u7ecf\u6253\u8d25\u4e86\u4e10\u5e2e\u4e00\u4f4d\u957f\u8001\uff0c\u4e0d\u77e5\u90a3\u72af\u7981\u4e4b\u7f6a\uff0c\u662f\u5426\u53ef\u4ee5\u514d\u9664\u4e86\uff1f\u201d \u3000\u3000\u4ed6\u4e0d\u95ee\u970d\u9e23\u98ce\uff0c\u800c\u95ee\u5b8b\u5b5a\uff0c\u4e5f\u6b63\u662f\u4ed6\u4e3a\u4eba\u539a\u9053\u4e4b\u5904\u3002 \u3000\u3000\u5b8b\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u970d\u5144\uff0c\u4f60\u610f\u4e0b\u5982\u4f55\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u957f\u957f\u4e00\u53f9\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u65b9\u8001\u5f1f\u4e0d\u7528\u4e8c\u5ea6\u51fa\u624b\uff0c\u8001\u673d\u4e5f\u4f1a\u8981\u90a3\u9a86\u5e08\u5f1f\u4ee3\u4ed6\u56de\u5e26\u9886\u7f5a\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u970d\u5144\u679c\u7136\u662f\u5177\u6709\u5927\u667a\u6167\u4e4b\u58eb\u2026\u2026\u201d \u3000\u3000\u5d14\u5927\u516c\u8fd9\u65f6\u62b1\u62f3\u9053\uff1a\u201c\u5e2e\u4e3b\uff0c\u9a86\u5e08\u5f1f\u65e2\u8981\u4ee3\u90a3\u65b9\u5c0f\u4fa0\u53d7\u7f5a\uff0c\u4f55\u4e0d\u7acb\u5373\u4ee4\u4ed6\u56de\u5e2e\u9886\u8d23\u5462\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u77a7\u4e86\u90a3\u8840\u67d3\u80f8\u524d\u7684\u5e08\u5f1f\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5927\u5e08\u5144\uff0c\u9a86\u5e08\u5f1f\u6050\u6015\u4f24\u5f97\u4e0d\u8f7b\uff01\u201d \u3000\u3000\u5d14\u5927\u516c\u9053\uff1a\u201c\u76ae\u8089\u4e4b\u4f24\uff0c\u7b97\u5f97\u4ec0\u4e48\uff1f\u65b9\u8001\u5f1f\u4ec1\u4eba\u4e4b\u5fc3\uff0c\u53ea\u662f\u70b9\u5230\u800c\u5df2\uff0c\u5e2e\u4e3b\u4e0d\u7528\u62c5\u5fc3\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u5411\u9a86\u5947\u9053\uff1a\u201c\u9a86\u5e08\u5f1f\uff0c\u4f60\u7684\u4f24\u52bf\u5f88\u91cd\u5417\uff1f\u201d \u3000\u3000\u9a86\u5947\u6709\u5fc3\u88c5\u4f5c\u4f46\u77e5\u9053\u7792\u4e0d\u8fc7\u5d14\u5927\u516c\uff0c\u53ea\u597d\u6447\u5934\u9053\uff1a\u201c\u5927\u5e08\u5144\u5df2\u7136\u8bf4\u8fc7\uff0c\u53ea\u662f\u76ae\u8089\u4e4b\u4f24\u800c\u5df2\uff0c\u4e2d\u59a8\u4e8b\u7684\u2026\u2026\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u63a2\u624b\u81ea\u6000\u4e2d\u53d6\u51fa\u4e00\u5305\u91d1\u521b\u836f\uff0c\u6495\u5f00\u8863\u895f\uff0c\u6577\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u970d\u9e23\u98ce\u70b9\u4e86\u70b9\u5934\uff0c\u5411\u5d14\u5927\u516c\u9053\uff1a\u201c\u5927\u5e08\u5144\uff0c\u773c\u4e0b\u524d\u5f80\u5c11\u6797\uff0c\u4e43\u662f\u6781\u987b\u7528\u4eba\u4e4b\u9645\uff0c\u9a86\u5e08\u5f1f\u5982\u662f\u56de\u53bb\uff0c\u5c82\u4e0d\u5c11\u4e86\u4e00\u540d\u597d\u624b\uff1f\u201d \u3000\u3000\u539f\u6765\u970d\u9e23\u98ce\u60f3\u7559\u4e0b\u9a86\u5947\u5bf9\u4ed8\u9b54\u5f92\u4eec\u3002 \u3000\u3000\u5d14\u5927\u516c\u8138\u8272\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u5e2e\u4e3b\uff0c\u5c0f\u5144\u6709\u4e00\u53e5\u8bdd\uff0c\u8bf4\u4e86\u51fa\u6765\uff0c\u5c1a\u671b\u5e2e\u4e3b\u83ab\u8981\u89c1\u602a\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u5927\u5e08\u5144\u6709\u8bdd\u8bf7\u8bb2\u3002\u201d \u3000\u3000\u5d14\u5927\u516c\u9053\uff1a\u201c\u65b9\u5c11\u4fa0\u672a\u6765\u4e4b\u524d\uff0c\u54b1\u4eec\u679c\u7136\u663e\u5f97\u6709\u4e9b\u52bf\u5b64\u529b\u8584\uff0c\u5c11\u4e0d\u5f97\u50cf\u9a86\u5e08\u5f1f\u8fd9\u7b49\u597d\u624b\uff0c\u4f46\u773c\u4e0b\u7684\u60c5\u51b5\uff0c\u5374\u662f\u5927\u5927\u4e0d\u540c\u4e86\uff0c\u6709\u4e86\u65b9\u5c11\u4fa0\u4e00\u4eba\uff0c\u7eb5\u7136\u54b1\u4eec\u56db\u4eba\u5b8c\u5168\u4e0d\u53bb\uff0c\u90a3\u4e5f\u4e0d\u59a8\u4e8b\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u7709\u5934\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u5409\u5a01\u5ffd\u7136\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u5e2e\u4e3b\uff0c\u5927\u5e08\u5144\u6240\u8bf4\u4e0d\u9519\uff0c\u9a86\u5e08\u5144\u7b54\u5e94\u56de\u5e2e\u9886\u7f5a\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u4f3c\u662f\u8feb\u4e8e\u65e0\u5948\uff0c\u957f\u547c\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u5c31\u53eb\u9a86\u5e08\u5f1f\u56de\u5e2e\u5427\uff01\u201d\u76ee\u5149\u4e00\u8f6c\uff0c\u5411\u9a86\u5947\u9053\uff1a\u201c\u9a86\u5e08\u5f1f\uff01\u201d \u3000\u3000\u9a86\u5947\u8138\u4e0a\u795e\u8272\u751a\u662f\u602a\u5f02\uff0c\u663e\u7136\u6709\u4e9b\u4e0d\u613f\uff0c\u4f46\u53c8\u4e0d\u5f97\u4e0d\u88c5\u51fa\u606d\u656c\u7684\u795e\u6001\uff0c\u9053\uff1a\u201c\u5e2e\u4e3b\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u521a\u624d\u5927\u5e08\u5144\u6240\u8a00\uff0c\u4f60\u542c\u5230\u4e86\uff1f\u201d \u3000\u3000\u9a86\u5947\u8fdf\u7591\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u542c\u5230\u4e86\u3002\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u5e08\u5f1f\u901f\u901f\u56de\u5e2e\uff0c\u5728\u7956\u5e08\u795e\u4f4d\u4e4b\u524d\uff0c\u6094\u7f6a\u5341\u65e5\uff01\u201d \u3000\u3000\u9a86\u5947\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5341\u5929\u5417\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u9a86\u5947\u9053\uff1a\u201c\u5e2e\u4e3b\uff0c\u7167\u7956\u5e08\u7237\u6212\u5f8b\uff0c\u53ea\u987b\u4e09\u5929\u554a\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u4e09\u5929\u4e4b\u7f5a\uff0c\u53ea\u662f\u6307\u7684\u5916\u4eba\uff0c\u5982\u662f\u672c\u5e2e\u5f1f\u5b50\u4ee3\u4eba\u9886\u7f5a\uff0c\u5c31\u8981\u5341\u5929\u4e86\uff01\u201d \u3000\u3000\u9a86\u5947\u9ed8\u9ed8\u5730\u770b\u4e86\u5d14\u5927\u516c\u3001\u5409\u5a01\u4e00\u773c\uff0c\u62fe\u8d77\u94dc\u68d2\uff0c\u62b1\u62f3\u5411\u970d\u9e23\u98ce\u9053\uff1a\u201c\u5e2e\u4e3b\u4ee4\u8c15\uff0c\u5c5e\u4e0b\u4e0d\u6562\u4e0d\u9075\uff0c\u4e0d\u77e5\u9053\u662f\u51e0\u65f6\u52a8\u8eab\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u5373\u523b\u52a8\u8eab\uff0c\u4e0d\u5f97\u5ef6\u8bef\uff01\u201d \u3000\u3000\u9a86\u5947\u5e94\u4e86\u4e00\u58f0\u9075\u547d\uff0c\u8f6c\u8eab\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u77a7\u4ed6\u4e00\u8138\u4e0d\u9ad8\u5174\u4e4b\u8272\uff0c\u663e\u7136\u4ed6\u662f\u4e0d\u613f\u79bb\u53bb\u7684\uff0c\u53ea\u662f\u4ed6\u4e3a\u4f55\u53c8\u4e0d\u613f\u79bb\u53bb\u5462\uff1f\u201d \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u4ed6\u89c9\u7740\u6709\u4ec0\u4e48\u4e0d\u5bf9\uff0c\u53ea\u662f\u60f3\u4e0d\u51fa\u5176\u4e2d\u7684\u9053\u7406\u4f55\u5728\u3002 \u3000\u3000\u5d14\u5927\u516c\u76ee\u7779\u9a86\u5947\u79bb\u53bb\uff0c\u5ffd\u7136\u5411\u5ed3\u4e0b\u7684\u5c11\u5e74\u5927\u559d\u9053\uff1a\u201c\u6556\u513f\u8fc7\u6765\uff01\u201d\u53ea\u89c1\u90a3\u5a01\u731b\u7684\u5c11\u5e74\u5c39\u6556\uff0c\u5e94\u58f0\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u795e\u6001\u606d\u656c\u5730\u5411\u5d14\u5927\u516c\u4e00\u793c\uff0c\u9053\uff1a\u201c\u5927\u5e08\u4f2f\u6709\u4f55\u89c1\u6559\uff1f\u201d \u3000\u3000\u5d14\u5927\u516c\u9053\uff1a\u201c\u8ddf\u5728\u4f60\u9a86\u5e08\u53d4\u8eab\u540e\uff0c\u770b\u4ed6\u53bb\u4e86\u54ea\u91cc\uff1f\u201d \u3000\u3000\u5c39\u6556\u9053\uff1a\u201c\u5f1f\u5b50\u8981\u4e00\u76f4\u8ddf\u5728\u4ed6\u8eab\u540e\u5417\uff1f\u201d \u3000\u3000\u5d14\u5927\u516c\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u5c39\u6556\u9053\u58f0\uff1a\u201c\u9075\u547d\uff01\u201d\u5c31\u5f85\u79bb\u53bb\u3002 \u3000\u3000\u970d\u9e23\u98ce\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u6556\u513f\u4e14\u6162\uff01\u201d\u8f6c\u5934\u5411\u5d14\u5927\u516c\u9053\uff1a\u201c\u5927\u5e08\u5144\uff0c\u4f60\u8981\u8fd9\u5b69\u5b50\u8ddf\u5728\u9a86\u5e08\u5f1f\u8eab\u540e\uff0c\u4e0d\u77e5\u7528\u610f\u4f55\u5728\uff1f\u201d \u3000\u3000\u5d14\u5927\u516c\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u5728\u5b8b\u5b5a\u548c\u65b9\u96ea\u5b9c\u8eab\u4e0a\u4e00\u8f6c\uff0c\u9053\uff1a\u201c\u5e2e\u4e3b\uff0c\u611a\u5144\u662f\u6015\u90a3\u9a86\u5e08\u5f1f\u4e00\u6012\u4e4b\u4e0b\uff0c\u4e71\u4e86\u5fc3\u667a\uff0c\u5982\u662f\u5728\u534a\u8def\u4e4b\u4e0a\u88ab\u9b54\u5973\u624b\u4e0b\u9047\u4e0a\uff0c\u5fc5\u5c06\u7a77\u4e8e\u5e94\u4ed8\uff0c\u6709\u4e86\u6556\u513f\u5728\u540e\u6697\u6697\u62a4\u9001\uff0c\u5c82\u4e0d\u662f\u653e\u5fc3\u597d\u591a\u4e86\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u4f3c\u662f\u77e5\u9053\u5d14\u5927\u516c\u8bf4\u7684\u4e0d\u662f\u771f\u8bdd\uff0c\u4f46\u4e5f\u672a\u518d\u591a\u95ee\uff0c\u53ea\u70b9\u4e86\u70b9\u5934\u9053\uff1a\u201c\u5927\u5e08\u5144\u987e\u8651\u5f97\u4e5f\u6709\u9053\u7406\uff01\u201d\u5fae\u5fae\u4e00\u7b11\uff0c\u5411\u5c39\u6556\u9053\uff1a\u201c\u5c0f\u5fc3\u62a4\u9001\u4f60\u9a86\u5e08\u53d4\uff0c\u4e94\u65e5\u4e4b\u540e\uff0c\u53ef\u6765\u5c11\u6797\u8986\u547d\uff01\u201d \u3000\u3000\u5c39\u6556\u606d\u656c\u5730\u884c\u4e86\u5927\u793c\uff0c\u8f6c\u8eab\u51fa\u95e8\u800c\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5b8b\u5b5a\u5ffd\u7136\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u5d14\u5144\u679c\u7136\u601d\u8651\u5468\u8be6\uff0c\u4e10\u5e2e\u56db\u8001\u4e4b\u9996\uff0c\u786e\u6709\u8fc7\u4eba\u4e4b\u5904\uff01\u201d \u3000\u3000\u5d14\u5927\u516c\u542c\u5f97\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u5b8b\u5144\u62ac\u4e3e\u4e86\u2026\u2026\u201d \u3000\u3000\u5409\u5a01\u5374\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u8fd9\u4e8b\u4e43\u662f\u672c\u5e2e\u81ea\u5df1\u7684\u5bb6\u52a1\uff0c\u5c1a\u76fc\u5b8b\u5144\u83ab\u518d\u63d2\u624b\u53ef\u597d\uff01\u201d\u6562\u60c5\uff0c\u5409\u5a01\u4e5f\u5728\u62ff\u8bdd\u70b9\u9192\u5b8b\u5b5a\uff01 \u3000\u3000\u539f\u6765\u5d14\u5927\u516c\u8981\u5c39\u6556\u8ddf\u5728\u9a86\u5947\u8eab\u540e\uff0c\u4e43\u662f\u4ed6\u5bf9\u9a86\u5947\u751f\u4e86\u7591\u5fc3\uff0c\u4ee5\u4e3a\u4ed6\u53ef\u80fd\u5df2\u7136\u53db\u5e2e\u6295\u5411\u9b54\u5973\uff0c\u5b8b\u5b5a\u4f55\u7b49\u7cbe\u660e\uff0c\u81ea\u5df1\u4e5f\u731c\u51fa\u6765\uff0c\u8fd9\u65b9\u59cb\u62ff\u8bdd\u660e\u6367\u6697\u635f\u7684\u635f\u4e86\u5d14\u5927\u516c\u4e00\u53e5\uff01\u8fd9\u4e00\u6765\u5409\u5a01\u624d\u4e0d\u5f97\u4e0d\u6697\u6697\u70b9\u7834\uff0c\u8981\u5b8b\u5b5a\u83ab\u518d\u591a\u7ba1\u3002 \u3000\u3000\u5b8b\u5b5a\u4e24\u773c\u4e00\u7ffb\uff0c\u5927\u7b11\u9053\uff1a\u201c\u5f53\u7136\uff01\u5f53\u7136\uff01\u5144\u5f1f\u4e0d\u7ba1\u4fbf\u662f\u2026\u2026\u201d \u3000\u3000\u90a3\u4e10\u5e2e\u5e2e\u4e3b\u970d\u9e23\u98ce\u4e43\u662f\u4e00\u4e2a\u803f\u76f4\u4e4b\u4eba\uff0c\u542c\u5f97\u4e09\u4eba\u8a00\u8bed\uff0c\u4e0d\u7531\u5f97\u8138\u8272\u5927\u53d8\uff0c\u5931\u58f0\u9053\uff1a \u3000\u3000\u201c\u5927\u5e08\u5144\uff0c\u4f60\u8bf4\u7684\u662f\u4ec0\u4e48\uff1f\u83ab\u975e\u90a3\u9a86\u5e08\u5f1f\u4ed6\u2026\u2026\u201d\u663e\u5f97\u4ed6\u662f\u6709\u4e9b\u4e0d\u4fe1\uff0c\u8bf4\u5230\u8fd9\u91cc\uff0c\u4e0d\u7531\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5d14\u5927\u516c\u957f\u53f9\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u5e2e\u4e3b\uff0c\u4e0d\u662f\u5c0f\u5144\u7591\u5fc3\u592a\u8fc7\uff0c\u5b9e\u662f\u90a3\u9a86\u5e08\u5f1f\u7684\u4e3e\u52a8\uff0c\u592a\u53eb\u4eba\u7a00\u5947\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u76b1\u7709\u9053\uff1a\u201c\u5927\u5e08\u5144\uff0c\u9a86\u5e08\u5f1f\u8eab\u53d7\u6069\u5e08\u6559\u8bf2\u683d\u57f9\u4e4b\u5fb7\uff0c\u4ed6\u5c82\u662f\u90a3\u7b49\u5fd8\u6069\u8d1f\u4e49\uff0c\u53db\u5e2e\u5356\u4e3b\u4e4b\u4eba\uff01\u201d \u3000\u3000\u5409\u5a01\u63a5\u9053\uff1a\u201c\u5e2e\u4e3b\uff0c\u4e09\u5e08\u5144\u8a00\u8bcd\u95ea\u70c1\uff0c\u800c\u4e14\u5b58\u5fc3\u6311\u62e8\u5e2e\u4e3b\u4e0e\u5144\u5f1f\u7b49\u60c5\u611f\uff0c\u4ed6\u7eb5\u65e0\u53db\u5e2e\u4e4b\u5acc\uff0c\u4e5f\u6709\u80cc\u4e49\u4e4b\u5931\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u90a3\u4e5f\u7528\u4e0d\u7740\u6d3e\u6556\u513f\u8ddf\u8e2a\u4e8e\u4ed6\u554a\uff01\u201d \u3000\u3000\u5d14\u5927\u516c\u5ffd\u7136\u5927\u58f0\u9053\uff1a\u201c\u5e2e\u4e3b\uff0c\u672c\u5e2e\u5728\u6b66\u6797\u4e4b\u4e2d\uff0c\u540d\u671b\u4e0d\u5728\u90a3\u5176\u4ed6\u516b\u5927\u95e8\u6d3e\u4e4b\u4e0b\uff0c\u5c31\u662f\u4e3a\u4e86\u672c\u5e2e\u5b50\u6700\u591a\uff0c\u6700\u6742\uff0c\u4f46\u5374\u6ca1\u6709\u90a3\u4e0d\u8096\u4e4b\u5f92\uff0c\u505a\u51fa\u8d25\u574f\u4e4b\u4e8b\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u5e08\u5144\u8bf4\u7684\u679c\u7136\u662f\u5b9e\u8bdd\uff01\u201d \u3000\u3000\u5d14\u5927\u516c\u76ee\u5149\u4e00\u9eef\uff0c\u63a5\u906d\uff1a\u201c\u5e2e\u4e3b\uff0c\u8fd9\u4e2d\u95f4\u7684\u9053\u7406\uff0c\u5e2e\u4e3b\u53ef\u77e5\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u7b11\u9053\uff1a\u201c\u672c\u6d3e\u4e00\u8109\u76f8\u627f\uff0c\u957f\u5e7c\u4e4b\u95f4\uff0c\u4eb2\u5982\u7236\u5b50\u624b\u8db3\uff0c\u81ea\u7136\u4eba\u4eba\u90fd\u80fd\u91cd\u89c6\u5168\u5e2e\u58f0\u8a89\u4e86\uff01\u201d \u3000\u3000\u5d14\u5927\u516c\u9053\uff1a\u201c\u5e2e\u89c4\u6781\u4e25\uff0c\u4e00\u6709\u53ef\u7591\uff0c\u7acb\u5373\u5c31\u8ffd\u800c\u4e0d\u820d\u5730\u67e5\u51fa\u539f\u56e0\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u5ffd\u7136\u8138\u8272\u4e00\u53d8\uff0c\u5927\u6b65\u5411\u524d\uff0c\u5411\u5d14\u5927\u516c\u62b1\u62f3\u957f\u63d6\u5230\u5730\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u5e08\u5144\u60e0\u6211\u826f\u591a\uff0c\u5c0f\u5f1f\u8eab\u53d7\u4e86\uff01\u201d \u3000\u3000\u76ee\u5149\u8f6c\u5230\u5409\u5a01\u9053\uff1a\u201c\u5409\u5e08\u5f1f\uff0c\u611a\u5144\u5728\u8fd9\u7b49\u4e8b\u4f53\u4e4b\u4e0a\uff0c\u679c\u771f\u662f\u5931\u5bdf\u592a\u591a\uff0c\u5c1a\u76fc\u5e08\u5f1f\u6b64\u540e\u80fd\u968f\u65f6\u63d0\u9192\u4e8e\u6211\uff01\u201d \u3000\u3000\u5409\u5a01\u6168\u7136\u7b11\u9053\uff1a\u201c\u5e2e\u4e3b\uff0c\u8fd9\u662f\u5c0f\u5f1f\u5206\u5185\u4e4b\u4e8b\u554a\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u76ee\u7779\u4ed6\u4eec\u4e4b\u95f4\u5982\u6b64\u5ba2\u6c14\uff0c\u5fc3\u4e2d\u5927\u611f\u5feb\u6170\uff0c\u5931\u58f0\u7b11\u9053\uff1a\u201c\u970d\u5e2e\u4e3b\u5927\u7565\u96c4\u624d\uff0c\u51e0\u4f4d\u53c8\u8fd9\u7b49\u9047\u4e8b\u8c28\u614e\uff0c\u4e10\u5e2e\u5927\u5c55\u5b8f\u56fe\u4e4b\u65e5\uff0c\u8001\u673d\u53ef\u4ee5\u8bd5\u76ee\u800c\u5f85\u4e86\uff01\u201d \u3000\u3000\u5409\u5a01\u8fd9\u65f6\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5e2e\u4e3b\uff0c\u54b1\u4eec\u5df2\u547d\u4e2d\u5dde\u5f1f\u5b50\u5907\u6709\u9152\u83dc\uff0c\u4e0d\u77e5\u53ef\u5426\u62ac\u5c06\u4e0a\u6765\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u5feb\u62ac\uff0c\u5feb\u62ac\uff0c\u611a\u5144\u4eca\u5bb5\u5341\u5206\u9ad8\u5174\uff0c\u8981\u4e0e\u5b8b\u5144\u548c\u65b9\u8001\u5f1f\u7b49\u559d\u4e2a\u75db\u5feb\u2026\u2026\u201d \u3000\u3000\u5409\u5a01\u8eab\u5411\u6bbf\u4fa7\u5b50\u5f1f\u4ea4\u4ee3\u4e00\u58f0\uff0c\u4e0d\u4e00\u77ac\u4fbf\u9001\u4e0a\u4e86\u4e00\u684c\u9152\u7b75\u3002 \u3000\u3000\u4e10\u5e2e\u996e\u98df\uff0c\u591a\u975e\u5c71\u73cd\u6d77\u5473\uff0c\u4f46\u8c03\u5236\u624b\u6cd5\u4e4b\u7cbe\uff0c\u867d\u662f\u5927\u5185\u5fa1\u53a8\u4e5f\u96be\u4ee5\u671b\u5176\u9879\u80cc\u3002\u5de6\u53f3\u4e0d\u8fc7\u662f\u9e21\u3001\u86c7\u7b49\u51e0\u5473\uff0c\u4f46\u5374\u6ee1\u6ee1\u6446\u4e86\u4e00\u684c\uff0c\u505a\u4e86\u4e0d\u4e0b\u5341\u9053\u5927\u83dc\u9001\u51fa\u3002 \u3000\u3000\u4e94\u8001\u4e00\u5c11\uff0c\u4e00\u8fde\u5403\u5230\u5929\u5df2\u53d1\u767d\uff0c\u65b9\u59cb\u6563\u5e2d\u3002 \u3000\u3000\u51e0\u4eba\u5728\u8fd9\u5173\u738b\u5e99\u4e2d\u7a0d\u7a0d\u6253\u5750\u8c03\u606f\uff0c\u4fbf\u5165\u57ce\u4f1a\u540c\u4e86\u90a3\u5b89\u5c0f\u840d\u548c\u6731\u7693\uff0c\u4e00\u540c\u524d\u5f80\u5c11\u6797\u800c\u53bb\u3002 \u3000\u3000\u5f53\u5929\u672a\u65f6\u5de6\u53f3\uff0c\u51e0\u4eba\u6765\u5230\u5c11\u6797\u4e2d\u5929\u798f\u5730\u95e8\u724c\u574a\u4e4b\u4e0b\uff0c\u53ea\u89c1\u56db\u540d\u618e\u4fa3\u8fce\u4e0a\u524d\u6765\uff0c\u6321\u4ef2\u53bb\u8def\uff01\u4ed6\u4eec\u4e5f\u4e0d\u8a00\u8bed\uff0c\u53ea\u662f\u4e0d\u8ba9\u4e00\u884c\u4eba\u8fc7\u53bb\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u77a7\u5f97\u751a\u662f\u4e0d\u89e3\uff0c\u5411\u5b8b\u5b5a\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u8fd9\u4e9b\u548c\u5c1a\u597d\u4e0d\u8bb2\u7406\u554a\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u600e\u89c1\u5f97\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4ed6\u4eec\u4e3a\u4f55\u6321\u4f4f\u6211\u4eec\u7684\u53bb\u8def\uff1f\u6211\u4eec\u4e0d\u662f\u524d\u6765\u4e3a\u4ed6\u4eec\u6392\u96be\u89e3\u7eb7\u7684\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u6ca1\u6709\u8bf4\u660e\uff0c\u4ed6\u4eec\u81ea\u7136\u4e0d\u653e\u5fc3\u4e86\uff01\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u5927\u6b65\u4e0a\u524d\u800c\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\u5409\u5a01\u5df2\u62a2\u5148\u51b7\u51b7\u559d\u9053\uff1a\u201c\u5927\u5e08\u4eec\u53ef\u662f\u5c11\u6797\u95e8\u4e0b\u7684\u5b50\u5f1f\u5417\uff1f\u201d \u3000\u3000\u90a3\u56db\u540d\u4e2d\u5e74\u50e7\u4eba\u4e92\u671b\u4e00\u773c\uff0c\u7531\u5de6\u9996\u90a3\u4f4d\u7626\u5c0f\u50e7\u4eba\u5408\u5341\u63a5\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8d2b\u50e7\u7b49\u6b63\u662f\u5c11\u6797\u95e8\u4e0b\uff0c\u4e0d\u77e5\u51e0\u4f4d\u65bd\u4e3b\u600e\u4e48\u79f0\u547c\uff1f\u201d \u3000\u3000\u5409\u5a01\u9053\uff1a\u201c\u8001\u592b\u4e10\u5e2e\u9634\u9633\u624b\u5409\u5a01\uff0c\u8fd9\u4f4d\u662f\u970d\u5e2e\u4e3b\uff01\u201d \u3000\u3000\u90a3\u540d\u50e7\u4eba\u95f4\u8a00\u76b1\u76b1\u7709\u9053\uff1a\u201c\u970d\u5e2e\u4e3b\u5417\uff1f\u201d \u3000\u3000\u5409\u5a01\u53ea\u542c\u5f97\u8138\u8272\u4e00\u53d8\uff0c\u6012\u9053\uff1a\u201c\u548c\u5c1a\uff0c\u4f60\u600e\u4e48\u597d\u50cf\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u618e\u4eba\u5408\u5341\u9053\uff1a\u201c\u8d2b\u50e7\u679c\u7136\u6709\u4e9b\u4e0d\u4fe1\uff01\u201d \u3000\u3000\u5409\u5a01\u6012\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u548c\u5c1a\uff0c\u4f60\u5bf9\u957f\u8f88\u8bf4\u8bdd\uff0c\u53ef\u8981\u5c0f\u5fc3\u4e9b\u624d\u662f\uff01\u201d \u3000\u3000\u53ea\u89c1\u90a3\u548c\u5c1a\u5ffd\u7136\u6717\u58f0\u4e00\u7b11\u9053\uff1a\u201c\u65bd\u4e3b\u81ea\u79f0\u957f\u8f88\uff0c\u4e0d\u77e5\u51ed\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5409\u5a01\u51b7\u51b7\u5e94\u9053\uff1a\u201c\u5c31\u51ed\u8001\u592b\u4e0e\u5165\u51e1\u5927\u5e08\u7684\u4ea4\u60c5\uff0c\u5c31\u79f0\u5f97\u662f\u4f60\u4eec\u7684\u957f\u8f88\u4e86\uff01\u201d \u3000\u3000\u90a3\u548c\u5c1a\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u65bd\u4e3b\uff0c\u8bf7\u95ee\u8fd9\u6b66\u6797\u4e4b\u4e2d\u6709\u51e0\u4f4d\u4e10\u5e2e\u5e2e\u4e3b\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u542c\u548c\u5fc3\u4e2d\u4e00\u9707\uff0c\u8131\u53e3\u9053\uff1a\u201c\u4e10\u5e2e\u5e2e\u4e3b\u81ea\u7136\u53ea\u6709\u8001\u592b\u4e00\u4e2a\u4eba\u4e86\uff01\u201d \u3000\u3000\u90a3\u548c\u5c1a\u6447\u5934\u9053\uff1a\u201c\u4e0d\u50cf\u554a\uff01\u201d \u3000\u3000\u5d14\u5927\u516c\u4e5f\u5fcd\u4e0d\u4f4f\u6012\u9053\uff1a\u201c\u548c\u5c1a\uff0c\u4f60\u4f11\u8981\u653e\u8086\uff0c\u8d35\u5bfa\u5165\u4e91\u638c\u95e8\u89c1\u4e86\u5e2e\u4e3b\u8bf4\u8bdd\u4e5f\u4e0d\u8be5\u5982\u6b64\u5927\u80c6\uff01\u201d \u3000\u3000\u548c\u5c1a\u4e00\u7b11\u9053\uff1a\u201c\u597d\u8bf4\uff0c\u53ea\u53ef\u60dc\u5404\u4f4d\u6765\u8fdf\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u76ee\u7779\u773c\u4e0b\u60c5\u666f\uff0c\u5fc3\u4e2d\u5927\u5403\u4e00\u60ca\uff0c\u6697\u9053\uff1a\u5c11\u6797\u5411\u4ee5\u6e05\u89c4\u6212\u6cd5\u95fb\u540d\u5929\u4e0b\uff0c\u6b64\u523b\u89c1\u6765\uff0c\u5374\u662f\u5927\u4e0d\u76f8\u79f0\u4e86\u2026\u2026 \u3000\u3000\u4ed6\u6025\u601d\u4e4b\u95f4\uff0c\u542c\u5f97\u90a3\u548c\u5c1a\u8bf4\u662f\u4ed6\u4eec\u6765\u8fdf\u4e86\uff0c\u4e0d\u7981\u5168\u8eab\u4e00\u9707\uff0c\u8131\u53e3\u559d\u9053\uff1a\u201c\u600e\u4e48\u6765\u8fdf\u4e86\uff1f\u53ef\u662f\u90a3\u5929\u9b54\u5973\u5c06\u4f60\u4eec\u5236\u670d\u4e86\u5417\uff1f\u201d \u3000\u3000\u90a3\u548c\u5c1a\u76ee\u5149\u5728\u65b9\u96ea\u5b9c\u8eab\u4e0a\u4e00\u8f6c\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u6211\u5c11\u6797\u5bfa\u662f\u4f55\u7b49\u6240\u5728\uff1f\u90a3\u5929\u9b54\u5973\u4e5f\u914d\u4f24\u5f97\u4e86\u6211\u4eec\uff1f\u201d \u3000\u3000\u5409\u5a01\u63a5\u9053\uff1a\u201c\u4f60\u8fd9\u6765\u8fdf\u4e4b\u610f\u4f55\u6307\uff1f\u201d \u3000\u3000\u548c\u5c1a\u9053\uff1a\u201c\u4e10\u5e2e\u970d\u5e2e\u4e3b\u65e9\u5df2\u5728\u6628\u665a\u5c31\u62b5\u8fbe\u672c\u5bfa\u4e86\uff01\u201d \u3000\u3000\u548c\u5c1a\u8bdd\u97f3\u4e00\u843d\uff0c\u970d\u9e23\u98ce\u7b49\u4eba\u4e0d\u7981\u5168\u90e8\u4e3a\u4e4b\u4e00\u5446\uff01 \u3000 \u7b2c\u4e8c\u5341\u56de\u3000\u5c11\u6797\u60ca\u53d8 \u3000 \u3000\u3000\u5c24\u5176\u662f\u970d\u9e23\u98ce\uff0c\u5185\u5fc3\u7684\u9707\u52a8\uff0c\u7b80\u76f4\u96be\u4ee5\u5f62\u5bb9\uff01 \u3000\u3000\u201c\u8fd9\u7b80\u76f4\u662f\u4e0d\u53ef\u80fd\u7684\u4e8b\uff01\u201d\u4ed6\u5fc3\u4e2d\u5927\u53eb\uff01 \u3000\u3000\u81ea\u5df1\u65e2\u65e0\u5206\u8eab\u4e4b\u672f\uff0c\u600e\u4e48\u53ef\u80fd\u5728\u6628\u591c\u524d\u6765\u5c11\u6797\uff1f \u3000\u3000\u5d14\u5927\u516c\u5728\u4e00\u60ca\u4e4b\u540e\uff0c\u7acb\u5373\u559d\u9053\uff1a\u201c\u5927\u548c\u5c1a\uff0c\u4f60\u8fd9\u8bdd\u662f\u771f\u7684\u5417\uff1f\u201d \u3000\u3000\u548c\u5c1a\u5408\u5341\u9053\uff1a\u201c\u4f5b\u95e8\u5f1f\u5b50\uff0c\u81ea\u662f\u4e0d\u6562\u8bf3\u8bed\u6b3a\u4eba\uff01\u201d \u3000\u3000\u5409\u5a01\u76ee\u5149\u4e00\u8f6c\uff0c\u6389\u5934\u5411\u970d\u9e23\u98ce\u9053\uff1a\u201c\u5e2e\u4e3b\uff0c\u770b\u6765\u679c\u771f\u6709\u4eba\u5728\u4e0e\u672c\u5e2e\u4e3a\u96be\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u5f88\u597d\u554a\uff01\u672c\u5ea7\u5012\u8981\u77a7\u77a7\u4ed6\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u53cc\u76ee\u7cbe\u5149\u56db\u5c04\uff0c\u5411\u90a3\u56db\u540d\u50e7\u4fa3\u9053\uff1a\u201c\u4e3e\u4e16\u4e4b\u95f4\uff0c\u4e10\u5e2e\u5e2e\u4e3b\uff0c\u53ea\u6709\u970d\u67d0\u4e00\u4eba\uff0c\u6709\u52b3\u5927\u5e08\u5feb\u53bb\u901a\u62a5\u5165\u4e91\u638c\u95e8\u4eba\uff0c\u5c31\u8bf4\u970d\u67d0\u5e94\u7ea6\u800c\u6765\uff01\u201d \u3000\u3000\u90a3\u548c\u5c1a\u51b7\u51b7\u4e00\u7b11\uff0c\u770b\u8eab\u65c1\u51e0\u4eba\u4e00\u773c\uff0c\u9053\uff1a\u201c\u8fd9\u53ef\u5c31\u662f\u602a\u4e8b\u4e86\u2026\u2026\u201d \u3000\u3000\u4ed6\u8eab\u65c1\u7684\u4e00\u4f4d\u7c97\u58ee\u548c\u5c1a\u63a5\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u4e8b\u679c\u7136\u5341\u5206\u5947\u602a\uff0c\u770b\u6765\u8fd9\u51e0\u4f4d\u7684\u6c14\u52bf\u4e0d\u51e1\uff0c\u4e0d\u4f3c\u4ec0\u4e48\u574f\u4eba\u554a\uff01\u201d \u3000\u3000\u7626\u5c0f\u7684\u548c\u5c1a\u70b9\u5934\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u54b1\u4eec\u5f97\u5546\u91cf\u4e00\u4e0b\uff01\u201d \u3000\u3000\u90a3\u7c97\u58ee\u7684\u548c\u5c1a\u9053\uff1a\u201c\u5e08\u5144\uff0c\u54b1\u4eec\u8981\u4e0d\u8981\u53bb\u901a\u62a5\u4e00\u4e0b\u5462\uff1f\u4e5f\u8bb8\u2026\u2026\u8fd9\u4e2d\u95f4\u6709\u4e86\u8bef\u4f1a\uff01\u201d \u3000\u3000\u7626\u548c\u5c1a\u6c89\u541f\u9053\uff1a\u201c\u8bef\u4f1a\u4e0d\u4e00\u5b9a\uff0c\u6709\u771f\u6709\u5047\uff0c\u90a3\u662f\u5fc5\u7136\u7684\u4e86\uff01\u201d \u3000\u3000\u7c97\u58ee\u548c\u5c1a\u5927\u7b11\u9053\uff1a\u201c\u8c01\u662f\u771f\u7684\u5462\uff1f\u201d \u3000\u3000\u7626\u548c\u5c1a\u9053\uff1a\u201c\u8c01\u6653\u5f97\uff1f\u201d \u3000\u3000\u8fd9\u65f6\u5b89\u5c0f\u840d\u5fcd\u4e0d\u4f4f\u53eb\u9053\uff1a\u201c\u5f53\u7136\u8fd9\u4e00\u4f4d\u662f\u771f\u7684\u4e86\uff01\u4f60\u4eec\u96be\u9053\u778e\u4e86\u773c\uff1f\u201d \u3000\u3000\u90a3\u7c97\u58ee\u548c\u5c1a\u7b11\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\u7247\u9762\u4e4b\u8bcd\uff0c\u8d2b\u50e7\u600e\u80fd\u76f8\u4fe1\u5f97\u4e86\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4f60\u8981\u600e\u6837\u624d\u4f1a\u76f8\u4fe1\u5462\uff1f\u201d \u3000\u3000\u7626\u548c\u5c1a\u7b11\u9053\uff1a\u201c\u5341\u5206\u7b80\u5355\uff0c\u4f60\u4eec\u5148\u884c\u9000\u53bb\uff0c\u7b49\u672c\u5bfa\u638c\u95e8\u4eba\u67e5\u660e\u4e00\u5207\uff0c\u518d\u884c\u524d\u6765\u3002\u201d \u3000\u3000\u77bf\u9e23\u98ce\u84ec\u53d1\u621f\u5f20\uff0c\u6012\u9053\uff1a\u201c\u80e1\u8bf4\uff01\u8001\u4eba\u600e\u4f1a\u6709\u4eba\u5192\u5145\uff1f\u4f60\u4eec\u53ea\u6015\u662f\u5b58\u5fc3\u5201\u96be\u7684\u4e86\uff1f\u201d \u3000\u3000\u90a3\u7c97\u58ee\u548c\u5c1a\u4e24\u773c\u4e00\u7ffb\uff0c\u559d\u9053\uff1a\u201c\u65bd\u4e3b\uff0c\u4f60\u628a\u5c11\u6797\u5bfa\u7684\u50e7\u4eba\u770b\u4f5c\u6b66\u6797\u5bb5\u5c0f\u4e86\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u5dee\u4e0d\u591a\uff1f\u201d \u3000\u3000\u7626\u548c\u5c1a\u52c3\u7136\u4f5c\u8272\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\uff0c\u4f60\u80c6\u6562\u8511\u89c6\u5c11\u6797\u50e7\u4fa3\uff0c\u4e0d\u6015\u672c\u5bfa\u6e05\u89c4\u6212\u5f8b\u4e4b\u7f5a\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u4e00\u6014\u9053\uff1a\u201c\u4f60\u4eec\u7684\u6e05\u89c4\u6212\u5f8b\uff0c\u4e0e\u6211\u4f55\u5e72\uff1f\u201d \u3000\u3000\u7626\u548c\u5c1a\u63a5\u9053\uff1a\u201c\u8981\u4f60\u5165\u5bfa\u9886\u7f5a\uff0c\u600e\u7684\u65e0\u5e72\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u770b\u4e86\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u8fd9\u662f\u771f\u7684\uff1f\u201d \u3000\u3000\u5979\u6ca1\u6709\u4ec0\u4e48\u6c5f\u6e56\u9605\u5386\uff0c\u5bf9\u8fd9\u4e9b\u4e8b\u81ea\u662f\u4e0d\u592a\u660e\u767d\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u70b9\u5934\u9053\uff1a\u201c\u8fd9\u4f4d\u5927\u5e08\u8bf4\u5f97\u4e0d\u9519\uff0c\u5c11\u6797\u5bfa\u9886\u8896\u6b66\u6797\uff0c\u786e\u662f\u4e0d\u8bb8\u5916\u4eba\u8bec\u8511\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u542c\u5904\u65b9\u96ea\u5b9c\u8fd9\u4e48\u4e00\u8bf4\uff0c\u53ef\u5c31\u4f7f\u4e0a\u6027\u5b50\u4e86\uff01 \u3000\u3000\u5f53\u4e0b\u7b11\u9053\uff1a\u201c\u65b9\u5144\uff0c\u5018\u662f\u5144\u5f1f\u4e0d\u53bb\u9886\u8d23\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5c11\u6797\u50e7\u4fa3\u5c31\u4f1a\u8ffd\u62ff\u4e8e\u4f60\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u95fb\u8a00\uff0c\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u60f3\u62ff\u6211\uff1f\u5c31\u51ed\u8fd9\u56db\u4e2a\u4e0d\u8d77\u773c\u7684\u548c\u5c1a\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u77e5\u9053\u5979\u4e43\u662f\u8a00\u51fa\u65e0\u5fc3\uff0c\u987a\u5634\u800c\u8bf4\uff0c\u6b8a\u4e0d\u6599\uff0c\u8fd9\u4e00\u6765\u53ef\u5c31\u66f4\u628a\u5c11\u6797\u50e7\u4eba\u7ed9\u5f97\u7f6a\u4e86\uff01\u53ea\u597d\u82e6\u7b11\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u54b1\u4eec\u8fdc\u6765\u662f\u5ba2\uff0c\u6700\u597d\u83ab\u8981\u5148\u4e0e\u4e3b\u4eba\u53d1\u751f\u7ea0\u7eb7\u624d\u662f\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u60f3\u4e86\u4e00\u60f3\uff0c\u5ffd\u7136\u683c\u683c\u4e00\u7b11\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u65e2\u7136\u4e0d\u8981\u6211\u60f9\uff0c\u90a3\u5c31\u9976\u4e86\u4ed6\u4eec\u4fbf\u4e86\uff01\u201d \u3000\u3000\u8fd9\u7b49\u53e3\u6c14\uff0c\u4f3c\u4e4e\u90a3\u56db\u540d\u548c\u5c1a\u5168\u90e8\u8981\u542c\u5979\u6446\u5e03\u4e00\u822c\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u5fae\u5fae\u4e00\u7b11\uff0c\u6ca1\u6709\u591a\u8bf4\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5d14\u5927\u516c\u5374\u5411\u90a3\u56db\u540d\u50e7\u4fa3\u9053\uff1a\u201c\u4e10\u5e2e\u5e2e\u4e3b\uff0c\u666e\u5929\u4e4b\u4e0b\uff0c\u53ea\u6709\u4e00\u4e2a\uff0c\u56db\u4f4d\u5927\u5e08\u4f55\u4e0d\u6d3e\u4eba\u5165\u5185\u901a\u62a5\u8d35\u5bfa\u638c\u95e8\u5165\u4e91\u5927\u5e08\uff0c\u8981\u90a3\u5047\u5192\u4e4b\u4eba\uff0c\u51fa\u6765\u4e00\u89c1\uff1f\u201d \u3000\u3000\u90a3\u7626\u548c\u5c1a\u770b\u4e86\u7c97\u58ee\u548c\u5c1a\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5e08\u5144\uff0c\u8fd9\u4f4d\u65bd\u4e3b\u8bf4\u7684\u4f3c\u662f\u751a\u6709\u9053\u7406\u5462\uff01\u201d \u3000\u3000\u7c97\u58ee\u7684\u50e7\u4eba\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u54b1\u4eec\u662f\u597d\u662f\u8bf7\u90a3\u77e5\u5ba2\u5e08\u53d4\u6765\u6b64\u2026\u2026\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u8fd9\u4f4d\u5c0f\u65bd\u4e3b\u85d0\u89c6\u672c\u5bfa\u4e4b\u4e8b\uff0c\u54b1\u4eec\u603b\u4e0d\u80fd\u4e0d\u7ba1\u5427\uff01\u201d \u3000\u3000\u6b64\u523b\u90a3\u4e00\u76f4\u672a\u66fe\u8bf4\u8bdd\u7684\u4e24\u540d\u50e7\u4eba\uff0c\u5ffd\u7136\u5927\u6b65\u4e0a\u524d\uff0c\u5411\u5b89\u5c0f\u840d\u8d70\u4e86\u8fc7\u6765\uff0c\u5176\u4e2d\u4e00\u540d\u8eab\u6750\u6781\u5176\u9ad8\u5927\uff0c\u8c8c\u76f8\u5341\u5206\u5a01\u731b\uff0c\u53e6\u4e00\u540d\u5374\u662f\u4e00\u8138\u83dc\u8272\uff0c\u4eff\u4f5b\u5927\u75c5\u672a\u6108\u4e00\u822c\u3002 \u3000\u3000\u90a3\u9ad8\u5927\u50e7\u4eba\u8d70\u5230\u5b89\u5c0f\u840d\u8eab\u524d\uff0c\u5ffd\u7136\u5927\u559d\u4e00\u58f0\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\uff0c\u4f60\u662f\u81ea\u884c\u53d7\u7f1a\uff0c\u8fd8\u662f\u7531\u6d12\u5bb6\u4eb2\u81ea\u52a8\u624b\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6251\u54e7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5e72\u5417\uff1f\u4f60\u548c\u5c1a\u8981\u8ddf\u6211\u52a8\u624b\u5417\uff1f\u201d \u3000\u3000\u9ad8\u5927\u7684\u548c\u9ad8\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u559d\u9053\uff1a\u201c\u6d12\u5bb6\u4e0d\u8ddf\u4f60\u800d\u5634\u76ae\u5b50\uff01\u4f60\u5982\u60f3\u5c11\u5403\u70b9\u82e6\u5934\uff0c\u6700\u597d\u662f\u81ea\u884c\u53d7\u7f1a\u5427\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u683c\u683c\u4e00\u7b11\u9053\uff1a\u201c\u548c\u5c1a\uff0c\u4f60\u5728\u4f5c\u68a6\uff01\u201d \u3000\u3000\u90a3\u9ad8\u5927\u7684\u548c\u5c1a\u5411\u8eab\u65c1\u7684\u90a3\u540d\u50e7\u4eba\u6446\u4e86\u6446\u624b\uff0c\u4e24\u4eba\u5ffd\u7136\u95ea\u8eab\u4e0a\u524d\uff0c\u5411\u5b89\u5c0f\u840d\u903c\u4e86\u8fc7\u6765\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u7ad9\u5728\u5b89\u5c0f\u840d\u8eab\u65c1\uff0c\u4f46\u4ed6\u5e76\u65e0\u51fa\u624b\u4e4b\u610f\uff0c\u539f\u6765\u4ed6\u5012\u771f\u60f3\u770b\u770b\u5b89\u5c0f\u840d\u7684\u6b66\u529f\u7a76\u7adf\u5982\u4f55\uff01 \u3000\u3000\u4e24\u540d\u50e7\u4fa3\u7a81\u7136\u76f8\u88ad\uff0c\u5b89\u5c0f\u840d\u5168\u6ca1\u6709\u5f53\u4f5c\u4e00\u4f1a\u4e8b\uff0c\u5979\u5fae\u5fae\u4e00\u7b11\uff0c\u5bb9\u5f97\u4e8c\u50e7\u51fa\u624b\u5206\u6293\u81ea\u5df1\u4e24\u81c2\u4e4b\u9645\uff0c\u8fd9\u624d\u54af\u54af\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4e2a\u548c\u5c1a\u771f\u4e0d\u77e5\u7f9e\uff0c\u7ed9\u6211\u6eda\u5f00\u4e9b\u2026\u2026\u201d\u53ea\u89c1\u5979\u53cc\u80a9\u8f7b\u8f7b\u4e00\u6447\uff0c\u90a3\u4e24\u540d\u50e7\u4fa3\u987f\u65f6\u5982\u906d\u91cd\u51fb\uff0c\u8dcc\u8dcc\u649e\u649e\u5730\u5411\u5916\u5012\u7a9c\u800c\u53bb\u3002 \u3000\u3000\u4e24\u50e7\u7684\u53f3\u624b\u4ff1\u7686\u4e0b\u5782\uff0c\u663e\u7136\u662f\u8d1f\u4e86\u91cd\u4f24\uff01 \u3000\u3000\u970d\u9e23\u98ce\u7779\u72b6\u5927\u5403\u4e00\u60ca\uff0c\u559d\u9053\uff1a\u201c\u8fd9\u4f4d\u8001\u5f1f\u597d\u5f3a\u7684\u7f61\u6c14\u5de5\u592b\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u5e2e\u4e3b\u8fc7\u5956\uff01\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u90a3\u7c97\u58ee\u7684\u50e7\u4eba\u8138\u8272\u4e00\u53d8\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\uff0c\u4f60\u95ef\u4e0b\u5927\u7978\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4ec0\u4e48\u5927\u7978\uff1f\u4ed6\u4eec\u60f3\u4f24\u6211\uff0c\u5374\u53c8\u6ca1\u6709\u90a3\u7b49\u6b66\u529f\uff0c\u5728\u4e0b\u8fde\u624b\u90fd\u6ca1\u62ac\uff0c\u96be\u9053\u8fd8\u8981\u602a\u6211\u5417\uff1f\u201d \u3000\u3000\u5d14\u5927\u516c\u4e5f\u63a5\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5c11\u6797\u5bfa\u5353\u7acb\u6b66\u6797\uff0c\u7d20\u6765\u6709\u7740\u535a\u5927\u8c26\u51b2\u7684\u6c14\u5ea6\uff0c\u4eca\u65e5\u4e00\u89c1\uff0c\u5374\u6559\u8001\u592b\u5f97\u77e5\uff0c\u539f\u6765\u5b8c\u5168\u4e0d\u662f\u90a3\u4e48\u4e00\u56de\u5b50\u4e8b\u2026\u2026\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u5411\u90a3\u7c97\u50e7\u559d\u9053\uff1a\u201c\u548c\u5c1a\uff0c\u4f60\u7684\u4e24\u4f4d\u540c\u4f4d\u5df2\u88ab\u8fd9\u4f4d\u5b89\u5c0f\u5f1f\u7f61\u6c14\u9707\u4f24\uff0c\u82e5\u4e0d\u8d76\u5feb\u56de\u5bfa\u8c03\u6cbb\uff0c\u90a3\u4e24\u6761\u80f3\u818a\u53ef\u5c31\u62a5\u5e9f\u4e86\uff01\u201d \u3000\u3000\u7c97\u3001\u7626\u4e8c\u50e7\u4f3c\u662f\u6709\u4e9b\u4e0d\u4fe1\uff0c\u662f\u6545\u8fdf\u7591\u672a\u7b54\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u95fb\u8a00\uff0c\u5374\u662f\u7b11\u9053\uff1a\u201c\u5d14\u8001\u524d\u8f88\uff0c\u4ed6\u4eec\u4e0d\u662f\u4f24\u5728\u7f61\u6c14\u53cd\u9707\u4e4b\u4e0b\uff0c\u518d\u8bf4\uff0c\u665a\u8f88\u6839\u672c\u4e0d\u66fe\u7ec3\u5c31\u62a4\u8eab\u7f61\u6c14\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5148\u524d\u4e5f\u53ea\u9053\u5b89\u5c0f\u840d\u7ec3\u5c31\u4e86\u7f61\u6c14\uff0c\u8fd9\u65f6\u4e00\u542c\uff0c\u4e0d\u7531\u7b11\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u4f60\u662f\u600e\u751f\u4f24\u4e86\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u6211\u7684\u79d8\u5bc6\uff0c\u5982\u662f\u6362\u4e86\u4ed6\u4eba\u95ee\u53ca\uff0c\u6211\u662f\u4e0d\u4f1a\u56de\u7b54\u7684\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5144\u5f1f\u2026\u2026\u4f60\u5982\u662f\u4e0d\u4fbf\u8bf4\u51fa\uff0c\u90a3\u5c31\u7b97\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u65b9\u5144\u65e2\u7136\u662f\u4f60\u76f8\u8be2\uff0c\u6211\u5f53\u7136\u8981\u7167\u5b9e\u8bf4\u5c06\u51fa\u6765\u7684\u4e86\u2026\u2026\u201d\u5979\u987f\u4e86\u987f\u8bdd\u97f3\uff0c\u63a5\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u6211\u53ea\u4e0d\u8fc7\u7528\u90a3\u5f39\u6307\u70b9\u7a74\u7684\u65b9\u6cd5\uff0c\u70b9\u4e86\u4ed6\u4eec\u7a74\u9053\u800c\u5df2\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\u4e00\u6014\uff0c\u970d\u9e23\u98ce\u5fcd\u4e0d\u4f4f\u5927\u58f0\u9053\uff1a\u201c\u5b89\u8001\u5f1f\uff0c\u8001\u592b\u6ca1\u6709\u77a7\u89c1\u4f60\u51fa\u624b\u554a\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u665a\u8f88\u53cc\u624b\u85cf\u5728\u8896\u4e2d\uff0c\u66f2\u6307\u4e00\u5f39\uff0c\u51e0\u4f4d\u81ea\u7136\u662f\u77a7\u4e0d\u89c1\u7684\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u7eb5\u7136\u4e0d\u4fe1\uff0c\u5374\u4e5f\u4e0d\u4fbf\u95ee\u4e0b\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e5f\u5728\u5fc3\u4e2d\u5c06\u60c5\u5c06\u7591\uff0c\u53ea\u662f\uff0c\u4ed6\u7701\u5f97\u5728\u8fd9\u4e48\u591a\u4eba\u4e4b\u524d\uff0c\u5b89\u5c0f\u840d\u5982\u662f\u4e0d\u60f3\u8bf4\u51fa\u8fd9\u79cd\u6b66\u529f\uff0c\u90a3\u4e5f\u662f\u4eba\u60c5\u4e4b\u5e38\uff0c\u5f53\u4e0b\u5fae\u5fae\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u8fd9\u4e24\u4f4d\u50e7\u4eba\u7684\u4f24\u52bf\uff0c\u53ef\u662f\u5e76\u4e0d\u4e25\u91cd\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4e0d\u91cd\uff0c\u4e24\u4e2a\u65f6\u8fb0\u4e4b\u540e\uff0c\u7a74\u9053\u4fbf\u53ef\u81ea\u89e3\uff0c\u4e0d\u8fc7\u2026\u2026\u201d\u5979\u5ffd\u7136\u610f\u5473\u6df1\u957f\u5730\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5982\u662f\u65e0\u4eba\u80fd\u591f\u80fd\u62cd\u89e3\u6211\u7684\u70b9\u7a74\u624b\u6cd5\uff0c\u4ed6\u4eec\u6700\u597d\u83ab\u8981\u5984\u60f3\u66ff\u4eba\u89e3\u7a74\uff01\u201d \u3000\u3000\u6562\u60c5\uff0c\u8fd9\u65f6\u90a3\u540d\u7626\u548c\u5c1a\u5df2\u7136\u5728\u542c\u5f97\u5b89\u5c0f\u840d\u8bf4\u662f\u4e8c\u50e7\u4eba\u53ea\u662f\u88ab\u5979\u70b9\u4e86\u7a74\u9053\uff0c\u4fbf\u8d70\u8fc7\u53bb\uff0c\u60f3\u62cd\u6d3b\u4e8c\u50e7\u7a74\u9053\u3002\u4f46\u4ed6\u624b\u672a\u62ac\u8d77\uff0c\u5b89\u5c0f\u840d\u7684\u540e\u9762\u51e0\u53e5\u8bdd\u97f3\u5df2\u7136\u5165\u8033\u3002\u987f\u65f6\uff0c\u4ed6\u8fc5\u901f\u7684\u5411\u9000\u4e86\u4e24\u6b65\u3002 \u3000\u3000\u90a3\u7c97\u58ee\u7684\u50e7\u4eba\u76b1\u4e86\u76b1\u7709\uff0c\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\uff0c\u4f60\u8bf4\u4e24\u4e2a\u65f6\u8fb0\u4e4b\u540e\uff0c\u7a74\u9053\u81ea\u89e3\uff0c\u8fd9\u662f\u771f\u7684\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4fe1\u4e0d\u4fe1\uff0c\u90a3\u662f\u4f60\u7684\u4e8b\u4e86\uff01\u201d \u3000\u3000\u90a3\u7c97\u58ee\u7684\u548c\u5c1a\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\u8fd9\u7b49\u53e3\u6c14\uff0c\u662f\u4e0d\u628a\u6211\u5c11\u6797\u5f1f\u5b50\u653e\u5728\u773c\u4e2d\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u662f\u53c8\u5982\u4f55\uff1f\u4f60\u53ef\u662f\u4e5f\u5c1d\u5c1d\u5728\u4e0b\u5f39\u7a74\u624b\u6cd5\u7684\u5473\u9053\uff1f\u201d \u3000\u3000\u90a3\u50e7\u4eba\u88ab\u5979\u8bf4\u5f97\u5341\u5206\u96be\u582a\uff0c\u7709\u4e00\u626c\uff0c\u6b63\u8981\u8bf4\u8bdd\uff0c\u5409\u5a01\u5ffd\u7136\u51b7\u7b11\u9053\uff1a\u201c\u548c\u5c1a\uff0c\u4f60\u6700\u597d\u5fcd\u4e00\u53e3\u6c14\uff0c\u5426\u5219\uff0c\u4f60\u4eec\u56db\u4f4d\u5168\u90fd\u2026\u2026\uff01\u201d \u3000\u3000\u90a3\u672a\u66fe\u53d7\u4f24\u7684\u4e8c\u50e7\u95fb\u8a00\uff0c\u679c\u7136\u4e0d\u6562\u518d\u5411\u5b89\u5c0f\u840d\u591a\u8bf4\u4ec0\u4e48\uff0c\u53ea\u8baa\u8baa\u4e00\u7b11\uff0c\u6321\u5728\u4e2d\u5929\u798f\u5730\u95e8\u5de8\u574a\u4e4b\u4e0b\uff0c\u4e0d\u8ba9\u51e0\u4eba\u8fc7\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u7779\u72b6\uff0c\u6709\u4e9b\u607c\u6012\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4e24\u4f4d\u5927\u5e08\uff01\u201d \u3000\u3000\u4e8c\u50e7\u9f50\u9f50\u5e94\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4f60\u4eec\u8fd9\u7b49\u4e0d\u8a00\u4e0d\u8bed\u5730\u6321\u4f4f\u53bb\u8def\uff0c\u4e0d\u5acc\u6709\u5931\u5c11\u6797\u5b50\u5f1f\u7684\u8eab\u4efd\u5417\uff1f\u201d \u3000\u3000\u4e8c\u50e7\u95fb\u8a00\uff0c\u4e92\u671b\u4e00\u773c\uff0c\u5f53\u4e0b\u7531\u90a3\u7626\u7684\u4e00\u4e2a\u63a5\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\uff0c\u8d2b\u50e7\u7b49\u5949\u547d\u5728\u6b64\u9547\u5b88\uff0c\u4e0d\u8bb8\u4efb\u4f55\u4eba\u8fc7\u53bb\uff0c\u7eb5\u7136\u5c0f\u65bd\u4e3b\u7528\u90a3\u6fc0\u5c06\u4e4b\u6cd5\uff0c\u8d2b\u50e7\u7b49\u4e5f\u65e0\u6240\u8c13\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u597d\u554a\uff0c\u4ed6\u4eec\u60f3\u6492\u8d56\u2026\u2026\u201d\u4f46\u4ed6\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u4e24\u4f4d\u5927\u5e08\u5012\u662f\u6709\u6db5\u517b\u7684\u5f88\uff01\u53ea\u662f\u5728\u4e0b\u5374\u6ca1\u6709\u4e24\u4f4d\u8fd9\u7b49\u8010\u6027\u2026\u2026\u201d \u3000\u3000\u9661\u7136\u6b3a\u8eab\u4e0a\u524d\uff0c\u63a2\u624b\u95ee\u90a3\u7c97\u58ee\u7684\u50e7\u4eba\u6293\u53bb\uff0c\u559d\u9053\uff1a\u201c\u65e2\u7136\u597d\u8bf4\u4e0d\u884c\uff0c\u5728\u4e0b\u53ea\u6709\u5f97\u7f6a\u4e86\uff01\u201d \u3000\u3000\u90a3\u50e7\u4eba\u89c1\u65b9\u96ea\u5b9c\u66f2\u6307\u6293\u5411\u81ea\u5df1\u8109\u95e8\uff0c\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u53cd\u624b\u5012\u6263\u65b9\u96ea\u5b9c\u53f3\u8155\uff01 \u3000\u3000\u51fa\u624b\u5230\u4e5f\u5f88\u6709\u51e0\u6210\u706b\u5019\u3002\u53ea\u662f\uff0c\u4ed6\u5f04\u9519\u5bf9\u4e8e\uff0c\u65b9\u96ea\u5b9c\u6293\u51fa\u4e4b\u52bf\u672a\u53d8\uff0c\u53ea\u662f\u5411\u4e0a\u4e00\u573a\uff0c\u6070\u597d\u6309\u5728\u90a3\u50e7\u4eba\u7684\u7709\u7a74\u4e0a\u3002 \u3000\u3000\u90a3\u50e7\u4eba\u8138\u8272\u5927\u53d8\uff0c\u95ea\u8eab\u5411\u540e\u4fbf\u9000\u3002 \u3000\u3000\u4f46\u65b9\u96ea\u5b9c\u5df2\u7136\u5b58\u4e0b\u8981\u786c\u95ef\u4e4b\u5fc3\uff0c\u81ea\u7136\u4e0d\u4f1a\u5bb9\u4ed6\u8ba9\u5f97\u5f00\u53bb\uff0c\u5ffd\u542c\u4ed6\u6c89\u58f0\u559d\u9053\uff1a\u201c\u7ad9\u4f4f\u3002\u201d \u3000\u3000\u53f3\u624b\u6307\u529b\u7565\u53d1\uff0c\u90a3\u50e7\u4eba\u679c\u771f\u50f5\u7acb\u5f53\u5730\uff0c\u8fde\u52a8\u4e5f\u4e0d\u52a8\u4e86\uff01 \u3000\u3000\u7626\u5c0f\u7684\u50e7\u4eba\uff0c\u4f3c\u662f\u672a\u66fe\u6599\u5230\u65b9\u96ea\u5b9c\u6b66\u529f\u8fd9\u7b49\u4e86\u5f97\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u624d\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\u679c\u7136\u6709\u7740\u8fc7\u4eba\u7684\u80fd\u8010\uff0c\u4f46\u8d2b\u50e7\u4e0d\u4fe1\u4f60\u80fd\u8fdb\u5f97\u4e86\u5c11\u6797\u2026\u2026\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5ffd\u7136\u4e00\u62f3\u51fb\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u51fa\u6307\u70b9\u4e86\u90a3\u7c97\u58ee\u50e7\u4eba\u7684\u7a74\u9053\uff0c\u5fc3\u4e2d\u5c31\u5df2\u731c\u5230\u90a3\u7626\u5c0f\u7684\u548c\u5c1a\u4e0d\u4f1a\u5750\u89c6\uff0c\u5bf9\u65b9\u4e3e\u529b\u752b\u53d1\uff0c\u65b9\u96ea\u5b9c\u5df2\u53f3\u624b\u4e00\u6325\uff0c\u56de\u51fb\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u638c\u867d\u7136\u8f7b\u63cf\u6de1\u5199\u4e00\u822c\u62cd\u51fa\uff0c\u4f46\u529b\u9053\u4e4b\u5f3a\uff0c\u5374\u662f\u90a3\u7626\u5c0f\u7684\u50e7\u4eba\u751f\u5e73\u6240\u4ec5\u89c1\uff01 \u3000\u3000\u53ea\u542c\u5f97\u90a3\u7626\u548c\u5c1a\u5389\u543c\u4e00\u58f0\uff0c\u5f20\u53e3\u5410\u51fa\u4e00\u53e3\u9c9c\u8840\uff0c\u549a\u7684\u4e00\u58f0\uff0c\u5012\u5411\u5730\u4e0a\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u56de\u987e\u4e86\u970d\u9e23\u98ce\u4e00\u773c\uff0c\u9053\uff1a\u201c\u970d\u5e2e\u4e3b\uff0c\u54b1\u4eec\u4e0d\u7528\u4ed6\u4eec\u62a5\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u4e0d\u7b49\u970d\u9e23\u98ce\u7b54\u8bdd\uff0c\u5927\u6b65\u5411\u5c11\u6797\u5c71\u95e8\u884c\u53bb \u3000\u3000\u970d\u9e23\u98ce\u4f3c\u662f\u6599\u4e0d\u5230\u65b9\u96ea\u5b9c\u51fa\u624b\u7adf\u6709\u8fd9\u822c\u5a01\u529b\uff0c\u6014\u5f97\u4e00\u6014\uff0c\u65b9\u59cb\u53f9\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u8fd9\u638c\u529b\u4e4b\u5f3a\uff0c\u771f\u53eb\u8001\u673d\u53f9\u4e3a\u89c2\u6b62\u2026\u2026\u201d \u3000\u3000\u8bed\u97f3\u5fae\u5fae\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u54b1\u4eec\u786c\u95ef\u4e2d\u5929\u798f\u5730\u95e8\uff0c\u53ea\u6015\u90a3\u5c11\u6797\u5bfa\u7684\u548c\u5c1a\uff0c\u4f1a\u8981\u6fc0\u8d77\u4f17\u6012\u4e86\uff01\u5c11\u65f6\u5982\u662f\u5c11\u6797\u50e7\u4eba\u8d23\u95ee\u6b64\u4e8b\uff0c\u5c0f\u5144\u5f1f\u4e0d\u7528\u63d2\u53e3\uff0c\u5168\u7531\u8001\u673d\u62c5\u4ee3\u5982\u4f55\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u5251\u7709\u66b4\u626c\uff0c\u7b11\u9053\uff1a\u201c\u5e2e\u4e3b\uff0c\u665a\u8f88\u6b64\u6765\u867d\u662f\u76f8\u52a9\u5c11\u6797\uff0c\u4f46\u665a\u8f88\u5e76\u975e\u6015\u4e8b\u4e4b\u4eba\uff0c\u5e2e\u4e3b\u4e00\u7247\u597d\u610f\uff0c\u665a\u8f88\u5fc3\u9886\u4e86\uff01\u201d \u3000\u3000\u663e\u7136\u4ed6\u662f\u62d2\u7edd\u4e86\u970d\u9e23\u51e4\u63fd\u4e8b\u4e4b\u5fc3\uff0c\u970d\u9e23\u51e4\u4e5f\u4e0d\u4fbf\u518d\u884c\u575a\u6301\uff0c\u53ea\u597d\u4e00\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\u4e0e\u90a3\u5c11\u6797\u76f8\u8bc6\u4e4b\u4eba\u4e0d\u591a\uff0c\u8001\u673d\u5149\u662f\u76f8\u514d\u53bb\u5f7c\u6b64\u4ec7\u89c6\u4e4b\u5fc3\u800c\u5df2\uff01\u65e2\u7136\u5c0f\u5144\u5f1f\u5982\u6b64\u8c6a\u8fc8\uff0c\u8001\u673d\u53c8\u80fd\u518d\u8bf4\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u7b11\u58f0\u4e00\u655b\uff0c\u4e3e\u6b65\u8fce\u8d8b\u5c11\u6797\u5c71\u95e8\u3002 \u3000\u3000\u5409\u5a01\u7ecf\u8fc7\u90a3\u8eba\u5728\u5730\u4e0a\u7684\u7626\u5c0f\u50e7\u4eba\u8eab\u7554\u4e4b\u65f6\uff0c\u5374\u6253\u6000\u4e2d\u638f\u51fa\u4e00\u9897\u4e13\u7597\u5185\u4f24\u7684\u4e39\u4e38\uff0c\u5582\u4e86\u4ed6\u5403\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u8fd9\u7b49\u4e3e\u52a8\uff0c\u53ea\u770b\u5f97\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u5927\u4e3a\u611f\u52a8\uff0c\u6697\u9053\uff1a\u201c\u4f11\u77a7\u8fd9\u5409\u5a01\u9762\u8272\u51b7\u9177\u8a00\u8bed\u68ee\u5389\uff0c\u4f46\u4ed6\u5fc3\u5730\u6148\u5584\uff0c\u53ef\u662f\u8bb8\u591a\u6b66\u6797\u4eba\u7269\u96be\u53ca\u554a\u3002\u201d \u3000\u3000\u5bfb\u601d\u95f4\uff0c\u8033\u4e2d\u542c\u5f97\u5b89\u5c0f\u840d\u8f7b\u8f7b\u4e00\u7b11\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u7ad9\u5728\u8fd9\u513f\u53d1\u7684\u4ec0\u4e48\u5446\uff1f\u4ed6\u4eec\u90fd\u8d70\u5566\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e3e\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u5b8b\u5b5a\u548c\u4e10\u5e2e\u5e2e\u4e3b\u7b49\u4eba\uff0c\u5df2\u7136\u8d70\u7684\u5f88\u8fdc\uff0c\u4e0d\u7981\u54d1\u7136\u5931\u7b11\u9053\uff1a \u3000\u3000\u201c\u8d24\u5f1f\uff0c\u54b1\u4eec\u5feb\u8ffd\u4e0a\u4ed6\u4eec\uff0c\u4e00\u5757\u5165\u5bfa\u2026\u2026\u201d \u3000\u3000\u653e\u6b65\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u7d27\u7d27\u5730\u8e09\u5728\u4ed6\u8eab\u65c1\uff0c\u53e3\u4e2d\u4f4e\u58f0\u9053\uff0c\u201c\u65b9\u5144\uff0c\u6211\u597d\u8c61\u542c\u5a18\u8bf4\u8fc7\uff0c\u90a3\u5c11\u6797\u5bfa\u4e0d\u8bb8\u5973\u4eba\u5165\u5185\uff0c\u4e0d\u77e5\u662f\u4e0d\u662f\u771f\u7684\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u88ab\u4ed6\u95ee\u5f97\u4e00\u5446\u3002\u6562\u60c5\uff0c\u8fd9\u4e8b\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u660e\u767d\u3002 \u3000\u3000\u5f53\u4e0b\u76b1\u7709\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4f60\u773c\u4e0b\u7a7f\u7684\u662f\u7537\u4eba\u8863\u7269\u4ed6\u4eec\u7eb5\u7136\u6709\u4e9b\u89c4\u5b9a\uff0c\u90a3\u4e5f\u662f\u4e0d\u8981\u7d27\u7684\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u65b9\u5144\uff0c\u6211\u6015\u4ed6\u4eec\u8ba4\u5f97\u51fa\u6765\u5440\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u5934\u9053\uff1a\u201c\u90a3\u600e\u4e48\u4f1a\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6c89\u541f\u4e0d\u8bed\uff0c\u4e24\u4eba\u5df2\u7136\u62b5\u8fbe\u5c71\u95e8\u4e4b\u524d\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5c11\u6797\u5bfa\u7684\u4e24\u6247\u5c71\u95e8\uff0c\u6b63\u7f13\u7f13\u5730\u6253\u4e86\u5f00\u6765\u3002 \u3000\u3000\u4e3e\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u4e00\u4f4d\u9ad8\u5927\u7684\u767d\u53d1\u8001\u50e7\uff0c\u5f53\u95e8\u800c\u7acb\u3002 \u3000\u3000\u5d14\u5927\u516c\u7ad9\u5728\u5b8b\u5b5a\u4e4b\u524d\uff0c\u4e00\u89c1\u90a3\u8001\u548c\u5c1a\u51fa\u73b0\uff0c\u7acb\u5373\u53cc\u624b\u62b1\u62f3\uff0c\u6717\u58f0\u9053\uff1a\u201c\u5927\u5e08\u8bf7\u4e86\uff01\u201d \u3000\u3000\u8001\u50e7\u5408\u638c\u5f53\u80f8\uff0c\u9053\uff1a\u201c\u8001\u65bd\u4e3b\uff0c\u9002\u624d\u53ef\u662f\u51e0\u4f4d\u5728\u8fd9\u91cc\u5927\u58f0\u55a7\u95f9\u5417\uff1f\u201d \u3000\u3000\u5d14\u5927\u516c\u9053\uff1a\u201c\u4e0d\u9519\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5728\u65c1\uff0c\u5fc3\u4e2d\u6709\u4e9b\u4e0d\u89e3\uff0c\u6697\u9053\uff1a\u201c\u600e\u6837\u4ed6\u4eec\u90fd\u4e0d\u8ba4\u8bc6\u8fd9\u4f4d\u8001\u548c\u5c1a\u5462\uff1f\u201d \u3000\u3000\u539f\u6765\u4ed6\u4ece\u970d\u9e23\u98ce\u53e3\u6c14\u4e4b\u4e2d\uff0c\u4eff\u4f5b\u542c\u51fa\u4ed6\u4eec\u5bf9\u4e8e\u5c11\u6797\u50e7\u4eba\uff0c\u76f8\u8bc6\u7684\u4e0d\u5c11\uff0c\u4f46\u6b64\u523b\u51fa\u73b0\u7684\u50e7\u4eba\uff0c\u663e\u7136\u4ed6\u4eec\u4e0d\u719f\u6089\uff0c\u5fc3\u4e2d\u81ea\u662f\u96be\u514d\u5947\u602a\u4e86\u3002 \u3000\u3000\u90a3\u9ad8\u5927\u7684\u8001\u50e7\u770b\u4e86\u5d14\u5927\u516c\u4e00\u773c\uff0c\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u8001\u65bd\u4e3b\uff0c\u4f60\u53ef\u77e5\u6653\u662f\u4ec0\u4e48\u6240\u5728\uff1f\u201d \u3000\u3000\u5d14\u5927\u516c\u95fb\u8a00\u4f3c\u662f\u4e00\u6014\uff0c\u9053\uff1a\u201c\u5c11\u6797\u5bfa\u554a\uff01\u8001\u592b\u5f53\u7136\u77e5\u9053\u4e86\uff01\u201d \u3000\u3000\u767d\u53d1\u8001\u50e7\u5408\u5341\u9053\uff1a\u201c\u8001\u65bd\u4e3b\u65e2\u7136\u77e5\u9053\u8fd9\u513f\u4e43\u662f\u5c11\u6797\u7985\u5bfa\uff0c\u600e\u6562\u5982\u6b64\u653e\u8086\uff1f\u201d \u3000\u3000\u5d14\u5927\u516c\u9053\uff1a\u201c\u8001\u592b\u653e\u8086\u4e86\u5417\uff1f\u201d \u3000\u3000\u8001\u50e7\u9053\uff1a\u201c\u5486\u54ee\u5c71\u95e8\uff0c\u8fd8\u80fd\u4e0d\u662f\u653e\u8086\u5417\uff1f\u201d \u3000\u3000\u5d14\u5927\u516c\u5ffd\u7136\u5475\u5475\u5927\u7b11\u9053\uff1a\u201c\u5927\u5e08\u53ef\u662f\u5c11\u6797\u50e7\u4fa3\uff1f\u201d \u3000\u3000\u679c\u7136\uff0c\u5d14\u5927\u516c\u4e0d\u8ba4\u5f97\u4ed6\uff01 \u3000\u3000\u770b\u6837\u5b50\uff0c\u8fde\u970d\u9e23\u98ce\u3001\u5b8b\u5b5a\u4e5f\u90fd\u4e0d\u8ba4\u8bc6\u4ed6\u3002 \u3000\u3000\u90a3\u9ad8\u5927\u7684\u50e7\u4eba\u54c8\u54c8\u63a5\u9053\uff1a\u201c\u8001\u8872\u81ea\u7136\u662f\u5c11\u6797\u95e8\u4e0b\u4e86\uff01\u5426\u5219\uff0c\u600e\u4f1a\u5728\u8fd9\u513f\u8ddf\u51e0\u4f4d\u8bb2\u8bdd\u3002\u201d \u3000\u3000\u5d14\u5927\u516c\u9053\uff1a\u201c\u5927\u5e08\u9762\u751f\u5f97\u5f88\uff0c\u8001\u592b\u4e0e\u5c11\u6797\u50e7\u4fa3\u76f8\u8bc6\u7684\u6781\u591a\uff0c\u600e\u7684\u4ece\u6765\u672a\u89c1\u8fc7\u5927\u5e08\u5462\uff1f\u201d \u3000\u3000\u8001\u50e7\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8001\u8872\u5411\u6765\u504f\u5c45\u84b2\u7530\u4e0b\u9662\uff0c\u5f88\u5c11\u524d\u6765\u5d69\u5c71\uff0c\u8bf8\u4f4d\u4e0d\u8bc6\uff0c\u4e5f\u65e0\u751a\u60ca\u5947\u4e4b\u5904\uff01\u201d \u3000\u3000\u5d14\u5927\u516c\u604d\u7136\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u8fd9\u65f6\u5ffd\u7136\u4e0a\u524d\u4e00\u6b65\u9053\uff1a\u201c\u5927\u5e08\u600e\u4e48\u79f0\u547c\uff1f\u201d \u3000\u3000\u8001\u50e7\u770b\u4e86\u5b8b\u5b5a\u4e00\u773c\uff0c\u9053\uff1a\u201c\u8001\u8872\u5165\u5723\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u95fb\u8a00\uff0c\u5446\u4e86\u5446\uff0c\u9053\uff1a\u201c\u5165\u5723\u5927\u5e08\uff1f\u201d \u3000\u3000\u8001\u50e7\u9053\uff1a\u201c\u6b63\u662f\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u90a3\u5165\u4e91\u638c\u95e8\u4eba\u662f\u5927\u5e08\u7684\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u8001\u5055\u9053\uff1a\u201c\u638c\u95e8\u4eba\u4e43\u662f\u8001\u8872\u7684\u4e09\u5e08\u5f1f\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u8fde\u5fd9\u62b1\u62f3\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u5927\u5e08\u5c31\u662f\u90a3\u4f4d\u6614\u5e74\u4e3a\u4e86\u900a\u8ba9\u638c\u95e8\u4e4b\u4f4d\uff0c\u5047\u6b7b\u9041\u4e16\u7684\u9ad8\u50e7\u4e86\uff1f\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u76ee\u5149\u4e00\u9eef\uff0c\u4f3c\u662f\u6709\u7740\u65e0\u9650\u611f\u89e6\u5730\u4f4e\u58f0\u9053\uff1a\u201c\u5f80\u4e8b\u5df2\u77e3\uff0c\u8001\u65bd\u4e3b\u63d0\u5b83\u4f5c\u751a\uff1f\u201d \u3000\u3000\u5ffd\u7136\u95f4\u4f3c\u662f\u60f3\u8d77\u4ec0\u4e48\uff0c\u7565\u4e00\u6c89\u541f\uff0c\u63a5\u9053\uff1a\u201c\u8001\u65bd\u4e3b\u5c0a\u59d3\u5927\u540d\uff0c\u600e\u77e5\u8001\u8872\u5f53\u5e74\u5f80\u4e8b\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u533a\u533a\u5b8b\u5b5a\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u60f3\u4e86\u4e00\u60f3\uff0c\u9053\uff1a\u201c\u8001\u8872\u81ea\u5e7c\u51fa\u5bb6\uff0c\u867d\u7136\u4fa7\u8eab\u5e08\u95e8\uff0c\u4f46\u5374\u4ece\u672a\u4e0e\u6b66\u6797\u4eba\u7269\u6765\u5f80\uff0c\u5f53\u5e74\u4e0d\u6562\u63a5\u4efb\u638c\u95e8\uff0c\u4e5f\u6b63\u662f\u4e3a\u4e86\u8fd9\u7b49\u7f18\u6545\uff0c\u5b8b\u65bd\u4e3b\u65e2\u77e5\u6614\u5e74\u4e4b\u4e8b\uff0c\u5b9a\u7136\u662f\u6b66\u6797\u4e2d\u5927\u6709\u6765\u5934\u7684\u4eba\u7269\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u5927\u5e08\u597d\u8bf4\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u624d\u660e\u767d\uff0c\u4e3a\u4f55\u4ed6\u4eec\u90fd\u4e0d\u8ba4\u8bc6\u8fd9\u4f4d\u81ea\u53d1\u8001\u50e7\uff0c\u539f\u6765\u8fd9\u4f4d\u5927\u5e08\u7adf\u7136\u4ece\u672a\u4e0e\u6b66\u6797\u4eba\u7269\u6765\u5f80\u8fc7\u3002 \u3000\u3000\u5b8b\u5b5a\u8bdd\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u6307\u7740\u8eab\u65c1\u7684\u5d14\u5927\u516c\uff0c\u63a5\u9053\uff1a\u201c\u5927\u5e08\uff0c\u8fd9\u4f4d\u662f\u4e10\u5e2e\u957f\u8001\u4e4c\u8863\u795e\u53df\u5d14\u5927\u516c\uff01\u201d \u3000\u3000\u5d14\u5927\u516c\u62b1\u62f3\u4e00\u63d6\u9053\uff1a\u201c\u5927\u5e08\u5f53\u5e74\u76db\u5fb7\uff0c\u5d14\u67d0\u8033\u95fb\u5df2\u4e45\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u5fae\u5fae\u4e00\u7b11\uff0c\u5408\u5341\u5ff5\u4f5b\u9053\uff1a\u201c\u8001\u8872\u4e43\u662f\u4e0b\u9a77\u4e4b\u6750\uff0c\u5047\u6b7b\u8ba9\u8d24\uff0c\u4e43\u662f\u5206\u5185\u4e4b\u4e8b\uff0c\u65bd\u4e3b\u518d\u63d0\u6b64\u4e8b\uff0c\u90a3\u662f\u8bda\u5fc3\u6316\u82e6\u8001\u8872\u4e86\uff01\u201d \u3000\u3000\u8bdd\u97f3\u5fae\u5fae\u4e00\u987f\uff0c\u76ee\u5149\u5728\u4f17\u4eba\u8eab\u4e0a\u63a0\u8fc7\uff0c\u65b9\u96ea\u5b9c\u53ea\u89c9\u4ed6\u4e24\u773c\u4e4b\u4e2d\uff0c\u522b\u6709\u4e00\u79cd\u6151\u4eba\u7684\u795e\u5149\uff0c\u4f7f\u4eba\u4e0d\u80fd\u903c\u89c6\uff0c\u4e0d\u7981\u9eef\u9053\uff1a\u201c\u8fd9\u548c\u5c1a\u597d\u7cbe\u5965\u7684\u5185\u529b\u7384\u529f\u2026\u2026\u201d \u3000\u3000\u8033\u4e2d\u542c\u5f97\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u8001\u65bd\u4e3b\uff0c\u8fd9\u51e0\u4f4d\u6a80\u6a3e\uff0c\u90fd\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5d14\u5927\u516c\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u672c\u5e2e\u5e2e\u4e3b\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4e10\u5e2e\u5e2e\u4e3b\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u62b1\u62f3\u9053\uff1a\u201c\u533a\u533a\u970d\u9e23\u98ce\uff0c\u89c1\u8fc7\u5927\u5e08\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u76b1\u7709\u9053\uff1a\u201c\u8fd9\u5c31\u5947\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u5927\u5e08\u6709\u4f55\u60ca\u8bb6\u4e0d\u7f6e\uff1f\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u672c\u5bfa\u65b9\u4e08\u7cbe\u5ba4\u4e4b\u5185\uff0c\u73b0\u6709\u4e00\u4f4d\u4e10\u5e2e\u5e2e\u4e3b\u5728\u5ea7\uff0c\u600e\u5730\u5c71\u95e8\u4e4b\u5916\uff0c\u53c8\u6709\u4e86\u4e00\u4f4d\u5e2e\u4e3b\u5462\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u62b1\u62f3\u9053\uff1a\u201c\u4e10\u5e2e\u5e2e\u4e3b\uff0c\u5929\u4e0b\u53ea\u6709\u4e00\u4f4d\uff0c\u90a3\u5c31\u662f\u4e0d\u624d\u970d\u67d0\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u8001\u8872\u6709\u4e9b\u7cca\u6d82\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u5bfa\u65b9\u4e08\u4e4b\u5185\uff0c\u4e43\u662f\u5047\u5192\u4e4b\u4eba\uff0c\u5927\u5e08\u4e0d\u8981\u88ab\u4ed6\u4eec\u8499\u9a97\u4e86\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u600e\u89c1\u5f97\u4ed6\u662f\u5047\u5192\uff1f\u8001\u65bd\u4e3b\uff1f\u8fd9\u4e8b\u8001\u8872\u53ef\u5206\u8fa8\u4e0d\u6e05\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u5446\u4e86\u4e00\u5446\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u5927\u5e08\u5230\u8ba4\u5b9a\u5728\u4e0b\u662f\u5047\u5192\u7684\u4e86\uff1f\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u9053\u4e3a\uff1a\u201c\u96be\u8bf4\u554a\uff01\u201d \u3000\u3000\u5409\u5a01\u95fb\u8a00\uff0c\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u5927\u5e08\u4e43\u662f\u5f97\u9053\u9ad8\u50e7\uff0c\u8fd9\u7b49\u8bdd\u4e5f\u80fd\u8bf4\u51fa\u53e3\u6765\uff0c\u5b9e\u5728\u53eb\u4eba\u5931\u671b\u5f97\u5f88\uff01\u201d \u3000\u3000\u5165\u5723\u76b1\u7709\u9053\uff1a\u201c\u65bd\u4e3b\u600e\u4e48\u79f0\u547c\uff1f\u201d \u3000\u3000\u5409\u5a01\u63a5\u9053\uff1a\u201c\u4e10\u5e2e\u5f1f\u5b50\u5409\u5a01\u3002\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u832b\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5409\u65bd\u4e3b\u5728\u4e10\u5e2e\u4e4b\u4e2d\uff0c\u662f\u4ec0\u4e48\u804c\u4f4d\uff1f\u201d \u3000\u3000\u5409\u5a01\u9053\uff1a\u201c\u56db\u5927\u957f\u8001\u4e4b\u4e00\u3002\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u5730\u4f4d\u5f88\u9ad8\u4e86\uff01\u201d \u3000\u3000\u5409\u5a01\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u53ea\u6015\u6bd4\u4e4b\u5927\u5e08\u5728\u5c11\u6797\u5bfa\u4e2d\u7684\u5730\u4f4d\uff0c\u8fd8\u8981\u5dee\u4e0a\u4e0d\u5c11\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u5408\u5341\u4e00\u7b11\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u65bd\u4e3b\u8fc7\u5956\u4e86\uff01\u201d \u3000\u3000\u8bed\u97f3\u5fae\u5fae\u4e00\u987f\uff0c\u5411\u970d\u9e23\u98ce\u9053\uff1a\u201c\u970d\u65bd\u4e3b\uff0c\u4f60\u662f\u5426\u4e10\u5e2e\u5e2e\u4e3b\u4e4b\u4e8b\uff0c\u751a\u662f\u5bb9\u6613\u89e3\u51b3\uff0c\u53ea\u662f\u90a3\u4f24\u4e86\u672c\u5bfa\u95e8\u4e0b\u5f1f\u5b50\u4e4b\u4eba\uff0c\u4e0d\u77e5\u662f\u8d35\u5e2e\u4e2d\u7684\u54ea\u4e00\u4f4d\u957f\u8001\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u4e24\u773c\u66b4\u7741\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u4f24\u4e86\u8d35\u5bfa\u5b50\u5f1f\u4e4b\u4eba\uff0c\u4fbf\u662f\u2026\u2026\u201d \u3000\u3000\u4ed6\u539f\u610f\u662f\u8981\u8bf4\u51fa\u4fbf\u662f\u970d\u67d0\uff0c\u4f46\u8bdd\u5230\u53e3\u8fb9\uff0c\u5ffd\u7136\u60f3\u8d77\u65b9\u96ea\u5b9c\u5148\u524d\u4ea4\u4ee3\u4e4b\u8bdd\uff0c\u4e0d\u7531\u5f97\u4fbf\u987f\u4e86\u4e00\u987f\uff0c\u6ca1\u6709\u8bf4\u5c06\u51fa\u6765\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u5fc3\u4e2d\u4e00\u9707\uff0c\u8fde\u5fd9\u63a5\u9053\uff1a\u201c\u5927\u5e08\uff0c\u4f24\u4e86\u8d35\u5bfa\u5b50\u5f1f\u7684\uff0c\u4e43\u662f\u5728\u4e0b\u3002\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u5446\u4e86\u4e00\u5446\uff0c\u76ee\u5149\u5728\u65b9\u96ea\u5b9c\u8eab\u4e0a\u8f6c\u52a8\u4e86\u534a\u664c\uff0c\u7b11\u9053\uff1a\u201c\u662f\u5c0f\u65bd\u4e3b\uff1f\u201d \u3000\u3000\u770b\u4ed6\u90a3\u795e\u60c5\uff0c\u5374\u662f\u5e76\u65e0\u591a\u5927\u654c\u610f\u3002\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b63\u662f\u5728\u4e0b\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\u662f\u90a3\u4e00\u6d3e\u5b50\u5f1f\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u5934\u9053\uff1a\u201c\u5728\u4e0b\u4e0d\u5c5e\u6b66\u6797\u4e5d\u5927\u95e8\u6d3e\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u4f3c\u662f\u6709\u4e9b\u4e0d\u4fe1\uff0c\u770b\u4e86\u970d\u9e23\u98ce\u4e00\u773c\uff0c\u9053\uff1a\u201c\u8fd9\u4f4d\u5c0f\u65bd\u4e3b\u5f53\u771f\u4e0d\u662f\u4e5d\u5927\u95e8\u6d3e\u7684\u5f1f\u5b50\u5417\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u4ed6\u5f53\u7136\u4e0d\u662f\u4e86\uff01\u4e0d\u8fc7\u970d\u67d0\u5018\u662f\u8bf4\u51fa\u4ed6\u7684\u5e08\u95e8\u6765\u5386\uff0c\u5927\u5e08\u5fc5\u7136\u662f\u77e5\u9053\u7684\u4e86\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u76b1\u7709\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u8001\u8872\u5bf9\u4e8e\u6b66\u6797\u4e2d\u7684\u4eba\u7269\u5168\u65e0\u4ea4\u5f80\uff0c\u65bd\u4e3b\u6b64\u8a00\uff0c\u53ea\u6015\u6709\u4e9b\u8fc7\u5206\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u9648\u5e0c\u6b63\u9648\u5927\u4fa0\uff0c\u5927\u5e08\u53ef\u66fe\u542c\u8bf4\u8fc7\uff1f\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u5ffd\u7136\u8138\u4e0a\u795e\u8272\u5927\u53d8\uff0c\u5408\u5341\u63a5\u9053\uff1a\u201c\u53ef\u5c31\u662f\u79f0\u4e3a\u5251\u795e\u7684\u9648\u5927\u4fa0\u5417\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u6ed4\u6ed4\u6d4a\u4e16\uff0c\u672c\u6765\u53ea\u6709\u4e00\u4f4d\u5251\u795e\u9648\u5e0c\u6b63\uff0c\u81ea\u662f\u4e0d\u4f1a\u518d\u6709\u522b\u4eba\u6562\u4e8e\u5047\u5192\u7684\u4e86\uff01\u201d \u3000\u3000\u8a00\u4e0b\u4e0d\u514d\u6709\u7740\u5fff\u5fff\u4e4b\u8272\u3002 \u3000\u3000\u5165\u5723\u5927\u5e08\u4e5f\u4e0d\u77e5\u542c\u4e0d\u51fa\u4ed6\u8bdd\u4e2d\u4e4b\u610f\uff0c\u8fd8\u662f\u6709\u5fc3\u88c5\u4f5c\u4e0d\u61c2\uff0c\u5408\u638c\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\uff0c\u4f60\u771f\u662f\u5251\u795e\u4f20\u4eba\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u62b1\u62f3\u9053\uff1a\u201c\u5251\u795e\u6b63\u662f\u5148\u5e08\uff0c\u5927\u5e08\u8ba4\u8bc6\u5417\uff1f\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u9648\u5927\u4fa0\u4e4b\u540d\uff0c\u8001\u8872\u5df2\u8033\u95fb\uff0c\u53ea\u662f\u672a\u66fe\u4e00\u7779\u829d\u989c\uff0c\u5f15\u4e3a\u7ec8\u8eab\u61be\u4e8b\u2026\u2026\u201d \u3000\u3000\u8bdd\u97f3\u4e00\u987f\uff0c\u7a81\u7136\u76b1\u7709\u4e00\u6014\uff0c\u63a5\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\uff0c\u9648\u5927\u4fa0\u4ed9\u901d\u4e86\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9eef\u7136\u9053\uff1a\u201c\u5148\u5e08\u5df2\u5728\u6708\u524d\u9053\u6210\u4ed9\u901d\u4e86\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u4e0d\u7531\u957f\u957f\u5730\u53f9\u606f\u4e86\u4e00\u9635\uff0c\u550f\u5618\u9053\uff1a\u201c\u8001\u8872\u65e0\u7f18\uff0c\u6b64\u884c\u7adf\u7136\u672a\u80fd\u4e00\u7779\u5251\u795e\u9762\u76ee\u2026\u2026\u201d\u987f\u4e86\u4e00\u987f\u8bdd\u97f3\uff0c\u63a5\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\uff0c\u4f60\u65e2\u662f\u5251\u795e\u4f20\u4eba\uff0c\u8bef\u4f24\u672c\u5bfa\u5b50\u5f1f\u4e4b\u4e8b\uff0c\u90a3\u5c31\u7b97\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u6697\u9053\uff1a\u201c\u5e08\u7236\u7684\u5a01\u52c7\u4ec1\u5fb7\uff0c\u679c\u7136\u535a\u5f97\u6b66\u6797\u5171\u540c\u656c\u4ef0\uff0c\u8fde\u5c11\u6797\u4e5f\u5982\u6b64\u770b\u91cd\u5e08\u7236\uff0c\u6211\u4eca\u540e\u53ef\u662f\u534a\u6b65\u4e0d\u80fd\u8d70\u9519\u4e86\u554a\uff01\u201d \u3000\u3000\u5f53\u4e0b\u53e3\u4e2d\u5374\u662f\u5341\u5206\u606d\u656c\u5730\u63a5\u9053\uff1a\u201c\u5927\u5e08\uff0c\u5728\u4e0b\u4e00\u65f6\u5931\u624b\uff0c\u5f00\u7f6a\u8d35\u5bfa\u4e4b\u5904\uff0c\u5728\u4e0b\u613f\u53d7\u8d23\u7f5a\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u89c1\u4ed6\u8fd9\u7b49\u8c26\u606d\uff0c\u663e\u7136\u751a\u662f\u611f\u52a8\uff0c\u5fd9\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\uff0c\u5c11\u6797\u5bfa\u89c4\u867d\u4e25\uff0c\u5c11\u6797\u5b50\u5f1f\u4e5f\u5411\u6765\u4e0d\u5bb9\u4ed6\u4eba\u6b3a\u8fb1\uff0c\u4f46\u672c\u5bfa\u5bf9\u4e8e\u5251\u795e\u9648\u5927\u4fa0\u751f\u524d\u6276\u6301\u6b66\u6797\u6b63\u4e49\u4e4b\u5fb7\uff0c\u5374\u662f\u4e07\u5206\u656c\u4f69\uff0c\u662f\u4ee5\u5c0f\u65bd\u4e3b\u7eb5\u6709\u89e6\u72af\u672c\u5bfa\u6212\u5f8b\u4e4b\u5904\uff0c\u90a3\u4e5f\u4e0d\u7528\u653e\u5728\u5fc3\u4e0a\u4e86\uff01\u201d \u3000\u3000\u4ed6\u4f3c\u662f\u4e0d\u60f3\u518d\u8c08\u6b64\u4e8b\uff0c\u76ee\u5149\u4e00\u8f6c\uff0c\u5411\u970d\u9e23\u98ce\u9053\uff1a\u201c\u65bd\u4e3b\uff0c\u4f60\u65e2\u4e0e\u5251\u795e\u4f20\u4eba\u8d70\u5728\u4e00\u8d77\uff0c\u60f3\u5fc5\u771f\u662f\u4e10\u5e2e\u5e2e\u4e3b\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u95fb\u8a00\uff0c\u6697\u9053\uff1a\u201c\u597d\u554a\uff0c\u8fd9\u548c\u5c1a\u5c45\u7136\u5bf9\u9648\u5927\u4fa0\u5982\u6b64\u4fe1\u670d\uff0c\u53ea\u6015\u5176\u4e2d\u5fc5\u6709\u7f18\u2026\u2026\u3002\u201d \u3000\u3000\u6b64\u65f6\u6b64\u523b\uff0c\u4ed6\u81ea\u7136\u4e0d\u597d\u8282\u5916\u751f\u679d\u5730\u518d\u95ee\u5176\u4ed6\uff0c\u5f53\u4e0b\u4e00\u7b11\u9053\uff1a\u201c\u5927\u5e08\u5012\u662f\u4f4d\u660e\u767d\u4eba\uff01 \u3000\u3000\u53ea\u662f\u90a3\u73b0\u5728\u8d35\u5bfa\u65b9\u4e08\u7cbe\u5ba4\u7684\u53e6\u4e00\u4f4d\u5047\u5192\u4e4b\u4eba\uff0c\u5927\u5e08\u7236\u53c8\u5c06\u5982\u4f55\u533a\u5904\u5462\uff1f\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u4f3c\u662f\u672a\u66fe\u6599\u5230\u970d\u9e23\u98ce\u6709\u6b64\u4e00\u95ee\uff0c\u987f\u65f6\u695e\u4e86\u4e00\u6123\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u8001\u8872\u2026\u2026 \u3000\u3000\u60f3\u638c\u95e8\u65b9\u4e08\u81ea\u80fd\u5b89\u7f6e\u4e8e\u4ed6\uff0c\u65bd\u4e3b\u4f3c\u662f\u7528\u4e0d\u7740\u62c5\u5fc3\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u7b11\u9053\uff1a\u201c\u5927\u5e08\uff0c\u970d\u67d0\u6709\u4e00\u53e5\u8bdd\uff0c\u8bf4\u5c06\u51fa\u6765\u5927\u5e08\u53ef\u83ab\u8981\u89c1\u602a\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u65bd\u4e3b\u8bf7\u8bb2\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u90a3\u5047\u5192\u970d\u67d0\u4e4b\u4eba\uff0c\u8d35\u5bfa\u867d\u7136\u4e0d\u60f3\u4e3a\u96be\u4e8e\u4ed6\uff0c\u4f46\u970d\u67d0\u5374\u4e0d\u4f1a\u653e\u8fc7\u4e8e\u4ed6\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u663e\u7136\u5bf9\u4e8e\u6c5f\u6e56\u4e0a\u7684\u8fc7\u8282\u77e5\u9053\u4e0d\u591a\uff0c\u95fb\u8a00\u76b1\u7709\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u5fc3\u4e2d\u6697\u6697\u5931\u7b11\uff0c\u53e3\u4e2d\u63a5\u9053\uff1a\u201c\u970d\u67d0\u8ba4\u5b9a\u90a3\u4eba\u4e43\u662f\u672c\u5e2e\u4e0d\u8096\u4e4b\u5f92\uff0c\u970d\u67d0\u8981\u63ed\u7a7f\u4ed6\u7684\u9762\u76ee\uff0c\u6cbb\u4ee5\u90a6\u89c4\uff0c\u800c\u656c\u6548\u5c24\u3002 \u3000\u3000\u5165\u5723\u5927\u5e08\u4f3c\u662f\u604d\u7136\u5927\u609f\uff0c\u9053\u201c\u662f\u554a\uff01\u8001\u8872\u600e\u7684\u672a\u66fe\u60f3\u5230\u5462\uff1f\u201d \u3000\u3000\u8baa\u8baa\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u8fd9\u662f\u8d35\u5e2e\u5185\u90e8\u4e4b\u4e8b\uff0c\u8001\u8872\u5f53\u7136\u4e0d\u4fbf\u8fc7\u95ee\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u542c\u4ed6\u5982\u6b64\u56de\u7b54\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u8fd9\u548c\u5c1a\u6839\u672c\u4e0d\u61c2\u6c5f\u6e56\u8fc7\u8282\uff0c\u4ed6\u63a8\u8bf4\u4e0d\u4fbf\u8fc7\u95ee\uff0c\u5374\u4e5f\u7b97\u4e0d\u5f97\u5373\u662f\u4ee3\u8868\u4e86\u5c11\u6797\u4e00\u6d3e\u7684\u7acb\u573a\uff0c\u6211\u597d\u6b79\u5f97\u8981\u4ed6\u6709\u4e2a\u627f\u8bfa\uff0c\u5c11\u65f6\u89c1\u4e86\u90a3\u5165\u4e91\u638c\u95e8\u4eba\u4e5f\u597d\u8bb2\u8bdd\uff01\u5fc3\u5ff5\u4e00\u5b9a\uff0c\u7acb\u5373\u7b11\u9053\uff1a\u201c\u5927\u5e08\uff0c\u4f60\u53ef\u662f\u8bf4\u5c11\u6797\u4e00\u6d3e\u4e0d\u613f\u5377\u5165\u655d\u5e2e\u5185\u90e8\u4e4b\u4e8b\u5417\uff1f\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u4e00\u6d3e\u7eaf\u771f\uff0c\u81ea\u7136\u4e0d\u4f1a\u60f3\u5230\u970d\u9e23\u98ce\u8a00\u4e0b\u4e4b\u610f\uff0c\u4e43\u5728\u6487\u5f00\u5c11\u6797\u50e7\u4eba\uff0c\u7adf\u7136\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u8d35\u6d3e\u6709\u4e86\u53db\u5f92\uff0c\u5c11\u6797\u4e0d\u80fd\u4ee3\u4e3a\u5ff5\u4e8b\uff0c\u5df2\u662f\u4e0d\u5f53\uff0c\u5c82\u4f1a\u8fdd\u53cd\u6b66\u6797\u6b63\u4e49\uff0c\u52a9\u90a3\u53db\u5e2e\u4e4b\u4eba\u5462\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u8fd9\u65f6\u5ffd\u7136\u63a5\u9053\uff1a\u201c\u5927\u5e08\uff0c\u54b1\u4eec\u53ef\u5426\u5165\u5bfa\u62dc\u89c1\u8d35\u5bfa\u65b9\u4e08\uff1f\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u6709\u4f55\u4e0d\u53ef\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u5927\u5e08\u80fd\u4ee3\u8001\u673d\u7b49\u5f15\u89c1\u5417\uff1f\u201d \u3000\u3000\u5165\u5723\u95fb\u8a00\uff0c\u8fd9\u624d\u5ffd\u7136\u54d1\u7136\u5931\u7b11\u9053\uff1a\u201c\u65bd\u4e3b\u5feb\u8bf7\u5165\u5185\uff0c\u8001\u8872\u4e45\u5750\u7985\u5173\uff0c\u7adf\u7136\u8fde\u5e94\u63a5\u7684\u9053\u7406\uff0c\u51e0\u4e4e\u90fd\u5fd8\u6000\u4e86\uff0c\u771f\u662f\u7f6a\u8fc7\u5f97\u5f88\u2026\u2026\u201d \u3000\u3000\u970d\u5730\u95ea\u8eab\uff0c\u8083\u5ba2\u5165\u5bfa\u3002 \u3000\u3000\u970d\u9e23\u98ce\u4e00\u62c9\u65b9\u96ea\u5b9c\uff0c\u5927\u58f0\u9053\uff1a\u201c\u6709\u52b3\u5927\u5e08\u4e86\uff01\u201d\u4e3e\u6b65\u8fdb\u4e86\u5c71\u95e8\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u4e00\u62e7\u5a07\u8eaf\uff0c\u8d34\u5728\u65b9\u96ea\u5b9c\u8eab\u65c1\uff0c\u8ddf\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5b8b\u5b5a\u7b49\u4eba\uff0c\u4e5f\u8fc8\u6b65\u8d70\u8fdb\u5c11\u6797\u5c71\u95e8\uff0c\u53ea\u89c1\u6cbf\u7740\u90a3\u53e4\u67cf\u53c2\u5929\u7684\u77f3\u9053\uff0c\u6bcf\u9694\u4e94\u6b65\uff0c\u4e24\u8fb9\u5373\u5404\u6709\u4e00\u540d\u624b\u6267\u7985\u6756\u7684\u5f1f\u5b50\u3002\u8083\u5ba2\u9e44\u7acb\u3002 \u3000\u3000\u663e\u7136\uff0c\u5c11\u6797\u5bfa\u4e2d\uff0c\u4e3a\u4e86\u5bf9\u4ed8\u5929\u9b54\u5973\uff0c\u6212\u5907\u5f97\u751a\u662f\u68ee\u4e25\uff0c\u5168\u5bfa\u5b50\u5f1f\uff0c\u4ff1\u5df2\u51fa\u52a8\uff0c\u8f6e\u73ed\u5b88\u536b\u3002 \u3000\u3000\u4e00\u884c\u4eba\u8f6c\u8fc7\u4e86\u51e0\u91cd\u4f5b\u6bbf\uff0c\u6253\u4e00\u7247\u7e41\u82b1\u4f3c\u9526\u7684\u5c0f\u56ed\u4e4b\u4e2d\uff0c\u6298\u5411\u53f3\u4fa7\u4e00\u5904\u758f\u7af9\u6ee1\u56ed\u7684\u7985\u9662\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u62ac\u5934\u671b\u53bb\uff0c\u53ea\u89c1\u90a3\u95f4\u7985\u9662\u7684\u6708\u95e8\u4e4b\u524d\uff0c\u6bcf\u8fb9\u7ad9\u4e86\u56db\u540d\u62b1\u5251\u6c99\u5f25\uff0c\u76ee\u5149\u70af\u70af\u5730\u6ce8\u89c6\u7740\u81ea\u5df1\u7b49\u4e00\u884c\u3002 \u3000\u3000\u5165\u5723\u5927\u5e08\u8fd9\u65f6\u5feb\u8d70\u4e00\u6b65\uff0c\u5411\u90a3\u516b\u540d\u6c99\u5f25\u9053\uff1a\u201c\u5feb\u5feb\u901a\u62a5\u638c\u95e8\u4eba\uff0c\u5c31\u8bf4\u771f\u6b63\u7684\u4e10\u5e2e\u970d\u5e2e\u4e3b\u5230\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5251\u7709\u4e0d\u7981\u4e00\u76b1\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u8001\u548c\u5c1a\u771f\u662f\u5dee\u52b2\u5f97\u5f88\uff0c\u600e\u53ef\u8fd9\u4e48\u8bf4\u8bdd\u5462\uff1f\u53ea\u6015\u65b9\u4e08\u7cbe\u5ba4\u5185\u7684\u5047\u5e2e\u4e3b\u542c\u5f97\u6b64\u8a00\u5fc5\u5c06\u9041\u8d70\u4e86\uff01\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u867d\u7136\u89c9\u5f97\u4e0d\u59a5\uff0c\u53e3\u4e2d\u53ef\u662f\u6ca1\u6709\u8bf4\u5c06\u51fa\u6765\u3002 \u3000\u3000\u4f46\u970d\u9e23\u98ce\u5374\u662f\u5931\u9053\uff1a\u201c\u5927\u5e08\uff0c\u8fd9\u4e48\u4f20\u8bdd\uff0c\u53ea\u6240\u6709\u4e9b\u4e0d\u59a5\u5427\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u4e00\u6014\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u672c\u6765\u4ed6\u5c31\u662f\u5047\u7684\u5440\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u5927\u5e08\uff0c\u8fd9\u7b49\u8bdd\u542c\u5165\u90a3\u5047\u5192\u4e4b\u8033\u4e2d\uff0c\u5165\u4e91\u638c\u95e8\u4eba\u53ef\u5c31\u96be\u4ee5\u7f6e\u8eab\u4e8b\u5916\uff01\u5927\u5e08\u5c1a\u8bf7\u4e09\u601d\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u60f3\u4e86\u4e00\u60f3\uff0c\u9053\uff1a\u201c\u65bd\u4e3b\u8bf4\u5f97\u4e0d\u65e0\u9053\u7406\uff01\u8001\u8872\u53eb\u4ed6\u4eec\u4e0d\u8fd9\u4e48\u8bf4\u4fbf\u662f\uff01\u201d\u8f6c\u5934\u5411\u90a3\u516b\u540d\u6c99\u5f25\u9053\uff1a\u201c\u5feb\u5feb\u7980\u544a\u638c\u95e8\u4eba\uff0c\u5c31\u8bf4\u5251\u795e\u4f20\u4eba\u6c42\u89c1\uff01\u201d \u3000\u3000\u90a3\u516b\u540d\u6c99\u5f25\u4e2d\u5de6\u624b\u524d\u4e00\u540d\uff0c\u5408\u5341\u4e00\u793c\uff0c\u8f6c\u8eab\u5165\u5185\u800c\u53bb\u3002 \u3000\u3000\u4e0d\u4e00\u77ac\u95f4\uff0c\u53ea\u89c1\u90a3\u540d\u6c99\u5f25\u5feb\u6b65\u800c\u6765\uff0c\u5927\u58f0\u9053\uff1a\u201c\u638c\u95e8\u4eba\u4eb2\u81ea\u51fa\u8fce\u6765\u4e86\uff01\u201d \u3000\u3000\u5c11\u6797\u638c\u95e8\u4eba\u4eb2\u81ea\u63a5\u4eba\u90a3\u53ef\u5f53\u771f\u662f\u6b66\u6797\u4e2d\u6781\u4e3a\u5c11\u6709\u4e4b\u4e8b\uff0c\u7167\u8bf4\uff0c\u5bf9\u4e8e\u970d\u9e23\u98ce\u8fd9\u7b49\u4e00\u5e2e\u5e2e\u4e3b\u7684\u8eab\u4efd\uff0c\u5c11\u6797\u638c\u95e8\u4eba\u5728\u793c\u6559\u4e0a\u81ea\u662f\u5e94\u5f53\u51fa\u8fce\uff0c\u4f46\u5bf9\u4e00\u4f4d\u6b66\u6797\u540e\u8f88\uff0c\u4ed6\u4eec\u4f3c\u662f\u4e0d\u7528\u5982\u6b64\u8c26\u606d\uff0c\u4f46\u4ed6\u7adf\u662f\u4eb2\u81ea\u51fa\u8fce\uff0c\u8db3\u89c1\u5251\u795e\u5728\u6b66\u6797\u4e2d\u7684\u540d\u671b\uff0c\u53c8\u662f\u4f55\u7b49\u53d7\u4eba\u666f\u4ef0\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u6b63\u5f85\u51fa\u8a00\u62e6\u963b\uff0c\u5374\u89c1\u90a3\u6708\u95e8\u4e4b\u5185\uff0c\u5df2\u7136\u51fa\u73b0\u4e86\u4e09\u4f4d\u5e74\u7eaa\u90fd\u5728\u4e03\u5341\u5f00\u5916\u7684\u50e7\u4eba\u3002 \u3000\u3000\u5f53\u4e2d\u7684\u4e00\u4f4d\uff0c\u4eba\u672a\u8d70\u51fa\u6708\u95e8\uff0c\u4e5f\u5df2\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u54ea\u4e00\u4f4d\u65bd\u4e3b\u662f\u5251\u795e\u5f1f\u5b50\uff1f\u8001\u8872\u5165\u4e91\u5019\u9a7e\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fde\u5fd9\u4e0a\u524d\uff0c\u62b1\u62f3\u9053\uff1a\u201c\u665a\u8f88\u65b9\u96ea\u5b9c\uff0c\u62dc\u89c1\u638c\u95e8\u5927\u5e08\uff01\u201d \u3000\u3000\u4e3e\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u8fd9\u5165\u4e91\u5927\u5e08\u751f\u7684\u751a\u662f\u9ad8\u5927\uff0c\u867d\u7136\u987b\u53d1\u7686\u767d\uff0c\u4f46\u4e0d\u89c1\u4e1d\u6beb\u9f99\u953a\u4e4b\u6001\uff0c\u7709\u5b87\u4e4b\u95f4\uff0c\u4e00\u6d3e\u6148\u7965\u7b11\u610f\u3002 \u3000\u3000\u5165\u4e91\u5927\u5e08\u76ee\u5149\u5728\u65b9\u96ea\u5b9c\u8eab\u4e0a\u4e00\u8f6c\uff0c\u7b11\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\uff0c\u5251\u795e\u9648\u5927\u4fa0\u5b89\u597d\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u795e\u60c5\u4e00\u60e8\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5148\u5e08\u5df2\u7136\u8fc7\u4e16\u4e86\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u987f\u65f6\u5446\u4e86\u4e00\u5446\uff0c\u534a\u664c\u65b9\u9053\uff1a\u201c\u600e\u4e48\uff1f\u9648\u5927\u4fa0\u7adf\u7136\u662f\u2026\u2026\u7adf\u7136\u662f\u2026\u2026\u201d\u4ed6\u4e00\u8fde\u8bf4\u4e86\u51e0\u4e2a\u7adf\u7136\u662f\uff0c\u672a\u628a\u4e0b\u9762\u4e4b\u8bdd\uff0c\u8bf4\u51fa\u53e3\u6765\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u9eef\u7136\u5730\u628a\u5e08\u7236\u75c5\u901d\u4e4b\u4e8b\uff0c\u7b80\u7565\u4ed6\u8bf4\u4e86\u4e00\u904d\u3002 \u3000\u3000\u5165\u4e91\u5927\u5e08\u53ea\u542c\u5f97\u795e\u60c5\u6fc0\u52a8\uff0c\u957f\u53f9\u8fde\u58f0\uff0c\u9053\uff1a\u201c\u4e00\u4ee3\u5927\u4fa0\uff0c\u7adf\u5c14\u4e0d\u5f97\u6c38\u5e74\uff0c\u771f\u53eb\u8001\u8872\u75db\u6000\u2026\u2026\u201d \u3000\u3000\u8bed\u97f3\u672a\u5df2\uff0c\u7ad9\u5728\u4ed6\u5de6\u4fa7\u5730\u90a3\u540d\u767d\u53d1\u5782\u80a9\u7684\u8001\u548c\u5c1a\uff0c\u5ffd\u7136\u4f4e\u58f0\u9053\uff1a\u201c\u6b64\u95f4\u4e0d\u662f\u8bf4\u8bdd\u6240\u5728\uff0c\u638c\u95e8\u4eba\u53ef\u613f\u8ba9\u51e0\u4f4d\u65bd\u4e3b\u5165\u5185\u4e00\u53d9\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u95fb\u8a00\uff0c\u8fde\u5fd9\u5411\u65b9\u96ea\u5b9c\u7b49\u4e00\u884c\u9053\uff1a\u201c\u51e0\u4f4d\u65bd\u4e3b\u5feb\u8bf7\u5165\u5185\u8bf4\u8bdd\u2026\u2026\u201d \u3000\u3000\u8fd9\u65f6\u76ee\u5149\u5ffd\u7136\u505c\u5728\u970d\u9e23\u98ce\u3001\u5b8b\u5b5a\u7b49\u8eab\u4e0a\uff0c\u4e0d\u7531\u5f97\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u970d\u5e2e\u4e3b\u5417\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u5927\u7b11\u9053\uff1a\u201c\u638c\u95e8\u4eba\u597d\u773c\u529b\uff0c\u4f60\u6ca1\u6709\u628a\u533a\u533a\u5fd8\u8bb0\uff0c\u603b\u7b97\u662f\u4e00\u6869\u53eb\u4eba\u5feb\u6170\u4e4b\u4e8b\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u767d\u7709\u8fde\u8fde\u7fd5\u52a8\u6c89\u541f\uff0c\u9053\uff1a\u201c\u8fd9\u5c31\u5947\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u4ec0\u4e48\u5947\u4e86\uff1f\u53ef\u662f\u6709\u4eba\u5047\u5192\u4e86\u533a\u533a\uff01\u201d \u3000\u3000\u5165\u4e91\u638c\u95e8\u6447\u5934\u9053\uff1a\u201c\u4e0d\u662f\u5047\u5192\uff0c\u800c\u662f\u2026\u2026\u800c\u662f\u9876\u66ff\u4e86\u5e2e\u4e3b\u8eab\u4efd\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u5927\u58f0\u9053\uff1a\u201c\u6b64\u4eba\u4f55\u5728\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u5ffd\u7136\u8138\u8272\u4e00\u6c89\uff0c\u5411\u8eab\u7554\u7684\u90a3\u540d\u767d\u53d1\u8001\u548c\u5c1a\uff0c\u201c\u5165\u6cd5\u5e08\u5f1f\uff0c\u5feb\u5feb\u65f6\u53bb\u7559\u4f4f\u90a3\u4f4d\u5192\u540d\u9876\u66ff\u4e4b\u4eba\u2026\u2026\u201d \u3000\u3000\u90a3\u5165\u6cd5\u5927\u5e08\u5e94\u58f0\u5411\u5185\u884c\u53bb\u3002 \u3000\u3000\u5165\u4e91\u638c\u95e8\u4eba\u957f\u957f\u4e00\u53f9\uff0c\u63a5\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u8001\u8872\u7adf\u4f1a\u88ab\u90a3\u5996\u5973\u4e00\u4f19\u7792\u4f4f\uff0c\u82e5\u975e\u970d\u65bd\u4e3b\u4eb2\u8eab\u524d\u6765\uff0c\u53ef\u771f\u662f\u540e\u679c\u4e0d\u582a\u8bbe\u60f3\uff01\u201d \u3000\u3000\u8bdd\u97f3\u4e00\u987f\uff0c\u76ee\u5149\u53c8\u8f6c\u5411\u4e86\u5b8b\u5b5a\u3002 \u3000\u3000\u5b8b\u5b5a\u7acb\u5373\u7b11\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u8ba4\u5f97\u8001\u5077\u513f\u5417\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u7b11\u9053\uff1a\u201c\u65bd\u4e3b\u4e43\u662f\u95f2\u4e91\u91ce\u9e64\uff0c\u4e0d\u77e5\u4eca\u65e5\u600e\u4f1a\u9a7e\u4e34\u655d\u5bfa\uff1f\u8fbe\u6469\u771f\u7ecf\u5df2\u7136\u4e0d\u5728\u85cf\u7ecf\u697c\u5185\uff0c\u65bd\u4e3b\u5982\u60f3\u4e0b\u624b\uff0c\u53ea\u6015\u96be\u4ee5\u5982\u613f\u4ee5\u507f\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u6c55\u6c55\u4e00\u7b11\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u8001\u5077\u513f\u6b64\u756a\u524d\u6765\uff0c\u53ef\u4e0d\u662f\u6253\u4f60\u7684\u4ec0\u4e48\u4e3b\u610f\uff0c\u5c31\u7b97\u4f60\u638c\u95e8\u4eba\u5927\u5f00\u65b9\u4fbf\u4e4b\u95e8\uff0c\u53cc\u624b\u6367\u4e0a\u8fbe\u6469\u771f\u7ecf\uff0c\u8001\u5077\u513f\u4e5f\u4e0d\u4f1a\u8981\u7684\u4e86\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u5475\u5475\u5927\u7b11\u9053\uff1a\u201c\u65bd\u4e3b\u597d\u8bf4\uff0c\u8001\u8872\u4e0d\u8fc7\u662f\u8ddf\u65bd\u4e3b\u591a\u5e74\u4e0d\u89c1\uff0c\u5f00\u4e2a\u73a9\u7b11\u800c\u5df2\uff0c\u65bd\u4e3b\u5343\u4e07\u83ab\u8981\u5f53\u771f\u2026\u2026\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u79fb\u6b65\u8083\u5ba2\u5165\u5185\u3002 \u3000\u3000\u8fd9\u65f6\u4e10\u5e2e\u7684\u4e09\u4f4d\u957f\u8001\u548c\u6731\u7693\u7b49\uff0c\u4e5f\u4e0a\u524d\u8ddf\u5165\u4e91\u638c\u95e8\u4eba\u89c1\u793c\uff0c\u77a7\u4ed6\u4eec\u8bf4\u8bdd\u795e\u6001\uff0c\u539f\u6765\u591a\u662f\u76f8\u8bc6\u4e4b\u4eba\u3002 \u3000\u3000\u8d70\u8fc7\u4e00\u7247\u7af9\u6797\uff0c\u8fce\u9762\u662f\u4e00\u680b\u4e09\u5f00\u95f4\uff0c\u524d\u540e\u5171\u6709\u4e09\u8fdb\u5efa\u7b51\u5b8f\u4f1f\u7684\u7cbe\u820d\u3002\u6b63\u9762\u7684\u4e00\u95f4\uff0c\u4e43\u662f\u4e00\u5904\u4f5b\u6bbf\uff0c\u51e0\u4e2a\u4eba\u521a\u521a\u8de8\u4e0a\u77f3\u9636\uff0c\u53ea\u89c1\u90a3\u5165\u6cd5\u5927\u5e08\u5df2\u5306\u5306\u8d70\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5165\u4e91\u5927\u5e08\u7779\u72b6\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4ed6\u4eec\u53ef\u662f\u9003\u8d70\u4e86\uff1f\u201d \u3000\u3000\u5165\u6cd5\u5927\u5e08\u70b9\u5934\u9053\uff1a\u201c\u7cbe\u820d\u4e4b\u5185\uff0c\u4e0d\u89c1\u4eba\u5f71\uff0c\u4e0d\u5f1f\u5df2\u7136\u5dee\u4ee4\u5f53\u503c\u5f1f\u5b50\u4f20\u4ee4\u641c\u67e5\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u70b9\u5934\u9053\uff1a\u201c\u53ea\u6015\u8fd8\u5f97\u6709\u52b3\u5e08\u5f1f\u4eb2\u81ea\u8d70\u4e0a\u4e00\u906d\u4e86\uff01\u201d \u3000\u3000\u5165\u6cd5\u5927\u5e08\u5408\u5341\u9053\uff1a\u201c\u5c0f\u5f1f\u9075\u547d\uff01\u201d\u8f6c\u8eab\u5feb\u6b65\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u5165\u4e91\u5927\u5e08\u6ee1\u542b\u6b49\u610f\u5730\u5411\u970d\u9e23\u98ce\u9053\uff1a\u201c\u970d\u5e2e\u4e3b\uff0c\u8001\u8872\u4e00\u65f6\u4e0d\u5bdf\uff0c\u7adf\u8ba9\u4ed6\u4eec\u9041\u53bb\uff0c\u771f\u662f\u7f6a\u8fc7\u5f97\u5f88\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u5fc3\u4e2d\u867d\u7136\u5341\u5206\u6124\u6124\uff0c\u4f46\u53e3\u4e2d\u5374\u4e0d\u80fd\u4e0d\u7b11\u9053\uff1a\u201c\u638c\u95e8\u4eba\u597d\u8bf4\uff0c\u672c\u5e2e\u5c45\u7136\u51fa\u4e86\u8fd9\u7b49\u4e0d\u8096\u4e4b\u9661\uff0c\u52b3\u52a8\u8d35\u5bfa\u5b50\u5f1f\uff0c\u970d\u67d0\u5fc3\u4e2d\u5012\u662f\u60ed\u6127\u5f97\u5f88\u2026\u2026\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5df2\u7136\u8d70\u8fc7\u4e00\u95f4\u7985\u623f\u3002 \u3000\u3000\u5165\u4e91\u5927\u5e08\u62db\u547c\u4e86\u4f17\u4eba\u5c31\u8fd9\u4e48\u5750\u5b9a\uff0c\u65b9\u96ea\u5b9c\u6253\u91cf\u4e86\u8fd9\u95f4\u7985\u623f\u4e00\u773c\uff0c\u53ea\u89c9\u5f97\u867d\u662f\u56db\u58c1\u6beb\u65e0\u88c5\u9970\u4e4b\u7269\uff0c\u4f46\u56e0\u90a3\u540e\u58c1\u7684\u4e00\u5e45\u4f5b\u7956\u7ed8\u50cf\uff0c\u6829\u6829\u5982\u751f\uff0c\u987f\u65f6\u4f7f\u4eba\u6709\u7740\u5fc3\u5730\u7965\u548c\uff0c\u4e86\u65e0\u4e00\u7269\u4e4b\u611f\uff0c\u4e0d\u7981\u81ea\u5fd6\u9053\uff1a\u201c\u8fd9\u7b49\u6e05\u9759\u4e4b\u5730\uff0c\u679c\u7136\u662f\u4fee\u771f\u7684\u5927\u597d\u6240\u5728\uff01\u201d \u3000\u3000\u5bfb\u606f\u672a\u5df2\uff0c\u8033\u4e2d\u5ffd\u7136\u542c\u5f97\u5165\u4e91\u638c\u95e8\u4eba\u7b11\u9053\uff1a\u201c\u970d\u5e2e\u4e3b\uff0c\u8fd9\u4f4d\u5c0f\u65bd\u4e3b\u53ef\u662f\u4f60\u65b0\u6536\u7684\u5f1f\u5b50\u5417\uff1f\u201d \u3000\u3000\u6562\u60c5\u8fd9\u4e48\u591a\u4eba\u4e4b\u4e2d\uff0c\u53ea\u6709\u5b89\u5c0f\u840d\u8fd8\u6ca1\u6709\u62db\u547c\u3002 \u3000\u3000\u970d\u9e23\u98ce\u95fb\u8a00\uff0c\u6447\u5934\u5927\u7b11\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u970d\u67d0\u5341\u751f\u4fee\u884c\uff0c\u53ea\u6015\u4e5f\u6536\u4e0d\u5230\u8fd9\u7b49\u8d44\u8d28\u7684\u5f1f\u5b50\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8fd9\u4f4d\u5c0f\u65bd\u4e3b\u662f\u54ea\u4f4d\u9ad8\u4eba\u7684\u95e8\u4e0b\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u88ab\u4ed6\u95ee\u5f97\u4e00\u6014\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u970d\u67d0\u53ef\u4e5f\u4e0d\u77e5\u9053\u4e86\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u53c8\u662f\u4e00\u6014\uff0c\u76b1\u7709\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u7b11\u7740\u6307\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u4f4d\u5b89\u516c\u5b50\u4e43\u662f\u65b9\u8001\u5f1f\u7684\u81f3\u4ea4\uff0c\u638c\u95e8\u4eba\u4f46\u95ee\u65b9\u8001\u5f1f\u4fbf\u53ef\u660e\u767d\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u65f6\u8fde\u5fd9\u62b1\u62f3\u9053\uff1a\u201c\u638c\u95e8\u4eba\u4e0d\u5fc5\u52a8\u7591\uff0c\u4ed6\u679c\u7136\u662f\u665a\u8f88\u7684\u597d\u53cb\uff0c\u5b89\u2026\u2026\u5b89\u840d\u5144\u5f1f\uff01\u201d\u4ed6\u5ffd\u7136\u95f4\u60f3\u5230\uff0c\u5b89\u5c0f\u840d\u4e43\u662f\u5973\u5b69\u513f\u5bb6\u7684\u540d\u5b57\uff0c\u8bf4\u51fa\u6765\u5fc5\u7136\u5f15\u8d77\u4ed6\u4eec\u731c\u7591\uff0c\u5c31\u628a\u90a3\u5c0f\u5b57\u7565\u53bb\u4e86\uff01 \u3000\u3000\u5165\u4e91\u5927\u5e08\u663e\u7136\u5bf9\u65b9\u96ea\u5b9c\u6709\u7740\u8fc7\u4eba\u7684\u4fe1\u4efb\uff0c\u95fb\u8a00\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u5b89\u65bd\u4e3b\uff01\u201d\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u5012\u4e5f\u4e56\u5de7\uff0c\u8fde\u5fd9\u8d77\u8eab\u89c1\u793c\u9053\uff1a\u201c\u665a\u8f88\u5b89\u840d\uff0c\u89c1\u8fc7\u638c\u95e8\u5927\u5e08\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u5408\u5341\u8fd8\u4e86\u50e7\u793c\uff0c\u7b11\u9053\uff1a\u201c\u5b89\u65bd\u4e3b\u662f\u54ea\u4e00\u4f4d\u9ad8\u4eba\u95e8\u4e0b\uff1f\u201d \u3000\u3000\u6562\u60c5\u4ed6\u867d\u7136\u76f8\u4fe1\u65b9\u96ea\u5b9c\uff0c\u4f46\u5bf9\u5b89\u840d\u7684\u51fa\u8eab\u6765\u5386\uff0c\u5374\u662f\u4e0d\u80fd\u4e0d\u95ee\u3002 \u3000\u3000\u5b89\u840d\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u665a\u8f88\u4e00\u5411\u5bb6\u5c45\u5357\u6d77\uff0c\u81ea\u5e7c\u867d\u7136\u8ddf\u968f\u5148\u7236\u4e60\u8fc7\u51e0\u5929\u62f3\u68d2\uff0c\u53ea\u4e0d\u8fc7\u662f\u7528\u6765\u5f3a\u8eab\uff0c\u5927\u5e08\u4e0b\u95ee\uff0c\u5012\u6559\u665a\u8f88\u4e0d\u5b89\u4e86\uff01\u201d \u3000\u3000\u5979\u56de\u7b54\u5f97\u751a\u662f\u5f97\u4f53\uff0c\u5c45\u7136\u8fde\u5165\u4e91\u5927\u5e08\u4e5f\u672a\u52a8\u7591\u3002 \u3000\u3000\u8fd9\u65f6\u5b8b\u5b5a\u5ffd\u7136\u5927\u7b11\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u54b1\u4eec\u6765\u6b64\uff0c\u4e0d\u662f\u4e3a\u4e86\u804a\u5929\uff0c\u4f60\u8981\u662f\u53ea\u662f\u76d8\u95ee\u4eba\u5bb6\u51fa\u8eab\u5bb6\u4e16\uff0c\u53c8\u4e0d\u6253\u7b97\u7ed9\u4ed6\u4eec\u505a\u5a92\u8bf4\u4eb2\uff0c\u8001\u673d\u53ef\u8981\u544a\u8f9e\u4e86\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u88ab\u4ed6\u8bf4\u5f97\u8138\u4e0a\u8baa\u8baa\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u65bd\u4e3b\u4e0d\u79ef\u53e3\u5fb7\uff0c\u4e5f\u4e0d\u6015\u8fdb\u90a3\u5272\u820c\u5730\u72f1\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u5927\u5e08\u597d\u8bf4\uff0c\u90a3\u662f\u8eab\u540e\u4e4b\u4e8b\uff0c\u5728\u8001\u673d\u770b\u6765\uff0c\u53ef\u4e0d\u503c\u5f97\u8d39\u5fc3\u4e86\uff01\u201d\u76ee\u5149\u4e00\u8f6c\uff0c\u5374\u89c1\u5b89\u5c0f\u840d\u6b63\u5728\u7ea2\u8138\u6012\u89c6\u81ea\u5df1\uff0c\u5fc3\u4e2d\u4e00\u6123\uff0c\u53c8\u4e0d\u7531\u5f97\u653e\u58f0\u5927\u7b11\u3002 \u3000\u3000\u6562\u60c5\u4ed6\u8fd9\u53e5\u505a\u5a92\uff0c\u53ef\u628a\u8fd9\u4f4d\u5c0f\u59d1\u5a18\u8bf4\u7f9e\u4e86\uff01\u53ea\u56e0\u5ba4\u5185\u4e0d\u77e5\u5979\u4e43\u662f\u5973\u513f\u4e4b\u8eab\u7684\u4eba\u751a\u591a\uff0c\u5b89\u5c0f\u840d\u5373\u4f7f\u6012\u6781\uff0c\u4e5f\u4e0d\u4fbf\u53d1\u4f5c\uff0c\u5b8b\u5b5a\u4e00\u5f97\u610f\uff0c\u662f\u4ee5\u5fcd\u4e0d\u4f4f\u7eb5\u58f0\u5927\u7b11\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u65f6\u4e5f\u89c9\u5f97\u8138\u4e0a\u6709\u4e9b\u8baa\u8baa\u7684\uff0c\u5f20\u53e3\u60f3\u8bf4\u8bdd\uff0c\u4f46\u5374\u6ca1\u6709\u8bf4\u51fa\u58f0\u6765\u3002 \u3000\u3000\u5e78\u800c\u5165\u4e91\u5927\u5e08\u6b64\u523b\u5df2\u7136\u63a5\u9053\uff1a\u201c\u8001\u65bd\u4e3b\u4e3a\u4f55\u53d1\u7b11\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u600e\u80fd\u8bf4\u51fa\u81ea\u5df1\u4e3a\u4f55\u53d1\u7b11\u7684\u539f\u6545\u5462\uff1f\u5f53\u4e0b\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u8001\u592b\u4e0d\u4fbf\u8bf4\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8bf4\u51fa\u6765\uff0c\u2026\u2026\u4f1a\u6709\u4eba\u4e0d\u597d\u610f\u601d\uff01\u201d \u3000\u3000\u4ed6\u672c\u662f\u4e00\u53e5\u5b9e\u8bdd\uff0c\u4f46\u542c\u5728\u5b89\u5c0f\u840d\u8033\u4e2d\uff0c\u5374\u53c8\u523a\u4eba\u5f97\u5f88\u3002 \u3000\u3000\u5165\u4e91\u5927\u5e08\u4e0d\u77e5\u5c31\u91cc\uff0c\u6014\u5f97\u4e00\u6536\u9053\uff1a\u201c\u600e\u4e48\u4f1a\uff1f\u65bd\u4e3b\u5feb\u8bf4\u5c06\u51fa\u6765\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u541f\u4e86\u4e00\u4e0b\uff0c\u5fc3\u4e2d\u5df2\u7ecf\u6709\u4e86\u4e3b\u610f\uff0c\u4ed6\u5b81\u53ef\u5f97\u7f6a\u5c11\u6797\u50e7\u4fa3\uff0c\u4e5f\u4e0d\u613f\u5f97\u7f6a\u8fd9\u4f4d\u5b89\u5c0f\u840d\u59d1\u5a18\uff0c\u662f\u4ee5\u6545\u4f5c\u59ff\u6001\u5730\u957f\u957f\u4e00\u53f9\uff0c\u9053\uff1a\u201c\u8001\u592b\u8bf4\u5c06\u51fa\u6765\uff0c\u638c\u95e8\u4eba\u5f53\u771f\u4e0d\u89c1\u602a\u5417\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u4f5b\u95e8\u5f1f\u5b50\uff0c\u4e0d\u6562\u59be\u72af\u55d4\u6212\uff0c\u65bd\u4e3b\u76f4\u8bf4\u5427\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u65e2\u662f\u638c\u95e8\u4eba\u6709\u4e86\u8fd9\u7b49\u80f8\u895f\uff0c\u8001\u673d\u4e0d\u8bf4\uff0c\u5012\u662f\u4e0d\u8be5\u7684\u4e86\uff01\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u8001\u673d\u5931\u7b11\uff0c\u5b9e\u56e0\u5ffd\u7136\u89c9\u5f97\uff0c\u5c11\u6797\u9886\u8896\u6b66\u6797\uff0c\u7d20\u4e3a\u5404\u65b9\u63a8\u91cd\uff0c\u4eca\u65e5\u4e00\u89c1\uff0c\u5374\u53eb\u4eba\u5927\u6709\u89c1\u9762\u4e0d\u5982\u95fb\u540d\u4e4b\u61be\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u8138\u8272\u84e6\u7136\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u8001\u65bd\u4e3b\uff0c\u4f60\u2026\u2026\u201d \u3000\u3000\u4f46\u4ed6\u663e\u7136\u662f\u60f3\u8d77\uff0c\u81ea\u5df1\u9002\u624d\u66fe\u7ecf\u8bf4\u8fc7\uff0c\u4e0d\u8d77\u55d4\u6212\uff0c\u662f\u6545\u8138\u8272\u4e00\u53d8\u4e4b\u540e\uff0c\u5374\u8fde\u5fd9\u5f3a\u81ea\u6309\u637a\uff0c\u4f4e\u7709\u5408\u5341\uff0c\u9053\uff1a\u201c\u8001\u65bd\u4e3b\uff0c\u6211\u5c11\u6797\u4f55\u4e8b\u5f97\u7f6a\u4e86\u65bd\u4e3b\u4e86\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u6ca1\u6709\u554a\uff0c\u8d35\u5bfa\u4e43\u662f\u6b66\u6797\u540d\u95e8\u6b63\u6d3e\uff0c\u81ea\u7136\u662f\u4e0d\u4f1a\u6709\u4eba\u5f97\u7f6a\u6211\u8fd9\u8001\u5077\u513f\u4e86\uff01\u201d \u3000\u3000\u8bdd\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4f46\u8d35\u5bfa\u7adf\u5c06\u4e10\u5e2e\u7684\u5e2e\u4e3b\u90fd\u5f04\u9519\u4e86\uff0c\u8fd9\u5c82\u4e0d\u662f\u5929\u5927\u7684\u7b11\u8bdd\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u7709\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u8001\u5077\u513f\u8bf4\u7684\u53ef\u662f\u4e0d\u9519\u5427\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u60ed\u6127\u5f97\u4f4e\u4e0b\u5934\u53bb\uff0c\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u5b8b\u5b5a\u76ee\u5149\u4e00\u8f6c\uff0c\u89c1\u90a3\u53e6\u5916\u7684\u4e24\u4f4d\u5c11\u6797\u957f\u8001\uff0c\u5168\u90fd\u4e00\u8138\u4ec7\u6068\u81ea\u5df1\u4e4b\u8272\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a \u3000\u3000\u201c\u73a9\u7b11\u4e0d\u4fbf\u5f00\u5f97\u592a\u8fc7\u4e86\u3002\u201d \u3000\u3000\u5f53\u4e0b\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u8001\u5077\u513f\u4e00\u5411\u559c\u6b22\u73a9\u7b11\uff0c\u4f60\u53ef\u83ab\u628a\u8001\u5077\u513f\u7684\u8bdd\u5f53\u4f5c\u771f\u7684\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u76ee\u5149\u4e00\u9eef\uff0c\u7adf\u662f\u957f\u53f9\u63a5\u9053\uff1a\u201c\u8001\u65bd\u4e3b\uff0c\u6211\u5c11\u6797\u4e00\u8109\uff0c\u5982\u4eca\u679c\u7136\u662f\u5f0f\u5fae\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u638c\u95e8\u4eba\u597d\u8bf4\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u8001\u65bd\u4e3b\uff0c\u4e0d\u662f\u8001\u8872\u81ea\u8d23\uff0c\u81ea\u4ece\u5f53\u5e74\u5165\u5723\u5e08\u5144\u5047\u6b7b\uff0c\u800c\u8981\u8001\u673d\u63a5\u638c\u95e8\u4e8b\u4ee5\u6765\uff0c\u8001\u8872\u81ea\u77e5\u624d\u667a\u8584\u5f31\uff0c\u4e8b\u4e8b\u8c28\u614e\u5c0f\u5fc3\uff0c\u4f46\u53c8\u600e\u77e5\u8fd9\u7b49\u8c28\u614e\u7684\u7ed3\u679c\uff0c\u5374\u662f\u7ed9\u672c\u5bfa\u5e26\u6765\u4e86\u8bb8\u591a\u65e0\u5984\u4e4b\u706b\uff0c\u5982\u4eca\u60f3\u6765\uff0c\u5951\u673a\u6240\u5728\uff0c\u4e43\u662f\u8001\u8872\u592a\u8fc7\u6015\u4e8b\u4e4b\u6545\u2026\u2026\u201d \u3000\u3000\u53ea\u89c1\u4ed6\u4e00\u8138\u4e25\u8083\uff0c\u679c\u771f\u6709\u7740\u5927\u5f7b\u5927\u609f\u4e4b\u5fc3\u3002 \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u8fd9\u4e9b\u4e8b\u4e0d\u7528\u591a\u8bf4\u4e86\uff01\u8001\u673d\u773c\u4e0b\u5c31\u6709\u4e00\u6869\u5927\u4e8b\uff0c\u60f3\u5411\u638c\u95e8\u4eba\u8bf7\u6559\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u90a3\u5929\u9b54\u5973\u51e0\u65f6\u524d\u6765\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u767d\u7709\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u4e48\uff1f\u8001\u8872\u53ea\u77e5\u5979\u8fd1\u65e5\u8981\u6765\uff0c\u4f46\u4f55\u65f6\u524d\u6765\uff0c\u8001\u8872\u5374\u65e0\u6cd5\u77e5\u6653\uff01\u201d \u3000\u3000\u8fd9\u65f6\u970d\u9e23\u98ce\u5ffd\u7136\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u90a3\u5929\u9b54\u5973\u5230\u5e95\u5b89\u7684\u4ec0\u4e48\u5fc3\uff1f\u5979\u8fd9\u7b49\u6307\u540d\u53eb\u9635\u7528\u610f\uff0c\u53ea\u6015\u4e0d\u5149\u662f\u9488\u5bf9\u4f60\u4eec\u5c11\u6797\u4e00\u6d3e\u554a\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9eef\u7136\u6447\u5934\uff0c\u957f\u957f\u4e00\u53f9\uff0c\u9053\uff1a\u201c\u65bd\u4e3b\u6240\u89c1\uff0c\u786e\u6709\u9053\u7406\uff01\u8001\u8872\u66fe\u6000\u7591\uff0c\u8fd9\u53ef\u80fd\u662f\u5929\u9b54\u5973\u7684\u8be1\u8ba1\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u4ec0\u4e48\u8be1\u8ba1\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u5979\u6216\u8bb8\u662f\u60f3\u85c9\u6b64\u673a\u4f1a\uff0c\u5148\u884c\u6b7c\u706d\u6b66\u6797\u4e4b\u4e2d\u4e00\u90e8\u9ad8\u624b\uff01\u201d \u3000\u3000\u970d\u545c\u98ce\u76b1\u7709\u9053\uff1a\u201c\u90a3\u5929\u9b54\u5973\u7684\u80c6\u5b50\u4e0d\u5c0f\u554a\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u5ffd\u7136\u63a5\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u5996\u5973\u80c6\u5b50\u4e0d\u5c0f\uff01\u4e0d\u8fc7\u2026\u2026\u201d\u987f\u4e86\u4e00\u987f\u8bdd\u97f3\uff0c\u63a5\u9053\uff1a \u3000\u3000\u201c\u8001\u8872\u5947\u602a\u7684\u662f\uff0c\u5979\u86f0\u5c45\u591a\u5e74\uff0c\u600e\u7684\u5ffd\u7136\u95f4\u4e0d\u89c1\u4e00\u4e1d\u5f81\u5146\u5730\u51fa\u9762\u9080\u6218\u5462\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u6447\u5934\u9053\uff1a\u201c\u5927\u5e08\uff0c\u90a3\u5929\u9b54\u5973\u86f0\u5c45\u4e0d\u51fa\u7684\u539f\u56e0\uff0c\u4f60\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u4e0d\u5927\u660e\u767d\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u76ee\u5149\u5728\u65b9\u96ea\u5b9c\u8eab\u4e0a\u4e00\u8f6c\uff0c\u9053\uff1a\u201c\u53ea\u56e0\u65b9\u8001\u5f1f\u7684\u5e08\u7236\u9648\u5927\u4fa0\u672a\u6b7b\u4e4b\u6545\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff01\u201d \u3000\u3000\u5409\u5a01\u5ffd\u7136\u63a5\u9053\uff1a\u201c\u5927\u5e08\uff0c\u6709\u4e00\u4ef6\u4e8b\u4f60\u4e5f\u6599\u9519\u4e86\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u4e00\u6014\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u9519\u4e86\uff1f\u201d \u3000\u3000\u5409\u5a01\u9053\uff1a\u201c\u90a3\u5929\u9b54\u5973\u65e9\u5c31\u5728\u6b66\u6797\u4e4b\u4e2d\u6697\u5e03\u5b9e\u529b\u4e86\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u672c\u6d3e\u4e0d\u8fc7\u9996\u5f53\u5176\u51b2\u800c\u5df2\u3002\u201d\u76ee\u5149\u5ffd\u7136\u4e00\u9eef\uff0c\u63a5\u9053\uff1a\u201c\u6211\u5c11\u6797\u7acb\u8eab\u6b66\u6797\u4ee5\u6765\uff0c\u4eab\u4e3a\u5404\u65b9\u656c\u4ef0\uff0c\u9664\u4e86\u5929\u9b54\u5973\u7684\u624b\u4e0b\uff0c\u53c8\u6709\u4ec0\u4e48\u4eba\u6562\u4e8e\u90a3\u7b49\u85d0\u89c6\u672c\u5bfa\uff1f\u201d \u3000\u3000\u542c\u4ed6\u4e4b\u8a00\uff0c\u4eff\u4f5b\u662f\u5c11\u6797\u5df2\u7136\u5403\u8fc7\u5927\u4e8f\u4e86\uff01 \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u8d35\u5bfa\u5411\u6709\u4e0d\u8bb8\u5802\u5ba2\u5165\u5bfa\u7684\u89c4\u77e9\uff0c\u90a3\u5929\u9b54\u5973\u7684\u624b\u4e0b\uff0c\u60f3\u5fc5\u4e0d\u4f1a\u662f\u7537\u4eba\u4e86\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u4e00\u540d\u5341\u4e03\u516b\u5c81\u7684\u5973\u65bd\u4e3b\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u6b64\u5973\u53ef\u662f\u6b66\u529f\u5f88\u9ad8\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u5f88\u9ad8\uff0c\u95ef\u5165\u672c\u5bfa\uff0c\u8fde\u4f24\u4e94\u540d\u9ad8\u624b\uff0c\u800c\u4e14\u2026\u2026\u800c\u4e14\u2026\u2026\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u4e00\u987f\u8bdd\u97f3\uff0c\u4e0d\u5f80\u4e0b\u8bb2\u3002\u663e\u7136\uff0c\u8fd9\u7b49\u4e8b\uff0c\u662f\u5341\u5206\u4e22\u4eba\u7684\u4e86\uff01 \u3000\u3000\u970d\u9e23\u98ce\u76b1\u7709\u9053\uff1a\u201c\u4e00\u4e2a\u5341\u4e03\u516b\u5c81\u7684\u5973\u5a03\u513f\uff0c\u54ea\u80fd\u90a3\u7b49\u5229\u5bb3\uff1f\u8001\u592b\u6709\u4e9b\u4e0d\u4fe1\uff01\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u8001\u65bd\u4e3b\uff0c\u90a3\u5973\u65bd\u4e3b\u5e74\u7eaa\u867d\u8f7b\uff0c\u4f46\u4e00\u8eab\u6b66\u529f\uff0c\u5f53\u771f\u4e86\u5f97\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u53c8\u662f\u4e00\u53f9\uff0c\u9053\uff1a\u201c\u672c\u5bfa\u56e0\u5b57\u8f88\u9ad8\u624b\uff0c\u5728\u5979\u624b\u4e0b\u4f24\u4e86\u4e94\u4eba\u4e4b\u591a\uff0c\u82e5\u975e\u5165\u6cd5\u5e08\u5f1f\u4eb2\u81ea\u51fa\u624b\uff0c\u8fd8\u4e0d\u77e5\u5979\u8981\u4f24\u4e86\u6211\u4eec\u591a\u5c11\u4eba\u5462\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u5929\u9b54\u5973\u4e00\u4e2a\u624b\u4e0b\u4e4b\u4eba\uff0c\u5c31\u6709\u8fd9\u7b49\u6b66\u529f\uff1f\u771f\u6b63\u53eb\u4eba\u96be\u4ee5\u76f8\u4fe1\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u5e2e\u4e3b\u4e0d\u4fe1\uff0c\u90a3\u4e5f\u4e0d\u8db3\u4e3a\u602a\uff0c\u5c31\u7b97\u8001\u8872\u82e5\u975e\u4eb2\u773c\u6240\u89c1\uff0c\u4e5f\u662f\u4e0d\u4f1a\u76f8\u4fe1\u7684\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u5979\u9664\u4e86\u4f20\u4fe1\u4ee5\u5916\uff0c\u8fd8\u8bf4\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u8138\u8272\u5fae\u5fae\u4e00\u53d8\uff0c\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u5e2e\u4e3b\uff0c\u975e\u662f\u8001\u8872\u4e0d\u8bf4\uff0c\u5b9e\u56e0\u8fd9\u4e8b\u4e43\u662f\u672c\u95e8\u79c1\u4e8b\uff0c\u672a\u4fbf\u518d\u4e3a\u5916\u4eba\u9053\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u638c\u95e8\u4eba\u65e2\u662f\u4e0d\u613f\u8bf4\u51fa\uff0c\u90a3\u5c31\u7b97\u4e86\uff01\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u5ffd\u7136\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4ee4\u5e08\u6614\u5e74\u5728\u4e16\u4e4b\u65e5\uff0c\u66fe\u7ecf\u51fb\u8d25\u8fc7\u8fd9\u4e94\u9b54\u5934\uff0c\u4e0d\u77e5\u4ee4\u5e08\u5f25\u7559\u4e4b\u9645\uff0c\u53ef\u66fe\u544a\u8bc9\u8fc7\u4f60\uff0c\u90a3\u5929\u9b54\u5973\u7684\u6b66\u529f\uff0c\u7a76\u7adf\u5982\u4f55\u9ad8\u660e\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u6447\u5934\u9053\uff1a\u201c\u8fd9\u4e2a\u5417\uff1f\u5148\u5e08\u672a\u66fe\u63d0\u8d77\u3002\u201d \u3000\u3000\u970d\u9e23\u98ce\u9887\u4e3a\u610f\u5916\u5730\u4e00\u6014\u9053\uff1a\u201c\u6ca1\u63d0\u8fc7\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u665a\u8f88\u4e0d\u6562\u6b3a\u9a97\u5e2e\u4e3b\uff0c\u5148\u5e08\u786e\u662f\u672a\u66fe\u63d0\u8fc7\u5979\u4eec\u7684\u6b66\u529f\u9ad8\u4f4e\uff01\u201d \u3000\u3000\u5409\u5a01\u5728\u65c1\u7b11\u9053\uff1a\u201c\u8001\u592b\u660e\u767d\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u5927\u6982\u6b63\u662f\u4e3a\u6b64\u4e0d\u89e3\uff0c\u76b1\u7709\u6c89\u541f\uff0c\u95fb\u8a00\u63a5\u9053\uff1a\u201c\u8d24\u5f1f\u660e\u767d\u4ec0\u4e48\uff1f\u90a3\u9648\u5927\u4fa0\u4e3a\u4f55\u4e0d\u63d0\u6b64\u4e8b\u5462\uff1f\u201d \u3000\u3000\u5409\u5a01\u9053\uff1a\u201c\u90a3\u5929\u9b54\u5973\u5927\u6982\u5728\u9648\u5927\u4fa0\u773c\u4e2d\uff0c\u6839\u672c\u4e0d\u503c\u5f97\u4e00\u63d0\u554a\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8fd9\u4e48\u7b80\u5355\uff1f\u201d \u3000\u3000\u5409\u5a01\u9053\uff1a\u201c\u9664\u6b64\u4ee5\u5916\uff0c\u5c0f\u5f1f\u5c31\u60f3\u4e0d\u51fa\u8fd8\u6709\u4ec0\u4e48\u539f\u56e0\uff0c\u4f1a\u4f7f\u90a3\u9648\u5927\u4fa0\u4e0d\u5728\u5f1f\u5b50\u9762\u524d\u6765\u8bb2\u8d77\u8fd9\u4e2a\u9b54\u5934\u7684\u6b66\u529f\u4e86\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u5ffd\u7136\u5408\u5341\u9053\uff1a\u201c\u8001\u8872\u5012\u662f\u60f3\u5230\u4e86\u4e00\u70b9\uff0c\u4e0d\u77e5\u662f\u5426\u6b63\u786e\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u638c\u95e8\u4eba\u8bf7\u8bb2\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u65b9\u65bd\u4e3b\u5e74\u7eaa\u751a\u8f7b\uff0c\u521d\u751f\u4e4b\u728a\u4e0d\u754f\u864e\uff0c\u9648\u8001\u65bd\u4e3b\u4e0d\u63d0\u4e94\u9b54\u6b66\u529f\uff0c\u4e5f\u8bb8\u8981\u65b9\u65bd\u4e3b\u4e0d\u53ef\u5192\u9669\u53bb\u627e\u90a3\u4e94\u5927\u9b54\u4e3b\u62fc\u6597\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u7b11\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u4f60\u53ef\u771f\u662f\u8bf4\u5bf9\u4e86\uff0c\u8001\u673d\u5fc3\u4e2d\uff0c\u4e5f\u662f\u8fd9\u4e48\u76d8\u7b97\u5462\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u6697\u9053\uff1a\u53ea\u6015\u4f60\u4eec\u90fd\u731c\u9519\u4e86\u554a\uff01\u5148\u5e08\u4e0d\u8bf4\uff0c\u4e43\u662f\u56e0\u4e3a\u6211\u7684\u6b66\u529f\u5df2\u6210\uff0c\u4e94\u9b54\u4fbf\u4e0d\u662f\u6211\u7684\u5bf9\u624b\u4e86\u2026\u2026 \u3000\u3000\u4ed6\u5fc3\u4e2d\u867d\u7136\u5728\u60f3\uff0c\u53e3\u4e2d\u5374\u4e0d\u66fe\u8bf4\u51fa\u3002 \u3000\u3000\u5165\u4e91\u5927\u5e08\u5408\u5341\u7b11\u9053\uff1a\u201c\u5e2e\u4e3b\u65e9\u5df2\u731c\u51fa\uff0c\u5374\u662f\u4e0d\u80af\u8a00\u660e\uff0c\u8db3\u89c1\u5e2e\u4e3b\u8c26\u96c5\u4e4b\u91cd\uff0c\u8001\u8872\u8eab\u5165\u4f5b\u95e8\uff0c\u4f9d\u7136\u4e0d\u80fd\u51c0\u7edd\u540d\u5229\uff0c\u771f\u662f\u7f6a\u8fc7\u5f97\u5f88\u3002\u201d \u3000\u3000\u4f59\u97f3\u672a\u5df2\uff0c\u53ea\u89c1\u4e09\u540d\u4e2d\u5e74\u50e7\u4fa3\uff0c\u5728\u9759\u5ba4\u95e8\u5916\u73b0\u8eab\u3002 \u3000\u3000\u5165\u4e91\u767d\u7709\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u6709\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5f53\u5148\u7684\u4e00\u50e7\uff0c\u5408\u5341\u606d\u8eab\u9053\uff1a\u201c\u542f\u7980\u638c\u95e8\u5e08\u4f2f\uff0c\u90a3\u5192\u5145\u4e10\u5e2e\u4e3b\u4e4b\u4eba\uff0c\u4e5f\u5df2\u9003\u79bb\u672c\u5bfa\u4e86\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u9003\u8d70\u4e86\uff1f\u201d \u3000\u3000\u90a3\u4e2d\u5e74\u50e7\u4eba\u9053\uff1a\u201c\u5728\u521d\u7956\u5eb5\u524d\uff0c\u66fe\u88ab\u4e3b\u6301\u521d\u7956\u5eb5\u7684\u6cd5\u679c\u5e08\u5144\u53d1\u73b0\uff0c\u4f46\u5bf9\u65b9\u6b66\u529f\u6781\u9ad8\uff0c\u867d\u7136\u6cd5\u679c\u5e08\u5144\u7387\u9886\u4e03\u540d\u5f1f\u5b50\u62e6\u622a\uff0c\u4ecd\u88ab\u4ed6\u9041\u53bb\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u6cd5\u679c\u73b0\u5728\u4f55\u5904\uff1f\u201d \u3000\u3000\u4e2d\u5e74\u50e7\u4eba\u9053\uff1a\u201c\u6cd5\u679c\u5e08\u5144\u88ab\u4ed6\u4eec\u51fb\u6210\u91cd\u4f24\uff0c\u73b0\u6b63\u5728\u521d\u7956\u5eb5\u4e2d\u5f85\u6551\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u8138\u8272\u5927\u53d8\uff0c\u9053\uff1a\u201c\u6709\u8fd9\u7b49\u4e8b\uff1f\u201d \u3000\u3000\u663e\u7136\uff0c\u90a3\u6cd5\u679c\u548c\u5c1a\uff0c\u5728\u4ed6\u5fc3\u76ee\u4e2d\u751a\u6709\u4efd\u91cf\u3002 \u3000\u3000\u8fd9\u65f6\u5165\u5723\u5927\u5e08\u5ffd\u7136\u6c89\u58f0\u9053\uff1a\u201c\u5de1\u5c71\u5b50\u5f1f\uff0c\u53ef\u6709\u56de\u62a5\uff1f\u201d \u3000\u3000\u90a3\u4e2d\u5e74\u50e7\u4eba\u6447\u5934\u9053\uff1a\u201c\u5c1a\u65e0\u6d88\u606f\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u559d\u9053\uff1a\u201c\u6cd5\u56e0\uff0c\u4f20\u6211\u53e3\u8c15\uff0c\u8981\u5bfa\u4e2d\u4e0d\u5f53\u8bfe\u503c\u7684\u5f1f\u5b50\uff0c\u5168\u90e8\u53c2\u52a0\u641c\u5bfb\u2026\u2026\u201d \u3000\u3000\u90a3\u4e2d\u5e74\u50e7\u4eba\u5408\u5341\u9053\uff1a\u201c\u5f1f\u5b50\u9075\u547d\u2026\u2026\u201d\u8f6c\u8eab\u51fa\u9662\u800c\u53bb\u3002 \u3000\u3000\u970d\u9e23\u98ce\u76ee\u5149\u4e00\u8f6c\uff0c\u957f\u957f\u7684\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u4e3a\u4e86\u672c\u5e2e\u53db\u5f92\uff0c\u7adf\u7136\u52b3\u52a8\u4e86\u5c0a\u5bfa\u8bb8\u591a\u9ad8\u50e7\uff0c\u771f\u662f\u53eb\u4eba\u5fc3\u4e2d\u4e0d\u5b89\u5f97\u5f88\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u5408\u5341\u9053\uff1a\u201c\u5e2e\u4e3b\u8fd9\u4e48\u8bf4\uff0c\u5012\u53eb\u8001\u8872\u611f\u5230\u4e0d\u5b89\u4e86\uff01\u8fd9\u4e8b\u8bf4\u6765\u60ed\u6127\uff0c\u8001\u8872\u8fa8\u8bc6\u4e0d\u660e\uff0c\u624d\u4f1a\u4f7f\u90a3\u5047\u5192\u4e4b\u5f92\u6e9c\u8d70\uff0c\u672c\u5bfa\u5f53\u771f\u662f\u6127\u5bf9\u5e2e\u4e3b\u5f97\u5f88\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u95fb\u8a00\uff0c\u970d\u7136\u7ad9\u4e86\u8d77\u6765\uff0c\u62b1\u62f3\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u5c11\u6797\u6b66\u6797\u4e2d\uff0c\u679c\u7136\u4e0d\u662f\u865a\u4f20\uff0c\u4eca\u65e5\u4e00\u89c1\u638c\u95e8\u4eba\u8fd9\u7b49\u8c26\u900a\u80f8\u895f\uff0c\u771f\u53eb\u8001\u673d\u94a6\u4f69\u65e0\u5df2\uff01\u201d \u3000\u3000\u56e0\u662f\u4ed6\u8d77\u8eab\u957f\u63d6\uff0c\u8fde\u5d14\u5927\u516c\u4e5f\u8fde\u5fd9\u8d77\u8eab\uff0c\u5782\u624b\u8083\u7acb\uff0c\u4e00\u6d3e\u606d\u656c\u4e4b\u8272\u3002 \u3000\u3000\u5165\u4e91\u5927\u5e08\u7779\u72b6\uff0c\u4e5f\u8d77\u8eab\u8fd8\u793c\uff0c\u6717\u6717\u7b11\u9053\uff1a\u201c\u5e2e\u4e3b\uff0c\u4f60\u8fd9\u662f\u592a\u89c1\u5916\u4e86\u2026\u2026\u201d \u3000\u3000\u5bb9\u5f97\u53cc\u65b9\u91cd\u65b0\u5165\u5ea7\uff0c\u5b8b\u5b5a\u5ffd\u7136\u4e00\u7b11\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u8001\u673d\u6709\u4e00\u4ef6\u4e8b\uff0c\u5728\u5ea7\u524d\u8bf7\u6559\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u7b11\u9053\uff1a\u201c\u8001\u65bd\u4e3b\u6709\u4f55\u89c1\u6559\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u62cd\u62cd\u809a\u76ae\uff0c\u5927\u7b11\u9053\uff1a\u201c\u533a\u533a\u7684\u4e94\u810f\u5e99\u4f3c\u4e4e\u8981\u4fee\u7406\u4e86\uff01\u201d \u3000\u3000\u65b9\u5b9c\u96ea\u542c\u5f97\u51e0\u4e4e\u5931\u58f0\u7b11\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4f46\u5165\u4e91\u5927\u5e08\u5374\u5fcd\u4e0d\u4f4f\u7b11\u9053\uff1a\u201c\u65bd\u4e3b\u95ee\u5f97\u597d\uff0c\u8001\u8872\u5f53\u771f\u758f\u5ffd\u4e86\uff01\u201d \u3000\u3000\u8f6c\u5934\u5411\u5916\u559d\u9053\uff1a\u201c\u5feb\u5c06\u7d20\u7b75\u5f00\u5728\u4fa7\u9662\u4e4b\u4e2d\uff01\u201d \u3000\u3000\u8fc7\u4e0d\u4e86\u591a\u4e45\uff0c\u4e00\u684c\u6781\u4e3a\u7cbe\u81f4\u7684\u7d20\u7b75\uff0c\u9001\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u5165\u4e91\u5927\u5e08\u62db\u547c\u4e86\u4f17\u4eba\u5165\u5ea7\uff0c\u5b8b\u5b5a\u8feb\u4e0d\u53ca\u5f85\u5730\u6367\u8d77\u4e00\u53e3\u9152\u7f38\uff0c\u4e00\u8fde\u704c\u4e86\u5341\u4f59\u53e3\uff0c\u8fd9\u624d\u62b9\u7740\u5634\uff0c\u53eb\u9053\uff1a\u201c\u597d\u9152\uff0c\u597d\u9152\uff01\u60f3\u4e0d\u5230\u5c11\u6797\u5bfa\u4e2d\uff0c\u7adf\u7136\u85cf\u6709\u8fd9\u7b49\u6843\u5b50\u6e05\u917f\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5f88\u5c11\u996e\u9152\uff0c\u4f46\u4eca\u65e5\u4e5f\u559d\u4e86\u4e24\u676f\uff0c\u4e00\u5e2d\u5403\u7f62\uff0c\u5df2\u662f\u9ec4\u660f\u65f6\u5206\u3002 \u3000\u3000\u5165\u4e91\u5927\u5e08\u8981\u5c0f\u6c99\u5f25\u64a4\u4e0b\u6b8b\u5e2d\uff0c\u6362\u4e0a\u8336\u679c\uff0c\u5ba4\u4e2d\u4eae\u8d77\u706f\u706b\uff0c\u6b63\u5f85\u4f5c\u7adf\u591c\u957f\u8c08\uff0c\u53ea\u89c1\u5148\u524d\u6765\u8fc7\u7684\u4e2d\u5e74\u50e7\u4eba\u6cd5\u56e0\u5927\u5e08\u5144\uff0c\u5306\u5306\u8d76\u4e86\u8fdb\u6765\u3002\u5165\u4e91\u638c\u95e8\u4eba\u4e00\u89c1\uff0c\u4e0d\u7981\u7709\u5934\u76b1\u8d77\uff0c\u559d\u9053\uff1a \u3000\u3000\u201c\u6cd5\u56e0\uff0c\u4ec0\u4e48\u4e8b\u8fd9\u7b49\u614c\u5f20\uff1f\u201d \u3000\u3000\u6cd5\u56e0\u5927\u5e08\u5408\u5341\u9053\uff1a\u201c\u542f\u7980\u638c\u95e8\u4eba\uff0c\u672c\u5e08\u6d3e\u51fa\u7684\u5f1f\u5b50\uff0c\u53c8\u6709\u5341\u4eba\u88ab\u5bb3\uff01\u201d \u3000\u3000\u5165\u4e91\u638c\u95e8\u4eba\u95fb\u8a00\u4e00\u6014\uff0c\u9053\uff1a\u201c\u53ef\u77e5\u4f55\u4eba\u4e0b\u7684\u6bd2\u624b\uff1f\u201d \u3000\u3000\u6cd5\u56e0\u5927\u5e08\u770b\u4e86\u970d\u9e23\u98ce\u4e00\u773c\uff1b\u9053\uff1a\u201c\u90a3\u88ab\u5bb3\u5b50\u5f1f\u7684\u80f8\u524d\u591a\u6709\u4e00\u53ea\u638c\u5370\uff0c\u636e\u5f1f\u5b50\u731c\u60f3\uff0c\u53ef\u80fd\u662f\u4f24\u5728\u5bf9\u65b9\u6781\u91cd\u7684\u624b\u6cd5\u4e4b\u4e0b\u2026\u2026\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u5ffd\u7136\u51b7\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u6cd5\u56e0\uff0c\u4f60\u60f3\u5fc5\u5df2\u7136\u77a7\u51fa\u90a3\u662f\u4f55\u79cd\u6b66\u529f\u4e86\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u80af\u8bf4\u51fa\u6765\uff1f\u201d \u3000\u3000\u6cd5\u56e0\u5927\u5e08\u6014\u6014\u5730\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u5f1f\u5b50\u4e0d\u6562\u4e71\u8bf4\uff01\u201d \u3000\u3000\u5165\u5723\u559d\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6cd5\u56e0\u53c8\u770b\u4e86\u970d\u9e23\u98ce\u4e00\u773c\uff0c\u9053\uff1a\u201c\u8fd9\u2026\u2026\u970d\u5e2e\u4e3b\u5728\u6b64\uff0c\u5f1f\u5b50\u5982\u662f\u8bf4\u51fa\uff0c\u5c82\u4e0d\u662f\u2026\u2026\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u4f4f\u53e3\u4e0d\u8bed\uff0c\u4f3c\u662f\u6709\u7740\u96be\u8a00\u4e4b\u9690\u3002 \u3000\u3000\u970d\u9e23\u98ce\u8fd9\u65f6\u5df2\u731c\u51fa\u4e86\u5927\u534a\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u77e5\u5ba2\u5927\u5e08\u4e0d\u7528\u4e3a\u96be\uff0c\u90a3\u79cd\u638c\u6cd5\uff0c\u9887\u4f3c\u672c\u5e2e\u6b66\u529f\u6467\u5fc3\u638c\uff0c\u662f\u4e5f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u9053\uff1a\u201c\u539f\u6765\u8fd9\u4f4d\u548c\u5c1a\u4e43\u662f\u5bfa\u4e2d\u77e5\u5ba2\uff01\u201d \u3000\u3000\u8033\u4e2d\u542c\u5f97\u6cd5\u56e0\u77e5\u5ba2\u63a5\u53e3\uff0c\u8bf4\u9053\uff1a\u201c\u5e2e\u4e3b\u6240\u8bf4\u751a\u662f\uff0c\u665a\u8f88\u679c\u662f\u7591\u5fc3\u90a3\u79cd\u638c\u529b\uff0c\u4e43\u662f\u8d35\u5e2e\u6467\u5fc3\u638c\uff01\u201d \u3000\u3000\u5165\u4e91\u638c\u95e8\u4eba\u8138\u8272\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u6cd5\u56e0\uff0c\u4f60\u662f\u5426\u4eb2\u773c\u770b\u5230\u4e86\uff1f\u201d \u3000\u3000\u6cd5\u56e0\u9053\uff1a\u201c\u5f1f\u5b50\u4eb2\u773c\u77a7\u5230\u4e86\u2026\u2026\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u8fd8\u662f\u9002\u624d\u5192\u5145\u970d\u5e2e\u4e3b\u7684\u90a3\u6279\u4eba\u7269\u4e86\uff1f\u201d \u3000\u3000\u6cd5\u56e0\u5408\u5341\u9053\uff1a\u201c\u5f1f\u5b50\u731c\u60f3\u5982\u6b64\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u4f20\u6211\u53e3\u8c15\uff0c\u5404\u5802\u957f\u8001\uff0c\u5728\u7f57\u6c49\u5802\u805a\u9f50\uff0c\u672c\u5ea7\u8981\u4eb2\u81ea\u770b\u770b\u8fd9\u4f4d\u5047\u5192\u970d\u5e2e\u4e3b\u4e4b\u4eba\uff0c\u7a76\u7adf\u662f\u8c01\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u9053\uff1a\u8fd9\u8001\u548c\u5c1a\u5012\u662f\u52a8\u4e86\u771f\u6c14\u4e86\u2026\u2026 \u3000\u3000\u6cd5\u56e0\u5927\u5e08\u5e94\u4e86\u4e00\u58f0\u9075\u547d\uff0c\u6b63\u5f85\u79bb\u53bb\uff0c\u53ea\u89c1\u970d\u9e23\u98ce\u5ffd\u7136\u7ad9\u4e86\u8d77\u6765\uff0c\u559d\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u76b1\u7709\u9053\uff1a\u201c\u5e2e\u4e3b\u4f55\u6545\u559d\u963b\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u5f0a\u5e2e\u51fa\u4e86\u53db\u5f92\uff0c\u8fde\u4f24\u8d35\u5bfa\u8bb8\u591a\u95e8\u4e0b\uff0c\u8001\u673d\u5fc3\u4e2d\u5df2\u662f\u5341\u5206\u4e0d\u5b89\uff0c\u5982\u662f\u518d\u884c\u60ca\u52a8\u5404\u5802\u957f\u8001\uff0c\u90a3\u5c82\u4e0d\u53eb\u970d\u67d0\u66f4\u4e3a\u8d1f\u7f6a\u5417\uff1f\u662f\u4ee5\u2026\u2026\u201d\u4ed6\u76ee\u5149\u4e00\u8f6c\uff0c\u770b\u4e86\u8fdf\u7591\u672a\u51b3\uff0c\u6b32\u884c\u672a\u884c\u7684\u6cd5\u56e0\u77e5\u5ba2\u4e00\u773c\uff0c\u63a5\u9053\uff1a\u201c\u970d\u67d0\u8ba4\u4e3a\uff0c\u8fd9\u4e8b\u65e2\u7136\u5230\u4e86\u8fd9\u7b49\u5730\u6b65\uff0c\u5e94\u662f\u4ea4\u7531\u970d\u67d0\u4eb2\u81ea\u524d\u5904\u7406\u4e86\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u5e2e\u4e3b\uff0c\u90a3\u5bfb\u4e8b\u4e4b\u4eba\uff0c\u786e\u662f\u8d35\u5e2e\u53db\u5f92\uff0c\u4f46\u6ecb\u4e8b\u7684\u5bf9\u8c61\uff0c\u5374\u662f\u672c\u5bfa\u50e7\u4fa3\uff0c\u5982\u662f\u5168\u9762\u7531\u5e2e\u4e3b\u524d\u53bb\u5904\u7406\uff0c\u53ea\u6015\u2026\u2026\u53ea\u6015\u2026\u2026\u201d \u3000\u3000\u970d\u9e23\u98ce\u7779\u72b6\uff0c\u4e0d\u7531\u5f97\u4e00\u6123\uff0c\u63a5\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u53ea\u6015\u4ec0\u4e48\u554a\uff1f\u8bf4\u5c06\u51fa\u6765\uff0c\u54b1\u4eec\u4e5f\u597d\u5546\u91cf\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u5e2e\u4e3b\uff0c\u5c11\u6797\u58f0\u8a89\uff0c\u603b\u4e0d\u80fd\u7838\u5728\u8d35\u5e2e\u8fd9\u6279\u53db\u5f92\u8eab\u4e0a\uff0c\u5e2e\u4e3b\u8ba4\u4e3a\u8001\u8872\u7684\u60f3\u6cd5\u53ef\u5bf9\u5417\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u5ffd\u7136\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u7528\u8fd9\u4e2a\u90a3\u4e2a\u4e86\uff01\u8001\u5077\u513f\u8bf4\u53e5\u516c\u9053\u8bdd\uff0c\u4eca\u65e5\u54b1\u4eec\u65e2\u7136\u6765\u4e86\u5c11\u6797\uff0c\u90a3\u90fd\u662f\u4e3a\u4e86\u5bf9\u4ed8\u9b54\u9053\u4e4b\u4eba\u800c\u6765\uff0c\u4efb\u4f55\u98ce\u5439\u8349\u52a8\u4e4b\u4e8b\uff0c\u54b1\u4eec\u90fd\u6709\u8d23\u4efb\uff0c\u8981\u627e\u51fa\u90a3\u6ecb\u4e8b\u7684\u53db\u5f92\uff0c\u54b1\u4eec\u4e5f\u5927\u4f19\u513f\u6709\u4efd\uff0c\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u4ed6\u8bdd\u97f3\u4e00\u843d\uff0c\u5d14\u5927\u516c\u7acb\u5373\u7b11\u9053\uff1a\u201c\u5b8b\u5144\u8bf4\u7684\u4e0d\u9519\uff0c\u4e3a\u4e86\u964d\u9b54\u51b2\u9053\uff0c\u54b1\u4eec\u679c\u771f\u662f\u4eba\u4eba\u6709\u8d23\u7684\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u84e6\u5730\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u9519\u554a\uff0c\u54b1\u4eec\u4e5f\u8be5\u6cef\u9664\u95e8\u6237\u4e4b\u5916\uff0c\u5171\u540c\u4e3a\u7ef4\u6301\u6b66\u6797\u6b63\u4e49\uff0c\u9f50\u5fc3\u534f\u529b\u4ee5\u8d74\u4e86\uff01\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u62b1\u62f3\u5411\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u4eca\u65e5\u4e00\u5207\uff0c\u542c\u4f60\u8c03\u5ea6\uff0c\u8001\u673d\u4e0e\u5e2e\u4e3b\u4e2d\u5b50\u5f1f\uff0c\u6089\u5019\u5dee\u9063\u4fbf\u4e86\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u6447\u5934\u9053\uff1a\u201c\u90a3\u600e\u4e48\u53ef\u4ee5\u554a\uff1f\u5e2e\u4e3b\u5982\u6b64\u8c26\u8ba9\uff0c\u90a3\u662f\u53eb\u8001\u8872\u4e0b\u4e0d\u4e86\u53f0\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u4eca\u65e5\u4e4b\u4e8b\uff0c\u4e43\u662f\u53d1\u751f\u5728\u5c11\u6797\u5bfa\u4e2d\uff0c\u8001\u673d\u81ea\u5e94\u5019\u547d\u5dee\u9063\uff0c\u5f02\u65e5\u5982\u662f\u6709\u4e8b\u53d1\u751f\u5728\u672c\u5e2e\u603b\u575b\uff0c\u5230\u90a3\u65f6\uff0c\u53ea\u6015\u638c\u95e8\u4eba\u5c31\u4e0d\u5f97\u4e0d\u542c\u547d\u4e8e\u8001\u673d\u4e86\u5462\uff01\u201d \u3000\u3000\u4ed6\u8fd9\u4e48\u4e00\u8bf4\uff0c\u5165\u4e91\u5927\u5e08\u5012\u662f\u4e0d\u597d\u518d\u63a8\u8c22\u4e86\uff01 \u3000\u3000\u7565\u4e00\u6c89\u541f\u4e4b\u9645\uff0c\u5b8b\u5b5a\u5df2\u5927\u58f0\u9053\uff1a\u201c\u4e24\u4f4d\uff0c\u4f60\u4eec\u518d\u662f\u8fd9\u4e48\u63a8\u6765\u63a8\u53bb\uff0c\u5c11\u6797\u5b50\u5f1f\u591a\u6b7b\u51e0\u4eba\u4e8b\u5c0f\uff0c\u53ea\u6015\u8981\u8ba9\u90a3\u53db\u5f92\u9003\u51fa\u5d69\u5c71\uff0c\u518d\u627e\u53ef\u5c31\u8fdf\u5566\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u5012\u662f\u4e00\u9488\u89c1\u8840\u4e4b\u8a00\u3002\u201d \u3000\u3000\u5f88\u4e45\u672a\u66fe\u8bf4\u8fc7\u4e00\u53e5\u8bdd\u7684\u5b89\u5c0f\u840d\uff0c\u5ffd\u7136\u4f4e\u58f0\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u54b1\u4eec\u5148\u51fa\u53bb\u770b\u770b\u53ef\u597d\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u6014\u9053\uff1a\u201c\u90a3\u600e\u4e48\u884c\uff1f\u54b1\u4eec\u4e5f\u5f97\u7b49\u90a3\u5165\u4e91\u5927\u5e08\u8c03\u5ea6\u554a\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u76b1\u7709\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u6211\u4eec\u6765\u6b64\u662f\u5ba2\uff0c\u5e72\u5417\u8981\u542c\u4ed6\u6307\u4f7f\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u8d24\u5f1f\uff0c\u4eba\u5bb6\u4e43\u662f\u4e00\u6d3e\u638c\u95e8\uff0c\u518d\u8bf4\u5e74\u7eaa\u4e5f\u6bd4\u6211\u4eec\u5927\u4e86\u8bb8\u591a\uff0c\u54b1\u4eec\u5c31\u7b97\u542c\u547d\u4e8e\u4ed6\uff0c\u90a3\u4e5f\u4e0d\u7b97\u4e22\u4eba\u4e4b\u4e8b\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u65b9\u5144\uff0c\u6709\u4e00\u4ef6\u4e8b\uff0c\u53ea\u6015\u4f60\u6ca1\u6709\u60f3\u5230\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u5462\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u5c11\u6797\u50e7\u4f17\u7684\u6b66\u529f\u4e0d\u5179\uff0c\u5c1a\u4e14\u843d\u5f97\u5982\u6b64\u60c5\u51b5\uff0c\u5c31\u7b97\u4e10\u5e2e\u8fd9\u51e0\u4f4d\u957f\u8001\u51fa\u624b\uff0c\u662f\u5426\u53ef\u4ee5\u4e00\u4e3e\u51fb\u6bd9\u90a3\u4e48\u591a\u4eba\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u4e86\u4e00\u5446\u9053\uff1a\u201c\u8fd9\u2026\u2026\u8d24\u5f1f\u53ef\u662f\u8ba4\u4e3a\u90a3\u4e00\u6279\u53db\u5f92\u4e4b\u4e2d\uff0c\u8fd8\u6709\u6b66\u529f\u6781\u9ad8\u4e4b\u4eba\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u5f53\u7136\u662f\u6709\u554a\uff01\u4e0d\u7136\uff0c\u4ed6\u4eec\u54ea\u6709\u90a3\u4e48\u5927\u7684\u80c6\u5b50\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6c89\u541f\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u4e8b\u60c5\u53d8\u5f97\u5f88\u590d\u6742\u4e86\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u65e9\u5c31\u5f88\u590d\u6742\u4e86\uff01\u65b9\u5144\uff0c\u4f60\u6700\u597d\u662f\u5411\u5165\u4e91\u5927\u5e08\u8bf4\u4e00\u58f0\uff0c\u54b1\u4eec\u4e0d\u59a8\u5148\u51fa\u53bb\u770b\u770b\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u2026\u2026\u201d \u3000\u3000\u4ed6\u8fdf\u7591\u4e86\u4e00\u4e0b\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u59a5\u5f53\u5427\uff01\u6025\uff0c\u4e5f\u4e0d\u7528\u6025\u5728\u8fd9\u4e00\u65f6\u554a\uff01\u201d \u3000 \u7b2c\u4e8c\u5341\u4e00\u56de\u3000\u5929\u9b54\u5973\u5f92 \u3000 \u3000\u3000\u65b9\u96ea\u5b9c\u548c\u5b89\u5c0f\u840d\u867d\u662f\u8033\u8bed\uff0c\u4f46\u5374\u7792\u4e0d\u8fc7\u5728\u573a\u7684\u8fd9\u7fa4\u9ad8\u624b\uff0c\u65b9\u96ea\u5b9c\u8bdd\u97f3\u752b\u843d\uff0c\u5165\u4e91\u5927\u5e08\u5df2\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u4e24\u4f4d\u5c0f\u65bd\u4e3b\u4e43\u662f\u672c\u5bfa\u8d35\u5bbe\uff0c\u4e00\u5207\u884c\u52a8\uff0c\u81ea\u662f\u4e0d\u53d7\u7ea6\u675f\uff0c\u672c\u5bfa\u4e4b\u4e2d\uff0c\u9664\u4e86\u90a3\u85cf\u7ecf\u697c\u800c\u5916\uff0c\u4e24\u4f4d\u5c3d\u53ef\u8fdb\u51fa\u81ea\u5982\u7684\u4e86\uff01\u201d \u3000\u3000\u8fd9\u5c31\u662f\u8bf4\uff0c\u4f60\u4eec\u53ef\u4ee5\u4efb\u610f\u53bb\u7559\u7684\u4e86\uff01 \u3000\u3000\u5b89\u5c0f\u840d\u95fb\u8a00\uff0c\u7acb\u5373\u62c9\u4e86\u65b9\u96ea\u5b9c\uff0c\u7ad9\u8d77\u8eab\u6765\uff0c\u7b11\u9053\uff1a\u201c\u591a\u8c22\u638c\u95e8\u4eba\u539a\u5f85\u2026\u2026\u201d \u3000\u3000\u8f6c\u8eab\u4fbf\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4ecd\u4e9b\u6709\u4e9b\u89c9\u7740\u4e0d\u59a5\uff0c\u6b63\u60f3\u518d\u8bf4\u4ec0\u4e48\uff0c\u53ea\u542c\u5f97\u5b8b\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u4e0d\u7528\u62d8\u675f\u90a3\u4fd7\u793c\u4e86\uff01\u638c\u95e8\u4eba\u65e2\u5df2\u8bf4\u51fa\u8fd9\u7b49\u8bdd\uff0c\u4f60\u66f4\u4e0d\u7528\u5ba2\u6c14\u4e86\uff01\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\u63a5\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u8001\u673d\u60f3\u8ddf\u8fd9\u4e24\u4f4d\u8001\u5f1f\u51fa\u53bb\u89c1\u8bc6\uff0c\u89c1\u8bc6\uff0c\u4e0d\u77e5\u638c\u95e8\u4eba\u53ef\u5426\u6069\u5178\u8001\u673d\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u7b11\u9053\uff1a\u201c\u65bd\u4e3b\u8bf7\u4fbf\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u4e00\u624b\u62c9\u4e86\u4e00\u4eba\uff0c\u5927\u6b65\u8d70\u51fa\u90a3\u9759\u9759\u7684\u7985\u9662\u3002 \u3000\u3000\u4e09\u4eba\u51fa\u4e86\u6708\u95e8\uff0c\u53ea\u89c1\u5929\u8272\u5df2\u7136\u6697\u4e86\u4e0b\u6765\uff0c\u5728\u6811\u6797\u7af9\u4e4b\u95f4\uff0c\u5374\u9690\u7ea6\u53ef\u89c1\u5c11\u6797\u50e7\u4fa3\u5954\u8d70\u4e0d\u606f\u3002 \u3000\u3000\u5b8b\u5b5a\u4f4e\u58f0\u9053\uff1a\u201c\u65b9\u8001\u5f1f\uff0c\u4ed6\u4eec\u6212\u5907\u5f97\u5f88\u4e25\u5bc6\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u770b\u6765\u5c11\u6797\u7684\u50e7\u4fa3\uff0c\u5f53\u771f\u5df2\u7ecf\u5403\u8fc7\u4ed6\u4eec\u7684\u82e6\u5934\u4e86\uff01\u201d \u3000\u3000\u4ed6\u6240\u6307\u7684\u81ea\u7136\u662f\u5929\u9b54\u5973\u548c\u90a3\u5192\u5145\u4e10\u5e2e\u5e2e\u4e3b\u4e4b\u4eba\u4e86\uff01 \u3000\u3000\u5b89\u5c0f\u840d\u8fd9\u65f6\u5374\u76b1\u7709\u9053\uff1a\u201c\u65b9\u5927\u54e5\uff0c\u54b1\u4eec\u53bb\u54ea\u91cc\uff1f\u53ea\u5728\u5bfa\u5185\u884c\u8d70\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u8fd9\u4e3b\u610f\u662f\u4f60\u51fa\u7684\uff0c\u5f53\u7136\u7531\u4f60\u6765\u4f5c\u4e3b\u7684\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u90a3\u6211\u4eec\u5c31\u51fa\u5bfa\u4e00\u884c\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u4e00\u6014\u9053\uff1a\u201c\u51fa\u5bfa\u4f55\u5f80\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u627e\u90a3\u5047\u5192\u5e2e\u4e3b\u4e4b\u4eba\u5440\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u5d69\u5c71\u5360\u5730\u6570\u767e\u91cc\u65b9\u5706\uff0c\u96be\u9053\u54b1\u4eec\u8981\u8dd1\u4e0a\u4e00\u591c\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u5fc5\u4e86\uff0c\u665a\u8f88\u76f8\u4fe1\uff0c\u4ed6\u4eec\u4e0d\u4f1a\u9000\u79bb\u5c11\u6797\uff01\u5728\u4ed6\u4eec\u80cc\u540e\u8fd8\u6709\u6307\u4f7f\u7684\u4eba\u5462\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8d24\u5f1f\u8ba4\u4e3a\u4ed6\u4eec\u9017\u7559\u5728\u5c11\u6797\u9644\u8fd1\uff0c\u4e43\u662f\u4ed7\u6043\u7740\u90a3\u5927\u9b54\u5973\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u4e3a\u4f55\u4e0d\u53ef\uff1f\u9664\u4e86\u5929\u9b54\u5973\uff0c\u53c8\u6709\u8c01\u6562\u4e0e\u5c11\u6797\u4f5c\u5bf9\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u4e2a\u5417\uff0c\u5728\u6211\u672a\u66fe\u89c1\u5230\u90a3\u5929\u9b54\u5973\u4ee5\u524d\uff0c\u8fd8\u4e0d\u6562\u76f8\u4fe1\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u5b8b\u8001\u4f60\u76f8\u4fe1\u665a\u8f88\u7684\u8bdd\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u600e\u6562\u4e0d\u4fe1\uff1f\u8fde\u90a3\u5929\u9b54\u5973\u8fd8\u8981\u5df4\u7ed3\u4f60\uff0c\u8001\u673d\u6bd4\u90a3\u5929\u9b54\u5973\u53ef\u5c31\u5dee\u591a\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u5b8b\u8001\u53d6\u7b11\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u76ee\u5149\u5411\u56db\u4e0b\u6253\u91cf\u4e86\u4e00\u756a\uff01\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u5b8b\u8001\u54b1\u4eec\u6253\u4ec0\u4e48\u65b9\u5411\u8d70\u554a\uff1f\u201d \u3000\u3000\u6562\u60c5\u4ed6\u672a\u66fe\u6765\u8fc7\u5c11\u6797\uff0c\u81ea\u662f\u88ab\u8fd9\u4e00\u5904\u4e00\u5904\u7684\u5173\u5361\u7ed9\u5f04\u8ff7\u7cca\u4e86\uff01 \u3000\u3000\u5b89\u5c0f\u840d\u4e5f\u6ca1\u6709\u6765\u8fc7\uff0c\u8fd9\u65f6\uff0c\u5f20\u4e86\u4e00\u53cc\u5927\u773c\uff0c\u770b\u770b\u5b8b\u5b5a\uff0c\u63a5\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u65b9\u5144\u8bf4\u5f97\u4e0d\u9519\uff0c\u8fd9\u5c11\u6797\u5bfa\u4e2d\uff0c\u5230\u5904\u90fd\u662f\u6bbf\u5b57\u4f5b\u50cf\uff0c\u54b1\u4eec\u522b\u95ef\u9519\u4e86\u5730\u65b9\u53ef\u5c31\u4e0d\u597d\u554a\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u4e24\u4f4d\u662f\u6253\u7b97\u53bb\u54ea\u91cc\uff1f\u524d\u9762\uff1f\u8fd8\u662f\u540e\u5c71\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u671b\u7740\u5b89\u5c0f\u840d\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4f60\u77e5\u9053\u8be5\u53bb\u54ea\u91cc\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6c89\u541f\u9053\uff1a\u201c\u521d\u7956\u5eb5\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u521d\u7956\u5eb5\u5728\u54ea\u91cc\uff1f\u54b1\u4eec\u53bb\u90a3\u513f\u4f5c\u751a\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u5fd8\u4e86\u90a3\u77e5\u5ba2\u5927\u5e08\u7684\u8bdd\u4e86\u5417\uff1f\u4ed6\u4e0d\u662f\u8bf4\uff0c\u5c11\u6797\u95e8\u4e0b\uff0c\u6709\u4e0d\u5c11\u4eba\u662f\u5728\u521d\u7956\u5eb5\u9644\u8fd1\u88ab\u4eba\u91cd\u521b\u800c\u6b7b\u561b\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u54b1\u4eec\u8d76\u53bb\uff0c\u4e5f\u8bb8\u4f1a\u78b0\u5230\u90a3\u5929\u9b54\u5973\u672c\u4eba\u4e86\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4e0d\u4e00\u5b9a\uff0c\u53cd\u6b63\u53bb\u8bd5\u8bd5\u561b\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u8fd9\u65f6\u7b11\u9053\uff1a\u201c\u8001\u4f4d\u8001\u5f1f\uff0c\u5018\u662f\u4f60\u4eec\u8981\u53bb\u521d\u7956\u5eb5\uff0c\u54b1\u4eec\u53ef\u5f97\u5411\u56de\u8d70\u4e86\uff01\u201d \u3000\u3000\u970d\u5730\u8f6c\u8eab\uff0c\u56de\u5934\u800c\u53bb\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u3001\u65b9\u96ea\u5b9c\u6014\u4e86\u4e00\u6014\uff0c\u60f3\u4e0d\u5230\u5b8b\u5b5a\u8bf4\u8d70\u5c31\u8d70\uff0c\u5f53\u4e0b\u4e92\u671b\u4e86\u4e00\u4e0b\uff0c\u53ea\u597d\u8ddf\u5728\u5b8b\u5b5a\u8eab\u540e\u884c\u53bb\u3002 \u3000\u3000\u7ed5\u8fc7\u4e86\u90a3\u95f4\u65b9\u4e08\u9759\u5ba4\uff0c\u4e09\u4eba\u76f4\u5954\u800c\u884c\uff0c\u7ea6\u83ab\u8d70\u4e86\u4e24\u91cc\u8fdc\u8fd1\uff0c\u4fbf\u5df2\u51fa\u4e86\u5c11\u6797\u5bfa\u7684\u5c71\u95e8\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u76b1\u7709\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u90a3\u521d\u7956\u5eb5\u5f88\u8fdc\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u6307\u4e86\u6307\u540e\u5c71\uff0c\u9053\uff1a\u201c\u4e0d\u8fdc\uff0c\u5c31\u5728\u90a3\u534a\u5c71\u4e4b\u4e0a\u3002\u201d \u3000\u3000\u4e09\u4eba\u653e\u5f00\u811a\u6b65\uff0c\u6025\u6025\u5954\u53bb\u3002 \u3000\u3000\u4e0d\u6d88\u76cf\u8336\u65f6\u5149\uff0c\u5df2\u7136\u6765\u5230\u4e00\u5904\u659c\u5761\uff0c\u5411\u4e0a\u671b\u53bb\uff0c\u53ea\u89c1\u4e00\u5904\u758f\u843d\u7684\u5ead\u56ed\uff0c\u63a9\u6620\u5728\u6797\u6728\u4e4b\u95f4\u3002 \u3000\u3000\u4e00\u7ebf\u706f\u5149\uff0c\u6253\u4ece\u679d\u53f6\u4e91\u9699\u4e4b\u4e2d\uff0c\u9732\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5b8b\u5b5a\u6307\u7740\u90a3\u706f\u5149\u4e4b\u5904\u9053\uff1a\u201c\u90a3\u4fbf\u662f\u521d\u7956\u5eb5\u4e86\uff01\u54b1\u4eec\u8981\u4e0d\u8981\u8fdb\u5185\u77a7\u77a7\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f53\u7136\u8981\u554a\uff01\u201d \u3000\u3000\u4f46\u5b89\u5c0f\u840d\u5ffd\u7136\u6447\u5934\u9053\uff1a\u201c\u54b1\u4eec\u5148\u5728\u8fd9\u56db\u5468\u67e5\u770b\u4e00\u756a\u2026\u2026\u201d \u3000\u3000\u4f59\u97f3\u672a\u7edd\uff0c\u5ffd\u7136\u8138\u8272\u4e00\u53d8\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u6709\u4eba\u6765\u4e86\uff0c\u54b1\u4eec\u5411\u90a3\u5927\u77f3\u540e\u907f\u4e00\u907f\uff01\u201d \u3000\u3000\u4e00\u62c9\u65b9\u96ea\u5b9c\uff0c\u4fbf\u5411\u4e00\u65c1\u95ea\u53bb\uff01 \u3000\u3000\u5b8b\u5b5a\u95fb\u8a00\uff0c\u76b1\u4e86\u4e00\u76b1\u7709\uff0c\u4e5f\u8ddf\u968f\u4e24\u4eba\u5411\u90a3\u77f3\u540e\u8eb2\u53bb\u3002 \u3000\u3000\u5c31\u5728\u4e09\u4eba\u9690\u597d\u8eab\u5f62\u7684\u5239\u90a3\uff0c\u53ea\u89c1\u4e09\u540d\u624b\u6267\u7985\u6756\u7684\u50e7\u4fa3\uff0c\u884c\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4ed6\u4eec\u5927\u7ea6\u662f\u5728\u8fdc\u5904\u542c\u5230\u51e0\u4eba\u8bf4\u8bdd\u4e4b\u58f0\uff0c\u65b9\u59cb\u8d76\u4e86\u8fc7\u6765\uff0c\u4f46\u4e34\u5230\u8fd1\u524d\uff0c\u5374\u4e0d\u89c1\u4eba\u8ff9\uff0c\u662f\u4ee5\u4e09\u4eba\u5374\u5728\u65b9\u96ea\u5b9c\u7b49\u9002\u624d\u7ad9\u4e4b\u5904\uff0c\u56db\u4e0b\u5f20\u671b\u3002 \u3000\u3000\u53ea\u542c\u5f97\u5176\u4e2d\u4e00\u4eba\u9053\uff1a\u201c\u5e08\u5144\uff0c\u4f60\u521a\u624d\u6ca1\u6709\u542c\u9519\u5427\u3002\u201d \u3000\u3000\u53e6\u4e00\u4eba\u7b54\u9053\uff1a\u201c\u5e08\u5f1f\u83ab\u975e\u8fde\u611a\u5144\u90fd\u4e0d\u76f8\u4fe1\u4e86\uff1f\u660e\u660e\u6709\u4eba\u5728\u6b64\u8bf4\u8bdd\uff0c\u600e\u5730\u54b1\u4eec\u8d76\u6765\u5c31\u4e0d\u89c1\u4eba\u5f71\u5462\uff1f\u201d \u3000\u3000\u5148\u524d\u90a3\u4eba\u9053\uff1a\u201c\u5e08\u5144\uff0c\u54b1\u4eec\u8981\u4e0d\u8981\u5728\u8fd9\u91cc\uff0c\u7b49\u4e0a\u4e00\u4f1a\u5462\uff1f\u4e5f\u8bb8\u2026\u2026\u201d \u3000\u3000\u53ea\u542c\u90a3\u4f4d\u5e08\u5144\u63a5\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u54b1\u4eec\u8eab\u5728\u660e\u5904\uff0c\u81ea\u7136\u662f\u4e0d\u4fbf\u5728\u6b64\u5b88\u682a\u5f85\u5154\u822c\u5446\u5f85\u4e86\uff01\u201d \u3000\u3000\u8bed\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u8fd9\u4e48\u529e\uff0c\u54b1\u4eec\u53ea\u5f53\u4f5c\u4e0d\u77e5\uff0c\u5411\u5c71\u4e0b\u5de1\u67e5\u8fc7\u53bb\u5427\uff01\u201d \u3000\u3000\u4e09\u50e7\u53c8\u9644\u8033\u5600\u5495\u4e86\u4e00\u9635\uff0c\u4fbf\u5411\u5c71\u4e0b\u8d70\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u9053\uff1a\u201c\u8fd9\u4e9b\u548c\u5c1a\u679c\u7136\u673a\u8b66\u5f97\u5f88\uff0c\u4ed6\u4eec\u4e3a\u4f55\u4e0d\u5411\u56db\u4e0b\u91cc\u627e\u627e\u5462\uff1f\u96be\u9053\u2026\u2026\u201d \u3000\u3000\u5ffd\u7136\u8033\u4e2d\u4f20\u6765\u5b8b\u5b5a\u4f20\u97f3\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u8fd9\u6279\u5c11\u6797\u50e7\u4eba\uff0c\u5f53\u771f\u88ab\u5929\u9b54\u5973\u5413\u574f\u4e86\uff0c\u77a7\u4ed6\u4eec\u795e\u6001\u4f3c\u662f\u660e\u77e5\u6211\u4eec\u8eb2\u5728\u8fd9\u77f3\u540e\uff0c\u5374\u4e5f\u4e0d\u6562\u524d\u6765\u6253\u6270\uff0c\u6bcb\u602a\u4ed6\u4eec\u4f1a\u4f24\u4ea1\u90a3\u4e48\u591a\u4eba\u4e86\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e5f\u7528\u4f20\u97f3\u63a5\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u4e9b\u548c\u5c1a\u679c\u771f\u662f\u611a\u7b28\u5f97\u5f88\uff0c\u8fd9\u7b49\u5bb9\u6613\u60f3\u5230\u7684\u4e8b\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u4f1a\u60f3\u53ca\u5462\u2026\u2026\u201d \u3000\u3000\u4f59\u97f3\u672a\u5df2\uff0c\u4e2d\u89c1\u90a3\u51e0\u540d\u50e7\u4eba\u7684\u6765\u8def\u5c3d\u5934\uff0c\u53c8\u6709\u51e0\u6761\u4eba\u5f71\u5feb\u6b65\u884c\u6765\u3002 \u3000\u3000\u5b8b\u5b5a\u5ffd\u7136\u7528\u4f20\u97f3\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u53c8\u6709\u4eba\u6765\u4e86\uff0c\u4e14\u770b\u770b\u4ed6\u4eec\u662f\u4ec0\u4e48\u8def\u6570\uff01\u201d \u3000\u3000\u4e09\u4eba\u5c4f\u58f0\u606f\u6c14\uff0c\u4e3e\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u90a3\u884c\u6765\u7684\u51e0\u6761\u4eba\u5f71\uff0c\u8eab\u6750\u4e0d\u9ad8\uff0c\u4eff\u4f5b\u4e43\u662f\u5973\u5b50\u4e00\u822c\uff0c\u5b89\u5c0f\u840d\u4e0d\u7981\u4f4e\u58f0\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u548c\u5c1a\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7528\u4f20\u97f3\u63a5\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u4e9b\u4eba\u8eab\u6750\u8fd9\u822c\u77ee\u5c0f\uff0c\u60f3\u5fc5\u4e43\u662f\u90a3\u5929\u9b54\u5973\u7684\u5c5e\u4e0b\u5b50\u5f1f\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u63a5\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u54b1\u4eec\u8981\u4e0d\u8981\u51fa\u53bb\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u522b\u5fd9\uff0c\u5148\u770b\u770b\u4ed6\u4eec\u4e8e\u4e9b\u4ec0\u4e48\u518d\u8bf4\uff01\u201d \u3000\u3000\u4ed6\u8bed\u97f3\u672a\u7edd\uff0c\u53ea\u89c1\u90a3\u4e09\u540d\u50e7\u4eba\u5ffd\u7136\u6389\u5934\u5954\u4e86\u8fc7\u6765\uff01 \u3000\u3000\u6562\u60c5\uff0c\u8fd9\u4e09\u4f4d\u50e7\u4eba\u5e76\u4e0d\u771f\u7b28\uff0c\u4e0d\u8fc7\u662f\u7528\u7684\u58f0\u4e1c\u51fb\u897f\u7684\u8bf1\u654c\u4e4b\u8ba1\u800c\u5df2\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u89c1\u90a3\u4e09\u540d\u5c11\u6797\u50e7\u4fa3\uff0c\u5ffd\u7136\u6298\u56de\u5934\u6765\uff0c\u5fc3\u4e2d\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u8fd9\u4e09\u4f4d\u50e7\u4eba\u53ea\u6015\u8981\u5403\u4e8f\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e0d\u4e00\u5b9a\uff0c\u77a7\u4ed6\u4eec\u8fd9\u7b49\u8bf1\u654c\u7684\u65b9\u5f0f\uff0c\u597d\u50cf\u662f\u6709\u6043\u65e0\u6050\uff0c\u8bf4\u4e0d\u5b9a\u4ed6\u4eec\u65e9\u5df2\u6709\u7740\u5236\u654c\u4e4b\u7b56\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u8f7b\u8f7b\u4e00\u7b11\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u6211\u770b\u6709\u4e9b\u4e0d\u89c1\u5f97\u554a\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8001\u5f1f\u6709\u4f55\u53d1\u73b0\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u4f60\u8001\u4f55\u4e0d\u8bd5\u76ee\u4ee5\u5f85\u5462\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u59d1\u5a18\u4f60\u8fd9\u662f\u4f55\u610f\uff1f\u83ab\u975e\u4f60\u8ba4\u4e3a\u90a3\u5c11\u6797\u5c1a\u53c8\u8981\u6d89\u9669\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u4eec\u53ea\u6015\u96be\u4ee5\u9003\u8fc7\u8fd9\u4e94\u540d\u9b54\u5973\u7684\u638c\u624b\u3002\u201d \u3000\u3000\u6562\u60c5\u90a3\u51e0\u540d\u73b0\u8eab\u7684\u4eba\u5f71\uff0c\u5df2\u53ef\u6e05\u6670\u5730\u770b\u51fa\u6765\uff0c\u4e43\u662f\u4e94\u540d\u5c11\u5973\u3002 \u3000\u3000\u5b8b\u5b5a\u7709\u4e00\u76b1\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u54b1\u4eec\u8981\u4e0d\u8981\u52a9\u90a3\u548c\u5c1a\u4e00\u81c2\u4e4b\u529b\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u4f9d\u4f60\u8001\u4e4b\u89c1\uff0c\u54b1\u4eec\u8fd8\u662f\u4e0d\u59a8\u51fa\u624b\u76f8\u52a9\u7684\u4e86\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u8fd9\u4e0d\u662f\u665a\u8f88\u7684\u610f\u89c1\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u6014\u4e86\u4e00\u6014\u9053\uff1a\u201c\u600e\u4e48\u4e86\uff1f\u4f60\u4eec\u4e24\u4f4d\u53ef\u662f\u5728\u627e\u8001\u673d\u7684\u78b4\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5931\u7b11\u9053\uff1a\u201c\u4e0d\u6562\u2026\u2026\u201d \u3000\u3000\u4f59\u97f3\u672a\u5df2\uff0c\u53ea\u89c1\u90a3\u4e09\u540d\u5c11\u6797\u50e7\u4fa3\u5927\u6b65\u5411\u90a3\u4e94\u4f4d\u5c11\u5973\u8d70\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u5176\u4e2d\u4e00\u50e7\uff0c\u5927\u559d\u4e00\u58f0\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u90a3\u4e94\u4e2a\u7ea4\u7ec6\u7684\u4eba\u5f71\uff0c\u540c\u65f6\u6b62\u6b65\uff0c\u53ea\u542c\u5f97\u4e00\u4e2a\u6e05\u8106\u7684\u5c11\u5973\u58f0\u97f3\u63a5\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f \u3000\u3000\u8d76\u5feb\u8d70\u8fc7\u6765\u8ba9\u6211\u77a7\u77a7\uff01\u201d \u3000\u3000\u5927\u6982\u5979\u8fd9\u7b49\u53cd\u95ee\u4e4b\u8f9e\uff0c\u5927\u51fa\u5c11\u6797\u50e7\u4fa3\u610f\u6599\u4e4b\u5916\uff0c\u90a3\u4e09\u540d\u50e7\u4eba\u6c89\u9ed8\u4e86\u4e00\u9635\uff0c\u65b9\u59cb\u7531\u5148\u524d\u559d\u95ee\u90a3\u4eba\u63a5\u9053\uff1a\u201c\u8d2b\u50e7\u4e43\u662f\u5c11\u6797\u5de1\u5c71\u5b50\u5f1f\uff0c\u5973\u65bd\u4e3b\u662f\u4ec0\u4e48\u4eba\u95e8\u4e0b\uff1f\u5165\u591c\u5c1a\u5728\u5c71\u4e2d\u884c\u8d70\uff0c\u4e0d\u6015\u9047\u7740\u864e\u72fc\u4e4b\u7c7b\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u548c\u5c1a\u771f\u662f\u7b28\u5f97\u5f88\uff0c\u8fd9\u4e9b\u5973\u4eba\u65e2\u6562\u591c\u665a\u5728\u6df1\u5c71\u884c\u8d70\uff0c\u5f53\u7136\u662f\u90a3\u8eab\u5177\u6b66\u529f\u7684\u4eba\u7269\uff0c\u53c8\u600e\u4f1a\u4f1a\u5bb3\u6015\u90a3\u8c7a\u72fc\u864e\u8c79\u5462\uff1f\u4ed6\u4eec\u8fd9\u7b49\u95ee\u6cd5\uff0c\u5c82\u4e0d\u662f\u7b49\u4e8e\u767d\u8d39\u5417\uff1f\u201d \u3000\u3000\u5bfb\u601d\u4e4b\u95f4\uff0c\u53ea\u542c\u90a3\u5c11\u5973\u51b7\u7b11\u9053\uff1a\u201c\u548c\u5c1a\uff0c\u8fd9\u7bd9\u5c71\u5982\u6709\u864e\u72fc\uff0c\u90a3\u5c82\u4e0d\u662f\u4e22\u4e86\u4f60\u4eec\u5c11\u6797\u5bfa\u7684\u50e7\u4fa3\u7684\u4eba\u5417\uff1f\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u548c\u5c1a\uff0c\u4f60\u4eec\u4e0d\u5728\u5e99\u91cc\u5ff5\u7ecf\u62dc\u4f5b\uff0c\u8dd1\u5230\u5c71\u4e0a\u6765\u778e\u649e\u4ec0\u4e48\u554a\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u542c\u5f97\u5931\u7b11\u9053\uff1a\u201c\u95ee\u5f97\u597d\uff0c\u4e0d\u77e5\u9053\u4e09\u4e2a\u548c\u5c1a\u8981\u600e\u4e48\u56de\u7b54\u624d\u597d\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u63a5\u9053\uff1a\u201c\u597d\u529e\u5f97\u5f88\uff0c\u7ffb\u8138\u6253\u4e00\u67b6\u800c\u5df2\uff01\u201d\u679c\u7136\u4ed6\u6ca1\u6709\u8bf4\u9519\uff0c\u53ea\u542c\u5f97\u90a3\u548c\u5c1a\u5927\u58f0\u9053\uff1a\u201c\u8d2b\u50e7\u7b49\u4e43\u662f\u8d1f\u6709\u5de1\u5c71\u4e4b\u8d23\uff0c\u5973\u65bd\u4e3b\u5982\u662f\u4e0d\u80af\u8bf4\u660e\u6765\u5386\uff0c\u53ef\u83ab\u602a\u8d2b\u50e7\u7b49\u8981\u5c06\u51e0\u4f4d\u7559\u4e0b\u4e86\uff01\u201d \u3000\u3000\u4e94\u540d\u5c11\u5973\u5ffd\u7136\u9f50\u9f50\u7b11\u51fa\u58f0\u6765\u3002 \u3000\u3000\u5c11\u6797\u50e7\u4eba\u4e00\u6014\uff0c\u5176\u4e2d\u4e00\u4eba\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u7b11\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8033\u4e2d\u5374\u542c\u5f97\u4e00\u4e2a\u5973\u4eba\u58f0\u97f3\u9053\uff1a\u201c\u59d1\u5a18\u7b11\u4f60\u4eec\u591c\u90ce\u81ea\u5927\uff0c\u606c\u4e0d\u77e5\u803b\uff01\u201d \u3000\u3000\u90a3\u4e09\u540d\u50e7\u4eba\u663e\u7136\u88ab\u5bf9\u65b9\u6fc0\u6012\uff0c\u53ea\u89c1\u5176\u4e2d\u4e00\u50e7\uff0c\u624b\u6301\u7985\u6756\uff0c\u8d8a\u4f17\u800c\u51fa\uff0c\u5927\u6b65\u5411\u90a3\u4e94\u5973\u8d70\u53bb\u3002 \u3000\u3000\u4ed6\u5fc3\u60c5\u6fc0\u52a8\uff0c\u4e3e\u6b65\u4e4b\u95f4\uff0c\u81ea\u662f\u5341\u5206\u6df1\u91cd\uff0c\u549a\u549a\u4e4b\u58f0\uff0c\u9707\u5f97\u9759\u591c\u7684\u5c71\u8c37\uff0c\u5982\u9e23\u5de8\u9f13\u4e00\u822c\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5c3d\u4e2d\u6697\u9053\uff1a\u201c\u8fd9\u548c\u5c1a\u7684\u5916\u95e8\u529f\u592b\uff0c\u4f3c\u4e4e\u4e0d\u5f31\uff0c\u4e0d\u77e5\u90a3\u51e0\u4e2a\u5c11\u5973\uff0c\u600e\u6837\u624d\u80fd\u80dc\u5f97\u4e86\u4ed6\u624b\u4e2d\u7684\u7985\u6756\uff1f\u201d \u3000\u3000\u601d\u5fd6\u672a\u5df2\uff0c\u4e94\u5973\u4e4b\u4e2d\uff0c\u4e5f\u6709\u4e00\u4eba\u8d8a\u4f17\u800c\u51fa\u3002 \u3000\u3000\u4e09\u4eba\u501f\u7740\u8ff7\u8499\u7684\u6708\u8272\uff0c\u867d\u7136\u4e0d\u80fd\u77a7\u5f97\u5341\u5206\u771f\u5207\uff0c\u4f46\u9690\u7ea6\u4e4b\u4e2d\uff0c\u5c1a\u80fd\u5206\u8fa8\u5979\u4eec\u8138\u90e8\u7684\u8f6e\u5ed3\u3002\u8fd9\u4e43\u662f\u4e09\u4eba\u76ee\u529b\u8fc7\u4eba\u4e4b\u6545\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u8d8a\u4f17\u800c\u6765\u7684\u5c11\u5973\uff0c\u5934\u4e0a\u624e\u4e86\u4e00\u5757\u9752\u5e03\uff0c\u5305\u4f4f\u4e86\u9ed1\u53d1\uff0c\u4e00\u8eab\u7ea2\u8272\u52b2\u88c5\uff0c\u88f9\u5728\u90a3\u66f2\u73b2\u73d1\u7684\u5a07\u8eaf\u4e4b\u5916\uff0c\u663e\u5f97\u7a88\u7a95\u52a8\u4eba\uff0c\u7709\u76ee\u867d\u662f\u77a7\u4e0d\u771f\u5207\uff0c\u4f46\u4e00\u7ebf\u53cc\u660e\u7738\uff0c\u5374\u5728\u6708\u5149\u4e0b\u95ea\u95ea\u53d1\u4eae\uff0c\u770b\u4e0a\u53bb\u4e5f\u662f\u529f\u529b\u4e0d\u5f31\u3002 \u3000\u3000\u90a3\u624b\u6301\u7985\u6756\u7684\u50e7\u4eba\uff0c\u773c\u89c1\u6709\u4eba\u5411\u81ea\u5df1\u884c\u6765\uff0c\u7acb\u5373\u4e00\u987f\u7985\u6756\uff0c\u9a7b\u8db3\u5927\u559d\u9053\uff1a\u201c\u5973\u65bd\u4e3b\uff0c\u4f60\u4eec\u591c\u884c\u8352\u5c71\uff0c\u5fc5\u7136\u4e0d\u662f\u4ec0\u4e48\u597d\u8def\u6570\uff0c\u5feb\u5feb\u62a5\u51fa\u6765\u5386\uff0c\u4e5f\u514d\u8d2b\u50e7\u8bef\u4f24\u4e86\u4f60\uff01\u201d \u3000\u3000\u5c11\u6797\u50e7\u4fa3\u679c\u7136\u6709\u7740\u8fc7\u4eba\u7684\u8010\u6027\uff0c\u867d\u7136\u4ed6\u65e9\u5df2\u52a8\u6012\uff0c\u5374\u4ecd\u7136\u5f3a\u81ea\u6309\u637a\uff0c\u518d\u95ee\u4e00\u904d\u3002 \u3000\u3000\u90a3\u5c11\u5973\u683c\u683c\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u8fd9\u5e2e\u5c11\u6797\u548c\u5c1a\uff0c\u53ef\u771f\u4e2a\u662f\u8fc2\u8150\u900f\u9876\uff0c\u59d1\u5a18\u672c\u5c31\u662f\u51b2\u7740\u4f60\u4eec\u5c11\u6797\u5bfa\u800c\u6765\uff0c\u4e0d\u5728\u8fd9\u5d69\u5c71\u884c\u8d70\uff0c\u53c8\u8981\u53bb\u54ea\u513f\u5462\uff1f\u201d \u3000\u3000\u90a3\u548c\u5c1a\u4e0d\u6599\u5bf9\u65b9\u56de\u7b54\u7684\u5982\u6b64\u5e72\u8106\uff0c\u7adf\u7136\u6014\u4e86\u4e00\u6014\uff0c\u534a\u664c\u65b9\u59cb\u63a5\u9053\uff1a\u201c\u539f\u6765\u4f60\u4eec\u679c\u7136\u662f\u4e00\u7fa4\u5996\u5973\u4e86\uff01\u201d \u3000\u3000\u90a3\u5c11\u5973\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u5996\u5973\uff1f\u548c\u5c1a\uff0c\u59d1\u5a18\u529d\u4f60\u8bf4\u8bdd\u53ef\u8981\u5c0f\u5fc3\u54e9\uff01\u201d \u3000\u3000\u5c11\u6797\u50e7\u4eba\u95fb\u8a00\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u662f\u5996\u5973\uff1f\u90a3\u662f\u4ec0\u4e48\u554a\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u542c\u5f97\u4e00\u7b11\u9053\uff1a\u201c\u65b9\u5144\uff0c\u8fd9\u4e9b\u548c\u5c1a\u7b28\u5f97\u5f88\u54e9\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e5f\u5fcd\u4e0d\u4f4f\u5728\u5fc3\u4e2d\u53d1\u7b11\uff0c\u4f46\u4ed6\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u8d24\u9053\uff0c\u541b\u5b50\u53ef\u4ee5\u6b3a\u5176\u65b9\uff0c\u5c11\u6797\u5bfa\u7684\u9ad8\u50e7\u4eec\uff0c\u4e00\u5fc3\u5411\u4f5b\uff0c\u62d9\u4e8e\u53e3\u9f7f\uff0c\u90a3\u4e5f\u4e0d\u8db3\u4e3a\u7f9e\u554a\u2026\u2026\u201d \u3000\u3000\u4e24\u4eba\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u53ea\u89c1\u90a3\u5c11\u5973\u5ffd\u7136\u95ea\u8eab\u5411\u524d\u4e00\u8dc3\uff0c\u5c16\u7740\u566a\u5b50\u53eb\u9053\uff1a\u201c\u548c\u5c1a\uff0c\u4f60\u662f\u5c11\u6797\u5bfa\u4e2d\u7684\u7b2c\u51e0\u4ee3\u5f1f\u5b50\uff1f\u201d \u3000\u3000\u4e24\u58f0\u6e05\u8106\u7684\u201c\u62cd\u2026\u2026\u62cd\u2026\u2026\u201d\u4e4b\u58f0\u4f20\u6765\uff0c\u90a3\u548c\u5c1a\u6ca1\u6709\u7b54\u8bdd\uff0c\u5374\u62a2\u8d77\u7985\u6756\uff0c\u5411\u90a3\u5c11\u5973\u7838\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u5931\u671b\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u90a3\u548c\u5c1a\u5403\u4e86\u4e24\u4e2a\u8033\u5149\uff0c\u5927\u6982\u662f\u771f\u7684\u52a8\u706b\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u4e5f\u7b11\u9053\uff1a\u201c\u662f\u554a\uff0c\u8fd9\u548c\u5c1a\u4e0d\u81ea\u91cf\u529b\uff0c\u53ea\u6015\u8fd8\u8981\u5403\u5927\u4e8f\u5462\uff1f\u201d \u3000\u3000\u4ed6\u8bdd\u97f3\u752b\u843d\uff0c\u5374\u89c1\u90a3\u548c\u5c1a\u62a2\u8d77\u7684\u7985\u6756\uff0c\u5df2\u7136\u5411\u5c11\u5973\u5f53\u5934\u7838\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u77a7\u5f97\u76b1\u7709\u9053\uff1a\u201c\u8fd9\u4e00\u6756\u529b\u9053\u4e0d\u8f7b\uff0c\u4e0d\u77e5\u90a3\u4f4d\u59d1\u5a18\u600e\u751f\u6321\u67b6\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u53ef\u662f\u751f\u51fa\u601c\u9999\u60dc\u7389\u4e4b\u5fc3\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u5fc3\u4e2d\u4e00\u9707\uff0c\u6697\u9053\uff1a\u201c\u5979\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u96be\u9053\u2026\u2026\u201d\u4ed6\u5ffd\u7136\u89c9\u51fa\u81ea\u5df1\u8fd9\u7b49\u60f3\u6cd5\uff0c\u672a\u514d\u597d\u7b11\uff0c\u4fbf\u6447\u5934\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u8d24\u5f1f\u53d6\u7b11\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5927\u6982\u4e5f\u89c9\u5f97\u81ea\u5df1\u521a\u624d\u90a3\u53e5\u8bdd\u6709\u4e9b\u513f\u8fc7\u4efd\uff0c\u626d\u5934\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u90a3\u9b54\u5973\u5df2\u7ecf\u628a\u548c\u5c1a\u9707\u9000\u4e86\u5566\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e3e\u76ee\u671b\u53bb\uff0c\u5374\u89c1\u90a3\u5c11\u5973\u4e5f\u4e0d\u77e5\u7528\u7684\u4ec0\u4e48\u624b\u6cd5\uff0c\u5a07\u8eaf\u4e00\u62e7\uff0c\u6253\u7985\u6756\u4e0b\u9762\u6ed1\u51fa\uff0c\u90a3\u9ad8\u5927\u7684\u548c\u5c1a\uff0c\u5c31\u5728\u5ffd\u7136\u95f4\u5012\u9000\u4e86\u4e09\u6b65\u7acb\u8db3\u4e0d\u7a33\uff0c\u6454\u5012\u5728\u5730\u3002 \u3000\u3000\u53e6\u5916\u7684\u4e24\u540d\u50e7\u4eba\uff0c\u89c1\u72b6\u5927\u5403\u4e00\u60ca\uff0c\u4e24\u6839\u7985\u6756\u9ad8\u4e3e\uff0c\u4fbf\u5411\u90a3\u5c11\u5973\u51fb\u53bb\u3002 \u3000\u3000\u8fd9\u56de\u53ea\u89c1\u90a3\u5c11\u5973\u8eab\u5f62\u4e00\u4fa7\uff0c\u5f80\u4e00\u65c1\u8dc3\u51fa\uff0c\u53f3\u624b\u4e00\u6296\uff0c\u9ed1\u591c\u4e2d\u867d\u7136\u77a7\u4e0d\u771f\u5207\u5730\u6253\u51fa\u4e86\u4ec0\u4e48\uff0c\u4f46\u90a3\u4e24\u7ebf\u94f6\u5149\uff0c\u5374\u662f\u6ca1\u6709\u9003\u8fc7\u65b9\u96ea\u5b9c\u7b49\u4e09\u4eba\u7684\u773c\u529b\uff0c\u663e\u7136\u6b63\u662f\u4e00\u79cd\u6781\u4e3a\u5229\u5bb3\u7684\u6697\u5668\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u8fde\u8f6c\u5ff5\u90fd\u672a\u6765\u5f97\u53ca\uff0c\u8033\u4e2d\u5df2\u542c\u5f97\u90a3\u4e24\u540d\u50e7\u4eba\u5404\u81ea\u95f7\u54fc\u4e86\u4e00\u58f0\uff0c\u4fbf\u5012\u5411\u5730\u4e0a\u3002 \u3000\u3000\u5b8b\u5b5a\u5931\u58f0\u9053\uff1a\u201c\u597d\u5389\u5bb3\u7684\u6697\u5668\uff01\u201d \u3000\u3000\u4ed6\u8fd9\u53e5\u8bdd\u53ef\u4e0d\u66fe\u7528\u4f20\u97f3\u4e4b\u672f\uff0c\u4e94\u540d\u5c11\u5973\u4f3c\u662f\u6ca1\u6709\u6599\u5230\u5728\u90a3\u5de8\u77f3\u4e4b\u540e\uff0c\u8fd8\u85cf\u7740\u522b\u4eba\uff0c\u540c\u65f6\u60ca\u54a6\u4e86\u4e00\u58f0\uff0c\u95ea\u7535\u822c\u5411\u4e09\u4eba\u5b58\u8eab\u4e4b\u5904\u5954\u6765\u3002 \u3000\u3000\u5341\u7f15\u94f6\u5149\uff0c\u7535\u63a0\u800c\u81f3\uff0c\u5979\u4eec\u4eba\u672a\u5230\uff0c\u6697\u5668\u5374\u5df2\u5148\u53d1\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u770b\u5f97\u5fc3\u4e2d\u5927\u5927\u4e0d\u5feb\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u4e9b\u5996\u5973\u679c\u7136\u5fc3\u80a0\u6b79\u6bd2\u5f97\u5f88\uff0c\u8fde\u77f3\u540e\u662f\u4ec0\u4e48\u4eba\u90fd\u4e0d\u95ee\uff0c\u4fbf\u5148\u4e0b\u6bd2\u624b\uff0c\u6253\u51fa\u6697\u5668\uff0c\u82e5\u4e0d\u91cd\u521b\u5979\u4eec\u4e00\u756a\uff0c\u53ea\u6015\u5c06\u6765\u4e0d\u77e5\u6709\u591a\u5c11\u4eba\u8981\u4e27\u5728\u4ed6\u4eec\u624b\u4e0b\uff01\u201d \u3000\u3000\u62d4\u51fa\u957f\u5251\uff0c\u632f\u81c2\u4e00\u6325\uff0c\u4e00\u9635\u201c\u53ee\u53ee\u201d\u4e4b\u58f0\u8fc7\u5904\uff0c\u6570\u5341\u7f15\u94f6\u5149\u5c3d\u884c\u51fb\u843d\uff0c\u957f\u8eab\u800c\u8d77\uff0c\u5927\u6b65\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u89c1\u4ed6\u73b0\u8eab\uff0c\u4fbf\u4e5f\u7ad9\u4e86\u8d77\u6765\uff0c\u8fd9\u65f6\u65b9\u96ea\u5b9c\u5df2\u7136\u8d70\u4ee5\u4e94\u5973\u8eab\u524d\u3002 \u3000\u3000\u539f\u6765\u90a3\u4e94\u540d\u5c11\u5b50\u521a\u521a\u5954\u5230\u5de8\u77f3\u4e4b\u524d\uff0c\u65b9\u96ea\u5b9c\u5df2\u7136\u5012\u63d0\u957f\u5251\uff0c\u8d70\u4e86\u51fa\u6765\uff0c\u662f\u4ee5\uff0c\u4e94\u5973\u4e00\u6014\u4e4b\u4e0b\uff0c\u540c\u65f6\u6b62\u6b65\u3002 \u3000\u3000\u90a3\u540d\u8eab\u7740\u6de1\u7ea2\u52b2\u88c5\u7684\u5c11\u5973\uff0c\u51b7\u51b7\u5730\u770b\u4e86\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u559d\u9053\uff1a\u201c\u4f60\u662f\u5c11\u6797\u95e8\u4e0b\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6253\u91cf\u4e86\u773c\u524d\u4e94\u5973\u4e00\u773c\uff0c\u53ea\u89c1\u5979\u4eec\u53e6\u5916\u56db\u4eba\uff0c\u4e00\u5f8b\u90fd\u662f\u6de1\u9ec4\u8272\u52b2\u88c5\uff0c\u624b\u63d0\u5b9d\u5251\uff0c\u8138\u8272\u60ca\u8bb6\u5730\u5446\u671b\u7740\u81ea\u5df1\uff0c\u5fc3\u4e2d\u4e0d\u7531\u5fd6\u7740\uff1a\u201c\u770b\u6765\u8fd9\u7ea2\u8863\u5c11\u5973\uff0c\u662f\u5979\u4eec\u4e94\u4eba\u4e2d\u7684\u5934\u4e86\uff01\u201d \u3000\u3000\u4f46\u4ed6\u53e3\u4e2d\u5374\u662f\u6c89\u58f0\u9053\uff1a\u201c\u533a\u533a\u4e0d\u662f\u5c11\u6797\u95e8\u4e0b\uff01\u4f60\u4eec\u51e0\u4f4d\u5987\u9053\u4eba\u5bb6\uff0c\u600e\u7684\u5bf9\u90a3\u5c11\u6797\u9ad8\u50e7\u66b4\u4e0b\u6bd2\u624b\uff1f\u53ef\u662f\u5f7c\u6b64\u4e4b\u95f4\uff0c\u6709\u7740\u4ec0\u4e48\u4ec7\u6068\u5417\uff1f\u201d \u3000\u3000\u90a3\u7ea2\u8863\u5c11\u5973\u95fb\u8a00\uff0c\u4f3c\u662f\u653e\u5fc3\u4e0d\u5c11\uff0c\u8138\u8272\u4e00\u7f13\uff0c\u683c\u683c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u516c\u5b50\u4e0d\u662f\u5c11\u6797\u95e8\u4e0b\uff1f \u3000\u3000\u4e0d\u77e5\u600e\u4e48\u79f0\u547c\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u7075\u673a\u4e00\u52a8\uff0c\u6697\u9053\uff1a\u201c\u4e0d\u77e5\u90a3\u67f3\u5a9a\u5a18\u662f\u5426\u5df2\u5c06\u6211\u51fa\u73b0\u6b66\u6797\u4e4b\u4e8b\uff0c\u544a\u8bc9\u4e86\u5929\u9b54\u5973\uff1f\u6211\u5982\u8bf4\u51fa\u81ea\u5df1\u5c31\u662f\u65b9\u96ea\u5b9c\uff0c\u5982\u82e5\u5979\u53d8\u8272\uff0c\u90a3\u5c31\u8868\u793a\u67f3\u5a9a\u5a18\u548c\u5929\u9b54\u5973\u4e4b\u95f4\u7684\u6d88\u606f\uff0c\u4f20\u9012\u5f97\u5341\u5206\u5feb\u6377\uff0c\u800c\u4e14\uff0c\u4e5f\u5f88\u53ef\u80fd\u6253\u6210\u4e86\u4e00\u6c14\u2026\u2026\u201d\u4ed6\u5ffd\u7136\u5931\u51fa\u4e86\u8fd9\u7b49\u8bd5\u63a2\u4e4b\u5fc3\uff0c\u8fde\u81ea\u5df1\u4e5f\u89c9\u7740\u751a\u4e3a\u610f\u5916\uff0c\u5f53\u4e0b\u8131\u53e3\u63a5\u9053\uff1a\u201c\u533a\u533a\u65b9\u96ea\u5b9c\uff01\u201d \u3000\u3000\u90a3\u7ea2\u8863\u5c11\u5973\u795e\u8272\u8d8a\u53d1\u4ece\u5bb9\uff0c\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u65b9\u516c\u5b50\uff0c\u59be\u8eab\u8fd9\u53a2\u6709\u793c\u4e86\uff01\u201d \u3000\u3000\u8bf4\u7740\u798f\u4e86\u4e00\u798f\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u89c1\u5979\u8fd9\u7b49\u795e\u60c5\uff0c\u4fbf\u77e5\u67f3\u5a9a\u5a18\u5e76\u672a\u5c06\u5251\u795e\u6709\u4e86\u4f20\u4eba\u51fa\u73b0\u4e4b\u4e8b\uff0c\u544a\u77e5\u5929\u9b54\u5973\uff0c\u4e0d\u7531\u5fc3\u4e2d\u6697\u559c\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u59d1\u5a18\u4eec\u600e\u4e48\u79f0\u8c13\uff1f\u662f\u54ea\u4f4d\u9ad8\u4eba\u7684\u95e8\u4e0b\u5462\uff1f\u201d \u3000\u3000\u7ea2\u8863\u5c11\u5973\u683c\u683c\u4e00\u7b11\u9053\uff1a\u201c\u65b9\u516c\u5b50\uff0c\u4f60\u771f\u7684\u4e0d\u662f\u5c11\u6797\u5bfa\u4e2d\u7684\u5b50\u5f1f\u5417\uff1f\u201d \u3000\u3000\u6562\u60c5\u5979\u4eec\u8fd8\u6709\u4e9b\u4e0d\u76f8\u4fe1\u65b9\u96ea\u5b9c\u4e4b\u8a00\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u5c11\u6797\u5bfa\u4e2d\uff0c\u51e0\u65f6\u6709\u8fc7\u4e0d\u51fa\u5bb6\u7684\u5f1f\u5b50\uff1f\u59d1\u5a18\u4e0d\u7528\u7591\u5fc3\u4e86\uff01\u201d \u3000\u3000\u90a3\u7ea2\u8863\u5c11\u5973\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u9519\u554a\uff01\u8fd9\u4e9b\u5e74\u6765\uff0c\u5c11\u6797\u5bfa\u90fd\u6ca1\u6709\u6536\u8fc7\u4fd7\u5bb6\u5b50\u5f1f\uff0c\u65b9\u516c\u5b50\u6b64\u8bf4\uff0c\u60f3\u5fc5\u4e0d\u4f1a\u5047\u7684\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u672c\u6765\u5c31\u662f\u5b9e\u8bdd\uff01\u201d \u3000\u3000\u7ea2\u653e\u5c11\u5973\u5fae\u5fae\u4e00\u7b11\uff0c\u4e0d\u66fe\u56de\u7b54\u65b9\u96ea\u5b9c\u6240\u95ee\u7684\u8bdd\uff0c\u53cd\u5012\u5411\u65b9\u96ea\u5b9c\u95ee\u9053\uff1a\u201c\u65b9\u516c\u5b50\uff0c\u4f60\u662f\u54ea\u4e00\u95e8\u6d3e\u7684\u4eba\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u9053\uff1a\u201c\u5979\u600e\u4e48\u8fd9\u4e48\u5201\uff1f\u53cd\u800c\u95ee\u8d77\u6211\u6765\u4e86\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u5ff5\u5934\u4e00\u8f6c\uff0c\u5374\u9053\uff1a\u201c\u533a\u533a\u4e0d\u5c5e\u4e5d\u5927\u95e8\u6d3e\uff0c\u59d1\u5a18\u662f\u5426\u653e\u5fc3\u4e86\uff1f\u201d \u3000\u3000\u7ea2\u8863\u5c11\u5973\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u65b9\u59cb\u63a5\u9053\uff1a\u201c\u59be\u8eab\u59ca\u59b9\u4e94\u4eba\uff0c\u4e43\u662f\u8fd9\u5c71\u4e0b\u5927\u6237\u4eba\u5bb6\u7684\u4f7f\u5973\u2026\u2026\u201d \u3000\u3000\u5979\u8bdd\u6ca1\u8bf4\u5b8c\uff0c\u65b9\u96ea\u5b9c\u5df2\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u5982\u4e0d\u80af\u76f4\u8a00\u76f8\u544a\uff0c\u90a3\u4e5f\u7528\u4e0d\u7740\u7f16\u51fa\u4e00\u5957\u5047\u8bdd\u6765\u9a97\u4eba\u554a\uff01\u201d \u3000\u3000\u7ea2\u8863\u5c11\u5973\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u516c\u5b50\u77e5\u6653\u6211\u4eec\u662f\u4ec0\u4e48\u4eba\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u533a\u533a\u5982\u662f\u77e5\u6653\uff0c\u90a3\u4e5f\u4e0d\u4f1a\u95ee\u4f60\u4e86\uff01\u201d \u3000\u3000\u7ea2\u8863\u5c11\u5973\u8fdf\u7591\u4e86\u4e00\u4f1a\uff0c\u4f1a\u5934\u770b\u4e86\u90a3\u56db\u540d\u9ec4\u8863\u5c11\u5973\u4e00\u773c\uff0c\u8fd9\u624d\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u65b9\u516c\u5b50\uff0c\u77a7\u4f60\u8bf4\u8bdd\u7684\u795e\u60c5\uff0c\u60f3\u5fc5\u4e5f\u662f\u6b66\u6797\u4e2d\u4eba\u7269\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u7c97\u901a\u62f3\u68d2\uff0c\u5f3a\u8eab\u58ee\u4f53\u800c\u5df2\u3002\u201d \u3000\u3000\u7ea2\u8863\u5c11\u5973\u63a5\u9053\uff1a\u201c\u516c\u5b50\uff0c\u6b66\u6797\u4e2d\u6709\u4e00\u4e2a\u91d1\u82b1\u5e2e\uff0c\u4e0d\u77e5\u516c\u5b50\u66fe\u542c\u4eba\u8bf4\u8fc7\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\u4e00\u6014\uff0c\u6697\u9053\uff1a\u201c\u6b66\u6797\u4e4b\u4e2d\uff0c\u51e0\u65f6\u53c8\u6709\u4e86\u4e00\u4e2a\u91d1\u82b1\u5e2e\u5462\uff1f\u96be\u9053\u2026\u2026\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u60f3\u8d77\u5b89\u5c0f\u840d\u7684\u4e24\u540d\u4f8d\u513f\uff0c\u66fe\u7ecf\u8bf4\u90a3\u5929\u9b54\u5973\u7ed9\u8fc7\u4ed6\u4eec\u4e00\u6735\u91d1\u82b1\u4e4b\u4e8b\uff0c\u4e0d\u7981\u5fc3\u5934\u604d\u7136\uff0c\u5fd6\u9053\uff1a\u201c\u516b\u6210\u8fd9\u91d1\u82b1\u5e2e\uff0c\u5c31\u662f\u5929\u9b54\u5973\u6240\u65b0\u7ec4\u7684\u5e2e\u4f1a\u4e86\uff01\u201d \u3000\u3000\u5f53\u4e0b\u6447\u9053\uff1a\u201c\u533a\u533a\u751a\u5c11\u5728\u6c5f\u6e56\u4e0a\u884c\u8d70\uff0c\u8fd9\u91d1\u82b1\u5e2e\u4e48\uff0c\u8fd8\u672a\u542c\u4eba\u8bf4\u8fc7\uff01\u201d \u3000\u3000\u8bed\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u59d1\u5a18\u60f3\u5fc5\u5c31\u662f\u91d1\u82b1\u5e2e\u95e8\u4e0b\u4e86\uff1f\u201d \u3000\u3000\u7ea2\u8863\u5c11\u5973\u7b11\u9053\uff1a\u201c\u516c\u5b50\u8bf4\u7684\u4e0d\u9519\uff0c\u59be\u8eab\u7b49\u6b63\u662f\u91d1\u82b1\u5e2e\u4e2d\u7684\u5f1f\u5b50\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u70b9\u4e86\u70b9\u5934\u9053\uff1a\u201c\u8fd9\u91d1\u82b1\u5e2e\u53ef\u662f\u65b0\u8fd1\u6210\u7acb\u7684\u5417\uff1f\u201d \u3000\u3000\u7ea2\u8863\u5c11\u5973\u7565\u4e00\u72b9\u8c6b\uff0c\u63a5\u9053\uff1a\u201c\u672c\u5e2e\u6210\u7acb\u5df2\u6709\u4e5d\u5e74\uff0c\u53ea\u662f\u6700\u8fd1\u65b9\u59cb\u884c\u8d70\u6c5f\u6e56\u800c\u5df2\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u9053\uff1a\u201c\u539f\u6765\u90a3\u5929\u9b54\u5973\u65e9\u5c31\u5728\u6697\u4e2d\u4f5c\u4e86\u51c6\u5907\uff0c\u770b\u6765\u5e08\u7236\u5f53\u5e74\u4e5f\u88ab\u5979\u7792\u8fc7\u4e86\uff01\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u867d\u7136\u5728\u60f3\uff0c\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u91d1\u82b1\u5e2e\u7684\u5e2e\u4e3b\uff0c\u4e0d\u77e5\u662f\u54ea\u4f4d\u6b66\u6797\u524d\u8f88\uff1f\u201d \u3000\u3000\u7ea2\u8863\u5c11\u5973\u9053\uff1a\u201c\u516c\u5b50\uff0c\u672c\u5e2e\u5e2e\u4e3b\uff0c\u5c31\u662f\u6211\u7684\u5e08\u7236\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\u4e00\u6014\uff0c\u5fc3\u60f3\uff1a\u4f60\u5e08\u7236\u53c8\u662f\u8c01\uff1f\u4f60\u4e0d\u8bf4\u5c06\u51fa\u6765\uff0c\u53c8\u6709\u8c01\u77e5\u9053\u5462\uff1f\u76ee\u5149\u4e00\u8f6c\uff0c\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u7684\u5c0a\u5e08\uff0c\u5728\u6b66\u6797\u4e4b\u4e2d\uff0c\u60f3\u5fc5\u5927\u6709\u540d\u671b\u4e86\uff01\u201d \u3000\u3000\u7ea2\u8863\u5c11\u5973\u683c\u683c\u4e00\u7b11\u9053\uff1a\u201c\u5f53\u7136\u554a\uff01\u5929\u9b54\u5973\u4e09\u5b57\uff0c\u65b9\u516c\u5b50\u4e00\u5b9a\u77e5\u9053\u5427\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5927\u7b11\u9053\uff1a\u201c\u95fb\u540d\u4e45\u77e3\uff01\u201d \u3000\u3000\u7b11\u58f0\u5ffd\u7136\u4e00\u655b\uff0c\u4e24\u773c\u51b7\u7535\u4e00\u822c\u5728\u90a3\u5c11\u5973\u8eab\u4e0a\u4e00\u8f6c\uff0c\u63a5\u9053\uff1a\u201c\u539f\u6765\u90a3\u91d1\u82b1\u5e2e\uff0c\u4e43\u662f\u4e94\u5927\u9b54\u4e3b\u6240\u521b\uff0c\u533a\u533a\u4eca\u65e5\u65b9\u77e5\uff0c\u771f\u6b63\u662f\u5b64\u964b\u5be1\u95fb\u5f97\u5f88\u3002\u201d \u3000\u3000\u90a3\u7ea2\u8863\u5c11\u5973\u88ab\u4ed6\u8fd9\u7b49\u795e\u60c5\uff0c\u5f04\u5f97\u6115\u4e86\u534a\u664c\uff0c\u67f3\u7709\u9891\u805a\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u65b9\u516c\u5b50\uff0c\u4f60\u8ba4\u8bc6\u5bb6\u5e08\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u5934\u9053\uff1a\u201c\u4e0d\u8ba4\u8bc6\uff01\u201d \u3000\u3000\u7ea2\u8863\u5c11\u5973\u8138\u4e0a\u7740\u6025\u4e0d\u5b89\u7684\u795e\u8272\uff0c\u4e00\u626b\u800c\u5c3d\uff0c\u7b11\u9053\uff1a\u201c\u65b9\u516c\u5b50\uff0c\u4f60\u53ef\u60f3\u8ba4\u8bc6\u5bb6\u5e08\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u60f3\uff0c\u5979\u8fd9\u662f\u4f55\u610f\uff1f\u83ab\u975e\u60f3\u5c06\u6211\u4e5f\u5f15\u8fdb\u5979\u4eec\u91d1\u82b1\u5e2e\u4e2d\uff1f\u8fd9\u5012\u662f\u6253\u5165\u5929\u9b54\u5973\u8eab\u8fb9\u7684\u4e00\u4e2a\u597d\u673a\u4f1a\u54e9\u2026\u2026 \u3000\u3000\u5bfb\u601d\u4e4b\u95f4\uff0c\u5ffd\u7136\u60f3\u8d77\uff0c\u5b89\u5c0f\u840d\u65e2\u7136\u5df2\u8499\u5929\u9b54\u5973\u90a3\u7b49\u5668\u91cd\uff0c\u81ea\u5df1\u600e\u53ef\u81ea\u8d2c\u8eab\u4ef7\uff0c\u4f5c\u90a3\u5929\u9b54\u5973\u7684\u5c5e\u4e0b\u4e4b\u81e3\uff1f \u3000\u3000\u4e00\u5ff5\u53ca\u6b64\uff0c\u4e0d\u7531\u81ea\u4e3b\u5730\u6447\u5934\u9053\uff1a\u201c\u4e0d\u5fc5\u4e86\uff01\u201d \u3000\u3000\u7ea2\u8863\u5c11\u5973\u89c1\u4ed6\u6c89\u541f\u4e86\u90a3\u4e48\u4e45\uff0c\u7adf\u7136\u8bf4\u51fa\u62d2\u7edd\u4e4b\u8a00\uff0c\u82b3\u5fc3\u5927\u4e3a\u4e0d\u5feb\uff0c\u51b7\u51b7\u4e00\u7b11\u9053\uff1a \u3000\u3000\u201c\u65b9\u516c\u5b50\uff0c\u4f60\u597d\u81ea\u5927\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u662f\u5417\uff1f\u533a\u533a\u5012\u4e0d\u89c9\u5f97\u2026\u2026\u201d \u3000\u3000\u7ea2\u8863\u5c11\u5973\u51b7\u7b11\u9053\uff1a\u201c\u65b9\u516c\u5b50\uff0c\u4f60\u53ef\u77e5\u9053\uff0c\u5f53\u4eca\u6b66\u6797\u4e4b\u4e2d\uff0c\u4e0d\u77e5\u6709\u591a\u5c11\u6210\u540d\u4e4b\u58eb\uff0c\u60f3\u4e00\u8bc6\u5bb6\u5e08\u800c\u4e0d\u5f97\u5176\u95e8\uff0c\u516c\u5b50\u4eca\u65e5\u7adf\u7136\u820d\u6b64\u826f\u673a\u4e0d\u53d6\uff0c\u90a3\u662f\u5341\u5206\u81ea\u5927\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u4eba\u5404\u6709\u5fd7\uff0c\u5404\u81ea\u5904\u4e16\u7684\u76ee\u7684\u4e5f\u81ea\u7136\u4e0d\u540c\uff0c\u59d1\u5a18\u7684\u76db\u610f\uff0c\u533a\u533a\u5fc3\u9886\u4e86\u3002\u201d \u3000\u3000\u8bed\u97f3\u5fae\u5fae\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u54b1\u4eec\u8bf4\u4e86\u534a\u5929\u8bdd\uff0c\u5c1a\u672a\u8bf7\u6559\u59d1\u5a18\u82b3\u540d\uff0c\u662f\u600e\u4e48\u79f0\u547c\u5462\uff1f\u201d \u3000\u3000\u7ea2\u8863\u5c11\u5973\u89c1\u4ed6\u95ee\u5230\u81ea\u5df1\u7684\u540d\u59d3\uff0c\u4e0d\u7531\u5f97\u9732\u51fa\u4e00\u80a1\u5c11\u5973\u7684\u5a07\u7f9e\u4e4b\u6001\uff0c\u7728\u52a8\u7740\u5927\u773c\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u59be\u8eab\u59d3\uff0c\u8d31\u540d\u4e00\u4e2a\u82b8\u5b57\u3002\u4e0d\u8fc7\uff0c\u5bb6\u5e08\u90fd\u53eb\u6211\u82b8\u513f\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u674e\u82b8\u513f\u59d1\u5a18\uff01\u201d \u3000\u3000\u62f1\u4e86\u62f1\u624b\uff0c\u63a5\u9053\uff1a\u201c\u59d1\u5a18\u5728\u5e2e\u4e2d\uff0c\u53f8\u4f55\u804c\u5b88\uff1f\u201d \u3000\u3000\u674e\u82b8\u4e00\u7b11\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4f60\u60f3\u6253\u542c\u672c\u5e2e\u7684\u673a\u5bc6\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u9053\uff1a\u8fd9\u4e2b\u5934\u5f88\u673a\u8b66\u54e9\uff01 \u3000\u3000\u4f46\u4ed6\u8868\u73b0\u4e0a\u5374\u662f\u5fae\u5fae\u5730\u4e00\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u5982\u662f\u4e0d\u4fbf\u8bf4\u51fa\uff0c\u90a3\u5c31\u7b97\u4e86\uff01\u201d \u3000\u3000\u8fd9\u7b49\u6b32\u64d2\u6545\u7eb5\u7684\u624b\u6cd5\uff0c\u679c\u7136\u5927\u5927\u6709\u7528\uff0c\u53ea\u89c1\u90a3\u674e\u82b8\u513f\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u65b9\u516c\u5b50\uff0c\u4f60\u83ab\u975e\u89c1\u602a\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u533a\u533a\u4e0d\u6562\uff01\u201d \u3000\u3000\u674e\u82b8\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u672c\u5e2e\u65e2\u5df2\u516c\u5f00\u5728\u6b66\u6797\u4e4b\u4e2d\u9732\u9762\uff0c\u4e5f\u4e0d\u6015\u4ec0\u4e48\u4eba\u6253\u542c\u5e2e\u4e2d\u7684\u4e00\u5207\u4e86\u2026\u2026\u201d \u3000\u3000\u5979\u5ffd\u7136\u56de\u7738\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u59be\u8eab\u5728\u91d1\u82b1\u5e2e\u4e2d\uff0c\u5e76\u65e0\u56fa\u5b9a\u7684\u804c\u5b88\uff0c\u4e00\u65e6\u5e2e\u4e2d\u6709\u4e8b\uff0c\u5e08\u7236\u5c31\u4f1a\u547d\u59be\u8eab\u524d\u53bb\u5168\u6743\u5904\u7406\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u9053\uff1a\u201c\u542c\u5979\u4e4b\u8a00\uff0c\u5728\u5e2e\u4e2d\u7684\u5730\u4f4d\u5012\u662f\u5341\u5206\u6e05\u9ad8\u7684\u4e86\uff01\u201d \u3000\u3000\u6545\u610f\u5730\u6c89\u541f\u4e86\u4e00\u4f1a\uff0c\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u5728\u8d35\u5e2e\u4e2d\u7684\u5730\u4f4d\uff0c\u662f\u5728\u5404\u5927\u5802\u4e3b\u4e4b\u4e0a\u4e86\uff01\u201d \u3000\u3000\u5176\u5b9e\uff0c\u65b9\u96ea\u5b9c\u6839\u672c\u4e0d\u77e5\u91d1\u82b1\u5e2e\u4e2d\u6709\u6ca1\u6709\u5802\u4e3b\u7684\u804c\u4f4d\uff0c\u7ed9\u4ed6\u987a\u53e3\u8bf4\u51fa\uff0c\u5012\u4e5f\u81ea\u89c9\u6709\u7406\uff01 \u3000\u3000\u674e\u82b8\u7b11\u9053\uff1a\u201c\u59be\u8eab\u59ca\u59b9\u5171\u6709\u516d\u4eba\uff0c\u9664\u4e86\u5927\u5e08\u59ca\u6709\u7740\u56fa\u5b9a\u7684\u804c\u53f8\u4ee5\u5916\uff0c\u5176\u4f59\u4e94\u4eba\uff0c\u90fd\u4e0d\u66fe\u4e13\u638c\u4e00\u5802\u4e4b\u4e8b\uff0c\u516c\u5b50\u731c\u60f3\u7684\u5012\u4e5f\u4e0d\u9519\uff0c\u672c\u5e2e\u5185\u5916\u4e09\u5802\u7684\u5802\u4e3b\uff0c\u679c\u7136\u90fd\u8981\u542c\u53d7\u59be\u8eab\u59ca\u59b9\u8282\u5236\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u6697\u5438\u4e86\u53e3\u6c14\uff0c\u5931\u7b11\u5fd6\u9053\uff1a\u5012\u662f\u88ab\u6211\u649e\u5bf9\u4e86\uff01\u53e3\u4e2d\u5374\u662f\u63a5\u9053\uff1a\u201c\u59d1\u5a18\u6b64\u756a\u6765\u5230\u5d69\u5c71\uff0c\u53ef\u662f\u5949\u4e86\u4ee4\u5e08\u4e4b\u547d\uff0c\u8981\u4e0e\u90a3\u5c11\u6797\u4e00\u6d3e\u4f5c\u5bf9\u7684\u5417\uff1f\u201d \u3000\u3000\u674e\u82b8\u4f3c\u662f\u6ca1\u6709\u6599\u5230\u65b9\u96ea\u5b9c\u5c45\u7136\u4f1a\u8fd9\u4e48\u5355\u5200\u76f4\u5165\u5730\u95ee\u51fa\u81ea\u5df1\u6b64\u884c\u76ee\u7684\uff0c\u4e0d\u7981\u5446\u4e86\u4e00\u5446\uff0c\u76b1\u7709\u9053\uff1a\u201c\u65b9\u516c\u5b50\uff0c\u4f60\u4e0e\u5c11\u6797\u5bfa\u6709\u4ec0\u4e48\u6e0a\u6e90\u6ca1\u6709\uff1f\u201d \u3000\u3000\u663e\u7136\uff0c\u5979\u5df2\u52a8\u4e86\u7591\u5ff5\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u7d20\u65e0\u76f8\u8bc6\u4e4b\u4eba\uff0c\u600e\u4f1a\u6709\u4ec0\u4e48\u6e0a\u6e90\uff1f\u201d \u3000\u3000\u4ed6\u56de\u7b54\u7684\u5341\u5206\u5e72\u8106\uff0c\u5012\u53eb\u674e\u82b8\u6709\u4e9b\u6478\u4e0d\u7740\u5934\u8111\uff0c\u5f53\u4e0b\u6c89\u541f\u9053\uff1a\u201c\u516c\u5b50\u65e2\u4e0e\u5c11\u6797\u6ca1\u6709\u8054\u7cfb\uff0c\u59be\u8eab\u4e0e\u5c11\u6797\u4e00\u6d3e\u7684\u4e8b\uff0c\u516c\u5b50\u6700\u597d\u4e0d\u7528\u591a\u95ee\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u60f3\uff0c\u8fd9\u5996\u5973\u9002\u624d\u4f24\u4eba\u7684\u624b\u6bb5\uff0c\u76f8\u5f53\u72e0\u6bd2\uff0c\u8fd9\u4f1a\u513f\u53d8\u5f97\u5982\u6b64\u59d4\u5c48\u6c42\u5168\uff0c\u56de\u62a4\u4e8e\u6211\uff0c\u5fc5\u7136\u6709\u7740\u4ec0\u4e48\u539f\u56e0\uff0c\u53ea\u662f\uff0c\u4ed6\u5374\u60f3\u4e0d\u51fa\u4ec0\u4e48\u539f\u56e0\u4f55\u5728\uff01\u53ea\u56e0\u4ed6\u5e74\u5c81\u751a\u8f7b\uff0c\u4e0d\u61c2\u7537\u5973\u4e4b\u95f4\u7684\u60c5\u612b\uff0c\u5426\u5219\uff0c\u90a3\u674e\u82b8\u7684\u773c\u8272\u795e\u60c5\uff0c\u5904\u5904\u5bf9\u4ed6\u9732\u51fa\u7231\u6155\u4e4b\u610f\uff0c\u4ed6\u4e0d\u5e94\u77a7\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u8fdf\u7591\u5730\u60f3\u4e86\u4e00\u4f1a\uff0c\u5ffd\u7136\u5927\u7b11\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u533a\u533a\u6709\u4e00\u53e5\u8bdd\uff0c\u4e0d\u77e5\u5f53\u8bf4\u4e0d\u5f53\u8bf4\uff1f\u201d \u3000\u3000\u90a3\u674e\u82b8\u672c\u662f\u4e00\u8138\u4f01\u671b\u5173\u5207\u4e4b\u5bb9\uff0c\u60f3\u5f97\u5230\u4ed6\u4e00\u53e5\u4e0d\u518d\u591a\u95ee\u5c11\u6797\u4e4b\u4e8b\u7684\u56de\u7b54\uff0c\u6b64\u523b\u89c1\u4ed6\u5ffd\u7136\u5192\u51fa\u8fd9\u4e48\u4e00\u53e5\u8bdd\u6765\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u4e00\u9707\uff0c\u76b1\u7709\u9053\uff1a\u201c\u516c\u5b50\u6709\u4ec0\u4e48\u8bdd\u8981\u8bf4\uff1f\u53ea\u8981\u4e0d\u6d89\u53ca\u5c11\u6797\u50e7\u4fa3\uff0c\u8bf7\u8bf4\u65e0\u59a8\uff01\u201d \u3000\u3000\u8a00\u4e0b\u4e4b\u610f\uff0c\u4e0d\u5ba3\u800c\u55bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u81ea\u7136\u542c\u5f97\u51fa\u6765\uff0c\u5979\u662f\u8981\u4ed6\u4e0d\u518d\u53c2\u5165\u6b64\u4e8b\u4e4b\u4e2d\uff0c\u4f46\u65b9\u96ea\u5b9c\u8868\u9762\u4e0a\u5374\u88c5\u4f5c\u4e0d\u61c2\uff0c\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u9002\u624d\u8fde\u4f24\u4e09\u4f4d\u5c11\u6797\u50e7\u4fa3\uff0c\u4e0d\u5acc\u4e0b\u624b\u72e0\u4e86\u4e00\u4e9b\u5417\uff1f\u201d \u3000\u3000\u674e\u82b8\u95fb\u8a00\uff0c\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u65b9\u516c\u5b50\uff0c\u5f7c\u6b64\u5bf9\u654c\uff0c\u96be\u9053\u8fd8\u8c08\u5f97\u4e0a\u6148\u60b2\u4e3a\u6000\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u90a3\u4e5f\u7528\u4e0d\u7740\u90a3\u7b49\u5fc3\u72e0\u624b\u8fa3\uff0c\u81f4\u4eba\u6b7b\u5730\u554a\uff01\u201d \u3000\u3000\u674e\u82b8\u5ffd\u5730\u683c\u683c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u516c\u5b50\uff0c\u5018\u82e5\u5f53\u65f6\u4e0d\u662f\u59be\u8eab\u4e0b\u624b\u6781\u5feb\uff0c\u6b64\u523b\u8ddf\u516c\u5b50\u8bf4\u8bdd\u7684\u4eba\uff0c\u6015\u4e0d\u662f\u59be\u8eab\uff0c\u800c\u662f\u90a3\u5c11\u6797\u5bfa\u4e2d\u7684\u548c\u5c1a\u4eec\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u77e5\u9053\uff0c\u5979\u8bf4\u7684\u4e43\u662f\u5b9e\u60c5\uff0c\u4f46\u56e0\u81ea\u5df1\u7684\u7acb\u573a\u4e0d\u540c\uff0c\u867d\u7136\u674e\u82b8\u6709\u7406\uff0c\u4e5f\u4e0d\u80fd\u4ee4\u4ed6\u5fc3\u670d\u3002 \u3000\u3000\u5f53\u4e0b\u6447\u5934\u4e00\u7b11\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u533a\u533a\u4e0d\u4fe1\u5c11\u6797\u50e7\u4fa3\uff0c\u4f1a\u662f\u90a3\u7b49\u6b8b\u5fcd\u55dc\u6740\u4e4b\u4eba\uff01\u201d \u3000\u3000\u674e\u82b8\u95fb\u8a00\uff0c\u7728\u7740\u5927\u773c\uff0c\u6ce8\u89c6\u4e86\u65b9\u96ea\u5b9c\u534a\u664c\uff0c\u5ffd\u5730\u51b7\u7b11\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u65b9\u516c\u5b50\uff0c\u59be\u8eab\u60f3\u4e0d\u5230\u7adf\u4f1a\u88ab\u4f60\u9a97\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u6697\u9053\uff1a\u4f60\u624d\u660e\u767d\uff1f\u53ef\u60dc\u8fdf\u5566\u2026\u2026 \u3000\u3000\u53ea\u662f\uff0c\u4ed6\u53e3\u4e2d\u5374\u662f\u4e0d\u8ba4\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u8fd9\u662f\u4f55\u610f\u5462\uff1f\u53ef\u662f\u628a\u65b9\u67d0\u5f53\u4f5c\u5c11\u6797\u5bfa\u4e2d\u7684\u5e2e\u624b\u4e86\u5417\uff1f\u201d \u3000\u3000\u674e\u82b8\u89c1\u4ed6\u795e\u60c5\u751a\u662f\u5e84\u91cd\uff0c\u53e3\u6c14\u53c8\u5341\u5206\u4e25\u8083\uff0c\u4e0d\u7531\u6c89\u541f\u9053\uff1a\u201c\u4e5f\u8bb8\u2026\u2026\u59be\u8eab\u6599\u9519\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u672c\u6765\u662f\u9519\u4e86\uff01\u533a\u533a\u4e5f\u5df2\u8bf4\u8fc7\uff0c\u4e0d\u662f\u5c11\u6797\u95e8\u4e0b\u4e4b\u4eba\uff0c\u59d1\u5a18\u4e00\u5b9a\u4e0d\u4fe1\uff0c\u53eb\u65b9\u67d0\u5fc3\u4e2d\u597d\u751f\u96be\u8010\uff01\u201d \u3000\u3000\u674e\u82b8\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u516c\u5b50\uff0c\u59be\u8eab\u7ed9\u4f60\u966a\u4e2a\u793c\uff0c\u516c\u5b50\u83ab\u8981\u8bb0\u5728\u5fc3\u4e0a\u2026\u2026\u201d \u3000\u3000\u5979\u4eba\u672c\u6765\u5c31\u751f\u5f97\u5f88\u7f8e\uff0c\u6708\u8272\u6726\u80e7\u4e4b\u4e0b\uff0c\u66f4\u663e\u5f97\u5a07\u8273\u52a8\u4eba\uff0c\u6b64\u523b\u6709\u5fc3\u5356\u5f04\uff0c\u66f4\u662f\u4f7f\u4eba\u770b\u6765\u67d4\u5a9a\u5165\u9aa8\uff01 \u3000\u3000\u5b89\u5c0f\u840d\u85cf\u5728\u77f3\u540e\uff0c\u8d8a\u770b\u8d8a\u89c9\u5f97\u6b64\u5973\u53ef\u607c\uff0c\u4e0d\u7531\u5f97\u62c9\u4e86\u4e00\u62c9\u5b8b\u5b5a\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u4f60\u51fa\u624b\u628a\u90a3\u5996\u5973\u8d76\u8d70\uff0c\u597d\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u54b1\u4eec\u4e0d\u80fd\u8d76\u8d70\u5979\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u54b1\u4eec\u80fd\u5c06\u5979\u4eec\u751f\u64d2\uff0c\u5c82\u4e0d\u80dc\u8fc7\u5c06\u5979\u4eec\u5413\u8d70\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u60f3\u4e86\u4e00\u60f3\u9053\uff1a\u201c\u8fd9\u4e5f\u4e0d\u9519\u2026\u2026\u201d\u8bdd\u97f3\u4e00\u843d\uff0c\u5c31\u5f85\u7ad9\u8d77\u8eab\u6765\u3002\u5c31\u5728\u6b64\u9645\u8033\u4e2d\u5ffd\u7136\u4f20\u6765\u4e00\u9635\u811a\u6b65\u4e4b\u58f0\u3002\u5979\u7565\u4e00\u72b9\u8c6b\uff0c\u5b8b\u5b5a\u5df2\u4f38\u624b\u62c9\u4e86\u5979\u4e00\u4e0b\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u54b1\u4eec\u518d\u8eb2\u4e0a\u4e00\u4f1a\u513f\uff0c\u77a7\u77a7\u6765\u7684\u662f\u4ec0\u4e48\u4eba\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5012\u4e5f\u542c\u8bdd\uff0c\u72ec\u81ea\u6309\u637a\u4e86\u4e00\u8154\u4e0d\u5feb\uff0c\u91cd\u65b0\u8e72\u5728\u90a3\u5de8\u77f3\u4e4b\u540e\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5374\u89c1\u90a3\u56db\u540d\u9ec4\u8863\u5c11\u5973\u8fc5\u901f\u5730\u5411\u5916\u6563\u5f00\uff0c\u5c06\u90a3\u674e\u82b8\u548c\u65b9\u96ea\u5b9c\u7559\u5728\u5f53\u4e2d\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u7779\u72b6\uff0c\u5927\u58f0\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u6709\u4eba\u6765\u4e86\uff01\u201d \u3000\u3000\u674e\u82b8\u5403\u4e86\u4e00\u60ca\uff0c\u5c06\u5a9a\u6001\u6536\u8d77\uff0c\u76ee\u5149\u95ea\u7535\u822c\u5730\u56db\u4e0b\u91cc\u770b\u4e86\u4e00\u770b\uff0c\u9053\uff1a\u201c\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u6562\u60c5\uff0c\u5979\u9002\u624d\u5168\u5fc3\u5168\u610f\u5730\u60f3\u7528\u5a9a\u672f\u8ff7\u60d1\u65b9\u96ea\u5b9c\uff0c\u8fde\u90a3\u811a\u6b65\u4e4b\u58f0\uff0c\u90fd\u672a\u542c\u5230\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u7684\u56db\u540d\u5c5e\u4e0b\uff0c\u5df2\u7136\u6563\u5f00\uff0c\u7eb5\u7136\u6709\u4eba\u524d\u6765\uff0c\u4e5f\u4f24\u4e0d\u5230\u59d1\u5a18\u4e86\uff01\u201d \u3000\u3000\u674e\u82b8\u8138\u4e0a\u7ea2\u6655\u4e4d\u73b0\uff0c\u4f4e\u5934\u4e00\u7b11\u9053\uff1a\u201c\u591a\u8c22\u516c\u5b50\u63d0\u9192\u4e86\u59be\u8eab\u2026\u2026\u201d \u3000\u3000\u8bdd\u97f3\u672a\u5df2\uff0c\u53ea\u542c\u8eab\u540e\u4f20\u6765\u4e00\u58f0\u8106\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u663e\u7136\uff0c\u90a3\u5411\u81ea\u5df1\u8eab\u540e\u6563\u53bb\u7684\u5c5e\u4e0b\uff0c\u5df2\u7136\u53d1\u73b0\u4e86\u6765\u4eba\uff01 \u3000\u3000\u90a3\u9ec4\u8863\u5c11\u5973\u8bdd\u97f3\u752b\u843d\uff0c\u53ea\u542c\u5f97\u4e00\u9635\u7c97\u8fc8\u7684\u5927\u7b11\u4e4b\u58f0\uff0c\u4f20\u5165\u8033\uff0c\u7b11\u58f0\u672a\u5df2\uff0c\u5374\u6709\u4e00\u4e2a\u4f4e\u6c89\u7684\u58f0\u97f3\u63a5\u9053\uff1a\u201c\u59d1\u5a18\u53c8\u662f\u4ec0\u4e48\u4eba\uff1f\u6df1\u66f4\u534a\u591c\uff0c\u8dd1\u6765\u8fd9\u8352\u5c71\u4e4b\u4e2d\uff0c\u4e0d\u6015\u88ab\u90a3\u79bd\u517d\u60ca\u5413\u4e86\u5417\uff1f\u201d \u3000\u3000\u811a\u6b65\u4e4b\u58f0\uff0c\u8d8a\u6765\u8d8a\u6c89\uff0c\u663e\u7136\u6765\u4eba\u5e76\u6731\u6b62\u6b65\u3002 \u3000\u3000\u674e\u82b8\u67f3\u7709\u4e00\u626c\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4f60\u83ab\u8981\u8d70\uff0c\u5f85\u59be\u8eab\u53bb\u77a7\u77a7\u2026\u2026\u201d \u3000\u3000\u4e00\u626d\u5a07\u8eaf\uff0c\u4fbf\u5411\u6765\u8def\u8d70\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u9053\uff1a\u4f60\u60f3\u6211\u4f1a\u8d70\u5417\uff1f\u4e0d\u77e5\u5229\u5bb3\u7684\u4e2b\u5934\uff0c\u4eca\u513f\u4f60\u662f\u5012\u5b9a\u9709\u4e86\u2026\u2026 \u3000\u3000\u4ed6\u5fc3\u4e2d\u51b7\u7b11\uff0c\u76ee\u5149\u5374\u5c04\u5411\u674e\u82b8\u7684\u53bb\u5904\uff0c\u53ea\u89c1\u5979\u8d70\u4e86\u4e0d\u5230\u4e08\u8bb8\u4fbf\u6709\u4e24\u6761\u4eba\u5f71\uff0c\u8fc5\u5982\u7535\u63a0\uff0c\u5411\u5979\u5954\u6765\u3002 \u3000\u3000\u8033\u4e2d\u542c\u5f97\u674e\u82b8\u5927\u559d\u4e00\u58f0\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u8fd8\u4e0d\u4e0e\u59d1\u5a18\u7ad9\u4f4f\uff01\u201d \u3000\u3000\u5f53\u5148\u7684\u90a3\u4eba\u5148\u662f\u5475\u5475\u5927\u7b11\u4e00\u9635\uff0c\u7136\u540e\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u51f6\u5df4\u5df4\u7684\u5e72\u4ec0\u4e48\uff1f\u8001\u592b\u4e5f\u4e0d\u662f\u4ec0\u4e48\u6c5f\u6d0b\u5927\u76d7\uff0c\u4f60\u6015\u4ec0\u4e48\uff1f\u771f\u662f\u7b11\u8bdd\u2026\u2026\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u4e24\u4eba\u5df2\u7136\u8d70\u5230\u4e86\u674e\u82b8\u8eab\u524d\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e3e\u76ee\u671b\u53bb\uff0c\u867d\u7136\u6708\u8272\u4e0d\u660e\uff0c\u4f46\u4e08\u8bb8\u8ddd\u79bb\uff0c\u4ed6\u8fd8\u662f\u80fd\u591f\u77a7\u5f97\u5341\u5206\u771f\u5207\uff01\u53ea\u662f\u8fd9\u4e24\u4eba\u4e43\u662f\u4e00\u4f4d\u8001\u4eba\u548c\u4e00\u4f4d\u4e2d\u5e74\u6587\u58eb\uff0c\u8bf4\u8bdd\u7684\u4eba\uff0c\u5c31\u662f\u90a3\u4f4d\u8001\u8005\u3002 \u3000\u3000\u674e\u82b8\u773c\u89c1\u4e24\u4eba\u5230\u4e86\u81ea\u5df1\u8eab\u524d\u4e94\u5c3a\uff0c\u65b9\u59cb\u6b62\u6b65\uff0c\u5fc3\u4e2d\u5927\u4e3a\u607c\u6012\uff0c\u6362\u4e86\u5e73\u65e5\uff0c\u90a3\u201c\u5929\u6bd2\u94f6\u9488\u201d\u65e9\u5df2\u5c31\u6697\u6697\u5c04\u4e86\u51fa\u53bb\uff0c\u53ea\u56e0\u9002\u624d\u88ab\u65b9\u96ea\u5b9c\u6570\u8bf4\u4e86\u51e0\u53e5\uff0c\u8fd9\u624d\u4e0d\u613f\u518d\u5728\u65b9\u96ea\u5b9c\u8ddf\u524d\uff0c\u7528\u90a3\u6b79\u6bd2\u7684\u6697\u5668\u4f24\u4eba\uff01 \u3000\u3000\u4f46\u5979\u867d\u7136\u4e0d\u4f1a\u4f7f\u7528\u6697\u5668\uff0c\u80cc\u540e\u7684\u957f\u5251\uff0c\u5374\u5df2\u51fa\u9798\uff0c\u51b7\u54fc\u4e00\u58f0\uff0c\u559d\u9053\uff1a\u201c\u4e24\u4f4d\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u90a3\u8eab\u7740\u9ec4\u886b\u7684\u9ad8\u5927\u8001\u4eba\uff0c\u5ffd\u7136\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u95ee\u7684\u662f\u8001\u592b\u5417\uff1f\u201d \u3000\u3000\u674e\u82b8\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u8fd9\u513f\u9664\u4e86\u4f60\u4eec\u4e24\u4e2a\uff0c\u8fd8\u6709\u8c01\uff1f\u4f60\u4eec\u83ab\u975e\u6ca1\u6709\u5e26\u8033\u6735\u5417\uff1f\u201d \u3000\u3000\u9ec4\u886b\u8001\u4eba\u5927\u7b11\u9053\uff1a\u201c\u8001\u592b\u5929\u751f\u4e00\u53cc\u62db\u98ce\u8033\uff0c\u4e0d\u8bf4\u8fd9\u70b9\u513f\u8fdc\uff0c\u5c31\u662f\u59d1\u5a18\u8fdc\u5728\u4e94\u91cc\u4e4b\u5916\u8bf4\u8bdd\uff0c\u8001\u592b\u4e5f\u80fd\u542c\u5f97\u660e\u660e\u767d\u767d\uff01\u201d \u3000\u3000\u7ad9\u5728\u9ec4\u886b\u8001\u4eba\u8eab\u65c1\u7684\u4e2d\u5e74\u6587\u58eb\uff0c\u5ffd\u7136\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u8463\u5144\uff0c\u54b1\u4eec\u54ea\u6709\u90a3\u4e48\u591a\u65f6\u95f4\u8ddf\u5979\u7a77\u626f\uff1f\u8fd8\u4e0d\u5982\u65e9\u4e9b\u6253\u53d1\u4e86\u5979\u4eec\u5427\uff01\u201d \u3000\u3000\u90a3\u9ec4\u886b\u8001\u4eba\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff01\u8096\u8001\u5f1f\uff0c\u662f\u8001\u673d\u51fa\u624b\uff0c\u8fd8\u662f\u4f60\u51fa\u624b\u6559\u8bad\u6559\u8bad\u5462\uff1f\u201d \u3000\u3000\u90a3\u59d3\u8096\u7684\u4e2d\u5e74\u6587\u58eb\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u8fd9\u5996\u5973\u4ea4\u7ed9\u5144\u5f1f\u597d\u4e86\uff01\u53e6\u5916\u90a3\u56db\u4f4d\uff0c\u53ef\u8981\u504f\u52b3\u8463\u5144\u4e86\uff01\u201d \u3000\u3000\u9661\u7136\u89c1\u4ed6\u4e00\u8f6c\u8eab\uff0c\u626c\u624b\u4e00\u6325\uff0c\u90a3\u5b88\u5728\u4ed6\u4eec\u8eab\u540e\u4e08\u8bb8\u7684\u4e00\u4f4d\u9ec4\u8863\u5c11\u5973\uff0c\u5ffd\u7136\u5624\u549b\u4e00\u58f0\uff0c\u4e00\u8de4\u683d\u5012\u5728\u5730\u4e0a\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u77a7\u5f97\u5fc3\u4e2d\u4e00\u9707\uff0c\u6697\u9053\uff1a\u201c\u597d\u5389\u5bb3\u7684\u6307\u529b\uff01\u201d \u3000\u3000\u539f\u6765\u90a3\u9ec4\u886b\u8001\u4eba\u8f6c\u8eab\u4e4b\u9645\uff0c\u51fa\u624b\u4e00\u6307\uff0c\u7528\u4e86\u9694\u7a7a\u70b9\u7a74\u7684\u5de5\u592b\uff0c\u5c06\u90a3\u5c11\u5973\u70b9\u5012\u5728\u5730\uff01 \u3000\u3000\u674e\u82b8\u8033\u542c\u4e24\u4eba\u4e00\u756a\u5bf9\u7b54\uff0c\u6b63\u5728\u5fc3\u4e2d\u51b7\u7b11\uff0c\u6ee1\u5fc3\u60f3\u8bbd\u523a\u4ed6\u4eec\u4e24\u53e5\uff0c\u8bdd\u513f\u5c1a\u672a\u51fa\u53e3\uff0c\u53ea\u89c1\u81ea\u5df1\u7684\u5c5e\u4e0b\uff0c\u5df2\u6709\u4e00\u4eba\u91cd\u4f24\u5012\u5730\uff0c\u9a87\u7136\u5927\u9707\u4e4b\u4e0b\uff0c\u8fde\u60f3\u90fd\u6ca1\u60f3\uff0c\u7693\u8155\u4e00\u626c\uff0c\u4e3e\u5251\u4fbf\u5411\u90a3\u767d\u8863\u4e2d\u5e74\u6587\u58eb\u523a\u4e86\u8fc7\u53bb\uff01 \u3000\u3000\u8fd9\u4e00\u5251\u4e0d\u4f46\u53bb\u52bf\u6781\u5feb\uff0c\u800c\u4e14\u4e5f\u662f\u5728\u5bf9\u65b9\u6beb\u65e0\u9632\u8303\u4e4b\u9645\uff0c\u8d38\u7136\u51fa\u624b\uff0c\u674e\u82b8\u539f\u4ee5\u4e3a\u5bf9\u65b9\u7eb5\u5728\u95ea\u907f\uff0c\u4e5f\u5fc5\u662f\u5f04\u7684\u72fc\u72c8\u4e0d\u582a\uff01 \u3000\u3000\u4f46\u4e8b\u5b9e\u4e0a\u90a3\u4e2d\u5e74\u6587\u58eb\u5374\u4ece\u5bb9\u5df2\u6781\uff0c\u674e\u82b8\u5251\u52bf\u752b\u51fa\uff0c\u4ed6\u5df2\u7136\u817e\u8eab\u8dc3\u8d77\u4e03\u5c3a\uff0c\u53f3\u624b\u4e00\u62ac\uff0c\u80c1\u4e0a\u5b9d\u5251\u4e5f\u544a\u51fa\u9798\uff1b\u800c\u4e14\u5251\u5c16\u4e00\u6307\uff0c\u8fc5\u5feb\u5730\u5411\u674e\u82b8\u8fd8\u51fb\u4e86\u4e00\u5251\uff01\u817e\u8eab\uff0c\u62d4\u5251\uff0c\u53cd\u624b\u653b\u654c\uff0c\u51e0\u4e4e\u662f\u4e00\u6c14\u5475\u6210\uff0c\u674e\u82b8\u867d\u662f\u5929\u9b54\u5973\u4eb2\u4f20\u5f1f\u5b50\uff0c\u4e5f\u4e0d\u7981\u88ab\u5bf9\u65b9\u8fd9\u7b49\u6c14\u52bf\u6240\u9707\uff0c\u5306\u5fd9\u4e2d\u4e00\u8fde\u9000\u4e86\u4e09\u6b65\uff01 \u3000\u3000\u4e2d\u5e74\u6587\u58eb\u8eab\u5f62\u843d\u5730\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u53ef\u662f\u5929\u9b54\u5973\u7684\u95e8\u4e0b\uff1f\u201d \u3000\u3000\u674e\u82b8\u51fa\u624b\u4e00\u62db\uff0c\u5c31\u88ab\u4eba\u5bb6\u53eb\u7834\u4e86\u6765\u5386\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u5927\u60ca\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u8c01\uff1f\u6b66\u6797\u89c1\u8bc6\u5f88\u5e7f\u554a\uff01\u4f46\u662f\u2026\u2026\u201d \u3000\u3000\u5979\u53e3\u4e2d\u5374\u63a5\u9053\uff1a\u201c\u662f\u53c8\u5982\u4f55\uff1f\u201d \u3000\u3000\u90a3\u4e2d\u5e74\u6587\u58eb\u8fd9\u65f6\u84e6\u5730\u6717\u6717\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\u554a\uff01\u8fd9\u771f\u53eb\u8e0f\u7834\u94c1\u978b\u65e0\u89c5\u5904\uff0c\u5f97\u6765\u5168\u4e0d\u8d39\u5de5\u592b\u4e86\uff01\u201d \u3000\u3000\u674e\u82b8\u95fb\u8a00\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u662f\u8c01\uff1f\u627e\u6211\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e2d\u5e74\u6587\u58eb\u5475\u5475\u4e00\u7b11\u9053\uff1a\u201c\u533a\u533a\u5417\uff1f\u59d3\u8096\u540d\u98d2\uff0c\u8499\u6b66\u6797\u670b\u53cb\u77a7\u5f97\u8d77\uff0c\u9001\u4e86\u533a\u533a\u4e00\u4e2a\u7ef0\u53f7\u2018\u5bd2\u6885\u5251\u5ba2\u2019\uff0c\u59d1\u5a18\u53ef\u66fe\u542c\u8bf4\u8fc7\u4e86\uff01\u201d \u3000\u3000\u674e\u82b8\u542c\u4ed6\u62a5\u51fa\u540d\u53f7\uff0c\u4e0d\u7981\u8138\u8272\u5927\u53d8\uff0c\u8131\u53e3\u9053\uff1a\u201c\u539f\u6765\u662f\u6606\u4ed1\u6d3e\u7684\u8096\u5927\u5148\u751f\uff0c\u59be\u8eab\u5012\u662f\u5931\u656c\u4e86\uff01\u201d \u3000\u3000\u8096\u98d2\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u8fc7\u5956\u4e86\uff0c\u59d1\u5a18\u600e\u4e48\u79f0\u8c13\uff1f\u5c0a\u5e08\u5929\u9b54\u5973\u53ef\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u674e\u82b8\u9053\uff1a\u201c\u59be\u8eab\u674e\u82b8\uff0c\u5bb6\u5e08\u5417\uff0c\u8fd8\u6ca1\u6765\uff01\u201d \u3000\u3000\u8096\u98d2\u53cc\u7709\u4e00\u626c\uff0c\u9053\uff1a\u201c\u4f60\u5e08\u7236\u51e0\u65f6\u6765\uff1f\u201d \u3000\u3000\u674e\u82b8\u6447\u5934\u9053\uff1a\u201c\u4e0d\u77e5\u9053\uff01\u201d \u3000\u3000\u8096\u98d2\u4e00\u6014\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u4e0d\u613f\u8bf4\u5417\uff1f\u201d \u3000\u3000\u674e\u82b8\u9053\uff1a\u201c\u59be\u8eab\u672c\u6765\u4e0d\u77e5\u9053\uff0c\u8096\u5927\u5148\u751f\u4e0d\u4fe1\uff0c\u90a3\u4e5f\u662f\u6ca1\u6709\u6cd5\u5b50\u7684\u4e8b\u4e86\uff01\u201d \u3000\u3000\u8096\u98d2\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u5ffd\u7136\u5411\u90a3\u9ec4\u8863\u8001\u4eba\u5927\u58f0\u9053\uff1a\u201c\u8463\u5144\uff0c\u90a3\u5929\u9b54\u5973\u5c1a\u672a\u5230\u5d69\u5c71\uff0c\u5979\u7684\u624b\u4e0b\u4e4b\u4eba\uff0c\u54b1\u4eec\u7528\u4e0d\u7740\u4e3a\u96be\u5979\u4eec\u4e86\uff01\u201d \u3000\u3000\u90a3\u9ec4\u886b\u8001\u4eba\u8fd9\u65f6\u5df2\u7136\u5c06\u56db\u540d\u9ec4\u8863\u5c11\u5973\u70b9\u5012\u4e86\u4e09\u4eba\uff0c\u95fb\u8a00\u5475\u5475\u4e00\u7b11\u9053\uff1a\u201c\u8096\u8001\u5f1f\u8fd9\u4e48\u8bf4\uff0c\u8001\u592b\u5c31\u4e0d\u4e3a\u5df1\u751a\u2026\u2026\u201d \u3000\u3000\u4e3e\u624b\u4e00\u638c\uff0c\u5c06\u90a3\u552f\u4e00\u672a\u4f24\u7684\u9ec4\u8863\u5c11\u5973\u9707\u9000\u4e09\u5c3a\uff0c\u8f6c\u8eab\u4fbf\u5411\u8096\u98d2\u7acb\u8eab\u4e4b\u5904\u884c\u6765\u3002 \u3000\u3000\u8096\u98d2\u5411\u674e\u82b8\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u4e43\u662f\u534e\u5c71\u9ec4\u886b\u53df\u8463\u5343\u91cc\uff0c\u674e\u59d1\u5a18\u60f3\u5fc5\u4e0d\u4f1a\u4e0d\u77e5\u5427\uff01\u201d \u3000\u3000\u674e\u82b8\u5fc3\u4e2d\u53c8\u662f\u4e00\u9707\uff0c\u8fde\u5fd9\u798f\u4e86\u4e00\u798f\uff0c\u9053\uff1a\u201c\u5c0f\u5973\u5b50\u674e\u82b8\uff0c\u89c1\u8fc7\u534e\u5c71\u8463\u957f\u8001\uff01\u201d \u3000\u3000\u8463\u5343\u91cc\u6380\u9aef\u4e00\u7b11\u9053\uff1a\u201c\u7f62\u4e86\uff01\u4f60\u5e08\u7236\u8fd8\u6ca1\u6709\u6765\uff1f\u201d \u3000\u3000\u674e\u82b8\u6447\u5934\u9053\uff1a\u201c\u5bb6\u5e08\u6ca1\u6709\u6765\u2026\u2026\u201d \u3000\u3000\u8463\u5343\u91cc\u4e0d\u7b49\u5979\u8bf4\u5b8c\uff0c\u5ffd\u7136\u76ee\u5149\u7535\u5c04\u4e08\u8bb8\u4e4b\u5916\u7684\u65b9\u96ea\u5b9c\uff0c\u76b1\u7709\u9053\uff1a\u201c\u90a3\u5c0f\u5b50\u662f\u4f60\u7684\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u674e\u82b8\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u4e43\u662f\u65b9\u516c\u5b50\uff0c\u5c0f\u5973\u5b50\u4e5f\u662f\u521a\u521a\u8ba4\u8bc6\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5728\u542c\u5230\u8463\u5343\u91cc\u559d\u95ee\u4e4b\u65f6\uff0c\u5df2\u7136\u5927\u6b65\u8d70\u4e86\u8fc7\u6765\uff0c\u5e94\u58f0\u63a5\u9053\uff1a\u201c\u674e\u59d1\u5a18\u8bf4\u7684\u4e0d\u9519\uff0c\u533a\u533a\u65b9\u96ea\u5b9c\uff0c\u9002\u624d\u8d70\u5230\u6b64\u5730\uff0c\u6b63\u597d\u9047\u4e0a\u4e86\u8fd9\u4f4d\u59d1\u5a18\u4e0e\u5c11\u6797\u5b50\u5f1f\u52a8\u624b\u2026\u2026\u201d \u3000\u3000\u4ed6\u5927\u6a21\u5927\u6837\u5730\u8d70\u6765\uff0c\u89c1\u4e86\u90a3\u8463\u5343\u91cc\u548c\u8096\u98d2\uff0c\u4e5f\u53ea\u662f\u62f1\u4e86\u62f1\u624b\uff01 \u3000\u3000\u8463\u5343\u91cc\u770b\u5f97\u7709\u5934\u4e00\u76b1\uff0c\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u559d\u9053\uff1a\u201c\u4f60\u5c0f\u5b50\u4e43\u662f\u5c11\u6797\u95e8\u4e0b\u5417\uff1f\u4e00\u7537\u6570\u5973\uff0c\u66f4\u6df1\u591c\u9759\uff0c\u5728\u8fd9\u5c71\u91ce\u4e4b\u5904\uff0c\u8c05\u4e5f\u5e72\u4e0d\u51fa\u4ec0\u4e48\u597d\u4e8b\u6765\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5374\u6ca1\u6709\u60f3\u5230\uff0c\u8fd9\u8463\u5343\u91cc\u8bf4\u8bdd\uff0c\u5c45\u7136\u5982\u6b64\u96be\u542c\uff0c\u4fca\u9762\u4e00\u7ea2\uff0c\u500f\u7136\u5fc3\u5934\u6012\u8d77\uff0c\u51b7\u51b7\u5730\u626b\u4e86\u8463\u5343\u91cc\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5c0a\u9a7e\u8bf4\u8bdd\u600e\u751f\u8fd9\u822c\u96be\u542c\uff0c\u65b9\u67d0\u4e0d\u8fc7\u662f\u770b\u4e0d\u60ef\u8fd9\u51e0\u4f4d\u59d1\u5a18\u51fa\u624b\u592a\u72e0\uff0c\u8fd9\u624d\u51fa\u9762\u4e0e\u5979\u4eec\u7406\u8bba\uff0c\u529d\u5979\u4eec\u4e0d\u8981\u4ed7\u7740\u5e08\u95e8\u5a01\u671b\u548c\u6b66\u529f\uff0c\u5728\u6b66\u6797\u4e2d\u6a2a\u884c\u9738\u9053\uff0c\u96be\u9053\u4e5f\u6709\u4ec0\u4e48\u4e0d\u662f\u5417\uff1f\u201d\u8bed\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4f55\u51b5\uff0c\u533a\u533a\u5e76\u4e0d\u6b62\u4e00\u4eba\u5728\u6b64\uff0c\u5c0a\u9a7e\u4fe1\u53e3\u96cc\u9ec4\uff0c\u771f\u53eb\u4eba\u542c\u4e86\u597d\u7b11\uff01\u201d \u3000\u3000\u4ed6\u8fd9\u51e0\u53e5\u8bdd\u8bf4\u5f97\u4e0d\u8f7b\uff0c\u8463\u5343\u91cc\u53ea\u542c\u5f97\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u4f60\u662f\u8bf4\u8001\u592b\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u51b7\u7b11\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u5c0a\u9a7e\u4e3a\u8001\u4e0d\u5c0a\uff0c\u53ea\u6015\u602a\u4e0d\u5f97\u522b\u4eba\u8ba5\u523a\u4e8e\u4f60\u4e86\uff01\u201d \u3000\u3000\u8463\u5343\u91cc\u88ab\u4ed6\u9876\u649e\u5f97\u5927\u5927\u96be\u582a\uff0c\u6012\u54fc\u4e00\u58f0\uff0c\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u53ef\u77e5\u9053\u8001\u592b\u662f\u8c01\uff1f\u7adf\u6562\u5982\u6b64\u653e\u8086\uff1f\u5feb\u5feb\u62a5\u51fa\u5e08\u95e8\uff0c\u8001\u592b\u81ea\u4f1a\u627e\u4f60\u7684\u5c0a\u957f\u7b97\u5e10\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5ffd\u7136\u8138\u8272\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u4e0d\u7528\u4e86\uff01\u5bb6\u5e08\u4e0d\u5de7\uff0c\u6b63\u597d\u5728\u6708\u524d\u4ed9\u901d\u53bb\u4e86\u2026\u2026\u201d \u3000\u3000\u8463\u5343\u91cc\u4e00\u6014\uff0c\u9053\uff1a\u201c\u8fd9\u4e48\u5de7\uff1f\u4f60\u5e08\u7236\u662f\u8c01\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u5417\uff1f\u6055\u96be\u5949\u544a\uff01\u201d \u3000\u3000\u8463\u5343\u91cc\u53ea\u9053\u4ed6\u6709\u5fc3\u8ddf\u81ea\u5df1\u8fc7\u4e0d\u53bb\uff0c\u52c3\u7136\u5927\u6012\u9053\uff1a\u201c\u9ec4\u6bdb\u672a\u9000\uff0c\u4e73\u81ed\u672a\u5e72\uff0c\u4f60\u5c45\u7136\u6562\u5bfb\u8001\u592b\u5f00\u5fc3\u5417\uff1f\u201d\u559d\u9a82\u58f0\u4e2d\uff0c\u4e3e\u624b\u4e00\u638c\uff0c\u4fbf\u5411\u65b9\u96ea\u5b9c\u62cd\u6765\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c0a\u9a7e\u597d\u4e0d\u8bb2\u7406\u2026\u2026\u201d \u3000\u3000\u66f2\u6307\u4e00\u5f39\uff0c\u4e00\u80a1\u52b2\u98ce\uff0c\u5b9b\u5982\u5229\u7bad\u4e00\u822c\uff0c\u51fb\u5411\u8463\u5343\u91cc\u7684\u65f6\u8282\u3002 \u3000\u3000\u8463\u5343\u91cc\u9a87\u7136\u4e00\u9707\uff0c\u62cd\u51fa\u7684\u638c\u52bf\u4e00\u6536\uff0c\u659c\u8de8\u4e00\u6b65\uff0c\u63a2\u624b\u53c8\u5411\u65b9\u96ea\u5b9c\u53f3\u8155\u6293\u6765\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u89c1\u4ed6\u8fd9\u4e00\u6293\u4e4b\u52bf\uff0c\u5341\u5206\u51f6\u72e0\uff0c\u5fc3\u4e2d\u9053\uff1a\u201c\u4e0d\u8ba9\u4ed6\u5403\u4e00\u70b9\u82e6\u5934\uff0c\u90a3\u662f\u51b3\u7136\u4e0d\u4f1a\u7f62\u4f53\u7684\u4e86\uff01\u201d \u3000\u3000\u8f6c\u5ff5\u4e4b\u95f4\uff0c\u53cd\u624b\u4e00\u638c\uff0c\u51fb\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8463\u5343\u91cc\u7779\u72b6\uff0c\u563f\u563f\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u4f60\u679c\u771f\u4e0d\u81ea\u91cf\u529b\u5f97\u5f88\u2026\u2026\u201d \u3000\u3000\u4f46\u4ed6\u7b11\u58f0\u53ea\u53d1\u51fa\u4e86\u4e00\u534a\uff0c\u84e6\u7136\u5927\u543c\u4e00\u58f0\uff0c\u98de\u5feb\u7684\u5411\u540e\u5012\u9000\u4e94\u5c3a\uff0c\u4e00\u8138\u60ca\u51db\u4e4b\u8272\uff0c\u5de6\u624b\u63e1\u53f3\u8155\uff0c\u76f4\u77a7\u7740\u65b9\u96ea\u5b9c\u53d1\u6123\uff01 \u3000\u3000\u5bd2\u6885\u5251\u5ba2\u8096\u98d2\u8138\u8272\u5341\u5206\u5947\u602a\uff0c\u4ed6\u770b\u4e86\u770b\u65b9\u96ea\u5b9c\uff0c\u8fd9\u624d\u4e00\u95ea\u8eab\uff0c\u884c\u5230\u8463\u5343\u91cc\u8eab\u524d\uff0c\u95ee\u9053\uff1a\u201c\u8463\u5144\uff0c\u600e\u4e48\u4e86\uff1f\u201d \u3000\u3000\u8463\u5343\u91cc\u5ffd\u7136\u76b1\u7709\u6c89\u541f\uff0c\u9053\uff1a\u201c\u6211\u2026\u2026\u88ab\u8fd9\u5c0f\u5b50\u7684\u5996\u6cd5\u4f24\u4e86\uff01\u201d \u3000\u3000\u4ed6\u8fd9\u8bdd\u51fa\u53e3\uff0c\u53ea\u628a\u674e\u82b8\u542c\u7684\u6251\u54e7\u4e00\u58f0\uff0c\u7b11\u4e86\u51fa\u6765\u3002 \u3000\u3000\u987b\u77e5\u5996\u6cd5\u65e0\u51ed\uff0c\u4eba\u4e16\u4e4b\u95f4\uff0c\u96be\u5f97\u4e00\u89c1\uff0c\u4ee5\u8463\u5343\u91cc\u4e43\u662f\u4e00\u6d3e\u957f\u8001\u7684\u8eab\u4efd\uff0c\u7adf\u800c\u8bf4\u51fa\u8fd9\u79cd\u8bdd\u6765\uff0c\u6bcb\u602a\u674e\u82b8\u542c\u5f97\u5fcd\u4e0d\u4f4f\u5931\u7b11\u4e86\uff01 \u3000\u3000\u8096\u98d2\u5251\u7709\u4e00\u626c\uff0c\u6447\u5934\u9053\uff1a\u201c\u8463\u5144\uff0c\u8fd9\u4f4d\u5c0f\u5144\u5f1f\u2026\u2026\u53ea\u6015\u5e76\u4e0d\u662f\u4f7f\u7684\u5996\u6cd5\u54e9\uff01\u201d \u3000\u3000\u8463\u5343\u91cc\u5140\u81ea\u4e0d\u4fe1\uff0c\u9053\uff1a\u201c\u4e0d\u662f\u5996\u6cd5\uff0c\u53c8\u600e\u4f1a\u4f24\u5f97\u4e86\u8001\u592b\u8155\u8109\uff1f\u201d \u3000\u3000\u8096\u98d2\u4e0d\u7406\u4f1a\u4ed6\u4e4b\u8a00\uff0c\u5374\u662f\u8f6c\u8eab\u5411\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u521a\u624d\u8fd9\u62db\u624b\u6cd5\uff0c\u8096\u67d0\u66fe\u7ecf\u89c1\u8fc7\uff0c\u4e0d\u77e5\u53ef\u662f\u2026\u2026\u201d \u3000\u3000\u4f59\u97f3\u672a\u5df2\uff0c\u5ffd\u7136\u4e00\u9635\u5927\u7b11\uff0c\u81ea\u90a3\u5de8\u77f3\u4e4b\u540e\u4f20\u6765\u3002 \u3000\u3000\u8096\u98d2\u8138\u8272\u4e00\u53d8\uff0c\u6389\u5934\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u53ea\u89c1\u5b8b\u5b5a\u5927\u6b65\u8d70\u4e86\u8fc7\u6765\uff0c\u7b11\u9053\uff1a\u201c\u8001\u5077\u513f\u5b8b\u5b5a\uff01\u201d \u3000\u3000\u8096\u98d2\u95fb\u8a00\uff0c\u4f3c\u662f\u677e\u4e86\u4e00\u53e3\u6c14\uff0c\u8138\u4e0a\u7b11\u610f\u5ffd\u73b0\uff0c\u770b\u4e86\u5b8b\u5b5a\u4e00\u773c\uff0c\u62b1\u62f3\u9053\uff1a\u201c\u5f53\u771f\u662f\u5b8b\u5144\uff01\u4e45\u8fdd\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u8096\u5144\uff0c\u8463\u5144\uff0c\u4f60\u4eec\u4e5f\u597d\u554a\u2026\u2026\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4e24\u4f4d\u53ef\u662f\u5e94\u90a3\u5165\u4e91\u638c\u95e8\u4eba\u4e4b\u9080\uff0c\u524d\u6765\u5d69\u5c71\u7684\u5417\uff1f\u201d \u3000\u3000\u8096\u98d2\u9053\uff1a\u201c\u4e0d\u9519\uff01\u5b8b\u5144\u5462\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u95fb\u8a00\u5927\u7b11\u9053\uff1a\u201c\u6211\u545c\uff1f\u8bf4\u53e5\u4e0d\u4e2d\u542c\u7684\u8bdd\uff0c\u8c01\u89c1\u7740\u6211\u90fd\u8981\u5934\u75bc\u4e0d\u5df2\uff0c\u81ea\u7136\u4e0d\u4f1a\u662f\u88ab\u8bf7\u6765\u7684\u4e86\uff01\u201d \u3000\u3000\u8463\u5343\u91cc\u8fd9\u65f6\u5ffd\u7136\u5927\u53eb\u4e00\u58f0\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u8fd9\u5c0f\u5b50\u53ef\u662f\u8ddf\u4f60\u4e00\u9053\u7684\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u662f\u554a\uff01\u54ea\u91cc\u4e0d\u5bf9\u4e86\uff1f\u201d \u3000\u3000\u8463\u5343\u91cc\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u4f60\u600e\u4e48\u8ddf\u90a3\u4f1a\u5996\u6cd5\u7684\u4eba\u4e5f\u8d70\u5728\u4e00\u8d77\u4e86\uff1f\u771f\u6b63\u6ca1\u6709\u51fa\u606f\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u6562\uff01\u5f7c\u6b64\uff0c\u5f7c\u6b64\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bf4\u5f97\u5341\u5206\u5947\u5140\uff0c\u53ea\u542c\u5f97\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u5931\u7b11\uff01 \u3000\u3000\u6562\u60c5\uff0c\u4ed4\u7ec6\u4e00\u60f3\uff0c\u5b8b\u5b5a\u628a\u90a3\u6ca1\u6709\u51fa\u606f\u56db\u5b57\uff0c\u7b49\u4e8e\u539f\u5c01\u8fd8\u4e86\u8463\u5343\u91cc\uff01 \u3000\u3000\u8096\u98d2\u8fd9\u65f6\u76b1\u4e86\u76b1\u7709\uff0c\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u8fd9\u4f4d\u5c0f\u5144\u5f1f\u662f\u4f60\u4ec0\u4e48\u4eba\uff1f\u4ed6\u9002\u624d\u90a3\u4e00\u62db\u53ef\u662f\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u5f92\u7136\u6446\u4e86\u6446\u624b\uff0c\u4e0d\u5bb9\u4ed6\u8bf4\u660e\uff0c\u63a5\u9053\uff1a\u201c\u8096\u5144\u4f60\u4e0d\u7528\u591a\u8bf4\u4e86\uff01\u4ed6\u8fd9\u62db\u624b\u6cd5\uff0c\u4e43\u662f\u8001\u5077\u513f\u4ece\u522b\u4eba\u5904\u5077\u6765\u7684\u6b66\u529f\uff0c\u8bf4\u5c06\u51fa\u6765\uff0c\u53ef\u53eb\u6211\u8001\u5077\u513f\u4e22\u4eba\u4e86\uff01\u201d \u3000\u3000\u4ed6\u9002\u624d\u5ffd\u7136\u73b0\u8eab\u51fa\u6765\uff0c\u5c31\u662f\u6015\u6cc4\u6f0f\u4e86\u65b9\u96ea\u5b9c\u51fa\u8eab\uff0c\u88ab\u674e\u82b8\u542c\u53bb\u800c\u8f6c\u544a\u4e86\u5929\u9b54\u5973\uff1b\u8bf4\u4e0d\u5b9a\u5c31\u4f1a\u60f9\u6765\u66f4\u591a\u610f\u60f3\u4e0d\u5230\u7684\u9ebb\u70e6\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u76ee\u4e2d\u81ea\u662f\u5341\u5206\u611f\u6fc0\uff0c\u5411\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u4f60\u8fd9\u4e00\u62db\u638c\u6cd5\u679c\u7136\u4e0d\u51e1\uff0c\u5c45\u7136\u4f1a\u8ba9\u4eba\u5bb6\u5f53\u6210\u4e86\u5996\u6cd5\u770b\uff0c\u771f\u6b63\u662f\u5927\u5927\u503c\u5f97\u9ad8\u5174\u4e00\u756a\u554a\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u83ab\u8981\u5f97\u597d\u5c31\u5356\u4e56\u2026\u2026\u201d \u3000\u3000\u8f6c\u8138\u5411\u8096\u98d2\u63a5\u9053\uff1a\u201c\u8096\u5144\uff0c\u5165\u4e91\u5927\u5e08\u6b64\u523b\u6b63\u548c\u4e10\u5e2e\u970d\u5e2e\u4e3b\u4e00\u540c\u5728\u5bfa\u4ed9\u5f85\u654c\uff0c\u4e24\u4f4d\u5982\u662f\u5e94\u9080\u800c\u6765\u6700\u597d\u5feb\u53bb\u4e0e\u4ed6\u4eec\u89c1\u89c1\u9762\uff01\u201d \u3000\u3000\u8096\u98d2\u95fb\u8a00\uff0c\u770b\u4e86\u8463\u5343\u91cc\u4e00\u773c\uff0c\u9053\uff1a\u201c\u8463\u5144\u610f\u4e0b\u5982\u4f55\uff1f\u201d \u3000\u3000\u8463\u5343\u91cc\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u63a5\u9053\uff1a\u201c\u5b8b\u5144\u8bf4\u5f97\u4e0d\u9519\uff0c\u65e2\u7136\u90a3\u970d\u5e2e\u4e3b\u5df2\u7136\u6765\u5230\uff0c\u54b1\u4eec\u81ea\u7136\u662f\u5feb\u53bb\u89c1\u8fc7\u4ed6\u4eec\u624d\u662f\uff01\u201d \u3000\u3000\u8096\u98d2\u5411\u5b8b\u5b5a\u62f1\u4e86\u62f1\u624b\uff0c\u770b\u4e86\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u4fbf\u81ea\u8f6c\u8eab\u5411\u5c71\u4e0b\u884c\u53bb\u3002 \u3000\u3000\u8463\u5343\u91cc\u4e5f\u5411\u5b8b\u5b5a\u62db\u547c\u4e86\u4e00\u58f0\uff0c\u968f\u7740\u8096\u98d2\u8d70\u4e86\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u76ee\u7779\u4e24\u4eba\u8f6c\u8fc7\u4e86\u5c71\u89d2\uff0c\u4e0d\u7981\u7b11\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u4ed6\u4eec\u6765\u5f97\u5f88\u7a81\u7136\uff0c\u4f46\u53bb\u5f97\u53ef\u4e5f\u771f\u5feb\u554a\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u59be\u81ea\u5c0a\u5927\uff0c\u672c\u8be5\u8981\u4ed6\u4eec\u5403\u4f60\u8001\u5f1f\u4e00\u987f\u6392\u5934\uff0c\u715e\u715e\u4ed6\u4eec\u7684\u50b2\u6c14\uff0c\u4f46\u8001\u673d\u6df1\u6050\u65e5\u540e\u5f7c\u6b64\u89c1\u9762\u65f6\u4e0d\u597d\u4ea4\u4ee3\uff0c\u6240\u5c31\u628a\u4ed6\u4eec\u529d\u8d70\u4e86\uff0c\u8001\u5f1f\uff0c\u4f60\u4e0d\u4f1a\u5acc\u8001\u673d\u591a\u4e8b\u5427\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u90a3\u600e\u4e48\u4f1a\uff1f\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u674e\u82b8\u5df2\u7136\u7f13\u7f13\u8f6c\u8fc7\u8eab\u6765\uff0c\u5411\u65b9\u96ea\u5b9c\u76c8\u76c8\u4e00\u793c\uff0c\u9053\uff1a\u201c\u591a\u8c22\u516c\u5b50\u4ed7\u4e49\u63f4\u624b\u2026\u2026\u76f8\u52a9\u4e4b\u60c5\uff0c\u59be\u8eab\u53ea\u597d\u5bb9\u56fe\u540e\u62a5\u4e86\uff01\u201d \u3000\u3000\u90a3\u82b1\u822c\u7684\u53cc\u988a\uff0c\u5ffd\u7136\u98de\u4e0a\u4e00\u7ea2\u6655\u3002 \u3000\u3000\u65b9\u96ea\u5b9a\u4f3c\u662f\u5e76\u672a\u6ce8\u610f\u5230\u5979\u90a3\u4e00\u80a1\u7f9e\u610f\uff0c\u53ea\u6de1\u6de1\u63a5\u9053\uff1a\u201c\u59d1\u5a18\u4e0d\u7528\u653e\u5728\u5fc3\u4e0a\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7709\u5934\u4e00\u76b1\uff0c\u5411\u674e\u82b8\u559d\u9053\uff1a\u201c\u674e\u59d1\u5a18\uff0c\u4f60\u5e08\u7236\u771f\u4e2a\u672a\u66fe\u6765\u5230\u5d69\u5c71\uff1f\u201d \u3000\u3000\u674e\u82b8\u9053\uff1a\u201c\u5bb6\u5e08\u786e\u5b9e\u672a\u6765\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u5979\u5ffd\u7136\u795e\u79d8\u5730\u5411\u65b9\u96ea\u5b9c\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u5979\u7684\u884c\u8e2a\u5411\u6765\u98d8\u5ffd\uff0c\u8bf4\u4e0d\u5b9a\uff0c\u773c\u4e0b\u5c31\u5df2\u6765\u5230\uff0c\u4e5f\u672a\u53ef\u77e5\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u674e\u82b8\u9053\uff1a\u201c\u59be\u8eab\u53ea\u662f\u6253\u4e2a\u6bd4\u55bb\u4e86\uff01\u5bb6\u5e08\u884c\u52a8\uff0c\u5411\u6765\u5f88\u5c11\u5bf9\u59be\u8eab\u7b49\u8bf4\u660e\uff0c\u8001\u4eba\u5bb6\u95ee\u6211\uff0c\u6211\u81ea\u7136\u4e0d\u77e5\u9053\u7684\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u6697\u9053\uff1a\u4e0d\u77e5\u9053\uff0c\u5c31\u662f\u4e0d\u77e5\u9053\uff0c\u5e72\u5417\u8981\u515c\u90a3\u4e48\u5927\u7684\u5708\u5b50\u8bb2\u8bdd\u5462\uff1f \u3000\u3000\u4ed6\u5fc3\u4e2d\u5728\u60f3\uff0c\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u6709\u4e00\u4ef6\u4e8b\uff0c\u5728\u4e0b\u60f3\u5411\u4f60\u8bf7\u6559\uff0c\u53ea\u662f\u2026\u2026\u5c1a\u76fc\u59d1\u5a18\u80fd\u7167\u5b9e\u56de\u7b54\uff01\u201d \u3000\u3000\u674e\u82b8\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u554a\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u90a3\u5c11\u6797\u5bfa\u7684\u548c\u5c1a\uff0c\u8fde\u65e5\u6709\u4e0d\u5c11\uff0c\u53d7\u5230\u5f3a\u654c\u622a\u6740\uff0c\u4e0d\u77e5\u662f\u4e0d\u662f\u59d1\u5a18\u6240\u4e3a\uff1f\u201d \u3000\u3000\u674e\u82b8\u9053\uff1a\u201c\u59be\u8eab\u521a\u5230\u6b64\uff0c\u4e0d\u662f\u59d1\u5a18\u6240\u4e3a\u3002\u201d \u3000\u3000\u5979\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u77e5\u4e24\u4f4d\u53ef\u66fe\u5bdf\u770b\u8fc7\u90a3\u4e9b\u50e7\u4fa3\u7684\u4f24\u75d5\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u6447\u5934\u9053\uff1a\u201c\u6ca1\u6709\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u770b\u90a3\u4e9b\u50e7\u4eba\u7684\u4f24\u75d5\u4f5c\u751a\uff1f\u201d \u3000\u3000\u674e\u82b8\u9053\uff1a\u201c\u8ba4\u51fa\u88ab\u4ec0\u4e48\u6240\u4f24\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u597d\u50cf\u662f\u2014\u2014\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u89c9\u5f97\u81ea\u5df1\u8bf4\u9519\u4e86\u822c\uff0c\u662f\u4ee5\u534a\u9014\u4e2d\u505c\u4f4f\u3002 \u3000\u3000\u674e\u82b8\u4e00\u6014\uff0c\u9053\uff1a\u201c\u516c\u5b50\u4e3a\u4f55\u4e0d\u8bf4\u5c06\u51fa\u6765\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fdf\u7591\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u8bf4\u662f\u59d1\u5a18\u521a\u6765\uff0c\u90a3\u4e9b\u5c11\u6797\u50e7\u4eba\uff0c\u5b9a\u7136\u662f\u4e3a\u4ed6\u4eba\u6240\u4f24\u2026\u2026\u201d \u3000\u3000\u4ed6\u672c\u60f3\u5c31\u8fd9\u4e48\u542b\u7cca\u5e94\u4ed8\u8fc7\u53bb\uff0c\u4f46\u4e0d\u6599\u674e\u82b8\u4e24\u773c\u7d27\u7d27\u7684\u76ef\u5728\u4ed6\u8eab\u4e0a\uff0c\u4f7f\u4ed6\u4e0d\u5f97\u4e0d\u5f80\u4e0b\u8bf4\u53bb\u3002 \u3000\u3000\u4ed6\u8bdd\u97f3\u53ea\u662f\u4e00\u987f\uff0c\u4fbf\u81ea\u63a5\u9053\uff1a\u201c\u5728\u4e0b\u542c\u8bf4\uff0c\u90a3\u4e10\u5e2e\u6709\u4e86\u4e00\u4f4d\u5192\u540d\u5e2e\u4e3b\u6765\u6b64\uff0c\u60f3\u5fc5\u662f\u4ed6\u4eec\u52a8\u7684\u624b\u4e86\uff01\u201d \u3000\u3000\u674e\u82b8\u8138\u4e0a\u6709\u7740\u6781\u4e3a\u8be7\u5f02\u7684\u795e\u8272\uff0c\u5979\u76ef\u7740\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u65b9\u516c\u5b50\u4f60\u8fd9\u8bdd\u5f53\u771f\u4e48\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u81ea\u7136\u4e0d\u4f1a\u9a97\u4f60\uff01\u201d \u3000\u3000\u674e\u82b8\u4ef0\u5934\u60f3\u4e86\u4e00\u60f3\uff0c\u5ffd\u7136\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4f60\u89c1\u8fc7\u90a3\u4eba\u662f\u4f55\u7b49\u6a21\u6837\uff1f\u201d \u3000\u3000\u8bf4\u7740\uff0c\u4fbf\u5411\u90a3\u8d1f\u4f24\u7684\u4e09\u5973\u884c\u53bb\u3002 \u3000\u3000\u5b8b\u5b5a\u8fd9\u65f6\u5ffd\u7136\u559d\u9053\uff1a\u201c\u7ad9\u4f4f\uff01\u201d \u3000\u3000\u674e\u82b8\u4e00\u6014\u4e4b\u4e0b\uff0c\u679c\u7136\u7ad9\u4f4f\u4e0d\u52a8\uff0c\u5934\u4e5f\u6ca1\u56de\uff0c\u63a5\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u554a\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u59d1\u5a18\u8981\u53bb\u54ea\u91cc\uff1f\u201d \u3000\u3000\u674e\u82b8\u4ecd\u7136\u672a\u66fe\u53e3\u5934\uff0c\u53ea\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u8fd9\u4e2a\u4e0d\u7528\u4f60\u7ba1\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8001\u592b\u5982\u662f\u4e00\u5b9a\u8981\u95ee\u5462\uff1f\u201d \u3000\u3000\u674e\u82b8\u9053\uff1a\u201c\u6c38\u8fdc\u662f\u4e0d\u5230\u7b54\u590d\uff01\u201d \u3000\u3000\u663e\u7136\uff0c\u4ed6\u4eec\u4e4b\u95f4\u5df2\u7136\u95ee\u50f5\u4e86\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u5251\u7709\u4e00\u626c\uff0c\u5bfb\u601d\u9053\uff1a\u201c\u8fd9\u674e\u82b8\u6025\u4e8e\u79bb\u53bb\uff0c\u83ab\u975e\u662f\u8981\u5c06\u8fd9\u7b49\u4fe1\u606f\uff0c\u544a\u77e5\u5929\u9b54\u5973\u5417\uff1f\u201d \u3000\u3000\u4e00\u5ff5\u53ca\u6b64\uff0c\u6b63\u60f3\u8981\u5b8b\u5b5a\u8ba9\u5979\u79bb\u53bb\uff0c\u7136\u540e\u81ea\u5df1\u4e09\u4eba\uff0c\u4e0d\u59a8\u6697\u4e2d\u8ffd\u8e2a\uff0c\u4fbf\u53ef\u627e\u5230\u4e86\u5929\u9b54\u5973\u9690\u8eab\u4e4b\u6240\u2026\u2026\u201d \u3000\u3000\u8033\u4e2d\u5ffd\u7136\u542c\u5f97\u5b8b\u5b5a\u5927\u58f0\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u65e2\u662f\u4e0d\u613f\u8bf4\uff0c\u90a3\u5c31\u7b97\u4e86\uff0c\u4e0d\u8fc7\u2014\u2014\u8001\u673d\u5c1a\u6709\u4e00\u4e8b\u8bf7\u6559\uff01\u201d \u3000 \u7b2c\u4e8c\u5341\u4e8c\u56de\u3000\u60c5\u6d77\u5174\u6ce2 \u3000 \u3000\u3000\u674e\u82b8\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u5982\u679c\u6211\u4e0d\u77e5\u9053\uff0c\u6055\u59be\u8eab\u4ecd\u7136\u65e0\u53ef\u5949\u7b54\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8fd9\u4e8b\u53ea\u6015\u59d1\u5a18\u4e00\u5b9a\u77e5\u9053\u7684\u4e86\uff01\u201d \u3000\u3000\u674e\u82b8\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4ee4\u5e08\u6d3e\u4eba\u524d\u6765\u5c11\u6797\u9001\u8fc7\u4e00\u6b21\u4fe1\uff0c\u8fd9\u9001\u4fe1\u4e4b\u4eba\uff0c\u53ef\u662f\u59d1\u5a18\u5417\uff1f\u201d \u3000\u3000\u674e\u82b8\u6447\u5934\u9053\uff1a\u201c\u4e0d\u662f\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u4f3c\u662f\u6709\u4e9b\u4e0d\u4fe1\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u662f\u59d1\u5a18\u5417\uff1f\u201d \u3000\u3000\u674e\u82b8\u9053\uff1a\u201c\u90a3\u9001\u4fe1\u4e4b\u4eba\uff0c\u4e43\u662f\u59be\u8eab\u7684\u4e8c\u59ca\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u5979\u7684\u6b66\u529f\u5f88\u9ad8\u554a\uff01\u201d \u3000\u3000\u674e\u82b8\u84e6\u5730\u8f6c\u8eab\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u59be\u8eab\u7684\u4e8c\u5e08\u59ca\u6b66\u529f\uff0c\u6bd4\u90a3\u5c11\u6797\u548c\u5c1a\u9ad8\u51fa\u4e0d\u5c11\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u63d2\u53e3\u9053\uff1a\u201c\u5979\u773c\u4e0b\u4f55\u5728\uff1f\u201d \u3000\u3000\u674e\u82b8\u7709\u76b1\u4e86\u4e00\u76b1\uff0c\u7728\u52a8\u7740\u5927\u773c\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u65b9\u516c\u5b50\u53ef\u662f\u60f3\u89c1\u5979\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8131\u53e3\u9053\uff1a\u201c\u5f88\u60f3\u89c1\u8bc6\u89c1\u8bc6\uff01\u201d \u3000\u3000\u674e\u82b8\u7b11\u9053\uff1a\u201c\u5979\u5f88\u7f8e\u554a\uff0c\u516c\u5b50\u4e0d\u6015\u88ab\u5979\u8ff7\u4f4f\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u4e0d\u7981\u4e00\u6014\uff0c\u5fd6\u9053\uff1a\u201c\u5979\u4e3a\u4f55\u5ffd\u7136\u60f3\u5230\u7f8e\u4e11\u7684\u95ee\u9898\u4e0a\u53bb\u4e86\uff1f\u201d \u3000\u3000\u4f46\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u59d1\u5a18\u53d6\u7b11\u4e86\uff01\u5728\u4e0b\u53ea\u662f\u60f3\u89c1\u8bc6\u89c1\u8bc6\u4f60\u90a3\u4e8c\u59ca\u7684\u6b66\u529f\u800c\u5df2\uff01\u201d \u3000\u3000\u674e\u82b8\u7b11\u9053\uff1a\u201c\u516c\u5b50\uff0c\u8981\u89c1\u8bc6\u5979\u7684\u6b66\u529f\uff0c\u5c11\u4e0d\u5f97\u975e\u8981\u89c1\u5230\u5979\u672c\u4eba\u4e0d\u53ef\uff0c\u59be\u8eab\u90a3\u4e8c\u59ca\uff0c\u5728\u6211\u4eec\u516d\u59b9\u59b9\u5f53\u4e2d\u662f\u51fa\u4e86\u540d\u7684\u7f8e\u4eba\uff0c\u53ea\u8981\u662f\u89c1\u8fc7\u5979\u7684\u7537\u4eba\uff0c\u6ca1\u6709\u4e00\u4e2a\u80fd\u591f\u4e0d\u4e3a\u5979\u52a8\u5fc3\uff0c\u7ec8\u4e8e\u79f0\u81e3\u88d9\u4e0b\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e0d\u7b49\u5979\u591a\u8bf4\uff0c\u51b7\u51b7\u5730\u4e00\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u5982\u6b64\u591a\u5fc3\uff0c\u90a3\u5c31\u4e0d\u7528\u518d\u8bf4\u4e86\uff01\u201d \u3000\u3000\u674e\u82b8\u4f9d\u7136\u7b11\u9053\uff1a\u201c\u516c\u5b50\uff0c\u59be\u8eab\u8bf4\u7684\u4e43\u662f\u5b9e\u8bdd\uff0c\u5c11\u6797\u50e7\u4f17\u90a3\u4e48\u591a\uff0c\u5c45\u7136\u4e0d\u80fd\u5c06\u5979\u6263\u7559\u5728\u5c11\u6797\u5bfa\u4e2d\uff0c\u53ea\u6015\u5149\u662f\u51ed\u4ed7\u6b66\u529f\uff0c\u4e8c\u59d0\u81ea\u5df1\u4e5f\u96be\u514d\u4e0d\u53d7\u4f24\u6b8b\u5427\uff01\u53ef\u89c1\u5176\u4e2d\u5b9a\u6709\u4e0d\u4e3a\u4eba\u77e5\u7684\u7f18\u6545\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u53ef\u662f\u8bf4\u2026\u2026\u90a3\u5c11\u6797\u957f\u8001\u53d7\u4f24\uff0c\u53ea\u56e0\u88ab\u4ed6\u7f8e\u8272\u6240\u60d1\uff1f\u201d \u3000\u3000\u674e\u82b8\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5728\u65c1\u5374\u7b11\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u90a3\u4e9b\u548c\u5c1a\uff0c\u4ff1\u662f\u9ad8\u50e7\uff0c\u600e\u4f1a\u5411\u9053\u4e4b\u5fc3\uff0c\u8fd9\u7b49\u5dee\u52b2\u5462\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u672a\u7b54\u8bdd\uff0c\u674e\u82b8\u5df2\u7136\u7b11\u9053\uff1a\u201c\u516c\u5b50\uff0c\u59be\u8eab\u90a3\u4e8c\u59ca\u4e43\u662f\u4eba\u95f4\u5c24\u7269\uff0c\u4f11\u8bf4\u5c11\u6797\u5bfa\u7684\u548c\u5c1a\uff0c\u5c31\u7b97\u4f5b\u7956\u4e5f\u96be\u8eb2\u4e8c\u59ca\u7684\u8102\u7c89\u5927\u52ab\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u6709\u8fd9\u7b49\u4e8b\uff1f\u6211\u5374\u4e0d\u4fe1\uff01\u201d \u3000\u3000\u674e\u82b8\u9053\uff1a\u201c\u516c\u5b50\u4e0d\u4fe1\uff0c\u4f55\u4e0d\u8ddf\u59be\u8eab\u4e00\u9053\u53bb\u627e\u5979\u5462\uff1f\u201d \u3000\u3000\u5148\u524d\uff0c\u5979\u4f3c\u662f\u6709\u7740\u62d2\u7edd\u65b9\u96ea\u5b9c\u89c1\u5979\u4e8c\u5e08\u59ca\u4e4b\u610f\uff0c\u6b64\u523b\u53c8\u81ea\u52a8\u63d0\u8d77\u8981\u9886\u4ed6\u540c\u53bb\uff0c\u4e0d\u89c9\u4f7f\u5f97\u65b9\u96ea\u5b9c\u5927\u529b\u56f0\u60d1\u3002 \u3000\u3000\u5b8b\u5b5a\u84e6\u5730\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u597d\u554a\uff01\u8001\u673d\u5374\u6709\u4e9b\u7b49\u4e0d\u53ca\u4e86\uff01\u59d1\u5a18\u5feb\u5feb\u5e26\u8def\uff01\u201d \u3000\u3000\u674e\u82b8\u7b11\u9053\uff1a\u201c\u65b9\u516c\u5b50\uff0c\u4f60\u8981\u4e0d\u8981\u53bb\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u6697\u9053\uff1a\u201c\u6211\u5982\u4e0d\u53bb\uff0c\u53ea\u6015\u8981\u88ab\u5979\u803b\u7b11\u4e86\u2026\u2026\u201d \u3000\u3000\u5251\u7709\u4e00\u626c\uff0c\u6b63\u5f85\u7b54\u8bdd\uff0c\u8033\u4e2d\u5ffd\u4f20\u6765\u4e00\u58f0\u51b7\u7b11\uff0c\u4e00\u6761\u4eba\u5f71\u7535\u60ca\u800c\u81f3\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5403\u4e86\u4e00\u60ca\uff0c\u4e3e\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u5b89\u5c0f\u840d\u53cc\u7709\u5e26\u715e\uff0c\u7ad9\u5728\u674e\u82b8\u8eab\u524d\u4e09\u5c3a\u4e0d\u5230\uff0c\u4e0a\u4e0a\u4e0b\u4e0b\uff0c\u5c06\u5979\u6253\u91cf\u4e0d\u4f11\u3002 \u3000\u3000\u5b8b\u5b5a\u7779\u72b6\uff0c\u6697\u9053\uff1a\u201c\u574f\u4e86\uff0c\u8fd9\u4e2b\u5934\u5403\u918b\u7684\u8bdd\uff0c\u4e8b\u60c5\u53ef\u5c31\u9ebb\u70e6\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e5f\u5728\u5fc3\u4e2d\u6697\u81ea\u5600\u5495\uff0c\u4e0d\u77e5\u5b89\u5c0f\u840d\u6253\u91cf\u674e\u82b8\uff0c\u5c06\u8981\u5f04\u51fa\u4ec0\u4e48\u4e8b\u6765\uff01\u4e0d\u5b89\u7684\u62ac\u773c\u5411\u674e\u82b8\u770b\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u770b\uff0c\u53ef\u628a\u65b9\u96ea\u5b9c\u770b\u5f97\u5982\u5760\u4e94\u91cc\u96fe\u4e2d\uff0c\u90a3\u674e\u82b8\u7684\u8138\u4e0a\uff0c\u7adf\u662f\u5145\u6ee1\u4e86\u602a\u5f02\u7684\u795e\u6001\uff0c\u8bf4\u4e0d\u51fa\u662f\u559c\u662f\u60ca\uff0c\u4f46\u6697\u6de1\u7684\u6708\u8272\u4e4b\u4e0b\uff0c\u65b9\u96ea\u5b9c\u53ef\u4ee5\u660e\u5730\u7684\u770b\u51fa\u674e\u82b8\u6839\u672c\u4e0d\u66fe\u5bf9\u5b89\u5c0f\u840d\u6709\u7740\u654c\u610f\u548c\u654c\u610f\u548c\u6212\u4ff1\u3002 \u3000\u3000\u4ed6\u4e0d\u7531\u5730\u53c8\u5446\u4e86\u4e00\u5446\uff0c\u6697\u9053\uff1a\u201c\u5973\u4eba\u7684\u4e8b\uff0c\u5f53\u771f\u662f\u5947\u602a\u7684\u5f88\u554a\uff01\u201d \u3000\u3000\u5bfb\u601d\u672a\u5df2\uff0c\u8033\u53e3\u5ffd\u7136\u542c\u5f97\u674e\u82b8\u5996\u6ef4\u6ef4\u5730\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4f4d\u516c\u5b50\u2026\u2026\u4f60\u8d35\u59d3\u554a\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6ca1\u6709\u56de\u7b54\uff0c\u4e00\u95ea\u8eab\uff0c\u7adf\u7136\u8f6c\u5230\u90a3\u674e\u82b8\u7684\u8eab\u540e\uff0c\u5411\u7740\u5979\u80cc\u5f71\uff0c\u53c8\u4e0a\u4e0a\u4e0b\u4e0b\u6253\u91cf\u4e0d\u5df1\uff01 \u3000\u3000\u5979\u8fd9\u7b49\u884c\u5f84\uff0c\u4e0d\u72ec\u4f7f\u5f97\u65b9\u96ea\u5b9c\u548c\u5b8b\u5b5a\u7591\u8651\u4e0d\u89e3\uff0c\u8fde\u90a3\u674e\u82b8\uff0c\u8fd9\u65f6\u4e5f\u84e6\u7136\u6ee1\u8138\u901a\u7ea2\uff0c\u5ff8\u6029\u4e0d\u5b89\u5730\u8f6c\u8d77\u5a07\u8eaf\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4f60\u2026\u2026\u8fd9\u662f\u4f5c\u4ec0\u4e48\u554a\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u8db3\u8db3\u770b\u4e86\u6709\u534a\u76cf\u70ed\u8336\u4e4b\u4e45\uff0c\u8fd9\u624d\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u6211\u53ea\u9053\u4f60\u662f\u600e\u6837\u7684\u4e00\u4e2a\u7f8e\u4eba\u80da\u5b50\uff0c\u60f3\u4e0d\u5230\u4f60\u4e0d\u8fc7\u662f\u865a\u6709\u5176\u8868\u7684\u4e2b\u5934\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u5fd6\u9053\uff1a\u201c\u679c\u7136\u662f\u4e86\uff01\u5979\u5fc5\u662f\u4e0d\u6ee1\u8fd9\u674e\u59d1\u5a18\u7684\u5996\u5a06\u4e3e\u52a8\u4e86\u2026\u2026\u201d \u3000\u3000\u674e\u82b8\u663e\u7136\u5bf9\u5b89\u5c0f\u840d\u5ffd\u7136\u8bf4\u51fa\u8fd9\u53e5\u8bdd\u6765\uff0c\u5927\u4e3a\u60ca\u8bb6\uff0c\u5148\u524d\u5b89\u5c0f\u840d\u5ffd\u5730\u73b0\u8eab\u800c\u6765\uff0c\u674e\u82b8\u4e00\u773c\u77a7\u5230\u4e86\u5b89\u5c0f\u840d\u4e4b\u9645\uff0c\u82b3\u5fc3\u4e4b\u4e2d\uff0c\u88ab\u5979\u90a3\u82f1\u4fca\u7684\u795e\u8c8c\u6240\u5f15\uff0c\u518d\u7ecf\u5979\u4e00\u9635\u6253\u91cf\uff0c\u66f4\u4f7f\u674e\u82b8\u82b3\u5fc3\u8d77\u4e86\u5947\u5999\u7684\u5e7b\u60f3\uff0c\u53ea\u9053\u4ed6\u4f1a\u606d\u7ef4\u81ea\u5df1\u4e00\u756a\uff0c\u6b63\u5982\u90a3\u4e9b\u62dc\u5012\u5728\u81ea\u5df1\u4e8c\u5e08\u59ca\u88d9\u4e0b\u7684\u7537\u4eba\u4e00\u6837\uff0c\u6447\u5c3e\u4e5e\u601c\uff0c\u9a6f\u5982\u7f94\u7f8a\uff0c\u6b8a\u4e0d\u77e5\u5b89\u5c0f\u840d\u770b\u4e86\u534a\u5929\uff0c\u5230\u5934\u6765\u5374\u662f\u8fde\u8bbd\u5e26\u9a82\u5730\u8bad\u4e86\u81ea\u5df1\u4e00\u987f\uff0c\u674e\u82b8\u81ea\u662f\u7981\u4e0d\u4f4f\u8981\u5927\u5403\u4e00\u60ca\u4e86\u3002 \u3000\u3000\u6b64\u523b\uff0c\u674e\u82b8\u4e0d\u6b62\u5403\u4e86\u4e00\u60ca\uff0c\u82b3\u5fc3\u4e4b\u4e2d\uff0c\u5374\u66f4\u5982\u6253\u7ffb\u4e94\u5473\u6cb9\u575b\u4e00\u822c\uff0c\u9178\u751c\u82e6\u8fa3\uff0c\u4e00\u9f50\u6cfc\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4e24\u53ea\u5927\u773c\u4e4b\u4e2d\uff0c\u9690\u9690\u5730\u73b0\u51fa\u6cea\u5149\uff0c\u4e00\u626d\u5934\uff0c\u4fbf\u987a\u7740\u5c71\u5f84\uff0c\u5411\u5c71\u4e0a\u5954\u53bb\uff01 \u3000\u3000\u5b8b\u5b5a\u4e00\u95ea\u8eab\uff0c\u8ba9\u5f00\u4e86\u53bb\u8def\u3002 \u3000\u3000\u4f46\u674e\u82b8\u521a\u5954\u53ca\u6570\u4e08\uff0c\u5ffd\u7136\u773c\u524d\u4eba\u5f71\u4e00\u82b1\uff0c\u5b89\u5c0f\u840d\u5c45\u7136\u540e\u53d1\u5148\u81f3\uff0c\u62e6\u5728\u674e\u82b8\u8eab\u524d\uff01 \u3000\u3000\u674e\u82b8\u6014\u4e86\u6014\uff0c\u7ad9\u7262\u4e86\u8eab\u5b50\uff0c\u51b7\u51b7\u559d\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4f60\u8fd9\u662f\u5e72\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u95ea\u8eab\u62e6\u5728\u674e\u82b8\u8eab\u524d\uff0c\u51b7\u54fc\u9053\uff1a\u201c\u4e0d\u8bb8\u4f60\u8d70\uff01\u201d \u3000\u3000\u674e\u82b8\u62ac\u773c\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u5fc3\u4e2d\u4e0d\u7531\u53c8\u8f6f\u4e86\u4e0b\u6765\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u516c\u5b50\u2026\u2026\u6709\u4f55\u89c1\u6559\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u53cc\u76ee\u5728\u5979\u8138\u4e0a\u4e00\u8f6c\uff0c\u63a5\u9053\uff1a\u201c\u6211\u4e5f\u6ca1\u6709\u4ec0\u4e48\u89c1\u6559\uff0c\u53ea\u4e0d\u8fc7\u8981\u544a\u8bc9\u4f60\u4e00\u53e5\u8bdd\uff01\u201d \u3000\u3000\u674e\u82b8\u9053\uff1a\u201c\u4ec0\u4e48\u8bdd\u554a\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4ece\u4eca\u4ee5\u540e\uff0c\u4e0d\u8bb8\u4f60\u518d\u89c1\u65b9\u516c\u5b50\uff01\u201d \u3000\u3000\u4ed6\u62e6\u4f4f\u4e86\u674e\u82b8\uff0c\u5374\u539f\u6765\u53ea\u662f\u8981\u544a\u8bc9\u5979\u8fd9\u4e48\u4e00\u53e5\u8bdd\uff0c\u53ea\u628a\u65b9\u96ea\u5b9c\u542c\u5f97\u5fc3\u4e2d\u5927\u5927\u4e00\u9707\uff01 \u3000\u3000\u53ea\u662f\uff0c\u4ed6\u867d\u7136\u89c9\u51fa\u8fd9\u4e2d\u95f4\u610f\u4e49\u4e0d\u540c\u5bfb\u5e38\uff0c\u4f46\u4ed6\u8fd8\u672a\u60f3\u5230\u5b89\u5c0f\u840d\u4e43\u662f\u4e00\u80a1\u918b\u610f\u5728\u4f5c\u602a\uff0c\u53ea\u9053\u5979\u4e43\u662f\u5173\u5fc3\u81ea\u5df1\u53bb\u89c1\u5996\u5973\uff0c\u6216\u8005\u4e3a\u5979\u4eec\u6240\u6697\u7b97\uff0c\u5982\u662f\u4ed6\u660e\u767d\u4e86\u5b89\u5c0f\u840d\u672c\u610f\u6240\u5728\uff0c\u6050\u6015\u65b9\u96ea\u5b9c\u518d\u4e5f\u4e0d\u6562\u4e0e\u5979\u5e38\u5728\u4e00\u8d77\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u53ea\u89c1\u674e\u82b8\u4f3c\u662f\u6123\u4e86\u4e00\u6123\uff0c\u65b9\u59cb\u63a5\u9053\uff1a\u201c\u516c\u5b50\u53eb\u4f4f\u59be\u8eab\uff0c\u53ea\u662f\u4e3a\u4e86\u8fd9\u4e00\u53e5\u8bdd\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u674e\u82b8\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4f60\u8d35\u59d3\u5927\u540d\u554a\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u89c1\u5979\u4e0d\u7b54\u81ea\u5df1\uff0c\u5374\u53cd\u5012\u95ee\u81ea\u5df1\u7684\u59d3\u540d\uff0c\u4e0d\u7981\u5927\u5927\u4e0d\u5feb\uff0c\u6012\u9053\uff1a\u201c\u6211\u7684\u59d3\u540d\u5417\uff1f \u3000\u3000\u4f60\u5e08\u7236\u77e5\u9053\uff01\u201d \u3000\u3000\u674e\u82b8\u95fb\u8a00\uff0c\u4e0d\u7981\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5bb6\u5e08\uff1f\u516c\u5b50\u8ba4\u5f97\u5bb6\u5e08\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4f60\u600e\u4e48\u4e0d\u5148\u56de\u7b54\u6211\u7684\u8bdd\uff1f\u201d \u3000\u3000\u674e\u82b8\u8fdf\u7591\u4e86\u4e00\u4e0b\uff0c\u8f6c\u773c\u770b\u4e86\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u8fd9\u624d\u8f7b\u8f7b\u4e00\u7b11\u9053\uff1a\u201c\u59be\u8eab\u7b54\u5e94\u516c\u5b50\u4e86\uff01\u201d \u3000\u3000\u6562\u60c5\u5979\u53d1\u73b0\u5b89\u5c0f\u840d\u66f4\u6bd4\u65b9\u96ea\u5b9c\u82f1\u4fca\uff0c\u82b3\u5fc3\u4e4b\u4e2d\u5df2\u7ecf\u6697\u6697\u6253\u5b9a\u4e3b\u610f\uff0c\u53ea\u8981\u4ed6\u8ba4\u5f97\u5e08\u7236\uff0c\u81ea\u5df1\u4e0d\u6101\u65e5\u540e\u6ca1\u6709\u63a5\u8fd1\u4ed6\u7684\u673a\u4f1a\uff0c\u662f\u4ee5\uff0c\u66f4\u5f88\u5feb\u5730\u7b54\u5e94\u4e0d\u518d\u4e0e\u65b9\u96ea\u5b9c\u89c1\u9762\uff01 \u3000\u3000\u5b89\u5c0f\u840d\u89c1\u5979\u4e00\u53e3\u7b54\u5e94\uff0c\u5fc3\u4e2d\u4e5f\u5c31\u9ad8\u5174\u4e86\uff01\u5f53\u4e0b\u6325\u624b\u4e00\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u53ef\u4ee5\u8d70\u4e86\uff01\u201d \u3000\u3000\u674e\u82b8\u539f\u60f3\u4ed6\u53ef\u80fd\u4f1a\u518d\u5411\u81ea\u5df1\u8bf4\u51e0\u53e5\u5ba2\u5957\u8bdd\uff0c\u4e0d\u60f3\u4ed6\u8bf4\u51fa\u6d3b\u6765\uff0c\u5374\u662f\u9010\u5ba2\u4e4b\u4ee4\uff0c\u4e00\u6014\u4e4b\u4e0b\uff0c\u674e\u82b8\u51e0\u4e4e\u6c14\u5f97\u60f3\u54ed\u51fa\u58f0\u6765\u3002 \u3000\u3000\u5982\u662f\u6362\u4e86\u522b\u4eba\uff0c\u674e\u82b8\u4e0d\u4f46\u65e9\u5df2\u5f00\u53e3\u76f8\u9a82\uff0c\u751a\u81f3\u65e9\u5df2\u51fa\u624b\uff0c\u53d6\u4e86\u5bf9\u65b9\u7684\u6027\u547d\uff0c\u4f46\u5979\u56e0\u4e3a\u5b89\u5c0f\u840d\u66fe\u8bf4\u8ddf\u5929\u9b54\u5973\u76f8\u8bc6\uff0c\u7eb5\u7136\u81ea\u5df1\u5fc3\u4e2d\u53c8\u6c14\u3001\u53c8\u6068\uff0c\u5374\u4e5f\u4e0d\u6562\u8868\u9732\u51fa\u6765\uff0c\u6697\u6697\u4e00\u54ac\u94f6\u7259\uff0c\u770b\u4e86\u5b89\u5c0f\u840d\u4e00\u773c\uff0c\u626d\u5934\u4fbf\u8d70\u3002 \u3000\u3000\u84e6\u7136\u95f4\u5b8b\u5b5a\u5927\u559d\u4e00\u58f0\u9053\uff1a\u201c\u59d1\u5a18\u6162\u8d70\u2026\u2026\u201d \u3000\u3000\u987b\u77e5\u90a3\u674e\u82b8\u672c\u5c31\u60f3\u8d56\u7740\u4e0d\u8d70\uff0c\u6b64\u523b\u731b\u7136\u542c\u5f97\u5b8b\u5b5a\u559d\u6b62\uff0c\u5012\u6b63\u662f\u6c42\u4e4b\u4e0d\u5f97\uff0c\u53bb\u52bf\u4e00\u987f\uff0c\u8f6c\u8eab\u9053\uff1a\u201c\u8001\u4eba\u5bb6\u6709\u4f55\u6307\u6559\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u5927\u6b65\u8d70\u4e86\u8fc7\u6765\uff0c\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u7b54\u5e94\u8001\u673d\u53bb\u89c1\u4f60\u4e8c\u5e08\u59ca\u7684\u4e8b\uff0c\u600e\u4e48\u529e\uff1f\u8fd8\u53bb\u4e0d\u53bb\uff1f\u201d \u3000\u3000\u674e\u82b8\u779f\u4e86\u5b89\u5c0f\u840d\u4e00\u773c\uff0c\u9053\uff1a\u201c\u8001\u4eba\u5bb6\uff0c\u8fd9\u4f4d\u516c\u5b50\u53ef\u662f\u8ddf\u4f60\u8001\u4e00\u9053\u7684\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b63\u662f\u8ddf\u8001\u673d\u4e00\u9053\u800c\u6765\u3002\u201d \u3000\u3000\u674e\u82b8\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u8fd9\u4f4d\u516c\u5b50\u5e94\u4e0d\u5e94\u5141\u5462\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u56de\u5934\u5411\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u60f3\u4e0d\u60f3\u53bb\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6c89\u541f\u672a\u8bed\uff0c\u65b9\u96ea\u5b9c\u5df2\u63a5\u9053\uff1a\u201c\u5f53\u7136\u8981\u53bb\u554a\uff01\u201d \u3000\u3000\u4ed6\u5982\u679c\u4e0d\u63d2\u53e3\uff0c\u6216\u8bb8\u5b89\u5c0f\u840d\u8fd8\u6b63\u5728\u8003\u8651\uff0c\u4f46\u65b9\u96ea\u5b9c\u8fd9\u4e48\u4e00\u8bf4\uff0c\u53ea\u542c\u5b89\u5c0f\u840d\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u53bb\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6014\u5f97\u4e00\u6014\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4f60\u8981\u53bb\u54ea\u513f\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u8f6c\u8eab\u4fbf\u5411\u5c71\u4e0b\u884c\u53bb\uff0c\u63a5\u9053\uff1a\u201c\u54ea\u513f\u4e5f\u4e0d\u53bb\uff01\u201d \u3000\u3000\u7728\u773c\u4e4b\u95f4\uff0c\u5df2\u53bb\u4e86\u5341\u4e08\uff01 \u3000\u3000\u5b8b\u5b5a\u9002\u624d\u4e5f\u5446\u4e86\u4e00\u5446\uff0c\u8fd9\u65f6\u773c\u89c1\u5b89\u5c0f\u840d\u8d1f\u6c14\u800c\u53bb\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u4e00\u6025\uff0c\u98de\u8eab\u5954\u4e86\u8fc7\u53bb\uff0c\u5927\u58f0\u9053\uff1a\u201c\u5b89\u8001\u5f1f\uff0c\u4f60\u7b49\u4e00\u7b49\u2026\u2026\u201d \u3000\u3000\u4e09\u4e2a\u8d77\u843d\uff0c\u4fbf\u5df2\u8ffd\u4e86\u4e0a\u53bb\uff0c \u3000\u3000\u65b9\u96ea\u5b9c\u53cc\u80a9\u4e00\u6447\uff0c\u5feb\u82e5\u98d8\u98ce\uff0c\u5b8b\u5b5a\u5c1a\u672a\u8d76\u5230\uff0c\u65b9\u96ea\u5b9c\u4e5f\u5df2\u62e6\u5728\u5b89\u5c0f\u840d\u7684\u8eab\u524d\uff0c\u6321\u4f4f\u4e86\u5b89\u5c0f\u840d\u7684\u53bb\u8def\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u76db\u6c14\u4e4b\u4e0b\u4e00\u89c1\u65b9\u96ea\u5b9c\u6321\u4f4f\u53bb\u8def\uff0c\u7ea4\u624b\u4e00\u6325\uff0c\u4fbf\u5411\u65b9\u96ea\u5b9c\u5f53\u80f8\u62cd\u53bb\uff0c\u53e3\u4e2d\u5927\u559d\u4e00\u58f0\u9053\uff1a\u201c\u8ba9\u5f00\u6211\u2026\u2026\u201d \u3000\u3000\u4f46\u95fb\u201c\u549a\u2026\u2026\u201d\u7684\u4e00\u58f0\uff0c\u65b9\u96ea\u5b9c\u5168\u8eab\u4e00\u9707\uff0c\u6643\u4e86\u4e24\u6643\uff0c\u626d\u8f6c\u5934\u53bb\uff0c\u5f20\u5634\u5410\u51fa\u4e00\u53e3\u9c9c\u8840\u3002 \u3000\u3000\u6562\u60c5\u5b89\u5c0f\u840d\u8fd9\u4e00\u638c\uff0c\u4f7f\u7684\u52b2\u9053\u4e0d\u5c0f\uff0c\u7adf\u662f\u5c06\u90a3\u65b9\u96ea\u5b9c\u7684\u5185\u8151\u9707\u4f24\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u76ee\u7779\u6b64\u53d8\uff0c\u4e0d\u7531\u5f97\u5927\u5927\u4e00\u5446\uff0c\u8dfa\u811a\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u8eb2\u554a\u2026\u2026\u65b9\u5144\u2026\u2026\u4f60\u4f24\u5f97\u5f88\u91cd\u5417\uff1f\u201d \u3000\u3000\u4e0a\u524d\u4e00\u628a\u6276\u4f4f\u65b9\u96ea\u5b9c\uff0c\u8fde\u58f0\u6025\u95ee\uff0c\u4e00\u8138\u4ff1\u662f\u60ca\u60f6\u6094\u60dc\u4e4b\u5bb9\u3002 \u3000\u3000\u5b8b\u5b5a\u9002\u5728\u8fd9\u65f6\u8d76\u5230\uff0c\u7779\u72b6\u4e5f\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u65b9\u8001\u5f1f\u600e\u4e48\u4e86\uff1f\u201d \u3000\u3000\u539f\u6765\u90a3\u5b89\u5c0f\u840d\u638c\u4f24\u65b9\u96ea\u5b9c\u4e4b\u65f6\uff0c\u56e0\u662f\u906e\u4f4f\u4e86\u5b8b\u5b5a\u7684\u89c6\u7ebf\uff0c\u6545\u9762\u4ed6\u5b8c\u5168\u672a\u66fe\u53d1\u73b0\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u82e6\u7740\u8138\uff0c\u9053\uff1a\u201c\u6211\u2026\u2026\u6253\u4f24\u4e86\u4ed6\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u6014\u6014\u7684\u9053\uff1a\u201c\u4f60\u600e\u4e48\u6253\u4e86\u4ed6\u5462\uff1f\u5b89\u59d1\u2026\u2026\u8001\u5f1f\uff0c\u8fd9\u5c31\u662f\u4f60\u7684\u4e0d\u5bf9\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u4f4e\u58f0\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u6211\u600e\u77e5\u4ed6\u4e0d\u8eb2\u4e0d\u8ba9\u554a\u2026\u2026\u4f60\u8001\u5feb\u770b\u770b\u561b\uff0c\u4ed6\u8981\u7d27\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u773c\u89c1\u65b9\u96ea\u5b9c\u53cc\u76ee\u7d27\u95ed\uff0c\u5251\u7709\u6df1\u9501\uff0c\u5c79\u7acb\u5f53\u5730\uff0c\u62bf\u7740\u5634\u5507\uff0c\u4e00\u52a8\u4e0d\u52a8\uff0c\u663e\u7136\u81ea\u5df1\u6b63\u5728\u8fd0\u529f\u7597\u4f24\uff0c\u5f53\u4e0b\u4f4e\u58f0\u9053\uff1a\u201c\u4ed6\u6b63\u5728\u8fd0\u529f\u7597\u4f24\uff0c\u8001\u5f1f\u4e0d\u8981\u518d\u6253\u6270\u4ed6\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u76b1\u7709\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u4ed6\u4e0d\u80af\u907f\u8ba9\uff0c\u4e0d\u7136\u6211\u4e5f\u4e0d\u4f1a\u4e0b\u624b\u90a3\u7b49\u91cd\u4e86\u2026\u2026\u5b8b\u8001\uff0c\u7b49\u65b9\u5144\u2026\u2026\u4ed6\u4f24\u52bf\u6cbb\u597d\uff0c\u6211\u966a\u4ed6\u4e00\u9053\u53bb\u89c1\u90a3\u4e2a\u5996\u5973\u4f7f\u4e86\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u957f\u957f\u4e00\u53f9\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u54b3\uff01\u59d1\u5a18\u83ab\u5fd8\u4e86\uff0c\u4ed6\u53ef\u662f\u4e3a\u4e86\u6b66\u6797\u6b63\u4e49\u7684\u5927\u4e8b\u554a\uff0c\u4f60\u2026\u2026 \u3000\u3000\u4ee5\u540e\u5343\u4e07\u522b\u518d\u4f7f\u8fd9\u7b49\u5c0f\u6027\u5b50\u624d\u597d\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u8dfa\u811a\u9053\uff1a\u201c\u600e\u4e48\u4f1a\uff1f\u6211\u6c38\u8fdc\u4e5f\u4e0d\u4f1a\u4e86\u2026\u2026\u201d \u3000\u3000\u53ea\u89c1\u65b9\u96ea\u5b9c\u5ffd\u7136\u7f13\u7f13\u7741\u5f00\u4e24\u773c\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u6211\u8fd9\u4e00\u638c\u53ef\u771f\u4e0d\u8f7b\u54df\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u542c\u5230\u4ed6\u80fd\u591f\u5f00\u53e3\u8bf4\u8bdd\uff0c\u4e0d\u7981\u5927\u559c\uff0c\u5fd9\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u597d\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u70b9\u5934\u9053\uff1a\u201c\u5b8c\u5168\u597d\u4e86\uff01\u6709\u52b3\u8d24\u5f1f\u548c\u5b8b\u8001\u62c5\u5fc3\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u518d\u8fd0\u6c14\u8bd5\u8bd5\uff0c\u8fd9\u7b49\u5185\u8151\u7684\u4f24\u52bf\uff0c\u53ef\u4e0d\u662f\u513f\u620f\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u665a\u8f88\u9002\u65f6\u5c06\u90a3\u53e3\u6de4\u8840\u5410\u4e86\u51fa\u6765\uff0c\u773c\u4e0b\u5df2\u662f\u4e0d\u788d\u4e8b\u4e86\uff01\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u5bf9\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u54b1\u4eec\u8ddf\u90a3\u674e\u59d1\u5a18\u8d70\u4e00\u8d9f\u5982\u4f55\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u4f4e\u4e0b\u4e86\u5934\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u2026\u2026\u5f53\u771f\u4e0d\u8981\u7d27\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u8fd9\u4e00\u70b9\u5c0f\u4f24\uff0c\u5c31\u7b97\u4e0d\u66fe\u75ca\u6108\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u5173\u7cfb\uff0c\u8d24\u5f1f\u4e0d\u5fc5\u6302\u5728\u5fc3\u4e0a\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6ceb\u7136\u63a5\u9053\uff1a\u201c\u65b9\u5144\u2026\u2026\u8fd9\u90fd\u662f\u6211\u4e0d\u597d\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e0d\u5bb9\u5979\u518d\u5f80\u4e0b\u8bf4\uff0c\u62c9\u8d77\u5979\u53f3\u624b\uff0c\u7b11\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u54b1\u4eec\u8fd9\u5c31\u8d70\uff0c\u53ef\u597d\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u662f\u554a\uff0c\u674e\u59d1\u5a18\u8fd8\u5728\u7b49\u7740\u54b1\u4eec\u5462\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u53ea\u89c9\u4e00\u80a1\u70ed\u6d41\uff0c\u6253\u4ed6\u624b\u4e0a\u4f20\u904d\u5168\u8eab\uff0c\u82b3\u5fc3\u4e00\u9707\uff0c\u4e0d\u7981\u7f9e\u5f97\u7c89\u9888\u4f4e\u5782\uff0c\u5583\u5583\u81ea\u8bed\u822c\u8bf4\u9053\uff1a\u201c\u65b9\u2026\u2026\u5144\u4f60\u653e\u5f00\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u62c9\u8d77\u5979\u7ea4\u7ea4\u7389\u624b\uff0c\u672c\u6765\u5012\u4e0d\u4f1a\u89c9\u5f97\u6709\u4ec0\u4e48\u611f\u89c9\uff0c\u4f46\u6b64\u523b\u8033\u4e2d\u542c\u5f97\u5b89\u5c0f\u840d\u8fd9\u822c\u53d1\u6296\u7684\u8bed\u97f3\uff0c\u987f\u65f6\u5fc3\u4e2d\u4e3a\u4e4b\u4e00\u8361\u3002\u53ea\u89c9\u90a3\u5b89\u5c0f\u840d\u7684\u67d4\u8351\uff0c\u5b9b\u5982\u4e00\u56e2\u706b\u822c\uff0c\u7099\u5f97\u4ed6\u8eab\u53d1\u70eb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u60ca\u4e4b\u4e0b\uff0c\u8fde\u5fd9\u677e\u624b\uff0c\u8baa\u8baa\u5730\u63a5\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4f60\u613f\u610f\u53bb\u4e86\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5077\u5077\u5730\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u54fc\u9053\uff1a\u201c\u5c0f\u5f1f\u4e0d\u6562\u4e0d\u53bb\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u76ee\u7779\u4e24\u4eba\u8138\u4e0a\u60c5\u666f\uff0c\u672c\u60f3\u6253\u8da3\u51e0\u53e5\uff0c\u4f46\u53c8\u6015\u7f9e\u4e86\u5b89\u5c0f\u840d\uff0c\u518d\u4f7f\u8d77\u6027\u5b50\uff0c\u53ef\u5c31\u8bef\u4e86\u5927\u4e8b\u4e86\uff01\u5f53\u4e0b\u5e72\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u8001\u5f1f\uff0c\u90a3\u674e\u59d1\u5a18\u8fd8\u5728\u7b49\u7740\u6211\u4eec\u5462\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7ea2\u7740\u8138\u4e00\u7b11\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u8bf7\u2026\u2026\u201d \u3000\u3000\u8bed\u97f3\u672a\u5df2\uff0c\u5df2\u7ecf\u4e3e\u6b65\u5411\u90a3\u674e\u82b8\u884c\u53bb\u3002 \u3000\u3000\u90a3\u674e\u82b8\u89c1\u4ed6\u4eec\u8d70\u6765\uff0c\u5ae3\u7136\u4e00\u7b11\u9053\uff1a\u201c\u59be\u8eab\u4e3a\u4e09\u4f4d\u5e26\u8def\uff01\u201d\u5f53\u5148\u5411\u5c71\u4e0a\u8d70\u53bb\u3002 \u3000\u3000\u4e00\u884c\u56db\u4eba\u8d70\u4e86\u4e0d\u8db3\u5341\u4e08\uff0c\u5b8b\u5b5a\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u674e\u59d1\u5a18\uff0c\u4f60\u5fd8\u4e86\u4e00\u6869\u5927\u4e8b\uff01\u201d \u3000\u3000\u674e\u82b8\u4e00\u6014\u9053\uff1a\u201c\u4ec0\u4e48\u5927\u4e8b\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4f60\u90a3\u4e09\u4f4d\u5c5e\u4e0b\uff0c\u4e0d\u8981\u5979\u4eec\u4e00\u9053\u8d70\u5417\uff1f\u201d \u3000\u3000\u674e\u82b8\u95fb\u8a00\u7b11\u9053\uff1a\u201c\u8001\u4eba\u5bb6\uff0c\u4e0d\u9700\u7ba1\u5979\u4eec\u4e86\uff0c\u672c\u5e2e\u7a0d\u65f6\u81ea\u4f1a\u6709\u4eba\u524d\u6765\u63a5\u5e94\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u4e0d\u7981\u6b62\u6b65\uff0c\u56de\u987e\u4e86\u56db\u4e0b\u91cc\u4e00\u773c\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u2026\u2026\u8d35\u5e2e\u5728\u8fd9\u5d69\u5c71\u6709\u591a\u5c11\u4eba\uff1f\u201d \u3000\u3000\u674e\u82b8\u5e76\u672a\u6b62\u6b65\uff0c\u53e3\u4e2d\u63a5\u9053\uff1a\u201c\u4e0d\u591a\uff0c\u5927\u7ea6\u6709\u4e24\u767e\u591a\u4eba\u5de6\u53f3\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u95fb\u8a00\uff0c\u4e0d\u7981\u5446\u4e86\u4e00\u5446\uff01\u5927\u6b65\u8d76\u4e86\u4e0a\u6765\uff0c\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u2026\u2026\u65b9\u624d\u4e0d\u662f\u8bf4\uff0c\u8d35\u5e2e\u6ca1\u6709\u591a\u5c11\u4eba\u6765\u6b64\u5417\uff1f\u600e\u4e48\u8fd9\u4e00\u4f1a\u513f\u65f6\u95f4\uff0c\u53c8\u6709\u4e86\u4e24\u767e\u591a\u4eba\u4e86\uff1f\u201d \u3000\u3000\u674e\u82b8\u7b11\u9053\uff1a\u201c\u8001\u4eba\u5bb6\uff0c\u5979\u4eec\u4eba\u6570\u867d\u591a\uff0c\u4f46\u90fd\u9690\u5728\u6697\u5904\uff0c\u6ca1\u6709\u59be\u8eab\u547d\u4ee4\uff0c\u5979\u4eec\u4e0d\u4f1a\u51fa\u9762\u73b0\u8eab\uff0c\u5982\u662f\u8bf4\u5979\u4eec\u4e0d\u4f1a\u6765\u6b64\uff0c\u90a3\u4e5f\u6ca1\u6709\u4ec0\u4e48\u4e0d\u5bf9\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u5f3a\u8f9e\u593a\u7406\u5417\uff1f\u201d \u3000\u3000\u4f46\u5b8b\u5b5a\u5374\u672a\u53cd\u9a73\uff0c\u53ea\u662f\u5475\u5475\u4e00\u7b11\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\u7684\u5417\uff1f\u8fd9\u5c31\u96be\u602a\u4e86\uff01\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u53ef\u662f\u5df2\u7ecf\u901a\u77e5\u5979\u4eec\u73b0\u8eab\u53bb\u6551\u90a3\u4e09\u540d\u5c5e\u4e0b\u4e86\uff1f\u201d \u3000\u3000\u674e\u82b8\u9053\uff1a\u201c\u901a\u77e5\u8fc7\u4e86\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u56db\u4eba\u5df2\u7136\u8d70\u4e86\u4e09\u56db\u91cc\u4e4b\u8fdc\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e3e\u76ee\u5411\u4e0a\u671b\u53bb\uff0c\u53ea\u89c1\u79bb\u90a3\u5c11\u5ba4\u7edd\u9876\u4e5f\u5df2\u4e0d\u8fdc\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u96be\u9053\u5979\u7684\u4e8c\u5e08\u59ca\u4f1a\u4f4f\u5728\u5c11\u5ba4\u5cf0\u9876\u5417\uff1f\u201d \u3000\u3000\u8033\u4e2d\u53ea\u542c\u5f97\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u674e\u59d1\u5a18\uff0c\u8fd8\u6709\u591a\u8fdc\uff1f\u201d \u3000\u3000\u674e\u82b8\u7b11\u9053\uff1a\u201c\u4e0d\u8fdc\u4e86\uff0c\u59be\u8eab\u4e8c\u59ca\u5c31\u4f4f\u5728\u90a3\u5c11\u5ba4\u5cf0\u7edd\u9762\u7684\u65c1\u8fb9\u3002\u201d \u3000\u3000\u5ffd\u7136\u653e\u5feb\u4e86\u811a\u6b65\uff0c\u5411\u4e0a\u5954\u53bb\u3002 \u3000\u3000\u51e0\u4eba\u8fd9\u4e00\u811a\u4e0b\u7528\u52b2\uff0c\u51e0\u91cc\u5c71\u8def\uff0c\u4e5f\u4e0d\u8fc7\u7728\u7728\u773c\u7684\u65f6\u5149\uff0c\u4fbf\u5df1\u62b5\u8fbe\u3002 \u3000\u3000\u674e\u82b8\u7ad9\u5728\u4e00\u5757\u5de8\u77f3\u4e4b\u65c1\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5230\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u6253\u91cf\u4e86\u4e00\u4e0b\uff0c\u53ea\u89c1\u81ea\u5df1\u5b58\u8eab\u4e4b\u5904\uff0c\u79bb\u90a3\u5c11\u5ba4\u7edd\u9876\uff0c\u5df2\u7136\u4e0d\u8db3\u4e00\u7bad\u4e4b\u9065\uff0c\u5c71\u52bf\u751a\u662f\u9661\u5ced\uff0c\u56db\u671b\u4e5f\u4e0d\u89c1\u4ec0\u4e48\u623f\u820d\u7bf7\u5e10\uff0c\u53ef\u8d44\u4f4f\u4eba\uff0c\u4e0d\u7981\u8be7\u5f02\u5730\u95ee\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u90a3\u4e8c\u5e08\u59ca\u4f4f\u5728\u54ea\u91cc\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u3001\u5b89\u5c0f\u840d\u4e5f\u6709\u7740\u548c\u5b8b\u5b5a\u4e00\u822c\u7684\u60f3\u6cd5\uff0c\u8fd9\u65f6\u95fb\u8a00\u5168\u90fd\u7741\u5927\u4e86\u773c\uff0c\u77a7\u7740\u674e\u82b8\uff0c\u7b49\u5979\u56de\u7b54\u3002 \u3000\u3000\u674e\u82b8\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c31\u5728\u8fd9\u91cc\uff01\u201d\u987a\u624b\u5411\u90a3\u8eab\u8fb9\u7684\u5927\u77f3\uff0c\u6307\u4e86\u4e00\u6307\u3002 \u3000\u3000\u5b8b\u5b5a\u6014\u6014\u5730\u9053\uff1a\u201c\u5de8\u77f3\u4e4b\u4e0b\u5417\uff1f\u201d \u3000\u3000\u674e\u82b8\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8001\u4eba\u5bb6\u83ab\u5c0f\u89d1\u4e86\u8fd9\u5757\u5de8\u77f3\uff0c\u91cc\u9762\u7684\u5730\u65b9\u53ef\u5927\u7740\u5462\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u4e3e\u624b\u4fbf\u5411\u77f3\u4e0a\u6309\u53bb\uff01 \u3000\u3000\u53ea\u542c\u89c1\u4e00\u9635\u8f67\u8f67\u4e4b\u58f0\u54cd\u8fc7\uff0c\u90a3\u5de8\u77f3\u5e95\u5c42\uff0c\u5ffd\u7136\u9732\u51fa\u4e86\u4e00\u4e2a\u53ef\u5bb9\u4e00\u4eba\u8fdb\u51fa\u7684\u6d1e\u95e8\u6765\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u9053\uff1a\u201c\u8fd9\u5012\u9887\u4f3c\u90a3\u5deb\u5c71\u77f3\u5e9c\u4e86\uff0c\u53ea\u4e0d\u77e5\u5185\u91cc\u7684\u4e00\u5207\u5982\u4f55\u3002\u201d \u3000\u3000\u601d\u5ff5\u4e4b\u95f4\uff0c\u4e0d\u7531\u5f97\u5411\u5b89\u5c0f\u840d\u770b\u4e86\u4e00\u773c\u3002 \u3000\u3000\u90a3\u5b89\u5c0f\u840d\u4f3c\u662f\u4e0e\u4ed6\u6709\u7740\u540c\u6837\u7684\u60f3\u6cd5\uff0c\u4e00\u89c1\u5230\u674e\u82b8\u4e3e\u624b\u5411\u77f3\u4e0a\u6309\u53bb\uff0c\u4fbf\u77aa\u5927\u773c\u775b\uff0c\u77a7\u7740\u674e\u82b8\u7684\u4e3e\u52a8\uff0c\u5bb9\u5f97\u90a3\u5de8\u77f3\u4e4b\u4e0b\u7684\u5c0f\u95e8\u9732\u51fa\uff0c\u5979\u4e0d\u7981\u4e5f\u5fae\u7b11\u7740\u5411\u65b9\u96ea\u5b9c\u671b\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4e24\u4eba\u76f8\u89c6\u4e00\u7b11\uff0c\u867d\u5728\u6726\u80e7\u6708\u8272\u4e4b\u4e0b\uff0c\u4f9d\u7136\u53ef\u4ee5\u77a7\u51fa\uff0c\u90a3\u5b89\u5c0f\u840d\u7c89\u8138\u4e00\u7ea2\uff0c\u8fc5\u5feb\u5730\u4f4e\u4e0b\u5934\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u53ea\u542c\u5f97\u4e00\u9635\u788e\u6b65\u4f20\u6765\uff0c\u6253\u90a3\u5c0f\u6d1e\u4e4b\u5185\uff0c\u9732\u51fa\u4e00\u4e2a\u5934\u6284\u53cc\u8b6c\u7684\u5c11\u5973\u8138\u5b54\uff0c\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u53ef\u662f\u56db\u59d1\u5a18\u5417\uff1f\u201d \u3000\u3000\u674e\u82b8\u9053\uff1a\u201c\u662f\u5c0f\u7fe0\u5417\uff1f\u4f60\u5bb6\u59d1\u5a18\u5728\u5417\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u63a5\u9053\uff1a\u201c\u59d1\u5a18\u521a\u521a\u56de\u6765\u2026\u2026\u201d \u3000\u3000\u5979\u5ffd\u7136\u53d1\u73b0\u4e86\u65b9\u96ea\u5b9c\u7b49\u4e09\u4eba\uff0c\u4e0d\u7981\u4e00\u6014\uff0c\u9053\uff1a\u201c\u56db\u59d1\u5a18\uff0c\u4ed6\u4eec\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u674e\u82b8\u7b11\u9053\uff1a\u201c\u90fd\u662f\u6211\u7684\u670b\u53cb\uff0c\u4ed6\u4eec\u8981\u89c1\u4f60\u5bb6\u59d1\u5a18\uff0c\u5c0f\u7fe0\uff0c\u5feb\u5feb\u53bb\u7980\u544a\u4e8c\u59d1\u5a18\u4e00\u58f0\uff0c\u83ab\u53eb\u4eba\u5bb6\u5728\u5916\u9762\u7ad9\u5f97\u817f\u9178\uff01\u201d \u3000\u3000\u90a3\u5c0f\u7fe0\u5a07\u7b11\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u56db\u59d1\u5a18\uff0c\u4f60\u522b\u53d1\u6025\uff0c\u5c0f\u7fe0\u8fd9\u5c31\u53bb\u7ed9\u4f60\u901a\u7980\u2026\u2026\u201d\u4e00\u626d\u5934\uff0c\u4fbf\u7f29\u5165\u4e86\u6d1e\u91cc\u3002 \u3000\u3000\u5b8b\u5b5a\u77a7\u5f97\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4e0d\u77e5\u4f60\u90a3\u4e8c\u5e08\u59ca\u7684\u59d3\u540d\u53ef\u5426\u5148\u884c\u89c1\u544a\uff1f\u201d \u3000\u3000\u674e\u82b8\u9053\uff1a\u201c\u5f53\u7136\u53ef\u4ee5\u4e86\uff01\u201d \u3000\u3000\u770b\u4e86\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u63a5\u9053\uff1a\u201c\u59be\u8eab\u7684\u4e8c\u5e08\u59ca\u53eb\u5c39\u68a6\u8377\uff0c\u6b66\u6797\u4e2d\u6709\u4e9b\u597d\u4e8b\u7684\u4eba\uff0c\u7ed9\u5979\u53d6\u4e86\u4e2a\u5916\u53f7\uff0c\u53eb\u505a\u8377\u82b1\u4ed9\u59d1\uff0c\u5176\u5b9e\uff0c\u4e8c\u5e08\u59ca\u81ea\u5df1\u5012\u5e76\u4e0d\u559c\u6b22\u8fd9\u4e2a\u540d\u53f7\u5462\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8377\u82b1\u4ed9\u59d1\u5417\uff1f\u8fd9\u540d\u513f\u5012\u662f\u633a\u5e72\u51c0\u7684\u2026\u2026\u201d\u4e0d\u7531\u5f97\u53d1\u51fa\u4e00\u9635\u5475\u5475\u5927\u7b11\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u773c\u89c1\u5b8b\u5b5a\u5ffd\u7136\u5927\u7b11\uff0c\u751a\u4e3a\u5947\u602a\uff0c\u7b11\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8fd9\u7b49\u9ad8\u5174\uff1f\u8377\u82b1\u4ed9\u59d1\u56db\u4e2a\u5b57\uff0c\u665a\u8f88\u5012\u662f\u89c9\u5f97\u633a\u597d\u561b\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u95fb\u8a00\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u4f46\u4ed6\u53c8\u4e0d\u4fbf\u8bf4\u660e\u81ea\u5df1\u53d1\u7b11\u7684\u539f\u56e0\uff0c\u53ea\u80fd\u8baa\u8baa\u5730\u63a5\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u8001\u673d\u53ea\u662f\u5ffd\u7136\u89c9\u5f97\u53ef\u7b11\uff0c\u5c31\u4e0d\u7531\u81ea\u4e3b\u5730\u7b11\u8d77\u6765\u4e86\u2026\u2026\u201d \u3000\u3000\u674e\u82b8\u3001\u65b9\u96ea\u5b9c\u867d\u542c\u51fa\u4e86\u5b8b\u5b5a\u53d1\u7b11\u7684\u539f\u56e0\uff0c\u4f46\u65b9\u96ea\u5b9c\u662f\u788d\u4e8e\u5b89\u5c0f\u840d\u4e43\u662f\u5973\u513f\u4e4b\u8eab\uff0c\u50cf\u8fd9\u7b49\u8bbd\u7b11\u90a3\u5c39\u68a6\u8377\u540d\u53f7\u5e72\u51c0\uff0c\u5b9e\u9645\u4e0a\u4e3a\u4eba\u5018\u5982\u674e\u82b8\u5148\u524d\u6240\u8a00\uff0c\u5219\u5b9a\u7136\u5341\u5206\u6deb\u8361\u4e4b\u8f88\uff0c\u4ed6\u81ea\u662f\u4e0d\u597d\u8bf4\u660e\u3002 \u3000\u3000\u674e\u82b8\u5219\u662f\u4e00\u5fc3\u60f3\u5df4\u7ed3\u5b89\u5c0f\u840d\uff0c\u4ed8\u4ed6\u7684\u6b22\u5fc3\uff0c\u5979\u81ea\u7136\u66f4\u4e0d\u80af\u8bf4\u51fa\u6765\u4e86\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u89c1\u5b8b\u5b5a\u8fd9\u4e48\u56de\u7b54\uff0c\u5012\u4e5f\u672a\u518d\u6df1\u7a76\uff0c\u53ea\u662f\u4f4e\u58f0\u95ee\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u90a3\u5c39\u68a6\u8377\u5982\u679c\u662f\u4e2a\u5996\u5973\u4eba\uff0c\u4f60\u4f1a\u4e0d\u4f1a\u6740\u4e86\u5979\uff1f\u201d \u3000\u3000\u5728\u4eba\u5bb6\u95e8\u53e3\u5c45\u7136\u8bf4\u51fa\u8fd9\u7b49\u8bdd\u6765\uff0c\u53eb\u65b9\u96ea\u5b9c\u53ea\u542c\u5f97\u6014\u4e86\u4e00\u6014\uff0c\u4e0d\u77e5\u5982\u4f55\u4f5c\u7b54\uff01 \u3000\u3000\u674e\u82b8\u4e5f\u5446\u4e86\u4e00\u5446\uff0c\u770b\u4e86\u5b89\u5c0f\u840d\u4e00\u773c\uff0c\u82b3\u5fc3\u4e4b\u4e2d\uff0c\u4e0d\u4f4f\u6026\u6026\u9707\u52a8\uff01 \u3000\u3000\u5979\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u8fd9\u4f4d\u5b89\u516c\u5b50\u600e\u5730\u8fd9\u7b49\u53e3\u6ca1\u906e\u62e6\u5462\uff1f\u8fd9\u8bdd\u5018\u662f\u88ab\u4e8c\u5e08\u59ca\u542c\u4ee5\uff0c\u90a3\u8fd8\u4e86\u5f97\u2026\u2026\u201d \u3000\u3000\u5979\u5bfb\u601d\u672a\u5df2\uff0c\u5ffd\u7136\u542c\u5f97\u90a3\u77f3\u6d1e\u4e4b\u5185\uff0c\u4f20\u51fa\u4e00\u9635\u5a49\u5982\u9ec4\u83ba\u51fa\u8c37\u4e00\u822c\u7684\u683c\u683c\u7b11\u58f0\uff0c\u5927\u60ca\u4e4b\u4e0b\uff0c\u5374\u53c8\u542c\u5f97\u4e00\u4e2a\u60a6\u8033\u7684\u58f0\u97f3\u53d1\u8bdd\u9053\uff1a\u201c\u82b8\u59b9\u59b9\uff0c\u4f60\u7684\u670b\u53cb\u662f\u4e9b\u4ec0\u4e48\u4eba\u554a\uff1f\u8bf7\u6055\u611a\u59ca\u6765\u8fdf\u4e00\u6b65\uff0c\u5343\u4e07\u83ab\u8981\u89c1\u7f6a\uff01\u5feb\u5feb\u8fdb\u6765\u8bf4\u8bdd\u5427\uff01\u201d \u3000\u3000\u674e\u82b8\u5fc3\u4e2d\u6026\u6026\u4e71\u8df3\uff0c\u5fd9\u9053\uff1a\u201c\u4e8c\u59ca\uff0c\u5c0f\u59b9\u524d\u6765\u6253\u6270\uff0c\u4f60\u4e0d\u89c1\u602a\u5c31\u597d\u4e86\u2026\u2026\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u4e5f\u5df1\u4fef\u8eab\u5411\u6d1e\u5185\u884c\u53bb\uff0c\u53e3\u4e2d\u63a5\u9053\uff1a\u201c\u5b8b\u8001\u548c\u4e24\u4f4d\u516c\u5b50\uff0c\u5feb\u968f\u59be\u8eab\u5165\u5185\uff01\u201d \u3000\u3000\u5979\u6b64\u523b\u4e0d\u77e5\u9053\u5c39\u68a6\u8377\u662f\u5426\u5df2\u7ecf\u542c\u5230\u5b89\u5c0f\u840d\u6240\u95ee\u7684\u90a3\u4e00\u53e5\u6d3b\uff0c\u4f46\u8868\u9762\u4e0a\uff0c\u90fd\u662f\u88c5\u5f97\u5f88\u9547\u5b9a\u3002 \u3000\u3000\u5b8b\u5b5a\u3001\u5b89\u5c0f\u840d\u3001\u65b9\u96ea\u5b9c\u9c7c\u8d2f\u968f\u5728\u90a3\u674e\u82b8\u8eab\u540e\uff0c\u5411\u6d1e\u5185\u884c\u53bb\u3002 \u3000\u3000\u8fd9\u77f3\u6d1e\u5165\u53e3\u4e4b\u5904\u867d\u5c0f\uff0c\u4f46\u4e0b\u53bb\u4e08\u8bb8\uff0c\u5c31\u8c41\u7136\u5f00\u6717\uff0c\u4e00\u6761\u957f\u957f\u7684\u77f3\u68af\uff0c\u76f4\u5f80\u5730\u4e0b\u6df1\u5165\uff0c\u4e00\u76f4\u8d70\u4e86\u7ea6\u6709\u5341\u4e08\uff0c\u65b9\u59cb\u73b0\u51fa\u4e00\u95f4\u77f3\u5ba4\u3002 \u3000\u3000\u6d1e\u5185\u56e0\u662f\u60ac\u4e86\u660e\u73e0\uff0c\u662f\u4ee5\u5012\u4e5f\u5341\u5206\u5149\u4eae\u3002 \u3000\u3000\u56db\u4eba\u8d70\u5230\u90a3\u77f3\u5ba4\u95e8\u524d\uff0c\u5374\u89c1\u4e00\u4f4d\u8eab\u7740\u6de1\u84dd\u7f57\u886b\uff0c\u957f\u53d1\u62ab\u80a9\uff0c\u6c14\u8d28\u9ad8\u96c5\uff0c\u5bb9\u8c8c\u51fa\u843d\u5f97\u5b9b\u5982\u8299\u84c9\u822c\u7684\u5c11\u5973\uff0c\u4fcf\u751f\u751f\u5730\u7acb\u5728\u95e8\u8fb9\uff0c\u671b\u7740\u56db\u4eba\uff0c\u542f\u5507\u5fae\u7b11\u3002 \u3000\u3000\u674e\u82b8\u83b2\u6b65\u8f7b\u79fb\uff0c\u8d8b\u524d\u798f\u4e86\u4e00\u798f\uff0c\u9053\uff1a\u201c\u5c0f\u59b9\u89c1\u8fc7\u4e8c\u5e08\u59ca\uff01\u201d \u3000\u3000\u539f\u6765\u8fd9\u84dd\u886b\u5c11\u5973\uff0c\u4fbf\u662f\u5c39\u68a6\u8377\u3002 \u3000\u3000\u5c39\u68a6\u8377\u5a07\u7b11\u989d\u9996\uff0c\u7b11\u9053\uff1a\u201c\u56db\u59b9\u4e0d\u7528\u591a\u793c\uff0c\u5feb\u5feb\u8bf7\u4e09\u4f4d\u4f73\u5bbe\u8fdb\u6765\u8bf4\u8bdd\uff01\u201d \u3000\u3000\u5a07\u8eaf\u8f7b\u79fb\uff0c\u95ea\u8eab\u8083\u5ba2\u3002 \u3000\u3000\u5b8b\u5b5a\u4e00\u4ef0\u5934\uff0c\u5927\u6b65\u8d70\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u968f\u5728\u65b9\u96ea\u5b9c\u8eab\u65c1\uff0c\u6602\u7136\u800c\u884c\uff0c\u5165\u5f97\u5ba4\u5185\uff0c\u53ea\u89c1\u9648\u8bbe\u7b80\u5355\uff0c\u53ea\u6709\u4e00\u5f20\u77f3\u684c\uff0c\u56db\u5f20\u77f3\u51f3\uff0c\u56db\u58c1\u8427\u7136\uff0c\u8fde\u4e2a\u8336\u51e0\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u4f46\u9760\u91cc\u9762\u7684\u77f3\u58c1\u4e4b\u5904\uff0c\u5374\u6709\u4e00\u5f20\u77f3\u69bb\uff0c\u60f3\u5fc5\u4fbf\u662f\u7528\u6765\u5b89\u606f\u4e4b\u5904\u3002 \u3000\u3000\u56db\u4eba\u521a\u5165\u5185\uff0c\u4fbf\u89c1\u90a3\u5c0f\u7fe0\u59d1\u5a18\u6253\u53f3\u624b\u7684\u4e00\u9053\u5c0f\u95e8\u4e2d\u6367\u6765\u4e86\u56db\u76cf\u6e05\u8336\uff0c\u653e\u5728\u684c\u4e0a\uff01 \u3000\u3000\u5c39\u68a6\u8377\u67f3\u8170\u6b3e\u6446\uff0c\u81ea\u884c\u8d70\u5230\u90a3\u69bb\u4e4b\u524d\uff0c\u8fd9\u624d\u5411\u674e\u82b8\u7b11\u9053\uff1a\u201c\u82b8\u59b9\u59b9\uff0c\u5feb\u8bf7\u51e0\u4f4d\u9ad8\u670b\u5165\u5ea7\u7528\u8336\u3002\u201d \u3000\u3000\u674e\u82b8\u5e94\u4e86\u58f0\u662f\uff0c\u4fbf\u8ba9\u4e09\u4eba\u5750\u4e0b\u3002 \u3000\u3000\u5c39\u68a6\u8377\u8fd9\u65f6\u4e5f\u76d8\u819d\u5750\u5230\u77f3\u69bb\u4e4b\u4e0a\uff0c\u661f\u76ee\u4e00\u8f6c\uff0c\u7b11\u9053\uff1a\u201c\u82b8\u59b9\u59b9\uff0c\u4f60\u4e0d\u7ed9\u611a\u59ca\u4ecb\u7ecd\u4e00\u4e0b\u5417\uff1f\u201d \u3000\u3000\u674e\u82b8\u4f3c\u662f\u5bf9\u5979\u5341\u5206\u754f\u60e7\uff0c\u662f\u6545\u4e3e\u63aa\u4e4b\u95f4\uff0c\u90fd\u6709\u4e9b\u513f\u62d8\u4fc3\uff0c\u95fb\u8a00\u7b11\u9053\uff1a\u201c\u662f\u554a\uff0c\u5c0f\u59b9\u8be5\u6b7b\u2026\u2026\u201d \u3000\u3000\u5f53\u4e0b\u4fbf\u4e3a\u56db\u4eba\u5f7c\u6b64\u4ecb\u7ecd\u4e86\u4e00\u756a\u3002 \u3000\u3000\u5c39\u68a6\u8377\u6d45\u6d45\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e45\u4ef0\u4e86\uff01\u59be\u8eab\u5728\u8fd9\u91cc\u53ea\u662f\u501f\u4f4f\u51e0\u5929\uff0c\u7b80\u964b\u5f97\u5f88\uff0c\u4e09\u4f4d\u53ef\u83ab\u8981\u89c1\u7b11\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u54ea\u91cc\uff0c\u54ea\u91cc\uff0c\u59d1\u5a18\u597d\u8bf4\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8d81\u7740\u6b64\u65f6\uff0c\u624d\u4ed4\u7ec6\u7684\u6253\u91cf\u4e86\u8fd9\u4f4d\u5c39\u68a6\u8377\u4e00\u773c\uff0c\u679c\u7136\u89c9\u51fa\u5979\u4e0e\u90a3\u674e\u82b8\u5927\u5927\u4e0d\u540c\u3002 \u3000\u3000\u53ea\u89c1\u5979\u8584\u65bd\u8102\u7c89\uff0c\u6de1\u626b\u86fe\u7709\uff0c\u957f\u53d1\u5782\u843d\u81c2\u9645\uff0c\u5374\u5728\u53f3\u9b13\u4e4b\u4e0a\uff0c\u7c2a\u4e86\u4e00\u6735\u7d20\u81f4\u7684\u5c0f\u767d\u82b1\uff0c\u886c\u6258\u5f97\u5979\u90a3\u4e00\u5f20\u5b9c\u559c\u5b9c\u55d4\u7684\u4fcf\u8138\uff0c\u8d8a\u53d1\u522b\u6709\u98ce\u81f4\u3002 \u3000\u3000\u5b8b\u5b5a\u7b11\u58f0\u752b\u843d\uff0c\u5b89\u5c0f\u840d\u5ffd\u7136\u5927\u58f0\u9053\uff1a\u201c\u5c39\u59d1\u5a18\uff0c\u4f60\u90a3\u4f4d\u5e08\u7236\u6765\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5c39\u68a6\u8377\u5148\u524d\u5e76\u672a\u7559\u5fc3\u5230\u5b89\u5c0f\u840d\uff0c\u53ea\u56e0\u5979\u4e00\u5411\u88ab\u7537\u4eba\u6367\u60ef\u4e86\uff0c\u6240\u4ee5\uff0c\u4e0d\u628a\u4efb\u4f55\u7537\u4eba\u770b\u5728\u773c\u4e2d\uff0c\u5728\u5979\u7684\u5fc3\u4e2d\uff0c\u8ba4\u4e3a\u5929\u4e0b\u7684\u7537\u4eba\uff0c\u5de6\u53f3\u90fd\u662f\u6ce5\u5df4\u505a\u7684\uff0c\u7eb5\u6709\u90a3\u51fa\u8272\u7684\u4eba\u7269\uff0c\u4e5f\u4e0d\u8fc7\u5c14\u5c14\uff0c\u8fd9\u65f6\u5b89\u5c0f\u840d\u7adf\u7136\u5f00\u53e3\u53ea\u95ee\u4e86\u5979\u7684\u5e08\u7236\uff0c\u800c\u6ca1\u628a\u5979\u653e\u5728\u773c\u4e2d\uff0c\u53ef\u4f7f\u5979\u5fc3\u4e2d\u5fae\u5fae\u4e00\u6014\uff0c\u661f\u76ee\u8f6c\u6ce8\u5728\u5b89\u5c0f\u840d\u8eab\u4e0a\u770b\u4e86\u534a\u664c\uff0c\u8fd9\u624d\u7b11\u9053\uff1a\u201c\u5b89\u516c\u5b50\uff0c\u5bb6\u5e08\u672a\u6765\u2026\u2026\u201d \u3000\u3000\u5979\u8bed\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u4e0d\u81ea\u89c9\u5730\u4e3e\u8d77\u7693\u8155\uff0c\u63a0\u4e86\u4e00\u63a0\u9b13\u53d1\uff0c\u63a5\u9053\uff1a\u201c\u516c\u5b50\u53ef\u662f\u4e0e\u5bb6\u5e08\u76f8\u8bc6\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u51b7\u51b7\u5730\u63a5\u9053\uff1a\u201c\u4e0d\u592a\u719f\uff0c\u89c1\u8fc7\u4e00\u9762\u800c\u5df2\uff01\u201d \u3000\u3000\u5979\u8f6c\u5934\u770b\u4e86\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u53ea\u89c1\u4ed6\u7adf\u662f\u6b63\u895f\u5371\u5750\uff0c\u95ed\u76ee\u4e0d\u8a00\u4e0d\u52a8\uff0c\u5fc3\u4e2d\u4e0d\u7531\u5f97\u5927\u4e3a\u8be7\u5f02\uff0c\u6697\u9053\uff1a\u201c\u4ed6\u8fd9\u662f\u600e\u4e48\u4e86\uff1f\u83ab\u975e\u8fd9\u5996\u5973\u5df2\u7136\u4e0b\u4e86\u65e0\u5f62\u4e4b\u6bd2\uff0c\u4f24\u4e86\u6211\u65b9\u5927\u54e5\u2026\u2026\u201d\u5979\u5173\u5fc3\u65b9\u96ea\u5b9c\u8fc7\u5207\uff0c\u4e0d\u7531\u6025\u4fc3\u5730\u9053\uff1a\u201c\u65b9\u516c\u5b50\uff0c\u4f60\u2026\u2026\u600e\u4e48\u5566\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u88ab\u5979\u53eb\u5f97\u7f13\u7f13\u7741\u5f00\u53cc\u76ee\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4f60\u2026\u2026\u53ef\u662f\u4e0d\u8212\u670d\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u5934\u9053\uff1a\u201c\u6ca1\u6709\u554a\uff01\u201d \u3000\u3000\u5c39\u68a6\u8377\u5ffd\u7136\u4f4e\u58f0\u9053\uff1a\u201c\u5b89\u516c\u5b50\uff0c\u8fd9\u4f4d\u65b9\u516c\u5b50\u53ea\u662f\u89c9\u5f97\u5fc3\u4e2d\u6709\u4e9b\u4e0d\u5feb\u800c\u5df2\uff0c\u4f60\u4e0d\u7528\u4e3a\u4ed6\u62c5\u5fc3\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u51b7\u54fc\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u5c39\u68a6\u8377\u5fae\u5fae\u4e00\u7b11\uff0c\u6ca1\u6709\u89e3\u8bf4\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u65f6\u5374\u970d\u7136\u66b4\u7741\u53cc\u76ee\uff0c\u5411\u5c39\u68a6\u8377\u9053\uff1a\u201c\u5c39\u59d1\u5a18\uff0c\u90a3\u4f20\u4fe1\u5c11\u6797\u4e4b\u4eba\uff0c\u53ef\u5c31\u662f\u59d1\u5a18\u5417\uff1f\u201d \u3000\u3000\u5c39\u68a6\u8377\u95fb\u8a00\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u516c\u5b50\u662f\u5c11\u6797\u95e8\u4e0b\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u662f\uff01\u201d \u3000\u3000\u5c39\u68a6\u8377\u770b\u4e86\u674e\u82b8\u4e00\u773c\uff0c\u9053\uff1a\u201c\u82b8\u59b9\u59b9\uff0c\u8fd9\u4f4d\u65b9\u516c\u5b50\u5f53\u771f\u662f\u4e0d\u662f\u5c11\u6797\u95e8\u4e0b\uff1f\u201d \u3000\u3000\u674e\u82b8\u600e\u4e48\u77e5\u9053\u4ed6\u5f53\u771f\u662f\u4e0d\u662f\u5462\uff1f \u3000\u3000\u4f46\u5979\u6b64\u523b\u4e0d\u80fd\u4e0d\u786c\u8d77\u5934\u76ae\uff0c\u63a5\u9053\uff1a\u201c\u65b9\u516c\u5b50\u672a\u4e0e\u5c11\u6797\u50e7\u4fa3\u540c\u884c\uff0c\u90a3\u81ea\u7136\u4e0d\u662f\u7684\u4e86\uff01\u201d \u3000\u3000\u5c39\u68a6\u8377\u86fe\u7709\u5fae\u805a\uff0c\u9053\uff1a\u201c\u65b9\u516c\u5b50\uff0c\u4f60\u65e2\u975e\u5c11\u6797\u95e8\u4e0b\uff0c\u4e0d\u77e5\u4e3a\u4f55\u8981\u95ee\u90a3\u5c11\u6797\u4f20\u4fe1\u4e4b\u4e8b\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u51b7\u51b7\u4e00\u7b11\uff1b\u9053\uff1a\u201c\u90a3\u51e0\u4f4d\u5c11\u6797\u9ad8\u50e7\uff0c\u4e5f\u662f\u4f24\u5728\u4f60\u7684\u624b\u4e0b\u4e86\uff1f\u201d \u3000\u3000\u5c39\u68a6\u8377\u5ffd\u5730\u54af\u54af\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u9519\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u59d1\u5a18\u624b\u6bb5\u5f88\u6076\u6bd2\u2026\u2026\u201d \u3000\u3000\u5c39\u68a6\u8377\u5374\u662f\u63a5\u9053\uff1a\u201c\u59be\u8eab\u53ea\u9053\u90a3\u5c11\u6797\u548c\u5c1a\u4eec\u7684\u529f\u592b\u5982\u4f55\u4e86\u5f97\uff0c\u4e0d\u6599\u5f53\u65e5\u4e00\u89c1\uff0c\u771f\u662f\u4e0d\u5982\u4f20\u95fb\u4e4b\u751a\uff01\u201d \u3000\u3000\u5979\u4f3c\u662f\u6beb\u4e0d\u7559\u5fc3\u65b9\u96ea\u5b9c\u8138\u4e0a\u4e0d\u5feb\u4e4b\u8272\uff0c\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u66f4\u662f\u4e00\u6d3e\u81ea\u5927\u7684\u610f\u5473\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5c11\u6797\u5bfa\u4e43\u662f\u5f53\u4ee3\u6b66\u5b66\u5b97\u5e9c\uff0c\u59d1\u5a18\u5c45\u7136\u8fdb\u51fa\u81ea\u5982\uff0c\u800c\u4e14\u80fd\u4e00\u4e3e\u4f24\u4e86\u4ed6\u4eec\u8bb8\u591a\u957f\u8001\uff0c\u8db3\u89c1\u59d1\u5a18\u4e00\u8eab\u6b66\u5b66\uff0c\u6bd4\u4ed6\u4eec\u66f4\u4e3a\u9ad8\u660e\u7684\u4e86\uff01\u201d \u3000\u3000\u5c39\u68a6\u8377\u7b11\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u516c\u5b50\u8fc7\u5956\u4e86\uff01\u201d \u3000\u3000\u5979\u7b11\u5f97\u751a\u662f\u597d\u770b\uff0c\u53ea\u628a\u5b8b\u5b5a\u77a7\u5f97\u5446\u4e86\u4e00\u5446\uff0c\u6697\u9053\uff1a\u201c\u65e0\u602a\u4e4e\u90a3\u674e\u82b8\u8bf4\u5979\u98a0\u5012\u6b66\u6797\u4f17\u751f\uff0c\u8fd9\u5973\u5a03\u513f\u679c\u771f\u662f\u751f\u5177\u5a9a\u76f8\u4f7f\u4eba\u6709\u7740\u4e0d\u53ef\u6297\u62d2\u7684\u88ab\u9b41\u4e4b\u611f\u2026\u2026\u201d \u3000\u3000\u4f46\u65b9\u96ea\u5b9c\u6b64\u523b\u5374\u4e1d\u6beb\u4e0d\u53d7\u5f71\u54cd\uff0c\u6562\u60c5\u4ed6\u9002\u624d\u95ed\u76ee\u800c\u5750\uff0c\u4fbf\u662f\u6f5c\u5fc3\u9759\u8651\uff0c\u628a\u81ea\u5df1\u90a3\u5728\u521d\u89c1\u5230\u5979\u7684\u6fc0\u52a8\u5fc3\u60c5\uff0c\u5b8c\u5168\u6291\u5236\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5c39\u68a6\u8377\u7b11\u5bb9\u672a\u655b\uff0c\u65b9\u96ea\u5b9c\u7acb\u5373\u63a5\u9053\uff1a\u201c\u5c39\u59d1\u5a18\uff0c\u4f60\u662f\u7528\u5251\u5417\uff1f\u201d \u3000\u3000\u5c39\u68a6\u8377\u9053\uff1a\u201c\u59be\u8eab\u5417\uff1f\u5200\u5251\u7686\u7528\uff01\u201d \u3000\u3000\u674e\u82b8\u8033\u4e2d\u542c\u5f97\u4e24\u4eba\u5bf9\u7b54\uff0c\u4e0d\u7981\u5fc3\u4e2d\u5927\u611f\u5947\u8bb6\uff0c\u4e0d\u77e5\u65b9\u96ea\u5b9c\u7a76\u7adf\u7528\u5fc3\u4f55\u5728\uff1f \u3000\u3000\u8fd9\u65f6\u65b9\u96ea\u5b9c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u533a\u533a\u4e0d\u624d\uff0c\u4e5f\u66fe\u5b66\u8fc7\u51e0\u624b\u5251\u6cd5\uff0c\u59d1\u5a18\u80af\u4e0d\u80af\u8d4f\u5149\uff0c\u8d50\u6559\u51e0\u624b\u9ad8\u660e\uff1f\u201d \u3000\u3000\u5c39\u68a6\u8377\u5927\u611f\u610f\u5916\u5730\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u600e\u4e48\uff1f\u516c\u5b50\u8981\u4e0e\u59be\u8eab\u8f83\u91cf\u5251\u6cd5\u5417\u5c38\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u5c39\u68a6\u8377\u4e00\u53cc\u5a9a\u773c\u8198\u4e86\u51e0\u779f\uff0c\u7b11\u9053\uff1a\u201c\u4f55\u5fc5\u5462\uff1f\u516c\u5b50\u4e0e\u59be\u8eab\u65e0\u4ec7\u65e0\u6028\uff0c\u53c8\u4f55\u7528\u4e89\u90a3\u4e00\u65f6\u4e4b\u5fff\uff1f\u201d \u3000\u3000\u5979\u8bed\u97f3\u5fae\u5fae\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u518d\u8bf4\uff0c\u59be\u8eab\u4e43\u662f\u5973\u6d41\u4e4b\u8f88\uff0c\u516c\u5b50\u4e5f\u72af\u4e0d\u7740\u8ddf\u6211\u4eec\u9017\u6c14\u73a9\u513f\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6599\u4e0d\u5230\u5979\u4f1a\u8bf4\u51fa\u8fd9\u822c\u8bdd\u6765\uff0c\u4e0d\u7531\u5f97\u6697\u9053\uff1a\u201c\u5979\u8fd9\u795e\u6001\uff0c\u5230\u4e5f\u4e0d\u50cf\u5fc3\u72e0\u624b\u8fa3\u4e4b\u4eba\u561b\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u771f\u8981\u8ddf\u5979\u6bd4\u5251\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8bdd\u5df2\u51fa\u53e3\uff0c\u81ea\u662f\u4e0d\u4fbf\u6536\u56de\uff0c\u70b9\u5934\u9053\uff1a\u201c\u5144\u5f1f\u786e\u6709\u6b64\u610f\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9f13\u638c\u9053\uff1a\u201c\u597d\u554a\uff0c\u5c0f\u5f1f\u5012\u53ef\u5927\u5f00\u8ddf\u754c\u4e86\uff01\u201d \u3000\u3000\u663e\u7136\uff0c\u5979\u8fd9\u662f\u5341\u5206\u8d5e\u6210\u7684\u4e86\uff01 \u3000\u3000\u5c39\u68a6\u8377\u76b1\u4e86\u4e00\u76b1\u67f3\u7709\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5b89\u516c\u5b50\uff0c\u59be\u8eab\u4e0d\u662f\u65b9\u516c\u5b50\u5bf9\u624b\uff0c\u6bd4\u5251\u7684\u4e8b\uff0c\u4e0d\u5982\u4f5c\u7f62\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u53ef\u662f\u5929\u9b54\u5973\u7684\u95e8\u4e0b\uff0c\u90fd\u662f\u4e00\u4e9b\u80c6\u602f\u7684\u5c0f\u59d1\u5a18\u5417\uff1f\u201d \u3000\u3000\u5979\u81ea\u5df1\u4e5f\u662f\u4e2a\u5c0f\u59d1\u5a18\uff0c\u8fd9\u65f6\u88c5\u5f97\u4e00\u6d3e\u8001\u6210\uff0c\u8bf4\u51fa\u8bdd\u6765\uff0c\u5012\u4e5f\u86ee\u50cf\u90a3\u4e48\u56de\u4e8b\u3002 \u3000\u3000\u5c39\u68a6\u8377\u548c\u674e\u82b8\u7684\u8138\u4e0a\uff0c\u5239\u90a3\u95f4\u5927\u5927\u53d8\u8272\uff01 \u3000\u3000\u5b89\u5c0f\u840d\u8fd9\u4e00\u53e5\u8bdd\u5f88\u4f24\u4eba\uff0c\u5979\u4eec\u81ea\u5df1\u53ef\u4ee5\u5fcd\u53d7\u5f97\u4e86\u522b\u4eba\u8ba5\u8bbd\uff0c\u4f46\u5979\u4eec\u7684\u5e08\u7236\uff0c\u5c31\u51b3\u4e0d\u4f1a\u5fcd\u53d7\uff01 \u3000\u3000\u662f\u4ee5\uff0c\u5c39\u68a6\u8377\u82e6\u7740\u5a07\u5bb9\u9053\uff1a\u201c\u5b89\u516c\u5b50\uff0c\u4f60\u600e\u7684\u9a82\u8d77\u59be\u8eab\u7684\u5e08\u95e8\u6765\u4e86\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u8eab\u4e3a\u5973\u4eba\uff0c\u7eb5\u7136\u5c39\u68a6\u8377\u7684\u5a9a\u52b2\u51fa\u4eba\u610f\u8868\uff0c\u4f46\u5bf9\u5979\u53ef\u8d77\u4e0d\u4e86\u591a\u5927\u4f5c\u7528\uff0c\u53ea\u89c1\u5979\u51b7\u51b7\u4e00\u7b11\u63a5\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u5e08\u7236\u6211\u4e5f\u89c1\u8fc7\uff0c\u9a82\u5979\u4e24\u53e5\uff0c\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u53ea\u662f\u4f60\u4eec\u5c45\u7136\u5982\u6b64\u80c6\u602f\uff0c\u4e0d\u6562\u773c\u65b9\u5144\u6bd4\u5251\u4e4b\u4e8b\uff0c\u82e5\u88ab\u4f60\u5e08\u7236\u77e5\u9053\uff0c\u6050\u6015\u59d1\u5a18\u5c31\u5927\u6709\u6666\u6c14\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5e76\u4e0d\u77e5\u9053\u5c39\u68a6\u8377\u5fc3\u4e2d\u5bb3\u6015\u7684\u6b63\u662f\u8fd9\u4e00\u70b9\uff0c\u987a\u53e3\u80e1\u626f\uff0c\u7adf\u662f\u626f\u5230\u4e86\u8282\u9aa8\u773c\u513f\u4e0a\uff0c\u5012\u4e5f\u5de7\u5f97\u5f88\u3002 \u3000\u3000\u90a3\u5c39\u68a6\u8377\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u5b89\u516c\u4e8e\uff0c\u59be\u8eab\u7b54\u5e94\u8ddf\u90a3\u65b9\u516c\u5b50\u8f83\u91cf\u5251\u6cd5\uff0c\u4f46\u516c\u5b50\u4e5f\u5f97\u7b54\u5e94\u59be\u8eab\u4e00\u4ef6\u4e8b\u624d\u597d\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5c39\u68a6\u8377\u4f4e\u58f0\u9053\uff1a\u201c\u516c\u5b50\u5343\u4e07\u4e0d\u53ef\u5c06\u4eca\u65e5\u4e4b\u4e8b\uff0c\u544a\u77e5\u5bb6\u5e08\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u5b89\u67d0\u53ef\u8981\u597d\u597d\u5730\u60f3\u4e0a\u4e00\u60f3\uff01\u201d \u3000\u3000\u5c39\u68a6\u8377\u89c1\u4ed6\u4e0d\u4f5c\u80af\u5b9a\u56de\u7b54\uff0c\u4e0d\u7981\u6709\u4e9b\u7740\u6025\uff0c\u7f13\u7f13\u5730\u81ea\u77f3\u69bb\u4e0a\u7ad9\u8d77\uff0c\u987a\u624b\u81ea\u5899\u4e0a\u53d6\u4e0b\u6302\u5251\uff0c\u6697\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4f60\u60f3\u597d\u4e86\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u89c1\u5979\u5df2\u7136\u62d4\u51fa\u5b9d\u5251\uff0c\u6697\u9053\uff1a\u201c\u53ea\u8981\u5979\u8ddf\u65b9\u96ea\u5b9c\u52a8\u4e0a\u624b\uff0c\u8c05\u5979\u4e5f\u4e0d\u53ef\u80fd\u518d\u8ff7\u60d1\u5f97\u4e86\u65b9\u5144\u7684\u5fc3\u795e\u4e86\u2026\u2026\u201d \u3000\u3000\u5f53\u70b9\u4e0b\u5934\u9053\uff1a\u201c\u60f3\u597d\u4e86\uff01\u201d \u3000\u3000\u5c39\u68a6\u8377\u9053\uff1a\u201c\u516c\u5b50\u662f\u53ef\u7b54\u5e94\u4e86\u59be\u8eab\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u533a\u533a\u4e0d\u51c6\u5907\u5c06\u6b64\u95f4\u4e4b\u4e8b\u544a\u8bc9\u4ee4\u5e08\uff0c\u59d1\u5a18\u653e\u5fc3\u4e0e\u65b9\u5144\u8f83\u91cf\u5427\uff01\u201d \u3000\u3000\u5c39\u68a6\u8377\u62b1\u5251\u5728\u6000\uff0c\u6b20\u4e86\u6b20\u8eab\uff0c\u5a07\u7b11\u9053\uff1a\u201c\u591a\u8c22\u5b89\u516c\u4e8e\u96c5\u91cf\u2026\u2026\u201d \u3000\u3000\u5999\u76ee\u4e00\u8f6c\uff0c\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u65b9\u516c\u5b50\uff0c\u59be\u8eab\u53ea\u6015\u8981\u732e\u4e11\u4e86\uff01\u5c1a\u671b\u516c\u5b50\u5251\u4e0b\u7559\u60c5\uff01\u201d \u3000\u3000\u5979\u8bf4\u6765\u751a\u662f\u5a49\u8f6c\uff0c\u795e\u60c5\u66f4\u662f\u695a\u695a\u52a8\u4eba\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5fae\u5fae\u4e00\u7b11\uff0c\u62d4\u51fa\u8eab\u7554\u5b9d\u5251\uff0c\u63a5\u9053\uff1a\u201c\u5ba4\u4e2d\u5730\u65b9\u4e0d\u5927\uff0c\u59d1\u5a18\u65bd\u5c55\u7684\u5f00\u5417\uff1f\u201d \u3000\u3000\u8fd9\u4e0d\u8fc7\u662f\u987a\u53e3\u4e00\u63d0\uff0c\u5728\u65b9\u96ea\u5b9c\u770b\u6765\u573a\u5730\u5927\u5c0f\uff0c\u6839\u672c\u65e0\u59a8\u4e8e\u5251\u6cd5\u7684\u65bd\u4e3a\uff0c\u539f\u56e0\u662f\u4ed6\u5df2\u4e60\u8fc7\u4e0a\u4e58\u5251\u9053\u4e4b\u6545\u3002 \u3000\u3000\u5c39\u68a6\u8377\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u8981\u7d27\uff0c\u53ea\u8981\u516c\u5b50\u4e0d\u5acc\u592a\u5c0f\uff0c\u90a3\u5012\u4e0d\u7528\u51fa\u6d1e\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u5982\u6b64\u751a\u597d\uff01\u59d1\u5a18\u8bf7\uff01\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u5b89\u5c0f\u840d\u3001\u674e\u82b8\u3001\u5b8b\u5b5a\u4e09\u4eba\uff0c\u5df2\u7136\u8d77\u8eab\u8d70\u5230\u77f3\u69bb\u8fb9\u6cbf\u5750\u5b9a\u3002 \u3000\u3000\u5c39\u68a6\u8377\u542c\u5f97\u65b9\u96ea\u5b9c\u8981\u5979\u5148\u51fa\u5251\uff0c\u5f53\u4e0b\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4f60\u662f\u5ba2\u4eba\uff0c\u5f53\u7136\u662f\u4f60\u5148\u8bf7\u7684\u4e86\uff01\u201d \u3000\u3000\u867d\u8bf4\u8fd9\u662f\u6bd4\u5251\uff0c\u4f46\u4ed6\u4eec\u5fc3\u4e2d\u90fd\u5f88\u660e\u767d\uff0c\u4e00\u52a8\u8d77\u624b\u6765\uff0c\u5fc5\u7136\u96be\u514d\u6709\u5931\u624b\u548c\u51f6\u9669\uff0c\u662f\u4ee5\uff0c\u8fd9\u4e9b\u5ba2\u5957\u8bdd\uff0c\u6839\u672c\u6ca1\u6709\u591a\u5927\u610f\u4e49\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u6de1\u6de1\uff0c\u4e00\u7b11\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u5728\u4e0b\u8eab\u4e3a\u5802\u5802\u7537\u5b50\u6c49\uff0c\u600e\u53ef\u62a2\u5148\u51fa\u624b\uff1f\u8fd8\u662f\u59d1\u5a18\u8bf7\u2026\u2026\u201d \u3000\u3000\u5c39\u68a6\u8377\u81ea\u4ece\u89c1\u5230\u65b9\u96ea\u5b9c\u95ed\u76ee\u9759\u5750\u4e4b\u540e\uff0c\u5c45\u7136\u5bf9\u81ea\u5df1\u7684\u5a9a\u672f\u6beb\u65e0\u53cd\u5e94\uff0c\u5c31\u5df2\u7136\u660e\u767d\uff0c\u773c\u524d\u8fd9\u4f4d\u5e74\u8f7b\u4eba\uff0c\u4e0d\u662f\u5bb9\u6613\u5bf9\u4ed8\u3002\u8fd9\u65f6\u65b9\u96ea\u5b9c\u575a\u8981\u5979\u51fa\u624b\uff0c\u5979\u5012\u4e5f\u4e0d\u518d\u63a8\u8ba9\uff0c\u5a07\u7b11\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5982\u6b64\uff0c\u59be\u8eab\u5c31\u6709\u50ed\u4e86\uff01\u201d \u3000\u3000\u5b9d\u5251\u4e00\u4e3e\uff0c\u5f53\u8138\u523a\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u6325\u5251\u4e00\u6321\uff0c\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u4e0d\u7528\u5ba2\u6c14\uff0c\u5728\u4e0b\u4e43\u662f\u8bda\u5fc3\u8bf7\u6559\uff0c\u4f60\u4e0d\u5fc5\u9690\u6280\u81ea\u73cd\u4e86\u2026\u2026\u201d \u3000\u3000\u5b9d\u5251\u4e00\u6321\u4e4b\u540e\uff0c\u7adf\u662f\u8fde\u51fa\u4e09\u62db\uff0c\u653b\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u9694\u7740\u4e00\u5f20\u77f3\u51f3\uff0c\u8fd9\u4e09\u62db\u5251\u52bf\u867d\u5feb\uff0c\u4f46\u5374\u5e76\u975e\u5236\u654c\u7684\u62db\u5f0f\uff0c\u65b9\u96ea\u5b9c\u7684\u7528\u5fc3\uff0c\u65e0\u975e\u8feb\u5979\u5c3d\u51fa\u5168\u529b\u800c\u5df2\u3002 \u3000\u3000\u679c\u7136\uff0c\u5c39\u68a6\u8377\u88ab\u4ed6\u8fd9\u4e09\u62db\u5251\u6cd5\u8feb\u5f97\u8fde\u8fde\u9000\u4e86\u4e09\u6b65\uff0c\u8fd9\u4e00\u6765\uff0c\u53ef\u5f15\u53d1\u4e86\u5979\u7684\u4e89\u5f3a\u597d\u80dc\u4e4b\u5fc3\uff0c\u4e5f\u4e0d\u7ba1\u773c\u524d\u8fd9\u5c11\u5e74\u4eba\uff0c\u6b63\u662f\u81ea\u5df1\u4e0d\u60f3\u5f97\u7f6a\u7684\u4fcf\u6717\u541b\uff0c\u7adf\u7136\u82b1\u5bb9\u8f6c\u5389\uff0c\u51b7\u51b7\u559d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u516c\u5b50\u597d\u5251\u6cd5\uff0c\u59be\u8eab\u53ef\u8981\u56de\u653b\u4e86\u2026\u2026\u201d \u3000\u3000\u559d\u58f0\u4e2d\uff0c\u731b\u7136\u523a\u51fa\u4e94\u5251\uff01 \u3000\u3000\u8fd9\u4e94\u5251\u51fa\u624b\u4e4b\u5feb\uff0c\u6bd4\u4e4b\u65b9\u96ea\u5b9c\u9002\u624d\u4e09\u5251\u6709\u8fc7\u4e4b\u800c\u65e0\u4e0d\u53ca\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u6697\u6697\u4e00\u60ca\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u5929\u9b54\u5973\u95e8\u4e0b\u7684\u5251\u6cd5\uff0c\u679c\u771f\u4e0d\u51e1\uff01\u201d \u3000\u3000\u601d\u5ff5\u4e4b\u95f4\uff0c\u957f\u5251\u6a2a\u67b6\u800c\u51fa\uff0c\u4e00\u62db\u201c\u4e91\u65ad\u5deb\u5c71\u201d\uff0c\u6d12\u51fa\u6f2b\u5929\u5251\u5e55\uff0c\u5c06\u90a3\u5c39\u68a6\u8377\u7684\u4e94\u5251\uff0c\u4e00\u9f50\u5c01\u4e86\u56de\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u65bd\u5c55\u5f97\u6070\u5230\u597d\u5904\uff0c\u53ea\u770b\u5f97\u5b89\u5c0f\u840d\u5927\u7b11\u9053\uff1a\u201c\u65b9\u5144\uff0c\u8fd9\u62db\u2018\u4e91\u65ad\u5deb\u5c71\u2019\uff0c\u679c\u7136\u5999\u6781\uff0c\u7a33\u6781\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u5979\u8fd9\u4e48\u4e00\u53eb\uff0c\u5ffd\u7136\u5fc3\u4e2d\u5927\u5927\u4e00\u9707\uff0c\u6697\u9053\uff1a\u201c\u5b89\u5c0f\u840d\u773c\u4e0b\u5c1a\u5728\u53ef\u654c\u53ef\u53cb\u9636\u6bb5\uff0c\u6211\u83ab\u8981\u6cc4\u5c3d\u4e86\u5e95\uff0c\u53ef\u5c31\u81ea\u627e\u82e6\u5934\u4e86\uff01\u201d \u3000\u3000\u4e00\u5ff5\u53ca\u6b64\uff0c\u4ed6\u4fbf\u6709\u4e86\u6212\u5fc3\uff0c\u51fa\u4e4b\u9645\uff0c\u7adf\u662f\u5927\u6709\u5206\u5bf8\uff0c\u4e0d\u613f\u592a\u8fc7\u66b4\u9732\u81ea\u5df1\u5b9e\u529b\u3002 \u3000\u3000\u4ed6\u8fd9\u7b49\u6253\u6cd5\uff0c\u81ea\u662f\u7ed9\u4e86\u5c39\u68a6\u8377\u4e0d\u5c11\u65b9\u4fbf\u3002 \u3000\u3000\u53ea\u662f\u5979\u5251\u5982\u6fc0\u9f99\uff0c\u62db\u62db\u90fd\u662f\u67aa\u653b\u7684\u5251\u52bf\u3002 \u3000\u3000\u53cd\u89c2\u65b9\u96ea\u5b9c\uff0c\u5219\u5341\u62db\u4e4b\u4e2d\uff0c\u5012\u6709\u4e5d\u62db\u5747\u662f\u70b9\u5230\u5373\u6b62\uff0c\u672c\u6765\u4ed6\u53ef\u4ee5\u4e00\u5251\u78d5\u98de\u5bf9\u65b9\u5b9d\u5251\uff0c\u4f46\u53cc\u5251\u5c1a\u672a\u76f8\u4ea4\uff0c\u4ed6\u4fbf\u6536\u5251\u53d8\u62db\uff0c\u6539\u653b\u4e3a\u5b88\u3002 \u3000\u3000\u4e8c\u5341\u62db\u8fc7\u53bb\uff0c\u8c01\u4e5f\u672a\u66fe\u6cbe\u5230\u4e00\u4e1d\u80dc\u673a\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u77a7\u5f97\u6709\u4e9b\u4e0d\u89e3\uff0c\u4f4e\u58f0\u5411\u5b8b\u5b5a\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u65b9\u5144\u600e\u4e48\u4e86\uff1f\u4e3a\u4f55\u6bcf\u4e00\u62db\u90fd\u662f\u90a3\u7b49\u8fdf\u6ede\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u77a7\u4e86\u534a\u664c\uff0c\u4e5f\u672a\u77a7\u51fa\u4ec0\u4e48\u9053\u7406\uff0c\u6447\u5934\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u8001\u673d\u4e5f\u7cca\u6d82\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u95fb\u8a00\uff0c\u4e0d\u7531\u5fc3\u4e2d\u5927\u6025\uff0c\u53ea\u9053\u65b9\u96ea\u5b9c\u53c8\u4e2d\u4e86\u5c39\u68a6\u8377\u7684\u4ec0\u4e48\u82b1\u62db\uff0c\u7cbe\u795e\u4e0d\u80fd\u8d2f\u6ce8\uff0c\u5a07\u8eaf\u4e00\u626d\uff0c\u5c31\u5f85\u8d77\u8eab\uff0c\u52a9\u90a3\u65b9\u96ea\u5b9c\u5236\u654c\u3002 \u3000\u3000\u5b8b\u5b5a\u5230\u5e95\u8981\u6bd4\u5b89\u5c0f\u840d\u8001\u7ec3\uff0c\u7779\u72b6\u5403\u4e86\u4e00\u60ca\uff0c\u4f38\u624b\u6309\u4f4f\u5b89\u5c0f\u840d\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8001\u5f1f\u4e0d\u53ef\u5192\u5931\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u76b1\u7709\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u4f60\u96be\u9053\u770b\u4e0d\u51fa\u90a3\u65b9\u5144\u6709\u4e9b\u529b\u4e0d\u4ece\u5fc3\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u6447\u5934\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u8fd9\u4e8b\u679c\u7136\u6709\u4e9b\u8e4a\u8df7\uff0c\u4f46\u8001\u673d\u77e5\u9053\uff0c\u65b9\u8001\u5f1f\u5251\u62db\u4e0d\u66fe\u610f\u5230\u5251\u5230\uff0c\u53ea\u6015\u522b\u6709\u7f18\u6545\u4e86\u2026\u2026\u201d \u3000\u3000\u674e\u82b8\u8fd9\u65f6\u5ffd\u7136\u5931\u58f0\u9053\uff1a\u201c\u59be\u8eab\u660e\u767d\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u77aa\u4e86\u5979\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u660e\u767d\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u674e\u82b8\u9053\uff1a\u201c\u90a3\u65b9\u516c\u5b50\u662f\u2026\u2026\u662f\u2026\u2026\u8bda\u5fc3\u8bf1\u6211\u4e8c\u5e08\u59ca\u5c06\u5e08\u95e8\u7684\u5251\u6cd5\u5c3d\u91cf\u66b4\u9732\u554a\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u4e00\u6293\u90a3\u516b\u5b57\u80e1\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff01\u4e0d\u9519\uff01\u65b9\u8001\u5f1f\u786e\u662f\u8fd9\u7b49\u7528\u5fc3\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5f53\u771f\u8fd9\u6837\u5417\uff1f\u5b8b\u8001\uff0c\u90a3\u662f\u665a\u8f88\u591a\u4f59\u62c5\u5fc3\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u5982\u662f\u6362\u4e86\u8001\u673d\uff0c\u90a3\u53ea\u6015\u771f\u4e0d\u662f\u8fd9\u4f4d\u59d1\u5a18\u7684\u5bf9\u624b\u4e86\uff01\u4e0d\u8fc7\uff0c\u65b9\u8001\u5f1f\u7684\u5251\u6cd5\uff0c\u8001\u673d\u77e5\u4e4b\u751a\u6df1\uff0c\u4ed6\u2026\u2026\u201d \u3000\u3000\u5ffd\u7136\u770b\u4e86\u674e\u82b8\u4e00\u773c\uff0c\u7b11\u9053\uff1a\u201c\u8fde\u674e\u59d1\u5a18\u4e0a\u53bb\u8054\u624b\uff0c\u4e5f\u65e0\u80dc\u7b97\u7684\u628a\u63e1\uff01\u201d \u3000\u3000\u674e\u82b8\u95fb\u8a00\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u5979\u81ea\u7136\u4e0d\u4f1a\u76f8\u4fe1\u6731\u5b5a\u8fd9\u756a\u8bdd\uff0c\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u8001\u4eba\u5bb6\u4e0d\u5acc\u592a\u6367\u65b9\u516c\u5b50\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u53ef\u662f\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u674e\u82b8\u9053\uff1a\u201c\u59be\u8eab\u679c\u771f\u6709\u4e9b\u4e0d\u6562\u76f8\u4fe1\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u51b7\u51b7\u5730\u770b\u4e86\u5979\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u4fe1\uff0c\u7b49\u90a3\u5230\u5c39\u68a6\u8377\u4e0d\u652f\u4e4b\u65f6\uff0c\u4f55\u59a8\u4e0a\u53bb\u8054\u624b\u4e00\u6218\uff01\u201d \u3000\u3000\u674e\u82b8\u8fdf\u7591\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4f60\u4e0d\u6015\u4f60\u90a3\u65b9\u5144\u4e00\u65f6\u5931\u624b\uff0c\u4f24\u5728\u6211\u4eec\u6697\u5668\u4e4b\u4e0b\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u95fb\u8a00\u4e0d\u7981\u4e00\u60ca\uff0c\u5fd6\u9053\uff1a\u201c\u662f\u554a\uff0c\u6211\u600e\u5730\u6ca1\u6709\u60f3\u5230\u5979\u4eec\u8fd8\u6709\u6697\u5668\u5462\uff1f\u201d \u3000\u3000\u5f53\u4e0b\u4fbf\u518d\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u5ffd\u7136\u95f4\u53ea\u89c1\u5b8b\u5b5a\u5fae\u5fae\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u5b89\u8001\u5f1f\uff0c\u4f60\u53ef\u66fe\u89c1\u5230\u8fc7\u65b9\u8001\u5f1f\u7684\u5251\u6cd5\u5965\u5999\u4e4b\u5904\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6447\u5934\u9053\uff1a\u201c\u672a\u7aa5\u5168\u8c8c\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u90a3\u65b9\u8001\u5f1f\u7684\u5251\u6cd5\u4e00\u65e6\u65bd\u5c55\uff0c\u4f11\u8bf4\u6697\u5668\uff0c\u53ea\u6015\u8fde\u66b4\u96e8\u4e5f\u6512\u4e0d\u8fdb\u53bb\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u4f60\u662f\u8bf4\u65b9\u5144\u4e0d\u6015\u5979\u4eec\u7684\u6697\u5668\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u81ea\u662f\u4e0d\u6015\u7684\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u76ee\u5149\u4e00\u8f6c\uff0c\u5411\u674e\u82b8\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u542c\u5230\u4e86\u5417\uff1f\u201d \u3000\u3000\u674e\u82b8\u9053\uff1a\u201c\u59be\u8eab\u542c\u5230\u4e86\uff0c\u53ea\u662f\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u63a5\u9053\uff1a\u201c\u53ea\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u674e\u82b8\u9053\uff1a\u201c\u53ea\u662f\u59be\u8eab\u7684\u6697\u5668\u975e\u6bd4\u5bfb\u5e38\uff0c\u9664\u4e86\u5929\u6bd2\u94f6\u9488\u4e4b\u5916\u8fd8\u6709\u4e00\u79cd\u6781\u5389\u5bb3\u7684\u6697\u5668\uff0c\u65b9\u516c\u5b50\u7eb5\u7136\u5251\u6cd5\u9ad8\u5f3a\uff0c\u90a3\u4e5f\u65e0\u6cd5\u62b5\u6321\u7684\u4e86 \u3000\u3000\u5b89\u5c0f\u840d\u6025\u9053\uff1a\u201c\u4ec0\u4e48\u6697\u5668\uff1f\u201d \u3000\u3000\u674e\u82b8\u5ffd\u7136\u5fc3\u4e2d\u4e00\u9707\uff0c\u6697\u9053\uff1a\u8fd9\u6697\u5668\u4e43\u662f\u5e08\u95e8\u4e00\u5927\u673a\u5bc6\uff0c\u600e\u53ef\u5411\u5916\u4eba\u8a00\u8bf4\uff1f\u82e5\u88ab\u5e08\u7236\u77e5\u6653\uff0c\u81ea\u5df1\u7109\u6709\u547d\u5728\uff1f\u2026\u2026 \u3000\u3000\u4e00\u5ff5\u53ca\u6b64\uff0c\u7adf\u662f\u4f4e\u5934\u4e0d\u8bed\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u89c1\u5979\u4e0d\u8bf4\uff0c\u4e0d\u7531\u5341\u5206\u7740\u6025\uff0c\u8131\u53e3\u9053\uff0c\u201c\u59d1\u5a18\uff0c\u4f60\u600e\u7684\u4e0d\u8bf4\u8bdd\u4e86\uff1f\u201d \u3000\u3000\u674e\u82b8\u7f13\u7f13\u62ac\u8d77\u5934\u6765\uff0c\u76ee\u5149\u5728\u5b89\u5c0f\u840d\u8138\u4e0a\u4e00\u8f6c\uff0c\u89c1\u4ed6\u90a3\u7b49\u60f6\u6025\u5730\u77a7\u7740\u81ea\u5df1\u7684\u795e\u6001\uff0c\u4e0d\u89c9\u5f97\u82b3\u5fc3\u4e00\u8361\uff0c\u957f\u957f\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4e0d\u662f\u59be\u8eab\u4e0d\u8bf4\uff0c\u5b9e\u662f\u6709\u7740\u96be\u8a00\u4e4b\u9690\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6025\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u5f53\u771f\u4e0d\u613f\u8bb2\u51fa\u6765\u5417\uff1f\u201d \u3000\u3000\u674e\u82b8\u90a3\u786e\u5b9e\u662f\u96be\u4ee5\u8bf4\u51fa\u7684\u8bdd\u513f\uff0c\u5df2\u7136\u81f4\u4e86\u53e3\u8fb9\uff0c\u4f46\u5979\u76ee\u7779\u5b89\u5c0f\u840d\u90a3\u4e00\u8138\u7126\u6025\u4e4b\u8272\uff0c\u4e0d\u77e5\u600e\u5730\u7adf\u7136\u8bf4\u4e0d\u51fa\u53e3\u6765\uff0c\u8fdf\u7591\u4e86\u4e00\u4e0b\uff0c\u5c45\u7136\u63a5\u9053\uff1a\u201c\u516c\u5b50\uff0c\u59be\u8eab\u5982\u662f\u8bf4\u51fa\uff0c\u516c\u5b50\u53ef\u5343\u4e07\u4e0d\u80fd\u518d\u544a\u8bc9\u522b\u4eba\u554a\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u600e\u4e48\u4f1a\uff01\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\u5411\u5b8b\u5b5a\u9053\uff1a\u201c\u5b8b\u8001\u4f60\u53ef\u4e5f\u522b\u5411\u4ed6\u4eba\u8bf4\u51fa\u6765\u554a\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u8001\u673d\u81ea\u7136\u4e0d\u4f1a\u8bf4\u7684\u4e86\uff01\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u674e\u82b8\u4e00\u60ca\u9053\uff1a\u201c\u4e0d\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8001\u673d\u53ef\u4ee5\u4e0d\u540c\u4efb\u4f55\u4eba\u8bf4\u51fa\uff0c\u4f46\u65b9\u8001\u5f1f\u4e48\uff0c\u53ef\u5f97\u4f8b\u5916\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u90a3\u662f\u81ea\u7136\uff01\u201d \u3000\u3000\u674e\u82b8\u89c1\u5b89\u5c0f\u840d\u7b54\u5e94\u8981\u544a\u8bc9\u65b9\u96ea\u5b9c\uff0c\u5374\u672a\u53cd\u5bf9\uff0c\u53ea\u6de1\u6de1\u5730\u7b11\u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4f60\u80fd\u5426\u8f6c\u8bf7\u65b9\u516c\u5b50\u4e0d\u5411\u522b\u4eba\u63d0\u8d77\u5462\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u5f53\u7136\u53ef\u4ee5\uff0c\u59d1\u5a18\uff0c\u4f60\u5feb\u8bf4\u51fa\u6765\u5427\uff01\u201d \u3000\u3000\u674e\u82b8\u4f9d\u7136\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u65b9\u9053\uff1a\u201c\u59be\u8eab\u5e08\u95e8\u7684\u8fd9\u79cd\u6697\u5668\uff0c\u4e43\u662f\u4e00\u79cd\u6bd2\u706b\u4e4b\u7269\u2026\u2026\u201d \u3000\u3000\u5979\u8bed\u97f3\u672a\u843d\uff0c\u5b8b\u5b5a\u5df2\u5931\u58f0\u9053\uff1a\u201c\u542b\u6bd2\u7684\u706b\u5668\u5417\uff1f\u201d \u3000\u3000\u674e\u82b8\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u76b1\u7709\u63a5\u9053\uff1a\u201c\u77a7\u59d1\u5a18\u8fd9\u7b49\u795e\u6001\uff0c\u53ea\u6015\u90a3\u706b\u5668\u662f\u5341\u5206\u72e0\u6bd2\u7684\u4e86\uff1f\u201d \u3000\u3000\u674e\u82b8\u9053\uff1a\u201c\u6b64\u7269\u4e00\u65e6\u53d1\u51fa\uff0c\u4e24\u4e08\u4e4b\u5185\u7686\u4e3a\u7126\u571f\uff0c\u4e09\u4e08\u4e4b\u5185\u6bd2\u96fe\u5f25\u6f2b\uff0c\u81ea\u7136\u5f53\u5f97\u5341\u5206\u5389\u5bb3\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u542c\u5f97\u5fc3\u4e2d\u5927\u5927\u4e00\u9707\uff0c\u5b89\u5c0f\u840d\u5219\u662f\u7c89\u9762\u4e3a\u4e4b\u5931\u8272\uff01 \u3000\u3000\u663e\u7136\uff0c\u4ed6\u4eec\u5747\u672a\u6599\u5230\u5929\u9b54\u5973\u5c45\u7136\u70bc\u5c31\u4e86\u8fd9\u6b79\u6bd2\u4e4b\u7269\uff0c\u4e00\u65f6\u95f4\u5230\u60f3\u4e0d\u51fa\u6709\u4ec0\u4e48\u5bf9\u4ed8\u4e4b\u6cd5\uff01 \u3000\u3000\u674e\u82b8\u8bdd\u97f3\u4e00\u987f\uff0c\u76ee\u7779\u5b89\u5c0f\u840d\u5f77\u60f6\u6101\u610f\u4e4b\u5bb9\uff0c\u82b3\u5fc3\u5927\u5927\u4e0d\u5b89\uff0c\u7adf\u662f\u4f4e\u58f0\u63a5\u9053\uff1a\u201c\u516c\u5b50\uff0c\u5bb6\u5e08\u8fd9\u70c8\u706b\u795e\u73e0\u867d\u7136\u5389\u5bb3\uff0c\u4f46\u4e5f\u4e3a\u6570\u6781\u5c11\uff0c\u8fde\u5bb6\u5e08\u81ea\u5df1\u8eab\u4e0a\u6240\u85cf\uff0c\u4e00\u5171\u4e5f\u4e0d\u8fc7\u53ea\u6709\u56db\u9897\u800c\u5df2\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u8131\u53e3\u9053\uff1a\u201c\u53ea\u6709\u56db\u9897\uff1f\u201d \u3000 \u7b2c\u4e8c\u5341\u4e09\u56de\u3000\u667a\u53d6\u795e\u73e0 \u3000 \u3000\u3000\u674e\u82b8\u9053\uff1a\u201c\u59be\u4e0d\u6562\u54c4\u9a97\u516c\u5b50\uff0c\u662f\u4ee5\uff0c\u5bb6\u5e08\u5bf9\u6b64\u73e0\u53ef\u8c13\u89c6\u5982\u81f3\u5b9d\u4e00\u822c\uff0c\u975e\u5230\u751f\u6b7b\u5173\u5934\uff0c\u90a3\u662f\u4e0d\u4f1a\u8f7b\u6613\u4f7f\u7528\u7684\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7565\u611f\u5bbd\u5fc3\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u8eab\u4e0a\u6709\u5417\uff1f\u201d \u3000\u3000\u674e\u82b8\u6447\u5934\u9053\uff1a\u201c\u6ca1\u6709\uff01\u4e0d\u8fc7\uff0c\u5927\u5e08\u59ca\uff0c\u548c\u4e8c\u5e08\u59ca\u8eab\u4e0a\u5404\u85cf\u4e86\u4e00\u9897\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u63a5\u9053\uff1a\u201c\u53e6\u5916\u7684\u4e24\u9897\u5462\uff1f\u201d \u3000\u3000\u674e\u82b8\u9053\uff1a\u201c\u5bb6\u5e08\u81ea\u884c\u4fdd\u7ba1\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u8fd9\u65f6\u5ffd\u7136\u7b11\u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u542c\u59d1\u5a18\u4e4b\u8a00\uff0c\u8fd9\u70c8\u706b\u795e\u73e0\u5f53\u771f\u65e0\u4eba\u53ef\u5fa1\u7684\u4e86\u2026\u2026\u4f3c\u8fd9\u7b49\u73cd\u73e0\u4e4b\u7269\uff0c\u59d1\u5a18\u53ef\u5f97\u8981\u4f60\u4e8c\u5e08\u59ca\u5c0f\u5fc3\u6536\u85cf\u8d77\u6765\u554a\u2026\u2026\u201d \u3000\u3000\u674e\u82b8\u7b11\u9053\uff1a\u201c\u4e8c\u5e08\u59ca\u628a\u5b83\u770b\u5f97\u6bd4\u6027\u547d\u8fd8\u91cd\uff0c\u51e0\u4e4e\u8fde\u5b89\u5bdd\u4e4b\u65f6\uff0c\u90fd\u63e3\u5728\u809a\u515c\u4e4b\u5185\u2026\u2026\u201d \u3000\u3000\u60f3\u5fc5\u662f\u5f53\u7740\u4e24\u4e2a\u7537\u4eba\u9762\u524d\uff0c\u8131\u53e3\u8bf4\u51fa\u5185\u8863\u7684\u540d\u79f0\uff0c\u4ee4\u5979\u5927\u611f\u7f9e\u60ed\uff0c\u7c89\u9762\u7ea2\u6655\u4e4d\u73b0\uff0c\u4f4f\u53e3\u4f4e\u4e0b\u5934\u53bb\uff01 \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u6b63\u662f\uff0c\u6b63\u662f\uff0c\u8fd9\u4e48\u8d35\u91cd\u4e4b\u7269\uff0c\u7406\u5e94\u8d34\u8eab\u85cf\u597d\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u53ea\u89c9\u5b8b\u5b5a\u8fd9\u4e9b\u8bdd\u95ee\u5f97\u5b9e\u5728\u6709\u4e9b\u591a\u4f59\uff0c\u7b11\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u4f60\u8001\u771f\u662f\u597d\u591a\u7ba1\u95f2\u4e8b\u5f97\u5f88\uff0c\u4eba\u5bb6\u628a\u4e1c\u897f\u6536\u85cf\u5f97\u9690\u4e0d\u9690\u79d8\uff0c\u4e0e\u4f60\u4ec0\u4e48\u76f8\u5e72\u561b\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u8fde\u58f0\u7b11\u9053\uff1a\u201c\u662f\u554a\uff0c\u8001\u673d\u4e0a\u4e86\u51e0\u5c81\u5e74\u7eaa\uff0c\u8bf4\u8bdd\u514d\u4e0d\u4e86\u6709\u4e9b\u98a0\u4e09\u5012\u56db\u7684\u4e86\uff0c\u8001\u5f1f\u6559\u8bad\u5f97\u4e0d\u9519\u2026\u2026\u201d\u8bdd\u97f3\u4e00\u987f\uff0c\u7adf\u7136\u5927\u7b11\u4e86\u4e09\u58f0\uff01 \u3000\u3000\u4ed6\u8fd9\u4e00\u7b11\uff0c\u53ef\u628a\u5b89\u5c0f\u840d\u8ddf\u674e\u82b8\u5168\u90e8\u7b11\u6123\u4e86\uff01 \u3000\u3000\u674e\u82b8\u5446\u5446\u5730\u671b\u7740\u4ed6\uff0c\u5140\u81ea\u7eb3\u95f7\uff0c\u4f46\u5b89\u5c0f\u840d\u5374\u5728\u4e00\u6123\u4e4b\u540e\uff0c\u5ffd\u7136\u5fc3\u4e2d\u5927\u5927\u4e00\u9707\uff0c\u6697\u9053\uff1a \u3000\u3000\u77a7\u4ed6\u8fd9\u7b49\u5f97\u610f\u7684\u795e\u60c5\uff0c\u5fc5\u7136\u662f\u4ec0\u4e48\u9ad8\u5174\u7684\u4e8b\uff0c\u4f46\u8fd9\u9877\u523b\u4e4b\u95f4\uff0c\u4ed6\u53c8\u80fd\u6709\u7740\u4ec0\u4e48\u53ef\u559c\u4e4b\u4e8b\u2026\u2026 \u3000\u3000\u84e6\u7136\u8111\u9645\u7075\u5149\u4e00\u73b0\uff0c\u5931\u58f0\u9053\uff1a\u201c\u662f\u4e86\u2026\u2026\u201d \u3000\u3000\u674e\u82b8\u88ab\u4ed6\u8bf4\u5f97\u4e00\u60ca\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5bf9\u5979\u7b11\u4e86\u4e00\u7b11\uff0c\u90a3\u53e5\u201c\u5b8b\u8001\u8981\u5077\u4f60\u4eec\u7684\u795e\u73e0\u201d\u51e0\u5b57\u9669\u4e9b\u8131\u53e3\u8bb2\u4e86\u51fa\u6765\uff01 \u3000\u3000\u4e0d\u8fc7\uff0c\u5979\u5341\u5206\u673a\u8b66\uff0c\u8bdd\u5230\u53e3\u8fb9\u5374\u541e\u4e86\u56de\u53bb\uff0c\u6539\u53e3\u9053\uff1a\u201c\u5728\u4e0b\u5ffd\u7136\u60f3\u901a\u4e86\u65b9\u5144\u7684\u5251\u6cd5\u5965\u5999\uff0c\u4e5f\u8bb8\u80fd\u591f\u514b\u5236\u90a3\u70c8\u706b\u795e\u73e0\uff0c\u662f\u4ee5\u5931\u58f0\u5927\u53eb\uff0c\u59d1\u5a18\u53ef\u83ab\u8981\u89c1\u7b11\uff01\u201d \u3000\u3000\u674e\u82b8\u7b11\u9053\uff1a\u201c\u59be\u8eab\u600e\u6562\u2026\u2026\u201d\u5b9e\u5219\u5979\u6b64\u523b\u53ef\u662f\u5341\u5206\u5feb\u6170\uff0c\u56e0\u4e3a\u5b89\u5c0f\u840d\u8fd8\u662f\u89c1\u9762\u4ee5\u6765\uff0c\u5934\u4e00\u906d\u8ddf\u5979\u7b11\u7740\u8bf4\u8bdd\uff0c\u4f46\u5979\u8bdd\u97f3\u4e00\u987f\uff0c\u5374\u53c8\u63a5\u9053\uff1a\u201c\u516c\u5b50\uff0c\u90a3\u65b9\u516c\u5b50\u7684\u5251\u6cd5\uff0c\u5f53\u771f\u8db3\u4ee5\u514b\u5236\u70c8\u706b\u795e\u73e0\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u672c\u662f\u4fe1\u53e3\u80e1\u8bf4\uff0c\u5979\u600e\u77e5\u90a3\u5251\u6cd5\u80fd\u4e0d\u80fd\u514b\u5236\u795e\u73e0\u3002\u8fd9\u65f6\u5374\u50cf\u715e\u6709\u4ecb\u4e8b\u5730\u7b11\u9053\uff1a \u3000\u3000\u201c\u5f53\u7136\u80fd\uff0c\u4e0d\u8fc7\uff0c\u7a76\u7adf\u662f\u8981\u7528\u54ea\u4e00\u62db\uff0c\u5728\u4e0b\u8fd8\u4e0d\u6562\u53d6\u4ee3\u4ed6\u51b3\u5b9a\uff01\u201d \u3000\u3000\u674e\u82b8\u6b63\u60f3\u518d\u95ee\u4e0b\u53bb\uff0c\u5ffd\u7136\u542c\u5f97\u5b8b\u5b5a\u5927\u58f0\u9053\uff1a\u201c\u5b89\u8001\u5f1f\uff0c\u4f60\u770b\u65b9\u8001\u5f1f\u8fd9\u4e00\u62db\u5251\u6cd5\u662f\u5426\u6709\u4e9b\u4e0d\u540c\u4e86\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6b63\u5728\u5f04\u5f97\u5c40\u4fc3\u65e0\u5948\u4e4b\u9645\uff0c\u5b8b\u5b5a\u8fd9\u4e00\u58f0\u5927\u559d\uff0c\u53ef\u5f53\u771f\u4f7f\u5979\u5982\u9047\u5927\u8d66\uff0c\u5fd9\u9053\uff1a \u3000\u3000\u201c\u54ea\u91cc\u4e0d\u540c\u4e86\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u53cd\u5b88\u4e3a\u653b\uff0c\u81ea\u7136\u662f\u5927\u4e0d\u76f8\u540c\u7684\u4e86\uff01\u201d \u3000\u3000\u539f\u6765\u6b64\u65f6\u65b9\u96ea\u5b9c\u5f53\u771f\u4e0d\u518d\u662f\u50cf\u5148\u524d\u90a3\u822c\u6253\u6cd5\uff0c\u800c\u662f\u62a2\u5360\u5148\u673a\uff0c\u4e00\u53e3\u6c14\u653b\u51fa\u4e86\u4e09\u5251\u4e4b\u591a\uff01 \u3000\u3000\u8fd9\u4e09\u5251\u51fa\u624b\u7684\u65b9\u4f4d\uff0c\u5404\u6709\u4e0d\u540c\uff0c\u4f46\u5251\u52bf\u4e4b\u5feb\uff0c\u53ea\u628a\u90a3\u5728\u4e00\u65c1\u89c2\u6218\u7684\u674e\u82b8\uff0c\u60ca\u5f97\u51e0\u4e4e\u53eb\u4e86\u8d77\u6765\u3002 \u3000\u3000\u6562\u60c5\uff0c\u8fd9\u4e09\u62db\u624b\u6cd5\uff0c\u6b63\u662f\u5979\u5e08\u7236\u7684\u5251\u6cd5\uff0c\u4e0d\u77e5\u90a3\u65b9\u96ea\u5b9c\u53c8\u600e\u4f1a\u65bd\u5c55\u5f97\u5982\u6b64\u7eaf\u719f\uff1f \u3000\u3000\u5446\u4e86\u4e00\u5446\u4e4b\u4e0b\uff0c\u4e0d\u7981\u8131\u53e3\u9053\uff1a\u201c\u8fd9\u65b9\u516c\u5b50\u2026\u2026\u600e\u5730\u4f1a\u59be\u8eab\u5e08\u95e8\u7684\u5251\u6cd5\u5462\u5462\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u3001\u5b8b\u5b5a\u65e0\u8a00\uff0c\u53ea\u662f\u5fae\u5fae\u5730\u7b11\u4e86\u4e00\u7b11\uff0c\u5012\u662f\u90a3\u8eab\u53d7\u65b9\u96ea\u5b9c\u4e09\u62db\u5feb\u5251\u653b\u7684\u6709\u4e9b\u624b\u5fd9\u811a\u4e71\uff0c\u8fde\u9000\u4e94\u6b65\u7684\u5c39\u68a6\u8377\uff0c\u8fd9\u65f6\u5374\u5931\u58f0\u53eb\u9053\uff1a\u201c\u65b9\u516c\u5b50\uff0c\u4f60\u8fd9\u662f\u4ec0\u4e48\u5251\u6cd5\uff1f\u59be\u8eab\u77a7\u7684\u773c\u719f\u5f97\u5f88\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e09\u62db\u653b\u51fa\uff0c\u4fbf\u81ea\u6a2a\u5251\u4e0d\u8fdb\uff0c\u95fb\u8a00\u5927\u7b11\u9053\uff1a\u201c\u5929\u9b54\u5251\uff0c\u4f60\u4eec\u6ca1\u6709\u542c\u8bf4\u8fc7\u5417\uff1f\u201d \u3000\u3000\u5b9e\u5219\u5929\u9b54\u8fd9\u5957\u5251\u6cd5\uff0c\u5374\u662f\u53e6\u5916\u6709\u4e2a\u540d\u79f0\uff0c\u53eb\u505a\u6563\u82b1\u5251\uff0c\u65b9\u96ea\u5b9c\u672c\u4e0d\u77e5\u9053\uff0c\u8fd9\u65f6\u987a\u53e3\u8bf4\u51fa\u8fd9\u5957\u5251\u6cd5\u53eb\u505a\u5929\u9b54\u5251\uff0c\u4e0d\u7981\u4f7f\u5f97\u5c39\u68a6\u8377\u548c\u674e\u82b8\u5168\u90fd\u4e3a\u4e4b\u4e00\u6014\u3002 \u3000\u3000\u5c31\u5728\u5979\u4eec\u4e00\u6014\u4e4b\u95f4\uff0c\u5b8b\u5b5a\u5df2\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u9519\u554a\uff0c\u8fd9\u5957\u5251\u6cd5\u6b63\u662f\u5929\u9b54\u5251\uff0c\u4e13\u95e8\u514b\u5236\u4f60\u4eec\u5929\u9b54\u5973\u4e00\u95e8\u4eba\u7269\uff0c\u90a3\u4f4d\u5c39\u59d1\u5a18\uff0c\u4f60\u53ef\u8981\u5f53\u5fc3\u4e86\uff01\u201d \u3000\u3000\u5c39\u68a6\u8377\u88ab\u4ed6\u8fd9\u4e48\u4e00\u8bf4\uff0c\u5012\u6fc0\u8d77\u4e86\u5fc3\u4e2d\u7684\u6012\u610f\uff0c\u5931\u58f0\u9053\uff1a\u201c\u8981\u4f60\u7ba1\uff0c\u4f60\u6700\u597d\u8bf4\u8bdd\u5c0f\u5fc3\u4e00\u70b9\u2026\u2026\u201d \u3000\u3000\u56de\u624b\u4e00\u5251\uff0c\u4fbf\u5411\u65b9\u96ea\u5b9c\u653b\u6765\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5251\u7709\u4e00\u626c\uff0c\u7b11\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u5929\u9b54\u5251\u53ea\u6015\u514b\u5236\u4e0d\u4e86\u5979\u4eec\u5462\uff01\u201d\u5237\u5237\u4e24\u5251\uff0c\u5c06\u90a3\u5c39\u68a6\u8377\u518d\u5ea6\u8feb\u9000\u3002 \u3000\u3000\u5c39\u68a6\u8377\u8fd9\u4e00\u4f1a\u513f\u53ef\u662f\u77a7\u771f\u5207\u4e86\uff01\u5979\u5446\u4e86\u4e00\u5446\uff0c\u559d\u9053\uff1a\u201c\u4f4f\u624b\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9000\u4e86\u4e00\u6b65\uff0c\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u6709\u4ec0\u4e48\u89c1\u6559\uff1f\u201d \u3000\u3000\u5c39\u68a6\u8377\u51b7\u51b7\u5730\u76ef\u7740\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4f60\u4f7f\u7684\u8fd9\u662f\u4ec0\u4e48\u5251\u6cd5\uff0c\u8c01\u4f20\u7ed9\u4f60\u7684\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u81ea\u5931\u7b11\uff0c\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u8fd9\u5957\u5251\u6cd5\u7684\u540d\u79f0\uff0c\u533a\u533a\u4e0d\u662f\u5df2\u7136\u8bf4\u8fc7\u4e86\u5417\uff1f\u53eb\u505a\u5929\u9b54\u5251\u554a\uff01\u4e0d\u8fc7\u2026\u2026\u201d\u4ed6\u5ffd\u7136\u54c8\u54c8\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u81f3\u4e8e\u662f\u4f55\u4eba\u542c\u6388\uff0c\u533a\u533a\u8fd8\u662f\u4e0d\u8bf4\u51fa\u6765\u7684\u597d\uff01\u201d \u3000\u3000\u5c39\u68a6\u8377\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8bf4\u51fa\u6765\u4e86\uff0c\u53ea\u6015\u4f60\u4e5f\u5948\u4f55\u4ed6\u4e0d\u4e86\uff01\u201d \u3000\u3000\u5c39\u68a6\u8377\u9053\uff1a\u201c\u59be\u8eab\u4e0d\u4fe1\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u4e0d\u4fe1\uff1f\u533a\u533a\u4e0d\u8fc7\u662f\u60f3\u4e3a\u59d1\u5a18\u4fdd\u5168\u9762\u5b50\u5462\uff01\u201d \u3000\u3000\u4ed6\u8fd9\u4e48\u4e00\u8bb2\uff0c\u5c39\u68a6\u8377\u8d8a\u53d1\u5730\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u65b9\u516c\u5b50\uff0c\u4f60\u6700\u597d\u83ab\u8981\u6545\u4f5c\u60ca\u4eba\u4e4b\u8c08\uff0c\u59be\u8eab\u53ef\u4e5f\u662f\u7ecf\u8fc7\u5927\u98ce\u5927\u6d6a\u7684\u4eba\uff0c\u5c31\u51ed\u516c\u5b50\u8fd9\u51e0\u53e5\u8bdd\uff0c\u90a3\u53ef\u662f\u5413\u4e0d\u5012\u59be\u8eab\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u662f\u5417\uff1f\u533a\u533a\u5012\u662f\u5c11\u89c1\u591a\u602a\u4e86\uff01\u201d \u3000\u3000\u8bed\u97f3\u4e00\u843d\uff0c\u53c8\u662f\u4e00\u5251\u523a\u4e86\u51fa\u53bb\uff0c\u540c\u65f6\u53e3\u4e2d\u559d\u9053\uff1a\u201c\u59d1\u5a18\u53ef\u8bc6\u5f97\u8fd9\u4e00\u62db\uff1f\u201d \u3000\u3000\u5c39\u68a6\u8377\u51b7\u54fc\u9053\uff1a\u201c\u600e\u7684\u4e0d\u8bc6\uff1f\u6b64\u4e43\u672c\u95e8\u6563\u82b1\u5251\u4e2d\u7684\u7b2c\u4e8c\u5341\u62db\u2018\u5bd2\u5858\u6708\u5f71\u2019\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u539f\u6765\u5929\u9b54\u5973\u7684\u8fd9\u4e00\u5957\u5251\u6cd5\u53eb\u505a\u6563\u82b1\u5251\uff0c\u679c\u7136\u6709\u4e9b\u9053\u7406\uff0c\u6bcb\u602a\u6bcf\u4e00\u5251\u6d12\u51fa\uff0c\u90fd\u6709\u5982\u98d8\u98d8\u6b32\u98de\u4e00\u822c\u2026\u2026\u201d \u3000\u3000\u5fc3\u4e2d\u867d\u5728\u8f6c\u5ff5\uff0c\u4e2d\u4e2d\u5374\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u4e00\u62db\u679c\u7136\u662f\u5bd2\u5858\u6708\u5f71\uff0c\u53ea\u662f\uff0c\u5374\u662f\u533a\u533a\u5929\u9b54\u5251\u4e2d\u7684\u7b2c\u4e8c\u5341\u4e8c\u62db\uff0c\u770b\u6765\u59d1\u5a18\u8fd8\u662f\u8bb0\u9519\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fc5\u5feb\u5730\u987a\u53e3\u8bf4\u51fa\uff0c\u5012\u53eb\u90a3\u5c39\u68a6\u8377\u53c8\u662f\u4e00\u6014\u3002 \u3000\u3000\u674e\u82b8\u5ffd\u7136\u5728\u65c1\u5927\u53eb\u9053\uff1a\u201c\u4e8c\u5e08\u59d0\uff0c\u54b1\u4eec\u4e0a\u4e86\u90a3\u65b9\u516c\u5b50\u7684\u5f53\u4e86\uff01\u201d \u3000\u3000\u5c39\u68a6\u8377\u76b1\u7709\u9053\uff1a\u201c\u4e0a\u4e86\u4ec0\u4e48\u5f53\uff1f\u201d \u3000\u3000\u674e\u82b8\u9053\uff1a\u201c\u8fd9\u660e\u660e\u662f\u54b1\u4eec\u5e08\u5085\u5251\u6cd5\uff0c\u65b9\u516c\u5b50\u786c\u8bf4\u4e0d\u662f\uff0c\u53ea\u6015\u2026\u2026\u53ea\u6015\u2026\u2026\u201d\u5979\u5ffd\u7136\u4e0d\u6562\u5f80\u4e0b\u8bf4\u53bb\u3002 \u3000\u3000\u5c39\u68a6\u8377\u9053\uff1a\u201c\u53ea\u6015\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u674e\u82b8\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u624d\u9053\uff1a\u201c\u53ea\u6015\u2026\u2026\u4e8c\u5e08\u59ca\u5c31\u662f\u4f20\u6388\u8fd9\u5957\u5251\u6cd5\u7684\u4eba\u5462\uff1f\u201d \u3000\u3000\u5c39\u68a6\u8377\u5ffd\u7136\u5a07\u5bb9\u60e8\u53d8\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026\u82b8\u59b9\uff0c\u8fd9\u8bdd\u4ece\u4f55\u8bf4\u8d77\u7684\u554a\uff1f\u201d \u3000\u3000\u674e\u82b8\u9053\uff1a\u201c\u5c0f\u59b9\u4ee5\u4e3a\u2026\u2026\u65b9\u516c\u5b50\u662f\u4ece\u4f60\u5b66\u7684\u672c\u95e8\u6563\u82b1\u5251\u6cd5\uff01\u201d \u3000\u3000\u5c39\u68a6\u8377\u5927\u58f0\u9053\uff1a\u201c\u600e\u4e48\u4f1a\uff1f\u6211\u2026\u2026\u4e0e\u65b9\u516c\u5b50\u6839\u672c\u4ece\u672a\u89c1\u8fc7\u9762\uff0c\u53c8\u600e\u4f1a\u4f20\u6388\u4e86\u4ed6\u7684\u5251\u6cd5\uff1f\u201d \u3000\u3000\u5979\u5ffd\u7136\u4f4e\u5934\u60f3\u4e86\u4e00\u60f3\uff0c\u683c\u683c\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u662f\u4e86\uff0c\u6211\u660e\u767d\u4e86\uff01\u201d \u3000\u3000\u674e\u82b8\u9053\uff1a\u201c\u4e8c\u59ca\u660e\u767d\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c39\u68a6\u8377\u9053\uff1a\u201c\u516b\u6210\u8fd9\u4f20\u5251\u7684\u4eba\u4e0d\u662f\u6211\uff0c\u800c\u662f\u56db\u5e08\u59b9\u4f60\u5427\uff01\u201d \u3000\u3000\u674e\u82b8\u53ea\u542c\u5f97\u5927\u5927\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4e8c\u59ca\uff0c\u4f60\u600e\u4f1a\u6709\u4e86\u8fd9\u7b49\u60f3\u6cd5\u5462\uff1f\u5c0f\u59b9\u4e0e\u65b9\u516c\u5b50\u4e00\u5171\u6ca1\u6709\u8bb2\u8fc7\u51e0\u53e5\u8bdd\uff0c\u54ea\u91cc\u5c31\u80fd\u4f20\u4e86\u4ed6\u7684\u5e08\u95e8\u5251\u6cd5\uff1f\u201d \u3000\u3000\u5c39\u68a6\u8377\u51b7\u7b11\u9053\uff1a\u201c\u662f\u4e48\uff1f\u90a3\u6211\u5462\uff1f\u4e0d\u4e5f\u662f\u6ca1\u6709\u8ddf\u65b9\u516c\u5b50\u8bb2\u8fc7\u51e0\u53e5\u8bdd\u5417\uff1f\u800c\u4e14\uff0c\u8fd9\u662f\u56db\u59b9\u4f60\u4eb2\u773c\u89c1\u5230\u7684\u5440\uff01\u201d \u3000\u3000\u674e\u82b8\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8fd9\u5c31\u602a\u4e86\uff0c\u90a3\u662f\u2026\u2026\u201d \u3000\u3000\u5979\u8bed\u97f3\u672a\u5df2\uff0c\u5b89\u5c0f\u840d\u5ffd\u7136\u4f4e\u58f0\u9053\uff1a\u201c\u674e\u59d1\u5a18\uff0c\u8fd9\u5957\u5251\u6cd5\u5c31\u662f\u4f60\u4e8c\u5e08\u59ca\u4f20\u7684\uff01\u201d \u3000\u3000\u674e\u82b8\u76b1\u7709\u9053\uff1a\u201c\u516c\u5b50\uff0c\u59be\u8eab\u53ef\u7cca\u6d82\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u8fd9\u6709\u4ec0\u4e48\u597d\u7cca\u6d82\u7684\uff1f\u9002\u624d\u4f60\u4e8c\u5e08\u59ca\u4e0d\u662f\u5df2\u7136\u7528\u8fd9\u5957\u5251\u6cd5\u8ddf\u65b9\u5144\u6253\u8fc7\u4e00\u9635\u4e86\u5417\uff1f\u8c01\u77e5\u9053\u5979\u662f\u771f\u7684\u8fd8\u662f\u5047\u7684\uff1f\u4f9d\u6211\u770b\uff0c\u5979\u5c31\u662f\u5728\u5077\u6109\u5730\u628a\u90a3\u5957\u5251\u6cd5\u4f20\u7ed9\u4e86\u65b9\u5144\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u773c\u89c1\u4e8c\u5973\u5bf9\u7b54\u4e4b\u8a00\uff0c\u77e5\u9053\u5979\u4eec\u5f7c\u6b64\u6b63\u5728\u63a8\u8bff\u8d23\u4efb\uff0c\u60f3\u5fc5\u5929\u9b54\u5973\u5bf9\u6563\u82b1\u5251\u6cd5\u751a\u662f\u91cd\u89c6\uff0c\u4e0d\u662f\u81ea\u5df1\u5f1f\u5b50\uff0c\u51b3\u4e0d\u5bb9\u5176\u4e60\u7ec3\uff0c\u662f\u4ee5\uff0c\u4e8c\u5973\u624d\u4f1a\u4e0d\u6562\u627f\u5f53\u8fd9\u7b49\u8d23\u4efb\uff0c\u5979\u8ba1\u4e0a\u5fc3\u6765\uff0c\u4fbf\u9f13\u52a8\u674e\u82b8\uff0c\u8981\u5979\u63ed\u7a7f\u4e8b\u5b9e\u3002 \u3000\u3000\u5176\u5b9e\uff0c\u674e\u82b8\u4e5f\u60f3\u5230\u4e86\u8fd9\u4e00\u70b9\uff0c\u53ea\u662f\u4e0d\u6562\u80af\u5b9a\uff0c\u6b64\u523b\u542c\u5f97\u5b89\u5c0f\u840d\u8fd9\u4e48\u8bf4\uff0c\u4e0d\u7531\u5f97\u6709\u4e86\u52c7\u6c14\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4e8c\u59ca\uff0c\u5c0f\u59b9\u5df2\u7ecf\u77e5\u9053\u65b9\u516c\u5b50\u7684\u5251\u6cd5\uff0c\u662f\u600e\u751f\u5b66\u6765\u7684\u4e86\uff01\u201d \u3000\u3000\u5c39\u68a6\u8377\u9053\uff1a\u201c\u4f60\u79c1\u76f8\u6388\u53d7\u4e8e\u4ed6\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u65b9\u96ea\u5b9c\u5374\u4ed7\u5251\u800c\u7acb\uff0c\u51b7\u7b11\u5730\u77a7\u7740\u4e8c\u5973\uff0c\u770b\u5979\u5f7c\u6b64\u6697\u6597\uff0c\u5fc3\u4e2d\u5374\u5728\u53d1\u5671\u4e0d\u5df2\uff01 \u3000\u3000\u674e\u82b8\u95fb\u8a00\uff0c\u6447\u4e86\u6447\u5934\uff0c\u7b11\u9053\uff1a\u201c\u4e0d\u662f\u5c0f\u59b9\uff0c\u800c\u662f\u4e8c\u59ca\u4f60\u4f20\u7ed9\u4ed6\u7684\uff01\u201d \u3000\u3000\u5c39\u68a6\u8377\u5ffd\u7136\u683c\u683c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\u554a\uff0c\u56db\u59b9\u4e5f\u5b66\u4f1a\u4e86\u72e1\u8bc8\u4e86\u5417\uff1f\u53ea\u6015\u89c1\u4e86\u5e08\u7236\uff0c\u5c31\u5bb9\u4e0d\u5f97\u4f60\u62b5\u8d56\u4e86\uff01\u201d \u3000\u3000\u674e\u82b8\u4e5f\u5ae3\u7136\u4e00\u7b11\u9053\uff1a\u201c\u4e8c\u59ca\uff0c\u6709\u4e00\u4ef6\u4e8b\u4f60\u53ef\u83ab\u5fd8\u8bb0\u4e86\uff01\u201d \u3000\u3000\u5c39\u68a6\u8377\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u674e\u82b8\u9053\uff1a\u201c\u8fd9\u5957\u6563\u82b1\u5251\u6cd5\uff0c\u5c0f\u59b9\u9002\u624d\u4eb2\u773c\u89c1\u5230\u4f60\u4e00\u8fde\u65bd\u5c55\u4e86\u4e24\u6b21\uff0c\u7ed3\u679c\u5e76\u672a\u5c06\u65b9\u516c\u5b50\u51fb\u8d25\uff01\u201d \u3000\u3000\u5c39\u68a6\u8377\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u90a3\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d \u3000\u3000\u674e\u82b8\u9053\uff1a\u201c\u5f53\u7136\u6709\u5173\u7cfb\uff01\u56e0\u4e3a\u2026\u2026\u8c01\u80fd\u77e5\u9053\u4f60\u4e0d\u662f\u501f\u8fd9\u673a\u4f1a\uff0c\u628a\u6563\u82b1\u5251\u6cd5\u4f20\u7ed9\u4e86\u65b9\u516c\u5b50\u5462\uff1f\u201d \u3000\u3000\u5c39\u68a6\u8377\u7c89\u8138\u4e4b\u4e0a\u5ffd\u5730\u7f69\u4e0a\u5bd2\u971c\uff0c\u6012\u9053\uff1a\u201c\u56db\u59b9\uff0c\u4f60\u8fd9\u7b49\u80e1\u8bf4\u4e0d\u6015\u5e08\u7236\u964d\u7f6a\u5417\uff1f\u201d \u3000\u3000\u674e\u82b8\u9053\uff1a\u201c\u5c0f\u59b9\u8bf4\u7684\u672c\u662f\u5b9e\u60c5\u561b\uff0c\u5c31\u662f\u544a\u8bc9\u5e08\u7236\uff0c\u90a3\u4e5f\u6ca1\u6709\u4ec0\u4e48\u5173\u7cfb\uff01\u201d \u3000\u3000\u5c39\u68a6\u8377\u67f3\u7709\u4e00\u76b1\uff0c\u8d2c\u52a8\u7740\u5927\u773c\uff0c\u9053\uff1a\u201c\u56db\u59b9\uff0c\u4f60\u8bf4\u8fd9\u8bdd\u53ef\u5f97\u8981\u6709\u8bc1\u636e\u624d\u884c\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u7a74\u8fd9\u65f6\u84e6\u5730\u5927\u7b11\u9053\uff1a\u201c\u6709\uff01\u674e\u59d1\u5a18\u7684\u8bdd\uff0c\u6ca1\u6709\u8bf4\u9519\uff0c\u4e5f\u8bb8\u533a\u533a\u5c31\u613f\u610f\u4e3a\u5979\u4f5c\u8bc1\uff01\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u84e6\u7684\u53c8\u662f\u4e00\u5251\u523a\u53bb\uff0c\u6309\u9053\uff1a\u201c\u8fd9\u4e00\u62db\uff0c\u59d1\u5a18\u4e5f\u4e0d\u751f\u758f\u5427\uff1f\u201d \u3000\u3000\u5c39\u68a6\u8377\u4e00\u62e7\u5a07\u8eaf\uff0c\u9000\u4e86\u4e00\u6b65\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u6563\u5316\u5251\u6cd5\u4e2d\u7684\u4e09\u5341\u4e5d\u62db\u2018\u82b1\u6708\u4e89\u8f89\u2019\uff0c\u59be\u8eab\u5f53\u7136\u77e5\u9053\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u4f60\u77e5\u9053\u5c31\u597d\uff01\u4e0d\u8fc7\uff0c\u8fd9\u5728\u5929\u9b54\u5251\u4e2d\uff0c\u5374\u662f\u7b2c\u5341\u4e00\u62db\u4e86\uff01\u201d \u3000\u3000\u5c39\u68a6\u8377\u4e00\u6014\uff1b\u9053\uff1a\u201c\u65b9\u516c\u5b50\uff0c\u4f60\u2026\u2026\u8fd9\u5957\u5251\u6cd5\u5f53\u771f\u53eb\u505a\u5929\u9b54\u5251\u5417\uff1f\u201d \u3000\u3000\u6562\u60c5\uff0c\u8fde\u5c39\u68a6\u8377\u4e5f\u88ab\u4ed6\u5f04\u7cca\u6d82\u4e86\uff01 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u65b9\u96ea\u5b9c\u7684\u5251\u62db\uff0c\u6839\u672c\u5c31\u662f\u5e08\u95e8\u6563\u82b1\u5251\uff0c\u4f46\u4ed6\u504f\u8bf4\u662f\u4ec0\u4e48\u5929\u9b54\u5251\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u5012\u53eb\u5979\u65e0\u6cd5\u51b3\u65ad\u4e86\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u8fdb\u70b9\u5934\u4e00\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff01\u59d1\u5a18\u53ef\u662f\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u5c39\u68a6\u8377\u5f53\u7136\u4e0d\u4f1a\u76f8\u4fe1\uff0c\u5f53\u4e0b\u63a5\u9053\uff1a\u201c\u516c\u5b50\uff0c\u8fd9\u5929\u9b54\u5251\u4e00\u5171\u6709\u591a\u5c11\u62db\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u4e2a\u5417\uff1f\u5927\u6982\u4e94\u5341\u62db\u5427\uff01\u201d \u3000\u3000\u5c39\u68a6\u8377\u5fc3\u4e2d\u4e00\u52a8\uff0c\u63a5\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4f60\u53ef\u5426\u65bd\u5c55\u4e00\u904d\uff0c\u8ba9\u59be\u8eab\u5f00\u5f00\u773c\u754c\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5927\u7b11\u9053\uff1a\u201c\u6709\u4f55\u4e0d\u53ef\uff1f\u201d \u3000\u3000\u957f\u5251\u4e00\u6325\uff0c\u6b63\u5f85\u523a\u51fa\uff0c\u5ffd\u7136\u6447\u4e86\u4e00\u6447\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u6210\u554a\uff0c\u533a\u533a\u5ffd\u7136\u60f3\u8d77\u6765\u4e86\uff0c\u5018\u5982\u5c39\u59d1\u5a18\u5b58\u5fc3\u60f3\u7aa5\u7834\u533a\u533a\u5251\u6cd5\u4e2d\u7684\u5965\u5999\uff0c\u6211\u4e0d\u662f\u5403\u4e8f\u4e86\u5417\uff1f\u201d \u3000\u3000\u5c39\u68a6\u8377\u88ab\u4ed6\u8bf4\u5f97\u4e00\u6123\uff0c\u8131\u53e3\u9053\uff1a\u201c\u59be\u8eab\u5c82\u662f\u90a3\u7b49\u4eba\uff1f\u516c\u5b50\u653e\u5fc3\u65bd\u4e3a\u5427\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u5bb3\u4eba\u4e4b\u5fc3\u4e0d\u53ef\u6709\uff0c\u9632\u4eba\u4e4b\u5fc3\u4e0d\u53ef\u65e0\uff0c\u533a\u533a\u5012\u662f\u6709\u4e86\u4e2a\u53d8\u901a\u6cd5\uff0c\u8fd9\u4e48\u529e\uff0c\u533a\u533a\u65bd\u5c55\u5251\u6cd5\u4e4b\u65f6\uff0c\u4e0d\u4f9d\u987a\u5e8f\uff0c\u800c\u662f\u5012\u5e8f\u800c\u5ba3\uff0c\u59d1\u5a18\u540c\u610f\u5417\uff1f\u201d \u3000\u3000\u5c39\u68a6\u8377\u8fd9\u65f6\u4e00\u5fc3\u53ea\u60f3\u77a7\u77a7\u8fd9\u5957\u5251\u6cd5\u662f\u4e0d\u662f\u6563\u82b1\u5251\uff0c\u7ba1\u4ed6\u987a\u8fd8\u662f\u5012\u5462\uff0c\u5f53\u4e0b\u63a5\u9053\uff1a \u3000\u3000\u201c\u968f\u516c\u5b50\u7684\u610f\u4fbf\u662f\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u5012\u662f\u901a\u60c5\u8fbe\u7406\u5f97\u5f88\u2026\u2026\u201d \u3000\u3000\u4ed6\u4e00\u987a\u624b\u4e2d\u957f\u5251\uff0c\u500f\u5730\u523a\u51fa\u4e00\u52bf\uff0c\u6309\u9053\uff1a\u201c\u4e24\u4f4d\u59d1\u5a18\u770b\u4ed4\u7ec6\u4e86\u3002\u201d \u3000\u3000\u53ea\u89c1\u4ed6\u5251\u52bf\u4e00\u8d77\uff0c\u5237\u5237\u4e4b\u58f0\uff0c\u4e0d\u7edd\u4e8e\u8033\uff01 \u3000\u3000\u5c39\u68a6\u8377\u3001\u674e\u82b8\u4e09\u5973\u53ea\u77a7\u5f97\u82b3\u5fc3\u72c2\u9707\uff0c\u6574\u4e2a\u7684\u4eba\u90fd\u5168\u7ed9\u6123\u5f80\u4e86\uff01 \u3000\u3000\u8fd9\u54ea\u91cc\u662f\u4ec0\u4e48\u5929\u9b54\u5251\uff1f \u3000\u3000\u91cc\u9762\u7684\u4e00\u62db\u4e00\u5f0f\uff0c\u5168\u90fd\u662f\u5e08\u5085\u5251\u6cd5\u201c\u6563\u82b1\u5251\u201d\u561b\u3002 \u3000\u3000\u5018\u82e5\u771f\u5982\u65b9\u96ea\u5b9c\u6240\u8bf4\uff0c\u8fd9\u4e00\u5957\u5929\u9b54\u5251\u4e43\u662f\u5012\u5e8f\u65bd\u5c55\uff0c\u90a3\u5c82\u4e0d\u5c31\u662f\u8bf4\uff0c\u5929\u9b54\u5251\u53ea\u4e2a\u8fc7\u662f\u5c06\u90a3\u6563\u82b1\u5251\u53cd\u8fc7\u6765\u53d1\u62db\u800c\u5df2\u5417\uff1f\u201d \u3000\u3000\u4e8c\u5973\u8d8a\u770b\u8d8a\u5fc3\u60ca\uff0c\u5c39\u68a6\u8377\u7684\u8138\u4e0a\uff0c\u5df2\u7136\u6c81\u51fa\u4e86\u9999\u6c57\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5b8b\u5b5a\u53ef\u662f\u5c0f\u5fc3\u5f97\u5f88\uff0c\u4ed6\u4e0d\u4f46\u5168\u8eab\u8d2f\u6ce8\u5728\u5c39\u68a6\u8377\u8eab\u4e0a\uff0c\u800c\u4e14\uff0c\u66f4\u6084\u6084\u5730\u8d77\u8eab\uff0c\u5411\u5c39\u68a6\u8377\u7acb\u8eab\u4e4b\u5904\uff0c\u8d70\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u540c\u65f6\uff0c\u53ea\u89c1\u4ed6\u5634\u4e2d\u5ff5\u5ff5\u6709\u8bcd\uff0c\u5374\u6ca1\u6709\u58f0\u97f3\u51fa\u6765\u3002 \u3000\u3000\u4f46\u90a3\u6b63\u5728\u5168\u529b\u65bd\u5c55\u5012\u7740\u6b21\u5e8f\u65bd\u5c55\u6563\u82b1\u5251\u7684\u65b9\u96ea\u5b9c\uff0c\u8033\u4e2d\u5374\u4f20\u6765\u4e86\u5b8b\u5b5a\u7684\u4f20\u97f3\u4e4b\u58f0\uff0c\u9053\uff1a\u201c\u65b9\u8001\u5f1f\uff0c\u90a3\u5c39\u68a6\u8377\u8eab\u4e0a\u85cf\u7740\u4e00\u652f\u5a01\u529b\u6781\u5927\u70c8\u706b\u795e\u73e0\uff0c\u636e\u8bf4\u6b64\u7269\u51fa\u624b\uff0c\u4e24\u4e08\u65b9\u5706\u65e0\u7269\u53ef\u514d\uff0c\u4f60\u5251\u6cd5\u65bd\u6bd5\uff0c\u4e0d\u59a8\u8d81\u52bf\u7ed9\u90a3\u5996\u5973\u4e00\u5251\uff0c\u8feb\u5f97\u5979\u5411\u8001\u592b\u8eab\u524d\u9000\u6765\uff0c\u6700\u597d\u80fd\u8981\u5979\u8d1f\u4f24\u60ca\u5012\uff0c\u8001\u673d\u4fbf\u53ef\u65bd\u5c55\u9b3c\u5b50\uff0c\u628a\u90a3\u70c8\u706b\u795e\u73e0\u5077\u6765\u4e86\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u5f97\u5fc3\u4e2d\u5403\u60ca\u4e0d\u5c0f\uff0c\u5f53\u4e0b\u6697\u4e2d\u4f5c\u4e86\u51b3\u5b9a\uff0c\u90a3\u6700\u540e\u4e00\u62db\uff0c\u6539\u7528\u81ea\u5df1\u7684\u5251\u6cd5\uff0c\u597d\u8d81\u52bf\u51fb\u5012\u5c39\u68a6\u8377\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u6709\u4e86\u8fd9\u7b49\u60f3\u6cd5\uff0c\u624b\u4e2d\u7684\u5251\u52bf\uff0c\u4e5f\u5c31\u7565\u4e3a\u5730\u7f13\u4e86\u4e00\u7f13\uff0c\u56e0\u4e3a\uff0c\u4ed6\u5fc5\u987b\u60f3\u597d\u7acb\u8eab\u4e4b\u5904\uff0c\u5fc5\u5f97\u4e00\u5251\u5c06\u90a3\u5c39\u68a6\u8377\u4f24\u5f97\u4e0d\u8f7b\u4e0d\u91cd\uff0c\u6070\u597d\u5012\u5411\u5b8b\u674e\u8eab\u524d\uff0c\u597d\u8ba9\u5b8b\u8001\u4e0b\u624b\u5077\u73e0\u3002 \u3000\u3000\u5c39\u68a6\u8377\u81ea\u7136\u4e0d\u66fe\u60f3\u5230\uff0c\u81ea\u5df1\u5df2\u7136\u5728\u4eba\u8ba1\u7b97\u4e4b\u4e2d\uff0c\u53ea\u89c9\u65b9\u96ea\u5b9c\u7684\u5251\u62db\u5ffd\u5730\u6162\u4e86\u4e0b\u6765\uff0c\u4f46\u662f\u8d8a\u5230\u540e\u9762\u51e0\u62db\uff0c\u8d8a\u53d1\u7684\u773c\u719f\uff0c\u987f\u65f6\u82b3\u5fc3\u4e4b\u4e2d\uff0c\u683c\u5916\u5730\u5403\u60ca\u4e0d\u5c0f\uff0c\u5fd6\u9053\uff1a\u201c\u4ed6\u83ab\u975e\u5f53\u771f\u662f\u5728\u9002\u624d\u4e0e\u6211\u52a8\u624b\u4e4b\u65f6\uff0c\u5077\u5077\u5730\u8bb0\u4e0b\u4e86\u8fd9\u4e00\u5957\u5e08\u95e8\u5251\u6cd5\uff1f\u5426\u5219\uff0c\u4ed6\u600e\u4f1a\u8bb0\u5f97\u4e0d\u592a\u719f\u6089\u5462\uff1f\u201d \u3000\u3000\u674e\u82b8\u5219\u8d8a\u77a7\u8d8a\u60ca\u4e4b\u5916\uff0c\u5fc3\u4e2d\u66f4\u6709\u4e86\u4e00\u4efd\u559c\u610f\uff0c\u6697\u9053\uff1a\u201c\u53ea\u8981\u8fd9\u65b9\u516c\u5b50\u7684\u5251\u6cd5\u679c\u7136\u662f\u5077\u81ea\u4e8c\u5e08\u59ca\uff0c\u53ea\u6015\u5e08\u7236\u4e00\u5b9a\u4e0d\u8f7b\u6613\u5730\u9976\u6055\u4e8e\u5979\u4e86\u2026\u2026\u201d \u3000\u3000\u5c31\u5728\u4e8c\u5973\u5fc3\u4e2d\u5404\u6000\u9b3c\u80ce\u4e4b\u9645\uff0c\u65b9\u96ea\u5b9c\u5df2\u7136\u65bd\u5c55\u5230\u7b2c\u56db\u5341\u4e03\u62db\u4e4b\u4e0a\uff0c\u5ffd\u7136\u95f4\uff0c\u4ed6\u5251\u5149\u4e00\u6536\uff0c\u76b1\u7709\u9053\uff1a\u201c\u7cdf\u4e86\uff0c\u7b2c\u56db\u5341\u516b\u62db\uff0c\u600e\u5730\u5ffd\u7136\u5fd8\u4e86\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5fc3\u4e2d\u4e00\u6025\uff0c\u4e0d\u77e5\u9053\u65b9\u96ea\u5b9c\u522b\u6709\u6240\u56fe\uff0c\u5fd9\u5411\u8eab\u65c1\u7684\u674e\u82b8\u7389\u624b\u4e0a\u4e00\u628a\uff0c\u4f4e\u58f0\u9053\uff1a \u3000\u3000\u201c\u59d1\u5a18\uff0c\u5feb\u5feb\u63d0\u9192\u65b9\u516c\u5b50\u4e00\u58f0\u2026\u2026\u201d \u3000\u3000\u674e\u82b8\u84e6\u7136\u5982\u906d\u96f7\u51fb\uff0c\u8111\u9645\u55e1\u5730\u4e00\u58f0\u50cf\u7740\u4e86\u8ff7\u4e00\u822c\uff0c\u4e0d\u6687\u601d\u7d22\uff0c\u8131\u53e3\u9053\uff1a\u201c\u7b2c\u56db\u5341\u516b\u62db\uff0c\u4e0d\u662f\u2018\u82b1\u8c22\u82b1\u98de\u2019\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u672c\u6765\u4e43\u662f\u88c5\u6a21\u4f5c\u6837\uff0c\u95fb\u8a00\u5927\u7b11\u9053\uff1a\u201c\u662f\u554a\uff01\u591a\u8c22\u59d1\u5a18\u63d0\u9192\u5728\u4e0b\u2026\u2026 \u3000\u3000\u5f53\u4e0b\u4e00\u6643\u957f\u5251\uff0c\u4fbf\u5c06\u56db\u5341\u516b\u3001\u56db\u5341\u4e5d\u4e24\u62db\u65bd\u51fa\uff0c\u5269\u4e0b\u7684\u6700\u540e\u4e00\u62db\uff0c\u4ed6\u5374\u53c8\u987f\u4e86\u4e00\u987f\uff0c\u7f13\u7f13\u5730\u5411\u5c39\u68a6\u8377\u8d70\u8fdb\u4e86\u4e00\u6b65\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u90a3\u6700\u540e\u4e00\u62db\uff0c\u4f60\u613f\u63d0\u9192\u5728\u4e0b\u4e00\u58f0\u5417\uff1f\u201d \u3000\u3000\u5c39\u68a6\u8377\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u5979\u8fde\u5ff5\u5934\u90fd\u672a\u6765\u7684\u53ca\u8f6c\uff0c\u65b9\u96ea\u5b9c\u5df2\u7136\u5927\u7b11\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4e0d\u7528\u4e86\uff01\u5728\u4e0b\u60f3\u8d77\u6765\u4e86\u2026\u2026\u201d \u3000\u3000\u5bd2\u5149\u5de8\u95ea\uff0c\u7adf\u5411\u5c39\u68a6\u8377\u5f53\u80f8\u523a\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5c39\u68a6\u8377\u4f5c\u68a6\u4e5f\u6ca1\u6709\u60f3\u5230\uff0c\u65b9\u96ea\u5b9c\u7adf\u5728\u8fd9\u7b49\u65f6\u673a\uff0c\u731b\u5411\u81ea\u5df1\u4e0b\u4e86\u6bd2\u624b\uff0c\u60ca\u53eb\u4e86\u4e00\u58f0\uff0c\u98de\u8eab\u4fbf\u5411\u540e\u8ba9\u53bb\u3002 \u3000\u3000\u53f3\u624b\u5b9d\u5251\uff0c\u4e5f\u4e0d\u7531\u81ea\u4e3b\u5730\u5411\u4e0a\u4e00\u64a9\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u4e00\u5251\uff0c\u8bda\u5fc3\u8981\u7f6e\u90a3\u5c39\u68a6\u8377\u4e8e\u7edd\u5730\uff0c\u65b9\u597d\u5bb9\u5f97\u5b8b\u5b5a\u4e0b\u624b\uff0c\u662f\u4ee5\u51fa\u5251\u4e4b\u72e0\u3001\u4e4b\u51c6\uff0c\u8feb\u5f97\u5c39\u68a6\u8377\u6beb\u65e0\u8fd8\u624b\u89e3\u6551\u4e4b\u673a\uff0c\u8033\u4e2d\u53ea\u542c\u5f97\u5c39\u68a6\u8377\u5c16\u53eb\u4e00\u58f0\uff0c\u5de6\u81c2\u8863\u886b\uff0c\u5df2\u7136\u7247\u7247\u788e\u88c2\u3002 \u3000\u3000\u4e00\u7f15\u9c9c\u8840\uff0c\u987a\u7f57\u886b\u6ef4\u843d\u5730\u4e0a\u3002 \u3000\u3000\u5b8b\u5b5a\u8fd9\u65f6\u5374\u75be\u6b65\u4e0a\u524d\uff0c\u4e00\u628a\u6400\u4f4f\u5c39\u68a6\u8377\uff0c\u5927\u58f0\u5411\u65b9\u96ea\u5b9c\u559d\u9053\uff1a\u201c\u65b9\u8001\u5f1f\uff0c\u4e0d\u53ef\u4f24\u4eba\u2026\u2026\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5df2\u7136\u5c06\u5c39\u68a6\u8377\u6a2a\u6276\u8fc7\u6765\uff0c\u81ea\u5df1\u7acb\u8eab\u6321\u4f4f\u65b9\u96ea\u5b9c\u9762\u524d\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u76ee\u5149\u5728\u5b8b\u5b5a\u8138\u4e0a\u4e00\u8f6c\uff0c\u987f\u65f6\u5fc3\u5934\u660e\u767d\uff0c\u5927\u58f0\u9053\uff1a\u201c\u8001\u524d\u8f88\u95ea\u5f00\uff0c\u533a\u533a\u8fd8\u8981\u89c1\u8bc6\u4e00\u4e0b\u91d1\u82b1\u5e2e\u95e8\u4e0b\u7684\u7edd\u5b66\uff0c\u5c39\u59d1\u5a18\u4e0d\u8fc7\u662f\u4e00\u65f6\u5927\u610f\uff0c\u90a3\u4e5f\u7b97\u4e0d\u5f97\u4ec0\u4e48\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u9000\u540e\u4e00\u6b65\u5427\uff01\u201d \u3000\u3000\u4e0d\u7b49\u65b9\u96ea\u5b9c\u7b54\u8bdd\uff0c\u7acb\u5373\u5411\u674e\u82b8\u559d\u9053\uff1a\u201c\u674e\u59d1\u5a18\uff0c\u5feb\u5c06\u5c39\u59d1\u5a18\u6276\u5230\u540e\u9762\u53bb\u7597\u4f24\u2026\u2026\u201d \u3000\u3000\u90a3\u674e\u82b8\u4e0d\u7b49\u5b8b\u5b5a\u62db\u547c\uff0c\u65e9\u5df2\u8d70\u4e86\u8fc7\u6765\uff0c\u6276\u7740\u5c39\u68a6\u8377\u5411\u4fa7\u5ba4\u884c\u53bb\u3002 \u3000\u3000\u5b8b\u5b5a\u76ee\u7779\u5979\u4eec\u8fdb\u5165\u4fa7\u5ba4\uff0c\u4fbf\u7528\u4f20\u97f3\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4e1c\u897f\u5230\u624b\u4e86\uff01\u54b1\u4eec\u53ef\u8981\u56de\u8f6c\u5c11\u6797\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u60f3\u4e86\u4e00\u60f3\uff0c\u7b11\u9053\uff1a\u201c\u8981\u56de\u5c11\u6797\uff0c\u54b1\u4eec\u4e5f\u5f97\u5148\u5c06\u4e8c\u5973\u64d2\u4f4f\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u597d\u4e3b\u610f\uff0c\u53ea\u662f\u6709\u4e00\u6869\uff0c\u90a3\u5c11\u6797\u5bfa\u4e2d\uff0c\u53ef\u4e0d\u5bb9\u5973\u4eba\u5165\u5185\u5462\uff01\u201d \u3000\u3000\u8bed\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u8fd8\u6709\u4e00\u4ef6\u4e8b\uff0c\u54b1\u4eec\u53ef\u4e0d\u80fd\u5fd8\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u7559\u5979\u4eec\u5728\u6b64\uff0c\u5bf9\u54b1\u4eec\u5e76\u65e0\u5bb3\u5904\uff0c\u4f55\u51b5\uff0c\u773c\u4e0b\u5979\u4eec\u4e5f\u4e0d\u660e\u4e86\u54b1\u4eec\u7acb\u573a\uff0c\u4f46\u80fd\u5728\u6697\u4e2d\u76f8\u52a9\u5c11\u6797\uff0c\u5c82\u4e0d\u6bd4\u6b63\u5f0f\u51fa\u9762\u66f4\u597d\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6c89\u541f\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u665a\u8f88\u89c9\u5f97\u53ef\u884c\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5374\u76b1\u7709\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u4f60\u8981\u662f\u8981\u54b1\u4eec\u6682\u65f6\u7559\u5728\u8fd9\u513f\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4f60\u53ef\u662f\u4e0d\u613f\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u8138\u4e0a\u9732\u51fa\u4e0d\u5b89\u4e4b\u8272\uff0c\u9053\uff1a\u201c\u665a\u8f88\u89c9\u5f97\u8fd9\u4e24\u4e2a\u5996\u5973\u884c\u4e3a\u53ef\u618e\uff0c\u4e0d\u60f3\u591a\u8ddf\u5979\u4eec\u7559\u5728\u4e00\u8d77\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u4f55\u7b49\u8001\u7ec3\uff0c\u95fb\u8a00\u5df2\u7136\u660e\u767d\uff0c\u5979\u4e0d\u8fc7\u662f\u4e0d\u653e\u5fc3\u65b9\u96ea\u5b9c\u4e0e\u5979\u4eec\u76f8\u5904\u4e00\u8d77\u800c\u5df2\uff0c\u5f53\u4e0b\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u4e0d\u7528\u62c5\u5fc3\uff0c\u5996\u5973\u80c6\u5b50\u518d\u5927\uff0c\u53ea\u6015\u5979\u4eec\u4e5f\u4e0d\u6562\u52fe\u5f15\u4f60\u4eec\u4e24\u4f4d\u7684\u4e86\uff0c\u81f3\u4e8e\u8001\u673d\uff0c\u8c05\u5979\u4eec\u4e5f\u4e0d\u4f1a\u770b\u5f97\u4e0a\u773c\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u88ab\u4ed6\u8bf4\u5f97\u7c89\u8138\u901a\u7ea2\uff0c\u4f4e\u4e0b\u5934\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u8fd8\u5251\u5165\u9798\uff0c\u7b11\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u54b1\u4eec\u5f53\u771f\u8981\u7559\u4e0b\u6765\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u5f53\u7136\u2026\u2026\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u5ffd\u7136\u542c\u5f97\u4e00\u9635\u4e1d\u4e1d\u4e4b\u58f0\u4f20\u6765\uff0c\u5b8b\u5b5a\u6b62\u4f4f\u8bdd\u58f0\uff0c\u6539\u7528\u4f20\u97f3\u4e4b\u672f\uff0c\u63a5\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u8fd9\u662f\u4ec0\u4e48\u58f0\u97f3\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7565\u4e00\u51dd\u795e\u7ec6\u542c\uff0c\u6447\u5934\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u554a\u3002\u201d \u3000\u3000\u4f46\u5b89\u5c0f\u840d\u5374\u6697\u6697\u7684\u76b1\u4e86\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u8fd9\u58f0\u97f3\u6709\u4e9b\u5947\u602a\uff0c\u96be\u9053\u2026\u2026\u201d \u3000\u3000\u4f59\u97f3\u672a\u5df2\uff0c\u53ea\u89c1\u6253\u90a3\u4fa7\u5ba4\u4e4b\u5185\uff0c\u5192\u51fa\u4e00\u80a1\u6d53\u70df\u3002 \u3000\u3000\u5b8b\u5b5a\u5927\u5403\u4e00\u60ca\uff0c\u559d\u9053\uff1a\u201c\u4e24\u4f4d\u5feb\u5c4f\u4f4f\u547c\u5438\u2026\u2026\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u4eba\u5df2\u8dc3\u9000\u4e09\u5c3a\uff0c\u5e76\u4e14\uff0c\u8fc5\u5feb\u5730\u5411\u6d1e\u5916\u5954\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\uff0c\u5b89\u5c0f\u840d\u95fb\u58f0\u77e5\u8b66\uff0c\u5c4f\u4f4f\u542c\u547c\uff0c\u5c31\u5f85\u79fb\u6b65\u9000\u51fa\u77f3\u6d1e\u3002 \u3000\u3000\u4f46\u8033\u4e2d\u5ffd\u7136\u542c\u5f97\u674e\u82b8\u4f4e\u58f0\u9053\uff1a\u201c\u4e09\u4f4d\u4e0d\u7528\u614c\u5f20\uff0c\u8fd9\u80a1\u6d53\u70df\uff0c\u5e76\u975e\u6bd2\u96fe\uff0c\u4e43\u662f\u59be\u8eab\u70e7\u6765\u66ff\u4e8c\u5e08\u59ca\u7597\u4f24\u7684\u836f\u7269\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5140\u81ea\u6709\u4e9b\u4e0d\u4fe1\uff0c\u4f46\u7728\u773c\u4e4b\u95f4\uff0c\u53ea\u89c1\u90a3\u6d53\u70df\u4e5f\u5df2\u6563\u53bb\uff0c\u6697\u9053\uff1a\u201c\u542c\u90a3\u674e\u82b8\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u5012\u4f3c\u4e0d\u662f\u5047\u7684\u2026\u2026\u201d\u8bd5\u7740\u7565\u4e00\u547c\u5438\uff0c\u679c\u771f\u6beb\u65e0\u5f02\u72b6\uff0c\u5f53\u4e0b\u5411\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u4f60\u4e0d\u7528\u8d70\u4e86\uff0c\u8fd9\u80a1\u6d53\u70df\uff0c\u5e76\u975e\u6bd2\u7269\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u5728\u674e\u82b8\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u4fbf\u5df2\u6b62\u4f4f\u53bb\u52bf\uff0c\u95fb\u8a00\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u8001\u673d\u6ca1\u6709\u8d70\u554a\u2026\u2026\u201d \u3000\u3000\u5927\u6b65\u53c8\u8d70\u8fdb\u6d1e\u6765\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u674e\u82b8\u5df2\u6253\u4fa7\u5ba4\u4e4b\u4e2d\uff0c\u8d70\u4e86\u8fc7\u6765\uff0c\u76ee\u5149\u5728\u65b9\u96ea\u5b9c\u8138\u4e0a\u4e00\u8f6c\uff0c\u957f\u957f\u4e00\u53f9\u9053\uff1a\u201c\u65b9\u516c\u5b50\uff0c\u4f60\u597d\u72e0\u7684\u5fc3\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u533a\u533a\u4f24\u7684\u59d1\u5a18\u5e08\u59ca\u4e0d\u91cd\uff0c\u4f55\u5c1d\u7528\u5f97\u4e0a\u72e0\u5fc3\u4e24\u5b57\u5462\uff1f\u201d \u3000\u3000\u674e\u82b8\u6447\u5934\u9053\uff1a\u201c\u516c\u5b50\u53ef\u8981\u5165\u5185\u770b\u770b\u5417\uff1f\u59be\u8eab\u90a3\u4e8c\u5e08\u59ca\u5df2\u7ecf\u2026\u2026\u5df2\u7ecf\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5ffd\u7136\u5fc3\u4e2d\u4e00\u9707\uff0c\u63a5\u9053\uff1a\u201c\u5df2\u7ecf\u600e\u4e48\u6837\u4e86\uff1f\u201d \u3000\u3000\u674e\u82b8\u4f4e\u58f0\u9053\uff1a\u201c\u5944\u5944\u4e00\u606f\uff0c\u751f\u673a\u5c3d\u7edd\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u4f1a\uff1f\u533a\u533a\u4e0d\u8fc7\u4f24\u4e86\u5979\u53f3\u81c2\u76ae\u8089\uff0c\u600e\u4f1a\u5df2\u7136\u7edd\u4e86\u751f\u673a\u5462\uff1f\u201d \u3000\u3000\u674e\u82b8\u9eef\u7136\u7684\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4f60\u90a3\u5251\u4e0a\u53ef\u662f\u6d82\u6709\u5de8\u6bd2\u7684\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u5934\u9053\uff1a\u201c\u6ca1\u6709\u554a\uff01\u201d \u3000\u3000\u674e\u82b8\u76b1\u7709\u9053\uff1a\u201c\u8fd9\u5c31\u5947\u4e86\uff01\u516c\u5b50\u5251\u4e0a\u5982\u662f\u65e0\u6bd2\uff0c\u4e8c\u5e08\u59ca\u600e\u4f1a\u6709\u4e86\u4e2d\u6bd2\u4e4b\u5146\uff01\u800c\u4e14\uff0c\u59be\u8eab\u7528\u672c\u95e8\u718f\u9999\u89e3\u6bd2\u4e4b\u6cd5\uff0c\u90fd\u718f\u4e0d\u53bb\u90a3\u91cd\u6bd2\u5462\uff01\u201d \u3000\u3000\u7ecf\u5979\u8fd9\u4e48\u4e00\u8bf4\uff0c\u53ef\u628a\u4e09\u4eba\u5168\u90fd\u8bf4\u6123\u4e86\uff01 \u3000\u3000\u5b89\u5c0f\u840d\u77a7\u4e86\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u5f53\u771f\u4f60\u90a3\u5251\u4e0a\u65e0\u6bd2\u4e48\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u672c\u6765\u6ca1\u6709\u6bd2\u561b\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u90a3\u5c39\u68a6\u8377\u53c8\u662f\u600e\u751f\u4e2d\u4e86\u5de8\u6bd2\u5462\uff1f\u201d \u3000\u3000\u674e\u82b8\u63a5\u9053\uff1a\u201c\u662f\u554a\uff01\u65b9\u516c\u5b50\u8fd9\u628a\u5251\uff0c\u53ea\u6015\u522b\u6709\u53e4\u602a\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u51b7\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u5e08\u4f20\u5b9d\u5251\uff0c\u7528\u4e0d\u7740\u59d1\u5a18\u8fc7\u95ee\uff0c\u65b9\u67d0\u77e5\u9053\u7684\u5e94\u662f\u6bd4\u4f60\u6e05\u695a\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u8fd9\u65f6\u5ffd\u7136\u51b7\u7b11\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8001\u673d\u660e\u767d\u4e86\uff01\u201d \u3000\u3000\u674e\u82b8\u9053\uff1a\u201c\u4f60\u8001\u660e\u767d\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u90a3\u5c39\u59d1\u5a18\u679c\u7136\u662f\u4e2d\u4e86\u6bd2\uff0c\u53ea\u662f\uff0c\u90a3\u6bd2\u5374\u4e0d\u662f\u65b9\u8001\u5f1f\u5251\u4e0a\u7684\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u539f\u6765\u4e0d\u662f\u5728\u4e0b\u5b9d\u5251\u6709\u6bd2\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6014\u6014\u5730\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u90a3\u6bd2\u662f\u4ece\u54ea\u513f\u6765\u7684\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u770b\u770b\u674e\u82b8\u51b7\u7b11\u9053\uff1a\u201c\u95ee\u5979\uff01\u5927\u6982\u53ea\u6709\u674e\u59d1\u5a18\u624d\u77e5\u9053\u8fd9\u6bd2\u662f\u6253\u4f55\u5904\u6765\u7684\u4e86\uff01\u201d \u3000\u3000\u674e\u82b8\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u4f60\u8001\u8fd9\u662f\u600e\u4e48\u8bf4\uff1f\u59be\u8eab\u600e\u4f1a\u77e5\u6653\u90a3\u4e8c\u5e08\u59ca\u5982\u4f55\u4e2d\u7684\u6bd2\u5462\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u76b1\u8d77\u7709\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u8fd9\u7a76\u7adf\u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff1f\u4f60\u2026\u2026\u201d \u3000\u3000\u53ea\u542c\u5f97\u65b9\u96ea\u5b9c\u5927\u58f0\u9053\uff1a\u201c\u662f\u4e86\u2026\u2026\u533a\u533a\u4e5f\u660e\u767d\u4e86\uff01\u597d\u4e00\u4e2a\u501f\u5200\u6740\u4eba\u7684\u72e0\u6bd2\u624b\u6bb5\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5403\u60ca\u5730\u770b\u4e86\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u8bf4\u8c01\u501f\u5200\u6740\u4eba\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u76ee\u5149\u5728\u674e\u82b8\u8eab\u4e0a\u4e00\u8f6c\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u9664\u4e86\u674e\u59d1\u5a18\uff0c\u662f\u4e0d\u4f1a\u518d\u6709\u7b2c\u4e8c\u4e2a\u4eba\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u674e\u59d1\u5a18\uff1f\u5979\u2026\u2026\u600e\u4e48\u4f1a\u5bb3\u5979\u7684\u5e08\u59d0\u5462\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u8fd9\u65f6\u5ffd\u7136\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u65e9\u77e5\u5982\u6b64\uff0c\u8001\u673d\u4e5f\u4e0d\u7528\u8d39\u90a3\u4e48\u591a\u7684\u5fc3\u673a\u4e86\uff01\u65b9\u8001\u5f1f\uff0c\u5230\u5e95\u662f\u5987\u4eba\u4e4b\u5fc3\uff0c\u8ddf\u6211\u4eec\u7537\u4eba\u4e0d\u540c\uff0c\u65e2\u72e0\u53c8\u7edd\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u8fd9\u65f6\u7a81\u7136\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u4f60\u8bf4\u8bdd\u53ef\u8981\u79ef\u70b9\u513f\u5fb7\u554a\uff01\u4e0d\u7136\u5f53\u5fc3\u8981\u8fdb\u5272\u820c\u5730\u72f1\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u626e\u4e86\u4e2a\u9b3c\u8138\uff0c\u7b11\u9053\uff1a\u201c\u662f\u554a\uff0c\u8001\u673d\u5fd8\u8bb0\u4e86\uff01\u8001\u5f1f\u5343\u4e07\u83ab\u653e\u5728\u5fc3\u4e0a\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7eb5\u7136\u6709\u6c14\uff0c\u5f53\u7740\u65b9\u96ea\u5b9c\u9762\u524d\uff0c\u5374\u662f\u4e0d\u4fbf\u591a\u8bf4\u4ec0\u4e48\uff0c\u53ea\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u81ea\u7136\u662f\u4e0d\u4f1a\u89c1\u602a\u7684\u4e86\uff01\u53ea\u662f\u5b8b\u8001\u4ee5\u540e\u5f53\u5fc3\u4e9b\u624d\u597d\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u8001\u673d\u8bb0\u4e0b\u4e86\uff01\u201d \u3000\u3000\u6b64\u523b\uff0c\u674e\u82b8\u5374\u7f13\u7f13\u5730\u79fb\u52a8\u5a07\u8eaf\uff0c\u5411\u77f3\u6d1e\u95e8\u53e3\u8d70\u53bb\u3002\u5e78\u800c\uff0c\u65b9\u96ea\u5b9c\u5bf9\u5979\u4e00\u76f4\u751a\u6709\u6212\u5fc3\uff0c\u8fd9\u65f6\u7779\u72b6\uff0c\u4e0d\u7531\u5f97\u5927\u58f0\u559d\u9053\uff1a\u201c\u59d1\u5a18\u4f60\u60f3\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u674e\u82b8\u5728\u542c\u5f97\u65b9\u96ea\u5b9c\u8bf4\u7a7f\u5979\u501f\u6cbb\u4f24\u4e3a\u540d\uff0c\u6697\u7b97\u4e86\u5c39\u68a6\u8377\u4e4b\u4e8b\uff0c\u82b3\u5fc3\u4e4b\u4e2d\u4e0d\u4f46\u751a\u4e3a\u9707\u9a87\uff0c\u800c\u4e14\u7adf\u7136\u6709\u7740\u4e00\u4e0d\u505a\u4e8c\u4e0d\u4f11\u4e4b\u60f3\uff0c\u5c45\u7136\u8fde\u4ed6\u4eec\u51e0\u4eba\u4e5f\u60f3\u6697\u7b97\u5728\u5185\u3002\u5979\u79fb\u52a8\u8eab\u5f62\uff0c\u76ee\u7684\u5c31\u662f\u60f3\u6e9c\u51fa\u6d1e\u53bb\uff0c\u5c01\u95ed\u6d1e\u95e8\uff0c\u5c06\u4ed6\u4eec\u4e09\u4eba\u548c\u4e24\u540d\u4e2b\u5934\uff0c\u6d3b\u57cb\u5c71\u8179\u4e4b\u4e2d\u3002 \u3000\u3000\u4f46\u5979\u5374\u672a\u6599\u5230\uff0c\u65b9\u96ea\u5b9c\u5e76\u672a\u653e\u8fc7\u5979\u7684\u4e00\u4e3e\u4e00\u52a8\uff0c\u5979\u79bb\u5f00\u6d1e\u95e8\u5c1a\u6709\u4e08\u8bb8\uff0c\u5c31\u88ab\u65b9\u96ea\u5b9c\u559d\u4f4f\uff0c\u5979\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u59be\u8eab\u6ca1\u6709\u60f3\u505a\u4ec0\u4e48\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u51b7\u7b11\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u5982\u662f\u60f3\u6e9c\uff0c\u5728\u4e0b\u4e5f\u4e0d\u4f1a\u62e6\u963b\u4e8e\u4f60\uff0c\u4f46\u5728\u4ee4\u5e08\u5c1a\u672a\u62b5\u8fbe\u5d69\u5c71\u4e4b\u524d\uff0c\u59d1\u5a18\u6700\u597d\u4e0d\u8981\u79bb\u53bb\uff01\u201d \u3000\u3000\u674e\u82b8\u53ea\u542c\u5f97\u82b3\u5fc3\u5927\u9707\uff0c\u8131\u53e3\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4f60\u2026\u2026\u8981\u544a\u8bc9\u6211\u5e08\u7236\u5417\uff1f\u201d \u3000\u3000\u4e0d\u6d88\u8bf4\uff0c\u5979\u5df2\u7136\u81ea\u8ba4\u5bb3\u6b7b\u90a3\u5c39\u68a6\u8377\u7684\u4e86\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u4e00\u5b9a\uff01\u522b\u4eba\u5bb3\u6015\u4ee4\u5e08\uff0c\u4f46\u533a\u533a\u5374\u4e0d\u6015\u5979\uff01\u201d \u3000\u3000\u8bed\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u533a\u533a\u4e5f\u4e0d\u80fd\u88ab\u4eba\u5f53\u4f5c\u5251\u4e0a\u6dec\u6bd2\u7684\u5c0f\u4eba\u4e4b\u8f88\uff01\u201d \u3000\u3000\u674e\u82b8\u5446\u4e86\u534a\u664c\uff0c\u9053\uff1a\u201c\u516c\u5b50\u8fd8\u662f\u6253\u7b97\u8981\u5c06\u6211\u6263\u7559\u4ea4\u7ed9\u5bb6\u5e08\u5904\u7f6e\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u76b1\u7709\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u6700\u597d\u79bb\u90a3\u77f3\u95e8\u8fdc\u4e9b\uff0c\u5426\u5219\u5728\u4e0b\u4e00\u65f6\u7591\u5fc3\u59d1\u5a18\u6709\u90a3\u9003\u8d70\u4e4b\u5fc3\uff0c\u51fa\u5251\u8ffd\u51fb\uff0c\u53ea\u6015\u96be\u514d\u8981\u8bef\u4f24\u59d1\u5a18\u4e86\uff01\u201d \u3000\u3000\u4ed6\u6b64\u8a00\u4e00\u51fa\u674e\u82b8\u5927\u5403\u4e00\u60ca\uff0c\u679c\u771f\u79fb\u52a8\u5411\u5185\u8d70\u4e86\u4e94\u5c3a\uff0c\u79bb\u90a3\u77f3\u95e8\u5df2\u7136\u6709\u4e86\u4e24\u4e08\u8fdc\u8fd1\u3002 \u3000\u3000\u6562\u60c5\u5979\u5728\u76ee\u7779\u65b9\u96ea\u5b9c\u5251\u4f24\u5c39\u68a6\u8377\u7684\u90a3\u96f7\u9706\u4e00\u51fb\u4e4b\u5a01\uff0c\u5fc3\u4e2d\u660e\u767d\uff0c\u5018\u82e5\u4ed6\u51fa\u624b\u5bf9\u81ea\u5df1\uff0c\u51b3\u4e0d\u8981\u5341\u62db\uff0c\u4fbf\u53ef\u91cd\u4f24\u81ea\u5df1\uff01 \u3000\u3000\u5b8b\u5b5a\u8fd9\u65f6\u7b11\u9053\uff1a\u201c\u65b9\u8001\u5f1f\uff0c\u4f60\u8981\u6263\u7559\u4e0b\u674e\u59d1\u5a18\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u6263\u7559\u4e0d\u6562\uff0c\u53ea\u662f\u4e0d\u8bb8\u5979\u5f04\u9b3c\u800c\u5df2\uff01\u201d \u3000\u3000\u8fd9\u65f6\u90a3\u5b89\u5c0f\u840d\u5ffd\u7136\u7eb5\u8eab\u4e00\u95ea\uff0c\u98d8\u5411\u674e\u82b8\u8eab\u524d\uff0c\u4e3e\u624b\u4e00\u62cd\uff0c\u5c01\u4e86\u674e\u82b8\u4e24\u5904\u7a74\u9053\uff0c\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u5bb3\u4eba\u4e4b\u5fc3\u4e0d\u53ef\u6709\uff0c\u9632\u4eba\u4e4b\u5fc3\u4e0d\u53ef\u65e0\uff0c\u533a\u533a\u5df2\u7136\u5c01\u4e86\u4f60\u4e24\u5904\u5927\u7a74\uff0c\u6700\u597d\u662f\u4e56\u4e56\u5730\u5750\u5728\u4e00\u65c1\u83ab\u8981\u4e71\u52a8\uff0c\u4e0d\u7136\u9006\u8840\u653b\u5fc3\uff0c\u53ef\u522b\u602a\u5728\u4e0b\u5fc3\u72e0\u624b\u8fa3\u4e86\uff01\u201d \u3000\u3000\u674e\u82b8\u5fc3\u4e2d\u72c2\u9707\uff0c\u4f46\u5374\u65e0\u53ef\u5948\u4f55\uff0c\u53ea\u597d\u4e56\u4e56\u5730\u7167\u7740\u5b89\u5c0f\u840d\u542c\u8bf4\uff0c\u5750\u5728\u5730\u4e0a\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u77a7\u5f97\u76b1\u7709\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u54b1\u4eec\u53ef\u662f\u4e5f\u966a\u7740\u5979\u5728\u6b64\u5e72\u8017\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u5417\uff0c\u53ef\u8981\u95ee\u5b8b\u8001\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6389\u5934\u671b\u5411\u5b8b\u5b5a\uff0c\u53ea\u89c1\u4ed6\u6b63\u8f6c\u8eab\u5411\u90a3\u4fa7\u95e8\u8d70\u4e86\u8fdb\u53bb\uff0c\u5f53\u4e0b\u95ea\u8eab\u8d76\u4e86\u8fc7\u53bb\uff0c\u5927\u58f0\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u4f60\u8981\u53bb\u770b\u90a3\u5c39\u59d1\u5a18\u6bd2\u4f24\u5417\uff1f\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u4e24\u4eba\u5df2\u7136\u8fdb\u4e86\u90a3\u95f4\u77f3\u5ba4\u3002 \u3000\u3000\u4e3e\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u5c39\u68a6\u8377\u53cc\u76ee\u7d27\u95ed\uff0c\u8138\u8272\u94c1\u9752\uff0c\u8eba\u5728\u4e00\u5f20\u5c0f\u5c0f\u7684\u77f3\u69bb\u4e4b\u4e0a\uff0c\u4e0d\u4f4f\u5730\u5598\u606f\u3002 \u3000\u3000\u4e24\u540d\u4e2b\u5934\uff0c\u5374\u4e0d\u5728\u5ba4\u5185\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u201c\u90a3\u4e24\u4e2a\u4e2b\u5934\u5462\uff1f\u600e\u7684\u4e0d\u5728\u5ba4\u4e2d\uff1f\u201d \u3000\u3000\u4f46\u4ed6\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u8fd9\u5c39\u59d1\u5a18\u6bd2\u4f24\u5f88\u91cd\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u4f4e\u5934\u5bdf\u770b\u4e86\u4e00\u4e0b\u90a3\u6761\u5251\u75d5\uff0c\u76b1\u7709\u9053\uff1a\u201c\u5c0f\u5b50\u4f60\u90a3\u5251\u4e0a\u771f\u7684\u6ca1\u6709\u6bd2\u5417\uff1f\u201d \u3000\u3000\u6562\u60c5\uff0c\u4ed6\u4e5f\u6000\u7591\u4e86\uff01 \u3000\u3000\u53ea\u56e0\u90a3\u5c39\u68a6\u8377\u7684\u4f24\u52bf\uff0c\u679c\u771f\u662f\u5341\u5206\u50cf\u4e3a\u4eba\u7528\u6bd2\u5251\u7834\u808c\u80a4\u800c\u4e2d\u5de8\u6bd2\u4e4b\u6545\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u5fc3\u4e2d\u751a\u662f\u4e0d\u5feb\uff0c\u63a5\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u8fde\u4f60\u4e5f\u4e0d\u76f8\u4fe1\u6211\u4e86\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e0d\u662f\u8001\u673d\u4e0d\u4fe1\uff0c\u8001\u5f1f\u77a7\u77a7\u8fd9\u9053\u4f24\u53e3\uff0c\u53ea\u6015\u4f60\u81ea\u5df1\u4e5f\u8981\u5403\u60ca\u5462\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u65f6\u5df2\u7ecf\u770b\u5230\u4e86\u5c39\u68a6\u8377\u7684\u5251\u4f24\uff0c\u4f24\u53e3\u9690\u6cdb\u4e4c\u7d2b\uff0c\u679c\u7136\u662f\u4e2d\u6bd2\u4e4b\u5146\uff0c\u5f53\u4e0b\u957f\u957f\u5730\u53f9\u4e86\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u8fd9\u4f4d\u674e\u59d1\u5a18\u7684\u7528\u5fc3\uff0c\u5b9e\u662f\u76f8\u5f53\u673a\u8bc8\uff0c\u770b\u6765\u90a3\u5929\u9b54\u5973\u662f\u4e00\u5b9a\u76f8\u4fe1\uff0c\u5c39\u68a6\u8377\u662f\u6b7b\u5728\u533a\u533a\u5251\u4e0b\u7684\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u5929\u9b54\u5973\u8bef\u89e3\u4e0e\u5426\uff0c\u5bf9\u4f60\u4e5f\u4e0d\u4f1a\u6709\u4ec0\u4e48\u5173\u7cfb\uff0c\u53ea\u662f\uff0c\u8fd9\u4f4d\u59d1\u5a18\u6240\u4e0b\u7684\u6bd2\u7269\uff0c\u54b1\u4eec\u6700\u597d\u80fd\u591f\u67e5\u660e\u2026\u2026\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u5b89\u5c0f\u840d\u4e5f\u8d70\u4e86\u8fdb\u6765\uff0c\u63a5\u53e3\u7b11\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u4f60\u8981\u67e5\u660e\u90a3\u674e\u82b8\u7528\u7684\u662f\u4f55\u7b49\u6bd2\u7269\uff0c\u4e0d\u662f\u5bb9\u6613\u5f97\u5f88\u5417\uff1f\u53ea\u8981\u95ee\u5979\u4e00\u53e5\u5c31\u5f97\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u6447\u5934\u9053\uff1a\u201c\u54ea\u6709\u8fd9\u7b49\u5bb9\u6613\u7684\u4e8b\uff1f\u674e\u82b8\u53ea\u6d88\u987a\u53e3\u8bf4\u51fa\u4e00\u79cd\uff0c\u54b1\u4eec\u5c31\u96be\u8fa8\u771f\u5047\u4e86\uff01\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u4f4e\u5934\u5728\u90a3\u5c39\u68a6\u8377\u4f24\u53e3\u4e4b\u4e0a\u952f\u6307\u6309\u53bb\u3002 \u3000\u3000\u5c39\u68a6\u8377\u65e0\u529b\u5730\u54fc\u4e86\u4e00\u58f0\uff0c\u663e\u7136\u751a\u662f\u75db\u82e6\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e0a\u524d\u4e00\u6b65\uff0c\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u665a\u8f88\u5012\u6709\u4e00\u4e2a\u6cd5\u5b50\uff0c\u53ef\u4ee5\u67e5\u51fa\u674e\u82b8\u7528\u7684\u4f55\u7b49\u6bd2\u7269\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4ec0\u4e48\u6cd5\u5b50\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8981\u5c39\u59d1\u5a18\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u5c39\u59d1\u5a18\u5417\uff1f\u5979\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fc5\u5feb\u7684\u51fa\u6307\uff0c\u5c01\u4e86\u5c39\u68a6\u8377\u4e09\u5904\u5927\u7a74\uff0c\u7b11\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u8fd9\u5996\u5973\u5982\u80fd\u4e0d\u6b7b\uff0c\u54b1\u4eec\u5c31\u53ef\u4ee5\u8981\u5979\u8bf4\u51fa\u6240\u4e2d\u7684\u662f\u4ec0\u4e48\u6bd2\u7269\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u5979\u7684\u6bd2\u5f88\u91cd\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u54b1\u4eec\u53ea\u8981\u5979\u8bf4\u51fa\u4e2d\u7684\u662f\u4ec0\u4e48\u6bd2\uff0c\u7eb5\u7136\u5979\u6d3b\u4e0d\u4e86\u591a\u4e45\uff0c\u90a3\u4e5f\u65e0\u5173\u8981\u7d27\u7684\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u6447\u5934\u4e00\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u8fd9\u4e2a\u5e76\u4e0d\u59a5\u5f53\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u5929\u9b54\u5973\u81ea\u662f\u4e0d\u4f1a\u76f8\u4fe1\u54b1\u4eec\u4e4b\u8a00\u2026\u2026\u9664\u975e\u54b1\u4eec\u80fd\u4f7f\u8fd9\u4f4d\u5c39\u59d1\u5a18\u6d3b\u5230\u5929\u9b54\u5973\u62b5\u6b64\u4e4b\u540e\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u76b1\u7709\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u5982\u662f\u8981\u5979\u6fc0\u8d77\u4e00\u80a1\u751f\u547d\u6b8b\u5b58\u7684\u529b\u91cf\uff0c\u8bf4\u51fa\u51e0\u53e5\u8bdd\uff0c\u5012\u4e5f\u6ca1\u6709\u4ec0\u4e48\u4e0d\u53ef\uff0c\u4f46\u82e5\u8981\u5979\u6bd2\u52bf\u5ef6\u8fdf\u4e0d\u53d1\u4f5c\uff0c\u5728\u4e0b\u53ef\u5c31\u6ca1\u6709\u8fd9\u7b49\u80fd\u8010\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8001\u673d\u4e5f\u6b63\u662f\u4e3a\u6b64\u5fe7\u5fc3\u2026\u2026\u201d \u3000\u3000\u5ffd\u7136\uff0c\u5b89\u5c0f\u840d\u4e00\u7b11\u9053\uff1a\u201c\u65b9\u5144\uff0c\u6211\u6709\u529e\u6cd5\u4f7f\u5979\u5ef6\u957f\u51e0\u5929\u751f\u547d\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5927\u559c\u9053\uff1a\u201c\u8d24\u5f1f\u6709\u4ec0\u4e48\u5999\u6cd5\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u6211\u5a18\u6559\u8fc7\u6211\u4e00\u79cd\u5c01\u7a74\u95ed\u8109\u7684\u624b\u6cd5\uff0c\u80fd\u591f\u5c06\u4e00\u4e2a\u4eba\u7684\u4f53\u5185\u7ecf\u7a74\u8109\u80f3\uff0c\u968f\u610f\u5c01\u95ed\uff0c\u5c39\u68a6\u8377\u65e2\u7136\u5de8\u6bd2\u5c1a\u672a\u653b\u5fc3\uff0c\u6211\u53ef\u4ee5\u7528\u8fd9\u79cd\u624b\u6cd5\uff0c\u52a9\u5979\u5ef6\u957f\u5341\u5929\u751f\u547d\uff0c\u4e0d\u4f7f\u90a3\u6bd2\u6db2\u6d41\u81f3\u5fc3\u5934\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u90a3\u5f88\u597d\u554a\uff01\u8d24\u5f1f\u5feb\u5feb\u52a8\u624b\uff0c\u4e5f\u8bb8\u90a3\u5de8\u6bd2\u8fd8\u5728\u9006\u8840\u4e0a\u884c\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5fae\u5fae\u4e00\u7b11\uff0c\u7f13\u6b65\u8d70\u5230\u5c39\u68a6\u8377\u8eab\u524d\uff0c\u4f7f\u51fa\u7ea4\u7ea4\u7389\u5343\uff0c\u4e00\u8fde\u70b9\u4e86\u5979\u5de6\u53f3\u53cc\u81c2\u53ca\u80f8\u524d\u4e03\u5904\u7a74\u9053\u3002 \u3000\u3000\u5979\u7684\u52a8\u4f5c\u5f88\u5feb\uff0c\u800c\u4e14\u8fd8\u81ea\u6000\u4e2d\u638f\u51fa\u4e00\u7c92\u836f\u4e38\uff0c\u5582\u7ed9\u4e86\u90a3\u5c39\u68a6\u8377\u541e\u670d\u4e0b\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u90a3\u662f\u4ec0\u4e48\u836f\u4e38\uff1f\u53ef\u662f\u5357\u6d77\u6bd2\u7075\u4e39\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u6211\u8fd9\u9897\u7075\u4e39\u53ea\u662f\u62a4\u4f4f\u5979\u7684\u5fc3\u795e\uff0c\u4f7f\u5979\u4e0d\u81f3\u4e8e\u8840\u8109\u53d7\u963b\u800c\u660f\u8ff7\u4e0d\u9192\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u8d24\u5f1f\u5012\u662f\u7ec6\u5fc3\u5f97\u5f88\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u8fd9\u65f6\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u65b9\u8001\u5f1f\uff0c\u8001\u673d\u5ffd\u7136\u60f3\u8d77\uff0c\u54b1\u4eec\u5982\u662f\u5f85\u5728\u6b64\u5904\uff0c\u5012\u4e0d\u5982\u5c06\u8fd9\u4e24\u4e2a\u5973\u5a03\u513f\u5f04\u5230\u5c11\u6797\u4e2d\u66f4\u597d\uff01\u90a3\u5929\u9b54\u5973\u6765\u6b64\uff0c\u4e00\u5b9a\u4f1a\u53bb\u5230\u5c11\u6797\u5bfa\u627e\u5bfb\u5979\u4eec\uff0c\u54b1\u4eec\u4ee5\u9038\u5f85\u52b3\uff0c\u5c82\u4e0d\u66f4\u5999\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u5b8b\u8001\u8ba4\u4e3a\u8fd9\u4e48\u505a\u66f4\u597d\uff0c\u90a3\u4e5f\u4f7f\u5f97\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u76b1\u7709\u9053\uff1a\u201c\u4e0d\u884c\u554a\uff01\u5979\u4eec\u81ea\u5df1\u4e0d\u80fd\u884c\u52a8\uff0c\u8c01\u6765\u80cc\u5979\u4eec\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u8fd9\u7b49\u5403\u529b\u7684\u4e8b\uff0c\u5f53\u7136\u4e0d\u7528\u52b3\u52a8\u8d24\u5f1f\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u95fb\u8a00\uff0c\u76b1\u7709\u6447\u5934\u9053\uff1a\u201c\u9519\u4e86\uff0c\u8fd9\u4e8b\u65b9\u5144\u53ef\u52a8\u4e0d\u5f97\u624b\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u6014\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u7537\u5973\u6388\u53d7\u4e0d\u4eb2\uff0c\u65b9\u5144\u90fd\u4e0d\u660e\u767d\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u63a5\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u5b89\u59d1\u5a18\u8bf4\u5f97\u4e0d\u9519\uff0c\u4f60\u4e0d\u80fd\u80cc\u8d1f\u5979\u4eec\uff0c\u8fd9\u80cc\u4eba\u4e4b\u4e8b\uff0c\u5f53\u7136\u843d\u5728\u8001\u673d\u80a9\u5934\u4e86\uff01\u201d \u3000\u3000\u8bdd\u97f3\u4e00\u987f\uff0c\u53cc\u624b\u6293\u8d77\u8eba\u5728\u77f3\u69bb\u4e0a\u7684\u5c39\u68a6\u8377\uff0c\u8f6c\u8eab\u4fbf\u5411\u5916\u95f4\u884c\u53bb\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u5a07\u8eaf\u4e00\u8f6c\uff0c\u62a2\u5148\u4e00\u6b65\uff0c\u5954\u4e86\u51fa\u53bb\uff0c\u62c9\u8d77\u90a3\u5750\u5728\u5730\u4e0a\u7684\u674e\u82b8\uff0c\u6325\u624b\u62cd\u5f00\u5979\u7684\u7a74\u9053\uff0c\u987a\u624b\u53e6\u5916\u5c01\u4e86\u5979\u4e24\u5904\u7ecf\u8109\uff0c\u8fd9\u624d\u51b7\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u5728\u4e0b\u5df2\u5c01\u4e86\u4f60\u4e24\u5904\u9633\u7ecf\u8109\u7edc\uff0c\u5982\u662f\u4f60\u4e0d\u60f3\u9001\u547d\uff0c\u6700\u597d\u662f\u4e56\u4e56\u5730\u8ddf\u7740\u5728\u4e0b\u524d\u53bb\u5c11\u6797\uff01\u201d \u3000\u3000\u90a3\u674e\u82b8\u6014\u4e86\u4e00\u6014\uff0c\u4fbf\u5374\u67d4\u987a\u5730\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u516c\u5b50\u5429\u5490\uff0c\u59be\u8eab\u60df\u547d\u662f\u4ece\uff01\u201d \u3000\u3000\u4e00\u884c\u4eba\u51fa\u4e86\u90a3\u77f3\u6d1e\u3001\u4f46\u81ea\u8d76\u5411\u5c11\u6797\u800c\u53bb\u3002 \u3000\u3000\u4f46\u8d70\u4e86\u4e0d\u53ca\u534a\u91cc\uff0c\u65b9\u96ea\u5b9c\u5ffd\u7136\u60f3\u8d77\u5c39\u68a6\u8377\u7684\u4e24\u540d\u4f8d\u5973\u4e3a\u4f55\u4e00\u76f4\u672a\u89c1\u73b0\u8eab\uff1f \u3000\u3000\u5f53\u4e0b\u5fcd\u4e0d\u4f4f\u559d\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u8d24\u5f1f\uff0c\u533a\u533a\u5ffd\u7136\u60f3\u8d77\u4e00\u6869\u53ef\u7591\u8fd9\u4e8b\u6765\u4e86\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u6b62\u6b65\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u90a3\u5c39\u59d1\u5a18\u7684\u4e24\u4e2a\u4e2b\u5934\u5462\uff1f\u600e\u5730\u672a\u89c1\u5230\u5979\u4eec\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u662f\u554a\uff0c\u4e0d\u662f\u8001\u5f1f\u63d0\u8d77\uff0c\u54b1\u4eec\u679c\u771f\u662f\u5ffd\u7565\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u8fd9\u65f6\u4e00\u628a\u6293\u4f4f\u4e86\u674e\u82b8\uff0c\u559d\u9053\uff1a\u201c\u8fd9\u4e8b\u674e\u59d1\u5a18\u5e94\u8be5\u77e5\u9053\u7684\u4e86\uff01\u201d \u3000\u3000\u674e\u82b8\u67f3\u7709\u5fae\u626c\uff0c\u4f4e\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4e8c\u59ca\u90a3\u4e24\u540d\u4e2b\u5934\uff0c\u65e9\u5df2\u4e0d\u5728\u5d69\u5c71\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u4e00\u6123\uff0c\u6012\u9053\uff1a\u201c\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u674e\u82b8\u9053\uff1a\u201c\u5927\u6982\u662f\u2026\u2026\u5bfb\u627e\u5bb6\u5e08\u53bb\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u5979\u4eec\u77e5\u9053\u5929\u9b54\u5973\u4f55\u5728\u5417\uff1f\u201d \u3000\u3000\u674e\u82b8\u9053\uff1a\u201c\u4e8c\u59ca\u6700\u4e3a\u5bb6\u5e08\u5ba0\u7231\uff0c\u5bb6\u5e08\u884c\u8e2a\uff0c\u4e8c\u59ca\u81ea\u662f\u77e5\u6653\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u76b1\u7709\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u8fd9\u4e8b\u8be5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5979\u4eec\u5982\u662f\u5df2\u7136\u79bb\u5f00\u5d69\u5c71\uff0c\u54b1\u4eec\u4e5f\u6ca1\u6709\u4ec0\u4e48\u529e\u6cd5\u53ef\u4ee5\u963b\u6b62\u5979\u4eec\uff0c\u53ea\u597d\u56de\u5230\u5c11\u6797\uff0c\u518d\u4f5c\u8ba1\u8f83\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7565\u4e00\u6c89\u541f\uff0c\u9053\uff1a\u201c\u662f\u554a\uff0c\u54b1\u4eec\u8fd8\u662f\u5148\u56de\u5c11\u6797\u518d\u4f5c\u8ba1\u8f83\u5427\uff01\u201d \u3000\u3000\u4e09\u4eba\u811a\u4e0b\u7d27\u4e86\u51e0\u6b65\u76f4\u5954\u5c11\u6797\u800c\u53bb\u3002 \u3000\u3000\u8fdb\u5f97\u90a3\u65b9\u4e08\u7985\u9662\uff0c\u53ea\u89c1\u90a3\u4e10\u5e2e\u5e2e\u4e3b\u201c\u5357\u534e\u795e\u4e5e\u201d\u970d\u9e23\u98ce\u6b63\u795e\u60c5\u7126\u6025\u5730\u5728\u9662\u4e2d\u5f20\u671b\uff0c\u4e00\u773c\u77a5\u89c1\u4e09\u4eba\u56de\u6765\uff0c\u4e0d\u7981\u5927\u559c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u4eec\u600e\u4e48\u8fd9\u4e48\u4e45\u624d\u56de\u6765\u554a\uff01\u53ef\u771f\u628a\u970d\u67d0\u6025\u574f\u4e86\uff01\u201d \u3000 \u7b2c\u4e8c\u5341\u56db\u56de\u3000\u602a\u4e8b\u4e1b\u4e1b \u3000 \u3000\u3000\u65b9\u96ea\u76f4\u62b1\u62f3\u7b11\u9053\uff1a\u201c\u6709\u52b3\u5e2e\u4e3b\u5b88\u5019\uff0c\u4e0d\u77e5\u5bfa\u4e2d\u53ef\u6709\u4ec0\u4e48\u52a8\u9759\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u76ee\u5149\u5728\u5c39\u3001\u674e\u4e8c\u5973\u8eab\u4e0a\u4e00\u8f6c\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u8001\u5f1f\u53f0\uff0c\u8fd9\u4e24\u4f4d\u59d1\u5a18\uff0c\u662f\u54ea\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u65b9\u96ea\u76f4\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u5929\u9b54\u5973\u7684\u4e24\u540d\u7231\u5f92\uff0c\u6b63\u5de7\u88ab\u54b1\u4eec\u9047\u4e0a\uff0c\u987a\u624b\u5c31\u64d2\u4e86\u56de\u6765\u3002\u201d \u3000\u3000\u970d\u9e23\u51e4\u9053\uff1a\u201c\u5979\u4eec\u771f\u662f\u5929\u9b54\u5973\u7684\u5f92\u513f\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u81ea\u7136\u662f\u771f\u7684\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u8fd9\u5c31\u597d\u4e86\uff01\u5feb\u5feb\u5165\u5185\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u51e0\u4eba\u8fdb\u5165\u65b9\u4e08\u9759\u5ba4\uff0c\u53ea\u89c1\u90a3\u5c11\u6797\u638c\u95e8\u4eba\u5165\u4e91\u5927\u5e08\uff0c\u6b63\u548c\u5165\u5723\u5927\u5e08\u4e24\u4eba\u53cc\u638c\u4e92\u62b5\uff0c\u76d8\u819d\u5750\u5728\u7985\u5e8a\u4e4b\u4e0a\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u51dd\u76ee\u671b\u53bb\uff0c\u53d1\u73b0\u8fd9\u4e24\u540d\u8001\u548c\u5c1a\u7684\u989d\u9645\uff0c\u9690\u9690\u5347\u8d77\u4e00\u7247\u767d\u96fe\uff0c\u7709\u76ee\u4e4b\u95f4\uff0c\u663e\u5f97\u751a\u4e3a\u75db\u82e6\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u6697\u9053\uff1a\u77a7\u4ed6\u4eec\u7684\u795e\u6001\uff0c\u597d\u4f3c\u6b63\u5728\u6bd4\u6597\u5185\u529b\uff0c\u53ea\u662f\u5728\u8fd9\u5f3a\u654c\u6765\u72af\u4e4b\u9645\uff0c\u4e3a\u4f55\u81ea\u5df1\u7adf\u5144\u5f1f\u9605\u5899\u8d77\u6765\u4e86\u5462\uff1f \u3000\u3000\u5bfb\u601d\u4e4b\u95f4\uff0c\u5b8b\u7387\u5df2\u5c06\u5c39\u68a6\u8377\u653e\u5728\u4e00\u53ea\u84b2\u4e4b\u4e0a\uff0c\u674e\u82b8\u5219\u5e2d\u5730\u800c\u5750\uff0c\u795e\u60c5\u751a\u662f\u4e56\u987a\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u663e\u7136\u4e5f\u88ab\u773c\u524d\u7684\u60c5\u666f\u6240\u611f\uff0c\u76b1\u7709\u5411\u970d\u9e23\u98ce\u9053\uff1a\u201c\u5e2e\u4e3b\uff0c\u8fd9\u4e24\u4f4d\u5927\u5e08\u600e\u5730\u4e86\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u4e34\u65f6\u62b1\u4f5b\u811a\uff0c\u6b63\u5728\u7ec3\u4e60\u4e00\u79cd\u6b66\u529f\uff0c\u51e0\u4f4d\u4e0d\u7528\u62c5\u5fc3\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u5fc3\u4e2d\u6697\u6697\u5931\u7b11\uff0c\u4ed8\u8fd9\uff1a\u201c\u8fd9\u8bdd\u5012\u662f\u4e0d\u5047\uff0c\u5c11\u6797\u9ad8\u50e7\u4e5f\u88ab\u8feb\u4e34\u65f6\u7ec3\u529f\uff0c\u8db3\u89c1\u5929\u9b54\u5973\u679c\u7136\u6709\u7740\u8fc7\u4eba\u4e4b\u5904\u2026\u2026\u201d\u4ed6\u8f6c\u5ff5\u95f4\uff0c\u4e0d\u7531\u5f97\u5728\u5fc3\u4e2d\u6dfb\u4e86\u51e0\u5206\u8b66\u60d5\uff01 \u3000\u3000\u5b8b\u5b5a\u6ca1\u6709\u77a7\u89c1\u4e10\u5e2e\u7684\u51e0\u4f4d\u957f\u8001\uff0c\u4e0d\u7531\u5f97\u7b11\u9053\uff1a\u201c\u970d\u5144\uff0c\u8d35\u5e2e\u7684\u51e0\u4f4d\u957f\u8001\u5462\uff1f\u4ed6\u4eec\u8fd8\u6ca1\u6709\u56de\u6765\u5417\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u82e6\u7b11\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5c11\u6797\u524d\u5bfa\u6765\u4e86\u5f3a\u654c\uff0c\u4ed6\u4eec\u53c8\u8d76\u53bb\u76f8\u52a9\u5bfa\u4e2d\u7684\u548c\u5c1a\u53bb\u4e86\u3002\u201d \u3000\u3000\u5b8b\u674e\u4e00\u6014\u9053\uff1a\u201c\u4ec0\u4e48\u5f3a\u654c\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u8001\u673d\u4e3a\u66ff\u5165\u4e91\u638c\u95e8\u4eba\u62a4\u6cd5\uff0c\u672a\u66fe\u524d\u53bb\u67e5\u770b\uff0c\u636e\u8bf4\u4e5f\u662f\u4e94\u9b54\u4e2d\u4eba\uff0c\u53ea\u4e0d\u77e5\u662f\u54ea\u4e00\u4f4d\u9b54\u4e3b\u800c\u5df2\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u76b1\u7709\u9053\uff1a\u201c\u8fd9\u4e48\u5de7\u5417\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u53ef\u4e0d\uff1f\u970d\u67d0\u4e5f\u6b63\u662f\u4e3a\u6b64\u5947\u602a\u4e0d\u5df2\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u90b5\u534e\u5c71\u8463\u5343\u91cc\uff0c\u6606\u4ed1\u8096\u98d2\uff0c\u53ef\u66fe\u6765\u5230\u5bfa\u4e2d\uff1f\u201d \u3000\u3000\u970d\u9e23\u51e4\u9053\uff1a\u2018\u54e8\u3001\u8463\u4e8c\u5144\u4e5f\u53bb\u4e86\u524d\u6bbf\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e0d\u5bf9\u554a\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u4e0d\u5bf9\u4e86\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u5bfa\u4e2d\u4e00\u7247\u6e05\u9759\uff0c\u4e0d\u50cf\u6709\u4eba\u751f\u4e8b\u561b\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u4e5f\u8bb8\u4ed6\u4eec\u5df2\u7136\u79fb\u5230\u5bfa\u5916\u53bb\u4e86\uff01\u5b8b\u5144\uff0c\u5165\u4e91\u638c\u95e8\u548c\u5165\u5723\u5927\u5e08\u518d\u6709\u4e2a\u628a\u65f6\u8fb0\uff0c\u5373\u53ef\u6210\u529f\uff0c\u8fd9\u62a4\u6cd5\u4e4b\u4e8b\uff0c\u6566\u8bf7\u5b8b\u5144\u4ee3\u52b3\u4e00\u756a\u5982\u4f55\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u4e00\u6014\u9053\uff1a\u201c\u970d\u5144\u4f60\u5462\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u5144\u5f1f\u524d\u53bb\u67e5\u770b\u4e00\u4e0b\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u90a3\u53c8\u4f55\u5fc5\uff1f\u4e00\u4e8b\u4e0d\u70e6\u4e8c\u4e3b\uff0c\u62a4\u6cd5\u7684\u4e8b\uff0c\u4ecd\u7531\u970d\u5144\u62c5\u4ee3\uff0c\u5144\u5f1f\u548c\u65b9\u8001\u5f1f\u4ee3\u52b3\u5230\u524d\u5bfa\u4e00\u884c\u4fbf\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u533a\u533a\u8fd9\u5c31\u524d\u53bb\u77a7\u77a7\uff01\u201d \u3000\u3000\u8f6c\u8eab\u4fbf\u5f85\u79bb\u53bb\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u65b9\u5144\uff0c\u6211\u966a\u4f60\u4e00\u9053\u53bb\u2026\u2026\u201d \u3000\u3000\u8f6c\u5934\u5411\u5b8b\u5b5a\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u8fd9\u4e24\u4f4d\u59d1\u5a18\uff0c\u4ea4\u7ed9\u5b8b\u8001\u7167\u987e\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u6123\u4e86\u4e00\u6123\uff0c\u9053\uff1a\u201c\u628a\u5979\u4eec\u4ea4\u7ed9\u8001\u673d\u5417\uff1f\u8fd9\u4e48\u8bf4\uff0c\u8001\u673d\u4e5f\u5f97\u5b88\u5728\u8fd9\u65b9\u4e08\u9759\u5ba4\u4e86\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u683c\u683c\u4e00\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6709\u52b3\u5b8b\u8001\u4e86\uff01\u201d \u3000\u3000\u4e24\u4eba\u4e00\u5148\u4e00\u540e\uff0c\u51fa\u4e86\u65b9\u4e08\u9759\u5ba4\u3002 \u3000\u3000\u7a7f\u5802\u8fc7\u6bbf\uff0c\u5230\u4e86\u5c71\u95e8\u9644\u8fd1\uff0c\u5374\u4f9d\u7136\u672a\u66fe\u89c1\u5230\u6709\u4eba\uff0c\u8fd9\u4e0d\u7981\u4f7f\u5f97\u5b89\u5c0f\u840d\u5927\u611f\u5947\u602a\uff0c\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u65b9\u5144\uff0c\u600e\u4e48\u534a\u4e2a\u4eba\u5f71\u5b50\u4e5f\u6ca1\u6709\u5462\uff1f\u90a3\u970d\u5e2e\u4e3b\u9a97\u4e86\u6211\u4eec\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u970d\u5e2e\u4e3b\u4e0d\u4f1a\u9a97\u6211\u4eec\uff0c\u53ea\u662f\uff0c\u4e5f\u8bb8\u4ed6\u4eec\u5df2\u7ecf\u79bb\u5bfa\u4ed6\u5f80\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u600e\u4e48\u4f1a\uff1f\u5c31\u7b97\u4ed6\u4eec\u8d70\u4e86\uff0c\u5bfa\u4e2d\u7684\u548c\u5c1a\uff0c\u4e5f\u4e0d\u8be5\u4e00\u4e2a\u4e0d\u89c1\u5440\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8d24\u5f1f\u8bf4\u7684\u662f\uff0c\u770b\u6765\uff0c\u54b1\u4eec\u8fd8\u5f97\u53bb\u5411\u970d\u5e2e\u4e3b\u8bf7\u6559\u4e00\u756a\u624d\u6210\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6447\u5934\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u7528\u4e86\uff01\u201d \u3000\u3000\u5a07\u8eaf\u4e00\u62e7\uff0c\u7adf\u5411\u5bfa\u5916\u6251\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5251\u7709\u4e00\u76b1\uff0c\u4f38\u624b\u62c9\u4f4f\u5b89\u5c0f\u840d\uff0c\u559d\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4f60\u8981\u53bb\u54ea\u91cc\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u88ab\u6293\u5f97\u7c89\u9762\u4e00\u7ea2\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u65b9\u5144\uff0c\u54b1\u4eec\u4e0d\u662f\u8981\u627e\u90a3\u4e10\u5e2e\u51e0\u4eec\u957f\u8001\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u54b1\u4eec\u6b63\u662f\u5728\u5bfb\u627e\u4ed6\u4eec\uff0c\u53ea\u662f\uff0c\u54b1\u4eec\u603b\u4e0d\u80fd\u832b\u65e0\u5934\u7eea\u5730\u4e71\u8dd1\u5440\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u65b9\u5144\u6709\u4f55\u9ad8\u89c1\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u677e\u5f00\u4e86\u53f3\u624b\uff0c\u7b11\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u6709\u4e00\u4ef6\u4e8b\uff0c\u4f60\u53ef\u662f\u60f3\u5230\u4e86\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5c31\u7b97\u4e10\u5e2e\u90a3\u51e0\u4f4d\u957f\u8001\u4e0d\u5728\u5bfa\u4e2d\uff0c\u4f46\u5bfa\u4e2d\u7684\u50e7\u4fa3\uff0c\u4e3a\u4f55\u4e5f\u4e00\u4e2a\u4e0d\u89c1\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u4e00\u6014\u9053\uff1a\u201c\u662f\u554a\uff0c\u6211\u600e\u7684\u6ca1\u6709\u60f3\u5230\uff1f\u96be\u9053\u8bf4\u8fd9\u4e9b\u548c\u5c1a\u5168\u90fd\u2026\u2026\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u4e0d\u53ef\u80fd\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u90a3\u2026\u2026\u4ed6\u4eec\u53bb\u4e86\u54ea\u91cc\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u8010\u4eba\u5bfb\u601d\u4e4b\u5904\u4e86\uff01\u5982\u662f\u5144\u5f1f\u6599\u60f3\u5f97\u4e0d\u5dee\uff0c\u53ea\u6015\u4ed6\u4eec\u522b\u6709\u4e00\u5904\u6240\u5728\u2026\u2026\u201d \u3000\u3000\u8bed\u97f3\u672a\u843d\uff0c\u53ea\u89c1\u4e00\u540d\u50e7\u4eba\u98de\u5954\u800c\u6765\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u65b9\u5144\uff0c\u6709\u4eba\u6765\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u70b9\u4e86\u70b9\u5934\uff0c\u5bb9\u5f97\u90a3\u50e7\u4eba\u8d70\u5230\u8fd1\u524d\uff0c\u84e6\u5730\u95ea\u6321\u4f4f\u53bb\u8def\uff0c\u559d\u9053\uff1a\u201c\u5927\u5e08\u8bf7\u4e86\uff01\u201d \u3000\u3000\u90a3\u50e7\u4eba\u4e00\u8def\u6025\u884c\u800c\u6765\uff0c\u5374\u672a\u6599\u5230\u5728\u8fd9\u5c71\u95e8\u5de6\u8fd1\uff0c\u6709\u4eba\u9690\u85cf\uff0c\u987f\u65f6\u5927\u5403\u4e00\u60ca\uff0c\u8131\u53e3\u9053\uff1a \u3000\u3000\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u76ee\u7779\u8fd9\u548c\u5c1a\u7684\u795e\u60c5\uff0c\u5fc3\u4e2d\u4e0d\u7981\u72af\u7591\uff0c\u6697\u9053\uff1a\u201c\u6b64\u50e7\u5982\u6b64\u614c\u5f20\uff0c\u5fc5\u6709\u539f\u6545\uff0c\u6211\u5f97\u7559\u5fc3\u4ed6\u4e00\u4e8c\u624d\u597d\u2026\u2026 \u3000\u3000\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u5927\u5e08\u8fd9\u822c\u5306\u5fd9\uff0c\u610f\u6b32\u4f55\u5f80\uff1f\u201d \u3000\u3000\u90a3\u548c\u5c1a\u5b9a\u775b\u770b\u53bb\uff0c\u89c1\u662f\u4e24\u4e2a\u4e0d\u8bc6\u7684\u5c11\u5e74\uff0c\u5f53\u4e0b\u80c6\u5b50\u58ee\u4e86\u4e0d\u5c11\uff0c\u5927\u58f0\u9053\uff0c\u201c\u5c0f\u65bd\u4e3b\uff0c\u4f60\u53ef\u77e5\u9053\u8fd9\u662f\u4ec0\u4e48\u6240\u5728\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u5d69\u5c71\u5c11\u6797\uff0c\u533a\u533a\u81ea\u662f\u77e5\u9053\u7684\u4e86\uff01\u201d \u3000\u3000\u90a3\u50e7\u4eba\u51b7\u7b11\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u65bd\u4e3b\u65e2\u77e5\u662f\u5d69\u5c71\u5c11\u6797\u5bfa\uff0c\u5c31\u8be5\u660e\u767d\u8fd9\u513f\u4e0d\u662f\u5bb9\u4eba\u6492\u91ce\u4e4b\u5904\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u6211\u4eec\u51e0\u65f6\u5728\u8fd9\u513f\u6492\u91ce\u4e86\uff1f\u5927\u548c\u5c1a\uff0c\u4f60\u8bf4\u8bdd\u53ef\u5f97\u8981\u5ba2\u6c14\u4e9b\uff01\u201d \u3000\u3000\u90a3\u548c\u5c1a\u51b7\u51b7\u5730\u770b\u4e86\u4e24\u4eba\u4e00\u773c\uff0c\u9053\uff1a\u201c\u65bd\u4e3b\uff0c\u4f60\u4eec\u662f\u600e\u6837\u8fdb\u5bfa\u6765\u7684\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u5c71\u95e8\u672a\u95ed\uff0c\u81ea\u5df1\u8d70\u8fdb\u6765\u7684\uff01\u4e0d\u8fc7\uff0c\u5947\u602a\u7684\u662f\uff0c\u600e\u7684\u4e0d\u89c1\u6709\u63a5\u5f85\u9999\u5ba2\u7684\u77e5\u5ba2\u50e7\u4fa3\u51fa\u73b0\uff1f\u5927\u5e08\u7236\uff0c\u8d35\u5bfa\u7684\u548c\u5c1a\u90fd\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u90a3\u548c\u5c1a\u53c8\u6253\u91cf\u4e86\u4e24\u4eba\u4e00\u773c\uff0c\u8fd9\u624d\u63a5\u9053\uff1a\u201c\u672c\u5bfa\u4eca\u665a\u6709\u4e86\u6781\u5927\u4e4b\u4e8b\uff0c\u5c0f\u65bd\u4e3b\u5982\u662f\u65e0\u4e8b\uff0c\u6700\u597d\u5feb\u5feb\u79bb\u5f00\u5d69\u5c71\u4e3a\u5999\uff0c\u5426\u5219\uff0c\u5982\u6709\u4f24\u635f\uff0c\u53ef\u83ab\u602a\u8d2b\u50e7\u672a\u66fe\u544a\u8beb\u4f60\u4eec\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u5f97\u5fc3\u4e2d\u6697\u6697\u597d\u7b11\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u5219\u89c9\u5f97\u751a\u662f\u6709\u8da3\uff0c\u7b11\u9053\uff1a\u201c\u591a\u8c22\u5927\u5e08\u7236\u5173\u7167\uff0c\u4e0d\u77e5\u5927\u5e08\u7236\u6b32\u5f85\u4f55\u53bb\uff1f\u201d \u3000\u3000\u90a3\u548c\u5c1a\u9053\uff1a\u201c\u8d2b\u50e7\u5949\u4e86\u638c\u95e8\u4eba\u4e4b\u547d\uff0c\u51fa\u5bfa\u516c\u5e72\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u533a\u533a\u60f3\u89c1\u8d35\u5bfa\u638c\u95e8\u4eba\u4e00\u9762\uff0c\u4e0d\u77e5\u8981\u600e\u751f\u624d\u80fd\u89c1\u5230\uff1f\u201d \u3000\u3000\u90a3\u548c\u5c1a\u663e\u7136\u662f\u6025\u4e8e\u79bb\u53bb\uff0c\u95fb\u8a00\u6447\u5934\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\uff0c\u4f60\u4eec\u6765\u7684\u4e0d\u662f\u65f6\u5019\uff0c\u638c\u95e8\u4eba\u4eca\u665a\u4e5f\u5df2\u5165\u5b9a\uff0c\u4e0d\u4f1a\u89c1\u5ba2\u7684\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u60cb\u60dc\u5730\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u54b1\u4eec\u771f\u662f\u4f5b\u7f18\u592a\u6d45\u4e86\u4e9b\u2026\u2026\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5411\u65b9\u96ea\u5b9c\u7728\u4e86\u4e00\u7728\u773c\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u77e5\u9053\u5979\u7684\u7528\u610f\uff0c\u662f\u8981\u81ea\u5df1\u6349\u5f04\u90a3\u548c\u5c1a\u4e00\u756a\uff0c\u4f46\u4ed6\u6b64\u523b\u5fc3\u4e2d\u5374\u5df2\u53e6\u5916\u6709\u4e86\u6253\u7b97\uff0c\u5f53\u4e0b\u7b11\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u54b1\u4eec\u6765\u5f97\u65e2\u662f\u5982\u6b64\u4e0d\u5de7\uff0c\u90a3\u5c31\u6539\u65e5\u518d\u6765\u5427\uff01\u201d \u3000\u3000\u4e00\u62c9\u5b89\u5c0f\u840d\u5927\u6b65\u8d70\u51fa\u5c71\u95e8\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u88ab\u4ed6\u5f04\u5f97\u60d1\u7136\u4e0d\u89e3\uff0c\u8d70\u51fa\u5c71\u95e8\u4e08\u8bb8\uff0c\u8d8a\u8fc7\u5c0f\u6eaa\uff0c\u4e0d\u7981\u4f4e\u58f0\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u8fd9\u662f\u4e3a\u4f55\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u56de\u5934\u770b\u4e86\u770b\u5c11\u6797\u5c71\u95e8\uff0c\u7b11\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u54b1\u4eec\u7b49\u90a3\u548c\u5c1a\u51fa\u6765\u4ee5\u540e\uff0c\u4fbf\u8ddf\u5728\u4ed6\u8eab\u540e\uff0c\u4e5f\u8bb8\u4f1a\u6709\u610f\u60f3\u4e0d\u5230\u7684\u6536\u83b7\u5462\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u8fd9\u624d\u604d\u7136\u7b11\u9053\uff1a\u201c\u539f\u6765\u65b9\u5144\u4e5f\u6709\u5fc3\u673a\u554a\u2026\u2026\u201d \u3000\u3000\u4e24\u4eba\u8bf4\u7b11\u95f4\uff0c\u8fdc\u8fdc\u671b\u89c1\u90a3\u548c\u5c1a\u63a2\u51fa\u5934\u6765\uff0c\u5411\u56db\u5468\u6253\u91cf\u4e0d\u5df2\uff0c\u53ea\u56e0\u591c\u8272\u751a\u6d53\uff0c\u662f\u4ee5\u4ed6\u624d\u671b\u4e0d\u89c1\u65b9\u96ea\u5b9c\u7b49\u4e24\u4eba\u7ad9\u5728\u6811\u4e0b\u672a\u8d70\uff0c\u53ea\u9053\u4ed6\u4eec\u4e5f\u5df2\u79bb\u53bb\uff0c\u7adf\u662f\u95ea\u8eab\u51fa\u4e86\u5bfa\u95e8\uff0c\u5411\u53f3\u4fa7\u5c71\u4e0b\u884c\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u62db\u547c\u4e86\u5b89\u5c0f\u840d\u4e00\u58f0\uff0c\u4e24\u4eba\u5c55\u5f00\u8eab\u5f62\uff0c\u8fdc\u8fdc\u5730\u76ef\u5728\u90a3\u548c\u5c1a\u8eab\u540e\u3002 \u3000\u3000\u90a3\u540d\u50e7\u4eba\u4e00\u8def\u75be\u5954\uff0c\u987a\u7740\u5c11\u5ba4\u5de6\u9e93\uff0c\u5f84\u5954\u592a\u5ba4\u800c\u53bb\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u6697\u7528\u4f20\u97f3\u4e4b\u672f\uff0c\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u8fd9\u548c\u5c1a\u8f7b\u529f\u4e0d\u5dee\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u53ea\u662f\u811a\u4e0b\u7a0d\u5acc\u6c89\u91cd\uff0c\u96be\u4ee5\u6301\u4e45\u2026\u2026\u201d \u3000\u3000\u4e00\u8def\u884c\u53bb\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u5df2\u6709\u5341\u91cc\u8fdc\u8fd1\uff0c\u53ea\u89c1\u90a3\u50e7\u4eba\u5ffd\u7136\u6b65\u5c65\u4e00\u7f13\uff0c\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u3001\u5b89\u5c0f\u840d\u8fde\u5fd9\u77ee\u8eab\u8e72\u5165\u8def\u65c1\u8349\u4e1b\u4e4b\u4e2d\uff0c\u6697\u6697\u671b\u53bb\uff0c\u53d1\u73b0\u90a3\u540d\u50e7\u4eba\u6b63\u5728\u5411\u6765\u8def\u4e4b\u4e0a\u5f20\u671b\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u4f4e\u58f0\u9053\uff1a\u201c\u597d\u9669\uff0c\u5dee\u70b9\u88ab\u4ed6\u53d1\u89c9\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u53ef\u4e0d\u662f\uff0c\u8fd9\u548c\u5c1a\u5f88\u673a\u8b66\uff0c\u54b1\u4eec\u53ef\u5f97\u591a\u52a0\u5c0f\u5fc3\u624d\u662f\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u70b9\u4e86\u70b9\u5934\uff0c\u53ea\u89c1\u90a3\u548c\u5c1a\u8f6c\u8fc7\u8eab\u53bb\uff0c\u659c\u523a\u91cc\u5954\u5411\u4e00\u5904\u6811\u6797\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u76b1\u7709\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4ed6\u4e3a\u4f55\u820d\u4e86\u5c0f\u5f84\u4e0d\u8d70\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4e5f\u8bb8\u5df2\u7ecf\u5230\u4e86\u5730\u5934\u5427\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8d70\u51fa\u8349\u4e1b\uff0c\u7565\u4e00\u72b9\u8c6b\uff0c\u62c9\u7740\u5b89\u5c0f\u840d\uff0c\u4fbf\u5411\u90a3\u677e\u6797\u5954\u53bb\u3002 \u3000\u3000\u8d70\u5230\u8fd1\u524d\uff0c\u8fd9\u624d\u53d1\u73b0\uff0c\u6253\u90a3\u677e\u6797\u4e4b\u4e2d\u9732\u51fa\u4e86\u4e00\u7f15\u6de1\u9ec4\u7684\u706f\u5149\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u5403\u4e86\u4e00\u60ca\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8fd9\u513f\u4f4f\u7740\u6709\u4eba\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5374\u7528\u4f20\u97f3\u4e4b\u672f\u63a5\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u54b1\u4eec\u518d\u8981\u8bf4\u8bdd\uff0c\u53ef\u5343\u4e07\u4e0d\u80fd\u51fa\u58f0\uff0c\u53ea\u53ef\u7528\u4f20\u97f3\u4e4b\u672f\u2026\u2026\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u4e3e\u6b65\u5411\u90a3\u706f\u5149\u884c\u53bb\u3002 \u3000\u3000\u4ed6\u4eec\u4e0d\u4f46\u6b66\u529f\u6781\u9ad8\uff0c\u800c\u4e14\u884c\u52a8\u5341\u5206\u5c0f\u5fc3\uff0c\u76f4\u81f3\u903c\u8fd1\u90a3\u9732\u51fa\u706f\u5149\u7684\u8305\u820d\u4e4b\u4e0b\uff0c\u5c4b\u5185\u4e4b\u4eba\uff0c\u4ecd\u672a\u53d1\u89c9\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u6253\u91cf\u4e86\u8305\u820d\u4e00\u773c\uff0c\u5411\u5b89\u5c0f\u840d\u7528\u4f20\u97f3\u8bf4\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4f60\u5230\u90a3\u7a97\u4e0b\u53bb\u77a7\u77a7\uff0c\u6211\u5c31\u5b88\u5728\u8fd9\u6b63\u95e8\u7684\u524d\u9762\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u70b9\u4e86\u70b9\u5934\uff0c\u77ee\u8eab\u4fbf\u5411\u7a97\u4e0b\u86c7\u884c\u800c\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u76ee\u7779\u5b89\u5c0f\u840d\u903c\u8fd1\u7a97\u53f0\uff0c\u81ea\u5df1\u4e5f\u8f7b\u8f7b\u5730\u79fb\u5411\u90a3\u534a\u63a9\u7684\u95e8\u3002 \u3000\u3000\u4ed6\u884c\u52a8\u5341\u5206\u654f\u6377\uff0c\u9760\u8fd1\u67f4\u95e8\uff0c\u5f20\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u95e8\u5185\u5bc2\u7136\uff0c\u4e00\u95ea\u8eab\uff0c\u4fbf\u8d34\u7740\u95e8\u7f1d\uff0c\u7a9c\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u5ba4\u5185\u9664\u4e86\u4e00\u5ea7\u795e\u9f9b\uff0c\u548c\u4e2d\u95f4\u9ad8\u6302\u7684\u4e00\u76cf\u4e07\u5e74\u706f\u800c\u5916\uff0c\u522b\u65e0\u4e00\u7269\u3002 \u3000\u3000\u5de6\u53f3\u4e24\u4fa7\u5404\u6709\u4e00\u6247\u6728\u95e8\uff0c\u5374\u662f\u5173\u5f97\u5f88\u7d27\u3002 \u3000\u3000\u4ed6\u7f13\u7f13\u5730\u5411\u53f3\u4fa7\u90a3\u95e8\u884c\u53bb\uff0c\u8d34\u8033\u4e00\u542c\uff0c\u5ba4\u5185\u679c\u7136\u4f20\u6765\u4f4e\u8bed\u4e4b\u58f0\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u9053\uff1a\u201c\u60f3\u5fc5\u90a3\u548c\u5c1a\u5c31\u5728\u8fd9\u95f4\u623f\u4e2d\u4e86\u2026\u2026\u201d \u3000\u3000\u601d\u5ff5\u672a\u5df2\uff0c\u5ffd\u7136\u6240\u5f97\u5ba4\u5185\u4f20\u6765\u4e00\u58f0\u66b4\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u63a5\u7740\u662f\u4e00\u9635\u7830\u7830\u4e4b\u58f0\uff0c\u6253\u5ba4\u5185\u54cd\u8d77\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5403\u4e86\u4e00\u60ca\uff0c\u8fde\u5fd9\u4fa7\u8eab\u9000\u5411\u95e8\u5916\uff0c\u4f46\u6728\u95e8\u672a\u542f\uff0c\u663e\u7136\u5ba4\u5185\u4e4b\u4eba\uff0c\u5e76\u672a\u53d1\u73b0\u81ea\u5df1\uff01 \u3000\u3000\u4f46\u4ed6\u8f6c\u5ff5\u4e00\u60f3\uff0c\u6697\u9053\uff1a\u201c\u7cdf\u4e86\uff01\u516b\u6210\u5b89\u5c0f\u840d\u66b4\u9732\u884c\u8ff9\u4e86\uff01\u201d \u3000\u3000\u4e00\u5ff5\u53ca\u6b64\uff0c\u5fc3\u4e2d\u5927\u4e3a\u7740\u6025\uff0c\u4e3e\u624b\u4e00\u638c\uff0c\u4fbf\u5411\u90a3\u6728\u95e8\u62cd\u53bb\u3002 \u3000\u3000\u8f69\u7136\u5927\u9707\uff0c\u6728\u5c51\u7eb7\u98de\uff0c\u90a3\u6247\u539a\u95e8\uff0c\u7adf\u7136\u88ab\u9707\u5f97\u788e\u6210\u516b\u5757\uff01 \u3000\u3000\u8dc3\u5165\u5ba4\u5185\uff0c\u53ea\u89c1\u5ba4\u5185\u5df2\u65e0\u4e00\u4eba\u3002 \u3000\u3000\u4f46\u7a97\u5916\u5374\u4f20\u6765\u53f1\u559d\u4e4b\u58f0\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u6765\u4e0d\u53ca\u6253\u91cf\u5ba4\u5185\u5149\u666f\uff0c\u957f\u8eab\u4e00\u8dc3\uff0c\u4fbf\u81ea\u90a3\u7a97\u6237\u7a9c\u51fa\u3002 \u3000\u3000\u53ea\u6761\u4e09\u6761\u4eba\u5f71\u5728\u7a97\u5916\u6253\u5728\u4e00\u8d77\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u7565\u4e00\u6ce8\u89c6\uff0c\u53d1\u73b0\u9664\u4e86\u90a3\u548c\u5c1a\u800c\u5916\uff0c\u8fd8\u6709\u4e00\u4f4d\u4e2d\u5e74\u58ee\u6c49\uff0c\u4e24\u4eba\u6b63\u5408\u624b\u56f4\u653b\u5b89\u5c0f\u840d\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u770b\u4e86\u4e00\u773c\uff0c\u4fbf\u77e5\u5b89\u5c0f\u840d\u5e94\u4ed8\u8fd9\u4e24\u4eba\uff0c\u5b9e\u662f\u7ef0\u7ef0\u6709\u4f59\uff0c\u5f53\u4e0b\u4e0d\u518d\u4e0a\u524d\uff0c\u5374\u662f\u8f6c\u8eab\u5411\u5c4b\u95e8\u884c\u53bb\uff0c\u4e00\u638c\u632f\u5f00\u90a3\u53e6\u4e00\u6247\u6728\u95e8\uff01 \u3000\u3000\u4e00\u80a1\u5e7d\u9999\uff0c\u6251\u9f3b\u800c\u6765\uff0c\u65b9\u96ea\u5b9c\u500f\u7136\u4e00\u60ca\uff0c\u63a9\u9f3b\u8fde\u9000\u4e09\u6b65\u3002 \u3000\u3000\u81ea\u4ece\u4ed6\u9047\u5230\u5b8b\u5b5a\u4ee5\u540e\uff0c\u7ecf\u9a8c\u9605\u5386\uff0c\u90fd\u589e\u8fdb\u4e86\u6781\u591a\uff0c\u9f3b\u4e2d\u55c5\u5f97\u5f02\u9999\uff0c\u4fbf\u4e0d\u6562\u7a0d\u6709\u5927\u610f\uff0c\u9000\u5f00\u4e86\u4e09\u5c3a\u4ee5\u540e\uff0c\u6df1\u6df1\u7684\u8fd0\u4e86\u4e00\u53e3\u6c14\uff0c\u53d1\u89c9\u5e76\u65e0\u5f02\u72b6\uff0c\u8fd9\u624d\u7f13\u6b65\u5411\u90a3\u623f\u5185\u884c\u53bb\u3002 \u3000\u3000\u4eba\u76ee\u6240\u89c1\uff0c\u4e0d\u7981\u4f7f\u65b9\u96ea\u5b9c\u5927\u4e3a\u5403\u60ca\uff01 \u3000\u3000\u679c\u7136\u4e0d\u51fa\u81ea\u5df1\u731c\u60f3\uff0c\u8fd9\u95f4\u5c0f\u623f\uff0c\u7adf\u7136\u662f\u9648\u8bbe\u7684\u5341\u5206\u534e\u4e3d\uff0c\u5986\u53f0\u6905\u955c\uff0c\u7ee3\u69bb\u9526\u5e10\uff0c\u76f4\u662f\u5927\u5bb6\u95fa\u9601\u7684\u5367\u623f\u4e00\u822c\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5728\u5367\u623f\u4e2d\u5de1\u89c6\u4e86\u4e00\u5468\uff0c\u9664\u4e86\u90a3\u4e9b\u9648\u8bbe\u4e4b\u5916\uff0c\u522b\u65e0\u4e1d\u6beb\u53d1\u73b0\uff0c\u53ea\u597d\u9000\u51fa\u5ba4\u5916\u3002 \u3000\u3000\u4f46\u4ed6\u4f9d\u7136\u89c9\u5f97\u8fd9\u680b\u8305\u820d\uff0c\u6709\u7740\u4e00\u79cd\u4f7f\u4eba\u8ff7\u60d1\u7684\u611f\u89c9\uff0c\u597d\u50cf\u770b\u6765\u770b\u53bb\uff0c\u90fd\u610f\u72b9\u672a\u5c3d\uff0c\u603b\u89c9\u5f97\u5176\u4e2d\u6709\u4ec0\u4e48\u5730\u65b9\uff0c\u81ea\u5df1\u672a\u66fe\u77a7\u51fa\u6765\uff0c\u662f\u4ee5\u4ed6\u5728\u8fd9\u8305\u820d\u56db\u5468\uff0c\u6765\u6765\u56de\u56de\uff0c\u8d70\u4e86\u4e0d\u4e0b\u56db\u5708\u4e4b\u591a\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u65b9\u96ea\u5b9c\u5374\u662f\u6beb\u65e0\u6240\u83b7\uff01 \u3000\u3000\u8fd9\u65f6\uff0c\u90a3\u4e00\u50e7\u4e00\u4fd7\u548c\u5b89\u5c0f\u840d\u7684\u82e6\u6597\uff0c\u5df2\u8fdb\u5165\u5341\u5206\u7d27\u5f20\u9636\u6bb5\uff0c\u65b9\u96ea\u5b9c\u8d2e\u7acb\u89c2\u6218\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u5979\u4e3a\u4f55\u4e0d\u4e0b\u715e\u624b\u5462\uff1f\u8fd9\u4e24\u4eba\u7684\u6b66\u529f\uff0c\u5b9e\u662f\u4e0d\u582a\u5979\u4e00\u51fb\u554a\u2026\u2026\u201d \u3000\u3000\u5bfb\u601d\u4e4b\u95f4\uff0c\u53ea\u89c1\u90a3\u4e2d\u5e74\u58ee\u6c49\u7a81\u7136\u731b\u653b\u4e86\u4e24\u638c\uff0c\u56de\u624b\u81ea\u957f\u886b\u4e4b\u4e0b\uff0c\u53d6\u51fa\u4e00\u5bf9\u94c1\u7b14\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u4e00\u653b\u5b89\u5c0f\u840d\u524d\u5fc3\uff0c\u4e00\u53d6\u5b89\u5c0f\u840d\u5c0f\u8179\uff01 \u3000\u3000\u8fd9\u7b49\u62db\u5f0f\uff0c\u5982\u662f\u5bf9\u7537\u4eba\u65bd\u5c55\uff0c\u5e76\u65e0\u4e0d\u53ef\uff0c\u4f46\u56e0\u5b89\u5c0f\u840d\u4e43\u662f\u4e00\u540d\u5c11\u5973\uff0c\u8fd9\u4e9b\u62db\u5f0f\u51fa\u624b\uff0c\u6b63\u662f\u5168\u90fd\u72af\u4e86\u8f7b\u8584\u4e4b\u5fcc\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u8fd9\u5bb6\u4f19\u867d\u7136\u4e0d\u77e5\u5b89\u5c0f\u840d\u4e43\u662f\u5c11\u5973\uff0c\u4f46\u4ed6\u8fd9\u7b49\u62db\u5f0f\uff0c\u5b9a\u5c06\u62db\u6765\u6740\u8eab\u4e4b\u7978\u2026\u2026\u201d \u3000\u3000\u679c\u7136\u65b9\u96ea\u5b9c\u5e76\u672a\u6599\u9519\uff0c\u90a3\u4e2d\u5e74\u6c49\u5b50\u7684\u4e24\u62db\u521a\u9012\u51fa\uff0c\u53ea\u89c1\u5b89\u5c0f\u840d\u8138\u8272\u4e00\u53d8\uff0c\u6012\u53f1\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u627e\u6b7b\u2026\u2026\u201d \u3000\u3000\u9661\u7136\u95f4\u773c\u524d\u767d\u5149\u4e4d\u95ea\uff0c\u65b9\u96ea\u5b9c\u8fd8\u6ca1\u770b\u6e05\u695a\u5b89\u5c0f\u840d\u7528\u4ec0\u4e48\u62db\u672f\uff0c\u5c31\u542c\u5f97\u90a3\u4f4d\u4e2d\u5e74\u6c49\u5b50\u5389\u543c\u4e00\u58f0\uff0c\u5f80\u540e\u8dcc\u51fa\u4e86\u4e24\u4e08\u8fdc\u8fd1\u3002 \u3000\u3000\u4ed6\u53e3\u4e2d\u9c9c\u8840\u72c2\u55b7\uff0c\u4e24\u624b\u62ff\u4e0d\u7262\u5175\u5203\uff0c\u4e00\u5bf9\u94c1\u7b14\u91cd\u91cd\u5730\u8dcc\u5728\u5730\u4e0a\uff0c\u8eab\u5b50\u6643\u4e86\u4e24\u6643\uff0c\u7ec8\u4e8e\u5411\u524d\u683d\u5012\u4e0b\u53bb\u3002 \u3000\u3000\u90a3\u540d\u50e7\u4fa3\u5927\u5403\u4e00\u60ca\uff0c\u64a4\u62db\u9000\u4e86\u4e94\u5c3a\uff0c\u6123\u5728\u5f53\u5730\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u65f6\u4e5f\u5927\u5927\u5728\u5fc3\u4e2d\u72c2\u9707\uff0c\u5931\u58f0\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u5929\u9e64\u95e8\u7684\u6b66\u529f\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u53f3\u624b\u4e00\u626c\uff0c\u4e00\u5e45\u67d4\u82e5\u65e0\u7269\u7684\u767d\u7eeb\uff0c\u7535\u95ea\u4e00\u822c\u7684\u63a3\u4e86\u56de\u53bb\u3002 \u3000\u3000\u5979\u67f3\u7709\u5012\u7ad6\uff0c\u674f\u76ee\u5706\u7741\uff0c\u7f13\u7f13\u5730\u903c\u8fd1\u90a3\u540d\u50e7\u4fa3\uff0c\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u60f3\u6d3b\u547d\uff0c\u6700\u597d\u8001\u8001\u5b9e\u5b9e\u5730\u56de\u7b54\u6211\u51e0\u4e2a\u95ee\u9898\uff01\u201d \u3000\u3000\u90a3\u540d\u50e7\u4eba\uff0c\u8fd9\u65f6\u5df2\u5413\u6389\u4e86\u4e09\u9b42\u516d\u9b44\uff0c\u5b89\u5c0f\u840d\u58f0\u97f3\u4e00\u843d\uff0c\u4ed6\u7acb\u5373\u8fde\u58f0\u9053\uff1a\u201c\u65bd\u4e3b\u8bf7\u8bb2\uff0c\u5c0f\u50e7\u77e5\u65e0\u4e0d\u8a00\u5c31\u662f\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u70b9\u4e86\u70b9\u5934\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u5728\u5c11\u6797\uff0c\u8d23\u53f8\u4f55\u804c\uff01\u201d \u3000\u3000\u90a3\u50e7\u4eba\u9053\uff1a\u201c\u5c0f\u50e7\u6cd5\u667a\uff0c\u5728\u5c11\u6797\u5bfa\u4e2d\u6267\u638c\u5e99\u4ea7\u5eb6\u52a1\u4e4b\u804c\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u76b1\u7709\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u53ef\u6709\u5fc3\u53db\u79bb\u5c11\u6797\u4e86\uff1f\u201d \u3000\u3000\u90a3\u50e7\u4eba\u5931\u8272\u9053\uff1a\u201c\u5c0f\u50e7\u4e0d\u6562\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u51b7\u7b11\u9053\uff1a\u201c\u90a3\u4e2d\u5e74\u6c49\u5b50\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u6cd5\u667a\u8fdf\u7591\u4e86\u4e00\u4e0b\uff0c\u63a5\u9053\uff1a\u201c\u4ed6\u2026\u2026\u662f\u2026\u2026\u516b\u86ee\u9f13\u738b\u91d1\u957f\u5e9a\u7684\u5f1f\u5b50\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u65f6\u8d70\u4e86\u8fc7\u6765\uff0c\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u516b\u86ee\u9f13\u738b\u91d1\u957f\u5e9a\uff0c\u4e5f\u662f\u4e94\u9b54\u4e4b\u4e00\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u65b9\u5144\uff0c\u5929\u9b54\u5973\u83ab\u975e\u5df2\u4e0e\u4ed6\u4eec\u8054\u624b\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u60f3\u8d77\u90a3\u8305\u820d\u4e4b\u4e2d\uff0c\u90a3\u95f4\u95fa\u623f\u4e4b\u4e8b\uff0c\u4e0d\u7981\u7b11\u9053\uff1a\u201c\u5927\u6709\u53ef\u80fd\uff01\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u5411\u6cd5\u667a\u559d\u9053\uff1a\u201c\u91d1\u957f\u5e9a\u7684\u5f1f\u5b50\uff0c\u600e\u4f1a\u4e0e\u4f60\u76f8\u8bc6\uff1f\u201d \u3000\u3000\u6cd5\u667a\u9eef\u7136\u4f4e\u4e0b\u5934\uff0c\u957f\u53f9\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u8fd9\u662f\u5c0f\u50e7\u7684\u7f6a\u8fc7\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u8c01\u7ba1\u4f60\u7f6a\u8fc7\u4e0d\u7f6a\u8fc7\uff1f\u4f60\u6700\u597d\u5c11\u8fd9\u4e48\u541e\u541e\u5410\u5410\u7684\uff01\u201d \u3000\u3000\u6cd5\u667a\u5408\u5341\u9053\uff1a\u201c\u65bd\u4e3b\uff0c\u4ed6\u2026\u2026\u4e43\u662f\u5c0f\u50e7\u4fd7\u5bb6\u7684\u5144\u957f\uff0c\u662f\u4ee5\uff0c\u5c0f\u50e7\u4e0d\u5f97\u4e0d\u88ab\u4ed6\u6240\u80c1\u8feb\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u4ed6\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u6cd5\u667a\u9053\uff1a\u201c\u82d7\u5927\u521a\uff0c\u6b66\u6797\u4e2d\u4eba\u79f0\u4ed6\u4e00\u58f0\u94c1\u7b14\u602a\u5ba2\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u8fd9\u53ef\u662f\u4e0e\u5929\u9b54\u5973\u6709\u4ec0\u4e48\u52fe\u7ed3\u5417\uff1f\u201d \u3000\u3000\u6cd5\u667a\u6447\u5934\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u5c0f\u50e7\u5168\u7136\u4e0d\u77e5\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6012\u9053\uff1a\u201c\u6211\u4e0d\u4fe1\uff01\u201d \u3000\u3000\u6cd5\u667a\u957f\u957f\u4e00\u53f9\u9053\uff1a\u201c\u65bd\u4e3b\u4e0d\u4fe1\uff0c\u5c0f\u50e7\u4e5f\u6ca1\u6709\u529e\u6cd5\uff0c\u5176\u5b9e\uff0c\u5c0f\u50e7\u6bcf\u6b21\u89c1\u4ed6\uff0c\u53ea\u662f\u4f20\u9012\u5bfa\u4e2d\u52a8\u9759\u800c\u5df2\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u90a3\u8305\u820d\u4e4b\u4e2d\uff0c\u6709\u7740\u4e00\u95f4\u5973\u4eba\u5c45\u4f4f\u7684\u95fa\u623f\uff0c\u4f60\u53ef\u77e5\u9053\uff1f\u201d \u3000\u3000\u6cd5\u667a\u70b9\u5934\u9053\uff1a\u201c\u516c\u5b50\u53ef\u662f\u6307\u7684\u8fdb\u95e8\u5de6\u624b\u7684\u90a3\u95f4\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u6cd5\u667a\u9053\uff1a\u201c\u90a3\u95f4\u95fa\u623f\uff0c\u4e43\u662f\u5bb6\u5ac2\u7684\u5367\u5ba4\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u6014\u9053\uff1a\u201c\u4f60\u5ac2\u5b50\uff1f\u5979\u662f\u8c01\uff1f\u201d \u3000\u3000\u6cd5\u667a\u9053\uff1a\u201c\u82d7\u5927\u521a\u7684\u5ac2\u5b50\uff0c\u81f3\u4e8e\u5979\u662f\u4ec0\u4e48\u51fa\u8eab\uff0c\u5c0f\u50e7\u4ece\u672a\u8fc7\u95ee\uff0c\u4e0d\u8fc7\u2026\u2026\u5c0f\u50e7\u53ea\u77e5\u9053\u5979\u540d\u53eb\u5c0f\u7fe0\u800c\u5df2\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5ffd\u7136\u63a5\u9053\uff1a\u201c\u65b9\u5144\uff0c\u5c0f\u7fe0\u4e0d\u662f\u90a3\u5c39\u68a6\u8377\u7684\u4e24\u4e2a\u4f7f\u5973\u4e2d\u7684\u4e00\u540d\u5417\uff1f\u8fd9\u4e48\u770b\u6765\uff0c\u5929\u9b54\u5973\u548c\u91d1\u957f\u5e9a\u65e9\u5c31\u52fe\u7ed3\u5728\u4e00\u8d77\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e5f\u8bb8\u662f\uff01\u4f46\u5929\u4e0b\u540c\u540d\u4e4b\u4eba\u751a\u591a\uff0c\u54b1\u4eec\u53ef\u4ee5\u6162\u6162\u4ece\u5c39\u68a6\u8377\u5904\u63a2\u8be2\u2026\u2026\u201d \u3000\u3000\u8bed\u97f3\u7565\u7565\u4e00\u5e06\u63a5\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u90a3\u82d7\u5927\u521a\u8fd8\u6709\u6551\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6012\u9053\uff1a\u201c\u4ed6\u51fa\u624b\u8f7b\u8584\uff0c\u6211\u5df2\u5e9f\u4e86\u4ed6\u7684\u4e00\u8eab\u6b66\u529f\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fae\u5fae\u4e00\u7b11\uff0c\u5411\u6cd5\u667a\u9053\uff1a\u201c\u5927\u5e08\uff0c\u4f60\u7531\u5bfa\u4e2d\u51fa\u6765\u4e4b\u65f6\uff0c\u5bfa\u4e2d\u7684\u50e7\u4fa3\uff0c\u90fd\u5f80\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u6cd5\u667a\u9053\uff1a\u201c\u4ed6\u4eec\u90fd\u5728\u7f57\u6c49\u5802\u4e2d\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u51b7\u7b11\u9053\uff1a\u201c\u4e10\u5e2e\u7684\u90a3\u51e0\u4f4d\u957f\u8001\u5462\uff1f\u83ab\u975e\u4e5f\u5728\u7f57\u6c49\u5802\uff1f\u201d \u3000\u3000\u6cd5\u667a\u9053\uff1a\u201c\u5c0f\u50e7\u79bb\u5f00\u4e4b\u65f6\uff0c\u4ed6\u4eec\u679c\u771f\u5168\u5728\u7f57\u6c49\u5802\u5185\uff0c\u56e0\u4e3a\u2026\u2026\u201d \u3000\u3000\u4ed6\u8fdf\u7591\u4e86\u4e0b\uff0c\u6168\u7136\u4e00\u53f9\uff0c\u63a5\u9053\uff1a\u201c\u90a3\u4e94\u5927\u9b54\u4e3b\u4e4b\u4e2d\u7684\u6bd2\u5927\u592b\uff0c\u5df2\u7136\u62a2\u5148\u4e00\u6b65\uff0c\u6765\u5230\u4e86\u5bfa\u4e2d\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u5f97\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u6076\u5b54\u660e\u5417\uff1f\u4ed6\u5df2\u7ecf\u6765\u5230\u5c11\u6797\uff1f\u201d \u3000\u3000\u6cd5\u667a\u9053\uff1a\u201c\u53ef\u4e0d\uff1f\u5c0f\u50e7\u8d76\u6765\u6b64\u5904\uff0c\u5c31\u662f\u5411\u6211\u957f\u5144\u62a5\u660e\u6bd2\u5927\u592b\u5df2\u7ecf\u62b5\u8fbe\u7684\u6d88\u606f\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7565\u4e00\u6c89\u541f\uff0c\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u54b1\u4eec\u56de\u5bfa\u53bb\u5427\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u8fd9\u548c\u5c1a\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u542c\u4ed6\u4e4b\u8a00\uff0c\u5e76\u975e\u5927\u6076\u4e4b\u5f92\uff0c\u54b1\u4eec\u4e5f\u4e0d\u7528\u5bf9\u4ed6\u4e0b\u624b\u4e86\uff01\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u5411\u6cd5\u667a\u9053\uff1a\u201c\u5927\u5e08\u4e43\u662f\u5c11\u6797\u5f1f\u5b50\uff0c\u65e5\u62dc\u957f\u4f5b\u5ff5\u7ecf\uff0c\u60f3\u5fc5\u660e\u81ea\uff0c\u4e00\u9519\u4e0d\u53ef\u518d\u9519\uff0c\u4ee4\u5144\u6b66\u529f\u5df2\u5e9f\uff0c\u4f60\u6700\u597d\u9001\u4ed6\u4e0b\u5c71\uff0c\u52a9\u4ed6\u4f5c\u4e2a\u5b89\u5b88\u5df1\u7684\u826f\u6c11\uff0c\u80dc\u8fc7\u518d\u5728\u6b66\u6797\u4e4b\u4e2d\u5174\u98ce\u4f5c\u6d6a\uff0c\u8fdf\u65e9\u96be\u514d\u8eab\u9996\u5f02\u5904\u4e4b\u7978\u2026\u2026\u201d \u3000\u3000\u6cd5\u667a\u95fb\u8a00\uff0c\u5927\u4e3a\u611f\uff0c\u5408\u5341\u9876\u793c\uff0c\u606d\u656c\u5730\u5e94\u9053\uff1a\u201c\u5c0f\u50e7\u7ec8\u8eab\u4e0d\u5fd8\u65bd\u4e3b\u5927\u5fb7\u2026\u2026\u201d \u3000\u3000\u4fef\u8eab\u62b1\u8d77\u82d7\u5927\u521a\uff0c\u5411\u4e24\u4eba\u7565\u4e00\u4e3a\u793c\uff0c\u5927\u6b65\u8d70\u53bb\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u6709\u4e9b\u4e0d\u6ee1\u65b9\u96ea\u5b9c\u8fd9\u7b49\u5bbd\u5927\u4e3a\u6000\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4fbf\u5b9c\u4e86\u4ed6\u4eec\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u80fd\u7f62\u624b\u65f6\u4e14\u7f62\u624b\uff0c\u5f97\u9976\u4eba\u5904\u4e14\u9976\u4eba\uff0c\u54b1\u4eec\u53c8\u4f55\u5fc5\u4e0e\u4ed6\u4eec\u65a4\u65a4\u8ba1\u8f83\u5462\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u54b1\u4eec\u8fd9\u5c31\u56de\u5bfa\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u6bd2\u5927\u592b\u5df2\u6765\uff0c\u54b1\u4eec\u5c11\u4e0d\u5f97\u8981\u53bb\u4e0e\u4ed6\u4eec\u5468\u65cb\u4e00\u756a\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u8fd9\u680b\u8305\u820d\u5462\uff1f\u8981\u4e0d\u8981\u653e\u706b\u70e7\u4e86\u5b83\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u5934\u9053\uff1a\u201c\u4e0d\u7528\u4e86\uff0c\u54b1\u4eec\u5c82\u662f\u6740\u4eba\u653e\u706b\u4e4b\u5f92\uff1f\u8d24\u5f1f\uff0c\u54b1\u4eec\u8d70\u5427\uff01\u201d \u3000\u3000\u8f6c\u8eab\u4fbf\u5411\u5c11\u6797\u5bfa\u5954\u53bb\u3002 \u3000\u3000\u54ea\u6d88\u7247\u523b\u65f6\u5149\uff0c\u4e24\u4eba\u4fbf\u5df2\u56de\u5230\u5bfa\u5185\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u7a0d\u4e00\u6253\u91cf\uff0c\u56de\u987e\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u90a3\u7f57\u6c49\u5802\u5fc5\u662f\u5728\u54b1\u4eec\u5148\u524d\u672a\u66fe\u5230\u8fc7\u4e4b\u5904\uff0c\u54b1\u4eec\u5c31\u4e13\u8d70\u4fa7\u5f84\uff0c\u5c31\u4e0d\u5fc5\u53bb\u5411\u5b8b\u8001\u4ed6\u4eec\u67e5\u95ee\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u4e0d\u9519\u554a\uff0c\u59be\u8eab\u4e5f\u8fd9\u4e48\u60f3\u2026\u2026\u201d \u3000\u3000\u5f53\u4e0b\u4e24\u4eba\u5c55\u5f00\u8eab\u5f62\uff0c\u4fbf\u5411\u53f3\u4fa7\u4e00\u5e26\u5c4b\u5b57\u5bfb\u53bb\u3002 \u3000\u3000\u7a7f\u8fc7\u51e0\u91cd\u9662\u843d\uff0c\u5728\u4e00\u7247\u6797\u6728\u91cd\u91cd\u63a9\u6620\u4e4b\u4e0b\uff0c\u7ec8\u4e8e\u627e\u5230\u4e86\u5c11\u6797\u91cd\u5730\u7f57\u6c49\u5802\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u4e3e\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u90a3\u7f57\u6c49\u5802\u5185\uff0c\u706f\u706b\u96ea\u4eae\uff0c\u4f46\u56e0\u56db\u5468\u6811\u6728\u906e\u63a9\uff0c\u4e0d\u5230\u8fd1\u524d\uff0c\u65e0\u4ece\u53d1\u73b0\u3002 \u3000\u3000\u5f53\u4e0b\u5411\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u54b1\u4eec\u5165\u5185\u4ee5\u540e\uff0c\u975e\u662f\u4e07\u5f97\u4e0d\u5df2\uff0c\u6700\u597d\u4e0d\u8981\u51fa\u624b\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u4e00\u6014\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u5144\u5f1f\u662f\u60f3\u66ff\u5c11\u6797\u50e7\u4fa3\uff0c\u7559\u4e00\u70b9\u9762\u5b50\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u5982\u662f\u4ed6\u4eec\u4e0d\u654c\u6076\u5b54\u660e\u65f6\uff0c\u54b1\u4eec\u4ecd\u7136\u4e00\u65c1\u5750\u89c6\u4e0d\u7ba1\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f53\u771f\u5230\u4e86\u90a3\u7b49\u65f6\u5019\uff0c\u81ea\u7136\u53c8\u5f53\u522b\u8bba\u4e86\uff01\u4e0d\u8fc7\uff0c\u7167\u6211\u731c\u60f3\uff0c\u90a3\u6076\u5b54\u660e\u4e0d\u4f1a\u5982\u6b64\u611a\u8822\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u65b9\u5144\uff0c\u8fd9\u7f57\u6c49\u5802\u5185\u706f\u5149\u901a\u660e\uff0c\u4e3a\u4f55\u4e0d\u95fb\u4e00\u4e1d\u6253\u6597\u4e4b\u58f0\uff1f\u83ab\u975e\u2026\u2026\u4ed6\u4eec\u5168\u90fd\u4f24\u5728\u6076\u5b54\u660e\u7684\u624b\u4e0b\u4e86\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u5934\u9053\uff1a\u201c\u4e0d\u4f1a\u5427\uff01\u201d \u3000\u3000\u4ed6\u53e3\u4e2d\u867d\u7136\u5982\u6b64\u56de\u7b54\uff0c\u4f46\u5fc3\u4e2d\u53ef\u4e5f\u5e76\u65e0\u628a\u63e1\uff0c\u5f53\u4e0b\u4e00\u62c9\u5b89\u5c0f\u840d\uff0c\u63a5\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u54b1\u4eec\u5feb\u5feb\u5165\u5185\uff01\u201d \u3000\u3000\u4e24\u4eba\u8054\u8882\u8de8\u4e0a\u77f3\u9636\uff0c\u4e3e\u76ee\u671b\u53bb\uff0c\u4e0d\u7531\u5f97\u540c\u65f6\u4e00\u5446\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u7f57\u6c49\u5802\u4e2d\uff0c\u706f\u5149\u5982\u663c\uff0c\u4f46\u9664\u4e86\u5f53\u4e2d\u7684\u795e\u5e54\u4f4e\u5782\uff0c\u548c\u4e24\u53a2\u4e00\u5341\u516b\u5c0a\u7f57\u6c49\u5851\u50cf\u800c\u5916\uff0c\u6839\u672c\u6ca1\u6709\u534a\u4e1d\u4eba\u5f71\uff01 \u3000\u3000\u5b89\u5c0f\u840d\u5446\u4e86\u4e00\u5446\uff0c\u7acb\u5373\u6012\u9053\uff1a\u201c\u65b9\u5144\uff0c\u54b1\u4eec\u4e0a\u4e86\u90a3\u6cd5\u667a\u7684\u5f53\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u90a3\u6cd5\u667a\u5982\u662f\u6709\u5fc3\u6b3a\u9a97\u6211\u4eec\uff0c\u53ea\u6015\u4e5f\u4e0d\u4f1a\u611a\u7b28\u5f97\u8981\u54b1\u4eec\u56de\u5230\u5bfa\u4e2d\u5462\uff1f\u4ed6\u5927\u53ef\u4ee5\u987a\u53e3\u8bf4\u51fa\u5bfa\u4e2d\u7684\u4efb\u4f55\u4e00\u5904\u6240\u5728\uff0c\u8981\u54b1\u4eec\u591a\u8dd1\u4e00\u4e9b\u51a4\u6789\u8def\u554a\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u53ef\u662f\uff0c\u8fd9\u7f57\u6c49\u5802\u4e2d\u660e\u660e\u6ca1\u6709\u4e00\u4e2a\u4eba\uff0c\u8fd9\u53c8\u600e\u4e48\u8bb2\u5462\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u6050\u6015\u773c\u4e0b\u5bfa\u4e2d\u53c8\u6709\u4e86\u6781\u5927\u7684\u53d8\u52a8\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u76b1\u7709\u9053\uff1a\u201c\u54b1\u4eec\u600e\u4e48\u529e\uff1f \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u56de\u8f6c\u65b9\u4e08\u9759\u5ba4\uff0c\u770b\u770b\u638c\u95e8\u5927\u5e08\u2026\u2026\u201d \u3000\u3000\u4e24\u4eba\u4e0d\u518d\u9017\u7559\uff0c\u8f6c\u8eab\u4fbf\u8eab\u65b9\u4e08\u7985\u9662\u5954\u53bb\u3002 \u3000\u3000\u9002\u624d\u4e4b\u4e8b\uff0c\u5bf9\u4ed6\u4eec\u4e24\u4eba\u800c\u8a00\uff0c\u5b9e\u5728\u662f\u5927\u5927\u51fa\u4e4e\u610f\u6599\uff0c\u662f\u4ee5\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u4e24\u4eba\u7adf\u7136\u5fd8\u4e86\u518d\u5728\u7f57\u6c49\u5802\u5185\u5bdf\u770b\u4e00\u756a\u3002 \u3000\u3000\u5426\u5219\uff0c\u4ed6\u4eec\u4e00\u5b9a\u53ef\u4ee5\u53d1\u89c9\u90a3\u795e\u9f9b\u4e4b\u540e\uff0c\u6b63\u6709\u4e00\u53cc\u660e\u4eae\u7684\u773c\u775b\uff0c\u5728\u6ce8\u89c6\u7740\u4ed6\u4eec\u7684\u884c\u52a8\u3002 \u3000\u3000\u4e24\u4eba\u5306\u5fd9\u5730\u56de\u5230\u65b9\u4e08\u7985\u9662\uff0c\u5fc3\u4e2d\u53ef\u662f\u751a\u4e3a\u7126\u8651\uff0c\u53ea\u89c1\u5230\u90a3\u5b8b\u5b5a\u6b63\u7ad9\u5728\u95e8\u5916\u5f20\u671b\uff0c\u624d\u7b97\u653e\u4e0b\u5fc3\u6765\u3002 \u3000\u3000\u5b8b\u5b5a\u76ee\u7779\u4e24\u4eba\u56de\u6765\uff0c\u4e0d\u7981\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u4eec\u6b64\u523b\u6536\u83b7\u5982\u4f55\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7709\u5934\u4e00\u76b1\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u65b9\u4e08\u9759\u5ba4\u8fd8\u53ef\u5e73\u5b89\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u5f88\u597d\uff0c\u5165\u4e91\u638c\u95e8\u4eba\u5927\u529f\u5373\u5c06\u544a\u6210\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u957f\u957f\u5730\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u662f\u665a\u8f88\u767d\u62c5\u4e86\u5fc3\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u542c\u5f97\u5fc3\u4e2d\u4e00\u52a8\uff0c\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u83ab\u975e\u5916\u95f4\u51fa\u4e86\u4ec0\u4e48\u602a\u4e8b\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b63\u662f\u51fa\u4e86\u8bb8\u591a\u602a\u4e8b\u3002\u201d \u3000\u3000\u5f53\u4e0b\u628a\u6b64\u884c\u7ecf\u8fc7\uff0c\u7565\u7565\u8bf4\u4e86\u4e00\u904d\u3002 \u3000\u3000\u5b8b\u5b5a\u53ea\u542c\u5f97\u8fde\u8fde\u6293\u5934\u9053\uff1a\u201c\u6709\u8fd9\u7b49\u4e8b\uff1f\u8001\u673d\u53ef\u662f\u5934\u4e00\u906d\u9047\u5230\u2026\u2026\u8fd9\u5168\u5bfa\u7684\u50e7\u4eba\uff0c\u600e\u4f1a\u5ffd\u7136\u4e0d\u89c1\u4e86\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u76b1\u7709\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u90a3\u5165\u4e91\u5927\u5e08\u8fd8\u8981\u591a\u4e45\u624d\u53ef\u6210\u529f\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u5feb\u4e86\uff0c\u5927\u7ea6\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bdd\u97f3\u672a\u5df2\uff0c\u8033\u4e2d\u5ffd\u7136\u4f20\u6765\u5165\u4e91\u5927\u5e08\u7684\u58f0\u97f3\u9053\uff1a\u201c\u5b8b\u65bd\u4e3b\uff0c\u8001\u8872\u5df2\u7136\u9192\u6765\uff0c\u5feb\u8bf7\u4e24\u4f4d\u5c0f\u65bd\u4e3b\u5165\u5185\u8bf4\u8bdd\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u3001\u5b89\u5c0f\u840d\u4e0d\u7b49\u5b8b\u5b5a\u8bf4\u8bdd\uff0c\u62a2\u5148\u4e00\u6b65\uff0c\u4fbf\u5954\u5165\u4e86\u5ba4\u5185\u3002 \u3000\u3000\u867d\u7136\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u751a\u4e3a\u53d1\u6025\uff0c\u4f46\u4ed6\u4f9d\u7136\u5341\u5206\u6709\u793c\u5730\u5411\u5165\u4e91\u5927\u5e08\u62b1\u62f3\u9053\uff1a\u201c\u606d\u559c\u5927\u5e08\u795e\u529f\u7ec3\u5c31\u2026\u2026\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\u597d\u8bf4\uff0c\u82e5\u975e\u51e0\u4f4d\u4ed7\u4e49\u76f8\u52a9\uff0c\u8001\u8872\u53c8\u54ea\u80fd\u5b89\u5fc3\u4e60\u7ec3\u2026\u2026\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5916\u95f4\u4e4b\u4e8b\uff0c\u60f3\u5fc5\u504f\u65c1\u5c0f\u65bd\u4e3b\u751a\u591a\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u5934\uff0c\u82e6\u7b11\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u6709\u4e00\u6869\u4e8b\uff0c\u53ea\u6015\u533a\u533a\u8bf4\u51fa\u6765\uff0c\u638c\u95e8\u4eba\u4e5f\u4e0d\u4f1a\u76f8\u4fe1\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5c11\u6797\u5bfa\u4e2d\uff0c\u773c\u4e0b\u7adf\u7136\u4e0d\u89c1\u4e00\u540d\u5c11\u6797\u50e7\u4fa3\u3002\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u95fb\u8a00\uff0c\u679c\u771f\u662f\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u600e\u4e48\u4f1a\uff1f\u5c0f\u65bd\u4e3b\u53ea\u6015\u770b\u9519\u4e86\u5427\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u5934\u9053\uff1a\u201c\u5728\u4e0b\u5df2\u7136\u8e0f\u904d\u8d35\u5bfa\uff0c\u5c31\u662f\u4e0d\u66fe\u89c1\u5230\u4e00\u4eba\u3002\u201d \u3000\u3000\u5f53\u4e0b\u628a\u6240\u9047\u5230\u7684\u4e8b\uff0c\u7565\u7565\u8bf4\u4e86\u4e00\u904d\uff0c\u4f46\u4ed6\u5fc3\u5b58\u5fe0\u539a\uff0c\u5c06\u90a3\u6cd5\u667a\u4e4b\u4e8b\uff0c\u5374\u9690\u8d77\u672a\u8bf4\u3002 \u3000\u3000\u5165\u4e91\u638c\u95e8\u4eba\u542c\u5f97\u6014\u4e86\u4e00\u6014\uff0c\u76ee\u6ce8\u5165\u5723\u5927\u5e08\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5e08\u5144\uff0c\u8fd9\u4e8b\u5f53\u771f\u4e0d\u5bfb\u5e38\u4e86\u3002\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u6b64\u4e8b\u53eb\u8001\u8872\u4e0d\u6562\u76f8\u4fe1\uff0c\u82e5\u975e\u65b9\u5c0f\u65bd\u4e3b\u6709\u9519\uff0c\u90a3\u5fc5\u662f\u522b\u6709\u91cd\u5927\u4e4b\u4e8b\u53d1\u73b0\uff0c\u4f7f\u5f97\u672c\u5bfa\u5b50\u5f1f\uff0c\u5168\u90e8\u51fa\u5bfa\u53bb\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u6697\u6697\u5fd6\u9053\uff1a\u201c\u770b\u6765\u4ed6\u4eec\u662f\u4e0d\u76f8\u4fe1\u4e86\uff01\u201d \u3000\u3000\u4f46\u4ed6\u5e76\u672a\u591a\u8bf4\uff0c\u53ea\u56e0\u90a3\u4f4d\u4e10\u5e2e\u5e2e\u4e3b\u201c\u5357\u534e\u795e\u4e5e\u201d\u970d\u9e23\u98ce\u5df2\u7136\u5927\u58f0\u9053\uff1a\u201c\u65b9\u8001\u5f1f\uff0c\u8001\u673d\u7684\u51e0\u4f4d\u5144\u5f1f\u5462\uff1f\u83ab\u975e\u4e5f\u4e0d\u5bfa\u4e2d\u4e86\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u665a\u8f88\u5e76\u672a\u89c1\u5230\u4ed6\u4eec\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u76b1\u7709\u9053\uff1a\u201c\u4ed6\u4eec\u4e0d\u5728\u5bfa\u4e2d\uff0c\u90a3\u5c31\u66f4\u5947\u602a\u4e86\uff01\u96be\u9053\u2026\u2026\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u4e00\u8dc3\u800c\u8d77\uff0c\u5411\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u54b1\u4eec\u5fc5\u5f97\u4eb2\u81ea\u524d\u53bb\u67e5\u770b\u4e00\u756a\u4e86\uff01 \u3000\u3000\u5165\u4e91\u5927\u5e08\u70b9\u5934\u9053\uff1a\u201c\u8001\u8872\u4ea6\u6709\u6b64\u5fc3\u2026\u2026\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u63a5\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\uff0c\u4f60\u8bf4\u90a3\u7f57\u6c49\u5802\u4e2d\uff0c\u4e5f\u4e0d\u66fe\u53d1\u73b0\u6709\u4eba\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u6b63\u662f\u4e0d\u89c1\u4e00\u4eba\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u638c\u95e8\u5e08\u5f1f\uff0c\u672c\u5bfa\u5b50\u5f1f\uff0c\u4e0d\u5c11\u4e8e\u4e94\u767e\uff0c\u5982\u662f\u6709\u4eba\u8981\u4e00\u4e3e\u6b8b\u6740\uff0c\u90a3\u662f\u4e0d\u53ef\u80fd\u7684\u4e86\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f55\u51b5\u4ed6\u4eec\u90fd\u8fd8\u6709\u7740\u4e00\u8eab\u6b66\u529f\uff01\u5c0f\u5f1f\u4e0d\u4fe1\u4ed6\u4eec\u4e1a\u5df2\u88ab\u5bb3\uff01\u201d \u3000\u3000\u65b9\u96ea\u76f4\u63a5\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u5426\u51fa\u4e86\u4ec0\u4e48\u4e8b\uff0c\u665a\u8f88\u4e0d\u6562\u59be\u52a0\u63e3\u6d4b\uff0c\u53ea\u662f\u5168\u5bfa\u6773\u65e0\u4e00\u4eba\uff0c\u786e\u5c5e\u4e8b\u5b9e\u3002\u201d \u3000\u3000\u5165\u4e91\u638c\u95e8\u4eba\u5408\u5341\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\u2026\u2026\u201d \u3000\u3000\u4ed6\u6b32\u8a00\u53c8\u6b62\uff0c\u53ea\u53eb\u4e86\u4e00\u58f0\uff0c\u5c31\u6ca1\u5f80\u4e0b\u518d\u8bf4\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u6447\u4e86\u6447\uff0c\u9053\uff1a\u201c\u6ca1\u6709\u4ec0\u4e48\uff0c\u8001\u8872\u51b3\u5b9a\u4eb2\u81ea\u67e5\u770b\u4e00\u4e0b\u2026\u2026\u201d \u3000\u3000\u7ad9\u8d77\u8eab\u6765\uff0c\u5411\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u5e08\u5144\uff0c\u4f60\u4e5f\u53bb\u5417\uff1f\u201d \u3000\u3000\u5165\u5723\u9053\uff1a\u201c\u5f53\u7136\uff0c\u6b64\u4e8b\u5982\u6b64\u8e4a\u8df7\uff0c\u600e\u53ef\u7b49\u95f2\u89c6\u4e4b\uff1f\u201d \u3000\u3000\u5165\u4e91\u770b\u4e86\u970d\u9e23\u98ce\u4e00\u773c\uff0c\u9053\uff1a\u201c\u970d\u5e2e\u4e3b\uff0c\u4f60\u5462\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u5927\u58f0\u9053\uff1a\u201c\u9047\u5230\u8fd9\u7b49\u602a\u4e8b\uff0c\u970d\u67d0\u81ea\u7136\u4e0d\u7518\u4eba\u540e\uff0c\u8d70\u2026\u2026\u201d \u3000\u3000\u7adf\u7136\u5f53\u5148\u4e3e\u6b65\uff0c\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u5165\u4e91\u3001\u5165\u5723\u4e8c\u50e7\uff0c\u968f\u5728\u970d\u9e23\u98ce\u8eab\u540e\uff0c\u51fa\u4e86\u65b9\u4e08\u9759\u5ba4\u3002 \u3000\u3000\u5b8b\u5b5a\u770b\u4e86\u65b9\u96ea\u5b9c\u3001\u5b89\u5c0f\u840d\u4e24\u4eba\u4e00\u773c\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u4e5f\u4e00\u540c\u53bb\u5427\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u3001\u5b89\u5c0f\u840d\u4e24\u4eba\u5fc3\u4e2d\u7591\u601d\u672a\u89e3\uff0c\u81ea\u7136\u662f\u6025\u6b32\u60f3\u77e5\u9053\u7ed3\u679c\uff0c\u5f53\u4e0b\u63a5\u9053\uff1a\u201c\u5f53\u771f\u8981\u53bb\u7684\u4e86\uff01\u8fd9\u7b49\u602a\u4e8b\uff0c\u54b1\u4eec\u600e\u80fd\u9519\u8fc7\u2026\u2026\u201d \u3000\u3000\u8bf4\u8bdd\u95f4\uff0c\u4eba\u5df2\u62a2\u5148\u5954\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4e00\u884c\u4eba\u51fa\u4e86\u65b9\u4e08\uff0c\u4fbf\u76f4\u5954\u5927\u6bbf\u3002 \u3000\u3000\u6b63\u5982\u65b9\u96ea\u5b9c\u6240\u8a00\uff0c\u5c11\u6797\u5168\u5bfa\uff0c\u5f53\u771f\u4e0d\u89c1\u4e00\u4eba\uff0c\u8fd9\u7b49\u60c5\u5f62\u4f7f\u5f97\u5165\u4e91\u638c\u95e8\u4eba\u8138\u8272\u5927\u53d8\uff0c\u989d\u9645\u6d94\u6d94\u89c1\u6c57\u3002 \u3000\u3000\u970d\u9e23\u98ce\u5219\u60ca\u8bb6\u7126\u8651\uff0c\u517c\u800c\u6709\u4e4b\uff0c\u53ea\u6709\u5b8b\u5b5a\u5341\u5206\u51b7\u9759\uff0c\u5f53\u4e00\u884c\u4eba\u91cd\u65b0\u8f6c\u5230\u90a3\u5927\u6bbf\u4e4b\u5185\u65f6\uff0c\u4ed6\u5ffd\u7136\u5927\u58f0\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u8001\u673d\u6709\u4e00\u53e5\u8bdd\uff0c\u4e0d\u77e5\u5f53\u4e0d\u5f53\u8bf4\uff1f\u201d \u3000\u3000\u5165\u4e91\u76b1\u7709\u9053\uff1a\u201c\u65bd\u4e3b\uff0c\u8fd9\u662f\u4ec0\u4e48\u65f6\u5019\u4e86\uff1f\u6709\u8bdd\uff0c\u5c3d\u7ba1\u8bf7\u8bf4\u5427\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u4e5f\u63a5\u9053\uff1a\u201c\u4eca\u65e5\u4e4b\u4e8b\uff0c\u4e43\u662f\u5c11\u6797\u767e\u5e74\u672a\u66fe\u9047\u5230\u8fc7\u7684\u602a\u4e8b\uff0c\u65bd\u4e3b\u5982\u6709\u6240\u89c1\uff0c\u5343\u4e07\u5feb\u5feb\u8bf4\u51fa\uff0c\u8001\u8872\u5144\u5f1f\u611f\u6fc0\u8fd8\u6765\u4e0d\u53ca\u5462\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u70b9\u5934\u9053\uff1a\u201c\u8fd9\u4e00\u70b9\uff0c\u8001\u673d\u660e\u767d\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u5165\u4e91\u638c\u95e8\u9053\uff1a\u201c\u65bd\u4e3b\uff0c\u7eb5\u7136\u4f60\u51b2\u649e\u4e86\u672c\u5bfa\uff0c\u8001\u8872\u4e5f\u4e0d\u4f1a\u602a\u4f60\uff0c\u65bd\u4e3b\u653e\u5fc3\u5feb\u8bf4\u5427\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u638c\u95e8\u4eba\u8fd9\u4e48\u8bf4\uff0c\u8001\u673d\u53ef\u5c31\u8981\u76f4\u8bf4\u4e86\uff01\u201d \u3000\u3000\u8bed\u97f3\u987f\u4e86\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5c31\u76ee\u4e0b\u60c5\u51b5\u770b\u6765\uff0c\u8d35\u5bfa\u5b50\u5f1f\uff0c\u5982\u975e\u88ab\u4eba\u8bf1\u79bb\u5230\u4e00\u5904\u5f88\u8fdc\u7684\u6240\u5728\uff0c\u90a3\u5c31\u662f\u8d35\u5bfa\u4e2d\u6709\u5bb9\u4eba\u85cf\u8eab\u4e4b\u5904\uff0c\u638c\u95e8\u5c1a\u672a\u53d1\u73b0\uff01\u201d \u3000\u3000\u4ed6\u8fd9\u8bdd\u8bf4\u4e86\u51fa\u6765\uff0c\u53ea\u628a\u65b9\u96ea\u5b9c\u542c\u5f97\u4e00\u6014\uff0c\u53ea\u89c9\u5f97\u5b8b\u5b5a\u8fd9\u8bdd\u8bf4\u7684\u5341\u5206\u51fa\u5947\uff0c\u82e5\u8bf4\u5bfa\u4e2d\u50e7\u4fa3\u5168\u90e8\u88ab\u4eba\u8bf1\u79bb\uff0c\u5c31\u5df2\u4e0d\u5927\u53ef\u80fd\uff0c\u4f55\u51b5\u518d\u8bf4\u5bfa\u4e2d\u8fd8\u6709\u90a3\u80fd\u591f\u5bb9\u7eb3\u6570\u767e\u4eba\u7684\u5730\u65b9\uff0c\u5c45\u7136\u4f1a\u8fde\u5165\u4e91\u638c\u95e8\u4eba\u4e5f\u4e0d\u77e5\u6653\uff1f \u3000\u3000\u4ed6\u60d1\u7136\u5730\u671b\u7740\u5b8b\u5b5a\uff0c\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u53ea\u4e0d\u77e5\u90a3\u5165\u4e91\u638c\u95e8\u4eba\u5c06\u600e\u751f\u56de\u7b54\uff1f\u201d \u3000\u3000\u90a3\u5165\u4e91\u638c\u95e8\u5728\u542c\u5f97\u5b8b\u5b5a\u4e4b\u8a00\u4ee5\u540e\uff0c\u7adf\u662f\u8138\u8272\u4e00\u53d8\uff0c\u6c89\u541f\u4e86\u534a\u664c\uff0c\u65b9\u9053\uff1a\u201c\u5b8b\u65bd\u4e3b\uff0c\u8fd9\u7b2c\u4e00\u70b9\uff0c\u8001\u8872\u4e0d\u5927\u76f8\u4fe1\uff0c\u56e0\u4e3a\u2026\u2026\u6570\u767e\u4eba\u884c\u52a8\uff0c\u4e0d\u4f1a\u90a3\u7b49\u7a0d\u65e0\u58f0\u606f\uff0c\u4f55\u51b5\uff0c\u4ed6\u4eec\u4e4b\u4e2d\uff0c\u603b\u4e0d\u4f1a\u8fde\u4e00\u4e2a\u524d\u6765\u62a5\u4fe1\u4e4b\u4eba\u7686\u65e0\u554a\uff01\u201d \u3000 \u7b2c\u4e8c\u5341\u4e94\u56de\u3000\u9b3c\u738b\u73b0\u8eab \u3000 \u3000\u3000\u5b8b\u5b5a\u5411\u5165\u4e91\u5927\u5e08\u95ee\u9053\uff1a\u201c\u5982\u6b64\u8bf4\uff0c\u90a3\u662f\u8d35\u5bfa\u53e6\u6709\u5bb9\u4eba\u85cf\u8eab\u4e4b\u5904\u4e86\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u8fdf\u7591\u4e86\u4e00\u4e0b\uff0c\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u672c\u5bfa\u786e\u662f\u6709\u7740\u4e00\u5904\u53ef\u4ee5\u9690\u85cf\u5343\u4eba\u4ee5\u4e0a\u7684\u5730\u4e0b\u79d8\u9053\uff0c\u53ea\u662f\uff0c\u8001\u8872\u60f3\u4e0d\u51fa\u4ed6\u4eec\u4e3a\u4e86\u4f55\u6545\uff0c\u8981\u9690\u8eab\u5176\u95f4\u4e0d\u51fa\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u5f97\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6697\u9053\uff1a\u201c\u539f\u6765\u8fd9\u5c11\u6797\u5bfa\u4e2d\uff0c\u4e5f\u6709\u85cf\u4eba\u4e4b\u6240\u2026\u2026\u201d \u3000\u3000\u5bfb\u601d\u95f4\uff0c\u8033\u4e2d\u542c\u5f97\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u8fd9\u53ef\u4e0d\u662f\u4f60\u60f3\u5f97\u51fa\u60f3\u4e0d\u51fa\u7684\u65f6\u5019\u4e86\uff01\u6700\u597d\uff0c\u54b1\u4eec\u53bb\u77a7\u77a7\uff01\u201d \u3000\u3000\u5165\u4e91\u4e00\u6014\u9053\uff1a\u201c\u54ea\u91cc\u53bb\u77a7\u77a7\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u90a3\u85cf\u4eba\u7684\u5730\u9053\u5440\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u5ffd\u7136\u9762\u73b0\u4e3a\u96be\u4e4b\u8272\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u970d\u9e23\u98ce\u770b\u5f97\u5fc3\u4e2d\u5927\u5927\u4e0d\u5feb\uff0c\u63a5\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u4f60\u2026\u2026\u600e\u4e48\u4e86\uff1f\u83ab\u975e\u8d35\u5bfa\u4e4b\u4e2d\uff0c\u8fd8\u6709\u7740\u4e0d\u53ef\u544a\u4eba\u7684\u79d8\u5bc6\u5417\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u75db\u82e6\u5730\u770b\u4e86\u5165\u5723\u5927\u5e08\u4e00\u773c\uff0c\u6ca1\u6709\u56de\u7b54\u3002 \u3000\u3000\u5165\u5723\u5927\u5e08\u95ed\u76ee\u5408\u5341\u7684\u5ff5\u4e86\u4e00\u9635\u4f5b\uff0c\u5ffd\u7136\u53cc\u76ee\u66b4\u7741\uff0c\u5927\u58f0\u5411\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u638c\u95e8\u5e08\u5f1f\uff0c\u5e26\u4ed6\u4eec\u53bb\uff0c\u4e00\u5207\u7f6a\u5b7d\uff0c\u90fd\u7531\u5c0f\u5144\u627f\u62c5\u4fbf\u662f\uff01\u201d \u3000\u3000\u4e24\u4f4d\u4e24\u548c\u5c1a\u8fd9\u7b49\u8868\u60c5\uff0c\u53ea\u628a\u65b9\u96ea\u5b9c\u548c\u5b89\u5c0f\u840d\u7740\u5f97\u5446\u4e86\u534a\u664c\uff0c\u751a\u81f3\u8fde\u5b8b\u5b5a\u90a3\u7b49\u8001\u6c5f\u6e56\uff0c\u4e5f\u89c9\u5f97\u5927\u4e3a\u832b\u7136\u3002 \u3000\u3000\u5165\u4e91\u5927\u5e08\u957f\u957f\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5e08\u5144\uff0c\u6069\u5e08\u4f5b\u8c15\uff0c\u5c0f\u5f1f\u5982\u662f\u4e0d\u9075\uff0c\u5c31\u4e0d\u914d\u518d\u4f5c\u5c11\u6797\u638c\u95e8\uff0c\u5c0f\u5f1f\u613f\u610f\u8fdb\u5165\u90a3\u5730\u4e0b\u79d8\u9053\uff0c\u4f46\u5e08\u5144\u5fc5\u5f97\u5e94\u5141\u5c0f\u5f1f\u4e00\u6869\u5927\u4e8b\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u5c0f\u5f1f\u8fdb\u5165\u5730\u9053\u4ee5\u540e\uff0c\u5e08\u5144\u5e94\u5141\u7acb\u5373\u63a5\u638c\u5c11\u6797\u95e8\u6237\uff01\u201d \u3000\u3000\u5165\u5723\u95fb\u8a00\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u4f60\u8fd9\u662f\u4ece\u4f55\u8bf4\u8d77\uff1f\u5c0f\u5144\u5982\u6709\u6b64\u5fc3\uff0c\u90a3\u4e5f\u4e0d\u4f1a\u4f4f\u5728\u8386\u7530\u5206\u9662\u6570\u5341\u5e74\u4e86\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u5e08\u5144\u4e0d\u7b54\u5e94\uff0c\u5c0f\u5f1f\u6012\u96be\u9075\u547d\uff01\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u970d\u9e23\u98ce\u5ffd\u7136\u5927\u58f0\u9053\uff1a\u201c\u4e24\u4f4d\u5012\u771f\u662f\u5ba2\u6c14\u5f97\u5f88\u554a\uff01\u53ea\u662f\u90a3\u5c11\u6797\u6570\u767e\u540d\u5b50\u5f1f\u7684\u4e0b\u843d\uff0c\u4f60\u4eec\u5c31\u600e\u5730\u5168\u4e0d\u653e\u5728\u5fc3\u4e0a\u5462\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u4f3c\u662f\u5403\u4e86\u4e00\u60ca\u822c\u7684\u6d51\u8eab\u4e00\u9707\uff0c\u5408\u5341\u5411\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u5e08\u5144\uff0c\u4e3a\u4e86\u5c11\u6797\u58f0\u8a89\uff0c\u5e08\u5144\u5feb\u4e9b\u7b54\u5e94\u5c0f\u5f1f\u5427\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u6447\u5934\u9053\uff1a\u201c\u8fd9\u4e2a\u5c0f\u5144\u65e0\u6cd5\u5e94\u5141\u2026\u2026\u201d \u3000\u3000\u663e\u7136\uff0c\u4ed6\u4eec\u4e24\u4e2a\u5404\u6301\u5df1\u89c1\uff0c\u8c01\u90fd\u4e0d\u80af\u8ba9\u6b65\u3002 \u3000\u3000\u970d\u9e23\u98ce\u51b7\u7b11\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u5927\u5e08\uff0c\u8001\u592b\u4eca\u65e5\u65b9\u59cb\u53d1\u73b0\uff0c\u4f60\u4eec\u771f\u662f\u8fc2\u8822\u5f97\u53ef\u601c\uff01 \u3000\u3000\u90a3\u5730\u9053\u5165\u53e3\u4f55\u5728\uff1f\u5018\u662f\u4f60\u4eec\u5c11\u6797\u5f1f\u5b50\u65e0\u6cd5\u5165\u5185\uff0c\u544a\u77e5\u8001\u592b\uff0c\u4e14\u7531\u8001\u592b\u4ee3\u4f60\u4eec\u5165\u5185\u5982\u4f55\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u5927\u58f0\u9053\uff1a\u201c\u662f\u554a\uff0c\u970d\u5e2e\u4e3b\u6240\u89c1\u751a\u5999\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e5f\u89c9\u5f97\u970d\u9e23\u98ce\u8fd9\u4e2a\u53d8\u901a\u7684\u529e\u6cd5\u5f88\u597d\u3002\u4f46\u4e0d\u6599\u90a3\u5165\u5723\u5927\u5e08\u5ffd\u7136\u6447\u5934\u9053\uff1a\u201c\u4e0d\u6210\uff0c\u90a3\u5730\u9053\u7684\u5165\u53e3\uff0c\u60ac\u6709\u4e0a\u4ee3\u638c\u95e8\u624b\u8c15\uff0c\u9664\u4e86\u5c11\u6797\u73b0\u4efb\u638c\u95e8\uff0c\u4ed6\u4eba\u51b3\u4e0d\u8bb8\u5165\u5185\uff0c\u65bd\u4e3b\u7684\u610f\u89c1\uff0c\u8001\u8872\u65e0\u6cd5\u63a5\u7eb3\uff01\u201d \u3000\u3000\u970d\u545c\u98ce\u5927\u4e3a\u4e0d\u89e3\u5730\u6012\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u5148\u5e08\u624b\u8c15\uff0c\u8001\u8872\u7b49\u53ea\u662f\u5949\u8c15\u884c\u4e8b\uff0c\u4e3a\u4e86\u4ec0\u4e48\uff0c\u90a3\u5c31\u4e0d\u662f\u8001\u8872\u6240\u5f53\u95ee\u7684\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u5f97\u751a\u662f\u5947\u602a\uff0c\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u5018\u662f\u8d35\u5bfa\u4e0a\u4e00\u4ee3\u638c\u95e8\u6709\u6b64\u9057\u8bad\uff0c\u90a3\u4f60\u81ea\u5df1\u5165\u5185\u4fbf\u662f\uff0c\u600e\u5730\u53c8\u8981\u5165\u5723\u5927\u5e08\u7b54\u5141\u63a5\u638c\u5c11\u6797\u95e8\u6237\u5728\u5148\u5462\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u4e3a\u96be\u5730\u770b\u770b\u65b9\u96ea\u5b9c\uff0c\u7f13\u7f13\u63a5\u9053\uff1a\u201c\u5176\u4e2d\u9053\u7406\uff0c\u8001\u8872\u5982\u4e0d\u8bf4\u51fa\uff0c\u5c0f\u65bd\u4e3b\u662f\u4e0d\u4f1a\u660e\u767d\u7684\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u665a\u8f88\u6b63\u8981\u8bf7\u6559\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u5148\u5e08\u624b\u8c15\u8a00\u660e\uff0c\u4e0d\u4f46\u8fdb\u5165\u5730\u9053\u4e4b\u4eba\uff0c\u8981\u8eab\u4e3a\u73b0\u4efb\u5c11\u6797\u638c\u95e8\uff0c\u800c\u4e14\u8fd8\u7279\u522b\u6307\u51fa\uff0c\u51e1\u662f\u8fdb\u5165\u5730\u9053\u7684\u5c11\u6797\u5f1f\u5b50\uff0c\u51fa\u6765\u4e4b\u540e\uff0c\u5c31\u518d\u4e5f\u4e0d\u8bb8\u62c5\u4efb\u638c\u95e8\u804c\u4f4d\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u4e3a\u4f55\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u8fd9\u4e2a\u4e48\uff1f\u8001\u8872\u4e5f\u4e0d\u660e\u767d\u4e3a\u4f55\u4e86\uff01\u5148\u5e08\u65e2\u7136\u5982\u6b64\u4ea4\u4ee3\uff0c\u603b\u6709\u539f\u6545\uff0c\u8001\u8872\u4f9d\u7136\u662f\u9075\u547d\u884c\u4e8b\uff0c\u662f\u4ee5\u5927\u5e08\u5144\u4e0d\u5141\u63a5\u638c\u95e8\u6237\uff0c\u8001\u8872\u5c31\u4e0d\u4fbf\u5165\u5730\u9053\u4e4b\u4e2d\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u604d\u7136\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff01\u602a\u4e0d\u5f97\u638c\u95e8\u4eba\u4e00\u518d\u63a8\u8bff\u62d6\u5ef6\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u76ee\u5149\u8f6c\u5411\u5165\u5723\u5927\u5e08\uff0c\u63a5\u9053\uff1a\u201c\u5927\u548c\u5c1a\uff0c\u4f60\u4e3a\u4f55\u4e0d\u7b54\u5e94\u554a\uff1f\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u5408\u5341\u9053\uff1a\u201c\u8001\u65bd\u4e3b\uff0c\u5165\u5723\u6709\u81ea\u77e5\u4e4b\u660e\uff0c\u56db\u5341\u5e74\u524d\u5c31\u66fe\u4e3a\u6b64\u800c\u51e0\u906d\u9003\u7985\u4e4b\u8ba5\uff0c\u5982\u4eca\u53c8\u8981\u5165\u5723\u63a5\u638c\u5c11\u6797\u95e8\u6237\uff0c\u90a3\u5c82\u4e0d\u662f\u5927\u8fdd\u521d\u8877\uff0c\u6709\u6096\u4f5b\u7956\u6267\u4e00\u4e4b\u5fb7\u5417\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u5927\u5e08\uff0c\u4f60\u53ef\u77e5\u9053\uff0c\u4f60\u5982\u4e0d\u7b54\u5e94\uff0c\u773c\u4e0b\u5c31\u65e0\u6cd5\u67e5\u660e\u8d35\u5bfa\u90a3\u6570\u767e\u540d\u5b50\u5f1f\u7684\u4e0b\u843d\u4e86\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u6447\u5934\u9053\uff1a\u201c\u65bd\u4e3b\uff0c\u4f60\u53ea\u77e5\u5176\u4e00\uff0c\u4e0d\u77e5\u5176\u4e8c\u3002\u638c\u95e8\u4eba\u6240\u8bf4\uff0c\u4e43\u662f\u5148\u5e08\u624b\u8c15\u4e0d\u5047\uff0c\u4f46\u5148\u5e08\u6b63\u679c\u4e4b\u65f6\uff0c\u4e5f\u66fe\u7279\u522b\u544a\u8bc9\u8fc7\u8001\u8872\u548c\u638c\u95e8\u5e08\u5f1f\uff0c\u5982\u662f\u5bfa\u4e2d\u6709\u4e86\u4ec0\u4e48\u91cd\u5927\u53d8\u6545\uff0c\u5fc5\u987b\u8fdb\u5165\u5730\u9053\u7684\u8bdd\uff0c\u8fd8\u6709\u4e00\u6761\u53d8\u901a\u7684\u529e\u6cd5\u53ef\u9041\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u4ec0\u4e48\u529e\u6cd5\uff1f\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u7531\u672c\u5bfa\u5177\u6709\u957f\u8001\u8eab\u4efd\u7684\u5f1f\u5b50\u4e00\u4eba\uff0c\u5728\u4f5b\u7956\u4e4b\u524d\uff0c\u53d1\u4e0b\u72ec\u5750\u5341\u5e74\u6b7b\u5173\u7684\u5b8f\u613f\u4ee5\u4e3a\u5f00\u542f\u5730\u9053\u8d4e\u7f6a\u2026\u2026\u201d \u3000\u3000\u970d\u9e23\u98ce\u4e00\u6014\u9053\uff1a\u201c\u5341\u5e74\u6b7b\u5173\uff1f\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u970d\u9e23\u9053\uff1a\u201c\u90a3\u5f88\u82e6\u554a\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u8001\u65bd\u4e3b\uff0c\u8fd9\u5341\u5e74\u6b7b\u5173\uff0c\u5728\u8001\u8872\u9762\u8a00\uff0c\u5374\u4e0d\u8db3\u4ee5\u8a00\u82e6\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u5165\u4e91\u638c\u95e8\u53c8\u4f55\u4e3a\u4e0d\u5141\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u5c1a\u672a\u8bf4\u8bdd\uff0c\u5165\u4e91\u5927\u5e08\u5df2\u7136\u63a5\u9053\uff1a\u201c\u8001\u65bd\u4e3b\uff0c\u5148\u5e08\u6240\u6307\u7684\u90a3\u5341\u5e74\u6b7b\u5173\uff0c\u662f\u4f55\u7b49\u6ecb\u5473\uff0c\u8001\u65bd\u4e3b\u60f3\u5fc5\u4e0d\u660e\u767d\u5427\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u8001\u592b\u4e0d\u66fe\u51fa\u5bb6\uff0c\u81ea\u7136\u4e0d\u5927\u660e\u767d\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u548c\u5c1a\u5750\u5173\uff0c\u4e43\u662f\u5e38\u4e8b\uff0c\u5c31\u7b97\u5341\u5e74\u6b7b\u5173\u5f88\u82e6\uff0c\u5728\u4ed6\u4eec\u8fd9\u7b49\u5f97\u9053\u9ad8\u50e7\u770b\u6765\uff0c\u5e94\u4e5f\u662f\u7b97\u4e0d\u5f97\u4ec0\u4e48\u624d\u662f\uff0c\u4e3a\u4f55\u8fd9\u4f4d\u5165\u4e91\u638c\u95e8\u5374\u770b\u5f97\u8fd9\u822c\u4e25\u91cd\u5462\uff1f \u3000\u3000\u5165\u4e91\u5927\u5e08\u8fd9\u65f6\u5ffd\u7136\u957f\u957f\u5730\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5e2e\u4e3b\uff0c\u5148\u5e08\u6240\u6307\u7684\u5341\u5e74\u6b7b\u5173\uff0c\u5e76\u975e\u4e00\u822c\u7684\u7985\u5173\uff0c\u800c\u662f\u5341\u9b54\u70bc\u5fc3\uff0c\u5982\u5760\u8f6e\u56de\u7684\u5bc2\u706d\u5173\uff0c\u8eab\u4f53\u53d1\u80a4\u4e4b\u82e6\uff0c\u867d\u7136\u96be\u71ac\uff0c\u4f46\u5927\u5e08\u5144\u5fc5\u80fd\u5fcd\u53d7\uff0c\u7cbe\u795e\u610f\u5fd7\u7684\u714e\u70bc\uff0c\u9664\u975e\u6728\u77f3\uff0c\u5fc5\u7136\u65e0\u4ee5\u627f\u5f53\uff0c\u672c\u5bfa\u4ece\u8fbe\u6469\u7956\u5e08\u5f00\u5c71\u4ee5\u6765\uff0c\u80fd\u6e21\u6b64\u5341\u5e74\u6b7b\u5173\u7684\u957f\u8001\uff0c\u4e5d\u767e\u591a\u5e74\uff0c\u4e5f\u53ea\u4e0d\u8fc7\u6709\u5be5\u5be5\u4e09\u4e94\u4eba\uff0c\u5176\u95f4\u56e0\u800c\u6bc1\u5374\u7985\u57fa\uff0c\u518d\u5760\u8f6e\u56de\u9996\uff0c\u591a\u8fbe\u767e\u540d\u4ee5\u4e0a\uff0c\u5927\u5e08\u5144\u7518\u5192\u8fd9\u7b49\u5927\u9669\u5927\u96be\uff0c\u53eb\u8001\u8872\u600e\u80fd\u5b89\u5fc3\u5e94\u5141\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u5f53\u771f\u8fd9\u7b49\u8270\u96be\u5417\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u9519\u975e\u5f53\u771f\u8fd9\u7b49\u5371\u9669\uff0c\u8001\u8872\u4e5f\u4e0d\u4f1a\u963b\u6b62\u5927\u5e08\u5144\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u541f\u9053\uff1a\u201c\u5018\u662f\u4e24\u4f4d\u8981\u4e00\u76f4\u575a\u6301\uff0c\u8001\u673d\u5c31\u60f3\u4e0d\u51fa\u8fd8\u6709\u4ec0\u4e48\u529e\u6cd5\u8db3\u4ee5\u89e3\u51b3\u4eca\u65e5\u7684\u5c40\u9762\u4e86\uff01\u2019\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u5b89\u5c0f\u840d\u5ffd\u7136\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u4f9d\u6211\u770b\uff0c\u4e24\u4f4d\u5927\u5e08\u90fd\u81ea\u79c1\u5f97\u5f88\u554a\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u81ea\u7709\u4e00\u76b1\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\uff0c\u8001\u8872\u51b3\u5fc3\u65e9\u4e0b\uff0c\u51e0\u65f6\u81ea\u79c1\u4e86\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u8d35\u5bfa\u51e0\u767e\u540d\u7684\u5f1f\u4e8e\u4e0b\u843d\u4e0d\u660e\uff0c\u4f60\u4eec\u5374\u4e0d\u653e\u5728\u5fc3\u4e0a\uff0c\u800c\u53ea\u662f\u65a4\u65a4\u8ba1\u8f83\u4e8e\u4ec0\u4e48\u5148\u5e08\u9057\u547d\uff0c\u4f5b\u7956\u9057\u89c4\uff0c\u533a\u533a\u770b\u6765\uff0c\u8fd9\u4e0d\u557b\u5168\u662f\u6cbd\u540d\u9493\u8a89\u7684\u6b3a\u4eba\u4e4b\u8c08\uff0c\u53ea\u77e5\u81ea\u5df1\u800c\u4e0b\u987e\u95e8\u4e0b\u5b50\u5f1f\uff0c\u4e0d\u662f\u81ea\u79c1\uff0c\u53c8\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5979\u8bf4\u5f97\u632f\u632f\u6709\u8f9e\uff0c\u53ea\u628a\u5165\u4e91\u3001\u5165\u5723\u4e24\u4eba\u8bf4\u5f97\u5fc3\u5934\u731b\u9707\uff0c\u6562\u60c5\uff0c\u5b89\u5c0f\u840d\u5e76\u6ca1\u6709\u8bf4\u60dc\uff0c\u4ed6\u4eec\u5f53\u771f\u662f\u9519\u4e86\uff01 \u3000\u3000\u53ea\u89c1\u90a3\u5165\u4e91\u5927\u5e08\u5e72\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\u6559\u8bad\u5f97\u597d\uff0c\u8001\u8872\u660e\u767d\u4e86\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u66f4\u662f\u6fc0\u52a8\u5730\u8d70\u5411\u5165\u4e91\u5927\u5e08\u8eab\u524d\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u638c\u95e8\u5e08\u5f1f\uff0c\u4eca\u65e5\u4e4b\u4e8b\uff0c\u5c0f\u5144\u53ef\u8981\u52c9\u5f3a\u5e08\u5f1f\u4e00\u6b21\u4e86\uff0c\u638c\u95e8\u4eba\u5c3d\u7ba1\u53bb\u90a3\u5730\u9053\uff0c\u5176\u4ed6\u7684\u4e00\u5207\u540e\u679c\uff0c\u90fd\u7531\u5c0f\u5144\u62c5\u4ee3\u4fbf\u662f\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u5408\u5341\u9053\uff1a\u201c\u5c0f\u5f1f\u9075\u547d\uff01\u201d \u3000\u3000\u8f6c\u9762\u5411\u970d\u9e23\u98ce\u63a5\u9053\uff1a\u201c\u5e2e\u4e3b\uff0c\u54b1\u4eec\u4e00\u540c\u524d\u5f80\u5982\u4f55\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u8001\u673d\u5949\u966a\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u5927\u58f0\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u8981\u53bb\uff0c\u54b1\u4eec\u90fd\u5f97\u53bb\uff0c\u5018\u662f\u771f\u6709\u4ec0\u4e48\u610f\u5916\u4e4b\u4e8b\uff0c\u4e5f\u597d\u591a\u51e0\u4e2a\u5e2e\u624b\u554a\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u82e6\u7b11\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6709\u52b3\u51e0\u4f4d\u4e86\u2026\u2026\u201d \u3000\u3000\u5f53\u4e0b\u8f6c\u8eab\uff0c\u5927\u6b65\u8fc8\u5411\u7f57\u6c49\u5802\u65b9\u5411\u884c\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u9053\uff1a\u201c\u539f\u6765\u90a3\u7f57\u6c49\u5802\u4e2d\u8fd8\u6709\u5730\u9053\u5417\uff1f\u8fd9\u4e48\u770b\u6765\uff0c\u5148\u524d\u6211\u5012\u592a\u5927\u610f\u4e86\u4e00\u4e9b\u4e86\uff01\u201d \u3000\u3000\u4e00\u884c\u4eba\u5f88\u5feb\u4fbf\u6765\u5230\u4e86\u7f57\u6c49\u3001\u5802\u5185\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u5165\u4e91\u5927\u5e08\u7f13\u6b65\u4e0a\u524d\uff0c\u5411\u6b63\u9762\u7684\u795e\u9f9b\u62dc\u4e86\u4e09\u62dc\uff0c\u9ed8\u7977\u4e86\u4e00\u9635\uff0c\u8fd9\u624d\u5411\u90a3\u795e\u9f9b\u4e4b\u540e\u884c\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4e0d\u591a\u65f6\uff0c\u5ffd\u7136\u542c\u5f97\u4e00\u9635\u8f67\u8f67\u4e4b\u58f0\uff0c\u90a3\u795e\u9f9b\u524d\u7684\u795e\u6848\uff0c\u7a81\u7136\u8fc5\u5feb\u5730\u5411\u4e0a\u5347\u8d77\uff0c\u795e\u9f9b\u7684\u524d\u65b9\uff0c\u9732\u51fa\u4e86\u4e00\u9053\u95e8\u6237\u3002 \u3000\u3000\u970d\u9e23\u98ce\u4e00\u95ea\u8eab\uff0c\u4fbf\u62a2\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5b8b\u5b5a\u7709\u5934\u4e00\u76b1\uff0c\u5927\u559d\u9053\uff1a\u201c\u5e2e\u4e3b\u5c0f\u5fc3\u2026\u2026\u201d\u4ed6\u8bed\u97f3\u793a\u5df2\uff0c\u5374\u89c1\u4e00\u80a1\u8f7b\u70df\u8d77\u5904\uff0c\u970d\u9e23\u98ce\u5df2\u7136\u5927\u8896\u53cc\u6325\uff0c\u66b4\u559d\u4e00\u58f0\uff0c\u5012\u8dc3\u800c\u56de\u3002 \u3000\u3000\u6070\u5728\u6b64\u65f6\uff0c\u90a3\u5165\u4e91\u5927\u5e08\u4e5f\u5df2\u4ece\u795e\u9f9b\u4e4b\u540e\u8d70\u51fa\u6765\uff0c\u76ee\u7779\u65af\u60c5\uff0c\u4e0d\u7531\u5f97\u8138\u8272\u4e8c\u53d8\uff0c\u8131\u53e3\u9053\uff1a\u201c\u5e2e\u4e3b\uff0c\u90a3\u5730\u9053\u4e2d\u6709\u4eba\u5417\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u795e\u60c5\u6709\u4e9b\u6fc0\u52a8\uff0c\u76ee\u5149\u51dd\u6ce8\u90a3\u5730\u9053\u5165\u53e3\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u770b\u4e0a\u53bb\uff0c\u6709\u4eba\u62a2\u5148\u638c\u95e8\u4eba\u4e00\u6b65\u4e86\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u79fb\u6b65\u5411\u90a3\u5730\u9053\u5165\u53e3\u884c\u53bb\uff0c\u4e00\u9762\u63a5\u9053\uff1a\u201c\u970d\u5e2e\u4e3b\uff1f\u8001\u8872\u6709\u4e9b\u4e0d\u4fe1\u2026\u2026\u201d\u4ed6\u53e3\u4e2d\u867d\u7136\u662f\u8bf4\u7740\u4e0d\u4fe1\uff0c\u4f46\u6b65\u5c65\u4e4b\u95f4\uff0c\u5374\u663e\u5f97\u751a\u662f\u5c0f\u5fc3\uff0c\u79bb\u90a3\u5165\u53e3\u5c1a\u6709\u4e94\u5c3a\uff0c\u4fbf\u5df2\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u4f3c\u662f\u89c9\u7740\u751a\u662f\u597d\u73a9\uff0c\u62c9\u4e86\u62c9\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u54b1\u4eec\u4e5f\u8fc7\u53bb\u77a7\u77a7\uff0c\u597d\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u65e9\u6709\u6b64\u5fc3\uff0c\u4f46\u56e0\u90a3\u5165\u4e91\u638c\u95e8\u4e43\u4eba\u662f\u4e00\u6d3e\u5b97\u4e3b\uff0c\u5c24\u5176\u662f\u81ea\u5df1\u7b49\u4eba\u4e43\u662f\u5c11\u6797\u4f5c\u5ba2\uff0c\u8fd9\u7b49\u55a7\u5bbe\u593a\u4e3b\u7684\u884c\u52a8\uff0c\u4ed6\u5f53\u7136\u535e\u4fbf\u8d38\u7136\u800c\u884c\uff0c\u8fd9\u65f6\u542c\u5f97\u5b89\u5c0f\u840d\u5982\u6b64\u4e00\u95ee\uff0c\u5f53\u4e0b\u7b11\u9053\uff1a \u3000\u3000\u201c\u597d\uff0c\u4f46\u54b1\u4eec\u53ef\u4e0d\u80fd\u62a2\u5728\u638c\u95e8\u4eba\u4e4b\u5148\u2026\u2026\u201d\u4e24\u4eba\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u79fb\u6b65\u5411\u5165\u4e91\u5927\u5e08\u884c\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5165\u4e91\u5927\u5e08\u5df2\u6df1\u6df1\u5730\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u6717\u58f0\u5411\u5730\u9053\u4e2d\u7684\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u85cf\u8eab\u5728\u672c\u5bfa\u79d8\u9053\u4e4b\u4e2d\uff1f\u8fd8\u4e0d\u5feb\u5feb\u51fa\u6765\u89c1\u8fc7\u672c\u5ea7\u3002\u201d \u3000\u3000\u8bed\u97f3\u8fc7\u540e\uff0c\u4e45\u4e45\u4e0d\u95fb\u56de\u54cd\u3002 \u3000\u3000\u5165\u4e91\u5927\u5e08\u8138\u8272\u53c8\u662f\u4e00\u53d8\uff0c\u559d\u9053\uff1a\u201c\u4f55\u65b9\u9ad8\u4eba\u64c5\u5165\u672c\u5bfa\u79d8\u9053\uff1f\u5c0a\u9a7e\u518d\u4e0d\u51fa\u6765\uff0c\u53ef\u83ab\u602a\u8001\u8872\u4e0b\u624b\u65e0\u60c5\u4e86\uff01\u201d \u3000\u3000\u7b49\u4e86\u4e00\u4f1a\uff0c\u4f9d\u65e7\u5bc2\u7136\u3002\u6562\u60c5\uff0c\u90a3\u5730\u9053\u4e2d\u7684\u4eba\uff0c\u6839\u672c\u4e0d\u4e88\u7f6e\u7406\u3002 \u3000\u3000\u90a3\u5165\u4e91\u5927\u5e08\u5012\u597d\u50cf\u6ca1\u6709\u4e86\u4e3b\u610f\uff0c\u5446\u5446\u5730\u770b\u4e86\u5165\u5723\u5927\u5e08\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5927\u5e08\u5144\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u638c\u95e8\u4eba\u6709\u4f55\u89c1\u6559\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u8fd9\u79d8\u9053\u4e4b\u4e2d\uff0c\u83ab\u975e\u65e0\u4eba\u5728\u5185\uff1f\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u6447\u5934\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u970d\u5e2e\u4e3b\u9669\u906d\u6697\u7b97\uff0c\u5982\u662f\u5730\u9053\u65e0\u4eba\uff0c\u600e\u4f1a\u5982\u6b64\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u7565\u4e00\u6c89\u541f\uff0c\u7a81\u7136\u4e3e\u6b65\u5411\u5165\u53e3\u8d70\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\u65b9\u96ea\u5b9c\u62c9\u7740\u5b89\u5c0f\u840d\uff0c\u4e00\u4e2a\u7bad\u6b65\u7a9c\u4e0a\u524d\u53bb\uff0c\u548c\u90a3\u5165\u4e91\u5927\u5e08\u8d70\u4e86\u4e00\u4e2a\u5e76\u6392\u3002\u4e09\u4eba\u884c\u81f3\u90a3\u5730\u9053\u5165\u53e3\uff0c\u5165\u4e91\u5927\u5e08\u6b63\u5f85\u63a2\u9996\u5f20\u671b\uff0c\u9661\u7136\u4e00\u7247\u767d\u70df\uff0c\u81ea\u90a3\u6d1e\u53e3\u5411\u4e0a\u5192\u8d77\u3002 \u3000\u3000\u970d\u9e23\u98ce\u9002\u624d\u662f\u5ffd\u9047\u6b64\u53d8\uff0c\u7acb\u5373\u95ea\u8eab\u540e\u8fb9\uff0c\u5165\u4e91\u5927\u5e08\u5374\u4e0d\u597d\u8ddf\u4ed6\u4e00\u6837\u4e86\uff0c\u5c31\u5728\u90a3\u767d\u96fe\u817e\u8d77\u4e4b\u9645\uff0c\u4ed6\u5f88\u5feb\u5730\u6325\u51fa\u4e00\u62f3\uff0c\u5c06\u90a3\u767d\u70df\u9707\u5f97\u5411\u56db\u4e0b\u6563\u5f00\u3002 \u3000\u3000\u5b8b\u5b5a\u5728\u8fdc\u5904\u5931\u9053\uff1a\u201c\u5f53\u5fc3\u767d\u96fe\u6709\u6bd2\uff0c\u5feb\u5feb\u5c4f\u4f4f\u547c\u5438\uff01\u201d \u3000\u3000\u5176\u5b9e\uff0c\u4e09\u4eba\u4e0d\u8981\u4ed6\u62db\u547c\uff0c\u5c31\u65e9\u5df2\u5c4f\u4f4f\u4e86\u547c\u5438\uff0c\u5165\u4e91\u5927\u5e08\u4e00\u638c\u62cd\u6563\u4e86\u90a3\u80a1\u767d\u96fe\uff0c\u65b9\u96ea\u5b9c\u66f4\u662f\u53cc\u638c\u8fde\u6325\uff0c\u5bf9\u90a3\u767d\u96fe\u9001\u5411\u4e86\u795e\u9f9b\u4e4b\u540e\u3002 \u3000\u3000\u5165\u4e91\u5927\u5e08\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u559d\u9053\uff1a\u201c\u65bd\u4e3b\u662f\u4ec0\u4e48\u4eba\uff1f\u8fdb\u4e86\u672c\u5bfa\u79d8\u9053\uff0c\u4e3a\u4f55\u4e0d\u6562\u89c1\u4eba\uff1f\u4f60\u5982\u518d\u4e0d\u51fa\u6765\uff0c\u53ef\u83ab\u602a\u672c\u5ea7\u4e0b\u90a3\u6bd2\u624b\uff0c\u903c\u4f60\u73b0\u8eab\u4e86\u201d\u8bdd\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u6389\u5934\u5411\u5165\u5723\u5927\u5e08\u63a5\u9053\uff1a \u3000\u3000\u201c\u5927\u5e08\u5144\uff0c\u8fd9\u5730\u9053\u5185\u7684\u65bd\u4e3b\u518d\u4e0d\u51fa\u6765\uff0c\u54b1\u4eec\u8bf4\u4e0d\u5f97\u53ea\u597d\u5c06\u90a3\u83b2\u6c60\u6c34\u95f8\u6253\u5f00\u5012\u704c\u8fdb\u53bb\u4e86\u3002\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u5408\u5341\u9053\uff1a\u201c\u5c0f\u5144\u9075\u547d\uff01\u201d \u3000\u3000\u8fd9\u65f6\u90a3\u970d\u9e23\u98ce\u4e5f\u5927\u58f0\u9053\uff1a\u201c\u8001\u673d\u4e5f\u53bb\u5e2e\u5e2e\u5fd9\u2026\u2026\u201d \u3000\u3000\u4e24\u4eba\u5927\u6b65\u5411\u5916\u8d70\u53bb\u3002 \u3000\u3000\u84e6\u7136\u6253\u90a3\u5730\u9053\u4e4b\u5185\uff0c\u4f20\u6765\u4e86\u4e00\u58f0\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u5165\u4e91\u4f60\u597d\u5927\u7684\u80c6\u5b50\uff0c\u8fd9\u5730\u9053\u4e4b\u4e2d\uff0c\u9664\u4e86\u6709\u7740\u4f60\u95e8\u4e0b\u5f1f\u5b50\u6570\u767e\u540d\uff0c\u8fd8\u6709\u90a3\u4e10\u5e2e\u51e0\u4f4d\u957f\u8001\uff0c\u5982\u679c\u4f60\u6562\u5f00\u95f8\u653e\u6c34\uff0c\u53ea\u6015\u5148\u6b7b\u7684\u5c31\u662f\u4ed6\u4eec\u4e86\u2026\u2026\u201d \u3000\u3000\u8fd9\u4eba\u7684\u58f0\u97f3\u6709\u4e9b\u5435\u54d1\uff0c\u597d\u4f3c\u6545\u610f\u505a\u51fa\u6765\u7684\u5047\u55d3\u5b50\u3002 \u3000\u3000\u5165\u4e91\u5927\u5e08\u95fb\u8a00\uff0c\u51b7\u51b7\u5730\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u53cd\u6b63\u4ed6\u4eec\u5df2\u5728\u4f60\u638c\u63e1\u4e4b\u4e0b\uff0c\u751f\u6b7b\u4e4b\u95f4\uff0c\u5df2\u96be\u6289\u62e9\uff0c\u672c\u5ea7\u4e14\u7b49\u5730\u9053\u6c34\u6ee1\u4e4b\u540e\uff0c\u518d\u4e3a\u4ed6\u4eec\u591a\u5ff5\u4e0a\u5343\u4e07\u904d\u5f80\u751f\u5492\u4fbf\u662f\uff01\u201d \u3000\u3000\u663e\u7136\u5165\u4e91\u5927\u5e08\u7684\u8fd9\u7b49\u56de\u7b54\uff0c\u751a\u4e3a\u51fa\u4e4e\u90a3\u5730\u9053\u4e2d\u4eba\u7684\u610f\u6599\uff0c\u53ea\u542c\u90a3\u4eba\u9053\uff1a\u201c\u4e14\u9992\u2026\u2026\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u65bd\u4e3b\u4f55\u4e0d\u51fa\u6765\u4e00\u89c1\uff1f\u201d \u3000\u3000\u90a3\u4eba\u63a5\u9053\uff1a\u201c\u6211\u5f53\u7136\u8981\u51fa\u6765\u2026\u2026\u201d \u3000\u3000\u5ffd\u7136\u8eab\u5f71\u4e00\u95ea\uff0c\u5f53\u771f\u4ece\u90a3\u5730\u9053\u4e4b\u4e2d\uff0c\u5192\u51fa\u6765\u4e86\u4e00\u4eba\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e3e\u76ee\u671b\u53bb\uff0c\u4e0d\u89c9\u8138\u4e0a\u795e\u8272\u5927\u53d8\uff01 \u3000\u3000\u539f\u6765\u6b64\u4eba\u975e\u4ed6\uff0c\u4e43\u662f\u81ea\u5df1\u5728\u5609\u5b9a\u5e9c\u4e0e\u4ed6\u6709\u8fc7\u534a\u591c\u957f\u8c08\u7684\u5e84\u6377\u3002 \u3000\u3000\u5b8b\u5b5a\u663e\u7136\u4e5f\u5403\u4e86\u4e00\u60ca\uff0c\u4ed6\u6ca1\u6709\u60f3\u5230\u8fd9\u4eba\u7adf\u4f1a\u662f\u90a3\u5e84\u6377\uff0c\u4e0d\u7981\u8131\u53e3\u9053\uff1a\u201c\u5e84\u526f\u5802\u4e3b\u5417\uff1f\u201d \u3000\u3000\u5e84\u6377\u4e5f\u5728\u770b\u5230\u4e86\u65b9\u96ea\u5b9c\u4e4b\u9645\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u4f46\u4ed6\u5f88\u5feb\u5730\u7b11\u9053\uff1a\u201c\u539f\u6765\u65b9\u5144\u5728\u8fd9\u91cc\uff0c\u771f\u662f\u610f\u60f3\u4e0d\u5230\uff0c\u5e78\u4f1a\u7684\u5f88\u554a\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5251\u7709\u4e00\u626c\uff0c\u9053\uff1a\u201c\u5e84\u526f\u5802\u4e3b\uff0c\u4e09\u4e49\u95e8\u6765\u4e86\u591a\u5c11\u4eba\uff1f\u4f55\u4e0d\u5168\u90fd\u51fa\u6765\u554a\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u751a\u4e3a\u610f\u5916\uff0c\u4ed6\u51b3\u672a\u60f3\u5230\u65b9\u96ea\u5b9c\u4f1a\u8ddf\u8fd9\u4eba\u76f8\u8bc6\uff0c\u4e00\u65f6\u95f4\u5230\u6123\u5728\u5f53\u5730\uff0c\u4e0d\u77e5\u5e94\u8be5\u8bf4\u4ec0\u4e48\u624d\u597d\u3002 \u3000\u3000\u90a3\u5e84\u6377\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4e09\u4e49\u95e8\u53ea\u6709\u5e84\u67d0\u4e00\u4f4d\uff0c\u4f46\u4e0d\u5c5e\u4e09\u4e49\u95e8\u7684\u670b\u53cb\uff0c\u5012\u6765\u4e86\u4e0d\u5c11\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u90a3\u90fd\u662f\u4e9b\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5e84\u6377\u76ee\u5149\u5728\u5b8b\u5b5a\u8eab\u4e0a\u4e00\u8f6c\uff0c\u76b1\u4e86\u76b1\u7709\u9053\uff1a\u201c\u539f\u6765\u5b8b\u8001\u4e5f\u5230\u5d69\u5c71\u6765\u4e86\uff0c\u96be\u602a\u5609\u5b9a\u5206\u5802\u7684\u5b50\u5f1f\u4eec\uff0c\u51e0\u4e4e\u6709\u4e24\u5929\u90fd\u6ca1\u6709\u83dc\u5403\u5462\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u597d\u554a\uff0c\u8001\u592b\u4e0d\u4e0b\u53a8\uff0c\u96be\u9053\u4f60\u4eec\u5c31\u65e0\u4eba\u505a\u996d\u4e86\u5417\uff1f\u5176\u5b9e\u2014\u2014\u8001\u592b\u90a3\u5929\u4e34\u8d70\u4e4b\u65f6\uff0c\u4e5f\u53ea\u4e0d\u8fc7\u5c06\u7c73\u4ed3\uff0c\u83dc\u5e93\u94a5\u5319\u4e22\u5230\u6bdb\u5751\u91cc\u800c\u5df2\uff0c\u4f60\u4eec\u6ca1\u6709\u996d\u5403\uff0c\u4e0e\u8001\u592b\u53c8\u6709\u4f55\u5e72\uff1f\u201d\u7b11\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5e84\u526f\u5802\uff0c\u6709\u4e00\u4ef6\u4e8b\u8001\u592b\u4e00\u76f4\u5728\u731c\uff0c\u9601\u4e0b\u7684\u771f\u6b63\u8eab\u4efd\uff0c\u5230\u5e95\u662f\u8c01\uff1f\u800c\u4eca\u4e0d\u5728\u5609\u5b9a\uff0c\u9601\u4e0b\u4f3c\u4e4e\u5e94\u8be5\u53ef\u4ee5\u8bf4\u51fa\u6765\u4e86\u5427\uff1f\u201d \u3000\u3000\u5e84\u6377\u95fb\u8a00\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u5c0a\u9a7e\u5f53\u771f\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8001\u592b\u82e5\u662f\u77e5\u9053\uff0c\u4f55\u5fc5\u591a\u6b64\u4e00\u95ee\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u65f6\u4e5f\u5341\u5206\u5173\u5fc3\u5730\u671b\u7740\u5e84\u6377\uff0c\u7b49\u4ed6\u8bf4\u8bdd\u3002 \u3000\u3000\u4f46\u90a3\u5e84\u6377\u5374\u54c8\u54c8\u4e00\u7b11\uff0c\u6447\u5934\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u5728\u5609\u5b9a\u5206\u5802\u4e4b\u65e5\u5e84\u67d0\u5bf9\u4f60\u53ef\u662f\u606d\u656c\u5f97\u5f88\u554a\uff01\u201d \u3000\u3000\u4ed6\u7b54\u975e\u6240\u95ee\uff0c\u4f7f\u5f97\u5b8b\u5b5a\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u8001\u592b\u5e74\u7eaa\u6bd4\u4f60\u5927\u4e86\u8bb8\u591a\uff0c\u4f60\u5bf9\u8001\u592b\u5c0a\u656c\uff0c\u90a3\u4e5f\u662f\u5e94\u8be5\u7684\uff0c\u53ea\u4e0d\u77e5\u9053\u4e0e\u9601\u4e0b\u7684\u8eab\u4efd\uff0c\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u5173\u7cfb\u662f\u6ca1\u6709\uff0c\u5e84\u67d0\u544a\u8bc9\u4f60\u5b8b\u8001\u8fd9\u53e5\u8bdd\uff0c\u53ea\u8981\u4f60\u77e5\u9053\uff0c\u5982\u679c\u4f60\u81ea\u5df1\u60f3\u4e0d\u51fa\u6765\u5e84\u67d0\u662f\u4f55\u51fa\u8eab\uff0c\u5e84\u67d0\u81ea\u5df1\u53ea\u6015\u4e5f\u60f3\u4e0d\u51fa\u6765\u4e86\uff01\u201d \u3000\u3000\u6731\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u5f88\u597d\uff0c\u8001\u592b\u4e0d\u95ee\u4fbf\u4e86\uff01\u201d \u3000\u3000\u4e24\u4eba\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5165\u4e91\u5927\u5e08\u5df2\u5411\u65b9\u96ea\u5b9c\u4f4e\u58f0\u95ee\u660e\u4e86\u5e84\u6377\u548c\u4ed6\u76f8\u8bc6\u7684\u7ecf\u8fc7\uff0c\u8fd9\u65f6\uff0c\u4e0d\u7981\u63d2\u53e3\u9053\uff1a\u201c\u5e84\u65bd\u4e3b\uff0c\u672c\u95e8\u5b50\u5f1f\u548c\u4e10\u5e2e\u7684\u51e0\u4f4d\u957f\u8001\uff0c\u53ef\u8fd8\u5728\u5730\u9053\u4e4b\u4e2d\u5417\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u5f53\u7136\u5728\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u5ff5\u4e86\u4e00\u58f0\u4f5b\u9053\uff1a\u201c\u65bd\u4e3b\u53ef\u662f\u5728\u4ed6\u4eec\u8eab\u4e0a\u505a\u4e86\u4ec0\u4e48\u624b\u811a\uff1f\u201d \u3000\u3000\u5e84\u6377\u5ffd\u7136\u5927\u7b11\u9053\uff1a\u201c\u638c\u95e8\u4eba\u592a\u770b\u5f97\u8d77\u5e84\u67d0\u4e86\uff01\u5e84\u67d0\u5982\u6709\u8fd9\u7b49\u80fd\u8010\uff0c\u90a3\u4e5f\u4e0d\u4f1a\u53d7\u547d\u5728\u6b64\u5b88\u95e8\u4e86\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u76b1\u7709\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u4eba\u505a\u7684\u624b\u811a\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u5e84\u67d0\u8ba4\u4e3a\u4f60\u5e94\u8be5\u65e9\u5df2\u77e5\u6653\u624d\u662f\uff0c\u8d35\u5bfa\u6570\u767e\u540d\u5b50\u5f1f\u4e0d\u8bf4\uff0c\u4ec5\u662f\u90a3\u4e10\u5e2e\u51e0\u4f4d\u957f\u8001\uff0c\u4e5f\u5c31\u4e0d\u662f\u4e00\u822c\u4eba\u6240\u80fd\u5bf9\u4ed8\u5f97\u4e86\u2026\u2026\u201d \u3000\u3000\u970d\u9e23\u98ce\u5ffd\u7136\u5927\u559d\u9053\uff1a\u201c\u4ed6\u4eec\u600e\u4e48\u4e86\uff1f\u201d \u3000\u3000\u5e84\u6377\u4e00\u6014\u9053\uff1a\u201c\u8c01\uff1f\u5c0a\u9a7e\u53c8\u662f\u8c01\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u88ab\u4ed6\u95ee\u5f97\u4e00\u6014\uff0c\u6012\u9053\uff1a\u201c\u8001\u592b\u970d\u9e23\u98ce\uff0c\u4f60\u6ca1\u6709\u542c\u8bf4\u8fc7\u5417\uff1f\u201d \u3000\u3000\u5e84\u6377\u5927\u7b11\u9053\uff1a\u201c\u970d\u5e2e\u4e3b\uff1f\u5931\u656c\u4e86\u3002\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u9601\u4e0b\u7528\u4e0d\u7740\u5ba2\u6c14\uff0c\u66f4\u522b\u60f3\u8bbd\u523a\u8001\u592b\uff0c\u8001\u592b\u53ea\u8981\u4f60\u8bf4\u51fa\u51e0\u4f4d\u5e08\u5f1f\u600e\u4e48\u6837\u4e86\uff01\u201d \u3000\u3000\u5e84\u6377\u5927\u7b11\u9053\uff1a\u201c\u5357\u534e\u795e\u4e5e\u539f\u6765\u4e5f\u8fd9\u4e48\u6c89\u4e0d\u4f4f\u6c14\uff0c\u5012\u662f\u5927\u51fa\u5e84\u67d0\u610f\u6599\u4e4b\u5916\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u8138\u8272\u5fae\u5fae\u4e00\u53d8\uff0c\u4f46\u77ac\u5373\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u970d\u67d0\u4e0d\u8fc7\u662f\u4e10\u5e2e\u7684\u4e00\u4f4d\u5e2e\u4e3b\uff0c\u5e76\u975e\u5723\u4eba\uff0c\u9047\u5230\u8fd9\u7b49\u602a\u4e8b\u81ea\u662f\u5c11\u4e0d\u5f97\u8981\u5403\u60ca\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u65f6\u5ffd\u7136\u63d2\u53e3\u9053\uff1a\u201c\u5e84\u5144\uff0c\u4f60\u8bf4\u6765\u4e86\u4e0d\u5c11\u4eba\uff0c\u4e0d\u77e5\u90fd\u662f\u90a3\u4e9b\u4eba\uff1f\u4ed6\u4eec\u73b0\u5728\u4f55\u5904\uff1f\u201d \u3000\u3000\u5e84\u6377\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u4ed6\u4eec\u5417\uff1f\u6b64\u523b\u5c31\u5728\u7f57\u6c49\u5802\u5185\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5730\u9053\u91cc\u9762\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u4e0d\u5168\u662f\u2026\u2026\u201d\u8bdd\u58f0\u987f\u4e86\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u65b9\u5144\uff0c\u5c11\u6797\u4e00\u6d3e\uff0c\u4e0e\u4f60\u53ef\u6709\u4ec0\u4e48\u6e0a\u6e90\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u6014\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u5e84\u5144\u95ee\u6b64\u4f5c\u751a\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u65b9\u5144\u5982\u662f\u4e0e\u5c11\u6797\u65e0\u6e0a\u6e90\uff0c\u5144\u5f1f\u5012\u6709\u4e00\u8a00\u76f8\u544a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u5144\u5f1f\u751a\u671b\u65b9\u5144\u9000\u51fa\u8fd9\u573a\u662f\u975e\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u53ea\u542c\u5f97\u5fc3\u4e2d\u597d\u7b11\uff0c\u4f46\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u5374\u662f\u4e3a\u4f55\uff1f\u5144\u5f1f\u80fd\u591f\u9047\u4e0a\u8fd9\u7b49\u70ed\u95f9\u4e4b\u4e8b\uff0c\u600e\u53ef\u9000\u51fa\uff1f\u201d \u3000\u3000\u5e84\u6377\u53cc\u7709\u4e00\u626c\uff0c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u8981\u4e0e\u5144\u5f1f\u4f5c\u5bf9\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7a81\u9053\uff1a\u201c\u90a3\u5012\u4e5f\u4e0d\u662f\uff0c\u5018\u662f\u5e84\u5144\u4e0d\u5148\u5b58\u4e0b\u4e0e\u6211\u4eec\u4f5c\u5bf9\u4e4b\u5fc3\uff0c\u54b1\u4eec\u4e0d\u5c31\u4e0d\u7528\u53cd\u76ee\u76f8\u5411\u4e86\u5417\uff1f\u201d \u3000\u3000\u5e84\u6377\u84e6\u5730\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u65b9\u5144\u662f\u8bda\u5fc3\u8981\u4e0e\u5144\u5f1f\u4f5c\u5bf9\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e5f\u626c\u58f0\u5927\u7b11\u9053\uff1a\u201c\u5e84\u5144\u5018\u662f\u80fd\u591f\u542c\u4fe1\u65b9\u67d0\u4e00\u8a00\uff0c\u90a3\u54b1\u4eec\u53ef\u4e0d\u5fc5\u4f5c\u5bf9\u4e86\uff01\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u4ec0\u4e48\u4e00\u8a00\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u518d\u4e0e\u5c11\u6797\u4f5c\u5bf9\uff0c\u4e0d\u518d\u8fc7\u95ee\u6b66\u6797\u4e89\u6743\u6740\u622e\u4e4b\u4e8b\uff01\u201d \u3000\u3000\u5e84\u6377\u957f\u957f\u5730\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u6df1\u6df1\u5730\u770b\u4e86\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u8fd9\u624d\u63a5\u9053\uff1a\u201c\u65b9\u5144\uff0c\u8bdd\u5012\u662f\u4e24\u53e5\u826f\u8a00\uff0c\u53ea\u662f\uff0c\u65b9\u5144\u8bf4\u5f97\u4e0d\u662f\u65f6\u5019\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5e84\u6377\u7a81\u9053\uff1a\u201c\u8fdf\u4e86\uff0c\u5e84\u67d0\u8eab\u4e0d\u7531\u5df1\uff0c\u53ea\u6015\u8981\u8fdd\u65b9\u5144\u4e4b\u547d\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e84\u5144\uff0c\u82e6\u6d77\u65e0\u8fb9\uff0c\u56de\u5934\u662f\u5cb8\uff0c\u4e00\u4e2a\u4eba\u53ea\u8981\u6709\u7740\u6539\u8fc7\u4e4b\u5fc3\uff0c\u6ca1\u6709\u4ec0\u4e48\u8fdf\u65e9\u554a\uff01\u201d \u3000\u3000\u5e84\u6377\u6447\u5934\u957f\u53f9\u9053\uff1a\u201c\u65b9\u5144\uff0c\u76db\u60c5\u5144\u5f1f\u5fc3\u9886\u4e86\uff0c\u5e84\u67d0\u6709\u751f\u4e4b\u5e74\uff0c\u4f1a\u8bb0\u5f97\u65b9\u5144\u8fd9\u756a\u5fc3\u610f\u4fbf\u4e86\u3002\u201d \u3000\u3000\u8bdd\u97f3\u4e00\u987f\uff0c\u5ffd\u7136\u5411\u5c11\u6797\u638c\u95e8\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u8d35\u5bfa\u6570\u767e\u5f1f\u7684\u6027\u547d\uff0c\u638c\u95e8\u4eba\u60f3\u4e0d\u60f3\u4e3a\u4ed6\u4eec\u4fdd\u5168\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u5408\u5341\u63a5\u9053\uff1a\u201c\u8001\u8872\u8eab\u4e3a\u638c\u95e8\uff0c\u81ea\u7136\u662f\u8981\u4fdd\u5168\u4ed6\u4eec\u4e86\uff01\u201d \u3000\u3000\u5e84\u6377\u7b11\u9053\uff1a\u201c\u4f46\u4ed6\u4eec\u73b0\u5728\u751f\u6b7b\u4e4b\u673a\uff0c\u5374\u662f\u63e1\u5728\u5e84\u67d0\u7b49\u4eba\u7684\u624b\u4e0a\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9ad8\u55a7\u4e86\u4e00\u58f0\u4f5b\u53f7\uff0c\u9053\uff1a\u201c\u8001\u8872\u4e0d\u4fe1\uff01\u201d \u3000\u3000\u5e84\u6377\u7b11\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u4f60\u4e0d\u4fe1\u5417\uff1f\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u987f\u4e86\u4e00\u987f\u8bdd\u97f3\uff0c\u6709\u610f\u65e0\u610f\u5730\u5411\u7f57\u6c49\u5802\u4e2d\u7684\u56db\u5468\u7f57\u6c49\u4f5b\u50cf\u770b\u4e86\u4e00\u770b\uff0c\u63a5\u9053\uff1a \u3000\u3000\u201c\u5018\u82e5\u638c\u95e8\u4eba\u771f\u4e2a\u6709\u5fc3\u8981\u4fdd\u5168\u4ed6\u4eec\uff0c\u90a3\u4e5f\u4e0d\u96be\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u8001\u8872\u81ea\u4fe1\u8db3\u4ee5\u5bf9\u4ed8\u4f60\u4eec\u2026\u2026\u201d \u3000\u3000\u5e84\u6377\u5ffd\u7136\u5927\u7b11\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u4f60\u8bef\u4f1a\u4e86\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u8001\u8872\u8bef\u4f1a\u4ec0\u4e48\uff1f\u4f60\u4eec\u5982\u60f3\u4ed7\u6043\u6b66\u529f\uff0c\u8001\u592b\u4e00\u640f\uff0c\u90a3\u4f60\u4eec\u662f\u81ea\u627e\u82e6\u5403\u4e86\uff01\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u638c\u95e8\u4eba\u5f88\u81ea\u4fe1\u554a\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u4f9d\u7136\u9762\u542b\u7b11\u5bb9\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5bf9\u4ed8\u4f60\u7b49\uff0c\u8001\u8872\u5c1a\u6709\u5341\u6210\u628a\u63e1\uff01\u201d \u3000\u3000\u5e84\u6377\u5251\u7709\u500f\u5730\u4e00\u626c\uff0c\u5927\u7b11\u9053\uff1a\u201c\u533a\u533a\u539f\u4ee5\u4e3a\u5c11\u6797\u638c\u95e8\uff0c\u5fc5\u662f\u901a\u6743\u8fbe\u53d8\u4e4b\u4eba\uff0c\u4eca\u65e5\u4e00\u89c1\uff0c\u65b9\u77e5\u4e0d\u7136\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u7b11\u5bb9\u4e00\u655b\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u65bd\u4e3b\uff0c\u8001\u8872\u7684\u8010\u6027\u6709\u9650\uff0c\u4f60\u5982\u4e0d\u518d\u8bf4\u51fa\u672c\u5bfa\u5f1f\u5b50\u773c\u4e0b\u7a76\u7adf\u600e\u4e48\u6837\u4e86\uff0c\u6055\u8001\u8872\u8981\u5bf9\u65bd\u4e3b\u65e0\u793c\u4e86\uff01\u201d \u3000\u3000\u5e84\u6377\u76b1\u4e86\u76b1\u7709\uff0c\u9053\uff1a\u201c\u6267\u8ff7\u4e0d\u609f\uff0c\u533a\u533a\u771f\u4e3a\u638c\u95e8\u4eba\u53ef\u60dc\u5f97\u5f88\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u970d\u9e23\u98ce\u5927\u559d\u4e00\u58f0\uff0c\u63d2\u53e3\u9053\uff1a\u201c\u59d3\u5e84\u7684\uff0c\u8fd9\u513f\u4e0d\u662f\u4f60\u6492\u91ce\u4e4b\u5904\uff0c\u82e5\u975e\u5165\u4e91\u638c\u95e8\u4eba\u8fd9\u597d\u7684\u8010\u6027\uff0c\u6362\u4e86\u8001\u592b\uff0c\u65e9\u5c31\u5c06\u4f60\u62ff\u4e0b\u4e86\uff01\u201d \u3000\u3000\u5e84\u6377\u5475\u5475\u4e00\u7b11\uff0c\u76ee\u5149\u5728\u970d\u9e23\u98ce\u8eab\u4e0a\u4e00\u8f6c\uff0c\u9053\uff1a\u201c\u5e2e\u4e3b\u53ef\u662f\u60f3\u52a8\u624b\u5417\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4f60\u4ee5\u4e3a\u672c\u5ea7\u4e0d\u6562\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u5ffd\u7136\u5927\u6012\uff0c\u4e3e\u624b\u4e00\u638c\uff0c\u6a2a\u62cd\u800c\u53bb\u3002\u4f46\u89c1\u4e00\u80a1\u52b2\u98ce\uff0c\u76f4\u903c\u5e84\u6377\u3002 \u3000\u3000\u5e84\u6377\u8138\u8272\u5fae\u5fae\u4e00\u53d8\uff0c\u95ea\u8eab\u8ba9\u5f00\u970d\u9e23\u98ce\u7684\u4e00\u51fb\uff0c\u5927\u58f0\u9053\uff1a\u201c\u970d\u9e23\u98ce\uff0c\u4f60\u90a3\u51e0\u4f4d\u5e08\u5144\u7684\u6027\u547d\uff0c\u4e5f\u4e0d\u7ba1\u4e86\u5417\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u7b2c\u4e8c\u638c\u521a\u521a\u626c\u8d77\uff0c\u95fb\u8a00\u4f46\u89c9\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6295\u9f20\u5fcc\u5668\u4e4b\u4e0b\u53ea\u597d\u6536\u624b\uff0c\u4f46\u5374\u6012\u9053\uff1a \u3000\u3000\u201c\u4f60\u628a\u4ed6\u4eec\u600e\u6837\u4e86\uff1f\u201d \u3000\u3000\u5e84\u6377\u773c\u89c1\u4ed6\u679c\u7136\u4e0d\u6562\u518d\u653b\uff0c\u987f\u65f6\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u5f88\u597d\uff0c\u53ea\u662f\u6682\u65f6\u4e0d\u80fd\u884c\u52a8\u800c\u5df2\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5251\u7709\u8038\u52a8\uff0c\u5fc3\u4e2d\u751a\u662f\u6c14\u607c\uff0c\u4ed6\u6b63\u60f3\u559d\u4ee4\u5e84\u6377\u5feb\u5feb\u628a\u90a3\u5730\u9053\uff01\u62a4\u7684\u4e10\u5e2e\u957f\u8001\u548c\u5c11\u6797\u5b50\u5f1f\u9001\u51fa\u6765\uff0c\u81ea\u5df1\u5c31\u4e0d\u518d\u4e0e\u4ed6\u8ba1\u8f83\u65f6\uff0c\u53ea\u542c\u5f97\u5b89\u5c0f\u840d\u84e6\u5730\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u65b9\u5144\uff0c\u8fd9\u59d3\u5e84\u7684\u5f88\u65e0\u793c\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u60f3\uff0c\u53ef\u4e0d\u662f\u2026\u2026\u4f46\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u4ed6\u53ea\u662f\u4ed7\u6043\u7740\u6211\u4eec\u6709\u4eba\u88ab\u4ed6\u6240\u6263\u4e4b\u6545\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u770b\u4e86\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u65b9\u5144\uff0c\u5018\u662f\u6211\u4eec\u80fd\u5c06\u4ed6\u64d2\u4f4f\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u6b64\u4eba\u6b66\u529f\u5982\u4f55\uff0c\u5144\u5f1f\u867d\u4e0d\u5168\u77e5\uff0c\u4f46\u5f53\u771f\u60f3\u5c06\u4ed6\u64d2\u4f4f\uff0c\u53ea\u6015\u4e0d\u6613\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u4e0d\u96be\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u867d\u7136\u89c1\u5230\u5979\u7684\u4e0d\u51e1\u6b66\u529f\uff0c\u4f46\u5fc3\u60f3\u5728\u773c\u524d\u8fd9\u7b49\u60c5\u51b5\u4e4b\u4e0b\uff0c\u5982\u60f3\u4e00\u4e3e\u5c06\u5e84\u6377\u64d2\u4f4f\uff0c\u53ea\u6015\u4e0d\u5bb9\u6613\u3002 \u3000\u3000\u662f\u4ee5\uff0c\u4ed6\u76b1\u7709\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u4f60\u4e0d\u53ef\u5192\u5931\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u8ba9\u6211\u8bd5\u8bd5\u597d\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fdf\u7591\u4e86\u4e00\u4e0b\uff0c\u5c1a\u672a\u8bf4\u8bdd\uff0c\u53ea\u542c\u5f97\u5e84\u6377\u4ef0\u5929\u5927\u7b11\u9053\uff1a\u201c\u65b9\u5144\uff0c\u8fd9\u4f4d\u5c0f\u5144\u5f1f\u597d\u72c2\u7684\u53e3\u6c14\uff0c\u4ed6\u662f\u4f60\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5251\u7709\u4e00\u626c\uff0c\u9053\uff1a\u201c\u65b9\u67d0\u62dc\u5f1f\u5b89\u840d\uff01\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u5b89\u5144\u5f1f\uff1f\u4ed6\u4f3c\u4e4e\u6bd4\u4f60\u65b9\u5144\u8981\u72c2\u5984\u5f97\u591a\u4e86\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bed\u97f3\u672a\u5df2\uff0c\u5ffd\u7136\u542c\u5f97\u5b89\u5c0f\u840d\u6c89\u58f0\u9053\uff1a\u201c\u59d3\u5e84\u7684\uff0c\u4f60\u53ef\u8981\u8bd5\u8bd5\uff1f\u201d \u3000\u3000\u5e84\u6377\u5bf9\u65b9\u96ea\u5b9c\u786e\u662f\u6709\u7740\u51e0\u5206\u5fcc\u60ee\uff0c\u4f46\u5bf9\u5b89\u5c0f\u840d\u53ef\u5e76\u672a\u653e\u5728\u773c\u4e2d\uff0c\u95fb\u8a00\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5e84\u67d0\u771f\u662f\u4e0d\u4fe1\uff0c\u4f60\u5b89\u8001\u5f1f\u5982\u8981\u901e\u5f3a\uff0c\u5e84\u67d0\u5949\u966a\u4fbf\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u67f3\u7709\u4e00\u7ad6\uff0c\u7f13\u6b65\u4e0a\u524d\uff0c\u9053\uff1a\u201c\u4f60\u653e\u5fc3\u4e86\uff01\u201d \u3000\u3000\u5e84\u6377\u5927\u523a\u523a\u5730\u7b11\u9053\uff1a\u201c\u53ea\u6015\u8981\u5c0f\u5fc3\u7684\u4e0d\u662f\u6211\u2026\u2026\u201d \u3000\u3000\u4f59\u97f3\u672a\u5df2\uff0c\u5ffd\u89c9\u773c\u524d\u767d\u5149\u4e00\u95ea\uff0c\u4f46\u611f\u5168\u8eab\u4e00\u9707\uff0c\u987f\u65f6\u7acb\u8db3\u4e0d\u7262\uff0c\u8f70\u7136\u4e00\u58f0\uff0c\u88ab\u4eba\u6454\u4e86\u4e00\u4e2a\u7b4b\u6597\u3002 \u3000\u3000\u5e84\u6377\u521a\u81ea\u6454\u5012\uff0c\u4f46\u7acb\u5373\u633a\u8eab\u8dc3\u8d77\uff0c\u77aa\u5927\u7740\u53cc\u773c\uff0c\u671b\u7740\u5b89\u5c0f\u840d\u76f4\u53d1\u5446\uff01\u6562\u60c5\uff0c\u5b89\u5c0f\u840d\u4e00\u6296\u624b\u4e4b\u95f4\uff0c\u5c31\u5c06\u4ed6\u6454\u4e86\u4e00\u8de4\uff0c\u5b9e\u5728\u662f\u53eb\u4ed6\u4e0d\u6562\u76f8\u4fe1\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u600e\u4e48\u6837\uff1f\u8981\u4e0d\u8981\u518d\u6454\u4e00\u6b21\u8bd5\u8bd5\uff1f\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u8fde\u5c11\u6797\u638c\u95e8\u3001\u4e10\u5e2e\u5e2e\u4e3b\u4e5f\u5168\u90e8\u4e3a\u6b64\u52a8\u5bb9\u3002 \u3000\u3000\u539f\u6765\uff0c\u5b89\u5c0f\u840d\u8fd9\u4e00\u51fa\u624b\uff0c\u4f7f\u5f97\u4ed6\u4eec\u4e1a\u5df2\u77a7\u51fa\u4e86\u5979\u7684\u6b66\u529f\u6765\u5386\u3002 \u3000\u3000\u970d\u9e23\u98ce\u5927\u559d\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u5929\u9e64\u95e8\u6b66\u529f\u554a\u2026\u2026\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u4e5f\u5408\u5341\u957f\u53f9\u9053\uff1a\u201c\u5357\u6d77\u5947\u529f\uff0c\u60f3\u4e0d\u5230\u4eca\u65e5\u518d\u73b0\u4e2d\u539f\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5411\u8fd9\u4e24\u4f4d\u4e00\u6d3e\u5b97\u4e3b\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8fd9\u4e24\u4f4d\u8001\u4eba\u5bb6\u8fc7\u5956\u4e86\uff01\u665a\u8f88\u662f\u4e0d\u8fc7\u7a0d\u61c2\u76ae\u6bdb\u800c\u5df2\uff01\u201d\u5979\u6389\u5934\u5411\u90a3\u53d1\u5446\u7684\u5e84\u6377\u559d\u9053\uff1a\u201c\u59d3\u5e84\u7684\uff0c\u4f60\u8981\u4e0d\u8981\u518d\u5c1d\u5c1d\u6454\u8de4\u7684\u5473\u9053\uff1f\u201d \u3000\u3000\u5e84\u6377\u867d\u7136\u5f88\u81ea\u5927\uff0c\u4f46\u5357\u6d77\u5929\u9e64\u95e8\u6b66\u529f\uff0c\u4ed6\u5012\u4e5f\u662f\u8033\u95fb\u5df2\u4e45\uff0c\u4f55\u51b5\uff0c\u81ea\u5df1\u9002\u624d\u53c8\u5c1d\u8fc7\u4e86\u82e6\u5934\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u8bdd\u97f3\u4e00\u843d\uff0c\u4ed6\u8138\u8272\u8fde\u53d8\uff0c\u7ec8\u4e8e\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5e84\u67d0\u5fc3\u4e2d\u679c\u7136\u4e0d\u670d\uff0c\u4f46\u662f\u2026\u2026\u201d \u3000\u3000\u4ed6\u987f\u4e86\u4e00\u987f\u8bdd\u97f3\uff0c\u63a5\u9053\uff1a\u201c\u5e84\u67d0\u4e0e\u5357\u6d77\u5929\u9e64\u95e8\u65e0\u6028\u65e0\u4ec7\uff0c\u867d\u7136\u5403\u4e86\u4e00\u70b9\u5c0f\u4e8f\uff0c\u90a3\u4e5f\u7b97\u4e0d\u5f97\u4ec0\u4e48\u4e86\uff01\u201d \u3000\u3000\u4ed6\u8fd9\u7b49\u8bf4\u6cd5\uff0c\u5230\u628a\u5b89\u5c0f\u840d\u542c\u5f97\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u600e\u4e48\u4e86\uff1f\u4f60\u4e0d\u6253\u7b97\u518d\u6218\uff1f\u201d \u3000\u3000\u5e84\u6377\u76ee\u5149\u5728\u51e0\u4eba\u8eab\u4e0a\u4e00\u8f6c\uff0c\u6700\u540e\u5374\u843d\u5728\u65b9\u96ea\u5b9c\u8eab\u4e0a\uff0c\u7b11\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4eca\u65e5\u4e4b\u4e8b\uff0c\u51b2\u7740\u4f60\u65b9\u5144\u548c\u5b89\u8001\u5f1f\uff0c\u5e84\u67d0\u51b3\u5fc3\u4e0d\u518d\u8fc7\u95ee\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9887\u4e3a\u610f\u5916\u5730\u4e00\u6014\uff0c\u201c\u5e84\u5144\u53ef\u662f\u8981\u9000\u51fa\u8fd9\u573a\u662f\u975e\u4e86\u5417\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u8bed\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u5411\u65b9\u96ea\u5b9c\u3001\u5b89\u5c0f\u840d\u4e00\u62b1\u62f3\uff0c\u63a5\u9053\uff1a\u201c\u5c11\u6797\u5b50\u5f1f\uff0c\u4e10\u5e2e\u957f\u8001\uff0c\u5747\u5728\u5730\u9053\u4e4b\u4e2d\uff0c\u4ed6\u4eec\u53ea\u662f\u4e2d\u4e86\u4e00\u4e9b\u8ff7\u795e\u7684\u836f\u7269\uff0c\u4e09\u4e2a\u65f6\u8fb0\u4e4b\u540e\uff0c\u4fbf\u53ef\u81ea\u52a8\u9192\u8f6c\uff0c\u65b9\u5144\u53ef\u8bf7\u5c11\u6797\u638c\u95e8\u4eba\u5165\u5185\u6599\u7406\uff0c\u5e84\u67d0\u544a\u8f9e\u4e86\uff01\u201d\u4e00\u4fa7\u8eab\u8eaf\uff0c\u4fbf\u5411\u7f57\u6c49\u5802\u5916\u884c\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u6014\u4e4b\u95f4\uff0c\u8033\u4e2d\u5ffd\u542c\u5b8b\u5b5a\u5927\u559d\u9053\uff1a\u201c\u7ad9\u4f4f\uff01\u201d \u3000\u3000\u5e84\u6377\u8fd9\u65f6\u6b63\u597d\u8d70\u5230\u970d\u9e23\u98ce\u8033\u8fb9\uff0c\u95fb\u80b2\u53ea\u662f\u987f\u5f97\u4e00\u987f\uff0c\u8eab\u5f62\uff0c\u4fbf\u5ffd\u7136\u53cc\u80a9\u4e00\u8038\uff0c\u98de\u8eab\u5411\u5916\uff0c\u75be\u63a0\u800c\u53bb\u3002 \u3000\u3000\u5b8b\u5b5a\u8138\u8272\u4e00\u53d8\uff0c\u559d\u9053\uff1a\u201c\u6321\u4f4f\u4ed6\u2026\u2026\u201d\u4e3e\u6b65\u4fbf\u8ffd\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u970d\u9e23\u98ce\u79bb\u90a3\u5e84\u6377\u8fd1\u7684\u751a\u591a\uff0c\u5c31\u5728\u5e84\u6377\u8fc8\u6b65\u4e4b\u9645\uff0c\u4e3e\u624b\u4e00\u638c\uff0c\u62cd\u5411\u4e86\u5e84\u6377\u8eab\u524d\u3002 \u3000\u3000\u5e84\u6377\u672c\u662f\u5411\u5916\u98de\u5954\u8eab\u5f62\uff0c\u88ab\u970d\u9e23\u98ce\u8fd9\u4e00\u638c\u903c\u5f97\u4e0d\u7531\u5f97\u4e00\u659c\uff0c\u5c31\u8fd9\u4e00\u6b65\u4e4b\u5dee\uff0c\u65b9\u96ea\u5b9c\u5df2\u98de\u8eab\u5835\u5411\u95e8\u53e3\u4e86\u3002 \u3000\u3000\u5e84\u6377\u76ee\u5149\u4e00\u8f6c\uff0c\u5ffd\u7136\u6325\u62f3\u76f4\u5411\u65b9\u96ea\u5b9c\u5f53\u80f8\u6363\u53bb\uff0c\u53e3\u4e2d\u5927\u559d\u9053\uff1a\u201c\u65b9\u5144\uff0c\u6055\u5144\u5f1f\u65e0\u793c\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u51b7\u7b11\u4e00\u58f0\uff0c\u4eae\u638c\u76f4\u6263\u5e84\u6377\u6765\u62f3\uff0c\u5de6\u624b\u4e00\u62db\uff0c\u4e94\u6307\u5982\u94a9\uff0c\u731b\u5982\u5e84\u6377\u80a9\u4e95\u3002 \u3000\u3000\u5e84\u6377\u4e00\u626c\u53f3\u80a9\uff0c\u62f3\u52bf\u6025\u653b\uff0c\u5de6\u638c\u84e6\u5730\u524a\u5411\u65b9\u96ea\u5b9c\u8155\u8109\uff0c\u53f3\u624b\u5374\u53d8\u62f3\u4e3a\u638c\uff0c\u6a2a\u62cd\u65b9\u96ea\u5b9c\u9501\u9aa8\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5927\u559d\u4e00\u58f0\u9053\uff1a\u201c\u6765\u5f97\u597d\uff01\u201d \u3000\u3000\u53cc\u81c2\u4e00\u5206\uff0c\u5ffd\u7136\u95f4\u53d8\u62db\u653b\u51fa\u4e86\u4e09\u638c\u4e94\u6307\u3002 \u3000\u3000\u4e00\u65f6\u95f4\uff0c\u4e24\u4eba\u5404\u81ea\u62a2\u653b\uff0c\u6253\u5728\u4e00\u8d77\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u83b2\u6b65\u8f7b\u79fb\uff0c\u8d70\u5230\u5b8b\u5b5a\u8eab\u8fb9\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u8bb8\u90a3\u5e84\u6377\u79bb\u53bb\u554a\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u6068\u6068\u5730\u63a5\u9053\uff1a\u201c\u4ed6\u5728\u5f04\u9b3c\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u63a5\u9053\uff1a\u201c\u4ed6\u6562\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u8001\u6c49\u76f8\u4fe1\u6ca1\u6709\u770b\u8d70\u773c\u2026\u2026\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u5ffd\u7136\u5411\u970d\u9e23\u98ce\u559d\u9053\uff1a\u201c\u970d\u5144\uff0c\u4f60\u5feb\u548c\u5165\u4e91\u5927\u5e08\u5c06\u8fd9\u7f57\u6c49\u7237\u4f5b\u50cf\u597d\u597d\u5730\u68c0\u67e5\u4e00\u904d\u2026\u2026\u201d \u3000\u3000\u970d\u9e23\u98ce\u548c\u5165\u4e91\u5927\u5e08\u95fb\u8a00\uff0c\u4e0d\u7981\u4e92\u671b\u4e86\u4e00\u773c\uff0c\u4f3c\u662f\u5bf9\u5b8b\u5b5a\u8fd9\u756a\u8bdd\uff0c\u5927\u611f\u5947\u602a\uff0c\u60f3\u4e0d\u51fa\u4ed6\u4e3a\u4f55\u8981\u4ed6\u4eec\u68c0\u67e5\u8fd9\u7f57\u6c49\u5802\u4e2d\u7684\u4e00\u5341\u516b\u5c0a\u4f5b\u50cf\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u4e5f\u88ab\u5b8b\u5b5a\u8fd9\u6d3b\u5f04\u5f97\u4e00\u5446\uff0c\u8131\u53e3\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u8fd9\u4e9b\u4f5b\u50cf\u83ab\u975e\u6709\u4ec0\u4e48\u4e0d\u5bf9\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u987f\u8db3\u9053\uff1a\u201c\u5f53\u7136\u6709\u2026\u2026\u201d\u4ed6\u540e\u97f3\u672a\u5df1\uff0c\u5ffd\u7136\u63a0\u8eab\u800c\u8d77\uff0c\u8fce\u5411\u53f3\u4fa7\u4e00\u5c0a\u4f5b\u50cf\u5954\u4e86\u8fc7\u53bb\u3002\u53ea\u89c1\u4ed6\u53f3\u624b\u4e00\u62d0\uff0c\u7adf\u5411\u90a3\u4f5b\u50cf\u62cd\u51fa\u4e00\u638c\u3002\u5165\u4e91\u5927\u5e08\u4e00\u89c1\uff0c\u8138\u8272\u5927\u53d8\uff0c\u559d\u9053\uff1a\u201c\u5b8b\u65bd\u4e3b\uff0c\u4f7f\u4e0d\u5f97\u2026\u2026\u8fd9\u7f57\u6c49\u68e0\u4e2d\u7684\u7f57\u6c49\u4f5b\u50cf\uff0c\u90fd\u662f\u94c1\u94f8\u7684\u6b66\u6797\u9ad8\u7684\u2026\u2026\u201d \u3000\u3000\u6562\u60c5\uff0c\u8fd9\u5c11\u6797\u7f57\u6c49\u5802\u4e43\u662f\u8be5\u5bfa\u8bba\u6b66\u4e4b\u5730\uff0c\u90a3\u4e00\u5341\u516b\u5c0a\u7f57\u6c49\u4f5b\u50cf\uff0c\u4e0d\u4f46\u5747\u662f\u7eaf\u94dc\u6253\u5c31\uff0c\u800c\u4e14\u8fd8\u7531\u673a\u5173\u64cd\u7eb5\uff0c\u53ef\u4ee5\u884c\u52a8\u81ea\u5982\uff0c\u6bcf\u4e00\u7f57\u6c49\u4ff1\u53ef\u65bd\u5c55\u4e00\u5957\u62f3\u638c\u6b66\u529f\uff0c\u5982\u662f\u5341\u516b\u5c0a\u4f5b\u50cf\u540c\u65f6\u53d1\u52a8\uff0c\u4fbf\u662f\u4e00\u5ea7\u94dc\u5899\u94c1\u58c1\u7684\u7f57\u6c49\u5927\u9635\u3002\u5c11\u6797\u5b50\u5f1f\u4e0b\u5c71\u884c\u9053\u4e4b\u524d\uff0c\u5fc5\u5f97\u5728\u7f57\u6c49\u5802\u4e2d\u8bd5\u529f\uff0c\u81f3\u5c11\u4e5f\u5f97\u901a\u8fc7\u4e5d\u5c0a\u4f5b\u50cf\u4e5d\u79cd\u62f3\u638c\u731b\u653b\uff0c\u65b9\u80fd\u72ec\u81ea\u4e0b\u5c71\u884c\u3002\u5426\u5219\u5c31\u4ecd\u5f97\u7559\u5728\u5bfa\u4e2d\u82e6\u7ec3\u6b66\u529f\u3002 \u3000\u3000\u662f\u4ee5\uff0c\u5b8b\u5b5a\u5ffd\u7136\u4e3e\u638c\u5411\u90a3\u5c0a\u7f57\u6c49\u51fb\u53bb\uff0c\u5165\u4e91\u5927\u5e08\u81ea\u662f\u60ca\u5f97\u5931\u58f0\u963b\u6b62\u4e86\uff01 \u3000\u3000\u4f46\u4ed6\u559d\u53eb\u4e4b\u58f0\u672a\u5df2\uff0c\u5374\u89c1\u90a3\u5c0a\u94c1\u94f8\u7f57\u6c49\u7adf\u7136\u4e00\u8dc3\u800c\u8d77\uff0c\u5e73\u98de\u4e00\u4e08\uff0c\u8ba9\u5f00\u4e86\u5b8b\u5b5a\u7684\u4e00\u51fb\u4e4b\u52bf\uff01 \u3000\u3000\u5165\u4e91\u5927\u5e08\u84e6\u5730\u8138\u8272\u4e00\u53d8\uff0c\u5927\u543c\u4e00\u58f0\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u7adf\u7136\u6bc1\u53bb\u4e86\u672c\u5bfa\u7f57\u6c49\u4f5b\u50cf\u2026\u2026\u201d \u3000\u3000\u4e00\u53e3\u9c9c\u8840\uff0c\u72c2\u55b7\u800c\u51fa\u3002 \u3000\u3000\u663e\u7136\u8fd9\u4f4d\u638c\u95e8\u5927\u5e08\u5df2\u662f\u6c14\u6025\u653b\u5fc3\u4e86\uff01 \u3000\u3000\u970d\u9e23\u98ce\u4e5f\u77a7\u5f97\u5927\u4e3a\u60ca\u51db\uff0c\u4f46\u4ed6\u5ffd\u89c1\u5165\u4e91\u5927\u5e08\u8fd9\u7b49\u5f62\u72b6\uff0c\u987e\u4e0d\u5f97\u518d\u77a7\u90a3\u7f57\u6c49\u4f5b\u50cf\u5f62\u72b6\uff0c\u8fde\u5fd9\u4f38\u624b\u6309\u5728\u5165\u4e91\u5927\u5e08\u540e\u5fc3\u547d\u95e8\u7a74\u4e0a\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u5feb\u5feb\u8fd0\u6c14\u8c03\u606f\u2026\u2026\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u5b89\u5c0f\u840d\u53ef\u5c31\u5446\u4e86\uff01 \u3000\u3000\u5979\u51e0\u66fe\u89c1\u8fc7\u6ce5\u96d5\u6728\u5851\u7684\u83e9\u8428\u4e5f\u80fd\u98de\u8eab\u800c\u8d77\u7684\u4e8b\uff1f\u662f\u4ee5\u5c16\u53eb\u4e00\u58f0\uff0c\u9000\u4e86\u4e94\u6b65\uff01\u5979\u8fd9\u4e00\u9000\uff0c\u5374\u6b63\u597d\u9000\u5230\u5de6\u8fb9\u7684\u7b2c\u56db\u5c0a\u4f5b\u50cf\u8eab\u524d\u4e08\u8bb8\u4e0d\u5230\u4e4b\u5904\uff0c\u5ffd\u7136\u90a3\u5c0a\u4f5b\u50cf\u4e24\u773c\u4e00\u52a8\uff0c\u53f3\u624b\u7adf\u662f\u7f13\u7f13\u4e3e\u8d77\u3002 \u3000\u3000\u8fd9\u4e00\u638c\u5982\u662f\u5288\u4e86\u4e0b\u53bb\uff0c\u9976\u662f\u90a3\u5b89\u5c0f\u840d\u6b66\u529f\u518d\u597d\uff0c\u53ea\u6015\u4e5f\u8981\u843d\u5f97\u4e2a\u6a2a\u5c38\u5f53\u5730\u4e86\uff01 \u3000\u3000\u773c\u770b\u90a3\u5c0a\u4f5b\u50cf\u7684\u53f3\u624b\u5c31\u5c06\u4e0b\u843d\u4e4b\u9645\uff0c\u5b89\u5c0f\u840d\u5ffd\u7136\u98de\u8eab\u8dc3\u8d77\uff0c\u8fc5\u5411\u524d\u9762\u6251\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u6562\u60c5\uff0c\u5c31\u5728\u8fd9\u65f6\uff0c\u90a3\u5c0a\u8dc3\u4e0b\u4f5b\u5ea7\uff0c\u548c\u5b8b\u5b5a\u52a8\u624b\u7684\u7f57\u6c49\u4f5b\u50cf\uff0c\u5df2\u88ab\u5b8b\u5b5a\u4e00\u638c\u9707\u5012\u5728\u5730\uff0c\u5b89\u5c0f\u840d\u597d\u5947\u5fc3\u8d77\uff0c\u4fbf\u76f4\u5954\u8fc7\u6765\uff0c\u60f3\u8981\u77a7\u77a7\u5c0a\u4f5b\u50cf\u5230\u5e95\u662f\u4eba\u8fd8\u662f\u9b3c\uff0c\u5979\u81ea\u7136\u672a\u66fe\u6599\u5230\uff0c\u53ea\u662f\u4e00\u5ff5\u597d\u5947\uff0c\u5374\u89e3\u4e86\u5979\u4e00\u573a\u5927\u96be\uff01 \u3000\u3000\u5b89\u5c0f\u840d\u5954\u5230\u90a3\u5012\u5730\u7684\u4f5b\u50cf\u4e4b\u524d\uff0c\u51dd\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u90a3\u4f5b\u50cf\u8138\u4e0a\u7684\u91d1\u6f06\u5df2\u7136\u8131\u843d\u4e86\u4e24\u4e09\u5757\u4e4b\u591a\uff0c\u9732\u51fa\u4e86\u4e00\u4e9b\u53d1\u9ed1\u7684\u5305\u76ae\uff0c\u4e0d\u7981\u4e00\u6014\u9053\uff1a\u201c\u4ed6\u4e0d\u662f\u83e9\u8428\u561b\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u5f53\u7136\u4e0d\u662f\uff01\u8001\u5f1f\uff0c\u4ed6\u662f\u4e2a\u4e0d\u6298\u4e0d\u6263\u7684\u6d3b\u4eba\uff0c\u4e0d\u8fc7\uff0c\u6b64\u523b\u53ea\u6015\u5df2\u7ecf\u6b7b\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5446\u4e86\u4e00\u5446\uff1b\u9053\uff1a\u201c\u4ed6\u2026\u2026\u4e3a\u4ec0\u4e48\u88c5\u795e\u88c5\u4f5b\u5462\uff1f\u5b8b\u8001\uff0c\u5269\u4e0b\u7684\u8fd9\u4e9b\u5462\u2026\u2026\u201d\u5979\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u4e0d\u7981\u8f6c\u8eab\u5411\u90a3\u56db\u5468\u7684\u4f5b\u50cf\u671b\u53bb\uff01 \u3000\u3000\u8fd9\u4e00\u671b\u4e0d\u8981\u7d27\uff0c\u5b89\u5c0f\u840d\u5ffd\u7136\u5931\u58f0\u7b11\u4e86\uff01 \u3000\u3000\u539f\u6765\u5979\u7a81\u7136\u89c1\u5230\u5176\u4e2d\u7684\u4e00\u5c0a\u4f5b\u50cf\u7684\u773c\u73e0\u52a8\u4e86\u4e00\u52a8\uff01 \u3000\u3000\u5b89\u5c0f\u840d\u6389\u5934\u770b\u4e86\u770b\u65b9\u96ea\u5b9c\u548c\u5e84\u6377\u4e4b\u6218\uff0c\u53ea\u89c1\u5e84\u6377\u5df2\u7136\u843d\u5728\u4e0b\u98ce\uff0c\u5f53\u4e0b\u5411\u5b8b\u5b5a\u62db\u4e86\u62db\u624b\uff0c\u4e00\u8bed\u6731\u53d1\uff0c\u4e3e\u6b65\u5411\u90a3\u5c0a\u8f6c\u52a8\u773c\u775b\u7684\u4f5b\u50cf\uff0c\u7f13\u7f13\u884c\u53bb\u3002 \u3000\u3000\u5b8b\u5b5a\u89c1\u5979\u79fb\u6b65\u5411\u524d\uff0c\u4fbf\u77e5\u5b89\u5c0f\u840d\u5fc5\u6709\u6240\u89c1\uff0c\u5f53\u4e0b\u6697\u4e2d\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u529f\u51dd\u53cc\u81c2\uff0c\u8ddf\u5728\u5b89\u5c0f\u840d\u8eab\u540e\uff0c\u5411\u7740\u90a3\u4e00\u4f5b\u50cf\u8d70\u53bb\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u8d70\u5230\u79bb\u90a3\u4f5b\u50cf\u8fd8\u6709\u4e08\u8bb8\uff0c\u4fbf\u505c\u4e0b\u8eab\u6765\uff0c\u8f6c\u5934\u5411\u8eab\u540e\u7684\u5b8b\u5b5a\u4e00\u7b11\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u8fd9\u5c0a\u4f5b\u50cf\u5f88\u597d\u73a9\uff0c\u51f6\u9738\u9738\u7684\uff0c\u54ea\u91cc\u662f\u4e2a\u7f57\u6c49\u2026\u2026\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5ffd\u7136\u53f3\u624b\u4e00\u626c\uff0c\u53ea\u89c1\u4e00\u7ee7\u767d\u5149\u4e4d\u8d77\uff0c\u63a5\u7740\u662f\u8f70\u7684\u4e00\u58f0\u5927\u9707\uff0c\u90a3\u5c0a\u5fcd\u4e0d\u4f4f\u7728\u52a8\u773c\u775b\u7684\u7f57\u6c49\u50cf\uff0c\u7adf\u7136\u4e00\u4e0b\u6253\u90a3\u4f5b\u5ea7\u4e0a\u6389\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4e00\u9635\u9635\u547b\u541f\uff0c\u6253\u4ed6\u53e3\u4e2d\u5410\u4e86\u51fa\u6765\uff0c\u60f3\u662f\u8fd9\u4e00\u8de4\u6454\u5f97\u4e0d\u8f7b\uff0c\u5934\u9aa8\u3001\u52b2\u9aa8\u5168\u90fd\u65ad\u4e86\uff01 \u3000\u3000\u5b8b\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u597d\u624b\u6cd5\uff0c\u5929\u9e64\u4e03\u65a9\u6b66\u529f\uff0c\u679c\u7136\u9ad8\u660e\u5f97\u5f88\u554a\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u8fd9\u4f5b\u50cf\u4e5f\u662f\u4e2a\u4eba\u5462\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u70b9\u5934\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u7167\u8fd9\u4e48\u770b\u6765\uff0c\u53ea\u6015\u8fd9\u7f57\u6c49\u5802\u4e2d\u7684\u4f5b\u50cf\uff0c\u4e2a\u4e2a\u90fd\u4e0d\u662f\u7f57\u6c49\u4e86\u2026\u2026\u201d \u3000\u3000\u4ed6\u4f59\u97f3\u672a\u843d\uff0c\u7a81\u7136\u542c\u5f97\u4e00\u58f0\u51b7\u7b11\uff0c\u6253\u90a3\u4f5b\u9f9b\u4e4b\u4e2d\u4f20\u51fa\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u5b8b\u8001\u90aa\u8bf4\u5bf9\u4e86\uff01\u8fd9\u513f\u6ca1\u6709\u4e00\u5c0a\u7f57\u6c49\uff0c\u4e2a\u4e2a\u90fd\u662f\u4f60\u8001\u90aa\u7684\u6b7b\u5bf9\u5934\u2026\u2026\u201d\u53ea\u89c1\u5e03\u5e18\u4e00\u95ea\uff0c\u6253\u90a3\u4f5b\u9f9b\u4e4b\u4e2d\uff0c\u8df3\u843d\u4e0b\u4e00\u4e2a\u4e94\u77ed\u8eab\u6750\uff0c\u5927\u5934\u73af\u76ee\u7684\u602a\u4eba\u6765\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u5403\u4e86\u4e00\u60ca\uff0c\u6389\u5934\u671b\u53bb\uff0c\u4e0d\u7981\u6697\u6697\u79f0\u5947\uff0c\u53ea\u56e0\u8fd9\u4eba\u4e0d\u4f46\u957f\u7684\u8111\u888b\u751a\u5927\uff0c\u800c\u4e14\u90a3\u4e00\u5f20\u5634\u4e5f\u5927\u5f97\u51fa\u5947\uff0c\u51e0\u4e4e\u5360\u4e86\u4ed6\u7684\u8138\u5b54\u7684\u4e00\u534a\u3002 \u3000\u3000\u5c31\u5728\u5979\u60ca\u8bb6\u4e4b\u95f4\uff0c\u8033\u4e2d\u542c\u5f97\u5b8b\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u679c\u7136\u662f\u4f60\u7ae5\u5144\u5728\u6b64\u5f04\u9b3c\uff01\u5b8b\u67d0\u65e9\u5c31\u731c\u5230\u4e86\uff01\u201d \u3000\u3000\u539f\u6765\u8fd9\u5927\u5934\u5634\u4e4b\u4eba\uff0c\u6b63\u662f\u90a3\u65b0\u8fd1\u81ea\u79f0\u56db\u7075\u6559\u7684\u5927\u5934\u9b3c\u738b\u7ae5\u5b50\u5947\uff01 \u3000\u3000\u7ae5\u5b50\u5947\u54a7\u5634\u563f\u563f\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u65e9\u77e5\u9053\u53c8\u600e\u4e48\u6837\uff1f\u4f60\u53ef\u77e5\u9053\u9047\u5230\u8001\u592b\u4e4b\u4eba\uff0c\u7ed3\u679c\u5982\u4f55\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u660e\u767d\u5f97\u5f88\uff0c\u4e0d\u8fc7\u2026\u2026\u5b8b\u67d0\u542c\u8bf4\u9601\u4e0b\u66fe\u5411\u9635\u5927\u4fa0\u53d1\u8fc7\u8a93\uff0c\u4e0d\u518d\u751f\u88c2\u6d3b\u4eba\uff0c\u4e0d\u518d\u4f5c\u90a3\u4f24\u751f\u4e4b\u4e8b\uff0c\u8fd9\u6d3b\u60f3\u662f\u4e0d\u4f1a\u5047\u7684\u4e86\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u51b7\u54fc\u9053\uff1a\u201c\u5b8b\u8001\u90aa\uff0c\u4f60\u662f\u542c\u4ec0\u4e48\u4eba\u8bf4\u7684\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u4e24\u773c\u4e00\u77aa\uff0c\u63a5\u9053\uff1a\u201c\u8fd9\u4e8b\u53ef\u8bf4\u6b66\u6797\u4e4b\u4e2d\uff0c\u4eba\u4eba\u7686\u77e5\uff0c\u6211\u5b8b\u8001\u90aa\u77e5\u9053\uff0c\u90a3\u53c8\u6709\u4ec0\u4e48\u5947\u602a\uff1f\u201d \u3000\u3000\u91cd\u5b50\u5947\u6012\u89c6\u4e86\u5b8b\u5b5a\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5b8b\u5b5a\uff0c\u8001\u592b\u6709\u4e00\u53e5\u8bdd\uff0c\u53ea\u6015\u4f60\u542c\u4e0d\u8fdb\u8033\u4e2d\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4ec0\u4e48\u8bdd\uff1f\u5b8b\u67d0\u4e00\u751f\uff0c\u6bd4\u4f60\u8fd9\u5927\u5934\u9b3c\u4e5f\u597d\u4e0d\u4e86\u591a\u5c11\uff0c\u6328\u9a82\u4e5f\u662f\u6328\u7684\u591f\u4e86\uff0c\u4e0d\u8bba\u4ec0\u4e48\u96be\u542c\u548c\u8bdd\uff0c\u6211\u5b8b\u67d0\u53ef\u8bf4\u90fd\u4e0d\u5fcc\u8bb3\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u4f60\u914d\u8ddf\u8001\u592b\u6bd4\uff1f\u59d3\u5b8b\u7684\uff0c\u4f60\u672a\u514d\u4e5f\u592a\u77a7\u5f97\u8d77\u81ea\u5df1\u4e86\uff01\u201d \u3000\u3000\u8bed\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u8001\u592b\u8bf4\u4f60\u542c\u4e0d\u8fdb\u8033\u4e2d\u7684\u90a3\u53e5\u8bdd\uff0c\u53ea\u662f\u8981\u63ed\u7a7f\u4f60\u521a\u624d\u626f\u7684\u90a3\u4e2a\u8c0e\u800c\u5df2\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u4e00\u6014\u9053\uff1a\u201c\u5b8b\u67d0\u54ea\u4e00\u53e5\u8bdd\u4e0d\u5b9e\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u5411\u9648\u5927\u4fa0\u53d1\u8a93\u4e4b\u4e8b\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5b9e\u4e0e\u4e0d\u5b9e\uff0c\u4e5f\u6ca1\u6709\u591a\u5927\u5173\u7cfb\u554a\uff01\u4f60\u7ae5\u5144\u5982\u6b64\u8ba4\u771f\uff0c\u770b\u6765\u5012\u662f\u771f\u7684\u4e86\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u6012\u9053\uff1a\u201c\u662f\u771f\u7684\u53c8\u600e\u4e48\u6837\uff1f\u8001\u592b\u77e5\u9053\uff0c\u9648\u5927\u4fa0\u80dc\u8fc7\u8001\u592b\u4e4b\u4e8b\uff0c\u6b66\u6797\u4e4b\u4e2d\uff0c\u786e\u662f\u4eba\u4eba\u77e5\u6653\uff0c\u4f46\u8001\u592b\u4ee5\u8a93\u4e0d\u518d\u751f\u88c2\u6d3b\u4eba\u4e4b\u4e8b\uff0c\u6b66\u6797\u4e4b\u4e2d\uff0c\u77e5\u6653\u4e4b\u4eba\uff0c\u4e0d\u4f1a\u8d85\u8fc7\u4e03\u540d\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u5341\u4eba\uff0c\u767e\u4eba\uff0c\u53c8\u6709\u591a\u5927\u5206\u522b\uff1f\u7ae5\u5144\u8fd9\u822c\u65a4\u65a4\u8ba1\u8f83\uff0c\u771f\u662f\u4e0d\u6015\u5931\u8eab\u4efd\u5417\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u59d3\u5b8b\u7684\uff0c\u8001\u592b\u4eca\u5929\u975e\u5f97\u67e5\u660e\u8c01\u4eba\u544a\u4f60\u6b64\u4e8b\u4e0d\u53ef\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u89c1\u4ed6\u8ba4\u4e86\u771f\uff0c\u4e0d\u7981\u4e5f\u5f15\u8d77\u4e86\u597d\u5947\u4e4b\u5fc3\uff0c\u7b11\u9053\uff1a\u201c\u4e3a\u4f55\u4f60\u7ae5\u5144\u4e00\u5b9a\u8981\u5f04\u660e\u767d\u8fd9\u4e8b\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u5927\u5634\u4e00\u54a7\uff0c\u53eb\u9053\uff1a\u201c\u56e0\u4e3a\u2026\u2026\u8001\u5927\u8981\u8bc1\u5b9e\u4e00\u6869\u4e8b\u4ef6\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u867d\u7136\u5fc3\u673a\u7075\u654f\u8fc7\u4eba\uff0c\u4f46\u8fd9\u65f6\u5374\u4e5f\u60f3\u4e0d\u51fa\u4ed6\u8981\u8bc1\u5b9e\u4e00\u6869\u4ec0\u4e48\u4e8b\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u77e5\u9053\u5982\u662f\u81ea\u5df1\u5148\u95ee\u4ed6\u8981\u8bc1\u5b9e\u4ec0\u4e48\u4e8b\uff0c\u7ae5\u5b50\u5947\u4e00\u5b9a\u4e0d\u80af\uff0c\u800c\u4e14\uff0c\u8bf4\u4e0d\u5b9a\u4ed6\u4f1a\u52a8\u86ee\uff0c\u4e0d\u60dc\u4e00\u62da\u5730\u6765\u5149\u903c\u81ea\u5df1\u8bf4\u51fa\u662f\u4ec0\u4e48\u4eba\u544a\u8bc9\u4e86\u4ed6\u5728\u9648\u5927\u4fa0\u9762\u524d\u53d1\u8a93\uff0c\u662f\u4ee5\uff0c\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u7adf\u662f\u53cd\u53e3\u95ee\u9053\uff1a \u3000\u3000\u201c\u7ae5\u5144\uff0c\u4f60\u8bf4\u4f60\u5728\u5251\u795e\u9648\u5927\u4fa0\u9762\u524d\u53d1\u8a93\u4e4b\u4e8b\uff0c\u77e5\u6653\u4e4b\u4eba\uff0c\u4e0d\u4f1a\u8d85\u8fc7\u4e03\u4eba\uff0c\u7109\u77e5\u4e0d\u662f\u8fd9\u4e03\u4eba\u4e2d\u7684\u4e00\u4eba\u544a\u8bc9\u5b8b\u67d0\u7684\u5462\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u5927\u5634\u4e00\u5f20\uff0c\u602a\u7b11\u9053\uff1a\u201c\u90a3\u600e\u4e48\u4f1a\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u7ae5\u5144\u83ab\u592a\u81ea\u4fe1\u4e86\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u5929\u9b54\u5973\uff0c\u67f3\u5a9a\u5a18\uff0c\u5b54\u5144\u548c\u91d1\u5144\u90a3\u662f\u51b3\u4e0d\u4f1a\u544a\u77e5\u4e8e\u4f60\uff0c\u81f3\u4e8e\u9648\u5927\u4fa0\u4e48\uff0c\u4ed6\u4e43\u662f\u6781\u8bb2\u4fe1\u8bfa\u7684\u6b63\u4eba\u541b\u5b50\uff0c\u81ea\u7136\u4e5f\u4e0d\u4f1a\u544a\u8bc9\u4f60\u7684\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u4eec\u4e94\u4f4d\u90fd\u4e0d\u66fe\u544a\u8bc9\u8fc7\u5b8b\u67d0\uff0c\u4f46\u8fd8\u6709\u522b\u4eba\u554a\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u5269\u4e0b\u6765\u7684\uff0c\u53ea\u6709\u4e24\u4f4d\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u4e5f\u8bb8\u6b63\u662f\u90a3\u4e24\u4f4d\u4e4b\u4e2d\u7684\u4e00\u4f4d\u5462\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u5927\u5934\u4e00\u6447\uff0c\u9053\uff1a\u201c\u66f4\u4e0d\u53ef\u80fd\uff0c\u56e0\u4e3a\u2026\u2026\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u76ee\u5149\u4e00\u5bd2\uff0c\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u5176\u4e2d\u7684\u4e00\u4eba\uff0c\u5c31\u662f\u8001\u592b\u81ea\u5df1\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u4e86\u4e00\u7b11\u9053\uff1a\u201c\u4f46\u662f\u8fd8\u6709\u4e00\u4f4d\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u90a3\u53e6\u4e00\u4f4d\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u91cd\u5b50\u5947\u563f\u563f\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c31\u662f\u4f60\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u95fb\u8a00\u4e0d\u7981\u5446\u4e86\u4e00\u5446\uff0c\u8ba8\u9053\uff1a\u201c\u8fd9\u4e2a\u8001\u9b54\u5934\u600e\u4e48\u4e86\uff1f\u4ed6\u83ab\u975e\u6c14\u7cca\u6d82\u4e86\uff1f\u201d\u4f46\u4ed6\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u7ae5\u5144\uff0c\u4f60\u65e2\u7136\u660e\u767d\u662f\u5b8b\u67d0\uff0c\u90a3\u8fd8\u95ee\u6765\u95ee\u53bb\u4f5c\u751a\uff1f\u201d \u3000\u3000\u91cd\u5b50\u5947\u7a81\u7136\u4e0a\u524d\u4e00\u6b65\uff0c\u63a5\u9053\uff1a\u201c\u5b8b\u5b5a\uff0c\u522b\u4eba\u4e0d\u6562\u60f9\u4f60\uff0c\u6015\u4f60\u7684\u8f7b\u529f\u8eab\u6cd5\u9ad8\u660e\uff0c\u4f46\u8001\u592b\u53ef\u4e0d\u5728\u4e4e\u4f60\u90a3\u4e24\u624b\u89c1\u4e0d\u5f97\u4eba\u7684\u8f7b\u529f\uff0c\u4f60\u660e\u767d\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u7ae5\u5144\u597d\u8bf4\u4e86\uff01\u5927\u5934\u9b3c\u738b\u53c8\u8eab\u517c\u56db\u7075\u6559\u4e3b\uff0c\u81ea\u7136\u4e0d\u4f1a\u628a\u5b8b\u67d0\u653e\u5728\u773c\u4e2d\u7684\u4e86\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u660e\u767d\u5c31\u597d\uff01\u201d \u3000\u3000\u4ed6\u7adf\u7136\u8bed\u97f3\u4e00\u987f\uff0c\u53c8\u7f13\u7f13\u9000\u4e86\u4e00\u6b65\uff0c\u8fd9\u624d\u63a5\u9053\uff1a\u201c\u5982\u679c\u4f60\u60f3\u77e5\u9053\u8001\u592b\u7684\u8f7b\u529f\uff0c\u6bd4\u4e0d\u5f97\u4f60\u5927\u6f20\u9b3c\u624b\uff0c\u54b1\u4eec\u5c31\u8bd5\u4e00\u8bd5\u5982\u4f55\uff1f\u201d \u3000\u3000\u663e\u7136\u8fd9\u9b54\u5934\u5341\u5206\u81ea\u5927\uff0c\u6545\u610f\u63a8\u5f00\u4e00\u6b65\uff0c\u4ee5\u793a\u5927\u65b9\u3002 \u3000\u3000\u5b8b\u5b5a\u5fc3\u4e2d\u5374\u6e05\u695a\u7684\u5f88\uff0c\u81ea\u5df1\u8f7b\u529f\u867d\u597d\uff0c\u4f46\u8981\u8ddf\u4e94\u5927\u9b54\u4e3b\u76f8\u6bd4\u8f83\uff0c\u4ec5\u4ed7\u8f7b\u529f\uff0c\u5728\u8fd9\u65b9\u5706\u4e0d\u53ca\u5341\u4e08\u7684\u7f57\u6c49\u5802\u5185\u52a8\u624b\uff0c\u90a3\u771f\u662f\u81ea\u627e\u82e6\u5403\u4e86\uff01\u6545\u800c\u95fb\u8a00\u4e4b\u540e\uff0c\u5374\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u4e0d\u7528\u8bd5\u4e86\uff0c\u5b8b\u67d0\u5c1a\u6709\u81ea\u77e5\u4e4b\u660e\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u8138\u4e0a\u63a0\u8fc7\u4e00\u4e1d\u602a\u5f02\u7684\u7b11\u5bb9\uff0c\u54fc\u4e86\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u5b8b\u5b5a\uff0c\u4f60\u73b0\u5728\u53ef\u77e5\u9053\u4e86\uff1f\u8001\u592b\u8d39\u4e86\u5927\u534a\u5929\u5507\u820c\uff0c\u53ea\u4e0d\u8fc7\u544a\u8bc9\u4f60\uff0c\u5929\u4e0b\u77e5\u6653\u8001\u592b\u5728\u9648\u5927\u4fa0\u9762\u524d\u8d77\u8a93\u4e4b\u4eba\uff0c\u4e00\u5171\u53ea\u5f97\u516d\u4f4d\uff0c\u800c\u8fd9\u516d\u4f4d\u90fd\u4e0d\u4f1a\u544a\u8bc9\u4f60\u8fd9\u6869\u4e8b\uff0c\u4f60\u5c45\u7136\u4eca\u65e5\u4e00\u89c1\u9762\u5c31\u53eb\u51fa\u8fd9\u6869\u4e0d\u4e3a\u4eba\u77e5\u4e4b\u4e8b\uff0c\u663e\u7136\u9664\u4e86\u4f60\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u4e2a\u6bd4\u4f60\u5148\u77e5\u9053\u7684\u4eba\u4e86\uff0c\u563f\u563f\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bf4\u5230\u6b64\u5904\uff0c\u5ffd\u7136\u5927\u8111\u888b\u5411\u524d\u4e00\u503e\uff0c\u559d\u9053\uff1a\u201c\u5b8b\u5b5a\uff0c\u8fd9\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u4ee5\u4e3a\u81ea\u5df1\u806a\u660e\u8fc7\u4eba\uff0c\u5957\u51fa\u4e86\u91cd\u5b50\u5947\u8bf4\u51fa\u5b9e\u8bdd\uff0c\u4e0d\u6599\u76f4\u5230\u6b64\u523b\uff0c\u65b9\u77e5\u81ea\u5df1\u7adf\u662f\u5760\u5165\u4e86\u7ae5\u5b50\u5947\u7684\u9884\u8bbe\u5708\u5957\u4e4b\u4e2d\uff01\u4e0d\u7531\u5f97\u5fc3\u4e2d\u6697\u6697\u53f9\u6028\u9053\uff1a\u201c\u770b\u6765\u8fd9\u4e94\u5927\u9b54\u4e3b\uff0c\u679c\u7136\u90fd\u6709\u7740\u4e0d\u540c\u5e73\u51e1\u7684\u667a\u6167\u2026\u2026\u201d \u3000\u3000\u8d5e\u53f9\u4e4b\u95f4\uff0c\u5fc3\u4e2d\u8fc5\u5feb\u5730\u5bfb\u601d\u56de\u7b54\u4e4b\u65b9\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u5c1a\u672a\u60f3\u51fa\u600e\u751f\u56de\u7b54\u624d\u597d\u4e4b\u65f6\uff0c\u7ae5\u5b50\u5947\u5df2\u8feb\u4e0d\u53ca\u5f85\u5730\u6c89\u58f0\u9053\uff1a\u201c\u53ef\u662f\u90a3\u9648\u5927\u4fa0\u7684\u4f20\u4eba\u544a\u8bc9\u4e86\u4f60\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u521a\u81ea\u8981\u60f3\u70b9\u5934\uff0c\u4f46\u7a81\u7136\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6697\u9053\uff1a\u201c\u542c\u4ed6\u8fd9\u7b49\u6025\u5207\u7684\u8bed\u6c14\uff0c\u83ab\u975e\u8fd8\u4e0d\u77e5\u9053\u9648\u5927\u4fa0\u6709\u4e86\u4f20\u4eba\u4e86\u5417\uff1f\u201d \u3000\u3000\u8f6c\u5ff5\u81f3\u6b64\uff0c\u4ed6\u7a81\u7136\u6709\u4e86\u8b66\u60d5\uff0c\u7acb\u5373\u6447\u5934\u9053\uff1a\u201c\u4e0d\u662f\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u7ec6\u5c0f\u7684\u7709\u6bdb\u76b1\u4e86\u4e00\u76b1\uff0c\u9887\u4e3a\u5931\u671b\u5730\u63a5\u9053\uff1a\u201c\u4e0d\u662f\uff1f\u8001\u592b\u6709\u4e9b\u4e0d\u4fe1\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u4e0d\u662f\u5c31\u4e0d\u662f\uff0c\u4f60\u4e0d\u76f8\u4fe1\u4e5f\u6ca1\u6709\u6cd5\u5b50\uff01\u4e0d\u8fc7\uff0c\u5b8b\u67d0\u53ef\u4ee5\u544a\u8bc9\u4f60\uff0c\u90a3\u8f6c\u544a\u6b64\u4e8b\u4e4b\u4eba\u662f\u8c01\u3002\u201d \u3000\u3000\u91cd\u5b50\u5947\u5927\u559c\u7b11\u9053\uff1a\u201c\u6b64\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u5929\u9b54\u5973\u7684\u5f1f\u5b50\uff01\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u7ae5\u5b50\u5947\u51e0\u4e4e\u8df3\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u5f20\u5927\u4e86\u90a3\u5f20\u602a\u5634\uff0c\u6ee1\u53e3\u9ec4\u7259\u5916\u9732\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u662f\u771f\u7684\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u4e00\u6a2a\u5fc3\uff0c\u63a5\u9053\uff1a\u201c\u5f53\u7136\u771f\u7684\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u90a3\u5f20\u9ec4\u8138\uff0c\u53d8\u5411\u53d1\u9ed1\uff0c\u5927\u58f0\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff01\u5929\u9b54\u5973\u600e\u53ef\u5982\u6b64\u80af\u4fe1\u8d1f\u4e49\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u7ae5\u5144\uff0c\u4f60\u522b\u628a\u5929\u9b54\u5973\u4f30\u8ba1\u5f97\u592a\u9ad8\u4e86\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u72e0\u72e0\u5730\u770b\u4e86\u5b8b\u5b5a\u4e00\u773c\uff0c\u9053\uff1a\u201c\u8001\u592b\u2026\u2026\u4ecd\u7136\u4e0d\u4fe1\u8fd9\u5929\u9b54\u5973\u4f1a\u5c06\u6b64\u4e8b\u8bf4\u51fa\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u4eba\u8bc1\u5c1a\u5728\u5c11\u6797\uff0c\u7ae5\u5144\u4e0d\u4fe1\uff0c\u53ef\u4ee5\u95ee\u5979\uff01\u201d \u3000\u3000\u6562\u60c5\u5b8b\u5b5a\u8fd9\u65f6\u5df2\u7136\u65ad\u5b9a\uff0c\u4eca\u65e5\u6765\u5230\u5c11\u6797\u7684\u4eba\u7269\u4e4b\u4e2d\uff0c\u5e76\u65e0\u5929\u9b54\u5973\u624b\u4e0b\u5b50\u5f1f\uff0c\u662f\u4ee5\uff0c\u4ed6\u80c6\u5b50\u4e5f\u5927\u4e86\u8d77\u6765\u3002 \u3000\u3000\u7ae5\u5b50\u5947\u95fb\u8a00\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u90a3\u5929\u9b54\u5973\u7684\u5f1f\u5b50\uff0c\u600e\u4f1a\u5728\u8fd9\u5c11\u6797\u5bfa\u4e2d\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u5728\u6b64\u517b\u4f24\uff0c\u53c8\u6709\u4f55\u4e0d\u53ef\uff1f\u201d \u3000\u3000\u4ed6\u8fd9\u4e48\u4e00\u8bf4\uff0c\u5728\u7ae5\u5b50\u5947\u542c\u6765\uff0c\u7b80\u76f4\u662f\u7384\u4e4b\u53c8\u7384\uff0c\u4ed6\u6839\u672c\u60f3\u4e0d\u900f\uff0c\u5929\u9b54\u5973\u7684\u5f1f\u5b50\uff0c\u53c8\u600e\u4f1a\u8dd1\u5230\u5c11\u6797\u5bfa\u4e2d\u517b\u4f24\u4e86\uff0c\u9664\u975e\u90a3\u5929\u9b54\u5973\u5df2\u7136\u6536\u670d\u4e86\u5c11\u6797\u50e7\u4fa3\uff0c\u6216\u662f\u90a3\u5929\u9b54\u5973\u5df2\u7136\u6295\u9760\u4e86\u5c11\u6797\u7b49\u4e5d\u95e8\u6d3e\uff01\u201d \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u7ae5\u5b50\u5947\u5446\u4e86\u534a\u664c\uff0c\u65b9\u9053\uff1a\u201c\u90a3\u5929\u9b54\u5973\u4e5f\u5728\u5c11\u6797\u5bfa\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u5fc3\u60f3\uff0c\u8fd9\u53ef\u4e0d\u80fd\u660e\u767d\u7684\u56de\u7b54\u4e86\u3002 \u3000\u3000\u5f53\u4e0b\u5fae\u5fae\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u773c\u4e0b\u662f\u5426\u4ecd\u5728\uff0c\u5b8b\u67d0\u5c31\u4e0d\u77e5\u9053\u4e86\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u76b1\u7709\u9053\uff1a\u201c\u5b8b\u5b5a\uff0c\u90a3\u5251\u795e\u9648\u5927\u4fa0\u7684\u5f1f\u5b50\uff0c\u4f60\u53ef\u89c1\u5230\u8fc7\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u8fc5\u5feb\u7684\u770b\u4e86\u5c1a\u5728\u6e38\u6597\u4e2d\u7684\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u63a5\u9053\uff1a\u201c\u8fd9\u4e2a\u5417\uff1f\u7ae5\u5144\u4f55\u4e0d\u53bb\u95ee\u9648\u5927\u4fa0\u5462\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u5446\u4e00\u5446\uff0c\u9053\uff1a\u201c\u9648\u5927\u4fa0\u4f20\u95fb\u4e5f\u5df2\u4ea1\u6545\uff0c\u4f60\u53eb\u8001\u592b\u5230\u54ea\u91cc\u53bb\u95ee\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u6697\u9053\uff1a\u201c\u4ed6\u6562\u60c5\u8fd8\u4e0d\u5927\u786e\u5b9a\u5251\u795e\u5df2\u6b7b\uff0c\u6211\u7d22\u6027\u5413\u4ed6\u4e00\u4e0b\u2026\u2026\u201d \u3000\u3000\u5f53\u4e0b\u54c8\u54c8\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u8c01\u544a\u8bc9\u4f60\u5251\u795e\u4e0d\u5728\u4eba\u4e16\u4e86\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u81ea\u7136\u4e5f\u4e0d\u662f\u90a3\u7b49\u5bb9\u6613\u88ab\u4eba\u552c\u4f4f\u7684\uff0c\u5f53\u4e0b\u4e24\u773c\u4e00\u7ffb\uff0c\u9053\uff1a\u201c\u5b8b\u5b5a\uff0c\u4f60\u5728\u6253\u4ec0\u4e48\u4e3b\u610f\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u5b8b\u67d0\u5728\u4f60\u7ae5\u5144\u9762\u524d\uff0c\u8fd8\u6562\u6253\u4ec0\u4e48\u9b3c\u4e3b\u610f\u5417\uff1f\u4e0d\u8fc7\u662f\u5fc3\u4e2d\u72af\u7591\uff0c\u60f3\u7b49\u5c0a\u9a7e\u8bc1\u5b9e\u90a3\u5251\u795e\u9648\u5927\u4fa0\u7684\u751f\u6b7b\u4e4b\u8c1c\u800c\u5df2\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u5b8b\u5b5a\uff0c\u4f60\u6700\u8fd1\u53ef\u89c1\u8fc7\u9648\u5e0c\u6b63\u4e86\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u4f60\u719f\u6089\u6b66\u6797\u670b\u53cb\u4e4b\u4e2d\uff0c\u53ef\u6709\u4eba\u89c1\u8fc7\u9648\u5e0c\u6b63\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8fd9\u4e2a\u5417\uff1f\u5b8b\u67d0\u5728\u6b66\u6797\u4e2d\u7684\u670b\u53cb\u4e0d\u7b97\u591a\uff0c\u7eb5\u7136\u4ed6\u4eec\u6ca1\u6709\u89c1\u5230\u8fc7\u9648\u5927\u4fa0\uff0c\u4e5f\u4e0d\u8db3\u4ee5\u8bc1\u660e\u9648\u5927\u4fa0\u5df2\u7ecf\u4e0d\u5728\u4eba\u4e16\u4e86\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6709\u4e00\u4ef6\u4e8b\uff0c\u53ea\u6015\u4f60\u5b8b\u5b5a\u672a\u66fe\u60f3\u5230\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u5251\u795e\u9648\u5927\u4fa0\u5982\u82e5\u4ecd\u5728\u4eba\u4e16\uff0c\u4ed6\u53ef\u4ee5\u4ec0\u4e48\u4eba\u90fd\u4e0d\u89c1\uff0c\u4f46\u5374\u4e0d\u80fd\u4e0d\u89c1\u8001\u592b\u7b49\u4e94\u4eba\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u90a3\u4e5f\u4e0d\u89c1\u5f97\u5427\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u5b8b\u5b5a\uff0c\u4f60\u83ab\u8981\u5fd8\u4e86\uff0c\u90a3\u5251\u795e\u9648\u5927\u4fa0\u66fe\u7ecf\u89c4\u5b9a\u8fc7\uff0c\u54b1\u4eec\u4e94\u4eba\uff0c\u6700\u5c11\u6bcf\u9694\u4e09\u5e74\uff0c\u8981\u89c1\u4ed6\u4e00\u6b21\uff0c\u4ece\u4e0a\u6b21\u5206\u624b\u4ee5\u540e\uff0c\u5982\u4eca\u5df2\u6709\u56db\u5e74\u51fa\u5934\uff0c\u4ecd\u7136\u672a\u89c1\u9648\u5927\u4fa0\u6709\u90a3\u624b\u8d34\u4f20\u6765\uff0c\u8fd9\u8db3\u53ef\u4ee5\u8bc1\u660e\u4e00\u4ef6\u4e8b\uff0c\u5c31\u662f\u5251\u795e\u9648\u5927\u4fa0\u5df2\u7136\u4e0d\u5728\u4eba\u95f4\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u95fb\u8a00\u5fc3\u4e2d\u4e00\u60ca\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u4e2a\u8001\u9b54\u5934\u679c\u7136\u662f\u5389\u5bb3\u5f97\u5f88\u3002\u201d \u3000\u3000\u4fbf\u4ed6\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u9601\u4e0b\u5c31\u51ed\u4e86\u8fd9\u4e00\u70b9\uff0c\u65ad\u5b9a\u90a3\u9648\u5927\u4fa0\u4e5f\u5df2\u4ed9\u901d\u4e86\u5417\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u5c31\u6b64\u4e00\u70b9\uff0c\u5df2\u7ecf\u5f88\u591f\u4e86\uff01\u201d\u8bed\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4f55\u51b5\uff0c\u9648\u5927\u4fa0\u5982\u5728\u4eba\u4e16\uff0c\u600e\u4f1a\u5bb9\u8bb8\u5929\u9b54\u5973\u7684\u5f1f\u5b50\uff0c\u5728\u5916\u80e1\u4f5c\u975e\u4e3a\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u8fd9\u4e48\u4e00\u8bf4\uff0c\u53ef\u628a\u5b8b\u5b5a\u7ed9\u9a73\u4f4f\u4e86\uff01 \u3000\u3000\u4ed6\u534a\u664c\u5374\u60f3\u4e0d\u51fa\u6709\u4ec0\u4e48\u597d\u7406\u7531\u96be\u4f4f\u4ed6\u3002 \u3000\u3000\u8fd9\u65f6\u90a3\u65b9\u96ea\u5b9c\u5ffd\u7136\u5927\u559d\u4e00\u58f0\u9053\uff1a\u201c\u5e84\u5144\uff0c\u4f60\u5982\u518d\u4e0d\u77e5\u8da3\uff0c\u53ef\u83ab\u602a\u5144\u5f1f\u4e0d\u7559\u60c5\u4e86\uff01\u201d \u3000\u3000\u6562\u60c5\uff0c\u4ed6\u4eec\u6253\u4e86\u534a\u5929\uff0c\u65b9\u96ea\u5b9c\u4e00\u76f4\u672a\u66fe\u4e0b\u8fc7\u6740\u624b\uff0c\u5426\u5219\uff0c\u6709\u5341\u4e2a\u5e84\u6377\uff0c\u4e5f\u8981\u6a2a\u5c38\u5f53\u5730\u7684\u4e86\uff01\u201d \u3000\u3000\u5e84\u6377\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4e8b\u5230\u8fd9\u822c\u65f6\u5019\uff0c\u5144\u5f1f\u4e5f\u65e0\u6cd5\u81ea\u5df1\uff0c\u54b1\u4eec\u597d\u6b79\u5f97\u6709\u4e00\u4e2a\u8eba\u4e0b\u2026\u2026\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u7adf\u7136\u653b\u4e86\u4e09\u638c\u4e94\u6307\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u4e24\u773c\u795e\u529f\u66b4\u5c04\uff0c\u4e00\u9762\u4e3e\u624b\u5c01\u67b6\uff0c\u4e00\u9762\u51b7\u51b7\u5730\u54fc\u4e86\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u5e84\u5144\uff0c\u770b\u6765\u4f60\u662f\u903c\u5f97\u5144\u5f1f\u975e\u5411\u4f60\u4e0b\u91cd\u624b\u4e0d\u53ef\u4e86\u2026\u2026\u201d \u3000\u3000\u5e84\u6377\u8fc5\u5feb\u5730\u53c8\u653b\u51fa\u4e86\u4e09\u62db\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u54b1\u4eec\u4e0d\u5206\u51fa\u751f\u6b7b\uff0c\u51b3\u4e0d\u7f62\u624b\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u84e6\u5730\u5927\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u65e0\u6028\u65e0\u4ec7\uff0c\u4f3c\u662f\u7528\u4e0d\u7740\u5206\u51fa\u751f\u6b7b\u5427\u2026\u2026\u201d\u8bed\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u53ea\u89c1\u4ed6\u53cc\u638c\u5206\u51fa\uff0c\u8fde\u653b\u4e86\u5e84\u6377\u4e24\u62db\uff0c\u63a5\u9053\uff1a\u201c\u54b1\u4eec\u53ea\u987b\u6253\u51fa\u4e2a\u9ad8\u4f4e\u4fbf\u6210\u4e86\uff01\u201d \u3000\u3000\u5e84\u6377\u51b7\u7b11\u9053\uff1a\u201c\u4e5f\u597d\u2026\u2026\u201d \u3000\u3000\u4ed6\u90a3\u597d\u5b57\u521a\u51fa\u53e3\uff0c\u65b9\u96ea\u5b9c\u5df2\u7136\u5927\u559d\u4e00\u58f0\u9053\uff1a\u201c\u5e84\u5144\uff0c\u4e14\u6055\u5144\u5f1f\u5f97\u7f6a\u4e86\uff01\u201d \u3000\u3000\u53ea\u89c1\u4ed6\u53f3\u81c2\u4e4e\u4f38\uff0c\u98df\u4e2d\u4e8c\u6307\u5e76\u7acb\uff0c\u84e6\u5730\u5708\u8f6c\uff0c\u7136\u540e\u5411\u5916\u5288\u51fa\u3002 \u3000\u3000\u5e84\u6377\u8138\u8272\u4e00\u53d8\uff0c\u98de\u8eab\u5411\u540e\u9000\u51fa\u3002 \u3000\u3000\u4f46\u65b9\u96ea\u5b9c\u8fd9\u4e00\u62db\u624b\uff0c\u4e43\u662f\u52bf\u5728\u5fc5\u5f97\uff0c\u6240\u4ee5\u5e84\u6377\u98de\u8eab\u540e\u9000\u7684\u867d\u7136\u751a\u5feb\uff0c\u7ed3\u679c\u5374\u5e76\u672a\u95ea\u907f\u5f97\u4e86\uff01\u8033\u4e2d\u53ea\u542c\u5f97\u7830\u7684\u4e00\u58f0\uff0c\u5e84\u6377\u4f46\u611f\u80f8\u524d\u4e00\u9707\uff0c\u987f\u65f6\u7acb\u8db3\u4e0d\u7a33\uff0c\u4e00\u8de4\u6454\u5012\u5728\u5730\u3002 \u3000\u3000\u6562\u60c5\uff0c\u65b9\u96ea\u5b9c\u8fd9\u4e00\u62db\u4e43\u662f\u7528\u7684\u4ee5\u6307\u4ee3\u5251\u7684\u5e08\u95e8\u5251\u6cd5\uff0c\u51fa\u624b\u70b9\u4e2d\u4e86\u5e84\u6377\u80f8\u524d\u7a74\u9053\u3002 \u3000\u3000\u4ed6\u4fef\u4e0b\u8eab\u53bb\uff0c\u4e00\u628a\u5c06\u5e84\u6377\u6293\u8d77\uff0c\u653e\u5728\u9760\u5899\u7684\u89d2\u843d\u5750\u597d\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u5e84\u5144\uff0c\u4f60\u7a74\u9053\u53d7\u5236\uff0c\u6700\u597d\u4e0d\u8981\u5984\u81ea\u8fd0\u6c14\u51b2\u7a74\uff0c\u5426\u5219\u4f24\u4e86\u5185\u8151\uff0c\u83ab\u602a\u5144\u5f1f\u8a00\u4e4b\u4e0d\u9884\u2026\u2026\u201d\u4e0d\u7b49\u5e84\u6377\u6709\u6240\u8868\u793a\uff0c\u8f6c\u8eab\u5927\u6b65\u5411\u5b8b\u5b5a\u8d70\u6765\u3002 \u3000\u3000\u5b8b\u5b5a\u672c\u662f\u5728\u76b1\u7709\u82e6\u601d\uff0c\u600e\u6837\u56de\u7b54\u90a3\u7ae5\u5b50\u5947\u4e4b\u8a00\uff0c\u8fd9\u65f6\u5ffd\u89c1\u65b9\u96ea\u5b9c\u8d70\u6765\uff0c\u4e0d\u7981\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u90a3\u5e84\u6377\u7684\u6b66\u529f\u4e0d\u574f\u561b\uff01\u5c45\u7136\u8ddf\u4f60\u6253\u4e86\u8fd9\u4e48\u4e45\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u8fd9\u4f4d\u8001\u4eba\u5bb6\u662f\u8c01\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u89c1\u4ed6\u7b54\u975e\u6240\u95ee\u5730\u53cd\u95ee\u81ea\u5df1\u4e00\u53e5\uff0c\u53ea\u597d\u7b11\u9053\uff1a\u201c\u4e94\u5927\u9b54\u4e3b\u4e4b\u4e2d\u7684\u4e00\u4f4d\uff0c\u5927\u5934\u9b3c\u738b\u7ae5\u5b50\u5947\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9002\u624d\u4e0e\u5e84\u6377\u5bf9\u654c\u4e4b\u65f6\uff0c\u5c31\u5df2\u628a\u4e24\u4eba\u5bf9\u7b54\u4e4b\u8a00\uff0c\u542c\u7684\u6e05\u6e05\u695a\u695a\uff0c\u4ed6\u51fa\u8a00\u76f8\u8be2\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e0d\u60f3\u663e\u9732\u81ea\u5df1\u800c\u5df2\uff0c\u95fb\u8a00\u7acb\u5373\u63a5\u9053\uff1a\u201c\u539f\u6765\u662f\u7ae5\u8001\u2026\u2026\u201d \u3000\u3000\u53cc\u624b\u62b1\u62f3\uff0c\u5411\u7ae5\u5b50\u5947\u4f5c\u4e86\u4e00\u63d6\u3002 \u3000\u3000\u7ae5\u5b50\u5947\u5927\u8111\u888b\u4e00\u6643\uff0c\u4e24\u773c\u4e00\u7ffb\uff0c\u9053\uff1a\u201c\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u4ec0\u4e48\u4eba\u7684\u95e8\u4e0b\uff1f\u77a7\u4f60\u80fd\u5c06\u5e84\u6377\u51fb\u8d25\uff0c\u8db3\u89c1\u4f60\u7684\u6b66\u529f\u4e0d\u9519\u554a\uff01\u201d \u3000\u3000\u4ed6\u4e00\u53e3\u6c14\u95ee\u4e86\u8fd9\u4e48\u591a\u8bdd\uff0c\u5012\u662f\u5927\u51fa\u5b8b\u5b5a\u610f\u6599\u4e4b\u5916\uff0c\u53ea\u56e0\u7ae5\u5b50\u5947\u548c\u90a3\u516b\u86ee\u9f13\u5e72\u91d1\u957f\u5e9a\u4e24\u4eba\uff0c\u5728\u4e94\u9b54\u4e4b\u4e2d\uff0c\u7d20\u6765\u662f\u6700\u4e0d\u559c\u5f00\u53e3\u591a\u8bf4\u51e0\u53e5\u8bdd\u7684\u4eba\uff0c\u4eca\u65e5\u4ece\u4ed6\u73b0\u8eab\uff0c\u5c31\u4e00\u76f4\u5927\u53cd\u5e38\u60c5\uff0c\u8db3\u89c1\u5176\u4e2d\u5fc5\u6709\u7f18\u6545\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u770b\u4e86\u5b8b\u5b5a\u4e00\u773c\uff0c\u63a5\u9053\uff1a\u201c\u533a\u533a\u59d3\u65b9\uff0c\u8349\u5b57\u96ea\u5b9c\uff0c\u81f3\u4e8e\u4f55\u4eba\u95e8\u4e0b\uff0c\u6055\u65b9\u67d0\u6b49\u96be\u5949\u544a\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u89c1\u4ed6\u5ffd\u7136\u62a5\u51fa\u4e86\u771f\u5b9e\u59d3\u540d\uff0c\u4e0d\u7531\u5446\u4e86\u4e00\u5446\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u4f4d\u8001\u5f1f\u771f\u4e0d\u591f\u673a\u8b66\uff0c\u5982\u662f\u8fd9\u9b54\u5934\u4e0e\u90a3\u67f3\u5a9a\u5a18\u89c1\u8fc7\u9762\uff0c\u4e00\u542c\u65b9\u96ea\u5b9c\u4e09\u5b57\uff0c\u4e0d\u5c31\u77e5\u4e86\u4f60\u662f\u5251\u795e\u9648\u5927\u4fa0\u7684\u552f\u4e00\u4f20\u4eba\u4e86\u5417\uff1f\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u4e00\u6025\uff0c\u4e0d\u7981\u8131\u53e3\u9053\uff1a\u201c\u65b9\u8001\u5f1f\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u5b8b\u8001\u6709\u4f55\u6307\u6559\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u76b1\u7709\u5401\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u5f53\u4e94\u5927\u9b54\u4e3b\u7684\u9762\u524d\uff0c\u4f60\u8bf4\u8bdd\u53ef\u5f97\u5c0f\u5fc3\u4e00\u4e9b\u624d\u662f\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u660e\u767d\u5b8b\u5b5a\u8bdd\u4e2d\u4e4b\u610f\uff0c\u4f46\u4ed6\u7684\u60f3\u6cd5\uff0c\u5374\u4e0e\u5b8b\u5b5a\u4e0d\u540c\uff0c\u5e08\u7236\u5728\u6b66\u6797\u4e4b\u4e2d\uff0c\u88ab\u4eba\u79f0\u4e3a\u5251\u795e\uff0c\u81ea\u5df1\u53c8\u600e\u80fd\u8eb2\u8eb2\u85cf\u85cf\uff0c\u662f\u4ee5\uff0c\u5b8b\u5b5a\u8bdd\u97f3\u4e00\u843d\uff0c\u4ed6\u7acb\u5373\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u665a\u8f88\u660e\u767d\uff01\u4f60\u8001\u4e0d\u7528\u653e\u5728\u5fc3\u4e0a\uff01\u201d \u3000\u3000\u90a3\u7ae5\u5b50\u5947\u76ee\u5149\u4e00\u8f6c\uff0c\u51b7\u7b11\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u8001\u592b\u89c1\u4f60\u6b66\u529f\u4e0d\u9519\uff0c\u624d\u4f1a\u52a8\u95ee\u4f60\u7684\u5e08\u95e8\uff0c\u5c0f\u5b50\uff0c\u4f60\u522b\u8981\u4e0d\u8bc6\u62ac\u4e3e\uff0c\u8001\u592b\u53ef\u662f\u5929\u4e0d\u6015\u5730\u4e0d\u6015\u7684\uff0c\u7eb5\u7136\u4f60\u662f\u4e5d\u5927\u95e8\u6d3e\u638c\u95e8\u7684\u5f1f\u5b50\uff0c\u8001\u592b\u4e00\u6837\u53ef\u4ee5\u53d6\u4f60\u4e4b\u547d\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u5fc3\u4e2d\u4e00\u6014\uff0c\u6697\u9053\uff1a\u8fd9\u4eba\u5230\u5e95\u662f\u4e2a\u9b54\u5934\uff0c\u597d\u8c61\u4e00\u4e2a\u4eba\u7684\u6027\u547d\uff0c\u5728\u4ed6\u773c\u4e2d\uff0c\u8d31\u5f97\u6bd4\u90a3\u9e21\u72ac\u4e0d\u5982\u2026\u2026 \u3000\u3000\u4ed6\u5fc3\u4e2d\u867d\u7136\u6709\u4e9b\u5403\u60ca\uff0c\u4f46\u4e5f\u6709\u7740\u4e00\u4efd\u6012\u610f\uff0c\u5f53\u4e0b\u8138\u8272\u4e00\u6c89\uff0c\u6012\u9053\uff1a\u201c\u5c0a\u9a7e\u53ef\u662f\u62ff\u6740\u4eba\u5f53\u4f5c\u513f\u620f\u5417\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u4f60\u53ef\u662f\u521d\u51fa\u9053\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u662f\u53c8\u5982\u4f55\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u4f60\u5982\u4e0d\u662f\u521a\u521a\u51fa\u9053\uff0c\u60f3\u5fc5\u5c31\u4e0d\u4f1a\u5bf9\u8001\u592b\u7684\u9002\u624d\u90a3\u53e5\u8bdd\u611f\u5230\u60ca\u5f02\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u51b7\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u542c\u9601\u4e0b\u4e4b\u8a00\uff0c\u4e00\u4e2a\u4eba\u7684\u751f\u547d\uff0c\u5728\u4f60\u773c\u4e2d\u662f\u4e00\u6587\u4e0d\u503c\u7684\u4e86\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u5982\u662f\u4f60\u90a3\u7b49\u8d2a\u751f\u6015\u6b7b\uff0c\u5f53\u5e74\u4f60\u5c31\u4e0d\u5e94\u6295\u5e08\u5b66\u827a\uff0c\u60f3\u5728\u6b66\u6797\u4e4b\u4e2d\uff0c\u626c\u540d\u4e89\u80dc\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5ffd\u7136\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u7ae5\u5b50\u5947\uff0c\u6709\u4e00\u6869\u4e8b\uff0c\u4e0d\u77e5\u9601\u4e0b\u53ef\u66fe\u60f3\u5230\u8fc7\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u5927\u5634\u4e00\u54a7\uff0c\u72c2\u7b11\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u4f60\u80c6\u5b50\u771f\u4e0d\u5c0f\uff0c\u8001\u592b\u7684\u540d\u53f7\uff0c\u4e5f\u662f\u4f60\u53eb\u5f97\u7684\u5417\uff1f\u201d \u3000\u3000\u4ed6\u7b11\u58f0\u4e00\u987f\uff0c\u53c8\u70b9\u4e86\u70b9\u5934\uff0c\u63a5\u9053\uff1a\u201c\u6b66\u6797\u4e4b\u4e2d\u51fa\u4e86\u4f60\u8fd9\u7b49\u5927\u80c6\u4e4b\u5f92\uff0c\u8001\u592b\u771f\u662f\u4f69\u670d\u7684\u5f88\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u80fd\u5f97\u5c0a\u9a7e\u4f69\u670d\uff0c\u5012\u662f\u8363\u5e78\u5f97\u5f88\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u4f60\u8bf4\u6709\u4e00\u6869\u4e8b\u8981\u95ee\u8001\u592b\uff0c\u4e0d\u77e5\u662f\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u5f88\u7b80\u5355\u7684\u4e00\u6869\u4e8b\uff0c\u5c0a\u9a7e\u8fd9\u4e00\u751f\u4e4b\u4e2d\uff0c\u53ef\u66fe\u9762\u4e34\u8fc7\u6b7b\u4ea1\u7684\u5a01\u80c1\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u5446\u4e86\u4e00\u5446\uff0c\u5b8c\u5168\u6ca1\u6709\u60f3\u5230\uff0c\u65b9\u96ea\u5b9c\u95ee\u7684\uff0c\u7adf\u662f\u8fd9\u4e48\u4e00\u53e5\u8bdd\uff0c\u662f\u4ee5\u6c89\u541f\u4e86\u534a\u664c\uff0c\u65b9\u59cb\u63a5\u9053\uff1a\u201c\u6ca1\u6709\uff01\u201d \u3000\u3000\u4ed6\u8eab\u4e3a\u4e94\u5927\u9b54\u4e3b\u4e4b\u4eba\uff0c\u7eb5\u7136\u5f53\u5e74\u5251\u795e\u9648\u5927\u4fa0\u964d\u670d\u4ed6\u4eec\u4e4b\u65f6\uff0c\u4f7f\u4ed6\u6709\u8fc7\u6b7b\u4ea1\u7684\u6050\u60e7\uff0c\u4f46\u8981\u4ed6\u4eb2\u53e3\u5411\u522b\u4eba\u8bf4\u51fa\uff0c\u90a3\u53ef\u6bd4\u8981\u4ed6\u7684\u8001\u547d\u66f4\u96be\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u4e5f\u4e0d\u7531\u5730\u6014\u4e86\u4e00\u6014\uff0c\u6697\u9053\uff1a\u201c\u4ed6\u56de\u7b54\u5f97\u8fd9\u7b49\u5e72\u8106\uff0c\u5982\u662f\u6362\u4e86\u522b\u4eba\uff0c\u53ea\u6015\u4e0d\u5f97\u4e0d\u76f8\u4fe1\u4ed6\u4e86\uff01\u201d \u3000\u3000\u5f53\u4e0b\u8138\u8272\u4e00\u6c89\uff0c\u51b7\u51b7\u7684\u63a5\u9053\uff1a\u201c\u5f53\u771f\u6ca1\u6709\u8fc7\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u8001\u592b\u5c82\u662f\u9a97\u4eba\u4e4b\u4eba\uff1f\u5c0f\u5b50\uff0c\u4f60\u8fd9\u7b49\u5c0f\u89c6\u8001\u592b\uff0c\u8001\u592b\u5230\u8981\u95ee\u4f60\uff0c\u4f60\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba\u7684\u95e8\u4e0b\uff1f\u201d \u3000 \u7b2c\u4e8c\u5341\u516d\u56de\u3000\u795e\u5251\u65bd\u5a01 \u3000 \u3000\u3000\u65b9\u96ea\u5b9c\u770b\u4e86\u5b8b\u5b5a\u4e00\u773c\uff0c\u53ea\u89c1\u4ed6\u6b63\u5728\u671d\u81ea\u5df1\u76b1\u7709\uff0c\u987f\u65f6\u7565\u4e00\u6c89\u541f\uff0c\u7b11\u9053\uff1a\u201c\u7ae5\u5b50\u5947\uff0c\u4f60\u771f\u60f3\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8001\u592b\u4eca\u5929\u975e\u5f97\u5f04\u660e\u767d\u4e0d\u53ef\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u56de\u987e\u4e86\u90a3\u4e24\u4f4d\u4e00\u6d3e\u5b97\u4e3b\u4e00\u773c\uff0c\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u533a\u533a\u6709\u4e2a\u6761\u4ef6\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u8138\u8272\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u6761\u4ef6\uff0c\u4f60\u7adf\u7136\u5728\u8001\u592b\u9762\u524d\u800d\u4ec0\u4e48\u82b1\u6837\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u5c0a\u9a7e\u4e0d\u7b54\u5e94\u5417\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u51b7\u7b11\u9053\uff1a\u201c\u8001\u592b\u5c82\u662f\u53d7\u4eba\u80c1\u5236\u4e4b\u4eba\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5927\u7b11\u9053\uff1a\u201c\u65e2\u7136\u5c0a\u9a7e\u4e0d\u613f\uff0c\u90a3\u5c31\u7b97\u4e86\uff01\u6055\u533a\u533a\u65e0\u6cd5\u8bf4\u51fa\u5e08\u95e8\u540d\u53f7\u4e86\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u5927\u6012\u9053\uff1a\u201c\u59d3\u65b9\u7684\u5c0f\u5b50\uff0c\u4f60\u4e0d\u6015\u6b7b\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u533a\u533a\u51fa\u9053\u4ee5\u6765\uff0c\u8fd8\u6ca1\u6709\u78b0\u5230\u8fc7\u4e00\u4e2a\u8db3\u4ee5\u5236\u6211\u4e8e\u6b7b\u5730\u4e4b\u4eba\uff0c\u5c0a\u9a7e\u53e3\u51fa\u6b64\u8a00\uff0c\u53ef\u662f\u8ba4\u4e3a\u533a\u533a\u7684\u6b66\u529f\u4e0d\u80fd\u81ea\u4fdd\u5417\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u54a7\u5f00\u5927\u5634\uff0c\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff01\u8001\u592b\u8981\u53d6\u5c14\u6027\u547d\uff0c\u5f53\u771f\u662f\u6613\u5982\u53cd\u638c\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5251\u7709\u4e00\u626c\uff0c\u7b11\u9053\uff1a\u201c\u533a\u533a\u4e0d\u4fe1\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u5927\u8111\u888b\u4e00\u6643\uff0c\u51b7\u54fc\u9053\uff1a\u201c\u4e0d\u4fe1\u4f55\u4e0d\u8bd5\u4e0a\u4e00\u8bd5\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u65f6\u5df2\u77e5\u90a3\u67f3\u5a9a\u5a18\u8fd9\u4e00\u6bb5\u65f6\u65e5\u5e76\u672a\u4e0e\u91cd\u5b50\u5947\u89c1\u9762\uff0c\u800c\u4e14\uff0c\u90a3\u827e\u4e1c\u6d77\u548c\u9ad8\u65e0\u6545\u4e5f\u672a\u548c\u4ed6\u78b0\u5934\uff0c\u5426\u5219\uff0c\u7ae5\u5b50\u5947\u5c31\u4e0d\u4f1a\u4e3a\u4e86\u8981\u67e5\u660e\u81ea\u5df1\u5e08\u95e8\u662f\u8c01\uff0c\u4f1a\u8bf4\u51fa\u8fd9\u7b49\u6781\u6709\u5a01\u80c1\u610f\u5473\u7684\u8bdd\u6765\u4e86\uff01 \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u4ed6\u5ffd\u7136\u6709\u4e86\u8ba1\u8f83\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u7ae5\u5b50\u5947\uff0c\u54b1\u4eec\u6253\u4e0a\u4e00\u4e2a\u8d4c\u5982\u4f55\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u4e00\u6014\u9053\uff1a\u201c\u6253\u8d4c\uff1f\u8d4c\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8d4c\u4f60\u80dc\u4e0d\u4e86\u533a\u533a\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u542c\u5f97\u4e00\u5446\u9053\uff1a\u201c\u80dc\u4e0d\u4e86\u4f60\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u9519\uff01\u4f60\u6562\u4e0d\u6562\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u5ffd\u7136\u72c2\u7b11\u9053\uff1a\u201c\u6562\uff01\u6562\uff01\u8001\u592b\u5982\u662f\u4e0d\u6562\uff0c\u90a3\u8fd8\u7b97\u5f97\u4e94\u5927\u9b54\u4e3b\u4e4b\u4e00\u5417\uff1f\u201d \u3000\u3000\u4ed6\u7b11\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u8fd9\u4e48\u529e\uff0c\u8001\u592b\u7ed9\u4f60\u4e00\u4e2a\u4fbf\u5b9c\uff0c\u54b1\u4eec\u8ba2\u4e0a\u4e00\u4e2a\u62db\u6570\u9650\u5236\u5982\u4f55\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u597d\uff01\u4e00\u5343\u62db\u5427\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u5927\u5634\u4e00\u5f20\uff0c\u534a\u664c\u6ca1\u6709\u5408\u4e86\u62e2\u6765\uff01\u4e24\u773c\u76f4\u6123\u6123\u5730\u770b\u7740\u65b9\u96ea\u5b9c\uff0c\u4e45\u4e45\u4e0d\u51fa\u58f0\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u600e\u4e48\u4e86\uff1f\u53ef\u662f\u5acc\u5c11\u5417\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u957f\u957f\u5730\u5438\u4e86\u4e00\u53e3\u5927\u6c14\uff0c\u9053\uff1a\u201c\u5c11\uff1f\u592a\u591a\u4e86\u5c0f\u5b50\uff0c\u4f60\u72c2\u5f97\u5f88\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6545\u610f\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u63a5\u9053\uff1a\u201c\u4e94\u767e\u62db\u5982\u4f55\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u4ecd\u7136\u592a\u591a\u4e86\uff01\u201d \u3000\u3000\u4ed6\u4e0d\u518d\u7b49\u65b9\u96ea\u5b9c\u8bf4\u8bdd\uff0c\u5f88\u5feb\u5730\u63a5\u9053\uff1a\u201c\u8fd9\u4e48\u529e\uff0c\u8001\u592b\u4e0e\u4f60\u52a8\u624b\uff0c\u53ea\u4ee5\u4e94\u5341\u62db\u4e3a\u9650\uff0c\u5982\u662f\u4e94\u5341\u62db\u4e4b\u5185\uff0c\u8001\u592b\u80dc\u4e0d\u4e86\u4f60\uff0c\u90a3\u5c31\u4e00\u5207\u4efb\u51ed\u4f60\u5c0f\u5b50\u5904\u7f6e\u4fbf\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u5fc3\u4e2d\u6697\u6697\u5931\u7b11\uff01 \u3000\u3000\u5b8b\u5b5a\u3001\u5b89\u5c0f\u840d\u3001\u970d\u9e23\u98ce\u4e09\u4eba\u4e5f\u5728\u4e00\u65c1\u6697\u611f\u5f97\u610f\uff0c\u53ea\u6709\u5165\u4e91\u5927\u5e08\u672a\u66fe\u771f\u6b63\u89c1\u8bc6\u8fc7\u65b9\u96ea\u5b9c\u7684\u6b66\u529f\uff0c\u542c\u4ed6\u7adf\u5411\u7ae5\u5b50\u5947\u6311\u6218\uff0c\u5df2\u7136\u5403\u4e86\u4e00\u60ca\uff0c\u8fd9\u65f6\u89c1\u90a3\u7ae5\u5b50\u5947\u8bf4\u51fa\u4e24\u4eba\u52a8\u624b\uff0c\u4ee5\u4e94\u5341\u62db\u4e3a\u9650\uff0c\u65b9\u96ea\u5b9c\u5c45\u7136\u9762\u9732\u4e0d\u5feb\uff0c\u8fd8\u4f3c\u5acc\u4ed6\u8bf4\u5f97\u592a\u5c11\u4e4b\u610f\uff0c\u4e0d\u7531\u5f97\u5927\u4e3a\u60ca\u5fc3\u3002 \u3000\u3000\u5165\u4e91\u5927\u5e08\u767d\u7709\u7565\u76b1\uff0c\u6b63\u5f85\u8bf4\u8bdd\uff0c\u8033\u4e2d\u53ea\u542c\u5f97\u5b8b\u5b5a\u5927\u58f0\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u4f60\u4e0d\u7528\u62c5\u5fc3\uff01 \u3000\u3000\u65b9\u8001\u5f1f\u4e0d\u4f1a\u8f93\u7684\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u54fc\u4e86\u4e00\u58f0\u9041\uff1b\u201c\u5b8b\u5b5a\uff0c\u4f60\u6562\u4f5c\u4fdd\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u6709\u4f55\u4e0d\u6562\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u63a5\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u4f60\u4e0d\u7528\u4f5c\u4fdd\uff0c\u53ea\u662f\u6709\u52b3\u4f60\u4ee3\u4e3a\u8bb0\u4e00\u4e0b\u52a8\u624b\u7684\u62db\u6570\u4fbf\u662f\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u7406\u5e94\u6548\u52b3\u2026\u2026\u201d \u3000\u3000\u7ae5\u5b50\u5947\u5ffd\u7136\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u5b8b\u5b5a\uff0c\u6709\u4e00\u4ef6\u4e8b\uff0c\u4f60\u53ef\u5f97\u7262\u7262\u8bb0\u4e0b\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u53ef\u662f\u6015\u5b8b\u67d0\u8bb0\u9519\u4e86\u62db\u6570\u5417\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u5b8b\u5b5a\uff0c\u8fd9\u5c0f\u5b50\u5982\u679c\u843d\u8d25\uff0c\u8001\u592b\u53ea\u6015\u4e5f\u9976\u4e0d\u4e86\u4f60\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u6545\u610f\u4e00\u5446\uff0c\u6478\u4e86\u6478\u8116\u5b50\uff0c\u7b11\u9053\uff1a\u201c\u83ab\u975e\u4f60\u7ae5\u5144\u5bf9\u5b8b\u67d0\u7684\u4eba\u5934\uff0c\u6709\u4e86\u5174\u8da3\u5417\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8001\u592b\u53eb\u4ed6\u4eec\u9972\u517b\u4e86\u51e0\u5934\u85cf\u72ac\uff0c\u9601\u4e0b\u7684\u4eba\u5934\uff0c\u5927\u6982\u53ef\u4ee5\u8ba9\u5b83\u4eec\u9971\u9910\u4e00\u987f\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u4f38\u4e86\u4f38\u820c\u5934\uff0c\u5411\u970d\u545c\u98ce\u7b11\u9053\uff1a\u201c\u970d\u5e2e\u4e3b\uff0c\u770b\u6765\u8981\u518d\u52b3\u5e2e\u4e3b\u7528\u90a3\u6253\u72d7\u68d2\u5b50\uff01\u201d \u3000\u3000\u970d\u9e23\u51e4\u7b11\u9053\uff1a\u201c\u8001\u673d\u968f\u65f6\u5019\u547d\u5982\u4f55\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u591a\u8c22\u5e2e\u4e3b\u52a9\u6211\u6740\u90a3\u6076\u72ac\u4e86\u2026\u2026\u201d \u3000\u3000\u4e24\u4eba\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u65b9\u96ea\u5b9c\u5df2\u4eae\u51fa\u4e86\u5b9d\u5251\u3002 \u3000\u3000\u7ae5\u5b50\u5947\u4e24\u53ea\u5c0f\u8ddf\u4e00\u77aa\uff0c\u76ef\u7740\u65b9\u96ea\u5b9c\u624b\u4e2d\u7684\u5b9d\u5251\uff0c\u8138\u4e0a\u4f3c\u662f\u6709\u7740\u60ca\u5947\u4e4b\u8272\uff0c\u4e0d\u7981\u8131\u53e3\u95ee\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u4f60\u8fd9\u53ea\u5b9d\u5251\u662f\u6253\u54ea\u513f\u6765\u7684\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6697\u9053\uff1a\u770b\u6765\u5e08\u7236\u8fd9\u67c4\u957f\u5251\uff0c\u5728\u4ed6\u7684\u5fc3\uff0c\u6709\u7740\u5f88\u6df1\u7684\u5370\u8c61\u4e86\u2026\u2026 \u3000\u3000\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u533a\u533a\u8fd9\u53ea\u5b9d\u5251\uff0c\u4e43\u662f\u4e00\u4f4d\u524d\u8f88\u76f8\u8d60\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u6025\u6025\u63a5\u9053\uff1a\u201c\u53ef\u662f\u9648\u5e0c\u6b63\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fae\u5fae\u4e00\u7b11\uff0c\u770b\u4e86\u5b9d\u5251\u4e00\u773c\uff0c\u63a5\u9053\uff1a\u201c\u8fd9\u4e2a\u5417\uff1f\u4e14\u7b49\u5c0a\u9a7e\u5728\u4e94\u5341\u62db\u4e4b\u5185\uff0c\u80dc\u4e86\u533a\u533a\uff0c\u90a3\u65f6\u81ea\u5f53\u76f8\u544a\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u6014\u4e86\u4e00\u6014\uff0c\u4f46\u4ed6\u4e00\u60f3\u5230\u81ea\u5df1\u5feb\u4e9b\u5c06\u8fd9\u4e2a\u5e74\u8f7b\u4eba\u51fb\u8d25\uff0c\u5373\u53ef\u77e5\u6653\u4e00\u5207\uff0c\u4fbf\u4e5f\u4e0d\u518d\u8feb\u95ee\uff0c\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u597d\uff0c\u8001\u592b\u5c31\u7a7a\u624b\u8bd5\u8bd5\u4f60\u7684\u5251\u6cd5\uff01\u5c0f\u5b50\uff0c\u5feb\u4e9b\u51fa\u624b\u5427\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u5c0a\u9a7e\u5982\u6b64\u81ea\u5927\uff0c\u533a\u533a\u53ea\u597d\u6709\u50ed\u4e86\uff01\u201d \u3000\u3000\u5530\u7684\u4e00\u5251\u523a\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u7ae5\u5b50\u5947\u53f3\u624b\u4e00\u7ffb\uff0c\u4e94\u6307\u7b95\u5f20\uff0c\u76f4\u5411\u65b9\u96ea\u5b9c\u5251\u8eab\u6293\u6765\u3002 \u3000\u3000\u8fd9\u4e00\u624b\u7a7a\u624b\u593a\u767d\u5203\u7684\u624b\u6cd5\uff0c\u5341\u5206\u5947\u5999\uff0c\u65b9\u96ea\u5b9c\u867d\u7136\u5e76\u672a\u9732\u51fa\u5251\u795e\u4f20\u6388\u7684\u5251\u6cd5\uff0c\u4f46\u4ed6\u8fd9\u4e00\u62db\uff0c\u4e5f\u4e0d\u662f\u5e73\u5e38\u7684\u5251\u6cd5\uff0c\u800c\u7ae5\u5b50\u5947\u4e00\u6293\u4e4b\u4e0b\uff0c\u7adf\u662f\u51e0\u4e4e\u5c06\u4ed6\u7684\u5251\u8eab\u6293\u4f4f\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5fae\u5fae\u4e00\u60ca\uff0c\u6697\u9053\uff1a\u8fd9\u59d3\u7ae5\u7684\u6bd4\u90a3\u67f3\u5a9a\u5a18\u4f3c\u4e4e\u9ad8\u660e\u4e0d\u5c11\uff01 \u3000\u3000\u8f6c\u5ff5\u95f4\uff0c\u5df2\u7136\u64a4\u56de\u4e86\u957f\u5251\u3002 \u3000\u3000\u7ae5\u5b50\u5947\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u5982\u662f\u4f60\u53ea\u4f1a\u8fd9\u4e48\u51e0\u62db\u5251\u6cd5\uff0c\u8001\u592b\u529d\u4f60\u8fd8\u662f\u4e0d\u8981\u518d\u6bd4\u4e0b\u53bb\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u51b7\u7136\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u4f11\u8bf4\u4e8c\u767e\u62db\uff0c\u5341\u62db\u4e0d\u5230\uff0c\u4f60\u5c31\u8981\u6492\u624b\u5f03\u5251\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u533a\u533a\u7684\u770b\u6cd5\u4e0d\u7136\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u9645\uff0c\u957f\u5251\u4e00\u6446\uff0c\u5e73\u80f8\u523a\u51fa\u3002 \u3000\u3000\u91cd\u5b50\u5947\u8fd9\u56de\u5012\u672a\u6293\u5251\uff0c\u800c\u662f\u66f2\u6307\u5411\u7740\u5251\u8eab\u5f39\u6765\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5251\u52bf\u53bb\u7684\u672c\u662f\u6781\u6162\uff0c\u800c\u4e14\u4e0d\u89c1\u4e1d\u6beb\u5947\u7279\u4e4b\u5904\uff0c\u7ae5\u5b50\u5947\u66f2\u6307\u5f39\u5251\uff0c\u53ef\u8bf4\u6b63\u662f\u6070\u5230\u597d\u5904\uff0c\u5018\u82e5\u65b9\u96ea\u5b9c\u4e2d\u9014\u4e0d\u53d8\u62db\u5f0f\uff0c\u51c6\u8981\u88ab\u7ae5\u5b50\u5947\u5f39\u4e2a\u6b63\u7740\u3002 \u3000\u3000\u773c\u89c1\u5251\u6307\u5373\u5c06\u76f8\u89e6\u7684\u5239\u90a3\uff0c\u65b9\u96ea\u5b9c\u5ffd\u7136\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u624b\u8155\u4e00\u6c89\uff0c\u957f\u5251\u5ffd\u5730\u53d8\u62db\u5411\u4e0a\uff0c\u95ea\u7535\u822c\u76f4\u62b5\u7ae5\u5b50\u5947\u7684\u90a3\u5f20\u5927\u5634\u800c\u53bb\uff01 \u3000\u3000\u7ae5\u5b50\u5947\u4f3c\u662f\u5fae\u89c9\u610f\u5916\u5730\u4e00\u6014\uff0c\u53f3\u624b\u539f\u5f0f\u4e0d\u53d8\uff0c\u4e5f\u6781\u5feb\u5730\u5411\u4e0a\u4e00\u6325\u3002 \u3000\u3000\u5982\u4ed6\u6240\u60f3\uff0c\u8fd9\u4e00\u6325\u4e4b\u4e0b\uff0c\u5b9a\u7136\u4f1a\u5c06\u90a3\u65b9\u96ea\u5b9c\u7684\u957f\u5251\u9707\u8131\u4e86\u53f3\u624b\uff01 \u3000\u3000\u4f46\u4e8b\u5b9e\u4e0a\u5374\u975e\u5982\u6b64\uff0c\u90a3\u65b9\u96ea\u5b9c\u624b\u4e2d\u7684\u957f\u5251\uff0c\u4e0d\u4f46\u672a\u66fe\u8131\u624b\uff0c\u53cd\u800c\u5bd2\u5149\u4e00\u95ea\uff0c\u987a\u7740\u91cd\u5b50\u5947\u7684\u624b\u52bf\uff0c\u62d0\u4e86\u4e00\u62d0\uff0c\u524a\u5411\u4ed6\u7684\u8098\u90e8\u800c\u6765\u3002 \u3000\u3000\u7ae5\u5b50\u5947\u5927\u53eb\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u597d\u5251\u6cd5\uff01\u201d \u3000\u3000\u6a2a\u79fb\u4e09\u6b65\uff0c\u907f\u5f00\u4e86\u788e\u65f6\u4e4b\u5371\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5374\u662f\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u7ae5\u5b50\u5947\uff0c\u4f60\u5c0f\u5fc3\u4e9b\uff0c\u533a\u533a\u7684\u5b9d\u5251\u5f88\u5229\u554a\uff01\u201d \u3000\u3000\u5237\u2026\u2026\u5237\u2026\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\uff0c\u77ac\u606f\u4e4b\u95f4\uff0c\u53c8\u653b\u51fa\u4e86\u4e09\u5251\u3002 \u3000\u3000\u8fd9\u4e00\u56de\u7ae5\u5b50\u5947\u53ef\u4e0d\u6562\u7a0d\u5b58\u8f7b\u654c\u4e4b\u5ff5\u4e86\uff01 \u3000\u3000\u6562\u60c5\u9002\u624d\u90a3\u4e00\u62db\u5251\u6cd5\uff0c\u5bf9\u4ed6\u6709\u7740\u4f3c\u66fe\u76f8\u8bc6\u4e4b\u611f\u3002\u82e5\u975e\u65b9\u96ea\u5b9c\u8fc5\u5feb\u7684\u53c8\u653b\u51fa\u4e09\u5251\uff0c\u4ed6\u5b9a\u7136\u53ef\u4ee5\u6709\u65f6\u95f4\u60f3\u51fa\u90a3\u4e00\u62db\u5251\u6cd5\uff0c\u6b63\u662f\u5251\u795e\u7684\u8def\u6570\u3002 \u3000\u3000\u4e24\u4eba\u4ea4\u624b\u5341\u56db\u4e94\u62db\uff0c\u7ae5\u5b50\u5947\u5df2\u7136\u6709\u4e9b\u5e94\u4ed8\u4e0d\u4e86\uff0c\u4f46\u4ed6\u8eab\u4e3a\u4e94\u5927\u9b54\u4e3b\u4e4b\u4e00\uff0c\u5927\u8bdd\u5df2\u7ecf\u51fa\u53e3\uff0c\u6b64\u523b\u5373\u4f7f\u8981\u4ed6\u53d6\u51fa\u5175\u5203\uff0c\u53ea\u6015\u4ed6\u4e5f\u6709\u4e9b\u4e0d\u613f\u3002 \u3000\u3000\u582a\u582a\u53c8\u6253\u4e86\u4e03\u516b\u62db\uff0c\u7ae5\u5b50\u5947\u5ffd\u7136\u6293\u4f4f\u4e00\u4e2a\u95f4\u4e0d\u5bb9\u53d1\u7684\u673a\u4f1a\uff0c\u53cc\u638c\u5e76\u51fa\uff0c\u731b\u653b\u4e86\u4e94\u5f0f\uff0c\u5927\u559d\u4e00\u58f0\u9053\uff1a\u201c\u4f4f\u624b\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u58f0\u6014\u4e86\u4e00\u6014\uff0c\u7acb\u5373\u6536\u5251\u8dc3\u9000\uff0c\u7a81\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4f60\u53ef\u8ba4\u8f93\u4e86\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u51b7\u51b7\u5730\u7741\u5927\u4e86\u5c0f\u773c\uff0c\u9053\uff1a\u201c\u4f60\u662f\u5251\u795e\u9648\u5927\u4fa0\u7684\u5f1f\u5b50\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u4e86\u4e00\u5446\uff0c\u6697\u9053\uff1a\u4ed6\u4e3a\u4f55\u4e00\u5b9a\u8981\u67e5\u660e\u6211\u662f\u4e0d\u662f\u5251\u795e\u5f1f\u5b50\uff0c\u5b9a\u7136\u6709\u7740\u4ec0\u4e48\u7f18\u6545\u2026\u2026 \u3000\u3000\u5bfb\u601d\u95f4\uff0c\u5e94\u58f0\u63a5\u9053\uff1a\u201c\u662f\u53c8\u5982\u4f55\uff1f\u4e0d\u662f\u53c8\u5982\u4f55\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u77a7\u4f60\u7684\u5251\u6cd5\uff0c\u6b63\u662f\u5251\u795e\u9648\u5927\u4fa0\u7684\u7edd\u5b66\u9f99\u884c\u516b\u5251\uff0c\u5982\u679c\u4f60\u4e0d\u662f\u9648\u5927\u4fa0\u4f20\u4eba\uff0c\u53c8\u600e\u4f1a\u5b66\u5230\u4e86\u9648\u5927\u4fa0\u7684\u5251\u6cd5\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u767e\u62db\u672a\u6ee1\uff0c\u5c0a\u9a7e\u4e5f\u672a\u80dc\u5f97\u4e86\u533a\u533a\uff0c\u5728\u4e0b\u5982\u662f\u4e0d\u8bf4\uff0c\u90a3\u4e5f\u4e0d\u7b97\u8fdd\u53cd\u5f7c\u6b64\u7684\u7ea6\u5b9a\u4e86\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u5ffd\u7136\u54a7\u5634\u54ac\u7259\u9053\uff1a\u201c\u59d3\u65b9\u7684\uff0c\u6709\u4e00\u4ef6\u4e8b\uff0c\u4f60\u5e94\u8be5\u660e\u767d\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u5251\u795e\u7684\u5a01\u671b\uff0c\u4e0d\u5bb9\u4ed6\u540e\u4eba\u6bc1\u5760\uff0c\u9601\u4e0b\u5982\u662f\u5251\u795e\u5f1f\u5b50\uff0c\u4f60\u5982\u662f\u5728\u4eba\u524d\u4e0d\u6562\u627f\u8ba4\uff0c\u53ea\u6015\u9601\u4e0b\u5c31\u6709\u4e9b\u6127\u5bf9\u4ee4\u5e08\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u5fd6\uff0c\u6014\u9053\uff1a\u201c\u4e0d\u9519\u554a\uff01\u867d\u7136\u4ed6\u662f\u7528\u7684\u6fc0\u5c06\u4e4b\u6cd5\uff0c\u8981\u6211\u8868\u660e\u8eab\u4efd\uff0c\u4f46\u5e08\u7236\u7684\u5a01\u540d\uff0c\u679c\u7136\u662f\u4e0d\u5bb9\u6bc1\u9668\u2026\u2026\u201d \u3000\u3000\u8f6c\u5ff5\u81f3\u6b64\uff0c\u4e0d\u7981\u6c89\u58f0\u9053\uff1a\u201c\u65b9\u67d0\u6b63\u662f\u5251\u795e\u4f20\u4eba\uff0c\u5c0a\u9a7e\u4e0d\u77e5\u6709\u4f55\u6253\u7b97\uff1f\u201d \u3000\u3000\u4ed6\u7ec8\u4e8e\u627f\u8ba4\u4e86\uff01 \u3000\u3000\u7ae5\u5b50\u5947\u6709\u4e9b\u610f\u5916\uff0c\u4f46\u53ea\u662f\u7a0d\u4e00\u8fdf\u7591\uff0c\u4fbf\u5373\u626c\u58f0\u5927\u7b11\u9053\uff1a\u201c\u4f60\u679c\u7136\u662f\u5251\u795e\u7684\u5f1f\u5b50\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5c0a\u9a7e\u8fd9\u4e0b\u603b\u8be5\u5fc3\u6ee1\u610f\u8db3\u4e86\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u5ffd\u7136\u5446\u5446\u5730\u770b\u7740\u65b9\u96ea\u5b9c\u597d\u4e45\uff0c\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u9648\u5927\u4fa0\u5f53\u771f\u662f\u65e0\u6240\u4e0d\u901a\uff0c\u7ec8\u4e8e\u8c03\u6559\u51fa\u4f60\u8fd9\u7b49\u4f20\u4eba\u51fa\u6765\u4e86\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u4e0d\u7981\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6697\u9053\uff1a\u201c\u542c\u4ed6\u8bdd\u4e2d\u4e4b\u610f\uff0c\u597d\u8c61\u4ed6\u4eec\u4e0d\u4fe1\u5e08\u7236\u80fd\u627e\u5f97\u5230\u4f20\u4eba\u4f3c\u7684\u2026\u2026\u201d \u3000\u3000\u4f46\u4ed6\u53e3\u4e2d\u5374\u63a5\u9053\uff1a\u201c\u5e08\u7236\u88ab\u6b66\u6797\u4e2d\u8a89\u4e3a\u5251\u795e\uff0c\u53ea\u53ef\u60dc\u533a\u533a\u624d\u80fd\u6709\u9650\uff0c\u96be\u5f97\u5e08\u7236\u6240\u4f20\u5341\u5206\u4e4b\u4e00\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u5927\u8111\u888b\u6643\u4e86\u4e24\u4e0b\uff0c\u5927\u58f0\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u53ef\u80fd\uff1f\u5929\u9b54\u5973\u600e\u5730\u5982\u6b64\u611a\u7b28\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u6014\u9053\uff1a\u201c\u8fd9\u4e0e\u5929\u9b54\u5973\u4f55\u5173\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u600e\u5730\u65e0\u5173\uff1f\u201d \u3000\u3000\u4ed6\u8bed\u97f3\u987f\u4e00\u987f\uff0c\u76b1\u8d77\u53cc\u7709\uff0c\u6c89\u541f\u9053\uff1a\u201c\u8bb0\u5f97\u4e0a\u6b21\u89c1\u8fc7\u4ee4\u5e08\u4e4b\u540e\uff0c\u54b1\u4eec\u4e94\u4eba\u66fe\u7ecf\u6709\u8fc7\u4e00\u6b21\u805a\u4f1a\uff0c\u636e\u90a3\u6bd2\u5927\u592b\u548c\u5927\u9b54\u5973\u6240\u8bf4\uff0c\u5251\u795e\u9648\u5927\u4fa0\u5df2\u7136\u8eab\u67d3\u91cd\u75c5\uff0c\u5fc5\u5c06\u4e0d\u4e45\u4e8e\u4eba\u4e16\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4f60\u662f\u5728\u5492\u9a82\u6211\u5e08\u7236\u5417\uff1f\u201d \u3000\u3000\u7ae5\u4e8e\u5947\u9053\uff1a\u201c\u6076\u5b54\u660e\u533b\u9053\u6781\u7cbe\uff0c\u5f53\u65e5\u5728\u90a3\u91d1\u9876\u5cf0\u5934\uff0c\u66fe\u66ff\u4ee4\u5e08\u628a\u8fc7\u4e86\u8109\uff0c\u6076\u5b54\u660e\u8bf4\uff0c\u6b63\u5982\u5929\u9b54\u5973\u6240\u6599\uff0c\u4ee4\u5e08\u4e5f\u5df2\u75c5\u5165\u818f\u76f2\uff0c\u51b3\u96be\u6d3b\u8fc7\u4e00\u5e74\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u80e1\u8bf4\uff0c\u5018\u662f\u6211\u5e08\u7236\u6d3b\u4e0d\u8fc7\u4e00\u5e74\uff0c\u53c8\u600e\u80fd\u4f20\u6388\u6211\u7684\u6b66\u529f\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u4e0d\u9519\u554a\uff01\u8fd9\u6b63\u662f\u53eb\u8001\u592b\u4e0d\u89e3\u4e4b\u5904\uff0c\u6309\u7406\uff0c\u6076\u5b54\u660e\u5e94\u662f\u4e0d\u66fe\u770b\u8d70\u4e86\u773c\uff0c\u4f55\u51b5\uff0c\u4ee4\u5e08\u5e76\u672a\u5f97\u5230\u91d1\u9ca4\u80c6\uff0c\u60f3\u8981\u5eb7\u590d\uff0c\u90a3\u662f\u6839\u672c\u4e0d\u53ef\u80fd\u4e4b\u4e8b\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e00\u6d3e\u80e1\u8a00\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u51b7\u51b7\u5730\u770b\u4e86\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u9053\uff1a\u201c\u65b9\u8001\u5f1f\uff0c\u8001\u592b\u95ee\u4f60\u4e00\u53e5\u6d3b\uff0c\u5e0c\u671b\u4f60\u80fd\u7167\u5b9e\u544a\u8bc9\u8001\u592b\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5c0a\u9a7e\u8bf7\u8bb2\uff01\u533a\u533a\u80fd\u591f\u56de\u7b54\uff0c\u81ea\u7136\u4f1a\u7167\u5b9e\u56de\u7b54\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u4ee4\u5e08\u73b0\u5728\u4f55\u5904\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u909b\u5d03\u5c71\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u4ee4\u5e08\u8fd8\u5728\u4eba\u4e16\u5427\uff1f\u4ed6\u7684\u8eab\u4f53\u5982\u4f55\uff1f\u201d \u3000\u3000\u65b9\u96ea\u76f4\u5ffd\u7136\u9762\u5bb9\u4e00\u9eef\uff0c\u6c89\u541f\u4e86\u534a\u664c\uff0c\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u65b9\u8001\u5f1f\uff0c\u4f60\u6c89\u541f\u4e0d\u8bed\uff0c\u53ef\u662f\u5251\u795e\u9648\u5927\u4fa0\u5df2\u7136\u4ed9\u901d\u4e86\uff1f\u201d \u3000\u3000\u4ed6\u4e00\u8fde\u4e24\u6b21\u79f0\u547c\u4ed6\u4e3a\u8001\u5f1f\uff0c\u663e\u7136\u5df2\u662f\u5b8c\u5168\u627f\u8ba4\u4e86\u65b9\u96ea\u5b9c\u7684\u8eab\u4efd\uff0c\u76f8\u4fe1\u4e86\u4ed6\u662f\u90a3\u5251\u795e\u9648\u5927\u4fa0\u7684\u4f20\u4eba\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u957f\u957f\u5730\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u6447\u5934\u9053\uff1a\u201c\u7ae5\u4e8e\u5947\uff0c\u53ea\u6709\u6b64\u4e8b\u6055\u533a\u533a\u65e0\u53ef\u5949\u544a\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u6123\u4e86\u4e00\u6123\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u613f\u8bf4\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u65e0\u53ef\u5949\u544a\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u84e6\u5730\u4ef0\u5927\u957f\u7b11\u9053\uff1a\u201c\u8001\u592b\u660e\u767d\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4f60\u660e\u767d\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u9648\u5927\u4fa0\u5df2\u7136\u6492\u624b\u5f52\u5929\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9eef\u7136\u4f4e\u5934\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u533a\u533a\u4f9d\u7136\u662f\u65e0\u53ef\u5949\u544a\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u8fd9\u4e09\u53e5\u65e0\u53ef\u5949\u544a\uff0c\u4f7f\u8001\u592b\u5b8c\u5168\u660e\u767d\uff0c\u4ee4\u5e08\u9648\u5927\u4fa0\u5b9a\u7136\u9a7e\u8fd4\u9053\u5c71\u4e86\u3002\u201d \u3000\u3000\u8bed\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u8001\u592b\u8fd8\u6709\u4e00\u4ef6\u4e8b\uff0c\u5fd8\u8bb0\u544a\u8bc9\u4f60\u8001\u5f1f\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u8001\u592b\u7b49\u4e0e\u4f60\u867d\u7136\u4e0d\u8bc6\uff0c\u4f46\u5bf9\u9648\u5927\u4fa0\u7684\u4f20\u4eba\uff0c\u5374\u4e0d\u80fd\u4e0d\u8bc6\uff1b\u53ea\u56e0\u5341\u5e74\u524d\u54b1\u4eec\u4e94\u4eba\u4e00\u81f4\u534f\u8bae\uff0c\u6b66\u6797\u4e4b\u4e2d\uff0c\u53ea\u80fd\u6709\u4e00\u4e2a\u9648\u5927\u4fa0\uff0c\u51b3\u4e0d\u8bb8\u6709\u7b2c\u4e8c\u4e2a\u9648\u5927\u4fa0\u51fa\u73b0\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u5fc3\u4e2d\u4e00\u9707\uff0c\u6697\u9053\uff1a\u201c\u96be\u9053\u4ed6\u4eec\u65e9\u5c31\u60f3\u5230\u5bf9\u4ed8\u4e8e\u6211\u4e86\uff1f\u201d \u3000\u3000\u4f46\u4ed6\u7ee7\u800c\u4e00\u60f3\uff0c\u53c8\u89c9\u5f97\u4e0d\u5bf9\uff0c\u5e08\u7236\u5c06\u6211\u4f5c\u4e3a\u5f1f\u5b50\uff0c\u81f3\u4eca\u4e5f\u4e0d\u8fc7\u56db\u5e74\u591a\uff0c\u4ed6\u4eec\u600e\u4f1a\u65e9\u5728\u5341\u5e74\u524d\uff0c\u5c31\u6709\u534f\u8bae\u6765\u5bf9\u4ed8\u6211\u5462\uff1f\u201d \u3000\u3000\u4e00\u65f6\u95f4\uff0c\u4ed6\u4e0d\u7981\u6c89\u601d\u4e0d\u8bed\u3002 \u3000\u3000\u7ae5\u5b50\u5947\u51b7\u7b11\u4e86\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4f60\u8001\u5f1f\u53ef\u662f\u6709\u4e9b\u4e0d\u4fe1\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7f13\u7f13\u5730\u62ac\u5934\uff1b\u9053\uff1a\u201c\u6b63\u662f\u6709\u4e9b\u4e0d\u4fe1\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u5982\u662f\u8001\u592b\u8bf4\u660e\u5176\u4e2d\u9053\u7406\uff0c\u4f60\u8001\u5f1f\u5c31\u4e0d\u4f1a\u4e0d\u4fe1\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5341\u5e74\u4e4b\u524d\uff0c\u533a\u533a\u5c1a\u672a\u6295\u5165\u5e08\u95e8\uff0c\u4f60\u53c8\u600e\u80fd\u4e8b\u5148\u5c31\u6709\u4e86\u534f\u8bae\uff0c\u6765\u5bf9\u4ed8\u65b9\u67d0\uff1f\u5c0a\u9a7e\u7eb5\u7136\u6709\u4ec0\u4e48\u9053\u7406\u53ef\u8bf4\uff0c\u90a3\u4e5f\u4e0d\u89c1\u5f97\u80fd\u4ee4\u6211\u76f8\u4fe1\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u5927\u7b11\u9053\uff1a\u201c\u65b9\u8001\u5f1f\uff0c\u8001\u592b\u8bf4\u4e86\u51fa\u6765\uff0c\u4f60\u81ea\u7136\u5c31\u4f1a\u76f8\u4fe1\u4e86\uff01\u201d \u3000\u3000\u8bed\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5f53\u5e74\u8001\u592b\u7b49\u4e94\u4eba\u534f\u8bae\u4e4b\u65f6\uff0c\u4f60\u679c\u7136\u5c1a\u672a\u6295\u5165\u9648\u5e0c\u6b63\u5927\u4fa0\u7684\u95e8\u4e0b\uff0c\u4f46\u54b1\u4eec\u6614\u65e5\u8ba1\u8bae\u4e4b\u65f6\uff0c\u5374\u5e76\u672a\u77e5\u6653\u90a3\u4f1a\u662f\u4f55\u4eba\uff0c\u53cd\u6b63\u54b1\u4eec\u51b3\u5b9a\u8fc7\uff0c\u9648\u5927\u4fa0\u4e00\u6709\u8863\u94b5\u5f1f\u5b50\uff0c\u54b1\u4eec\u5c31\u5c06\u4e0d\u62e9\u624b\u6bb5\u5730\u5bf9\u4ed8\u4e8e\u4ed6\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4f60\u4eec\u7684\u8ba1\u8bae\uff0c\u4e00\u5b9a\u662f\u5341\u5206\u6076\u6bd2\u7684\u4e86\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u5f53\u5e74\u54b1\u4eec\u8ba1\u8bae\u4e4b\u65f6\uff0c\u9648\u5927\u4fa0\u5c1a\u5728\u4eba\u4e16\uff0c\u5982\u4eca\u4e48\uff0c\u9648\u5927\u4fa0\u4e5f\u5df2\u4ed9\u53bb\uff0c\u5408\u54b1\u4eec\u4e94\u4eba\u4e4b\u529b\uff0c\u5bf9\u4ed8\u4e8e\u4f60\uff0c\u5e94\u662f\u7ef0\u7ef0\u6709\u4f59\u7684\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u624d\u604d\u7136\u4e00\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5c0a\u9a7e\u8fd9\u7b49\u60f3\u6cd5\uff0c\u5012\u4e5f\u6b63\u662f\u4eba\u60c5\u4e4b\u5e38\u2026\u2026\u201d \u3000\u3000\u4ed6\u5fae\u5fae\u4e00\u987f\u8bdd\u97f3\uff0c\u6447\u4e86\u6447\u5934\uff0c\u4e00\u632f\u624b\u4e2d\u5b9d\u5251\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u4f46\u533a\u533a\u5374\u8981\u544a\u8bc9\u5c0a\u9a7e\uff0c\u65b9\u67d0\u624b\u4e2d\u8fd9\u652f\u957f\u5251\uff0c\u6b63\u662f\u6069\u5e08\u6614\u65e5\u626c\u5a01\u6b66\u6797\u7684\u795e\u5668\uff0c\u4f60\u4eec\u5018\u82e5\u8ba4\u4e3a\u5bf9\u4ed8\u65b9\u67d0\u7ef0\u7ef0\u6709\u4f59\uff0c\u4e00\u65e6\u6210\u4e86\u5251\u4e0b\u4e4b\u9b3c\uff0c\u90a3\u53ef\u522b\u602a\u65b9\u67d0\u5e74\u8f7b\u6c14\u76db\uff0c\u6bd4\u4e0d\u5f97\u5bb6\u5e08\u90a3\u7b49\u4ec1\u6148\u4e86\uff01\u201d \u3000\u3000\u663e\u7136\uff0c\u4ed6\u8fd9\u51e1\u53e5\u8bdd\u4e2d\u7684\u542b\u610f\uff0c\u662f\u8981\u7ae5\u5b50\u5947\u660e\u767d\uff0c\u4e00\u65e6\u52a8\u8d77\u624b\u6765\uff0c\u81ea\u5df1\u4e0d\u4f1a\u8f7b\u6613\u5730\u653e\u8fc7\u4e86\u4ed6\u4eec\uff01 \u3000\u3000\u7ae5\u5b50\u5947\u542c\u5f97\u8138\u8272\u5fae\u5fae\u4e00\u53d8\uff0c\u5927\u5634\u4e00\u5f20\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u6bd4\u4ee4\u5e08\u7684\u8c26\u865a\uff0c\u5dee\u4e86\u5f88\u8fdc\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u672c\u6765\u5982\u6b64\uff01\u201d \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u4ed6\u8138\u8272\u4e00\u6c89\uff0c\u559d\u9053\uff1a\u201c\u7ae5\u5b50\u5947\uff0c\u90a3\u6bd2\u5927\u592b\u548c\u5929\u9b54\u5973\u7b49\u4eba\uff0c\u73b0\u5728\u4f55\u5904\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u6447\u6643\u5927\u8111\u888b\uff0c\u9053\uff1a\u201c\u4e0d\u77e5\u9053\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4f3c\u662f\u60f3\u4e0d\u5230\u4ed6\u4f1a\u56de\u7b54\u5f97\u5982\u6b64\u5e72\u8106\uff0c\u5fae\u5fae\u4e00\u6123\uff0c\u9053\uff1a\u201c\u4f60\u600e\u5730\u4f1a\u4e0d\u77e5\u9053\uff1f\u4f60\u4eec\u4e94\u4eba\u4e0d\u662f\u5e38\u5e38\u805a\u9996\u5417\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u542c\u5f97\u54c8\u54c8\u602a\u7b11\u9053\uff1a\u201c\u8c01\u8bf4\u7684\uff0c\u8fd9\u4e48\u770b\u6765\uff0c\u9648\u5927\u4fa0\u597d\u8c61\u6ca1\u6709\u628a\u6211\u4eec\u7684\u5e95\u7ec6\u5168\u90e8\u544a\u8bc9\u4e8e\u4f60\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u76b1\u4e86\u76b1\u7709\uff0c\u51b7\u54fc\u9053\uff1a\u201c\u50cf\u4f60\u4eec\u8fd9\u7b49\u4eba\uff0c\u5bb6\u5e08\u672c\u5c31\u4e0d\u5c51\u591a\u8c08\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u8138\u8272\u53c8\u662f\u4e00\u53d8\uff0c\u6012\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u5f53\u5e74\u9648\u5927\u4fa0\u867d\u7136\u6709\u7740\u8bdb\u6740\u6211\u7b49\u4e4b\u80fd\uff0c\u4e5f\u672a\u66fe\u5f53\u7740\u522b\u4eba\u6298\u539a\u8fc7\u8001\u592b\uff0c\u4f60\u8fd9\u822c\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u5c45\u7136\u5982\u6b64\u80c6\u5927\u5984\u4e3a\uff0c\u591a\u901e\u53e3\u820c\u4e4b\u5feb\uff0c\u5b9e\u662f\u53eb\u8001\u592b\u96be\u4ee5\u5fcd\u8010\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5251\u7709\u4e00\u626c\uff0c\u9053\uff1a\u201c\u5c0a\u9a7e\u5982\u662f\u89c9\u5f97\u4e0d\u987a\u8033\uff0c\u90a3\u5c31\u4e0d\u7528\u542c\u4e86\uff01\u201d \u3000\u3000\u4e00\u6296\u624b\u4e2d\u5b9d\u5251\uff0c\u63a5\u9053\uff1a\u201c\u4f46\u4f60\u5982\u60f3\u4e0d\u542c\uff0c\u5374\u4e5f\u4e0d\u6613\uff0c\u81f3\u5c11\u5148\u5f97\u80dc\u8fc7\u533a\u533a\u8fd9\u652f\u5b9d\u5251\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u5927\u5634\u54a7\u4e86\u4e00\u54a7\uff0c\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4f60\u8fd8\u60f3\u6253\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4f60\u5982\u80c6\u602f\u4e86\uff0c\u90a3\u5c31\u7b97\u4e86\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u5982\u4f55\u53d7\u5f97\u4e86\u4ed6\u8fd9\u7b49\u8baa\u7b11\uff0c\u987f\u65f6\u5927\u6012\u9053\uff1a\u201c\u8001\u592b\u77a7\u4f60\u662f\u9648\u5927\u4fa0\u7684\u5f1f\u5b50\uff0c\u624d\u4f1a\u5bf9\u4f60\u8fd9\u822c\u5ba2\u6c14\uff0c\u987b\u77e5\u8001\u592b\u867d\u7136\u65e9\u6709\u6740\u4f60\u4e4b\u5fc3\uff0c\u4f46\u672a\u6740\u4f60\u4e4b\u524d\uff0c\u5374\u662f\u4e0d\u60f3\u8868\u73b0\u5f97\u90a3\u822c\u5c0f\u6c14\uff0c\u4f60\u5c45\u7136\u5982\u6b64\u4e0d\u77e5\u81ea\u91cf\uff0c\u8001\u592b\u8bf4\u4e0d\u5f97\u53ea\u597d\u53eb\u4f60\u5c1d\u5c1d\u6b7b\u7684\u6ecb\u5473\u4e86\uff01\u201d \u3000\u3000\u7a81\u7136\u4ef0\u5929\u53d1\u51fa\u4e00\u9635\u5927\u7b11\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u4ed6\u7b11\u58f0\u752b\u544a\u51fa\u53e3\uff0c\u4f20\u5165\u7f57\u6c49\u5802\u8bf8\u4eba\u4e4b\u8033\uff0c\u5ffd\u53c8\u8fc5\u5feb\u7684\u987f\u4fe1\uff0c\u4f3c\u662f\u4ed6\u7684\u7b11\u58f0\uff0c\u53ea\u662f\u5439\u4e86\u4e00\u53e3\u6c14\u822c\u7684\u4e0d\u8d39\u529b\u3002 \u3000\u3000\u4f46\u90a3\u7f57\u6c49\u5802\u4e2d\u7684\u5c4b\u74e6\uff0c\u5374\u8dcc\u843d\u4e86\u767e\u5341\u6765\u7247\u4e4b\u591a\uff0c\u4e00\u5730\u5288\u62cd\u4e4b\u58f0\uff0c\u4e0d\u7edd\u4e8e\u8033\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u6697\u5403\u4e86\u4e00\u60ca\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u9b54\u5934\u7684\u529f\u529b\uff0c\u679c\u7136\u4e0d\u540c\u51e1\u54cd\u2026\u2026\u201d \u3000\u3000\u4f46\u4ed6\u8138\u4e0a\u5374\u662f\u5341\u5206\u5e73\u9759\uff0c\u7b11\u9053\uff1a\u201c\u5c0a\u9a7e\u5982\u662f\u60f3\u7528\u8fd9\u7b49\u611a\u7b28\u7684\u65b9\u6cd5\uff0c\u5148\u58f0\u593a\u4eba\uff0c\u90a3\u53ef\u5c31\u9519\u4e86\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u6ca1\u6709\u8bf4\u8bdd\uff0c\u5374\u662f\u63a2\u624b\u81ea\u6000\u662f\u53d6\u51fa\u4e00\u4ef6\u5175\u5203\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e3e\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u4ed6\u624b\u4e2d\u7684\u8fd9\u79cd\u5175\u5203\uff0c\u5341\u5206\u5947\u7a81\uff0c\u957f\u77ed\u53ea\u6709\u5c3a\u8bb8\u5de6\u53f3\uff0c\u4f3c\u7b14\u975e\u7b14\uff0c\u4f3c\u5c3a\u975e\u5c3a\uff0c\u7c97\u5982\u513f\u81c2\u4e00\u822c\uff0c\u9ed1\u9edd\u9edd\u7684\u5149\u91c7\uff0c\u4fbf\u4eba\u4e00\u671b\u5373\u77e5\uff0c\u4e43\u662f\u7cbe\u94a2\u6253\u9020\u3002 \u3000\u3000\u7ae5\u5b50\u5947\u6325\u4e86\u6325\u90a3\u6839\u77ed\u68d2\u4e4b\u7269\uff0c\u559d\u9053\uff1a\u201c\u4f60\u53ef\u8ba4\u5f97\u8001\u592b\u624b\u4e2d\u7684\u5175\u5668\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u672a\u66fe\u542c\u5f97\u5e08\u7236\u8bf4\u8fc7\uff0c\u5f53\u7136\u4e0d\u77e5\u4ed6\u8fd9\u5175\u5668\u7684\u540d\u79f0\uff0c\u5f53\u4e0b\u6447\u5934\u4e00\u7b11\uff0c\u7167\u5b9e\u8bf4\u9053\uff1a \u3000\u3000\u201c\u533a\u533a\u773c\u76ae\u751a\u6d45\uff0c\u4f60\u8fd9\u7b49\u4f3c\u7b14\u975e\u7b14\uff0c\u4f3c\u5c3a\u975e\u5c3a\uff0c\u5b9b\u5982\u4e00\u6839\u77ed\u68d2\u7684\u5175\u5668\uff0c\u5728\u4e0b\u8fd8\u662f\u5934\u4e00\u906d\u89c1\u5230\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u5927\u5934\u9b3c\u738b\u7684\u593a\u547d\u68d2\uff0c\u4f60\u90fd\u6ca1\u6709\u542c\u8bf4\u8fc7\uff0c\u90a3\u771f\u662f\u5b64\u964b\u5be1\u95fb\u5f97\u5f88\u4e86\uff01\u201d \u3000\u3000\u4ed6\u8bdd\u97f3\u65b9\u843d\uff0c\u5b8b\u5b5a\u5df2\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u65b9\u8001\u5f1f\uff0c\u8fd9\u7ae5\u8001\u9b54\u7684\u593a\u547d\u68d2\uff0c\u4e0d\u4f46\u53ef\u4ee5\u4f38\u7f29\u81ea\u5982\uff0c\u800c\u4e14\u5c1a\u6709\u5176\u4ed6\u6b79\u6bd2\u6697\u5668\u85cf\u4e8e\u68d2\u8eab\u4e2d\u95f4\uff0c\u4f60\u5c11\u65f6\u52a8\u624b\uff0c\u53ef\u8981\u5f53\u5fc3\u4e00\u4e9b\u624d\u597d\u3002\u201d \u3000\u3000\u7ae5\u5b50\u5947\u4e24\u773c\u4e00\u7ffb\uff0c\u51b7\u54fc\u9053\uff1a\u201c\u5b8b\u5b5a\uff0c\u4f60\u5927\u6982\u662f\u6d3b\u5f97\u4e0d\u8010\u70e6\u4e86\u5427\uff01\u201d \u3000\u3000\u663e\u7136\uff0c\u5b8b\u5b5a\u53eb\u7834\u4ed6\u624b\u4e2d\u5175\u5203\u7684\u79d8\u5bc6\uff0c\u4f7f\u4ed6\u5927\u4e3a\u607c\u6012\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5374\u662f\u5fc3\u4e2d\u5927\u4e3a\u611f\u6fc0\uff0c\u7b11\u4e86\u4e00\u7b11\u9053\uff1a\u201c\u591a\u8c22\u5b8b\u8001\u2026\u2026\u201d \u3000\u3000\u84e6\u5730\u8138\u8272\u4e00\u6c89\uff0c\u5411\u7ae5\u5b50\u5947\u559d\u9053\uff1a\u201c\u5c0a\u9a7e\u83ab\u8981\u53d1\u72e0\u8fc1\u6012\uff0c\u53ea\u8981\u4f60\u80dc\u5f97\u4e86\u65b9\u67d0\u624b\u4e2d\u4e4b\u5251\uff0c\u4f60\u5927\u53ef\u7231\u600e\u4e48\u529e\u90fd\u884c\uff0c\u5426\u5219\uff0c\u533a\u533a\u62c5\u5fc3\u7684\u5374\u662f\u4f60\u81ea\u5df1\u8eab\u9996\u96be\u4fdd\u5462\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u5f53\u5e74\u8001\u592b\u867d\u7136\u4e0d\u662f\u4f60\u5e08\u7236\u9648\u5e0c\u6b63\u4e4b\u654c\uff0c\u4f46\u8001\u592b\u5982\u662f\u518d\u6597\u4e0d\u8fc7\u4ed6\u7684\u4f20\u4eba\uff0c\u90a3\u5c82\u4e0d\u662f\u767d\u6d3b\u5728\u4eba\u4e16\u4e86\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u5c0a\u9a7e\u4e0d\u7528\u4e0d\u4fe1\uff0c\u7a0d\u65f6\u54b1\u4eec\u52a8\u8fc7\u624b\u4ee5\u624b\u3002\u4f60\u5c31\u660e\u767d\u4e86\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u8fd9\u65f6\u5ffd\u7136\u53cc\u76ee\u5927\u7741\uff0c\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u6709\u4e00\u4ef6\u4e8b\uff0c\u5c11\u5728\u4f60\u5e08\u7236\u53ef\u66fe\u544a\u8bc9\u8fc7\u4f60\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u54b1\u4eec\u4e94\u4eba\u66fe\u7ecf\u51b3\u5b9a\uff0c\u4e3e\u4e16\u4e4b\u95f4\uff0c\u51b3\u4e0d\u5bb9\u8bb8\u7b2c\u4e8c\u4e2a\u5251\u795e\u51fa\u73b0\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u4f60\u8bb2\u8fc7\u4e86\uff01\u201d \u3000\u3000\u91cd\u5b50\u5947\u9053\uff1a\u201c\u90a3\u4e5f\u5c31\u662f\u8bf4\uff0c\u9648\u5927\u4fa0\u5982\u6709\u4f20\u4eba\uff0c\u90a3\u4fbf\u662f\u6211\u4eec\u5168\u529b\u8ffd\u6740\u7684\u5bf9\u8c61\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u533a\u533a\u65e9\u5df2\u731c\u60f3\u51fa\u6765\u4e86\uff0c\u5c0a\u9a7e\u5982\u6709\u6740\u6211\u4e4b\u80fd\uff0c\u4e0d\u59a8\u4e0b\u624b\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u76ee\u5149\u4e00\u8f6c\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u8001\u592b\u4e5f\u6709\u4e86\u4e00\u6869\u4e8b\uff0c\u5fc3\u4e2d\u751a\u4e3a\u4e0d\u89e3\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u5f88\u597d\uff0c\u4f60\u5982\u6709\u4ec0\u4e48\u4e0d\u89e3\u4e4b\u4e8b\uff0c\u4e0d\u59a8\u65e9\u4e9b\u8bf4\u51fa\uff0c\u5982\u662f\u4e0d\u5e78\u4e27\u8eab\u533a\u533a\u5251\u4e0b\uff0c\u90a3\u5c31\u662f\u6c38\u8fdc\u6ca1\u6709\u660e\u767d\u7684\u673a\u4f1a\u4e86\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u8fd9\u51e0\u5e74\u6765\uff0c\u54b1\u4eec\u66fe\u7ecf\u82b1\u4e86\u4e0d\u5c11\u5fc3\u8840\uff0c\u6253\u63a2\u542c\u9648\u5927\u4fa0\u9690\u8eab\u4e4b\u5904\uff0c\u7ed3\u679c\u5374\u672a\u67e5\u51fa\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5927\u7b11\u9053\uff1a\u201c\u6211\u5e08\u7236\u7684\u884c\u8e2a\uff0c\u5c82\u662f\u4f60\u4eec\u6240\u80fd\u67e5\u63a2\u5f97\u51fa\u7684\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u8001\u592b\u7b49\u4eba\u63a2\u9648\u5927\u4fa0\u4e0b\u843d\uff0c\u76ee\u7684\u5c31\u5728\u627e\u51fa\u4ed6\u662f\u5426\u6709\u4e86\u4f20\u4eba\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u533a\u533a\u4fbf\u662f\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8001\u592b\u5df2\u7ecf\u60f3\u51fa\u6765\u4e86\u2026\u2026\u4f46\u8001\u592b\u7b49\u5230\u4eca\u65e5\u59cb\u77e5\u6653\uff0c\u5b9e\u662f\u6e56\u6d82\u5f97\u5f88\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u8fdf\u554a\uff0c\u81f3\u5c11\u4f60\u5728\u6b7b\u524d\uff0c\u8fd8\u77e5\u9053\u4e86\u8fd9\u4e00\u6869\u4e8b\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u5728\u6b66\u6797\u4e4b\u4e2d\uff0c\u9648\u5e0c\u6b63\u7d20\u4ee5\u4ec1\u4e49\u6b63\u76f4\u4e3a\u4eba\u79f0\u9053\uff0c\u4f46\u8001\u592b\u7b49\u4eba\u600e\u77e5\uff0c\u4ed6\u4e5f\u4f1a\u505a\u51fa\u90a3\u7b49\u5351\u9119\u4e4b\u4e8b\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u4ed6\u53e3\u4e2d\u7adf\u7136\u8bf4\u51fa\u81ea\u5df1\u5e08\u7236\u505a\u51fa\u4e86\u4ec0\u4e48\u5351\u9119\u4e4b\u4e8b\u3002\u5fc3\u4e2d\u4e0d\u7531\u5f97\u5927\u6012\uff0c\u51b7\u51b7\u5730\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u7ae5\u5b50\u5947\uff0c\u4f60\u5634\u91cc\u653e\u5e72\u51c0\u4e9b\u3002\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u8001\u592b\u6ca1\u6709\u8bf4\u9519\uff0c\u90a3\u9648\u5e0c\u6b63\u505a\u4e86\u4e00\u6869\u51fa\u4eba\u610f\u8868\u7684\u4e8b\uff0c\u624d\u4f1a\u4f7f\u4ed6\u6709\u4e86\u4f20\u4eba\uff0c\u8001\u592b\u7b49\u4ecd\u7136\u4e0d\u77e5\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u51b7\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u51fa\u4eba\u610f\u8868\u4e4b\u4e8b\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u6613\u5bb9\u6539\u626e\uff0c\u63a9\u53bb\u4e86\u672c\u6765\u9762\u76ee\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u4e5f\u7b97\u4e0d\u5f97\u4ec0\u4e48\uff0c\u4e00\u4e2a\u4eba\u7231\u600e\u4e48\u6253\u626e\u81ea\u5df1\uff0c\u522b\u4eba\u4e5f\u65e0\u6cd5\u8fc7\u95ee\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4efb\u4f55\u4eba\u90fd\u53ef\u4ee5\u8fd9\u4e48\u505a\uff0c\u4f46\u662f\uff0c\u5251\u795e\u9648\u5927\u4fa0\u5982\u662f\u8fd9\u6837\u505a\uff0c\u90a3\u5c31\u4e0d\u88ab\u8c05\u89e3\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u5251\u795e\u9648\u5927\u4fa0\u5728\u6b66\u6797\u4e4b\u4e2d\uff0c\u53d7\u5c3d\u4e86\u4e3e\u4e16\u4eba\u7269\u7684\u656c\u4ef0\uff0c\u4ed6\u5982\u4e5f\u6613\u5bb9\u6539\u626e\uff0c\u90a3\u5c31\u5927\u9519\u7279\u9519\u7684\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u7b11\u8bdd\uff0c\u4e3a\u4ec0\u4e48\u522b\u4eba\u53ef\u4ee5\uff0c\u6211\u5e08\u7236\u5c31\u4e0d\u53ef\u4ee5\u5462\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u522b\u4eba\u53ef\u4ee5\uff0c\u56e0\u4e3a\u522b\u4eba\u4e0d\u662f\u5251\u795e\uff0c\u4e0d\u662f\u53d7\u6b66\u6797\u4e07\u4eba\u5d07\u4ef0\u7684\u4ec1\u4e49\u6b63\u76f4\u4e4b\u4eba\u3002\u201d \u3000\u3000\u4ed6\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u6b64\u523b\u603b\u8be5\u660e\u767d\uff0c\u8001\u592b\u4e3a\u4f55\u8bf4\u4f60\u5e08\u7236\u505a\u4e86\u4e00\u6869\u5351\u9119\u4e4b\u4e8b\u4e86\u5427\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u662f\u4e48\uff1f\u4f46\u4f60\u83ab\u8981\u5fd8\u8bb0\uff0c\u4e00\u4e2a\u4eba\u9047\u5230\u7d27\u6025\u4e4b\u4e8b\u65f6\uff0c\u4e5f\u53ef\u901a\u6743\u8fbe\u53d8\u554a\uff01 \u3000\u3000\u5bb6\u5e08\u4e3a\u4e86\u5bf9\u4ed8\u4f60\u4eec\u4e94\u4e2a\u9b54\u5934\uff0c\u4e0d\u60dc\u6539\u88c5\u6613\u5bb9\uff0c\u90a3\u4e5f\u4e0d\u8db3\u4e3a\u8fc7\u554a\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u653e\u5c41\uff01\u4e3a\u4ec0\u4e48\u5bf9\u4ed8\u6211\u4eec\u5c31\u4e0d\u8db3\u4e3a\u8fc7\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u90a3\u53ef\u8981\u95ee\u4f60\u4eec\u81ea\u5df1\u4e86\uff01\u201d \u3000\u3000\u7ae5\u4e88\u5947\u9053\uff1a\u201c\u8001\u592b\u54ea\u91cc\u4e0d\u5bf9\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u4eec\u4e94\u4eba\uff0c\u88ab\u6b66\u6797\u540c\u9053\u5949\u627f\u4e3a\u4e94\u5927\u9b54\u4e3b\uff0c\u8db3\u89c1\u4f60\u4eec\u53cc\u624b\u8840\u8165\uff0c\u9020\u4e86\u591a\u5c11\u7684\u6740\u5b7d\u4e86\uff01\u201d \u3000\u3000\u8bed\u95f4\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5bb6\u5e08\u4e3a\u4e86\u907f\u5f00\u4f60\u7b49\u8033\u76ee\uff0c\u4e3a\u6b66\u6797\u82cd\u751f\u9020\u798f\uff0c\u4e00\u65f6\u4e0d\u60dc\u81ea\u8d2c\u8eab\u4efd\uff0c\u6539\u88c5\u6613\u5bb9\uff0c\u4f5c\u4e86\u4eba\u5bb6\u5974\u4ec6\uff0c\u90a3\u4e5f\u4e0d\u662f\u4ec0\u4e48\u5351\u9119\u4e4b\u4e8b\u554a\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u5ffd\u7136\u62cd\u624b\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e0d\u9519\uff0c\u9648\u5927\u4fa0\u8fd9\u7b49\u4e3e\u52a8\uff0c\u4e0d\u4f46\u4e0d\u8db3\u4ee5\u73b7\u6c61\u4ed6\u90a3\u5251\u795e\u540d\u671b\uff0c\u800c\u4e14\u4e00\u65e6\u4f20\u626c\u51fa\u53bb\uff0c\u5c31\u66f4\u8981\u5f97\u5230\u6b66\u6797\u540c\u9053\u7684\u996e\u4f69\uff01\u201d \u3000\u3000\u7ae5\u5b50\u5947\u4e00\u65f6\u4e3a\u4e4b\u8bed\u585e\uff0c\u770b\u4e86\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5c31\u7b97\u4f60\u8bf4\u5bf9\u4e86\uff0c\u4f46\u5374\u53eb\u8001\u592b\u7b49\u4eba\u4e0a\u4e86\u4e00\u6b21\u5927\u5f53\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u84e6\u5730\u5927\u7b11\u9053\uff1a\u201c\u9648\u5927\u4fa0\u5018\u82e5\u4e0d\u80fd\u53eb\u4f60\u4eec\u4e0a\u5f53\uff0c\u6b66\u6797\u4e4b\u4e2d\uff0c\u5c82\u4e0d\u662f\u8981\u9057\u5bb3\u65e0\u7a77\u4e86\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u65f6\u5374\u5251\u7709\u4e00\u626c\uff0c\u7b11\u9053\uff1a\u201c\u7ae5\u5b50\u5947\uff0c\u4f60\u5982\u4eca\u660e\u767d\u4e86\u5417\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u8001\u592b\u660e\u767d\u4ee5\u540e\uff0c\u4e5f\u5c31\u662f\u4f60\u6d3b\u4e0d\u4e45\u7684\u65f6\u671f\u5230\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u4f46\u613f\u4f60\u5982\u613f\u5f97\u507f\u3002\u201d\u5ffd\u5730\u4e00\u632f\u624b\u4e2d\u957f\u5251\uff0c\u63a5\u9053\uff1a\u201c\u5c0f\u5fc3\u4e86\uff01\u201d \u3000\u3000\u5530\u7684\u4e00\u58f0\uff0c\u4e3e\u5251\u5e73\u523a\u800c\u53bb\u3002 \u3000\u3000\u7ae5\u5b50\u5947\u5927\u8111\u888b\u4e00\u6643\uff0c\u6325\u624b\u4e2d\u94c1\u68d2\uff0c\u66b4\u559d\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u5982\u4e0d\u7528\u5251\u795e\u7684\u5251\u6cd5\uff0c\u4e5f\u592b\u53ea\u9700\u5341\u62db\uff0c\u5c31\u53ef\u5236\u4f60\u6b7b\u547d\u2026\u2026\u201d \u3000\u3000\u5ffd\u542c\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u4e24\u4eba\u5251\u68d2\u5df2\u7136\u78b0\u5728\u4e00\u8d77\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u5fae\u89c9\u624b\u5fc3\u4e00\u9707\uff0c\u62bd\u8eab\u9000\u4e86\u4e00\u6b65\uff01 \u3000\u3000\u91cd\u5b50\u5947\u89c9\u51fa\u65b9\u96ea\u5b9c\u7684\u8fd9\u4e00\u5251\u867d\u7136\u5e73\u6de1\uff0c\u4f46\u5251\u4e0a\u7684\u771f\u529b\uff0c\u5374\u662f\u6781\u4e3a\u5f3a\u52b2\uff0c\u81ea\u5df1\u624b\u4e2d\u7684\u77ed\u68d2\uff0c\u88ab\u9707\u5f97\u6b6a\u5411\u4e86\u4e00\u8fb9\uff01\u4e0d\u7981\u5fc3\u4e2d\u5927\u4e3a\u60ca\u8bb6\uff0c\u6697\u9053\uff1a\u201c\u770b\u4e0d\u51fa\u8fd9\u5c0f\u5b50\u7684\u5185\u529b\uff0c\u5c45\u7136\u5982\u6b64\u4e4b\u5f3a\uff0c\u8001\u592b\u5012\u662f\u8981\u5c0f\u5fc3\u4e00\u4e9b\u624d\u597d\uff01\u201d \u3000\u3000\u8f6c\u5ff5\u4e4b\u95f4\uff0c\u4e24\u53ea\u5c0f\u773c\u4e00\u77aa\uff0c\u4e00\u821e\u77ed\u68d2\uff0c\u731b\u653b\u800c\u6765\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4fbf\u786c\u6321\u4e86\u4e00\u5251\uff0c\u5fc3\u4e2d\u5df2\u7ecf\u660e\u767d\uff0c\u81ea\u5df1\u5728\u5185\u529b\u65b9\u9762\uff0c\u4e0d\u6bd4\u8fd9\u9b54\u5934\u5f3a\uff0c\u662f\u4ee5\u6253\u5b9a\u5fc3\u610f\uff0c\u4e0d\u518d\u8ddf\u4ed6\u786c\u62da\u3002 \u3000\u3000\u773c\u89c1\u7ae5\u5b50\u5947\u4e00\u68d2\u7838\u6765\uff0c\u987f\u65f6\u8eab\u5f62\u8fc5\u5feb\u5730\u4e00\u8f6c\uff0c\u6325\u5251\u659c\u659c\u4e00\u6321\uff0c\u4e24\u4eba\u4ea4\u9519\u800c\u8fc7\u3002 \u3000\u3000\u7ae5\u5b50\u5947\u51b7\u7b11\u4e00\u58f0\uff0c\u559d\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u8fd9\u7b49\u6253\u6cd5\uff0c\u4e5f\u4e0d\u6015\u6298\u8fb1\u4e86\u5251\u795e\u7684\u540d\u53f7\u5417\uff1f\u201d \u3000\u3000\u53c8\u662f\u4e00\u68d2\uff0c\u8fce\u5934\u7838\u4e0b\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u4f60\u60f3\u6fc0\u6211\u4e0e\u4f60\u786c\u997c\uff1f\u54fc\u2026\u2026\u201d \u3000\u3000\u5251\u8d70\u8f7b\u7075\uff0c\u5374\u5411\u7ae5\u5b50\u5947\u7684\u80a9\u5934\u524a\u53bb\u3002 \u3000\u3000\u4e24\u4eba\u642d\u4e0a\u624b\uff0c\u5404\u81ea\u62a2\u653b\uff0c\u7728\u773c\u4e4b\u95f4\uff0c\u5df2\u8fc7\u4e86\u5341\u4e03\u516b\u62db\u4e4b\u591a\u3002 \u3000\u3000\u5b8b\u5b5a\u7b49\u4eba\uff0c\u867d\u662f\u7ad9\u5728\u4e08\u8bb8\u4e4b\u5916\uff0c\u4f9d\u7136\u89c9\u51fa\u4e24\u4eba\u624b\u4e2d\u5175\u5203\u6240\u53d1\u51fa\u7684\u52b2\u6c14\uff0c\u523a\u5165\u808c\u80a4\uff0c\u903c\u4eba\u7709\u53d1\u3002 \u3000\u3000\u970d\u9e23\u98ce\u77a7\u4e86\u4e00\u9635\uff0c\u4f4e\u58f0\u5411\u5b8b\u5b5a\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u8fd9\u968f\u540c\u7ae5\u5b50\u5947\u6765\u6b64\u4e4b\u4eba\uff0c\u54b1\u4eec\u8981\u4e0d\u8981\u4e0b\u624b\u5c06\u4ed6\u4eec\u9664\u53bb\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u770b\u4e86\u5c11\u6797\u5bfa\u638c\u95e8\u5165\u4e91\u5927\u5e08\u4e00\u773c\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u54b1\u4eec\u6700\u597d\u95ee\u95ee\u5165\u4e91\u638c\u95e8\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u77e5\u9053\u4ed6\u8a00\u4e0b\u4e4b\u610f\uff0c\u4e43\u662f\u56e0\u4e3a\u81ea\u5df1\u7b49\u4eba\u8eab\u5728\u5c11\u6797\u7f57\u6c49\u5802\u4e2d\uff0c\u5982\u662f\u6709\u4ec0\u4e48\u4e3e\u52a8\uff0c\u81ea\u7136\u5e94\u8be5\u5411\u5c11\u6797\u638c\u95e8\u5927\u5e08\u6253\u4e0a\u4e00\u4e2a\u62db\u547c\uff01\u95fb\u8a00\u4e4b\u540e\uff0c\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u5b8b\u5144\u8bf4\u7684\u4e0d\u9519\uff0c\u54b1\u4eec\u6b63\u8be5\u5411\u5165\u4e91\u638c\u95e8\u4eba\u8bf4\u4e0a\u4e00\u58f0\u624d\u662f\u2026\u2026\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5c31\u5f85\u5411\u7ad9\u5728\u4e00\u8fb9\u7684\u5165\u4e91\u5927\u5e08\u884c\u53bb\u3002 \u3000\u3000\u4f46\u4ed6\u521a\u81ea\u79fb\u6b65\uff0c\u4fbf\u542c\u5f97\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u90fd\u4e0d\u662f\u4ec0\u4e48\u597d\u4eba\uff0c\u591a\u6740\u4e00\u4e2a\uff0c\u5c31\u7b49\u4e8e\u591a\u884c\u4e00\u6869\u5584\u4e8b\uff0c\u5e2e\u4e3b\u53c8\u4f55\u5fc5\u754f\u754f\u7f29\u7f29\u2026\u2026\u201d \u3000\u3000\u8bed\u97f3\u672a\u5df2\uff0c\u7adf\u7136\u98de\u8eab\u5411\u4e00\u540d\u5927\u6c49\u6251\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u970d\u545c\u98ce\u7709\u5934\u4e00\u76b1\uff0c\u6b63\u60f3\u559d\u963b\uff0c\u8033\u4e2d\u5df2\u4f20\u6765\u4e00\u58f0\u60e8\u53f7\uff0c\u4f46\u89c1\u5b89\u5c0f\u840d\u53f3\u624b\u626c\u4e86\u4e00\u626c\uff0c\u90a3\u540d\u5927\u6c49\uff0c\u5df2\u7136\u5c31\u58f0\u5012\u5730\uff0c\u6c14\u7edd\u8eab\u6b7b\uff01 \u3000\u3000\u970d\u9e23\u98ce\u5fc3\u4e2d\u5927\u5403\u4e00\u60ca\uff0c\u6697\u9053\uff1a\u201c\u597d\u5feb\u7684\u624b\u6cd5\uff0c\u597d\u72e0\u6bd2\u7684\u6b66\u529f\u2026\u2026\u201d \u3000\u3000\u4e5f\u4e0d\u8fc7\u662f\u4ed6\u8f6c\u5ff5\u4e4b\u95f4\uff0c\u5b89\u5c0f\u840d\u4e5f\u5df2\u4e00\u8fde\u51fb\u5012\u4e86\u4e09\u4eba\uff01 \u3000\u3000\u90a3\u7ae5\u5b50\u5947\u73b0\u8eab\u4e4b\u65f6\uff0c\u4e5f\u53ea\u6709\u56db\u4e2a\u5927\u6c49\uff0c\u968f\u4ed6\u800c\u6765\uff0c\u6b64\u523b\u90a3\u5269\u4e0b\u7684\u4e00\u540d\uff0c\u773c\u89c1\u540c\u6765\u7684\u4e09\u4eba\uff0c\u5168\u90fd\u88ab\u5b89\u5c0f\u840d\u4e3e\u624b\u4e4b\u95f4\uff0c\u4fbf\u5df2\u51fb\u5012\uff0c\u53ea\u5413\u5f97\u8138\u8272\u60e8\u53d8\uff0c\u62b1\u5934\u5411\u5916\u5954\u53bb\u3002 \u3000\u3000\u4ed6\u8dd1\u5f97\u867d\u7136\u4e0d\u6162\uff0c\u4f46\u5374\u672a\u66fe\u6599\u5230\uff0c\u90a3\u5b89\u5c0f\u840d\u7684\u8eab\u6cd5\uff0c\u6bd4\u4ed6\u5feb\u4e86\u4f55\u6b62\u5341\u500d\uff01 \u3000\u3000\u90a3\u540d\u5927\u6c49\u521a\u521a\u5954\u5411\u7f57\u6c49\u5802\u7684\u95e8\u524d\uff0c\u773c\u524d\u4eba\u5f71\u4e00\u82b1\uff0c\u8033\u4e2d\u542c\u5f97\u4e00\u58f0\u51b7\u7b11\uff0c\u8fde\u60ca\u547c\u5c1a\u672a\u51fa\u53e3\uff0c\u53ea\u89c9\u80f8\u524d\u4e00\u9707\uff0c\u53cc\u76ee\u53d1\u9ed1\uff0c\u4e00\u53e3\u9c9c\u8840\u55b7\u51fa\uff0c\u4eba\u5df2\u5012\u5730\u4e0d\u8d77\uff01 \u3000\u3000\u5b89\u5c0f\u840d\u4e3e\u624b\u4e4b\u95f4\uff0c\u4fbf\u5c06\u7ae5\u5b50\u5947\u7684\u56db\u5927\u4f8d\u536b\u9664\u53bb\uff0c\u4f11\u8bf4\u970d\u9e23\u98ce\u77a7\u5f97\u5931\u8272\u5fc3\u60ca\uff0c\u8fde\u90a3\u5b8b\u5b5a\u548c\u5165\u4e91\u5728\u5e08\uff0c\u4e5f\u4e0d\u7981\u4e3a\u4e4b\u795e\u9a70\u76ee\u7729\uff0c\u6697\u6697\u79f0\u5947\u3002 \u3000\u3000\u6b64\u523b\uff0c\u7ae5\u5b50\u5947\u4f3c\u4e5f\u89c1\u5230\u968f\u6765\u4e4b\u4eba\uff0c\u4ff1\u5df2\u4e27\u751f\uff0c\u4e0d\u7531\u5f97\u5389\u543c\u4e00\u58f0\uff0c\u77ed\u68d2\u62db\u5f0f\u4e00\u53d8\uff0c\u731b\u5411\u65b9\u96ea\u5b9c\u653b\u51fa\u4e09\u68d2\u3002 \u3000\u3000\u8fd9\u4e09\u68d2\u529b\u9053\u4e4b\u5f3a\uff0c\u771f\u8db3\u4ee5\u5288\u5c71\u79fb\u6d77\uff0c\u4f46\u95fb\u4e00\u9635\u9635\u9510\u5578\u751f\u98ce\uff0c\u4eba\u4eba\u4e3a\u4e4b\u8272\u53d8\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u76ee\u7779\u7ae5\u5b50\u5947\u60c5\u6025\u62fc\u547d\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u53eb\u9053\uff1a\u201c\u6211\u82e5\u4e0d\u91cd\u521b\u4e8e\u4ed6\uff0c\u4ed6\u5fc5\u7136\u4e0d\u4f1a\u7518\u5fc3\u670d\u8f93\uff0c\u5f03\u68d2\u8ba4\u8d25\u7684\u4e86\uff01\u201d \u3000\u3000\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u624b\u4e2d\u5251\u62db\u6025\u5c14\u4e00\u53d8\u3002 \u3000\u3000\u5237\uff01\u5237\uff01\u4e24\u5251\uff0c\u5e7b\u8d77\u5343\u6735\u5251\u82b1\uff0c\u5c06\u90a3\u7ae5\u5b50\u5947\u5168\u8eab\u7f69\u4f4f\uff0c\u4ed6\u8fd9\u4e24\u5f0f\u5251\u6cd5\uff0c\u6b63\u662f\u5251\u795e\u9f99\u884c\u516b\u5251\u4e2d\u7684\u6740\u624b\uff0c\u7ae5\u5b50\u5947\u81ea\u7136\u8bc6\u5f97\u5389\u5bb3\uff0c\u77ed\u68d2\u72c2\u6325\uff0c\u95ea\u8eab\u5f80\u540e\u4fbf\u9000\uff01 \u3000\u3000\u4f46\u4ed6\u7ec8\u4e8e\u53c8\u6162\u4e86\u4e00\u6b65\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u65e2\u662f\u6709\u4e86\u901f\u6218\u901f\u51b3\u4e4b\u5fc3\uff0c\u81ea\u662f\u4e0d\u4f1a\u518d\u5bb9\u4ed6\u6709\u90a3\u4fa5\u5e78\u4e4b\u673a\uff0c\u4f46\u542c\u4ed6\u66b4\u559d\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u7ae5\u5b50\u5947\uff0c\u4f60\u7ed9\u6211\u8eba\u4e0b\u5427\u2026\u2026\u201d \u3000\u3000\u55e4\u7684\u4e00\u58f0\uff0c\u957f\u5251\u5df2\u5c06\u7ae5\u5b50\u5947\u957f\u886b\u5212\u88c2\uff01 \u3000\u3000\u70b9\u70b9\u9c9c\u8840\uff0c\u987a\u7740\u957f\u886b\u6ef4\u843d\uff0c\u4f46\u91cd\u5b50\u5947\u5e76\u672a\u5e94\u58f0\u5012\u5730\uff0c\u77aa\u7740\u4e24\u53ea\u5c0f\u773c\uff0c\u5411\u65b9\u96ea\u5b9c\u53d1\u6123\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u89c1\u4ed6\u5c45\u7136\u4e0d\u7ba1\u81ea\u5df1\u7684\u4f24\u52bf\uff0c\u7741\u7740\u773c\u671b\u7740\u81ea\u5df1\u53d1\u5446\uff0c\u5fc3\u4e2d\u4e0d\u7531\u5f97\u6014\u4e86\u4e00\u6014\uff1b\u6697\u9053\uff1a\u201c\u4ed6\u662f\u600e\u4e48\u4e86\uff1f\u96be\u9053\u8fd9\u4f24\u52bf\u7adf\u662f\u51fa\u4e4e\u610f\u5916\u7684\u4e25\u91cd\u5417\uff1f\u201d \u3000\u3000\u8f6c\u5ff5\u4e4b\u95f4\uff0c\u53ea\u89c1\u90a3\u7ae5\u5b50\u5947\u5ffd\u7136\u5389\u5578\u4e00\u58f0\uff0c\u624b\u4e2d\u77ed\u68d2\u4e00\u6325\uff0c\u5f53\u5934\u76f4\u5411\u65b9\u96ea\u5b9c\u7838\u53bb\uff01 \u3000\u3000\u4ed6\u8fd9\u6837\u884c\u52a8\uff0c\u5927\u51fa\u5728\u573a\u8bf8\u4eba\u610f\u6599\u4e4b\u5916\uff0c\u5b89\u5c0f\u840d\u867d\u7136\u8fdc\u5728\u4e24\u4e08\u4ee5\u5916\uff0c\u4f9d\u7136\u5fcd\u4e0d\u4f4f\u5c16\u58f0\u60ca\u53eb\u8d77\u6765\u3002 \u3000\u3000\u4f46\u65b9\u96ea\u5b9c\u81ea\u5df1\u5374\u662f\u5341\u5206\u9547\u9759\uff0c\u4e1d\u6beb\u4e0d\u4e3a\u7ae5\u5b50\u5947\u8fd9\u7b49\u5192\u5931\u7684\u4e3e\u52a8\u6240\u60ca\uff0c\u624b\u4e2d\u957f\u5251\u95ea\u7535\u822c\u5f80\u5916\u4e00\u6321\uff0c\u8eab\u5f62\u5df2\u7136\u5411\u540e\u66b4\u9000\u4e09\u5c3a\u3002 \u3000\u3000\u4ed6\u8eab\u5f62\u5fae\u5fae\u4e00\u9000\uff0c\u53e3\u4e2d\u4e5f\u540c\u65f6\u5927\u559d\u4e00\u58f0\u9053\uff1a\u201c\u7ae5\u5b50\u5947\uff0c\u4f60\u5f53\u771f\u662f\u5728\u627e\u6b7b\u4e86\u2026\u2026\u201d \u3000\u3000\u5927\u6012\u4e4b\u4e0b\uff0c\u624b\u4e2d\u5b9d\u5251\u4e00\u5410\uff0c\u4e00\u62db\u201c\u6012\u9f99\u51fa\u5cab\u201d\uff0c\u75be\u5feb\u5730\u5411\u7ae5\u5b50\u5947\u80f8\u524d\u523a\u53bb\u3002 \u3000\u3000\u7ae5\u5b50\u5947\u8fd9\u65f6\u77ed\u68d2\u5df2\u7136\u64a4\u4e86\u56de\u53bb\uff0c\u65b9\u96ea\u5b9c\u957f\u5251\u5c1a\u672a\u523a\u6765\uff0c\u4ed6\u5df2\u7136\u957f\u5578\u4e00\u58f0\uff0c\u821e\u68d2\u594b\u62f3\uff0c\u9707\u7834\u4e86\u7f57\u6c49\u5802\u7684\u5c4b\u9876\uff0c\u5411\u5bfa\u5916\u9003\u53bb\uff01 \u3000\u3000\u539f\u6765\u4ed6\u9002\u624d\u8fce\u5934\u4e00\u68d2\uff0c\u4e43\u662f\u4ee5\u8fdb\u4e3a\u9000\u4e4b\u7b56\u3002 \u3000 \u7b2c\u4e8c\u5341\u4e03\u56de\u3000\u795e\u5251\u5947\u88ad \u3000 \u3000\u3000\u5728\u573a\u7684\u51e0\u4eba\u5747\u662f\u6b66\u6797\u4e00\u6d41\u9ad8\u624b\uff0c\u6b64\u523b\u4e5f\u4e0d\u7531\u5f97\u5446\u4e86\u4e00\u5446\uff0c\u671b\u7740\u90a3\u7834\u53bb\u7684\u5c4b\u9876\uff0c\u987f\u8db3\u53d1\u6123\uff01 \u3000\u3000\u970d\u9e23\u98ce\u84e6\u5730\u653e\u58f0\u5927\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4e94\u5927\u9b54\u4e3b\u4e2d\u7684\u5927\u5934\u9b3c\u738b\uff0c\u5c45\u7136\u4e5f\u662f\u65e0\u8d56\u4e4b\u8f88\u2026\u2026\u201d \u3000\u3000\u7b11\u58f0\u4e00\u843d\uff0c\u5927\u6b65\u5411\u4eba\u4e91\u5927\u5e08\u8d70\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5165\u4e91\u5927\u5e08\u8fd9\u65f6\u6b63\u53cc\u624b\u5408\u5341\uff0c\u95ed\u76ee\u9ad8\u8bf5\u4f5b\u53f7\u4e0d\u6b62\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4f3c\u4e4e\u4e5f\u88ab\u773c\u524d\u7684\u5f62\u72b6\uff0c\u5f04\u5f97\u5446\u4e86\u597d\u4e45\uff0c\u7ec8\u4e8e\u4ed6\u5fcd\u4e0d\u4f4f\u957f\u53f9\u4e00\u58f0\u9053\uff1a\u201c\u533a\u533a\u4e00\u65f6\u5927\u610f\uff0c\u4e0d\u60f3\u7adf\u88ab\u8fd9\u9b54\u5934\u8131\u8eab\u800c\u53bb\u4e86\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u5728\u65c1\u4e5f\u4e0d\u7981\u987f\u8db3\u957f\u53f9\uff0c\u4ed6\u4eec\u4f3c\u662f\u8c01\u4e5f\u672a\u66fe\u6599\u5230\uff0c\u7ae5\u5b50\u5947\u4f1a\u7528\u8fd9\u7b49\u4e0b\u4e0b\u4e4b\u7b56\u9041\u53bb\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u8fd9\u65f6\u51b7\u54fc\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e94\u5927\u9b54\u4e3b\uff0c\u4e5f\u4e0d\u8fc7\u662f\u62b1\u5934\u9f20\u7a9c\u4e4b\u6d41\uff0c\u771f\u662f\u4e22\u4eba\u2026\u2026\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u53cc\u76ee\u7cbe\u5149\u66b4\u5c04\uff0c\u7a81\u7136\u5411\u970d\u9e23\u98ce\u9053\uff1a\u201c\u970d\u5e2e\u4e3b\uff0c\u54b1\u4eec\u5feb\u4e9b\u53bb\u770b\u770b\u90a3\u5730\u9053\u4e4b\u4e2d\u7684\u8d35\u5e2e\u957f\u8001\u5427\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u76b1\u4e86\u76b1\u7709\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u54b1\u4eec\u679c\u7136\u8981\u8d76\u5feb\u2026\u2026\u201d \u3000\u3000\u4f46\u662f\uff0c\u65b9\u96ea\u5b9c\u5ffd\u7136\u5927\u58f0\u9053\uff1a\u201c\u4e24\u4f4d\u4e14\u6162\uff01\u201d \u3000\u3000\u4ed6\u8f6c\u8eab\u5927\u6b65\u5411\u5e84\u6377\u8d70\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5165\u4e91\u5927\u5e08\u548c\u970d\u9e23\u98ce\u95fb\u8a00\uff0c\u5168\u90fd\u6014\u4e86\u4e00\u6014\uff0c\u6b62\u6b65\u4e0d\u524d\uff0c\u53ea\u89c1\u65b9\u96ea\u5b9c\u8d70\u5230\u5e84\u6377\u8eab\u524d\uff0c\u4e00\u638c\u62cd\u5f00\u4e86\u4ed6\u7684\u7a74\u9053\uff0c\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u5e84\u5144\uff0c\u533a\u533a\u6709\u51e0\u53e5\u8bdd\u8981\u8bf7\u6559\uff0c\u5e0c\u671b\u4f60\u7167\u5b9e\u56de\u7b54\u624d\u597d\uff01\u201d \u3000\u3000\u5e84\u6377\u6d3b\u52a8\u4e86\u4e0b\u624b\u811a\uff0c\u5341\u5206\u9547\u5b9a\u5730\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u65b9\u5144\u6709\u4f55\u6307\u6559\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u90a3\u5730\u9053\u4e4b\u4e2d\uff0c\u8fd8\u6709\u4ec0\u4e48\u4eba\u5728\u5185\uff1f\u201d \u3000\u3000\u5e84\u6377\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u6ca1\u6709\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5c11\u6797\u5b50\u5f1f\u548c\u4e10\u5e2e\u957f\u8001\u5462\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u4ed6\u4eec\u8fd8\u5728\u5730\u9053\u4e4b\u4e2d\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4f60\u4eec\u53ef\u662f\u70b9\u4e86\u4ed6\u4eec\u7684\u7a74\u9053\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u4e0d\u662f\u70b9\u4e86\u4ed6\u4eec\u7684\u7a74\u9053\uff0c\u800c\u662f\u2026\u2026\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u8138\u8272\u4e00\u6c89\uff0c\u559d\u9053\uff1a\u201c\u5e84\u5144\uff0c\u4f60\u6700\u597d\u4e0d\u8981\u5728\u533a\u533a\u9762\u524d\u5f04\u9b3c\u3002\u201d \u3000\u3000\u5e84\u6377\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u65b9\u5144\uff0c\u8fde\u7ae5\u5b50\u5947\u90fd\u4e0d\u662f\u4f60\u7684\u5bf9\u624b\uff0c\u5144\u5f1f\u53c8\u600e\u6562\u5728\u65b9\u5144\u5f1f\u9762\u524d\u5f04\u9b3c\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u4f60\u77e5\u9053\u5c31\u597d\uff01\u4ed6\u4eec\u5230\u5e95\u600e\u4e48\u6837\u4e86\u3002\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u4e2d\u4e86\u6bd2\uff0c\u516b\u4e2a\u65f6\u8fb0\u4e4b\u540e\uff0c\u5c31\u4f1a\u9192\u8f6c\u8fc7\u6765\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u597d\uff01\u533a\u533a\u5c31\u76f8\u4fe1\u4f60\u4e00\u6b21\uff01\u201d \u3000\u3000\u8fd9\u624d\u56de\u5934\u5411\u5165\u4e91\u5927\u5e08\u4e00\u7b11\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u5982\u662f\u8fd9\u4f4d\u5e84\u5144\u6ca1\u6709\u8bf4\u8c0e\uff0c\u8d35\u5bfa\u7684\u5b50\u5f1f\u548c\u4e10\u5e2e\u957f\u8001\u4eec\u5fc5\u5f97\u7b49\u5230\u51e0\u4e2a\u65f6\u8fb0\u4e4b\u540e\u624d\u4f1a\u9192\u8f6c\u7684\u4e86\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u5341\u5206\u611f\u6fc0\u5730\u5408\u5341\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\u76f8\u52a9\u4e4b\u60c5\uff0c\u8001\u8872\u6c38\u8bb0\u5728\u5fc3\u4e2d\u2026\u2026\u201d \u3000\u3000\u8fd9\u7b49\u65f6\u523b\uff0c\u4ed6\u8fd8\u5728\u5ba2\u5957\uff0c\u53ea\u628a\u65b9\u96ea\u5b9c\u542c\u5f97\u5446\u4e86\u4e00\u5446\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u5230\u5e95\u662f\u4f5b\u95e8\u9ad8\u50e7\uff0c\u867d\u662f\u9762\u4e34\u8fd9\u7b49\u5927\u53d8\uff0c\u4f9d\u7136\u4e0d\u62d8\u5c0f\u8282\uff0c\u771f\u662f\u96be\u5f97\u7684\u5f88\u2026\u2026\u201d \u3000\u3000\u4f46\u4ed6\u53e3\u4e2d\u5374\u662f\u63a5\u9053\uff1a\u201c\u638c\u95e8\u4eba\u597d\u8bf4\uff01\u201d \u3000\u3000\u4ed6\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u90a3\u5730\u9053\u4e2d\u7684\u5404\u4f4d\u5927\u5e08\uff0c\u54b1\u4eec\u8fd8\u662f\u5148\u53bb\u770b\u770b\u4ed6\u4eec\u624d\u662f\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8001\u8872\u7b49\u8fd9\u5c31\u5165\u5185\u2026\u2026\u201d \u3000\u3000\u8bed\u97f3\u672a\u843d\uff0c\u4eba\u5df2\u8f6c\u8eab\u5411\u90a3\u5730\u9053\u4e4b\u5185\u2026\u2026 \u3000\u3000\u970d\u9e23\u98ce\u8fd9\u65f6\u4e5f\u5feb\u6b65\u8ddf\u4e86\u8fc7\u53bb\uff0c\u5b8b\u5b5a\u770b\u4e86\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u5728\u5916\u9762\u7b49\u4e00\u7b49\uff0c\u8001\u673d\u8fd9\u4e5f\u5165\u5185\u770b\u770b\u53bb\u3002\u201d \u3000\u3000\u4e09\u4f4d\u8001\u4eba\uff0c\u4e00\u8f6c\u773c\u5168\u90fd\u8fdb\u5165\u4e86\u5730\u9053\u4e4b\u4e2d\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u5999\u76ee\u4e00\u8f6c\uff0c\u7b11\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u6253\u7b97\u600e\u4e48\u5904\u7f6e\u8fd9\u4f4d\u5e84\u5927\u4fa0\u5440\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u59d4\u5c48\u4ed6\u7b49\u5230\u90a3\u4e9b\u5c11\u6797\u5927\u5e08\u9192\u6765\u4e4b\u540e\uff0c\u653e\u4ed6\u79bb\u53bb\u4fbf\u662f\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u5012\u662f\u4ec1\u6148\u5f97\u5f88\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u8fd9\u4f4d\u5e84\u5144\u5012\u4e0d\u662f\u600e\u6837\u4e3a\u975e\u505a\u6b79\u4e4b\u4eba\uff0c\u6240\u4ee5\uff0c\u5144\u5f1f\u4e0d\u60f3\u4e3a\u96be\u4e8e\u4ed6\u3002\u201d \u3000\u3000\u5e84\u6377\u8fd9\u65f6\u5728\u65c1\u5ffd\u7136\u8138\u8272\u4e00\u53d8\uff0c\u53cc\u76ee\u4e4b\u4e2d\uff0c\u9690\u9690\u5730\u6cdb\u73b0\u6cea\u5149\u3002 \u3000\u3000\u6562\u60c5\u65b9\u96ea\u5b9c\u8fd9\u51e0\u53e5\u8bdd\uff0c\u4f7f\u4ed6\u5927\u4e3a\u611f\u52a8\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u4e24\u773c\u5728\u90a3\u5e84\u6377\u8eab\u4e0a\u4e00\u8f6c\uff0c\u7b11\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u8fd9\u7b49\u80f8\u895f\uff0c\u679c\u7136\u53eb\u4eba\u4f69\u670d\uff01\u201d \u3000\u3000\u5979\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u65b9\u5144\uff0c\u8fd9\u4f4d\u5e84\u5144\u597d\u50cf\u5927\u53d7\u611f\u52a8\u5462\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u53ea\u9053\u5b89\u5c0f\u840d\u5728\u8bf4\u7b11\uff0c\u6389\u5934\u5411\u5e84\u6377\u8d70\u53bb\uff0c\u6b64\u65f6\uff0c\u4ed6\u4e0d\u7531\u5f97\u4e00\u6014\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a \u3000\u3000\u201c\u679c\u7136\u4ed6\u5012\u4f3c\u6709\u7740\u6094\u609f\u4e4b\u5fc3\u4e86\uff01\u201d \u3000\u3000\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u53e3\u4e2d\u63a5\u9053\uff1a\u201c\u5e84\u5144\uff0c\u4f60\u53ef\u662f\u6709\u4e9b\u540e\u6094\u5417\uff1f\u201d \u3000\u3000\u5e84\u6377\u5f3a\u81ea\u5fcd\u8010\u7740\u5fc3\u4e2d\u7684\u75db\u82e6\uff0c\u52c9\u5f3a\u4e00\u7b11\u9053\uff1a\u201c\u65b9\u5144\uff0c\u6709\u8bb8\u591a\u4e8b\uff0c\u4e0d\u662f\u4e00\u4e2a\u4eba\u80fd\u81ea\u4e3b\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6697\u9053\uff1a\u201c\u770b\u6765\u4ed6\u662f\u6709\u70b9\u52a8\u4e86\u4e86\uff01\u201d \u3000\u3000\u5f53\u4e0b\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u5e84\u5144\uff0c\u5144\u5f1f\u6709\u4e00\u53e5\u8bdd\uff0c\u4e0d\u77e5\u5f53\u8bf4\u4e0d\u5f53\u8bf4\uff01\u201d \u3000\u3000\u5e84\u6377\u867d\u7136\u77e5\u9053\u4ed6\u53ef\u80fd\u8981\u8bf4\u7684\u662f\u4ec0\u4e48\uff0c\u4f46\u4f9d\u7136\u82e6\u7b11\u9053\uff1a\u201c\u65b9\u5144\u6709\u8bdd\u8bf7\u8bf4\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e84\u5144\u8bf7\u6055\u5144\u5f1f\u4ea4\u6d45\u8a00\u6df1\uff0c\u5982\u662f\u5e84\u5144\u6709\u4f55\u4e3a\u96be\u4e4b\u5904\uff0c\u53ea\u8981\u4f60\u5e84\u5144\u8bf4\u51fa\uff0c\u5144\u5f1f\u5b9a\u7136\u5c3d\u529b\u4e3a\u4f60\u5206\u5fe7\u89e3\u6101\uff01\u201d \u3000\u3000\u5e84\u6377\u773c\u4e2d\u53c8\u6709\u4e86\u6cea\u5149\uff0c\u62b1\u62f3\u4e00\u63d6\uff0c\u5e7d\u5e7d\u5730\u53f9\u9053\uff1a\u201c\u65b9\u5144\u76db\u60c5\uff0c\u5144\u5f1f\u5fc3\u9886\u4e86\u2026\u2026\u201d \u3000\u3000\u5ffd\u7136\u76ee\u5149\u4e00\u8f6c\uff0c\u5411\u90a3\u5341\u516b\u5c0a\u7f57\u6c49\u770b\u4e86\u4e00\u773c\uff0c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u8fd9\u7f57\u6c49\u5802\u5185\uff0c\u8fd8\u6709\u7ae5\u5b50\u5947\u624b\u4e0b\u4e4b\u4eba\uff0c\u4e0d\u77e5\u65b9\u5144\u53ef\u66fe\u53d1\u89c9\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u5e84\u6377\u4ef0\u624b\u4e00\u6307\u90a3\u5c1a\u5728\u795e\u4f4d\u4e4b\u4e0a\u7684\u5341\u4e03\u5c0a\u7f57\u6c49\uff0c\u9053\uff1a\u201c\u5c31\u662f\u8fd9\u4e9b\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bdd\u97f3\u672a\u5df2\uff0c\u90a3\u5341\u4e03\u5c0a\u7f57\u6c49\u7adf\u7136\u547c\u5578\u4e00\u58f0\uff0c\u5168\u90fd\u8dc3\u4e0b\u795e\u5ea7\uff0c\u5c06\u4e09\u4eba\u56f4\u5728\u5f53\u4e2d\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u7779\u72b6\uff0c\u4e0d\u7981\u5251\u7709\u66b4\u626c\uff0c\u7eb5\u58f0\u5927\u7b11\u9053\uff1a\u201c\u5c31\u662f\u8fd9\u4e9b\u88c5\u795e\u5f04\u9b3c\u7684\u89d2\u8272\uff1f\u533a\u533a\u5012\u662f\u60f3\u77a7\u77a7\u4ed6\u4eec\u6709\u4ec0\u4e48\u80fd\u8010\uff0c\u662f\u5426\u6bd4\u90a3\u7ae5\u5b50\u5947\u8fd8\u8981\u9ad8\u660e\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u9661\u7136\u65cb\u8eab\u62cd\u51fa\u4e00\u638c\u3002 \u3000\u3000\u4f46\u542c\u5f97\u4e00\u58f0\u95f7\u54fc\u4eba\u8033\uff0c\u7ad9\u5728\u4ed6\u8eab\u540e\u7684\u4e00\u540d\u7f57\u6c49\uff0c\u4e1a\u5df2\u5012\u5730\u4e0d\u8d77\uff01 \u3000\u3000\u663e\u7136\uff0c\u90a3\u5341\u4e03\u4e2a\u7ae5\u5b50\u5947\u624b\u4e0b\u4e4b\u4eba\uff0c\u90fd\u672a\u7c98\u5230\u65b9\u96ea\u5b9c\u7adf\u4f1a\u66b4\u4e0b\u6740\u624b\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u5168\u90fd\u5413\u5f97\u9000\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u5999\u76ee\u4e00\u8f6c\uff0c\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u5f85\u6211\u4e0e\u4f60\u8054\u624b\uff0c\u770b\u770b\u8c01\u6740\u7684\u5feb\uff0c\u6740\u7684\u591a\uff0c\u597d\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u597d\u554a\uff01\u4f60\u5148\u51fa\u624b\u5427\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u4e0d\u7528\u65b9\u96ea\u5b9c\u50ac\u4fc3\uff0c\u53ea\u89c1\u5979\u683c\u683c\u4e00\u7b11\uff0c\u53cc\u638c\u4e00\u9519\uff0c\u76f4\u5411\u90a3\u4e00\u7fa4\u7f57\u6c49\u653b\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5411\u5e84\u6377\u7b11\u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6709\u52b3\u5e84\u5144\u4ee3\u6211\u63a0\u9635\u2026\u2026\u201d \u3000\u3000\u970d\u5730\u8eab\u5f62\u4e00\u65cb\uff0c\u957f\u5251\u63d2\u56de\u9798\u5185\uff0c\u4e5f\u81ea\u53cc\u81c2\u4e00\u5708\uff0c\u5411\u90a3\u5341\u516d\u540d\u5047\u626e\u7684\u7f57\u6c49\u62cd\u51fa\u4e24\u638c\u3002 \u3000\u3000\u5341\u516d\u822c\u5175\u5203\u540c\u65f6\u51fa\u4e8e\uff0c\u5728\u58f0\u52bf\u4e0a\u5374\u4e5f\u4e0d\u51e1\u3002 \u3000\u3000\u4f46\u5728\u65b9\u96ea\u5b9c\u548c\u5b89\u5c0f\u840d\u7684\u773c\u4e2d\uff0c\u90a3\u5341\u516d\u822c\u5175\u5668\u7b80\u76f4\u5982\u540c\u5341\u516d\u4ef6\u7834\u94dc\u70c2\u94c1\u4e00\u822c\u7684\u4e0d\u5c51\u4e00\u987e\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u5728\u4ed6\u4eec\u672a\u4eae\u51fa\u5175\u5203\u4ee5\u524d\uff0c\u53ea\u662f\u968f\u624b\u51fa\u638c\u653b\u654c\uff0c\u5bb9\u5f97\u5341\u516d\u4eba\u7684\u5175\u5203\u9f50\u51fa\uff0c\u5979\u4e0d\u7531\u5f97\u67f3\u7709\u4e00\u626c\uff0c\u8138\u8272\u4e00\u6c89\uff0c\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u54b1\u4eec\u8fd8\u662f\u901f\u6218\u901f\u51b3\uff0c\u4e5f\u597d\u53bb\u6551\u4eba\u554a\uff01\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u53ea\u89c1\u5979\u53f3\u624b\u4e00\u626c\uff0c\u4e00\u7f15\u767d\u5149\u98de\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4f46\u542c\u5f97\u4e00\u4e32\u60ca\u547c\u4e4b\u58f0\u4f20\u51fa\uff0c\u8fce\u9762\u5904\u7684\u56db\u540d\u7f57\u6c49\uff0c\u4e1a\u5df2\u6454\u5012\u5730\u4e0a\uff0c\u4e00\u9635\u98a4\u52a8\uff0c\u770b\u770b\u662f\u6c14\u7edd\u8eab\u6b7b\u4e86\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u89c1\u5b89\u5c0f\u840d\u51fa\u624b\u4e4b\u95f4\u4fbf\u5df2\u6740\u5374\u4e94\u4eba\uff0c\u4e24\u773c\u795e\u5149\u9661\u5c04\uff0c\u53e3\u4e2d\u66b4\u559d\u4e00\u58f0\uff0c\u53cd\u624b\u62d4\u51fa\u5b9d\u5251\u3002\u4e00\u62db\u201c\u9704\u9707\u4e5d\u9704\u201d\uff0c\u5bd2\u5149\u8fc7\u5904\uff0c\u6b8b\u5200\u65ad\u5203\uff0c\u9152\u4e86\u4e00\u5730\uff0c\u4e09\u540d\u5047\u626e\u7684\u7f57\u6c49\uff0c\u80a2\u6b8b\u3001\u81c2\u65ad\u3001\u8840\u6d41\u4e94\u6b65\u7684\u6454\u6b7b\u5730\u4e0a\uff01 \u3000\u3000\u4e24\u4eba\u8fd9\u4e00\u53d1\u72e0\uff0c\u987f\u65f6\u82e6\u4e86\u90a3\u51e0\u540d\u7ae5\u5b50\u5947\u7684\u5c5e\u4e0b\uff0c\u4e5f\u4e0d\u8fc7\u7728\u773c\u4e4b\u95f4\uff0c\u5341\u4e03\u4eba\u5168\u90fd\u4e27\u5728\u4e24\u4eba\u767d\u7eeb\u3001\u957f\u5251\u4e4b\u4e0b\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u770b\u4e86\u5730\u4e0a\u7684\u5c38\u4f53\u4e00\u773c\uff0c\u76b1\u7709\u9053\uff1a\u201c\u65b9\u5144\uff0c\u8fd9\u4e9b\u4eba\u600e\u5730\u8fd9\u822c\u4e0d\u582a\u4e00\u51fb\u554a\uff1f\u201d \u3000\u3000\u8a00\u4e0b\u4e4b\u610f\uff0c\u4f3c\u662f\u5174\u72b9\u672a\u5c3d\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u5341\u4e03\u6761\u6027\u547d\u54e9\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4ed6\u4eec\u6b7b\u6709\u4f59\u8f9c\uff0c\u4f60\u8fd8\u4e3a\u4ed6\u4eec\u53ef\u60dc\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u957f\u957f\u4e00\u53f9\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u6211\u5e76\u975e\u4e3a\u4ed6\u4eec\u53ef\u60dc\uff0c\u53ea\u662f\u89c9\u6709\u4e9b\u5fc3\u4e2d\u4e0d\u5fcd\u2026\u2026\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u6389\u5934\u5411\u5e84\u6377\u9053\uff1a\u201c\u5e84\u5144\uff0c\u8fd9\u5341\u516b\u540d\u5047\u5192\u7f57\u6c49\u4e4b\u4eba\uff0c\u4f60\u53ef\u76f8\u8bc6\uff1f\u201d \u3000\u3000\u5e84\u6377\u6447\u5934\u9053\uff1a\u201c\u8fd9\u5341\u516b\u4eba\u5747\u662f\u90a3\u7ae5\u5b50\u5947\u65b0\u6536\u7684\u5c5e\u4e0b\uff0c\u5144\u5f1f\u4ff1\u90fd\u4e0d\u8bc6\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4ed6\u4eec\u7684\u6b66\u529f\u90fd\u8fd8\u5f88\u5dee\uff0c\u90a3\u7ae5\u5b50\u5947\u600e\u4f1a\u5c31\u653e\u5fc3\u5e26\u4e86\u4ed6\u4eec\u51fa\u6765\uff1f\u201d \u3000\u3000\u5e84\u6377\u957f\u957f\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u8fd9\u8bdd\u53ef\u8981\u770b\u662f\u600e\u4e48\u8bf4\u4e86\uff01\u5018\u82e5\u90a3\u7ae5\u5b50\u5947\u4e0d\u662f\u9047\u5230\u65b9\u5144\uff0c\u8bd5\u95ee\u8fd9\u5c11\u6797\u5bfa\u4e2d\uff0c\u53c8\u6709\u4ec0\u4e48\u80fd\u6321\u5f97\u4ed6\u4e00\u51fb\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5982\u662f\u6211\u548c\u5b89\u59d1\u5a18\u4e0d\u5728\uff0c\u53ea\u6015\u5c11\u6797\u5bfa\u7684\u5c40\u9762\uff0c\u5df2\u7136\u4e0d\u582a\u8bbe\u60f3\u4e86\uff01\u201d \u3000\u3000\u5fc3\u5ff5\u81f3\u6b64\uff0c\u4e0d\u7981\u53f9\u606f\u9053\uff1a\u201c\u662f\u554a\uff01\u5e84\u5144\u8bf4\u7684\u4e0d\u5047\uff0c\u4ed6\u4eec\u8ddf\u968f\u90a3\u7ae5\u5b50\u5947\u4eb2\u81ea\u51fa\u9a6c\uff0c\u81ea\u7136\u60f3\u4e0d\u5230\u4f1a\u4e27\u547d\u7684\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5728\u65c1\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u54b1\u4eec\u8981\u4e0d\u8981\u53bb\u81f3\u5730\u9053\u4e2d\u77a7\u77a7\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u5f97\u8981\u5411\u5e84\u5144\u8bf7\u6559\u4e00\u756a\u4e86\u3002\u201d \u3000\u3000\u5e84\u6377\u6168\u7136\u4e00\u7b11\u9053\uff1a\u201c\u65b9\u5144\uff0c\u9002\u624d\u5144\u5f1f\u5df2\u89c1\u8bc6\u8fc7\u4e86\u4e24\u4f4d\u6b66\u529f\uff0c\u4ee5\u4e24\u4f4d\u4e4b\u80fd\uff0c\u5144\u5f1f\u8fd8\u6709\u4ec0\u4e48\u53ef\u6548\u52b3\u4e4b\u5904\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u5e84\u5144\u5938\u5956\u4e86\uff01\u5144\u5f1f\u60f3\u5411\u5e84\u5144\u8bf7\u6559\u4e4b\u4e8b\uff0c\u4e43\u662f\u60f3\u77e5\u9053\u90a3\u5730\u9053\u4e2d\u7684\u5c11\u6797\u95e8\u4e0b\uff0c\u662f\u5426\u5f53\u771f\u6ca1\u6709\u89e3\u836f\u53ef\u6551\uff1f\u201d \u3000\u3000\u5e84\u6377\u95fb\u8a00\uff0c\u6c89\u541f\u4e00\u4e0b\uff0c\u63a5\u9053\uff1a\u201c\u5144\u5f1f\u8bf4\u7684\u5b9e\u8bdd\uff0c\u90a3\u89e3\u836f\u5e26\u5728\u7ae5\u5b50\u5947\u8eab\u4e0a\uff0c\u522b\u4eba\u6ca1\u6709\uff01 \u3000\u3000\u4e0d\u8fc7\u4e48\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4e0d\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u7ae5\u5b50\u5947\u5e76\u975e\u5584\u4e8e\u7528\u6bd2\u4e4b\u4eba\uff0c\u4ed6\u542c\u7528\u7684\u8fd9\u79cd\u6bd2\u9999\uff0c\u5144\u5f1f\u77a7\u4ed6\u9887\u4f3c\u6c5f\u6e56\u4e2d\u4e0b\u4e94\u95e8\u7684\u4e94\u9f13\u65ad\u9b42\u9999\uff0c\u5982\u662f\u7528\u51b7\u6c34\u5411\u90a3\u4e2d\u6bd2\u4e4b\u4eba\u55b7\u9152\uff0c\u4e5f\u8bb8\u4ed6\u4eec\u5c31\u4f1a\u9192\u8f6c\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e84\u5144\u6b64\u8a00\u5f53\u771f\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u5144\u5f1f\u53ea\u662f\u610f\u6d4b\u4e4b\u8a00\uff0c\u662f\u5426\u6709\u6548\uff0c\u5144\u5f1f\u4e5f\u6ca1\u6709\u628a\u63e1\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4e0d\u7ba1\u662f\u5426\u6709\u6548\uff0c\u8bd5\u4e0a\u4e00\u8bd5\uff0c\u90a3\u4e5f\u4e0d\u96be\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5982\u6b64\u6709\u52b3\u5e84\u5144\u4e00\u8d9f\uff0c\u54b1\u4eec\u53bb\u62ac\u4e00\u6876\u51c9\u6c34\u524d\u6765\u8bd5\u8bd5\u53ef\u597d\uff1f\u201d \u3000\u3000\u5e84\u6377\u7b11\u9053\uff1a\u201c\u5144\u5f1f\u81ea\u5f53\u6548\u52b3\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5411\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u4f60\u5728\u8fd9\u513f\u770b\u5b88\u4e00\u4e0b\u2026\u2026\u201d \u3000\u3000\u4e24\u4eba\u8f6c\u8eab\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u4e0d\u4e00\u4f1a\u513f\uff0c\u4ed6\u4eec\u5df2\u5404\u4eba\u63d0\u4e86\u4e00\u6876\u51b7\u6c34\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5411\u5b89\u5c0f\u840d\u4e00\u7b11\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u6709\u52b3\u4f60\u5728\u8fd9\u513f\u5b88\u5019\u4e00\u9635\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u65b9\u5144\u5feb\u4e9b\u53bb\u5427\uff01\u6211\u4f1a\u5f53\u5fc3\uff0c\u4e0d\u8ba9\u522b\u4eba\u524d\u6765\u6253\u6270\u7684\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u4e86\u4e00\u7b11\uff0c\u4fbf\u548c\u5e84\u6377\u63d0\u4e86\u51b7\u6c34\u5411\u90a3\u5730\u9053\u4e4b\u4e2d\u884c\u53bb\u3002 \u3000\u3000\u4e24\u4eba\u8e0f\u5165\u5730\u9053\uff0c\u53ea\u89c1\u90a3\u5c11\u6797\u638c\u95e8\u5165\u4e91\u5927\u5e08\uff0c\u6b63\u5728\u5408\u5341\u5583\u5583\u9ed8\u7977\uff0c\u4e10\u5e2e\u5e2e\u4e3b\u970d\u9e23\u98ce\u5219\u53cc\u624b\u5728\u4e4c\u8863\u795e\u53df\u5d14\u5927\u516c\u8eab\u4e0a\u63a8\u6765\u63a8\u53bb\uff0c\u663e\u7136\uff0c\u4ed6\u4eec\u82b1\u4e86\u8fd9\u4e48\u4e45\u65f6\u95f4\uff0c\u4ecd\u672a\u5c06\u90a3\u5730\u9053\u4e2d\u4eba\u6551\u9192\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u548c\u5e84\u6377\u4e24\u4eba\u63d0\u4e86\u51b7\u6c34\u5165\u5185\uff0c\u5b8b\u5b5a\u9996\u5148\u770b\u5230\uff0c\u4e0d\u7981\u76b1\u7709\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u8fd9\u662f\u4ec0\u4e48\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u665a\u8f88\u9002\u624d\u66fe\u4e0e\u5e84\u5144\u7814\u7a76\u8fc7\u4e86\uff0c\u90a3\u7ae5\u5b50\u5947\u5e76\u975e\u5584\u4e8e\u7528\u6bd2\u4e4b\u4eba\uff0c\u53ea\u6015\u4ed6\u8fd9\u79cd\u8ff7\u4eba\u7684\u6bd2\u9999\uff0c\u4e43\u662f\u6c5f\u6e56\u4e2d\u4e0b\u4e94\u95e8\u7684\u718f\u9999\u4e4b\u7c7b\uff0c\u5982\u662f\u7528\u51b7\u6c34\u4e00\u6d47\uff0c\u53ef\u4ee5\u6551\u9192\u4ed6\u4eec\uff0c\u4e5f\u672a\u53ef\u77e5\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u6709\u8fd9\u7b49\u4e8b\u5417\uff01\u54b1\u4eec\u679c\u7136\u4e0d\u59a8\u8bd5\u4e0a\u4e00\u8bd5\u8fd9\u65f6\u5165\u4e91\u5927\u5e08\u4e5f\u5f20\u5f00\u4e86\u53cc\u76ee\uff0c\u53e3\u55a7\u4f5b\u53f7\uff0c\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\uff0c\u4f60\u4eec\u624b\u4e2d\u63d0\u7684\u53ef\u5c31\u662f\u51b7\u6c34\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u6b63\u662f\u51b7\u6c34\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u63d0\u8d77\u624b\u4e2d\u90a3\u6876\u51c9\u6c34\uff0c\u4fbf\u5411\u4e10\u5e2e\u957f\u8001\u9634\u9633\u624b\u5409\u5a01\u515c\u5934\u6d47\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u5bf9\u4e8e\u8fd9\u7b49\u51b7\u6c34\u6d47\u5934\u4e4b\u4e8b\uff0c\u6ca1\u6709\u7ecf\u9a8c\uff0c\u4e00\u6876\u51c9\u6c34\uff0c\u53ea\u6d47\u4e00\u4e2a\u4eba\uff0c\u5012\u662f\u4e0d\u5acc\u6d6a\u8d39\uff01\u201d \u3000\u3000\u90a3\u970d\u9e23\u98ce\u8fd9\u65f6\u4e5f\u505c\u4e86\u63a8\u62ff\uff0c\u77aa\u5927\u4e86\u53cc\u773c\uff0c\u770b\u770b\u90a3\u5409\u5a01\u7684\u52a8\u9759\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u77ac\uff0c\u53ea\u89c1\u90a3\u5409\u5a01\u5ffd\u7136\u53d1\u51fa\u4e00\u58f0\u957f\u53f9\uff0c\u7f13\u7f13\u5730\u7741\u5f00\u53cc\u773c\uff0c\u5750\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5b8b\u5b5a\u77a7\u5f97\u62cd\u624b\u5927\u7b11\u9053\uff1a\u201c\u5999\uff01\u5999\u554a\uff01\u539f\u6765\u8fd9\u51b7\u6c34\u5f53\u771f\u80fd\u591f\u6551\u4ed6\u4eec\u2026\u2026\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u4f38\u624b\u5c06\u5e84\u6377\u624b\u4e2d\u7684\u90a3\u4e00\u6876\u51b7\u6c34\u63a5\u4e86\u8fc7\u6765\uff0c\u5c31\u5f85\u5411\u90a3\u5d14\u5927\u516c\u8fce\u5934\u6cfc\u53bb\uff01 \u3000\u3000\u5165\u4e91\u5927\u5e08\u5ffd\u7136\u4e00\u58f0\u9053\uff1a\u201c\u65bd\u4e3b\u4e14\u6162\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u638c\u95e8\u4eba\u6709\u4f55\u6307\u6559\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u8001\u65bd\u4e3b\uff0c\u6bcf\u4eba\u8138\u4e0a\u53ea\u987b\u4e00\u6367\u51b7\u6c34\u5373\u591f\uff0c\u4f60\u4e0d\u7528\u4e00\u6876\u6d47\u5728\u5d14\u65bd\u4e3b\u8eab\u4e0a\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u542c\u5f97\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8001\u673d\u4e00\u65f6\u9ad8\u5174\uff0c\u5012\u628a\u8fd9\u7b49\u4e8b\u5fd8\u8bb0\u4e86\uff01\u201d \u3000\u3000\u653e\u4e0b\u6c34\u6876\uff0c\u7b11\u5411\u5e84\u6377\u548c\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4f60\u4eec\u522b\u95f2\u7740\uff0c\u54b1\u4eec\u4e00\u9f50\u52a8\u624b\u5427\uff01\u201d \u3000\u3000\u51e0\u4e4e\u7acb\u5373\u5404\u81ea\u6367\u6c34\uff0c\u5411\u90a3\u5730\u9053\u4e2d\u7684\u50e7\u4fa3\u548c\u4e10\u5e2e\u957f\u8001\u4eec\u6d12\u53bb\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u884c\u52a8\u867d\u7136\u5f88\u5feb\uff0c\u4f46\u4f9d\u7136\u82b1\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u624d\u5c06\u90a3\u4e9b\u5c11\u6797\u95e8\u4e0b\u4e00\u4e00\u5f04\u9192\u8fc7\u6765\u3002 \u3000\u3000\u9634\u9633\u624b\u5409\u6210\u957f\u7709\u4e00\u76b1\uff0c\u76ee\u5149\u5728\u4f17\u4eba\u8eab\u4e0a\u4e00\u8f6c\uff0c\u51b7\u51b7\u559d\u9053\uff1a\u201c\u90a3\u7ae5\u5b50\u5947\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u6562\u60c5\uff0c\u4ed6\u4eec\u672a\u4e2d\u8ff7\u9999\u4e4b\u524d\uff0c\u5df2\u7136\u89c1\u5230\u90a3\u7ae5\u5b50\u5947\u4e86\uff01 \u3000\u3000\u970d\u9e23\u98ce\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u90a3\u91cd\u5b50\u5947\u5417\uff1f\u4ed6\u5df2\u88ab\u65b9\u8001\u5f1f\u542c\u4f24\uff0c\u9003\u8d70\u4e86\uff01\u201d \u3000\u3000\u5409\u5a01\u9053\uff1a\u201c\u9003\u8d70\u4e86\u5417\uff1f\u4fbf\u5b9c\u4e86\u8fd9\u53ae\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5404\u4f4d\u662f\u600e\u751f\u4e2d\u4e86\u4ed6\u7684\u8be1\u8ba1\u5462\uff1f\u770b\u6765\u8fd9\u7ae5\u5b50\u5947\u7528\u7684\u8ff7\u9999\uff0c\u679c\u7136\u662f\u4e00\u822c\u7684\u718f\u9999\u4e4b\u7c7b\u2026\u2026\u201d \u3000\u3000\u5d14\u5927\u516c\u8fd9\u65f6\u5ffd\u7136\u957f\u53f9\u9053\uff1a\u201c\u541b\u5b50\u53ef\u4ee5\u6b3a\u5176\u65b9\uff01\u8001\u592b\u7b49\u4eba\u54ea\u91cc\u4f1a\u60f3\u5230\uff0c\u4ee5\u7ae5\u5b50\u5947\u8fd9\u7b49\u8eab\u4efd\u7684\u9b54\u5934\uff0c\u4e5f\u4f1a\u73a9\u51fa\u90a3\u7b49\u4e0b\u6d41\u624b\u6bb5\u6765\u5462\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u5408\u638c\u9053\uff1a\u201c\u8001\u65bd\u4e3b\uff0c\u8fd9\u6bd2\u662f\u7ae5\u5b50\u5947\u4eb2\u624b\u4f7f\u51fa\u7684\u5417\uff1f\u201d \u3000\u3000\u6562\u60c5\u8fd9\u4f4d\u8001\u548c\u5c1a\u8fd8\u6709\u4e9b\u4e0d\u4fe1\uff01\u5b9e\u5219\uff0c\u8fd9\u672c\u4e5f\u53eb\u4eba\u5341\u5206\u96be\u4ee5\u76f8\u4fe1\u4e4b\u4e8b\uff01\u8bd5\u60f3\uff0c\u4ee5\u7ae5\u5b50\u5947\u90a3\u7b49\u4e94\u5927\u9b54\u4e3b\u8eab\u4efd\u4e4b\u4eba\uff0c\u4e00\u65e6\u4e0e\u4eba\u4e3a\u654c\uff0c\u9664\u4e86\u51ed\u6043\u4ed6\u7684\u6b66\u529f\uff0c\u8fd8\u4f1a\u7528\u4e0a\u6c5f\u6e56\u4e2d\u7684\u8ff7\u9999\uff0c\u82e5\u975e\u4ed6\u4eec\u4eb2\u773c\u6240\u89c1\uff0c\u53ea\u6015\u8c01\u4e5f\u4e0d\u4f1a\u76f8\u4fe1\u4e86\uff01 \u3000\u3000\u5d14\u5927\u516c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u7b2c\u4e00\u628a\u8ff7\u9999\u6b63\u662f\u90a3\u8001\u9b54\u5934\u81ea\u5df1\u6d12\u51fa\u51fa\u6765\uff0c\u5426\u5219\uff0c\u54b1\u4eec\u6c11\u4e0d\u4f1a\u4e2d\u4e86\u4ed6\u7684\u9053\u513f\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u5d14\u5144\uff0c\u90a3\u7ae5\u5b50\u5947\u603b\u7b97\u8fd8\u6709\u81ea\u77e5\u4e4b\u660e\uff0c\u5018\u82e5\u4ed6\u4e0d\u662f\u7528\u6bd2\uff0c\u51ed\u4ed6\u4e00\u4eba\u4e4b\u80fd\uff0c\u53c8\u600e\u4f1a\u6597\u5f97\u5c11\u6797\u8bf8\u4f4d\u5927\u5e08\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u5408\u5341\u9053\uff1a\u201c\u5e2e\u4e3b\u597d\u8bf4\uff0c\u90a3\u7ae5\u65bd\u4e3b\u82e5\u975e\u56e0\u4e3a\u89c1\u5230\u5d14\u65bd\u4e3b\u7b49\u5728\u6b64\uff0c\u8c05\u4ed6\u4e5f\u4e0d\u4f1a\u51fa\u6b64\u81ea\u6bc1\u58f0\u540d\u7684\u4e0b\u7b56\u4e86\uff01\u201d \u3000\u3000\u8fd9\u7b49\u65f6\u523b\uff0c\u4ed6\u4eec\u8fd8\u5728\u5ba2\u5957\uff0c\u53ea\u628a\u90a3\u521a\u521a\u8d70\u8fdb\u5730\u9053\u7684\u5b89\u5c0f\u840d\u542c\u5f97\u683c\u683c\u7b11\u51fa\u58f0\u6765\u3002 \u3000\u3000\u5b8b\u5b5a\u5728\u65c1\u5374\u662f\u5927\u58f0\u9053\uff1a\u201c\u4e24\u4f4d\u638c\u95e8\uff0c\u8001\u673d\u8ba4\u4e3a\uff0c\u4f60\u4eec\u4e0d\u5fc5\u518d\u5f7c\u6b64\u5ba2\u6c14\u4e86\uff01\u54b1\u4eec\u8fd8\u662f\u8d76\u5feb\u51fa\u53bb\u5427\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u4e0d\u9519\u2026\u2026\u201d \u3000\u3000\u4ed6\u7f13\u7f13\u5730\u8f6c\u4e86\u8eab\uff0c\u53ea\u89c1\u90a3\u5c11\u6797\u5b50\u5f1f\uff0c\u4e2a\u4e2a\u5782\u624b\u8083\u7acb\uff0c\u4e00\u8138\u61ca\u607c\u4e4b\u5bb9\u3002 \u3000\u3000\u663e\u7136\uff0c\u8fd9\u6570\u767e\u4f4d\u5c11\u6797\u50e7\u4fa3\uff0c\u90fd\u4e3a\u81ea\u5df1\u5931\u624b\u88ab\u90a3\u7ae5\u5b50\u5947\u5236\u670d\u4e4b\u4e8b\uff0c\u5927\u611f\u7f9e\u5fff\u96be\u5f53\u3002 \u3000\u3000\u5165\u4e91\u5927\u5e08\u53f9\u606f\u4e00\u58f0\uff0c\u4e00\u8bed\u672a\u53d1\u5730\u5927\u6b65\u5411\u5916\u884c\u53bb\u3002\u65b9\u96ea\u5b9c\u3001\u5b89\u5c0f\u840d\u3001\u970d\u9e23\u98ce\u3001\u5b8b\u5b5a\u3001\u5e84\u6377\u3001\u7fc1\u6606\u4ed1\u7b49\u4eba\uff0c\u968f\u5728\u5165\u4e91\u5927\u5e08\u8eab\u540e\uff0c\u4e5f\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u5165\u4e91\u5927\u5e08\u51fa\u4e86\u5730\u9053\u4eba\u53e3\uff0c\u4e00\u773c\u77a7\u89c1\u90a3\u5730\u4e0a\u7684\u5341\u516b\u5177\u7f57\u6c49\u5c38\u4f53\uff0c\u4e0d\u7531\u5f97\u53cc\u624b\u5408\u5341\uff0c\u8fde\u8fde\u53e3\u8bf5\u4f5b\u53f7\u4e0d\u6b62\u3002 \u3000\u3000\u5b8b\u5b5a\u7779\u72b6\uff0c\u4e5f\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u600e\u4e48\u8fd9\u4e9b\u7f57\u6c49\u7684\u4f5b\u50cf\uff0c\u5168\u90fd\u53d8\u6210\u4e86\u6d3b\u4eba\u5462\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u683c\u683c\u4e00\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u7f57\u6c49\u4f5b\u50cf\uff0c\u4f60\u6ca1\u6709\u773c\u89c1\u5417\uff0c\u4ed6\u4eec\u5168\u662f\u90a3\u7ae5\u5b50\u5947\u7684\u5c5e\u4e0b\u5440\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u8fd9\u65f6\u4e5f\u8d70\u4e86\u8fc7\u6765\uff0c\u4ed6\u9010\u4e00\u5730\u770b\u90a3\u5341\u516b\u5177\u5c38\u4f53\u4e00\u773c\uff0c\u63a5\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u90a3\u7ae5\u5b50\u5947\u60f3\u5f97\u5012\u662f\u4e0d\u9519\uff0c\u8981\u501f\u8fd9\u4e2a\u5802\uff0c\u5c06\u8d35\u5bfa\u50e7\u4f17\uff0c\u4e00\u7f51\u6253\u5c3d\uff0c\u4f46\u4ed6\u5343\u7b97\u4e07\u7b97\uff0c\u5374\u7b97\u6f0f\u4e00\u7740\uff0c\u60f3\u4e0d\u5230\u5251\u795e\u7684\u4f20\u4eba\uff0c\u5c45\u7136\u4e5f\u5728\u5c11\u6797\u5bfa\u4e2d\uff0c\u5426\u5219\uff0c\u4eca\u65e5\u7684\u7ed3\u5c40\uff0c\u53ef\u662f\u4e0d\u582a\u8bbe\u60f3\u4e86\u3002\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u5408\u5341\u9053\uff1a\u201c\u8001\u65bd\u4e3b\u8bf4\u7684\u662f\uff0c\u65b9\u5c0f\u65bd\u4e3b\u4e8e\u672c\u5bfa\u6709\u7740\u518d\u9020\u4e4b\u6069\uff0c\u5927\u5fb7\u4e0d\u6562\u8a00\u8c22\uff0c\u4eca\u540e\u53ea\u8981\u5c0f\u65bd\u4e3b\u4e00\u53e5\u8bdd\uff0c\u4e3a\u4e86\u6b66\u6797\u8fd9\u4e8b\uff0c\u54ea\u6015\u662f\u8d74\u6c64\u8e48\u706b\uff0c\u672c\u5bfa\u5b50\u5f1f\uff0c\u4ea6\u5c06\u5168\u529b\u4ee5\u8d74\u7684\u4e86\uff01\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u5fc3\u4e2d\u5927\u662f\u611f\u52a8\uff0c\u5fd9\u9053\uff1a\u201c\u638c\u95e8\u4eba\u82e5\u6709\u6b64\u5fc3\uff0c\u5b9e\u662f\u6b66\u6797\u4e4b\u798f\u2026\u2026\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u76ee\u5149\u5728\u4f17\u4eba\u8eab\u4e0a\u4e00\u8f6c\uff0c\u5ffd\u7136\u8083\u5bb9\u9053\uff1a\u201c\u4e94\u9b54\u8822\u52a8\u4e4b\u5fc3\u5df2\u73b0\uff0c\u5c24\u5176\u662f\u7ae5\u5b50\u5947\u4eca\u65e5\u65ad\u7fbd\u800c\u53bb\uff0c\u5fc5\u7136\u4e0d\u4f1a\u5c31\u6b64\u7518\u4f11\uff0c\u8001\u8872\u60f3\u8bf7\u5404\u4f4d\u518d\u53bb\u9759\u5ba4\u5c0f\u5750\uff0c\u4e5f\u597d\u5546\u8ba8\u4e00\u4e0b\u5e94\u5bf9\u4e4b\u7b56\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u7b11\u9053\uff1a\u201c\u672c\u8be5\u5982\u6b64\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u8f6c\u5934\u5411\u8eab\u540e\u7684\u5165\u6cd5\u5927\u5e08\u5631\u5490\u4e86\u51e0\u53e5\uff0c\u8fd9\u624d\u7b11\u9053\uff1a\u201c\u6b64\u95f4\u4e4b\u4e8b\uff0c\u54b1\u4eec\u4e0d\u80fd\u8fc7\u95ee\u4e86\u2026\u2026\u201d \u3000\u3000\u8bdd\u97f3\u672a\u5df2\uff0c\u5b89\u5c0f\u840d\u5df2\u7136\u7b11\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u8d35\u5bfa\u90a3\u5341\u516b\u5c0a\u4f5b\u7956\u50cf\u5462\uff1f\u83ab\u975e\u88ab\u90a3\u7ae5\u5b50\u5947\u5077\u8d70\u4e86\u5417\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u6447\u5934\u9053\uff1a\u201c\u6ca1\u6709\uff01\u7ae5\u5b50\u5947\u53ea\u662f\u5c06\u90a3\u7f57\u6c49\u4f5b\u7956\u7684\u5723\u50cf\uff0c\u79fb\u5230\u5730\u9053\u4e4b\u4e2d\u53bb\u4e86\uff01 \u3000\u3000\u8001\u8872\u5df2\u7136\u8981\u5bfa\u4e2d\u5f1f\u5b50\u91cd\u65b0\u88c5\u4fee\uff0c\u591a\u8c22\u5c0f\u65bd\u4e3b\u5173\u6000\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u77e5\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u2026\u2026\u6211\u53ea\u77e5\u9053\u90a3\u7ae5\u5b50\u5947\u770b\u4e0a\u4e86\u90a3\u4f5b\u50cf\u91d1\u8eab\uff0c\u5077\u652f\u6362\u4e86\u94f6\u5b50\u5462\uff01\u201d \u3000\u3000\u5979\u8fd9\u4e48\u4e00\u8bf4\uff0c\u628a\u5728\u573a\u7684\u8001\u4eba\uff0c\u5168\u90e8\u8bf4\u7684\u7b11\u4e86\u3002 \u3000\u3000\u5165\u4e91\u5927\u5e08\u5408\u5341\u4e00\u793c\uff0c\u9053\u4e00\u58f0\uff1a\u201c\u8001\u8872\u4e3a\u5404\u4f4d\u9886\u8def\u2026\u2026\u201d \u3000\u3000\u4e3e\u6b65\u5f53\u5148\u51fa\u4e86\u7f57\u6c49\u5802\u5927\u95e8\u3002 \u3000\u3000\u4e00\u884c\u4eba\u9c7c\u8d2f\u76f8\u968f\uff0c\u5411\u90a3\u65b9\u4e08\u9759\u5ba4\u8d70\u53bb\u3002 \u3000\u3000\u539f\u6765\u5341\u5206\u9759\u5bc2\u5c11\u6797\uff0c\u8fd9\u65f6\u663e\u5f97\u5341\u5206\u70ed\u95f9\u8d77\u6765\u4e86\uff01\u51e0\u540d\u5c0f\u6c99\u5f25\uff0c\u5954\u8d70\u4e0d\u505c\uff0c\u4e0d\u6d88\u4e00\u77ac\u65f6\u5149\uff0c\u5c31\u5728\u90a3\u65b9\u4e08\u5ba4\u4e2d\uff0c\u6446\u4e0b\u4e86\u4e00\u684c\u7d20\u5e2d\u3002 \u3000\u3000\u5165\u4e91\u5927\u5e08\u8bf7\u90a3\u65b9\u96ea\u5b9c\u5750\u4e86\u9996\u4f4d\uff0c\u867d\u7136\u65b9\u96ea\u5b9c\u4e00\u518d\u63a8\u62d6\uff0c\u7ec8\u4e8e\u8fd8\u662f\u5374\u4e0d\u8fc7\u5165\u4e91\u5927\u5e08\u7684\u76db\u610f\uff0c\u5750\u4e86\u9996\u5e2d\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u7d27\u65c1\u5728\u65b9\u96ea\u5b9c\u7684\u8eab\u8fb9\u3002 \u3000\u3000\u5750\u5b9a\u4ee5\u540e\uff0c\u5165\u4e91\u5927\u5e08\u4ee5\u4e3b\u4eba\u8eab\u4efd\uff0c\u656c\u4e86\u5728\u5ea7\u8bf8\u4eba\u4e00\u676f\uff0c\u8fd9\u624d\u957f\u957f\u5730\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u672c\u5bfa\u4eca\u65e5\u906d\u9022\u767e\u5e74\u6765\u672a\u6709\u7684\u60e8\u53d8\uff0c\u82e5\u975e\u51e0\u4f4d\u65bd\u4e3b\u76f8\u52a9\uff0c\u8001\u8872\u771f\u4e0d\u77e5\u7ed3\u5c40\u4f1a\u662f\u4ec0\u4e48\u6837\u5b50\u4e86\u2026\u2026\u201d \u3000\u3000\u8bed\u97f3\u5fae\u5fae\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4e94\u5927\u9b54\u4e3b\uff0c\u5148\u540e\u51fa\u73b0\uff0c\u53ea\u6240\u4ed6\u4eec\u5f53\u771f\u662f\u4e0d\u518d\u628a\u5251\u795e\u9648\u5927\u4fa0\u7684\u544a\u8beb\u653e\u5728\u5fc3\u4e2d\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u53cc\u7709\u4e00\u626c\uff0c\u5927\u58f0\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u90a3\u7ae5\u5b50\u5947\u7684\u53e3\u6c14\uff0c\u4f3c\u662f\u5bf9\u4e8e\u9648\u5927\u4fa0\u6ba1\u5929\u4e4b\u4e8b\uff0c\u5148\u524d\u5e76\u4e0d\u77e5\u6653\uff0c\u4f46\u4ed6\u4eec\u5374\u5728\u4e0d\u77e5\u9648\u5927\u4fa0\u751f\u6b7b\u7684\u60c5\u51b5\u4e0b\uff0c\u5c31\u6562\u5927\u80c6\u5984\u4e3a\uff0c\u8db3\u89c1\u4ed6\u4eec\u65e9\u5c31\u6ca1\u6709\u628a\u9648\u5927\u4fa0\u7684\u8a00\u8bed\uff0c\u653e\u5728\u5fc3\u4e2d\u4e86\uff01\u638c\u95e8\u4eba\u8fd8\u60f3\u6307\u671b\u4ed6\u4eec\u4e0d\u66ff\u6b66\u6797\u5e26\u6765\u7eb7\u6270\uff0c\u5c82\u975e\u662f\u75f4\u5fc3\u5984\u60f3\u5417\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u6c89\u58f0\u9053\uff1a\u201c\u5e2e\u4e3b\u8bf4\u5f97\u4e0d\u9519\uff0c\u8001\u8872\u679c\u7136\u662f\u592a\u628a\u4ed6\u4eec\u5f53\u4f5c\u6b63\u4eba\u541b\u5b50\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u8fd9\u65f6\u5ffd\u7136\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\uff0c\u8001\u673d\u6709\u4e00\u53e5\u8bdd\uff0c\u4e0d\u77e5\u5f53\u8bf4\u4e0d\u5f53\u8bf4\uff1f\u201d \u3000\u3000\u970d\u545c\u98ce\u5927\u7b11\u9053\uff1a\u201c\u5b8b\u5144\u6709\u8bdd\u8bf7\u8bf4\u4fbf\u662f\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u4e5f\u9053\uff1a\u201c\u65bd\u4e3b\u6709\u4ec0\u4e48\u8bdd\u4e0d\u597d\u8bf4\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u8001\u673d\u53ea\u662f\u89c9\u5f97\uff0c\u4e94\u5927\u9b54\u4e3b\u6b64\u756a\u53d1\u52a8\uff0c\u9996\u5148\u628a\u76ee\u5149\u6307\u5411\u5c11\u6797\uff0c\u5fc5\u7136\u6709\u7740\u5f88\u6df1\u7684\u7528\u610f\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u4ec0\u4e48\u7528\u610f\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8001\u673d\u8bf4\u51fa\uff0c\u638c\u95e8\u4eba\u53ef\u83ab\u8981\u89c1\u602a\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u600e\u4e48\u4f1a\uff1f\u65bd\u4e3b\u5feb\u8bf4\u5427\uff01\u201d \u3000 \u7b2c\u4e8c\u5341\u516b\u56de\u3000\u4efb\u91cd\u9053\u8fdc \u3000 \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u6740\u9e21\u656c\u7334\uff0c\u5b58\u5fc3\u5f81\u670d\u6b66\u6797\u4e5d\u5927\u95e8\u6d3e\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u2026\u2026\u91ce\u5fc3\u4e0d\u5c0f\u554a\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u8fd9\u65f6\u4e5f\u4e24\u773c\u4e00\u77aa\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4ed6\u4eec\u4e5f\u914d\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u8c01\u914d\u4e0d\u914d\u7684\u5173\u7cfb\uff0c\u4e24\u4f4d\u82e5\u662f\u4e0d\u4fe1\u4f55\u4e0d\u95ee\u95ee\u8fd9\u4f4d\u5e84\u8001\u5f1f\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u53cc\u76ee\u4e00\u8f6c\u9053\uff1a\u201c\u5f53\u771f\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u770b\u4e86\u5e84\u6377\u4e00\u773c\uff0c\u9053\uff1a\u201c\u65bd\u4e3b\u53ef\u662f\u7ae5\u5b50\u5947\u7684\u95e8\u4e0b\uff1f\u201d \u3000\u3000\u5e84\u6377\u5fae\u5fae\u4e00\u7b11\uff0c\u62b1\u62f3\u9053\uff1a\u201c\u665a\u751f\u5e76\u975e\u771f\u662f\u90a3\u7ae5\u5b50\u5947\u7684\u95e8\u4e0b\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u8001\u5f1f\u5f88\u795e\u79d8\u554a\uff01\u201d \u3000\u3000\u5e84\u6377\u7b11\u9053\uff1a\u201c\u665a\u751f\u4e43\u662f\u5949\u547d\u6253\u5165\u4e09\u4e49\u95e8\u4e2d\uff0c\u90a3\u7ae5\u5b50\u5947\u867d\u7136\u662f\u6210\u7384\u901a\u7b49\u4eba\u7684\u5e55\u540e\u652f\u6301\u4e4b\u4eba\uff0c\u4f46\u665a\u751f\u5374\u7b97\u4e0d\u5f97\u662f\u4ed6\u4eec\u7684\u95e8\u4e0b\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u5408\u5341\u9053\uff1a\u201c\u65bd\u4e3b\u5949\u4e86\u4f55\u4eba\u4e4b\u547d\uff0c\u6253\u5165\u90a3\u4e09\u4e49\u95e8\u4e2d\uff1f\u201d \u3000\u3000\u5e84\u6377\u95fb\u8a00\uff0c\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u65b9\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u665a\u8f88\u7684\u51fa\u8eab\uff0c\u8bf4\u5c06\u51fa\u6765\uff0c\u53ea\u6015\u4e24\u4f4d\u638c\u95e8\u4eba\u6709\u4e9b\u4e0d\u4fe1\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u7b11\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u665a\u751f\u4e43\u662f\u8ba1\u901a\u7684\u5e08\u5f1f\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u95fb\u8a00\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8ba1\u901a\uff0c\u53ef\u662f\u90a3\u6bd2\u5927\u592b\u6076\u5b54\u660e\u4e48\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u5927\u4e3a\u610f\u5916\u5730\u4e00\u6014\u9053\uff1a\u201c\u90a3\u6076\u5b54\u660e\u51e0\u65f6\u4f1a\u6709\u4e86\u4e00\u4f4d\u5e08\u5f1f\u6765\u4e86\u3002\u201d \u3000\u3000\u5e84\u6377\u7b11\u9053\uff1a\u201c\u5e2e\u4e3b\u53ef\u662f\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u6b63\u662f\uff0c\u8001\u592b\u4e0d\u4fe1\u90a3\u8ba1\u901a\u7adf\u4f1a\u8fd8\u6709\u50cf\u4f60\u8fd9\u7b49\u5e74\u8f7b\u7684\u5e08\u5f1f\uff01\u201d \u3000\u3000\u5e84\u6377\u7b11\u9053\uff1a\u201c\u5e2e\u4e3b\u4e0d\u4fe1\uff0c\u665a\u751f\u4e5f\u6ca1\u6709\u529e\u6cd5\uff0c\u4e0d\u8fc7\uff0c\u5e2e\u4e3b\u53ef\u4ee5\u8bbe\u60f3\u5f97\u5230\uff0c\u9664\u4e86\u50cf\u6211\u5e08\u5144\u90a3\u7b49\u4eba\uff0c\u53c8\u6709\u8c01\u80fd\u4f7f\u5144\u5f1f\u6df7\u5165\u7ae5\u5b50\u5947\u624b\u4e0b\uff0c\u800c\u4e0d\u4e3a\u4ed6\u6240\u89c9\u3002\u201d \u3000\u3000\u970d\u9e23\u98ce\u53f9\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\u3002\u201d \u3000\u3000\u4ed6\u76b1\u4e86\u4e00\u76b1\u7709\u5934\uff0c\u63a5\u9053\uff1a\u201c\u5e84\u8001\u5f1f\uff0c\u4f60\u90a3\u5e08\u5c0a\u662f\u54ea\u4e00\u4f4d\u9ad8\u4eba\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u5bb6\u5e08\u795d\u9ad8\u67ab\uff0c\u4eba\u79f0\u5929\u5357\u602a\u53df\uff01\u201d \u3000\u3000\u4ed6\u8bdd\u97f3\u4e00\u843d\uff0c\u53ea\u628a\u5165\u4e91\u5927\u5e08\u548c\u970d\u9e23\u98ce\u542c\u5f97\u5927\u5927\u4e00\u60ca\u3002 \u3000\u3000\u6562\u60c5\u90a3\u795d\u9ad8\u67ab\u4e4b\u540d\uff0c\u5728\u6b66\u6797\u4e4b\u4e2d\uff0c\u751a\u4e2d\u54cd\u4eae\uff0c\u800c\u4e14\uff0c\u6b64\u4eba\u9ad8\u8e48\u81ea\u9690\uff0c\u4ece\u4e0d\u5e72\u9884\u6c5f\u6e56\u4e8b\u52a1\uff0c\u60f3\u4e0d\u5230\u6b64\u4eba\u7adf\u662f\u90a3\u4e94\u5927\u9b54\u4e3b\u4e2d\u7684\u6076\u5b54\u660e\u7684\u5e08\u7236\uff01\u201d \u3000\u3000\u4f46\u662f\u65b9\u96ea\u5b9c\u3001\u5b89\u5c0f\u840d\u4e24\u4eba\uff0c\u5bf9\u4e8e\u90a3\u795d\u9ad8\u67ab\u4e4b\u540d\uff0c\u5374\u662f\u4ece\u6765\u672a\u542c\u4eba\u63d0\u8fc7\uff0c\u5012\u4e5f\u4e0d\u66fe\u6709\u4f55\u60ca\u8bb6\u4e4b\u5904\uff01 \u3000\u3000\u8fd9\u65f6\u5b8b\u5b5a\u5374\u634f\u7740\u90a3\u516b\u5b57\u5c0f\u80e1\uff0c\u7b11\u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5e84\u8001\u5f1f\uff0c\u4ee4\u5e08\u73b0\u5728\u4f55\u5904\uff1f\u201d \u3000\u3000\u5e84\u6377\u7b11\u9053\uff1a\u201c\u7ec8\u5357\u5c71\u4e2d\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u53cc\u7709\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u90a3\u795d\u8001\u4ecd\u5728\u7ec8\u5357\u9690\u5c45\u5417\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u5bb6\u5e08\u4e00\u5411\u4f4f\u7ec8\u5357\uff0c\u4ece\u672a\u4ed6\u53bb\u3002\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u53cc\u624b\u5408\u5341\uff0c\u53e3\u5ba3\u4f5b\u53f7\u9053\uff1a\u201c\u795d\u8001\u65bd\u4e3b\uff0c\u4e00\u4ee3\u5927\u4fa0\uff0c\u600e\u4f1a\u6536\u4e86\u8ba1\u901a\u90a3\u7b49\u5f1f\u5b50\uff0c\u8001\u8872\u6709\u4e9b\u96be\u4ee5\u76f8\u4fe1\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u63a5\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u90a3\u6076\u5b54\u660e\u7684\u4e3a\u4eba\uff0c\u663e\u7136\u5341\u5206\u72e0\u6bd2\uff0c\u5fc3\u8ba1\u4e5f\u8fc7\u4eba\u4e00\u7b79\uff0c\u4f46\u636e\u8001\u673d\u6240\u77e5\uff0c\u4ed6\u4f3c\u662f\u5c1a\u672a\u6709\u8fc7\u771f\u6b63\u4f24\u5929\u5bb3\u7406\u7684\u884c\u4e3a\uff0c\u662f\u4ee5\uff0c\u4ed6\u867d\u662f\u88ab\u4eba\u5217\u4e3a\u4e94\u5927\u9b54\u4e3b\u4e4b\u4e00\uff0c\u4e0d\u8fc7\u662f\u56e0\u4e3a\u4ed6\u4e56\u50fb\u5f97\u592a\u8fc7\u5206\u800c\u5df2\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u5b8b\u65bd\u4e3b\u4e4b\u610f\uff0c\u90a3\u8ba1\u901a\u5e76\u4e0d\u662f\u574f\u4eba\u4e86\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u4e0d\u5c3d\u7136\uff0c\u8ba1\u901a\u662f\u574f\u4eba\uff0c\u4f46\u5e76\u975e\u5341\u8db3\u6076\u4e0d\u53ef\u8d66\u7684\u574f\u4eba\u800c\u5df2\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u4f9d\u4f60\u4e4b\u89c1\uff0c\u54b1\u4eec\u53ef\u4ee5\u628a\u6bd2\u5927\u592b\u4e4b\u540d\uff0c\u81ea\u4e94\u9b54\u4e2d\u5254\u9664\u7684\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u5e2e\u4e3b\u5148\u83ab\u8981\u610f\u6c14\uff0c\u8001\u673d\u53ea\u4e0d\u8fc7\u662f\u5c31\u4e8b\u8bba\u4e8b\u800c\u5df2\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u65f6\u5fcd\u4e0d\u4f4f\u7b11\u9053\uff1a\u201c\u5e84\u5144\uff0c\u4ee4\u5e08\u60f3\u5fc5\u662f\u6b66\u6797\u7684\u524d\u8f88\u9ad8\u4eba\u4e86\uff01\u201d \u3000\u3000\u5e84\u6377\u5bf9\u4e8e\u522b\u4eba\uff0c\u90fd\u4e0d\u65f6\u6d41\u9732\u51fa\u4e00\u4efd\u50b2\u610f\uff0c\u552f\u72ec\u5bf9\u65b9\u96ea\u5b9c\uff0c\u5374\u662f\u5341\u5206\u5c0a\u656c\uff0c\u95fb\u8a00\u7b11\u9053\uff1a \u3000\u3000\u201c\u65b9\u5144\u597d\u8bf4\uff0c\u5bb6\u5e08\u5982\u4e0e\u5251\u795e\u9648\u5927\u4fa0\u7684\u540d\u671b\u76f8\u8f83\uff0c\u90a3\u5c31\u5982\u8150\u5149\u8424\u5149\uff0c\u4e0d\u4f1a\u548c\u7693\u6708\u4e89\u8f89\u7684\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u51b3\u6765\u6599\u5230\u4ed6\u4f1a\u5982\u6b64\u7684\u8c26\u900a\uff0c\u5f53\u4e0b\u76b1\u7709\u9053\uff1a\u201c\u5e84\u5144\uff0c\u4f60\u592a\u89c1\u5916\u4e86\uff01\u201d \u3000\u3000\u5e84\u6377\u7b11\u9053\uff1a\u201c\u65b9\u5144\uff0c\u5144\u5f1f\u8bf4\u7684\u4e43\u662f\u5b9e\u8a00\uff0c\u5728\u5ea7\u7684\u51e0\u4f4d\u524d\u8f88\uff0c\u5fc5\u7136\u660e\u767d\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e84\u5144\u8a00\u91cd\u4e86\u3002\u201d \u3000\u3000\u8bed\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u795d\u8001\u5927\u540d\uff0c\u5144\u5f1f\u867d\u7136\u5148\u524d\u672a\u542c\u4eba\u8bf4\u8fc7\uff0c\u4f46\u4ece\u4e24\u4f4d\u638c\u95e8\u4e2d\uff0c\u5144\u5f1f\u5df2\u53ef\u660e\u767d\u4e0d\u5c11\u2026\u2026\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u65b9\u5144\uff0c\u6709\u5173\u5bb6\u5e08\u7684\u4e00\u5207\uff0c\u4e16\u4eba\u77e5\u9053\u7684\u5e76\u4e0d\u591a\uff0c\u4f46\u5144\u5f1f\u53ea\u80fd\u8bf4\u4e00\u53e5\uff0c\u4ed6\u8001\u4eba\u5bb6\u7684\u5fc3\u5730\uff0c\u5341\u5206\u5584\u826f\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5144\u5f1f\u76f8\u4fe1\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u8fd9\u65f6\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5e84\u8001\u5f1f\uff0c\u4ee4\u5e08\u5bf9\u4e8e\u8ba1\u901a\u7684\u884c\u4e3a\uff0c\u53ef\u66fe\u6709\u8fc7\u8033\u95fb\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u5bb6\u5e08\u65e9\u5c31\u77e5\u6653\uff0c\u53ea\u662f\u2026\u2026\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u6c89\u541f\u4e0d\u8bed\uff0c\u957f\u957f\u5730\u53f9\u4e86\u4e00\u53e3\u6c14\uff01 \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u600e\u4e48\uff0c\u795d\u8001\u65bd\u4e3b\uff0c\u65e9\u5c31\u77e5\u9053\u90a3\u8ba1\u901a\u7684\u884c\u4e3a\u4e86\uff1f\u8001\u8872\u5947\u602a\u7684\u662f\uff0c\u4ee4\u5e08\u4e3a\u4f55\u4e0d\u51fa\u624b\u5236\u6b62\u4e8e\u4ed6\u5462\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u4e5f\u9053\uff1a\u201c\u662f\u554a\uff0c\u8fd9\u5c31\u662f\u4ee4\u5e08\u7684\u5927\u9519\u4e86\u3002\u201d \u3000\u3000\u5e84\u6377\u957f\u957f\u5730\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u2026\u2026\u5bb6\u5e08\u5df2\u662f\u5fc3\u6709\u4f59\uff0c\u800c\u529b\u4e0d\u8db3\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u542c\u5f97\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u56e0\u4e3a\u2026\u2026\u5bb6\u5e08\u4e00\u8eab\u6b66\u529f\uff0c\u5269\u4e0b\u5341\u4e4b\u4e94\u516d\uff0c\u4e0d\u662f\u5927\u5e08\u5144\u7684\u5bf9\u624b\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5e84\u5144\uff0c\u795d\u8001\u53ef\u662f\u53d7\u4e86\u4ec0\u4e48\u4eba\u7684\u6697\u7b97\u4e86\u3002\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u76b1\u7709\u9053\uff1a\u201c\u5e84\u8001\u5f1f\uff0c\u4f60\u8fd9\u8bdd\u5f53\u771f\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u53e5\u53e5\u5b9e\u8a00\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u6697\u7b97\u4e86\u4ee4\u5e08\uff1f\u201d \u3000\u3000\u5e84\u6377\u7adf\u7136\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5fcd\u4e0d\u4f4f\u5927\u58f0\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u5417\uff1f\u5929\u4e0b\u54ea\u6709\u8fd9\u79cd\u4e0d\u901a\u7684\u9053\u7406\uff1f\u201d \u3000\u3000\u5e84\u6377\u4e00\u6014\u9053\uff1a\u201c\u4f60\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u600e\u6837\uff1f\u53ef\u662f\u8ba4\u4e3a\u6211\u8bf4\u9519\u4e86\u5417\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u5e84\u67d0\u975e\u662f\u6b64\u610f\uff0c\u5728\u4e0b\u4e0d\u660e\u767d\u5144\u53f0\u6240\u6307\u4e0d\u901a\u7684\u9053\u7406\uff0c\u662f\u54ea\u4e00\u70b9\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u5982\u662f\u53d7\u4e86\u4ed6\u4eba\u6697\u7b97\uff0c\u54ea\u6709\u4e0d\u77e5\u9053\u7684\u9053\u7406\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u5728\u4e0b\u8bf4\u7684\u4e43\u662f\u5b9e\u8bdd\uff0c\u5bb6\u5e08\u6b63\u662f\u56e0\u4e3a\u4e0d\u77e5\u9053\u90a3\u6697\u7b97\u4e4b\u4eba\u662f\u8c01\uff0c\u624d\u4f1a\u4e00\u76f4\u65e0\u6cd5\u6cbb\u597d\u4f24\u52bf\uff01\u5426\u5219\u5bb6\u5e08\u4e5f\u4e0d\u4f1a\u62d6\u5230\u5982\u4eca\uff0c\u4f9d\u7136\u4e0d\u6562\u51fa\u5c71\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u4f60\u90a3\u5e08\u7236\u8fde\u662f\u5426\u8ddf\u4eba\u7ed3\u4ec7\u6028\uff0c\u90fd\u8bb0\u4e0d\u5f97\u4e86\u5417\uff1f\u201d \u3000\u3000\u5e84\u6377\u6447\u5934\u9053\uff1a\u201c\u5bb6\u5e08\u4e3a\u4eba\u5341\u5206\u5e73\u6de1\uff0c\u751f\u5e73\u4e4b\u4e2d\uff0c\u5e76\u672a\u4e0e\u4eba\u6709\u8fc7\u6069\u6028\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u4ee4\u5e08\u662f\u4e2a\u597d\u4eba\u554a\uff01\u201d \u3000\u3000\u5e84\u6377\u5c1a\u672a\u8bf4\u8bdd\uff0c\u90a3\u5b8b\u5b5a\u5df2\u7136\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u90a3\u795d\u5144\u679c\u7136\u7b97\u5f97\u662f\u4e00\u4f4d\u5927\u597d\u4eba\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u5b8b\u8001\u8fd9\u4e48\u8bf4\uff0c\u90a3\u662f\u4e0d\u4f1a\u9519\u7684\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u770b\u4e86\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u7b11\u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u65b9\u5144\u6211\u5e76\u975e\u4e0d\u4fe1\u554a\uff01\u2026\u2026\u53ea\u662f\uff0c\u6211\u6000\u7591\u90a3\u6bd2\u5927\u592b\uff0c\u4f1a\u4e0d\u4f1a\u5c31\u662f\u6697\u7b97\u4ed6\u81ea\u5df1\u5e08\u7236\u4e4b\u4eba\uff01\u201d \u3000\u3000\u5e84\u6377\u95fb\u8a00\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u90a3\u600e\u4e48\u53ef\u80fd\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e5f\u76b1\u7709\u9053\uff1a\u201c\u662f\u554a\uff01\u60f3\u90a3\u6bd2\u5927\u592b\u5fc3\u80a0\u518d\u72e0\uff0c\u5927\u6982\u4e5f\u4e0d\u4f1a\u5bf9\u81ea\u5df1\u7684\u5e08\u7236\u4e0b\u624b\u5427\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u90a3\u53ef\u4e0d\u4e00\u5b9a\uff0c\u4e00\u4e2a\u4eba\u5982\u662f\u6627\u4e86\u826f\u5fc3\uff0c\u53c8\u6709\u4ec0\u4e48\u4e8b\u505a\u4e0d\u51fa\u6765\u5462\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u5408\u5341\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\uff0c\u8fd9\u7b49\u4e8b\u6765\u89c1\u660e\u8bc1\u4e4b\u5148\uff0c\u4e0d\u53ef\u4e71\u8bf4\u554a\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u7ffb\u4e00\u7ffb\u773c\uff0c\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u8001\u673d\u7684\u770b\u6cd5\uff0c\u53ea\u6015\u4e0e\u4f60\u4e0d\u540c\u3002\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u5e2e\u4e3b\u6709\u4f55\u9ad8\u89c1\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u8001\u673d\u8ba4\u4e3a\uff0c\u8ba1\u901a\u53ef\u80fd\u505a\u5f97\u51fa\u8fd9\u79cd\u4e8b\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u76b1\u7709\u9053\uff1a\u201c\u4eba\u4f26\u5927\u9053\uff0c\u5c82\u53ef\u4e0d\u987e\uff0c\u5e2e\u4e3b\uff0c\u8001\u8872\u8ba4\u4e3a\u4ec0\u65bd\u4e3b\u4e0d\u4f1a\u8fd9\u7b49\u81ea\u7edd\u4e8e\u5929\u4e0b\u4eba\u4e4b\u524d\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5ffd\u7136\u51b7\u7b11\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u5982\u679c\u4e00\u4e2a\u4eba\u88ab\u90a3\u540d\u5229\u5fc3\u6240\u6627\uff0c\u4ec0\u4e48\u4e8b\u90fd\u80fd\u5e72\u5f97\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u5979\u867d\u7136\u5e74\u5c81\u751a\u8f7b\uff0c\u4f46\u56e0\u81ea\u5df1\u7236\u6bcd\u7684\u906d\u9047\u4e0d\u540c\u5e73\u51e1\uff0c\u662f\u4ee5\u611f\u6168\u826f\u591a\uff0c\u5bf9\u4e8e\u4eba\u6027\u4e4b\u6076\uff0c\u6709\u7740\u4e0d\u540c\u5bfb\u5e38\u7684\u770b\u6cd5\u3002 \u3000\u3000\u5165\u4e91\u5927\u5e08\u88ab\u5979\u8bf4\u5f97\u6123\u4e86\u4e00\u6123\uff0c\u9053\uff1a\u201c\u5018\u82e5\u771f\u662f\u5982\u6b64\uff0c\u8001\u8872\u771f\u8981\u4e3a\u6b66\u6797\u53ef\u60b2\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u51b0\u51bb\u4e09\u5c3a\uff0c\u975e\u4e00\u65e5\u4e4b\u5bd2\uff0c\u8d35\u5bfa\u8fd9\u4e9b\u5e74\u6765\uff0c\u9664\u4e86\u5165\u51e1\u5927\u5e08\u4e00\u4eba\u800c\u5916\uff0c\u751a\u5c11\u4eba\u5728\u6c5f\u6e56\u8d70\u52a8\uff0c\u4f3c\u8fd9\u7b49\u95ed\u5173\u81ea\u4fdd\u7684\u4e3e\u63aa\uff0c\u5f71\u54cd\u6240\u53ca\uff0c\u5c82\u4e0d\u662f\u6b63\u597d\u6388\u90a3\u90aa\u9b54\u5916\u9053\u4ee5\u53ef\u4e58\u4e4b\u673a\u5417\uff1f\u638c\u95e8\u4eba\u4e3a\u6b66\u6797\u53ef\u60b2\uff0c\u90a3\u4e5f\u662f\u7406\u6240\u5f53\u7136\u7684\u4e86\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u957f\u53f9\u4e00\u58f0\u9053\uff1a\u201c\u5e2e\u4e3b\uff0c\u8001\u8872\u7b49\u672c\u662f\u4f5b\u95e8\u5f1f\u5b50\uff0c\u6212\u5173\u6212\u55d4\u4e4b\u5fc3\uff0c\u5e38\u5728\u80f8\u5934\uff0c\u4e0d\u6562\u5377\u5165\u6c5f\u6e56\u662f\u975e\uff0c\u90a3\u4e5f\u662f\u4e07\u4e0d\u5f97\u5df2\u4e4b\u4e8b\u2026\u2026\u5e2e\u4e3b\u5982\u6b64\u76f8\u8d23\uff0c\u5b9e\u4ee4\u8001\u8872\u6709\u7740\u8fdb\u9000\u4e24\u96be\u4e4b\u82e6\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u53cc\u7709\u4e00\u626c\uff0c\u5475\u5475\u5927\u7b11\u9053\uff1a\u201c\u5230\u4e86\u8fd9\u7b49\u65f6\u523b\uff0c\u638c\u95e8\u4eba\u597d\u4f3c\u5c1a\u672a\u9886\u609f\u554a\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u8001\u8872\u2026\u2026\u5e76\u975e\u6ca1\u6709\u9886\u609f\uff0c\u53ea\u662f\u4e8b\u60c5\u53d8\u5316\u5f97\u592a\u5feb\uff0c\u4f7f\u8001\u8872\u6709\u7740\u63aa\u624b\u4e0d\u53ca\u4e4b\u611f\uff01\u201d \u3000\u3000\u9634\u9633\u624b\u5409\u5a01\u5ffd\u7136\u63a5\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u5c11\u6797\u5bfa\u4e00\u5411\u4e3a\u6b66\u6797\u4e2d\u7684\u6cf0\u5c71\u5317\u6597\uff0c\u4e5d\u5927\u95e8\u6d3e\u4e2d\u7684\u5b50\u5f1f\uff0c\u5927\u534a\u4ee5\u8d35\u5bfa\u4e3e\u52a8\u9a6c\u9996\u662f\u77bb\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u8d35\u5bfa\u867d\u7136\u751a\u5c11\u8fc7\u95ee\u6b66\u6797\u4e2d\u4e8b\uff0c\u4f46\u56e0\u6709\u4e86\u5251\u795e\u9648\u5927\u4fa0\u4e00\u624b\u6491\u5929\uff0c\u624d\u4f7f\u6c5f\u6e56\u4e0a\u52c9\u5f3a\u7ef4\u6301\u4e86\u4e00\u4e2a\u5e73\u5b89\u7684\u5c40\u9762\uff0c\u5982\u4eca\u9648\u5927\u4fa0\u4e1a\u5df2\u4ea1\u6545\uff0c\u9648\u5927\u4fa0\u7684\u4f20\u4eba\u65b9\u8001\u5f1f\u6b66\u529f\u867d\u9ad8\uff0c\u4f46\u7a76\u7adf\u5e74\u8f7b\uff0c\u4ee5\u4ed6\u4e00\u4eba\u4e4b\u529b\uff0c\u5982\u60f3\u633d\u6b64\u72c2\u6f9c\uff0c\u90a3\u53ef\u662f\u5f3a\u4eba\u6240\u96be\u5f97\u5f88\uff0c\u662f\u4ee5\uff0c\u5409\u5a01\u8ba4\u4e3a\uff0c\u5927\u5e08\u5e94\u662f\u51fa\u9762\u53f7\u53ec\u6b66\u6797\u540c\u9053\u7684\u65f6\u5019\u4e86\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u95fb\u8a00\u5fc3\u4e2d\u5927\u5927\u4e00\u9707\uff0c\u6697\u9053\uff1a\u201c\u679c\u7136\u662f\u4e86\uff01\u770b\u6765\u8001\u8872\u975e\u5f97\u51fa\u9762\u4e0d\u53ef\u4e86\u2026\u2026\u201d \u3000\u3000\u4ed6\u5bfb\u601d\u4e4b\u95f4\uff0c\u8033\u4e2d\u542c\u5f97\u90a3\u5e84\u6377\u5927\u58f0\u9053\uff1a\u201c\u542c\u4e86\u8fd9\u4f4d\u5b89\u5144\u5f1f\u7684\u4e00\u756a\u8bdd\uff0c\u4f7f\u5f97\u5e84\u67d0\u8305\u585e\u5927\u5f00\uff0c\u51e0\u4f4d\u524d\u8f88\u8bf7\u6055\u5e84\u67d0\u4e4b\u7f6a\uff0c\u4e0d\u80fd\u518d\u5728\u5c11\u6797\u505c\u7559\u4e86\uff01\u201d \u3000\u3000\u53ea\u89c1\u4ed6\u7ad9\u4e86\u8d77\u6765\uff0c\u62b1\u62f3\u5411\u65b9\u96ea\u5b9c\u548c\u5b89\u5c0f\u840d\u4e00\u793c\uff0c\u9053\uff1a\u201c\u5144\u5f1f\u8981\u8d76\u5f80\u6d1b\u5357\uff0c\u53e9\u89c1\u6069\u5e08\uff0c\u67e5\u660e\u4e00\u5207\u4e4b\u540e\uff0c\u5144\u5f1f\u81ea\u4f1a\u518d\u51fa\u6b66\u6797\uff0c\u5b89\u5144\u6307\u70b9\u4e4b\u9645\uff0c\u5144\u5f1f\u53ea\u597d\u5bb9\u56fe\u540e\u62a5\u4e86\u3002\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u5927\u6b65\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5251\u7709\u8038\u52a8\uff0c\u4f3c\u662f\u6709\u8bdd\u8981\u8bb2\uff0c\u4f46\u4ed6\u773c\u770b\u5e84\u6377\u5df2\u7136\u51fa\u4e86\u623f\u95e8\uff0c\u5f53\u4e0b\u53ea\u662f\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5e84\u5144\u6162\u8d70\uff0c\u6055\u5144\u5f1f\u4e0d\u9001\u4e86\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5e84\u6377\u5df2\u7136\u8d70\u7684\u4e0d\u89c1\u8e2a\u5f71\u3002\u970d\u9e23\u98ce\u7b11\u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u795d\u9ad8\u67ab\u4e00\u8eab\u6b66\u529f\u867d\u7136\u6bd4\u4e0d\u4e0a\u5251\u795e\u9648\u5927\u4fa0\uff0c\u4f46\u4ed6\u90a3\u624b\u6b67\u9ec4\u4e4b\u672f\u53ef\u79f0\u5929\u4e0b\u72ec\u6b65\uff0c\u5018\u82e5\u8fd9\u8001\u513f\u771f\u80fd\u51fa\u5c71\uff0c\u5bf9\u6211\u6b66\u6797\u540c\u9053\u800c\u8a00\uff0c\u90a3\u53ef\u662f\u5f88\u5927\u7684\u798f\u6c14\u4e86\uff01\u201d \u3000\u3000\u9634\u9633\u624b\u5409\u5a01\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5929\u5357\u602a\u81fe\u7684\u533b\u9053\uff0c\u7d20\u6709\u56fd\u624b\u4e4b\u8a89\uff0c\u4ed6\u80fd\u51fa\u5c71\uff0c\u5b9a\u53ef\u6551\u6d3b\u4e0d\u5c11\u4eba\u6027\u547d\u2026\u2026 \u3000\u3000\u5165\u4e91\u5927\u5e08\u53e3\u55a7\u4f5b\u53f7\uff0c\u9053\uff1a\u201c\u970d\u5e2e\u4e3b\uff0c\u8001\u8872\u60f3\u8d77\u4e00\u4ef6\u4e8b\u6765\uff0c\u4e0d\u77e5\u5e2e\u4e3b\u613f\u5426\u5e94\u5141\u76f8\u52a9\u3002\u201d \u3000\u3000\u970d\u9e23\u98ce\u95ef\u8a00\u4e00\u6014\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u8001\u548c\u5c1a\u53c8\u6709\u4ec0\u4e48\u82b1\u6837\u8981\u800d\uff1f\u201d \u3000\u3000\u4f46\u4ed6\u53e3\u4e2d\u5374\u662f\u63a5\u9053\uff1a\u201c\u638c\u95e8\u4eba\u6709\u4f55\u89c1\u6559\uff1f\u970d\u67d0\u4eba\u529b\u6240\u80fd\u53ca\uff0c\u81ea\u5f53\u6548\u547d\u7684\u4e86\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u4e3a\u4e86\u5bf9\u4ed8\u90a3\u4e94\u5927\u9b54\u4e3b\uff0c\u54b1\u4eec\u4e5d\u5927\u95e8\u6d3e\uff0c\u81ea\u662f\u4e49\u4e0d\u5bb9\u8f9e\uff0c\u4f46\u8001\u8872\u8eab\u4e3a\u6211\u4f5b\u7b2c\u5b50\uff0c\u4e0d\u4fbf\u591a\u884c\u90a3\u6740\u751f\u4f24\u4eba\u4e4b\u4e8b\uff0c\u8001\u8872\u751a\u671b\u970d\u5e2e\u4e3b\u4f53\u8c05\u8001\u8872\u8fd9\u7b49\u82e6\u8877\uff0c\u4e3b\u6301\u76ee\u4e0b\u4e4b\u4e8b\uff0c\u4f46\u6709\u7528\u7684\u7740\u8001\u8872\u4e4b\u5904\uff0c\u8001\u8872\u5b9a\u5c06\u5168\u529b\u4ee5\u8d74\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u4f3c\u662f\u672a\u66fe\u6599\u5230\uff0c\u5165\u4e91\u5927\u5e08\u7adf\u662f\u5c06\u8fd9\u4e3b\u76df\u7684\u8d23\u4efb\uff0c\u4e00\u4e0b\u5b50\u6263\u5230\u81ea\u5df1\u5934\u4e0a\uff0c\u987f\u65f6\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u90a3\u600e\u4e48\u6210\uff1f\u638c\u95e8\u4eba\u4e0d\u80af\u51fa\u9762\uff0c\u4f46\u5728\u6b66\u5f53\u3001\u534e\u5c71\u7b49\u6d3e\uff0c\u53ea\u6015\u4e5f\u8981\u50cf\u8d35\u5bfa\u4e00\u822c\u4e86\uff0c\u4f55\u51b5\u2026\u2026\u970d\u67d0\u8fd9\u533a\u533a\u4e10\u5e2e\uff0c\u867d\u7136\u8110\u8eab\u4e5d\u5927\u95e8\u6d3e\u4e4b\u4e2d\uff0c\u4f46\u5728\u6b66\u5f53\u3001\u534e\u5c71\u7b49\u4eba\u773c\u4e2d\uff0c\u8001\u592b\u4f9d\u7136\u662f\u90a3\u91ce\u72d0\u7985\u4e4b\u6d41\uff0c\u5982\u662f\u7531\u6211\u4e3b\u6301\uff0c\u5b9a\u5c06\u6210\u4e8b\u4e0d\u8db3\u3001\u8d25\u4e8b\u6709\u4f59\u4e86\u3002\u201d \u3000\u3000\u970d\u9e23\u98ce\u8fd9\u756a\u8bdd\uff0c\u5e76\u975e\u65e0\u793c\u3002\u90a3\u5165\u4e91\u5927\u5e08\u95fb\u8a00\uff0c\u6c89\u541f\u4e86\u826f\u4e45\uff0c\u65b9\u9053\uff1a\u201c\u5e2e\u4e3b\u8fd9\u756a\u8bdd\uff0c\u672c\u662f\u81ea\u8c26\u4e4b\u8bcd\uff0c\u4e0d\u8fc7\uff0c\u8001\u8872\u4e5f\u77e5\u9053\u6b66\u5f53\u3001\u534e\u5c71\u751a\u662f\u9ad8\u50b2\uff0c\u5982\u662f\u53ea\u7531\u5e2e\u4e3b\u51fa\u9762\uff0c\u4ed6\u4eec\u5012\u4e5f\u771f\u4e2a\u53ef\u80fd\u4e0d\u5b50\u7406\u4f1a\u7684\u2026\u2026\u201d \u3000\u3000\u970d\u9e23\u98ce\u7b11\u9053\uff1a\u201c\u638c\u95e8\u4eba\u660e\u767d\u5c31\u597d\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u8001\u8872\u5012\u662f\u6709\u4e00\u4e2a\u529e\u6cd5\uff0c\u4e0d\u77e5\u5e2e\u4e3b\u540c\u4e0d\u540c\u610f\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u4ec0\u4e48\u529e\u6cd5\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u90a3\u4e3b\u76df\u4e4b\u4eba\uff0c\u53ef\u7531\u65b9\u5c0f\u65bd\u4e3b\u62c5\u4efb\uff0c\u5e2e\u4e3b\u548c\u8001\u8872\u4f5c\u4e3a\u4ed6\u7684\u52a9\u624b\uff0c\u4f60\u770b\u5982\u4f55\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u4f7f\u5f97\uff01\u4f7f\u5f97\uff0c\u8001\u673d\u6b63\u662f\u6b64\u5fc3\uff0c\u4e0d\u66fe\u8bf4\u51fa\uff0c\u4e43\u662f\u6050\u6015\u5927\u5e08\u4e0d\u80af\u800c\u5df2\uff01\u201d \u3000\u3000\u4e24\u4eba\u8fd9\u4e48\u4e00\u8bf4\uff0c\u987f\u65f6\u53eb\u90a3\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u5927\u5403\u4e00\u60ca\uff0c\u4f3c\u8fd9\u7b49\u5927\u4e8b\uff0c\u5c45\u7136\u8981\u4ed6\u51fa\u9762\u4e3b\u76df\uff0c\u4e00\u65f6\u4f7f\u4ed6\u6709\u7740\u624b\u8db3\u65e0\u63aa\u4e4b\u611f\uff0c\u5f53\u4e0b\u8fde\u5fd9\u63a5\u9053\uff1a\u201c\u4f7f\u4e0d\u5f97\uff0c\u665a\u8f88\u613f\u5019\u4e24\u4f4d\u5dee\u9063\uff0c\u8d74\u6c64\u8e48\u706b\uff0c\u5728\u6240\u4e0d\u8f9e\uff0c\u4f46\u5982\u662f\u8981\u6211\u4e3b\u6301\u4e00\u5207\uff0c\u90a3\u662f\u6298\u6740\u665a\u8f88\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u5927\u7b11\u9053\uff1a\u201c\u65b9\u8001\u5f1f\uff0c\u4f60\u63a8\u4e0d\u4e86\u7684\u5566\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u4e5f\u7b11\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\uff0c\u4f60\u672c\u662f\u8981\u7ee7\u627f\u4ee4\u5e08\u9648\u5927\u4fa0\u7684\u8863\u94b5\uff0c\u964d\u9b54\u884c\u9053\uff0c\u66f4\u662f\u4efd\u5185\u4e4b\u4e8b\uff0c\u4f60\u4e0d\u5141\u4e3b\u6301\u4e00\u5207\uff0c\u90a3\u5c82\u4e0d\u662f\u6709\u6096\u4ee4\u5e08\u7684\u9057\u5fd7\u4e86\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u665a\u8f88\u4ece\u65c1\u534f\u52a9\uff0c\u5019\u547d\u5dee\u9063\uff0c\u90a3\u4e5f\u662f\u4e00\u6837\u7684\u554a\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u6447\u5934\u9053\uff1a\u201c\u4e0d\u4e00\u6837\uff0c\u4ee4\u5e08\u9648\u5927\u4fa0\u5728\u4e16\u4e4b\u65e5\uff0c\u5c31\u80fd\u72ec\u5f53\u4e00\u9762\uff0c\u51ed\u4e00\u5df1\u4e4b\u529b\uff0c\u964d\u670d\u4e86\u4e94\u5927\u9b54\u4e3b\uff0c\u4f60\u8001\u5f1f\u65e2\u662f\u9648\u5927\u4fa0\u7684\u8863\u94b5\u5f1f\u5b50\uff0c\u5e94\u8be5\u4e49\u4e0d\u5bb9\u8f9e\u554a\uff01\u4f60\u662f\u4e0d\u7528\u518d\u8bf4\u4ec0\u4e48\u4e86\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u5408\u5341\u4e00\u793c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u970d\u5e2e\u4e3b\u6240\u8bf4\uff0c\u4e43\u662f\u5b9e\u60c5\uff0c\u5c0f\u65bd\u4e3b\u5018\u662f\u6709\u5fc3\u964d\u9b54\u9664\u9053\uff0c\u7ee7\u627f\u5148\u5e08\u672a\u5b8c\u9057\u613f\uff0c\u53ef\u5c31\u4e0d\u5f97\u518d\u884c\u63a8\u8f9e\u4e86\u3002\u201d \u3000\u3000\u8fd9\u65f6\u5b8b\u5b5a\u5ffd\u7136\u4e00\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u5982\u6709\u5fc3\u4e3a\u6b66\u6797\u540c\u9053\u9020\u798f\uff0c\u8fd9\u6b63\u662f\u4e00\u4e2a\u5927\u597d\u7684\u826f\u673a\uff0c\u8001\u673d\u4e0d\u624d\uff0c\u613f\u8ffd\u968f\u5de6\u53f3\uff0c\u671b\u8001\u5f1f\u4e09\u601d\u800c\u884c\u3002\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8001\u5f1f\u5982\u518d\u63a8\u4e09\u963b\u56db\uff0c\u5c31\u663e\u5f97\u4e0d\u591f\u8bda\u610f\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u5927\u4e3a\u611f\u52a8\uff0c\u6697\u9053\uff1a\u201c\u4ed6\u4eec\u8fd9\u6837\u770b\u91cd\u4e8e\u6211\uff0c\u5018\u662f\u6211\u518d\u4e0d\u5e94\u5141\uff0c\u5f53\u771f\u662f\u8981\u53eb\u4ed6\u4eec\u7070\u5fc3\u4e86\uff01\u201d \u3000\u3000\u5f53\u4e0b\u7565\u4e00\u6c89\u541f\uff0c\u9053\uff1a\u201c\u65e2\u7136\u4e09\u4f4d\u5982\u6b64\u5668\u91cd\u665a\u975e\uff0c\u665a\u8f88\u5982\u518d\u4e0d\u5141\uff0c\u90a3\u4e5f\u672a\u514d\u592a\u4e0d\u8fd1\u4eba\u60c5\u4e86\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u6148\u7709\u4e00\u626c\uff0c\u5408\u5341\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\u5e94\u5141\u4e86\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u5374\u662f\u5927\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u5c31\u662f\u4f60\u8fd9\u4e00\u53e5\u8bdd\uff0c\u53ef\u5c31\u662f\u4e3a\u6b66\u6797\u8981\u9020\u798f\u4e0d\u5c0f\uff01\u201d \u3000\u3000\u53ea\u6709\u5b8b\u5b5a\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u8fd9\u624d\u662f\u771f\u6b63\u7684\u7531\u8877\u4e4b\u8a00\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u51e0\u4f4d\u524d\u8f88\u8fd9\u7b49\u7231\u62a4\u665a\u8f88\uff0c\u665a\u8f88\u7eb5\u7136\u6df1\u77e5\u81ea\u5df1\u80fd\u529b\u6709\u9650\uff0c\u4f46\u4e5f\u4e0d\u5f97\u4e0d\u52c9\u4e3a\u5176\u96be\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u672c\u662f\u4e00\u65c1\u9ed8\u5750\u672a\u8bed\uff0c\u8fd9\u65f6\u5ffd\u5730\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u773c\u4e0b\u7684\u8eab\u4efd\uff0c\u4e0d\u662f\u7b49\u4e8e\u6b66\u6797\u76df\u4e3b\u4e00\u822c\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u539f\u6765\u53ea\u662f\u89c9\u5f97\u4eca\u540e\u7684\u4efb\u52a1\uff0c\u5341\u5206\u6c89\u91cd\uff0c\u5e76\u672a\u60f3\u5230\u540d\u671b\u5173\u7cfb\uff0c\u8fd9\u65f6\u88ab\u5b89\u5c0f\u840d\u4e00\u63d0\uff0c\u5012\u662f\u5927\u5927\u5730\u5403\u4e86\u4e00\u60ca\uff0c\u6697\u9053\uff1a\u201c\u53ef\u4e0d\u662f\u5417\uff1f\u6211\u773c\u4e0b\u867d\u65e0\u5176\u540d\uff0c\u5374\u6709\u5176\u5b9e\uff0c\u8fd9\u771f\u662f\u4e00\u6869\u53eb\u4eba\u4e3a\u96be\u7684\u4e8b\u554a\u2026\u2026\u201d \u3000\u3000\u6562\u60c5\u4ed6\u5ffd\u7136\u60f3\u5230\uff0c\u4e00\u65e6\u81ea\u5df1\u6709\u4e86\u6b66\u6797\u76df\u4e3b\u7684\u8eab\u4efd\uff0c\u90a3\u53ef\u662f\u5bf9\u81ea\u5df1\u7684\u4e00\u4e3e\u4e00\u52a8\uff0c\u90fd\u6709\u7740\u6781\u5927\u7684\u62d8\u675f\u4f5c\u7528\uff0c\u5018\u662f\u81ea\u5df1\u6ca1\u6709\u79c1\u4e8b\u5f85\u529e\uff0c\u90a3\u5012\u8fd8\u597d\uff0c\u53ea\u56e0\u81ea\u5df1\u8fd8\u6709\u7740\u4e0d\u5c11\u79c1\u4e8b\u8981\u529e\uff0c\u6240\u4ee5\uff0c\u81ea\u5df1\u7684\u884c\u52a8\uff0c\u53ef\u4e07\u4e07\u4e0d\u80fd\u53d7\u5230\u4e16\u4fd7\u7684\u9650\u5236\uff0c\u5426\u5219\uff0c\u90a3\u53ef\u4e0e\u81ea\u5df1\u6295\u5e08\u5b66\u6b66\u7684\u521d\u8877\uff0c\u5927\u4e3a\u76f8\u60bb\u4e86\uff01 \u3000\u3000\u4e00\u5ff5\u53ca\u6b64\uff0c\u4e0d\u7531\u5f97\u5411\u5165\u4e91\u5927\u5e08\u671b\u4e86\u8fc7\u53bb\uff0c\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u665a\u8f88\u6709\u4e00\u4ef6\u4e8b\uff0c\u4e0d\u5f97\u4e0d\u4e8b\u5148\u8bf4\u660e\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u542c\u5f97\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\u83ab\u975e\u6539\u53d8\u4e86\u521d\u8877\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u665a\u8f88\u53ea\u662f\u60f3\u5148\u628a\u79c1\u4eba\u4e4b\u4e8b\u529e\u6bd5\uff0c\u7136\u540e\u624d\u597d\u5168\u5fc3\u5168\u610f\u4e0e\u90a3\u4e94\u5927\u9b54\u4e3b\u5468\u65cb\u5230\u5e95\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u5c0f\u65bd\u4e3b\u5230\u5e95\u8fd8\u6709\u4ec0\u4e48\u79c1\u4eba\u4e4b\u4e8b\u8981\u529e\uff0c\u8001\u8872\u7b49\u4e0d\u77e5\u53ef\u5426\u4ee3\u52b3\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u82e6\u7b11\u9053\uff1a\u201c\u4e0d\u6562\u6709\u52b3\u5927\u5e08\u2026\u2026\u201d \u3000\u3000\u53ea\u56e0\u4ed6\u4e0d\u613f\u628a\u81ea\u5df1\u7236\u4eb2\u548c\u5802\u53d4\u4e4b\u4e8b\u544a\u77e5\u5176\u4ed6\u4eba\uff0c\u662f\u4ee5\uff0c\u4ed6\u4e00\u53e3\u62d2\u7edd\u5165\u4e91\u5927\u5e08\u7684\u597d\u610f\u3002 \u3000\u3000\u970d\u9e23\u98ce\u76ee\u5149\u4e00\u8f6c\uff0c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u5018\u662f\u4f60\u4fe1\u5f97\u8fc7\u8001\u673d\uff0c\u8001\u673d\u613f\u610f\u52a9\u4f60\u5148\u5c06\u79c1\u4eba\u4e4b\u4e8b\u529e\u59a5\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u76b1\u4e86\u76b1\u7709\uff0c\u9053\uff1a\u201c\u591a\u8c22\u8001\u524d\u8f88\u597d\u5fc3\uff0c\u665a\u8f88\u81ea\u5df1\u7684\u4e8b\uff0c\u4e0d\u6562\u6709\u52b3\u4f60\u8001\uff01\u201d \u3000\u3000\u4ed6\u76ee\u5149\u5ffd\u7136\u5c04\u5411\u5b89\u5c0f\u840d\u548c\u5b8b\u5b5a\uff0c\u9053\uff1a\u201c\u665a\u8f88\u53ea\u60f3\u501f\u91cd\u5b8b\u8001\u548c\u5b89\u5144\u4e4b\u529b\uff0c\u4e0d\u77e5\u4e24\u4f4d\u8001\u524d\u8f88\u610f\u4e0b\u5982\u4f55\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u6c89\u541f\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\u6267\u610f\u5982\u6b64\uff0c\u8001\u8872\u7b49\u81ea\u5f53\u5c0a\u91cd\u5c0f\u65bd\u4e3b\u4e4b\u610f\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u5982\u679c\u4f60\u662f\u60f3\u627e\u5bfb\u4ec0\u4e48\u4eba\uff0c\u672c\u5e2e\u5f1f\u5b50\u904d\u5e03\u5929\u4e0b\u3001\u53ea\u987b\u4f60\u8001\u5f1f\u4e00\u53e5\u8bdd\uff0c\u672c\u5e2e\u5b9a\u53ef\u5e2e\u4f60\u5c06\u90a3\u8981\u627e\u4e4b\u4eba\u5bfb\u5230\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u5fc3\u4e2d\u5927\u5927\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u201c\u4e10\u5e2e\u5b50\u5f1f\u4e4b\u591a\uff0c\u679c\u7136\u662f\u904d\u5e03\u5929\u4e0b\uff0c\u5982\u679c\u80fd\u591f\u5f97\u5230\u4ed6\u4eec\u4e4b\u52a9\uff0c\u8bf4\u4e0d\u5b9a\u5f88\u5feb\u5c31\u53ef\u67e5\u51fa\u6211\u5a18\u4e0b\u843d\u2026\u2026\u201d \u3000\u3000\u8f6c\u5ff5\u81f3\u6b64\uff0c\u4ed6\u4e45\u4e45\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u970d\u9e23\u98ce\u7b49\u4eba\uff0c\u7741\u7740\u773c\u5728\u7b49\u4ed6\uff0c\u53ea\u56e0\u4ed6\u7684\u7b54\u590d\uff0c\u5173\u7cfb\u7740\u4eca\u540e\u6b66\u6797\u7684\u5174\u8870\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u60f3\u4e86\u4e00\u4f1a\u513f\uff0c\u8fd9\u624d\u7f13\u7f13\u5730\u62ac\u8d77\u5934\u6765\uff0c\u957f\u957f\u5730\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8001\u524d\u8f88\u7684\u76db\u60c5\uff0c\u665a\u8f88\u8fd9\u513f\u5148\u884c\u8c22\u8fc7\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u95fb\u8a00\u5927\u559c\u9053\uff1a\u201c\u8001\u5f1f\u5e94\u5141\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8001\u524d\u8f88\u5b50\u5f1f\u904d\u5e03\u5929\u4e0b\uff0c\u679c\u7136\u5bf9\u665a\u8f88\u4e4b\u4e8b\uff0c\u5927\u6709\u52a9\u76ca\uff0c\u662f\u4ee5\uff0c\u665a\u8f88\u5148\u884c\u8c22\u4f60\u8001\u76f8\u52a9\u4e4b\u5fb7\u3002\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u8bf4\u51fa\u8981\u627e\u4e4b\u4eba\uff0c\u8001\u673d\u8fd9\u5c31\u5dee\u4eba\u524d\u53bb\u5bfb\u627e\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5ffd\u7136\u6c89\u541f\u9053\uff1a\u201c\u8fd9\u2026\u2026\u665a\u8f88\u8981\u5bfb\u4e4b\u4eba\uff0c\u4e43\u662f\u5bb6\u6bcd\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u65b9\u592b\u4eba\u5417\uff1f\u8001\u5f1f\u53ef\u77e5\u5979\u843d\u811a\u4f55\u65b9\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u4e2a\u5417\uff1f\u665a\u8f88\u4e0d\u77e5\u6653\uff01\u4f46\u5bb6\u6bcd\u770b\u7834\u7ea2\u5c18\uff0c\u524a\u53d1\u4e3a\u5c3c\uff0c\u9041\u5165\u7a7a\u95e8\u4e4b\u4e8b\uff0c\u665a\u8f88\u5374\u662f\u77e5\u6653\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u5ffd\u7136\u5408\u5341\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\uff0c\u4ee4\u5802\u65e2\u7136\u843d\u53d1\u7688\u4f9d\u4f5b\u7956\uff0c\u4e0d\u95ee\u4e16\u4e8b\uff0c\u4e13\u5fc3\u4fee\u884c\uff1f \u3000\u3000\u4f60\u2026\u2026\u4f55\u5fc5\u6253\u6270\u4e8e\u5979\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u5934\u9053\uff1a\u201c\u638c\u95e8\u4eba\u6709\u6240\u4e0d\u77e5\uff0c\u5bb6\u6bcd\u867d\u7136\u7688\u4f9d\u4f5b\u7956\uff0c\u4f46\u665a\u8f88\u5374\u4e0d\u80fd\u4e0d\u5c3d\u4eba\u5b50\u4e4b\u5fc3\u554a\uff01\u4f55\u51b5\u2026\u2026\u5509\u2026\u2026\u201d \u3000\u3000\u4ed6\u957f\u53f9\u4e00\u58f0\uff0c\u4f4f\u53e3\u4e0d\u8bed\uff01 \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\u53ef\u662f\u8fd8\u6709\u4ec0\u4e48\u8bdd\u4e0d\u4fbf\u5411\u8001\u8872\u7b49\u4eba\u8bf4\u51fa\u6765\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u665a\u8f88\u7684\u5148\u7236\uff0c\u636e\u6069\u5e08\u76f8\u544a\u3002\u4f3c\u662f\u6b7b\u7684\u6709\u4e9b\u4e0d\u660e\u4e0d\u767d\uff0c\u56e0\u6b64\u5fc5\u5f97\u627e\u5230\u5bb6\u6bcd\u65b9\u53ef\u95ee\u660e\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4ee4\u5c0a\u662f\u6b66\u6797\u4e4b\u4e2d\u4eba\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u7b97\u5f97\u4e0a\u662f\uff01\u4e0d\u8fc7\uff0c\u5bb6\u7236\u5f03\u517b\u751a\u65e9\uff0c\u5728\u6b66\u6797\u4e4b\u4e2d\uff0c\u7c4d\u7c4d\u65e0\u540d\uff0c\u8bf4\u5c06\u51fa\u6765\uff0c\u542c\u6015\u8001\u524d\u8f88\u4e5f\u4e0d\u8bc6\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u7b11\u9053\uff1a\u201c\u4e0d\u4e8c\u5b9a\uff0c\u4f60\u90a3\u4f2f\u7236\u65b9\u5929\u6210\uff0c\u8001\u673d\u5c31\u4e0e\u4ed6\u76f8\u8bc6\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5bb6\u7236\u65b9\u5929\u7fa4\u4f60\u8001\u8ba4\u8bc6\u5417\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u5446\u4e86\u4e00\u5446\u9053\uff1a\u201c\u65b9\u5929\u7fa4\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4f60\u8001\u5b9a\u7136\u4e0d\u8bc6\u7684\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u5ffd\u7136\u5927\u7b11\u9053\uff1a\u201c\u8c01\u8bf4\u7684\uff1f\u8001\u5f1f\uff0c\u90a3\u65b9\u5929\u7fa4\u8001\u5f1f\u53f0\uff0c\u4e0e\u8001\u592b\u4e43\u662f\u83ab\u9006\u4e4b\u4ea4\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u771f\u7684\u5417\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u8001\u673d\u600e\u4f1a\u9a97\u4f60\uff1f\u8001\u5f1f\uff0c\u4eca\u5802\u65b9\u592b\u4eba\uff0c\u8bf4\u6765\u66f4\u4e0d\u662f\u5916\u4eba\u4e86\uff1f\u6b64\u4e8b\u8001\u673d\u4e00\u5b9a\u53ef\u4ee5\u4e3a\u4f60\u529e\u5230\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6123\u6123\u5730\u671b\u7740\u970d\u9e23\u98ce\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u4f60\u8001\u4e5f\u8ba4\u8bc6\u5bb6\u6bcd\u5417\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u7b11\u9053\uff1a\u201c\u5f53\u7136\uff01\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u56de\u987e\u4e86\u5d14\u5927\u516c\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5d14\u5144\uff0c\u672c\u5e2e\u91d1\u9675\u5206\u5802\u5802\u4e3b\u6c5f\u5357\u795e\u6756\u53f6\u5144\u7684\u5e7c\u59b9\uff0c\u4e0d\u5c31\u662f\u5ac1\u4e86\u65b9\u5929\u7fa4\u5417\uff1f\u201d \u3000\u3000\u5d14\u5927\u516c\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e0d\u662f\u5e2e\u4e3b\u63d0\u8d77\uff0c\u5144\u5f1f\u53ef\u5012\u662f\u5fd8\u8bb0\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\u4e0d\u7531\u5f97\u5446\u4e86\u4e00\u5446\uff0c\u6697\u9053\uff1a\u201c\u539f\u6765\u6211\u5a18\u662f\u4e10\u5e2e\u91d1\u9675\u5206\u5802\u4e3b\u7684\u59b9\u59b9\u5417\uff0c\u600e\u5730\u4e8c\u4f2f\u6bcd\u4ece\u672a\u5411\u6211\u63d0\u8d77\u8fc7\u5462\uff1f\u201d \u3000\u3000\u4ed6\u7591\u8651\u4e4b\u95f4\uff0c\u8033\u4e2d\u542c\u5f97\u970d\u9e23\u98ce\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u8981\u5bfb\u627e\u4ee4\u5802\u4e4b\u4e8b\uff0c\u4ea4\u7ed9\u8001\u673d\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fde\u5fd9\u62b1\u62f3\u4e00\u793c\uff0c\u9053\uff1a\u201c\u591a\u8c22\u8001\u524d\u8f88\uff01\u201d \u3000\u3000\u8fd9\u65f6\u5b8b\u5b5a\u5ffd\u7136\u5411\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u90a3\u4e2d\u5dde\u4e09\u4fa0\u4e2d\u7684\u90d1\u5927\u4fa0\u53ef\u66fe\u6765\u5230\u5c11\u6797\u5bfa\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u6447\u5934\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u8001\u8872\u4e0d\u592a\u660e\u767d\uff01\u201d \u3000\u3000\u6562\u60c5\u4ed6\u8bf4\u7684\u4e43\u662f\u5b9e\u8bdd\uff0c\u53ea\u56e0\u90a3\u90d1\u5927\u521a\u5373\u4f7f\u524d\u6765\u5c11\u6797\uff0c\u4ee5\u5165\u4e91\u5927\u5e08\u90a3\u638c\u95e8\u7684\u8eab\u4efd\uff0c\u90d1\u5927\u521a\u4e5f\u65e0\u6cd5\u89c1\u5230\u7684\u4e86\u3002 \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u5927\u5e08\u53ef\u8981\u5bfa\u4e2d\u7684\u5927\u5e08\u4eec\u67e5\u95ee\u4e00\u4e0b\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u5b8b\u65bd\u4e3b\u627e\u90a3\u90d1\u5927\u4fa0\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e0d\u662f\u6211\uff0c\u662f\u65b9\u8001\u5f1f\u8981\u627e\u4ed6\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u8001\u8872\u8fd9\u5c31\u53eb\u4ed6\u4eec\u53bb\u67e5\u95ee\u4e00\u4e0b\u2026\u2026\u201d \u3000\u3000\u4e3e\u624b\u51fb\u4e09\u638c\uff0c\u53ea\u89c1\u5b88\u5728\u95e8\u5916\u7684\u4e00\u540d\u5c0f\u6c99\u5f25\uff0c\u5927\u6b65\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u5165\u4e91\u5927\u5e08\u4ea4\u4ee3\u4e86\u90a3\u540d\u5c0f\u6c99\u5f25\u51e0\u53e5\uff0c\u53ea\u89c1\u4ed6\u8fc5\u901f\u5730\u8d70\u51fa\u9759\u5ba4\uff0c\u8fc7\u4e86\u4e0b\u4e00\u4f1a\uff0c\u90a3\u5165\u771f\u5927\u5e08\uff0c\u5feb\u6b65\u800c\u6765\u3002 \u3000\u3000\u5165\u4e91\u638c\u95e8\u6c89\u58f0\u9053\uff1a\u201c\u5165\u771f\u5e08\u5f1f\uff0c\u90a3\u4e2d\u5dde\u4e09\u4fa0\u4e2d\u7684\u90d1\u5927\u521a\u65bd\u4e3b\uff0c\u53ef\u66fe\u6765\u8fc7\u5c11\u6797\uff1f\u201d \u3000\u3000\u5165\u771f\u5927\u5e08\u9053\uff1a\u201c\u90d1\u5927\u65bd\u4e3b\u6765\u8fc7\u5c11\u6797\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u5408\u5341\u9053\uff1a\u201c\u5c1a\u5728\u5bfa\u4e2d\u5417\uff1f\u201d \u3000\u3000\u5165\u771f\u9053\uff1a\u201c\u4e0d\u5728\uff0c\u90d1\u65bd\u4e3b\u6765\u53bb\u5306\u5306\uff0c\u672a\u66fe\u7a0d\u7559\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u76b1\u7709\u9053\uff1a\u201c\u4f60\u4eec\u4e3a\u4f55\u4e0d\u544a\u77e5\u672c\u5ea7\u3002\u201d \u3000\u3000\u5165\u771f\u5927\u5e08\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u8fd9\u2026\u2026\u5c0f\u5f1f\u4e0d\u77e5\u638c\u95e8\u4eba\u4e0e\u4ed6\u76f8\u8bc6\uff0c\u662f\u4ee5\u672a\u66fe\u60ca\u52a8\u638c\u95e8\u5e08\u5144\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u4f3c\u662f\u77e5\u9053\u81ea\u5df1\u4e00\u65f6\u6027\u6025\uff0c\u9519\u602a\u4e86\u5165\u771f\uff0c\u5f53\u4e0b\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u672c\u5ea7\u4e0e\u90a3\u90d1\u5927\u4fa0\u5e76\u4e0d\u76f8\u8bc6\uff0c\u53ea\u662f\u65b9\u5c0f\u65bd\u4e3b\u8981\u89c1\u4ed6\u4e00\u9762\u3002\u201d \u3000\u3000\u5165\u771f\u5927\u5e08\u8fd9\u624d\u677e\u4e86\u4e00\u53e3\u6c14\uff0c\u6697\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff01\u201d \u3000\u3000\u4f46\u4ed6\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u638c\u95e8\u5e08\u5144\u53ef\u662f\u8981\u627e\u90a3\u90d1\u65bd\u4e3b\u518d\u6765\u672c\u5bfa\u5417\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u5e08\u5f1f\u77e5\u6653\u4ed6\u73b0\u5728\u4f55\u5904\uff1f\u201d \u3000\u3000\u5165\u771f\u5927\u5e08\u9053\uff1a\u201c\u53ef\u7531\u5c0f\u5f1f\u6d3e\u4eba\u51fa\u5916\u6253\u63a2\uff0c\u4e2d\u5dde\u4e09\u4fa0\u4e43\u662f\u5927\u6709\u540d\u671b\u4e4b\u58eb\uff0c\u8c05\u5fc5\u53ef\u4ee5\u627e\u5f97\u5230\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6709\u52b3\u5e08\u5f1f\u8f9b\u82e6\u4e00\u8d9f\u4e86\uff01\u201d \u3000\u3000\u5165\u771f\u5927\u5e08\u5408\u5341\u4e00\u793c\uff0c\u6b63\u5f85\u79bb\u53bb\uff0c\u53ea\u89c1\u90a3\u9634\u9633\u624b\u5409\u5a01\u51b7\u7b11\u4e00\u58f0\uff0c\u559d\u9053\uff1a\u201c\u5927\u5e08\u6162\u8d70\uff01\u201d \u3000\u3000\u5165\u771f\u5927\u5e08\u95fb\u8a00\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u811a\u4e0b\u4e0d\u7531\u81ea\u4e3b\u5730\u7ad9\u5b9a\uff0c\u9053\uff1a\u201c\u65bd\u4e3b\u5524\u7684\u8001\u8872\u5417\uff1f\u201d \u3000\u3000\u5409\u5a01\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u5165\u771f\u5927\u5e08\u76b1\u7709\u9053\uff1a\u201c\u65bd\u4e3b\u6709\u4f55\u89c1\u6559\uff1f\u201d \u3000\u3000\u5409\u5a01\u9053\uff1a\u201c\u5927\u5e08\u4e0d\u7528\u8d39\u5fc3\u627e\u90a3\u90d1\u5927\u521a\u4e86\uff01\u201d \u3000\u3000\u5165\u771f\u5927\u5e08\u8138\u8272\u5fae\u53d8\uff0c\u9053\uff1a\u201c\u600e\u4e48\uff1f\u65bd\u4e3b\u53ef\u662f\u8ba4\u4e3a\u8001\u8872\u627e\u4e0d\u5230\u90d1\u65bd\u4e3b\u5417\uff1f\u201d \u3000\u3000\u5409\u5a01\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u662f\uff0c\u56e0\u4e3a\uff0c\u90a3\u90d1\u5927\u521a\u773c\u4e0b\u7684\u8eab\u4efd\uff0c\u4e43\u5929\u9b54\u5973\u624b\u4e0b\u4e4b\u4eba\uff0c\u53ea\u6015\u5927\u5e08\u5c3d\u6d3e\u5bfa\u4e2d\u50e7\u4fa3\uff0c\u4e5f\u96be\u4ee5\u5c06\u4ed6\u627e\u5230\u3002\u201d \u3000\u3000\u5165\u771f\u5927\u5e08\u9053\uff1a\u201c\u6709\u8fd9\u7b49\u4e8b\uff1f\u8001\u8872\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u5409\u5a01\u9053\uff1a\u201c\u5927\u5e08\uff0c\u90a3\u90d1\u5927\u521a\u524d\u6765\u5c11\u6797\u4e4b\u65f6\uff0c\u4e0d\u77e5\u5927\u5e08\u53ef\u66fe\u4e0e\u4ed6\u89c1\u8fc7\u3002\u201d \u3000\u3000\u5165\u771f\u5927\u5e08\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u8001\u8872\u5e76\u672a\u89c1\u5230\u3002\u201d \u3000\u3000\u5409\u5a01\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4e86\uff0c\u60f3\u5fc5\u5927\u5e08\u5982\u662f\u95ee\u95ee\u90a3\u4f4d\u4e0e\u90d1\u5927\u521a\u89c1\u9762\u4e4b\u4eba\uff0c\u5c31\u4f1a\u660e\u767d\u5409\u67d0\u4e4b\u8a00\u6709\u7406\u4e86\uff01\u201d \u3000\u3000\u5165\u771f\u5927\u5e08\u5c06\u4fe1\u5c06\u7591\u5730\u770b\u4e86\u5165\u4e91\u638c\u95e8\u4eba\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5409\u65bd\u4e3b\uff0c\u4f9d\u4f60\u4e4b\u89c1\uff0c\u672c\u5bfa\u662f\u4e0d\u7528\u5dee\u4eba\u51fa\u5916\u5bfb\u627e\u4e8e\u4ed6\u4e86\u3002\u201d \u3000\u3000\u5409\u5a01\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d \u3000\u3000\u8fd9\u65f6\u90a3\u5165\u4e91\u5927\u5e08\u63a5\u9053\uff1a\u201c\u5409\u65bd\u4e3b\uff0c\u65b9\u5c0f\u65bd\u4e3b\u6025\u4e8e\u89c1\u90a3\u90d1\u5927\u521a\u5927\u4fa0\uff0c\u5018\u662f\u672c\u5bfa\u4e0d\u6d3e\u5b50\u5f1f\u51fa\u53bb\u5bfb\u627e\uff0c\u90a3\u90d1\u65bd\u4e3b\u53c8\u600e\u4f1a\u89c1\u5f97\u5230\u65b9\u5c0f\u65bd\u4e3b\uff1f\u201d \u3000\u3000\u5409\u5a01\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u8fd9\u4e8b\u5409\u67d0\u8ba4\u4e3a\u4e0d\u5fc5\u6025\u5728\u4e00\u65f6\u3002\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u770b\u4e86\u5b8b\u5b5a\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5b8b\u65bd\u4e3b\u4e4b\u89c1\u5462\uff1f\u201d \u3000\u3000\u6562\u60c5\uff0c\u8fd9\u8bdd\u7531\u5b8b\u5b5a\u63d0\u51fa\uff0c\u5f53\u7136\u8fd8\u5f97\u7b49\u90a3\u5b8b\u5b5a\u51b3\u5b9a\u4e86\u3002 \u3000\u3000\u5b8b\u5b5a\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u65b9\u8001\u5f1f\u6240\u8c13\u79c1\u4eba\u4e4b\u4e8b\uff0c\u5b9e\u9645\u4e0a\u5c31\u662f\u4e24\u4ef6\u4e8b\uff0c\u4e00\u662f\u5bfb\u627e\u4ed6\u7684\u6bcd\u4eb2\uff0c\u53e6\u5916\u4e00\u4ef6\uff0c\u4fbf\u662f\u5bfb\u627e\u4e2d\u5dde\u4e09\u4fa0\u7684\u4e0b\u843d\uff0c\u8fd9\u5bfb\u6bcd\u4e4b\u4e8b\uff0c\u5df2\u7531\u970d\u5e2e\u4e3b\u627f\u62c5\u4e0b\u6765\uff0c\u53ea\u6015\u8fd9\u5bfb\u627e\u4e2d\u5dde\u4e09\u4fa0\u4e4b\u4e8b\uff0c\u5c31\u8981\u6709\u52b3\u638c\u95e8\u4eba\u4e86\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u8fd9\u5bfb\u627e\u4e2d\u5dde\u4e09\u4fa0\u7684\u4e8b\uff0c\u8001\u8872\u672c\u5e94\u6548\u52b3\uff0c\u4e0d\u8fc7\uff0c\u5409\u65bd\u4e3b\u5374\u8bf4\u4e0d\u5fc5\uff0c\u7a76\u7adf\u5982\u4f55\uff0c\u5012\u53eb\u8001\u8872\u4e3a\u96be\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u773c\u89c1\u5165\u4e91\u5927\u5e08\u4e3a\u96be\uff0c\u4e0d\u7531\u5f97\u63a5\u53e3\u4e00\u7b11\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u8fd9\u4e8b\u679c\u7136\u5982\u90a3\u5409\u8001\u6240\u8bf4\uff0c\u5c31\u4e0d\u7528\u6025\u5728\u4e00\u65f6\u7684\u4e86\u3002\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u4e00\u6014\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\u662f\u8bf4\u2026\u2026\u76ee\u4e0b\u4e0d\u5fc5\u6d3e\u4eba\u51fa\u5916\u5bfb\u627e\u90d1\u5927\u521a\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u6b63\u662f\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u5411\u90a3\u4eba\u771f\u5927\u5e08\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u4e0d\u7528\u6d3e\u4eba\u53bb\u627e\u90a3\u90d1\u65bd\u4e3b\u4e86\uff01\u4e0d\u8fc7\uff0c\u5018\u662f\u90d1\u65bd\u4e3b\u518d\u6765\u5c11\u6797\uff0c\u4f60\u4eec\u83ab\u8981\u5fd8\u4e86\u8981\u4ed6\u524d\u6765\u89c1\u8fc7\u672c\u5ea7\u3002\u201d \u3000\u3000\u5165\u771f\u5927\u5e08\u5408\u5341\u4e00\u793c\u9053\uff1a\u201c\u5c0f\u5f1f\u8bb0\u4e0b\u4e86\uff01\u201d\u8f6c\u8eab\u5927\u6b65\u800c\u53bb\u3002 \u3000\u3000\u6b64\u523b\uff0c\u8fd9\u970d\u9e23\u98ce\u5df2\u7136\u8981\u90a3\u7fc1\u6606\u4ed1\u5199\u4e0b\u4e00\u9053\u624b\u4e66\uff0c\u53bb\u5230\u524d\u5bfa\uff0c\u53eb\u5b88\u5019\u7684\u5d69\u5c71\u7684\u4e10\u5e2e\u5f1f\u5b50\uff0c\u7528\u98de\u9e3d\u4f20\u8baf\u4e4b\u65b9\uff0c\u901a\u77e5\u91d1\u9675\u5206\u5802\uff0c\u5e2e\u52a9\u5bfb\u627e\u53f6\u5802\u4e3b\u7684\u80de\u59b9\u53f6\u4e16\u82b3\uff01 \u3000\u3000\u7b49\u5230\u7fc1\u6606\u4ed1\u56de\u5230\u9759\u5ba4\uff0c\u6709\u5173\u65b9\u96ea\u5b9c\u7684\u79c1\u4eba\u4e4b\u4e8b\uff0c\u7b97\u662f\u6709\u4e86\u4e00\u4e2a\u6bb5\u843d\u3002 \u3000\u3000\u5f53\u4e0b\u90a3\u5165\u4e91\u5927\u5e08\u53c8\u8a00\u5f52\u6b63\u4f20\uff0c\u8981\u65b9\u96ea\u5b9c\u9886\u8854\uff0c\u5199\u5c31\u4e00\u5c01\u4e66\u4fe1\uff0c\u901a\u77e5\u6b66\u5f53\u3001\u534e\u5c71\u7b49\u4e5d\u5927\u95e8\u6d3e\uff0c\u8be6\u7ec6\u53d9\u660e\u4e94\u5927\u9b54\u4e3b\u4e1a\u5df2\u91cd\u51fa\u6b66\u6797\u5e76\u4e14\u6709\u5fc3\u72ec\u9738\u6c5f\u6e56\uff0c\u9010\u4e2a\u5c06\u4e5d\u5927\u95e8\u6d3e\u98a0\u8986\uff0c\u5c11\u6797\u5df2\u7136\u5148\u8d77\u6218\u673a\uff0c\u8bf7\u5404\u5927\u95e8\u6d3e\u7684\u638c\u95e8\u4eba\uff0c\u4e8e\u4e24\u6708\u4e4b\u540e\u7684\u6708\u534a\uff0c\u524d\u6765\u5d69\u5c71\u5c11\u6797\u805a\u4f1a\uff0c\u5171\u5546\u5bf9\u4ed8\u4e94\u5927\u9b54\u4e3b\u4e4b\u7b56\u3002 \u3000\u3000\u672b\u5c3e\u7684\u7f72\u540d\uff0c\u4e43\u662f\u7531\u65b9\u96ea\u5b9c\u9886\u8854\uff0c\u4f9d\u6b21\u624d\u662f\u970d\u9e23\u98ce\u3001\u5165\u4e91\u5927\u5e08\u3001\u4e10\u5e2e\u4e09\u8001\u3001\u5c11\u6797\u957f\u8001\u548c\u5927\u6f20\u795e\u624b\u5b8b\u5b5a\u7b49\u4eba\u3002 \u3000\u3000\u4e66\u4fe1\u62df\u5c31\uff0c\u5df2\u662f\u5929\u4eae\u65f6\u5206\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u6367\u7740\u4e66\u4fe1\uff0c\u4eb2\u624b\u4ea4\u4ee3\u5bfa\u4e2d\u7ecf\u623f\u5f1f\u5b50\uff0c\u4f9d\u5f0f\u817e\u5199\u4e03\u4efd\uff0c\u7136\u540e\u53c8\u9009\u4e86\u4e03\u540d\u6cd5\u5b57\u8f88\u9ad8\u50e7\uff0c\u4e13\u7a0b\u9001\u81f3\u5404\u5927\u95e8\u6d3e\u7684\u603b\u575b\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u7b49\u4eba\uff0c\u4e5f\u5c31\u5728\u5c11\u6797\u4f4f\u4e86\u4e0b\u6765\uff0c\u7b49\u7740\u90a3\u4e24\u6708\u4e4b\u540e\u7684\u4e5d\u5927\u95e8\u6d3e\u638c\u95e8\u4eba\u5171\u540c\u805a\u9996\u7684\u76db\u4f1a\u3002 \u3000\u3000\u8fd9\u6bb5\u65e5\u5b50\u4e4b\u4e2d\uff0c\u6700\u5148\u89c9\u5f97\u4e0d\u4fbf\u7684\uff0c\u5c31\u662f\u5b89\u5c0f\u840d\u3002 \u3000\u3000\u5979\u4ee5\u4e00\u4e2a\u5973\u513f\u5bb6\u7684\u8eab\u4efd\uff0c\u4f4f\u5728\u5c11\u6797\u5bfa\u4e2d\uff0c\u8bf4\u6765\u5e94\u662f\u524d\u6240\u672a\u6709\u7684\u4e8b\uff0c\u5e78\u800c\u65b9\u96ea\u5b9c\u5bf9\u5979\u5341\u5206\u5173\u7167\uff0c\u8bb8\u591a\u5c0f\u4e8b\uff0c\u90fd\u4ee3\u5979\u6599\u7406\uff0c\u518d\u52a0\u4e0a\u5b8b\u5b5a\u6df1\u77e5\u5e95\u7ec6\uff0c\u4e5f\u4e0d\u65f6\u5728\u65c1\u5e2e\u5fd9\uff0c\u603b\u7b97\u672a\u66fe\u9732\u51fa\u9a6c\u811a\u3002 \u3000\u3000\u8fd9\u4e00\u5929\uff0c\u65b9\u96ea\u5b9c\u7b49\u4eba\u5728\u5bfa\u4e2d\u5df2\u4f4f\u4e86\u4e00\u4e2a\u6708\u96f6\u4e09\u5929\uff0c\u5bfa\u4e2d\u5348\u8bfe\u65b9\u7f62\uff0c\u5b89\u5c0f\u840d\u62c9\u7740\u65b9\u96ea\u5b9c\uff0c\u8981\u5230\u90a3\u592a\u5ba4\u5cf0\u5934\u53bb\u63a2\u89c6\u5979\u5728\u90a3\u513f\u7b51\u8d77\u7684\u4e00\u5ea7\u5c0f\u9e1f\u5de2\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4e9b\u65e5\u5b50\u6765\uff0c\u4ed6\u4eec\u51e0\u4e4e\u6bcf\u9694\u4e24\u5929\uff0c\u4fbf\u8981\u53bb\u90a3\u592a\u5ba4\u5cf0\u5934\u4e00\u8d9f\uff0c\u5b89\u5c0f\u840d\u5e74\u8f7b\u597d\u73a9\uff0c\u5979\u53bb\u90a3\u592a\u5ba4\u5cf0\u5934\uff0c\u53d1\u73b0\u4e86\u4e00\u79cd\u91d1\u7d2b\u8272\u7684\u5c0f\u96c0\uff0c\u4fbf\u5728\u90a3\u77f3\u7f1d\u4e4b\u4e2d\u3001\u62f4\u4e86\u4e9b\u67af\u679d\u91ce\u8349\uff0c\u4e3a\u90a3\u5c0f\u96c0\u513f\u4eec\u642d\u4e86\u4e00\u4e2a\u9e1f\u5de2\uff0c\u5e0c\u671b\u90a3\u96c0\u513f\u5728\u5de2\u5185\u4e0b\u86cb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u6bd4\u5b89\u5c0f\u840d\u5927\u4e0d\u4e86\u591a\u5c11\uff0c\u8fd9\u7b49\u620f\u800d\u4e4b\u4e8b\uff0c\u5012\u4e5f\u9017\u5f97\u8d77\u4ed6\u7684\u5174\u8da3\uff0c\u6545\u5219\u5b89\u5c0f\u840d\u4e00\u8bf4\u8981\u53bb\uff0c\u4ed6\u4e5f\u672a\u52a0\u8003\u8651\uff0c\u4fbf\u7b54\u5e94\u4e0b\u6765\u3002\u53cc\u53cc\u771f\u5954\u592a\u5ba4\u5cf0\u5934\u800c\u53bb\u3002 \u3000\u3000\u4e24\u4eba\u62b5\u5cf0\u9876\uff0c\u5ffd\u7136\u89c9\u51fa\u6709\u4e9b\u4e0d\u5bf9\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u53ea\u89c9\u8fd9\u592a\u5ba4\u5cf0\u9876\u5934\uff0c\u4f3c\u662f\u6709\u4e86\u522b\u4eba\u6765\u8fc7\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u4e5f\u6709\u7740\u8fd9\u7b49\u611f\u89c9\uff0c\u5f53\u5979\u4f38\u624b\u5230\u77f3\u7f1d\u4e4b\u4e2d\uff0c\u62b1\u51fa\u9e1f\u5de2\u4ee5\u540e\uff0c\u4e24\u4eba\u7684\u7591\u8651\uff0c\u679c\u7136\u6210\u4e86\u4e8b\u5b9e\u3002 \u3000\u3000\u5927\u5ba4\u5cf0\u5934\uff0c\u771f\u662f\u6709\u4ec0\u4e48\u4eba\u6765\u8fc7\uff1f \u3000\u3000\u5b89\u5c0f\u840d\u6240\u642d\u8d77\u7684\u9e1f\u5de2\u4e4b\u4e2d\uff0c\u6b64\u523b\u5c45\u7136\u6709\u4e86\u4e09\u679a\u5341\u5206\u602a\u5f02\u7684\u5c0f\u86cb\u3002 \u3000\u3000\u672c\u6765\uff0c\u5b89\u5c0f\u840d\u7684\u539f\u610f\uff0c\u642d\u4e86\u9e1f\u5de2\uff0c\u5c31\u662f\u60f3\u8fd9\u9e1f\u513f\u4e0b\u86cb\uff0c\u5de2\u5185\u6709\u4e86\u9e1f\u86cb\uff0c\u81ea\u662f\u5e94\u8be5\u6b22\u559c\u624d\u662f\u3002 \u3000\u3000\u4f46\u5979\u5374\u5728\u89c1\u4e86\u90a3\u4e09\u679a\u9e1f\u86cb\u4ee5\u540e\uff0c\u5927\u4e3a\u5931\u8272\uff0c\u51e0\u4e4e\u5c06\u624b\u4e2d\u7684\u9e1f\u5de2\u90fd\u6454\u6389\u4e86\uff0c\u53ea\u56e0\u90a3\u9e1f\u5de2\u4e4b\u4e0b\uff0c\u6401\u4e86\u4e00\u5f20\u767d\u7b3a\u3002 \u3000\u3000\u9e1f\u53ef\u4ee5\u751f\u86cb\uff0c\u4f46\u51b3\u4e0d\u4f1a\u751f\u767d\u7b3a\u6765\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u53d1\u73b0\u4e86\u5b89\u5c0f\u840d\u90a3\u5931\u60ca\u7684\u8138\u8272\uff0c\u8fde\u5fd9\u51d1\u4e86\u8fc7\u6765\uff0c\u51dd\u76ee\u5411\u9e1f\u5de2\u4e4b\u5185\u671b\u53bb\uff0c\u84e6\u7136\u95f4\uff0c\u4ed6\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u602a\u554a\uff01\u8fd9\u662f\u4ec0\u4e48\u4eba\u653e\u7684\u9e1f\u86cb\uff1f\u201d \u3000\u3000\u6562\u60c5\u4ed6\u4eec\u90fd\u8ba4\u4e3a\uff0c\u8fd9\u4e09\u53ea\u5c0f\u86cb\uff0c\u4e0d\u662f\u90a3\u91d1\u7d2b\u8272\u9e1f\u96c0\u6240\u751f\uff01 \u3000\u3000\u5b89\u5c0f\u840d\u6447\u5934\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u6653\u5f97\uff01\u65b9\u5144\uff0c\u4e5f\u8bb8\u2026\u2026\u8fd9\u592a\u5ba4\u5cf0\uff0c\u6765\u4e86\u4e13\u95e8\u6349\u4e4c\u7684\u4eba\uff0c\u628a\u90a3\u5bf9\u96c0\u9e1f\u6355\u53bb\u4e86\uff01\u201d \u3000\u3000\u5979\u751a\u662f\u559c\u7231\u90a3\u4e00\u5bf9\u91d1\u96c0\uff0c\u662f\u4ee5\u82b3\u5fc3\u4e4b\u4e2d\uff0c\u5bf9\u90a3\u96c0\u513f\u7684\u5b89\u5371\uff0c\u751a\u662f\u6302\u8651\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4ed4\u7ec6\u5730\u5bdf\u770b\u4e86\u90a3\u4e09\u679a\u86cb\u534a\u664c\uff0c\u5ffd\u7136\u5931\u7b11\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u8fd9\u662f\u4ec0\u4e48\u86cb\uff0c\u4f60\u77a7\u51fa\u6765\u4e86\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6447\u5934\u9053\uff1a\u201c\u77a7\u4e0d\u51fa\u6765\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u597d\u50cf\u9e4c\u9e51\u86cb\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u76b1\u7709\u9053\uff1a\u201c\u65b9\u5144\uff0c\u54b1\u4eec\u600e\u4e48\u529e\uff1f\u7ba1\u4ed6\u662f\u4ec0\u4e48\u86cb\uff0c\u6211\u53ea\u8981\u90a3\u2014\u5bf9\u91d1\u96c0\u513f\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u89c1\u5979\u4e00\u8138\u90fd\u662f\u6101\u6025\u4e4b\u8272\uff0c\u4e0d\u7531\u5f97\u7b11\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u54b1\u4eec\u5728\u8fd9\u513f\u7b49\u4e0a\u4e00\u523b\u77a7\u77a7\uff0c\u4e5f\u8bb8\uff0c\u90a3\u5bf9\u9e1f\u513f\u4e43\u662f\u51fa\u5916\u89c5\u98df\u53bb\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u4e00\u60f3\uff0c\u5ffd\u7136\u4f38\u624b\u5c06\u90a3\u4e09\u53ea\u9e1f\u86cb\u6293\u8d77\uff0c\u653e\u5728\u4e00\u8fb9\u7684\u8349\u4e1b\u4e4b\u4e2d\uff0c\u63a5\u9053\uff1a\u201c\u65b9\u5144\uff0c\u54b1\u4eec\u5c31\u5728\u8fd9\u513f\u5b88\u7740\uff0c\u5982\u662f\u8fd9\u9e1f\u86cb\u4e43\u662f\u90a3\u5bf9\u91d1\u96c0\u6240\u751f\uff0c\u5b83\u4eec\u56de\u6765\u4e4b\u540e\uff0c\u4e0d\u89c1\u4e86\u9e1f\u86cb\uff0c\u5b9a\u7136\u4f1a\u98de\u65cb\u5bfb\u627e\uff0c\u5982\u82e5\u5b83\u4eec\u4e0d\u66fe\u6709\u6240\u4e3e\u52a8\uff0c\u90a3\u5c31\u6709\u4eba\u6765\u8fc7\u8fd9\u91cc\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u60f3\u5f97\u5012\u662f\u4e0d\u9519\uff0c\u53ea\u6015\u90a3\u9e1f\u513f\u82e5\u662f\u88ab\u4eba\u6349\u8d70\uff0c\u54b1\u4eec\u5c31\u7b49\u4e0d\u5230\u5b83\u4eec\u56de\u6765\u4e86\uff01\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u867d\u4f5c\u6b64\u60f3\uff0c\u53e3\u4e2d\u5374\u662f\u4e0d\u66fe\u8bf4\u51fa\uff0c\u53ea\u7b11\u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6b63\u662f\uff0c\u54b1\u4eec\u53ef\u4ee5\u7b49\u7740\u77a7\u4e0a\u4e00\u77a7\uff01\u201d \u3000\u3000\u5f53\u4e0b\u4e24\u4eba\u4fbf\u8f6c\u8eab\u5411\u90a3\u5927\u77f3\u4e4b\u540e\u4e00\u5904\u907f\u98ce\u77f3\u7a9f\uff0c\u8d70\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u84e6\u7136\u95f4\uff0c\u4e24\u4eba\u540c\u65f6\u6123\u4f4f\u4e86\uff01 \u3000\u3000\u539f\u6765\u8fd9\u4e2a\u77f3\u7a9f\u4e4b\u4e2d\uff0c\u9664\u4e86\u6709\u51e0\u53ea\u733f\u7334\u8fc7\u53bb\u5e38\u5728\u5176\u4e2d\u620f\u800d\u800c\u5916\uff0c\u5176\u6237\u5411\u6765\u522b\u65e0\u4ed6\u7269\uff0c\u4f46\u6b64\u523b\u4e24\u4eba\u76ee\u5149\u6240\u53ca\uff0c\u5374\u662f\u53d1\u73b0\u90a3\u77f3\u7a9f\u4e4b\u4e2d\uff0c\u8eba\u4e86\u4e00\u4e2a\u751f\u4eba\u3002 \u3000\u3000\u867d\u7136\u90a3\u77f3\u7a9f\u4e4b\u4e2d\uff0c\u751a\u4e3a\u9634\u6697\uff0c\u4f46\u56e0\u4e24\u4eba\u76ee\u529b\u7686\u4f73\uff0c\u8db3\u4ee5\u4e00\u773c\u671b\u53bb\uff0c\u4fbf\u5df2\u770b\u5f97\u660e\u660e\u767d\u767d\uff0c\u90a3\u8eba\u5728\u77f3\u7a9f\u4e2d\u7684\u751f\u4eba\uff0c\u4e43\u662f\u4e00\u4e2a\u84ec\u9996\u57a2\u9762\uff0c\u8863\u886b\u4e0d\u6574\u7684\u8001\u8005\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u867d\u7136\u80c6\u5b50\u751a\u5927\uff0c\u4f46\u5ffd\u7136\u95f4\u5728\u8fd9\u7b49\u6240\u5728\uff0c\u89c1\u5230\u4e86\u8fd9\u7b49\u602a\u4eba\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u7981\u4e0d\u51fa\u60ca\u53eb\u4e00\u58f0\uff01 \u3000\u3000\u5979\u8fd9\u4e00\u58f0\u60ca\u53eb\uff0c\u4f3c\u662f\u5c06\u90a3\u719f\u7761\u4e4b\u4e2d\u7684\u8001\u4eba\uff0c\u53eb\u5f97\u9192\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u6253\u4e86\u4e00\u4e2a\u957f\u957f\u7684\u5475\u6b20\uff0c\u7f13\u7f13\u5730\u5750\u8eab\u8d77\u6765\uff0c\u770b\u4e86\u4e24\u4eba\u4e00\u773c\uff0c\u7adf\u662f\u4e00\u8bed\u672a\u53d1\uff0c\u53c8\u8eba\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u8001\u4eba\u8fd9\u7b49\u602a\u5f02\u7684\u4e3e\u52a8\uff0c\u4f7f\u5f97\u65b9\u96ea\u5b9c\u5927\u4e3a\u5947\u602a\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u4eba\u600e\u5730\u5982\u6b64\u9547\u5b9a\uff0c\u800c\u4e14\uff0c\u89c1\u5230\u4e86\u6211\u4eec\uff0c\u4e3a\u4f55\u4e1d\u6beb\u4e0d\u89c9\u5f97\u8be7\u5f02\u5462\uff1f\u201d \u3000\u3000\u8f6c\u5ff5\u4e4b\u95f4\uff0c\u53ea\u542c\u5b89\u5c0f\u840d\u5df2\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5582\uff0c\u4f60\u662f\u4ec0\u4e48\u4eba\uff0c\u600e\u4f1a\u8dd1\u5230\u8fd9\u91cc\u7761\u89c9\uff1f\u201d \u3000\u3000\u90a3\u8001\u4eba\u76b1\u4e86\u76b1\u957f\u7709\uff0c\u54fc\u4e86\u4e00\u58f0\u3002\u61d2\u6d0b\u6d0b\u5730\u91cd\u65b0\u5750\u8d77\u8eab\u5b50\uff0c\u7741\u7740\u4e00\u53ea\u773c\u775b\uff0c\u76f4\u6123\u6123\u5730\u5411\u4e24\u4eba\u671b\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u53ea\u662f\uff0c\u4ed6\u4f9d\u65e7\u4e0d\u66fe\u8bf4\u8bdd\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u88ab\u4ed6\u770b\u5f97\u6709\u4e9b\u80c6\u602f\uff0c\u9000\u4e86\u4e00\u6b65\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4f60\u600e\u4e48\u4e0d\u8bb2\u8bdd\u554a\uff1f\u83ab\u975e\u662f\u4e2a\u54d1\u5b50\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u65f6\u5374\u7559\u5fc3\u5230\u4e86\u4e00\u4ef6\u4e8b\uff0c\u539f\u6765\u4ed6\u53d1\u89c9\u90a3\u8001\u4eba\u7684\u8eab\u4e0b\uff0c\u6a2a\u57ab\u7740\u4e00\u67c4\u5b9d\u5251\uff0c\u5355\u4ece\u90a3\u80fd\u591f\u89c1\u5230\u7684\u5251\u9798\u770b\u6765\uff0c\u90a3\u652f\u5b9d\u5251\uff0c\u5fc5\u662f\u4e00\u652f\u6709\u540d\u7684\u53e4\u4ee3\u5229\u5668\u3002 \u3000\u3000\u4ed6\u8ba4\u4e3a\u80fd\u591f\u6301\u6709\u8fd9\u7b49\u73cd\u8d35\u5b9d\u5251\u4e4b\u4eba\uff0c\u5fc5\u662f\u6b66\u6797\u4e4b\u4e2d\uff0c\u5927\u6709\u540d\u671b\u4e4b\u58eb\u3002 \u3000\u3000\u662f\u4ee5\uff0c\u5b89\u5c0f\u840d\u8bdd\u97f3\u4e00\u843d\uff0c\u4ed6\u7acb\u5373\u4f4e\u58f0\u9053\uff1a\u201c\u8d24\u5f1f\u4e0d\u53ef\u65e0\u793c\uff01\u201d \u3000\u3000\u4ed6\u53cc\u624b\u62b1\u62f3\uff0c\u5411\u90a3\u8001\u4eba\u4e00\u793c\u3002\u63a5\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u665a\u8f88\u65b9\u96ea\u5b9c\u8fd9\u53a2\u6709\u793c\u4e86\u3002\u201d \u3000\u3000\u90a3\u8001\u4eba\u76ee\u5149\u4e00\u8f6c\uff0c\u51b7\u51b7\u5730\u5f00\u4e86\u8154\u9053\uff1a\u201c\u8fd9\u4e2a\u4e2b\u5934\uff0c\u662f\u4f60\u4ec0\u4e48\u4eba\uff1f\u4e00\u4e2a\u4eba\u82e5\u662f\u5a36\u4e86\u8fd9\u7b49\u51f6\u9738\u9738\u7684\u5ab3\u5987\uff0c\u90a3\u5c82\u4e0d\u662f\u5012\u6bd2\u900f\u9876\u4e86\uff1f\u201d \u3000\u3000\u8001\u4eba\u4e00\u5f00\u53e3\uff0c\u7adf\u7136\u662f\u8bf4\u51fa\u8fd9\u7b49\u8bdd\u6765\uff0c\u4e0d\u7981\u628a\u90a3\u5b89\u5c0f\u840d\u7f9e\u5f97\u7c89\u9762\u901a\u7ea2\uff0c\u534a\u664c\u4e0d\u6562\u62ac\u5934\u3002 \u3000\u3000\u5979\u90a3\u82b3\u5fc3\u4e4b\u4e2d\uff0c\u53ef\u662f\u65e2\u7f9e\u53c8\u607c\uff0c\u6ee1\u60f3\u56de\u656c\u90a3\u8001\u4eba\u51e0\u53e5\uff0c\u4f46\u6015\u65b9\u96ea\u5b9c\u751f\u6c14\uff0c\u53ea\u597d\u9690\u5fcd\u7740\u4f4e\u5934\u4e0d\u8bed\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4f3c\u4e5f\u5927\u4e3a\u610f\u5916\u5f97\u4e00\u6014\uff0c\u6599\u4e0d\u5230\u8fde\u5c11\u6797\u638c\u95e8\u548c\u4e10\u5e2e\u5e2e\u4e3b\u90fd\u672a\u77a7\u51fa\u5b89\u5c0f\u840d\u4e43\u662f\u5973\u513f\u4e4b\u8eab\uff0c\u8fd9\u8001\u4eba\u53ea\u4e0d\u8fc7\u770b\u5979\u4e00\u773c\uff0c\u5c31\u5df2\u770b\u7834\uff0c\u8db3\u89c1\u8fd9\u8001\u4eba\u773c\u529b\u4e4b\u5f3a\uff0c\u771f\u662f\u4e0d\u540c\u51e1\u54cd\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u867d\u7136\u751a\u4e3a\u5403\u60ca\uff0c\u4f46\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u5b89\u5c0f\u840d\u59d1\u5a18\u2026\u2026\u5e76\u975e\u662f\u2026\u2026\u201d \u3000\u3000\u4ed6\u672c\u662f\u60f3\u8bf4\uff0c\u5979\u5e76\u975e\u665a\u8f88\u7684\u5ab3\u5987\uff0c\u4f46\u4e00\u773c\u77a7\u89c1\u5b89\u5c0f\u840d\u90a3\u7b49\u542b\u7f9e\u795e\u60c5\uff0c\u4fbf\u8fde\u5fd9\u6b62\u4f4f\u4e86\u8bdd\u97f3\uff0c\u628a\u90a3\u8981\u8bf4\u4e4b\u8bdd\uff0c\u786c\u884c\u54bd\u4e86\u56de\u53bb\u3002 \u3000\u3000\u90a3\u8001\u4eba\u5475\u5475\u4e00\u7b11\u9053\uff1a\u201c\u5b89\u59d1\u5a18\u5417\uff1f\u90a3\u5979\u4e0d\u662f\u4f60\u5c0f\u5b50\u7684\u5ab3\u5987\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u9519\u2026\u2026\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u89c9\u5f97\u8fd9\u4f4d\u8001\u4eba\u6709\u4e9b\u5947\u602a\uff0c\u8fd9\u7b49\u522b\u4eba\u4e4b\u4e8b\uff0c\u4e3a\u4f55\u4ed6\u504f\u8981\u5982\u6b64\u6709\u5174\u8da3\u5730\u95ee\u6765\u95ee\u53bb\u5462\uff1f \u3000\u3000\u4f55\u51b5\uff0c\u8fd9\u79cd\u4e8b\u4e0d\u4f46\u5b89\u5c0f\u840d\u542c\u6765\u96be\u53d7\u3001\u5bb3\u7f9e\uff0c\u8fde\u81ea\u5df1\u90fd\u89c9\u5f97\u4e0d\u597d\u610f\u601d\u4e86\uff01 \u3000\u3000\u8001\u4eba\u6839\u672c\u4f3c\u662f\u672a\u66fe\u77a7\u5230\u65b9\u96ea\u5b9c\u7684\u4e0d\u5b89\uff0c\u4e24\u773c\u7ffb\u4e86\u4e00\u7ffb\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u5230\u8fd9\u513f\u6765\u5e72\u4ec0\u4e48\uff0c\u53ef\u662f\u4ee5\u4e3a\u8fd9\u513f\u6ca1\u6709\u4eba\uff0c\u8dd1\u6765\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u542c\uff1f\u89c9\u5f97\u5982\u5bb9\u4ed6\u518d\u8bf4\u4e0b\u53bb\uff0c\u90a3\u5fc5\u7136\u662f\u5341\u5206\u96be\u542c\uff0c\u662f\u4ee5\u8fde\u5fd9\u6c89\u58f0\u9053\uff1a\u201c\u8001\u524d\u8f88\u2026\u2026\u201d \u3000\u3000\u90a3\u8001\u4eba\u5ffd\u7136\u53cc\u76ee\u4e00\u62ac\uff0c\u5bd2\u5149\u66b4\u5c04\uff0c\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8001\u4eba\u5bb6\u7684\u5c0a\u59d3\u5927\u540d\uff0c\u53ef\u5426\u89c1\u544a\uff1f\u201d \u3000\u3000\u90a3\u8001\u4eba\u770b\u4e86\u4e00\u773c\uff0c\u84e6\u5730\u5927\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u60f3\u77e5\u9053\u8001\u592b\u7684\u540d\u53f7\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u539f\u662f\u8981\u8bf7\u6559\uff01\u201d \u3000\u3000\u90a3\u8001\u4eba\u5ffd\u7136\u4e00\u4e2a\u7ffb\u8eab\uff0c\u8df3\u4e86\u8d77\u6765\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4f60\u4eec\u522b\u5fd9\u7740\u95ee\u8001\u592b\u7684\u540d\u53f7\uff0c\u5012\u662f\u4f60\u4eec\u4e3a\u4f55\u8dd1\u5230\u8fd9\u592a\u5ba4\u5cf0\u7edd\u9876\u6765\u7684\uff0c\u5148\u5f97\u8ddf\u8001\u592b\u8bf4\u660e\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u9053\uff1a\u201c\u8fd9\u5c31\u5947\u4e86\uff01\u6211\u4eec\u6ca1\u6709\u95ee\u4ed6\u600e\u5730\u6765\u6b64\uff0c\u4ed6\u5012\u5148\u95ee\u8d77\u6211\u4eec\u6765\u4e86\uff01\u201d \u3000\u3000\u4f46\u4ed6\u53e3\u4e2d\u5374\u8bf4\uff1a\u201c\u665a\u8f88\u773c\u4e0b\u4f4f\u5728\u90a3\u5c11\u6797\u5bfa\u4e2d\uff0c\u6bcf\u5929\u5747\u66fe\u6765\u6b64\uff01\u201d \u3000\u3000\u8001\u4eba\u5927\u7b11\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4f60\u4eec\u5929\u5929\u6765\u6b64\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u8001\u4eba\u7b11\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u90a3\u662f\u8001\u592b\u6bd4\u4f60\u4eec\u540e\u6765\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u9053\uff1a\u8c01\u8bf4\u4e0d\u662f\u5462\uff1f\u53ea\u662f\uff0c\u4ed6\u53e3\u4e2d\u5374\u4e0d\u662f\u8fd9\u4e48\u56de\u7b54\uff0c\u5374\u9053\uff1a\u201c\u8001\u524d\u8f88\u662f\u4eca\u65e5\u65b9\u5230\u6b64\u5904\u7684\u5417\uff1f\u201d \u3000\u3000\u8001\u4eba\u6447\u4e86\u6447\u5934\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4e0d\u662f\uff01\u201d \u3000\u3000\u8bed\u97f3\u987f\u4f4f\uff0c\u5ffd\u7136\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u4f60\u53ef\u4ee5\u4e3a\u8001\u592b\u662f\u8fd9\u592a\u5ba4\u5cf0\u9876\u7684\u751f\u5ba2\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u96be\u9053\u4e0d\u662f\uff1f\u201d \u3000\u3000\u8001\u4eba\u9053\uff1a\u201c\u5f53\u7136\u4e0d\u662f\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5728\u65c1\uff0c\u672c\u662f\u4f4e\u5934\u6015\u7f9e\uff0c\u4e0d\u6562\u8bf4\u8bdd\uff0c\u8fd9\u65f6\u542c\u90a3\u8001\u4eba\u8bf4\u662f\u4ed6\u4eff\u4f5b\u4e5f\u5e38\u5728\u8fd9\u592a\u5ba4\u5cf0\u5934\uff0c\u4e00\u65f6\u5fcd\u4e0d\u4f4f\u63a5\u9053\uff1a\u201c\u90a3\u600e\u5730\u524d\u51e0\u5929\u6ca1\u6709\u89c1\u5230\u4f60\u5462\uff1f\u201d \u3000\u3000\u8001\u4eba\u7b11\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u4eec\u672a\u66fe\u89c1\u5230\u8001\u592b\uff0c\u4e0d\u89c1\u5f97\u5c31\u53ef\u8bf4\u8001\u592b\u4e0d\u662f\u5e38\u4f4f\u592a\u5ba4\u5cf0\u7684\u4e3b\u4eba\u5427\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6014\u4e86\u4e00\u6014\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u8001\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u8001\u592b\u79bb\u5f00\u592a\u5ba4\uff0c\u5df2\u7ecf\u534a\u5e74\u5de6\u53f3\uff0c\u6628\u591c\u65b9\u59cb\u5f52\u6765\u3002\u4f60\u4eec\u4e0d\u77e5\u9053\uff0c\u90a3\u4e5f\u4e0d\u8db3\u4e3a\u602a\u7684\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u8001\u662f\u51fa\u5916\u4e91\u6e38\u53bb\u4e86\u3002\u201d \u3000\u3000\u8001\u4eba\u9053\uff1a\u201c\u8001\u592b\u4e91\u6e38\u4ec0\u4e48\uff1f\u4f60\u4eec\u8fd9\u4e9b\u5e74\u8f7b\u4eba\uff0c\u4e00\u5929\u5230\u665a\u53ea\u77e5\u8d2a\u73a9\uff0c\u6b8a\u4e0d\u77e5\u8001\u592b\u6b64\u756a\u51fa\u5916\uff0c\u4e43\u662f\u5728\u5bfb\u627e\u4e00\u79cd\u4e16\u95f4\u5c11\u89c1\u7684\u73cd\u5947\u4e4b\u7269\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u4f38\u624b\u6293\u4e86\u6293\u90a3\u84ec\u6563\u7684\u4e71\u53d1\uff0c\u9053\uff1a\u201c\u603b\u7b97\u7687\u5929\u4e0d\u8d1f\u82e6\u5fc3\u4eba\uff0c\u7ec8\u4e8e\u8ba9\u8001\u5927\u627e\u5230\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u65f6\u53ea\u89c9\u8fd9\u4e2a\u8001\u4eba\u7684\u6027\u683c\u5341\u5206\u6709\u8da3\uff0c\u867d\u7136\u5e74\u7eaa\u751a\u5927\uff0c\u4f46\u8a00\u8c08\u4e3e\u6b62\uff0c\u5374\u4e0d\u5931\u8d64\u5b50\u5929\u771f\uff0c\u5f53\u4e0b\u7b11\u9053\uff1a\u201c\u8001\u524d\u8f88\u627e\u7684\u662f\u4ec0\u4e48\u73cd\u5947\u4e4b\u7269\uff1f\u201d \u3000\u3000\u4ed6\u8bdd\u5df2\u51fa\u53e3\uff0c\u65b9\u89c9\u6709\u4e9b\u4e0d\u59a5\u3002 \u3000\u3000\u8bd5\u60f3\uff0c\u5bf9\u65b9\u8fde\u59d3\u540d\u90fd\u4e0d\u613f\u8bf4\u51fa\u6765\uff0c\u53c8\u600e\u4f1a\u628a\u90a3\u5bfb\u627e\u7684\u957f\u8fbe\u534a\u5e74\u65f6\u5149\u7684\u73cd\u5947\u4e4b\u7269\uff0c\u544a\u8bc9\u81ea\u5df1\u5462\uff1f\u65b9\u96ea\u5b9c\u521a\u81ea\u89c9\u5f97\u95ee\u5f97\u6709\u4e9b\u5510\u7a81\uff0c\u6b8a\u4e0d\u6599\u90a3\u8001\u4eba\u7adf\u7136\u54c8\u54c8\u5927\u77e5\u9053\uff1a\u201c\u90a3\u73cd\u5947\u4e4b\u7269\u5417\uff1f\u8bf4\u6765\u4e0d\u503c\u4e00\u7b11\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u542c\u5f97\u5446\u4e86\u4e00\u5446\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u8bdd\uff1f\u5373\u662f\u4e16\u95f4\u73cd\u5947\u4e4b\u7269\uff0c\u53c8\u600e\u4e48\u4f1a\u8bf4\u6765\u4e0d\u503c\u4e00\u7b11\uff1f\u201d \u3000\u3000\u8f6c\u5ff5\u81f3\u6b64\uff0c\u4e0d\u7981\u7b11\u9053\uff1a\u201c\u8001\u4eba\u5bb6\uff0c\u4f60\u8001\u8fd9\u8bdd\u53eb\u4eba\u597d\u751f\u4e0d\u61c2\u554a\uff01\u201d \u3000\u3000\u8001\u4eba\u7b11\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u59d1\u5a18\u8ba4\u4e3a\u8001\u592b\u8bf4\u9519\u4e86\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u662f\u2026\u2026\uff0c\u665a\u8f88\u8ba4\u4e3a\uff0c\u4e3e\u51e1\u4e16\u4e0a\u73cd\u5947\u4e4b\u7269\uff0c\u5fc5\u7136\u4e0d\u4f1a\u662f\u4e0d\u503c\u4e00\u7b11\uff0c\u5426\u5219\uff0c\u90a3\u53c8\u6709\u4ec0\u4e48\u597d\u73cd\u8d35\u7684\u5462\uff1f\u201d \u3000\u3000\u8001\u4eba\u95fb\u8a00\uff0c\u70b9\u4e86\u70b9\u84ec\u9996\uff0c\u9053\uff1a\u201c\u6709\u9053\u7406\uff01\u53ea\u662f\uff0c\u5176\u4e2d\u6709\u4e00\u5173\u952e\uff0c\u4f60\u5374\u4e0d\u4f1a\u6ce8\u610f\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4ec0\u4e48\u5173\u952e\uff1f\u201d \u3000\u3000\u8001\u4eba\u9053\uff1a\u201c\u4eba\u4e4b\u6240\u597d\uff0c\u5404\u6709\u4e0d\u540c\uff0c\u8001\u592b\u8ba4\u4e3a\u7a00\u4e16\u4e4b\u7269\uff0c\u6216\u8bb8\u5728\u4ed6\u4eba\u773c\u4e2d\uff0c\u5374\u662f\u4e00\u6587\u4e0d\u503c\u3002\u4e5f\u672a\u53ef\u77e5\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u4e00\u6014\u9053\uff1a\u201c\u4f46\u4f60\u8001\u4e0d\u77e5\u559c\u7231\u7684\u4f55\u79cd\u4e8b\u7269\uff1f\u201d \u3000\u3000\u8001\u4eba\u9053\uff1a\u201c\u5947\u79cd\u5f02\u9e1f\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u542c\u5f97\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u662f\u4e86\uff01\u770b\u6765\u90a3\u4e09\u53ea\u9e1f\u86cb\uff0c\u4f3c\u662f\u8fd9\u4e2a\u8001\u4eba\u5bb6\u627e\u6765\u7684\u4e86\uff01\u201d \u3000\u3000\u5979\u8fd9\u4e00\u60f3\uff0c\u5012\u662f\u6ca1\u6709\u60f3\u9519\uff01\u8fd9\u65f6\u90a3\u8001\u4eba\u76ee\u5149\u6295\u6ce8\u5728\u4e24\u4eba\u8eab\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u8001\u592b\u6709\u4e00\u4ef6\u4e8b\uff0c\u5e94\u8be5\u5148\u8c22\u8c22\u4e24\u4f4d\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\u4e00\u6123\uff0c\u6697\u9053\uff1a\u201c\u6211\u4eec\u4e0e\u4ed6\u7d20\u672a\u8c0b\u9762\uff0c\u600e\u5730\u4ed6\u5ffd\u7136\u6709\u4e8b\u8981\u8c22\u8c22\u6211\u4eec\u5462\uff1f\u201d \u3000\u3000\u601d\u5fd6\u95f4\uff0c\u4e0d\u89c9\u7b11\u9053\uff1a\u201c\u8001\u524d\u8f88\u6307\u7684\u662f\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u8001\u4eba\u7b11\u9053\uff1a\u201c\u8001\u592b\u5728\u8fd9\u592a\u5ba4\u5cf0\u9876\uff0c\u66fe\u7ecf\u517b\u4e86\u4e00\u5bf9\u5f02\u79cd\u91d1\u96c0\uff0c\u8fd9\u6b21\u8001\u592b\u56de\u6765\uff0c\u53d1\u89c9\u6709\u4eba\u66ff\u5b83\u4eec\u505a\u4e86\u4e00\u4e2a\u9e1f\u5de2\uff0c\u60f3\u5fc5\u662f\u51fa\u81ea\u4e24\u4f4d\u4e4b\u624b\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u539f\u6765\u90a3\u9e1f\u513f\u662f\u4f60\u8001\u517b\u7684\uff1f\u201d \u3000\u3000\u8001\u4eba\u9053\uff1a\u201c\u4e0d\u9519\uff01\u4f60\u4eec\u53ef\u522b\u5c0f\u770b\u4e86\u90a3\u4e00\u5bf9\u91d1\u96c0\uff0c\u8001\u592b\u4e3a\u4e86\u9972\u517b\u5b83\u4eec\uff0c\u53ef\u771f\u82b1\u8d39\u4e86\u4e0d\u5c11\u5fc3\u8840\u5462\uff1f\u201d \u3000\u3000\u5176\u5b9e\uff0c\u4e0d\u7528\u90a3\u8001\u4eba\u6765\u8bf4\uff0c\u4ed6\u4eec\u4e5f\u53ef\u4ee5\u60f3\u8c61\u5f97\u51fa\u3002\u8fd9\u7b49\u91d1\u96c0\uff0c\u9972\u517b\u7684\u592a\u5ba4\u7edd\u9876\u4e4b\u4e0a\uff0c\u786e\u662f\u4e00\u6869\u4e0d\u5bb9\u6613\u7684\u4e8b\uff0c\u662f\u4ee5\u5b89\u5c0f\u840d\u7acb\u5373\u7b11\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u665a\u8f88\u65e9\u5c31\u731c\u5230\u4e86\uff0c\u8fd9\u4e00\u5bf9\u91d1\u96c0\u5fc5\u662f\u6709\u4e3b\u4e4b\u7269\uff01\u201d \u3000\u3000\u8001\u4eba\u7b11\u9053\uff1a\u201c\u4f60\u65e9\u5c31\u731c\u5230\u4e86\u5417\uff1f\u201d \u3000\u3000\u5176\u5b9e\uff0c\u5b89\u5c0f\u840d\u54ea\u91cc\u662f\u731c\u5230\u5462\u4e86\u5979\u53ea\u662f\u89c9\u5f97\uff0c\u90a3\u4e00\u5bf9\u91d1\u96c0\u81ea\u5df1\u65e2\u7136\u4e0d\u80fd\u636e\u4e3a\u5df1\u6709\uff0c\u5c31\u4e0d\u5982\u5148\u8bf4\u65e9\u5df2\u731c\u5230\uff0c\u4e5f\u597d\u5728\u5fc3\u4e2d\u597d\u53d7\u4e00\u4e9b\u3002 \u3000\u3000\u539f\u6765\u5979\u751a\u4e3a\u559c\u7231\u90a3\u4e00\u5bf9\u91d1\u96c0\uff0c\u4e00\u65e6\u542c\u8bf4\u4e43\u662f\u6709\u4e3b\u4e4b\u7269\uff0c\u82b3\u5fc3\u4e4b\u4e2d\uff0c\u81ea\u662f\u4e0d\u514d\u751a\u662f\u96be\u8fc7\u3002 \u3000\u3000\u8001\u4eba\u8bed\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u4e5f\u4e0d\u7b49\u5b89\u5c0f\u840d\u53e3\u7b54\uff0c\u5140\u81ea\u70b9\u4e86\u4e00\u70b9\u5934\uff0c\u63a5\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u60f3\u5fc5\u5f88\u559c\u6b22\u8001\u592b\u8fd9\u4e00\u5bf9\u91d1\u96c0\u513f\u5427\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u4f4e\u5934\u9053\uff1a\u201c\u5f88\u559c\u6b22\uff01\u201d \u3000\u3000\u8001\u4eba\u5ffd\u7136\u7b11\u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8001\u592b\u5982\u662f\u7b54\u5e94\u9001\u7ed9\u4f60\uff0c\u59d1\u5a18\u613f\u4e0d\u6536\u4e0b\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6014\u4e86\u4e00\u6014\u9053\uff1a\u201c\u771f\u7684\uff1f\u201d \u3000\u3000\u663e\u7136\uff0c\u4ed6\u4e0d\u4f46\u6709\u4e9b\u610f\u5916\uff0c\u800c\u4e14\u8fd8\u4e0d\u6562\u76f8\u4fe1\u3002 \u3000\u3000\u8001\u4eba\u9053\uff1a\u201c\u8001\u592b\u81ea\u7136\u4e0d\u4f1a\u9a97\u4eba\uff01\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u4ed6\u770b\u4e86\u5b89\u5c0f\u840d\u4e00\u77ac\uff0c\u63a5\u9053\uff1a\u201c\u8001\u592b\u8fd8\u6709\u51e0\u4e2a\u6761\u4ef6\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u559c\u51fa\u671b\u5916\uff0c\u5f53\u4e0b\u8feb\u4e0d\u53ca\u5f85\u5730\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u6761\u4ef6\u554a\uff01\u201d \u3000\u3000\u8001\u4eba\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u77a7\u5230\u90a3\u5de2\u4e2d\u7684\u4e09\u53ea\u9e1f\u86cb\u4e86\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u77a7\u5230\u4e86\uff01\u201d \u3000\u3000\u8001\u4eba\u9053\uff1a\u201c\u7b49\u90a3\u4e09\u53ea\u9e1f\u86cb\u5b75\u51fa\u4ee5\u540e\uff0c\u59d1\u5a18\u5c31\u5730\u5c06\u90a3\u4e00\u5bf9\u91d1\u96c0\u6367\u53bb\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u539f\u4ee5\u4e3a\u4ed6\u4f1a\u63d0\u51fa\u4e00\u4e2a\u5341\u5206\u5201\u96be\u7684\u6761\u4ef6\uff0c\u662f\u6545\u82b3\u5fc3\u4e4b\u4e2d\uff0c\u5927\u662f\u4e0d\u5b89\uff0c\u4f46\u8fd9\u65f6\u89c1\u4ed6\u8bf4\u51fa\u7684\u6761\u4ef6\uff0c\u4e0d\u8fc7\u662f\u8981\u81ea\u5df1\u7b49\u4ed6\u628a\u9e1f\u86cb\u5b75\u51fa\uff0c\u4e0d\u7531\u5f97\u5927\u559c\u9053\uff1a\u201c\u90a3\u4e2a\u81ea\u7136\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5374\u662f\u5728\u65c1\u5bfb\u601d\u9053\uff1a\u201c\u5018\u662f\u8fd9\u4f4d\u8001\u524d\u8f88\u51fa\u5916\u5bfb\u627e\u7684\u5341\u5206\u73cd\u5947\u4e4b\u7269\uff0c\u4fbf\u662f\u90a3\u4e09\u53ea\u4e4c\u86cb\uff0c\u8c05\u5fc5\u8fd9\u4e09\u53ea\u9e1f\u86cb\u5b75\u51fa\u7684\u9e1f\u513f\uff0c\u66f4\u662f\u4eba\u95f4\u5c11\u89c1\u5f02\u79cd\u4e86\uff01\u201d \u3000\u3000\u4ed6\u4e00\u5ff5\u53ca\u6b64\uff0c\u4e0d\u7981\u63a5\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u90a3\u4e09\u53ea\u9e1f\u86cb\u53ef\u662f\u4f60\u8001\u51fa\u5916\u5bfb\u627e\u56de\u6765\u7684\u5417\uff1f\u201d \u3000\u3000\u8001\u4eba\u9053\uff1a\u201c\u4e0d\u9519\uff01\u8001\u5927\u82b1\u4e86\u516d\u4e2a\u6708\u65f6\u5149\uff0c\u7ec8\u4e8e\u5728\u5317\u6d77\u51b0\u6e90\uff0c\u627e\u5230\u4e86\u8fd9\u4e09\u53ea\u86cb\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8001\u524d\u8f88\u3002\u8fd9\u4e09\u53ea\u9e1f\u86cb\uff0c\u4e0d\u77e5\u53ef\u4e5f\u662f\u8fd9\u79cd\u91d1\u96c0\uff1f\u201d \u3000\u3000\u8001\u4eba\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u540c\uff01\u5018\u82e5\u5c31\u662f\u8fd9\u79cd\u91d1\u96c0\uff0c\u90a3\u53c8\u4f55\u5fc5\u8981\u8001\u592b\u8d39\u90a3\u4e48\u591a\u65f6\u95f4\u5fc3\u8840\uff1f\u201d \u3000\u3000\u4ed6\u8bed\u97f3\u5fae\u5fae\u4e00\u987f\uff0c\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u53c8\u590d\u957f\u957f\u5730\u5410\u4e86\u4e00\u53e3\u5927\u6c14\uff0c\u65b9\u9053\uff1a\u201c\u8fd9\u4e09\u53ea\u9e1f\u86cb\uff0c\u53ef\u8c13\u662f\u4e16\u95f4\u6781\u96be\u89c1\u5230\u4e4b\u7269\uff0c\u7eb5\u7136\u662f\u82b1\u8d39\u4e86\u8001\u592b\u5982\u8bb8\u7684\u7cbe\u795e\uff0c\u90a3\u4e5f\u662f\u503c\u5f97\u7684\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u89c1\u4ed6\u4f3c\u662f\u81ea\u8a00\u81ea\u8bed\uff0c\u4f46\u4e5f\u5bfb\u601d\u9053\uff1a\u201c\u770b\u6765\u8fd9\u4e09\u53ea\u9e1f\u86cb\u5f53\u771f\u662f\u4ed6\u5341\u5206\u5fc3\u7231\u4e4b\u7269\u4e86\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5374\u662f\u7b11\u4e86\u4e00\u7b11\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u90a3\u4e09\u53ea\u4e4c\u86cb\u4e00\u65e6\u5b75\u51fa\u4ee5\u540e\uff0c\u4e0d\u77e5\u662f\u4ec0\u4e48\u6837\u7684\u9e1f\u513f\uff1f\u201d \u3000\u3000\u8001\u4eba\u9053\uff1a\u201c\u597d\u9e1f\u513f\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u662f\u5440\uff01\u5b83\u4eec\u4f1a\u662f\u4ec0\u4e48\u6837\u5b50\uff1f\u201d \u3000\u3000\u8001\u4eba\u5ffd\u7136\u5927\u7b11\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u90a3\u9e1f\u86cb\u5f88\u5c0f\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u4ed6\u8fd9\u7b49\u7b54\u975e\u6240\u95ee\uff0c\u53ea\u628a\u5b89\u5c0f\u840d\u542c\u5f97\u4e00\u6123\uff0c\u9053\uff1a\u201c\u662f\u554a\uff0c\u90a3\u9e1f\u86cb\u5f88\u5c0f\u3002\u201d \u3000\u3000\u8001\u4eba\u9053\uff1a\u201c\u4f46\u8003\u592b\u8bf4\u51fa\u6765\uff0c\u53ea\u6015\u59d1\u5a18\u4e0d\u4fe1\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8001\u4eba\u9053\uff1a\u201c\u56e0\u4e3a\u2026\u2026\u90a3\u4e09\u53ea\u9e1f\u86cb\u867d\u5c0f\uff0c\u4f46\u5b75\u51fa\u6765\u7684\u9e1f\u513f\uff0c\u5374\u662f\u4e0d\u5c0f\u7684\u554a\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u8fd9\u4e09\u53ea\u9e1f\u86cb\uff0c\u5b75\u51fa\u6765\u7684\uff0c\u53c8\u662f\u4ec0\u4e48\u9e1f\u513f\u5462\uff1f\u201d \u3000\u3000\u8001\u4eba\u7b11\u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9e64\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u4ed9\u9e64\u5417\uff1f\u201d \u3000\u3000\u8001\u4eba\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u56de\u987e\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u9053\uff1a\u201c\u65b9\u5927\u54e5\uff0c\u4f60\u89c1\u8fc7\u90a3\u4ed9\u9e64\u5b75\u86cb\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u5934\u9053\uff1a\u201c\u6ca1\u6709\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5411\u8001\u4eba\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u4f60\u8fd9\u9e64\u86cb\uff0c\u662f\u6253\u54ea\u513f\u6361\u6765\u7684\uff1f\u201d \u3000\u3000\u8001\u4eba\u5927\u7b11\u9053\uff1a\u201c\u5c0f\u4e2b\u5934\uff0c\u4f60\u51e0\u65f6\u6361\u5230\u8fc7\u9e64\u86cb\u6765\u7740\uff1f\u8001\u592b\u4e43\u662f\u82b1\u4e86\u534a\u5e74\u4e4b\u4e45\uff0c\u5728\u957f\u767d\u5c71\u4e0a\u627e\u6765\u7684\u554a\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5ffd\u7136\u5931\u58f0\u53eb\u9053\uff1a\u201c\u600e\u4e48\uff1f\u8fd9\u662f\u5929\u9e64\u86cb\uff1f\u201d \u3000\u3000\u8001\u4eba\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u90a3\u5c31\u4e0d\u8db3\u4e3a\u5947\u4e86\uff01\u201d \u3000\u3000\u8001\u4eba\u4e24\u773c\u4e00\u7ffb\uff0c\u6012\u9053\uff1a\u201c\u4e0d\u8db3\u4e3a\u5947\uff1f\u4f60\u90a3\u4e2b\u5934\uff0c\u4e00\u751f\u4e4b\u4e2d\uff0c\u53c8\u89c1\u8fc7\u591a\u5c11\u5929\u9e64\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u6211\u89c1\u8fc7\u591a\u5c11\uff1f\u8001\u524d\u8f88\u53c8\u89c1\u8fc7\u591a\u5c11\uff1f\u201d \u3000\u3000\u8001\u4eba\u9053\uff1a\u201c\u8001\u592b\u5417\uff1f\u4e00\u751f\u4e4b\u4e2d\uff0c\u89c1\u8fc7\u56db\u6b21\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5ffd\u7136\u683c\u683c\u4e00\u7b11\u9053\uff1a\u201c\u6211\u4e00\u5929\u5c31\u4e0d\u6b62\u89c1\u8fc7\u4e94\u6b21\uff0c\u8001\u524d\u8f88\uff0c\u4f60\u8bf4\u591a\u4e0d\u591a\uff1f\u201d \u3000\u3000\u8001\u4eba\u95f4\u8a00\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u83ab\u975e\u9972\u517b\u8fc7\u8fd9\u7b49\u5929\u9e64\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u5dee\u4e0d\u591a\uff01\u201d \u3000\u3000\u8001\u4eba\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u2026\u2026\u8001\u592b\u4e0d\u4fe1\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u8001\u4eba\u5bb6\u4e0d\u4fe1\uff0c\u90a3\u5c31\u7b97\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5728\u65c1\u542c\u5f97\u6697\u6697\u7b11\u9053\uff1a\u201c\u770b\u6765\u8fd9\u4f4d\u8001\u4eba\u8981\u4e0a\u5b89\u8d24\u59b9\u7684\u5f53\u4e86\uff01\u201d \u3000\u3000\u5bfb\u601d\u4e4b\u95f4\uff0c\u8033\u4e2d\u542c\u5f97\u8001\u4eba\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u77a7\u4f60\u8fd9\u4e48\u8bf4\uff0c\u4f60\u9972\u6709\u5929\u9e64\uff0c\u90a3\u662f\u771f\u7684\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4fe1\u4e0d\u4fe1\u7531\u4f60\uff01\u201d \u3000\u3000\u8001\u4eba\u7c7b\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u8001\u673d\u6709\u4e00\u6869\u4e8b\u60f3\u8bf7\u6559\u4e00\u756a\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u8001\u4eba\u9053\uff1a\u201c\u90a3\u5929\u9e64\u662f\u600e\u751f\u5b75\u5375\uff0c\u59d1\u5a18\u53ef\u66fe\u89c1\u8fc7\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u89c1\u8fc7\uff01\u201d \u3000\u3000\u8001\u4eba\u9053\uff1a\u201c\u600e\u751f\u5b75\u6cd5\uff1f\u59d1\u5a18\u53ef\u5426\u8bf4\u660e\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u665a\u8f88\u4e0d\u5927\u660e\u767d\u4e86\uff01\u201d \u3000\u3000\u8001\u4eba\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u4e0d\u77e5\u9053\uff1f\u201d \u3000 \u7b2c\u4e8c\u5341\u4e5d\u56de\u3000\u5de7\u5999\u5b89\u6392 \u3000 \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u4e0d\u77e5\u9053\uff01\u90a3\u5929\u9e64\u5b75\u86cb\u4e4b\u65f6\uff0c\u53e6\u5916\u6709\u4eba\u7167\u987e\uff0c\u665a\u8f88\u6240\u4ee5\u4e0d\u77e5\uff01\u201d \u3000\u3000\u8001\u4eba\u9053\uff1a\u201c\u8001\u592b\u542c\u8bf4\uff0c\u90a3\u5929\u9e64\u4ece\u4e0d\u81ea\u5df1\u5b75\u5375\uff0c\u4e0d\u77e5\u53ef\u662f\u771f\u7684\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u5f53\u7136\u662f\u771f\u7684\uff0c\u4e0d\u7136\uff0c\u6211\u7237\u7237\u5e72\u5417\u8fd8\u517b\u4e86\u8bb8\u591a\u522b\u79cd\u7684\u4ed9\u9e64\u5462\uff1f\u201d \u3000\u3000\u5979\u8bdd\u5df2\u51fa\u53e3\uff0c\u4eff\u4f5b\u5df2\u7136\u53d1\u89c9\u81ea\u5df1\u8bf4\u6f0f\u4e86\u5634\uff0c\u8fde\u5fd9\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u4f60\u60f3\u5957\u6211\u7684\u8bdd\u5417\uff1f\u201d \u3000\u3000\u8001\u4eba\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u8001\u592b\u5df2\u660e\u767d\u4e86\uff01\u201d\u4ed6\u7b11\u58f0\u4e00\u987f\uff0c\u76ee\u5149\u5728\u5b89\u5c0f\u840d\u7684\u8eab\u4e0a\u8f6c\u6765\u8f6c\u53bb\uff0c\u63a5\u9053\uff1a\u201c\u59d1\u5a18\u53ef\u662f\u59d3\u5b89\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u662f\u53c8\u600e\u6837\uff1f\u201d\u90a3\u9c9c\u7ea2\u7684\u5c0f\u5634\uff0c\u5df2\u7136\u9f13\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8001\u4eba\u70b9\u4e86\u70b9\u5934\u9053\uff1a\u201c\u8001\u5927\u6709\u4e00\u4f4d\u6545\u4ea4\uff0c\u4e5f\u662f\u59d3\u5b89\uff0c\u4e0d\u77e5\u59d1\u5a18\u77e5\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u8001\u524d\u8f88\u8bf4\u5c06\u51fa\u6765\uff0c\u665a\u8f88\u5982\u662f\u77e5\u6653\uff0c\u5b9a\u7136\u76f8\u544a\uff01\u201d \u3000\u3000\u8001\u4eba\u9053\uff1a\u201c\u6d77\u5916\u6709\u4e00\u5904\u5929\u5fc3\u5c9b\uff0c\u8001\u673d\u8fd9\u4f4d\u6545\u4ea4\uff0c\u4eba\u79f0\u5929\u5fc3\u5c9b\u4e3b\uff0c\u59d1\u5a18\u60f3\u5fc5\u8ba4\u8bc6\u7684\u4e86\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u8138\u8272\u5927\u5927\u4e00\u53d8\uff0c\u77aa\u7740\u4e00\u53cc\u5927\u773c\uff0c\u5728\u90a3\u8001\u4eba\u8eab\u4e0a\u4e0d\u505c\u5730\u6253\u91cf\uff0c\u534a\u664c\u5374\u662f\u6ca1\u6709\u8bf4\u8bdd\uff0c\u65b9\u96ea\u5b9c\u53ea\u77a7\u5f97\u6709\u4e9b\u60d1\u7136\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u4f4d\u8001\u4eba\u4e0d\u77e5\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba\uff1f\u4ed6\u4f3c\u4e4e\u5bf9\u5b89\u5bb6\u7684\u4e00\u5207\uff0c\u751a\u662f\u719f\u6089\u2026\u2026\u201d\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u5fcd\u4e0d\u4f4f\u62b1\u62f3\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u4f60\u8001\u7684\u5927\u540d\uff0c\u665a\u8f88\u5c1a\u672a\u8bf7\u6559\uff01\u201d \u3000\u3000\u90a3\u8001\u4eba\u5374\u662f\u672a\u7b54\u65b9\u96ea\u5b9c\u6240\u95ee\uff0c\u5927\u58f0\u5411\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u59d1\u5a18\u4f60\u4e3a\u4f55\u4e0d\u8bf4\u8bdd\u4e86\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u76b1\u4e86\u76b1\u7709\uff0c\u5e7d\u5e7d\u5730\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u90a3\u662f\u6211\u7239\u7239\uff01\u201d \u3000\u3000\u90a3\u8001\u4eba\u987f\u65f6\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5b89\u632f\u6b66\u662f\u4f60\u7239\u7239\uff1f\u201d \u3000\u3000\u4ed6\u8bed\u97f3\u987f\u4e00\u987f\uff0c\u5ffd\u5730\u957f\u957f\u4e00\u53f9\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u59d1\u5a18\u5df2\u7ecf\u8fd9\u4e48\u5927\u4e86\uff01\u8001\u673d\u6614\u5e74\u89c1\u5230\u59d1\u5a18\u4e4b\u65f6\uff0c\u5927\u6982\u59d1\u5a18\u53ea\u6709\u534a\u5c81\u5de6\u53f3\uff0c\u5982\u4eca\u7b97\u6765\uff0c\u5df2\u7ecf\u5341\u516d\u5e74\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u542c\u5f97\u8001\u4eba\u63d0\u53ca\u81ea\u5df1\u513f\u65f6\u4e4b\u4e8b\uff0c\u7acb\u5373\u91cd\u65b0\u5411\u90a3\u8001\u4eba\u4e00\u793c\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8001\u524d\u8f88\u4e0e\u5148\u7236\u5f88\u719f\u5417\uff1f\u665a\u8f88\u7adf\u7136\u4e0d\u8ba4\u8bc6\u4f60\u8001\uff0c\u771f\u662f\u7f6a\u8fc7\u5f97\u5f88\uff01\u201d \u3000\u3000\u90a3\u8001\u4eba\u5475\u5475\u4e00\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u4e0d\u7528\u5ba2\u6c14\u4e86\u2026\u2026\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u7b11\u58f0\u4e00\u655b\uff0c\u695e\u4e86\u4e00\u6123\uff0c\u5927\u58f0\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u7239\u83ab\u975e\u5df2\u7ecf\u4ed9\u53bb\u4e86\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9eef\u7136\u9053\uff1a\u201c\u5148\u7236\u89c1\u5f03\uff0c\u5df2\u7ecf\u6709\u5341\u5e74\u4e86\uff01\u201d \u3000\u3000\u90a3\u8001\u4eba\u5446\u4e86\u4e00\u5446\uff0c\u534a\u664c\u65b9\u9053\uff1a\u201c\u5df2\u7ecf\u6709\u5341\u5e74\u4e86\uff1f\u600e\u5730\u8001\u673d\u4e00\u70b9\u90fd\u4e0d\u77e5\u9053\uff1f\u4ee4\u5802\u5462\uff1f \u3000\u3000\u8fd8\u5728\u5929\u5fc3\u5c9b\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u4f3c\u662f\u4e0d\u613f\u591a\u8bf4\u6bcd\u4eb2\u4e4b\u4e8b\uff0c\u5f53\u4e0b\u6447\u5934\u9053\uff1a\u201c\u5bb6\u6bcd\u4e0d\u5728\u5929\u5fc3\u5c9b\u4e5f\u5f88\u4e45\u4e86\uff01\u201d\u5979\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u8001\u8001\u524d\u8f88\u600e\u4e48\u79f0\u547c\uff1f\u201d \u3000\u3000\u663e\u7136\uff0c\u5b89\u5c0f\u840d\u4e3a\u4e86\u4e0d\u518d\u591a\u8c08\u81ea\u5df1\u7684\u8eab\u5bb6\uff0c\u624d\u4f1a\u8fd9\u7b49\u5411\u8001\u4eba\u53cd\u95ee\u3002 \u3000\u3000\u90a3\u8001\u4eba\u6293\u4e86\u6293\u957f\u53d1\uff0c\u9053\uff1a\u201c\u8001\u592b\u795d\u516c\u660e\uff0c\u59d1\u5a18\u53ef\u66fe\u542c\u8fc7\u4ee4\u5c0a\u63d0\u53ca\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u95fb\u8a00\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u5ffd\u7136\u91cd\u53c8\u68c0\u9996\u4e3a\u793c\uff0c\u9053\uff1a\u201c\u539f\u6765\u662f\u795d\u516c\u516c\uff0c\u665a\u8f88\u5148\u524d\u4e0d\u77e5\uff0c\u5931\u793c\u5f97\u5f88\u554a\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u6447\u5934\u9053\uff1a\u201c\u59d1\u5a18\u4e0d\u7528\u5ba2\u6c14\u2026\u2026\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u65b9\u96ea\u5b9c\u4e5f\u8d8b\u524d\u62b1\u62f3\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u5929\u53f0\u6a35\u53df\u795d\u8001\u5927\u540d\uff0c\u665a\u8f88\u4e5f\u66fe\u4e45\u95fb\uff0c\u4e0d\u60f3\u4eca\u65e5\u4f1a\u5728\u5d69\u5c71\u62dc\u89c1\uff0c\u771f\u662f\u4eba\u751f\u4f55\u5904\u4e0d\u76f8\u9022\u4e86\uff01\u201d \u3000\u3000\u5929\u53f0\u6a35\u53df\u795d\u516c\u660e\u6014\u4e86\u4e00\u5f81\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u8001\u5f1f\u4e5f\u77e5\u9053\u8001\u592b\uff1f\u602a\u4e86\uff01\u8001\u592b\u4e00\u751f\uff0c\u751a\u5c11\u6d89\u8db3\u6c5f\u6e56\uff0c\u77e5\u9053\u8001\u592b\u7684\u4eba\uff0c\u90a3\u53ef\u662f\u5c11\u4e4b\u53c8\u5c11\uff0c\u5982\u6b64\u770b\u6765\uff0c\u4ee4\u5e08\u5fc5\u662f\u9f0e\u9f0e\u6709\u540d\u7684\u4eba\u7269\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u795e\u8272\u4e00\u6014\u9053\uff1a\u201c\u5148\u5e08\u9648\u5e0c\u6b63\uff01\u201d \u3000\u3000\u5929\u53f0\u6a35\u53df\u5ffd\u5730\u53cc\u76ee\u795e\u5149\u66b4\u5c04\uff0c\u5475\u5475\u5927\u7b11\u9053\uff1a\u201c\u4f60\u662f\u5251\u795e\u9648\u5144\u7684\u5f1f\u5b50\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u8096\u7684\u5f88\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u7b11\u9053\uff1a\u201c\u8001\u592b\u4e0d\u4e45\u4e4b\u524d\uff0c\u65b9\u59cb\u542c\u8bf4\u4ee4\u5e08\u9648\u5927\u4fa0\u5df2\u7136\u6492\u624b\u897f\u53bb\uff0c\u4e5f\u66fe\u542c\u8bf4\u5251\u795e\u7684\u4f20\u4eba\uff0c\u5355\u8eab\u652f\u5251\uff0c\u884c\u9053\u6c5f\u6e56\uff0c\u4e0d\u6599\u539f\u6765\u5c31\u662f\u4f60\u8001\u5f1f\u2026\u2026\u201d\u8bed\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u770b\u4e86\u90a3\u5b89\u5c0f\u840d\u4e00\u773c\uff0c\u63a5\u9053\uff1a\u201c\u4f46\u4e0d\u77e5\u4f60\u4eec\u600e\u4f1a\u6765\u5230\u5d69\u5c71\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fae\u5fae\u4e00\u7b11\uff0c\u628a\u90a3\u5929\u9b54\u5973\u7684\u91ce\u5fc3\uff0c\u7565\u7565\u4ed6\u8bf4\u4e86\u4e00\u904d\uff01 \u3000\u3000\u795d\u516c\u660e\u53ea\u542c\u5f97\u53cc\u76fe\u4e00\u626c\uff0c\u9053\uff1a\u201c\u5929\u9b54\u5973\u7684\u80c6\u5b50\u4e0d\u5c0f\uff0c\u7adf\u7136\u8d77\u4e0b\u541e\u5e76\u6b66\u6797\u4e4b\u5fc3\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u53ef\u4e0d\uff1f\u5148\u5e08\u672a\u66fe\u53bb\u4e16\u4e4b\u524d\uff0c\u4ed6\u4eec\u4f3c\u4e4e\u8fd8\u6709\u7740\u5c11\u8bb8\u987e\u5fcc\uff0c\u5982\u4eca\uff1f\u4ed6\u4eec\u6070\u5982\u51fa\u5c71\u4e4b\u6218\uff0c\u65e9\u5c31\u4e0d\u628a\u4e16\u4eba\u653e\u5728\u773c\u4e2d\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u5ffd\u5730\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u5979\u6562\uff1f\u6b66\u6797\u4e4b\u4e2d\uff0c\u6b66\u529f\u9ad8\u8fc7\u5979\u4eec\u4e94\u9b54\u7684\u4eba\uff0c\u4e0d\u89c1\u5f97\u5168\u90fd\u6b7b\u5149\u4e86\u554a\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5728\u65c1\u683c\u683c\u4e00\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5728\u6b66\u6797\u4e4b\u4e2d\uff0c\u5012\u662f\u6709\u7740\u4e0d\u5c11\u6b66\u529f\u9ad8\u8fc7\u4ed6\u4eec\u4e4b\u4eba\uff0c\u53ea\u662f\u4eba\u4eba\u90fd\u662f\u6d01\u8eab\u81ea\u4fdd\uff0c\u7eb5\u7136\u6d3b\u7684\u4eba\u518d\u591a\uff0c\u8fd8\u4e0d\u662f\u7b49\u4e8e\u6b7b\u4e86\u4e00\u822c\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u95fb\u8a00\uff0c\u5fcd\u4e0d\u4f4f\u5931\u58f0\u9053\uff1a\u201c\u662f\u554a\uff01\u59d1\u5a18\u4e00\u8bed\u53ef\u628a\u8001\u673d\u7ed9\u63d0\u9192\u4e86\uff01\u201d \u3000\u3000\u4ed6\u8bed\u97f3\u4e00\u843d\uff0c\u5927\u6b65\u8d70\u51fa\u90a3\u5ea7\u77f3\u6d1e\uff0c\u6253\u91cf\u504f\u897f\u7684\u6708\u8272\u4e00\u773c\uff0c\u8fd9\u624d\u9053\uff1a\u201c\u5750\u800c\u8a00\uff0c\u4e0d\u5982\u8d77\u800c\u884c\uff0c\u8001\u673d\u6253\u4eca\u65e5\u4e5f\u8981\u6539\u6539\u813e\u6c14\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u201c\u5e08\u7236\u66fe\u7ecf\u8bf4\u8fc7\uff0c\u8fd9\u5929\u53f0\u6a35\u53df\u7684\u6b66\u529f\uff0c\u4e0d\u5728\u4ed6\u4e4b\u4e0b\uff0c\u53ea\u56e0\u6b64\u4eba\u4e00\u5411\u4e0d\u80af\u8fc7\u95ee\u662f\u975e\uff0c\u6b66\u6797\u4e4b\u4e2d\uff0c\u624d\u4f1a\u77e5\u8005\u4e0d\u591a\uff0c\u5018\u662f\u6b64\u8001\u771f\u4e2a\u6709\u5fc3\u51fa\u5c71\uff0c\u90a3\u5012\u771f\u662f\u4e00\u5927\u559c\u8baf\uff01\u201d \u3000\u3000\u4e00\u5ff5\u53ca\u6b64\uff0c\u4e0d\u7531\u5f97\u8d8b\u524d\u62b1\u62f3\u9053\uff1a\u201c\u8001\u524d\u8f88\u82e5\u80fd\u51fa\u5c71\u4e3b\u6301\u4e00\u5207\uff0c\u90a3\u771f\u662f\u6b66\u6797\u540c\u9053\uff0c\u83ab\u5927\u7684\u798f\u6c14\u4e86\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u56de\u987e\u4e86\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u7684\u610f\u601d\uff0c\u8981\u8001\u592b\u51fa\u9762\uff0c\u6307\u6325\u4e00\u5207\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5982\u662f\u4f60\u8001\u4e3b\u6301\uff0c\u81ea\u662f\u4e8b\u534a\u529f\u500d\u4e86\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u6447\u5934\u9053\uff1a\u201c\u4e0d\u6210\u554a\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u8001\u592b\u4e00\u5411\u95f2\u4e91\u91ce\u9e64\uff0c\u6f2b\u65e0\u62d8\u675f\u7684\u65e5\u5b50\u8fc7\u60ef\u4e86\uff0c\u5982\u662f\u8981\u8001\u592b\u5c3d\u4e00\u4efd\u5fc3\uff0c\u51fa\u4e00\u4efd\u529b\uff0c\u90a3\u5012\u4e0d\u96be\uff0c\u4f46\u5982\u8981\u8001\u592b\u7edf\u7387\u4e09\u519b\uff0c\u53f7\u4ee4\u4e00\u5207\uff0c\u90a3\u4fbf\u662f\u8fde\u4f60\u4eec\u90fd\u53d8\u6210\u4e4c\u5408\u4e4b\u4f17\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u9053\uff1a\u8fd9\u8bdd\u4e5f\u6709\u51e0\u5206\u9053\u7406\u2026\u2026\uff0c\u5f53\u4e0b\u62b1\u62f3\u9053\uff1a\u201c\u8001\u524d\u8f88\u5982\u662f\u4e0d\u613f\u4e3b\u6301\u4e00\u5207\uff0c\u665a\u8f88\u5012\u4e5f\u4e0d\u6562\u52c9\u5f3a\uff0c\u53ea\u662f\u2026\u2026\u665a\u8f88\u7b49\u5982\u4e8b\u76f8\u5546\uff0c\u5374\u53c8\u600e\u80fd\u627e\u5f97\u5230\u4f60\u8001\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u8fd9\u4e2a\u5bb9\u6613\uff0c\u8001\u592b\u5728\u8fd9\u592a\u5ba4\u5cf0\u5934\uff0c\u4f4f\u4e86\u5df2\u6709\u5341\u5e74\uff0c\u4e00\u65f6\u4e4b\u95f4\u561b\uff0c\u8fd8\u4e0d\u4f1a\u79bb\u53bb\uff0c\u4f60\u4eec\u5982\u6709\u7528\u5f97\u7740\u8001\u592b\u4e4b\u5904\uff0c\u53ea\u5dee\u4eba\u6765\u6b64\u76f8\u53ec\uff0c\u8001\u592b\u5c31\u4f1a\u7acb\u5373\u8d76\u53bb\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u539f\u60f3\u628a\u4ed6\u8bf7\u5230\u5c11\u6797\uff0c\u4e5f\u597d\u968f\u65f6\u5411\u4ed6\u8bf7\u9886\u6559\u76ca\uff0c\u4f46\u5728\u542c\u5f97\u795d\u516c\u660e\u8fd9\u756a\u8bdd\u4ee5\u540e\uff0c\u4e5f\u5c31\u4e0d\u518d\u52c9\u5f3a\uff0c\u7b11\u4e86\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u5982\u6b64\u4e5f\u597d\uff0c\u665a\u8f88\u82e5\u662f\u6709\u4e8b\uff0c\u5b9a\u4f1a\u4eb2\u81ea\u524d\u6765\u8fd9\u592a\u5ba4\u5cf0\u95f4\u4fc3\u9a7e\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u5fc5\uff01\u8001\u592b\u65e2\u7136\u5e94\u5141\u4e86\u4f60\u4eec\uff0c\u81ea\u7136\u4e0d\u4f1a\u53d8\uff0c\u4f60\u53c8\u4f55\u5fc5\u81ea\u5df1\u8dd1\u6765\uff1f\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u5374\u662f\u6389\u5934\u5411\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u90a3\u4e24\u53ea\u91d1\u96c0\u513f\uff0c\u8fd8\u8981\u4e0d\u8981\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u8001\u524d\u8f88\u4e0d\u662f\u8981\u7528\u5b83\u4eec\u6765\u5b75\u90a3\u5929\u9e64\u86cb\u5417\uff1f\u665a\u8f88\u5982\u662f\u8981\u53bb\uff0c\u4f60\u8001\u53c8\u7528\u4f55\u7269\u6765\u5b75\u5375\u5462\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u7b11\u9053\uff1a\u201c\u90a3\u5bb9\u6613\u5f97\u5f88\uff0c\u7b49\u8001\u592b\u5c06\u90a3\u5929\u9e64\u5b75\u51fa\u6765\u4e4b\u540e\uff0c\u4f60\u518d\u62ff\u8d70\uff0c\u4e0d\u5c31\u5f97\u4e86\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u5982\u6b64\u2026\u2026\u665a\u8f88\u5c31\u5148\u8c22\u8fc7\u4e86\uff01\u201d \u3000\u3000\u795d\u516c\u5475\u5475\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u5148\u522b\u8c22\uff0c\u987b\u77e5\u8001\u592b\u8fd9\u7b49\u63aa\u65bd\uff0c\u8fd8\u6709\u4e0b\u6587\u672a\u8bb2\u5462\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u83ab\u975e\u4f60\u8001\u8fd8\u6709\u4ec0\u4e48\u6761\u4ef6\u4ea4\u6362\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8001\u592b\u6168\u8d60\u91d1\u96c0\uff0c\u4e0d\u8fc7\u662f\u610f\u5728\u629b\u7816\u5f15\u7389\u800c\u5df2\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8001\u524d\u8f88\u6b64\u8bdd\u600e\u8bb2\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u8001\u592b\u90a3\u4e09\u4e2a\u5929\u9e64\u86cb\uff0c\u4e0d\u4e00\u5b9a\u80fd\u591f\u5b75\u5f97\u51fa\u6765\u662f\u5b75\u4e0d\u51fa\u6765\uff0c\u8001\u592b\u53ef\u5c31\u8981\u8bf7\u59d1\u5a18\u5e2e\u5fd9\u7684\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u665a\u8f88\u660e\u767d\u4e86\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u65e2\u7136\u660e\u767d\uff0c\u5c31\u4f1a\u76f8\u4fe1\uff0c\u8001\u592b\u8fd9\u629b\u7816\u5f15\u7389\u4e4b\u8a00\uff0c\u786e\u662f\u4e0d\u5047\u7684\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u665a\u8f88\u76f8\u4fe1\u4f60\u8001\u679c\u7136\u6ca1\u6709\u9a97\u4eba\u2026\u2026\u8fd9\u5929\u9e64\u86cb\u5982\u662f\u5b75\u4e0d\u51fa\u6765\uff0c\u53ea\u6015\u4f60\u8001\u662f\u60f3\u665a\u8f88\u9001\u4f60\u8001\u4e00\u53ea\u5929\u9e64\u7684\u4e86\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u59d1\u5a18\u806a\u660e\u5f97\u5f88\uff0c\u8001\u592b\u4e43\u662f\u4e00\u7247\u79c1\u5fc3\uff0c\u59d1\u5a18\u53ef\u83ab\u8981\u89c1\u7b11\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u600e\u4e48\u4f1a\uff1f\u5982\u662f\u4f60\u8001\u8fd9\u4e09\u4e2a\u9e64\u86cb\u5b75\u4e0d\u51fa\u6765\uff0c\u5c06\u6765\u5c31\u8bf7\u4f60\u8001\u53bb\u5230\u5929\u5fc3\u5c9b\u968f\u610f\u6311\u9009\u4e24\u53ea\u5929\u9e64\u4fbf\u4e86\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u5927\u7b11\u9053\uff1a\u201c\u770b\u6765\uff0c\u8001\u673d\u53ef\u8981\u5148\u8c22\u8c22\u59d1\u5a18\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u8001\u524d\u8f88\u5ba2\u6c14\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u65f6\u5728\u65c1\u7b11\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u4f60\u72ec\u5c45\u592a\u5ba4\u5cf0\u9876\uff0c\u8fd9\u4e00\u65e5\u4e09\u9910\uff0c\u53c8\u5c06\u5982\u4f55\u6599\u7406\u3002\u201d \u3000\u3000\u795d\u516c\u660e\u5927\u7b11\u9053\uff1a\u201c\u8001\u592b\u4e09\u65e5\u53ea\u5403\u4e00\u9910\uff0c\u81ea\u662f\u5bb9\u6613\u6253\u53d1\u5f97\u5f88\uff0c\u8d24\u4f84\u4e0d\u7528\u6302\u5728\u5fc3\u4e0a\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u6014\u4e86\u4e00\u6014\u9053\uff1a\u201c\u8001\u524d\u8f88\u5df2\u7ecf\u80fd\u591f\u8f9f\u5f40\u4e86\u5417\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u5927\u7b11\u9053\uff1a\u201c\u8c08\u4f55\u5bb9\u6613\uff1f\u8001\u673d\u53ea\u4e0d\u8fc7\u662f\u5e38\u5403\u4ed9\u679c\u91ce\u5473\uff0c\u81f3\u4e8e\u8f9f\u5f40\u4e8c\u5b57\uff0c\u5b9e\u5728\u662f\u592a\u96be\u592a\u96be\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u65b9\u5144\u4f60\u997f\u4e0d\u997f\u5440\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u83ab\u8d24\u975e\u5f1f\u5df2\u7ecf\u997f\u4e86\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u53ef\u4e0d\u662f\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8d24\u5f1f\u4e4b\u610f\uff0c\u662f\u54b1\u4eec\u5148\u884c\u56de\u5bfa\u7528\u996d\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u90a3\u53c8\u4f55\u5fc5\u2026\u2026\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u63a2\u624b\u6253\u6000\u4e2d\u53d6\u51fa\u4e00\u4e2a\u9769\u56ca\uff0c\u9635\u9635\u9999\u5473\u81ea\u90a3\u9769\u56ca\u4e2d\u900f\u4e86\u51fa\u6765\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u77a7\u7684\u4e00\u5446\uff0c\u9053\uff1a\u201c\u600e\u4e48\uff1f\u8d24\u5f1f\u5c45\u7136\u5e26\u4e86\u98df\u7269\u6765\u4e86\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u5927\u54e5\u4e0d\u559c\u6b22\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u5934\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u662f\uff0c\u53ea\u662f\u6709\u4e9b\u610f\u5916\u7f62\u4e86\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u770b\u7740\u5b89\u5c0f\u840d\u624b\u4e2d\u7684\u9769\u56ca\u7b11\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u8fd9\u9769\u56ca\u4e2d\u53ef\u6709\u9152\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u683c\u683c\u4e00\u7b11\u9053\uff1a\u201c\u6ca1\u6709\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u53ef\u60dc\u5440\u53ef\u60dc\uff01\u59d1\u5a18\u8fd9\u9769\u56ca\u4e4b\u4e2d\uff0c\u660e\u660e\u591a\u534a\u88c5\u6709\u4e0a\u597d\u7684\u9e7f\u812f\uff0c\u5018\u82e5\u65e0\u9152\u4f50\u9910\uff0c\u90a3\u591a\u626b\u5174\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u8bdd\u97f3\u521a\u843d\uff0c\u5ffd\u7136\u4e00\u58f0\u957f\u5578\uff0c\u81ea\u5c71\u4e0b\u4f20\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u6123\u4e86\u4e00\u6123\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8d24\u5f1f\u6ca1\u6709\u542c\u51fa\u6765\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u76b1\u4e86\u76b1\u7709\uff0c\u9053\uff1a\u201c\u8d24\u4f84\u542c\u51fa\u4e86\u6765\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5927\u6f20\u795e\u624b\u5b8b\u5b5a\u3002\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u5b8b\u5077\u513f\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u9519\uff01\u8001\u524d\u8f88\u8ba4\u8bc6\u4ed6\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u8ba4\u8bc6\uff0c\u4e0d\u8fc7\u5df2\u7ecf\u6709\u591a\u5e74\u4e0d\u89c1\u4e86\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u90a3\u5b8b\u5b5a\u7684\u8eab\u5f62\uff0c\u5df2\u5728\u5cf0\u9876\u73b0\u8eab\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5927\u58f0\u9053\uff1a\u201c\u5b8b\u8001\u6709\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u90a3\u5b8b\u5b5a\u4e0a\u5f97\u5cf0\u9876\uff0c\u56e0\u662f\u51e0\u4eba\u88ab\u90a3\u5de8\u77f3\u6321\u4f4f\uff0c\u662f\u4ee5\u5e76\u672a\u53d1\u73b0\u4ed6\u4eec\uff0c\u76f4\u5230\u542c\u5f97\u65b9\u96ea\u5b9c\u547c\u5524\uff0c\u8fd9\u624d\u817e\u8eab\u5411\u7740\u4e09\u4eba\u8d70\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u540c\u65f6\uff0c\u53e3\u4e2d\u63a5\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u4eec\u53eb\u8001\u5077\u513f\u597d\u627e\u2026\u2026\u201d \u3000\u3000\u5ffd\u7136\u4ed6\u8bdd\u97f3\u4e2d\u9014\u6253\u4f4f\uff0c\u76ee\u5149\u5728\u795d\u516c\u660e\u8eab\u4e0a\u4e00\u8f6c\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u8001\u602a\u7269\uff0c\u662f\u4f60\u5417\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u600e\u4e48\uff1f\u83ab\u975e\u4f60\u6765\u5f97\u5d69\u5c71\uff0c\u8001\u592b\u5c31\u6765\u4e0d\u5f97\u5d69\u5c71\uff1f\u8fd9\u7b49\u5927\u60ca\u5c0f\u602a\u4f5c\u751a\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u53cc\u624b\u5728\u5934\u4e0a\u4e71\u6293\uff0c\u63a5\u9053\uff1a\u201c\u5999\u554a\uff01\u60f3\u4e0d\u5230\u4f60\u8fd9\u8001\u602a\u7269\u5c45\u7136\u4e5f\u4f1a\u6765\u4e86\u5d69\u5c71\uff0c\u96be\u5f97\u5f97\u5f88\uff01\u96be\u5f97\u5f97\u5f88\u554a\uff01\u2026\u2026\u201d \u3000\u3000\u795d\u516c\u660e\u7b11\u9053\uff1a\u201c\u5077\u5144\u5f1f\uff0c\u8fd1\u5e74\u6765\u624b\u6c14\u53ef\u597d\uff1f\u5927\u5185\u7684\u73cd\u5b9d\uff0c\u53c8\u6709\u51e0\u4ef6\u5230\u4e86\u4f60\u624b\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u4e24\u773c\u4e00\u77aa\u9053\uff1a\u201c\u8001\u5077\u513f\u91d1\u76c6\u6d17\u624b\uff0c\u5df2\u7ecf\u4e94\u5e74\u591a\u4e86\uff01\u8001\u602a\u7269\u53ef\u83ab\u8981\u51a4\u6789\u597d\u4eba\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u6539\u4e86\u884c\uff1f\u53ef\u8d3a\u53ef\u559c\uff01\u8fd9\u4e9b\u5e74\u4f60\u5728\u54ea\u91cc\u53d1\u8d22\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u5bc4\u4eba\u7bf1\u4e0b\uff0c\u708a\u706b\u70e7\u996d\u800c\u5df2\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u4e00\u6014\u9053\uff1a\u201c\u4f19\u5934\u519b\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4f60\u53ef\u662f\u77a7\u4e0d\u8d77\u8001\u5077\u513f\u7684\u624b\u827a\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u7b11\u9053\uff1a\u201c\u4e0d\u6562\uff01\u4f60\u8001\u5077\u513f\u5144\u5f1f\u7684\u53a8\u4e0b\u5de5\u592b\uff0c\u6b66\u6797\u79f0\u4e3a\u4e00\u7edd\uff0c\u8001\u592b\u6050\u6015\u6ca1\u6709\u90a3\u7b49\u53e3\u798f\u800c\u5df2\uff01\u54ea\u91cc\u8fd8\u6562\u77a7\u4e0d\u8d77\u5462\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u5475\u5475\u4e00\u7b11\u9053\uff1a\u201c\u8001\u602a\u7269\uff0c\u4f60\u53ef\u60f3\u5c1d\u5c1d\u8001\u5077\u513f\u8fd1\u5e74\u6765\u7684\u5200\u4e0b\u5de5\u592b\u3002\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u5f53\u7136\u60f3\uff1f\u6614\u5e74\u90a3\u987f\u9ec4\u6cb3\u9c9c\u9ca4\u5927\u4f1a\u3002\u81f3\u4eca\u53eb\u6211\u63d0\u8d77\u5c1a\u4e14\u6d41\u6d8e\u4e09\u5c3a\uff0c\u7109\u6709\u4e0d\u60f3\u54c1\u5c1d\u4e4b\u7406\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u90a3\u5bb9\u6613\uff0c\u4f60\u540c\u6211\u53bb\u81f3\u5c11\u6797\uff0c\u8001\u5077\u513f\u5c31\u4e3a\u4f60\u518d\u505a\u4e00\u987f\u9c9c\u9ca4\u5927\u4f1a\u5982\u4f55\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u7b11\u9053\uff1a\u201c\u4e00\u5b9a\u8981\u6253\u6270\uff01\u4e0d\u8fc7\uff0c\u773c\u4e0b\u53ef\u4e0d\u884c\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u4e03\u65e5\u4e4b\u5185\uff0c\u8001\u592b\u4e0d\u80fd\u79bb\u5f00\u8fd9\u592a\u5ba4\u5cf0\u5934\uff0c\u6240\u4ee5\u8fd9\u6253\u6270\u4e4b\u4e8b\uff0c\u8981\u7b49\u4e03\u65e5\u4e4b\u540e\u518d\u8bb2\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u4e00\u6014\u9053\uff1a\u201c\u8001\u602a\u7269\u6709\u4ec0\u4e48\u6bdb\u75c5\uff1f\u8fd9\u7b49\u6240\u5728\uff0c\u53c8\u6709\u4ec0\u4e48\u53ef\u604b\u4e4b\u5904\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u7b11\u9053\uff1a\u201c\u5176\u4e2d\u539f\u56e0\uff0c\u65e5\u540e\u4f60\u81ea\u5df1\u77e5\u9053\uff01\u773c\u4e0b\u6700\u597d\u83ab\u8981\u591a\u95ee\uff01\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u5b89\u5c0f\u840d\u5df2\u5c06\u90a3\u9769\u56ca\u6253\u5f00\uff0c\u9635\u9635\u9e7f\u812f\u9999\u5473\uff0c\u900f\u9f3b\u800c\u5165\uff0c\u5b8b\u5b5a\u8038\u4e86\u8038\u80a9\uff0c\u5927\u58f0\u9053\uff1a \u3000\u3000\u201c\u59d1\u5a18\uff0c\u4f60\u600e\u4e48\u5077\u5230\u8001\u592b\u5934\u4e0a\u6765\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u8f7b\u8f7b\u7b11\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u4f60\u4e00\u4e2a\u4eba\u5403\uff0c\u591a\u65e0\u804a\u554a\uff01\u54b1\u4eec\u5927\u5bb6\u5e2e\u5e2e\u5fd9\u5c82\u4e0d\u751a\u597d\uff1f\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u5411\u795d\u516c\u660e\u9053\uff1a\u201c\u795d\u8001\u524d\u8f88\uff0c\u8bf7\u554a\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u7b11\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u8fd9\u9e7f\u812f\u539f\u6765\u662f\u8001\u5077\u513f\u7684\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u6b63\u662f\u4ed6\u81ea\u5df1\u4eb2\u624b\u505a\u51fa\u6765\u7684\u2026\u2026\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u90a3\u5f53\u7136\u8981\u5403\uff01\u201d \u3000\u3000\u8bf4\u7740\uff0c\u4fbf\u5750\u4e86\u4e0b\u53bb\uff0c\u4f38\u624b\u6293\u4e86\u4e00\u5757\u9e7f\u812f\uff0c\u6295\u5165\u53e3\u4e2d\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u3001\u5b8b\u5b5a\u4e5f\u5750\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u795d\u516c\u660e\u54ac\u4e86\u51e0\u53e3\uff0c\u5ffd\u7136\u62cd\u624b\u9053\uff1a\u201c\u5982\u5473\u9053\uff0c\u5b8b\u5077\u513f\uff0c\u4f60\u7684\u624b\u827a\u5f53\u771f\u767e\u5c3a\u7aff\u5934\uff0c\u53c8\u8fdb\u4e86\u4e00\u6b65\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u82e6\u7b11\u9053\uff1a\u201c\u597d\u8bf4\uff01\u53ef\u60dc\u8001\u5077\u513f\u7684\u6b66\u529f\uff0c\u5374\u662f\u4e0e\u65e5\u4ff1\u4e0b\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u773c\u89c1\u5b8b\u5b5a\u4e0a\u5f97\u5cf0\u9876\uff0c\u5e76\u672a\u8bf4\u51fa\u56e0\u4f55\u6765\u6b64\uff0c\u5fc3\u4e2d\u4e0d\u89c9\u5f97\u6709\u4e9b\u513f\u4e0d\u5b89\uff0c\u8d81\u7740\u5927\u5bb6\u54ac\u7740\u9e7f\u8089\u4e4b\u9645\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u5c11\u6797\u6ca1\u6709\u4ec0\u4e48\u53d8\u6545\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u6ca1\u6709\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u82e5\u662f\u65e0\u4e8b\uff0c\u4f60\u8001\u600e\u4f1a\u627e\u5230\u8fd9\u513f\u6765\u4e86\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8001\u673d\u5728\u5bfa\u4e2d\u95f7\u5f97\u614c\uff0c\u60f3\u627e\u4f60\u8001\u5f1f\u804a\u804a\uff0c\u7ed3\u679c\u5374\u662f\u5230\u5904\u6ca1\u6709\u627e\u5230\uff0c\u6240\u4ee5\u53ea\u60f3\u72ec\u81ea\u4e00\u4e2a\u722c\u4e0a\u8fd9\u592a\u5ba4\u5cf0\u5934\u6563\u6563\u5fc3\uff0c\u600e\u6599\u4f60\u4eec\u90fd\u5728\u6b64\u5730\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u8fd9\u624d\u7565\u4e3a\u653e\u5fc3\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u8001\u5ffd\u7136\u73b0\u8eab\uff0c\u5012\u53eb\u665a\u8f88\u5403\u4e86\u4e00\u60ca\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u600e\u4f1a\u9047\u5230\u8fd9\u4f4d\u795d\u8001\u602a\u7684\uff1f\u83ab\u975e\u4ed6\u65e9\u5c31\u5728\u8fd9\u592a\u5ba4\u5cf0\u5934\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u795d\u8001\u544a\u8bc9\u665a\u8f88\uff0c\u4ed6\u5728\u6b64\u5df2\u7136\u5728\u4e86\u4e00\u4e8c\u5341\u5e74\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u76ee\u5149\u8f6c\u5411\u795d\u516c\u660e\uff0c\u7b11\u9053\uff1a\u201c\u771f\u7684\u5417\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u5f53\u7136\u662f\u771f\u7684\uff01\u8001\u592b\u53c8\u4e0d\u662f\u4ec0\u4e48\u5077\u76d7\u4e4b\u8f88\uff0c\u6015\u4eba\u627e\u5230\u4ed6\u7684\u8001\u7a9d\uff0c\u6293\u5230\u5b98\u91cc\u5750\u7262\uff0c\u4e3a\u4f55\u4e0d\u8bf4\u771f\u8bdd\u5462\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u4e24\u773c\u4e00\u77aa\uff0c\u5927\u7b11\u9053\uff1a\u201c\u8001\u602a\u7269\uff0c\u4f60\u8bf4\u8bdd\u4f3c\u662f\u6709\u4e9b\u5e26\u523a\u554a\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u7b11\u9053\uff1a\u201c\u4e0d\u597d\u542c\uff1f\u90a3\u4f60\u4e0d\u7528\u542c\u4fbf\u662f\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u6447\u5934\u9053\uff1a\u201c\u5c0a\u9a7e\u8fd9\u4e2a\u813e\u6c14\uff0c\u5927\u6982\u4e00\u8f88\u5b50\u4e5f\u6539\u4e0d\u4e86\u7684\u5566\uff01\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u8bf4\u4e03\u65e5\u4e4b\u5185\uff0c\u4e0d\u4fbf\u79bb\u5f00\uff0c\u90a3\u53c8\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u5475\u5475\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5929\u673a\u4e0d\u53ef\u6cc4\u9732\uff0c\u6055\u8001\u592b\u8981\u5356\u4e2a\u5173\u5b50\u4e86\uff01\u201d \u3000\u3000\u4e00\u9762\u8bf4\u8bdd\uff0c\u4e00\u9762\u5374\u5411\u65b9\u96ea\u5b9c\u548c\u5b89\u5c0f\u840d\u4e24\u4eba\u7728\u773c\u3002 \u3000\u3000\u5b8b\u5b5a\u53ea\u542c\u5f97\u76b1\u7709\u9053\uff1a\u201c\u8001\u602a\u5c45\u7136\u4e5f\u6709\u79d8\u5bc6\uff0c\u5230\u662f\u5927\u51fa\u8001\u5077\u513f\u610f\u6599\u4e4b\u5916\u3002\u201d \u3000\u3000\u795d\u516c\u660e\u7b11\u9053\uff1a\u201c\u8001\u592b\u884c\u4e8b\uff0c\u5411\u6765\u559c\u6b22\u5927\u51fa\u4ed6\u4eba\u610f\u6599\uff0c\u8001\u5077\u513f\u5982\u662f\u6709\u5174\u8da3\uff0c\u4f60\u5c31\u7b49\u7740\u77a7\u597d\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u5f53\u7136\u597d\uff0c\u5b8b\u67d0\u5c31\u7b49\u7740\u77a7\u2026\u2026\u201d \u3000\u3000\u795d\u516c\u660e\u4e24\u773c\u4e00\u8f6c\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u8fd9\u4e9b\u4eba\u5ffd\u7136\u5728\u5c11\u6797\u5bfa\u4e2d\u805a\u5934\uff0c\u9664\u4e86\u4e94\u9b54\u4e4b\u4e8b\uff0c\u8fd8\u6709\u522b\u7684\u4e8b\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u95fb\u8a00\uff0c\u6014\u4e86\u4e00\u6014\u9053\uff1a\u201c\u90a3\u4e94\u9b54\u7684\u4e8b\u8fd8\u4e0d\u591f\u53eb\u4eba\u9ebb\u70e6\u5417\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u7b11\u9053\uff1a\u201c\u533a\u533a\u4e94\u4e2a\u4e0d\u5165\u6b63\u6d41\u7684\u8df3\u6881\u5c0f\u9b3c\uff0c\u7adf\u7136\u4e5f\u503c\u5f97\u4f60\u4eec\u5927\u60ca\u5c0f\u602a\uff0c\u771f\u53eb\u8001\u592b\u597d\u7b11\u2026\u2026\u201d \u3000\u3000\u663e\u7136\uff0c\u8fd9\u8001\u4eba\u81ea\u89c6\u6781\u9ad8\u3002 \u3000\u3000\u5b8b\u5b5a\u95fb\u8a00\uff0c\u7b11\u9053\uff1a\u201c\u8001\u602a\u7269\u51e0\u5e74\u4e0d\u89c1\uff0c\u53e3\u6c14\u600e\u5730\u4e5f\u53d8\u5f97\u5927\u8d77\u6765\u4e86\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u8fd9\u6709\u4ec0\u4e48\u53ef\u60ca\u4e4b\u5904\uff1f\u5206\u522b\u4e09\u65e5\u522e\u76ee\u76f8\u770b\uff0c\u8001\u592b\u4e0e\u4f60\u5df2\u6709\u4e8c\u5341\u5e74\u4e0d\u89c1\uff0c\u5f53\u7136\u662f\u8981\u6709\u7740\u5f88\u591a\u7684\u4e0d\u540c\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u7709\u6bdb\u626c\u4e86\u4e00\u626c\uff0c\u6697\u9053\uff1a\u201c\u8001\u602a\u7269\u867d\u7136\u5411\u6765\u7231\u8bf4\u5927\u8bdd\uff0c\u4f46\u5982\u771f\u60f3\u5c06\u4ed6\u8bf4\u52a8\uff0c\u8981\u4ed6\u91cd\u5165\u6c5f\u6e56\uff0c\u5230\u672a\u5c1d\u4e0d\u662f\u4e00\u6869\u597d\u4e8b\u3002\u201d\u8f6c\u5ff5\u81f3\u6b64\uff0c\u53e3\u4e2d\u63a5\u9053\uff1a\u201c\u8001\u602a\u7269\uff0c\u4f60\u4f3c\u4e4e\u4e0d\u628a\u90a3\u4e94\u9b54\u653e\u5728\u773c\u4e2d\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u672c\u6765\u5c31\u6ca1\u6709\u653e\u5728\u773c\u4e2d\uff0c\u600e\u4e48\uff0c\u4f60\u8001\u5077\u513f\u53ef\u662f\u6015\u4ed6\u4eec\u6015\u5f97\u5f88\u51f6\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u4e94\u5927\u9b54\u4e3b\u7684\u6b66\u529f\uff0c\u5404\u6709\u8fc7\u4eba\u4e4b\u5904\uff0c\u8001\u5927\u81ea\u5fd6\uff0c\u53ef\u6709\u4e9b\u60f9\u4ed6\u4eec\u4e0d\u8d77\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u7b11\u9053\uff1a\u201c\u6ca1\u6709\u51fa\u606f\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u8001\u5077\u513f\u6765\u6ca1\u6709\u4f60\u8001\u602a\u7269\u6709\u51fa\u606f\uff0c\u53ea\u662f\uff0c\u50cf\u4f60\u8fd9\u7b49\u7f29\u5728\u5d69\u5c71\uff0c\u4e0d\u6562\u89c1\u4eba\uff0c\u5374\u4e5f\u4e0d\u89c1\u5f97\u6bd4\u6211\u8001\u5077\u513f\u9ad8\u660e\u591a\u5c11\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u95fb\u8a00\uff0c\u84e6\u7136\u9f13\u5927\u4e86\u4e24\u773c\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u8001\u592b\u4e0d\u6bd4\u4f60\u9ad8\u660e\u591a\u5c11\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9041\uff1a\u201c\u672c\u6765\u9ad8\u660e\u4e0d\u4e86\u591a\u5c11\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u597d\u554a\uff0c\u53ea\u8981\u90a3\u4e94\u9b54\u6562\u5230\u8001\u592b\u9762\u524d\uff0c\u4f60\u77a7\u77a7\u8001\u65bd\u5c55\u4e00\u4e9b\u964d\u9b54\u624b\u6bb5\u7ed9\u4f60\u770b\u770b\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u7b49\u4eba\u5bb6\u627e\u4e0a\u4f60\u7684\u5934\u4e0a\u6765\uff0c\u4f60\u8001\u602a\u7269\u624d\u6562\u62fc\u547d\uff1f\u90a3\u8fd8\u4e0d\u662f\u6ca1\u51fa\u606f\u3002\u201d \u3000\u3000\u795d\u516c\u660e\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4f60\u8981\u600e\u4e48\u529e\uff0c\u624d\u7b97\u6bd4\u4f60\u4e0d\u540c\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u5982\u662f\u4ee5\u6211\uff0c\u8001\u5077\u513f\u5c31\u8e0f\u904d\u5929\u6daf\u6d77\u89d2\uff0c\u628a\u90a3\u4e94\u9b54\u4e00\u4e2a\u4e00\u4e2a\u627e\u5230\uff0c\u9664\u53bb\u3002\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u8981\u6211\u4e0b\u5c71\u53bb\u627e\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e0d\u9519\uff01\u53ea\u6015\u4f60\u6ca1\u6709\u8fd9\u7b49\u80c6\u91cf\u3002\u201d \u3000\u3000\u795d\u516c\u660e\u7b11\u9053\uff1a\u201c\u5982\u662f\u8001\u592b\u6ca1\u6709\u90a3\u7b49\u80c6\u91cf\uff0c\u6b66\u6797\u4e4b\u4e2d\uff0c\u5c31\u4e5f\u627e\u4e0d\u51fa\u7b2c\u4e8c\u4e2a\u4eba\u6765\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u3001\u5b89\u5c0f\u840d\u4e24\u4eba\u5728\u65c1\u542c\u5f97\u6697\u7b11\uff0c\u5fc3\u60f3\uff0c\u539f\u6765\u8fd9\u5b8b\u8001\u5077\u513f\u4e43\u662f\u7528\u7684\u6fc0\u5c06\u4e4b\u6cd5\uff0c\u60f3\u5c06\u795d\u516c\u660e\u6fc0\u5f97\u51fa\u5c71\uff0c\u53ea\u662f\uff01\u4ed6\u5374\u672a\u66fe\u60f3\u5230\uff0c\u795d\u516c\u660e\u5df2\u7136\u7b54\u5e94\u8fc7\u81ea\u5df1\u4e24\u4eba\uff0c\u8fc7\u4e86\u4e0d\u591a\u4e45\uff0c\u5c31\u4f1a\u771f\u7684\u4e0b\u5c71\u4e86\uff01\u201d \u3000\u3000\u4e24\u4eba\u5fc3\u4e2d\u867d\u89c9\u5f97\u597d\u7b11\uff0c\u4f46\u5bf9\u5b8b\u5b5a\u8fd9\u7b49\u7528\u5fc3\uff0c\u53ef\u4e5f\u5341\u5206\u7684\u656c\u4f69\u3002 \u3000\u3000\u6545\u800c\uff0c\u8c01\u4e5f\u4e0d\u5f00\u53e3\u8bf4\u7834\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5b8b\u5b5a\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u8001\u602a\u7269\uff0c\u4f60\u81ea\u89c6\u6709\u628a\u63e1\u5f97\u5f88\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u83ab\u975e\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e0d\u662f\u4e0d\u5bf9\uff0c\u8001\u5077\u513f\u76f8\u4fe1\uff0c\u5373\u4f7f\u90a3\u4e94\u5927\u9b54\u4e3b\u773c\u4e0b\u5c31\u5728\u5d69\u5c71\uff0c\u4f60\u8001\u602a\u7269\u4e5f\u4e0d\u89c1\u5f97\u5c31\u4f1a\u964d\u4f0f\u5f97\u4e86\u4ed6\u4eec\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u4e00\u6014\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u5c0a\u9a7e\u7684\u6b66\u529f\uff0c\u4e0d\u4e00\u5b9a\u6bd4\u4ed6\u4eec\u5f3a\uff0c\u90a3\u53c8\u600e\u80fd\u964d\u9b54\u9664\u9053\u5462\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u53d8\u8272\u9053\uff1a\u201c\u597d\u54c7\uff0c\u4f60\u679c\u7136\u628a\u8001\u592b\u770b\u6241\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e0d\u662f\u8001\u5077\u513f\u628a\u4f60\u770b\u6241\u4e86\uff01\u5b9e\u5728\u662f\u2026\u2026\u4f60\u8fd9\u7b49\u53ea\u8bf4\u4e0d\u505a\u7684\u9ad8\u8e48\u9690\u81ea\u884c\u4e3a\uff0c\u53eb\u4eba\u770b\u6765\u5f88\u5dee\u52b2\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u8001\u5077\u513f\uff0c\u4f9d\u4f60\u4e4b\u89c1\uff0c\u8001\u592b\u8981\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u5bb9\u6613\u5f97\u5f88\uff0c\u4e0b\u5c71\u627e\u4ed6\u4eec\u4e00\u8f83\u9ad8\u4f4e\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u5c31\u8fd9\u6837\u7b80\u5355\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5c31\u8fd9\u4e48\u7b80\u5355\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u7b11\u9053\uff1a\u201c\u597d\uff01\u53cd\u6b63\u8001\u592b\u5df2\u7136\u7b54\u5e94\u8fd9\u65b9\u5c0f\u5b50\u548c\u5b89\u59d1\u5a18\uff0c\u4e0d\u65e5\u4e4b\u540e\uff0c\u5c31\u8981\u4e0b\u5c71\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u95fb\u8a00\u4e00\u6014\u9053\uff1a\u201c\u4f60\u7b54\u5e94\u8fc7\u4ed6\u4eec\u4e0b\u5c71\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u4e0d\u9519\uff01\u54ea\u91cc\u53c8\u4e0d\u5bf9\u4e86\uff1f\u201d \u3000\u3000\u6731\u5b5a\u6447\u5934\u9053\uff1a\u201c\u6ca1\u6709\u4ec0\u4e48\u4e0d\u5bf9\uff01\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u56de\u987e\u4e86\u65b9\u96ea\u5b9c\u4e8c\u4eba\u4e00\u773c\uff0c\u7b11\u9053\uff1a\u201c\u4e24\u4f4d\u8001\u5f1f\u53ef\u662f\u51a4\u82e6\u4e86\u8001\u673d\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u4f60\u53ef\u522b\u89c1\u602a\uff0c\u665a\u8f88\u548c\u5b89\u8d24\u5f1f\u53ea\u662f\u89c9\u5f97\uff0c\u795d\u8001\u867d\u7136\u5e94\u5141\u4e0b\u5c71\uff0c\u4f46\u5374\u8981\u5728\u5f88\u4e45\u4ee5\u540e\uff0c\u5982\u662f\u4f60\u8001\u80fd\u529d\u4ed6\u65e9\u65e5\u4e0b\u5c71\uff0c\u90a3\u5c82\u4e0d\u66f4\u597d\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u8fd9\u662f\u592a\u770b\u5f97\u8d77\u8001\u5077\u513f\u4e86\uff01\u53ea\u53ef\u60dc\u8fd9\u8001\u602a\u7269\u5374\u4f9d\u7136\u8981\u7b49\u5230\u6570\u65e5\u4e4b\u540e\u624d\u80af\u4e0b\u5c71\uff0c\u60f3\u8d77\u6765\u5f53\u53eb\u8001\u673d\u5947\u602a\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u5b8b\u8001\u4e5f\u4e0d\u7528\u5947\u602a\uff0c\u8fd9\u4e2d\u95f4\u9053\u7406\uff0c\u665a\u8f88\u5df2\u7ecf\u660e\u767d\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u77e5\u9053\uff0c\u4e0d\u8fc7\uff0c\u773c\u4e0b\u5374\u662f\u4e0d\u4fbf\u8bf4\u51fa\u6765\uff0c\u4f60\u8001\u4e5f\u4e0d\u7528\u591a\u95ee\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u597d\uff01\u8001\u673d\u4e0d\u95ee\u4fbf\u662f\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u5feb\u4e9b\u5403\u70b9\u4e1c\u897f\uff0c\u8001\u592b\u53d1\u89c9\u6709\u4eba\u4e0a\u6765\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u8001\u513f\u83ab\u975e\u7ec3\u5c31\u4e86\u5929\u8033\u901a\uff1f\u600e\u5730\u6211\u4e00\u70b9\u58f0\u606f\u90fd\u672a\u89c9\u51fa\uff0c\u4ed6\u7adf\u7136\u4f1a\u5bfb\u601d\u4e4b\u95f4\uff0c\u4e0d\u89c9\u5730\u7ad9\u8d77\u8eab\u6765\uff0c\u5c31\u5f85\u5411\u5916\u67e5\u770b\u3002 \u3000\u3000\u795d\u516c\u660e\u6325\u624b\u6b62\u4f4f\u4e86\u4ed6\uff0c\u6447\u5934\u9053\uff1a\u201c\u4e0d\u8981\u51fa\u53bb\uff0c\u6b64\u4eba\u5982\u662f\u4e94\u9b54\u4e2d\u4eba\uff0c\u5c31\u7531\u8001\u673d\u5148\u6765\u5bf9\u4ed8\u2026\u2026\u201d \u3000\u3000\u4f59\u97f3\u672a\u5df2\uff0c\u679c\u7136\u6709\u7740\u4e00\u9635\u811a\u6b65\u4e4b\u58f0\uff0c\u4f20\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u51e0\u4eba\u6ca1\u51fa\u58f0\uff0c\u5404\u81ea\u6382\u4e86\u4e00\u5757\u9e7f\u8089\uff0c\u653e\u5728\u53e3\u4e2d\u5480\u56bc\u3002 \u3000\u3000\u8fc7\u4e86\u4e0d\u591a\u4e45\uff0c\u53ea\u89c1\u4e00\u6761\u4eba\u5f71\u5982\u98de\u822c\u5728\u5cf0\u9876\u4e4b\u4e0a\u51fa\u73b0\u3002 \u3000\u3000\u8fd9\u4eba\u5728\u5cf0\u4e0a\u7565\u4e00\u5f20\u671b\uff0c\u7adf\u7136\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u5947\u602a\u554a\uff01\u660e\u660e\u6709\u4eba\uff0c\u600e\u5730\u8001\u592b\u8d76\u5230\u6b64\u5904\uff0c\u5374\u53c8\u4e0d\u89c1\u4e86\u5462\uff1f\u201d \u3000\u3000\u542c\u4ed6\u7684\u53e3\u6c14\uff0c\u751a\u662f\u82cd\u8001\uff0c\u4e5f\u5341\u5206\u964c\u751f\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u770b\u4e86\u5b8b\u5b5a\u4e00\u773c\uff0c\u4f3c\u662f\u60f3\u8bd5\u8bd5\u4ed6\u4f1a\u4e0d\u4f1a\u542c\u5f97\u51fa\u6765\u4eba\u662f1\u8c01\u3002 \u3000\u3000\u4f46\u5b8b\u5b5a\u5374\u832b\u7136\u5730\u6447\u4e86\u4e00\u6447\u5934\uff0c\u663e\u7136\uff0c\u4ed6\u662f\u4e0d\u77e5\u9053\u8fd9\u4eba\u662f\u8c01\u4e86\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u7709\u626c\u4e86\u4e00\u626c\uff0c\u970d\u5730\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u6562\u60c5\uff0c\u4ed6\u8981\u51fa\u6d1e\u53bb\u77a7\u77a7\u90a3\u6765\u4eba\u662f\u8c01\u4e86\uff01 \u3000\u3000\u795d\u516c\u660e\u5ffd\u7136\u4e00\u4f38\u624b\uff0c\u62c9\u4f4f\u4e86\u65b9\u96ea\u5b9c\uff0c\u7528\u4f20\u97f3\u4e4b\u672f\u8bf4\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u522b\u51fa\u53bb\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u867d\u7136\u5927\u4e3a\u597d\u5947\uff0c\u56e0\u76ee\u7779\u795d\u516c\u660e\u8138\u4e0a\u7684\u795e\u8272\u751a\u662f\u4e25\u8083\uff0c\u53ea\u597d\u4e00\u8a00\u4e0d\u53d1\uff0c\u56de\u8eab\u5750\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u6b64\u65f6\uff0c\u795d\u516c\u660e\u5374\u53cc\u624b\u628a\u5934\u53d1\u6293\u4e71\uff0c\u53c8\u6293\u4e86\u4e00\u5927\u628a\u77f3\u82d4\u62b9\u5728\u8138\u4e0a\uff0c\u7136\u540e\u53c8\u8fc5\u5feb\u5730\u628a\u63d2\u5728\u80cc\u540e\u8170\u4e0a\u7684\u4e00\u67c4\u677f\u65a7\u53d6\u4e86\u4e0b\u6765\uff0c\u85cf\u5728\u77f3\u6d1e\u7684\u4e00\u89d2\uff0c\u8fd9\u624d\u5750\u5230\u539f\u5904\uff0c\u770b\u4e86\u4e09\u4eba\u4e00\u773c\uff0c\u7528\u4f20\u97f3\u4e4b\u672f\u9053\uff1a\u201c\u5c11\u65f6\u90a3\u8001\u4eba\u5982\u662f\u95ee\u5230\u8001\u592b\u662f\u8c01\uff0c\u4f60\u4eec\u53ef\u90fd\u5f97\u627f\u8ba4\uff0c\u6211\u662f\u4e10\u5e2e\u7684\u90a3\u4f4d\u5931\u8e2a\u957f\u8001\u54d1\u4e10\u9ec4\u51b2\uff01\u201d \u3000\u3000\u4ed6\u4eec\u867d\u7136\u60f3\u4e0d\u51fa\u795d\u516c\u660e\u4e3a\u4e86\u4ec0\u4e48\uff0c\u4f46\u4ed6\u65e2\u662f\u5982\u6b64\u614e\u91cd\uff0c\u90a3\u5fc5\u7136\u662f\u5f88\u91cd\u8981\u7684\u9053\u7406\u4e86\uff0c\u5f53\u4e0b\uff0c\u4e09\u4eba\u5168\u90fd\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u4eec\u91cd\u65b0\u6293\u8d77\u9e7f\u812f\u98df\u7528\u4e4b\u65f6\uff0c\u5ffd\u7136\u4e00\u4e2a\u767d\u53d1\u82cd\u82cd\u7684\u8001\u4eba\uff0c\u5728\u6d1e\u53e3\u73b0\u8eab\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u62ac\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u90a3\u8001\u4eba\u9aef\u53d1\u7693\u7136\uff0c\u8eab\u6750\u751a\u662f\u7626\u5c0f\uff0c\u7a7f\u7740\u4e00\u4ef6\u8910\u8272\u957f\u886b\uff0c\u867d\u7136\u751a\u662f\u7834\u8d25\u4f46\u5374\u6d46\u6d17\u5f97\u751a\u4e3a\u5e72\u51c0\uff0c\u8138\u8272\u5f88\u6e05\u766f\uff0c\u53cc\u76ee\u795e\u5149\u5145\u6c9b\uff0c\u5b9b\u5982\u4e24\u76cf\u660e\u706f\u4e00\u822c\u3002 \u3000\u3000\u8fd9\u8001\u4eba\u7ad9\u5728\u77f3\u6d1e\u95e8\u53e3\uff0c\u4e5f\u4f3c\u662f\u5403\u4e86\u4e00\u60ca\u822c\u5446\u4f4f\u3002 \u3000\u3000\u53ea\u56e0\u4ed6\u5374\u672a\u60f3\u5230\u8fd9\u77f3\u6d1e\u4e4b\u4e2d\uff0c\u4f1a\u6709\u7740\u8fd9\u4e48\u591a\u4eba\u3002 \u3000\u3000\u4f46\u4ed6\u7565\u7565\u4e00\u6014\u4e4b\u4f59\uff0c\u7acb\u5373\u5927\u6b65\u8d70\u8fdb\u6d1e\u5185\uff0c\u76ee\u5149\u8fc5\u5feb\u7684\u626b\u89c6\u4e86\u56db\u4eba\u4e00\u773c\uff0c\u6c89\u58f0\u9053\uff1a \u3000\u3000\u201c\u4f60\u4eec\u90fd\u662f\u4e9b\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u53e3\u6c14\u5341\u5206\u4e0d\u5584\uff0c\u53ea\u542c\u5f97\u5b89\u5c0f\u840d\u5728\u5fc3\u4e2d\u8001\u5927\u4e0d\u5feb\uff0c\u82e5\u975e\u795d\u516c\u660e\u53ca\u65f6\u770b\u4e86\u5979\u4e00\u773c\uff0c\u5979\u5c31\u8981\u60f3\u8d77\u6765\u548c\u8fd9\u4e2a\u8001\u4eba\u5f00\u53e3\u4e86\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5374\u662f\u62b1\u4e86\u62b1\u62f3\uff0c\u9053\uff1a\u201c\u8001\u4e08\u662f\u95ee\u7684\u5728\u4e0b\u56db\u4eba\u5417\uff1f\u201d \u3000\u3000\u90a3\u8001\u4eba\u9053\uff1a\u201c\u4e0d\u662f\u4f60\u4eec\u662f\u8c01\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6b64\u523b\u7684\u4e2a\u6027\uff0c\u663e\u5f97\u751a\u662f\u5bbd\u5b8f\uff0c\u8001\u4eba\u867d\u7136\u8bf4\u8bdd\u751a\u662f\u96be\u542c\uff0c\u4ed6\u5374\u5e76\u672a\u52a8\u6012\uff0c\u53cd\u5012\u7b11\u9053\uff1a\u201c\u90a3\u771f\u662f\u62b1\u6b49\u5f97\u5f88\uff0c\u8001\u4e08\u4e0d\u7528\u52a8\u6012\uff0c\u6709\u4ec0\u4e48\u8bdd\uff0c\u8bf7\u5750\u4e0b\u518d\u8bf4\u53ef\u597d\uff01\u201d \u3000\u3000\u90a3\u8001\u4eba\u51b7\u54fc\u4e00\u58f0\uff0c\u76ee\u5149\u5728\u795d\u516c\u660e\u8eab\u4e0a\u4e00\u8f6c\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4ed6\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u4e0d\u95ee\u56db\u4eba\uff0c\u53ea\u95ee\u795d\u516c\u660e\u4e00\u4eba\uff0c\u663e\u7136\uff0c\u8fd9\u8001\u513f\u4e43\u662f\u6709\u6240\u4e3a\u800c\u6765\u7684\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u8001\u75af\u5b50\u5417\uff1f\u201d \u3000\u3000\u90a3\u8001\u4eba\u4e00\u6014\u9053\uff1a\u201c\u4ed6\u662f\u75af\u5b50\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4f55\u6b62\u662f\u75af\uff1f\u8001\u5b9e\u8bf4\uff0c\u4ed6\u8fd8\u662f\u4e2a\u54d1\u5df4\u54e9\uff01\u201d \u3000\u3000\u90a3\u8001\u4eba\u5446\u4e86\u4e00\u5446\uff0c\u8138\u4e0a\u5c3d\u662f\u4e0d\u4fe1\u4e4b\u60c5\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u89c1\u90a3\u8001\u4eba\u6ca1\u6709\u8bf4\u8bdd\uff0c\u5f53\u4e0b\u7b11\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4f60\u53ef\u662f\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u90a3\u7626\u8001\u4eba\u9053\uff1a\u201c\u8001\u592b\u6709\u4e9b\u4e0d\u4fe1\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u8001\u4e08\u65e2\u80fd\u4e0a\u5f97\u592a\u5ba4\u5cf0\u5934\uff0c\u800c\u4e14\uff0c\u8eab\u4e0a\u7a7f\u7684\u8fd9\u7b49\u5355\u8584\uff0c\u5fc5\u662f\u4e00\u4f4d\u6b66\u6797\u4e2d\u7684\u4eba\u7269\u4e86\uff01\u201d \u3000\u3000\u90a3\u7626\u8001\u4eba\u9053\uff1a\u201c\u662f\u53c8\u5982\u4f55\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u6b66\u6797\u4e4b\u4e2d\uff0c\u5927\u5927\u6709\u540d\u7684\u4e10\u5e2e\uff0c\u4f60\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u7626\u8001\u4eba\u9053\uff1a\u201c\u4e10\u5e2e\uff1f\u90a3\u4e5f\u7b97\u5f97\u6709\u540d\uff1f\u54fc\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u5f97\u5fc3\u4e2d\u4e00\u9707\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u8001\u513f\u662f\u4ec0\u4e48\u4eba\uff1f\u4ed6\u5c45\u7136\u628a\u6574\u4e2a\u4e10\u5e2e\u90fd\u6ca1\u653e\u5728\u773c\u4e2d\uff0c\u5018\u662f\u5982\u6b64\uff0c\u90a3\u54d1\u4e10\u9ec4\u51b2\u56db\u5b57\uff0c\u53c8\u600e\u6837\u80fd\u5413\u5f97\u4e86\u4ed6\uff1f\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u56fa\u7136\u8fd9\u4e48\u60f3\uff0c\u4f46\u53e3\u4e2d\u5374\u662f\u7b11\u9053\uff1a\u201c\u8001\u4e08\u7684\u53e3\u6c14\u4e0d\u5c0f\u554a\uff01\u201d \u3000\u3000\u7626\u4eba\u9053\uff1a\u201c\u4e0d\u9519\u2026\u2026\u90a3\u5357\u534e\u795e\u4e5e\u89c1\u4e86\u8001\u592b\u8fd8\u8981\u78d5\u5934\uff0c\u5c0f\u5c0f\u4e00\u4e2a\u4e10\u5e2e\uff0c\u53c8\u80fd\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u76b1\u4e00\u76b1\u7709\uff0c\u63a5\u9053\uff1a\u201c\u8001\u4e08\uff0c\u4f60\u65e2\u7136\u6bd4\u90a3\u4e10\u5e2e\u5e2e\u4e3b\u8fd8\u8981\u9ad8\u4e86\u4e00\u8f88\uff0c\u60f3\u5fc5\u5c31\u662f\u4e10\u5e2e\u7684\u4e00\u4f4d\u957f\u8001\u4e4b\u5c0a\u4e86\uff01\u201d \u3000\u3000\u7626\u8001\u4eba\u95f4\u8a00\uff0c\u5927\u6012\u9053\uff1a\u201c\u8c01\u8bf4\u8001\u592b\u662f\u4e10\u5e2e\u7684\u957f\u8001\uff1f\u6df7\u5e10\uff0c\u96be\u9053\u4f60\u4eec\u8fde\u6b66\u6797\u4e00\u602a\u7684\u5927\u540d\u90fd\u6ca1\u542c\u8fc7\uff1f\u201d \u3000\u3000\u6562\u60c5\u8fd9\u7626\u8001\u4eba\u4e00\u6012\u4e4b\u4e0b\uff0c\u81ea\u5df1\u5012\u5149\u62a5\u51fa\u540d\u53f7\u6765\u4e86\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u3001\u5b89\u5c0f\u840d\u867d\u7136\u89c9\u5f97\u8fd9\u6b66\u6797\u4e00\u602a\u56db\u5b57\uff0c\u6709\u70b9\u513f\u9707\u60ca\uff0c\u4f46\u56e0\u4ed6\u4eec\u6ca1\u6709\u542c\u4eba\u63d0\u8fc7\uff0c\u6545\u800c\u8fdc\u4e0d\u5982\u5b8b\u5b5a\u5728\u542c\u5230\u8fd9\u56db\u5b57\u4ee5\u540e\u7684\u9707\u60b8\uff01 \u3000\u3000\u53ea\u89c1\u4ed6\u4e24\u624b\u6309\u5730\uff0c\u5c31\u5f85\u811a\u5e95\u63e9\u6cb9\uff0c\u5f00\u6e9c\u4e86\uff01 \u3000\u3000\u4f46\u65b9\u96ea\u5b9c\u5374\u795e\u8272\u4e0d\u52a8\u5730\u7b11\u9053\uff1a\u201c\u6b66\u6797\u4e00\u602a\u53ef\u5c31\u662f\u8001\u4e08\u7684\u540d\u53f7\uff1f\u201d \u3000\u3000\u90a3\u8001\u4eba\u773c\u89c1\u56db\u4eba\u542c\u4e86\u81ea\u5df1\u540d\u53f7\uff0c\u4e0d\u5982\u6240\u6599\u7684\u60ca\u614c\uff0c\u4e5f\u6709\u7740\u610f\u5916\u4e4b\u611f\uff0c\u6012\u9053\uff1a\u201c\u4e0d\u9519\uff01 \u3000\u3000\u8001\u592b\u5c31\u662f\u6b66\u6797\u4e00\u602a\u8f9b\u67cf\u516c\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u542c\u5f97\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u8001\u4eba\u5bb6\u7684\u8f9b\u67cf\u516c\u4e09\u5b57\uff0c\u5012\u662f\u633a\u6b63\u76f4\u7684\u540d\u53f7\uff0c\u53ea\u662f\uff0c\u52a0\u4e0a\u6b66\u6797\u4e00\u602a\u56db\u5b57\uff0c\u53ef\u8bf4\u96be\u542c\u5f97\u5f88\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u5446\u4e86\u4e00\u5446\u5411\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u5c0f\u4e2b\u5934\uff0c\u4f60\u5728\u80e1\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u6211\u8bf4\u4f60\u597d\u597d\u7684\u4e00\u4e2a\u8f9b\u67cf\u516c\uff0c\u4e3a\u4ec0\u4e48\u8981\u52a0\u4e0a\u6b66\u6797\u4e00\u602a\u56db\u5b57\uff0c\u4f7f\u4eba\u542c\u6765\u602a\u91cc\u602a\u6c14\u7684\u54e9\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4e0d\u597d\u542c\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u53ef\u4e0d\u662f\uff1f\u4e00\u4e2a\u4eba\u597d\u597d\u7684\uff0c\u4e3a\u4ec0\u4e48\u8981\u4f5c\u602a\u7269\u5462\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u95fb\u8a00\u5446\u4e86\u4e00\u5446\uff0c\u62ac\u8d77\u5934\u6765\uff0c\u534a\u664c\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u5b8b\u5b5a\u8fd9\u91cc\u53ef\u5c31\u5728\u5fc3\u4e2d\u6697\u6697\u6253\u9f13\uff0c\u53ea\u56e0\u4ed6\u5bf9\u6b66\u6797\u4e00\u602a\u8f9b\u4f2f\u516c\u7684\u4e3a\u4eba\uff0c\u77e5\u9053\u7684\u4e0d\u5c11\uff0c\u5f53\u4e0b\u8fde\u8fde\u65bd\u5c55\u773c\u8272\uff0c\u60f3\u8981\u5b89\u5c0f\u840d\u522b\u518d\u591a\u8bf4\u4e0b\u53bb\uff0c\u60f9\u5f97\u8fd9\u8001\u602a\u7269\u52a8\u4e86\u809d\u706b\uff0c\u53ef\u5c31\u6709\u4e9b\u513f\u4e0d\u597d\u5bf9\u4ed8\u3002 \u3000\u3000\u4f46\u5b89\u5c0f\u840d\u5374\u4f3c\u5168\u4e0d\u77e5\u4e00\u822c\uff0c\u62ac\u8d77\u90a3\u5f20\u5b9c\u559c\u5b9c\u55d4\u7684\u7c89\u8138\uff0c\u63a5\u9053\uff1a\u201c\u8f9b\u8001\u516c\u516c\uff0c\u4f60\u5e72\u5417\u4e0d\u8bf4\u8bdd\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u76b1\u4e86\u76b1\u7709\uff0c\u9053\uff1a\u201c\u4e2b\u5934\uff0c\u8001\u592b\u662f\u5728\u60f3\uff0c\u4f60\u8fd9\u756a\u8bdd\u662f\u6709\u4e00\u70b9\u9053\u7406\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u771f\u7684\uff1f\u90a3\u4f60\u8001\u4f55\u4e0d\u597d\u597d\u505a\u4eba\uff0c\u4e0d\u518d\u505a\u90a3\u602a\u7269\u5462\uff1f\u201d\u8f9b\u67cf\u516c\u4e24\u773c\u5b9b\u5982\u51b7\u7535\u4e00\u822c\u7684\u5c04\u5411\u5b89\u5c0f\u840d\u8eab\u4e0a\uff0c\u6c89\u541f\u9053\uff1a\u201c\u8fd9\u4e2a\u5417\uff1f\u8001\u592b\u8fd8\u8981\u60f3\u4e0a\u4e00\u60f3\uff01\u201d \u3000\u3000\u5ffd\u7136\u5411\u65b9\u96ea\u5b9c\u77aa\u4e86\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u4f60\u521a\u624d\u63d0\u8d77\u4e10\u5e2e\uff0c\u90a3\u7a76\u7adf\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u89c1\u4ed6\u88ab\u5b89\u5c0f\u840d\u51e0\u53e5\u8bdd\u4e00\u9876\uff0c\u5c45\u7136\u53d8\u5f97\u4e0d\u518d\u90a3\u4e48\u6a2a\u86ee\u81ea\u50b2\uff0c\u5f53\u4e0b\u6697\u9053\uff1a\u201c\u6b64\u4eba\u867d\u7136\u6709\u7740\u4e00\u602a\u4e4b\u540d\uff0c\u53ea\u6015\u5e76\u975e\u771f\u662f\u90a3\u9b54\u9053\u4e4b\u6d41\u2026\u2026\u201d \u3000\u3000\u6709\u4e86\u8fd9\u7b49\u60f3\u6cd5\uff0c\u4ed6\u4e5f\u5c31\u4e0d\u518d\u8bda\u5fc3\u8c03\u4f83\u8fd9\u8001\u4eba\uff0c\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u4e10\u4e2d\u6709\u4e00\u4f4d\u54d1\u4e10\u9ec4\u51b2\uff0c\u4e0d\u77e5\u8f9b\u8001\u53ef\u77e5\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u95fb\u8a00\uff0c\u5168\u8eab\u4e00\u9707\uff0c\u5927\u58f0\u9053\uff1a\u201c\u9ec4\u51b2\uff1f\u4ed6\u8fd8\u6ca1\u6709\u6b7b\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u89c1\u4ed6\u4e00\u542c\u5230\u9ec4\u51b2\u4e4b\u540d\uff0c\u5c45\u7136\u5982\u6b64\u5403\u60ca\uff0c\u4e5f\u662f\u6709\u4e9b\u5947\u602a\uff0c\u5fd9\u9053\uff1a\u201c\u8c01\u8bf4\u4ed6\u6b7b\u4e86\uff0c\u773c\u524d\u8fd9\u4f4d\u53c8\u75af\u53c8\u54d1\u4e4b\u4eba\uff0c\u4fbf\u662f\u90a3\u4f4d\u54d1\u4e10\u5e2e\u9ec4\u957f\u8001\u4e86\u3002\u201d \u3000\u3000\u8f9b\u67cf\u516c\u6709\u4e9b\u513f\u4e0d\u4fe1\uff0c\u9000\u4e86\u4e00\u6b65\uff0c\u77aa\u773c\u5728\u795d\u516c\u660e\u8eab\u4e0a\u8f6c\u6765\u8f6c\u53bb\u3002 \u3000\u3000\u8db3\u8db3\u6709\u4e86\u76cf\u8336\u4e4b\u4e45\uff0c\u8fd9\u624d\u957f\u957f\u4e00\u53f9\u9053\uff1a\u201c\u662f\u771f\u662f\u5047\uff0c\u8001\u592b\u4e5f\u77a7\u4e0d\u51fa\u6765\u4e86\u2026\u2026\u201d\u4ed6\u8bed\u97f3\u4e00\u987f\uff0c\u5ffd\u7136\u62b1\u62f3\u5411\u795d\u516c\u660e\u4e00\u63d6\u9053\uff1a\u201c\u5f53\u5e74\u5728\u6cf0\u5c71\u5357\u5929\u95e8\u724c\u574a\u4e4b\u4e0b\uff0c\u627f\u8499\u9ec4\u5144\u52a9\u8fc7\u8001\u673d\u4e00\u81c2\u4e4b\u529b\uff0c\u53ea\u56e0\u5929\u8272\u660f\u6697\uff0c\u672a\u80fd\u8bb0\u6ce8\u9ec4\u5144\u7684\u5f62\u8c8c\uff0c\u540e\u6765\u8001\u673d\u867d\u7136\u5230\u5904\u5bfb\u627e\u9ec4\u5144\uff0c\u5374\u4e00\u76f4\u662f\u7f18\u6da9\u4e00\u9762\uff0c\u53eb\u4eba\u597d\u751f\u96be\u8fc7\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u90a3\u53c8\u4f55\u5fc5\uff0c\u9ec4\u957f\u8001\u4e0d\u5c31\u5728\u773c\u524d\u5417\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8001\u673d\u540e\u6765\u542c\u8bf4\uff0c\u9ec4\u51b2\u5df2\u88ab\u4ed6\u4eec\u81ea\u5df1\u5e2e\u4e2d\u4e00\u540d\u665a\u8f88\u5bb3\u6b7b\uff0c\u6545\u4f7f\u8001\u592b\u5931\u5374\u4e86\u62a5\u6069\u7684\u673a\u4f1a\uff0c\u6545\u800c\u8001\u592b\u5bf9\u4e8e\u4e10\u5e2e\u5f1f\u5b50\uff0c\u5411\u6765\u4e0d\u5047\u8f9e\u8272\uff0c\u60f3\u4e0d\u5230\uff0c\u539f\u6765\u9ec4\u5144\u957f\u672a\u8fc7\u4e16\uff01\u201d \u3000\u3000\u4ed6\u8fd9\u4e00\u756a\u8bdd\uff0c\u53ea\u628a\u65b9\u96ea\u5b9c\u542c\u5f97\u5446\u4e86\u534a\u664c\u3002 \u3000\u3000\u76f4\u5230\u6b64\u523b\uff0c\u4ed6\u624d\u609f\u51fa\u4e86\u795d\u516c\u660e\u5192\u5145\u9ec4\u51b2\u4e4b\u610f\uff0c\u6562\u60c5\uff0c\u4e43\u662f\u4e3a\u4e86\u66ff\u4e10\u5e2e\u5316\u89e3\u4e00\u4e2a\u5f3a\u654c\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u90a3\u795d\u516c\u660e\u5ffd\u7136\u6293\u7740\u9e7f\u812f\uff0c\u53fd\u54e9\u5495\u54af\u5730\u5411\u8f9b\u67cf\u516c\u6307\u624b\u5212\u811a\uff0c\u4e71\u53eb\u4e86\u4e00\u9635\u3002 \u3000\u3000\u8f9b\u67cf\u516c\u795e\u6001\u751a\u662f\u606d\u8c28\uff0c\u7779\u72b6\u5411\u5b89\u5c0f\u840d\u9053\uff0c\u201c\u5c0f\u59d1\u5a18\uff0c\u4f60\u4eec\u65e2\u7136\u4e0e\u9ec4\u5144\u5728\u4e00\u8d77\uff0c\u60f3\u5fc5\u77e5\u9053\u4e00\u4e9b\u4ed6\u7684\u624b\u52bf\u548c\u54d1\u8bed\u7684\u4e86\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u987a\u53e3\u7b11\u9053\uff1a\u201c\u5f53\u7136\uff01\u4e0d\u7136\u54b1\u4eec\u600e\u4f1a\u5728\u4e00\u5904\u5403\u8089\u804a\u5929\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u9053\uff1a\u201c\u5979\u600e\u4e48\u8fd9\u7b49\u4fe1\u53e3\u5f00\u6cb3\uff1f\u201d \u3000\u3000\u53ea\u542c\u5f97\u8f9b\u67cf\u516c\u7b11\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u521a\u624d\u9ec4\u5144\u8bf4\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4f60\u771f\u7684\u8981\u542c\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u4e00\u6014\u9053\uff1a\u201c\u5f53\u7136\u662f\u771f\u7684\uff0c\u9ec4\u5144\u65e2\u662f\u6307\u70b9\u7740\u8001\u673d\u51fa\u58f0\uff0c\u90a3\u5fc5\u7136\u662f\u5bf9\u8001\u673d\u5728\u8bf4\u8bdd\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5728\u5fc3\u4e2d\u5927\u4e3a\u7740\u6025\uff0c\u56e0\u4e3a\u795d\u516c\u660e\u521a\u624d\u53ea\u4e0d\u8fc7\u662f\u80e1\u4e71\u9b3c\u53eb\u4e86\u4e00\u9635\uff0c\u54ea\u91cc\u8bf4\u8fc7\u4ec0\u4e48\uff1f \u3000\u3000\u5b89\u5c0f\u840d\u5c45\u7136\u5982\u6b64\u8bf4\u6cd5\uff0c\u4ed6\u771f\u731c\u4e0d\u900f\u5979\u8981\u5bf9\u8f9b\u67cf\u516c\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5728\u8f9b\u67cf\u516c\u8bed\u97f3\u4e00\u843d\u4e4b\u9645\uff0c\u7acb\u5373\u63a5\u9053\uff1a\u201c\u9ec4\u8001\u662f\u8bf4\uff0c\u8fc7\u53bb\u7684\u4e8b\uff0c\u90fd\u591a\u5c11\u5e74\u4e86\uff0c\u8fd8\u63d0\u4ed6\u4f5c\u751a\uff1f\u773c\u4e0b\u5982\u679c\u4f60\u8f9b\u8001\u54e5\u5b58\u7740\u62a5\u6069\u4e4b\u5fc3\uff0c\u5374\u6709\u4e24\u4ef6\u4e8b\u8981\u505a\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4e24\u6869\u4e8b\uff1f\u90a3\u65f6\u5728\u5357\u5929\u95e8\u4ed6\u53ea\u662f\u52a9\u6211\u4e00\u638c\u800c\u5df2\u554a\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5931\u7b11\u9053\uff1a\u201c\u597d\u554a\uff0c\u539f\u6765\u4f60\u662f\u5047\u7684\u62a5\u6069\u7684\u5417\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u8c01\u8bf4\u662f\u5047\u7684\uff0c\u8001\u592b\u4e00\u7247\u771f\u5fc3\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u5982\u679c\u4f60\u662f\u771f\u5fc3\uff0c\u90a3\u53c8\u4f55\u5fc5\u8ba1\u8f83\u4e00\u6869\u4e24\u6869\uff1f\u6216\u662f\u4e00\u638c\u4e24\u638c\u5462\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u4e00\u5446\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u5012\u662f\u8001\u592b\u9519\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u5f53\u7136\u662f\u4f60\u9519\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u6293\u4e86\u6293\u5934\uff0c\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u9ec4\u5144\u8bf4\u7684\u662f\u54ea\u4e24\u6869\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u7b2c\u4e00\u6869\uff0c\u4ed6\u6ca1\u6709\u6b7b\uff0c\u4f60\u6b64\u540e\u5bf9\u4e10\u5e2e\u7684\u5f1f\u5b50\uff0c\u53ef\u8981\u5ba2\u6c14\u4e9b\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u5927\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u5f53\u7136\uff01\u201d\u8bdd\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u7b2c\u4e8c\u6869\u5462\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6c89\u541f\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u7b2c\u4e8c\u6869\u561b\uff0c\u53ea\u6015\u8f9b\u8001\u4e0d\u613f\u7b54\u5e94\u4e86\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4e0d\u8981\u7d27\uff0c\u5929\u4e0b\u6ca1\u6709\u96be\u5f97\u5012\u8001\u592b\u4e4b\u4e8b\uff0c\u4f60\u53ea\u7ba1\u8bf4\u5427\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u76ee\u5149\u5728\u65b9\u96ea\u5b9c\u8138\u4e0a\u4e00\u8f6c\uff0c\u65b9\u96ea\u5b9c\u5403\u4e86\u4e00\u60ca\uff0c\u6697\u9053\uff1a\u201c\u5979\u5343\u4e07\u4e0d\u8981\u628a\u6211\u626f\u8fdb\u53bb\u624d\u597d\u2026\u2026\u201d \u3000\u3000\u5bfb\u601d\u95f4\uff0c\u5b89\u5c0f\u840d\u5df1\u7136\u7b11\u9053\uff1a\u201c\u90a3\u9ec4\u8001\u8bf4\uff0c\u4ed6\u4e0e\u8fd9\u4f4d\u65b9\u516c\u5b50\u4e43\u662f\u5fd8\u5e74\u4e4b\u4ea4\uff0c\u65b9\u516c\u5b50\u773c\u4e0b\u5df2\u88ab\u6b66\u6797\u4e2d\u63a8\u4e3e\u4e3a\u5bf9\u6297\u4e94\u9b54\u7684\u4e3b\u76df\u4e4b\u4eba\uff0c\u8981\u4f60\u8f9b\u8001\u4eca\u540e\u968f\u5728\u65b9\u516c\u5b50\u8eab\u65c1\uff0c\u52a9\u4ed6\u964d\u4f0f\u4e94\u9b54\u3002\u201d \u3000\u3000\u8c01\u4e5f\u4e0d\u66fe\u60f3\u5230\uff0c\u5979\u8bf4\u51fa\u6765\u7684\u7adf\u662f\u8fd9\u4e48\u4e00\u56de\u4e8b\uff01\u4e0d\u4f46\u65b9\u96ea\u5b9c\u5446\u4e86\u4e00\u5446\uff0c\u8fde\u795d\u516c\u660e\u90fd\u51e0\u4e4e\u5fd8\u4e86\u81ea\u5df1\u4e43\u662f\u5047\u7684\u54d1\u4e10\u9ec4\u51b2\uff0c\u800c\u8981\u5931\u58f0\u53eb\u4e86\uff01 \u3000\u3000\u4f46\u8f9b\u67cf\u516c\u5374\u767d\u7709\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u914d\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u914d\u4e0d\u914d\u4e8e\u4f60\u8001\u65e0\u5173\uff0c\u9ec4\u8001\u65e2\u8fd9\u4e48\u8bf4\uff0c\u4f60\u8001\u5c31\u5e94\u8be5\u8981\u9075\u4ece\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8001\u592b\u4e3a\u4f55\u8981\u9075\u4ece\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u4f60\u60f3\u62a5\u6069\uff0c\u600e\u53ef\u4e0d\u9075\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u6293\u5934\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u8001\u592b\u5b9e\u5728\u6709\u4e9b\u4e0d\u670d\u6c14\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u683c\u683c\u4e00\u7b11\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u670d\u6c14\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u4e73\u81ed\u672a\u5e72\uff0c\u4ed6\u51ed\u4ec0\u4e48\u53ef\u4ee5\u4f7f\u5524\u8001\u592b\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4f60\u597d\u50cf\u77a7\u4e0d\u8d77\u65b9\u5144\u662f\u5417\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u9ec4\u53e3\u4e73\u5b50\uff0c\u600e\u80fd\u53eb\u8001\u592b\u53e6\u773c\u76f8\u5f85\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u6709\u5fd7\u4e0d\u5728\u5e74\u9ad8\uff0c\u4f60\u8001\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e2b\u5934\uff0c\u8001\u592b\u53ef\u7528\u4e0d\u7740\u4f60\u6765\u6559\u8bad\uff0c\u82e5\u4e0d\u662f\u77a7\u5728\u9ec4\u5144\u7684\u4efd\u4e0a\uff0c\u4f60\u8fd9\u7b49\u9876\u649e\u8001\u592b\uff0c\u5c31\u7b97\u6709\u5341\u6761\u6027\u547d\uff0c\u4e5f\u65e9\u6d3b\u4e0d\u6210\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5012\u662f\u5ffd\u7136\u53d8\u5f97\u5341\u5206\u6709\u8010\u6027\uff0c\u95fb\u8a00\u7b11\u9053\uff1a\u201c\u770b\u6765\u4f60\u8001\u5012\u662f\u5f88\u559c\u6b22\u6740\u4eba\u7684\u4e86\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u662f\u53c8\u5982\u4f55\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4e0d\u5982\u4f55\uff01\u53ea\u662f\u2026\u2026\u51a4\u51a4\u76f8\u62a5\uff0c\u4f60\u8001\u4e5f\u4e0d\u6015\u522b\u4eba\u6740\u4f60\u5417\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u5ffd\u7136\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u6740\u6211\uff1f\u6b66\u6797\u4e2d\u5982\u6709\u8fd9\u7b49\u4eba\uff0c\u8001\u592b\u8fd8\u80fd\u6d3b\u5230\u4eca\u5929\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4f60\u53e3\u6c14\u4e0d\u5c0f\u554a\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u8001\u592b\u4eca\u5929\u5df2\u7ecf\u5341\u5206\u8c26\u865a\u4e86\uff0c\u5c0f\u4e2b\u5934\uff0c\u4f60\u53ef\u77e5\u9053\uff0c\u8001\u592b\u5f80\u65e5\u8bf4\u8bdd\u6839\u672c\u4e0d\u7528\u7ec6\u60f3\u5462\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u95fb\u8a00\uff0c\u4e0d\u7981\u683c\u683c\u4e00\u7b11\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u8bf4\u8bdd\u5982\u679c\u4e0d\u7528\u7ec6\u60f3\uff0c\u90a3\u4e48\u5fc5\u662f\u5f88\u597d\u73a9\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u5f53\u7136\uff01\u4e0d\u8fc7\u2026\u2026\u5bf9\u4e8e\u4f60\u4eec\u5987\u9053\u4eba\u5bb6\uff0c\u90a3\u53ef\u4e0d\u5927\u5408\u9002\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7ae5\u5fc3\u672a\u6cef\uff0c\u95fb\u8a00\u5927\u611f\u5174\u8da3\uff0c\u7b11\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u4eba\u4e0d\u90fd\u4e00\u6837\u5417\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u5f53\u7136\u4e0d\u4e00\u6837\uff0c\u5973\u5b69\u513f\u5bb6\u5982\u679c\u80e1\u4e71\u8bf4\uff0c\u7eb5\u7136\u81ea\u5df1\u4e0d\u5bb3\u7f9e\uff0c\u522b\u4eba\u542c\u4e86\u4e5f\u4e0d\u5927\u5bf9\u52b2\u554a\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u95fb\u8a00\uff0c\u60f3\u4e86\u4e00\u60f3\uff0c\u9053\uff1a\u201c\u8f9b\u8001\uff0c\u4f60\u8fd8\u6ca1\u6709\u7b54\u5e94\u9ec4\u8001\u7684\u7b2c\u4e8c\u4e2a\u6761\u4ef6\u5462\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u7709\u4e00\u626c\uff0c\u770b\u4e86\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u9053\uff1a\u201c\u597d\u5427\uff0c\u8001\u592b\u7b54\u5e94\u662f\u53ef\u4ee5\uff0c\u4f46\u81f3\u5c11\u53ef\u4ee5\u53eb\u8001\u592b\u660e\u767d\u8fd9\u5c0f\u5b50\u51ed\u4ec0\u4e48\u80fd\u591f\u6709\u8d44\u683c\u6307\u4f7f\u8001\u592b\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u5f88\u7b80\u5355\uff0c\u8001\u592b\u8981\u8f83\u91cf\u8f83\u91cf\u4ed6\u7684\u6b66\u529f\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4f60\u8981\u8f83\u91cf\u65b9\u5144\u5f1f\u7684\u6b66\u529f\u5417\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u770b\u4e86\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u2026\u2026\u610f\u4e0b\u5982\u4f55\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u672c\u6765\u5c31\u6015\u5979\u628a\u81ea\u5df1\u62ac\u4e86\u51fa\u53bb\u548c\u8fd9\u8f9b\u67cf\u516c\u52a8\u624b\uff0c\u8fd9\u65f6\u95fb\u8a00\uff0c\u4e0d\u7981\u5927\u58f0\u9053\uff1a\u201c\u8d24\u5f1f\u4f60\u4e0d\u8981\u628a\u6211\u626f\u4e0a\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u65b9\u5144\u4e8b\u5230\u4e34\u5934\uff0c\u4f60\u63a8\u4e0d\u6389\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8d24\u5f1f\u4f60\u2026\u2026\u201d \u3000\u3000\u8f9b\u67cf\u516c\u8fd9\u65f6\u5374\u5475\u5475\u4e00\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4e5d\u5927\u95e8\u6d3e\u63a8\u7684\u4e3b\u76df\u4e4b\u4eba\uff0c\u7adf\u7136\u662f\u4e00\u4e2a\u6015\u4e8b\u7684\u9ec4\u6bdb\u4e73\u5b50\uff0c\u771f\u53eb\u8001\u592b\u7b11\u6389\u4e86\u7259\u4e86\uff01\u201d \u3000\u3000\u4ed6\u8fd9\u7b49\u595a\u843d\uff0c\u542c\u5728\u65b9\u96ea\u5b9c\u8033\u4e2d\uff0c\u81ea\u7136\u662f\u4e0d\u597d\u53d7\u7684\u4e86\uff01 \u3000\u3000\u4f46\u65b9\u96ea\u5b9c\u4f9d\u7136\u672a\u66fe\u53d1\u4f5c\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u67f3\u7709\u5374\u626c\u4e86\u4e00\u626c\uff0c\u9053\uff1a\u201c\u8f9b\u8001\uff0c\u4f60\u8bf4\u8bdd\u4e0d\u8981\u592a\u523a\u4eba\u4e86\u554a\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u770b\u4e86\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u2026\u2026\u600e\u4e48\u4e00\u70b9\u70b9\u7537\u5b50\u6c14\u90fd\u6ca1\u6709\u4e86\uff1f\u201d\u6562\u60c5\uff0c\u5b89\u5c0f\u840d\u5df2\u7ecf\u52a8\u706b\u4e86\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u5bf9\u4e8e\u8f9b\u67cf\u516c\u7684\u4e00\u518d\u523a\u8bbd\u81ea\u5df1\uff0c\u5e76\u672a\u89c9\u5f97\u6709\u4ec0\u4e48\u592a\u5927\u7684\u96be\u53d7\uff0c\u4f46\u5b89\u5c0f\u840d\u7684\u8fd9\u53e5\u8bdd\uff0c\u5374\u4f7f\u4ed6\u7684\u8138\u8272\u5927\u53d8\uff0c\u80f8\u4e2d\u5927\u4e3a\u6c14\u607c\uff01 \u3000\u3000\u4ed6\u53cc\u76ee\u66b4\u7741\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5144\u5f1f\u4f60\u522b\u518d\u7ed9\u6211\u627e\u5c94\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u65b9\u5144\u5f1f\uff0c\u8fd9\u4e0d\u662f\u6211\u627e\u4f60\u7684\u5c94\u554a\uff01\u201d \u3000\u3000\u5979\u6307\u4e86\u6307\u8f9b\u67cf\u516c\uff0c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u8fd9\u4f4d\u8001\u4eba\u5bb6\u8ba4\u4e3a\u4f60\u6839\u672c\u4e0d\u591f\u8d44\u683c\u5f53\u4e3b\u76df\u5bf9\u4ed8\u4e94\u5927\u9b54\u4e3b\uff0c\u4f60\u4e3a\u4f55\u4e0d\u8ba9\u4ed6\u89c1\u8bc6\u89c1\u8bc6\u4f60\u7684\u5251\u6cd5\u5462\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u95fb\u8a00\uff0c\u5ffd\u7136\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u5c0f\u5b50\u4f60\u7528\u5251\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u5f88\u597d\uff01\u8001\u592b\u81ea\u8ba4\u5251\u6cd5\u7b2c\u4e00\uff0c\u5c0f\u5b50\u4f60\u6562\u4e0e\u8001\u592b\u8bd5\u8bd5\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\u4e00\u6014\uff0c\u6697\u9053\uff1a\u201c\u4ed6\u65e2\u7136\u6562\u8ba4\u5251\u6cd5\u7b2c\u4e00\uff0c\u72c2\u5f97\u5f88\u554a\uff01\u201d \u3000\u3000\u8f6c\u5ff5\u95ee\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u5251\u6cd5\u6562\u79f0\u6b66\u6797\u7b2c\u4e00\u5417\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u8981\u4e0d\u4fe1\uff0c\u4f55\u4e0d\u8bd5\u8bd5\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u8bd5\u4e0d\u8bd5\uff0c\u5f85\u4f1a\u513f\u518d\u8bb2\uff0c\u4f46\u5728\u4e0b\u5374\u4e0d\u4fe1\u4f60\u7684\u5251\u6cd5\u5728\u6b66\u6797\u4e0a\u7b2c\u4e00\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u4e0d\u4fe1\u8001\u592b\u5251\u6cd5\u662f\u6b66\u6797\u7b2c\u4e00\uff0c\u4f60\u4fe1\u8c01\u662f\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u5728\u4e0b\u5012\u4e00\u65f6\u60f3\u4e0d\u51fa\u6765\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5ffd\u7136\u63a5\u9053\uff1a\u201c\u8f9b\u8001\uff0c\u4f60\u8bf4\u4f60\u81ea\u5df1\u5251\u6cd5\u5728\u6b66\u6797\u7b2c\u4e00\uff0c\u90a3\u4e48\u9648\u5e0c\u6b63\u53c8\u662f\u5929\u4e0b\u7b2c\u51e0\u5462\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u5c0f\u4e2b\u5934\uff0c\u4f60\u662f\u8bf4\u9648\u5e0c\u6b63\u6bd4\u8001\u592b\u7684\u5251\u6cd5\u8981\u5f3a\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4eba\u5bb6\u79f0\u4e3a\u5251\u795e\u5f53\u6bd4\u4f60\u9ad8\u660e\u7684\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u80e1\u8bf4\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u8f9b\u8001\uff0c\u53ea\u6015\u4f60\u81ea\u5df1\u624d\u662f\u80e1\u8bf4\u5462\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7a81\u7136\u63d2\u53e3\u9053\uff1a\u201c\u8f9b\u8001\uff0c\u4f60\u89c1\u8fc7\u5251\u795e\u9648\u5927\u4fa0\u5417\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u5ffd\u7136\u6447\u6447\u5934\u9053\uff1a\u201c\u6ca1\u6709\uff01\u90a3\u9648\u5e0c\u6b63\u7684\u5e74\u7eaa\uff0c\u6bd4\u8001\u592b\u5c0f\u4e86\u4e00\u4e8c\u5341\u5c81\uff0c\u8001\u592b\u5c82\u80af\u4e0e\u4ed6\u8fd9\u7b49\u542f\u751f\u665a\u8f88\u52a8\u624b\uff1f\u201d \u3000\u3000\u679c\u7136\u53e3\u6c14\u4e0d\u5c0f\uff0c\u8fde\u5251\u795e\u5728\u4ed6\u7684\u5fc3\u76ee\u4e2d\u4e5f\u6210\u4e86\u540e\u751f\u665a\u8f88\u4e86\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u7709\u5934\u4e00\u626c\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u5c0a\u9a7e\u65e2\u7136\u672a\u4e0e\u9648\u5927\u4fa0\u52a8\u8fc7\u624b\uff0c\u53c8\u600e\u77e5\u9648\u5927\u4fa0\u7684\u5251\u6cd5\uff0c\u4e0d\u6bd4\u5c0a\u9a7e\u9ad8\u660e\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u5927\u7b11\u9053\uff1a\u201c\u90a3\u8fd8\u4e0d\u5bb9\u6613\u5417\uff1f\u5c31\u51ed\u8001\u592b\u6bd4\u4ed6\u5927\u4e86\u4e8c\u5341\u5c81\uff0c\u5c31\u53ef\u77e5\u9053\u4ed6\u7684\u5251\u6cd5\u7edd\u975e\u8001\u592b\u4e4b\u654c\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u9053\uff1a\u8fd9\u662f\u4ec0\u4e48\u9053\u7406\uff1f\u96be\u9053\u5e74\u7eaa\u5927\u7684\u4eba\uff0c\u5c31\u4e00\u5b9a\u6bd4\u5e74\u8f7b\u4eba\u5f3a\u4e9b\u5417\uff1f \u3000\u3000\u5bfb\u601d\u95f4\uff0c\u5374\u542c\u5f97\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u8f9b\u8001\uff0c\u8fd9\u8bdd\u4f3c\u4e4e\u662f\u6709\u4e9b\u4e0d\u901a\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u54ea\u91cc\u4e0d\u901a\u4e86\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u7684\u6b66\u529f\u9ad8\u4f4e\uff0c\u96be\u9053\u5c31\u662f\u9760\u5e74\u7eaa\u6765\u51b3\u5b9a\u7684\u5417\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u591a\u7ec3\u4e86\u4e8c\u5341\u591a\u5e74\uff0c\u81ea\u7136\u662f\u8981\u9ad8\u51fa\u4e86\u4e00\u4e9b\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u84e6\u5730\u683c\u683c\u4e00\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u4fe1\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4e0d\u4fe1\u5c31\u8bd5\u8bd5\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u674f\u773c\u5706\u77aa\u9053\uff1a\u201c\u4f60\u8001\u8981\u627e\u6211\u6bd4\u5251\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u627e\u4f60\uff0c\u90a3\u662f\u4f60\u627e\u6b7b\u4e86\uff01\u201d \u3000\u3000\u76ee\u5149\u5ffd\u7136\u8f6c\u8eab\u5411\u65b9\u96ea\u5b9c\uff0c\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u5982\u679c\u4f60\u662f\u7528\u5251\uff0c\u8001\u592b\u4eca\u513f\u7834\u4f8b\u6307\u70b9\u4f60\u51e0\u624b\u5251\u6cd5\u5982\u4f55\uff1f\u201d \u3000\u3000\u8fd9\u8001\u4eba\u53e3\u6c14\u8d8a\u6765\u8d8a\u5927\u4e86\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u5251\u7709\u4e00\u626c\uff0c\u63a5\u9053\uff1a\u201c\u5c0a\u9a7e\u7684\u53e3\u6c14\uff0c\u707c\u707c\u903c\u4eba\uff0c\u96be\u9053\u4e5d\u5927\u95e8\u6d3e\u4e5f\u4e0d\u653e\u5728\u4f60\u773c\u4e2d\u5417\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4f60\u522b\u4ee5\u4e3a\u88ab\u4e5d\u5927\u95e8\u6d3e\u9009\u51fa\u6765\uff0c\u5c31\u81ea\u4ee5\u4e3a\u4e86\u4e0d\u8d77\u4e86\uff0c\u5176\u5b9e\u5728\u8001\u592b\u770b\u6765\uff0c\u4f60\u8fd8\u5dee\u5f97\u8fdc\u5462\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u53cc\u7709\u66b4\u626c\uff0c\u63a5\u9053\uff1a\u201c\u5c0a\u9a7e\u679c\u662f\u8981\u4e0e\u5728\u4e0b\u52a8\u624b\u7684\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u6307\u70b9\u4f60\u51e0\u62db\u5251\u6cd5\uff0c\u53eb\u4f60\u4ee5\u540e\u522b\u518d\u773c\u4e2d\u65e0\u4eba\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u518d\u597d\u7684\u6027\u5b50\u8fd9\u65f6\u4e5f\u65e0\u6cd5\u5fcd\u53d7\u4e86\uff01 \u3000\u3000\u4ed6\u970d\u7136\u7acb\u8d77\uff0c\u62d4\u51fa\u4e86\u80a9\u5934\u957f\u5251\u3002 \u3000\u3000\u5b8b\u5b5a\u5728\u65c1\u8fb9\u6697\u6697\u5730\u6447\u4e86\u6447\u5934\uff0c\u5374\u662f\u5e76\u672a\u51fa\u58f0\u963b\u6b62\u3002 \u3000\u3000\u795d\u516c\u660e\u4f9d\u7136\u5728\u88c5\u54d1\uff0c\u731b\u56bc\u9e7f\u8089\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u773c\u89c1\u8fd9\u4e24\u4f4d\u8001\u4eba\u672a\u4f5c\u4efb\u4f55\u8868\u793a\uff0c\u5f53\u4e0b\u4e5f\u5c31\u7b11\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u6700\u597d\u51fa\u624b\u5feb\u4e00\u4e9b\uff0c\u4e5f\u597d\u53eb\u5e74\u7eaa\u5927\u7684\u4eba\u660e\u767d\uff0c\u5e74\u8f7b\u4eba\u5e76\u4e0d\u662f\u4ed6\u6240\u60f3\u7684\u6ca1\u6709\u51fa\u606f\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e0d\u7b28\uff0c\u81ea\u7136\u542c\u5f97\u51fa\u6765\u5b89\u5c0f\u840d\u8a00\u5916\u4e4b\u610f\uff0c\u90a3\u662f\u8981\u4ed6\u4e00\u4e0a\u6765\u5c31\u4e0d\u59a8\u5c3d\u51fa\u7edd\u62db\uff0c\u4e5f\u597d\u6740\u6740\u8f9b\u67cf\u516c\u7684\u9a84\u6c14\u3002 \u3000\u3000\u4ed6\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8d24\u5f1f\u653e\u5fc3\uff0c\u8fd9\u70b9\u660e\u767d\uff01\u201d \u3000\u3000\u957f\u5251\u4e00\u987f\uff0c\u5411\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u54b1\u4eec\u8981\u4e0d\u8981\u51fa\u6d1e\u3002\u201d \u3000\u3000\u8f9b\u67cf\u516c\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u7528\u4e86\uff01\u201d \u3000\u3000\u56de\u624b\u5728\u90a3\u957f\u886b\u4e4b\u4e0b\uff0c\u638f\u4e86\u534a\u5929\uff0c\u624d\u6293\u51fa\u4e00\u53ea\u77ed\u5251\u3002 \u3000\u3000\u8fd9\u5251\u957f\u5ea6\u53ea\u5f97\u4e00\u5c3a\u516d\u4e03\uff0c\u6bd4\u4e00\u822c\u4eba\u7684\u5b9d\u5251\uff0c\u8981\u77ed\u4e86\u4e00\u5c3a\uff0c\u4f46\u9000\u53bb\u5251\u9798\u4ee5\u540e\uff0c\u5374\u6676\u5149\u95ea\u95ea\uff0c\u5bd2\u6c14\u4fb5\u4eba\uff01 \u3000\u3000\u5b89\u5c0f\u840d\u770b\u5f97\u5927\u58f0\u53eb\u9053\uff1a\u201c\u597d\u5251\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u751a\u662f\u5f97\u610f\u5730\u7b11\u9053\uff1a\u201c\u5c0f\u4e2b\u5934\uff0c\u4f60\u770b\u5f97\u51fa\u8fd9\u5251\u4e0d\u9519\uff0c\u8db3\u89c1\u4f60\u8fd8\u6709\u51e0\u5206\u773c\u529b\uff0c\u53ea\u662f\uff0c\u4f60\u53ef\u77e5\u9053\u8fd9\u652f\u77ed\u5251\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u867d\u7136\u77a7\u5f97\u51fa\u8fd9\u652f\u77ed\u5251\u662f\u53e4\u7269\uff0c\u4f46\u8981\u53eb\u51fa\u5251\u540d\uff0c\u53ef\u5c31\u628a\u5979\u7ed9\u95ee\u4f4f\u4e86\uff01 \u3000\u3000\u5979\u6014\u4e86\u4e00\u6014\uff0c\u6b63\u8981\u6447\u5934\u8bf4\u51fa\u81ea\u5df1\u4e0d\u77e5\uff0c\u53ea\u542c\u90a3\u5b8b\u5b5a\u7528\u4f20\u97f3\u4e4b\u672f\u9053\uff1a\u201c\u5b89\u59d1\u5a18\uff0c\u8fd9\u662f\u8d8a\u56fd\u4e94\u5251\u4e4b\u4e2d\u7684\u7b2c\u56db\u53e3\uff0c\u540d\u5524\u9c7c\u80a0\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u4f55\u7b49\u4e56\u5de7\uff0c\u4e00\u65e6\u542c\u5f97\u5b8b\u5b5a\u4f20\u97f3\uff0c\u7acb\u5373\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u65b9\u9053\uff1a\u201c\u8f9b\u8001\uff0c\u770b\u6b64\u5251\u7684\u957f\u77ed\uff0c\u5927\u6982\u662f\u9c7c\u80a0\u5251\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u5446\u4e86\u4e00\u5446\uff0c\u671b\u7740\u5b89\u5c0f\u840d\u4e00\u4f1a\u513f\uff0c\u5ffd\u7136\u5927\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\uff0c\u4f60\u8fd9\u5c0f\u4e2b\u5934\u7684\u89c1\u8bc6\uff0c\u5012\u771f\u7684\u662f\u5e7f\u5f97\u5f88\u2026\u2026\u201d \u3000\u3000\u4ed6\u76ee\u5149\u4e00\u8f6c\uff0c\u53c8\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u9c7c\u80a0\u4e43\u662f\u6625\u79cb\u53e4\u7269\uff0c\u5c0f\u5b50\uff0c\u4f60\u90a3\u652f\u5251\u5982\u662f\u51e1\u94c1\uff0c\u8001\u592b\u5c31\u4e0d\u7528\u5b83\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u9053\uff1a\u201c\u4eba\u867d\u72c2\uff0c\u4f46\u5374\u4e5f\u72c2\u5f97\u751a\u6709\u4e49\u6c14\uff0c\u4ed6\u4e0d\u80af\u7528\u795e\u7269\u5229\u5668\u5bf9\u6211\u957f\u5251\u8db3\u89c1\u6b64\u4eba\u5fc3\u6027\u5e76\u4e0d\u662f\u771f\u7684\u5f88\u574f\uff01\u201d \u3000\u3000\u5fc3\u4e2d\u98de\u5feb\u5730\u8f6c\u5ff5\uff0c\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u4e0d\u59a8\u4e8b\uff0c\u5728\u4e0b\u8fd9\u652f\u957f\u5251\uff0c\u4e5f\u662f\u767e\u70bc\u7cbe\u94a2\uff0c\u9c7c\u80a0\u5251\u867d\u5229\uff0c\u4e0d\u4e00\u5b9a\u4f24\u5f97\u4e86\u5b83\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u95fb\u8a00\uff0c\u4e0d\u81ea\u7981\u5730\u770b\u4e86\u65b9\u96ea\u5b9c\u624b\u4e2d\u5b9d\u5251\u4e00\u773c\uff0c\u5c31\u7b97\u4ed6\u773c\u529b\u751a\u597d\uff0c\u4f46\u4e5f\u77a7\u4e0d\u51fa\u65b9\u96ea\u5b9c\u624b\u4e2d\u7684\u5b9d\u5251\uff0c\u6709\u4f55\u795e\u5947\uff0c\u4e0d\u6015\u9c7c\u80a0\u5251\u7684\u950b\u5229\uff01 \u3000\u3000\u4f46\u4ed6\u5374\u4e0d\u518d\u591a\u8bf4\uff0c\u53f3\u624b\u5e73\u63e1\u77ed\u5251\uff0c\u7b11\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u4f60\u5148\u51fa\u624b\u5427\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u9053\uff1a\u201c\u4ed6\u81ea\u8be9\u5251\u6cd5\u7b2c\u4e00\uff0c\u5f53\u7136\u4e0d\u4f1a\u62a2\u5148\u7684\u4e86\u2026\u2026\u201d \u3000\u3000\u5f53\u4e0b\u4e00\u5f15\u5251\u8bc0\uff0c\u5530\u7684\u4e00\u5251\uff0c\u5e73\u523a\u800c\u51fa\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u76f4\u523a\u800c\u6765\uff0c\u5176\u4e2d\u9690\u542b\u5341\u79cd\u53d8\u5316\u3002 \u3000\u3000\u8f9b\u67cf\u516c\u6697\u6697\u4e00\u60ca\uff0c\u8131\u53e3\u53eb\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u5c0f\u5b50\u5728\u5251\u6cd5\u4e0a\uff0c\u679c\u7136\u6709\u70b9\u51fa\u606f\uff01\u201d\u77ed\u5251\u4e00\u6325\uff0c\u7531\u4e0b\u800c\u4e0a\uff0c\u6572\u5411\u65b9\u96ea\u5b9c\u8155\u8109\u3002 \u3000\u3000\u8fd9\u4e00\u624b\u5012\u662f\u653b\u5f97\u6070\u5230\u597d\u5904\uff0c\u65e0\u8bba\u65b9\u96ea\u5b9c\u5251\u62db\u6709\u591a\u5c11\u53d8\u5316\uff0c\u624b\u8155\u88ab\u5236\uff0c\u5219\u4e00\u5207\u82b1\u62db\uff0c\u90fd\u52bf\u5c06\u843d\u7a7a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5403\u4e00\u60ca\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u8001\u513f\u7684\u5251\u6cd5\uff0c\u679c\u7136\u4e0d\u5dee\uff0c\u51fa\u624b\u62db\u5f0f\u5012\u662f\u7280\u5229\u5f97\u5f88\uff01\u201d \u3000\u3000\u5fc3\u4e2d\u5728\u60f3\uff0c\u624b\u4e2d\u957f\u5251\u5374\u662f\u5f80\u4e0b\u4e00\u6c89\uff0c\u5251\u950b\u500f\u5730\u4e00\u8f6c\uff0c\u7ed5\u5411\u8f9b\u67cf\u516c\u7684\u53f3\u8098\u524a\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u5f0f\u53d8\u62db\u6781\u5feb\uff0c\u5feb\u5f97\u4f7f\u8f9b\u67cf\u516c\u5927\u53eb\u4e00\u58f0\uff0c\u64a4\u5251\u9000\u4e86\u4e00\u6b65\uff01 \u3000\u3000\u663e\u7136\uff0c\u8f9b\u67cf\u516c\u4f4e\u4f30\u4e86\u65b9\u96ea\u5b9c\u7684\u6b66\u529f\uff0c\u65b9\u96ea\u5b9c\u53d8\u62db\u53c8\u5feb\uff0c\u8feb\u5f97\u4ed6\u53ea\u6709\u6487\u5251\u540e\u9000\u3002 \u3000\u3000\u9ad8\u624b\u8f83\u5251\uff0c\u50cf\u8fd9\u7b49\u60c5\u51b5\u51fa\u73b0\uff0c\u8f9b\u67cf\u516c\u6309\u7406\u5c31\u5e94\u7f62\u624b\u8ba4\u8f93\u624d\u5bf9\u3002 \u3000\u3000\u4f46\u8f9b\u67cf\u516c\u53cc\u76ee\u5bd2\u5149\u4e4d\u5c04\uff0c\u5927\u58f0\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u7528\u7684\u53ef\u662f\u9648\u5e0c\u6b63\u7684\u5251\u6cd5\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5c0a\u9a7e\u4e0d\u662f\u672a\u66fe\u548c\u5251\u795e\u52a8\u8fc7\u624b\u5417\uff1f\u600e\u5730\u53c8\u8ba4\u7684\u51fa\u5251\u795e\u7684\u5251\u6cd5\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u6012\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5929\u4e0b\u5404\u79cd\u5251\u6cd5\uff0c\u8001\u592b\u90fd\u66fe\u6d89\u730e\uff0c\u533a\u533a\u9f99\u884c\u516b\u5251\uff0c\u8001\u592b\u81ea\u7136\u8ba4\u5f97\u51fa\u6765\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u9053\uff1a\u201c\u4ed6\u53e3\u4e2d\u8bf4\u7684\u53ea\u662f\u9f99\u884c\u516b\u5251\uff0c\u60f3\u5fc5\u5e08\u7956\u52a0\u521b\u7684\u4e00\u62db\uff0c\u4ed6\u662f\u4e0d\u66fe\u6653\u5f97\u4e86\uff1f\u201d \u3000\u3000\u4e00\u5ff5\u53ca\u6b64\uff0c\u63a5\u53e3\u7b11\u9053\uff1a\u201c\u5c0a\u9a7e\u65e2\u662f\u8ba4\u5f97\u51fa\u9f99\u884c\u516b\u5251\uff0c\u60f3\u5fc5\u4e5f\u6709\u90a3\u7834\u89e3\u4e4b\u6cd5\u7684\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u5c0f\u5b50\u53ef\u8981\u8bd5\u8bd5\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u5c0a\u9a7e\u5982\u662f\u4e0d\u613f\u8ba4\u8f93\uff0c\u533a\u533a\u540c\u610f\u518d\u6253\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u95fb\u8a00\uff0c\u77e5\u9053\u4ed6\u8bdd\u4e2d\u542b\u6709\u8bbd\u523a\u4e4b\u610f\uff0c\u4f46\u4ed6\u5374\u6545\u610f\u88c5\u4f5c\u4e0d\u61c2\uff0c\u5475\u5475\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u5c0f\u5b50\uff0c\u4f60\u5982\u662f\u4e0d\u6015\u8001\u5927\u6298\u4e86\u4f60\u624b\u4e2d\u957f\u5251\uff0c\u518d\u6253\u5341\u62db\u516b\u62db\uff0c\u8001\u592b\u4e5f\u4e0d\u53cd\u5bf9\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u5341\u62db\u516b\u62db\u4e0d\u5fc5\uff0c\u54b1\u4eec\u518d\u8bd5\u4e00\u62db\u4fbf\u4e86\uff01\u201d \u3000\u3000\u8bdd\u97f3\u4e00\u843d\uff0c\u4e3e\u5251\u5e73\u5e73\u523a\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u8f9b\u67cf\u516c\u626c\u7709\u5927\u7b11\uff0c\u4e00\u6325\u77ed\u5251\uff0c\u76f4\u5411\u65b9\u96ea\u5b9c\u7684\u957f\u5251\u64a9\u6765\uff0c\u53e3\u4e2d\u5374\u8bf4\u9053\uff1a\u201c\u53e3\u6c14\u4e0d\u5c0f\u2026\u2026\u201d \u3000\u3000\u4ed6\u4e0b\u9762\u4f3c\u4e4e\u8fd8\u6709\u672a\u66fe\u8bf4\u5b8c\u7684\u8bdd\uff0c\u4f46\u56e0\u65b9\u96ea\u5b9c\u8fd9\u4e00\u62db\u5251\u6cd5\u7684\u53d8\u5316\uff0c\u4e0e\u9f99\u884c\u516b\u5251\u5927\u4e0d\u76f8\u540c\uff0c\u4f7f\u5f97\u4ed6\u5403\u60ca\u4e4b\u4e0b\uff0c\u621b\u7136\u4f4f\u53e3\uff01 \u3000\u3000\u77ed\u5251\u72c2\u6325\uff0c\u4e00\u53e3\u6c14\u8fde\u8fde\u53d8\u4e86\u4e03\u5f0f\u5251\u6cd5\u523a\u51fa\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u6ca1\u6709\u4e00\u5f0f\u5251\u6cd5\u80fd\u5c06\u65b9\u96ea\u5b9c\u7684\u5251\u62db\u5316\u89e3\u3002 \u3000\u3000\u53ea\u89c9\u773c\u524d\u6676\u5149\u4e4d\u95ea\uff0c\u53f3\u81c2\u4e00\u9ebb\uff0c\u65b9\u96ea\u5b9c\u7684\u5251\u5c16\uff0c\u5df2\u5728\u81ea\u5df1\u66f2\u6c60\u7a74\u4e0a\u70b9\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u8f9b\u67cf\u516c\u8138\u8272\u5927\u53d8\uff0c\u98de\u8eab\u5012\u9000\u4e86\u4e09\u5c3a\uff0c\u4eba\u5df2\u7acb\u5728\u6d1e\u53e3\uff01 \u3000\u3000\u4ed6\u8fc5\u5feb\u5730\u8fd0\u6c14\u8c03\u606f\uff0c\u53ea\u89c9\u90a3\u5de6\u81c2\u88ab\u70b9\u7684\u7a74\u9053\uff0c\u5e76\u65e0\u4e0d\u9002\u4e4b\u5904\uff0c\u8fd9\u624d\u5927\u5927\u5730\u677e\u4e86\u4e00\u53e3\u6c14\u3002 \u3000\u3000\u9c7c\u80a0\u5251\u4e00\u8f6c\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u4f60\u521a\u624d\u90a3\u4e00\u62db\uff0c\u4e0d\u662f\u9f99\u884c\u516b\u5251\u4e2d\u7684\u62db\u5f0f\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u8c01\u8bf4\u4e0d\u662f\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u8001\u592b\u8bf4\u4e0d\u662f\uff01\u201d\u5ffd\u7136\u4e00\u62ac\u6b65\uff0c\u6325\u5251\u76f4\u5411\u65b9\u96ea\u5b9c\u523a\u6765\uff0c\u53e3\u4e2d\u63a5\u9053\uff1a\u201c\u8001\u592b\u8981\u518d\u8bd5\u8bd5\u4f60\u521a\u624d\u7684\u90a3\u4e00\u62db\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u77e5\u9053\u4ed6\u4e0d\u66fe\u5c31\u6b64\u7f62\u624b\uff0c\u5f53\u4e0b\u7b11\u9053\uff1a\u201c\u5c0a\u9a7e\u4e0d\u670d\uff0c\u518d\u8bd5\u4e00\u6b21\u4e5f\u597d\uff01\u201d \u3000\u3000\u5b9d\u5251\u4e00\u626c\uff0c\u5e73\u5e73\u5730\u70b9\u51fa\u3002 \u3000\u3000\u4e24\u4eba\u867d\u7136\u53ea\u662f\u5404\u51fa\u4e00\u62db\uff0c\u4f46\u8fd9\u4e00\u62db\u4e4b\u4e2d\uff0c\u5374\u6709\u7740\u65e0\u5e38\u7684\u53d8\u4ec6\u3002 \u3000\u3000\u53ea\u89c1\u8f9b\u67cf\u516c\u77ed\u5251\u5feb\u5982\u7075\u86c7\u98de\u821e\uff0c\u5728\u65b9\u96ea\u5b9c\u90a3\u7ef5\u5bc6\u7684\u5251\u7f51\u4e4b\u4e2d\uff0c\u4e0d\u4f4f\u5730\u95ea\u52a8\u3002 \u3000\u3000\u4f46\u65b9\u96ea\u5b9c\u7684\u8fd9\u4e00\u62db\u5251\u6cd5\u4e4b\u5999\uff0c\u5374\u53c8\u4e0e\u8f9b\u67cf\u516c\u5927\u4e0d\u76f8\u540c\uff0c\u5bd2\u5149\u904d\u5e03\u77f3\u6d1e\u4e4b\u5185\uff0c\u7eb5\u4f7f\u90a3\u8f9b\u67cf\u516c\u5251\u52bf\u5feb\u903e\u98d8\u98ce\uff0c\u5374\u8131\u4e0d\u51fa\u65b9\u96ea\u5b9c\u5c42\u5c42\u5251\u5e55\u7b3c\u7f69\u3002 \u3000\u3000\u77ed\u5251\u4e00\u6536\uff0c\u75be\u5feb\u5730\u9000\u4e86\u4e09\u6b65\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u65f6\u4e5f\u624b\u8155\u4e00\u6c89\uff0c\u6536\u4f4f\u4e86\u5251\u52bf\uff0c\u7b11\u9053\uff1a\u201c\u8001\u524d\u8f88\u627f\u8ba9\u4e86\u3002\u201d \u3000\u3000\u545b\u5577\u4e00\u58f0\uff0c\u5bd2\u5149\u987f\u655b\uff0c\u957f\u5251\u5df2\u7136\u8fd8\u9798\uff01 \u3000\u3000\u8f9b\u67cf\u516c\u6536\u8d77\u77ed\u5251\uff0c\u62b1\u62f3\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u8fd9\u4e00\u62db\u5251\u6cd5\uff0c\u53ef\u5f53\u5f97\u6b66\u6797\u7b2c\u4e00\u62db\u4e4b\u8a89\uff0c\u4e0d\u77e5\u662f\u4ec0\u4e48\u5251\u6cd5\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u9f99\u884c\u4e5d\u5251\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u95fb\u8a00\uff0c\u5446\u4e86\u4e00\u5446\u9053\uff0c\u201c\u9f99\u884c\u4e5d\u5251\uff1f\u8fd8\u662f\u5251\u795e\u7684\u5251\u6cd5\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff01\u6b63\u662f\u5148\u5e08\u7684\u5251\u6cd5\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u5ffd\u7136\u4e00\u6014\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026\u5e08\u7236\u662f\u9648\u5e0c\u6b63\uff1f\u4ed6\u83ab\u975e\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5148\u5e08\u5728\u6570\u6708\u4e4b\u524d\u4ed9\u53bb\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u6447\u5934\u9053\uff1a\u201c\u5947\u602a\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u76b1\u7709\u9053\uff1a\u201c\u8001\u524d\u8f88\u4e0d\u4fe1\u5417\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u6b63\u662f\u6709\u4e9b\u513f\u4e0d\u4fe1\uff0c\u4ee4\u5e08\u5e74\u7eaa\uff0c\u6bd4\u8001\u592b\u5c11\u4e86\u4e8c\u5341\u5de6\u53f3\uff0c\u4ed6\u600e\u4f1a\u5148\u6b7b\u4e86\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8138\u8272\u4e00\u9eef\uff0c\u4f4e\u53f9\u9053\uff1a\u201c\u5bb6\u5e08\u4e4b\u6b7b\uff0c\u4e43\u662f\u56e0\u4e3a\u906d\u4eba\u6697\u7b97\u4e4b\u6545\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u4e24\u773c\u4e00\u7ffb\uff0c\u6447\u5934\u9053\uff1a\u201c\u90a3\u5c31\u66f4\u7a00\u5947\u4e86\uff01\u201d \u3000\u3000\u4ed6\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u522b\u4eba\u5982\u662f\u53d7\u4e86\u4ed6\u4eba\u6697\u7b97\uff0c\u8001\u592b\u4e5f\u8bb8\u4f1a\u76f8\u4fe1\uff0c\u5982\u8bf4\u8fde\u5251\u795e\u4e5f\u4f1a\u4e2d\u4eba\u6697\u7b97\uff0c\u8001\u592b\u5374\u662f\u4e0d\u4fe1\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5e7d\u5e7d\u5730\u53f9\u606f\u9053\uff1a\u201c\u8001\u524d\u8f88\u4e0d\u4fe1\uff0c\u4e5f\u662f\u6ca1\u6709\u529e\u6cd5\uff0c\u4f46\u5148\u5e08\u786e\u662f\u56e0\u6b64\u800c\u6b7b\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u8f9b\u67cf\u516c\u5927\u58f0\u9053\uff1a\u201c\u4e0d\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u8fc7\u5148\u5e08\u53d7\u4eba\u6697\u7b97\u4e4b\u4e8b\u5df2\u7136\u6709\u5341\u591a\u5e74\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u8001\u592b\u4f3c\u4e4e\u672a\u66fe\u542c\u4eba\u8bf4\u8fc7\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5148\u5e08\u4e3a\u4e86\u4e0d\u6562\u8ba9\u4e94\u9b54\u77e5\u6653\uff0c\u6240\u4ee5\uff0c\u4e00\u76f4\u6781\u529b\u9690\u7792\u6b64\u4e8b\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u4f46\u4e0d\u77e5\u5251\u795e\u662f\u53d7\u4e86\u4ec0\u4e48\u6697\u7b97\uff1f\u4f60\u5c0f\u5b50\u67e5\u51fa\u6765\u4e86\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u6ca1\u6709\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u6c89\u58f0\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u8fd9\u5c31\u662f\u4f60\u7684\u4e0d\u5bf9\u4e86\uff01\u4e3a\u4eba\u5b50\u5f1f\uff0c\u600e\u53ef\u8fde\u5e08\u7236\u7684\u4ec7\u5bb6\u90fd\u4e0d\u67e5\u660e\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u62b1\u62f3\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u4e0d\u662f\u665a\u8f88\u4e0d\u67e5\uff0c\u800c\u662f\u2026\u2026\u800c\u662f\u2026\u2026\u8fd9\u4e8b\u5df2\u7531\u5e08\u53d4\u4e00\u53e3\u5e94\u627f\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u6123\u4e86\u534a\u664c\uff0c\u9053\uff1a\u201c\u4f60\u8fd8\u6709\u4e00\u4e2a\u5e08\u53d4\uff1f\u5251\u795e\u51e0\u65f6\u6709\u4e86\u540c\u95e8\u5144\u5f1f\uff0c\u4ed6\u662f\u54ea\u4e00\u95e8\u6b66\u6797\u9ad8\u4eba\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6c89\u541f\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u665a\u8f88\u6682\u65f6\u5374\u4e0d\u4fbf\u8bf4\u5c06\u51fa\u6765\uff0c\u5c1a\u671b\u524d\u8f88\u539f\u8c05\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u5012\u4e5f\u672a\u66fe\u8ffd\u95ee\uff0c\u7b11\u4e86\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u521a\u624d\u70b9\u4e86\u8001\u592b\u66f2\u6c60\u7a74\u7684\u90a3\u4e00\u62db\u5251\u6cd5\uff0c\u5f53\u771f\u662f\u9f99\u884c\u5251\u6cd5\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u665a\u8f88\u8bf4\u7684\u4e43\u662f\u5b9e\u8bdd\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u95fb\u8a00\uff0c\u4f4e\u5934\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u53c8\u4ef0\u9762\u60f3\u4e86\u60f3\uff0c\u8fd9\u624d\u6ce8\u76ee\u95ee\u9053\uff1a\u201c\u53ef\u662f\u4f60\u5e08\u7236\u6559\u4f60\u7684\uff1f\u201d \u3000\u3000\u4ed6\u8fd9\u4e00\u95ee\u53ea\u628a\u5b89\u5c0f\u840d\u95ee\u5f97\u7b11\u4e86\uff01 \u3000\u3000\u8f9b\u67cf\u516c\u76fe\u4e00\u76b1\uff0c\u6012\u9053\uff1a\u201c\u4f60\u7b11\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u4f9d\u7136\u7b11\u7740\u63a5\u9053\uff1a\u201c\u7b11\u4f60\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u7b11\u7740\u8001\u592b\u4f55\u6765\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u65b9\u5927\u54e5\u65e2\u662f\u5251\u795e\u7684\u5f1f\u5b50\uff0c\u4ed6\u7684\u5251\u6cd5\uff0c\u81ea\u7136\u662f\u5251\u795e\u6240\u6559\u7684\u4e86\uff01\u4f60\u8fd9\u4e48\u95ee\uff0c\u5c82\u4e0d\u53ef\u7b11\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u51b7\u54fc\u9053\uff1a\u201c\u4e2b\u5934\uff0c\u8001\u592b\u95ee\u7684\u81ea\u6709\u9053\u7406\uff0c\u4e0d\u4fe1\u4f60\u4f55\u4e0d\u542c\u542c\u65b9\u8001\u5f1f\u600e\u751f\u56de\u7b54\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u665a\u8f88\u6b63\u8981\u542c\u542c\u65b9\u5927\u54e5\u600e\u4e48\u8bf4\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u65f6\u5374\u62b1\u6b49\u5730\u5411\u5b89\u5c0f\u840d\u4e00\u7b11\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u8fd9\u4e00\u62db\u5251\u6cd5\uff0c\u679c\u7136\u4e0d\u662f\u6211\u5e08\u7236\u6559\u7684\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u771f\u7684\u4e0d\u662f\u5251\u795e\u6240\u6388\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u662f\uff01\u201d \u3000\u3000\u4ed6\u8bed\u97f3\u5fae\u5fae\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u8fd9\u4e00\u62db\u5251\u6cd5\uff0c\u4e43\u662f\u6211\u90a3\u5e08\u53d4\u6240\u4f20\u6388\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u8138\u8272\u4e00\u53d8\uff0c\u5927\u58f0\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u8fd9\u62db\u5251\u6cd5\u662f\u4f60\u5e08\u53d4\u4f20\u6388\u7684\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u6b63\u662f\u5728\u4e0b\u5e08\u53d4\u6240\u6388\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4e5f\u662f\u9f99\u884c\u5251\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u540c\u4e3a\u5e08\u7956\u5f1f\u5b50\uff0c\u81ea\u7136\u4e5f\u662f\u9f99\u884c\u5251\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u6447\u5934\u9053\uff1a\u201c\u5947\u602a\u554a\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u4e24\u773c\u4e00\u7ffb\uff0c\u9053\uff1a\u201c\u5947\u602a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u7b11\u9053\uff1a\u201c\u5251\u795e\u7684\u5251\u6cd5\uff0c\u7adf\u7136\u4e0d\u5982\u4ed6\u7684\u5e08\u5f1f\uff0c\u672a\u514d\u53eb\u4eba\u610f\u5916\u5f97\u5f88\uff01\u201d \u3000 \u7b2c\u4e09\u5341\u56de\u3000\u610f\u5916\u60ca\u559c \u3000 \u3000\u3000\u65b9\u96ea\u5b9c\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u8001\u4e0d\u7528\u5947\u602a\uff0c\u8fd9\u4e00\u62db\u5251\u6cd5\uff0c\u4e43\u662f\u5728\u4e0b\u5e08\u7956\u665a\u5e74\u6240\u7814\u521b\uff0c\u5148\u5e08\u4e0d\u77e5\uff0c\u90a3\u4e5f\u4e0d\u8db3\u4e3a\u5947\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u6293\u4e86\u6293\u5934\uff0c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u7167\u4f60\u773c\u4e0b\u7684\u6b66\u529f\u770b\u6765\uff0c\u5df2\u7136\u662f\u9752\u51fa\u4e8e\u84dd\u800c\u7528\u80dc\u84dd\u7684\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\u4e00\u9707\uff0c\u5fd9\u9053\uff1a\u201c\u5728\u4e0b\u624d\u667a\u6709\u9650\uff0c\u600e\u6562\u548c\u5148\u5e08\u76f8\u63d0\u5e76\u8bba\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u8fd9\u65f6\u76ee\u5149\u5728\u5b8b\u5b5a\u8eab\u4e0a\u4e00\u8f6c\uff0c\u7b11\u9053\uff1a\u201c\u8f9b\u8001\u516c\u516c\uff0c\u54d1\u5df4\u524d\u8f88\u8981\u4f60\u505a\u7684\u4e8b\uff0c\u4f60\u5e94\u5141\u4e0d\u5e94\u5141\u554a\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u53cc\u7709\u4e00\u76b1\uff0c\u770b\u4e86\u90a3\u795d\u516c\u660e\u4e00\u773c\uff0c\u9053\uff1a\u201c\u9ec4\u5144\u4e8e\u6211\u6709\u5927\u6069\uff0c\u8001\u592b\u5982\u662f\u4e0d\u5141\uff0c\u90a3\u5c82\u4e0d\u662f\u6210\u4e86\u5fd8\u6069\u8d1f\u4e49\u7684\u5c0f\u4eba\u4e86\u5417\uff1f\u201d \u3000\u3000\u957f\u957f\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u63a5\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u5982\u662f\u4f60\u61c2\u5f97\u54d1\u8bed\uff0c\u8bf7\u59d1\u5a18\u544a\u8bc9\u9ec4\u5144\uff0c\u8001\u673d\u5e94\u5141\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5fcd\u4e0d\u4f4f\u62cd\u624b\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u4e0d\u7528\u4f60\u8001\u803d\u5fc3\u4e86\u2026\u2026\u201d \u3000\u3000\u8bdd\u97f3\u4e00\u843d\uff0c\u53ea\u89c1\u5979\u5411\u7740\u795d\u516c\u660e\u6307\u624b\u5212\u811a\uff0c\u53fd\u54e9\u5495\u5495\u505a\u4e86\u534a\u5929\u624b\u52bf\uff0c\u597d\u4f3c\u5728\u5411\u54d1\u4e10\u8bf4\u660e\u4ec0\u4e48\uff1f \u3000\u3000\u5176\u5b9e\uff0c\u4e3b\u96ea\u5b9c\u7b49\u4eba\u5fc3\u4e2d\u660e\u767d\uff0c\u5979\u8fd9\u7b49\u624b\u52bf\uff0c\u4f11\u8bf4\u795d\u516c\u660e\u4e0d\u61c2\uff0c\u53ea\u6015\u4e3e\u4e16\u7684\u54d1\u5df4\u4e5f\u65e0\u4eba\u77e5\u6653\u5979\u8bb2\u7684\u662f\u4ec0\u4e48\uff01 \u3000\u3000\u4f46\u795d\u516c\u660e\u5374\u88c5\u4f5c\u4f1a\u610f\u4e4b\u72b6\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u54bf\u54bf\u5514\u5514\u5730\u54fc\u4e86\u51e0\u58f0\uff01 \u3000\u3000\u5b89\u5c0f\u840d\u683c\u683c\u5730\u7b11\u4e86\u4e00\u9635\uff0c\u5411\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u8f9b\u8001\uff0c\u4f60\u6253\u7b97\u51e0\u65f6\u5c65\u884c\u5141\u8bfa\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u8001\u673d\u5c1a\u6709\u4e00\u70b9\u4e8b\u513f\uff0c\u8981\u4e09\u4e94\u5929\u65b9\u80fd\u529e\u597d\uff0c\u81f3\u8fdf\u4e94\u5929\uff0c\u8001\u673d\u518d\u6765\u6b64\u5982\u4f55\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u770b\u4e86\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u9053\uff1a\u201c\u65b9\u5927\u54e5\uff0c\u6765\u5f97\u53ca\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u9053\uff1a\u591a\u4e0a\u4e00\u4e2a\u5e2e\u624b\uff0c\u90a3\u4e5f\u6ca1\u6709\u4ec0\u4e48\u4e0d\u53ef\u2026\u2026\u5f53\u4e0b\u5fae\u5fae\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u6765\u5f97\u53ca\uff01\u5c31\u8bf7\u8f9b\u8001\u4e94\u65e5\u4e4b\u540e\uff0c\u53bb\u81f3\u5c11\u6797\u5bfa\u4e2d\u76f8\u805a\u4fbf\u4e86\uff01\u201d\u8f9b\u67cf\u516c\u626c\u4e86\u626c\u7709\uff0c\u5411\u6d1e\u4e2d\u4f17\u4eba\u7565\u4e00\u62b1\u62f3\uff0c\u9053\uff1a\u201c\u8001\u673d\u544a\u8f9e\u4e86\uff01\u4e94\u65e5\u4e4b\u540e\uff0c\u8001\u673d\u5b9a\u5f53\u8d76\u6765\u5c11\u6797\u2026\u2026\u201d \u3000\u3000\u84e6\u5730\u8f6c\u8eab\uff0c\u51fa\u6d1e\u800c\u53bb\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u53eb\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8f9b\u8001\u6162\u8d70\uff0c\u6055\u665a\u8f88\u4e0d\u9001\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u5df2\u51fa\u4e86\u77f3\u6d1e\uff0c\u4f46\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u59d1\u5a18\u4e0d\u7528\u5ba2\u6c14\uff0c\u4e94\u65e5\u4e4b\u540e\uff0c\u8001\u673d\u5b9a\u4f1a\u8d77\u6765\uff01\u201d \u3000\u3000\u4f59\u97f3\u6e10\u6c93\uff0c\u663e\u7136\u4ed6\u5df2\u4e0b\u5c71\u800c\u53bb\u3002 \u3000\u3000\u618b\u4e86\u534a\u5929\u4e0d\u80fd\u8bf4\u8bdd\u7684\u795d\u516c\u660e\uff0c\u8fd9\u65f6\u5927\u5927\u5730\u5410\u4e86\u4e00\u53e3\u6c14\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u8001\u513f\u518d\u4e0d\u8d70\uff0c\u53ef\u8981\u628a\u8001\u592b\u7ed9\u95f7\u6b7b\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u95f7\u6b7b\u4e8b\u5c0f\uff0c\u8001\u5077\u513f\u53ef\u60f3\u4e0d\u51fa\u65e5\u540e\u4f60\u4eec\u518d\u8981\u78b0\u4e0a\uff0c\u53c8\u80fd\u62ff\u4ec0\u4e48\u8bdd\u5706\u5176\u8bf4\u554a\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u4e00\u6014\u9053\uff1a\u201c\u65e5\u540e\u8fd8\u8981\u78b0\u4e0a\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u5f53\u7136\u90a3\u8001\u602a\u7269\u4e0d\u662f\u8fd8\u8981\u6765\u5417\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u7b11\u9053\uff1a\u201c\u4ed6\u6765\u4ed6\u7684\uff0c\u8001\u592b\u4e0d\u8ddf\u4ed6\u7167\u9762\uff0c\u90a3\u5c31\u4e0d\u7ed3\u4e86\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u600e\u4e48\u53ef\u80fd\uff1f\u4f60\u4e0d\u662f\u4e5f\u8981\u4e0b\u5c71\u5417\uff0c\u4f60\u83ab\u975e\u5fd8\u4e86\u7b54\u5e94\u8fc7\u5b89\u59d1\u5a18\u7684\u8bdd\u554a\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u95fb\u8a00\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8fd9\u2026\u2026\u8001\u592b\u5012\u662f\u5fd8\u8bb0\u4e86\uff01\u8001\u5077\u513f\uff0c\u4f60\u8bf4\u8fd9\u8981\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u6211\u600e\u4e48\u77e5\u9053\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u5927\u58f0\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u9053\uff0c\u8c01\u77e5\u9053\uff1f\u8fd9\u9ebb\u70e6\u627e\u6765\uff0c\u53ef\u662f\u5927\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u81ea\u5df1\u6211\u7684\uff0c\u602a\u8c01\uff1f\u4e0d\u8fc7\u8001\u592b\u5012\u662f\u6709\u4e00\u4e2a\u8bf4\u6cd5\uff0c\u53ea\u4e0d\u77e5\u4f60\u8001\u513f\u613f\u4e0d\u613f\u610f\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u4ec0\u4e48\u529e\u6cd5\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u6539\u5934\u6362\u9762\uff0c\u83ab\u518d\u8fd9\u7b49\u88c5\u675f\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u4f60\u8981\u8001\u592b\u600e\u5931\u6253\u626e\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u7a7f\u5f97\u5e72\u51c0\u4e00\u4e9b\uff0c\u53eb\u90a3\u8001\u602a\u7269\u8ba4\u4e0d\u51fa\u6765\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u7792\u5f97\u4e86\u90a3\u602a\u7269\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u5f53\u7136\u7792\u5f97\u4e86\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u65e0\u53ef\u5948\u4f55\u5730\u6447\u5934\u9053\uff1a\u201c\u53ea\u8981\u80fd\u7792\u5f97\u4e86\u90a3\u4e2a\u8001\u602a\u7269\uff0c\u8001\u592b\u53ea\u597d\u6539\u626e\u4e00\u756a\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u4f60\u8001\u513f\u65e9\u8be5\u7a7f\u5f97\u5e72\u51c0\u4e00\u4e9b\u4e86\uff0c\u4e5f\u514d\u5f97\u8ba9\u4eba\u5f53\u771f\u628a\u4f60\u5f53\u4f5c\u4e86\u4e10\u5e2e\u7684\u5b50\u5f1f\u554a\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u7b11\u9053\uff1a\u201c\u4e0d\u8981\u7d27\uff0c\u970d\u9e23\u98ce\u5982\u679c\u8ba4\u6211\u4f5c\u4ed6\u5e08\u53d4\uff0c\u4e5f\u65e0\u4e0d\u53ef\u3002\u201d \u3000\u3000\u7b11\u58f0\u4e00\u655b\uff0c\u63a5\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u90a3\u8001\u602a\u7269\u4e94\u5929\u4e4b\u540e\u5fc5\u6765\uff0c\u4f46\u8001\u592b\u5374\u6700\u5feb\u8981\u7b49\u4e5d\u5929\uff0c\u5230\u65f6\u5982\u662f\u5f7c\u6b64\u4e0d\u518d\u78b0\u9762\uff0c\u90a3\u53ef\u662f\u6700\u597d\u6ca1\u6709\uff0c\u4f46\u613f\u5b8b\u5144\u60f3\u4e2a\u6cd5\u5b50\uff0c\u8981\u65b9\u8001\u5f1f\u5c06\u8001\u602a\u7269\u652f\u9063\u79bb\u53bb\uff0c\u4f60\u770b\u5982\u4f55\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u8fd9\u53ef\u529e\u4e0d\u5230\u4e86\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u529e\u4e0d\u5230\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u542c\u5230\u5b89\u59d1\u5a18\u8bf4\u7684\u5417\uff1f\u90a3\u8001\u602a\u7269\u6b64\u540e\u53ef\u80fd\u5f97\u968f\u65f6\u5b88\u5728\u65b9\u8001\u5f1f\u7684\u8eab\u8fb9\u554a\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u76b1\u7709\u9053\uff0c\u201c\u975e\u89c1\u9762\u4e0d\u53ef\u4e86\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e0d\u4f46\u975e\u89c1\u9762\u4e0d\u53ef\uff0c\u800c\u4e14\uff0c\u8fd8\u5f97\u957f\u65e5\u76f8\u805a\u5462\uff01\u4f60\u8001\u513f\u6700\u597d\u662f\u8d76\u5feb\u6362\u6362\u8863\u670d\uff0c\u53bb\u628a\u5934\u53d1\u6574\u7406\u4e00\u4e0b\u5427\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u82e6\u7b11\u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8001\u5077\u513f\uff0c\u5982\u6b64\u8bf4\u6765\uff0c\u8001\u592b\u662f\u65e0\u6cd5\u4e0d\u4e0e\u90a3\u8001\u602a\u7269\u5e38\u5e38\u89c1\u9762\u7684\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e0d\u5e78\u6b63\u662f\u5982\u6b64\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u957f\u957f\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u597d\u5427\uff0c\u8001\u592b\u660e\u65e5\u4e0b\u5c71\u4e00\u884c\uff0c\u4e70\u51e0\u4ef6\u8863\u7269\u4fbf\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4f60\u8001\u513f\u5982\u662f\u4e0d\u4fbf\u4e0b\u5c71\uff0c\u8001\u592b\u4ee3\u4f60\u8dd1\u4e00\u8d9f\u767b\u5c01\uff0c\u4e5f\u65e0\u4e0d\u53ef\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u4e0d\u5fc5\u4e86\uff01\u8001\u592b\u8fd8\u8981\u6d17\u5934\u5462\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u65f6\u7ad9\u8d77\u8eab\u5230\u6d1e\u53e3\uff0c\u770b\u4e86\u770b\u5929\u8272\uff0c\u5927\u58f0\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u54b1\u4eec\u8be5\u56de\u5bfa\u53bb\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u968f\u5728\u4ed6\u8eab\u540e\uff0c\u63a2\u5934\u5411\u6d1e\u5916\u6253\u91cf\u4e86\u4e00\u773c\uff0c\u7b11\u9053\uff1a\u201c\u662f\u554a\uff0c\u5929\u90fd\u5feb\u9ed1\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u4e00\u8dc3\u800c\u8d77\uff0c\u5927\u58f0\u9053\uff1a\u201c\u795d\u5144\uff0c\u8fd9\u9e7f\u812f\u591f\u4f60\u4eca\u665a\u5403\u7684\u4e86\uff0c\u54b1\u4eec\u660e\u665a\u518d\u6765\uff0c\u4f46\u613f\u4f60\u5df2\u6362\u4e86\u88c5\u675f\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u8001\u592b\u9664\u975e\u8ddf\u90a3\u8001\u602a\u7269\u6253\u67b6\uff0c\u5426\u5219\u8001\u592b\u5c82\u6709\u4e0d\u6362\u8863\u886b\u4e4b\u7406\u2026\u2026\u201d \u3000\u3000\u8bf4\u8bdd\u95f4\uff0c\u5b8b\u5b5a\u4e5f\u8d77\u8eab\u51fa\u4e86\u5c71\u6d1e\u3002 \u3000\u3000\u4e09\u4eba\u56de\u8f6c\u5c11\u6797\uff0c\u5df2\u662f\u9ec4\u660f\u8fc7\u540e\u3002 \u3000\u3000\u970d\u9e23\u98ce\u7b49\u4eba\uff0c\u4f3c\u662f\u6b63\u5728\u4e3a\u4e09\u4eba\u534a\u5929\u4e0d\u77e5\u53bb\u5411\u7740\u6025\uff0c\u773c\u89c1\u4e09\u4eba\u56de\u6765\uff0c\u8fd9\u624d\u5927\u5927\u5730\u677e\u4e86\u4e00\u53e3\u6c14\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u89c1\u5230\u4e86\u5165\u4e91\u638c\u95e8\u4eba\uff0c\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u665a\u8f88\u90a3\u90d1\u5927\u4f2f\uff0c\u8fd1\u65e5\u53ef\u6709\u6d88\u606f\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u6447\u5934\u9053\uff1a\u201c\u6ca1\u6709\u554a\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u5fae\u5fae\u4e00\u7b11\u63a5\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\uff0c\u8001\u8872\u65e9\u5df2\u6d3e\u4eba\u53bb\u4e86\uff01\u4e0d\u8fc7\uff0c\u5df2\u7136\u6709\u4e86\u8fd9\u4e48\u591a\u5929\uff0c\u8fd8\u4e0d\u89c1\u56de\u8f6c\uff0c\u8001\u8872\u53ea\u6015\u6bd4\u5c0f\u65bd\u4e3b\u8fd8\u8981\u803d\u5fc3\u5462\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u5f97\u4e00\u6014\uff0c\u9053\uff1a\u201c\u638c\u95e8\u4eba\u65e9\u5df2\u6d3e\u4eba\u53bb\u4e86\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u7b11\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\u4e3a\u4e86\u672c\u5bfa\u4e4b\u4e8b\uff0c\u8d39\u5fc3\u8d39\u529b\uff0c\u672c\u5bfa\u4e3a\u5c0f\u65bd\u4e3b\u7a0d\u6548\u5954\u8d70\u90a3\u4e5f\u5e94\u8be5\u7684\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5ffd\u7136\u89c9\u5f97\u5fc3\u4e2d\u5927\u4e3a\u60ed\u6127\uff0c\u6697\u9053\uff1a\u201c\u6211\u662f\u9519\u602a\u4e86\u4ed6\u4eec\u3002\u201d\u5f53\u4e0b\u8fde\u5fd9\u62b1\u62f3\u9053\uff1a \u3000\u3000\u201c\u6709\u52b3\u638c\u95e8\u4eba\u8d39\u5fc3\uff0c\u665a\u8f88\u53ea\u597d\u65e5\u540e\u518d\u56fe\u62a5\u7b54\u4e86\u2026\u2026\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\u597d\u8bf4\uff0c\u8001\u8872\u611f\u6fc0\u5c0f\u65bd\u4e3b\u7684\u53ef\u66f4\u591a\u5462\uff1f\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u53ea\u89c1\u90a3\u5165\u5723\u5927\u5e08\uff0c\u5306\u5306\u5fd9\u5fd9\u5730\u6253\u5916\u9762\u95ef\u4e86\u8fdb\u6765\uff0c\u4e00\u89c1\u65b9\u96ea\u5b9c\uff0c\u4fbf\u9053\uff1a \u3000\u3000\u201c\u5c0f\u65bd\u4e3b\uff0c\u90a3\u592a\u5ba4\u5cf0\u4e0a\u51fa\u73b0\u4e86\u4e8c\u4f4d\u8001\u4eba\uff0c\u4f60\u548c\u5b89\u65bd\u4e3b\u53ef\u66fe\u89c1\u5230\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e0d\u7981\u5728\u5fc3\u4e2d\u6697\u7b11\uff0c\u5fd6\u9053\uff1a\u5c11\u6797\u7684\u9ad8\u50e7\uff0c\u4f3c\u662f\u4e5f\u88ab\u4e94\u9b54\u5934\u5413\u7834\u4e86\u80c6\u4e86\u2026\u2026\uff0c\u4f46\u4ed6\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u89c1\u5230\u4e86\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u90a3\u4eba\u53ef\u662f\u6bd2\u5927\u592b\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u662f\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u76b1\u7709\u9053\uff1a\u201c\u5947\u602a\uff0c\u90a3\u6076\u5b54\u660e\u66fe\u8bf4\u5728\u90a3\u7ae5\u4e8e\u5947\u4e4b\u524d\uff0c\u8d77\u6765\u5c11\u6797\u5bfa\u7684\uff0c\u4ed6\u600e\u5730\u672a\u66fe\u5728\u5d69\u5c71\u5730\u5e26\u73b0\u8eab\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u4e5f\u8bb8\u90a3\u6bd2\u5927\u592b\u6545\u4f5c\u6b64\u8a00\uff0c\u60d1\u4eba\u542c\u95fb\u7684\u5427\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\uff0c\u90a3\u5cf0\u9876\u4e4b\u4e0a\u7684\u8001\u4eba\uff0c\u4e0d\u77e5\u5c0f\u65bd\u4e3b\u53ef\u77e5\u662f\u4f55\u4eba\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u90a3\u4f4d\u8001\u4eba\uff0c\u4e43\u662f\u2018\u5929\u53f0\u6a35\u53df\u2019\u795d\u516c\u660e\u524d\u8f88\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u4e00\u6014\uff0c\u9053\uff1a\u201c\u795d\u8001\u65bd\u4e3b\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u4e0d\u662f\u4ed6\u8fd8\u6709\u8c01\uff1f\u4e0d\u4fe1\u4f60\u53ef\u95ee\u95ee\u5b8b\u8001\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u795d\u8001\u602a\u9690\u8eab\u592a\u5ba4\u5cf0\uff0c\u5df2\u7136\u5f88\u4e45\uff0c\u600e\u7684\u5927\u5e08\u7684\u95e8\u4e0b\uff0c\u5c31\u6ca1\u6709\u4eba\u53d1\u73b0\u5462\uff1f\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u4ed6\u8f6c\u5934\u5411\u5165\u4e91\u638c\u95e8\u4eba\uff0c\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u5165\u4e91\u638c\u95e8\u4eba\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8001\u65bd\u4e3b\uff0c\u672c\u5bfa\u5f1f\u5b50\u867d\u7136\u8eab\u5728\u6b66\u6797\uff0c\u4f46\u7985\u89c4\u751a\u4e25\uff0c\u5c11\u5ba4\u4e4b\u5916\uff0c\u4ed6\u4eec\u672a\u5949\u8c15\u4ee4\uff0c\u4ece\u6765\u4e0d\u6562\u524d\u53bb\uff0c\u90a3\u795d\u8001\u65bd\u4e3b\u867d\u7136\u4f4f\u5728\u5c71\u4e0a\u751a\u4e45\uff0c\u4f46\u56e0\u672c\u5bfa\u5b50\u5f1f\u65e0\u4eba\u524d\u53bb\uff0c\u81ea\u662f\u4e0d\u77e5\u6653\u7684\u4e86\uff01\u201d \u3000\u3000\u663e\u7136\u4ed6\u8fd9\u662f\u85c9\u8bcd\u63a9\u9970\uff0c\u5c11\u6797\u5bfa\u6ca1\u843d\u8001\u5927\u4e4b\u6001\uff0c\u5c31\u5728\u8fd9\u51e0\u53e5\u8bdd\u4e2d\uff0c\u8868\u9732\u65e0\u9057\u3002 \u3000\u3000\u5b8b\u5b5a\u95fb\u8a00\uff0c\u53ea\u662f\u7b11\u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u795d\u8001\u4f4f\u5728\u5d69\u5c71\uff0c\u5bf9\u4e8e\u8d35\u5bfa\u5012\u4e5f\u6ca1\u6709\u4ec0\u4e48\u4e0d\u5229\uff0c\u5927\u5e08\u4e0d\u7528\u6302\u5728\u5fc3\u4e0a\u4e86\uff01\u201d \u3000\u3000\u8bed\u97f3\u4e00\u843d\uff0c\u4ed6\u4f3c\u662f\u5ffd\u7136\u60f3\u8d77\u4e86\u4ec0\u4e48\uff0c\u63a5\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u6709\u4e00\u4ef6\u4e8b\uff0c\u8001\u5077\u513f\u4e00\u76f4\u5fd8\u8bb0\u4e86\u5411\u4f60\u8bf7\u6559\uff0c\u90a3\u534e\u5c71\u548c\u6606\u4ed1\u7684\u4e24\u4f4d\u957f\u8001\u4ee5\u53ca\u4e0e\u8001\u5077\u513f\u540c\u6765\u7684\u2018\u98de\u9f99\u65e0\u5f71\u201d\u6731\u7693\uff0c\u600e\u7684\u8fd9\u6b64\u65e5\u5b50\u672a\u66fe\u89c1\u5230\uff1f\u201d \u3000\u3000\u7ecf\u4ed6\u4e00\u63d0\uff0c\u65b9\u96ea\u5b9c\u4e5f\u89c9\u51fa\u4e0d\u5bf9\uff0c\u90a3\u534e\u5c71\u201c\u9ec4\u886b\u53df\u201d\u8463\u5343\u91cc\uff0c\u6606\u4ed1\u201c\u5bd2\u6885\u5251\u5ba2\u201d\u8096\u98d2\u4e8c\u4eba\uff0c\u660e\u660e\u662f\u8bf4\u8981\u524d\u6765\u5c11\u6797\uff0c\u602a\u7684\u662f\u81ea\u5df1\u7b49\u62b5\u6b64\uff0c\u5374\u662f\u672a\u66fe\u89c1\u5230\u4ed6\u4eec\u3002 \u3000\u3000\u8fd8\u6709\u90a3\u6731\u7693\uff0c\u4e0d\u77e5\u600e\u5730\u4e5f\u672a\u89c1\u5230\uff01 \u3000\u3000\u5165\u4e91\u5927\u5e08\u53cc\u7709\u6df1\u9501\uff0c\u6c89\u541f\u4e86\u4e00\u4f1a\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u5df2\u7136\u79bb\u53bb\u4e86\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u4e00\u6014\u9053\uff1a\u201c\u8d70\u4e86\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e09\u4f4d\u65bd\u4e3b\u90fd\u5df2\u79bb\u53bb\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u5927\u4e3a\u4e0d\u89e3\uff0c\u76ee\u5149\u5728\u970d\u9e23\u98ce\u8eab\u4e0a\u4e00\u8f6c\uff0c\u9053\uff1a\u201c\u970d\u5144\uff0c\u8fd9\u4e8b\u53eb\u5144\u5f1f\u597d\u751f\u4e0d\u89e3\u2026\u2026\u201d \u3000\u3000\u970d\u9e23\u98ce\u7b11\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u4ed6\u4eec\u79bb\u53bb\uff0c\u90a3\u5012\u662f\u4e8b\u5b9e\uff0c\u53ea\u56e0\u5144\u5f1f\u5728\u5c11\u6797\u8fd8\u89c1\u5230\u4e86\u4ed6\u4eec\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4ed6\u4eec\u53bb\u4e86\u54ea\u91cc\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u56de\u5934\u770b\u4e86\u5165\u4e91\u638c\u95e8\u4eba\u4e00\u773c\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u8001\u673d\u4e0d\u4fbf\u8bf4\u5c06\u51fa\u6765\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u751a\u662f\u5947\u602a\uff0c\u63a5\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u554a\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u5176\u4e2d\u5173\u7cfb\u5230\u4e00\u6869\u6781\u5927\u7684\u673a\u5bc6\uff0c\u8001\u673d\u4e0d\u8bf4\uff0c\u5b9e\u6709\u4e0d\u5f97\u5df2\u7684\u539f\u56e0\u2026\u2026\u201d\u4ed6\u8bed\u97f3\u4e00\u987f\uff0c\u59be\u9053\uff1a\u201c\u4f46\u5982\u679c\u5165\u4e91\u5927\u5e08\u613f\u610f\u8bf4\u51fa\uff0c\u8001\u673d\u5012\u4e5f\u4e0d\u53cd\u5bf9\uff01\u201d \u3000\u3000\u8fd9\u4f4d\u4e10\u5e2e\u7684\u5e2e\u4e3b\uff0c\u5c45\u7136\u5982\u6b64\u4e0d\u613f\u62c5\u5f53\u8d23\u4efb\uff0c\u663e\u7136\u662f\u522b\u6709\u539f\u56e0\uff0c\u5426\u5219\uff0c\u5c82\u4e0d\u662f\u5bf9\u4ed6\u7684\u4e3a\u4eba\uff0c\u5927\u5927\u5730\u4e0d\u9002\u5408\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u4e86\u4e00\u5446\uff0c\u6697\u9053\uff1a\u542c\u4ed6\u8fd9\u7b49\u541e\u541e\u5410\u5410\uff0c\u5176\u4e2d\u5fc5\u662f\u6709\u4e86\u751a\u5927\u7684\u539f\u56e0\u4e86\u2026\u2026 \u3000\u3000\u5bfb\u601d\u4e4b\u95f4\uff0c\u53ea\u542c\u5f97\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u5b8b\u65bd\u4e3b\uff0c\u90a3\u6731\u65bd\u4e3b\u7b49\u4e09\u4f4d\uff0c\u76ee\u4e0b\u751a\u662f\u5b89\u5168\uff0c\u65bd\u4e3b\u4e0d\u7528\u653e\u5728\u5fc3\u4e0a\uff0c\u65f6\u673a\u4e00\u5230\uff0c\u8001\u8872\u81ea\u4f1a\u544a\u77e5\u4ed6\u4eec\u53bb\u5411\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7565\u4e00\u8fdf\u7591\uff0c\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u4ed6\u4eec\u53bb\u4e86\u4f55\u65b9\uff0c\u8001\u5077\u513f\u4e5f\u76f8\u4fe1\u4e0d\u4f1a\u6709\u4ec0\u4e48\u5371\u9669\uff0c\u5927\u5e08\u65e2\u662f\u4e0d\u4fbf\u8bf4\u51fa\uff0c\u90a3\u5c31\u7b97\u4e86\uff01\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u5927\u7b11\u4e86\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u4e0d\u65e5\u4e4b\u5185\uff0c\u8d35\u5bfa\u53ef\u80fd\u53c8\u6765\u4e00\u4f4d\u602a\u5ba2\uff0c\u4e0d\u77e5\u638c\u95e8\u4eba\u9ad8\u5174\u4e0d\u9ad8\u5174\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u88ab\u4ed6\u7b11\u5f97\u4e00\u6014\uff0c\u5408\u5341\u9053\uff1a\u201c\u4ec0\u4e48\u602a\u5ba2\uff1f\u53ef\u662f\u90a3\u4e94\u5927\u9b54\u4e3b\u53c8\u8bf7\u51fa\u4e86\u4ec0\u4e48\u7edd\u4ee3\u7684\u9ad8\u4eba\u51fa\u5c71\u6765\u4e86\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u6447\u6447\u5934\u9053\uff1a\u201c\u4e0d\u662f\uff01\u201d\u4ed6\u8bdd\u97f3\u5fae\u5fae\u5730\u987f\u4e86\u4e00\u987f\uff0c\u770b\u4e86\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u7b11\u9053\uff1a\u201c\u90a3\u4f4d\u602a\u5ba2\uff0c\u4e43\u662f\u4e3a\u4e86\u65b9\u8001\u5f1f\u800c\u6765\uff01\u201d \u3000\u3000\u5165\u4e91\u638c\u95e8\u8138\u4e0a\u795e\u60c5\u5927\u4e3a\u548c\u7f13\uff0c\u7b11\u9053\uff1a\u201c\u65e2\u662f\u4e3a\u4e86\u65b9\u65bd\u4e3b\u800c\u6765\u90a3\u4e0d\u4f1a\u662f\u5bf9\u5934\u7684\u4eba\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u53ea\u662f\u2026\u2026\u8fd9\u4e2a\u8001\u602a\u7269\u53ef\u662f\u522b\u4eba\u60f9\u4e0d\u8d77\u7684\u4eba\u7269\u554a\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u95fb\u8a00\uff0c\u76b1\u7709\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u4f60\u522b\u5356\u5173\u5b50\u597d\u4e0d\u597d\uff1f\u53e3\u53e3\u58f0\u58f0\u90a3\u4e2a\u8001\u602a\u7269\u4e0d\u597d\u60f9\uff0c\u4f46\u4e0d\u77e5\u4ed6\u7a76\u7adf\u662f\u8c01\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u4e86\u4e00\u7b11\u9053\uff1a\u201c\u970d\u5144\uff0c\u6b64\u4eba\u662f\u8c01\uff1f\u8001\u5077\u513f\u8bf4\u4e86\u51fa\u6765\uff0c\u53ea\u6015\u4f60\u8001\u54e5\u7684\u5fc3\u4e2d\u8981\u5927\u5403\u4e00\u60ca\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u5927\u7b11\u9053\uff1a\u201c\u600e\u4e48\u4f1a\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e00\u5b9a\u4f1a\uff01\u201d\u4ed6\u795e\u79d8\u5730\u7b11\u4e86\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u970d\u5144\uff0c\u4f60\u8fd8\u8bb0\u5f97\u4f60\u90a3\u4f4d\u5e08\u53d4\u9ec4\u5927\u4fa0\u5417\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u9ec4\u5e08\u53d4\u4e43\u662f\u5144\u5f1f\u957f\u8f88\uff0c\u7109\u6709\u8bb0\u4e0d\u5f97\u4e4b\u7406\uff1f\u4f46\u4ed6\u8001\u4eba\u5bb6\u4e1a\u5df2\u6492\u624b\u5c18\u4e16\u591a\u5e74\u4e86\u554a\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u9ec4\u5927\u4fa0\u4ed9\u53bb\uff0c\u8001\u5077\u513f\u77e5\u9053\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u82e6\u7b11\u9053\uff1a\u201c\u65e2\u662f\u5b8b\u5144\u77e5\u9053\uff0c\u5ffd\u7136\u63d0\u8d77\u5144\u5f1f\u9ec4\u5e08\u53d4\uff0c\u90a3\u53c8\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u970d\u5144\uff0c\u9ec4\u5927\u4fa0\u5728\u4e16\u4e4b\u65e5\uff0c\u53ef\u66fe\u5bf9\u4f60\u8bf4\u8fc7\uff0c\u4ed6\u5728\u90a3\u6cf0\u5c71\u5357\u5929\u95e8\uff0c\u6697\u4e2d\u76f8\u52a9\u8fc7\u4e00\u4f4d\u6b66\u6797\u602a\u4eba\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u95fb\u8a00\uff0c\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u8f9b\u2026\u2026\u67cf\u516c\u5417\uff1f\u201d \u3000\u3000\u6562\u60c5\uff0c\u90a3\u54d1\u4e10\u5bf9\u4ed6\u8bf4\u8fc7\u6b64\u4e8b\uff01 \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b63\u662f\u90a3\u6b66\u6797\u4e00\u602a\u8f9b\u8001\u513f\u8981\u6765\uff01\u201d \u3000\u3000\u7ed3\u679c\uff0c\u4e0d\u4f46\u970d\u9e23\u98ce\u7528\u4e0a\u53d8\u8272\uff0c\u8fde\u5165\u4e91\u5927\u5e08\uff0c\u4e5f\u9732\u51fa\u4e0d\u5b89\u7684\u795e\u6001\uff0c\u5408\u5341\u9053\uff1a\u201c\u8f9b\u8001\u65bd\u4e3b\u5927\u540d\uff0c\u8001\u8872\u4e45\u5df2\u8033\u95fb\uff0c\u636e\u8bf4\u4ed6\u4e3a\u4eba\u884c\u4e8b\uff0c\u602a\u8bde\u4e0d\u9738\uff0c\u662f\u975e\u5584\u5ffd\u4e4b\u5ff5\uff0c\u53ea\u5728\u4ed6\u81ea\u5df1\u559c\u6012\u4e4b\u4e2d\uff0c\u4ed6\u524d\u6765\u5c11\u6797\uff0c\u4e0d\u77e5\u627e\u5bfb\u65b9\u65bd\u4e3b\u53c8\u4e3a\u4e86\u4f55\u4e8b\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u638c\u95e8\u4eba\u53ef\u662f\u4e0d\u5927\u6b22\u8fce\u4ed6\u6765\u6b64\u5417\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u7eb5\u7136\u5fc3\u4e2d\u6709\u4e00\u5343\u4e2a\u4e0d\u6b22\u8fce\u4e4b\u610f\uff0c\u4f46\u788d\u5728\u65b9\u96ea\u5b9c\u7684\u9762\u4e0a\uff0c\u5374\u4e5f\u4e3a\u80fd\u8bf4\u51fa\uff0c\u53ea\u8baa\u8baa\u5730\u5408\u5341\u9053\uff1a\u201c\u8001\u8872\u4e0d\u6562\uff0c\u4e0d\u8fc7\u2026\u2026\u8fd9\u4f4d\u8001\u65bd\u4e3b\u559c\u6012\u65e0\u5e38\uff0c\u751a\u662f\u4e0d\u597d\u5e94\u4ed8\uff0c\u4ed6\u6765\u4e86\u4e4b\u540e\uff0c\u54b1\u4eec\u53ef\u5f97\u5c0f\u5fc3\u4e9b\u8bf4\u8bdd\u624d\u597d\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u638c\u95e8\u4eba\u5012\u662f\u4ed4\u7ec6\u5f97\u5f88\uff0c\u4e0d\u8fc7\uff0c\u636e\u8001\u592b\u6240\u77e5\uff0c\u90a3\u8f9b\u8001\u513f\u6b64\u756a\u524d\u6765\uff0c\u5012\u662f\u89c4\u77e9\u5f97\u5f88\u4e86\uff01\u201d \u3000\u3000\u4ed6\u770b\u65b9\u96ea\u5b9c\u4e00\u773c\u63a5\u9053\uff1a\u201c\u65b9\u8001\u5f1f\u5e74\u7eaa\u867d\u7136\u4e0d\u5927\uff0c\u90a3\u8f9b\u8001\u602a\u5bf9\u4ed6\u5374\u662f\u5341\u5206\u5c0a\u656c\uff0c\u53ea\u8981\u6709\u65b9\u8001\u5f1f\u5728\u6b64\uff0c\u8c05\u90a3\u8f9b\u67cf\u516c\u4e5f\u4e0d\u6562\u505a\u51fa\u4ec0\u4e48\u4efb\u610f\u80e1\u4e3a\u7684\u4e8b\u6765\u3002\u201d \u3000\u3000\u970d\u9e23\u98ce\u6014\u6014\u5730\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u4f60\u8bf4\u4e86\u534a\u5929\uff0c\u5230\u5e95\u8fd9\u662f\u600e\u4e48\u4e00\u56de\u4e8b\u5462\uff1f\u4f20\u95fb\u4e4b\u4e2d\uff0c\u8f9b\u67cf\u516c\u65e9\u5df2\u6b7b\u53bb\uff0c\u4eca\u65e5\u542c\u4f60\u8bf4\u6765\uff0c\u4ed6\u4e0d\u4f46\u6ca1\u6709\u6b7b\uff0c\u800c\u4e14\u2026\u2026\u4f3c\u662f\u8fd8\u6d3b\u5f97\u5f88\u5065\u58ee\u554a\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u4ed6\u672c\u6765\u6d3b\u5f97\u5f88\u5065\u58ee\u561b\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u5230\u5e95\u90a3\u8f9b\u8001\u602a\u6765\u6b64\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6562\u60c5\u4ed6\u5bf9\u8f9b\u67cf\u516c\u751a\u662f\u5fcc\u8bb3\uff0c\u662f\u4ee5\u4e00\u518d\u8ffd\u95ee\u539f\u56e0\u3002 \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u970d\u5144\uff0c\u4f60\u8fd9\u7b49\u558b\u558b\u4e0d\u4f11\u5730\u8ffd\u95ee\uff0c\u53ef\u662f\u6614\u65e5\u4f60\u5f97\u7f6a\u8fd9\u4f4d\u8001\u602a\u7269\u5417\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u6447\u5934\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u4e0d\u8fc7\uff0c\u672c\u5e2e\u4e0e\u4ed6\uff0c\u5374\u662f\u6709\u8fc7\u4e00\u4e9b\u5c0f\u5c0f\u7684\u8bef\u4f1a\u800c\u5df2\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u6211\u8bf4\u5462\uff01\u679c\u7136\u4f60\u662f\u5fc3\u4e2d\u6709\u9b3c\u554a\u3002\u201d\u7b11\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u8fd9\u4e00\u56de\u4f60\u5927\u53ef\u653e\u5fc3\uff0c\u90a3\u8f9b\u8001\u602a\u6765\u6b64\u4e4b\u540e\uff0c\u4e0d\u4f46\u4e0d\u4f1a\u518d\u8ddf\u4f60\u7b97\u65e7\u5e10\uff0c\u53ea\u6015\u8fd8\u8981\u7ed9\u4f60\u5e26\u6765\u4e0d\u5c11\u7684\u597d\u5904\u5462\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u610f\u5916\u5730\u4e00\u6014\u9053\uff1a\u201c\u600e\u4e48\u53ef\u80fd\uff1f\u90a3\u8f9b\u8001\u602a\u53ea\u8981\u4e0d\u8bb0\u5f53\u5e74\u5148\u5e08\u4e0e\u4ed6\u7684\u8bef\u4f1a\uff0c\u5144\u5f1f\u5c31\u5f53\u771f\u611f\u6fc0\u4e0d\u5c3d\u7684\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u970d\u5144\u5927\u53ef\u653e\u5fc3\uff0c\u8fd9\u4e00\u56de\u8f9b\u8001\u602a\u4e43\u662f\u6253\u8d4c\u8f93\u5728\u65b9\u8001\u5f1f\u624b\u4e2d\uff0c\u4eca\u540e\u7684\u4e00\u5207\uff0c\u90a3\u8001\u602a\u7269\u5fc5\u987b\u542c\u547d\u4e8e\u65b9\u8001\u5f1f\uff0c\u53ea\u8981\u65b9\u8001\u5f1f\u544a\u8bc9\u4ed6\u4e00\u58f0\uff0c\u4ed6\u53c8\u600e\u6562\u627e\u4f60\u9ebb\u70e6\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u77a7\u7740\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u8fd9\u662f\u771f\u7684\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u4e00\u534a\u662f\u771f\u3002\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u5931\u7b11\u9053\uff1a\u201c\u600e\u7684\u4e00\u534a\u662f\u771f\u5462\uff0c\u5c0f\u65bd\u4e3b\uff0c\u5176\u6237\u7ecf\u8fc7\u53ef\u5426\u8bf4\u5f97\u660e\u767d\u4e00\u4e9b\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u90a3\u8f9b\u8001\u524d\u8f88\u5728\u592a\u5ba4\u5cf0\u5934\uff0c\u81ea\u8be9\u4ed6\u5251\u6cd5\u4e3a\u5929\u4e0b\u7b2c\u4e00\uff0c\u8fde\u5148\u5e08\u4e5f\u4e0d\u5728\u4ed6\u773c\u4e2d\uff0c\u4e00\u65f6\u6fc0\u8d77\u665a\u8f88\u597d\u80dc\u4e4b\u5fc3\uff0c\u548c\u4ed6\u52a8\u624b\u6bd4\u91cf\u4e86\u51e0\u62db\u5251\u6cd5\u2026\u2026\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u8f9b\u8001\u65bd\u4e3b\u7684\u6b66\u529f\uff0c\u8001\u8872\u4e5f\u66fe\u542c\u4eba\u63d0\u8fc7\uff0c\u679c\u7136\u662f\u9ad8\u4e0d\u53ef\u6d4b\uff0c\u4f46\u4ed6\u81ea\u5938\u5251\u6cd5\u5929\u4e0b\u7b2c\u4e00\u90a3\u5c31\u662f\u6709\u4e9b\u4e0d\u5b9e\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u7b11\u9053\uff1a\u201c\u662f\u554a\uff01\u6700\u5c11\u4ed6\u5e76\u672a\u83b7\u5f97\u5251\u795e\u7684\u96c5\u53f7\uff0c\u53ef\u77e5\u4ed6\u5728\u5251\u6cd5\u7684\u9020\u8be3\u4e4b\u4e0a\uff0c\u6bd4\u4e0d\u5f97\u5251\u795e\u9648\u5927\u4fa0\u4e86\uff01\u201d \u3000\u3000\u4e24\u4f4d\u4e00\u6d3e\u4e4b\u5c0a\uff0c\u8fd9\u7b49\u8bf4\u6cd5\uff0c\u90a3\u5df2\u662f\u76f8\u4fe1\uff0c\u65b9\u96ea\u5b9c\u7684\u5e08\u4f20\u5251\u6cd5\uff0c\u5fc5\u80dc\u8fc7\u90a3\u6b66\u6797\u4e00\u602a\u8f9b\u67cf\u516c\u7684\u4e86\uff01 \u3000\u3000\u4f46\u65b9\u96ea\u5b9c\u5374\u636e\u5b9e\u6447\u5934\u63a5\u9053\uff1a\u201c\u4e0d\u7136\uff0c\u90a3\u8f9b\u8001\u524d\u8f88\u7684\u5251\u6cd5\uff0c\u679c\u7136\u4e0d\u6bd4\u5148\u5e08\u7a0d\u900a\u2026\u2026\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u4e00\u6014\u9053\uff1a\u201c\u83ab\u975e\u5c0f\u65bd\u4e3b\u7684\u5251\u6cd5\uff0c\u4e0d\u6bd4\u90a3\u4e2a\u8f9b\u8001\u65bd\u4e3b\u9ad8\u660e\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u5018\u82e5\u65b9\u8001\u5f1f\u7684\u6b66\u529f\u4e0d\u6bd4\u8f9b\u8001\u602a\u7269\u9ad8\u5f3a\uff0c\u90a3\u4e2a\u8001\u602a\u624b\u53c8\u5c82\u80af\u542c\u547d\u4e8e\u4ed6\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u8fd9\u5012\u4e0d\u5047\uff01\u53ea\u662f\u65b9\u8001\u5f1f\u8bf4\u8f9b\u67cf\u516c\u7684\u5251\u6cd5\u4e0d\u5728\u5251\u795e\u9648\u5927\u4fa0\u4e4b\u4e0b\uff0c\u53c8\u5f53\u600e\u6837\u89e3\u8bf4\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u5417\uff1f\u53ea\u56e0\u665a\u8f88\u7684\u5251\u6cd5\u4e4b\u4e2d\uff0c\u6709\u4e00\u62db\u4e43\u662f\u5e08\u53d4\u6240\u4f20\uff0c\u800c\u4e14\u8fd9\u4e00\u62db\u5251\u6cd5\uff0c\u5374\u662f\u90a3\u8f9b\u8001\u524d\u8f88\u65e0\u6cd5\u8fd8\u654c\u7684\u7f18\u6545\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff01\u6562\u60c5\u5c0f\u65bd\u4e3b\u8fd8\u53e6\u6709\u6240\u5b66\u2026\u2026\u201d \u3000\u3000\u8a00\u4e0b\u4e4b\u610f\uff0c\u4f3c\u662f\u8ba4\u4e3a\u8fd9\u4e00\u62db\u5251\u6cd5\u5fc5\u7136\u4e0d\u662f\u5251\u795e\u9648\u5e0c\u6b63\u540c\u6e0a\u6e90\u7684\u4e86\u2026\u2026 \u3000\u3000\u65b9\u96ea\u5b9c\u5fd9\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u665a\u8f88\u8fd9\u4e00\u62db\u5251\u6cd5\u867d\u662f\u7531\u5bb6\u5e08\u53d4\u4f20\u6388\uff0c\u4f46\u4f9d\u7136\u662f\u5148\u5e08\u7684\u5251\u6cd5\u4e2d\u4e00\u62db\uff0c\u4e0d\u8fc7\uff0c\u56e0\u4e3a\u8fd9\u4e00\u62db\u5251\u6cd5\u4e43\u662f\u5148\u5e08\u7956\u665a\u5e74\u65b9\u59cb\u60f3\u4e86\u51fa\u6765\uff0c\u662f\u4ee5\u5148\u5e08\u672a\u66fe\u5b66\u7684\u5468\u5168\u800c\u5df2\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u7b11\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\uff0c\u8001\u8872\u5931\u8a00\u4e86\uff01\u201d \u3000\u3000\u7a76\u7adf\u4ed6\u4e0d\u5931\u4e3a\u4e00\u6d3e\u638c\u95e8\uff0c\u4e3a\u4eba\u8bf4\u8bdd\uff0c\u5149\u660e\u5f97\u5f88\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u54ea\u91cc\u8bdd\uff0c\u5927\u5e08\u5fd2\u8c26\u4e86\uff01\u201d \u3000\u3000\u8bed\u8a00\u987f\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u665a\u8f88\u5148\u5e08\u7684\u9f99\u884c\u516b\u5251\uff0c\u52a0\u4e0a\u4e86\u5e08\u7956\u7eed\u521b\u7684\u8fd9\u4e00\u62db\u5251\u6cd5\uff0c\u6b63\u5f0f\u7684\u540d\u79f0\u5e94\u6539\u4e3a\u201c\u9f99\u884c\u4e5d\u5251\u201d\uff0c\u665a\u8f88\u5c31\u4ed7\u7740\u90a3\u4e00\u62db\u5251\u6cd5\uff0c\u5728\u592a\u5ba4\u5cf0\u5934\uff0c\u4fa5\u5e78\u5730\u80dc\u4e86\u8f9b\u8001\u524d\u8f88\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u5927\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u90a3\u8001\u602a\u60f3\u5fc5\u662f\u5403\u60ca\u4e0d\u5c0f\u7684\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u8f9b\u8001\u524d\u8f88\u4e00\u65f6\u5931\u624b\uff0c\u81ea\u662f\u6709\u4e9b\u60ca\u8bb6\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u4e43\u662f\u6b66\u6797\u957f\u8005\uff0c\u5c0f\u632b\u4e4b\u540e\uff0c\u5012\u4e5f\u6177\u6168\u5f97\u5f88\uff0c\u4ed6\u5e94\u5141\u4e86\u665a\u8f88\uff0c\u5168\u5fc3\u5168\u529b\uff0c\u76f8\u52a9\u665a\u8f88\u5c06\u90a3\u4e94\u5927\u9b54\u4e3b\u9664\u53bb\u3002\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u771f\u7684\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u8f9b\u8001\u524d\u8f88\u4eb2\u53e3\u5e94\u5141\u4e4b\u4e8b\uff0c\u81ea\u7136\u4e0d\u4f1a\u53cd\u6094\u7684\u4e86\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u5ffd\u7136\u5408\u5341\u9053\uff1a\u201c\u5584\u54c9\uff0c\u5584\u54c9\uff01\u653e\u4e0b\u5c60\u5200\uff0c\u7acb\u5730\u6210\u4f5b\uff0c\u5c0f\u65bd\u4e3b\u5982\u80fd\u4f7f\u90a3\u8f9b\u8001\u65bd\u4e3b\u4ece\u6b64\u6d17\u5fc3\u9769\u9762\uff0c\u4e0d\u518d\u4efb\u610f\u4f5c\u4e3a\uff0c\u529f\u5fb7\u6c60\u4e2d\uff0c\u5c0f\u65bd\u4e3b\u53ef\u8c13\u9020\u798f\u4e0d\u5c0f\u554a\u2026\u2026\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u5927\u5e08\u8fc7\u5956\u4e86\uff01\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5728\u65c1\u65e9\u5df2\u542c\u5f97\u4e0d\u4e9b\u4e0d\u8010\uff0c\u8fd9\u65f6\u5fcd\u4e0d\u4f4f\u5927\u58f0\u9053\uff1a\u201c\u90a3\u8001\u5934\u8fd8\u6ca1\u6709\u6765\uff0c\u4f60\u4eec\u5e72\u5417\u5c31\u8c08\u7684\u4e00\u4e2a\u6ca1\u5b8c\uff1f\u5b8b\u8001\uff0c\u54b1\u4eec\u51fa\u6765\u77a7\u77a7\u53ef\u597d\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u4e0d\u597d\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u4e00\u6014\u9053\uff1a\u201c\u4f60\u8001\u600e\u4e48\u4e86\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e0d\u600e\u4e48\uff0c\u53ea\u662f\u4e94\u810f\u6709\u6b20\u4fee\u7406\uff01\u201d \u3000\u3000\u4ed6\u8fd9\u8bdd\u4e00\u51fa\u53e3\uff0c\u5b89\u5c0f\u840d\u4e5f\u5931\u58f0\u9053\uff1a\u201c\u662f\u554a\uff0c\u5728\u4e0b\u4e5f\u89c9\u7740\u6709\u4e9b\u997f\u5f97\u614c\uff0c\u54b1\u4eec\u4e0d\u5982\u53bb\u9910\u5802\u77a7\u77a7\u5427\uff01\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5c31\u4f8d\u8d77\u8eab\u51fa\u53bb\u3002 \u3000\u3000\u5165\u4e91\u5927\u5e08\u5408\u5341\u7b11\u9053\uff1a\u201c\u4e24\u4f4d\u65bd\u4e3b\u8bf7\u83ab\u89c1\u602a\uff0c\u8001\u8872\u4e00\u65f6\u7cca\u6d82\uff0c\u7adf\u7136\u5fd8\u4e86\u8981\u4ed6\u4eec\u51c6\u5907\u665a\u9910\u4e86\u2026\u2026\u201d \u3000\u3000\u8f6c\u5934\u5411\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u5927\u5e08\u5144\uff0c\u5feb\u5feb\u8981\u4ed6\u4eec\u628a\u658b\u996d\u9001\u6765\u65b9\u4e08\u5ba4\u4fbf\u662f\u4e86\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u5e94\u4e86\u4e00\u58f0\uff0c\u8fc5\u5feb\u5730\u51fa\u4e86\u9759\u5ba4\u3002 \u3000\u3000\u4e0d\u4e00\u4f1a\u51e1\u540d\u5c0f\u6c99\u5f25\u9001\u6765\u4e86\u658b\u996d\uff0c\u5927\u4f19\u513f\u4fbf\u5728\u65b9\u4e08\u5ba4\u4e2d\u80e1\u4e71\u5403\u4e86\u4e00\u987f\uff0c\u95f2\u804a\u4e00\u4f1a\uff0c\u4fbf\u518d\u56de\u53bb\u4f11\u6b47\uff01 \u3000\u3000\u53c8\u662f\u4e09\u5929\u8fc7\u53bb\uff0c\u90a3\u4e94\u5927\u9b54\u4e3b\u4f9d\u7136\u6ca1\u6709\u52a8\u9759\uff0c\u5b89\u5c0f\u840d\u4f3c\u5728\u5bfa\u4e2d\u803d\u7684\u6709\u4e9b\u4e0d\u8010\uff0c\u7b2c\u56db\u5927\u4e00\u65e9\uff0c\u4fbf\u5435\u7740\u8981\u65b9\u96ea\u5b9c\u966a\u5979\u53bb\u767b\u5c01\u73a9\u73a9\u3002 \u3000\u3000\u4f46\u65b9\u96ea\u5b9c\u5374\u672a\u5e94\u5141\uff0c\u53ea\u56e0\u4ed6\u803d\u5fc3\u6b66\u6797\u4e00\u602a\u8f9b\u67cf\u516c\u53ef\u80fd\u8981\u6765\uff0c\u540c\u65f6\uff0c\u4ed6\u66f4\u7740\u6025\u7684\u5374\u662f\u60f3\u77e5\u6653\u90d1\u5927\u521a\u7684\u4e0b\u843d\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u89c1\u65b9\u96ea\u5b9c\u4e0d\u80af\u966a\u5979\u53bb\uff0c\u4e00\u8d4c\u6c14\uff0c\u7adf\u7136\u72ec\u81ea\u4e00\u4eba\uff0c\u5077\u5077\u5730\u6e9c\u51fa\u4e86\u5c11\u6797\u3002 \u3000\u3000\u76f4\u5230\u8fd9\u4e00\u5927\u6df1\u591c\uff0c\u65b9\u96ea\u5b9c\u548c\u5b8b\u5b5a\u59cb\u53d1\u89c9\uff0c\u5b89\u5c0f\u840d\u79bb\u53bb\uff0c\u4ecd\u672a\u56de\u8f6c\u3002\u987f\u65f6\uff0c\u65b9\u96ea\u5b9c\u5927\u4e3a\u7126\u8651\uff0c\u62c9\u7740\u5b8b\u5b5a\uff0c\u5728\u5c11\u6797\u5bfa\u5185\u5916\u627e\u4e86\u4e00\u904d\uff0c\u4f9d\u7136\u672a\u89c1\u8e2a\u5f71\u3002 \u3000\u3000\u5b8b\u5b5a\u966a\u7740\u65b9\u96ea\u5b9c\u53c8\u8fde\u591c\u8d76\u5230\u592a\u5ba4\u5cf0\u9876\uff0c\u53ea\u89c1\u90a3\u795d\u516c\u660e\u8737\u5367\u5728\u5c71\u6d1e\u4e4b\u4e2d\uff0c\u547c\u547c\u5927\u7761\uff01 \u3000\u3000\u5b8b\u5b5a\u4e00\u628a\u5c06\u4ed6\u62d6\u8d77\uff0c\u95ee\u4ed6\u53ef\u66fe\u89c1\u5230\u4e86\u5b89\u5c0f\u840d\uff0c\u795d\u516c\u660e\u63c9\u7740\u773c\uff0c\u8fde\u8fde\u6447\u5934\uff0c\u9a82\u9053\uff1a \u3000\u3000\u201c\u8001\u5077\u513f\uff0c\u8001\u592b\u9664\u4e86\u8fd9\u4e00\u8eab\u7834\u70c2\uff0c\u5565\u5b50\u6ca1\u6709\uff0c\u4f60\u8981\u60f3\u5077\u60f3\u62a2\uff0c\u53ef\u522b\u627e\u9519\u4e86\u5730\u65b9\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u54ea\u6709\u5fc3\u4e8b\u8ddf\u4ed6\u9b3c\u626f\uff1f\u62c9\u7740\u65b9\u96ea\u5b9c\uff0c\u4fbf\u81ea\u6389\u5934\u5411\u5c71\u4e0b\u5954\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u65f6\u4e4b\u95f4\u4e5f\u6ca1\u6709\u4e3b\u610f\uff0c\u76b1\u7709\u9053\uff1a\u201c\u54b1\u4eec\u83ab\u975e\u4e0d\u627e\u5979\u4e86\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u5f53\u7136\u8981\u627e\uff0c\u4e0d\u8fc7\uff0c\u4eca\u5929\u5374\u662f\u4e0d\u884c\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u56de\u5bfa\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u53ea\u597d\u5148\u56de\u53bb\u4e86\uff01\u5982\u662f\u90a3\u5b89\u59d1\u5a18\u660e\u65e5\u4e0d\u56de\u6765\uff0c\u54b1\u4eec\u518d\u53bb\u767b\u5c01\uff0c\u4e5f\u4e0d\u4e3a\u8fdf\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u867d\u7136\u6709\u4e9b\u4e0d\u613f\uff0c\u5374\u4e5f\u60f3\u4e0d\u51fa\u522b\u7684\u529e\u6cd5\uff0c\u5f53\u4e0b\u53ea\u597d\u968f\u7740\u5b8b\u5b5a\u56de\u5230\u5c11\u6797\u5bfa\u3002 \u3000\u3000\u9e21\u9e23\u752b\u8d77\uff0c\u65b9\u96ea\u5b9c\u4fbf\u53eb\u8d77\u4e86\u5b8b\u5b5a\uff0c\u8981\u53bb\u90a3\u767b\u5c01\u57ce\u4e2d\u5bfb\u627e\u5b89\u5c0f\u840d\u3002 \u3000\u3000\u5c31\u5728\u4e24\u4eba\u8de8\u51fa\u5c71\u95e8\uff0c\u672a\u53ca\u534a\u91cc\uff0c\u53ea\u89c1\u5b89\u5c0f\u840d\u6b63\u5e26\u7740\u51e0\u540d\u58ee\u6c49\uff0c\u62ac\u4e86\u4e00\u4eba\uff0c\u76f4\u5411\u5bfa\u4e2d\u8d70\u6765\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u957f\u957f\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u6447\u5934\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u54b1\u4eec\u53ef\u662f\u767d\u803d\u5fc3\u5566\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u672c\u6765\u5c31\u662f\u767d\u803d\u5fc3\u4e86\u554a\uff01\u5b89\u5c0f\u840d\u4e00\u8eab\u6b66\u529f\uff0c\u4e0d\u5728\u4f60\u8001\u5f1f\u4e4b\u4e0b\uff0c\u8bd5\u60f3\uff0c\u6b66\u6797\u4e4b\u4e2d\uff0c\u53c8\u6709\u4ec0\u4e48\u80fd\u591f\u4f24\u5f97\u5979\u5462\uff1f\u5982\u4eca\u5979\u5df2\u56de\u6765\uff0c\u4f60\u8001\u5f1f\u603b\u53ef\u653e\u5fc3\u4e86\u5427\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5b89\u5c0f\u840d\u5df2\u7136\u75be\u6b65\u8d76\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u5979\u4e00\u89c1\u65b9\u96ea\u5b9c\uff0c\u4f7f\u5927\u58f0\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u731c\u6211\u628a\u4ec0\u4e48\u4eba\u62ac\u56de\u6765\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u60f3\uff0c\u4f60\u4e0d\u8bf4\u51fa\uff0c\u6211\u4e5f\u4e0d\u662f\u795e\u4ed9\uff0c\u53c8\u600e\u4f1a\u77e5\u9053\u5979\u62ac\u56de\u6765\u4e86\u4ec0\u4e48\u4eba\uff1f \u3000\u3000\u4f46\u4ed6\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u77a7\u4f60\u8fd9\u7b49\u9ad8\u5174\uff0c\u8fd9\u4eba\u5fc5\u662f\u4ec0\u4e48\u91cd\u8981\u4eba\u7269\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u53ef\u4e0d\u662f\uff1f\u4ed6\u662f\u4f60\u8981\u627e\u7684\u90d1\u5927\u4fa0\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u987f\u65f6\u5446\u4e86\u534a\u664c\uff0c\u5927\u58f0\u9053\uff1a\u201c\u662f\u6211\u90a3\u90d1\u5927\u4f2f\u7236\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u53ef\u4e0d\u662f\uff01\u201d \u3000\u3000\u5979\u5ae3\u7136\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u65b9\u5144\uff0c\u6211\u8fd9\u4e00\u8d9f\u51fa\u95e8\uff0c\u53ef\u6ca1\u6709\u767d\u8d39\u5427\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u65f6\u5df2\u5927\u6b65\u62a2\u4e0a\u524d\u53bb\uff0c\u8d70\u5230\u90a3\u4e09\u540d\u58ee\u6c49\u8eab\u524d\uff0c\u53ea\u89c1\u90a3\u4e00\u6247\u95e8\u677f\u4e4b\u4e0a\uff0c\u8eba\u7684\u6b63\u662f\u201c\u4e2d\u5dde\u4e09\u4fa0\u201d\u4e2d\u7684\u5927\u4fa0\u90d1\u5927\u521a\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u8d81\u7740\u9ece\u660e\u7684\u6668\u66e6\uff0c\u6ce8\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u90d1\u5927\u521a\u53cc\u76ee\u7d27\u95ed\uff0c\u9762\u5982\u6de1\u91d1\uff0c\u4f46\u5e78\u800c\u547c\u5438\u5c1a\u662f\u5300\u79f0\uff0c\u663e\u7136\u4e43\u662f\u8eab\u53d7\u91cd\u4f24\u4e4b\u4e0b\uff0c\u88ab\u5b89\u5c0f\u840d\u6551\u6765\u3002 \u3000\u3000\u4e24\u9897\u6cea\u73e0\uff0c\u5ffd\u7136\u6253\u65b9\u96ea\u5b9c\u773c\u7736\u5185\u6ef4\u843d\uff0c\u4ed6\u60f3\u8d77\u5728\u5609\u5b9a\u5e9c\u57ce\u5916\uff0c\u82e5\u975e\u90d1\u5927\u521a\u8ffd\u8e2a\u800c\u53bb\uff0c\u6551\u4e86\u81ea\u5df1\u4e00\u6761\u5c0f\u547d\uff0c\u81ea\u5df1\u65e9\u6b7b\u5728\u4f2f\u7236\u65b9\u5929\u6210\u7684\u638c\u4e0b\uff0c\u4f5c\u4e86\u51a4\u6b7b\u4e4b\u9b3c\u4e86\uff01 \u3000\u3000\u4ed6\u5446\u7acb\u4e86\u4e00\u77ac\uff0c\u53ea\u542c\u5f97\u5b8b\u5b5a\u5728\u8eab\u540e\u4f4e\u58f0\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u90d1\u5927\u4fa0\u4f3c\u662f\u53d7\u6781\u91cd\u5185\u4f24\uff0c\u8fd8\u662f\u8d76\u5feb\u62ac\u5165\u5bfa\u5185\u8bbe\u6cd5\u6551\u6cbb\u8981\u7d27\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u609a\u7136\u4e00\u60ca\uff0c\u5fd9\u9053\uff1a\u201c\u662f\u554a\u2026\u2026\u6709\u52b3\u4e09\u4f4d\u5feb\u5c06\u90d1\u5927\u4f2f\u62ac\u5165\u5bfa\u5185\uff01\u201d \u3000\u3000\u8f6c\u8eab\u9886\u7740\u4e09\u540d\u58ee\u6c49\uff0c\u76f4\u5411\u5c71\u95e8\u884c\u53bb\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u548c\u5b8b\u5b5a\u7d27\u7d27\u5730\u968f\u5728\u8eab\u540e\uff0c\u4e00\u8def\u4f4e\u58f0\u800c\u8c08\uff0c\u65b9\u96ea\u5b9c\u662f\u60e6\u8bb0\u7740\u90d1\u5927\u521a\u4f24\u52bf\uff0c\u4fbf\u4e5f\u672a\u66fe\u7559\u5fc3\u4ed6\u4eec\u8bf4\u7684\u4ec0\u4e48\u3002 \u3000\u3000\u90d1\u5927\u521a\u7684\u4f24\u52bf\uff0c\u663e\u662f\u4e0d\u8f7b\uff0c\u62ac\u5165\u5bfa\u5185\u4ee5\u540e\uff0c\u5b8b\u5b5a\u6253\u53d1\u4e86\u90a3\u4e09\u540d\u58ee\u5f53\uff0c\u4fbf\u7ec6\u5fc3\u5730\u5bdf\u770b\u4ed6\u7684\u4f24\u52bf\uff0c\u534a\u664c\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u662f\u7740\u6025\uff0c\u4f46\u53c8\u4e0d\u597d\u51fa\u53e3\u76f8\u8be2\uff0c\u53ea\u6025\u5f97\u5728\u623f\u4e2d\u8f6c\u6765\u8f6c\u53bb\uff0c\u5927\u6709\u624b\u8db3\u65e0\u63aa\u4e4b\u611f\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u77a7\u4ed6\u8fd9\u7b49\u795e\u60c5\uff0c\u4e0d\u7981\u82b3\u5fc3\u5927\u611f\u4e0d\u5b89\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u5750\u4e0b\u6765\u6b47\u6b47\u5427\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u76b1\u7709\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u90d1\u5927\u4f2f\u600e\u4e48\u53d7\u7684\u4f24\uff1f\u4f60\u2026\u2026\u5728\u54ea\u91cc\u53d1\u73b0\u4e86\u4ed6\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u767b\u5c01\u57ce\u5916\u7684\u53e4\u5e99\u4e4b\u524d\u3002\u201d\u5979\u8bed\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u65b9\u5144\uff0c\u548b\u65e5\u6211\u4e0d\u662f\u8981\u4f60\u966a\u6211\u4e00\u9053\u524d\u53bb\u767b\u5c01\uff0c\u4f60\u6ca1\u6709\u7b54\u5e94\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u76f4\u63a5\u9053\uff1a\u201c\u6211\u2026\u2026\u8d24\u5f1f\uff0c\u6211\u5e76\u975e\u4e0d\u60f3\u524d\u53bb\uff0c\u53ea\u662f\u6211\u6015\u5929\u9b54\u5973\u5728\u6211\u7b49\u79bb\u53bb\u8fd9\u4e4b\u65f6\u8d77\u6765\uff0c\u6216\u662f\u90a3\u8f9b\u8001\u4e0d\u901f\u800c\u81f3\uff0c\u6211\u4eec\u5982\u4e0d\u5728\uff0c\u5c82\u4e0d\u7ed9\u5c11\u6797\u5bfa\u5e73\u6dfb\u4e0d\u5c11\u9ebb\u70e6\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u662f\u554a\uff0c\u6240\u4ee5\u6211\u624d\u4e00\u4e2a\u4eba\u51fa\u53bb\u7684\u561b\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8d24\u5f1f\u600e\u4e48\u89c1\u5230\u6211\u5927\u4f2f\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5ae3\u7136\u4e00\u7b11\u9053\uff1a\u201c\u8bf4\u6765\u53ef\u4e5f\u771f\u662f\u5de7\u5f97\u5f88\uff0c\u6211\u6628\u513f\u5728\u767b\u5c01\u901b\u4e86\u4e00\u534a\u5929\uff0c\u5b9e\u5728\u89c9\u5f97\u5f88\u65e0\u804a\uff0c\u521d\u66f4\u4e00\u8fc7\uff0c\u6211\u4fbf\u6253\u7b97\u56de\u6765\uff0c\u8c01\u77e5\u5c31\u5728\u6211\u51fa\u57ce\u4e4b\u9645\uff0c\u5ffd\u7136\u77a7\u5230\u4e86\u51e0\u4e2a\u884c\u8ff9\u53ef\u7591\u7684\u6b66\u6797\u4eba\u7269\uff0c\u4e00\u65f6\u597d\u5947\uff0c\u4fbf\u8ddf\u5728\u4e86\u4ed6\u4eec\u8eab\u540e\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u5927\u6982\u662f\u4e94\u9b54\u7684\u624b\u4e0b\u5427\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u6014\u9053\uff1a\u201c\u8d24\u5f1f\u7adf\u662f\u4e0d\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u6211\u54ea\u6709\u95f2\u5de5\u592b\u53bb\u95ee\u4ed6\u4eec\uff01\u201d \u3000\u3000\u5979\u8bed\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u6211\u968f\u7740\u90a3\u516d\u4e03\u540d\u6301\u5200\u5e26\u5251\u7684\u4eba\u51fa\u57ce\u4ee5\u540e\uff0c\u53d1\u73b0\u4ed6\u4eec\u7adf\u7136\u76f4\u5954\u57ce\u5916\u7684\u4e00\u5ea7\u53e4\u5e99\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u90d1\u5927\u6015\u5c31\u5728\u53e4\u5e99\u4e4b\u4e2d\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u90d1\u5927\u4f2f\u679c\u7136\u5c31\u5728\u53e4\u5e99\u4e4b\u4e2d\u2026\u2026\uff0c\u4e0d\u8fc7\uff0c\u5148\u524d\u6211\u5e76\u4e0d\u77e5\u9053\u4ed6\u4eec\u53bb\u5e72\u4ec0\u4e48\uff0c\u800c\u4e14\uff0c\u6211\u4e5f\u6ca1\u6709\u89c1\u8fc7\u90d1\u5927\u6015\u5440\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8d24\u5f1f\u662f\u600e\u751f\u77a7\u51fa\u6765\u90d1\u5927\u6015\u7684\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u6211\u773c\u89c1\u8fd9\u4e00\u4f19\u8fdb\u4e86\u53e4\u5e99\u4e4b\u540e\uff0c\u7adf\u7136\u5341\u5206\u8c28\u614e\u5730\u6563\u5e03\u5f00\u6765\uff0c\u4eff\u4f5b\u7b49\u5019\u7740\u4ec0\u4e48\u4eba\u524d\u6765\uff0c\u5fc3\u4e2d\u4fbf\u66f4\u4e3a\u597d\u5947\uff0c\u5f53\u4e0b\u9690\u8eab\u5728\u5927\u6bbf\u7684\u627f\u6881\u4e4b\u4e0a\uff0c\u7559\u5fc3\u7740\u4ed6\u4eec\u4e00\u4e3e\u4e00\u52a8\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u76b1\u7709\u9053\uff1a\u201c\u53ef\u662f\u6709\u4eba\u6765\u4e86\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u5f53\u7136\u6709\u4eba\u6765\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8131\u53e3\u9053\uff1a\u201c\u90d1\u5927\u6015\u7236\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4e0d\u662f\u3002\u201d\u5979\u6447\u4e86\u6447\u5934\uff0c\u63a5\u9053\uff1a\u201c\u90a3\u6765\u7684\u4eba\uff0c\u4e43\u662f\u4e00\u4e2a\u957f\u50cf\u5341\u5206\u5947\u602a\u7684\u5934\u9640\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u60f3\u5fc5\u662f\u90a3\u5e99\u4e2d\u7684\u4e3b\u6301\u50e7\u4eba\u4e86\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u6211\u4e5f\u6ca1\u6709\u95ee\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u5934\u9640\u8fdb\u4e86\u5e99\u5b87\u4ee5\u540e\uff0c\u4fbf\u76f4\u8d8a\u5927\u6bbf\u4e2d\uff0c\u6253\u90a3\u795e\u6848\u4e4b\u4e0b\uff0c\u94bb\u4e86\u8fdb\u53bb\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u6014\u9053\uff1a\u201c\u6562\u60c5\u90a3\u513f\u8fd8\u6709\u6697\u95e8\u7684\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u90a3\u795e\u6848\u4e4b\u4e0b\uff0c\u6b63\u662f\u6709\u7740\u6697\u5ba4\uff0c\u5f53\u65f6\u6211\u6b63\u60f3\u98de\u8eab\u8dc3\u843d\uff0c\u4e5f\u8ddf\u8fdb\u53bb\u67e5\u770b\u4e00\u756a\uff0c\u4e0d\u6599\u90a3\u5934\u9640\u4e00\u8f6c\u773c\uff0c\u53c8\u4ece\u795e\u6848\u4e4b\u4e0b\uff0c\u94bb\u4e86\u51fa\u6765\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6ca1\u6709\u63a5\u53e3\uff0c\u6697\u9053\uff1a\u90a3\u5ea7\u5e99\u5b87\uff0c\u53ea\u6015\u4e0d\u662f\u4e00\u4e2a\u4ec0\u4e48\u597d\u6240\u5728\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u8fd9\u65f6\u63a5\u9053\uff1a\u201c\u90a3\u5934\u9640\u56e0\u662f\u751f\u5f88\u53c8\u77ee\u5927\u80d6\uff0c\u8fdc\u8fdc\u671b\u53bb\uff0c\u76f4\u50cf\u662f\u4e00\u4e2a\u5927\u7403\uff0c\u6211\u77a7\u4ed6\u6eda\u6765\u6d41\u53bb\uff0c\u5012\u4e5f\u5341\u5206\u597d\u7b11\u3002\u201d \u3000\u3000\u8bf4\u7740\u5c31\u771f\u7684\u7b11\u51fa\u6765\uff0c\u60f3\u5fc5\u90a3\u540d\u5934\u9640\u679c\u7136\u957f\u5f97\u5341\u5206\u96be\u770b\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u540e\u6765\u5462\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u90a3\u5934\u9640\u6eda\u4e86\u51fa\u6765\u4e0d\u4e45\uff0c\u6253\u90a3\u795e\u6848\u4e4b\u4e0b\uff0c\u53c8\u51fa\u6765\u4e86\u4e09\u4e2a\u4eba\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u5176\u4e2d\u8fd9\u4e00\uff0c\u4fbf\u662f\u90d1\u5927\u4f2f\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u4e86\u4e00\u5446\u9053\uff1a\u201c\u600e\u4e48\u4f1a\uff1f\u90d1\u5927\u4f2f\u4e0d\u77e5\u662f\u5426\u5f53\u65f6\u4e1a\u5df2\u53d7\u4f24\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u90a3\u65f6\u662f\u597d\u751f\u751f\u7684\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e0d\u7981\u8f6c\u5934\u770b\u4e86\u90d1\u5927\u521a\u4e00\u773c\uff0c\u53ea\u89c1\u5b8b\u5b5a\u6b63\u4ee5\u53cc\u638c\u5728\u66ff\u4ed6\u63a8\u62ff\uff0c\u5fc3\u4e2d\u4e0d\u7531\u5f97\u5fd6\u9053\uff1a \u3000\u3000\u90d1\u5927\u4f2f\u53c8\u600e\u662f\u6295\u8eab\u9b54\u4e3b\u4e4b\u4eba\uff0c\u53ea\u6015\u662f\u5c0f\u840d\u770b\u5dee\u4e86\u2026\u2026\u4ed6\u5fc3\u4e2d\u8fd9\u4e48\u60f3\uff0c\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u6211\u8fd9\u4f4d\u90d1\u5927\u4f2f\uff0c\u4e3a\u4eba\u5341\u5206\u6b63\u6d3e\u554a\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u90d1\u5927\u6015\u4e3a\u4eba\u5341\u5206\u6b63\u6d3e\uff0c\u6211\u53c8\u600e\u4e48\u51fa\u624b\u5c06\u4ed6\u6551\u4e86\u56de\u6765\u5462\uff1f\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4ed6\u4eec\u4e09\u4eba\u8d70\u51fa\u795e\u6848\u4e4b\u540e\uff0c\u90a3\u5934\u9640\u4fbf\u8eab\u90d1\u5927\u4f2f\u8fde\u58f0\u8d23\u9a82\uff0c\u602a\u4ed6\u628a\u4e94\u5927\u9b54\u4e3b\u8981\u53bb\u5c11\u6797\u4e4b\u4e8b\uff0c\u6cc4\u4e86\u51fa\u53bb\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u90d1\u5927\u4f2f\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4e00\u53e3\u5426\u8ba4\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u957f\u957f\u5730\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u90d1\u5927\u6015\u4e0d\u80af\u627f\u8ba4\uff0c\u90a3\u672c\u662f\u610f\u6599\u4e2d\u4e8b\u2026\u2026\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u5251\u7709\u4e00\u626c\uff0c\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u90a3\u53e6\u5916\u7684\u4e24\u4e2a\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4e0d\u77e5\u9053\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u95ee\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4ed6\u4eec\u7684\u6b66\u529f\u592a\u5dee\uff0c\u6211\u4e00\u51fa\u624b\uff0c\u4ed6\u4eec\u5c31\u6ca1\u6709\u547d\u4e86\uff0c\u54ea\u91cc\u8fd8\u80fd\u591a\u95ee\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u539f\u6765\u8d24\u5f1f\u6740\u4e86\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4e3a\u4e86\u8981\u66ff\u65b9\u5144\u6551\u4e0b\u90d1\u5927\u4f2f\u7236\uff0c\u6211\u53ea\u597d\u75db\u4e0b\u6740\u624b\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u90a3\u5934\u9640\u53ef\u4e5f\u88ab\u4f60\u6740\u4e86\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u6ca1\u6709\uff01\u90a3\u5934\u9640\u4e00\u8eab\u6b66\u529f\u5f88\u9ad8\uff0c\u6211\u4e00\u65f6\u6ca1\u7559\u5fc3\uff0c\u7adf\u7136\u8ba9\u4ed6\u8dd1\u4e86\uff01\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4ed6\u90a3\u5de6\u81c2\uff0c\u5374\u6ce8\u5b9a\u6b8b\u5e9f\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u671b\u7740\u5b89\u5c0f\u840d\u7b11\u95ee\u9053\uff1a\u201c\u8d24\u5f1f\u53ef\u95ee\u8fc7\u90a3\u5934\u9640\u7684\u540d\u53f7\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u6ca1\u6709\u95ee\uff0c\u4e0d\u8fc7\uff0c\u5728\u90d1\u5927\u4fa0\u7684\u53e3\u4e2d\uff0c\u6211\u5374\u542c\u51fa\u4e86\u4ed6\u59d3\u6c0f\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u59d3\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u90d1\u5927\u4f2f\u79f0\u4ed6\u4e3a\u9f99\u5c0a\u8001\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u6697\u9053\uff1a\u5979\u521a\u5bf9\u66fe\u8bf4\uff0c\u90a3\u548c\u5c1a\u5f88\u80d6\uff0c\u7109\u77e5\u4e0d\u662f\u79f0\u4ed6\u4e3a\u80d6\u5c0a\u8005\u5462\uff1f\u53ea\u662f\uff0c\u4ed6\u5fc3\u4e2d\u867d\u7136\u8fd9\u4e48\u60f3\uff0c\u53e3\u4e2d\u5374\u672a\u8bf4\u51fa\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u773c\u89c1\u65b9\u96ea\u5b9c\u6c89\u541f\u4e0d\u8bed\uff0c\u63a5\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u53ef\u662f\u4e0d\u4fe1\u90a3\u5934\u9640\u53eb\u9f99\u5c0a\u8005\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u662f\uff01\u201d \u3000\u3000\u8bdd\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u8d24\u5f1f\u4f60\u662f\u600e\u4e48\u63a2\u67e5\u77e5\u4e86\u90d1\u5927\u4f2f\u7684\u8eab\u4efd\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5ae3\u7136\u4e00\u7b11\u9053\uff1a\u201c\u90a3\u4e0d\u5bb9\u6613\u5417\uff1f\u81ea\u4ece\u90d1\u5927\u4f2f\u51fa\u6765\u4ee5\u540e\uff0c\u90a3\u5934\u9640\u4fbf\u4e00\u53e3\u4e00\u58f0\u7684\u53eb\u4ed6\u90d1\u5927\u521a\uff0c\u6211\u81ea\u7136\u77e5\u9053\u4ed6\u662f\u4f60\u7684\u5927\u4f2f\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u8bdd\u97f3\u4e00\u843d\uff0c\u65b9\u96ea\u5b9c\u4e0d\u7981\u62b1\u62f3\u9053\uff1a\u201c\u8d24\u5f1f\u5982\u6b64\u5173\u5fc3\u5144\u5f1f\u4e4b\u4e8b\uff0c\u771f\u6b63\u53eb\u4eba\u611f\u6fc0\u5f97\u5f88\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u8fd9\u4e5f\u7b97\u4e0d\u4e86\u4ec0\u4e48\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u90d1\u5927\u4f2f\u600e\u6837\u53d7\u4e86\u91cd\u4f24\u5462\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u8fd9\u2026\u2026\u53ea\u602a\u6211\u5f53\u65f6\u592a\u8fc7\u5927\u610f\uff0c\u6ca1\u6709\u60f3\u5230\u53e6\u5916\u4e24\u4e2a\u7684\u5fc3\u80a0\u90a3\u4e48\u72e0\u6bd2\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u662f\u90a3\u5934\u9640\u4eb2\u4f24\u90d1\u5927\u4f2f\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4e0d\u662f\uff01\u201d \u3000\u3000\u5979\u8138\u4e0a\u63a0\u8fc7\u4e00\u4e1d\u6068\u6068\u4e4b\u8272\uff0c\u987f\u8db3\u9053\uff1a\u201c\u602a\u53ea\u602a\u6211\u4e00\u65f6\u7c97\u5fc3\uff0c\u773c\u89c1\u90a3\u5934\u9640\u4e0d\u505c\u559d\u9a82\u8d23\u95ee\u90d1\u5927\u6015\uff0c\u5c31\u8be5\u60f3\u5230\uff0c\u4ed6\u4eec\u53ef\u80fd\u4f1a\u5bf9\u4ed6\u4e0b\u90a3\u6bd2\u624b\uff0c\u4f46\u6211\u5374\u5ffd\u7565\u4e86\u8fd9\u4e00\u70b9\uff0c\u7ec8\u4e8e\u5728\u90a3\u5934\u9640\u66b4\u6012\u559d\u4ee4\u4ed6\u4eec\u5e9f\u4e86\u90d1\u5927\u521a\u4e4b\u65f6\uff0c\u624d\u5306\u5fd9\u51fa\u624b\u76f8\u6551\uff0c\u5374\u53eb\u90d1\u5927\u4f2f\u5403\u4e86\u4e0d\u5c11\u7684\u82e6\u5934\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u867d\u7136\u89c9\u5f97\u5b89\u5c0f\u840d\u771f\u662f\u6709\u4e9b\u4e0d\u5bf9\uff0c\u4f46\u8f6c\u800c\u4e00\u60f3\uff0c\u5982\u679c\u5979\u4e0d\u662f\u65e0\u5de7\u4e0d\u5de7\u78b0\u4e0a\u4e86\u8fd9\u7b49\u4e8b\uff0c\u90d1\u5927\u4f2f\u65e9\u5df2\u6b7b\u5728\u4ed6\u4eec\u624b\u4e0b\uff0c\u81ea\u5df1\u8fd8\u4e0d\u77e5\u9053\u5462\uff01\u8f6c\u5ff5\u81f3\u6b64\uff0c\u4e0d\u7981\u67d4\u58f0\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u8fd9\u4e0d\u662f\u4f60\u7684\u9519\uff0c\u60f3\u5fc5\u662f\u90d1\u5927\u4f2f\u547d\u4e2d\u6709\u7740\u8fd9\u7b49\u9b54\u96be\uff0c\u82e5\u975e\u8d24\u5f1f\u9047\u4e0a\uff0c\u53ea\u6015\u90d1\u5927\u4f2f\u7684\u6027\u547d\u4e5f\u8981\u96be\u4fdd\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u677e\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u6ca1\u6709\u602a\u6211\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u6211\u53ea\u6709\u611f\u6fc0\u4f60\u6551\u4e86\u90d1\u5927\u4f2f\u4e4b\u5fc3\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5ae3\u7136\u56de\u7738\uff0c\u7b11\u9053\uff1a\u201c\u65b9\u5144\u4e0d\u602a\u6211\uff0c\u6211\u5c31\u653e\u5fc3\u4e86\u554a\uff01\u201d \u3000\u3000\u5979\u7b11\u610f\u76c8\u816e\uff0c\u663e\u5f97\u751a\u662f\u9ad8\u5174\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u90a3\u6563\u5e03\u5728\u5e99\u4e2d\u7684\u4eba\u5462\uff1f\u4f60\u628a\u4ed6\u4eec\u600e\u751f\u5904\u65ad\u4e86\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u6211\u5728\u90a3\u5934\u9640\u559d\u4ee4\u7ad9\u5728\u90d1\u5927\u4f2f\u8eab\u65c1\u4e4b\u4eba\uff0c\u4e0b\u624b\u5e9f\u4e86\u90d1\u5927\u4f2f\u4e4b\u65f6\uff0c\u98de\u8eab\u8dc3\u4e0b\u627f\u6881\uff0c\u867d\u7136\u5c06\u53f3\u8fb9\u4e00\u4eba\u51fb\u51fa\u4e09\u4e08\uff0c\u8dcc\u6b7b\u5f53\u5730\uff0c\u4f46\u5de6\u8fb9\u7684\u90a3\u4eba\uff0c\u5374\u5728\u4f24\u4e86\u90d1\u5927\u4f2f\u4e00\u638c\u4e4b\u540e\uff0c\u624d\u88ab\u6211\u7528\u6307\u529b\u70b9\u5012\u3002\u201d \u3000\u3000\u89c1\u5979\u8fd9\u7b49\u6fc0\u52a8\u4e4b\u8272\uff0c\u4fbf\u53ef\u770b\u51fa\uff0c\u5f53\u65f6\u7684\u60c5\u666f\uff0c\u4f55\u7b49\u7684\u53eb\u4eba\u9707\u9a87\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u60f3\u5fc5\u8d24\u5f1f\u51fa\u624b\u4f24\u4eba\u4e4b\u540e\uff0c\u90a3\u4e9b\u4eba\u4ece\u56db\u9762\u516b\u65b9\u8d76\u4e86\u8fc7\u6765\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u53ef\u4e0d\u662f\uff1f\u5426\u5219\uff0c\u90a3\u5934\u9640\u4e5f\u9003\u4e0d\u4e86\u4ed6\u90a3\u72d7\u547d\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95ee\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u90a3\u516d\u4e03\u540d\u6b66\u6797\u4eba\u7269\u7684\u6b66\u529f\u5982\u4f55\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4e0d\u582a\u4e00\u51fb\uff01\u201d \u3000\u3000\u5979\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u5f53\u65f6\u90d1\u5927\u4f2f\u5df2\u7ecf\u88ab\u4eba\u5728\u547d\u7a74\u4e0a\u51fb\u4e86\u4e00\u638c\uff0c\u5012\u5730\u4e0d\u8d77\uff0c\u6545\u800c\u6211\u53ea\u6709\u5148\u6551\u90d1\u5927\u4f2f\u8981\u7d27\uff0c\u4e5f\u5c31\u65e0\u6cd5\u8ffd\u8d76\u90a3\u51f6\u50e7\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8f9b\u82e6\u8d24\u5f1f\u4e86\uff01\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u53c8\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u90a3\u5e99\u5b87\u4e2d\u7684\u6697\u5ba4\uff0c\u4f60\u53ef\u66fe\u5165\u5185\u67e5\u770b\u8fc7\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u770b\u8fc7\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u90a3\u662f\u4ec0\u4e48\u6240\u5728\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4e00\u5904\u5730\u7262\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u76b1\u7709\u9053\uff1a\u201c\u90a3\u91cc\u9762\u8fd8\u56da\u6709\u4eba\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u6709\uff01\u201d \u3000\u3000\u5979\u5ffd\u7136\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u5982\u679c\u4e0d\u662f\u90a3\u91cc\u9762\u56da\u5f97\u6709\u4eba\uff0c\u6211\u8fd8\u771f\u627e\u4e0d\u5230\u4eba\u628a\u90d1\u5927\u4f2f\u62ac\u56de\u6765\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u4e86\u4e00\u5446\u9053\uff1a\u201c\u83ab\u975e\u90a3\u4e09\u540d\u58ee\u6c49\u4e5f\u662f\u88ab\u56da\u5728\u5730\u7262\u4e2d\u4e4b\u4eba\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u53ef\u4e0d\u662f\uff1f\u6211\u653e\u4e86\u4ed6\u4eec\u51fa\u6765\uff0c\u5f53\u7136\u4e5f\u8981\u53eb\u4ed6\u4eec\u66ff\u6211\u505a\u4e00\u6869\u4e8b\u62a5\u507f\u5440\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u771f\u4e8f\u8d24\u5f1f\u60f3\u5f97\u51fa\u6765\uff01\u53ea\u4e0d\u77e5\u90a3\u5ea7\u5e99\u5b87\uff0c\u53ef\u6709\u4e3b\u6301\u4e4b\u4eba\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u597d\u50cf\u6ca1\u6709\uff0c\u4e0d\u8fc7\uff0c\u6211\u5df2\u7ecf\u653e\u4e86\u4e00\u628a\u706b\u628a\u5b83\u70e7\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u5f97\u4e00\u6014\u9053\uff1a\u201c\u70e7\u4e86\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u90a3\u5e99\u5b87\u516b\u6210\u662f\u4e94\u5927\u9b54\u4e3b\u5c5e\u4e0b\u4e4b\u4eba\u843d\u811a\u4e4b\u5904\uff0c\u70e7\u4e86\u5b83\u4e0d\u662f\u5f88\u597d\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u76b1\u7709\u9053\uff1a\u201c\u6740\u4eba\u653e\u706b\uff0c\u5c82\u662f\u6211\u7b49\u6240\u4e3a\uff1f\u8d24\u5f1f\uff0c\u4ee5\u540e\u53ef\u5343\u4e07\u4f7f\u4e0d\u5f97\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u767d\u4e86\u4ed6\u4e00\u773c\uff0c\u7b11\u9053\uff1a\u201c\u4e0b\u6b21\u4e0d\u6562\u4e86\uff01\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u53ea\u542c\u5f97\u90d1\u5927\u521a\u957f\u957f\u5730\u53d1\u51fa\u4e86\u4e00\u58f0\u4f4e\u53f9\u3002\u6562\u60c5\uff0c\u4ed6\u9192\u8f6c\u8fc7\u6765\u4e86\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u8fc5\u5feb\u5730\u8f6c\u8eab\uff0c\u8d70\u4e86\u8fc7\u53bb\uff0c\u4fef\u4e0b\u8eab\u5b50\uff0c\u5927\u58f0\u9053\uff1a\u201c\u90d1\u5927\u6015\u90d1\u5927\u521a\u7f13\u7f13\u5730\u7741\u5f00\u53cc\u76ee\uff0c\u76ef\u5728\u65b9\u96ea\u5b9c\u8eab\u4e0a\u826f\u4e45\uff0c\u8fd9\u624d\u4e4f\u529b\u5730\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u53ef\u662f\u745c\u513f\u8d24\u4f84\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u773c\u7736\u4e2d\u70ed\u6cea\u6d41\u8f6c\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u662f\u554a\uff01\u5c0f\u4f84\u6b63\u662f\u65b9\u745c\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u8138\u4e0a\u6709\u7740\u60ca\u559c\u4e4b\u8272\uff0c\u9053\uff1a\u201c\u8d24\u4f84\uff0c\u4f60\u957f\u5f97\u8fd9\u822c\u9ad8\u5927\uff0c\u53eb\u6211\u51e0\u4e4e\u8ba4\u4e0d\u51fa\u6765\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5927\u4f2f\uff0c\u4f60\u8001\u2026\u2026\u597d\u4e9b\u4e86\u5417\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u8fd9\u624d\u60f3\u8d77\u81ea\u5df1\u5df2\u7136\u8d1f\u4f24\u4e4b\u4e8b\uff0c\u8fde\u5fd9\u6389\u5934\u5411\u90a3\u6c57\u6d41\u6ee1\u9762\u7684\u5b8b\u5b5a\u5927\u58f0\u9053\uff1a\u201c\u591a\u8c22\u8fd9\u4f4d\u8001\u4eba\u5bb6\u76f8\u6551\u2026\u2026\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u8bdd\u97f3\u4e00\u987f\uff0c\u60ca\u9053\uff1a\u201c\u4f60\u2026\u2026\u83ab\u975e\u662f\u5b8b\u5144\uff1f\u2026\u2026\u201d \u3000\u3000\u6562\u60c5\uff0c\u4ed6\u4eec\u4e2d\u5dde\u4e09\u4fa0\u4e0e\u5b8b\u5b5a\u539f\u662f\u8001\u670b\u53cb\u3002 \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u8001\u5f1f\u8fd8\u8bb0\u5f97\u8001\u5077\u513f\u5417\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u4e00\u4f38\u624b\uff0c\u6309\u5728\u65b9\u96ea\u5b9c\u80a9\u5934\u4e4b\u4e0a\uff0c\u7f13\u7f13\u5730\u5750\u4e86\u8d77\u6765\uff0c\u7b11\u9053\uff1a\u201c\u5144\u5f1f\u5f53\u7136\u4e0d\u4f1a\u5fd8\u8bb0\u5b8b\u5144\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u8fd9\u65f6\u4e5f\u4f38\u624b\u6276\u4f4f\u4e86\u90d1\u5927\u521a\uff0c\u4e24\u4eba\u540c\u65f6\u7528\u529b\uff0c\u987f\u65f6\u5c06\u90d1\u5927\u521a\u67b6\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u9002\u65f6\u5730\u642c\u8fc7\u6765\u4e00\u53ea\u4e0b\u6709\u83b2\u5ea7\u7684\u84b2\u56e2\uff0c\u8ba9\u90a3\u90d1\u5927\u521a\u5750\u4e0b\u3002 \u3000\u3000\u90d1\u5927\u521a\u82e6\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8c22\u8c22\u5b8b\u5144\uff01\u201d\u4ed6\u8bf4\u4e86\u8fd9\u4e00\u53e5\u8bdd\uff0c\u4f3c\u662f\u751a\u4e3a\u5403\u529b\u7ade\u662f\u6df1\u6df1\u5730\u5598\u4e86\u4e24\u4e09\u53e3\u5927\u6c14\u3002 \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u90d1\u8001\u5f1f\uff0c\u5b8b\u67d0\u624d\u667a\u6709\u9650\uff0c\u4f60\u8fd9\u4f24\u52bf\uff0c\u53ea\u6015\u4e0d\u662f\u5b8b\u67d0\u6240\u80fd\u6108\u7684\u4e86\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u542c\u8bf4\u5b8b\u5b5a\u6cbb\u4e0d\u4e86\u81ea\u5df1\u4f24\u52bf\uff0c\u5e76\u65e0\u4e1d\u6beb\u5931\u671b\u4e4b\u8272\uff0c\u5374\u662f\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5b8b\u5144\u76db\u610f\uff0c\u5144\u5f1f\u5df2\u7136\u611f\u6fc0\u4e0d\u5c3d\uff0c\u6b7b\u751f\u7531\u547d\uff0c\u5bcc\u8d35\u5728\u5929\uff0c\u5144\u5f1f\u53ef\u4ece\u6765\u4e0d\u66fe\u6709\u8fc7\u5962\u60f3\uff0c\u5b8b\u5144\u4e0d\u7528\u4e3a\u5144\u5f1f\u7684\u4f24\u52bf\u803d\u5fc3\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u90d1\u8001\u5f1f\u7684\u8c6a\u8fbe\uff0c\u679c\u7136\u4e0d\u4e9a\u5f53\u5e74\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u65f6\u5374\u9053\uff1a\u201c\u5927\u4f2f\u7236\uff0c\u4f60\u7684\u4f24\u52bf\u5fc5\u53ef\u6cbb\u7597\u597d\u7684\uff01\u4f60\u8001\u4e0d\u7528\u803d\u5fc3\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u7b11\u9053\uff1a\u201c\u8d24\u4f84\uff0c\u4f60\u4ee5\u4e3a\u4f60\u7684\u90d1\u5927\u4f2f\u7236\u662f\u4e2a\u6015\u6b7b\u7684\u4eba\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u4e00\u60ca\uff0c\u5fd9\u9053\uff1a\u201c\u4f84\u6ca1\u6709\u8fd9\u4e48\u60f3\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u8fd9\u624d\u70b9\u4e86\u70b9\u5934\uff0c\u63a5\u9053\uff1a\u201c\u90a3\u5c31\u5bf9\u4e86\uff01\u4f60\u60f3\u60f3\uff0c\u6211\u5982\u8d2a\u751f\u6015\u6b7b\uff0c\u4e5f\u4e0d\u4f1a\u843d\u5f97\u8eab\u53d7\u91cd\u4f24\u7684\u4e86\uff01\u201d \u3000\u3000\u4ed6\u8bf4\u5230\u8fd9\u91cc\uff0c\u5ffd\u7136\u987f\u4e86\u4e00\u987f\u8bdd\u58f0\uff0c\u5411\u65b9\u96ea\u5b9c\u4f4e\u58f0\u9053\uff1a\u201c\u8d24\u4f84\uff0c\u662f\u8c01\u628a\u4e3a\u4f2f\u4ece\u90a3\u767b\u5c01\u6551\u6765\u5c11\u6797\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5b89\u59d1\u5a18\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u4e00\u6014\u9053\uff1a\u201c\u5b89\u59d1\u5a18\uff1f\u5979\uff1f\u2026\u2026\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u6562\u60c5\uff0c\u90d1\u5927\u521a\u5374\u77a7\u4e0d\u51fa\u5b89\u5c0f\u840d\u7684\u672c\u6765\u9762\u76ee\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u6307\u7740\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u5c31\u662f\u5979\u554a\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u8fd9\u65f6\u5df2\u8d70\u4e86\u8fc7\u6765\uff0c\u62b1\u62f3\u9053\uff1a\u201c\u665a\u8f88\u5b89\u5c0f\u840d\uff0c\u89c1\u8fc7\u90d1\u5927\u4fa0\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u6b20\u4e86\u6b20\u8eab\u5b50\uff0c\u60f3\u5750\u8d77\u6765\u8fd8\u793c\uff0c\u4f46\u4ed6\u5374\u88ab\u65b9\u96ea\u5b9c\u6309\u4f4f\uff0c\u4e00\u6025\u4e4b\u4e0b\uff0c\u4e0d\u7981\u8fde\u8fde\u7684\u53d1\u51fa\u4e86\u51e0\u58f0\u5e72\u54b3\uff0c\u53eb\u9053\uff1a\u201c\u4e0d\u6562\u2026\u2026\u6551\u547d\u4e4b\u6069\uff0c\u90d1\u5927\u521a\u7ec8\u8eab\u4e0d\u6562\u7a0d\u5fd8\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u770b\u4e86\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u7b11\u9053\uff1a\u201c\u90d1\u5927\u4f2f\uff0c\u665a\u8f88\u600e\u6562\u65bd\u6069\u56fe\u62a5\u5462\uff1f\u4f60\u8001\u592a\u5ba2\u6c14\u5566\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u957f\u957f\u5730\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u8eab\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u745c\u513f\uff0c\u8fd9\u51e0\u5e74\uff0c\u4f60\u90fd\u5728\u54ea\u91cc\uff1f\u77a7\u4f60\u8fd9\u7b49\u795e\u60c5\uff0c\u60f3\u662f\u62dc\u5728\u90a3\u4f4d\u9ad8\u4eba\u95e8\u4e0b\uff0c\u5b66\u6765\u4e00\u8eab\u6b66\u529f\u7684\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fd9\u9053\uff1a\u201c\u5927\u4f2f\u79ef\u6781\u517b\u8eab\u4f53\u8981\u7d27\uff0c\u8fd9\u4e9b\u4e8b\u6539\u65e5\u518d\u8c08\u4e5f\u4e0d\u8fdf\u554a\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u76b1\u7709\u9053\uff1a\u201c\u600e\u4e48\u4e86\uff1f\u4f60\u771f\u628a\u6211\u5f53\u4f5c\u90a3\u4e0d\u4e2d\u7528\u7684\u8001\u4eba\u4e86\uff1f\u745c\u513f\uff0c\u5176\u5b9e\u6211\u624d\u4e94\u5341\u4e0d\u5230\uff0c\u6bd4\u5b8b\u5144\u8fd8\u5dee\u5f97\u5f88\u8fdc\u5462\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u5927\u4f2f\uff0c\u6211\u53ef\u4e0d\u66fe\u8fd9\u4e48\u60f3\uff01\u53ea\u56e0\u5927\u4f2f\u4f24\u75c5\u672a\u6108\u4e4b\u524d\uff0c\u4f3c\u662f\u4e0d\u7528\u591a\u64cd\u5fc3\u7684\u4e86\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u53ea\u89c1\u90a3\u5c11\u6797\u638c\u95e8\u4eba\u5165\u4e91\u5927\u5e08\u5df2\u7136\u8d70\u4e86\u8fdb\u6765\uff01 \u3000\u3000\u4ed6\u53cc\u624b\u5408\u5341\uff0c\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\uff0c\u90d1\u5927\u4fa0\u7684\u4f24\u52bf\uff0c\u53ef\u80af\u5bb9\u8001\u8872\u77a7\u77a7\u5417\uff1f\u201d \u3000\u3000\u6562\u60c5\uff0c\u4ed6\u5728\u65b9\u4e08\u9759\u5ba4\u4e4b\u4e2d\uff0c\u542c\u5f97\u5bfa\u4e2d\u50e7\u4fa3\u62a5\u4fe1\uff0c\u65b9\u624d\u8d76\u6765\u5ba2\u5802\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u600e\u6562\u52b3\u52a8\u638c\u95e8\u4eba\u2026\u2026\u201d \u3000\u3000\u4eba\u4e91\u5927\u5e08\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\u5ba2\u6c14\u4e86\uff01\u201d \u3000\u3000\u4e3e\u6b65\u4fbf\u5411\u90d1\u5927\u521a\u8d70\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u90d1\u5927\u521a\u6b20\u4e86\u6b20\u8eab\u5b50\uff0c\u7b11\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u90d1\u67d0\u4e43\u662f\u4e00\u4e2a\u7c97\u4fd7\u7684\u6b66\u592b\uff0c\u60ca\u52a8\u4f5b\u9a7e\uff0c\u5b9e\u662f\u4e0d\u5b89\u5f97\u5f88\uff0c\u53c8\u600e\u80fd\u9ebb\u70e6\u638c\u95e8\u66ff\u90d1\u67d0\u6cbb\u7597\u4f24\u75db\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u90a3\u5165\u4e91\u5927\u5e08\u5df2\u5fae\u5fae\u4e00\u7b11\uff0c\u6293\u8d77\u4ed6\u7684\u53f3\u624b\u5207\u8109\u51dd\u795e\u3002 \u3000\u3000\u8fc7\u4e86\u7ea6\u6709\u76cf\u8336\u65f6\u5206\uff0c\u53ea\u89c1\u5165\u4e91\u5927\u5e08\u7709\u5934\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u65bd\u4e3b\u4f24\u5f97\u4e0d\u8f7b\uff0c\u6c14\u8109\u865a\u6563\uff0c\u8840\u8c61\u4e0d\u5b81\uff0c\u83ab\u975e\u88ab\u4eba\u7528\u4ec0\u4e48\u5de5\u592b\u4f24\u4e86\u6c14\u8109\u8840\u6d77\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5728\u65c1\u7b11\u9053\uff1a\u201c\u90d1\u5927\u4fa0\u4e43\u662f\u88ab\u4eba\u6697\u4e2d\u4e00\u638c\uff0c\u51fb\u5728\u547d\u95e8\u7a74\u4e0a\u3002\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u70b9\u5934\u9053\uff1a\u201c\u8fd9\u5c31\u96be\u602a\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u63a5\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u6211\u90d1\u5927\u4f2f\u7236\u7684\u4f24\u52bf\uff0c\u53ef\u6709\u529e\u6cd5\u6cbb\u6108\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u7b11\u9053\uff1a\u201c\u4f24\u52bf\u867d\u91cd\uff0c\u4f46\u5374\u5e76\u975e\u4e0d\u6cbb\u4e4b\u75c7\uff01\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u56de\u8f6c\u8eab\u6765\uff0c\u5411\u7ad9\u4e2d\u95e8\u5916\u7684\u77e5\u5ba2\u5927\u5e08\u6cd5\u56e0\u9053\uff1a\u201c\u5feb\u53bb\u5411\u76d1\u5bfa\u5927\u5e08\u5904\uff0c\u53d6\u4e00\u9897\u4e5d\u8f6c\u5927\u8fd8\u91d1\u4e39\u4ea4\u7ed9\u672c\u5ea7\u3002\u201d \u3000\u3000\u90a3\u6cd5\u56e0\u5927\u5e08\u95fb\u8a00\uff0c\u5148\u662f\u6123\u4e86\u4e00\u695e\uff0c\u4f46\u5f88\u5feb\u5730\u5e94\u4e86\u4e00\u58f0\uff0c\u201c\u5f1f\u5b50\u9075\u547d\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u8fd9\u65f6\u5374\u957f\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u8fd9\u4e5d\u8f6c\u5927\u8fd8\u91d1\u4e39\uff0c\u4e43\u662f\u5c11\u6797\u9547\u5c71\u4e09\u5b9d\u4e4b\u4e00\uff0c\u90d1\u5927\u4fa0\u7684\u4f24\u52bf\uff0c\u80fd\u6709\u4e5d\u8f6c\u91d1\u4e39\u6cbb\u7597\uff0c\u90a3\u662f\u6307\u65e5\u53ef\u6108\u7684\u4e86\uff01\u201d \u3000\u3000\u4ed6\u6545\u610f\u53eb\u51fa\u4e5d\u8f6c\u91d1\u4e39\u4e43\u662f\u5c11\u6797\u9547\u5c71\u4e4b\u5b9d\uff0c\u4e0d\u557b\u6697\u793a\u65b9\u96ea\u5b9c\uff0c\u5165\u4e91\u5927\u5e08\u662f\u4f55\u7b49\u770b\u91cd\u4e8e\u4ed6\u7684\u4e86\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u5f97\u5fc3\u4e2d\u5927\u5927\u4e00\u9707\u3002 \u3000\u3000\u4ed6\u8fde\u5fd9\u5411\u5165\u4e91\u5927\u5e08\u62b1\u62f3\u4e00\u793c\uff0c\u9053\uff1a\u201c\u5927\u5e08\u5bf9\u90d1\u5927\u4f2f\u7684\u6069\u60e0\u665a\u8f88\u662f\u611f\u540c\u8eab\u53d7\u4e86\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u7b11\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\u4e3a\u672c\u5bfa\u4e4b\u4e8b\uff0c\u4ed8\u51fa\u4e86\u83ab\u5927\u7684\u5fc3\u8840\uff0c\u8001\u8872\u4e3a\u5c0f\u65bd\u4e3b\u6548\u52b3\uff0c\u90a3\u4e5f\u662f\u5206\u5185\u4e4b\u4e8b\u554a\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u90a3\u6cd5\u56e0\u5927\u5e08\uff0c\u4e1a\u5df2\u5927\u6b65\u8d70\u4e86\u56de\u6765\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u9012\u4e86\u4e00\u4e2a\u5c0f\u5c0f\u7684\u74f7\u74f6\u7ed9\u4e86\u4eba\u4e91\u5927\u5e08\uff0c\u5165\u4e91\u5927\u5e08\u7acb\u5373\u63a5\u4e86\u8fc7\u53bb\uff0c\u5e76\u4ece\u4e2d\u5012\u51fa\u7ea6\u6709\u9f99\u773c\u5927\u5c0f\u7684\u9ed1\u8272\u4e39\u4e5d\uff0c\u987a\u624b\u5c06\u74f7\u74f6\u4ea4\u8fd8\u6cd5\u56e0\u77e5\u5ba2\uff0c\u5c06\u90a3\u9897\u4e39\u4e5d\uff0c\u585e\u8fdb\u4e86\u90d1\u5927\u521a\u7684\u53e3\u4e2d\u3002 \u3000\u3000\u5b8b\u5b5a\u5728\u65c1\u7b11\u9053\uff1a\u201c\u4e5d\u8f6c\u91d1\u4e39\uff0c\u4e43\u662f\u4f24\u79d1\u5723\u836f\uff0c\u53ef\u60dc\u7684\u662f\uff0c\u5c11\u6797\u4f3c\u662f\u53ea\u6b64\u4e00\u9897\u4e86\u5427\uff01\u201d \u3000\u3000\u539f\u6765\u4ed6\u77a7\u5230\u90a3\u5165\u4e91\u5927\u5e08\u5012\u53bb\u4e39\u4e5d\u4e4b\u65f6\uff0c\u74f6\u5e95\u4e1a\u5df2\u671d\u4e0a\u4e86\uff01 \u3000\u3000\u5165\u53bb\u5927\u5e08\u5374\u662f\u4e00\u7b11\u9053\uff1a\u201c\u8001\u65bd\u4e3b\uff0c\u4f60\u9519\u4e86\uff01\u8fd9\u4e5d\u8f6c\u5927\u8fd8\u91d1\u4e39\uff0c\u5176\u6709\u4e24\u79cd\uff0c\u4f20\u95fb\u4e2d\u7684\u4e00\u79cd\uff0c\u53ea\u662f\u6b66\u6797\u4e2d\u670b\u53cb\u7684\u8bef\u4f20\uff0c\u5176\u5b9e\uff0c\u91d1\u4e39\u867d\u7136\u96be\u4ee5\u70bc\u5236\uff0c\u4f46\u836f\u65b9\u5e76\u672a\u5931\u4f20\u3002\u81f3\u4e8e\u90a3\u8bef\u4f20\u7684\u4e00\u79cd\uff0c\u5219\u662f\u6839\u672c\u6ca1\u6709\u7684\u4e8b\uff0c\u4ee5\u8bb9\u4f20\u8bb9\u7684\u7ed3\u679c\uff0c\u5c31\u628a\u8fd9\u79cd\u91d1\u4e39\uff0c\u5f62\u6210\u672c\u5bfa\u4e09\u5b9d\u4e4b\u4e00\uff0c\u5176\u5b9e\uff0c\u4e0d\u7792\u65bd\u4e3b\u8bf4\uff0c\u4e5d\u8f6c\u91d1\u4e39\u7684\u529f\u6548\u867d\u7136\u597d\uff0c\u4f46\u4e5f\u4e0d\u5982\u4f20\u95fb\u4e4b\u751a\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u539f\u6765\u5916\u95f4\u4f20\u95fb\u5931\u5b9e\u5417\uff1f\u90a3\u5c31\u96be\u602a\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u5411\u6cd5\u56e0\u77e5\u5ba2\u770b\u4e86\u4e00\u773c\uff0c\u63a5\u9053\uff1a\u201c\u90d1\u65bd\u4e3b\u670d\u836f\u4e4b\u540e\uff0c\u5e94\u8be5\u8c03\u606f\u4e00\u756a\uff0c\u4f60\u53ef\u5feb\u5c06\u5ba2\u623f\u5355\u72ec\u6536\u62fe\u597d\u4e00\u95f4\uff0c\u597d\u8ba9\u90d1\u65bd\u4e3b\u4f11\u6b47\u3002\u201d \u3000\u3000\u6cd5\u56e0\u77e5\u5ba2\u5408\u5341\u9053\uff1a\u201c\u5f1f\u5b50\u65e9\u5df2\u51c6\u5907\u597d\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u5fd9\u548c\u5b8b\u5b5a\u6276\u8d77\u90d1\u5927\u521a\uff0c\u968f\u7740\u77e5\u5ba2\u6cd5\u56e0\u5927\u5e08\u53bb\u81f3\u5de6\u53a2\u7684\u7985\u623f\u3002 \u3000\u3000\u90d1\u5927\u521a\u670d\u4e0b\u4e5d\u8f6c\u5927\u8fd8\u91d1\u4e39\u4ee5\u540e\uff0c\u8138\u8272\u5df2\u7136\u4e0d\u518d\u90a3\u7b49\u82cd\u767d\uff0c\u6276\u5165\u7985\u623f\u4ee5\u540e\uff0c\u5c31\u95ed\u76ee\u5750\u5728\u7985\u5e8a\u4e4b\u4e0a\u8c03\u606f\u3002 \u3000\u3000\u5b8b\u5b5a\u770b\u4e86\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u54b1\u4eec\u5230\u5916\u9762\u53bb\u7b49\u5427\uff01\u4e5f\u8bb8\u4ed6\u8fd9\u4e00\u987f\u8c03\u606f\uff0c\u8981\u82b1\u4e0a\u51e0\u4e2a\u65f6\u8fb0\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u70b9\u4e86\u70b9\u5934\uff0c\u4e24\u4eba\u8f7b\u624b\u8f7b\u811a\u5730\u8d70\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5165\u4e91\u5927\u5e08\u5728\u95e8\u5916\u76f8\u5019\uff0c\u89c1\u4e24\u4eba\u51fa\u6765\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u9910\u5802\u5df2\u7136\u6446\u4e0b\u4e86\u9152\u83dc\uff0c\u51e0\u4f4d\u65bd\u4e3b\u4e5f\u8be5\u7528\u4e00\u4e9b\u70b9\u5fc3\u4e86\uff01\u201d \u3000\u3000\u4e00\u884c\u4eba\u8f6c\u81f3\u9910\u5802\uff0c\u53ea\u89c1\u5e84\u6377\u65e9\u5728\u90a3\u91cc\u76f8\u5019\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u773c\u77a7\u89c1\u5e84\u6377\uff0c\u4e0d\u7981\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5e84\u5144\uff0c\u8fd9\u51e0\u65e5\u4f60\u90a3\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u5e84\u6377\u7b11\u9053\uff1a\u201c\u5144\u5f1f\u53bb\u4e86\u4e00\u8d9f\u5173\u6d1b\uff0c\u53ea\u56e0\u4e34\u884c\u5306\u5fd9\uff0c\u6240\u4ee5\u672a\u5411\u65b9\u5144\u8a00\u660e\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u5e84\u5144\u6b64\u884c\uff0c\u5fc5\u6709\u6240\u4e3a\u7684\u4e86\uff01\u201d \u3000\u3000\u5e84\u6377\u7b11\u9053\uff1a\u201c\u5949\u547d\u884c\u4e8b\uff0c\u4e0d\u8fc7\uff0c\u603b\u7b97\u4e0d\u865a\u6b64\u884c\u2026\u2026\u201d \u3000\u3000\u8bed\u97f3\u987f\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u65b9\u5144\uff0c\u90a3\u9ad8\u65e0\u654c\u773c\u4e0b\u5df2\u7136\u6765\u5230\u6cb3\u5357\u5883\u5185\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u6014\u9053\uff1a\u201c\u4e0d\u662f\u7ae5\u5b50\u5947\u8981\u4ed6\u6765\u7684\u5417\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u4e0d\u662f\uff01\u201d \u3000\u3000\u8bdd\u97f3\u5fae\u4e00\u987f\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u4e00\u56de\u9ad8\u65e0\u654c\u524d\u6765\uff0c\u4e43\u662f\u4e3a\u4e86\u65b9\u5144\u4e4b\u6545\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u6014\u9053\uff1a\u201c\u4e3a\u4e86\u5144\u5f1f\u5417\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u6b63\u662f\u5982\u6b64\u2026\u2026\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u5165\u4e91\u5927\u5e08\u5df2\u7136\u8bf7\u4e86\u4f17\u4eba\u5165\u5ea7\uff0c\u7b11\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\uff0c\u6709\u4ec0\u4e48\u8bdd\uff0c\u54b1\u4eec\u8fb9\u5403\u8fb9\u8c08\u5427\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\u4e86\u8c22\uff0c\u5403\u4e86\u51e0\u53e3\uff0c\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u5e84\u5144\uff0c\u90a3\u9ad8\u65e0\u654c\u4e3a\u5c0f\u5f1f\u7684\u4e8b\u800c\u6765\uff1f\u201d \u3000\u3000\u5e84\u6377\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u5144\u5f1f\u5728\u6d1b\u9633\u9047\u5230\u9ad8\u5144\uff0c\u77a7\u4ed6\u90a3\u7b49\u60c5\u51b5\uff0c\u663e\u7136\u662f\u5403\u4e86\u7ae5\u5b50\u5947\u5f88\u5927\u7684\u6392\u5934\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e84\u5144\u5dee\u522b\u8fc7\u4ed6\u5417\uff1f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u6b64\u4e8b\u8bf4\u6765\u8bdd\u957f\uff0c\u65b9\u5144\u60f3\u5fc5\u8fd8\u8bb0\u5f97\u5728\u90a3\u7530\u8fdc\u7684\u5de2\u7a74\u4e4b\u4e2d\uff0c\u52a9\u90a3\u9ad8\u65e0\u654c\u548c\u827e\u4e1c\u6d77\u51fb\u9000\u67f3\u5a9a\u5a18\u4e4b\u4e8b\u5427\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f53\u7136\u8bb0\u5f97\uff01\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u5173\u5065\u5c31\u662f\u51fa\u5728\u8fd9\u91cc\u4e86\u3002\u201d \u3000\u3000\u4ed6\u53f9\u4e86\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u90a3\u827e\u4e1c\u6d77\u7adf\u5728\u7ae5\u5b50\u5947\u7684\u9762\u524d\u642c\u5f04\u662f\u975e\uff0c\u4e00\u53e3\u54ac\u5b9a\u9ad8\u65e0\u654c\u662f\u65b9\u5144\u7684\u5185\u5e94\u5978\u7ec6\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u6123\u9053\uff1a\u201c\u6709\u8fd9\u7b49\u4e8b\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u4e5f\u5fcd\u4e0d\u4f4f\u7b11\u9053\uff1a\u201c\u827e\u4e1c\u6d77\u8001\u5144\uff0c\u6df7\u5e10\u5f97\u5f88\uff0c\u770b\u6765\u4ed6\u662f\u5bb9\u4e0d\u4e0b\u90a3\u4f4d\u795e\u529b\u5929\u76ee\u9ad8\u65e0\u654c\u4e86\uff01\u201d \u3000\u3000\u5e84\u6377\u7b11\u9053\uff1a\u201c\u6b63\u662f\u5982\u6b64\uff0c\u90a3\u7ae5\u5b50\u5947\u867d\u7136\u5bf9\u9ad8\u65e0\u654c\u5341\u5206\u559c\u7231\uff0c\u4f46\u7981\u4e0d\u4f4f\u827e\u4e1c\u6d77\u4ece\u4e2d\u6311\u62e8\uff0c\u7ec8\u4e8e\u4e00\u6012\u4e4b\u4e0b\uff0c\u51e0\u4e4e\u662f\u5c06\u9ad8\u65e0\u654c\u7684\u4e00\u8eab\u6b66\u529f\u6bc1\u53bb\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u4e00\u5446\u9053\uff1a\u201c\u90a3\u50bb\u5c0f\u5b50\u53d7\u4f24\u4e86\uff1f\u201d \u3000\u3000\u5e84\u6377\u7b11\u9053\uff1a\u201c\u76ae\u8089\u8fd9\u4f24\uff0c\u65e0\u5173\u7d27\u8981\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u636e\u9ad8\u5144\u76f8\u544a\uff0c\u7ae5\u5b50\u5947\u76db\u6012\u4e4b\u4e0b\uff0c\u672c\u662f\u8981\u5c06\u4ed6\u5e9f\u53bb\u6b66\u529f\uff0c\u4f46\u4e0d\u77e5\u4e3a\u4f55\u4e0b\u624b\u4e4b\u65f6\uff0c\u5c45\u7136\u672a\u66fe\u4f7f\u51fa\u5185\u529b\uff0c\u662f\u4ee5\uff0c\u5728\u8868\u9762\u770b\u4e0a\u53bb\uff0c\u9ad8\u65e0\u654c\u5f53\u65f6\u5df2\u6b66\u529f\u5168\u5e9f\uff0c\u4f46\u4e8b\u5b9e\u4e0a\u53ea\u662f\u7565\u53d7\u76ae\u8089\u4e4b\u4f24\u800c\u5df2\uff01\u201d \u3000 \u7b2c\u4e09\u5341\u4e00\u56de\u3000\u4e4d\u95fb\u5669\u8017 \u3000 \u3000\u3000\u5b8b\u5b5a\u542c\u4e86\u5e84\u6377\u7684\u53d9\u8ff0\uff0c\u5927\u7b11\u9053\uff1a\u201c\u7ae5\u5b50\u5947\u600e\u5730\u6709\u4e86\u5584\u5fc3\uff1f\u83ab\u975e\u4e43\u662f\u82e6\u8089\u8ba1\u5417\uff1f\u201d \u3000\u3000\u5e84\u6377\u7b11\u9053\uff1a\u201c\u4e0d\u4f1a\uff01\u5018\u82e5\u771f\u662f\u82e6\u8089\u4e4b\u8ba1\uff0c\u4ed6\u5c31\u4e0d\u4f1a\u4e0b\u624b\u7559\u60c5\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e84\u5144\uff0c\u90a3\u9ad8\u65e0\u654c\u5f53\u771f\u662f\u4e3a\u4e86\u5144\u5f1f\u4e4b\u4e8b\uff0c\u6765\u5230\u6d1b\u9633\u5417\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u9ad8\u5144\u5bf9\u6211\u8bf4\u8fc7\uff0c\u4ed6\u786e\u662f\u4e3a\u4e86\u65b9\u5144\u5f1f\u4e4b\u4e8b\u800c\u6765\uff0c\u53ea\u56e0\u4ed6\u6b64\u523b\u5df2\u7136\u79bb\u5f00\u90a3\u7ae5\u5b50\u5947\u7684\u56db\u7075\u6559\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4ed6\u53ef\u662f\u8981\u627e\u5144\u5f1f\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e0d\u8fc7\uff0c\u773c\u4e0b\u4ed6\u8fd8\u4e0d\u80fd\u6765\u6b64\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6c89\u541f\u9053\uff1a\u201c\u4ed6\u65e2\u662f\u60f3\u627e\u6211\uff0c\u4e3a\u4ec0\u4e48\u773c\u4e0b\u53c8\u4e0d\u80af\u524d\u6765\u5c11\u6797\uff1f\u201d \u3000\u3000\u5e84\u6377\u7b11\u9053\uff1a\u201c\u65b9\u5144\uff0c\u90a3\u7a0b\u5b50\u671b\u7a0b\u5e84\u4e3b\uff0c\u65b9\u5144\u8fd8\u8bb0\u5f97\u4ed6\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f53\u7136\u8bb0\u5f97\uff01\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u9ad8\u5144\u773c\u4e0b\u6b63\u548c\u7a0b\u5e84\u4e3b\u8d70\u5728\u4e00\u9053\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u54e6\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u4eec\u522b\u6709\u6240\u56fe\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u636e\u9ad8\u5144\u544a\u77e5\uff0c\u4ed6\u548c\u7a0b\u5e84\u4e3b\u6b63\u5728\u8fdb\u884c\u4e00\u6869\u751a\u662f\u795e\u79d8\u4e4b\u4e8b\uff0c\u4e8b\u60c5\u672a\u66fe\u5206\u6653\u4e4b\u524d\uff0c\u4e0d\u613f\u6765\u6b64\u76f8\u89c1\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6014\u4e86\u4e00\u6014\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u5c31\u602a\u4e86\uff01\u4ed6\u4eec\u5982\u662f\u6709\u4ec0\u4e48\u4e0e\u4eba\u6709\u5173\u4e4b\u4e8b\u5728\u6697\u4e2d\u8fdb\u884c\uff0c\u53c8\u600e\u5730\u4e0d\u80af\u5148\u884c\u544a\u77e5\u4e8e\u6211\u5462\uff1f\u96be\u9053\u90a3\u771f\u662f\u4ec0\u4e48\u5bf9\u6211\u4e0d\u5229\u4e4b\u4e8b\u5417\uff1f\u201d\u5fc3\u4e2d\u98de\u5feb\u5730\u8f6c\u52a8\u7740\u5ff5\u5934\uff0c\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u5e84\u5144\u53ef\u66fe\u63a2\u542c\u8fc7\u4ed6\u4eec\u53e3\u6c14\uff1f\u201d \u3000\u3000\u5e84\u6377\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u65e2\u662f\u4e0d\u613f\u8bf4\u51fa\u6765\uff0c\u591a\u95ee\u4e5f\u662f\u6789\u7136\uff0c\u662f\u4ee5\u5144\u5f1f\u5e76\u672a\u8ffd\u6839\u7a76\u5e95\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u60f3\u4e86\u4e00\u60f3\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u4e8b\u5012\u628a\u5144\u5f1f\u5f04\u7cca\u6d82\u4e86\uff01\u201d \u3000\u3000\u5e84\u6377\u7b11\u9053\uff1a\u201c\u65b9\u5144\u4e0d\u5fc5\u6302\u5728\u5fc3\u4e0a\uff01\u636e\u5144\u5f1f\u770b\u6765\uff0c\u4ed6\u4eec\u5e76\u65e0\u4e0d\u5229\u65b9\u5144\u4e4b\u5fc3\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u4f46\u613f\u5982\u6b64\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u8fd9\u65f6\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u5e84\u8001\u5f1f\uff0c\u4f60\u524d\u53bb\u6d1b\u9633\uff0c\u53c8\u4e3a\u4e86\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5e84\u6377\u770b\u4e86\u5165\u4e91\u5927\u5e08\u4e00\u773c\uff0c\u201c\u665a\u8f88\u4e43\u662f\u5949\u4e86\u638c\u95e8\u4eba\u4e4b\u547d\u524d\u53bb\u2026\u2026\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u6de1\u6de1\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u8001\u8872\u70e6\u52b3\u5e84\u65bd\u4e3b\u53bb\u5230\u6d1b\u9633\uff0c\u4e43\u662f\u60f3\u6253\u63a2\u4e2d\u5dde\u4e09\u4fa0\u7684\u4e0b\u843d\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u611f\u6fc0\u5730\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u4e3a\u4e86\u65b9\u67d0\u4e4b\u4e8b\uff0c\u8981\u638c\u95e8\u4eba\u548c\u5e84\u5144\u8d39\u5fc3\uff0c\u5f53\u771f\u7f6a\u8fc7\u5f97\u5f88\uff01\u201d \u3000\u3000\u5e84\u633a\u95fb\u8a00\uff0c\u5374\u662f\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u6709\u4e00\u6869\u4e8b\uff0c\u53ea\u6015\u5144\u5f1f\u8bf4\u51fa\u540e\uff0c\u65b9\u5144\u5fc5\u5c06\u4e0d\u4e50\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u53ef\u662f\u6709\u5173\u4e2d\u5dde\u4e09\u4fa0\u7684\u4e8b\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e2d\u5dde\u4e09\u4fa0\u4e2d\u7684\u90d1\u5927\u4f2f\uff0c\u5df2\u7136\u6765\u5728\u5c11\u6797\uff0c\u5e84\u5144\u60f3\u5fc5\u77e5\u6653\u4e86\uff01\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u5144\u5f1f\u9002\u624d\u5df2\u7136\u542c\u8bf4\uff01\u201d \u3000\u3000\u4ed6\u987f\u4e00\u987f\u8bdd\u97f3\uff0c\u63a5\u9053\uff1a\u201c\u4f46\u5144\u5f1f\u8981\u8bf4\u7684\uff0c\u5374\u662f\u77f3\u4e09\u4fa0\u548c\u65b9\u4e8c\u4fa0\u592b\u5987\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8138\u8272\u4e00\u53d8\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4ed6\u4eec\u600e\u6837\u4e86\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u636e\u5c0f\u5f1f\u6253\u542c\u6240\u77e5\uff0c\u65b9\u4e8c\u4fa0\u548c\u77f3\u4e09\u4fa0\u4f3c\u4e4e\u5ffd\u7136\u5728\u4eba\u4e16\u4e4b\u95f4\uff0c\u5931\u53bb\u4e86\u8e2a\u8ff9\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u53ef\u80fd\u5427\uff01\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u5144\u5f1f\u539f\u4e5f\u62b1\u7740\u8fd9\u7b49\u60f3\u6cd5\uff0c\u8ba4\u4e3a\u4ed6\u4eec\u4e0d\u53ef\u80fd\u5ffd\u7136\u5931\u53bb\u6d88\u606f\uff0c\u4f46\u662f\uff0c\u7ed3\u679c\u5374\u53eb\u5144\u5f1f\u4e0d\u5f97\u4e0d\u76f8\u4fe1\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u653e\u4e0b\u624b\u4e2d\u7af9\u7b77\uff0c\u7ad9\u4e86\u8d77\u6765\u9053\uff1a\u201c\u5e84\u5144\u5fc5\u6709\u53d1\u73b0\u7684\u4e86\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5144\u5f1f\u786e\u662f\u67e5\u51fa\u4e00\u4e9b\u86db\u4e1d\u9a6c\u8ff9\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e84\u5144\u5feb\u8bb2\u2026\u2026\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u5144\u5f1f\u5728\u6d1b\u9633\u66fe\u7ecf\u6309\u7167\u5e2e\u4e3b\u624b\u8c15\uff0c\u5bfb\u5230\u4e86\u4e10\u5e2e\u8bbe\u5728\u6d1b\u9633\u7684\u5206\u5802\uff0c\u6211\u89c1\u4e86\u5206\u5802\u4e3b\u6c60\u5929\u548c\u524d\u8f88\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e0d\u7981\u6389\u5934\uff0c\u5411\u5750\u5728\u4e00\u4fa7\u5fae\u7b11\u7684\u970d\u5e2e\u4e3b\u671b\u53bb\u3002 \u3000\u3000\u970d\u545c\u98ce\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u4e14\u542c\u5e84\u8001\u5f1f\u8bf4\u4e0b\u53bb\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6ca1\u6709\u63d2\u53e3\uff0c\u5374\u542c\u5f97\u5e84\u6377\u63a5\u9053\uff1a\u201c\u5144\u5f1f\u5bf9\u90a3\u6c60\u524d\u8f88\u8bf4\u660e\u6765\u610f\u4e4b\u540e\uff0c\u4ed6\u7adf\u7136\u5446\u4e86\u534a\u664c\uff0c\u6ca1\u6709\u5f00\u53e3\u8bf4\u8bdd\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u6014\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u636e\u6c60\u5802\u4e3b\u76f8\u544a\uff0c\u90a3\u65b9\u5929\u6210\u4e8c\u4fa0\uff0c\u548c\u77f3\u4fca\u4e09\u4fa0\uff0c\u65e9\u5728\u4e09\u5e74\u4e4b\u524d\uff0c\u4fbf\u5df2\u8fc7\u4e16\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u600e\u4e48\u4f1a\uff1f\u201d\u4ed6\u5ffd\u7136\u5251\u7709\u4e00\u626c\uff0c\u63a5\u9053\uff1a\u201c\u6211\u90a3\u4e8c\u5a76\u548c\u73de\u5144\u5f1f\u5462\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u6700\u4e3a\u4ee4\u4eba\u4e0d\u89e3\u4e4b\u5904\u4e86\uff01\u636e\u6c60\u5802\u4e3b\u76f8\u544a\uff0c\u5f53\u65e5\u4e10\u5e2e\u95e8\u4e0b\u5b50\u5f1f\uff0c\u53ea\u89c1\u5230\u4e86\u65b9\u4e8c\u4fa0\u548c\u77f3\u4e09\u4fa0\u4e24\u4eba\u7684\u884c\u8e2a\uff0c\u4f46\u5374\u5e76\u672a\u89c1\u5230\u65b9\u592b\u4eba\u548c\u65b9\u516c\u5b50\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u987f\u65f6\u5446\u4e86\u534a\u664c\uff0c\u5411\u5e84\u6377\u9053\uff1a\u201c\u5e84\u5144\uff0c\u90a3\u6c60\u5802\u4e3b\u600e\u77e5\u6211\u77f3\u4e09\u53d4\u5df2\u6b7b\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u4ed6\u4eec\u7684\u540e\u4e8b\u4e43\u662f\u4e10\u5e2e\u5f1f\u5b50\u4ee3\u4e3a\u6599\u7406\uff0c\u81ea\u7136\u662f\u4e0d\u4f1a\u9519\u7684\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5ffd\u7136\u5927\u58f0\u9053\uff1a\u201c\u6211\u4e0d\u4fe1\uff01\u201d\u9661\u8f6c\u8eab\u5b50\uff0c\u4fbf\u5411\u658b\u5802\u4e4b\u5916\u5954\u53bb\u3002\u5b89\u5c0f\u840d\u7c89\u8138\u4e4b\u4e0a\u5145\u6ee1\u4e86\u60ca\u8bb6\u4e4b\u8272\uff0c\u5c16\u53eb\u4e00\u58f0\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u5979\u4e5f\u8d77\u8eab\u8ddf\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u4eec\u8fd9\u4e48\u4e00\u8d70\uff0c\u987f\u65f6\u8fde\u5165\u4e91\u5927\u5e08\u4e5f\u6709\u4e9b\u7740\u4e86\u614c\uff0c\u5168\u90e8\u8ddf\u5728\u4ed6\u4eec\u8eab\u540e\u884c\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u65b9\u96ea\u5b9c\u4e00\u76f4\u5954\u5411\u4e86\u90a3\u90d1\u5927\u521a\u8c03\u606f\u7684\u7985\u623f\uff0c\u63a8\u5f00\u4e86\u5b88\u5728\u95e8\u5916\u7684\u5c0f\u6c99\u5f25\uff0c\u5f00\u95e8\u8d70\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u6562\u60c5\uff0c\u4ed6\u4e43\u662f\u60f3\u5411\u90d1\u5927\u521a\u67e5\u8bc1\u90a3\u5e84\u6377\u4e4b\u8a00\uff0c\u662f\u5426\u5c5e\u5b9e\u3002 \u3000\u3000\u5e78\u800c\u5b89\u5c0f\u840d\u7684\u6b65\u5c65\u6781\u5feb\uff0c\u5c31\u5728\u65b9\u96ea\u5b9c\u8d70\u5230\u90d1\u5927\u521a\u8eab\u524d\u4e4b\u65f6\uff0c\u5979\u62a2\u4e0a\u524d\u53bb\uff0c\u4e00\u628a\u5c06\u4ed6\u90a3\u4f38\u51fa\u7684\u53f3\u624b\u67b6\u4f4f\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u6014\u4e4b\u95f4\uff0c\u5b89\u5c0f\u840d\u5df2\u4f4e\u58f0\u8bf4\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u4e0d\u53ef\u60ca\u6270\u90d1\u5927\u4fa0\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u4e86\u4e00\u5446\uff0c\u7a81\u7136\u5410\u4e86\u53e3\u5927\u6c14\uff0c\u5782\u4e0b\u624b\u6765\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u4f38\u624b\u5c06\u4ed6\u62c9\u51fa\u7985\u623f\uff0c\u7b11\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u5feb\u53bb\u6b47\u6b47\u5427\uff01\u201d \u3000\u3000\u8fd9\u65f6\u5b8b\u5b5a\u4e5f\u5728\u65c1\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u6709\u8bb8\u591a\u4e8b\u662f\u6025\u4e0d\u5f97\u7684\uff0c\u4f60\u5c82\u53ef\u56e0\u4e00\u65f6\u4e4b\u6025\uff0c\u8bef\u4e86\u5927\u4e8b\u5462\uff1f\u8fd8\u662f\u5feb\u4e9b\u6b47\u606f\u4e00\u4e0b\uff0c\u7b49\u90a3\u90d1\u5927\u4fa0\u4f24\u6108\u4e4b\u540e\u518d\u95ee\u4e5f\u4e0d\u8fdf\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5251\u7709\u8038\u52a8\uff0c\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u665a\u8f88\u4e00\u65f6\u4e0d\u5bdf\uff0c\u51e0\u4e4e\u917f\u6210\u5927\u9519\u4e86\uff01\u201d\u8bed\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u54b1\u4eec\u56de\u8f6c\u5ba2\u5802\u5427\uff01\u201d \u3000\u3000\u4e00\u884c\u4eba\u7acb\u5373\u79bb\u5f00\u90d1\u5927\u521a\u8c03\u606f\u4e4b\u5904\uff0c\u56de\u5230\u5ba2\u5802\u3002 \u3000\u3000\u5e84\u6377\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u8fd9\u4e8b\u7aef\u602a\u5728\u5144\u5f1f\u672a\u66fe\u628a\u5b83\u529e\u597d\uff0c\u4f46\u5176\u4e2d\u7ecf\u8fc7\uff0c\u5144\u5f1f\u5c1a\u672a\u8bf4\u5b8c\uff0c\u65b9\u5144\u5ffd\u6fc0\u52a8\u8d77\u6765\uff0c\u771f\u53eb\u5144\u5f1f\u4e3a\u96be\u5f97\u5f88\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u65f6\u5df2\u7136\u51b7\u9759\u4e0b\u6765\uff0c\u7b11\u9053\uff1a\u201c\u5e84\u5144\u597d\u8bf4\uff01\u8fd9\u4e8b\u4e43\u662f\u5144\u5f1f\u64cd\u4e4b\u8fc7\u6025\u4e86\uff01\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u957f\u957f\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5e84\u5144\uff0c\u8f9b\u82e6\u4f60\u4e86\uff01\u201d \u3000\u3000\u5e84\u6377\u62b1\u62f3\u9053\uff1a\u201c\u65b9\u5144\uff0c\u5144\u5f1f\u8fd8\u6709\u8bdd\u6ca1\u6709\u8bf4\u5b8c\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e84\u5144\u8bf7\u8bb2\uff0c\u5144\u5f1f\u6d17\u8033\u606d\u542c\uff01\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u6c60\u5929\u548c\u5728\u544a\u77e5\u5144\u5f1f\u65b9\u4e8c\u4fa0\u548c\u77f3\u4e8c\u4fa0\u6b7b\u8baf\u4e4b\u65f6\uff0c\u4ed6\u8fd8\u8bf4\u51fa\u4e00\u70b9\u5341\u5206\u53ef\u7591\u4e4b\u4e8b\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4ec0\u4e48\u53ef\u7591\u4e4b\u4e8b\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u636e\u6c60\u5929\u548c\u524d\u8f88\u8bf4\uff0c\u5bfb\u90a3\u65b9\u4e8c\u4fa0\u548c\u77f3\u4e09\u4fa0\u7684\u5c38\u4f53\uff0c\u9664\u4e86\u90a3\u8eab\u8863\u7740\u4e4b\u5916\uff0c\u9762\u76ee\u5df2\u7136\u6a21\u7cca\u4e0d\u6e05\uff0c\u662f\u4ee5\uff0c\u4ed6\u975e\u5e38\u6000\u7591\uff0c\u90a3\u4e24\u5177\u5c38\u4f53\uff0c\u4f1a\u4e0d\u4f1a\u771f\u662f\u65b9\u4e8c\u4fa0\u548c\u77f3\u4e09\u4fa0\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u662f\u6c60\u5802\u4e3b\u8bf4\u7684\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u5144\u5f1f\u5c82\u4f1a\u6b3a\u9a97\u65b9\u5144\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u5e84\u8001\u5f1f\uff0c\u8fd9\u771f\u662f\u6c60\u5929\u548c\u8bf4\u7684\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u6c60\u8001\u524d\u8f88\u786e\u662f\u5982\u6b64\u76f8\u544a\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u76ee\u5149\u8f6c\u5411\u970d\u9e23\u98ce\uff0c\u7b11\u9053\uff1a\u201c\u970d\u5144\uff0c\u8fd9\u4e8b\u4f60\u53ef\u662f\u65e9\u5df2\u77e5\u9053\u4e86\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u6447\u5934\u9053\uff1a\u201c\u6c60\u5929\u548c\u6ca1\u6709\u5411\u8001\u673d\u62a5\u544a\uff0c\u8001\u673d\u5e76\u4e0d\u77e5\u6653\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u5947\u602a\u554a\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u4ec0\u4e48\u5947\u602a\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8fd9\u7b49\u5927\u4e8b\uff0c\u6c60\u5929\u548c\u600e\u53ef\u4e0d\u5411\u4f60\u7980\u62a5\uff1f\u5176\u4e2d\u53ea\u6015\u522b\u6709\u539f\u6545\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u8fd9\u65f6\u5374\u4ef0\u5929\u5927\u7b11\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u4f60\u600e\u7684\u5982\u6b64\u53d1\u6025\uff0c\u83ab\u975e\u8fd9\u4e8b\u771f\u7684\u5f88\u8981\u7d27\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u600e\u7684\u4e0d\u8981\u7d27\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u7b11\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u4f60\u5982\u662f\u89c9\u7740\u6b64\u4e8b\u5173\u7cfb\u751a\u5927\uff0c\u5c1a\u671b\u4f60\u8bf4\u5f97\u660e\u767d\u4e00\u4e9b\u624d\u597d\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u65b9\u8001\u5f1f\u7684\u90a3\u4f4d\u4f2f\u7236\u548c\u77f3\u4fca\u4e09\u4fa0\uff0c\u7a76\u7adf\u662f\u751f\u662f\u6b7b\uff0c\u7aef\u5728\u90a3\u6c60\u5929\u548c\u6240\u8bf4\u662f\u771f\u662f\u5047\u7684\u4efd\u4e0a\uff0c\u4f60\u8001\u513f\u96be\u9053\u90e8\u4e0d\u660e\u767d\u5417\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u8001\u673d\u81ea\u7136\u660e\u767d\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4f60\u65e2\u7136\u660e\u767d\uff0c\u4e3a\u4f55\u4e0d\u7559\u5fc3\u90a3\u6c60\u5929\u548c\u6240\u8bf4\uff0c\u662f\u5426\u6709\u8bc8\u5462\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u7b11\u9053\uff1a\u201c\u6c60\u5929\u548c\u4e3a\u4eba\u672c\u5206\uff0c\u51b3\u4e0d\u4f1a\u8bf4\u5047\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u90a3\u662f\u771f\u7684\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u5f53\u7136\u662f\u771f\u7684\uff01\u4e0d\u8fc7\uff0c\u8001\u673d\u8981\u544a\u8bc9\u4f60\uff0c\u65b9\u5929\u6210\u548c\u77f3\u4fca\u7684\u5c38\u4f53\uff0c\u4e0d\u662f\u4ed6\u4eec\u672c\u4eba\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u957f\u957f\u5730\u5401\u4e86\u4e00\u53e3\u6c14\u3002\u4ed6\u6ee1\u8154\u7684\u5e0c\u671b\uff0c\u53ea\u662f\u8981\u77e5\u9053\uff0c\u4ed6\u4eec\u5e76\u672a\u88ab\u5bb3\uff0c\u662f\u4ee5\uff0c\u970d\u9e23\u98ce\u6b64\u8a00\u4e00\u51fa\uff0c\u4ed6\u5927\u5927\u5730\u677e\u4e86\u4e00\u53e3\u6c14\uff01 \u3000\u3000\u5b8b\u5b5a\u95fb\u8a00\uff0c\u5374\u662f\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u970d\u5144\uff0c\u4f60\u600e\u77e5\u90a3\u4e0d\u662f\u4ed6\u4eec\u672c\u4eba\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u7b11\u9053\uff1a\u201c\u6b64\u4e8b\u8001\u673d\u65e9\u5df2\u5dee\u4eba\u67e5\u660e\u5728\u5148\uff0c\u5426\u5219\uff0c\u6c60\u5929\u548c\u4e5f\u4e0d\u4f1a\u4e0d\u5411\u8001\u673d\u62a5\u544a\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4f60\u65e9\u5c31\u77e5\u9053\u4e86\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u672c\u6765\u5c31\u77e5\u9053\u4e86\u554a\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u4e24\u773c\u4e00\u7ffb\uff0c\u6012\u9053\uff1a\u201c\u970d\u5144\uff0c\u4f60\u51a4\u5f97\u6211\u597d\u82e6\u554a\uff01\u201d \u3000\u3000\u970d\u545c\u98ce\u7b11\u9053\uff1a\u201c\u8001\u673d\u4e0d\u4f1a\u51a4\u4f60\u2026\u2026\u8fd9\u5b9e\u9645\u4e0a\u662f\u4f60\u81ea\u5df1\u627e\u4e0a\u8eab\u53bb\u7684\uff0c\u5374\u602a\u4e0d\u5f97\u4ed6\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u6447\u4e86\u6447\u5934\uff0c\u957f\u53f9\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4f60\u80fd\u5f53\u90a3\u4e10\u5e2e\u7684\u5e2e\u4e3b\uff0c\u679c\u7136\u8981\u6bd4\u5144\u5f1f\u6c89\u5f97\u4f4f\u6c14\u591a\u4e86\uff01\u201d \u3000\u3000\u4ed6\u8bed\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u970d\u5144\uff0c\u4f60\u65e2\u7136\u77e5\u9053\u90a3\u4e24\u5177\u5c38\u4f53\u4e0d\u662f\u65b9\u5929\u6210\u548c\u77f3\u4fca\uff0c\u65b9\u3001\u77f3\u4e24\u4f4d\u7684\u4e0b\u843d\uff0c\u4f60\u60f3\u5fc5\u4e5f\u662f\u77e5\u6653\u7684\u4e86\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u7b11\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u4f60\u628a\u6211\u5f53\u4f5c\u8bf8\u845b\u4eae\u4e86\uff1f\u65b9\u3001\u77f3\u4e8c\u5144\u7684\u5c38\u4f53\uff0c\u53ef\u4ee5\u4ece\u4ed6\u4eec\u6b7b\u72b6\u8ba4\u660e\u662f\u4e0e\u4e0d\u662f\uff0c\u81f3\u4e8e\u4ed6\u4eec\u4e24\u4f4d\u7a76\u7adf\u53bb\u4e86\u4f55\u5904\uff0c\u5144\u5f1f\u53ef\u771f\u662f\u4e00\u70b9\u90fd\u4e0d\u660e\u767d\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u76b1\u7709\u9053\uff1a\u201c\u771f\u7684\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u65f6\u4e0d\u7981\u63d2\u53e3\uff0c\u9053\uff1a\u201c\u5e84\u5144\uff0c\u90a3\u6c60\u5802\u4e3b\u8fd8\u5bf9\u4f60\u8bf4\u8fc7\u4ec0\u4e48\u5417\uff1f\u201d \u3000\u3000\u5e84\u6377\u6c89\u541f\u4e00\u4e0b\uff0c\u63a5\u9053\uff1a\u201c\u6c60\u8001\u53ea\u8bf4\uff0c\u65b9\u5929\u6210\u548c\u77f3\u4fca\u4e24\u4eba\u5f88\u53ef\u80fd\u4ecd\u5728\u6d1b\u9633\u9644\u8fd1\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u771f\u7684\uff1f\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u662f\u5426\u771f\u7684\uff0c\u5144\u5f1f\u4e5f\u4e0d\u6562\u5984\u4e0b\u65ad\u8bed\uff01\u53ea\u662f\uff0c\u6c60\u5802\u4e3b\u65e2\u6709\u8fd9\u7b49\u8bf4\u6cd5\uff0c\u90a3\u5fc5\u7136\u5df2\u6709\u6240\u89c1\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5e84\u5144\uff0c\u5144\u5f1f\u60f3\u62dc\u6258\u4f60\u4e00\u6869\u4e8b\uff0c\u4e0d\u77e5\u5e84\u5144\u53ef\u613f\u76f8\u52a9\uff1f\u201d \u3000\u3000\u5e84\u6377\u7b11\u9053\uff1a\u201c\u65b9\u5144\u5c3d\u7ba1\u5429\u5490\uff0c\u8d74\u6c64\u8e48\u706b\uff0c\u5144\u5f1f\u5728\u6240\u4e0d\u8f9e\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8d77\u8eab\u62b1\u62f3\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u5e84\u5144\uff0c\u5144\u5f1f\u60f3\u8bf7\u5e84\u5144\u518d\u53bb\u6d1b\u9633\u4e00\u884c\u3002\u201d \u3000\u3000\u5e84\u6377\u95fb\u8a00\u4e00\u6014\uff0c\u9053\uff1a\u201c\u518d\u53bb\u6d1b\u9633\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5e84\u5144\u53ef\u613f\u4ee3\u5144\u5f1f\u5206\u52b3\uff1f\u201d \u3000\u3000\u5e84\u6377\u5927\u7b11\u9053\uff1a\u201c\u6709\u4f55\u4e0d\u53ef\uff1f\u8981\u5144\u5f1f\u524d\u53bb\u4f5c\u751a\uff1f\u65b9\u5144\u8bf7\u5148\u8bf4\u660e\u624d\u597d\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5144\u5f1f\u60f3\u8bf7\u5e84\u5144\u518d\u4e0e\u90a3\u6c60\u5802\u4e3b\u8054\u7cfb\uff0c\u8bf7\u4ed6\u76f8\u52a9\u5e84\u5144\uff0c\u5728\u6d1b\u9633\u9644\u8fd1\u8ffd\u67e5\u5144\u5f1f\u7684\u77f3\u4e09\u53d4\u4e0b\u843d\u3002\u201d \u3000\u3000\u5e84\u6377\u9053\uff1a\u201c\u5144\u5f1f\u9075\u547d\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6df1\u6df1\u4e00\u793c\uff0c\u9053\uff1a\u201c\u5e84\u5144\u76db\u60c5\uff0c\u5144\u5f1f\u65e5\u540e\u81ea\u5f53\u5bfb\u8c0b\u62a5\u7b54\u4e4b\u9014\uff0c\u4e00\u5207\u90fd\u4ef0\u4ed7\u5e84\u5144\u4e86\uff01\u201d \u3000\u3000\u5e84\u776b\u8fde\u5fd9\u7b54\u793c\uff0c\u7b11\u9053\uff1a\u201c\u533a\u533a\u5c0f\u4e8b\uff0c\u65b9\u5144\u4e0d\u7528\u8a00\u8c22\u2026\u2026\u201d\u8f6c\u8eab\u4fbf\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u76ee\u9001\u5e84\u6377\u79bb\u53bb\uff0c\u8fd9\u624d\u53c8\u5411\u970d\u9e23\u98ce\u62b1\u62f3\u4e00\u63d6\u9053\uff1a\u201c\u970d\u8001\uff0c\u665a\u8f88\u6709\u7740\u4e00\u70b9\u4e0d\u60c5\u4e4b\u8bf7\uff0c\u4e0d\u77e5\u970d\u8001\u613f\u5426\u76f8\u52a9\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u5927\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u4e0d\u7528\u5ba2\u6c14\uff0c\u5982\u6709\u7528\u5f97\u7740\u8001\u673d\u4e4b\u5904\uff0c\u8001\u673d\u51b3\u4e0d\u63a8\u8bff\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6df1\u6df1\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u665a\u8f88\u5148\u884c\u8c22\u4e86\uff01\u201d\u4ed6\u65bd\u8fc7\u793c\u540e\uff0c\u8fd9\u624d\u63a5\u9053\uff1a\u201c\u6c60\u5929\u548c\u4e45\u4f4f\u6d1b\u9633\uff0c\u5bf9\u4e8e\u6d1b\u9633\u4e00\u5207\u6b66\u6797\u4eba\u7269\uff0c\u5e94\u662f\u77e5\u4e4b\u751a\u8be6\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u51e4\u7b11\u9053\uff1a\u201c\u90a3\u662f\u81ea\u7136\uff0c\u5426\u5219\uff0c\u8001\u673d\u4e5f\u4e0d\u4f1a\u8981\u4ed6\u62c5\u4efb\u6d1b\u9633\u5206\u5802\u7684\u5802\u4e3b\uff0c\u957f\u8fbe\u5341\u5e74\u4e4b\u4e45\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u665a\u8f88\u60f3\u8bf7\u4f60\u5148\u8981\u90a3\u6c60\u5802\u4e3b\u51fa\u9762\uff0c\u4ee3\u665a\u8f88\u5728\u6d1b\u9633\u5730\u5e26\uff0c\u5bfb\u627e\u77f3\u4e09\u53d4\uff0c\u4e0d\u77e5\u4f60\u8001\u53ef\u613f\u4e0b\u6b64\u6cd5\u8c15\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u7b11\u9053\uff1a\u201c\u6709\u4f55\u4e0d\u53ef\uff1f\u8001\u673d\u5c31\u5dee\u4eba\u524d\u53bb\u5982\u4f55\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u6014\u9053\uff1a\u201c\u5dee\u4eba\u524d\u53bb\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8001\u5f1f\u8fd9\u6837\u603b\u8be5\u653e\u5fc3\u4e86\u5427\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u970d\u8001\uff0c\u665a\u8f88\u4e4b\u610f\uff0c\u53ea\u8981\u4f60\u8001\u4ea4\u4ee3\u4ed6\u4eec\u4e00\u53e5\u5373\u53ef\uff0c\u4e0d\u5fc5\u53e6\u5916\u5dee\u4eba\u524d\u7684\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u7b11\u9053\uff1a\u201c\u8001\u673d\u7684\u51e0\u4f4d\u5e08\u5f1f\uff0c\u5c1a\u5728\u5bfa\u4e2d\uff0c\u8ba9\u4ed6\u4eec\u53bb\u4e00\u8d9f\uff0c\u90a3\u4e5f\u6ca1\u6709\u4ec0\u4e48\u5173\u7cfb\u554a\uff01\u201d \u3000\u3000\u4ed6\u8bed\u97f3\u7565\u7565\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4e3a\u4e86\u8001\u5f1f\u53f0\u4e4b\u4e8b\uff0c\u8001\u673d\u5373\u4ee4\u662f\u81ea\u5df1\u5954\u8d70\uff0c\u90a3\u4e5f\u6ca1\u6709\u4ec0\u4e48\u4e0d\u53ef\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u90a3\u5c31\u66f4\u4e0d\u6562\u5f53\u4e86\u2026\u2026\u201d \u3000\u3000\u970d\u9e23\u98ce\u54c8\u54c8\u4e00\u7b11\uff0c\u5927\u6b65\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u4e0d\u591a\u65f6\uff0c\u5409\u5a01\u5df2\u968f\u5728\u4ed6\u8eab\u540e\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u6562\u60c5\uff0c\u4ed6\u547d\u9634\u9633\u624b\u5409\u5a01\uff0c\u53bb\u81f3\u6d1b\u9633\u5750\u9547\uff0c\u6307\u6325\u5e2e\u4e2d\u5f1f\u5b50\uff0c\u5bfb\u8bbf\u77f3\u4fca\u7684\u4e0b\u843d\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5927\u4e3a\u611f\u52a8\u4e3e\u6b65\u4e0a\u524d\uff0c\u5411\u5409\u5a01\u6df1\u6df1\u4e00\u793c\uff0c\u9053\uff1a\u201c\u6709\u52b3\u5409\u8001\u524d\u8f88\u4e86\u2026\u2026\u201d \u3000\u3000\u5409\u5a01\u90a3\u51b7\u51b7\u7684\u8138\u4e0a\uff0c\u7efd\u5f00\u4e86\u4e00\u4e1d\u7b11\u610f\uff0c\u9053\uff1a\u201c\u8001\u5f1f\u4e0d\u7528\u5ba2\u6c14\uff0c\u5409\u67d0\u80fd\u4e3a\u8001\u5f1f\u6548\u52b3\uff0c\u5b9e\u662f\u673a\u4f1a\u96be\u5f97\u7684\u5f88\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u77e5\u9053\uff0c\u5409\u5a01\u8fd9\u53e5\u8bdd\u4e5f\u7b97\u5f97\u662f\u7531\u8877\u4e4b\u8a00\uff0c\u5f53\u4e0b\u62b1\u62f3\u9053\uff1a\u201c\u5409\u8001\u76db\u60c5\u665a\u8f88\u8bb0\u4e0b\u4e86\uff01\u201d \u3000\u3000\u5409\u5a01\u54c8\u54c8\u4e00\u7b11\uff0c\u8f6c\u8eab\u51fa\u4e86\u5ba2\u5385\uff0c\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u5e84\u6377\u3001\u5409\u5a01\u4e24\u4eba\u4e00\u8d70\uff0c\u65b9\u96ea\u5b9c\u4f3c\u662f\u653e\u4e0b\u4e86\u4e00\u6869\u5fc3\u4e8b\uff0c\u957f\u957f\u5730\u53f9\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f46\u671b\u5409\u8001\u548c\u5e84\u5144\u4e0d\u4f1a\u7a7a\u8dd1\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u4e0d\u7528\u803d\u5fc3\uff0c\u5409\u5e08\u5f1f\u7684\u80fd\u8010\uff0c\u8001\u673d\u660e\u767d\u5f97\u5f88\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4f46\u613f\u5982\u6b64\uff01\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u5411\u5165\u4e91\u5927\u5e08\u4e00\u793c\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u8d35\u5bfa\u8bf8\u591a\u4e8b\u52a1\uff0c\u90fd\u8981\u638c\u95e8\u4eba\u5904\u7406\uff0c\u4f60\u8001\u4e0d\u7528\u518d\u5728\u6b64\u95f4\u5b88\u5019\u4e86\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u7b11\u4e86\u4e00\u7b11\uff0c\u4e5f\u4e0d\u8c26\u8ba9\uff0c\u70b9\u70b9\u5934\uff0c\u5408\u5341\u9053\uff1a\u201c\u5982\u6b64\u2026\u2026\u8001\u8872\u544a\u9000\u4e86\uff01\u201d\u8f6c\u8eab\u9000\u4e86\u5ba2\u5802\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u770b\u4e86\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u4f60\u8001\u4e5f\u8be5\u56de\u623f\u4f11\u606f\u4e00\u4e0b\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u8001\u673d\u4e0d\u7d2f\u554a\uff01\u8001\u673d\u8fd8\u662f\u966a\u4f60\u8001\u5f1f\u5750\u5750\u5427\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u665a\u8f88\u5f88\u597d\uff0c\u4f60\u4e5f\u4e0d\u7528\u803d\u5fc3\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b55\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u8d76\u8001\u5077\u513f\u8d70\uff0c\u53ef\u662f\u6709\u4ec0\u4e48\u8981\u529e\uff1f\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5374\u65e0\u610f\u4e2d\u628a\u76ee\u5149\u8f6c\u5411\u4e86\u5b89\u5c0f\u840d\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u53ef\u628a\u65b9\u96ea\u5b9c\u7ed9\u5f04\u5f97\u4fca\u8138\u901a\u7ea2\uff0c\u8fde\u58f0\u9053\uff1a\u201c\u6ca1\u6709\u2026\u2026\u6ca1\u6709\u4e8b\uff0c\u4f60\u8001\u7231\u7559\u4e0b\uff0c\u90a3\u5c31\u7559\u4e0b\u5427\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5374\u662f\u8e6c\u4e86\u5b8b\u5b5a\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5b8b\u5b5a\uff0c\u4f60\u5f80\u540e\u8bf4\u8bdd\uff0c\u53ef\u4e0d\u8bb8\u5e26\u523a\u554a\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u5927\u7b11\u9053\uff1a\u201c\u8001\u673d\u8bb0\u4e0b\u4e86\uff01\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u5176\u5b9e\u8001\u673d\u4e5f\u6ca1\u6709\u8bf4\u9519\u4ec0\u4e48\u554a\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4f60\u8fd8\u8981\u8bf4\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u59d1\u5a18\u83ab\u8981\u52a8\u6012\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u53ea\u8981\u4f60\u4e0d\u4e71\u8bb2\uff0c\u6211\u53c8\u4f55\u5fc5\u751f\u6c14\u5462\uff1f\u201d \u3000\u3000\u8bdd\u97f3\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u90a3\u4e5f\u6ca1\u6709\u4ec0\u4e48\u5173\u7cfb\uff0c\u53ea\u8981\u6211\u8ddf\u65b9\u5144\u5f88\u6e05\u767d\uff0c\u4f60\u4eec\u8bf4\u5f97\u518d\u591a\u4e5f\u6ca1\u6709\u7528\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u542c\u5f97\u5148\u662f\u5446\u4e86\u4e00\u5446\uff0c\u7ee7\u800c\u604d\u7136\u5927\u609f\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e0d\u9519\uff0c\u522b\u4eba\u8bf4\u4e48\uff0c\u5c3d\u7ba1\u8ba9\u4ed6\u4eec\u53bb\u8bf4\u597d\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u6211\u53ef\u53ea\u662f\u6307\u7684\u4f60\u554a\uff01\u5982\u679c\u522b\u4eba\u4e5f\u8bf4\uff0c\u6211\u53ef\u5c31\u7b54\u5e94\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8001\u5077\u513f\u627f\u60c5\u4e86\uff01\u4e0d\u8fc7\uff0c\u970d\u5e2e\u4e3b\u5c31\u5728\u4e00\u65c1\uff0c\u4f60\u8fd9\u7b49\u5bf9\u5f85\u4e8e\u4ed6\uff0c\u53ea\u6015\u4ed6\u5fc3\u91cc\u5f88\u4e0d\u9ad8\u5174\u554a\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u90a3\u4e5f\u4e0d\u8981\u7d27\uff0c\u970d\u5e2e\u4e3b\u4e5f\u4e0d\u5217\u5165\u4fbf\u662f\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u2026\u2026\u4f60\u5982\u6b64\u539a\u5f85\uff0c\u5012\u53eb\u8001\u673d\u5927\u611f\u610f\u6599\u4e4b\u5916\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u90a3\u4e5f\u6ca1\u6709\u4ec0\u4e48\u3002\u56e0\u4e3a\u4f60\u8001\u662f\u4f4d\u957f\u8f88\u554a\uff01\u201d \u3000\u3000\u5979\u7b11\u4e86\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u4f55\u51b5\uff0c\u4f60\u8001\u53c8\u662f\u4e00\u5e2e\u4e4b\u4e3b\u5462\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u7b11\u9053\uff1a\u201c\u8fd9\u4e0e\u4e00\u5e2e\u4e4b\u4e3b\u4f55\u5173\uff1f\u59d1\u5a18\uff0c\u5012\u662f\u8001\u673d\u6709\u4e00\u53e5\u8bdd\uff0c\u4e0d\u77e5\u5f53\u8bf4\u4e0d\u5f53\u8bf4\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4ec0\u4e48\u8bdd\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u59d1\u5a18\u5c06\u6765\u4e0e\u65b9\u8001\u5f1f\u5927\u559c\u4e4b\u65e5\uff0c\u8001\u673d\u5c31\u7b97\u662f\u4e00\u4e2a\u8bc1\u4eba\u5982\u4f55\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u95fb\u8a00\uff0c\u8fde\u9888\u5b50\u90fd\u7f9e\u7ea2\u4e86\uff01\u5979\u8eab\u5f62\u4e00\u8f6c\uff0c\u4fbf\u5411\u5916\u8dd1\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5b8b\u5b5a\u5374\u5fcd\u4e0d\u4f4f\u5475\u5475\u5927\u7b11\u9053\uff1a\u201c\u5999\u554a\uff01\u8001\u5077\u513f\u4e5f\u7b97\u5f97\u4e0a\u4e00\u4e2a\u624d\u662f\u5462\uff1f\u201d \u3000\u3000\u4f46\u5b89\u5c0f\u840d\u6b64\u65f6\u5df2\u7136\u53bb\u4e86\u5f88\u8fdc\u4e86\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u8138\u4e0a\u4e5f\u662f\u70ed\u70ed\u7684\uff0c\u4f46\u5374\u7b11\u9053\uff1a\u201c\u5b8b\u8001\u3001\u970d\u8001\uff0c\u4f60\u4eec\u8fd9\u4e2a\u73a9\u7b11\u53ef\u5f00\u4e0d\u5f97\uff0c\u5982\u662f\u88ab\u5c11\u6797\u50e7\u4fa3\u77e5\u9053\u4e86\u5b89\u8d24\u5f1f\u4e43\u662f\u5973\u513f\u4e4b\u8eab\uff0c\u90a3\u53ef\u5c31\u9ebb\u70e6\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8fd9\u8bdd\u5012\u662f\u4e0d\u5047\u2026\u2026\u201d \u3000\u3000\u4f46\u970d\u9e23\u98ce\u7b11\u9053\uff1a\u201c\u5b8b\u5144\uff0c\u4f60\u77e5\u9053\u5165\u4e91\u5927\u5e08\u4e0d\u77e5\u9053\u5b89\u59d1\u5a18\u4e43\u662f\u5973\u513f\u4e4b\u8eab\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4ed6\uff01\u83ab\u975e\u5df2\u7136\u77e5\u6653\u4e86\u5b89\u59d1\u5a18\u4e43\u662f\u5973\u6d41\u5417\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u7b11\u9053\uff1a\u201c\u5f53\u7136\u77e5\u9053\u4e86\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bed\u97f3\u7565\u7565\u4e00\u987f\uff0c\u770b\u4e86\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u63a5\u9053\uff1a\u201c\u82e5\u975e\u8fd1\u65e5\u65b9\u8001\u5f1f\u548c\u5b89\u59d1\u5a18\u663e\u5f97\u751a\u662f\u4eb2\u70ed\uff0c\u4ed6\u4eec\u5012\u4e5f\u4e0d\u4e00\u5b9a\u771f\u80fd\u77a7\u5f97\u51fa\u6765\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8138\u4e0a\u66f4\u7ea2\uff0c\u4e0d\u7981\u4f4e\u4e0b\u5934\u53bb\u3002 \u3000\u3000\u5b8b\u5b5a\u76b1\u7709\u9053\uff1a\u201c\u5982\u662f\u4ed6\u4eec\u5df2\u7136\u77e5\u9053\uff0c\u53c8\u600e\u4f1a\u4e0d\u5411\u6211\u7b49\u63d0\u51fa\u6765\u5462\uff1f\u5c11\u6797\u5bfa\u4e0d\u7559\u5973\u5ba2\uff0c\u90a3\u662f\u4ed6\u4eec\u5386\u4ee3\u7956\u5e08\u9057\u547d\uff0c\u5165\u4e91\u5927\u5e08\u600e\u53ef\u8fdd\u80cc\uff1f\u201d \u3000\u3000\u970d\u545c\u98ce\u9053\uff1a\u201c\u4e8b\u6709\u6743\u5b9c\uff0c\u96be\u9053\u4f60\u5fd8\u4e86\u5417\uff1f\u773c\u4e0b\u5c11\u6797\u5df2\u5728\u70ad\u53d1\u53ef\u5371\u4e4b\u79cb\uff0c\u5982\u662f\u4ed6\u4eec\u4e0d\u5047\u88c5\u4e0d\u77e5\u9053\uff0c\u5c06\u5b89\u59d1\u5a18\u9001\u51fa\u5bfa\u5916\uff0c\u53ea\u6015\u65b9\u8001\u5f1f\u4e5f\u4f1a\u968f\u5979\u800c\u53bb\uff0c\u90a3\u4e94\u4f4d\u5927\u9b54\u4e3b\u5018\u662f\u6765\u4e86\uff0c\u4ed6\u4eec\u5c82\u4e0d\u662f\u65e0\u6cd5\u5e94\u4ed8\u4e86\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u95fb\u8a00\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u5fcd\u4e0d\u4f4f\u957f\u53f9\u4e00\u58f0\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5165\u4e91\u5927\u5e08\uff0c\u4e5f\u8d70\u5230\u53ef\u60b2\u7684\u5730\u6b65\uff0c\u53ea\u6015\u4ed6\u5fc3\u4e2d\u4e4b\u82e6\uff0c\u51b3\u975e\u6211\u7b49\u53ef\u731c\u60f3\u5f97\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u51e4\u9053\uff1a\u201c\u5c11\u6797\u57fa\u4e1a\uff0c\u603b\u4e0d\u80fd\u6bc1\u53bb\u554a\u2026\u2026\u201d \u3000\u3000\u4f59\u97f3\u672a\u5df2\uff0c\u53ea\u89c1\u5b89\u5c0f\u840d\u4e00\u8df3\u4e00\u8e66\u7684\u53c8\u8dd1\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u8ddf\u5728\u5979\u8eab\u540e\uff0c\u7adf\u7136\u662f\u90a3\u4f4d\u6b66\u6797\u4e00\u602a\u8f9b\u67cf\u516c\u3002 \u3000\u3000\u5b8b\u5b5a\u8fde\u5fd9\u7ad9\u4e86\u8d77\u6765\uff0c\u8fce\u5c06\u51fa\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e5f\u8d77\u8eab\u76f8\u8fce\uff0c\u5927\u58f0\u9053\uff1a\u201c\u8f9b\u8001\uff0c\u60f3\u4e0d\u5230\u4f60\u8001\u6765\u5f97\u597d\u5feb\u554a\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u4e24\u773c\u4e00\u7ffb\uff0c\u5927\u58f0\u9053\uff1a\u201c\u8001\u592b\u65e2\u7136\u5e94\u5141\u4e86\u4f60\uff0c\u53c8\u4f55\u5fc5\u62d6\u62d6\u62c9\u62c9\u5462\uff1f\u83ab\u975e\u4f60\u5c0f\u5b50\u6b22\u8fce\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u600e\u4e48\u6562\uff1f\u665a\u8f88\u76fc\u671b\u4e86\u51e0\u5929\u5566\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u7eb5\u58f0\u5927\u7b11\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u8bf4\u8bdd\u600e\u7684\u8fd9\u4e48\u4e0d\u5207\u5b9e\u9645\uff1f\u54b1\u4eec\u5206\u5f00\u624d\u51e0\u5929\uff1f\u9a97\u4eba\u53ef\u4e5f\u4e0d\u662f\u8fd9\u7b49\u9a97\u6cd5\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8baa\u8baa\u4e00\u7b11\u9053\uff1a\u201c\u665a\u8f88\u76fc\u671b\u4f60\u8001\u524d\u6765\uff0c\u90a3\u603b\u662f\u771f\u7684\u554a\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u8c22\u8c22\u4f60\u4e86\uff01\u5c0f\u5b50\uff0c\u53cd\u6b63\u8001\u72ac\u5728\u90a3\u4e94\u9b54\u5934\u672a\u66fe\u626b\u51c0\u4e4b\u524d\uff0c\u8001\u592b\u4e0d\u518d\u79bb\u4f60\u4e00\u6b65\u4fbf\u662f\uff01\u201d \u3000\u3000\u4ed6\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u7740\u5411\u5b8b\u5b5a\u9053\uff1a\u201c\u8001\u5077\u513f\uff0c\u90a3\u5c11\u6797\u5bfa\u7684\u638c\u95e8\u4eba\u5462\uff1f\u201d \u3000\u3000\u53e3\u6c14\u4e0d\u5c0f\uff0c\u4f3c\u662f\u8981\u90a3\u5c11\u6797\u638c\u95e8\u4eba\u524d\u6765\u62dc\u89c1\u4e8e\u4ed6\uff01 \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u5b89\u8001\u5f1f\u6ca1\u6709\u5bf9\u5c11\u6797\u50e7\u4fa3\u8bf4\u8fc7\u8f9b\u8001\u54e5\u5df2\u7ecf\u62b5\u8fbe\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u6ca1\u6709\u554a\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8001\u673d\u53bb\u544a\u8bc9\u4ed6\u4eec\uff01\u201d\u4e00\u8f6c\u8eab\uff0c\u4fbf\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u970d\u9e23\u98ce\u8fd9\u65f6\u4e5f\u8d70\u4e86\u8fc7\u6765\uff0c\u62b1\u62f3\u9053\uff1a\u201c\u8f9b\u5927\u53d4\u8fd8\u8bb0\u5f97\u5c0f\u4f84\u5417\uff1f\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5df2\u7136\u957f\u957f\u65bd\u4e86\u4e00\u793c\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u6697\u9053\uff1a\u201c\u539f\u6765\u4ed6\u5f53\u771f\u6bd4\u90a3\u970d\u5e2e\u4e3b\u957f\u4e86\u4e00\u8f88\u3002\u201d\u4ed6\u5fc3\u4e2d\u867d\u7136\u5403\u60ca\uff0c\u4f46\u53e3\u4e2d\u5374\u6ca1\u6709\u8bf4\u51fa\u4ec0\u4e48\uff1f \u3000\u3000\u5b89\u5c0f\u840d\u5374\u4e0d\u7136\u4e86\uff01\u5979\u4e00\u542c\u970d\u545c\u98ce\u53eb\u90a3\u8f9b\u67cf\u516c\u53eb\u4e3a\u5927\u53d4\uff0c\u4e0d\u7981\u5931\u7b11\u9053\uff1a\u201c\u970d\u8001\uff0c\u4f60\u600e\u4e48\u6bd4\u4ed6\u5c0f\u4e86\u4e00\u8f88\u5417\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u8f9b\u5927\u53d4\u548c\u970d\u67d0\u7684\u6069\u5e08\uff0c\u4e43\u662f\u91d1\u5170\u4e4b\u4ea4\uff0c\u81ea\u7136\u8be5\u662f\u970d\u67d0\u7684\u957f\u8f88\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u7709\u5934\u4e00\u76b1\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u5c0f\u4e2b\u5934\uff0c\u8fd9\u4e9b\u4e8b\u8bf4\u6765\u8bf4\u53bb\u90fd\u662f\u4f60\u641e\u7684\u9b3c\uff0c\u8001\u592b\u95ee\u4f60\uff0c\u90a3\u54d1\u4e5e\u5462\uff1f\u8fd8\u5728\u5d69\u5c71\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u5f53\u7136\u5728\uff01\u4e0d\u8fc7\uff0c\u8fd9\u4e24\u5929\u662f\u4e0d\u662f\u5df2\u7136\u79bb\u53bb\uff0c\u665a\u8f88\u5c31\u77e5\u9053\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u770b\u4e86\u970d\u9e23\u98ce\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5c0f\u82b1\u5b50\uff0c\u4f60\u76f8\u4fe1\u4e0d\u7bb1\u4fe1\uff1f\u4f60\u90a3\u9ec4\u5e08\u53d4\u8fd8\u6d3b\u5728\u4e16\u4e0a\u3002\u201d \u3000\u3000\u970d\u545c\u98ce\u5446\u4e86\u4e00\u5446\u9053\uff1a\u201c\u9ec4\u5e08\u53d4\u5417\u2026\u2026\u201d \u3000\u3000\u6b64\u523b\uff0c\u5b89\u5c0f\u840d\u76f4\u6025\u5f97\u5411\u970d\u9e23\u98ce\u8fde\u8fde\u7728\u773c\uff0c\u53ea\u662f\uff0c\u970d\u9e23\u98ce\u5374\u6ca1\u6709\u770b\u5230\uff0c\u5140\u81ea\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u9ec4\u5e08\u53d4\u4ed9\u53bb\u591a\u5e74\uff0c\u5927\u53d4\u63d0\u4ed6\u4f5c\u751a\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u5927\u7b11\u9053\uff1a\u201c\u8001\u592b\u63d0\u4ed6\uff0c\u53ea\u56e0\u6570\u65e5\u4e4b\u524d\uff0c\u8001\u592b\u5c45\u7136\u78b0\u5230\u4e86\u4ed6\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u90a3\u600e\u4e48\u53ef\u80fd\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u7b11\u9053\uff1a\u201c\u8001\u592b\u4e5f\u77e5\u9053\u4e0d\u53ef\u80fd\uff0c\u4e0d\u8fc7\uff0c\u8001\u592b\u77e5\u6653\u5f97\u8fdf\u4e86\u4e00\u4e9b\uff0c\u624d\u4f1a\u88ab\u8fd9\u4e2b\u5934\u7ed9\u9a97\u4e86\uff01\u201d \u3000\u3000\u4ed6\u867d\u7136\u53e3\u4e2d\u5728\u6307\u8d23\u5b89\u5c0f\u840d\uff0c\u795e\u8272\u4e2d\uff0c\u5012\u4e5f\u6ca1\u6709\u4ec0\u4e48\u6012\u610f\uff0c\u663e\u7136\uff0c\u5bf9\u4e8e\u6b64\u4e8b\uff0c\u4ed6\u5e76\u672a\u5341\u5206\u89c1\u602a\uff01 \u3000\u3000\u5b89\u5c0f\u840d\u6da8\u7ea2\u4e86\u7c89\u8138\uff0c\u5728\u65c1\u63a9\u53e3\u62b5\u7b11\u3002 \u3000\u3000\u970d\u9e23\u98ce\u5374\u662f\u6014\u6014\u7684\u9053\uff1a\u201c\u8f9b\u5927\u53d4\uff0c\u5b89\u59d1\u5a18\u662f\u600e\u751f\u7684\u8bf4\u7684\uff1f\u4f60\u8001\u2026\u2026\u201d \u3000\u3000\u8f9b\u6015\u516c\u7b11\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u8fd9\u4e8b\u4e5f\u7b97\u4e0d\u4e86\u4ec0\u4e48\u5927\u4e8b\uff0c\u8001\u592b\u4e00\u751f\u4e4b\u4e2d\uff0c\u53d7\u4eba\u6700\u5927\u7684\u4e00\u70b9\u6069\u60c5\uff0c\u4fbf\u662f\u4f60\u90a3\u9ec4\u5e08\u53d4\u4e86\uff01\u53ef\u6068\u7684\u5f88\uff0c\u76f4\u5230\u90a3\u9ec4\u5144\u4ed9\u53bb\uff0c\u8001\u592b\u5374\u672a\u89c1\u8fc7\u4ed6\u4e00\u9762\u2026\u2026\u201d \u3000\u3000\u4ed6\u957f\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u63a5\u9053\uff1a\u201c\u8fd9\u4e2b\u5934\u867d\u7136\u662f\u9a97\u4e86\u8001\u592b\uff0c\u4f46\u4e5f\u672a\u5c1d\u4e0d\u662f\u6697\u4e2d\u76f8\u52a9\u4e86\u8001\u592b\u4e00\u56de\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u7b11\u9053\uff1a\u201c\u5927\u53d4\u4e0d\u4f1a\u8d23\u602a\u5b89\u59d1\u5a18\u4e86\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u5f53\u7136\u4e0d\u4f1a\uff01\u5426\u5219\uff0c\u8001\u592b\u5c82\u80af\u524d\u6765\u5c11\u6797\uff1f\u4f55\u51b5\uff0c\u9648\u5e0c\u6b63\u7684\u8fd9\u4e2a\u5f92\u513f\uff0c\u8001\u592b\u5012\u662f\u5341\u5206\u559c\u6b22\u4ed6\uff0c\u5982\u679c\u4eca\u540e\u80fd\u51ed\u7740\u8001\u592b\u7684\u4e00\u8eab\u6240\u5b66\uff0c\u52a9\u4ed6\u5c06\u4e94\u9b54\u9664\u53bb\uff0c\u4e00\u662f\u4e0d\u6789\u8001\u592b\u6709\u751f\u4e4b\u5e74\u7684\u5c81\u6708\uff0c\u4e8c\u6765\u4e5f\u7b97\u662f\u62a5\u7b54\u4e86\u9ec4\u5144\u6551\u6211\u4e4b\u5fb7\uff0c\u8fd9\u7b49\u4e24\u5168\u5176\u7f8e\u4e4b\u4e8b\uff0c\u8001\u592b\u53c8\u4f55\u4e50\u800c\u4e0d\u4e3a\uff1f\u201d\u8bf4\u5230\u6700\u540e\uff0c\u8001\u4eba\u5df2\u662f\u54c8\u54c8\u5927\u7b11\u8d77\u6765\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5728\u542c\u5230\u8f9b\u67cf\u516c\u8bc6\u7834\u9ec4\u51b2\u65e9\u6b7b\u4e4b\u8baf\u65f6\uff0c\u5fc3\u4e2d\u786e\u662f\u5927\u5927\u4e0d\u5b89\uff0c\u624b\u5fc3\u4e2d\u4e5f\u634f\u4e86\u4e00\u628a\u51b7\u6c57\uff0c\u4f46\u5bb9\u5f97\u8f9b\u67cf\u516c\u628a\u8bdd\u8bf4\u5b8c\uff0c\u4ed6\u4e0d\u7531\u5f97\u5927\u5927\u677e\u4e86\u53e3\u6c14\uff0c\u5fc3\u4e2d\u6697\u9053\u4fa5\u5e78\uff01 \u3000\u3000\u970d\u9e23\u98ce\u7d27\u5f20\u7684\u795e\u8272\uff0c\u4e5f\u987f\u65f6\u4e00\u61c8\uff0c\u7b11\u9053\uff1a\u201c\u5927\u53d4\uff0c\u6709\u4f60\u8001\u76f8\u52a9\uff0c\u90a3\u771f\u662f\u592a\u597d\u4e86\u2026\u2026\u201d \u3000\u3000\u8f9b\u67cf\u516c\u7b11\u9053\uff1a\u201c\u5c0f\u82b1\u5b50\u2026\u2026\u201d \u3000\u3000\u4ed6\u5927\u6982\u89c9\u5f97\u970d\u9e23\u98ce\u80e1\u53d1\u5df2\u7136\u6cdb\u767d\uff0c\u90a3\u4e2a\u5c0f\u5b57\u52a0\u5728\u4ed6\u5934\u4e0a\uff0c\u786e\u662f\u6709\u4e9b\u4e0d\u59a5\uff0c\u5f53\u4e0b\u6539\u53e3\u63a5\u9053\uff1a\u201c\u970d\u8d24\u4f84\uff0c\u4f60\u90a3\u9ec4\u5e08\u53d4\u7684\u575f\u83b9\u4f55\u5728\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u9ec4\u5e08\u53d4\u7684\u9057\u8715\uff0c\u846c\u5728\u672c\u5e2e\u5386\u4ee3\u957f\u8001\u7684\u575f\u83b9\u4e4b\u4e2d\u2026\u2026\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u90a3\u662f\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u5728\u957f\u5b89\u57ce\u5916\u7684\u5b50\u5348\u9547\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u8d24\u4f84\uff0c\u7b49\u5230\u6b66\u6797\u5927\u4e8b\u5b89\u5b9a\u4e4b\u65e5\uff0c\u4f60\u53ef\u5f97\u9886\u7740\u8001\u592b\u524d\u53bb\u796d\u5960\u4e00\u756a\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u5c0f\u4f84\u9075\u547d\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u5475\u5475\u4e00\u7b11\uff0c\u6389\u5934\u5411\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u8001\u592b\u6709\u4e00\u4e8b\u4e0d\u89e3\uff0c\u59d1\u5a18\u53ef\u5426\u4e3a\u8001\u592b\u8bf4\u660e\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u4e00\u6014\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u554a\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4f60\u8fd9\u7b49\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u600e\u77e5\u8001\u592b\u751f\u5e73\u6700\u5927\u7684\u6069\u4eba\uff0c\u4e43\u662f\u54d1\u5df4\u9ec4\u51b2\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u4e00\u6014\u9053\uff1a\u201c\u8fd9\u4e2a\u5417\uff1f\u81ea\u7136\u662f\u522b\u4eba\u544a\u8bc9\u665a\u8f88\u7684\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u544a\u8bc9\u4f60\u7684\uff0c\u8fd8\u6709\uff0c\u90a3\u592a\u5ba4\u5cf0\u5934\u7684\u8001\u513f\uff0c\u53c8\u662f\u8c01\u4eba\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u82b3\u5fc3\u4e00\u8f6c\uff0c\u6697\u9053\uff1a\u201c\u77a7\u90a3\u795d\u516c\u660e\u5f53\u65e5\u4e00\u89c1\u4ed6\u73b0\u8eab\uff0c\u4fbf\u795e\u8272\u5927\u53d8\uff0c\u53ea\u6015\u4ed6\u4eec\u4e4b\u95f4\uff0c\u6709\u7740\u6781\u5927\u7684\u601d\u6028\uff0c\u6211\u5982\u8bf4\u4e86\u51fa\u6765\uff0c\u5c82\u4e0d\u662f\u66f4\u4f7f\u795d\u8001\u4e3a\u96be\u4e86\u5417\uff1f\u201d \u3000\u3000\u5979\u4e00\u5ff5\u53ca\u6b64\uff0c\u4e0d\u7531\u5f97\u5ae3\u7136\u4e00\u7b11\u9053\uff1a\u201c\u8001\u4eba\u5bb6\u4f60\u8001\u95ee\u8fd9\u4e2a\u4f5c\u751a\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u8001\u592b\u867d\u7136\u4e0d\u60f3\u89c1\u602a\u4f60\u9a97\u4e86\u8001\u592b\uff0c\u4f46\u4f60\u4e3a\u4f55\u77e5\u9053\u8001\u592b\u6765\u5386\u4e4b\u4e8b\uff0c\u6211\u603b\u5f97\u5f04\u4e2a\u660e\u767d\u5427\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u6709\u5173\u4f60\u8001\u7684\u6765\u5386\uff0c\u90a3\u662f\u6211\u7237\u7237\u5bf9\u6211\u8bf4\u7684\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u4e00\u6014\u9053\uff1a\u201c\u4f60\u7237\u7237\uff1f\u4ed6\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u60ca\u5929\u53df\u554a\uff01\u201d \u3000\u3000\u5927\u6982\u201c\u60ca\u5929\u53df\u201d\u4e09\u5b57\u5341\u5206\u60ca\u4eba\uff0c\u90a3\u8f9b\u67cf\u516c\u4e00\u542c\uff0c\u987f\u65f6\u6123\u4e86\u597d\u4e45\uff0c\u4e24\u773c\u5728\u5b89\u5c0f\u840d\u8eab\u4e0a\u8f6c\u6765\u8f6c\u53bb\uff0c\u534a\u664c\u65b9\u59cb\u5927\u53eb\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026\u6765\u81ea\u4e1c\u6d77\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6709\u4e9b\u610f\u5916\uff0c\u504f\u7740\u5934\u7b11\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u6709\u4ec0\u4e48\u4e0d\u5bf9\u5417\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4e0d\u5bf9\uff1f\u90a3\u5012\u6ca1\u6709\uff01\u201d \u3000\u3000\u4ed6\u5ffd\u5730\u4ef0\u5929\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\uff0c\u5f53\u771f\u7684\u60f3\u4e0d\u5230\u2026\u2026\u201d \u3000\u3000\u970d\u9e23\u98ce\u6697\u6697\u5403\u60ca\uff0c\u5bfb\u601d\u9053\uff1a\u201c\u8fd9\u5b89\u5c0f\u840d\u7684\u7956\u7236\uff0c\u53ea\u6015\u8ddf\u4ed6\u6709\u7740\u4ec0\u4e48\u8fc7\u8282\u4e86\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u4e5f\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u4ec0\u4e48\u4e8b\u60f3\u4e0d\u5230\uff1f\u83ab\u975e\u4f60\u8001\u8ba4\u8bc6\u665a\u8f88\u7684\u7237\u7237\u2026\u2026\u201d \u3000\u3000\u8f9b\u67cf\u516c\u5927\u7b11\u9053\uff1a\u201c\u5c82\u6b62\u8ba4\u5f97\uff1f\u54b1\u4eec\u52a8\u8fc7\u624b\uff0c\u8f83\u91cf\u8fc7\u6b66\u529f\uff0c\u800c\u4e14\u5728\u4f60\u5bb6\u90a3\u5929\u5fc3\u5c9b\u4e0a\u4f5c\u8fc7\u5ba2\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5148\u542c\u4ed6\u8bf4\u4e0e\u81ea\u5df1\u7684\u7956\u7236\u52a8\u8fc7\u624b\uff0c\u5fc3\u4e2d\u4e0d\u7981\u4e00\u60ca\uff0c\u4f46\u53c8\u542c\u4ed6\u8bf4\u66fe\u5728\u5929\u5fc3\u5c9b\u4e0a\u4f5c\u8fc7\u5ba2\uff0c\u4e00\u65f6\u90a3\u60ac\u5728\u534a\u7a7a\u7684\u5c0f\u5fc3\u7075\uff0c\u5ffd\u7136\u5927\u653e\uff0c\u56e0\u4e3a\u5979\u77e5\u9053\uff0c\u81ea\u5df1\u5bb6\u4e2d\u7684\u5929\u5fc3\u5c9b\uff0c\u9664\u975e\u662f\u7237\u7237\u7684\u81f3\u4ea4\uff0c\u522b\u4eba\u662f\u51b3\u53bb\u4e0d\u6210\u7684\u4e86\uff01\u4f55\u51b5\uff0c\u60f3\u4e0d\u5230\u81ea\u5df1\u8fd9\u4e48\u4e71\u8bf4\u4e00\u901a\uff0c\u5c45\u7136\u8499\u5bf9\u4e86\uff01 \u3000\u3000\u662f\u4ee5\uff0c\u5979\u6b22\u547c\u4e86\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u771f\u7684\u5417\uff1f\u5982\u6b64\u665a\u8f88\u8981\u5c0a\u79f0\u4f60\u8001\u4e00\u58f0\u8f9b\u516c\u516c\u4e86\uff01\u201d\u8bf4\u7740\uff0c\u4fbf\u4e0a\u524d\u4e00\u6b65\uff0c\u6df1\u6df1\u4e00\u793c\u3002 \u3000\u3000\u8f9b\u67cf\u516c\u5927\u7b11\u6447\u5934\u9053\uff1a\u201c\u4e0d\u8981\u5ba2\u6c14\u4e86\uff0c\u59d1\u5a18\uff0c\u8001\u592b\u82e5\u65e9\u65e9\u77e5\u9053\u4f60\u662f\u90a3\u5b89\u4e5d\u516c\u5144\u53f0\u7684\u5b59\u5973\u513f\uff0c\u4e5f\u5c31\u4e0d\u7528\u6d6a\u8d39\u90a3\u4e48\u591a\u5507\u820c\u4e86\u3002\u201d \u3000\u3000\u53ea\u6b64\u4e00\u8a00\uff0c\u5c31\u53ef\u542c\u51fa\uff0c\u5b89\u5c0f\u840d\u731c\u60f3\u5f97\u6ca1\u6709\u9519\uff0c\u8f9b\u67cf\u516c\u548c\u5b89\u4e5d\u516c\u679c\u7136\u662f\u6709\u7740\u5f88\u597d\u7684\u4ea4\u60c5\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8f9b\u516c\u516c\uff0c\u665a\u8f88\u2026\u2026\u600e\u5730\u6ca1\u6709\u8ddf\u4f60\u8001\u5728\u5929\u5fc3\u5c9b\u89c1\u8fc7\u9762\u5462\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u53ea\u542c\u5f97\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u561b\uff1f\u8001\u592b\u5230\u5929\u5fc3\u5c9b\u7684\u65f6\u5019\uff0c\u4f60\u8fd8\u5728\u4f60\u5a18\u7684\u809a\u5b50\u91cc\u5462\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7c89\u8138\u4e00\u7ea2\uff0c\u4f4e\u58f0\u4e0d\u8bed\u3002 \u3000\u3000\u8f9b\u67cf\u516c\u7b11\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u4f4e\u58f0\u9053\uff1a\u201c\u665a\u8f88\u53eb\u5c0f\u840d\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u90a3\u2026\u2026\u8001\u592b\u4ee5\u540e\u53eb\u4f60\u840d\u513f\u53ef\u597d\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4f60\u8001\u7231\u600e\u4e48\u53eb\u90fd\u597d\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u70b9\u5934\u9053\uff1a\u201c\u5c31\u8fd9\u4e48\u53eb\u5427\uff01\u840d\u513f\uff0c\u90a3\u592a\u5ba4\u5cf0\u95f4\u4f2a\u5192\u9ec4\u51b2\u7684\u8001\u513f\uff0c\u4ed6\u662f\u8c01\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6700\u6015\u7684\uff0c\u5c31\u662f\u4ed6\u903c\u95ee\u90a3\u795d\u516c\u660e\u662f\u8c01\uff0c\u8fd9\u65f6\u8f9b\u67cf\u516c\u679c\u7136\u63d0\u51fa\u76f8\u8be2\uff0c\u4e00\u65f6\u5012\u53eb\u5730\u4e0d\u77e5\u600e\u4e48\u56de\u7b54\u624d\u597d\u3002 \u3000\u3000\u5979\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u4ed6\u2026\u2026\u665a\u8f88\u4e5f\u4e0d\u5927\u660e\u767d\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u542c\u5f97\u4e00\u6123\uff0c\u76b1\u7709\u9053\uff1a\u201c\u4e0d\u77e5\u9053\uff1f\u8001\u592b\u4e0d\u4fe1\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u771f\u7684\u561b\uff01\u665a\u8f88\u6ca1\u6709\u8bf4\u5047\u8bdd\u554a\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u76ee\u5149\u5411\u65b9\u96ea\u5b9c\u8eab\u4e0a\u4e00\u8f6c\uff0c\u63a5\u9053\uff1a\u201c\u8001\u5f1f\u53f0\uff0c\u4f60\u60f3\u5fc5\u662f\u77e5\u9053\u7684\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u773c\u89c1\u5b89\u5c0f\u840d\u4e0d\u80af\u63d0\u90a3\u795d\u516c\u660e\u7684\u540d\u5b57\uff0c\u5fc3\u4e2d\u5c31\u60f3\u5230\uff0c\u516b\u6210\u662f\u5979\u803d\u5fc3\u8f9b\u67cf\u516c\u4e0e\u795d\u516c\u660e\u4e4b\u95f4\uff0c\u6709\u7740\u4ec0\u4e48\u6069\u6028\uff0c\u624d\u4f1a\u4e0d\u80af\u5c06\u90a3\u795d\u516c\u660e\u7684\u6765\u5386\u8bf4\u51fa\u2026\u2026\u201d\u8f6c\u5ff5\u81f3\u6b64\uff0c\u4ed6\u4e5f\u6709\u4e9b\u72b9\u8c6b\u4e0d\u5b9a\uff0c\u62ff\u4e0d\u51c6\u4e3b\u610f\uff0c\u8be5\u4e0d\u8be5\u5c06\u795d\u516c\u660e\u7684\u540d\u59d3\u8bf4\u51fa\u3002 \u3000\u3000\u8f9b\u67cf\u516c\u89c1\u4ed6\u4e5f\u5728\u6c89\u541f\uff0c\u4e0d\u7981\u6709\u7740\u4e0d\u6109\u4e4b\u8272\uff0c\u5927\u58f0\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u8bf4\uff1f\u83ab\u975e\u90a3\u8001\u5934\u513f\uff0c\u4e43\u662f\u4e00\u4e2a\u5341\u6076\u4e0d\u8d66\u7684\u5927\u574f\u86cb\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7709\u5934\u4e00\u76b1\uff0c\u51b7\u54fc\u9053\uff1a\u201c\u600e\u4e48\u4f1a\uff1f\u5929\u53f0\u6a35\u53df\u867d\u7136\u4e3a\u4eba\u5b64\u50fb\uff0c\u4f46\u4e5f\u4e0d\u662f\u574f\u4eba\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bdd\u8bf4\u51fa\uff0c\u624d\u53d1\u73b0\u81ea\u5df1\u4e0a\u5f53\u4e86\uff01\u8f9b\u67cf\u516c\u5374\u662f\u54c8\u54c8\u602a\u7b11\u9053\uff1a\u201c\u6211\u8bf4\u5462\uff01\u679c\u7136\u662f\u795d\u516c\u660e\u8fd9\u8001\u513f\u800d\u7684\u82b1\u6837\uff01\u201d\u7b11\u58f0\u4e00\u655b\uff0c\u63a5\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u7a76\u7adf\u5e74\u8f7b\uff0c\u706b\u6c14\u5927\uff0c\u8001\u592b\u4e00\u6fc0\uff0c\u53ef\u5c31\u628a\u4f60\u6fc0\u51fa\u6765\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u751a\u662f\u540e\u6094\uff0c\u4f46\u4e00\u60f3\uff0c\u4e8b\u5df2\u81f3\u6b64\uff0c\u540e\u6094\u4e5f\u6ca1\u6709\u7528\u4e86\u3002 \u3000\u3000\u5f53\u4e0b\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u90a3\u4f4d\u8001\u4eba\u6b63\u662f\u795d\u516c\u660e\uff0c\u8f9b\u8001\u4e5f\u4ed6\u76f8\u8bc6\u5417\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u5f53\u7136\u76f8\u8bc6\uff01\u201d \u3000\u3000\u4ed6\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u8001\u592b\u5947\u602a\u7684\u662f\uff0c\u4f60\u548c\u840d\u513f\u600e\u7684\u4e0d\u613f\u8bf4\u51fa\u4ed6\u7684\u540d\u59d3\u5462\uff1f\u53ef\u662f\u90a3\u795d\u516c\u660e\u4ea4\u4ee3\u4e86\u4f60\u4eec\u4ec0\u4e48\u8bdd\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u5934\u9053\uff1a\u201c\u6ca1\u6709\u3002\u665a\u8f88\u7b49\u4e0d\u613f\u8bf4\u51fa\uff0c\u4e43\u662f\u4e0d\u60f3\u4e24\u4f4d\u56e0\u6b64\u5f04\u5f97\u4e0d\u5feb\u3002\u201d \u3000\u3000\u8f9b\u67cf\u516c\u7b11\u9053\uff1a\u201c\u4e0d\u5feb\uff0c\u8001\u592b\u600e\u4e48\u4f1a\uff1f\u4f55\u51b5\u2026\u2026\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8001\u5927\u8fd8\u6b20\u4e86\u90a3\u795d\u8001\u5f1f\u4e00\u70b9\u4eba\u60c5\u503a\u5462\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u542c\u5f97\u8f9b\u67cf\u516c\u8fd9\u7b49\u8bf4\u6cd5\uff0c\u7acb\u5373\u7b11\u9053\uff1a\u201c\u8f9b\u516c\u516c\uff0c\u4f60\u8ddf\u795d\u8001\u4eba\u5e76\u6ca1\u6709\u4ec0\u4e48\u4ec7\u6068\u5417\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u6ca1\u6709\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5947\u602a\u9053\uff1a\u201c\u771f\u7684\u6ca1\u6709\uff1f\u90a3\u2026\u2026\u795d\u8001\u4eba\u4e3a\u4ec0\u4e48\u6709\u4e9b\u4e0d\u613f\u8ddf\u4f60\u89c1\u9762\u5462\uff1f\u201d \u3000\u3000\u8f9b\u6015\u516c\u9053\uff1a\u201c\u4ed6\u2026\u2026\u5509\uff0c\u8fd9\u4e8b\u8bf4\u6765\u8bdd\u957f\uff0c\u4ed6\u4e0d\u613f\u4e0e\u8001\u592b\u89c1\u9762\uff0c\u53ea\u662f\u4e0d\u60f3\u8001\u592b\u8fd8\u4ed6\u4e00\u4ef6\u4e1c\u897f\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u5f97\u597d\u5947\u5fc3\u5927\u53d1\uff0c\u7b11\u9053\uff1a\u201c\u5929\u4e0b\u54ea\u6709\u8fd9\u7b49\u602a\u4e8b\uff1f\u8001\u524d\u8f88\uff0c\u4f60\u8981\u8fd8\u4ed6\u7684\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u7b11\u9053\uff1a\u201c\u90a3\u4e1c\u897f\u5417\uff1f\u53ea\u662f\u4e00\u9762\u5c0f\u5c0f\u7684\u9ec4\u65d7\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u6014\u9053\uff1a\u201c\u5c0f\u5c0f\u9ec4\u65d7\uff0c\u4f5c\u4ec0\u4e48\u7528\u7684\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u522b\u5c0f\u770b\u4e86\u90a3\u5c0f\u5c0f\u7684\u4e09\u89d2\u9ec4\u65d7\uff0c\u5b83\u53ef\u4ee5\u53f7\u4ee4\u5357\u4e03\u7701\u7684\u7eff\u6797\u8c6a\u6770\u5462\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u5728\u65c1\u542c\u5f97\u8138\u8272\u4e00\u53d8\uff0c\u63d2\u53e3\u9053\uff1a\u201c\u8f9b\u5927\u53d4\uff0c\u4f60\u8001\u6240\u8bf4\u7684\u9ec4\u65d7\uff0c\u53ef\u662f\u5357\u4e03\u7701\u7684\u7eff\u6797\u76df\u4e3b\u4ee4\u65d7\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u663e\u7136\u4e5f\u5bf9\u8fd9\u4e8b\u5927\u611f\u5174\u8da3\uff0c\u7b11\u9053\uff1a\u201c\u8f9b\u516c\u516c\uff0c\u90a3\u76df\u4e3b\u65d7\u5f88\u5a01\u98ce\u5417\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u5f53\u7136\uff01\u840d\u513f\uff0c\u5357\u4e03\u7701\u7684\u7eff\u6797\u8c6a\u6770\uff0c\u4e3a\u6570\u4e0d\u4e0b\u4e09\u5343\u4eba\uff0c\u4e00\u65e6\u624b\u4e2d\u6709\u7740\u8fd9\u76df\u4e3b\u4ee4\u65d7\uff0c\u5f53\u7136\u662f\u5341\u5206\u5a01\u98ce\u7684\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4f60\u8001\u662f\u5411\u795d\u8001\u501f\u7684\u5417\uff1f\u201d\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8001\u592b\u4e3a\u4e86\u6551\u4e00\u4e2a\u6545\u4ea4\u7684\u5f1f\u5b50\uff0c\u624d\u5411\u4ed6\u501f\u4e86\u8fc7\u6765\u4e00\u7528\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u795d\u8001\u600e\u4f1a\u6709\u90a3\u4eca\u65d7\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u7b11\u9053\uff1a\u201c\u56db\u5341\u5e74\u524d\uff0c\u90a3\u795d\u516c\u660e\u5c31\u662f\u5357\u4e03\u7701\u7eff\u6797\u76df\u4e3b\u795d\u5f6c\u7684\u5f1f\u5f1f\uff0c\u795d\u5f6c\u4e00\u5fc3\u60f3\u4ed6\u63a5\u638c\u76df\u4e3b\u4e4b\u4f4d\uff0c\u5c31\u628a\u90a3\u4ee4\u65d7\u4ea4\u7ed9\u4ed6\u4fdd\u7ba1\uff0c\u6240\u4ee5\uff0c\u8001\u592b\u5411\u4ed6\u60dc\u6765\u4e00\u7528\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff1f\u8fd9\u60f3\u5fc5\u662f\u5f88\u4e45\u7684\u4e8b\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u7b11\u9053\uff1a\u201c\u4e09\u5341\u4e94\u5e74\u4e86\uff01\u8fd9\u8001\u513f\u5c45\u7136\u8eb2\u4e86\u8001\u592b\u4e09\u5341\u4e94\u5e74\u4e4b\u4e45\uff0c\u4f60\u8bf4\u4ed6\u607c\u4eba\u4e0d\u607c\u4eba\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6c89\u541f\u9053\uff1a\u201c\u8f9b\u8001\uff0c\u90a3\u4ee4\u65d7\u65e2\u7136\u88ab\u4f60\u501f\u53bb\uff0c\u5357\u4e03\u7701\u7684\u7eff\u6797\u76df\u4e3b\u53c8\u662f\u8c01\u63a5\u66ff\u4e86\u5462\uff1f\u6ca1\u6709\u4ee4\u65d7\uff0c\u53c8\u600e\u4e48\u80fd\u53f7\u4ee4\u5f97\u4e86\u90a3\u4e9b\u8c6a\u4fa0\u4e4b\u58eb\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u7b11\u9053\uff1a\u201c\u795d\u516c\u660e\u4e0d\u63a5\uff0c\u5f53\u7136\u53ea\u597d\u7531\u795d\u5f6c\u7684\u5927\u5f1f\u5b50\u6768\u51b2\u6682\u65f6\u63a5\u638c\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u4ee4\u65d7\u5462\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u795d\u5f6c\u53e6\u5916\u53c8\u4eff\u5236\u4e00\u9762\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u7559\u4e0b\u9057\u8a00\uff0c\u795d\u516c\u660e\u51e0\u65f6\u6536\u56de\u4ee4\u65d7\uff0c\u4ed6\u51e0\u65f6\u5c31\u8981\u5f53\u90a3\u5357\u4e03\u7701\u7684\u7eff\u6797\u76df\u4e3b\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5931\u7b11\u9053\uff1a\u201c\u602a\u4e0d\u5f97\uff01\u6562\u60c5\u8fd9\u4e2d\u95f4\u8fd8\u6709\u5982\u8bb8\u7684\u539f\u6545\uff01\u201d \u3000\u3000\u4ed6\u7b11\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u795d\u8001\u5927\u53ef\u4e0d\u5fc5\u8eb2\u907f\u554a\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4ed6\u53ea\u7ba1\u6536\u56de\u4ee4\u65d7\uff0c\u5c06\u5b83\u8f6c\u4ea4\u7ed9\u6768\u51b2\uff0c\u4e0d\u5c31\u7ed3\u4e86\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u4e00\u6014\u9053\uff1a\u201c\u662f\u554a\uff01\u795d\u8001\u600e\u5730\u4e0d\u8fd9\u4e48\u60f3\u5462\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u5927\u7b11\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u4eec\u4e0d\u77e5\u90a3\u795d\u516c\u660e\u7684\u4e3a\u4eba\uff0c\u81ea\u7136\u662f\u4f1a\u8fd9\u4e48\u60f3\u7684\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u795d\u8001\u4e3a\u4eba\uff1f\u6709\u4ec0\u4e48\u7279\u522b\u4e4b\u5904\u5417\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u7684\u4e3a\u4eba\u6781\u4e3a\u606d\u608c\uff0c\u4ed6\u5bf9\u795d\u5f6c\u4e00\u8a00\u4e00\u5b57\uff0c\u90fd\u662f\u8bda\u5fc3\u542c\u547d\uff0c\u662f\u4ee5\u8981\u4ed6\u6536\u56de\u4ee4\u65d7\uff0c\u8f6c\u7ed9\u6768\u51b2\uff0c\u90a3\u5c31\u4e0d\u662f\u8981\u4ed6\u8fdd\u6096\u4e86\u4ed6\u5144\u957f\u7684\u9057\u547d\u4e48\uff1f\u5728\u4ed6\u800c\u8a00\uff0c\u90a3\u6bd4\u6b7b\u4e86\u8fd8\u8981\u96be\u8fc7\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u5934\u9053\uff1a\u201c\u90a3\u5c31\u4e0d\u5bf9\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u795d\u5f6c\u751f\u524d\u8981\u4ed6\u63a5\u638c\u7eff\u6797\u76df\u4e3b\uff0c\u4ed6\u4e0d\u613f\u610f\uff0c\u90a3\u4e0d\u5df2\u7136\u8fdd\u80cc\u4e86\u4ed6\u5144\u5f1f\u7684\u613f\u4e86\u5417\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4e0d\u7136\uff0c\u795d\u5f6c\u5728\u4e16\u4e4b\u65e5\uff0c\u4ed6\u867d\u7136\u66fe\u63a5\u638c\u7eff\u6797\u76df\u4e3b\uff0c\u4ed6\u53ef\u4e5f\u6ca1\u6709\u8bf4\u8fc7\u4e00\u53e5\u4e0d\u613f\uff0c\u53ea\u662f\u5c3d\u91cf\u53e3\u907f\u63d0\u5230\u8fd9\u4e8b\uff0c\u5426\u5219\uff0c\u4ed6\u4e5f\u4e0d\u4f1a\u628a\u4ee4\u65d7\u501f\u7ed9\u8001\u592b\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u795d\u8001\u679c\u7136\u662f\u4e2a\u606d\u608c\u5fc3\u6781\u91cd\u7684\u4eba\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u7b11\u9053\uff1a\u201c\u53ef\u4e0d\uff0c\u5426\u5219\uff0c\u2026\u2026\u8001\u592b\u4e5f\u65e9\u5c31\u628a\u4ee4\u65d7\u8fd8\u7ed9\u4ed6\u5566\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u95fb\u8a00\uff0c\u5446\u4e86\u4e00\u5446\uff1a\u201c\u8f9b\u516c\u516c\uff0c\u4f60\u8001\u662f\u2026\u2026\u6709\u5fc3\u627e\u4e0d\u5230\u4ed6\u5417\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u7b11\u9053\uff1a\u201c\u627e\u522b\u4eba\u4e0d\u5bb9\u6613\uff0c\u627e\u4ed6\u4e48\uff0c\u90a3\u53ef\u4e0d\u662f\u96be\u4e8b\uff0c\u8001\u592b\u4e3a\u4e86\u6210\u5168\u4ed6\u7684\u5919\u613f\uff0c\u6240\u4ee5\u624d\u88c5\u7740\u627e\u4e0d\u5230\u4ed6\u800c\u5df2\uff01\u4f55\u51b5\u2026\u2026\u8001\u592b\u4e5f\u65e9\u5df2\u628a\u4ee4\u65d7\u8fd8\u7ed9\u4e86\u7eff\u6797\u76df\u4e3b\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u95ee\u9053\uff1a\u201c\u54ea\u4e2a\u7eff\u6797\u76df\u4e3b\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u6768\u51b2\u554a\uff01\u201d\u4ed6\u5ffd\u7136\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u53ea\u53ef\u60dc\uff0c\u795d\u8001\u5f1f\u88ab\u8499\u5728\u9f13\u91cc\u8db3\u8db3\u6709\u4e8c\u5341\u5e74\u4e4b\u4e45\u2026\u2026\u201d \u3000\u3000\u7b11\u97f3\u672a\u843d\uff0c\u53ea\u89c1\u5165\u4e91\u5927\u5e08\u548c\u5b8b\u5b5a\u5df2\u5927\u6b65\u8d70\u6765\u3002 \u3000\u3000\u5165\u4e91\u5927\u5e08\u62a2\u5728\u524d\u5934\uff0c\u53cc\u624b\u5408\u5341\u5411\u8f9b\u67cf\u516c\u65bd\u793c\u9053\uff1a\u201c\u8d2b\u50e7\u5165\u4e91\uff0c\u53c2\u89c1\u8f9b\u8001\u65bd\u4e3b\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u767d\u7709\u4e00\u626c\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u7f62\u4e86\uff0c\u638c\u95e8\u4eba\u8fd9\u7b49\u53e3\u6c14\uff0c\u8f9b\u67d0\u53ef\u662f\u62c5\u5f53\u4e0d\u8d77\u2026\u2026\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u8001\u65bd\u4e3b\u4e43\u662f\u524d\u8f88\u9ad8\u4eba\uff0c\u8d2b\u50e7\u8fd9\u7b49\u79f0\u8c13\uff0c\u672c\u662f\u7406\u6240\u5f53\u7136\u2026\u2026\u201d \u3000\u3000\u8bed\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u8d2b\u50e7\u4e0d\u77e5\u8001\u65bd\u4e3b\u8f69\u9a7e\u5149\u4e34\uff0c\u672a\u66fe\u51fa\u5bfa\u76f8\u4faf\uff0c\u5931\u793c\u4e4b\u7f6a\uff0c\u5c1a\u76fc\u8001\u65bd\u4e3b\u89c1\u8c05\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u6de1\u6de1\u4e00\u7b11\uff0c\u6325\u624b\u9053\uff1a\u201c\u90a3\u5c31\u8d8a\u53d1\u5f97\u4e0d\u6562\u5f53\u4e86\uff01\u638c\u95e8\u4eba\u65e2\u6709\u8fd9\u7b49\u5fc3\u610f\uff0c\u8001\u592b\u5fc3\u9886\u76db\u60c5\u4fbf\u662f\u3002\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u90a3\u5927\u5e08\u5144\u53ef\u5728\u5bfa\u4e2d\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u5165\u5723\u5927\u5e08\u5144\u5df2\u7531\u8386\u7530\u6765\u6b64\uff0c\u8d2b\u50e7\u8fd9\u5c31\u5dee\u4eba\u544a\u77e5\u4e8e\u4ed6\u2026\u2026\u201d \u3000\u3000\u6b63\u5f85\u8981\u90a3\u5b88\u5728\u95e8\u5916\u7684\u5c0f\u6c99\u5f25\u524d\u53bb\u901a\u77e5\u5165\u5723\u5927\u5e08\uff0c\u5374\u89c1\u5165\u5723\u5927\u5e08\u5df2\u7136\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u8f9b\u67cf\u516c\u4e00\u89c1\u5165\u5723\u5927\u5e08\uff0c\u8138\u4e0a\u7acb\u5373\u5806\u4e86\u7b11\u5bb9\uff0c\u5927\u58f0\u9053\uff1a\u201c\u548c\u5c1a\uff0c\u4f60\u597d\u5417\uff1f\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u5374\u662f\u5408\u5341\u5f53\u80f8\uff0c\u795e\u6001\u606d\u656c\u5730\u7b11\u9053\uff1a\u201c\u591a\u5e74\u4e0d\u66fe\u62dc\u7779\u8001\u83e9\u8428\u5bb9\u989c\uff0c\u8001\u83e9\u8428\u4f9d\u7136\u8fd9\u7b49\u5065\u58ee\uff0c\u53eb\u8d2b\u50e7\u597d\u751f\u6b22\u559c\u3002\u201d \u3000\u3000\u8f9b\u67cf\u516c\u5927\u7b11\u9053\uff1a\u201c\u548c\u5c1a\u597d\u8bf4\uff0c\u8001\u592b\u90fd\u6d3b\u5f97\u6709\u4e9b\u817b\u4e86\uff01\u600e\u5948\u960e\u738b\u4e0d\u6536\uff0c\u5c0f\u9b3c\u4e0d\u6562\u62d8\u62ff\uff0c\u8001\u592b\u81ea\u5df1\u4e5f\u53ea\u597d\u5728\u4e16\u95f4\u53d7\u82e6\u5566\uff01\u201d \u3000\u3000\u7b11\u58f0\u9707\u8033\uff0c\u8fde\u90a3\u5ba2\u5802\u4e0a\u7684\u5c4b\u74e6\uff0c\u90fd\u7eb7\u7eb7\u9707\u52a8\u3002 \u3000\u3000\u5165\u5723\u5927\u5e08\u7b11\u9053\uff1a\u201c\u8001\u83e9\u8428\u53d6\u7b11\u4e86\uff01\u201d\u8bed\u97f3\u4e00\u5e06\u63a5\u9053\uff1a\u201c\u8001\u83e9\u8428\u4e00\u5411\u4e0d\u613f\u516c\u7136\u8fc7\u95ee\u6b66\u6797\u4e4b\u4e8b\uff0c\u5982\u4eca\u5ffd\u7136\u6765\u5230\u5c11\u6797\uff0c\u4e0d\u77e5\u4e3a\u4e86\u4f55\u6545\uff1f\u201d \u3000\u3000\u539f\u6765\uff0c\u5165\u5723\u5927\u5e08\u5e76\u4e0d\u77e5\u6653\u8f9b\u67cf\u516c\u6765\u6b64\u4e4b\u6545\u3002 \u3000\u3000\u8f9b\u67cf\u516c\u95fb\u8a00\uff0c\u6307\u7740\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u90fd\u662f\u4e3a\u4e86\u8fd9\u4f4d\u8001\u5f1f\u554a\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4e3a\u4e86\u65b9\u65bd\u4e3b\u5417\uff1f\u4f46\u4e0d\u77e5\u65b9\u65bd\u751f\u4e0e\u8001\u83e9\u8428\u6709\u4ec0\u4e48\u6069\u6028\u7275\u8fde\uff1f\u201d\u8a00\u4e0b\u4e4b\u610f\u4f3c\u662f\u751a\u4e3a\u803d\u5fc3\u3002 \u3000\u3000\u8f9b\u67cf\u516c\u95fb\u8a00\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u6069\u6028\uff1f\u8bf4\u8d77\u6765\uff0c\u53ea\u6015\u4f60\u8001\u548c\u5c1a\u6709\u4e9b\u4e0d\u4fe1\uff01\u201d \u3000\u3000\u5165\u5723\u592a\u5e08\u9053\uff1a\u201c\u600e\u7684\u4e86\uff1f\u96be\u9053\u8001\u83e9\u8428\u8eab\u4e0a\uff0c\u53d1\u751f\u4e86\u4ec0\u4e48\u60ca\u4eba\u4e4b\u4e8b\u5417\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8001\u592b\u4e00\u751f\uff0c\u8fd8\u6ca1\u6709\u505a\u8fc7\u4ec0\u4e48\u4e22\u4eba\u4e4b\u4e8b\uff0c\u4f46\u8fd9\u4e00\u56de\u561b\uff0c\u53ef\u4e22\u4eba\u4e22\u5927\u4e86\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u5446\u4e86\u4e00\u5446\uff0c\u63a5\u9053\uff1a\u201c\u8001\u83e9\u8428\uff0c\u4f60\u8d8a\u8bf4\u8d8a\u53eb\u8d2b\u50e7\u7cca\u6d82\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u7b11\u9053\uff1a\u201c\u548c\u5c1a\uff0c\u8001\u592b\u4e00\u751f\uff0c\u5927\u5c0f\u7ecf\u8fc7\u5343\u767e\u573a\u786c\u4ed7\uff0c\u51e0\u4e4e\u5168\u80dc\u800c\u5f52\u2026\u2026\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u8001\u83e9\u8428\u529f\u529b\u901a\u795e\uff0c\u81ea\u662f\u4e0d\u4f1a\u843d\u8d25\u7684\u4e86\u3002\u201d \u3000\u3000\u8f9b\u67cf\u516c\u7b11\u9053\uff1a\u201c\u90a3\u53ef\u4e0d\u4e00\u5b9a\uff0c\u8001\u592b\u5728\u8fd9\u884c\u5c06\u5c31\u6728\u4e4b\u5e74\uff0c\u53ef\u8bf4\u8d25\u4e86\u4e00\u6b21\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u5446\u4e86\u4e00\u5446\uff0c\u76b1\u7709\u9053\uff1a\u201c\u6709\u8fd9\u79cd\u4e8b\uff1f\u4ec0\u4e48\u4eba\u5c45\u7136\u80dc\u8fc7\u8001\u83e9\u8428\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u548c\u5c1a\uff0c\u4f60\u90fd\u60f3\u4e0d\u51fa\u6765\u5417\uff1f\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u8fd9\u65f6\u5b9e\u9645\u5df2\u7136\u60f3\u51fa\u6765\uff0c\u8fd9\u80dc\u4ed6\u4e4b\u4eba\uff0c\u5fc5\u662f\u65b9\u96ea\u5b9c\u65e0\u7591\uff0c\u4f46\u4ed6\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u8d2b\u50e7\u7b28\u62d9\u5f97\u5f88\uff0c\u8fd8\u662f\u8bf7\u8001\u83e9\u8428\u660e\u544a\u3002\u201d \u3000\u3000\u8f9b\u6015\u516c\u9053\uff1a\u201c\u8001\u592b\u4e09\u65e5\u4e4b\u524d\uff0c\u8d25\u5728\u8fd9\u4f4d\u8001\u5f1f\u624b\u4e0b\u4e86\u3002\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u542c\u5f97\u5fc3\u4e2d\u597d\u7b11\uff0c\u4f46\u8868\u9762\u4e0a\u5374\u88c5\u51fa\u4e00\u526f\u6115\u7136\u795e\u8272\u9053\uff1a\u201c\u8d2b\u50e7\u4e0d\u4fe1\u2026\u2026\u65b9\u65bd\u4e3b\u6b66\u529f\u5f97\u81ea\u540d\u5e08\u4f20\u6388\uff0c\u90a3\u672c\u6765\u4e0d\u5047\uff0c\u53ea\u662f\uff0c\u4ed6\u518d\u5f3a\u4e5f\u5f3a\u4e0d\u8fc7\u8001\u83e9\u8428\u554a\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u4e24\u773c\u4e00\u77aa\u9053\uff1a\u201c\u8001\u592b\u5c82\u662f\u8bf4\u8c0e\u4e4b\u4eba\uff1f\u65b9\u8001\u5f1f\u80dc\u4e86\u8001\u592b\uff0c\u4e43\u662f\u5343\u771f\u4e07\u786e\u4e4b\u4e8b\u2026\u2026\u201d \u3000\u3000\u5f53\u4e0b\uff0c\u4ed6\u5f53\u7740\u4f17\u4eba\uff0c\u4fbf\u5c06\u81ea\u5df1\u548c\u65b9\u96ea\u4e14\u8f83\u91cf\u5251\u6cd5\uff0c\u5982\u4f55\u843d\u8d25\u4e4b\u4e8b\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5728\u4ed6\u8bdd\u97f3\u4e00\u843d\u4e4b\u9645\uff0c\u8fde\u5fd9\u63a5\u9053\uff1a\u201c\u5927\u5e08\uff0c\u8f9b\u8001\u524d\u8f88\u4e43\u662f\u6709\u5fc3\u6210\u5168\u665a\u8f88\uff0c\u4f60\u4eec\u53ef\u83ab\u5f53\u771f\u3002\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u7b11\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\u4e0d\u8bf4\uff0c\u8001\u8872\u4e5f\u660e\u767d\u8001\u83e9\u8428\u4e43\u662f\u6709\u5fc3\u6210\u5168\u4e8e\u4f60\u2026\u2026\u201d \u3000\u3000\u8f9b\u67cf\u516c\u95fb\u8a00\uff0c\u4e24\u773c\u4e00\u7ffb\u9053\uff1a\u201c\u597d\u554a\uff0c\u4f60\u4eec\u5f53\u771f\u628a\u8001\u592b\u770b\u4f5c\u4e3e\u4e16\u65e0\u654c\u4e4b\u4eba\uff1f\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u5ffd\u7136\u53e3\u55a7\u4f5b\u53f7\uff0c\u63a5\u9053\uff1a\u201c\u8001\u65bd\u4e3b\u529f\u529b\u901a\u7384\uff0c\u4e43\u662f\u4eba\u6240\u7686\u77e5\u4e4b\u4e8b\uff0c\u867d\u7136\u5929\u4e0b\u65e0\u654c\u56db\u5b57\uff0c\u5931\u4e4b\u8fc7\u5206\uff0c\u4f46\u5c31\u773c\u4e0b\u6b66\u6797\u800c\u8a00\uff0c\u90a3\u662f\u4e0d\u4f1a\u6709\u4eba\u9ad8\u8fc7\u8001\u65bd\u4e3b\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u663e\u7136\u88ab\u4ed6\u4eec\u8bf4\u5f97\u6709\u4e9b\u65e0\u53ef\u5948\u4f55\uff0c\u53ea\u597d\u6447\u5934\u53f9\u9053\uff1a\u201c\u4f60\u4eec\u771f\u662f\u8fc2\u5f97\u5f88\u2026\u2026\u201d\u8bdd\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u8001\u592b\u4e00\u4e3b\u4e4b\u4e2d\uff0c\u9664\u4e86\u5931\u624b\u5728\u65b9\u8001\u5f1f\u624b\u4e0b\uff0c\u8fd8\u66fe\u6709\u8fc7\u4e00\u6b21\u8d25\u7ee9\u2026\u2026\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u53ef\u771f\u7684\u5403\u4e00\u60ca\u4e86\uff01 \u3000\u3000\u4ed6\u76b1\u7709\u9053\uff1a\u201c\u8001\u83e9\u8428\uff0c\u8fd9\u2026\u2026\u54ea\u4f4d\u9ad8\u4eba\u5c45\u7136\u80fd\u591f\u51fb\u8d25\u8fc7\u4f60\u8001\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4eba\u4e0a\u6709\u4eba\uff0c\u5929\u5916\u6709\u5929\uff0c\u8001\u592b\u5982\u82e5\u5c06\u90a3\u4eba\u540d\u59d3\u8bf4\u51fa\uff0c\u53ea\u6015\u4f60\u4eec\u518d\u4e5f\u4e0d\u4f1a\u4e0d\u76f8\u4fe1\u4e86\u3002\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u8d2b\u50e7\u6d17\u8033\u606d\u542c\u2026\u2026\u201d \u3000\u3000\u8f9b\u67cf\u516c\u770b\u4e86\u5b89\u5c0f\u840d\u4e00\u773c\u9053\uff1a\u201c\u5c31\u662f\u5979\u7237\u7237\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u5b89\u65bd\u4e3b\u7684\u7237\u7237\u5417\uff1f\u4f46\u4e0d\u77e5\u6b64\u8001\u7684\u5927\u540d\u662f\u2026\u2026\u201d \u3000\u3000\u4ed6\u4f59\u8a00\u672a\u5df2\uff0c\u8f9b\u67cf\u516c\u5df2\u7136\u63a5\u9053\uff1a\u201c\u4e1c\u6d77\u60ca\u5929\u53df\u5b89\u4e5d\u516c\uff0c\u4f60\u4eec\u603b\u4e0d\u4f1a\u4e0d\u77e5\u9053\u5427\uff01\u201d \u3000\u3000\u679c\u7136\uff0c\u5b89\u4e5d\u516c\u4e09\u5b57\uff0c\u5bf9\u4ed6\u4eec\u4f3c\u662f\u6709\u7740\u6781\u5927\u7684\u9707\u6151\u4f5c\u7528\uff0c\u5165\u5723\u5927\u5e08\u95fb\u8a00\u4e4b\u540e\uff0c\u7adf\u662f\u8fde\u8fde\u9ad8\u55a7\u4f5b\u53f7\u3002 \u3000\u3000\u6562\u60c5\uff0c\u6570\u5341\u5e74\u7684\u5f80\u4e8b\uff0c\u8fd8\u6df1\u523b\u5728\u4e5d\u5927\u95e8\u6d3e\u5f1f\u5b50\u7684\u5fc3\u7075\u4e4b\u4e2d\u3002 \u3000\u3000\u5341\u5927\u9ad8\u624b\u5728\u5929\u53f0\u60e8\u8d25\u4e4b\u4e8b\uff0c\u662f\u4e2d\u539f\u6b66\u6797\u7684\u65e0\u6bd4\u76db\u4e8b\uff0c\u662f\u4ee5\uff0c\u8f9b\u67cf\u516c\u4e00\u8bf4\u5b89\u4e5d\u516c\u4e4b\u540d\uff0c\u4ed6\u4eec\u81ea\u7136\u662f\u76f8\u4fe1\u7684\u4e86\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u5ffd\u7136\u5411\u5b89\u5c0f\u840d\u5408\u5341\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\uff0c\u4ee4\u7956\u8fd8\u5728\u4e1c\u6d77\u7eb3\u798f\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6447\u5934\u9053\uff1a\u201c\u5148\u7956\u5df2\u7136\u4ed9\u53bb\u591a\u5e74\u4e86\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u767d\u7709\u8038\u52a8\uff0c\u957f\u957f\u5730\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e00\u4ee3\u5947\u4eba\uff0c\u7adf\u7136\u64a4\u624b\u800c\u53bb\uff0c\u53ef\u60dc\u5f97\u5f88\u2026\u2026\u201d \u3000\u3000\u8f9b\u67cf\u516c\u5728\u65c1\u7b11\u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8001\u592b\u4e0e\u5b89\u5144\u66fe\u7ecf\u52a8\u8fc7\u4e00\u6b21\u624b\uff0c\u8868\u9762\u4e0a\u770b\u6765\uff0c\u5230\u4e5f\u662f\u4e2a\u4e0d\u8f93\u4e0d\u8d62\u4e4b\u5c40\uff0c\u4f46\u8001\u592b\u660e\u767d\uff0c\u518d\u6253\u4e0b\u53bb\uff0c\u8001\u592b\u5fc5\u8d25\u65e0\u7591\u3002\u201d \u3000\u3000\u8fd9\u56de\u53ef\u662f\u5b89\u5c0f\u840d\u89c9\u5f97\u5947\u602a\u4e86\uff01\u5979\u7b11\u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u9053\u7406\u662f\u5341\u5206\u7b80\u5355\uff0c\u5f53\u65f6\u8001\u592b\u7684\u5251\u62db\uff0c\u5df2\u7136\u56de\u590d\u4f7f\u7528\u4e86\u4e03\u6b21\uff0c\u800c\u4ee4\u7956\u5374\u53ea\u7528\u4e86\u4e00\u6b21\uff0c\u53ef\u89c1\u8001\u592b\u5df2\u662f\u9ed4\u9a74\u6280\u7a77\uff0c\u800c\u4ee4\u7956\u5374\u4ecd\u6e38\u5203\u6709\u4f59\uff0c\u80dc\u8d25\u4e4b\u673a\uff0c\u5c82\u975e\u662f\u660e\u767d\u5f97\u5f88\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u90a3\u4e5f\u4e0d\u4e00\u5b9a\u54ea\uff01\u5018\u662f\u4f60\u8001\u8fd8\u85cf\u6709\u7edd\u62db\uff0c\u4e00\u6837\u8db3\u4ee5\u626d\u8f6c\u6218\u5c40\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u8001\u592b\u8fd8\u6709\u4ec0\u4e48\u7edd\u62db\uff1f\u53ea\u53ef\u60dc\u90a3\u65f6\u8fd9\u65b9\u8001\u5f1f\u7684\u5e08\u7956\u4e0d\u80af\u6559\u6211\uff0c\u5426\u5219\uff0c\u6211\u5012\u53ef\u4ee5\u53cd\u8d25\u4e3a\u80dc\u5462\uff01\u201d \u3000\u3000\u4ed6\u8bdd\u97f3\u4e00\u843d\uff0c\u5ffd\u7136\u7b11\u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5b89\u4e5d\u516c\u540e\u6765\u624d\u544a\u8bc9\u8001\u592b\uff0c\u4ed6\u5728\u5929\u53f0\u4e4b\u4f1a\uff0c\u4e43\u662f\u8d25\u5728\u65b9\u8001\u5f1f\u7684\u5e08\u7956\u624b\u4e0b\u2026\u2026\u201d \u3000\u3000\u8fd9\u4e9b\u4e8b\uff0c\u65b9\u96ea\u5b9c\u66fe\u7ecf\u542c\u5f97\u5e08\u7236\u63d0\u8fc7\uff0c\u662f\u6545\u5e76\u672a\u9732\u51fa\u4ec0\u4e48\u60ca\u5947\u4e4b\u8272\uff0c\u4f46\u5bf9\u522b\u4eba\u800c\u8a00\uff0c\u90fd\u662f\u6709\u4e9b\u524d\u6240\u672a\u95fb\u4e86\uff01 \u3000\u3000\u662f\u4ee5\uff0c\u4e00\u65f6\u95f4\uff0c\u5ba4\u5185\u4e4b\u4eba\uff0c\u5168\u4e3a\u4e4b\u795e\u5f80\uff01 \u3000\u3000\u5b89\u5c0f\u840d\u6c89\u541f\u4e86\u4e00\u4f1a\uff0c\u7b11\u9053\uff1a\u201c\u65b9\u5144\uff0c\u8fd9\u4e8b\u6211\u5df2\u77e5\u9053\uff0c\u4f60\u5e08\u7956\u8fd8\u53bb\u90a3\u4e1c\u6d77\u73a9\u4e86\u5f88\u4e45\u5462\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u8d24\u5f1f\u8bf4\u5f97\u4e0d\u9519\uff0c\u5148\u5e08\u4e5f\u66fe\u5bf9\u6211\u63d0\u8fc7\u8fd9\u4e9b\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bed\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u5374\u662f\u6709\u610f\u5730\u5c94\u5f00\u4e86\u8bdd\u9898\uff0c\u63a5\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u90a3\u795d\u516c\u660e\u8001\u4eba\uff0c\u4e0d\u77e5\u4eca\u65e5\u4f1a\u4e0d\u4f1a\u6765\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5446\u4e86\u4e00\u5446\uff0c\u5931\u58f0\u9053\uff1a\u201c\u6211\u600e\u4e48\u77e5\u9053\uff1f\u4eca\u513f\u6211\u4e5f\u6ca1\u53bb\u592a\u5ba4\u5440\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u8fd9\u5c31\u53bb\u770b\u770b\u53ef\u597d\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u4e00\u542c\uff0c\u683c\u683c\u7b11\u9053\uff1a\u201c\u5f53\u7136\u597d\uff01\u201d \u3000\u3000\u5a07\u8eaf\u4e00\u62e7\uff0c\u4fbf\u5411\u5916\u5954\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5374\u662f\u5411\u5165\u5723\u5927\u5e08\u4e00\u63d6\u9053\uff1a\u201c\u665a\u8f88\u53bb\u53bb\u5c31\u56de\u2026\u2026\u201d\u4e5f\u4e0d\u62db\u547c\u522b\u4eba\uff0c\u4fbf\u81ea\u51fa\u95e8\u800c\u53bb\u3002 \u3000\u3000\u8f9b\u67cf\u516c\u770b\u5f97\u6014\u4e86\u4e00\u6014\uff0c\u5ffd\u7136\u5927\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u7b49\u6211\u4e00\u9053\u554a\u2026\u2026\u201d \u3000\u3000\u53ea\u89c1\u4ed6\u53cc\u80a9\u4e00\u6447\uff0c\u4eba\u5df2\u98de\u51fa\u95e8\u5916\u3002 \u3000\u3000\u5239\u90a3\u95f4\u4e09\u4eba\u4e00\u53bb\uff0c\u5ba4\u5185\u8bf8\u4eba\u4e5f\u5c31\u5404\u81ea\u6563\u53bb\u4e86\uff01 \u3000\u3000\u5b8b\u5b5a\u5411\u970d\u9e23\u98ce\u7b11\u4e86\u4e00\u7b11\uff0c\u8981\u4ed6\u7559\u5fc3\u90a3\u90d1\u5927\u521a\u51e0\u65f6\u9192\u6765\uff0c\u7adf\u4e5f\u72ec\u81ea\u4e00\u4eba\uff0c\u51fa\u4e86\u5c11\u6797\u5c71\u95e8\u3002 \u3000\u3000\u6562\u60c5\uff0c\u4ed6\u4e43\u662f\u5728\u65b9\u96ea\u5b9c\u3001\u5b89\u5c0f\u840d\u548c\u8f9b\u67cf\u516c\u8eab\u540e\uff0c\u5954\u5411\u90a3\u5d69\u5c71\u592a\u5ba4\u5cf0\u5934\u3002 \u3000\u3000\u56db\u4eba\u51e0\u4e4e\u540c\u65f6\u5230\u4e86\u90a3\u77f3\u6d1e\u7684\u95e8\u524d\u3002 \u3000\u3000\u4f46\u5b89\u5c0f\u840d\u5374\u662f\u7b2c\u4e00\u4e2a\u62a2\u8fdb\u6d1e\u5185\u4e4b\u4eba\u3002 \u3000\u3000\u53ea\u89c1\u5979\u5a07\u8eaf\u4e00\u95ea\uff0c\u5ffd\u5730\u5012\u8dc3\u800c\u51fa\uff0c\u5927\u58f0\u9053\uff1a\u201c\u90a3\u795d\u8001\u4e0d\u5728\u6d1e\u5185\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u76b1\u7709\u9053\uff1a\u201c\u4e0d\u5728\u5417\uff1f\u201d\u8bf4\u8bdd\u95f4\uff0c\u5374\u5df2\u4f38\u5934\u5411\u6d1e\u5185\u5f20\u671b\u3002 \u3000\u3000\u679c\u7136\u6d1e\u5185\u6ca1\u6709\u795d\u516c\u660e\u7684\u8eab\u5f62\u3002 \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u83ab\u975e\u4ed6\u53bb\u5582\u9e1f\u513f\u6765\u7684\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4e0d\u4f1a\uff01\u6211\u5c31\u662f\u6253\u90a3\u513f\u6765\u7684\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u95fb\u8a00\uff0c\u6014\u4e86\u6014\uff0c\u63a5\u9053\uff1a\u201c\u4ec0\u4e48\u9e1f\u513f\uff1f\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u795d\u516c\u660e\u8fd9\u4e9b\u5e74\u6765\uff0c\u5929\u5929\u5728\u5d69\u5c71\u4e2d\u4e0e\u9e1f\u513f\u4e3a\u4f34\uff0c\u8fd9\u592a\u5ba4\u5cf0\u9876\uff0c\u5c31\u6709\u4e00\u53ea\u5947\u9e1f\uff0c\u662f\u4ed6\u6240\u517b\u7684\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u604d\u7136\u7b11\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff01\u201d \u3000\u3000\u4ed6\u56de\u987e\u4e86\u5b89\u5c0f\u840d\u4e00\u773c\uff0c\u9053\uff1a\u201c\u840d\u513f\uff0c\u90a3\u795d\u516c\u660e\u771f\u7684\u4e0d\u5728\u9e1f\u5de2\u6240\u5728\u4e4b\u5904\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u6211\u600e\u4f1a\u54c4\u9a97\u8f9b\u516c\u516c\u5462\uff1f\u201d \u3000\u3000\u5979\u5ffd\u7136\u7c89\u8138\u53d8\u8272\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u8fd8\u6709\u2026\u2026\u90a3\u4e00\u53ea\u91d1\u96c0\u600e\u7684\u4e5f\u4e0d\u89c1\u4e86\u5462\uff1f\u201d\u6562\u60c5\uff0c\u5979\u624d\u60f3\u8d77\uff0c\u4e0d\u4f46\u795d\u516c\u660e\u4e0d\u5728\u90a3\u9e1f\u5de2\u4e4b\u65c1\u5979\u7ecf\u8fc7\u9e1f\u5de2\u4e4b\u65f6\uff0c\u8fde\u90a3\u53ea\u91d1\u9e4a\u4e5f\u4e0d\u5728\u5de2\u5185\u4e86\uff01 \u3000\u3000\u5b8b\u5b5a\u95fb\u8a00\uff0c\u5bfb\u601d\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u662f\u4e86\uff01\u8001\u673d\u660e\u767d\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4f60\u660e\u767d\u4ec0\u4e48\uff1f\u90a3\u795d\u8001\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4ed6\u5417\uff1f\u5927\u6982\u53e6\u5916\u627e\u4e86\u4e2a\u5de2\u513f\uff0c\u628a\u90a3\u53ea\u91d1\u9e4a\u4e5f\u79fb\u8fc7\u53bb\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u90a3\u9e4a\u5de2\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u4e0d\u77e5\u9053\uff01\u4e0d\u8fc7\uff0c\u795d\u516c\u660e\u4e0d\u4f1a\u51fa\u4e86\u4ec0\u4e48\u5c94\uff0c\u90a3\u662f\u4e00\u5b9a\u7684\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u4e00\u542c\uff0c\u4f3c\u662f\u7a0d\u7a0d\u5730\u653e\u4e86\u5fc3\uff0c\u9053\uff1a\u201c\u60f3\u5fc5\u4f60\u6ca1\u6709\u8bf4\u9519\u3002\u201d \u3000\u3000\u8f9b\u67cf\u516c\u5728\u65c1\u76b1\u7709\u9053\uff1a\u201c\u795d\u516c\u660e\u4e0d\u5728\u6b64\u5904\uff0c\u53c8\u4f1a\u53bb\u4e86\u54ea\u91cc\u5462\uff1f\u201d \u3000\u3000\u4ed6\u4f3c\u662f\u81ea\u8a00\u81ea\u8bed\uff0c\u4e5f\u5e76\u672a\u7b49\u5f97\u522b\u4eba\u56de\u7b54\u3002 \u3000\u3000\u4f46\u65b9\u96ea\u5b9c\u5374\u63a5\u9053\uff1a\u201c\u8f9b\u8001\uff0c\u4f1a\u4e0d\u4f1a\u2026\u2026\u795d\u8001\u6b63\u597d\u53bb\u4e86\u5c11\u6797\u54b1\u4eec\u4e2d\u9014\u9519\u8fc7\u4e86\u5462\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u8fd9\u4e5f\u53ef\u80fd\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u4e00\u542c\uff0c\u8f6c\u8eab\u4fbf\u8981\u4e0b\u53bb\u3002 \u3000\u3000\u4f46\u5b8b\u5b5a\u5374\u7b11\u9053\uff1a\u201c\u4e14\u6162\uff0c\u5982\u90a3\u795d\u516c\u660e\u53bb\u4e86\u5c11\u6797\uff0c\u54b1\u4eec\u56de\u53bb\u4e5f\u4e0d\u7528\u6025\u5728\u8fd9\u4e00\u65f6\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u8fd9\u5012\u4e0d\u9519\uff0c\u4e0d\u8fc7\u2026\u2026\u4f60\u8001\u8981\u6211\u4eec\u7559\u5728\u8fd9\u5c71\u4e0a\uff0c\u8fd8\u6709\u4ec0\u4e48\u4e8b\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8001\u673d\u60f3\u770b\u4e00\u4e0b\uff0c\u4e5f\u8bb8\uff0c\u90a3\u795d\u516c\u660e\u5728\u6d1e\u4e2d\u7559\u4e0b\u6709\u4ec0\u4e48\u6697\u8bb0\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5931\u58f0\u9053\uff1a\u201c\u4ed6\u5982\u662f\u53bb\u4e86\u5c11\u6797\uff0c\u7559\u4e0b\u6697\u8bb0\uff0c\u53c8\u6709\u4f55\u7528\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u4ed6\u53bb\u4e86\u5c11\u6797\uff0c\u4e5f\u53ea\u662f\u6211\u4eec\u63e3\u6d4b\u4e4b\u8bcd\uff0c\u5018\u662f\u4ed6\u5e76\u672a\u66fe\u53bb\uff0c\u54b1\u4eec\u5c82\u4e0d\u53c8\u8981\u91cd\u6765\u6b64\u95f4\u67e5\u770b\u4e86\u5417\u2026\u2026\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u4eba\u5df2\u8fdb\u5165\u77f3\u6d1e\u4e4b\u4e2d\u3002 \u3000\u3000\u4ed6\u8bf4\u662f\u67e5\u770b\uff0c\u5012\u771f\u662f\u770b\u5f97\u5341\u5206\u4ed4\u7ec6\u3002 \u3000\u3000\u51e0\u4e4e\u8fde\u90a3\u6d1e\u5185\u7684\u4e00\u77f3\u4e00\u74e6\uff0c\u90fd\u8981\u770b\u4e0a\u4e00\u4f1a\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u89c1\u4ed6\u5982\u6b64\u7528\u5fc3\u7ec6\u60f3\uff0c\u4e0d\u7531\u81ea\u4e3b\u5730\u4e5f\u5728\u56db\u9762\u6d1e\u58c1\u4e4b\u4e0a\u6d4f\u89c8\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u5374\u662f\u77a7\u4e0d\u51fa\u6709\u4ec0\u4e48\u53ef\u7591\u4e4b\u5904\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5b8b\u5b5a\u5df2\u5c06\u6574\u4e2a\u77f3\u6d1e\uff0c\u90fd\u5df2\u8d70\u904d\uff0c\u5ffd\u7136\u957f\u957f\u5730\u5401\u4e86\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u90a3\u795d\u5144\u6ca1\u6709\u51fa\u4e8b\uff0c\u54b1\u4eec\u4e0d\u7528\u803d\u5fc3\u4e86\uff01\u201d \u3000\u3000\u539f\u6765\u4ed6\u770b\u4e86\u534a\u664c\uff0c\u4e43\u662f\u60f3\u67e5\u660e\u795d\u516c\u660e\u662f\u5426\u51fa\u4e86\u4e8b\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u4e00\u6014\u9053\uff1a\u201c\u4f60\u2026\u2026\u600e\u77e5\u9053\u4ed6\u5e76\u672a\u51fa\u4e8b\uff1f\u521a\u624d\u4f60\u4e0d\u662f\u8bf4\u4ed6\u53ef\u80fd\u53bb\u4e86\u5c11\u6797\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u6211\u8bf4\u4ed6\u53bb\u4e86\u5c11\u6797\uff0c\u672c\u662f\u731c\u60f3\u7684\u4e8b\uff0c\u81f3\u4e8e\u6211\u8bf4\u4ed6\u662f\u4e0d\u51fa\u4e8b\uff0c\u4e43\u662f\u6709\u7740\u8bc1\u636e\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4ec0\u4e48\u8bc1\u636e\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u6307\u7740\u77f3\u6d1e\u9053\uff1a\u201c\u8fd9\u6d1e\u5185\u4e00\u5207\u4e95\u7136\u6709\u5e8f\uff0c\u8868\u793a\u90a3\u795d\u5144\u4e34\u53bb\u4e4b\u65f6\uff0c\u5341\u5206\u4ece\u5bb9\uff0c\u5e76\u65e0\u8352\u4e71\u4e4b\u8c61\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u683c\u683c\u4e00\u7b11\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff01\u4f46\u5982\u795d\u8001\u56e0\u4e3a\u8fd9\u4e00\u4e24\u65e5\u4fbf\u8981\u79bb\u53bb\uff0c\u65e9\u5df2\u5c06\u6d1e\u5185\u6574\u7406\u5f97\u4e95\u7136\u6709\u5e8f\u4e86\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u5f97\u6697\u9053\uff1a\u201c\u5929\u4e0b\u90a3\u6709\u8fd9\u4e48\u5de7\u7684\u4e8b\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u663e\u7136\u8fd8\u6709\u4e9b\u4e0d\u653e\u5fc3\uff0c\u6447\u5934\u9053\uff1a\u201c\u665a\u8f88\u4e0d\u4fe1\uff0c\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u4f3c\u662f\u8fd8\u5f85\u89e3\u8bf4\uff0c\u4f46\u65b9\u96ea\u5b9c\u5728\u65c1\u7b11\u9053\uff1a\u201c\u7b97\u4e86\uff0c\u54b1\u4eec\u4e0d\u5fc5\u518d\u5728\u8fd9\u513f\u7a7a\u7b49\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8001\u5f1f\uff0c\u54b1\u4eec\u4e0b\u5c71\u53bb\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u5f53\u5148\u5411\u5cf0\u4e0b\u5954\u53bb\u3002 \u3000 \u7b2c\u4e09\u5341\u4e8c\u56de\u3000\u5947\u5cf0\u7a81\u8d77 \u3000 \u3000\u3000\u5b89\u5c0f\u840d\u4e00\u89c1\u8f9b\u67cf\u516c\u8d70\u4e86\uff0c\u5f53\u4e0b\u4e5f\u5c31\u4e0d\u518d\u591a\u8bf4\uff0c\u548c\u65b9\u96ea\u5b9c\u3001\u5b8b\u5b5a\u4e00\u9053\u51fa\u4e86\u6d1e\u6765\u3002\u5c31\u5728\u4ed6\u4eec\u51c6\u5907\u5411\u5cf0\u4e0b\u884c\u53bb\u4e4b\u65f6\uff0c\u53ea\u89c1\u8f9b\u67cf\u516c\u5df2\u7136\u53cc\u624b\u6a2a\u62b1\u4e00\u4eba\uff0c\u5411\u5cf0\u4e0a\u5954\u6765\u3002 \u3000\u3000\u4e09\u4eba\u5403\u4e86\u4e00\u60ca\uff0c\u51dd\u76ee\u671b\u53bb\uff0c\u5b8b\u5b5a\u5ffd\u7136\u5931\u58f0\u53eb\u9053\uff1a\u201c\u7cdf\u4e86\uff0c\u8fd9\u4eba\u4e0d\u6b63\u662f\u90a3\u795d\u5144\u5417\uff1f\u201d\u6562\u60c5\uff0c\u795d\u516c\u660e\u90a3\u8eab\u8863\u886b\uff0c\u4e43\u662f\u4ed6\u4e70\u6765\u7684\uff0c\u662f\u4ee5\uff0c\u8fdc\u8fdc\u5730\u4ed6\u4e00\u773c\u5c31\u53ef\u4ee5\u77a7\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u5927\u60ca\u9053\uff1a\u201c\u771f\u7684\u5417\uff1f\u795d\u8001\u83ab\u975e\u88ab\u4eba\u5bb3\u4e86\u2026\u2026\u201d \u3000\u3000\u4e24\u4eba\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u90a3\u8f9b\u67cf\u516c\u5df2\u7136\u4e0a\u4e86\u5c71\u5cf0\u3002\u4e09\u4eba\u987f\u65f6\u56f4\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u8f9b\u8001\uff0c\u8fd9\u4eba\u53ef\u662f\u795d\u516c\u660e\uff1f\u201d \u3000\u3000\u4ed6\u8bdd\u5df2\u51fa\u53e3\uff0c\u76ee\u5149\u624d\u770b\u6e05\u4e86\u8f9b\u67cf\u516c\u624b\u4e2d\u6240\u62b1\u4e4b\u4eba\u662f\u8c01\uff01 \u3000\u3000\u539f\u6765\u90a3\u4eba\u5e74\u7eaa\u53ea\u6709\u56db\u5341\u4e0a\u4e0b\uff0c\u81ea\u7136\u4e0d\u4f1a\u662f\u795d\u516c\u660e\u4e86\uff01 \u3000\u3000\u5b8b\u5b5a\u3001\u5b89\u5c0f\u840d\u4e5f\u77a7\u51fa\u8fd9\u4eba\u4e0d\u662f\u795d\u516c\u660e\uff01 \u3000\u3000\u53ea\u662f\uff0c\u5b8b\u5b5a\u5374\u8fde\u58f0\u53eb\u9053\uff1a\u201c\u5947\u602a\u5440\uff01\u5947\u602a\uff0c\u8001\u5077\u513f\u4e3a\u795d\u516c\u660e\u4e70\u7684\u8863\u670d\uff0c\u600e\u4f1a\u7a7f\u5230\u4e86\u8fd9\u5bb6\u4f19\u8eab\u4e0a\u5462\uff1f\u201d \u3000\u3000\u4ed6\u8fd9\u4e48\u4e00\u8bf4\uff0c\u8fde\u65b9\u96ea\u5b9c\u4e5f\u5fcd\u4e0d\u4f4f\u5931\u58f0\u79f0\u5947\u4e86\uff01 \u3000\u3000\u56e0\u4e3a\uff0c\u8fd9\u4e8b\u5927\u8fc7\u5947\u602a\uff0c\u82e5\u975e\u795d\u516c\u660e\u88ab\u4eba\u6240\u5bb3\uff0c\u8863\u670d\u53c8\u600e\u4f1a\u7a7f\u5230\u522b\u4eba\u8eab\u4e0a\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u8d70\u5230\u6d1e\u53e3\uff0c\u653e\u4e0b\u4e86\u624b\u4e2d\u5c38\u4f53\uff0c\u76b1\u7709\u9053\uff1a\u201c\u8fd9\u4eba\u4e0d\u662f\u795d\u516c\u660e\u5427\uff01\u201d\u6562\u60c5\uff0c\u4ed6\u4e5f\u4e0d\u6562\u786e\u5b9a\u8fd9\u4eba\u662f\u5426\u662f\u795d\u516c\u660e\uff0c\u60f3\u662f\u4ed6\u4eec\u5206\u522b\u751a\u4e45\uff0c\u8f9b\u67cf\u516c\u5df2\u4e0d\u80fd\u786e\u8bb0\u795d\u516c\u660e\u7684\u5f62\u8c8c\u4e86\uff01 \u3000\u3000\u5b8b\u5b5a\u63a5\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u8fd9\u4eba\u8d77\u7801\u6bd4\u795d\u516c\u660e\u5e74\u8f7b\u4e86\u4e09\u5341\u51e0\u51fa\u5934\uff0c\u5f53\u7136\u4e0d\u4f1a\u662f\u4ed6\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u8fd9\u4e00\u8eab\u8863\u7740\uff0c\u5f53\u771f\u662f\u4f60\u4e3a\u795d\u8001\u6240\u4e70\u7684\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u662f\u8001\u592b\u4eb2\u81ea\u4e70\u6765\uff0c\u81ea\u662f\u4e0d\u4f1a\u8ba4\u9519\uff01\u4e0d\u8fc7\uff0c\u90a3\u795d\u8001\u513f\u4e3a\u4f55\u81ea\u5df1\u4e0d\u7a7f\uff0c\u800c\u8981\u8ba9\u522b\u4eba\u7a7f\u4e0a\u4e86\u5462\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u60f3\u4e86\u4e00\u60f3\uff0c\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u59d1\u5a18\u77e5\u9053\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u8fd9\u5bb6\u4f19\u516b\u6210\u662f\u8ddf\u4f60\u8001\u5b66\u8fc7\u624b\u827a\u5427\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u5077\uff0c\u83ab\u975e\u4f60\u8bf4\u8fd9\u4eba\u5077\u4e86\u795d\u516c\u660e\u7684\u8863\u670d\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u96be\u9053\u4e0d\u662f\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8001\u673d\u5012\u771f\u662f\u8ff7\u7cca\u4e86\uff01\u8fd9\u4eba\u51ed\u4ec0\u4e48\u80fd\u5077\u5f97\u5230\u795d\u8001\u54e5\u7684\u8863\u670d\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u8d24\u5f1f\u8bf4\u5f97\u4e0d\u9519\uff0c\u8fd9\u4eba\u5012\u662f\u5f88\u53ef\u80fd\u5077\u4e86\u795d\u8001\u7684\u8863\u670d\u5462\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u8fd9\u65f6\u5df2\u5c06\u90a3\u4eba\u7684\u8863\u886b\u89e3\u5f00\uff0c\u67e5\u770b\u4ed6\u81f4\u6b7b\u7684\u539f\u56e0\uff0c\u53ea\u89c1\u4ed6\u5ffd\u7136\u5931\u58f0\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u638c\u529b\uff0c\u7adf\u7136\u5982\u6b64\u6b79\u6bd2\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u8fde\u5fd9\u4f4e\u4e0b\u5934\u53bb\uff0c\u53ea\u89c9\u90a3\u4e2d\u5e74\u4eba\u7684\u80f8\u524d\u679c\u7136\u6709\u7740\u4e00\u53cc\u7ea2\u8272\u7684\u638c\u5370\uff0c\u5341\u5206\u660e\u786e\u7684\u7559\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5b8b\u5b5a\u4e5f\u770b\u5230\u4e86\u90a3\u53ea\u638c\u5370\uff0c\u8138\u8272\u5ffd\u7136\u5927\u53d8\uff0c\u5931\u58f0\u9053\uff1a\u201c\u8d64\u7130\u638c\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u8d64\u7130\u638c\u4e43\u662f\u5929\u5357\u602a\u53df\u795d\u9ad8\u67ab\u7684\u72ec\u95e8\u6b66\u529f\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u6b63\u662f\u90a3\u795d\u8001\u7684\u72ec\u95e8\u6b66\u529f\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u6b64\u4eba\u662f\u6b7b\u5728\u795d\u516c\u660e\u624b\u4e0b\u7684\u4e86\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u53ef\u80fd\u662f\uff0c\u4f46\u4e5f\u53ef\u80fd\u4e0d\u662f\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u7eb5\u7709\u9053\uff1a\u201c\u662f\u5c31\u662f\uff0c\u4e0d\u662f\u5c31\u4e0d\u662f\uff0c\u4e3a\u4f55\u8981\u8bf4\u53ef\u80fd\u662f\uff0c\u53c8\u53ef\u80fd\u4e0d\u662f\u5462\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8d64\u7130\u638c\u867d\u662f\u795d\u5bb6\u7684\u72ec\u95e8\u6b66\u529f\uff0c\u4f46\u90a3\u4e94\u9b54\u4e4b\u4e2d\u7684\u6bd2\u5927\u592b\u6076\u5b54\u660e\uff0c\u6b63\u662f\u795d\u9ad8\u67ab\u7684\u5f1f\u5b50\uff0c\u8f9b\u8001\u83ab\u975e\u4e0d\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u6bd2\u5927\u592b\u4e5f\u4f1a\u8d64\u7130\u638c\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u795d\u5bb6\u7684\u5f1f\u5b50\uff0c\u54ea\u6709\u4e0d\u4f1a\u4e4b\u7406\uff1f\u4e0d\u8fc7\uff0c\u5b8b\u67d0\u8ba4\u4e3a\uff0c\u6076\u5b54\u660e\u773c\u4e0b\u53ea\u6015\u8fd8\u4e0d\u6562\u524d\u6765\u5d69\u5c71\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u6076\u5b54\u660e\u4e3a\u4eba\uff0c\u673a\u8bc8\u767e\u51fa\uff0c\u4ed6\u5982\u662f\u6ca1\u7edd\u5bf9\u7684\u628a\u63e1\uff0c\u90a3\u662f\u4e00\u5b9a\u4e0d\u4f1a\u524d\u6765\u7684\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6709\u9053\u7406\uff01\u90a3\u6bd2\u5927\u592b\u5982\u662f\u672a\u6765\u6b64\uff0c\u8fd9\u4eba\u516b\u6210\u662f\u6b7b\u5728\u795d\u516c\u660e\u7684\u624b\u4e0b\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u7167\u8bf4\u4e0d\u8be5\u518d\u6709\u522b\u4eba\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u4e86\uff0c\u4ed6\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u8fd9\u4e8b\u5728\u60c5\u7406\u4e0a\u4f3c\u4e4e\u6709\u4e9b\u8bb2\u4e0d\u901a\u5427\uff01\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u54ea\u4e9b\u8bb2\u4e0d\u901a\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u4eba\u5982\u662f\u56e0\u4e3a\u5077\u4e86\u795d\u8001\u7684\u8863\u886b\u800c\u6fc0\u6012\u4e86\u795d\u8001\uff0c\u90a3\u795d\u8001\u600e\u4e48\u6740\u4ed6\u4e4b\u540e\uff0c\u4e0d\u5c06\u8863\u886b\u5265\u4e86\u4e0b\u6765\u5462\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u5982\u662f\u8001\u673d\u80fd\u660e\u767d\u8fd9\u4e00\u70b9\uff0c\u5c82\u4e0d\u662f\u4e00\u5207\u90fd\u65e9\u5c31\u660e\u767d\u4e86\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u665a\u8f88\u6709\u4e00\u79cd\u60f3\u6cd5\uff0c\u53ea\u4e0d\u77e5\u80fd\u4e0d\u80fd\u8bf4\u51fa\u6765\u2026\u2026\u201d \u3000\u3000\u5b8b\u5b5a\u4e5f\u9053\uff1a\u201c\u8001\u5f1f\u8bf7\u8bf4\u65e0\u59a8\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u665a\u8f88\u8ba4\u4e3a\uff0c\u8fd9\u4eba\u4e0d\u662f\u4f24\u5728\u795d\u8001\u624b\u4e0b\u2026\u2026\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4f55\u4ee5\u89c1\u5f97\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u665a\u8f88\u53ea\u662f\u8fd9\u4e48\u611f\u89c9\u800c\u5df2\uff01\u81f3\u4e8e\u8bf4\u662f\u4e3a\u4ec0\u4e48\uff0c\u665a\u8f88\u53ef\u5c31\u65e0\u6cd5\u660e\u767d\u5730\u8bf4\u51fa\u6765\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u8fd9\u4e0d\u7b49\u4e8e\u6ca1\u6709\u8bf4\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u665a\u8f88\u56e0\u4e3a\u89c9\u5f97\u5176\u4e2d\u4f3c\u6709\u7279\u6b8a\u4e4b\u5904\u2026\u2026\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4ec0\u4e48\u7279\u6b8a\u4e4b\u5904\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u665a\u8f88\u731c\u60f3\uff0c\u90a3\u795d\u8001\u65e2\u975e\u6740\u4eba\u4e4b\u4eba\uff0c\u5219\u8fd9\u4eba\u4e4b\u6b7b\uff0c\u4e0d\u4f1a\u90a3\u7b49\u7b80\u5355\uff0c\u5176\u4e2d\u53ef\u80fd\u6709\u7740\u5f88\u5927\u7684\u539f\u56e0\u3002\u201d \u3000\u3000\u8f9b\u67cf\u516c\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff01\u8001\u592b\u4e5f\u662f\u8fd9\u4e48\u60f3\u554a\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5ffd\u7136\u683c\u683c\u4e00\u7b11\u9053\uff1a\u201c\u8f9b\u516c\u516c\uff0c\u4f60\u4eec\u4fe9\u8bf4\u4e86\u534a\u5929\uff0c\u5168\u662f\u5e9f\u8bdd\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u7b11\u9053\uff1a\u201c\u4f60\u53c8\u6709\u4ec0\u4e48\u9ad8\u89c1\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u9ad8\u89c1\u6ca1\u6709\uff0c\u53ea\u662f\u5012\u6709\u4e00\u70b9\u513f\u4e0d\u8010\u70e6\u4e86\uff01\u8f9b\u516c\u516c\uff0c\u54b1\u4eec\u5927\u6982\u4e5f\u8be5\u4e0b\u5c71\u4e86\u5427\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4e0b\u5c71\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u8f9b\u516c\u516c\u8fd8\u4e0d\u613f\u8d70\u5417\uff1f\u8fd9\u5c71\u4e0b\u65e2\u7136\u4e0d\u89c1\u795d\u8001\uff0c\u54b1\u4eec\u7559\u5728\u8fd9\u513f\u53c8\u6709\u4ec0\u4e48\u7528\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u8f9b\u8001\uff0c\u5b89\u59d1\u5a18\u8bf4\u5f97\u4e0d\u9519\uff0c\u54b1\u4eec\u679c\u7136\u6ca1\u6709\u518d\u7559\u5728\u6b64\u95f4\u7684\u5fc5\u8981\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u65e2\u7136\u4f60\u4eec\u90fd\u8bf4\u7559\u6b64\u65e0\u7528\uff0c\u90a3\u54b1\u4eec\u5c31\u4e0b\u5c71\u5427\uff01\u201d\u4ed6\u8bed\u97f3\u4e00\u987f\uff0c\u5411\u5b8b\u5b5a\u63a5\u9053\uff1a\u201c\u8001\u5077\u513f\uff0c\u54b1\u4eec\u8981\u4e0d\u8981\u628a\u8fd9\u4eba\u5c38\u4f53\u5f04\u4e0b\u5c71\u53bb\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u6447\u5934\u9053\uff1a\u201c\u4e0d\u7528\u4e86\uff01\u5c31\u8ba9\u4ed6\u7559\u5728\u8fd9\u5c71\u9876\u4e4b\u4e0a\uff0c\u4e5f\u8bb8\u66f4\u597d\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u4e00\u6014\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u66f4\u597d\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u6b64\u95f4\u6bd4\u90a3\u5bfa\u4e2d\u5bd2\u51b7\u751a\u591a\uff0c\u6b64\u4eba\u5c38\u4f53\u7559\u5728\u5c71\u4e0a\uff0c\u81f3\u5c11\u767e\u65e5\u4e4b\u5185\uff0c\u4e0d\u4f1a\u8150\u70c2\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u5b8b\u8001\u679c\u7136\u60f3\u5f97\u751a\u662f\u5468\u5230\uff0c\u5982\u662f\u6362\u4e0a\u665a\u8f88\uff0c\u53ea\u6015\u5c31\u8981\u5c06\u8fd9\u4eba\u5c38\u4f53\u5f04\u4e0b\u5c71\u53bb\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u95fb\u8a00\uff0c\u5446\u4e86\u4e00\u5446\u9053\uff1a\u201c\u65b9\u5927\u54e5\uff0c\u4e3a\u4ec0\u4e48\u8fd9\u4eba\u5c38\u4f53\u4e0d\u80fd\u8fd0\u4e0b\u5c71\u53bb\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u5018\u662f\u90a3\u795d\u8001\u6765\uff0c\u8981\u60f3\u67e5\u770b\u6b64\u4eba\u5c38\u4f53\uff0c\u7559\u5728\u5c71\u4e0a\uff0c\u5fc5\u7136\u4e0d\u4f1a\u8150\u70c2\uff0c\u90a3\u5c82\u4e0d\u662f\u6bd4\u8fd0\u4e0b\u5c71\u53bb\u65b9\u4fbf\u5f97\u591a\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u8fd9\u624d\u604d\u7136\u7b11\u9053\uff1a\u201c\u662f\u554a\uff01\u6211\u600e\u4e48\u6ca1\u6709\u60f3\u5230\u5462\uff0c\u5230\u5e95\u8fd8\u662f\u6211\u7b28\u4e9b\u2026\u2026\u201d \u3000\u3000\u8f9b\u67cf\u516c\u5ffd\u7136\u5927\u7b11\u9053\uff1a\u201c\u7b28\u7684\u53ea\u6015\u4e0d\u53ea\u4f60\u4e00\u4f4d\uff0c\u8001\u592b\u4e0d\u4e5f\u662f\u6ca1\u6709\u60f3\u5230\u5417\u2026\u2026\u201d\u5ffd\u7136\u95ea\u8eab\u5411\u5c71\u4e0b\u5954\u53bb\u3002 \u3000\u3000\u5b8b\u5b5a\u3001\u65b9\u96ea\u5b9c\u3001\u5b89\u5c0f\u840d\u4e09\u4eba\u4e5f\u4e0d\u518d\u505c\u7559\uff0c\u8ddf\u5728\u8f9b\u67cf\u516c\u8eab\u540e\uff0c\u4e0b\u4e86\u592a\u5ba4\u3002 \u3000\u3000\u4e00\u884c\u4eba\u56de\u5230\u5c11\u6797\uff0c\u5df2\u662f\u9ec4\u660f\u65f6\u5206\uff0c\u53ea\u89c1\u5c11\u6797\u5bfa\u5185\u7684\u5404\u5904\uff0c\u90fd\u5df2\u706f\u5149\u80fd\u660e\uff0c\u5c71\u95e8\u4e4b\u5916\uff0c\u66f4\u662f\u6392\u5217\u4e86\u6570\u5341\u540d\u50e7\u4fa3\uff0c\u4e2a\u4e2a\u660e\u706f\u6267\u6756\uff0c\u5982\u4e34\u5927\u654c\u4e00\u822c\uff01 \u3000\u3000\u8f9b\u67cf\u516c\u9996\u5148\u62b5\u8fbe\uff0c\u770b\u5230\u8fd9\u7b49\u60c5\u5f62\uff0c\u4e0d\u7531\u5f97\u505c\u4e0b\u6b65\u6765\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u65b9\u96ea\u5b9c\u7b49\u4e09\u4eba\uff0c\u4e5f\u81ea\u8d76\u5230\u3002 \u3000\u3000\u53ea\u89c1\u5165\u5723\u5927\u5e08\u5df2\u7136\u6253\u5c71\u95e8\u4e4b\u524d\uff0c\u8fce\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u8f9b\u67cf\u516c\u4e24\u773c\u4e00\u77aa\uff0c\u5927\u58f0\u9053\uff1a\u201c\u548c\u5c1a\uff0c\u8fd9\u662f\u600e\u4e48\u4e86\uff1f\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u5408\u5341\u4e00\u53f9\u9053\uff1a\u201c\u8001\u83e9\u8428\uff0c\u4f60\u4eec\u521a\u521a\u79bb\u53bb\u4e0d\u4e45\uff0c\u7adf\u7136\u6709\u4eba\u4e0a\u5c11\u6797\u6ecb\u4e8b\u6765\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u542c\u5f97\u51b7\u54fc\u4e86\u4e00\u58f0\uff1a\u201c\u90a3\u662f\u4ec0\u4e48\u4eba\uff1f\u4f60\u4eec\u53ef\u66fe\u5c06\u4ed6\u64d2\u4f4f\u4e86\uff1f\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u6447\u5934\u9053\uff1a\u201c\u6ca1\u6709\u554a\uff01\u90a3\u4eba\u5728\u5bfa\u4e2d\u4f24\u4e86\u4e94\u540d\u5f1f\u5b50\uff0c\u5bb9\u5f97\u6211\u4eec\u53d1\u73b0\uff0c\u5374\u5df2\u627e\u4e0d\u5230\u4eba\u5f71\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u6012\u9053\uff1a\u201c\u4f60\u4eec\u600e\u7684\u8fd9\u7b49\u6ca1\u6709\u7528\uff1f\u5802\u5802\u7684\u4e00\u5ea7\u5c11\u6797\u5bfa\uff0c\u7adf\u7136\u8ba9\u4eba\u5bb6\u5982\u5165\u65e0\u4eba\u4e4b\u5883\uff0c\u8bf4\u51fa\u6765\u4e5f\u4e0d\u6015\u8138\u7ea2\uff1f\u771f\u4e0d\u77e5\u9053\u4f60\u4eec\u662f\u600e\u4e48\u6df7\u51fa\u6765\u7684\u540d\u671b\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u8d8a\u8bf4\u8d8a\u6c14\uff0c\u82e5\u975e\u65b9\u96ea\u5b9c\u9002\u65f6\u5e72\u54b3\u4e86\u4e00\u58f0\uff0c\u5e76\u4e14\u4f38\u624b\u6293\u4f4f\u4e86\u8f9b\u67cf\u516c\u7684\u53f3\u624b\uff0c\u90a3\u5165\u5723\u5927\u5e08\u5fc5\u7136\u8981\u6328\u8f9b\u67cf\u516c\u51e0\u4e2a\u8033\u5149\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u4e5f\u663e\u5f97\u6709\u4e9b\u4e0d\u5feb\uff0c\u51b7\u51b7\u5730\u7b11\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5927\u5e08\uff0c\u4f60\u4eec\u6446\u4e0b\u8fd9\u7b49\u9635\u4ed7\u5e72\u5417\uff1f\u4eba\u5bb6\u4f24\u4e86\u4f60\u4eec\u7684\u4eba\uff0c\u6015\u4e0d\u65e9\u5df2\u6e9c\u5f97\u8fdc\u8fdc\uff0c\u8fd8\u4f1a\u56de\u6765\u7b49\u4f60\u4eec\u6349\u62ff\u5417\uff1f\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u957f\u53f9\u4e86\u4e00\u53f9\u58f0\uff0c\u9053\uff1a\u201c\u65bd\u4e3b\uff0c\u8fd9\u4e8b\u8d2b\u50e7\u7b49\u679c\u7136\u5904\u7406\u5f97\u6709\u9519\uff0c\u53ea\u662f\uff0c\u6765\u4eba\u6b66\u529f\u5341\u5206\u9ad8\u660e\uff0c\u8d2b\u50e7\u7b49\u5144\u5f1f\uff0c\u53c8\u6b63\u5728\u85cf\u7ecf\u697c\u6709\u4e8b\uff0c\u53ea\u9760\u4e09\u4ee3\u5f1f\u5b50\u4eec\u51fa\u9762\u4e0e\u6765\u4eba\u5468\u65cb\uff0c\u5bb9\u5f97\u654c\u4eba\u8131\u53bb\uff0c\u5b9e\u4e5f\u96be\u4ee5\u8d23\u602a\u4ed6\u4eec\u4e86\uff01\u201d \u3000\u3000\u8f9b\u6015\u516c\u51b7\u54fc\u9053\uff1a\u201c\u970d\u9e23\u98ce\u548c\u4e10\u5e2e\u7684\u4e24\u4f4d\u957f\u8001\u5462\uff1f\u4ed6\u4eec\u4e5f\u4e0d\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u4ed6\u4eec\u5168\u5728\u5ba2\u5ba4\u4e2d\uff0c\u770b\u671b\u7740\u90d1\u5927\u521a\u65bd\u4e3b\uff0c\u7b49\u5230\u5f97\u8baf\u8d76\u53bb\uff0c\u654c\u4eba\u5df2\u7136\u9038\u53bb\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u51b7\u54fc\u9053\uff1a\u201c\u53ef\u607c\u5f97\u5f88\u2026\u2026\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u90a3\u6765\u72af\u4e4b\u4eba\uff0c\u662f\u4ec0\u4e48\u8def\u6570\uff1f\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u8baa\u8baa\u5730\u5408\u5341\u4e00\u7b11\u9053\uff1a\u201c\u90a3\u51e0\u4eba\u6765\u53bb\u5982\u98ce\uff0c\u7adf\u662f\u4e0d\u66fe\u4eae\u51fa\u8eab\u4efd\u2026\u2026\u201d \u3000\u3000\u8f9b\u67cf\u516c\u95fb\u8a00\uff0c\u53ea\u6c14\u5f97\u8dfa\u811a\u9053\uff1a\u201c\u8113\u5305\uff01\u4f60\u4eec\u8fd9\u6279\u548c\u5c1a\u771f\u662f\u8113\u5305\u5230\u4e86\u5bb6\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5728\u65c1\u7b11\u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5927\u5e08\uff0c\u4ed6\u4eec\u867d\u7136\u4e0d\u77e5\u6765\u4eba\u7684\u8eab\u4efd\uff0c\u81f3\u5c11\u4e5f\u8be5\u770b\u51fa\u4ed6\u4eec\u7684\u8eab\u6750\u9ad8\u4f4e\u5427\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\u4e0d\u8bf4\uff0c\u8d2b\u50e7\u5230\u662f\u5fd8\u8bb0\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u4e0d\u7b49\u4ed6\u63a5\u53e3\uff0c\u7acb\u5373\u559d\u9053\uff1a\u201c\u83ab\u975e\u6709\u4eba\u77a7\u5230\u4e86\u4ed6\u4eec\u7684\u9762\u8c8c\u4e86\uff1f\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u9762\u8c8c\u5012\u4e0d\u66fe\u77a7\u89c1\uff0c\u53ea\u662f\uff0c\u90a3\u4e00\u884c\u4eba\u7684\u8eab\u6750\uff0c\u5012\u662f\u88ab\u51e0\u540d\u5f1f\u5b50\u770b\u51fa\u6765\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u51b7\u54fc\u9053\uff1a\u201c\u5f88\u80fd\u5e72\u554a\uff01\u4e5f\u4e0d\u6015\u4e22\u4eba\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u5408\u5341\u9053\uff1a\u201c\u8001\u83e9\u8428\uff0c\u5728\u4f60\u8001\u9762\u524d\uff0c\u8d2b\u50e7\u7eb5\u662f\u591a\u6328\u51e0\u53e5\u9a82\uff0c\u90a3\u4e5f\u7b97\u4e0d\u5f97\u4ec0\u4e48\uff01\u201d \u3000\u3000\u8bdd\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u636e\u90a3\u76ee\u51fb\u6765\u4eba\u7684\u5f1f\u5b50\u8bf4\uff0c\u90ca\u4e9b\u4eba\u7684\u8eab\u6750\uff0c\u90fd\u5341\u5206\u7ea4\u7ec6\u77ee\u5c0f\uff0c\u770b\u6765\u4f3c\u662f\u4e00\u7fa4\u5973\u65bd\u4e3b\u6a21\u6837\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u8fd9\u4e48\u770b\u6765\uff0c\u90a3\u53c8\u662f\u5929\u9b54\u5973\u7684\u95f9\u4ec0\u4e48\u82b1\u6837\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u597d\u554a\uff01\u53ea\u8981\u5979\u4eec\u80c6\u6562\u518d\u6765\uff0c\u8001\u592b\u8981\u4e0d\u5c06\u5979\u4eec\u4e00\u4e2a\u4e00\u4e2a\u751f\u64d2\u6d3b\u6349\uff0c\u8001\u592b\u5c31\u628a\u8f9b\u5b57\u4ece\u6b64\u5012\u5199\uff01\u201d \u3000\u3000\u53ea\u89c1\u4ed6\u53cc\u76ee\u66b4\u7741\uff0c\u4e00\u8138\u6012\u610f\uff0c\u663e\u7136\u662f\u5341\u5206\u6c14\u6012\uff01 \u3000\u3000\u5b89\u5c0f\u840d\u77a7\u5f97\u5fcd\u4e0d\u4f4f\u6251\u54e7\u4e00\u58f0\uff0c\u7b11\u51fa\u6765\u3002 \u3000\u3000\u8f9b\u67cf\u516c\u4e24\u773c\u4e00\u7ffb\uff0c\u5486\u54ee\u9053\uff1a\u201c\u4f60\u7b11\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u8f9b\u516c\u516c\uff0c\u6211\u53ef\u4e0d\u662f\u7b11\u2026\u2026\u201d \u3000\u3000\u5979\u5634\u4e2d\u867d\u8bf4\u7740\u4e0d\u7b11\uff0c\u4f46\u5374\u53c8\u5fcd\u4e0d\u4f4f\u7b11\u5f2f\u4e86\u8170\uff01 \u3000\u3000\u8f9b\u67cf\u516c\u607c\u5f97\u8fde\u8fde\u987f\u8db3\u9053\uff1a\u201c\u4f60\u8fd8\u6562\u7b11\uff1f\u4e0d\u6015\u8f9b\u516c\u516c\u626d\u65ad\u4f60\u7684\u80f3\u818a\u2026\u2026\u201d\u8bf4\u7740\uff0c\u4fbf\u4f38\u624b\u53bb\u6293\u5b89\u5c0f\u840d\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u4f55\u7b49\u5201\u86ee\uff0c\u4e00\u542c\u8f9b\u67cf\u516c\u8981\u626d\u65ad\u5979\u80f3\u818a\uff0c\u5a07\u8eaf\u4e00\u62e7\uff0c\u65e9\u5df2\u5954\u8fdb\u4e86\u5c11\u6797\u5bfa\u5185\uff01 \u3000\u3000\u8f9b\u67cf\u516c\u76b1\u4e86\u76b1\u7709\u5934\uff0c\u56de\u5934\u5411\u5165\u5723\u5927\u5e08\u559d\u9053\uff1a\u201c\u548c\u5c1a\uff0c\u8fd8\u4e0d\u5feb\u53eb\u4f60\u7684\u8fd9\u4e9b\u8113\u5305\u5b50\u5f1f\u4eec\u53bb\u591a\u5ff5\u51e0\u904d\u4f5b\uff1f\u5b88\u5728\u8fd9\u513f\uff0c\u5c0f\u5fc3\u62db\u4e86\u5c71\u98ce\uff0c\u53d7\u4e86\u51c9\u554a\u2026\u2026\u201d\u8bdd\u97f3\u4e00\u843d\uff0c\u4e3e\u6b65\u4fbf\u8de8\u5165\u5bfa\u5185\u3002 \u3000\u3000\u5b8b\u5b5a\u8fd9\u65f6\u624d\u5fae\u5fae\u4e00\u7b11\uff0c\u5411\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u5927\u5e08\uff0c\u8f9b\u8001\u8bf4\u5f97\u4e0d\u9519\uff0c\u4ed6\u4eec\u4e0d\u7528\u5728\u8fd9\u513f\u4e25\u9635\u4ee5\u5f85\u4e86\uff0c\u7eb5\u7136\u90a3\u5929\u9b54\u5973\u7684\u624b\u4e0b\u518d\u6765\uff0c\u6709\u4e86\u65b9\u8001\u5f1f\u548c\u8f9b\u8001\u5728\u6b64\uff0c\u4e5f\u4e0d\u4f1a\u518d\u5bb9\u5979\u4eec\u653e\u8086\u7684\u4e86\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u5408\u5341\u9053\uff1a\u201c\u8001\u8872\u9075\u547d\u2026\u2026\u201d \u3000\u3000\u5f53\u4e0b\u4ee4\u90a3\u6279\u5217\u9635\u4ee5\u5f85\u7684\u50e7\u4fa3\uff0c\u5168\u90e8\u64a4\u5165\u5bfa\u5185\uff0c\u53ea\u7559\u4e0b\u503c\u66f4\u4e4b\u4eba\uff0c\u5c0f\u5fc3\u5de1\u67e5\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u8fdb\u5f97\u5bfa\u6765\uff0c\u6025\u6b65\u62a2\u5230\u5ba2\u5802\uff0c\u4e3e\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u90d1\u5927\u521a\u4ecd\u7136\u6253\u5750\u672a\u9192\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u867d\u751a\u7740\u6025\uff0c\u4f46\u5374\u53c8\u4e0d\u80fd\u5c06\u4ed6\u5f04\u9192\uff0c\u53ea\u80fd\u8d1f\u8d77\u53cc\u624b\uff0c\u671b\u7740\u90d1\u5927\u521a\u53d1\u5446\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u8f9b\u67cf\u516c\u6b63\u81ea\u5927\u6b65\u7531\u5916\u800c\u6765\u3002 \u3000\u3000\u4ed6\u8fdb\u5f97\u8fd9\u95f4\u5ba2\u623f\uff0c\u76ee\u5149\u5728\u90d1\u5927\u521a\u8eab\u4e0a\u4e00\u8f6c\uff0c\u626c\u7709\u4e00\u7b11\uff0c\u5411\u65b9\u96ea\u5b9c\u9053\uff1b\u201c\u8001\u5f1f\uff0c\u4f60\u53ef\u662f\u6025\u4e8e\u540c\u4ed6\u8bb2\u8bdd\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u82e6\u7b11\u9053\uff1a\u201c\u665a\u8f88\u867d\u6709\u6b64\u5fc3\uff0c\u4f46\u90d1\u5927\u4f2f\u8c03\u606f\u4f24\u52bf\u672a\u9192\uff0c\u7740\u6025\u4e5f\u662f\u6ca1\u6709\u7528\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u7b11\u4e86\u4e00\u7b11\u9053\uff1a\u201c\u8c01\u8bf4\u7740\u6025\u6ca1\u6709\u7528\uff1f\u8001\u592b\u5012\u6709\u529e\u6cd5\uff0c\u5e2e\u4ed6\u65e9\u4e9b\u9192\u8f6c\u2026\u2026\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u53ea\u89c1\u4ed6\u8d70\u5230\u90d1\u5927\u521a\u8eab\u524d\uff0c\u7a0d\u7a0d\u6253\u91cf\u4e86\u90d1\u5927\u521a\u4e00\u773c\uff0c\u7b11\u9053\uff1a\u201c\u5f85\u8001\u592b\u8bd5\u8bd5\uff01\u201d \u3000\u3000\u5ffd\u7136\u5750\u5728\u4e91\u5e8a\u4e4b\u4e0a\uff0c\u4e3e\u624b\u6309\u5411\u90d1\u5927\u521a\u540e\u5fc3\uff0c\u95ed\u4e0a\u53cc\u76ee\uff0c\u8fd0\u8d77\u5185\u529b\uff0c\u4f20\u5165\u90d1\u5927\u521a\u4f53\u5185\u3002 \u3000\u3000\u6562\u60c5\uff0c\u4ed6\u4e43\u662f\u4ee5\u81ea\u5df1\u65e0\u6bd4\u7684\u529f\u529b\uff0c\u52a9\u90a3\u90d1\u5927\u521a\u7597\u4f24\uff01 \u3000\u3000\u90d1\u5927\u521a\u5185\u4f24\u867d\u91cd\uff0c\u4f46\u56e0\u5c11\u6797\u7075\u4e39\u7684\u836f\u6027\uff0c\u6b63\u662f\u4e3e\u4e16\u96be\u6c42\u7684\u7597\u4f24\u5723\u54c1\uff0c\u4e00\u7c92\u5165\u5589\uff0c\u5df2\u5c06\u4f24\u52bf\u904f\u963b\uff0c\u7ecf\u8fc7\u534a\u5929\u7684\u8c03\u606f\uff0c\u672c\u5df2\u597d\u4e86\u5341\u4e4b\u4e03\u516b\uff0c\u6b64\u523b\u5ffd\u7136\u6709\u7740\u4e00\u80a1\u6781\u4e3a\u5f3a\u5927\u7684\u529f\u529b\uff0c\u8fdb\u5165\u4f53\u5185\uff0c\u987a\u7740\u5168\u8eab\u7ecf\u8109\uff0c\u50ac\u52a8\u836f\u529b\uff0c\u53ea\u89c9\u6d51\u8eab\u987f\u65f6\u547d\u7545\uff0c\u79ef\u538b\u5728\u80f8\u524d\u7684\u95f7\u55dd\u4e4b\u611f\uff0c\u5239\u90a3\u95f4\u4e00\u626b\u800c\u5149\u3002 \u3000\u3000\u4e5f\u4e0d\u8fc7\u76cf\u8336\u65f6\u5149\uff0c\u90d1\u5927\u521a\u5ffd\u7136\u957f\u957f\u7684\u5410\u4e86\u4e00\u53e3\u5927\u6c14\uff0c\u970d\u7136\u7741\u5f00\u4e86\u53cc\u76ee\u3002 \u3000\u3000\u8f9b\u67cf\u516c\u4e00\u6536\u624b\uff0c\u7b11\u9053\uff1a\u201c\u65b9\u8001\u5f1f\uff0c\u5982\u4f55\uff1f\u8001\u592b\u6ca1\u6709\u5439\u725b\u54df\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u5927\u4e3a\u611f\u6fc0\uff0c\u8fde\u5fd9\u62b1\u62f3\u9053\uff1a\u201c\u591a\u8c22\u8001\u524d\u8f88\u60f3\u52a9\u4e4b\u5fb7\u2026\u2026\u201d \u3000\u3000\u8f9b\u67cf\u516c\u4e00\u8dc3\u4e0b\u4e86\u4e91\u5e8a\uff0c\u7b11\u9053\uff1a\u201c\u4e3e\u624b\u4e4b\u52b3\uff0c\u4f55\u8db3\u9053\u54c9\u2026\u2026\u201d \u3000\u3000\u8bdd\u97f3\u4e00\u987f\uff0c\u5374\u5728\u4e00\u65c1\u7684\u4e00\u53ea\u84b2\u56e2\u4e4b\u4e0a\u5750\u4e0b\uff0c\u63a5\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u6709\u4ec0\u4e48\u4e8b\uff0c\u4f60\u53ef\u4ee5\u8ddf\u4ed6\u4ea4\u8c08\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u611f\u6fc0\u5730\u7b11\u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u665a\u8f88\u9075\u547d\uff01\u201d \u3000\u3000\u8d8b\u524d\u4e24\u6b65\uff0c\u5c48\u819d\u4fbf\u5411\u90d1\u5927\u521a\u62dc\u5012\uff0c\u53e3\u4e2d\u63a5\u9053\uff1a\u201c\u90d1\u5927\u4f2f\uff0c\u8fd8\u8bb0\u5f97\u5c0f\u4f84\u5417\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u76ee\u5149\u5728\u65b9\u96ea\u5b9c\u8eab\u4e0a\u8f6c\u52a8\uff0c\u5ffd\u7136\u53cc\u76ee\u4e4b\u4e2d\uff0c\u9690\u9690\u73b0\u51fa\u4e86\u6cea\u5149\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u745c\u513f\uff0c\u9002\u624d\u4f60\u5df2\u89c1\u8fc7\u793c\u4e86\uff01\u5feb\u5feb\u8d77\u6765\u8bf4\u8bdd\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4f9d\u7136\u62dc\u4e86\u4e00\u62dc\uff0c\u8fd9\u624d\u8d77\u8eab\uff0c\u5782\u624b\u8083\u7acb\u4e00\u65c1\u3002 \u3000\u3000\u90d1\u5927\u521a\u4ece\u5934\u5230\u811a\uff0c\u628a\u4ed6\u770b\u4e86\u4e00\u9635\uff0c\u6cea\u5149\u95ea\u95ea\uff0c\u7adf\u662f\u4ef0\u5929\u5927\u7b11\u9053\uff1a\u201c\u8d24\u4f84\uff0c\u606d\u559c\u4f60\u4e86\uff01\u51e0\u5e74\u4e0d\u89c1\u4e86\uff0c\u4e0d\u4f46\u4f60\u957f\u5f97\u8fd9\u822c\u7ed3\u5b9e\uff0c\u800c\u4e14\uff0c\u53c8\u5f97\u660e\u5e08\u6536\u5f52\u95e8\u4e0b\u7ec3\u5c31\u4e86\u4e00\u8eab\u7edd\u9876\u6b66\u529f\uff0c\u771f\u662f\u4e0d\u5728\u611a\u4f2f\u5728\u90a3\u6797\u4e2d\u6551\u4f60\u4e00\u573a\u4e86\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u53ea\u89c9\u9f3b\u5934\u4e00\u9178\uff0c\u60b2\u4ece\u4e2d\u6765\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5927\u4f2f\u6551\u547d\u4e4b\u6069\uff0c\u4f84\u513f\u4e00\u65e5\u4e0d\u6562\u6216\u5fd8\u2026\u2026\u201d \u3000\u3000\u90d1\u5927\u521a\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u745c\u513f\uff0c\u8fd9\u6551\u547d\u4e4b\u6069\u56db\u5b57\uff0c\u4ee5\u540e\u53ef\u4e0d\u8bb8\u518d\u63d0\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5927\u4f2f\u2026\u2026\u4f60\u2026\u2026\u201d \u3000\u3000\u90d1\u5927\u521a\u6325\u4e86\u6325\u624b\uff0c\u63a5\u9053\uff1a\u201c\u4e3a\u4f2f\u7684\u4e00\u6761\u547d\uff0c\u773c\u4e0b\u4e0d\u4e5f\u662f\u56e0\u4e3a\u4f60\uff0c\u624d\u6361\u56de\u6765\u7684\u5417\uff1f\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u95ea\u8eab\u4e0b\u4e86\u4e91\u5e8a\uff0c\u8fce\u5411\u8f9b\u4f2f\u516c\u957f\u957f\u4e00\u62dc\uff0c\u9053\uff1a\u201c\u591a\u8c22\u8001\u524d\u8f88\u76f8\u6551\u5927\u5fb7\uff0c\u90d1\u5927\u521a\u6709\u751f\u4e4b\u5e74\uff0c\u4e0d\u6562\u7a0d\u5fd8\u3002\u201d \u3000\u3000\u8f9b\u67cf\u516c\u51b7\u51b7\u54fc\u4e00\u58f0\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u5fc5\uff01\u8001\u592b\u662f\u4e3a\u4e86\u65b9\u8001\u5f1f\u624d\u51fa\u624b\u52a9\u4f60\uff0c\u4f60\u5982\u8981\u8c22\uff0c\u5c3d\u7ba1\u8c22\u4f60\u8fd9\u4f4d\u4e16\u4f84\u5427\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u88ab\u4ed6\u56de\u649e\u5f97\u6123\u4e86\u4e00\u6123\uff0c\u4f46\u4ed6\u660e\u767d\uff0c\u773c\u524d\u8fd9\u4f4d\u8001\u4eba\uff0c\u81ea\u5df1\u867d\u7136\u4e0d\u8bc6\uff0c\u4e0d\u77e5\u4ed6\u7684\u540d\uff0c\u800c\u4e3e\u624b\u4e4b\u95f4\uff0c\u4fbf\u4f7f\u81ea\u5df1\u970d\u7136\u800c\u6108\uff0c\u662f\u4f4d\u6b66\u6797\u5947\u5ba2\uff0c\u90a3\u662f\u6ca1\u6709\u7591\u95ee\u7684\u4e86\uff01\u662f\u4ee5\u4ed6\u5e76\u4e0d\u89c9\u5f97\u96be\u8fc7\uff0c\u53cd\u5012\u7b11\u9053\uff1a\u201c\u8001\u524d\u8f88\u865a\u6000\u82e5\u8c37\uff0c\u66f4\u4f7f\u90d1\u5927\u521a\u5fc3\u4e2d\u656c\u4f69\u4e07\u5206\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u65f6\u7b11\u9053\uff1a\u201c\u5927\u4f2f\uff0c\u8fd9\u4f4d\u8001\u524d\u8f88\uff0c\u4e43\u662f\u5f53\u4ee3\u5947\u4eba\uff0c\u8f9b\u67cf\u516c\u8f9b\u8001\u524d\u8f88\uff0c\u5927\u4f2f\u60f3\u5fc5\u542c\u8bf4\u8fc7\u7684\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u95fb\u8a00\uff0c\u4e0d\u7981\u5446\u4e86\u4e00\u5446\uff01 \u3000\u3000\u663e\u7136\uff0c\u8fd9\u8001\u4eba\u5c45\u7136\u4f1a\u662f\u201c\u6b66\u6797\u4e00\u602a\u201d\u8f9b\u67cf\u516c\uff0c\u5927\u5927\u5730\u51fa\u4e4e\u4ed6\u610f\u6599\u4e4b\u5916\u4e86\uff01 \u3000\u3000\u90d1\u5927\u521a\u4f9d\u7a00\u8bb0\u5f97\uff0c\u81ea\u5df1\u5c1a\u5728\u7ae5\u7a1a\u4e4b\u5e74\uff0c\u5c31\u542c\u8bf4\u8fc7\u8fd9\u4f4d\u8001\u4eba\u7684\u8bf8\u591a\u4fa0\u8e2a\u602a\u4e8b\uff0c\u60f3\u4e0d\u5230\u6570\u5341\u5e74\u540e\uff0c\u4ed6\u4ecd\u7136\u8fd8\u5728\u4eba\u4e16\uff0c\u4e0d\u4f46\u5982\u6b64\uff0c\u4ed6\u7adf\u7136\u4e0e\u4f20\u95fb\u4e2d\u7684\u602a\u7656\u5927\u4e0d\u76f8\u7b26\uff0c\u800c\u51fa\u624b\u76f8\u52a9\u81ea\u5df1\u63d0\u65e9\u6cbb\u6108\u4e86\u4f24\u52bf\uff0c\u5c82\u4e0d\u4ee4\u4ed6\u611f\u5230\u65e0\u6bd4\u7684\u60ca\u6115\uff1f \u3000\u3000\u90d1\u5927\u521a\u6014\u4e86\u534a\u664c\uff0c\u65b9\u59cb\u53c8\u662f\u4e00\u63d6\uff0c\u606d\u58f0\u9053\uff1a\u201c\u539f\u6765\u662f\u8f9b\u8001\u524d\u8f88\uff0c\u665a\u8f88\u771f\u662f\u6709\u773c\u4e0d\u8bc6\u6cf0\u5c71\uff0c\u7f6a\u8fc7\u5f97\u5f88\u2026\u2026\u201d \u3000\u3000\u8f9b\u67cf\u516c\u76b1\u4e86\u76b1\u7709\uff0c\u9053\uff1a\u201c\u7b97\u5566\uff01\u8001\u592b\u4e0d\u559c\u6b22\u5ba2\u5957\uff0c\u4f60\u522b\u518d\u7f57\u55e6\u4e86\uff01\u65b9\u8001\u5f1f\u8fd8\u7b49\u7740\u5411\u4f60\u8bf7\u6559\u5462\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u65e2\u7136\u77e5\u9053\u4e86\u8fd9\u4f4d\u8001\u4eba\u5c31\u662f\u6b66\u6797\u4e00\u602a\uff0c\u5bf9\u4e8e\u4ed6\u8fd9\u7b49\u51b7\u8a00\u76f8\u5f85\u81ea\u5df1\uff0c\u4fbf\u662f\u4e0d\u89c9\u5f97\u96be\u8fc7\u4e86\uff01 \u3000\u3000\u4ed6\u5fae\u5fae\u4e00\u7b11\uff0c\u5341\u5206\u793c\u8c8c\u7684\u62b1\u62f3\u9053\uff1a\u201c\u665a\u8f88\u4e0d\u6562\u6253\u6270\u4f60\u8001\u4e86\uff01\u201d \u3000\u3000\u6df1\u6df1\u4e00\u793c\u3002\u8fd9\u624d\u9000\u5230\u4e91\u5e8a\u4e4b\u524d\uff0c\u5411\u65b9\u96ea\u5b9c\u4e86\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u745c\u513f\uff0c\u611a\u4f2f\u5728\u90a3\u767b\u5c01\u57ce\u5916\u88ab\u56da\uff0c\u4e0d\u77e5\u662f\u54ea\u4f4d\u670b\u53cb\u5c06\u6211\u6551\u6765\u6b64\u95f4\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u90a3\u662f\u5c0f\u4f84\u7684\u4e00\u4f4d\u62dc\u5f1f\u2026\u2026\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4ed6\u73b0\u5728\u4f55\u5904\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u5b89\u8d24\u5f1f\u73b0\u5728\u5c11\u6797\uff0c\u5927\u4f2f\u7236\u53ef\u662f\u8981\u627e\u4ed6\u524d\u6765\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u6551\u547d\u4e4b\u6069\uff0c\u81ea\u5f53\u62dc\u8c22\uff0c\u745c\u513f\u4f60\u5feb\u53bb\u5c06\u4ed6\u8bf7\u6765\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5c0f\u4f84\u9075\u547d\uff01\u201d\u8f6c\u8eab\u4fbf\u5f85\u53bb\u627e\u90a3\u5b89\u5c0f\u840d\u6765\u6b64\u3002 \u3000\u3000\u5b8b\u5b5a\u5ffd\u7136\u63d2\u53e3\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u4e0d\u7528\u53bb\u4e86\uff0c\u8001\u673d\u4ee3\u4f60\u8d70\u4e00\u8d9f\u5427\uff01\u201d \u3000\u3000\u8bdd\u97f3\u4e00\u987f\uff0c\u4fbf\u8f6c\u8eab\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u6709\u52b3\u5b8b\u8001\u4e86\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u5374\u76b1\u4e86\u76b1\u7709\uff0c\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u6562\u5f53\uff1f\u2026\u2026\u201d \u3000\u3000\u90a3\u5750\u5728\u4e00\u65c1\u7684\u8f9b\u67cf\u516c\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u90d1\u5927\u521a\uff0c\u6709\u8bb8\u591a\u4e8b\u4f60\u4e0d\u7528\u653e\u5728\u5fc3\u4e0a\uff0c\u987b\u77e5\u4f60\u8fd9\u4f4d\u4e16\u4f84\uff0c\u773c\u4e0b\u5728\u6b66\u6797\u4e4b\u4e2d\uff0c\u4e43\u662f\u4e3e\u8db3\u8f7b\u91cd\u4e4b\u4eba\uff0c\u5c31\u8fde\u8001\u592b\u5728\u5185\uff0c\u4e5f\u5df2\u51b3\u5fc3\u542c\u547d\u4e8e\u4ed6\uff0c\u5982\u662f\u6709\u4eba\u8981\u56e0\u4ed6\u4e4b\u6545\uff0c\u4ee3\u4f60\u90d1\u5927\u521a\u505a\u4e86\u4ec0\u4e48\uff1f\u90a3\u4f60\u4e5f\u4e0d\u7528\u611f\u8c22\u4ed6\u4eec\u7684\u4e86\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u665a\u8f88\u8bb0\u4e0b\u4e86\uff01\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u745c\u513f\uff0c\u90a3\u4f4d\u4e1c\u8033\u8001\u4eba\uff0c\u53ef\u5c31\u662f\u5251\u795e\u9648\u5927\u4fa0\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e1c\u8033\u8001\u4eba\u6b63\u662f\u5251\u795e\u2026\u2026\u201d \u3000\u3000\u90d1\u5927\u521a\u957f\u957f\u5730\u53f9\u606f\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u611a\u4f2f\u7b49\u7adf\u7136\u6709\u773c\u4e0d\u8bc6\u9ad8\u4eba\uff0c\u8981\u90a3\u5251\u795e\u9648\u5927\u4fa0\u6210\u4e86\u4e2d\u5dde\u4e09\u4fa0\u7684\u9a7e\u8f66\u6253\u6742\u4e4b\u4eba\uff0c\u53eb\u4eba\u77e5\u9053\uff0c\u5c82\u4e0d\u662f\u7b11\u8bdd\u6211\u4eec\u5931\u793c\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u5927\u4f2f\uff0c\u5148\u5e08\u5012\u4e0d\u66fe\u89c1\u602a\uff0c\u90a3\u662f\u56e0\u4e3a\u9648\u5927\u4fa0\u4e43\u662f\u7edd\u4ee3\u9ad8\u4eba\u4e4b\u6545\u2026\u2026\u201d \u3000\u3000\u90d1\u5927\u521a\u5ffd\u7136\u6014\u4e86\u4e00\u6014\uff0c\u63a5\u9053\uff1a\u201c\u8d24\u4f84\uff0c\u4f60\u521a\u624d\u53e3\u79f0\u5148\u5e08\u4e24\u5b57\uff0c\u83ab\u975e\u6c5f\u6e56\u4f20\u8a00\uff0c\u5251\u795e\u9648\u5927\u4fa0\u771f\u7684\u5df2\u7136\u4ed9\u53bb\u4e86\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5927\u4f2f\u8bf4\u5f97\u4e0d\u9519\uff0c\u5e08\u7236\u5df2\u7ecf\u5728\u909b\u5d03\u5c71\u4e2d\u5750\u5316\u4e86\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u5931\u58f0\u9053\uff1a\u201c\u6b66\u6797\u4e71\u8c61\u5df2\u70bd\uff0c\u9648\u5927\u4fa0\u4ed9\u53bb\uff0c\u771f\u6b63\u53eb\u4eba\u96be\u8fc7\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u751a\u662f\u60b2\u621a\u5730\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5927\u4f2f\u8bf4\u5f97\u4e0d\u9519\uff0c\u53ea\u662f\uff0c\u5148\u5e08\u4f24\u52bf\uff0c\u65e9\u5df2\u65e0\u6551\uff0c\u4ed6\u8001\u4eba\u5bb6\u82e5\u4e0d\u662f\u4e3a\u4e86\u5c0f\u4f84\uff0c\u53ea\u6015\u65e9\u5728\u4e24\u5e74\u4e4b\u524d\uff0c\u5c31\u5df2\u8c22\u4e16\u4e86\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u542c\u5f97\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u600e\u4e48\u56de\u4e8b\uff1f\u542c\u4f60\u4e4b\u8a00\uff0c\u9648\u5927\u4fa0\u83ab\u975e\u662f\u53d7\u4e86\u4f24\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5148\u5e08\u4e43\u662f\u53d7\u4e86\u4ed6\u4eba\u7684\u6697\u7b97\u3002\u201d \u3000\u3000\u5173\u961f\u521a\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u53ef\u80fd\uff1f\u4ee5\u5251\u795e\u9648\u5927\u4fa0\u4e4b\u80fd\uff0c\u600e\u7684\u4f1a\u53d7\u4e86\u522b\u4eba\u7684\u6697\u7b97\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5927\u4f2f\uff0c\u8fd9\u4e8b\u4e5f\u6b63\u662f\u5c0f\u4f84\u6025\u5f85\u67e5\u660e\u7684\u554a\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u987f\u8db3\u9053\uff1a\u201c\u5e08\u7236\u4e4b\u4ec7\uff0c\u4e0d\u5171\u6234\u5927\uff0c\u8fd9\u4e8b\u745c\u513f\u4f60\u5f53\u7136\u8981\u67e5\u95ee\u660e\u767d\uff0c\u4ee3\u5251\u795e\u9648\u5927\u4fa0\u590d\u4ec7\u624d\u662f\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5c0f\u4f84\u77e5\u9053\u2026\u2026\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5b8b\u5b5a\u548c\u5b89\u5c0f\u840d\u6253\u5916\u9762\u5feb\u6b65\u800c\u6765\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u8fd8\u6ca1\u6709\u8de8\u5165\u5ba4\u5185\uff0c\u4fbf\u81ea\u683c\u683c\u4e00\u7b11\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u627e\u6211\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u90d1\u5927\u4f2f\u8981\u5411\u4f60\u5f53\u9762\u9053\u8c22\u6551\u547d\u4e4b\u6069\u554a\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u95fb\u8a00\uff0c\u6014\u4e86\u4e00\u6014\u9053\uff1a\u201c\u90d1\u5927\u4f2f\u9762\u8c22\u6211\uff1f\u90a3\u600e\u4e48\u6562\u5f53\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u8fd9\u65f6\u5df2\u5927\u6b65\u4e0a\u524d\uff0c\u5411\u5b89\u5c0f\u840d\u6025\u6025\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u90d1\u5927\u521a\u4e0d\u5e78\u843d\u5165\u4e94\u9b54\u4e4b\u624b\uff0c\u82e5\u975e\u5c11\u4fa0\u76f8\u6551\uff0c\u6b64\u523b\u65e9\u5df2\u846c\u8eab\u6ce5\u571f\u4e4b\u4e2d\uff0c\u518d\u9020\u4e4b\u6069\uff0c\u81ea\u5f53\u7ec8\u8eab\u4e0d\u6562\u7a0d\u5fd8\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u53ef\u662f\u8fde\u5fd9\u95ea\u8eab\u900a\u8ba9\uff0c\u7b11\u9053\uff1a\u201c\u90d1\u5927\u4f2f\uff0c\u4f11\u8bf4\u65b9\u5144\u4e0e\u5c0f\u4f84\u6709\u8fc7\u7ed3\u4e49\u4e4b\u60c5\uff0c\u5c31\u7b97\u4e0d\u66fe\u76f8\u8bc6\u4e4b\u4eba\uff0c\u5c0f\u4f84\u9047\u5230\u8fd9\u7b49\u4e8b\uff0c\u4e5f\u4e0d\u4f1a\u8896\u624b\u4e0d\u7ba1\u554a\uff01\u90d1\u5927\u4f2f\u5982\u662f\u8fd9\u4e48\u4e00\u518d\u8a00\u8c22\uff0c\u90a3\u5c31\u662f\u89c1\u5916\u5c0f\u4f84\u4e86\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u88ab\u4ed6\u8bf4\u5f97\u4e00\u6014\u3002 \u3000\u3000\u4f46\u4ed6\u4e43\u662f\u6781\u4e3a\u8c6a\u723d\u4e4b\u4eba\uff0c\u6123\u4e86\u4e00\u6123\u4e4b\u540e\uff0c\u7acb\u5373\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5927\u6069\u4e0d\u6562\u8a00\u62a5\uff0c\u65e5\u540e\u5b89\u516c\u5b50\u5982\u6709\u7528\u5f97\u7740\u90d1\u67d0\u4e4b\u5904\uff0c\u90d1\u67d0\u5b9a\u5f53\u613f\u6548\u72ac\u9a6c\u4e4b\u52b3\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u5927\u4f2f\u8a00\u91cd\u4e86\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u53c8\u5411\u5b89\u5c0f\u840d\u65bd\u4e86\u4e00\u793c\uff0c\u7f13\u7f13\u9000\u5230\u4e91\u5e8a\u7684\u524d\u9762\uff0c\u63a5\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u4f60\u8bf7\u5750\u554a\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u90d1\u8001\u5f1f\uff0c\u4f60\u91cd\u4f24\u521d\u6108\uff0c\u5148\u5750\u7740\u6b47\u6b47\u5427\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u65f6\u4e5f\u62db\u547c\u51e0\u4eba\u5750\u5b9a\u3002 \u3000\u3000\u970d\u9e23\u98ce\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u90d1\u8001\u5f1f\uff0c\u6709\u4e00\u4ef6\u4e8b\uff0c\u4e0d\u77e5\u8001\u5f1f\u77e5\u9053\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u5e2e\u4e3b\u8bf7\u8bb2\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u90a3\u5929\u9b54\u5973\u53ef\u5728\u767b\u5c01\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u5929\u9b54\u5973\u6765\u8fc7\u767b\u5c01\uff0c\u4f46\u524d\u51e0\u5929\u5c31\u5df2\u79bb\u53bb\uff0c\u4f55\u65f6\u4f1a\u6765\uff0c\u665a\u751f\u5374\u4e0d\u77e5\u9053\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u6076\u5b54\u660e\u5462\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u665a\u751f\u4e0d\u66fe\u89c1\u5230\u4ed6\u5728\u767b\u5c01\u51fa\u73b0\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u8ba1\u901a\u53ea\u6015\u6682\u65f6\u4e0d\u6562\u516c\u7136\u73b0\u8eab\u7684\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u795d\u5bb6\u6709\u4eba\u5728\u5d69\u5c71\u9690\u5c45\uff0c\u4ed6\u5f53\u7136\u662f\u4e0d\u6562\u516c\u7136\u9732\u9762\u7684\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u7b11\u9053\uff1a\u201c\u6076\u5b54\u660e\u8be1\u8ba1\u591a\u7aef\uff0c\u54b1\u4eec\u8fd8\u662f\u9632\u7740\u4ed6\u624d\u597d\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u5ffd\u5730\u53cc\u76ee\u66b4\u7741\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u970d\u9e23\u98ce\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u88ab\u4ed6\u53eb\u5f97\u4e00\u9707\uff0c\u5fd9\u9053\uff1a\u201c\u5927\u53d4\u6709\u4f55\u6307\u793a\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4f60\u8eab\u4e3a\u4e00\u5e2e\u4e4b\u4e3b\uff0c\u600e\u7684\u5374\u662f\u8fd9\u7b49\u80c6\u5c0f\u6015\u4e8b\u5462\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u7b11\u9053\uff1a\u201c\u5927\u53d4\uff0c\u6211\u53ef\u4e0d\u662f\u6015\u4e8b\u554a\uff01\u53ea\u56e0\u8ba1\u901a\u7684\u4e3a\u4eba\u592a\u574f\uff0c\u54b1\u4eec\u660e\u5904\u4e0d\u6015\u4ed6\uff0c\u6697\u5730\u91cc\u5374\u662f\u6709\u4e9b\u60f9\u4ed6\u4e0d\u8d77\u554a\uff01\u201d \u3000\u3000\u8bdd\u97f3\u4e00\u843d\uff0c\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u4e0d\u662f\u6709\u4e8b\u8981\u627e\u90d1\u5927\u4fa0\u5427\u5417\uff1f\u600e\u7684\u89c1\u4e86\u9762\uff0c\u53c8\u4ec0\u4e48\u90fd\u4e0d\u8bb2\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u662f\u554a\uff0c\u665a\u8f88\u679c\u771f\u6709\u5f88\u591a\u8bdd\u8981\u5411\u90d1\u5927\u6015\u8bf7\u6559\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u63a5\u9053\uff1a\u201c\u745c\u513f\uff0c\u4f60\u6709\u4ec0\u4e48\u8bdd\u8981\u95ee\u6211\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5927\u4f2f\uff0c\u5c0f\u4f84\u60f3\u77e5\u9053\u73de\u5144\u5f1f\u548c\u5a76\u6bcd\u7684\u4e0b\u843d\u4f55\u5728\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u95fb\u8a00\uff0c\u7adf\u7136\u957f\u957f\u4e00\u53f9\u9053\uff1a\u201c\u745c\u513f\uff0c\u4f60\u90a3\u5a76\u6bcd\u548c\u73de\u5144\u5f1f\uff0c\u4e43\u662f\u548c\u77f3\u4e09\u5f1f\u4e00\u9053\u800c\u53bb\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5927\u4f2f\uff0c\u4f60\u6ca1\u6709\u8ddf\u4ed6\u4ed6\u4e00\u9053\u5417\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u6211\u8e09\u4ed6\u4eec\u4e00\u9053\u800c\u884c\uff0c\u4f46\u5728\u8fd4\u62b5\u4f60\u5bb6\u6545\u5c45\u4e4b\u524d\uff0c\u56e0\u4e3a\u51fa\u4e00\u70b9\u5c94\u4e8b\uff0c\u4fbf\u5148\u884c\u79bb\u5f00\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5927\u4f2f\u4ee5\u540e\u6ca1\u6709\u89c1\u8fc7\u5a76\u6bcd\u548c\u73de\u5144\u5f1f\u4e86\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d\u4ed6\u5ffd\u7136\u82e6\u7b11\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u611a\u4f2f\u81ea\u79bb\u5f00\u4ed6\u4eec\u4ee5\u540e\uff0c\u4e5f\u66fe\u53bb\u4e86\u4f60\u5bb6\u6545\u5c45\u6570\u6b21\uff0c\u4f46\u5374\u672a\u89c1\u5230\u4f60\u5a76\u6bcd\u548c\u77f3\u4e09\u5f1f\uff0c\u611a\u4f2f\u867d\u7136\u6613\u5bb9\u6539\u626e\uff0c\u5728\u6c5f\u6e56\u4e0a\u67e5\u8bbf\u4e86\u5f88\u4e45\uff0c\u4f46\u7ed3\u679c\u4f9d\u7136\u6ca1\u6709\u5934\u7eea\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u76b1\u7709\u9053\uff1a\u201c\u8fd9\u5c31\u5947\u4e86\u3002\u201d\u4ed6\u6c89\u541f\u4e00\u9635\uff0c\u63a5\u9053\uff1a\u201c\u5927\u4f2f\uff0c\u77f3\u4e09\u53d4\u548c\u65b9\u5929\u6210\u6709\u4eba\u5728\u6d1b\u9633\u9644\u8fd1\u89c1\u5230\u4ed6\u4eec\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u771f\u7684\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u8fd9\u65f6\u7a81\u7136\u7b11\u9053\uff1a\u201c\u672c\u5e2e\u6d1b\u9633\u5206\u5802\u66fe\u7ecf\u53d1\u73b0\u4ed6\u4eec\u7684\u5c38\u4f53\u3002\u201d\u90d1\u5927\u521a\u5446\u4e86\u4e00\u5446\u9053\uff1a \u3000\u3000\u201c\u77f3\u4e09\u5f1f\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u53ea\u89c1\u4ed6\u8138\u8272\u4e00\u53d8\uff0c\u4e24\u9897\u70ed\u6cea\uff0c\u7acb\u523b\u987a\u816e\u6d41\u51fa\u3002 \u3000\u3000\u970d\u9e23\u98ce\u610f\u5916\u5730\u6014\u4e86\u4e00\u6014\u3002\u6697\u9053\uff1a\u201c\u8fd9\u90d1\u5927\u521a\u679c\u7136\u662f\u4e2a\u8840\u6027\u6c49\u5b50\uff0c\u4e00\u542c\u77f3\u4e09\u5f1f\u7684\u6b7b\u8baf\uff0c\u7acb\u5373\u8fd9\u7b49\u4f24\u5fc3\u4e86\u2026\u2026\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u867d\u7136\u5728\u611f\u53f9\uff0c\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u90d1\u5144\uff0c\u4f46\u636e\u8001\u592b\u6240\u77e5\uff0c\u90a3\u77f3\u4fca\u5e76\u672a\u6b7b\u53bb\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u95fb\u8a00\uff0c\u53c8\u662f\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5e2e\u4e3b\u8fd9\u662f\u4f55\u610f\uff1f\u65e2\u7136\u8d35\u5e2e\u6d1b\u9633\u5206\u5802\u6709\u4eba\u53d1\u73b0\u4ed6\u4eec\u7684\u5c38\u4f53\uff0c\u53c8\u600e\u7684\u8bf4\u90a3\u77f3\u4e09\u5f1f\u5e76\u672a\u6b7b\u53bb\u5462\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u90d1\u5144\u4e0d\u7528\u5fc3\u6025\uff0c\u6d1b\u9633\u5206\u5802\u867d\u7136\u53d1\u73b0\u4e86\u4ed6\u4eec\u7684\u5c38\u4f53\uff0c\u4f46\u90a3\u4eba\u53ea\u662f\u522b\u4eba\u7684\u4f2a\u88c5\u800c\u5df2\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u4e00\u8138\u7591\u8651\u4e4b\u8272\uff0c\u63a5\u9053\uff1a\u201c\u771f\u7684\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5927\u4f2f\uff0c\u5e2e\u4e3b\u8bf4\u7684\u662f\u771f\u7684\uff0c\u636e\u5206\u5802\u8bf4\uff0c\u90a3\u4e24\u5177\u5c38\u4f53\u7ecf\u8fc7\u68c0\u67e5\uff0c\u4e0d\u662f\u77f3\u4e09\u53d4\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u771f\u7684\uff1f\u201d\u4ed6\u5ffd\u7136\u4ef0\u5929\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e09\u5f1f\u4e0d\u662f\u77ed\u547d\u4e4b\u76f8\uff0c\u770b\u6765\u90a3\u6b7b\u53bb\u7684\u4eba\uff0c\u4e0d\u4f1a\u662f\u4ed6\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5927\u4f2f\uff0c\u8fd9\u4e8b\u5f88\u5947\u602a\u554a\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u4e8b\u679c\u7136\u5947\u602a\uff0c\u65b9\u5929\u6210\u53c8\u600e\u4f1a\u548c\u77f3\u4e09\u5f1f\u5728\u4e00\u8d77\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5927\u4f2f\u2026\u2026\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4f60\u83ab\u975e\u60f3\u51fa\u4e86\u5176\u4e2d\u7684\u9053\u7406\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u4e0d\u8fc7\uff0c\u5c0f\u4f84\u8ba4\u4e3a\uff0c\u77f3\u4e09\u53d4\u65e2\u7136\u548c\u65b9\u5929\u6210\u5728\u4e00\u9053\uff0c\u5176\u4e2d\u5fc5\u6709\u5f88\u5927\u7684\u539f\u56e0\u2026\u2026\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u611a\u4f2f\u4e5f\u8fd9\u4e48\u60f3\uff0c\u53ea\u662f\uff0c\u90a3\u53c8\u4e3a\u4e86\u4ec0\u4e48\uff1f\u77f3\u4e09\u5f1f\u660e\u77e5\u65b9\u5929\u6210\u548c\u6211\u5272\u888d\u65ad\u4e49\u4e86\u554a\uff01\u201d\u4ed6\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u63a5\u9053\uff1a\u201c\u745c\u513f\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5927\u4f2f\u6709\u4f55\u89c1\u6559\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4f60\u6614\u65e5\u5728\u90a3\u6811\u6797\u4e2d\u66fe\u8bf4\uff0c\u65b9\u745c\u4e24\u5b57\uff0c\u5df2\u7136\u4e0d\u518d\u7528\u4e86\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5c0f\u4f84\u4e00\u76f4\u662f\u7528\u5148\u7236\u6240\u53d6\u7684\u96ea\u5b9c\u4e24\u5b57\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u5f88\u597d\uff0c\u6b64\u540e\u6211\u4e5f\u4e0d\u518d\u53eb\u4f60\u745c\u513f\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5927\u4f2f\uff0c\u8981\u53eb\u90a3\u4e5f\u6ca1\u6709\u5173\u7cfb\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4e0d\u53eb\u4e86\uff01\u611a\u4f2f\u6b64\u540e\u53eb\u4f60\u8d24\u4f84\u4fbf\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4efb\u51ed\u5927\u4f2f\u4fbf\u662f\u2026\u2026\u201d\u4ed6\u8bed\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5927\u4f2f\uff0c\u6709\u5173\u77f3\u4e09\u53d4\u7684\u4e8b\uff0c\u970d\u5e2e\u4e3b\u5df2\u7136\u6d3e\u4eba\u524d\u53bb\u6d1b\u9633\u67e5\u95ee\uff01\u5012\u662f\u6211\u90a3\u5a76\u6bcd\u548c\u73de\u5144\u5f1f\uff0c\u800c\u4eca\u53c8\u4e0d\u77e5\u53bb\u4e86\u4f55\u5904\u554a\u2026\u2026\uff1f\u201d \u3000\u3000\u8bed\u97f3\u672a\u5df2\uff0c\u4e5f\u5df2\u7136\u76ee\u4e2d\u6ee1\u5b55\u4e86\u6cea\u5149\uff01 \u3000\u3000\u90d1\u5927\u521a\u53f9\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8d24\u4f84\uff0c\u8fd9\u4e8b\u53ea\u6015\u975e\u5f97\u7b49\u627e\u5230\u77f3\u4e09\u5f1f\u4e0d\u53ef\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u4eff\u4f5b\u6709\u4e9b\u4e0d\u8010\u70e6\uff0c\u5927\u58f0\u9053\uff1a\u201c\u65b9\u8001\u5f1f\uff0c\u4f60\u4eec\u8bf4\u6765\u8bf4\u53bb\uff0c\u53ef\u66fe\u8bf4\u51fa\u4ec0\u4e48\u7ed3\u679c\u4e86\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8f9b\u8001\uff0c\u8bf4\u5b8c\u4e86\uff0c\u5269\u4e0b\u7684\u4e8b\uff0c\u8981\u7b49\u5230\u89c1\u5230\u77f3\u4e09\u53d4\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u7b11\u9053\uff1a\u201c\u90a3\u5f88\u597d\uff0c\u4f60\u4eec\u53ef\u4ee5\u6b47\u6b47\u4e86\uff0c\u90d1\u5927\u521a\u5927\u4f24\u521d\u6108\uff0c\u8fd8\u662f\u542c\u8001\u592b\u7684\u8bdd\uff0c\u5c11\u8d39\u795e\uff0c\u591a\u4f11\u606f\u4f11\u606f\u5427\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u4f3c\u662f\u60f3\u8bf4\u4ec0\u4e48\uff0c\u4f46\u4ed6\u770b\u4e86\u8f9b\u67cf\u516c\u4e00\u773c\uff0e\u6447\u4e86\u6447\u5934\uff0c\u62b1\u62f3\u9053\uff1a\u201c\u665a\u8f88\u77e5\u9053\u4e86\uff01\u201d\u5f53\u5373\u5728\u90a3\u4e91\u5e8a\u4e4b\u4e0a\uff0c\u5750\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u8f9b\u67cf\u516c\u70b9\u4e86\u70b9\u5934\uff0c\u7b11\u9053\uff1a\u201c\u65b9\u8003\u5f1f\uff0c\u54b1\u4eec\u522b\u6253\u6270\u4ed6\uff0c\u8fd8\u662f\u51fa\u53bb\u5f04\u70b9\u4ec0\u4e48\u5403\u7684\u5427\uff01\u201d\u5f53\u5148\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u6211\u4f9d\u4f60\u8001\u2026\u2026\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u548c\u970d\u9e23\u98ce\u3001\u5b8b\u5b5a\u3001\u5b89\u5c0f\u840d\u4e5f\u5168\u90e8\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u665a\u996d\u4e4b\u540e\uff0c\u65b9\u96ea\u5b9c\u8d76\u56de\u5230\u90d1\u5927\u521a\u6b47\u606f\u7684\u5ba2\u623f\uff0c\u8fd8\u6ca1\u6709\u5750\u4e0b\u6765\uff0c\u53ea\u89c1\u90a3\u5165\u4e91\u5927\u5e08\u5306\u5306\u5730\u8d70\u5165\u623f\u4e2d\uff0c\u5408\u5341\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\uff0c\u6709\u4f4d\u8001\u4eba\u5728\u85cf\u7ecf\u697c\u6307\u540d\u8981\u89c1\u5c0f\u65bd\u4e3b\uff0c\u8001\u8872\u638c\u95e8\u5e08\u5f1f\uff0c\u7279\u547d\u8001\u8872\u524d\u6765\u76f8\u8bf7\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5927\u5e08\u53ef\u77e5\u90a3\u4f4d\u8001\u4eba\u662f\u8c01\u5417\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u6447\u5934\u9053\uff1a\u201c\u8001\u8872\u638c\u95e8\u5e08\u5f1f\u66fe\u7ecf\u95ee\u8fc7\u4ed6\uff0c\u4f46\u56e0\u4ed6\u9690\u8eab\u5728\u85cf\u7ecf\u697c\u7684\u4e00\u5ea7\u7ecf\u67dc\u4e4b\u5185\uff0c\u8001\u8872\u7b49\u770b\u4e0d\u6e05\u4ed6\u7684\u5bb9\u8c8c\uff0c\u4ed6\u53c8\u6ca1\u6709\u8bf4\u51fa\u540d\u59d3\uff0c\u662f\u4ee5\u76f4\u5230\u6b64\u523b\uff0c\u5c1a\u4e0d\u77e5\u6b64\u8001\u662f\u8c01\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u53ea\u542c\u5f97\u5fc3\u4e2d\u6697\u611f\u5947\u602a\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u5c31\u5947\u4e86\uff01\u4ec0\u4e48\u4eba\u5c45\u7136\u80fd\u591f\u51af\u4eba\u5c11\u6797\u5bfa\u85cf\u7ecf\u697c\u4e2d\u9690\u533f\u3002\u201d \u3000\u3000\u5bfb\u601d\u95ee\uff0c\u4e0d\u7981\u63a5\u9053\uff1a\u201c\u90a3\u4f4d\u8001\u4eba\u600e\u5730\u8fdb\u5165\u85cf\u7ecf\u697c\uff0c\u96be\u9053\u697c\u4e0a\u6ca1\u6709\u770b\u5b88\u4e4b\u4eba\u5417\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\uff0c\u85cf\u7ecf\u697c\u4e43\u662f\u672c\u5bfa\u6700\u4e3a\u68ee\u4e25\u4e4b\u5730\uff0c\u666e\u901a\u4eba\u90fd\u662f\u4e0a\u53bb\u4e0d\u4e86\u7684\u554a\uff01\u201d \u3000\u3000\u4ed6\u4e0d\u7b49\u65b9\u96ea\u5b9c\u518d\u95ee\uff0c\u957f\u53f9\u4e00\u58f0\uff0c\u63a5\u76f4\uff1a\u201c\u8fd9\u4e2a\u8001\u65bd\u4e3b\u7684\u6b66\u529f\uff0c\u770b\u6765\u4e0d\u5f31\uff0c\u5426\u5219\uff0c\u4ed6\u51b3\u7136\u65e0\u6cd5\u9690\u7792\u8fc7\u957f\u65e5\u5750\u9547\u697c\u4e0a\u7684\u5165\u6cd5\u7b49\u4e24\u4f4d\u5e08\u5f1f\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fd6\u9053\uff1a\u201c\u679c\u7136\u662f\uff01\u8fd9\u4eba\u82e5\u975e\u6b66\u529f\u6781\u9ad8\uff0c\u53ea\u6015\u5165\u5723\u5927\u5e08\u4e5f\u4e0d\u4f1a\u5dee\u4eba\u524d\u6765\u9080\u7ea6\u6211\u7b49\u53bb\u7684\u4e86\u3002\u201d\u4ed6\u5fc3\u4e2d\u8f6c\u52a8\u5ff5\u5934\uff0c\u5fc3\u4e2d\u5374\u76f4\uff1a\u201c\u90a3\u8001\u4eba\u6b66\u529f\u518d\u9ad8\uff0c\u53ea\u6015\u4e5f\u65e0\u6cd5\u80dc\u5f97\u8fc7\u5927\u5e08\u4eec\u8054\u624b\u76f8\u903c\u5462\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201d\u5c0f\u65bd\u4e3b\uff0c\u90a3\u85cf\u7ecf\u697c\u73cd\u85cf\u4e86\u672c\u5bfa\u5386\u4ee3\u76f8\u4f20\u7684\u5b9d\u7b08\uff0c\u4e00\u65e6\u52a8\u624b\uff0c\u5018\u662f\u60f9\u607c\u4e86\u90a3\u4f4d\u8001\u65bd\u4e3b\uff0c\u6bc1\u53bb\u51e0\u67dc\u7ecf\u5178\uff0c\u90a3\u5c82\u4e0d\u662f\u8001\u8872\u7b49\u7f6a\u5b7d\u901a\u5929\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u604d\u7136\u9053\uff1a\u201c\u539f\u6765\u6709\u7740\u8fd9\u7b49\u5fcc\u8bb3\u2026\u2026\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u5411\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u665a\u8f88\u8fd9\u5c31\u524d\u53bb\uff01\u201d \u3000\u3000\u4ed6\u56de\u987e\u4e86\u5c1a\u5728\u8c03\u606f\u7684\u90d1\u5927\u521a\u4e00\u773c\uff0c\u968f\u5728\u5165\u4e91\u5927\u5e08\u7684\u8eab\u540e\uff0c\u76f4\u5411\u85cf\u7ecf\u697c\u5954\u53bb\u3002 \u3000\u3000\u8d8a\u8fc7\u4e86\u4e09\u91cd\u4f5b\u6bbf\uff0c\u659c\u659c\u7684\u7a7f\u51fa\u4e00\u5904\u82b1\u5703\uff0c\u53ea\u89c1\u5728\u90a3\u6797\u6728\u63a9\u6620\u4e4b\u4e2d\uff0c\u6709\u7740\u4e00\u680b\u706f\u5149\u660e\u4eae\u7684\u697c\u5b87\uff0c\u90a3\u6b63\u662f\u5c11\u6797\u91cd\u5730\u85cf\u7ecf\u697c\u4e86\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u968f\u7740\u5165\u4e91\u5927\u5e08\u4e0a\u5f97\u697c\u53bb\uff0c\u53ea\u89c1\u5728\u5de6\u9996\u7684\u4e00\u95f4\u6ee1\u67b6\u7ecf\u6587\u7684\u53a2\u623f\u4e4b\u4e2d\uff0c\u5165\u5723\u638c\u95e8\u4eba\u6b63\u81ea\u5140\u7acb\u95e8\u9996\uff0c\u5411\u5916\u5f20\u671b\u4e0d\u4f11\u3002 \u3000\u3000\u4ed6\u4e00\u773c\u77a7\u89c1\u65b9\u96ea\u5b9c\u968f\u5728\u5165\u4e91\u5927\u5e08\u7684\u8eab\u540e\u884c\u6765\uff0c\u987f\u65f6\u8138\u4e0a\u795e\u8272\u5927\u4e3a\u653e\u677e\uff0c\u53cc\u624b\u5408\u4e0b\u9053\uff1a \u3000\u3000\u201c\u5c0f\u65bd\u4e3b\uff0c\u8001\u8872\u5728\u6b64\u5019\u9a7e\u751a\u4e45\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u75be\u6b65\u4e0a\u524d\uff0c\u62b1\u62f3\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u90a3\u8001\u4eba\u5bb6\u53ef\u8fd8\u5728\u8fd9\u53a2\u623f\u4e4b\u4e2d\u5417\uff1f\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u8fd9\u4f4d\u8001\u65bd\u4e3b\u5c1a\u5728\u623f\u4e2d\u2026\u2026\u201d \u3000\u3000\u4f59\u97f3\u672a\u5df2\uff0c\u53ea\u542c\u5f97\u623f\u4e2d\u6709\u4eba\u5927\u7b11\u9053\uff1a\u201c\u65b9\u8001\u5f1f\uff0c\u4f60\u6765\u4e86\uff1f\u201d \u3000\u3000\u5374\u89c1\u5de6\u624b\u7684\u90a3\u5c42\u7ecf\u67dc\u7684\u9876\u4e0a\uff0c\u63a2\u51fa\u4e00\u4e2a\u84ec\u9996\u57a2\u9762\u7684\u8111\u888b\u6765\u4e86\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u51dd\u76ee\u671b\u53bb\uff0c\u4e0d\u7981\u5927\u58f0\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\u8001\uff1f\u771f\u662f\u628a\u4eba\u6025\u574f\u4e86\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u76b1\u7709\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\uff0c\u4f60\u679c\u7136\u662f\u8ba4\u5f97\u8fd9\u4f4d\u8001\u65bd\u4e3b\uff1f\u201d \u3000\u3000\u6562\u60c5\uff0c\u8fd9\u4f4d\u638c\u95e8\u4eba\u5148\u524d\u8fd8\u4e0d\u4fe1\u65b9\u96ea\u5b9c\u4e0e\u4ed6\u76f8\u8bc6\u5462\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u5f53\u7136\u8ba4\u5f97\uff01\u638c\u95e8\u4eba\uff0c\u8fd9\u4f4d\u8001\u524d\u8f88\uff0c\u662f\u90a3\u4f4f\u5728\u592a\u5ba4\u5cf0\u9876\u7684\u8001\u4eba\u554a\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u4e00\u6014\u9053\uff1a\u201c\u795d\u8001\u65bd\u4e3b\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u53ef\u4e0d\u662f\uff1f\u201d \u3000\u3000\u539f\u6765\u90a3\u63a2\u5934\u800c\u51fa\u7684\u8001\u4eba\uff0c\u6b63\u662f\u201c\u5929\u53f0\u6a35\u53df\u201d\u795d\u516c\u660e\u3002 \u3000\u3000\u795d\u516c\u660e\u867d\u7136\u63a2\u51fa\u4e86\u5934\u6765\uff0c\u4f46\u4ed6\u4ecd\u7136\u672a\u66fe\u5168\u8eab\u800c\u51fa\uff0c\u4f4e\u58f0\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u90a3\u8f9b\u8001\u602a\u6765\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u65e9\u5df2\u6765\u4e86\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u987f\u65f6\u7f29\u56de\u4e86\u8111\u888b\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4ed6\u77e5\u4e0d\u77e5\u9053\u8001\u592b\u9690\u5c45\u5728\u592a\u5ba4\u5cf0\u9876\uff1f\u4f60\u4eec\u2026\u2026\u6ca1\u6709\u544a\u8bc9\u4ed6\u8001\u592b\u90a3\u5929\u626e\u8fc7\u54d1\u4e10\u9ec4\u51b2\u5427\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u8fd9\u8001\u513f\u8eb2\u5728\u7ecf\u67dc\u4e4b\u4e0a\uff0c\u6211\u5982\u662f\u7167\u5b9e\u8bf4\u660e\uff0c\u53ea\u6015\u4ed6\u5c31\u4e0d\u80af\u4e0b\u6765\u4e86\uff01\u5c11\u4e0d\u5f97\u6211\u8981\u9a97\u4ed6\u4e00\u9a97\uff0c\u5c06\u4ed6\u5f04\u4e0b\u6765\u624d\u597d\u3002\u201d \u3000\u3000\u5fc3\u5ff5\u4e00\u5b9a\uff0c\u5f53\u4e0b\u9053\uff1a\u201c\u8f9b\u8001\u6765\u6b64\u4ee5\u540e\uff0c\u6839\u672c\u672a\u63d0\u5f53\u65e5\u4e4b\u4e8b\uff0c\u4f60\u8001\u4e0d\u7528\u653e\u5728\u5fc3\u4e0a\u4e86\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u76b1\u4e86\u76b1\u7709\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u6ca1\u6709\u9a97\u6211\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u6ca1\u6709\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u7f13\u7f13\u5730\u63a2\u51fa\u5934\u6765\uff0c\u5411\u56db\u5468\u6253\u91cf\u4e86\u4e00\u4e0b\uff0c\u8fd9\u624d\u53c8\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u90a3\u8f9b\u8001\u513f\u73b0\u5728\u4f55\u5904\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u8f9b\u8001\u73b0\u5728\u524d\u5bfa\u4e4b\u4e2d\u2026\u2026\u201d \u3000\u3000\u795d\u516c\u660e\u7b11\u9053\uff1a\u201c\u4ed6\u4e0d\u5728\u8fd9\u697c\u4e0b\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u6ca1\u6709\uff01\u665a\u8f88\u4e43\u662f\u72ec\u81ea\u4e00\u4eba\u524d\u6765\uff0c\u8f9b\u8001\u81ea\u662f\u4e0d\u4f1a\u77e5\u9053\u4f60\u8001\u5df2\u5728\u5bfa\u4e2d\u7684\u4e86\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u8fd9\u624d\u63a2\u51fa\u534a\u4e86\u4e2a\u8eab\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u771f\u7684\u4e0d\u66fe\u5bf9\u8f9b\u67cf\u516c\u8bf4\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e0d\u7b49\u4ed6\u628a\u8bdd\u8bf4\u5b8c\uff0c\u4fbf\u81ea\u63a5\u9053\uff1a\u201c\u8001\u524d\u8f88\u665a\u8f88\u7b49\u4eba\u65e2\u662f\u77e5\u9053\u4f60\u8001\u4e0d\u613f\u8ba9\u8f9b\u8001\u77e5\u9053\u4f60\u7684\u8eab\u4efd\uff0c\u90a3\u81ea\u7136\u662f\u4e0d\u4f1a\u5bf9\u4ed6\u8a00\u660e\u4e86\u2026\u2026\u201d\u4f3c\u8fd9\u7b49\u6627\u5fc3\u4e4b\u8a00\uff0c\u65b9\u96ea\u5b9c\u53e3\u4e2d\u867d\u7136\u662f\u8bf4\u4e86\u51fa\u6765\uff0c\u4f46\u4fca\u8138\u4e4b\u4e0a\uff0c\u5df2\u6697\u6697\u611f\u5230\u53d1\u70e7\u3002 \u3000\u3000\u5e78\u800c\u795d\u516c\u660e\u53ea\u987e\u5411\u7740\u95e8\u5916\u63a2\u770b\uff0c\u672a\u66fe\u77a7\u5230\u65b9\u96ea\u5b9c\u8d6b\u7136\u7684\u795e\u8272\uff01 \u3000\u3000\u795d\u516c\u660e\u8fd9\u65f6\u5927\u6982\u770b\u660e\u767d\u4e86\u95e8\u5916\u5e76\u65e0\u8f9b\u67cf\u516c\u7684\u8eab\u5f71\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u95ea\u8eab\u6253\u7ecf\u67dc\u4e4b\u4e0a\uff0c\u8df3\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5165\u4e91\u5927\u5e08\u5728\u795d\u516c\u660e\u843d\u5730\u4ee5\u540e\uff0c\u4fbf\u81ea\u8d70\u8fdb\u95e8\u5185\uff0c\u5408\u5341\u5411\u4ed6\u4e3a\u793c\uff0c\u7b11\u9053\uff1a\u201c\u8d2b\u50e7\u5165\u4e91\uff0c\u89c1\u8fc7\u795d\u8001\u65bd\u4e3b\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u5bf9\u90a3\u8f9b\u67cf\u516c\u867d\u7136\u5341\u5206\u656c\u754f\uff0c\u4f46\u5bf9\u5165\u4e91\u638c\u95e8\u4eba\u5374\u662f\u6446\u51fa\u4e00\u526f\u5927\u523a\u523a\u7684\u795e\u8272\uff0c\u6446\u4e86\u6446\u624b\uff0c\u7b11\u9053\uff1a\u201c\u638c\u95e8\u4eba\u514d\u793c\uff01\u201d \u3000\u3000\u4ed6\u8fd9\u7b49\u53e3\u6c14\uff0c\u53ea\u628a\u65b9\u96ea\u5b9c\u542c\u5f97\u6014\u4e86\u4e00\u6014\uff0c\u6697\u9053\uff1a\u201c\u4ed6\u600e\u53ef\u7528\u8fd9\u7b49\u53e3\u6c14\uff0c\u5bf9\u5f85\u5c11\u6797\u638c\u95e8\u5462\uff1f\u9664\u975e\u4ed6\u662f\u5165\u4e91\u5927\u5e08\u7684\u957f\u8f88\u2026\u2026\u201d \u3000\u3000\u601d\u5fd6\u95f4\uff0c\u53ea\u89c1\u5165\u5723\u5927\u5e08\u4e5f\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u795d\u516c\u660e\u77a7\u89c1\u5165\u5723\uff0c\u7adf\u662f\u76b1\u7709\u9053\uff1a\u201c\u798f\u5efa\u8386\u7530\u4e0b\u9662\uff0c\u53ef\u662f\u5927\u548c\u5c1a\u7ba1\u7684\u5417\uff1f\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u5408\u5341\u9053\uff1a\u201c\u8001\u65bd\u4e3b\uff0c\u8386\u7530\u4e0b\u9662\uff0c\u5df2\u7531\u8d2b\u50e7\u5f1f\u5b50\u63a5\u638c\u65b9\u4e08\u4e86\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u51b7\u7b11\u9053\uff1a\u201c\u5927\u5e08\u4f3c\u662f\u5341\u5206\u653e\u5fc3\u90a3\u798f\u5efa\u8386\u7530\u4e0b\u9662\u7684\u4e86\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u8001\u65bd\u4e3b\u2026\u2026\u83ab\u975e\u8001\u65bd\u4e3b\u8fd1\u65e5\u53bb\u8fc7\u798f\u5efa\u8386\u7530\u4e0b\u9662\u5417\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u8001\u592b\u90a3\u6709\u5de5\u592b\u8dd1\u5230\u798f\u5efa\uff1f\u4e0d\u8fc7\uff0c\u636e\u8001\u592b\u6240\u77e5\uff0c\u5927\u548c\u5c1a\u6765\u5230\u5d69\u5c71\u4ee5\u540e\uff0c\u8386\u7530\u5c11\u6797\u5c31\u51fa\u4e86\u4e8b\u4e86\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8001\u65bd\u4e3b\u65e2\u662f\u672a\u53bb\u798f\u5efa\uff0c\u53c8\u600e\u7684\u77e5\u6653\u8386\u7530\u5c11\u6797\u4e0b\u9662\u51fa\u4e86\u4e8b\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u8001\u592b\u600e\u751f\u77e5\u6653\uff0c\u4f60\u5012\u4e0d\u5fc5\u8fc7\u95ee\uff0c\u53cd\u6b63\uff0c\u8001\u592b\u6ca1\u6709\u9a97\u4f60\u4fbf\u662f\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u770b\u4e86\u5165\u4e91\u5927\u5e08\u4e00\u773c\uff0c\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u8fd1\u65e5\u8386\u7530\u53ef\u6709\u6d88\u606f\u4f20\u6765\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u6628\u65e5\u65b9\u59cb\u6536\u5230\u5b8f\u5149\u5e08\u4f84\u4fe1\u606f\uff0c\u5018\u662f\u8386\u7530\u90a3\u8fb9\u51fa\u4e86\u4ec0\u4e48\u4e8b\uff0c\u90a3\u4e5f\u4e0d\u4f1a\u518d\u6709\u4fe1\u9e3d\u524d\u6765\u7684\u4e86\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u90a3\u4fe1\u4e0a\u600e\u4e48\u8bf4\u7684\u4e86\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u6c89\u541f\u4e00\u4e0b\uff0c\u65b9\u9053\uff1a\u201c\u90a3\u4fe1\u4e0a\u8a00\u9053\uff0c\u5bfa\u4e2d\u4e00\u5207\u5b89\u8c27\u5982\u5e38\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u5ffd\u7136\u5927\u7b11\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u4f60\u4eec\u4e0a\u5f53\u4e86\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u4e00\u6014\u9053\uff1a\u201c\u8d2b\u50e7\u4e0a\u4e86\u4ec0\u4e48\u5f53\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u90a3\u4e66\u4fe1\u53ea\u6015\u4e0d\u662f\u4f60\u90a3\u5b8f\u5149\u5e08\u4f84\u6240\u5199\u7684\u5427\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u7b14\u8ff9\u4e0d\u9519\uff0c\u600e\u4f1a\u6709\u5047\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u7b11\u9053\uff1a\u201c\u5927\u5e08\u4e4b\u610f\uff0c\u90a3\u6b63\u662f\u4f60\u5e08\u4f84\u4eb2\u7b14\u7684\u51fd\u4ef6\u5417\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u4e0d\u9519\u2026\u2026\u201d \u3000\u3000\u795d\u516c\u660e\u770b\u4e86\u4e24\u4f4d\u548c\u5c1a\u4e00\u773c\uff0c\u7b11\u9053\uff1a\u201c\u5018\u662f\u6709\u4eba\u4eff\u9020\u7b14\u8ff9\u5462\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u600e\u4e48\u4f1a\uff0c\u60f3\u90a3\u5ba0\u5149\u5e08\u4f84\u4e3a\u4eba\u7cbe\u660e\u80fd\u5e72\uff0c\u8d2b\u50e7\u4e0d\u4fe1\uff0c\u4ed6\u4f1a\u88ab\u4eba\u8499\u853d\u2026\u2026\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u8499\u853d\u81ea\u7136\u4e0d\u4f1a\uff0c\u5982\u662f\u6709\u4eba\u8feb\u4ed6\u4e66\u5199\u8fd9\u7b49\u5e73\u5b89\u4fe1\u51fd\uff0c\u53ea\u6015\u53c8\u5f53\u522b\u8bba\u4e86\u5427\uff01\u201d \u3000\u3000\u4ed6\u8bdd\u97f3\u4e00\u843d\uff0c\u5165\u4e91\u5927\u5e08\u4e0d\u7981\u5446\u4e86\u4e00\u5446\u3002 \u3000\u3000\u5165\u5723\u5927\u5e08\u8138\u8272\u5927\u53d8\uff0c\u63a5\u9053\uff1a\u201c\u8001\u65bd\u4e3b\u4e4b\u8a00\uff0c\u662f\u90a3\u8386\u7530\u4e0b\u9662\u5df2\u7136\u843d\u5728\u654c\u4eba\u7684\u624b\u4e2d\u4e86\u5417\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u4e5f\u8bb8\u6ca1\u6709\uff0c\u53ea\u662f\uff0c\u8001\u592b\u5374\u542c\u8bf4\u5230\u8386\u7530\u4e0b\u9662\u5df2\u7ecf\u88ab\u4eba\u63a7\u5236\u4e86\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u600e\u4e48\u8bf4\u7684\u3002\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u81ea\u7136\u662f\u4e94\u5927\u9b54\u4e3b\u624b\u4e0b\u7684\u4eba\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u6c89\u541f\u9053\uff1a\u201c\u4f46\u4e0d\u77e5\u65bd\u4e3b\u5728\u4f55\u5904\u542c\u6765\u7684\u6d88\u606f\uff1f\u90a3\u8bf4\u51fa\u6b64\u4e8b\u4e4b\u4eba\u73b0\u5728\u4f55\u5904\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u90a3\u4eba\u5417\uff1f\u4ed6\u6b64\u523b\u53ea\u6015\u5df2\u7ecf\u88ab\u4eba\u6740\u6b7b\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u65f6\u53ea\u542c\u5f97\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u201c\u83ab\u975e\u90a3\u8bf4\u51fa\u6b64\u4e8b\u4e4b\u4eba\uff0c\u4fbf\u662f\u6b7b\u5728\u592a\u5ba4\u5cf0\u5934\u7684\u6c49\u5b50\u4e86\uff1f\u201d \u3000\u3000\u4ed6\u4e00\u5ff5\u53ca\u6b64\uff0c\u4e0d\u7981\u63a5\u9053\uff1a\u201c\u795d\u8001\uff0c\u90a3\u4eba\u662f\u6b7b\u5728\u4f60\u8001\u7684\u624b\u4e0b\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u7b11\u9053\uff1a\u201c\u8c01\u8bf4\u7684\uff1f\u8001\u592b\u5c82\u662f\u968f\u610f\u6740\u4eba\u4e4b\u4eba\uff1f\u8001\u5f1f\uff0c\u4f60\u8fd9\u8bdd\u662f\u4ece\u4f55\u800c\u8d77\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u665a\u8f88\u5728\u65e5\u843d\u4e4b\u524d\uff0c\u66fe\u7ecf\u53bb\u8fc7\u592a\u5ba4\u5cf0\u5934\u4e00\u6b21\u2026\u2026\u201d \u3000\u3000\u795d\u516c\u660e\u95fb\u8a00\uff0c\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u4f60\u4eca\u5929\u53bb\u8fc7\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4f60\u8001\u4e0d\u76f8\u4fe1\u5417\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u76f8\u4fe1\uff0c\u4e00\u5343\u4e2a\u76f8\u4fe1\uff0c\u4e00\u4e07\u4e2a\u76f8\u4fe1\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u5df2\u7136\u6709\u4e86\u4e03\u516b\u5206\u660e\u767d\uff0c\u5f53\u4e0b\u53ea\u662f\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u795d\u8001\uff0c\u665a\u8f88\u53d1\u73b0\uff0c\u90a3\u6b7b\u53bb\u4e4b\u4eba\uff0c\u4f3c\u662f\u7a7f\u7740\u90a3\u5b8b\u5b5a\u4e3a\u4f60\u4e70\u7684\u8863\u5c65\u5462\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u76b1\u8d77\u4e86\u53cc\u7709\uff0c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u8fd9\u4e48\u8bf4\uff0c\u90a3\u4eba\u5f53\u771f\u7684\u5df2\u7136\u6b7b\u6389\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u6b7b\u4e86\uff01\u201d\u4ed6\u8bed\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u795d\u8001\uff0c\u90a3\u4eba\u4e43\u662f\u6b7b\u5728\u4e00\u79cd\u53eb\u505a\u2018\u8d64\u7130\u638c\u2019\u7684\u6b66\u529f\u4e4b\u4e0b\uff0c\u4e0d\u77e5\u80fd\u53d1\u51fa\u8fd9\u2018\u8d64\u7130\u638c\u2019\u7684\u4eba\uff0c\u662f\u4ec0\u4e48\u6765\u8def\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8d64\u7130\u638c\u5417\uff1f\u8001\u592b\u5f53\u7136\u77e5\u9053\u8fd9\u79cd\u6b66\u529f\u6765\u8def\u554a\uff01\u201d\u4ed6\u5ffd\u7136\u957f\u53f9\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u5509\uff01\u679c\u7136\u662f\u4ed6\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4f60\u8001\u77e5\u9053\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u5fc5\u662f\u4e94\u5927\u9b54\u4e3b\u4e2d\u7684\u6076\u5b54\u660e\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u6697\u9053\uff1a\u4ed6\u4e5f\u8bf4\u662f\u6076\u5b54\u660e\uff0c\u770b\u6765\u8fd9\u4e94\u9b54\u4e2d\u7684\u6bd2\u5927\u592b\u679c\u771f\u4e5f\u6765\u5230\u4e86\u5d69\u5c71\u4e86\uff01\u8f6c\u5ff5\u4e4b\u610f\uff0c\u9053\uff1a\u201c\u795d\u8001\uff0c\u90a3\u8f9b\u8001\u524d\u8f88\u66fe\u8bf4\uff0c\u90a3\u4eba\u5982\u975e\u6b7b\u5728\u6076\u5b54\u660e\u4e4b\u624b\uff0c\u5c31\u662f\u6b7b\u5728\u4f60\u8001\u624b\u4e2d\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bdd\u672a\u8bf4\u5b8c\uff0c\u795d\u516c\u660e\u5df2\u7136\u795e\u60c5\u5927\u53d8\u4e86\uff0c\u53eb\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u90a3\u8f9b\u8001\u602a\u4e5f\u53bb\u4e86\u592a\u5ba4\u5cf0\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u795d\u8001\uff0c\u6709\u4e00\u4ef6\u4e8b\uff0c\u665a\u8f88\u8981\u8bf7\u4f60\u8001\u89c1\u8c05\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u665a\u8f88\u9002\u624d\u9a97\u4e86\u4f60\u4e86\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u9a97\u4e86\u8001\u592b\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u6709\u5173\u4f60\u8001\u9690\u5c45\u5728\u5d69\u5c71\u4e4b\u4e8b\uff0c\u90a3\u8f9b\u8001\u4e2d\u5df2\u77e5\u9053\u4e86\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u4ed6\u77e5\u9053\u4e86\uff1f\u8c01\u544a\u8bc9\u4e86\u4ed6\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8c01\u4e5f\u4e0d\u66fe\u8bf4\u51fa\u6765\uff0c\u90a3\u662f\u4ed6\u8001\u4eba\u5bb6\u81ea\u5df1\u60f3\u51fa\u6765\u7684\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u5ffd\u7136\u95ea\u8eab\u4fbf\u5f85\u518d\u5411\u90a3\u58c1\u6a71\u4e4b\u4e0a\u8dc3\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5728\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u5c31\u66fe\u7559\u5fc3\u4ed6\u7684\u8138\u8272\uff0c\u7779\u72b6\u5fd9\u9053\uff1a\u201c\u795d\u8001\uff0c\u4e0d\u7528\u518d\u8eb2\u907f\u8f9b\u8001\u4e86\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u4e00\u6014\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u4f60\u8001\u5f1f\u77e5\u9053\u8001\u8ba9\u4ed6\u7684\u539f\u56e0\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u77e5\u9053\uff01\u4e0d\u8fc7\uff0c\u8f9b\u8001\u4ed6\u8bf4\uff0c\u90a3\u9762\u4ee4\u65d7\u4ed6\u5df2\u7136\u65e9\u5c31\u4ea4\u56de\u7ed9\u4f60\u90a3\u4f4d\u5e08\u4f84\u6768\u51b2\u4e86\uff01 \u3000\u3000\u795d\u516c\u660e\u6123\u6123\u5730\u9053\uff1a\u201c\u771f\u7684\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8f9b\u8001\u4eb2\u53e3\u6240\u8bf4\uff0c\u81ea\u7136\u4e0d\u4f1a\u5047\u7684\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u5ffd\u7136\u957f\u957f\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u90a3\u5c31\u597d\u4e86\uff01\u201d \u3000\u3000\u5ffd\u7136\u8f6c\u5934\u5411\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u548c\u5c1a\uff0c\u90a3\u8386\u7530\u4e0b\u9662\u773c\u4e0b\u786e\u5df2\u843d\u4eba\u4e86\u4e94\u5927\u9b54\u4e3b\u4e4b\u624b\u4e86\uff01\u8001\u592b\u521a\u624d\u6240\u8bf4\uff0c\u591a\u534a\u662f\u81ea\u90a3\u6b7b\u5728\u592a\u5ba4\u5cf0\u5934\u7684\u6076\u5b54\u660e\u624b\u4e0b\u4e4b\u4eba\u53e3\u4e2d\u542c\u6765\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u8138\u8272\u5927\u53d8\uff0c\u9053\uff1a\u201c\u8fd9\u2026\u2026\u90a3\u5b8f\u5149\u600e\u5730\u5982\u6b64\u7cca\u6d82\uff1f\u5c11\u6797\u4e0b\u9662\u5982\u679c\u771f\u5df2\u88ab\u4eba\u63a7\u5236\uff0c\u4ed6\u81f3\u5c11\u4e5f\u8be5\u8bbe\u6cd5\u901a\u77e5\u8001\u8872\u624d\u662f\u2026\u2026\u201d \u3000\u3000\u795d\u516c\u660e\u7b11\u9053\uff1a\u201c\u548c\u5c1a\uff0c\u4f60\u592a\u4f4e\u4f30\u4e86\u4e94\u9b54\u7684\u624b\u6bb5\u5566\uff01\u4ed6\u4eec\u65e2\u7136\u6709\u5fc3\u8981\u4f60\u4eec\u67e5\u4e0d\u51fa\u8386\u7530\u5c11\u6797\u4e4b\u4e8b\uff0c\u53ea\u6015\u5c31\u662f\u4f60\u548c\u5c1a\u6b64\u523b\u4eb2\u81ea\u8d76\u53bb\uff0c\u90a3\u4e5f\u6ca1\u6709\u7528\u5904\u7684\u4e86\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u95e8\u4eba\u5408\u5341\u9053\uff1a\u201c\u8001\u65bd\u4e3b\u8bf4\u5f97\u4e0d\u9519\uff0c\u90a3\u4e94\u9b54\u884c\u4e8b\uff0c\u679c\u7136\u6bd2\u8fa3\u5f97\u5f88\uff0c\u773c\u4e0b\u5b8f\u5149\u5e08\u4f84\u7684\u5904\u5883\uff0c\u5b9a\u7136\u5341\u5206\u9669\u6076\u7684\u4e86\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u611a\u5144\u60f3\u53bb\u798f\u5efa\u4e00\u884c\u2026\u2026\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u4e0d\u7b49\u4ed6\u5f80\u4e0b\u518d\u8bf4\uff0c\u7adf\u7136\u6447\u5934\u9053\uff1a\u201c\u5e08\u5144\u4f60\u53bb\u4e0d\u5f97\u7684\u4e86\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u4e00\u6014\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u5e08\u5144\u672a\u66fe\u56de\u8f6c\u8386\u7530\uff0c\u4e5f\u8bb8\u5b8f\u5149\u5e08\u4f84\u7b49\u4eba\uff0c\u5c1a\u4e0d\u81f3\u4e8e\u9001\u547d\uff0c\u5e08\u5144\u4e00\u65e6\u56de\u53bb\uff0c\u4f1a\u66ff\u4ed6\u4eec\u62db\u6765\u6740\u8eab\u4e4b\u7978\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u81ea\u662f\u4e0d\u5927\u76f8\u4fe1\uff0c\u63a5\u9053\uff1a\u201c\u638c\u95e8\u4eba\u600e\u77e5\u4ed6\u4eec\u6b64\u523b\u5e76\u672a\u4e27\u547d\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u4e94\u9b54\u65e2\u7136\u5df2\u7136\u63a7\u5236\u4e86\u8386\u7530\u4e0b\u9662\uff0c\u5374\u4f9d\u7136\u8981\u90a3\u5b8f\u5149\u5e08\u4f84\u6309\u65f6\u62a5\u8baf\uff0c\u8db3\u89c1\u4ed6\u4eec\u6b64\u4e3e\uff0c\u4e43\u662f\u522b\u6709\u7528\u5fc3\u7684\u4e86\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u638c\u95e8\u4eba\u9ad8\u89c1\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u5ffd\u5730\u957f\u53f9\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u5982\u662f\u5c0f\u5f1f\u60f3\u7684\u4e0d\u9519\uff0c\u4ed6\u4eec\u53ef\u80fd\u662f\u60f3\u501f\u7528\u8386\u7530\u4e0b\u9662\u7684\u540d\u4e49\uff0c\u5728\u6b66\u6797\u4e2d\u53e6\u5916\u6380\u8d77\u4e00\u58f0\u98ce\u6ce2\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u90a3\u4e0e\u5c0f\u5144\u8d76\u56de\u798f\u5efa\u53c8\u6709\u4f55\u5173\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u5f53\u7136\u6709\u5173\u7684\u4e86\uff01\u201d\u8bed\u97f3\u7565\u7565\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5b8f\u5149\u5e08\u4f84\u5728\u6b66\u6797\u4e2d\u7684\u540d\u671b\uff0c\u81ea\u7136\u96be\u6bd4\u5e08\u5144\u4e07\u4e00\uff0c\u662f\u4ee5\uff0c\u4ed6\u4eec\u5982\u60f3\u5728\u4e1c\u5357\u6b66\u6797\u5236\u9020\u4ec0\u4e48\u7eb7\u4e71\uff0c\u81ea\u7136\u662f\u8981\u60f3\u5230\u5e08\u5144\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u4e00\u6014\u9053\uff1a\u201c\u60f3\u5230\u611a\u5144\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u7b49\u4f60\u56de\u8f6c\u8386\u7530\uff0c\u5229\u7528\u4e8e\u4f60\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u8386\u7530\u65e0\u8b66\uff0c\u611a\u5144\u53c8\u600e\u4f1a\u56de\u53bb\u3002\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u957f\u957f\u4e00\u53f9\u9053\uff1a\u201c\u5e08\u5144\uff0c\u8fd9\u5c31\u662f\u4ed6\u4eec\u9ad8\u660e\u7684\u5730\u65b9\u4e86\uff01\u4ed6\u4eec\u4e0d\u4f7f\u8386\u7530\u544a\u6025\uff0c\u4e43\u662f\u8981\u677e\u9a70\u5e08\u5144\u6212\u5907\u4e4b\u5fc3\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u6709\u4e00\u4ef6\u4e8b\uff0c\u638c\u95e8\u4eba\u5e94\u662f\u77e5\u9053\uff0c\u5d69\u5c71\u672c\u5bfa\u5251\u62d4\u5f29\u5f20\u4e4b\u4e0b\uff0c\u611a\u5144\u51b3\u4e0d\u4f1a\u8d76\u8fd4\u8386\u7530\u5440\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u5e08\u5144\uff0c\u773c\u4e0b\u5d69\u5c71\u60c5\u51b5\uff0c\u8f83\u4e4b\u5e08\u5144\u6765\u6b64\u4e4b\u65f6\uff0c\u4e0d\u662f\u5e73\u9759\u4e86\u5f88\u591a\u5417\uff1f\u53ea\u6015\u4ed6\u4eec\u518d\u6709\u6570\u65e5\u6309\u5175\u4e0d\u52a8\uff0c\u5e08\u5144\u5c31\u4f1a\u56de\u8f6c\u8386\u7530\u7684\u4e86\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1b\u201c\u8fd9\u5012\u53ef\u80fd\u2026\u2026\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u5e08\u5144\u660e\u767d\u4fbf\u597d\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u8fd9\u65f6\u5ffd\u7136\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u90a3\u8f9b\u67cf\u516c\u73b0\u5728\u4f55\u5904\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5c31\u5728\u5c11\u6797\u5bfa\u4e2d\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u8001\u673d\u8be5\u4e0d\u8be5\u53bb\u89c1\u89c1\u4ed6\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u5f97\u5fc3\u4e2d\u597d\u7b11\uff0c\u6697\u9053\uff1a\u201c\u53bb\u4e0d\u53bb\u89c1\u4ed6\uff0c\u600e\u5730\u8981\u95ee\u6211\u5462\uff1f\u201d \u3000\u3000\u4f46\u4ed6\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u90a3\u8f9b\u8001\u65e2\u7136\u5df2\u5c06\u4ee4\u65d7\u4ea4\u7ed9\u4e86\u6768\u51b2\uff0c\u4f60\u8001\u53c8\u4f55\u5fc5\u518d\u6015\u89c1\u4ed6\u5462\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u90a3\u8f9b\u8001\u602a\u4e0d\u4f1a\u9a97\u4eba\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u795d\u8001\uff0c\u665a\u8f88\u731c\u60f3\uff0c\u90a3\u8f9b\u8001\u4e0d\u50cf\u662f\u8bf4\u8c0e\u7684\u4eba\uff0c\u4f60\u8001\u53ef\u4ee5\u4e0d\u5fc5\u6302\u5728\u5fc3\u4e0a\u4e86\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u95fb\u8a00\uff0c\u4f9d\u7136\u6c89\u541f\u4e86\u597d\u4e00\u9635\uff0c\u8fd9\u624d\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4e11\u5ab3\u5987\u4e5f\u5f97\u89c1\u516c\u5a46\uff0c\u8001\u673d\u8eb2\u907f\u8fd9\u8f9b\u8001\u602a\u5df2\u7136\u8eb2\u4e86\u6570\u5341\u5e74\uff0c\u5b9e\u5728\u662f\u8eb2\u817b\u4e86\uff0c\u7eb5\u7136\u4ed6\u662f\u9a97\u4eba\uff0c\u8001\u673d\u4e5f\u4e0d\u5728\u4e4e\u4e86\uff01\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u4fbf\u5411\u95e8\u53e3\u884c\u53bb\u3002 \u3000\u3000\u5165\u4e91\u5927\u5e08\u8fd9\u65f6\u5ffd\u7136\u5408\u5341\u9053\uff1a\u201c\u8001\u65bd\u4e3b\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u8d70\u5230\u95e8\u5916\uff0c\u95fb\u80b2\u8f6c\u8eab\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u8001\u65bd\u4e3b\uff0c\u5173\u4e8e\u5c11\u6797\u4e0b\u9662\u4e4b\u4e8b\uff0c\u4f60\u8001\u8fd8\u6709\u4ec0\u4e48\u6307\u70b9\u6ca1\u6709\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u6ca1\u6709\u4e86\uff01\u4e0d\u8fc7\uff0c\u544a\u8bc9\u4f60\uff0c\u5728\u771f\u50cf\u672a\u660e\u4e4b\u524d\uff0c\u4f60\u4eec\u6700\u597d\u83ab\u8981\u8f7b\u4e3e\u5984\u52a8\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u8001\u8872\u53d7\u6559\u4e86\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u8fd9\u65f6\u63a5\u9053\uff1a\u201c\u8001\u65bd\u4e3b\uff0c\u90a3\u8bf4\u51fa\u8386\u7530\u4e0b\u9662\u4e4b\u4e8b\u7684\u4eba\uff0c\u73b0\u5728\u4f55\u5904\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u592a\u5ba4\u5cf0\u5934\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u591a\u8c22\u8001\u65bd\u4e3b\u76f8\u544a\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u7b11\u9053\uff1a\u201c\u4e0d\u7528\u4e86\uff01\u201d\u8f6c\u8eab\u5411\u697c\u4e0b\u884c\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u95ea\u8eab\uff0c\u4e5f\u8ddf\u5728\u4ed6\u8eab\u540e\u884c\u53bb\uff0c\u4f46\u4ed6\u521a\u521a\u8e0f\u51fa\u95e8\u53e3\uff0c\u53ea\u89c1\u795d\u516c\u660e\u5927\u6b65\u8d70\u4e86\u56de\u6765\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u5446\u9053\uff1a\u201c\u795d\u8001\uff0c\u60a8\u600e\u5730\u53c8\u56de\u6765\u4e86\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u7b11\u9053\uff1a\u201c\u8001\u592b\u5fd8\u8bb0\u4e00\u4ef6\u4e8b\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u6709\u4e9b\u4e0d\u89e3\uff0c\u4ec0\u4e48\u4e8b\u4f7f\u4ed6\u53bb\u800c\u590d\u8f6c\uff0c\u6b63\u60f3\u8be2\u95ee\uff0c\u53ea\u89c1\u795d\u516c\u660e\u5df2\u7136\u5411\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u548c\u5c1a\uff0c\u4f60\u53ef\u662f\u60f3\u53bb\u592a\u5ba4\u5cf0\u5934\u627e\u4eba\u5417\uff1f\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u662f\u554a\uff0c\u8001\u8872\u6b63\u60f3\u53bb\u770b\u770b\u90a3\u4eba\uff0c\u6253\u542c\u4e00\u4e0b\u8386\u7530\u4e0b\u9662\u4e4b\u4e8b\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u542c\u5230\u65b9\u8001\u5f1f\u8bf4\uff0c\u90a3\u4eba\u5df2\u7ecf\u88ab\u4eba\u6740\u6b7b\u4e86\u554a\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u5408\u5341\u9053\uff1a\u201c\u8001\u65bd\u4e3b\u91cd\u4e0a\u7ecf\u697c\uff0c\u5c31\u662f\u544a\u8d2b\u50e7\u8fd9\u4e00\u4ef6\u4e8b\u5417\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f46\u8001\u592b\u8981\u4f60\u660e\u767d\uff0c\u90a3\u5cf0\u9876\u4e4b\u4eba\uff0c\u4e0d\u662f\u6b7b\u5728\u8001\u592b\u624b\u4e0b\uff0c\u56e0\u6b64\uff0c\u4f60\u5982\u524d\u53bb\uff0c\u53ef\u5f97\u5c0f\u5fc3\uff0c\u90a3\u513f\u6709\u9ad8\u624b\uff0c\u4f60\u53ef\u83ab\u8981\u5403\u4e86\u4e8f\uff0c\u53cd\u602a\u8001\u592b\u4e8b\u5148\u6ca1\u6709\u8bf4\u660e\u554a\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u591a\u5f80\u8001\u65bd\u4e3b\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u7b11\u9053\uff1a\u201c\u542c\u8fd9\u5927\u5e08\u8fd9\u7b49\u53e3\u6c14\uff0c\u90a3\u662f\u8fd8\u60f3\u53bb\u81f3\u5d69\u5c71\u592a\u5ba4\u5cf0\u5934\u4e86\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u8d2b\u50e7\u6b63\u6709\u6b64\u610f\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u548c\u5c1a\uff0c\u4f60\u5982\u679c\u8981\u53bb\uff0c\u8001\u592b\u5e76\u4e0d\u62e6\u963b\uff0c\u4f46\u6709\u4e00\u6869\uff0c\u4f60\u53ef\u8981\u8bb0\u7262\u3002\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u8d2b\u50e7\u6d17\u8033\u606d\u542c\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u83ab\u8981\u5355\u72ec\u524d\u53bb\uff0c\u4ee5\u514d\u4e2d\u4e86\u6076\u5b54\u660e\u7684\u4ec0\u4e48\u5bb3\u4eba\u624b\u6bb5\uff01\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u8d2b\u50e7\u8bb0\u4e0b\u4e86\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u8fd9\u624d\u7b11\u4e86\u4e00\u7b11\uff0c\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u54b1\u4eec\u53bb\u770b\u8f9b\u8001\u602a\u53bb\u3002\u201d\u4e24\u4eba\u5927\u6b65\u4e0b\u4e86\u7ecf\u697c\u3002 \u3000\u3000\u5165\u4e91\u5927\u5e08\u548c\u5165\u5723\u5927\u5e08\uff0c\u968f\u5728\u4e24\u4eba\u8eab\u540e\uff0c\u4e5f\u4e0b\u4e86\u7ecf\u697c\u3002 \u3000\u3000\u4e00\u884c\u4eba\u5f88\u5feb\u7684\u8f6c\u5230\u524d\u9662\u5ba2\u5802\uff0c\u53ea\u89c1\u90a3\u5ba2\u5802\u4e4b\u4e2d\uff0c\u7adf\u7136\u662f\u9ad8\u670b\u6ee1\u5ea7\uff0c\u5750\u4e86\u4e00\u5c4b\u5b50\u7684\u4eba\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u548c\u795d\u516c\u660e\u5927\u6b65\u8fdb\u4e86\u5ba2\u5802\uff0c\u4e0d\u7981\u5927\u51fa\u5728\u5ea7\u8bf8\u4eba\u7684\u610f\u6599\uff0c\u5b8b\u5b5a\u7b2c\u4e00\u4e2a\u5fcd\u4e0d\u4f4f\u5931\u58f0\u9053\uff1a \u3000\u3000\u201c\u795d\u5144\uff0c\u4f60\u2026\u2026\u600e\u4e48\u548c\u65b9\u8001\u5f1f\u78b0\u5728\u4e00\u8d77\u4e86\uff1f\u201d \u3000\u3000\u90a3\u8f9b\u67cf\u516c\u5374\u662f\u95ea\u8eab\u800c\u8d77\uff0c\u76f4\u5411\u795d\u516c\u660e\u8d70\u4e86\u8fc7\u6765\uff0c\u5927\u58f0\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u600e\u7684\u53d8\u6210\u8fd9\u4e2a\u602a\u6837\u5b50\u4e86\uff1f\u602a\u4e0d\u5f97\u90a3\u5929\u5728\u592a\u5ba4\u5cf0\u5934\uff0c\u53eb\u6211\u90fd\u8ba4\u4f60\u4e0d\u51fa\u6765\u5566\u2026\u2026\u201d \u3000\u3000\u795d\u516c\u660e\u8fde\u5fd9\u62b1\u62f3\u9053\uff1a\u201c\u8f9b\u5144\uff0c\u54b1\u4eec\u90fd\u8001\u4e86\uff0c\u81ea\u7136\u4e0d\u6bd4\u90a3\u4e8c\u5341\u5e74\u524d\u957f\u7684\u82f1\u4fca\u597d\u770b\u4e86\uff01\u201d \u3000\u3000\u4ed6\u8bdd\u97f3\u4e00\u843d\uff0c\u5b89\u5c0f\u840d\u542c\u5f97\u6251\u54e7\u4e00\u58f0\uff0c\u7b11\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8f9b\u67cf\u516c\u95fb\u8a00\uff0c\u4f3c\u4e5f\u6709\u4e9b\u5fcd\u4fca\u4e0d\u7981\uff0c\u7b11\u9053\uff1a\u201c\u795d\u8001\u5f1f\u4f60\u8fd9\u8bdd\u8bf4\u5f97\u4e0d\u9519\uff0c\u5e74\u7eaa\u5927\u4e86\uff0c\u81ea\u7136\u6ca1\u6709\u5f53\u5e74\u82f1\u4fca\uff0c\u53ea\u662f\uff0c\u636e\u5144\u5f1f\u6240\u77e5\uff0c\u4f60\u8001\u5f1f\u5e74\u8f7b\u7684\u65f6\u5019\uff0c\u4e5f\u4e0d\u600e\u4e48\u597d\u770b\u554a\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u7b11\u9053\uff1a\u201c\u4e11\u4eba\u5fc3\u5584\uff0c\u8f9b\u5144\u5e94\u662f\u77e5\u9053\u7684\u4e86\uff01\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u770b\u4e86\u970d\u9e23\u98ce\u4e00\u773c\u9053\uff1a\u201c\u8fd9\u4f4d\u53ef\u662f\u4e10\u5e2e\u7684\u970d\u5e2e\u4e3b\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u8d77\u8eab\u62b1\u62f3\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u665a\u751f\u6b63\u662f\u970d\u9e23\u98ce\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u7b11\u9053\uff1a\u201c\u8001\u673d\u66fe\u7ecf\u5192\u5145\u8d35\u5e2e\u9ec4\u957f\u8001\u4e00\u6b21\uff0c\u5c1a\u671b\u5e2e\u4e3b\u83ab\u8981\u89c1\u602a\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u5728\u4ed6\u4eec\u9762\u524d\uff0c\u5374\u662f\u53ea\u6709\u665a\u8f88\u81ea\u5c45\u7684\u8eab\u4efd\uff0c\u95fb\u8a00\u7b11\u9053\uff1a\u201c\u795d\u8001\u597d\u8bf4\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u8f6c\u8eab\u62db\u547c\u4e86\u795d\u516c\u660e\u5750\u5b9a\uff0c\u8fd9\u65f6\uff0c\u5c11\u6797\u638c\u95e8\u5165\u4e91\u5927\u5e08\u548c\u5165\u5723\u5927\u5e08\uff0c\u4e5f\u8fdb\u4e86\u5ba2\u5802\u3002 \u3000\u3000\u5927\u4f19\u513f\u5206\u5bbe\u4e3b\u5c31\u5ea7\u4ee5\u540e\uff0c\u5bfa\u4e2d\u50e7\u4fa3\u5df2\u7136\u9001\u4e0a\u9999\u8317\u3002 \u3000 \u7b2c\u4e09\u5341\u4e09\u7ae0\u3000\u8fd1\u4e61\u60c5\u602f \u3000 \u3000\u3000\u795d\u516c\u660e\u76ee\u5149\u5728\u4f17\u4eba\u8eab\u4e0a\u4e00\u8f6c\uff0c\u5374\u5411\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u8f9b\u5144\uff0c\u521a\u624d\u8001\u5f1f\u66fe\u8bf4\uff0c\u90a3\u5d69\u5c71\u592a\u5ba4\u5cf0\u5934\uff0c\u4f60\u5df2\u7ecf\u53bb\u8fc7\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u53bb\u8fc7\u4e86\uff01\u201d\u4ed6\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u8001\u592b\u5230\u4e86\u90a3\u5cf0\u5934\u4e4b\u540e\uff0c\u5374\u53d1\u73b0\u4e86\u4e00\u5177\u5c38\u4f53\uff0c\u795d\u5144\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u65b9\u8001\u5f1f\u8bf4\u4e86\uff01\u4e0d\u8fc7\uff0c\u90a3\u4eba\u867d\u7136\u662f\u6b7b\u5728\u8d64\u7130\u638c\u4e4b\u4e0b\uff0c\u5374\u975e\u5144\u5f1f\u4e0b\u7684\u6bd2\u624b\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u8001\u592b\u4e5f\u77e5\u9053\u4e0d\u662f\u4f60\u4e0b\u7684\u6bd2\u624b\uff01\u201d \u3000\u3000\u4ed6\u7b11\u4e86\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u4ee5\u90a3\u4eba\u6240\u53d7\u4f24\u52bf\u800c\u8a00\uff0c\u5982\u662f\u4f60\u8001\u5f1f\u51fa\u624b\uff0c\u90a3\u4e5f\u672a\u514d\u53eb\u4eba\u5927\u5927\u5931\u671b\u4e86\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u5df2\u7136\u63a5\u9053\uff1a\u201c\u8f9b\u5144\u6b64\u8bdd\u4f55\u6307\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4e09\u5341\u5e74\u6765\uff0c\u4f60\u8001\u5f1f\u603b\u4e0d\u4f1a\u653e\u4e0b\u4e86\u6b66\u529f\u672a\u7ec3\u5427\uff01\u8001\u592b\u67e5\u770b\u90a3\u4eba\u4f24\u52bf\uff0c\u53d1\u73b0\u4ed6\u81f4\u6b7b\u7684\u638c\u529b\uff0c\u6bd4\u4f60\u4e09\u5341\u5e74\u524d\u7684\u529f\u592b\uff0c\u8fd8\u8981\u5dee\u4e0a\u4e00\u7b79\u554a\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u5ffd\u5730\u5927\u7b11\u9053\uff1a\u201c\u8db3\u89c1\u8f9b\u5144\u679c\u7136\u4e0d\u540c\u5bfb\u5e38\u2026\u2026\u201d \u3000\u3000\u8f9b\u67cf\u516c\u5374\u662f\u4e24\u773c\u4e00\u7ffb\u9053\uff1a\u201c\u4ec0\u4e48\u4e0d\u540c\u5bfb\u5e38\uff1f\u8001\u673d\u4e0d\u8fc7\u662f\u6309\u60c5\u63a8\u7406\uff0c\u4efb\u4f55\u4eba\u4e5f\u77a7\u5f97\u51fa\u6765\u4e86\uff01\u201d \u3000\u3000\u4ed6\u8bdd\u97f3\u521a\u843d\uff0c\u5b8b\u5b5a\u5df2\u7136\u63a5\u9053\uff1a\u201c\u795d\u5144\uff0c\u6709\u4e00\u4ef6\u4e8b\u53eb\u8001\u5077\u513f\u5341\u5206\u4e0d\u89e3\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u90a3\u7559\u5728\u592a\u5ba4\u5cf0\u5934\u7684\u5c38\u4f53\uff0c\u600e\u5730\u4f1a\u7a7f\u4e86\u5144\u5f1f\u4e3a\u4f60\u4e70\u7684\u8863\u886b\u5462\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u5417\uff1f\u90a3\u662f\u8001\u592b\u81ea\u5df1\u66ff\u4ed6\u7a7f\u4e0a\u8eab\u53bb\u7684\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u4e00\u6014\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u4f60\u600e\u5730\u81ea\u5df1\u4e0d\u7a7f\u5462\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u957f\u957f\u4e00\u53f9\u9053\uff1a\u201c\u8001\u592b\u4e0d\u662f\u4e0d\u7a7f\uff0c\u53ea\u56e0\u8001\u592b\u5982\u662f\u7a7f\u4e0a\u90a3\u4e00\u5957\u8863\u886b\uff0c\u6b64\u523b\u53ef\u80fd\u5c31\u4e0d\u80fd\u5728\u8fd9\u513f\u4e0e\u4f60\u4eec\u8c08\u7b11\u98ce\u751f\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u90a3\u53c8\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u906d\u4e86\u4ed6\u4eba\u6697\u7b97\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u7b11\u9053\uff1a\u201c\u8001\u592b\u660e\u767d\u4e86\uff0c\u60f3\u5fc5\u90a3\u9057\u5c38\u592a\u5ba4\u5cf0\u5934\u4e4b\u4eba\uff0c\u4e43\u662f\u4f5c\u4e86\u4f60\u8001\u5f1f\u7684\u66ff\u6b7b\u9b3c\u4e86\u5417\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u5144\u5f1f\u4e3a\u6b64\uff0c\u6b63\u662f\u6709\u4e9b\u4e0d\u5b89\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u795d\u5144\uff0c\u8fd9\u4e2d\u95f4\u5374\u662f\u4e3a\u4e86\u4f55\u6545\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u8fd9\u4e8b\u8bf4\u6765\u4e5f\u662f\u6709\u4e9b\u51d1\u5de7\uff0c\u5144\u5f1f\u82e5\u975e\u542c\u5230\u90a3\u4eba\u548c\u62b5\u6b64\u7684\u540c\u4f34\u5077\u5077\u8c08\u8bba\uff0c\u90a3\u8001\u592b\u53ef\u771f\u8981\u5403\u4e86\u5927\u4e8f\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u5960\u975e\u90a3\u6076\u5b54\u660e\u7adf\u662f\u8981\u5bf9\u4f60\u4e0b\u624b\u5417\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d\u4ed6\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u8001\u592b\u5728\u542c\u5230\u90a3\u4eba\u548c\u4ed6\u540c\u4f34\u7684\u8a00\u8c08\u4ee5\u540e\uff0c\u5bb9\u90a3\u53e6\u5916\u4e00\u4eba\uff0c\u4e0b\u5c71\u53bb\u5411\u6076\u5b54\u660e\u62a5\u4fe1\uff0c\u8001\u592b\u4fbf\u5c06\u7559\u5728\u5c71\u4e0a\u8fd9\u4eba\u64d2\u4e0b\uff0c\u4e0d\u4f46\u95ee\u51fa\u4e86\u6076\u5b54\u660e\u8c0b\u5bb3\u8001\u592b\u4e4b\u5fc3\uff0c\u540c\u65f6\u4e5f\u83b7\u6089\u90a3\u8386\u7530\u5c11\u6797\u7985\u9662\uff0c\u5df2\u7ecf\u843d\u5165\u4e86\u4e94\u9b54\u7684\u9b54\u638c\u4e4b\u4e2d\uff0c\u6210\u4e86\u4ed6\u4eec\u5728\u4e1c\u5357\u4e00\u5e26\u53d1\u53f7\u65bd\u4ee4\u7684\u6240\u5728\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u6709\u8fd9\u7b49\u4e8b\uff1f\u201d \u3000\u3000\u4ed6\u4e0d\u7531\u81ea\u4e3b\u7684\u770b\u4e86\u5165\u5723\u5927\u5e08\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5927\u5e08\u53ef\u5df2\u77e5\u9053\u4e86\uff1f\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u8001\u65bd\u4e3b\u5df2\u5411\u8d2b\u50e7\u8bf4\u8fc7\u4e86\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u957f\u957f\u7684\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u63a5\u9053\uff1a\u201c\u8001\u592b\u5728\u95ee\u660e\u4e86\u8fd9\u4e9b\u4e4b\u540e\uff0c\u4fbf\u8131\u4e0b\u7a7f\u5728\u8eab\u4e0a\u8863\u886b\uff0c\u6362\u5230\u90a3\u4eba\u8eab\u4e0a\uff0c\u5e76\u4e14\u70b9\u4e86\u4ed6\u7684\u7a74\u9053\uff0c\u5c06\u4ed6\u653e\u5728\u77f3\u6d1e\u4e4b\u4e2d\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u542c\u5f97\u5927\u611f\u5174\u8da3\uff0c\u7b11\u9053\uff1a\u201c\u795d\u8001\uff0c\u90a3\u524d\u6765\u4f24\u4eba\u7684\u53ef\u662f\u6076\u5b54\u660e\u81ea\u5df1\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u4e0d\u662f\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4e0d\u662f\u6076\u5b54\u660e\u81ea\u5df1\uff0c\u53c8\u6709\u4ec0\u4e48\u4eba\u80fd\u591f\u65bd\u5c55\u8d64\u7130\u638c\u5462\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u5144\u5f1f\u8bf4\u51fa\uff0c\u53ea\u6015\u8f9b\u5144\u4e0d\u4fe1\uff0c\u90a3\u524d\u6765\u4e0b\u624b\u4e4b\u4eba\uff0c\u7adf\u662f\u6211\u90a3\u5e08\u4f84\u6768\u51b2\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u542c\u5f97\u51e0\u4e4e\u8df3\u4e86\u8d77\u6765\uff0c\u5927\u58f0\u9053\uff1a\u201c\u90a3\u600e\u4e48\u53ef\u80fd\u5462\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u5144\u5f1f\u4eb2\u773c\u89c1\u4ed6\u5c06\u90a3\u4eba\u4e00\u638c\u9707\u6bd9\uff0c\u81ea\u7136\u662f\u4e0d\u4f1a\u9519\u7684\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u5927\u6012\u9053\uff1a\u201c\u8fd9\u6768\u51b2\u600e\u6562\u5982\u6b64\u9006\u4f26\u72af\u4e0a\uff1f\u8001\u592b\u53ef\u7b97\u662f\u778e\u4e86\u773c\u4e86\u2026\u2026\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u8f9b\u5144\uff0c\u8fd9\u4e8b\u4e0d\u4f46\u4f60\u4e0d\u6562\u76f8\u4fe1\uff0c\u8fde\u5144\u5f1f\u4e5f\u5403\u60ca\u4e0d\u5c0f\u5462\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u8f9b\u5144\uff0c\u8fd9\u4e8b\u53ef\u602a\u4e0d\u5f97\u4f60\uff0c\u90a3\u6768\u51b2\u7684\u5e73\u65e5\u4e3a\u4eba\uff0c\u5fe0\u539a\u8c28\u614e\uff0c\u4e00\u70b9\u4e5f\u77a7\u4e0d\u51fa\u4ec0\u4e48\u5978\u8bc8\uff0c\u82e5\u975e\u8fd9\u4e00\u4f1a\u673a\u7f18\u51d1\u5de7\uff0c\u88ab\u5144\u5f1f\u8bc6\u7834\uff0c\u53ea\u6015\u6c38\u8fdc\u4e5f\u4e0d\u4f1a\u6709\u4eba\u770b\u5f97\u51fa\u4ed6\u771f\u6b63\u7684\u4e3a\u4eba\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4f60\u53ef\u66fe\u5c06\u4ed6\u62ff\u4e0b\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u6ca1\u6709\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u53d8\u8272\u9053\uff1a\u201c\u50cf\u8fd9\u7b49\u72fc\u5fc3\u72d7\u80ba\u4e4b\u4eba\uff0c\u4f60\u4e3a\u4f55\u8fd8\u5bb9\u4ed6\u6d3b\u5728\u4e16\u4e0a\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u8f9b\u8001\u83ab\u8981\u6fc0\u52a8\uff0c\u5144\u5f1f\u4e0d\u5c06\u4ed6\u62ff\u4e0b\uff0c\u4e43\u662f\u522b\u6709\u539f\u56e0\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4ec0\u4e48\u539f\u56e0\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u5144\u5f1f\u60f3\u67e5\u660e\u4ed6\u662f\u5426\u548c\u90a3\u6076\u5b54\u660e\u6709\u7740\u7275\u8fde\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u53ea\u8981\u5c06\u4ed6\u62ff\u4f4f\uff0c\u8fd8\u4e0d\u662f\u4e00\u95ee\u5c31\u660e\u767d\u4e86\u5417\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u8f9b\u5144\uff0c\u8bdd\u53ef\u4e0d\u80fd\u8fd9\u4e48\u8bf4\uff0c\u5018\u662f\u4ed6\u5b81\u53ef\u4e00\u6b7b\uff0c\u4e5f\u4e0d\u613f\u8bf4\u51fa\u6765\u5462\uff1f\u662f\u4ee5\uff0c\u5144\u5f1f\u773c\u770b\u4ed6\u79bb\u53bb\uff0c\u5374\u672a\u60ca\u52a8\u4e8e\u4ed6\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u4f60\u8fd9\u6869\u4e8b\u505a\u5f97\u53ef\u4e0d\u5927\u9ad8\u660e\u5462\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u600e\u89c1\u5f97\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4f60\u653e\u4ed6\u79bb\u53bb\u4ee5\u540e\uff0c\u5c06\u6765\u5c31\u662f\u5c06\u4ed6\u6355\u4f4f\uff0c\u53c8\u600e\u80fd\u53eb\u4ed6\u627f\u8ba4\u66fe\u5728\u592a\u5ba4\u5cf0\u5934\uff0c\u6709\u8fc7\u5bb3\u4f60\u4e4b\u5fc3\u5462\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u5bb9\u6613\uff0c\u5144\u5f1f\u65e2\u7136\u6709\u4e86\u5c06\u4ed6\u62ff\u4e0b\u4e4b\u5fc3\uff0c\u5230\u65f6\u4e5f\u4e0d\u6015\u4ed6\u4e0d\u627f\u8ba4\u4e86\u2026\u2026\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5144\u5f1f\u8fd9\u65f6\u4e0d\u5c06\u4ed6\u62ff\u4f4f\uff0c\u8bf4\u7a7f\u4e86\u8fd8\u6709\u522b\u79cd\u7528\u5fc3\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4ec0\u4e48\u7528\u5fc3\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u653e\u957f\u7ebf\uff0c\u9493\u5927\u9c7c\u554a\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u65e2\u7136\u5c06\u4ed6\u653e\u8d70\uff0c\u8001\u592b\u591a\u8bf4\uff0c\u90a3\u4e5f\u662f\u6ca1\u6709\u7528\u5904\u7684\u4e86\uff01\u4e0d\u8fc7\u2026\u2026\u201d\u4ed6\u5ffd\u7136\u6447\u4e86\u6447\u5934\uff0c\u63a5\u9053\uff1a\u201c\u6768\u51b2\u9664\u975e\u4e0d\u518d\u88ab\u8001\u592b\u78b0\u4e0a\uff0c\u53ea\u8981\u4ed6\u88ab\u8001\u592b\u78b0\u4e0a\uff0c\u795d\u5144\u5f1f\uff0c\u5230\u65f6\u5019\u8981\u600e\u751f\u5904\u7f6e\u4e8e\u4ed6\uff0c\u5c1a\u671b\u795d\u8001\u5f1f\u83ab\u8981\u8fc7\u95ee\u4e86\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u7b11\u9053\uff1a\u201c\u539f\u6765\u8f9b\u5144\u662f\u8981\u5c06\u4ed6\u62ff\u4e0b\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u7b11\u9053\uff1a\u201c\u5144\u5f1f\u4e0d\u662f\u8981\u5c06\u4ed6\u62ff\u4e0b\uff0c\u800c\u662f\u8981\u95ee\u95ee\u4ed6\uff0c\u4e3a\u4f55\u6627\u4e86\u826f\u5fc3\uff0c\u4f5c\u90a3\u7b49\u4f24\u5929\u5bb3\u7406\u4e4b\u4e8b\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u7b11\u9053\uff1a\u201c\u8f9b\u5144\uff0c\u6709\u8bb8\u591a\u4e8b\uff0c\u53ef\u4e0d\u662f\u4f60\u6211\u6240\u80fd\u9884\u77e5\uff0c\u8fd9\u6768\u51b2\u7684\u4e3a\u4eba\uff0c\u7adf\u7136\u5982\u6b64\u4e4b\u574f\uff0c\u90a3\u81ea\u7136\u662f\u5927\u51fa\u6211\u7b49\u610f\u6599\uff0c\u4e0d\u8fc7\u6b64\u4e8b\u5c1a\u5728\u5f00\u7aef\uff0c\u54b1\u4eec\u7528\u4e0d\u7740\u90a3\u7b49\u8d39\u5fc3\u4e86\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u8bdd\u7adf\u521a\u843d\uff0c\u65b9\u96ea\u5b9c\u5fcd\u4e0d\u4f4f\u63d2\u53e3\u9053\uff1a\u201c\u795d\u8001\uff0c\u50cf\u6768\u51b2\u8fd9\u7b49\u4eba\uff0c\u4f60\u8001\u600e\u53ef\u5bbd\u6055\u4e8e\u4ed6\u5462\uff1f\u8f9b\u8001\u8bf4\u5f97\u4e0d\u9519\uff0c\u5728\u90a3\u592a\u5ba4\u5cf0\u5934\u4f60\u2026\u2026\u679c\u771f\u4e0d\u5e94\u5bb9\u4ed6\u79bb\u53bb\u554a\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u4e5f\u8ba4\u4e3a\u8001\u592b\u9517\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u665a\u8f88\u53ea\u662f\u8ba4\u4e3a\uff0c\u50cf\u6768\u51b2\u8fd9\u7b49\u5fc3\u5730\u4e4b\u4eba\uff0c\u59d4\u5b9e\u4e0d\u5e94\u653e\u4ed6\u8131\u8d70\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u8001\u5f1f\u8bf4\u5f97\u4e5f\u6709\u9053\u7406\uff0c\u4f46\u8001\u673d\u521a\u624d\u5df2\u7136\u8bf4\u8fc7\uff0c\u8fd9\u6768\u51b2\u4e4b\u53bb\uff0c\u5bf9\u8001\u592b\u5e76\u65e0\u574f\u5904\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u90a3\u4e5f\u4e0d\u4f1a\u6709\u591a\u5c11\u597d\u5904\u5427\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u8fd9\u4e2a\u5417\uff1f\u5f88\u96be\u8bf4\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u795d\u8001\u4f3c\u662f\u5f88\u6709\u628a\u63e1\u5417\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u7b11\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u628a\u63e1\u7684\u95ee\u9898\uff0c\u8001\u592b\u53ea\u662f\u8ba4\u4e3a\uff0c\u8fd9\u4e8b\u82e5\u80fd\u771f\u5982\u8001\u592b\u6240\u6599\uff0c\u5219\u4e0b\u4e0a\u8fd9\u4e00\u7740\u68cb\uff0c\u5c06\u6765\u672a\u5fc5\u6ca1\u6709\u5927\u7528\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4ec0\u4e48\u7528\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u63a2\u660e\u90a3\u6c5f\u5357\u7eff\u6797\uff0c\u662f\u5426\u4e5f\u88ab\u4e94\u9b54\u6240\u63a7\u5236\uff0c\u6210\u4e86\u4e94\u9b54\u7684\u5e2e\u51f6\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u5934\u9053\uff1a\u201c\u8001\u524d\u8f88\u8fd9\u8d4c\u6ce8\u592a\u5927\u4e86\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u7b11\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4f60\u8001\u51e0\u4e4e\u8d54\u4e0a\u4e00\u6761\u81ea\u5df1\u7684\u8001\u547d\u554a\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u7b11\u9053\uff1a\u201c\u90a3\u4e5f\u6ca1\u6709\u4ec0\u4e48\u5927\u4e0d\u4e86\uff0c\u4f55\u51b5\uff0c\u4ed6\u6839\u672c\u8981\u4e0d\u4e86\u8001\u592b\u7684\u547d\u5462\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5982\u662f\u90a3\u6768\u51b2\u5df2\u7136\u88ab\u4e94\u9b54\u4e3b\u6536\u5f52\u4e86\u95e8\u4e0b\uff0c\u6c5f\u5357\u4e03\u7701\u7684\u7eff\u6797\u4eba\u7269\uff0c\u5c82\u4e0d\u90fd\u6210\u4e86\u4e94\u9b54\u7684\u5f1f\u5b50\u4e86\u5417\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u4e0d\u9519\uff01\u90a3\u6b63\u662f\u8001\u592b\u60f3\u67e5\u660e\u767d\u7684\u4e8b\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u8fd9\u65f6\u5927\u58f0\u9053\uff1a\u201c\u65b9\u8001\u5f1f\u83ab\u8981\u518d\u8bf4\u8fd9\u4e9b\u597d\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u8f9b\u8001\u6709\u4f55\u9ad8\u89c1\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u90a3\u6768\u51b2\u4e0d\u7ba1\u600e\u6837\uff0c\u8001\u592b\u603b\u6709\u5c06\u4ed6\u6293\u5230\u624b\u4e2d\u7684\u4e00\u5929\uff01\u8001\u5f1f\u4f3c\u662f\u4e0d\u7528\u518d\u8d39\u5fc3\u60f3\u4ed6\u7684\u4e8b\u4e86\uff01\u201c \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u65e2\u7136\u8f9b\u8001\u4e0d\u613f\u518d\u8c08\uff0c\u665a\u8f88\u81ea\u662f\u9075\u547d\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u76ee\u5149\u4e00\u8f6c\uff0c\u843d\u5728\u5b89\u5c0f\u840d\u8eab\u4e0a\u3002 \u3000\u3000\u5b8b\u5b5a\u5fc3\u4e2d\u4e00\u9707\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u8001\u602a\u4e00\u5f00\u53e3\uff0c\u51c6\u6ca1\u6709\u597d\u8bdd\u8bf4\uff0c\u522b\u8ba9\u4ed6\u5c06\u5b89\u5c0f\u840d\u7684\u5973\u513f\u8eab\u4efd\u53eb\u7834\uff0c\u90a3\u53ef\u5c31\u9ebb\u70e6\u4e86\uff01\u201d \u3000\u3000\u4e00\u5ff5\u53ca\u6b64\uff0c\u8fde\u5fd9\u7528\u4f20\u97f3\u5411\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u8f9b\u8001\uff0c\u5c11\u6797\u5bfa\u4e2d\u89c4\u77e9\u5f88\u5927\uff0c\u5411\u6765\u662f\u4e0d\u8bb8\u5802\u5ba2\u5165\u5185\uff0c\u4f60\u8001\u53ef\u5343\u4e07\u522b\u5c06\u8fd9\u4e2b\u5934\u7684\u8eab\u4efd\u63ed\u7a7f\u554a\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u672c\u5df2\u5f20\u53e3\uff0c\u542c\u5f97\u5b8b\u5b5a\u4f20\u97f3\uff0c\u7acb\u5373\u70b9\u4e86\u70b9\u5934\uff0c\u5374\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u65b9\u8001\u5f1f\uff0c\u4f60\u90a3\u90d1\u5927\u4f2f\u7684\u75c5\u60c5\uff0c\u6b64\u523b\u5b9a\u7136\u5927\u597d\u4e86\uff01\u201d \u3000\u3000\u4ed6\u672c\u662f\u4e3a\u4e86\u6539\u53e3\u8bf4\u8bdd\uff0c\u81ea\u7136\u662f\u4e0d\u66fe\u60f3\u5230\u8fd9\u53e5\u8bdd\u4f1a\u53eb\u65b9\u96ea\u5b9c\u542c\u5f97\u6709\u4e9b\u6ca1\u5934\u6ca1\u8111\u4e86\uff01\u662f\u4ee5\uff0c\u65b9\u96ea\u5b9c\u95fb\u8a00\u4e4b\u540e\uff0c\u679c\u7136\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8001\u524d\u8f88\u53ef\u662f\u8981\u8ddf\u4ed6\u8bb2\u8bdd\u5417\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u4e00\u6014\uff0c\u4ed6\u5ffd\u7136\u60f3\u8d77\uff0c\u81ea\u5df1\u65e2\u7136\u90a3\u7b49\u5411\u65b9\u96ea\u5b9c\uff0c\u603b\u5f97\u627e\u4e2a\u7406\u7531\u8bf4\u660e\u95ee\u51fa\u8fd9\u8bdd\u7684\u9053\u7406\u624d\u662f\uff0c\u5f53\u4e0b\u8fde\u5fd9\u63a5\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u8001\u592b\u60f3\u8d77\u4e00\u4ec0\u4e8b\u60c5\u6765\u4e86\uff0c\u4e5f\u8bb8\u90d1\u5927\u521a\u77e5\u9053\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u76b1\u7709\u9053\uff1a\u201c\u8001\u524d\u8f88\u8981\u53bb\u89c1\u90d1\u5927\u4f2f\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7ad9\u8d77\u8eab\u6765\uff0c\u8ddf\u5728\u8f9b\u67cf\u516c\u8eab\u540e\uff0c\u4e24\u4eba\u4fbf\u51fa\u4e86\u90a3\u5ba2\u5802\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u90a3\u8f9b\u67cf\u516c\u51fa\u4e86\u5ba2\u5802\uff0c\u5e76\u672a\u5411\u90d1\u5927\u521a\u4f11\u606f\u4e4b\u5904\u884c\u53bb\uff0c\u800c\u662f\u9886\u7740\u65b9\u96ea\u5b9c\uff0c\u5411\u90a3\u65b9\u4e08\u9759\u5ba4\u7684\u65b9\u4f4d\u884c\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8f9b\u8001\uff0c\u8fd9\u4e0d\u662f\u90d1\u5927\u4f2f\u6b47\u606f\u4e4b\u5904\u554a\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u54b1\u4eec\u4e0d\u7528\u53bb\u627e\u90d1\u5927\u521a\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u8001\u2026\u2026\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u8001\u592b\u4e0d\u8fc7\u662f\u627e\u4e0a\u4e00\u4e2a\u501f\u53e3\uff0c\u548c\u4f60\u51fa\u6765\u6563\u6563\u5fc3\u800c\u5df2\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u539f\u6765\u4f60\u8001\u4e0d\u662f\u8981\u53bb\u627e\u90d1\u5927\u4f2f\u5417\uff1f\u665a\u8f88\u5012\u662f\u4e0a\u5f53\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4f60\u4e0a\u4e86\u4ec0\u4e48\u5f53\uff0c\u8001\u592b\u4e5f\u6ca1\u6709\u9a97\u4f60\u4ec0\u4e48\u5440\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u56e0\u4e3a\u2026\u2026\u665a\u8f88\u767d\u803d\u4e86\u5fc3\u554a\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u5927\u7b11\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u591a\u52b3\u4e00\u70b9\u5fc3\uff0c\u90a3\u4e5f\u4e0d\u662f\u574f\u4e8b\uff0c\u8001\u5f1f\uff0c\u4f60\u5e72\u5417\u8fd9\u4e48\u4e00\u70b9\u5e74\u7eaa\uff0c\u5c31\u60f3\u5077\u61d2\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u665a\u8f88\u5012\u4e0d\u662f\u60f3\u5077\u61d2\uff0c\u53ea\u662f\u6709\u4e9b\u5947\u602a\u800c\u5df2\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4f60\u5947\u602a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4f60\u8001\u597d\u597d\u7684\u600e\u4f1a\u5ffd\u7136\u8981\u51fa\u6765\u6563\u6563\u5fc3\u5462\uff1f\u8fd9\u4e2d\u95f4\u5fc5\u6709\u9053\u7406\u7684\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u4ed6\u9053\uff1a\u201c\u539f\u56e0\u5f53\u7136\u6709\uff0c\u4e0d\u8fc7\u8001\u592b\u5374\u4e0d\u6253\u7b97\u8bf4\u51fa\u6765\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u56e0\u4e3a\u2026\u2026\u201d\u4ed6\u6c89\u541f\u4e00\u9635\uff0c\u5c1a\u672a\u8bf4\u8bdd\uff0c\u5ffd\u7136\u8138\u8272\u4e00\u53d8\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u8001\u592b\u7684\u539f\u56e0\u4e0d\u7528\u8bf4\uff0c\u4f60\u4e14\u7b49\u7740\u77a7\u5427\uff01\u201d \u3000\u3000\u53ea\u89c1\u4ed6\u53f3\u624b\u4e00\u626c\uff0c\u4e3e\u624b\u62cd\u51fa\u4e00\u638c\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u5446\u5730\u5411\u4ed6\u4e3e\u624b\u62ac\u7684\u65b9\u4f4d\u770b\u53bb\uff0c\u53ea\u542c\u5f97\u4e00\u58f0\u5389\u5578\u5165\u8033\uff0c\u4e00\u6761\u9ad8\u5927\u7684\u4eba\u5f71\uff0c\u51b2\u5929\u800c\u8d77\uff0c\u7728\u773c\u95f4\uff0c\u6d88\u901d\u5728\u5bfa\u5916\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u6014\u9053\uff1a\u201c\u8f9b\u8001\uff0c\u539f\u6765\u4f60\u8001\u53d1\u73b0\u6709\u4e86\u654c\u4eba\u6f5c\u5165\u5bfa\u5185\u5417\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u5fc3\u4e2d\u6697\u9053\u60ed\u6127\uff0c\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u53ef\u4e0d\uff1f\u53ea\u662f\u6ca1\u6709\u60f3\u5230\uff0c\u6765\u4eba\u6b66\u529f\uff0c\u5c45\u7136\u5341\u5206\u9ad8\u660e\uff0c\u88ab\u4ed6\u9041\u53bb\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u90a3\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u8001\u592b\u4e5f\u6ca1\u6709\u77a7\u5230\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u80fd\u5728\u8001\u592b\u638c\u529b\u4e4b\u4e0b\uff0c\u4e0d\u4f1a\u53d7\u4f24\uff0c\u53ea\u662f\u53d7\u60ca\u800c\u9041\uff0c\u8fd9\u4eba\u4e00\u8eab\u6b66\u529f\uff0c\u679c\u7136\u662f\u4e0d\u540c\u51e1\u54cd\u7684\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8f9b\u8001\uff0c\u54b1\u4eec\u8981\u4e0d\u8981\u8ffd\u51fa\u53bb\u770b\u770b\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u6447\u5934\u9053\uff1a\u201c\u4e0d\u7528\u4e86\uff01\u4ed6\u6b64\u65f6\u5df2\u7136\u53bb\u8fdc\u4e86\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u795d\u516c\u660e\u7b49\u4eba\uff0c\u4e5f\u51fa\u4e86\u5ba2\u5802\uff0c\u5411\u8fd9\u513f\u5954\u884c\u8fc7\u6765\u3002 \u3000\u3000\u5b8b\u5b5a\u4e00\u773c\u77a7\u89c1\u65b9\u96ea\u5b9c\u548c\u8f9b\u67cf\u516c\u7ad9\u5728\u4e00\u682a\u53e4\u67cf\u4e4b\u4e0b\uff0c\u987f\u65f6\u6536\u4f4f\u4e86\u75be\u884c\u4e4b\u52bf\uff0c\u7b11\u9053\uff1a\u201c\u8f9b\u8001\uff0c\u521a\u624d\u662f\u4ec0\u4e48\u4eba\u559d\u53eb\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u7b11\u9053\uff1a\u201c\u4e00\u540d\u5978\u7ec6\uff0c\u4f46\u5df2\u88ab\u8001\u592b\u5413\u8d70\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u4f60\u8001\u771f\u662f\u795e\u4eba\uff0c\u8033\u76ee\u4e4b\u654f\uff0c\u5f53\u771f\u53eb\u4eba\u656c\u4f69\u5f97\u5f88\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u7b11\u9053\uff1a\u201c\u8001\u6109\u513f\uff0c\u4f60\u522b\u635f\u4eba\uff0c\u5f53\u5fc3\u8001\u592b\u7ed9\u4f60\u597d\u770b\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u95fb\u8a00\uff0c\u9000\u4e86\u4e00\u6b65\uff0c\u7b11\u9053\uff1a\u201c\u4e0d\u6562\u2026\u2026\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u5165\u5723\u5927\u5e08\u548c\u5165\u4e91\u5927\u5e08\u968f\u5728\u795d\u516c\u660e\u8eab\u540e\uff0c\u4e5f\u8d76\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u8f9b\u67cf\u516c\u4e0d\u7b49\u4ed6\u4eec\u8be2\u95ee\uff0c\u4fbf\u5c06\u6709\u4eba\u9690\u5728\u5bf9\u9762\u7684\u6bbf\u5916\u7aa5\u4f3a\u4e4b\u4e8b\uff0c\u8bf4\u4e86\u4e00\u904d\u3002 \u3000\u3000\u5165\u4e91\u5927\u5e08\u5374\u662f\u6014\u4e86\u4e00\u6014\u9053\uff0c\u201c\u60f3\u4e0d\u5230\u5c11\u6797\u5bfa\u5185\uff0c\u7adf\u7136\u5982\u540c\u65e0\u4eba\u8bbe\u9632\u4e00\u822c\uff0c\u771f\u53eb\u8d2b\u50e7\u96be\u8fc7\u2026\u2026\u201d \u3000\u3000\u8f9b\u67cf\u516c\u7b11\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u4e0d\u662f\u8001\u592b\u6cc4\u4f60\u7684\u6c14\uff0c\u9002\u624d\u6765\u6b64\u4e4b\u4eba\uff0c\u6b66\u529f\u4e4b\u9ad8\uff0c\u53ea\u6015\u4e0d\u5728\u4f60\u638c\u95e8\u4e4b\u4e0b\u5462\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u90a3\u2026\u2026\u4f1a\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u53ea\u6015\u662f\u4e94\u5927\u9b54\u4e3b\u4e4b\u4e00\u4e86\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u957f\u957f\u4e00\u53f9\uff0c\u9053\uff1a\u201c\u8001\u65bd\u4e3b\uff0c\u8fd9\u4eba\u53ef\u662f\u5df2\u88ab\u4f60\u6240\u4f24\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u6447\u5934\u9053\uff1a\u201c\u6ca1\u6709\uff01\u8001\u592b\u4e00\u638c\uff0c\u53ea\u662f\u5c06\u4ed6\u60ca\u9000\u800c\u5df2\uff01\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u638c\u95e8\u4eba\u53ef\u5f97\u8d39\u5fc3\u4e00\u4e0b\uff0c\u8981\u8d35\u5bfa\u5b50\u5f1f\uff0c\u5f80\u540e\u773c\u775b\u7741\u5f97\u5927\u4e9b\u624d\u597d\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u8138\u4e00\u7ea2\uff0c\u4f46\u5374\u5408\u5341\u9053\uff1a\u201c\u8001\u65bd\u4e3b\u6559\u8bad\u5f97\u662f\uff0c\u8d2b\u50e7\u81ea\u5f53\u7559\u5fc3\u53ca\u6b64\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u8fd9\u65f6\u8d70\u5230\u65b9\u96ea\u5b9c\u8eab\u8fb9\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u65b9\u5927\u54e5\uff0c\u521a\u624d\u6765\u7684\u662f\u4ec0\u4e48\u4eba\uff1f\u6211\u4e5f\u6ca1\u6709\u77a7\u6e05\u695a\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u5934\u9053\uff1a\u201c\u90a3\u4eba\u53bb\u52bf\u6781\u5feb\uff0c\u662f\u4ee5\u6211\u8ddf\u8f9b\u8001\u672a\u66fe\u77a7\u6e05\u695a\u4ed6\u7684\u9762\u8c8c\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u90a3\u4eba\u6b66\u529f\u679c\u771f\u662f\u5f88\u5f3a\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u53ef\u4e0d\uff1f\u90a3\u4eba\u82e5\u662f\u6b66\u529f\u7a0d\u5dee\uff0c\u53ea\u6015\u65e9\u88ab\u8f9b\u8001\u4e00\u638c\u51fb\u6bd9\u5728\u6bbf\u89d2\u4e4b\u4e0b\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u5411\u90a3\u5165\u4e91\u5927\u5e08\u6325\u4e86\u6325\u624b\uff0c\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u8fd9\u513f\u6ca1\u6709\u4e8b\u4e86\uff01\u4f60\u53bb\u53ec\u96c6\u5bfa\u4e2d\u5f1f\u5b50\u544a\u8beb\u4ed6\u4eec\u4e00\u756a\uff0c\u8001\u592b\u548c\u795d\u5144\u7b49\u4eba\uff0c\u8fd9\u5c31\u56de\u8f6c\u5ba2\u5802\u6b47\u606f\u4e86\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u5408\u5341\u5e94\u662f\uff0c\u9886\u7740\u5165\u5723\u5e08\u5144\uff0c\u8f6c\u8eab\u884c\u53bb\u3002 \u3000\u3000\u8f9b\u67cf\u516c\u7b49\u4eba\uff0c\u4e5f\u5c31\u5f88\u5feb\u5730\u56de\u8f6c\u4e86\u5ba2\u5ba4\u3002 \u3000\u3000\u5750\u5b9a\u4ee5\u540e\uff0c\u8f9b\u67cf\u516c\u5ffd\u7136\u5411\u5b8b\u5b5a\u9053\uff1a\u201c\u8001\u5077\u513f\uff0c\u90a3\u4e94\u5927\u9b54\u4e3b\u4e4b\u4e2d\uff0c\u4ec0\u4e48\u4eba\u957f\u5f97\u8eab\u6750\u5341\u5206\u9ad8\u5927\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u5982\u8bba\u8eab\u6750\u9ad8\u5927\uff0c\u5b9a\u662f\u516b\u86ee\u9f13\u738b\u91d1\u957f\u5e9a\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u90a3\u9002\u624d\u4e4b\u4eba\uff0c\u5b9a\u662f\u90a3\u91d1\u957f\u5e9a\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u90a3\u4eba\u8eab\u6750\u5341\u5206\u7ec6\u9ad8\u5417\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u5f88\u9ad8\u5927\uff01\u4f46\u662f\u7626\u662f\u80d6\uff0c\u4e00\u65f6\u4e5f\u5206\u8fa8\u4e0d\u51fa\u6765\u3002\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u5982\u662f\u4e94\u5927\u9b54\u4e3b\uff0c\u90a3\u81ea\u7136\u53ea\u6709\u91d1\u957f\u5e9a\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u6c89\u541f\u9053\uff1a\u201c\u90a3\u91d1\u957f\u5e9a\u5982\u662f\u524d\u6765\uff0c\u53c8\u600e\u4f1a\u5982\u6b64\u5077\u5077\u6478\u6478\uff1f\u5b8b\u5144\uff0c\u53ea\u6015\u4e0d\u662f\u4ed6\u5427\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u4e0d\u662f\u4ed6\uff0c\u53c8\u80fd\u662f\u8c01\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u8fd9\u4e8b\u53ea\u6015\u8981\u6210\u7591\u6848\u4e86\uff01\u201d\u8bdd\u97f3\u4e00\u987f\uff0c\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u7528\u518d\u60f3\u4e86\uff01\u53cd\u6b63\u4ed6\u5982\u518d\u6765\uff0c\u8001\u592b\u5b9a\u8981\u5c06\u4ed6\u7559\u4e0b\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u662f\u554a\uff01\u53ea\u8981\u4ed6\u518d\u6765\uff0c\u90a3\u662f\u8d70\u4e0d\u4e86\u7684\u4e86\uff01\u201d \u3000\u3000\u8fd9\u65f6\u795d\u516c\u660e\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u8f9b\u5144\uff0c\u6709\u4e00\u4ef6\u4e8b\uff0c\u5012\u53eb\u5144\u5f1f\u751a\u4e3a\u4e0d\u89e3\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u9002\u624d\u8f9b\u5144\u5ffd\u7136\u8981\u53bb\u627e\u90a3\u90d1\u5927\u521a\uff0c\u83ab\u975e\u8f9b\u5144\u5750\u5728\u6b64\u5904\uff0c\u5c31\u53d1\u89c9\u4e86\u90a3\u8fb9\u6709\u4eba\u4fb5\u5165\u4e86\u5417\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u7591\u8fdf\u4e86\u4e00\u4e0b\uff0c\u8fd9\u624d\u5927\u7b11\u9053\uff1a\u201c\u795d\u5144\uff0c\u4e0d\u7792\u4f60\u8bf4\uff0c\u9002\u624d\u4e4b\u4e8b\uff0c\u4e0d\u8fc7\u662f\u8bef\u6253\u8bef\u649e\u4e0a\u800c\u5df2\uff01\u795d\u5144\u7684\u773c\u5149\uff0c\u679c\u7136\u5389\u5bb3\u5f97\u5f88\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u7b11\u4e86\u4e00\u7b11\u9053\uff1a\u201c\u8f9b\u5144\u597d\u8bf4\uff0c\u8fd9\u53ef\u4e0d\u662f\u5144\u5f1f\u7684\u773c\u5149\u5389\u5bb3\uff0c\u800c\u662f\u8f9b\u5144\u81ea\u5df1\u6cc4\u4e86\u5e95\u800c\u5df2\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u76b1\u7709\u9053\uff1a\u201c\u5144\u5f1f\u54ea\u91cc\u6cc4\u4e86\u5e95\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u8f9b\u5144\u8bf4\u51fa\u8981\u53bb\u89c1\u90a3\u90d1\u5927\u521a\u4e4b\u5148\uff0c\u539f\u662f\u8981\u8bf4\u4ec0\u4e48\u522b\u7684\uff0c\u4f46\u56e0\u6709\u4eba\u7528\u90a3\u4f20\u97f3\u4e4b\u672f\u5411\u4f60\u8bf4\u4e86\u51e0\u53e5\u8bdd\uff0c\u8f9b\u5144\u624d\u6539\u4e86\u4e3b\u610f\uff0c\u6240\u4ee5\uff0c\u5144\u5f1f\u624d\u4f1a\u89c9\u51fa\uff0c\u8f9b\u5144\u5e76\u672a\u5df2\u7136\u53d1\u73b0\u6709\u4eba\u6f5c\u5165\u5bfa\u5185\u554a\u2026\u2026\u201d\u8f9b\u67cf\u516c\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u795d\u5144\uff0c\u4f60\u600e\u77e5\u6709\u4eba\u7528\u4f20\u97f3\u4e4b\u672f\u5411\u5144\u5f1f\u8bf4\u4e86\u4e9b\u8bdd\u5462\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u5144\u5f1f\u66fe\u5728\u90a3\u2018\u6355\u98ce\u6349\u97f3\u2019\u5de5\u592b\u4e0a\u82b1\u4e86\u5341\u5e74\u82e6\u529f\uff0c\u773c\u4e0b\u867d\u65e0\u5927\u6210\uff0c\u4f46\u5728\u4e94\u4e08\u4e4b\u5185\uff0c\u6709\u4eba\u7528\u4f20\u97f3\u4e4b\u672f\u8bf4\u8bdd\uff0c\u5144\u5f1f\u5c1a\u53ef\u542c\u5230\u516b\u6210\u3002\u201d \u3000\u3000\u8f9b\u67cf\u516c\u5446\u4e86\u4e00\u5446\u9053\uff1a\u201c\u795d\u5144\u5c45\u7136\u628a\u90a3\u2018\u6355\u98ce\u6349\u97f3\u2019\u7684\u795e\u529f\u7ec3\u5c31\u4e86\uff1f\u53ef\u559c\u53ef\u8d3a\u554a\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u82e6\u7b11\u9053\uff1a\u201c\u8f9b\u5144\uff0c\u5144\u5f1f\u53ea\u5b66\u4f1a\u4e00\u70b9\u513f\u76ae\u6bdb\u800c\u5df2\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u795d\u5144\uff0c\u6b66\u6797\u4e4b\u4e2d\uff0c\u80fd\u591f\u7ec3\u5c31\u8fd9\u7b49\u6b66\u529f\u4e4b\u4eba\uff0c\u53ef\u8c13\u51e4\u6bdb\u9e9f\u89d2\uff0c\u795d\u5144\u5df2\u7136\u7ec3\u5c31\uff0c\u5b9e\u662f\u53eb\u5144\u5f1f\u656c\u4f69\u65e0\u5df2\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u957f\u53f9\u9053\uff1a\u201c\u8f9b\u5144\u8fc7\u5956\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u795d\u5144\uff0c\u770b\u6765\u9002\u624d\u5b8b\u5144\u5bf9\u5144\u5f1f\u4f20\u97f3\u6240\u8bf4\uff0c\u4f60\u5fc5\u5df2\u542c\u5230\u4e86\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u7b11\u9053\uff1a\u201c\u542c\u5230\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u70b9\u4e86\u70b9\u5934\u9053\uff1a\u201c\u6b64\u4e8b\u82e5\u975e\u5b8b\u5144\u8bf4\u51fa\u6765\uff0c\u5144\u5f1f\u53ef\u771f\u8981\u4f7f\u5b89\u59d1\u5a18\u4e3a\u96be\u4e86\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u95fb\u8a00\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u626f\u5230\u665a\u8f88\u8eab\u4e0a\u6765\u4e86\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u7b11\u9053\uff1a\u201c\u4e8b\u60c5\u4e0d\u5927\uff0c\u4f46\u5173\u7cfb\u59d1\u5a18\u80fd\u4e0d\u80fd\u4f4f\u5728\u5c11\u6797\u5bfa\u4e2d\uff0c\u6240\u4ee5\uff0c\u5b8b\u5144\u624d\u544a\u8bc9\u8001\u592b\uff0c\u5343\u4e07\u4e0d\u53ef\u5c06\u59d1\u5a18\u7684\u8eab\u4efd\u6cc4\u6f0f\u51fa\u6765\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5931\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u4e3a\u7684\u8fd9\u4e2a\u5417\uff1f\u5230\u53eb\u665a\u8f88\u5403\u4e86\u4e00\u60ca\u3002\u201d \u3000\u3000\u8f9b\u67cf\u516c\u7b11\u9053\uff1a\u201c\u840d\u513f\uff0c\u8fd9\u4e8b\u5e76\u4e0d\u4e25\u91cd\uff0c\u4f46\u8001\u592b\u5982\u679c\u4e0d\u7559\u5fc3\u8bf4\u6f0f\u4e86\u5634\uff0c\u90a3\u53ef\u5c31\u8981\u7ed9\u4f60\u60f9\u6765\u4e0d\u5c11\u9ebb\u70e6\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u90a3\u4e5f\u6ca1\u6709\u4ec0\u4e48\uff0c\u5165\u4e91\u5927\u5e08\u65e9\u5df2\u77e5\u6653\u665a\u8f88\u7684\u8eab\u4efd\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u771f\u7684\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u5f53\u7136\u771f\u7684\u554a\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u770b\u4e86\u5b8b\u5b5a\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5b8b\u5144\u5b89\u59d1\u5a18\u8bf4\u7684\u53ef\u662f\u771f\u7684\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u5fc3\u7167\u4e0d\u5ba3\uff0c\u53ea\u662f\u4e0d\u4fbf\u8bf4\u51fa\u6765\u800c\u5df2\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u90a3\u662f\u4ec0\u4e48\u9053\u7406\uff1f\u63a9\u8033\u76d7\u94c3\u5417\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u8f9b\u8001\uff0c\u8fd9\u662f\u9762\u5b50\u4e0a\u4e0d\u597d\u4ea4\u4ee3\u554a\uff01\u54b1\u4eec\u53c8\u4f55\u5fc5\u63ed\u7a7f\u5462\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u5927\u7b11\u9053\uff1a\u201c\u5f88\u597d\uff01\u8001\u592b\u4ee5\u540e\u8bb0\u4f4f\u5c31\u662f\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u683c\u683c\u4e00\u7b11\u9053\uff1a\u201c\u8f9b\u516c\u516c\uff0c\u8fd9\u4e8b\u4e0d\u5927\uff0c\u4f60\u8001\u4e0d\u7528\u653e\u5728\u5fc3\u4e0a\u4e86\uff01\u201d \u3000\u3000\u8f9b\u6015\u516c\u7b11\u9053\uff1a\u201c\u8001\u592b\u672c\u4e5f\u4e0d\u66fe\u653e\u5728\u5fc3\u4e0a\u554a\uff01\u4f46\u5b8b\u8001\u513f\u65e2\u662f\u8bf4\u5f97\u90a3\u7b49\u90d1\u91cd\uff0c\u8001\u5927\u53c8\u600e\u80fd\u4e0d\u7559\u5fc3\u5462\uff1f\u201d \u3000\u3000\u8c08\u7b11\u4e4b\u95f4\uff0c\u53ea\u89c1\u5c11\u6797\u638c\u95e8\u4eba\u5165\u4e91\u5927\u5e08\u65bd\u65bd\u7136\u8d70\u4e86\u8fdb\u6765\uff0c\u5408\u5341\u9053\uff1a\u201c\u8f9b\u8001\u65bd\u4e3b\uff0c\u8001\u8872\u7279\u6765\u5411\u4f60\u8001\u81f4\u8c22\u6765\u4e86\uff1f\u201d \u3000\u3000\u4ed6\u8fd9\u8bdd\u663e\u5f97\u6709\u4e9b\u513f\u7a81\u5140\uff0c\u53ea\u628a\u8f9b\u67cf\u516c\u542c\u5f97\u5446\u4e86\u4e00\u5446\u9053\uff1a\u201c\u638c\u95e8\u4eba\u8fd9\u662f\u4f55\u610f\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u8001\u8872\u9002\u624d\u66fe\u53bb\u67e5\u8be2\uff0c\u65b9\u77e5\u90a3\u524d\u6765\u4e4b\u4eba\uff0c\u4e0d\u53ea\u4e00\u4f4d\u2026\u2026\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4e0d\u53ea\u4e00\u4f4d\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8001\u8872\u9002\u624d\u67e5\u8be2\u4e4b\u4e0b\uff0c\u65b9\u59cb\u660e\u767d\uff0c\u4ed6\u4eec\u6765\u4e86\u4e0d\u5c11\u4eba\uff0c\u7528\u5fc3\u751a\u4e3a\u6076\u6bd2\uff0c\u82e5\u975e\u8001\u65bd\u4e3b\u5c06\u4ed6\u4eec\u4e3a\u9996\u4e4b\u4eba\u60ca\u9000\uff0c\u5c11\u6797\u5bfa\u6050\u6015\u5c31\u8981\u906d\u4e00\u573a\u7a7a\u524d\u5927\u52ab\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u4e00\u6014\u9053\uff1a\u201c\u6709\u8fd9\u7b49\u4e8b\u5417\uff1f\u771f\u662f\u53eb\u4eba\u96be\u4fe1\u4e86\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8bf4\u6765\u53ea\u6015\u8001\u65bd\u4e3b\u4e0d\u4fe1\uff0c\u4ed6\u4eec\u5728\u672c\u5bfa\u7684\u56db\u5468\uff0c\u5806\u4e0b\u4e86\u4e0d\u5c11\u6613\u71c3\u4e4b\u7269\uff0c\u5b58\u5fc3\u8981\u4e00\u4e3e\u5c06\u672c\u5bfa\u6bc1\u53bb\u3002\u201d \u3000\u3000\u795d\u516c\u660e\u6014\u4e86\u4e00\u6014\uff0c\u5927\u58f0\u9053\uff1a\u201c\u8fd9\u771f\u662f\u5de7\u5f97\u5f88\u5566\u2026\u2026\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u4e8b\u679c\u771f\u5f88\u5de7\uff01\u82e5\u975e\u54b1\u4eec\u8bef\u6253\u8bef\u649e\u7684\u5c06\u4ed6\u4eec\u7684\u9996\u9886\u60ca\u8d70\uff0c\u90a3\u53ef\u771f\u8981\u8d39\u4e0a\u4e0d\u5c11\u7684\u624b\u811a\u5566\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u76ee\u5149\u4e00\u8f6c\uff0c\u63a5\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u90a3\u6765\u6b64\u653e\u706b\u4e4b\u4eba\uff0c\u53ef\u6709\u4ec0\u4e48\u75d5\u8ff9\u7559\u4e0b\u6765\u4e86\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u6ca1\u6709\uff01\u672c\u5bfa\u5f1f\u4e86\u66fe\u5728\u5404\u5904\u67e5\u770b\uff0c\u5747\u65e0\u4e00\u4e9b\u75d5\u8ff9\u53ef\u627e\uff0c\u770b\u6765\uff0c\u4ed6\u4eec\u7684\u624b\u811a\uff0c\u5012\u662f\u5e72\u51c0\u5f97\u5f88\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u8d35\u5bfa\u76ee\u4e0b\u4f3c\u662f\u5728\u5df2\u5f3a\u654c\u5305\u56f4\u4e4b\u4e0b\u4e86\uff01\u5927\u5e08\u53ef\u5f97\u8981\u4ed6\u4eec\u5f53\u5fc3\u4e00\u4e9b\u624d\u597d\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u8001\u8872\u6b63\u6709\u540c\u611f\u2026\u2026\u201d\u8bed\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u65e2\u7136\u8bf8\u4f4d\u65bd\u4e3b\u5747\u5728\u672c\u5bfa\uff0c\u8c05\u90a3\u4e94\u9b54\u4e5f\u4e0d\u6562\u653e\u624b\u800c\u4e3a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u5f97\u5fc3\u4e2d\u4e00\u6014\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u5802\u5802\u7684\u5c11\u6797\u638c\u95e8\uff0c\u600e\u4e48\u7adf\u4f1a\u8bf4\u51fa\u8fd9\u7b49\u4f9d\u8d56\u522b\u4eba\u7684\u8bdd\u6765\uff1f\u201d\u4ed6\u5fc3\u4e2d\u867d\u7136\u8fd9\u4e48\u60f3\uff0c\u4e0d\u4fbf\u8bf4\u5c06\u51fa\u6765\u3002 \u3000\u3000\u795d\u516c\u660e\u5374\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u8001\u592b\u7b49\u4eba\u867d\u7136\u6682\u65f6\u4f4f\u5728\u8d35\u5bfa\uff0c\u4f46\u4e0d\u5b9a\u54ea\u5929\u5c31\u4f1a\u79bb\u53bb\uff0c\u5c11\u6797\u5982\u60f3\u7ef4\u6301\u5728\u6b66\u6797\u4e2d\u7684\u5730\u4f4d\uff0c\u53ea\u6015\u8fd8\u5f97\u9760\u638c\u95e8\u4eba\u81ea\u5df1\u591a\u591a\u8d39\u5fc3\u4e86\uff01\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\uff0c\u53ea\u628a\u5165\u4e91\u5e08\u8bf4\u5f97\u8138\u4e0a\u6cdb\u73b0\u7ea2\u6655\u3002 \u3000\u3000\u4ed6\u957f\u53f9\u4e00\u58f0\uff0c\u5408\u5341\u9053\uff1a\u201c\u8001\u65bd\u4e3b\u6559\u8bad\u5f97\u5f88\u597d\uff0c\u8d2b\u50e7\u8bb0\u4e0b\u4e86\uff01\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\u5411\u4f17\u4eba\u4e00\u793c\u9053\uff1a\u201c\u591c\u8272\u5df2\u6df1\uff0c\u8bf8\u4f4d\u65bd\u4e3b\u5c31\u8981\u5b89\u6b47\uff0c\u6055\u8d2b\u50e7\u4e0d\u518d\u627e\u6270\u5404\u4f4d\u2026\u2026\u201d \u3000\u3000\u8f6c\u8eab\u5927\u6b65\u800c\u53bb\u3002 \u3000\u3000\u8f9b\u67cf\u516c\u76ee\u5149\u4e00\u8f6c\uff0c\u7b11\u9053\uff1a\u201c\u795d\u5144\uff0c\u54b1\u4eec\u8be5\u4f11\u606f\u5566\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f46\u5144\u5f1f\u5374\u8fd8\u5f97\u53bb\u90a3\u85cf\u7ecf\u697c\u8fc7\u591c\u624d\u6210\u3002\u201d \u3000\u3000\u8bdd\u97f3\u4e00\u843d\uff0c\u4e0d\u7b49\u522b\u4eba\u591a\u8bf4\uff0c\u8eab\u5f62\u4e00\u95ea\uff0c\u7adf\u662f\u7a7f\u7a97\u800c\u53bb\u3002 \u3000\u3000\u8f9b\u67cf\u516c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5947\u602a\u554a\uff01\u8fd9\u8001\u513f\u600e\u5730\u8981\u5230\u85cf\u7ecf\u697c\u8fc7\u591c\u5462\uff1f\u96be\u9053\u8fd9\u4e2d\u95f4\u6709\u4ec0\u4e48\u53e4\u602a\u4e0d\u6210\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e5f\u662f\u88ab\u90a3\u795d\u516c\u660e\u7684\u884c\u52a8\uff0c\u5f04\u5f97\u4e00\u6014\uff0c\u6697\u9053\uff1a\u201c\u53ef\u4e0d\u662f\uff1f\u4ed6\u4e3a\u4f55\u8981\u53bb\u85cf\u7ecf\u697c\u2026\u2026\uff1f\u201d \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u5ba2\u5385\u4e2d\u7684\u7fa4\u96c4\uff0c\u90fd\u6709\u7740\u6478\u4e0d\u6e05\u5934\u8111\u4e4b\u611f\u3002 \u3000\u3000\u4f46\u5b89\u5c0f\u840d\u5374\u662f\u5ae3\u7136\u4e00\u7b11\u9053\uff1a\u201c\u8f9b\u516c\u516c\uff0c\u6211\u77e5\u9053\u4ed6\u4e3a\u4ec0\u4e48\u8981\u53bb\u85cf\u7ecf\u697c\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4f60\u77e5\u9053\uff1f\u90a3\u662f\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4ed6\u2026\u2026\u4e00\u5b9a\u662f\u628a\u90a3\u5b9d\u8d1d\u9e1f\u513f\u5f04\u5728\u90a3\u85cf\u7ecf\u697c\u4e0a\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4ec0\u4e48\u9e1f\u513f\uff1f\u8fd9\u8001\u513f\u5728\u517b\u4ec0\u4e48\u5947\u79cd\u5f02\u9e1f\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u53ef\u4e0d\uff1f\u201d\u5f53\u4e0b\u628a\u5979\u548c\u65b9\u96ea\u5b9c\u5728\u592a\u5ba4\u9022\u9876\u53d1\u73b0\u4e86\u4e00\u5bf9\u91d1\u96c0\u4e4b\u4e8b\u8bf4\u4e86\u4e00\u904d\u3002 \u3000\u3000\u8f9b\u67cf\u516c\u542c\u5f97\u604d\u7136\u5927\u7b11\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff1f\u770b\u6765\u597d\u4f3c\u731c\u5bf9\u4e86\u3002\u201d \u3000\u3000\u4f17\u4eba\u53c8\u8bf4\u7b11\u4e86\u4e00\u4f1a\u513f\uff0c\u8fd9\u624d\u5404\u81ea\u56de\u623f\u5b89\u6b47\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5374\u662f\u4f34\u5728\u90d1\u5927\u521a\u8eab\u8fb9\uff0c\u4e00\u591c\u4e0d\u66fe\u5408\u773c\uff0c\u76f4\u5230\u5bfa\u4e2d\u50e7\u4fa3\u65e9\u8bfe\u5df2\u7f62\uff0c\u4ed6\u624d\u7565\u4e3a\u5730\u6253\u5750\u8c03\u606f\u4e86\u4e00\u4e2a\u65f6\u8fb0\u3002 \u3000\u3000\u5bb9\u5f97\u4ed6\u5f20\u5f00\u773c\u6765\uff0c\u53ea\u89c1\u90a3\u90d1\u5927\u521a\u6b63\u5728\u81ea\u5df1\u7684\u8eab\u8fb9\u542b\u7b11\u76f8\u5f85\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e0d\u597d\u610f\u601d\u5730\u8fde\u5fd9\u8d77\u8eab\uff1b\u9053\uff1a\u201c\u5927\u4f2f\uff0c\u4f60\u8001\u53ef\u5df2\u75ca\u6108\u4e86\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u4f38\u624b\u62c9\u7740\u65b9\u96ea\u5b9c\uff0c\u8981\u4ed6\u91cd\u65b0\u5750\u4e0b\uff0c\u7b11\u9053\uff1a\u201c\u597d\u4e86\uff01\u5c11\u6797\u795e\u4e39\uff0c\u679c\u7136\u7075\u6548\u5f97\u5f88\uff01\u201d \u3000\u3000\u8bdd\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u8d24\u4f84\uff0c\u5c11\u6797\u4e4b\u4e8b\u4f55\u65e5\u53ef\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u6014\u9053\uff1a\u201c\u5927\u4f2f\uff0c\u4f60\u8001\u53ef\u662f\u8981\u5c0f\u4f84\u8ddf\u4f60\u79bb\u53bb\u5417\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4e0d\u9519\uff01\u611a\u53d4\u60f3\u540c\u4f60\u53bb\u4e2d\u5dde\u4e00\u884c\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u4e00\u9707\uff0c\u8131\u53e3\u9053\uff1a\u201c\u56de\u5bb6\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u611a\u53d4\u76f8\u4fe1\uff0c\u4f60\u90a3\u4f2f\u6bcd\u548c\u73de\u5144\u5f1f\uff0c\u4e00\u5b9a\u8fd8\u5728\u4e2d\u5dde\u9644\u8fd1\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6014\u6014\u5730\u9053\uff1a\u201c\u5927\u4f2f\u8fd9\u8bf4\uff0c\u5fc5\u6709\u6240\u89c1\u7684\u4e86\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u611a\u53d4\u5012\u4e0d\u6562\u80af\u5b9a\u4ed6\u8bf4\u6709\u4ec0\u4e48\u6240\u89c1\uff0c\u4f46\u4ee5\u611a\u53d4\u731c\u60f3\uff0c\u4f60\u90a3\u4f2f\u6bcd\u548c\u73de\u5144\u5f1f\u5f88\u53ef\u80fd\u662f\u6446\u8131\u4e86\u65b9\u5929\u6210\u7684\u63a7\u5236\uff0c\u8eb2\u5230\u4ec0\u4e48\u5730\u65b9\u53bb\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5927\u4f2f\u8bf4\u5f97\u5bf9\uff01\u5c0f\u4f84\u8fd9\u5c31\u53bb\u540c\u5165\u4e91\u5927\u5e08\u8bf4\u660e\uff0c\u966a\u540c\u5927\u4f2f\u53bb\u81f3\u4e2d\u5dde\u4e00\u884c\u53ef\u597d\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u76b1\u4e86\u76b1\u7709\u9053\uff1a\u201c\u8d24\u4f84\uff0c\u90a3\u5c11\u6797\u5b89\u5371\uff0c\u73b0\u5728\u4f3c\u4e4e\u662f\u5df2\u5168\u7136\u843d\u5728\u4f60\u7684\u80a9\u4e0a\uff0c\u4f60\u5982\u79bb\u53bb\uff0c\u53ea\u6015\u4e0d\u5927\u59a5\u5f53\u5427\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5927\u4f2f\uff0c\u6709\u4e86\u8f9b\u3001\u795d\u4e8c\u8001\u5728\u6b64\uff0c\u5c0f\u4f84\u5728\u4e0d\u5728\u6b64\uff0c\u5df2\u7136\u6ca1\u6709\u591a\u5927\u7684\u5173\u7cfb\u4e86\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u6c89\u541f\u9053\uff1a\u201c\u8d24\u4f84\u8bf4\u5f97\u6709\u9053\u7406\uff0c\u53ea\u662f\uff0c\u611a\u53d4\u603b\u89c9\u5f97\uff0c\u5018\u662f\u5148\u628a\u5c11\u6797\u7684\u5371\u673a\u89e3\u9664\uff0c\u54b1\u4eec\u518d\u53bb\u4e2d\u5dde\u4e0d\u8fdf\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4f3c\u662f\u60f3\u518d\u8bf4\u4ec0\u4e48\uff0c\u4f46\u4ed6\u5c1a\u672a\u5f00\u53e3\uff0c\u90a3\u5b89\u5c0f\u840d\u5df2\u7136\u6025\u6025\u5954\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u90d1\u5927\u521a\u4e00\u89c1\u5b89\u5c0f\u840d\uff0c\u7acb\u5373\u8fc5\u5feb\u5730\u8d77\u8eab\uff0c\u62b1\u62f3\u9053\uff1a\u201c\u6069\u516c\u8bf7\u5750\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u88ab\u4ed6\u5f04\u5f97\u4e00\u6014\uff0c\u8baa\u8baa\u7b11\u9053\uff1a\u201c\u90d1\u5927\u4f2f\uff0c\u4f60\u8fd9\u4e48\u8bf4\u4e0d\u662f\u8981\u628a\u6211\u6298\u6b7b\u4e86\u5417\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u6069\u516c\u4e43\u662f\u90d1\u67d0\u6551\u547d\u7684\u6069\u4eba\uff0c\u90d1\u67d0\u8fd9\u4e00\u70b9\u656c\u610f\uff0c\u4e43\u662f\u7406\u6240\u5f53\u7136\u554a\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u90d1\u5927\u4f2f\uff0c\u665a\u8f88\u4e0e\u65b9\u5927\u54e5\u60c5\u540c\u5144\u5f1f\uff0c\u4f60\u8001\u5343\u4e07\u83ab\u8981\u8fd9\u4e48\u8bf4\u4e86\u2026\u2026\u201d \u3000\u3000\u5979\u8bed\u97f3\u4e00\u987f\uff0c\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u65b9\u5927\u54e5\uff0c\u8f9b\u516c\u516c\u627e\u4f60\u53bb\u8bb2\u8bdd\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8d24\u5f1f\u53ef\u77e5\u8f9b\u8001\u627e\u6211\u4f55\u4e8b\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4e0d\u77e5\u9053\uff0c\u4f46\u770b\u4ed6\u795e\u8272\u6124\u6124\uff0c\u5927\u6982\u53c8\u662f\u4ec0\u4e48\u4eba\u5f97\u7f6a\u4e86\u4ed6\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u5fc3\u4e2d\u5403\u4e86\u4e00\u60ca\uff0c\u5fd9\u5411\u90d1\u5927\u521a\u9053\uff1a\u201c\u5927\u4f2f\uff0c\u54b1\u4eec\u4e00\u8d77\u53bb\u77a7\u77a7\u5427\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u8f9b\u8001\u4e5f\u662f\u611a\u53d4\u7684\u6069\u4eba\uff0c\u81ea\u5f53\u524d\u53bb\u62dc\u8c22\u2026\u2026\u201d \u3000\u3000\u4e09\u4eba\u8fc5\u5feb\u5730\u5411\u524d\u6bbf\u8d70\u53bb\uff0c\u6562\u60c5\u90a3\u8f9b\u6015\u516c\u548c\u795d\u516c\u660e\u4e8c\u4eba\uff0c\u4e43\u662f\u5728\u5c71\u95e8\u4e4b\u5916\u7684\u5c0f\u6eaa\u4e4b\u7554\uff0c\u4e34\u6d41\u800c\u5750\uff0c\u4e24\u4eba\u4e4b\u95f4\uff0c\u6709\u4e00\u5757\u5de8\u77f3\uff0c\u5de8\u77f3\u4e4b\u4e0a\uff0c\u88ab\u4ed6\u4eec\u7528\u91d1\u521a\u6307\u529b\uff0c\u523b\u6210\u4e86\u4e00\u9762\u68cb\u67b0\uff0c\u53ea\u662f\uff0c\u67b0\u4e0a\u5374\u65e0\u68cb\u5b50\uff0c\u5bf9\u4e8e\u4e09\u4eba\u5230\u6765\uff0c\u4e5f\u672a\u5728\u610f\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u770b\u5f97\u5446\u4e86\u4e00\u5446\uff0c\u5411\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u6653\u5f97\u554a\uff0c\u521a\u624d\u6211\u51fa\u6765\u627e\u8f9b\u516c\u516c\uff0c\u4ed6\u5374\u5927\u58f0\u5411\u6211\u543c\u53eb\uff0c\u8981\u6211\u5c06\u4f60\u627e\u6765\u561b\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u76b1\u7709\u9053\uff1a\u201c\u8fd9\u5c31\u5947\u4e86\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u95f4\uff0c\u4eba\u5df2\u8d70\u5230\u4e8c\u8001\u8eab\u8fb9\uff0c\u76ee\u5149\u5728\u62a8\u4e0a\u4e00\u8f6c\uff0c\u62b1\u62f3\u9053\uff1a\u201c\u8f9b\u8001\uff0c\u4f60\u627e\u665a\u8f88\u5417\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u70b9\u4e86\u70b9\u5934\uff0c\u5374\u662f\u4e0d\u66fe\u8bf4\u8bdd\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u67f3\u7709\u4e00\u626c\uff0c\u53eb\u9053\uff1a\u201c\u8f9b\u516c\u516c\uff0c\u4f60\u8981\u6211\u627e\u65b9\u5927\u54e5\u524d\u6765\uff0c\u6211\u5df2\u7136\u628a\u4ed6\u627e\u5230\uff0c\u600e\u7684\u4f60\u8001\u5374\u4e0d\u8bf4\u8bdd\u4e86\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u76b1\u4e86\u76b1\u7709\uff0c\u7a81\u7136\u5411\u795d\u516c\u660e\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u54b1\u4eec\u8fd9\u5c40\u68cb\u4e0d\u7528\u518d\u4e0b\u4e86\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u8f9b\u5144\uff0c\u795d\u67d0\u5df2\u7136\u6709\u591a\u5e74\u4e0d\u66fe\u4e0b\u8fc7\u8fd9\u79cd\u65e0\u5b50\u5fc3\u7b97\u7684\u68cb\u5c40\u4e86\uff0c\u518d\u8d70\u4e0b\u53bb\uff0c\u5144\u5f1f\u662f\u8f93\u5f97\u66f4\u60e8\u5566\uff01\u201d\u54c8\u54c8\u4e00\u7b11\uff0c\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8f9b\u67cf\u516c\u4e5f\u81ea\u5475\u5475\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8f9b\u67d0\u4e5f\u662f\u4e00\u6837\uff01\u201d \u3000\u3000\u76ee\u5149\u8f6c\u5230\u65b9\u96ea\u5b9c\u8eab\u4e0a\uff0c\u5ffd\u5730\u8138\u8272\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u90a3\u4e94\u5927\u9b54\u4e3b\u5dee\u4eba\u9001\u4fe1\u6765\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u6014\uff0c\u9053\uff1a\u201c\u5728\u90a3\u91cc\u2026\u2026\u201d \u3000\u3000\u8f9b\u6015\u516c\u9053\uff1a\u201c\u5728\u8fd9\u91cc\u2026\u2026\u201d\u53ea\u89c1\u4ed6\u8863\u8896\u4e00\u6296\uff0c\u98d8\u51fa\u4e00\u5f20\u7eb8\u7b3a\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4f38\u624b\u63a5\u5230\u624b\u4e2d\uff0c\u51dd\u76ee\u671b\u53bb\u3002\u9661\u7136\uff0c\u4ed6\u5931\u58f0\u7b11\u4e86\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u8df3\u4e86\u8fc7\u6765\uff0c\u4e00\u628a\u62a2\u8fc7\u90a3\u5f20\u7eb8\u7b3a\uff0c\u53ea\u770b\u4e86\u4e00\u773c\uff0c\u5979\u5c31\u5fcd\u4e0d\u4f4f\u683c\u683c\u5a07\u7b11\u4e0d\u5df2\uff01 \u3000\u3000\u5b8b\u5b5a\u548c\u90d1\u5927\u521a\u51d1\u8fc7\u6765\uff0c\u5411\u90a3\u7eb8\u7b3a\u4e0a\u770b\u53bb\uff0c\u5374\u89c1\u90a3\u4e0a\u9762\u5199\u7740\u201c\u65b9\u5c11\u4fa0\u96ea\u5b9c\uff0c\u8bf7\u8f6c\u544a\u8001\u4e0d\u6b7b\u8f9b\u67cf\u516c\u3001\u795d\u516c\u660e\u4e24\u4eba\uff0c\u4e8e\u4e09\u65e5\u4e4b\u540e\uff0c\u53bb\u5230\u5c11\u5ba4\u5cf0\u5934\u9886\u6b7b\u3002\u201d\u4e0b\u9762\u7684\u7f72\u540d\uff0c\u53ea\u662f\u4e94\u5927\u9b54\u4e3b\u56db\u5b57\u3002 \u3000\u3000\u90d1\u5927\u521a\u770b\u5f97\u4e0d\u7981\u6709\u4e9b\u76b1\u7709\uff0c\u4f46\u5b8b\u5b5a\u5374\u4e5f\u5fcd\u4e0d\u4f4f\u7b11\u4e86\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4e24\u4f4d\u53bb\u4e0d\u53bb\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u51b7\u7b11\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u53bb\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u8001\u5077\u513f\uff0c\u8001\u592b\u548c\u8f9b\u5144\u5982\u662f\u4e0d\u53bb\uff0c\u5c82\u4e0d\u662f\u88ab\u90a3\u4e94\u4e2a\u9b54\u5d3d\u5b50\u7b11\u8bdd\u5417\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u8fd9\u65f6\u77aa\u773c\u63a5\u9053\uff1a\u201c\u5b8b\u5b5a\uff0c\u8001\u592b\u672c\u5c31\u8981\u627e\u4ed6\u4eec\uff0c\u96be\u5f97\u4ed6\u4eec\u5148\u627e\u4e0a\u4e86\u8001\u592b\uff0c\u90a3\u81ea\u7136\u5f88\u597d\u7684\u4e86\uff01\u8001\u592b\u4e09\u5929\u4e4b\u540e\u5b9a\u4f1a\u53eb\u4ed6\u4eec\u4e0b\u5c71\u6765\u6c42\u9976\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201c\u8001\u5077\u513f\u76f8\u4fe1\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u76b1\u7709\u6c89\u541f\u9053\uff1a\u201c\u8f9b\u8001\uff0c\u5982\u679c\u4ed6\u4eec\u4e0d\u6765\u5462\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u95fb\u8a00\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4e0d\u6765\uff1f\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u6765\uff1f\u8fd9\u7eb8\u7b3a\u4e0d\u662f\u4ed6\u4eec\u81ea\u5df1\u5dee\u4eba\u9020\u6765\u7684\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f46\u665a\u8f88\u8ba4\u4e3a\u4ed6\u4eec\u4e5f\u8bb8\u53ea\u662f\u8bda\u5fc3\u8ddf\u4f60\u8001\u5f00\u4e00\u4e2a\u73a9\u7b11\uff01\u201d\u8f9b\u67cf\u516c\u9053\uff1a \u3000\u3000\u201c\u4ed6\u4eec\u6562\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u7b11\u9053\uff1a\u201c\u65b9\u5144\u5f1f\uff0c\u8001\u592b\u76f8\u4fe1\u4e94\u4e2a\u9b54\u5934\u867d\u7136\u80c6\u5b50\u5f88\u5927\uff0c\u4f46\u5982\u8981\u8ddf\u8001\u592b\u548c\u8f9b\u5144\u73a9\u7b11\uff0c\u8c05\u4ed6\u4eec\u4e5f\u4e0d\u6562\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u4e86\u4e00\u7b11\uff0c\u6ca1\u6709\u518d\u8bf4\u4ec0\u4e48\uff01 \u3000\u3000\u53ea\u56e0\u4ed6\u660e\u767d\uff0c\u5c31\u662f\u4ed6\u518d\u8bf4\uff0c\u90a3\u4e5f\u6ca1\u6709\u4ec0\u4e48\u7528\u5904\uff0c\u4f46\u4ed6\u5fc3\u4e2d\u5374\u751a\u662f\u660e\u767d\uff0c\u4e94\u9b54\u773c\u4e0b\u4e0d\u4f1a\u540c\u65f6\u5728\u5d69\u5c71\u51fa\u73b0\uff0c\u8db3\u89c1\u8fd9\u4e00\u7eb8\u767d\u7b3a\uff0c\u516b\u6210\u662f\u6709\u4eba\u5728\u5f00\u73a9\u7b11\u3002 \u3000\u3000\u5f53\u7136\uff0c\u4ed6\u65e0\u6cd5\u660e\u786e\u7684\u77e5\u6653\u662f\u4ec0\u4e48\u4eba\u5f00\u7684\u73a9\u7b11\uff0c\u662f\u4ee5\u4ed6\u4e0d\u65b9\u4fbf\u518d\u5f80\u4e0b\u8bf4\u53bb\u3002 \u3000\u3000\u8f9b\u67cf\u516c\u7f13\u7f13\u5730\u7ad9\u4e86\u8d77\u6765\uff0c\u4eff\u4f5b\u4e00\u8154\u6012\u6c14\uff0c\u5c1a\u672a\u6d88\u9664\uff0c\u7adf\u7136\u4e3e\u624b\u4e00\u638c\uff0c\u5c06\u90a3\u5757\u68cb\u62a8\u51fb\u6210\u7c89\u788e\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u4f38\u4e86\u4f38\u820c\u5934\uff0c\u53eb\u9053\uff1a\u201c\u8f9b\u516c\u516c\uff0c\u4f60\u8fd9\u4e00\u638c\u7684\u529b\u9053\u6709\u591a\u5927\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4f60\u8bf4\u6709\u591a\u5927\uff1f\u4e00\u5343\u65a4\uff1f\u8fd8\u662f\u4e94\u767e\u65a4\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u665a\u8f88\u731c\u60f3\uff0c\u82e5\u65e0\u5343\u65a4\u4e4b\u529b\uff0c\u8fd9\u5757\u5de8\u77f3\u4e5f\u4e0d\u4f1a\u788e\u5230\u8fd9\u7b49\u5730\u6b65\u7684\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u773c\u529b\u4e0d\u9519\uff01\u201d \u3000\u3000\u8bdd\u97f3\u4e00\u843d\uff0c\u5411\u795d\u516c\u660e\u9053\uff1a\u201c\u795d\u5144\uff0c\u54b1\u4eec\u5148\u53bb\u5c11\u5ba4\u5cf0\u5934\u770b\u770b\u53ef\u597d\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u7b11\u9053\uff1a\u201c\u5f53\u7136\u597d\uff01\u201d \u3000\u3000\u4e24\u4eba\u8bdd\u97f3\u4e00\u843d\uff0c\u7adf\u7136\u540c\u65f6\u4e3e\u6b65\uff0c\u76f4\u5954\u800c\u53bb\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u770b\u4e86\u770b\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u8981\u4e0d\u8981\u53bb\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u770b\u4e86\u90d1\u5927\u521a\u4e00\u773c\uff0c\u6447\u5934\u9053\uff1a\u201c\u4e0d\u53bb\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4e0d\u53bb\u5417\uff1f\u65b9\u5144\uff0c\u8fd9\u4e8b\u4e5f\u8bb8\u5173\u7cfb\u5f88\u5927\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u6211\u60f3\u2026\u2026\u201d\u4ed6\u5ffd\u7136\u6c89\u541f\u4e00\u9635\uff0c\u4e0d\u518d\u5f80\u4e0b\u8bf4\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u63a5\u9053\uff1a\u201c\u4f60\u60f3\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u6211\u60f3\u8ddf\u90d1\u5927\u4f2f\u53bb\u4e00\u8d9f\u4e2d\u5dde\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4f60\u8981\u53bb\u6d1b\u9633\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u957f\u957f\u5730\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u611a\u5144\u6b63\u662f\u8981\u53bb\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u770b\u770b\u90d1\u5927\u521a\uff0c\u9053\uff1a\u201c\u90d1\u5927\u4f2f\uff0c\u4f60\u4eec\u771f\u7684\u53bb\u6d1b\u9633\u4e00\u884c\u4e48\uff1f\u201d \u3000\u3000\u5173\u5927\u521a\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u53ea\u662f\u2026\u2026\u6211\u60f3\u7b49\u90a3\u4e94\u5927\u9b54\u4e3b\u6765\u4e86\u4ee5\u540e\u518d\u53bb\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u90a3\u2026\u2026\u65b9\u5927\u54e5\u600e\u4e48\u8bf4\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u6447\u5934\u9053\uff1a\u201c\u5927\u4f2f\uff0c\u54b1\u4eec\u6b64\u53bb\u53ea\u8981\u4e09\u5929\u4fbf\u53ef\u56de\u6765\uff0c\u8fd9\u513f\u4e09\u5929\u4e4b\u5185\uff0c\u60f3\u5fc5\u662f\u4e0d\u4f1a\u51fa\u4e8b\u7684\u4e86\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u8d24\u4f84\uff0c\u4e09\u5927\u8d76\u5f97\u53ca\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u6765\u5f97\u53ca\uff01\u201d \u3000\u3000\u76ee\u5149\u5728\u5b8b\u5b5a\u8eab\u4e0a\u4e00\u8f6c\uff0c\u63a5\u9053\uff1a\u201c\u5b8b\u8001\uff0c\u5c11\u6797\u4e00\u5207\uff0c\u4ef0\u4ed7\u4f60\u548c\u8f9b\u795d\u4e8c\u8001\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u7b11\u9053\uff1a\u201c\u8001\u5f1f\u653e\u5fc3\uff0c\u6709\u8f9b\u795d\u4e8c\u8001\u5728\u6b64\uff0c\u5c11\u6797\u4e0d\u4f1a\u6709\u4ec0\u4e48\u5dee\u9519\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5ffd\u7136\u4f4e\u58f0\u9053\uff1a\u201c\u65b9\u5927\u54e5\uff0c\u6211\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u81ea\u7136\u662f\u7559\u5728\u5bfa\u4e2d\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u4e0d\u8981\u6211\u53bb\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u8fd9\u662f\u611a\u5144\u5bb6\u4e2d\u4e4b\u4e8b\uff0c\u600e\u597d\u52b3\u52a8\u8d24\u5f1f\u5954\u8d70\u5462\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u5927\u54e5\uff0c\u6211\u8981\u53bb\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u77a7\u5979\u4e00\u8138\u60bb\u60bb\u4e4b\u8272\uff0c\u6b32\u5f85\u963b\u6b62\uff0c\u53ea\u6015\u53c8\u60f9\u5979\u4e0d\u5feb\uff0c\u82e6\u7b11\u4e86\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u8d24\u5f1f\u8981\u53bb\uff0c\u611a\u5144\u4e5f\u4e0d\u62e6\u963b\uff0c\u4f46\u611a\u5144\u5374\u6709\u4e00\u4e2a\u6761\u4ef6\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u4e00\u542c\uff0c\u6b22\u547c\u4e00\u58f0\u9053\uff1a\u201c\u4ec0\u4e48\u6761\u4ef6\uff1f\u53ea\u8981\u4e0d\u5c06\u6211\u7559\u5728\u5c11\u6797\uff0c\u4ec0\u4e48\u6761\u4ef6\u90fd\u4e0d\u8981\u7d27\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u6211\u4eec\u53bb\u81f3\u6d1b\u9633\uff0c\u5e76\u4e0d\u662f\u627e\u4eba\u6bd4\u6597\uff0c\u662f\u4ee5\uff0c\u4f60\u5343\u4e07\u4e0d\u53ef\u4efb\u6027\u60f9\u4e8b\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u5bb9\u6613\uff0c\u6211\u4e00\u5207\u542c\u547d\u5927\u54e5\uff0c\u5927\u54e5\u4e0d\u8981\u6211\u8bb2\u8bdd\uff0c\u6211\u5c31\u4e0d\u8bb2\u8bdd\uff0c\u4e0d\u8981\u6211\u52a8\u624b\uff0c\u6211\u5c31\u662f\u88ab\u4eba\u6253\u6b7b\uff0c\u4e5f\u4e0d\u8fd8\u624b\u4fbf\u662f\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u5f97\u4e00\u5446\uff0c\u6697\u9053\uff1a\u201c\u5979\u600e\u4e48\u8fde\u6027\u81ea\u5df1\u7684\u547d\u4e5f\u4e0d\u653e\u5728\u5fc3\u4e0a\u4e86\uff1f\u201d \u3000\u3000\u4f46\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u5fc5\u5566\uff0c\u53ea\u8981\u8d24\u5f1f\u4e0d\u591a\u60f9\u4e8b\u975e\uff0c\u611a\u5144\u4e5f\u5c31\u653e\u5fc3\u4e86\uff01\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u5411\u90d1\u5927\u521a\u9053\uff1a\u201c\u5927\u6015\uff0c\u54b1\u4eec\u8fd9\u5c31\u52a8\u8eab\u5427\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u7565\u4e00\u8fdf\u7591\uff0c\u7ec8\u4e8e\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u65e9\u53bb\u65e9\u56de\u4e5f\u597d\uff01\u201d \u3000\u3000\u8f6c\u5411\u5b8b\u5b5a\u62b1\u4e86\u62b1\u62f3\uff0c\u5927\u6b65\u5411\u5c71\u5916\u884c\u53bb\u3002 \u3000\u3000\u4e09\u4eba\u811a\u7a0b\u6781\u5feb\uff0c\u800c\u4e14\u8d70\u7684\u53c8\u662f\u6377\u5f84\uff0c\u662f\u4ee5\u5348\u65f6\u752b\u8fc7\uff0c\u4fbf\u5df2\u62b5\u8fbe\u4e86\u6d1b\u9633\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u7684\u6545\u5c45\uff0c\u4e43\u662f\u5728\u6d1b\u9633\u7684\u897f\u57ce\u4e4b\u5916\u4e94\u91cc\uff0c\u4e09\u4eba\u8fc5\u5feb\u5730\u7a7f\u8fc7\u4e86\u6d1b\u9633\u57ce\uff0c\u76f4\u5954\u5411\u65b9\u5bb6\u5e84\u800c\u884c\u3002 \u3000\u3000\u8d8a\u8d70\u8d8a\u8fd1\uff0c\u65b9\u96ea\u5b9c\u7684\u8138\u8272\uff0c\u4e5f\u8d8a\u53d1\u5730\u663e\u5f97\u7d27\u5f20\u3001\u82cd\u767d\uff0c\u5bb9\u5f97\u5df2\u7136\u671b\u89c1\u90a3\u4e00\u7247\u9690\u5728\u67f3\u6797\u4e4b\u4e2d\u9ed1\u74e6\u767d\u5899\u5e84\u820d\uff0c\u4ed6\u51e0\u4e4e\u8fde\u4e3e\u6b65\u90fd\u6709\u4e9b\u513f\u6c89\u91cd\uff0c\u4e00\u4e2a\u4eba\u843d\u540e\u4e86\u597d\u8fdc\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u89c1\u4ed6\u4e3e\u6b65\u5f88\u96be\uff0c\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\uff0c\u8fde\u5fd9\u5411\u90d1\u5927\u521a\u9053\uff1a\u201c\u5927\u4f2f\uff0c\u65b9\u5927\u54e5\u597d\u50cf\u8d70\u4e0d\u52a8\u5566\uff01\u6211\u4eec\u7b49\u4ed6\u4e00\u4e0b\u5427\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u7ad9\u5b9a\u8eab\u5b50\uff0c\u4f4e\u53f9\u4e00\u58f0\uff1a\u201c\u59d1\u5a18\u554a\uff01\u8fd9\u53eb\u8fd1\u4e61\u60c5\u66f4\u602f\uff0c\u65b9\u8d24\u4f84\u5fc3\u4e2d\u7684\u62c5\u5b50\u592a\u91cd\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6084\u6084\u5730\u9053\uff1a\u201c\u5927\u4f2f\uff0c\u8981\u6211\u529d\u529d\u4ed6\u5417\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u5f90\u5f90\u5730\u770b\u4e86\u5979\u4e00\u773c\uff0c\u6447\u5934\u9053\uff1a\u201c\u4e0d\u7528\u529d\u4ed6\uff0c\u59d1\u5a18\uff0c\u53ea\u8981\u4f60\u4e0d\u79bb\u5f00\u4ed6\u8eab\u8fb9\uff0c\u5927\u6982\u4e0d\u4f1a\u6709\u4ec0\u4e48\u5173\u7cfb\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u4e00\u6014\u9053\uff1a\u201c\u6211\u2026\u2026\u5f53\u7136\u4e0d\u4f1a\u79bb\u5f00\u4ed6\u7684\u4e86\uff01\u201d \u3000\u3000\u8bdd\u513f\u51fa\u53e3\uff0c\u5374\u662f\u7ea2\u7740\u8138\u4f4e\u4e0b\u5934\u53bb\u3002 \u3000\u3000\u90d1\u5927\u521a\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u65b9\u8d24\u4f84\u80fd\u591f\u9047\u5230\u4f60\uff0c\u90a3\u771f\u662f\u4e09\u751f\u4fee\u6765\u7684\u798f\u6c14\u2026\u2026\u201d \u3000\u3000\u4e24\u4eba\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u65b9\u96ea\u5b9c\u6162\u6162\u5730\u8d70\u4e86\u8fc7\u6765\uff0c\u76ee\u5149\u6709\u4e9b\u513f\u5446\u6ede\uff0c\u4f46\u5374\u4e00\u76f4\u76ef\u5728\u524d\u9762\u91cc\u8bb8\u4e4b\u5916\u7684\u67f3\u6797\u3002 \u3000\u3000\u5bb9\u5f97\u4ed6\u8d70\u5230\u4e24\u4eba\u8eab\u524d\uff0c\u90d1\u5927\u521a\u5ffd\u7136\u5927\u559d\uff0c\u9053\uff1a\u201c\u65b9\u8d24\u4f84\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5df2\u4ece\u4e24\u4eba\u8eab\u524d\u8de8\u4e86\u8fc7\u53bb\uff0c\u95fb\u8a00\u970d\u7136\u4e00\u9707\uff0c\u7ad9\u5b9a\u4e0b\u6765\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u4e00\u95ea\u8eab\u4fbf\u5230\u4e86\u4ed6\u7684\u8eab\u524d\uff0c\u53eb\u9053\uff1a\u201c\u65b9\u5927\u54e5\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7728\u4e86\u7728\u773c\uff0c\u4eff\u4f5b\u521a\u81ea\u68a6\u4e2d\u9192\u6765\u4e00\u822c\uff0c\u5446\u5446\u5730\u671b\u7740\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u6211\u2026\u2026\u5f88\u597d\u554a\u2026\u2026\u201d \u3000\u3000\u90d1\u5927\u521a\u4e5f\u8d70\u4e86\u8fc7\u6765\uff0c\u7b11\u9053\uff1a\u201c\u8d24\u4f84\uff0c\u4f60\u53ef\u662f\u60f3\u8d77\u4e86\u7ae5\u5e74\u7684\u4e00\u5207\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u70b9\u70b9\u5934\uff0c\u4ef0\u5929\u5401\u4e86\u4e00\u53e3\u5927\u6c14\u9053\uff1a\u201c\u5927\u6015\uff0c\u6211\u2026\u2026\u521a\u624d\u4eff\u4f5b\u505a\u4e86\u4e00\u573a\u68a6\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u7b11\u9053\uff1a\u201c\u9192\u6765\u4e86\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8baa\u8baa\u4e00\u7b11\u9053\uff1a\u201c\u9192\u6765\u4e86\u53ea\u662f\u2026\u2026\u5fc3\u91cc\u597d\u751f\u96be\u8fc7\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u751a\u662f\u7126\u8651\u5730\u63a5\u9053\uff1a\u201c\u65b9\u5927\u54e5\uff0c\u4f60\u5e72\u5417\u8981\u96be\u8fc7\u5462\uff1f\u56de\u5bb6\u770b\u770b\uff0c\u90a3\u4e0d\u662f\u5f88\u597d\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8bf4\u6765\u4e5f\u662f\u2026\u2026\u53ea\u662f\u6211\u603b\u662f\u6709\u4e9b\u513f\u5fc3\u4e2d\u5bb3\u6015\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6123\u4e86\u4e00\u6123\uff0c\u9053\uff1a\u201c\u5bb3\u6015\uff1f\u65b9\u5927\u54e5\uff0c\u8fd9\u513f\u4e5f\u6ca1\u6709\u5f3a\u654c\uff0c\u4f60\u600e\u4f1a\u5bb3\u6015\u5462\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u7b11\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u5982\u662f\u771f\u6709\u5f3a\u654c\uff0c\u65b9\u8d24\u4f84\u5012\u4e5f\u4e0d\u4f1a\u5bb3\u6015\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6df1\u6df1\u5730\u53f9\u4e86\u4e00\u58f0\uff0c\u5251\u7709\u4e00\u626c\uff0c\u5927\u58f0\u9053\uff1a\u201c\u5927\u4f2f\uff0c\u8bf7\u554a\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u89c1\u4ed6\u53cc\u76ee\u4e4b\u4e2d\uff0c\u795e\u5149\u7684\u7684\uff0c\u77e5\u9053\u4ed6\u5df2\u629b\u53bb\u4e86\u90a3\u79cd\u8fd1\u4e61\u60c5\u602f\u4e4b\u601d\uff0c\u5f53\u4e0b\u7b11\u9053\uff1a\u201c\u968f\u6211\u6765\uff01\u201d \u3000\u3000\u4e09\u4eba\u8fc5\u5feb\u5730\u5411\u90a3\u67f3\u6797\u4e2d\u7684\u5e84\u9662\u884c\u53bb\u3002 \u3000\u3000\u5230\u5f97\u95e8\u524d\uff0c\u90d1\u5927\u521a\u4e3e\u624b\u5411\u90a3\u4e2a\u94dc\u73af\u4e4b\u4e0a\u53e9\u53bb\u3002 \u3000\u3000\u6562\u60c5\uff0c\u90a3\u5e84\u9662\u7684\u9ed1\u6f06\u5927\u95e8\uff0c\u4e43\u662f\u5173\u5f97\u7d27\u7d27\u7684\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u7ad9\u5728\u95e8\u5916\uff0c\u5fc3\u4e2d\u6b63\u5728\u5bfb\u601d\uff0c\u4e0d\u548c\u8fbe\u65b9\u5bb6\u5e84\u4e2d\uff0c\u8fd8\u6709\u7740\u65b9\u96ea\u5b9c\u7684\u554a\u4e9b\u4eb2\u4eba\u5728\u5185\uff0c\u662f\u4ee5\u5979\u90a3\u4e00\u53cc\u5927\u773c\uff0c\u4e00\u76f4\u76ef\u5728\u95e8\u4e0a\uff0c\u8fde\u4e00\u77ac\u90fd\u6ca1\u6709\u7728\u52a8\u3002\u8fc7\u4e0d\u4e86\u591a\u4e45\uff0c\u53ea\u542c\u5f97\u5440\u7136\u4e00\u58f0\uff0c\u5927\u95e8\u6253\u5f00\uff0c\u4e00\u4e2a\u79c3\u5934\u8001\u4eba\u5f53\u95e8\u800c\u7acb\u3002 \u3000\u3000\u4f46\u4ed6\u4e00\u773c\u77a7\u5230\u7ad9\u5728\u95e8\u5916\u7684\u7adf\u662f\u90d1\u5927\u521a\uff0c\u987f\u65f6\u7b11\u9053\uff1a\u201c\u6562\u60c5\u662f\u5927\u8001\u7237\u56de\u6765\u4e86\u5417\uff1f\u8001\u5974\u53ef\u76fc\u5230\u5566\u2026\u2026\uff1f\u201d \u3000\u3000\u77a7\u4ed6\u90a3\u7b49\u559c\u60a6\u4e4b\u8272\uff0c\u771f\u4e0d\u557b\u5ffd\u7136\u63a5\u5230\u8d22\u795e\u7237\u822c\u7684\u9ad8\u5174\u3002 \u3000\u3000\u90d1\u5927\u521a\u7b11\u9053\uff1a\u201c\u65b9\u798f\uff0c\u8fd9\u4e9b\u5e74\u8f9b\u82e6\u4f60\u5566\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u4e09\u4eba\u5df2\u7136\u8fdb\u4e86\u5c4b\u5185\u7684\u5927\u5385\u3002 \u3000\u3000\u65b9\u798f\u5f20\u7f57\u4e86\u8336\u6c34\uff0c\u53c8\u53eb\u53a8\u4e0b\u51c6\u5907\u8336\u996d\uff0c\u5fd9\u5f97\u4e0d\u4ea6\u4e50\u4e4e\u3002 \u3000\u3000\u90d1\u5927\u521a\u5bb9\u5f97\u65b9\u798f\u4ece\u53a8\u4e0b\u56de\u6765\uff0c\u8fd9\u624d\u7b11\u9053\uff1a\u201c\u65b9\u798f\uff0c\u4f60\u8fd8\u8bb0\u5f97\u4e8c\u516c\u5b50\u5417\uff1f\u201d \u3000\u3000\u65b9\u798f\u4e00\u6014\u9053\uff1a\u201c\u4e8c\u516c\u5b50\uff1f\u54ea\u4f4d\u4e8c\u516c\u5b50\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u6307\u7740\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u745c\u513f\u5440\uff01\u201d \u3000\u3000\u65b9\u798f\u7741\u5927\u7740\u4e24\u773c\uff0c\u9053\uff1a\u201c\u662f\u745c\u5c11\u7237\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u53ef\u4e0d\uff1f\u957f\u5927\u4e86\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u65b9\u798f\u5ffd\u7136\u5954\u5230\u65b9\u96ea\u5b9c\u8eab\u524d\uff0c\u4e0a\u4e0a\u4e0b\u4e0b\u5730\u6253\u91cf\u4e86\u4e00\u9635\uff0c\u5ffd\u7136\u5927\u7b11\u9053\uff1a\u201c\u679c\u7136\u662f\u745c\u5c11\u7237\uff0c\u4f60\u957f\u5f97\u8fd9\u4e48\u9ad8\uff0c\u8001\u5974\u771f\u7684\u8ba4\u4e0d\u51fa\u6765\u5566\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u70ed\u6c6a\u76c8\u7736\uff0c\u62c9\u6625\u65b9\u798f\u9053\uff1a\u201c\u5927\u53d4\uff0c\u4f60\u8001\u8fd9\u4e9b\u5e74\u53ef\u597d\uff1f\u5bb6\u91cc\u8fd8\u6709\u4ec0\u4e48\u4eba\u5728\uff1f\u201d \u3000\u3000\u65b9\u798f\u5ffd\u7136\u8138\u8272\u4e00\u9eef\uff0c\u6447\u5934\u9053\uff1a\u201c\u5c11\u7237\uff0c\u81ea\u4ece\u90a3\u5e74\u4f60\u8ddf\u5927\u5e84\u4e3b\u3001\u5927\u5976\u5976\u548c\u73de\u5c11\u7237\u79bb\u5bb6\u4e4b\u540e\uff0c\u8fd9\u5bb6\u91cc\u7531\u8001\u5974\u548c\u5f20\u5e08\u7237\u5728\u7ba1\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u5927\u5976\u5976\u548c\u73de\u5c11\u7237\u867d\u662f\u56de\u6765\u4e86\u4e00\u8d9f\uff0c\u4f46\u6ca1\u4f4f\u591a\u4e45\u5c31\u53c8\u8d70\u4e86\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6c89\u541f\u9053\uff1a\u201c\u5927\u53d4\uff0c\u6211\u5a18\u5462\uff1f\u6709\u6d88\u606f\u5417\uff1f\u201d \u3000\u3000\u65b9\u798f\u6447\u5934\u9053\uff1a\u201c\u6ca1\u6709\uff01\u5929\u53ef\u601c\u89c1\uff0c\u4e8c\u5976\u5976\u600e\u7684\u72e0\u5f97\u4e0b\u5fc3\uff0c\u7adf\u7136\u8fde\u4f60\u4e5f\u4e22\u4e0b\u4e0d\u7ba1\u2026\u2026\u201d\u60f3\u662f\u4ed6\u8bf4\u5230\u4f24\u5fc3\u4e4b\u5904\uff0c\u7adf\u7136\u8001\u6cea\u7eb5\u6a2a\uff0c\u6ce3\u4e0d\u6210\u58f0\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u53ea\u89c9\u5fc3\u4e2d\u4e00\u9178\uff0c\u4e24\u9897\u773c\u6cea\uff0c\u987a\u816e\u6ef4\u843d\u3002 \u3000\u3000\u90d1\u5927\u521a\u957f\u957f\u5730\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u65b9\u798f\uff0c\u77f3\u4e09\u5f1f\u56de\u6765\u8fc7\u6ca1\u6709\uff1f\u201d \u3000\u3000\u65b9\u798f\u64e6\u4e86\u64e6\u773c\u6cea\u9053\uff1a\u201c\u56de\u6765\u8fc7\uff01\u5c31\u662f\u90a3\u5e74\u8ddf\u5927\u5976\u5976\u4e00\u9053\u4f4f\u4e86\u4e0d\u5230\u4e24\u4e2a\u6708\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4ed6\u53ef\u662f\u8ddf\u5927\u5976\u5976\u4e00\u9053\u8d70\u7684\uff1f\u201d \u3000\u3000\u55b7\u798f\u9053\uff1a\u201c\u662f\u4e00\u9053\u8d70\u7684\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4ee5\u540e\u6ca1\u6709\u56de\u6765\u8fc7\uff1f\u201d \u3000\u3000\u65b9\u798f\u9053\uff1a\u201c\u6ca1\u6709\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u53ef\u66fe\u6d3e\u4eba\u901a\u8fc7\u4fe1\u606f\uff1f\u201d \u3000\u3000\u65b9\u798f\u9053\uff1a\u201c\u4e5f\u6ca1\u6709\u2026\u2026\u201d \u3000\u3000\u90d1\u5927\u521a\u76b1\u7709\u9053\uff1a\u201c\u65b9\u798f\uff0c\u542c\u8bf4\u90a3\u77f3\u4e09\u5f1f\u5df2\u5728\u6d1b\u9633\u9732\u9762\uff0c\u4f60\u4e00\u70b9\u90fd\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u65b9\u798f\u9053\uff1a\u201c\u8001\u5974\u6ca1\u6709\u542c\u8bf4\u8fc7\u554a\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u65f6\u5ffd\u7136\u65b9\u798f\u9053\uff1a\u201c\u5927\u53d4\uff0c\u5f20\u5e08\u7237\u8fd8\u5728\u5e84\u4e2d\u5417\uff1f\u201d \u3000\u3000\u65b9\u798f\u957f\u957f\u4e00\u53f9\u9053\uff1a\u201c\u745c\u5c11\u7237\uff0c\u90a3\u5f20\u5e08\u7237\u5df2\u7136\u5728\u524d\u51e0\u5e74\u6b7b\u5566\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5f20\u5927\u53d4\u6b7b\u4e86\u5417\uff1f\u201d \u3000\u3000\u65b9\u798f\u9053\uff1a\u201c\u5f20\u5e08\u7237\u75c5\u4e86\u5f88\u4e45\uff0c\u8001\u5974\u8dd1\u904d\u4e86\u7701\u57ce\uff0c\u8bf7\u904d\u4e86\u540d\u533b\uff0c\u90fd\u6ca1\u6709\u80fd\u6cbb\u597d\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6df1\u6df1\u5730\u5438\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5927\u53d4\uff0c\u5f20\u5e08\u7237\u6b7b\u4e86\u4ee5\u540e\uff0c\u5e84\u4e2d\u7684\u5e10\u76ee\uff0c\u662f\u6258\u7684\u4ec0\u4e48\u4eba\u7ecf\u7ba1\u5462\uff1f\u201d \u3000\u3000\u65b9\u798f\u9053\uff1a\u201c\u662f\u5f20\u5e08\u7237\u7684\u513f\u5b50\u5f20\u5146\u5728\u5e2e\u5fd9\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u90a3\u5f88\u597d\uff01\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u53a8\u4e0b\u5df2\u7136\u9001\u4e0a\u4e86\u83dc\u996d\uff0c\u65b9\u96ea\u5b9c\u52c9\u5f3a\u5730\u5403\u4e86\u4e00\u4e9b\uff0c\u4fbf\u5728\u65b9\u798f\u966a\u540c\u4e0b\uff0c\u5728\u5e84\u4e2d\u5230\u5904\u5bdf\u770b\u4e86\u4e00\u756a\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u672c\u6765\u4e5f\u8981\u8ddf\u53bb\uff0c\u4f46\u90d1\u5927\u521a\u5374\u963b\u6b62\u4e86\u5979\uff0c\u4e24\u4eba\u4fbf\u5728\u5385\u4e2d\u8fb9\u5403\u8fb9\u7b49\u3002\u7ea6\u83ab\u8fc7\u4e86\u4e2a\u628a\u8fdb\u8fb0\uff0c\u65b9\u96ea\u5b9c\u624d\u548c\u65b9\u798f\u56de\u5230\u5385\u4e2d\u3002 \u3000\u3000\u90d1\u5927\u521a\u7b11\u9053\uff1a\u201c\u8d24\u4f84\uff0c\u4e00\u5207\u90fd\u8fd8\u597d\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u5927\u4f2f\uff0c\u5c0f\u4f84\u771f\u4e0d\u77e5\u9053\u600e\u751f\u611f\u8c22\u65b9\u5927\u53d4\u2026\u2026\uff1f\u201d \u3000\u3000\u65b9\u798f\u5fd9\u9053\uff1a\u201c\u745c\u5c11\u7237\uff0c\u8fd9\u4e48\u8bf4\uff0c\u5c82\u4e0d\u6298\u6740\u4e86\u8001\u5974\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5927\u53d4\uff0c\u8fd9\u4e2a\u5bb6\u6ca1\u6709\u4f60\u8001\uff0c\u65e9\u5df2\u4e0d\u77e5\u6210\u4e2a\u4ec0\u4e48\u6837\u5b50\u5566\uff01\u2026\u2026\u5927\u53d4\uff0c\u8fd9\u4e2a\u5bb6\u5c31\u7b49\u4e8e\u662f\u4f60\u7684\u4e86\uff01\u201d \u3000\u3000\u65b9\u798f\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u745c\u5c11\u7237\uff0c\u4f60\u8fd9\u662f\u4ec0\u4e48\u8bdd\uff1f\u8001\u5974\u81ea\u4ece\u4e09\u5c81\u8fdb\u5e84\uff0c\u5982\u4eca\u5df2\u662f\u4e03\u5341\u51fa\u5934\uff0c\u54ea\u4e00\u5929\u4e0d\u628a\u8fd9\u4e2a\u5bb6\u5f53\u6210\u81ea\u5df1\u7684\u5462\uff1f\u745c\u5c11\u7237\uff0c\u4f60\u8fd9\u4e48\u8bf4\uff0c\u8001\u5974\u53ef\u627f\u5f53\u4e0d\u8d77\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u77e5\u9053\u4ed6\u8bef\u4f1a\u4e86\u81ea\u5df1\u7684\u610f\u601d\uff0c\u5f53\u4e0b\u5fd9\u9053\uff1a\u201c\u5927\u53d4\uff0c\u4f60\u8001\u60f3\u9519\u5566\uff01\u6211\u662f\u8bf4\u2026\u2026\u4ee5\u540e\u8fd9\u4e2a\u5bb6\u5c31\u7b97\u662f\u4f60\u8001\u7684\u4e86\uff01\u201d \u3000\u3000\u65b9\u798f\u542c\u5f97\u5446\u4e86\u534a\u664c\uff0c\u9053\uff1a\u201c\u4f60\uff01\u745c\u5c11\u7237\u53ef\u662f\u4e0d\u6253\u7b97\u56de\u6765\u4e86\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5c0f\u4f84\u4e00\u65f6\u53ea\u6015\u4e0d\u80fd\u56de\u6765\u4e86\uff01\u201d \u3000\u3000\u65b9\u798f\u9661\u7136\u5931\u58f0\u9053\uff1a\u201c\u90a3\u600e\u4e48\u6210\u5462\uff1f\u745c\u5c11\u7237\uff0c\u4f60\u4e0d\u56de\u6765\uff0c\u73de\u5c11\u7237\u5462\uff1f\u4ed6\u56de\u4e0d\u56de\u6765\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u6211\u4f1a\u627e\u4ed6\u56de\u6765\u7684\uff01\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u5411\u90d1\u5927\u521a\u9053\uff1a\u201c\u5927\u4f2f\uff0c\u54b1\u4eec\u8981\u4e0d\u8981\u51fa\u53bb\u8d70\u8d70\uff1f\u201d \u3000\u3000\u5173\u5927\u521a\u9053\uff1a\u201c\u5982\u662f\u4f60\u90a3\u77f3\u4e09\u53d4\u4e0d\u66fe\u6765\u8fc7\uff0c\u54b1\u4eec\u8fd9\u4e00\u8d9f\u7b97\u662f\u767d\u8dd1\u5566\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u4e00\u76f4\u6ca1\u6709\u8bf4\u8bdd\uff0c\u8fd9\u65f6\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u90d1\u5927\u4f2f\uff0c\u54b1\u4eec\u5148\u53bb\u627e\u90a3\u4e10\u5e2e\u7684\u5409\u957f\u8001\u53ef\u597d\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u7b11\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u4ed6\u5728\u4f55\u5904\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u2026\u2026\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u90a3\u600e\u4e48\u53bb\u627e\u5462\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u6d1b\u9633\u57ce\u4e2d\u603b\u6709\u4e5e\u4e10\u554a\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u6c89\u541f\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u54b1\u4eec\u5982\u662f\u627e\u5230\u4e5e\u4e10\uff0c\u60f3\u5fc5\u5b9a\u53ef\u95ee\u51fa\u90a3\u5409\u8001\u7684\u4e0b\u843d\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5927\u4f2f\uff0c\u54b1\u4eec\u8fd9\u5c31\u53bb\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u70b9\u5934\u9053\uff1a\u201c\u5929\u8272\u5c1a\u65e9\uff0c\u53bb\u57ce\u91cc\u770b\u770b\u4e5f\u597d\uff01\u201d \u3000\u3000\u4ed6\u56de\u987e\u4e86\u65b9\u798f\u4e00\u773c\u9053\uff1a\u201c\u8001\u5144\u5f1f\uff0c\u9ebb\u70e6\u4f60\u5f20\u7f57\u4e00\u4e0b\u5bbf\u5904\uff0c\u6211\u4eec\u53ef\u80fd\u8981\u4f4f\u4e0a\u4e24\u5929\u5462\u3002\u201d \u3000\u3000\u65b9\u798f\u4e00\u8fed\u8fde\u58f0\u9053\uff1a\u201c\u8001\u5974\u8fd9\u5c31\u53bb\u6253\u626b\u6614\u65e5\u4f60\u8001\u4f4f\u7684\u5c4b\u5b50\u2026\u2026\u201d \u3000\u3000\u4ed6\u770b\u4e86\u5b89\u5c0f\u840d\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5927\u8001\u7237\uff0c\u8fd9\u4f4d\u516c\u5b50\u5462\uff1f\u4ed6\u53ef\u662f\u8981\u8ddf\u745c\u5c11\u7237\u4f4f\u5728\u4e00\u5904\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u84e6\u7136\u7c89\u9762\u901a\u7ea2\uff0c\u5927\u53eb\u9053\uff1a\u201c\u80e1\u8bf4\uff01\u201d \u3000\u3000\u65b9\u798f\u88ab\u4ed6\u559d\u5f97\u5446\u4e86\u4e00\u5446\uff0c\u65b9\u96ea\u5b9c\u8fde\u5fd9\u63a5\u9053\uff1a\u201c\u5927\u53d4\uff0c\u8fd9\u4f4d\u662f\u5b89\u59d1\u5a18\uff0c\u4f60\u5c31\u628a\u6211\u5a18\u7684\u623f\u95f4\u6253\u626b\u4e00\u4e0b\uff0c\u597d\u8ba9\u5b89\u59d1\u5a18\u5b89\u6b47\u2026\u2026\u201d \u3000\u3000\u8bdd\u97f3\u4e00\u843d\uff0c\u751f\u6015\u90a3\u8001\u4eba\u5bb6\u53c8\u8bf4\u51fa\u4ec0\u4e48\u4e0d\u4e2d\u542c\u7684\u8bdd\u6765\uff0c\u7acb\u5373\u5411\u90d1\u5927\u521a\u9053\uff1a\u201c\u5927\u4f2f\uff0c\u54b1\u4eec\u8be5\u8d70\u5566\uff01\u201d\u5f53\u5148\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u90d1\u5927\u521a\u7b11\u4e86\u7b11\uff0c\u5411\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u8001\u4eba\u5bb6\u4e0d\u77e5\u8005\u4e0d\u602a\u7f6a\uff0c\u4f60\u53ef\u83ab\u8981\u653e\u5728\u5fc3\u4e0a\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6447\u4e86\u6447\u5934\uff0c\u4e00\u62e7\u5a07\u8eaf\uff0c\u7bad\u4e00\u822c\u5730\u5411\u95e8\u5916\u5c04\u53bb\u3002 \u3000\u3000\uff08\u6b64\u5904\u7f3a\u5c11\u4e00\u6bb5\uff0c\u5982\u679c\u8c01\u6709\uff0c\u5e0c\u671b\u80fd\u591f\u8865\u4e0a\uff01\uff09 \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4ed6\u4eec\u5982\u679c\u4e0d\u8bf4\uff0c\u59d1\u5a18\u53c8\u80fd\u5982\u4f55\uff1f\u54b1\u4eec\u603b\u4e0d\u80fd\u8ddf\u4ed6\u4eec\u52a8\u6b66\u5427\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u60bb\u60bb\u5730\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u90d1\u5927\u4f2f\uff0c\u4f60\u8001\u5e72\u5417\u8fd9\u4e48\u6015\u4e8b\u5462\uff1f\u5176\u5b9e\uff0c\u665a\u8f88\u8ba4\u4e3a\u2026\u2026\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4f60\u4e0d\u80fd\u53bb\uff01\u201d \u3000\u3000\u4ed6\u8bf4\u5f97\u65a9\u9489\u622a\u94c1\uff0c\u53ea\u628a\u5b89\u5c0f\u840d\u542c\u5f97\u4e00\u5446\u3002 \u3000\u3000\u65b9\u96ea\u76f4\u63a5\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u5927\u4f2f\u8bf4\u4e0d\u80fd\u53bb\uff0c\u90a3\u5c31\u4e0d\u8981\u53bb\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u8fd9\u624d\u7b11\u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u2026\u2026\u6709\u4ec0\u4e48\u529e\u6cd5\u53ef\u4ee5\u67e5\u51fa\u4ed6\u4eec\u7684\u6765\u5386\u5462\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u54b1\u4eec\u8fd8\u662f\u5148\u7b49\u4e00\u4f1a\u513f\uff0c\u77a7\u4ed6\u4eec\u4e94\u4eba\u7684\u795e\u60c5\uff0c\u4f3c\u4e4e\u662f\u5728\u7b49\u4ec0\u4e48\u4eba\uff0c\u5bb9\u4ed6\u4eec\u6240\u7b49\u4e4b\u4eba\u5230\u6765\uff0c\u54b1\u4eec\u518d\u53e6\u60f3\u8ba4\u8bc6\u4e4b\u6cd5\uff0c\u4e5f\u65e0\u4e0d\u53ef\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u90a3\u4e5f\u597d\u2026\u2026\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5ffd\u7136\u6253\u90a3\u697c\u68af\u4eba\u53e3\u4e4b\u5904\uff0c\u51fa\u73b0\u4e86\u4e09\u4e2a\u4eba\u3002 \u3000\u3000\u8fd9\u4e09\u4eba\u5f62\u72b6\u751a\u4e3a\u602a\u5f02\uff0c\u5f53\u4e2d\u4e00\u4eba\uff0c\u751f\u5f97\u53c8\u77ee\u53c8\u80d6\uff0c\u800c\u90a3\u4e24\u65c1\u7684\u4e24\u4eba\uff0c\u5219\u53c8\u751f\u5f97\u6781\u9ad8\uff0c\u662f\u4ee5\u8fd9\u4e09\u4eba\u7ad9\u5728\u4e00\u8d77\uff0c\u5b8c\u5168\u7684\u4e0d\u76f8\u79f0\u3002 \u3000\u3000\u662f\u4ee5\uff0c\u8fd9\u4e09\u4eba\u4e00\u65e6\u73b0\u8eab\uff0c\u697c\u4e0a\u7684\u98df\u5ba2\uff0c\u5168\u90fd\u628a\u76ee\u5149\u6295\u671b\u8fc7\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5251\u7709\u4e00\u626c\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5927\u4f2f\uff0c\u8fd9\u4e09\u4e2a\u4eba\u597d\u50cf\u6765\u5386\u4e0d\u5bf9\u554a\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u4eec\u53ef\u80fd\u662f\u9ad8\u624b\u4e2d\u7684\u9ad8\u624b\u2026\u2026\u201d \u3000\u3000\u8bdd\u97f3\u4e00\u987f\u4e4b\u9645\uff0c\u53ea\u89c1\u90a3\u4e09\u4eba\u7adf\u662f\u5411\u90a3\u4e94\u4eba\u6240\u5750\u7684\u684c\u6b21\u8d70\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u65b9\u5927\u54e5\uff0c\u4ed6\u4eec\u679c\u7136\u662f\u8ddf\u90a3\u4e94\u4e2a\u4eba\u76f8\u8bc6\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7a81\u9053\uff1a\u201c\u54b1\u4eec\u7b49\u7740\u77a7\u77a7\u5427\uff01\u4e5f\u8bb8\uff0c\u54b1\u4eec\u53ef\u4ee5\u4ece\u4ed6\u4eec\u8eab\u4e0a\u627e\u51fa\u4e00\u70b9\u7709\u76ee\u6765\u5462\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u76ee\u4e0d\u8f6c\u775b\u5730\u76ef\u7740\u90a3\u4e09\u4eba\uff0c\u53ea\u89c1\u4ed6\u4eec\u8d70\u5230\u90a3\u4e94\u4eba\u8eab\u524d\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u4fbf\u5728\u90a3\u684c\u4e0a\u7684\u7a7a\u4f4d\u4e0a\u5750\u4e86\u4e0b\u53bb\u3002\u90a3\u4e94\u4eba\u4e5f\u6ca1\u6709\u8bf4\u8bdd\uff0c\u4eff\u4f5b\u4e0d\u8bc6\u4e00\u822c\uff0c\u5140\u81ea\u53ea\u7ba1\u5403\u81ea\u5df1\u7684\u4e1c\u897f\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u770b\u5f97\u751a\u5947\u602a\uff0c\u82e5\u975e\u65b9\u96ea\u5b9c\u5c06\u5979\u62c9\u4f4f\uff0c\u5979\u53c8\u5fcd\u4e0d\u4f4f\u524d\u53bb\u627e\u9ebb\u70e6\u4e86\u3002 \u3000\u3000\u90a3\u4e09\u4eba\u4e0e\u540c\u684c\u4e4b\u4eba\u4e0d\u4f46\u4e0d\u8bf4\u8bdd\uff0c\u5e76\u4e14\u8fde\u4e00\u70b9\u4e1c\u897f\u4e5f\u4e0d\u66fe\u5403\uff01 \u3000\u3000\u8fd9\u60c5\u51b5\uff0c\u663e\u7136\u662f\u592a\u4e0d\u5408\u4e4e\u5e38\u60c5\uff0c\u5982\u8bf4\u4ed6\u4eec\u76f8\u8bc6\uff0c\u5219\u4ed6\u4eec\u8fd9\u7b49\u4e0d\u8bf4\u8bdd\uff0c\u672a\u514d\u5947\u602a\uff0c\u5982\u679c\u8bf4\u4ed6\u4eec\u4e0d\u76f8\u8bc6\uff0c\u5219\u53c8\u600e\u5730\u5750\u5728\u4e00\u8d77\u5462\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u76f4\u5728\u5fc3\u4e2d\u5bfb\u601d\uff0c\u8fd9\u4e9b\u4eba\u5230\u5e95\u662f\u4ec0\u4e48\u5173\u7cfb\uff0c\u4f46\u4ed6\u5374\u53c8\u4e00\u76f4\u60f3\u4e0d\u6e05\u695a\u3002 \u3000\u3000\u5c31\u8fd9\u4e48\u8017\u4e86\u8db3\u8db3\u6709\u987f\u996d\u4e4b\u4e45\uff0c\u697c\u4e0a\u53c8\u4f20\u6765\u4e00\u9635\u5306\u6025\u7684\u811a\u6b65\u4e4b\u58f0\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u542c\u5230\u8fd9\u4e00\u9635\u811a\u6b65\u4e4b\u58f0\uff0c\u987f\u65f6\u4f38\u957f\u4e86\u8116\u5b50\uff0c\u5411\u697c\u53e3\u671b\u53bb\u3002 \u3000 \u7b2c\u4e09\u5341\u56db\u56de\u3000\u4e09\u7edd\u4e94\u5996 \u3000 \u3000\u3000\u53ea\u89c1\u811a\u6b65\u4e4b\u58f0\u7a0d\u987f\uff0c\u4e00\u4e2a\u5f62\u5bb9\u7325\u7410\u7684\u77ee\u5b50\uff0c\u5728\u697c\u68af\u5165\u53e3\u4e4b\u5904\u73b0\u8eab\u3002 \u3000\u3000\u6b64\u4eba\u8eab\u9ad8\u6bd4\u90a3\u4e09\u4eba\u4e2d\u7684\u90a3\u4e2a\u77ee\u5b50\u8fd8\u77ee\uff0c\u5927\u6982\u4e09\u5c3a\u4e0d\u5230\uff0c\u4e00\u5934\u4e71\u53d1\uff0c\u6ee1\u8eab\u8e09\u8e4b\uff0c\u90a3\u4ef6\u957f\u886b\u53ca\u819d\u7684\u957f\u886b\uff0c\u4e0b\u6446\u66f4\u662f\u7834\u70c2\u4e0d\u582a\uff0c\u8d64\u7740\u4e00\u53ea\u811a\uff0c\u82e5\u975e\u4ed6\u5728\u8fd9\u7b49\u65f6\u5149\u8dd1\u4e0a\u697c\u6765\uff0c\u51c6\u8981\u88ab\u4eba\u5f53\u4f5c\u4e86\u4e5e\u4e10\u3002 \u3000\u3000\u4f46\u5b89\u5c0f\u840d\u5374\u53d1\u73b0\u4ed6\u7684\u4e0d\u5e73\u51e1\u4e4b\u5904\u3002 \u3000\u3000\u4ed6\u90a3\u53cc\u53e3\u7741\u7fd5\u4e4b\u95f4\uff0c\u51b7\u8292\u5982\u7535\uff0c\u76f4\u53ef\u5c04\u51fa\u5c3a\u8bb8\u4e00\u822c\u3002 \u3000\u3000\u8fd9\u4e0d\u662f\u666e\u901a\u9ad8\u624b\u80fd\u591f\u7ec3\u5230\u7684\uff0c\u53cc\u76ee\u5bd2\u5149\u80fd\u591f\u5f62\u6210\u6709\u5f62\u7684\u5149\u675f\uff0c\u8fd9\u8868\u793a\u6b64\u4eba\u6b66\u529f\u4e4b\u9ad8\uff0c\u5df2\u5230\u7089\u706b\u7eaf\u9752\u4e4b\u5883\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e5f\u77a7\u51fa\u6b64\u4eba\u7684\u773c\u795e\uff0c\u5fc3\u4e2d\u4e0d\u7531\u6697\u9053\uff1a\u201c\u6b64\u4eba\u957f\u5f97\u8fd9\u7b49\u96be\u770b\uff0c\u60f3\u4e0d\u5230\u5374\u662f\u5c11\u89c1\u7684\u9ad8\u624b\u3002\u201d \u3000\u3000\u5c31\u5728\u6b64\u4eba\u73b0\u8eab\u4e4b\u9645\uff0c\u90a3\u684c\u4e0a\u7684\u516b\u4eba\uff0c\u8138\u8272\u540c\u65f6\u4e00\u53d8\u3002 \u3000\u3000\u90a3\u77ee\u5b50\u5219\u5728\u95e8\u53e3\u5f20\u671b\u4e86\u4e00\u4e0b\uff0c\u5f53\u4ed6\u7684\u76ee\u5149\u63a0\u8fc7\u65b9\u96ea\u5b9c\u548c\u5b89\u5c0f\u840d\u4e24\u4eba\u65f6\uff0c\u4f3c\u662f\u7709\u5934\u76b1\u4e86\u4e00\u76b1\u3002 \u3000\u3000\u4f46\u4ed6\u76ee\u5149\u5230\u90a3\u516b\u4eba\u684c\u4e0a\u65f6\uff0c\u5219\u53c8\u7b11\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u7b11\u5bb9\u4e00\u655b\uff0c\u7acb\u5373\u5927\u58f0\u9053\uff1a\u201c\u597d\u554a\uff01\u8001\u592b\u603b\u7b97\u6ca1\u6709\u767d\u8dd1\uff0c\u4f60\u4eec\u679c\u7136\u90fd\u5728\u8fd9\u5927\u767d\u5c45\u7684\u697c\u4e0a\u2026\u2026\u201d\u8bed\u97f3\u672a\u5df2\uff0c\u4e3e\u6b65\u4fbf\u5411\u90a3\u516b\u4eba\u884c\u53bb\u3002 \u3000\u3000\u90a3\u516b\u4eba\u4e4b\u4e2d\uff0c\u540e\u6765\u7684\u90a3\u4e2a\u77ee\u5b50\u54a7\u4e86\u54a7\u5634\uff0c\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u627e\u5230\u4e86\u53c8\u600e\u6837\uff1f\u201d \u3000\u3000\u90a3\u77ee\u8001\u4eba\u8fd9\u65f6\u5df2\u7ad9\u5728\u4ed6\u4eec\u684c\u524d\uff0c\u95fb\u8a00\u5927\u7b11\uff1a\u201c\u600e\u4e48\u6837\uff1f\u4f60\u8fd8\u4e0d\u660e\u767d\u5417\uff1f\u201d \u3000\u3000\u90a3\u80d6\u77ee\u5b50\u7709\u5934\u4e00\u626c\uff0c\u9053\uff1a\u201c\u6c5f\u67d0\u4e0d\u660e\u767d\uff01\u201d \u3000\u3000\u77ee\u8001\u4eba\u5927\u58f0\u9053\uff1a\u201c\u4f60\u771f\u4e0d\u660e\u767d\u5417\uff1f\u201d \u3000\u3000\u80d6\u77ee\u5b50\u7709\u5c16\u4e5f\u626c\u4e86\u626c\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u6c5f\u67d0\u4e0e\u4f60\u7d20\u65e0\u74dc\u845b\uff0c\u4f60\u627e\u6c5f\u67d0\u4f55\u4e8b\uff0c\u6c5f\u67d0\u81ea\u662f\u4e0d\u77e5\u7684\u4e86\uff01\u201d \u3000\u3000\u77ee\u8001\u4eba\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u6c5f\u5929\u6653\uff0c\u4f60\u4eec\u5929\u53f0\u4e09\u7edd\u4e00\u5411\u5230\u4e5f\u5b89\u4efd\u5b88\u5df1\uff0c\u6240\u4ee5\u8001\u592b\u548c\u90a3\u5251\u795e\u9648\u8001\u5f1f\u4e5f\u5c31\u4e0d\u66fe\u627e\u8fc7\u4f60\u4eec\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u7684\u5446\u4e86\u4e00\u5446\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u4e2a\u77ee\u8001\u4eba\u662f\u8c01\uff1f\u4ed6\u7adf\u7136\u79f0\u6211\u5e08\u7236\u4e3a\u8001\u5f1f\uff1f\u201d \u3000\u3000\u601d\u5fd6\u4e4b\u95f4\uff0c\u53ea\u542c\u5f97\u80d6\u77ee\u8001\u4eba\u8eab\u65c1\u7684\u90a3\u4f4d\u9ad8\u5927\u7684\u9a7c\u8001\u4eba\u5ffd\u7136\u7ad9\u4e86\u8d77\u6765\uff0c\u5927\u559d\u4e00\u58f0\u9053\uff1a\u201c\u6e38\u5144\uff0c\u522b\u4eba\u4e5f\u8bb8\u6015\u4f60\u8fd9\u4f4d\u5343\u624b\u795e\u7fc1\uff0c\u987b\u77e5\u4f59\u67d0\u5374\u4e0d\u6015\u4f60\uff01\u201d \u3000\u3000\u539f\u6765\u8fd9\u4f4d\u77ee\u8001\u4eba\u4e43\u662f\u5343\u624b\u795e\u7fc1\u6e38\u516c\u7384\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u5403\u4e86\u4e00\u60ca\uff0c\u4ed6\u4f9d\u7a00\u8bb0\u5f97\uff0c\u81ea\u5df1\u5e08\u7236\u66fe\u7ecf\u8bf4\u8fc7\uff0c\u8fd9\u5343\u624b\u795e\u7fc1\u8ddf\u81ea\u5df1\u5e08\u7956\u5251\u5723\u9f99\u8d85\u7fa4\u4e43\u662f\u540c\u65f6\u4ee3\u7684\u4eba\u7269\uff0c\u6e38\u4fa0\u6c5f\u6e56\uff0c\u4e13\u7ba1\u4e9b\u6b66\u6797\u4e0d\u5e73\u4e4b\u4e8b\uff0c\u53ea\u56e0\u4ed6\u6b66\u529f\u4e0d\u5728\u5251\u5723\u4e4b\u4e0b\uff0c\u662f\u4ee5\u6df1\u53d7\u6b66\u6797\u4e2d\u767d\u9053\u4eba\u7269\u7684\u656c\u4ef0\uff0c\u8ddf\u81ea\u5df1\u7684\u5e08\u7956\u88ab\u4ed6\u4eec\u5408\u79f0\u4e3a\u5929\u5730\u4e8c\u5947\uff0c\u60f3\u4e0d\u5230\u4ed6\u5c45\u7136\u8fd8\u6d3b\u5728\u4eba\u4e16\uff01 \u3000\u3000\u540c\u65f6\u4ee4\u65b9\u96ea\u5b9c\u5403\u60ca\u7684\u662f\u8fd9\u5929\u53f0\u4e09\u7edd\u4e09\u4f4d\u602a\u5f62\u602a\u72b6\u7684\u8001\u4eba\uff0c\u7adf\u7136\u5bf9\u90a3\u6e38\u516c\u7384\u79f0\u5144\u9053\u5f1f\uff0c\u8fd9\u7b49\u770b\u6765\uff0c\u8fd9\u4e09\u4eba\u4e5f\u662f\u6b66\u6797\u4e2d\u9690\u5c45\u7684\u8001\u4e00\u8f88\u4eba\u7269\u4e86\uff01\u4e00\u5ff5\u53ca\u6b64\uff0c\u65b9\u96ea\u5b9c\u4e0d\u7981\u6df1\u611f\u4e0d\u5b89\uff0c\u83ab\u975e\u9664\u4e86\u4e94\u5927\u9b54\u4e3b\u4e4b\u5916\uff0c\u6b66\u6797\u4e2d\u53c8\u6709\u65b0\u7684\u4ec0\u4e48\u9b54\u5934\u5d1b\u8d77\uff0c\u628a\u8fd9\u4e9b\u8001\u4e00\u8f88\u7684\u4eba\u7269\u4e5f\u4f1a\u8bf7\u4e86\u51fa\u6765\uff1f \u3000\u3000\u4ed6\u6b63\u5fc3\u4e2d\u6697\u6697\u6c89\u601d\uff0c\u8033\u4e2d\u5374\u542c\u5f97\u6e38\u516c\u7384\u9053\uff1a\u201c\u9a7c\u5b50\uff0c\u4f60\u60f3\u5a01\u80c1\u8001\u592b\u5417\uff1f\u201d \u3000\u3000\u90a3\u59d3\u4f59\u7684\u9a7c\u5b50\u51b7\u51b7\u5730\u63a5\u9053\uff1a\u201c\u6e38\u5144\uff0c\u4f59\u67d0\u4eba\u5e76\u65e0\u80c1\u6c42\u6e38\u5144\u4e4b\u610f\u2026\u2026\u4e0d\u8fc7\uff0c\u5018\u662f\u4f60\u6e38\u5144\u592a\u4e0d\u7ed9\u5144\u5f1f\u7b49\u9762\u5b50\uff0c\u53ea\u6015\u5144\u5f1f\u7b49\u4e5f\u4e0d\u7518\u53d7\u8fb1\u3002\u201d\u8fd9\u7b49\u8272\u5389\u5185\u832c\u7684\u8bed\u6c14\uff0c\u76f4\u628a\u5b89\u5c0f\u840d\u542c\u5f97\u51e0\u4e4e\u7b11\u51fa\u58f0\u6765 \u3000\u3000\u4f46\u5979\u5e76\u672a\u771f\u7684\u7b11\u51fa\u6765\uff0c\u53ea\u56e0\u6b64\u65f6\u6b64\u5730\uff0c\u5979\u5230\u4e5f\u61c2\u5f97\u5982\u4f55\u5c3d\u91cf\u5730\u9690\u85cf\u950b\u8292\uff01 \u3000\u3000\u6e38\u516c\u7384\u95fb\u8a00\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u4f59\u6770\uff0c\u4f60\u53e3\u6c14\u4e0d\u5c0f\u554a\uff01\u201d \u3000\u3000\u90a3\u9a7c\u5b50\u59d3\u4f59\u540d\u6770\uff0c\u7ef0\u53f7\u201c\u5357\u5d16\u9a7c\u9f99\u201d\u548c\u4ed6\u8eab\u8fb9\u7684\u53e6\u4e00\u4f4d\u9ad8\u5927\u7684\u8001\u4eba\u201c\u5317\u5cf0\u5c0a\u8005\u201d\u674e\u5146\u975e\u4e43\u662f\u7ed3\u62dc\u7684\u5144\u5f1f\u3002 \u3000\u3000\u4f59\u6770\u4e24\u773c\u4e00\u77aa\u9053\uff1a\u201c\u53e3\u6c14\u4e0d\u5c0f\uff0c\u83ab\u975e\u53c8\u788d\u7740\u6e38\u5144\u4ec0\u4e48\u7684\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u8fd9\u5f53\u7136\u4e0d\u788d\u8001\u592b\uff0c\u53ea\u662f\u8001\u592b\u5230\u60f3\u77e5\u9053\u4e00\u70b9\uff01\u201d \u3000\u3000\u4f59\u6770\u9053\uff1a\u201c\u54ea\u4e00\u70b9\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u4e09\u4f4d\u8eb2\u8eb2\u85cf\u85cf\u7684\u6765\u5230\u4e86\u6d1b\u9633\uff0c\u90a3\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4f59\u6770\u6012\u9053\uff1a\u201c\u8bf4\u6211\u4eec\u8eb2\u8eb2\u85cf\u85cf\uff1f\u4f59\u6770\u7b49\u4e0d\u662f\u5750\u5728\u5e7f\u5ead\u5927\u4f17\u4e4b\u95f4\u5417\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u4eec\u6b64\u523b\u679c\u7136\u662f\u5750\u5728\u5927\u5ead\u5e7f\u4f17\u4e4b\u95f4\uff0c\u53ea\u662f\uff0c\u4f60\u4eec\u5fc3\u91cc\u5934\u60f3\u7684\u4ec0\u4e48\uff0c\u6562\u4e0d\u6562\u8bf4\u51fa\u6765\uff1f\u201d \u3000\u3000\u4f59\u6770\u6014\u4e86\u4e00\u6014\uff0c\u5c1a\u672a\u8bf4\u8bdd\uff0c\u5317\u5cf0\u5c0a\u8005\u674e\u5146\u4e30\u5ffd\u7136\u51b7\u7b11\u9053\uff1a\u201c\u6e38\u5144\uff0c\u4f60\u4e00\u8def\u4e0a\u8ddf\u7740\u54b1\u4eec\uff0c\u8fd9\u53c8\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6697\u9053\uff1a\u539f\u6765\u8fd9\u4f4d\u6e38\u8001\u524d\u8f88\u76ef\u5728\u4ed6\u4eec\u8eab\u540e\uff0c\u5df2\u7ecf\u5f88\u4e45\u4e86\uff0c\u602a\u4e0d\u5f97\u8fd9\u4e09\u4eba\u5f88\u607c\u706b\uff01\u770b\u6765\u6e38\u8001\u662f\u6709\u5fc3\u5728\u76d1\u89c6\u7740\u4ed6\u4eec\u7684\u4e86\uff01 \u3000\u3000\u6e38\u516c\u7384\u5374\u662f\u5475\u5475\u4e00\u7b11\u9053\uff1a\u201c\u674e\u8001\u5f1f\uff0c\u60f3\u4e0d\u5230\u4f60\u4e5f\u5b66\u6c5f\u77ee\u5b50\u88c5\u4f6f\u5417\uff1f\u5176\u5b9e\uff0c\u4ed6\u4eec\u5373\u4f7f\u4e0d\u80af\u660e\u8bf4\u51fa\u6765\uff0c\u8001\u592b\u4e5f\u77e5\u9053\u4f60\u4eec\u4e3a\u4f55\u800c\u6765\u7684\u4eba\uff01\u201d \u3000\u3000\u674e\u5146\u975e\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u77e5\u9053\u5417\uff1f\u600e\u7684\u4e0d\u8bf4\u51fa\u6765\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u7b11\u9053\uff1a\u201c\u8001\u592b\u662f\u8bf4\uff0c\u90a3\u4e94\u4e2a\u9b54\u5934\u9080\u8bf7\u4e86\u4f60\u4eec\u524d\u6765\u6d1b\u9633\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u5929\u53f0\u4e09\u7edd\u540c\u65f6\u8138\u8272\u4e00\u53d8\uff0c\u6c5f\u5929\u6653\u76b1\u7709\u63a5\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u4ed6\u8fd9\u7b49\u53cd\u95ee\uff0c\u4e0d\u557b\u5df2\u7136\u627f\u8ba4\u4e86\u6e38\u516c\u7384\u6ca1\u6709\u8bf4\u9519\u3002 \u3000\u3000\u6e38\u516c\u7384\u7b11\u9053\uff1a\u201c\u82e5\u8981\u4eba\u4e0d\u77e5\uff0c\u9664\u975e\u5df1\u83ab\u4e3a\uff0c\u6c5f\u8001\u5f1f\uff0c\u522b\u4eba\u77e5\u9053\u4e4b\u540e\uff0c\u4e5f\u8bb8\u4e0d\u6562\u5bf9\u4f60\u5982\u4f55\uff0c\u4f46\u8fd9\u7b49\u4e8b\u65e2\u88ab\u8001\u5927\u83b7\u6089\uff0c\u90a3\u81ea\u7136\u662f\u4e0d\u4f1a\u653e\u8fc7\u7684\u4e86\uff01\u201d \u3000\u3000\u6c5f\u5929\u6653\u9053\uff1a\u201c\u4f60\uff01\u6253\u7b97\u5982\u4f55\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u7b11\u9053\uff1a\u201c\u4e0d\u6253\u7b97\u5982\u4f55\uff01\u8001\u592b\u8981\u544a\u8bc9\u4f60\u4eec\uff0c\u5018\u662f\u4f60\u4eec\u771f\u60f3\u52a9\u90a3\u4e94\u4e2a\u4e0d\u6210\u624d\u7684\u9b54\u5934\uff0c\u8001\u592b\u5c31\u5c11\u4e0d\u5f97\u8981\u4f38\u624b\u7ba1\u4e0a\u4e00\u7ba1\u4e86\uff01\u201d \u3000\u3000\u6c5f\u5929\u6653\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u8981\u7ba1\u5417\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u4e0d\u9519\uff01\u8001\u592b\u6b63\u662f\u8981\u7ba1\u4e0a\u4e00\u7ba1\uff01\u201d \u3000\u3000\u4f59\u6770\u76ee\u5149\u4e00\u8f6c\uff0c\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u6e38\u5144\uff0c\u4f60\u53ef\u662f\u8ba4\u4e3a\u54b1\u4eec\u4e00\u5b9a\u6015\u4f60\u7ba1\u8fd9\u4ef6\u4e8b\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u8001\u592b\u5e76\u6ca1\u6709\u8981\u4f60\u4eec\u6015\uff01\u4f46\u4f60\u4eec\u5982\u60f3\u8001\u592b\u6492\u624b\u4e0d\u95ee\uff0c\u90a3\u5374\u662f\u529e\u4e0d\u5230\u7684\u4e86\uff01\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u5411\u6c5f\u5929\u6653\u63a5\u9053\uff1a\u201c\u6c5f\u77ee\u5b50\uff0c\u5018\u82e5\u4f60\u4eec\u60f3\u4fdd\u6301\u4e00\u4e16\u7f8e\u540d\uff0c\u8001\u592b\u5230\u6709\u4e00\u8a00\u76f8\u544a\uff01\u201d \u3000\u3000\u6c5f\u5929\u6653\u9053\uff1a\u201c\u4ec0\u4e48\u8bdd\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u957f\u6c5f\u540e\u6d6a\u63a8\u524d\u6d6a\uff0c\u4e00\u4ee3\u65b0\u4eba\u6362\u65e7\u4eba\uff01\u8001\u5f1f\u77e5\u9053\u8fd9\u53e5\u8bdd\u7684\u610f\u601d\u5417\uff1f\u201d \u3000\u3000\u6c5f\u5929\u6653\u9053\uff1a\u201c\u8fd9\u4e0e\u6c5f\u67d0\u7b49\u4eba\u4f55\u5173\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u5f53\u7136\u6709\u5173\uff01\u201d\u4ed6\u7565\u4e00\u6c89\u541f\uff0c\u63a5\u9053\uff1a\u201c\u6b66\u6797\u4eba\u7269\u7684\u76db\u8870\uff0c\u4e5f\u6709\u5b9a\u6570\uff0c\u6240\u8c13\u6c5f\u5c71\u4ee3\u6709\u624d\u4eba\u51fa\uff0c\u5404\u9886\u98ce\u9a9a\u4e94\u767e\u5e74\uff0c\u8001\u5f1f\uff0c\u54b1\u4eec\u5df2\u7ecf\u8001\u4e86\uff0c\u6709\u8bb8\u591a\u4e8b\uff0c\u5927\u53ef\u4e0d\u7528\u51fa\u5934\u9732\u9762\u7684\u4e86\uff01\u201d \u3000\u3000\u6c5f\u5929\u6653\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u6e38\u5144\u8fd9\u662f\u4f55\u610f\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u8001\u592b\u5949\u529d\u4f60\u4eec\uff0c\u4e0d\u8981\u53bb\u52a9\u90a3\u4e94\u4e2a\u4e0d\u6210\u624d\u7684\u9b54\u5934\uff0c\u514d\u5f97\u5f04\u5f97\u8eab\u8d25\u540d\u88c2\uff01\u201d \u3000\u3000\u4f59\u6770\u4e00\u6014\u9053\uff1a\u201c\u4f60\u8fd9\u2026\u2026\u662f\u8bf4\uff0c\u6b66\u6797\u7684\u4e0b\u4e00\u4ee3\u4e4b\u4e2d\uff0c\u53c8\u6709\u4e86\u4ec0\u4e48\u6770\u51fa\u7684\u9ad8\u624b\uff0c\u5f3a\u8fc7\u4f59\u67d0\u7b49\u4eba\u5417\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u4e0d\u9519\uff01\u8001\u592b\u6b63\u662f\u6b64\u610f\uff01\u201d \u3000\u3000\u674e\u5146\u4e30\u51b7\u7b11\u9053\uff1a\u201c\u674e\u67d0\u4e0d\u4fe1\u2026\u2026\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u4f60\u4e0d\u4fe1\u5417\uff1f\u8001\u592b\u544a\u8bc9\u4f60\uff0c\u8fd9\u4e43\u662f\u5343\u771f\u4e07\u786e\u4e4b\u4e8b\uff0c\u90a3\u5c31\u7b97\u8001\u592b\u6ca1\u6709\u8bf4\u4fbf\u662f\u4e86\uff01\u201d \u3000\u3000\u6c5f\u5929\u6653\u89c1\u4ed6\u795e\u8272\u751a\u4e3a\u6b63\u7ecf\uff0c\u4e0d\u4f3c\u73a9\u7b11\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u8001\u5144\u5411\u6765\u8bf4\u8bdd\u90fd\u662f\u563b\u563b\u54c8\u54c8\uff0c\u4eca\u65e5\u8fd9\u7b49\u90d1\u91cd\uff0c\u53ea\u6015\u4e0d\u662f\u73a9\u7b11\u2026\u2026\u201d\u5f53\u4e0b\u63a5\u9053\uff1a\u201c\u6e38\u5144\uff0c\u4f60\u2026\u2026\u53ef\u77e5\u9053\u90a3\u4eba\u53eb\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6562\u60c5\u4ed6\u5df2\u7136\u76f8\u4fe1\u4e86\u6e38\u516c\u7384\u4e4b\u8a00\uff0c\u6b66\u6797\u7684\u4e0b\u4e00\u4ee3\u4e2d\uff0c\u5df2\u7136\u51fa\u4e86\u9876\u5c16\u7684\u9ad8\u624b\u3002 \u3000\u3000\u6c5f\u5929\u6653\u8bed\u97f3\u752b\u843d\uff0c\u6e38\u516c\u7384\u7acb\u5373\u63a5\u9053\uff1a\u201c\u4f60\u4eec\u53ef\u662f\u60f3\u95ee\u51fa\u4ed6\u7684\u540d\u53f7\uff0c\u8bbe\u6cd5\u53bb\u6697\u7b97\u4eba\u5bb6\u5417\uff1f\u201d \u3000\u3000\u6c5f\u5929\u6653\u76b1\u7709\u9053\uff1a\u201c\u5144\u5f1f\u5c82\u662f\u90a3\u7b49\u6697\u7bad\u4f24\u4eba\u4e4b\u5f92\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u65e2\u7136\u4f60\u4eec\u4e0d\u4f1a\u6709\u90a3\u6697\u7bad\u4f24\u4eba\u4e4b\u610f\uff0c\u53c8\u4f55\u5fc5\u8981\u67e5\u95ee\u4eba\u5bb6\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u6c5f\u5929\u6653\u9053\uff1a\u201c\u5144\u5f1f\u53ea\u662f\u6709\u4e9b\u597d\u5947\u548c\u4e0d\u76f8\u4fe1\u800c\u5df2\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u5927\u7b11\u9053\uff1a\u201c\u76f8\u4fe1\u4e0d\u76f8\u4fe1\uff0c\u90a3\u662f\u4f60\u7684\u4e8b\uff01\u81f3\u4e8e\u597d\u5947\u5417\uff1f\u8001\u592b\u8ba4\u4e3a\u4f60\u5927\u53ef\u4e0d\u5fc5\u4e86\uff01\u201d \u3000\u3000\u674e\u5146\u4e30\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u5417\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u5929\u4e0b\u6709\u8bb8\u591a\u4eba\u4e00\u4e16\u82f1\u540d\uff0c\u5728\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u95f4\u4e27\u5931\uff0c\u7a76\u5176\u539f\u56e0\uff0c\u4e00\u534a\u90fd\u662f\u56e0\u4e3a\u597d\u5947\u4e4b\u6545\uff01\u201d \u3000\u3000\u674e\u5146\u4e30\u9053\uff1a\u201c\u8fd9\u4e0e\u6211\u7b49\u4f55\u5173\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u771f\u7684\u65e0\u5173\uff1f\u5018\u662f\u4f60\u4eec\u4e5f\u597d\u5947\uff0c\u53ea\u6015\u7ed3\u679c\u4e5f\u8981\u65ad\u9001\u4e00\u4e16\u82f1\u540d\u4e86\uff01\u201d \u3000\u3000\u6c5f\u5929\u6653\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u6e38\u5144\uff0c\u4f60\u8fd9\u8bdd\u5230\u53eb\u5144\u5f1f\u8d8a\u53d1\u751f\u51fa\u597d\u5947\u4e4b\u5fc3\u4e86\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u4f60\u771f\u662f\u4e0d\u6015\u4e00\u4e16\u82f1\u540d\uff0c\u4ed8\u8bf8\u6d41\u6c34\u5417\uff1f\u201d \u3000\u3000\u6c5f\u5929\u6653\u7b11\u9053\uff1a\u201c\u5144\u5f1f\u4e0d\u4fe1\u6b66\u6797\u4e2d\u4f1a\u5728\u7a81\u7136\u4e4b\u95f4\uff0c\u51fa\u73b0\u4e86\u90a3\u7b49\u9ad8\u624b\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u8001\u592b\u4e5f\u77e5\u9053\u4f60\u4eec\u4e0d\u4f1a\u76f8\u4fe1\uff0c\u770b\u6765\u8001\u592b\u8fd9\u756a\u5fc3\u8840\uff0c\u5c06\u662f\u767d\u8d39\u4e86\uff01\u201d \u3000\u3000\u6c5f\u5929\u6653\u7b11\u9053\uff1a\u201c\u6e38\u5144\u76db\u60c5\uff0c\u8001\u5f1f\u7b49\u5fc3\u9886\u4e86\uff01\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u90a3\u4e09\u4eba\u4e2d\u9a7c\u80cc\u8001\u4eba\u5ffd\u7136\u4e00\u7b11\u9053\uff1a\u201c\u6e38\u5144\uff0c\u4f59\u67d0\u6709\u4e00\u53e5\u8bdd\uff0c\u4e0d\u77e5\u5f53\u4e0d\u5f53\u8bf4\u8bdd\u51fa\u6765\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u5357\u5d16\u9a7c\u9f99\u4f59\u6770\uff0c\u5411\u6765\u662f\u4e00\u8a00\u4e5d\u9f0e\u4e4b\u4e0a\uff0c\u4f60\u5982\u4e5f\u6709\u4ec0\u4e48\u8bdd\u8981\u8bf4\uff0c\u90a3\u5fc5\u662f\u5341\u5206\u91cd\u8981\u7684\u4e86\uff01\u201d \u3000\u3000\u4f59\u6770\u7b11\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u6e38\u5144\u8fc7\u5956\u4e86\uff01\u201d\u4ed6\u8bdd\u97f3\u4e00\u987f\uff0c\u76ee\u5149\u5728\u8eab\u4fa7\u4e94\u4eba\u8eab\u4e0a\u4e00\u8f6c\uff0c\u63a5\u9053\uff1a \u3000\u3000\u201c\u6e38\u5144\uff0c\u5144\u5f1f\u7b49\u5929\u53f0\u4e09\u7edd\uff0c\u5728\u6b66\u6797\u4e2d\u4e00\u5411\u8584\u6709\u6e05\u8a89\uff0c\u4ece\u6765\u4e0d\u66fe\u4f5c\u8fc7\u4ec0\u4e48\u4f24\u5929\u5bb3\u7406\u4e4b\u4e8b\uff0c\u8fd9\u4e00\u70b9\u60f3\u5fc5\u6e38\u5144\u4e00\u5b9a\u5f88\u6e05\u695a\u7684\u4e86\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u7b11\u9053\uff1a\u201c\u8001\u592b\u82e5\u4e0d\u660e\u767d\uff0c\u90a3\u4e5f\u4e0d\u4f1a\u8d76\u6765\u591a\u4e8b\u7684\u4e86\uff01\u201d \u3000\u3000\u4f59\u6770\u9053\uff1a\u201c\u6e38\u5144\u65e2\u662f\u660e\u767d\uff0c\u90a3\u4e5f\u8be5\u77e5\u9053\uff0c\u5144\u5f1f\u7b49\u4e09\u4eba\u6765\u6b64\uff0c\u4e43\u662f\u6709\u7740\u4e0d\u5f97\u5df2\u7684\u82e6\u8877\u4e86\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u4ec0\u4e48\u82e6\u8877\uff1f\u201d \u3000\u3000\u4f59\u6770\u9053\uff1a\u201c\u53d7\u5236\u4e8e\u4eba\uff0c\u591f\u4e0d\u591f\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1b\u201c\u4f60\u4eec\u53d7\u5236\u4e8e\u4eba\uff1f\u8c01\u6709\u8fd9\u7b49\u80fd\u8010\uff1f\u201d \u3000\u3000\u4f59\u7070\u9053\uff1a\u201c\u8bf4\u6765\u5e94\u662f\u5341\u5206\u4e22\u4eba\u4e4b\u4e8b\uff0c\u4f46\u5728\u6e38\u5144\u9762\u524d\uff0c\u8001\u5f1f\u4e5f\u53ea\u597d\u76f4\u8bf4\u4e86\u2026\u2026\u201d\u4ed6\u5ffd\u7136\u957f\u53f9\u4e00\u58f0\uff0c\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u6e38\u5144\uff0c\u54b1\u4eec\u5144\u5f1f\u4e00\u5411\u9690\u5c45\u5929\u53f0\uff0c\u4e0d\u95ee\u4e16\u4e8b\uff0c\u600e\u77e5\u6570\u6708\u4e4b\u524d\uff0c\u90a3\u5929\u9b54\u5973\u7adf\u7136\u4e0a\u5c71\u627e\u4e0a\u54b1\u4eec\u5144\u5f1f\uff0c\u786c\u903c\u54b1\u4eec\u4e0b\u5c71\u76f8\u52a9\u4e8e\u5979\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u66fe\u62d2\u7edd\u5417\uff1f\u201d \u3000\u3000\u4f59\u6770\u9053\uff1a\u201c\u5144\u5f1f\u7b49\u4eba\u5c82\u6709\u4e0d\u62d2\u7edd\u4e4b\u7406\uff1f\u53ea\u662f\uff0c\u90a3\u5929\u9b54\u5973\u7adf\u7136\u51b7\u7b11\u8fde\u58f0\u544a\u8bc9\u54b1\u4eec\uff0c\u5982\u679c\u6211\u4eec\u4e0d\u7b54\u5e94\u4e0b\u5c71\u76f8\u52a9\uff0c\u4e0d\u51fa\u4e00\u6708\uff0c\u5373\u5c06\u4e27\u547d\u5929\u53f0\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u6709\u8fd9\u79cd\u4e8b\uff1f\u201d \u3000\u3000\u4f59\u6770\u9053\uff1a\u201c\u5144\u5f1f\u7b49\u4eba\u5148\u524d\u4e5f\u662f\u4e0d\u4fe1\uff0c\u4f46\u7ecf\u5929\u9b54\u5973\u8bf4\u51fa\u5176\u4e2d\u539f\u59d4\uff0c\u54b1\u4eec\u5374\u662f\u4e0d\u5f97\u4e0d\u4fe1\u4e86\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4f59\u6770\u9053\uff1a\u201c\u90a3\u5996\u5973\u5728\u6211\u7b49\u8eab\u4e0a\uff0c\u65e9\u5df2\u505a\u4e86\u624b\u811a\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u76b1\u7709\u9053\uff1a\u201c\u4f60\u4eec\u2026\u2026\u600e\u4f1a\u88ab\u5979\u505a\u4e86\u624b\u811a\uff1f\u5947\u602a\u554a\uff0c\u5947\u602a\uff01\u201d \u3000\u3000\u4f59\u6770\u957f\u957f\u4e00\u53f9\u9053\uff1a\u201c\u8bf4\u6765\u4e5f\u662f\uff0c\u82e5\u975e\u90a3\u5996\u5973\u8bf4\u660e\uff0c\u54b1\u4eec\u771f\u7684\u8981\u8499\u5728\u9f13\u91cc\u4e86\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u5929\u9b54\u5973\u662f\u4e0d\u662f\u5728\u4f60\u4eec\u8eab\u4e0a\u4e0b\u4e86\u6bd2\uff1f\u201d \u3000\u3000\u4f59\u6770\u9053\uff1a\u201c\u90a3\u5996\u5973\u4e70\u901a\u4e86\u5144\u5f1f\u7b49\u4eba\u7684\u53a8\u5e08\uff0c\u662f\u4ee5\uff0c\u54b1\u4eec\u6bcf\u65e5\u98df\u7528\u4e4b\u7269\uff0c\u90fd\u88ab\u90a3\u5996\u5973\u4e0b\u4e86\u5c11\u91cf\u7684\u6bd2\u7269\u5728\u5185\uff01\u201d \u3000\u3000\u6e38\u7384\u516c\u5ffd\u7136\u60ca\u54e6\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u662f\u4e86\uff01\u90a3\u5996\u5973\u7684\u5fc3\u673a\uff0c\u5f53\u771f\u662f\u5389\u5bb3\u5f97\u5f88\uff0c\u5979\u4e0d\u66fe\u4e0b\u591f\u6bd2\u7269\u7684\u4efd\u91cf\uff0c\u5c31\u662f\u5bb3\u6015\u4f60\u4eec\u5728\u5e73\u65e5\u7ec3\u529f\u4e4b\u65f6\uff0c\u5bdf\u89c9\u51fa\u6709\u4ec0\u4e48\u4e0d\u5bf9\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u4f59\u6770\u9053\uff1a\u201c\u6b63\u662f\u5982\u6b64\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u4eec\u81ea\u7136\u662f\u5c1a\u672a\u83b7\u5f97\u89e3\u836f\u4e86\uff1f\u201d \u3000\u3000\u4f59\u6770\u9053\uff1a\u201c\u53ef\u4e0d\uff01\u90a3\u5996\u5973\u5e94\u5141\u54b1\u4eec\u5144\u5f1f\uff0c\u5728\u5979\u72ec\u9738\u6b66\u6797\uff0c\u5236\u670d\u4e86\u4e5d\u5927\u95e8\u6d3e\u4e4b\u65f6\uff0c\u4fbf\u662f\u89e3\u9664\u54b1\u4eec\u8eab\u4e0a\u6bd2\u7269\u4e4b\u65f6\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u5ffd\u7136\u5927\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u90a3\u5929\u9b54\u5973\u8bf4\u7684\u5417\uff1f\u201d \u3000\u3000\u4f59\u6770\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u4f59\u5144\uff0c\u4f60\u4eec\u679c\u7136\u662f\u7cca\u6d82\u5f97\u5f88\uff01\u201d \u3000\u3000\u6c5f\u5929\u6653\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u5144\u5f1f\u7b49\u4eba\u7cca\u6d82\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u90a3\u5929\u9b54\u5973\u7684\u8bdd\uff0c\u4f60\u4eec\u76f8\u4fe1\u5417\uff1f\u201d \u3000\u3000\u6c5f\u5927\u6653\u9053\uff1a\u201c\u6e38\u5144\uff0c\u8fd9\u7b49\u4e8b\u53d7\u5236\u4e8e\u522b\u4eba\u4e4b\u4e0b\uff0c\u54b1\u4eec\u53c8\u600e\u80fd\u4e0d\u4fe1\u5462\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u4f60\u4eec\u76f8\u4fe1\uff0c\u8001\u592b\u53ef\u5c31\u4e0d\u4fe1\u4e86\uff01\u201d \u3000\u3000\u6c5f\u5929\u6653\u6014\u6014\u5730\u9053\uff1a\u201c\u6e38\u5144\u4e0d\u4fe1\u5417\uff1f\u90a3\u53c8\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u90a3\u5929\u9b54\u5973\u5229\u7528\u4e86\u4e09\u4f4d\u4e4b\u540e\uff0c\u5979\u4e3a\u4f55\u8fd8\u8981\u7ed9\u4f60\u4eec\u89e3\u836f\uff1f\u5154\u6b7b\u72d7\u70f9\uff0c\u4f60\u4eec\u90fd\u4e0d\u660e\u767d\u5417\uff1f\u201d \u3000\u3000\u6c5f\u5929\u6653\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u6e38\u5144\u4e4b\u610f\uff0c\u90a3\u662f\u8bf4\u5929\u9b54\u5973\u5728\u6210\u529f\u4e4b\u540e\uff0c\u4f9d\u7136\u4e0d\u66fe\u7ed9\u6211\u4eec\u89e3\u836f\u4e86\uff1f\u201d \u3000\u3000\u6c5f\u5929\u6653\u770b\u4e86\u674e\u5146\u4e30\u3001\u4f59\u6770\u4e24\u4eba\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5144\u5f1f\uff01\u6e38\u5144\u4e4b\u8bf4\u679c\u7136\u6709\u7406\uff0c\u53ea\u6015\u54b1\u4eec\u771f\u7684\u8981\u683d\u5728\u90a3\u5996\u5973\u624b\u4e2d\u4e86\uff01\u201d \u3000\u3000\u674e\u5146\u4e30\u9053\uff1a\u201c\u6c5f\u5144\u6709\u4f55\u9ad8\u89c1\uff1f\u5144\u5f1f\u548c\u4f59\u5144\u4e00\u5b9a\u9075\u547d\u884c\u4e8b\u4fbf\u662f\u3002\u201d \u3000\u3000\u6c5f\u5929\u6653\u9053\uff1a\u201c\u8fd9\u4e8b\u5982\u679c\u771f\u5982\u6e38\u5144\u6240\u6599\uff0c\u5219\u54b1\u4eec\u4e0d\u4f46\u66ff\u90a3\u5996\u5973\u5356\u529b\uff0c\u7ed3\u679c\u5fc5\u7136\u8fd8\u6709\u8981\u843d\u5f97\u4e2a\u6bd2\u53d1\u8eab\u6b7b\u7684\u7ed3\u5c40\uff0c\u90a3\u54b1\u4eec\u53c8\u4f55\u5fc5\u5c06\u4e00\u4e16\u6e05\u767d\u58f0\u8a89\uff0c\u65ad\u9001\u5728\u8fd9\u5996\u5973\u624b\u4e2d\uff1f\u201d \u3000\u3000\u4f59\u6770\u9053\uff1a\u201c\u6c5f\u5144\u4e4b\u610f\uff0c\u90a3\u662f\u54b1\u4eec\u4e0d\u7528\u518d\u4e3a\u8fd9\u5996\u5973\u51fa\u9762\u548c\u4e5d\u5927\u95e8\u6d3e\u4e3a\u654c\u4e86\uff1f\u201d \u3000\u3000\u6c5f\u5929\u6653\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u54b1\u4eec\u6b63\u662f\u4e0d\u7528\u518d\u4e3a\u5979\u5356\u529b\u4e86\uff01\u201d \u3000\u3000\u4f59\u6770\u9053\uff1a\u201c\u6c5f\u5144\uff0c\u90a3\u54b1\u4eec\u8eab\u4e0a\u6240\u4e2d\u7684\u6bd2\u7269\u5462\uff1f\u201d \u3000\u3000\u6c5f\u5929\u6653\u9053\uff1a\u201c\u6162\u6162\u8bbe\u6cd5\uff0c\u603b\u6709\u89e3\u6bd2\u4e4b\u65e5\uff01\u201d \u3000\u3000\u4f59\u6770\u6c89\u541f\u9053\uff1a\u201c\u6c5f\u5144\uff0c\u65e2\u662f\u4f60\u4f5c\u4e86\u8fd9\u7b49\u51b3\u5b9a\uff0c\u5144\u5f1f\u548c\u674e\u5144\u81ea\u7136\u6ca1\u6709\u8bdd\u8bf4\u4e86\u2026\u2026\u201d \u3000\u3000\u6e38\u516c\u7384\u7b11\u9053\uff1a\u201c\u4e09\u4f4d\u5230\u5e95\u662f\u660e\u767d\u4eba\uff0c\u8001\u592b\u4e00\u70b9\uff0c\u4f60\u4eec\u5c31\u77e5\u6089\u5176\u4e2d\u5229\u5bb3\uff0c\u5144\u5f1f\u76f8\u4fe1\uff0c\u4f60\u4eec\u8eab\u4e0a\u6240\u4e2d\u4e4b\u6bd2\uff0c\u4ea6\u5fc5\u80fd\u591f\u5f88\u5feb\u5316\u89e3\u5f97\u4e86\uff01\u201d \u3000\u3000\u6c5f\u5929\u6653\u5ffd\u7136\u5475\u5475\u4e00\u7b11\u9053\uff1a\u201c\u6e38\u5144\uff0c\u4e00\u4e2a\u4eba\u7eb5\u7136\u6d3b\u4e86\u5343\u5c81\uff0c\u4e5f\u5c06\u96be\u514d\u4e00\u6b7b\uff0c\u5144\u5f1f\u7b49\u8eab\u4e0a\u4e4b\u6bd2\uff0c\u5316\u89e3\u4e0d\u5316\u89e3\u5f97\u4e86\uff0c\u90a3\u4e5f\u6ca1\u6709\u591a\u5927\u7684\u5173\u7cfb\uff0c\u4f46\u90a3\u5929\u9b54\u5973\u72ec\u9738\u6b66\u6797\u4e4b\u5fc3\uff0c\u54b1\u4eec\u5374\u4e0d\u80fd\u5750\u89c6\u4e0d\u7ba1\u7684\u4e86\u2026\u2026\u201d \u3000\u3000\u8a00\u4e0b\u4e4b\u610f\uff0c\u5230\u4f3c\u662f\u8981\u53cd\u4e0e\u5929\u9b54\u5973\u4e3a\u4ec7\u4e86\uff01 \u3000\u3000\u6e38\u516c\u7384\u95fb\u8a00\u7b11\u9053\uff1a\u201c\u8001\u5144\u5f1f\uff0c\u4f60\u8fd9\u662f\u4f55\u610f\uff1f\u201d \u3000\u3000\u6c5f\u5929\u6653\u7b11\u9053\uff1a\u201c\u6e38\u5144\u9002\u624d\u7684\u4e00\u756a\u8bdd\uff0c\u5374\u4f7f\u5144\u5f1f\u8305\u585e\u987f\u5f00\uff0c\u90a3\u5929\u9b54\u5973\u5982\u662f\u5f53\u771f\u8981\u4e3a\u7978\u6b66\u6797\uff0c\u54b1\u4eec\u4e5f\u4e0d\u80fd\u5750\u89c6\u4e0d\u7ba1\u554a\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u5927\u7b11\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u4e09\u4f4d\u662f\u8981\u5012\u6208\u76f8\u5411\u7684\u4e86\uff1f\u201d \u3000\u3000\u6c5f\u5929\u6653\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5144\u5f1f\u7b49\u6b63\u6709\u6b64\u5fc3\uff01\u201d \u3000\u3000\u6e38\u5929\u7384\u9053\uff1a\u201c\u90a3\u5f88\u597d\uff0c\u8001\u592b\u4e5f\u7b97\u4e0d\u6789\u6b64\u884c\u4e86\uff01\u201d \u3000\u3000\u6c5f\u5929\u6653\u9053\uff1a\u201c\u6e38\u5144\u5c31\u662f\u4e3a\u4e86\u6211\u7b49\u8d76\u6765\u7684\u5417\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u4e3a\u4e86\u4f60\u4eec\uff0c\u8001\u592b\u6765\u6b64\u4f5c\u751a\uff1f\u201d \u3000\u3000\u6c5f\u5929\u6653\u957f\u53f9\u4e00\u58f0\u9053\uff1a\u201c\u6e38\u5144\uff0c\u8fd9\u4e48\u8bf4\u6765\uff0c\u5144\u5f1f\u7b49\u771f\u662f\u60ed\u6127\u7684\u5f88\u4e86\u2026\u2026\u201d \u3000\u3000\u6e38\u516c\u7384\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u5fc5\u4e86\uff01\u53ea\u8981\u4f60\u4eec\u4e00\u5fc3\u5411\u5584\uff0c\u8001\u592b\u5373\u662f\u591a\u8dd1\u4e9b\u8def\uff0c\u591a\u51fa\u4e9b\u6c57\uff0c\u90a3\u4e5f\u4e0d\u7b97\u4ec0\u4e48\uff01\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u5411\u90a3\u53e6\u5916\u4e94\u4f4d\u770b\u4e86\u4e00\u773c\uff0c\u63a5\u9053\uff1a\u201c\u6c5f\u8001\u5f1f\uff0c\u8fd9\u4e94\u4f4d\u4f60\u53ef\u8ba4\u8bc6\u3002\u201d \u3000\u3000\u6c5f\u5929\u6653\u9053\uff1a\u201c\u4e0d\u66fe\u89c1\u8fc7\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u4e00\u6014\u9053\uff0c\u201c\u4e0d\u8ba4\u8bc6\uff1f\u201d \u3000\u3000\u6c5f\u5929\u6653\u9053\uff1a\u201c\u6b63\u662f\u4e0d\u8ba4\u8bc6\u3002\u201d \u3000\u3000\u6e38\u516c\u7384\u6c89\u541f\u9053\uff1a\u201c\u65e2\u662f\u4e0d\u8bc6\uff0c\u4f60\u4e3a\u4f55\u8ddf\u4ed6\u4eec\u5750\u5728\u4e00\u8d77\uff1f\u201d \u3000\u3000\u6c5f\u5929\u6653\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u5144\u5f1f\u548c\u90a3\u9b54\u5973\u7ea6\u5b9a\u8d76\u6765\u6d1b\u9633\uff0c\u5c31\u5728\u8fd9\u9152\u697c\u4e4b\u4e0a\u89c1\u9762\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u90a3\u4e0e\u4ed6\u4eec\u4f55\u5173\uff1f\u201d \u3000\u3000\u6c5f\u5929\u6653\u5ffd\u7136\u6307\u7740\u684c\u4e0a\u4e00\u53ea\u5012\u6263\u7684\u74f7\u7897\uff0c\u548c\u6446\u5728\u7897\u4e0a\u7684\u4e00\u53ea\u7af9\u7b77\uff0c\u63a5\u9053\uff1a\u201c\u8fd9\u662f\u54b1\u4eec\u7ea6\u5b9a\u89c1\u9762\u7684\u6697\u53f7\uff0c\u5144\u5f1f\u7b49\u4e0a\u5f97\u697c\u4e0a\uff0c\u89c1\u5230\u8fd9\u7b49\u6697\u8bb0\uff0c\u81ea\u7136\u662f\u8981\u5750\u8fc7\u6765\u7684\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u4ed6\u4eec\u4e94\u4eba\u662f\u5929\u9b54\u5973\u7684\u5c5e\u4e0b\uff1f\u201d \u3000\u3000\u6c5f\u5929\u6653\u6447\u5934\u9053\uff1a\u201c\u4e0d\u662f\uff01\u201d \u3000\u3000\u8fd9\u65f6\u65b9\u96ea\u5b9c\u4e0d\u7981\u89c9\u5f97\u5927\u611f\u5947\u602a\uff0c\u770b\u4e86\u90d1\u5927\u521a\u4e00\u773c\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u5f88\u5947\u602a\uff1f\u90a3\u4e94\u4eba\u5982\u82e5\u4e0d\u662f\u5929\u9b54\u5973\u624b\u4e0b\uff0c\u4e3a\u4f55\u4ed6\u4eec\u6446\u51fa\u90a3\u7b49\u6697\u8bb0\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u6447\u4e86\u6447\u5934\uff0c\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u5374\u9053\uff1a\u201c\u662f\u554a\uff01\u8fd9\u4e8b\u679c\u7136\u6709\u4e9b\u5947\u602a\uff0c\u4f46\u90a3\u59d3\u6c5f\u7684\u597d\u50cf\u8bf4\u7684\u4e0d\u662f\u5047\u8bdd\u2026\u2026\u201d \u3000\u3000\u5979\u8bdd\u97f3\u672a\u5df2\uff0c\u53ea\u89c1\u6e38\u516c\u7384\u5728\u6c89\u541f\u4e86\u4e00\u4f1a\u513f\u4e4b\u540e\uff0c\u7a81\u7136\u5927\u7b11\u9053\uff1a\u201c\u8001\u592b\u660e\u767d\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6014\u4e86\u4e00\u6014\uff0c\u6b62\u4f4f\u4e86\u8bdd\u58f0\uff0c\u53ea\u542c\u5f97\u6e38\u516c\u7384\u63a5\u9053\uff1a\u201c\u4ed6\u4eec\u53ea\u6015\u4e5f\u548c\u4f60\u4eec\u4e00\u822c\uff0c\u4e5f\u662f\u5e94\u7ea6\u6765\u6b64\u4e4b\u4eba\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u8fd9\u65f6\u5411\u65b9\u96ea\u5b9c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u65b9\u5927\u54e5\uff0c\u4ed6\u4eec\u731c\u4e86\u534a\u5929\uff0c\u5927\u6982\u662f\u731c\u5bf9\u4e86\uff01\u53ea\u662f\uff0c\u6709\u4e00\u70b9\u6211\u5374\u4e0d\u4e86\u89e3\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u54ea\u4e00\u70b9\u4e0d\u89e3\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4ed6\u4eec\u95ee\u6765\u95ee\u53bb\uff0c\u4e3a\u4f55\u4e0d\u76f4\u63a5\u95ee\u90a3\u4e94\u4eba\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5931\u58f0\u7b11\u9053\uff1a\u201c\u662f\u554a\uff0c\u4ed6\u4eec\u770b\u6765\u4e0d\u5982\u8d24\u5f1f\u806a\u654f\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5ffd\u7136\u8138\u4e0a\u4e00\u7ea2\uff0c\u4f4e\u4e0b\u5934\u53bb\u3002\u6562\u60c5\uff0c\u5979\u88ab\u65b9\u96ea\u5b9c\u79f0\u8d5e\u7684\u6709\u4e9b\u7f9e\u60ed\uff0c\u4f46\u5374\u4e4f\u7740\u7b11\u610f\uff0c\u90a3\u662f\u8868\u793a\u4e86\u5185\u5fc3\u6b22\u60a6\u3002 \u3000\u3000\u90d1\u5927\u521a\u76ee\u7779\u65af\u60c5\uff0c\u5fc3\u4e2d\u5927\u662f\u9ad8\u5174\uff0c\u6697\u9053\uff1a\u201c\u5982\u662f\u8fd9\u4e24\u4e2a\u5b69\u5b50\u80fd\u591f\u957f\u76f8\u53ae\u5b88\uff0c\u90a3\u771f\u662f\u6b66\u6797\u4e2d\u7684\u4e00\u5927\u4f73\u8bdd\u2026\u2026\u201d \u3000\u3000\u4ed6\u601d\u5fd6\u4e4b\u95f4\uff0c\u5ffd\u7136\u542c\u5f97\u90a3\u6e38\u516c\u7384\u6717\u7b11\u4e4b\u58f0\u4f20\u6765\u3002 \u3000\u3000\u90d1\u5927\u521a\u8fc5\u5feb\u5730\u5b9a\u775b\u770b\u53bb\uff0c\u53ea\u89c1\u90a3\u4e94\u4eba\u4e2d\u4e00\u4eba\uff0c\u7adf\u662f\u7ad9\u4e86\u8d77\u6765\uff0c\u5bf9\u90a3\u6e38\u516c\u7384\u6012\u76ee\u77aa\u89c6\uff0c\u8fd9\u4eba\u8eab\u5b50\u6781\u9ad8\uff0c\u5927\u7ea6\u4e0d\u4e0b\u516b\u5c3a\uff0c\u662f\u4ee5\u770b\u4e0a\u53bb\u8981\u6bd4\u6e38\u516c\u7384\u9ad8\u51fa\u4e24\u5c3a\u591a\u6709\u4f59\u3002 \u3000\u3000\u6e38\u516c\u7384\u7b11\u58f0\u4e00\u655b\uff0c\u5411\u90a3\u5927\u6c49\u9053\uff1a\u201c\u4f60\u53ef\u662f\u4e2a\u51fa\u5bb6\u4eba\u5417\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u95fb\u8a00\u4e00\u6014\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u4eba\u600e\u4f1a\u662f\u4e2a\u51fa\u5bb6\u4eba\u3002\u201d \u3000\u3000\u8bdd\u97f3\u672a\u5df2\uff0c\u53ea\u89c1\u90a3\u5927\u6c49\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6d12\u5bb6\u5927\u80dc\uff01\u201d \u3000\u3000\u539f\u6765\u8fd9\u4f4d\u8eab\u6750\u9ad8\u5927\u4e4b\u4eba\uff0c\u6b63\u662f\u4e00\u4e2a\u50e7\u4fa3\u3002 \u3000\u3000\u6e38\u516c\u7384\u626c\u7709\u9053\uff1a\u201c\u5728\u80dc\u5934\u9640\u5417\uff1f\u201d \u3000\u3000\u5927\u80dc\u5934\u9640\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u6e38\u516c\u7384\u76ee\u5149\u5728\u53e6\u5916\u56db\u4e2a\u8eab\u4e0a\u4e00\u8f6c\uff0c\u7b11\u9053\uff1a\u201c\u4e94\u4f4d\u60f3\u5fc5\u662f\u4e1c\u6d77\u4e94\u5996\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e1c\u6d77\u4e94\u5996\u201d\u56db\u5b57\u5165\u8033\uff0c\u90d1\u5927\u521a\u8138\u8272\u4e00\u53d8\uff0c\u5931\u58f0\u9053\uff1a\u201c\u6211\u65e9\u5c31\u8be5\u60f3\u51fa\u4ed6\u4eec\u4e94\u4eba\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5927\u4f2f\uff0c\u4f60\u8ba4\u8bc6\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4e0d\u8ba4\u8bc6\uff0c\u4f46\u611a\u53d4\u5374\u662f\u77e5\u6653\u8fd9\u4e94\u4eba\u7684\u4e00\u5207\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u5927\u4f2f\uff0c\u4ed6\u4eec\u662f\u574f\u4eba\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u8fd9\u4e1c\u6d77\u4e94\u5996\u7684\u4e3a\u4eba\uff0c\u679c\u7136\u751a\u574f\uff01\u8d24\u4f84\u5973\u4e00\u76f4\u5c45\u4f4f\u4e1c\u6d77\uff0c\u7adf\u672a\u542c\u4eba\u8bf4\u8fc7\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4ed6\u4eec\u4e94\u4eba\u5728\u6b66\u6797\u4e2d\u53e3\u7891\u6781\u574f\uff01\u4e09\u7537\u4e8c\u5973\uff0c\u65e0\u6076\u4e0d\u4f5c\uff0c\u800c\u4e14\u6bcf\u4eba\u90fd\u6709\u7740\u8fc7\u4eba\u7684\u6b66\u529f\uff0c\u662f\u4ee5\uff0c\u867d\u7136\u4ed6\u4eec\u4f5c\u7684\u574f\u4e8b\u751a\u591a\uff0c\u4f46\u5374\u65e0\u4eba\u80fd\u591f\u5c06\u4ed6\u4eec\u5236\u670d\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u90a3\u2026\u2026\u4e0d\u53c8\u662f\u4e94\u4e2a\u9b54\u5934\u5417\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u53ef\u4e0d\u662f\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5ffd\u7136\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u65b9\u5927\u54e5\uff0c\u4f60\u5e08\u7236\u600e\u7684\u4e0d\u8fde\u4ed6\u4eec\u4e00\u9053\u6536\u670d\u5462\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u4e86\u4e00\u5446\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u611a\u5144\u4e5f\u6709\u4e9b\u4e0d\u660e\u767d\uff01\u4f46\u5e08\u7236\u4e0d\u66fe\u627e\u4e0a\u4ed6\u4eec\uff0c\u53ea\u6015\u522b\u6709\u539f\u56e0\u4e86\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u611a\u53d4\u5230\u662f\u77e5\u9053\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u90a3\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u8fd9\u4e1c\u6d77\u4e94\u5996\uff0c\u51fa\u9053\u8f83\u8fdf\uff0c\u53ea\u6015\u9648\u5927\u4fa0\u5f53\u65f6\u5df2\u7136\u8eab\u53d7\u522b\u4eba\u6697\u7b97\uff0c\u5143\u529b\u5236\u4f4f\u4ed6\u4eec\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u90a3\u662f\u8bf4\u2026\u2026\u4ed6\u4eec\u51fa\u9053\u5c1a\u672a\u8d85\u8fc7\u5341\u5e74\u4e86\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4e0d\u9519\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bdd\u97f3\u672a\u5df2\uff0c\u5ffd\u7136\u542c\u5f97\u90a3\u5927\u80dc\u5934\u9640\u5927\u58f0\u9053\uff1a\u201c\u6e38\u8001\u65bd\u4e3b\uff0c\u4ed6\u4eec\u5929\u53f0\u4e09\u7edd\u5bb3\u6015\uff0c\u4f46\u6d12\u5bb6\u5374\u662f\u4e0d\u6015\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u7b11\u9053\uff1a\u201c\u8001\u592b\u4e5f\u6ca1\u6709\u8bf4\u4f60\u4eec\u6015\u4ec0\u4e48\u4eba\u554a\uff01\u4f60\u4f55\u5fc5\u8fd9\u7b49\u5927\u60ca\u5c0f\u602a\uff1f\u201d \u3000\u3000\u5927\u80dc\u5934\u9640\u9053\uff1a\u201c\u4f60\u660e\u767d\u6700\u597d\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u4e94\u4f4d\u4e5f\u662f\u5e94\u9080\u524d\u6765\u7684\u5417\uff1f\u201d \u3000\u3000\u5927\u80dc\u5934\u9640\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u90a3\u5929\u9b54\u5973\u4e5f\u5728\u4f60\u4eec\u8eab\u4e0a\u505a\u4e86\u624b\u811a\uff1f\u201d \u3000\u3000\u5927\u80dc\u5934\u9640\u9053\uff1a\u201c\u6d12\u5bb6\u7b49\u4eba\u4e0e\u5929\u53f0\u4e09\u7edd\u4e0d\u540c\uff0c\u5929\u9b54\u5973\u81ea\u7136\u4e0d\u4f1a\u5728\u6211\u4eec\u8eab\u4e0a\u4e0b\u6bd2\u7684\u4e86\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u7b11\u9053\uff1a\u201c\u90a3\u53ef\u597d\u554a\uff01\u4f60\u4eec\u81f3\u5c11\u4e0d\u7528\u803d\u5fc3\u81ea\u5df1\u6d3b\u4e0d\u4e45\uff01\u201d \u3000\u3000\u5927\u80dc\u5934\u9640\u9053\uff1a\u201c\u8fd9\u4e5f\u4e0d\u662f\u4ec0\u4e48\u7279\u522b\u7684\u4e8b\uff0c\u8001\u65bd\u4e3b\u4f55\u5fc5\u63d0\u51fa\u6765\u4e00\u95ee\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u8fd9\u4e8b\u5f53\u7136\u5f88\u91cd\u8981\uff0c\u6700\u5927\u7684\u5173\u952e\uff0c\u90a3\u662f\u5728\u4e8e\u4f60\u4eec\u6ca1\u6709\u4e2d\u6bd2\uff0c\u4f46\u4ed6\u4eec\u4e09\u4f4d\u5374\u4e2d\u4e86\u6bd2\u554a\uff01\u201d \u3000\u3000\u5927\u80dc\u5934\u9640\u9053\uff1a\u201c\u8fd9\u4e5f\u6ca1\u6709\u4ec0\u4e48\u4e86\u4e0d\u8d77\uff0c\u53ea\u8981\u53d6\u5230\u89e3\u836f\uff0c\u4e00\u5207\u5c82\u4e0d\u8fce\u5203\u800c\u89e3\u5417\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u4f60\u5230\u662f\u5f88\u8f7b\u677e\uff0c\u662f\u545c\uff1f\u4f46\u8001\u592b\u7684\u770b\u6cd5\u5374\u4e0d\u7136\u4e86\uff01\u201d \u3000\u3000\u5927\u80dc\u5934\u9640\u9053\uff1a\u201c\u54ea\u91cc\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u4e0d\u5230\u81ea\u5df1\u751f\u6b7b\u9047\u5230\u5a01\u80c1\u7684\u65f6\u5019\uff0c\u5f53\u7136\u662f\u4e0d\u61c2\u5176\u4e2d\u7684\u4e0d\u540c\u4e4b\u5904\u4e86\uff01\u201d \u3000\u3000\u5927\u80dc\u5934\u9640\u76ee\u5149\u5ffd\u7136\u8f6c\u6ce8\u4e94\u4eba\u4e2d\u7684\u4e00\u4f4d\u80cc\u63d2\u4e00\u67c4\u5f2f\u628a\u67f4\u5200\u3001\u7626\u5f97\u5982\u540c\u733f\u7334\u7684\u8001\u4eba\uff0c\u9053\uff1a \u3000\u3000\u201c\u51af\u65bd\u4e3b\uff0c\u4f60\u660e\u767d\u4ed6\u7684\u8bdd\u4e2d\u542b\u610f\u4f55\u5728\u5417\uff1f\u201d \u3000\u3000\u90a3\u59d3\u51af\u7684\u7626\u5b50\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u61c2\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u7b11\u9053\uff1a\u201c\u4f60\u53ef\u662f\u5438\u8840\u9b3c\u9a6c\u5200\u96f7\uff1f\u201d \u3000\u3000\u51af\u96f7\u9053\uff1a\u201c\u5929\u4e0b\u9664\u4e86\u51af\u67d0\uff0c\u8fd8\u6709\u8c01\u7528\u83dc\u5200\u5f53\u4f5c\u8fc7\u5175\u5668\u5417\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u90a3\u662f\u81ea\u7136\u7684\u4e86\uff01\u5929\u4e0b\u4e0d\u66fe\u6709\u4eba\u7528\u4f60\u8fd9\u7b49\u6076\u6bd2\u7684\u9b3c\u5200\uff0c\u8001\u592b\u987b\u77a7\u5230\u50cf\u8fd9\u67c4\u5f2f\u628a\uff0c\u5185\u85cf\u5978\u8bc8\u7684\u9b3c\u5200\uff0c\u5c31\u8be5\u8ba4\u51fa\u6765\u624d\u5bf9\u554a\uff01\u201d \u3000\u3000\u51af\u96f7\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u50cf\u4f60\u8fd9\u7b49\u5927\u540d\u9f0e\u9f0e\u7684\u4eba\u7269\uff0c\u7adf\u7136\u4e00\u773c\u4e0d\u66fe\u77a7\u51fa\u533a\u533a\u6765\u5386\uff0c\u5f53\u771f\u6709\u4e9b\u8d70\u773c\u4e86\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u5927\u7b11\u9053\uff1a\u201c\u51af\u96f7\uff0c\u4f60\u5df2\u662f\u5c06\u6b7b\u4e4b\u4eba\uff0c\u7eb5\u7136\u8bbd\u7b11\u8001\u592b\u513f\u53e5\uff0c\u8001\u592b\u4e5f\u4e0d\u4f1a\u89c1\u602a\u4e8e\u4f60\u7684\u4e86\uff01\u201d \u3000\u3000\u51af\u96f7\u95fb\u8a00\u5446\u4e86\u4e00\u5446\uff0c\u4f46\u77ac\u5373\u5927\u6012\u9053\uff1a\u201c\u4f60\u53ef\u662f\u60f3\u5411\u51af\u67d0\u6311\u6218\u5417\uff1f\u522b\u4eba\u6015\u4f60\u5343\u624b\u795e\u7fc1\uff0c\u51af\u67d0\u53ef\u4e0d\u6015\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u7b11\u9053\uff1a\u201c\u8001\u592b\u5c82\u80af\u4e0e\u4f60\u8fd9\u7b49\u4eba\u52a8\u624b\uff1f\u8001\u592b\u8bf4\u4f60\u6d3b\u4e0d\u4e86\u591a\u4e45\uff0c\u90a3\u5374\u662f\u53e6\u6709\u6240\u6307\u7684\u4e86\uff01\u201d \u3000\u3000\u51af\u96f7\u53c8\u662f\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u53e6\u6709\u6240\u6307\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u8001\u592b\u53ef\u4ee5\u8ddf\u4f60\u4eec\u6253\u4e00\u4e2a\u8d4c\uff0c\u5982\u679c\u4f60\u548c\u90a3\u5929\u9b54\u5973\u7f20\u5728\u4e00\u8d77\uff0c\u6700\u540e\u7684\u4e00\u6761\u8def\u5c31\u662f\u6b7b\uff01\u201d \u3000\u3000\u51af\u96f7\u8138\u8272\u4e00\u53d8\uff0c\u6012\u9053\uff1a\u201c\u4e00\u6d3e\u80e1\u8a00\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u8001\u592b\u662f\u5426\u80e1\u8a00\uff0c\u5c14\u7b49\u65e5\u540e\u4fbf\u77e5\uff01\u5176\u5b9e\uff0c\u90a3\u5929\u9b54\u5973\u867d\u7136\u4e0d\u4f1a\u5728\u4f60\u4eec\u8eab\u4e0a\u4e0b\u6bd2\uff0c\u4f46\u5979\u5374\u65e9\u5df2\u6709\u4e86\u6740\u6b7b\u4f60\u4eec\u4e4b\u5fc3\u2026\u2026\u201d \u3000\u3000\u5927\u80dc\u5934\u9640\u6012\u9053\uff1a\u201c\u65bd\u4e3b\u8fd9\u79bb\u95f4\u4e4b\u672f\uff0c\u4f3c\u662f\u4e0d\u5927\u9ad8\u660e\u554a\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u662f\u4e0d\u4fe1\u7684\u4e86\uff01\u2019 \u3000\u3000\u51af\u96f7\u9053\uff1a\u201c\u5f53\u7136\u4e0d\u4fe1\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u4fe1\u6ca1\u6709\u5173\u7cfb\uff0c\u8001\u592b\u6709\u4e00\u53e5\u8bdd\u60f3\u5411\u4e94\u4f4d\u8bf7\u6559\uff01\u201d \u3000\u3000\u51af\u96f7\u9053\u201c\u4ec0\u4e48\u8bdd\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u4ee5\u4f60\u4eec\u4e94\u4f4d\u7684\u6b66\u529f\uff0c\u5982\u4e0e\u90a3\u5929\u9b54\u5973\u5355\u6253\u72ec\u6597\uff0c\u662f\u4f60\u4eec\u5f3a\uff0c\u8fd8\u662f\u5929\u9b54\u5973\u5f3a\uff1f\u201d \u3000\u3000\u51af\u96f7\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u6ca1\u6709\u63a5\u8bdd\uff01 \u3000\u3000\u4f46\u90a3\u5927\u80dc\u5934\u9640\u5374\u957f\u957f\u5730\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u6d12\u5bb6\u8ba4\u4e3a\uff0c\u90a3\u5929\u9b54\u65bd\u4e3b\u4f3c\u662f\u8981\u6bd4\u6d12\u5bb6\u7b49\u4e94\u4eba\u80dc\u8fc7\u4e00\u6b65\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u7b11\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4e86\uff01\u90a3\u5929\u9b54\u5973\u660e\u77e5\u4f60\u4eec\u6b66\u529f\u4e0d\u6bd4\u5979\u5f3a\uff0c\u5374\u8981\u9080\u7ea6\u4f60\u7b49\u524d\u6765\u76f8\u52a9\uff0c\u5979\u7684\u7528\u5fc3\u4f55\u5728\uff0c\u5404\u4f4d\u8fd8\u60f3\u4e0d\u51fa\u6765\u5417\uff1f\u201d \u3000\u3000\u8fd9\u65f6\u90a3\u4e94\u4eba\u4e2d\u7684\u4e00\u4f4d\u767d\u8863\u5c11\u5973\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u6e38\u8001\u4eba\u5bb6\uff0c\u4f60\u8bf4\u8bdd\u600e\u5730\u90a3\u4e48\u559c\u6b22\u515c\u5708\u5b50\u5462\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u53ef\u662f\u7389\u65e0\u5e38\u767d\u745b\u5417\uff1f\u201d \u3000\u3000\u90a3\u767d\u745b\u5ae3\u7136\u4e00\u7b11\u9053\uff1a\u201c\u6b63\u662f\u8d31\u59be\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u8138\u8272\u4e00\u6c89\u9053\uff1b\u201c\u4f60\u8bf4\u8001\u592b\u8bf4\u8bdd\u515c\u5708\u5b50\u5417\uff1f\u5176\u5b9e\uff0c\u8001\u592b\u53ea\u662f\u60f3\u4ee4\u4f60\u4eec\u591a\u660e\u767d\u4e00\u70b9\u800c\u5df2\uff01\u201d \u3000\u3000\u767d\u745b\u5a07\u7b11\u9053\uff1a\u201c\u591a\u660e\u767d\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u8fd9\u5973\u4eba\u751f\u5177\u5a9a\u76f8\uff0c\u8fd9\u65f6\u5ffd\u7136\u8fd9\u822c\u5a07\u7b11\uff0c\u53ea\u628a\u90d1\u5927\u521a\u77a7\u5f97\u5fc3\u4e2d\u5927\u9707\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u7b49\u6deb\u8361\u7684\u5973\u4eba\uff0c\u6d3b\u5728\u4e16\u4e0a\uff0c\u771f\u4e0d\u77e5\u8981\u5bb3\u6b7b\u591a\u5c11\u7537\u4eba\u2026\u2026\u201d \u3000\u3000\u4ed6\u9a87\u7136\u4e4b\u95f4\uff0c\u53ea\u542c\u5f97\u6e38\u516c\u7384\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u8001\u592b\u4e0d\u8fc7\u8981\u4f60\u4eec\u660e\u767d\uff0c\u5929\u9b54\u4e43\u662f\u7528\u7684\u501f\u5200\u6740\u4eba\u4e4b\u8ba1\u800c\u5df2\uff01\u201d \u3000\u3000\u767d\u745b\u9053\uff1a\u201c\u6e38\u8001\u4eba\u5bb6\u4f60\u4ee5\u4e3a\u6211\u4eec\u4e0d\u61c2\u8fd9\u7b49\u9053\u7406\u5417\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u5982\u662f\u4f60\u4eec\u61c2\u5f97\uff0c\u90a3\u53c8\u4f55\u5fc5\u524d\u6765\uff1f\u201d \u3000\u3000\u767d\u745b\u683c\u683c\u4e00\u7b11\u9053\uff1a\u201c\u90a3\u5929\u9b54\u5973\u7eb5\u6709\u501f\u5200\u6740\u4eba\u4e4b\u5fc3\uff0c\u4f46\u5979\u53c8\u600e\u77e5\u6211\u7b49\u65e9\u5df2\u6709\u4e86\u5168\u8eab\u4e4b\u7b56\u5462\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u4f3c\u4e4e\u6bd4\u8001\u5927\u60f3\u8c61\u4e2d\u8981\u806a\u660e\u4e9b\u554a\uff01\u201d \u3000\u3000\u767d\u745b\u7b11\u9053\uff1a\u201c\u90a3\u662f\u81ea\u7136\u7684\u4e86\uff01\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u4f60\u8001\u4e0d\u8bf4\uff0c\u59be\u8eab\u7b49\u4e5f\u65e9\u6709\u81ea\u77e5\u4e4b\u660e\uff0c\u50cf\u5929\u9b54\u5973\u2026\u2026\u201d \u3000\u3000\u5979\u5ffd\u7136\u4f4f\u53e3\u4e0d\u4f4f\u4e0b\u8bf4\u4e86\uff01 \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u542c\u4f60\u4e4b\u610f\uff0c\u4f60\u4eec\u4f3c\u5df2\u6709\u5907\u800c\u6765\u7684\u4e86\uff01\u201d \u3000\u3000\u767d\u745b\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8d31\u59be\u7b49\u679c\u7136\u662f\u6709\u5907\u7684\u4e86\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u5ffd\u7136\u957f\u957f\u5730\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u90a3\u662f\u8001\u592b\u591a\u8651\u4e86\uff01\u4f46\u613f\u4e94\u4f4d\u5c0f\u5fc3\u4e00\u4e9b\u6700\u597d\uff01\u201d \u3000\u3000\u90a3\u767d\u745b\u770b\u4e86\u51e0\u4eba\u4e00\u773c\uff0c\u63a5\u7740\uff1a\u201c\u4e0d\u52b3\u8d39\u5fc3\u2026\u2026\u201d \u3000\u3000\u6e38\u516c\u7384\u5fae\u5fae\u4e00\u7b11\uff0c\u5411\u6c5f\u5929\u6653\u7b49\u4e09\u4eba\u9053\uff1a\u201c\u6c5f\u8001\u5f1f\uff0c\u4f60\u4eec\u65e2\u5df2\u5e61\u7136\u7701\u609f\uff0c\u90a3\u4e5f\u4e0d\u7528\u5750\u5728\u8fd9\u5f20\u684c\u4f4d\u4e4b\u4e0a\u4e86\uff01\u201d \u3000\u3000\u6c5f\u5929\u6653\u5ffd\u7136\u5927\u7b11\u9053\uff1a\u201c\u662f\u554a\uff0c\u54b1\u4eec\u679c\u7136\u4e0d\u5e94\u518d\u5728\u8fd9\u91cc\u5750\u4e0b\u53bb\u4e86\u2026\u2026\u201d\u8bdd\u97f3\u4e00\u843d\uff0c\u5927\u6b65\u5411\u53e6\u5916\u7684\u4e00\u5f20\u5ea7\u684c\u4e4b\u4e0a\u884c\u53bb\u3002 \u3000\u3000\u674e\u5146\u4e30\uff0c\u4f59\u6770\u548c\u6e38\u516c\u7384\u4e5f\u8ddf\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u9152\u697c\u4e4b\u4e0a\u7684\u5176\u4f59\u5ba2\u4eba\uff0c\u65e9\u5728\u4ed6\u4eec\u5927\u58f0\u559d\u53eb\u4e4b\u65f6\uff0c\u7ecf\u90a3\u5e97\u5bb6\u4e0a\u5f97\u697c\u6765\uff0c\u4e00\u4e00\u529d\u8d70\uff0c\u504c\u5927\u7684\u9152\u697c\u4e4b\u4e0a\uff0c\u9664\u4e86\u6e38\u516c\u7384\uff0c\u5927\u53f0\u4e09\u7edd\uff0c\u4e1c\u6d77\u4e94\u5996\u4e4b\u5916\uff0c\u53ea\u5269\u4e0b\u4e86\u65b9\u96ea\u5b9c\u548c\u90d1\u5927\u521a\uff0c\u5b89\u5c0f\u840d\u7b49\u4e09\u4eba\u3002 \u3000\u3000\u662f\u4ee5\uff0c\u6e38\u516c\u7384\u7b49\u56db\u4eba\u4f1a\u5b9a\u4ee5\u540e\uff0c\u90fd\u7981\u4e0d\u4f4f\u6389\u5934\u5411\u65b9\u96ea\u5b9c\u7b49\u4eba\u770b\u4e86\u4e00\u773c\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u548c\u90d1\u5927\u521a\u5140\u81ea\u88c5\u4f5c\u4e0d\u77e5\uff0c\u81ea\u884c\u996e\u9152\uff0c\u53ea\u6709\u5b89\u5c0f\u840d\u5374\u751a\u662f\u4e0d\u5feb\uff0c\u5c0f\u5634\u5df2\u7136\u9f13\u4e86\u51fa\u6765\u3002 \u3000\u3000\u6070\u597d\uff0c\u8fd9\u65f6\u90a3\u4e1c\u6d77\u4e94\u5996\u4eba\uff0c\u4e5f\u6389\u5934\u770b\u5411\u65b9\u96ea\u5b9c\u7b49\u4eba\uff0c\u90a3\u4e94\u5996\u4e4b\u5c6f\u4e00\u76f4\u672a\u66fe\u5f00\u53e3\u7684\u4e24\u4eba\uff0c\u4e00\u4e2a\u8eab\u7740\u7384\u8272\u957f\u886b\u7684\u4e2d\u5e74\u4e66\u751f\uff0c\u53e6\u4e00\u4f4d\u5219\u9752\u4e2d\u88f9\u5934\uff0c\u4e00\u8eab\u6e56\u7eff\u52b2\u88c5\u5916\u7f69\u6de1\u9ec4\u7f57\u886b\u7684\u5c11\u5973\uff0c\u8fd9\u65f6\uff0c\u90a3\u7384\u8863\u4e66\u751f\u5728\u76ef\u4e86\u5b89\u5c0f\u840d\u4e00\u773c\u4e4b\u540e\uff0c\u5fcd\u4e0d\u4f4f\u5931\u58f0\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u6807\u81f4\u7684\u5a18\u513f\u4eec\uff1f\u6c60\u67d0\u771f\u662f\u751f\u5e73\u4ec5\u89c1\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bdd\u97f3\u4e00\u8f6c\uff0c\u53ea\u89c1\u90a3\u7389\u65e0\u5e38\u767d\u745b\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u53c8\u5728\u52a8\u6b6a\u4e3b\u610f\u4e86\u5417\uff1f\u59be\u8eab\u7eb5\u7136\u5927\u91cf\uff0c\u4f60\u4e5f\u4e0d\u80fd\u5f53\u7740\u59be\u8eab\u7684\u9762\u524d\uff0c\u8fd9\u7b49\u5938\u8d5e\u522b\u4eba\u5427\uff01\u201d \u3000\u3000\u90a3\u7384\u8863\u4e66\u751f\u4f38\u4e86\u4f38\u820c\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u5a18\u5b50\u83ab\u8981\u8bef\u4f1a\uff0c\u5c0f\u751f\u5373\u4f7f\u6709\u5929\u5927\u7684\u80c6\u4e86\uff0c\u90a3\u4e5f\u4e0d\u4f1a\u5f53\u7740\u5a18\u4e4e\u7684\u9762\u524d\u6253\u90a3\u522b\u7684\u5973\u4eba\u4e3b\u610f\u554a\uff01\u4f55\u51b5\uff0c\u8fd9\u59d1\u5a18\u53c8\u662f\u4e00\u4e2a\u5ae9\u8d27\u2026\u2026\u201d\u7a81\u7136\u53ea\u89c1\u4e4c\u5149\u4e00\u95ea\uff0c\u90a3\u7384\u8863\u4e66\u751f\u5927\u53eb\u4e00\u58f0\uff0c\u8fde\u4eba\u5e26\u6905\u5b50\u5411\u540e\u4e00\u8de4\u683d\u5012\uff01 \u3000\u3000\u767d\u745b\u8138\u8272\u4e00\u53d8\uff0c\u4f38\u624b\u6276\u4f4f\u4e86\u7384\u8863\u4e66\u751f\uff0c\u4e00\u8fed\u8fde\u58f0\u9053\uff1a\u201c\u6c60\u90ce\u4f60\u600e\u4e48\u4e86\u2026\u2026\u201d\u76ee\u5149\u8fc7\u540e\uff0c\u53ea\u89c1\u90a3\u7384\u8863\u4e66\u751f\u7684\u4e24\u816e\uff0c\u5404\u81ea\u9732\u51fa\u4e86\u4e24\u5bf8\u591a\u957f\u4e00\u622a\u4e4c\u6728\u7b77\u5b50\uff0c\u9c9c\u8840\u6b63\u81ea\u90a3\u4e24\u816e\u6e17\u4e86\u51fa\u6765\u3002 \u3000\u3000\u539f\u6765\uff0c\u662f\u4e00\u6839\u4e4c\u6728\u7b77\u5b50\uff0c\u7a7f\u900f\u4e86\u6b64\u4eba\u7684\u53cc\u816e\u3002 \u3000\u3000\u90a3\u5927\u80dc\u5934\u9640\u5ffd\u5730\u62cd\u684c\u800c\u8d77\uff0c\u76ee\u5149\u5411\u6e38\u516c\u7384\u671b\u53bb\uff0c\u5927\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u80c6\u6562\u51fa\u624b\u4f24\u4eba\uff1f\u8fd8\u4e0d\u4e0e\u6d12\u5bb6\u6eda\u4e86\u8d77\u6765\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u89c1\u4ed6\u671b\u7740\u81ea\u5df1\u4e00\u684c\u4e4b\u4eba\uff0c\u4e0d\u7981\u54c8\u54c8\u4e00\u7b11\u3002 \u3000\u3000\u5927\u80dc\u5934\u9640\u88ab\u4ed6\u7b11\u7684\u8138\u8272\u4e00\u53d8\uff0c\u559d\u9053\uff1a\u201c\u4f60\u7b11\u4ec0\u4e48\uff1f\u662f\u4f60\u5e72\u7684\u597d\u4e8b\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u8d8a\u53d1\u5730\u4ef0\u5929\u5927\u7b11\u4e0d\u5df1\uff0c\u534a\u664c\u65b9\u9053\uff1a\u201c\u5934\u9640\uff0c\u4f60\u53ef\u66fe\u4ed4\u7ec6\u5730\u77a7\u77a7\u54b1\u4eec\u6240\u5750\u4e4b\u5904\uff0c\u5982\u662f\u6709\u4eba\u51fa\u624b\uff0c\u4f1a\u4e0d\u4f1a\u4e00\u7b77\u6a2a\u7a7f\u53cc\u816e\uff1f\u201d \u3000\u3000\u90a3\u5927\u80dc\u5934\u9640\u770b\u4e86\u4e00\u770b\uff0c\u679c\u7136\u89c9\u51fa\u4e86\u4e0d\u5bf9\u3002 \u3000\u3000\u53ea\u56e0\u90a3\u6e38\u516c\u7384\u7b49\u4eba\u6240\u5750\u4e4b\u5904\uff0c\u7384\u8863\u4e66\u751f\u4e43\u662f\u6b63\u9762\u76f8\u5bf9\uff0c\u5018\u662f\u4ed6\u4eec\u4e4b\u4e2d\u6709\u7740\u4efb\u4f55\u4eba\u8981\u60f3\u51fa\u624b\uff0c\u90a3\u4e5f\u65e0\u6cd5\u5c06\u6728\u7b77\u4ece\u7384\u8863\u4e66\u751f\u816e\u8fb9\u6253\u5165\u7684\u4e86\uff01 \u3000\u3000\u4ed6\u60f3\u660e\u767d\u4e86\u8fd9\u4e00\u70b9\uff0c\u987f\u65f6\u6389\u5934\u5411\u65b9\u96ea\u5b9c\u8fd9\u4e00\u684c\u671b\u6765\uff0c\u8fd9\u4e00\u671b\u4e0d\u8981\u7d27\uff0c\u5927\u80dc\u5934\u9640\u987f\u65f6\u5446\u4e86\u4e00\u5446\u3002 \u3000\u3000\u539f\u6765\u90a3\u5b89\u5c0f\u840d\u6b63\u7741\u7740\u4e00\u53cc\u5927\u773c\uff0c\u72e0\u72e0\u5730\u77aa\u7740\u81ea\u5df1\uff01 \u3000\u3000\u5927\u80dc\u5934\u9640\u8fd8\u672a\u5f20\u53e3\u8bf4\u8bdd\uff0c\u53ea\u89c1\u201c\u5438\u8840\u9b3c\u5200\u201d\u51af\u96f7\u5df2\u7ecf\u970d\u7136\u8d77\u8eab\uff0c\u5927\u6b65\u5411\u5b89\u5c0f\u840d\u7b49\u4eba\u8d70\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u90a3\u5927\u80dc\u5934\u9640\u8ddf\u5728\u51af\u96f7\u8eab\u540e\uff0c\u4eba\u672a\u5230\uff0c\u4e1a\u5df2\u5148\u884c\u5f20\u53e3\u5927\u559d\u9053\uff1a\u201c\u6d12\u5bb6\u7684\u670b\u53cb\uff0c\u53ef\u662f\u4f60\u4eec\u624b\u4e0b\u6240\u4f24\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u672c\u5c31\u662f\u4e2a\u65e0\u4e8b\u627e\u4e8b\u7684\u6027\u5b50\uff0c\u95fb\u8a00\u683c\u683c\u4e00\u7b11\u9053\uff1a\u201c\u662f\u53c8\u5982\u4f55\uff1f\u201d\u5979\u539f\u5df2\u88ab\u90a3\u7384\u8863\u4e66\u751f\u7684\u51e0\u53e5\u8bdd\u8bf4\u7684\u4e00\u809a\u5b50\u6012\u706b\uff0c\u65b9\u624d\u987a\u624b\u53d6\u4e86\u4e00\u6839\u6728\u7b77\u51fb\u51fa\uff0c\u4f24\u4e86\u90a3\u7384\u8863\u4e66\u751f\uff0c\u4f46\u7b49\u89c1\u5230\u90a3\u7384\u8863\u4e66\u751f\u53d7\u4f24\u540e\u7684\u60c5\u72b6\uff0c\u6012\u610f\u5df2\u6d88\u8d70\u4e86\u4e00\u5927\u534a\uff0c\u5426\u5219\uff0c\u5979\u6b64\u523b\u56de\u8bdd\uff0c\u5c31\u4e0d\u4f1a\u53d1\u51fa\u7b11\u58f0\u4e86\uff01 \u3000\u3000\u51af\u96f7\u548c\u5927\u80dc\u5934\u9640\u8d70\u5230\u65b9\u96ea\u5b9c\u7b49\u4eba\u8eab\u524d\u4e24\u5c3a\uff0c\u65b9\u59cb\u7ad9\u4e86\u4e0b\u6765\uff0c\u90a3\u51af\u96f7\u51b7\u51b7\u5730\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u8001\u592b\u60f3\u4e0d\u5230\uff0c\u5374\u88ab\u4f60\u4eec\u7792\u8fc7\u53bb\u4e86\uff01\u521a\u624d\u51fa\u624b\u4f24\u4eba\uff0c\u53ef\u662f\u4f24\u8fd9\u4f4d\u5c0f\u59d1\u5a18\u5417\uff1f\u201d \u3000\u3000\u8fd9\u51af\u96f7\u7684\u53e3\u6c14\uff0c\u5df2\u7136\u4e0d\u82e5\u5927\u80dc\u90a3\u7b49\u96be\u542c\uff0c\u663e\u7136\u4ed6\u5df2\u89c9\u51fa\uff0c\u773c\u524d\u7684\u8fd9\u4e09\u4eba\uff0c\u4ff1\u7686\u4e0d\u662f\u597d\u60f9\u4e4b\u4eba\u4e86\uff01 \u3000\u3000\u5b89\u5c0f\u840d\u5374\u662f\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u51fa\u53e3\u4f24\u4eba\u5728\u5148\uff0c\u96be\u9053\u8fd8\u8981\u602a\u522b\u4eba\u6574\u6212\u4e8e\u4ed6\u5417\uff1f\u201d \u3000\u3000\u51af\u96f7\u9053\uff1a\u201c\u90a3\u679c\u7136\u662f\u59d1\u5a18\u51fa\u624b\u7684\u4e86\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b63\u662f\u59d1\u5a18\u6559\u8bad\u4e86\u4ed6\uff01\u4ed6\u5982\u662f\u518d\u6562\u53e3\u51fa\u4e0d\u900a\u4e4b\u8a00\uff0c\u59d1\u5a18\u5c11\u4e0d\u5f97\u5c31\u8981\u53d6\u4ed6\u6027\u547d\u4e86\uff01\u201d \u3000\u3000\u5927\u80dc\u5934\u9640\u5ffd\u7136\u4e24\u773c\u4e00\u7ffb\uff0c\u521a\u8981\u5f00\u53e3\uff0c\u51af\u96f7\u5df2\u62a2\u5148\u6447\u4e86\u6447\u624b\uff0c\u63a5\u9053\uff1a\u201c\u59d1\u5a18\u600e\u4e48\u79f0\u547c\uff1f\u8fd9\u4e24\u4f4d\u5462\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u6211\u53eb\u5b89\u5c0f\u840d\uff0c\u8fd9\u4e24\u4f4d\uff01\u5417\uff0c\u4e00\u4f4d\u662f\u6211\u90d1\u5927\u4f2f\uff0c\u4e00\u4f4d\u662f\u6211\u65b9\u5927\u54e5\uff01\u201d \u3000\u3000\u90a3\u51af\u96f7\u7709\u5934\u76b1\u4e86\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u8001\u592b\u60f3\u8d77\u6765\u4e86\uff0c\u8fd9\u4f4d\u53ef\u5c31\u662f\u4e2d\u5dde\u4e09\u4fa0\u7684\u8001\u5927\uff0c\u90d1\u5927\u521a\uff1f\u201d \u3000\u3000\u4eba\u5bb6\u4e00\u53e3\u53eb\u51fa\u4e86\u81ea\u5df1\u7684\u540d\u53f7\uff0c\u90d1\u5927\u521a\u4e0d\u597d\u518d\u4e0d\u7b54\u8bdd\uff0c\u5f53\u4e0b\u6de1\u6de1\u5730\u7b11\u4e86\u4e00\u7b11\u9053\uff1a\u201c\u533a\u533a\u6b63\u662f\u90d1\u67d0\uff0c\u6c5f\u6e56\u4e0a\u7684\u65e0\u540d\u5c0f\u8f88\uff0c\u5230\u53eb\u51af\u5144\u89c1\u7b11\u4e86\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u4e5f\u6709\u81ea\u77e5\u4e4b\u660e\uff0c\u4e1c\u6d77\u4e94\u5996\u7b49\u4eba\uff0c\u9519\u975e\u9002\u624d\u5b89\u5c0f\u840d\u51fa\u624b\u4f24\u4eba\u7684\u624b\u6cd5\uff0c\u9547\u4f4f\u4e86\u4ed6\u4eec\uff0c\u4ec5\u4ec5\u662f\u9760\u81ea\u5df1\u7684\u4e2d\u5dde\u4e09\u4fa0\u540d\u5934\uff0c\u90a3\u662f\u5413\u4e0d\u5012\u4ed6\u4eec\u7684\u4e86\uff01\u662f\u4ee5\uff0c\u4ed6\u624d\u8bf4\u51fa\u4e0d\u5351\u4e0d\u4ea2\u7684\u8bdd\u6765\u3002 \u3000\u3000\u51af\u96f7\u54fc\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u8fd9\u4f4d\u5b89\u59d1\u5a18\u2026\u2026\u53ef\u662f\u90d1\u5927\u4fa0\u7684\u5b50\u5f1f\uff1f\u201d \u3000\u3000\u4ed6\u5982\u6b64\u53cd\u95ee\uff0c\u90a3\u662f\u60f3\u63a2\u51fa\u5b89\u5c0f\u840d\u7684\u51fa\u8eab\uff0c\u90d1\u5927\u521a\u6c5f\u6e56\u9605\u5386\u4f55\u7b49\u5e7f\u6cdb\uff0c\u95fb\u8a00\u7b11\u9053\uff1a\u201c\u4e0d\u662f\uff01\u5979\u2026\u2026\u53ea\u662f\u90d1\u67d0\u4eba\u7684\u4e00\u4f4d\u6545\u53cb\u4e4b\u5973\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u5fc3\u4e2d\u660e\u767d\uff0c\u90a3\u4e1c\u6d77\u4e94\u5996\u80fd\u591f\u770b\u51fa\u5b89\u5c0f\u840d\u5973\u626e\u7537\u88c5\uff0c\u5c31\u5df2\u663e\u51fa\u4ed6\u4eec\u773c\u529b\u4e0d\u5f31\uff0c\u5018\u4e0d\u662f\u81ea\u5df1\u4e0d\u5c0f\u5fc3\u5e94\u5bf9\uff0c\u5f04\u51fa\u4ec0\u4e48\u5dee\u9519\uff0c\u5374\u662f\u5bf9\u4ed6\u4eec\u4e8c\u5c0f\u7684\u9762\u5b50\u4e0d\u5927\u597d\u770b\uff0c\u56e0\u6b64\uff0c\u4ed6\u5bf9\u7b54\u4e4b\u95f4\uff0c\u4e5f\u5c31\u663e\u7684\u5206\u5916\u5c0f\u5fc3\u4e86\u3002 \u3000\u3000\u51af\u96f7\u53cc\u80a9\u8038\u52a8\uff0c\u6c89\u541f\u4e86\u4e00\u9635\u63a5\u9053\uff1a\u201c\u8fd9\u4f4d\u65b9\u8001\u5f1f\u662f\u54ea\u4e00\u4f4d\u5927\u4fa0\u7684\u5b50\u5f1f\uff1f\u201d \u3000\u3000\u4ed6\u8fd9\u7b49\u5fcd\u6c14\u7684\u8be2\u95ee\uff0c\u751a\u662f\u51fa\u4e8e\u5927\u80dc\u5934\u9640\u610f\u6599\u4e4b\u5916\uff0c\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u5927\u53eb\u9053\uff1a\u201c\u51af\u65bd\u4e3b\uff0c\u4f60\u5982\u6b64\u558b\u558b\u4e0d\u4f11\u5730\u95ee\u6765\u95ee\u53bb\uff0c\u90a3\u53c8\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u8fd9\u4f4d\u59d1\u5a18\u4f24\u4e86\u6211\u4eec\u7684\u5fc3\uff0c\u90a3\u5df2\u662f\u6709\u76ee\u5171\u7779\u4e4b\u4e8b\uff0c\u54b1\u4eec\u627e\u56de\u8fc7\u8282\u4e0d\u5c31\u5f97\u4e86\u5417\uff1f\u201d \u3000\u3000\u51af\u96f7\u76ae\u7b11\u8089\u4e0d\u7b11\u5730\u54fc\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u548c\u5c1a\uff0c\u54b1\u4eec\u95ee\u6e05\u695a\u4e86\u4e00\u5207\u518d\u52a8\u624b\uff0c\u90a3\u4e5f\u4e0d\u8fdf\u554a\uff01\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u76ee\u5149\u8f6c\u5411\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u53ef\u662f\u65b9\u5929\u6210\u7684\u513f\u5b50\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7a81\u7136\u6012\u9053\uff1a\u201c\u4e0d\u662f\uff01\u201d \u3000\u3000\u51af\u96f7\u6014\u4e86\u4e00\u6014\uff0c\u4ed6\u60f3\u4e0d\u51fa\u65b9\u96ea\u5b9c\u4e3a\u4ec0\u4e48\u7a81\u7136\u53d1\u6012\uff0c\u76b1\u4e86\u76b1\u7709\uff0c\u9053\uff1a\u201c\u8001\u5f1f\u4e3a\u4f55\u53d1\u6012\uff1f\u51af\u67d0\u4f3c\u662f\u6ca1\u6709\u8bf4\u9519\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u53ef\u662f\u51b3\u5fc3\u76f8\u52a9\u90a3\u5929\u9b54\u5973\u4e89\u9738\u6b66\u6797\u5417\uff1f\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u5192\u51fa\u8fd9\u4e48\u4e00\u53e5\u53cd\u95ee\u4e4b\u8bcd\uff0c\u4f7f\u5f97\u51af\u96f7\u8d8a\u53d1\u5730\u5fc3\u5934\u72c2\u9707\uff0c\u6697\u9053\uff1a\u4e8b\u60c5\u679c\u7136\u8d8a\u6765\u8d8a\u590d\u6742\u4e86\u2026\u2026\uff0c\u4f46\u4ed6\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u8001\u5f1f\u4e0e\u5929\u9b54\u5973\u8ba4\u8bc6\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u51b7\u54fc\u9053\uff1a\u201c\u8fd9\u4e2a\u4f60\u7ba1\u4e0d\u7740\uff0c\u53ea\u8981\u56de\u7b54\u5728\u4e0b\u6240\u95ee\u4fbf\u662f\uff01\u201d \u3000\u3000\u51af\u96f7\u4f3c\u4e4e\u662f\u4e3a\u4ed6\u7684\u6c14\u52bf\u6240\u6151\uff0c\u8131\u53e3\u9053\uff1a\u201c\u90a3\u8fd8\u7528\u95ee\uff1f\u51af\u67d0\u4eba\u65e2\u7136\u6765\u5230\u6d1b\u9633\uff0c\u5f53\u7136\u662f\u5e94\u7ea6\u800c\u6765\u52a9\u5979\u7684\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u626c\u4e86\u626c\u7709\uff0c\u9053\uff1a\u201c\u5f88\u597d\uff01\u4f60\u4eec\u5148\u548c\u5b89\u8d24\u5f1f\u628a\u8fc7\u8282\u63ed\u8fc7\uff0c\u533a\u533a\u8fd8\u6709\u522b\u7684\u4e8b\u8bf7\u6559\uff01\u201d \u3000\u3000\u5927\u80dc\u5934\u5ffd\u7136\u5927\u559d\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6d12\u5bb6\u5c31\u5148\u5c06\u8fd9\u4e2b\u5934\u62ff\u4e0b\uff01\u201d \u3000\u3000\u84e6\u5730\u95ea\u8eab\u4e0a\u524d\uff0c\u9694\u7740\u65b9\u96ea\u5b9c\uff0c\u4fbf\u5411\u5b89\u5c0f\u840d\u6293\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u6362\u5728\u4e4e\u65e5\uff0c\u4e00\u5b9a\u4f1a\u62a2\u5148\u51fa\u624b\u62e6\u4f4f\u5927\u80dc\u5934\u9640\uff0c\u4f46\u6b64\u523b\u4ed6\u5374\u7aef\u5750\u4e0d\u52a8\uff0c\u773c\u770b\u7740\u5927\u80dc\u5934\u9640\u5411\u5b89\u5c0f\u840d\u6293\u53bb\uff0c\u8138\u4e0a\u8fd8\u9732\u51fa\u4e00\u4e1d\u7b11\u610f\u3002 \u3000\u3000\u5927\u80dc\u5934\u9640\u8fd9\u4e00\u6293\u4e4b\u529b\uff0c\u4f3c\u4e4e\u7528\u7684\u662f\u9e70\u722a\u529f\uff0c\u53ea\u89c1\u4ed6\u53f3\u624b\u7535\u95ea\u800c\u6765\uff0c\u4e00\u80a1\u5f3a\u5927\u7684\u529d\u98ce\uff0c\u900f\u8863\u800c\u5165\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u663e\u5f97\u65e0\u6bd4\u9547\u9759\uff0c\u76f4\u5230\u5927\u80dc\u5934\u9640\u7684\u624b\u6307\uff0c\u5df2\u7136\u89e6\u53ca\u5979\u7684\u8863\u886b\uff0c\u8fd9\u624d\u51b7\u7b11\u4e86\u4e00\u58f0\uff0c\u51fa\u6307\u5411\u90a3\u5927\u6307\u5934\u9640\u7684\u8098\u95f4\u4e00\u5f39\u3002 \u3000\u3000\u987f\u65f6\uff0c\u5927\u80dc\u5934\u9640\u66b4\u53eb\u4e86\u4e00\u58f0\uff0c\u4e00\u8fde\u5012\u9000\u4e86\u4e94\u6b65\u3002 \u3000\u3000\u51af\u96f7\u7709\u5934\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u548c\u5c1a\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u5927\u80dc\u5934\u9640\u4e00\u624b\u6309\u4f4f\u53f3\u65f6\uff0c\u5927\u53eb\u9053\uff1a\u201c\u8fd9\u4e2b\u5934\u7528\u7684\u4ec0\u4e48\u624b\u6cd5\uff1f\u6d12\u5bb6\u8fd9\u6761\u53f3\u81c2\u4f3c\u4e4e\u662f\u5b8c\u5566\u2026\u2026\u201d\u4e00\u8138\u6012\u610f\uff0c\u4f46\u5374\u4e0d\u6562\u518d\u4e0a\u524d\u53bb\u3002 \u3000\u3000\u51af\u96f7\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u53ea\u89c1\u90a3\u5916\u62ab\u9ec4\u886b\u7684\u5c11\u5973\uff0c\u4e1a\u5df2\u59d7\u59d7\u4e3e\u6b65\u8d70\u4e86\u8fc7\u6765\u3002\u5979\u8138\u4e0a\u5982\u7f69\u5bd2\u971c\uff0c\u53cc\u76ee\u6709\u4f3c\u5229\u5203\u822c\u6295\u6ce8\u5728\u5b89\u5c0f\u840d\u7684\u8138\u4e0a\uff0c\u5c16\u58f0\u9053\uff1a\u201c\u4f60\u53ef\u662f\u7528\u7684\u5929\u5fc3\u6307\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u4f60\u2026\u2026\u600e\u4e48\u4f1a\u8ba4\u5f97\u5929\u5fc3\u6307\uff1f\u201d \u3000\u3000\u90a3\u9ec4\u886b\u5c11\u5973\u8138\u4e0a\u7684\u5bd2\u971c\uff0c\u5239\u90a3\u95f4\u9000\u8d70\u4e86\u4e00\u534a\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u59d3\u5b89\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4e0d\u9519\u554a\uff01\u6211\u521a\u624d\u5df2\u7ecf\u8bf4\u8fc7\u5566\uff01\u201d \u3000\u3000\u9ec4\u886b\u5c11\u5973\u95fb\u8a00\uff0c\u7adf\u662f\u7b11\u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u5929\u5fc3\u5c9b\u4e3b\u5b89\u5927\u4fa0\uff0c\u662f\u59d1\u5a18\u7684\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u77aa\u7740\u5927\u773c\uff0c\u9053\uff1a\u201c\u6211\u7239\u7239\uff01\u201d \u3000\u3000\u9ec4\u886b\u5c11\u5973\u5ffd\u7136\u8d8b\u524d\u4e00\u6b65\uff0c\u798f\u4e86\u4e00\u798f\u9053\uff1a\u201c\u539f\u6765\u662f\u5c0f\u59d0\u5230\u4e86\uff01\u5a62\u5b50\u8fd9\u53a2\u62dc\u89c1\uff01\u201d \u3000\u3000\u5979\u7a81\u7136\u81ea\u79f0\u5a62\u5b50\uff0c\u53ea\u628a\u5b89\u5c0f\u840d\u542c\u5f97\u5446\u4e86\uff01\u67f3\u7709\u95ea\u52a8\uff0c\u6447\u5934\u9053\uff1a\u201c\u6211\u4e0d\u8ba4\u5f97\u4f60\u554a\uff01\u201d \u3000\u3000\u9ec4\u886b\u5c11\u5973\u9053\uff1a\u201c\u5a62\u5b50\u6bd5\u79cb\u84ec\uff0c\u5bb6\u6bcd\u7d2b\u51e4\uff0c\u53ca\u662f\u5c0f\u59d0\u7956\u6bcd\u8001\u592b\u4eba\u623f\u4e2d\u7684\u4e2b\u5934\uff0c\u5c0f\u59d0\u8fd8\u8bb0\u5f97\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6014\u4e86\u4e00\u6014\uff0c\u4f4e\u5934\u60f3\u4e86\u4e00\u4f1a\u513f\uff0c\u9053\uff1a\u201c\u7d2b\u59d1\u59d1\u662f\u4f60\u5a18\uff1f\u201d \u3000\u3000\u6bd5\u79cb\u83b2\u7b11\u9053\uff1a\u201c\u5c0f\u59d0\u60f3\u8d77\u6765\u4e86\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u4f60\u662f\u6bd5\u5927\u53d4\u7684\u5973\u513f\uff1f\u201d \u3000\u3000\u6bd5\u79cb\u83b2\u9053\uff1a\u201c\u6b63\u662f\u5a62\u5b50\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6b22\u547c\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6bd5\u5927\u53d4\u597d\u5417\uff1f\u4ed6\u2026\u2026\u73b0\u5728\u90a3\u91cc\uff1f\u6211\u597d\u4e45\u6ca1\u6709\u89c1\u5230\u4ed6\u4e86\uff01\u201d \u3000\u3000\u6bd5\u79cb\u83b2\u5ffd\u7136\u8138\u8272\u4e00\u9eef\uff0c\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u6211\u7239\u6b7b\u5566\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u6bd5\u5927\u53d4\u8fc7\u4e16\u4e86\u5417\uff1f\u600e\u5730\u6211\u6ca1\u6709\u542c\u8bf4\uff1f\u201d \u3000\u3000\u6bd5\u79cb\u83b2\u9053\uff1a\u201c\u7239\u7239\u8fc7\u4e16\u4e0d\u4e45\uff0c\u53ea\u6015\u9001\u4fe1\u7684\u4eba\u53bb\u81f3\u5c9b\u4e0a\u4e4b\u65f6\uff0c\u5c0f\u59d0\u5df2\u7136\u79bb\u5f00\u5929\u5fc3\u5c9b\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff01\u7d2b\u59d1\u59d1\u5462\uff1f\u5979\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u6bd5\u79cb\u83b2\u9053\uff1a\u201c\u5bb6\u6bcd\u4f4f\u5728\u666e\u9640\uff0c\u6253\u4ece\u4e09\u6708\u524d\u7239\u7239\u53bb\u4e16\u4e4b\u540e\uff0c\u5df2\u7136\u524a\u53d1\u51fa\u5bb6\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u8138\u4e0a\u6709\u4e9b\u60d8\u7136\u5931\u671b\u4e4b\u8272\uff0c\u53f9\u4e86\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u554a\uff01\u7d2b\u59d1\u59d1\u600e\u4f1a\u51fa\u4e86\u5bb6\u5462\uff1f\u201d \u3000\u3000\u6bd5\u79cb\u83b2\u8138\u4e0a\u7565\u7565\u9732\u51fa\u4e00\u4e1d\u7ea2\u6655\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u8fd9\u4e8b\u5a62\u5b50\u4e5f\u4e0d\u660e\u767d\u2026\u2026\u201d \u3000\u3000\u5979\u8bed\u97f3\u4e00\u8f6c\uff0c\u56de\u5934\u5411\u51af\u96f7\uff1a\u201c\u51af\u5927\u53d4\uff0c\u6c60\u5927\u53d4\u7684\u4ec7\uff0c\u662f\u4e0d\u80fd\u62a5\u7684\u4e86\uff01\u201d \u3000\u3000\u51af\u96f7\u542c\u5f97\u4e24\u4eba\u5bf9\u7b54\uff0c\u5fc3\u4e2d\u65e9\u5df2\u660e\u4e86\uff0c\u94c1\u6247\u5b50\u6c60\u5f6a\u7684\u7a7f\u816e\u4e4b\u6068\uff0c\u5df2\u7136\u6ca1\u6709\u6307\u671b\u635e\u56de\u9762\u5b50\u4e86\uff01\u662f\u4ee5\uff0c\u957f\u957f\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8d24\u4f84\u5973\uff0c\u8fd9\u4f4d\u5b89\u59d1\u5a18\uff0c\u773c\u4e0b\u53ef\u662f\u5929\u5fc3\u5c9b\u7684\u4e3b\u4eba\u4e86\uff1f\u201d \u3000\u3000\u6bd5\u79cb\u83b2\u9053\uff1a\u201c\u5c9b\u4e3b\u592b\u5987\u5df2\u7136\u8fc7\u4e16\uff0c\u5c0f\u59d0\u662f\u5c9b\u4e3b\u592b\u5987\u7684\u552f\u4e00\u9aa8\u8089\uff0c\u90a3\u5f53\u7136\u662f\u5c9b\u4e3b\u4e86\uff01\u201d \u3000\u3000\u5927\u59d3\u5934\u9640\u5ffd\u7136\u63a5\u9053\uff1a\u201c\u6d12\u5bb6\u8ba4\u4e3a\uff0c\u8fd9\u4f4d\u5973\u65bd\u4e3b\u65e2\u7136\u662f\u5929\u5fc3\u5c9b\u4e3b\uff0c\u54b1\u4eec\u5c31\u4e0d\u7528\u8ddf\u5979\u8ba1\u8f83\u4e86\uff01\u201d \u3000\u3000\u51af\u96f7\u5931\u58f0\u9053\uff1a\u201c\u8fd9\u8fd8\u7528\u4f60\u8bf4\u5417\uff1f\u201d \u3000\u3000\u6389\u5934\u5411\u90a3\u767d\u745b\u9053\uff1a\u201c\u5ac2\u592b\u4eba\uff0c\u6c60\u5144\u7684\u4ec7\uff0c\u54b1\u4eec\u662f\u4e0d\u80fd\u62a5\u590d\u7684\u4e86\uff01\u201d \u3000\u3000\u767d\u745b\u7c89\u8138\u4e0a\u6ee1\u662f\u6cea\u75d5\uff0c\u6ca1\u6709\u8bf4\u8bdd\uff0c\u4e5f\u6ca1\u6709\u70b9\u5173\u6216\u662f\u6447\u5934\uff0c\u4f7f\u4eba\u60f3\u4e0d\u900f\u5979\u7a76\u7adf\u662f\u5b89\u7740\u600e\u6837\u7684\u5fc3\u4e8b\uff01 \u3000\u3000\u6bd5\u79cb\u83b2\u7b49\u4e86\u4e00\u4f1a\u513f\u4e0d\u89c1\u5979\u8bf4\u8bdd\uff0c\u5fc3\u4e2d\u4f3c\u662f\u5927\u4e3a\u4e0d\u60a6\uff0c\u5c16\u58f0\u9053\uff1a\u201c\u767d\u59d0\u59d0\uff0c\u4f60\u600e\u4e48\u4e0d\u8bf4\u8bdd\uff1f\u51af\u5927\u53d4\u7684\u8bdd\uff0c\u4f60\u542c\u89c1\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u767d\u745b\u76ee\u5149\u4e2d\u542b\u7740\u8bb8\u591a\u6012\u610f\uff0c\u4f46\u5979\u5374\u672a\u66fe\u53d1\u4f5c\u51fa\u6765\uff0c\u53ea\u56e0\u5979\u6709\u6781\u5927\u7684\u5fcc\u8bb3\uff0c\u4e0d\u6562\u5bf9\u201c\u5929\u5fc3\u5c9b\u201d\u4e3b\u4eba\u4e0d\u656c\u3002 \u3000\u3000\u662f\u4ee5\u6bd5\u79cb\u83b2\u8fd9\u7b49\u76f8\u95ee\uff0c\u5979\u987f\u65f6\u4f4e\u4e0b\u4e86\u5934\u9053\uff1a\u201c\u542c\u89c1\u4e86\uff01\u5c0f\u59b9\u4e0d\u7528\u903c\u6211\uff0c\u6211\u660e\u767d\u4e0d\u80fd\u62a5\u4ec7\u4e86\uff01\u201d \u3000\u3000\u6bd5\u79cb\u83b2\u9053\uff1a\u201c\u8c22\u8c22\u4f60\u4e86\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u542c\u5f97\u5979\u4eec\u5bf9\u7b54\u4e4b\u8f9e\uff0c\u89c9\u5f97\u6709\u4e9b\u5947\u602a\uff0c\u4f4e\u4f4e\u4e00\u7b11\u9053\uff1a\u201c\u6bd5\u59d0\u59d0\uff0c\u5979\u5982\u662f\u5fc3\u4e2d\u4e0d\u670d\uff0c\u6211\u4e5f\u4e0d\u6015\u5979\u627e\u6211\u62a5\u4ec7\u554a\uff01\u201d \u3000\u3000\u6bd5\u79cb\u83b2\u9053\uff1a\u201c\u5979\u600e\u4e48\u6562\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u5979\u4e3a\u4ec0\u4e48\u4e0d\u6562\u5462\uff1f\u53ef\u662f\u5979\u6b66\u529f\u592a\u5dee\u5417\uff1f\u201d \u3000\u3000\u6bd5\u79cb\u83b2\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u4f11\u8bf4\u5979\u90a3\u5fae\u672a\u7684\u6b66\u529f\u65e0\u6cd5\u548c\u4f60\u76f8\u6bd4\uff0c\u5c31\u7b97\u5979\u6b66\u529f\u4e0e\u5c0f\u59d0\u76f8\u5dee\u4e0d\u591a\uff0c\u5979\u4e5f\u4e0d\u6562\u5411\u4f60\u62a5\u4ec7\u554a\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u90a3\u53c8\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6bd5\u79cb\u83b2\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u5a62\u5b50\u4e94\u4eba\u90fd\u662f\u4f4f\u5728\u4e1c\u6d77\uff0c\u636e\u6211\u7239\u8bf4\uff0c\u5f53\u5e74\u82e5\u975e\u8001\u4e3b\u4eba\u4e49\u51fa\u5a9b\u624b\uff0c\u4ed6\u4eec\u90fd\u5df2\u6ca1\u6709\u4e86\u6027\u547d\uff0c\u5979\u4eec\u4e0d\u4e0e\u5929\u5fc3\u5c9b\u4eba\u4e3a\u4ec7\uff0c\u90a3\u4e5f\u4e0d\u8db3\u4e3a\u5947\u554a\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff01\u201d \u3000\u3000\u6bd5\u79cb\u83b2\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u4f60\u5230\u4e2d\u539f\u6765\uff0c\u4e3a\u4e86\u4f55\u4e8b\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u8fd9\u4e2a\u5417\uff1f\u8ddf\u4f60\u4eec\u76f8\u540c\uff01\u201d \u3000\u3000\u6bd5\u79cb\u83b2\u4e00\u6014\u9053\uff1a\u201c\u5c0f\u59d0\u4e5f\u662f\u88ab\u5929\u9b54\u5973\u8bf7\u6765\u7684\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u8138\u8272\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u662f\uff01\u6211\u7684\u6765\u610f\uff0c\u5374\u4e0e\u4f60\u4eec\u6b63\u597d\u76f8\u53cd\u3002\u201d \u3000\u3000\u6bd5\u79cb\u83b2\u5446\u4e86\u4e00\u5446\uff0c\u4f46\u5374\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u76f4\u5230\u6b64\u523b\u65b9\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u6709\u4e00\u53e5\u8bdd\u4e0d\u77e5\u5f53\u4e0d\u5f53\u8bf4\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4ec0\u4e48\u8bdd\uff1f\u5927\u54e5\u8bf7\u8bb2\u4fbf\u662f\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u60f3\u90a3\u5929\u9b54\u5973\u5982\u6b64\u5174\u5e08\u52a8\u4f17\uff0c\u4e0d\u60dc\u628a\u8fd9\u51e0\u4f4d\u957f\u5e74\u9690\u5c45\u7684\u9ad8\u4eba\u60c5\u51fa\u5c71\u6765\uff0c\u5979\u90a3\u72ec\u9738\u6b66\u6797\u4e4b\u5fc3\uff0c\u541e\u5e76\u4e5d\u5927\u95e8\u6d3e\u7684\u4e3e\u52a8\uff0c\u53ea\u6015\u5df2\u7136\u8feb\u5728\u7709\u776b\u4e4b\u95f4\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4e0d\u9519\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u6211\u60f3\u2026\u2026\u201d\u4ed6\u76ee\u5149\u5728\u6bd5\u79cb\u83b2\u548c\u51af\u96f7\u8eab\u4e0a\u4e00\u8f6c\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4ed6\u4eec\u90fd\u662f\u5e94\u90a3\u4e94\u5927\u9b54\u4e4b\u4e3b\u8bf7\u800c\u6765\uff0c\u5018\u662f\u54b1\u4eec\u80fd\u8981\u4ed6\u4eec\u91dc\u5e95\u62bd\u85aa\uff0c\u4e5f\u8bb8\u2026\u2026\u5bf9\u6b66\u6797\u4e43\u662f\u4e00\u5927\u52a9\u76ca\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u5c31\u662f\u8fd9\u4e2a\u4e8b\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u662f\u554a\uff01\u8d24\u5f1f\u53ef\u5426\u8ddf\u4ed6\u4eec\u8bf4\u4e0a\u4e00\u8bf4\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u5f53\u7136\u884c\u2026\u2026\u201d \u3000\u3000\u5979\u8f6c\u5934\u5411\u6bd5\u79cb\u83b2\u9053\uff1a\u201c\u6bd5\u59d0\u59d0\uff0c\u4f60\u610f\u4e0b\u5982\u4f55\uff1f\u201d \u3000\u3000\u6bd5\u79cb\u83b2\u5ae3\u7136\u4e00\u7b11\u9053\uff1a\u201c\u5c0f\u59d0\u8bf4\u7684\u8bdd\uff0c\u813e\u5b50\u600e\u6562\u4e0d\u9075\uff1f\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u613f\u4e0d\u613f\u610f\uff0c\u5a62\u5b50\u53ef\u5c31\u4e0d\u6562\u4fdd\u9669\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u542c\u5f97\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u4e0d\u7b54\u5e94\uff1f\u201d \u3000\u3000\u6bd5\u79cb\u83b2\u4e3a\u96be\u5730\u770b\u4e86\u51af\u96f7\u4e00\u773c\uff0c\u51af\u96f7\u88c5\u4f5c\u6ca1\u6709\u77a7\u89c1\uff0c\u4f46\u90a3\u5927\u80dc\u5934\u9640\u5374\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6d12\u5bb6\u7b49\u4eba\u5b9e\u96be\u5e94\u5141\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u53cc\u773c\u4e4b\u4e2d\uff0c\u5ffd\u7136\u95f4\u5bd2\u5149\u66b4\u5c04\uff0c\u7c89\u8138\u4e4b\u4e0b\uff0c\u6cdb\u8d77\u4e86\u7ea2\u6655\uff0c\u663e\u7136\uff0c\u5979\u53c8\u8981\u4f7f\u6027\u5b50\u4e86\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u7779\u72b6\uff0c\u5fd9\u7b11\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4f60\u5207\u83ab\u53d1\u4f5c\uff0c\u8fd9\u4e8b\u7531\u6211\u6765\u8ddf\u4ed6\u4eec\u6253\u4e2a\u4ea4\u9053\uff01\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u5411\u90a3\u5927\u80dc\u5934\u9640\u7b11\u9053\uff1a\u201c\u5927\u5e08\uff0c\u4f60\u4eec\u4e0d\u7b54\u5e94\uff0c\u662f\u5426\u8ba4\u4e3a\u5929\u9b54\u5973\u4e00\u5b9a\u4f1a\u6210\u529f\uff1f\u201d \u3000\u3000\u5927\u80dc\u5934\u9640\u9053\uff1a\u201c\u6d12\u5bb6\u77a7\u4e0d\u51fa\u9b54\u5973\u6709\u4f55\u5931\u7b56\u4e4b\u5904\uff0c\u662f\u4ee5\u6d12\u5bb6\u76f8\u4fe1\u5929\u9b54\u5973\u65bd\u4e3b\uff0c\u4e5d\u6210\u80fd\u591f\u5982\u613f\u4ee5\u507f\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u5927\u5e08\u662f\u8ba4\u4e3a\u4e5d\u5927\u95e8\u6d3e\u65e0\u529b\u652f\u6301\u7684\u4e86\uff1f\u201d \u3000\u3000\u5927\u80dc\u5934\u9640\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u5927\u5e08\uff0c\u4f60\u53ef\u66fe\u5c06\u5728\u4e0b\u7b97\u8ba1\u5728\u5185\uff1f\u201d \u3000\u3000\u5927\u80dc\u5934\u9640\u4e00\u6014\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u5927\u80dc\u5934\u9640\u7b11\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\uff0c\u4f60\u662f\u54ea\u4e00\u95e8\u6d3e\u5f1f\u5b50\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5728\u4e0b\u4e0d\u5c5e\u4efb\u4f55\u95e8\u6d3e\uff01\u201d \u3000\u3000\u5927\u80dc\u5934\u9640\u5927\u7b11\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\uff0c\u4f60\u65e2\u7136\u4e0d\u662f\u4e5d\u5927\u95e8\u6d3e\u7684\u5f1f\u5b50\uff0c\u6d12\u5bb6\u81ea\u7136\u662f\u4e0d\u7528\u628a\u4f60\u8ba1\u7b97\u5728\u5185\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u5927\u5e08\uff0c\u4f60\u53ef\u77e5\u9053\uff0c\u90a3\u5c11\u6797\u638c\u95e8\u4eba\u5374\u628a\u533a\u533a\u8bf7\u5230\u4e86\u5c11\u6797\u5bfa\u4e2d\u5750\u9547\u5417\uff1f\u201d \u3000\u3000\u5927\u80dc\u5934\u9640\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\u5750\u9547\u5c11\u6797\u4f5c\u751a\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u7b49\u90a3\u5929\u9b54\u5973\u524d\u6765\u554a\uff01\u201d \u3000\u3000\u5927\u80dc\u5934\u9640\u7b11\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\uff0c\u542c\u4f60\u7684\u53e3\u6c14\uff0c\u4f3c\u662f\u4f60\u6bd4\u5929\u9b54\u5973\u8fd8\u8981\u9ad8\u660e\u5f97\u591a\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5927\u5e08\u53ef\u662f\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u5927\u80dc\u5934\u9640\u7b11\u9053\uff1a\u201c\u6d12\u5bb6\u6b63\u662f\u6709\u4e9b\u4e0d\u4fe1\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5927\u5e08\uff0c\u4ee5\u4f60\u7684\u6b66\u529f\u6bd4\u90a3\u5929\u9b54\u5973\u5982\u4f55\uff1f\u201d \u3000\u3000\u5927\u80dc\u5934\u9640\u7b11\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\uff0c\u6d12\u5bb6\u7684\u6b66\u529f\u867d\u7136\u4e0d\u5f31\uff0c\u4f46\u6bd4\u8d77\u5929\u9b54\u5973\u65bd\u4e3b\uff0c\u90a3\u81ea\u7136\u5dee\u5f97\u5f88\u8fdc\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u4e94\u4f4d\u4e4b\u4e2d\uff0c\u54ea\u4e00\u4f4d\u7684\u6b66\u529f\u6bd4\u90a3\u5929\u9b54\u5973\u66f4\u597d\uff1f\u201d \u3000\u3000\u4ed6\u8fd9\u7b49\u95ee\u6cd5\uff0c\u53ea\u628a\u90d1\u5927\u521a\u542c\u5f97\u6014\u4e86\u4e00\u6014\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u600e\u4e48\u4f1a\u8fd9\u4e48\u95ee\u5462\uff1f\u4ed6\u4eec\u4e94\u4eba\u7684\u6b66\u529f\uff0c\u81ea\u7136\u6bd4\u4e0d\u4e0a\u5929\u9b54\u5973\u7684\u4e86\uff01\u5426\u5219\uff0c\u90a3\u5929\u9b54\u5973\u53c8\u600e\u80fd\u628a\u4ed6\u4eec\u53eb\u6765\uff1f\u201d \u3000\u3000\u5927\u80dc\u5934\u9640\u95fb\u8a00\u4e5f\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\uff0c\u54b1\u4eec\u8fd9\u4f4d\u5973\u65bd\u4e3b\u7684\u6b66\u529f\uff0c\u81ea\u7136\u662f\u6bd4\u4e0d\u4e0a\u5929\u9b54\u5973\u7684\u3002\u201d\u4ed6\u5ffd\u7136\u56de\u5934\u770b\u4e86\u767d\u745b\u4e00\u773c\uff0c\u7b11\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\uff0c\u54b1\u4eec\u8fd9\u4f4d\u5973\u65bd\u4e3b\u7684\u6b66\u529f\uff0c\u6bd4\u5929\u9b54\u5973\u4e5f\u5dee\u4e0d\u4e86\u591a\u5c11\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5dee\u4e0d\u4e86\u591a\u5c11\u5417\uff1f\u201d \u3000\u3000\u5927\u80dc\u5934\u9640\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\uff0c\u4f60\u5982\u679c\u60f3\u8bd5\u8bd5\uff0c\u4f55\u4e0d\u51fa\u6765\u5411\u5979\u53eb\u9635\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u662f\u554a\uff01\u5728\u4e0b\u6b63\u6709\u6b64\u5fc3\uff01\u201d\u53ea\u89c1\u4ed6\u7f13\u7f13\u5730\u7ad9\u4e86\u8d77\u6765\uff0c\u8d70\u5230\u5927\u80dc\u5934\u9640\u8eab\u524d\uff0c\u63a5\u9053\uff1a\u201c\u5927\u5e08\uff0c\u4f60\u4eec\u95ea\u5f00\u4e86\uff01\u201d \u3000\u3000\u5927\u80dc\u5934\u9640\u62c9\u4e00\u628a\u51af\u96f7\uff0c\u4fbf\u5411\u4e00\u65c1\u95ea\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u5374\u5411\u6bd5\u79cb\u83b2\u62db\u4e86\u62db\u624b\uff0c\u7b11\u9053\uff1a\u201c\u6bd5\u59d0\u59d0\uff0c\u4f60\u5750\u4e0b\u554a\uff01\u201d \u3000\u3000\u6bd5\u79cb\u83b2\u6709\u4e9b\u53d7\u5e9e\u82e5\u60ca\uff0c\u6014\u4e86\u4e00\u6014\u4e4b\u540e\uff0c\u4e00\u8fb9\u8bf4\u4e86\u51e0\u4e2a\u8c22\u5b57\uff0c\u8fd9\u624d\u5750\u5728\u5b89\u5c0f\u840d\u8eab\u8fb9\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5251\u7709\u4e00\u626c\uff0c\u5411\u90a3\u4ecd\u5728\u63a9\u9762\u4f4e\u6ce3\u7684\u767d\u745b\u6717\u58f0\u9053\uff1a\u201c\u767d\u745b\uff0c\u4f60\u6562\u4e0d\u6562\u8ddf\u533a\u533a\u8f83\u91cf\u4e00\u4e0b\uff1f\u201d \u3000\u3000\u767d\u745b\u4e00\u8154\u6028\u6012\u6b63\u662f\u65e0\u5904\u53d1\u6cc4\uff0c\u95fb\u8a00\u8793\u9996\u4e00\u62ac\uff0c\u51b7\u51b7\u5730\u770b\u4e86\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u5a07\u8eaf\u4e00\u95ea\u800c\u8d77\uff0c\u98d8\u8eab\u8d70\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u5979\u67f3\u7709\u542b\u715e\uff0c\u8d70\u5230\u65b9\u96ea\u5b9c\u8eab\u524d\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u8981\u627e\u6211\u52a8\u624b\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u4e0d\u9519\u554a\uff01\u59d1\u5a18\u6562\u4e0d\u6562\uff1f\u201d \u3000\u3000\u767d\u745b\u51b7\u51b7\u5730\u62d4\u51fa\u8eab\u540e\u53cc\u5200\uff0c\u559d\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u6562\uff1f\u4f60\u62d4\u5251\u5427\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fae\u5fae\u4e00\u7b11\uff0c\u6a21\u4e86\u6478\u8eab\u8fb9\u957f\u5251\uff0c\u63a5\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u5728\u4e0b\u5982\u662f\u62d4\u5251\uff0c\u53ea\u6015\u4f60\u4e0d\u662f\u5728\u4e0b\u624b\u4e2d\u5341\u5408\u4e4b\u5c06\u4e86\u3002\u201d \u3000\u3000\u767d\u745b\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u72c2\u5f97\u5f88\u554a\uff01\u90a3\u5929\u9b54\u5973\u4e5f\u8ddf\u6211\u5f00\u4e86\u767e\u62db\uff0c\u624d\u80dc\u6211\u4e00\u62db\uff0c\u4f60\u6562\u8bf4\u5341\u62db\u4e4b\u5185\uff0c\u80dc\u5f97\u4e86\u6211\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u4e0d\u4fe1\uff0c\u54b1\u4eec\u8d4c\u70b9\u4ec0\u4e48\u5982\u4f55\uff1f\u201d \u3000\u3000\u767d\u745b\u9053\uff1a\u201c\u8d4c\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5982\u679c\u5341\u62db\u4e4b\u5185\uff0c\u65b9\u67d0\u80dc\u4e86\u4f60\uff0c\u8db3\u89c1\u90a3\u5929\u9b54\u5973\u7684\u6b66\u529f\uff0c\u4e0e\u5728\u4e0b\u76f8\u5dee\u6781\u8fdc\uff0c\u4f60\u4eec\u4e5f\u4e0d\u7528\u76f8\u52a9\u4e8e\u4ed6\u7684\u4e86\uff0c\u662f\u4ee5\uff0c\u5728\u4e0b\u60f3\u8d4c\u7684\uff0c\u4fbf\u662f\u8fd9\u4e2a\uff01\u201d \u3000\u3000\u767d\u745b\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u8fd9\u4e8b\u53ea\u80fd\u9650\u4e8e\u59be\u8eab\u592b\u5987\u4e8c\u4eba\uff0c\u5927\u80dc\u5934\u9640\u4e09\u4f4d\uff0c\u59be\u8eab\u53ef\u4e0d\u6562\u4ee3\u4e3a\u7b54\u5e94\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u5f88\u597d\uff0c\u65b9\u67d0\u5c31\u8d4c\u4e24\u4f4d\u8fdf\u51fa\u5427\uff01\u201c\u767d\u745b\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u59be\u8eab\u51fa\u9053\u4ee5\u6765\uff0c\u5927\u5c0f\u6570\u767e\u6218\uff0c\u4e5f\u66fe\u8d25\u8fc7\u51e0\u6b21\uff0c\u4f46\u4ece\u672a\u5c11\u4e8e\u5341\u4e94\u62db\uff0c\u5c0f\u5144\u5f1f\u5982\u6b64\u72c2\u50b2\uff0c\u5230\u662f\u6211\u751f\u5e73\u7b2c\u4e00\u6b21\u89c1\u5230\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u4f60\u5982\u4e0d\u4fe1\uff0c\u4e00\u8bd5\u4f46\u89c1\u5206\u6653\u4e86\uff01\u201d \u3000\u3000\u767d\u745b\u4e00\u63e1\u53cc\u5200\u9053\uff1a\u201c\u62d4\u5251\u5427\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fae\u5fae\u4e00\u7b11\uff0c\u7f13\u7f13\u5730\u62d4\u51fa\u957f\u5251\u3002 \u3000\u3000\u90a3\u6bd5\u79cb\u83b2\u4e0d\u77e5\u65b9\u96ea\u5b9c\u7684\u6b66\u529f\u9ad8\u4f4e\uff0c\u89c1\u4ed6\u8fd9\u7b49\u5927\u8a00\uff0c\u4e0d\u628a\u767d\u745b\u653e\u5728\u773c\u4e2d\uff0c\u82b3\u5fc3\u6697\u6697\u5403\u60ca\uff0c\u6389\u5934\u5411\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u8fd9\u4f4d\u65b9\u516c\u5b50\u6b66\u529f\u5f88\u597d\u5417\uff1f\u90a3\u767d\u745b\u53ef\u4e0d\u5bb9\u8f7b\u654c\u554a\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u6bd5\u59d0\u59d0\uff0c\u767d\u745b\u6b66\u529f\u867d\u597d\uff0c\u4f46\u5982\u60f3\u5728\u65b9\u5927\u54e5\u624b\u4e0b\u8d70\u6ee1\u5341\u62db\uff0c\u53ea\u6015\u8fd8\u529e\u4e0d\u5230\u5462\uff01\u201d \u3000\u3000\u6bd5\u79cb\u83b2\u7b11\u9053\uff1a\u201c\u771f\u7684\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u5f53\u7136\u662f\u771f\u7684\u4e86\uff01\u201d \u3000\u3000\u4e24\u4eba\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u65b9\u96ea\u5b9c\u5df2\u7136\u6267\u5251\u5728\u624b\uff0c\u7b11\u9053\uff1a\u201c\u767d\u59d1\u5a18\uff0c\u54b1\u4eec\u8981\u4e0d\u8981\u627e\u4eba\u8ba1\u7b97\u4e00\u4e0b\u62db\u6570\uff1f\u201d \u3000\u3000\u767d\u745b\u9053\uff1a\u201c\u5f53\u7136\u8981\uff01\u201d \u3000\u3000\u5979\u76ee\u5149\u5728\u51af\u96f7\u8eab\u4e0a\u4e00\u8f6c\uff0c\u9053\uff1a\u201c\u51af\u5144\uff0c\u6709\u52b3\u4f60\u8ba1\u6570\u5982\u4f55\uff1f\u201d \u3000\u3000\u51af\u96f7\u7b11\u9053\uff1a\u201c\u5144\u5f1f\u7406\u5e94\u6548\u52b3\u2026\u2026\u201d \u3000\u3000\u767d\u745b\u53cc\u81c2\u4e00\u632f\uff0c\u4e24\u67c4\u5355\u5200\uff0c\u95ea\u8d77\u4e86\u4e00\u7247\u94f6\u5149\uff0c\u8106\u559d\u4e00\u58f0\u9053\uff1a\u201c\u65b9\u516c\u5b50\uff0c\u52a8\u624b\u554a\uff01\u201d \u3000\u3000\u5979\u7a76\u7adf\u5927\u4e86\u51e0\u5c81\u5e74\u7eaa\uff0c\u4e0d\u597d\u610f\u601d\u62a2\u5148\u51fa\u624b\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u542c\u5979\u4e4b\u8a00\uff0c\u90a3\u5927\u9b54\u5973\u5c1a\u8981\u767e\u62db\u4e4b\u540e\uff0c\u65b9\u53ef\u5c06\u5979\u51fb\u8d25\uff0c\u60f3\u5fc5\u8fd9\u5973\u4eba\u7684\u4e00\u8eab\u6b66\u529f\uff0c\u4e0d\u5728\u90a3\u67f3\u5a9a\u5a18\u4e4b\u4e0b\uff0c\u6211\u5982\u4e0d\u62a2\u5148\u52a8\u624b\uff0c\u5f53\u771f\u662f\u5931\u4e86\u5148\u673a\uff0c\u90a3\u5c31\u4e0a\u4e86\u5979\u7684\u5927\u5f53\u4e86\u2026\u2026\u201d \u3000\u3000\u8f6c\u5ff5\u81f3\u6b64\uff0c\u5c31\u4e0d\u518d\u8c26\u8ba9\uff0c\u4e00\u626c\u624b\u4e2d\u5b9d\u5251\uff0c\u7b11\u9053\uff1a\u201c\u65b9\u67d0\u606d\u656c\u4e0d\u5982\u4ece\u547d\uff01\u201d\u5237\u5730\u4e00\u58f0\uff0c\u4e3e\u5251\u5e73\u523a\u800c\u51fa\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u5251\u523a\u51fa\uff0c\u53bb\u52bf\u4e0d\u89c1\u591a\u5feb\uff0c\u4f46\u767d\u745b\u5374\u662f\u8138\u8272\u5927\u53d8\uff0c\u5c16\u58f0\u9053\uff1a\u201c\u597d\u5251\u6cd5\uff0c\u516c\u5b50\u679c\u7136\u6b66\u529f\u9ad8\u660e\u5f97\u5f88\u2026\u2026\u201d \u3000\u3000\u53cc\u5200\u5e76\u4e3e\uff0c\u4ea4\u53c9\u5411\u65b9\u96ea\u5b9c\u5251\u4e0a\u78d5\u53bb\u3002 \u3000\u3000\u6562\u60c5\u65b9\u96ea\u5b9c\u8fd9\u4e00\u5251\u4e43\u662f\u6697\u85cf\u4e86\u51e0\u5f0f\u53d8\u5316\u7684\u5389\u5bb3\u7684\u62db\u672f\uff0c\u767d\u745b\u5018\u662f\u95ea\u8eab\u9000\u8ba9\uff0c\u4e08\u8bb8\u4e4b\u5185\uff0c\u90fd\u5c06\u88ab\u65b9\u96ea\u5b9c\u5c01\u65ad\u4e86\u53bb\u8def\uff0c\u53ea\u6015\u4e00\u62db\u4e4b\u4e0b\uff0c\u4fbf\u8981\u4e22\u4eba\u4e86\uff01 \u3000\u3000\u767d\u745b\u7adf\u7136\u5728\u4e00\u77a5\u4e4b\u95f4\uff0c\u4f7f\u80fd\u77a7\u51fa\u8fd9\u62db\u5251\u6cd5\u7684\u5389\u5bb3\uff0c\u8db3\u89c1\u5979\u7684\u773c\u529b\u4e0d\u5f31\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u76ee\u7779\u53cc\u5200\u540c\u51fa\uff0c\u4f3c\u662f\u8981\u5c06\u81ea\u5df1\u7684\u957f\u5251\u5939\u4f4f\uff0c\u5fc3\u4e2d\u6697\u6697\u5931\u7b11\uff0c\u5fd6\u9053\uff1a\u201c\u5979\u53ea\u770b\u5f97\u51fa\u6211\u7684\u524d\u4e09\u5f0f\u53d8\u5316\uff0c\u5374\u4e0d\u77e5\u6211\u8fd9\u62db\u5251\u6cd5\u5171\u6709\u4e94\u5f0f\u53d8\u62db\u2026\u2026\u201d\u5bfb\u601d\u4e4b\u95f4\uff0c\u53f3\u8155\u4e00\u6c89\uff0c\u5b9d\u5251\u5ffd\u7136\u4e00\u7a7f\u800c\u51fa\u3002 \u3000\u3000\u7531\u90a3\u767d\u745b\u7684\u53cc\u5200\u4e4b\u4e2d\u8131\u51fa\u3002 \u3000\u3000\u767d\u745b\u6014\u5f97\u4e00\u6014\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u53f3\u624b\u5355\u5200\u4e00\u626c\uff0c\u62a4\u4f4f\u4e86\u81ea\u5df1\uff0c\u5de6\u624b\u7684\u5355\u5200\uff0c\u5374\u662f\u5c3d\u529b\u5411\u65b9\u96ea\u5b9c\u5251\u8eab\u62cd\u6765\uff0c\u8fd9\u4e00\u624b\u53d8\u62db\uff0c\u5230\u4e5f\u6070\u5230\u597d\u5904\uff0c\u8feb\u4f7f\u65b9\u96ea\u5b9c\u7684\u5b9d\u5251\uff0c\u4e0d\u6562\u518d\u5411\u524d\u51fb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u597d\u5200\u6cd5\uff01\u201d\u957f\u5251\u500f\u5730\u62bd\u56de\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u7b2c\u4e8c\u62db\uff01\u201d \u3000\u3000\u4f46\u89c1\u6676\u5149\u7535\u821e\uff0c\u957f\u5251\u659c\u98de\u800c\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u5251\u6cd5\u51fa\u624b\uff0c\u90a3\u201c\u5438\u8840\u9b3c\u5200\u201d\u51af\u96f7\u4e0d\u7531\u5f97\u5931\u58f0\u5927\u53eb\u9053\uff1a\u201c\u9f99\u884c\u516b\u5251\uff01\u201d \u3000\u3000\u5927\u80dc\u5934\u9640\u8be7\u9053\uff1a\u201c\u4ed6\u662f\u5251\u795e\u7684\u5f1f\u5b50\uff1f\u201d \u3000\u3000\u767d\u745b\u4f3c\u662f\u4e5f\u770b\u51fa\u8fd9\u62db\u5251\u6cd5\u7684\u5389\u5bb3\uff0c\u4e0d\u6562\u4e3e\u5200\u62e6\u67b6\uff0c\u5374\u662f\u98de\u8eab\u5411\u540e\u8dc3\u9000\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u6709\u5fc3\u7acb\u5a01\uff0c\u81ea\u7136\u518d\u4e5f\u4e0d\u6015\u70ab\u8000\uff0c\u5927\u559d\u4e00\u58f0\u9053\uff1a\u201c\u54ea\u91cc\u8d70\uff01\u201d\u5bd2\u5149\u4e4d\u52a8\uff0c\u4e1d\u4e1d\u51b7\u6c14\u903c\u4eba\u7709\u5b87\u3002 \u3000\u3000\u663e\u7136\uff0c\u65b9\u96ea\u5b9c\u8fd9\u4e00\u5251\u5982\u662f\u523a\u5b9e\uff0c\u90a3\u767d\u745b\u5fc5\u5c06\u91cd\u521b\u5728\u4ed6\u5251\u4e86\uff01 \u3000\u3000\u4f46\u6b64\u65f6\u53ea\u542c\u5f97\u90a3\u53e6\u4e00\u684c\u4e0a\u7684\u201c\u5343\u624b\u795e\u7fc1\u201d\u6e38\u516c\u7384\u5927\u53eb\u9053\uff1a\u201c\u8001\u5f1f\u5251\u4e0b\u7559\u60c5\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8033\u95fb\u6e38\u516c\u7384\u559d\u58f0\uff0c\u624b\u4e2d\u5251\u52bf\u987f\u65f6\u4e00\u7f13\uff0c\u867d\u662f\u5982\u6b64\uff0c\u4ecd\u7136\u5c06\u90a3\u767d\u745b\u4f24\u5728\u5251\u950b\u4e4b\u4e0b\uff0c\u4e00\u7247\u767d\u886b\u8dcc\u843d\uff0c\u51e0\u70b9\u9c9c\u8840\uff0c\u987a\u7740\u5de6\u80a9\u6d12\u5728\u767d\u886b\u4e4b\u4e0a\uff0c\u767d\u745b\u5ffd\u7136\u629b\u53bb\u53cc\u5200\uff0c\u53f3\u624b\u7d27\u62c9\u7740\u788e\u88c2\u7684\u767d\u886b\uff0c\u5411\u540e\u8fde\u9000\u4e94\u5c3a\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5b9d\u5251\u5165\u9798\uff0c\u6717\u58f0\u4e00\u7b11\u9053\uff1a\u201c\u627f\u8ba9\u4e86\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u8fd9\u65f6\u5927\u6b65\u8d70\u4e86\u8fc7\u6765\uff0c\u76ee\u5149\u5728\u90a3\u201c\u94c1\u6247\u5b50\u201d\u6c60\u5f6a\u3001\u201c\u7389\u65e0\u5e38\u201d\u767d\u745b\u4e24\u4eba\u8eab\u4e0a\u4e00\u6e9c\uff0c\u51b7\u51b7\u559d\u9053\uff1a\u201c\u8d24\u4f09\u4fea\u53ef\u4ee5\u53bb\u4e86\uff01\u201d \u3000\u3000\u767d\u745b\u6068\u6068\u5730\u770b\u4e86\u6e38\u516c\u7384\u4e00\u773c\uff0c\u7adf\u662f\u4e00\u8a00\u4e0d\u53d1\uff0c\u548c\u90a3\u6c60\u5f6a\u7ad9\u4e86\u8d77\u6765\uff0c\u75be\u6b65\u4e0b\u697c\u800c\u53bb\u3002 \u3000\u3000\u6e38\u516c\u7384\u5475\u5475\u4e00\u7b11\u9053\uff1a\u201c\u75db\u5feb\uff01\u75db\u5feb\uff0c\u8001\u673d\u529d\u52a8\u4e86\u5929\u53f0\u4e09\u7edd\uff0c\u8001\u5f1f\u5374\u6c14\u8d70\u4e86\u4e94\u5996\u4e2d\u7684\u4e8c\u5996\uff0c\u770b\u6765\u5929\u9b54\u5973\u53ef\u4e0d\u5229\u5f97\u5f88\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5fae\u5fae\u4e00\u7b11\uff0c\u5411\u6e38\u516c\u7384\u62b1\u62f3\u9053\uff1a\u201c\u8001\u524d\u8f88\u597d\u8bf4\uff0c\u5feb\u8bf7\u4e0a\u5ea7\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u5fd9\uff01\u8001\u5f1f\uff0c\u4f60\u53ef\u662f\u5251\u795e\u9648\u5144\u7684\u4f20\u4eba\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u665a\u8f88\u987a\u5f97\u540d\u5e08\uff0c\u4f46\u5374\u4e0d\u66fe\u5b66\u5f97\u6069\u5e08\u6b66\u529f\u5341\u4e4b\u4e8c\u4e09\uff0c\u60ed\u6127\u5f97\u5f88\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u5012\u662f\u8c26\u865a\u5f97\u5f88\u554a\uff01\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u8001\u673d\u4e0e\u4ee4\u5e08\u4e00\u522b\u591a\u5e74\uff0c\u76ee\u524d\u66fe\u95fb\u4f20\u8a00\uff0c\u9648\u5144\u5df2\u5f52\u9053\u5c71\uff0c\u4e0d\u77e5\u662f\u771f\u662f\u5047\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5148\u5e08\u786e\u7136\u5df2\u7ecf\u901d\u4e16\u4e86\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u957f\u957f\u5730\u5401\u4e86\u53e3\u6c14\uff0c\u63a5\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u65e2\u662f\u9648\u5144\u5f1f\u5b50\uff0c\u90a3\u81ea\u7136\u8981\u7ee7\u627f\u4f60\u5e08\u7236\u7684\u9057\u5fd7\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u665a\u8f88\u5f29\u9a77\u4e4b\u6750\uff0c\u53ea\u6015\u96be\u5f53\u5927\u4efb\u3002\u201d \u3000\u3000\u6e38\u516c\u7384\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u81ea\u8c26\u4e86\uff01\u8001\u673d\u521a\u624d\u5df2\u7ecf\u89c1\u8bc6\u8fc7\u4f60\u7684\u5251\u6cd5\uff0c\u529f\u529b\u6216\u6709\u4e0d\u53ca\u9648\u5144\u4e4b\u5904\uff0c\u4f46\u53d8\u5316\u4e4b\u5999\uff0c\u663e\u7136\u5df2\u7ecf\u8d85\u8fc7\u9648\u5144\u4e0d\u5c11\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8001\u524d\u8f88\u8fc7\u5956\u4e86\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u76ee\u5149\u4e00\u8f6c\uff0c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u54b1\u4eec\u8fd9\u697c\u4e0a\u5171\u6709\u5341\u4eba\uff0c\u4f55\u4e0d\u53eb\u5e97\u5bb6\u5e76\u5728\u4e00\u5904\u843d\u5ea7\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u5c31\u4f9d\u8001\u524d\u8f88\u3002\u201d \u3000\u3000\u90a3\u8dd1\u5802\u7684\u4f19\u8ba1\uff0c\u867d\u7136\u4e2a\u4e2a\u80c6\u98a4\u5fc3\u60ca\uff0c\u5374\u4ecd\u7136\u8eb2\u5728\u89d2\u843d\u91cc\u4e0d\u6562\u79bb\u53bb\uff0c\u8fd9\u65f6\u773c\u89c1\u4eec\u4e0d\u518d\u52a8\u624b\uff0c\u5e74\u7eaa\u7a0d\u5927\u7684\u4e00\u4e24\u4eba\uff0c\u8d70\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u6e38\u516c\u7384\u6307\u70b9\u4ed6\u4eec\u91cd\u65b0\u5f00\u4e0a\u4e00\u684c\u9152\u83dc\uff0c\u5341\u4eba\u56f4\u5728\u4e00\u5f20\u5706\u684c\u4e4b\u4e0a\u3002 \u3000\u3000\u6e38\u516c\u7384\u5206\u522b\u66ff\u51e0\u4eba\u5f15\u89c1\u4e4b\u540e\uff0c\u8fd9\u624d\u7b11\u9053\uff1a\u201c\u5929\u53f0\u4e09\u4f4d\u8001\u5f1f\uff0c\u5df2\u7136\u51b3\u5fc3\u4e0d\u518d\u76f8\u52a9\u5929\u9b54\u5973\u4e86\uff0c\u6c60\u5f6a\u592b\u5987\uff0c\u4e5f\u5df2\u751f\u6c14\u9000\u51fa\uff0c\u53ea\u4e0d\u77e5\u51af\u8001\u5f1f\u4e09\u4f4d\uff0c\u53c8\u5c06\u5982\u4f55\u6253\u7b97\uff1f\u201d\u663e\u7136\uff0c\u6e38\u516c\u7384\u4e00\u5fc3\u8981\u5c06\u4ed6\u4eec\u4e5f\u62c9\u62e2\u8fc7\u6765\u3002 \u3000\u3000\u4ed6\u8bdd\u97f3\u752b\u843d\uff0c\u6bd5\u79cb\u83b2\u7b11\u9053\uff1a\u201c\u5a62\u5b50\u51b3\u5b9a\u4e0d\u53bb\u89c1\u90a3\u5929\u9b54\u5973\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u4e86\u4e00\u7b11\u9053\uff1a\u201c\u6bd5\u59d0\u59d0\uff0c\u8c22\u8c22\u4f60\u5566\u3002\u201d \u3000\u3000\u6e38\u516c\u7384\u70b9\u5934\u9053\uff1a\u201c\u59d1\u5a18\u660e\u667a\u5f97\u5f88\uff01\u201d \u3000\u3000\u76ee\u5149\u8f6c\u5411\u51af\u96f7\u548c\u5927\u80dc\u5934\u9640\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u5462\uff1f\u201d \u3000\u3000\u5927\u80dc\u5934\u9640\u54a7\u5634\u4e00\u7b11\uff0c\u6b63\u8981\u8bf4\u8bdd\uff0c\u53ea\u89c1\u51af\u96f7\u5df2\u7136\u62a2\u5148\u8bf4\u9053\uff1a\u201c\u51af\u67d0\u4ecd\u7136\u8981\u53bb\u89c1\u90a3\u5929\u9b54\u5973\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u4f60\u8981\u53bb\uff1f\u201d \u3000\u3000\u51af\u96f7\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5ffd\u7136\u6012\u9053\uff1a\u201c\u4f60\u6562\uff1f\u59d1\u5a18\u53eb\u4f60\u53bb\u4e0d\u6210\uff0c\u4f60\u5c31\u662f\u53bb\u4e0d\u6210\u4e86\uff01\u201d \u3000\u3000\u51af\u96f7\u7b11\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u51af\u67d0\u548c\u5927\u80dc\u5934\u9640\uff0c\u4ecd\u5c06\u53bb\u89c1\u5929\u9b54\u5973\uff01\u201d \u3000\u3000\u4ed6\u5982\u6b64\u56fa\u6267\uff0c\u663e\u7136\u5927\u51fa\u5b89\u5c0f\u840d\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e5f\u89c9\u7740\u6709\u4e9b\u4e0d\u5bf9\uff0c\u7b11\u9053\uff1a\u201c\u51af\u5927\u4fa0\uff0c\u4f60\u8fd9\u4e48\u505a\uff0c\u53c8\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u51af\u96f7\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u6709\u4e00\u6869\u4e8b\uff0c\u53ea\u6015\u4f60\u4eec\u90fd\u4e0d\u66fe\u7559\u5fc3\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u51af\u96f7\u9053\uff1a\u201c\u5728\u5750\u8bf8\u4f4d\u53ef\u77e5\u7a76\u7adf\u6709\u591a\u5927\u52bf\u529b\uff1f\u5929\u9b54\u5973\u9664\u4e86\u51af\u67d0\u7b49\u4eba\u800c\u8fd8\u8bf7\u4e86\u90a3\u4e9b\u4eba\u51fa\u5c71\u76f8\u52a9\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u8fd9\u2026\u2026\u533a\u533a\u5374\u662f\u4e0d\u77e5\uff01\u201d \u3000\u3000\u51af\u96f7\u770b\u4e86\u6e38\u516c\u7384\u4e00\u773c\uff0c\u9053\uff1a\u201c\u6e38\u8001\u5462\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u8001\u673d\u53c8\u4e0d\u662f\u5929\u9b54\u5973\u809a\u4e2d\u86d4\u866b\uff0c\u81ea\u7136\u4e5f\u662f\u4e0d\u77e5\u9053\u7684\u4e86\uff01\u201d \u3000\u3000\u51af\u96f7\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u8fd9\u53c8\u6709\u4ec0\u4e48\u4e0d\u5bf9\u5417\uff1f\u201d \u3000\u3000\u51af\u96f7\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u53e4\u8bed\u8bf4\u77e5\u5df2\u77e5\u5f7c\uff0c\u767e\u6218\u767e\u80dc\uff0c\u90a3\u5929\u9b54\u5973\u6709\u4e9b\u4ec0\u4e48\u4eba\u624b\uff0c\u54b1\u4eec\u4e00\u4e9b\u90fd\u4e0d\u77e5\u9053\uff0c\u90a3\u5c82\u4e0d\u662f\u751a\u4e3a\u5192\u5931\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u51ed\u90a3\u5929\u9b54\u5973\uff0c\u5979\u53c8\u80fd\u8bf7\u5230\u4ec0\u4e48\u9ad8\u4eba\u52a9\u9635\uff1f\u201d \u3000\u3000\u51af\u96f7\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u8bdd\u53ef\u4e0d\u80fd\u8fd9\u4e48\u8bf4\uff0c\u6b66\u6797\u4e4b\u4e2d\uff0c\u5c3d\u591a\u5947\u624d\u5f02\u80fd\u4e4b\u58eb\uff0c\u54b1\u4eec\u5c0f\u5fc3\u4e00\u4e9b\uff0c\u603b\u8981\u597d\u4e9b\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u8fd9\u65f6\u5df2\u542c\u51fa\u4e86\u51af\u96f7\u8a00\u4e0b\u4e4b\u610f\uff0c\u5fd9\u5411\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4f60\u83ab\u518d\u8bf4\u4e86\uff0c\u51af\u5144\u4e4b\u610f\uff0c\u611a\u5144\u5df2\u7ecf\u660e\u767d\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4ed6\u662f\u4f55\u610f\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u51af\u5144\u60f3\u5fc5\u662f\u8981\u548c\u5927\u80dc\u5934\u9640\u6253\u5165\u5929\u9b54\u5973\u624b\u4e0b\uff0c\u63a2\u542c\u4ed6\u4eec\u7684\u865a\u5b9e\u3002\u201d \u3000\u3000\u6e38\u516c\u7384\u7b11\u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u51af\u8001\u5f1f\u6b64\u4e3e\uff0c\u5230\u662f\u4e0d\u5931\u4e0a\u7b56\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u6c5f\u5927\u6653\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u6709\u4f55\u4e0d\u53ef\uff1f\u53ea\u662f\u2026\u2026\u54b1\u4eec\u4eca\u65e5\u5728\u9152\u697c\u4e4b\u4e0a\u4e00\u95f9\uff0c\u5982\u662f\u5929\u9b54\u5973\u624b\u4e0b\u6709\u4eba\u89c1\u5230\uff0c\u90a3\u5c82\u4e0d\u662f\u7cdf\u900f\u4e86\u5417\uff1f\u201d \u3000\u3000\u51af\u96f7\u7b11\u9053\uff1a\u201c\u4e0d\u8981\u7d27\uff0c\u8fd9\u7b49\u65f6\u523b\uff0c\u5929\u9b54\u5973\u662f\u4e0d\u4f1a\u6d3e\u4eba\u524d\u6765\u7684\u4e86\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u600e\u89c1\u5f97\uff1f\u201d \u3000\u3000\u51af\u96f7\u9053\uff1a\u201d\u54b1\u4eec\u9664\u4e86\u7ea6\u5b9a\u6697\u53f7\u4e4b\u5916\uff0c\u8fd8\u66fe\u7ea6\u6709\u65f6\u523b\uff0c\u65f6\u523b\u672a\u5230\uff0c\u53ea\u6015\u5979\u4e0d\u4f1a\u5dee\u4eba\u6765\u6b64\u3002\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u4f60\u4eec\u7ea6\u7684\u662f\u4ec0\u4e48\u65f6\u523b\uff1f\u201d \u3000\u3000\u51af\u96f7\u9053\uff1a\u201c\u521d\u66f4\u5de6\u53f3\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u90a3\u8fd8\u65e9\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u770b\u770b\u5929\u8272\uff0c\u8fd9\u65f6\u9ec4\u660f\u672a\u5c3d\uff0c\u79bb\u521d\u66f4\uff0c\u8fd8\u6709\u4e2a\u628a\u65f6\u8fb0\uff0c\u5f53\u4e0b\u7b11\u9053\uff1a\u201c\u4f46\u613f\u51af\u5144\u731c\u60f3\u7684\u4e0d\u9519\uff0c\u5426\u5219\uff0c\u51af\u5144\u548c\u5929\u53f0\u4e09\u8001\u7b49\u4eba\uff0c\u5904\u5883\u5c31\u5341\u5206\u5371\u9669\u4e86\uff01\u201d \u3000\u3000\u51af\u96f7\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u8fd9\u4e00\u70b9\u4f60\u4e0d\u5fc5\u6302\u5728\u5fc3\u4e0a\uff0c\u54b1\u4eec\u867d\u65e0\u654c\u90a3\u4e94\u9b54\u4e4b\u80fd\uff0c\u4f46\u5374\u81ea\u4fe1\u5c1a\u6709\u81ea\u4fdd\u4e4b\u529b\uff01\u201d \u3000\u3000\u6c5f\u5927\u6653\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5929\u9b54\u5973\u5982\u60f3\u6740\u5bb3\u6211\u7b49\uff0c\u53ef\u4e5f\u4e0d\u662f\u5bb9\u6613\u7684\u4e8b\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u674e\u3001\u4f59\u4e24\u4f4d\u8001\u5f1f\u613f\u610f\u5417\uff1f\u201d \u3000\u3000\u674e\u5146\u4e30\u3001\u4f59\u6770\u540c\u65f6\u7b11\u9053\uff1a\u201c\u6c5f\u5144\u65e2\u6709\u8fd9\u7b49\u8c6a\u6c14\uff0c\u8001\u5f1f\u7b49\u81ea\u7136\u613f\u9644\u9aa5\u5c3e\u4e86\uff01\u201d \u3000 \u7b2c\u4e09\u5341\u4e94\u56de\u3000\u5143\u51f6\u6388\u9996 \u3000 \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u5fc3\u4e2d\u751a\u662f\u611f\u52a8\uff0c\u6697\u9053\uff1a\u201c\u6b66\u6797\u4e2d\u6b63\u90aa\u4e4b\u8fa8\uff0c\u7a76\u7adf\u80dc\u8fc7\u751f\u6b7b\u2026\u2026\u201d \u3000\u3000\u601d\u5fd6\u4e4b\u95f4\uff0c\u53ea\u89c1\u6e38\u516c\u7384\u4e3e\u8d77\u9762\u524d\u9152\u6597\uff0c\u5927\u58f0\u9053\uff1a\u201c\u6e38\u67d0\u5148\u884c\u656c\u4f60\u4eec\u4e00\u6597\u2026\u2026\u201d \u3000\u3000\u51af\u96f7\u3001\u5927\u80dc\u5934\u9640\u3001\u5929\u53f0\u4e09\u7edd\u540c\u65f6\u4e3e\u676f\uff0c\u4e00\u996e\u800c\u5c3d\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u4e86\u4e00\u7b11\uff0c\u4e5f\u5b66\u7740\u6e38\u516c\u7384\uff0c\u656c\u4e86\u4ed6\u4eec\u4e00\u6597\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u6c89\u541f\u4e00\u4e0b\uff0c\u76b1\u7709\u9053\uff1a\u201c\u65b9\u5927\u54e5\uff0c\u6709\u4e00\u4ef6\u4e8b\uff0c\u6211\u89c9\u5f97\u4e0d\u5927\u59a5\u5f53\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u90a3\u767d\u745b\u592b\u5987\u5ffd\u7136\u4e0d\u66fe\u524d\u6765\u5e94\u7ea6\uff0c\u6bd5\u59d0\u59d0\u8ddf\u6211\u4eec\u4e00\u8fb9\uff0c\u6050\u6015\u5929\u9b54\u5973\u8fd9\u5996\u5987\u6709\u4e86\u8b66\u60d5\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u4e8b\u679c\u7136\u6709\u4e9b\u7834\u7efd\u2026\u2026\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5fcd\u4e0d\u4f4f\u5411\u51af\u96f7\u671b\u53bb\u3002 \u3000\u3000\u51af\u96f7\u6c89\u541f\u9053\uff1a\u201c\u8fd9\u4e8b\u786e\u662f\u5bb9\u6613\u8d77\u4eba\u7591\u5fc3\uff0c\u4f46\u5982\u5728\u4e0b\u7a0d\u7a0d\u5c0f\u5fc3\uff0c\u4e5f\u5c31\u4e0d\u4f1a\u9732\u51fa\u9a6c\u811a\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4f60\u6253\u7b97\u600e\u751f\u5e94\u5bf9\uff1f\u201d \u3000\u3000\u51af\u96f7\u9053\uff1a\u201c\u53ea\u8bf4\u4ed6\u4eec\u56e0\u6545\u4e0d\u80fd\u524d\u6765\u4fbf\u53ef\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u6251\u54e7\u4e00\u7b11\u9053\uff0c\u201c\u8fd9\u80fd\u9a97\u5f97\u8fc7\u5927\u9b54\u5973\u5417\uff1f\u4f60\u4eec\u60f3\u5f97\u597d\u5929\u771f\u554a\uff01\u201d \u3000\u3000\u51af\u96f7\u7b11\u9053\uff1a\u201c\u5149\u8bf4\u56e0\u6545\uff0c\u90a3\u5f53\u7136\u4e0d\u884c\uff0c\u4e0d\u8fc7\uff0c\u533a\u533a\u5982\u662f\u8bf4\u5979\u4eec\u4e09\u4eba\u5148\u884c\u53bb\u5bf9\u4ed8\u2018\u5343\u624b\u795e\u7fc1\u2019\uff0c\u53ea\u6015\u5929\u9b54\u5973\u5c31\u4e0d\u4f1a\u4e0d\u4fe1\u4e86\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u5927\u7b11\u9053\uff1a\u201c\u5999\u6781\uff01\u5999\u6781\uff01\u90a3\u5929\u9b54\u5973\u4e00\u542c\u8bf4\u6709\u4eba\u5bf9\u4ed8\u8001\u592b\uff0c\u90a3\u81ea\u7136\u662f\u9ad8\u5174\u5f97\u5f88\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u8fd9\u5012\u884c\u5f97\u901a\u2026\u2026\u201d \u3000\u3000\u8bf4\u7b11\u4e4b\u95f4\uff0c\u5341\u4eba\u5404\u81ea\u75db\u996e\u4e86\u4e09\u676f\uff0c\u6e38\u516c\u7384\u770b\u4e86\u770b\u7a97\u5916\u5929\u8272\uff0c\u9053\uff1a\u201c\u6c5f\u8001\u5f1f\uff0c\u54b1\u4eec\u53ef\u4ee5\u5206\u684c\u800c\u5750\u4e86\uff01\u201d \u3000\u3000\u4f17\u4eba\u8ba1\u8bae\u5df2\u5b9a\uff0c\u770b\u5929\u8272\u4e0d\u65e9\uff0c\u51b3\u5b9a\u5206\u684c\u800c\u5750\uff0c\u5927\u80dc\u3001\u51af\u96f7\u3001\u548c\u5929\u53f0\u4e09\u7edd\u4e94\u4eba\uff0c\u7acb\u5373\u62db\u547c\u5e97\u5bb6\uff0c\u5728\u9760\u8fd1\u697c\u68af\u5165\u53e3\u4e4b\u5904\uff0c\u53e6\u5916\u6446\u4e86\u4e00\u5e2d\u3002 \u3000\u3000\u6e38\u516c\u7384\u8fd9\u624d\u53eb\u8fc7\u5e97\u5bb6\uff0c\u5429\u5490\u638c\u706f\uff0c\u5e76\u4e14\u8981\u5e97\u4f19\u8ba1\u5feb\u53bb\u5c06\u697c\u4e0b\u7684\u98df\u5ba2\uff0c\u5168\u90e8\u79fb\u5230\u697c\u4e0a\uff0c\u5239\u90a3\u4e4b\u95f4\uff0c\u51b7\u51b7\u6e05\u6e05\u7684\u697c\u4e0a\uff0c\u5341\u5206\u70ed\u95f9\u8d77\u6765\u3002 \u3000\u3000\u90d1\u5927\u521a\u5728\u8fd9\u4e9b\u4eba\u9762\u524d\uff0c\u4e00\u76f4\u6ca1\u6709\u8bf4\u8bdd\uff0c\u8fd9\u65f6\u65b9\u59cb\u5411\u6e38\u516c\u7384\u9053\uff1a\u201c\u6e38\u8001\uff0c\u665a\u8f88\u90d1\u5927\u521a\uff0c\u656c\u4f60\u4e00\u676f\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u4e0d\u7528\u4e86\uff01\u90a3\u5929\u9b54\u5973\u7684\u624b\u4e0b\uff0c\u4f3c\u4e4e\u5df2\u7ecf\u6765\u5566\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u4e09\u4eba\uff0c\u987f\u65f6\u4e3e\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u5728\u90a3\u697c\u68af\u5165\u53e3\u5904\uff0c\u51fa\u73b0\u4e86\u4e24\u7537\u4e00\u5973\u3002\u4ed6\u4eec\u5411\u697c\u4e0a\u7565\u4e00\u5f20\u671b\uff0c\u4fbf\u5f84\u76f4\u671d\u6c5f\u5929\u6653\u548c\u51af\u96f7\u7b49\u4e94\u4eba\u6240\u5750\u7684\u684c\u5b50\u8d70\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u6e38\u516c\u7384\u4f4e\u58f0\u9053\uff1a\u201c\u5929\u9b54\u5973\u884c\u4e8b\uff0c\u679c\u7136\u5927\u80c6\u5f97\u5f88\uff0c\u8001\u592b\u76f8\u4fe1\uff0c\u65b9\u8001\u5f1f\u8fd9\u4e00\u6b21\u5fc5\u53ef\u5b8c\u6210\u5251\u795e\u7684\u5919\u613f\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u4e86\u4e00\u7b11\uff0c\u6b20\u8eab\u63a5\u9053\uff1a\u201c\u665a\u8f88\u4e00\u4eba\u529b\u91cf\u6709\u9650\uff0c\u8fd8\u5f97\u6e38\u8001\u5927\u529b\u652f\u63f4\u554a\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u7b11\u9053\uff1a\u201d\u8001\u592b\u53ea\u6709\u529b\u80fd\u6240\u53ca\uff0c\u5b9a\u7136\u76f8\u52a9\u8001\u5f1f\u6210\u6b64\u5927\u529f\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u53ea\u89c1\u90a3\u4e8c\u7537\u4e00\u5973\u5df2\u9886\u7740\u6c5f\u5929\u6653\u7b49\u4e94\u4eba\u4e0b\u697c\u884c\u53bb\u3002 \u3000\u3000\u51af\u96f7\u4e34\u53bb\u4e4b\u65f6\uff0c\u5374\u56de\u624b\u5f39\u51fa\u4e00\u4e2a\u5c0f\u5c0f\u7eb8\u56e2\uff0c\u88ab\u6bd5\u79cb\u83b2\u4f38\u624b\u63a5\u4f4f\u3002 \u3000\u3000\u3001 \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u6bd5\u59d0\u59d0\uff0c\u90a3\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6bd5\u79cb\u83b2\u6253\u5f00\u7eb8\u56e2\uff0c\u4ea4\u5230\u5b89\u5c0f\u840d\u7684\u624b\u4e2d\u9053\uff1a\u201c\u5c0f\u59d0\u8bf7\u770b\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u51dd\u76ee\u671b\u53bb\uff0c\u90a3\u56e2\u767d\u7eb8\u4e4b\u4e0a\uff0c\u53ea\u5199\u4e86\u51e0\u4e2a\u5c0f\u5b57\uff1a\u201c\u5982\u4f55\u8054\u7edc\uff0c\u53ef\u95ee\u6bd5\u59d1\u5a18\uff01\u201d \u3000\u3000\u6bd5\u79cb\u83b2\u8fd9\u65f6\u4e5f\u770b\u5230\u4e86\u7eb8\u4e0a\u7684\u5b57\u8ff9\uff0c\u4e0d\u7b49\u5b89\u5c0f\u840d\u8be2\u95ee\uff0c\u4fbf\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u51af\u53d4\u53d4\u662f\u6015\u54b1\u4eec\u627e\u4e0d\u5230\u4ed6\u4eec\u4e0b\u843d\uff0c\u624d\u7279\u522b\u8bf4\u660e\u8054\u7edc\u65b9\u6cd5\uff0c\u76f8\u4fe1\u51af\u53d4\u53d4\u4f1a\u5728\u4e00\u8def\u4e4b\u4e0a\u7559\u4e0b\u6697\u8bb0\uff0c\u597d\u8ba9\u6211\u4eec\u627e\u5230\u90a3\u5929\u9b54\u5973\u5728\u5904\u4e86\u3002\u201d \u3000\u3000\u6e38\u516c\u7384\u5927\u7b11\u9053\uff1a\u201c\u5438\u8840\u9b3c\u5200\u679c\u771f\u5fc3\u601d\u7ec6\u5bc6\u5f97\u5f88\uff0c\u6bd5\u59d1\u5a18\uff0c\u6b64\u540e\u53ef\u5f97\u501f\u91cd\u4f60\u4e86\uff01\u201d \u3000\u3000\u6bd5\u79cb\u83b2\u7f9e\u7b11\u9053\uff1a\u201c\u8001\u524d\u8f88\u8a00\u91cd\u4e86\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u8fd9\u65f6\u5ffd\u7136\u7b11\u5411\u6e38\u516c\u7384\u9053\uff1a\u201c\u6e38\u8001\uff0c\u4f60\u8001\u6765\u5230\u4e2d\u5dde\uff0c\u4f4f\u5728\u4f55\u5916\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u6447\u5934\u9053\uff1a\u201c\u8001\u592b\u5c1a\u65e0\u95f2\u7a7a\u5bfb\u627e\u4f4f\u5904\uff0c\u4f60\u4eec\u5462\uff1f\u4f4f\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u7b11\u9053\uff1a\u201c\u57ce\u897f\u672c\u662f\u665a\u8f88\u7b49\u6545\u5c45\uff0c\u6e38\u8001\u5982\u4e0d\u5acc\u5f03\uff0c\u4f55\u4e0d\u5c31\u53bb\u6b47\u4e0a\u4e00\u5bbf\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u5931\u58f0\u7b11\u9053\uff1a\u201c\u53ef\u4e0d\u662f\u5417\uff1f\u4e2d\u5dde\u4e09\u4fa0\uff0c\u90a3\u81ea\u7136\u662f\u4f4f\u5728\u6cb3\u5357\u57ce\u91cc\u7684\u4e86\uff01\u8001\u592b\u600e\u5730\u5ffd\u7136\u5fd8\u8bb0\u4e86\u8fd9\u4e00\u70b9\u2026\u2026\u201d\u7b11\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u662f\u90fd\u5728\u5c11\u6797\u5417\uff1f\u4e3a\u4f55\u4e0d\u7acb\u5373\u8d76\u56de\u5c11\u6797\u5462\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u6e38\u8001\uff0c\u53ea\u56e0\u65b9\u8d24\u4f84\u6302\u5ff5\u4ed6\u7684\u5927\u4f2f\u6bcd\u4e0b\u843d\uff0c\u6545\u800c\u56de\u6765\u5bfb\u627e\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u53ef\u6709\u7709\u76ee\u4e86\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u7b11\u9053\uff1a\u201c\u6ca1\u6709\uff01\u201d\u4ed6\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u63a5\u9053\uff1a\u201c\u6e38\u8001\uff0c\u77f3\u4e09\u5f1f\u53ea\u8981\u8ddf\u5979\u5728\u4e00\u8d77\uff0c\u90a3\u662f\u4e0d\u4f1a\u5f53\u771f\u51fa\u4e8b\u7684\u5417\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u627e\u5230\u77f3\u4fca\u90a3\u4e0d\u5c31\u53ef\u627e\u5230\u65b9\u592b\u4eba\u4e86\u4e48\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5ffd\u7136\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u6e38\u8001\uff0c\u5173\u952e\u5c31\u5728\u8fd9\u91cc\u4e86\uff01\u90a3\u77f3\u4e09\u53d4\u7684\u4e0b\u843d\uff0c\u5374\u662f\u65e0\u4eba\u77e5\u6653\u554a\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u76b1\u7709\u9053\uff1a\u201c\u6709\u8fd9\u7b49\u4e8b\uff1f\u8001\u592b\u4e0d\u4fe1\u6b64\u95f4\u7adf\u662f\u65e0\u4eba\u77e5\u6653\u4ed6\u4eec\u7684\u4e0b\u843d\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u6e38\u8001\u8bf4\u5f97\u4e0d\u9519\uff0c\u90d1\u67d0\u4e5f\u6709\u4e9b\u4e0d\u4fe1\uff0c\u90a3\u77f3\u4e09\u5f1f\u4e3a\u4eba\u751a\u662f\u673a\u8b66\uff0c\u5018\u82e5\u771f\u6709\u4ec0\u4e48\u53d8\u6545\uff0c\u4ed6\u4e0d\u4f1a\u4e0d\u6258\u4eba\u544a\u77e5\u665a\u8f88\u7684\u4e86\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u8001\u592b\u6709\u4e00\u53e5\u8bdd\uff0c\u8bf4\u5c06\u6765\u51fa\uff0c\u53ea\u6015\u90d1\u8001\u5f1f\u5fc3\u4e2d\u4e0d\u4fe1\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4ec0\u4e48\u8bdd\uff1f\u6e38\u8001\u524d\u8f88\u8bf7\u8bf4\u65e0\u59a8\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u90a3\u77f3\u4e09\u4fa0\u5982\u662f\u4e45\u4e45\u6ca1\u6709\u6d88\u606f\uff0c\u53ea\u6015\u4e0e\u90a3\u4e94\u5927\u9b54\u4e3b\u6709\u5173\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u95fb\u8a00\u4e00\u5446\u9053\uff1a\u201c\u8fd9\u2026\u2026\u600e\u4e48\u4f1a\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e5f\u76b1\u8d77\u53cc\u7709\uff0c\u6c89\u541f\u9053\uff1a\u201c\u6e38\u8001\uff0c\u4ed6\u4eec\u600e\u80fd\u4e0e\u4e94\u5927\u9b54\u4e3b\u6709\u5173\u5462\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u957f\u957f\u5730\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u6b66\u6797\u4e2d\u7684\u4e00\u5207\uff0c\u773c\u4e0b\u6050\u5c06\u90fd\u4e0e\u4e94\u5927\u9b54\u4e3b\u6709\u5173\u4e86\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u5018\u662f\u5982\u6b64\uff0c\u4f9d\u4f60\u8001\u4e4b\u89c1\uff0c\u53c8\u5f53\u5982\u4f55\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u7b11\u9053\uff1a\u201c\u4f9d\u8001\u592b\u4e4b\u89c1\uff0c\u90a3\u662f\u6682\u65f6\u4e0d\u7528\u518d\u53bb\u5bfb\u627e\u4e86\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u53ea\u6d88\u5c06\u4e94\u9b54\u9664\u53bb\uff0c\u8001\u592b\u76f8\u4fe1\uff0c\u5fc5\u53ef\u627e\u5f97\u51fa\u77f3\u4e09\u4fa0\u7684\u4e0b\u843d\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8001\u524d\u8f88\u4e4b\u610f\uff0c\u662f\u8bf4\u6211\u90a3\u77f3\u4e09\u53d4\u7b49\u4eba\uff0c\u53ef\u80fd\u4e5f\u88ab\u4e94\u9b54\u6240\u4fd8\u5417\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u8001\u592b\u6b63\u662f\u6b64\u610f\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5982\u82e5\u77f3\u4e09\u53d4\u771f\u88ab\u4e94\u9b54\u6240\u4fd8\uff0c\u8001\u524d\u8f88\u4e4b\u8a00\uff0c\u5e76\u65e0\u4e0d\u5f53\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u8001\u5f1f\u522b\u6709\u6240\u89c1\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u665a\u8f88\u6015\u7684\u662f\u4ed6\u4eec\u5e76\u672a\u88ab\u4e94\u9b54\u6240\u63b3\uff0c\u5219\u4e8b\u60c5\u5c31\u8981\u715e\u8d39\u5468\u7ae0\u4e86\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u8001\u5f1f\u6240\u8651\uff0c\u4e5f\u4e0d\u65e0\u9053\u7406\uff01\u4f46\u8001\u592b\u8ba4\u4e3a\u8001\u5f1f\u5e94\u8be5\u4ee5\u6b66\u6797\u516c\u7406\u6b63\u4e49\u4e3a\u91cd\uff0c\u5148\u5c06\u4e94\u9b54\u4e4b\u4e8b\u529e\u597d\uff0c\u518d\u884c\u53bb\u627e\u4f60\u5927\u4f2f\u6bcd\u7684\u4e0b\u843d\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u662f\u554a\uff01\u8001\u524d\u8f88\u6559\u8bad\u5f97\u662f\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u6c89\u541f\u9053\uff1a\u201c\u6e38\u8001\u524d\u8f88\u8bf4\u5f97\u4e0d\u9519\uff0c\u8d24\u4f84\u5e94\u4ee5\u6b66\u6797\u5927\u4e8b\u4e3a\u91cd\uff0c\u9664\u53bb\u4e94\u9b54\uff0c\u5012\u662f\u4e0d\u6101\u627e\u4e0d\u5230\u90a3\u77f3\u4e09\u5f1f\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5c0f\u4f84\u8bb0\u4e0b\u4e86\uff01\u201d \u3000\u3000\u5f53\u665a\uff0c\u4ed6\u4eec\u56de\u5230\u65b9\u5bb6\u5e84\u6b47\u606f\uff0c\u4e00\u5bbf\u65e0\u8bdd\uff0c\u7b2c\u4e8c\u5929\u4e2d\u5348\uff0c\u4e00\u884c\u4e94\u4eba\uff0c\u5954\u5411\u5c11\u6797\u3002\u9ec4\u660f\u4ee5\u540e\uff0c\u4ed6\u4eec\u4fbf\u5df2\u62b5\u8fbe\u3002 \u3000\u3000\u6e38\u516c\u7384\u548c\u90a3\u51e0\u4f4d\u8001\u4e00\u8f88\u7684\u4eba\u7269\uff0c\u90fd\u662f\u6545\u4ea4\uff0c\u76f8\u89c1\u4e4b\u4e0b\uff0c\u81ea\u662f\u5341\u5206\u9ad8\u5174\uff0c\u4f46\u5b89\u5c0f\u840d\u548c\u6bd5\u79cb\u83b2\u5b89\u5bbf\uff0c\u5374\u4f7f\u5f97\u5c11\u6797\u548c\u5c1a\uff0c\u715e\u8d39\u4e86\u4e0d\u5c11\u5fc3\u601d\uff01\u7ec8\u4e8e\uff0c\u5165\u4e91\u5927\u5e08\u5728\u4f5b\u7956\u9762\u524d\u8bb8\u4e0b\u79ef\u4fee\u5341\u4e07\u5916\u529f\u7684\u5fc3\u613f\uff0c\u7834\u4f8b\u5728\u521d\u7956\u5eb5\u4e2d\uff0c\u817e\u51fa\u4e00\u95f4\u9759\u5ba4\uff0c\u4f5c\u4e3a\u4e8c\u5973\u4f4f\u6240\u3002 \u3000\u3000\u4e00\u8fde\u4e24\u5927\uff0c\u5012\u4e5f\u6ca1\u6709\u4ec0\u4e48\u4e8b\u6545\uff0c\u76f4\u5230\u7b2c\u4e09\u5929\u7684\u664c\u5348\u65f6\u5206\uff0c\u90a3\u5165\u4e91\u5927\u5e08\uff0c\u5ffd\u7136\u5dee\u4eba\u5c06\u4ed6\u4eec\u8bf7\u5230\u65b9\u4e08\u7985\u5ba4\u5185\u3002 \u3000\u3000\u8f9b\u67cf\u516c\u3001\u795d\u516c\u660e\u3001\u6e38\u516c\u7384\u3001\u970d\u9e23\u98ce\u3001\u5d14\u5927\u516c\u3001\u7fc1\u6606\u4ed1\u3001\u5b8b\u5b5a\u3001\u90d1\u5927\u521a\u3001\u5e84\u6377\u3001\u5b89\u5c0f\u840d\u3001\u6bd5\u79cb\u83b2\u3001\u65b9\u96ea\u5b9c\uff0c\u5916\u52a0\u5c11\u6797\u51e0\u4f4d\u957f\u8001\uff0c\u51e0\u4e4e\u5c06\u65b9\u4e08\u7684\u90a3\u95f4\u7985\u5802\u5750\u6ee1\u3002 \u3000\u3000\u5c0f\u6c99\u5f25\u5949\u4e0a\u4e86\u6e05\u8336\uff0c\u8f9b\u67cf\u516c\u7ffb\u4e86\u7ffb\u773c\uff0c\u5927\u58f0\u9053\uff1a\u201c\u65b9\u4e08\u5927\u5e08\uff0c\u4f60\u628a\u8001\u592b\u7b49\u4eba\uff0c\u5168\u90e8\u8bf7\u6765\uff0c\u53ef\u662f\u6709\u7740\u4ec0\u4e48\u6781\u4e3a\u91cd\u5927\u4e4b\u4e8b\u5417\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u5408\u5341\u4e00\u53f9\uff0c\u8bf4\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u8001\u65bd\u4e3b\uff0c\u8d2b\u50e7\u6b63\u662f\u6709\u7740\u6781\u4e3a\u91cd\u5927\u4e4b\u4e8b\u76f8\u544a\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u4ec0\u4e48\u5927\u4e8b\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u4e94\u9b54\u7684\u6218\u4e66\u5230\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f1a\u4e0d\u4f1a\u53c8\u662f\u73a9\u7b11\uff1f\u201d \u3000\u3000\u4ed6\u4f3c\u662f\u60f3\u8d77\u524d\u51e0\u5929\u7684\u90a3\u6869\u5b50\u4e8b\u3002 \u3000\u3000\u5165\u4e91\u5927\u5e08\u6447\u5934\u9053\uff1a\u201c\u8fd9\u4e00\u56de\u4e0d\u662f\u5047\u7684\u4e86\uff01\u201d \u3000\u3000\u4ed6\u7f13\u7f13\u5730\u8f6c\u8eab\uff0c\u4ece\u9999\u6848\u4e4b\u4e0b\uff0c\u53d6\u51fa\u4e00\u4e2a\u5927\u7ea2\u5c01\u5957\uff0c\u9012\u7ed9\u4e86\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u7ea6\u6218\u7684\u4e66\u7b80\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u5c06\u90a3\u5c01\u4e66\u7b80\u53d6\u51fa\uff0c\u53ea\u89c1\u4ed6\u770b\u4e86\u4e00\u773c\uff0c\u4fbf\u8138\u8272\u4e00\u53d8\uff0c\u6012\u9053\uff1a\u201c\u597d\u5927\u7684\u53e3\u6c14\uff01\u8fd9\u4e94\u4e2a\u5996\u5f92\uff0c\u53ea\u9053\u4e16\u95f4\u5f53\u771f\u65e0\u4eba\u662f\u4ed6\u4eec\u7684\u654c\u624b\u4e86\uff01\u201d\u987a\u624b\u5c06\u4e66\u7b80\u4ea4\u7ed9\u4e86\u795d\u516c\u660e\uff01 \u3000\u3000\u795d\u516c\u660e\u7b11\u9053\uff1a\u201c\u8f9b\u5144\u706b\u6c14\u5f53\u771f\u4e0d\u5c0f\u2026\u2026\u201d\u4f46\u4ed6\u8bf4\u8bdd\u95f4\uff0c\u76ee\u5149\u89e6\u53ca\u4e66\u7b80\u4e0a\u5b57\u8ff9\uff0c\u4e5f\u4e0d\u7981\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u600e\u5730\u8fd9\u822c\u5927\u80c6\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u4f3c\u662f\u88ab\u4e24\u4eba\u7684\u795e\u6001\u60f9\u5f97\u6709\u4e9b\u4e0d\u8010\u70e6\uff0c\u7b11\u9053\uff1a\u201c\u4e24\u4f4d\u600e\u4e48\u4e86\uff1f\u4f55\u4e0d\u5148\u5c06\u8fd9\u4e0a\u9762\u5199\u7684\u4ec0\u4e48\u8bfb\u51fa\u6765\uff0c\u4e5f\u597d\u8ba9\u5404\u4eba\u660e\u767d\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u8981\u8bfb\uff0c\u6e38\u5144\u8bf7\u8bfb\uff0c\u8001\u592b\u5374\u8d8a\u770b\u8d8a\u6709\u706b\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u7b11\u9053\uff1a\u201c\u5982\u6b64\u5144\u5f1f\u4ee3\u4e3a\u2026\u2026\u201d\u4ed6\u63a5\u8fc7\u4e66\u7b80\uff0c\u5148\u770b\u4e86\u4e00\u773c\uff0e\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u4eec\u679c\u771f\u662f\u5f88\u72c2\u5984\u2026\u2026\u201d\u7565\u7565\u4e00\u987f\u7b11\u5bb9\uff0c\u7f13\u7f13\u5ff5\u9053\uff1a\u201c\u672c\u6559\u8ba2\u4e8e\u4e59\u4e11\u5e74\u7532\u7533\u6708\u671b\u65e5\u5728\u4e2d\u5cb3\u5e99\u53ec\u5f00\u6b66\u6797\u5927\u4f1a\uff0c\u656c\u5019\u5404\u6d3e\u5149\u4e34\uff0c\u4e94\u5723\u6559\u8c28\u8ba2\u3002\u201d \u3000\u3000\u4ed6\u521a\u521a\u8bfb\u5230\u8fd9\u91cc\uff0c\u5b89\u5c0f\u840d\u5df2\u7136\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u8fd9\u4e5f\u5f88\u666e\u901a\u554a\uff0c\u4e24\u4f4d\u53c8\u4f55\u5fc5\u751f\u90a3\u4e48\u5927\u7684\u6c14\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u8fd9\u4e0b\u9762\u8fd8\u6709\u5f88\u591a\u7684\u8bdd\u5462\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u8fd8\u6709\u5417\uff1f\u5feb\u8bfb\u554a\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u63a5\u7b80\u5ff5\u9053\uff1a\u201c\u672c\u6b21\u6b66\u6797\u5927\u4f1a\u4e43\u662f\u672c\u6559\u4e3b\u76df\u6b66\u6797\u4e4b\u59cb\uff0c\u63a5\u5230\u6b64\uff0c\u4e0d\u6765\u8d74\u4f1a\u4e4b\u4eba\uff0c\u5c31\u4e3a\u672c\u6559\u4e4b\u654c\uff0c\u672c\u6559\u5b9a\u4e0d\u5bbd\u8d66\u2026\u2026\u201d\u4ed6\u8bfb\u5230\u8fd9\u91cc\uff0c\u970d\u9e23\u98ce\u4f3c\u662f\u4e5f\u6709\u4e9b\u5fcd\u4e0d\u4f4f\u6012\u9053\uff1a\u201c\u970d\u67d0\u672c\u6765\u60f3\u53bb\uff0c\u542c\u4ed6\u4eec\u8fd9\u4e48\u4e00\u8bf4\uff0c\u672c\u5e2e\u5012\u4e0d\u7528\u53bb\u4e86\u3002\u970d\u67d0\u60f3\u770b\u770b\u4ed6\u4eec\u53c8\u5c06\u5982\u4f55\u5bf9\u4ed8\u672c\u5e2e\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u7b11\u9053\uff1a\u201c\u6709\u9053\u7406\uff01\u4f46\u970d\u8001\u5f1f\u83ab\u8981\u5fd8\u4e86\uff0c\u8fd9\u56de\u4f60\u53ef\u975e\u53bb\u4e0d\u53ef\u554a\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u4ed6\u4eec\u4e94\u4e2a\u9b54\u5934\u4e0d\u53ef\u80fd\u751f\u79bb\u90a3\u4e2d\u5cb3\u5e99\uff0c\u4f60\u5982\u4e0d\u53bb\uff0c\u5c82\u4e0d\u5c11\u770b\u4e86\u4e00\u573a\u70ed\u95f9\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u5927\u7b11\u9053\uff1a\u201c\u662f\u554a\uff01\u5144\u5f1f\u600e\u628a\u8fd9\u7b49\u70ed\u95f9\u4e4b\u4e8b\u7ed9\u5fd8\u8bb0\u4e86\u5462\uff1f\u8981\u53bb\uff01\u90a3\u662f\u4e00\u5b9a\u8981\u53bb\u7684\u4e86\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u660e\u767d\u4e86\u5417\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u70b9\u5934\u9053\uff1a\u201c\u660e\u767d\u4e86\uff01\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u6e38\u516c\u7384\u5df2\u63a5\u7740\u5f80\u4e0b\u5ff5\u9053\uff1a\u201c\u672c\u6559\u53ec\u5f00\u6b64\u6b21\u6b66\u6797\u5927\u4f1a\uff0c\u53e6\u5916\u5c1a\u6709\u4e00\u5c42\u6df1\u610f\uff0c\u60f3\u6211\u6b66\u6797\u670b\u53cb\uff0c\u7ec8\u65e5\u884c\u8d70\u6c5f\u6e56\u5f7c\u6b64\u4e4b\u95f4\uff0c\u96be\u514d\u7ed3\u6709\u6028\u4ec7\uff0c\u672c\u6559\u5df2\u5728\u4f1a\u573a\u642d\u6709\u4e00\u5ea7\u201c\u5316\u89e3\u201d\u53f0\uff0c\u4e13\u4f9b\u7ed3\u6028\u7684\u670b\u53cb\u4f7f\u7528\uff0c\u4e0d\u8bba\u4efb\u4f55\u5927\u4ec7\u6df1\u6068\uff0c\u7686\u53ef\u60dc\u6b64\u673a\u4f1a\u5316\u89e3\uff0c\u76db\u4f1a\u4e4b\u540e\uff0c\u6b66\u6797\u5df1\u4e3a\u672c\u6559\u7684\u540c\u76df\uff0c\u81ea\u662f\u4e0d\u5bb9\u518d\u6709\u4ec7\u6740\u4e4b\u4e8b\uff0c\u5982\u6709\u8fdd\u53cd\uff0c\u672c\u6559\u4ea6\u5c06\u8bdb\u6740\u4e0d\u8d66\uff01\u201d\u6e38\u516c\u7384\u987f\u4e86\u4e00\u987f\uff0c\u7b11\u9053\uff1a \u3000\u3000\u201c\u4ed6\u4eec\u4ee5\u9738\u4e3b\u7684\u8eab\u4efd\u53d1\u51fa\u4e86\u8fd9\u7b49\u8bf7\u67ec\uff0c\u771f\u53eb\u8001\u592b\u4e0d\u6562\u76f8\u4fe1\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u76b1\u7709\u9053\uff1a\u201c\u5ff5\u5b8c\u4e86\u5417\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u6ca1\u6709\uff01\u201d \u3000\u3000\u4ed6\u76ee\u5149\u91cd\u65b0\u6295\u5230\u624b\u4e2d\u8bf7\u67ec\u4e4b\u4e0a\uff0c\u5ff5\u9053\uff1a\u201c\u672c\u6559\u6210\u7acb\u4ee5\u6765\uff0c\u6700\u6068\u6cbd\u540d\u9493\u8a89\u4e4b\u5f92\uff0c\u9648\u5e0c\u6b63\u73b0\u5df2\u4f0f\u6cd5\uff0c\u5251\u795e\u865a\u540d\uff0c\u5728\u672c\u6559\u9996\u5e2d\u6559\u4e3b\u6167\u773c\u4e4b\u4e0b\uff0c\u5df2\u88ab\u63ed\u7a7f\uff0c\u51e1\u6211\u6b66\u6797\u540c\u9053\uff0c\u5e94\u4ee5\u9648\u5e0c\u6b63\u884c\u52a8\u4e3a\u6212\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bfb\u5230\u8fd9\u91cc\uff0c\u53ea\u628a\u65b9\u96ea\u5b9c\u542c\u5f97\u53c8\u662f\u597d\u6c14\uff0c\u53c8\u662f\u597d\u7b11\uff01 \u3000\u3000\u5251\u795e\u7adf\u7136\u6210\u4e86\u6cbd\u540d\u9493\u8a89\u4e4b\u5f92\uff0c\u771f\u4e0d\u77e5\u6b66\u6797\u4e2d\u8981\u600e\u6837\u7684\u4eba\u7269\uff0c\u624d\u662f\u597d\u4eba\u4e86\uff01 \u3000\u3000\u8f9b\u67cf\u516c\u8d8a\u542c\u8d8a\u6709\u706b\uff0c\u6012\u9053\uff1a\u201c\u6e38\u8001\u5934\uff0c\u4f60\u5ff5\u5feb\u70b9\u4e0d\u597d\u5417\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u5475\u5475\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u679c\u771f\u662f\u5e94\u5f53\u5ff5\u5f97\u5feb\u4e9b\u624d\u662f\uff01\u201d \u3000\u3000\u4ed6\u7565\u7565\u4e00\u987f\uff0c\u63a5\u7740\u5ff5\u9053\uff1a\u201c\u6b64\u6b21\u6b66\u6797\u5927\u4f1a\u53ec\u5f00\u4e4b\u65e5\uff0c\u5373\u662f\u672c\u6559\u6b63\u5f0f\u4e3b\u76df\u6b66\u6797\u4e4b\u65f6\uff0c\u51e1\u6211\u6b66\u6797\u540c\u9053\uff0c\u4e0d\u8bba\u4e2a\u4eba\u6216\u5168\u6d3e\u5168\u6559\u5b50\u5f1f\uff0c\u82e5\u6709\u4e0d\u613f\u81e3\u670d\uff0c\u4e0d\u59a8\u5728\u5316\u89e3\u53f0\u4e0a\uff0c\u4e0e\u672c\u6559\u4e00\u8f83\u957f\u77ed\uff0c\u53ea\u6d88\u80fd\u8fde\u80dc\u4e09\u573a\uff0c\u672c\u6559\u4fbf\u4e0d\u8fc7\u95ee\u53bb\u7559\u3002\u201d\u8bfb\u5230\u8fd9\u91cc\uff0c\u6e38\u516c\u7384\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5b8c\u554a\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u957f\u957f\u5730\u5410\u4e86\u53e3\u5927\u6c14\uff0c\u9053\uff1a\u201c\u65e0\u803b\u4e4b\u5c24\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u5374\u662f\u51b7\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4e00\u6d3e\u80e1\u8a00\uff01\u201d \u3000\u3000\u4f46\u90a3\u970d\u9e23\u98ce\u5374\u9053\uff1a\u201c\u4e94\u9b54\u81ea\u79f0\u4e94\u5723\uff0c\u4ed6\u4eec\u679c\u771f\u4e0d\u628a\u4e5d\u5927\u6d3e\u653e\u5728\u773c\u4e2d\u4e86\uff01\u201d \u3000\u3000\u5c11\u6797\u638c\u95e8\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u970d\u65bd\u4e3b\uff0c\u54b1\u4eec\u53bb\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u53bb\uff1f\u54b1\u4eec\u4e5d\u5927\u95e8\u6d3e\u8054\u624b\uff0c\u96be\u9053\u8fd8\u5bf9\u4ed6\u4e0d\u8fc7\u5417\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u70b9\u5934\u5408\u5341\u9053\uff1a\u201c\u65bd\u4e3b\u8bf4\u7684\u662f\uff0c\u54b1\u4eec\u5c4a\u65f6\u524d\u53bb\u4fbf\u662f\u3002\u201d \u3000\u3000\u8f9b\u67cf\u516c\u76ee\u5149\u4e00\u8f6c\uff0c\u5927\u58f0\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u8fd9\u5c01\u8bf7\u67ec\uff0c\u662f\u4ec0\u4e48\u4eba\u9001\u6765\u7684\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u95fb\u8a00\uff0c\u5ffd\u7136\u8138\u8272\u4e00\u53d8\uff0c\u957f\u53f9\u9053\uff1a\u201c\u8d2b\u50e7\u6ca1\u6709\u89c1\u5230\u9001\u67ec\u4e4b\u4eba\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4f60\u5bfa\u4e2d\u7684\u548c\u5c1a\uff0c\u96be\u9053\u4e5f\u5143\u522b\u4eba\u89c1\u5230\u5417\uff1f\u201d \u3000\u3000\u4eba\u4e91\u5927\u5e08\u9053\uff1a\u201c\u8bf4\u6765\u60ed\u6127\uff0c\u636e\u672c\u5bfa\u503c\u5802\u5f1f\u5b50\u62a5\u544a\uff0c\u8fd9\u5c01\u8bf7\u67ec\uff0c\u4e43\u662f\u6446\u5728\u5927\u6bbf\u7684\u4f5b\u5802\u4e4b\u524d\u3002\u201d \u3000\u3000\u8f9b\u67cf\u516c\u5ffd\u7136\u8138\u8272\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u5c11\u6797\u9886\u8896\u6b66\u6797\uff0c\u5df2\u5386\u6570\u4e16\uff0c\u600e\u5730\u76ee\u4e0b\u90fd\u662f\u5982\u6b64\u7684\u4e0d\u6d4e\u4e8b\u4e86\uff1f\u4eba\u5bb6\u5dee\u4eba\u9001\u4e86\u8bf7\u67ec\u6765\u6b64\uff0c\u4f60\u4eec\u600e\u4f1a\u53d1\u73b0\u4e0d\u4e86\u7684\u5462\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u5374\u662f\u6c89\u541f\u9053\uff1a\u201c\u8f9b\u5144\uff0c\u5144\u5f1f\u5012\u662f\u60f3\u5230\u4e86\u4e00\u70b9\u53ef\u7591\u4e4b\u5904\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4ec0\u4e48\u53ef\u7591\u4e4b\u5904\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u53ea\u6015\u5c11\u6797\u6709\u4e86\u5185\u5978\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u4e00\u8df3\u800c\u8d77\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4e0d\u9519\uff01\u4e0d\u9519\uff01\u201d \u3000\u3000\u4f46\u90a3\u4eba\u4e91\u5927\u5e08\u5374\u662f\u53d8\u8272\u9053\uff1a\u201c\u6e38\u65bd\u4e3b\uff01\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u638c\u95e8\u4eba\u6709\u4f55\u89c1\u6559\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u5c11\u6797\u5b50\u5f1f\uff0c\u90fd\u662f\u4f5b\u7956\u5f1f\u5b50\uff0c\u600e\u4f1a\u505a\u51fa\u8fd9\u7b49\u6709\u8fb1\u5e08\u95e8\u7684\u4e8b\u6765\uff1f\u201d \u3000\u3000\u6e38\u516c\u7384\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u8bf7\u67ec\u80fd\u591f\u653e\u5728\u5927\u6bbf\u4f5b\u524d\uff0c\u51b3\u4e0d\u4f1a\u81ea\u5df1\u98de\u6765\uff0c\u5982\u662f\u8d35\u5bfa\u65e0\u4eba\u89c1\u5230\u6709\u4eba\u5165\u5185\uff0c\u90a3\u81ea\u7136\u662f\u5bfa\u5185\u50e7\u4fa3\u653e\u4e0a\u53bb\u7684\u4e86\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u5408\u5341\u9053\uff1a\u201c\u8d2b\u50e7\u4f9d\u7136\u4e0d\u6562\u76f8\u4fe1\u2026\u2026\u201d \u3000\u3000\u8f9b\u67cf\u516c\u51b7\u54fc\u4e00\u58f0\u9053\uff1a\u201c\u4e0d\u4fe1\u6709\u4e2a\u5c41\u7528\uff1f\u8fd9\u4e8b\u5df2\u660e\u660e\u6446\u5728\u773c\u524d\uff0c\u90a3\u8fd8\u6709\u4ec0\u4e48\u5047\u7684\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u63a5\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u6e38\u5144\u4e4b\u8a00\uff0c\u5b81\u53ef\u4fe1\u5176\u6709\uff0c\u4e0d\u53ef\u4fe1\u5176\u65e0\uff0c\u638c\u95e8\u4eba\u6700\u597d\u662f\u67e5\u770b\u4e00\u4e0b\u2026\u2026\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u8d2b\u50e7\u9075\u547d\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u95f4\uff0c\u5c31\u5f85\u4f20\u4ee4\uff0c\u8981\u90a3\u5168\u5bfa\u5f1f\u5b50\u524d\u53bb\u5927\u6bbf\u5019\u67e5\u3002 \u3000\u3000\u4f46\u662f\uff0c\u5b8b\u5b5a\u5ffd\u7136\u6447\u624b\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u4f60\u628a\u5168\u5bfa\u5f1f\u5b50\u96c6\u4e2d\u5230\u5927\u6bbf\u4ee5\u540e\uff0c\u53c8\u5c06\u600e\u4e48\u67e5\u6cd5\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u8d2b\u50e7\u5f53\u4f17\u5ba3\u79f0\uff0c\u672c\u5bfa\u6709\u4e86\u5978\u7ec6\uff0c\u8981\u4ed6\u4eec\u9762\u5411\u4f5b\u7956\u53cd\u6094\uff0c\u5982\u662f\u5177\u6709\u5978\u7ec6\uff0c\u4ed6\u5fc5\u7136\u53d7\u4e0d\u4e86\u826f\u5fc3\u91cd\u8d23\uff0c\u81ea\u884c\u51fa\u9762\u8ba4\u7f6a\u4e86\uff01\u201d \u3000\u3000\u4ed6\u8fd9\u7b49\u8bf4\u6cd5\uff0c\u53ea\u628a\u8f9b\u67cf\u516c\u542c\u5f97\u8fde\u8fde\u6447\u5934\uff0c\u795d\u516c\u660e\u3001\u6e38\u516c\u7384\u4e0d\u4f4f\u957f\u53f9\uff0c\u5b89\u5c0f\u840d\u5219\u5fcd\u4e0d\u4f4f\u6251\u54e7\u4e00\u7b11\u51fa\u58f0\u3002 \u3000\u3000\u5165\u4e91\u5927\u5e08\u4e00\u6014\u9053\uff1a\u201c\u600e\u4e48\uff1f\u53ef\u662f\u8d2b\u50e7\u8bf4\u9519\u4e86\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5374\u9053\uff0c\u201c\u638c\u95e8\u4eba\uff0c\u4ed6\u4eec\u4e0d\u4f1a\u627f\u8ba4\u554a\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u600e\u7684\u4e0d\u4f1a\u627f\u8ba4\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4ed6\u5982\u662f\u6709\u5fc3\u53db\u5bfa\uff0c\u54ea\u91cc\u8fd8\u6709\u826f\u5fc3\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u4e5f\u7b11\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u5b89\u59d1\u5a18\u8bf4\u5f97\u4e0d\u9519\uff0c\u4ed6\u4eec\u5982\u6709\u826f\u5fc3\uff0c\u90a3\u4e5f\u4e0d\u4f1a\u80cc\u53db\u5c11\u6797\u4e86\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u4f9d\u65bd\u4e3b\u4e4b\u89c1\u5462\uff1f\u201d \u3000\u3000\u5b8b\u5b5a\u9053\uff1a\u201d\u6839\u672c\u4e0d\u7528\u67e5\u95ee\uff0c\u4e14\u7b49\u53c2\u4e0e\u90a3\u6b66\u6797\u5927\u4f1a\u4e4b\u540e\uff0c\u4e0d\u67e5\u4e5f\u5c31\u81ea\u660e\u4e86\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u6c89\u541f\u9053\uff1a\u201c\u5982\u662f\u65bd\u4e3b\u7b49\u8ba4\u4e3a\u8fd9\u6837\u6700\u597d\uff0c\u8001\u8872\u81ea\u5f53\u9075\u547d\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u81ea\u7136\u662f\u8fd9\u4e48\u529e\u4e86\uff01\u5426\u5219\uff0c\u53ea\u6015\u4f60\u548c\u5c1a\u4e0d\u4f46\u627e\u4e0d\u51fa\u5978\u7ec6\uff0c\u8bf4\u4e0d\u8fc7\u8fd8\u5f97\u8d34\u4e0a\u51e0\u6761\u4eba\u547d\u5462\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u53ea\u6709\u5408\u5341\u53e3\u55a7\u4f5b\u53f7\u4e0d\u6b62\u3002 \u3000\u3000\u970d\u545c\u98ce\u5ffd\u7136\u6d53\u7709\u4e00\u626c\uff0c\u5927\u58f0\u9053\uff1a\u201c\u8f9b\u8001\u524d\u8f88\uff0c\u5927\u4f1a\u4e3a\u671f\u4e0d\u8fdc\uff0c\u665a\u8f88\u60f3\u8981\u79bb\u5bfa\u4e09\u65e5\uff0c\u9884\u4f5c\u5b89\u6392\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bdd\u672a\u5b8c\uff0c\u53ea\u89c1\u77e5\u5ba2\u6cd5\u56e0\uff0c\u6025\u6b65\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u8eac\u8eab\u5408\u5341\u9053\uff1a\u201c\u8fb0\u5dde\u8a00\u5bb6\u638c\u95e8\u6709\u6c42\u89c1\uff1f\u201d \u3000\u3000\u5165\u5723\u5927\u5e08\u9053\uff1a\u201c\u8a00\u5bb6\u7684\u638c\u95e8\u4eba\u6765\u6b64\u4f5c\u751a\uff1f\u4f60\u4e0d\u4f1a\u5c06\u4ed6\u63a5\u5165\u5ba2\u5ba4\u5417\uff1f\u201d \u3000\u3000\u6cd5\u56e0\u9053\uff1a\u201c\u4ed6\u4e43\u662f\u627e\u65b9\u65bd\u4e3b\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8a00\u5144\u4e0e\u665a\u8f88\u4e43\u662f\u6545\u4ea4\uff0c\u5c1a\u671b\u638c\u95e8\u4eba\u5bb9\u4ed6\u5165\u5185\u4e00\u6664\uff01\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u9053\uff1a\u201c\u65e2\u662f\u5c0f\u65bd\u4e3b\u6545\u4ea4\uff0c\u6cd5\u56e0\u5feb\u5feb\u6709\u8bf7\uff01\u201d \u3000\u3000\u6cd5\u56e0\u5927\u5e08\u5e94\u58f0\u800c\u53bb\u3002\u4e0d\u591a\u65f6\uff0c\u53ea\u89c1\u8a00\u51e4\u537f\u6ee1\u9762\u98ce\u5c18\u5730\u8d70\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u8d77\u8eab\u8fce\u4e86\u8fc7\u53bb\uff0c\u53cc\u624b\u62c9\u4f4f\u4e86\u8a00\u51e4\u537f\uff0c\u7b11\u9053\uff1a\u201c\u8a00\u5144\uff0c\u60f3\u715e\u5c0f\u5f1f\u4e86\uff01\u201d \u3000\u3000\u8a00\u51e4\u537f\u76ee\u5149\u5982\u7535\uff0c\u5728\u65b9\u96ea\u5b9c\u8eab\u4e0a\u6253\u91cf\u4e86\u4e00\u4f1a\uff0c\u5ffd\u7136\u6717\u58f0\u7b11\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u4f60\u957f\u5f97\u5982\u6b64\u5f3a\u58ee\uff0c\u82e5\u662f\u5728\u8def\u4e0a\u9047\u89c1\uff0c\u611a\u5144\u53ef\u771f\u4e0d\u6562\u76f8\u8bc6\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u4e86\u4e00\u7b11\uff0c\u7acb\u5373\u4e3a\u4ed6\u4ecb\u7ecd\u4e86\u5728\u5ea7\u8bf8\u4eba\u3002 \u3000\u3000\u8a00\u51e4\u537f\u5e74\u7eaa\u4e0d\u5927\uff0c\u81ea\u7136\u4e0e\u5728\u5ea7\u4eba\u4e2d\uff0c\u76f8\u8bc6\u8005\u6781\u5c11\uff0c\u65b9\u96ea\u5b9c\u4e3a\u4ed6\u4ecb\u7ecd\u4ee5\u540e\uff0c\u4fbf\u4e00\u4e00\u4e0a\u524d\u62dc\u89c1\uff0c\u7136\u540e\u5750\u5728\u65b9\u96ea\u5b9c\u8eab\u8fb9\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4f4e\u58f0\u9053\uff1a\u201c\u8a00\u5144\uff0c\u4e2d\u5cb3\u5e99\u6b66\u6797\u5927\u4f1a\u4e4b\u4e8b\uff0c\u4f60\u53ef\u63a5\u5230\u8bf7\u67ec\u4e86\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u63a5\u9053\uff1a\u201c\u63a5\u5230\u4e86\uff01\u5144\u5f1f\uff0c\u611a\u5144\u6b64\u6765\uff0c\u672c\u60f3\u9080\u4f60\u8d81\u8fd9\u673a\u4f1a\uff0c\u4e00\u540c\u524d\u53bb\u53c2\u4e0e\u5462\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u7b11\u9053\uff1a\u201c\u5f88\u597d\u554a\uff01\u5144\u5f1f\u548c\u8bf8\u4f4d\u524d\u8f88\uff0c\u4e5f\u6b63\u5728\u5546\u91cf\u6b64\u4e8b\uff01\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u5ffd\u7136\u60f3\u8d77\u8a00\u5bb6\u95e8\u5931\u843d\u7684\u6b66\u529f\u4e4b\u4e8b\uff0c\u63a5\u9053\uff1a\u201c\u8a00\u5144\uff0c\u4f60\u53bb\u8fc7\u9ed1\u9f99\u6f6d\u5417\uff1f\u5931\u53bb\u7684\u6b66\u529f\uff0c\u53ef\u66fe\u5bfb\u7740\u4e86\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u957f\u957f\u4e00\u53f9\u9053\uff1a\u201c\u53bb\u8fc7\u4e86\uff0c\u53ea\u662f\u2026\u2026\u5e76\u672a\u7ed3\u679c\uff0c\u770b\u6765\u662f\u65e0\u6cd5\u5bfb\u5230\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6c89\u601d\u4e86\u4e00\u4f1a\uff0c\u9053\uff1a\u201c\u8a00\u5144\uff0c\u6b66\u6797\u5927\u4f1a\u4e4b\u540e\uff0c\u5144\u5f1f\u966a\u4f60\u524d\u53bb\u5bfb\u627e\u5931\u53bb\u7684\u6b66\u529f\u5982\u4f55\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u76ee\u5149\u5728\u8a00\u98ce\u537f\u8eab\u4e0a\u4e00\u8f6c\uff0c\u5927\u58f0\u9053\uff1a\u201c\u8fb0\u5dde\u8a00\u5bb6\u62f3\u6cd5\u7684\u7cbe\u9ad3\uff0c\u4f60\u5b66\u4f1a\u4e86\u591a\u5c11\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u81ea\u7136\u542c\u8fc7\u6b66\u6797\u4e00\u602a\u4e4b\u540d\uff0c\u95fb\u8a00\u5fd9\u9053\uff1a\u201c\u8a00\u5bb6\u95e8\u5386\u906d\u51f6\u9669\uff0c\u4ee5\u81f4\u8fd9\u51e0\u5341\u5e74\u6765\uff0c\u65e5\u8d8b\u6ca1\u843d\uff0c\u81ea\u4ece\u4e94\u5341\u5e74\u524d\u7684\u90a3\u4e00\u4ee3\u638c\u95e8\u4eba\u548c\u5341\u5927\u9ad8\u624b\u76f8\u7ee7\u6b7b\u53bb\u4e4b\u540e\uff0c\u8a00\u5bb6\u95e8\u6b66\u529f\u7cbe\u9ad3\uff0c\u5df2\u7136\u5168\u90e8\u5931\u4f20\uff0c\u665a\u8f88\u53ef\u8bf4\u662f\u534a\u70b9\u95e8\u5f84\u4e5f\u672a\u5f97\u7aa5\u2026\u2026\u201d \u3000\u3000\u8f9b\u67cf\u516c\u7b11\u9053\uff1a\u201c\u4f60\u66fe\u63d0\u8fc7\u9ed1\u9f99\u6f6d\uff0c\u4e0d\u77e5\u662f\u6cf0\u5c71\u7684\uff1f\u8fd8\u662f\u4e91\u5357\u7684\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u6cf0\u5c71\u9ed1\u9f99\u6f6d\u3002\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u8d35\u95e8\u7684\u6b66\u529f\uff0c\u662f\u5931\u843d\u5728\u90a3\u91cc\u5417\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u665a\u8f88\u591a\u5e74\u8ffd\u5bfb\u7684\u7ed3\u679c\uff0c\u4f3c\u662f\u672c\u95e8\u7684\u6b66\u529f\u7cbe\u534e\uff0c\u591a\u534a\u662f\u7559\u5728\u2018\u9ed1\u9f99\u6f6d\u2019\u9644\u8fd1\u67d0\u4e00\u573a\u6240\uff01\u201d\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u5c0f\u5a03\u513f\uff0c\u8001\u592b\u6709\u4e00\u53e5\u8bdd\uff0c\u8bf4\u5c06\u51fa\u6765\uff0c\u4f60\u53ef\u83ab\u8981\u89c1\u602a\uff01\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u8001\u524d\u8f88\u8bf7\u8bf4\u65e0\u59a8\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u8d35\u95e8\u6b66\u529f\u7cbe\u534e\uff0c\u5982\u662f\u672a\u66fe\u5931\u53bb\uff0c\u4f60\u81ea\u4fe1\u5168\u90e8\u5b66\u4f1a\u4ee5\u540e\uff0c\u662f\u5426\u4fbf\u8db3\u4ee5\u548c\u6b66\u6797\u4e2d\u4e00\u6d41\u9ad8\u624b\u76f8\u8f83\uff1f\u201d \u3000\u3000\u8a00\u98ce\u537f\u4e00\u65f6\u5f04\u4e0d\u6e05\u8f9b\u67cf\u516c\u95ee\u8bdd\u7684\u7528\u610f\u4f55\u5728\uff0c\u8fdf\u7591\u4e86\u4e00\u4e0b\uff0c\u63a5\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u81ea\u7136\u5dee\u4e86\u4e00\u7b79\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u7b11\u9053\uff1a\u201c\u662f\u4e86\uff0c\u65e2\u7136\u4f60\u5373\u4f7f\u5c06\u5931\u53bb\u7684\u6b66\u529f\u627e\u56de\uff0c\u4f9d\u7136\u4e0d\u8db3\u4ee5\u5728\u6b66\u6797\u4e2d\u5927\u653e\u5f02\u5f69\uff0c\u4f55\u4e0d\u521b\u6c42\u6eaa\u5f84\uff0c\u53e6\u95ef\u95e8\u8def\u5462\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u76b1\u7709\u9053\uff1a\u201c\u672c\u95e8\u5386\u4ee3\u76f8\u4f20\u7684\u6b66\u529f\uff0c\u665a\u8f88\u600e\u80fd\u5bb9\u5176\u57cb\u6ca1\u5931\u843d\uff1f\u4f55\u51b5\u2026\u2026\u665a\u8f88\u4e5f\u60f3\u4e0d\u51fa\u8fd8\u6709\u4ec0\u4e48\u65b9\u6cd5\uff0c\u8db3\u4ee5\u91cd\u632f\u672c\u95e8\u58f0\u5a01\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u770b\u4e86\u795d\u516c\u660e\u4e00\u773c\uff0c\u7b11\u9053\uff1a\u201c\u5929\u4e0b\u4e4b\u4eba\uff0c\u591a\u7684\u662f\u5947\u4eba\u5f02\u4e0a\uff0c\u4f60\u5982\u4e0d\u53bb\u5bfb\u6c42\uff0c\u90a3\u81ea\u7136\u662f\u65e0\u4ece\u91cd\u632f\u58f0\u5a01\u4e86\uff01\u201d \u3000\u3000\u8a00\u51e4\u537f\u95fb\u8a00\uff0c\u5fc3\u4e2d\u4e00\u9707\uff0c\u6697\u9053\uff1a\u201c\u542c\u4ed6\u4e4b\u8a00\uff0c\u5012\u4f3c\u6709\u7740\u5f26\u5916\u4e4b\u610f\u2026\u2026\u201d\u4f46\u4ed6\u53e3\u4e2d\u5374\u9053\uff1a \u3000\u3000\u201c\u8001\u524d\u8f88\u6709\u4f55\u6307\u6559\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u8001\u592b\u501f\u8457\u4ee3\u7b79\uff0c\u773c\u4e0b\u4fbf\u6709\u4e00\u4eba\uff0c\u8db3\u53ef\u52a9\u4f60\u91cd\u632f\u58f0\u5a01\uff01\u201d \u3000\u3000\u8a00\u51e4\u537f\u5fc3\u60f3\uff0c\u662f\u4e86\uff0c\u4ed6\u679c\u7136\u6709\u4e86\u8981\u6211\u53e6\u6295\u95e8\u5e08\u4e4b\u610f\uff0c\u53ea\u662f\uff0c\u6211\u8eab\u4e3a\u4e00\u4ee3\u638c\u95e8\uff0c\u8fd9\u4e8b\u53c8\u600e\u80fd\u4f7f\u5f97\uff0c\u987f\u65f6\uff0c\u534a\u664c\u6ca1\u8bf4\u8bdd\u3002 \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4f60\u600e\u4e48\u4e0d\u8bf4\u8bdd\u4e86\uff0c\u83ab\u975e\u4f60\u4e0d\u76f8\u4fe1\uff0c\u6709\u4eba\u80fd\u4f7f\u4f60\u626c\u540d\u6b66\u6797\u5417\uff1f\u201d \u3000\u3000\u8a00\u98ce\u537f\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8001\u524d\u8f88\u7684\u76db\u610f\uff0c\u665a\u8f88\u5341\u5206\u611f\u6fc0\uff0c\u53ea\u662f\uff0c\u8981\u665a\u8f88\u53e6\u6295\u4ed6\u4eba\u95e8\u4e0b\u5b66\u827a\uff0c\u665a\u8f88\u4e0d\u6562\u5141\u8bfa\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u6b66\u6797\u5927\u9006\uff0c\u83ab\u8fc7\u53db\u5e08\u4e4b\u4eba\uff0c\u665a\u8f88\u4e0d\u6562\u81ea\u7edd\u4e8e\u95e8\u4eba\u4e4b\u524d\u3002\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u8001\u592b\u51e0\u65f6\u8bf4\u8fc7\u8981\u4f60\u53db\u5e08\u4ed6\u6295\u4e86\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u665a\u8f88\u60f3\u9519\u4e86\u5417\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u6b63\u662f\u4f60\u60f3\u9519\u4e86\uff01\u8001\u592b\u4e4b\u610f\uff0c\u53ea\u662f\u66ff\u4f60\u627e\u4e00\u4e2a\u6b66\u529f\u5353\u7edd\u4e4b\u58eb\uff0c\u6307\u70b9\u4f60\u4e00\u756a\uff0c\u4f7f\u5f97\u4f60\u4eec\u8a00\u5bb6\u95e8\u7684\u6b66\u529f\u66f4\u4e0a\u5c42\u697c\uff01\u201d \u3000\u3000\u8a00\u51e4\u537f\u6c89\u541f\u9053\uff1a\u201c\u8fd9\u2026\u2026\u7b97\u4e0d\u7b97\u53e6\u6295\u5e08\u95e8\u5427\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5927\u58f0\u9053\uff1a\u201c\u5f53\u7136\u4e0d\u7b97\uff01\u201d \u3000\u3000\u8a00\u51e4\u537f\u611f\u6fc0\u5730\u770b\u4e86\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u8fd9\u624d\u5411\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u4f60\u8001\u8981\u4ec0\u4e48\u4eba\u6307\u70b9\u665a\u8f88\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u5929\u9e64\u4e03\u65a9\u7684\u4f20\u4eba\u548c\u795d\u8001\u513f\u4e24\u4eba\u5982\u4f55\uff1f\u201d \u3000\u3000\u4ed6\u8bdd\u97f3\u4e00\u843d\uff0c\u5b89\u5c0f\u840d\u51e0\u4e4e\u60ca\u5f97\u8df3\u4e86\u8d77\u6765\uff01\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u8f9b\u516c\u516c\uff0c\u4f60\u8001\u4eba\u5bb6\u600e\u4f1a\u627e\u4e0a\u4e86\u6211\u561b\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u3001\u8a00\u51e4\u537f\u4e5f\u90fd\u89c9\u5f97\u751a\u4e3a\u610f\u5916\uff0c\u4f46\u4ed6\u4eec\u5374\u6ca1\u6709\u5f00\u53e3\u8bf4\u8bdd\u3002 \u3000\u3000\u8f9b\u67cf\u516c\u7b11\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u541b\u5b50\u6210\u4eba\u4e4b\u7f8e\uff0c\u90a3\u8a00\u5bb6\u638c\u95e8\uff0c\u4e43\u662f\u4f60\u65b9\u5927\u54e5\u7684\u597d\u670b\u53cb\uff0c\u5e2e\u5e2e\u4ed6\u7684\u5fd9\uff0c\u6709\u4f55\u4e0d\u53ef\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5148\u662f\u6014\u4e86\u4e00\u6014\uff0c\u968f\u800c\u5ffd\u7136\u60f3\u901a\u4e86\u4ec0\u4e48\u4f3c\u7684\uff0c\u7c89\u9762\u4e00\u7ea2\uff0c\u5a07\u7b11\u4e00\u58f0\uff0c\u4f4e\u5934\u9053\uff1a\u201c\u8f9b\u516c\u516c\uff0c\u53ea\u6015\u2026\u2026\u665a\u8f88\u52a9\u4e0d\u4e86\u8a00\u5927\u638c\u95e8\u554a\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u5927\u7b11\u9053\uff1a\u201c\u52a9\u5f97\u4e86\uff01\u90a3\u8a00\u5bb6\u62f3\u638c\uff0c\u672c\u6709\u4e00\u534a\u662f\u8131\u80ce\u4e8e\u5929\u9e64\u4e03\u65a9\uff0c\u59d1\u5a18\u6307\u70b9\u4e8e\u4ed6\uff0c\u6b63\u662f\u4f7f\u4ed6\u4eec\u8a00\u5bb6\u95e8\u7684\u6b66\u529f\uff0c\u8fd4\u8865\u5f52\u771f\u5927\u6709\u4e8b\u534a\u529f\u500d\u4e4b\u6548\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u5f53\u771f\u5982\u6b64\u5417\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u8001\u592b\u51e0\u65f6\u9a97\u8fc7\u4f60\u4eec\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u8fd9\u65f6\u5374\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u8f9b\u5144\uff0c\u597d\u597d\u7684\u5dee\u4e8b\uff0c\u5927\u6982\u662f\u4e34\u4e0d\u5230\u5144\u5f1f\u7684\u5934\u4e0a\u7684\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u7b11\u9053\uff1a\u201c\u795d\u5144\u5f1f\uff0c\u4f60\u53ef\u662f\u4e0d\u613f\u5417\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u4e0d\u662f\u4e0d\u613f\uff0c\u53ea\u662f\u6709\u4e9b\u513f\u5fc3\u6240\u4e0d\u7518\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u5982\u662f\u4f60\u51fa\u9762\u6307\u70b9\u4e00\u756a\uff0c\u5c82\u4e0d\u662f\u8981\u6bd4\u5144\u5f1f\u5f3a\u8fc7\u751a\u591a\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u4f60\u9519\u5566\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u600e\u4e48\u9519\u4e86\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4ee4\u53d4\u7684\u8d64\u7130\u638c\uff0c\u5176\u4e2d\u7684\u62db\u6570\uff0c\u5927\u534a\u662f\u6e90\u51fa\u4e8e\u8a00\u5bb6\u7684\u638c\u6cd5\uff0c\u4f60\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u77e5\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4e86\uff01\u90a3\u8a00\u5bb6\u7684\u6b66\u529f\uff0c\u6b63\u5982\u8a00\u51e4\u537f\u6240\u8a00\uff0c\u5931\u843d\u5728\u6cf0\u5c71\u9ed1\u9f99\u6f6d\uff0c\u5176\u5b9e\uff0c\u8bf4\u4ed6\u5931\u843d\uff0c\u90a3\u4e5f\u4e0d\u786e\uff0c\u4e43\u662f\u8a00\u5bb6\u7684\u90a3\u4f4d\u638c\u95e8\u4eba\uff0c\u5728\u9ed1\u9f99\u6f6d\u9047\u4e0a\u4e86\u4e00\u540d\u9b54\u5934\uff0c\u5979\u4e0d\u4f46\u593a\u53d6\u8a00\u5bb6\u7684\u6b66\u529f\uff0c\u5e76\u4e14\u5728\u77ed\u77ed\u7684\u4e00\u6708\u4e4b\u4e2d\uff0c\u5c06\u8a00\u5bb6\u7684\u5341\u5927\u9ad8\u624b\u626b\u6570\u6bd2\u6b7b\u2026\u2026\uff0c\u5176\u5b9e\uff0c\u90a3\u540d\u5996\u5987\u5e76\u975e\u662f\u60f3\u8981\u8a00\u5bb6\u7684\u6b66\u529f\uff0c\u5979\u53d6\u5f97\u8a00\u5bb6\u6b66\u529f\u79d8\u7b08\u4ee5\u540e\uff0c\u7adf\u662f\u8fde\u7740\u9769\u56ca\uff0c\u6295\u5165\u4e86\u9ed1\u9f99\u6f6d\u4e2d\u3002\u201d \u3000\u3000\u8a00\u51e4\u537f\u6697\u9053\uff1a\u201c\u539f\u6765\u6295\u5165\u4e86\u6f6d\u4e2d\u53bb\u4e86\uff0c\u96be\u602a\u4e94\u5341\u5e74\u6765\u627e\u5b83\u4e0d\u5230\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u90a3\u5a07\u5987\u539f\u662f\u82d7\u7586\u7684\u4e00\u4f4d\u5cd2\u4e3b\u7684\u5973\u513f\uff0c\u5e74\u8f7b\u65f6\u5728\u4e2d\u539f\u8d70\u52a8\uff0c\u4e0e\u8a00\u5bb6\u95e8\u90a3\u638c\u95e8\u4eba\u76f8\u9047\uff0c\u7ed3\u4e0b\u4e0d\u89e3\u4e4b\u7f18\uff0c\u540e\u6765\u5374\u53c8\u95f9\u7ffb\uff0c\u90a3\u4f4d\u638c\u95e8\u53e6\u5a36\u4e86\u59bb\u5ba4\uff0c\u5996\u5973\u542c\u8bf4\uff0c\u4fbf\u91cd\u5c65\u4e2d\u539f\uff0c\u7ec8\u4e8e\u4e0b\u4e86\u6bd2\u624b\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u8fd9\u4e0e\u8d64\u7130\u638c\u4f55\u5173\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u5f53\u7136\u6709\u5173\uff0c\u56e0\u4e3a\u4ee4\u53d4\u5728\u90a3\u5996\u5987\u6c89\u4e0b\u79d8\u7b08\u540e\u7684\u4e09\u5e74\uff0c\u5076\u7136\u53bb\u4e86\u9ed1\u9f99\u6f6d\u5bfb\u627e\u4e00\u5c3e\u91d1\u9ca4\u5f53\u4f5c\u836f\u5f15\uff0c\u91d1\u9ca4\u4e0d\u66fe\u9493\u5230\uff0c\u5374\u628a\u90a3\u5305\u79d8\u7b08\u9493\u4e86\u4e0a\u6765\u3002\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u83ab\u975e\u5144\u5f1f\u5bb6\u4f20\u6b66\u529f\uff0c\u5c31\u662f\u8a00\u5bb6\u95e8\u7684\u6b66\u529f\u5417\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u90a3\u4e5f\u4e0d\u662f\uff0c\u4ee4\u53d4\u6253\u5f00\u9769\u56ca\u4e00\u770b\uff0c\u53d1\u73b0\u4e43\u662f\u8a00\u5bb6\u6b66\u529f\u548c\u82d7\u7586\u7684\u6b66\u529f\u2026\u2026\u201d \u3000\u3000\u8a00\u51e4\u537f\u4e00\u6014\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u5176\u4e2d\u8fd8\u6709\u82d7\u7586\u6b66\u529f\u5417\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u90a3\u82d7\u5987\u6068\u4f60\u90a3\u4f4d\u957f\u8f88\u5165\u9aa8\uff0c\u91cd\u5165\u4e2d\u539f\uff0c\u4fbf\u5df2\u6709\u5055\u4ea1\u4e4b\u5fc3\uff0c\u662f\u4ee5\u6c89\u4e0b\u6b66\u529f\u65f6\uff0c\u5979\u628a\u81ea\u5df1\u7684\u6b66\u529f\u4e5f\u88c5\u5165\u5176\u4e2d\u4e86\uff01\u201d \u3000\u3000\u8a00\u51e4\u537f\u6697\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4ee4\u53d4\u5f97\u5230\u79d8\u7b08\uff0c\u4fbf\u5c06\u5176\u4e2d\u7684\u6b66\u529f\uff0c\u770b\u4e86\u4e00\u904d\uff0c\u5bf9\u4e8e\u5929\u5357\u602a\u5ba2\u800c\u8a00\uff0c\u81ea\u7136\u662f\u5f88\u5c11\u770b\u5f97\u4e0a\u773c\u4e86\uff0c\u4e0d\u8fc7\u4ed6\u5374\u53d1\u73b0\u5176\u4e2d\u4e00\u5957\u638c\u6cd5\uff0c\u5982\u662f\u4e0e\u82d7\u7586\u6b66\u529f\u5408\u5e76\uff0c\u5fc5\u53ef\u5f62\u6210\u4e00\u79cd\u7edd\u5b66\uff0c\u8fd9\u5c31\u662f\u8d64\u7130\u638c\u521b\u7acb\u7684\u7531\u6765\uff0c\u5144\u5f1f\u4f60\u8bf4\u8001\u592b\u8be5\u4e0d\u8be5\u8981\u4f60\u6307\u70b9\u4e8e\u4ed6\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u90a3\u662f\u4e49\u4e0d\u5bb9\u8f9e\u7684\u4e86\uff01\u201d \u3000\u3000\u8a00\u51e4\u537f\u5374\u662f\u751a\u4e3a\u5173\u5fc3\u90a3\u79d8\u7b08\u4e0b\u843d\uff0c\u5fd9\u9053\uff1a\u201c\u8f9b\u8001\u524d\u8f88\uff0c\u90a3\u795d\u5927\u4fa0\u53ef\u66fe\u8bf4\u8fc7\u628a\u90a3\u4e9b\u79d8\u7b08\u600e\u4e48\u5904\u7f6e\u4e86\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u8fd9\u5012\u4e0d\u77e5\uff0c\u4e0d\u8fc7\uff0c\u89c1\u5230\u6076\u5b54\u660e\u8ba1\u901a\u4e4b\u65f6\uff0c\u5c31\u53ef\u77e5\u6653\uff01\u201d \u3000\u3000\u8a00\u51e4\u537f\u6014\u4e86\u4e00\u6014\uff0c\u5c1a\u672a\u6765\u5f97\u53ca\u8bf4\u8bdd\uff0c\u5b89\u5c0f\u840d\u5df2\u7b11\u9053\uff1a\u201c\u8f9b\u516c\u516c\uff0c\u8a00\u5bb6\u6b66\u529f\u4e3a\u4f55\u4e0e\u5929\u9e64\u4e03\u65a9\u6709\u5173\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u5176\u4e2d\u8be6\u60c5\uff0c\u8001\u592b\u4e5f\u4e0d\u751a\u6e05\u695a\uff0c\u4e0d\u8fc7\uff0c\u8a00\u5bb6\u95e8\u6b66\u529f\uff0c\u66fe\u7ecf\u878d\u5165\u5927\u9e64\u4e03\u65a9\u62db\u5f0f\uff0c\u4e43\u662f\u9648\u5e0c\u6b63\u544a\u77e5\u4e8e\u6211\uff0c\u60f3\u5fc5\u5b9a\u4e0d\u4f1a\u9519\u7684\u4e86\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5251\u795e\u9648\u5927\u4fa0\u8bf4\u7684\u5417\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u636e\u9648\u8001\u5f1f\u8bf4\uff0c\u59d1\u5a18\u5bb6\u4f20\u6b66\u529f\uff0c\u539f\u975e\u5929\u9e64\u4e03\u65a9\uff0c\u800c\u662f\u5929\u9e64\u4e09\u5251\uff0c\u5f53\u5e74\u4ee4\u7956\u592b\u5987\u8fdb\u5165\u4e2d\u539f\u6240\u7528\u7684\u5251\u6cd5\uff0c\u5927\u6982\u662f\u4e0d\u5047\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u8f9b\u5144\u4e4b\u8a00\uff0c\u4e00\u70b9\u4e0d\u9519\uff0c\u5144\u5f1f\u4e0e\u90a3\u5b89\u516c\u592b\u5987\uff0c\u4e24\u4ee3\u81f3\u4ea4\uff1b\u5929\u9e64\u4e09\u5251\u6b63\u662f\u5b89\u6c0f\u5bb6\u4f20\u6b66\u529f\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u795d\u516c\u516c\uff0c\u65e2\u7136\u5929\u9e64\u4e09\u5251\u662f\u6211\u7684\u5bb6\u4f20\u6b66\u529f\uff0c\u4e3a\u4f55\u73b0\u5728\u53c8\u6210\u4e86\u5929\u9e64\u4e03\u65a9\u5462\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u8fd9\u4e2a\u8001\u673d\u5c31\u4e0d\u592a\u660e\u767d\u4e86\uff01\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4e0d\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u8001\u673d\u4e5f\u66fe\u95ee\u8fc7\u4ee4\u7956\uff0c\u4ee4\u7956\u7b11\u800c\u4e0d\u7b54\uff0c\u5012\u662f\u4f60\u7956\u6bcd\u8bf4\u4e86\u4e00\u53e5\u8bdd\uff0c\u8010\u4eba\u5bfb\u601d\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u6211\u5976\u5976\u8bf4\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u4f60\u7956\u6bcd\u8bf4\u4ed6\u592b\u5987\u53e6\u6709\u9047\u5408\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u4e00\u6014\u9053\uff1a\u201c\u83ab\u975e\u662f\u8a00\u5bb6\u95e8\u7684\u6b66\u529f\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u7b11\u9053\uff1a\u201c\u8001\u673d\u4e0d\u6562\u8bf4\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u795d\u5144\uff0c\u5929\u9e64\u4e03\u65a9\u4e0d\u662f\u51fa\u81ea\u8fb0\u5dde\u8a00\u5bb6\u95e8\uff0c\u800c\u662f\u51fa\u81ea\u5251\u5723\u9f99\u5927\u4fa0\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8f9b\u8001\u524d\u8f88\uff0c\u4f60\u53ef\u662f\u8bf4\u7684\u665a\u8f88\u7956\u5e08\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u6c89\u541f\u9053\uff1a\u201c\u8f9b\u5144\u600e\u4f1a\u60f3\u5230\u4ed6\u51fa\u81ea\u5251\u5723\u9f99\u5927\u4fa0\u7684\u4f20\u6388\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u7b11\u9053\uff1a\u201c\u5f53\u5e74\u5b89\u4e5d\u516c\u592b\u5987\u8fdb\u5165\u4e2d\u539f\uff0c\u66fe\u5c06\u4e2d\u539f\u5341\u5927\u9ad8\u624b\u51fb\u8d25\uff0c\u540e\u6765\u4e0d\u6218\u800c\u9000\u4e4b\u4e8b\uff0c\u795d\u5144\u81ea\u662f\u77e5\u9053\u4e86\u2026\u2026\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5b89\u5144\u4e5f\u66fe\u5411\u6211\u8bf4\u8fc7\uff01\u4ed6\u4eec\u4e43\u662f\u8d25\u5728\u5251\u5723\u9f99\u5927\u4fa0\u7684\u2018\u9f99\u884c\u5251\u6cd5\u2019\u4e4b\u4e0b\uff0c\u4f7f\u56de\u8f6c\u4e1c\u6d77\u53bb\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u5566\uff01\u540e\u6765\u9f99\u5927\u4fa0\u66fe\u7ecf\u4e13\u7a0b\u6765\u5230\u4e1c\u6d77\uff0c\u63a2\u8bbf\u8fc7\u4ed6\u4eec\uff0c\u795d\u5144\u4e5f\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u77e5\u9053\uff01\u5b89\u5144\u4e5f\u66fe\u63d0\u53ca\u8fc7\uff0c\u4f46\u4e0d\u77e5\u8fd9\u4e8b\u4e0e\u5929\u9e64\u4e03\u65a9\u4f55\u5173\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u5f53\u7136\u6709\u5173\uff0c\u90a3\u5b89\u5144\u592b\u5987\u56de\u5230\u4e1c\u6d77\uff0c\u7eb5\u7136\u5728\u6b66\u529f\u4e0a\u6709\u6240\u521b\u9020\u90a3\u4e5f\u4e0d\u53ef\u80fd\u5728\u77ed\u77ed\u65f6\u95f4\u4e4b\u5185\uff0c\u609f\u51fa\u8fd9\u7b49\u9ad8\u6df1\u7684\u6b66\u529f\uff0c\u662f\u4ee5\u8001\u673d\u8ba4\u4e3a\u4ed6\u4eec\u90a3\u53e5\u522b\u6709\u9047\u5408\uff0c\u5fc5\u662f\u6307\u7684\u9f99\u5927\u4fa0\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u60f3\u4e86\u4e00\u60f3\uff0c\u84e6\u5730\u5927\u7b11\u9053\uff1a\u201c\u662f\u554a\uff01\u5144\u5f1f\u4e3a\u4f55\u65e9\u5c31\u4e0d\u66fe\u60f3\u5230\u5462\uff1f\u6b64\u95f4\u9664\u4e86\u5251\u5723\u9f99\u5927\u4fa0\uff0c\u53c8\u6709\u8c01\u80fd\u609f\u51fa\u90a3\u7b49\u9ad8\u660e\u6b66\u529f\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u770b\u4e86\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u7b11\u9053\uff1a\u201c\u65b9\u5927\u54e5\uff0c\u8fd9\u4e8b\u662f\u771f\u7684\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u8fd9\u2026\u2026\u5927\u6982\u4e0d\u5047\uff01\u201d \u3000\u3000\u4ed6\u8fd9\u7b49\u56de\u7b54\uff0c\u522b\u4eba\u5012\u8fd8\u72b9\u53ef\uff0c\u5b89\u5c0f\u840d\u542c\u6765\uff0c\u5927\u5927\u4e0d\u611f\u5174\u8da3\uff0c\u54fc\u4e86\u54fc\u9053\uff1a\u201c\u4ec0\u4e48\u5927\u6982\u4e0d\u5927\u6982\uff0c\u65b9\u5927\u54e5\uff0c\u4f60\u5012\u8bf4\u660e\u4e00\u4e9b\u561b\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6c89\u541f\u9053\uff1a\u201c\u5148\u5e08\u66fe\u7ecf\u8bad\u793a\u8fc7\uff0c\u8981\u6211\u9047\u5230\u4e1c\u6d77\u6b66\u529f\uff0c\u5e94\u8be5\u7279\u522b\u5f53\u5fc3\uff0c\u5e08\u7236\u8a00\u4e0b\u4e4b\u610f\uff0c\u90a3\u662f\u8bf4\u7956\u5e08\u6614\u5e74\u53bb\u81f3\u4e1c\u6d77\u4ee5\u540e\uff0c\u66fe\u7ecf\u7559\u4e0b\u53e6\u5916\u7684\u4e00\u5957\u6b66\u5b66\uff0c\u7528\u6765\u5236\u9f99\u884c\u5251\u6cd5\uff0c\u4ee5\u514d\u4ee5\u540e\u51fa\u4e86\u4e0d\u8096\u4e4b\u5f92\uff0c\u5f15\u8d77\u6c5f\u6e56\u6740\u52ab\uff01\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\uff0c\u5929\u9e64\u4e03\u65a9\u5f53\u771f\u4e5f\u51fa\u81ea\u65b9\u5927\u54e5\u7956\u5e08\u6240\u6388\u7684\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u6b63\u662f\u5982\u6b64\uff01\u201d\u5b89\u5c0f\u840d\u5ffd\u7136\u770b\u4e86\u4ed6\u4e8c\u773c\uff0c\u7b11\u9053\uff1a\u201c\u65b9\u5927\u54e5\uff0c\u5982\u679c\u4f60\u5f53\u771f\u505a\u4e86\u4ec0\u4e48\u9519\u4e8b\uff0c\u6211\u4e5f\u4e0d\u4f1a\u7528\u5929\u9e64\u4e03\u65a9\u4f24\u4f60\u7684\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5446\u4e86\u4e00\u5446\uff0c\u5fc3\u4e2d\u4e0a\u9635\u6fc0\u52a8\uff0c\u4f46\u4ed6\u5374\u662f\u4e0d\u66fe\u8bf4\u51fa\u4ec0\u4e48\uff0c\u53ea\u56e0\u6b64\u65f6\u6b64\u5730\uff0c\u4ed6\u7eb5\u662f\u60f3\u8bf4\uff0c\u53c8\u600e\u4e48\u63aa\u8bcd\uff1f \u3000\u3000\u8fd9\u65f6\u8a00\u51e4\u537f\u5ffd\u7136\u63e1\u62f3\u9053\uff1a\u201c\u8f9b\u8001\u524d\u8f88\uff0c\u7167\u4f60\u8001\u8fd9\u4e48\u89e3\u8bf4\uff0c\u6211\u8fb0\u5dde\u8a00\u5bb6\u6b66\u529f\uff0c\u83ab\u975e\u4e0e\u5251\u5723\u4e5f\u6709\u6e0a\u6e90\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u5f53\u7136\u6709\uff01\u4f60\u7237\u7237\u7684\u6b66\u529f\uff0c\u6709\u4e00\u534a\u662f\u5f97\u81ea\u5251\u5723\u7684\u6307\u70b9\uff0c\u5426\u5219\uff0c\u6c5f\u6e56\u4e0a\u54ea\u91cc\u8fd8\u6709\u8a00\u5bb6\u7acb\u8db3\u4e4b\u5730\uff1f\u201d \u3000\u3000\u8a00\u51e4\u537f\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff01\u201d \u3000\u3000\u4ed6\u8fd9\u624d\u660e\u767d\uff0c\u8f9b\u67cf\u516c\u8981\u5b89\u5c0f\u840d\u52a9\u4ed6\u7684\u7528\u610f\u4f55\u5728\u4e86\uff01\u5f53\u4e0b\u8bed\u97f3\u4e00\u987f\uff0c\u8d77\u8eab\u5411\u8f9b\u67cf\u516c\u4e00\u63d6\u3002 \u3000\u3000\u63a5\u9053\uff1a\u201c\u8001\u524d\u8f88\u6307\u70b9\u8ff7\u6d25\uff0c\u665a\u8f88\u7ec8\u751f\u4e0d\u6562\u7a0d\u5fd8\u2026\u2026\u201d \u3000\u3000\u8f9b\u67cf\u516c\u51b7\u51b7\u5730\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4e0d\u7528\u4e86\uff0c\u8001\u592b\u751a\u671b\u9601\u4e0b\u80fd\u4e0d\u5fd8\u7956\u8bad\uff0c\u597d\u597d\u5149\u5927\u8fb0\u5dde\u8a00\u5bb6\u95e8\u98ce\uff01\u201d\u51e4\u537f\u609a\u7136\u52a8\u5bb9\uff0c\u8fde\u58f0\u79f0\u662f\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5165\u4e91\u5927\u5e08\u5df2\u7136\u8981\u5bfa\u4e2d\u81b3\u623f\u9001\u4e0a\u9152\u83dc\uff0c\u5c31\u5728\u4f17\u4eba\u9152\u8fc7\u4e09\u5de1\u4e4b\u9645\uff0c\u90a3\u5949\u547d\u524d\u5f80\u6d1b\u9633\u7684\u4e10\u5e2e\u957f\u8001\u5409\u5a01\uff0c\u5174\u51b2\u51b2\u5730\u56de\u8f6c\u4e86\u5c11\u6797\u3002 \u3000\u3000\u968f\u5728\u4ed6\u8eab\u540e\uff0c\u53e6\u6709\u4e00\u4eba\uff0c\u6b64\u4eba\u6ee1\u8138\u98ce\u5c18\u4e4b\u8272\uff0c\u4f3c\u662f\u7ecf\u8fc7\u4e00\u9635\u957f\u9014\u5954\u6ce2\u3002 \u3000\u3000\u970d\u9e23\u98ce\u4e00\u773c\u77a7\u89c1\u5409\u5a01\u8eab\u540e\u4e4b\u4eba\uff0c\u8138\u4e0a\u5ffd\u9732\u7b11\u5bb9\uff0c\u5927\u58f0\u9053\uff1a\u201c\u53f6\u5802\u4e3b\u600e\u5730\u4e5f\u6765\u4e86\uff1f\u201d \u3000\u3000\u6562\u60c5\u8fd9\u4f4d\u624b\u6301\u4e03\u5c3a\u94c1\u6756\u7684\u7070\u53d1\u8001\u4eba\uff0c\u7adf\u662f\u90a3\u4e10\u5e2e\u91d1\u9675\u516c\u5802\u7684\u5802\u4e3b\u201c\u6c5f\u5357\u795e\u6756\u201d\u53f6\u4e16\u8c6a\u3002 \u3000\u3000\u53f6\u4e16\u8c6a\u5927\u6b65\u4e0a\u524d\uff0c\u5411\u970d\u9e23\u98ce\u4e00\u63d6\u9053\uff1a\u201c\u8001\u673d\u53e9\u89c1\u5e2e\u4e3b\u2026\u2026\u201d \u3000\u3000\u4ed6\u5e74\u7eaa\u6bd4\u90a3\u970d\u9e23\u98ce\u4f3c\u662f\u5927\u4e86\u5341\u5c81\uff0c\u77a7\u4ed6\u8fd9\u7b49\u79f0\u547c\uff0c\u73ed\u8f88\u4e0a\u4f3c\u4e4e\u8fd8\u5728\u970d\u9e23\u98ce\u4e4b\u4e0b\u3002 \u3000\u3000\u970d\u9e23\u98ce\u6b20\u8eab\u8fd8\u4e86\u534a\u793c\uff0c\u7b11\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u5feb\u5feb\u89c1\u8fc7\u8fd9\u51e0\u4f4d\u9ad8\u4eba\uff01\u201d \u3000\u3000\u53f6\u4e16\u8c6a\uff0c\u5409\u5a01\u548c\u4ed6\u4eec\u90fd\u66fe\u6709\u8fc7\u4e00\u9762\u4e4b\u96c5\uff0c\u662f\u4ee5\u4e5f\u4e0d\u987b\u591a\u591a\u5bd2\u55a7\u3002\u4f46\u5f53\u53f6\u4e16\u8c6a\u76ee\u5149\u770b\u5230\u65b9\u96ea\u5b9c\u8eab\u4e0a\u4e4b\u65f6\uff0c\u4e0d\u7531\u5f97\u6014\u4e86\u4e00\u6014\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u4ed6\u83ab\u975e\u4fbf\u662f\u90a3\u745c\u513f\uff1f\u201d \u3000\u3000\u8f6c\u5ff5\u4e4b\u95f4\uff0c\u7981\u4e0d\u4f4f\u4fbf\u5411\u65b9\u96ea\u5b9c\u8d70\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5728\u542c\u4ed6\u4eec\u4e92\u9053\u5bd2\u55a7\u4e4b\u65f6\uff0c\u4fbf\u4e5f\u77e5\u6653\u773c\u524d\u8fd9\u4f4d\u624b\u6301\u94c1\u6756\u7684\u8001\u4eba\uff0c\u5c31\u662f\u81ea\u5df1\u7684\u5a18\u8205\uff0c\u8fd9\u65f6\u89c1\u4ed6\u5411\u81ea\u5df1\u8d70\u4e86\u8fc7\u6765\uff0c\u4fbf\u8fde\u5fd9\u8d77\u8eab\uff0c\u4e0a\u524d\u62dc\u5012\uff0c\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u7525\u513f\u96ea\u5b9c\uff0c\u53e9\u89c1\u8205\u8205\u2026\u2026\u201d \u3000\u3000\u53f6\u4e16\u8c6a\u53cc\u76ee\u795e\u5149\u7535\u5c04\uff0c\u5475\u5475\u4e00\u7b11\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u679c\u7136\u662f\u4f60\u554a\uff01\u957f\u5f97\u8fd9\u4e48\u9ad8\u4e86\uff0c\u771f\u662f\u96be\u5f97\uff0c\u96be\u5f97\u2026\u2026\u201d\u7b11\u58f0\u672a\u5df2\uff0c\u53cc\u76ee\u4e4b\u4e2d\u7adf\u662f\u9690\u9690\u73b0\u51fa\u6cea\u5149\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u62dc\u4e86\u4e09\u62dc\uff0c\u65b9\u59cb\u8d77\u8eab\u3002 \u3000\u3000\u5165\u4e91\u5927\u5e08\u8bf7\u90a3\u4e24\u4eba\u5165\u5ea7\uff0c\u53f6\u4e16\u8c6a\u81ea\u662f\u5750\u5728\u65b9\u96ea\u5b9c\u7684\u8eab\u8fb9\uff0c\u5409\u5a01\u5374\u662f\u6328\u9053\u970d\u9e23\u98ce\u5750\u4e0b\uff0c\u970d\u9e23\u98ce\u770b\u4e86\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u90a3\u6d1b\u9633\u57ce\u4e2d\uff0c\u53ef\u66fe\u6253\u542c\u51fa\u4ec0\u4e48\u5934\u7eea\uff1f\u201d \u3000\u3000\u5409\u5a01\u9053\uff1a\u201c\u5c0f\u5f1f\u5df2\u7136\u67e5\u51fa\u4e00\u4e9b\u7709\u76ee\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u5fcd\u4e0d\u4f4f\u63a5\u9053\uff1a\u201c\u5409\u8001\u67e5\u51fa\u77f3\u4fca\u7684\u4e0b\u843d\u4e86\uff1f\u201d \u3000\u3000\u5409\u5a01\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5409\u67d0\u5e78\u4e0d\u8fb1\u547d\uff0c\u5df2\u5c06\u77f3\u4e09\u4fa0\u548c\u65b9\u592b\u4eba\u7684\u4e0b\u843d\u627e\u5230\u4e86\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u4ed6\u4eec\u73b0\u5728\u4f55\u5904\uff1f\u201d \u3000\u3000\u5409\u5a01\u9053\uff1a\u201c\u4e94\u5723\u6559\u7684\u603b\u575b\u4e4b\u4e2d\u3002\u201d \u3000\u3000\u4ed6\u6b64\u8a00\u4e00\u51fa\uff0c\u4f17\u4eba\u90fd\u4f3c\u4e0d\u66fe\u89c9\u5f97\u610f\u5916\u3002 \u3000\u3000\u5409\u5a01\u63a5\u9053\uff1a\u201c\u90a3\u65b9\u5929\u6210\u4e5f\u5728\u8be5\u5904\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u90d1\u5927\u521a\u6025\u95ee\u9053\uff1a\u201c\u4e0d\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5409\u5a01\u9053\uff1a\u201c\u4e0d\u8fc7\u65b9\u5929\u6210\u4e0e\u4ed6\u4eec\u7684\u5730\u4f4d\u4e0d\u540c\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e5f\u9053\uff1a\u201c\u54ea\u91cc\u4e0d\u540c\uff1f\u201d \u3000\u3000\u5409\u5a01\u9053\uff1a\u201c\u77f3\u4e09\u4fa0\u548c\u65b9\u592b\u4eba\u4e43\u662f\u9636\u4e0b\u4e4b\u56da\uff0c\u65b9\u5929\u6210\u5374\u662f\u5ea7\u4e0a\u4f73\u5bbe\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u8fd9\u662f\u771f\u7684\u5417\uff1f\u201d \u3000\u3000\u5409\u5a01\u9053\uff1a\u201c\u5c0f\u5f1f\u600e\u6562\u6b3a\u9a97\u5e2e\u4e3b\uff1f\u6211\u4e2d\u5dde\u5206\u5802\u5df2\u6d3e\u6709\u5341\u540d\u5b50\u5f1f\u6295\u8eab\u4e94\u9b54\u624b\u4e0b\u4efb\u804c\uff0c\u4ed6\u4eec\u4eb2\u773c\u6240\u89c1\uff0c\u81ea\u7136\u4e0d\u5047\u3002\u201d \u3000\u3000\u970d\u545c\u98ce\u70b9\u4e86\u70b9\u5934\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u5012\u662f\u771f\u7684\u4e86\uff01\u201d \u3000\u3000\u5409\u5a01\u9053\uff1a\u201c\u5c0f\u5f1f\u5341\u5206\u60ed\u6127\uff0c\u90a3\u65b9\u5929\u6210\u7a76\u7adf\u662f\u5426\u4ed6\u672c\u4eba\uff0c\u8fc4\u4eca\u65e0\u6cd5\u67e5\u51fa\u3002\u201d \u3000\u3000\u90d1\u5927\u521a\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e0d\u6d88\u67e5\u4e86\uff0c\u90d1\u67d0\u5df2\u7136\u76f8\u4fe1\uff0c\u4ed6\u51b3\u4e0d\u662f\u65b9\u4e8c\u5f1f\u672c\u4eba\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u90d1\u5144\u8ba4\u4e3a\u4ed6\u662f\u8c01\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u8fd9\u2026\u2026\u5144\u5f1f\u731c\u4ed6\u53ef\u80fd\u662f\u90a3\u2018\u5316\u5f71\u4eba\u9b54\u2019\u82cf\u5c0f\u5761\uff01\u201d \u3000\u3000\u5b8b\u5b5a\u95fb\u8a00\u5446\u4e86\u4e00\u5446\u9053\uff1a\u201c\u82cf\u5c0f\u5761\uff1f\u4ed6\u2026\u2026\u901f\u4e0d\u662f\u5341\u4e03\u5e74\u524d\u5728\u6cf0\u5c71\u88ab\u5251\u795e\u9648\u5144\u5251\u5288\u5357\u5929\u95e8\u5c71\u9053\u4e4b\u4e2d\u4e86\u5417\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u9053\uff1a\u201c\u4f20\u95fb\u679c\u662f\u5982\u6b64\uff0c\u53ea\u662f\u65e0\u4eba\u89c1\u8fc7\u4ed6\u7684\u5c38\u4f53\uff0c\u800c\u4e14\uff0c\u8fde\u9648\u5927\u4fa0\u81ea\u5df1\u4e5f\u4e0d\u80af\u627f\u8ba4\u6b64\u4e8b\uff0c\u662f\u4ee5\uff0c\u5144\u5f1f\u624d\u4f1a\u8054\u60f3\u5230\u53ef\u80fd\u662f\u4ed6\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u76b1\u7709\uff1a\u201c\u5927\u4f2f\uff0c\u8fd9\u82cf\u5c0f\u5761\u662f\u600e\u6837\u7684\u4eba\uff1f\u201d \u3000\u3000\u90d1\u5927\u521a\u53cc\u76ee\u5bd2\u5c04\uff0c\u9053\uff1a\u201c\u6deb\u6076\u4e4b\u5f92\uff0c\u4eba\u4eba\u5f97\u800c\u8bdb\u4e4b\uff01\u201c\u795d\u516c\u660e\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u8bc4\u8bed\u751a\u5408\u543e\u5fc3\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u5374\u662f\u51b7\u7b11\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u795d\u8001\uff0c\u90a3\u82cf\u5c0f\u5761\u7684\u6b66\u529f\u4f60\u89c1\u8bc6\u8fc7\u5417\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u88ab\u4ed6\u95ee\u5f97\u4e00\u6014\u9053\uff1a\u201c\u6ca1\u6709\u554a\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u90d1\u5927\u521a\u8fd9\u53e5\u4eba\u4eba\u5f97\u800c\u8bdb\u4e4b\uff0c\u672c\u662f\u4e0d\u9519\uff0c\u53ea\u662f\uff0c\u5c31\u8001\u592b\u6240\u77e5\uff0c\u90a3\u82cf\u5c0f\u5761\u4e00\u8eab\u6b66\u529f\u4e4b\u9ad8\uff0c\u53ea\u6015\u4f11\u8bf4\u4eba\u4eba\u65e0\u6cd5\u8bdb\u4e4b\uff0c\u5373\u4ee4\u4f60\u8001\u54e5\u5b50\u4eb2\u81ea\u51fa\u624b\uff0c\u4e5f\u4e0d\u80fd\u53d6\u4e86\u4ed6\u7684\u6027\u547d\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u53cc\u80a9\u8038\u52a8\uff0c\u5fc3\u4e2d\u4f3c\u662f\u751a\u4e3a\u4e0d\u670d\uff0c\u5927\u7b11\u9053\uff1a\u201c\u5144\u5f1f\u4e0d\u4fe1\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u90a3\u5bb9\u6613\uff0c\u54b1\u4eec\u524d\u53bb\u8d74\u4f1a\u4e4b\u65f6\uff0c\u53ea\u8981\u90a3\u82cf\u5c0f\u5761\u4e5f\u5728\uff0c\u628a\u4ed6\u4ea4\u4f60\u4fbf\u662f\uff01\u201d \u3000\u3000\u970d\u9e23\u98ce\u5ffd\u5730\u7b11\u9053\uff1a\u201c\u8f9b\u8001\uff0c\u5018\u662f\u90a3\u65b9\u5929\u6210\u4e0d\u662f\u5316\u5f71\u4eba\u9b54\u82cf\u5c0f\u5761\u5462\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u5982\u679c\u4e0d\u662f\uff0c\u81ea\u7136\u662f\u4e0d\u987b\u795d\u5144\u52a8\u624b\u7684\u4e86\uff01\u201d \u3000\u3000\u90d1\u5927\u521a\u5374\u662f\u4e00\u53f9\u9053\uff1a\u201c\u5fc5\u7136\u662f\u4ed6\u7684\u4e86\uff01\u56e0\u4e3a\u2026\u2026\u82cf\u5c0f\u5761\u4e43\u662f\u65b9\u592b\u4eba\u7684\u59fb\u8868\u4e4b\u4eb2\uff0c\u65b9\u592b\u4eba\u5ac1\u4e0e\u5929\u6210\u4e8c\u5f1f\u4e4b\u524d\uff0c\u82cf\u5c0f\u5761\u5c31\u66fe\u6258\u4eba\u8bf4\u8fc7\u591a\u6b21\u4eb2\u554a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8a00\uff0c\u5446\u4e86\u534a\u664c\u3002\u4ed6\u8fc5\u5feb\u5730\u60f3\u5230\u5609\u5b9a\u57ce\u5916\u6797\u4e2d\u7684\u90a3\u4e00\u5e55\u5f80\u4e8b\uff0c\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u770b\u6765\uff0c\u8fd9\u82cf\u5c0f\u5761\u516b\u6210\u662f\u6740\u4e86\u6211\u7239\u7239\u7684\u51f6\u624b\u2026\u2026\u201d \u3000\u3000\u7b49\u5f85\u4e2d\u5cb3\u5e99\u4e94\u5723\u6559\u5927\u4f1a\u7684\u8fd9\u4e00\u6bb5\u65e5\u5b50\uff0c\u5c11\u6797\u5bfa\u4e2d\uff0c\u5374\u662f\u5341\u5206\u7684\u5e73\u9759\uff0c\u5929\u9b54\u5973\u4e5f\u672a\u518d\u6709\u4ec0\u4e48\u9a9a\u6270\u7684\u4e3e\u52a8\u3002 \u3000\u3000\u8a00\u51e4\u537f\u5374\u662f\u5728\u8fd9\u4e00\u6bb5\u65f6\u5149\u4e4b\u4e2d\uff0c\u968f\u7740\u5b89\u5c0f\u840d\u548c\u795d\u516c\u660e\u52e4\u7ec3\u62f3\u638c\u529f\u592b\uff0c\u65b9\u96ea\u5b9c\u4e5f\u4e0d\u65f6\u5728\u65c1\u534f\u52a9\u3002 \u3000\u3000\u8f9b\u67cf\u516c\u548c\u795d\u516c\u660e\uff0c\u9664\u4e86\u996e\u9152\u4e0b\u68cb\u4fbf\u662f\u8fde\u8882\u51fa\u5916\u6e38\u800d\uff0c\u81f3\u4e8e\u53bb\u4e86\u54ea\u91cc\uff0c\u5219\u65e0\u4eba\u77e5\u6653\u3002 \u3000\u3000\u8fd9\u4e00\u6bb5\u65f6\u5149\uff0c\u4e0d\u4f46\u5d69\u5c71\u65e0\u4e8b\uff0c\u751a\u81f3\u8fde\u6574\u4e2a\u7684\u6b66\u6797\uff0c\u4e5f\u5341\u5206\u5e73\u9759\uff0c\u53ea\u6709\u4e00\u6869\u4e8b\u56f0\u6270\u4e86\u5c11\u6797\u50e7\u4fa3\uff0c\u90a3\u5c31\u662f\u8386\u7530\u4e0b\u9662\uff0c\u786e\u5df2\u8bc1\u5b9e\u843d\u5165\u4e86\u4e94\u5723\u6559\u624b\u4e2d\u3002 \u3000\u3000\u4e94\u5723\u6559\u53ec\u5f00\u7684\u6b66\u6797\u5927\u4f1a\u524d\u4e00\u5929\uff0c\u5c11\u6797\u7b2c\u4e00\u9ad8\u624b\u5165\u51e1\u5927\u5e08\uff0c\u4e5f\u5306\u5306\u5730\u4e91\u6e38\u5f52\u6765\uff0c\u968f\u4ed6\u540c\u6765\u7684\u4eba\uff0c\u8fd8\u6709\u6b66\u5f53\u767d\u62c2\u9053\u957f\uff0c\u4ee5\u53ca\u51e0\u4f4d\u6b66\u5b66\u5927\u5e08\u3002 \u3000\u3000\u6b66\u6797\u4e5d\u5927\u95e8\u6d3e\uff0c\u51e0\u4e4e\u5168\u90fd\u6709\u4eba\u8d76\u6765\u5d69\u5c71\uff0c\u7a0d\u6709\u5934\u8138\u7684\u4eba\u7269\uff0c\u5c11\u6797\u5168\u90e8\u5c06\u4ed6\u4eec\u63a5\u5f85\u5bfa\u4e2d\u3002 \u3000\u3000\u5c11\u6797\u5bfa\u5185\uff0c\u4e00\u65f6\u663e\u5f97\u70ed\u95f9\u975e\u51e1\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u4e5f\u8d81\u7740\u8fd9\u4e00\u6bb5\u65f6\u5149\uff0c\u4ece\u4ed6\u8205\u52c7\u53f6\u4e16\u8c6a\u7684\u53e3\u4e2d\uff0c\u77e5\u6089\u4e86\u6bcd\u4eb2\u7684\u666f\u51b5\uff0c\u4ed6\u8ba1\u5212\u7740\u9664\u53bb\u4e86\u4e94\u9b54\uff0c\u6740\u5374\u4e86\u82cf\u5c0f\u5761\u540e\uff0c\u4fbf\u5c06\u6bcd\u4eb2\u63a5\u56de\u4e2d\u5dde\u65b9\u5bb6\u5e84\u5c45\u4f4f\uff0c\u539f\u6765\u53f6\u4e16\u8c6a\u544a\u8bc9\u4ed6\uff0c\u4ed6\u6bcd\u4eb2\u53f6\u5b9c\u82b3\u9690\u8eab\u7a7a\u95e8\uff0c\u4e43\u662f\u8eb2\u907f\u82cf\u5c0f\u5761\u7684\u8ffd\u6740\u3002 \u3000\u3000\u8fd9\u4e00\u592d\u7684\u665a\u4e0a\uff0c\u5c11\u6797\u638c\u95e8\u5728\u5927\u6bbf\u4e2d\u6446\u4e86\u6570\u5341\u684c\u9152\u7b75\uff0c\u6b22\u5bb4\u660e\u65e5\u5f80\u4e2d\u5cb3\u5e99\u5366\u4f1a\u7684\u5404\u6d3e\u9ad8\u624b\uff0c\u8fd9\u4e00\u9910\u5bb4\uff0c\u4e0d\u557b\u662f\u4e00\u6b21\u996e\u8840\u76df\u8a93\uff0c\u4e0e\u5bb4\u7684\u9ad8\u624b\uff0c\u4e2a\u4e2a\u90fd\u6709\u7740\u540c\u4ec7\u654c\u5ffe\u4e4b\u5fc3\uff0c\u770b\u4e0d\u51fa\u6709\u8c01\u7518\u5fc3\u670d\u5728\u4e94\u9b54\u624b\u4e0b\u3002 \u3000\u3000\u8fd9\u5bf9\u65b9\u96ea\u5b9c\u800c\u8a00\uff0c\u786e\u662f\u4e00\u9897\u5b9a\u5fc3\u4e38\uff0c\u4ed6\u4e00\u76f4\u803d\u5fc3\uff0c\u5018\u662f\u6b66\u6797\u4e5d\u5927\u95e8\u6d3e\u7684\u957f\u8001\u548c\u638c\u95e8\u4eba\u81ea\u5df1\u5148\u4e71\u4e86\u6b65\u6cd5\uff0c\u81ea\u5df1\u7684\u964d\u9b54\u4e4b\u5fc3\uff0c\u5fc5\u5c06\u53d7\u5230\u8bb8\u591a\u4e0d\u5fc5\u8981\u7684\u56f0\u6270\uff01\u7ecf\u6b64\u4e00\u5bb4\uff0c\u65b9\u96ea\u5b9c\u5c11\u53bb\u4e86\u8fd9\u4e00\u6869\u987e\u8651\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u665a\uff0c\u4ed6\u7761\u5f97\u6781\u597d\uff0c\u8fd9\u4e5f\u662f\u4ed6\u5fc3\u6027\u8fc7\u4eba\u4e4b\u5904\uff0c\u8d8a\u662f\u9047\u5f97\u5927\u4e8b\u8d8a\u53d1\u663e\u5f97\u9547\u5b9a\uff01\u5373\u4ee4\u4ed6\u8fd8\u662f\u803f\u803f\u96be\u5fd8\u5e08\u7236\u88ab\u4eba\u6697\u7b97\u4e4b\u4e8b\uff0c\u4f46\u4ed6\u6b64\u523b\u5374\u5df2\u65ad\u5b9a\uff0c\u5e08\u7236\u6240\u4e2d\u4e4b\u6bd2\uff0c\u5fc5\u662f\u4e94\u9b54\u4e2d\u4e00\u4eba\u6240\u4e3a\u3002 \u3000\u3000\u56e0\u800c\uff0c\u4ed6\u628a\u4e00\u5207\u90fd\u6263\u5411\u4e94\u9b54\u5934\u4e0a\uff0c\u653e\u5fc3\u5730\u7761\u4e86\u4e00\u89c9\u3002 \u3000\u3000\u51cc\u6668\u5bc5\u65f6\u752b\u5230\uff0c\u65b9\u96ea\u5b9c\u5ffd\u7136\u81ea\u68a6\u4e2d\u60ca\u9192\uff0c\u534a\u660e\u534a\u6697\u7684\u5929\u8272\u4e2d\uff0c\u4ed6\u8d6b\u7136\u53d1\u73b0\uff0c\u81ea\u5df1\u7684\u4e91\u5e8a\u4e4b\u524d\uff0c\u7ad9\u4e86\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u770b\u770b\u5c1a\u5728\u53e6\u5904\u4e24\u5f20\u4e91\u5e8a\u4e0a\u6253\u5750\u7684\u8f9b\u67cf\u516c\u3001\u795d\u516c\u660e\u3001\u5b8b\u5b5a\u548c\u90d1\u5927\u521a\u56db\u4eba\u4e00\u773c\uff0c\u9661\u7136\u5927\u5927\u5730\u5403\u4e86\u4e00\u60ca\uff0c\u663e\u7136\uff0c\u8fd9\u4eba\u8fdb\u5f97\u623f\u6765\uff0c\u7adf\u7136\u8fde\u8f9b\u67cf\u516c\u4e5f\u672a\u53d1\u89c9\u3002 \u3000\u3000\u65b9\u96ea\u5b98\u4e00\u60ca\u4e00\u5413\uff0c\u6b63\u5f85\u5f20\u53e3\u559d\u95ee\uff0c\u53ea\u89c1\u90a3\u4eba\u4f4e\u58f0\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u662f\u6211\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u4e00\u542c\uff0c\u5ffd\u7136\u8df3\u4e0b\u4e91\u5e8a\uff0c\u7eb3\u5934\u4fbf\u62dc\uff0c\u53e3\u4e2d\u9ad8\u53eb\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u4f60\u8001\u4eba\u5bb6\u4e5f\u6765\u4e86\u5417\uff1f\u201d \u3000\u3000\u6562\u60c5\u8fd9\u4eba\u4e43\u662f\u5251\u795e\u7684\u5e08\u59b9\uff0c\u91d1\u9876\u795e\u5c3c\u3002 \u3000\u3000\u795e\u5c3c\u4f38\u624b\u62c9\u8d77\u65b9\u96ea\u5b9c\uff0c\u7b11\u9053\uff1a\u201c\u4eca\u65e5\u4e43\u662f\u4e94\u9b54\u4e3e\u884c\u6b66\u6797\u5927\u4f1a\u4e4b\u671f\uff0c\u6211\u81ea\u7136\u662f\u8981\u8d76\u6765\u7684\u4e86\uff01\u201d \u3000\u3000\u4e24\u4eba\u8fd9\u4e00\u8bf4\u8bdd\uff0c\u5ba4\u5185\u7684\u56db\u4eba\uff0c\u4e5f\u540c\u65f6\u60ca\u89c9\u3002 \u3000\u3000\u8f9b\u67cf\u516c\u76ee\u5149\u4e00\u8f6c\uff0c\u7a81\u7136\u5927\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u6797\u59d1\u5a18\u5417\uff1f\u4e45\u8fdd\u4e86\uff01\u201d \u3000\u3000\u8fd9\u91d1\u9876\u795e\u5c3c\uff0c\u4fd7\u5bb6\u59d3\u6797\uff0c\u540d\u53eb\u82b7\u4ed9\uff0c\u8f9b\u67cf\u516c\u4e0e\u5979\u76f8\u8bc6\uff0c\u4e43\u662f\u5728\u51fa\u5bb6\u4e4b\u524d\uff0c\u65e0\u602a\u4ed6\u4ecd\u7136\u79f0\u4ed6\u6797\u59d1\u5a18\u4e86\u3002 \u3000\u3000\u91d1\u9876\u795e\u5c3c\u95fb\u8a00\uff0c\u5374\u662f\u5fae\u559f\u9053\uff1a\u201c\u8001\u65bd\u4e3b\uff0c\u8d2b\u5c3c\u5df2\u662f\u65b9\u5916\u4e4b\u4eba\uff0c\u8001\u65bd\u4e3b\u53eb\u6211\u4e00\u58f0\u91ca\u95e8\u5f1f\u5b50\u5fc3\u5982\u8db3\u77e3\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u5ffd\u7136\u5931\u58f0\u9053\uff1a\u201c\u600e\u4e48\uff01\u91d1\u9876\u795e\u5c3c\u5fc3\u5982\u5927\u5e08\u539f\u6765\u662f\u6797\u2026\u2026\u201d\u4ed6\u53e3\u4e2d\u59d1\u5a18\u4e8c\u5b57\uff0c\u5374\u662f\u53ca\u65f6\u5fcd\u4e86\u56de\u53bb\uff0c\u6539\u53e3\u9053\uff1a\u201c\u771f\u662f\u60f3\u4e0d\u5230\u5f97\u5f88\u554a\uff1f\u201d \u3000\u3000\u795e\u5c3c\u5408\u5341\u9053\uff1a\u201c\u8001\u65bd\u4e3b\u597d\u8bf4\uff01\u8d2b\u5c3c\u9041\u8ff9\u7a7a\u95e8\uff0c\u672c\u662f\u56fe\u4e2a\u731c\u9759\uff0c\u4f46\u4ecd\u7136\u60f9\u5f97\u6b66\u6797\u4e2d\u670b\u53cb\u52a0\u4e0a\u5982\u8bb8\u4e4b\u591a\u7684\u79f0\u8c13\uff0c\u771f\u662f\u5927\u8fdd\u8d2b\u5c3c\u521d\u8877\uff0c\u6709\u6127\u4f5b\u7956\u7684\u718f\u9676\u4e86\u2026\u2026\u201d \u3000\u3000\u8f9b\u67cf\u516c\u7b11\u9053\uff1a\u201c\u5e08\u592a\u8d76\u6765\uff0c\u53ef\u662f\u4e3a\u4e86\u4e94\u5723\u6559\u7684\u90a3\u4e2a\u6b66\u6797\u5927\u4f1a\u5417\uff1f\u201d \u3000\u3000\u795e\u5c3c\u957f\u957f\u4e00\u53f9\uff1a\u201c\u820d\u6b64\u4e4b\u5916\uff0c\u8d2b\u5c3c\u8fd8\u6709\u4e00\u4e8b\u8981\u529e\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u795e\u5c3c\u9053\uff1a\u201c\u66ff\u8d2b\u5c3c\u5e08\u5144\u62a5\u4ec7\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u53ef\u662f\u66ff\u9648\u5e0c\u6b63\u62a5\u4ec7\u5417\uff1f\u201d \u3000\u3000\u795e\u5c3c\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u9648\u8001\u5f1f\u662f\u6b7b\u5728\u4ec0\u4e48\u4eba\u624b\u4e2d\uff1f\u201d \u3000\u3000\u795e\u5c3c\u9053\uff1a\u201c\u8bf4\u6765\u53ea\u6015\u5404\u4f4d\u4e0d\u4fe1\uff0c\u8d2b\u5c3c\u5e08\u5144\uff0c\u4e43\u88ab\u90a3\u5c11\u6797\u7b2c\u4e00\u9ad8\u624b\u5728\u5165\u51e1\u5927\u5e08\u6240\u6697\u7b97\uff01\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u53ea\u628a\u8f9b\u3001\u795d\u4e8c\u8001\u542c\u5f97\u8df3\u4e86\u8d77\u6765\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u66f4\u662f\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u8fd9\u662f\u771f\u7684\u5417\uff1f\u201d \u3000\u3000\u795e\u5c3c\u9053\uff1a\u201c\u5f53\u5e74\u6211\u4e0e\u4f60\u5e08\u7236\u867d\u7136\u4e3a\u4e86\u4e00\u70b9\u5c0f\u4e8b\uff0c\u6709\u4e86\u8bef\u4f1a\uff0c\u4f46\u5206\u624b\u4e4b\u65f6\uff0c\u611a\u53d4\u66fe\u7ecf\u9001\u4e86\u4ed6\u4e00\u6869\u7269\u4e8b\uff0c\u4f46\u4f60\u5e08\u7236\u6b7b\u540e\uff0c\u6211\u5374\u672a\u53d1\u73b0\u90a3\u4ef6\u4e1c\u897f\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4ec0\u4e48\u4e1c\u897f\u554a\uff1f\u201d \u3000\u3000\u795e\u5c3c\u9053\uff1a\u201c\u4e00\u652f\u5315\u9996\u3002\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u6447\u5934\u9053\uff1a\u201c\u81ea\u4ece\u5f1f\u5b50\u968f\u5e08\u4ee5\u540e\uff0c\u4f3c\u4e4e\u4ece\u672a\u89c1\u8fc7\u5e08\u7236\u6709\u8fc7\u5315\u9996\u5728\u8eab\u554a\uff01\u201d \u3000\u3000\u795e\u5c3c\u957f\u53f9\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u65e9\u5728\u4e2d\u4eba\u6697\u7b97\u4e4b\u65f6\uff0c\u4fbf\u5df2\u5931\u53bb\uff0c\u4f60\u81ea\u662f\u4e0d\u66fe\u89c1\u5230\u7684\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u63d2\u53e3\u9053\uff1a\u201c\u5e08\u592a\uff0c\u90a3\u652f\u5315\u9996\uff0c\u53ef\u662f\u5341\u5206\u73cd\u8d35\uff1f\u201d \u3000\u3000\u795e\u5c3c\u9eef\u7136\u9053\uff1a\u201c\u8001\u65bd\u4e3b\uff0c\u6e38\u9f99\u5251\u4ee4\uff0c\u4f60\u60f3\u5fc5\u542c\u8bf4\u8fc7\u7684\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u90a3\u5315\u9996\u662f\u90a3\u6e38\u9f99\u5251\u4ee4\uff1f\u201d \u3000\u3000\u795e\u5c3c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5426\u5219\u6211\u4e5f\u4e0d\u4f1a\u9001\u7ed9\u5e08\u5144\u4e86\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u8001\u673d\u53ea\u8bf4\u90a3\u6e38\u9f99\u5251\u4ee4\u5df2\u88ab\u5723\u9f99\u5927\u4fa0\u6bc1\u53bb\u4e86\uff0c\u600e\u7684\u53c8\u5230\u4e86\u5e08\u592a\u624b\u4e2d\u3002\u201d \u3000\u3000\u795e\u5c3c\u9053\uff1a\u201c\u5148\u5e08\u5e76\u672a\u5c06\u5251\u4ee4\u6bc1\u53bb\uff0c\u53ea\u662f\u5c06\u5b83\u4f20\u7ed9\u4e86\u8d2b\u5c3c\u800c\u5df2\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u90a3\u5251\u4ee4\u662f\u843d\u5165\u51e1\u624b\u4e2d\u4e86\uff1f\u201d \u3000\u3000\u795e\u5c3c\u9053\uff1a\u201c\u82e5\u975e\u8d2b\u5c3c\u53d1\u73b0\u4ed6\u6301\u6709\u5251\u4ee4\uff0c\u8d2b\u5c3c\u4e5f\u4e0d\u4f1a\u6307\u8ba4\u4ed6\u662f\u6697\u7b97\u5251\u795e\u4e4b\u4eba\u4e86\u3002\u201d \u3000\u3000\u8f9b\u67cf\u516c\u76b1\u7709\u9053\uff1a\u201c\u6e38\u9f99\u5251\u4ee4\u672a\u6bc1\uff0c\u5f53\u771f\u662f\u5927\u51fa\u8001\u592b\u7684\u610f\u5916\u3002\u201d \u3000\u3000\u4f59\u97f3\u672a\u5df2\uff0c\u795d\u516c\u660e\u5df2\u7136\u5927\u58f0\u9053\uff1a\u201c\u5e08\u592a\uff0c\u4f46\u4e0d\u77e5\u5f53\u5e74\u5171\u7acb\u5251\u4ee4\u7684\u4e24\u5f20\u8c31\u724c\uff0c\u662f\u5426\u8fd8\u5728\uff1f\u201d \u3000\u3000\u795e\u5c3c\u9053\uff1a\u201c\u5f53\u7136\u5728\uff01\u5148\u5e08\u5c06\u5251\u4ee4\u4f20\u7ed9\u4e86\u8d2b\u5c3c\uff0c\u90a3\u4e24\u5f20\u8c31\u724c\uff0c\u5374\u5df2\u4ea4\u7ed9\u4e86\u9648\u5e08\u5144\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u5251\u795e\u6b7b\u540e\uff0c\u5e08\u592a\u53ef\u66fe\u627e\u5230\u90a3\u4e24\u5f20\u8c31\u724c\u7684\u85cf\u5904\uff1f\u201d \u3000\u3000\u795e\u5c3c\u63a5\u5934\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u53ea\u6015\u4e5f\u843d\u5728\u90a3\u5165\u51e1\u7684\u624b\u4e2d\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u987f\u8db3\u9053\uff1a\u201c\u90a3\u5c82\u4e0d\u7cdf\u4e86\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u957f\u53f9\u9053\uff1a\u201c\u6b63\u662f\u7cdf\u4e86\u2026\u2026\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5bf9\u4e8e\u6e38\u9f99\u5251\u4ee4\u4e4b\u4e8b\uff0c\u4f3c\u662f\u4e00\u65e0\u6240\u77e5\uff0c\u6014\u6014\u5730\u9053\uff1a\u201c\u90a3\u5165\u51e1\u5927\u5e08\u5c31\u5728\u5bfa\u4e2d\uff0c\u5e08\u53d4\u4f55\u4e0d\u627e\u4ed6\u95ee\u4e0a\u4e00\u95ee\uff1f\u201d \u3000\u3000\u795e\u5c3c\u9053\uff1a\u201c\u4ed6\u5982\u4e0d\u80af\u627f\u8ba4\uff0c\u5c82\u4e0d\u662f\u6253\u8349\u60ca\u86c7\uff1f\u611a\u53d4\u4e4b\u89c1\uff0c\u4e0d\u5982\u7b49\u5230\u4eca\u65e5\u5927\u4f1a\u4e4b\u4e0a\uff0c\u90a3\u5165\u51e1\u4eae\u51fa\u5251\u4ee4\u4e4b\u65f6\uff0c\u518d\u884c\u5411\u5979\u8ffd\u56de\u3002\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u90a3\u5165\u51e1\u5982\u4e0d\u80af\u627f\u8ba4\uff0c\u8001\u592b\u5c31\u5c06\u4ed6\u780d\u6210\u8089\u6ce5\uff0c\u4e5f\u8981\u627e\u51fa\u5251\u4ee4\u548c\u53cc\u8c31\u3002\u201d \u3000\u3000\u795d\u516c\u660e\u6c89\u541f\u9053\uff1a\u201c\u8f9b\u5144\uff0c\u8fd9\u53ef\u4e07\u4e07\u4f7f\u4e0d\u5f97\u3002\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u7eb5\u7136\u4e09\u4e2a\u5165\u51e1\u5927\u5e08\u8054\u624b\uff0c\u8001\u592b\u4e00\u6837\u53ef\u4ee5\u53eb\u4ed6\u8840\u6d41\u4e94\u6b65\uff0c\u6a2a\u5c38\u5c11\u6797\u3002\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u8f9b\u5144\uff0c\u8fd9\u53ef\u4e0d\u662f\u6b66\u529f\u9ad8\u4f4e\u7684\u95ee\u9898\uff01\u5982\u662f\u4f60\u6740\u4e86\u4ed6\uff0c\u4ecd\u7136\u627e\u4e0d\u51fa\u5251\u4ee4\u548c\u53cc\u8c31\u53c8\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u8001\u592b\u4e0d\u4fe1\u4ed6\u5b81\u6b7b\u4e0d\u8bf4\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u90a3\u53ef\u4e0d\u4e00\u5b9a\uff0c\u8fd9\u4e2a\u9669\u54b1\u4eec\u4e0d\u5b9c\u5192\u554a\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4f9d\u795d\u5144\u4e4b\u89c1\uff0c\u662f\u540c\u610f\u5e08\u592a\u505a\u6cd5\uff1f\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5f53\u5e74\u6d77\u5185\u5916\u6b63\u90aa\u4e24\u9053\u7684\u4e8c\u5341\u540d\u9ad8\u624b\uff0c\u4e00\u540c\u7b7e\u540d\u5929\u9e64\u7fa4\u9f99\u53cc\u8c31\uff0c\u7acb\u4e0b\u6e38\u9f99\u5251\u4ee4\uff0c\u539f\u610f\u4e43\u662f\u63a8\u5d07\u5251\u5723\u4e00\u8109\u76f8\u4f20\u7684\u5b50\u5f1f\uff0c\u638c\u6b64\u5251\u4ee4\uff0c\u800c\u7ef4\u6b66\u6797\u592a\u5e73\uff0c\u5982\u662f\u5251\u4ee4\u53cc\u8c31\u843d\u5165\u4ed6\u4eba\u624b\u4e2d\uff0c\u9664\u4e86\u5177\u540d\u7684\u4e8c\u5341\u4eba\u672c\u4eba\u6216\u6d3e\u5b50\u5f1f\u4ee3\u8868\uff0c\u4f46\u5747\u8981\u626b\u6570\u5230\u9f50\uff0c\u53ef\u4ee5\u5411\u90a3\u6301\u4ee4\u4e4b\u4eba\u7d22\u56de\uff0c\u6b64\u4e8b\u8f9b\u5144\u5e94\u662f\u8bb0\u5f97\u7684\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u5f53\u7136\u8bb0\u5f97\u4e86\uff0c\u8001\u592b\u4e5f\u662f\u7b7e\u540d\u4e4b\u4eba\u554a\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4e86\uff01\u4f46\u5982\u90a3\u4e8c\u5341\u4eba\u5230\u4e0d\u9f50\u5462\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u5982\u662f\u7acb\u8c31\u7684\u4e8c\u5341\u4eba\u5230\u4e0d\u9f50\uff0c\u5f53\u7136\u53ea\u6709\u5251\u5723\u7684\u4f20\u4eba\u624d\u6709\u8d44\u683c\u8ba8\u56de\u4e86\uff01\u201d \u3000\u3000\u795d\u516c\u660e\u9053\uff1a\u201c\u8f9b\u5144\uff0c\u6b63\u90aa\u4e8c\u5341\u9ad8\u624b\uff0c\u4eca\u65e5\u5df2\u7136\u4e0d\u5168\u5728\u4e16\u95f4\uff0c\u4ed6\u4eec\u7684\u5b50\u5f1f\uff0c\u662f\u5426\u5230\u9f50\uff0c\u4e5f\u9887\u6709\u95ee\u9898\uff0c\u4f55\u51b5\uff0c\u5f53\u5e74\u9a70\u540d\u7684\u90aa\u9053\u4eba\u7269\u4e2d\u7684\u4e94\u9b54\uff0c\u6839\u672c\u5f88\u53ef\u80fd\u4fbf\u662f\u652f\u6301\u5165\u51e1\u5927\u5e08\u4e4b\u4eba\uff0c\u8fd9\u8054\u624b\u8ba8\u5251\u4ee4\u4e4b\u4e8b\uff0c\u5c82\u975e\u884c\u4e0d\u901a\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u60f3\u4e86\u4e00\u60f3\u9053\uff1a\u201c\u6709\u9053\u7406\uff0c\u770b\u6765\u53ea\u6709\u4f9d\u7740\u795d\u5144\u4e4b\u89c1\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u542c\u5f97\u6709\u4e9b\u8ff7\u7cca\uff0c\u8131\u53e3\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u8fd9\u7a76\u7adf\u662f\u600e\u4e48\u4e00\u56de\u4e8b\u5462\uff1f\u201d \u3000\u3000\u91d1\u9876\u795e\u5c3c\u957f\u957f\u5730\u53f9\u4e86\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u6b64\u4e8b\u8bf4\u6765\u8bdd\u957f\uff0c\u4f46\u4eca\u65e5\u5982\u662f\u4e0d\u5bf9\u4f60\u8bf4\u660e\uff0c\u5c11\u65f6\u8981\u4f60\u51fa\u9762\u8ffd\u56de\u5251\u4ee4\u53cc\u8c31\uff0c\u53ea\u6015\u8981\u66f4\u8d39\u5507\u820c\u4e86\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5f1f\u5b50\u656c\u5019\u5e08\u53d4\u6307\u793a\u3002\u201d \u3000\u3000\u91d1\u9876\u795e\u5c3c\u70b9\u4e86\u70b9\u5934\uff0c\u5f53\u4e0b\u8bf4\u4e86\u4e00\u756a\u7ecf\u8fc7\u3002 \u3000\u3000\u539f\u6765\u5f53\u5e74\u5251\u5723\u9f99\u8d85\u7fa4\uff0c\u5728\u5b89\u4e5d\u516c\u592b\u5987\u8fdb\u5165\u4e2d\u539f\u6b66\u6797\uff0c\u5929\u53f0\u6597\u8d25\u5341\u9ad8\u624b\u4e4b\u540e\uff0c\u4eb2\u81ea\u8d76\u53bb\u5929\u53f0\uff0c\u201c\u51c9\u5929\u53df\u201d\u5b89\u4e5d\u516c\u592b\u5987\uff0c\u4e0d\u51fa\u5341\u62db\uff0c\u4fbf\u8d25\u5728\u5251\u5723\u624b\u4e2d\uff0c\u81ea\u662f\u4f7f\u5f97\u5b89\u4e5d\u516c\u592b\u5987\u656c\u4f69\u4e0d\u5df2\uff0c\u4f46\u4e0e\u90a3\u4e2d\u539f\u5341\u5927\u9ad8\u624b\uff0c\u4ee5\u53ca\u5f53\u65e5\u4e5f\u5728\u5c71\u4e0a\uff0c\u7531\u5b89\u4e5d\u516c\u592b\u5987\u9886\u5934\u7684\u4e00\u4efd\u5341\u4eba\uff0c\u5168\u662f\u4ee3\u8868\u4e2d\u539f\u9b54\u9053\u9ad8\u624b\uff0c\u56e0\u662f\u53eb\u505a\u201c\u5929\u9e64\u8c31\u724c\u201d\uff0c\u4ee5\u8f9b\u67cf\u516c\u548c\u5c11\u6797\u4e0a\u4efb\u638c\u95e8\u957f\u7709\u5927\u5e08\u4e3a\u9996\u7684\u4e00\u4efd\uff0c\u5219\u53eb\u201c\u7fa4\u9f99\u8c31\u724c\u201d\uff0c\u53cc\u8c31\u4e4b\u95f4\uff0c\u5e76\u7531\u957f\u7709\u5927\u5e08\u5199\u4e0b\u5e8f\u6587\uff0c\u8bf4\u660e\u7acb\u8c31\u7acb\u4ee4\u7ecf\u8fc7\uff0c\u5e76\u4e14\u8a00\u660e\uff0c\u51ed\u6b64\u5251\u4ee4\uff0c\u53ef\u4ee5\u4f7f\u53cc\u8c31\u4e4b\u4e0a\u5217\u540d\u7684\u5404\u6d3e\u5404\u95e8\u4eba\u7269\uff0c\u542c\u4ee4\u884c\u4e8b\uff0c\u5f53\u65f6\uff0c\u5251\u5723\u4e5f\u66fe\u53cd\u5bf9\uff0c\u4f46\u56e0\u5374\u4e0d\u8fc7\u4e8c\u5341\u540d\u9ad8\u624b\u7684\u76db\u4e3e\uff0c\u4e5f\u4e3a\u4e86\u60f3\u7ef4\u6301\u6b66\u6797\u6682\u65f6\u7684\u5b81\u9759\uff0c\u5251\u5723\u7ec8\u4e8e\u63a5\u53d7\uff0c\u4e0d\u8fc7\uff0c\u5251\u5723\u63d0\u51fa\u4e00\u4e2a\u6761\u4ef6\uff0c\u6b64\u4ee4\u6b64\u8c31\uff0c\u4ec5\u4ee5\u4e94\u5341\u5e74\u4e3a\u9650\uff0c\u4e94\u5341\u5e74\u540e\uff0c\u4fbf\u987b\u4f5c\u5e9f\uff0c\u4e8c\u5341\u540d\u9ad8\u624b\u7684\u540e\u4eba\uff0c\u4fbf\u53ef\u4e0d\u518d\u63a5\u53d7\u5251\u4ee4\u7684\u53ec\u5524\u4e86\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u95fb\u8bf4\uff0c\u8fd9\u624d\u604d\u7136\u5927\u609f\uff0c\u60f3\u4e0d\u5230\u81ea\u5df1\u7684\u5e08\u95e8\uff0c\u7adf\u662f\u8fd8\u7275\u6d89\u8fd9\u8bb8\u591a\u6b66\u6797\u6069\u6028\u5728\u5185\u3002 \u3000\u3000\u8f9b\u67cf\u516c\u770b\u4e86\u65b9\u96ea\u5b9c\u4e00\u773c\uff0c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f9d\u7167\u4f60\u5e08\u53d4\u548c\u795d\u8001\u513f\u7684\u89c1\u5730\uff0c\u90a3\u5251\u4ee4\u548c\u53cc\u8c31\u4e0d\u4f46\u5173\u7cfb\u5230\u4f60\u5e08\u7236\u6740\u8eab\u4e4b\u4ec7\uff0c\u800c\u4e14\u5173\u7cfb\u5230\u6b66\u6797\u4eca\u540e\u7684\u5927\u5c40\uff0c\u4eca\u65e5\u4e94\u5723\u6559\u8fd9\u5927\u4f1a\u4e4b\u4e0a\uff0c\u4f60\u5c11\u4e0d\u5f97\u5fc5\u987b\u4ee5\u5251\u5723\u518d\u4f20\u5f1f\u5b50\u7684\u8eab\u4efd\uff0c\u633a\u8eab\u800c\u51fa\uff0c\u7d22\u8fd8\u5251\u4ee4\u53cc\u8c31\uff0c\u5426\u5219\uff0c\u8fd9\u5c1a\u6709\u4e8c\u5341\u5e74\u7684\u671f\u9650\uff0c\u53ef\u8981\u5f04\u5f97\u6b66\u6797\u5927\u4e71\uff0c\u4eba\u4eba\u81ea\u5371\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u60ca\u7136\u52a8\u5bb9\uff0c\u606d\u58f0\u9053\uff1a\u201c\u665a\u8f88\u8bb0\u4e0b\u4e86\u2026\u2026\u201d \u3000\u3000\u91d1\u9876\u795e\u5c3c\u5411\u8f9b\u67cf\u516c\u7b49\u5408\u5341\u9053\uff1a\u201c\u65bd\u4e3b\uff0c\u8d2b\u5c3c\u4e0d\u613f\u5728\u5c11\u6797\u9017\u7559\uff0c\u5348\u65f6\u4e09\u523b\uff0c\u54b1\u4eec\u5230\u4e2d\u5cb3\u5e99\u518d\u89c1\u4e86\u3002\u201d\u8bed\u97f3\u4e00\u843d\uff0c\u4fbf\u8f6c\u8eab\u79bb\u53bb\u3002 \u3000\u3000\u795d\u516c\u660e\u770b\u5f97\u6447\u5934\u4e00\u53f9\u9053\uff1a\u201c\u6797\u59d1\u5a18\u7684\u6b66\u529f\uff0c\u5f53\u771f\u662f\u5df2\u5165\u5316\u5883\u4e86\uff01\u5979\u73b0\u8eab\u5ba4\u5185\uff0c\u5c45\u7136\u4e0d\u66fe\u60ca\u52a8\u6211\u7b49\uff0c\u9519\u975e\u5230\u4e86\u98de\u884c\u7edd\u8ff9\u5730\u6b65\uff0c\u53c8\u600e\u80fd\u7792\u5f97\u8fc7\u6211\u7b49\u8033\u76ee\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u5faa\u8ff9\u7a7a\u95e8\uff0c\u5fc3\u65e0\u4ed6\u987e\uff0c\u90a3\u81ea\u7136\u662f\u65e5\u6709\u8fdb\u76ca\u7684\u4e86\uff01\u201d \u3000\u3000\u4ed6\u8bed\u97f3\u4e00\u987f\uff0c\u957f\u957f\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u770b\u770b\u7a97\u5916\u7684\u5929\u6c14\uff0c\u7b11\u9053\uff1a\u201c\u5404\u4f4d\uff0c\u54b1\u4eec\u4e5f\u8be5\u53bb\u51c6\u5907\u4e00\u4e0b\u4e86\uff01\u201d \u3000\u3000\u4e00\u884c\u4eba\u4fbf\u81ea\u524d\u53bb\u5927\u6bbf\uff0c\u5c11\u6797\u638c\u95e8\u548c\u51e0\u4f4d\u957f\u8001\uff0c\u65e9\u5df2\u5728\u5176\u4e2d\u7b49\u5019\uff0c\u524d\u5f80\u5c11\u6797\u7684\u5404\u6d3e\u4eba\u7269\u805a\u9f50\u4ee5\u540e\uff0c\u7531\u8f9b\u67cf\u516c\u7387\u9886\uff0c\u6d69\u6d69\u8361\u8361\u5411\u4e2d\u5cb3\u5e99\u884c\u53bb\u3002 \u3000\u3000\u4e2d\u5cb3\u5e99\u5728\u592a\u5ba4\u4e1c\u5357\u9ec4\u76d6\u5cf0\u4e0b\uff0c\u7fd8\u5236\u5b8f\u58ee\uff0c\u7891\u77f3\u68ee\u7acb\uff0c\u6e38\u5c71\u4e4b\u4eba\uff0c\u8d8b\u4e4b\u82e5\u60ca\uff0c\u4f46\u8fd1\u5e74\u4ee5\u6765\uff0c\u56e0\u90a3\u4e94\u9b54\u5904\u5fc3\u79ef\u8651\uff0c\u5efa\u7acb\u4e94\u5723\u6559\u4e4b\u6545\uff0c\u4e2d\u5cb3\u5e99\u6e38\u4eba\u5927\u4e3a\u51cf\u5c11\uff0c\u4e0d\u8fc7\uff0c\u6b64\u523b\u5374\u662f\u4e0d\u540c\uff0c\u53ea\u89c1\u90a3\u5c71\u9053\u4e4b\u4e0a\uff0c\u4eba\u5996\u4e0d\u7edd\uff0c\u5c31\u8fde\u5e99\u95e8\u4ee5\u5916\uff0c\u4e5f\u6324\u6ee1\u4e86\u4eba\u7fa4\u3002 \u3000\u3000\u8f9b\u67cf\u516c\u7b49\u4e00\u884c\u62b5\u8fbe\u5e99\u95e8\u4e4b\u524d\uff0c\u4fbf\u53ef\u671b\u89c1\u90a3\u9ad8\u9ad8\u8038\u7acb\u7684\u4e00\u5ea7\u724c\u697c\uff0c\u724c\u697c\u4e4b\u4e0b\uff0c\u6446\u4e86\u4e0d\u4e0b\u767e\u684c\u9152\u7b75\u3002 \u3000\u3000\u9760\u8fd1\u724c\u697c\u7684\u4e8c\u5341\u4f59\u5e2d\u867d\u7136\u8fd8\u662f\u7a7a\u7740\uff0c\u4f46\u90a3\u5176\u4f59\u7684\u5e2d\u6b21\uff0c\u5dee\u4e0d\u591a\u4ff1\u5df2\u5750\u6ee1\u3002 \u3000\u3000\u4e94\u9b54\u4e00\u4eba\u672a\u89c1\uff0c\u51fa\u9762\u63a5\u5f85\u7684\u4eba\u7269\u4e4b\u4e2d\uff0c\u65b9\u96ea\u5b9c\u53ea\u8ba4\u8bc6\u827e\u4e1c\u6d77\u548c\u5929\u53f0\u4e09\u7edd\uff0c\u4ee5\u53ca\u51af\u96f7\u3001\u5927\u80dc\u7b49\u4eba\u3002 \u3000\u3000\u4e00\u4f19\u4eba\u5750\u5b9a\u4ee5\u540e\uff0c\u90a3\u5165\u51e1\u5927\u5e08\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u638c\u95e8\u4eba\uff0c\u90a3\u4e94\u9b54\u6210\u7acb\u4e94\u5723\u6559\u4e4b\u4e8b\uff0c\u636e\u8bf4\u8fd8\u6709\u5e55\u540e\u7b56\u52a8\u4e4b\u4eba\uff0c\u4e0d\u77e5\u638c\u95e8\u4eba\u53ef\u66fe\u77e5\u6653\uff1f\u201d \u3000\u3000\u5165\u4e91\u5927\u5e08\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u8fd9\u2026\u2026\u672c\u5ea7\u5012\u662f\u672a\u66fe\u542c\u8bf4\uff0c\u4e0d\u77e5\u5e08\u5144\u53ef\u5df2\u67e5\u51fa\u90a3\u5e55\u540e\u8fd9\u4eba\u662f\u8c01\u3002\u201d \u3000\u3000\u5165\u51e1\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5c1a\u672a\u5b8c\u5168\u67e5\u51fa\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u4f59\u97f3\u672a\u5df2\uff0c\u5ffd\u7136\u6253\u90a3\u5e99\u4e2d\u4f20\u6765\u4e09\u58f0\u949f\u78d0\u4e4b\u58f0\u3002 \u3000\u3000\u4e00\u9635\u6b22\u547c\u58f0\u4e2d\uff0c\u4e94\u5927\u9b54\u4e3b\u5728\u5929\u9b54\u5973\u9886\u5bfc\u4e4b\u4e0b\uff0c\u5927\u6b65\u8d70\u51fa\u5e99\u95e8\uff0c\u767b\u4e0a\u724c\u697c\u4e0b\u65b9\u7684\u4e00\u5ea7\u6728\u53f0\u3002 \u3000\u3000\u6076\u5b54\u660e\u8ba1\u901a\uff0c\u76ee\u5149\u5728\u53f0\u4e0b\u7fa4\u8c6a\u8eab\u4e0a\u4e00\u8f6c\uff0c\u8d70\u5230\u53f0\u53e3\u5927\u58f0\u9053\uff1a\u201c\u4e94\u5723\u6559\u627f\u8499\u541b\u7fa4\u8c6a\u5149\u4e34\uff0c\u8ba1\u67d0\u5fc3\u4e2d\u5341\u5206\u611f\u4f69\uff01\u7c97\u8336\u6de1\u9152\uff0c\u4e0d\u6210\u656c\u610f\uff0c\u5c1a\u76fc\u8bf8\u4f4d\u5c3d\u60c5\u5403\u7528\u3002\u201d\u4ed6\u8bed\u97f3\u4e00\u987f\uff0c\u7b11\u4e86\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u8bf8\u4f4d\u4e4b\u4e2d\uff0c\u5982\u6709\u4e2a\u4eba\u6069\u4ec7\uff0c\u6216\u662f\u4e0d\u613f\u542c\u547d\u672c\u6559\u7684\u670b\u53cb\uff0c\u4e0d\u59a8\u4e0a\u53f0\u8868\u6f14\uff0c\u672c\u6559\u51fd\u67ec\u4e4b\u4e0a\uff0c\u8bf4\u5f97\u660e\u767d\uff0c\u60f3\u5fc5\u8bf8\u4f4d\u4e5f\u4e0d\u7528\u8ba1\u67d0\u5728\u6b64\u7f57\u54a6\u4e86\uff01\u5927\u4f1a\u53ea\u6709\u534a\u65e5\u65f6\u5149\uff0c\u8bf8\u4f4d\u5207\u5207\u4e0d\u53ef\u9519\u8fc7\u2026\u2026\u201d\u7565\u4e00\u62b1\u62f3\uff0c\u4fbf\u81ea\u8f6c\u8eab\u56de\u5230\u540e\u53f0\u7684\u5ea7\u4f4d\u4e2d\u5750\u4e0b\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u770b\u4e86\u8f9b\u67cf\u516c\u4e00\u773c\uff0c\u9053\uff1a\u201c\u8f9b\u8001\uff0c\u665a\u8f88\u4e0a\u53f0\u53bb\u95f9\u95f9\u4ed6\u4eec\u53ef\u597d\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u4e0d\u597d\uff01\u4f60\u6700\u597d\u83ab\u8981\u5148\u4e0a\u53f0\uff0c\u5012\u662f\u5bf9\u4ed8\u4e94\u9b54\u4e4b\u4e8b\uff0c\u5e94\u7531\u8001\u592b\u51fa\u9762\uff01\u201d\u8bed\u97f3\u4e00\u843d\uff0c\u7a81\u7136\u957f\u5578\u4e00\u58f0\uff0c\u76f4\u5411\u53f0\u4e0a\u8dc3\u843d\u3002 \u3000\u3000\u4e94\u9b54\u4e00\u89c1\u6709\u4eba\u4e0a\u53f0\uff0c\u5148\u662f\u4e00\u6014\uff0c\u5bb9\u5f97\u770b\u6e05\u6765\u4eba\u662f\u8c01\uff0c\u53c8\u4e0d\u7981\u540c\u65f6\u4e00\u60ca\u3002 \u3000\u3000\u8f9b\u67cf\u516c\u51b7\u7b11\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5929\u9b54\u5973\uff0c\u4f60\u4eec\u4e94\u4e2a\u4e00\u9f50\u4e0a\u5427\uff01\u8001\u592b\u8981\u4ee3\u90a3\u9648\u8001\u5f1f\u6536\u62fe\u4f60\u4eec\u4e86\uff01\u201d \u3000\u3000\u5929\u9b54\u5973\u76b1\u4e86\u76b1\u7709\uff0c\u6b63\u5f85\u8bf4\u8bdd\uff0c\u5ffd\u7136\u53c8\u662f\u4e00\u6761\u4eba\u5f71\uff0c\u4e0a\u5f97\u53f0\u6765\uff0c\u6562\u597d\uff0c\u8fd9\u4e0a\u53f0\u4e4b\u4eba\uff0c\u7adf\u662f\u5b89\u5c0f\u840d\u3002 \u3000\u3000\u8f9b\u67cf\u516c\u54fc\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u6765\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u8f9b\u516c\u516c\uff0c\u665a\u8f88\u6765\u544a\u8bc9\u4f60\uff0c\u4ed6\u4eec\u4e94\u4e2a\u5df2\u7136\u6ca1\u6709\u52a8\u624b\u7684\u529b\u6c14\u4e86\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u521a\u624d\u665a\u8f88\u83b7\u6089\uff0c\u4ed6\u4eec\u4e94\u4eba\u90fd\u5df2\u53d7\u4e86\u6697\u7b97\uff0c\u4e0d\u4e0e\u4eba\u52a8\u624b\uff0c\u8fd8\u4e0d\u4f1a\u6bd2\u53d1\u8eab\u6b7b\uff0c\u53ea\u8981\u4e00\u65e6\u4e0e\u4eba\u52a8\u624b\uff0c\u4e0d\u51fa\u534a\u4e2a\u65f6\u8fb0\uff0c\u5fc5\u5c06\u547d\u4e27\u5f53\u573a\uff01\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u771f\u7684\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u6bd5\u59d0\u59d0\u548c\u51af\u5927\u53d4\u78b0\u8fc7\u4e86\u5934\uff0c\u51af\u5927\u53d4\u8bf4\u7684\u8bdd\uff0c\u5fc5\u662f\u4e0d\u5047\uff01\u201d \u3000\u3000\u4e94\u9b54\u95fb\u8a00\uff0c\u54ea\u91cc\u76f8\u4fe1\uff1f\u53ea\u89c1\u90a3\u516b\u86ee\u9f13\u738b\u91d1\u957f\u5e9a\u5927\u559d\u4e00\u58f0\uff0c\u6325\u62f3\u4fbf\u5411\u8f9b\u67cf\u516c\u51fb\u6765\u3002 \u3000\u3000\u8f9b\u67cf\u516c\u6012\u76ee\u4e00\u77aa\uff0c\u4e3e\u624b\u62cd\u51fa\u4e00\u62f3\u3002 \u3000\u3000\u8fd9\u4e00\u638c\u529b\u9053\u4e4b\u5f3a\uff0c\u53ea\u9707\u5f97\u5750\u5728\u53f0\u4e0a\u7684\u51e0\u4eba\uff0c\u51e0\u4e4e\u5b58\u4e0d\u4f4f\u8eab\u3002 \u3000\u3000\u516b\u86ee\u9f13\u738b\u8138\u8272\u4e00\u53d8\uff0c\u8fde\u6c14\u90fd\u672a\u66fe\u5598\u5f97\u8fc7\u6765\uff0c\u4fbf\u95f7\u54fc\u4e00\u58f0\uff0c\u6454\u5012\u53f0\u4e0a\uff01\u602a\u7684\u662f\u4ed6\u7adf\u7136\u53e3\u4e2d\u5410\u4e86\u4e00\u6ee9\u9ed1\u8840\uff01 \u3000\u3000\u8fd9\u5149\u666f\u77a7\u5f97\u5929\u9b54\u5973\u7b49\u56db\u4eba\u5927\u60ca\u4e0d\u5df2\uff0c\u8131\u53e3\u53eb\u8fd9\uff1a\u201c\u91d1\u5144\u2026\u2026\u4f60\u771f\u7684\u4e2d\u4e86\u6bd2\u5417\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u90a3\u8fd8\u6709\u5047\uff1f\u5929\u9b54\u5973\uff0c\u4f60\u5982\u8981\u60f3\u6d3b\u547d\uff0c\u59d1\u5a18\u5012\u6709\u4e00\u4e2a\u6cd5\u5b50\uff01\u201d \u3000\u3000\u5929\u9b54\u5973\u6b64\u523b\u5df2\u7136\u65b9\u5bf8\u5927\u4e71\uff0c\u60f3\u4e0d\u5230\u81ea\u5df1\u8bf7\u6765\u7684\u670b\u53cb\uff0c\u7adf\u7136\u4f1a\u662f\u5bb3\u4e86\u81ea\u5df1\u4e4b\u4eba\uff0c\u5f53\u4e0b\u63a5\u5230\uff1a\u201c\u59d1\u5a18\u4ec0\u4e48\u6761\u4ef6\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4f60\u5728\u5929\u53f0\u4e09\u7edd\u8eab\u4e0a\u6240\u4e0b\u4e4b\u6bd2\uff0c\u53ef\u6709\u89e3\u836f\uff1f\u53ea\u6d88\u4f60\u89e3\u53bb\u4ed6\u4eec\u4e09\u4eba\u4e4b\u6bd2\uff0c\u59d1\u5a18\u5c31\u8981\u51af\u5927\u53d4\u89e3\u9664\u4f60\u4eec\u8eab\u4e0a\u4e4b\u6bd2\u3002\u201d \u3000\u3000\u5b89\u5c0f\u840d\u8bdd\u97f3\u4e00\u843d\uff0c\u5929\u9b54\u5973\u7adf\u7136\u60f3\u90fd\u4e0d\u60f3\uff0c\u4fbf\u53d6\u51fa\u4e09\u9897\u89e3\u836f\uff0c\u9012\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u5411\u53f0\u4e0b\u62db\u624b\uff0c\u5929\u53f0\u4e09\u7edd\u4fbf\u4e0a\u4e86\u6728\u53f0\uff0c\u5b89\u5c0f\u840d\u770b\u770b\u4ed6\u4eec\u541e\u4e0b\u89e3\u836f\uff0c\u8fc7\u4e86\u534a\u76cf\u8336\u65f6\uff0c\u65b9\u9053\uff1a\u201c\u4e09\u4f4d\u89c9\u5f97\u5982\u4f55\uff01\u201d \u3000\u3000\u6c5f\u5929\u6653\u5ffd\u7136\u53cc\u76ee\u4e00\u7738\u9053\uff1a\u201c\u591a\u4e86\uff01\u591a\u8c22\u59d1\u5a18\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u4e00\u8f6c\uff0c\u5c31\u5f85\u5411\u5929\u9b54\u5973\u884c\u53bb\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u4e09\u4f4d\u524d\u8f88\uff0c\u4eca\u65e5\u4e4b\u4e8b\uff0c\u4e0d\u52b3\u4e09\u4f4d\u52a8\u624b\u4e86\uff01\u201d \u3000\u3000\u5929\u53f0\u4e09\u7edd\u6014\u4e86\u4e00\u6014\uff0c\u4f46\u5374\u4f9d\u8a00\u8d70\u4e0b\u53f0\u53bb\uff01 \u3000\u3000\u8f9b\u67cf\u516c\u8fd9\u65f6\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u5929\u9b54\u5973\uff0c\u4e94\u5723\u6559\u662f\u4ec0\u4e48\u4eba\u9f13\u52b1\u5c14\u7b49\u7ec4\u6210\u7684\uff1f\u201d \u3000\u3000\u5929\u9b54\u5973\u8138\u8272\u4e00\u53d8\uff0c\u5374\u5411\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u5929\u53f0\u4e09\u7edd\u7684\u6bd2\u529b\u5df2\u9664\uff0c\u59be\u8eab\u7b49\u6240\u4e2d\u4e4b\u6bd2\uff0c\u59d1\u5a18\u53ef\u5426\u8981\u51af\u5144\u9ad8\u62ac\u8d35\u624b\u2026\u2026\u201d \u3000\u3000\u5b89\u5c0f\u840d\u7b11\u9053\uff1a\u201c\u522b\u5fd9\u554a\uff01\u8f9b\u516c\u516c\u7684\u8bdd\u8fd8\u6ca1\u8bb2\u5b8c\u5462\uff01\u201d \u3000\u3000\u5929\u9b54\u5973\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u672c\u6559\u4e43\u662f\u5949\u6e38\u9f99\u5251\u4ee4\u4e4b\u547d\u7ec4\u6210\uff0c\u90a3\u53d1\u4ee4\u4e4b\u4eba\uff0c\u53ea\u6015\u662f\u5251\u5723\u7684\u540e\u4eba\u4e86\u3002\u201d \u3000\u3000\u8f9b\u67cf\u516c\u51b7\u7b11\u9053\uff1a\u201c\u4ed6\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u5929\u9b54\u5973\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u59be\u8eab\u4e0d\u77e5\u554a\uff01\u201d\u77a7\u5979\u7684\u795e\u6001\uff0c\u5012\u4e5f\u4e0d\u50cf\u505a\u4f5c\u3002 \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u4eec\u53ea\u662f\u5080\u5121\u4e86\uff1f\u201d \u3000\u3000\u5929\u9b54\u5973\u70b9\u4e86\u70b9\u5934\uff0c\u4e0d\u66fe\u4f5c\u58f0\u3002 \u3000\u3000\u5b89\u5c0f\u840d\u76ee\u5149\u4e00\u8f6c\u9053\uff1a\u201c\u4f60\u4eec\u65e2\u7136\u4e0d\u662f\u6b63\u4e3b\uff0c\u6700\u597d\u4e0b\u53bb\u5427\uff01\u201d \u3000\u3000\u5929\u9b54\u5973\u7b49\u4eba\u4e00\u5446\uff0c\u8ba1\u901a\u63a5\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u89e3\u836f\u5462\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u840d\u9053\uff1a\u201c\u4f60\u4eec\u53ea\u8981\u4e0b\u53f0\uff0c\u5e76\u4e14\u53d1\u8a93\u4eca\u540e\u4e0d\u518d\u5728\u6b66\u6797\u4e2d\u9732\u9762\uff0c\u81ea\u7136\u6709\u4eba\u4f1a\u7ed9\u4f60\u89e3\u836f\u3002\u201d \u3000\u3000\u5929\u9b54\u5973\u7b49\u56db\u4eba\u4e92\u770b\u4e86\u4e00\u773c\uff0c\u518d\u77a7\u6c14\u7edd\u591a\u65f6\u7684\u516b\u86ee\u9f13\u738b\u7684\u5168\u8eab\u53d8\u6210\u9ed1\u70ad\u822c\u53d1\u5149\uff0c\u53ea\u597d\u957f\u957f\u5730\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u5f53\u771f\u4e0b\u53f0\u800c\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u53ea\u89c1\u53c8\u6709\u4e09\u6761\u4eba\u5f71\uff0c\u4e0a\u4e86\u6728\u53f0\uff0c\u4ed6\u4eec\u6b63\u662f\u91d1\u9876\u795e\u5c3c\u548c\u65b9\u96ea\u5b9c\u3001\u90d1\u5927\u521a\u7b49\u4e09\u4eba\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5411\u90a3\u8f9b\u67cf\u516c\u4e00\u63d6\u9053\uff1a\u201c\u8f9b\u8001\uff0c\u4e94\u9b54\u7684\u4e8b\uff0c\u4f60\u8001\u65e2\u7136\u5c65\u884c\u4e86\u8bfa\u8a00\uff0c\u5269\u4e0b\u7684\u4e8b\uff0c\u7531\u665a\u8f88\u5904\u7406\u5982\u4f55\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u76ee\u5149\u4e00\u8f6c\u5927\u7b11\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u8001\u592b\u4e5f\u6ca1\u6709\u8ddf\u4f60\u7ea6\u5b9a\uff0c\u8bf4\u4e0d\u8bb8\u8001\u592b\u518d\u7ba1\u522b\u7684\u4e8b\u5427\uff01\u201d\u8bdd\u97f3\u4e00\u987f\uff0c\u5ffd\u7136\u5411\u53f0\u4e0b\u7684\u65b9\u5929\u6210\u5927\u559d\u9053\uff1a\u201c\u82cf\u5c0f\u5761\uff0c\u4f60\u4e5f\u8be5\u4e0a\u53f0\u4e86\uff01\u201d \u3000\u3000\u90a3\u5047\u65b9\u5929\u6210\u5446\u4e86\u4e00\u5446\uff0c\u4f46\u5374\u8d70\u4e0a\u4e86\u6728\u53f0\uff01 \u3000\u3000\u8f9b\u67cf\u516c\u51b7\u7b11\u9053\uff1a\u201c\u65b9\u5927\u5b9c\u548c\u65b9\u5929\u6210\u90fd\u662f\u6b7b\u5728\u4f60\u7684\u624b\u4e0b\uff1f\u201d \u3000\u3000\u5047\u65b9\u5929\u6210\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u8f9b\u67cf\u516c\uff0c\u4f60\u600e\u4f1a\u77e5\u6653\u533a\u533a\u4fbf\u662f\u90a3\u5316\u5f71\u4eba\u9b54\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u80fd\u7792\u5f97\u8fc7\u8001\u592b\uff1f\u59d3\u82cf\u7684\uff0c\u4f60\u7ed9\u6211\u7ad9\u4e00\u4f1a\u513f\uff0c\u91d1\u9876\u795e\u5c3c\u8fd8\u6709\u4e00\u70b9\u513f\u4e8b\uff0c\u8981\u5148\u884c\u529e\u59a5\uff0c\u4f60\u548c\u65b9\u5bb6\u7684\u6069\u6028\uff0c\u54b1\u4eec\u6700\u597d\u518d\u6765\u89e3\u51b3\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u91d1\u9876\u795e\u5c3c\u5df2\u53cc\u624b\u5408\u5341\uff0c\u5411\u53f0\u4e0b\u53eb\u9053\uff1a\u201c\u8d2b\u5c3c\u6709\u8bf7\u5165\u51e1\u5927\u5e08\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u90a3\u5165\u51e1\u5927\u5e08\u6014\u4e86\u4e00\u6014\uff0c\u8fc5\u5feb\u5730\u8d77\u8eab\uff0c\u4e0a\u4e86\u6728\u53f0\u3002 \u3000\u3000\u91d1\u9876\u795e\u5c3c\u9053\uff1a\u201c\u5927\u5e08\uff0c\u8d2b\u5c3c\u6709\u4e00\u4e8b\u4e0d\u660e\uff0c\u60f3\u5411\u5927\u5e08\u8bf7\u6559\uff01\u201d \u3000\u3000\u5165\u51e1\u5927\u5e08\u9053\uff1a\u201c\u5e08\u592a\u6709\u4f55\u89c1\u6559\uff1f\u201d \u3000\u3000\u91d1\u9876\u795e\u5c3c\u51b7\u7b11\u9053\uff1a\u201c\u6e38\u9f99\u5251\u4ee4\u548c\u5929\u9e64\u3001\u7fa4\u9f99\u53cc\u8c31\uff0c\u662f\u5426\u843d\u5728\u5927\u5e08\u624b\u4e2d\uff1f\u201d \u3000\u3000\u5165\u51e1\u5927\u5e08\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b63\u5728\u8001\u8872\u8eab\u7554\u3002\u201d \u3000\u3000\u91d1\u9876\u795e\u5c3c\u9053\uff1a\u201c\u4f60\u662f\u600e\u751f\u5f97\u6765\u7684\uff1f\u201d \u3000\u3000\u5165\u51e1\u5927\u5e08\u9053\uff1a\u201c\u8001\u8872\u662f\u4ece\u9648\u65bd\u4e3b\u5904\u5f97\u6765\uff01\u201d \u3000\u3000\u91d1\u9876\u795e\u5c3c\u5ffd\u7136\u53cc\u76ee\u4e4b\u4e2d\uff0c\u6cea\u5149\u95ea\u73b0\uff0c\u53f1\u9053\uff1a\u201c\u4f5b\u95e8\u5f1f\u5b50\uff0c\u60f3\u4e0d\u5230\u7adf\u7136\u4f1a\u505a\u51fa\u6697\u7b97\u4f24\u4eba\u4e4b\u4e8b\uff0c\u8d2b\u5c3c\u4eca\u65e5\u2026\u2026\u201d \u3000\u3000\u5979\u8bdd\u97f3\u672a\u5df2\uff0c\u5165\u51e1\u5927\u5e08\u5df2\u7136\u63a5\u9053\uff1a\u201c\u5e08\u592a\uff0c\u4f60\u83ab\u975e\u4ee5\u4e3a\u90a3\u9648\u5927\u4fa0\u4e43\u662f\u8001\u8872\u6697\u7b97\u7684\u5417\uff1f\u201d \u3000\u3000\u91d1\u9876\u795e\u5c3c\u9053\uff1a\u201c\u4e0d\u662f\u4f60\u53c8\u662f\u8c01\uff1f\u201d \u3000\u3000\u5165\u51e1\u5927\u5e08\u76ee\u5149\u5728\u5929\u9b54\u5973\u8eab\u4e0a\u4e00\u8f6c\uff0c\u5408\u5341\u7b11\u9053\uff1a\u201c\u5e08\u5927\uff0c\u8fd9\u4e8b\u53ef\u662f\u4f60\u8bef\u4f1a\u4e86\uff0c\u6697\u7b97\u9648\u5927\u4fa0\u4e4b\u4eba\uff0c\u4e43\u662f\u5929\u9b54\u5973\u7684\u4e00\u540d\u5f1f\u5b50\uff01\u201d \u3000\u3000\u91d1\u9876\u795e\u5c3c\u9053\uff1a\u201c\u771f\u7684\uff1f\u201d \u3000\u3000\u5165\u51e1\u5927\u5e08\u9053\uff1a\u201c\u8001\u8872\u5c82\u662f\u9a97\u4eba\u4e4b\u5f92\uff01\u800c\u4e14\u5929\u9b54\u5973\u7684\u5f1f\u5b50\u67f4\u82b3\u541b\uff0c\u5f53\u65f6\u5df2\u7136\u5728\u8001\u8872\u624b\u4e0b\uff0c\u9648\u5927\u4fa0\u4e43\u662f\u4eb2\u76ee\u6240\u89c1\uff0c\u81ea\u7136\u662f\u4e0d\u4f1a\u6709\u5047\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5ffd\u7136\u63d2\u53e3\u9053\uff1a\u201c\u5927\u5e08\uff0c\u8fd9\u4eab\u6709\u4e9b\u4e0d\u5bf9\uff01\u201d \u3000\u3000\u5165\u51e1\u5927\u5e08\u9053\uff1a\u201c\u53ef\u662f\u9648\u5927\u4fa0\u8bf4\u4ed6\u4e0d\u77e5\u4ec0\u4e48\u4eba\u6697\u7b97\u4e8e\u4ed6\u7684\u5417\uff1f\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u5165\u51e1\u5927\u5e08\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\uff0c\u5f53\u65f6\u4f11\u8bf4\u9648\u5927\u4fa0\u4e0d\u77e5\u67f4\u82b3\u541b\u662f\u4f55\u8bb8\u4eba\uff0c\u5c31\u8fde\u8001\u8872\u4e5f\u4e0d\u77e5\u5979\u662f\u8c01\uff01\u53ea\u56e0\u5979\u88c5\u6210\u4e86\u4e00\u540d\u5e97\u5bb6\u7684\u5987\u4eba\uff0c\u9648\u5927\u4fa0\u624d\u4f1a\u4e0a\u4e86\u5979\u7684\u5f53\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u9053\uff1a\u201c\u5927\u5e08\u76ee\u4e0b\u53c8\u600e\u77e5\u5979\u662f\u5929\u9b54\u5973\u7684\u7b2c\u5b50\uff1f\u201d \u3000\u3000\u5165\u51e1\u5927\u5e08\u9053\uff1a\u201c\u8fd9\u4e2a\u5417\uff1f\u90a3\u662f\u56e0\u4e3a\u8001\u8872\u4ece\u5929\u9b54\u5973\u53e6\u4e00\u4f4d\u5f1f\u5b50\u53e3\u4e2d\u83b7\u6089\u2026\u2026\u5c0f\u65bd\u4e3b\uff0c\u8001\u8872\u4fd7\u5bb6\u59d3\u767d\uff0c\u5929\u9b54\u5973\u7684\u5927\u5f1f\u5b50\u767d\u7389\u8776\uff0c\u4e43\u662f\u8001\u8872\u7684\u4f84\u5973\uff0c\u662f\u4ee5\u8001\u8872\u624d\u77e5\u5176\u4e2d\u539f\u59d4\uff01\u201d \u3000\u3000\u91d1\u9876\u795e\u5c3c\u9053\uff1a\u201c\u6211\u90a3\u5e08\u5144\u4e3a\u4f55\u8981\u628a\u5251\u4ee4\u7ed9\u4f60\uff1f\u201d \u3000\u3000\u5165\u51e1\u5927\u5e08\u63a2\u624b\u53d6\u51fa\u4e86\u5251\u4ee4\u548c\u4e24\u5f20\u7f8a\u76ae\u8c31\u724c\uff0c\u606d\u656c\u5730\u4ea4\u7ed9\u4e86\u65b9\u96ea\u5b9c\uff0c\u8fd9\u624d\u63a5\u9053\uff1a\u201c\u9648\u5927\u4fa0\u5728\u4e2d\u6bd2\u4ee5\u540e\uff0c\u4f3c\u662f\u81ea\u77e5\u751f\u547d\u4e0d\u4e45\uff0c\u5f53\u65f6\u53ea\u6709\u8001\u8872\u548c\u767d\u62c2\u9053\u957f\u5728\u65c1\uff0c\u9648\u5927\u4fa0\u4fbf\u5c06\u4ee4\u8c31\u4ea4\u7ed9\u8001\u8872\u4fdd\u7ba1\uff0c\u8a00\u660e\u6682\u4ee5\u4e8c\u5341\u5e74\u4e3a\u9650\uff0c\u4e8c\u5341\u5e74\u540e\uff0c\u5982\u662f\u4ed6\u4ecd\u65e0\u4f20\u4eba\u51fa\u73b0\uff0c\u5c31\u8981\u8001\u8872\u5c06\u5b83\u6bc1\u53bb\u2026\u2026\u201d\u8bdd\u97f3\u5fae\u987f\uff0c\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u63a5\u9053\uff1a\u201c\u81f3\u4eca\u5df2\u6709\u5341\u4e94\u5e74\u51fa\u5934\uff0c\u65b9\u5c0f\u65bd\u4e3b\u884c\u9053\u6c5f\u6e56\u4e4b\u8baf\u4f20\u51fa\uff0c\u8001\u8872\u5fc3\u4e2d\u5341\u5206\u6b22\u559c\uff0c\u5982\u975e\u4e3a\u4e86\u76d1\u89c6\u4e94\u9b54\uff0c\u4e0d\u8bb8\u4ed6\u4eec\u6697\u4f24\u6b66\u6797\u540c\u9053\uff0c\u8001\u8872\u65e9\u5728\u534a\u5e74\u4e4b\u524d\uff0c\u4fbf\u8d76\u5c11\u6797\u6765\u4e86\uff0c\u4e0d\u8fc7\uff0c\u6709\u4e00\u4ef6\u4e8b\uff0c\u8001\u8872\u8fd8\u5f97\u8bf4\u660e\uff0c\u8fd9\u53cc\u8c31\u4e43\u662f\u771f\u7684\uff0c\u4f46\u90a3\u5251\u4ee4\uff0c\u8001\u8872\u53d1\u73b0\u5374\u662f\u5047\u7684\uff01\u201d \u3000\u3000\u91d1\u9876\u795e\u5c3c\u5408\u5341\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u90a3\u662f\u8d2b\u5c3c\u9519\u602a\u5927\u5e08\u4e86\uff0c\u53ea\u4e0d\u77e5\u771f\u7684\u5251\u4ee4\u4f55\u5728\uff1f\u201d \u3000\u3000\u5165\u51e1\u5927\u5e08\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u5e08\u592a\u597d\u8bf4\uff01\u8001\u8872\u8d23\u4efb\u5df2\u5c3d\uff0c\u6b64\u540e\u56de\u8f6c\u5c11\u6797\uff0c\u4fbf\u8981\u5750\u90a3\u5341\u5e74\u6b7b\u5173\uff0c\u6b66\u6797\u540c\u9053\uff0c\u5c1a\u76fc\u5e08\u592a\u548c\u5c0f\u65bd\u4e3b\u591a\u591a\u7167\u5e94\u2026\u2026\u81f3\u4e8e\u771f\u7684\u5251\u4ee4\uff0c\u53ea\u6015\u5728\u90a3\u5316\u5f71\u4eba\u9b54\u8eab\u4e0a\u4e86\uff01\u201d \u3000\u3000\u8bdd\u97f3\u4e00\u843d\uff0c\u98de\u8eab\u4e0b\u53f0\uff0c\u626c\u957f\u800c\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u770b\u4e86\u624b\u4e2d\u5251\u4ee4\u548c\u53cc\u8c31\u4e00\u773c\uff0c\u987a\u624b\u63e3\u5165\u6000\u4e2d\uff0c\u5ffd\u7136\u8d70\u5411\u82cf\u5c0f\u5761\u9053\uff1a\u201c\u59d3\u82cf\u7684\uff0c\u4f60\u628a\u4ed6\u4eec\u600e\u6837\u4e86\uff1f\u6211\u90a3\u4f2f\u6bcd\u548c\u73de\u5144\u5f1f\u73b0\u5728\u4f55\u5904\uff1f\u201d \u3000\u3000\u82cf\u5c0f\u5761\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u6bcd\u5b50\u5747\u5728\u4e2d\u5cb3\u5e99\u5730\u7262\u4e4b\u4e2d\uff0c\u4f60\u5982\u60f3\u89c1\u4ed6\u4eec\uff0c\u4e0d\u59a8\u675f\u624b\u5c31\u7f1a\uff0c\u82cf\u67d0\u5e26\u4f60\u524d\u53bb\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u51b7\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u5148\u7236\u548c\u5927\u4f2f\u7236\u53ef\u662f\u6b7b\u5728\u4f60\u624b\uff1f\u5251\u4ee4\u4e3a\u4f55\u88ab\u4f60\u5f97\u53bb\uff1f\u201d \u3000\u3000\u82cf\u5c0f\u5761\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u65b9\u5929\u6210\u593a\u6211\u4e4b\u7231\uff0c\u65b9\u5929\u5b9c\u5374\u5728\u6211\u6740\u65b9\u5929\u6210\u4e4b\u540e\uff0c\u770b\u51fa\u82cf\u67d0\u672c\u6765\u9762\u76ee\uff0c\u4ed6\u4eec\u90fd\u6709\u53d6\u6b7b\u4e4b\u9053\uff0c\u82cf\u67d0\u6740\u4e86\u4ed6\u4eec\uff0c\u4e5f\u4e0d\u4e3a\u8fc7\uff01\u81f3\u4e8e\u5251\u4ee4\u4e48\uff1f\u90a3\u662f\u67f4\u82b3\u541b\u5077\u6765\u9001\u7ed9\u4e86\u533a\u533a\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5ffd\u7136\u76ee\u6bd7\u6b32\u88c2\uff0c\u5927\u559d\u4e00\u58f0\u9053\uff1a\u201c\u82cf\u5c0f\u5761\uff0c\u65b9\u67d0\u4eca\u65e5\u8981\u53d6\u51fa\u4f60\u7684\u5fc3\u809d\uff0c\u5960\u796d\u5148\u7236\u548c\u5927\u4f2f\u7236\u5728\u5929\u4e4b\u7075\u2026\u2026\u201d \u3000\u3000\u545b\u5577\u4e00\u58f0\uff0c\u957f\u5251\u5316\u6210\u4e00\u7f15\u7cbe\u5149\uff0c\u7535\u95ea\u800c\u51fa\u3002 \u3000\u3000\u4ed6\u6012\u6781\u51fa\u624b\uff0c\u81ea\u7136\u662f\u51f6\u731b\u65e0\u6bd4\uff0c\u82cf\u5c0f\u5761\u5403\u4e86\u4e00\u60ca\uff0c\u5de6\u624b\u62cd\u51fa\u4e00\u638c\uff0c\u53f3\u624b\u64a4\u51fa\u5b9d\u5251\uff0c\u5411\u4e0a\u67b6\u53bb\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5fc3\u4e2d\u5145\u6ee1\u4e86\u6012\u7130\uff0c\u5251\u52bf\u4e00\u53d1\uff0c\u4fbf\u5982\u957f\u6c5f\u5927\u6cb3\u4e00\u822c\uff0c\u53ea\u89c1\u5bd2\u5149\u7535\u95ea\uff0c\u4e1d\u4e1d\u52b2\u6c14\uff0c\u53ea\u8feb\u5f97\u53f0\u4e0a\u8bf8\u4eba\uff0c\u5168\u90fd\u8d70\u5411\u4e00\u8fb9\u3002 \u3000\u3000\u82cf\u5c0f\u5761\u7684\u6b66\u529f\uff0c\u6bd4\u90a3\u90d1\u5927\u521a\u597d\u4e0d\u4e86\u591a\u5c11\uff0c\u600e\u5f53\u5f97\u8d77\u8fd9\u65b9\u96ea\u5b9c\u7684\u4e00\u8f6e\u731b\u653b\uff1f \u3000\u3000\u5341\u62db\u4e0d\u5230\uff0c\u53ea\u542c\u5f97\u65b9\u96ea\u5b9c\u5927\u559d\u4e00\u58f0\u9053\uff1a\u201c\u82cf\u5c0f\u5761\uff0c\u62ff\u547d\u6765\u2026\u2026\u201d \u3000\u3000\u7d27\u8ddf\u7740\u4f46\u95fb\u53f6\u901a\u4e00\u58f0\uff0c\u82cf\u5c0f\u5761\u524d\u80f8\u6d1e\u7a7f\u4e86\u4e00\u4e2a\u5927\u5b54\uff01\u9c9c\u8840\u548c\u7740\u90a3\u4e94\u810f\uff0c\u6d41\u4e86\u4e00\u53f0\u3002 \u3000\u3000\u65b9\u96ea\u5b9c\u5927\u6b65\u4e0a\u524d\uff0c\u4e00\u5251\u6311\u8d77\u82cf\u5c0f\u5761\u7684\u5fc3\u810f\uff0c\u5e76\u4ece\u4ed6\u6000\u4e2d\u627e\u51fa\u771f\u7684\u5251\u4ee4\uff0c\u8f6c\u9762\u5411\u7740\u53f0\u4e0b\u7fa4\u8c6a\u9053\uff1a\u201c\u4e94\u5723\u6559\u5df2\u7136\u74e6\u89e3\u51b0\u6d88\uff0c\u5316\u5f71\u4eba\u9b54\u4e5f\u5df2\u4f0f\u8bdb\uff0c\u6b66\u6797\u4e2d\u5341\u5e74\u4e4b\u540e\uff0c\u60f3\u5fc5\u4e0d\u4f1a\u6709\u4ec0\u4e48\u7eb7\u6270\u4e86\uff01\u201d \u3000\u3000\u53f0\u4e0b\uff0c\u8fd9\u65f6\u5ffd\u7136\u54cd\u8d77\u4e86\u4e00\u9635\u6b22\u547c\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u770b\u4e86\u8f9b\u67cf\u516c\u4e00\u773c\u9053\uff1a\u201c\u8f9b\u8001\uff0c\u90a3\u5929\u9e64\u3001\u7fa4\u9f99\u4e8c\u8c31\u4e0a\u5217\u540d\u4e4b\u4eba\uff0c\u4eca\u65e5\u5230\u4e86\u591a\u5c11\uff1f\u201d \u3000\u3000\u8f9b\u67cf\u516c\u9053\uff1a\u201c\u9664\u53bb\u5df2\u6545\u4e4b\u4eba\uff0c\u5927\u6982\u4e0d\u8db3\u5341\u4eba\u4e4b\u6570\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u5251\u4ea4\u5de6\u624b\uff0c\u5c06\u90a3\u63e3\u5728\u6000\u4e2d\u5047\u6e38\u9f99\u5251\u4ee4\u548c\u5929\u9e64\u3001\u7fa4\u9f99\u4e8c\u5f20\u8c31\u724c\u53d6\u51fa\uff0c\u5ffd\u7136\u4ef0\u5929\u53d1\u51fa\u4e00\u58f0\u957f\u5578\uff0c\u7528\u90a3\u5251\u4ee4\u5c06\u53cc\u8c31\u65a9\u5f97\u7c89\u788e\uff01 \u3000\u3000\u8f9b\u67cf\u516c\u770b\u5f97\u5446\u4e86\u4e00\u5446\uff0c\u53ea\u89c1\u65b9\u96ea\u5b9c\u53c8\u5c06\u90a3\u4e24\u652f\u5251\u4ee4\u629b\u5411\u7a7a\u4e2d\uff0c\u66f2\u6307\u8fde\u5f39\uff0c\u5c06\u90a3\u5251\u4ee4\u9707\u6210\u5341\u4e94\u516d\u7247\uff01 \u3000\u3000\u8f9b\u67cf\u516c\u5ffd\u5730\u5927\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u6bc1\u8c31\u788e\u5251\uff0c\u5176\u5fc3\u53ef\u611f\u53ef\u656c\uff0c\u8001\u673d\u8fd9\u53a2\u5148\u884c\u8c22\u8fc7\u4e86\uff01\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u767d\u62c2\u9053\u957f\u548c\u5c11\u6797\u638c\u95e8\u4ee5\u53ca\u4e94\u9b54\u4e2d\u4ec5\u5b58\u7684\u56db\u9b54\uff0c\u4e5f\u5168\u90fd\u8083\u7136\u8d77\u5ea7\uff0c\u8fde\u8fde\u5ff5\u4f5b\u3002 \u3000\u3000\u53f0\u4e0b\u7fa4\u8c6a\uff0c\u66f4\u662f\u6fc0\u52a8\u4e0d\u5df2\uff01 \u3000\u3000\u65b9\u96ea\u5b9c\u5411\u91d1\u9876\u795e\u5c3c\u6df1\u6df1\u4e00\u793c\uff0c\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u5f1f\u5b50\u8981\u53bb\u5730\u7262\u4e4b\u4e2d\u6551\u90a3\u5927\u4f2f\u6bcd\uff0c\u73de\u5144\u5f1f\u548c\u77f3\u4e09\u53d4\uff0c\u4e00\u6708\u4e4b\u540e\uff0c\u5f1f\u5b50\u63a5\u4e86\u5bb6\u6bcd\uff0c\u6253\u7b97\u53bb\u7956\u83b9\uff0c\u5960\u796d\u5148\u7236\u548c\u4f2f\u7236\u5728\u5929\u4e4b\u7075\u2026\u2026\u201d \u3000\u3000\u91d1\u9876\u795e\u5c3c\u51c4\u7136\u4e00\u7b11\u9053\uff1a\u201c\u5f88\u597d\uff01\u4f60\u53bb\u5427\uff01\u4f46\u83ab\u5fd8\u8bb0\uff0c\u8d2b\u5c3c\u5df2\u7136\u8bf7\u4e86\u5f53\u5e74\u5217\u540d\u5929\u9e64\u3001\u7fa4\u9f99\u4e8c\u8c31\u9ad8\u624b\u548c\u4ed6\u4eec\u7684\u540e\u4eba\uff0c\u5728\u4e09\u6708\u540e\uff0c\u524d\u5f80\u4f60\u5e08\u7236\u57cb\u8eab\u4e4b\u5904\uff0c\u5bb6\u4e2d\u4e8b\u60c5\u529e\u5b8c\uff0c\u4f60\u548c\u5b89\u59d1\u5a18\u8981\u65e9\u4e9b\u8d76\u6765\uff01\u201d \u3000\u3000\u65b9\u96ea\u5b9c\u606d\u5e94\u4e86\u4e00\u58f0\uff1a\u201c\u5f1f\u5b50\u9075\u547d\u2026\u2026\u201d\u56de\u987e\u4e86\u5b89\u5c0f\u840d\u4e00\u773c\uff0c\u5728\u90d1\u5927\u521a\u548c\u5b89\u5c0f\u840d\u966a\u540c\u4e4b\u4e0b\uff0c\u6311\u7740\u90a3\u9897\u4ec7\u4eba\u7684\u5fc3\u810f\u6b65\u4e0b\u6728\u53f0\uff0c\u5411\u5cb3\u5e99\u8d70\u53bb\uff01 \u3000\u3000\u7fa4\u8c6a\u7684\u6b22\u547c\u4e4b\u58f0\uff0c\u5728\u4ed6\u8eab\u540e\u6e10\u6e10\u6d88\u5931\uff0c\u53ea\u6709\u5b89\u5c0f\u840d\u5e7d\u5e7d\u7684\u53d1\u9999\uff0c\u51b2\u6563\u4e86\u8840\u6c14\u8165\u5473\uff0c\u7f13\u7f13\u5730\u6c81\u5165\u4e86\u4ed6\u7684\u9f3b\u4e2d\uff0c\u5f53\u4ed6\u6253\u5f00\u5730\u7262\u7684\u77f3\u95e8\u4ee5\u540e\uff0c\u7ec8\u4e8e\u6ef4\u843d\u4e86\u611f\u594b\u7684\u773c\u6cea\uff0c\u53d1\u51fa\u4e86\u4e00\u58f0\u51c4\u51c9\u7684\u82e6\u7b11\uff01 \u3000\u3000\uff08\u5168\u4e66\u5b8c\xb7\u79cb\u8349\u7f16\u8f91\uff09"}),showOnHomepage:!0}]}}]);