"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[77347],{5671:(e,n,t)=>{t.r(n),t.d(n,{default:()=>a});var s=t(74848);const a=[{name:"\u7b2c\u4e00\u7ae0\u3000\u501f\u5c38\u8fd8\u9b42",content:(0,s.jsx)(s.Fragment,{children:"\u8fd9\u4e0d\u662f\u9b3c\u6545\u4e8b\uff0c\u5374\u6bd4\u4e16\u4e0a\u4efb\u4f55\u9b3c\u6545\u4e8b\u90fd\u79bb\u5947\u53ef\u6016\u3002 \u3000\u3000\u4e5d\u6708\u4e8c\u5341\u516b\uff0c\u7acb\u51ac\u3002 \u3000\u3000\u8fd9\u5929\u5728\u201c\u63b7\u676f\u5c71\u5e84\u201d\u53d1\u751f\u7684\u4e8b\uff0c\u695a\u7559\u9999\u82e5\u975e\u4eb2\u773c\u89c1\u5230\uff0c\u6015\u6c38\u8fdc\u4e5f\u65e0\u6cd5\u76f8\u4fe1\u3002 \u3000\u3000\u201c\u63b7\u676f\u5c71\u5e84\u201d\u5728\u677e\u6c5f\u5e9c\u57ce\u5916\uff0c\u8ddd\u79bb\u540d\u95fb\u5929\u4e0b\u7684\u79c0\u91ce\u6865\u8fd8\u4e0d\u5230\u53c1\u91cc\uff0c\u6bcf\u5e74\u51ac\u81f3\u524d\u5f8c\uff0c\u695a\u7559 \u9999\u51e0\u4e4e\u90fd\u8981\u5230\u8fd9\u91cc\u6765\u5f80\u51e0\u5929\uff0c\u56e0\u4e3a\u4ed6\u4e5f\u548c\u5b63\u9e70\u5148\u751f\u5f20\u7ff0\u4e00\u6837\uff0c\u79cb\u98ce\u4e00\u8d77\uff1b\u5c31\u6709\u4e86\u9c88\u4e4b\u601d\uff0c\u56e0 \u4e3a\u5929\u4e0b\u552f\u6709\u677e\u6c5f\u79c0\u91ce\u6865\u4e0b\u6240\u4ea7\u7684\u9c88\u624d\u662f\u56db\u816e\u7684\uff0c\u800c\u6c5f\u6e56\u4e2d\u4eba\u8c01\u90fd\u77e5\u9053\uff0c\u201c\u63b7\u676f\u5c71\u5e84\u201d\u7684\u4e3b\u4eba \u5de6\u4e8c\u7237\u9664\u4e86\u638c\u6cd5\u51a0\u7edd\u6c5f\u5357\u5916\uff0c\u4eb2\u624b\u70f9\u8c03\u7684\u9c88\u9c7c\u810d\u66f4\u662f\u5999\u7edd\u5929\u4e0b\u3002 \u3000\u3000\u6c5f\u6e56\u4e2d\u4eba\u4e5f\u90fd\u77e5\u9053\uff0c\u666e\u5929\u4e4b\u4e0b\u80fd\u4ee4\u5de6\u4e8c\u7237\u4eb2\u81ea\u4e0b\u53a8\u623f\uff0c\u6d17\u624b\u505a\u9c7c\u7fb9\u7684\uff0c\u603b\u5171\u4e5f\u4e0d\u8fc7\u53ea\u6709 \u4e24\u4e2a\u4eba\u800c\u5df2\u3002 \u3000\u3000\u695a\u7559\u9999\u6070\u5de7\u5c31\u662f\u8fd9\u4e24\u4eba\u5176\u4e2d\u4e4b\u4e00\u3002 \u3000\u3000\u4f46\u8fd9\u6b21\u695a\u7559\u9999\u5230\u201c\u63b7\u676f\u5c71\u5e84\u201d\u6765\uff0c\u5e76\u6ca1\u6709\u5c1d\u5230\u5de6\u4e8c\u7237\u5999\u624b\u4eb2\u8c03\u7684\u9c88\u9c7c\u810d\uff0c\u5374\u9047\u5230\u4e86\u4e00\u4ef6 \u5e73\u751f\u4ece\u672a\u9047\u5230\u8fc7\u7684\uff0c\u6700\u8352\u5510\u3001\u6700\u79bb\u5947\u3001\u6700\u795e\u79d8\u3001\u4e5f\u6700\u53ef\u6016\u7684\u4e8b\u3002 \u3000\u3000\u4ed6\u4ece\u6765\u4e5f\u4e0d\u4fe1\u4e16\u4e0a\u7adf\u771f\u6709\u8fd9\u79cd\u4e8b\u53d1\u751f\u3002 \u3000\u3000\u5de6\u4e8c\u7237\u4e5f\u548c\u695a\u7559\u9999\u4e00\u6837\uff01\u662f\u6700\u61c2\u5f97\u4eab\u53d7\u751f\u547d\u7684\u4eba\uff0c\u4ed6\u4e0d\u6c42\u5c01\u4faf\uff0c\u4f46\u6c42\u5e38\u4e50\uff0c\u6240\u4ee5\u81ea\u53f7 \u201c\u8f7b\u4faf\u201d\u3002 \u3000\u3000\u201c\u63b7\u676f\u5c71\u5e84\u201d\u4e2d\u6709\u6c5f\u5357\u6700\u7f8e\u7684\u6b4c\u5993\uff0c\u6700\u9187\u7684\u7f8e\u9152\uff0c\u9a6c\u53a9\u4e2d\u6709\u5357\u4e03\u7701\u8dd1\u5f97\u6700\u5feb\u7684\u5343\u91cc\u9a6c\uff0c \u5927\u5385\u4e2d\u4e5f\u6709\u6700\u98ce\u96c5\u7684\u98df\u5ba2\u3002\u4f46\u5de6\u4e8c\u7237\u6700\u5f97\u610f\u7684\u4e8b\u5374\u8fd8\u4e0d\u662f\u8fd9\u4e9b\u3002 \u3000\u3000\u5de6\u4e8c\u7237\u5e73\u751f\u6700\u5f97\u610f\u7684\u6709\u53c1\u4ef6\u4e8b\u3002 \u3000\u3000\u7b2c\u4e00\u4ef6\u4ee4\u4ed6\u5f97\u610f\u7684\u4e8b\uff0c\u5c31\u662f\u4ed6\u6709\u695a\u7559\u9999\u8fd9\u79cd\u670b\u53cb\uff0c\u4ed6\u5e38\u8bf4\u5b81\u53ef\u780d\u4e0b\u81ea\u5df2\u7684\u5de6\u624b\uff0c\u4e5f\u4e0d\u613f \u5931\u53bb\u695a\u7559\u9999\u8fd9\u4e2a\u670b\u53cb\u3002 \u3000\u3000\u7b2c\u4e8c\u4ef6\u4ee4\u4ed6\u5f97\u610f\u7684\u4e8b\u662f\u4ed6\u6709\u4e2a\u4e16\u4e0a\u6700\u53ef\u6015\u7684\u4ec7\u654c\u3001\u90a3\u5c31\u662f\u53f7\u79f0\u201c\u5929\u4e0b\u7b2c\u4e00\u5251\u5ba2\u201d\u7684\u201c\u8840 \u8863\u4eba\u201d\u859b\u5927\u4fa0\u3002\u4ed6\u548c\u859b\u8863\u4eba\u505a\u4e86\u53c1\u5341\u5e74\u7684\u51a4\u5bb6\u5bf9\u5934\uff0c\u5c45\u7136\u8fd8\u80fd\u8212\u8212\u670d\u670d\u7684\u6d3b\u5230\u73b0\u5728\uff0c\u859b\u8863\u4eba \u867d\u7136\u5a01\u9707\u5929\u4e0b\uff0c\u5374\u4e5f\u5c06\u4ed6\u65e0\u53ef\u5948\u4f55\u3002\u8fd9\u4ef6\u4e8b\u5de6\u4e8c\u7237\u6bcf\u4e00\u63d0\u8d77\uff0c\u5c31\u5fcd\u4e0d\u4f4f\u8981\u5f00\u6000\u5927\u7b11\u3002 \u3000\u3000\u7b2c\u53c1\u4ef6\u4e8b\uff0c\u4e5f\u662f\u4ed6\u6700\u6700\u5f97\u610f\u7684\u4e00\u4ef6\u4e8b\uff0c\u90a3\u5c31\u662f\u4ed6\u6709\u4e2a\u6700\u806a\u660e\u3001\u6700\u6f02\u4eae\u3001\u4e5f\u6700\u542c\u8bdd\u7684\u4e56\u5973 \u513f\u3002\u5de6\u4e8c\u7237\u6ca1\u6709\u513f\u5b50\uff0c\u4f46\u5374\u4ece\u6765\u4e0d\u89c9\u5f97\u9057\u61be\uff0c\u53ea\u56e0\u4ed6\u8ba4\u4e3a\u4ed6\u8fd9\u5973\u513f\u6bd4\u522b\u4eba\u4e24\u767e\u4e2a\u513f\u5b50\u52a0\u8d77\u6765 \u90fd\u5f3a\u80dc\u5341\u500d\u3002\u5de6\u660e\u73e0\u4e5f\u7684\u786e\u4ece\u6765\u6ca1\u6709\u4ee4\u5979\u7236\u4eb2\u5931\u671b\u8fc7\u3002\u5979\u4ece\u5c0f\u5230\u5927\uff0c\u51e0\u4e4e\u4ece\u6ca1\u6709\u751f\u8fc7\u75c5\uff0c\u66f4 \u7edd\u6ca1\u6709\u60f9\u8fc7\u4efb\u4f55\u9ebb\u70e6\uff0c\u73b0\u5728\u5e74\u5df2\u5341\u516b\u5c81\uff0c\u5374\u4ecd\u548c\u4e24\u5c81\u65f6\u4e00\u6837\u53ef\u7231\uff0c\u4e00\u6837\u542c\u8bdd\u3002 \u3000\u3000\u5979\u7684\u6b66\u529f\u867d\u7136\u5e76\u4e0d\u5341\u5206\u9ad8\u660e\uff0c\u4f46\u5728\u5973\u4eba\u4e2d\u5df2\u53ef\u7b97\u662f\u4f7c\u4f7c\u8005\u4e86\uff0c\u5230\u5916\u9762\u53bb\u8d70\u4e86\u4e24\u8d9f\u4e4b\u5f8c\uff0c \u4e5f\u6709\u4e86\u4e2a\u5f88\u54cd\u4eae\u7684\u540d\u5934\uff0c\u53eb\u201c\u7389\u4ed9\u5a03\u201d\u3002 \u3000\u3000\u867d\u7136\u5927\u5bb6\u90fd\u77e5\u9053\uff0c\u6c5f\u6e56\u4e2d\u4eba\u5982\u6b64\u6367\u5979\u7684\u573a\uff0c\u81f3\u5c11\u6709\u4e00\u534a\u662f\u770b\u5728\u5de6\u4e8c\u7237\u7684\u9762\u4e0a\uff0c\u4f46\u5de6\u4e8c\u7237 \u81ea\u5df2\u5374\u4e00\u70b9\u4e5f\u4e0d\u5728\u610f\u3002 \u3000\u3000\u5de6\u4e8c\u7237\u5e76\u4e0d\u5e0c\u671b\u4ed6\u5973\u513f\u662f\u4e2a\u5973\u9b54\u738b\u3002 \u3000\u3000\u4f55\u51b5\uff0c\u5979\u4e5f\u5e76\u6ca1\u6709\u592a\u591a\u7684\u65f6\u95f4\u53bb\u7ec3\u6b66\uff0c\u5979\u4e0d\u4f46\u8981\u966a\u5979\u7236\u4eb2\u4e0b\u68cb\u3001\u559d\u9152\uff0c\u8fd8\u8981\u4e3a\u5979\u7236\u4eb2\u629a \u7434\u3001\u63d2\u82b1\u3001\u586b\u8bcd\u3001\u541f\u8bd7\u2014\u2014\u5979\u65e0\u8bba\u505a\u4efb\u4f55\u4e8b\uff0c\u90fd\u662f\u4e3a\u5979\u7236\u4eb2\u505a\u7684\uff0c\u56e0\u4e3a\u5979\u751f\u547d\u4e2d\u8fd8\u6ca1\u6709\u7b2c\u4e8c \u4e2a\u7537\u4eba\u3002 \u3000\u3000\u603b\u800c\u8a00\u4e4b\uff0c\u8fd9\u4f4d\u5de6\u59d1\u5a18\u6b63\u662f\u6bcf\u4e2a\u7236\u4eb2\u5fc3\u76ee\u4e2d\u6240\u671f\u671b\u7684\u90a3\u79cd\u4e56\u5973\u513f\uff0c\u5de6\u4e8c\u7237\u51e0\u4e4e\u4ece\u6765\u6ca1\u6709 \u4e3a\u5979\u64cd\u8fc7\u5fc3\u3002 \u3000\u3000\u2014\u2014\u76f4\u5230\u76ee\u524d\u4e3a\u6b62\uff0c\u5de6\u4e8c\u7237\u8fd8\u672a\u4e3a\u5979\u64cd\u8fc7\u5fc3\u3002 \u3000\u3000\u4f46\u73b0\u5728\uff0c\u73b0\u5728\u8fd9\u4ef6\u6700\u8352\u5510\u3001\u6700\u79bb\u5947\u3001\u6700\u795e\u79d8\u3001\u6700\u53ef\u6016\u3001\u51e0\u4e4e\u4ee4\u4eba\u5b8c\u5168\u4e0d\u80fd\u76f8\u4fe1\u7684\u4e8b\uff0c\u6b63 \u662f\u53d1\u751f\u5728\u5979\u8eab\u4e0a\u3002 \u3000\u3000\u4e5d\u6708\uff0c\u5bd2\u610f\u5df2\u7ecf\u5f88\u91cd\u4e86\u3002 \u3000\u3000\u4f46\u65e0\u8bba\u5728\u591a\u51b7\u7684\u5929\u6c14\u91cc\uff0c\u53ea\u8981\u4e00\u8d70\u8fdb\u201c\u63b7\u676f\u5c71\u5e84\u201d\uff0c\u5c31\u4f1a\u751f\u51fa\u4e00\u79cd\u6e29\u6696\u8212\u9002\u7684\u611f\u89c9\uff0c\u5c31 \u50cf\u75b2\u5026\u7684\u6d6a\u5b50\u56de\u5230\u4e86\u5bb6\u4e00\u6837\u3002 \u3000\u3000\u56e0\u4e3a\u201c\u63b7\u676f\u5c71\u5e84\u201d\u4e2d\u4e0a\u4e0a\u4e0b\u4e0b\u6bcf\u4e2a\u4eba\uff0c\u9762\u4e0a\u90fd\u5e26\u7740\u6b22\u4e50\u9762\u597d\u5ba2\u7684\u7b11\u5bb9\uff0c\u5373\u4f7f\u662f\u5b88\u5728\u95e8\u53e3 \u7684\u95e8\u4e01\uff0c\u5bf9\u5ba2\u4eba\u4e5f\u662f\u90a3\u9ebd\u800c\u6709\u793c\uff0c\u4f60\u8fd8\u672a\u8d70\u8fdb\u5927\u95e8\uff0c\u5c31\u4f1a\u55c5\u5230\u4e00\u9635\u9635\u9152\u9999\u3001\u83dc\u9999\u3001\u8102\u7c89\u7684\u5e7d \u9999\u3001\u82b1\u6728\u7684\u6e05\u9999\u5c31\u4f1a\u542c\u5230\u4e00\u9635\u9635\u60a0\u626c\u7684\u4e1d\u7af9\u7ba1\u5f26\u58f0\uff0c\u8c6a\u723d\u7684\u7b11\u58f0\uff0c\u548c\u78b0\u676f\u65f6\u53d1\u751f\u7684\u6e05\u8106\u58f0 \u54cd\u3002 \u3000\u3000\u8fd9\u4e9b\u58f0\u97f3\u50cf\u662f\u5728\u544a\u8bc9\u4f60\uff0c\u6240\u6709\u7684\u6b22\u4e50\u90fd\u5728\u7b49\u7740\u4f60\uff0c\u90a3\u79cd\u611f\u89c9\u53c8\u597d\u50cf\u5c06\u4e00\u53cc\u8d70\u5f97\u53d1\u9ebb\u7684\u811a \u6ce1\u5165\u6e29\u6c34\u91cc\u3002\u4f46\u8fd9\u6b21\uff0c\u695a\u7559\u9999\u8fd8\u8fdc\u5728\u6570\u5341\u4e08\u5916\uff0c\u5c31\u89c9\u5f97\u60c5\u51b5\u4e0d\u5bf9\u4e86\u3002 \u3000\u3000\u201c\u63b7\u676f\u5c71\u5e84\u201d\u90a3\u4e24\u6247\u7ec8\u5e74\u5e38\u5f00\u7684\u9ed1\u6f06\u5927\u95e8\uff0c\u6b64\u523b\u7adf\u7d27\u95ed\u7740\u3002\u95e8\u53e3\u7adf\u51b7\u6e05\u6e05\u7684\u77a7\u4e0d\u89c1\u8f66 \u9a6c\u3002 \u3000\u3000\u695a\u7559\u9999\u6572\u4e86\u534a\u5929\u95e8\uff0c\u624d\u6709\u4e2a\u8001\u5934\u5b50\u51fa\u6765\u5f00\u95e8\uff0c\u4ed6\u89c1\u5230\u695a\u7559\u9999\uff0c\u867d\u7136\u7acb\u523b\u5c31\u9732\u51fa\u6b22\u8fce\u7684\u7b11 \u5bb9\uff0c\u4f46\u5374\u663e\u7136\u7b11\u5f97\u5f88\u52c9\u5f3a\u3002 \u3000\u3000\u6614\u65e5\u90a3\u79cd\u6b22\u4e50\u7684\u6c14\u6c1b\uff0c\u5982\u4eca\u7adf\u8fde\u4e00\u4e1d\u4e5f\u770b\u4e0d\u5230\u4e86\u3002 \u3000\u3000\u9662\u5b50\u91cc\u5c45\u7136\u5806\u6ee1\u4e86\u843d\u53f6\u672a\u626b\uff0c\u4e00\u9635\u9635\u79cb\u98ce\u5377\u8d77\u4e86\u843d\u53f6\uff0c\u5e26\u7ed9\u4eba\u4e00\u79cd\u8bf4\u4e0d\u51fa\u7684\u51c4\u51c9\u8427\u7d22\u4e4b \u610f\u3002 \u3000\u3000\u7b49\u5230\u695a\u7559\u9999\u770b\u5230\u5de6\u8f7b\u4faf\u65f6\uff0c\u66f4\u5403\u4e86\u4e00\u60ca\u3002 \u3000\u3000\u8fd9\u4f4d\u6c5f\u6e56\u5927\u8c6a\u7ea2\u6da6\u7684\u9762\u8272\uff0c\u7adf\u5df2\u53d8\u5f97\u82cd\u767d\u800c\u6194\u60b4\uff0c\u8fde\u773c\u775b\u90fd\u51f9\u4e86\u4e0b\u53bb\uff0c\u624d\u4e00\u5e74\u4e0d\u89c1\uff0c\u4ed6 \u597d\u50cf\u5c31\u5df2\u8001\u4e86\u5341\u51e0\u5c81\u3002 \u3000\u3000\u5728\u4ed6\u8138\u4e0a\u5df2\u627e\u4e0d\u51fa\u4e1d\u6beb\u6614\u65e5\u90a3\u79cd\u8c6a\u723d\u4e50\u5929\u7684\u5f71\u5b50\uff0c\u52c9\u5f3a\u88c5\u51fa\u6765\u7684\u7b11\u5bb9\u4e5f\u63a9\u4e0d\u4f4f\u4ed6\u7709\u5b87\u95f4 \u90a3\u79cd\u5fe7\u90c1\u6101\u82e6\u4e4b\u8272\u3002 \u3000\u3000\u5927\u5385\u91cc\u4e5f\u662f\u51b7\u6e05\u6e05\u7684\uff0c\u5ea7\u4e0a\u5ba2\u5df2\u6563\uff0c\u76db\u9152\u7684\u91d1\u6a3d\u4e2d\u5374\u79ef\u6ee1\u4e86\u7070\u5c18\uff0c\u751a\u81f3\u8fde\u6881\u4e0a\u7684\u71d5\u5b50\u90fd \u5df2\u98de\u53bb\u4e86\u522b\u5bb6\u9662\u91cc\u3002 \u3000\u3000\u201c\u63b7\u676f\u5c71\u5e84\u201d\u4e2d\u7a76\u7adf\u53d1\u751f\u4e86\u4ec0\u9ebd\u60ca\u4eba\u53d8\u6545\uff0c\u600e\u9ebd\u4f1a\u6210\u5982\u6b64\u6a21\u6837\uff0c\u695a\u7559\u9999\u60ca\u5947\u5f97\u51e0\u4e4e\u8fde\u8bdd \u90fd\u8bf4\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u5de6\u4e8c\u7237\u7d27\u7d27\u63e1\u4f4f\u4e86\u4ed6\u7684\u624b\uff0c\u4e5f\u662f\u4e45\u4e45\u90fd\u8bf4\u4e0d\u51fa\u8bdd\u3002 \u3000\u3000\u695a\u7559\u9999\u5fcd\u4e0d\u4f4f\u8bd5\u63a2\u7740\u95ee\u9053\uff0c\u201c\u4e8c\u54e5\u4f60\u2026\u2026\u4f60\u8fd1\u6765\u8fd8\u597d\u5417\uff1f\u201d \u3000\u3000\u5de6\u4e8c\u7237\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u597d\u2026\u2026\u201d \u3000\u3000\u4ed6\u4e00\u8fde\u5c06\u8fd9\u201c\u597d\u201d\u5b57\u8bf4\u4e86\u4e03\u516b\u904d\uff0c\u76ee\u4e2d\u4f3c\u5df2\u6709\u70ed\u6cea\u593a\u7736\u800c\u51fa\uff0c\u628a\u695a\u7559\u9999\u7684\u624b\u63e1\u5f97\u66f4\u7d27\uff0c \u55c4\u58f0\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\u660e\u73e0\uff01\u660e\u73e0\u5979\u2026\u2026\u201d\u695a\u7559\u9999\u52a8\u5bb9\u9053\uff1a\u201c\u660e\u73e0\u5979\u600e\u9ebd\u6837\u4e86\uff1f\u201d \u3000\u3000\u5de6\u8f7b\u4faf\u6c89\u91cd\u7684\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9eef\u7136\u9053\uff1a\u201c\u5979\u75c5\u4e86\uff0c\u75c5\u5f97\u5f88\u91cd\u3002\u201d \u3000\u3000\u5176\u5b9e\u7528\u4e0d\u7740\u4ed6\u8bf4\uff0c\u695a\u7559\u9999\u4e5f\u77e5\u9053\u5de6\u660e\u73e0\u5fc5\u5b9a\u75c5\u5f97\u5f88\u91cd\u7684\uff0c\u5426\u5219\u8fd9\u4e50\u5929\u7684\u8001\u4eba\u53c8\u600e\u4f1a\u5982\u6b64 \u6101\u82e6\u3002 \u3000\u3000\u695a\u7559\u9999\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u5e74\u8f7b\u4eba\u75c5\u4e00\u573a\u7b97\u5f97\u4e86\u4ec0\u9ebd\uff1f\u75c5\u597d\u4e86\u53cd\u800c\u5403\u5f97\u66f4\u591a\u4e9b\u3002\u201d \u3000\u3000\u5de6\u8f7b\u4faf\u6447\u7740\u5934\uff0c\u957f\u53f9\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u9053\uff0c\u4f60\u4e0d\u77e5\u9053\uff0c\u8fd9\u5b69\u5b50\u751f\u7684\u75c5\uff01\u662f\u2026\u2026\u662f\u4e00\u79cd\u602a \u75c5\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u602a\u75c5\uff1f\u201d \u3000\u3000\u5de6\u8f7b\u4faf\u9053\uff1a\u201c\u5979\u8eba\u5728\u5e8a\u4e0a\uff0c\u70b9\u6c34\u672a\u8fdb\uff0c\u7c92\u7c73\u672a\u6cbd\uff0c\u4e0d\u5403\u4e0d\u559d\u5df2\u7ecf\u5feb\u4e00\u4e2a\u6708\u4e86\uff0c\u5c31\u7b97\u4f60\u6211 \u4e5f\u7981\u4e0d\u8d77\u8fd9\u9ebd\u6298\u78e8\u7684\uff0c\u4f55\u51b5\u5979\u2026\u2026\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u75c5\u56e0\u67e5\u51fa\u6765\u4e86\u5417\uff1f\u201d \u3000\u3000\u5de6\u8f7b\u4faf\u9053\uff1a\u201c\u6211\u5df2\u5c06\u6c5f\u5357\u7684\u540d\u533b\u90fd\u627e\u6765\u4e86\uff0c\u5374\u8fd8\u662f\u67e5\u4e0d\u51fa\u8fd9\u662f\u4ec0\u9ebd\u75c5\uff0c\u6709\u7684\u4eba\u628a\u4e86\u8109\uff0c \u751a\u81f3\u8fde\u65b9\u5b50\u90fd\u4e0d\u80af\u5f00\uff0c\u82e5\u975e\u9760\u5f20\u7b80\u658b\u6bcf\u5929\u4e00\u5e16\u7eed\u547d\u4e38\u4fdd\u4f4f\u4e86\u5979\u8fd9\u6761\u5c0f\u547d\uff0c\u8fd9\u5b69\u5b50\u5982\u4eca\u53ea\u6015\u65e9 \u5df2\u2026\u2026\u65e9\u5df2\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u54fd\u54bd\uff0c\u8001\u6cea\u5df1\u5fcd\u4e0d\u4f4f\u6d41\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e8c\u7237\u7684\u5f20\u7b80\u658b\uff0c\u53ef\u662f\u90a3\u4f4d\u53f7\u79f0\u2018\u4e00\u6307\u5224\u751f\u6b7b\u2019\u7684\u795e\u533b\u540d\u4fa0\u7b80\u658b\u5148\u751f\u3002\u201d \u3000\u3000\u5de6\u8f7b\u5019\u9053\uff1a\u201c\u55ef\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5c55\u989c\u9053\uff1a\u201c\u82e5\u662f\u8fd9\u4f4d\u8001\u5148\u751f\u6765\u4e86\uff0c\u4e8c\u54e5\u8fd8\u6709\u4ec0\u9ebd\u4e0d\u653e\u5fc3\u7684\uff0c\u53ea\u8981\u4ed6\u8001\u5148\u751f\u80af\u51fa \u624b\uff0c\u5929\u4e0b\u8fd8\u6709\u4ec0\u9ebd\u6cbb\u4e0d\u597d\u7684\u75c5\u3002\u201d \u3000\u3000\u5de6\u8f7b\u4faf\u53f9\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u9053\u2026\u2026\u4f60\u4e0d\u77e5\u9053\uff0c\u4ed6\u672c\u6765\u4e5f\u4e0d\u80af\u5f00\u65b9\u5b50\u7684\uff0c\u53ea\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u7a81\u89c1\u4e00\u4f4d\u9762\u5bb9\u6e05\u77bf\uff0c\u76ee\u5149\u70af\u70af\u7684\u534e\u773c\u8001\u4eba\u5306\u5306\u8d70\u4e86\u8fdb\u6765\uff0c\u5411\u695a\u7559\u9999\u70b9\u70b9\u5934\uff0c\u5c31\u5306\u5306\u8d70\u5230 \u5de6\u8f7b\u4faf\u9762\u524d\uff0c\u5c06\u4e00\u7c92\u4e38\u836f\u585e\u5165\u4ed6\u5634\u91cc\uff0c\u9053\uff1a\u201c\u541e\u4e0b\u53bb\u3002\u201d \u3000\u3000\u5de6\u8f7b\u4faf\u4e0d\u7531\u81ea\u4e3b\u541e\u4e0b\u4e86\u4e38\u836f\uff0c\u8bb6\u7136\u9053\uff1a\u201c\u8fd9\u662f\u4e3a\u4e86\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u8001\u4eba\u5374\u5df2\u8f6c\u56de\u5934\uff0c\u9053\uff1a\u201c\u968f\u6211\u6765\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u8ba4\u5f97\u8fd9\u8001\u4eba\u6b63\u662f\u540d\u6ee1\u5929\u4e0b\u7684\u7b80\u658b\u5148\u751f\uff0c\u89c1\u5230\u8fd9\u79cd\u795e\u60c5\uff0c\u695a\u7559\u9999\u5df1\u9690\u9690\u89c9\u51fa\u4e8b\u60c5\u4e0d \u5999\u4e86\u3002 \u3000\u3000\u53c1\u4e2a\u4eba\u5306\u5306\u8d70\u4eba\u5f8c\u56ed\uff0c\u53ea\u89c1\u83ca\u82b1\u4e1b\u4e2d\u7684\u7cbe\u8f69\u5916\uff0c\u8083\u7136\u4f2b\u7acb\u7740\u5341\u51e0\u4e2a\u8001\u5988\u5b50\uff0c\u5c0f\u4e2b\u5934\uff0c\u4e00 \u4e2a\u4e2a\u60e7\u90fd\u5782\u7740\u5934\uff0c\u773c\u775b\u53d1\u7ea2\u3002 \u3000\u3000\u5de6\u8f7b\u4faf\u53d8\u5bb9\u9053\uff1a\u201c\u73e0\u513f\u5979\u2026\u2026\u5979\u83ab\u975e\u5df2\u2026\u2026\u201d \u3000\u3000\u7b80\u658b\u5148\u751f\u957f\u957f\u53f9\u4e86\u53e3\u6c14\u6c89\u91cd\u7684\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u5de6\u8f7b\u4faf\u72c2\u547c\u4e00\u58f0\uff0c\u51b2\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u7b49\u695a\u7559\u9999\u8ddf\u7740\u8fdb\u53bb\u7684\u65f6\u5019\uff0c\u5de6\u8f7b\u4faf\u5df2\u6655\u5012\u5728\u75c5\u69bb\u524d\uff0c\u69bb\u4e0a\u9759\u9759\u7684\u8eba\u7740\u4e2a\u7f8e\u4e3d\u7684\u5c11\u5973\uff0c\u9762 \u5bb9\u82cd\u767d\uff0c\u53cc\u76ee\u7d27\u95ed\u3002 \u3000\u3000\u7b80\u658b\u5148\u751f\u62c9\u8d77\u88ab\u5355\uff0c\u76d6\u4f4f\u4e86\u5979\u7684\u8138\uff0c\u5374\u5411\u695a\u7559\u9999\u9053\uff1a\u201c\u8001\u673d\u5c31\u662f\u6015\u5de6\u4e8c\u7237\u6025\u75db\u653b\u5fc3\uff0c\u4e5f \u53d1\u751f\u610f\u5916\uff0c\u6240\u4ee5\u5148\u8ba9\u4ed6\u670d\u4e0b\u4e00\u7c92\u62a4\u5fc3\u4e39\uff0c\u624d\u6562\u5c06\u8fd9\u6076\u8017\u544a\u8bc9\u4ed6\uff0c\u60f3\u4e0d\u5230\u4ed6\u8fd8\u662f\u2026\u2026\u8fd8 \u662f\u2026\u2026\u201d \u3000\u3000\u8fd9\u672c\u5df2\u5c06\u751f\u6b7b\u770b\u5f97\u6781\u6de1\u7684\u8001\u4eba\uff0c\u6b64\u523b\u9762\u4e0a\u4e5f\u4e0d\u7981\u9732\u51fa\u51c4\u51c9\u4f24\u75db\u4e4b\u8272\uff0c\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c \u9053\uff1a\u201c\u4ed6\u8fde\u53d7\u52b3\u82e6\uff0c\u8001\u673d\u6015\u4ed6\u5185\u5916\u4ea4\u653b\uff01\u53c8\u751f\u4e0d\u6d4b\uff0c\u5e78\u597d\u9999\u5e05\u6765\u4e86\uff0c\u6b63\u597d\u4ee5\u5185\u529b\u5148\u62a4\u4f4f\u4ed6\u7684 \u5fc3\u8109\uff0c\u5426\u5219\u8001\u673d\u5f53\u771f\u4e5f\u4e0d\u77e5\u5982\u4f55\u662f\u597d\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u4e0d\u7b49\u4ed6\u8bf4\u5b8c\uff0c\u5df2\u7528\u638c\u5fc3\u62b5\u4f4f\u5de6\u8f7b\u4faf\u7684\u5fc3\u53e3\uff0c\u5c06\u4e00\u822c\u5185\u529b\u6e90\u6e90\u4e0d\u7edd\u7684\u8f93\u9001\u8fc7\u53bb\u2014\u2014 \u3000\u3000\u5e55\u8272\u6e10\u6df1\uff0c\u591c\u5df2\u5c06\u4e34\uff0c\u4f46\u5e7f\u5927\u7684\u201c\u63b7\u676f\u51fa\u5e84\u201d\u5c1a\u8fd8\u6ca1\u6709\u71c3\u706f\uff0c\u79cb\u98ce\u867d\u6025\uff0c\u5374\u4e5f\u5439\u4e0d\u6563\u90a3 \u79cd\u6d53\u91cd\u7684\u51c4\u82e6\u9634\u68ee\u4e4b\u610f\u3002 \u3000\u3000\u524d\u5f8c\u516d\u4e03\u91cd\u9662\u843d\uff0c\u90fd\u662f\u9759\u6084\u6084\uff01\u6ca1\u6709\u4eba\u8bf4\u8bdd\uff0c\u4e5f\u6ca1\u6709\u4eba\u8d70\u52a8\uff0c\u6bcf\u4e2a\u4eba\u90fd\u50cf\u751f\u6015\u6709\u6765\u81ea\u5730 \u72f1\u7684\u51a4\u9b42\uff0c\u6b63\u8eb2\u5728\u9ed1\u6697\u7684\u89d2\u843d\u867d\u7b49\u7740\u6b89\u4eba\u9b42\u9b44\u3002 \u3000\u3000\u6811\u53f6\u51e0\u4e4e\u5df2\u5168\u90e8\u51cb\u843d\uff0c\u53ea\u5269\u4e0b\u5bc2\u5bde\u7684\u67af\u679d\u5728\u98ce\u4e2d\u8427\u7d22\u8d77\u821e\uff0c\u5c31\u8fde\u5fd9\u788c\u7684\u79cb\u866b\u90fd\u5df2\u611f\u89c9\u51fa \u8fd9\u79cd\u4ee4\u4eba\u7a92\u606f\u7684\u60b2\u54c0\uff0c\u800c\u4e0d\u518d\u4f4e\u8bed\u3002 \u3000\u3000\u5de6\u660e\u73e0\u7684\u5c38\u8eab\u4ecd\u505c\u7559\u5728\u90a3\u51c4\u51c9\u7684\u5c0f\u8f69\u4e2d\uff0c\u5de6\u4e8c\u7237\u4e0d\u8bb8\u4efb\u4f55\u4eba\u52a8\u5979\uff0c\u4ed6\u81ea\u5df2\u8dea\u5728\u7075\u5e8a\u65c1\uff0c \u50cf\u662f\u5df2\u53d8\u6210\u4e00\u5177\u77f3\u50cf\u3002 \u3000\u3000\u695a\u7559\u9999\u5fc3\u60c5\u4e5f\u51fa\u7684\u6c89\u91cd\uff0c\u56e0\u4e3a\u4ed6\u6df1\u77e5\u8fd9\u8001\u4eba\u5bf9\u4ed6\u7231\u5973\u7684\u60c5\u611f\u3002\u5404\u5730\u7684\u540d\u533b\u4e5f\u90fd\u9ed8\u9ed8\u65e0\u8a00 \u7684\u5750\u5728\u90a3\u91cc\uff0c\u4e5f\u4e0d\u77e5\u8be5\u8d70\uff0c\u8fd8\u662f\u4e0d\u8be5\u8d70\uff0c\u5fc3\u91cc\u65e2\u89c9\u5f97\u60ed\u6127\uff0c\u4e5f\u514d\u4e0d\u4e86\u6709\u4e9b\u96be\u53d7\u3002 \u3000\u3000\u53ea\u6709\u5f20\u7b80\u658b\u5728\u5ba4\u4e2d\u4e0d\u505c\u7684\u5f80\u6765\u8e40\u8e31\u7740\uff0c\u4f46\u811a\u6b65\u4e5f\u8f7b\u5f97\u5b9b\u5982\u5e7d\u7075\uff0c\u4f3c\u4e4e\u4e5f\u751f\u6015\u8e0f\u788e\u4e86\u8fd9\u65e0 \u8fb9\u7684\u9759\u5bc2\u3002 \u3000\u3000\u5de6\u4e8c\u7237\u4e00\u76f4\u5c06\u5934\u6df1\u6df1\u57cb\u85cf\u5728\u638c\u5fc3\u91cc\uff0c\u6b64\u523b\u5ffd\u7136\u62ac\u8d77\u5934\u6765\uff0c\u5e03\u6ee1\u8840\u4e1d\u7684\u773c\u775b\u832b\u7136\u77aa\u7740\u8fdc \u65b9\uff0c\u5636\u58f0\u9053\uff1a\u201c\u706f\u5462\uff1f\u4e3a\u4ec0\u9ebd\u6ca1\u6709\u4eba\u70b9\u706f\uff0c\u96be\u9053\u4f60\u4eec\u8fde\u770b\u90fd\u4e0d\u8bb8\u6211\u770b\u5979\u5417\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u65e0\u8a00\u7684\u7ad9\u4e86\u8d77\u6765\uff0c\u5728\u684c\u4e0a\u627e\u5230\u4e86\u706b\u5200\u548c\u706b\u77f3\uff0c\u521a\u71c3\u8d77\u4e86\u90a3\u76cf\u5e26\u7740\u6c34\u6676\u7f69\u5b50\u7684\u9752\u94dc \u706f\uff0c\u5ffd\u7136\u4e00\u9635\u72c2\u98ce\u81ea\u7a97\u5916\u5026\u5377\u4e86\u8fdb\u6765\uff0c\u5377\u8d77\u4e86\u76d6\u5728\u5c38\u8eab\u4e0a\u7684\u767d\u88ab\u5355\uff0c\u5377\u8d77\u4e86\u5e8a\u5e54\uff0c\u5e10\u4e0a\u7684\u94dc \u94a9\u6447\u8d77\u4e86\u4e00\u9635\u5355\u8c03\u7684\u201c\u53ee\u5f53\u201d\u58f0\uff0c\u5c31\u5b9b\u5982\u9b3c\u5352\u7684\u6444\u9b42\u94c3\uff0c\u72c2\u98ce\u4e2d\u4eff\u4f5b\u4e5f\u4e0d\u77e5\u591a\u5c11\u9b54\u9b3c\u6b63\u5728\u72de \u7b11\u7740\u98de\u821e\u3002 \u3000\u3000\u201c\u5657\u201d\u7684\u4e00\u58f0\uff0c\u695a\u7559\u9999\u624b\u91cc\u7684\u706f\u706b\u4e5f\u88ab\u5439\u706d\u4e86\u3002 \u3000\u3000\u4ed6\u53ea\u89c9\u98ce\u4e2d\u7adf\u4f3c\u5e26\u7740\u79cd\u5996\u5f02\u7684\u5bd2\u610f\uff0c\u7adf\u5fcd\u4e0d\u4f4f\u673a\u4f36\u4f36\u6253\u4e86\u4e2a\u5bd2\u5664\uff0c\u624b\u91cc\u7684\u6c34\u6676\u706f\u7f69\u4e5f\u8dcc \u843d\u5728\u5730\u4e0a\uff0c\u8dcc\u5f97\u7c89\u788e\u3002 \u3000\u3000\u56db\u4e0b\u7acb\u523b\u53c8\u88ab\u9ed1\u6697\u7b3c\u7f69\u3002 \u3000\u3000\u98ce\u4ecd\u5728\u547c\u5578\uff0c\u90a3\u4e9b\u6c5f\u5357\u540d\u533b\u5df2\u5fcd\u4e0d\u4f4f\u7f29\u8d77\u4e86\u8116\u5b50\uff0c\u6709\u7684\u4eba\u8eab\u5b50\u5df2\u4e0d\u7981\u5728\u5f00\u59cb\u53d1\u6296\uff0c\u6709\u7684 \u4eba\u638c\u5fc3\u5df2\u6ccc\u51fa\u4e86\u51b7\u6c57\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5e8a\u4e0a\u7684\u4f53\u5ffd\u7136\u5f20\u5f00\u773c\u775b\uff0c\u5750\u4e86\u8d77\u6765\uff01 \u3000\u3000\u8fd9\u5239\u90a3\u95f4\uff0c\u6bcf\u4e2a\u4eba\u7684\u5fc3\u623f\u90fd\u9aa4\u7136\u505c\u6b62\u4e86\u8df3\u52a8\u3002 \u3000\u3000\u7136\u5f8c\u5c31\u6709\u4eba\u4e0d\u7531\u81ea\u4e3b\uff0c\u653e\u58f0\u60ca\u547c\u51fa\u6765\u3002 \u3000\u3000\u5c31\u8fde\u695a\u7559\u9999\u90fd\u60c5\u4e0d\u81ea\u7981\u7684\u9000\u5f8c\u534a\u6b65\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u201c\u4f53\u201d\u7684\u773c\u775b\u5148\u662f\u5446\u5446\u7684\u51dd\u6ce8\u7740\u524d\u65b9\uff0c\u518d\u6e10\u6e10\u5f00\u59cb\u8f6c\u52a8\uff0c\u4f46\u53cc\u76ee\u4e2d\u5374\u4ecd\u5e26\u7740\u79cd\u8be1 \u79d8\u7684\u6b7b\u6c14\u3002 \u3000\u3000\u5de6\u8f7b\u4faf\u663e\u7136\u4e5f\u9a87\u5446\u4e86\uff0c\u5634\u5507\u5728\u52a8\uff0c\u5374\u53d1\u4e0d\u51fa\u58f0\u97f3\u3002 \u3000\u3000\u90a3\u201c\u4f53\u201d\u773c\u73e0\u5b50\u5446\u6ede\u7684\u8f6c\u4e86\u4e24\u904d\uff0c\u5ffd\u7136\u653e\u51fa\u58f0\u5c16\u547c\u8d77\u6765\u3002 \u3000\u3000\u547c\u58f0\u8bf4\u4e0d\u51fa\u7684\u51c4\u5389\u53ef\u6016\uff0c\u6709\u7684\u4eba\u5df2\u60f3\u593a\u95e8\u800c\u9003\uff0c\u4f46\u4e24\u6761\u817f\u5374\u597d\u50cf\u7435\u7436\u4f3c\u7684\u6296\u4e2a\u4e0d\u505c\uff0c\u54ea \u91cc\u8fd8\u6709\u529b\u6c14\u4e3e\u6b65\u3002 \u3000\u3000\u90a3\u201c\u4f53\u201d\u547c\u58f0\u6e10\u6e10\u5636\u54d1\uff0c\u624d\u5598\u606f\u7740\u54d1\u58f0\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u9ebd\u5730\u65b9\uff0c\u6211\u600e\u4f1a\u5230\u8fd9\u91cc\u6765\u4e86\uff1f\u201d \u3000\u3000\u5de6\u4e8c\u7237\u5f20\u5927\u4e86\u773c\u775b\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u8001\u5929\u7237\u6148\u60b2\uff0c\u8001\u5929\u7237\u53ef\u601c\u6211\uff0c\u660e\u73e0\u6ca1\u6709\u6b7b\uff0c\u660e\u73e0\u53c8\u6d3b\u56de \u6765\u4e86\u2026\u2026\u201d \u3000\u3000\u4ed6\u76ee\u4e2d\u5df2\u9732\u51fa\u72c2\u559c\u4e4b\u8272\uff0c\u5ffd\u7136\u8df3\u8d77\u6765\uff0c\u63fd\u62b1\u7740\u4ed6\u7684\u7231\u5973\uff0c\u9053\uff1a\u201c\u660e\u73e0\u4f60\u83ab\u8981\u5bb3\u6015\uff0c\u8fd9\u662f \u4f60\u7684\u5bb6\uff0c\u4f60\u53c8\u91cd\u56de\u9633\u4e16\u4e86\u3002\u201d \u3000\u3000\u8c01\u77e5\u4ed6\u7684\u5973\u513f\u5374\u547d\u63a8\u5f00\u4e86\u4ed6\uff0c\u4e24\u53ea\u624b\u75c9\u631b\u7740\u7d27\u6293\u4f4f\u6252\u5728\u5979\u8eab\u4e0a\u7684\u767d\u88ab\u5355\u5168\u8eab\u90fd\u7d27\u5f20\u5f97\u53d1 \u6296\uff0c\u4e00\u53cc\u773c\u775b\u5403\u60ca\u7684\u77aa\u7740\u5de6\u8f7b\u4faf\uff0c\u76ee\u4e2d\u7684\u77b3\u5b54\u4e5f\u56e0\u6050\u60e7\u800c\u5f20\u5927\u4e86\u8d77\u6765\uff0c\u5c31\u50cf\u662f\u89c1\u5230\u201c\u9b3c\u201d\u4e00 \u6837\u3002 \u3000\u3000\u5de6\u4e8c\u7237\u5598\u606f\u7740\uff0c\u5403\u5403\u9053\uff1a\u201c\u660e\u73e0\uff0c\u4f60\u2026\u2026\u4f60\u2026\u2026\u96be\u9053\u5df2\u4e0d\u8ba4\u5f97\u7239\u7239\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u90a3\u201c\u4f53\u201d\u8eab\u5b50\u7f29\u6210\u4e00\u56e2\uff0c\u5ffd\u53c8\u54d1\u58f0\u72c2\u547c\u9053\uff1a\u201c\u6211\u4e0d\u662f\u660e\u73e0\uff0c\u4e0d\u662f\u4f60\u5973\u513f\uff0c\u6211\u4e0d\u8ba4\u5f97 \u4f60\uff01\u201d \u3000\u3000\u5de6\u4e8c\u7237\u6014\u4f4f\u4e86\uff0c\u695a\u7559\u9999\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u90fd\u6014\u4f4f\u4e86\uff01 \u3000\u3000\u5de6\u4e8c\u7237\u6c42\u52a9\u7684\u671b\u7740\u695a\u7559\u9999\uff0c\u9053\uff1a\u201c\u8fd9\u2026\u2026\u8fd9\u5b69\u5b50\u6015\u53d7\u4e86\u60ca\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bdd\u672a\u8bf4\u5b8c\uff0c\u90a3\u201c\u4f53\u201d\u53c8\u5927\u558a\u8d77\u6765\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u662f\u4f60\u7684\u5b69\u5b50\uff0c\u4f60\u4eec\u7a76\u7adf\u662f\u4ec0\u9ebd\u4eba\uff1f\u4e3a\u4ec0 \u9ebd\u628a\u6211\u7ed1\u5230\u8fd9\u91cc\u6765\uff1f\u5feb\u653e\u6211\u56de\u2026\u2026\u53bb\u3002\u201d \u3000\u3000\u5de6\u4e8c\u7237\u53c8\u60ca\u53c8\u6025\uff0c\u8fde\u8fde\u987f\u8db3\uff0c\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u75af\u4e86\u9ebd\uff1f\u8fd9\u5b69\u5b50\u75af\u4e86\u9ebd\u2026\u2026\u201d \u3000\u3000\u5b9e\u5728\u4ed6\u81ea\u5df2\u624d\u771f\u7684\u5df2\u7ecf\u5feb\u6025\u75af\u4e86\u3002 \u3000\u3000\u90a3\u201c\u4f53\u201d\u6323\u624e\u7740\u60f3\u8df3\u4e0b\u5e8a\uff0c\u54d1\u58f0\u9053\uff1a\u201c\u4f60\u624d\u662f\u75af\u5b50\uff0c\u4f60\u4eec\u624d\u662f\u75af\u5b50\uff0c\u6211\u8981\u56de\u53bb\uff0c\u8ba9\u6211 \u8d70\uff01\u201d \u3000\u3000\u695a\u7559\u9999\u5fc3\u91cc\u867d\u4e5f\u662f\u60ca\u5947\u4ea4\u96c6\uff0c\u4f46\u4e5f\u77e5\u9053\u5728\u8fd9\u79cd\u65f6\u5019\uff0c\u4ed6\u82e5\u4e0d\u9547\u5b9a\u4e0b\u6765\uff0c\u5c31\u6ca1\u6709\u4eba\u80fd\u9547\u5b9a \u4e0b\u6765\u4e86\u3002 \u3000\u3000\u4ed6\u62cd\u4e86\u62cd\u5de6\u4e8c\u7237\u7684\u80a9\u5934\uff0c\u8f7b\u8f7b\u9053\uff1a\u201c\u4f60\u4eec\u6682\u65f6\u83ab\u8981\u8bf4\u8bdd\uff0c\u6211\u5148\u53bb\u8ba9\u5979\u5b89\u9759\u4e0b\u6765\u518d\u8bf4\u3002\u201d \u3000\u3000\u4ed6\u7f13\u7f13\u8d70\u8fc7\u53bb\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u5927\u75c5\u521d\u6108\uff0c\u65e0\u8bba\u4f60\u662f\u4ec0\u9ebd\u4eba\uff0c\u90fd\u4e0d\u8be5\u4e71\u5435\u4e71\u52a8\uff0c\u4f60 \u7684\u75c5\u82e5\u53c8\u590d\u53d1\u4e86\uff0c\u5927\u5bb6\u90fd\u4f1a\u4f24\u5fc3\u7684\u3002\u201d \u3000\u3000\u90a3\u201c\u4f53\u201d\u6b63\u60ca\u60f6\u7684\u8df3\u4e0b\u5e8a\uff0c\u4f46\u695a\u7559\u9999\u6e29\u67d4\u7684\u76ee\u5149\u4e2d\u5374\u4f3c\u6709\u4ee4\u4eba\u4e0d\u53ef\u6297\u62d2\u7684\u9547\u5b9a\u529b\u91cf\uff0c\u4ee4 \u4efb\u4f55\u4eba\u90fd\u4e0d\u80fd\u4e0d\u4fe1\u4efb\u4ed6\u3002 \u3000\u3000\u5979\u4e24\u53ea\u624b\u7d27\u7d27\u7684\u6321\u5728\u81ea\u5df2\u80f8\u524d\uff0c\u9762\u4e0a\u867d\u4ecd\u5145\u6ee1\u4e86\u6050\u60e7\u60ca\u60f6\u4e4b\u8272\uff0c\u4f46\u547c\u5438\u5df2\u4e0d\u89c9\u6e10\u6e10\u5e73\u9759 \u4e86\u4e0b\u6765\u3002 \u3000\u3000\u695a\u7559\u9999\u6e29\u67d4\u7684\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u8fd9\u6837\u624d\u662f\u4e56\u5b69\u5b50\uff0c\u73b0\u5728\u6211\u95ee\u4f60\uff0c\u4f60\u53ef\u8ba4\u5f97\u6211\u9ebd\uff1f\u201d \u3000\u3000\u90a3\u201c\u4f53\u201d\u5f20\u5927\u4e86\u773c\u775b\u77aa\u4e86\u5f88\u4e45\uff0c\u624d\u7528\u529b\u6447\u6447\u5934\u3002 \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u8fd9\u5c4b\u5b50\u91cc\u7684\u4eba\u4f60\u90fd\u4e0d\u8ba4\u5f97\uff1f\u201d \u3000\u3000\u90a3\u201c\u4f53\u201d\u53c8\u6447\u4e86\u6447\u5934\uff0c\u6839\u672c\u6ca1\u6709\u77a7\u4efb\u4f55\u4eba\u4e00\u773c\u3002 \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u90a3\u9ebd\uff0c\u4f60\u53ef\u77e5\u9053\u4f60\u81ea\u5df2\u662f\u8c01\u9ebd\uff1f\u201d \u3000\u3000\u90a3\u201c\u4f53\u201d\u5927\u58f0\u9053\uff1a\u201c\u6211\u5f53\u7136\u77e5\u9053\uff0c\u6211\u662f\u2018\u65bd\u5bb6\u5e84\u2019\u7684\u65bd\u5927\u59d1\u5a18\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u76b1\u4e86\u76b1\u7709\u9053\uff1a\u201c\u90a3\u9ebd\uff0c\u4f60\u96be\u9053\u662f\u91d1\u5f13\u592b\u4eba\u7684\u5973\u513f\uff1f\u201d \u3000\u3000\u90a3\u201c\u4f53\u201d\u773c\u775b\u4eae\u4e86\uff0c\u9053\uff1a\u201c\u4e00\u70b9\u4e5f\u4e0d\u9519\uff0c\u4f60\u4eec\u65e2\u7136\u77e5\u9053\u6211\u6bcd\u4eb2\u7684\u540d\u5b57\uff0c\u5c31\u5e94\u8be5\u4e58\u65e9\u9001\u6211 \u56de\u53bb\uff0c\u514d\u5f97\u81ea\u60f9\u9ebb\u70e6\u4e0a\u8eab\u3002\u201d \u3000\u3000\u5de6\u4e8c\u7237\u65e9\u5df2\u6c14\u5f97\u8138\u90fd\u9ec4\u4e86\uff0c\u8dfa\u7740\u811a\u9053\uff1a\u201c\u8fd9\u4e2b\u5934\uff0c\u4f60\u4eec\u770b\u8fd9\u4e2b\u5934\uff0c\u5f8c\u7136\u8ba4\u8d3c\u4e3a\u6bcd\u8d77\u6765\u201d \u3000\u3000\u90a3\u201c\u4f53\u201d\u77aa\u773c\u9053\uff1a\u201c\u8c01\u662f\u8d3c\uff1f\u4f60\u4eec\u624d\u662f\u8d3c\uff0c\u7adf\u6562\u7ed1\u6211\u7684\u7968\u3002\u201d \u3000\u3000\u5de6\u4e8c\u7237\u65e9\u5df2\u6c14\u5f97\u5168\u8eab\u53d1\u6296\uff0c\u9000\u5f8c\u4e24\u6b65\uff0c\u5012\u5728\u6905\u5b50\u4e0a\u76f4\u5598\u6c14\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u773c\u4e2d\u4e0d\u7981\u53c8\u6d41\u4e0b \u6cea\u6765\uff0c\u8f6c\u8eab\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u4e0d\u77e5\u53c8\u5f97\u4e86\u4ec0\u9ebd\u75c5\uff0c\u5404\u4f4d\u82e5\u80fd\u6cbb\u5f97\u597d\u5979\uff0c\u6211\u2026\u2026\u6211\u4e0d\u60dc\u5c06\u5168\u90e8\u5bb6\u4ea7 \u5206\u7ed9\u4ed6\u4e00\u534a\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u663e\u7136\u4e5f\u89c9\u5f97\u5f88\u63a0\u8bb6\uff0c\u5f88\u5947\u602a\uff0c\u671b\u7740\u5f20\u7b80\u658b\u9053\uff1a\u201c\u5f20\u8001\u5148\u751f\uff0c\u4f9d\u4f60\u770b\u2026\u2026\u201d \u3000\u3000\u5f20\u7b80\u658b\u6c89\u541f\u4e86\u534a\u664c\uff0c\u624d\u7f13\u7f13\u9053\uff1a\u201c\u770b\u5979\u7684\u75c5\u60c5\uff0c\u4eff\u4f5b\u662f\u2018\u79bb\u9b42\u75c7\u2019\uff0c\u4f46\u53ea\u6709\u53d7\u8fc7\u5927\u60ca \u9a87\uff0c\u5927\u523a\u6fc0\u7684\u4eba\u624d\u4f1a\u5f97\u6b64\u75c7\uff0c\u8001\u592b\u884c\u533b\u8fd1\u4e94\u5341\u5e74\uff0c\u4e5f\u4ece\u672a\u89c1\u8fc7\u2026\u2026\u201d \u3000\u3000\u90a3\u201c\u4f53\u201d\u7684\u8138\u7adf\u4e5f\u6c14\u7ea2\u4e86\uff0c\u5927\u58f0\u9053\uff1a\u201c\u8c01\u5f97\u4e86\u2018\u79bb\u9b42\u75c7\u2019\uff0c\u6211\u770b\u4f60\u624d\u5f97\u4e86\u2018\u79bb\u9b42\u75c7\u2019\uff0c \u6ee1\u5634\u80e1\u8bf4\u516b\u9053\u3002\u201d \u3000\u3000\u5f20\u7b80\u658b\u51dd\u6ce8\u7740\u5979\u671b\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u5c06\u5c4b\u89d2\u7684\u4e00\u9762\u94dc\u955c\u642c\u4e86\u8fc7\u6765\uff0c\u642c\u5230\u8fd9\u5c11\u5973\u7684\u9762\u524d\uff0c\u6c89\u58f0 \u9053\uff1a\u201c\u4f60\u518d\u770b\u770b\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\u81ea\u5df1\u662f\u8c01\uff1f\u201d \u3000\u3000\u8fd9\u5c11\u5973\u6012\u9053\uff1a\u201c\u6211\u5f53\u7136\u77e5\u9053\u81ea\u5df2\u662f\u8c01\uff0c\u7528\u4e0d\u7740\u770b\u2026\u2026\u201d \u3000\u3000\u5979\u660e\u91cc\u867d\u8bf4\u201c\u7528\u4e0d\u7740\u770b\u201d\uff0c\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u77a7\u4e86\u955c\u5b50\u4e00\u773c\u3002 \u3000\u3000\u53ea\u77aa\u4e86\u4e00\u773c\uff0c\u5979\u8138\u4e0a\u5c31\u5ffd\u53c8\u53d8\u5f97\u8bf4\u4e0d\u51fa\u7684\u60ca\u9a87\u3001\u6050\u60e7\uff0c\u5931\u58f0\u9a87\u547c\u9053\uff1a\u201c\u8fd9\u662f\u8c01\uff1f\u6211\u4e0d\u8ba4 \u5f97\u5979\uff0c\u6211\u4e0d\u8ba4\u5f97\u5979\u2026\u2026\u201d \u3000\u3000\u5f20\u7b80\u658b\u6c89\u58f0\u9053\uff1a\u201c\u7167\u5728\u955c\u5b50\u91cc\u7684\uff0c\u81ea\u7136\u662f\u4f60\u81ea\u5df1\uff0c\u4f60\u8fde\u81ea\u5df1\u90fd\u4e0d\u8ba4\u5f97\u4e86\u5417\uff1f\u201d \u3000\u3000\u5c11\u5973\u5ffd\u7136\u8f6c\u8eab\u6251\u5230\u5e8a\u4e0a\uff0c\u7528\u88ab\u8499\u4f4f\u4e86\u5934\uff0c\u54d1\u58f0\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u6211\uff0c\u4e0d\u662f\u6211\uff0c\u6211\u600e\u9ebd\u53d8\u6210\u8fd9 \u6a21\u6837\uff0c\u6211\u600e\u4f1a\u53d8\u6210\u8fd9\u6a21\u6837\uff1f\u201d\u5979\u4e00\u8fb9\u8bf4\uff0c\u4e00\u8fb9\u7528\u529b\u9524\u7740\u5e8a\uff0c\u7adf\u653e\u58f0\u5927\u54ed\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u6bcf\u4e2a\u4eba\u60e7\u662f\u76ee\u5b9a\u53e3\u5446\uff0c\u4f5c\u58f0\u4e0d\u5f97\uff0c\u5927\u5bb6\u5fc3\u91cc\u867d\u5df2\u9690\u9690\u7ea6\u7ea6\u731c\u51fa\u8fd9\u662f\u600e\u9ebd\u56de\u4e8b\u4e86\uff0c \u4f46\u5374\u53c8\u8c01\u90fd\u4e0d\u6562\u76f8\u4fe1\u3002 \u3000\u3000\u5f20\u7b80\u658b\u5c06\u695a\u7559\u9999\u548c\u5de6\u8f7b\u4faf\u62c9\u5230\u4e00\u8fb9\uff0c\u6c89\u7740\u8138\u9053\uff1a\u201c\u5979\u6ca1\u6709\u75c5\u3002\u201d \u3000\u3000\u5de6\u4e8c\u7237\u9053\uff1a\u201c\u6ca1\u6709\u75c5\u53c8\u600e\u4f1a\u2026\u2026\u600e\u4f1a\u53d8\u6210\u8fd9\u6837\u5b50\uff01\u201d \u3000\u3000\u5f20\u7b80\u658b\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5979\u867d\u7136\u6ca1\u6709\u75c5\uff0c\u4f46\u6211\u5374\u5e0c\u671b\u5979\u6709\u75c5\u53cd\u800c\u597d\u4e9b\u3002\u201d \u3000\u3000\u5de6\u4e8c\u7237\u9053\uff1a\u201c\u4e3a\u2026\u2026\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u5f20\u7b80\u658b\u9053\uff1a\u201c\u53ea\u56e0\u5979\u6ca1\u6709\u75c5\u6bd4\u6709\u75c5\u8fd8\u8981\u2026\u2026\u53ef\u6015\u5f97\u591a\u3002\u201d \u3000\u3000\u5de6\u8f7b\u4faf\u5934\u4e0a\u5df2\u65e5\u51fa\u4e86\u51b7\u6c57\uff0c\u5636\u58f0\u9053\uff1a\u201c\u53ef\u6015\uff1f\u201d \u3000\u3000\u5f20\u7b80\u658b\u9053\uff1a\u201c\u5979\u5931\u53bb\u77e5\u89c9\u5df2\u6709\u4e00\u4e2a\u6708\u4e86\uff0c\u800c\u4e14\u6c34\u7c73\u672a\u6cbe\uff0c\u5c31\u7b97\u75c5\u6108\uff0c\u4f53\u529b\u4e5f\u7edd\u4e0d\u4f1a\u6062\u590d \u5f97\u8fd9\u9ebd\u5feb\uff0c\u4f55\u51b5\uff0c\u5979\u65b9\u5207\u660e\u662f\u5fc3\u8109\u60e7\u65ad\uff0c\u8fd4\u9b42\u65e0\u672f\u7684\u4e86\uff0c\u8001\u592b\u53ef\u4ee5\u4e94\u5341\u5e74\u7684\u4fe1\u8a89\u4f5c\u4fdd\uff0c\u7edd\u4e0d \u4f1a\u8bca\u6240\u6709\u8bef\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u5f20\u8001\u5148\u751f\u7684\u533b\u9053\uff0c\u5929\u4e0b\u8c01\u4eba\u4e0d\u77e5\uff0c\u54ea\u4e2a\u4e0d\u4fe1\u3002\u201d \u3000\u3000\u5f20\u7b80\u658b\u8138\u8272\u66f4\u6c89\u91cd\uff0c\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u9ebd\u8001\u592b\u5c31\u8981\u8bf7\u6559\u9999\u5e05\uff0c\u4e00\u4e2a\u4eba\u660e\u660e\u5df2\u6b7b\u4e86\uff0c\u53c8 \u600e\u4f1a\u5ffd\u7136\u6d3b\u56de\u6765\u5462\uff1f\u9999\u5e05\u89c1\u591a\u8bc6\u5e7f\u3002\u53ef\u66fe\u89c1\u8fc7\u8fd9\u79cd\u602a\u4e8b\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u6014\u4e86\u534a\u664c\u3002\u542b\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u975e\u4f46\u672a\u66fe\u89c1\u8fc7\uff0c\u8fde\u542c\u4e5f\u672a\u542c\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u5f20\u7b80\u658b\u9053\uff1a\u201c\u4f46\u5979\u5374\u660e\u660e\u5df2\u6d3b\u56de\u6765\u4e86\uff0c\u4ee5\u9999\u5e05\u4e4b\u89c1\uff0c\u8fd9\u79cd\u4e8b\u8be5\u5982\u4f55\u89e3\u91ca\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u53c8\u6014\u4e86\u534a\u664c\uff0c\u9053\uff1a\u201c\u5f20\u8001\u5148\u751f\u4f60\u89c9\u5f97\u8fd9\u4ef6\u4e8b\u8be5\u5982\u4f55\u89e3\u91ca\u5462\uff1f\u201d \u3000\u3000\u5f20\u7b80\u658b\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u76ee\u4e2d\u4f3c\u4e4e\u9732\u51fa\u4e86\u60ca\u6016\u4e4b\u8272\uff0c\u538b\u4f4e\u58f0\u97f3\u9053\uff1a\u201c\u4ee5\u8001\u592b\u770b\u6765\uff0c\u8fd9\u4ef6\u4e8b\u53ea \u6709\u4e00\u4e2a\u89e3\u91ca\u2026\u2026\u501f\u5c38\u8fd8\u9b42\u201d \u3000\u3000\u201c\u501f\u5c38\u8fd8\u9b42\u201d \u3000\u3000\u5de6\u8f7b\u4faf\u8df3\u4e86\u8d77\u6765\uff0c\u543c\u9053\uff1a\u201c\u5f20\u7b80\u658b\uff0c\u6211\u8fd8\u4ee5\u4e3a\u4f60\u6709\u4ec0\u9ebd\u4e86\u4e0d\u5f97\u7684\u9ad8\u89c1\uff0c\u8c01\u77e5\u4f60\u7adf\u4f1a\u8bf4\u51fa \u5982\u6b64\u8352\u8c2c\u4e0d\u7ecf\u7684\u8bdd\u6765\uff0c\u8bf7\u8bf7\u8bf7\uff0c\u50cf\u4f60\u8fd9\u6837\u7684\u540d\u533b\uff0c\u5de6\u67d0\u5df2\u4e0d\u6562\u9886\u6559\u4e86\u3002\u201d \u3000\u3000\u5f20\u7b80\u658b\u6c89\u4e0b\u4e86\u8138\uff0c\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u8001\u592b\u5c31\u6b64\u544a\u8f9e\u3002\u201d \u3000\u3000\u4ed6\u4e00\u6012\u4e4b\u4e0b\uff0c\u5c31\u8981\u6cb8\u8896\u800c\u51fb\uff0c\u4f46\u695a\u7559\u9999\u653e\u4efb\u4e86\u4ed6\uff0c\u4e00\u9762\u5411\u4ed6\u633d\u7559\u3002\u4e00\u9762\u5411\u5de6\u8f7b\u4faf\u529d\u9053\uff1a \u201c\u4e8b\u53d8\u975e\u5e38\u3002\u5927\u5bb6\u90fd\u8be5\u4efd\u5916\u9547\u5b9a\uff0c\u5207\u5207\u4e0d\u53ef\u610f\u6c14\u7528\u4e8b\u3002\u201d \u3000\u3000\u5de6\u8f7b\u4faf\u77aa\u7740\u670d\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u2026\u2026\u4f60\u96be\u9053\u4e5f\u76f8\u4fe1\u8fd9\u79cd\u9b3c\u8bdd\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9ed8\u7136\u534a\u54cd\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u65e0\u8bba\u5982\u4f55\uff0c\u4e24\u4f4d\u90fd\u8bf7\u5148\u9759\u4e0b\u6765\uff0c\u7b49\u6211\u518d\u53bb\u95ee\u95ee\u5979\uff0c\u95ee\u4e2a\u6e05 \u695a\u518d\u8bf4\u3002\u201d \u3000\u3000\u4ed6\u8d70\u5230\u5e8a\u8fb9\uff0c\u7b49\u90a3\u5c11\u5973\u7684\u54ed\u58f0\u6e10\u6e10\u5c0f\u4e86\uff0c\u624d\u67d4\u58f0\u9053\uff1a\u201c\u59d1\u5a18\u7684\u5fc3\u60c5\uff0c\u6211\u4e0d\u4f46\u5f88\u4e86\u89e3\u800c\u4e14 \u5f88\u540c\u60c5\uff0c\u65e0\u8bba\u8c01\u9898\u7740\u8fd9\u4ef6\u4e8b\uff0c\u90fd\u4e00\u5b9a\u4f1a\u5f88\u96be\u53d7\uff0c\u6211\u53ea\u5e0c\u671b\u59d1\u5a18\u76f8\u4fe1\u6211\uff0c\u6211\u4eec\u7edd\u6ca1\u6709\u4f24\u5bb3\u59d1\u5a18 \u7684\u610f\u601d\uff0c\u66f4\u4e0d\u662f\u6211\u4eec\u5c06\u59d1\u5a18\u7ed1\u5230\u8fd9\u91cc\u6765\u7684\u3002\u201d \u3000\u3000\u4ed6\u58f0\u97f3\u4e2d\u4f3c\u4e4e\u6709\u79cd\u4ee4\u4eba\u9547\u5b9a\u7684\u529b\u91cf\uff0c\u90a3\u5c11\u5973\u7684\u54ed\u58f0\u679c\u7136\u505c\u6b62\u4e86\u3002\u4f46\u8fd8\u662f\u5c06\u5934\u8499\u5728\u88ab\u91cc\uff0c \u55c4\u58f0\u9053\uff1a\u201c\u4e0d\u662f\u4f60\u4eec\u5c06\u6211\u7ed1\u6765\u7684\uff0c\u6211\u600e\u4f1a\u5230\u8fd9\u91cc\u6765\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u59d1\u5a18\u4f55\u59a8\u9759\u4e0b\u5fc3\u6765\u60f3\u60f3\uff0c\u7a76\u7adf\u662f\u600e\u9ebd\u5230\u8fd9\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u7684\u5fc3\u4e71\u5f97\u72e0\uff0c\u597d\u50cf\u4ec0\u9ebd\u4e8b\u90fd\u8bb0\u4e0d\u6e05\u4e86\u2026\u2026\u201d \u3000\u3000\u5979\u4e0d\u7531\u81ea\u4e3b\u7684\u62ac\u8d77\u5934\uff0c\u7f8e\u4e3d\u7684\u773c\u775b\u91cc\u4eff\u4f5b\u7b3c\u7740\u4e00\u5c42\u8ff7\u96fe\uff0c\u695a\u7559\u9999\u5e76\u6ca1\u6709\u50ac\u4fc3\u5979\uff0c\u8fc7\u4e86\u5f88 \u4e45\uff0c\u5979\u624d\u7f13\u7f13\u63a5\u7740\u9053\uff1a\u201c\u6211\u8bb0\u5f97\u6211\u75c5\u4e86\u5f88\u4e45\uff0c\u800c\u4e14\u75c5\u5f97\u5f88\u91cd\u3002\u201d \u3000\u3000\u5de6\u8f7b\u4faf\u7acb\u523b\u73b0\u51fa\u559c\u8272\uff0c\u9053\uff1a\u201c\u597d\u5b69\u5b50\uff0c\u4f60\u603b\u7b97\u60f3\u8d77\u6765\u4e86\uff0c\u4f60\u7684\u786e\u75c5\u4e86\u5f88\u4e45\uff0c\u8fd9\u4e00\u4e2a\u591a\u6708 \u6765\uff0c\u4f60\u59cb\u7ec8\u8eba\u5728\u8fd9\u5f20\u5e8a\u4e0a\u4ece\u6ca1\u6709\u8d77\u6765\u8fc7\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u65ad\u7136\u6447\u4e86\u6447\u5934\u5927\u58f0\u9053\uff1a\u201c\u6211\u867d\u7136\u5728\u5e8a\u4e0a\u8eba\u4e86\u4e00\u4e2a\u591a\u6708\uff0c\u4f46\u5374\u7edd\u4e0d\u662f\u8eba\u5728\u8fd9\u5f20\u5e8a \u4e0a\u3002\u201d \u3000\u3000\u5de6\u8f7b\u4faf\u901a\uff1a\u201c\u4e0d\u5728\u8fd9\u91cc\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u81ea\u7136\u662f\u6211\u81ea\u5df1\u7684\u5bb6\u91cc\uff0c\u6211\u81ea\u5df1\u7684\u5c4b\u5b50\u91cc\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u89c1\u5230\u5de6\u8f7b\u4faf\u8138\u8272\u53c8\u53d8\u4e86\uff0c\u62a2\u7740\u9053\uff1a\u201c\u59d1\u5a18\u53ef\u8fd8\u8bb0\u5f97\u90a3\u662f\u600e\u9ebd\u6837\u7684\u5c4b\u5b50\uff1f\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u90a3\u662f\u6211\u4ece\u5c0f\u751f\u957f\u7684\u5730\u65b9\uff0c\u6211\u600e\u9ebd\u4f1a\u4e0d\u8bb0\u5f97\uff1f\u201d \u3000\u3000\u5979\u76ee\u5149\u56db\u4e0b\u779f\u4e86\u4e00\u773c\uff0c\u63a5\u7740\u9053\uff1a\u201c\u90a3\u95f4\u623f\u5b50\u548c\u8fd9\u91cc\u5dee\u4e0d\u591a\u5927\uff0c\u6211\u7761\u7684\u5e8a\u5c31\u6446\u5728\u90a3\u8fb9\uff0c\u5e8a \u65c1\u8fb9\u6709\u4e2a\u7d2b\u6a80\u6728\u7684\u5986\u53f0\uff0c\u5986\u53f0\u65c1\u662f\u4e2a\u6211\u67b6\uff0c\u4e0a\u9762\u5378\u6446\u7740\u4e00\u7089\u9999\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u76ee\u5149\u95ea\u52a8\uff0c\u9053\uff1a\u201c\u5986\u53f0\u4e0a\u6446\u7740\u4ec0\u9ebd\u5462\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u4e5f\u6ca1\u6709\u4ec0\u9ebd\u7279\u522b\u7684\u4e1c\u897f\uff0c\u53ea\u4e0d\u8fc7\u60ef\u7528\u7684\u8102\u7c89\u548c\u9999\u6cb9\uff0c\u90fd\u662f\u62db\u4eba\u4ece\u5317\u4eac\u57ce\u91cc \u7684\u2018\u5b9d\u9999\u658b\u2019\u4e70\u6765\u7684\u3002\u201d \u3000\u3000\u5979\u7684\u8138\u4f3c\u4e4e\u5ffd\u7136\u7ea2\u4e86\u53c8\u7ea2\uff0c\u7acb\u523b\u5c31\u63a5\u7740\u9053\uff1a\u201c\u4f46\u6211\u7684\u5c4b\u5b50\u91cc\u5374\u7edd\u6ca1\u6709\u82b1\u56e0\u4e3a\u6211\u4e00\u95fb\u5230\u82b1 \u7c89\u7684\u5473\u9053\u76ae\u80a4\u5c31\u4f1a\u53d1\u75b9\uff0c\u800c\u4e14\u6211\u5c4b\u91cc\u7684\u7a97\u6237\u4e0a\u90fd\u6302\u7740\u5f88\u539a\u7684\u7d2b\u7ed2\u7a97\u5e18\uff0c\u56e0\u4e3a\u6211\u4ece\u5c0f\u5c31\u4e0d\u559c\u6b22 \u9633\u5149\u3002\u201d \u3000\u3000\u8fd9\u5c4b\u5b50\u7684\u7a97\u6237\u4e0a\u7f18\u4e5f\u63a5\u7740\u7a97\u5e18\uff0c\u4f46\u5374\u662f\u6e58\u7406\u7af9\u7f16\u6210\u7684\uff0c\u5c4b\u89d2\u91cc\u6446\u7740\u4e00\u76c6\u83ca\u82b1\uff0c\u5f00\u5f97\u6b63 \u76db\u3002 \u3000\u3000\u90a3\u5c11\u5973\u89c1\u5230\u8fd9\u76c6\u83ca\u82b1\uff0c\u76ee\u4e2d\u7acb\u523b\u9732\u51fa\u538c\u6076\u4e4b\u8272\u3002 \u3000\u3000\u695a\u7559\u9999\u6697\u4e2d\u53f9\u4e86\u53e3\u6c14\uff0c\u56e0\u4e3a\u4ed6\u4e5f\u77e5\u9053\u5de6\u660e\u73e0\u662f\u5f88\u559c\u6b22\u82b1\u7684\uff0c\u800c\u4e14\u6700\u7231\u7684\u5c31\u662f\u83ca\u82b1\uff0c\u6240\u4ee5 \u624d\u5c06\u83ca\u82b1\u8fde\u76c6\u642c\u5230\u5c4b\u91cc\u6765\u3002 \u3000\u3000\u4f46\u4ed6\u5e76\u6ca1\u6709\u8bf4\u4ec0\u9ebd\uff0c\u53ea\u662f\u5c06\u83ca\u82b1\u64a4\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u90a3\u5c11\u5973\u611f\u6fc0\u7684\u77a7\u4e86\u4ed6\u4e00\u773c\uff0c\u9053\uff1a\u201c\u53ef\u662f\u5728\u5c4b\u91cc\u95f7\u4e86\u4e00\u4e2a\u591a\u6708\u4e4b\u5f8c\uff0c\u6211\u5374\u5ffd\u7136\u76fc\u671b\u89c1\u9633\u5149 \u4e86\uff0c\u6240\u4ee5\u4eca\u5929\u65e9\u4e0a\uff0c\u6211\u5c31\u53eb\u4eba\u5c06\u5c4b\u91cc\u7684\u7a97\u6237\u5168\u90fd\u6253\u5f00\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4eca\u5929\u65e9\u4e0a\u4e86\u59d1\u5a18\u662f\u53eb\u4ec0\u9ebd\u4eba\u5c06\u7a97\u6237\u6253\u5f00\u7684\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u662f\u6881\u5988\uff0c\u4e5f\u5c31\u662f\u6211\u7684\u5976\u5a18\uff0c\u7167\u987e\u6211\u5df2\u6709\u8bb8\u591a\u5e74\u4e86\u3002\u56e0\u4e3a\u5bb6\u6bcd\u4e00\u5411\u5f88\u5fd9\uff0c\u5e73 \u65f6\u5f88\u5c11\u6709\u65f6\u95f4\u548c\u6211\u4eec\u5728\u4e00\u8d77\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u91d1\u5f13\u592b\u4eba\u7684\u5927\u540d\uff0c\u5728\u4e0b\u65e9\u5df2\u4e45\u4ef0\u5f97\u5f88\u4e86\u3002\u201d \u3000\u3000\u5de6\u4e8c\u7237\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u7ec8\u65bc\u8fd8\u662f\u5fcd\u8010\u7740\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u90a3\u5c11\u5973\u76ee\u5149\u51dd\u6ce8\u7740\u7a97\u5916\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4eca\u5929\u65e9\u4e0a\u7684\u4e8b\uff0c\u6211\u8fd8\u8bb0\u5f97\u5f88\u6e05\u695a\uff0c\u4f46\u73b0\u5728\u2026\u2026\u73b0\u5728 \u5929\u600e\u4f1a\u5ffd\u7136\u9ed1\u4e86\uff1f\u6211\u96be\u9053\u53c8\u8eba\u4e86\u5f88\u4e45\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4eca\u5929\u65e9\u4e0a\u7684\u4e8b\uff0c\u59d1\u5a18\u8fd8\u8bb0\u5f97\u4e9b\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u6211\u770b\u5230\u5916\u9762\u7684\u9633\u5149\u5f88\u7f8e\uff0c\u5fc3\u91cc\u89c9\u5f97\u5f88\u9ad8\u5174\uff0c\u5ffd\u7136\u60f3\u5230\u56ed\u5b50\u91cc\u53bb\u6563\u6563\u5fc3\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u59d1\u5a18\u80fd\u8d70\u52a8\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u51c4\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5176\u5b9e\u6211\u5df2\u8fde\u7ad9\u90fd\u7ad9\u4e0d\u8d77\u6765\u4e86\uff0c\u4f46\u6881\u5988\u4e0d\u5fcd\u62c2\u6211\u7684\u5fc3\u610f\uff0c\u8fd8\u662f\u6276 \u6211\u8d77\u6765\uff0c\u66ff\u6211\u6362\u4e86\u5957\u8863\u670d\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5c31\u662f\u59d1\u5a18\u73b0\u5728\u7a7f\u7684\u8fd9\u5957\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u7edd\u4e0d\u662f\uff0c\u90a3\u662f\u6211\u6700\u559c\u6b22\u7684\u4e00\u5957\u8863\u670d\uff0c\u662f\u6881\u5988\u4eb2\u624b\u505a\u7684\uff0c\u6599\u5b50\u4e5f\u662f\u6258\u4eba\u4ece\u5317 \u4eac\u5e26\u56de\u6765\u7684\u7ec7\u9526\u7f0e\uff0c\u7ea2\u5e95\u5b50\u7ee3\u7740\u94f6\u8272\u7684\u51e4\u51f0\u3002\u201d \u3000\u3000\u4e5f\u4e0d\u77e5\u4e3a\u4e86\u4ec0\u9ebd\uff0c\u8bf4\u7740\u8bf4\u7740\uff0c\u5979\u7684\u8138\u53c8\u7ea2\u4e86\u8d77\u6765\u3002 \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5f8c\u6765\u59d1\u5a18\u53ef\u6709\u51fa\u53bb\u901b\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u56e0\u4e3a\u5bb6\u6bcd\u6070\u5de7\u6765\u4e86\uff0c\u8fd8\u5e26\u6765\u4e00\u4f4d\u5f88\u6709\u540d\u7684\u5927\u592b\u3002\u201d \u3000\u3000\u5f20\u7b80\u658b\u62a2\u7740\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u6068\u62a5\u9053\uff1a\u201c\u5bb6\u6bcd\u8bdd\u8bf4\u5c31\u56e0\u4e3a\u6c5f\u5357\u7684\u540d\u533b\u5168\u90fd\u88ab\u201c\u63b7\u676f\u5c71\u5e84\u201d\u62a2\u7740\u5b9a\u4e86\uff0c\u6211\u7684\u75c5\u624d \u4e0d\u4f1a\u597d\uff0c\u6240\u4ee5\u5979\u8001\u4eba\u5bb6\u8fd9\u6b21\u7279\u5730\u4ece\u5317\u65b9\u5c06\u738b\u96e8\u8f69\u5148\u751f\u8bf7\u4e86\u6765\uff0c\u4e5f\u5c31\u662f\u90a3\u4f4d\u548c\u5357\u65b9\u5f20\u7b80\u658b\u9f50\u540d \u7684\u738b\u8001\u5148\u751f\uff0c\u6c5f\u6e56\u4e2d\u4eba\u79f0\u2018\u5317\u738b\u5357\u5f20\u2019\u7684\u3002\u201d \u3000\u3000\u5f20\u7b80\u658b\u6273\u7740\u8138\u9053\uff1a\u201c\u662f\u5357\u5f20\u5317\u738b\uff0c\u4e0d\u662f\u5317\u738b\u5357\u5f20\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u671b\u4e86\u4ed6\u4e00\u773c\uff0c\u5931\u58f0\u9053\uff1a\u201c\u4f60\u96be\u9053\u5c31\u662f\u5f20\u7b80\u658b\uff1f\u8fd9\u91cc\u96be\u9053\u5c31\u662f\u63b7\u676f\u5c71\u5e84\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u773c\u73e0\u5b50\u8f6c\u6765\u8f6c\u53bb\uff0c\u663e\u5f97\u53c8\u60ca\u8bb6\uff0c\u53c8\u5bb3\u6015\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u9053\uff1a\u201c\u738b\u8001\u5148\u751f\u4ec0\u9ebd\u4e5f\u6ca1 \u6709\u8bf4\uff0c\u628a\u8fc7\u6211\u7684\u8109\u5c40\uff0c\u7acb\u523b\u5c31\u8d70\u4e86\u51fa\u53bb\uff0c\u5bb6\u6bcd\u5c31\u66ff\u6211\u5c06\u88ab\u76d6\u597d\uff0c\u53eb\u6211\u597d\u597d\u4f11\u606f\uff0c\u5207\u83ab\u80e1\u601d\u4e71 \u60f3\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5f8c\u6765\u5462\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u5f8c\u6765\u2026\u2026\u5f8c\u6765\u2026\u3002\u201d \u3000\u3000\u5979\u76ee\u5149\u53c8\u6df7\u4e71\u4e86\u8d77\u6765\u54ac\u7740\u5634\u5507\u9053\uff1a\u201c\u5f8c\u6765\u6211\u597d\u50cf\u662f\u505a\u4e86\u4e2a\u68a6\uff0c\u68a6\u5230\u6211\u7684\u75c5\u5ffd\u7136\u597d\u4e86\uff0c\u5c31 \u7a7f\u7740\u90a3\u8eab\u8863\u670d\u4ece\u7a97\u5b50\u91cc\u98de\u4e86\u51fa\u53bb\uff0c\u9662\u5b50\u7684\u4eba\u50cf\u662f\u7279\u522b\u591a\uff0c\u4f46\u5374\u6ca1\u6709\u4eba\u770b\u5f97\u5230\u6211\uff0c\u4e5f\u6ca1\u6709\u4eba\u542c \u5f97\u5230\u6211\u8bf4\u8bdd\uff0c\u6211\u5fc3\u91cc\u6b63\u5728\u5947\u602a\uff0c\u5ffd\u7136\u542c\u5230\u6881\u5988\u653e\u58f0\u5927\u54ed\u8d77\u6765\uff0c\u522b\u7684\u4eba\u4e5f\u7acb\u523b\u5168\u90fd\u8d76\u5230\u6211\u7684\u90a3 \u95f4\u5c4b\u5b50\u91cc\u53bb\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u54b3\u55fd\u4e86\u4e24\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u81ea\u5df2\u5462\uff1f\u662f\u5426\u4e5f\u56de\u53bb\u4e86\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u6211\u672c\u6765\u4e5f\u60f3\u56de\u5c4b\u5b50\u53bb\u770b\u770b\u7684\u3002\u4f46\u5374\u5ffd\u7136\u6709\u4e00\u9635\u98ce\u5439\u8fc7\u6765\uff0c\u6211\u7adf\u8eab\u4e0d\u7531\u4e3b\uff0c \u88ab\u98ce\u5439\u8fc7\u5899\uff0c\u5f8c\u6765\u2026\u2026\u5f8c\u6765\uff0e\u2026\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u8ffd\u95ee\u9053\uff1a\u201c\u5f8c\u6765\u600e\u6837\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u771f\u5947\u602a\uff0c\u5f8c\u6765\u7684\u4e8b\uff0c\u6211\u8fde\u4e00\u70b9\u4e5f\u4e0d\u8bb0\u5f97\u4e86\u3002\u201d \u3000\u3000\u706f\u706b\u867d\u5df2\u71c3\u8d77\uff0c\u4f46\u5c4b\u5b50\u91cc\u7684\u9634\u68ee\u4e4b\u610f\u5374\u4e1d\u6beb\u672a\u51cf\u3002 \u3000\u3000\u90a3\u5c11\u5973\u5168\u8eab\u53d1\u7740\u6296\uff0c\u6d41\u7740\u51b7\u6c57\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u600e\u4f1a\u5230\u8fd9\u91cc\u6765\u7684\uff0c\u6211\u5df2\u5c06\u6211\u80fd\u8bb0 \u5f97\u8d77\u7684\u4e8b\u5168\u90fd\u8bf4\u4e86\u51fa\u6765\uff0c\u4f60\u4eec\u2026\u2026\u4f60\u4eec\u7a76\u7adf\u8981\u5bf9\u6211\u600e\u6837\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u65b9\u5df2\u8bf4\u8fc7\uff0c\u6211\u4eec\u5bf9\u59d1\u5a18\u4f60\u7edd\u65e0\u6076\u610f\u2026\u2026\u201d \u3000\u3000\u90a3\u5c11\u5973\u5927\u58f0\u9053\uff1a\u201c\u65e2\u7136\u6ca1\u6709\u6076\u610f\uff0c\u4e3a\u4ec0\u9ebd\u8fd8\u4e0d\u653e\u6211\u56de\u53bb\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u77a7\u4e86\u5de6\u8f7b\u4faf\u4e00\u773c\uff0c\u52c9\u5f3a\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u7684\u73b0\u5728\u8fd8\u6ca1\u6709\u5927\u597d\u3002\u8fd8\u662f\u5148\u5728\u8fd9\u91cc\u4f11 \u517b\u4e9b\u65f6\u5019\uff0c\u7b49\u5230\u2026\u2026\u201d \u3000\u3000\u90a3\u5c11\u5973\u5ffd\u7136\u7ad9\u4e86\u8d77\u6765\uff0c\u53eb\u9053\uff1a\u201c\u6211\u4e0d\u8981\u5728\u8fd9\u91cc\u4f11\u517b\uff0c\u6211\u8981\u56de\u5bb6\u53bb\uff0c\u8c01\u6562\u518d\u62e6\u6211\uff0c\u6211\u5c31\u8ddf \u4ed6\u62fc\u547d\u201d \u3000\u3000\u547c\u58f0\u4e2d\uff0c\u5979\u4eba\u5df2\u98de\u63a0\u800c\u8d77\uff0c\u60f3\u51b2\u51fa\u7a97\u5b50\u3002 \u3000\u3000\u5de6\u8f7b\u4faf\u543c\u9053\uff1a\u201c\u62e6\u4f4f\u5979\uff0c\u62e6\u4f4f\u5979\u201d \u3000\u3000\u90a3\u5c11\u5973\u4f46\u89c9\u773c\u524d\u4e00\u82b1\uff0c\u4f46\u4e0d\u77e5\u600e\u5730\uff0c\u65b9\u8fd8\u7ad9\u5728\u5e8a\u8fb9\u7684\u695a\u7559\u9999\u5ffd\u7136\u5c31\u51fa\u73b0\u5979\u9762\u524d\uff0c\u62e6\u4f4f\u4e86 \u5979\u7684\u53bb\u8def\u3002 \u3000\u3000\u5979\u54ac\u54ac\u7259\uff0c\u7a81\u7136\u51fa\u624b\u5411\u695a\u7559\u9999\u80a9\u8180\u6293\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u5979\u5341\u6307\u7ea4\u7ea4\uff0c\u5f2f\u5c48\u5982\u722a\uff0c\u8eab\u5b50\u8fd8\u5728\u7a7a\u4e2d\uff0c\u4e24\u53ea\u624b\u5df2\u6293\u5411\u695a\u7559\u9999\u5de6\u53f3\u201c\u80a9\u4e95\u201d\u7a74\u3002\u51fa \u624b\u7adf\u662f\u5341\u5206\u72e0\u6bd2\u8001\u8fa3\u3002\u4f46\u695a\u7559\u9999\u8eab\u5b50\u4e00\u6ed1\uff0c\u5c31\u81ea\u5979\u8098\u4e0b\u7a7f\u8fc7\uff0c\u90a3\u5c11\u5973\u62db\u5f0f\u660e\u660e\u5df2\u7528\u8001\uff0c\u624b\u638c \u7a81\u53c8\u4e00\u7ffb\uff0c\u5de6\u638c\u53cd\u6293\u695a\u7559\u9999\u80a9\u5f8c\u201c\u79c9\u98ce\u201d\u3001\u201c\u66f2\u6c60\u201d\u4e24\u5904\u5927\u7a74\uff0c\u53f3\u638c\u626c\u8d77\u6293\u5411\u695a\u7559\u9999\u8170\u95f4 \u201c\u5c11\u6d77\u201d\u3001\u201c\u66f2\u6cfd\u201d\u4e24\u5904\u5927\u7a74\uff0c\u975e\u4f46\u53d8\u62db\u5947\u5feb\uff0c\u800c\u4e14\u4e00\u51fa\u624b\u5c31\u6293\u7684\u662f\u5bf9\u65b9\u5173\u8282\u5904\u7684\u8981\u5bb3\u5927 \u7a74\uff0c\u8ba4\u7a74\u4e4b\u51c6\uff0c\u66f4\u662f\u5168\u65e0\u5398\u7c73\u4e4b\u5dee\u3002 \u3000\u3000\u4f46\u695a\u7559\u9999\u6b66\u529f\u4e4b\u9ad8\uff0c\u53c8\u5c82\u662f\u8fd9\u79cd\u5e74\u7eaa\u8f7b\u8f7b\u7684\u5c0f\u6cf5\u5a18\u6240\u80fd\u60f3\u50cf\uff0c\u5979\u660e\u660e\u89c9\u5f97\u81ea\u5df1\u624b\u6307\u5df2\u89e6 \u53ca\u4e86\u695a\u7559\u9999\u7684\u7a74\u9053\uff0c\u53ea\u8981\u529b\u900f\u6307\u5c16\uff0c\u4fbf\u53ef\u5c06\u695a\u7559\u9999\u7a74\u9053\u634f\u4f4f\uff0c\u4ee4\u4ed6\u5168\u8eab\u9ebb\uff0c\u5931\u53bb\u62b5\u6297\u4e4b\u529b\u3002 \u3000\u3000\u8c01\u77e5\u5c31\u5728\u8fd9\u5239\u90a3\u95f4\uff0c\u695a\u7559\u9999\u7684\u8eab\u5b50\u5ffd\u7136\u53c8\u6e38\u9c7c\u822c\u6ed1\u4e86\u51fa\u53bb\uff0c\u6ed1\u5230\u5979\u80cc\u5f8c\uff0c\u6e29\u67d4\u7684\u4f4e\u8bed \u9053\uff1a\u201c\u59d1\u5a18\u8fd8\u662f\u5148\u7761\u4e00\u89c9\u5427\uff0c\u4e00\u89c9\u9192\u6765\uff0c\u4e8b\u60c5\u4e5f\u8bb8\u5c31\u4f1a\u53d8\u597d\u4e86\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u53ea\u89c9\u695a\u7559\u9999\u7684\u624b\u4f3c\u4e4e\u5728\u5979\u8eab\u4e0a\u8f7b\u8f7b\u62c2\u4e86\u62c2\uff0c\u8f7b\u67d4\u5f97\u5c31\u50cf\u662f\u6625\u65e5\u7684\u5fae\u98ce\uff0c\u4ee4\u4eba\u51e0\u4e4e \u611f\u89c9\u4e0d\u51fa\u3002 \u3000\u3000\u63a5\u7740\uff0c\u5979\u5c31\u89c9\u5f97\u6709\u4e00\u9635\u4ee4\u4eba\u65e0\u6cd5\u6297\u62d2\u7684\u7761\u610f\u7a81\u7136\u88ad\u6765\uff0c\u5979\u8eab\u5b50\u8fd8\u672a\u7ad9\u7a33\uff0c\u4fbf\u5df2\u5815\u5165\u7761 \u4e61\u3002 \u3000\u3000\u5f20\u7b80\u658b\u4e00\u76f4\u5728\u7559\u610f\u7740\u4ed6\u4eec\u7684\u51fa\u624b\uff0c\u8fd9\u65f6\u624d\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u9759\u5982\u5904\u5b50\uff0c\u52a8\u5982\u8131\u5154\uff0c \u7528\u8fd9\u4e24\u53e5\u8bdd\u6765\u5f62\u5bb9\u9999\u5e05\uff0c\u6b63\u662f\u518d\u4e5f\u6070\u5f53\u4e0d\u8fc7\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u7b49\u5230\u5de6\u8f7b\u4faf\u8d76\u8fc7\u6765\u7279\u90a3\u5c11\u5973\u6276\u4e0a\u5e8a\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u65b9\u5979\u7528\u7684\u662f\u4ec0\u9ebd\u6b66 \u529f\uff1f\u8001\u5148\u751f\u53ef\u770b\u51fa\u6765\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u5f20\u7b80\u658b\u6c89\u541f\u7740\uff0c\u9053\uff1a\u201c\u53ef\u662f\u5c0f\u9e70\u722a\u529b\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8001\u5148\u751f\u679c\u7136\u9ad8\u660e\uff0c\u5979\u7528\u7684\u6b63\u662f\u2018\u5c0f\u9e70\u722a\u529b\u2019\u5939\u6742\u7740\u2018\u4e03\u5341\u4e8c\u8def\u5206\u7b4b \u9519\u9aa8\u624b\u2019\uff0c\u800c\u4e14\u529f\u529b\u8fd8\u4e0d\u9519\u3002\u201d \u3000\u3000\u5f20\u7b80\u658b\u671b\u7740\u5de6\u8f7b\u4faf\uff0c\u7f13\u9053\uff1a\u201c\u636e\u8001\u592b\u6240\u77e5\uff0c\u6c5f\u6e56\u4e2d\u80fd\u7528\u8fd9\u79cd\u529f\u592b\u7684\u5973\u5b50\u5e76\u4e0d\u591a\uff0c\u53ea \u6709\u2026\u2026\u3002\u201d\u4ed6\u54b3\u54bd\u4e86\u4e24\u58f0\uff0c\u5ffd\u7136\u505c\u53e3\u4e0d\u8bed\u3002 \u3000\u3000\u5de6\u8f7b\u4faf\u5374\u5df2\u5389\u58f0\u9053\uff1a\u201c\u6211\u4e5f\u77e5\u9053\u2018\u5c0f\u9e70\u722a\u529b\u2019\u4e43\u662f\u65bd\u91d1\u5f13\u90a3\u8001\u5a46\u5a18\u7684\u5bb6\u4f20\u6b66\u529f\uff0c\u4f46\u5979\u4e5f \u660e\u660e\u662f\u6211\u7684\u5973\u513f\uff0c\u8c01\u4e5f\u4e0d\u80fd\u5426\u8ba4\u3002\u201d \u3000\u3000\u5f20\u7b80\u658b\u9053\uff1a\u201c\u4ee4\u5ad2\u6614\u65e5\u96be\u9053\u4e5f\u7ec3\u8fc7\u8fd9\u79cd\u529f\u592b\u9ebd\uff1f\u201d \u3000\u3000\u5de6\u8f7b\u4faf\u6014\u4e86\u6014\u8bf4\u4e0d\u51fa\u8bdd\u6765\u4e86\u3002 \u3000\u3000\u5176\u5b9e\u4ed6\u4e0d\u5fc5\u56de\u7b54\u522b\u4eba\u4e5f\u77e5\u9053\u5de6\u4e8c\u7237\u7684\uff1a\u201c\u98de\u82b1\u624b\u201d\u540d\u52a8\u6b66\u6797\uff0c\u4e43\u662f\u6c5f\u6e56\u4e2d\u53d8\u5316\u6700\u7e41\u590d\u7684 \u638c\u6cd5\uff0c\u800c\u4e14\u81f3\u9634\u81f3\u67d4\uff0c\u6b63\u662f\u201c\u9e70\u722a\u201d\u3001\u201c\u6454\u7891\u624b\u201d\u8fd9\u79cd\u9633\u521a\u638c\u6cd5\u7684\u514b\u661f\uff0c\u4ed6\u7684\u5973\u513f\u53c8\u600e\u4f1a\u7ec3 \u9e70\u722a\u529b\uff1f \u3000\u3000\u5f20\u7b80\u658b\u867d\u662f\u6c5f\u5357\u540d\u533b\uff0c\u4f46\u201c\u5f39\u6307\u795e\u901a\u201d\u7684\u529f\u529b\uff0c\u636e\u8bf4\u5df2\u7ec3\u5165\u5316\u5883\uff0c\u672c\u4e5f\u662f\u6b66\u6797\u4e2d\u7684\u5927\u884c \u5bb6\uff0c\u5bf9\u5404\u95e8\u540d\u6d3e\u7684\u6b66\u529f\uff0c\u5177\u90fd\u4e86\u5982\u638c\u6307\uff0c\u4ed6\u89c1\u5230\u5de6\u8f7b\u4faf\u7684\u5fe7\u6025\u6101\u82e6\u4e4b\u5bb9\uff0c\u4e5f\u4e0d\u7981\u9732\u51fa\u540c\u60c5\u4e4b \u8272\uff0c\u53f9\u9053\uff1a\u201c\u5e84\u4e3b\u6b64\u523b\u7684\u5fc3\u60c5\uff0c\u8001\u673d\u4e5f\u5e76\u975e\u4e0d\u77e5\u9053\uff0c\u53ea\u4e0d\u8fc7\uff0c\u4e16\u4e0a\u672c\u6709\u4e00\u4e9b\u4e0d\u53ef\u601d\u8bae\u3001\u65e0\u6cd5 \u89e3\u91ca\u7684\u4e8b\uff0c\u73b0\u5728\u8fd9\u79cd\u4e8b\u65e2\u5df2\u53d1\u751f\u2026\u2026\u201d \u3000\u3000\u5de6\u8f7b\u4faf\u560e\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u4e3a\u4f55\u4e00\u5b9a\u8981\u6211\u76f8\u4fe1\u8fd9\u79cd\u8352\u5510\u7684\u4e8b\uff1f\u4f60\u96be\u9053\u771f\u7684\u76f8\u4fe1\u8fd9\u662f\u501f\u5c38 \u8fd8\u9b42\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5f20\u8001\u5148\u751f\u7684\u610f\u601d\uff0c\u53ea\u4e0d\u8fc7\u662f\u8981\u4e8c\u54e5\u4f60\u5148\u51b7\u9759\u4e0b\u6765\uff0c\u5927\u5bb6\u518d\u60f3\u5982\u4f55\u5e94\u4ed8\u6b64\u4e8b \u7684\u6cd5\u5b50\u3002\u201d \u3000\u3000\u5f20\u7b80\u658b\u53f9\u9053\uff1a\u201c\u9999\u5e05\u8bf4\u7684\u4e0d\u9519\uff0c\u4eba\u529b\u4e5f\u5e76\u975e\u4e0d\u53ef\u80dc\u5929\u3002\u201d \u3000\u3000\u5de6\u8f7b\u4faf\u6413\u7740\u624b\uff0c\u8dfa\u7740\u811a\u9053\uff1a\u201c\u73b0\u5728\u6211\u7684\u5fc3\u4e5f\u4e71\u4e86\u3002\u4f60\u4eec\u8be5\u600e\u9ebd\u529e\uff0c\u5c31\u600e\u9ebd\u529e\u5427\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6c89\u58f0\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u7684\u786e\u6709\u8bb8\u591a\u4e0d\u53ef\u601d\u4eea\u4e4b\u5904\uff0c\u660e\u73e0\u600e\u4f1a\u5ffd\u7136\u4f1a\u4f7f\u91d1\u5f13\u592b\u4eba\u7684\u5bb6\u4f20 \u6b66\u529f\uff1f\u8fd9\u70b9\u66f4\u4ee4\u4eba\u65e0\u6cd5\u89e3\u91ca\uff0c\u4f46\u6211\u4eec\u8fd8\u662f\u8981\u5148\u67e5\u660e\u5979\u65b9\u8bf4\u7684\u8bdd\u7a76\u7adf\u662f\u771f\u662f\u5047\uff1f\u65bd\u91d1\u5f13\u7684\u5973\u513f \u662f\u5426\u771f\u7684\u6b7b\u4e86\u3002\u201d \u3000\u3000\u5de6\u8f7b\u4faf\u8dfa\u811a\u9053\uff1a\u201c\u4f60\u660e\u660e\u77e5\u9053\u65bd\u91d1\u5f13\u662f\u6211\u90a3\u6b7b\u5bf9\u5934\u8001\u602a\u7269\u7684\u4eb2\u5bb6\uff0c\u96be\u9053\u8fd8\u8981\u6211\u5230\u65bd\u5bb6\u5e84 \u53bb\u95ee\u5979\u9ebd\uff1f\u201d \u3000\u3000\u5f20\u7b80\u88d4\u9053\uff1a\u201c\u5de6\u5e84\u4e3b\u867d\u53bb\u4e0d\u5f97\uff0c\u4f46\u695a\u9999\u5e05\u5374\u662f\u53bb\u5f97\u7684\u3002\u201d \u3000\u3000\u5de6\u8f7b\u4faf\u9053\uff1a\u201c\u695a\u7559\u9999\u4e43\u662f\u5de6\u8f7b\u4faf\u7684\u597d\u670b\u53cb\uff0c\u8fd9\u4ef6\u4e8b\u6c5f\u6e56\u4e2d\u8c01\u4e0d\u77e5\u9053\uff0c\u695a\u7559\u9999\u5230\u4e86\u65bd\u5bb6 \u5e84\uff0c\u90a3\u8001\u865e\u5a46\u4e0d\u62ff\u626b\u628a\u5288\u4ed6\u51fa\u6765\u624d\u602a\u3002\u201d \u3000\u3000\u5f20\u7b80\u658b\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f46\u5e84\u4e3b\u4e5f\u83ab\u8981\u5fd8\u4e86\uff0c\u695a\u9999\u5e05\u7684\u8f7b\u529f\u5999\u7edd\u5929\u4e0b\uff0c\u8fde\u2018\u795e\u6c34\u5bab\u2019\u4ed6\u90fd \u53ef\u6765\u53bb\u81ea\u5982\uff0c\u53c8\u4f55\u51b5\u5c0f\u5c0f\u7684\u65bd\u5bb6\u5e84\uff1f\u201d \u53e4\u9f99\u300a\u695a\u7559\u9999\u7cfb\u5217\xb7\u9b3c\u604b\u4f20\u5947\u300b \u7b2c\u4e8c\u7ae0\u3000\u65bd\u5bb6\u5e84\u7684\u6bcd\u8001\u864e \u3000\u3000\u5176\u5b9e\u65bd\u5bb6\u5e84\u975e\u4f46\u4e0d\u5c0f\uff0c\u800c\u4e14\u89c4\u6a21\u4e4b\u96c4\u4f1f\uff0c\u8303\u56f4\u4e4b\u8fbd\u9614\uff0c\u90fd\u4e0d\u5728\u201c\u63b7\u676f\u5c71\u5e84\u201d\u4e4b\u4e0b\uff0c\u65bd\u5bb6 \u5e84\u7684\u5e84\u4e3b\u65bd\u5b5d\u5ec9\u867d\u4e0d\u662f\u6c5f\u6e56\u4e2d\u4eba\uff0c\u4f46\u65bd\u592b\u4eba\u82b1\u91d1\u5f13\u5728\u6c5f\u6e56\u4e2d\u5374\u662f\u8d6b\u8d6b\u6709\u540d\uff0c\u5979\u7684\u201c\u91d1\u5f13\u94f6\u5f39 \u94c1\u9e70\u722a\u201d\uff0c\u66f4\u53ef\u8bf4\u662f\u6c5f\u5357\u4e00\u7edd\u3002 \u3000\u3000\u65bd\u5bb6\u5e84\u8fd8\u6709\u4ef6\u5f88\u51fa\u540d\u7684\u4e8b\uff0c\u5c31\u662f\u201c\u6015\u8001\u5a46\u201d\uff0c\u6c5f\u6e56\u4e2d\u4eba\u5bf9\u201c\u65bd\u5bb6\u5e84\u201d\u4e5f\u8bb8\u8fd8\u4e0d\u592a\u719f\u6089\uff0c \u4f46\u63d0\u8d77\u201c\u72ee\u543c\u5e84\u201d\u6765\uff0c\u5374\u5f53\u771f\u662f\u65e0\u4eba\u4e0d\u77e5\uff0c\u65e0\u4eba\u4e0d\u6653\uff0c\u5de6\u8f7b\u4faf\u548c\u65bd\u5b5d\u5ec9\u672c\u662f\u4e16\u4ea4\uff0c\u5c31\u56e0\u4e3a\u4ed6 \u5a36\u4e86\u8fd9\u8001\u5a46\uff0c\u4e24\u4eba\u53cd\u76ee\u6210\u4ec7\u3002\u6709\u4e00\u6b21\u5de6\u4e8c\u7237\u4e58\u7740\u9152\u5f8c\uff0c\u8fd8\u5230\u65bd\u5bb6\u5e84\u95e8\u5916\u53bb\u6302\u4e86\u5757\u724c\u5b50\u201c\u5185\u6709 \u6076\u72ac\uff0c\u8bf8\u4eb2\u597d\u53cb\u4e00\u5f8b\u6b62\u6b65\u3002\u201d \u3000\u3000\u8fd9\u4ef6\u4e8b\u4e4b\u5f8c\uff0c\u4e24\u5bb6\u66f4\u662f\u52bf\u540c\u6c34\u706b\u3002 \u3000\u3000\u8fd9\u4ef6\u4e8b\u81ea\u7136\u4e5f\u88ab\u6c5f\u6e56\u4e2d\u4eba\u4f20\u4e3a\u7b11\u8bdd\uff0c\u53ea\u56e0\u4eba\u4eba\u90fd\u77e5\u9053\u65bd\u8001\u5e84\u4e3b\u56fa\u7136\u6709\u5b5d\u5e38\u4e4b\u5f0a\uff0c\u5c11\u5e84\u4e3b \u65bd\u4f20\u5b97\u66f4\u662f\u754f\u59bb\u5982\u864e\u3002 \u3000\u3000\u5176\u5b9e\u8fd9\u4e5f\u4e0d\u80fd\u602a\u65bd\u4f20\u5b97\u6ca1\u6709\u7537\u5b50\u6c14\u6982\uff0c\u53ea\u80fd\u602a\u4ed6\u5a36\u7684\u5ab3\u5987\uff0c\u6765\u5934\u5b9e\u5728\u592a\u5927\uff0c\u82b1\u91d1\u5f13\u867d\u7136 \u52c7\u608d\u6cfc\u8fa3\uff0c\u4f46\u4e5f\u60f9\u4e0d\u8d77\u5979\u8fd9\u95e8\u4eb2\u5bb6\u3002 \u3000\u3000\u6c5f\u6e56\u4e2d\u7b80\u76f4\u6ca1\u6709\u4eba\u80fd\u60f9\u5f97\u8d77\u5979\u8fd9\u95e8\u4eb2\u5bb6\uff0c\u53ea\u56e0\u5979\u7684\u4eb2\u5bb6\u5c31\u662f\u53f7\u79f0\u201c\u5929\u4e0b\u7b2c\u4e00\u5251\u5ba2\u201d\u7684\u5927 \u4fa0\u859b\u8863\u4eba\u3002 \u3000\u3000\u859b\u8863\u4eba\u5c11\u5e74\u65f6\u4ee5\u201c\u8840\u8863\u4eba\u201d\u4e4b\u540d\u95ef\u8361\u6c5f\u6e56\u65f6\uff0c\u4fa0\u610f\u601d\u4ec7\uff0c\u6740\u4eba\u5982\u8349\u82a5\u3002\u4e2d\u5e74\u5f8c\u5df2\u706b\u6c14\u6d88 \u78e8\uff0c\u9000\u9690\u6797\u4e0b\uff0c\u4f46\u4e00\u67c4\u5251\u5374\u66f4\u7ec3\u5f97\u51fa\u795e\u5165\u5316\uff0c\u636e\u8bf4\u56db\u5341\u5e74\u6765\uff0c\u4ece\u65e0\u4e00\u4eba\u80fd\u5728\u4ed6\u5251\u4e0b\u8d70\u8fc7\u5341 \u62db\u3002 \u3000\u3000\u800c\u859b\u8863\u4eba\u4e5f\u6b63\u662f\u5de6\u8f7b\u4faf\u7684\u751f\u51a4\u5bb6\u6d3b\u5bf9\u5934\u3002 \u3000\u3000\u591c\u8272\u6df1\u6c89\uff0c\u65bd\u5bb6\u5e84\u5185\u7684\u706f\u706b\u4e5f\u9634\u6697\u5f97\u5f88\u3002 \u3000\u3000\u5f8c\u56ed\u4e2d\u82b1\u6728\u90fd\u5df2\u51cb\u843d\uff0c\u79cb\u610f\u8083\u6740\uff0c\u665a\u98ce\u8083\u7d22\uff0c\u5c31\u8fde\u90a3\u4e00\u4e1b\u9ec4\u83ca\uff0c\u591c\u5e7d\u5e7d\u7684\u6708\u8272\u4e2d\u4e5f\u5f04\u4e0d \u8d77\u821e\u59ff\u3002 \u3000\u3000\u695a\u7559\u9999\u7684\u5fc3\u60c5\u4e5f\u6c89\u91cd\u5f97\u5f88\u3002 \u3000\u3000\u4ed6\u7684\u8f7b\u529f\u867d\u72ec\u6b65\u5929\u4e0b\uff0c\u4f46\u5230\u4e86\u8fd9\u91cc\uff0c\u8fd8\u662f\u4e0d\u6562\u4e1d\u6beb\u5927\u610f\uff0c\u6b63\u9690\u8eab\u5728\u4e00\u682a\u68a7\u6850\u6811\u4e0a\uff0c\u4e0d\u77e5 \u8be5\u5982\u4f55\u4e0b\u624b\u3002 \u3000\u3000\u7a81\u542c\u79cb\u98ce\u4e2d\u9690\u9690\u4f20\u6765\u4e00\u9635\u555c\u6ce3\u58f0\uff0c\u4ed6\u8eab\u5b50\u7acb\u523b\u8dc3\u8d77\uff0c\u98de\u71d5\u822c\u63a0\u4e86\u8fc7\u53bb\uff0c\u5728\u591c\u8272\u4e2d\u771f\u662f\u5c31 \u5b9b\u5982\u4e00\u53ea\u5de8\u5927\u7684\u8759\u8760\u3002 \u3000\u3000\u7af9\u6797\u4e2d\u6709\u51e0\u95f4\u7cbe\u81f4\u7684\u5c0f\u5c4b\uff0c\u4e00\u706f\u5982\u8c46\uff0c\u6ee1\u7a97\u660f\u9ec4\uff0c\u90a3\u60b2\u75db\u7684\u5438\u6ce3\u58f0\uff0c\u663e\u7136\u5c31\u662f\u4ece\u5c4b\u91cc\u4f20 \u51fa\u6765\u7684\u3002 \u3000\u3000\u5c4b\u89d2\u91cc\u653e\u7740\u5f20\u5e8a\uff0c\u5e8a\u65c1\u8fb9\u6709\u4e2a\u8e6c\u82b1\u7684\u7d2b\u64c5\u6728\u5986\u53f0\uff0c\u5986\u53f0\u65c1\u6709\u4e2a\u82b1\u67b6\uff0c\u665a\u98ce\u5165\u7a97\uff0c\u82b1\u67b6\u4e0a \u9999\u70df\u7ed5\u7ee6\uff0c\u53c8\u4e00\u4e1d\u4e1d\u6d88\u5931\u5728\u665a\u98ce\u91cc\u3002 \u3000\u3000\u5e8a\u4e0a\u4ef0\u5367\u7740\u4e00\u4e2a\u5973\u5b50\uff0c\u5374\u6709\u4e2a\u6ee1\u5934\u94f6\u53d1\u5982\u4e1d\u7684\u8001\u5987\u4eba\u6b63\u8dea\u5e8a\u8fb9\u60b2\u75db\u7684\u557c\u54ed\u7740\uff0c\u4eff\u4f5b\u8fd8\u95fb \u5979\u5583\u5583\u9053\uff1a\u201c\u8335\u513f\uff0c\u8335\u513f\uff0c\u4f60\u600e\u9ebd\u80fd\u6b7b\uff1f\u600e\u9ebd\u80fd\u6b7b\u2026\u2026\u201d \u3000\u3000\u695a\u7559\u9999\u53ea\u77aa\u4e86\u4e00\u773c\uff0c\u4fbf\u673a\u4f36\u4f36\u6253\u4e86\u4e2a\u5bd2\u9178\u3002 \u3000\u3000\u65bd\u5bb6\u7684\u5927\u59d1\u5a18\u679c\u7136\u6b7b\u4e86\uff0c\u5979\u95fa\u623f\u4e2d\u7684\u9648\u8bbe\u679c\u7136\u548c\u201c\u90a3\u5c11\u5973\u201d\u6240\u8bf4\u7684\u5b8c\u5168\u4e00\u6837\uff0c\u800c\u4e14\u5979\u8eab \u4e0a\u7a7f\u7740\u7684\uff0c\u4e5f\u731b\u7136\u6b63\u662f\u4e00\u4ef6\u6c34\u7ea2\u8272\u7684\u7ec7\u9526\u7f0e\u8863\u88f3\uff0c\u4e0a\u9762\u4e5f\u731b\u7136\u7ee3\u4e86\u51e0\u53ea\u6829\u6829\u5982\u751f\u7684\u7d2b\u51e4\u51f0\u3002 \u3000\u3000\u4f46\u5979\u7684\u5c38\u8eab\u4e3a\u4f55\u8fd8\u672a\u88c5\u6b93\uff0c\u6b64\u523b\u8dea\u5728\u5e8a\u8fb9\u54c0\u6389\u7684\u53c8\u662f\u8c01\u5462\uff1f\u695a\u7559\u9999\u77e5\u9053\u8fd9\u8001\u5987\u4eba\u7edd\u4e0d\u662f \u82b1\u91d1\u5f13\u3002 \u3000\u3000\u90a3\u9ebd\uff0c\u5979\u96be\u9053\u5c31\u662f\u201c\u90a3\u5c11\u5973\u201d\u6240\u8bf4\u7684\u6881\u5988\uff1f \u3000\u3000\u53ea\u89c1\u90a3\u8001\u5987\u4eba\u54ed\u7740\u54ed\u7740\uff0c\u5934\u6e10\u6e10\u4f4e\u4e86\u4e0b\u53bb\uff0c\u4f0f\u5230\u5e8a\u4e0a\uff0c\u4fdd\u662f\u56e0\u4e3a\u60b2\u75db\u8fc7\u5ea6\uff0c\u7adf\u5728\u4e0d\u77e5\u4e0d \u89c9\u95f4\u7761\u7740\u4e86\u3002 \u3000\u3000\u6c34\u7ea2\u8272\u7684\u7ec7\u9526\u7f0e\uff0c\u6811\u7740\u5979\u6ee1\u5934\u82cd\u82cd\u767d\u53d1\uff0c\u4e00\u7f15\u7f15\u8f7b\u70df\uff0c\u56f4\u8fc7\u4e86\u63a5\u7740\u7d2b\u7ed2\u7a97\u7684\u7a97\u5b50\u3002 \u3000\u3000\u8fdc\u5904\u6709\u96f6\u843d\u7684\u66f4\u9f13\u58f0\u4f20\u6765\uff0c\u5df2\u662f\u56db\u66f4\u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\u5fc3\u91cc\u4e5f\u4e0d\u7981\u6cdb\u8d77\u4e00\u79cd\u51c4\u51c9\u4e4b\u610f\uff0c\u53c8\u89c9\u5f97\u6709\u70b9\u5bd2\u610f\u7684\uff0c\u751a\u81f3\u8fde\u90a3\u6f02\u6e3a\u56db\u6563\u7684\u9999\u6c14 \u4e2d\uff0c\u90fd\u4eff\u4f5b\u5e26\u7740\u79cd\u8be1\u79d8\u6050\u6016\u7684\u6b7b\u4ea1\u6c14\u606f\u3002 \u3000\u3000\u4ed6\u9690\u8eab\u5728\u7a97\u5916\u7684\u9ed1\u6697\u4e2d\uff0c\u6728\u7acb\u4e86\u534a\u664c\uff0c\u89c1\u5230\u5e8a\u8fb9\u7684\u8001\u5987\u4eba\u9f3b\u606f\u7eed\u6e10\u6c89\u91cd\uff0c\u4f3c\u5df2\u771f\u7684\u7761\u7740 \u4e86\uff0c\u4ed6\u8fd9\u624d\u8f7b\u8f7b\u7a7f\u7a97\u5165\u5c4b\u811a\u6b65\u751a\u81f3\u6bd4\u7a97\u5916\u7684\u79cb\u98ce\u8fd8\u8f7b\uff0c\u5c31\u7b97\u90a3\u8001\u5987\u4eba\u6ca1\u6709\u7761\u7740\uff0c\u4e5f\u7edd\u4e0d\u4f1a\u542c \u5f97\u5230\u3002 \u3000\u3000\u5e8a\u4e0a\u7684\u5c11\u5973\u9762\u5982\u8721\u8272\uff0c\u5f62\u8272\u67af\u7a3f\uff0c\u7626\u5f97\u53ea\u5269\u4e0b\u4e00\u628a\u9aa8\u5934\u4e86\uff0c\u6b7b\u524d\u60f3\u5fc5\u5df2\u548c\u75c5\u9b54\u6323\u624e\u4e86\u5f88 \u4e45\u3002 \u3000\u3000\u8fd9\u5c11\u5973\u7709\u76ee\u867d\u548c\u5de6\u660e\u73e0\u7edd\u6ca1\u6709\u4e1d\u6beb\u76f8\u4f3c\u4e4b\u5916\uff0c\u4f46\u4f9d\u7a00\u72b9\u53ef\u770b\u51fa\u5979\u751f\u524d\u5fc5\u5b9a\u4e5f\u662f\u4e2a\u7f8e\u4eba\u3002 \u3000\u3000\u800c\u73b0\u5728\uff0c\u6b7b\u4ea1\u975e\u4f46\u5df2\u593a\u53bb\u4e86\u5979\u7684\u751f\u547d\uff0c\u4e5f\u593a\u4e86\u5979\u7684\u7f8e\u4e3d\uff0c\u6b7b\u4ea1\u5168\u4e0d\u61c2\u601c\u60dc\u7edd\u4e0d\u4f1a\u4e3a\u4efb\u4f55 \u4eba\u7559\u4e0b\u4ec0\u9ebd\u3002 \u3000\u3000\u695a\u7559\u9999\u7ad9\u5728\u90a3\u8001\u5987\u4eba\u8eab\u5f8c\u671b\u7740\u5e8a\u4e0a\u5c11\u5973\u7684\u5c38\u8eab\uff0c\u671b\u7740\u5979\u8863\u88f3\u4e0a\u90a3\u53ea\u51e4\u51f0\uff0c\u60f3\u5230\u201c\u90a3\u5c11 \u5973\u201d\u8bf4\u7684\u8bdd\uff0c\u638c\u5fc3\u5ffd\u7136\u6c81\u51fa\u51b7\u6c57\u3002 \u3000\u3000\u4ed6\u8d76\u5feb\u8f6c\u8fc7\u8eab\uff0c\u62ff\u8d77\u5986\u53f0\u4e0a\u4e00\u76d2\u82b1\u7c89\uff0c\u53ea\u89c1\u76d2\u5e95\u5370\u7740\u4e00\u65b9\u5c0f\u5c0f\u7684\u6731\u5370\uff0c\u4e0a\u5728\u5199\u7684\u8d6b\u7136\u6b63 \u662f\u201c\u4eac\u90fd\u5b9d\u9999\u658b\u201d\u3002\u62ff\u7740\u8fd9\u76d2\u793c\u7c89\uff0c\u695a\u7559\u9999\u53ea\u89c9\u5168\u8eab\u7684\u5bd2\u6bdb\u90fd\u4e00\u6839\u6839\u7ad6\u4e86\u8d77\u6765\uff0c\u624b\u4e0a\u7684\u51b7\u6c57 \u5df2\u6e17\u5165\u4e86\u7c89\u76d2\u3002 \u3000\u3000\u7a81\u542c\u90a3\u8001\u5987\u4eba\u5636\u58f0\u64bc\u9053\uff1a\u201c\u4f60\u4eec\u62a2\u8d70\u4e86\u6211\u7684\u8335\u513f\uff0c\u8fd8\u6211\u7684\u8335\u513f\u6765\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7684\u624b\u4e00\u9707\uff0c\u82b1\u7c89\u76d2\u5df2\u6389\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u8001\u5987\u4eba\u4e00\u53cc\u5df2\u4e7e\u762a\u4e86\u7684\u624b\u7d27\u7d27\u6293\u7740\u6b7b\u8eab\u4e0a\u7a7f\u7684\u7ea2\u7f0e\u8863\u670d\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u53c8\u6e10\u6e10\u653e \u677e\u3002 \u3000\u3000\u5979\u53d1\u9ec4\u7684\u8116\u5b50\u4e0a\u5192\u4e86\u4e00\u7c92\u7c92\u51b7\u6c57\uff0c\u4f46\u5934\u53c8\u4f0f\u5728\u5e8a\u4e0a\uff0c\u5598\u606f\u53c8\u60ed\u6e10\u5e73\u9759\uff0c\u53c8\u6e10\u6e10\u7761\u7740\u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\u8fd9\u4e00\u751f\u4e2d\uff0c\u4e5f\u4e0d\u77e5\u9047\u89c1\u8fc7\u591a\u5c11\u60ca\u9669\u53ef\u6016\u7684\u4e8b\uff0c\u4f46\u5374\u4ece\u6765\u4e5f\u6ca1\u6709\u88ab\u5413\u5f97\u5982\u6b64\u5389\u5bb3\u3002 \u3000\u3000\u4ed6\u81ea\u7136\u4e0d\u662f\u6015\u8fd9\u8001\u5987\u4eba\uff0c\u4e5f\u4e0d\u662f\u6015\u5e8a\u4e0a\u7684\u6b7b\uff0c\u4e25\u683c\u8bf4\u6765\uff0c\u4ed6\u81ea\u5df2\u90fd\u4e0d\u77e5\u9053\u6015\u7684\u662f\u4ec0\u9ebd\u3002 \u3000\u3000\u4ed6\u53ea\u89c9\u8fd9\u5c4b\u5b50\u91cc\u5145\u6ee1\u4e86\u4e00\u79cd\u9634\u68ee\u8be1\u79d8\u7684\u9b3c\u6c14\uff0c\u50cf\u662f\u968f\u65f6\u90fd\u53ef\u80fd\u6709\u4ee4\u4eba\u4e0d\u53ef\u6297\u62d2\u3001\u4e5f\u65e0\u6cd5 \u601d\u53ca\u7684\u4e8b\u53d1\u751f\u4e00\u6837\u3002 \u3000\u3000\u201c\u501f\u5c38\u8fd8\u9b42\u201d\u8fd9\u79cd\u4e8b\u4ed6\u672c\u6765\u4e5f\u7edd\u4e0d\u4f1a\u76f8\u4fe1\uff0c\u53ef\u662f\u73b0\u5728\u3002\u6240\u6709\u7684\u8bc1\u636e\u90fd\u5728\u4ed6\u773c\u524d\uff0c\u4ed6\u5df2\u65e0 \u6cd5\u4e0d\u4fe1\u3002 \u3000\u3000\u4e00\u9635\u98ce\u5439\u8fc7\uff0c\u5377\u8d77\u4e86\u7d2b\u7ed2\u7a97\u5e18\uff0c\u7a97\u5e18\u91cc\u5c31\u50cf\u6709\u4e2a\u53ef\u6015\u7684\u5e7d\u7075\u8981\u4e58\u52bf\u800c\u8d77\uff0c\u4ee4\u4eba\u6068\u4e0d\u5f97\u7acb \u523b\u5c31\u79bb\u5f00\u8fd9\u5c4b\u5b50\uff0c\u8d70\u5f97\u8d8a\u8fdc\u8d8a\u597d\u3002 \u3000\u3000\u695a\u7559\u9999\u5728\u8863\u670d\u4e0a\u64e6\u4e7e\u4e86\u624b\u638c\uff0c\u62fe\u8d77\u4e86\u5730\u4e0a\u7684\u82b1\u7c89\u3002 \u3000\u3000\u4ed6\u4e00\u5b9a\u8981\u5c06\u8fd9\u76d2\u7c89\u5e26\u56de\u53bb\uff0c\u8ba9\u5de6\u8f7b\u4faf\u5224\u65ad\uff0c\u5426\u5219\uff0c\u4ed6\u771f\u4e0d\u77e5\u8be5\u5982\u4f55\u5411\u5de6\u8f7b\u4faf\u89e3\u91ca\u3002 \u3000\u3000\u8fd9\u4ef6\u4e8b\u6839\u672c\u5c31\u65e0\u6cd5\u89e3\u91ca\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u7684\u8170\u521a\u5f2f\u4e0b\u53bb\u5c31\u53d1\u73b0\u4e86\u4e00\u53cc\u7ee3\u978b\u3002 \u3000\u3000\u695a\u7559\u9999\u8fd9\u4e00\u751f\uff0c\u4e5f\u4e0d\u77e5\u89c1\u5230\u8fc7\u591a\u5c11\u53cc\u7ee3\u978b\uff0c\u89c1\u8fc7\u5404\u5f0f\u5404\u6837\u7684\u7ee3\u978b\uff0c\u7a7f\u5728\u5404\u5f0f\u5404\u6837\u7684\u5973\u4eba \u811a\u4e0a\u3002\u4ed6\u4ece\u6765\u4e0d\u66fe\u60f3\u5230\u4e00\u53cc\u7ee3\u978b\u4e5f\u4f1a\u4ee4\u4ed6\u5403\u60ca\u3002\u4f46\u73b0\u5728\u4ed6\u7684\u786e\u5403\u4e86\u4e00\u60ca\u3002 \u3000\u3000\u8fd9\u53cc\u7ee3\u978b\u5c31\u50cf\u7a81\u7136\u767d\u5730\u4e0a\u7684\u9b3c\u72f1\u4e2d\u5192\u51fa\u6765\u7684\u3002 \u3000\u3000\u4e25\u683c\u8bf4\u6765\uff0c\u4ed6\u5e76\u6ca1\u6709\u770b\u5230\u4e00\u53cc\u978b\u5b50\uff0c\u53ea\u4e0d\u8fc7\u770b\u5230\u4e00\u53cc\u978b\u5c16\uff0c\u978b\u5c16\u5f88\u7ea4\u5de7\uff0c\u7eff\u8272\u7684\u978b\u5c16\uff0c \u770b\u6765\u50cf\u662f\u4e00\u53cc\u65b0\u53d1\u7684\u6625\u7b0b\u3002 \u3000\u3000\u978b\u5b50\u7684\u5176\u4ed6\u90e8\u4efd\uff0c\u90fd\u88ab\u4e00\u53cc\u6c34\u8471\u8272\u7684\u6d12\u811a\u88e4\u7ba1\u76d6\u4f4f\u4e86\uff0c\u811a\u88e4\u4e0a\u8fd8\u7ee3\u7740\u91d1\u8fb9\uff0c\u7ee3\u5f97\u5f88\u7cbe \u81f4\u3002 \u3000\u3000\u8fd9\u672c\u662f\u53cc\u5f88\u7f8e\u7684\u7ee3\u978b\uff0c\u4e00\u6761\u5f88\u7f8e\u7684\u88e4\u5b50\uff0c\u4f46\u4e5f\u4e0d\u77e5\u4e3a\u4ec0\u9ebd\uff0c\u695a\u7559\u9999\u7adf\u4e0d\u7531\u81ea\u4e3b\u60f3\u5230\uff0c\u8fd9 \u53cc\u811a\u4e0a\u9762\u4f1a\u4e0d\u4f1a\u6ca1\u6709\u5934\uff1f \u3000\u3000\u4ed6\u5fcd\u4e0d\u4f4f\u8981\u5f80\u4e0a\u7784\uff0c\u4f46\u8fd8\u6ca1\u6709\u77a7\u89c1\uff0c\u5c31\u542c\u5230\u4e00\u4eba\u51b7\u51b7\u9053\uff1a\u201c\u5c31\u8fd9\u6837\u8e72\u7740\uff0c\u83ab\u8981\u52a8\uff0c\u4f60\u5168 \u8eab\u4e0a\u4e0b\u65e0\u8bba\u4f55\u5904\u53ea\u8981\u79fb\u52a8\u4e86\u534a\u5bf8\uff0c\u6211\u7acb\u523b\u5c31\u6253\u70c2\u4f60\u7684\u5934\u3002\u201d \u3000\u3000\u8fd9\u65e0\u7591\u662f\u5973\u4eba\u5728\u8bf4\u8bdd\uff0c\u58f0\u97f3\u53c8\u51b7\u3001\u53c8\u786c\uff0c\u4e1d\u6beb\u4e5f\u6ca1\u6709\u5973\u4eba\u90a3\u6e29\u67d4\u4f18\u7f8e\u4e4b\u610f\uff0c\u53ea\u542c\u5979\u7684\u58f0 \u97f3\uff0c\u5c31\u77e5\u9053\u8fd9\u79cd\u5973\u4eba\u82e5\u8bf4\u8981\u6253\u70c2\u4e00\u4e2a\u4eba\u5934\uff0c\u5979\u5c31\u4e00\u5b9a\u80fd\u505a\u5f97\u5230\uff0c\u800c\u4e14\u7edd\u4e0d\u4f1a\u53ea\u6253\u70c2\u534a\u4e2a\u3002 \u3000\u3000\u695a\u7559\u9999\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u5728\u5973\u4eba\u9762\u524d\uff0c\u4ed6\u4ece\u4e0d\u505a\u4e0d\u5fc5\u8981\u7684\u5192\u9669\u3002 \u3000\u3000\u4f55\u51b5\uff0c\u8fd9\u4e5f\u8bb8\u5e76\u4e0d\u662f\u4e2a\u5973\u4eba\uff0c\u800c\u662f\u4e2a\u5973\u9b3c\u3002 \u3000\u3000\u8fd9\u58f0\u97f3\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff0c\u5077\u5077\u6478\u6478\u7684\u5728\u8fd9\u91cc\u5e72\u4ec0\u9ebd\uff1f\u5feb\u8001\u8001\u5b9e\u5b9e\u8bf4\u51fa\u6765\u3002\u4f46\u8bb0\u7740\uff0c\u6211\u53ea\u8981 \u4f60\u7684\u5634\u52a8\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u8003\u8651\u4e86\u5f88\u4e45\uff0c\u89c9\u5f97\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u8fd8\u662f\u8bf4\u8001\u5b9e\u8bdd\u6700\u597d\uff0c\u201c\u695a\u7559\u9999\u201d\u8fd9\u540d\u5b57\u65e0\u8bba\u662f \u4eba\u662f\u9b3c\u542c\u4e86\u4e5f\u90fd\u4f1a\u5403\u4e00\u60ca\u3002 \u3000\u3000\u53ea\u8981\u5979\u5403\u4e00\u60ca\uff0c\u4ed6\u5c31\u6709\u673a\u4f1a\u4e86\u3002 \u3000\u3000\u65bc\u662f\u4ed6\u7acb\u523b\u9053\uff1a\u201c\u5728\u4e0b\u695a\u7559\u9999\u2026\u2026\u201d \u3000\u3000\u8c01\u77e5\u4ed6\u7684\u8bdd\u8fd8\u672a\u8bf4\u5b8c\u8fd9\u5973\u5b50\u5c31\u51b7\u7b11\u4e86\u8d77\u6765\u9053\uff1a\u201c\u695a\u7559\u9999\uff01\u563f\u563f\uff0c\u4f60\u82e5\u662f\u695a\u7559\u9999\u6211\u5c31\u662f\u6c34 \u6bcd\u2018\u9634\u59ec\u2019\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53ea\u6709\u82e6\u7b11\u6bcf\u6b21\u4ed6\u8bf4\u81ea\u5df2\u662f\u201c\u5f20\u53c1\u674e\u56db\u201d\u65f6\uff0c\u522b\u4eba\u603b\u8981\u6000\u7591\u4ed6\u662f\u695a\u7559\u9999\uff0c\u4f46\u6bcf\u6b21\u4ed6 \u771f\u8bf4\u51fa\u81ea\u5df1\u7684\u540d\u5b57\uff0c\u522b\u4eba\u53cd\u800c\u4e0d\u4fe1\uff0c\u800c\u4e14\u8fd8\u4f3c\u4e4e\u89c9\u5f97\u72e0\u53ef\u7b11\u3002 \u3000\u3000\u53ea\u542c\u8fd9\u5973\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u5176\u5b9e\u6211\u65e9\u5c31\u5df2\u77e5\u9053\u4f60\u662f\u8c01\uff0c\u4f60\u4f11\u60f3\u7792\u5f97\u8fc7\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u82e6\u7b11\u9053\uff1a\u201c\u6211\u82e5\u4e0d\u662f\u695a\u7559\u9999\uff0c\u90a3\u9ebd\u6211\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u8fd9\u5973\u5b50\u5389\u58f0\uff1a\u201c\u6211\u77e5\u9053\u4f60\u5c31\u662f\u90a3\u4e2a\u5c0f\u755c\u7272\uff0c\u90a3\u4e2a\u8be5\u6b7b\u7684\u5c0f\u755c\u7272\u3002\u4f46\u6211\u5374\u672a\u60f3\u5230\u4f60\u5c45\u7136\u8fd8 \u6709\u80c6\u5b50\u6562\u5230\u8fd9\u91cc\u6765\u3002\u201d \u3000\u3000\u5979\u7684\u58f0\u97f3\u5ffd\u7136\u5145\u6ee1\u5fff\u6012\uff0c\u5389\u58f0\u53c8\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u8335\u513f\u662f\u600e\u9ebd\u6b7b\u7684\u9ebd\uff1f\u4ed6\u5c31\u662f\u6b7b\u5728\u4f60\u624b\u4e0a \u7684\uff0c\u4f60\u5bb3\u4e86\u5979\u4e00\u8f88\u5b50\uff0c\u5bb3\u6b7b\u4e86\u5979\u8fd8\u4e0d\u591f\uff0c\u8fd8\u60f3\u6765\u5e72\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u5b8c\u5168\u4e0d\u77e5\u9053\u5979\u5728\u8bf4\u4ec0\u9ebd\uff0c\u53ea\u6709\u7d27\u7d27\u95ed\u7740\u5634\u3002 \u3000\u3000\u8fd9\u5973\u5b50\u66f4\u6124\u6012\u5730\u9053\uff1a\u201c\u4f60\u660e\u660e\u77e5\u9053\u8335\u513f\u5df2\u8bb8\u914d\u7ed9\u859b\u5927\u4fa0\u7684\u4e8c\u516c\u5b50\u4e86\uff0c\u5c45\u7136\u8fd8\u6709\u80c6\u5b50\u52fe\u5f15 \u5979\uff0c\u4f60\u4ee5\u4e3a\u8fd9\u4e9b\u4e8b\u6211\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u73b0\u5728\u81ea\u7136\u5df2\u77e5\u9053\u8fd9\u5973\u4eba\u5e76\u4e0d\u662f\u9b3c\uff0c\u800c\u662f\u65bd\u8335\u7684\u6bcd\u4eb2\uff0c\u5c31\u662f\u4ee5\u6cfc\u8fa3\u95fb\u540d\u6c5f\u6e56\u7684\u91d1\u5f13 \u592b\u4eba\u3002 \u3000\u3000\u4ed6\u5e73\u751f\u6700\u5934\u75db\u7684\u5c31\u662f\u6cfc\u8fa3\u7684\u5973\u4eba\u3002 \u3000\u3000\u7a81\u542c\u4e00\u4eba\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u5c31\u662f\u53f6\u76db\u5170\u9ebd\uff1f\u80c6\u5b50\u5012\u771f\u4e0d\u5c0f\u3002\u201d\u8fd9\u58f0\u97f3\u6bd4\u82b1\u91d1\u5f13\u66f4\u5c16\u9510\uff0c\u66f4\u5389 \u5bb3\u3002 \u3000\u3000\u695a\u7559\u9999\u773c\u524d\u53c8\u51fa\u73b0\u4e86\u4e00\u53ea\u817f\uff0c\u7a7f\u7740\u6c34\u7ea2\u8272\u7684\u978b\uff0c\u5927\u7ea2\u7f0e\u5b50\u7684\u5f13\u978d\u978b\u5c16\u4e0a\u8fd8\u6709\u4e2a\u7ea2\u7ed2\u3002 \u3000\u3000\u82e5\u8981\u770b\u4e00\u4e2a\u5973\u4eba\u7684\u813e\u6c14\uff0c\u53ea\u8981\u770b\u770b\u5979\u7a7f\u7684\u4ec0\u9ebd\u978b\u5b50\u5c31\u53ef\u77e5\u9053\uff0c\u8fd9\u53ea\u978b\u5b50\u770b\u6765\u5c31\u6d3b\u50cf\u662f\u4e24 \u53ea\u7ea2\u8fa3\u6912\u3002 \u3000\u3000\u695a\u7559\u9999\u6697\u4e2d\u53f9\u4e86\u53e3\u6c14\uff0c\u4e16\u4e0a\u8fd8\u6709\u6bd4\u9047\u89c1\u4e00\u4e2a\u6cfc\u5987\u66f4\u5934\u75db\u7684\u4e8b\uff0c\u90a3\u5c31\u662f\u9047\u89c1\u4e86\u4e24\u4e2a\u6cfc\u5987\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u5728\u8fd9\u79cd\u5973\u4eba\u9762\u524d\uff0c\u5c31\u7b97\u6709\u5929\u5927\u7684\u9053\u7406\u4e5f\u8bb2\u4e0d\u6e05\u7684\uff0c\u6700\u597d\u7684\u6cd5\u5b50\u5c31\u662f\u8d76\u5feb\u811a\u5e95\u63e9 \u6cb9\uff0c\u6e9c\u4e4b\u5927\u5409\u3002 \u3000\u3000\u4f46\u4ed6\u4e5f\u77e5\u9053\u82b1\u91d1\u5f13\u7684\u94f6\u5f39\u5fc5\u5b9a\u5df2\u5bf9\u51c6\u4e86\u4ed6\u7684\u8111\u888b\uff0c\u4f55\u51b5\u8fd9\u4f4d\u201c\u7ea2\u88e4\u5b50\u201d\u59d1\u5a18\u770b\u6765\u516b\u6210\u5c31 \u662f\u859b\u8863\u4eba\u7684\u5927\u5973\u513f\uff0c\u65bd\u5bb6\u5e84\u7684\u5927\u5ab3\u5987\uff0c\u859b\u8863\u4eba\u5251\u6cd5\u72ec\u6b65\u5929\u4e0b\uff0c\u4ed6\u7684\u5973\u513f\u4e5f\u7edd\u4e0d\u4f1a\u6302\u7701\u6cb9\u706f\u3002 \u3000\u3000\u4ed6\u5e76\u4e0d\u662f\u6015\u5979\u4eec\uff0c\u53ea\u4e0d\u8fc7\u5b9e\u5728\u4e0d\u613f\u610f\u548c\u8fd9\u79cd\u5973\u4eba\u52a8\u624b\u3002 \u3000\u3000\u53ea\u542c\u82b1\u91d1\u5f13\u9053\uff1a\u201c\u5c11\u5976\u5976\u4f60\u6765\u5f97\u6b63\u597d\uff0c\u4f60\u770b\u6211\u4eec\u8be5\u628a\u8fd9\u5c0f\u5b50\u5982\u4f55\u5904\u6cbb\u3002\u201d \u3000\u3000\u65bd\u5c11\u5976\u5976\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u79cd\u767b\u5f92\u5b50\uff0c\u6574\u5929\u52fe\u5f15\u826f\u5bb6\u5987\u5973\uff0c\u6d3b\u57cb\u4e86\u6700\u597d\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53c8\u597d\u6c14\uff0c\u53c8\u597d\u7b11\uff0c\u4e5f\u96be\u602a\u65bd\u5c11\u5e84\u4e3b\u754f\u59bb\u5982\u864e\u4e86\uff0c\u539f\u6765\u8fd9\u4f4d\u5c11\u5976\u5976\u4e0d\u95ee\u9752\u7ea2\u7682\u767d\u5c31 \u8981\u6d3b\u57cb\u4eba\u3002 \u3000\u3000\u82b1\u91d1\u5f13\u9053\uff1a\u201c\u6d3b\u57cb\u8fd8\u592a\u4fbf\u5b9c\u4e86\u4ed6\uff0c\u4f9d\u6211\u770b\uff0c\u4e7e\u8106\u70b9\u4ed6\u7684\u5929\u706f\u3002\u201d \u3000\u3000\u65bd\u5c11\u5976\u5976\u9053\uff1a\u201c\u70b9\u5929\u706f\u4e5f\u884c\uff0c\u4f46\u6211\u5012\u60f3\u5148\u770b\u770b\u4ed6\uff0c\u7a76\u7adf\u6709\u54ea\u70b9\u6bd4\u6211\u4eec\u5bb6\u8001\u4e8c\u5f3a\uff0c\u5c45\u7136\u80fd \u5bb3\u5f97\u8335\u59d1\u5a18\u4e3a\u4ed6\u5f97\u76f8\u601d\u75c5\u3002\u201d \u3000\u3000\u82b1\u91d1\u5f13\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5582\uff0c\u5c0f\u4f19\u5b50\uff0c\u4f60\u62ac\u8d77\u5934\u6765\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5012\u4e5f\u60f3\u770b\u770b\u5979\u4eec\u7684\u6a21\u6837\u3002 \u3000\u3000\u53ea\u89c1\u8fd9\u4f4d\u91d1\u5f13\u592b\u4eba\u5e74\u7eaa\u867d\u7136\u5df2\u6709\u4e94\u5341\u591a\u4e86\uff0c\u4f46\u4ecd\u7136\u6253\u626e\u5f97\u82b1\u679d\u62db\u5c55\uff0c\u8138\u4e0a\u7684\u7c89\u522e\u4e0b\u6765\u8d77 \u7801\u4e5f\u6709\u4e00\u65a4\u3002 \u3000\u3000\u800c\u4e14\u5979\u90a3\u53cc\u773c\u775b\u4ecd\u662f\u6c34\u6c6a\u6c6a\u7684\uff0c\u5de6\u8fb9\u4e00\u779f\uff0c\u53f3\u8fb9\u4e00\u8f6c\uff0c\u8fd8\u771f\u6709\u51e0\u5206\u9500\u9b42\u4e4b\u610f\uff0c\u60f3\u5f53\u521d\u65bd \u4e3e\u4eba\u5fc5\u5b9a\u5c31\u662f\u8fd9\u9ebd\u6837\u88ab\u5979\u52fe\u4e0a\u7684\u3002 \u3000\u3000\u90a3\u65bd\u5c11\u5976\u5976\u5374\u4e0d\u6562\u606d\u7ef4\uff0c\u957f\u957f\u7684\u4e00\u5f20\u9a6c\u8111\uff0c\u8840\u76c6\u822c\u4e00\u5f20\u5927\u5634\uff0c\u9f3b\u5b50\u5374\u6bd4\u5634\u8fd8\u8981\u5927\u4e0a\u4e00 \u500d\u3002 \u3000\u3000\u5979\u82e5\u4e0d\u662f\u859b\u8863\u4eba\u7684\u5973\u513f\uff0c\u80fd\u5ac1\u5f97\u51fa\u53bb\u624d\u602a\u3002 \u3000\u3000\u695a\u7559\u9999\u5ffd\u7136\u89c9\u5f97\u90a3\u4f4d\u65bd\u5c11\u5e84\u4e3b\u5f88\u503c\u5f97\u540c\u60c5\uff0c\u5a36\u5f97\u4e2a\u6cfc\u5987\u5df2\u7ecf\u591f\u53ef\u601c\u7684\u4e86\uff0c\u800c\u4ed6\u5a36\u7684\u7b80\u76f4 \u662f\u6761\u6bcd\u9a6c\u3002 \u3000\u3000\u695a\u7559\u9999\u5728\u6253\u91cf\u7740\u5979\u4eec\u7684\u65f6\u5019\uff0c\u5979\u4eec\u81ea\u7136\u4e5f\u5728\u6253\u91cf\u695a\u7559\u9999\uff0c\u82b1\u91d1\u5f13\u90a3\u53cc\u773c\u775b\u56fa\u7136\u8981\u6ef4\u4e0b\u6c34 \u6765\uff0c\u5c31\u8fde\u5c11\u5976\u5976\u90a3\u53c8\u7ec6\u53c8\u957f\u7684\u9a6c\u773c\u4e5f\u4f3c\u4e4e\u53d8\u5f97\u6c34\u6c6a\u6c6a\u4e86\uff0c\u8138\u4e0a\u7684\u8868\u60c5\u4e5f\u7f13\u548c\u4e86\u4e9b\u9053\uff1a\u201c\u679c\u7136 \u662f\u4e2a\u6cb9\u5934\u7c89\u9762\u7684\u5c0f\u767d\u8138\uff0c\u96be\u602a\u6211\u4eec\u7684\u59d1\u5976\u5976\u4f1a\u88ab\u4ed6\u8ff7\u4e0a\u4e86\u3002\u201d \u3000\u3000\u82b1\u91d1\u5f13\u9053\uff1a\u201c\u4ed6\u5c45\u7136\u8fd8\u6562\u5192\u5145\u695a\u7559\u9999\uff0c\u6211\u770b\u4ed6\u505a\u695a\u7559\u9999\u7684\u513f\u5b50\u6015\u8fd8\u5c0f\u4e86\u4e9b\u3002\u201d \u3000\u3000\u8981\u77e5\u695a\u7559\u9999\u6210\u540d\u5df2\u8fd1\u5341\u5e74\uff0c\u6c5f\u6e56\u4e2d\u4eba\u90fd\u77e5\u9053\u695a\u7559\u9999\u638c\u6cd5\u7edd\u4e16\uff0c\u8f7b\u529f\u65e0\u53cc\uff0c\u5374\u6ca1\u6709\u51e0\u4eba\u771f \u7684\u89c1\u8fc7\u8fd9\u4f4d\u9999\u5e05\u3002 \u3000\u3000\u5927\u5bb6\u90fd\u60f3\u695a\u7559\u9999\u65e2\u7136\u6709\u8fd9\u9ebd\u5927\u7684\u540d\u6c14\uff0c\u8fd9\u5927\u7684\u672c\u4e8b\uff0c\u90a3\u9ebd\u5e74\u7eaa\u81ea\u7136\u4e5f\u4e0d\u4f1a\u592a\u5c0f\uff0c\u6709\u4eba\u751a \u81f3\u4ee5\u4e3a\u4ed6\u5df2\u662f\u4e2a\u8001\u5934\u5b50\u3002\u695a\u7559\u9999\u53ea\u6709\u82e6\u7b11\u3002 \u3000\u3000\u90a3\u8001\u5987\u4eba\u6881\u5988\u4e0d\u77e5\u4f55\u65f6\u4e5f\u5b9a\u5230\u524d\u9762\u6765\uff0c\u50cf\u662f\u4e5f\u60f3\u770b\u770b\u8fd9\u201c\u767b\u5f92\u5b50\u201d\u7684\u6a21\u6837\uff0c\u695a\u7559\u9999\u89c9\u5f97 \u5979\u770b\u6765\u5012\u5f88\u6148\u7965\u3002 \u3000\u3000\u4ed6\u5fc3\u91cc\u5ffd\u7136\u60f3\u8d77\u4e2a\u5ff5\u5934\uff0c\u4f46\u8fd9\u65f6\u82b1\u91d1\u5f13\u5df2\u5927\u58f0\u9053\uff1a\u201c\u65e0\u8bba\u6211\u4eec\u8981\u6d3b\u57cb\u4ed6\u8fd8\u662f\u70b9\u5929\u706f\uff0c\u603b \u5f97\u5148\u5c06\u4ed6\u5236\u4f4f\u518d\u8bf4\u201d \u3000\u3000\u53ea\u89c1\u91d1\u5149\u4e00\u95ea\uff0c\u5979\u624b\u91cc\u7684\u91d1\u5f13\u5df2\u5411\u695a\u7559\u9999\u7684\u201c\u6c14\u8840\u6d77\u201d\u7a74\u70b9\u4e86\u8fc7\u6765\uff0c\u539f\u6765\u5979\u8fd9\u67c4\u91d1\u5f13\u4e0d \u4f46\u53ef\u53d1\u94f6\u5f39\uff0c\u800c\u4e14\u5f13\u67c4\u5982\u97e7\u5200\uff0c\u4e24\u7aef\u90fd\u53ef\u4f5c\u70b9\u7a74\u954a\u7528\uff0c\u8ba4\u7a74\u5373\u51c6\uff0c\u51fa\u624b\u66f4\u5feb\uff0c\u5c45\u7136\u8fd8\u662f\u70b9\u7a74 \u7684\u9ad8\u624b\u3002 \u3000\u3000\u695a\u7559\u9999\u73b0\u5728\u81ea\u7136\u4e0d\u80fd\u88c5\u7cca\u6d82\u4e86\uff0c\u8eab\u5b50\u4e00\u7f29\uff0c\u5df2\u5f8c\u9000\u4e86\u51e0\u5c3a\uff0c\u4ed6\u8eab\u5b50\u9000\u5f97\u7adf\u6bd4\u82b1\u91d1\u5f13\u7684\u51fa \u624b\u66f4\u5feb\u3002 \u3000\u3000\u82b1\u91d1\u5f13\u4e00\u62db\u843d\u7a7a\uff0c\u8f6c\u8eab\u53cd\u6253\uff0c\u91d1\u5f13\u5e26\u8d77\u4e00\u9635\u6025\u98ce\uff0c\u6a2a\u626b\u695a\u7559\u9999\u5de6\u8170\uff0c\u201c\u70b9\u7a74\u954a\u201d\u5df2\u53d8\u4e3a \u68cd\u68d2\u3002 \u3000\u3000\u695a\u7559\u9999\u8fd9\u624d\u77e5\u9053\u8fd9\u4f4d\u91d1\u5f13\u592b\u4eba\u624b\u4e0b\u7684\u786e\u4e0d\u5f31\uff0c\u4e00\u67c4\u91d1\u5f13\u7adf\u53ef\u4f5c\u597d\u51e0\u79cd\u5175\u5668\u7528\uff0c\u96be\u602a\u6c5f\u6e56 \u4e2d\u4eba\u90fd\u8bf4\u5979\u662f\u6c5f\u5357\u6b66\u6797\u7684\u7b2c\u4e00\u4f4d\u5973\u5b50\u9ad8\u624b\u3002 \u3000\u3000\u8fd9\u65f6\u695a\u7559\u9999\u5df2\u9000\u81f3\u5986\u53f0\u3002\u5df2\u9000\u65e0\u53ef\u9000\uff0c\u8fd9\u4e00\u62db\u6a2a\u626b\u8fc7\u6765\uff0c\u4ed6\u6839\u672c\u4e0d\u80fd\u5411\u5de6\u53f3\u95ea\u907f\uff0c\u518d\u5411 \u5f8c\u9000\uff0c\u4fbf\u8981\u649e\u4e0a\u5986\u53f0\u3002\u800c\u91d1\u5f13\u592b\u4eba\u8fd9\u4e00\u62db\u5374\u663e\u7136\u8fd8\u7559\u6709\u5f8c\u7740\uff0c\u5c31\u7b49\u7740\u4ed6\u649e\u4e0a\u5986\u53f0\u4e4b\u5f8c\u518d\u53d8\u62db \u5236\u654c\uff0c\u53cd\u70b9\u7a74\u9053\u3002 \u3000\u3000\u8c01\u77e5\u695a\u56fe\u9999\u8eab\u5b50\u53c8\u4e00\u7f29\uff0c\u7adf\u8f7b\u98d8\u98d8\u7684\u98d8\u5230\u5986\u53f0\u7684\u94dc\u955c\u4e0a\uff0c\u5ffd\u7136\u95f4\u53c8\u8d34\u7740\u5899\u58c1\u5411\u65c1\u8fb9\u6ed1\u4e86 \u51fa\u53bb\u3002 \u3000\u3000\u4ed6\u8eab\u5b50\u5c31\u4eff\u4f5b\u6d41\u4e91\u4e00\u822c\uff0c\u53ef\u4ee5\u5728\u7a7a\u4e2d\u6d41\u52a8\u81ea\u5982\u3002 \u3000\u3000\u82b1\u91d1\u5f13\u8138\u8272\u8fd9\u624d\u53d8\u4e86\u53d8\uff0c\u987f\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u60f3\u4e0d\u5230\u4f60\u8fd8\u771f\u6709\u4e24\u4e0b\u3002\u201d \u3000\u3000\u65bd\u5c11\u5976\u5976\u5bd2\u7740\u8138\u9053\uff1a\u201c\u8fd9\u79cd\u4e0b\u4e94\u95e8\u7684\u6deb\u8d3c\uff0c\u5077\u9e21\u6478\u72d7\u7684\u5c0f\u5de7\u529f\u592b\u5f53\u7136\u4f1a\u4e0d\u9519\u3002\u201d \u3000\u3000\u5979\u4f38\u624b\u4e00\u63a2\uff0c\u638c\u4e2d\u5ffd\u7136\u5c31\u591a\u4e86\u4e24\u67c4\u5bd2\u5149\u95ea\u95ea\u7684\u77ed\u5251\uff0c\u4e00\u53e5\u8bdd\u672a\u8bf4\uff0c\u5df2\u5411\u695a\u7559\u9999\u523a\u51fa\u4e03 \u5251\u3002 \u3000\u3000\u8fd9\u79cd\u77ed\u5251\u5c31\u662f\u53e4\u4ee3\u5973\u5b50\u7684\u9632\u8eab\u5229\u5668\u8fd9\u4f4d\u5c11\u5976\u5976\u66f4\u662f\u5bb6\u5b66\u6e0a\u6e90\uff0c\u4e00\u51fa\u624b\u5c31\u7528\u7684\u662f\u201c\u516c\u5b59\u5927 \u5a18\u201d\u6240\u521b\u7684\u201c\u957f\u6b4c\u98de\u8679\u5251\u201d\u3002 \u3000\u3000\u9c8d\u5b59\u5927\u5a18\u4e43\u521d\u5510\u65f6\u4e4b\u5251\u5723\uff0c\u5251\u6cd5\u4e4b\u9ad8\uff0c\u636e\u8bf4\u5df2\u4e0d\u5728\u201c\u7d22\u5973\u201d\u4e4b\u4e0b\uff0c\u6b64\u523b\u65bd\u5c11\u5976\u5976\u5c06\u8fd9\u516b \u516b\u516d\u5341\u56db\u624b\u201c\u957f\u6b4c\u98de\u8679\u5251\u201d\u65bd\u5c55\u5f00\u6765\uff0c\u679c\u7136\u662f\u5203\u4f3c\u98de\u8679\uff0c\u4eba\u5982\u6e38\u9f99\uff0c\u77e2\u77eb\u53d8\u5316\uff0c\u4e0d\u53ef\u65b9\u7269\u3002 \u3000\u3000\u4f55\u51b5\uff0c\u8fd9\u5c4b\u5b50\u4e0d\u5927\uff0c\u6b63\u9002\u65bc\u8fd9\u79cd\u5315\u9996\u822c\u7684\u77ed\u5251\u65bd\u5c55\uff0c\u5979\u7684\u5bf9\u624b\u82e5\u4e0d\u662f\u695a\u7559\u9999\uff0c\u4eba\u65e2\u5df2\u88ab \u903c\u5230\u5899\u89d2\uff0c\u662f\u518d\u4e5f\u907f\u4e0d\u5f00\u5979\u8fd9\u4e03\u5251\u7684\u4e86\u3002 \u3000\u3000\u53ea\u53ef\u501f\u5979\u9047\u7740\u7684\u662f\u695a\u7559\u9999\u3002 \u3000\u3000\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\uff0c\u5583\u5583\u9053\uff1a\u201c\u5c31\u7b97\u6211\u662f\u53f6\u76db\u5170\uff0c\u4e24\u4f4d\u4e5f\u4e0d\u5fc5\u975e\u6740\u4e86\u6211\u4e0d\u53ef\u5440\uff01\u201d \u3000\u3000\u4ed6\u4e00\u5171\u53ea\u8bf4\u4e86\u4e24\u53e5\u8bdd\uff0c\u4f46\u8fd9\u53e5\u8bdd\u8bf4\u5b8c\u65f6\uff0c\u4ed6\u7684\u4eba\u5df2\u6ed1\u4e0a\u5c4b\u9876\uff0c\u53c8\u81ea\u5c4b\u9876\u6ed1\u4e86\u4e0b\u6765\uff0c\u6ed1\u5230 \u95e8\u53e3\u3002 \u3000\u3000\u82b1\u91d1\u5f13\u987f\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u60f3\u8d70\uff0c\u65bd\u5bb6\u5e84\u96be\u9053\u662f\u4f60\u6765\u53bb\u81ea\u5982\u7684\u9ebd\uff1f\u201d \u3000\u3000\u5979\u51fa\u624b\u4e5f\u4e0d\u6162\uff0c\u8fd9\u4e24\u53e5\u8bdd\u8fd8\u672a\u8bf4\u5b8c\uff0c\u4f46\u95fb\u5f13\u5f26\u5982\u8fde\u73e0\u7435\u7436\u822c\u4e00\u9635\u6025\u8c03\uff0c\u91d1\u5f13\u94f6\u5f39\u5df2\u66b4\u96e8 \u822c\u5411\u695a\u7559\u9999\u6253\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u94f6\u5f39\u7684\u53bb\u52bf\u6709\u6025\u6709\u7f13\uff0c\u5f8c\u53d1\u7684\u53cd\u800c\u5148\u81f3\uff0c\u6709\u7684\u8fd8\u5728\u7a7a\u4e2d\u4e92\u649e\uff0c\u9aa4\u7136\u6539\u53d8\u65b9\u5411\uff0c\u6709\u7684\u5374\u4f3c \u4e4e\u5c04\u5931\u624b\u4e86\uff0c\u5c04\u5728\u95e8\u6846\u4e0a\uff0c\u4f46\u5728\u95e8\u6846\u4e0a\u4e00\u5f39\u4e4b\u5f8c\uff0c\u7acb\u523b\u53c8\u53cd\u6fc0\u800c\u8d77\uff0c\u659c\u659c\u7684\u6253\u5411\u695a\u7559\u9999\u524d \u9762\u3002 \u3000\u3000\u91d1\u5f13\u592b\u4eba\u7684\u201c\u94f6\u5f39\u91d1\u5f13\u201d\u7aef\u7684\u4e0d\u540c\u51e1\u54cd\uff0c\u4e0d\u6127\u4e3a\u6c5f\u5357\u6b66\u6797\u7684\u4e00\u7edd\uff0c\u4f46\u695a\u7559\u9999\u8eab\u5b50\u4e5f\u4e0d\u77e5 \u600e\u9ebd\u6837\u4e00\u8f6c\uff0c\u5df2\u81ea\u66b4\u96e8\u822c\u7684\u94f6\u5f39\u4e2d\u98de\u4e86\u51fa\u53bb\uff0c\u8eab\u5b50\u518d\u4e00\u95ea\uff0c\u5c31\u5df2\u8fdc\u5728\u5341\u4e08\u5916\u3002 \u3000\u3000\u91d1\u5f13\u592b\u4eba\u6014\u4e86\u6014\uff0c\u4e00\u6b65\u7a9c\u5230\u95e8\u53e3\uff0c\u5927\u58f0\u9053\uff1a\u201c\u5582\uff0c\u5c0f\u5b50\uff0c\u6211\u95ee\u4f60\uff0c\u4f60\u96be\u9053\u771f\u662f\u695a\u7559 \u9999\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u8eab\u5b50\u843d\u5728\u7af9\u68a2\uff0c\u8f7b\u8f7b\u4e00\u5f39\u53c8\u98de\u8eab\u800c\u8d77\uff0c\u53ea\u89c1\u4ed6\u6325\u4e86\u6325\u624b\uff0c\u4f46\u5374\u770b\u4e0d\u51fa\u662f\u5728\u62db\u624b\uff0c \u8fd8\u662f\u5728\u6447\u624b\u3002 \u3000\u3000\u65bd\u5c11\u5976\u5976\u54ac\u7740\u7259\u9053\uff1a\u201c\u695a\u7559\u9999\u548c\u6211\u4eec\u4e95\u6c34\u4e0d\u72af\u6cb3\u6c34\uff0c\u600e\u4f1a\u5230\u8fd9\u91cc\u6765\uff1f\u201d \u3000\u3000\u91d1\u5f13\u592b\u4eba\u51fa\u4e86\u4f1a\u513f\u795e\uff0c\u5ffd\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u65e0\u8bba\u4ed6\u662f\u5426\u662f\u695a\u7559\u9999\uff0c\u53cd\u6b63\u90fd\u8dd1\u4e0d\u4e86\u7684\u3002\u201d \u3000\u3000\u65bd\u5c11\u5976\u5976\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u91d1\u5f13\u592b\u4eba\u76ee\u5149\u9065\u63a7\u90a3\u8fb9\u7684\u4e00\u5ea7\u4ead\u5b50\uff0c\u9053\uff1a\u201c\u4f60\u90a3\u5b9d\u8d1d\u4e8c\u53d4\u65e2\u7136\u9001\u4e86\u6211\u4eec\u56de\u6765\uff0c\u6ca1\u6709\u5403\u5bb5 \u591c\u7684\u70b9\u5fc3\u4ed6\u600e\u9ebd\u80af\u8d70\u5462\uff1f\u6211\u7b97\u51c6\u4ed6\u73b0\u5728\u4e00\u5b9a\u8fd8\u5728\u4ead\u5b50\u91cc\u7b49\u7740\u3002\u201d \u3000\u3000\u65bd\u5c11\u5976\u5976\u5634\u89d2\u4e5f\u6cdb\u8d77\u4e00\u4e1d\u6076\u610f\u7684\u5fae\u7b11\uff0c\u9053\uff0c\uff1a\u201c\u4e0d\u9519\uff0c\u53ea\u8981\u5b9d\u4e8c\u53d4\u5728\u4ead\u5b50\u91cc\uff0c\u65e0\u8bba\u662f\u8c01 \u90fd\u8d70\u4e0d\u4e86\u7684\u3002\u201d \u3000\u3000\u4ead\u5b50\u91cc\u679c\u7136\u6709\u4e2a\u4eba\uff0c\u6b63\u5750\u5728\u77f3\u7ea7\u4e0a\uff0c\u4ef0\u9762\u671b\u7740\u5929\uff0c\u5634\u91cc\u5ff5\u5ff5\u6709\u8bcd\uff0c\u4e5f\u4e0d\u77e5\u5728\u8bf4\u4e9b\u4ec0\u9ebd\u3002 \u3000\u3000\u4ed4\u7ec6\u4e00\u770b\uff0c\u4ed6\u539f\u6765\u5728\u6570\u5929\u4e0a\u7684\u8fb0\u661f\u3002 \u3000\u3000\u201c\u2014\u5343\u53c1\u767e\u4e8c\u5341\u4e03\uff0c\u4e00\u5343\u53c1\u767e\u4e8c\u5341\u516b\u2026\u2026\u201d \u3000\u3000\u4ed6\u5e74\u7eaa\u6700\u5c11\u4e5f\u6709\u56db\u5341\u591a\u4e86\uff0c\u80e1\u5b50\u5df2\u6709\u4e9b\u82b1\u767d\uff0c\u8eab\u4e0a\u5374\u7a7f\u7740\u4ef6\u5927\u7ea2\u7ee3\u82b1\u7684\u8863\u670d\uff0c\u7ee3\u7684\u662f\u5218 \u6d77\u6d12\u91d1\u94b1\uff0c\u811a\u4e0a\u8fd8\u7a7f\u7740\u53cc\u864e\u5934\u7ea2\u7ed2\u94fe\uff0c\u661f\u5149\u4e0b\u770b\u6765\uff0c\u4ed6\u8138\u8272\u4f3c\u4e4e\u5341\u5206\u7ea2\u6da6\uff0c\u4ed4\u7ec6\u4e00\u770b\uff0c\u539f\u6765 \u7adf\u6d82\u7740\u80ed\u8102\u3002 \u3000\u3000\u4ed6\u4e00\u5fc3\u4e00\u610f\u7684\u6570\u7740\uff0c\u4e00\u9762\u7528\u624b\u6307\u6307\u70b9\u70b9\uff0c\u624b\u4e0a\u4e5f\u201c\u53ee\u53ee\u5f53\u5f53\u201d\u7684\u76f4\u54cd\uff0c\u539f\u6765\u4ed6\u624b\u8155\u4e0a\u8fd8 \u6234\u7740\u51e0\u53ea\u63a5\u7740\u94c3\u9501\u7684\u91d1\u5708\u5b50\u3002 \u3000\u3000\u695a\u7559\u9999\u4e00\u5fc3\u60f3\u5feb\u79bb\u5f00\u8fd9\u5730\u65b9\uff0c\u672c\u6765\u4e5f\u6ca1\u6709\u6cd5\u610f\u5230\u4ead\u5b50\u91cc\u8fd8\u6709\u4e2a\u4eba\uff0c\u542c\u5230\u4ead\u91cc\u201c\u53ee\u53ee\u5f53 \u5f53\u201d\u7684\u58f0\u97f3\uff0c\u624d\u5f80\u90a3\u8fb9\u77a7\u4e86\u4e00\u773c\u3002 \u3000\u3000\u53ea\u77a7\u4e86\u4e00\u773c\uff0c\u4ed6\u5df2\u5fcd\u4e0d\u4f4f\u8981\u7b11\u4e86\u51fa\u6765\uff0c\u82e5\u662f\u6362\u5728\u5e73\u65f6\uff0c\u4ed6\u4e00\u5b9a\u5fcd\u4e0d\u4f4f\u997c\u53bb\u786e\u77a7\u8fd9\u6d3b\u5b9d\u662f \u4f55\u8bb8\u4eba\u4e5f\uff0c\u4f46\u73b0\u5728\u4ed6\u5374\u5df2\u6ca1\u6709\u8fd9\u6837\u597d\u7684\u5fc3\u60c5\uff0c\u811a\u5c16\u5fae\u5fae\u70b9\u5730\uff0c\u4eba\u5df2\u81ea\u4ead\u5b50\u4e0a\u63a0\u4e86\u8fc7\u53bb\uff0c\u53ea\u8981 \u518d\u4e24\u4e2a\u8d77\u843d\uff0c\u4fbf\u53ef\u51fa\u8fd9\u7247\u5ead\u56ed\u3002 \u3000\u3000\u8c01\u77e5\u5c31\u5728\u8fd9\u65f6\u7a81\u542c\u201c\u98d5\u201d\u7684\u58f0\uff0c\u4e00\u6761\u4eba\u5f71\u6e05\u822c\u81ea\u4ead\u5b50\u91cc\u7a9c\u4e86\u51fa\u6765\uff0c\u6321\u5728\u695a\u7559\u9999\u524d\u9762\u3002 \u3000\u3000\u695a\u7559\u9999\u63a0\u4e0a\u4ead\u5b50\u518d\u63a0\u4e0b\uff0c\u8fd9\u4eba\u5374\u81ea\u4ead\u5b50\u91cc\u76f4\u63a5\u6e9c\u51fa\uff0c\u8ddd\u79bb\u867d\u6bd4\u695a\u7559\u9999\u77ed\u4e86\u4e9b\uff0c\u4f46\u8fd9\u79cd\u8eab \u624b\u5374\u8fd8\u662f\u60ca\u4eba\u7684\u5f88\u3002\u695a\u7559\u9999\u518d\u4e5f\u60f3\u4e0d\u5230\u4f1a\u5728\u8fd9\u7528\u9047\u89c1\u8f7b\u529f\u5982\u6b64\u7cbe\u7edd\u7684\u9ad8\u624b\uff0c\u518d\u4e00\u770b\uff0c\u8fd9\u201c\u9ad8 \u624b\u201d\u5c45\u7136\u5c31\u662f\u90a3\u5fd9\u7740\u6570\u661f\u661f\u7684\u6d3b\u5b9d\u3002 \u3000\u3000\u4ed6\u7ad9\u8d77\u6765\u5f8c\uff0c\u5c31\u53ef\u770b\u51fa\u4ed6\u8eab\u4e0a\u7684\u8863\u670d\u53c8\u77ed\u53c8\u5c0f\uff0c\u5c31\u50cf\u662f\u5077\u6765\u7684\uff0c\u5934\u53d1\u548c\u80e1\u5b50\u68b3\u5f97\u5f88\u4eae\uff0c \u4e0a\u9762\u8fd8\u50cf\u662f\u6d82\u4e86\u5228\u82b1\u6cb9\uff0c\u518d\u52a0\u4e0a\u80a1\u82b1\u7c89\u59fb\u8102\uff0c\u770b\u6765\u771f\u6709\u51e0\u5206\u50cf\u662f\u5f69\u8863\u5a31\u4eb2\u7684\u8001\u83b1\u5b50\u3002 \u3000\u3000\u695a\u7559\u9999\u4e5f\u4e0d\u7981\u6014\u4f4f\u4e86\uff0c\u4ed6\u770b\u4e0d\u51fa\u8fd9\u9ebd\u4e00\u4e2a\u6d3b\u5b9d\u7adf\u4f1a\u6709\u5982\u6b64\u60ca\u4eba\u7684\u8eab\u624b\u3002 \u3000\u3000\u8fd9\u6d3b\u5b9d\u4e5f\u5728\u4e0a\u4e0a\u4e0b\u4e0b\u7684\u6253\u91cf\u7740\u4ed6\uff0c\u5ffd\u7136\u563b\u7684\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4f4d\u5927\u53d4\u4f60\u662f\u4ece\u54ea\u91cc\u6765\u7684\u5440\uff1f \u6211\u600e\u9ebd\u4ece\u6765\u4e5f\u6ca1\u6709\u89c1\u8fc7\u4f60\u5462\uff1f\u201d \u3000\u3000\u8fd9\u8001\u5934\u5b50\u5c45\u7136\u660e\u4ed6\u201c\u5927\u53d4\u201d\uff0c\u695a\u7559\u9999\u5b9e\u5728\u6709\u4e9b\u54ed\u7b11\u4e0d\u5f97\uff0c\u5e78\u597d\u82b1\u91d1\u5f13\u5979\u4eec\u8fd8\u6ca1\u6709\u8ffd\u8fc7 \u6765\uff0c\u695a\u7559\u9999\u773c\u73e0\u4e00\u8f6c\uff0c\u4e5f\u7b11\u9053\uff1a\u201c\u8001\u5148\u751f\u4e0d\u5fc5\u5ba2\u6c14\uff0c\u5927\u53d4\u8fd9\u4e24\u5b57\u5728\u4e0b\u5b9e\u5728\u62c5\u5f53\u4e0d\u8d77\u3002\u201d \u3000\u3000\u8c01\u77e5\u4ed6\u8bdd\u521a\u8bf4\u5b8c\uff0c\u8fd9\u6d3b\u5b9d\u5df2\u5927\u7b11\u8d77\u6765\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u4e2a\u5446\u5b50\uff0c\u6211\u660e\u660e\u53ea\u6709\u5341\u4e8c\u5c81\uff0c\u4f60\u5374 \u53eb\u6211\u8001\u5148\u751f\uff0c\u6211\u5927\u54e5\u542c\u5230\u4e86\uff0c\u5b9a\u8981\u7b11\u7834\u809a\u5b50\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53c8\u6014\u4f4f\u4e86\uff0c\u5fcd\u4e0d\u4f4f\u6478\u4e86\u6478\u9f3b\u5b50\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u53ea\u6709\u5341\u4e8c\u5c81\uff1f\u201d \u3000\u3000\u8fd9\u6d3b\u5b9d\u6273\u7740\u624b\u6307\u6570\u4e86\u6570\uff0c\u9053\uff1a\u201c\u4eca\u5929\u521a\u6ee1\u5341\u4e8c\u5c81\uff0c\u4e00\u5929\u4e5f\u4e0d\u591a\uff0c\u4e00\u5929\u4e5f\u4e0d\u5c11\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u90a3\u9ebd\u4f60\u5927\u54e5\u5462\uff1f\u201d \u3000\u3000\u8fd9\u6d3b\u5b9d\u7b11\u9053\uff1a\u201c\u6211\u5927\u54e5\u5e74\u7eaa\u53ef\u5927\u5f97\u591a\u4e86\u3002\u6015\u6bd4\u5927\u53d4\u8fd8\u5927\u51e0\u5c81\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4ed6\u662f\u8c01\uff1f\u201d \u3000\u3000\u8fd9\u6d3b\u5b9d\u9053\uff1a\u201c\u4ed6\u53eb\u505a\u859b\u8863\u4eba\uff0c\u6211\u53eb\u505a\u859b\u7b11\u4eba\uff0c\u4f46\u662f\u522b\u4eba\u90fd\u53eb\u6211\u859b\u5b9d\u5b9d\u2026\u859b\u5b9d\u5b9d\u2026\u859b\u5b9d \u5b9d\u3002\u4f60\u8bf4\u8fd9\u540d\u5b57\u597d\u542c\u4e0d\u597d\u542c\uff1f\u201d \u3000\u3000\u8fd9\u767d\u75f4\u7adf\u662f\u4e00\u4ee3\u5251\u8c6a\u859b\u8863\u4eba\u7684\u5f1f\u5f1f\uff0c\u8fd9\u624d\u53eb\u505a\uff1a\u201c\u9f99\u751f\u4e5d\u5b50\u3002\u5b50\u5b50\u4e0d\u540c\u201d\u3002\u695a\u7559\u9999\u6697\u4e2d \u53f9\u4e86\u53e3\u6c14\uff0c\u5b9e\u5728\u4e0d\u613f\u548c\u8fd9\u4eba\u591a\u8bf4\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u540d\u5b57\u597d\u542c\u6781\u4e86\uff0c\u4f46\u4f60\u65e2\u7136\u53eb\u5b9d\u5b9d\uff0c\u5c31\u5e94\u8be5\u505a\u4e2a \u4e56\u5b9d\u5b9d\uff0c\u5feb\u8ba9\u6211\u8d70\u5427\uff0c\u4e0b\u6b21\u6211\u4e00\u5b9a\u5e26\u7cd6\u7ed9\u4f60\u5403\u3002\u201d \u3000\u3000\u4ed6\u5c45\u7136\u5c06\u8fd9\u56db\u4e94\u5341\u5c81\u7684\u4eba\u53eb\u505a\u201c\u4e56\u5b9d\u5b9d\u201d\uff0c\u8fde\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u7981\u89c9\u5f97\u6709\u4e9b\u597d\u7b11\uff0c\u4e00\u9762\u62e3\u7740 \u624b\uff0c\u4e00\u9762\u5df2\u98de\u8eab\u63a0\u8d77\u3002 \u3000\u3000\u8c01\u77e5\u8fd9\u859b\u5b9d\u5b9d\u7adf\u4e5f\u7a81\u7136\u98de\u8eab\u800c\u8d77\uff0c\u987a\u624b\u5c31\u81ea\u8170\u5e26\u4e0a\u62bd\u51fa\u6bd2\u86c7\u822c\u7684\u8f6f\u5251\uff0c\u201c\u5220\u8e66\uff0c\u5ffd\u201d\uff0c \u4e00\u8fde\u53c1\u5251\u523a\u4e86\u51fa\u6765\u8fd9\u53c1\u5251\u5f53\u771f\u662f\u53c8\u5feb\uff0c\u53c8\u51c6\u3002\u53c8\u72e0\u5251\u6cd5\u4e4b\u8fc5\u901f\u7cbe\u786e\uff0c\u5c31\u8fde\u4e2d\u539f\u4e00\u70b9\u7ea2\uff0c\u9ec4\u9c81 \u76f4\u8fd9\u4e9b\u4eba\u90fd\u8981\u4e4e\u5176\u5f8c\u3002 \u3000\u3000\u695a\u7559\u9999\u867d\u7136\u907f\u5f00\u4e86\u8fd9\u53c1\u5251\u5374\u5df1\u88ab\u903c\u843d\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u53ea\u89c1\u859b\u5b9d\u5b9d\u4e00\u53ea\u811a\u7ad9\u5728\u5bf9\u9762\u7684\u5047\u5c71\u4e0a\uff0c\u7b11\u563b\u563b\u7684\u56b7\u7740\u9053\uff1a\u201c\u5927\u53d4\u4f60\u574f\u4e86\u6211\u7684\u5927\u4e8b\uff0c\u8fd8\u6ca1 \u6709\u8d54\u627e\u600e\u9ebd\u80fd\u8d70\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u671b\u7740\u4ed6\u5df2\u5f04\u4e0d\u6e05\u8fd9\u4eba\u7a76\u7adf\u662f\u4e0d\u662f\u767d\u75f4\u4e86\u3002 \u3000\u3000\u770b\u4ed6\u7684\u6a21\u6837\u6253\u626e\uff0c\u542c\u4ed6\u7684\u8bf4\u8bdd\uff0c\u660e\u660e\u662f\u4e2a\u4e0d\u6298\u4e0d\u6263\u7684\u5927\u767d\u75f4\uff0c\u4f46\u767d\u75f4\u53c8\u600e\u4f1a\u4f7f\u5f97\u51fa\u5982\u6b64 \u8f9b\u8fa3\u8fc5\u6025\u7684\u5251\u6cd5\uff1f \u3000\u3000\u695a\u7559\u9999\u53ea\u6709\u82e6\u7b11\u9053\uff1a\u6211\u574f\u4e86\u4f60\u7684\u5927\u4e8b\uff1f\u4ec0\u9ebd\u5927\u4e8b\uff1f\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u77ac\u8d77\u4e86\u5634\u9053\uff1a\u201c\u65b9\u6211\u6b63\u5728\u6570\u5929\u4e0a\u7684\u661f\u661f\uff0c\u597d\u5bb9\u6613\u5df2\u5c06\u6708\u4eae\u90a3\u8fb9\u7684\u661f\u661f\u90fd\u6570\u6e05\u4e86\uff0c \u53ef\u662f\u4f60\u4e00\u6765\uff0c\u5c31\u5435\u5f97\u6211\u5168\u5fd8\u5f97\u4e7e\u4e7e\u51c0\u51c0\uff0c\u4f60\u975e\u8d54\u6211\u4e0d\u53ef\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u597d\u597d\u597d\uff0c\u6211\u8d54\u4f60\uff0c\u4f46\u600e\u9ebd\u6837\u8d54\u6cd5\u5462\uff1f\u201d \u3000\u3000\u4ed6\u5634\u91cc\u8bf4\u7740\u8bdd\u8eab\u5f62\u5df2\u659c\u7a9c\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u63a0\u4ed6\u5df2\u5c3d\u4e86\u529b\uff0c\u4ee5\u695a\u9999\u5e05\u8f7b\u529f\u4e4b\u5999\uff0c\u5929\u4e0b\u6709\u8c01\u80fd\u8ffd\u5f97\u4e0a\u3002 \u3000\u3000\u8c01\u77e5\u859b\u5b9d\u5b9d\u7adf\u50cf\u65e9\u5df1\u77e5\u9053\u4ed6\u8981\u6e9c\u4e86\uff0c\u695a\u7559\u9999\u8eab\u5f62\u521a\u52a8\uff0c\u4ed6\u624b\u4e0a\u5957\u7740\u7684\u91d1\u5708\u5df2\u98de\u4e86\u51fa\u6765\u3002 \u3000\u3000\u53ea\u542c\u201c\u53ee\u94c3\u94c3\u201d\u4e00\u8fde\u4e32\u58f0\u97f3\u56db\u53ea\u91d1\u8c03\u5b50\u5728\u665a\u7a7a\u4e2d\u5212\u8d77\u56db\u9053\u91d1\u5f27\uff0c\u62d0\u7740\u5f2f\u513f\u5230\u695a\u7559\u9999\u524d \u9762\u3002 \u3000\u3000\u695a\u7559\u9999\u53ea\u89c1\u773c\u524d\u91d1\u82b1\u4e00\u95ea\uff0c\u201c\u53ee\u5f53\uff0c\u53ee\u5f53\u201d\u4e24\u58f0\u54cd\u3002\u56db\u53ea\u91d1\u9501\u5728\u534a\u7a7a\u76f8\u51fb\uff0c\u7a81\u7136\u8fce\u9762\u5411 \u4ed6\u649e\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u8fd9\u201c\u767d\u75f4\u201d\u4e0d\u4f46\u8f7b\u529f\u9ad8\uff0c\u5251\u6cd5\u9ad8\uff0c\u53d1\u6697\u5668\u7684\u624b\u6cd5\u66f4\u662f\u5999\u5230\u6781\u70b9\uff0c\u82b1\u91d1\u5f13\u7684\u94f6\u5f39\u548c\u4ed6\u2014 \u6bd4\uff0c\u7b80\u76f4\u5c31\u50cf\u662f\u5c0f\u5b69\u4e88\u5728\u800d\u6ce5\u4e38\u3002 \u3000\u3000\u695a\u7559\u9999\u7684\u53bb\u52bf\u65e2\u4e5f\u6025\u5982\u6d41\u77e2\u773c\u770b\u4ed6\u9669\u4e9b\u5c31\u8981\u649e\u4e0a\u91d1\u94a5\u5b50\u4e86\uff0c\u5728\u8fd9\u95f4\u4e0d\u5bb9\u53d1\u7684\u5239\u90a3\u95f9\uff0c\u4ed6 \u522b\u65e0\u9009\u62e9\uff0c\u8eab\u5f62\u6597\u7136\u4e00\u5f13\uff0c\u5411\u5f8c\u9000\u4e86\u56de\u53bb\u4e24\u53ea\u624b\u201c\u5206\u5149\u6349\u5f71\u201d\u6284\u5f80\u4e86\u53c1\u53ea\u91d1\u9501\u5b50\uff0c\u5269\u4e0b\u7684\u4e00 \u53ea\u4e5f\u88ab\u4ed6\u7528\u63a5\u5728\u624b\u91cc\u7684\u53c1\u53ea\u6253\u98de\u3002 \u3000\u3000\u8fd9\u8eab\u5b50\u4e00\u7f29\uff0c\u4f38\u624b\u4e00\u6349\uff0c\u8bf4\u6765\u867d\u5bb9\u6613\uff0c\u5176\u5b9e\u5374\u96be\u6781\u4e86\uff0c\u65e0\u8bba\u8eab\u3001\u773c\u3001\u65f6\u95f4\u3001\u90e8\u4f4d\u90fd\u8981\u62ff \u634f\u5f97\u6070\u5230\u597d\u5904\uff0c\u9519\u4e0d\u5f97\u534a\u5206\uff0c\u82e5\u6ca1\u6709\u6781\u5feb\u7684\u51fa\u624b\uff0c\u56fa\u7136\u6284\u4e0d\u5230\u8fd9\u56db\u53ea\u91d1\u9501\uff0c\u82e5\u6ca1\u6709\u7edd\u9876\u7684\u8f7b \u529f\uff0c\u4e5f\u65e0\u6cd5\u5c06\u91d1\u9501\u7684\u529b\u9053\u6d88\u51cf\uff0c\u90a3\u6837\u7eb5\u80fd\u52c9\u5f3a\u6284\u7740\u91d1\u9501\u864e\u53e3\u6015\u4e5f\u8981\u88ab\u9707\u88c2\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\u7b49\u4ed6\u6284\u4f4f\u91d1\u9501\uff0c\u4ed6\u7684\u4eba\u5df2\u9000\u56de\u539f\u5904\u3002 \u3000\u3000\u53ea\u89c1\u859b\u5b9d\u5b9d\u8dfa\u7740\u80f8\u9053\uff1a\u201c\u5927\u53d4\u4f60\u660e\u660e\u8bf4\u597d\u8981\u8d54\u6211\uff0c\u600e\u9ebd\u53c8\u6e9c\u4e86\uff0c\u5927\u4eba\u600e\u9ebd\u80fd\u9a97\u5c0f\u5b69 \u5b50\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u5ffd\u7136\u53d1\u73b0\u8fd9\u767d\u75f4\u7adf\u662f\u4ed6\u751f\u5e73\u7f55\u89c1\u7684\u96be\u7f20\u5bf9\u624b\uff0c\u4ed6\u867d\u7136\u8eab\u7ecf\u767e\u6218\uff0c\u4e00\u65f6\u4e4b\u95f4\u5374\u4e5f\u4e0d \u77e5\u8be5\u5982\u4f55\u5bf9\u4ed8\u624d\u597d\u3002 \u3000\u3000\u859b\u5b9d\u5b9d\u8fd8\u5728\u8dfa\u7740\u811a\u9053\uff1a\u201c\u5927\u53d4\u4f60\u8bf4\u4f60\u7a76\u7adf\u662f\u8d54\uff0c\u8fd8\u662f\u4e0d\u8d54\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u81ea\u7136\u8981\u8d54\u7684\u4f46\u600e\u9ebd\u8d54\u6cd5\u5462\uff1f\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u7acb\u523b\u5c55\u989d\u7b11\u9053\uff1a\u201c\u90a3\u5bb9\u6613\u5f97\u5f88\uff0c\u53ea\u8981\u4f60\u5c06\u6708\u4eae\u90a3\u8fb9\u7684\u661f\u661f\u66ff\u6211\u6570\u6e05\u695a\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6478\u4e86\u6478\u9f3b\u5b50\uff0c\u9053\uff1a\u201c\u54ea\u4e00\u8fb9\uff1f\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u4f38\u624b\u6307\u4e86\u6307\uff0c\u9053\uff1a\u201c\u5c31\u662f\u90a3\u8fb9\u3002\u201d \u3000\u3000\u5176\u5b9e\u8fd9\u65f6\u5929\u4e0a\u6839\u672c\u6ca1\u6709\u6708\u4eae\uff0c\u5374\u6709\u7e41\u661f\u6ee1\u5929\uff0c\u4e00\u4e2a\u4eba\u5c31\u7b97\u751f\u4e86\u4e8c\u767e\u53cc\u773c\u775b\uff0c\u4e00\u767e\u53ea\u624b\uff0c \u4e5f\u6ca1\u6709\u6cd5\u5b50\u5c06\u8fd9\u6ee1\u5929\u7e41\u5c4b\u6570\u6e05\u695a\u7684\u3002 \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u54e6\uff0c\u4f60\u8bf4\u7684\u662f\u8fd9\u8fb9\u9ebd\uff1f\u90a3\u771f\u597d\u6781\u4e86\u3002\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u7728\u7740\u773c\u775b\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\u597d\u6781\u4e86\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u8fd9\u8fb9\u7684\u661f\u661f\u6211\u521a\u5c31\u5df2\u6570\u8fc7\uff0c\u4e00\u5171\u662f\u4e24\u4e07\u516b\u5343\u56db\u767e\u53c1\u5341\u4e03\u4e2a\u3002\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u9053\uff1a\u201c\u771f\u7684\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u81ea\u7136\u662f\u771f\u7684\uff0c\u5927\u4eba\u600e\u9ebd\u4f1a\u9a97\u5c0f\u5b69\u5b50\uff0c\u4f60\u4e0d\u4fe1\u5c31\u81ea\u5df1\u6570\u6570\u770b\u3002\u201d \u3000\u3000\u4ed6\u5fc3\u91cc\u65e9\u5df2\u6253\u597d\u4e3b\u610f\uff0c\u8fd9\u201c\u767d\u75f4\u201d\u82e5\u662f\u4e0d\u4e0a\u5f53\uff0c\u90a3\u9ebd\u4ed6\u8fd9\u75f4\u5446\u5c31\u5fc5\u662f\u88c5\u51fa\u6765\u7684\uff0c\u695a\u7559\u9999 \u867d\u4e0d\u613f\u548c\u771f\u7684\u767d\u75f4\u6253\u67b6\uff0c\u4f46\u5bf9\u5047\u81ea\u75f4\u53ef\u5c31\u4e0d\u540c\u3002 \u3000\u3000\u8c01\u77e5\u788e\u5b9d\u5b9d\u5df2\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u662f\u4e24\u4e07\u516b\u5343\u56db\u767e\u53c1\u5341\u4e03\u4e2a\uff0c\u597d\uff0c\u6211\u6570\u6570\u3002\u201d\u4ed6\u7adf\u771f\u7684\u4ef0\u7740\u5934 \u6570\u4e86\u8d77\u6765\u3002 \u3000\u3000\u695a\u7559\u9999\u6697\u4e2d\u677e\u4e86\u53e3\u6c14\uff0c\u8eab\u5b50\u5982\u7bad\u4e00\u822c\u7a9c\u4e86\u51fa\u53bb\uff0c\u8fd9\u65f6\u859b\u5b9d\u5b9d\u7adf\u4f3c\u5df2\u6570\u5f97\u51fa\u795e\uff0c\u5b8c\u5168\u6ca1\u6709 \u7559\u610f\u5230\u4ed6\u3002 \u3000\u3000\u695a\u7559\u9999\u8fd9\u624d\u77e5\u9053\u771f\u7684\u9047\u89c1\u4e00\u4e2a\u6b66\u529f\u9ad8\u5f97\u5413\u4eba\u7684\u767d\u75f4\uff0c\u4ed6\u53ea\u89c9\u6709\u4e9b\u597d\u7b11\uff0c\u53c8\u6709\u4e9b\u8bb6\u5f02\u3002 \u3000\u3000\u8fd9\u4ef6\u4e8b\u7684\u786e\u6709\u4e9b\u4e0d\u53ef\u601d\u8bae\uff0c\u4f46\u4ed6\u51b3\u5b9a\u6682\u65f6\u7edd\u4e0d\u60f3\u8fd9\u4ef6\u4e8b\uff0c\u56e0\u4e3a\u8fd8\u6709\u4ef6\u66f4\u4e0d\u53ef\u601d\u8bae\u7684\u4e8b\u8fd8 \u672a\u89e3\u51b3\u3002 \u3000\u3000\u501f\u5c38\u8fd8\u9b42 \u3000\u3000\u65bd\u8335\u7684\u9b42\u9b44\u4f3c\u771f\u7684\u501f\u4e86\u5de6\u660e\u73e0\u7684\u4f53\u800c\u590d\u6d3b\u4e86\u3002 \u3000\u3000\u5de6\u4e8c\u7237\u770b\u5230\u4ed6\u62ff\u56de\u6765\u7684\u82b1\u7c89\u65f6\uff0c\u4e5f\u4e0d\u7981\u4e3a\u4e4b\u76ee\u5b9a\u53e3\u5446\uff0c\u6c57\u6d41\u6fb3\u80cc\uff1b\u8db3\u8db3\u6709\u76cf\u8336\u65f6\u5206\u8bf4\u4e0d \u51fa\u8bdd\u6765\u3002 \u3000\u3000\u5f20\u7b80\u658b\u76b1\u7740\u7709\u95ee\u9053\uff1a\u201c\u90a3\u5c4b\u65bc\u662f\u5426\u771f\u548c\u5979\u6240\u8bf4\u7684\u5b8c\u5168\u4e00\u6837\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5b8c\u5168\u4e00\u6837\u3002\u201d \u3000\u3000\u5f20\u7b80\u658b\u9053\uff1a\u201c\u90a3\u4f4d\u65bd\u59d1\u5a18\u771f\u662f\u4eca\u5929\u6b7b\u7684\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5979\u4f53\u8fd8\u672a\u6536\uff0c\u6211\u8fd8\u770b\u5230\u90a3\u8eab\u8863\u670d\u4e5f\u2026\u3002\u201d \u3000\u3000\u5de6\u4e8c\u7237\u5ffd\u7136\u8df3\u8d77\u6765\uff0c\u5927\u543c\u9053\uff1a\u201c\u6211\u4e0d\u7ba1\u90a3\u662f\u4ec0\u9ebd\u8863\u670d\uff0c\u4e5f\u4e0d\u7ba1\u59d3\u65bd\u7684\u5973\u513f\u6b7b\u4e86\u6ca1\u6709\uff0c\u6211 \u53ea\u77e5\u9053\u660e\u73e0\u662f\u6211\u7684\u5973\u513f\uff0c\u8c01\u4e5f\u62a2\u4e0d\u8d70\u3002\u201d \u3000\u3000\u5f20\u7b80\u658b\u9053\uff1a\u201c\u53ef\u662f\uff0c\u5979\u82e5\u4e0d\u627f\u8ba4\u4f60\u662f\u5979\u7236\u4eb2\u5462\uff1f\u201d \u3000\u3000\u5de6\u4e8c\u7237\u6012\u543c\u9053\uff1a\u201c\u5979\u82e5\u6562\u4e0d\u8ba4\u6211\u4e3a\u7236\uff0c\u6211\u5c31\u2026\u2026\u6211\u5c31\u6740\u4e86\u5979\u3002\u201d\u74dc \u3000\u3000\u5f20\u7b80\u658b\u9053\uff1a\u201c\u4f60\u771f\u7684\u5fcd\u5fc3\u4e0b\u5f97\u4e86\u624b\uff1f\u201d \u3000\u3000\u5de6\u4e8c\u7237\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u6211\u4e3a\u4f55\u4e0b\u4e0d\u4e86\u624b\uff1f\u6211\u2026\u2026\u6211\u2026\u2026\u6211\u2026\u2026\u3002\u201d\u5ef7 \u3000\u3000\u8bf4\u5230\u7b2c\u53c1\u4e2a\u201c\u6211\u201d\u5b57\uff0c\u773c\u6cea\u4e0d\u7981\u5df2\u593a\u7736\u800c\u51fa\uff0c\u9b41\u4f1f\u7684\u8eab\u5b50\u5012\u5728\u6905\u4e0a\uff0c\u4eff\u4f5b\u518d\u4e5f\u65e0\u529b\u7ad9\u8d77 \u6765\u4e86\u3002 \u3000\u3000\u5f20\u7b80\u658b\u6447\u5934\u53f9\u606f\u9053\uff1a\u201c\u9020\u5316\u5f04\u4eba\u2026\u2026\u9020\u5316\u5f04\u4eba\u7adf\u4e00\u81f3\u65bc\u65af\uff0c\u4f60\u6211\u592b\u590d\u4f55\u8a00\uff1f\u201d \u3000\u3000\u5de6\u4e8c\u7237\u53cc\u624b\u5e9c\u7740\u5934\uff0c\u6ca7\u7136\u9053\uff1a\u201c\u53ef\u662f\u2026\u2026\u53ef\u662f\u4f60\u4eec\u96be\u9053\u8981\u6211\u627f\u8ba4\u660e\u73e0\u662f\u90a3\u6cfc\u5987\u7684\u5973\u513f\uff1f \u4f60\u4eec\u96be\u9053\u8981\u6211\u6d3b\u751f\u751f\u7684\u5c06\u81ea\u5df1\u7684\u5973\u513f\u9001\u7ed9\u522b\u4eba\uff1f\u201d \u3000\u3000\u5f20\u7b80\u658b\u7528\u624b\u6478\u7740\u81ea\u5df1\u7684\u80e1\u5b50\uff0c\u6765\u53bb\u7684\u8e0f\u7740\u65b9\u6b65\uff0c\u8fd9\u6c5f\u6e56\u540d\u533b\u867d\u6709\u5999\u624b\u6210\u6625\u7684\u672c\u4e8b\uff0c\u5bf9\u8fd9 \u4ef6\u4e8b\u5374\u4e5f\u675f\u624b\u65e0\u7b56\u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u5979\u8fd8\u5728\u7761\u9ebd\uff1f\u201d \u3000\u3000\u5de6\u4e8c\u7237\u8e87\u7136\u7586\u9053\uff1a\u201c\u8fd8\u7761\u5f97\u72e0\u6c89\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7ad9\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u4e8c\u54e5\u4f60\u82e5\u76f8\u4fe1\u6211\uff0c\u5c31\u5c06\u8fd9\u4ef6\u4e8b\u4ea4\u7ed9\u6211\u529e\u5427\u3002\u201d \u3000\u3000\u5f20\u7b80\u658b\u957f\u53f9\u9053\uff1a\u201c\u4e16\u4e0a\u82e5\u8fd8\u6709\u4e00\u4e2a\u4eba\u80fd\u89e3\u51b3\u8fd9\u4ef6\u4e8b\uff0c\u90a3\u5fc5\u5b9a\u5c31\u662f\u695a\u9999\u5e05\u4e86\uff0c\u5de6\u4e8c\u7237\u7740\u4e0d \u76f8\u4fe1\u4f60\uff0c\u4ed6\u8fd8\u80fd\u76f8\u4fe1\u8c01\uff1f\u201d \u53e4\u9f99\u300a\u695a\u7559\u9999\u7cfb\u5217\xb7\u9b3c\u604b\u4f20\u5947\u300b \u7b2c\u4e09\u7ae0\u3000\u5510\u7a81\u4f73\u4eba \u3000\u3000\u5929\u5df2\u4eae\u4e86\u3002 \u3000\u3000\u521d\u5347\u7684\u9633\u5149\u81ea\u7a97\u5b50\u9699\u7f1d\u7167\u8fdb\u6765\uff0c\u7167\u89c1\u5979\u7684\u8138\u8272\u82cd\u767d\uff0c\u4e00\u53cc\u7f8e\u4e3d\u7684\u773c\u775b\u91cc\u5374\u5e03\u6ee1\u4e86\u7ea2\u4e1d\u3002 \u3000\u3000\u8fd9\u786e\u662f\u5de6\u660e\u73e0\u7684\u8138\uff0c\u786e\u662f\u5de6\u660e\u73e0\u7684\u773c\u775b\u2014\u2014\u4f46\u8fd9\u5c11\u5973\u662f\u5426\u5de6\u660e\u73e0\uff1f\u8fde\u695a\u7559\u9999\u4e5f\u5f04\u4e0d\u6e05 \u4e86\u3002 \u3000\u3000\u4ed6\u751a\u81f3\u4e0d\u77e5\u8be5\u5982\u4f55\u79f0\u547c\u5979\u624d\u597d\uff0c\u82e5\u79f0\u5979\u4e3a\u201c\u5de6\u660e\u73e0\u201d\uff0c\u5979\u660e\u660e\u662f\u201c\u65bd\u8335\u201d\u7684\u601d\u60f3\u548c\u7075 \u9b42\uff0c\u4f46\u82e5\u5979\u4e3a\u201c\u65bd\u8335\u201d\uff0c\u5979\u5374\u53c8\u660e\u660e\u662f\u201c\u5de6\u660e\u73e0\u201d\u3002 \u3000\u3000\u8fd9\u5c11\u5973\u5782\u7740\u5934\uff0c\u54ac\u7740\u5634\u5507\u9053\uff1a\u201c\u4f60\u65e2\u7136\u5df2\u770b\u8fc7\u4e86\uff0c\u603b\u8be5\u76f8\u4fe1\u6211\u8bf4\u7684\u8bdd\u5427\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\uff1a\u201c\u4f60\u7684\u786e\u6ca1\u6709\u9a97\u6211\u3002\u201d \u3000\u3000\u8fd9\u5c11\u5973\u9053\uff1a\u201c\u90a3\u9ebd\u4f60\u4e3a\u4f55\u8fd8\u4e0d\u653e\u6211\u8d70\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u53ef\u4ee5\u653e\u4f60\u8d70\uff0c\u4f46\u4f60\u80fd\u56de\u5f97\u53bb\u9ebd\uff1f\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u6211\u4e3a\u4ec0\u9ebd\u56de\u4e0d\u53bb\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4ee5\u4f60\u73b0\u5728\u8fd9\u6478\u6837\uff0c\u4f60\u56de\u53bb\u4e4b\u5f8c\u522b\u4eba\u4f1a\u4e0d\u4f1a\u8fd8\u627f\u8ba4\u4f60\u662f\u65bd\u8335\uff1f\u201d \u3000\u3000\u5c11\u5973\u773c\u6cea\u7acb\u523b\u6d41\u4e86\u4e0b\u6765\uff0c\u75db\u82e6\u7740\u9053\uff1a\u201c\u5929\u5440\uff0c\u6211\u600e\u4f1a\u53d8\u6210\u8fd9\u6837\u5b50\u7684\uff1f\u4f60\u53eb\u6211\u600e\u9ebd\u529e \u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u67d4\u58f0\u9053\uff1a\u201c\u6211\u65e2\u7136\u76f8\u4fe1\u4f60\u7684\u8bdd\uff0c\u4f60\u4e5f\u8be5\u76f8\u4fe1\u6211\u7684\u8bdd\uff0c\u65e0\u8bba\u4f60\u7684\u2018\u5fc3\u2019\u662f\u8c01\uff0c\u4f46\u4f60 \u7684\u8eab\u5b50\u7684\u786e\u662f\u5de6\u660e\u73e0\uff0c\u662f\u5de6\u8f7b\u4faf\u7684\u5973\u513f\u3002\u201d \u3000\u3000\u5c11\u5973\u4ee5\u624b\u6376\u5e8a\uff0c\u9053\uff1a\u201c\u4f46\u6211\u7684\u786e\u4e0d\u662f\u5de6\u660e\u73e0\uff0c\u66f4\u4e0d\u8ba4\u5f97\u5de6\u8f7b\u4faf\uff0c\u6211\u600e\u9ebd\u80fd\u627f\u8ba4\u4ed6\u662f\u6211\u7684 \u7236\u4eb2\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f46\u65bd\u4e3e\u4eba\u53ea\u6015\u4e5f\u4e0d\u4f1a\u8ba4\u4f60\u4e3a\u5973\u513f\u7684\uff0c\u53ea\u6015\u8fde\u53f6\u76db\u5170\u90fd\u4e0d\u4f1a\u8ba4\u5f97\u4f60\uff0c\u518d\u4e5f\u4e0d \u4f1a\u5c06\u5b9d\u9999\u658b\u7684\u82b1\u7c89\u9001\u7ed9\u4f60\u4e86\u3002\u201d \u3000\u3000\u5c11\u5973\u8eab\u5b50\u4e00\u9707\uff0c\u560e\u58f0\u9053\uff1a\u201c\u4f60\u600e\u9ebd\u4f1a\u8ba4\u5f97\u4ed6\u7684\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u600e\u9ebd\u4f1a\u8ba4\u5f97\u4ed6\u7684\uff1f\u201d \u3000\u3000\u5c11\u5973\u4f4e\u535c\u5934\uff0c\u9053\uff1a\u201c\u90a3\u662f\u5f88\u4e45\u4ee5\u524d\u7684\u4e8b\u4e86\uff0c\u6211\u4e5f\u4e0d\u77e5\u9053\uff0c\u6211\u600e\u4f1a\u88ab\u3002\u201d \u3000\u3000\u5979\u5ffd\u53c8\u62a2\u8d77\u5934\u3002\u5927\u58f0\u9053\uff1a\u201c\u4f46\u4e0d\u7ba1\u600e\u9ebd\u6837\u90a3\u4ef6\u4e8b\u90fd\u65e9\u5df2\u8fc7\u53bb\uff0c\u73b0\u5728\u6211\u5df2\u4e0d\u8ba4\u5f97\u53f6\u76db\u5170\uff0c \u6211\u53ea\u77e5\u9053\u6211\u662f\u859b\u5bb6\u672a\u8fc7\u95e8\u7684\u5ab3\u5987\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6697\u4e2d\u53f9\u4e86\u53e3\u6c14\u8fd9\u4ef6\u4e8b\u6700\u9ebb\u70e6\u7684\u5c31\u5728\u8fd9\u91cc\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u5de6\u4e8c\u7237\u65e9\u5df2\u5c06\u5de6\u660e\u73e0\u8bb8\u914d\u7ed9 \u4e01\u5bb6\u7684\u516c\u5b50\u4e86\u3002 \u3000\u3000\u5c31\u7b97\u5de6\u4e8c\u7237\u548c\u65bd\u4e3e\u4eba\u80fd\u5fc3\u5e73\u6c14\u548c\u7684\u5904\u7406\u8fd9\u4ef6\u4e8b\uff0c\u8fd9\u5973\u5b69\u5b50\u5c31\u80af\u627f\u8ba4\u4ed6\u4eec\u90fd\u662f\u5979\u7684\u7236\u4eb2\uff0c \u5374\u4e5f\u4e07\u4e07\u4e0d\u80fd\u5ac1\u7ed9\u4e24\u4e2a\u4e08\u592b\u7684\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u7a81\u542c\u5916\u9762\u201c\u7830\u201d\u7684\u4e00\u58f0\u5927\u9707\uff0c\u63a5\u7740\u5c31\u6709\u5404\u5f0f\u5404\u6837\uff0c\u4e71\u4e03\u516b\u7cdf\u7684\u58f0\u97f3\u54cd\u4e86\u8d77 \u6765\uff0c\u6709\u6454\u74f6\u5b50\uff0c\u6253\u76d8\u5b50\u7684\u58f0\u97f3\uff0c\u6709\u77f3\u5934\u63b7\u5728\u5c4b\u9876\u4e0a\uff0c\u5c4b\u74e6\u88ab\u6253\u788e\u7684\u58f0\u97f3\uff0c\u5176\u4e2d\u8fd8\u5939\u7740\u4e00\u5927\u7fa4 \u4eba\u5406\u559d\u6012\u9a82\u7684\u58f0\u97f3\u3002 \u3000\u3000\u695a\u7559\u9999\u76b1\u8d77\u4e86\u7709\uff0c\u89c9\u5f97\u5f88\u5947\u602a\uff0c\u96be\u9053\u771f\u6709\u4eba\u6562\u5230\u201c\u63b7\u676f\u5c71\u5e84\u201d\u6765\u6363\u4e71\u64a4\u91ce\u3002 \u3000\u3000\u53ea\u542c\u4e00\u4e2a\u53c8\u5c16\u3001\u53c8\u54cd\u4eae\u7684\u5973\u5b50\u58f0\u97f3\u9053\uff1a\u201c\u5de6\u8f7b\u4faf\uff0c\u8fd8\u6211\u7684\u5973\u513f\u6765\uff01\u201d \u3000\u3000\u5c11\u5973\u773c\u775b\u4e00\u4eae\uff0c\u5927\u559c\u9053\uff1a\u201c\u6211\u6bcd\u4eb2\u6765\u4e86\uff0c\u5979\u5df2\u77e5\u9053\u6211\u5728\u8fd9\u91cc\uff0c\u4f60\u4eec\u8fd8\u80fd\u4e0d\u653e\u6211\u8d70\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5979\u5230\u8fd9\u91cc\u6765\uff0c\u7edd\u4e0d\u662f\u6765\u627e\u4f60\u7684\u3002\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u4e0d\u662f\u627e\u6211\u627e\u8c01\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u8fd8\u672a\u8bf4\u8bdd\uff0c\u82b1\u91d1\u5f13\u5c16\u9510\u7684\u58f0\u97f3\u53c8\u4f20\u4e86\u8fdb\u6765\uff01 \u3000\u3000\u201c\u6211\u5973\u513f\u5c31\u662f\u88ab\u4f60\u8fd9\u8001\u9b3c\u5bb3\u6b7b\u7684\uff0c\u4f60\u77e5\u9053\u5979\u5f97\u4e86\u75c5\uff0c\u5c31\u6545\u610f\u5c06\u6240\u6709\u7684\u5927\u592b\u5168\u90fd\u85cf\u5728\u4f60 \u5bb6\uff0c\u8ba9\u5979\u7684\u75c5\u6ca1\u4eba\u6cbb\uff0c\u5426\u5219\u5979\u600e\u9ebd\u4f1a\u6b7b\uff1f\u6211\u8981\u4f60\u8d54\u547d\u3002\u201d \u3000\u3000\u5c11\u5973\u672c\u6765\u5df2\u60f3\u51b2\u51fa\u53bb\uff0c\u6b64\u523b\u53c8\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u695a\u56fe\u9999\u53f9\u9053\uff1a\u201c\u4f60\u73b0\u5728\u603b\u8be5\u77e5\u9053\u5979\u662f\u4e3a\u4ec0\u9ebd\u6765\u7684\u4e86\u5427\uff1f\u201d \u3000\u3000\u5c11\u5973\u4e00\u6b65\u6b65\u5f80\u5f8c\u9000\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u5979\u4e5f\u8bf4\u6211\u5df2\u7ecf\u6b7b\u4e86\uff0c\u6211\u96be\u9053\u2026\u2026\u96be\u9053\u771f\u7684\u5df2\u7ecf\u6b7b\u4e86 \u5417\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u5f53\u7136\u6ca1\u6709\u6b7b\uff0c\u53ea\u4e0d\u8fc7\u8fd9\u4ef6\u4e8b\u5b9e\u5728\u592a\u5947\u602a\uff0c\u8bf4\u51fa\u6765\u8c01\u4e5f\u4e0d\u4f1a\u76f8\u4fe1\u3002\u8fde\u4f60\u6bcd \u4eb2\u4e5f\u4e0d\u4f1a\u76f8\u4fe1\u7684\uff0c\u4f60\u73b0\u5728\u51fa\u53bb\u5979\u4e5f\u4e0d\u4f1a\u627f\u8ba4\u4f60\u662f\u5979\u7684\u5973\u513f\u3002\u201d \u3000\u3000\u5c11\u5973\u53d1\u4e86\u534a\u664c\u6014\uff0c\u5ffd\u7136\u8f6c\u8eab\u6251\u5012\u5728\u5e8a\u4e0a\uff0c\u4ee5\u624b\u6376\u5e8a\uff0c\u560e\u58f0\u9053\uff1a\u201c\u6211\u600e\u9ebd\u529e\u5462\uff1f\u6211\u600e\u9ebd\u529e \u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u67d4\u58f0\u9053\uff1a\u201c\u4f60\u82e5\u662f\u80af\u5b8c\u5168\u4fe1\u4efb\u6211\uff0c\u6211\u4e5f\u8bb8\u6709\u6cd5\u5b50\u66ff\u4f60\u89e3\u51b3\u8fd9\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u5c11\u5973\u4f0f\u5728\u5e8a\u4e0a\uff0c\u53c8\u54ed\u4e86\u5f88\u4e45\uff0c\u624d\u8f6c\u8fc7\u8eab\uff0c\u51dd\u6ce8\u7740\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u771f\u662f\u695a\u9999\u5e05\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6709\u65f6\u5019\u6211\u771f\u5e0c\u671b\u6211\u4e0d\u662f\u695a\u7559\u9999\uff0c\u4f46\u547d\u4e2d\u5374\u6ce8\u5b9a\u4e86\u6211\u975e\u505a\u695a\u7559\u9999\u4e0d \u53ef\u3002\u201d \u3000\u3000\u5c11\u5973\u51dd\u6ce8\u7740\u4ed6\u7684\u773c\u775b\uff0c\u9053\uff1a\u201c\u597d\uff0c\u6211\u5c31\u5728\u8fd9\u91cc\u803d\u53c1\u5929\uff0c\u8fc7\u4e86\u53c1\u5929\uff0c\u4f60\u82e5\u8fd8\u662f\u4e0d\u80fd\u89e3\u51b3\u8fd9 \u4ef6\u4e8b\uff0c\u6211\u2026\u2026\u6211\u5c31\u6b7b\uff0c\u6b7b\u4e86\u53cd\u800c\u597d\u4e9b\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u89c9\u5f97\u81ea\u5df1\u8fd9\u65f6\u8fd8\u662f\u83ab\u8981\u548c\u82b1\u91d1\u5f13\u76f8\u89c1\u7684\u597d\uff0c\u6240\u4ee5\u51b3\u5b9a\u5148\u53bb\u597d\u597d\u7761\u4e00\u89c9\uff0c\u517b\u8db3\u4e86\u7cbe \u795e\u665a\u4e0a\u624d\u597d\u529e\u4e8b\u3002 \u3000\u3000\u4ed6\u5fc3\u91cc\u4f3c\u4e4e\u5df2\u6709\u4e86\u5f88\u591a\u4e3b\u610f\uff0c\u53ea\u4e0d\u8fc7\u4ed6\u5374\u672a\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u7b49\u4ed6\u9192\u6765\u7684\u65f6\u5019\uff0c\u5929\u5df2\u9ed1\uff0c\u5de6\u4e8c\u7237\u5df2\u4e0d\u77e5\u6765\u770b\u8fc7\u4ed6\u591a\u5c11\u6b21\uff0c\u770b\u89c1\u4ed6\u9192\u6765\uff0c\u7b80\u76f4\u5982\u83b7\u81f3 \u5b9d\uff0c\u4e00\u628a\u62c9\u7740\u4ed6\u7684\u624b\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u4f60\u5012\u7761\u5f97\u597d\uff0c\u53ef\u77e5\u9053\u6211\u8fd9\u4e00\u5929\u53c8\u53d7\u4e86\u591a\u5c11\u7f6a\u9ebd\uff1f\u6211 \u7b80\u76f4\u8fde\u5934\u53d1\u90fd\u5feb\u6025\u79c3\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8dfa\u7740\u811a\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u82b1\u91d1\u5f13\u90a3\u6cfc\u5987\u5df2\u6765\u8fc7\u4e86\u9ebd\uff1f\u5979\u5c45\u7136\u6562\u5e26\u4e86\u4e00\u7fa4\u65e0\u8d56\u6765\u8fd9\u91cc\u5435 \u95f9\uff0c\u800c\u4e14\u8fd8\u8981\u6211\u66ff\u4ed6\u5973\u513f\u8d54\u547d\uff01\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u4f60\u662f\u600e\u9ebd\u6837\u5c06\u5979\u4eec\u6253\u53d1\u8d70\u4e86\uff1f\u201d \u3000\u3000\u5de6\u8f7b\u4faf\u6068\u6068\u9053\uff1a\u201c\u9047\u5230\u8fd9\u79cd\u6cfc\u5987\uff0c\u6211\u4e5f\u5b9e\u5728\u6ca1\u6709\u6cd5\u5b50\u4e86\uff0c\u6211\u82e5\u662f\u4f24\u4e86\u5979\uff0c\u5c82\u975e\u8981\u88ab\u6c5f\u6e56 \u670b\u53cb\u7b11\u6211\u8ddf\u5979\u4e00\u822c\u89c1\u8bc6\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e00\u70b9\u4e5f\u4e0d\u9519\uff0c\u5979\u6015\u5c31\u56e0\u4e3a\u77e5\u9053\u4e8c\u54e5\u7edd\u4e0d\u4f1a\u51fa\u624b\uff0c\u6240\u4ee5\u624d\u6562\u6765\u7684\u3002\u201d \u3000\u3000\u5de6\u8f7b\u4faf\u9053\uff1a\u201c\u6211\u53ea\u6709\u62ff\u90a3\u4e9b\u6cfc\u76ae\u65e0\u8d56\u51fa\u6c14\uff0c\u5979\u770b\u5230\u81ea\u5df2\u5e26\u6765\u7684\u4eba\u5168\u8eba\u4e0b\u4e86\u3002\u6c14\u7130\u624d\u5c0f\u4e86 \u4e9b\uff0c\u4f46\u4e34\u8d70\u7684\u65f6\u5019\u5374\u8fd8\u5728\u6492\u91ce\uff0c\u8bf4\u5979\u660e\u5929\u8fd8\u8981\u6765\u3002\u201d \u3000\u3000\u4ed6\u62c9\u7740\u695a\u7559\u9999\u7684\u624b\uff0c\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u4f60\u4eca\u5929\u665a\u4e0a\u597d\u6b79\u4e5f\u8981\u518d\u5230\u65bd\u5bb6\u5e84\u53bb\u8d70\u4e00\u8d9f\uff0c\u7ed9\u90a3\u6bcd\u8001 \u864e\u4e00\u4e2a\u6559\u8bad\uff0c\u5979\u660e\u5929\u82e5\u662f\u518d\u6765\uff0c\u6211\u53ef\u5b9e\u5728\u5403\u4e0d\u6d88\u3002\u201d\u4ed6\u81ea\u5df1\u4e0d\u613f\u548c\u82b1\u91d1\u5f13\u4ea4\u624b\uff0c\u5374\u53eb\u695a\u7559\u9999 \u53bb\uff0c\u8fd9\u79cd\u201c\u70eb\u828b\u5934\u201d\u695a\u7559\u9999\u867d\u5df2\u63a5\u5f97\u591a\u4e86\uff0c\u5374\u8fd8\u662f\u6709\u4e9b\u54ed\u7b11\u4e0d\u5f97\u3002 \u3000\u3000\u5de6\u8f7b\u4faf\u81ea\u5df1\u4f3c\u4e5f\u89c9\u5f97\u6709\u4e9b\u4e0d\u597d\u610f\u601d\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u77e5\u9053\u8fd9\u662f\u4ef6\u5f88\u4ee4\u4eba\u5934\u75bc\u7684\u4e8b\u3002\u4f46\u4e16 \u4e0a\u82e5\u8fd8\u6709\u4e00\u4e2a\u4eba\u80fd\u89e3\u51b3\u8fd9\u79cd\u4e8b\uff0c\u90a3\u4eba\u5c31\u662f\u4f60\u695a\u9999\u5e05\u3002\u201d \u3000\u3000\u8fd9\u79cd\u8bdd\u695a\u7559\u9999\u4e5f\u542c\u5f97\u591a\u4e86\u3002\u5fcd\u4e0d\u4f4f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u5c0f\u80e1\u8fd9\u6b21\u6ca1\u6709\u6765\uff0c\u5426\u5219\u8ba9\u4ed6 \u53bb\u5bf9\u4ed8\u82b1\u91d1\u5f13\uff0c\u624d\u771f\u662f\u5bf9\u75c7\u4e0b\u836f\u3002\u201d \u3000\u3000\u5de6\u8f7b\u4faf\u9053\uff1a\u201c\u5144\u5f1f\u4f60\u2026\u2026\u4f60\u96be\u9053\u4e0d\u53bb\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u4e8c\u54e5\u4f60\u653e\u5fc3\uff0c\u6211\u4e00\u5b9a\u6709\u6cd5\u5b50\u53eb\u5979\u660e\u5929\u6765\u4e0d\u4e86\u7684\u3002\u201d \u3000\u3000\u5de6\u8f7b\u4faf\u8fd9\u624d\u677e\u4e86\u53e3\u6c14\uff0c\u5ffd\u53c8\u76b1\u7709\u9053\uff1a\u201c\u53e6\u5916\u8fd8\u6709\u4ef6\u4e8b\uff0c\u4e5f\u5f97\u8981\u5144\u5f1f\u4f60\u66ff\u6211\u62ff\u4e2a\u4e3b\u610f\uff0c\u82b1 \u91d1\u5f13\u524d\u811a\u521a\u521a\u8d70\uff0c\u5f8c\u9762\u5c31\u6709\u4eba\u8ddf\u7740\u6765\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u8c01\uff1f\u4e16\u4e0a\u96be\u9053\u8fd8\u6709\u6bd4\u82b1\u91d1\u5f13\u66f4\u96be\u5bf9\u4ed8\u7684\u4eba\u9ebd\uff1f\u201d \u3000\u3000\u5de6\u8f7b\u4f7f\u53f9\u9053\uff1a\u201c\u82a6\u82b1\u8361\uff0c\u4e03\u661f\u5858\u7684\u4e01\u6c0f\u53cc\u4fa0\uff0c\u5144\u5f1f\u4f60\u603b\u8be5\u77e5\u9053\u5427\uff1f\u4eca\u5929\u6765\u7684\u5c31\u662f\u2018\u5434\u94a9 \u5251\u2019\u4e01\u6e1d\u4e01\u8001\u4e8c\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e01\u6c0f\u53cc\u4fa0\u5c82\u975e\u90fd\u662f\u4e8c\u54e5\u7684\u597d\u670b\u53cb\u9ebd\uff1f\u201d \u3000\u3000\u5de6\u8f7b\u4faf\u9053\uff1a\u201c\u975e\u4f46\u662f\u6211\u7684\u597d\u670b\u53cb\uff0c\u8fd8\u662f\u6211\u7684\u4eb2\u5bb6\uff0c\u4f46\u9ebb\u70e6\u4e5f\u5c31\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4ed6\u83ab\u975e\u662f\u6765\u8fce\u4eb2\u7684\uff1f\u201d \u3000\u3000\u5de6\u8f7b\u4faf\u8dcc\u8db3\u9053\uff1a\u201c\u4e00\u70b9\u4e5f\u4e0d\u9519\uff0c\u53ea\u56e0\u6211\u4eec\u4e0a\u4e2a\u6708\u5df2\u5546\u91cf\u597d\uff0c\u8ba2\u5728\u8fd9\u4e2a\u6708\u4e3a\u73e0\u513f\u548c\u4e01\u5982\u98ce \u6210\u4eb2\uff0c\u4e01\u8001\u4e8c\u8fd9\u6b21\u6765\uff0c\u6b63\u662f\u4e3a\u4e86\u8fd9\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e0a\u4e2a\u6708\u660e\u73e0\u5c82\u975e\u5df2\u7ecf\u75c5\u4e86\uff1f\u201d \u3000\u3000\u5de6\u8f7b\u4faf\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u5979\u75c5\u4e86\uff0c\u6240\u4ee5\u6211\u624d\u60f3\u4e3a\u8fd9\u5b69\u5b50\u51b2\u51b2\u559c\uff0c\u53ea\u671b\u5979\u4e00\u5ac1\u8fc7\u53bb\uff0c\u75c5\u5c31\u80fd\u597d \u8d77\u6765\uff0c\u8c01\u6599\u9053\u73b0\u5728\u7adf\u4f1a\u51fa\u4e86\u8fd9\u79cd\u4e8b\uff1f\u201d \u3000\u3000\u82e6\u7740\u8138\u9053\uff1a\u201c\u73b0\u5728\u6211\u82e5\u7b54\u5e94\u4ed6\u5728\u6708\u4e2d\u6210\u4eb2\uff0c\u73e0\u513f\u2026\u2026\u73e0\u513f\u600e\u9ebd\u80af\u5ac1\u8fc7\u53bb\uff0c\u5979\u82e5\u4e0d\u7b54\u5e94\uff0c \u53c8\u80fd\u6709\u4ec0\u9ebd\u6cd5\u5b50\u63a8\u6258\uff0c\u6211\u2026\u2026\u6211\u8fd9\u7b80\u76f4\u662f\u5728\u4f5c\u6cd5\u81ea\u6bd9\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u8272\u53ea\u6709\u6478\u9f3b\u5b50\u4e86\uff0c\u5583\u5583\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u82b1\u91d1\u5f13\u662f\u5426\u4e5f\u4e3a\u4ed6\u5973\u513f\u548c\u859b\u4e8c\u5c11\u8ba2\u4e86\u5a5a \u671f\u2026\u3002\u201d \u3000\u3000\u53ea\u89c1\u4e00\u4e2a\u5bb6\u4e01\u5306\u5306\u8d76\u8fc7\u6765\uff0c\u8eac\u8eab\u9053\uff1a\u201c\u4e01\u4e8c\u4fa0\u53eb\u5c0f\u4eba\u6765\u95ee\u8001\u7237\u695a\u9999\u5e05\u662f\u5426\u5df2\u9192\u4e86\uff0c\u82e5\u662f \u9192\u4e86\uff0c\u4ed6\u4e5f\u8981\u6765\u656c\u695a\u9999\u5e05\u7684\u9152\uff0c\u82e5\u662f\u6ca1\u6709\u9192\uff0c\u5c31\u8bf7\u8001\u7237\u5148\u5230\u524d\u9762\u53bb\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u4e45\u95fb\u4e01\u5bb6\u5f1f\u5144\u4e5f\u662f\u6d77\u91cf\uff0c\u5f20\u7b80\u658b\u5374\u8981\u4fdd\u517b\u8eab\u4f53\uff0c\u8fde\u4e00\u676f\u9152\u90fd\u4e0d\u996e\u7684\uff0c\u4e01 \u8001\u4e8c\u4e00\u5b9a\u89c9\u5f97\u4e00\u4e2a\u4eba\u559d\u9152\u6ca1\u610f\u601d\u3002\u201d \u3000\u3000\u5de6\u8f7b\u4faf\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5144\u5f1f\u4f60\u5c31\u5feb\u966a\u6211\u53bb\u5e94\u4ed8\u5e94\u4ed8\u4ed6\u5427\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u4e8c\u54e5\u96be\u9053\u8981\u6211\u9189\u85b0\u85b0\u7684\u95ef\u5230\u65bd\u5bb6\u5e84\u53bb\u9ebd\uff1f\u201d \u3000\u3000\u6c5f\u6e56\u4f20\u8bf4\u4e2d\uff0c\u6709\u4e9b\u201c\u9152\u4fa0\u201d\u3001\u201c\u9152\u4ed9\u201d\u4eec\uff0c\u9152\u559d\u5f97\u8d8a\u591a\uff0c\u6b66\u529f\u5c31\u8d8a\u9ad8\uff0c\u695a\u7559\u9999\u603b\u662f\u89c9\u5f97 \u8fd9\u4e9b\u4f20\u8bf4\u6709\u4e9b\u53ef\u7b11\u3002\u53ea\u56e0\u4ed6\u77e5\u9053\u4e00\u4e2a\u4eba\u9152\u82e5\u559d\u591a\u4e86\uff0c\u80c6\u5b50\u4e5f\u8bb8\u4f1a\u58ee\u4e9b\uff0c\u529b\u6c14\u4e5f\u8bb8\u4f1a\u5927\u4e9b\uff0c\u4f46 \u53cd\u5e94\u5374\u4e00\u5b9a\u4f1a\u53d8\u5f97\u8fdf\u949d\u5f97\u591a\u3002 \u3000\u3000\u6591\u624b\u76f8\u4e89\uff0c\u82e5\u662f\u4e00\u4e2a\u4eba\u7684\u53cd\u5e94\u8fdf\u949d\u4e86\uff0c\u5c31\u5fc5\u8d25\u65e0\u7591\u3002 \u3000\u3000\u6240\u4ee5\u695a\u7559\u9999\u867d\u7136\u4e5f\u5f88\u559c\u6b22\u559d\u9152\uff0c\u4f46\u5728\u771f\u6b63\u9047\u7740\u5f3a\u654c\u65f6\uff0c\u5934\u8111\u4e00\u5b9a\u4fdd\u6301\u7740\u6e05\u9192\u3002\u5947\u602a\u7684 \u662f\uff0c\u6c5f\u6e56\u4e2d\u5c45\u7136\u4e5f\u6709\u4eba\u8bf4\uff1a\u201c\u695a\u9999\u5e05\u7684\u9152\u559d\u5f97\u8d8a\u591a\uff0c\u6b66\u529f\u8d8a\u9ad8\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u8ba4\u4e3a\u8fd9\u4e9b\u8bdd\u4e00\u5b9a\u662f\u90a3\u4e9b\u4e0d\u4f1a\u559d\u9152\u7684\u4eba\u8bf4\u51fa\u6765\u7684\uff0c\u4e0d\u559d\u9152\u7684\u4eba\uff0c\u597d\u50cf\u603b\u8ba4\u4e3a\u559d\u9152\u7684 \u4eba\u662f\u67d0\u79cd\u602a\u7269\uff0c\u8fde\u8eab\u4f53\u7684\u6784\u9020\u90fd\u548c\u522b\u4eba\u4e0d\u540c\uff0c\u5176\u5b9e\u201c\u9152\u4ed9\u201d\u4e5f\u662f\u4eba\uff0c\u201c\u9152\u4fa0\u201d\u4e5f\u662f\u4eba\uff0c\u9152\u82e5 \u559d\u591a\u4e86\u7684\u4eba\uff0c\u8111\u888b\u4e5f\u4e00\u6837\u4f1a\u7cca\u6d82\u7684\u3002 \u3000\u3000\u4eca\u5929\u695a\u7559\u9999\u6ca1\u6709\u559d\u9152\uff0c\u5012\u5e76\u4e0d\u662f\u56e0\u4e3a\u82b1\u91d1\u5f13\u5a46\u5ab3\u96be\u5bf9\u4ed8\uff0c\u800c\u662f\u56e0\u4e3a\u90a3\u6b66\u529f\u7edd\u9ad8\u7684\u201c\u767d \u75f4\u201d\u3002 \u3000\u3000\u4ed6\u603b\u89c9\u5f97\u90a3\u201c\u767d\u75f4\u201d\u6709\u4e9b\u795e\u79d8\uff0c\u6709\u4e9b\u5947\u602a\uff0c\u7edd\u5bf9\u4e0d\u53ef\u8f7b\u89c6\u3002 \u3000\u3000\u53c1\u66f4\u524d\u695a\u7559\u9999\u4fbf\u5df2\u5230\u4e86\u201c\u65bd\u5bb6\u5e84\u201d\uff0c\u8fd9\u4e00\u6b21\u4ed6\u8f7b\u8f66\u719f\u8def\uff0c\u76f4\u7a9c\u5f8c\u56ed\uff0c\u5f8c\u56ed\u4e2d\u5bc2\u65e0\u4eba\u8ff9\uff0c \u53ea\u6709\u90a3\u7af9\u6797\u95fb\u7684\u5c0f\u5c4b\u91cc\u4ecd\u4eae\u7740\u706f\u5149\u3002 \u3000\u3000\u65bd\u8335\u7684\u4f53\u83ab\u975e\u8fd8\u5728\u5c0f\u5c4b\u91cc\uff1f \u3000\u3000\u695a\u7559\u9999\u8f7b\u70df\u822c\u63a0\u4e0a\u5c4b\u9876\uff0c\u63a2\u9996\u4e0b\u671b\uff0c\u5c31\u53d1\u73b0\u65bd\u8335\u4f53\u5df2\u88ab\u642c\u4e86\u51fa\u6765\uff0c\u4e00\u4e2a\u9752\u8863\u7d20\u670d\u3001\u4e2b\u5934 \u6253\u626e\u7684\u5c11\u5973\u6b63\u5728\u6536\u62fe\u7740\u5c4b\u5b50\u3002 \u3000\u3000\u706f\u5149\u4e2d\u770b\u6765\uff0c\u8fd9\u5c11\u5973\u4eff\u4f5b\u751a\u7f8e\uff0c\u5e76\u4e0d\u50cf\u505a\u8d31\u4e8b\u7684\u4eba\u3002 \u3000\u3000\u5979\u7684\u624b\u4e2d\u5728\u6574\u7406\u7740\u5e8a\u94fa\uff0c\u4e00\u53cc\u6c34\u6c6a\u6c6a\u7684\u773c\u775b\u5374\u779f\u7740\u5986\u53f0\u3002\u5ffd\u7136\u4f38\u624b\u62ff\u8d77\u4e00\u5323\u80ed\u8102\u5077\u5077\u85cf \u5728\u6000\u91cc\uff0c\u8fc7\u4e86\u534a\u664c\u53c8\u5bf9\u7740\u90a3\u94dc\u955c\uff0c\u8f7b\u8f7b\u7684\u626d\u52a8\u8170\u80a2\uff0c\u626d\u7740\u626d\u7740\uff0c\u81ea\u5df1\u62bf\u7740\u5634\u5077\u5077\u7684\u7b11\u4e86\u8d77 \u6765\u3002 \u3000\u3000\u695a\u7559\u9999\u6b63\u89c9\u5f97\u6709\u4e9b\u597d\u7b11\uff0c\u7a81\u542c\u4e00\u4eba\u9053\uff1a\u201c\u8fd9\u6b21\u4f60\u603b\u9003\u4e0d\u4e86\u5427\uff01\u201d \u3000\u3000\u5c4b\u89d2\u5f8c\u4eba\u5f71\u4e00\u95ea\uff0c\u8df3\u4e86\u51fa\u6765\u3002 \u3000\u3000\u695a\u7559\u9999\u4e5f\u4e0d\u7981\u5403\u4e86\u4e00\u60ca\u3002\u8fd9\u4eba\u597d\u5389\u5bb3\u7684\u773c\u529b\uff0c\u5c45\u7136\u53d1\u73b0\u695a\u7559\u9999\u7684\u85cf\u8eab\u4e4b\u5904\u3002 \u3000\u3000\u8c01\u77e5\u8fd9\u4eba\u8fde\u770b\u4e5f\u6ca1\u6709\u5411\u4ed6\u8fd9\u8fb9\u770b\u4e00\u773c\uff0c\u5634\u91cc\u8bf4\u7740\u8bdd\uff0c\u4eba\u5df2\u51b2\u8fdb\u4e86\u5c4b\u5b50\uff0c\u90a3\u662f\u4e2a\u7a7f\u7740\u81ea\u5b5d \u670d\u7684\u5c11\u5e74\u3002 \u3000\u3000\u90a3\u4e2b\u5934\u663e\u7136\u4e5f\u60ca\u4e86\u60ca\uff0c\u4f46\u56de\u5934\u770b\u5230\u8fd9\u5c11\u5e74\uff0c\u5c31\u7b11\u4e86\uff0c\u62cd\u7740\u80f8\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u5c11\u5e84\u4e3b\uff0c\u5bb3 \u5f97\u6211\u5413\u4e86\u4e00\u8df3\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u8fd9\u624d\u770b\u6e05\u4e86\u8fd9\u4f4d\u65bd\u5bb6\u5e84\u7684\u5c11\u5e84\u4e3b\uff0c\u53ea\u89c1\u767d\u751f\u751f\u7684\u817f\uff0c\u5df2\u6709\u4e9b\u53d1\u798f\uff0c\u663e\u7136\u662f\u5403\u5f97\u592a \u597d\uff0c\u7761\u5f97\u592a\u8db3\u4e86\u3002 \u3000\u3000\u4ed6\u8eab\u4e0a\u7a7f\u7684\u867d\u662f\u5b5d\u670d\uff0c\u4f46\u72b9\u53ef\u770b\u5230\u91cc\u9762\u90a3\u4e00\u8eab\u5929\u9752\u7684\u7f0e\u5b50\u8863\u670d\uff0c\u8138\u4e0a\u66f4\u6ca1\u6709\u4e1d\u6beb\u60b2\u621a\u4e4b \u8272\uff0c\u53cd\u800c\u7b11\u563b\u563b\u9053\uff1a\u201c\u4f60\u6015\u4ec0\u9ebd\uff1f\u6211\u4e5f\u4e0d\u4f1a\u5403\u4eba\u7684\uff0c\u6700\u591a\u4e5f\u4e0d\u8fc7\u5403\u5403\u4f60\u7684\u5634\u4e0a\u7684\u80ed\u8102\u3002\u201d \u3000\u3000\u90a3\u4e2b\u5934\u7b11\u9053\uff1a\u201c\u4eba\u5bb6\u4eca\u5929\u53c8\u6ca1\u6709\u643d\u80ed\u8102\uff01\u201d \u3000\u3000\u65bd\u4f20\u5b97\u9053\uff1a\u201c\u6211\u4e0d\u4fe1\uff0c\u6ca1\u6709\u643d\u80ed\u8102\uff0c\u5634\u600e\u9ebd\u4f1a\u7ea2\u5f97\u50cf\u6a31\u6843\uff0c\u6211\u8981\u5c1d\u3002\u201d\u4ed6\u4e00\u9762\u8bf4\u7740\u8bdd\uff0c \u4e00\u9762\u5df2\u63a5\u4f4f\u4e86\u90a3\u4e2b\u5934\u7684\u8170\u3002 \u3000\u3000\u90a3\u4e2b\u5934\u8dfa\u7740\u811a\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u597d\u5927\u7684\u80c6\u5b50\uff0c\u5feb\u653e\u624b\uff0c\u4e0d\u7136\u6211\u53ef\u8981\u53eb\u3002\u201d\u65bd\u4f20\u5b97\u8d4c\u7740\u6c14 \u9053\uff1a\u201c\u4f60\u53eb\u5427\uff0c\u6211\u4e0d\u6015\uff0c\u6211\u4e5f\u6ca1\u6709\u5077\u4e1c\u897f\uff01\u201d \u3000\u3000\u90a3\u4e2b\u5934\u773c\u73e0\u5b50\u4e00\u8f6c\uff0c\u4f3c\u7b11\u975e\u7b11\u7684\u5a07\u7740\u9053\uff1a\u201c\u597d\u5440\uff01\u4f60\u60f3\u8981\u631f\u6211\uff0c\u6211\u624d\u4e0d\u7a00\u7f55\u8fd9\u5323\u80ed\u8102\uff0c \u6211\u82e5\u60f3\u8981\uff0c\u4e5f\u4e0d\u77e5\u6709\u591a\u5c11\u4eba\u62a2\u7740\u6765\u9001\u7ed9\u6211\u3002\u201d \u3000\u3000\u65bd\u4f20\u5b97\u7b11\u9053\uff1a\u201c\u6211\u9001\u7ed9\u4f60\uff0c\u4f60\u9001\u7ed9\u4f60\u2026\u2026\u597d\u6a31\u513f\uff0c\u53ea\u8981\u4f60\u80af\u5c06\u5c31\u6211\uff0c\u6211\u628a\u5b9d\u9999\u658b\u7684\u80ed\u8102 \u82b1\u7c89\u5168\u90fd\u4e70\u6765\u9001\u7ed9\u4f60\u3002\u201d \u3000\u3000\u6a31\u513f\u54ac\u7740\u5634\u5507\u9053\uff1a\u201c\u6211\u53ef\u4e0d\u6562\u8981\uff0c\u6211\u6015\u5c11\u5976\u5976\u5265\u6211\u7684\u76ae\u3002\u201d \u3000\u3000\u65bd\u4f20\u5b97\u9053\uff1a\u201c\u6ca1\u5173\u7cfb\uff0c\u6ca1\u5173\u7cfb\u2026\u2026\u90a3\u6bcd\u8001\u864e\u4e0d\u4f1a\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u4ed6\u8eab\u5b50\u4e00\u6251\uff0c\u4e24\u4e2a\u4eba\u5c31\u6eda\u5230\u5e8a\u4e0a\u53bb\u4e86\u3002 \u3000\u3000\u6a31\u513f\u5598\u606f\u7740\u9053\uff1a\u201c\u4eca\u5929\u4e0d\u884c\uff0c\u8fd9\u5730\u65b9\u4e5f\u4e0d\u884c\u2026\u2026\u6628\u5929\u4e8c\u5c0f\u59d0\u5979\u3002\u201d\u8bdd\u672a\u8bf4\u5b8c\u5634\u5c31\u4f3c\u4e4e\u88ab \u4ec0\u9ebd\u4e1c\u897f\u5835\u4f4f\u4e86\u3002 \u3000\u3000\u65bd\u4f20\u5b97\u7684\u5598\u606f\u58f0\u66f4\u7c97\uff0c\u9053\uff1a\u201c\u4eca\u5929\u4e0d\u884c\uff0c\u660e\u5929\u5c31\u6ca1\u673a\u4f1a\u4e86\uff0c\u90a3\u6bcd\u8001\u864e\u76ef\u5f97\u597d\u51f6\u2026\u2026\u597d\u6a31 \u513f\uff0c\u53ea\u8981\u4f60\u7b54\u5e94\u8fd9\u4e00\u6b21\uff0c\u6211\u4ec0\u9ebd\u90fd\u7ed9\u4f60\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53c8\u597d\u6c14\uff0c\u53c8\u597d\u7b11\uff0c\u60f3\u5230\u90a3\u4f4d\u5c11\u5976\u5976\u7684\u201c\u5c0a\u5bb9\u201d\uff0c\u4ed6\u4e5f\u89c9\u5f97\u8fd9\u4f4d\u5c11\u5e84\u4e3b\u6709\u4e9b\u53ef\u601c\u3002 \u3000\u3000\u4ed6\u4e5f\u77e5\u9053\u8001\u5a46\u76ef\u5f97\u8d8a\u6b7b\uff0c\u7537\u4eba\u8d8a\u8981\u50cf\u5634\u998b\uff0c\u5929\u4e0b\u7684\u7537\u4eba\u90fd\u662f\u4e00\u6837\uff0c\u4e5f\u4e0d\u80fd\u602a\u8fd9\u4f4d\u5c11\u5e84 \u4e3b\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\u4ed6\u9009\u7684\u65f6\u5019\u548c\u5730\u65b9\u5b9e\u5728\u592a\u4e0d\u5bf9\u4e86\uff0c\u695a\u7559\u9999\u867d\u4e0d\u613f\u7ba1\u8fd9\u79cd\u95f9\u4e8b\uff0c\u4f46\u4e5f\u5b9e\u5728\u770b\u4e0d\u4e0b \u53bb\u3002 \u3000\u3000\u90a3\u5f20\u5e8a\u4e0d\u505c\u5730\u5728\u52a8\uff0c\u5df2\u6709\u6761\u767d\u751f\u751f\u7684\u817f\u6302\u4e0b\u5e8a\u6cbf\u3002 \u3000\u3000\u695a\u7559\u9999\u7a81\u7136\u6572\u4e86\u6572\u7a97\u6237\uff0c\u9053\uff1a\u201c\u6709\u4eba\u6765\u4e86\u3002\u201d \u3000\u3000\u8fd9\u77ed\u77ed\u56db\u4e2a\u5b57\u8fd8\u6ca1\u6709\u8bf4\u5b8c\uff0c\u5e8a\u4e0a\u7684\u4e24\u4e2a\u4eba\u5df2\u7ecf\u50cf\u4e24\u6761\u88ab\u4eba\u5543\u7740\u5c3e\u5df4\u7684\u732b\u4e00\u822c\u98a4\u4e86\u8d77\u6765\u3002 \u3000\u3000\u65bd\u4f20\u5b97\u8eab\u5b50\u5377\u6210\u4e00\u56e2\u7684\u53d1\u6296\u3002 \u3000\u3000\u6a31\u513f\u7684\u80c6\u5b50\u53cd\u5012\u5927\u4e9b\uff0c\u4e00\u9762\u7a7f\u8863\u670d\uff0c\u4e00\u9762\u5927\u58f0\u9053\uff1a\u201c\u662f\u8c01\uff1f\u60f3\u6765\u5077\u4e1c\u897f\u5417\uff1f\u201d \u3000\u3000\u65bd\u4f20\u5b97\u7acb\u523b\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e00\u5b9a\u662f\u5c0f\u5077\uff0c\u6211\u53bb\u53eb\u4eba\u6765\u6293\u3002\u201d \u3000\u3000\u4ed6\u811a\u5e95\u62b9\u6cb9\uff0c\u5df2\u60f3\u6e9c\u4e4b\u5927\u5409\u4e86\u3002 \u3000\u3000\u4f46\u695a\u7559\u9999\u8eab\u5b50\u4e00\u95ea\uff0c\u5df2\u6321\u4f4f\u4e86\u4ed6\u7684\u53bb\u8def\u3002 \u3000\u3000\u65bd\u4f20\u5b97\u4e5f\u4e0d\u77e5\u8fd9\u4eba\u600e\u9ebd\u6765\u5f97\u8fd9\u9ebd\u5feb\u7684\uff0c\u5403\u60ca\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u9ebd\u4eba\u2026\u201c\u597d\u5927\u80c6\u5b50\uff0c\u5077\u4e1c\u897f\u5c45 \u7136\u6562\u505a\u5230\u8fd9\u91cc\u6765\uff0c\u5feb\u5939\u7740\u5c3e\u5df4\u9003\u8d70\uff0c\u5c11\u5e84\u4e3b\u8fd8\u53ef\u4ee5\u9976\u4f60\u4e00\u547d\u3002\u201d \u3000\u3000\u770b\u5230\u6765\u4eba\u662f\u4e2a\u964c\u751f\u4eba\uff0c\u4ed6\u7684\u80c6\u5b50\u4e5f\u5ffd\u7136\u58ee\u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u4f60\u6700\u597d\u5148\u660e\u767d\u53c1\u4ef6\u4e8b\uff0c\u7b2c\u4e00\uff0c\u6211\u7edd\u4e0d\u4f1a\u9003\u8d70\uff0c\u7b2c\u4e8c\uff0c\u4f60\u6839\u672c\u4e0d\u662f\u6211\u7684\u5bf9 \u624b\u3002\u7b2c\u53c1\u6211\u66f4\u4e0d\u6015\u4f60\u53eb\u4eba\u3002\u201d \u3000\u3000\u4ed6\u6839\u672c\u6ca1\u6709\u505a\u51fa\u4efb\u4f55\u793a\u5a01\u7684\u52a8\u4f5c\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u50cf\u65bd\u4f20\u5b97\u8fd9\u6837\u7684\u98ce\u6d41\u9614\u5c11\uff0c\u7528\u51e0\u53e5\u8bdd\u5c31\u53ef \u4ee5\u5413\u4f4f\u4e86\u3002 \u3000\u3000\u65bd\u4f20\u5b97\u8138\u8272\u679c\u7136\u53d1\u4e86\u9752\uff0c\u5403\u5403\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u60f3\u600e\u9ebd\u6837\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u53ea\u95ee\u4f60\u60f3\u600e\u9ebd\u6837\uff1f\u662f\u8981\u6211\u53bb\u5c06\u4f60\u8001\u5a46\u627e\u6765\uff1f\u8fd8\u662f\u5e26\u6211\u53bb\u627e\u6881\u5988\u3002\u201d \u3000\u3000\u65bd\u4f20\u5b97\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u5e26\u4f60\u53bb\u627e\u6881\u5988\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e0d\u9519\u8fd9\u4e24\u6837\u4e8b\u968f\u4fbf\u4f60\u9009\u4e00\u6837\u3002\u201d \u3000\u3000\u8fd9\u9009\u62e9\u7b80\u76f4\u7adf\u50cf\u95ee\u4eba\u662f\u613f\u610f\u5403\u7ea2\u70e7\u8089\uff0c\u8fd8\u662f\u613f\u610f\u5403\u5927\u4fbf\u4e00\u6837\uff0c\u65bd\u4f20\u5b97\u4e00\u9897\u5fc3\u987f\u65f6\u5b9a\u4e86\u4e0b \u6765\u3002 \u3000\u3000\u4ed6\u6df1\u6015\u695a\u7559\u9999\u4f1a\u6539\u53d8\u4e3b\u610f\uff0c\u8d76\u7d27\u70b9\u5934\u9053\uff1a\u201c\u6211\u5e26\u4f60\u53bb\u627e\u6881\u5988\u3002\u201d \u3000\u3000\u5c0f\u9662\u4e2d\u7684\u504f\u5385\u5df2\u6539\u4f5c\u7075\u5802\u3002 \u3000\u3000\u6881\u5988\u5750\u5728\u7075\u4f4d\u65c1\uff0c\u5782\u7740\u5934\uff0c\u4f3c\u53c8\u7761\u7740\u4e86\uff0c\u6697\u6de1\u7684\u70db\u5149\uff0c\u6620\u7740\u9ec4\u68fa\u67e9\uff0c\u6620\u7740\u5979\u82cd\u82cd\u767d\u53d1\uff0c \u770b\u6765\u771f\u662f\u8bf4\u4e0d\u51fa\u7684\u51c4\u60e8\u3002 \u3000\u3000\u65bd\u4f20\u5b97\u5e26\u7740\u695a\u7559\u9999\u7ed5\u5c0f\u8def\u8d70\u5230\u8fd9\u91cc\uff0c\u5fc3\u91cc\u4e00\u76f4\u5728\u5947\u602a\uff0c\u65e0\u8bba\u5982\u4f55\u4e5f\u60f3\u4e0d\u51fa\u8fd9\u4eba\u627e\u6881\u5988\u662f \u4e3a\u7684\u4ec0\u9ebd\uff1f \u3000\u3000\u53ea\u89c1\u695a\u7559\u9999\u8d70\u8fc7\u53bb\u7ad9\u5728\u6881\u5988\u9762\u524d\uff0c\u8f7b\u8f7b\u5fae\u54b3\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u6881\u5988\u4e00\u60ca\uff0c\u51e0\u4e4e\u8fde\u5165\u5e26\u6905\u5b50\u90fd\u8dcc\u5012\u5728\u5730\uff0c\u4f46\u7b49\u5979\u770b\u6e05\u695a\u9762\u524d\u7684\u4eba\u65f6\uff0c\u5979\u5df2\u54ed\u5f97\u53d1\u7ea2\u7684\u8001 \u773c\u4e2d\u7adf\u4e5f\u9732\u51fa\u4e00\u4e1d\u6b23\u6170\u4e4b\u610f\uff0c\u9053\uff1a\u201c\u539f\u6765\u53c8\u662f\u4f60\uff0c\u4f60\u603b\u7b97\u662f\u4e2a\u6709\u826f\u5fc3\u7684\u4eba\uff0c\u4e5f\u4e0d\u6789\u8335\u513f\u4e3a\u4e86 \u4f60\u2026\u2026\u201d \u3000\u3000\u8bf4\u5230\u201c\u8335\u513f\u201d\uff0c\u5979\u5589\u5934\u53c8\u88ab\u585e\u4f4f\u3002 \u3000\u3000\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e0d\u8ba4\u5f97\u4f60\u7684\u4eba\uff0c\u4e00\u5b9a\u4f1a\u4ee5\u4e3a\u4f60\u624d\u662f\u8335\u59d1\u5a18\u7684\u6bcd\u4eb2\u3002\u201d \u3000\u3000\u6881\u5988\u54fd\u54bd\u7740\u9053\uff1a\u201c\u8335\u513f\u867d\u4e0d\u662f\u6211\u751f\u7684\uff0c\u5374\u662f\u6211\u4ece\u5c0f\u5e26\u5927\u7684\uff0c\u6211\u5b64\u82e6\u4f36\u4ec3\uff0c\u65e0\u4f9d\u65e0\u9760\uff0c\u53ea \u6709\u5979\u53ef\u7b97\u662f\u6211\u7684\u4eb2\u4eba\uff0c\u73b0\u5728\u5979\u5df2\u6b7b\u4e86\uff0c\u6211\u2026\u2026\u6211\u2026\u2026\u201d \u3000\u3000\u695a\u7559\u9999\u5fc3\u91cc\u4e5f\u4e0d\u7981\u89c9\u5f97\u6709\u4e9b\u51c4\u51c9\uff0c\u8fd9\u65f6\u65bd\u4f20\u5b97\u5df2\u6084\u6084\u6e9c\u8d70\uff0c\u4f46\u4ed6\u5374\u6545\u610f\u88c5\u4f5c\u6ca1\u6709\u770b\u5230\u3002 \u3000\u3000\u6881\u5988\u64e6\u7740\u773c\u6cea\uff0c\u9053\uff1a\u201c\u4f60\u65e2\u6765\u4e86\uff0c\u4e5f\u7b97\u5c3d\u5230\u4e86\u4f60\u7684\u5fc3\u610f\uff0c\u73b0\u5728\u8fd8\u662f\u5feb\u8d70\u5427\uff0c\u82e5\u662f\u518d\u88ab\u592b \u4eba\u53d1\u73b0\uff0c\u6015\u5c31\u2026\u2026\u201d \u3000\u3000\u695a\u7559\u9999\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u60f3\u4e0d\u60f3\u518d\u89c1\u8335\u59d1\u5a18\u4e00\u9762\uff1f\u201d \u3000\u3000\u6881\u5988\u970d\u7136\u62ac\u8d77\u5934\uff0c\u5403\u60ca\u7684\u671b\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u4f46\u2026\u2026\u4f46\u5979\u5df2\u6b7b\u4e86\uff01\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u82e5\u60f3\u89c1\u5979\uff0c\u6211\u8fd8\u6709\u6cd5\u5b50\u3002\u201d \u3000\u3000\u6881\u5988\u9a87\u7136\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u6709\u4ec0\u9ebd\u6cd5\u5b50\uff1f\u96be\u9053\u4f60\u4f1a\u62db\u9b42\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u73b0\u5728\u4e5f\u4e0d\u5fc5\u591a\u95ee\uff0c\u603b\u4e4b\uff0c\u660e\u5929\u6b63\u5348\u65f6\uff0c\u4f60\u82e5\u80af\u5728\u79c0\u91ce\u6865\u5934\u7b49\u6211\uff0c\u6211\u5c31\u6709 \u6cd5\u5b50\u5e26\u4f60\u53bb\u89c1\u8335\u59d1\u5a18\u3002\u201d \u3000\u3000\u6881\u5988\u5446\u4e86\u5f88\u4e45\uff0c\u6697\u54fa\u9053\uff1a\u201c\u660e\u5929\u6b63\u5348\uff0c\u79c0\u91ce\u6865\uff0c\u4f60\u2026\u2026\u4f60\u96be\u9053\u2026\u2026\u201d \u3000\u3000\u7a81\u542c\u4e00\u4eba\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u7b97\u4f60\u591f\u80c6\uff0c\u6628\u5929\u9976\u4e86\u4f60\u4e00\u547d\uff0c\u4eca\u5929\u4f60\u5c45\u7136\u8fd8\u6562\u6765\uff01\u201d \u3000\u3000\u695a\u7559\u9999\u4e0d\u7528\u56de\u5934\uff0c\u5c31\u5df2\u77e5\u9053\u8fd9\u662f\u82b1\u91d1\u5f13\u6765\u4e86\uff0c\u4f46\u4ed6\u770b\u6765\u4e00\u70b9\u4e5f\u4e0d\u5403\u60ca\uff0c\u4f3c\u4e4e\u65e9\u5c31\u7b49\u7740\u5979 \u6765\u3002 \u3000\u3000\u53ea\u89c1\u82b1\u91d1\u5f13\u548c\u65bd\u5c11\u5976\u5976\u4eca\u5929\u90fd\u6362\u4e86\u4e00\u8eab\u7d27\u8eab\u8863\u88e4\uff0c\u8fd8\u5e26\u7740\u5341\u51e0\u4e2a\u52b2\u88c5\u7684\u4e2b\u73af\uff0c\u6bcf\u4e2a\u4eba\u90fd \u624b\u6301\u91d1\u5f13\uff0c\u80cc\u63d2\u53cc\u5251\uff0c\u884c\u52a8\u5c45\u7136\u90fd\u5341\u5206\u77eb\u5065\u3002 \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u4e45\u95fb\u592b\u4eba\u7684\u5a18\u5b50\u519b\u82f1\u52c7\u66f4\u80dc\u987b\u7709\uff0c\u4eca\u65e5\u4e00\u89c1\uff0c\u679c\u7136\u4e0d\u865a\u3002\u201d \u3000\u3000\u82b1\u91d1\u5f13\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u5c11\u6765\u62cd\u9a6c\u5c41\uff0c\u6211\u53ea\u95ee\u4f60\uff0c\u4f60\u7a76\u7adf\u662f\u4e0d\u662f\u695a\u7559\u9999\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u695a\u7559\u9999\uff0c\u6211\u770b\u6765\u5f88\u50cf\u695a\u7559\u9999\u5417\uff1f\u201d \u3000\u3000\u65bd\u5c11\u5976\u5976\u94c1\u9752\u7740\u8138\uff0c\u5389\u58f0\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u7ba1\u4f60\u662f\u695a\u7559\u9999\uff0c\u8fd8\u662f\u695a\u7559\u81ed\uff0c\u4f60\u65e2\u7136\u6709\u80c6\u5b50\u6765\uff0c \u6211\u4eec\u5c31\u6709\u672c\u4e8b\u53eb\u4f60\u6765\u5f97\u53bb\u4e0d\u5f97\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u597d\u5a01\u98ce\u5440\uff0c\u597d\u6740\u6c14\uff0c\u96be\u602a\u65bd\u5c11\u5e84\u4e3b\u8981\u754f\u4f60\u5982\u864e\u4e86\u3002\u201d \u3000\u3000\u65bd\u4f20\u5b97\u5ffd\u7136\u5728\u7a97\u5b50\u5916\u4e00\u63a2\u5934\uff0c\u5927\u58f0\u9053\uff1a\u201c\u6211\u4eec\u592b\u59bb\u662f\u76f8\u656c\u5982\u5bbe\uff0c\u4f60\u5c0f\u5b50\u5c11\u6765\u6311\u62e8\u79bb \u95f4\u3002\u201d \u3000\u3000\u82b1\u91d1\u5f13\u9053\uff1a\u201c\u5e9f\u8bdd\u5c11\u8bf4\uff0c\u6211\u95ee\u4f60\u662f\u60f3\u6d3b\uff1f\u8fd8\u662f\u60f3\u6b7b\uff1f\u201d\u74dc \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5728\u4e0b\u6d3b\u5f97\u86ee\u6709\u8da3\uff0c\u81ea\u7136\u60f3\u6d3b\u7684\u3002\u201d \u3000\u3000\u82b1\u91d1\u5f13\u9053\uff1a\u201c\u4f60\u82e5\u60f3\u6d3b\uff0c\u5c31\u4e56\u4e56\u7684\u8dea\u4e0b\u6765\u675f\u624b\u5c31\u7f1a\uff0c\u7b49\u6211\u4eec\u95ee\u6e05\u695a\u4f60\u7684\u6765\u5386\uff0c\u4e5f\u8bb8\u2026\u2026 \u4e5f\u8bb8\u975e\u4f46\u4e0d\u6740\u4f60\uff0c\u8fd8\u6709\u597d\u5904\u7ed9\u4f60\u3002\u201d \u3000\u3000\u5979\u6545\u610f\u5c06\u201c\u597d\u5904\u201d\u4e24\u4e2a\u5b57\u8bf4\u5f97\u53c8\u8f7b\u53c8\u8f6f\uff0c\u600e\u5948\u695a\u7559\u9999\u5374\u50cf\u4e00\u70b9\u4e5f\u4e0d\u61c2\uff0c\u6de1\u6de1\u95ee\u9053\uff1a\u201c\u6211 \u82e5\u60f3\u6b7b\u5462\uff1f\u201d \u3000\u3000\u82b1\u91d1\u5f13\u6012\u9053\uff1a\u201c\u90a3\u5c31\u66f4\u5bb9\u6613\uff0c\u6211\u53ea\u8981\u4e00\u62ac\u624b\uff0c\u8fde\u73e0\u7bad\u4e00\u53d1\uff0c\u4f60\u5c31\u8981\u53d8\u523a\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u7261\u4e39\u82b1\u4e0b\u6b7b\uff0c\u505a\u9b3c\u4e5f\u98ce\u6d41\uff0c\u505a\u523a\u53c8\u4f55\u59a8\uff1f\u201d \u3000\u3000\u82b1\u91d1\u5f13\u9053\uff1a\u201c\u597d\uff0c\u8fd9\u662f\u4f60\u81ea\u627e\u7684\uff0c\u6028\u4e0d\u5f97\u6211\u3002\u201d \u3000\u3000\u5979\u7684\u624b\u4e00\u62db\uff0c\u91d1\u5f13\u5df2\u642d\u8d77\u3002\u5341\u51e0\u4e2a\u5a18\u5b50\u519b\u4e5f\u7acb\u523b\u5f20\u5f13\u642d\u7bad\uff0c\u770b\u5979\u4eec\u7684\u624b\u52bf\uff0c\u5df2\u77e5\u9053\u8fd9\u4e9b \u5c0f\u6cf5\u5a18\u4e00\u4e2a\u4e2a\u90fd\u662f\u767e\u6b65\u7a7f\u6768\u7684\u597d\u624b\uff0c\u4f55\u51b5\u201c\u8fde\u73e0\u7bad\u201d\u8fde\u7ef5\u4e0d\u7edd\uff0c\u5c31\u7b97\u80fd\u8eb2\u5f97\u4e86\u7b2c\u4e00\u8f6e\u7bad\uff0c\u7b2c \u4e8c\u8f6e\u7bad\u5c31\u672a\u5fc5\u8eb2\u5f97\u5f00\u4e86\u3002 \u3000\u3000\u8c01\u77e5\u5c31\u5728\u8fd9\u65f6\uff0c\u695a\u7559\u9999\u8eab\u5b50\u5ffd\u7136\u4e00\u95ea\uff0c\u53ea\u542c\u4e00\u8fde\u4e32\u5a07\u547c\uff0c\u4e5f\u4e0d\u77e5\u600e\u5730\uff0c\u5341\u9980\u67c4\u91d1\u5f13\u5ffd\u7136 \u5168\u90fd\u5230\u4e86\u695a\u7559\u9999\u5b50\u4e0a\uff0c\u5341\u9980\u4e2a\u5c11\u5973\u77f3\u50cf\u822c\u5b9a\u5728\u90a3\u91cc\uff0c\u7adf\u5df2\u5168\u90e8\u90fd\u88ab\u70b9\u4e86\u7a74\u9053\uff0c\u82b1\u91d1\u5f13\u548c\u65bd\u5c11 \u5976\u5976\u867d\u7136\u660e\u77e5\u9053\uff1a\u201c\u6f02\u4eae\u5c0f\u4f19\u5b50\u201d\u6709\u4e24\u4e0b\u5b50\u3002\u201d\u5374\u4ece\u672a\u60f3\u5230\u4ed6\u7adf\u6709\u5982\u6b64\u5feb\u7684\u51fa\u624b\u4e24\u4eba\u4ea4\u6362\u4e86 \u4e2a\u773c\u8272\uff0c\u4e00\u67c4\u5f13\uff0c\u4e24\u53e3\u5251\uff0c\u95ea\u7535\u822c\u653b\u51fa\u3002 \u3000\u3000\u4f46\u695a\u7559\u9999\u4eca\u5929\u5374\u4f3c\u5b58\u5fc3\u8981\u7ed9\u5979\u4eec\u70b9\u989c\u8272\u770b\uff0c\u518d\u4e5f\u4e0d\u50cf\u6628\u5929\u90a3\u9ebd\u5ba2\u6c14\u4e86\uff0c\u8eab\u5b50\u4e00\u8f6c\uff0c\u4e5f\u4e0d \u77e5\u7528\u4e86\u4ec0\u9ebd\u62db\u5f0f\uff0c\u5c31\u5df2\u62ce\u4f4f\u4e86\u65bd\u5c11\u5976\u5976\u7684\u624b\u8155\uff0c\u5c06\u5979\u7684\u5251\u5411\u524d\u9762\u4e00\u9001\uff0c\u53ea\u542c\u201c\u55e1\u201d\u7684\u4e00\u58f0\uff0c \u82b1\u91d1\u5f13\u7684\u5f26\u5df2\u88ab\u5272\u65ad\u3002\u695a\u7559\u9999\u5012\u9000\u51e0\u6b65\uff0c\u8eac\u8eab\u7b11\u9053\uff1a\u201c\u5510\u7a81\u4f73\u4eba\uff0c\u4e07\u4e0d\u5f97\u5df2\uff0c\u6055\u7f6a\u6055\u7f6a\u3002\u201d \u3000\u3000\u65bd\u5c11\u5976\u5976\u8138\u8272\u53d1\u767d\uff0c\u5979\u6bd5\u7adf\u662f\u540d\u5bb6\u4e4b\u5973\uff0c\u8bc6\u8d27\u5f97\u5f88\uff0c\u6b64\u523b\u5df2\u770b\u51fa\u81ea\u5df1\u7edd\u4e0d\u662f\u8fd9\u5c0f\u4f19\u5b50\u7684 \u5bf9\u624b\uff0c\u5ffd\u7136\u629b\u4e0b\u53cc\u5251\uff0c\u4e00\u628a\u5c06\u65bd\u4f20\u5b97\u4ece\u95e8\u5916\u63ea\u4e86\u8fdb\u6765\uff0c\u8dfa\u811a\u9053\uff1a\u201c\u4f60\u8001\u5a46\u88ab\u4eba\u6b3a\u8d1f\uff0c\u4f60\u5374\u53ea \u4f1a\u6218\u5728\u65c1\u8fb9\u505a\u7f29\u5934\u4e4c\u9f9f\uff0c\u8fd9\u8fd8\u80fd\u7b97\u4e2a\u7537\u4eba\u5417\uff1f\u5feb\u6253\u6b7b\u4ed6\uff0c\u66ff\u6211\u51fa\u6c14\u3002\u201d \u3000\u3000\u65bd\u4f20\u5b97\u8138\u8272\u6bd4\u4ed6\u8001\u5a46\u66f4\u81ea\uff0c\u9053\uff1a\u201c\u662f\u662f\u662f\uff0c\u6211\u6253\u6b7b\u4ed6\uff0c\u6211\u66ff\u4f60\u51fa\u6c14\u3002\u201d \u3000\u3000\u4ed6\u5634\u4e0a\u8bf4\u5f97\u867d\u54cd\uff0c\u4e24\u6761\u817f\u53ef\u6ca1\u6709\u79fb\u52a8\u534a\u6b65\u3002\u3001 \u3000\u3000\u65bd\u5c11\u5976\u5976\u7528\u62f3\u5934\u64ad\u7740\u4ed6\u7684\u80f8\u819b\u9053\uff1a\u201c\u53bb\u5440\uff0c\u53bb\u5440\uff0c\u96be\u9053\u8fde\u8fd9\u70b9\u80c6\u5b50\u90fd\u6ca1\u6709\uff1f\u201d \u3000\u3000\u65bd\u4f20\u5b97\u88ab\u6253\u5f97\u8df3\u7259\u7684\u5634\uff0c\u8fde\u8fde\u9053\uff1a\u201c\u597d\uff0c\u6211\u53bb\uff0c\u6211\u8fd9\u5c31\u53bb\u3002\u201d \u3000\u3000\u8bdd\u672a\u8bf4\u5b8c\uff0c\u5ffd\u7136\u4e00\u6e9c\u70df\u7684\u9003\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u65bd\u5c11\u5976\u5976\u54ac\u7740\u7259\uff0c\u7adf\u7136\u653e\u58f0\u5927\u54ed\u8d77\u6765\uff0c\u558a\u7740\u9053\uff1a\u201c\u5929\u5440\uff0c\u6211\u5ac1\u4e86\u4e2a\u8fd9\u9ebd\u6ca1\u7528\u7684\u7537\u4eba\uff0c\u4f60 \u53eb\u6211\u600e\u9ebd\u6d3b\u5440\u2026\u2026\u201d \u3000\u3000\u5979\u5ffd\u7136\u4e00\u5934\u649e\u4eba\u82b1\u91d1\u5f13\u6000\u91cc\uff0c\u5636\u58f0\u9053\uff1a\u201c\u6211\u5ac1\u5230\u4f60\u4eec\u5bb6\u91cc\u771f\u662f\u5012\u4e86\u516b\u8f88\u5b50\u7684\u9709\uff0c\u5426\u5219\u6709 \u8c01\u6562\u6b3a\u8d1f\u6211\uff0c\u6211\u4e5f\u4e0d\u60f3\u6d3b\u4e86\uff0c\u4f60\u4eec\u4e7e\u8106\u6740\u4e86\u6211\u2026\u2026\u201d \u3000\u3000\u695a\u7559\u9999\u770b\u5f97\u53c8\u597d\u6c14\uff0c\u53c8\u597d\u7b11\uff0c\u4ed6\u4e5f\u60f3\u4e0d\u5230\u8fd9\u4f4d\u5c11\u5976\u5976\u4e0d\u4f46\u4f1a\u4f7f\u5251\uff0c\u64a4\u6cfc\u64a4\u8d56\u7684\u672c\u4e8b\u4e5f\u4e0d \u9519\u3002 \u3000\u3000\u53ea\u89c1\u82b1\u91d1\u5f13\u4e24\u773c\u53d1\u76f4\uff0c\u663e\u7136\u4e5f\u62ff\u5979\u8fd9\u5ab3\u5987\u6ca1\u6cd5\u5b50\u3002 \u3000\u3000\u695a\u7559\u9999\u60a0\u7136\u9053\uff1a\u201c\u5c11\u5976\u5976\u8fd9\u64a4\u8d56\u7684\u529f\u592b\uff0c\u96be\u9053\u4e5f\u662f\u5bb6\u4f20\u7684\u9ebd\uff1f\u201d \u3000\u3000\u65bd\u5c11\u5976\u5976\u773a\u4e86\u8d77\u6765\uff0c\u54ed\u543c\u7740\uff1a\u201c\u65bd\u653e\u7684\u4ec0\u9ebd\u5c41\uff1f\u9664\u4e86\u6b3a\u8d1f\u5973\u4eba\u4f60\u8fd8\u4f1a\u5e72\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u672c\u6765\u4e5f\u8ba4\u4e3a\u4f60\u771f\u662f\u5973\u4eba\uff0c\u73b0\u5728\u5374\u5df2\u6709\u4e9b\u6000\u7591\u4e86\u3002\u201d \u3000\u3000\u65bd\u5c11\u5976\u5976\u54ac\u7740\u7259\u9053\uff1a\u201c\u4f60\u80fd\u7b97\u662f\u7537\u4eba\u9ebd\uff1f\u4f60\u82e5\u6562\u8ddf\u6211\u53bb\u89c1\u7239\u7239\uff0c\u7b97\u4f60\u662f\u4e2a\u7537\u4eba\uff0c\u5426\u5219\u3002 \u4f60\u5c31\u662f\u4e2a\u4e0d\u7537\u4e0d\u5973\u7684\u56e1\u79cd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u6de1\u6de1\u9053\uff1a\u201c\u6211\u82e5\u4e0d\u6562\u53bb\uff0c\u4eca\u5929\u665a\u4e0a\u4e5f\u5c31\u4e0d\u4f1a\u518d\u6765\u4e86\uff0c\u4f46\u4f60\u73b0\u5728\u6700\u597d\u5b89\u9759\u4e9b\uff0c\u5426\u5219 \u6211\u5c31\u7528\u7a3b\u8349\u585e\u4f4f\u4f60\u7684\u5634\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u7684\u5e84\u9662\u89c4\u6a21\u4e0d\u5982\u201c\u63b7\u676f\u5c71\u5e84\u201d\u5e9e\u5927\uff0c\u4f46\u98ce\u683c\u5374\u66f4\u5e7d\u96c5\uff0c\u5385\u5802\u4e2d\u9648\u8bbe\u867d\u975e\u534e\u7f8e\uff0c\u4f46 \u5374\u5f53\u771f\u662f\u4e00\u5c18\u4e0d\u67d3\uff0c\u7a97\u4e0a\u7edd\u6ca1\u6709\u4e1d\u6beb\u79ef\u5c18\uff0c\u9662\u5b50\u91cc\u7edd\u6ca1\u6709\u4e00\u7247\u843d\u53f6\uff0c\u6b64\u523b\u867d\u65b9\u6e05\u6668\uff0c\u5374\u5df2\u6709 \u4eba\u5728\u6e05\u626b\u7740\u5ead\u9662\u3002 \u3000\u3000\u65bd\u5c11\u5976\u5976\u4e00\u8def\u4e0a\u679c\u7136\u90fd\u8001\u5b9e\uff0c\u695a\u7559\u9999\u6697\u6697\u597d\u7b11\u3002 \u3000\u3000\u4f46\u4e00\u5230\u4e86\u859b\u5bb6\u5e84\uff0c\u5c31\u7acb\u523b\u53c8\u5a01\u98ce\u4e86\u8d77\u6765\uff0c\u8df3\u7740\u811a\uff0c\u6307\u7740\u695a\u7559\u9999\u7684\u9f3b\u5b50\u9053\uff1a\u201c\u4f60\u6709\u79cd\u5c31\u83ab \u8981\u9003\u8d70\uff0c\u6211\u53bb\u53eb\u7239\u7239\u51fa\u6765\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u82e5\u8981\u8d70\uff0c\u53c8\u4f55\u5fc5\u6765\uff1f\u201d \u3000\u3000\u82b1\u91d1\u5f13\u773c\u775b\u779f\u7740\u4ed6\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u80c6\u5b50\u592a\u5927\uff0c\u547d\u5c31\u4f1a\u77ed\u7684\u3002\u201d \u3000\u3000\u65bd\u5c11\u5976\u5976\u521a\u51b2\u8fdb\u53bb\u6ca1\u591a\u4e45\uff0c\u5c31\u542c\u5f97\u4e00\u4eba\u6c89\u58f0\u9053\uff1a\u201c\u4f60\u4e0d\u597d\u597d\u5728\u5bb6\u4f3a\u5019\u7fc1\u59d1\uff0c\u53c8\u5230\u8fd9\u91cc\u6765 \u4f5c\u751a\uff1f\u201d \u3000\u3000\u8fd9\u58f0\u97f3\u4f4e\u6c89\u4e2d\u9690\u9690\u6709\u5a01\u4e00\u542c\u5c31\u77e5\u9053\u662f\u64c5\u65bc\u53d1\u53f7\u65bd\u4ee4\u4e4b\u4eba\u3002 \u3000\u3000\u65bd\u5c11\u5976\u5976\u5e26\u7740\u54ed\u58f0\u9053\uff1a\u201c\u6709\u4eba\u6b3a\u8d1f\u4e86\u5973\u513f\uff0c\u7239\u4e5f\u4e0d\u95ee\u4e00\u58f0\uff0c\u5c31\u2026\u2026\u201d \u3000\u3000\u90a3\u4eba\u5389\u58f0\u9053\uff1a\u201c\u4f60\u82e5\u5b89\u4efd\u5b88\u5df1\u505a\u4eba\uff0c\u6709\u8c01\u4f1a\u5e73\u767d\u65e0\u6545\u7684\u6765\u6b3a\u8d1f\u4f60\uff0c\u60f3\u5fc5\u662f\u4f60\u53c8\u72af\u4e86\u5c0f\u5b69 \u5b50\u813e\u6c14\u2026\u3002\u4eb2\u5bb6\u6bcd\uff0c\u4f60\u8be5\u591a\u7ba1\u6559\u7ba1\u6559\u5979\u624d\u662f\uff0c\u4e07\u4e07\u4e0d\u53ef\u5ba2\u6c14\u3002\u201d \u3000\u3000\u82b1\u91d1\u5f13\u5df2\u8d76\u7d27\u7ad9\u4e86\u8d77\u6765\uff0c\u966a\u7b11\u9053\uff1a\u201c\u8fd9\u8d9f\u4e8b\u53ef\u534a\u70b9\u4e0d\u80fd\u602a\u59d1\u5976\u5976\uff0c\u5168\u662f\u8fd9\u5c0f\u5b50\u2026\u2026\u201d \u3000\u3000\u5979\u82b1\u8bf4\u4ec0\u9ebd\uff0c\u695a\u7559\u9999\u5df2\u61d2\u5f97\u53bb\u542c\u4e86\uff0c\u53ea\u89c1\u540d\u6ee1\u5929\u4e0b\u7684\u7b2c\u4e00\u5251\u5ba2\u859b\u8863\u4eba\uff0c\u6b64\u523b\u5df2\u5230\u4ed6\u773c \u524d\u3002 \u3000\u3000\u53ea\u89c1\u8fd9\u8001\u4eba\u9762\u5bb9\u6e05\u77bb\uff0c\u5e03\u978b\u767d\u889c\uff0c\u7a7f\u7740\u4ef6\u84dd\u5e03\u957f\u886b\uff0c\u98ce\u91c7\u4e5f\u6ca1\u6709\u4ec0\u9ebd\u7279\u5f02\u5904\uff0c\u53ea\u4e0d\u8fc7\u4e00 \u53cc\u773c\u775b\u5374\u662f\u70af\u70af\u6709\u5149\uff0c\u4ee4\u4eba\u4e0d\u6562\u903c\u89c6\u3002 \u3000\u3000\u65bd\u5c11\u5976\u5976\u6b63\u5728\u5927\u58f0\u9053\uff1a\u201c\u8fd9\u4eba\u53eb\u53f6\u76db\u5170\uff0c\u8335\u5927\u59b9\u5b50\u5c31\u662f\u88ab\u4ed6\u5bb3\u6b7b\u7684\uff0c\u4ed6\u5c45\u7136\u8fd8\u6709\u8138\u6562\u64a4 \u91ce\uff0c\u8fde\u4f60\u8001\u4eba\u5bb6\u4ed6\u90fd\u4e0d\u77a7\u5728\u773c\u91cc\u3002\u201d \u3000\u3000\u82b1\u91d1\u5f13\u9053\uff1a\u201c\u636e\u8bf4\u8fd9\u4eba\u4e43\u662f\u4eac\u91cc\u7684\u4e00\u4e2a\u6d6a\u8361\u5b50\uff0c\u4ec0\u9ebd\u90fd\u4e0d\u4f1a\uff0c\u5c31\u4f1a\u5728\u5973\u4eba\u8eab\u4e0a\u4e0b\u529f\u592b\uff0c \u4e5f\u4e0d\u77e5\u5bb3\u8fc7\u591a\u5c11\u4eba\u4e86\u3002\u201d \u3000\u3000\u65bd\u5c11\u5976\u5976\u9053\uff1a\u201c\u4f60\u8001\u4eba\u5bb6\u5feb\u51fa\u624b\u6559\u8bad\u4ed6\u5427\u3002\u201d \u3000\u3000\u5979\u4eec\u5728\u8bf4\u4ec0\u9ebd\uff0c\u859b\u8863\u4eba\u4f3c\u4e4e\u4e5f\u5168\u672a\u542c\u5230\uff0c\u4ed6\u53ea\u662f\u77ac\u4e5f\u4e0d\u77ac\u5728\u51dd\u6ce8\u7740\u695a\u7559\u9999\u5ffd\u7136\u62b1\u4e86\u62b1 \u62f3\uff0c\u9053\uff1a\u201c\u5c0f\u5973\u65e0\u77e5\uff0c\u4f46\u671b\u9601\u4e0b\u6055\u7f6a\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u4e5f\u8eac\u8eab\u9053\uff1a\u201c\u859b\u5927\u4fa0\u8a00\u91cd\u4e86\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u8bf7\u5148\u7528\u8336\uff0c\u5c11\u65f6\u8001\u673d\u518d\u7f6e\u9152\u4e3a\u9601\u4e0b\u6d17\u5c18\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u591a\u8c22\u3002\u201d \u3000\u3000\u65bd\u5c11\u5976\u5976\u77a7\u5f97\u773c\u775b\u53d1\u76f4\uff0c\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u7239\uff0c\u4f60\u8001\u4eba\u5bb6\u4f55\u5fc5\u5bf9\u8fd9\u79cd\u4eba\u5ba2\u6c14\uff0c\u4ed6\u2026\u2026\u201d \u3000\u3000\u859b\u8863\u4eba\u5ffd\u7136\u6c89\u4e0b\u4e86\u8138\uff0c\u9053\uff1a\u201c\u4ed6\u600e\u6837\uff0c\u4ed6\u82e5\u4e0d\u770b\u5728\u4f60\u5e74\u5e7c\u65e0\u77e5\uff0c\u4f60\u8fd8\u53ef\u6d3b\u7740\u56de\u6765\u89c1\u6211 \u9ebd\uff1f\u201d \u3000\u3000\u65bd\u5c11\u5976\u5976\u6014\u4e86\u6014\uff0c\u4e5f\u4e0d\u77e5\u5979\u7239\u7239\u600e\u4f1a\u770b\u51fa\u5979\u4e0d\u662f\u4eba\u5bb6\u7684\u5bf9\u624b\u3002 \u3000\u3000\u82b1\u91d1\u5f13\u8d54\u7b11\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u2026\u2026\u201d \u3000\u3000\u859b\u8863\u4eba\u6c89\u58f0\u9053\uff1a\u201c\u4eb2\u5bb6\u6bcd\uff0c\u8001\u592b\u82e5\u662f\u4e24\u773c\u8fd8\u4e0d\u778e\uff0c\u53ef\u4ee5\u65ad\u8a00\u8fd9\u4f4d\u670b\u53cb\u7edd\u4e0d\u662f\u4eac\u57ce\u7684\u6d6a\u8361 \u5b50\u3002\u4e5f\u4e0d\u662f\u53f6\u76db\u5170\uff0c\u5426\u5219\u4ed6\u5c31\u4e0d\u4f1a\u6765\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8f6c\u5411\u695a\u7559\u9999\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u98ce\u91c7\u7167\u4eba\uff0c\u795e\u6c14\u5185\u655b\uff0c\u6c5f\u6e56\u4e2d\u867d\u662f\u4eba\u6750\u8f88\u51fa\uff0c\u66f4 \u80dc\u4ece\u524d\u3002\u4f46\u636e\u8001\u673d\u6240\u77e5\uff0c\u50cf\u9601\u4e0b\u8fd9\u6837\u7684\u5c11\u5e74\u82f1\u4fca\uff0c\u666e\u5929\u4e4b\u4e0b\u4e5f\u4e0d\u8fc7\u53ea\u6709\u4e8c\u4eba\u800c\u5df2\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u524d\u8f88\u8fc7\u5956\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u76ee\u5149\u95ea\u52a8\uff0c\u9053\uff1a\u201c\u636e\u95fb\u91d1\u575b\u7684\u2018\u8759\u8760\u516c\u5b50\u2019\u65e0\u8bba\u6b66\u529f\u4eba\u671b\uff0c\u4ff1\u5df2\u9690\u7136\u6709\u9886\u8896\u4e2d\u539f \u6b66\u6797\u4e4b\u52bf\uff0c\u4f46\u9601\u4e0b\u663e\u7136\u4e0d\u662f\u8759\u8760\u516c\u5b50\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u600e\u6562\u4e0e\u8759\u8760\u516c\u5b50\u76f8\u6bd4\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u4e5f\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u7684\u6b66\u529f\u4eba\u671b\uff0c\u6015\u8fd8\u5728\u8759\u8760\u516c\u5b50\u4e4b\u4e0a\uff0c\u82e5\u662f\u8001\u673d\u4f30\u8ba1\u4e0d\u9519\u9601 \u4e0b\u60f3\u5fc5\u5c31\u662f\u2026\u2026\u201d \u3000\u3000\u4ed6\u76ef\u7740\u695a\u7559\u9999\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u695a\u9999\u5e05\uff1f\u201d \u3000\u3000\u8fd9\u8001\u4eba\u4e00\u773c\u770b\u51fa\u4e86\u4ed6\u7684\u6765\u5386\uff0c\u695a\u7559\u9999\u6697\u4e2d\u4e5f\u89c9\u5403\u4e86\u4e00\u60ca\uff0c\u52a8\u5bb9\u9053\uff1a\u201c\u524d\u8f88\u5f53\u771f\u662f\u795e\u76ee\u5982 \u7535\uff0c\u665a\u8f88\u597d\u751f\u94a6\u4f69\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u634b\u987b\u800c\u7b11\uff0c\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u8001\u673d\u8fd9\u53cc\u773c\u775b\u6bd5\u7adf\u4e0d\u8ff7\uff0c\u8fd8\u662f\u8ba4\u5f97\u82f1\u96c4\u7684\u3002\u201d \u3000\u3000\u82b1\u91d1\u5f13\u548c\u65bd\u5c11\u5976\u5976\u9762\u5bb9\u5168\u90fd\u6539\u53d8\u4e86\uff0c\u5931\u58f0\u9053\uff1a\u201c\u4f60\u771f\u7684\u662f\u695a\u7559\u9999\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u5fae\u7b11\u7740\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u82b1\u91d1\u5f13\u773c\u775b\u53d1\u76f4\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u4e3a\u4f55\u4e0d\u65e9\u8bf4\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5728\u4e0b\u6628\u591c\u4fbf\u5df2\u8bf4\u4e86\u3002\u600e\u5948\u592b\u4eba\u4e0d\u80af\u76f8\u4fe1\u800c\u5df2\u3002\u201d \u3000\u3000\u82b1\u91d1\u5f13\u6014\u4e86\u534a\u54cd\uff0c\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u82e5\u975e\u53f6\u76db\u5170\uff0c\u4e3a\u4f55\u5230\u6211\u4eec\u90a3\u91cc\u53bb\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e45\u95fb\u592b\u4eba\u4e4b\u540d\uff0c\u7279\u53bb\u62dc\u8bbf\u3002\u201d \u3000\u3000\u82b1\u91d1\u5f13\u7b11\u4e86\uff0c\u8fde\u773c\u775b\u90fd\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u4f60\u603b\u7b97\u770b\u5f97\u8d77\u6211\uff0c\u6211\u5374\u597d\u50cf\u6709\u70b9\u5bf9\u4e0d\u8d77 \u4f60\u3002\u201d\u8fd9\u6837\u5427\uff0c\u660e\u5929\u665a\u4e0a\u6211\u8bf7\u4f60\u5403\u9c88\u9c7c\uff0c\u6211\u4eb2\u81ea\u4e0b\u53a8\uff0c\u53eb\u4f60\u770b\u770b\u6211\u7684\u624b\u827a\u662f\u4e0d\u662f\u6bd4\u5de6\u8001\u5934\u5b50 \u5dee\uff01\u4f60\u53ef\u5343\u4e07\u8981\u8d4f\u8138\u5440\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u592b\u4eba\u8d50\u600e\u6562\u8f9e\u3002\u201d \u3000\u3000\u65bd\u5c11\u5976\u5976\u5ffd\u53c8\u51b2\u4e86\u8fdb\u53bb\uff0c\u4e00\u9762\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u4f1a\u8c03\u7406\u9c7c\uff0c\u6211\u8fd9\u5c31\u4e0b\u53a8\u623f\u53bb\u3002\u201d \u3000\u3000\u82b1\u91d1\u5f13\u683c\u683c\u7b11\u9053\uff1a\u201c\u695a\u9999\u5e05\uff0c\u4f60\u53ef\u771f\u662f\u597d\u53e3\u798f\uff0c\u6211\u4eec\u5bb6\u7684\u5b97\u513f\u548c\u5979\u505a\u4e86\u597d\u51e0\u5e74\u592b\u59bb\uff0c\u90fd \u6ca1\u6709\u770b\u5230\u5979\u4e0b\u8fc7\u4e00\u6b21\u53a8\u623f\u8036\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u53ea\u6709\u88c5\u4f5c\u6ca1\u6709\u542c\u5230\uff0c\u54b3\u55fd\u51e0\u58f0\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4e45\u95fb\u9999\u5e05\u4e0d\u4f7f\u5251\uff0c\u4f46\u5929\u4e0b\u7684\u540d\u5251\u7ecf\u9999 \u5e05\u54c1\u9898\uff0c\u4fbf\u7acb\u523b\u8eab\u4ef7\u767e\u500d\uff0c\u8001\u673d\u5012\u4e5f\u6709\u51e0\u53e3\u5251\uff0c\u60f3\u8bf7\u9999\u5e05\u6cd5\u773c\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5927\u559c\u9053\uff1a\u201c\u56fa\u6240\u613f\u51fa\uff0c\u4e0d\u6562\u8bf7\u8033\u3002\u201d \u3000\u3000\u82b1\u91d1\u5f13\u7b11\u9053\uff1a\u201c\u4f60\u4eca\u5929\u975e\u4f46\u53e3\u798f\u4e0d\u5dee\uff0c\u773c\u775b\u66f4\u597d\uff0c\u6211\u4eec\u4eb2\u5bb6\u7fc1\u7684\u90a3\u51e0\u53e3\u5251\uff0c\u5e73\u65f6\u4ece\u6765\u4e5f \u4e0d\u7ed9\u4eba\u770b\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u6de1\u6de1\u9053\uff1a\u201c\u5251\u4e3a\u51f6\u5668\uff0c\u4eb2\u5bb6\u6bcd\u4eca\u5929\u8fd8\u662f\u83ab\u8981\u53bb\u770b\u7684\u597d\u3002\u201d \u7b2c\u56db\u7ae0\u3000\u5929\u4e0b\u7b2c\u4e00\u5251>> \u53e4\u9f99\u300a\u695a\u7559\u9999\u7cfb\u5217\xb7\u9b3c\u604b\u4f20\u5947\u300b \u7b2c\u56db\u7ae0\u3000\u5929\u4e0b\u7b2c\u4e00\u5251 \u3000\u3000\u859b\u5bb6\u5e84\u4e5f\u662f\u4f9d\u5c71\u800c\u5efa\u7684\uff0c\u9752\u8272\u7684\u5c71\u8109\uff0c\u873f\u8712\u4f38\u5c55\u5165\u5f8c\u5c71\uff0c\u6709\u65f6\u56ed\u4e2d\u7684\u96fe\u51e0\u4e4e\u5df2\u65f6\u548c\u5c71\u95f4 \u7684\u4e91\u96fe\u7ed3\u5728\u4e00\u8d77\u3002 \u3000\u3000\u4ed6\u4eec\u8e0f\u7740\u788e\u77f3\u5b50\u7684\u8def\uff0c\u7a7f\u8fc7\u5f8c\u56ed\uff0c\u56ed\u5b50\u91cc\u5e76\u6ca1\u6709\u9c9c\u8273\u7684\u82b1\u6728\uff0c\u4e00\u4ead\u4e00\u77f3\u90fd\u5bd3\u7740\u96c5\u81f4\u53e4\u5178 \u4e4b\u610f\u3002 \u3000\u3000\u695a\u7559\u9999\u548c\u859b\u8863\u4eba\u5e76\u80a9\u800c\u884c\uff0c\u8c01\u90fd\u6ca1\u6709\u8bf4\u8bdd\uff0c\u4e00\u4e2a\u4eba\u5230\u4e86\u67d0\u79cd\u5730\u4f4d\u65f6\uff0c\u5c31\u81ea\u7136\u4f1a\u53d8\u6210\u4e2a\u4e0d \u591a\u8bdd\u7684\u4eba\u3002 \u3000\u3000\u79cb\u5929\u7684\u65e9\u4e0a\u98ce\u5e76\u4e0d\u51b7\uff0c\u5929\u5374\u5f88\u9ad8\u4ed6\u4eec\u8d70\u4eba\u4e2a\u9752\u7fe0\u7684\u7af9\u6797\uff0c\u9732\u73e0\u51dd\u7ed3\u5728\u7af9\u53f6\u4e0a\uff0c\u5c31\u50cf\u662f\u9576 \u5d4c\u660e\u7fe0\u7684\u73cd\u73e0\u3002 \u3000\u3000\u7af9\u6797\u7684\u5c3d\u5934\u4fbf\u8fde\u7ed3\u7740\u5c71\u9e93\uff0c\u5df2\u88ab\u9752\u82d7\u67d3\u7f18\u7684\u5c71\u58c1\u4e0a\uff0c\u6709\u95f4\u53e4\u62d9\u7684\u5c0f\u5c4b\uff0c\u770b\u6765\u575a\u5b9e\u6c89\u91cd\u3002 \u3000\u3000\u859b\u8863\u4eba\u5f00\u4e86\u95e8\uff0c\u9053\uff1a\u201c\u9999\u5e05\u8bf7\uff0c\u8001\u592b\u5e26\u8def\u3002\u201d \u3000\u3000\u95e8\u5f8c\u662f\u6761\u957f\u800c\u9ed1\u6697\u7684\u77f3\u9053\uff0c\u5bd2\u6c14\u68ee\u68ee\uff0c\u8d2c\u4eba\u808c\u80a4\uff0c\u859b\u8863\u4eba\u7b49\u695a\u7559\u9999\u8d70\u8fdb\u6765\uff0c\u5c31\u7acb\u523b\u53c8\u5c06 \u95e8\u7d27\u7d27\u95ed\u4e0a\uff0c\u5c06\u5149\u660e\u548c\u6e29\u6696\u4e00\u8d77\u9694\u65ad\u5728\u95e8\u5916\uff0c\u56db\u4e0b\u9aa4\u7136\u6c89\u5bc2\u4e86\u8d77\u6765\uff0c\u8fde\u4e1d\u58f0\u97f3\u90fd\u542c\u4e0d\u5230\u3002 \u3000\u3000\u82e5\u662f\u8981\u6740\u4eba\uff0c\u8fd9\u7684\u786e\u662f\u597d\u5730\u65b9\u3002 \u3000\u3000\u4f46\u695a\u7559\u9999\u5374\u5e76\u6ca1\u6709\u4e1d\u6beb\u4e0d\u5b89\uff0c\u4ed6\u4f3c\u4e4e\u5bf9\u859b\u8863\u4eba\u4fe1\u4efb\uff0c\u859b\u8863\u4eba\u548c\u4ed6\u521d\u89c1\uff0c\u4fbf\u5c06\u4ed6\u5e26\u5230\u8fd9\u79d8 \u5bc6\u7684\u91cd\u5730\u4e2d\u6765\uff0c\u4ed6\u4f3c\u4e5f\u5e76\u4e0d\u89c9\u5f97\u5947\u602a\u3002 \u3000\u3000\u77f3\u9053\u8f6c\u51e0\u6298\uff0c\u4fbf\u5230\u4e86\u4e2a\u6d1e\u7a74\u3002 \u3000\u3000\u77f3\u58c1\u4e0a\u5d4c\u7740\u94dc\u706f\uff0c\u9634\u68ee\u68ee\u7684\u706f\u5149\u4e0b\uff0c\u53ea\u89c1\u6d1e\u7a74\u56db\u9762\u90fd\u6392\u7740\u77f3\u6848\uff0c\u6bcf\u5f20\u77f3\u6848\u4e0a\u90fd\u6709\u4e2a\u6e5b\u9ed1 \u7684\u94c1\u531d\u3002 \u3000\u3000\u8fce\u9762\u4e00\u5f20\u77f3\u6848\u4e0a\u7684\u94c1\u5323\u957f\u800c\u7a84\uff0c\u91cc\u9762\u88c5\u7684\u60f3\u5fc5\u5c31\u662f\u859b\u8863\u4eba\u89c6\u540c\u62f1\u74a7\u7684\u5251\u5668\uff0c\u4f46\u53e6\u4e00\u4e9b\u94c1 \u5323\u4e2d\u88c5\u7684\u662f\u4ec0\u9ebd\u5462\uff1f \u3000\u3000\u859b\u8863\u4eba\u63ba\u7740\u5251\u5323\uff0c\u4f3c\u4e4e\u5fd8\u4e86\u8eab\u65c1\u8fd8\u6709\u695a\u7559\u9999\u5b58\u5728\uff0c\u4ed6\u5168\u5fc3\u5168\u610f\u90fd\u5df2\u6eb6\u5165\u5251\u4e2d\uff0c\u5230\u4e86\u5fd8\u4eba \u5fd8\u6211\u7684\u5883\u754c\u3002 \u3000\u3000\u711a\u7559\u9999\u5ffd\u7136\u53d1\u73b0\u8fd9\u8001\u4eba\u7adf\u4f3c\u5b8c\u5168\u53d8\u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\u7b2c\u4e00\u773c\u770b\u5230\u4ed6\u65f6\uff0c\u53ea\u89c9\u5f97\u4ed6\u7684\u98ce\u5ea6\u4f18\u96c5\u800c\u4ece\u5bb9\uff0c\u5c31\u50cf\u662f\u4e2a\u4e0d\u6c42\u95fb\u8fbe\u7684\u667a\u8005\u4e5f\u50cf\u662f \u4e2a\u5df2\u538c\u5026\u7ea2\u5c18\uff0c\u9690\u9000\u6797\u4e0b\u7684\u540d\u4eba\uff0c\u795e\u60c5\u867d\u672a\u514d\u7a0d\u89c9\u51b7\u5389\uff0c\u4f46\u5374\u7edd\u6ca1\u6709\u9732\u51fa\u4ee4\u4eba\u4e0d\u5b89\u7684\u950b\u8292\u3002 \u3000\u3000\u695a\u7559\u9999\u65b9\u548c\u4ed6\u5e76\u80a9\u8d70\u8fc7\u8fd8\u4e0d\u5230\u53c1\u5c3a\u5bbd\u7684\u5c0f\u5f84\u4e0a\u4e5f\u6ca1\u6709\u89c9\u5f97\u4e1d\u6beb\u8b66\u5146\uff0c\u5c31\u4eff\u4f5b\u548c\u4e2a\u5e73\u51e1\u7684 \u8001\u4eba\u8d70\u5728\u4e00\u8d77\u3002 \u3000\u3000\u4f46\u73b0\u5728\uff0c\u5251\u8fd8\u672a\u51fa\uff0c\u695a\u7559\u9999\u5df1\u89c9\u5f97\u6709\u79cd\u901a\u4eba\u7684\u5251\u6c14\u900f\u4f53\u751f\u5bd2\uff0c\u8fd9\u5251\u6c14\u663e\u7136\u4e0d\u662f\u201c\u5251\u201d\u53d1 \u51fa\u6765\u7684\u3002 \u3000\u3000\u8fd9\u5251\u6c14\u5c31\u662f\u859b\u8863\u4eba\u672c\u8eab\u53d1\u51fa\u6765\u7684\u3002\u5728\u8fd9\u91cc\u4ed6\u5df2\u4e0d\u518d\u662f\u548c\u5973\u513f\u4eb2\u5bb6\u95f2\u8bdd\u5bb6\u5e38\u7684\u8001\u4eba\uff0c\u4e00\u8e0f \u5165\u8fd9\u9053\u95e8\uff0c\u4ed6\u5c31\u53c8\u53d8\u6210\u4e86\u6614\u65e5\u4f20\u95fb\u6c5f\u6e56\u5feb\u610f\u6069\u4ec7\u7684\u540d\u4fa0\u3002\u8fd9\u5730\u65b9\u85cf\u7684\u4e0d\u53ea\u662f\u5251\uff0c\u8fd8\u85cf\u7559\u4ed6\u6614 \u65e5\u7684\u56de\u5fc6\uff0c\u6240\u4ee5\u4ed6\u624d\u7edd\u4e0d\u5141\u8bb8\u4efb\u4f55\u4eba\u4fb5\u72af\u5230\u8fd9\u91cc\u6765\u3002 \u3000\u3000\u859b\u8863\u4eba\u7f13\u7f13\u5f00\u542f\u4e86\u94c1\u5323\uff0c\u53d6\u51fa\u4e86\u67c4\u5251\u3002 \u3000\u3000\u8fd9\u53e3\u5251\u5f62\u72b6\u53e4\uff0c\u9edd\u9ed1\u4e2d\u63aa\u7740\u58a8\u7eff\u7684\u5251\u8eab\uff0c\u5e76\u6ca1\u6709\u6444\u76ee\u7684\u5149\u8292\uff0c\u53ea\u4e0d\u8fc7\u695a\u7559\u9999\u8fdc\u5728\u516b\u5c3a \u5916\uff0c\u5df2\u89c9\u5f97\u5bd2\u6c14\u8d2c\u4eba\u808c\u80a4\u3002 \u3000\u3000\u201c\u545b\u201d\u7684\uff0c\u859b\u8863\u4eba\u4ee5\u6307\u5f39\u5251\uff0c\u5251\u4f5c\u9f99\u541f\u3002 \u3000\u3000\u695a\u7559\u9999\u8131\u53e3\u9053\uff1a\u201c\u597d\u5251\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u76ee\u5149\u95ea\u52a8\uff0c\u9053\uff1a\u201c\u9999\u5e05\u8ba4\u5f97\u8fd9\u53e3\u662f\u4ec0\u9ebd\u5251\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7f13\u7f13\u9053\uff1a\u201c\u6614\u65e5\u4e2d\u5174\u5468\u5ba4\u4e4b\u540d\u4e3b\u592a\u5eb7\u3001\u5c11\u5eb7\u7236\u5b50\uff0c\u96c6\u5929\u4e0b\u540d\u5320\uff0c\u94f8\u516b\u65b9\u4e4b\u94dc\uff0c\u5341 \u4e2d\u800c\u5f97\u4e00\u4f8b\uff0c\u4fbf\u662f\u90a3\u516b\u65b9\u94dc\u5251\u3002\u201d\u5ef7 \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u597d\uff0c\u597d\u773c\u529b\u3002\u201d \u3000\u3000\u4ed6\u867d\u5728\u5927\u58f0\u79f0\u8d5e\uff0c\u9762\u4e0a\u5374\u6beb\u65e0\u8868\u60c5\uff0c\u53c8\u53d6\u51fa\u53e3\u5251\u6765\u3002 \u3000\u3000\u8fd9\u53e3\u5251\u76ae\u8d3f\u534e\u7f8e\uff0c\u67c4\u4e0a\u5d4c\u7740\u677e\u7eff\u77f3\uff0c\u9576\u91d1\u4e1d\uff0c\u5251\u67c4\u4e0e\u5251\u8eab\u4e2d\u7684\u201c\u5f6a\u201d\uff0c\u867d\u4f3c\u9ec4\u91d1\u94f8\u6210\uff0c \u90fd\u4f5c\u7384\u94dc\u989d\u8272\u3002 \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u8fd9\u53e3\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u53e4\u6765\u96c4\u4e3b\uff0c\u7686\u6709\u540d\u5251\uff0c\u5c11\u5eb7\u94f8\u516b\u65b9\u94dc\u5251\uff0c\u989d\u989c\u6709\u2018\u753b\u5f71\u2019\u3001\u2018\u817e\u7a7a\u2019\uff0c\u592a \u7532\u6709\u5251\u540d\u2018\u6587\u5149\u2019\u3001\u6b66\u4e01\u6709\u5251\u540d\u2018\u7167\u80c6\u2026\u3002\u201d \u3000\u3000\u4ed6\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u53e3\u5251\u5c31\u662f\u2018\u7167\u80c6\u2019\uff0c\u4f46\u5251\u5323\u5374\u88ab\u5f8c\u4eba\u52a0\u4ee5\u88c5\u9970\u8fc7\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u597d\u597d\u773c\u529b\u201d \u3000\u3000\u4ed6\u51b7\u6f20\u7684\u9762\u4e0a\u5374\u4ecd\u4e0d\u52a8\u58f0\u8272\uff0c\u4f46\u76ee\u4e2d\u5df2\u6709\u4e9b\u8d5e\u8d4f\u4e4b\u610f\uff0c\u8fc7\u4e86\u534a\u664c\u53c8\u7f13\u7f13\u53d6\u51fa\u4e86\u4e00\u53e3\u5251 \u6765\u3002 \u3000\u3000\u8fd9\u53e3\u5251\u4e4c\u7f6e\u76ae\u69b4\uff0c\u7d2b\u94dc\u541e\u53e3\uff0c\u957f\u5251\u51fa\u9798\u624d\u534a\u5bf8\uff0c\u5df2\u6709\u79cd\u7070\u8499\u8499\u3001\u78a7\u68ee\u68ee\u7684\u5bd2\u5149\u6620\u5165\u7709 \u776b\u3002 \u3000\u3000\u859b\u8863\u4eba\u624b\u91cc\u6367\u7740\u8fd9\u53e3\u5251\uff0c\u773c\u775b\u91cc\u7684\u5149\u4eff\u4f5b\u66f4\u4eae\u4e86\u3002 \u3000\u3000\u4ed6\u51dd\u6ce8\u7740\u5251\u950b\uff0c\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u624d\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u9999\u5e05\u8bf7\u770b\u8fd9\u53e3\u5251\u662f\u4ec0\u9ebd\u5251\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u4e5f\u51dd\u6ce8\u7740\u5251\u950b\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u624d\u7f13\u7f13\u9053\uff1a\u201c\u8fd9\u662f\u53e3\u65e0\u540d\u4e4b\u5251\u3002\u201d \u3000\u3000\u85e4\u8863\u4eba\u9053\uff1a\u201c\u6b64\u8bdd\u600e\u8bb2\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5e72\u5c06\u83ab\u90a3\uff0c\u524d\u8f88\u53ef\u77e5\u9053\u9ebd\uff1f\u201d \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u5e72\u5c06\u83ab\u90aa\u4e0a\u53e4\u795e\u5175\uff0c\u8001\u673d\u867d\u672a\u5f97\u89c1\uff0c\u5374\u542c\u5230\u8fc7\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u5176\u5b9e\u2018\u5e72\u5c06\u83ab\u90aa\u2019\u53ea\u4e0d\u8fc7\u4e00\u53cc\u592b\u59bb\u7684\u540d\u5b57\uff0c\u4f46\u767e\u5e74\u4ee5\u5f8c\uff0c\u63d0\u8d77 \u2018\u5e72\u5c06\u83ab\u90aa\u2019\u56db\u4e2a\u5b57\uff0c\u5374\u53ea\u77e5\u6709\u5251\uff0c\u800c\u5c06\u5176\u4eba\u5fd8\u6000\u4e86\u3002\u201d \u3000\u3000\u4ed6\u4e0d\u7b49\u859b\u8863\u4eba\u8bf4\u8bdd\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u8d8a\u738b\u8058\u6b27\u51b6\u5b50\u94f8\u5251\u4e94\uff0c\u662f\u4e3a\u2018\u7eaf\u94a9\u2019\u3001\u2018\u6e5b\u5362\u2019\u3001 \u2018\u6beb\u66f9\u2019\u3001\u2018\u9c7c\u80a0\u2019\u3001\u2018\u5de8\u9619\u2019\uff0c\u695a\u738b\u547d\u98ce\u80e1\u5b50\u6c42\u5251\u5f97\u53c1\uff0c\u662f\u4e3a\u2018\u9f99\u6e0a\u2019\u3001\u2018\u592a\u963f\u3001\u2018\u5de5 \u5e03\u2019\uff0c\u5343\u8f7d\u4ee5\u6765\uff0c\u63d0\u8d77\u8fd9\u516b\u53e3\u5251\u6765\uff0c\u53ef\u8bf4\u65e0\u4eba\u4e0d\u77e5\uff0c\u4f46\u77e5\u9053\u6b27\u51b6\u5b50\u4e0e\u98ce\u80e1\u5b50\u662f\u8fd9\u4e24\u4f4d\u5927\u5e08\u7684 \u53c8\u6709\u51e0\u4eba\uff1f\u201d \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u9999\u5e05\u7684\u610f\u601d\u662f\u2026\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u8fd9\u53ea\u56e0\u4e3a\u4eba\u56e0\u5251\u540d\uff0c\u4eba\u7684\u5149\u8292\u5df2\u88ab\u5251\u7684\u5149\u8292\u6240\u63a9\u76d6\uff0c\u662f\u4ee5\u5f8c\u4eba\u4f46\u77e5\u6709\u6e5b \u5362\u3001\u5de8\u9619\uff0c\u800c\u4e0d\u77e5\u6709\u6b27\u51b6\u5b50\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b66\u6797\u4e2d\u8fd8\u8bb0\u5f97\u6b27\u51b6\u4e88\u7684\u4eba\u786e\u5b9e\u4e0d\u591a\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u524d\u8f88\u638c\u4e2d\u8fd9\u53e3\u5251\uff0c\u5251\u867d\u65e0\u540d\uff0c\u4f46\u80fd\u4f7f\u6b64\u5251\u7684\u5374\u5fc5\u975e\u5bfb\u5e38\u4eba\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u54e6\uff1f\u4f55\u4ee5\u89c1\u5f97\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u53ea\u56e0\u6b64\u5251\u950b\u8292\u6bd5\u9732\uff0c\u6740\u6c14\u903c\u4eba\uff0c\u82e5\u975e\u7edd\u4ee3\u9ad8\u624b\uff0c\u82e5\u65e0\u60ca\u4eba\u4e4b\u624b\u6bb5\uff0c\u66f4\u4e0d\u8db3 \u4ee5\u9a6d\u6b64\u5251\uff0c\u53ea\u6015\u53cd\u8981\u88ab\u5251\u4f24\u8eab\u3002\u201d \u3000\u3000\u4ed6\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u82e5\u662f\u5728\u4e0b\u4e24\u773c\u4e0d\u778e\uff0c\u8fd9\u53e3\u5251\u5fc5\u5b9a\u5c31\u662f\u524d\u8f88\u6614\u65e5\u7eb5\u6a2a\u6c5f\u6e56\u65f6\u6240\u4f69\u4e4b \u7269\u3002\u201d \u3000\u3000\u542c\u5230\u8fd9\u65f6\uff0c\u859b\u8863\u4eba\u624d\u4e3a\u4e4b\u8038\u7136\u52a8\u5bb9\u5931\u58f0\u9053\uff1a\u201c\u9999\u5e05\u5f53\u771f\u662f\u795e\u65e5\u5982\u7535\uff0c\u8001\u673d\u597d\u751f\u4f69\u670d\u3002\u201d \u3000\u3000\u8fd9\u756a\u8bdd\u4e5f\u6b63\u662f\u695a\u7559\u9999\u8d5e\u7f8e\u859b\u8863\u4eba\u7684\u8bdd\uff0c\u4e24\u4eba\u76f8\u89c6\u4e00\u7b11\uff0c\u5404\u4eba\u5fc3\u91cc\u90fd\u4e0d\u7981\u751f\u51fa\u51e0\u5206\u656c\u91cd\u76f8 \u60dc\u4e4b\u610f\u3002 \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u6c5f\u6e56\u4f20\u8a00\u7684\u786e\u4e0d\u865a\uff0c\u9999\u5e05\u7684\u89c1\u8bc6\u548c\u773c\u529b\u679c\u7136\u90fd\u975e\u540c\u5c0f\u53ef\uff0c\u4f46\u9999\u5e05\u53ef\u77e5\u9053\u56db \u58c1\u7684\u8fd9\u4e9b\u94c1\u5323\u91cc\u88c5\u7684\u662f\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u80fd\u4e0e\u540d\u5251\u4f5c\u4f34\uff0c\u5323\u4e2d\u5fc5\u975e\u5e38\u7269\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u6253\u5f00\u4e86\u4e2a\u94c1\u5323\uff0c\u5323\u5b50\u91cc\u5374\u53ea\u6709\u4ef6\u957f\u886b\u3002 \u3000\u3000\u96f7\u767d\u7684\u957f\u886b\uff0c\u5df2\u5fae\u5fae\u53d1\u9ec4\uff0c\u53ef\u89c1\u8d2e\u85cf\u7684\u5e74\u4ee3\u5df2\u6709\u4e0d\u5c11\u3002 \u3000\u3000\u859b\u8863\u4eba\u5c06\u957f\u886b\u4e00\u6296\uff0c\u695a\u7559\u9999\u624d\u53d1\u73b0\u957f\u886b\u7684\u524d\u80f8\u5904\u6709\u4e00\u4e32\u8840\u8ff9\uff0c\u5c31\u50cf\u662f\u6761\u8d64\u7ea2\u7684\u6bd2\u86c7\u822c\u873f \u8712\u5728\u90a3\u91cc\u3002 \u3000\u3000\u5728\u60e8\u6de1\u7684\u706f\u5149\u4e0b\u770b\u6765\uff0c\u8840\u8ff9\u5df2\u53d1\u9ed1\u4e86\u3002 \u3000\u3000\u859b\u8863\u4eba\u7f13\u7f13\u9053\uff1a\u201c\u9999\u5e05\u53ef\u77e5\u9053\u8fd9\u670d\u4e0a\u67d3\u7684\u662f\u8c01\u7684\u8840\uff1f\u201d \u3000\u3000\u4ed6\u773c\u775b\u867d\u5728\u76ef\u7740\u957f\u886b\u4e0a\u7684\u8840\u8ff9\uff0c\u5374\u53c8\u4f3c\u4e4e\u5728\u671b\u7740\u5f88\u8fdc\u5f88\u8fdc\u7684\u5730\u65b9\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u6de1\u6de1 \u7b11\uff0c\u63a5\u9053\uff1a\u201c\u8fd9\u5df2\u662f\u5f88\u4e45\u4ee5\u524d\u7684\u4e8b\u4e86\uff0c\u9999\u5e05\u53ea\u6015\u5e76\u672a\u542c\u5230\u8fc7\u8fd9\u4eba\u7684\u540d\u5b57\uff0c\u4f46\u53c1\u5341\u5e74\u524d\uff0c\u2018\u6740 \u624b\u65e0\u5e38\u2019\u88f4\u73af\u5374\u4e5f\u975e\u7b49\u95f2\u4eba\u7269\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u8083\u7136\u9053\uff1a\u201c\u665a\u8f88\u867d\u5e74\u8f7b\u8bc6\u6d45\uff0c\u5374\u4e5f\u77e5\u9053\u2018\u6740\u624b\u65e0\u5e38\u2019\u624b\u4e2d\u4e00\u53cc\u65e0\u5e38\u94a9\u6253\u904d\u5357\u4e03 \u7701\uff0c\u5374\u4e0d\u77e5\u6b64\u4eba\u5df2\u6b7b\u5728\u524d\u8f88\u624b\u4e0a\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u90a3\u662f\u5728\u52fe\u6f0f\u5c71\u3002\u2026\u201d \u3000\u3000\u4ed6\u795e\u601d\u4f3c\u5df2\u56de\u5230\u9065\u8fdc\u7684\u5f80\u65e5\uff0c\u7f13\u7f13\u7684\u53d9\u8bf4\u7740\u3002 \u3000\u3000\u695a\u7559\u9999\u773c\u524d\u4eff\u4f5b\u5df2\u5c55\u51fa\u4e00\u5e45\u8083\u6740\u82cd\u51c9\u7684\u56fe\u753b\u3002\u74dc \u3000\u3000\u8d1d\u6f0f\u5c71\uff0c\u66ae\u8272\u82cd\u832b\uff0c\u897f\u5929\u5982\u8840\u3002 \u3000\u3000\u859b\u8863\u4eba\u767d\u8863\u5982\u80c4\uff0c\u72ec\u7acb\u5728\u5bd2\u98ce\u4e2d\uff0c\u5c71\u5d16\u4e0a\uff0c\u671b\u7740\u9762\u8c8c\u72f0\u72de\u7684\u201c\u6740\u624b\u65e0\u5e38\u201d\u7f13\u7f13\u8d70\u4e86\u8fc7 \u6765\u3002 \u3000\u3000\u7136\u5f8c\u5251\u5149\u4e00\u95ea\u3002 \u3000\u3000\u9c9c\u8840\u6e85\u5728\u96ea\u2014\u822c\u7684\u8863\u670d\u4e0a\uff0c\u5b9b\u5982\u5728\u96ea\u5730\u4e0a\u6d12\u843d\u4e00\u4e32\u6885\u82b1\u2026\u3002 \u3000\u3000\u859b\u8863\u4eba\u7f13\u7f13\u9053\uff1a\u201c\u5982\u4eca\u53c1\u5341\u5e74\u7684\u5c81\u6708\u7ecf\u5df2\u6d88\u901d\uff0c\u4f46\u4ed6\u4eec\u7684\u8840\u5374\u662f\u6c38\u8fdc\u4e0d\u4f1a\u6d88\u5931\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4ed6\u4eec\u7684\u8840\uff1f\u96be\u9053\u8fd9\u4e9b\u94c1\u531d\u53ca\u2026\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u51b7\u51b7\u9053\uff1a\u201c\u9999\u5e05\u96be\u9053\u4e0d\u660e\u767d\u8840\u8863\u4eba\u8fd9\u53c1\u5b57\u662f\u5982\u4f55\u6765\u7684\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u671b\u7740\u56db\u9762\u77f3\u6848\u4e0a\u7684\u94c1\u5323\uff0c\u60f3\u5230\u6bcf\u4e2a\u94c1\u5323\u91cc\u90fd\u85cf\u7740\u4e00\u4ef6\u96ea\u767d\u7684\u957f\u886b\uff0c\u6bcf\u4ef6\u957f\u886b\u4e0a\u90fd \u67d3\u7740\u4e00\u4e2a\u4eba\u7684\u9c9c\u8840\uff0c\u6bcf\u6ef4\u9c9c\u8840\u4e2d\u90fd\u5305\u542b\u7740\u4e00\u4e2a\u4ee4\u4eba\u6177\u6168\u6fc0\u6602\u7684\u6545\u4e8b\uff0c\u6bcf\u4e2a\u6545\u4e8b\u4e2d\u90fd\u5fc5\u6709\u573a\u60ca \u5fc3\u52a8\u9b44\u7684\u8840\u6218\u2026\u3002\u201d \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u695a\u7559\u9999\u5fc3\u5e95\u4e5f\u4e0d\u7981\u6cdb\u8d77\u4e00\u9635\u5bd2\u610f\u3002 \u3000\u3000\u859b\u8863\u4eba\u76ee\u5149\u5982\u5200\uff0c\u4e00\u5b57\u4e00\u5b57\u9053\uff1a\u201c\u4eba\u4e0d\u72af\u6211\uff0c\u6211\u4e0d\u72af\u4eba\uff0c\u4eba\u82e5\u72af\u6211\uff0c\u5251\u4e0b\u65e0\u60c5\uff0c\u5c31\u662f\u8fd9 \u67c4\u5251\uff0c\u4e0d\u77e5\u996e\u4e0b\u4e86\u591a\u5c11\u4eba\u7684\u9c9c\u8840\u3002\u201d \u3000\u3000\u4ed6\u5251\u5149\u4e00\u95ea\uff0c\u5ffd\u7136\u95ea\u7535\u6bbf\u5411\u695a\u7559\u9999\u523a\u4e86\u51fa\u6765\u3002 \u3000\u3000\u89c1\u5230\u4e2d\u539f\u70b9\u7ea2\u65f6\uff0c\u695a\u7559\u9999\u5df2\u89c9\u5f97\u4ed6\u5251\u6cd5\u4e4b\u5feb\uff0c\u5f53\u4e16\u65e0\u53cc\uff0c\u89c1\u5230\u5e05\u4e00\u5e06\u65f6\uff0c\u695a\u7559\u9999\u5c31\u89c9\u5f97 \u4e00\u70b9\u7ea2\u8fd8\u4e0d\u7b97\u662f\u5929\u4e0b\u7b2c\u4e00\u5feb\u5251\uff0c\u89c1\u5230\u90a3\u201c\u767d\u75f4\u201d\u65f6\uff0c\u695a\u7559\u9999\u53c8\u89c9\u5f97\u5e05\u4e00\u5e06\u7684\u5251\u6cd5\u4e0d\u7b97\u4ec0\u9ebd \u4e86\u3002 \u3000\u3000\u4f46\u6b64\u523b\uff0c\u695a\u7559\u9999\u624d\u7ec8\u65bc\u77e5\u9053\u4ec0\u9ebd\u662f\u771f\u6b63\u7684\u201c\u5feb\u5251\u201d\u2026\u3002 \u3000\u3000\u859b\u8863\u4eba\u8fd9\u4e00\u5251\u523a\u6765\u7adf\u6765\u5f97\u5b8c\u5168\u65e0\u5f71\u65e0\u8e2a\u8c01\u4e5f\u770b\u4e0d\u51fa\u4ed6\u8fd9\u4e00\u5251\u662f\u5982\u4f55\u51fa\u624b\uff0c\u662f\u4ece\u54ea\u91cc\u523a\u8fc7 \u6765\u7684\u3002 \u3000\u3000\u695a\u7559\u9999\u5c45\u7136\u6839\u672c\u6ca1\u6709\u95ea\u907f\u3002 \u3000\u3000\u4f46\u8fd9\u5feb\u5982\u95ea\u7535\u822c\u7684\u96f7\u76df\u7684\u4e00\u5251\uff0c\u5230\u4e86\u695a\u7559\u9999\u54bd\u5589\u524d\u534a\u5bf8\u5904\uff0c\u5c31\u5ffd\u7136\u505c\u987f\u4e86\uff0c\u505c\u65f6\u5c31\u50cf\u53d1 \u65f6\u540c\u6837\u5feb\uff0c\u540c\u6837\u7a81\u7136\uff0c\u540c\u6837\u4ee4\u4eba\u4e0d\u53ef\u6349\u6a21\uff0c\u4e0d\u53ef\u601d\u8bae\uff0c\u8fd9\u201c\u4e00\u505c\u201d\u5b9e\u6bd4\u201c\u4e00\u53d1\u201d\u66f4\u4ee4\u695a\u7559\u9999 \u60ca\u8bb6\u3002 \u3000\u3000\u859b\u8863\u4eba\u53d1\u8fd9\u4e00\u5251\u65f6\u663e\u7136\u8fd8\u672a\u5c3d\u5168\u529b\u5426\u5219\u5c31\u505c\u4e0d\u4e0b\u6765\u4e86\uff0c\u4ed6\u672a\u4f7f\u5168\u529b\u65f6\u523a\u51fa\u7684\u4e00\u5207\u5df2\u662f\u5982 \u6b64\u6025\u8feb\uff0c\u4f7f\u51fa\u5168\u529b\u6765\u90a3\u8fd8\u5f97\u4e86\u3002 \u3000\u3000\u859b\u8863\u4eba\u671b\u7740\u695a\u7559\u9999\uff0c\u4f3c\u4e4e\u4e5f\u6709\u4e9b\u60ca\u5f02\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u5230\u4e86\u4ed6\u54bd\u5589\u65f6\uff0c\u4ed6\u975e\u4f46\u795e\u8272\u4e0d\u53d8\uff0c\u800c\u4e14\u8fde\u773c\u90fd\u672a\u7728\uff0c\u8fd9\u5e74\u8f7b\u4eba\u5df2\u6709\u4e86\u201c\u6cf0\u5c71\u5d29\u65bc \u524d\u800c\u8272\u4e0d\u53d8\uff0c\u7cdc\u9e7f\u5174\u65bc\u5de6\u800c\u76ee\u4e0d\u77ac\u201d\u7684\u5b9a\u529b\uff0c\u5355\u53ea\u8fd9\u4efd\u5b9a\u529b\u53c8\u9690\u7136\u6709\u4e00\u4ee3\u5b97\u4e3b\u7684\u6c14\u9b44\u3002 \u3000\u3000\u5251\u5c16\u867d\u8fd8\u672a\u523a\u5165\u695a\u7559\u9999\u7684\u54bd\u5589\uff0c\u4f46\u68ee\u51b7\u7684\u5251\u6c14\u5374\u5df2\u523a\u4eba\u4ed6\u7684\u808c\u80a4\uff0c\u4ed6\u5589\u5934\u7684\u76ae\u80a4\u4e0a\u867d\u5df2 \u8d77\u4e86\u9897\u9897\u5bd2\u6817\uff0c\u9762\u4e0a\u5374\u4f9d\u7136\u672a\u52a8\u58f0\u8272\uff0c\u5bf9\u695a\u7559\u9999\u8bf4\u6765\uff0c\u88ab\u4eba\u7528\u5251\u5c16\u62b5\u7740\u54bd\u5589\uff0c\u8fd9\u5df2\u4e0d\u662f\u7b2c\u4e00 \u6b21\u8d9f\u3002 \u3000\u3000\u867d\u7136\u4ed6\u4e5f\u77e5\u9053\u8fd9\u4e00\u6b21\u7684\u5251\u6bd4\u4ee5\u524d\u4efb\u4f55\u4e00\u6b21\u90fd\u8981\u5feb\u5f97\u591a\uff0c\u8fd9\u9ebd\u5feb\u7684\u5251\u82e5\u5df2\u5230\u4e86\u54bd\u5589\u524d\uff0c\u4e16 \u4e0a\u5c31\u6ca1\u6709\u4eba\u80fd\u95ea\u907f\u5f00\u4e86\uff0c\u859b\u8863\u4eba\u51b7\u51b7\u7684\u671b\u7740\u4ed6\u8fc7\u4e86\u5f88\u4e45\u624d\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u4f60\u53ef\u662f\u4e3a\u4e86\u6211\u7684\u5251\u800c \u6765\u7684\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u6211\u60f3\u6765\u5077\u4f60\u7684\u5251\uff1f\u201d \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u695a\u9999\u5e05\u7684\u540d\u58f0\uff0c\u6211\u65e9\u5df2\u4e45\u4ef0\u5f97\u5f88\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u90a3\u9ebd\u4f60\u5c31\u8be5\u77e5\u9053\u4ed6\u4ece\u672a\u5728\u670b\u53cb\u8eab\u4e0a\u6253\u8fc7\u4e3b\u610f\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u65e0\u8bba\u4efb\u4f55\u4e8b\u90fd\u6709\u4f8b\u5916\u7684\uff0c\u4e5f\u8bb8\u4f60\u8fd9\u6b21\u5c31\u662f\u4f8b\u5916\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u8fd9\u6b21\u6211\u4e3a\u4f55\u8981\u4f8b\u5916\uff1f\u201d \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u4f60\u5bf9\u5251\u4e0d\u4f46\u5f88\u6709\u5b66\u95ee\uff0c\u4e5f\u5f88\u6709\u5174\u8da3\uff0c\u662f\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u53c8\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u5bf9\u5251\u5f88\u6709\u5174\u8da3\uff0c\u6211\u5bf9\u7ea2\u70e7\u8089\u4e5f\u5f88\u6709\u5174\u8da3\uff0c\u4f46\u6211\u5374\u4ece\u672a\u60f3 \u8fc7\u5077\u6761\u732a\u56de\u5bb6\u53bb\u517b\u7740\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u5389\u58f0\u9053\uff1a\u201c\u90a3\u9ebd\u5c14\u662f\u4e3a\u4f55\u800c\u6765\u7684\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u6de1\u6de1\u9053\uff1a\u201c\u6709\u4eba\u7528\u5251\u5bf9\u7740\u7684\u6211\u7684\u8116\u5b50\u65f6\uff0c\u6211\u901a\u5e38\u90fd\u4e0d\u559c\u6b22\u987e\u4ed6\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u4f60\u559c\u6b22\u6211\u628a\u5251\u523a\u4e0b\u53bb\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u5927\u7b11\u9053\uff1a\u201c\u859b\u8863\u4eba\u82e5\u662f\u4f1a\u523a\u51b7\u5251\u7684\u4eba\uff0c\u90a3\u9ebd\u6211\u5c31\u771f\u770b\u9519\u4f60\u4e86\uff0c\u6211\u82e5\u770b\u9519\u4e86\u4f60\uff0c\u5c31 \u7b97\u6b7b\u5728\u4f60\u7684\u624b\u4e0a\u53ea\u80fd\u6028\u6211\u76ee\u5df2\u6709\u773c\u65e0\u73e0\uff0c\u4e00\u70b9\u4e5f\u4e0d\u51a4\u6789\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u51dd\u6ce8\u4e86\u4ed6\u5f88\u4e45\uff0c\u7ee5\u7f13\u9053\uff1a\u201c\u4f60\u4ece\u6765\u6ca1\u6709\u770b\u9519\u8fc7\u4eba\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u5fae\u7b11\u9053\uff1a\u201c\u6211\u82e5\u80af\u8ba9\u4ed6\u624b\u91cc\u62ff\u7740\u5251\uff0c\u7ad9\u5728\u6211\u8eab\u65c1\uff0c\u5c31\u7edd\u4e0d\u4f1a\u770b\u9519\u4ed6\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u4ef0\u9762\u5927\u7b11\u9053\uff1a\u201c\u597d\u695a\u7559\u9999\u679c\u7136\u6d51\u8eab\u662f\u80c6\uff0c\u679c\u7136\u540d\u4e0d\u865a\u4f20\u3002\u201d \u3000\u3000\u201c\u545b\u201d\u7684\u4e00\u58f0\uff0c\u5251\u5df2\u5165\u9798\u3002 \u3000\u3000\u859b\u8863\u4eba\u5fae\u7b11\u9053\uff1a\u201c\u4f46\u82e5\u8bf4\u695a\u7559\u9999\u662f\u4e3a\u4e86\u82b1\u91d1\u5f13\u624d\u5230\u65bd\u5bb6\u5e84\u6765\u7684\uff0c\u6211\u65e0\u8bba\u5982\u4f55\u662f\u4e0d\u4f1a\u76f8\u4fe1 \u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u8fde\u6211\u81ea\u5df1\u90fd\u4e0d\u76f8\u4fe1\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u7b11\u5bb9\u53c8\u9010\u6e10\u6d88\u5931\u9053\uff1a\u201c\u9999\u5e05\u5230\u65bd\u5bb6\u53bb\uff0c\u83ab\u975e\u5c31\u662f\u4e3a\u4e86\u8981\u53eb\u82b1\u91d1\u5f13\u5e26\u4f60\u6765\u89c1\u6211\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u859b\u5927\u4fa0\u65e2\u5df2\u9000\u9690\u6797\u6cc9\uff0c\u5728\u4e0b\u8981\u89c1\u975e\u5e38\u4e4b\u4eba\uff0c\u53ea\u6709\u7528\u975e\u5e38\u7684\u624b\u6bb5\u4e86\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u76ee\u5149\u95fb\u52a8\u9053\uff1a\u201c\u4f60\u4e3a\u4f55\u5982\u6b64\u6025\u7740\u89c1\u6211\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u6c89\u541f\u4e86\u534a\u664c\uff0c\u9053\uff1a\u201c\u5927\u7ea6\u53c1\u56db\u5e74\u4ee5\u524d\u6c5f\u6e56\u4e2d\u5ffd\u7136\u51fa\u73b0\u4e86\u4e00\u7fa4\u804c\u4e1a\u523a\u5ba2\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u8038\u7136\u9053\uff1a\u201c\u804c\u4e1a\u523a\u5ba2\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u4e9b\u4eba\u4e0d\u8fa8\u662f\u975e\uff0c\u4e0d\u5206\u5584\u6076\uff0c\u53ea\u4ee5\u6740\u4eba\u4e3a\u4e1a\uff0c\u65e0\u8bba\u8c01\u53ea\u8981\u51fa\u5f97\u8d77\u4ef7 \u94b1\uff0c\u4ed6\u4eec\u5c31\u4f1a\u4e3a\u4ed6\u6740\u4eba\u3002\u201d \u3000\u3000\u4ed6\u53f9\u4e86\u53e3\u6c14\uff0c\u63a5\u9053\uff1a\u201c\u4ed6\u4eec\u65e0\u8bba\u4ec0\u9ebd\u4eba\u90fd\u6740\uff0c\u9ed1\u9053\u7684\u4ed6\u4eec\u4e5f\u6740\uff0c\u767d\u9053\u4ed6\u4eec\u4e5f\u6740\u3002\u5c31\u7b97\u90a3 \u4e9b\u4e0e\u6b66\u6797\u6beb\u65e0\u5173\u8fde\u7684\u4eba\u4ed6\u4eec\u90fd\u6740\uff0c\u5c31\u56e0\u4e3a\u5982\u6b64\uff0c\u6240\u4ee5\u6211\u8ba4\u4e3a\u4ed6\u4eec\u5b9e\u5728\u6bd4\u90a3\u4e9b\u6740\u4eba\u653e\u706b\u7684\u5f3a\u76d7 \u8fd8\u8981\u53ef\u6068\uff0c\u8fd8\u8981\u53ef\u6015\uff0c\u56e0\u4e3a\u5f3a\u76d7\u6740\u4eba\u81f3\u5c11\u8fd8\u8981\u9009\u62e9\u9009\u62e9\u5bf9\u8c61\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u52a8\u5bb9\u9053\uff1a\u201c\u6c5f\u6e56\u4e2d\u51fa\u4e86\u8fd9\u79cd\u4eba\uff0c\u6211\u600e\u9ebd\u8fde\u4e00\u70b9\u98ce\u58f0\u90fd\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u884c\u4e8b\u5f88\u9690\u79d8\uff0c\u82e5\u975e\u4ed6\u4eec\u627e\u5230\u6211\u5934\u4e0a\u6765\uff0c\u6211\u4e5f\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u82e5\u662f\u7b97\u8ba1\u5230\u9999\u5e05\u8eab\u4e0a\uff0c\u53ea\u6015\u5df2\u79bb\u672b\u65e5\u4e0d\u8fdc\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u73b0\u5728\u7684\u786e\u5df2\u6b7b\u7684\u6b7b\uff0c\u4f24\u7684\u4f24\uff0c\u4e0d\u590d\u518d\u80fd\u4e3a\u6076\uff0c\u53ea\u4e0d\u8fc7\u2026\u2026\u8fd9\u4e9b\u4eba\u7684 \u9996\u9886\u5374\u81f3\u4eca\u4ecd\u9053\u9065\u6cd5\u5916\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u4ed6\u4eec\u7684\u9996\u9886\u662f\u8c01\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u81f3\u4eca\u8fd8\u4e0d\u77e5\u9053\u6b64\u4eba\u662f\u8c01\uff0c\u53ea\u77e5\u4ed6\u975e\u4f46\u673a\u667a\u8fc7\u4eba\uff0c\u800c\u4e14\u5251\u6cd5\u7edd\u9ad8\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201d\u6240\u4ee5\u9999\u5e05\u5c31\u6000\u7591\u8fd9\u4eba\u5c31\u662f\u6211\uff1f\u201d\u74dc \u3000\u3000\u695a\u7559\u9999\u4e5f\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u82e5\u975e\u5982\u6b64\uff0c\u6211\u4e5f\u4e0d\u4f1a\u5230\u8fd9\u91cc\u6765\u4e86\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u76ee\u5149\u707c\u707c\u3002\u9053\uff1a\u201c\u9999\u5e05\u5982\u4eca\u5df2\u67e5\u51fa\u6765\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7f13\u7f13\u9053\uff1a\u201c\u9601\u4e0b\u65b9\u90a3\u4e00\u5251\u51fa\u624b\uff0c\u7684\u786e\u548c\u4ed6\u4eec\u6709\u4e03\u5206\u76f8\u4f3c\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u6c89\u58f0\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u8ba4\u4e3a\u6211\u5c31\u662f\u90a3\u523a\u5ba2\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u5fae\u7b11\u9053\uff1a\u201c\u9601\u4e0b\u82e5\u662f\u90a3\u523a\u5ba2\u7684\u9996\u9886\uff0c\u65b9\u90a3\u4e00\u5251\u5c31\u4e0d\u4f1a\u6536\u56de\u53bb\u4e86\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u4ec0\u9ebd\u4e5f\u6ca1\u6709\u8bf4\uff0c\u7f13\u7f13\u8f6c\u8fc7\u8eab\u3002\u5c06\u957f\u5251\u85cf\u5165\u77f3\u5323\uff0c\u53ea\u89c1\u4ed6\u80a9\u5934\u8d77\u4f0f\uff0c\u5fc3\u60c5\u4f3c\u4e4e\u5f88\u6fc0 \u52a8\uff0c\u8fc7\u4e86\u5f88\u4e45\u3002\u624d\u7f13\u7f13\u95ee\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u6211\u4e3a\u4f55\u81f3\u4eca\u8fd8\u672a\u6740\u6b7b\u5de6\u8f7b\u4faf\uff1f\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u95ee\u4e86\u8fd9\u53e5\u8bdd\u6765\uff0c\u695a\u7559\u9999\u4e0d\u7981\u6014\u4e86\u6014\u3002 \u3000\u3000\u5e78\u597d\u859b\u8863\u4eba\u4e5f\u5e76\u6ca1\u6709\u7b49\u4ed6\u56de\u7b54\u53c8\u9053\uff1a\u201c\u53ea\u56e0\u6211\u8fd9\u4e00\u751f\u975e\u4f46\u5f88\u5c11\u6709\u670b\u53cb\uff0c\u8fde\u4ec7\u4eba\u90fd\u4e0d\u591a\uff0c \u5c24\u5176\u662f\u50cf\u5de6\u8f7b\u4faf\u90a3\u6837\u7684\u4ec7\u4eba\uff0c\u6211\u82e5\u6740\u4e86\u4ed6\uff0c\u5c31\u66f4\u5bc2\u5bde\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u770b\u4e0d\u5230\u4ed6\u7684\u8138\uff0c\u4f46\u671b\u7740\u4ed6\u524a\u5efa\u7684\u80cc\u5f71\uff0c\u671b\u7740\u4ed6\u957f\u767d\u7684\u5934\u53d1\uff0c\u5fc3\u91cc\u4e5f\u4e0d\u7981\u6cdb\u8d77\u4e00\u9635 \u51c4\u51c9\u4e4b\u610f\uff0c\u957f\u53f9\u9053\uff1a\u201c\u53e4\u6765\u82f1\u96c4\u591a\u5bc2\u5bde\u2026\u3002\u4e00\u4e2a\u4eba\u5728\u4f4e\u5904\u65f6\uff0c\u603b\u60f3\u5f80\u9ad8\u5904\u8d70\uff0c\u4f46\u8d70\u5f97\u8d8a\u9ad8\u3002 \u8ddf\u4e0a\u53bb\u7684\u4eba\u5c31\u8d8a\u5c11\uff0c\u7b49\u4ed6\u53d1\u73b0\u9ad8\u5904\u53ea\u5269\u4e0b\u4ed6\u4e2a\u4eba\u65f6\uff0c\u518d\u60f3\u56de\u5934\u5df2\u6765\u4e0d\u53ca\u4e86\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u6807\u67aa\u822c\u633a\u7acb\u7740\u7684\u8eab\u5b50\uff0c\u5ffd\u7136\u50cf\u662f\u53d8\u5f97\u6709\u4e9b\u4f97\u5d5d\uff0c\u4ed6\u53c8\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u624d\u957f\u53f9\u4e86\u58f0\uff0c \u9053\uff1a\u201c\u4f46\u6211\u5df2\u6e10\u6e10\u8001\u4e86\uff0c\u4e00\u4e2a\u4eba\u5230\u4e86\u5feb\u6b7b\u7684\u65f6\u5019\uff0c\u603b\u60f3\u5c06\u8eab\u524d\u7684\u5e10\u7ed3\u7ed3\u6e05\uff0c\u4e5f\u514d\u5f97\u6b7b\u5f8c\u5e26\u8fdb \u68fa\u6750\u53bb\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6c89\u9ed8\u7740\uff0c\u56e0\u4e3a\u4ed6\u4e0d\u77e5\u8be5\u8bf4\u4ec0\u9ebd\u3002 \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u6240\u4ee5\u6211\u548c\u5de6\u8f7b\u4faf\u5df2\u7ea6\u5b9a\uff0c\u5728\u4eca\u5e74\u9664\u5915\u4f5c\u751f\u6b7b\u7684\u51b3\u6597\uff0c\u90a3\u4e0d\u5355\u662f\u6211\u548c\u4ed6\u4e24\u4eba \u7684\u51b3\u6597\uff0c\u4e5f\u662f\u6211\u4eec\u859b\u5de6\u4e24\u5bb6\u7684\u51b3\u6597\uff0c\u56e0\u4e3a\u6211\u4eec\u4e24\u5bb6\u662f\u767e\u5e74\u7684\u4e16\u4ec7\u4ec7\u6068\u51e0\u4e4e\u5df2\u4e45\u8fdc\u5f97\u4ee4\u4eba\u8fde\u7ed3 \u4ec7\u7684\u539f\u56e0\u90fd\u5fd8\u8bb0\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u8038\u7136\u52a8\u5bb9\uff0c\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u8f7b\u4faf\u4e3a\u4f55\u6ca1\u6709\u544a\u8bc9\u6211\uff1f\u201d \u3000\u3000\u4ed6\u5fc3\u91cc\u5df2\u604d\u7136\u660e\u767d\u5de6\u8f7b\u4faf\u4e3a\u4f55\u6025\u7740\u8981\u5c06\u5973\u513f\u5ac1\u5230\u4e01\u5bb6\u53bb\u4e86\uff0c\u53ea\u56e0\u5973\u513f\u4e00\u79bb\u53bb\uff0c\u5c31\u4e0d\u518d\u662f \u5de6\u5bb6\u4eba\uff0c\u8c05\u4e0d\u5fc5\u518d\u53c2\u4e0e\u8fd9\u573a\u51b3\u751f\u6b7b\u7684\u8840\u6218\u3002\u53cb\u8f7b\u4faf\u4e3a\u5973\u513f\u7684\u82e6\u5fc3\uff0c\u5b9e\u5728\u662f\u65e0\u5fae\u4e0d\u81f3\u3002 \u3000\u3000\u859b\u8863\u4eba\u970d\u7136\u8f6c\u8fc7\u8eab\uff0c\u51dd\u6ce8\u7740\u695a\u7559\u9999\uff0c\u9053\uff1a\u201c\u4f46\u6211\u4ee5\u4e3a\u4ed6\u5df2\u544a\u8bc9\u4e86\u4f60\uff0c\u4ee5\u4e3a\u4f60\u5c31\u662f\u4e3a\u4e86\u8981 \u52a9\u62f3\u624d\u5230\u677e\u6c5f\u5e9c\u6765\u7684\u3002\u6240\u4ee5\u5148\u8981\u8bbe\u6cd5\u6765\u63a2\u542c\u6211\u7684\u865a\u5b9e\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6240\u4ee5\u624d\u8981\u8bbe\u6cd5\u6765\u5077\u4f60\u7684\u5251\uff0c\u4e00\u4e2a\u4eba\u8981\u548c\u8001\u864e\u640f\u6597\u6700\u597d\u5148\u62d4\u6323\u4ed6\u7684\u7259\u9f7f\u3002\u201d \u3000\u3000\u4ed6\u7b11\u4e86\u7b11\u8c08\u6de1\u9053\uff1a\u201c\u4f46\u695a\u7559\u9999\u5c31\u7b97\u662f\u8fd9\u6837\u7684\u4eba\u3002\u5de6\u8f7b\u4faf\u5374\u7edd\u4e0d\u4f1a\u662f\u8fd9\u6837\u7684\u4eba\uff0c\u5426\u5219\u5c31\u4e0d \u914d\u505a\u859b\u8863\u4eba\u7684\u5bf9\u5934\u4e86\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u695a\u7559\u9999\u82e5\u662f\u8fd9\u79cd\u4eba\uff0c\u90a3\u9ebd\u6211\u5c31\u7b97\u770b\u9519\u4f60\u4e86\uff0c\u90a3\u4e5f\u53ea\u602a\u6211\u81ea\u5df1\u6709\u773c\u65e0\u73e0\u602a\u4e0d \u5f97\u522b\u4eba\uff0c\u662f\u9ebd\uff1f\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u6b63\u662f\u695a\u7559\u9999\u65b9\u5bf9\u4ed6\u8bf4\u7684\u3002\u695a\u7559\u9999\u671b\u7740\u4ed6\u51b7\u6f20\u7684\u9762\u5bb9\u4e2d\u5fc3\u91cc\u5ffd\u7136\u6cdb\u8d77\u4e00\u9635\u6e29\u6696\u4e4b \u5fc3\uff0c\u53ea\u56e0\u4ed6\u5df2\u53d1\u73b0\u8fd9\u8001\u4eba\u5176\u5b9e\u5e76\u4e0d\u50cf\u5916\u8868\u770b\u6765\u90a3\u9ebd\u51b7\u9177\u3002 \u3000\u3000\u4ed6\u6697\u4e2d\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u7684\u9664\u5915\u51b3\u6597\u96be\u9053\u5df2\u52bf\u5728\u5fc5\u884c\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u859b\u8863\u4eba\u9ed8\u4e86\u534a\u664c\u5ffd\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6b64\u65f6\u9c7c\u60f3\u5fc5\u5df2\u70e7\u597d\u4e86\uff0c\u6211\u4eec\u4e3a\u4f55\u4e0d\u5148\u559d\u676f\u518d\u8bf4\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u5e76\u4e0d\u662f\u80e1\u94c1\u82b1\u90a3\u6837\u7684\u9152\u9b3c\uff0c\u4ed6\u767d\u5929\u4e00\u5411\u5f88\u5c11\u559d\u9152\u7684\uff0c\u53ea\u6709\u5fc3\u60c5\u7279\u522b\u9ad8\u5174\u6216\u8005\u7279\u522b \u60b2\u4f24\u65f6\u624d\u4f1a\u4f8b\u5916\u3002 \u3000\u3000\u4eca\u5929\u4e5f\u5c31\u662f\u4f8b\u5916\u3002\u4f46\u4ed6\u5374\u4e0d\u77e5\u9053\u4eca\u5929\u662f\u7279\u522b\u9ad8\u5174\uff0c\u8fd8\u662f\u7279\u522b\u96be\u8fc7\uff0c\u4ed6\u5fc3\u91cc\u6709\u5f88\u591a\u4e8b\uff0c\u800c \u4e14\u5f88\u590d\u6742\uff0c\u4ed6\u8981\u627e\u4e2a\u65f6\u5019\u597d\u597d\u60f3\u6e05\u695a\u3002\u5728\u6ca1\u6709\u60f3\u6e05\u695a\u4e4b\u524d\uff0c\u4ed6\u51b3\u5b9a\u4ec0\u9ebd\u4e8b\u4e5f\u4e0d\u505a\u3002 \u3000\u3000\u9a74\u9c7c\u70e7\u5f97\u7684\u786e\u4e0d\u5dee\uff0c\u53ea\u4e0d\u8fc7\u695a\u7559\u9999\u5374\u6000\u7591\u9c7c\u4e0d\u662f\u90a3\u4f4d\u65bd\u5c11\u5976\u5976\u505a\u7684\uff0c\u56e0\u4e3a\u5979\u624b\u4e0a\u8fde\u4e00\u70b9 \u6cb9\u817b\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u695a\u7559\u9999\u89c1\u8fc7\u5f88\u591a\u4e0d\u4f1a\u70e7\u83dc\u7684\u6587\u4eba\uff0c\u5374\u504f\u559c\u6b22\u8eb2\u5728\u53a8\u623f\uff0c\u7136\u5f8c\u518d\u5c06\u83dc\u7aef\u51fa\u6765\uff0c\u786c\u8bf4\uff1a\u201c\u83b1 \u70e7\u5f97\u4e0d\u597d\uff0c\u8bf7\u539f\u8c05\u3002\u201d \u3000\u3000\u8ba9\u522b\u4eba\u4ee5\u4e3a\u83dc\u5c31\u662f\u5979\u70e7\u7684\uff0c\u56e0\u4e3a\u5c31\u8fde\u8fd9\u79cd\u5973\u4eba\u4e5f\u77e5\u70e7\u83dc\u4e0d\u4f46\u662f\u505a\u59bb\u5b50\u7684\u5149\u8363\uff0c\u4e5f\u662f\u5979\u6587 \u592b\u7684\u5149\u8363\u3002 \u3000\u3000\u695a\u7559\u9999\u603b\u89c9\u5f97\u8fd9\u79cd\u4eba\u5f88\u53ef\u7b11\uff0c\u603b\u60f3\u95ee\u95ee\u5979\u4eec\uff0c\u201c\u4f60\u65e2\u7136\u89c9\u5f97\u4e0d\u4f1a\u70e7\u83dc\u5f88\u4e22\u4eba\uff0c\u4ee5\u524d\u4e3a\u4f55 \u4e0d\u5b66\u5b66\u5462\uff1f\u201d \u3000\u3000\u65bd\u5c11\u5976\u5976\u679c\u7136\u5df2\u5a07\u7b11\u9053\uff1a\u201c\u70e7\u5f97\u6015\u4e0d\u597d\u9999\u5e05\u4f60\u83ab\u8981\u89c1\u7b11\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u8fd8\u672a\u8bf4\u8bdd\uff0c\u859b\u8863\u4eba\u5df2\u6de1\u6de1\u9053\uff1a\u201c\u4f60\u6839\u672c\u8fde\u7092\u86cb\u90fd\u4e0d\u4f1a\uff0c\u8fd9\u6761\u9c7c\u4e5f\u4e0d\u662f\u4f60\u70e7\u7684 \u2014\u3002\u201d \u3000\u3000\u4ed6\u8bdd\u672a\u8bf4\u5b8c\uff0c\u65bd\u5c11\u5976\u5976\u5df2\u7ea2\u7740\u8138\u6e9c\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u82b1\u91d1\u5f13\u5403\u5403\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4eb2\u5bb6\u7fc1\u4e5f\u4f1a\u8bf4\u8bdd\uff0c\u60f3\u5fc5\u662f\u56e0\u4e3a\u89c1\u4e86\u9999\u5e05\u5fc3\u60c5\u624d\u7279\u522b\u597d\uff0c\u8fd9\u5e94 \u8be5\u8c22\u8c22\u6211\u624d\u662f\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201d\u4e0d\u9519\uff0c\u7b49\u65bd\u4e3e\u4eba\u6765\u4e86\uff0c\u6211\u5b9a\u656c\u4ed6\u4e00\u676f\u3002\u201d \u3000\u3000\u82b1\u91d1\u5f13\u6014\u4e86\u6014\uff0c\u52c9\u5f3a\u7b11\u9042\uff1a\u201c\u9999\u5e05\u5728\u8fd9\u91cc\u5750\uff0c\u6211\u5230\u5f8c\u9762\u627e\u4eb2\u5bb6\u6bcd\u804a\u5929\u53bb\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u7b49\u5979\u8d70\u4e86\uff0c\u624d\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5979\u603b\u7b97\u542c\u61c2\u4e86\u6211\u7684\u8bdd\uff0c\u603b\u7b97\u77e5\u9053\u81ea\u5df2\u8be5\u5230\u4ec0\u9ebd\u5730\u65b9 \u53bb\u4e86\uff0c\u8fd9\u5012\u4e0d\u5bb9\u6613\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u7684\u786e\u4e0d\u5bb9\u6613\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u4e3e\u676f\u9053\uff1a\u201c\u82e5\u4e0d\u628a\u5973\u4eba\u8d76\u8d70\uff0c\u7537\u4eba\u600e\u80fd\u5b89\u5fc3\u559d\u9152\uff0c\u6765\u559d\u4e00\u676f\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u996e\u800c\u5c3d\uff0c\u5ffd\u7136\u957f\u53f9\u9053\uff1a\u201c\u82e5\u975e\u859b\u5de6\u4e24\u5bb6\u7684\u4e16\u4ec7\uff0c\u4f60\u548c\u5de6\u8f7b\u4faf\u4e00\u5b9a\u4f1a\u4ea4\u6210\u597d\u670b\u53cb \u7684\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u8138\u8272\u53d8\u4e86\u53d8\uff0c\u9053\uff1a\u201c\u4f60\u672c\u662f\u5de6\u8f7b\u4faf\u7684\u670b\u53cb\uff0c\u5982\u4eca\u4e5f\u5df2\u662f\u6211\u7684\u670b\u53cb\uff0c\u6211\u53ea\u671b\u4f60\u660e\u767d \u4ef6\u4e8b\u2026\u2026\u859b\u5de6\u4e24\u5bb6\u4ec7\u6068\uff0c\u662f\u8c01\u4e5f\u5316\u89e3\u4e0d\u5f00\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u859b\u8863\u4eba\u53f9\u58f0\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u8fd9\u4e00\u767e\u5e74\u6765\uff0c\u859b\u5bb6\u5df2\u6709\u591a\u5c11\u4eba\u6b7b\u5728\u5de6\u5bb6\u4eba\u624b\u4e0a\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u662f\u5426\u548c\u5de6\u5bb6\u4eba\u6b7b\u5728\u859b\u5bb6\u4eba\u624b\u4e0a\u7684\u5dee\u4e0d\u591a\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u6b63\u662f\u5982\u6b64\uff0c\u4e5f\u6b63\u56e0\u5982\u6b64\uff0c\u662f\u4ee5\u859b\u5de6\u4e24\u5bb6\u7684\u4ec7\u6068\u624d\u8d8a\u9677\u8d8a\u6df1\uff0c\u9664\u975e\u8fd9\u4e24\u5bb6\u4eba \u4e2d\u6709\u4e00\u5bb6\u6b7b\u5c3d\u6b7b\u7edd\u5426\u5219\u8fd9\u4ec7\u6068\u8c01\u4e5f\u4f11\u60f3\u5316\u89e3\u5f97\u5f00\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53ea\u542c\u5f97\u5fc3\u91cc\u53d1\u51b7\uff0c\u6b63\u4e0d\u77e5\u8be5\u8bf4\u4ec0\u9ebd\u3002 \u3000\u3000\u7a81\u542c\u4eba\u5927\u58f0\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u4eec\u6709\u597d\u9152\u597d\u83dc\uff0c\u4e5f\u4e0d\u53eb\u6211\u6765\u5403\u3002\u201d \u3000\u3000\u4e00\u4e2a\u4eba\u6a2a\u51b2\u76f4\u95ef\u7684\u8d70\u4e86\u8fdb\u6765\uff0c\u5374\u6b63\u662f\u90a3\u201c\u767d\u75f4\u201d\u859b\u5b9d\u5b9d\uff0c\u4ed6\u4eca\u5929\u7a7f\u7684\u4e00\u5957\u7ea2\u8863\u670d\uff0c\u4e0a\u9762 \u7adf\u7ee3\u7740\u53ea\u7eff\u4e4c\u9f9f\u3002 \u3000\u3000\u695a\u7559\u9999\u53d1\u73b0\u4ed6\u597d\u50cf\u5df2\u5168\u4e0d\u8ba4\u5f97\u81ea\u5df1\u3001\u4e00\u5750\u4e0b\u6765\u5c31\u5c06\u6574\u76d8\u9c7c\u642c\u5230\u9762\u524d\u7528\u624b\u63d0\u8d77\u6765\u5c31\u5403\u3002 \u3000\u3000\u859b\u8863\u4eba\u76b1\u4e86\u7709\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u820d\u5f1f\u7b11\u4eba\uff0c\u4ed6\u2026\u2026\u4ed6\u2026\u4e00\u3002\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u6ee1\u5634\u90fd\u662f\u9c7c\uff0c\u4e00\u9762\u5410\u523a\u4e00\u9762\u7b11\u9053\uff1a\u201c\u859b\u8863\u4eba\u662f\u5927\u5251\u5ba2\uff0c\u859b\u7b11\u4eba\u5374\u662f\u5927\u5403\u5ba2\uff0c\u859b\u7b11 \u4eba\u867d\u7136\u4ece\u5c0f\u6253\u4e0d\u8fc7\u859b\u8863\u4eba\u4f46\u5403\u8d77\u6765\u859b\u8863\u4eba\u5374\u8981\u843d\u8352\u800c\u9003\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u6012\u9053\uff1a\u201c\u8c01\u53eb\u4f60\u6765\u7684\uff1f\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u7b11\u563b\u563b\u9053\uff1a\u201c\u8fd9\u4e5f\u662f\u6211\u7684\u5bb6\uff0c\u6211\u4e3a\u4f55\u4e0d\u80fd\u6765\uff0c\u4f60\u53ef\u4ee5\u9a82\u6211\u7b28\uff0c\u9a82\u6211\u6ca1\u51fa\u606f\uff0c\u603b\u4e0d \u80fd\u8bf4\u6211\u4e0d\u662f\u859b\u8001\u7239\u7684\u513f\u5b50\u5427\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u6447\u7740\u5934\u9053\uff1a\u201c\u9999\u5e05\u83ab\u89c1\u7b11\uff0c\u4ed6\u672c\u6765\u4e0d\u662f\u8fd9\u6837\u5b50\u7684\uff0c\u76f4\u5230\u4e03\u516b\u5e74\u524d\uff0c \u4e5f\u4e0d\u77e5\u9053\u4e3a\u4e86\u4ec0\u9ebd\uff0c\u7adf\u5ffd\u7136\u3002\u2026\u2014\u5ffd\u7136\u53d8\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5fc3\u91cc\u6697\u6697\u53f9\u606f\u3002\u201c\u5bb6\u5bb6\u90fd\u6709\u672c\u96be\u5ff5\u7684\u7ecf\u201d\uff0c\u8fd9\u4e00\u4ee3\u540d\u4fa0\uff0c\u5176\u5b9e\u4e5f\u548c\u666e\u901a\u4eba\u4e00\u6837\uff0c \u4e5f\u6709\u4ed6\u7684\u70e6\u607c\u548c\u4e0d\u5e78\uff0c\u53ea\u4e0d\u8fc7\u8fd9\u4e9b\u4e8b\u90fd\u5df2\u88ab\u4ed6\u8000\u76ee\u7684\u5149\u8f89\u6240\u63a9\uff0c\u4eba\u4eec\u53ea\u80fd\u770b\u5230\u4ed6\u7684\u5149\u5f69\uff0c\u5374 \u5fd8\u4e86\u6709\u5149\u7684\u5730\u65b9\u5fc5\u6709\u9634\u5f71\u3002 \u3000\u3000\u695a\u7559\u9999\u7684\u672c\u610f\u786e\u5b9e\u662f\u4e3a\u4e86\u8981\u63a2\u67e5\u90a3\u523a\u5ba2\u96c6\u56e2\u7684\u795e\u79d8\u9996\u9886\u800c\u6765\u7684\uff0c\u4f46\u73b0\u5728\u4ed6\u4e3b\u8981\u7684\u76ee\u7684\u5374 \u6539\u53d8\u4e86\u3002 \u3000\u3000\u5de6\u8f7b\u4faf\u662f\u4ed6\u7684\u597d\u670b\u53cb\uff0c\u4ed6\u5b9a\u4e3a\u5de6\u8f7b\u4faf\u89e3\u51b3\u8fd9\u95ee\u9898\uff0c\u4f55\u51b5\uff0c\u201c\u501f\u5c38\u8fd8\u9b42\u201d\u8fd9\u4ef6\u4e8b\u5b9e\u5728\u592a\u4e0d \u53ef\u601d\u8bae\uff0c\u4ed6\u81ea\u5df2\u4e5f\u60f3\u5c06\u8fd9\u4ef6\u4e8b\u5f04\u660e\u767d\uff0c\u5230\u201c\u859b\u5bb6\u201d\u6765\u4e4b\u524d\uff0c\u4ed6\u672c\u6709\u8bb8\u591a\u8bdd\u8981\u5bf9\u859b\u8863\u4eba\u8bf4\u3002 \u3000\u3000\u53ef\u662f\u73b0\u5728\u4ed6\u5ffd\u53c8\u6539\u53d8\u4e86\u4e3b\u610f\uff0c\u4ed6\u5ffd\u7136\u53d1\u73b0\u8fd9\u4ef6\u4e8b\u5176\u4e2d\u6709\u8bb8\u591a\u503c\u5f97\u7814\u7a76\u4e4b\u5904\uff0c\u6240\u4ee5\u4ed6\u51b3\u5b9a \u6682\u65f6\u4ec0\u9ebd\u90fd\u4e0d\u8bf4\u3002 \u3000\u3000\u859b\u8863\u4eba\u5e76\u6ca1\u6709\u575a\u6301\u633d\u7559\u4ed6\uff0c\u53ea\u548c\u4ed6\u8ba2\u4e0b\u4e86\u5f8c\u4f1a\u4e4b\u671f\uff0c\u7136\u5f8c\u4eb2\u81ea\u9001\u4ed6\u5230\u95e8\u53e3\uff0c\u76ee\u9001\u7740\u4ed6\u8fdc \u53bb\u3002 \u3000\u3000\u859b\u5b9d\u5b9d\u5374\u8eb2\u5728\u95e8\u5f8c\u5403\u5403\u7684\u7b11\u3002 \u3000\u3000\u695a\u65e5\u9999\u6ca1\u6709\u4e58\u8f66\uff0c\u4e5f\u6ca1\u6709\u9a91\u9a6c\uff0c\u4ed6\u4e00\u76f4\u8ba4\u4e3a\u8d70\u8def\u7684\u65f6\u5019\u5934\u8111\u5f80\u5f80\u4f1a\u53d8\u5f97\u5f88\u6e05\u695a\uff0c\u56e0\u4e3a\u8d70 \u8def\u53ef\u4ee5\u4f7f\u8840\u6db2\u4e0b\u964d\uff0c\u8840\u6db2\u4e0b\u964d\u4e86\uff0c\u5934\u8111\u81ea\u7136\u5c31\u4f1a\u51b7\u9759\u3002\u800c\u4ed6\u73b0\u5728\u6700\u9700\u8981\u7684\u5c31\u662f\u4e2a\u51b7\u9759\u7684\u5934 \u8111\u3002 \u3000\u3000\u4f46\u4ed6\u7a76\u7adf\u53d1\u73b0\u4e86\u4ec0\u9ebd\uff1f\u7a76\u7adf\u60f3\u4ec0\u9ebd\u5462\uff1f \u3000\u3000\u79cb\u5929\u7684\u592a\u9633\u7167\u5728\u4eba\u8eab\u4e0a\uff0c\u8f7b\u67d4\u6e29\u6696\u5f97\u5c31\u50cf\u662f\u60c5\u4eba\u7684\u624b\u4ee4\u4eba\u89c9\u5f97\u8bf4\u4e0d\u51fa\u7684\u8212\u670d\uff0c\u79cb\u5929\uff0c\u6b63 \u662f\u9002\u65bc\u8d70\u8def\u7684\u65f6\u5019\u3002 \u3000\u3000\u53ef\u662f\uff0c\u8fd8\u6ca1\u6709\u8d70\u51fa\u591a\u8fdc\u695a\u7559\u9999\u5c31\u53d1\u73b0\u5f8c\u9762\u6709\u4e2a\u4eba\u4e0d\u5373\u4e0d\u79bb\u7684\u76ef\u7740\u4ed6\uff0c\u8fd9\u4eba\u9a91\u7740\u5339\u9ed1\u6cb9\u6cb9 \u9a74\u5b50\uff0c\u5934\u4e0a\u6234\u7740\u9876\u53c8\u5bbd\u53c8\u5927\u7684\u5e3d\u5b50\uff0c\u800c\u4e14\u4e00\u76f4\u4f4e\u5782\u7740\u5934\uff0c\u4f3c\u4e4e\u751f\u6015\u522b\u4eba\u77a7\u89c1\u4ed6\u7684\u8138\u3002 \u3000\u3000\u695a\u7559\u9999\u6839\u672c\u5c31\u6ca1\u6709\u56de\u987e\u77a7\u4ed6\u4e00\u773c\u3002\u50cf\u662f\u4e0d\u77e5\u9053\u5f8c\u9762\u6709\u4eba\uff0c\u8fd9\u4eba\u7684\u80c6\u5b50\u5c31\u8d8a\u6765\u8d8a\u5927\u4e86\u8d70\u5f97 \u8d8a\u6765\u8d8a\u8fd1\u3002 \u3000\u3000\u695a\u7559\u9999\u6697\u89c9\u5f97\u597d\u7b11\uff0c\u8fd9\u4eba\u60f3\u5fc5\u662f\u4e2a\u521d\u51fa\u6c5f\u6e56\u7684\u65b0\u624b\u5426\u5219\u4ed6\u600e\u4f1a\u6709\u8fd9\u9ebd\u5927\u7684\u80c6\u5b50\u6765\u76ef\u695a\u7559 \u9999\u7684\u7a0d\uff1f \u3000\u3000\u5c06\u8fd1\u6b63\u5348\u7684\u65f6\u5019\uff0c\u695a\u7559\u9999\u5c31\u5230\u4e86\u79c0\u91ce\u6865\u3002 \u3000\u3000\u6865\u4e0a\u6709\u4e2a\u9752\u8863\u5987\u4eba\u6b63\u95ea\u95ea\u7f29\u7f29\u7684\u5411\u897f\u5934\u773a\u671b\uff0c\u5979\u5934\u4e0a\u5305\u7740\u5e03\u5e15\uff0c\u7528\u4e24\u53ea\u624b\u7d27\u7d27\u6293\u4f4f\uff0c\u663e \u7136\u4e5f\u751f\u6015\u88ab\u4eba\u77a7\u89c1\u9762\u76ee\u3002\u4f46\u695a\u7559\u9999\u662f\u4e00\u773c\u5c31\u77a7\u51fa\u5979\u662f\u8c01\u4e86\u3002 \u3000\u3000\u90a3\u9a91\u7740\u9ed1\u9a74\u5b50\u7684\u4eba\u770b\u89c1\u695a\u7559\u9999\u8d70\u4e0a\u6865\uff0c\u5c31\u8eb2\u5728\u4e00\u68f5\u6811\u5f8c\uff0c\u5374\u9732\u51fa\u4e86\u534a\u8fb9\u8138\u4e00\u53ea\u773c\u775b\uff0c\u5c06 \u5e3d\u5b50\u968f\u624b\u6458\u4e86\u4e0b\u6765\u3002\u4ed6\u597d\u50cf\u4ee5\u4e3a\u53ea\u6709\u81ea\u5df2\u6709\u773c\u775b\uff0c\u522b\u4eba\u90fd\u778e\u5b50\u3002 \u3000\u3000\u695a\u7559\u9999\u5374\u597d\u50cf\u771f\u7684\u5ffd\u7136\u53d8\u6210\u778e\u5b50\u4e86\u3002 \u3000\u3000\u6865\u4e0a\u7684\u9752\u519c\u5987\u4eba\u81ea\u7136\u5c31\u662f\u6881\u5988\uff0c\u5979\u2014\u5f20\u82cd\u8001\u7684\u8138\u4e5f\u4e0d\u77e5\u662f\u56e0\u4e3a\u88ab\u98ce\u5439\u7684\uff0c\u8fd8\u662f\u9a87\u6015\u53d1\u4e86 \u9752\u3002 \u3000\u3000\u770b\u5230\u695a\u7559\u9999\uff0c\u5979\u5c31\u5306\u5306\u8d76\u8fc7\u6765\uff0c\u5598\u606f\u7740\u9053\uff1a\u201c\u8c22\u5929\u8c22\u5730\uff0c\u4f60\u603b\u7b97\u6765\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u6211\u9a97\u4f60\uff1f\u4ee5\u4e3a\u6211\u4e0d\u4f1a\u6765\uff1f\u201d \u3000\u3000\u6881\u5988\u53ee\u5631\u7740\u9053\uff1a\u201c\u4f46\u4f60\u771f\u6709\u6cd5\u5b50\u80fd\u8ba9\u6211\u518d\u89c1\u5230\u5c0f\u59d0\u9ebd\uff1f\u53ea\u8981\u80fd\u89c1\u5c0f\u59d0\u4e00\u9762\uff0c\u6211\u3002\u3002\u3002\u6211 \u6b7b\u4e86\u4e5f\u7518\u5fc3\u3002\u201d"}),showOnHomepage:!0},{name:"\u7b2c\u4e94\u7ae0\u3000\u523a\u5ba2",content:(0,s.jsx)(s.Fragment,{children:"\u6881\u5988\u671b\u7740\u695a\u7559\u9999\uff0c\u4e0d\u80dc\u4f01\u76fc\u7684\u9053\uff1a\u201c\u4f60\u771f\u80fd\u591f\u8ba9\u6211\u89c1\u5230\u5c0f\u59d0\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u82e5\u6709\u8bda\u5fc3\uff0c\u81ea\u7136\u770b\u5f97\u5230\u5979\u3002\u201d \u3000\u3000\u6881\u5988\u9053\uff1a\u201c\u6211\u5f53\u7136\u8bda\u5fc3\uff0c\u89c2\u97f3\u83e9\u8428\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u4e0d\u8ba9\u5979\u8bf4\u5b8c\u8fd9\u53e5\u8bdd\uff0c\u5c31\u62a2\u7740\u9053\uff1a\u201c\u597d\uff0c\u90a3\u9ebd\u4f60\u53c1\u5929\u5f8c\u518d\u6765\uff0c\u83ab\u8981\u5728\u6b63\u5348\u7b49\u5230\u5929\u9ed1 \u4e86\u518d\u6765\u3002\u201d \u3000\u3000\u6881\u5988\u6014\u4e86\u6014\u9053\uff1a\u201c\u53c1\u5929\u8fd8\u8981\u518d\u8fc7\u53c1\u5929\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u6b63\u8272\u9053\uff1a\u201c\u8fd9\u79cd\u4e8b\u81ea\u7136\u8981\u9009\u65e5\u5b50\u3002\u6025\u4e0d\u5f97\u7684\uff0c\u4f60\u82e5\u771f\u6709\u8bda\u5fc3\uff0c\u8fde\u53c1\u5929\u90fd\u7b49\u4e0d \u5f97\u3002\u201d \u3000\u3000\u6881\u5988\u81ea\u7136\u5f88\u5bb9\u6613\u5c31\u88ab\u6253\u53d1\u8d70\u4e86\uff0c\u695a\u7559\u9999\u867d\u89c9\u5f97\u5bf9\u5584\u826f\u7684\u8001\u592a\u5a46\u6709\u4e9b\u62b1\u6b49\uff0c\u4f46\u8fd9\u53c1\u5929\u7684\u65f6 \u95f4\u5173\u7cfb\u5374\u5b9e\u5728\u592a\u5927\u3002 \u3000\u3000\u997c\u4e86\u53c1\u5929\u5f8c\uff0c\u6240\u6709\u7684\u4e8b\u4e5f\u8bb8\u5c31\u4f1a\u90fd\u6539\u89c2\u4e86\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u8e44\u58f0\u9aa4\u54cd\u3002 \u3000\u3000\u90a3\u9a91\u91cc\u9ed1\u9a74\u5b50\u7684\u4eba\u5ffd\u7136\u52a0\u901f\u6025\u9a70\u800c\u6765\uff0c\u8feb\u5230\u695a\u7559\u9999\u8eab\u5f8c\uff0c\u7a81\u5730\u53cd\u624b\u4e00\u97ad\uff0c\u5411\u695a\u7559\u9999\u7684\u8116 \u5b50\u62bd\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u957f\u97ad\u7834\u7a7a\uff0c\u5212\u8d77\u4e86\u5c16\u9510\u7684\u98ce\u58f0\u3002 \u3000\u3000\u695a\u7559\u9999\u5934\u4e5f\u672a\u56de\uff0c\u4e00\u4f38\u624b\u3002\u5c31\u6362\u4f4d\u4e86\u97ad\u7a0d\uff0c\u7b11\u665a\u9053\uff1a\u201c\u4e0b\u6765\u5427\u3002\u201d \u3000\u3000\u4ed6\u968f\u624b\u6296\uff0c\u90a3\u4eba\u8eab\u5b50\u5c31\u81ea\u978d\u4e0a\u98de\u8d77\uff0c\u51cc\u7a7a\u4e00\u4e2a\u7ffb\u8eab\uff0c\u505c\u5728\u6768\u67f3\u7554\uff0c\u5934\u4e0a\u7684\u906e\u9633\u5dfe\u4e5f\u6254\u6389 \u4e86\uff0c\u9732\u51fa\u4e86\u4e00\u5f20\u957f\u7684\u9a6c\u8138\u3002 \u3000\u3000\u8fd9\u5c45\u7136\u662f\u65bd\u5c11\u5976\u5976\u3002 \u3000\u3000\u9ed1\u9a74\u5b50\u76f4\u51b2\u5230\u6865\u5934\u624d\u505c\u4e86\u4e0b\u6765\uff0c\u7528\u9888\u5b50\u78e8\u7740\u6865\uff0c\u58f0\u58f0\u8f7b\u5636\u3002\u90a3\u795e\u60c5\u5012\u6709\u51e0\u5206\u548c\u65bd\u5c11\u5976\u5976 \u76f8\u4f3c\u3002 \u3000\u3000\u695a\u7559\u9999\u5fae\u7b11\u9053\uff1a\u201c\u4e0d\u77e5\u662f\u5c11\u5976\u5976\u9a7e\u5230\u9669\u4e9b\u5c31\u5f97\u7f6a\u4e86\u3002\u8fd8\u8bf7\u6055\u7f6a\u3002\u201d \u3000\u3000\u65bd\u5c11\u5976\u5976\u6068\u6068\u76ef\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u4f60\u5c11\u8bf4\u98ce\u51c9\u8bdd\uff0c\u6211\u95ee\u4f60\u4e00\u5929\u5230\u665a\u9b3c\u9b3c\u795f\u795f\u7684\u7a76\u7adf\u5728\u5e72\u4e9b\u4ec0 \u9ebd\uff1f\u4f60\u7a76\u7adf\u6253\u6211\u4ec0\u9ebd\u4e3b\u610f\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u6709\u5929\u5927\u7684\u80c6\u5b50\uff0c\u4e5f\u4e0d\u592a\u6253\u5c11\u5976\u5976\u4f60\u7684\u4e3b\u610f\u5440\u3002\u201d \u3000\u3000\u65bd\u5c11\u5976\u5976\u7684\u8138\u5c45\u7136\u4e5f\u7ea2\u4e86\uff0c\u5927\u58f0\u9053\uff1a\u201c\u90a3\u9ebd\uff0c\u4f60\u5c06\u6881\u5988\u627e\u6765\u5e72\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4ec0\u9ebd\u4e5f\u6ca1\u6709\uff0c\u53ea\u4e0d\u8fc7\u804a\u804a\u5929\u800c\u5df2\u3002\u201d \u3000\u3000\u65bd\u5c11\u5976\u5976\u51b7\u7b11\u9053\uff1a\u201c\u695a\u9999\u5e05\u7684\u5473\u53e3\u662f\u51e0\u65f6\u6539\u53d8\u4e86\u7684\uff0c\u51e0\u65f6\u53d8\u5f97\u559c\u6b22\u8ddf\u8001\u592a\u5a46\u804a\u5929\u4e86\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u53c8\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u6211\u4e0d\u627e\u8001\u592a\u5a46\u804a\u5929\uff0c\u96be\u9053\u5c11\u5976\u5976\u80af\u966a\u6211\u804a\u5929\u9ebd\uff1f\u201d \u3000\u3000\u65bd\u5c11\u5976\u5976\u76ef\u7740\u4ed6\u773c\u775b\u91cc\u5ffd\u7136\u6709\u4e86\u7b11\u610f\uff0c\u5ffd\u7136\u6389\u5934\u5c31\u8d70\uff0c\u5979\u7684\u8eab\u6750\u4e0d\u9519\uff0c\u53ea\u770b\u80cc\u5f71\uff0c\u5012\u9887 \u6709\u97f5\u81f4\u3002 \u3000\u3000\u695a\u7559\u9999\u53ea\u5e0c\u671b\u5979\u83ab\u8981\u56de\u5934\uff0c\u4e00\u56de\u5934\u5c31\u6e9c\u4e86\u3002 \u3000\u3000\u4e0d\u5e78\u65bd\u5c11\u5976\u5976\u5374\u504f\u504f\u8981\u56de\u5934\uff0c\u9762\u4e14\u8fd8\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u65e2\u7136\u8981\u8ddf\u6211\u804a\uff0c\u4e3a\u4ec0\u9ebd\u4e0d\u8ddf\u6211 \u6765\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u771f\u7684\u53f9\u4e86\u53e3\u6c14\uff0c\u4ed6\u60f3\u7740\u6709\u8c01\u6562\u7528\u201c\u56de\u7738\u4e00\u7b11\u767e\u5a9a\u751f\u201d\u8fd9\u53e5\u8bdd\u6765\u5f62\u5bb9\u8fd9\u4f4d\u5c11\u5976\u5976\uff0c \u4ed6\u4e00\u5b9a\u8981\u8ddf\u90a3\u4eba\u6253\u67b6\u3002 \u3000\u3000\u65bd\u5c11\u5976\u5976\u4e0d\u4f46\u5728\u7b11\uff0c\u8fd8\u629b\u4e86\u4e2a\u98de\u773c\uff0c\u9053\uff1a\u201c\u4f60\u6015\u4ec0\u9ebd\uff1f\u96be\u9053\u6211\u4f1a\u5403\u4e86\u4f60\uff1f\u201d \u3000\u3000\u695a\u6d41\u9999\u5583\u5583\u9053\uff1a\u201c\u4f60\u770b\u6765\u5012\u771f\u50cf\u4f1a\u54ac\u4eba\u7684\u3002\u201d \u3000\u3000\u65bd\u5c11\u5976\u5976\u9053\uff1a\u201c\u4f60\u5634\u91cc\u5495\u54dd\u5495\u54dd\u5728\u8bf4\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u82e6\u7b11\u9053\uff1a\u201c\u6211\u4ec0\u9ebd\u4e5f\u6ca1\u8bf4\uff0c\u53ea\u4e0d\u8fc7\u5634\u5728\u62bd\u7b4b\u800c\u5df2\u3002\u201d \u3000\u3000\u4ed6\u5c3d\u907f\u53ea\u5e0c\u671b\u65bd\u5c11\u5976\u5976\u7684\u8116\u5b50\u5ffd\u7136\u626d\u4e86\u7b4b\uff0c\u518d\u4e5f\u56de\u4e0d\u8fc7\u5934\u6765\uff0c\u600e\u5948\u65bd\u5c11\u5976\u5976\u7684\u8116\u5b50\u5374\u7075 \u6d3b\u5f97\u5f88\uff0c\u4e00\u4e0b\u5b50\u53c8\u56de\u8fc7\u5934\u6765\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u53c8\u4e0d\u662f\u5c0f\u72c8\uff0c\u4e3a\u4ec0\u9ebd\u8981\u8ddf\u5728\u4eba\u5bb6\u5f8c\u9762\u8d70\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u53ea\u597d\u786c\u7740\u5934\u76ae\u8d70\u4e0a\u524d\u53bb\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u5c11\u5976\u5976\uff0c\u968f\u4fbf\u4ec0\u9ebd\u5730\u65b9\u90fd\u53ef\u4ee5 \u804a\u5929\u7684\uff0c\u4f60\u8981\u5230\u54ea\u91cc\uff1f\u201d \u3000\u3000\u65bd\u5c11\u5976\u5976\u53c8\u767d\u4e86\u4ed6\u773c\uff0c\u9053\uff1a\u201c\u6709\u5f88\u591a\u5c0f\u4f19\u5b50\u90fd\u5728\u5077\u5077\u7684\u79f0\u6211\u2018\u96ea\u91cc\u7ea2\u2019\u8fd8\u4ee5\u4e3a\u6211\u4e0d\u77e5 \u9053\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53ea\u6709\u6478\u9f3b\u5b50\uff0c\u53d1\u8a93\u4eca\u5f8c\u518d\u4e5f\u4e0d\u5403\u201c\u96ea\u91cc\u7ea2\u7092\u8089\u4e1d\u201d\u8fd9\u9053\u83dc\u4e86\uff0c\u5b81\u53ef\u5403\u841d\u535c\u5e72\u4e5f\u4e0d \u5403\u96ea\u91cc\u7ea2\u3002 \u3000\u3000\u859b\u7ea2\u7ea2\u7fd8\u8d77\u4e86\u5634\u9053\uff1a\u201c\u5582\uff0c\u4f60\u60f3\u627e\u6211\u804a\u5929\uff0c\u600e\u9ebd\u4e0d\u8bf4\u8bdd\u5440\uff1f\u96be\u9053\u53d8\u6210\u4e86\u54d1\u5427\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u770b\u5230\u5979\u90a3\u7fd8\u8d77\u4e86\u7684\u5634\uff0c\u53ea\u6068\u4e0d\u5f97\u80fd\u5728\u4e0a\u9762\u6302\u4e2a\u6cb9\u74f6\u3002 \u3000\u3000\u53ea\u6068\u80e1\u94c1\u82b1\u6ca1\u6709\u6765\uff0c\u4e5f\u8bb8\u771f\u505a\u5f97\u51fa\u7684\u3002 \u3000\u3000\u695a\u7559\u9999\u4e7e\u54b3\u4e86\u58f0\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u90a3\u4f4d\u4e8c\u53d4\u53ef\u771f\u6709\u8da3\uff0c\u5c31\u50cf\u4e2a\u5b69\u5b50\u4f3c\u7684\uff0c\u4f46\u5251\u6cd5\u5374\u53c8\u90a3\u9ebd \u9ad8\uff0c\u90a3\u5929\u665a\u4e0a\u6211\u8981\u4e0d\u662f\u8dd1\u5f97\u5feb\uff0c\u5dee\u70b9\u5c31\u88ab\u4ed6\u523a\u4e86\u4e2a\u900f\u660e\u7a9f\u7abf\u3002\u201d \u3000\u3000\u859b\u7ea2\u7ea2\u4e5f\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u5e78\u597d\u4f60\u8dd1\u5f97\u5feb\uff0c\u6211\u4e8c\u53d4\u9664\u4e86\u5403\u4e4b\u5916\uff0c\u5c31\u4f1a\u4f7f\u5251\u3002\u4ed6\u75af\u75c5\u7f62\u53d1\u4f5c\u7684 \u65f6\u5019\uff0c\u786c\u903c\u7740\u6211\u7239\u7239\u548c\u4ed6\u52a8\u624b\u3002\u8fde\u7239\u7239\u90fd\u51e0\u4e4e\u88ab\u4ed6\u523a\u4e86\u5251\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u773c\u775b\u4f3c\u4e4e\u5ffd\u7136\u4eae\u4e86\uff0c\u9053\uff1a\u201c\u5f8c\u6765\u5462\uff1f\u201d \u3000\u3000\u859b\u7ea2\u7ea2\u7b11\u9053\uff1a\u201c\u5f8c\u6765\u7239\u7239\u81ea\u7136\u8fd8\u662f\u5c06\u4ed6\u5236\u670d\u4e86\uff0c\u4ed6\u2014\u6c14\u4e4b\u4e0b\uff0c\u5c31\u75af\u5f97\u66f4\u5389\u5bb3\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u636e\u4ee4\u5c0a\u5927\u4eba\u8bf4\uff0c\u4ed6\u672c\u6765\u5e76\u4e0d\u662f\u8fd9\u6837\u5b50\u7684\u3002\u201d \u3000\u3000\u859b\u7ea2\u7ea2\u9053\uff1a\u201c\u4ed6\u5c31\u662f\u7ec3\u5251\u7ec3\u75af\u4e86\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u859b\u7ea2\u7ea2\u9053\uff1a\u201c\u4ed6\u5251\u6cd5\u6839\u672c\u5c31\u4e0d\u9519\uff0c\u4f46\u6bd4\u8d77\u6211\u7239\u7239\u6765\u81ea\u7136\u8fd8\u5dee\u5f97\u8fdc\uff0c\u6240\u4ee5\u5c31\u62fc\u547d\u7ec3\u5251\uff0c\u4e00 \u5fc3\u60f3\u80dc\u8fc7\u6211\u7239\u7239\uff0c\u7ec3\u5f97\u996d\u4e5f\u4e0d\u5403\uff0c\u89c9\u4e5f\u4e0d\u7761\uff0c\u4f46\u65e0\u8bba\u4ed6\u600e\u9ebd\u7ec3\uff0c\u8fd8\u662f\u6bd4\u4e0d\u4e0a\u7239\u7239\u3002\u6709\u4e00\u5929\u665a \u4e0a\uff0c\u4ed6\u5ffd\u5c06\u4e8c\u5a76\u6740\u4e86\u3002\u8bf4\u662f\u4e8c\u5a76\u603b\u662f\u6270\u4e71\u4ed6\u7ec3\u5251\uff0c\u4f46\u6740\u4e86\u4e8c\u5a76\u5f8c\uff0c\u4ed6\u81ea\u5df1\u4e5f\u53d8\u5f97\u6108\u75af\u766b\uff0c\u8001 \u8bf4\u81ea\u5df2\u53ea\u6709\u5341\u5c81\uff0c\u5c31\u56e0\u4e3a\u5e74\u7eaa\u5c0f\uff0c\u6240\u4ee5\u5251\u6cd5\u624d\u4e0d\u5982\u7239\u7239\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u5230\u65e0\u53ef\u5948\u4f55\u65f6\uff0c\u4e5f\u53ea\u6709\u81ea\u5df1\u9a97\u9a97\u81ea\u5df2\u4e86\uff0c\u53ea\u4e0d\u8fc7\u4ed6\u2026\u201c \u3000\u3000\u859b\u7ea2\u7ea2\u5ffd\u7136\u5a07\u54c2\u9053\uff1a\u201c\u6211\u4eec\u4e3a\u4ec0\u9ebd\u8001\u662f\u8981\u63d0\u4ed6\u5462\uff1f\u96be\u9053\u6ca1\u6709\u5947\u4ed6\u7684\u4e8b\u53ef\u63d0\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u6478\u4e86\u6478\u9f3b\u5b50\uff0c\u9053\uff1a\u201c\u4f60\u60f3\u542c\u4ec0\u9ebd\uff1f\u6211\u5c31\u966a\u4f60\u804a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u859b\u7ea2\u7ea2\u779f\u4e86\u4ed6\u4e00\u773c\u3002\u62bf\u5634\u7b11\u9053\uff1a\u201c\u4e00\u4e2a\u7537\u4eba\u548c\u4e00\u4e2a\u5973\u4eba\u5728\u4e00\u8d77\uff0c\u53ef\u804a\u7684\u4e8b\u592a\u591a\u4e86\uff0c\u4f60\u96be \u9053\u8fd8\u4e0d\u77e5\u9053\uff0c\u96be\u9053\u8fd8\u8981\u6211\u6765\u6559\u4f60\uff1f\u201d \u3000\u3000\u5979\u5403\u5403\u7b11\u9053\uff1a\u201c\u4f60\u82e5\u8fd8\u8981\u522b\u4eba\u6559\uff0c\u4f60\u5c31\u4e0d\u662f\u98ce\u6d41\u4fa0\u76d7\u695a\u7559\u9999\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u542c\u201c\u98ce\u6d41\u4fa0\u76d7\u201d\u8fd9\u540d\u5b57\u5c31\u5934\u75bc\uff0c\u66f4\u4ee4\u4ed6\u5934\u75bc\u7684\u662f\u4ed6\u53d1\u73b0\u859b\u7ea2\u7ea2\u5e26\u7740\u8d70\u7684\u8def\u8d8a\u6765\u8d8a \u504f\u50fb\u800c\u4e14\u8def\u7684\u5c3d\u5934\uff0c\u6797\u6728\u63a9\u6620\u4e2d\uff0c\u4f3c\u4e4e\u8fd8\u6709\u51e0\u95f4\u5c4b\u5b50\uff0c\u4ed6\u4e0d\u6562\u60f3\u50cf\u5230\u4e86\u5c4b\u5b50\u91cc\u4e4b\u5f8c\u4f1a\u53d1\u751f\u4ec0 \u9ebd\u4e8b\u3002 \u3000\u3000\u4f46\u8fd9\u65f6\u4ed6\u60f3\u8d70\u5df2\u6765\u4e0d\u53ca\u4e86\u3002 \u3000\u3000\u859b\u7ea2\u7ea2\u5df2\u62c9\u7740\u4ed6\u7684\u624b\uff0c\u5a9a\u7b11\u9053\uff1a\u201c\u6211\u5e26\u6211\u5230\u4e2a\u597d\u5730\u65b9\u53bb\uff0c\u4f60\u5e94\u8be5\u600e\u6837\u611f\u6fc0\u6211\u624d\u662f\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u2026\u2026\u54b3\u54b3\uff0c\u8fd9\u2026\u201d\u54b3\u54b3\u2026\u2026\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u8df3\u8d77\u6765\uff0c\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u4f60\u90a3\u5339\u9ed1\u9a74\u5b50\u4e0d\u89c1\u4e86\uff0c\u5feb\u56de\u53bb\u627e\u5427\u201d \u3000\u3000\u859b\u7ea2\u7ea2\u683c\u683c\u7b11\u9053\uff1a\u201c\u4e00\u5339\u9a74\u5b50\u4e5f\u6ca1\u6709\u4ec0\u9ebd\u4e86\u4e0d\u5f97\uff0c\u6211\u6709\u4e86\u4f60\uff0c\u8fd8\u8981\u9a74\u5b50\u4f5c\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u82e5\u6709\u4eba\u8bf4\u695a\u7559\u9999\u4f1a\u8138\u7ea2\uff0c\u975e\u4f46\u522b\u4eba\u4e0d\u4fe1\uff0c\u53ea\u6015\u8fde\u4ed6\u81ea\u5df2\u90fd\u4e0d\u4f1a\u76f8\u4fe1\uff0c\u4f46\u73b0\u5728\u4ed6\u7684\u8138\u5219\u771f \u6709\u4e9b\u7ea2\u4e86\u3002 \u3000\u3000\u859b\u8863\u4eba\u4e5f\u8bb8\u5c31\u56e0\u4e3a\u6740\u4eba\u6740\u5f97\u592a\u591a\u4e86\uff0c\u6240\u4ee5\u624d\u4f1a\u751f\u4e0b\u8fd9\u79cd\u5b9d\u8d1d\u5973\u513f\uff0c\u4ed6\u8fd8\u6ca1\u6709\u88ab\u5973\u513f\u6c14 \u6b7b\uff0c\u5012\u771f\u662f\u602a\u4e8b\u4e00\u4ef6\u3002 \u3000\u3000\u859b\u7ea2\u7ea2\u5df2\u62c9\u7740\u695a\u7559\u9999\u5411\u90a3\u67ab\u6797\u8d70\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u9633\u5149\u6620\u5f97\u4e00\u6797\u67ab\u53f6\u7ea2\u5982\u665a\u971e\uff0c\u67ab\u6797\u4e2d\u5c71\u5c4b\u53c1\u4e94\u95f4\uff0c\u5efa\u5f97\u53c8\u5c0f\u5de7\uff0c\u53c8\u7cbe\u81f4\uff0c\u770b\u6765\u5c31\u5b9b\u5982\u56fe \u753b\u3002 \u3000\u3000\u6b64\u523b\u5728\u695a\u7559\u9999\u8eab\u65c1\u7684\u82e5\u4e0d\u662f\u859b\u7ea2\u7ea2\uff0c\u5230\u4e86\u8fd9\u79cd\u5730\u65b9\uff0c\u4ed6\u4e00\u5b9a\u4f1a\u89c9\u5f97\u6709\u4e9b\u201c\u98d8\u7136\u6b32\u4ed9\u201d\uff0c \u4f46\u73b0\u5728\u4ed6\u5374\u89c9\u5f97\u81ea\u5df2\u597d\u50cf\u4e2a\u6d3b\u9b3c\u3002 \u3000\u3000\u859b\u7ea2\u7ea2\u4e00\u53ea\u624b\u62d6\u7740\u4ed6\uff0c\u4e00\u53ea\u624b\u5df2\u5728\u63a8\u95e8\u3002 \u3000\u3000\u695a\u7559\u9999\u82e6\u7b11\u9053\uff1a\u201c\u8fd9\u2026\u2026\u8fd9\u662f\u8c01\u7684\u5c4b\u5b50\u4f60\u4e5f\u4e0d\u77e5\u9053\uff0c\u600e\u9ebd\u968f\u4fbf\u63a8\u4eba\u5bb6\u7684\u95e8\uff1f\u82e5\u8981\u88ab\u4eba\u5f53 \u5c0f\u5077\u6293\u4f4f\u5c82\u975e\u51a4\u6789\uff1f\u201d \u3000\u3000\u859b\u7ea2\u7ea2\u9053\uff1a\u201c\u8c01\u6562\u5c06\u6211\u5f53\u5c0f\u5077\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5e73\u65f6\u81ea\u7136\u4e0d\u4f1a\uff0c\u4f46\u4f60\u82e5\u8ddf\u6211\u5728\u4e00\u8d77\uff0c\u5c31\u8bf4\u4e0d\u5b9a\u4e86\uff0c\u6211\u7684\u540d\u58f0\u4e00\u5411\u4e0d\u597d\uff0c\u8bf4 \u4e0d\u5b9a\u4f1a\u8fde\u7d2f\u4f60\u3002\u201d \u3000\u3000\u4ed6\u4e00\u9762\u8bf4\uff0c\u4e00\u9762\u5c31\u60f3\u6e9c\u4e4b\u5927\u5409\u3002 \u3000\u3000\u4f46\u859b\u7ea2\u7ea2\u5374\u5c06\u4ed6\u7684\u624b\u6293\u5f97\u66f4\u7d27\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u653e\u5fc3\u5427\uff0c\u8fd9\u91cc\u4e5f\u662f\u859b\u5bb6\u7684\u4ea7\u4e1a\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53c8\u60f3\u6478\u9f3b\u5b50\uff0c\u600e\u5948\u4e24\u53ea\u624b\u90fd\u88ab\u859b\u7ea2\u7ea2\u6293\u4f4f\u4e86\uff0c\u53ea\u6709\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u5bb6\u7684\u4ea7\u4e1a\u5012\u771f \u4e0d\u5c11\u3002\u201d \u3000\u3000\u859b\u7ea2\u7ea2\u9053\uff1a\u201c\u8fd9\u672c\u662f\u6211\u4e8c\u53d4\u6ca1\u6709\u53d1\u75af\u65f6\u72ec\u5c45\u7ec3\u5251\u7684\u5730\u65b9\uff0c\u5f8c\u6765\u5c31\u7a7a\u4e86\u4e0b\u6765\uff0c\u6211\u4e8c\u5f1f\u6253\u730e \u65f6\u4e5f\u65f6\u5e38\u6765\u4f4f\uff0c\u4f46\u8fd9\u51e0\u5929\u4ed6\u5374\u5230\u2026\u2026\u201d \u3000\u3000\u5979\u4e00\u8fb9\u8bf4\u7740\u8bdd\uff0c\u4e00\u8fb9\u5df2\u63a8\u5f00\u95e8\uff0c\u8bf4\u5230\u8fd9\u91cc\uff0c\u7a81\u7136\u542c\u4e00\u4eba\u5ffd\u543c\u9053\uff1a\u201c\u4ec0\u9ebd\u4eba\u6562\u4e71\u95ef\uff1f\u201d \u3000\u3000\u543c\u58f0\u4e2d\uff0c\u4e00\u6837\u9ed1\u5ffd\u5ffd\u7684\u4e1c\u897f\u76f4\u6253\u4e86\u51fa\u6765\u3002\u64e6\u7740\u859b\u7ea2\u7ea2\u7684\u5934\u76ae\u98de\u8fc7\uff0c\u8fdc\u8fdc\u843d\u5728\u95e8\u5916\uff0c\u7adf\u662f \u53ea\u9774\u5b50\u3002 \u3000\u3000\u5c4b\u4e88\u91cc\u5e03\u7f6e\u5f97\u7b80\u5355\u800c\u96c5\u81f4\uff0c\u5e8a\u4e0a\u94fa\u7740\u53c8\u539a\u53c8\u8f6f\u7684\u517d\u76ae\uff0c\u4e24\u4e2a\u51e0\u4e4e\u5df2\u8131\u5f97\u5b8c\u5168\u8d64\u88f8\u7684\u4eba\uff0c \u6b63\u5728\u517d\u76ae\u4e0a\u6253\u6eda\u3002 \u3000\u3000\u859b\u7ea2\u7ea2\u4e00\u5f00\u95e8\uff0c\u7537\u7684\u7acb\u523b\u6012\u543c\u7684\u8df3\u8d77\u6765\uff0c\u6284\u8d77\u53ea\u9774\u5b50\u5c31\u5f80\u5916\u9762\u4e22\u3002\u5973\u7684\u8d76\u7d27\u62a1\u8d77\u4ef6\u8863 \u670d\uff0c\u63a9\u4f4f\u80f8\u8179\uff0c\u5374\u8fd8\u662f\u6ca1\u6709\u63a9\u4f4f\u4e24\u6761\u767d\u751f\u751f\u7684\u817f\uff0c\u5373\u4f7f\u7528\u695a\u7559\u9999\u7684\u773c\u5149\u6765\u770b\u8fd9\u4e24\u6761\u817f\u4e5f\u7b97\u662f \u4e00\u6d41\u7684\u3002 \u3000\u3000\u90a3\u7537\u7684\u5e74\u7eaa\u5f88\u8f7b\uff0c\u4e5f\u662f\u8eab\u7ec6\u76ae\u767d\u8089\uff0c\u957f\u5f97\u5012\u5f88\u82f1\u4fca\uff0c\u53ea\u4e0d\u8fc7\u8138\u8272\u82cd\u767d\u773c\u775b\u91cc\u5e03\u6ee1\u4e86\u7ea2 \u4e1d\u3002 \u3000\u3000\u770b\u5230\u7834\u95e8\u7684\u859b\u7ea2\u7ea2\uff0c\u4ed6\u8138\u4e0a\u7684\u6012\u5bb9\u7acb\u523b\u53d8\u4e3a\u60ca\u8bb6\uff0c\u859b\u7ea2\u7ea2\u770b\u5230\u4ed6\uff0c\u4e5f\u5403\u4e86\u60ca\u5931\u58f0\u9053\uff1a \u201c\u662f\u4f60\uff1f\u201d \u3000\u3000\u8fd9\u5c11\u5e74\u4e00\u628a\u6293\u8d77\u8863\u670d\u5c31\u8eb2\u5230\u6905\u5b50\u5f8c\u9762\u53bb\u4e86\u3002 \u3000\u3000\u90a3\u5973\u7684\u60f3\u7ad9\u8d77\u6765\uff0c\u770b\u5230\u695a\u7559\u9999\u7b11\u772f\u772f\u7684\u773c\u795e\uff0c\u8d76\u7d27\u53c8\u5750\u4e86\u4e0b\u6765\uff0c\u4e24\u53ea\u53c8\u957f\u53c8\u76f4\u7684\u817f\u62fc\u547d \u5411\u91cc\u7f29\u3002 \u3000\u3000\u859b\u7ea2\u7ea2\u94c1\u9752\u7740\u8138\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u5df2\u7ecf\u5230\u7701\u57ce\u53bb\u529e\u5e74\u8d27\u4e86\u9ebd\uff1f\u600e\u9ebd\u4f1a\u5230\u4e86\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5e74\u4e00\u9762\u7a7f\u8863\u670d\uff0c\u4e00\u9762\u8d54\u7b11\u9053\uff1a\u201c\u79bb\u8fc7\u5e74\u53cd\u6b63\u8fd8\u65e9\u5f97\u5f88\uff0c\u6211\u60f3\u7b79\u753b\u4e24\u5929\u518d\u53bb\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u859b\u7ea2\u7ea2\u51b7\u7b11\u9053\uff1a\u201c\u6211\u65e9\u5c31\u5728\u5947\u602a\uff0c\u4f60\u600e\u9ebd\u4f1a\u5ffd\u7136\u52e4\u5feb\u8d77\u6765\u4e86\uff0c\u5c45\u7136\u62a2\u7740\u529e\u4e8b\uff0c\u539f\u6765\u4f60\u662f \u60f3\u907f\u5f00\u7239\u7239\u5230\u5916\u9762\u6765\u627e\u91ce\u98df\u3002\u201d \u3000\u3000\u5979\u773c\u775b\u4e00\u77aa\uff0c\u9053\uff1a\u201c\u6211\u95ee\u4f60\uff0c\u8fd9\u5973\u7684\u662f\u8c01\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5e74\u9053\uff0c\u201c\u662f\u201d\u662f\u6211\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u859b\u7ea2\u7ea2\u51b7\u7b11\u9053\uff1a\u201c\u670b\u53cb\u6211\u770b\u4f60\u3002\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u5ffd\u7136\u4f38\u51fa\u5934\u6765\uff0c\u62a2\u7740\u9053\uff1a\u201c\u6211\u95ee\u4f60\uff0c\u4f60\u8fd9\u7537\u7684\u53c8\u662f\u8c01\uff1f\u201d \u3000\u3000\u859b\u7ea2\u7ea2\u6014\u4e86\u6014\u9053\uff1a\u201c\u662f\u2026\uff0e\u81ea\u7136\u662f\u6211\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u4e5f\u51b7\u7b11\u9053\uff1a\u201c\u670b\u53cb\uff1f\u6211\u770b\u6015\u672a\u5fc5\u5427\u201d \u3000\u3000\u859b\u7ea2\u7ea2\u607c\u7f9e\u6210\u6012\uff0c\u8df3\u8d77\u6765\u543c\u9053\uff1a\u201c\u8001\u4e8c\uff0c\u6211\u544a\u8bc9\u4f60\uff0c\u4f60\u5c11\u7ba1\u6211\u7684\u95f2\u4e8b\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u60a0\u60a0\u9053\uff1a\u201c\u597d\uff0c\u6211\u4eec\u6765\u8ba2\u4e2a\u4ea4\u6613\uff0c\u53ea\u8981\u4f60\u4e0d\u7ba1\u6211\u7684\u95f2\u4e8b\uff0c\u6211\u4e5f\u7edd\u4e0d\u7ba1\u4f60\u7684\u95f2\u4e8b\uff0c \u5426\u5219\u82e5\u662f\u95f9\u51fa\u53bb\uff0c\u53ea\u6015\u4f60\u6bd4\u6211\u66f4\u4e22\u4eba\u3002\u201d \u3000\u3000\u859b\u7ea2\u7ea2\u51b2\u4e86\u8fc7\u53bb\uff0c\u62ac\u8d77\u4e00\u817f\u5c06\u6905\u5b50\u8e22\u7ffb\uff0c\u5927\u53eb\u9053\uff1a\u201c\u6211\u6709\u4ec0\u9ebd\u597d\u4e22\u4eba\u7684\uff1f\u6211\u53c8\u6ca1\u8131\u5149\u5c41 \u80a1\u8ddf\u4eba\u6363\u9b3c\u3002\u2026\uff1a\u201c \u3000\u3000\u695a\u7559\u9999\u5b9e\u5728\u4e0d\u60f3\u518d\u542c\u4e0b\u53bb\u4e86\u6084\u6084\u5e26\u8d77\u95e8\uff0c\u6e9c\u4e86\u51fa\u53bb\uff0c\u5fc3\u91cc\u8bf4\u4e0d\u51fa\u7684\u96be\u53d7\u2014\u2014\u66ff\u859b\u8863\u4eba\u96be \u53d7\u3002 \u3000\u3000\u4ed6\u73b0\u5728\u81ea\u7136\u5df2\u7ecf\u77e5\u9053\u8fd9\u5c11\u4e2d\u5c31\u662f\u859b\u5bb6\u4e8c\u516c\u5b50\u859b\u658c\uff0c\u8fd9\u59d0\u5f1f\u4e24\u4eba\u771f\u662f\u4e00\u4e2a\u6a21\u5b50\u91cc\u94f8\u51fa\u6765\u7684 \u6d3b\u5b9d\u3002 \u3000\u3000\u53ea\u53ef\u601c\u859b\u8863\u4eba\u4e00\u4e16\u82f1\u540d\uff0c\u7adf\u751f\u51fa\u8fd9\u9ebd\u6837\u4e00\u5bf9\u513f\u5973\u6765\uff0c\u201c\u8c6a\u95e8\u591a\u5b7d\u5b50\u201d\uff0c\u695a\u7559\u9999\u53d1\u89c9\u8fd9\u53e5 \u8bdd\u771f\u662f\u8bf4\u5f97\u6709\u5b66\u95ee\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u7740\u60f3\u6210\u4e3a\u5929\u4e0b\u65e0\u53cc\u7684\u5251\u5ba2\uff0c\u5c31\u6700\u597d\u4e0d\u8981\u517b\u513f\u5973\uff0c\u56e0\u4e3a\u6700\u597d\u7684\u5251\u5ba2\uff0c\u5fc5\u5b9a\u662f\u6700\u574f\u7684 \u7236\u4eb2\u3002 \u3000\u3000\u5251\uff0c\u5c31\u50cf\u662f\u5973\u4eba\u4e00\u6837\uff0c\u4f60\u60f3\u5b83\u670d\u4ece\u4f60\uff0c\u5c31\u4e00\u5b9a\u8981\u5168\u5fc3\u5168\u610f\u7684\u5bf9\u5b83\uff0c\u5426\u5219\u5b83\u5c31\u4f1a\u51fa\u5356\u4f60\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u7eb5\u5728\u88ab\u5973\u4eba\u51fa\u5356\u4e86\u4e24\u767e\u6b21\uff0c\u8fd8\u53ef\u4ee5\u518d\u627e\u7b2c\u4e24\u767e\u96f6\u4e00\u4e2a\u5973\u4eba\uff0c\u4f46\u53ea\u8981\u88ab\u5251\u51fa\u5356\u4e00 \u6b21\uff0c\u5c31\u5f97\u6b7b\u3002 \u3000\u3000\u695a\u7559\u9999\u5438\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u859b\u8863\u4eba\uff0c\u859b\u8863\u4eba\uff0c\u4f60\u867d\u80fd\u5c06\u5251\u62db\u6325\u5982\u610f\uff0c\u4f46\u662f\u4f60\u81ea\u5df1\u53c8\u4f55\u5c1d\u4e0d \u662f\u5251\u7684\u5974\u96b6\u2026\u3002\u201c \u3000\u3000\u623f\u5b50\u91cc\u90a3\u59d0\u5f1f\u4e24\u4eba\u8fd8\u5728\u4e89\u5435\uff0c\u800c\u4e14\u58f0\u97f3\u8d8a\u6765\u8d8a\u5927\uff0c\u4f46\u95e8\u5374\u5ffd\u7136\u5f00\u4e86\uff0c\u4e00\u4e2a\u4eba\u98de\u8dd1\u4e86\u51fa \u6765\uff0c\u5927\u58f0\u9053\uff1a\u201c\u5582\uff0c\u4f60\u7b49\u7b49\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u56de\u5934\uff0c\u5c31\u770b\u5230\u90a3\u65b9\u50cf\u6761\u5c0f\u767d\u7f8a\u822c\u5377\u66f2\u5728\u864e\u76ae\u4e0a\u7684\u5973\u5b69\u5b50\uff0c\u6b63\u5728\u5411\u4ed6\u4e0d\u505c\u7684\u62db\u624b\u3002 \u3000\u3000\u73b0\u5728\u5979\u5f53\u7136\u7a7f\u8d77\u4e86\u8863\u670d\uff0c\u4f46\u6263\u5b50\u8fd8\u6ca1\u6709\u6263\u4e0a\uff0c\u4e5f\u6ca1\u6709\u7a7f\u978b\u5b50\uff0c\u8863\u895f\u91cc\u9732\u51fa\u4e86\u4e00\u6bb5\u96ea\u767d\u7684 \u76ae\u80a4\uff0c\u767d\u7684\u4ee4\u4eba\u773c\uff0c\u82b1\u767e\u6298\u88d9\u4e0b\u9762\u9732\u51fa\u4e00\u622a\u4fee\u957f\u7684\u5c0f\u817f\u7ea4\u5de7\u7684\u8db3\u8e1d\u548c\u53cc\u5e95\u5e73\u8dbe\u655b\u7684\u811a\u3002 \u3000\u3000\u695a\u7559\u9999\u5c3d\u91cf\u60f3\u4f7f\u81ea\u5df1\u7684\u773c\u775b\u89c4\u77e9\u4e9b\uff0c\u5c3d\u91cf\u4e0d\u5f80\u5979\u7684\u8863\u895f\u91cc\u9762\u770b\uff0c\u4f46\u8fd9\u53cc\u811a\u5374\u5b9e\u5728\u662f\u79cd\u8bf1 \u60d1\u3002 \u3000\u3000\u53ea\u8981\u662f\u7537\u4eba\u5c31\u65e0\u6cd5\u62d2\u7edd\u8fd9\u79cd\u8bf1\u60d1\u3002 \u3000\u3000\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u662f\u5728\u53eb\u6211\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201d\u6211\u6709\u8bdd\u8981\u8ddf\u4f60\u8bf4\u3002\u201d \u3000\u3000\u5979\u98de\u5954\u8fc7\u6765\uff0c\u7a81\u7136\u8f7b\u547c\u4e86\u4e00\u58f0\uff0c\u4e00\u4e2a\u53c8\u9999\uff0c\u53c8\u751c\uff0c\u53c8\u6e29\u67d4\u7684\u8eab\u5b50\u5c31\u6574\u4e2a\u5012\u5165\u4e86\u695a\u7559\u9999\u6000 \u91cc\u3002 \u3000\u3000\u695a\u7559\u9999\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u82e5\u60f3\u627e\u4e2a\u4eba\u66ff\u859b\u4e8c\u5c11\u7237\u505a\u5b8c\u4ed6\u65b9\u8fd8\u6ca1\u6709\u505a\u5b8c\u7684\u4e8b\uff0c\u4f60\u53ea\u6015\u627e\u9519 \u4e86\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u4f3c\u4e4e\u6839\u672c\u6ca1\u6709\u542c\u5230\u4ed6\u5728\u8bf4\u4ec0\u9ebd\uff0c\u98a0\u58f0\u9053\uff1a\u201c\u6211\u7684\u80f8\u3002\u6211\u7684\u811a\u2026\u3002\u201c \u3000\u3000\u695a\u7559\u9999\u8fd9\u624d\u53d1\u73b0\u5979\u7684\u811a\u539f\u6765\u5df2\u88ab\u77f3\u5934\u5272\u7834\u4e86\uff0c\u9c9c\u8840\u4e00\u6ef4\u6ef4\u5f80\u4e0b\u6d41\uff0c\u75bc\u5f97\u5979\u773c\u6cea\u90fd\u51e0\u4e4e\u6d41 \u4e86\u51fa\u6765\u3002 \u3000\u3000\u5979\u4e0d\u4f46\u817f\u7f8e\uff0c\u811a\u7f8e\uff0c\u8138\u4e5f\u7f8e\uff0c\u6b64\u523b\u7f8e\u4e3d\u7684\u8138\u4e0a\u6ee1\u662f\u75db\u82e6\u4e4b\u8272\uff0c\u518d\u52a0\u4e0a\u51e0\u6ef4\u773c\u6cea\uff0c\u66f4\u663e\u5f97 \u695a\u695a\u53ef\u601c\u3002 \u3000\u3000\u695a\u7559\u9999\u53c8\u4e0d\u7981\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e0b\u6b21\u8ddf\u522b\u4eba\u5e7d\u4f1a\u7684\u65f6\u5019\uff0c\u8bb0\u4f4f\u5343\u4e07\u83ab\u8981\u8131\u978b\u5b50\u3002\u201d \u3000\u3000\u8fd9\u5973\u5b69\u5b50\u770b\u6765\u867d\u662f\u90a3\u9ebd\u4e30\u6ee1\uff0c\u4f46\u8eab\u5b50\u5374\u8f7b\u5f97\u5f88\uff0c\u695a\u7559\u9999\u51e0\u4e4e\u5b8c\u5168\u6ca1\u6709\u7528\u529b\u6c14\uff0c\u5c31\u5c06\u5979\u62b1 \u4e86\u8d77\u6765\u3002 \u3000\u3000\u90a3\u5c11\u5973\u54ac\u7740\u5634\u5507\u52c9\u5f3a\u4e00\u7b11\uff0c\u8f7b\u8f7b\u9053\uff1a\u201c\u8c22\u8c22\u4f60\u3002\u201d \u3000\u3000\u695a\u6d41\u9999\u7684\u9f3b\u5b50\u867d\u7136\u4e0d\u7075\uff0c\u4f46\u8fd8\u662f\u55c5\u5230\u4e86\u4e00\u9635\u5982\u5170\u4f3c\u99a8\uff0c\u53ef\u4ee5\u4ee4\u4efb\u4f55\u7537\u4eba\u5fc3\u8df3\u52a0\u5feb\u7684\u9999 \u6c14\u3002 \u3000\u3000\u4ed6\u53ea\u6709\u5c06\u9f3b\u5b50\u5c3d\u91cf\u79bb\u5f97\u8fdc\u4e9b\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u4ed6\u7528\u4e0d\u7740\u8c22\u8c22\u6211\uff0c\u8fd8\u662f\u8c22\u8c22\u4f60\u7684\u811a\u5427\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u7684\u8138\u98de\u7ea2\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u5feb\u8d70\uff0c\u83ab\u8981\u7b49\u4ed6\u4eec\u8ffd\u51fa\u6765\u3002\u201d \u3000\u3000\u5176\u5b9e\u695a\u7559\u9999\u53c8\u4f55\u5c1d\u4e0d\u6015\u859b\u7ea2\u7ea2\u8ffd\u51fa\u6765\uff0c\u7528\u4e0d\u7740\u5979\u8bf4\uff0c\u695a\u7559\u9999\u5df2\u4e00\u6e9c\u70df\u822c\u7a9c\u5165\u4e86\u5c71\u5761\u4e0b\u7684 \u6811\u6797\u91cc\u3002 \u3000\u3000\u867d\u7136\u521a\u8fc7\u6b63\u5348\u8fd8\u6ca1\u6709\u591a\u4e45\uff0c\u6811\u6797\u4e2d\u5149\u7ebf\u5374\u5f88\u5e7d\u6666\uff0c\u65e0\u8bba\u4efb\u4f55\u5973\u4eba\u3002\u5728\u8fd9\u79cd\u5149\u7ebf\u4e2d\u770b\u6765\u90fd \u4f1a\u53d8\u5f97\u6f02\u4eae\u4e9b\u7684\uff0c\u4f55\u51b5\u8fd9\u5973\u5b69\u5b50\u672c\u6765\u5c31\u7f8e\u5f97\u5f88\u695a\u7559\u9999\u5b9e\u5728\u4e0d\u77e5\u9053\u81ea\u5df1\u80fd\u4e0d\u80fd\u53d7\u5f97\u4e86\u8fd9\u79cd\u8bf1 \u60d1\u3002 \u3000\u3000\u4ed6\u53ea\u597d\u8f6c\u8fc7\u773c\u775b\uff0c\u9053\uff1a\u201c\u4f60\u8981\u6211\u5c06\u4f60\u62b1\u5230\u4ec0\u9ebd\u5730\u65b9\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u5598\u606f\u7740\uff0c\u5ffd\u7136\u62e8\u51fa\u4e00\u67c4\u5c16\u5200\u3002 \u3000\u3000\u695a\u7559\u9999\u6b63\u89c9\u5f97\u5979\u8eab\u4e0a\u7684\u9999\u6c14\u6709\u70b9\u8981\u547d\uff0c\u8fd9\u67c4\u5c16\u5200\u5df2\u62b5\u4f4f\u4e86\u4ed6\u7684\u80f8\u819b\uff0c\u201c\u5636\u201d\u7684\uff0c\u5c06\u4ed6\u7684 \u8863\u670d\u5212\u7834\u4e86\u4e00\u6761\u7ebf\u3002 \u3000\u3000\u8fd9\u4e00\u7740\u5012\u771f\u7684\u5927\u51fa\u695a\u7559\u9999\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u53ea\u542c\u90a3\u5c11\u5973\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u82e5\u8fd8\u60f3\u8981\u547d\uff0c\u5c31\u5f97\u7b54\u5e94\u6211\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u54c2\u9053\uff1a\u201c\u50cf\u4f60\u8fd9\u6837\u7684\u5973\u5b69\u5b50\u8981\u7537\u4eba\u7b54\u5e94\u4f60\uff0c\u8fd8\u7528\u5f97\u7740\u5200\u9ebd\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u54ac\u7740\u7259\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4f60\u5c11\u80e1\u601d\u4e71\u60f3\uff0c\u6211\u4e0d\u662f\u4f60\u60f3\u7684\u90a3\u79cd\u5973\u4eba\uff01\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u4f60\u83ab\u4ee5\u4e3a\u6211\u521a\u521a\u662f\u5728\u3002\u3002\u3002\u5728\u4e0e\u90a3\u59d3\u859b\u7684\u5e7d\u4f1a\uff0c\u6211\u53ea\u662f\u2026\u53ea\u662f\u2026\u2014\u201d\u8bf4\u7740 \u8bf4\u7740\uff0c\u5979\u773c\u6cea\u53c8\u6d41\u4e86\u4e0b\u6765\u4e86\uff0c\u7f8e\u4e3d\u7684\u8138\u4e0a\u5145\u6ee1\u4e86\u6124\u6012\u7684\u6028\u6068\u4e4b\u8272\uff0c\u751a\u81f3\u8fde\u5634\u5507\u90fd\u88ab\u54ac\u51fa\u8840 \u6765\u3002 \u3000\u3000\u695a\u7559\u9999\u5f00\u59cb\u89c9\u5f97\u8fd9\u5973\u5b69\u5b50\u6709\u8da3\u4e86\uff0c\u53ea\u56e0\u4ed6\u5df2\u88ab\u5979\u5f15\u8d77\u4e86\u597d\u5947\u4e4b\u5fc3\uff0c\u4ed6\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4f60 \u53ea\u662f\u5728\u5e72\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u590d\u4ec7\u201d \u3000\u3000\u695a\u7559\u9999\u8bb6\u7136\u9053\uff1a\u201c\u590d\u4ec7\uff1f\u4e3a\u8c01\u590d\u4ec7\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u6211\u59d0\uff01\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u59d0\u59d0\uff1f\u5979\u96be\u9053\u662f\u6b7b\u5728\u90a3\u4f4d\u859b\u516c\u5b50\u624b\u4e0a\u7684\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u6068\u6839\u9053\uff1a\u201c\u859b\u658c\u867d\u6ca1\u6709\u6740\u5979\uff0c\u4f46\u5979\u6b7b\u5f97\u5374\u66f4\u60e8\uff0c\u859b\u658c\u82e5\u4e00\u5200\u6740\u4e86\u5979\uff0c\u53cd\u800c\u597d \u4e9b\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff0c\u201c\u90a3\u9ebd\u4ed6\u662f\u7528\u4ec0\u9ebd\u6cd5\u5b50\u5bb3\u6b7b\u4f60\u59d0\u59d0\u7684\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u4ed6\u7528\u7684\u662f\u6700\u5351\u9119\u3001\u6700\u53ef\u6068\u7684\u624b\u6bb5\uff0c\u5bb3\u5f97\u6211\u59d0\u59d0\u2026\u3002\u201d \u3000\u3000\u5979\u5ffd\u7136\u987f\u4f4f\u8bed\u58f0\uff0c\u77aa\u7740\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u5df2\u8bf4\u5f97\u592a\u591a\u4e86\uff0c\u6211\u53ea\u95ee\u4f60\uff0c\u4f60\u80af\u4e0d\u80af\u7b54\u5e94\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u7b54\u5e94\u4ec0\u9ebd\u4e8b\uff1f\u4f60\u8981\u6211\u5e2e\u4f60\u590d\u4ec7\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u5c06\u4e8b\u60c5\u5bf9\u6211\u8bf4\u6e05\u695a\uff0c\u6211\u600e\u9ebd\u80fd\u5e2e\u4f60\u7684\u5fd9\u5462\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u65e0\u8bba\u5982\u4f55\uff0c\u4f60\u90fd\u975e\u7b54\u5e94\u6211\u4e0d\u53ef\uff0c\u5426\u5219\u5c31\u8981\u4f60\u7684\u547d\uff01\u201d\u3105\u695a\u7559\u9999\u7b11\u4e86\u9053\uff1a \u201c\u4f60\u4ee5\u4e3a\u4f60\u771f\u80fd\u6740\u6b7b\u6211\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u5c06\u5200\u63e1\u5f97\u66f4\u7d27\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u6211\u4e0d\u6562\u6740\u4f60\uff1f\u201d \u3000\u3000\u5979\u8bdd\u521a\u8bf4\u5b8c\uff0c\u7a81\u89c9\u8eab\u5b50\u4e00\u9ebb\uff0c\u624b\u91cc\u7684\u5200\u4e5f\u4e0d\u77e5\u600e\u5730\u5ffd\u7136\u5c31\u5230\u4e86\u695a\u7559\u9999\u624b\u4e0a\uff0c\u5c31\u597d\u50cf\u695a\u7559 \u9999\u7528\u4e86\u4ec0\u9ebd\u9b54\u6cd5\u6837\u3002 \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u8fd9\u628a\u5200\u672c\u6765\u662f\u51c6\u5907\u6740\u859b\u516c\u5b50\u7684\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u62fc\u547d\u5506\u7740\u7259\uff0c\u5168\u8eab\u8fd8\u662f\u5728\u6296\u4e2a\u4e0d\u505c\u3002 \u3000\u3000\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5e78\u597d\u4f60\u65b9\u8fd8\u6ca1\u6709\u673a\u4f1a\u4e0b\u624b\uff0c\u5426\u5219\u6b64\u523b\u53ea\u6015\u4e5f\u5df2\u6b7b\u5728\u859b\u658c\u624b\u4e0a \u4e86\u3002\u201d \u3000\u3000\u4ed6\u7684\u624b\u4e00\u626c\uff0c\u5200\u5c31\u98de\u4e86\u51fa\u53bb\uff0c\u201c\u593a\u201d\u7684\uff0c\u9489\u5728\u6811\u4e0a\u3002 \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u65e2\u975e\u6740\u4eba\u7684\u5973\u5b69\u5b50\uff0c\u8fd9\u628a\u5200\u4e5f\u4e0d\u662f\u6740\u4eba\u7684\u5200\uff0c\u4f60\u82e5\u771f\u7684\u60f3\u590d\u4ec7\uff0c\u770b\u6765\u8fd8 \u5f97\u53e6\u5916\u60f3\u522b\u7684\u6cd5\u5b50\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u5ffd\u7136\u653e\u58f0\u75db\u54ed\u8d77\u6765\uff0c\u7528\u4e00\u53cc\u53c8\u767d\u53c8\u6487\u7684\u5c0f\u624b\uff0c\u62fc\u547d\u731b\u7740\u695a\u7559\u9999\u7684\u80f8\u819b\uff0c\u75db\u54ed\u7740 \u9053\uff1a\u201c\u4f60\u6740\u4e86\u6211\u5427\u2026\u3002\u4f60\u4e7e\u8106\u6740\u4e86\u6211\u5012\u597d\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u83ab\u5f04\u9519\u4e86\uff0c\u6211\u53ef\u4e0d\u662f\u90a3\u4f4d\u859b\u516c\u5b50\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u560e\u58f0\u9053\uff1a\u201c\u82e5\u4e0d\u80fd\u4e3a\u6211\u59d0\u590d\u4ec7\uff0c\u6211\u4e5f\u4e0d\u60f3\u6d3b\u4e86\u2026\u3002\u6211\u4e5f\u4e0d\u613f\u6d3b\u4e86\uff01\u201c \u3000\u3000\u5979\u5ffd\u7136\u6323\u624e\u7740\u4ece\u695a\u7559\u9999\u6000\u91cc\u8df3\u4e0b\u53bb\uff0c\u53bb\u62e8\u6811\u4e0a\u7684\u5200\u3002 \u3000\u3000\u4f46\u5979\u8fd8\u6ca1\u6709\u51b2\u8fc7\u53bb\uff0c\u695a\u7559\u9999\u5ffd\u53c8\u5230\u4e86\u5979\u9762\u524d\u3002 \u3000\u3000\u5979\u8eab\u5b50\u53c8\u51b2\u5165\u4e86\u695a\u7559\u9999\u6000\u91cc\u3002 \u3000\u3000\u695a\u7559\u9999\u8f7b\u8f7b\u62cd\u7740\u5979\u7684\u80a9\u5934\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u50cf\u4f60\u8fd9\u6837\u53c8\u5e74\u8f7b\u53c8\u7f8e\u4e3d\u7684\u5973\u5b69\u5b50\uff0c\u82e5\u4e0d\u80af\u6d3b\u4e0b \u53bb\uff0c\u90a3\u8fd8\u6709\u4ec0\u9ebd\u4eba\u80fd\u6d3b\u5f97\u4e0b\u53bb\u7684\u5462\uff1f\u4f60\u82e5\u8fde\u6d3b\u7684\u52c7\u6c14\u90fd\u6ca1\u6709\uff0c\u600e\u9ebd\u80fd\u66ff\u4f60\u59d0\u59d0\u590d\u4ec7\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u5782\u7740\u5934\uff0c\u8dfa\u7740\u811a\uff0c\u6d41\u6cea\u76f4\u6d41\uff0c\u53cd\u6b63\u5df2\u6ca1\u5e0c\u671b\u4e86\uff0c\u6b7b\u4e86\u5012\u4e7e\u51c0\u3002\u74dc \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u8c01\u8bf4\u4f60\u6ca1\u5e0c\u671b\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u970d\u7136\u59cb\u8d77\u5934\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u80af\u5e2e\u6211\u7684\u5fd9\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e5f\u8bb8\uff0c\u53ef\u662f\u4f60\u4e00\u5b9a\u8981\u5148\u5c06\u8fd9\u4ef6\u4e8b\u8bf4\u660e\u767d\u3002\u201d \u3000\u3000\u4ed6\u6276\u7740\u5979\u5728\u6811\u4e0b\u5750\u4e86\u4e0b\u6765\uff0c\u9759\u9759\u7684\u77a7\u7740\u5979\u9053\uff1a\u201c\u4f60\u81f3\u5c11\u603b\u5f97\u5148\u544a\u8bc9\u6211\u4f60\u662f\u8c01\uff1f\u4ec0\u9ebd\u540d \u5b57\uff1f\u201d \u3000\u3000\u4ed6\u76ee\u5149\u662f\u90a3\u9ebd\u6e29\u67d4\uff0c\u53c8\u90a3\u9ebd\u660e\u4eae\uff0c\u4ee4\u4f60\u89c9\u5f97\u4ed6\u4e0d\u4f46\u53ef\u4ee5\u505a\u4f60\u6e29\u67d4\u7684\u60c5\u4eba\uff0c\u4e5f\u53ef\u4ee5\u505a\u4f60\u5fe0 \u8bda\u7684\u670b\u53cb\u3002\u90a3\u5c11\u5973\u4f4e\u4e0b\u5934\uff0c\u82cd\u767d\u7684\u9762\u988a\u5df2\u8d77\u4e86\u9635\u7ea2\u6655\uff0c\u560e\u8815\u7740\u9053\uff1a\u201c\u6211\u59d3\u77f3\u2026\u3002\u201c \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u77f3\u5c0f\u6bdb\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u7ea2\u7740\u8138\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u77f3\u7ee3\u4e91\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u8fd9\u540d\u5b57\u6b63\u914d\u5f97\u4e0a\u4f60\uff0c\u4f60\u4e5f\u662f\u8fd9\u5730\u65b9\u7684\u4eba\uff1f\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5c31\u4f7f\u5728\u8fd9\u9644\u8fd1\uff1f\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u9053\uff1a\u201c\u6211\u4eec\u5bb6\u79cd\u7684\u7530\uff0c\u4e5f\u662f\u859b\u5bb6\u957f\u968f\uff0c\u7236\u4eb2\u6ca1\u6709\u53bb\u4e16\u7684\u65f6\u5019\uff0c\u8fd8\u5728\u859b\u5bb6\u7684\u79c1\u587e\u91cc \u6559\u8fc7\u4e66\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u59d0\u59d0\u624d\u4f1a\u8ba4\u5f97\u859b\u658c\uff1f\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u54ac\u7740\u5634\u5507\u9053\uff1a\u201c\u859b\u658c\u5c0f\u7684\u65f6\u5019\uff0c\u6211\u7236\u4eb2\u6700\u559c\u6b22\u4ed6\uff0c\u603b\u8bf4\u4ed6\u53c8\u806a\u660e\uff0c\u53c8\u80fd\u5e72\uff0c\u6587\u6b66 \u5168\u6750\uff0c\u5c06\u6765\u4e00\u5b9a\u6709\u51fa\u606f\uff0c\u6240\u4ee5\u65f6\u5e38\u5e26\u56de\u5bb6\u6765\u73a9\uff0c\u8c01\u77e5\u4ed6\uff0c\u2026\u3002\u4ed6\u7adf\u662f\u4e2a\u4eba\u9762\u517d\u5fc3\u7684\u755c\u7272\uff0c\u7239 \u7239\u5728\u4e5d\u6cc9\u4e0b\u82e5\u77e5\u9053\u4ed6\u505a\u7684\u4e8b\uff0c\u6015\uff01\u6015\u3002\u201d\u8bf4\u7740\u8bf4\u7740\uff0c\u5979\u4e0d\u7981\u53c8\u8f7b\u8f7b\u555c\u6ce3\u8d77\u6765\u3002 \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u59d0\u59d0\u7a76\u7adf\u662f\u600e\u9ebd\u6b7b\u7684\u5462\uff1f\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u53ea\u662f\u6447\u5934\uff0c\u6d41\u6cea\uff0c\u4ec0\u9ebd\u8bdd\u90fd\u4e0d\u8bf4\u3002 \u3000\u3000\u695a\u7559\u9999\u77e5\u9053\u8fd9\u4ef6\u4e8b\u5176\u4e2d\u5fc5\u6709\u8bb8\u591a\u96be\u8a00\u7684\u9690\u8877\uff0c\u4ed6\u672c\u4e0d\u613f\u903c\u522b\u4eba\u8bf4\u51fa\u81ea\u5df1\u4e0d\u613f\u8bf4\u7684\u4e8b\u3002 \u3000\u3000\u4f46\u859b\u658c\u5374\u662f\u65bd\u8335\u7684\u672a\u5a5a\u592b\u5a7f\uff0c\u6709\u5173\u4ed6\u7684\u6bcf\u4ef6\u4e8b\uff0c\u90fd\u53ef\u80fd\u5173\u7cfb\u7740\u8fd9\u201c\u501f\u5c38\u8fd8\u9b42\u201d\u7684\u79d8\u5bc6\u3002 \u3000\u3000\u695a\u7559\u9999\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u7684\u811a\u8fd8\u75bc\u9ebd\uff1f\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u53c8\u6d41\u7740\u6cea\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u695a\u7559\u9999\u8f7b\u8f7b\u63e1\u4f4f\u4e86\u5979\u7ec6\u5de7\u7684\u8db3\u8e1d\uff0c\u7528\u4e00\u5757\u6d01\u767d\u7684\u4e1d\u5dfe\u6e29\u67d4\u7684\u66ff\u5979\u64e6\u51c0\u4e86\u811a\u5e95\u7684\u8840\u6c61\u548c\u6ce5 \u6c99\u3002 \u3000\u3000\u77f3\u7ee3\u4e91\u7684\u8eab\u5b50\u5df2\u5267\u70c8\u7684\u98a4\u6296\u8d77\u6765\uff0c\u8138\u4e0a\u66f4\u7ea2\u5f97\u50cf\u662f\u665a\u971e\uff0c\u53ea\u89c9\u5168\u8eab\u518d\u4e5f\u6ca1\u6709\u4e00\u4e1d\u529b\u6c14\uff0c \u8fde\u5934\u90fd\u65e0\u6cd5\u62ac\u8d77\u3002\u5168\u8eab\u90fd\u5728\u53d1\u6296\u3002 \u3000\u3000\u695a\u7559\u9999\u7528\u4e1d\u5dfe\u66ff\u5979\u5305\u624e\u7740\u4f24\u53e3\uff0c\u5ffd\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u59d0\u59d0\u662f\u4e0d\u662f\u4e0a\u4e86\u859b\u658c\u7684\u5f53\uff1f\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u4f3c\u4e4e\u5df2\u8fde\u4e00\u4e1d\u6297\u62d2\u7684\u529b\u91cf\u90fd\u6ca1\u6709\u4e86\uff0c\u65e0\u8bba\u695a\u7559\u9999\u95ee\u5979\u4ec0\u9ebd\uff0c\u5979\u90fd\u4f1a\u6beb\u4e0d\u8fdf\u7591\u7684\u56de \u7b54\u3002 \u3000\u3000\u5979\u8bf4\u5f97\u867d\u7136\u542b\u7cca\u4e0d\u6e05\uff0c\u4f46\u695a\u7559\u9999\u4e5f\u5df2\u660e\u767d\u5979\u59d0\u59d0\u5728\u75f4\u604b\u7740\u4e00\u4e2a\u4eba\uff0c\u90a3\u4eba\u5374\u662f\u4e2a\u8584\u60c5\u4eba\uff0c \u5979\u59d0\u59d0\u4e3a\u76f8\u601d\u6240\u82e6\uff0c\u7f20\u7ef5\u5165\u9aa8\uff0c\u7adf\u81f3\u4e00\u75c5\u4e0d\u8d77\uff0c\u770b\u5230\u5979\u59d0\u59d0\u6b7b\u524d\u7684\u75db\u82e6\uff0c\u6240\u4ee5\u624d\u51b3\u5fc3\u6740\u6b7b\u8fd9 \u8d1f\u5fc3\u7684\u4eba\u3002 \u3000\u3000\u695a\u7559\u9999\u54c2\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u4e0d\u9519\uff0c\u4ed6\u9a97\u5f97\u5979\u8fd9\u9ebd\u60e8\uff0c\u5012\u771f\u4e0d\u5982\u4e00\u5200\u6740\u4e86\u5979\u53cd\u5012\u4ec1\u6148\u4e9b\uff0c\u53ef \u662f\u2026\u2026\u4f60\u662f\u600e\u9ebd\u77e5\u9053\u8fd9\u7537\u4eba\u5c31\u662f\u859b\u658c\uff1f\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u6068\u6068\u9053\uff1a\u201c\u6211\u5f53\u7136\u77e5\u9053\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u662f\u4f60\u59d0\u59d0\u544a\u8bc9\u4f60\u7684\uff1f\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u53c8\u6d41\u6cea\u9053\uff1a\u201c\u5979\u2026\u2026\u5979\u5bf9\u4ed6\u5b9e\u5728\u592a\u597d\u4e86\uff0c\u76f4\u5230\u4e34\u6b7b\u65f6\u8fd8\u4e0d\u80af\u8bf4\u51fa\u4ed6\u7684\u540d\u5b57\uff0c\u4f46\u7528 \u4e0d\u7740\u5979\u8bf4\uff0c\u6211\u4e5f\u77e5\u9053\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u9053\uff1a\u201c\u56e0\u4e3a\u59d0\u59d0\u75c5\u91cd\u7684\u65f6\u5019\uff0c\u859b\u658c\u603b\u662f\u501f\u6545\u6765\u63a2\u542c\u6d88\u606f\uff0c\u770b\u4ed6\u90a3\u79cd\u9b3c\u5934\u9b3c\u8111\u7684\u6837 \u5b50\uff0c\u6211\u5c31\u77e5\u9053\u4ed6\u6ca1\u6709\u5b89\u4ec0\u9ebd\u597d\u5fc3\u3002\u201d \u3000\u3000\u5979\u54ac\u7740\u7259\u9053\uff1a\u201c\u6211\u77e5\u9053\u4ed6\u662f\u5e0c\u671b\u6211\u59d0\u59d0\u5feb\u4e9b\u6b7b\uff0c\u4ed6\u624d\u597d\u653e\u5fc3\u8ddf\u65bd\u8335\u6210\u4eb2\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6267\u541f\u7740\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u82e5\u548c\u8fd9\u4ef6\u4e8b\u5168\u65e0\u5173\u7cfb\u53c8\u600e\u4f1a\u5bf9\u4f60\u59d0\u59d0\u7684\u75c5\u90a3\u9ebd\u5173\u5fc3\uff1f\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u9053\uff1a\u201c\u6240\u4ee5\u6211\u59d0\u59d0\u6b7b\u4e4b\u5f8c\uff0c\u6211\u5c31\u51b3\u5fc3\u6740\u4e86\u4ed6\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\u3002\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u5c31\u5230\u90a3\u91cc\u53bb\u627e\u4ed6\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u9053\uff1a\u201c\u6211\u77e5\u9053\u4ed6\u65f6\u5e38\u90fd\u5230\u90a3\u5c0f\u5c4b\u5b50\u91cc\u53bb\u7684\uff0c\u6240\u4ee5\u5c31\u5728\u90a3\u91cc\u7b49\u7740\uff0c\u7b49\u4e86\u4e24\u5929\uff0c\u679c\u7136 \u88ab\u6211\u7b49\u5230\u4e86\uff0c\u53ef\u662f\u2026\u2026\u201d \u3000\u3000\u5979\u5e7d\u7136\u63a5\u7740\u9053\uff1a\u201c\u53ef\u662f\u6211\u4e5f\u77e5\u9053\u6211\u7edd\u6ca1\u6709\u6740\u6b7b\u4ed6\u7684\u529b\u91cf\uff0c\u6240\u4ee5\u2026\u2026\u6240\u4ee5\u6211\u5c31\u2026\u2026\u201d \u3000\u3000\u695a\u7559\u9999\u6478\u4e86\u6478\u9f3b\u5b50\uff0c\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u5c31\u60f3\u5230\u4e86\u90a3\u6cd5\u5b50\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u5782\u5934\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u6211\u9664\u4e86\u7528\u90a3\u79cd\u6cd5\u5b50\u4e4b\u5916\uff0c\u6839\u672c\u5c31\u6ca1\u6709\u522b\u7684\u6cd5\u5b50\u63a5\u8fd1\u4ed6\u3002\u201d \u3000\u3000\u7f8e\u4e3d\u7684\u8eab\u4f53\u7684\u786e\u662f\u5973\u4eba\u6700\u597d\u7684\u6b66\u5668\u3002 \u3000\u3000\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u89c9\u5f97\u8fd9\u6cd5\u5b50\u592a\u5192\u9669\u4e86\u4e9b\uff1f\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u5934\u5782\u5f97\u66f4\u4f4e\uff0c\u6d41\u6cea\u9053\uff1a\u201c\u6211\u65e9\u5df2\u51c6\u5907\u6740\u4e86\u4ed6\u4e4b\u5f8c\uff0c\u81ea\u5df2\u4e5f\u4e00\u6b7b\u4e86\u4e4b\u3002\u201d \u3000\u3000\u695a\u7559\u660f\u6c89\u9ed8\u4e86\u534a\u664c\uff0c\u5ffd\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u59d0\u59d0\u662f\u5728\u90a3\u5929\u6b7b\u7684\uff1f\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u9053\uff1a\u201c\u4e5d\u6708\u4e8c\u5341\u4e03\uff0c\u7acb\u51ac\u524d\u4e00\u5929\u7684\u665a\u4e0a\uff0c\u4e5f\u5c31\u662f\u5927\u524d\u5929\u665a\u4e0a\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u90a3\u9ebd\uff0c\u5979\u73b0\u5728\u8fd8\u6ca1\u6709\u4e0b\u846c\uff1f\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u9053\uff1a\u201c\u7b2c\u4e8c\u5929\u5c31\u5df2\u7ecf\u4e0b\u846c\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u76b1\u7709\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\u8981\u5982\u6b64\u5306\u5fd9\uff1f\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u9053\uff1a\u201c\u6211\u4e8c\u53d4\u575a\u6301\u8981\u5feb\u4e9b\u5c06\u5979\u4e0b\u846c\uff0c\u4ed6\u8001\u4eba\u51db\u8bf4\u4eba\u6b7b\u4e86\u4e4b\u5f8c\uff0c\u53ea\u4e3a\u2018\u4eba\u571f\u4e3a \u5b89\u2019\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u4e8c\u53d4\uff1f\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u9053\uff1a\u201c\u6211\u7236\u6bcd\u90fd\u5df2\u53bb\u4e16\u4e86\uff0c\u4ec0\u9ebd\u4e8b\u90fd\u7531\u4e8c\u53d4\u4f5c\u4e3b\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53c8\u505c\u4e86\u534a\u664c\uff0c\u9053\uff1a\u201c\u6211\u60f3\u3002\u3002\u6211\u60f3\u5230\u4f60\u59d0\u59d0\u7684\u5893\u4e0a\u53bb\u77a7\u77a7\u3002\u201d \u3000\u3000\u79cb\u98ce\u8083\u6740\uff0c\u5df2\u5439\u5bd2\u4e86\u767d\u6768\u4e0b\u7684\u4e00\u574f\u9ec4\u571f\u3002 \u3000\u3000\u5355\u8584\u7684\u77f3\u7891\u4e0a\u5f88\u7b80\u5355\u7684\u5230\u7740\uff1a\u201c\u77f3\u67ab\u4e91\u4e4b\u5893\u3002\u201d \u3000\u3000\u4e00\u4e2a\u88ab\u9ebb\u5e26\u5b5d\u7684\u5c11\u4e2d\uff0c\u6b63\u8dea\u5728\u5893\u524d\uff0c\u54c0\u54c0\u7684\u60b2\u54ed\u7740\u3002 \u3000\u3000\u695a\u7559\u9999\u548c\u77f3\u7ee3\u4e91\u8fdc\u8fdc\u5c31\u770b\u5230\u8fd9\u5c11\u5e74\u4e86\u3002 \u3000\u3000\u77f3\u7ee3\u4e91\u8bb6\u7136\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u8c01\uff1f\u4e3a\u4ec0\u9ebd\u6765\u54ed\u6211\u59d0\u59d0\u7684\u5893\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u4e5f\u89c9\u5f97\u5f88\u5947\u602a\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u9053\u4ed6\u662f\u8c01\uff1f\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u9053\uff1a\u201c\u9664\u4e86\u4e8c\u53d4\u5916\uff0c\u6211\u4eec\u8fde\u4e2a\u4eb2\u4eba\u90fd\u6ca1\u6709\u2026\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u4f3c\u4e4e\u5df1\u88ab\u4ed6\u4eec\u7684\u811a\u6b65\u58f0\u60ca\u52a8\uff0c\u7a81\u7136\u8df3\u4e86\u8d77\u6765\uff0c\u7528\u53c8\u624b\u63a9\u7740\u8138\u98de\u4e5f\u4f3c\u7684\u8dd1\u8d70\u3002 \u3000\u3000\u4ed6\u8eab\u6cd5\u5c45\u7136\u5f88\u5feb\uff0c\u770b\u6765\u8f7b\u529f\u7684\u6839\u57fa\u5f88\u4e0d\u9517\u3002 \u3000\u3000\u4f46\u6ca1\u6709\u4eba\u80fd\u4efb\u695a\u7559\u9999\u9762\u524d\u8dd1\u6389\u7684\u3002\u695a\u7559\u9999\u8eab\u5f62\u95ea\uff0c\u5df2\u6321\u4f4f\u4ed6\u9762\u524d\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u4ece\u672a\u89c1\u8fc7\u8eab\u6cd5\u8fd9\u9ebd\u5feb\u7684\u4eba\uff0c\u7b80\u76f4\u662f\u5feb\u5982\u9b3c\u9b45\uff0c\u4e00\u60ca\u4e4b\u4e0b\uff0c\u8138\u8272\u90fd\u9ec4\u4e86\uff0c\u51fa\u58f0\u9053\uff1a \u201c\u6c42\u6c42\u4f60\uff0c\u8ba9\u6211\u8d70\u5427\uff0c\u6211\u5e76\u6ca1\u6709\u505a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u65e2\u7136\u6ca1\u6709\u505a\u4ec0\u9ebd\u4e8b\uff0c\u4e3a\u4f55\u8981\u9003\u5462\uff1f\u201d \u3000\u3000\u8fd9\u5c11\u5e74\u9053\uff1a\u201c\u6211\u3002\u3002\u3002\u6211\u3002\u2026\u201d \u3000\u3000\u7a81\u7136\u51fa\u624b\u4e00\u62f3\uff0c\u5411\u695a\u7559\u9999\u80f8\u819b\u51fb\u51fa\u3002 \u3000\u3000\u8fd9\u62f3\u5c45\u7136\u4e5f\u5f88\u5feb\uff0c\u770b\u6765\u4ed6\u6b66\u529f\u7684\u6839\u57fa\u4e5f\u5f88\u4e0d\u9519\u3002 \u3000\u3000\u4f46\u9664\u4e86\u64a4\u5a07\u7684\u5973\u5b69\u5b50\u5916\uff0c\u53c8\u6709\u8c01\u7684\u62f3\u5934\u80fd\u6253\u5f97\u4e0a\u695a\u7559\u9999\u7684\u80f8\u819b\uff1f \u3000\u3000\u695a\u7559\u9999\u53c8\u4e00\u95ea\uff0c\u4f38\u624b\u5c31\u62ff\u4efb\u4e86\u4ed6\u7684\u8155\u8109\u3002 \u3000\u3000\u8fd9\u65f6\u77f3\u7ee3\u4e91\u4e5f\u5df2\u8d76\u4e86\u8fc7\u6765\uff0c\u8fd9\u5c11\u5e74\u771f\u6068\u4e0d\u5f97\u5c06\u81ea\u5df1\u7684\u5934\u85cf\u5230\u88e4\u6863\u91cc\u53bb\uff0c\u4f46\u77f3\u7ee3\u4e91\u8fd8\u662f\u770b \u5230\u4e86\u4ed6\uff0c\u5931\u58f0\u9053\uff1a\u201c\u662f\u4f60\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u8ba4\u5f97\u4ed6\uff1f\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u9053\uff1a\u201c\u4ed6\u662f\u859b\u658c\u7684\u4e66\u7ae5\uff0c\u5c0f\u65f6\u5019\u4e5f\u5e38\u8ddf\u7740\u5230\u6211\u5bb6\u53bb\u7684\u3002\u201d \u3000\u3000\u5979\u77aa\u7740\u90a3\u5c11\u5e74\uff0c\u9053\uff1a\u201c\u501a\u5251\uff0c\u6211\u95ee\u4f60\uff0c\u4f60\u614c\u91cc\u614c\u5f20\uff0c\u9b3c\u9b3c\u795f\u795f\u7a76\u7adf\u5728\u5e72\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u501a\u5251\u4f3c\u4e4e\u521a\u6d41\u8fc7\u6cea\uff0c\u6b64\u523b\u5374\u5728\u6d41\u7740\u51b7\u6c57\uff0c\u52c9\u5f3a\u8d54\u7b11\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u6ca1\u6709\u5440\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u9053\uff1a\u201c\u6211\u59d0\u59d0\u6b7b\u4e86\uff0c\u4e3a\u4ec0\u9ebd\u8981\u4f60\u6765\u62ab\u9ebb\u6302\u5b5d\uff1f\u201d \u3000\u3000\u501a\u5251\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u2026\u3002\u201d \u3000\u3000\u4ed6\u4f3c\u4e4e\u5ffd\u7136\u7075\u673a\u4e00\u52a8\uff0c\u7acb\u523b\u5927\u58f0\u9053\uff1a\u201c\u77f3\u6559\u5e08\u4e00\u5411\u5bf9\u6211\u5f88\u597d\uff0c\u77f3\u59d1\u5a18\u53bb\u4e16\uff0c\u6211\u81ea\u7136\u8981\u5c3d \u5c3d\u5fc3\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u9053\uff1a\u201c\u90a3\u9ebd\u6211\u7236\u4eb2\u53bb\u4e16\u7684\u65f6\u5019\uff0c\u4f60\u4e3a\u4ec0\u9ebd\u6ca1\u6709\u62ab\u9ebb\u5e26\u5b5d\u5462\uff1f\u201d \u3000\u3000\u501a\u5251\u6014\u4f4f\u4e86\uff0c\u6ee1\u5934\u5927\u6c57\u5982\u96e8\u800c\u843d\u3002 \u3000\u3000\u77f3\u7ee3\u4e91\u5ffd\u7136\u4e00\u628a\u63ea\u4f4f\u4e86\u4ed6\u7684\u5934\u53d1\uff0c\u560e\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u96be\u9053\u6562\u5bf9\u59d0\u59d0\u2026\u2026\u3002\u201d \u3000\u3000\u5979\u8bdd\u672b\u8bf4\u5b8c\uff0c\u501a\u5251\u5df2\u8dea\u4e86\u4e0b\u53bb\uff0c\u4ee5\u9996\u987f\u5730\uff0c\u5636\u58f0\u9053\uff1a\u201c\u6211\u8be5\u6b7b\uff0c\u6c42\u59d1\u5a18\u9976\u6211\u6211\u8be5 \u6b7b\u2026\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u77aa\u7740\u4ed6\uff0c\u8eab\u5b50\u53c8\u98a4\u6296\u8d77\u6765\uff0c\u5ffd\u7136\u72c2\u543c\u9053\uff1a\u201c\u6211\u6740\u4e86\u4f60\u2026\u2026\u6211\u6740\u4e86\u4f60\u2026\u2026\u201d \u3000\u3000\u4f46\u695a\u7559\u9999\u5df2\u63e1\u4f4f\u4e86\u5979\u7684\u624b\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u65e0\u8bba\u5982\u4f55\uff0c\u4ed6\u8fd9\u9ebd\u505a\u603b\u662f\u51fa\u65bc\u8bda\u5fc3\uff0c\u6211\u82e5\u6b7b\u4e86\uff0c \u82e5\u6709\u4eba\u80af\u4e3a\u6211\u62ab\u9ebb\u5e26\u5b5d\uff0c\u6211\u4e5f\u5c31\u6b7b\u5f97\u5f88\u5b89\u5fc3\u4e86\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u2026\u3002\u201d\u4ed6\u600e\u9ebd\u80fd\u5bf9\u6211\u59d0\u59d0\u2026\u2026\u6211\u59d0\u600e\u9ebd\u4f1a\u5bf9\u4ed6\u2026\u2026\u201c \u3000\u3000\u5979\u53c8\u6025\uff0c\u53c8\u6012\uff0c\u8fde\u8bdd\u90fd\u4e0d\u8bf4\u6e05\u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\u54c2\u9053\uff1a\u201c\u4f60\u83ab\u5fd8\u4e86\uff0c\u4ed6\u4e5f\u662f\u4eba\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u5ffd\u7136\u653e\u58f0\u54ed\u4e86\u8d77\u6765\uff0c\u8dfa\u7740\u811a\u9053\uff1a\u201c\u6211\u9519\u4e86\uff0c\u6211\u5f04\u9519\u4e86\uff0c\u6211\u4e0d\u8be5\u53bb\u627e\u859b\u658c\uff0c\u6211\u600e\u9ebd \u80fd\u5728\u4ed6\u9762\u524d\u90a3\u9ebd\u4e22\u4eba\uff1f\u6211\u4ee5\u5f8c\u8fd8\u6709\u4ec0\u9ebd\u8138\u89c1\u4eba\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u8f7b\u8f7b\u6402\u4f4f\u4e86\u5979\uff0c\u4ed6\u7684\u624b\u81c2\u662f\u90a3\u9ebd\u6e29\u67d4\uff0c\u90a3\u9ebd\u575a\u5f3a\u3002\u65e0\u8bba\u591a\u9ebd\u60b2\u4f24\uff0c\u591a\u9ebd\u7d0a\u4e71\u7684\u5fc3 \u5728\u8fd9\u91cc\u90fd\u4f3c\u80fd\u83b7\u5f97\u5e73\u9759\u3002 \u3000\u3000\u501a\u5251\u4ecd\u7136\u8dea\u5728\u5730\u4e0a\uff0c\u6d41\u7740\u6cea\u3002 \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5979\u6b7b\u4e86\u4f60\u5982\u6b64\u4f24\u5fc3\uff0c\u5979\u6d3b\u7740\uff0c\u4f60\u4e3a\u4f55\u4e0d\u5bf9\u5979\u597d\u4e9b\uff1f\u201d \u3000\u3000\u501a\u5251\u6d41\u6cea\u9053\uff1a\u201c\u5c0f\u6c11\u4e0d\u6562\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e0d\u6562\uff1f\u4e3a\u4ec0\u9ebd\u4e0d\u6562\uff1f\u201d \u3000\u3000\u501a\u5251\u9053\uff1a\u201c\u6211\u662f\u4e2a\u4f4e\u53c1\u4e0b\u56db\u7684\u4eba\uff0c\u6211\u914d\u4e0d\u4e0a\u5979\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u5b81\u53ef\u773c\u770b\u7740\u5979\u4e3a\u4f60\u800c\u6b7b\uff1f\u201d \u3000\u3000\u501a\u5251\u75db\u54ed\u5931\u58f0\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u5979\u4f1a\u8fd9\u6837\uff0c\u6211\u4e5f\u4e0d\u77e5\u9053\u5979\u5bf9\u6211\u8fd9\u9ebd\u597d\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u65e0\u8bba\u600e\u9ebd\u6837\uff0c\u5979\u75c5\u91cd\u7684\u65f6\u5019\uff0c\u4f60\u4eba\u603b\u8be5\u53bb\u770b\u770b\u5979\u7684\u3002\u201d \u3000\u3000\u501a\u5251\u9053\uff1a\u201c\u662f\u5979\u53eb\u6211\u83ab\u8981\u53bb\u627e\u5979\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53c8\u9053\uff1a\u201c\u8fd9\u5973\u5b69\u5b50\u82e5\u8981\u4f60\u83ab\u53bb\u627e\u5979\uff0c\u5979\u7684\u610f\u601d\u4e5f\u8bb8\u5c31\u662f\u8981\u4f60\u53bb\u627e\u5979\uff0c\u4f60\u82e5\u8fde\u8fd9\u9053 \u7406\u90fd\u4e0d\u660e\u767d\uff0c\u600e\u9ebd\u80fd\u505a\u7537\u4eba\uff1f\u201d \u3000\u3000\u501a\u5251\u6014\u4e86\u6014\uff0c\u5403\u5403\u9053\uff1a\u201c\u4f46\u5979\u8bf4\u5979\u6c38\u8fdc\u4e5f\u4e0d\u8981\u518d\u89c1\u6211\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\uff1a\u201c\u90a3\u662f\u56e0\u4e3a\u5979\u89c9\u5f97\u4f60\u592a\u6ca1\u6709\u52c7\u6c14\uff0c\u6240\u4ee5\u624d\u6545\u610f\u8fd9\u9ebd\u8bf4\u7684\uff0c\u4f60\u82e5\u771f\u7684\u7231\u5979\uff0c \u5c31\u8be5\u9f13\u8d77\u52c7\u6c14\u5411\u5979\u6c42\u4eb2\u3002\u201d \u3000\u3000\u501a\u5251\u9053\uff1a\u201c\u5979\u82e5\u771f\u6709\u8fd9\u610f\u601d\uff0c\u4e3a\u4ec0\u9ebd\u4e0d\u8bf4\u51fa\u6765\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u82e6\u7b11\u9053\uff1a\u201c\u5979\u82e5\u80af\u8bf4\u51fa\u6765\uff0c\u5c31\u4e0d\u662f\u5973\u5b50\u4e86\u3002\u201d \u3000\u3000\u501a\u5251\u6014\u4e86\u534a\u664c\uff0c\u5ffd\u7136\u5c06\u5934\u649e\u5728\u5730\u4e0a\uff0c\u75c5\u54ed\u7740\u9053\uff1a\u201c\u67ab\u4e91\uff0c\u6211\u8be5\u6b7b\uff0c\u6211\u662f\u4e2a\u6df7\u86cb\uff0c\u662f\u4e2a\u5446 \u5b50\u2026\u2026\u53ef\u662f\u4f60\u4e3a\u4ec0\u9ebd\u8981\u8fd9\u6837\u505a\uff0c\u4f60\u4e0d\u4f46\u5bb3\u82e6\u4e86\u6211\uff0c\u4e5f\u5bb3\u4e86\u81ea\u5df1\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5176\u5b9e\u4f60\u4e5f\u7528\u4e0d\u7740\u96be\u53d7\uff0c\u5728\u81ea\u5df1\u559c\u6b22\u7684\u5973\u4eba\u9762\u524d\uff0c\u6bcf\u4e2a\u7537\u4eba\u90fd\u4f1a \u53d8\u6210\u5446\u5b50\u7684\u3002\u201d \u3000\u3000\u770b\u7740\u4e00\u4e2a\u5927\u7537\u4eba\u5728\u81ea\u5df1\u9762\u524d\u53f7\u6dd8\u5927\u54ed\uff0c\u5b9e\u5728\u4e0d\u662f\u4e00\u4ef6\u6109\u5feb\u7684\u4e8b\uff0c\u7b49\u501a\u5251\u54ed\u58f0\u505c\u4e0b\u6765\u7684\u65f6 \u5019\uff0c\u695a\u7559\u9999\u5c31\u7acb\u523b\u9053\uff1a\u201c\u6211\u60f3\u8bf7\u4f60\u505a\u4ef6\u4e8b\uff0c\u4e0d\u77e5\u9053\u4f60\u80af\u4e0d\u80af\u7b54\u5e94\uff1f\u201d \u3000\u3000\u501a\u5251\u62bd\u62c9\u7740\u9053\uff1a\u201c\u4f60\u662f\u4e2a\u597d\u4eba\uff0c\u65e0\u8bba\u4f60\u8981\u6211\u505a\u4ec0\u9ebd\uff0c\u6211\u90fd\u7b54\u5e94\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u8bf7\u4f60\u8f6c\u8fbe\u859b\u516c\u5b50\uff0c\u5c31\u8bf4\u6211\u5927\u5f8c\u5929\u665a\u4e0a\u5728\u90a3\u5c0f\u5c4b\u7b49\u4ed6\uff0c\u5e0c\u671b\u4ed6\u6765\u4e0e\u6211\u89c1\u89c1 \u9762\u3002\u201d \u3000\u3000\u501a\u5251\u9053\uff1a\u201c\u53ef\u662f\u3002\u2026\u6211\u5bb6\u516c\u5b50\u600e\u77e5\u9053\u4f60\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u53eb\u695a\u7559\u9999\u3002\u201d \u3000\u3000\u501a\u5251\u5c31\u50cf\u662f\u5ffd\u7136\u541e\u4e0b\u4e2a\u719f\u9e21\u86cb\uff0c\u6574\u4e2a\u4eba\u90fd\u98a4\u4f4f\u4e86\uff0c\u8fde\u6c14\u90fd\u5598\u4e0d\u8fc7\u6765\u3002 \u3000\u3000\u4ed6\u7741\u5927\u4e86\u773c\u775b\uff0c\u5f20\u5927\u4e86\u5634\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u624d\u957f\u957f\u5410\u51fa\u53e3\u6c14\uff0c\u5403\u5403\u9053\uff1a\u201c\u4f60\u8001\u4eba\u5bb6\u5c31\u662f\u695a\u9999 \u5e05\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u5c31\u662f\u695a\u7559\u9999\uff0c\u4f46\u5374\u5e76\u4e0d\u8001\u3002\u201d\u74dc \u3000\u3000\u501a\u5251\u7528\u8896\u5b50\u64e6\u4e86\u64e6\u9f3b\u6d95\uff0c\u5583\u5583\u5730\u8bf4\u9053\uff1a\u201c\u65e9\u77e5\u4f60\u8001\u4eba\u5bb6\u5c31\u662f\u695a\u7559\u9999\uff0c\u65b9\u5c31\u7b97\u6740\u4e86\u6211\uff0c\u6211 \u4e5f\u4e0d\u6562\u51fa\u624b\u4e86\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u8fd9\u65f6\u5f20\u5927\u4e86\u773c\u775b\u75f4\u75f4\u7684\u671b\u7740\u695a\u7559\u9999\uff0c\u7b49\u501a\u5251\u8d70\u4e86\uff0c\u624d\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u539f\u6765 \u4f60\u8fd9\u9ebd\u6709\u540d\u2026\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u82e6\u7b11\u9053\uff1a\u201c\u6709\u540d\u5e76\u4e0d\u662f\u4ef6\u597d\u4e8b\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u5782\u4e0b\u4e86\u5934\uff0c\u671b\u7740\u81ea\u5df1\u7684\u811a\uff0c\u671b\u7740\u80f8\u4e0a\u7684\u90a3\u5757\u4e1d\u5dfe\uff0c\u4e5f\u4e0d\u77e5\u5728\u60f3\u4ec0\u9ebd\u7adf\u60f3\u5f97\u51fa\u4e86 \u795e\u3002 \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u4e5f\u60f3\u6c42\u4f60\u4e00\u4ef6\u4e8b\uff0c\u4e0d\u77e5\u4f60\u80af\u4e0d\u80af\u7b54\u5e94\uff1f\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u8f7b\u8f7b\u9053\uff1a\u201c\u4f60\u8bf4\u5427\uff0c\u65e0\u8bba\u4ec0\u9ebd\u4e8b\u6211\u90fd\u80af\u7b54\u5e94\u4f60\u3002\u201d \u3000\u3000\u5979\u4f3c\u4e4e\u5ffd\u7136\u53d1\u89c9\u81ea\u5df1\u8fd9\u53e5\u8bdd\u8bf4\u5f97\u6709\u4e9b\u8bed\u75c5\uff0c\u9762\u8272\u53c8\u98de\u7ea2\u4e86\u8d77\u6765\uff0c\u5728\u6e10\u5df2\u897f\u659c\u7684\u9633\u5149\u4e0b\uff0c \u770b\u6765\u5c31\u50cf\u662f\u4e00\u6735\u6d77\u68e0\u3002 \u3000\u3000\u695a\u7559\u9999\u5fc3\u91cc\u4e5f\u4e0d\u7981\u6cdb\u8d77\u4e86\u9635\u6d9f\u6f5e\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u90a3\u9ebd\u4f60\u8d76\u5feb\u56de\u5bb6\u597d\u597d\u7761\u4e00\u89c9\uff0c\u5c06\u8fd9\u6240\u6709\u7684 \u4e00\u5207\u4e8b\u90fd\u6682\u65f6\u5fd8\u8bb0\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u9053\uff1a\u201c\u4f60\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u8fd8\u8981\u53bb\u529e\u4e9b\u4e8b\uff0c\u7b49\u5230\u2026\u2026\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u5ffd\u7136\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u5927\u58f0\u9053\uff1a\u201c\u5176\u5b9e\u4f60\u7528\u4e0d\u7740\u8d76\u6211\u8d70\uff0c\u6211\u4e5f\u4e0d\u4f1a\u7f20\u4f4f\u4f60\u7684\uff0c\u6211\u81f3 \u5c11\u8fd8\u6ca1\u6709\u4f60\u60f3\u50cf\u4e2d\u90a3\u9ebd\u2026\u2026\u4e0d\u8981\u8138\u2026\u2026\u201d \u3000\u3000\u5979\u867d\u7136\u5728\u52c9\u5f3a\u63a7\u5236\u7740\u81ea\u5df1\uff0c\u8bed\u58f0\u8fd8\u662f\u4e0d\u514d\u5df2\u6709\u4e9b\u54fd\u54bd\uff0c\u521a\u4e7e\u4e86\u7684\u773c\u6cea\u53c8\u6d41\u4e86\u4e0b\u6765\uff0c\u8bdd\u6ca1 \u6709\u8bf4\u5b8c\uff0c\u5c31\u626d\u5934\u98de\u5954\u4e86\u51fa\u53bb\uff0c\u53ef\u662f\u8fd8\u6ca1\u6709\u5954\u51fa\u51e0\u6b65\uff0c\u811a\u4e0b\u4e00\u4e2a\u8e09\u8dc4\uff0c\u53c8\u8dcc\u5012\u5728\u5730\u4e0a\u3002 \u3000\u3000\u695a\u7559\u9999\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u9ebd\u8981\u8bf4\u8fd9\u79cd\u8bdd\uff1f\u4f60\u53ef\u77e5\u9053\uff0c\u5c31\u7b97\u4f60\u4e0d\u7f20\u4f4f\u6211\uff0c\u6211\u4e5f\u8981\u7f20\u4f60 \u7684\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u6d41\u7740\u6cea\u8bf4\u9053\uff1a\u201c\u4f60\u4e5f\u7528\u4e0d\u7740\u6765\u9a97\u6211\uff0c\u505a\u4f60\u8fd9\u6837\u7684\u540d\u4eba\uff0c\u81ea\u7136\u4e0d\u4f1a\u613f\u610f\u548c\u6211\u8fd9\u6837\u7684 \u5973\u5b69\u5b50\u6765\u5f80\uff0c\u4f60\u2026\u2026\u4f60\u8d70\u5427\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u4fef\u4e0b\u8eab\uff0c\u8f7b\u629a\u7740\u5979\u7684\u67d4\u53d1\uff0c\u9053\uff1a\u201c\u8c01\u8bf4\u6211\u4e0d\u613f\u548c\u4f60\u6765\u5f80\uff0c\u6211\u4e00\u76f4\u60f3\u7ea6\u4f60\u4eca\u5929\u665a\u4e0a \u5728\u8fd9\u91cc\u89c1\u9762\uff0c\u53ef\u60dc\u4f60\u4e0d\u7b49\u6211\u8bf4\u5b8c\u8bdd\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u6014\u4e86\u6014\uff0c\u773c\u6cea\u4e0d\u518d\u6d41\u4e86\uff0c\u5934\u5374\u5782\u5f97\u66f4\u4f4e\uff0c\u5e7d\u5e7d\u9053\uff1a\u201c\u73b0\u5728\u6211\u65e2\u7136\u5df2\u8ddf\u4f60\u53d1\u4e86\u813e \u6c14\uff0c\u4f60\u81ea\u7136\u4e0d\u613f\u610f\u518d\u548c\u6211\u89c1\u9762\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u6211\u548c\u4f60\u4e00\u6837\uff0c\u4e5f\u4f1a\u53d1\u5b69\u5b50\u813e\u6c14\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u8e1e\u8d77\u4e86\u5634\uff0c\u9053\uff1a\u201c\u8c01\u8bf4\u6211\u662f\u5b69\u5b50\uff1f\u4f60\u770b\u6211\u8fd8\u662f\u5b69\u5b50\u9ebd\uff1f\u201d \u3000\u3000\u4efb\u4f55\u4eba\u90fd\u53ef\u4ee5\u770b\u51fa\u5979\u4e0d\u518d\u662f\u5b69\u5b50\u4e86\uff0c\u5c31\u7b97\u662f\u5b69\u4e86\u4e5f\u53ef\u4ee5\u611f\u89c9\u5f97\u51fa\uff0c\u5979\u81ea\u5df1\u4e5f\u660e\u767d\u8fd9\u70b9\u3002 \u6545\u610f\u6df1\u6df1\u5438\u4e86\u53e3\u6c14\u4f3c\u4e4e\u60f3\u8bc1\u5b9e\u81ea\u5df1\u7684\u8bdd\uff0c\u53c8\u4f3c\u4e4e\u5728\u5411\u695a\u7559\u9999\u793a\u5a01\uff0c\u90a3\u4e30\u6ee1\u7684\u80f8\u819b\u51e0\u4e4e\u5df2\u6da8\u7834 \u4e86\u8863\u670d\u3002 \u3000\u3000\u695a\u7559\u9999\u6478\u4e86\u6a21\u9f3b\u5b50\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u81ea\u7136\u4e5f\u662f\u5927\u4eba\u4e86\uff0c\u6240\u4ee5\u5c31\u8be5\u50cf\u5927\u4eba\u4e00\u6837\uff0c\u83ab\u8981\u4e71\u53d1\u813e\u6c14 \u4e5f\u83ab\u8981\u518d\u80e1\u601d\u4e71\u60f3\u2026\u2026\u201d \u3000\u3000\u4ed6\u76ee\u5149\u81ea\u5979\u7684\u80f8\u819b\u671b\u4e0b\uff0c\u843d\u5728\u5979\u7ea4\u5de7\u7684\u8e1d\u4e0a\uff0c\u5305\u5728\u5979\u7ea4\u8db3\u4e0a\u7684\u4e1d\u5370\uff0c\u53c8\u6e17\u51fa\u4e86\u4e00\u4e1d\u4e1d \u8840\u3002 \u3000\u3000\u695a\u7559\u9999\u5fcd\u4e0d\u4f4f\u53c8\u9053\uff1a\u201c\u4f60\u7684\u811a\u82e5\u8fd8\u5728\u75bc\uff0c\u6211\u2026\u2026\u6211\u62b1\u4f60\u56de\u53bb\u597d\u4e0d\u597d\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u9053\uff1a\u201c\u4f60\u82e5\u62b1\u6211\u56de\u5bb6\uff0c\u4ee5\u5f8c\u53ea\u6015\u5c31\u8981\u522b\u4eba\u62b1\u4f60\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u201c\u5509\u561b\u201d\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4e8c\u53d4\u82e5\u770b\u5230\u4f60\u62b1\u6211\u56de\u5bb6\uff0c\u4e0d\u6253\u65ad\u4f60\u7684\u817f\u624d\u602a\u3002\u201d\u5979\u5a07\u7b11 \u7740\u81ea\u695a\u7559\u9999\u8eab\u65c1\u8df3\u5f00\uff0c\u5ffd\u53c8\u56de\u5934\u7b11\u9053\uff1a\u201c\u83ab\u5fd8\u4e86\uff0c\u4eca\u5929\u665a\u4e0a\uff01\u201d\u8fd9\u6b21\u5979\u8dd1\u5f97\u5f88\u5feb\uff0c\u4e5f\u6ca1\u6709\u6454 \u8de4\u3002\u5979\u7684\u811a\u4f3c\u5df2\u4e0d\u75db\u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\u671b\u7740\u5979\u7ea4\u7ec6\u7684\u8170\u8eab\uff0c\u98de\u626c\u7684\u9ed1\u53d1\uff0c\u5fcd\u4e0d\u4f4f\u5f80\u81ea\u5df1\u7684\u9f3b\u5b50\u91cd\u91cd\u7684\u634f\u4e86\u4e0b\uff0c\u82e6\u7b11\u7740 \u9053\uff1a\u201c\u695a\u7559\u9999\u5440\u695a\u7559\u9999\uff0c\u770b\u6765\u4f60\u7684\u75c5\u5df2\u8d8a\u6765\u8d8a\u91cd\u4e86\u3002\u201d \u3000\u3000\u4ed6\u56fa\u5df2\u5f88\u660e\u767d\u81ea\u5df1\u7684\u6bdb\u75c5\uff0c\u90a3\u5c31\u662f\u4e00\u9047\u89c1\u7f8e\u4e3d\u7684\u5973\u5b69\u5b50\uff0c\u4ed6\u7684\u5fc3\u5c31\u8f6f\u4e86\uff0c\u968f\u4fbf\u600e\u9ebd\u6837\u4e5f \u6273\u4e0d\u8d77\u8138\u6765\u8bf4\u8bdd\u3002\u4e5f\u4e0d\u77e5\u4e3a\u4e86\u4ec0\u9ebd\uff0c\u4e5f\u8bb8\u662f\u56e0\u4e3a\u4ed6\u7684\u8fd0\u6c14\u592a\u597d\uff0c\u4e5f\u8bb8\u662f\u56e0\u4e3a\u4ed6\u8fd0\u6c14\u592a\u574f\uff0c\u4ed6 \u65f6\u5e38\u603b\u662f\u4f1a\u9047\u89c1\u4e00\u4e9b\u7f8e\u4e3d\u7684\u5973\u5b69\u5b50\u3002 \u3000\u3000\u6700\u8981\u547d\u7684\u662f\uff0c\u8fd9\u4e9b\u5973\u5b69\u5b50\u4e5f\u90fd\u5f88\u559c\u6b22\u4ed6\u3002 \u3000\u3000\u695a\u7559\u9999\u7b97\u51c6\u859b\u7ea2\u7ea2\u548c\u859b\u658c\u90fd\u5df2\u8d70\u4e86\u3002\u65bc\u662f\u4ed6\u53c8\u56de\u5230\u90a3\u5c0f\u5c4b\uff0c\u5c0f\u5c4b\u679c\u7136\u7a7a\u65e0\u4eba\u8ff9\u5012\u4e86\u7684\u6905 \u5b50\u4e5f\u6ca1\u6709\u6309\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u5c31\u50cf\u9057\u843d\u4e86\u4ec0\u9ebd\u4e1c\u897f\u4f3c\u7684\uff0c\u5728\u5c4b\u5b50\u91cc\u641c\u7d22\u4e86\u5f88\u4e45\uff0c\u8868\u60c5\u770b\u6765\u5f88\u5931\u671b\u3002\u663e\u7136\u4ec0\u9ebd\u4e5f\u6ca1\u6709 \u627e\u7740\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u6709\u4e2a\u5f88\u5927\u7684\u94c1\u706b\u7089\uff0c\u73b0\u5728\u8fd8\u662f\u79cb\u5929\uff0c\u8fd9\u706b\u7089\u81ea\u7136\u5df2\u6709\u5f88\u4e45\u6ca1\u6709\u7528\u8fc7\u4e86\u3002\u7089\u5b50\u4e0a\u5374 \u8fde\u4e00\u70b9\u7070\u5c18\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u695a\u7559\u9999\u773c\u775b\u4e00\u4eae\uff0c\u6253\u5f00\u4e86\u7089\u95e8\u5c31\u53d1\u73b0\u7089\u91cc\u5b50\u85cf\u6709\u5c0f\u94c1\u7bb1\uff0c\u7bb1\u91cc\u88c5\u7684\u7adf\u90fd\u662f\u5973\u5b50\u68b3\u5986\u7684\u82b1 \u7c89\u3002 \u3000\u3000\u8fd9\u5c0f\u5c4b\u672c\u662f\u4e2a\u5f88\u7537\u6027\u5316\u7684\u5730\u65b9\uff0c\u53ea\u6709\u8fd9\u94c1\u7bb1\u5374\u663e\u7136\u662f\u5973\u5b50\u4e4b\u7269\uff0c\u91cc\u9762\u6bcf\u6837\u4e1c\u897f\u90fd\u5f88\u7cbe \u81f4\uff0c\u6709\u4e2a\u5c0f\u5c0f\u7684\u83f1\u82b1\u955c\uff0c\u4e24\u67c4\u6a80\u9999\u6728\u7684\u68b3\u5b50\uff0c\u51e0\u76d2\u7c89\u4e5f\u90fd\u662f\u5f88\u4e0a\u7b49\u7684\u54c1\u8d28\uff0c\u8fd9\u4e9b\u4e1c\u897f\u7684\u4e3b\u4eba \u60f3\u5fc5\u662f\u4e2a\u5f88\u8bb2\u7a76\u4fee\u9970\u7684\u5973\u5b50\uff0c\u8eab\u4efd\u4e5f\u4e00\u5b9a\u4e0d\u4f4e\uff0c\u5426\u5219\u5c31\u7528\u4e0d\u8d77\u8fd9\u9ebd\u8d35\u7684\u4e1c\u897f\u3002 \u3000\u3000\u4e00\u4e2a\u548c\u522b\u4eba\u5e7d\u4f1a\u8fc7\u7684\u5973\u5b50\uff0c\u81ea\u7136\u5f88\u9700\u8981\u68b3\u641e\u5934\u53d1\uff0c\u62b9\u62b9\u53d1\u814a\uff0c\u5c06\u81ea\u5df1\u91cd\u65b0\u6253\u626e\u6253\u626e\uff0c\u624d \u597d\u56de\u53bb\u89c1\u81ea\u5df2\u7684\u4e08\u592b\u3002 \u3000\u3000\u4f46\u8fd9\u94c1\u7bb1\u5b50\u5374\u7edd\u4e0d\u662f\u82b1\u91d1\u5f13\u7684\uff0c\u66f4\u4e0d\u662f\u859b\u7ea2\u7ea2\u7684\uff0c\u56e0\u4e3a\u4ed6\u4eec\u8eab\u4e0a\u7684\u9999\u6c14\u5f88\u6d53\u90c1\u8fd9\u4e9b\u82b1\u7c89 \u7684\u9999\u6c14\u5374\u5f88\u6e05\u96c5\u3002 \u3000\u3000\u90a3\u9ebd\uff0c\u662f\u8c01\u628a\u8fd9\u94c1\u7bb1\u5b50\u85cf\u5728\u8fd9\u91cc\u7684\u5462\uff1f \u3000\u3000\u695a\u7559\u9999\u7528\u624b\u6307\u6cbe\u4e86\u4e9b\u82b1\u7c89\uff0c\u62b9\u5728\u9f3b\u5b50\u4e0a\uff0c\u4ed4\u7ec6\u55c5\u4e86\u5f88\u4e45\uff0c\u5634\u89d2\u6e10\u6e10\u7f00\u51fa\u4e86\u4e1d\u6ee1\u610f\u7684\u5fae \u7b11\u2026\u2026 \u3000\u3000\u95e8\u662f\u5f00\u7740\u7684\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\u7a81\u7136\u6709\u4e2a\u4eba\u81ea\u95e8\u5916\u63a0\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u4ed6\u7a7f\u7740\u7d27\u8eab\u7684\u8863\uff0c\u4ee5\u9ed1\u5dfe\u8499\u9762\uff0c\u8eab\u6cd5\u5feb\u5982\u6025\u98ce\uff0c\u8f7b\u5982\u98de\u7d6e\uff0c\u624b\u4e2d\u4e00\u67c4\u957f\u5251\u66f4\u6025\u5982\u95ea\u7535\u3002 \u3000\u3000\u957f\u5251\u95f2\u7535\u822c\u523a\u5411\u695a\u7559\u9999\u7684\u80cc\u5fc3\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u4e4b\u5feb\uff0c\u7eb5\u7136\u662f\u8fce\u9762\u523a\u6765\u7684\u4e16\u4e0a\u53ea\u6015\u4e5f\u5f88\u5c11\u6709\u4eba\u80fd\u95ea\u907f\u5f97\u5f00\uff0c\u4f55\u51b5\u662f\u81ea\u80cc\u5f8c\u6697\u7b97\u3002 \u3000\u3000\u695a\u7559\u9999\u53ea\u89c9\u80cc\u5fc3\u5bd2\u5251\u98ce\u523a\u8033\uff0c\u518d\u60f3\u95ea\u907f\uff0c\u5df2\u6765\u4e0d\u53ca\u4e86\u3002\u5251\u5c16\u5df2\u523a\u5165\u4ed6\u7684\u80cc\u810a\u3002 \u7b2c\u516d\u7ae0\u3000\u6b7b\u91cc\u9003\u751f>> \u53e4\u9f99\u300a\u695a\u7559\u9999\u7cfb\u5217\xb7\u9b3c\u604b\u4f20\u5947\u300b \u7b2c\u516d\u7ae0\u3000\u6b7b\u91cc\u9003\u751f \u3000\u3000\u4e00\u9635\u5c16\u9510\u7684\u75db\u52bf\uff0c\u76f4\u900f\u5165\u695a\u7559\u9999\u7684\u5fc3\u5e95\u3002 \u3000\u3000\u4ed6\u8eab\u4e0a\u6bcf\u4e00\u5757\u808c\u8089\uff0c\u5168\u90fd\u751f\u51fa\u4e86\u4e00\u79cd\u5267\u70c8\u7684\u53cd\u5e94\uff0c\u8eab\u5b50\u4e5f\u7acb\u523b\u98de\u63a0\u800c\u8d77\u51cc\u7a7a\u4e00\u4e2a\u7ffb\u8eab\uff0c \u53cd\u624b\u5c06\u4e24\u76d2\u82b1\u7c89\u64a4\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u4e00\u5251\u5f97\u624b\uff0c\u7b2c\u4e8c\u5251\u53c8\u5f85\u523a\u51fa\u7a81\u89c1\u4e00\u7247\u6d45\u7ea2\u8272\u7684\u7c89\u96fe\u81ea\u695a\u7559\u9999\u624b\u91cc\u64de\u4e86\u51fa\u6765\u9f3b\u5b50\u91cc \u4e5f\u55c5\u5230\u4e86\u4e00\u9635\u6de1\u6de1\u7684\u9999\u6c14\u3002 \u3000\u3000\u4ed6\u5927\u60ca\u4e4b\u4e0b\uff0c\u7acb\u523b\u95ed\u8d77\u773c\u775b\uff0c\u638c\u4e2d\u5251\u5316\u4e3a\u4e00\u7247\u5149\u5e55\uff0c\u62a4\u4f4f\u4e86\u5168\u8eab\uff0c\u5012\u9000\u516b\u5c3a\uff0c\u9000\u5230\u95e8 \u53e3\u3002 \u3000\u3000\u7b49\u4ed6\u518d\u5f20\u5f00\u773c\u775b\uff0c\u53ea\u89c1\u695a\u7559\u9999\u8fd8\u662f\u67aa\u4e00\u822c\u7b14\u76f4\u7684\u7ad9\u5728\u90a3\u91cc\uff0c\u9759\u9759\u7684\u671b\u7740\u4ed6\uff0c\u5634\u89d2\u5c45\u7136\u4e5f \u8fd8\u5e26\u7740\u5fae\u7b11\u3002 \u3000\u3000\u4f46\u5251\u5c16\u4e0a\u5374\u5df2\u6709\u9c9c\u8840\u5728\u6ef4\u843d\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u4e5f\u7b11\u4e86\uff0c\u683c\u683c\u7b11\u9053\uff1a\u201c\u695a\u7559\u9999\u5e94\u53d8\u4e4b\u5feb\uff0c\u679c\u7136\u662f\u5929\u4e0b\u65e0\u53cc\uff0c\u53ea\u53ef\u501f\u8fd8\u662f\u6ca1\u6709\u907f\u5f00 \u6211\u90a3\u4e00\u523a\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u672c\u4e5f\u5947\u602a\uff0c\u662f\u8c01\u7684\u5251\u5982\u6b64\u5feb\uff0c\u60f3\u4e0d\u5230\u539f\u6765\u662f\u4f60\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u7b11\u9053\uff1a\u201c\u4f60\u5c82\u975e\u6b63\u5728\u627e\u6211\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u4e00\u76f4\u90fd\u5728\u627e\u4f60\uff0c\u5374\u672a\u60f3\u5230\u4f60\u771f\u7684\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4f60\u65e2\u7136\u5728\u8fd9\u91cc\uff0c\u6211\u81ea\u7136\u4e5f\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u96be\u9053\u4f60\u662f\u8ddf\u7740\u6211\u6765\u7684\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d \u3000\u3000\u8fd9\u4eba\u81ea\u7136\u5c31\u662f\u90a3\u523a\u5ba2\u7ec4\u7ec7\u7684\u9996\u9886\u3002 \u3000\u3000\u4ed6\u9e70\u822c\u7684\u76ee\u5149\u77aa\u7740\u695a\u7559\u9999\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4e00\u76f4\u5728\u627e\u6211\uff0c\u6211\u4e5f\u4e00\u76f4\u5728\u627e\u4f60\uff0c\u4f60\u60f3\u8981\u6211\u7684 \u547d\uff0c\u6211\u4e5f\u60f3\u8981\u4f60\u7684\u547d\uff0c\u6211\u4eec\u4e24\u4eba\u4e4b\u95f4\uff0c\u53cd\u6b63\u53ea\u6709\u4e00\u4e2a\u4eba\u80fd\u6d3b\u4e0b\u53bb\u3002\u201d\u767e\uff1f\uff1f\uff1f\u7cfb\u65f6\u5019\u5c31\u53ef\u4ee5 \u6570\u5b8c\u3002 \u3000\u3000\u4f46\u9ed1\u8863\u4eba\u5374\u89c9\u5f97\u597d\u50cf\u6c38\u8fdc\u4e5f\u6570\u4e0d\u5b8c\u4f3c\u7684\u3002 \u3000\u3000\u4ed6\u672c\u4e5f\u662f\u4e2a\u8d4c\u5f92\uff0c\u53ea\u4e0d\u8fc7\u8fd9\u6b21\u8d4c\u5f97\u672a\u514d\u592a\u5927\u4e86\uff0c\u4e5f\u672a\u514d\u592a\u5192\u9669\uff0c\u82e5\u6709\u9009\u62e9\u7684\u9980\u5730\u4ed6\u5c31\u7edd \u4e0d\u4f1a\u5c06\u8d4c\u6ce8\u62bc\u4e0b\u53bb\u3002 \u3000\u3000\u201c\u4e5d\u767e\u4e5d\u5341\u4e8c\uff0c\u4e5d\u767e\u4e5d\u5341\u53c1\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u201c\u7830\u201d\u7684\u649e\u5f00\u95e8\uff0c\u4e00\u8dc3\u800c\u51fa\uff0c\u4e24\u4e2a\u8d77\u843d\u5f8c\u4f7f\u5df2\u63a0\u5230\u7b2c\u4e00\u773c\u770b\u5230\u6811\u4e1b\uff0c\u5730\u4e0a\u679c\u7136\u6709 \u7528\u6811\u679d\u5212\u51fa\u7684\u5b57\u8ff9\u3002 \u3000\u3000\u53ea\u6709\u56db\u4e2a\u5b57\uff0c \u3000\u3000\u201c\u4f60\u672a\u4e2d\u6bd2\u3002\u201d \u3000\u3000\u6b6a\u6b6a\u659c\u659c\u7684\u5b57\u8ff9\uff0c\u50cf\u662f\u6b63\u5728\u5bf9\u4ed6\u5632\u7b11\u3002\u9ed1\u8863\u4eba\u53ea\u4f4f\u4e86\uff0c\u5446\u4e86\u534a\u664c\u5fcd\u4e0d\u4f4f\u5728\u8fd9\u56db\u4e2a\u5b57\u4e0a\u91cd \u91cd\u5410\u4e86\u53e3\u53e3\u6c34\uff0c\u53c8\u72e0\u72e0\u8e29\u4e86\u51e0\u811a\uff0c\u9053\uff1a\u201c\u76f4\u5a18\u8d3c\uff0c\u5988\u90a3\u5df4\u5b50\uff0c\u64cd\u2026\u3002\u201d \u3000\u3000\u4ed6\u51e0\u4e4e\u5c06\u5404\u7701\u9551\u5730\uff0c\u53ea\u8981\u77e5\u9053\u7684\u9a82\u4eba\u7684\u8bdd\u5168\u90fd\u9a82\u4e86\u51fa\u6765\uff0c\u201c\u8fd9\u59d3\u695a\u7684\u738b\u516b\u86cb\u539f\u6765\u53c8\u5728\u4f7f \u8bc8\u3002\u201d \u3000\u3000\u539f\u6765\u4ed6\u65b9\u53ea\u8981\u4e00\u6784\u624b\u5c31\u53ef\u5c06\u695a\u7559\u9999\u7f6e\u4e4b\u4e8e\u6b7b\u5730\u3002 \u3000\u3000\u4ed6\u5b9e\u5728\u60f3\u4e0d\u901a\u695a\u7559\u9999\u82b1\u90a3\u79cd\u65f6\u5019\u600e\u9ebd\u8fd8\u80fd\u4e00\u70b9\u4e5f\u4e0d\u7d27\u5f20\uff0c\u695a\u7559\u9999\u90a3\u65f6\u53ea\u8981\u6dcc\u51fa\u4e00\u6ef4\u6c57\uff0c \u4ed6\u7684\u5251\u53ea\u6015\u65e9\u5df2\u51fa\u624b\u3002 \u3000\u3000\u201c\u695a\u7559\u9999\uff0c\u695a\u7559\u9999\uff0c\u4f60\u4e5f\u7528\u4e0d\u7740\u5f97\u610f\uff0c\u4eca\u65e5\u4f60\u867d\u7136\u53c8\u9003\u8131\u4e86\u4e00\u6b21\uff0c\u4f46\u8981\u6740\u4f60\u7684\u673a\u4f1a\u8fd8\u662f \u591a\u5f97\u5f88\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u60f3\u8d77\u695a\u7559\u9999\u65e2\u5df2\u53d7\u4e86\u91cd\u4f24\uff0c\u5c31\u5fc5\u9003\u4e0d\u8fdc\u7684\uff0c\u5c31\u7b97\u5df2\u9003\u51fa\u4e00\u5343\u6b65\uff0c\u4ed6\u8fd8\u662f\u5f88\u5feb\u5c31\u80fd \u8ffd\u4e0a\u3002 \u3000\u3000\u5730\u4e0a\u679c\u7136\u6709\u4e00\u6ef4\u5df2\u4e7e\u6db8\u4e86\u7684\u8840\u8ff9\u3002\u9ed1\u8863\u4eba\u4f0f\u4e0b\u8eab\u5b50\uff0c\u730e\u72d7\u822c\u5728\u5730\u4e0a\u641c\u7d22\uff0c\u7ec8\u65bc\u627e\u5230\u4e86\u4e00 \u884c\u8db3\u8ff9\u3002 \u3000\u3000\u4ed6\u5c31\u50cf\u72fc\u4e00\u822c\u8ffd\u51fa\u53bb\u3002\u4ee5\u695a\u7559\u9999\u53d7\u4f24\u4e4b\u91cd\uff0c\u7684\u786e\u662f\u9003\u4e0d\u8fdc\u7684\uff0c\u4ed6\u7684\u786e\u5f88\u5feb\u5c31\u80fd\u8ffd\u4e0a\u3002\u53ea \u53ef\u60dc\u695a\u7559\u9999\u6839\u672c\u6ca1\u6709\u9003\uff0c\u4ed6\u5c31\u8eb2\u5728\u8fd9\u6a61\u6811\u4e0a\uff0c\u9ed1\u8863\u4eba\u9a82\u4ed6\u7684\u6bcf\u53e5\u8bdd\u4ed6\u90fd\u542c\u5f97\u6e05\u6e05\u695a\u695a\u3002\u4ed6\u8fd9 \u4e00\u751f\u4e2d\u6328\u7684\u9a82\u53ea\u6015\u8fd8\u6ca1\u6709\u4eca\u5929\u2014\u5929\u591a\u3002 \u3000\u3000\u695a\u7559\u9999\u671b\u7740\u9ed1\u8863\u4eba\u53bb\u8fdc\uff0c\u53ea\u89c9\u773c\u524d\u6ca5\u6ca5\u53d1\u82b1\uff0c\u8eab\u5b50\u8bf4\u4e0d\u51fa\u7684\u865a\u5f31\uff0c\u7adf\u81ea\u6811\u4e0a\u76f4\u8dcc\u4e86\u4e0b \u6765\u3002 \u3000\u3000\u73b0\u5728\u9ed1\u8863\u4eba\u82e5\u662f\u8d76\u56de\u6765\u4ed6\u6839\u672c\u5168\u65e0\u62b5\u6297\u4e4b\u529b\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u4ed6\u4e5f\u662f\u8840\u8089\u4e4b\u8eaf\uff0c\u88ab\u4eba\u5728\u80cc\u4e0a \u523a\u4e86\u5251\u603b\u4e0d\u662f\u73a9\u7684\u3002 \u3000\u3000\u695a\u7559\u9999\u867d\u770b\u4e0d\u5230\u80cc\u4e0a\u7684\u4f24\u52bf\uff0c\u5374\u77e5\u9053\u8fd9\u4e00\u5251\u523a\u5f97\u5f88\u6df1\uff0c\u8bf4\u4e0d\u5b9a\u5df2\u7ecf\u523a\u5230\u9aa8\u5934\uff0c\u6d41\u7684\u8840\u81ea \u7136\u4e5f\u4e0d\u5c11\u3002 \u3000\u3000\u4ee5\u4ed6\u73b0\u5728\u7684\u4f53\u529b\u7edd\u5bf9\u65e0\u6cd5\u8d70\u56de\u201c\u63b7\u676f\u5c71\u5e84\u201d\u3002 \u3000\u3000\u4ed6\u501a\u7740\u6811\u5e72\uff0c\u5598\u4e86\u534a\u5929\u6c14\u6b63\u60f3\u627e\u4e2a\u5730\u65b9\u5148\u8eb2\u8eb2\uff0c\u7a81\u542c\u9635\u201c\u6c99\u6c99\u201d\u7684\u811a\u6b65\u58f0\u7a7f\u6797\u8c22\u6765\u3002 \u3000\u3000\u695a\u7559\u9999\u8fde\u547c\u5438\u90fd\u51e0\u4e4e\u505c\u987f\u4e86\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u82e5\u662f\u53bb\u800c\u590d\u8fd4\uff0c\u4ed6\u53ea\u6709\u6b7b\u8def\u4e00\u6761\u3002 \u3000\u3000\u53ea\u542c\u4e00\u4eba\u9053\uff1a\u201c\u8fd9\u79cd\u5730\u65b9\u600e\u4f1a\u6709\u597d\u6237\u5934\uff0c\u770b\u6765\u6211\u53c8\u4e0a\u4e86\u4f60\u8fd9\u5c0f\u8d3c\u7684\u5f53\u4e86\u3002\u201d \u3000\u3000\u53e6\u4e00\u4eba\u9053\uff1a\u201c\u6211\u9a97\u4f60\u5e72\u4ec0\u9ebd\uff0c\u6211\u6bcf\u6b21\u53ea\u8981\u4e00\u6765\uff0c\u4ed6\u4eec\u4e00\u51fa\u624b\u81f3\u5c11\u5c31\u662f\u4e94\u94b1\u94f6\u5b50\u3002\u201d \u3000\u3000\u7b2c\u4e00\u4eba\u9053\uff1a\u201c\u4e94\u94b1\u94f6\u5b50\u7ed9\u81ed\u8981\u996d\u7684\uff0c\u90a3\u4eba\u96be\u9053\u9614\u7ffb\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u7b2c\u4e8c\u4eba\u7b11\u9053\uff1a\u201c\u8fd9\u4f60\u5c31\u4e0d\u61c2\u4e86\uff0c\u7537\u4eba\u5728\u5973\u4eba\u9762\u524d\uff0c\u603b\u4f1a\u88c5\u5f97\u5927\u65b9\u4e9b\u7684\u2026\u3002\u6211\u8bf4\u7684\u53ef\u4e0d\u662f \u592b\u59bb\u662f\u60c5\u4eba\uff0c\u5728\u8001\u5a46\u9762\u524d\u5c31\u4e0d\u4f1a\u5927\u65b9\u4e86\u3002\u201d \u3000\u3000\u7b2c\u4e00\u4eba\u4e5f\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u8fd9\u4e00\u7537\u4e00\u5973\u4e24\u4f4d\u8d22\u795e\u7237\u5728\u90a3\u91cc\uff1f\u201d\u74dc \u3000\u3000\u7b2c\u4e8c\u4eba\u9053\uff1a\u201c\u5c31\u5728\u524d\u9762\u7684\u5c0f\u5c4b\u91cc\uff0c\u4f9d\u6211\u770b\uff0c\u4ed6\u4fe9\u516b\u6210\u662f\u5728\u90a3\u91cc\u5e7d\u4f1a\u3002\u201d \u3000\u3000\u8fd9\u4e24\u4eba\u8bf4\u8bdd\u7684\u58f0\u97f3\u4ff1\u662f\u7ae5\u5b50\u53e3\u97f3\u3002 \u3000\u3000\u695a\u7559\u9999\u6697\u4e2d\u677e\u4e86\u53e3\u6c14\uff0c\u56de\u5934\u671b\u53bb\uff0c\u53ea\u89c1\u4e24\u4e2a\u5341\u53c1\u56db\u5c81\u7684\u5316\u5b50\u7b11\u563b\u563b\u7684\u5f80\u8fd9\u8fb9\u8d70\uff0c\u4e24\u4eba\u7a7f \u7684\u867d\u7136\u7834\u7834\u70c2\u70c2\uff0c\u795e\u60c5\u5374\u662f\u9ad8\u9ad8\u5174\u5174\uff0c\u5de6\u9762\u7684\u662f\u4e2a\u5c0f\u9ebb\u5b50\uff0c\u5927\u5927\u7684\u773c\u775b\u6ee1\u8138\u90fd\u662f\u8c03\u76ae\u6363\u86cb\u7684 \u6837\u5b50\u3002 \u3000\u3000\u53f3\u9762\u7684\u4e00\u4e2a\u662f\u5c0f\u79c3\u5b50\uff0c\u770b\u6765\u6bd4\u5c0f\u9ebb\u4e88\u8fd8\u8981\u8c03\u76ae\u5341\u500d\uff0c\u4e24\u4eba\u8eab\u6cd5\u90fd\u5f88\u8f7b\u7075\uff0c\u6b66\u529f\u7684\u6839\u57fa\u663e \u7136\u4e0d\u5f31\u3002 \u3000\u3000\u695a\u7559\u9999\u8fd9\u4e00\u751f\u4e2d\u7b80\u76f4\u6ca1\u6709\u770b\u5230\u8fc7\u6bd4\u8fd9\u4e24\u4e2a\u5c0f\u53eb\u5316\u5b50\u66f4\u4ee4\u4ed6\u75db\u5feb\u7684\u4eba\u4e86\uff0c\u4ed6\u4ece\u672a\u60f3\u5230\u53eb\u5316 \u5b50\u5c45\u7136\u5982\u6b64\u53ef\u7231\u3002 \u3000\u3000\u90a3\u5c0f\u79c3\u5b50\u548c\u5c0f\u9ebb\u5b50\u4e5f\u77a7\u89c1\u4e86\u4ed6\uff0c\u4e24\u4eba\u4e00\u8d77\u505c\u4e0b\u811a\u6b65\uff0c\u56db\u53ea\u5927\u773c\u775b\u77aa\u7740\u4ed6\u6ef4\u6e9c\u7684\u4e71\u8f6c\u3002 \u3000\u3000\u695a\u7559\u9999\u5411\u4ed6\u4eec\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u5c0f\u5144\u5f1f\u811a\u4e0b\u529f\u592b\u4e0d\u9519\uff0c\u4e0d\u77e5\u53ef\u662f\u4e10\u5e2e\u95e8\u4e0b\uff1f\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u773c\u73e0\u5b50\u4e00\u8f6c\uff0c\u9053\uff1a\u201c\u6211\u4e3a\u4f55\u8981\u544a\u8bc9\u4f60\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u80fd\u5e26\u6211\u53bb\u89c1\u6b64\u5730\u7684\u9f99\u5934\u5927\u54e5\u9ebd\uff1f\u3002\u201d \u3000\u3000\u5c0f\u9ebb\u5b50\u773c\u73e0\u4e5f\u8f6c\u4e86\u8f6c\uff0c\u9053\uff1a\u201c\u6211\u4e3a\u4f55\u8981\u5e26\u4f60\u53bb\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u53eb\u695a\u7559\u9999\uff0c\u6211\u60f3\u4ed6\u4e00\u5b9a\u613f\u610f\u89c1\u6211\u7684\u3002\u3002 \u3000\u3000\u5c0f\u9ebb\u5b50\u9053\uff1a\u201c\u695a\u7559\u9999\u662f\u4ec0\u9ebd\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bdd\u672a\u8bf4\u5b8c\u8111\u4e0a\u5df2\u6328\u4e86\u5c0f\u79c3\u5b50\u4e00\u4e2a\u8033\u5149\uff0c\u5927\u53eb\u9053\uff1a\u201c\u4f60\u4e3a\u4f55\u6253\u6211\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u626e\u4e86\u4e2a\u9b3c\u8138\u9053\uff1a\u201c\u4f60\u82e5\u8fde\u695a\u9999\u5e05\u90fd\u4e0d\u77e5\u9053\uff0c\u5c31\u7b97\u6328\u5341\u4e2a\u8033\u5149\u90fd\u592a\u5c11\u4e86\u3002\u201d \u3000\u3000\u5c0f\u9ebb\u5b50\u6342\u7740\u8138\u773c\u775b\u5ffd\u7136\u4eae\u4e86\u9053\uff1a\u201c\u695a\u9999\u5e05\uff1f\u4f60\u8bf4\u662f\u90a3\u2018\u76d7\u5e05\u591c\u7559\u9999\uff0c\u5a01\u540d\u9707\u516b\u65b9\u2019\u7684\u695a \u9999\u5e05\uff1f\u201d \u3000\u3000\u5c0f\u79c3\u4e88\u9053\uff1a\u201c\u9664\u4e86\u8fd9\u4f4d\u695a\u9999\u5e05\u54ea\u91cc\u8fd8\u6709\u7b2c\u4e8c\u4f4d\u695a\u9999\u5e05\u3002\u201d \u3000\u3000\u5c0f\u5e2d\u5b50\u201c\u556a\u201d\u7684\u53c8\u7ed9\u4e86\u81ea\u5df1\u4e00\u4e2a\u8033\u5149\uff0c\u9053\uff1a\u201c\u6211\u7684\u5988\u5440\u2026\u3002\u201d \u3000\u3000\u5175\u91cc\u72d7\u8089\uff0c\u9999\u5f97\u8981\u547d\uff0c\u4e16\u4e0a\u7eb5\u6709\u4e0d\u5506\u53eb\u5316\u4e88\u7684\u72d7\uff0c\u4e5f\u5f88\u5c11\u6709\u4e0d\u5403\u72d7\u8089\u7684\u53eb\u5316\u5b50\u3002\u8fd9\u6b63\u5982 \u559d\u9152\u7684\u65f6\u5019\u53ef\u4ee5\u4e0d\u5403\u72d7\u8089\uff0c\u5403\u72d7\u8089\u968f\u65f6\u5019\u5374\u7edd\u4e0d\u80fd\u4e0d\u559d\u9152\uff0c\u53eb\u5316\u5b50\u3001\u72d7\u8089\u3001\u9152\uff0c\u597d\u50cf\u6c38\u8fdc\u5206 \u4e0d\u5f00\u7684\u3002 \u3000\u3000\u7834\u5e99\u91cc\u6709\u5341\u6765\u4e2a\u53eb\u5316\u5b50\uff0c\u8863\u886b\u867d\u7834\u70c2\uff0c\u795e\u60c5\u5374\u7edd\u4e0d\u72fc\u7410\uff0c\u4e00\u671b\u800c\u77e5\u5fc5\u5b9a\u90fd\u662f\u4e10\u5e2e\u5f1f\u5b50\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u80cc\u5f8c\u5927\u591a\u80cc\u7740\u4e24\u53c1\u53ea\u9ebb\u888b\uff0c\u5176\u4e2d\u53ea\u6709\u4e00\u4e2a\u8138\u9ed1\u77ed\u5c0f\u7684\u5c11\u5e74\u4e5e\u4e10\u3002\u8eab\u4e0a\u7684\u662f\u9ebb\u888b\u6709 \u516d\u53ea\uff0c\u8170\u4e0a\u8fd8\u63d2\u7740\u4e2a\u9ed1\u94c1\u7b80\uff0c\u4e5f\u4e0d\u77e5\u662f\u505a\u4ec0\u9ebd\u7528\u7684\uff0c\u695a\u7559\u9999\u5f8c\u6765\u624d\u77e5\u9053\u4ed6\u53eb\u201c\u5c0f\u706b\u795e\u201d\u3002\u6b63 \u662f\u6b64\u95f4\u7684\u9f99\u5934\u8001\u5927\u3002 \u3000\u3000\u6b64\u523b\u6570\u5341\u53cc\u773c\u8180\u90fd\u5728\u671b\u7740\u695a\u7559\u9999\uff0c\u76ee\u5149\u4e2d\u5145\u6ee1\u4e86\u656c\u754f\u4ef0\u6155\u4e4b\u8272\uff0c\u4e5f\u6709\u51e0\u5206\u4eb2\u5207\u4e4b\u610f\uff0c\u56e0 \u4e3a\u5927\u5bb6\u90fd\u77e5\u9053\u695a\u9999\u5e08\u662f\u4e10\u5e2e\u7684\u670b\u53cb\u3002 \u3000\u3000\u8fd9\u4e5f\u662f\u6bcf\u4e2a\u4e10\u5e2e\u5f1f\u5b50\u90fd\u5f15\u4ee5\u4e3a\u8363\u7684\u4e8b\u3002 \u3000\u3000\u5c0f\u706b\u795e\u6b63\u7b11\u9053\uff1a\u201c\u5f1f\u5b50\u65e9\u5df2\u4e45\u4ef0\u9999\u5e05\u7684\u5927\u540d\u4e86\uff0c\u53ef\u662f\u505a\u68a6\u4e5f\u672a\u601d\u5230\u4eca\u65e5\u5c45\u7136\u80fd\u771f\u6709\u5e78\u89c1 \u5230\u9999\u5e05\u7684\u5927\u9a7e\uff0c\u8fd9\u5b9e\u5728\u662f\u5929\u5927\u7684\u559c\u4e8b\uff01\u201d \u3000\u3000\u695a\u7559\u9999\u4f24\u53e3\u5df2\u5305\u624e\u597d\u4e86\uff0c\u6b64\u523b\u6b63\u501a\u5728\u795e\u6848\u524d\u555c\u7740\u6bd4\u4eba\u53c2\u8fd8\u6ecb\u8865\u7684\u7ec6\u6c64\uff0c\u5fae\u7b11\u7740\u9053\uff1a\u201c\u4f60 \u4eec\u73b0\u5728\u6b22\u559c\uff0c\u4ee5\u5f8c\u6015\u8ba8\u538c\u90fd\u6765\u4e0d\u53ca\u3002\u201d \u3000\u3000\u4ed6\u53c8\u555c\u4e86\u53e3\u72d7\u8089\u6c64\uff0c\u7b11\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u4eec\u8bf7\u6211\u5403\u8089\uff0c\u6211\u5374\u662f\u6765\u627e\u4f60\u4eec\u9ebb\u70e6\u7684\u3002\u201d \u3000\u3000\u5c0f\u706b\u795e\u6014\u4e86\u6014\uff0c\u5403\u5403\u9053\uff1a\u201c\u5144\u5f1f\u6709\u4ec0\u9ebd\u5730\u65b9\u5f97\u7f6a\u4e86\u9999\u5e05\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u901a\u201c\u4f60\u4eec\u600e\u4f1a\u5f97\u7f6a\u6211\uff0c\u53ea\u4e0d\u8fc7\uff0c\u6211\u6709\u51e0\u4ef6\u9ebb\u70e6\u4e8b\u60f3\u6c42\u4f60\u4eec\u800c\u5df2\u3002\u201d \u3000\u3000\u5c0f\u706b\u795e\u5410\u4e86\u53e3\u6c14\uff0c\u5c55\u989c\u9053\uff1a\u201c\u9999\u5e05\u5bf9\u4e10\u5e2e\u601d\u91cd\u5982\u5c71\uff0c\u83ab\u8bf4\u8981\u6211\u4eec\u6548\u52b3\u505a\u4e8b\uff0c\u5c31\u7b97\u8981\u6211\u4eec \u8df3\u6cb3\uff0c\u6211\u4eec\u4e5f\u7167\u8df3\u4e0d\u8bef\u3002\u201d \u3000\u3000\u4e10\u5e2e\u95e8\u4e0b\u867d\u7136\u591a\u7684\u662f\u8840\u6027\u7537\u5b50\uff0c\u695a\u7559\u9999\u77e5\u9053\u82e5\u662f\u5bf9\u8fd9\u4e9b\u4eba\u8bb2\u5ba2\u6c14\u8bdd\uff0c\u5c31\u663e\u5f97\u81ea\u5df2\u662f\u4f2a\u541b \u5b50\uff0c\u5f53\u4e0b\u6b63\u8272\u9053\uff1a\u201c\u7b2c\u2014\u4ef6\u4e8b\uff0c\u6211\u8981\u4f60\u4eec\u53bb\u6253\u542c\u4e00\u4e2a\u4eba\uff0c\u8fd9\u4eba\u672c\u6765\u7684\u540d\u5b57\u53eb\u53f6\u76db\u5170\uff0c\u636e\u8bf4\u662f \u5728\u4eac\u57ce\u6df7\u7684\uff0c\u4f46\u6211\u60f3\u8fd9\u51e0\u5929\u4ed6\u5fc5\u5b9a\u5df2\u5230\u4e86\u8fd9\u91cc\uff0c\u5e0c\u671b\u4f60\u4eec\u80fd\u6253\u542c\u51fa\u4ed6\u843d\u811a\u4ec0\u9ebd\u5730\u65b9\uff1f\u7a76\u7adf\u662f \u5e72\u4ec0\u9ebd\u7684\u662f\u4e0d\u662f\u6709\u4eba\u548c\u4ed6\u540c\u4f4f\uff1f\u201d \u3000\u3000\u5c0f\u706b\u795e\u542c\u695a\u7559\u9999\u8bf4\uff0c\u7b2c\u4e00\u4ef6\u4e8b\u60c5\u8981\u4ed6\u7531\u6253\u542c\u53f6\u76db\u5170\u7684\u8fd1\u51b5\uff0c\u4e0d\u7531\u7b11\u9053\uff1a\u201c\u9999\u5e05\u8bf7\u653e\u5fc3\uff0c \u6253\u542c\u6d88\u606f\u6b63\u662f\u6211\u4eec\u7684\u62ff\u624b\u672c\u4e8b\uff0c\u53ea\u8981\u4e16\u4e0a\u6709\u53f6\u76db\u5170\u8fd9\u4e2a\u4eba\uff0c\u6211\u4e00\u5b9a\u80fd\u627e\u51fa\u4ed6\u7684\u6839\u6765\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u7b2c\u4e8c\u4ef6\u4e8b\uff0c\u6211\u8981\u4f60\u6d3e\u51e0\u4f4d\u5144\u5f1f\u53bb\u76ef\u4f4f\u859b\u5bb6\u5e84\u7684\u4e8c\u516c\u5b50\u548c\u65bd\u5bb6\u5e84\u91cc\u7684\u4e00\u4e2a\u8001 \u5976\u5976\u53eb\u6881\u5988\u7684\uff0c\u65e0\u8bba\u4ed6\u4eec\u5230\u90a3\u91cc\u53bb\uff0c\u90fd\u8981\u76ef\u4f4f\u3002\u201d \u3000\u3000\u5c0f\u706b\u795e\u9053\uff1a\u201c\u8fd9\u4e5f\u529e\u5f97\u5230\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u7b2c\u53c1\u4ef6\u4e8b\uff0c\u6211\u5e0c\u671b\u4f60\u80fd\u60f3\u4e2a\u6cd5\u5b50\u8054\u7edc\u2018\u4e01\u5bb6\u53cc\u5251\u2019\u7684\u4e01\u8001\u4e8c\uff0c\u8fd9\u4e24\u5929\u4ed6\u4e5f \u5230\u8fd9\u91cc\u6765\u4e86\uff0c\u5c31\u4f4f\u5728\u63b7\u676f\u5c71\u5e84\u3002\u201d \u3000\u3000\u5c0f\u706b\u795e\u60f3\u4e86\u60f3\uff0c\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u4e5f\u5305\u5728\u654c\u4eec\u8eab\u4e0a\uff0c\u4e00\u5b9a\u66ff\u9999\u72ec\u529e\u597d\u3002\u2019 \u3000\u3000\u695a\u7559\u9999\u957f\u957f\u6bb4\u51fa\u53e3\u6c14\uff0c\u9053\uff0c\u201c\u7b2c\u56db\u4ef6\u4e8b\u53ef\u5c31\u56e0\u96be\u4e9b\u4e86\u3002\u201d \u3000\u3000\u5c0f\u706b\u795e\u7b11\u9053\uff0c\u201c\u53ea\u8981\u662f\u9999\u5e05\u4ea4\u5f85\u4e0b\u6765\u7684\u4e8b\uff0c\u518d\u56f0\u96be\u6211\u4eec\u4e5f\u529e\u5f97\u5230\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u597d\uff0c\u4eca\u5929\u665a\u4e0a\u6211\u8981\u4f60\u4eec\u966a\u6211\u53bb\u6316\u575f\u3002\u201d \u3000\u3000\u5c0f\u706b\u795e\u8fd9\u624d\u771f\u7684\u6014\u4f4f\u4e86\uff0c\u201c\u9999\u5e05\u201d\u7684\u4e3b\u610f\u96be\u9053\u5df2\u6253\u5230\u6b7b\u4eba\u8eab\u4e0a\u53bb\u4e86\u9ebd\uff1f\u5c0f\u706b\u795e\u773c\u775b\u53d1 \u76f4\uff0c\u7b80\u76f4\u6709\u4e9b\u54ed\u7b11\u4e0d\u5f97\u3002 \u3000\u3000\u5c0f\u79c3\u5b50\u9053\uff1a\u201c\u8001\u5927\u82e5\u4e0d\u6562\u53bb\uff0c\u6211\u53bb\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u4f60\u771f\u6562\u53bb\uff1f\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u9053\uff1a\u201c\u82e5\u662f\u522b\u4eba\u53eb\u6211\u53bb\u6316\u4eba\u5bb6\u7684\u575f\uff0c\u6211\u4e0d\u6253\u4ed6\u5341\u4e03\u516b\u4e2a\u8033\u5149\u624d\u7f62\uff0c\u9999\u5e05\u8981\u6211\u53bb\u6316 \u575f\uff0c\u6211\u5c31\u53bb\u6316\u575f\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u7728\u4e86\u7728\u773c\u775b\uff0c\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u77e5\u9053\u9999\u5e05\u7edd\u4e0d\u4f1a\u8981\u6211\u4eec\u53bb\u505a\u574f\u4e8b\u7684\u3002\u201d \u3000\u3000\u5c0f\u9ebb\u5b50\u7acb\u523b\u9053\uff1a\u201d\u4e0d\u9519\u3002\u6211\u4e5f\u53bb\u3002\u201d \u3000\u3000\u5c0f\u706b\u795e\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u770b\u6765\u8fd9\u4e24\u4e2a\u5c0f\u8868\u8fd8\u61c2\u4e8b\uff0c\u6bd4\u6211\u8fd8\u77e5\u9053\u597d\u6b79\u2026\u2026\u9999\u5e05\u4f60\u8981\u6211 \u4eec\u4ec0\u9ebd\u65f6\u5019\u53bb\u6316\u575f\uff0c\u6211\u4eec\u5c31\u4ec0\u9ebd\u65f6\u5019\u53bb\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4eca\u591c\u53c1\u66f4\u3002\u201d \u3000\u3000\u4ed6\u62c9\u8d77\u4e86\u4e24\u4e2a\u5b69\u5b50\u7684\u624b\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u662f\u6211\u7684\u597d\u670b\u53cb\uff0c\u4f46\u6709\u65f6\u6211\u4e5f\u4f1a\u5e26\u4f60\u4eec\u53bb\u505a\u574f\u4e8b \u7684\uff0c\u8fc7\u4e24\u4e2d\u7b49\u4f60\u4eec\u957f\u5927\u4e86\u4e9b\uff0c\u6211\u5b9a\u6765\u627e\u4f60\u4eec\u53bb\u75db\u75db\u5feb\u5feb\u7684\u559d\u51e0\u676f\uff0c\u8fd8\u8981\u627e\u4e24\u4e2a\u5c0f\u7f8e\u4eba\u513f\u6765\u66ff \u4f60\u4eec\u659f\u9152\u3002\u201d \u3000\u3000\u4ed6\u5927\u7b11\u7740\u63a5\u9053\uff1a\u201c\u8fd9\u4e9b\u4e5f\u5e76\u4e0d\u662f\u4ec0\u9ebd\u597d\u4e8b\uff0c\u4f46\u603b\u6bd4\u6316\u575f\u6709\u8da3\u4e9b\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5c45\u7136\u62ff\u4ed6\u4eec\u5f53\u670b\u53cb\uff0c\u5c45\u7136\u8981\u8bf7\u4ed6\u4eec\u559d\u9152\uff0c\u5c0f\u79c3\u5b50\u548c\u5c0f\u9ebb\u5b50\u51e0\u4e4e\u5f00\u5fc3\u5f97\u66f4\u8981\u53d1\u75af \u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\u5ffd\u7136\u53c8\u9053\uff1a\u201c\u4f60\u4eec\u4eca\u5929\u672c\u6765\u662f\u60f3\u5230\u90a3\u5c0f\u5c4b\u53bb\u7684\u9ebd\uff1f\u201d \u3000\u3000\u5c0f\u9ebb\u5b50\u9053\uff1a\u201c\u5c0f\u79c3\u5b50\u8bf4\u90a3\u5c0f\u5c4b\u91cc\u6709\u4e24\u4e2a\u5f88\u5927\u65b9\u7684\u4eba\uff0c\u4ed6\u7b2c\u4e00\u6b21\u9047\u89c1\u4ed6\u4eec\uff0c\u4ed6\u4eec\u5c31\u7ed9\u4e86\u4ed6 \u4e00\u4e24\u591a\u94f6\u5b50\uff0c\u7b2c\u4e8c\u6b21\u53c8\u662f\u4e03\u516b\u94b1\u3002\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u7b11\u9053\uff1a\u201c\u4f46\u662f\u6211\u5374\u4e0d\u662f\u6545\u610f\u53bb\u6572\u7af9\u6760\u7684\uff0c\u7b2c\u4e00\u6b21\u6211\u662f\u5230\u90a3\u91cc\u53bb\u6349\u8774\u8776\uff0c\u9047\u89c1\u4ed6\u4eec \u4ece\u90a3\u5c0f\u5c4b\u91cc\u51fa\u6765\uff0c\u4ed6\u4eec\u786c\u8981\u7ed9\u6211\u94f6\u5b50\uff0c\u6211\u4e5f\u53ea\u597d\u6536\u4e0b\u4e86\u3002\u201d \u3000\u3000\u5c0f\u9ebb\u5b50\u9053\uff1a\u201c\u7b2c\u4e8c\u6b21\u5462\uff1f\u96be\u9053\u4e5f\u4e0d\u662f\u6545\u610f\u7684\u5417\uff1f\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u624d\u7b11\u9053\uff1a\u201c\u4ee5\u5f8c\u6211\u53ea\u4e0d\u8fc7\u65f6\u5e38\u90fd\u53bb\u901b\u901b\u7f62\u4e86\uff0c\u4ece\u6765\u4e5f\u6ca1\u6709\u51fb\u6572\u8fc7\u4ed6 \u4eec\u7684\u95e8\uff0c\u4e5f\u4e0d\u53ca\u6bcf\u6b21\u90fd\u80fd\u9047\u89c1\u4ed6\u4eec\u7684\u3002\u201d \u3000\u3000\u5c0f\u9ebb\u5b50\u6487\u4e86\u6487\u5634\uff0c\u9053\uff1a\u201d\u8fd8\u8bf4\u4ec0\u9ebd\u6709\u798f\u540c\u4eab\uff0c\u6709\u96be\u540c\u5f53\uff0c\u81ea\u5df1\u53bb\u4e86\u5341\u4e03\u516b\u6b21\u624d\u53eb\u6211 \u53bb\u3002\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u7b11\u9053\uff1a\u201c\u6211\u662f\u6015\u4f60\u751f\u5f97\u592a\u4e11\uff0c\u628a\u4eba\u5bb6\u5413\u8dd1\u4e86\u3002\u201d \u3000\u3000\u5c0f\u561b\u5b50\u53eb\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u6211\u4e11\uff1f\u4f60\u5f88\u7f8e\u5417\uff1f\u79c3\u4e0d\u79c3\uff0c\u989c\u80e1\u82a6\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u4e5f\u7b11\u4e86\uff0c\u4f46\u773c\u775b\u53d1\u7740\u5149\uff0c\u53c8\u95ee\u9053\uff1a\u201c\u90a3\u4e24\u4eba\u662f\u4e00\u7537\u4e00\u5973\uff1f\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u9053\uff1a\u201c\u4e24\u4eba\u90fd\u5f88\u5e74\u8f7b\u3002\u7a7f\u5f97\u4e5f\u90fd\u5f88\u6f02\u4eae\uff0c\u4e00\u770b\u5c31\u77e5\u9053\u662f\u6709\u94b1\u4eba\u5bb6\u7684\u5c0f\u59d0\u5c11\u7237\u3002\u4f46 \u5bf9\u4eba\u5374\u5f88\u548c\u6c14\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4ed6\u4eec\u957f\u76f8\u662f\u4f55\u6a21\u6837\uff1f\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u60f3\u4e86\u60f3\uff0c\u9053\uff1a\u201c\u4e24\u4eba\u957f\u5f97\u90fd\u6ca1\u6709\u4ec0\u9ebd\u7279\u522b\u7684\u5730\u65b9\uff0c\u90fd\u4e0d\u96be\u770b\uff0c\u5c24\u5176\u90a3\u4f4d\u59d1\u5a18\uff0c\u4e00 \u7b11\u5c31\u6709\u4e24\u4e2a\u9152\u7978\u7f8e\u6781\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e0b\u6b21\u4f60\u82e5\u518d\u770b\u5230\u4ed6\u4eec\uff0c\u8fd8\u8ba4\u4e0d\u8ba4\u5f97\uff1f\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u76f4\u9053\uff1a\u201d\u5f53\u7136\u8ba4\u5f97\uff0c\u6211\u5c0f\u79c3\u5b50\u53ef\u4e0d\u662f\u5fd8\u601d\u8d1f\u4e49\u7684\u4eba\uff0c\u65e0\u8bba\u8c01\u5bf9\u6211\u6709\u597d\u5904\uff0c\u6211\u4e00\u8f88 \u5b50\u90fd\u5fd8\u4e0d\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u62cd\u4e86\u62cd\u4ed6\u80a9\u5934\uff0c\u7b11\u9053\uff1a\u201c\u597d\uff0c\u597d\u6781\u4e86\u2026\u3002\u201d \u7b2c\u4e03\u7ae0\u3000\u4eba\u7ea6\u9ec4\u660f\u5f8c>> \u53e4\u9f99\u300a\u695a\u7559\u9999\u7cfb\u5217\xb7\u9b3c\u604b\u4f20\u5947\u300b \u7b2c\u4e03\u7ae0\u3000\u4eba\u7ea6\u9ec4\u660f\u5f8c \u3000\u3000\u5929\u8fd8\u6ca1\u6709\u9ed1\u77f3\u7ee3\u4e91\u5c31\u5df2\u5728\u7b49\u7740\u4e86\u3002 \u3000\u3000\u5979\u65e2\u4e0d\u77e5\u9053\u695a\u7559\u9999\u4e3a\u4f55\u8981\u7ea6\u5979\u5728\u8fd9\u91cc\u76f8\u89c1\uff0c\u66f4\u60f3\u4e0d\u5230\u81ea\u5df1\u4f1a\u5728\u4eb2\u59d0\u59d0\u7684\u575f\u5893\u524d\u548c\u4e2a\u964c\u751f \u7684\u7537\u4eba\u6709\u7ea6\u4f1a\u3002 \u3000\u3000\u4f46\u5979\u5374\u8fd8\u662f\u6765\u4e86\uff0c\u8fd8\u6ca1\u6709\u5403\u665a\u996d\uff0c\u5979\u7684\u5fc3\u5c31\u5df2\u98de\u5230\u4e86\u8fd9\u91cc\u3002\u521a\u63d0\u8d77\u7b77\u5b50\uff0c\u5c31\u6068\u4e0d\u5f97\u66f0\u5c06 \u996d\u6252\u5149\u3002 \u3000\u3000\u7136\u5f8c\u5979\u5c31\u7ad9\u5728\u95e8\u53e3\u7b49\u5929\u9ed1\u4e0b\u6765\uff0c\u5de6\u7b49\u5929\u4e5f\u4e0d\u9ed1\uff0c\u53f3\u7b49\u5929\u4e5f\u4e0d\u9ed1\uff0c\u5979\u5e38\u542c\u4eba\u8bf4\u5230\u4e86\u79cb\u5929\u5c31 \u4f1a\u9ed1\u5f97\u65e9\u4e9b\u3002 \u3000\u3000\u53ef\u662f\u4eca\u5929\uff0c\u5929\u9ed1\u5f97\u4e3a\u4ec0\u9ebd\u7279\u522b\u6162\uff1f \u3000\u3000\u5e78\u597d\u8fd9\u5730\u65b9\u5f88\u8352\u51c9\uff0c\u7ec8\u65e5\u77a7\u4e0d\u89c1\u4eba\u5f71\uff0c\u6240\u4ee5\u5979\u4e00\u4e2a\u4eba\u5728\u8fd9\u91cc\u75f4\u75f4\u7684\u7b49\u3002\u65e0\u8bba\u7b49\u591a\u4e45\u90fd\u4e0d \u6015\u88ab\u4eba\u77a7\u89c1\u3002 \u3000\u3000\u671b\u7740\u81ea\u5df1\u59d0\u59d0\u7684\u575f\uff0c\u5979\u5fc3\u91cc\u672c\u8be5\u53d1\u9178\u3001\u53d1\u82e6\u624d\u662f\uff0c\u4f46\u73b0\u5728\u53ea\u8981\u4e00\u60f3\u8d77\u695a\u7559\u9999\uff0c\u5979\u5fc3\u91cc\u5c31 \u89c9\u5f97\u751c\u751c\u7684\uff0c\u628a\u522b\u7684\u4e8b\u5168\u90fd\u5fd8\u4e86\u3002 \u3000\u3000\u811a\u8fd8\u6709\u4e9b\u75bc\uff0c\u5979\u5df2\u5c06\u695a\u7559\u9999\u66ff\u5979\u5305\u624e\u7684\u90a3\u5757\u4e1d\u5dfe\u6084\u6084\u85cf\u5728\u6000\u91cc\u3002\u6084\u6084\u6362\u4e86\u53cc\u65b0\u7ee3\u82b1\u978b\u3002 \u3000\u3000\u59d0\u59d0\u521a\u6b7b\u4e86\u6ca1\u51e0\u5929\uff0c\u5979\u5c31\u7a7f\u4e0a\u65b0\u7684\u7ee3\u82b1\u978b\u4e86\uff0c\u5979\u81ea\u5df2\u4e5f\u89c9\u5f97\u81ea\u5df2\u5f88\u4e0d\u5bf9\uff0c\u5374\u53c8\u5b9e\u5728\u5fcd\u4e0d \u4f4f\u4e0d\u7a7f\u3002 \u3000\u3000\u5979\u5c06\u8fd9\u53cc\u65b0\u7ee3\u82b1\u978b\u8131\u4e0b\u6765\u597d\u51e0\u6b21\uff0c\u6700\u5f8c\u8fd8\u662f\u7a7f\u4e86\u51fa\u6765\uff1b\u603b\u89c9\u5f97\u695a\u7559\u9999\u7684\u4e00\u53cc\u773c\u775b\u603b\u662f\u5728 \u770b\u7740\u5979\u7684\u811a\u3002 \u3000\u3000\u5979\u89c9\u5f97\u81ea\u5df1\u4e00\u7a7f\u4e0a\u8fd9\u53cc\u65b0\u978b\u5b50\uff0c\u811a\u5c31\u663e\u5f97\u7279\u522b\u597d\u770b\u3002 \u3000\u3000\u5929\u8d8a\u6765\u8d8a\u9ed1\uff0c\u98ce\u8d8a\u6765\u8d8a\u5927\u3002 \u3000\u3000\u5979\u5374\u89c9\u5f97\u8eab\u5b50\u5728\u53d1\u70ed\uff0c\u70ed\u5f97\u8981\u547d\u3002 \u3000\u3000\u201c\u4ed6\u4e3a\u4ec0\u9ebd\u8fd8\u4e0d\u6765\uff1f\u4f1a\u4e0d\u4f1a\u4e0d\u6765\u4e86\uff1f\u201d \u3000\u3000\u5979\u54ac\u7740\u5634\u5507\uff0c\u671b\u7740\u521a\u5347\u8d77\u7684\u65b0\u6708\u3002 \u3000\u3000\u201c\u6708\u4eae\u5347\u5230\u6811\u8fd9\u9ebd\u9ad8\u7684\u65f6\u5019\uff0c\u4ed6\u82e5\u8fd8\u4e0d\u6765\uff0c\u6211\u7edd\u4e0d\u518d\u7b49\u3002\u201d \u3000\u3000\u53ef\u662f\u6708\u4eae\u65e9\u5df2\u722c\u8fc7\u4e86\u6811\u68a2\uff0c\u5979\u8fd8\u662f\u5728\u7b49\u3002 \u3000\u3000\u5979\u4e00\u9762\u75f4\u75f4\u7684\u7b49\uff0c\u4e00\u9762\u6084\u6084\u7684\u6068\u3002 \u3000\u3000\u201c\u4ed6\u5c31\u7b97\u6765\u4e86\uff0c\u6211\u4e5f\u7edd\u4e0d\u776c\u4ed6\u3002\u201d \u3000\u3000\u53ef\u662f\u77a7\u89c1\u695a\u7559\u9999\u8eab\u5f71\uff0c\u5979\u5c31\u4ec0\u9ebd\u90fd\u5fd8\u4e86\uff0c\u5fd8\u5f97\u4e7e\u4e7e\u51c0\u51c0\u3002 \u3000\u3000\u5979\u98de\u4e5f\u4f3c\u7684\u8fce\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u695a\u7559\u9999\u7ec8\u65bc\u6765\u4e86\uff0c\u8fd8\u5e26\u6765\u4e86\u8bb8\u591a\u4eba\u3002 \u3000\u3000\u77f3\u7ee3\u4e91\u5219\u8dd1\u51fa\u4e24\u6b65\uff0c\u53c8\u505c\u4e0b\u811a\u3002 \u3000\u3000\u695a\u7559\u9999\u6b63\u5728\u5bf9\u7740\u5979\u5fae\u7b11\uff0c\u7b11\u5f97\u90a3\u9ebd\u6e29\u67d4\u3002 \u3000\u3000\u201c\u53ef\u662f\u4f60\u4e3a\u4ec0\u9ebd\u8981\u5e26\u8fd9\u9ebd\u591a\u4eba\u6765\u5462\uff1f\u201d\u77f3\u7ee3\u4e91\u54ac\u4e86\u54ac\u7259\uff0c\u626d\u5934\u5c31\u8d70\u3002 \u3000\u3000\u5979\u5e0c\u671b\u695a\u7559\u9999\u8ffd\u4e0a\u6765\uff0c\u4f46\u5374\u504f\u504f\u542c\u4e0d\u5230\u811a\u6b65\u58f0\uff0c\u5979\u5fcd\u4e0d\u4f4f\u653e\u7f13\u4e86\u6b65\u60f3\u56de\u5934\u53bb\uff0c\u5374\u53c8\u6015\u88ab \u4eba\u5bb6\u7b11\u3002\u5979\u53c8\u662f\u751f\u6c14\uff0c\u53c8\u662f\u4f24\u5fc3\uff0c\u53c8\u6709\u4e9b\u7740\u6025\uff0c\u6709\u4e9b\u8d28\u7591\uff0c\u6b63\u4e0d\u77e5\u8be5\u5982\u4f55\u662f\u597d\uff0c\u7a81\u542c\u8eab\u65c1\u6709 \u4eba\u5728\u7b11\uff0c\u695a\u7559\u9999\u4e0d\u77e5\u4f55\u65f6\u5df2\u8ffd\u4e0a\u6765\u4e86\uff0c\u6b63\u5e26\u7740\u7b11\u8e1e\u7740\u5979\uff0c\u7b11\u5f97\u90a3\u9ebd\u53ef\u7231\uff0c\u53c8\u90a3\u9ebd\u53ef\u6068\uff0c\u50cf\u662f \u5df2\u770b\u900f\u4e86\u5979\u5fc3\u4e8b\u3002 \u3000\u3000\u77f3\u7ee3\u4e91\u7684\u8138\u7ea2\u4e86\u3002\u695a\u7559\u9999\u6ca1\u6709\u8ffd\u4e0a\u6765\u7684\u65f6\u5019\uff0c\u5979\u60f3\u505c\u4e0b\u6765\uff0c\u695a\u7559\u9999\u8ffd\u4e0a\u6765\uff0c\u5979\u7684\u811a\u6b65\u5c31 \u53c8\u52a0\u5feb\u4e86\uff0c\u4f4e\u7740\u5934\u4ece\u695a\u7559\u9999\u9762\u524d\u51b2\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4f46\u695a\u7559\u9999\u5374\u62c9\u4f4f\u4e86\u5979\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u4f60\u8981\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u54ac\u7740\u4e0b\u5507\uff0c\u8dfa\u7740\u811a\u9053\uff1a\u201c\u653e\u624b\uff0c\u8ba9\u6211\u8d70\uff0c\u4f60\u65e2\u7136\u4e0d\u613f\u610f\u89c1\u6211\uff0c\u4e3a\u4f55\u53c8\u6765\u7f20\u7740 \u6211\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u8c01\u8bf4\u6211\u4e0d\u613f\u610f\u89c1\u4f60\uff1f\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u9053\uff1a\u201c\u90a3\u9ebd\u5c31\u7b97\u6211\u4e0d\u613f\u610f\u89c1\u4f60\u597d\u4e86\uff0c\u8ba9\u6211\u8d70\u5427\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff0c\u201c\u4f60\u65e2\u7136\u4e0d\u613f\u610f\u89c1\u6211\uff0c\u4e3a\u4ec0\u9ebd\u8981\u5728\u8fd9\u91cc\u7b49\u6211\uff1f\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u7684\u8138\u66f4\u7ea2\uff0c\u773c\u5708\u513f\u4e5f\u7ea2\u4e86\uff0c\u8dfa\u7740\u811a\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u60f3\u89c1\u4f60\uff0c\u660e\u77e5\u6211\u4e00\u5b9a\u4f1a\u5728\u8fd9 \u91cc\u7b49\u4f60\uff0c\u6240\u4ee5\u5c31\u5e26\u8fd9\u9ebd\u591a\u4eba\u6765\uff0c\u4f60\u591a\u6709\u672c\u4e8b\uff0c\u5230\u5904\u90fd\u6709\u5973\u5b69\u5b50\u7b49\u4f60\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u5176\u5b9e\u6211\u4e5f\u4e0d\u60f3\u5e26\u4ed6\u4eec\u6765\u7684\uff0c\u4f46\u6709\u4ef6\u4e8b\u5374\u975e\u8981\u4ed6\u4eec\u5e2e\u5fd9\u4e0d\u53ef\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u5fcd\u4e0d\u4f7f\u95ee\u9053\uff1a\u201c\u4ec0\u9ebd\u4e8b\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u8981\u4ed6\u4eec\u5c06\u8fd9\u5ea7\u575f\u5893\u6316\u5f00\u6765\u77a7\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u660e\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201d\u4f60\u2026\u75af\u4e86\uff0c\u4e3a\u4ec0\u9ebd\u8981\u6316\u6211\u59d0\u59d0\u7684\u575f\u3002\u201d\u74dc \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u4f60\u59d0\u59d0\u7684\u575f\uff0c\u82e5\u662f\u6211\u731c\u7684\u4e0d\u9519\uff0c\u8fd9\u4e00\u5b9a\u662f\u5ea7\u7a7a\u575f\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u53f9\u58f0\u9053\uff1a\u201c\u8c01\u8bf4\u7684\uff1f\u6211\u660e\u660e\u770b\u5230\u4ed6\u4eec\u628a\u68fa\u6750\u57cb\u4e0b\u53bb\u2026\u2026\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4ed6\u4eec\u867d\u7136\u5c06\u68fa\u6750\u57cb\u4e86\u4e0b\u53bb\uff0c\u4f46\u68fa\u6750\u91cc\u7edd\u4e0d\u4f1a\u6709\u4eba\u3002\u201d \u3000\u3000\u4ed6\u8f7b\u8f7b\u5730\u629a\u7740\u77f3\u7ee3\u4e91\u7684\u624b\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u6211\u7edd\u4e0d\u4f1a\u9a97\u4f60\uff0c\u5426\u5219\u6211\u5c31\u4e0d\u4f1a\u7ea6\u4f60\u5230\u8fd9\u91cc\u6765\u4e86\uff0c \u53ea\u8981\u4f60\u80af\u7b49\u4e00\u7b49\uff0c\u5c31\u4f1a\u77e5\u9053\u6211\u8bf4\u7684\u8bdd\u4e0d\u5047\u3002\u201d \u3000\u3000\u5f0a\u6750\u91cc\u679c\u7136\u6ca1\u6709\u4eba\uff0c\u53ea\u88c5\u7740\u51e0\u5757\u7816\u5934\u3002 \u3000\u3000\u51b7\u591c\u8352\u575f\uff0c\u79cb\u98ce\u745f\u745f\uff0c\u51b7\u6e05\u7684\u661f\u5149\u7167\u7740\u4e00\u5ea7\u88ab\u64a9\u5f00\u7684\u65b0\u575f\uff0c\u4e00\u53e3\u6f5c\u85cf\u7684\u68fa\u6750\uff0c\u68fa\u6750\u91cc\u5374 \u53ea\u6709\u51e0\u5757\u7816\u5934\u2026\u2026 \u3000\u3000\u6b7b\u4eba\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u96be\u9053\u5979\u5df2\u590d\u6d3b\uff1f \u3000\u3000\u77f3\u7ee3\u4e91\u5168\u8eab\u90fd\u5728\u53d1\u6296\uff0c\u7ec8\u65bc\u5fcd\u4e0d\u4f4f\u5636\u58f0\u5927\u53eb\u8d77\u6765\u3002 \u3000\u3000\u201c\u6211\u59d0\u59d0\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u6211\u59d0\u59d0\u600e\u4f1a\u53d8\u6210\u4e86\u7816\u5934\uff1f\u201d \u3000\u3000\u51c4\u5389\u7684\u547c\u58f0\u5e26\u8d77\u4e86\u56de\u97f3\uff0c\u5b9b\u5982\u9b3c\u54ed\uff0c\u53c8\u5b9b\u5982\u9b3c\u7b11\uff0c\u56db\u4e0b\u575f\u4e2d\u7684\u51a4\u9b3c\u90fd\u4f3c\u4e4e\u4e00\u8d77\u6eb6\u5165\u4e86\u9ed1 \u6697\u4e2d\uff0c\u5728\u5411\u5979\u5632\u5f04\u3002 \u3000\u3000\u5c31\u8fde\u4e45\u8d70\u6c5f\u6e56\u7684\u4e10\u5e2e\u5f1f\u5b50\u5fc3\u91cc\u90fd\u4e0d\u7981\u6cdb\u8d77\u4e86\u4e00\u9635\u5bd2\u610f\u3002 \u3000\u3000\u695a\u7559\u9999\u8f7b\u8f7b\u6402\u7740\u77f3\u7ee3\u4e91\u7684\u80a9\u5934\uff0c\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u770b\u5230\u4ed6\u4eec\u5c06\u4f60\u59d0\u59d0\u7684\u5c38\u8eab\u653e\u5165\u68fa\u6750\uff1f\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u9053\uff1a\u201c\u6211\u770b\u5230\u7684\uff0c\u6211\u4eb2\u773c\u770b\u5230\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u9489\u68fa\u6750\u7684\u65f6\u5019\u5462\uff1f\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u60f3\u4e86\u60f3\uff0c\u9053\uff1a\u201c\u9489\u68fa\u6750\u7684\u65f6\u5019\u6211\u4e0d\u5728\u2026\u3002\u6211\u672c\u6765\u4e5f\u4e0d\u613f\u610f\u79bb\u5f00\uff0c\u53ef\u662f\u4e8c\u5a76\u89c1\u6211\u60b2 \u54c0\u8fc7\u5ea6\uff0c\u4e00\u5b9a\u8981\u6211\u56de\u623f\u53bb\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u662f\u4f60\u4e8c\u53d4\u9489\u7684\uff1f\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u9053\uff1a\u201c\u55ef\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9080\u201c\u73b0\u5728\u4ed6\u7684\u4eba\u5462\uff1f\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u9053\uff1a\u201c\u59d0\u843d\u846c\u5f8c\u7b2c\u4e8c\u5929\uff0c\u4e8c\u53d4\u5c31\u5230\u7701\u57ce\u53bb\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u53bb\u4f5c\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u9053\uff1a\u201c\u53bb\u66ff\u859b\u5bb6\u5e84\u91c7\u529e\u5e74\u8d27\u3002\u201d \u3000\u3000\u91c7\u529e\u5e74\u8d27\u81ea\u7136\u662f\u4ef6\u5f88\u80a5\u7684\u5dee\u4f7f\u3002 \u3000\u3000\u695a\u7559\u9999\u773c\u775b\u4eae\u4e86\u3002\u9053\uff1a\u201c\u859b\u5bb6\u5e84\u7684\u5e74\u8d27\u662f\u4e0d\u662f\u6bcf\u5e74\u90fd\u7531\u4ed6\u91c7\u8d2d\uff1f\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u9053\uff1a\u201c\u5f80\u5e74\u90fd\u4e0d\u662f\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5634\u89d2\u9732\u51fa\u4e00\u4e1d\u96be\u6eaf\u7684\u7b11\u5bb9\uff0c\u9053\u201c\u5f80\u5e74\u90fd\u4e0d\u662f\uff0c\u4eca\u5e74\u8fd9\u5dee\u4f7f\u5374\u5ffd\u7136\u843d\u5230\u4ed6\u5934\u4e0a \u4e86\u2026\u2026\u6709\u8da3\u6709\u8da3\u8fd9\u4ef6\u4e8b\u7684\u786e\u6709\u8da3\u5f97\u5f88\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u53c8\u95ee\u9053\uff1a\u201c\u8fd9\u5dee\u4f7f\u662f\u4e0d\u662f\u859b\u4e8c\u516c\u5b50\u6d3e\u7ed9\u4ed6\u7684\uff1f\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u9053\uff1a\u201d\u4e0d\u9519\uff0c\u5c31\u56e0\u4e3a\u5982\u6b64\uff0c\u6240\u4ee5\u6211\u624d\u66f4\u8ba4\u4e3a\u59d0\u59d0\u662f\u88ab\u4ed6\u5bb3\u6b7b\u7684\uff0c\u4ed6\u4e3a\u4e86\u8d4e\u7f6a\u6240\u4ee5 \u624d\u5c06\u8fd9\u5dee\u4f7f\u6d3e\u7ed9\u4e8c\u53d4\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u4ed6\u53ea\u6015\u4e0d\u662f\u4e3a\u4e86\u8d4e\u7f6a\uff0c\u800c\u662f\u4e00\u2026\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u9053\uff1a\u201c\u662f\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u590d\u6742\u5f97\u5f88\uff0c\u73b0\u5728\u6211\u4eec\u5c31\u7b97\u5bf9\u4f60\u8bf4\u4e86\uff0c\u4f60\u4e5f\u4e0d\u4f1a\u660e\u767d\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u6d41\u6cea\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u60f3\u660e\u767d\uff0c\u6211\u53ea\u8981\u77e5\u9053\u6211\u59d0\u59d0\u7684\u5c38\u8eab\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u6c89\u541f\u4e86\u534a\u664c\uff0c\u9053\uff1a\u201c\u82e5\u662f\u6211\u731c\u7684\u4e0d\u9519\uff0c\u4e0d\u51fa\u53c1\u5929\uff0c\u6211\u5c31\u53ef\u4ee5\u5c06\u5979\u7684\u5c38\u8eab\u5e26\u56de\u7ed9 \u4f60\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u77e5\u9053\u5979\u7684\u4f53\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5230\u76ee\u524d\u4e3a\u6b62\uff0c\u6211\u8fd8\u53ea\u4e0d\u8fc7\u662f\u731c\u6d4b\u800c\u5df2\uff0c\u5e76\u4e0d\u80fd\u786e\u5b9a\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u9053\uff1a\u201c\u5979\u5c38\u8eab\u96be\u9053\u662f\u88ab\u4eba\u76d7\u8d70\u7684\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u9053\uff1a\u201c\u662f\u8c01\u76d7\u8d70\u4e86\u5979\u7684\u4f53\uff0c\u4e3a\u7684\u662f\u4ec0\u9ebd\uff1f\u5979\u53c8\u6ca1\u6709\u4ec0\u9ebd\u73e0\u5b9d\u966a\u846c\u4e4b\u7269\uff0c\u90a3\u4eba\u628a\u5979 \u7684\u4f53\u76d7\u8d70\u53c8\u6709\u4ec0\u9ebd\u7528\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u67d4\u58f0\u9053\uff1a\u201c\u73b0\u5728\u4f60\u6700\u597d\u4ec0\u9ebd\u90fd\u4e0d\u8981\u591a\u95ee\uff0c\u6211\u7b54\u5e94\u4f60\uff0c\u53c1\u5929\u4e4b\u5185\uff0c\u4e00\u5b9a\u628a\u6240\u6709\u7684\u4e8b \u90fd\u5bf9\u4f60\u8bf4\u6e05\u695a\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u56de\u5230\u201c\u63b7\u676f\u5c71\u5e84\u201d\u7684\u65f6\u5019\uff0c\u5929\u5df2\u5feb\u4eae\u4e86\u3002 \u3000\u3000\u5de6\u8f7b\u4faf\u867d\u7136\u65e9\u5df2\u7761\u4e0b\uff0c\u4f46\u542c\u5230\u695a\u7559\u9999\u56de\u6765\uff0c\u7acb\u523b\u5c31\u7a7f\u7740\u8863\u88f3\u8d76\u5230\u4ed6\u623f\u91cc\uff0c\u89c1\u9762\u5c31\u6293\u7740\u4ed6 \u7684\u624b\uff0c\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u6574\u5929\u90fd\u89c1\u4e0d\u5230\u4f60\u7684\u4eba\u5f71\uff0c\u53ef\u771f\u5feb\u628a\u6211\u6025\u6b7b\uff0c\u4f60\u7a76\u7adf\u8dd1\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u53ef\u63a2 \u51fa\u4ec0\u9ebd\u6d88\u606f\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u5148\u4e0d\u56de\u7b54\u4ed6\u8fd9\u53e5\u8bdd\uff0c\u5374\u53cd\u95ee\u9053\uff1a\u201c\u4e01\u4e8c\u4fa0\u5462\uff1f\u201d \u3000\u3000\u5de6\u8f7b\u4faf\u9053\uff1a\u201c\u4e01\u8001\u4e8c\u672c\u6765\u4e00\u76f4\u5728\u903c\u7740\u6211\uff0c\u7b80\u76f4\u903c\u7740\u6211\u8981\u53d1\u75af\u3002\u4f46\u4eca\u5929\u665a\u4e0a\uff0c\u4e5f\u4e0d\u77e5\u4e3a\u4e86 \u4ec0\u9ebd\uff0c\u4ed6\u53c8\u5ffd\u7136\u8dd1\u4e86\uff0c\u8fde\u8bdd\u90fd\u6ca1\u6709\u8bf4\uff0c\u770b\u60c5\u5f62\u597d\u50cf\u5bb6\u91cc\u51fa\u4e86\u4ec0\u9ebd\u4e8b\u6837\u3002\u201d \u3000\u3000\u4ed6\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u5144\u5f1f\u4e0d\u662f\u6211\u5e78\u707e\u4e50\u7978\u4f46\u6211\u4eec\u771f\u613f\u671b\u4ed6\u4eec\u5bb6\u91cc\u51fa\u4e9b\u4e8b\uff0c\u83ab\u8981\u518d\u5230 \u8fd9\u91cc\u6765\u76f8\u903c\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u59d1\u5a18\u5462\uff1f\u201d \u3000\u3000\u5de6\u8f7b\u5019\u9053\uff1a\u201c\u5979\u5012\u771f\u542c\u4f60\u7684\u8bdd\uff0c\u6574\u5929\u90fd\u5c06\u81ea\u5df1\u5173\u5728\u5c4b\u91cc\uff0c\u6ca1\u6709\u51fa\u53bb\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5979\u672c\u6765\u5c31\u662f\u4e2a\u4e56\u5b69\u5b50\u3002\u201d \u3000\u3000\u5de6\u8f7b\u4faf\u9053\uff1a\u201c\u53ef\u662f\u2026\u2026\u53ef\u662f\u8fd9\u7a76\u7adf\u662f\u600e\u9ebd\u56de\u4e8b\u5462\uff1f\u6211\u7a76\u7adf\u8be5\u600e\u9ebd\u529e\uff1f\u4e01\u5bb6\u90a3\u8fb9\u4e5f\u4e0d\u80fd\u8005 \u662f\u8fd9\u6837\u62d6\u4e0b\u53bb\u3002\u201d \u3000\u3000\u4ed6\u7d27\u7d27\u62c9\u7740\u695a\u7559\u9999\u7684\u624b\uff0c\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u4f60\u53ef\u5343\u4e07\u8981\u66ff\u6211\u60f3\u4e2a\u6cd5\u5b50\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6cd5\u5b50\u603b\u6709\u7684\uff0c\u4f46\u4e8c\u54e5\u73b0\u5728\u5374\u4e0d\u80fd\u7740\u6025\uff0c\u4e5f\u8bb8\u4e0d\u51fa\u53c1\u5929\uff0c\u4ec0\u9ebd\u90fd\u53ef\u4ee5\u89e3\u51b3 \u4e86\u2026\u2026\u201d \u3000\u3000\u53c1\u5929\u53c1\u5929\u2026\u2026\u8fd9\u53c1\u5929\u5185\u96be\u9053\u4f1a\u6709\u4ec0\u9ebd\u5947\u8ff9\u51fa\u73b0\u4e0d\u6210\uff1f \u3000\u3000\u5de6\u8f7b\u4faf\u8fd8\u5f85\u518d\u95ee\uff0c\u695a\u7559\u9999\u5374\u5c45\u7136\u5df2\u7761\u7740\u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\u9192\u65f6\uff0c\u5c31\u542c\u8bf4\u6709\u4e24\u4e2a\u4eba\u5728\u5916\u9762\u7b49\u7740\u4ed6\u3002 \u3000\u3000\u4e00\u4e2a\u4e10\u5e2e\u7684\u5f1f\u5b50\uff0c\u5de6\u4e8c\u7237\u5df2\u8bf7\u4ed6\u5728\u5ba2\u5385\u91cc\u559d\u8336\uff0c\u8fd8\u6709\u4e00\u4e2a\u4eba\u5374\u4e0d\u80af\u8bf4\u51fa\u81ea\u5df1\u7684\u6765\u610f\uff0c\u800c \u4e14\u4e00\u76f4\u7b49\u5728\u5927\u95e8\u5916\uff0c\u4e0d\u80af\u8fdb\u6765\u3002 \u3000\u3000\u695a\u7559\u9999\u76b1\u4e86\u76b1\u7709\uff0c\u9053\uff1a\u201c\u8fd9\u4eba\u957f\u5f97\u4ec0\u9ebd\u6837\u5b50\uff1f\u201d \u3000\u3000\u56de\u8bdd\u7684\u4eba\u5de6\u5347\uff0c\u662f\u5de6\u4e8c\u7237\u7684\u4eb2\u4fe1\uff0c\u81ea\u7136\u4e5f\u662f\u4e2a\u5f88\u7cbe\u660e\u5e72\u7ec3\u7684\u4eba\uff0c\u4ed6\u60f3\u4e86\u60f3\u624d\u7b11\u7740\u9053\uff1a \u201c\u8fd9\u4eba\u957f\u5f97\u5012\u4e5f\u5f88\u5e73\u5e38\uff0c\u4f46\u5f62\u8ff9\u5374\u5f88\u53ef\u7591\uff0c\u800c\u4e14\u4e0d\u8bf4\u5b9e\u8bdd\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u5de6\u5347\u9053\uff1a\u201c\u4ed6\u8bf4\u662f\u81ea\u8fdc\u9053\u8d76\u6765\u7684\uff0c\u4f46\u5c0f\u4eba\u770b\u4ed6\u8eab\u4e0a\u5374\u5f88\u4e7e\u51c0\uff0c\u4e00\u70b9\u4e5f\u6ca1\u6709\u98ce\u5c18\u4e4b\u8272\uff0c\u9a91 \u6765\u7684\u90a3\u5339\u9a6c\u4e5f\u4e0d\u50cf\u662f\u8d70\u8fc7\u8fdc\u8def\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u770b\u4ed6\u50cf\u4e0d\u50cf\u7ec3\u5bb6\u5b50\uff1f\u201d \u3000\u3000\u5de6\u5347\u9053\uff1a\u201c\u4ed6\u8d70\u8def\u5f88\u8f7b\u5feb\u52a8\u4f5c\u4e5f\u5f88\u654f\u6377\uff0c\u770b\u6765\u867d\u6709\u51e0\u5206\u529f\u592b\uff0c\u4f46\u5374\u7edd\u4e0d\u50cf\u662f\u6c5f\u6e56\u4eba\uff0c\u5c0f \u4eba\u6562\u62c5\u4fdd\u4ed6\u8fd9\u8f88\u5b50\u7edd\u6ca1\u6709\u8d70\u51fa\u677e\u6c5f\u5e9c\u767e\u91cc\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u96be\u602a\u4e8c\u7237\u603b\u662f\u8bf4\u4f60\u80fd\u5e72\uff0c\u5c31\u51ed\u4f60\u8fd9\u53cc\u773c\u775b\u3002\u6c5f\u6e56\u4e2d\u5df2\u5f88\u5c11\u6709\u4eba\u80fd\u8d76 \u5f97\u4e0a\u4f60\u3002\u201d \u3000\u3000\u5de6\u5347\u8d76\u7d27\u8eac\u8eab\u9053\uff1a\u201c\u8fd9\u8fd8\u4e0d\u90fd\u662f\u4e8c\u7237\u548c\u9999\u5e05\u4f60\u8001\u4eba\u5bb6\u7684\u6559\u8bf2\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e8c\u7237\u5462\uff1f\u201d \u3000\u3000\u201c\u4e8c\u7237\u5403\u4e86\u5f20\u8001\u5148\u751f\u4e24\u5e16\u5b81\u795e\u836f\uff0c\u5230\u5348\u65f6\u624d\u6b47\u4e0b\uff0c\u73b0\u5728\u8fd8\u6ca1\u9192\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5927\u59d1\u5a18\u5462\uff1f\u201d \u3000\u3000\u5de6\u5347\u9053\uff1a\u201c\u59d1\u5a18\u770b\u6765\u6c14\u8272\u5012\u5f88\u597d\uff0c\u800c\u4e14\u4e5f\u5403\u5f97\u4e0b\u4e1c\u897f\u4e86\uff0c\u5c31\u662f\u4e0d\u8ba9\u4eba\u5230\u5979\u5c4b\u91cc\u53bb\uff0c\u6574\u5929 \u5173\u7740\u623f\u95e8\u5728\u5c4b\u5b50\u91cc\u3002\u201d \u3000\u3000\u4ed6\u53f9\u4e86\u53e3\u6c14\u538b\u4f4e\u4e86\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u9999\u5e05\u603b\u8be5\u77e5\u9053\uff0c\u59cb\u5a18\u4ee5\u524d\u4e0d\u662f\u8fd9\u4e2a\u6837\u5b50\uff0c\u4ece\u6765\u4e0d\u613f\u5173\u5728 \u5c4b\u5b50\u91cc\uff0c\u8fd9\u4ef6\u4e8b\u3002\u3002\u3002\u8fd9\u4ef6\u4e8b\u7684\u786e\u6709\u70b9\u90aa\u95e8\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6c89\u541f\u7740\uff0c\u9053\uff1a\u201c\u70e6\u4f60\u53bb\u901a\u62a5\u59d1\u5a18\uff0c\u5c31\u8bf4\u6211\u660e\u5929\u4e00\u5b9a\u6709\u597d\u6d88\u606f\u544a\u8bc9\u5979\uff0c\u53eb\u5979\u83ab\u8981\u7740 \u6025\u3002\u201d \u3000\u3000\u5de6\u5347\u9053\uff1a\u201c\u4f60\u8001\u4eba\u5bb6\u73b0\u5728\u662f\u4e0d\u662f\u8981\u5148\u5230\u5ba2\u5385\u53bb\u89c1\u89c1\u90a3\u4f4d\u4e10\u5e2e\u7684\u5c0f\u5144\u5f1f\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u597d\u3002\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u663e\u7136\u5df2\u7ecf\u7b49\u5f97\u4e0d\u8010\u70e6\u4e86\uff0c\u6b63\u5728\u90a3\u91cc\u4e1c\u5f20\u897f\u671b\uff0c\u770b\u5230\u695a\u7559\u9999\u7acb\u523b\u5c31\u8fce\u4e0a\u524d\u6765\u8bf7\u5b89\uff0c \u7136\u5f8c\u5c31\u7b11\u9053\uff0c\u201c\u9999\u5e05\u6628\u5929\u76fc\u5490\u6211\u4eec\u529e\u7684\u4e8b\uff0c\u4eca\u5929\u5df2\u7ecf\u6709\u4e9b\u7709\u76ee\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u529e\u4e8b\u5012\u771f\u5feb\u3002\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u9053\uff1a\u201c\u6628\u5929\u9999\u5e05\u4e00\u4ea4\u4ee3\u4e0b\u6765\uff0c\u5927\u54e5\u7acb\u523b\u5c31\u53eb\u5168\u57ce\u7684\u5f1f\u5144\u56db\u4e0b\u6253\u65ad\uff0c\u6700\u8fd1\u6709\u6ca1\u6709\u8bf4 \u5317\u65b9\u8bdd\u7684\u4e24\u751f\u4eba\u5728\u57ce\u91cc\u843d\u811a\uff0c\u4eca\u5929\u4e0a\u5348\uff0c\u5c31\u6709\u4e86\u6d88\u606f\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5fae\u5fae\u7b11\u7740\uff0c\u7b49\u4ed6\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u5c0f\u79c3\u5b50\u9053\uff1a\u201c\u6700\u8fd1\u5230\u677e\u6c5f\u5e9c\u6765\u7684\u5317\u65b9\u4eba\u4e00\u5171\u5341\u4e00\u4e2a\uff0c\u5176\u4e2d\u516d\u4e2a\u662f\u4ece\u5f20\u5bb6\u53e3\u6765\u7684\u76ae\u8d27\u5546\uff0c \u5e74\u7eaa\u5df2\u6709\u56db\u4e94\u5341\u4e86\uff0c\u5f53\u7136\u4e0d\u4f1a\u662f\u9999\u5e05\u8981\u627e\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u55ef\u3002\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u9053\uff1a\u201c\u8fd8\u6709\u56db\u4e2a\u4eba\u662f\u4eac\u57ce\u6765\u7684\u6b66\u5e08\uff0c\u6709\u4e24\u4f4d\u5e74\u7eaa\u5f88\u8f7b\uff0c\u4f46\u6211\u4eec\u5df2\u53bb\u76d8\u8fc7\u4ed6\u7684\u5e95\uff0c \u56db\u4e2a\u4eba\u4e2d\u6ca1\u6709\u4e00\u4e2a\u59d3\u53f6\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u8fd8\u6709\u4e24\u4e2a\u4eba\u5462\uff1f\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u9053\uff1a\u201d\u90a3\u4e24\u4eba\u662f\u5bf9\u592b\u59bb\uff0c\u4e24\u4eba\u5e74\u7eaa\u90fd\u5f88\u8f7b\uff0c\u4e5f\u90fd\u5f88\u597d\u770b\uff0c\u636e\u8bf4\u662f\u4eac\u57ce\u4ec0\u9ebd\u5927\u5b98\u7684 \u516c\u5b50\uff0c\u5e26\u7740\u65b0\u5a5a\u5ab3\u5987\u5230\u6c5f\u5357\u6765\u6e38\u8d4f\uff0c\u987a\u4fbf\u4e5f\u6765\u5c1d\u5c1d\u677e\u6c5f\u5e9c\u82a6\u9c7c\uff0c\u4f46\u5c31\u8fde\u90a3\u5ba2\u6808\u7684\u5e97\u5c0f\u4e8c\u90fd\u77e5 \u9053\u4ed6\u5728\u8bf4\u8c0e\u3002\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u4eec\u8bf4\u6765\u6e38\u5c71\u73a9\u6c34\u7684\uff0c\u5374\u6574\u5929\u5173\u5728\u5c4b\u5b50\u91cc\u4e0d\u4e5f\u51fa\u6765\uff0c\u66f4\u4ece\u6765\u4e5f\u6ca1\u6709\u5403 \u8fc7\u4e00\u6761\u9c9c\u9c7c\uff0c\u4e24\u4eba\u7a7f\u7684\u8863\u670d\u867d\u7136\u72e0\u534e\u8d35\uff0c\u4f46\u6c14\u6d3e\u5374\u5f88\u5c0f\uff0c\u51fa\u624b\u4e5f\u4e0d\u5927\u65b9\uff0c\u4e00\u70b9\u4e5f\u4e0d\u50cf\u6709\u94b1\u7684 \u9614\u5c11\u7237\u3002\u201d \u3000\u3000\u4ed6\u7b11\u4e86\u7b11\uff0c\u4fcf\u58f0\u9053\uff1a\u201c\u542c\u90a3\u5e97\u5c0f\u4e8c\u54e5\u8bf4\uff0c\u6709\u4e00\u5929\u4ed6\u65e0\u610f\u4e2d\u89c1\u8fd9\u4f4d\u5927\u5c11\u7237\u5c45\u7136\u66ff\u4ed6\u8001\u5a46\u6d17 \u811a\uff0c\u4ed6\u8001\u5a46\u5acc\u6c34\u592a\u70ed\uff0c\u4e00\u811a\u5c06\u6574\u76c6\u6d17\u811a\u6c34\u5168\u90fd\u8e22\u5728\u8fd9\u4f4d\u5927\u5c11\u7237\u8eab\u4e0a\uff0c\u8fd9\u5927\u5c11\u7237\u5374\u8fde\u5c41\u4e5f\u4e0d\u6562 \u653e\u4e00\u4e2a\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u773c\u775b\u4eae\u4e86\uff0c\u9053\uff1a\u201c\u5979\u59d3\u4ec0\uff1f\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u9053\uff1a\u201c\u4ed6\u5728\u67dc\u53f0\u4e0a\u8bf4\u7684\u540d\u5b57\u662f\u674e\u660e\u751f\uff0c\u4f46\u540d\u5b57\u53ef\u4ee5\u6539\u7684\u3002\u201d \u3000\u3000\u201d\u4e0d\u9519\uff0c\u540d\u5b57\u53ef\u4ee5\u7528\u5047\u7684\u3002\u8fd9\u4e24\u4eba\u4f4f\u5728\u54ea\u5bb6\u5ba2\u6808\uff1f\u201d\u74dc \u3000\u3000\u5c0f\u79c3\u5b50\u9053\uff1a\u201c\u5c31\u5728\u4e1c\u57ce\u95e8\u53e3\u90a3\u5bb6\u798f\u76db\u8001\u5e97\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u597d\uff0c\u4f60\u5148\u5230\u90a3\u91cc\u7b49\u6211\uff0c\u6211\u968f\u5f8c\u5c31\u6765\uff1f\u201d \u3000\u3000\u6cb3\u7554\u7684\u67f3\u6811\u4e0b\u7cfb\u7740\u4e00\u5339\u767d\u9a6c\u3002\u4e00\u4e2a\u9752\u8863\u4eba\u6b63\u7ad9\u5728\u6811\u4e0b\uff0c\u773c\u775b\u76ef\u7740\u201c\u63b7\u676f\u5c71\u5e84\u201d\u7684\u5927\u95e8\u3002 \u3000\u3000\u695a\u7559\u9999\u5e76\u4e0d\u8ba4\u5f97\u4ed6\uff0c\u4ed6\u5374\u8ba4\u5f97\u695a\u7559\u9999\u3002 \u3000\u3000\u695a\u7559\u9999\u95ee\u4ed6\uff1a\u201c\u6709\u4f55\u8d35\u5e72\uff1f\u201d \u3000\u3000\u8fd9\u9752\u8863\u4eba\u53ea\u9053\uff1a\u201c\u4e3b\u4eba\u6709\u5f88\u8981\u7d27\u7684\u4e8b\u8981\u89c1\u9999\u5e05\u4e00\u9762\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u95ee\u4ed6\uff1a\u201c\u4f60\u5bb6\u4e3b\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u8fd9\u9752\u8863\u4eba\u8d54\u7b11\u9053\uff1a\u201c\u662f\u9999\u5e05\u7684\u6545\u4ea4\uff0c\u9999\u5e05\u4e00\u89c1\u9762\u5c31\u77e5\u9053\u4e86\uff0c\u73b0\u5728\u4ed6\u6b63\u5728\u524d\u9762\u76f8\u5019\uff0c\u7279\u547d \u5c0f\u4eba\u6765\u8fd9\u91cc\u76f8\u8bf7\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u95ee\u4ed6\uff1a\u201c\u4f60\u5bb6\u4e3b\u4eba\u4e3a\u4f55\u4e0d\u6765\uff1f\u53c8\u4e3a\u4f55\u4e0d\u8ba9\u4f60\u8bf4\u51fa\u4ed6\u7684\u59d3\u540d\uff1f\u201d \u3000\u3000\u8fd9\u9752\u8863\u4eba\u5374\u4ec0\u9ebd\u8bdd\u90fd\u4e0d\u80af\u8bf4\u4e86\uff0c\u53ea\u662f\u5f2f\u7740\u8170\uff0c\u8d54\u7740\u7b11\uff0c\u4f46\u5374\u663e\u7136\u662f\u5047\u7b11\uff0c\u4e0d\u6000\u597d\u610f\u7684 \u7b11\u3002 \u3000\u3000\u695a\u7559\u9999\u4e5f\u7b11\u4e86\uff0c\u51dd\u6ce8\u7740\u4ed6\uff0c\u60a0\u7136\u9053\uff1a\u201c\u4f60\u4ec0\u9ebd\u90fd\u4e0d\u80af\u8bf4\uff0c\u600e\u77e5\u6211\u4f1a\u8ddf\u4f60\u53bb\u5462\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u8d54\u7b11\u9053\uff1a\u201c\u9999\u5e05\u82e5\u662f\u4e0d\u53bb\uff0c\u5c82\u975e\u5c31\u6c38\u8fdc\u4e0d\u77e5\u9053\u6211\u5bb6\u4e3b\u4eba\u662f\u8c01\u4e86\uff0c\u90a3\u9ebd\u9999\u5e05\u591a\u5c11\u603b \u4f1a\u89c9\u5f97\u6709\u4e9b\u9057\u61be\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u5927\u7b11\u9053\uff1a\u201c\u7167\u5440\uff0c\u4f60\u5bb6\u4e3b\u4eba\u5012\u771f\u662f\u7b97\u51c6\u4e86\u6211\u7684\u77ed\u5904\uff0c\u6211\u82e5\u4e0d\u53bb\u89c1\u4ed6\u4e00\u9762\uff0c\u53ea\u6015\u771f \u7684\u8981\u8fde\u89c9\u90fd\u7761\u4e0d\u7740\u4e86\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u7b11\u9053\uff1a\u201c\u6211\u5bb6\u4e3b\u4eba\u65e9\u8bf4\u8fc7\uff0c\u5929\u4e0b\u7edd\u6ca1\u6709\u695a\u9999\u5e05\u4e0d\u6562\u89c1\u7684\u4eba\uff0c\u4e5f\u7edd\u6ca1\u6709\u695a\u9999\u5e05\u4e0d\u6562 \u53bb\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u4ed6\u4e00\u9762\u8bf4\u8bdd\uff0c\u4e00\u9762\u5df2\u89e3\u5f00\u4e86\u7cfb\u5728\u6811\u4e0a\u7684\u9a6c\u978d\uff0c\u7528\u8863\u8896\u62ed\u51c0\u4e86\u978d\u4e0a\u7684\u5c18\u571f\uff0c\u8eac\u8eab\u8d54\u7b11\u9053\uff1a \u201c\u9999\u5e05\u8bf7\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u9a91\u9a6c\uff0c\u4f60\u5462\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u7b11\u9053\uff1a\u201c\u5df2\u7ecf\u7528\u4e0d\u7740\u6211\u4e86\uff0c\u8fd9\u5339\u9a6c\u81ea\u7136\u4f1a\u5e26\u9999\u5e05\u53bb\u7684\u3002\u201d \u3000\u3000\u8fd9\u9752\u8863\u4eba\u7684\u786e\u6a21\u900f\u4e86\u695a\u7559\u9999\u7684\u813e\u6c14\uff0c\u8d8a\u5371\u9669\uff0c\u8d8a\u8be1\u79d8\u7684\u4e8b\uff0c\u695a\u7559\u9999\u5f80\u5f80\u4f1a\u89c9\u5f97\u8d8a\u6709\u8da3\u3002 \u3000\u3000\u6709\u65f6\u4ed6\u7eb5\u7136\u660e\u77e5\u524d\u9762\u662f\u9677\u9631\uff0c\u4e5f\u4f1a\u5fcd\u4e0d\u4f4f\u5f80\u4e0a\u8df3\u7684\u3002 \u3000\u3000\u695a\u7559\u9999\u9a91\u7740\u9a6c\u9a91\u8fc7\u5c0f\u6865\uff0c\u8fd8\u9690\u9690\u53ef\u4ee5\u542c\u5230\u90a3\u9752\u8863\u4eba\u7684\u7b11\u58f0\u4f20\u6765\uff0c \u3000\u3000\u7b11\u58f0\u4e2d\u5e26\u7740\u53c1\u5206\u8c17\u5a9a\uff0c\u5374\u5e26\u7740\u4e03\u5206\u8be1\u610f\u3002 \u3000\u3000\u4ed6\u7684\u4e3b\u4eba\u7a76\u7adf\u662f\u8c01\uff0c\u83ab\u975e\u5c31\u662f\u90a3\u523a\u5ba2\u7ec4\u7ec7\u7684\u9996\u9886\uff1f \u3000\u3000\u695a\u7559\u9999\u89c9\u5f97\u5f88\u5174\u594b\uff0c\u5c31\u50cf\u662f\u5c0f\u65f6\u5019\u548c\u5c0f\u5b69\u5b50\u6349\u8ff7\u85cf\u65f6\u7684\u5fc3\u60c5\u4e00\u6837\uff0c\u5145\u6ee1\u4e86\u65b0\u5947\u7684\u7d27\u5f20\u548c \u523a\u6fc0\u3002 \u3000\u3000\u9a6c\u8d70\u5f97\u5f88\u5e73\u7a33\uff0c\u4e5f\u5f88\u5feb\uff0c\u663e\u7136\u662f\u4e45\u7ecf\u8bad\u7ec3\u7684\u826f\u9a79\u3002 \u3000\u3000\u695a\u7559\u9999\u5e76\u6ca1\u6709\u633d\u7ebd\u3002\u4ed6\u5c45\u7136\u968f\u968f\u4fbf\u4fbf\u7684\u5c31\u5c06\u81ea\u5df1\u7684\u547d\u8fd0\u6258\u7ed9\u8fd9\u5339\u9a6c\u4e86\uff0c\u800c\u4e14\u5c45\u7136\u4e00\u70b9\u4e5f \u4e0d\u7740\u6025\u3002 \u3000\u3000\u8fd9\u5339\u4e45\u7ecf\u8bad\u7ec3\u7684\u826f\u9a79\uff0c\u7adf\u80cc\u7740\u9999\u7387\u6f2b\u65e0\u76ee\u7684\u9a70\u53bb\uff0c\u9a6c\u8d8a\u8d70\u8d8a\u5feb\uff0c\u4e24\u8fb9\u7684\u6811\u6728\u98de\u4e5f\u4f3c\u7684\u5012 \u9000\u56de\u53bb\u3002 \u3000\u3000\u695a\u7559\u9999\u7d22\u6027\u95ed\u4e0a\u4e86\u773c\u775b\u3002 \u3000\u3000\u4ed6\u5f20\u5f00\u773c\u775b\u65f6\u4f1a\u770b\u5230\u4ec0\u9ebd\u5462\uff1f \u3000\u3000\u7ea6\u4ed6\u7684\u4eba\u4e5f\u8bb8\u5e76\u4e0d\u662f\u90a3\u795e\u79d8\u7684\u523a\u5ba2\uff0c\u4e5f\u8bb8\u5e76\u4e0d\u662f\u4ed6\u7684\u4ec7\u654c\uff0c\u800c\u662f\u4ed6\u7684\u670b\u53cb\uff0c\u4ed6\u6709\u5f88\u591a\u670b \u53cb\u90fd\u559c\u6b22\u5f00\u73a9\u7b11\u7684\u3002 \u3000\u3000\u4f55\u51b5\uff0c\u8fd8\u6709\u8bb8\u591a\u5973\u5b69\u5b50\uff0c\u8bb8\u591a\u7f8e\u4e3d\u7684\u5973\u5b69\u5b50\u2026\u2026 \u3000\u3000\u4ed6\u5ffd\u7136\u60f3\u8d77\u4e00\u4e2a\u59d3\u8521\u7684\u5973\u5b69\u5b50\uff0c\u5927\u5927\u7684\u773c\u775b\u7ec6\u7ec6\u7684\u8170\u8fd8\u6709\u4e24\u4e2a\u5f88\u6df1\u7684\u9152\u6da1\uff0c\u6709\u4e00\u6b21\u5728\u8863 \u67dc\u91cc\u8eb2\u4e86\u5927\u534a\u5929\uff0c\u8fde\u996d\u90fd\u6ca1\u6709\u5403\u997f\u5f97\u51e0\u4e4e\u817f\u90fd\u8f6f\u4e86\uff0c\u4e3a\u7684\u662f\u8981\u7b49\u4ed6\u56de\u6765\uff0c\u5413\u4ed6\u4e00\u8df3\u3002 \u3000\u3000\u711a\u7559\u9999\u5fcd\u4e0d\u4f4f\u7b11\u4e86\u3002 \u3000\u3000\u4ed6\u53ea\u5e0c\u671b\u767d\u5df1\u5f20\u5f00\u773c\u775b\u65f6\uff0c\u4f1a\u770b\u5230\u5979\u4eec\u5176\u4e2d\u4e00\u4e2a\u3002 \u3000\u3000\u5176\u5b9e\u4ed6\u4e5f\u5e76\u4e0d\u662f\u4e2a\u5f88\u559c\u6b22\u505a\u68a6\u7684\u4eba\uff0c\u53ea\u4e0d\u8fc7\u9047\u7740\u7684\u4e8b\u8d8a\u5371\u9669\uff0c\u4ed6\u8d8a\u559c\u6b22\u53bb\u60f3\u4e00\u4e9b\u6709\u8da3\u7684 \u4e8b\u3002 \u3000\u3000\u4ed6\u4e0d\u559c\u6b22\u7d27\u5f20\uff0c\u8651\uff0c\u5bb3\u6015\u2026\u2026 \u3000\u3000\u4ed6\u77e5\u9053\u8fd9\u4e9b\u4e8b\u5bf9\u4efb\u4f55\u4eba\u90fd\u6ca1\u6709\u597d\u5904\u3002 \u3000\u3000\u9a6c\u5954\u884c\u4e86\u5f88\u4e45\uff0c\u9aa4\u7136\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u8e44\u58f0\u9aa4\u989d\uff0c\u53ea\u5269\u4e0b\u5fae\u98ce\u5728\u8033\u7554\u8f7b\u8f7b\u5439\u52a8\uff0c\u5929\u5730\u95f4\u4eff\u4f5b\u5f88\u5b89\u9759\u2014\u2014\u4ed6\u8fd8\u662f\u6ca1\u6709\u5f20\u5f00\u773c\u775b\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u6b63\u5411\u4ed6\u8d70\u8fc7\u6765\u3002 \u3000\u3000\u8fd9\u4eba\u8d70\u5728\u843d\u53f6\u4e0a\uff0c\u811a\u6b65\u867d\u4ecd\u662f\u5341\u5206\u8f7b\u5fae\uff0c\u9664\u4e86\u695a\u7559\u9999\u4e4b\u5916\uff0c\u4e16\u4e0a\u6015\u5f88\u5c11\u6709\u4eba\u80fd\u542c\u5f97\u5230\u3002 \u3000\u3000\u8fd9\u4eba\u8fd8\u8fdc\u5728\u5341\u6b65\u5916\uff0c\u695a\u7559\u9999\u5c31\u89c9\u5f97\u6709\u4e00\u80a1\u53ef\u6015\u7684\u5251\u6c14\u8feb\u4eba\u7709\u776b\uff0c\u4f46\u662f\u4ed6\u53cd\u800c\u7b11\uff0c\u5fae\u7b11 \u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\uff0c\u6211\u5b9e\u5728\u6ca1\u6709\u60f3\u5230\u4f1a\u662f\u4f60\u3002\u201d \u3000\u3000\u5728\u695a\u7559\u9999\u9762\u524d\u7684\u4eba\uff0c\u5c45\u7136\u7adf\u662f\u859b\u8863\u4eba\u3002 \u3000\u3000\u79cb\u98ce\u5377\u8d77\u4e86\u6ee1\u5730\u9ec4\u53f6\u3002\u859b\u8863\u4eba\u6b63\u6807\u67aa\u822c\u8083\u7acb\u5728\u98de\u821e\u7684\u9ec4\u53f6\u4e2d\uff0c\u7a7f\u7740\u8eab\u96ea\u767d\u7684\u8863\u88f3\uff0c\u767d\u5f97 \u8000\u773c\uff1b\u4ed6\u8eab\u5f8c\u80cc\u7740\u67c4\u4e4c\u9798\u957f\u5251\uff0c\u80cc\u5251\u7684\u65b9\u5f0f\uff0c\u4efb\u4f55\u4eba\u90fd\u60f3\u5f97\u5230\u4ed6\u5982\u6b64\u80cc\u5251\uff0c\u53ea\u4e3a\u4e86\u80fd\u5728\u6700\u77ed \u7684\u65f6\u95f4\u91cc\u5c06\u5251\u62d4\u51fa\u6765\u3002 \u3000\u3000\u73b0\u5728\uff0c\u5251\u8fd8\u672a\u51fa\u62d4\u5251\u6c14\u5374\u5df2\u51fa\u9798\u3002 \u3000\u3000\u4ed6\u7684\u773c\u775b\u91cc\u7adf\u6709\u80a1\u53ef\u6015\u7684\u5251\u6c14\u3002\u53ea\u56e0\u4ed6\u7684\u5251\u5c31\u662f\u4ed6\u7684\u4eba\uff0c\u4ed6\u7684\u4eba\u5df2\u548c\u4ed6\u7684\u5251\u878d\u4e3a\u4e00\u4f53\u3002 \u3000\u3000\u4ed6\u9759\u9759\u7684\u671b\u7740\u695a\u7559\u9999\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u65e9\u5c31\u8be5\u60f3\u5230\u662f\u6211\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u65e9\u8be5\u60f3\u5230\u4f60\u7684\uff0c\u8fde\u5de6\u5347\u90fd\u5df2\u770b\u51fa\u4f60\u90a3\u4f4d\u4f7f\u8005\u5e76\u975e\u8fdc\u9053\u800c\u6765\uff0c\u859b\u5bb6 \u5e84\u7684\u4eba\u5230\u4e86\u5de6\u5bb6\uff0c\u81ea\u7136\u4e0d\u80af\u8bf4\u51fa\u81ea\u5df1\u7684\u8eab\u4efd\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u51b3\u6218\u5728\u5373\uff0c\u6211\u4e0d\u613f\u4ed6\u518d\u548c\u5de6\u5bb6\u7684\u4eba\u751f\u4e8b\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f46\u4ed6\u5728\u6211\u524d\u9762\u4e3a\u4f55\u8fd8\u4e0d\u80af\u8bf4\u51fa\u6765\u610f\u5462\uff1f\u201d \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u53ea\u56e0\u4ed6\u6015\u4f60\u4e0d\u6562\u6765\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e0d\u6562\u6765\uff1f\u6211\u4e3a\u4f55\u4e0d\u6562\u6765\uff1f\u6709\u670b\u53cb\u7ea6\u6211\uff0c\u6211\u65e0\u8bba\u5982\u4f55\u90fd\u4f1a\u8d76\u6765\u7684\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u77aa\u7740\u4ed6\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u4f60\u4e0d\u6562\u6765\uff0c\u53ea\u56e0\u4e3a\u4f60\u5df2\u4e0d\u662f\u6211\u7684\u670b\u53cb\u201d \u3000\u3000\u695a\u7559\u9999\u6478\u4e86\u6478\u9f3b\u5b50\uff0c\u7b11\u9053\uff1a\u201c\u6211\u6628\u5929\u8fd8\u662f\u4f60\u7684\u670b\u53cb\uff0c\u600e\u9ebd\u4eca\u5929\u5c31\u4e0d\u662f\u4e86\uff1f\u201d \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u6211\u672c\u6765\u786e\u60f3\u4ea4\u4f60\u8fd9\u4e2a\u670b\u53cb\uff0c\u6240\u4ee5\u624d\u8bfa\u4f60\u5165\u5251\u5ba4\uff0c\u8c01\u77e5\u4f60\u2026\u3002\u201d \u3000\u3000\u4ed6\u9762\u4e0a\u5ffd\u7136\u6cdb\u8d77\u4e00\u9635\u9752\u6c14\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u8c01\u77e5\u4f60\u6839\u672c\u4e0d\u914d\u505a\u670b\u53cb\u3002\u201d \u3000\u3000\u201c\u4f60\u2026\u2026\u4f60\u96be\u9053\u8ba4\u4e3a\u6211\u5077\u4e86\u4f60\u7684\u5251\uff1f\u201d \u3000\u3000\u859b\u8863\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u53ea\u56e0\u6211\u5e26\u4f60\u53bb\u8fc7\u4e00\u6b21\uff0c\u6240\u4ee5\u4f60\u624d\u8f7b\u8f66\u719f\u8def\uff0c\u5426\u5219\u4f60\u600e\u80fd\u5f97\u624b\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u51e0\u4e4e\u5c06\u9f3b\u5b50\u90fd\u6478\u7ea2\u4e86\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u7684\u5251\u771f\u7684\u88ab\u7a83\u4e86\uff1f\u201d \u3000\u3000\u859b\u8863\u4eba\u6ca1\u6709\u56de\u7b54\u8fd9\u53e5\u8bdd\uff0c\u5374\u5782\u4e0b\u5934\u51dd\u7740\u81ea\u5df1\u8eab\u4e0a\u7684\u767d\u886b\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u8fd9\u4ef6\u8863\u670d\uff0c\u8fd8\u662f \u6211\u4e8c\u5341\u5e74\u524d\u505a\u7684\uff0c\u6211\u76f4\u5230\u4eca\u5929\u624d\u7a7f\u4e0a\u5b83\uff0c\u56e0\u4e3a\u76f4\u5230\u4eca\u5929\u6211\u624d\u9047\u89c1\u4e00\u4e2a\u8be5\u6740\u7684\u4eba\uff0c\u503c\u5f97\u6211\u6740\u7684 \u4eba\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u7b2c\u4e00\u5929\u6211\u5230\u4f60\u5bb6\uff0c\u8fc7\u4e24\u5929\u4f60\u7684\u5251\u5c31\u88ab\u4eba\u5077\u4e86\uff0c\u8fd9\u4e5f\u96be\u602a\u4f60\u8981\u7591 \u5fc3\u662f\u6211\u5077\u7684\uff0c\u53ef\u662f\u4f60\u82e5\u6740\u4e86\u6211\uff0c\u5c31\u6c38\u8fdc\u4e0d\u4f1a\u77e5\u9053\u8c01\u662f\u90a3\u771f\u6b63\u5077\u5251\u7684\u8d31\u4eba\u4e86\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u4e0d\u662f\u4f60\u662f\u8c01\uff1f\u96be\u9053\u6211\u8fd8\u4f1a\u6545\u610f\u9677\u5bb3\u4f60\uff1f\u6211\u82e5\u8981\u6740\u4f60\uff0c\u6839\u672c\u5c31\u7528\u4e0d\u7740\u7f16\u9020\u4efb \u4f55\u7406\u7531\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u81ea\u7136\u4e0d\u5fc5\u9677\u5bb3\u6211\uff0c\u4f46\u5374\u6709\u4eba\u60f3\u9677\u5bb3\u6211\uff0c\u4ed6\u5077\u4e86\u4f60\u7684\u5251\uff0c\u5c31\u4e3a\u4e86\u8981\u4f60\u6740 \u6211\uff0c\u4f60\u96be\u9053\u8fd8\u4ece\u672a\u542c\u8bf4\u8fc7\u2018\u501f\u5200\u6740\u4eba\u2019\u4e4b\u8ba1\uff1f\u201d \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u8c01\u4f1a\u4ee5\u6b64\u6765\u9677\u5bb3\u4f60\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u82e6\u7b11\u9053\uff1a\u201c\u8001\u5b9e\u8bf4\uff0c\u60f3\u9677\u5bb3\u6211\u7684\u4eba\u53ef\u771f\u4e0d\u5c11\uff0c\u6211\u6628\u5929\u8fd8\u6328\u4e86\u522b\u4eba\u51b7\u5251\u2026\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u76b1\u7709\u9053\uff1a\u201c\u4f60\u53d7\u4e86\u4f24\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff0c\u201c\u53d7\u4f24\u5e76\u4e0d\u662f\u4ec0\u9ebd\u5149\u5f69\u7684\u4e8b\u6211\u4e3a\u4f55\u8981\u8bf4\u8c0e\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u662f\u8c01\u4f24\u4e86\u4f60\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5c31\u662f\u6211\u8981\u627e\u7684\u523a\u5ba2\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u9510\u5229\u7684\u76ee\u5149\u5728\u4ed6\u8eab\u4e0a\u4e00\u626b\uff0c\u9053\uff1a\u201c\u4f24\u5728\u4f55\u5904\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u80cc\u5f8c\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u6709\u4eba\u5728\u4f60\u80cc\u5f8c\u51fa\u624b\uff0c\u5802\u5802\u7684\u695a\u9999\u5e05\u7adf\u4f1a\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u6478\u4e86\u6478\u9f3b\u5b50\u9053\uff1a\u201c\u5f53\u6211\u53d1\u89c9\u65f6\uff0c\u5df2\u8eb2\u4e0d\u5f00\u4e86\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u9601\u4e0b\u82e5\u662f\u65f6\u5e38\u88ab\u4eba\u6697\u7b97\uff0c\u80fd\u6d3b\u5230\u73b0\u5728\u5012\u771f\u4e0d\u5bb9\u6613\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u88ab\u4eba\u6697\u7b97\u7684\u6b21\u6570\u867d\u4e0d\u5c11\uff0c\u4f46\u8d1f\u4f24\u5012\u662f\u751f\u5e73\u7b2c\u4e00\u906d\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u4ed6\u7684\u5251\u5f88\u5feb\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\uff1a\u201c\u5feb\u6781\u4e86\uff0c\u5728\u4e0b\u751f\u5e73\u8fd8\u672a\u9047\u5230\u8fc7\u8fd9\u9ebd\u5feb\u7684\u7684\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u6c89\u541f\u4e86\u534a\u664c\uff0c\u9053\uff1a\u201c\u542c\u8bf4\u4f60\u548c\u77f3\u89c2\u97f3\u3001\u2018\u6c34\u6bcd\u2019\u9634\u59ec\u3001\u5e05\u4e00\u5e06\u8fd9\u4e9b\u4eba\u90fd\u4ea4\u8fc7 \u624b\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u77f3\u89c2\u97f3\u51fa\u624b\u8be1\u79d8\uff0c\u5e05\u4e00\u5e06\u5251\u6c14\u5df2\u5165\u95e8\uff0c\u2018\u6c34\u6bcd\u2019\u9634\u59ec\u5185\u529b\u4e4b\u6df1 \u539a\uff0c\u66f4\u662f\u9a87\u4eba\u542c\u95fb\uff0c\u4f46\u8bba\u51fa\u624b\u4e4b\u6289\uff0c\u5374\u8fd8\u662f\u90fd\u6bd4\u4e0d\u4e0a\u6b64\u4eba\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u8138\u4e0a\u4f3c\u5df2\u5347\u8d77\u4e86\u79cd\u5174\u594b\u7684\u7ea2\u5149\uff0c\u9053\uff1a\u201c\u8fd9\u4eba\u7adf\u6709\u5982\u6b64\u5feb\u7684\u5251\uff0c\u6211\u4e5f\u60f3\u4f1a\u4f1a\u4ed6\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53c8\u7b11\u4e86\u7b11\u7b11\u5bb9\u6709\u4e9b\u795e\u79d8\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4ed6\u65e2\u5df2\u5230\u4e86\u8fd9\u91cc\uff0c\u5e84\u4e3b\u8fdf\u65e9\u603b\u4f1a\u89c1\u7740\u4ed6 \u7684\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u4f60\u96be\u9053\u60f3\u8bf4\u76d7\u5251\u7684\u4eba\u5c31\u662f\u4ed6\uff1f\u662f\u4ed6\u60f3\u501f\u6211\u7684\u624b\u6740\u4f60\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u8fd9\u4e5f\u662f\u6211\u60f3\u4e0d\u901a\u7684\u5730\u65b9\uff0c\u4f46\u8981\u7ed9\u6211\u51e0\u5929\u65f6\u95f4\uff0c\u6211\u53ef\u4ee5\u4fdd\u8bc1\u4e00\u5b9a\u80fd\u5c06\u771f\u76f8\u63a2 \u67e5\u51fa\u6765\u201d \u3000\u3000\u859b\u8863\u4eba\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u53d7\u4e86\u4f24\uff0c\u5b9e\u5728\u662f\u4f60\u7684\u8fd0\u6c14\u2026\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u63a0\u4e0a\u9a6c\u80cc\uff0c\u6025\u9a70\u800c\u53bb\u3002 \u3000\u3000\u695a\u7559\u9999\u9ed8\u7136\u534a\u664c\uff0c\u9053\uff1a\u201c\u674e\u660e\u751f\u5f53\u771f\u7684\u5c31\u662f\u53f6\u76db\u5170\uff0c\u90a3\u624d\u771f\u662f\u6211\u7684\u8fd0\u6c14\u3002\u201d \u3000\u3000\u57c3\u76db\u8001\u5e97\u662f\u4e2a\u5f88\u65e7\u5f0f\u7684\u5ba2\u6808\uff0c\u5c4b\u5b50\u5df1\u5f88\u9648\u65e7\uff0c\u674e\u660e\u751f\u201c\u592b\u5987\u201d\u5c31\u4f4f\u5728\u6700\u5f8c\u9762\u7684\u4e00\u4e2a\u5c0f\u8de8 \u9662\u91cc\u3002 \u3000\u3000\u695a\u7559\u9999\u53d1\u73b0\u4ed6\u4eec\u4f4f\u7684\u5c4b\u5b50\u4e0d\u4f46\u95e8\u5173\u7740\uff0c\u8fde\u7a97\u5b50\u4e5f\u662f\u7d27\u7d27\u5173\u7740\u7684\uff0c\u867d\u7136\u662f\u767d\u5929\uff0c\u4ed6\u4eec\u5374\u8fd8 \u662f\u8eb2\u5728\u623f\u91cc\u7761\u5927\u89c9\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u7a76\u7adf\u6709\u4ec0\u9ebd\u89c1\u4e0d\u5f97\u4eba\u7684\u5730\u65b9\uff1f \u3000\u3000\u695a\u7559\u9999\u95ee\u9053\uff1a\u201c\u4ed6\u4eec\u6ca1\u6709\u51fa\u53bb\uff1f\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u9053\uff1a\u201c\u6ca1\u6709\u51fa\u53bb\uff0c\u4ece\u6628\u5929\u665a\u4e0a\u8d77\uff0c\u8fd9\u91cc\u4e00\u76f4\u90fd\u6709\u4eba\u5b88\u7740\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u76ee\u5149\u4e00\u8f6c\uff0c\u5ffd\u7136\u5927\u58f0\u9053\uff1a\u201c\u674e\u5144\u600e\u4f1a\u5230\u8fd9\u91cc\u6765\u4e86\uff0c\u5c31\u4f4f\u5728\u8fd9\u91cc\u9ebd\uff1f\u201d \u3000\u3000\u4ed6\u4e00\u9762\u8bf4\u7740\u8bdd\uff0c\u4e00\u9762\u5df2\u8d70\u8fc7\u53bb\uff0c\u7528\u529b\u62cd\u7740\u95e8\uff0c\u5524\u9053\uff1a\u201c\u5f00\u95e8\u3002\u201d \u3000\u3000\u623f\u5b50\u91cc\u7acb\u523b\u201c\u6089\u6089\u7d22\u7d22\u201d\u54cd\u8d77\u9635\u7a7f\u8863\u670d\u7684\u58f0\u97f3\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u542c\u5230\u4e00\u4e2a\u4eba\u61d2\u6d0b\u6d0b\u7684\u9053\uff1a \u201c\u662f\u8c01\uff1f\u4f60\u627e\u9519\u95e8\u4e86\u5427\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u662f\u6211\uff0c\u5f20\u8001\u53c1\uff0c\u674e\u5144\u96be\u9053\u8fde\u8001\u670b\u53cb\u7684\u58f0\u97f3\u90fd\u542c\u4e0d\u51fa\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u53c8\u8fc7\u4e86\u534a\u664c\uff0c\u90a3\u623f\u95e8\u624d\u201c\u5440\u201d\u7684\u5f00\u4e86\u4e00\u7ebf\uff0c\u4e00\u4e2a\u9762\u8272\u82cd\u767d\u3002\u5934\u53d1\u51cc\u4e71\u7684\u5c11\u5e74\u4eba\u63a2\u51fa\u534a\u4e2a \u8eab\u5b50\u6765\uff0c\u4e0a\u4e0a\u4e0b\u4e0b\u77a7\u4e86\u695a\u7559\u9999\u4e00\u773c\uff0c\u76b1\u7709\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u6211\u4e0d\u8ba4\u5f97\u4f60\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u8ba4\u5f97\u6211\u6211\u5374\u8ba4\u5f97\u4f60\u201d \u3000\u3000\u90a3\u5c11\u5e74\u9762\u8272\u53d8\u4e86\u53d8\uff0c\u8eab\u5b50\u7acb\u523b\u7f29\u4e86\u56de\u53bb\uff0c\u4f46\u4ed6\u8fd8\u6ca1\u6709\u5c06\u95e8\u5173\u4e0a\uff0c\u695a\u7559\u9999\u7684\u817f\u5df2\u63d2\u5165\u8fdb \u53bb\uff0c\u8f7b\u8f7b\u63a8\uff0c\u95e8\u5c31\u88ab\u63a8\u5f00\u4e86\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u88ab\u63a8\u5f97\u5f8c\u9000\u4e86\u597d\u51e0\u6b65\uff0c\u6012\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u6709\u6bdb\u75c5\u9ebd\uff0c\u60f3\u5e72\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u5fae\u7b11\u9053\uff1a\u201c\u6211\u60f3\u5e72\u4ec0\u9ebd\uff0c\u4f60\u96be\u9053\u8fd8\u4e0d\u660e\u767d\uff1f\u201d \u3000\u3000\u5c4b\u91cc\u8fd8\u6709\u4e2a\u5957\u95f4\uff0c\u95e8\u6ca1\u6709\u5173\u597d\uff0c\u695a\u7559\u9999\u4e00\u773c\u626b\u8fc7\uff0c\u5df2\u53d1\u73b0\u5e8a\u4e0a\u8eba\u7740\u4e2a\u4eba\uff0c\u7528\u7ef5\u88ab\u8499\u7740 \u5934\uff0c\u5374\u9732\u51fa\u4e00\u53ea\u773c\u775b\u6765\u5077\u5077\u7684\u77aa\uff0c\u5e8a\u4e0b\u5f3a\u7740\u53cc\u7ea2\u7ee3\u978b\uff0c\u65c1\u8fb9\u7684\u6905\u5b50\u4e0a\u8fd8\u6362\u7740\u51e0\u4ef6\u7c89\u7ea2\u7f0e\u5b50\u7684 \u8863\u88d9\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u9762\u4e0a\u66f4\u8fde\u4e00\u70b9\u8840\u8272\u90fd\u6ca1\u6709\u4e86\uff0c\u62a2\u7740\u60f3\u53bb\u628a\u8fd9\u6247\u95e8\u5173\u4e0a\uff0c\u4f46\u662f\u695a\u7559\u9999\u8eab\u4e00\u95ea\uff0c\u5df2\u6321 \u4f4f\u4e86\u4ed6\u7684\u53bb\u8def\uff0c\u7b11\u9053\uff1a\u201c\u65e2\u5df2\u627e\u7740\u4e86\u4f60\u4eec\uff0c\u518d\u8eb2\u53c8\u6709\u4f55\u7528\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5e74\u98a4\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u53ef\u662f\u66f9\u5bb6\u6d3e\u6765\u7684\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u76b1\u4e86\u76b1\u7709\uff0c\u9053\uff1a\u201c\u66f9\u5bb6\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5e74\u7a81\u7136\u201c\u5509\u201d\u5730\u8dea\u4e86\u4e0b\u53bb\u3002\u54ed\u4e27\u7740\u8138\u9053\uff1a\u201c\u5c0f\u4eba\u8be5\u6b7b\uff0c\u53ea\u6c42\u5927\u7237\u4f60\u653e\u6211\u4eec\u4e00\u6761\u751f \u8def\u2026\u3002\u201d \u3000\u3000\u5e8a\u4e0a\u90a3\u5973\u5b50\u5ffd\u7136\u8df3\u4e86\u8d77\u6765\uff0c\u957f\u5f97\u679c\u7136\u72e0\u5e74\u8f7b\uff0c\u5f88\u5996\u5a06\uff0c\u5374\u5f88\u6cfc\u8fa3\u3002 \u3000\u3000\u8eab\u4e0a\u53ea\u7a7f\u7740\u4ef6\u5f88\u8584\u7684\u4eb5\u8863\uff0c\u51e0\u4e4e\u5b8c\u5168\u662f\u900f\u660e\u7684\uff0c\u8fde\u5927\u817f\u90fd\u9732\u4e86\u51fa\u6765\uff0c\u4f46\u5979\u5374\u5b8c\u5168\u4e0d\u77a5\uff0c \u51b2\u5230\u695a\u7559\u9999\u9762\u524d\uff0c\u4e24\u624b\u53c9\u7740\u8170\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4f60\u7684\u7136\u662f\u66f9\u5bb6\u6d3e\u6765\u7684\uff0c\u90a3\u5c31\u66f4\u597d\u4e86\uff0c\u4f60\u4e0d\u59a8\u56de\u53bb \u544a\u8bc9\u66f9\u8001\u5934\uff0c\u5c31\u8bf4\u6211\u5df2\u8ddf\u5b9a\u4e86\u5c0f\u8c22\uff0c\u518d\u4e5f\u4e0d\u4f1a\u56de\u53bb\u53d7\u4ed6\u90a3\u79cd\u6d3b\u7f6a\uff0c\u6211\u867d\u7136\u5e26\u4e86\u4ed6\u4e00\u5323\u9996\u9970\u51fa \u6765\uff0c\u4f46\u90a3\u4e5f\u662f\u4ed6\u7ed9\u6211\u7684\uff0c\u518d\u8bf4\u6211\u4e00\u4e2a\u9ec4\u82b1\u95fa\u5973\u8ddf\u4e86\u4ed6\u597d\u51e0\u5e74\uff0c\u62ff\u4ed6\u51e0\u6587\u81ed\u94b1\u53c8\u6709\u4ec0\u9ebd\u4e0d\u5e94 \u8be5\uff0c\u4f60\u8bf4\u2026\u4f60\u8bf4\u3002\u6709\u4ec0\u9ebd\u4e0d\u5e94\u8be5\uff1f\u201d \u3000\u3000\u5979\u8bf4\u8bdd\u5c31\u50cf\u7206\u8695\u8c46\u4f3c\u7684\uff0c\u522b\u4eba\u7b80\u76f4\u63d2\u4e0d\u4e0a\u5634\u3002 \u3000\u3000\u695a\u7559\u9999\u6014\u5f80\u4e86\uff0c\u5b9e\u5728\u6709\u4e9b\u54ed\u7b11\u4e0d\u5f97\u3002 \u3000\u3000\u4ed6\u73b0\u5728\u5df2\u77e5\u9053\u81ea\u5df1\u627e\u9519\u4e86\u4eba\uff0c\u8fd9\u5c11\u5e74\u5e76\u4e0d\u662f\u53f6\u76db\u5170\uff0c\u800c\u662f\u201c\u5c0f\u8c22\u201d\uff0c\u8fd9\u5c11\u5973\u66f4\u4e0d\u662f\u4ed6\u60f3 \u50cf\u4e2d\u7684\u90a3\u4eba\u3002 \u3000\u3000\u770b\u6765\u5979\u53ea\u4e0d\u8fc7\u662f\u201c\u66f9\u5bb6\u201d\u7684\u9003\u59bb\uff0c\u770b\u4e0a\u4e86\u201c\u5c0f\u8c22\u201d\uff0c\u5c31\u5377\u4e86\u7ec6\u8f6f\uff0c\u548c\u5c0f\u8c22\u53cc\u53cc\u79c1\u5954\u5230\u8fd9 \u91cc\u6765\u3002 \u3000\u3000\u4ed6\u4eec\u77e5\u9053\u66f9\u8001\u5934\u4e0d\u80af\u5c31\u6b64\u7f62\u4f11\uff0c\u81ea\u7136\u8eb2\u7740\u4e0d\u6562\u89c1\u4eba\u3002 \u3000\u3000\u695a\u7559\u9999\u6478\u4e86\u6478\u9f3b\u5b50\uff0c\u968f\u7740\u9053\uff1a\u201c\u6e05\u5b98\u96be\u65ad\u5bb6\u52a1\u4e8b\uff0c\u4f46\u4f60\u4eec\u82e5\u771f\u7684\u60f3\u597d\u597d\u8fc7\u65e5\u5b50\uff0c\u5c31\u8be5\u60f3 \u6cd5\u62e2\u4e9b\u6b63\u5f53\u4e8b\u505a\uff0c\u600e\u9ebd\u80fd\u6574\u5929\u5173\u8d77\u95e8\u6765\u7761\u89c9\u3002\u201d \u3000\u3000\u5c0f\u8c22\u7684\u8138\u7ea2\u4e86\uff0c\u987f\u9996\u9053\uff0c\u201c\u662f\uff0c\u662f\uff0c\u662f\uff0c\u5c0f\u4eba\u4e00\u5b9a\u542c\u5927\u7237\u7684\u76fc\u5490\uff0c\u4ece\u6b64\u597d\u597d\u505a\u4eba\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5df2\u8d70\u51fa\u4e86\u95e8\uff0c\u5374\u8fd8\u4e0d\u80af\u653e\u5fc3\uff0c\u5ffd\u53c8\u56de\u5934\u6765\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u65e2\u662f\u4eac\u57ce\u6765\u7684\uff0c\u53ef\u77e5\u9053\u4e00 \u4e2a\u53eb\u53f6\u76db\u5170\u7684\u9ebd\uff1f\u201d \u3000\u3000\u5c0f\u8c22\u9053\uff1a\u201c\u53f6\u76db\u5170\uff1f\u5927\u7237\u8bf4\u7684\u53ef\u662f\u5927\u6805\u680f\uff0c\u2018\u5bcc\u5458\u9898\u2019\u91cc\u90a3\u5531\u82b1\u65e6\u7684\u5c0f\u53f6\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7684\u5fc3\u5df2\u8df3\u4e86\u8d77\u6765\uff0c\u5374\u8fd8\u662f\u4e0d\u52a8\u58f0\u8272\uff0c\u9053\uff1a\u201c\u6211\u8bf4\u7684\u5c31\u662f\u4ed6\u3002\u201d \u3000\u3000\u5c0f\u8c22\u9053\uff1a\u201c\u6211\u524d\u51e0\u5929\u8fd8\u770b\u5230\u8fc7\u4ed6\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6562\u7d27\u95ee\u9053\uff1a\u201d\u5728\u90a3\u91cc\uff1f\u201d \u3000\u3000\u5c0f\u8c22\u9053\uff1a\u201c\u4ed6\u597d\u50cf\u5c31\u4f4f\u5728\u524d\u9762\u90a3\u6761\u2018\u9752\u8863\u5df7\u2019\uff0c\u662f\u7b2c\u51e0\u5bb6\u95e8\u5c0f\u4eba\u5374\u6ca1\u6ce8\u610f\uff0c\u56e0\u4e3a\u4ed6\u597d\u50cf \u6709\u70b9\u9b3c\u9b3c\u795f\u795f\u7684\uff0c\u8fde\u4eba\u90fd\u4e0d\u6562\u89c1\u3002\u201d \u3000\u3000\u4ed6\u53ea\u987e\u8bf4\u522b\u4eba\uff0c\u5374\u5fd8\u4e86\u81ea\u5df2\uff0c\u7b49\u4ed6\u8bf4\u5b8c\u4e86\u8bdd\uff0c\u518d\u62ac\u8d77\u5934\u6765\uff0c\u9762\u524d\u7684\u4eba\u5ffd\u7136\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\u53c8\u662f\u5174\u594b\uff0c\u53c8\u662f\u597d\u7b11\u3002 \u3000\u3000\u4ed6\u731c\u7684\u679c\u7136\u4e0d\u9519\uff0c\u53f6\u76db\u5170\u679c\u7136\u5c31\u8eb2\u5728\u8fd9\u677e\u6c5f\u57ce\uff0c\u5374\u672a\u60f3\u5230\u53f6\u76db\u5170\u662f\u4e2a\u5531\u620f\u66f2\u3002 \u3000\u3000\u9752\u8863\u5df7\u662f\u6761\u5f88\u957f\u7684\u5df7\u5b50\uff0c\u6700\u5c11\u6709\u4e00\u767e\u591a\u6237\u4eba\u5bb6\uff0c\u53f6\u76db\u5170\u7a76\u7adf\u4f4f\u5728\u8c01\u5bb6\u91cc\uff1f \u3000\u3000\u5c0f\u79c3\u5b50\u62cd\u7740\u80f8\u819b\uff0c\u8bf4\u662f\u7528\u4e0d\u7740\u4e24\u4e2a\u65f6\u8fb0\uff0c\u4ed6\u5c31\u80fd\u4ed6\u627e\u51fa\u6765\u3002 \u3000\u3000\u8fd9\u65f6\u5929\u5df2\u5feb\u9ed1\u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\u627e\u4e86\u5bb6\u9986\u5b50\uff0c\u7ed3\u7ed3\u5b9e\u5b9e\u7684\u5927\u5403\u4e86\u4e00\u987f\uff0c\u5c31\u53bb\u627e\u77f3\u7ee3\u4e91\uff0c\u544a\u8bc9\u81ea\u5df1\u8fd9\u662f\u4e3a\u4e86\u6b63\u4e8b\uff0c \u800c\u975e\u4e3a\u4e86\u79c1\u60c5\u3002 \u3000\u3000\u4ed6\u81ea\u5df1\u662f\u5426\u771f\u5fc3\u8bf4\u7684\u8fd9\u53e5\u8bdd\u5462\uff1f\u90a3\u5c31\u53ea\u6709\u5929\u77e5\u9053\u4e86\u3002 \u3000\u3000\u77f3\u7ee3\u4e91\u7684\u5bb6\uff0c\u662f\u4e00\u680b\u5f88\u5c0f\u7684\u5c4b\u5b50\uff0c\u663e\u7136\u6700\u8fd1\u624d\u7c89\u5237\u4e00\u65b0\uff0c\u8fde\u90a3\u4e24\u6247\u6728\u677f\u95e8\u4e5f\u662f\u65b0\u6cb9\u6f06 \u7684\u3002 \u3000\u3000\u77f3\u7ee3\u4e91\u6b63\u5728\u9662\u5b50\u91cc\u8d76\u9e21\u56de\u7b3c\u3002 \u3000\u3000\u5979\u7a7f\u4ef6\u7c97\u5e03\u8863\u670d\uff0c\u5934\u53d1\u4e5f\u6ca1\u6709\u62e2\u597d\uff0c\u8d64\u7740\u8db3\u7a7f\u7740\u53cc\u6728\u5c55\uff0c\u6b63\u662f\u201c\u5708\u4e0a\u8db3\u5982\u971c\uff0c\u4e0d\u7740\u9e26\u5934 \u889c\u201d\uff0c\u867d\u7136\u84ec\u5934\u7c97\u670d\uff0c\u770b\u6765\u5374\u522b\u6709\u4e00\u79cd\u98ce\u60c5\u3002 \u3000\u3000\u695a\u7559\u9999\u5728\u7af9\u6e7e\u5916\u6084\u6084\u7684\u6b23\u8d4f\u4e86\u534a\u5929\uff0c\u624d\u8f7b\u8f7b\u9053\uff1a\u201c\u77f3\u59d1\u5a18\uff0c\u77f3\u7ee3\u4e91\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u4e00\u60ca\uff0c\u62a2\u7740\uff0c\u77a7\u89c1\u4e86\u4ed6\uff0c\u8138\u5ffd\u7136\u98de\u7ea2\u4e86\u8d77\u6765\uff0c\u8bdd\u4e5f\u4e0d\u8bf4\uff0c\u626d\u5934\u5c31\u8d70\uff0c\u98de\u4e5f\u4f3c\u7684\u8d76 \u4e86\u56de\u53bb\u3002 \u3000\u3000\u8dd1\u56de\u95e8\u53e3\uff0c\u624d\u6446\u4e86\u6446\u624b\uff0c\u597d\u50cf\u662f\u53eb\u695a\u7559\u9999\u5728\u5916\u9762\u7b49\u3002 \u3000\u3000\u695a\u7559\u9999\u53ea\u6709\u7b49\u3002 \u3000\u3000\u7b49\u4e86\u534a\u5929\uff0c\u77f3\u7ee3\u4e91\u624d\u51fa\u6765\uff0c\u5934\u5df2\u68b3\u597d\u4e86\uff0c\u8863\u670d\u4e5f\u6362\u8fc7\u4e86\u3002\u53c8\u7a7f\u8d77\u4e86\u90a3\u53cc\u6c34\u7ea2\u8272\u7684\u7ee3\u978b\u3002 \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\uff0c\u8f7b\u58f0\u9053\uff1a\u201c\u4f60\u8fd9\u53cc\u978b\u5b50\u597d\u7cbe\u81f4\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u8138\u7a81\u7136\u53c8\u98de\u7ea2\u4e86\u8d77\u6765\u3002\u54ac\u7740\u5634\u5507\uff0c\u8dfa\u7740\u811a\u9053\uff1a\u201c\u4f60\u8981\u6765\uff0c\u4e3a\u4ec0\u9ebd\u4e5f\u4e0d\u5148\u8bf4\u2014 \u58f0\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u672c\u6765\u60f3\u660e\u5929\u6765\u7684\uff0c\u53ef\u662f\u4eca\u5929\u665a\u4e0a\u6211\u53c8\u975e\u6765\u4e0d\u53ef\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u5782\u7740\u5934\uff0c\u5f04\u7740\u8863\u89d2\uff0c\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u4e8c\u5a76\u5462\uff1f\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u5077\u5077\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5979\u8d77\u5f97\u65e9\uff0c\u73b0\u5728\u5df2\u7761\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u80fd\u51fa\u6765\u5417\uff1f\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u9053\uff1a\u201c\u8fd9\u9ebd\u665a\u4e86\uff0c\u53eb\u6211\u51fa\u53bb\u5e72\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u5979\u547c\u5438\u4f3c\u4e4e\u5df2\u6709\u4e9b\u6025\u6349\uff0c\u4f46\u58f0\u97f3\u5df2\u6709\u4e9b\u53d1\u98a4\uff0c\u695a\u7559\u9999\u53ea\u89c9\u5fc3\u91cc\u4e00\u9635\u8361\u6f3e\uff0c\u5fcd\u4e0d\u4f4f\u81ea\u7af9\u7bf1 \u95f4\u6444\u4f4f\u4e86\u5979\u7684\u624b\u3002 \u3000\u3000\u5979\u7684\u624b\u597d\u70eb\u3002 \u3000\u3000\u77f3\u7ee3\u4e91\u6025\u9053\uff1a\u201c\u5feb\u653e\u624b\u3002\u88ab\u6211\u4e8c\u5a76\u770b\u5230\uff0c\u5c0f\u5fc3\u5979\u6253\u65ad\u4f60\u7684\u817f\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u563b\u563b\u9053\uff1a\u201c\u6211\u4e0d\u6015\uff0c\u53cd\u6b63\u5df2\u7ecf\u6765\u4e86\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u9053\uff1a\u201c\u4f60\u2026\uff01\u4f60\u2026\uff01\u4f60\uff01\u4f60\u4e0d\u662f\u597d\u4eba\uff0c\u6211\u504f\u4e0d\u51fa\u53bb\uff0c\u770b\u4f60\u600e\u9ebd\u6837\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u4e0d\u51fa\u6765\uff0c\u6211\u5c31\u4e0d\u8d70\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u773c\u775b\u77a7\u7740\u4ed6\uff0c\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u771f\u662f\u6211\u547d\u91cc\u7684\u9b54\u661f\u2026\u2026\u3002\u201d \u3000\u3000\u7a81\u542c\u5c4b\u5b50\u91cc\u6709\u4eba\u5524\u9053\uff1a\u201c\u7ee3\u4e91\uff0c\u6709\u4eba\u6765\u5417\uff0c\u4f60\u5728\u8ddf\u8c01\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u7d27\u5f20\u9053\uff1a\u201c\u6ca1\u6709\u4eba\uff0c\u53ea\u4e0d\u8fc7\u662f\u6761\u91ce\u72d7\u3002\u201d \u3000\u3000\u5979\u53c8\u77aa\u4e86\u695a\u7559\u9999\u4e00\u773c\uff0c\u81ea\u5df1\u4e5f\u5fcd\u4e0d\u4f4f\u5657\u55e4\u4e00\u7b11\uff0c\u5728\u4ed6\u624b\u4e0a\u91cd\u91cd\u62e7\u4e86\u4e00\u628a\uff0c\u6068\u6068\u9053\uff1a\u201c\u6211 \u4e00\u770b\u5230\u4f60\uff0c\u5c31\u77e5\u9053\u8981\u5012\u9709\u4e86\u3002\u201d \u3000\u3000\u5979\u4e00\u626d\u8170\u8dd1\u4e86\u51fa\u6765\uff0c\u695a\u7559\u9999\u671b\u7740\u5979\u98de\u626c\u7684\u53d1\u4e1d\uff0c\u5fc3\u91cc\u53ea\u89c9\u751c\u4e1d\u4e1d\u7684\u5c31\u4eff\u4f5b\u53c8\u56de\u5230\u9065\u8fdc\u7684 \u5c11\u5e74\u65f6\uff0c\u4ed6\u548c\u90bb\u5bb6\u7684\u5c0f\u5973\u5b69\u5b50\u5077\u5077\u7ea6\u4f1a\u665a\u4e0a\u53bb\u6e56\u7554\u6349\u9c7c\uff0c\u9c7c\u513f\u867d\u59cb\u7ec8\u6ca1\u6709\u6349\u5230\u5374\u6349\u56de\u4e86\u65e0\u9650 \u751c\u7b11\u3002 \u3000\u3000\u77f3\u7ee3\u4e91\u5df2\u8d70\u51fa\u4e86\u95e8\u3002\u4e0d\u80af\u8fc7\u6765\u3002 \u3000\u3000\u695a\u7559\u9999\u5fcd\u4e0d\u4f4f\u997c\u53bb\u62b1\u4f4f\u4e86\u5979\uff0c\u8f7b\u8f7b\u54ac\u4e86\u5979\u4e00\u53e3\u3002 \u3000\u3000\u77f3\u7ee3\u4e91\u5a07\u9053\uff1a\u201c\u4f60\u2014\u3002\u2014\u4f60\u5e72\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u4f60\u521a\u5219\u4e0d\u662f\u8bf4\u6211\u662f\u6761\u91ce\u72d7\u9ebd\uff1f\u91ce\u72d7\u672c\u6765\u5c31\u4f1a\u54ac\u4eba\u7684\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u5506\u7740\u5634\u5507\u9053\uff1a\u201c\u4f60\u4e0d\u4f46\u662f\u6761\u91ce\u72d7\uff0c\u7b80\u76f4\u662f\u6761\u5c0f\u75af\u72d7\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5ffd\u7136\u201c\u6c6a\u201d\u7684\u4e00\u58f0\uff0c\u5f20\u5f00\u4e86\u5927\u5634\u3002 \u3000\u3000\u77f3\u7ee3\u4e91\u5a07\u7b11\u7740\u8f6c\u8eab\u9003\u4e86\u51fa\u53bb\uff0c\u695a\u7559\u9999\u5c31\u5728\u5f8c\u9762\u8ffd\u3002 \u3000\u3000\u5929\u4e0a\u661f\u5149\u95ea\u70c1\uff0c\u5929\u5730\u95ee\u5145\u6ee1\u4e86\u6e29\u67d4\u4e4b\u610f\uff0c\u7530\u91cc\u7684\u7a3b\u5b50\u5df2\u719f\u4e86\uff0c\u5728\u665a\u98ce\u4e2d\u8d77\u4f0f\u7740\uff0c\u50cf\u6d77 \u6d6a\u3002\u8c01\u8bf4\u751f\u547d\u662f\u676f\u82e6\u9152\uff1f \u3000\u3000\u77f3\u7ee3\u4e91\u5df2\u7b11\u7684\u6ca1\u6709\u529b\u6c14\u4e86\uff0c\u8dd1\u7740\u8dd1\u7740\uff0c\u5ffd\u7136\u5012\u5728\u8c37\u4ed3\u7684\u8349\u5806\u4e0a\uff0c\u4e0d\u505c\u7684\u5598\u606f\u7740\uff0c\u8f7b\u8f7b\u5524 \u9053\uff1a\u201c\u6551\u547d\u5440\u6709\u75af\u72d7\u8981\u54ac\u4eba\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u201c\u6c6a\u201d\u7684\u4e00\u58f0\uff0c\u6251\u4e86\u8fc7\u53bb\uff0c\u62b1\u8d77\u4e86\u5979\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u53eb\u5427\uff01\u6ca1\u6709\u4eba\u4f1a\u6765\u6551\u4f60\u7684\uff0c\u6211 \u8981\u5148\u54ac\u6389\u4f60\u7684\u9f3b\u5b50\uff0c\u518d\u54ac\u6389\u4f60\u7684\u8033\u6735\uff0c\u518d\u54ac\u7834\u4f60\u7684\u5634\u2026\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u54fc\u4e00\u58f0\uff0c\u60f3\u53bb\u63a8\u4ed6\uff0c\u600e\u5948\u5168\u8eab\u90fd\u5df2\u53d1\u8f6f\uff0c\u54ea\u6709\u534a\u5206\u529b\u6c14\uff0c\u53ea\u6709\u5c06\u5934\u57cb\u4eba\u4ed6\u6000\u91cc\uff0c \u6c42\u9976\u9053\uff0c\u201c\u9976\u4e86\u6211\u5427\uff1f\u4e0b\u6b21\u6211\u518d\u4e5f\u4e0d\u6562\u2026\u3002\u201d \u3000\u3000\u5979\u8fd9\u53e5\u8bdd\u6ca1\u6709\u8bf4\u5b8c\uff0c\u56e0\u4e3a\u5979\u7684\u5634\u5507\u5df2\u88ab\u652f\u4f4f\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\u95f4\uff0c\u5979\u5168\u8eab\u90fd\u5d29\u6e83\u4e86\uff0c\u53ea\u89c9\u4e00\u4e2a\u4eba\u5df2\u5728\u5f80\u4e0b\u6c89\u843d\uff0c\u575a\u5b9e\u7684\u5927\u5730\u4f3c\u5df1\u53d8\u6210\u6e29\u67d4\u7684 \u6e56\u6c34\u3002 \u3000\u3000\u5979\u7684\u4eba\u6b63\u5728\u5f80\u6e56\u5fc3\u6c89\u843d\u2026\u2026 \u3000\u3000\u661f\u5149\u4eff\u4f5b\u6b63\u5728\u5411\u4ed6\u4eec\u7728\u7740\u773c\uff0c\u665a\u98ce\u5374\u4f3c\u5728\u8f7b\u8f7b\u7684\u7b11\uff0c\u8fde\u7530\u91cc\u7684\u7a3b\u5b50\u90fd\u4f4e\u4e0b\u4e86\u5934\uff0c\u4e0d\u597d\u610f \u601d\u518d\u770b\u4e86\u3002 \u3000\u3000\u751f\u547d\u539f\u6765\u662f\u5982\u6b64\u7f8e\u597d\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u695a\u7559\u9999\u5ffd\u7136\u7ad9\u4e86\u8d77\u6765\uff0c\u67d4\u58f0\u901a\u201c\u65f6\u5019\u5df2\u4e0d\u65e9\u4e86\uff0c\u6211\u4eec\u8d70\u5427\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u8f6f\u8f6f\u7684\u7f29\u5728\u8349\u5806\u4e0a\uff0c\u5410\u6c14\u5982\u4e1d\uff0c\u9053\uff1a\u201d\u8fd8\u8981\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u8981\u5e26\u4f60\u53bb\u770b\u6837\u4e1c\u897f\uff0c\u4f60\u770b\u5230\u4e4b\u5f8c\uff0c\u4e00\u5b9a\u4f1a\u5f88\u60ca\u5947\u7684\u3002\u201d \u7b2c\u516b\u7ae0\u3000\u6210\u4eba\u4e4b\u7f8e>> \u53e4\u9f99\u300a\u695a\u7559\u9999\u7cfb\u5217\xb7\u9b3c\u604b\u4f20\u5947\u300b \u7b2c\u516b\u7ae0\u3000\u6210\u4eba\u4e4b\u7f8e \u3000\u3000\u77f3\u7ee3\u4e91\u4f0f\u5728\u695a\u7559\u9999\u80cc\u4e0a\uff0c\u5c31\u597d\u50cf\u5728\u817e\u4e91\u9a7e\u96fe\u4e00\u6837\u3002\u4e00\u91cd\u91cd\u5c4b\u810a\uff0c\u4e00\u68f5\u68f5\u6811\u6728\uff0c\u8fce\u9762\u5411\u7ea6 \u98de\u6765\uff0c\u53c8\u81ea\u5979\u811a\u5e95\u98de\u8fc7\u53bb\u3002 \u3000\u3000\u5979\u7b2c\u4e00\u6b21\u9886\u7565\u5230\u8fd9\u79cd\u65b0\u5947\u7684\u523a\u6fc0\uff0c\u89c9\u5f97\u53ea\u8981\u548c\u695a\u7559\u9999\u5728\u4e00\u8d77\uff0c\u968f\u65f6\u968f\u5730\u90fd\u53ef\u80fd\u6709\u65b0\u5947\u7684 \u4e8b\u53d1\u751f\u3002 \u3000\u3000\u8fd9\u65f6\u4ed6\u4eec\u5df2\u5230\u4e86\u4e2a\u5f88\u5927\u7684\u5ead\u56ed\u4e2d\uff0c\u4ed6\u4eec\u6084\u6084\u7a7f\u8fc7\u8bb8\u591a\u5c0f\u7af9\u6797\uff0c\u6765\u5230\u4e2a\u5c0f\u9662\uff0c\u9662\u4e2d\u7af9\u6797\u8427 \u7d22\uff0c\u5c4b\u91cc\u4e00\u706f\u5982\u8c46\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u6ca1\u6709\u4eba\uff0c\u53ea\u5b58\u53e3\u68fa\u6750\uff0c\u70db\u53f0\u4e0a\u70db\u70db\u6cea\u5df2\u4e7e\uff0c\u4ec5\u5269\u4e0b\u4e00\u706f\u83b9\u83b9\uff0c\u7d22\u6f2b\u9ec4\u683c\uff0c\u66f4\u663e\u5f97 \u8bf4\u4e0d\u51fa\u7684\u51c4\u51c9\u3002 \u3000\u3000\u795e\u6848\u4e0a\u6709\u4e2a\u795e\u724c\uff0c\u4e0a\u9762\u5199\u7684\u540d\u5b57\u662f\u201c\u65bd\u8335\u201d\u3002 \u3000\u3000\u77f3\u7ee3\u4e91\u98a4\u58f0\u9053\uff1a\u201c\u8fd9\u91cc\u96be\u9053\u662f\u65bd\u5bb6\u5e84\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u55ef\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u5e26\u6211\u8fd9\u91cc\u6765\u5e72\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u6ca1\u6709\u8bf4\u8bdd\uff0c\u90fd\u63a8\u5f00\u95e8\uff0c\u62c9\u7740\u5979\u8d70\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u77f3\u7ee3\u4e91\u53ea\u89c9\u5f97\u5168\u8eab\u90fd\u5728\u53d1\u51b7\uff0c\u9053\uff0c\u201c\u4f60\u8fd9\u4eba\u771f\u5947\u602a\uff0c\u5e26\u6211\u5230\u8fbe\u91cc\u6765\u5e72\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u4ed6\u7b11\u5f97\u5f88\u795e\u79d8\uff0c\u9053\uff1a\u201c\u8ba9\u4f60\u6765\u770b\u770b\u8fd9\u4f4d\u65bd\u59d1\u5a18\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u673a\u4f36\u4f36\u6253\u4e86\u4e2a\u5bd2\u9178\uff0c\u560e\u58f0\u9053\uff1a\u201c\u6211\u4e0d\u8981\u770b\uff0c\u6211\u3002\u2026\u6211\u4eec\u5feb\u8d70\u5427\uff01\u201d \u3000\u3000\u695a\u7559\u9999\u975e\u4f46\u4e0d\u653e\u5979\u8d70\uff0c\u53cd\u9762\u628a\u5979\u62c9\u5230\u68fa\u6750\u65c1\u3002 \u3000\u3000\u77f3\u7ee3\u4e91\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u8981\u9a87\u6781\u5927\u54ed\u8d77\u6765\uff0c\u4f46\u5374\u5df2\u6015\u5f97\u8fde\u58f0\u97f3\u90fd\u53d1\u4e0d\u51fa\u4e86\uff0c\u5979\u518d\u4e5f\u60f3\u4e0d\u51fa\u695a\u7559 \u9999\u4e3a\u4f55\u8981\u8fd9\u6837\u5bf9\u5979\u3002 \u3000\u3000\u695a\u7559\u9999\u7adf\u5df2\u5c06\u68fa\u6750\u6380\u5f00\u3002 \u3000\u3000\u4ed6\u5168\u795e\u8d2f\u6ce8\u5728\u68fa\u6750\u91cc\uff0c\u7adf\u672a\u53d1\u89c9\u7a97\u5916\u6709\u4e2a\u4eba\u6b63\u5c4f\u4f4f\u4e86\u547c\u5438\uff0c\u5728\u5077\u5077\u7684\u76ef\u4f4f\u4ed6\uff0c\u76ee\u4e2d\u5145\u6ee1 \u4e86\u6000\u6068\u4e4b\u610f\u3002 \u3000\u3000\u695a\u7559\u9999\u5ffd\u7136\u628a\u624b\u4f38\u5165\u4e86\u68fa\u6750\uff0c\u53bb\u6478\u6b7b\u4eba\u7684\u8138\u3002 \u3000\u3000\u77f3\u7ee3\u4e91\u7259\u9f7f\u683c\u683c\u7684\u6253\u6218\uff0c\u4eba\u5df2\u51e0\u4e4e\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5979\u8fd9\u624d\u53d1\u73b0\u695a\u7559\u9999\u771f\u7684\u75af\u4e86\uff0c\u75af\u5f97\u53ef\u6015\u3002 \u3000\u3000\u695a\u7559\u9999\u4f3c\u4e4e\u5728\u6b7b\u4eba\u8138\u4e0a\u63ed\u4e0b\u4e86\u5c42\u76ae\uff0c\u5ffd\u7136\u56de\u5934\u9053\uff1a\u201c\u4f60\u6765\u770b\u770b\uff0c\u8ba4\u4e0d\u8ba4\u5f97\u5979\uff1f\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u62fc\u547d\u7684\u6447\u5934\u9053\uff1a\u201c\u4e0d\u201c\u2026\u4e0d\u2026\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u67d4\u58f0\u9053\uff1a\u201c\u4f60\u53ea\u8981\u770b\u4e00\u773c\uff0c\u5c31\u77e5\u9053\u6211\u4e3a\u4ec0\u9ebd\u8981\u4f60\u5230\u8fd9\u91cc\u6765\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u53ea\u6709\u53bb\u770b\u4e00\u773c\u3002 \u3000\u3000\u8fd9\u4e00\u773c\u770b\u8fc7\uff0c\u5979\u4e5f\u597d\u4f3c\u5ffd\u7136\u75af\u4e86\u4f3c\u7684\uff0c\u5f20\u5f00\u5634\u5927\u53eb\u8d77\u6765\u3002 \u3000\u3000\u5f0a\u6750\u91cc\u7684\u6b7b\u4eba\u7adf\u662f\u5979\u59d0\u59d0\u3002 \u3000\u3000\u695a\u7559\u9999\u4e0d\u7b49\u5979\u547c\u58f0\u53d1\u51fa\uff0c\u5df2\u63a9\u4f4f\u4e86\u5979\u7684\u5634\uff0c\u8f7b\u8f7b\u6276\u7740\u5979\u7684\u80cc\uff0c\u7b49\u5979\u7684\u60ca\u614c\u5e73\u9759\u4e0b\u6765\u624d\u67d4 \u58f0\u9053\uff1a\u201c\u5c0f\u58f0\u8bf4\u8bdd\uff0c\u83ab\u8981\u60ca\u52a8\u522b\u4eba\uff0c\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u70b9\u4e86\u70b9\u5934\uff0c\u7b49\u695a\u7559\u9999\u7684\u624b\u653e\u5f00\uff0c\u5979\u76ee\u4e2d\u5df2\u4e0d\u7981\u6d41\u4e0b\u6cea\u6765\uff0c \u3000\u3000\u98a4\u58f0\u6ce3\u9053\uff1a\u201c\u6211\u59d0\u59d0\u7684\u4f53\u600e\u4f1a\u5230\u8fd9\u91cc\u6765\u4e86\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u773c\u775b\u91cc\u53d1\u5149\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u53ea\u56e0\u4e3a\u8981\u6709\u4e00\u4e2a\u4eba\u7684\u4f53\u6765\u9876\u66ff\u65bd\u8335\uff0c\u4f60\u59d0\u59d0\u53c8\u6070\u5de7\u75c5\u5728 \u5782\u5371\uff0c\u6240\u4ee5\u4ed6\u5c31\u9009\u4e0a\u4e86\u4f60\u59d0\u59d0\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u9053\uff1a\u201c\u8fd9\u2026\u4eba\u96be\u9053\u662f\u548c\u6211\u4e8c\u53d4\u4e32\u901a\u597d\u4e86\u7684\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u8d22\u5e1b\u52a8\u4eba\u5fc3\uff0c\u8fd9\u4e5f\u6028\u4e0d\u4e86\u4f60\u4e8c\u53d4\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u5f20\u5927\u4e86\u5634\uff0c\u8fde\u6c14\u90fd\u51e0\u4e4e\u505c\u4f4f\u4e86\u3002\u5979\u518d\u4e5f\u60f3\u4e0d\u5230\u4e16\u4e0a\u7adf\u6709\u8fd9\u79cd\u4e0d\u53ef\u601d\u8bae\u7684\u4e8b\u3002 \u3000\u3000\u997c\u4e86\u534a\u664c\uff0c\u5979\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u68fa\u6750\u91cc\u65e2\u7136\u662f\u6211\u59d0\u59d0\uff0c\u90a3\u9ebd\u65bd\u8335\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u82e5\u662f\u6211\u731c\u7684\u4e0d\u9519\uff0c\u4f60\u5f88\u5feb\u5c31\u53ef\u770b\u5230\u5979\u4e86\uff01\u201d \u3000\u3000\u7b49\u695a\u7559\u9999\u4ed6\u4eec\u8d70\u51fa\u53bb\uff01\u8eb2\u5728\u7a97\u5916\u7684\u4eba\u7acb\u523b\u4e5f\u8f6c\u8eab\u98de\u5954\uff0c\u661f\u5149\u7167\u7740\u5979\u5934\u4e0a\u7684\u767d\u53d1\uff0c\u8fd9\u4eba\u5c45 \u7136\u7adf\u662f\u6881\u5988\u3002 \u3000\u3000\u96be\u9053\u5979\u65e9\u5df1\u77e5\u9053\u68fa\u6750\u7684\u4f53\u5e76\u975e\u5979\u7684\u8335\u513f\uff1f\u90a3\u9ebd\u5979\u53c8\u4e3a\u4f55\u8fd8\u8981\u6545\u4f5c\u60b2\u4f24\uff1f\u8fd9\u548c\u5584\u7684\u8001\u5987\u4eba \u96be\u9053\u4e5f\u6709\u4ec0\u9ebd\u8be1\u79d8\u7684\u56fe\u8c0b\u4e0d\u6210\uff1f \u3000\u3000\u695a\u7559\u9999\u62c9\u7740\u77f3\u7ee3\u4e91\u5411\u5916\u8dd1\uff0c\u53ea\u671b\u80fd\u5feb\u4e9b\u79bb\u5f00\u8fd9\u5730\u65b9\u3002 \u3000\u3000\u4f46\u5c31\u5728\u8fd9\u65f6\u7a81\u7136\u4eba\u9053\uff1a\u201c\u5927\u53d4\u4f60\u9a97\u6211\uff0c\u5927\u4eba\u600e\u9ebd\u80fd\u9a97\u5c0f\u5b69\u3002\u201d\u8fd9\u53e5\u8bdd\u6ca1\u8bf4\u5b8c\uff0c\u5df2\u6709\u4e2a\u4eba \u62e6\u4f4f\u4e86\u4ed6\u7684\u53bb\u8def\u3002 \u3000\u3000\u53ea\u89c1\u8fd9\u4eba\u7ea2\u7ea2\u7684\u8138\uff0c\u5934\u4e0a\u90fd\u5df2\u767d\u53d1\u82cd\u82cd\u8eab\u4e0a\u7a7f\u7740\u4ef6\u5927\u7ea2\u7ee3\u82b1\u7684\u7ae5\u8863\u8fd9\u4e0d\u662f\u90a3\u4f4d\u859b\u5b9d\u5b9d\u662f \u8c01\uff1f \u3000\u3000\u695a\u7559\u9999\u6697\u4e2d\u53f9\u4e86\u53e3\u6c14\u63a8\u5f00\u77f3\u7ee3\u4e91\uff0c\u6084\u6084\u9053\uff1a\u201c\u8f6c\u89d2\u90a3\u8fb9\u6709\u9053\u95e8\uff0c\u5feb\u8d70\uff0c\u56de\u5bb6\u53bb\u7b49\u6211\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u65e9\u5df2\u5413\u5446\u4e86\u3002\u8fde\u8dd1\u90fd\u5df2\u8dd1\u4e0d\u52a8\u3002 \u3000\u3000\u859b\u5b9d\u5b9d\u6839\u672c\u6ca1\u6709\u7559\u610f\u5230\u5979\uff0c\u53ea\u662f\u77aa\u7740\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u9a97\u6211\uff0c\u5929\u4e0a\u7684\u661f\u4e0d\u662f\u4e24\u4e07\u516b\u5343\u56db\u767e \u53c1\u5341\u4e03\u4e2a\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u89c1\u5230\u77f3\u7ee3\u4e91\u5df2\u8d70\u8fdc\uff0c\u624d\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u662f\u9ebd\uff1f\u53ea\u6015\u6211\u6570\u9519\u3002\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u9053\uff1a\u201c\u5927\u4eba\u4e0d\u53ef\u4ee5\u9a97\u5c0f\u5b69\u5b50\uff0c\u4f60\u5374\u9a97\u4e86\u6211\uff0c\u6211\u3002\u3002\u201d \u3000\u3000\u4ed6\u7684\u5634\u4e00\u64a4\uff0c\u5ffd\u7136\u5750\u5230\u5730\u4e0a\u653e\u58f0\u5927\u54ed\u8d77\u6765\u3002 \u3000\u3000\u8fd9\u4e00\u7740\u5012\u51fa\u4e86\u695a\u7559\u9999\u610f\u6599\u4e4b\u5916\uff0c\u53ea\u6709\u8d54\u7b11\u9053\uff1a\u201c\u6211\u4eca\u5929\u665a\u4e0a\u66ff\u4f60\u6570\u6e05\u695a\u660e\u5929\u518d\u544a\u8bc9\u4f60\u597d \u4e0d\u597d\uff1f\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u9053\uff1a\u201c\u4e0d\u884c\uff0c\u4f60\u4eca\u5929\u665a\u4e0a\u5c31\u8981\u966a\u6211\u6570\uff0c\u9664\u975e\u4f60\u80af\u8ba9\u6211\u6478\u6478\u4f60\u9f3b\u5b50\uff0c\u5426\u5219\u6211\u7edd\u4e0d\u653e \u4f60\u8d70\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u9ebd\u8981\u6478\u6211\u7684\u9f3b\u5b50\uff1f\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u7684\u9f3b\u5b50\u5f88\u597d\u73a9\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u6211\u7684\u9f3b\u5b50\u5f88\u597d\u73a9\uff1f\u6709\u4ec0\u9ebd\u597d\u73a9\u7684\uff1f\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u9053\uff1a\u201c\u4f60\u7684\u9f3b\u5b50\u82e5\u4e0d\u597d\u73a9\uff01\u4f60\u81ea\u5df1\u4e3a\u4ec0\u9ebd\u8001\u662f\u53bb\u6478\u5b83\uff1f\u201d \u3000\u3000\u4ed6\u8df3\u7740\u811a\uff0c\u64a4\u8d56\u9053\uff1a\u201c\u6211\u4e5f\u8981\u6a21\u4f60\u7684\u9f3b\u5b50\uff0c\u6211\u4e5f\u8981\u6478\u2026\u2026\u5feb\u4e9b\u7ed9\u6211\u6478\u2026\u4f60\u8981\u662f\u4e0d\u7ed9\u6211 \u6478\uff0c\u6211\u5c31\u8981\u4f60\u8d54\u661f\u661f\u3002\u201d \u3000\u3000\u88ab\u4eba\u6478\u9f3b\u5b50\u867d\u7136\u4e0d\u5927\u6109\u5feb\uff0c\u4f46\u603b\u6bd4\u6570\u661f\u661f\u597d\u591a\u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\u5b9e\u5728\u4e0d\u613f\u548c\u8fd9\u767d\u75f4\u518d\u7ea0\u7f20\u4e0b\u53bb\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u6211\u8ba9\u4f60\u6478\u9f3b\u5b50\u3002\u4f60\u5c31\u4e0d\u518d\u7f20\u7740\u6211\uff1f\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u7acb\u523b\u7834\u6d95\u4e3a\u7b11\uff0c\u9053\uff1a\u201c\u6211\u53ea\u8981\u6478\u4e00\u4e0b\uff0c\u5c31\u8ba9\u4f60\u8d70\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u597d\uff01\u6478\u5427\uff01\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u96c0\u8dc3\u53c1\u4e08\uff0c\u7f13\u7f13\u4f38\u51fa\u624b\uff0c\u53bb\u6478\u695a\u7559\u9999\u7684\u9f3b\u5b50\u3002 \u3000\u3000\u4ed6\u8138\u4e0a\u4e00\u76f4\u7b11\u563b\u563b\u7684\uff0c\u52a8\u4f5c\u672c\u6765\u5f88\u6162\uff0c\u4f46\u7a81\u7136\u95f4\uff0c\u5982\u95ea\u7535\u822c\u5411\u695a\u7559\u9999\u9f3b\u65c1\u7684\u201c\u8fce\u9999\u7a74\u201d \u4e00\u634f\u3002\u695a\u7559\u9999\u53ea\u89c9\u8eab\u5b50\u4e00\u9ebb\uff0c\u4eba\u5df2\u88ab\u4ed6\u4e3e\u8d77\u3002 \u3000\u3000\u53ea\u542c\u4ed6\u683c\u683c\u5956\u9053\uff1a\u201c\u4f60\u5f04\u574f\u4e86\u6211\u7684\u661f\u661f\uff0c\u6211\u8981\u7838\u6241\u4f60\u7684\u5934\u3002\u201d \u3000\u3000\u4ed6\u7adf\u5c06\u695a\u7559\u9999\u7684\u8eab\u5b50\u62a1\u4e86\u8d77\u6765\uff0c\u5f80\u5047\u5c71\u4e0a\u63b7\u4e86\u8fc7\u53bb\u3002\u695a\u7559\u9999\u7684\u5934\u773c\u770b\u5c31\u8981\u88ab\u7838\u5f97\u7a00\u70c2\u3002 \u3000\u3000\u77f3\u7ee3\u4e91\u5954\u5230\u89d2\u95e8\u65f6\uff0c\u5df2\u5598\u4e0d\u8fc7\u6c14\u6765\u4e86\u3002\u95e8\u867d\u7136\u6ca1\u6709\u4e0a\u9501\uff0c\u5374\u662f\u7528\u94c1\u6813\u62f4\u7740\u7684\u3002 \u3000\u3000\u77f3\u7ee3\u4e91\u5598\u606f\uff0c\u53bb\u62d4\u95e8\u6813\uff0c\u600e\u5948\u94c1\u62f4\u5df2\u9500\u4f4f\uff0c\u5979\u8d8a\u7740\u6025\uff0c\u5c31\u8d8a\u62c9\u4e0d\u5f00\uff0c\u8d8a\u62c9\u4e0d\u5f00\uff0c\u5c31\u8d8a\u7740 \u6025\u3002 \u3000\u3000\u5979\u7b80\u76f4\u5feb\u6025\u75af\u4e86\u53c8\u4e0d\u77e5\u695a\u7559\u9999\u4f1a\u4e0d\u4f1a\u8d77\u6765\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u7a81\u542c\u4e00\u4eba\u683c\u683c\u7b11\u9053\uff1a\u201c\u4f60\u65e2\u5df2\u6765\u4e86\uff0c\u5c31\u5728\u8fd9\u91cc\u4f4f\u51e0\u5929\u5427\uff01\u4f55\u5fc5\u6025\u7740\u8d70\u5462\uff1f\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u5413\u5f97\u9b42\u90fd\u6ca1\u6709\u4e86\uff0c\u8fde\u5934\u90fd\u4e0d\u6562\u56de\uff0c\u62d4\u811a\u5c31\u8dd1\u3002\u53ef\u662f\u624d\u8dd1\u4e86\u4e24\u6b65\uff0c\u5c31\u6709\u53ea\u53c8\u7626\u53c8 \u4e7e\uff0c\u9b3c\u722a\u822c\u7684\u624b\u4f38\u4e86\u8fc7\u6765\uff0c\u4e00\u628a\u627c\u4f4f\u4e86\u5979\u96ea\u767d\u7684\u8116\u5b50\u3002\u5979\u8fde\u60ca\u547c\u90fd\u6ca1\u53d1\u51fa\uff0c\u5c31\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u695a\u7559\u9999\u505a\u9b3c\u4e5f\u6ca1\u6709\u60f3\u5230\u81ea\u5df2\u7adf\u4f1a\u6b7b\u5728\u4e2a\u201c\u767d\u75f4\u201d\u7684\u624b\u91cc\u3002\u859b\u5b9d\u5b9d\u4e00\u677e\u624b\u4ed6\u8eab\u5b50\u5c31\u5411\u5047\u5c71 \u98de\u4e86\u8fc7\u53bb\uff0c\u8fd9\u65f6\u4ed6\u867d\u5df2\u80fd\u52a8\u5f39\uff0c\u4f46\u82e5\u60f3\u6539\u53d8\u8eab\u5f62\uff0c\u5374\u662f\u65e0\u8bba\u5982\u4f55\u4e5f\u6765\u4e0d\u53ca\u7684\u4e86\u3002 \u3000\u3000\u4ed6\u53ea\u6709\u7528\u624b\u6342\u7740\u5934\uff0c\u5e0c\u671b\u80fd\u52c9\u5f3a\u6321\u4e00\u6321\uff0c\u53ef\u662f\u4ed6\u81ea\u5df1\u4e5f\u77e5\u9053\u8fd9\u4e0b\u5c31\u7b97\u80fd\u4e0d\u6b7b\u81f3\u5c11\u4e5f\u53bb\u4e86 \u534a\u6761\u547d\u3002 \u3000\u3000\u90a3\u201c\u75af\u5b50\u201d\u4ecd\u7136\u4e0d\u4f1a\u653e\u8fc7\u4ed6\u7684\u3002\u53ea\u542c\u201c\u8f70\u201d\u7684\u4e00\u58f0\uff0c\u5b9b\u5982\u5929\u5d29\u5730\u88c2\uff0c\u77f3\u5934\u4e00\u7247\u7247\u98de\u4e86\u8d77 \u6765\uff0c\u4ed6\u7684\u5934\u76ae\u6ca1\u6709\u88ab\u649e\u7834\uff0c\u5047\u5c71\u53cd\u800c\u88ab\u649e\u5f00\u4e86\u4e00\u4e2a\u5927\u6d1e\u3002\u4ed6\u7684\u5934\u96be\u9053\u6bd4\u77f3\u5934\u8fd8\u786c\uff1f \u3000\u3000\u859b\u5b9d\u5b9d\u672c\u6765\u5728\u62cd\u624b\u5927\u7b11\u7740\uff0c\u5ffd\u7136\u4e5f\u6014\u4f4f\u4e86\uff0c\u5927\u53eb\u9053\uff1a\u201c\u4e0d\u5f97\u4e86\uff0c\u4e0d\u5f97\u4e86\uff0c\u8fd9\u4eba\u7684\u8111\u888b\u662f \u94c1\u505a\u7684\u3002\u201d \u3000\u3000\u4ed6\u4e00\u9762\u5927\u53eb\uff0c\u4e00\u9762\u5df2\u8f6c\u8eab\u98de\u5954\u4e86\u51fa\u53bb\uff0c\u695a\u7559\u9999\u53ea\u89c9\u5f97\u5168\u8eab\u53d1\u75bc\uff0c\u8111\u888b\u53d1\u6655\uff0c\u4e5f\u5f04\u4e0d\u6e05\u8fd9 \u662f\u600e\u9ebd\u56de\u4e8b\u3002\u4ed6\u4eff\u4f5b\u542c\u5230\u5047\u5c71\u91cc\u6709\u4eba\u547c\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u695a\u7559\u9999\u9ebd\u2026\u3002\u201d \u3000\u3000\u58f0\u97f3\u53c8\u5c16\u53c8\u54cd\u3002\u4e00\u542c\u5c31\u77e5\u9053\u662f\u82b1\u91d1\u5f13\u3002 \u3000\u3000\u695a\u7559\u9999\u6323\u624e\u7740\uff0c\u63c9\u4e86\u63c9\u773c\u775b\uff0c\u624d\u770b\u6e05\u81ea\u5df2\u7adf\u5df2\u8dcc\u5728\u5f20\u5e8a\u4e0a\uff0c\u5e8a\u65c1\u8fb9\u6709\u4e2a\u4eba\u7528\u624b\u63a9\u4f4f\u80f8 \u819b\uff0c\u6b63\u662f\u82b1\u91d1\u5f13\u3002\u53e6\u5916\u8fd8\u6709\u4e2a\u7537\u4eba\u5df2\u5377\u6210\u4e00\u56e2\uff0c\u4e0d\u4f4f\u53d1\u6296\u3002 \u3000\u3000\u8fd9\u5047\u5c71\u539f\u6765\u662f\u7a7a\u7684\uff0c\u5916\u9762\u770b\u6765\u867d\u7136\u5f88\u575a\u5b9e\uff0c\u5176\u5b9e\u5374\u85cf\u5f97\u5f88\u597d\uff0c\u800c\u4e14\u5e76\u4e0d\u662f\u77f3\u5934\uff0c\u53ea\u662f\u7528 \u6c34\u6ce5\u780c\u6210\u4e86\u5047\u5c71\u7684\u6a21\u6837\uff0c\u4e0a\u9762\u518d\u94fa\u4e9b\u9752\u8349\u3002\u8fd9\u539f\u6765\u5c31\u662f\u82b1\u91d1\u5f13\u548c\u7537\u4eba\u5e7d\u4f1a\u7684\u5730\u65b9\u3002 \u3000\u3000\u695a\u7559\u9999\u5fcd\u4e0d\u4f4f\u7b11\u4e86\uff0c\u4ed6\u89c9\u5f97\u81ea\u5df1\u8fd0\u6c14\u5b9e\u5728\u4e0d\u9519\u3002\u53ea\u89c1\u90a3\u7537\u4eba\u5df2\u4e00\u6e9c\u70df\u9003\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u695a\u7559\u9999\u4e5f\u7ad9\u4e86\u8d77\u6765\uff0c\u62b1\u62f3\u7b11\u9053\uff1a\u201c\u5bf9\u4e0d\u8d77\uff01\u5bf9\u4e0d\u8d77\uff0c\u4e0b\u6b21\u6211\u82e5\u518d\u5f80\u77f3\u5934\u4e0a\u649e\u65f6\uff0c\u5b9a\u5148\u6572 \u6572\u95e8\u3002\u201d \u3000\u3000\u82b1\u91d1\u5f13\u5374\u4e00\u628a\u6280\u4f4f\u4e86\u4ed6\uff0c\u4f3c\u7b11\u975e\u7b11\u7684\u779f\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u4f60\u73b0\u5728\u5c31\u60f3\u8d70\uff0c\u4f60\u96be\u9053\u4e0d\u662f\u6765\u627e\u6211 \u7684\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u5b9e\u5728\u4e0d\u6562\u53bb\u77a7\u5979\u7b11\u5bb9\uff0c\u66f4\u4e0d\u6562\u53bb\u77a7\u5979\u90a3\u8d64\u88f8\u88f8\u7684\u8eab\u5b50\uff0c\u4ed6\u5b9e\u5728\u53d7\u4e0d\u4e86\uff0c\u773c\u775b\u4e5f\u4e0d \u77e5\u8be5\u5f80\u54ea\u91cc\u662d\u624d\u597d\uff0c\u53ea\u6709\u82e6\u7b11\u9053\u201c\u6211\u867d\u7136\u662f\u6765\u627e\u4f60\u7684\u2026\u2026\u201d \u3000\u3000\u8bdd\u8fd8\u672a\u8bf4\u5b8c\uff0c\u82b1\u91d1\u5f13\u65e9\u5df2\u6251\u4e86\u8fc7\u6765\uff0c\u5403\u5403\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u6211\u65e9\u5c31\u77e5\u9053\u4f60\u8fdf\u65e9\u603b\u5fcd\u4e0d\u4f4f \u4f1a\u6765\u627e\u6211\u7684\uff0c\u6211\u65e9\u5c31\u77e5\u9053\u4f60\u4e0d\u662f\u597d\u4e1c\u897f\uff0c\u770b\u5728\u4f60\u8fd9\u53cc\u8981\u4eba\u547d\u7684\u773c\u775b\u4efd\u4e0a\uff0c\u59d0\u59d0\u5c31\u7b54\u5e94\u4e86\u4f60\u8fd9 \u4e00\u6b21\u5427\uff01\u201d \u3000\u3000\u5979\u8eab\u4e0a\u6c57\u6dcb\u6dcb\u7684\uff0c\u53c8\u9ecf\u53c8\u6e7f\uff0c\u867d\u7136\u5230\u5904\u90fd\u64e6\u6ee1\u9999\u6c34\u548c\u82b1\u7c89\uff0c\u8fd8\u662f\u63a9\u4e0d\u4f4f\u90a3\u4e00\u80a1\u72d0\u72f8\u81ed\u3002 \u3000\u3000\u695a\u7559\u9999\u751f\u5e73\u7b2c\u4e00\u6b21\u89c9\u5f97\u9f3b\u5b50\u4e0d\u7075\u4e5f\u6709\u597d\u5904\uff0c\u8d76\u7d27\u4f38\u624b\u53bb\u63a8\uff0c\u4e00\u4e0d\u5c0f\u5fc3\uff0c\u5374\u63a8\u5728\u4e00\u56e2\u8f6f\u7ef5 \u7ef5\u7684\u4e1c\u897f\u4e0a\u3002 \u3000\u3000\u82b1\u91d1\u5f13\u683c\u683c\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u53cc\u624b\u53ef\u771f\u4e0d\u8001\u5b9e\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u8fde\u52a8\u90fd\u4e0d\u6562\u52a8\u4e86\uff0c\u82e6\u7740\u8111\u9053\uff1a\u201c\u6211\u672c\u6765\u867d\u662f\u6765\u627e\u4f60\uff0c\u53ef\u662f\u6211\u73b0\u5728\u4e0d\u60f3\u8d70\u4e5f\u4e0d\u884c \u4e86\u3002\u201d \u3000\u3000\u82b1\u91d1\u5f13\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u96be\u9053\u6ca1\u6709\u770b\u5230\u6211\u662f\u88ab\u859b\u5b9d\u5b9d\u629b\u8fdb\u6765\u7684\uff1f\u73b0\u5728\u4ed6\u5df2\u7ecf\u77e5\u9053\u6211\u5728\u8fd9\u91cc\uff0c\u8fd9\u5730 \u65b9\u53c8\u6709\u4e86\u4e2a\u5927\u6d1e\uff0c\u82e5\u662f\u88ab\u522b\u4eba\u77a7\u89c1\uff0c\u88ab\u65bd\u4e3e\u4eba\u77a7\u89c1\u2026\u3002\u201d \u3000\u3000\u82b1\u91d1\u5f13\u9053\uff1a\u201c\u6211\u624d\u4e0d\u6015\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u53ef\u662f\u859b\u5b9d\u5b9d\u82e5\u53c8\u56de\u6765\u6363\u86cb\u5462\uff1f\u90a3\u5c82\u975e\u5927\u715e\u98ce\u666f\uff0c\u4f60\u603b\u8be5\u77e5\u9053\uff0c\u4ed6\u90a3\u79cd\u4eba\u662f \u4ec0\u9ebd\u4e8b\u90fd\u505a\u5f97\u51fa\u7684\u3002\u201d \u3000\u3000\u82b1\u91d1\u5f13\u8fd9\u624d\u677e\u4e86\u624b\uff0c\u6068\u6068\u9053\uff1a\u201c\u8fd9\u75af\u5b50\uff0c\u767d\u75f4\u3002\u3002\u6211\u9976\u4ed6\u624d\u602a\u3002\u3002\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u8fd9\u624d\u677e\u4e86\u53e3\u6c14\uff0c\u5374\u53c8\u95ee\u9053\uff1a\u201c\u4ed6\u771f\u662f\u767d\u75f4\uff1f\u767d\u75f4\u771f\u4f1a\u6709\u90a3\u9ebd\u597d\u7684\u529f\u592b\uff1f\u201d \u3000\u3000\u82b1\u91d1\u5f13\u9053\uff1a\u201c\u4ed6\u4ece\u5c0f\u5c31\u53d7\u54e5\u54e5\u7684\u6c14\uff0c\u4ed6\u54e5\u603b\u662f\u9a82\u4ed6\u6ca1\u51fa\u606f\u5f8c\u3002\u4eba\u90fd\u8bf4\u4ed6\u662f\u7ec3\u6b66\u7ec3\u75af\u7684\uff0c \u6211\u770b\u4ed6\u7b80\u76f4\u662f\u88ab\u6c14\u75af\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u624d\u53f9\u606f\u7740\uff0c\u9053\uff1a\u201c\u54e5\u54e5\u82e5\u662f\u6709\u540d\uff0c\u505a\u5f1f\u5f1f\u7684\u4eba\u603b\u662f\u5403\u4e8f\u4e9b\u7684\u3002\u201d \u3000\u3000\u82b1\u91d1\u5f13\u5ffd\u53c8\u6293\u4f4f\u4e86\u4ed6\u7684\u624b\uff0c\u695a\u7559\u9999\u5413\u5f97\u51e0\u4e4e\u8fde\u51b7\u6c57\u90fd\u5192\u51fa\u6765\u4e86\uff0c \u3000\u3000\u5e78\u597d\u82b1\u91d1\u5f13\u5e76\u6ca1\u6709\u4ec0\u9ebd\u52a8\u4f5c\uff0c\u53ea\u662f\u7528\u773c\u775b\u779f\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u4f60\u8fd8\u6765\u4e0d\u6765\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u8f7b\u54b3\u4e86\u4e24\u58f0\uff0c\u9053\uff1a\u201c\u5f53\u7136\u8981\u6765\u3002\u201d \u3000\u3000\u82b1\u91d1\u5f13\u9053\uff1a\u201c\u4ec0\u9ebd\u65f6\u5019\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u660e\u2026\u2026\u660e\u5f8c\u5929\uff0c\u6211\u4e00\u5b9a\u2026\u3002\u3002\u4e00\u5b9a\u2026\u2026\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u8df3\u4e86\u8d77\u6765\u3002\u9053\uff1a\u201c\u53c8\u6709\u4eba\u6765\u4e86\uff0c\u6211\u5f97\u8d76\u7d27\u8d70\u2026\u2026\u201d \u3000\u3000\u8bdd\u672a\u8bf4\u5b8c\uff0c\u4ed6\u5df2\u94bb\u4e86\u51fa\u53bb\uff0c\u9003\u5f97\u771f\u5feb\u3002\u5e78\u597d\u4ed6\u8d70\u5f97\u5feb\uff0c\u5426\u5219\u9ebb\u70e6\u53c8\u5927\u4e86\u3002 \u3000\u3000\u4ed6\u4e00\u8d70\uff0c\u5c31\u77a7\u89c1\u51e0\u5341\u4e2a\u4eba\u98de\u8dd1\u4e86\u8fc7\u6765\uff0c\u6709\u7684\u62ff\u706f\u7b3c\uff0c\u6709\u7684\u63d0\u5200\uff0c\u8d70\u6700\u524d\u9762\u7684\u662f\u4e2a\u53c8\u9ad8\u53c8 \u5927\u7684\u80d6\u8001\u5934\uff0c\u8eab\u4e0a\u53ea\u7a7f\u7740\u5957\u77ed\u88e4\uff0c\u624b\u91cc\u4e5f\u63d0\u7740\u628a\u5355\u5200\uff0c\u6c14\u5f97\u4e00\u5f20\u8138\u90fd\u7ea2\u4e86\uff0c\u6124\u6012\u96be\u6291\u7684\u6325\u7740 \u5200\u9053\uff1a\u201c\u8c01\u6253\u6b7b\u90a3\u91c7\u82b1\u76d7\uff0c\u9ec4\u91d1\u767e\u4e24\uff0c\u5343\u4e07\u83ab\u8ba9\u4ed6\u9003\u8d70\u201d \u3000\u3000\u695a\u7559\u9999\u867d\u88ab\u4ed6\u5f53\u505a\u91c7\u82b1\u8d3c\uff0c\u4f3c\u4e5f\u5e76\u4e0d\u602a\u4ed6\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u4eba\u7684\u786e\u5f88\u53ef\u601c\uff0c\u4e0d\u4f46\u5a36\u9519\u4e86\u5ab3\u5987\uff0c\u4e5f\u5a36\u6f84\u4e86\u8001\u5a46\uff0c\u5bb6\u91cc\u5b58\u4e0b\u8fd9\u6837\u4e24\u4e2a\u5973\u5b50\uff0c\u5c45\u7136 \u8fd8\u672a\u88ab\u6c14\u6b7b\u5df2\u5f88\u4e0d\u5bb9\u6613\u4e86\u3002\u4f46\u4ed6\u5374\u600e\u4f1a\u77e5\u9053\u8fd9\u91cc\u6709\u4e2d\u201c\u91c7\u82b1\u8d3c\u201d\u5462\uff1f\u96be\u9053\u662f\u90a3\u201c\u767d\u75f4\u201d\u53bb\u544a \u8bc9\u4ed6\u7684\uff1f\u695a\u7559\u9999\u8d8a\u6765\u8d8a\u89c9\u5f97\u90a3\u201c\u767d\u75f4\u201d\u5371\u9669\uff0c\u4e5f\u8d8a\u6765\u8d8a\u89c9\u5f97\u4ed6\u6709\u610f\u601d\u4e86\u201c\u2026\u2019 \u3000\u3000\u677e\u6c5f\u5e9c\u695a\u7559\u9999\u867d\u5df2\u6765\u8fc7\u8bb8\u591a\u6b21\uff0c\u4f46\u8def\u8fd8\u662f\u4e0d\u719f\u767d\u7ed5\u4e86\u4e2a\u5708\u5b50\uff0c\u624d\u603b\u7b97\u627e\u5230\u90a3\u6761\u201c\u9752\u8863 \u5df7\u201d\u3002 \u3000\u3000\u53ea\u89c1\u5c0f\u79c3\u5b50\u6b63\u8e72\u5728\u4e00\u6839\u7cfb\u9a6c\u77f3\u65c1\u5543\u70e7\u997c\uff0c\u4e00\u53cc\u5927\u773c\u775b\u5728\u9ed1\u6697\u4e2d\u6ef4\u6e9c\u4e71\u8f6c\u3002\u695a\u7559\u9999\u4e00\u773c\u5c31 \u77a7\u89c1\u4e86\u4ed6\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u7b49\u5230\u695a\u7559\u9999\u5df2\u6765\u5230\u4ed6\u8eab\u65c1\uff0c\u624d\u77a7\u89c1\u695a\u7559\u9999\uff0c\u4ed6\u5413\u4e86\u4e00\u8df3\uff0c\u8fde\u624b\u91cc\u7684\u534a\u4e2a\u70e7\u997c\u90fd\u5413 \u98de\u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\u4e00\u4f38\u624b\u5c31\u5c06\u5c0f\u79c3\u5b50\u5413\u98de\u6389\u7684\u70e7\u997c\u63a5\u4f4f\uff0c\u8fd8\u7ed9\u4e86\u4ed6\uff0c\u9053\uff1a\u201c\u4eca\u5929\u4f60\u5b9a\u8fde\u996d\u90fd\u6ca1\u7a7a \u5403\uff0c\u5f8c\u5929\u6211\u4e00\u5b9a\u597d\u597d\u8bf7\u4f60\u5927\u5403\u5b83\u4e00\u987f\uff0c\u4f60\u613f\u5403\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u671b\u7740\u4ed6\uff0c\u6ee1\u8138\u90fd\u662f\u503e\u6155\u4e4b\u8272\uff0c\u9053\uff1a\u201c\u6211\u4ec0\u9ebd\u90fd\u4e0d\u60f3\u5403\uff0c\u60f3\u5b66\u4f1a\u5927\u53d4\u4f60\u4e00\u8eab\u672c\u4e8b\uff0c \u5c31\u5fc3\u6ee1\u610f\u8db3\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u62cd\u4e86\u62cd\u4ed6\u80a9\u5934\u7b11\u9053\u201c\u672c\u4e8b\u8981\u5b66\uff0c\u996d\u4e5f\u8981\u5403\uff0c\u65e0\u8bba\u672c\u4e8b\u591a\u5927\u7684\u4eba\uff0c\u4e5f\u90fd\u8981\u5403\u996d\u3002\u201d \u3000\u3000\u4ed6\u76ee\u5149\u4e00\u8f6c\uff0c\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u627e\u7740\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u62cd\u4e86\u62cd\u80f8\u819b\uff0c\u9053\uff1a\u201c\u5f53\u7136\u627e\u7740\u4e86\uff0c\u5c31\u662f\u524d\u9762\u90a3\u4e2a\u63a5\u7740\u76cf\u5c0f\u706f\u7b3c\u7684\u95e8\u3002\u201d \u3000\u3000\u4ed6\u5c06\u70e7\u997c\u541e\u4e0b\u53bb\u5f8c\uff0c\u8bdd\u624d\u8bf4\u5f97\u6e05\u695a\u4e86\u4e9b\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u8fd9\u6761\u5f04\u5b50\u91cc\u53ea\u4ed6\u4eec\u4e00\u5bb6\u662f\u521a\u642c\u6765 \u7684\uff0c\u800c\u4e14\u53ea\u6709\u5c0f\u592b\u59bb\u4e24\u4e2a\uff0c\u8fde\u4e2b\u5934\u90fd\u6ca1\u6709\uff0c\u592a\u592a\u597d\u50cf\u662f\u672c\u5730\u4eba\uff0c\u7537\u7684\u8bf4\u8bdd\u5374\u662f\u5317\u65b9\u53e3\u97f3\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4ed6\u4eec\u5728\u4e0d\u5728\u5bb6\uff1f\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u9053\uff1a\u201c\u542c\u8bf4\u8fd9\u592b\u59bb\u4e24\u4eba\u4e5f\u662f\u6574\u5929\u90fd\u5173\u5728\u5bb6\u91cc\u7684\uff0c\u8fde\u83dc\u90fd\u4e0d\u51fa\u53bb\u4e70\uff0c\u66f4\u4e0d\u548c\u522b\u4eba\u6253 \u4ea4\u9053\uff0c\u53ef\u662f\u521a\u5374\u6709\u4eba\u5728\u627e\u4ed6\u4eec\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u54e6\uff1f\u662f\u4ec0\u9ebd\u6837\u7684\u4eba\uff1f\u201d \u3000\u3000\u5c0f\u8868\u4e88\u9053\uff1a\u201c\u662f\u4e2a\u8001\u592a\u5a46\uff0c\u8fde\u5934\u53d1\u90fd\u767d\u4e86\uff0c\u4f46\u7cbe\u795e\u5f88\u597d\uff0c\u53ea\u4e0d\u8fc7\u770b\u6765\u5f88\u7d27\u5f20\u3002\u4e00\u8def\u4e0d\u505c \u7684\u5411\u5f8c\u9762\u671b\uff0c\u751f\u6015\u5f8c\u9762\u6709\u9b3c\u4f3c\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u773c\u775b\u4eae\u4e86\uff1a\u201c\u8001\u592a\u5a46\u2026\u2026\u5979\u662f\u4ec0\u9ebd\u65f6\u5019\u6765\u7684\uff1f\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u9053\uff1a\u201c\u5979\u6765\u7684\u65f6\u5019\u6211\u6b63\u5728\u5f00\u59cb\u5403\u70e7\u997c\uff0c\u5230\u73b0\u5728\u516b\u4e2a\u70e7\u997c\u8fd8\u6ca1\u6709\u5403\u5b8c\u3002\u201d \u3000\u3000\u4ed6\u62b9\u4e86\u62b9\u5634\uff0c\u81ea\u8a00\u81ea\u8bed\u5730\u9053\uff1a\u201c\u6211\u5403\u8d77\u70e7\u997c\u6765\u5c31\u597d\u50cf\u5403\u8695\u8c46\u4e00\u6837\uff0c\u5feb\u5f97\u5f88\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5979\u8fd8\u5728\u91cc\u9762\uff1f\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u9053\uff1a\u201c\u8fd8\u6ca1\u6709\u51fa\u6765\u3002\u201d \u3000\u3000\u4ed6\u8fd9\u53e5\u521a\u8bf4\u5b8c\u3002\u695a\u7559\u9999\u5df2\u98de\u8eab\u63a0\u5165\u4e86\u90a3\u95f4\u5c4b\u5b50\u3002 \u3000\u3000\u5c0f\u79c3\u5b50\u5410\u4e86\u5410\u820c\u5934\uff0c\u968f\u53f9\u9053\uff1a\u201c\u6211\u82e5\u975e\u65e9\u5c31\u770b\u6e05\u4e86\u4ed6\u662f\u4e2a\u4eba\uff0c\u53ea\u6015\u771f\u8981\u4ee5\u4e3a\u4ed6\u662f\u53ea \u9e1f\u3002\u3002\u3002\u201d \u3000\u3000\u8fd9\u662f\u95f4\u5f88\u666e\u901a\u7684\u5c4b\u5b50\uff0c\u5c0f\u5c0f\u7684\u9662\u5b50\u91cc\u79cd\u7740\u4e24\u682a\u6842\u6811\uff0c\u79cb\u5df1\u6df1\u4e86\uff0c\u6842\u82b1\u5f00\u5f97\u6b63\u76db\uff0c\u9ad3\u53d1\u7740 \u4e00\u9635\u9635\u6e05\u9999\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u8fd8\u4eae\u7740\u706f\u5149\uff0c\u95e8\u7a97\u662f\u5173\u7740\u7684\u3002 \u3000\u3000\u7a97\u4e0a\u6709\u4e2a\u5973\u4eba\u7684\u5f71\u5b50\uff0c\u6458\u7740\u5f88\u8001\u6d3e\u7684\u53d1\u9afb\uff0c\u6b63\u5750\u5728\u684c\u65c1\uff0c\u4f4e\u7740\u5934\uff0c\u50cf\u662f\u5728\u5199\u5b57\uff0c\u53c8\u50cf\u662f \u5728\u7ee3\u82b1\u3002 \u3000\u3000\u5230\u4e86\u8fd9\u65f6\uff0c\u695a\u7559\u9999\u4e5f\u987e\u4e0d\u5f97\u662f\u5426\u65e0\u793c\u4e86\uff0c\u7528\u529b\u63a8\u5f00\u4e86\u95e8\uff0c\u5c4b\u5185\u7684\u4eba\u539f\u6765\u6b63\u5728\u5403\u7a00\u996d\uff0c\u4e00 \u60ca\u4e4b\u4e0b\uff0c\u7897\u4e5f\u788e\u4e86\uff0c\u8fd9\u4eba\u9752\u8863\u5e03\u88d9\uff0c\u767d\u53d1\u82cd\u82cd\uff0c\u7adf\u662f\u6881\u5988\u3002 \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u679c\u7136\u662f\u4f60\uff1f\u201d \u3000\u3000\u6881\u5988\u62cd\u7740\u5fc3\u53e3\uff0c\u5598\u7740\u6c14\u9053\uff1a\u201c\u5413\u6b7b\u4eba\u4e86\uff0c\u6211\u8fd8\u4ee5\u4e3a\u662f\u5f3a\u76d7\uff0c\u60f3\u4e0d\u5230\u539f\u6765\u662f\u516c\u5b50\uff0c\u516c\u5b50\u4f60 \u4eca\u5929\u600e\u9ebd\u4f1a\u6709\u7a7a\u5230\u8fd9\u91cc\u6765\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u6b63\u8981\u95ee\u4f60\uff0c\u4f60\u662f\u600e\u9ebd\u4f1a\u5230\u8fd9\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u4ed6\u773c\u775b\u4e00\u626b\uff0c\u5c31\u77a7\u89c1\u684c\u4e0a\u662f\u53c1\u526f\u7897\u7b77\u3002 \u3000\u3000\u6881\u5988\u8d54\u7b11\u9053\uff1a\u201c\u6211\u672c\u6765\u662f\u6ca1\u7a7a\u7684\uff0c\u53ef\u662f\u6709\u597d\u51e0\u5929\u6ca1\u6709\u89c1\u5230\u4ed6\u4eec\uff0c\u5c31\u5fcd\u4e0d\u4f4f\u60f3\u6765\u770b\u770b\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u76ee\u5149\u707c\u707c\uff0c\u76ef\u7740\u5979\u9053\uff1a\u201c\u4ed6\u4eec\u662f\uff1f\u201d \u3000\u3000\u6881\u5988\u9053\uff1a\u201c\u6211\u5973\u513f\uff0c\u8fd8\u6709\u6211\u5973\u5a7f\u2026\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u51b7\u7b11\u9053\uff1a\u201c\u771f\u7684\uff0c\u6211\u4e5f\u60f3\u89c1\u89c1\u4ed6\u4eec\u3002\u201d \u3000\u3000\u6881\u5988\u5c45\u7136\u6ca1\u6709\u62d2\u7edd\uff0c\u7acb\u523b\u5c31\u558a\u9053\uff1a\u201c\u5927\u4e2d\uff0c\u5c0f\u73e0\uff0c\u5feb\u51fa\u6765\uff0c\u6709\u5ba2\u4eba\u6765\u5c4b\u5b50\u91cc\u3002\u679c\u7136\u6709\u4e00 \u7537\u4e00\u5973\u4e24\u4e2a\u4eba\u8d70\u51fa\u6765\uff0c\u4e24\u4eba\u90fd\u662f\u6ee1\u8138\u7684\u4e0d\u9ad8\u5174\uff0c\u5634\u91cc\u8fd8\u5728\u5600\u5495\u7740\uff1a\u201c\u53c1\u66f4\u534a\u591c\u7684\uff0c\u8fde\u89c9\u90fd\u4e0d \u8ba9\u4eba\u7761\u5417\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u6014\u4f4f\u4e86\u3002\u8fd9\u4e24\u4eba\u867d\u7136\u5e74\u7eaa\u90fd\u5f88\u8f7b\uff0c\u4f46\u5973\u7684\u53c8\u9ad8\u53c8\u80d6\uff0c\u5c31\u50cf\u662f\u6761\u725b\uff0c\u7537\u7684\u4e5f\u662f\u61a8\u5934 \u61a8\u8111\uff0c\u54ea\u91cc\u50cf\u662f\u4e2a\u5531\u82b1\u65e6\u7684\uff0c\u5012\u50cf\u662f\u4e2a\u5531\u9ed1\u5934\u7684\u3002 \u3000\u3000\u6881\u5988\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u516c\u5b50\u60f3\u89c1\u89c1\u4f60\u4eec\uff0c\u53ea\u6015\u662f\u77e5\u9053\u4f60\u4eec\u5bb6\u7a77\uff0c\u60f3\u6765\u6551\u6d4e\u4f60\u4eec\u7684\uff0c\u8fd8\u4e0d\u8d76\u5feb \u8fc7\u6765\u78d5\u5934\u3002\u201d \u3000\u3000\u90a3\u4e24\u53e3\u5b50\u679c\u7136\u8dea\u4e86\u4e0b\u53bb\uff0c\u5c45\u7136\u8fd8\u4f38\u51fa\u4e86\u624b\u6765\u3002 \u3000\u3000\u695a\u7559\u9999\u54ed\u7b11\u4e0d\u5f97\uff0c\u53ea\u6709\u5f80\u6000\u91cc\u638f\u94f6\u5b50\uff0c\u5634\u91cc\u542b\u542b\u7cca\u7cca\u7684\u8bf4\u7740\u8bdd\uff0c\u5374\u8fde\u4ed6\u81ea\u5df1\u90fd\u4e0d\u77e5\u9053\u5728 \u8bf4\u4ec0\u9ebd\u3002 \u3000\u3000\u597d\u5bb9\u6613\u627e\u4e86\u4e2a\u673a\u4f1a\uff0c\u4ed6\u624d\u7b97\u8131\u4e86\u8eab\uff0c\u53c1\u811a\u4e24\u6b65\u5c31\u51b2\u51fa\u4e86\u95e8\u3002 \u3000\u3000\u6881\u5988\u5c06\u95e8\u7f13\u7f13\u63a9\u4e0a\uff0c\u4e00\u56de\u5230\u5c4b\u91cc\u5c31\u683c\u683c\u7684\u7b11\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u8fd9\u4e0b\u4e88\u695a\u7559\u9999\u603b\u7b97\u683d\u4e86\u4e2a\u5927 \u8ddf\u5934\u3002\u201d \u3000\u3000\u90a3\u5973\u7684\u4e00\u9762\u6570\u94f6\u5b50\uff0c\u9762\u7b11\u9053\uff1a\u201c\u4e00\u4e24\u4e00\u94b1\u7684\u94f6\u5b50\uff0c\u4e00\u5171\u6709\u5341\u53c1\u4e2a\uff0c\u60f3\u4e0d\u5230\u8fd9\u4f4d\u76d7\u5e05\u7684\u4e5f \u4f1a\u6709\u5077\u9e21\u4e0d\u7740\u5012\u8680\u628a\u7c73\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u6881\u5988\u5374\u5df2\u722c\u4e0a\u684c\u5b50\uff0c\u6572\u4e86\u6572\u5c4b\u9876\uff0c\u9053\uff1a\u201c\u5c11\u7237\u5c0f\u59d0\u4e0b\u6765\u5427\uff0c\u4ed6\u5df2\u8d70\u8fc7\u4e86\u534a\u664c\uff0c\u5c4b\u9876\u4e0a\u7684 \u6728\u677f\u5c31\u5ffd\u7136\u88ab\u6380\u8d77\uff0c\u4e24\u4e2a\u4eba\u4e00\u5148\u4e00\u5f8c\u8df3\u4e86\u4e0b\u6765\uff0c\u5f88\u6f02\u4eae\uff0c\u4e5f\u5f88\u79c0\u6c14\u3002\u4e00\u770b\u5c31\u77e5\u9053\u662f\u4f4d\u517b\u5c0a\u5904 \u4f18\u7684\u5c0f\u59d0\uff0c\u7537\u7684\u5374\u66f4\u6f02\u4eae\uff0c\u66f4\u79c0\u6c14\uff0c\u7b80\u76f4\u6bd4\u5973\u4eba\u8fd8\u8981\u50cf\u5973\u4eba\u3002 \u3000\u3000\u4ed6\u7b11\u7684\u4e5f\u5f88\u6e29\u67d4\uff0c\u8df3\u4e0b\u6765\u5c31\u7b11\u9053\uff1a\u201c\u4eca\u5929\u53ef\u771f\u591a\u8c22\u6881\u5988\u4e86\uff0c\u54b1\u4eec\u771f\u4e0d\u77e5\u8be5\u600e\u9ebd\u6837\u8c22\u4f60\u8001 \u4eba\u5bb6\u3002\u201d \u3000\u3000\u4ed6\u4e00\u53e3\u4eac\u7247\u5b50\u53c8\u751c\u53c8\u8106\uff0c\u5c31\u597d\u50cf\u9ec4\u83ba\u513f\u5531\u6b4c\u4e00\u6837\u3002 \u3000\u3000\u6881\u5988\u7b11\u5f97\u8fde\u773c\u775b\u90fd\u77a7\u4e0d\u89c1\u4e86\uff0c\u9053\uff1a\u201c\u53ea\u8981\u5c11\u7237\u4ee5\u5f8c\u597d\u597d\u5bf9\u6211\u4eec\u5c0f\u59d0\uff0c\u6211\u8001\u5a46\u5b50\u5c31\u6bd4\u4ec0\u9ebd \u90fd\u53d7\u7528\u4e86\u3002\u201d \u3000\u3000\u8fd9\u5c11\u5e74\u6e29\u67d4\u7684\u77a7\u4e86\u8eab\u65c1\u7684\u5c11\u5987\u4e00\u773c\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u4f60\u8001\u4eba\u5bb6\u5c31\u7b97\u8981\u6211\u5bf9\u5979\u574f\u4e9b\uff0c\u6211\u4e5f\u662f\u6ca1 \u6cd5\u5b50\u505a\u5230\u3002\u201d \u3000\u3000\u5c11\u5987\u7ea2\u7740\u8138\uff0c\u7b11\u5676\u9053\uff1a\u201c\u4f60\u770b\u4ed6\u8fd9\u5f20\u5634\u6709\u591a\u751c\u3002\u201d \u3000\u3000\u90a3\u61a8\u5934\u61a8\u8111\u7684\u50bb\u5c0f\u5b50\u5ffd\u4e5f\u7b11\u9053\uff1a\u201c\u5c11\u7237\u7684\u5634\u82e5\u4e0d\u751c\uff0c\u53ea\u6015\u5c0f\u59d0\u4e5f\u5c31\u4e0d\u4f1a\u975e\u5ac1\u4ed6\u4e0d\u53ef \u4e86\u3002\u201d \u3000\u3000\u6881\u5988\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u81ea\u5df1\u4e5f\u4e0d\u7981\u7b11\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5c11\u5e74\u4e7e\u54b3\u4e86\u4e24\u58f0\u9053\uff1a\u201c\u8fd9\u4e00\u6b21\u96be\u5173\u867d\u7136\u6e21\u8fc7\u3002\u4f46\u8fd9\u91cc\u5374\u5df2\u975e\u4e45\u7559\u4e4b\u5730\u3002\u201d \u3000\u3000\u5c11\u5987\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u90a3\u4f4d\u76d7\u5e05\u695a\u7559\u9999\u679c\u7136\u975e\u540c\u5c0f\u53ef\uff0c\u96be\u602a\u6c5f\u6e56\u4e2d\u4eba\u90fd\u8bf4\u4ec0\u9ebd\u4e8b\u4e5f\u4f11\u60f3\u7792\u5f97 \u8fc7\u4ed6\u3002\u201d \u3000\u3000\u5ffd\u542c\u4e00\u4eba\u7b11\u9053\uff1a\u201c\u591a\u8c22\u59d1\u5a18\u7684\u5938\u5956\uff0c\u5728\u4e0b\u6709\u4e9b\u4e0d\u6562\u5f53\u2026\u2026\u201d \u3000\u3000\u5c4b\u5b50\u91cc\u7684\u4eba\u8138\u8272\u5168\u90fd\u53d8\u4e86\u3002 \u3000\u3000\u6881\u5988\u58f0\u9053\uff1a\u201c\u4ec0\u2026\u4ec0\u9ebd\u4eba\uff1f\u201d \u3000\u3000\u5176\u5b9e\u5979\u6839\u672c\u7528\u4e0d\u7740\u95ee\uff0c\u4e5f\u77e5\u9053\u6765\u7684\u4eba\u662f\u8c01\uff0c\u53ea\u89c1\u95e8\u53c8\u88ab\u63a8\u5f00\uff0c\u4e00\u4e2a\u4eba\u7b11\u5475\u5475\u7ad9\u5728\u95e8\u53e3\uff0c \u5374\u4e0d\u662f\u695a\u7559\u9999\u662f\u8c01\uff01\u90a3\u5c11\u5e74\u4e00\u8dfa\u811a\uff0c\u8eab\u5b50\u5df2\u51cc\u7a7a\u7ffb\u8d77\uff0c\u8fde\u73af\u8e22\u5411\u695a\u7559\u9999\u7684\u80f8\u819b\uff0c\u7528\u7684\u5c45\u7136\u662f \u6b63\u5b97\u5317\u6d3e\u6f6d\u817f\u7684\u529f\u592b\u3002 \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5357\u62f3\u5317\u817f\uff0c\u5317\u65b9\u6b66\u4eba\uff0c\u817f\u4e0a\u7684\u529f\u592b\u591a\u4e0d\u5f31\uff0c\u4f46\u80fd\u5c06\u817f\u51cc\u7a7a\u8fde\u73af\u8e22\u51fa\u7684\u5374\u4e5f \u4e0d\u591a\u3002\u201d \u3000\u3000\u53ea\u56e0\u817f\u4e0a\u529f\u592b\u8bb2\u7a76\u7684\u662f\u4e0b\u76d8\u7a33\u56fa\uff0c\u6c89\u7a33\u6709\u9980\uff0c\u8f7b\u7075\u4fbf\u4e0d\u8db3\uff0c\u662f\u4ee5\u817f\u6cd5\u4e2d\u6700\u96be\u7ec3\u7684\u5c31\u662f\u8fd9 \u79cd\u9e33\u9e2f\u817f\u3002 \u3000\u3000\u77a7\u8fd9\u5c11\u5e74\u7684\u529f\u592b\uff0c\u663e\u7136\u5df2\u662f\u5317\u6d3e\u6b66\u6797\u4e2d\u7684\u5065\u8005\u3002\u53ea\u53ef\u60dc\u4ed6\u9047\u89c1\u7684\u662f\u695a\u7559\u9999\u3002 \u3000\u3000\u4ed6\u4e24\u6761\u817f\u65b9\u8e22\u51fa\uff0c\u5c31\u89c9\u5f97\u8180\u4e0a\u201c\u7eed\u9f3b\u201d\u7a74\u4e00\u9ebb\uff0c\u8eab\u5b50\u5df2\u76f4\u843d\u4e0b\u53bb\uff0c\u7adf\u672a\u770b\u6e05\u695a\u695a\u7559\u9999\u662f \u5982\u4f55\u51fa\u624b\u7684\u3002 \u3000\u3000\u90a3\u5c11\u5987\u4e00\u4e2a\u7bad\u6b65\uff0c\u6251\u4e0a\u53bb\u63a5\u4f4f\u4e86\u4ed6\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u4ed6\u2026\u2026\u4ed6\u4f24\u4e86\u4f60\u9ebd\uff1f\u201d \u3000\u3000\u5c11\u5e74\u54ac\u7740\u7259\uff0c\u6447\u4e86\u6447\u5934\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4ed6\u65e2\u6765\u4e86\uff0c\u5c31\u7edd\u4e0d\u80fd\u653e\u4ed6\u8d70\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u627e\u4e24\u4f4d\u5df2\u627e\u4e86\u5f88\u4e45\uff0c\u4e24\u4f4d\u5c31\u662f\u8981\u6211\u8d70\uff0c\u6211\u4e5f\u4e0d\u4f1a\u8d70\u7684\u3002\u201d \u3000\u3000\u90a3\u5c11\u5987\u9053\uff1a\u201c\u6211\u4eec\u6839\u672c\u4e0d\u8ba4\u5f97\u4f60\uff0c\u4f60\u627e\u6211\u4eec\u5e72\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u4e24\u4f4d\u867d\u4e0d\u8ba4\u5f97\u6211\uff0c\u6211\u5374\u65e9\u5df2\u4e45\u4ef0\u4e24\u4f4d\u7684\u5927\u540d\uff0c\u5c24\u5176\u662f\u8fd9\u4f4d\u53f6\u76f8\u516c\uff0c\u4eac\u57ce \u7684\u738b\u5b59\u516c\u5b50\u8c01\u4e0d\u77e5\u9053\u53f6\u76db\u5170\u53f6\u76f8\u516c\u6587\u6b66\u5168\u624d\uff0c\u8272\u827a\u53cc\u7edd\u3002\u201d \u3000\u3000\u4ed6\u5728\u201c\u6587\u6b66\u5168\u624d\u201d\u4e0b\u9762\u5c45\u7136\u7528\u4e0a\u201c\u8272\u827a\u53cc\u7edd\u201d\u56db\u5b57\uff0c\u9762\u4e14\u8fd8\u662f\u7528\u5728\u7537\u4eba\u8eab\u4e0a\uff0c\u5f53\u771f\u662f\u3002 \u3000\u3000\u5c11\u5e74\u7684\u8138\u7acb\u523b\u7ea2\u4e86\u3002 \u3000\u3000\u90a3\u5c11\u5987\u5374\u51b7\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u662f\u4e2a\u5531\u82b1\u65e6\u7684\uff0c\u4f46\u5531\u82b1\u65e6\u7684\u4e5f\u662f\u4eba\u3002\u4f55\u51b5\u5531\u82b1\u65e6\u81f3\u5c11\u603b\u6bd4 \u505a\u5f3a\u76d7\u8981\u597d\u4e9b\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u82e5\u662f\u60c5\u6709\u72ec\u949f\uff0c\u7684\u786e\u8c01\u4e5f\u4e0d\u80fd\u5e72\u6d89\uff0c\u59d1\u5a18\u4f60\u597d\u597d\u7684\u4eba\u4e0d \u505a\uff0c\u4e3a\u4f55\u8981\u505a\u9b3c\u5462\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5987\u9762\u8272\u53d8\u4e86\u53d8\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u4ec0\u9ebd\uff1f\u6211\u4e0d\u61c2\u201d \u3000\u3000\u695a\u7559\u9999\u6de1\u6de1\u9053\uff1a\u201c\u4e8b\u5df2\u81f3\u6b64\uff0c\u65bd\u59d1\u5a18\u53ea\u6015\u4e0d\u61c2\u4e5f\u5f97\u61c2\u4e86\u3002\u201d \u3000\u3000\u90a3\u5c11\u5987\u8eab\u5b50\u4e00\u9707\uff0c\u4e0d\u7531\u81ea\u4e3b\u5f8c\u9000\u4e86\u4e24\u6b65\uff0c\u53d8\u8272\u9053\uff1a\u201c\u65bd\u59a8\u5a18\uff1f\u8c01\u662f\u65bd\u59d1\u5a18\uff1f\u6211\u4e0d\u8ba4\u5f97 \u5979\u3002\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u65bd\u59cb\u5a18\u5c31\u662f\u65bd\u4e3e\u4eba\u7684\u5973\u4eba\uff0c\u59d3\u65bd\u540d\u8335\uff0c\u5979\u7231\u4e0a\u4e00\u4f4d\u59d3\u53f6\u540d\u76db\u5170\u7684\u5c11\u5e74\u4eba\uff0c \u53ea\u53ef\u60dc\u65bd\u4e3e\u4eba\u592b\u5987\u5374\u4e0d\u61c2\u5973\u513f\u5fc3\u4e8b\uff0c\u5b9a\u8981\u5c06\u5979\u8bb8\u7ed9\u859b\u5bb6\u5e84\u7684\u4e8c\u516c\u5b50\uff0c\u8fd9\u4f4d\u65bd\u59d1\u5a18\u60c5\u6839\u5df2\u6df1 \u79cd\uff0c\u53ea\u6709\u8bc8\u6b7b\u9003\u8bfa\uff0c\u4f46\u4eba\u6b7b\u4e86\u4e5f\u8981\u6709\u4e2a\u4f53\uff0c\u6240\u4ee5\u5979\u5c31\u7528\u4e00\u4f4d\u77f3\u67ab\u4e91\u77f3\u59d1\u5a18\u7684\u4f53\u4ee3\u66ff\u5979\u3002\u201d \u3000\u3000\u4ed6\u5fae\u5fae\u4e00\u7b11\uff0c\u60a0\u7136\u63a5\u7740\u9053\uff1a\u201c\u65bd\u59d1\u5a18\uff0c\u6211\u8bf4\u7684\u5df2\u7ecf\u591f\u660e\u767d\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u6881\u5988\u4e00\u76f4\u72e0\u72e0\u7684\u77aa\u7740\u4ed6\u6b64\u65f6\u5ffd\u7136\u5927\u58f0\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u8bf4\u7684\u5b8c\u5168\u4e0d\u9519\u3002\u5979\u5c31\u662f\u6211\u7684\u8335\u59d1 \u5a18\uff0c\u4f60\u60f3\u600e\u9ebd\u6837\uff1f\u201d \u3000\u3000\u65bd\u8335\u7d27\u7d27\u63e1\u4f4f\u53f6\u76db\u5170\u7684\u624b\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4f60\u82e5\u60f3\u8981\u6211\u56de\u53bb\uff0c\u9664\u975e\u6740\u4e86\u6211\u3002\u201d \u3000\u3000\u53f6\u76db\u5170\u9053\uff0c\u201c\u4f60\u6700\u597d\u5148\u6740\u4e86\u6211\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u901a\uff1a\u201c\u6211\u65e9\u5df2\u8bf4\u8fc7\uff0c\u4e00\u4e2a\u4eba\u7684\u60c5\u611f\u8c01\u4e5f\u4e0d\u80fd\u52c9\u5f3a\u2026\u3002\u201d \u3000\u3000\u65bd\u8335\u9053\uff1a\u201c\u90a3\u9ebd\u4f60\u4e3a\u4f55\u8981\u6765\u7ba1\u6211\u4eec\u7684\u95f2\u4e8b\uff1f\u201d \u3000\u3000\u6881\u5988\u9053\uff1a\u201c\u5979\u8fd8\u4e0d\u5230\u4e24\u5c81\u65f6\u5c31\u8ddf\u7740\u6211\uff0c\u6bd4\u6211\u7684\u4eb2\u751f\u5973\u513f\u8fd8\u4eb2\uff0c\u6211\u7edd\u4e0d\u80fd\u8ba9\u5979\u5ac1\u4e2a\u5979\u4e0d\u559c \u6b22\u7684\u4eba\uff0c\u75db\u82e6\u7ec8\u751f\uff0c\u65e0\u8bba\u8c01\u82e5\u4ee4\u5979\u75db\u82e6\u6211\u90fd\u7edd\u4e0d\u9976\u4ed6\u3002\u201d \u3000\u3000\u5979\u76ef\u7740\u695a\u7559\u9999\uff0c\u5389\u58f0\u9053\uff1a\u201c\u6240\u4ee5\u6211\u529d\u4f60\u6700\u597d\u83ab\u8981\u518d\u7ba1\u8fd9\u4ef6\u4e8b\uff0c\u5426\u5219\u3002\u3002\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6253\u65ad\u4e86\u5979\u7684\u8bdd\u3002\u5fae\u7b11\u7740\u9053\uff1a\u201c\u6211\u5e76\u6ca1\u6709\u8981\u5979\u56de\u53bb\uff0c\u66f4\u6ca1\u6709\u8981\u62c6\u6563\u4ed6\u4eec\u7684\u610f\u601d\u3002\u6211 \u8981\u627e\u5230\u5979\uff0c\u53ea\u4e0d\u8fc7\u4e3a\u4e86\u8981\u8bc1\u5b9e\u5979\u6ca1\u6709\u6b7b\u3002\u201d \u3000\u3000\u6881\u5988\u9053\uff1a\u201c\u4f60\u2014\u3002\u4f60\u6ca1\u6709\u522b\u7684\u610f\u601d\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u9664\u6b64\u4e4b\u5916\uff0c\u6211\u60f3\u8ba8\u4ed6\u4eec\u53c1\u676f\u559c\u9152\u5403\u3002\u201d \u3000\u3000\u6881\u5988\u6014\u4e86\u534a\u664c\u795e\u60c5\u4f3c\u4e4e\u6709\u4e9b\u6127\u759a\u3002\u51e0\u6b21\u60f3\u8bf4\u8bdd\uff0c\u90fd\u6ca1\u6709\u8bf4\u51fa\u53e3\uff0c\u4e5f\u4e0d\u77e5\u5979\u7a76\u7adf\u60f3\u8bf4\u4ec0 \u9ebd\u3002 \u3000\u3000\u8fd9\u65f6\u53f6\u76db\u5170\u548c\u65bd\u8335\u5df2\u53cc\u53cc\u62dc\u5012\uff0c\u7b49\u4ed6\u4eec\u59cb\u8d77\u5934\u6765\u65f6\u695a\u7559\u9999\u5df2\u4e0d\u89c1\uff0c\u53ea\u542c\u5230\u4ed6\u7684\u58f0\u97f3\u8fdc\u8fdc \u4f20\u6765\uff0c\u9053\uff1a\u201c\u660e\u591c\u53c1\u66f4\uff0c\u4f46\u671b\u5728\u6b64\u76f8\u89c1\u2026\u201d\u3002\u8bf4\u5230\u6700\u5f8c\u4e00\u4e2a\u5b57\u65f6\uff0c\u4eba\u5df2\u5230\u4e86\u5c0f\u5df7\u5c3d\u5934\u3002 \u3000\u3000\u6881\u5988\u8fd9\u624d\u5410\u51fa\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u65e9\u77e5\u695a\u9999\u5e05\u662f\u5982\u6b64\u901a\u60c5\u8fbe\u7406\u7684\u4eba\uff0c\u6211\u5c31\u4e0d\u5fc5\u5c06\u90a3\u4f4d\u77f3\u6cb9 \u5a18\u7559\u4e0b\u6765\u4f5c\u5a01\u80c1\u4ed6\u7684\u4eba\u8d28\u4e86\u3002\u201d \u3000\u3000\u53f6\u76db\u5170\u773c\u73e0\u5b50\u4e00\u8f6c\uff0c\u7b11\u9053\uff1a\u201c\u90fd\u5df2\u9519\u4e86\uff0c\u4e3a\u4f55\u4e0d\u5c06\u9519\u5c31\u9519\uff1f\u201d \u3000\u3000\u6881\u5988\u9053\uff1a\u201c\u600e\u9ebd\u6837\u5c06\u9519\u5c31\u9519\uff1f\u201d\u74dc \u3000\u3000\u53f6\u76db\u5170\u7b11\u9053\uff1a\u201c\u4f60\u8001\u4eba\u5bb6\u4e0d\u5982\u79d8\u5bc6\u5c06\u90a3\u77f3\u59d1\u5a18\u5e26\u5230\u8fd9\u91cc\u6765\uff0c\u7b49\u7740\u695a\u7559\u9999\u2014\u3002\u4ed6\u65e2\u7136\u6210\u5168 \u4e86\u6211\u4eec\uff0c\u6211\u4eec\u4e3a\u4f55\u4e0d\u4e5f\u6210\u5168\u4ed6\uff1f\u201d \u3000\u3000\u65bd\u8335\u5374\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4ed6\u6210\u5168\u4e86\u6211\u4eec\uff0c\u4f46\u671b\u4ed6\u4e5f\u80fd\u6210\u5168\u522b\u4eba\u624d\u597d\u3002\u201d"}),showOnHomepage:!0},{name:"\u7b2c\u4e5d\u7ae0\u3000\u60fa\u60fa\u76f8\u60dc",content:(0,s.jsx)(s.Fragment,{children:"\u695a\u7559\u9999\u73b0\u5728\u53ea\u5269\u4e0b\u4e00\u4e2a\u95ee\u9898\u3002 \u3000\u3000\u65bd\u8335\u65e2\u7136\u6ca1\u6709\u6b7b\uff0c\u90a3\u9ebd\u5de6\u660e\u73e0\u53c8\u600e\u80fd\u501f\u5979\u7684\u9b42\u800c\u590d\u6d3b\u5462\uff1f \u3000\u3000\u5de6\u660e\u73e0\u7684\u6b7b\u672c\u662f\u5343\u771f\u4e07\u786e\uff0c\u4e00\u70b9\u4e5f\u4e0d\u5047\u7684\u3002 \u3000\u3000\u5f20\u7b80\u658b\u4e00\u4ee3\u540d\u533b\uff0c\u81f3\u5c11\u603b\u8be5\u80fd\u5206\u5f97\u51fa\u4e00\u4e2a\u4eba\u7684\u751f\u6b7b\uff0c\u4ed6\u65e2\u5df2\u65ad\u5b9a\u5de6\u660e\u73e0\u6b7b\u4e86\uff0c\u5979\u5c31\u672c\u65e0 \u590d\u6d3b\u4e4b\u7406\u3002 \u3000\u3000\u8fd9\u95ee\u9898\u7684\u786e\u5f88\u96be\u89e3\u91ca\uff0c\u4f46\u695a\u7559\u9999\u5374\u5c45\u7136\u4e00\u70b9\u4e5f\u4e0d\u7740\u6025\uff0c\u770b\u6765\u7adf\u50cf\u662f\u65e9\u5df2\u80f8\u6709\u6210\u7af9\u4f3c\u7684\uff0c \u5c0f\u79c3\u5b50\u8981\u8bf7\u4ed6\u559d\u8c46\u8150\u8111\uff0c\u5403\u70e7\u997c\u6cb9\u6761\uff0c\u4ed6\u5c31\u53bb\u4e86\u3002 \u3000\u3000\u201c\u8bf7\u5ba2\u201d\u672c\u662f\u4ef6\u5f88\u6109\u5feb\u7684\u4e8b\uff0c\u80fd\u8bf7\u4eba\u7684\u5ba2\uff0c\u603b\u6bd4\u8981\u4eba\u8bf7\u6109\u5feb\u5f97\u591a\uff0c\u6700\u5999\u7684\u662f\uff0c\u8d8a\u7a77\u7684\u4eba \u53cd\u800c\u8d8a\u559c\u6b22\u8bf7\u5ba2\u3002 \u3000\u3000\u5c0f\u79c3\u5b50\u5f00\u5fc3\u6781\u4e86\uff0c\u7b80\u76f4\u6068\u4e0d\u5f97\u628a\u8fd9\u5c0f\u5e97\u7684\u70e7\u997c\u6cb9\u6761\u548c\u8c46\u8150\u8111\u5168\u642c\u51fa\u6765\uff0c\u4e0d\u505c\u7684\u529d\u695a\u7559\u9999 \u591a\u5403\u4e00\u4e9b\u3002 \u3000\u3000\u8fd9\u65f6\u5929\u8fd8\u6ca1\u6709\u4eae\uff0c\u4e1c\u65b9\u521a\u73b0\u51fa\u8c08\u6de1\u7684\u9c7c\u809a\u767d\u8272\u3002 \u3000\u3000\u695a\u7559\u9999\u559d\u5230\u7b2c\u4e8c\u7897\u8c46\u8150\u8111\u7684\u65f6\u5019\uff0c\u5c0f\u706b\u795e\u548c\u5c0f\u9ebb\u5b50\u4e5f\u627e\u6765\u4e86\uff0c\u4e24\u4eba\u7684\u8138\u8272\u90fd\u5f88\u7126\u6025\uff0c\u50cf \u662f\u5f88\u7d27\u5f20\u3002 \u3000\u3000\u5c0f\u9ebb\u5b50\u8fd8\u5728\u4e0d\u4f4f\u4e1c\u5f20\u897f\u671b\uff0c\u5c31\u50cf\u751f\u6015\u6709\u4eba\u8ddf\u8e2a\u4f3c\u7684\u3002 \u3000\u3000\u5c0f\u706b\u795e\u4e00\u5750\u4e0b\u6765\uff0c\u5c31\u538b\u4f4e\u58f0\u97f3\u9053\uff1a\u201c\u6628\u5929\u665a\u4e0a\u53c8\u51fa\u4e86\u4e24\u4ef6\u5927\u4e8b\u3002\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u54e6\u4ec0\u9ebd\u4e8b\uff1f\u201d \u3000\u3000\u5c0f\u706b\u795e\u9053\uff1a\u201c\u4e24\u4ef6\u4e8b\u90fd\u662f\u5728\u859b\u5bb6\u5e84\u91cc\u53d1\u751f\u7684\u2026\u2026\u201d \u3000\u3000\u5c0f\u9ebb\u5b50\u62a2\u7740\u9053\uff1a\u201c\u859b\u8863\u4eba\u85cf\u7684\u51e0\u53e3\u5b9d\u5251\uff0c\u7adf\u4f1a\u4e0d\u89c1\u4e86\u3002\u201d \u3000\u3000\u5c0f\u706b\u795e\u9053\uff1a\u201c\u859b\u5bb6\u5e84\u91cc\u8fde\u70e7\u996d\u7684\u53a8\u5b50\u90fd\u4f1a\u51e0\u624b\u5251\u6cd5\uff0c\u5b88\u9662\u7684\u5bb6\u4e01\u5305\u53ef\u8bf4\u65e0\u4e00\u4e0d\u662f\u9ad8\u624b\uff0c \u8fd9\u4eba\u7adf\u80fd\u51fa\u5165\u81ea\u5982\uff0c\u800c\u4e14\u8fd8\u5077\u8d70\u4e86\u859b\u8863\u4eba\u7684\u85cf\u5251\uff0c\u4e0d\u8bf4\u522b\u7684\uff0c\u53ea\u8bf4\u8fd9\u4efd\u8f7b\u529f\uff0c\u8fd9\u4efd\u80c6\u91cf\uff0c\u5c31 \u5df2\u7ecf\u975e\u540c\u5c0f\u53ef\u3002\u201d \u3000\u3000\u4ed6\u5634\u91cc\u8bf4\u7740\u8bdd\uff0c\u773c\u775b\u9aa8\u788c\u788c\u5728\u695a\u7559\u9999\u8138\u4e0a\u6253\u8f6c\u3002 \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\u3002\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6709\u8fd9\u79cd\u8f7b\u529f\u7684\u4eba\u5b9e\u5728\u4e0d\u591a\uff0c\u4f46\u8fd9\u4ef6\u4e8b\u6211\u65e9\u5df2\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u5c0f\u706b\u795e\u6014\u4e86\u6014\uff0c\u8fde\u547c\u5438\u90fd\u505c\u6b62\u4e86\u3002 \u3000\u3000\u5c0f\u9ebb\u5b50\u5c79\u5403\u9053\uff1a\u201c\u8fd9\u2026\u2026\u9999\u5e05\u4f60\u600e\u4f1a\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u60a0\u7136\u9053\uff1a\u201c\u7b2c\u4e00\u4e2a\u77e5\u9053\u5b9d\u5251\u5931\u7a83\u7684\u4eba\uff0c\u81ea\u7136\u662f\u90a3\u5077\u5251\u7684\u4eba\uff0c\u4ed6\u6545\u610f\u505c\u4f4f\u8bed\u58f0\uff0c\u53ea \u89c1\u5c0f\u706b\u795e\u548c\u5c0f\u9ebb\u5b50\u4e24\u4eba\u8138\u8272\u5374\u5df2\u53d1\u4e86\u767d\uff0c\u800c\u4e14\u6b63\u5077\u5077\u4f7f\u773c\u8272\u3002\u201d\u663e\u7136\u5df2\u8ba4\u5b9a\u4e86\u695a\u7559\u9999\u5c31\u662f\u5077 \u5251\u7684\u4eba\u3002 \u3000\u3000\u695a\u7559\u9999\u8fd9\u624d\u5fae\u7b11\u7740\u63a5\u9053\uff1a\u201c\u4f46\u6211\u77e5\u9053\u8fd9\u4ef6\u4e8b\uff0c\u5374\u662f\u859b\u8863\u4eba\u81ea\u5df1\u544a\u8bc9\u6211\u7684\u3002\u201d \u3000\u3000\u5c0f\u9ebb\u5b50\u677e\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u5c31\u96be\u602a\u9999\u5e05\u6bd4\u6211\u4eec\u77e5\u9053\u5f97\u8fd8\u65e9\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u7b2c\u4e8c\u4ef6\u4e8b\u5462\uff1f\u201d \u3000\u3000\u5c0f\u706b\u795e\u58f0\u97f3\u538b\u5f97\u66f4\u4f4e\uff0c\u9053\uff1a\u201c\u859b\u5bb6\u5e84\u6628\u5929\u665a\u4e0a\u5c45\u7136\u6765\u4e86\u523a\u5ba2\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u4e5f\u89c9\u5f97\u6709\u4e9b\u610f\u5916\uff0c\u76b1\u7709\u9053\uff1a\u201c\u523a\u5ba2\uff1f\u8981\u8c0b\u523a\u8c01\uff1f\u201d \u3000\u3000\u5c0f\u706b\u795e\u9053\uff1a\u201c\u859b\u8863\u4eba\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7f13\u7f13\u62ac\u8d77\u624b\uff0c\u4e0d\u77e5\u4e0d\u89c9\u53c8\u6478\u5728\u9f3b\u5b50\u4e0a\u4e86\u3002 \u3000\u3000\u5c0f\u706b\u795e\u9053\uff1a\u201c\u859b\u8863\u4eba\u53f7\u79f0\u5929\u4e0b\u7b2c\u4e00\u5251\u5ba2\uff0c\u5c45\u7136\u6709\u4eba\u6562\u53bb\u523a\u6740\u4ed6\uff0c\u8fd9\u4eba\u7684\u80c6\u5b50\uff0c\u5b9e\u5728\u6bd4\u8001 \u864e\u8fd8\u5927\u3002\u201d \u3000\u3000\u4ed6\u4e00\u9762\u8bf4\u8bdd\uff0c\u4e00\u9762\u4e0d\u4f4f\u7528\u773c\u89d2\u5077\u53bb\u770b\u695a\u7559\u9999\u3002 \u3000\u3000\u695a\u7559\u9999\u5fcd\u4e0d\u4f4f\u7b11\u9053\uff1a\u201c\u4f60\u65e2\u7136\u4ee5\u4e3a\u8fd9\u4eba\u5c31\u662f\u6211\uff0c\u4e3a\u4ec0\u9ebd\u4e0d\u8bf4\u51fa\u6765\u5462\uff1f\u201d \u3000\u3000\u5c0f\u706b\u795e\u8138\u7ea2\u4e86\uff0c\u5403\u5403\u7b11\u9053\uff1a\u201c\u542c\u859b\u5bb6\u5e84\u7684\u4eba\u8bf4\uff0c\u4ed6\u4eec\u56db\u4e94\u5341\u4e2a\u4eba\uff0c\u975e\u4f46\u6ca1\u6709\u6349\u4f4f\u8fd9\u523a \u5ba2\uff0c\u800c\u4e14\u8fde\u4ed6\u7684\u8eab\u6750\u9762\u8c8c\u90fd\u6ca1\u6709\u770b\u6e05\u695a\uff0c\u53ea\u95fb\u5230\u4e00\u9635\u6de1\u6de1\u7684\u9999\u6c14\uff0c\u6240\u4ee5\u6211\u60f3\u3002\u3002\u3002\u6211 \u60f3\u3002\u3002\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u60f3\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u5c0f\u706b\u795e\u6c55\u6c55\u7684\u7b11\u9053\uff1a\u201c\u9664\u4e86\u695a\u9999\u5e05\u4e4b\u5916\u3002\u6211\u5b9e\u5728\u60f3\u4e0d\u51fa\u7b2c\u4e8c\u4e2a\u4eba\u6709\u8fd9\u9ebd\u9ad8\u7684\u8f7b\u529f\uff0c\u8fd9\u9ebd \u5927\u7684\u80c6\u5b50\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u83ab\u8bf4\u4f60\u60f3\u4e0d\u51fa\uff0c\u8fde\u6211\u90fd\u60f3\u4e0d\u51fa\u6765\u3002\u201d \u3000\u3000\u5c0f\u9ebb\u5b50\u9053\uff1a\u201c\u73b0\u5b9e\u859b\u8863\u4eba\u5df2\u8ba4\u5b9a\u4e86\u8fd9\u4e24\u4ef6\u4e8b\u90fd\u662f\u9999\u5e05\u505a\u7684\uff0c\u6240\u4ee5\u4ece\u53c1\u66f4\u8d77\uff0c\u5df2\u6d3e\u51fa\u597d\u51e0 \u6279\u4eba\u5206\u5934\u6765\u627e\u9999\u5e05\uff0c\u53c8\u5728\u2018\u63b7\u676f\u5c71\u5e84\u2019\u90a3\u8fb9\u57cb\u4e0b\u4e86\u6697\u68a2\u3002\u201d \u3000\u3000\u5c0f\u706b\u795e\u9053\uff1a\u201c\u57ce\u91cc\u57ce\u5916\u603b\u5171\u53ea\u6709\u8fd9\u9ebd\u5927\u70b9\u5730\u65b9\uff0c\u9999\u5e05\u7740\u4e0d\u8d76\u7d27\u60f3\u4e2a\u597d\u6cd5\u5b50\u6015\u8fdf\u65e9\u4f1a\u88ab\u4ed6 \u4eec\u53d1\u73b0\u7684\u3002\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u5ffd\u7136\u5927\u58f0\u9053\uff1a\u201c\u60f3\u6cd5\u5b50\uff1f\u60f3\u4ec0\u9ebd\u6cd5\u5b50\uff1f\u96be\u9053\u8981\u9999\u5e05\u8eb2\u8d77\u6765\uff0c\u8981\u9999\u5e05\u9003\u8d70\u5417\uff1f\u201d \u3000\u3000\u5c0f\u706b\u795e\u8138\u4e00\u6c89\uff0c\u6b64\u9053\uff1a\u201c\u4f60\u5c11\u8bf4\u8bdd\u2026\u2026\u9999\u5e05\uff0c\u859b\u8863\u4eba\u867d\u6ca1\u6709\u771f\u7684\u6536\u8fc7\u5f92\u5f1f\uff0c\u4f46\u95e8\u4e0b\u5bb6\u4e01 \u5374\u90fd\u5f97\u8fc7\u4ed6\u7684\u4f20\u6388\uff0c\u5251\u6cd5\u90fd\u4e0d\u5f31\uff0c\u859b\u5bb6\u5e84\u4e0a\u4e0a\u4e0b\u4e0b\u52a0\u8d77\u6765\u4e00\u5171\u6709\u4e03\u516b\u5341\u628a\u5251\uff0c\u5c31\u8fde\u773c\u524d\u80dc\u6781 \u4e00\u65f6\u7684\u9ec4\u5c71\u6d3e\u90fd\u4e0d\u6562\u548c\u4ed6\u4eec\u786c\u62fc\uff0c\u9999\u5e05\u4f60\u53c8\u4f55\u82e6\u8ddf\u4ed6\u6597\u8fd9\u95f2\u6c14\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5fae\u7b11\u9053\uff1a\u201c\u591a\u8c22\u4f60\u7684\u597d\u610f\uff0c\u53ea\u53ef\u60dc\u4e8b\u5df2\u81f3\u6b64\uff0c\u6211\u5c31\u7b97\u60f3\u8dd1\uff0c\u4e5f\u8dd1\u4e0d\u4e86\u7684\u3002\u201d \u3000\u3000\u7a81\u542c\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u603b\u7b97\u8fd8\u806a\u660e\uff0c\u5230\u4e86\u8fd9\u65f6\uff0c\u4f60\u8fd8\u80fd\u8dd1\u5f97\u4e86\uff0c\u90a3\u624d\u662f\u602a\u4e8b\u3002\u201d \u3000\u3000\u5356\u8c46\u8150\u8111\u7684\u5730\u65b9\u662f\u4e2a\u5728\u8857\u89d2\u642d\u8d77\u7684\u7af9\u68da\u5b50\uff0c\u8fd9\u53e5\u8bdd\u8bf4\u5b8c\uff0c\u53ea\u542c\u201c\u54d7\u201d\u7684\u4e00\u58f0\uff0c\u7af9\u68da\u7684\u9876 \u7a81\u7136\u88ab\u6380\u8d77\u3002 \u3000\u3000\u5341\u9980\u4e2a\u52b2\u88c5\u6025\u670d\u9ed1\u8863\u4eba\u540c\u65f6\u8dc3\u4e86\u4e0b\u6765\uff0c\u6bcf\u4e2a\u4eba\u624b\u4e2d\u90fd\u63d0\u7740\u67c4\u9752\u94a2\u5251\uff0c\u8eab\u624b\u679c\u7136\u5168\u90fd\u662f\u4e0d \u5f31\u3002 \u3000\u3000\u5c0f\u706b\u795e\u7684\u8138\u8272\u7acb\u523b\u53d8\u4e86\uff0c\u53cd\u624b\u6284\u8d77\u5f20\u957f\u6273\u51f3\u629b\u4e86\u51fa\u53bb\uff0c\u677f\u51f3\u867d\u4e0d\u91cd\uff0c\u8fd9\u629b\u4e4b\u529b\u5374\u4e0d\u5c0f\u3002 \u3000\u3000\u8c01\u77e5\u4e3a\u9996\u90a3\u9ed1\u8863\u4eba\u8f7b\u8f7b\u7528\u5251\u5c16\u6311\uff0c\u5c31\u5c06\u8fd9\u5f20\u677f\u51f3\u6311\u4e86\u56de\u6765\uff0c\u6765\u52bf\u7adf\u6bd4\u53bb\u52bf\u66f4\u5f3a\uff0c\u51e0\u4e4e\u5c31 \u6454\u5728\u5c0f\u706b\u795e\u8eab\u4e0a\u3002 \u3000\u3000\u684c\u5b50\u4e0a\u88c5\u8c46\u8150\u8111\u7684\u7897\u5168\u90fd\u88ab\u6454\u5f97\u626c\u788e\u3002 \u3000\u3000\u90a3\u9ed1\u8863\u4eba\u6012\u559d\u9053\uff1a\u201c\u5c0f\u706b\u795e\uff0c\u6211\u4eec\u62ff\u4f60\u5f53\u670b\u53cb\uff0c\u5411\u4f60\u6253\u542c\u695a\u7559\u9999\u7684\u6d88\u606f\uff0c\u4f60\u4e0d\u8bf4\u4e5f\u5c31\u7f62 \u4e86\uff0c\u8c01\u77e5\u4f60\u7adf\u5403\u91cc\u722c\u5916\uff0c\u53cd\u5230\u59d3\u695a\u7684\u8fd9\u91cc\u51fa\u5356\u6211\u4eec\u3002\u201d\u6012\u559d\u58f0\u4e2d\uff0c\u5df2\u6709\u4e24\u53c1\u67c4\u5251\u5411\u5c0f\u706b\u795e\u523a \u51fa\u3002 \u3000\u3000\u695a\u7559\u9999\u7a81\u7136\u7ad9\u8d77\u8eab\u6765\u3002\u8fd9\u51e0\u4eba\u5403\u4e86\u4e00\u60ca\uff0c\u4e0d\u7531\u81ea\u4e3b\u9000\u4e86\u4e24\u6b65\uff0c\u8c01\u77e5\u695a\u7559\u9999\u53ea\u662f\u62cd\u4e86\u62cd\u5c0f \u79c3\u5b50\u7684\u80a9\u8180\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u8c46\u8150\u8111\u771f\u597d\uff0c\u6211\u8d70\u4e4b\u524d\u4e00\u5b9a\u8fd8\u8981\u6765\u5403\u4e00\u6b21\u3002\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u867d\u5df2\u5413\u5f97\u8138\u8272\u53d1\u81ea\uff0c\u5374\u8fd8\u662f\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4e0b\u6b21\u8fd8\u662f\u6211\u8bf7\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u4e0b\u6b21\u8be5\u8f6e\u5230\u6211\u4e86\u3002\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\uff0c\u4e0d\uff0c\u6211\u53ea\u8bf7\u5f97\u8d77\u8c46\u8150\u8111\uff0c\u4f60\u8981\u8bf7\uff0c\u5c31\u8bf7\u6211\u559d\u9152\u3002\u201d \u3000\u3000\u4ed6\u4eec\u642d\u6321\u7adf\u4f3c\u5168\u672a\u5c06\u8fd9\u4e9b\u9ed1\u8863\u5251\u624b\u77a7\u5728\u773c\u91cc\u3002 \u3000\u3000\u4e3a\u9996\u90a3\u9ed1\u8863\u4eba\u6012\u559d\u4e00\u58f0\uff0c\u95ea\u7535\u822c\u4e00\u5251\u523a\u51fa\u3002 \u3000\u3000\u5176\u9980\u7684\u4eba\u4e5f\u7acb\u523b\u6325\u5251\u62a2\u653b\uff0c\u8fd9\u4e9b\u4eba\u4e0d\u4f46\u5251\u6cd5\u5feb\uff0c\u51fa\u624b\u7684\u90e8\u4f4d\u914d\u5408\u5f97\u4e5f\u5f88\u5de7\u5999\u5c31\u4ee5\u8fd9\u51fa\u624b \u4e00\u5251\uff0c\u522b\u4eba\u5df2\u96be\u62db\u67b6\u3002 \u3000\u3000\u53ea\u542c\u201c\u545b\u201d\u4e00\u9635\u54cd\uff0c\u5251\u4e0e\u5251\u76f8\u51fb\uff0c\u5251\u5149\u5305\u56f4\u4e2d\u7684\u695a\u7559\u9999\u4e0d\u77e5\u7528\u4e86\u4e2a\u4ec0\u9ebd\u8eab\u6cd5\uff0c\u7adf\u5ffd\u7136\u4e0d \u89c1\u4e86\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u60ca\u9000\u5f8c\uff0c\u56de\u5251\u62a4\u8eab\u3002 \u3000\u3000\u53ea\u542c\u7af9\u68da\u4e0a\u4f20\u4e0b\u4e00\u9635\u7b11\u58f0\uff0c\u539f\u6765\u695a\u7559\u9999\u4e0d\u77e5\u4f55\u65f6\u5df2\u63a0\u4e0a\u7af9\u6805\uff0c\u6b63\u542b\u7b11\u77a7\u7740\u4ed6\u4eec\uff0c\u60a0\u7136 \u9053\uff1a\u201c\u4f60\u4eec\u8fd8\u4e0d\u662f\u6211\u7684\u5bf9\u624b\uff0c\u8fd8\u662f\u5e26\u6211\u53bb\u89c1\u859b\u5927\u5e84\u4e3b\u5427\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u7eb7\u7eb7\u547c\u559d\u7740\uff0c\u53c8\u60f3\u6251\u4e0a\u53bb\uff0c\u5374\u88ab\u4e3a\u9996\u7684\u4eba\u559d\u4f4f\uff0c\u8fd9\u4eba\u4e00\u53cc\u773c\u775b\u5251\u4e5f\u5f88\u6709\u5a01\u4eea\uff0c\u77aa \u7740\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u6562\u53bb\u89c1\u6211\u5bb6\u5e84\u4e3b\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u4e3a\u4f55\u4e0d\u6562\uff1f\u96be\u9053\u4ed6\u4f1a\u5403\u4eba\u9ebd\uff1f\u201d \u3000\u3000\u5929\u5df2\u4eae\u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\u60a0\u95f2\u5730\u8d70\u5728\u524d\u9762\uff0c\u6ee1\u8138\u5bb9\u5149\u7115\u53d1\uff0c\u795e\u60c5\u4e5f\u5f88\u6109\u5feb\uff0c\u770b\u4ed6\u7684\u6837\u5b50\uff0c\u8c01\u4e5f\u60f3\u4e0d\u5230\u4ed6\u4e00 \u591c\u6ca1\u6709\u7761\u89c9\uff0c\u66f4\u60f3\u4e0d\u5230\u8ddf\u5728\u4ed6\u8eab\u5f8c\u7684\u90a3\u4e9b\u4eba\u968f\u65f6\u90fd\u53ef\u80fd\u5728\u4ed6\u80cc\u5f8c\u523a\u4e2a\u5927\u7a9f\u7abf\u3002 \u3000\u3000\u82de\u5728\u4ed6\u8eab\u5f8c\u7684\u4eba\u5df2\u8d8a\u6765\u8d8a\u591a\u4e86\uff0c\u597d\u51e0\u8def\u7684\u4eba\u90fd\u5df2\u6c47\u96c6\u5728\u4e00\u5904\uff0c\u5927\u5bb6\u90fd\u5728\u7a83\u7a83\u79c1\u8bae\uff0c\u4e0d\u660e \u767d\u8fd9\u59d3\u695a\u7684\u80c6\u5b50\u4e3a\u4f55\u8fd9\u9ebd\u5927\uff0c\u5c45\u7136\u7adf\u6562\u8ddf\u7740\u4ed6\u4eec\u56de\u53bb\uff0c\u6709\u4e9b\u4eba\u5c31\u8ba4\u4e3a\u8fd9\u4eba\u5b9a\u548c\u4ed6\u4eec\u4e8c\u5e84\u4e3b\u4e00 \u6837\uff0c\u8111\u888b\u6709\u4e9b\u6bdb\u75c5\u3002 \u3000\u3000\u5c0f\u706b\u795e\u3001\u5c0f\u79c3\u5b50\u548c\u5c0f\u9ebb\u5b50\u53c1\u4e2a\u4eba\u4e5f\u5728\u5f8c\u9762\u8fdc\u8fdc\u7684\u8ddf\u7740\u3002\u770b\u5230\u695a\u7559\u9999\u7684\u60a0\u95f2\u4e4b\u6001\uff0c\u4ed6\u4eec\u4e5f \u731c\u4e0d\u51fa\u4ed6\u5728\u6253\u4ec0\u9ebd\u4e3b\u610f\uff0c\u624b\u5fc3\u5374\u4e0d\u7981\u634f\u628a\u51b7\u6c57\u3002\u859b\u5bb6\u5e84\u5df2\u65e0\u5f02\u4f3c\u9f99\u6f6d\u864e\u7a74\uff0c\u859b\u8863\u4eba\u7684\u5251\u66f4\u6bd4 \u9f99\u864e\u8fd8\u53ef\u6015\uff0c\u695a\u7559\u9999\u6b64\u756a\u4e00\u53bb\uff0c\u8fd8\u80fd\u6d3b\u7740\u8d70\u51fa\u6765\u9ebd\uff1f \u3000\u3000\u5c0f\u706b\u795e\u4e00\u9762\u8d70\uff0c\u4e00\u9762\u6253\u624b\u5f0f\uff0c\u65bc\u662f\u56db\u9762\u516b\u65b9\u7684\u53eb\u5316\u5b50\u4e5f\u5168\u90fd\u6c47\u96c6\u4e86\u8fc7\u6765\uff0c\u8ddf\u5728\u4ed6\u8eab\u8fb9\u7684 \u4e5f\u8d8a\u6765\u8d8a\u591a\u4e86\u3002 \u3000\u3000\u524d\u5934\u8d70\u7740\u4e2a\u5f88\u82f1\u4fca\uff0c\u53c8\u6f47\u6d12\u7684\u4eba\u3002\u5f8c\u9762\u8ddf\u7740\u7fa4\u51f6\u795e\u6076\u715e\u822c\u7684\u5251\u624b\uff0c\u518d\u5f8c\u9762\u8fd8\u6709\u7fa4\u53eb\u5316 \u4e86\u3002 \u3000\u3000\u8fd9\u4e2a\u884c\u5217\u5f53\u771f\u662f\u6d69\u6d69\u8361\u8361\uff0c\u597d\u770b\u6781\u4e86\uff0c\u5e78\u597d\u6b64\u65f6\u5929\u521a\u4eae\uff0c\u8def\u4e0a\u7684\u884c\u4eba\u8fd8\u4e0d\u591a\uff0c\u4e24\u65c1\u7684\u5e97 \u94fa\u4e5f\u8fd8\u6ca1\u6709\u5f00\u95e8\u3002 \u3000\u3000\u4ed6\u4eec\u5230\u4e86\u859b\u5bb6\u5e84\u65f6\u859b\u8863\u4eba\u5e76\u6ca1\u6709\u8fce\u51fa\u6765\uff0c\u5374\u642c\u4e86\u5f20\u5f88\u8212\u670d\u7684\u6905\u5b50\uff0c\u5750\u5728\u5f8c\u56ed\u7684\u6811\u836b\u4e0b\u95ed \u76ee\u517b\u795e\u3002 \u3000\u3000\u8fd9\u4f4d\u5929\u4e0b\u7b2c\u4e00\u5251\u5bb9\uff0c\u679c\u7136\u4e0d\u5080\u4e3a\u6c5f\u6e56\u4e2d\u7684\u5927\u884c\u5bb6\uff0c\u201c\u4ee5\u9038\u5f85\u52b3\u201d\u8fd9\u56db\u4e2a\u5b57\uff0c\u8c01\u4e5f\u6ca1\u6709\u4ed6 \u77e5\u9053\u5f97\u6e05\u695a\u3002 \u3000\u3000\u6709\u5173\u695a\u7559\u9999\u7684\u6545\u4e8b\u4ed6\u5df2\u542c\u5f97\u591a\u4e86\uff0c\u6c5f\u6e56\u4f20\u8bf4\u4e2d\uff0c\u7b80\u76f4\u5df2\u628a\u201c\u695a\u7559\u9999\u201d\u8bf4\u6210\u4e00\u4e2a\u795e\u8bdd\u822c\u7684 \u4eba\u7269\u3002 \u3000\u3000\u8fd9\u4e9b\u4f20\u8bf4\u4ed6\u867d\u7136\u4e0d\u592a\u76f8\u60c5\uff0c\u4f46\u201c\u5999\u50e7\u201d\u65e0\u82b1\uff0c\u77f3\u89c2\u97f3\uff0c\u751a\u81f3\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u90fd\u66fe\u8d25\u5728\u695a\u7559 \u9999\u624b\u4e0b\uff0c\u8fd9\u4e9b\u4e8b\u603b\u4e0d\u4f1a\u5047\uff0c\u65e0\u8bba\u695a\u7559\u9999\u662f\u4ec0\u9ebd\u6cd5\u5b50\u53d6\u80dc\uff0c\u4f46\u80dc\u5c31\u662f\u80dc\uff0c\u4e5f\u4e0d\u662f\u522b\u7684\u4e1c\u897f\u80fd\u4ee3 \u66ff\u7684\u3002 \u3000\u3000\u859b\u8863\u4eba\u5bf9\u695a\u7559\u9999\u4ece\u6765\u4e5f\u6ca1\u6709\u5b58\u8fc7\u4e1d\u6beb\u8f7b\u89c6\u4e4b\u5fc3\uff0c\u6b64\u523b\u5fc3\u91cc\u751a\u81f3\u6709\u4e9b\u5174\u594b\uff0c\u6709\u4e9b\u7d27\u5f20\u3002 \u3000\u3000\u8fd9\u79cd\u611f\u89c9\u4ed6\u5df2\u591a\u5e74\u672a\u6709\u4e86\uff0c\u6240\u4ee5\u4ed6\u73b0\u5728\u5b9a\u8981\u6c89\u5f97\u4f4f\u6c14\u3002\u76f4\u7b49\u695a\u6d41\u9999\u5df2\u5230\u4e86\u4ed6\u9762\u524d\uff0c\u4ed6\u624d \u5f20\u5f00\u773c\u6765\u3002 \u3000\u3000\u695a\u7559\u9999\u6b63\u77a7\u7740\u4ed6\u5fae\u7b11\u3002 \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u4f60\u6765\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u6765\u4e86\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u4f60\u7684\u4f24\u597d\u4e86\u9ebd\uff1f\u201d\u74dc \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6258\u798f\uff0c\u597d\u5f97\u591a\u4e86\u3002\u2019 \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u5f88\u597d\u3002\u201d \u3000\u3000\u4ed6\u518d\u4e5f\u4e0d\u591a\u95ee\u4e00\u53e5\u8bdd\uff0c\u4e0d\u591a\u8bf4\u4e00\u53e5\u8bdd\uff0c\u5c31\u7ad9\u4e86\u8d77\u6765\uff0c\u6325\u4e86\u6325\u624b\uff0c\u65c1\u8fb9\u5c31\u6709\u4eba\u63a5\u6765\u4e00\u67c4 \u5251\u3002 \u3000\u3000\u5251\u5f88\u957f\uff0c\u6bd4\u6c5f\u6e56\u901a\u7528\u7684\u4f3c\u4e4e\u8981\u957f\u53c1\u5bf8\u5230\u56db\u5bf8\uff0c\u5251\u5df2\u51fa\u9798\uff0c\u5e76\u6ca1\u6709\u5251\u7a57\uff0c\u4ed6\u7684\u5251\u65e2\u975e\u4e3a\u4e86 \u88c5\u9970\uff0c\u4e5f\u975e\u4e3a\u4e86\u597d\u770b\u3002 \u3000\u3000\u4ed6\u7684\u5251\u662f\u4e3a\u4e86\u6740\u4eba\u7684\u3002 \u3000\u3000\u94c1\u9752\u8272\u7684\u5251\uff0c\u5374\u53d1\u7740\u6de1\u6ecb\u7684\u9752\u5149\uff0c\u695a\u7559\u9999\u867d\u8fdc\u5728\u6570\u5c3a\u5916\uff0c\u5df2\u53ef\u611f\u89c9\u5230\u81ea\u5251\u4e0a\u53d1\u51fa\u7684\u9634\u68ee \u5bd2\u610f\u3002 \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u597d\u5251\uff0c\u8fd9\u624d\u662f\u771f\u6b63\u7684\u5229\u5668\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u5e76\u6ca1\u6709\u53d6\u5251\uff0c\u6de1\u6de1\u9053\uff1a\u201c\u4f60\u7528\u4ec0\u9ebd\u5175\u5203T\u201d \u3000\u3000\u695a\u7559\u9999\u6ca1\u6709\u56de\u7b54\u8fd9\u53e5\u8bdd\uff0c\u5374\u56db\u4e0b\u671b\u4e86\u4e00\u773c\u3002 \u3000\u3000\u52b2\u88c5\u4f69\u5203\u7684\u9ed1\u8863\u4eba\u5df2\u5c06\u5f8c\u56ed\u56f4\u4e86\u8d77\u6765\u3002 \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u4e0d\u89c9\u8fd9\u91cc\u592a\u6324\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u859b\u8863\u4eba\u51b7\u9053\uff1a\u201c\u859b\u67d0\u751f\u5e73\u4e0e\u4eba\u4ea4\u624b\uff0c\u4ece\u672a\u501f\u8fc7\u522b\u4eba\u4e00\u6307\u4e4b\u529b\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u4e5f\u77e5\u9053\u4ed6\u4eec\u7edd\u4e0d\u6562\u51fa\u624b\u7684\uff0c\u4f46\u4ed6\u4eec\u90fd\u662f\u4f60\u7684\u5c5e\u4e0b\uff0c\u6709\u4ed6\u4eec\u5728\u65c1\u8fb9\uff0c\u7eb5\u4e0d \u51fa\u624b\uff0c\u4e5f\u4ee4\u6211\u89c9\u5f97\u6709\u5a01\u80c1\u3002\u201d \u3000\u3000\u4ed6\u7b11\u4e86\u7b11\uff0c\u63a5\u7740\u8bf4\uff1a\u201c\u6211\u4e00\u591c\u672a\u7761\uff0c\u6b64\u523b\u4e0e\u4f60\u4ea4\u624b\uff0c\u5df2\u5931\u5929\u65f6\uff0c\u8fd9\u662f\u4f60\u7684\u82b1\u56ed\uff0c\u4f60\u5bf9\u6b64 \u95f4\u4e00\u6728\u4e00\u6811\u90fd\u719f\u6089\u7684\u5f88\uff0c\u6211\u5728\u8fd9\u91cc\u4e0e\u4f60\u4ea4\u624b\uff0c\u53c8\u5931\u4e86\u5730\u5229\uff0c\u82e5\u518d\u5931\u5374\u4e86\u4eba\u548c\uff0c\u8fd9\u4e00\u6218\u4f60\u5df2\u4e0d \u5fc5\u51fa\u624b\uff0c\u6211\u5df2\u662f\u5fc5\u8d25\u65e0\u7591\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u51b7\u51b7\u51dd\u6ce8\u7740\u4ed6\uff0c\u76ee\u5149\u867d\u51b7\u9177\uff0c\u4f46\u5374\u5df2\u7406\u51fa\u4e00\u4e1d\u656c\u91cd\u4e4b\u8272\uff0c\u8fd9\u662f\u5927\u884c\u5bb6\u5bf9\u53e6\u4e00\u5927\u884c \u5bb6\u7279\u6709\u7684\u656c\u610f\u3002 \u3000\u3000\u4e24\u4eba\u76ee\u5149\u76f8\u5bf9\uff0c\u5f7c\u6b64\u5fc3\u91cc\u90fd\u5df2\u6709\u4e86\u4e86\u89e3\u3002 \u3000\u3000\u859b\u8863\u4eba\u5ffd\u7136\u6325\u4e86\u6325\u624b\uff0c\u9053\uff1a\u201c\u9000\u4e0b\u53bb\uff0c\u6ca1\u6709\u6211\u7684\u5429\u5490\uff0c\u8c01\u4e5f\u4e0d\u8bb8\u8fdb\u5165\u6b64\u5730\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u591a\u8c22\u3002\u201d \u3000\u3000\u4ed6\u9762\u8272\u5df2\u51dd\u91cd\uff0c\u8fd9\u201c\u591a\u8c22\u201d\u4e24\u4e2a\u5b57\u4e2d\u7edd\u65e0\u4e1d\u6beb\u63a2\u523a\u4e4b\u610f\uff0c\u4ed6\u4e00\u751f\u4e2d\u867d\u8bf4\u8fc7\u8bb8\u591a\u6b21\u201c\u591a \u8c22\u201d\uff0c\u4f46\u5374\u4ece\u6ca1\u6709\u8fd9\u4e00\u6b21\u8bf4\u5f97\u5982\u6b64\u614e\u91cd\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u859b\u8863\u4eba\u4ee4\u5c5e\u4e0b\u9000\u5f8c\uff0c\u4e5f\u662f\u5bf9\u4ed6\u8868\u793a\u7684\u4e00 \u79cd\u656c\u610f\u3002 \u3000\u3000\u8fd9\u4e00\u6218\u7eb5\u7136\u7acb\u5206\u751f\u6b7b\u3002\u8fd9\u4efd\u656c\u610f\u4e5f\u540c\u6837\u503c\u5f97\u611f\u6fc0\u3002 \u3000\u3000\u81ea\u654c\u4eba\u5904\u5f97\u5230\u7684\u656c\u610f\u6c38\u8fdc\u6bd4\u81ea\u670b\u53cb\u5904\u66f4\u96be\u80fd\u53ef\u8d35\uff0c\u4e5f\u66f4\u4ee4\u4eba\u611f\u52a8\u3002 \u3000\u3000\u859b\u8863\u4eba\u62ff\u8d77\u4e86\u5251\u3002 \u3000\u3000\u4ed6\u5bf9\u8fd9\u67c4\u5251\u51dd\u6ce8\u4e86\u5f88\u4e45\u3002\u4e00\u62ac\u8d77\u5934\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u53d6\u4f60\u7684\u5175\u5203\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7f13\u7f13\u9053\uff1a\u201c\u4e00\u4e2a\u6708\u524d\uff0c\u6211\u66fe\u5728\u864e\u4e18\u5251\u6c60\u65c1\u4e5f\u5e05\u4e00\u5e06\u5e05\u8001\u524d\u8f88\u4ea4\u624b\uff0c\u90a3\u6b21\u6211\u7528\u7684\u5175 \u5203\u53ea\u662f\u4e00\u6839\u67d4\u679d\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u51b7\u51b7\u7684\u671b\u7740\u4ed6\u7b49\u7740\u4ed6\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u90a3\u65f6\u6211\u5df2\u5bf9\u5e05\u8001\u524d\u8f88\u8bf4\u8fc7\u9ad8\u624b\u76f8\u4e89\uff0c\u53d6\u80dc\u4e4b\u9053\u5e76\u4e0d\u5728\u5229\u5668\u3002\u6211\u4ee5\u6811\u679d\u8fce \u6218\uff0c\u975e\u4f46\u6ca1\u6709\u5403\u4e8f\uff0c\u53cd\u5360\u4e86\u4fbf\u5b9c\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u76b1\u4e86\u76b1\u7709\uff0c\u4f3c\u4e5f\u4e0d\u61c2\u4ee5\u6811\u679d\u5bf9\u5229\u5251\u600e\u4f1a\u5360\u5f97\u5230\u4fbf\u5b9c\uff0c\u53ef\u662f\u4ed6\u5e76\u6ca1\u6709\u5c06\u5fc3\u91cc\u7684\u60f3\u6cd5 \u8bf4\u51fa\u6765\u3002 \u3000\u3000\u695a\u7559\u9999\u5df2\u63a5\u7740\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u4ee5\u67d4\u679d\u5bf9\u5229\u5251\uff0c\u5fc5\u5b9a\u4f1a\u4ee4\u5e05\u8001\u524d\u8f88\u7684\u5fc3\u7406\u53d7\u5230\u5f71\u54cd\u3002\u4ee5\u4ed6\u7684 \u8eab\u4efd\u7edd\u4e0d\u4f1a\u60f3\u5728\u5175\u5203\u4e0a\u5360\u6211\u7684\u4fbf\u5b9c\uff0c\u662f\u4ee5\u51fa\u624b\u4fbf\u6709\u987e\u5fcc\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u4e0d\u89c9\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e0d\u5360\u4fbf\u5b9c\uff0c\u5c31\u662f\u5403\u4e8f\u4e86\uff0c\u8b6c\u5982\u8bf4\uff0c\u6211\u82e5\u4ee5\u4e00\u62db\u2018\u51e4\u51f0\u5c55\u7fc5\u2019\u653b\u4ed6\u7684\u4e0a\u65b9\uff0c \u4ed6\u672c\u8be5\u7528\u4e00\u62db\u2018\u957f\u8679\u7ecf\u5929\u2019\u53cd\u64a9\u6211\u7684\u5175\u5203\uff0c\u53ef\u662f\u4ed6\u60f3\u5230\u6211\u7528\u7684\u5175\u5203\u53ea\u4e0d\u8fc7\u662f\u6839\u6811\u679d\uff0c\u5c31\u7edd\u4e0d \u4f1a\u518d\u7528\u8fd9\u62db\u4e86\uff0c\u6211\u4fbf\u5728\u4ed6\u53d8\u6362\u62db\u5f0f\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u62a2\u5f97\u5148\u673a\u3002\u201d \u3000\u3000\u4ed6\u5fae\u5fae\u7b11\uff0c\u63a5\u7740\u9053\uff1a\u201c\u9ad8\u624b\u76f8\u654c\uff0c\u6b63\u5982\u4e24\u56fd\u4ea4\u5175\uff0c\u5206\u5bf8\u4e4b\u5730\uff0c\u90fd\u5728\u6240\u5fc5\u4e89\uff0c\u82e5\u662f\u6709\u4e86\u987e \u5fcc\u4e4b\u5fc3\uff0c\u8fd9\u4e00\u6218\u4fbf\u96be\u514d\u8981\u5931\u5229\u4e86\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u76ee\u4e2d\u53c8\u9732\u51fa\u4e86\u8d5e\u8bb8\u4e4b\u8272\uff0c\u6de1\u7136\u9053\uff1a\u201c\u6211\u5e76\u4e0d\u662f\u5e05\u4e00\u5e06\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5e05\u4e00\u5e06\u7684\u5251\u6cd5\u5904\u5904\u4e0d\u79bb\u89c4\u77e9\uff0c\u9762\u524d\u8f88\u4f60\u7684\u5251\u6cd5\u90fd\u662f\u4ee5\u53d6\u80dc\u4e3a\u5148\uff0c\u8fd9 \u4e24\u8005\u4e4b\u95f4\u7684\u5dee\u522b\u6b63\u5982\u4e00\u4e2a\u4ee5\u620f\u66f2\u4e3a\u6d88\u9063\u7684\u7968\u53cb\u548c\u4e00\u4e2a\u4ee5\u620f\u66f2\u4e3a\u751f\u7684\u4f36\u4eba\uff0c\u4ed6\u4eec\u7684\u706b\u5019\u7eb5\u7136\u76f8 \u5dee\u65e0\u51e0\uff0c\u4f46\u529f\u67b6\u5374\u8fd8\u662f\u6709\u9ad8\u4f4e\u4e4b\u522b\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u4e0d\u89c9\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u5f88\u597d\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u6211\u4e5f\u4e0d\u51c6\u5907\u518d\u7528\u6811\u679d\u4e0e\u524d\u8f88\u4ea4\u624b\u3002\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u4f60\u51c6\u5907\u7528\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u51c6\u5907\u5c31\u7528\u8fd9\u4e00\u53cc\u624b\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u76b1\u7709\u9053\uff1a\u201c\u4f60\u7adf\u60f3\u4ee5\u8089\u638c\u6765\u8fce\u6218\u6211\u7684\u5229\u5251\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u524d\u8f88\u4e4b\u5251\uff0c\u950b\u5229\u65e0\u5339\uff1b\u524d\u8f88\u4e4b\u5251\u6cd5\uff0c\u66f4\u662f\u950b\u4e0d\u53ef\u5f53\uff0c\u5728\u4e0b\u65e0\u8bba\u7528\u4ec0\u9ebd\u5175\u5203 \u90fd\u7edd\u4e0d\u53ef\u80fd\u62b5\u6321\u3002\u4f55\u51b5\uff0c\u524d\u8f88\u51fa\u624b\u4e4b\u5feb\uff0c\u66f4\u662f\u5929\u4e0b\u65e0\u53cc\u3002\u6211\u5c31\u7b97\u80fd\u627e\u5230\u548c\u8fd9\u67c4\u5251\u540c\u6837\u7684\u5229 \u5668\u3002\u524d\u8f88\u4e00\u62db\u51fa\u624b\u6211\u8fd8\u662f\u6765\u4e0d\u53ca\u62db\u67b6\u7684\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u76ee\u4e2d\u5df2\u4e2a\u89c9\u9732\u51fa\u6b22\u559c\u5f97\u610f\u4e4b\u8272\u3002\u201c\u5343\u7a7f\u4e07\u7a7f\uff0c\u9a6c\u5c41\u4e0d\u7a7f\u201d\uff0c\u606d\u7ef4\u8bdd\u6bd5\u7adf\u662f\u4eba\u4eba\u7231 \u542c\u7684\u3002 \u3000\u3000\u4f55\u51b5\u8fd9\u4e9b\u8bdd\u53c8\u51fa\u81ea\u695a\u7559\u9999\u4e4b\u53e3\u3002 \u3000\u3000\u695a\u7559\u9999\u8bf4\u8bdd\u65f6\u4e00\u76f4\u5728\u7559\u610f\u7740\u4ed6\u9762\u4e0a\u7684\u795e\u8272\uff0c\u6162\u6162\u7684\u63a5\u7740\u9053\uff1a\u201c\u6240\u4ee5\u6211\u548c\u524d\u8f88\u4ea4\u624b\uff0c\u7edd\u4e0d \u60f3\u627e\u6321\u62db\u67b6\u8d2a\u529f\u6025\u8fdb\uff0c\u60f3\u4ee5\u5c0f\u5de7\u7684\u8eab\u6cd5\u95ea\u907f\uff0c\u624b\u4e0a\u6ca1\u6709\u5175\u5203\u8d1f\u62c5\u53cd\u9762\u8f7b\u4e9b\u8d1f\u62c5\u8d8a\u8f7b\u8eab\u6cd5\u8d8a \u5feb\u3002\u201d \u3000\u3000\u4ed6\u53c8\u7b11\u4e86\u7b11\uff0c\u63a5\u7740\u9053\uff1a\u201c\u4e0d\u7792\u524d\u8f88\u8bf4\uff0c\u6211\u82e5\u975e\u4e3a\u4e86\u4e0d\u6562\u5728\u524d\u8f88\u9762\u524d\u5931\u793c\uff0c\u672c\u60f3\u5c06\u8eab\u4e0a\u8fd9 \u51e0\u4ef6\u8863\u670d\u90fd\u8131\u4e0b\u6765\u7684\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u6c89\u9ed8\u4e86\u534a\u664c\u7f13\u7f13\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\u4f60\u5c82\u975e\u5df2\u81ea\u56f4\u4e8e\u2018\u4e0d\u80dc\u2019\u4e4b\u5730\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f46\u2018\u4e0d\u8d25\u2019\u4fbf\u5df2\u662f\u2018\u80dc\u2019\uff0c\u6211\u53ea\u671b\u80fd\u5728\u2018\u4e0d\u8d25\u2019\u4e2d\u518d\u6c42\u80dc\u4e4b\u9053\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u76ee\u5149\u95ea\u52a8\u9053\uff1a\u201c\u4f60\u6709\u628a\u63e1\u4e0d\u8d25\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u6de1\u8c08\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u548c\u6c34\u6bcd\u9634\u59ec\u4ea4\u624b\u65f6\uff0c\u53c8\u4f55\u5c1d\u6709\u4e1d\u6beb\u628a\u63e1\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u7eb5\u58f0\u800c\u7b11\uff0c\u7b11\u58f0\u53d1\u5373\u6b62\uff0c\u5389\u58f0\u9053\uff1a\u201c\u597d\uff0c\u4f60\u51c6\u5907\u7740\u95ea\u907f\u5427\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u65e9\u5df2\u5728\u51c6\u5907\u7740\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5f00\u59cb\u8bf4\u7b2c\u4e00\u53e5\u8bdd\u65f6\uff0c\u4fbf\u5df2\u8fdb\u5165\u4e86\u201c\u6218\u4e89\u72b6\u6001\u201d\uff0c\u4ed6\u8bf4\u7684\u6bcf\u4e00\u53e5\u8bdd\u90fd\u6709\u76ee\u7684\u7684\uff0c\u4ed6 \u8bf4\u8bdd\u4e5f\u662f\u4e00\u79cd\u6218\u7565\u3002 \u3000\u3000\u4ed6\u4e5f\u77e5\u9053\u859b\u8863\u4eba\u8fd9\u4e00\u5251\u51fa\u624b\uff0c\u5fc5\u5982\u96f7\u8f70\u7535\u51fb\uff0c\u9510\u4e0d\u53ef\u5f53\u3002 \u3000\u3000\u859b\u8863\u4eba\u7684\u5251\u5c1a\u672a\u51fa\u624b\uff0c\u4ed6\u7684\u8eab\u6cd5\u5df2\u5c55\u5f00\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\u5251\u5149\u5df2\u5982\u95ea\u7535\u822c\u4eae\u8d77\uff0c\u5239\u90a3\u4e4b\u95f4\u4fbf\u5df2\u5411\u695a\u7559\u9999\u7684\u80a9\u3001\u80f8\u3001\u8170\u3001\u817f\u51fa\u4e86\u516d\u5251\u3002 \u3000\u3000\u4ed6\u62db\u5f0f\u770b\u6765\u5e76\u6ca1\u6709\u4ec0\u9ebd\u5947\u7279\u4e4b\u5904\uff0c\u4f46\u5374\u5feb\u5f97\u4e0d\u53ef\u601d\u8bae\uff0c\u8fd9\u516d\u5251\u523a\u51fa\uff0c\u4e00\u67c4\u5251\u7adf\u50cf\u662f\u5316\u4e3a \u516d\u67c4\u5251\u3002 \u3000\u3000\u5e78\u597d\u695a\u7559\u9999\u8eab\u5f62\u5df2\u5148\u5c55\u52a8\u624d\u582a\u582a\u907f\u8fc7\u3002 \u3000\u3000\u4f46\u859b\u8863\u4eba\u7684\u5251\u6cd5\u5374\u5982\u957f\u6c5f\u5927\u6cb3\u4e4b\u6c34\uff0c\u4e00\u5343\u91cc\u3002\u516d\u62db\u523a\u8fc7\uff0c\u53c8\u662f\u516d\u62db\u8ddf\u7740\u523a\u51fa\u7edd\u4e0d\u7ed9\u4eba\u4e1d \u6beb\u5598\u6c14\u7684\u673a\u4f1a\u3002 \u3000\u3000\u53ea\u89c1\u5251\u5149\u7ef5\u5bc6\u5b9b\u5982\u4e00\u7247\u5149\u843d\uff0c\u7edd\u5bf9\u770b\u4e0d\u89c1\u6709\u4e1d\u6beb\u7a7a\u9699\u3002\u53c8\u6b63\u5982\u6c34\u94f6\u4e4b\u6cfb\u5730\uff0c\u65e0\u5b54\u4e0d\u5165\u3002 \u3000\u3000\u695a\u7559\u9999\u7684\u8f7b\u529f\u8eab\u6cd5\u867d\u5999\u7edd\u5929\u4e0b\uff0c\u4f46\u859b\u8863\u4eba\u516d\u4e5d\u4e94\u5341\u56db\u5251\u95ea\u8fc7\uff0c\u4ed6\u5df2\u6709\u4e94\u6b21\u9047\u7740\u9669\u62db\u3002 \u3000\u3000\u6bcf\u4e00\u6b21\u5251\u950b\u90fd\u4ec5\u53ea\u582a\u582a\u6444\u8eab\u800c\u8fc7\uff0c\u4ed6\u5df2\u80fd\u611f\u89c9\u51fa\u5251\u950b\u51b7\u82e5\u51b0\u971c\uff0c\u82e5\u662f\u518d\u6162\u4e00\u6b65\u4fbf\u4e0d\u582a\u8bbe \u60f3\u3002 \u3000\u3000\u4f46\u4ed6\u7684\u773c\u775b\u5374\u8fde\u8d2c\u90fd\u6ca1\u6709\u7728\uff0c\u59cb\u7ec8\u8ddf\u968f\u7740\u859b\u8863\u4eba\u638c\u4e2d\u7684\u5251\u950b\uff0c\u4f3c\u4e4e\u4e00\u5fc3\u60f3\u7740\u859b\u8863\u4eba\u51fa\u62db \u5f0f\u7684\u53d8\u5316\uff0c\u51fa\u624b\u7684\u65b9\u6cd5\u3002 \u3000\u3000\u859b\u8863\u4eba\u7b2c\u4e5d\u5341\u516d\u624b\u5251\u523a\u51fa\u65f6\u695a\u7559\u9999\u5ffd\u7136\u8f7b\u5578\u4e00\u58f0\uff0c\u51b2\u5929\u800c\u8d77\uff0c\u859b\u8863\u4eba\u4e0b\u4e00\u5251\u523a\u51fa\u65f6\u4ed6\u5df2 \u63a0\u51fa\u4e86\u53c1\u4e08\u5f00\u5916\u3002 \u3000\u3000\u7b49\u5230\u859b\u8863\u4eba\u7b2c\u4e00\u767e\u96f6\u53c1\u624b\u5251\u523a\u51fa\u65f6\uff0c\u4ed6\u5df2\u63a0\u4e0a\u4e86\u5c0f\u6865\uff0c\u811a\u6b65\u70b9\u5730\uff0c\u53c8\u81ea\u5c0f\u6865\u63a0\u4e0a\u4e86\u5047 \u5c71\u3002 \u3000\u3000\u5e78\u597d\u8fd9\u4e00\u7247\u56ed\u6797\u5360\u5730\u5f88\u5e7f\uff0c\u695a\u7559\u9999\u7684\u8eab\u6cd5\u4e00\u5c55\u5f00\uff0c\u5c31\u5a49\u5982\u98de\u4e4c\u822c\u98de\u8dc3\u4e0d\u505c\uff0c\u81ea\u5047\u5c71\u81f3\u5c0f \u4ead\uff0c\u81ea\u5c0f\u4ead\u81f3\u6811\u68a2\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u4eba\u5df2\u8138\u4e0d\u89c1\u4e86\uff0c\u53ea\u80fd\u9690\u89c1\u4e00\u6761\u7070\u5f71\u5728\u524d\u9762\u514d\u8d77\u72d0\u843d\u3002\u4e00\u9053\u95ea\u4eae\u7684\u98de\u8dd1\u5728\u5f8c\u9762\u5982\u5f71 \u968f\u5f62\u7684\u8ddf\u7740\uff0c\u53ea\u542c\u5f97\u201c\u9686\u9686\u201d\u4e4b\u58f0\u4e0d\u7edd\uff0c\u6ee1\u56ed\u843d\u53f6\u5982\u9526\u3002 \u3000\u3000\u859b\u8863\u4eba\u8fd9\u624d\u77e5\u9053\u695a\u7559\u9999\u8f7b\u529f\u4e4b\u9ad8\uff0c\u5b9e\u662f\u65e0\u4eba\u80fd\u53ca\u3002 \u3000\u3000\u4ed6\u81ea\u5df2\u672c\u4e5f\u4ee5\u201c\u5251\u6cd5\uff0c\u8f7b\u529f\u201d\u53cc\u7edd\u800c\u79f0\u9738\u6c5f\u6e56\u3002\u4f46\u6b64\u523b\u5374\u5df2\u89c9\u5f97\u56ed\u4e2d\u7684\u4ead\u53f0\u6811\u6728\u4eff\u4f5b\u90fd \u5df2\u5728\u98de\u4e2a\u4e0d\u505c\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u82e5\u662f\u9a70\u9a6c\u9762\u8fc7\u6797\u836b\u9053\uff0c\u4fbf\u4f1a\u611f\u89c9\u5230\u4e24\u65c1\u7684\u6811\u672c\u90fd\u5df2\u98de\u8d77\u3002\u2014\u6839\u6839\u5411\u4ed6\u8fce\u9762\u98de\u4e86\u8fc7 \u6765\u3002 \u3000\u3000\u859b\u8863\u4eba\u6b64\u523b\u7684\u8eab\u6cd5\u66f4\u5feb\u903e\u98de\u9e1f\uff0c\u81ea\u7136\u4e5f\u96be\u514d\u6709\u8fd9\u79cd\u611f\u89c9\uff0c\u53ea\u4e0d\u8fc7\u4ed6\u60f3\u695a\u7559\u9999\u4e5f\u662f\u4e2a\u4eba\u81ea \u7136\u4e5f\u4e0d\u4f1a\u4f8b\u5916\u3002 \u3000\u3000\u4ed6\u53ea\u76fc\u695a\u7559\u9999\u6709\u773c\u82b1\u7684\u65f6\u5019\u3002 \u3000\u3000\u695a\u7559\u9999\u8fd9\u79cd\u4ea4\u624b\u7684\u65b9\u6cd5\u672c\u975e\u6b63\u9053\uff0c\u4f46\u4ed6\u65e9\u5df2\u8bf4\u8fc7\uff0c\u201c\u4e0d\u8fce\u6218\uff0c\u53ea\u95ea\u907f\u201d\uff0c\u6240\u4ee5\u859b\u8863\u4eba\u73b0 \u5728\u4e5f\u4e0d\u80fd\u8d23\u5907\u4ed6\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u81ea\u4e24\u6a61\u6811\u4e4b\u95f4\u7a9c\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u8c01\u77e5\u4e24\u6811\u4e4b\u95f4\u8fd8\u6709\u682a\u6811\uff0c\u53c1\u682a\u6811\u6210\u53c1\u89d2\u6392\u5217\uff0c\u524d\u9762\u4e24\u682a\u6811\u7684\u6811\u836b\u5c06\u5f8c\u9762\u4e00\u682a\u63a9\u4f4f\u4e86\u3002 \u3000\u3000\u82e5\u5728\u5e73\u65f6\uff0c\u695a\u7559\u9999\u81ea\u7136\u8fd8\u662f\u80fd\u770b\u5f97\u89c1\u3002\u4f46\u6b64\u65f6\u4ed6\u8eab\u4e0d\u5b9e\u5728\u592a\u5feb\uff0c\u7b49\u4ed6\u53d1\u73b0\u5f8c\u9762\u8fd8\u6709\u4e00\u682a \u6811\u65f6\uff0c\u4eba\u5df2\u5411\u6811\u4e0a\u649e\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5230\u4e86\u8fd9\u65f6\uff0c\u4ed6\u6536\u52bf\u5df2\u6765\u4e0d\u53ca\u4e86\u3002 \u3000\u3000\u859b\u8863\u4eba\u559c\u51fa\u671b\u5916\uff0c\u4e00\u5251\u5df2\u523a\u51fa\u3002 \u3000\u3000\u695a\u7559\u9999\u8eab\u5b50\u8981\u662f\u649e\u4e0a\u6811\u5e72\u54ea\u91cc\u8fd8\u8eb2\u5f97\u5f00\u8fd9\u4e00\u5251\uff0c\u4f55\u51b5\u4ed6\u7eb5\u7136\u6536\u52bf\u5f8c\u9000\uff0c\u4e5f\u96be\u514d\u8981\u88ab\u5251\u950b \u523a\u7a7f\u3002 \u3000\u3000\u859b\u8863\u4eba\u77e5\u9053\u81ea\u5df2\u8fd9\u4e00\u5207\u5fc5\u5b9a\u518d\u4e5f\u4e0d\u4f1a\u5931\u624b\u3002 \u3000\u3000\u82e5\u662f\u5728\u6b63\u5e38\u60c5\u51b5\u4e4b\u4e0b\u4ea4\u624b\uff0c\u4ed6\u5fc3\u91cc\u4e5f\u8bb8\u8fd8\u4f1a\u6709\u601c\u624d\u4e4b\u610f\uff0c\u4e0b\u624b\u65f6\u4e5f\u8bb8\u8fd8\u4e0d\u4f1a\u592a\u4e0d\u7559\u60c5\u3002 \u3000\u3000\u53ef\u662f\u73b0\u5728\u6bcf\u4ef6\u4e8b\u90fd\u53d1\u751f\u5f97\u592a\u5feb\uff0c\u6839\u672c\u4e0d\u4f1a\u7ed9\u4ed6\u6709\u4e1d\u6beb\u601d\u7d22\u8003\u8651\u7684\u673a\u4f1a\uff0c\u4ed6\u7684\u5251\u5df2\u523a\u5269\u4e86 \u51fa\u53bb\u3002 \u3000\u3000\u4ed6\u7684\u5251\u4e00\u51fa\u624b\uff0c\u5c31\u8fde\u4ed6\u81ea\u5df1\u4e5f\u65e0\u6cd5\u633d\u56de\u3002 \u3000\u3000\u201c\u9686\u201d\u7684\uff0c\u5251\u5df2\u523a\u5165\u2026\u3002 \u3000\u3000\u4f7f\u523a\u5165\u7684\u7adf\u4e0d\u662f\u695a\u7559\u9999\u7684\u80cc\u810a\uff0c\u800c\u662f\u6811\u5e72\u3002 \u3000\u3000\u539f\u6765\u695a\u7559\u9999\u8fd9\u7740\u7adf\u662f\u8bf1\u654c\u4e4b\u8ba1\uff0c\u4ed6\u8eab\u6cd5\u53d8\u5316\u4e4b\u5feb\uff0c\u7b80\u76f4\u4e0d\u662f\u4efb\u4f55\u4eba\u6240\u80fd\u60f3\u50cf\u5f97\u51fa\u7684\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u5df2\u5feb\u649e\u4e0a\u6811\u5e72\u7684\u90a3\u4e00\u77ac\u95f4\uff0c\u4ed6\u8eab\u5b50\u7a81\u7136\u7f29\u8d77\uff0c\u7528\u53cc\u624b\u62b1\u7740\u679d\u5934\uff0c\u5c31\u5730\u4e00\u63a5\uff0c\u63a0\u51fa \u4e86\u4e24\u53c1\u4e08\u3002\u4ed6\u542c\u5230\u201c\u9648\u201d\u7684\u4e00\u58f0\uff0c\u5c31\u77e5\u9053\u5251\u5df2\u523a\u5165\u6811\u5e72\u3002 \u3000\u3000\u8fd9\u662f\u5f88\u575a\u5b9e\u7684\u6850\u6811\u3002\u5251\u8eab\u523a\u5165\u5f8c\uff0c\u7edd\u4e0d\u53ef\u80fd\u5e94\u624b\u5c31\u62d4\u51fa\u6765\uff0c\u90a3\u5fc5\u9700\u8981\u82b1\u4e9b\u529b\u6c14\u8d39\u4e9b\u65f6 \u95f4\u3002 \u3000\u3000\u695a\u7559\u9999\u82e5\u5728\u8fd9\u5239\u90a3\u95f4\u4eae\u51fa\u62f3\u811a\uff0c\u859b\u8863\u4eba\u5c31\u672a\u5fc5\u80fd\u95ea\u9003\u5f97\u5f00\uff0c\u81f3\u5c11\u5fc5\u5b9a\u6765\u4e0d\u53ca\u628a\u5251\u62d4\u51fa \u6765\u3002 \u3000\u3000\u859b\u8863\u4eba\u624b\u4e2d\u65e0\u5251\uff0c\u5c31\u6ca1\u6709\u5982\u6b64\u53ef\u6015\u4e86\u3002 \u3000\u3000\u4f46\u695a\u7559\u9999\u5e76\u6ca1\u6709\u8fd9\u9ebd\u6837\u505a\uff0c\u53ea\u662f\u8fdc\u8fdc\u7684\u7ad9\u5728\u4e00\u8fb9\uff0c\u9759\u9759\u7684\u77a7\u7740\u859b\u8863\u4eba\u4f3c\u4e4e\u8fd8\u5728\u7b49\u7740\u4ed6\u51fa \u624b\u3002 \u3000\u3000\u859b\u8863\u4eba\u65e2\u6ca1\u6709\u51fa\u624b\uff0c\u4e5f\u6ca1\u6709\u62d4\u5251\u3002 \u3000\u3000\u4ed6\u5374\u6ce8\u610f\u7740\u5d4c\u5728\u6811\u5e72\u4e2d\u7684\u5251\u6c89\u9ed8\u4e86\u5f88\u4e45\u5f88\u4e45\uff0c\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201d\u4f60\u679c\u7136\u6709\u4f60\u7684\u53d6\u80dc\u4e4b \u9053\uff0c\u679c\u7136\u6ca1\u6709\u8d25\u3002\u201d \u3000\u3000\u4ed6\u627f\u8ba4\u695a\u7559\u9999\u672a\u8d25\uff0c\u4fbf\u65e0\u5f02\u5df2\u627f\u8ba4\u695a\u7559\u9999\u80dc\u4e86\u3002 \u3000\u3000\u859b\u8863\u4eba\u53f7\u79f0\u201c\u5929\u4e0b\u7b2c\u4e00\u5251\u201d\u5e73\u751f\u672a\u9047\u654c\u4eba\uff0c\u6b64\u523b\u5374\u80fd\u5c06\u80dc\u8d1f\u4e4b\u4e8b\u4ee5\u4e00\u7b11\u7f6e\u4e4b\uff0c\u8fd9\u4efd\u80f8 \u895f\uff0c\u8fd9\u79cd\u6c14\u5ea6\u786e\u4e5f\u975e\u5e38\u4eba\u53ef\u53ca\u3002 \u3000\u3000\u695a\u7559\u9999\u5fc3\u91cc\u4e5f\u4e0d\u7981\u6697\u6697\u89c9\u5f97\u656c\u4f69\uff0c\u8083\u7136\u9053\uff1a\u201c\u5728\u4e0b\u867d\u672a\u8d25\uff0c\u524d\u8f88\u4e5f\u672a\u8d25\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u4f60\u82e5\u672a\u8d25\u4fbf\u53ef\u7b97\u662f\u80dc\uff0c\u6211\u82e5\u4e0d\u80dc\u5c31\u8be5\u7b97\u662f\u8d25\u4e86\u3002\u56e0\u4e3a\u6211\u4eec\u6240\u7528\u7684\u65b9\u6cd5\u4e0d \u540c\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5728\u4e0b\u4e07\u4e07\u4e0d\u6562\u8a00\u80dc\uff0c\u53ea\u56e0\u5728\u4e0b\u4e5f\u5360\u4e86\u524d\u8f88\u4e4b\u4fbf\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u53c8\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u5176\u5b9e\u6211\u4e5f\u77e5\u9053\uff0c\u6211\u6bd5\u7adf\u662f\u4e0a\u4e86\u4f60\u7684\u5f53\u3002\u201d\u74dc \u3000\u3000\u4ed6\u63a5\u7740\u9053\uff1a\u201c\u6211\u517b\u7cbe\u84c4\u9510\u5728\u8fd9\u91cc\u7b49\u7740\u4f60\uff0c\u90a3\u65f6\u6211\u65e0\u8bba\u7cbe\u795e\u4f53\u529b\u90fd\u6b63\u5728\u5f3a\u950b\u72b6\u51b5\u4e4b\u4e2d\uff0c\u6b63 \u5982\u5343\u77f3\u4e4b\u5f13\u5f15\u7586\u5f85\u53d1\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u662f\u4ee5\u5728\u4e0b\u90a3\u65f6\u4e07\u4e07\u4e0d\u6562\u548c\u524d\u8f88\u4ea4\u624b\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u4f60\u5148\u548c\u6211\u8bf4\u8bdd\uff0c\u5206\u6563\u6211\u7684\u795e\u5fd7\uff0c\u518d\u4ee5\u8a00\u8bcd\u4f7f\u6211\u5f97\u610f\u3002\u7b49\u5230\u6211\u5bf9\u4f60\u6709\u4e86\u597d\u611f \u65f6\uff0c\u6597\u5fd7\u4e5f\u5c31\u6e10\u6e10\u6d88\u5931\u3002\u201d \u3000\u3000\u4ed6\u6de1\u6de1\u7b11\u9053\uff1a\u201c\u4f60\u7528\u7684\u6b63\u662f\u5b59\u5b50\u5175\u6cd5\u4e0a\u7684\u5999\u7b56\uff0c\u672a\u4ea4\u6218\u4e4b\u524d\uff0c\u5148\u4ee4\u5bf9\u65b9\u7684\u58eb\u6c14\u4e00\u800c\u8870\uff0c \u518d\u800c\u7aed\uff0c\u7136\u5f8c\u518d\u4ee5\u8f7b\u529f\u6d88\u8017\u6211\u7684\u4f53\u529b\uff0c\u6700\u5f8c\u518d\u4f7f\u51fa\u8f7b\u5175\u8bf1\u654c\u4e4b\u8ba1\uff0c\u5251\u6cd5\u4e43\u4e00\u4eba\u654c\u3002\u4f60\u6240\u7528\u7684 \u5175\u6cd5\u6218\u7565\u5374\u4e3a\u4e07\u4eba\u654c\uff0c\u8fd9\u4e5f\u96be\u602a\u4f60\u6218\u65e0\u4e0d\u80dc\uff0c\u8fde\u77f3\u89c2\u97f3\u548c\u795e\u6c34\u5bab\u4e3b\u90fd\u4e0d\u662f\u4f60\u7684\u5bf9\u624b\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6478\u4e86\u6478\u9f3b\u5b50\u5782\u9996\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u5b9e\u662f\u60ed\u6127\u5f97\u5f88\u2026\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u9ad8\u624b\u5bf9\u654c\u6b63\u5982\u4e24\u56fd\u4ea4\u6218\uff0c\u80fd\u4ee5\u8ba1\u8d25\u6211\uff0c\u6211\u4e5f\u662f\u53e3\u670d\u5fc3\u670d\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u524d\u8f88\u4e4b\u80f8\u895f\u6c14\u5ea6\uff0c\u5728\u4e0b\u66f4\u662f\u4e94\u4f53\u6295\u5730\uff0c\u5728\u4e0b\u672c\u5c31\u6ca1\u6709\u548c\u524d\u8f88\u4e89 \u957f\u77ed\u4e4b\u610f\uff0c\u8fd9\u4e00\u6218\u5b9e\u662f\u60c5\u975e\u5f97\u5df1\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u53f9\u9053\uff1a\u201c\u8fd9\u5b9e\u5728\u662f\u6211\u9519\u602a\u4e86\u4f60\u3002\u201d \u3000\u3000\u4ed6\u4e0d\u8ba9\u695a\u7559\u9999\u8bf4\u8bdd\u62a2\u7740\u9053\uff1a\u201c\u73b0\u5728\u6211\u4e5f\u5df2\u660e\u767d\uff0c\u4f60\u7edd\u975e\u90a3\u76d7\u5251\u884c\u523a\u7684\u4eba\uff0c\u5426\u5219\u6211\u65b9\u4e00\u5251 \u5931\u624b\u4f60\u5c31\u4e07\u4e07\u4e0d\u80af\u653e\u8fc7\u6211\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5728\u4e0b\u4eca\u65e5\u524d\u6765\uff0c\u53ea\u662f\u4e3a\u4e86\u8981\u5411\u524d\u8f88\u89e3\u91ca\uff0c\u4e5f\u4e3a\u7684\u662f\u60f3\u89c2\u6479\u81c2\u6479\u524d\u8f88\u7684\u5251 \u6cd5\uff0c\u53ea\u56e0\u603b\u89c9\u5f97\u90a3\u771f\u6b63\u523a\u5ba2\u7684\u5251\u6cd5\uff0c\u51fa\u624b\u548c\u524d\u8f88\u6709\u4e9b\u76f8\u4f3c\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u52a8\u5bb9\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u8fdf\u65e9\u603b\u514d\u4e0d\u4e86\u8981\u548c\u90a3\u4eba\u4e00\u6218\uff0c\u90a3\u4e00\u6218\u7684\u80dc\u8d1f\u5173\u7cfb\u91cd\u5927\uff0c\u6211\u4e07\u4e07\u8d25\u4e0d\u5f97\u3002 \u662f\u4ee5\u6211\u624d\u5148\u6765\u89c2\u6479\u524d\u8f88\u5251\u6cd5\uff0c\u4ee5\u4f5c\u501f\u9274\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u6211\u4e5f\u60f3\u770b\u770b\u90a3\u4eba\u7684\u771f\u9762\u76ee\u2026\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6c89\u601d\u7740\u5f90\u5f90\u9053\uff1a\u201c\u6709\u524d\u8f88\u5728\uff0c\u6211\u60f3\u90a3\u4eba\u662f\u4e07\u4e07\u4e0d\u4f1a\u73b0\u8eab\u7684\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u859b\u8863\u4eba\u518d\u8ffd\u95ee\u9053\uff1a\u201c\u4f60\u96be\u9053\u8ba4\u4e3a\u90a3\u4eba\u548c\u6211\u6709\u4ec0\u9ebd\u5173\u7cfb\uff1f\u201d \u3000\u3000\u4ed6\u9762\u4e0a\u5df2\u9732\u51fa\u60ca\u7591\u4e4b\u8272\uff0c\u4f46\u695a\u7559\u9999\u8fd8\u662f\u4e0d\u80af\u6b63\u9762\u56de\u7b54\u4ed6\u8fd9\u53e5\u8bdd\uff0c\u5374\u62ac\u8d77\u5934\u56db\u9762\u89c2\u671b\u7740\uff0c \u50cf\u662f\u5bf9\u8fd9\u5730\u65b9\u7684\u666f\u8272\u53d1\u751f\u4e86\u5174\u8da3\u3002 \u3000\u3000\u8fd9\u662f\u4e2a\u5f88\u5e7d\u9759\u7684\u5c0f\u56ed\u3002\u6797\u672c\u5bc6\u68ee\u5374\u5927\u591a\u662f\u767e\u5e74\u4ee5\u4e0a\u7684\u53e4\u6811\uff0c\u679d\u79bb\u5730\u81f3\u5c11\u5728\u4e94\u4e08\u4ee5\u4e0a\uff0c\u85cf \u8eab\u4e4b\u5904\u5e76\u4e0d\u591a\uff0c\u5c4b\u5b87\u548c\u56f4\u5899\u90fd\u5efa\u5f97\u7279\u522b\u9ad8\uff0c\u5c31\u7b97\u662f\u4e00\u7b49\u4e00\u7684\u8f7b\u529f\u9ad8\u624b\uff0c\u4e5f\u5f88\u96be\u968f\u610f\u51fa\u5165\uff0c\u6765 \u53bb\u81ea\u5982\u3002 \u3000\u3000\u6709\u7ecf\u9a8c\u7684\u591c\u884c\u4eba\u662f\u7edd\u4e0d\u4f1a\u8f7b\u6613\u95ef\u5230\u8fd9\u79cd\u5730\u65b9\u6765\u7684\u3002\u4f55\u51b5\u4f4f\u5728\u8fd9\u91cc\u7684\uff0c\u53c8\u662f\u5929\u4e0b\u7b2c\u4e00\u5251\u5ba2 \u859b\u8863\u4eba\u3002 \u3000\u3000\u859b\u8863\u4eba\u6c89\u541f\u7740\u9053\uff1a\u201c\u82e5\u6362\u4e86\u662f\u6211\uff0c\u6211\u5c31\u672a\u5fc5\u6562\u95ef\u5230\u8fd9\u91cc\u6765\u884c\u5269\uff0c\u9664\u975e\u6211\u65e9\u5df2\u7559\u4e0b\u4e86\u9000 \u8def\uff0c\u800c\u4e14\u7b97\u51c6\u4e86\u5fc5\u5b9a\u53ef\u4ee5\u5168\u8eab\u800c\u9000\u3002\u201d \u3000\u3000\u4ed6\u53d1\u73b0\u5858\u89d2\u8fd8\u6709\u4e2a\u5c0f\u95e8\uff0c\u56db\u9762\u5899\u4e0a\u90fd\u722c\u6ee1\u4e86\u7eca\u7ed3\u7684\u7eff\u85e4\u3002\u6240\u4ee5\u8fd9\u6247\u95e8\u5012\u6709\u4e00\u5927\u534a\u88ab\u63a9\u6ca1 \u5728\u85e4\u7ba9\u4e2d\uff0c\u82e5\u4e0d\u7559\u610f\uff0c\u5c31\u5f88\u96be\u53d1\u73b0\u3002 \u3000\u3000\u695a\u7559\u9999\u5f88\u5feb\u7684\u8d70\u4e86\u8fc7\u53bb\uff0c\u9053\uff1a\u201c\u96be\u9053\u8fd9\u5c31\u662f\u4ed6\u7684\u9000\u8def\uff1f\u201d \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u8fd9\u6247\u95e8\u5e73\u65e5\u4e00\u76f4\u662f\u9501\u7740\u7684\uff0c\u800c\u4e14\u5df2\u6709\u591a\u5e74\u672a\u66fe\u5f00\u3002\u95e8\u4e0a\u7684\u94c1\u6813\u90fd\u5df2\u751f\u4e86 \u9508\uff0c\u7684\u786e\u50cf\u662f\u591a\u5e74\u672a\u66fe\u5f00\u542f\u3002\u4f46\u4ed4\u7ec6\u4e00\u770b\uff0c\u5c31\u53ef\u53d1\u73b0\u6813\u9501\u4e0a\u7684\u94c1\u9508\u6709\u4e9b\u88ab\u522e\u843d\u5728\u5730\u4e0a\u800c\u4e14\u75d5 \u8ff9\u5f88\u65b0\u3002 \u3000\u3000\u695a\u7559\u9999\u4ece\u5730\u4e0a\u62fe\u8d77\u4e86\u4e00\u7247\u94c1\u9508\uff0c\u6c89\u601d\u7740\u9053\uff1a\u201c\u8fd9\u5730\u65b9\u662f\u4e0d\u662f\u7ecf\u5e38\u6709\u4eba\u6253\u626b\uff1f\u201d \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u6bcf\u5929\u90fd\u6709\u4eba\u6253\u626b\uff0c\u53ea\u4e0d\u8fc7\u2026\u2026\u8fd9\u4e24\u5929\u2026\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e24\u5929\u5927\u5bb6\u90fd\u5728\u5fd9\u7740\u6349\u8d3c\uff0c\u81ea\u7136\u5c31\u5fd8\u4e86\u6253\u626b\u9662\u5b50\u6240\u4ee5\u8fd9\u4e9b\u94c1\u9508 \u624d\u4f1a\u7559\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u94c1\u7ee3\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u8fd9\u6247\u95e8\u6700\u8fd1\u4e00\u5b9a\u88ab\u4eba\u6253\u5f00\u8fc7\uff0c\u6240\u4ee5\u95e8\u6813\u548c\u94c1\u9501\u4e0a\u7684\u9508\u624d\u4f1a\u88ab\u522e\u4e0b\u6765\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u524d\u5929\u65e9\u4e0a\u6ca1\u6709\u4eba\u6253\u626b\u8fc7\u9662\u5b50\uff0c\u626b\u9662\u5b50\u7684\u8001\u674e\u505a\u4e8b\u4e00\u5411\u6700\u4ed4\u7ec6\uff0c\u4ed6\u6253\u626b\u8fc7\u7684 \u5730\u65b9\uff0c\u843d\u53f6\u90fd\u4e0d\u4f1a\u7559\u4e0b\u6765\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6240\u4ee5\u8fd9\u6247\u95e8\u4e00\u5b9a\u662f\u5728\u8001\u674e\u626b\u8fc7\u9662\u5b50\u4ee5\u5f8c\u624d\u88ab\u4eba\u6253\u5f00\u7684\uff0c\u4e5f\u8bb8\u5c31\u5728\u524d\u5929\u665a \u4e0a\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u52a8\u5bb9\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u2026\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u662f\u8bf4\u90a3\u523a\u5ba2\u4e5f\u8bb8\u5c31\u662f\u4ece\u8fd9\u6247\u95e8\u91cc\u6e9c\u8fdb\u6765\uff0c\u518d\u4ece\u8fd9\u6247\u95e8\u51fa\u53bb\u7684\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u8138\u8272\u66f4\u6c89\u91cd\u3002\u80cc\u8d1f\u7740\u53cc\u624b\u7eed\u7f13\u7f13\u8e6c\u7740\u6b65\uff0c\u6c89\u601d\u9053\uff1a\u201c\u6b64\u95e8\u4e45\u5df2\u5e9f\u5f03\u4e0d\u7528\uff0c\u77e5\u9053\u8fd9 \u6247\u95e8\u7684\u4eba\u5e76\u4e0d\u591a\u3002\u3002\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u8f7b\u8f7b\u7684\u6478\u7740\u9f3b\u5b50\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\u4ed6\u5728\u60f3\u4ec0\u9ebd\u3002 \u3000\u3000\u859b\u8863\u4eba\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u624d\u63a5\u7740\u9053\uff1a\u201c\u90a3\u4eba\u8eab\u624b\u6377\u5065\uff0c\u8f7b\u529f\u4e0d\u5f31\uff0c\u5c3d\u53ef\u9ad8\u6765\u9ad8\u53bb\uff0c\u4e3a\u4ec0\u9ebd\u4e00 \u5b9a\u8981\u8d70\u8fd9\u6247\u95e8\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u8c01\u4e5f\u60f3\u4e0d\u5230\u4ed6\u4f1a\u4ece\u6b64\u95e8\u51fa\u5165\uff0c\u6240\u4ee5\u4ed6\u624d\u8981\u5229\u7528\u8fd9\u6247\u95e8\uff0c\u6084\u7136\u800c\u6765\uff0c \u5168\u8eab\u800c\u9000\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u4ecd\u73b0\u5728\u8fd9\u6247\u95e8\u53c8\u9501\u4e0a\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u55ef\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u4ed6\u9003\u8d70\u4e4b\u5f8c\u96be\u9053\u8fd8\u6562\u56de\u6765\u9501\u95e8\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4e5f\u8bb8\u4ed6\u6709\u628a\u63e1\u80fd\u907f\u5f00\u522b\u4eba\u7684\u8033\u76ee\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u96be\u9053\u4ed6\u8ba4\u4e3a\u8fd9\u91cc\u7684\u4eba\u90fd\u662f\u778e\u5b50\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e5f\u8bb8\u4ed6\u6709\u7279\u522b\u7684\u6cd5\u5b50\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u4ec0\u9ebd\u6cd5\u5b50\uff1f\u96be\u9053\u4ed6\u8fd8\u4f1a\u9690\u8eab\u6cd5\u4e0d\u6210\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u4e0d\u8bf4\u8bdd\u4e86\uff0c\u5374\u4e00\u76f4\u5728\u76ef\u7740\u95e8\u4e0a\u7684\u9501\u3002 \u3000\u3000\u7136\u5f8c\u4ed6\u4e5f\u4e0d\u77e5\u4ece\u54ea\u91cc\u6478\u51fa\u4e86\u4e00\u6839\u5f88\u957f\u7684\u94c1\u4e1d\uff0c\u5728\u9501\u5b54\u91cc\u8f7b\u8f7b\u4e00\u6311\u3002\u53ea\u542c\u201c\u683c\u201d\u7684\u4e00\u58f0\uff0c \u9501\u5df2\u5f00\u4e86\u3002 \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u6211\u4e5f\u77e5\u9053\u8fd9\u79cd\u9501\u7edd\u5bf9\u96be\u4e0d\u5012\u6709\u7ecf\u9a8c\u7684\u591c\u884c\u4eba\uff0c\u53ea\u4e0d\u8fc7\u804a\u5907\u4e00\u683c\uff0c\u4ee5\u9632\u541b \u5b50\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u53ea\u53ef\u501f\u8fd9\u4e16\u4e0a\u7684\u541b\u5b50\u5e76\u4e0d\u591a\uff0c\u5c0f\u4eba\u5374\u4e0d\u5c11\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u4e5f\u53d1\u89c9\u81ea\u5df1\u5931\u8a00\u4e86\uff0c\u4e7e\u54b3\u4e86\u4e24\u58f0\uff0c\u62a2\u5148\u6253\u5f00\u4e86\u95e8\uff0c\u9053\uff1a\u201c\u9999\u5e05\u662f\u5426\u60f3\u5230\u9694\u58c1\u56e0\u9662 \u5b50\u77a7\u77a7\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u786e\u6709\u6b64\u610f\uff0c\u8bf7\u524d\u8f88\u5e26\u8def\u3002\u201d \u3000\u3000\u4ed6\u4f3c\u4e4e\u5bf9\u8fd9\u628a\u751f\u4e86\u9508\u7684\u94c1\u9501\u5f88\u6709\u5174\u8da3\u3002\u5c45\u7136\u4e58\u859b\u8863\u4eba\u5148\u8d70\u51fa\u95e8\u7684\u65f6\u5019\u987a\u624b\u7275\u7f8a\uff0c\u5c06\u8fd9\u628a \u9501\u85cf\u5165\u6000\u91cc\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u9694\u58c1\u8fd9\u9662\u5b50\u4e5f\u5f88\u5e7d\u9759\u3002\u623f\u5c4b\u7684\u5efa\u4e5f\u5dee\u4e0d\u591a\uff0c\u53ea\u4e0d\u8fc7\u9662\u4e2d\u843d\u53f6\u672a\u626b\uff0c\u7a97\u524d\u79ef\u5c18\u67d3\u7eb8\uff0c \u663e\u5f97\u6709\u79cd\u8bf4\u4e0d\u51fa\u7684\u8352\u51c9\u8427\u7d22\u4e4b\u610f\u3002 \u3000\u3000\u859b\u8863\u4eba\u76ee\u5149\u626b\u8fc7\u79ef\u5c18\u548c\u843d\u53f6\uff0c\u9762\u4e0a\u5df2\u6709\u6012\u5bb9\u3002\u2014\u65e0\u8bba\u8c01\u90fd\u53ef\u4ee5\u770b\u5f97\u51fa\u6765\uff0c\u8fd9\u5730\u65b9\u81f3\u5c11\u5df2 \u6709\u53c1\u4e2a\u6708\u672a\u66fe\u6253\u626b\u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\u5fc3\u91cc\u6697\u6697\u597d\u7b11\u539f\u6765\u859b\u5bb6\u5e84\u7684\u5974\u4ec6\u4e5f\u548c\u522b\u7684\u5730\u65b9\u4e00\u6837\uff0c\u529f\u592b\u4e5f\u53ea\u4e0d\u8fc7\u505a\u5728\u4e3b\u4eba\u7684\u773c \u524d\u800c\u5df2\u3002 \u3000\u3000\u6709\u98ce\u5439\u8fc7\uff0c\u5439\u5f97\u6ee1\u9662\u843d\u53f6\u730e\u730e\u7684\u98de\u821e\u800c\u8d77\u3002 \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u8fd9\u9662\u5b50\u662f\u7a7a\u7740\u7684\uff1f\u201d \u3000\u3000\u859b\u8863\u4eba\u53c8\u4e7e\u54b3\u4e86\u4e24\u58f0\uff0c\u9053\uff1a\u201c\u8fd9\u91cc\u662f\u6211\u4e8c\u5f1f\u7b11\u4eba\u7684\u5c45\u5904\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u73b0\u5728\u5462\uff1f\u201d \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u73b0\u5728\uff1f\u54b3\u54b3\uff0c\u53f0\u5f1f\u4e00\u5411\u4e0d\u62d8\u5c0f\u8282\uff0c\u6240\u4ee5\u4e0b\u4eba\u4eec\u624d\u6562\u5982\u6b64\u653e\u8086\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8bf4\u5f97\u5f88\u6709\u6280\u5de7\u5374\u8bf4\u660e\u4e86\u53c1\u4ef6\u4e8b\u3002 \u3000\u3000\u7b2c\u4e00\u859b\u7b11\u4eba\u8fd8\u662f\u5f80\u5728\u8fd9\u91cc\u3002 \u3000\u3000\u7b2c\u4e8c\uff0c\u4e0b\u4eba\u4eec\u5e76\u6ca1\u6709\u5c06\u8fd9\u4f4d\u201c\u859b\u4e8c\u7237\u201d\u653e\u5728\u5fc3\u4e0a\uff0c\u6240\u4ee5\u8fd9\u5730\u65b9\u4f1a\u6ca1\u6253\u626b\u3002 \u3000\u3000\u7b2c\u53c1\uff0c\u4ed6\u4e5f\u65e0\u5f02\u8bf4\u51fa\u4e86\u4ed6\u4eec\u5144\u5f1f\u4e4b\u95f4\u7684\u60c5\u611f\u5f88\u758f\u8fdc\u3002\u4ed6\u82e5\u65f6\u5e38\u5230\u8fd9\u91cc\u6765\uff0c\u4e0b\u4eba\u4eec\u53c8\u600e\u6562 \u5077\u61d2\uff1f\u90a3\u6247\u95e8\u53c8\u600e\u4f1a\u9501\u8d77\uff1f \u3000\u3000\u695a\u7559\u9999\u76ee\u5149\u95ea\u52a8\u9053\uff1a\u201c\u859b\u4e8c\u4fa0\u6700\u8fd1\u6015\u4e5f\u5f88\u5c11\u4f4f\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u201c\u54fc\u201d\u4e86\u4e00\u58f0\u53c8\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u201c\u54fc\u201d\u662f\u8868\u793a\u4e0d\u6ee1\uff1b\u53f9\u6c14\u5374\u662f\u8868\u793a\u5a49\u60dc\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u7a81\u542c\u5916\u9762\u4e00\u9635\u9a9a\u52a8\u3002\u6709\u4eba\u60ca\u547c\u7740\u9053\uff1a\u201c\u706b\u2026\u2026\u9a6c\u68da\u8d77\u706b\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u867d\u7136\u6c89\u5f97\u4f4f\u6c14\uff0c\u4f46\u76ee\u4e2d\u8fd8\u662f\u5c04\u51fa\u4e86\u6012\u706b\u51b7\u7b11\u9053\uff1a\u201c\u597d\u597d\uff0c\u597d\uff0c\u524d\u5929\u6709\u4eba\u6765\u76d7\u5251\uff0c \u6628\u5929\u6709\u4eba\u6765\u884c\u523a\uff0c\u4eca\u5929\u5c45\u7136\u6709\u4eba\u6765\u653e\u706b\u4e86\uff0c\u96be\u9053\u8d39\u859b\u8863\u4eba\u771f\u7684\u8001\u4e86\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u6562\u7d27\u8d54\u7b11\u9053\uff1a\u201c\u79cb\u51ac\u7269\u71e5\u4e0d\u5c0f\u5fc3\u5c31\u4f1a\u6709\u706b\u7130\u4e4b\u707e\uff0c\u4f55\u51b5\u9a6c\u68da\u91cc\u53c8\u5168\u662f\u7a3b\u8349\u2026\u3002\u201d \u3000\u3000\u4ed6\u5634\u91cc\u867d\u8fd9\u9ebd\u8bf4\uff0c\u5176\u5b9e\u5fc3\u91cc\u5df2\u660e\u767d\u8fd9\u662f\u8c01\u7684\u6770\u4f5c\u4e86\u2014\u201c\u5c0f\u706b\u795e\u201d\u4ed6\u4eec\u89c1\u5230\u695a\u7559\u9999\u8fdb\u6765\u8fd9 \u9ebd\u4e45\u8fd8\u65e0\u6d88\u606f\uff0c\u600e\u9ebd\u80af\u5728\u5916\u9762\u5b89\u5b89\u4efd\u4efd\u7684\u7b49\u7740\u3002 \u3000\u3000\u859b\u8863\u4eba\u52c9\u5f3a\u7b11\u4e86\u7b11\uff0c\u8fd8\u672a\u8bf4\u5b8c\uff0c\u7a81\u7136\u53c8\u6709\u4e00\u9635\u60ca\u547c\u9a9a\u52a8\u4e4b\u58f0\u4f20\u4e86\u8fc7\u6765\u201c\u53a8\u623f\u4e5f\u8d77\u706b \u4e86\u2026\u2026\u5c0f\u5fc3\u5f8c\u9662\uff0c\u5c31\u662f\u90a3\u653e\u7684\u706b\uff01\u8ffd\u3002\u201d \u3000\u3000\u5c0f\u706b\u795e\u653e\u706b\u7684\u6280\u672f\u539f\u6765\u5e76\u4e0d\u5341\u5206\u9ad8\u660e\u3002\u7ec8\u662f\u88ab\u4eba\u53d1\u73b0\u4e86\u884c\u8e2a\u3002 \u3000\u3000\u695a\u7559\u9999\u6697\u4e2d\u53f9\u4e86\u53e3\u6c14\u3002\u53ea\u89c1\u859b\u8863\u4eba\u9762\u4e0a\u5df2\u5168\u65e0\u534a\u5206\u8840\u8272\uff0c\u4f3c\u4e4e\u60f3\u81ea\u5df2\u51fa\u9a6c\u51fb\u9000\u90a3\u7eb5\u706b\u7684 \u4eba\uff0c\u53c8\u4e0d\u4fbf\u5c06\u695a\u7559\u9999\u4e00\u4e2a\u4eba\u629b\u4e0b\u6765\u3002 \u3000\u3000\u5f80\u9ad8\u5899\u4e0a\u671b\u8fc7\u53bb\u53c8\u53ef\u671b\u89c1\u95ea\u95ea\u7684\u706b\u82d7\u3002 \u3000\u3000\u695a\u7559\u9999\u5fc3\u5ff5\u4e00\u95ea\u9053\uff1a\u201c\u524d\u8f88\u4f60\u53ea\u7ba1\u53bb\u7167\u8f91\u706b\u573a\uff0c\u5728\u4e0b\u5c31\u5728\u8fd9\u91cc\u901b\u901b\uff0c\u859b\u4e8c\u4fa0\u4e0d\u4e00\u5b9a\u6070\u5de7 \u56de\u6765\u4e86\uff0c\u6211\u8fd8\u53ef\u4ee5\u8ddf\u4ed6\u804a\u804a\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u8dfa\u4e86\u8dfa\u811a\uff0c\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\u5728\u4e0b\u5c31\u5931\u966a\u7247\u523b\u3002\u201d \u3000\u3000\u4ed6\u8d70\u4e86\u4e24\u6b65\u7a81\u53c8\u56de\u9996\u9053\uff1a\u201c\u820d\u5f1f\u82e5\u6709\u4ec0\u9ebd\u5931\u793c\u4e4b\u5904\uff0c\u9999\u5e05\u4f60\u7528\u4e0d\u7740\u5bf9\u4ed6\u5ba2\u6c14\u53ea\u7ba1\u6559\u8bad\u4ed6 \u5c31\u662f\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5fae\u7b11\u7740\uff0c\u7b11\u5f97\u597d\u50cf\u5f88\u795e\u79d8\u3002 \u7b2c\u5341\u7ae0\u3000\u859b\u4e8c\u7237\u7684\u79d8\u5bc6>> \u53e4\u9f99\u300a\u695a\u7559\u9999\u7cfb\u5217\xb7\u9b3c\u604b\u4f20\u5947\u300b \u7b2c\u5341\u7ae0\u3000\u859b\u4e8c\u7237\u7684\u79d8\u5bc6 \u3000\u3000\u859b\u7b11\u4eba\u4f4f\u7684\u5c4b\u5b50\u51e0\u4e4e\u548c\u4ed6\u54e5\u5b8c\u5168\u4e00\u5f0f\u4e00\u6837\uff0c\u53ea\u4e0d\u8fc7\u7a97\u524d\u79ef\u5c18\u3002\u6a90\u4e0b\u7ed3\u7f51\uff0c\u8fde\u5eca\u4e0a\u7684\u5730\u677f \u90fd\u5df2\u8150\u673d\uff0c\u8d70\u4e0a\u53bb\u5c31\u4f1a\u201c\u5431\u5431\u683c\u683c\u201d\u7684\u53d1\u54cd\u3002 \u3000\u3000\u95e8\u5012\u662f\u5173\u7740\u7684\uff0c\u4e14\u8fd8\u7528\u8349\u7ef3\u5728\u95e8\u68c0\u4e0a\u6253\u4e86\u4e2a\u7ed3\u3002 \u3000\u3000\u5047\u5982\u6709\u4eba\u60f3\u8fdb\u53bb\uff0c\u7528\u5341\u6839\u8349\u7ef3\u6253\u5341\u4e2a\u7ed3\u4e5f\u7167\u6837\u62e6\u4e0d\u4f4f\uff0c\u7528\u8349\u7ef3\u6253\u7ed3\u7684\u610f\u601d\uff0c\u53ea\u4e0d\u8fc7\u662f\u60f3 \u77e5\u9053\u6709\u6ca1\u6709\u4eba\u5077\u5077\u8fdb\u53bb\u8fc7\u800c\u5df1\u3002 \u3000\u3000\u8fd9\u610f\u601d\u695a\u7559\u9999\u81ea\u7136\u5f88\u660e\u767d\u3002 \u3000\u3000\u4ed6\u773c\u775b\u95ea\u7740\u5149\uff0c\u770b\u5230\u4ef6\u5f88\u6709\u8da3\u7684\u4e8b\uff0c\u773c\u775b\u76ef\u7740\u8fd9\u8349\u7ef3\u7684\u7ed3\uff0c\u4ed6\u89e3\u4e86\u5f88\u4e45\uff0c\u624d\u6253\u5f00\u7ed3\u63a8\u5f00 \u95e8\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u5e76\u6ca1\u6709\u7acb\u523b\u8d70\u8fdb\u53bb\u3002 \u3000\u3000\u95e8\u8fd8\u5728\u968f\u98ce\u6447\u6643\u7740\uff0c\u53d1\u51fa\u9635\u9635\u5f88\u523a\u9a82\u7684\u58f0\u97f3\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u6697\u5f97\u5f88\uff0c\u65e5\u5149\u88ab\u9ad8\u56f4\u3001\u6d53\u836b\u3001\u5782\u679d\u6240\u6321\uff0c\u6839\u672c\u7167\u4e0d\u8fdb\u53bb\u3002 \u3000\u3000\u695a\u7559\u9999\u7b49\u81ea\u5df1\u7684\u773c\u775b\u5b8c\u5168\u4e60\u60ef\u65bc\u8fd9\u79cd\u5149\u7ebf\u5f8c\uff0c\u624d\u8bd5\u63a2\u7740\u5f80\u91cc\u8d70\uff0c\u8d70\u5f97\u975e\u5e38\u6162\uff0c\u800c\u4e14\u975e\u5e38 \u5c0f\u5fc3\u3002 \u3000\u3000\u96be\u9053\u4ed6\u8ba4\u4e3a\u8fd9\u5c4b\u4e88\u91cc\u4f1a\u6709\u4ec0\u9ebd\u5371\u9669\u4e0d\u6210\uff1f\u4e0d\u9519\uff0c\u6709\u65f6\u201c\u75af\u5b50\u201d\u7684\u786e\u5f88\u5371\u9669\u7684\uff0c\u4f46\u75af\u5b50\u4f4f \u7684\u7834\u5c4b\u5b50\u53c8\u4f1a\u6709\u4ec0\u9ebd\u5371\u9669\u5462\uff1f \u3000\u3000\u65e0\u8bba\u8c01\u8981\u53bb\u627e\u201c\u859b\u8863\u4eba\u2019\uff0c\u4e00\u8d70\u8fdb\u8fd9\u5c4b\u5b50\uff0c\u90fd\u4f1a\u8ba4\u4e3a\u81ea\u5df1\u8d70\u9519\u4e86\uff0c \u3000\u3000\u56e0\u4e3a\u8fd9\u5b9e\u4e0d\u50cf\u662f\u7537\u4eba\u4f4f\u7684\u5730\u65b9\u3002 \u3000\u3000\u5c4b\u5b50\u7684\u89d2\u843d\u91cc\uff0c\u653e\u7740\u5f20\u5f88\u5927\u7684\u68b3\u5986\u53f0\uff0c\u4e0a\u9762\u6446\u6ee1\u4e86\u5404\u5f0f\u5404\u6837\u7684\u4e1c\u897f\uff0c\u5341\u6837\u4e2d\u5012\u6709\u4e5d\u6837\u662f \u5973\u4e88\u68b3\u5986\u65f6\u7528\u7684\u3002 \u3000\u3000\u5e8a\u4e0a\u3001\u6905\u5b50\u4e0a\uff0c\u5806\u6ee1\u4e86\u5404\u5f0f\u5404\u6837\u7684\u8863\u670d\u6bcf\u4e00\u4ef6\u90fd\u662f\u82b1\u82b1\u7eff\u7eff\uff0c\u4e94\u989c\u516d\u8272\u5341\u4e2a\u5973\u5b69\u5b50\uff0c\u53ea \u6015\u6700\u591a\u4e5f\u53ea\u6709\u4e00\u4e24\u4e2a\u4eba\u6562\u7a7f\u8fd9\u79cd\u8863\u88f3\uff1a \u3000\u3000\u4f4f\u5728\u8fd9\u91cc\u7684\u5f53\u771f\u662f\u4e2a\u5973\u4eba\uff0c\u8fd9\u5973\u4eba\u4e5f\u5fc5\u5b9a\u5f88\u6709\u95ee\u9898\u3002\u4f55\u51b5\u4f4f\u5728\u8fd9\u91cc\u7684\u7adf\u662f\u4e2a\u7537\u4eba\uff0c\u56db\u5341 \u591a\u5c81\u7684\u7537\u4eba\u3002 \u3000\u3000\u8fd9\u7537\u4eba\u81ea\u7136\u6beb\u65e0\u7591\u95ee\u662f\u4e2a\u75af\u5b50\u3002 \u3000\u3000\u695a\u7559\u9999\u7684\u773c\u795e\u4f3c\u53c8\u6697\u6de1\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u5728\u5c4b\u5b50\u91cc\u6253\u7740\u8f6c\uff0c\u5c06\u6bcf\u6837\u4e1c\u897f\u90fd\u62ff\u8d77\u6765\u77a7\u77a7\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u53d1\u73b0\u201c\u859b\u5b9d\u5b9d\u201d\u5c45\u7136\u662f\u4e2a\u5f88\u8003\u7a76\u7684\u4eba\uff0c\u7528\u7684\u4e1c\u897f\u90fd\u662f\u4e0a\u597d\u7684\u8d27\u3002\u8863\u670d\u7684\u8d28\u6599\u5f88\u9ad8 \u8d35\uff0c\u800c\u4e14\u5f88\u4e7e\u51c0\u3002 \u3000\u3000\u800c\u4e14\u8fd9\u5c4b\u4e88\u91cc\u7684\u4e1c\u897f\u867d\u6446\u5f97\u4e71\u4e03\u516b\u7cdf\uff0c\u5176\u5b9e\u5374\u7b80\u76f4\u53ef\u8bf4\u662f\u4e00\u5c18\u4e0d\u67d3\uff0c\u6bcf\u6837\u4e1c\u897f\u90fd\u4e7e\u51c0\u6781 \u4e86\u3002 \u3000\u3000\u662f\u8c01\u5728\u6253\u626b\u5c4b\u5b50\uff1f \u3000\u3000\u82e5\u6709\u4eba\u66ff\u4ed6\u6253\u626b\u5c4b\u5b50\uff0c\u4e3a\u4ec0\u9ebd\u6ca1\u6709\u4eba\u66ff\u4ed6\u6253\u626b\u9662\u5b50\uff1f \u3000\u3000\u695a\u7559\u9999\u773c\u775b\u53c8\u4eae\u4e86\u3002 \u3000\u3000\u7a81\u7136\u95f4\u5c4b\u9876\u4e0a\u201c\u5ffd\u5618\u201d\u4e00\u58f0\u54cd\u3002 \u3000\u3000\u695a\u7559\u9999\u4e00\u60ca\uff0c\u53cd\u624b\u5c06\u4e00\u6839\u94f6\u7c2a\u5c04\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u94f6\u7c2a\u672c\u5c31\u5728\u68b3\u5986\u53f0\u4e0a\u7684\uff0c\u4ed6\u6b63\u62ff\u5728\u624b\u91cc\u628a\u73a9\uff0c\u6b64\u523b\u4f46\u89c1\u94f6\u5149\u4e00\u95ea\uff0c\u201c\u593a\u201d\u7684\u4e00\u58f0\u9489\u5165\u4e86 \u5c4b\u9876\u3002 \u3000\u3000\u5c4b\u9876\u4e0a\u7adf\u53d1\u51fa\u4e86\u4e00\u58f0\u4ee4\u4eba\u6bdb\u9aa8\u8038\u7136\u7684\u58f0\u97f3\u3002 \u3000\u3000\u539f\u6765\u8fd9\u5c4b\u5b50\u7684\u6881\u95f4\u8fd8\u6709\u5c42\u6728\u677f\uff0c\u770b\u6765\u4eff\u4f5b\u5efa\u6709\u9601\u697c\uff0c\u4f46\u5374\u770b\u4e0d\u5230\u697c\u68af\uff0c\u4e5f\u770b\u4e0d\u5230\u5165\u53e3\u3002 \u3000\u3000\u94f6\u7c2a\u53ea\u5269\u4e0b\u4e00\u5c0f\u622a\u9732\u5728\u5916\u9762\uff0c\u95ea\u95ea\u7684\u53d1\u7740\u5149\u3002 \u3000\u3000\u695a\u7559\u9999\u8eab\u5b50\u8f7b\u98d8\u98d8\u7684\u7eb5\u4e86\u4e0a\u53bb\uff0c\u8d34\u5728\u5c4b\u9876\u4e0a\uff0c\u5c31\u50cf\u662f\u4e00\u5f20\u997c\u6363\u5728\u9505\u91cc\u5e73\u5e73\u7684\uff0c\u7a33\u7a33\u7684\u7edd \u6ca1\u6709\u4eba\u62c5\u5fc3\u4ed6\u4f1a\u6389\u4e0b\u6765\u3002 \u3000\u3000\u4ed6\u8f7b\u8f7b\u7684\u62d4\u51fa\u4e86\u94f6\u7c2a\uff0c\u5c31\u53d1\u73b0\u6709\u4e1d\u8840\u968f\u7740\u94f6\u7c2a\u6d41\u51fa\uff0c\u7d2b\u7684\u8840\u770b\u6765\u51e0\u4e4e\u5c31\u50cf\u58a8\u6c41\uff0c\u800c\u4e14\u5e26 \u7740\u79cd\u65e0\u6cd5\u5f62\u5bb9\u7684\u6076\u81ed\u3002 \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u201c\u539f\u6765\u53ea\u4e0d\u8fc7\u662f\u53ea\u8001\u9f20\u3002\u201d \u3000\u3000\u4f46\u8fd9\u53ea\u8001\u9f20\u5c31\u5e2e\u4e86\u4ed6\u5f88\u5927\u7684\u5fd9\u3002 \u3000\u3000\u4ed6\u5148\u5c06\u5c4b\u9876\u4e0a\u7684\u8840\u64e6\u4e7e\u51c0\uff0c\u7136\u5f8c\u518d\u7528\u94f6\u7c2a\u8f7b\u6572\u3002 \u3000\u3000\u5c4b\u9876\u4e0a\u81ea\u7136\u662f\u7a7a\u7684\u3002 \u3000\u3000\u695a\u7559\u9999\u6e38\u9c7c\u822c\u5728\u5c4b\u9876\u4e0a\u6ed1\u4e86\u534a\u5708\uff0c\u7a81\u7136\u4e00\u4ef0\u624b\uff0c\u5757\u6728\u677f\u5c31\u5947\u8ff9\u822c\u88ab\u4ed6\u6258\u4e86\u8d77\u8f7b\u9732\u51fa\u4e86\u9ed1 \u9edd\u9edd\u7684\u5165\u95e8\u3002 \u3000\u3000\u5916\u9762\u7684\u7f00\u52a8\u547c\u58f0\u5df2\u79bb\u5f97\u66f4\u8fdc\u4e86\uff0c\u4ee4\u4eba\u5931\u671b\u7684\u662f\u8fd9\u9601\u697c\u4e0a\u5e76\u6ca1\u6709\u4ec0\u9ebd\u60ca\u4eba\u7684\u79d8\u5bc6\uff0c\u53ea\u4e0d\u8fc7 \u6709\u5f20\u51f3\u5b50\u6709\u4e2a\u8863\u7bb1\u3002 \u3000\u3000\u8863\u7bb1\u5f88\u7834\u65e7\uff0c\u50cf\u662f\u4e45\u5df2\u88ab\u4e3b\u4eba\u6240\u5e9f\u5f03\u3002\u4f46\u695a\u7559\u9999\u7528\u624b\u53bb\u6478\u4e86\u6478\u3002 \u3000\u3000\u4e0a\u9762\u7684\u79ef\u5c18\u5c45\u7136\u5e76\u4e0d\u591a\u3002 \u3000\u3000\u6253\u5f00\u8863\u7bb1\u4e00\u770b\uff0c\u91cc\u9762\u53ea\u4e0d\u8fc7\u6709\u51e0\u4ef6\u5f88\u666e\u901a\u7684\u8863\u670d\u3002 \u3000\u3000\u8fd9\u4e9b\u8863\u670d\u7edd\u6ca1\u6709\u4e1d\u6beb\u5947\u5f02\u4e4b\u5904\uff0c\u8c01\u770b\u5230\u90fd\u4e0d\u4f1a\u89c9\u5f97\u5947\u602a\u3002 \u3000\u3000\u53ea\u6709\u695a\u7559\u9999\u662f\u4f8b\u5916\uff0c\u4e5f\u8bb8\u5c31\u56e0\u4e3a\u8fd9\u4e9b\u8863\u670d\u592a\u5e73\u51e1\uff0c\u592a\u666e\u901a\u4e86\uff0c\u695a\u7559\u9999\u624d\u4f1a\u89c9\u5f97\u5947\u602a\u3002 \u3000\u3000\u4e00\u4e2a\u75af\u5b50\u7684\u9601\u697c\u4e0a\uff0c\u600e\u4f1a\u85cf\u7740\u666e\u901a\u4eba\u7a7f\u7684\u8863\u670d\uff1f\u96be\u9053\u8fd9\u4e9b\u8863\u670d\u662f\u666e\u901a\u4eba\u7a7f\u7684\uff0c\u8863\u7bb1\u4ece\u539f \u8def\u9000\u4e0b\u53bb\uff0c\u5c06\u6728\u677f\u76d6\u597d\uff0c\u5f80\u4e0b\u9762\u671b\u4e0a\u53bb\uff0c\u7edd\u5bf9\u770b\u4e0d\u51fa\u6709\u4eba\u4e0a\u53bb\u8fc7\u3002 \u3000\u3000\u7136\u5f8c\u4ed6\u53c8\u5c06\u90a3\u6839\u94f6\u7c2a\u653e\u56de\u5986\u53f0\uff0c\u8d70\u51fa\u95e8\uff0c\u5173\u8d77\u95e8\uff0c\u7528\u539f\u6765\u7684\u90a3\u6839\u8349\u7ef3\u5728\u95e8\u6813\u4e0a\u6253\u4e86\u4e2a\u76f8 \u540c\u7684\u7ed3\u3002 \u3000\u3000\u770b\u4ed6\u7684\u6837\u5b50\uff0c\u5c45\u7136\u597d\u50cf\u5c31\u8981\u8d70\u4e86\u3002 \u3000\u3000\u5899\u5934\u4e0a\u7684\u706b\u82d7\u5df2\u5316\u4f5c\u8f7b\u70df\uff0c\u706b\u52bf\u663e\u7136\u5df2\u88ab\u6251\u706d\u3002 \u3000\u3000\u9662\u5916\u5df2\u4f20\u6765\u4e86\u4e00\u9635\u547c\u5524\u58f0\uff0c\u6b63\u662f\u6765\u627e\u695a\u7559\u9999\u7684\u3002 \u3000\u3000\u695a\u7559\u9999\u7a81\u7136\u4e00\u63a0\u800c\u8d77\u8f7b\u70df\u822c\u76f4\u4e0a\u5c4b\u810a\u3002 \u3000\u3000\u4ed6\u542c\u5230\u6709\u4e24\u4e2a\u4eba\u5954\u5165\u8fd9\u9662\u5b50\uff0c\u4e00\u4eba\u5524\u9053\uff1a\u201c\u695a\u76f8\u516c\uff0c\u695a\u5927\u4fa0\uff0c\u6211\u5bb6\u5e84\u4e3b\u8bf7\u4f60\u5230\u524d\u5385\u7528 \u8336\u3002\u201d \u3000\u3000\u53e6\u4eba\u9053\uff1a\u201c\u4eba\u5bb6\u660e\u660e\u5df2\u8d70\u4e86\uff0c\u4f60\u8fd8\u7a77\u543c\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90a3\u4eba\u4f3c\u4e4e\u53c8\u77a7\u4e86\u534a\u5929\uff0c\u624d\u55e1\u55e1\u7740\u9053\uff1a\u201c\u4ed6\u600e\u9ebd\u4f1a\u4e0d\u544a\u800c\u522b\u83ab\u975e\u662f\u88ab\u6211\u4eec\u90a3\u4f4d\u5b9d\u8d1d\u4e8c\u7237\u62c9 \u8d70\u4e86\u3002\u201d \u3000\u3000\u53e6\u4eba\u7b11\u9053\uff1a\u201c\u8fd9\u59d3\u695a\u7684\u4e00\u6765\uff0c\u5c31\u5bb3\u5f97\u6211\u4eec\u8fd9\u4e9b\u4eba\u51e0\u5929\u6ca1\u5f97\u597d\u7761\uff0c\u6bd4\u4ed6\u5403\u5403\u6211\u4eec\u90a3\u5b9d\u8d1d\u4e8c \u7237\u7684\u82e6\u5934\u4e5f\u597d\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u95f7\u58f0\u4e0d\u54cd\u7684\u542c\u7740\uff0c\u53ea\u6709\u6697\u4e2d\u82e6\u7b11\u7b49\u8fd9\u4e24\u4eba\u90fd\u8d70\u4e86\u51fa\u53bb\uff0c\u5ffd\u7136\u6380\u8d77\u4e86\u51e0\u7247\u5c4b\u74e6\uff0c\u5728 \u5c4b\u9876\u4e0a\u6316\u4e86\u4e2a\u6d1e\u3002\u5c06\u6316\u51fa\u6765\u7684\u6ce5\u90fd\u7528\u5927\u624b\u5dfe\u5305\u4e86\u8d77\u6765\uff0c\u7528\u5c4b\u74e6\u538b\u7740\uff0c\u514d\u5f97\u88ab\u98ce\u5439\u6563\u3002 \u3000\u3000\u8fd9\u4e9b\u4e8b\u82e5\u6362\u4e86\u522b\u4eba\u7684\u624b\u505a\uff0c\u6709\u591a\u9ebb\u70e6\u3002\u4f46\u695a\u7559\u9999\u5374\u505a\u5f97\u53c8\u4e7e\u51c0\uff0c\u53c8\u5229\u843d\uff0c\u800c\u4e14\u8fde\u4e00\u70b9\u58f0 \u97f3\u90fd\u6ca1\u6709\uff0c\u5c31\u7b97\u6709\u6761\u72d7\u5728\u5c4b\u9876\u4e0b\u90fd\u7edd\u4e0d\u4f1a\u88ab\u60ca\u52a8\u3002\u4ece\u5934\u5230\u5c3e\u8fd8\u6ca1\u6709\u82b1\u534a\u76cf\u8336\u529f\u592b\uff0c\u4ed6\u5df2\u795e\u4e0d \u77e5\u3001\u9b3c\u4e0d\u89c9\u7684\u53c8\u6e9c\u56de\u4e86\u90a3\u9601\u697c\u3002 \u3000\u3000\u5929\u5149\u4ece\u6d1e\u91cc\u7167\u8fdb\u6765\u9601\u697c\u6bd4\u521a\u4eae\u5f97\u591a\u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\u627e\u7740\u4e86\u90a3\u53ea\u6b7b\u8001\u9f20\u8fdc\u8fdc\u629b\u5230\u4e00\u8fb9\u3002\u626f\u4e0b\u5757\u8863\u895f\u3002\u5c06\u6728\u677f\u4e0a\u9732\u51fa\u4e86\u65b9\u88ab\u94f6\u7c2a\u9489\u51fa\u6765 \u7684\u5c0f\u5b54\u3002\u695a\u7559\u9999\u5728\u4e0a\u9762\u77a7\u4e86\u77a7\u53c8\u7528\u90a3\u6839\u5f00\u9501\u7684\u94c1\u4e1d\u5c06\u8fd9\u5c0f\u5b54\u7a0d\u5fae\u901a\u5927\u4e86\u4e9b\u3002\u7136\u5f8c\u4ed6\u5c31\u8212\u8212\u670d \u670d\u7684\u8eba\u4e01\u4e0b\u6765\uff0c\u8f7b\u8f7b\u7684\u63c9\u7740\u9f3b\u5b50\u5634\u89d2\u9732\u51fa\u4e86\u5fae\u7b11\uff0c\u50cf\u662f\u5bf9\u8fd9\u73b0\u884c\u7684\u4e00\u5207\u90fd\u89c9\u5f97\u5f88\u6ee1\u610f\u3002 \u3000\u3000\u53c8\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\u4e0b\u9762\u7684\u95e8\u5ffd\u7136\u53d1\u51fa\u201c\u5431\u201d\u7684\u4e00\u58f0\u8f7b\u54cd\uff0c\u660e\u660e\u7761\u7740\u4e86\u7684\u695a\u7559\u9999\u5c45\u7136\u7acb\u523b\u5c31 \u9192\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4ed6\u8f7b\u8f7b\u4e00\u7ffb\u8eab\uff0c\u773c\u775b\u5c31\u5df2\u51d1\u5230\u90a3\u9488\u773c\u822c\u7684\u5c0f\u5b54\u4e0a\u3002 \u3000\u3000\u695a\u7559\u9999\u65e9\u5df2\u5c06\u4f4d\u7f6e\u7b97\u597d\u3002\u5f00\u5b54\u7684\u65f6\u5019\uff0c\u6240\u7528\u7684\u624b\u6cd5\u4e5f\u5f88\u5de7\u5999\uff0c\u662f\u4ee5\u5b54\u867d\u4e0d\u5927\uff0c\u4f46\u4e00\u4e2a\u4eba \u82e5\u8d70\u8fdb\u5c4b\u5b50\uff0c\u4ed6\u4e3b\u8981\u7684\u6d3b\u52a8\u8303\u56f4\uff0c\u5168\u90fd\u5728\u8fd9\u5c0f\u5b54\u7684\u89c6\u754c\u4e4b\u5185\uff0c\u4ece\u91cc\u9762\u671b\u4e0a\u53bb\u3002\u8fd9\u5c0f\u5b54\u5374\u53ea\u4e0d \u8fc7\u662f\u4e2a\u5c0f\u9ed1\u70b9\u3002 \u3000\u3000\u8d70\u8fdb\u5c4b\u5b50\u6765\u7684\uff0c\u679c\u7136\u5c31\u662f\u859b\u5b9d\u5b9d\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u9762\u6253\u5475\u6b20\uff0c\u4e00\u9762\u4f38\u61d2\u8170\uff0c\u4e00\u9762\u53c8\u7528\u4e24\u624b\u6376\u7740\u80f8\u819b\uff0c\u5728\u5c4b\u5b50\u91cc\u6253\u4e86\u51e0\u4e2a\u8f6c\u50cf\u662f\u5728\u6d3b \u52a8\u7b4b\u9aa8\u3002 \u3000\u3000\u9664\u4e86\u4ed6\u8eab\u4e0a\u7a7f\u7684\u8863\u670d\u5916\uff0c\u770b\u4ed6\u73b0\u5728\u7684\u4e3e\u52a8\uff0c\u5b9e\u5728\u5e76\u6ca1\u6709\u4ec0\u9ebd\u75af\u75af\u7684\u6a21\u6837\uff1b\u4f46\u4e00\u4e2a\u75af\u5b50\u56de \u5230\u81ea\u5df1\u7684\u5c4b\u4e88\u91cc\uff0c\u662f\u4e0d\u662f\u5c31\u4f1a\u53d8\u5f97\u6b63\u5e38\u4e9b\u5462\uff1f\u4e16\u4e0a\u5927\u591a\u6570\u75af\u5b50\uff0c\u5c82\u975e\u90fd\u662f\u89c1\u5230\u4eba\u4e4b\u5f8c\u624d\u4f1a\u53d1 \u75af\u7684\u5417\uff1f \u3000\u3000\u695a\u7559\u9999\u4f3c\u4e4e\u89c9\u5f97\u6709\u8da3\u3002\u56e0\u4e3a\u4ed6\u867d\u7136\u89c1\u591a\u8bc6\u5e7f\uff0c\u5374\u4e5f\u4ece\u6765\u4e0d\u77e5\u9053\u75af\u5b50\u4e00\u4e2a\u4eba\u7684\u65f6\u5019\u4f1a\u505a\u4e9b \u4ec0\u9ebd\u4e8b\u3002 \u3000\u3000\u53ea\u89c1\u859b\u5b9d\u5b9d\u8f6c\u4e86\u51e0\u4e2a\u5708\u5b50\uff0c\u5c31\u5750\u5728\u68b3\u679a\u53f0\u524d\uff0c\u671b\u7740\u94dc\u955c\u5446\u5446\u7684\u51fa\u795e\u3002\u53c8\u62ff\u8d77\u90a3\u6839\u94f6\u7c2a\uff0c \u653e\u5728\u9f3b\u5b50\u4e0a\u55c5\u4e86\u55c5\uff0c\u5bf9\u7740\u955c\u5b50\u505a\u4e86\u4e2a\u9b3c\u8138\uff0c\u5583\u5583\u9053\uff1a\u201c\u6b7b\u5c0f\u5077\uff0c\u574f\u5c0f\u5077\uff0c\u4f60\u60f3\u6765\u5077\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u4ed6\u679c\u7136\u5df2\u7ecf\u53d1\u73b0\u6709\u4eba\u8fdb\u8fc7\u8fd9\u5c4b\u5b50\u3002 \u3000\u3000\u695a\u7559\u9999\u9762\u4e0a\u4e0d\u7981\u9732\u51fa\u4e86\u5f97\u610f\u4e4b\u8272\uff0c\u5c31\u597d\u50cf\u4e00\u4e2a\u730e\u4eba\u5df2\u6349\u4f4f\u4e86\u72d0\u72f8\u5c3e\u5df4\u3002\u8c01\u77e5\u4ed6\u521a\u7728\u773c\u859b \u5b9d\u5b9d\u7adf\u7a81\u7136\u95f4\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u539f\u6765\u4ed6\u4e5f\u4e0d\u77e5\u662f\u6709\u5fc3\uff1f\u8fd8\u662f\u65e0\u610f\uff1f\u4e00\u95ea\u8eab\u5df2\u5230\u4e86\u695a\u7559\u9999\u77a7\u4e0d\u89c1\u7684\u89d2\u843d\uff0c\u695a\u7559\u9999\u867d\u77a7\u4e0d\u89c1 \u4ed6\u8fd8\u662f\u542c\u5230\u5730\u677f\u5728\u201c\u5431\u5431\u201d\u7684\u54cd\u3002 \u3000\u3000\u859b\u5b9d\u5b9d\u4ed6\u7a76\u7adf\u5728\u5e72\u4ec0\u9ebd\uff1f \u3000\u3000\u82e5\u662f\u6362\u4e86\u522b\u4eba\u5b9a\u4f1a\u7b49\u4ed6\u518d\u51fa\u73b0\u3002\u4f46\u695a\u7559\u9999\u5374\u77e5\u9053\u81ea\u5df1\u7b49\u5f97\u5df2\u7ecf\u591f\u4e45\u4e86\uff0c\u73b0\u5728\u8fd9\u65f6\u673a\u518d\u4e5f \u4e0d\u80fd\u9519\u8fc7\u3002 \u3000\u3000\u4ed6\u8eab\u5b50\u4e00\u7ffb\u5df2\u6380\u8d77\u90a3\u5757\u6728\u677f\u3002\u4ed6\u7684\u4eba\u5df2\u8f7b\u70df\u822c\u8dc3\u4e0b\u3002 \u3000\u3000\u695a\u7559\u9999\u82e5\u662f\u8fdf\u4e86\u4e00\u6b65\uff0c\u6015\u5c31\u5f88\u96be\u518d\u89c1\u5230\u859b\u5b9d\u5b9d\u8fd9\u4e2a\u4eba\u4e86\u3002 \u3000\u3000\u5986\u53f0\u5f8c\u5df2\u9732\u51fa\u4e86\u4e2a\u5730\u9053\uff0c\u859b\u5b9d\u5b9d\u5df2\u51e0\u4e4e\u94bb\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u695a\u7559\u9999\u5fae\u7b11\u9053\uff1a\u201c\u5ba2\u4eba\u6765\u4e86\uff0c\u4e3b\u4eba\u53cd\u5012\u8981\u8d70\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u4e00\u56de\u5934\uff0c\u770b\u5230\u695a\u7559\u9999\u7acb\u523b\u5c31\u8df3\u4e86\u8d77\u6765\u5927\u53eb\u9053\uff1a\u201c\u5ba2\u4eba\uff1f\u4f60\u7b97\u662f\u4ec0\u9ebd\u5ba2\u4eba\uff1f\u4f60\u662f\u5927 \u9a97\u5b50\uff0c\u5c0f\u5077\u2026\u3002\u201d \u3000\u3000\u4ed6\u624b\u91cc\u672c\u6765\u62ff\u7740\u6837\u6241\u6241\u7684\u4e1c\u897f\uff0c\u6b64\u523b\u4e58\u7740\u4e00\u56de\u5934\uff0c\u4e00\u7728\u773c\u7684\u529f\u592b\uff0c\u5df2\u5c06\u8fd9\u6837\u4e1c\u897f\u585e\u5165\u6000 \u91cc\u3002 \u3000\u3000\u695a\u7559\u9999\u597d\u50cf\u6839\u672c\u6ca1\u6709\u7559\u610f\u3002\u8fd8\u662f\u5fae\u7b11\u9053\uff1a\u201c\u4e0d\u8bba\u5982\u4f55\uff0c\u6211\u5e76\u6ca1\u6709\u505a\u4e8f\u5fc3\u4e8b\uff0c\u6240\u4ee5\u4e5f\u4e0d\u5fc5 \u94bb\u5730\u6d1e\u3002\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u542c\u695a\u7559\u9999\u8bf4\u505a\u4e86\u4e8f\u5fc3\u4e8b\u624d\u94bb\u5730\u6d1e\u3002\u53c8\u8df3\u8d77\u6765\u543c\u9053\uff1a\u201c\u6211\u94bb\u5730\u6d1e\u627e\u670b\u53cb\uff0c\u5e72\u4f60\u4ec0\u9ebd \u4e8b\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u54e6\uff1f\u94bb\u5730\u6d1e\u662f\u4e3a\u4e86\u627e\u670b\u53cb\uff1f\u96be\u9053\u4ee4\u53cb\u5065\u5728\u5730\u6d1e\u91cc\uff1f\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u9053\uff1a\u201c\u4e00\u70b9\u4e5f\u4e0d\u9519\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b54\u9053\uff1a\u201c\u53ea\u6709\u5154\u5b50\u624d\u5f80\u5728\u5730\u6d1e\u91cc\uff0c\u96be\u9053\u4f60\u7684\u670b\u53cb\u662f\u514d\u5b50\uff1f\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u77aa\u773c\u9053\uff1a\u201c\u4e00\u70b9\u4e5f\u4e0d\u9519\uff0c\u5154\u5b50\u6bd4\u4eba\u597d\u73a9\u591a\u4e86\uff0c\u6211\u4e3a\u4ec0\u9ebd\u4e0d\u80fd\u8ddf\u5b83\u4eec\u4ea4\u7684\u53cb\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u627e\u514d\u5b50\u4ea4\u670b\u53cb\u81f3\u5c11\u6ca1\u6709\u5371\u9669\uff0c\u65e0\u8bba\u8c01\u60f3\u88c5\u75af\uff0c\u5154\u5b50\u5b9a\u770b \u4e0d\u51fa\u3002\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u5c45\u7136\u8fde\u773c\u775b\u90fd\u6ca1\u6709\u7728\uff0c\u53cd\u800c\u5927\u7b11\u8d77\u6765\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u539f\u6765\u4f60\u4e5f\u559c\u6b22\u8ddf\u5154\u5b50\u4ea4\u670b \u53cb\uff0c\u6765\uff0c\u6765\u8ddf\u6211\u4e00\u8d77\u8d70\u3002\u201d \u3000\u3000\u4ed6\u8df3\u8fc7\u6765\u5c31\u60f3\u62c9\u695a\u7559\u9999\u7684\u624b\u3002 \u3000\u3000\u4f46\u695a\u7559\u9999\u8fd9\u6b21\u53ef\u4e0d\u518d\u4e0a\u5f53\u4e86\uff0c\u4e00\u95ea\u8eab\uff0c\u5df2\u8f6c\u5230\u4ed6\u80cc\u5f8c\uff0c\u7b11\u9053\uff1a\u201c\u6211\u65e2\u6ca1\u6709\u6740\u4eba\u4e5f\u4e0d\u5fc5\u88c5 \u75af\uff0c\u4e3a\u4ec0\u9ebd\u8981\u8ddf\u5154\u5b50\u4ea4\u670b\u53cb\uff1f\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u7b11\u5676\u5676\u9053\uff1a\u201c\u4f60\u5728\u8bf4\u4ec0\u9ebd\uff0c\u6211\u4e0d\u61c2\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u773c\u775b\u77aa\u7740\u4ed6\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u4f60\u5df2\u7528\u4e0d\u7740\u518d\u88c5\u75af\uff0c\u6211\u5df2\u77e5\u9053\u4f60\u662f\u8c01\u4e86\u3002\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u5927\u7b11\u9053\uff1a\u201c\u4f60\u5f53\u7136\u77e5\u9053\u6211\u662f\u8c01\uff0c\u6211\u662f\u859b\u5bb6\u7684\u4e8c\u5c11\u7237\uff0c\u5929\u4e0b\u7b2c\u4e00\u4e2a\u5929\u624d\u513f\u7ae5\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u9664\u6b64\u4e4b\u5916\uff0c\u4f60\u8fd8\u662f\u5929\u4e0b\u7b2c\u4e00\u53f7\u7684\u51b7\u8840\u51f6\u624b\u3002\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u7b11\u9053\uff1a\u201c\u51f6\u624b\uff1f\u4ec0\u9ebd\u51f6\u624b\uff1f\u96be\u9053\u6211\u968f\u624b\u5f88\u51f6\u9ebd\uff1f\u6211\u770b\u5012\u4e00\u70b9\u4e5f\u4e0d\u51f6\u5440\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u4e5f\u4e0d\u7406\u4ed6\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4f60\u8d70\u8fdb\u8fd9\u5c4b\u5b50\uff0c\u5c31\u7acb\u523b\u77e5\u9053\u6709\u4eba\u6765\u8fc7\u4e86\uff0c\u56e0\u4e3a\u4f60\u7684\u4e1c\u897f\u770b \u6765\u867d\u653e\u5f97\u4e71\u4e03\u516b\u7cdf\uff0c\u5176\u5b9e\u522b\u4eba\u53ea\u8981\u52a8\u4e00\u52a8\uff0c\u4f60\u7acb\u523b\u5c31\u77e5\u9053\u3002\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u5927\u7b11\u9053\uff1a\u201c\u4f60\u82e5\u5230\u6211\u5154\u5b50\u670b\u53cb\u7684\u6d1e\u91cc\u53bb\u8fc7\uff0c\u5b83\u4eec\u4e5f\u7acb\u523b\u5c31\u4f1a\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u7b97\u51c6\u9664\u4e86\u6211\u4e4b\u5916\uff0c\u7edd\u6ca1\u6709\u4eba\u6000\u7591\u5230\u4f60\uff0c\u6240\u4ee5\u4f60\u53d1\u73b0\u6709\u4eba\u8fdb\u6765\u8fc7\uff0c\u5c31\u7acb\u523b \u60f3\u5230\u662f\u6211\u3002\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u9053\uff1a\u201c\u8fd9\u53ea\u56e0\u4e3a\u6211\u65e9\u5df2\u77e5\u9053\u4f60\u4e0d\u4f46\u662f\u9a97\u5b50\uff0c\u8fd8\u662f\u5c0f\u5077\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u8fd9\u5c4b\u5b50\u770b\u6765\u867d\u7136\u50cf\u662f\u4e2a\u75af\u5b50\u4efb\u7684\u5730\u65b9\uff0c\u5176\u5b9e\u8fd8\u6709\u5f88\u591a\u7834\u7efd\uff0c\u662f\u4e07\u4e07\u7792\u4e0d \u8fc7\u660e\u773c\u4eba\u7684\u3002\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u9053\uff1a\u201c\u4f60\u662f\u660e\u773c\u4eba\u9ebd\uff0c\u6211\u770b\u4f60\u773c\u775b\u975e\u4f46\u4e0d\u660e\uff0c\u8fd8\u6709\u4e9b\u53d1\u7ea2\uff0c\u6709\u70b9\u50cf\u6211\u7684\u514d\u5b50\u670b\u53cb \u54e9\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u8fd9\u5c4b\u4e88\u5c31\u50cf\u662f\u4e66\u751f\u7684\u4e66\u658b\uff0c\u4f60\u770b\u4f60\u628a\u4e66\u5806\u5f97\u4e71\u4e03\u516b\u7cdf\uff0c\u5176\u5b9e\u5374\u81ea\u6709\u6761\u7406\uff0c \u552f\u4e00\u4e0d\u540c\u7684\u662f\u8fd9\u91cc\u5b9e\u5728\u6bd4\u4e66\u751f\u7684\u4e66\u658b\u4e7e\u51c0\u591a\u3002\u4ed6\u8ddf\u968f\u4e00\u8f6c\uff0c\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4ee5\u5f8c\u82e5\u8fd8\u60f3\u88c5 \u75af\uff0c\u6700\u597d\u53bb\u5f04\u4e9b\u725b\u7caa\u72d7\u5c3f\uff0c\u6d12\u5f84\u8fd9\u5c4b\u5b50\u91cc\uff0c\u7528\u7684\u7c89\u4e5f\u5207\u5207\u4e0d\u53ef\u5982\u6b64\u8003\u7a76\uff0c\u7528\u4e9b\u5899\u58c1\u7070\u6d82\u4e0a\u4e5f \u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u62cd\u624b\u7b11\u9053\uff1a\u201c\u96be\u602a\u4f60\u7684\u8138\u8fd9\u9ebd\u767d\u539f\u6765\u4f60\u6d82\u5899\u7070\u3002\u3002 \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6700\u91cd\u8981\u7684\u662f\uff0c\u4f60\u4e0d\u8be5\u5c06\u90a3\u4e9b\u8863\u670d\u653e\u5728\u9601\u697c\u4e0a\u3002\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u7728\u4e86\u7728\u773c\uff0c\u9053\uff1a\u201c\u8863\u670d\uff1f\u4ec0\u9ebd\u8863\u670d\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5c31\u662f\u4f60\u81f3\u6740\u4eba\u65f6\u7684\u8863\u670d\u3002\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u7a81\u7136\u201c\u683c\u683c\u201d\u7684\u7b11\u4e86\u8d77\u6765\uff0c\u4f46\u76ee\u4e2d\u5374\u5df2\u8fde\u534a\u5206\u7b11\u610f\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u695a\u7559\u9999\u76ef\u4f4f\u4ed6\u7684\u773c\u775b\u9053\uff1a\u201c\u4f60\u77e5\u9053\u6211\u5df2\u53d1\u73b0\u4e86\u8fd9\u4e9b\u4e8b\u3002\u77e5\u9053\u4f60\u7684\u79d8\u5bc6\u8fdf\u65e9\u603b\u4f1a\u88ab\u9f99\u63ed \u7a7f\uff0c\u6240\u4ee5\u5c31\u60f3\u8d76\u5feb\u4e00\u6e9c\u4e86\u4e4b\u3002\u4f46\u8fd9\u6b21\u6211\u53c8\u600e\u4f1a\u8ba9\u4f60\u6e9c\u8d70\uff1f\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u8d8a\u7b11\u8d8a\u5389\u5bb3\uff0c\u5230\u5f8c\u6765\u5c45\u7136\u7b11\u5f97\u6ee1\u5730\u6253\u6eda\u3002\u600e\u5948\u695a\u7559\u9999\u7684\u773c\u775b\u4e00\u76f4\u76ef\u7740\u4ed6\u65e0\u8bba\u4ed6\u6eda \u5230\u54ea\u91cc\u90fd\u518d\u4e5f\u4e0d\u80af\u653e\u677e\u3002 \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u521d\u89c1\u4f60\u7684\u65f6\u5019\uff0c\u867d\u89c9\u6709\u4e9b\u5947\u602a\uff0c\u5374\u8fd8\u6ca1\u6709\u60f3\u5230\u4f60\u5c31\u662f\u90a3\u51b7\u8840\u7684\u51f6\u624b\uff0c\u4f60 \u82e5\u4e0d\u662f\u90a3\u9ebd\u6025\u7740\u6740\u6211\uff0c\u6211\u4e5f\u8bb8\u6c38\u8fdc\u90fd\u60f3\u4e0d\u5230\u3002\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u5728\u5730\u4e0a\u6eda\u7740\u7b11\u9053\uff1a\u201c\u522b\u4eba\u90fd\u8bf4\u6211\u662f\u75af\u5b50\uff0c\u53ea\u6709\u4f60\u8bf4\u6211\u4e0d\u75af\uff0c\u4f60\u771f\u662f\u4e2a\u597d\u4eba\u3002\u201d \u3000\u3000\u4ed6\u6eda\u5230\u695a\u7559\u9999\u9762\u524d\u695a\u7559\u9999\u7acb\u523b\u53c8\u9000\u5f97\u5f88\u8fdc\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u5230\u5f8c\u6765\u4f60\u4e5f\u77e5\u9053\u8981\u6740\u6211\u5e76\u4e0d\u662f\u4ef6 \u5bb9\u6613\u7684\u4e8b\uff0c\u6240\u4ee5\u4f60\u624d\u60f3\u5ac1\u7978\u65bc\u6211\uff0c\u60f3\u501f\u4f60\u5144\u957f\u7684\u5229\u5251\u6765\u8981\u6211\u7684\u547d\u3002\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u867d\u8fd8\u52c9\u5f3a\u5728\u7b11\u4f46\u5df2\u6e10\u6e10\u7b11\u4e0d\u51fa\u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u65bc\u662f\u4f60\u5c31\u5148\u53bb\u76d7\u5251\u3002\u518d\u6765\u884c\u523a\u3002\u859b\u5bb6\u5e84\u6bcf\u4e00\u5c3a\u5730\u4f60\u90fd\u4e86\u5982\u6307\u638c\uff0c\u4f60\u81ea\u7136\u53ef \u4ee5\u6765\u53bb\u81ea\u5982\uff0c\u8c01\u4e5f\u6293\u4e0d\u4f4f\u4f60\u3002\u201d \u3000\u3000\u4ed6\u7b11\u4e86\u7b11\u63a5\u7740\u9053\uff1a\u201c\u5c24\u5176\u90a3\u6247\u95e8\uff0c\u522b\u4eba\u6293\u523a\u5ba2\u7684\u65f6\u5019\uff0c\u4f60\u5f80\u90a3\u6247\u95e8\u6e9c\u8d70\uff0c\u6e9c\u56de\u81ea\u5df1\u7684\u5c4b \u91cc\uff0c\u7b49\u522b\u4eba\u4e0d\u6ce8\u610f\u65f6\uff0c\u518d\u5077\u5077\u8fc7\u53bb\u5c06\u9501\u9501\u4e0a\uff0c\u4f60\u660e\u77e5\u5c31\u7b97\u88ab\u4eba\u77a7\u89c1\uff0c\u4e5f\u6ca1\u6709\u4ec0\u9ebd\u5173\u7cfb\uff0c\u56e0\u4e3a \u8c01\u4e5f\u4e0d\u4f1a\u6ce8\u610f\u5230\u4f60\uff0c\u5728\u522b\u4eba\u773c\u4e2d\uff0c\u4f60\u53ea\u4e0d\u8fc7\u662f\u4e2a\u65e0\u8db3\u8f7b\u91cd\u7684\u75af\u5b50\uff0c\u8fd9\u5c31\u662f\u4f60\u7684\u2018\u9690\u8eab\u6cd5\u3002\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u970d\u7136\u6218\u4e86\u8d77\u6765\uff0c\u76ef\u7740\u695a\u7559\u9999\u3002 \u3000\u3000\u695a\u7559\u9999\u6de1\u6de1\u9053\uff1a\u201c\u4f60\u7684\u786e\u662f\u4e2a\u806a\u660e\u4eba\uff0c\u6bcf\u4ef6\u4e8b\u90fd\u8bbe\u8ba1\u5f97\u5929\u8863\u65e0\u7f1d\uff0c\u8ba9\u8c01\u4e5f\u4e0d\u4f1a\u731c\u5230\u4f60\uff0c \u859b\u5bb6\u5e84\u4e8c\u5c11\u7237\uff0c\u859b\u8863\u4eba\u7684\u4eb2\u5f1f\u5f1f\uff0c\u5c45\u7136\u4f1a\u505a\u7528\u94b1\u4e70\u5f97\u5230\u7684\u523a\u5ba2\uff0c\u5c45\u7136\u4f1a\u4e3a\u94b1\u53bb\u6740\u4eba\u8fd9\u8bdd\u5c31\u7b97 \u8bf4\u51fa\u6765\uff0c\u53ea\u6015\u4e5f\u6ca1\u6709\u4eba\u76f8\u4fe1\u3002\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u7a81\u53c8\u5927\u7b11\u8d77\u6765\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u859b\u4e8c\u516c\u5b50\u4f1a\u4e3a\u4e86\u94b1\u800c\u6740\u4eba\u9ebd\uff1f\u8fd9\u7b80\u76f4\u8352\u5510\u5df2\u6781\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e00\u70b9\u4e5f\u4e0d\u8352\u5510\u56e0\u4e3a\u4f60\u6740\u4eba\u5e76\u975e\u771f\u7684\u4e3a\u4e86\u94b1\uff0c\u800c\u662f\u4e3a\u4e86\u6743\u529b\u4e3a\u4e86\u8865\u507f\u4f60\u6240\u53d7 \u7684\u6c14\u3002\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u9053\uff1a\u201c\u6211\u53d7\u7684\u6c14\uff1f\u53d7\u4e86\u8c01\u7684\u6c14\uff1f\u201d \u3000\u3000\u4ed6\u9762\u4e0a\u4f3c\u4e4e\u8d77\u4e86\u79cd\u96be\u8a00\u7684\u53d8\u5316\uff0c\u6574\u5f20\u8138\u90fd\u626d\u66f2\u4e86\u8d77\u6765\uff0c\u201c\u683c\u683c\u201d\u7b11\u9053\uff1a\u201c\u8c01\u4e0d\u77e5\u9053\u6211\u5927 \u54e5\u662f\u5929\u4e0b\u7b2c\u4e00\u5251\u5ba2\uff0c\u8c01\u6562\u53eb\u6211\u53d7\u6c14\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u8f7b\u8f7b\u53f9\u606f\u4e86\u58f0\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u4ee4\u5144\u662f\u5929\u4e0b\u7b2c\u4e00\u5251\u5ba2\uff0c\u6240\u4ee5\u4f60\u624d\u4f1a\u843d\u5230\u8fd9\u5730\u6b65\u3002\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u672c\u6765\u65e2\u806a\u660e\uff0c\u53c8\u6709\u624d\u6c14\u3002\u6b66\u529f\u4e4b\u9ad8\uff0c\u66f4\u53ef\u8bf4\u662f\u6b66\u6797\u5c11\u89c1\u7684\u9ad8\u624b\u4ee5\u4f60\u7684\u6b66 \u529f\u548c\u624d\u6c14\u672c\u53ef\u5728\u6b66\u6797\u4e2d\u4eab\u5927\u540d\uff0c\u53ea\u53ef\u501f\u2026\u3002\u201d \u3000\u3000\u4ed6\u957f\u53f9\u4e86\u58f0\uff0c\u7f13\u7f13\u63a5\u7740\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u4f60\u662f\u859b\u8863\u4eba\u7684\u5f1f\u5f1f\u3002\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u7684\u5634\u89d2\u7a81\u7136\u5267\u70c8\u7684\u98a4\u6296\u8d77\u6765\uff0c\u5c31\u597d\u50cf\u88ab\u4eba\u5728\u8138\u4e0a\u62bd\u4e86\u4e00\u97ad\u5b50\u3002 \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u6240\u6709\u7684\u6210\u5c31\uff0c\u90fd\u5df2\u88ab\u5929\u4e0b\u7b2c\u4e00\u5251\u5ba2\u7684\u5149\u8363\u6240\u63a9\u6ca1\uff0c\u65e0\u8bba\u4f60\u505a\u4e86\u4ec0\u9ebd \u4e8b\uff0c\u522b\u4eba\u90fd\u4e0d\u4f1a\u5411\u4f60\u559d\u91c7\uff0c\u53ea\u4f1a\u5411\u2018\u5929\u4e0b\u7b2c\u4e00\u5251\u5ba2\u4e4b\u5f1f\u2019\u559d\u91c7\uff0c\u4f60\u82e5\u6709\u6240\u6210\u5c31\uff0c\u90a3\u662f\u5e94\u8be5 \u7684\uff0c\u56e0\u4e3a\u4f60\u662f\u5929\u4e0b\u7b2c\u4e00\u5251\u5ba2\u7684\u5f1f\u5f1f\uff0c\u4f60\u82e5\u5076\u800c\u505a\u9519\u4e86\u4e00\u4ef6\u4e8b\u90a3\u5c31\u4f1a\u53d8\u5f97\u7f6a\u5927\u6076\u6781\u3002\u56e0\u4e3a\u5927\u5bb6 \u90fd\u4f1a\u89c9\u5f97\u4f60\u4e22\u4e86\u4f60\u54e5\u54e5\u7684\u4eba\u3002\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u5168\u8eab\u90fd\u53d1\u8d77\u6296\u6765\u3002 \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u82e5\u662f\u6362\u4e86\u522b\u4eba\uff0c\u4e5f\u8bb8\u5c31\u6b64\u5411\u547d\u8fd0\u4f4e\u5934\uff0c\u751a\u81f3\u5c31\u6b64\u6d88\u6c89\u3002\u4f46\u4f60\u5374\u662f\u4e0d\u80af\u8ba4\u8f93 \u7684\u4eba\uff0c\u600e\u5948\u7684\u4e5f\u77e5\u9053\u4f60\u7684\u6210\u5c31\u6c38\u8fdc\u65e0\u6cd5\u80dc\u8fc7\u4f60\u7684\u54e5\u54e5\u3002\u201d \u3000\u3000\u4ed6\u957f\u957f\u53f9\u606f\u4e86\u58f0\u6447\u5934\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u4f60\u8d70\u7684\u90a3\u6761\u8def\u8d70\u9519\u4e86\u3002\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u4f3c\u4e4e\u60f3\u8bf4\u4ec0\u9ebd\uff0c\u5374\u4ec0\u9ebd\u4e5f\u6ca1\u6709\u8bf4\u3002 \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u8fd9\u81ea\u7136\u4e5f\u56e0\u4e3a\u4f60\u54e5\u54e5\u4ece\u5c0f\u5bf9\u4f60\u671f\u671b\u592a\u6df1\uff0c\u7ea6\u675f\u4f60\u592a\u4e25\uff0c\u7231\u4e4b\u6df1\u4fbf\u672a\u514d\u8d23\u4e4b \u5207\uff0c\u6240\u4ee5\u4f60\u624d\u60f3\u53cd\u6297\uff0c\u4f46\u4f60\u4e5f\u77e5\u9053\u5728\u4f60\u54e5\u54e5\u7684\u7ea6\u675f\u4e0b\uff0c\u6839\u672c\u5c31\u4e0d\u80fd\u5984\u52a8\uff0c\u6240\u4ee5\u4f60\u624d\u60f3\u51fa\u4e86 \u2018\u88c5\u75af\u2019\u8fd9\u4e2a\u5999\u6cd5\u5b50\uff0c\u8ba9\u522b\u4eba\u5bf9\u4f60\u4e0d\u518d\u6ce8\u610f\uff0c\u8ba9\u522b\u4eba\u5bf9\u4f60\u5931\u671b\uff0c\u4f60\u624d\u597d\u81ea\u7531\u81ea\u5728\uff0c\u505a\u4f60\u60f3\u505a \u7684\u4e8b\u3002\u201d \u3000\u3000\u4ed6\u671b\u7740\u859b\u5b9d\u5b9d\uff0c\u76ee\u4e2d\u5145\u6ee1\u4e86\u60cb\u60dc\u4e4b\u610f\u3002 \u3000\u3000\u859b\u5b9d\u5b9d\u7a81\u53c8\u72c2\u7b11\u4e86\u8d77\u6765\uff0c\u6307\u7740\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u60f3\u5f97\u5f88\u5999\uff0c\u8bf4\u5f97\u66f4\u5999\uff0c\u53ef\u60dc\u8fd9\u53ea\u4e0d\u8fc7\u662f\u4f60 \u5728\u81ea\u8bf4\u81ea\u8bdd\u800c\u5df1\uff0c\u4f60\u7740\u8ba4\u4e3a\u6211\u5c31\u662f\u90a3\u523a\u5ba2\u7ec4\u7ec7\u7684\u4e3b\u4f7f\u4eba\u81f3\u5c11\u4e5f\u5f97\u6709\u771f\u51ed\u5b9e\u636e\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u8981\u8bc1\u636e\uff1f\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u5389\u58f0\u9053\uff1a\u201c\u4f60\u82e5\u62ff\u4e0d\u51fa\u8bc1\u636e\u6765\u5c31\u662f\u542b\u8840\u55b7\u4eba\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u597d\u4f60\u8981\u8bc1\u636e\uff0c\u6211\u5c31\u62ff\u8bc1\u636e\u7ed9\u4f60\u770b\u3002\u201d \u3000\u3000\u4ed6\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u81ea\u6000\u4e2d\u5c06\u90a3\u94c1\u9501\u62ff\u4e86\u51fa\u6765\u6258\u5728\u624b\u4e0a\uff0c\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u8bc1\u636e\u3002\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u7b97\u4ec0\u9ebd\u8bc1\u636e\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u8fd9\u628a\u9501\u5c31\u662f\u90a3\u95e8\u4e0a\u7684\u9501\uff0c\u5df2\u6709\u8bb8\u4e45\u672a\u66fe\u88ab\u4eba\u52a8\u8fc7\uff0c\u53ea\u6709\u90a3\u523a\u5ba2\u524d\u5929\u66fe\u7ecf\u5f00 \u8fc7\u8fd9\u628a\u9501\uff0c\u662f\u9ebd\uff1f\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u95ed\u7d27\u4e86\u5634\uff0c\u76ee\u4e2d\u5145\u6ee1\u4e86\u60ca\u5ea6\u4e4b\u8272\uff0c\u663e\u7136\u4ed6\u8fd8\u731c\u4e0d\u900f\u695a\u7559\u9999\u53c8\u5728\u73a9\u4ec0\u9ebd\u82b1\u6837\uff0c\u4ed6\u51b3 \u5fc3\u4e0d\u518d\u4e0a\u5f53\u3002 \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5f00\u9501\u7684\u4eba\uff0c\u5fc5\u5b9a\u4f1a\u5728\u9501\u4e0a\u7559\u4e0b\u624b\u5370\u3002\u8fd9\u628a\u9501\u6700\u8fd1\u65e2\u7136\u53ea\u6709\u90a3\u523a\u5ba2\u5f00\u8fc7\u6240\u4ee5 \u9501\u4e0a\u672c\u8be5\u53ea\u6709\u90a3\u523a\u5ba2\u7684\u624b\u5370\uff0c\u662f\u9ebd\uff1f\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u7684\u5634\u95ed\u5f97\u66f4\u7d27\u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f46\u73b0\u5728\u8fd9\u628a\u9501\u4e0a\u5374\u53ea\u6709\u4f60\u7684\u624b\u5370\u3002\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u7ec8\u65bc\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u624b\u5370\uff1f\u4ec0\u9ebd\u624b\u5370\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u5fae\u7b11\u9053\uff1a\u201c\u4eba\u4e3a\u4e07\u7269\u4e4b\u7075\uff0c\u4e0a\u5929\u9020\u4eba\uff0c\u7684\u786e\u5947\u5999\u5f97\u5f88\uff0c\u4f60\u6211\u867d\u540c\u6837\u662f\u4eba\uff0c\u4f46\u4f60\u6211 \u7684\u9762\u8c8c\u8eab\u6750\uff0c\u5374\u7edd\u4e0d\u76f8\u540c\uff0c\u4e16\u4e0a\u4e5f\u7edd\u6ca1\u6709\u4e24\u4e2a\u9762\u8c8c\u5b8c\u5168\u76f8\u540c\u7684\u4eba\u3002\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u8fd8\u662f\u6293\u4e0d\u51c6\u4ed6\u7a76\u7adf\u8981\u8bf4\u4ec0\u9ebd\u3002 \u3000\u3000\u695a\u7559\u9999\u4f38\u51fa\u4e86\u624b\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u770b\uff0c\u6bcf\u4e2a\u4eba\u624b\u4e0a\u90fd\u6709\u638c\u7eb9\uff0c\u6307\u4e0a\u4e5f\u6709\u6307\u7eb9\uff0c\u4f46\u6bcf\u4e2a\u4eba\u638c\u7eb9 \u548c\u6307\u7eb9\u4e5f\u7edd\u4e0d\u76f8\u540c\u7684\u3002\u4e16\u4e0a\u66f4\u6ca1\u6709\u4e24\u4e2a\u638c\u7eb9\u5b8c\u5168\u76f8\u540c\u7684\u4eba\uff0c\u4f60\u82e5\u4ed4\u7ec6\u7814\u7a76\uff0c\u5c31\u4f1a\u53d1\u89c9\u8fd9\u662f\u4ef6 \u5f88\u6709\u8da3\u7684\u4e8b\uff0c\u53ea\u53ef\u60dc\u8c01\u4e5f\u6ca1\u6709\u7559\u610f\u8fc7\u8fd9\u4ef6\u4e8b\u9762\u5df2\u3002\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u8d8a\u542c\u8d8a\u89c9\u5f97\u8ff7\u7cca\uff0c\u4eba\u4eec\u9762\u5bf9\u7740\u81ea\u5df1\u4e0d\u61c2\u7684\u4e8b\uff0c\u603b\u4f1a\u4f5c\u51fa\u4e00\u79cd\u50b2\u7136\u4e0d\u5c51\u4e4b\u6001\uff0c\u859b\u5b9d \u5b9d\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u4e9b\u8bdd\u53ea\u80fd\u9a97\u9a97\u53c1\u5c81\u5b69\u5b50\uff0c\u5374\u9a97\u4e0d\u4e86\u6211\u3002\u201d \u3000\u3000\u4ed6\u5634\u91cc\u8fd9\u9ebd\u8bf4\uff0c\u4e24\u53ea\u624b\u5374\u5df2\u4e0d\u7531\u81ea\u4e3b\u85cf\u81f3\u80cc\u5f8c\u3002 \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u73b0\u5728\u4f60\u518d\u5c06\u624b\u85cf\u8d77\u6765\u4e5f\u6ca1\u6709\u7528\u4e86\u3002\u56e0\u4e3a\u6211\u5df2\u68c0\u67e5\u4f60\u68b3\u5986\u53f0\u4e0a\u7684\u4e1c\u897f\u3002\u4e0a \u9762\u7684\u624b\u5370\u6b63\u548c\u8fd9\u628a\u9501\u4e0a\u7684\u624b\u5370\u4e00\u6837\uff0c\u53ea\u8981\u4e24\u4e0b\u4e00\u6bd4\uff0c\u4f60\u7684\u7f6a\u8bc1\u5c31\u6e05\u6e05\u695a\u695a\u7684\u4e86\u3002\u90a3\u662f\u8d56\u4e5f\u8d56 \u4e0d\u6389\u7684\u3002\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u53c8\u60ca\u53c8\u7591\u3002\u9762\u4e0a\u5df2\u4e0d\u7981\u53d8\u4e86\u989c\u8272\uff0c\u7a81\u7136\u53cd\u624b\u4e00\u626b\uff0c\u5c06\u68b3\u5986\u53f0\u4e0a\u7684\u4e1c\u897f\u5168\u90fd\u626b\u843d\u5728 \u5730\u3002 \u3000\u3000\u695a\u7559\u9999\u5927\u7b11\u9053\uff1a\u201c\u4f60\u770b\uff0c\u4f60\u8fd9\u4e0d\u662f\u505a\u8d3c\u5fc3\u865a\u662f\u4ec0\u9ebd\uff1f\u5c31\u53ea\u8fd9\u4ef6\u4e8b\uff0c\u5df2\u8db3\u591f\u8bc1\u660e\u4f60\u7684\u7f6a\u884c \u4e86\u3002\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u72c2\u543c\u9053\uff1a\u201c\u4f60\u8fd9\u5389\u9b3c\uff0c\u4f60\u7b80\u76f4\u4e0d\u662f\u4eba\uff0c\u6211\u65e9\u5c31\u8be5\u6740\u4e86\u4f60\u7684\u3002\u201d \u3000\u3000\u72c2\u543c\u58f0\u4e2d\uff0c\u4ed6\u5df2\u5411\u695a\u7559\u9999\u6251\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u7a81\u542c\u4e00\u4e2a\u4eba\u5927\u559d\u9053\uff1a\u201c\u4f4f\u624b\u3002\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u4e00\u60ca\uff0c\u5c31\u53d1\u73b0\u859b\u8863\u4eba\u5df2\u7ad9\u5728\u95e8\u53e3\u3002 \u3000\u3000\u859b\u8863\u4eba\u7684\u8138\u8272\u4e5f\u82cd\u767d\u5f97\u53ef\u6015\uff0c\u957f\u957f\u7684\u53f9\u606f\u7740\uff0c\u7f00\u7136\u9053\uff1a\u201c\u4e8c\u5f1f\uff0c\u4f60\u8fd8\u662f\u4e0a\u4e86\u4ed6\u7684\u5f53 \u4e86\u3002\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u6ee1\u5934\u51b7\u6c57\u76f4\u843d\uff0c\u7adf\u52a8\u4e5f\u4e0d\u6562\u52a8\uff0c\u201c\u957f\u5144\u4e3a\u7236\u201d\uff0c\u4ed6\u5bf9\u8fd9\u4f4d\u5927\u54e5\u81ea\u5c0f\u5c31\u5b58\u7740\u4e00\u4efd\u754f \u60e7\u4e4b\u5fc3\u3002 \u3000\u3000\u859b\u8863\u4eba\u53f9\u9053\uff1a\u201c\u695a\u7559\u9999\u8bf4\u7684\u9053\u7406\u5e76\u6ca1\u6709\u9519\uff0c\u6bcf\u4e2a\u4eba\u638c\u4e0a\u7684\u7eb9\u8def\u7684\u786e\u90fd\u7edd\u4e0d\u76f8\u540c\uff0c\u4eba\u624b\u63a5 \u89e6\u5230\u7269\u4ef6\uff0c\u4e5f\u6781\u53ef\u80fd\u4f1a\u7559\u4e0b\u624b\u5370\uff0c\u4f46\u8fd9\u53ea\u4e0d\u8fc7\u4ec5\u4ec5\u662f\u2018\u9053\u7406\u2019\u800c\u5df2\uff0c\u6b63\u5982\u6709\u4eba\u8bf4\u2018\u5929\u5706\u5730 \u65b9\u2019\uff0c\u4f46\u5374\u6c38\u8fdc\u65e0\u6cd5\u8bc1\u660e\u3002\u201d \u3000\u3000\u4ed6\u51dd\u89c6\u7740\u695a\u7559\u9999\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u9999\u5e05\u4f60\u4e5f\u6c38\u8fdc\u65e0\u6cd5\u8bc1\u660e\u8fd9\u79cd\u2018\u9053\u7406\u2019\u7684\uff0c\u662f\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u6478\u4e86\u6478\u9f3b\u5b50\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u8fd9\u4e9b\u9053\u7406\u5343\u767e\u5e74\u4ee5\u5f8c\u4e5f\u8bb8\u6709\u4eba\u80fd\u8bc1\u660e\uff0c\u73b0\u5728\u786e\u662f\u4e07\u4e07\u4e0d \u80fd\u3002\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u8fd9\u624d\u77e5\u9053\u76ee\u5df1\u6bd5\u7adf\u8fd8\u662f\u53c8\u4e0a\u4e86\u4ed6\u7684\u5f53\uff0c\u773c\u775b\u77aa\u7740\u695a\u7559\u9999\u3002\u4e5f\u4e0d\u77e5\u662f\u60b2\u662f\u6012\uff1f\u5fc3\u91cc \u66f4\u4e0d\u77e5\u662f\u4f55\u6ecb\u6627\u3002 \u3000\u3000\u859b\u8863\u4eba\u5ffd\u7136\u4e00\u7b11\u9053\uff1a\u201c\u4f46\u9999\u5e05\u4f60\u4e5f\u4e0a\u4e86\u6211\u4e00\u4e2a\u5f53\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u4e0a\u4e86\u4f60\u7684\u5f53\uff1f\u201d \u3000\u3000\u859b\u8863\u4eba\u5f90\u5f90\u9053\uff1a\u201c\u90a3\u523a\u5ba2\u7ec4\u7ec7\u7684\u9996\u9886\uff0c\u5176\u5b9e\u5e76\u4e0d\u662f\u4ed6\uff0c\u800c\u662f\u6211\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u8fd9\u624d\u771f\u7684\u5403\u4e86\u60ca\uff0c\u5931\u58f0\u9053\uff1a\u201c\u662f\u4f60\uff1f\u201d \u3000\u3000\u859b\u8863\u4eba\u4e2d\u5b57\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u6211\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6014\u4e86\u534a\u664c\u957f\u53f9\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4eec\u5144\u5f1f\u60c5\u6df1\uff0c\u6240\u4ee5\u4e0d\u60dc\u66ff\u4ed6\u53d7\u8fc7\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u6211\u8fd9\u4e0d\u8fc7\u662f\u4e0d\u5fcd\u8981\u4ed6\u66ff\u6211\u53d7\u8fc7\u800c\u5df2\u3002\u201d \u3000\u3000\u4ed6\u957f\u53f9\u7740\u63a5\u9053\uff1a\u201c\u4f60\u770b\uff0c\u8fd9\u5e84\u9662\u662f\u4f55\u7b49\u5e7f\u9614\uff0c\u5e84\u4e2d\u98df\u5ba2\u662f\u4f55\u7b49\u6daa\u7e41\u3002\u6211\u9000\u9690\u5df2\u6709\u6570\u5341 \u5e74\uff0c\u82e5\u6ca1\u6709\u4efd\u5916\u4e4b\u8d22\uff0c\u53c8\u5982\u4f55\u80fd\u7ef4\u6301\u5f97\u4e0b\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u8fd9\u2026\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u9053\uff1a\u201c\u6211\u65e2\u4e0d\u4f1a\u7ecf\u5546\u8425\u5229\uff0c\u4e5f\u4e0d\u4f1a\u6c42\u5b98\u6c42\u4ff8\uff0c\u66f4\u4e0d\u4f1a\u5077\u9e21\u6478\u72d7\uff0c\u6211\u552f\u4e00\u7cbe\u901a\u7684 \u4e8b\uff0c\u5c31\u662f\u4ee5\u53c1\u5c3a\u4e4b\u5251\uff0c\u53d6\u4eba\u9879\u4e0a\u5934\u9885\u3002\u201d \u3000\u3000\u4ed6\u51c4\u7ae3\u5b50\u5f1f\u4e30\u8863\u8db3\u98df\uff0c\u6211\u53ea\u6709\u4ee5\u522b\u4eba\u7684\u7272\u547d\u6362\u53d6\u94b1\u8d22\uff0c\u8fd9\u9053\u7406\u9999\u5e05\u4f60\u96be\u9053\u8fd8\u4e0d\u660e\u767d\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u8fd9\u4e00\u751f\u4e2d\uff0c\u4ece\u672a\u6bd4\u6b64\u65f6\u66f4\u89c9\u5f97\u60ca\u609f\u3001\u96be\u53d7\uff0c\u4ed6\u5446\u5446\u7684\u6014\u5728\u90a3\u91cc\uff0c\u800c\u4e14\u8fde\u4e00\u53e5\u8bdd\u90fd \u8bf4\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u859b\u8863\u4eba\u9ed8\u7136\u9053\uff1a\u201c\u6211\u4e8c\u5f1f\u4ed6\u4e3a\u4e86\u5bb6\u65cf\u7684\u5149\u8363\uff0c\u624d\u4e0d\u60dc\u66ff\u6211\u53d7\u8fc7\uff0c\u4e0d\u7136\u6211\u2026\u2026\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u7a81\u7136\u72c2\u543c\u7740\u9053\uff1a\u201c\u4f60\u83ab\u8981\u8bf4\u4e86\uff0c\u83ab\u8981\u518d\u8bf4\u4e86\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u5389\u58f0\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u5df2\u4e0e\u4f60\u65e0\u5173\uff0c\u6211\u81ea\u4f1a\u548c\u9999\u7684\u4f5c\u4e00\u4e86\u65ad\uff0c\u4f60\u8fd8\u4e0d\u5feb\u51fa\u53bb\u3002\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u54ac\u4e86\u54ac\u7259\uff0c\u54fc\u58f0\u9053\uff1a\u201c\u6211\u4ece\u5c0f\u4e00\u76f4\u542c\u4f60\u7684\u8bdd\uff0c\u4f60\u65e0\u8bba\u8981\u6211\u4f5c\u4ec0\u9ebd\uff0c\u6211\u4ece\u6765\u4e5f\u4e0d\u6562 \u8fdd\u6297\uff0c\u4f46\u662f\u8fd9\u6b21\u2026\u8fd9\u6b21\u6211\u518d\u4e5f\u4e0d\u542c\u4f60\u7684\u4e86\uff01\u201d \u3000\u3000\u859b\u8863\u4eba\u6012\u9053\uff1a\u201c\u4f60\u6562\uff01\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u9053\uff1a\u201c\u6211\u56db\u5c81\u7684\u65f6\u5019\uff0c\u4f60\u6559\u6211\u8bc6\u5b57\u3002\u516d\u5c81\u7684\u65f6\u6559\u6211\u5b66\u5251\uff0c\u65e0\u8bba\u4ec0\u9ebd\u4e8b\u90fd\u662f\u4f60\u6559\u6211 \u7684\uff0c\u6211\u8fd9\u4e00\u751f\u867d\u5df2\u88ab\u4f60\u538b\u5f97\u900f\u4e0d\u8fc7\u6c14\u6765\uff01\u4f46\u6211\u8fd8\u662f\u8981\u611f\u6fc0\u4f60\uff0c\u7b97\u6765\u8fd8\u662f\u6b20\u4f60\u5f88\u591a\uff01\u73b0\u5728\u4f60\u53c8 \u8981\u66ff\u6211\u53d7\u8fc7\u4e86\uff0c\u4f60\u6c38\u8fdc\u662f\u6709\u60c5\u6709\u4e49\u7684\u5927\u54e5\uff0c\u6211\u6c38\u8fdc\u662f\u4e0d\u77e5\u597d\u6b79\u7684\u5f1f\u5f1f\u2026\u3002\u201d \u3000\u3000\u8bf4\u7740\u8bf4\u7740\uff0c\u4ed6\u5df2\u6d95\u6cea\u4ff1\u6d41\u653e\u58f0\u75db\u54ed\uff0c\u5636\u7740\u7684\u558a\u9053\uff1a\u201c\u4f46\u4f60\u53c8\u600e\u77e5\u9053\u6211\u5b9a\u8981\u53d7\u4f60\u7684\u6069\u60e0\uff0c \u6211\u505a\u7684\u4e8b\u60c5\u6709\u6211\u81ea\u5df1\u8d1f\u62c5\uff0c\u7528\u4e0d\u7740\u4f60\u6765\u505a\u597d\u4eba\uff0c\u7528\u4e0d\u7740\u3002\u201d \u3000\u3000\u859b\u8863\u4eba\u9762\u8272\u5df2\u60e8\u53d8\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u2026\u2014\u201d \u3000\u3000\u859b\u5b9d\u5b9d\u4ef0\u9996\u5927\u547c\u9053\uff1a\u201c\u51f6\u624b\u662f\u6211\uff0c\u523a\u5ba2\u4e5f\u662f\u6211\uff0c\u6211\u6740\u7684\u4eba\u5df2\u4e0d\u8ba1\u5176\u6570\uff0c\u6211\u6b7b\u4e86\u4e5f\u5f88\u591f\u672c \u4e86\uff0c\u2014\u2026\u695a\u7559\u9999\u4f60\u4e3a\u4f55\u8fd8\u4e0d\u8fc7\u6765\u52a8\u624b\uff1f\u201d \u3000\u3000\u859b\u8863\u4eba\u4e5f\u6cea\u6d41\u6ee1\u76ee\uff0c\u54d1\u58f0\u9053\uff1a\u201c\u8fd9\u5168\u662f\u6211\u7684\u9519\uff0c\u6211\u7684\u786e\u5bf9\u4f60\u505a\u5f97\u592a\u8fc7\u4efd\u4e86\uff0c\u4e5f\u903c\u5f97\u4f60\u592a \u7d27\u3002\u9999\u5e05\u771f\u6b63\u7684\u7f6a\u9b41\u7978\u9996\u662f\u6211\u3002\u4f60\u6740\u4e86\u6211\u5427\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53ea\u89c9\u5f97\u9f3b\u5b50\u9178\u9178\u7684\u773c\u6cea\u51e0\u4e4e\u5486\u8981\u593a\u7736\u800c\u51fa\u3002 \u3000\u3000\u859b\u5b9d\u5b9d\u5389\u58f0\u9053\uff1a\u201c\u695a\u7559\u9999\uff0c\u4f60\u8fd8\u5047\u6148\u60b2\u4ec0\u9ebd\uff1f\u2026\u597d\uff0c\u4f60\u4e0d\u52a8\u624b\uff0c\u6211\u81ea\u5df1\u6765\u3002\u3002\u3002\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\u7a81\u7136\u62bd\u51fa\u4e00\u67c4\u5df4\u9996\uff0c\u53cd\u624b\u523a\u5411\u81ea\u5df1\u7684\u54bd\u5589\u3002 \u3000\u3000\u8bed\u58f0\u7a81\u7136\u65ad\u7edd\u3002 \u3000\u3000\u859b\u8863\u4eba\u60ca\u547c\u7740\u5954\u8fc7\u53bb\u5df2\u6765\u4e0d\u53ca\u4e86\u3002 \u3000\u3000\u9c9c\u8840\u7bad\u4e00\u8dcb\u98de\u6e85\u5230\u4ed6\u80f8\u819b\u4e0a\uff0c\u518d\u6b21\u67d3\u7ea2\u4e86\u4ed6\u7684\u8863\u670d\u3002 \u3000\u3000\u4f46\u8fd9\u6b21\u5374\u662f\u4ed6\u5f1f\u5f1f\u7684\u8840 \u3000\u3000\u8fd9\u4ef6\u8863\u670d\u4ed6\u662f\u5426\u4f1a\u50cf\u4ee5\u524d\u6837\u7559\u4e0b\u6765\u5462\uff1f \u3000\u3000\u8840\u8863\u4eba\u5509\uff01\u859b\u8863\u4eba\u2026 \u7b2c\u5341\u4e00\u7ae0\u3000\u60c5\u6709\u6240\u953a>> \u53e4\u9f99\u300a\u695a\u7559\u9999\u7cfb\u5217\xb7\u9b3c\u604b\u4f20\u5947\u300b \u7b2c\u5341\u4e00\u7ae0\u3000\u60c5\u6709\u6240\u953a \u3000\u3000\u695a\u7559\u9999\u6162\u6162\u9000\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4e3a\u4e86\u8fd9\u523a\u5ba2\u7ec4\u7ec7\u7684\u9996\u9886\uff0c\u4ed6\u5df2\u4e0d\u77e5\u82b1\u4e86\u591a\u5c11\u5fc3\u8840\uff0c\u4e5f\u4e0d\u77e5\u9053\u8ffd\u8e2a\u4e86\u591a\u4e45\uff0c\u73b0\u5728\u4ed6\u603b\u7b97\u5fc3 \u613f\u5f97\u507f\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u5fc3\u91cc\u771f\u7684\u5f88\u9ad8\u5174\u9ebd\uff1f \u3000\u3000\u6df1\u79cb\u663c\u77ed\u3002\u66ae\u8272\u4f3c\u5df2\u5c06\u6765\u4e34\u3002 \u3000\u3000\u79cb\u98ce\u821e\u7740\u9ec4\u67de\u3002\u4f36\u4f70\u7684\u6854\u6838\u4e5f\u966a\u7740\u5728\u79cb\u98ce\u4e2d\u98a4\u52a8\u3002 \u3000\u3000\u695a\u7559\u9999\u81ea\u5730\u4e0a\u6361\u8d77\u4e86\u7247\u843d\u53f6\uff0c\u6014\u6014\u7684\u770b\u4e86\u8bb8\u4e45\uff0c\u53c8\u8f7b\u8f7b\u7684\u653e\u4e86\u4e0b\u53bb\u770b\u7740\u5b83\u88ab\u79cb\u98ce\u5377\u8d77\u3002 \u3000\u3000\u4ed6\u633a\u8d77\u80f8\uff0c\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u695a\u7559\u9999\u4e00\u8d70\u51fa\u859b\u5bb6\u5e84\u7684\u95e8\uff0c\u5c31\u5df2\u53d1\u73b0\u6709\u4e2a\u4eba\u8fdc\u8fdc\u8eb2\u5728\u6811\u5f8c\uff0c\u4e0d\u65f6\u8d3c\u5934\u8d3c\u8111\u7684\u5f80\u8fd9\u8fb9\u5077\u770b \u4e00\u773c\u3002 \u3000\u3000\u4ed6\u867d\u7136\u53ea\u9732\u51fa\u534a\u53ea\u773c\u775b\uff0c\u4f46\u695a\u7559\u9999\u4e5f\u5df2\u8ba4\u51fa\u4ed6\u662f\u8c01\u2026\u3002\u9664\u4e86\u5c0f\u79c3\u5b50\u5916\uff0c\u8c01\u6709\u8fd9\u9ebd\u79c3\u7684 \u5934\u3002 \u3000\u3000\u5c0f\u79c3\u5b50\u89c1\u695a\u7559\u9999\uff0c\u773c\u775b\u5c31\u772f\u4e86\u8d77\u6765\u3002\u695a\u7559\u9999\u5374\u597d\u50cf\u6839\u672c\u6ca1\u6709\u77bb\u89c1\u4ed6\uff0c\u5c0f\u79c3\u5b50\u6025\u5f97\u76f4\u64e6 \u6c57\uff0c\u76f4\u62db\u624b\uff0c\u695a\u7559\u9999\u8fd8\u662f\u4e0d\u7406\u3002\u53cd\u800c\u6545\u610f\u5f80\u53e6\u4e00\u8fb9\u8d70\uff0c\u5c0f\u79c3\u5b50\u95ea\u95ea\u7f29\u7f29\u5728\u5f8c\u9762\u8ddf\u7740\uff0c\u4e5f\u4e0d\u6562 \u51fa\u58f0\u62db\u547c\u3002 \u3000\u3000\u7f62\u5728\u522b\u4eba\u5bb6\u91cc\u653e\u5b8c\u4e86\u706b\uff0c\u5fc3\u603b\u662f\u6709\u4e9b\u865a\u7684\uff0c\u76f4\u7b49\u695a\u7559\u9999\u5df2\u8d70\u51fa\u5f88\u8fdc\uff0c\u5c0f\u79c3\u5b50\u624d\u6562\u8fc7\u53bb\uff0c \u7b11\u563b\u563b\u9053\uff1a\u201c\u4f60\u8001\u4eba\u5bb6\u7740\u518d\u4e0d\u51fa\u6765\uff0c\u53ef\u771f\u8981\u628a\u6211\u4eec\u6025\u6b7b\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6273\u7740\u8138\u3002\u9053\uff0c\u201c\u6211\u4e00\u70b9\u4e5f\u4e0d\u8001\uff0c\u4e5f\u7528\u4e0d\u7740\u4f60\u4eec\u7740\u6025\u3002\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u6014\u4e86\u6014\uff0c\u8d54\u7b11\u9053\uff1a\u201c\u695a\u9999\u5e05\u83ab\u975e\u5728\u751f\u6211\u4eec\u5144\u5f1f\u7684\u6c14\u9ebd\uff0c\u96be\u9053\u662f\u4e3a\u4e86\u6211\u4eec\u5144\u5f1f\u4e0d\u6562 \u8fdb\u53bb\u5e2e\u5fd9\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u51b7\u51b7\u9053\uff1a\u201c\u5e2e\u5fd9\u5012\u4e0d\u6562\uff0c\u53ea\u6c42\u4f60\u4eec\u4ee5\u5f8c\u83ab\u8981\u518d\u8ba4\u6211\u8fd9\u670b\u53cb\u5c31\u662f\u4e86\u3002\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u672c\u6765\u8fd8\u5728\u5077\u7740\u7b11\uff0c\u4e00\u542c\u5b8c\u8fd9\u53e5\u8bdd\uff0c\u8138\u4e0a\u7684\u7b11\u5bb9\u5ffd\u7136\u90fd\u7586\u5728\u90a3\u91cc\u4e86\u8fc7\u4e86\u534a\u664c\uff0c\u624d\u671f \u671f\u827e\u827e\u7684\u95ee\u9053\uff1a\u201c\u4e3a\u2026\u2026\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u867d\u7136\u4ec0\u9ebd\u6837\u7684\u670b\u53cb\u90fd\u6709\uff0c\u4f46\u6740\u4eba\u653e\u706b\u7684\u670b\u53cb\u5012\u662f\u6ca1\u6709\uff0c\u5c0f\u5c0f\u5e74\u7eaa\u5c31 \u5b66\u4f1a\u4e86\u6740\u4eba\u653e\u706b\u957f\u5927\u4e86\u90a3\u8fd8\u5f97\u4e86\u3002\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u7740\u6025\u9053\uff1a\u201c\u6211\u2026\u3002\uff0e\u6211\u4ece\u6765\u4e5f\u6ca1\u6709\u6740\u8fc7\u4eba\u54ea\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u653e\u706b\u5462\uff1f\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u82e6\u7740\u8138\u9053\uff1a\u201c\u90a3\u2026\uff0c\u90a3\u5012\u4e0d\u662f\u6ca1\u6709\u53ea\u4e0d\u8fc7\u2026\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\u600e\u6837\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e3a\u4e86\u6211\u624d\u653e\u7684\u706b\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u8138\u4e0a\u76f4\u6d41\u6c57\uff0c\u4e5f\u4e0d\u77e5\u662f\u8be5\u70b9\u5934\u8fd8\u662f\u6447\u5934\u3002 \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u4e3a\u6211\u653e\u4e86\u706b\uff0c\u6211\u5c31\u8be5\u611f\u6fc0\u4f60\uff0c\u662f\u4e0d\u662f\uff1f\u90a3\u9ebd\u4f60\u5c06\u6765\u82e5\u518d\u4e3a\u6211\u6740\u4eba\uff0c\u6211\u662f \u4e0d\u662f\u66f4\u5e94\u8be5\u611f\u6fc0\u4f60\uff1f\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u6025\u5f97\u51e0\u4e4e\u5df2\u5feb\u54ed\u4e86\u51fa\u6765\u3002 \u3000\u3000\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u653e\u706b\u70e7\u7684\u82e5\u662f\u6076\u4eba\u7684\u5c4b\u5b50\uff0c\u6740\u7684\u82e5\u662f\u6076\u4eba\uff0c\u867d\u7136\u5df2\u7ecf\u4e0d\u5e94\u8be5 \u4e86\uff0c\u5012\u662f\u60c5\u6709\u53ef\u539f\uff0c\u70e7\u7684\u82e5\u662f\u597d\u4eba\u7684\u5c4b\u5b50\uff0c\u6740\u7684\u82e5\u662f\u597d\u4eba\uff0c\u90a3\u9ebd\u4f60\u65e0\u8bba\u4e3a\u4e86\u8c01\u90fd\u4e0d\u884c\uff0c\u65e0\u8bba \u4ec0\u9ebd\u7406\u7531\u90fd\u8bb2\u4e0d\u901a\uff0c\u4f60\u660e\u767d\u9ebd\uff1f\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u62fc\u547d\u70b9\u5934\uff0c\u773c\u6cea\u5df2\u6d41\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u695a\u7559\u9999\u8138\u8272\u548c\u7f13\u4e0b\u6765\uff0c\u9053\uff1a\u201c\u4f60\u73b0\u5728\u5e74\u7eaa\u8fd8\u8f7b\u6211\u4e00\u5b9a\u8981\u4f60\u660e\u767d\u2018\u5927\u6587\u592b\u6709\u6240\u4e0d\u4e3a\u2019\u8fd9\u4e03 \u4e2a\u5b57\uff0c\u90a3\u5c31\u662f\u8bf4\uff0c\u6709\u4e9b\u4e8b\u4f60\u65e0\u8bba\u4e3a\u4e86\u4ec0\u9ebd\u7406\u7531\uff0c\u90fd\u7edd\u4e0d\u80fd\u505a\u7684\u3002\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u201c\u5495\u54af\u201d\u4e00\u58f0\u5c31\u8dea\u4e86\u4e0b\u6765\uff0c\u4e00\u628a\u773c\u6cea\uff0c\u4e00\u628a\u9f3b\u6ecb\u54e9\u58f0\u9053\uff1a\u201c\u6211\u660e\u767d\u4e86\uff0c\u4e0b\u6b21\u6211\u518d \u4e5f\u4e0d\u6562\u4e86\uff0c\u65e0\u8bba\u4e3a\u4e86\u4ec0\u9ebd\u539f\u56e0\uff0c\u6211\u90fd\u7edd\u4e0d\u505a\u574f\u4e8b\uff0c\u7edd\u4e0d\u6740\u4eba\u653e\u706b\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u8fd9\u624d\u5c55\u98a7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53ea\u8981\u4f60\u8bb0\u7740\u4eca\u5929\u7684\u8fd9\u53e5\u8bdd\uff0c\u4f60\u4e0d\u4f46\u662f\u6211\u7684\u597d\u670b\u53cb\uff0c\u8fd8\u662f\u6211 \u7684\u597d\u5144\u5f1f\u3002\u201d \u3000\u3000\u4ed6\u62c9\u8d77\u4e86\u5c0f\u79c3\u5b50\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u8981\u8bb0\u7740\uff0c\u7537\u4eba\u773c\u6cea\u8981\u5f80\u809a\u5b50\u91cc\u6d41\u3002\u9f3b\u6d95\u5374\u4e07\u4e07\u4e0d\u53ef\u541e\u5230\u809a \u5b50\u91cc\u53bb\u3002\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u5fcd\u4e0d\u4f4f\u7b11\u4e86\u3002\u4ed6\u4e0d\u7b11\u8fd8\u597d\u4e00\u7b11\u8d77\u6765\u9669\u4e9b\u771f\u7684\u5c06\u9f3b\u6d95\u541e\u4e86\u4e0b\u53bb\u8d76\u7d27\u7528\u529b\u5438\uff0c\u5168\u90e8\u9f3b \u6d95\u201c\u547c\u5582\u201d\u4e00\u58f0\u5c31\u53c8\u7f29\u4e86\u56de\u53bb\u3002 \u3000\u3000\u695a\u7559\u9999\u51fa\u5fcd\u4e0d\u4f4f\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u8fd8\u6709\u8fd9\u9ebd\u6837\u2014\u624b\u5185\u529f\u7edd\u6280\u3002\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u7ea2\u7740\u8138\u5403\u5403\u7b11\u9053\uff1a\u201c\u5c0f\u9ebb\u5b50\u4e5f\u603b\u60f3\u5b66\u6211\u8fd9\u4e00\u624b\uff0c\u5374\u603b\u662f\u5b66\u4e0d\u4f1a\u9f3b\u6d95\u5f04\u5f97\u6ee1\u8138\u90fd \u662f\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4ed6\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u9053\uff1a\u201c\u4ed6\u966a\u7740\u4e00\u4e2a\u4eba\u5728\u90a3\u8fb9\u7b49\u9999\u5e05\u3002\u73b0\u5728\u6015\u5df2\u7b49\u6025\u6b7b\u3002\u201d \u3000\u3000\u5c0f\u9ebb\u5b50\u679c\u7136\u5df2\u6025\u6b7b\u4e86\u3002\u4f46\u4ed6\u966a\u7740\u7684\u90a3\u4e2a\u4eba\u5374\u66f4\u6025\uff0c\u8fde\u695a\u7559\u9999\u90fd\u672a\u60f3\u5230\u5c31\u662f\u859b\u658c\u7684\u4e66\u7ae5\u501a \u5251\u3002 \u3000\u3000\u501a\u5251\u4e00\u89c1\u4e86\u695a\u7559\u9999\uff0c\u5c31\u8981\u62dc\u5012\u3002 \u3000\u3000\u695a\u7559\u9999\u5f53\u7136\u63a5\u5f80\u4e86\u4ed6\uff0c\u7b11\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u672c\u6765\u5c31\u8ba4\u8bc6\u7684\uff1f\u201d \u3000\u3000\u5c0f\u9ebb\u5b50\u62a2\u7740\u9053\uff1a\u201c\u6211\u4eec\u8981\u4e0d\u8ba4\u5f97\u4ed6\uff0c\u4eca\u5929\u8bf4\u4e0d\u5b9a\u5c31\u60e8\u4e86\uff0c\u82e5\u4e0d\u662f\u4ed6\u653e\u4e86\u6211\u4eec\u4e00\u9a6c\uff0c\u521a\u6211 \u4eec\u5c31\u672a\u5fc5\u80fd\u9003\u5f97\u4e86\u3002\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u542c\u4ed6\u53c8\u8981\u8bf4\u653e\u706b\u7684\u4e8b\u8d76\u7d27\u5c06\u4ed6\u62c9\u5230\u4e00\u8fb9\u3002 \u3000\u3000\u501a\u5251\u606d\u58f0\u9053\uff1a\u201c\u9999\u5e05\u7684\u610f\u601d\uff0c\u5c0f\u4eba\u5df2\u8f6c\u544a\u7ed9\u4e8c\u516c\u5b50\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4ed6\u7684\u610f\u601d\u5462\uff1f\u201d \u3000\u3000\u501a\u5251\u9053\uff1a\u201c\u4e8c\u516c\u5b50\u4e5f\u5df2\u4e45\u5e55\u9999\u5e05\u4fa0\u540d\uff0c\u6b64\u523b\u53ea\u6015\u5df2\u5728\u90a3\u8fb9\u5c4b\u4e2d\u606d\u5019\u9999\u5e05\u7684\u5927\u9a7e\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u5f88\u597d\uff0c\u518d\u987b\u4f60\u53bb\u8f6c\u544a\u859b\u4e8c\u516c\u5b50\uff0c\u8bf7\u4ed6\u7a0d\u5019\u7247\u523b\uff0c\u8bf4\u6211\u9a6c\u4e0a\u5c31\u5230\u3002\u201d \u3000\u3000\u7b49\u501a\u5251\u8d70\u4e86\uff0c\u695a\u7559\u9999\u53c8\u6c89\u541f\u4e86\u534a\u664c\uff0c\u9053\uff1a\u201c\u6211\u8fd8\u6709\u4ef6\u4e8b\uff0c\u8981\u627e\u4f60\u4e24\u4e2a\u505a\u3002\u201d \u3000\u3000\u5c0f\u9ebb\u5b50\u6015\u6328\u9a82\uff0c\u4f4e\u5934\u4e0d\u6562\u8fc7\u6765\uff0c\u5c0f\u79c3\u5b50\u5df2\u6328\u8fc7\u4e86\u9a82\uff0c\u89c9\u5f97\u81ea\u5df1\u597d\u50cf\u6bd4\u5c0f\u9ebb\u5b50\u795e\u6c14\u591a\u4e86\uff0c \u62a2\u7740\u9053\uff1a\u201c\u83ab\u8bf4\u4e00\u4ef6\u4e8b\uff0c\u4e00\u767e\u4ef6\u4e8b\u4e5f\u6ca1\u5173\u7cfb\u3002\u201d \u3000\u3000\u201c\u6628\u5929\u665a\u4e0a\u6211\u53bb\u627e\u7684\u90a3\u5bf9\u592b\u59bb\uff0c\u4f60\u8ba4\u5f97\u51fa\u9ebd\uff1f\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u9053\uff1a\u201c\u5f53\u7136\u8ba4\u5f97\u51fa\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u597d\uff0c\u4f60\u73b0\u5728\u5c31\u53bb\u627e\u4ed6\u4eec\uff0c\u5c06\u4ed6\u4eec\u4e5f\u5e26\u5230\u90a3\u8fb9\u730e\u5c4b\u53bb\uff0c\u5c31\u8bf4\u6211\u8bf7\u4ed6\u4eec\u53bb \u7684\u3002\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u9053\uff1a\u201c\u6ca1\u95ee\u9898\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f46\u662f\u4f60\u4eec\u5230\u4e86\u90a3\u8fb9\u730e\u5c4b\u5f8c\uff0c\u5148\u5728\u5916\u9762\u7b49\u7740\uff0c\u6700\u597d\u83ab\u8981\u88ab\u4eba\u53d1\u73b0\uff0c\u7b49\u6211\u53eb\u4f60 \u4eec\u8fdb\u53bb\u65f6\u518d\u9732\u9762\u3002\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u4e00\u9762\u70b9\u5934\uff0c\u4e00\u9762\u62c9\u7740\u5c0f\u9ebb\u5b50\u5c31\u8dd1\u3002 \u3000\u3000\u695a\u7559\u9999\u4ef0\u9762\u5411\u5929\uff0c\u957f\u957f\u4f38\u4e2a\u61d2\u8170\uff0c\u968f\u5583\u9053\uff1a\u201c\u8c22\u5929\u8c22\u5730\uff0c\u6240\u6709\u7684\u9ebb\u70e6\u4e8b\uff0c\u603b\u7b97\u90fd\u8981\u8fc7 \u53bb\u2026\u2026\u201d \u3000\u3000\u695a\u7559\u9999\u5e76\u6ca1\u6709\u8d39\u4ec0\u9ebd\u529f\u592b\u5c31\u5c06\u5de6\u8f7b\u4faf\u7a33\u4f4f\uff0c\u53c8\u5c06\u90a3\u4f4d\u4e5f\u4e0d\u77e5\u662f\u771f\u8fd8\u662f\u5047\u7684\u201c\u5de6\u660e\u73e0\u201d\u59d1 \u5a18\u5e26\u51fa\u4e86\u63b7\u676f\u5c71\u5e84\u3002 \u3000\u3000\u8fd9\u4f4d\u201c\u5de6\u59d1\u5a18\u201d\u8138\u8272\u8fd8\u662f\u82cd\u767d\u5f97\u53ef\u6015\uff0c\u773c\u775b\u5374\u4eae\u5f97\u5f88\uff0c\u8fd9\u4e24\u5929\u5979\u597d\u50cf\u5df2\u517b\u8db3\u4e86\u7cbe\u795e\uff0c\u4f46 \u8d70\u8def\u8fd8\u662f\u6162\u541e\u541e\u7684\uff0c\u8ddf\u5728\u695a\u7559\u9999\u5f8c\u9762\u8d70\u4e86\u5f88\u4e45\uff0c\u624d\u60a0\u60a0\u7684\u9053\uff1a\u201c\u73b0\u5728\u5df2\u7ecf\u5feb\u5230\u53c1\u5929\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u5de6\u59d1\u5a18\u9053\uff1a\u201c\u4f60\u7b54\u5e94\u8fc7\u6211\uff0c\u53ea\u8981\u7b49\u53c1\u5929\uff0c\u5c31\u8ba9\u6211\u56de\u5bb6\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u55ef\u3002\u201d \u3000\u3000\u5de6\u59d1\u5a18\u9053\uff1a\u201c\u90a3\u9ebd\u2026\u2026\u90a3\u9ebd\u4f60\u73b0\u5728\u5c31\u80af\u8ba9\u6211\u56de\u53bb\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u81ea\u7136\u80af\u8ba9\u4f60\u8d70\uff0c\u53ea\u4e0d\u8fc7\uff0c\u4f60\u56de\u5230\u5bb6\u4ee5\u5f8c\u4f60\u7236\u6bcd\u8fd8\u8ba4\u4f60\u9ebd\uff1f\u2026\u8981\u6362\u4e86\u6211\uff0c\u662f \u7edd\u4e0d\u4f1a\u8ba4\u4e00\u4e2a\u964c\u751f\u5973\u5b69\u5b50\u505a\u81ea\u5df1\u7684\u5973\u513f\u7684\u3002\u201d \u3000\u3000\u5de6\u59d1\u5a18\u54ac\u7740\u5634\u5507\uff0c\u9053\uff1a\u201c\u53ef\u662f\u2026\u3002\u53ef\u662f\u4f60\u5df2\u7ecf\u7b54\u5e94\u8fc7\u6211\uff0c\u4f60\u5c31\u8be5\u66ff\u6211\u53bb\u89e3\u91ca\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u82b1\u91d1\u5f13\u592b\u4eba\u4f1a\u76f8\u4fe1\u6211\u7684\u8bdd\uff1f\u201d \u3000\u3000\u5de6\u59d1\u5a18\u9053\uff1a\u201c\u6c5f\u6e56\u4e2d\u8c01\u4e0d\u77e5\u695a\u9999\u5e05\u4e00\u8bfa\u5343\u91d1\uff1f\u53ea\u8981\u9999\u5e05\u8bf4\u51fa\u6765\u7684\u8bdd\uff0c\u5c31\u7b97\u4f60\u7684\u4ec7\u4eba\uff0c\u4e5f \u7edd\u4e0d\u4f1a\u4e0d\u76f8\u4fe1\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6c89\u9ed8\u4e86\u534a\u664c\uff0c\u5ffd\u53c8\u56de\u5934\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u6211\u603b\u8ba9\u4f60\u5982\u613f\u5c31\u662f\uff0c\u53ea\u4e0d\u8fc7\u4ec0\u9ebd\u4e8b \u90fd\u8981\u6162\u6162\u6765\uff0c\u4e0d\u80fd\u7740\u6025\uff0c\u4e00\u7740\u6025\uff0c\u6211\u7684\u7ae0\u6cd5\u5c31\u4e71\u3002 \u3000\u3000\u5de6\u59d1\u5a18\u5782\u4e0b\u4e86\u5934\uff0c\u53c8\u8d70\u4e86\u534a\u664c\u524d\u9762\u5df2\u5230\u4e86\u90a3\u5c0f\u6811\u6797\uff0c\u8fdc\u8fdc\u671b\u53bb\uff0c\u5df2\u53ef\u9690\u7ea6\u89c1\u5230\u90a3\u680b\u5c0f\u6728 \u5c4b\uff0c\u5979\u5ffd\u7136\u505c\u4e0b\u811a\u6b65\uff0c\u9053\uff1a\u201c\u4f60\u2014\u2026\u4f60\u65e2\u4e0d\u60f3\u9001\u6211\u56de\u53bb\u56e2\u805a\uff0c\u53c8\u8981\u5e26\u6211\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u77a7\u89c1\u90a3\u8fb9\u7684\u6728\u5c4b\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u5de6\u59d1\u5a18\u8138\u8272\u66f4\u82cd\u767d\u52c9\u5f3a\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u8d70\u7d2f\u4e86\uff0c\u6211\u4eec\u5148\u5230\u90a3\u5c4b\u5b50\u53bb\u5750\u5750\u3002\u201d \u3000\u3000\u5de6\u59a8\u5a18\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u2026\u2026\u6211\u4e0d\u60f3\u53bb\u3002\u201d \u3000\u3000\u5979\u867d\u7136\u52c9\u5f3a\u63a7\u5236\u7740\u81ea\u5df1\uff0c\u4f46\u5634\u5507\u8fd8\u662f\u6709\u4e9b\u53d1\u79d1\u3002 \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u90a3\u5c4b\u5b50\u91cc\u53c8\u6ca1\u6709\u9b3c\uff0c\u4f60\u6015\u4ec0\u9ebd\uff0c\u4f55\u51b5\uff0c\u4f60\u5df2\u6b7b\u8fc7\u4e00\u6b21\uff0c\u5c31\u6709\u9b3c\u4f60\u4e5f\u4e0d\u5fc5 \u5bb3\u6015\u7684\u3002\u201d \u3000\u3000\u5de6\u59d1\u5a18\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u542c\u8bf4\u8fc7\u90a3\u5c4b\u5b50\u662f\u859b\u5bb6\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u4f60\u82e5\u662f\u5de6\u660e\u73e0\u81ea\u7136\u4e0d\u80fd\u5230\u859b\u5bb6\u7684\u5c4b\u5b50\u53bb\uff0c\u4f46\u4f60\u53c8\u4e0d\u662f\u771f\u7684\u5de6\u660e\u73e0\uff0c\u5de6\u660e \u73e0\u65e9\u5df2\u6b7b\u4e86\uff0c\u4f60\u53ea\u4e0d\u8fc7\u662f\u501f\u4e86\u5979\u7684\u5c38\u8fd8\u9b42\u800c\u5df2\uff0c\u4e3a\u4ec0\u9ebd\u53bb\u4e0d\u5f97\uff1f\u201d \u3000\u3000\u4ed6\u7b11\u563b\u563b\u9053\uff1a\u201c\u4f55\u51b5\uff0c\u4f60\u65e2\u662f\u859b\u4e8c\u516c\u5b50\u672a\u8fc7\u95e8\u7684\u5ab3\u5987\uff0c\u8fdf\u65e9\u603b\u662f\u8981\u5230\u859b\u5bb6\u53bb\u7684\u3002\u201d \u3000\u3000\u5de6\u59d1\u5a18\u9053\uff1a\u201c\u53ef\u662f\u2026\u3002\u53ef\u662f\u2026\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u4e5f\u6ca1\u5173\u7cfb\uff0c\u6211\u662f\u859b\u8863\u4eba\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u5de6\u59d1\u5a18\u597d\u50cf\u5446\u4f4f\u4e86\uff0c\u5446\u4e86\u534a\u664c\uff0c\u52c9\u5f3a\u4f4e\u7740\u5934\u8ddf\u695a\u7559\u9999\u8d70\u4e86\u8fc7\u53bb\uff0c\u811a\u4e0b\u5c31\u50cf\u662f\u62d6\u7740\u5343\u65a4\u94c1 \u7ec3\u4f3c\u7684\u3002 \u3000\u3000\u695a\u7559\u9999\u5374\u8d70\u5f97\u5f88\u8f7b\u5feb\uff0c\u4ed6\u4eec\u521a\u8d70\u5230\u90a3\u6728\u5c4b\u95e8\u53e3\uff0c\u95e8\u5c31\u5f00\u4e86\uff0c\u4e00\u4e2a\u5f88\u82f1\u4fca\u7684\u9526\u8863\u5c11\u5e74\u8d70\u4e86 \u51fa\u6765\uff0c\u4ed6\u8138\u4e0a\u672c\u6765\u5e26\u7740\u7b11\uff0c\u663e\u7136\u662f\u51fa\u6765\u8fce\u63a5\u695a\u7559\u9999\u7684\uff0c\u4f46\u4e00\u77a7\u89c1\u8fd9\u4f4d\u201c\u5de6\u59d1\u5a18\u201d\uff0c\u4ed6\u7684\u7b11\u5bb9 \u5c31\u51bb\u7ed3\u4e86\u3002 \u3000\u3000\u5de6\u59cb\u5a18\u867d\u7136\u4e00\u76f4\u5782\u7740\u5934\u4f46\u8138\u8272\u4e5f\u96be\u770b\u5f97\u72e0\u3002 \u3000\u3000\u695a\u7559\u9999\u76ee\u5149\u5728\u4e24\u4eba\u8138\u4e0a\u4e00\u626b\uff0c\u7b11\u9053\uff1a\u201c\u4e24\u4f4d\u539f\u6765\u65e9\u5c31\u8ba4\u8bc6\u4e86\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u548c\u5de6\u59d1\u5a18\u7acb\u523b\u540c\u65f6\u62a2\u7740\u9053\uff1a\u201c\u4e0d\u8ba4\u5f97\u2026\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u4e0d\u8ba4\u5f97\uff1f\u2026\u54ea\u4e5f\u65e0\u59a8\uff0c\u53cd\u6b63\u4e24\u4f4d\u8fdf\u65e9\u603b\u662f\u8981\u8ba4\u8bc6\u7684\u3002\u201d \u3000\u3000\u4ed6\u542b\u7b11\u5411\u90a3\u5c11\u5e74\u62b1\u62f3\uff0c\u9053\uff1a\u201c\u8fd9\u4f4d\u60f3\u5fc5\u5c31\u662f\u859b\u4e8c\u516c\u5b50\u4e86\u3002\u201d \u3000\u3000\u859b\u658c\u8eac\u8eab\u5782\u65e8\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u5f1f\u5b50\u6b63\u662f\u859b\u658c\uff0c\u9999\u5e05\u7684\u5927\u540d\uff0c\u5f1f\u5b50\u65e9\u5df2\u5982\u96f7\u8d2f\u8033\uff0c\u5374\u4e0d\u77e5\u9999 \u5e05\u8fd9\u6b21\u6709\u4f55\u5429\u5490\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u201c\u5429\u5490\u5012\u4e5f\u4e0d\u6562\u8bf7\u5148\u8fdb\u53bb\u5750\u5750\u518d\u8bf4\u3002\u201d \u3000\u3000\u4ed6\u53cd\u5012\u50cf\u4e2a\u4e3b\u4eba\u5728\u95e8\u53e3\u542b\u7b11\u63d6\u5ba2\uff0c\u859b\u658c\u548c\u5de6\u59d1\u5a18\u53ea\u6709\u4f4e\u7740\u5934\u5f80\u91cc\u8d70\uff0c\u5c31\u50cf\u8116\u5b50\u5ffd\u7136\u65ad \u4e86\uff0c\u518d\u4e5f\u62ac\u4e0d\u8d77\u5934\u3002 \u3000\u3000\u501a\u5251\u7acb\u523b\u9000\u4e86\u51fa\u6765\uff0c\u9000\u5230\u95e8\u53e3\uff0c\u53ea\u542c\u695a\u7559\u9999\u4f4e\u58f0\u9053\uff1a\u201c\u7b49\u5c0f\u79c3\u5b50\u6765\u4e86\uff0c\u53eb\u4ed6\u4e00\u4e2a\u4eba\u5148\u8fdb \u6765\u3002\u201d \u3000\u3000\u53ea\u89c1\u5de6\u59d1\u5a18\u548c\u859b\u658c\u4e00\u4e2a\u7ad9\u8fc7\u5de6\u8fb9\u5c4b\u89d2\uff0c\u4e00\u4e2a\u7ad9\u5728\u53f3\u8fb9\u5c4b\u89d2\uff0c\u4e24\u4eba\u773c\u89c2\u9f3b\uff0c\u9f3b\u89c2\u5fc3\uff0c\u52a8\u4e5f \u4e0d\u52a8\u3002 \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u8fd9\u5730\u65b9\u5b9e\u5728\u4e0d\u9519\uff0c\u5c31\u7b97\u662f\u505a\u65b0\u623f\uff0c\u4e5f\u505a\u5f97\u8fc7\u4e86\u3002\u859b\u516c\u5b50\uff0c\u4f60\u8bf4\u662f\u9ebd\uff1f\u201d \u3000\u3000\u859b\u658c\u54c8\u54c8\u9053\uff1a\u201c\u4e0d\u6562\u2026\u3002\u662f\u2026\u3002\u54b3\u54b3\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53c8\u5728\u5c4b\u91cc\u8f6c\u4e86\u51e0\u4e2a\u5708\u5b50\uff0c\u66fc\u58f0\u7b11\u9053\uff1a\u201c\u6708\u4e0a\u67f3\u68a2\u5934\uff0c\u4eba\u7ea6\u9ec4\u660f\u5f8c\u2026\u201d\u53ea\u662f\u7ea6\u5728\u6b64 \u95f4\uff0c\u5012\u771f\u4e0d\u9519\u2026\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u62c9\u5f00\u95e8\uff0c\u5c0f\u79c3\u5b50\u6b63\u597d\u8d70\u5230\u95e8\u53e3\u3002 \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u4f60\u6765\u5f97\u6b63\u597d\uff0c\u8fd9\u4e24\u4f4d\u4e0d\u77e5\u4f60\u53ef\u8ba4\u5f97\u9ebd\uff1f\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u773c\u775b\u4e00\u8f6c\uff0c\u7acb\u523b\u7709\u5f00\u773c\u7b11\uff0c\u9053\uff1a\u201c\u600e\u9ebd\u4e0d\u8ba4\u5f97\uff0c\u8fd9\u4f4d\u516c\u5b50\u548c\u8fd9\u4f4d\u5c0f\u59d0\u90fd\u662f\u5927\u65b9 \u4eba\uff0c\u7b2c\u4e00\u6b21\u89c1\u9762\u5c31\u7ed9\u4e86\u6211\u51e0\u4e24\u94f6\u5b50\u3002\u201d \u3000\u3000\u4ed6\u8bdd\u672a\u8bf4\u5b8c\uff0c\u5de6\u59d1\u5a18\u548c\u859b\u658c\u7684\u8138\u8272\u5df2\u53d8\u4e86\u3002 \u3000\u3000\u4e24\u4eba\u62a2\u7740\u9053\uff1a\u201c\u6211\u4e0d\u8ba4\u5f97\u4ed6\uff0c\u4e00\u2026\u8fd9\u5b69\u5b50\u8ba4\u9519\u4e86\u4eba\u3002\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u7728\u7740\u773c\u7b11\u9053\uff1a\u201c\u6211\u7edd\u4e0d\u4f1a\u8ba4\u9519\u3002\u53eb\u5316\u9047\u5230\u5927\u65b9\u4eba\uff0c\u90a3\u662f\u6c38\u8fdc\u4e5f\u5fd8\u4e0d\u4e86\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u62cd\u638c\u7b11\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u859b\u516c\u4e88\u548c\u5de6\u59d1\u5a18\u7684\u786e\u662f\u65e9\u5df2\u8ba4\u5f97\u7684\u4e86\u3002\u201d \u3000\u3000\u5de6\u59d1\u5a18\u5ffd\u7136\u5927\u54ed\u8d77\u6765\u9053\uff1a\u201c\u6211\uff01\u2026\u6211\u4e0d\u59d3\u5de6\uff0c\u4f60\u4eec\u770b\u9519\u4e86\uff0c\u6211\u662f\u65bd\u8335\u2026\u3002\u6211\u4e0d\u8ba4\u5f97\u4ed6\u201d \u3000\u3000\u4e00\u9762\u72c2\u543c\uff0c\u4e00\u9762\u5c31\u60f3\u51b2\u51fa\u53bb\u3002 \u3000\u3000\u4f46\u662f\u5979\u7acb\u523b\u5c31\u53d1\u73b0\u771f\u7684\u201c\u65bd\u66f2\u201d\u5df2\u7ad9\u5728\u95e8\u53e3\u3002 \u3000\u3000\u695a\u7559\u9999\u6307\u7740\u65bd\u8335\uff0c\u542b\u7b11\u9053\uff1a\u201c\u4f60\u8ba4\u5f97\u5979\u9ebd\uff1f\u201d \u3000\u3000\u5de6\u660e\u73e0\u5168\u8eab\u53d1\u6296\u989d\u58f0\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u2026\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u82e5\u662f\u65bd\u8335\uff0c\u5979\u53c8\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u5de6\u660e\u73e0\u547b\u541f\u4e00\u58f0\uff0c\u7a81\u7136\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u53f6\u76db\u5170\u3001\u65bd\u8335\u3001\u548c\u6881\u5988\u7ad9\u5728\u4e00\u8fb9\u3002\u8138\u4e0a\u7684\u8868\u8bb0\u90fd\u5f88\u5947\u7279\uff0c\u4e5f\u4e0d\u77e5\u662f\u60ca\u60f6\uff0c\u662f\u7d27\u5f20\uff0c\u8fd8\u662f \u6b22\u559c\u3002 \u3000\u3000\u501a\u5251\uff0c\u5c0f\u79c3\u5b50\u548c\u5c0f\u9ebb\u4e25\u7ad9\u5728\u65c1\u8fb9\u53d1\u5446\uff0c\u663e\u7136\u8fd8\u5e76\u4e0d\u61c2\u8fd9\u662f\u600e\u9ebd\u56de\u4e8b\uff0c\u5fc3\u91cc\u53c8\u662f\u7591\u60d1\uff0c\u53c8 \u89c9\u597d\u5947\u3002 \u3000\u3000\u5de6\u660e\u73e0\u501a\u5728\u859b\u658c\u6000\u91cc\u4eff\u4f5b\u518d\u4e5f\u65e0\u529b\u7ad9\u7acb\u3002 \u3000\u3000\u4ed6\u4eec\u672c\u662f\u201c\u4e0d\u8ba4\u5f97\u201d\u7684\uff0c\u4f46\u5de6\u660e\u73e0\u4e00\u6655\u5012\uff0c\u859b\u658c\u5c31\u4e0d\u987e\u4e00\u5207\uff0c\u5c06\u5979\u62b1\u4e86\u8d77\u6765\uff0c\u518d\u4e5f\u4e0d\u80af \u677e\u624b\u3002 \u3000\u3000\u5927\u5bb6\u7684\u5fc3\u60c5\u867d\u4e0d\u540c\uff0c\u53cb\u60c5\u4e5f\u4e0d\u540c\uff0c\u6bcf\u4e2a\u4eba\u7684\u773c\u775b\u5374\u90fd\u5728\u671b\u7740\u695a\u7559\u9999\uff0c\u90fd\u5728\u7b49\u7740\u4ed6\u8bf4\u8bdd\u3002 \u3000\u3000\u695a\u7559\u9999\u5c06\u706f\u82af\u6311\u9ad8\u4e86\u4e9b\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u6211\u542c\u5230\u8fc7\u5f88\u591a\u4eba\u8c08\u8d77\u2018\u9b3c\u2019\uff0c\u4f46\u771f\u7684\u89c1\u8fc7\u9b3c\u7684\u4eba\uff0c \u5374\u8fde\u4e00\u4e2a\u4e5f\u6ca1\u6709\uff0c\u6211\u4e5f\u542c\u8fc7\u4eba\u8bf4\u2018\u501f\u5c38\u8fd8\u9b42\u2019\u3002\u2026\u201d \u3000\u3000\u4ed6\u7b11\u4e86\u8d77\u6765\uff0c\u63a5\u7740\u9053\uff1a\u201c\u8fd9\u79cd\u4e8b\u672c\u6765\u4e5f\u5f88\u96be\u4ee4\u4eba\u76f8\u4fe1\uff0c\u4f46\u8fd9\u6b21\u6211\u5374\u51e0\u4e4e\u76f8\u4fe1\u4e86\uff0c\u56e0\u4e3a\u4eb2 \u773c\u89c1\u5230\u5de6\u59d1\u5a18\u6b7b\uff0c\u53c8\u4eb2\u773c\u89c1\u5230\u5979\u590d\u6d3b\u7684\u3002\u201d \u3000\u3000\u5927\u5bb6\u90fd\u5728\u6c89\u9ed8\u7740\uff0c\u7b49\u4ed6\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u4e5f\u4eb2\u773c\u89c1\u5230\u65bd\u59a8\u5a18\u7684\u5c38\u8eab\uff0c\u751a\u81f3\u8fde\u5979\u6b7b\u65f6\u7a7f\u7684\u8863\u670d\u3002\u90fd\u548c\u5de6\u59d1\u5a18\u590d\u6d3b\u65f6 \u7a7f\u7684\u4e00\u6837\uff0c\u8fd9\u7684\u786e\u662f\u2018\u501f\u5c38\u8fd8\u9b42\u2019\uff0c\u8c01\u4e5f\u4e0d\u80fd\u4e0d\u4fe1\u3002\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u773c\u775b\u90fd\u76f4\u4e86\uff0c\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u4f46\u73b0\u5728\u65bd\u59d1\u5a18\u5e76\u6ca1\u6709\u6b7b\uff0c\u5de6\u59d1\u5a18\u53c8\u600e\u9ebd\u4f1a\u8bf4\u8bdd\u7684\u5462\uff0c \u65bd\u59d1\u5a18\u65e2\u6ca1\u6709\u6b7b\uff0c\u5979\u7684\u5c38\u8eab\u53c8\u600e\u9ebd\u56de\u4e8b\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u7684\u786e\u5f88\u590d\u6742\uff0c\u5f88\u5947\u602a\uff0c\u6211\u672c\u6765\u4e5f\u767e\u601d\u4e0d\u5f97\u5176\u89e3\uff0c\u76f4\u5230\u6211\u65e0\u610f\u4e2d\u95ef \u5165\u8fd9\u5c4b\u5b50\uff0c\u53d1\u73b0\u4e86\u706b\u7089\u4e2d\u7684\u5986\u5323\u82b1\u7c89\u3002\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u9053\uff1a\u201c\u68b3\u5986\u5323\u5b50\u548c\u2018\u501f\u5c38\u8fd8\u9b42\u2019\u53c8\u6709\u4ec0\u9ebd\u5173\u7cfb\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u82e5\u60f3\u542c\u8fd9\u79d8\u5bc6\uff0c\u5c31\u5feb\u4e3a\u6211\u627e\u4e00\u4e2a\u4eba\u6765\uff0c\u56e0\u4e3a\u5979\u548c\u8fd9\u4ef6\u4e8b\u4e5f\u6709\u5f88\u5927\u7684\u5173 \u7cfb\uff0c\u5979\u4e00\u5b9a\u4e5f\u5f88\u60f3\u542c\u3002\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u8fd8\u672a\u8bf4\u8bdd\uff0c\u7cb1\u5988\u5ffd\u7136\u9053\uff1a\u201c\u9999\u5e05\u8981\u627e\u7684\u53ef\u662f\u90a3\u4f4d\u77f3\u59d1\u5a18\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e0d\u9519\u4f60\u4e5f\u8ba4\u5f97\u5979\uff1f\u201d \u3000\u3000\u6881\u5988\u82cd\u8001\u7684\u8138\u4e0a\u5c45\u7136\u4e5f\u7ea2\u4e86\u7ea2\u9053\uff1a\u201c\u6211\u5df2\u5c06\u5979\u8bf7\u6765\u4e86\uff0c\u53ef\u662f\u77f3\u59d1\u5a18\u5b9a\u8981\u5148\u56de\u53bb\u6362\u8863\u886b\u3002 \u624d\u80af\u6765\u89c1\u9999\u5e05\u3002\u201d \u3000\u3000\u695a\u5267\u9999\u53f9\u4e86\u53e3\u6c14\u4e0d\u8bf4\u8bdd\u4e86\uff0c\u56e0\u4e3a\u4ed6\u4e5f\u65e0\u8bdd\u53ef\u8bf4\u3002 \u3000\u3000\u5e78\u597d\u77f3\u7ee3\u4e91\u5e74\u7eaa\u8fd8\u8f7b\uff0c\u5e74\u8f7b\u7684\u5973\u5b69\u5b50\u4fee\u9970\u5f97\u603b\u6bd4\u8f83\u5feb\u4e9b\u3002\u5973\u4eba\u4fee\u9970\u7684\u65f6\u95f4\uff0c\u5b9a\u548c\u5979\u7684\u5e74 \u9f84\u6210\u6b63\u6bd4\u7684\u3002 \u3000\u3000\u77f3\u7ee3\u4e91\u770b\u5230\u8fd9\u9ebd\u591a\u4eba\uff0c\u81ea\u7136\u4e5f\u5f88\u60ca\u8bb6\u3002 \u3000\u3000\u5c0f\u79c3\u5b50\u6bd4\u5979\u66f4\u7740\u6025\uff0c\u5df2\u62a2\u7740\u95ee\u9053\uff1a\u201c\u68b3\u5986\u5323\u5b50\u548c\u8fd9\u4ef6\u4e8b\u5230\u5e95\u6709\u4ec0\u9ebd\u5173\u7cfb\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u706b\u7089\u91cc\u6709\u68b3\u5986\u5323\uff0c\u5c31\u8868\u793a\u5fc5\u5b9a\u6709\u53cc\u7537\u5973\u65f6\u5e38\u5728\u8fd9\u91cc\u76f8\u4f1a\uff0c\u6211\u672c\u6765 \u4ee5\u4e3a\u662f\u53e6\u5916\u4e24\u4e2a\u4eba\uff0c\u4f46\u5979\u4eec\u8eab\u4e0a\u7684\u9999\u6c14\u5374\u548c\u8fd9\u5323\u5b50\u91cc\u7684\u82b1\u7c89\u4e0d\u540c\u3002\u201d \u3000\u3000\u4ed6\u6ca1\u6709\u8bf4\u51fa\u859b\u7ea2\u7ea2\u548c\u82b1\u91d1\u5f13\u7684\u540d\u5b57\uff0c\u56e0\u4e3a\u4ed6\u4ece\u4e0d\u613f\u610f\u4f24\u5bb3\u5230\u522b\u4eba\uff0c\u4f46\u8fd9\u65f6\u5de6\u660e\u73e0\u7684\u8138\u5df1 \u7ea2\u4e86\u3002 \u3000\u3000\u5c0f\u79c3\u5b50\u77aa\u4e86\u5979\u4e00\u773c\uff0c\u5fcd\u4e0d\u4f4f\u53c8\u9053\uff1a\u201c\u4f60\u542c\u6211\u4e00\u8bf4\u4e00\u2026\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u6211\u542c\u4f60\u4e00\u8bf4\u5c31\u731c\u51fa\u5176\u4e2d\u6709\u4e00\u4eba\u5fc5\u662f\u859b\u516c\u5b50\uff0c\u4f46\u859b\u516c\u5b50\u7684\u2026 \u7684\u2018\u670b\u53cb\u2019\u662f\u8c01\uff1f\u6211\u8fd8\u662f\u731c\u4e0d\u51fa\u3002\u201d \u3000\u3000\u4ed6\u7684\u8fd9\u201c\u670b\u53cb\u201d\u4e24\u5b57\u5012\u4e5f\u7528\u5f97\u5999\u6781\uff0c\u859b\u658c\u7684\u8138\u4e5f\u7ea2\u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u672c\u6765\u4ee5\u4e3a\u77f3\u5927\u59d1\u5a18\uff0c\u76f4\u7b49\u6211\u89c1\u5230\u8fd9\u4f4d\u501a\u5251\u5144\u5f1f\u65f6\uff0c\u624d\u77e5\u9053\u6211\u60f3\u9519\u4e86\u3002\u201d \u3000\u3000\u501a\u5251\u5782\u4e0b\u4e86\u5934\uff0c\u773c\u6cea\u5df2\u5feb\u6d41\u51fa\u6765\u3002 \u3000\u3000\u695a\u7559\u9999\u53c8\u9053\uff1a\u201c\u65bc\u662f\u6211\u66f4\u5947\u602a\u4e86\uff0c\u77f3\u5927\u59d1\u5a18\u65e2\u7136\u548c\u859b\u516c\u5b50\u5168\u65e0\u5173\u7cfb\u3002\u859b\u516c\u5b50\u4e3a\u4f55\u4f1a\u5bf9\u5979 \u7684\u75c5\u60c5\u90a3\u9ebd\u5173\u5fc3\uff1f\u53c8\u4e3a\u4f55\u4f1a\u5bf9\u5979\u7684\u4e8c\u53d4\u90a3\u9ebd\u7167\u987e\uff1f\u4ed6\u751a\u81f3\u5b81\u613f\u88ab\u77f3\u7ee3\u4e91\u7684\u5a18\u8bef\u4f1a\uff0c\u4e5f\u4e0d\u613f\u610f \u8fa8\u81ea\uff0c\u53cd\u9762\u60f3\u5c06\u9519\u5c31\u9519\u2026\u2026\u6240\u4ee5\u6211\u60f3\u8fd9\u5176\u4e2d\u5fc5\u5b9a\u6709\u7edd\u5927\u7684\u9690\u79d8\uff0c\u5426\u5219\u4efb\u4f55\u4eba\u90fd\u4e0d\u613f\u610f\u8d1f\u8fd9\u79cd \u51a4\u540d\u7684\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u72e0\u72e0\u77aa\u4e86\u859b\u658c\u4e00\u773c\uff0c\u81ea\u5df1\u7684\u8138\u4e5f\u7ea2\u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u60f3\u8fd9\u79d8\u5bc6\u5fc5\u5b9a\u548c\u77f3\u5927\u59d1\u5a18\u4e4b\u6b7b\u6709\u5173\u3002\u6240\u4ee5\u5c31\u4e0d\u60dc\u6316\u575f\u68fa\uff0c\u4e5f\u8981\u67e5\u660e\u7a76 \u7adf\uff0c\u8c01\u77e5\u2026\u2026\u3002\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u62a2\u7740\u9053\uff1a\u201c\u8c01\u77e5\u77f3\u5927\u59d1\u5a18\u4e5f\u6ca1\u6709\u6b7b\uff0c\u68fa\u6750\u91cc\u53ea\u4e0d\u8fc7\u662f\u4e9b\u7816\u5934\u800c\u5df2\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\u3002\u9053\uff1a\u201c\u77f3\u5927\u59d1\u5a18\u7684\u786e\u662f\u6b7b\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u56e0\u4e3a\u5979\u7684\u5c38\u8eab\u5df1\u88ab\u4eba\u501f\u8d70\u3002\u201d \u3000\u3000\u4ed6\u4e0d\u8ba9\u5c0f\u79c3\u5b50\u8bf4\u8bdd\uff0c\u5df2\u63a5\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u859b\u516c\u5b50\u8981\u501f\u5979\u7684\u5c38\u8eab\u6240\u4ee5\u624d\u90a3\u9ebd\u5173\u5fc3\u5979\u7684\u75c5\u60c5\uff0c \u5c31\u56e0\u4e3a\u5c01\u68fa\u7684\u4eba\u662f\u5979\u7684\u4e8c\u53d4\uff0c\u6240\u4ee5\u859b\u516c\u5b50\u624d\u4f1a\u5bf9\u5979\u7684\u4e8c\u53d4\u90a3\u9ebd\u7167\u987e\u3002\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u62a2\u7740\u9053\uff1a\u201c\u53ef\u662f\u2026\u2026\u53ef\u662f\u859b\u516c\u5b50\u8981\u501f\u77f3\u5927\u59d1\u5a18\u7684\u6b7b\u53c8\u6709\u4ec0\u9ebd\u7528\u5462\uff1f\u201d \u3000\u3000\u4ed6\u5b9e\u5728\u8d8a\u542c\u9f13\u52b2\u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u53ea\u56e0\u859b\u516c\u4e88\u8981\u7528\u77f3\u5927\u59d1\u5a18\u7684\u4f53\uff0c\u6765\u626e\u6210\u65bd\u8335\u59d1\u5a18\u7684\u4f53\uff0c\u8ba9\u522b\u4eba\u90fd\u4ee5\u4e3a\u65bd\u59d1 \u5a18\u771f\u7684\u5df2\u6b7b\u4e86\u3002\u201d \u3000\u3000\u4ed6\u53f9\u606f\u63a5\u9053\uff1a\u201c\u77f3\u5927\u59d1\u5a18\u8eab\u6750\u3001\u9762\u5bb9\u4e5f\u8bb8\u672c\u6765\u5c31\u6709\u51e0\u5206\u548c\u65bd\u59d1\u5a18\u76f8\u4f3c\uff0c\u4f55\u51b5\uff0c\u4eba\u6b7b\u5f8c\u9762 \u5bb9\u6709\u4e9b\u6539\u53d8\uff0c\u4efb\u4f55\u4eba\u4e5f\u90fd\u4e0d\u66fe\u5bf9\u6b7b\u770b\u5f97\u592a\u4ed4\u7ec6\u7684\uff0c\u88c5\u626e\u5f97\u867d\u7136\u4e0d\u592a\u50cf\uff0c\u4e5f\u5fc5\u5b9a\u53ef\u4ee5\u6df7\u8fc7\u53bb\uff0c \u66f4\u4f55\u51b5\u6881\u5988\u4e5f\u53c2\u9884\u4e86\u8fd9\u79d8\u5bc6\u3002\u201d \u3000\u3000\u7cb1\u5988\u7684\u5934\u4e5f\u4f4e\u4e0b\u6765\u4e86\u3002 \u3000\u3000\u5c0f\u79c3\u5b50\u6478\u7740\u79c3\u5934\uff0c\u9053\uff1a\u201c\u53ef\u662f\u3002\u3002\u3002\u3002\uff0e\u65bd\u59d1\u5a18\u53c8\u662f\u4e3a\u4e86\u4ec0\u9ebd\u8981\u88c5\u6b7b\u5462\uff1f\u201d\u6ed4 \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u65bd\u8335\u82e5\u662f\u6ca1\u6709\u6b7b\uff0c\u5de6\u660e\u73e0\u53c8\u600e\u80fd\u626e\u5f97\u51fa\u2018\u501f\u5c38\u8fd8\u9b42\u2019\u7684\u628a\u620f\u3002\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u82e6\u7b11\u9053\uff1a\u201c\u6211\u7b80\u76f4\u8d8a\u542c\u8d8a\u7cca\u6d82\u4e86\u5de6\u59d1\u5a18\u597d\u597d\u4e00\u4e2a\u4eba\uff0c\u4e3a\u4ec0\u9ebd\u8981\u3002\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5e72\u9884\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u770b\u6765\u7684\u786e\u5f88\u590d\u6742\uff0c\u5176\u5b9e\u5f88\u7b80\u5355\uff0c\u56e0\u4e3a\u8fd9\u5176\u4e2d\u6700\u5927\u7684 \u5173\u952e\u53ea\u4e0d\u8fc7\u662f\u4e2a\u2018\u60c5\u2019\u5b57\u3002\u201d \u3000\u3000\u4ed6\u7684\u76ee\u5149\u81ea\u5de6\u660e\u73e0\u9762\u4e0a\u626b\u8fc7\uff0c\u505c\u7559\u5728\u859b\u658c\u9762\u4e0a\uff0c\u5fae\u7b11\u63a5\u7740\u9053\uff1a\u201c\u5de6\u660e\u73e0\u81ea\u5e7c\u5c31\u88ab\u8bb8\u914d\u7ed9 \u4e86\u4e01\u5bb6\u7684\u516c\u5b50\uff0c\u8fd9\u672c\u662f\u4e00\u5207\u95e8\u5f53\u6237\u5bf9\u7684\u826f\u7f18\uff0c\u771f\u53ef\u60dc\u5979\u504f\u504f\u9047\u89c1\u4e86\u859b\u658c\uff0c\u53c8\u504f\u504f\u5bf9\u4ed6\u6709\u4e86\u60c5 \u610f\u3002\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u9053\uff1a\u201c\u4f46\u859b\u5bb6\u548c\u5de6\u5bb6\u5c82\u975e\u672c\u662f\u751f\u51a4\u5bb6\u6d3b\u5bf9\u5934\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e0d\u9519\u3002\u5de6\u660e\u73e0\u89c1\u5230\u859b\u516c\u5b50\u65f6\uff0c\u6015\u4e5f\u77e5\u9053\u81ea\u5df1\u662f\u7edd\u4e0d\u8be5\u7231\u4e0a\u4ed6\u7684\u53ea\u4e0d\u8fc7 \u2018\u60c5\u2019\u4e4b\u5b57\u6700\u662f\u5fae\u5999\uff0c\u975e\u4f46\u522b\u4eba\u65e0\u6cd5\u52c9\u5f3a\u5c31\u8fde\u81ea\u5df1\u4e5f\u5f80\u5f80\u4f1a\u63a7\u5236\u4e0d\u4f4f\uff0c\u6709\u65f6\u4f60\u867d\u7136\u660e\u77e5\u81ea\u5df1 \u4e0d\u8be5\u7231\u4e0a\u67d0\u4e2a\u4eba\uff0c\u5374\u504f\u504f\u4f1a\u4e0d\u7531\u81ea\u4e3b\u7684\u7231\u4e0a\u4ed6\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u5ffd\u7136\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u5e38\u542c\u8bf4\u8fc7\u4e00\u4e2a\u4eba\u82e5\u5760\u4eba\u4e86\u60c5\u7f51\u5f80\u5f80\u5c31\u4f1a\u53d8\u6210\u778e\u5b50\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6e29\u67d4\u7684\u77a7\u4e86\u5979\u4e00\u773c\uff0c\u9053\uff1a\u201c\u6709\u4e9b\u4eba\u867d\u7136\u672c\u613f\u53d8\u6210\u778e\u5b50\uff0c\u4f46\u4e16\u4e0a\u5374\u8fd8\u662f\u6709\u8bb8\u591a\u4eba\uff0c \u8bb8\u591a\u4eba\u8981\u4ee4\u4ed6\u7684\u773c\u775b\u4e0d\u5f97\u4e0d\u7741\u5f00\u6765\u3002\u201d \u3000\u3000\u4ed6\u76ee\u5149\u56de\u5230\u5de6\u660e\u73e0\u548c\u859b\u658c\u8eab\u4e0a\uff0c\u63a5\u7740\u9053\uff1a\u201c\u5de6\u660e\u73e0\u548c\u859b\u516c\u5b50\u867d\u7136\u76f8\u7231\u6781\u6df1\uff0c\u4f46\u4e5f\u77e5\u9053\u4e24 \u4eba\u662f\u6c38\u65e0\u53ef\u80fd\u7ed3\u5408\u7684\uff0c\u82e5\u662f\u6362\u4e86\u522b\u7684\u4eba\uff0c\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\u4e5f\u8bb8\u4f1a\u53cc\u53cc\u81ea\u6740\u6b89\u60c5\u7684\u2026\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u832b\u7136\u51dd\u6ce8\u7740\u52c9\u5149\uff0c\u5583\u5583\u9053\uff1a\u201c\u8fd9\u6cd5\u5b50\u592a\u7b28\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u8fd9\u81ea\u7136\u662f\u5f31\u8005\u6240\u4e3a\u2026\u2026\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u5ffd\u7136\u62a2\u8d77\u5934\uff0c\u9053\uff1a\u201c\u82e5\u6362\u4e86\u662f\u6211\uff0c\u6211\u4e5f\u8bb8\u4f1a\u2026\u2026\u4f1a\u79c1\u5954\u3002\u201d \u3000\u3000\u5979\u9f13\u8db3\u4e86\u5927\u52c7\u6c14\uff0c\u624d\u8bf4\u51fa\u8fd9\u53e5\u8bdd\uff0c\u8bdd\u672a\u8bf4\u5b8c\u8138\u5df2\u7ea2\u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\u6447\u4e86\u6447\u5934\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u79c1\u5954\u4e5f\u4e0d\u662f\u597d\u6cd5\u5b50\uff0c\u56e0\u4e3a\u4ed6\u4eec\u660e\u77e5\u5de6\u3001\u859b\u4e24\u5bb6\u662f\u4e16\u4ec7\uff0c\u4ed6 \u4eec\u79c1\u5954\u4e86\uff0c\u4e24\u5bb6\u7684\u4ec7\u6068\u4e5f\u8bb8\u4f1a\u56e0\u6b64\u7ed3\u5f97\u66f4\u6df1\u2026\u2026\u2019 \u3000\u3000\u4ed6\u5fae\u5fae\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u4f55\u51b5\uff0c\u4e24\u5bb6\u7684\u751f\u6b7b\u51b3\u6597\u5df2\u8fd1\u5728\u773c\u524d\uff0c\u4ed6\u4eec\u79c1\u5954\u4e4b\u5f8c\uff0c\u82e5\u662f\u77e5\u9053\u81ea \u5df1\u7684\u7236\u5144\u5df2\u88ab\u4e24\u5bb6\u5bf9\u65b9\u6240\u6740\uff0c\u53c8\u600e\u80fd\u65bc\u5fc3\u65e0\u759a\uff1f\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u6f5e\u7136\u70b9\u4e86\u70b9\u5934\uff0c\u5e7d\u5e7d\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u79c1\u5954\u4e5f\u4e0d\u662f\u597d\u6cd5\u5b50\uff0c\u5e76\u4e0d\u80fd\u89e3\u51b3\u4efb\u4f55 \u4e8b\u2026\u2026\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u9053\uff1a\u201c\u5de6\u660e\u73e0\u548c\u859b\u516c\u5b50\u975e\u4f46\u4e0d\u662f\u5f31\u8005\uff0c\u4e5f\u4e0d\u662f\u7b28\u4eba\uff0c\u4ed6\u4eec\u5728\u65e0\u53ef\u5948\u4f55\u4e4b\u4e2d\uff0c\u7adf\u56e0 \u51fa\u4e86\u4e00\u4e2a\u6700\u8352\u5510\u5374\u53c8\u662f\u6700\u5947\u5999\u7684\u6cd5\u5b50\uff0c\u90a3\u5c31\u662f\u2026\u2026\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u5fcd\u4e0d\u4f4f\u62a2\u7740\u9053\uff1a\u201c\u501f\u5c38\u8fd8\u9b42\u201d \u3000\u3000\u4ed6\u4ee5\u8d5e\u8bb8\u7684\u76ee\u5149\u77a7\u4e86\u5de6\u660e\u73e0\u4e00\u773c\uff0c\u63a5\u7740\u9053\uff1a\u201c\u5de6\u660e\u73e0\u7740\u771f\u501f\u4e86\u65bd\u8335\u7684\u9b42\u800c\u590d\u6d3b\u3002\u90a3\u9ebd\u5de6 \u660e\u73e0\u5df2\u53d8\u6210\u4e86\u65bd\u8335\uff0c\u65bd\u8335\u672c\u662f\u859b\u658c\u672a\u8fc7\u95e8\u7684\u59bb\u5b50\uff0c\u56fa\u7136\u5e94\u8be5\u5ac1\u859b\u5bb6\u3002\u5de6\u7237\u65e2\u65e0\u6cd5\u53cd\u5bf9\uff0c\u859b\u5927 \u4fa0\u4e5f\u4e0d\u80fd\u4e0d\u63a5\u53d7\u3002\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u9053\uff1a\u201c\u65bd\u4e3e\u4eba\u548c\u82b1\u91d1\u5f13\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u82b1\u91d1\u5f13\u672c\u610f\u53ea\u662f\u60f3\u548c\u859b\u5927\u4fa0\u591a\u62c9\u62e2\u5c42\u5173\u7cfb\uff0c\u89c1\u5230\u660e\u660e\u5df2\u6b7b\u4e86\u7684\u5973\u513f \u53c8\u2018\u590d\u6d3b\u2019\u9ad8\u5174\u8fd8\u6765\u4e0d\u53ca\u600e\u4f1a\u53cd\u5bf9\u5462\uff1f\u201d\u3105\u5c0f\u79c3\u5b50\u70b9\u5934\u7b11\u9053\uff1a\u201c\u597d\u6781\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6700\u5999\u7684\u662f\uff0c\u65bd\u8335\u2018\u501f\u4e86\u5de6\u660e\u73e0\u7684\u8eaf\u58f3\u5de6\u660e\u73e0\u53c8\u2018\u501f\u2019\u4e86\u65bd\u8335\u7684\u2018\u9b42\uff0c\u5de6\u660e \u73e0\u548c\u65bd\u8335\u4e8b\u5b9e\u4e0a\u5df2\u53d8\u6210\u4e2a\u4eba\uff0c\u8fd9\u4e2a\u4eba\u5ac1\u7ed9\u859b\u658c\u5f8c\uff0c\u90a3\u9ebd\u5de6\u7237\u5c31\u53d8\u6210\u4e86\u859b\u516c\u5b50\u7684\u5cb3\u7236\u5927\u4eba\uff0c\u4e5f \u5c31\u53d8\u6210\u4e86\u859b\u5927\u4fa0\u7684\u513f\u5973\u4eb2\u5bb6\u2026\u3002\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u62a2\u7740\u9053\uff1a\u201c\u56e0\u4e3a\u65e0\u8bba\u600e\u9ebd\u8bf4\uff0c\u859b\u5927\u4fa0\u7684\u5ab3\u5987\u81f3\u5c11\u6709\u4e00\u534a\u662f\u5de6\u5e84\u4e3b\u7684\u5973\u513f\u3002\u4e24\u4eba\u5fc3 \u91cc\u5934\u7eb5\u7136\u4e0d\u613f\u610f\uff0c\u53ef\u4e5f\u6ca1\u6cd5\u5b50\u4e0d\u627f\u8ba4\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u6b63\u662f\u5982\u6b64\uff0c\u5230\u90a3\u65f6\u4e24\u4eba\u96be\u514d\u8fd8\u6709\u51b3\u6597\u4e4b\u5fc3\uff0c\u53ea\u6015\u4e5f\u72e0\u4e0d\u4e0b\u5fc3\u6765\u4e86\uff0c\u56e0\u4e3a \u4e24\u5bb6\u7684\u6068\u6bd5\u7adf\u5df2\u5f88\u9065\u8fdc\u3002\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u62cd\u624b\u7b11\u9053\uff1a\u201c\u8fd9\u6cd5\u5b50\u771f\u5999\u6781\u4e86\u2026\u2026\u201d \u3000\u3000\u5c0f\u9ebb\u4e88\u5ffd\u7136\u9053\uff1a\u201c\u4f46\u4e5f\u8352\u5510\u6781\u4e86\uff0c\u82e5\u6362\u4e86\u662f\u6211\u6211\u4e00\u5b9a\u4e0d\u76f8\u4fe1\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6240\u4ee5\u4ed6\u4eec\u7684\u5fc5\u987b\u5468\u5bc6\uff0c\u884c\u5f97\u8d77\u6765\u66f4\u8981\u505a\u5f97\u5929\u8863\u65e0\u7f1d\uff0c\u90a3\u9ebd\u522b\u4eba\u5c31\u7b97 \u4e0d\u4fe1\uff0c\u4e5f\u4e0d\u80fd\u4e0d\u4fe1\u4e86\u3002\u201d \u3000\u3000\u4ed6\u63a5\u7740\u9053\uff1a\u201c\u8981\u5b9e\u884c\u8fd9\u8ba1\u5212\uff0c\u7b2c\u4e00\uff0c\u81ea\u7136\u662f\u8981\u5f97\u5230\u65bd\u8335\u7684\u540c\u610f\uff0c\u8981\u65bd\u8335\u80af\u88c5\u6b7b\u3002\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u62a2\u7740\u9053\uff1a\u201c\u65bd\u59d1\u5a18\u81ea\u7136\u4e0d\u4f1a\u53cd\u5bf9\u7684\uff0c\u56e0\u4e3a\u5979\u4e5f\u53e6\u6709\u5fc3\u4e0a\u4eba\uff0c\u672c\u6765\u5c31\u4e0d\u80af\u5ac1\u7ed9\u859b\u516c \u5b50\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u542b\u7b11\u9053\uff1a\u201c\u6b63\u662f\u5982\u6b64\uff0c\u6211\u542c\u8bf4\u65bd\u59d1\u5a18\u6240\u7528\u82b1\u7c89\u4ff1\u662f\u4e00\u4f4d\u53f6\u516c\u5b50\u81ea\u4eac\u57ce\u5e26\u6765\u7684\u65f6\uff0c \u5df2\u6709\u4e86\u6000\u7591\uff0c\u90a3\u65f6\u6211\u5c31\u5728\u60f3\uff0c\u4e5f\u8bb8\u65bd\u59d1\u5a18\u662f\u5728\u8bc8\u6b7b\u9003\u5a5a\u3002\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u9053\uff1a\u201c\u6240\u4ee5\u5c31\u8981\u6211\u4eec\u53bb\u8c03\u67e5\u53f6\u76db\u5170\u8fd9\u4e2a\u4eba\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u7b49\u6211\u89c1\u5230\u4ed6\u4eec\u4e24\u4f4d\u65f6\uff0c\u8fd9\u4ef6\u4e8b\u5c31\u5df2\u5b8c\u5168\u6c34\u843d\u77f3\u3002\u201d \u3000\u3000\u4ed6\u63a5\u7740\u9053\uff1a\u201c\u6211\u4e0d\u59a8\u5c06\u8fd9\u4ef6\u4e8b\u4ece\u5934\u5230\u5c3e\u518d\u8bf4\u4e00\u6b21\u3002\u5de6\u660e\u73e0\u548c\u65bd\u8335\u65e9\u5df2\u7ea6\u597d\u4e86\u2018\u6b7b\u2019\u65f6\u7684 \u65f6\u8fb0\uff0c\u6240\u4ee5\u90a3\u8fb9\u65bd\u8335\u4e00\u2018\u6b7b\uff0c\u5de6\u660e\u73e0\u5728\u8fd9\u8fb9\u5c31\u2018\u590d\u6d3b\u2019\u4e86\u3002\u65bd\u8335\u81ea\u7136\u65e9\u5df2\u5c06\u81ea\u5df2\u2018\u6b7b\u2019\u65f6\u6240 \u7a7f\u7684\u8863\u670d\u548c\u5c4b\u5b50\u91cc\u7684\u9635\u8bbe\u5168\u90fd\u544a\u8bc9\u4e86\u5de6\u660e\u73e0\u3002\u6240\u4ee5\u5de6\u660e\u73e0\u2018\u590d\u6d3b\u2019\u5f8c\u624d\u80fd\u8bf4\u5f97\u5206\u6beb\u4e0d\u5dee\u3002\u201d \u3000\u3000\u201c\u4e3a\u4e86\u65bd\u8335\u8981\u88c5\u6b7b\u3002\u6240\u4ee5\uff0c\u5fc5\u9700\u8981\u501f\u4e00\u4e2a\u4eba\u7684\u5c38\u8eab\uff0c\u6070\u5de7\u90a3\u65f6\u77f3\u5927\u59d1\u5a18\u5df2\u75c5\u5371\uff0c\u6240\u4ee5\u859b \u516c\u4e88\u5c31\u9009\u4e0a\u4e86\u5979\u3002\u201d \u3000\u3000\u201c\u859b\u516c\u5b50\u4e70\u901a\u4e86\u77f3\u5927\u59d1\u5a18\u7684\u4e8c\u53d4\uff0c\u5728\u4eba\u6b7b\u65f6\u5c06\u5979\u7684\u4f53\u6389\u5305\u6362\u8d70\uff0c\u6539\u626e\u9001\u5230\u65bd\u8335\u7684\u95fa\u623f \u91cc\uff0c\u5c06\u6d3b\u7684\u65bd\u8335\u6362\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u6881\u5988\u5bf9\u65bd\u8335\u7231\u5982\u5df2\u51fa\uff0c\u4e00\u5fc3\u60f3\u5979\u80fd\u5e78\u798f\u8fd9\u4ef6\u4e8b\u7740\u6ca1\u6709\u6881\u5988\u6210\u5168\uff0c\u5c31\u6839\u672c\u505a\u4e0d\u6210\u4e86\u3002\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u695a\u7559\u9999\u624d\u957f\u957f\u5410\u51fa\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u6700\u56f0\u96be\u7684\u5730\u65b9\uff0c\u5c31\u662f\u8981\u5c06\u65f6\u95f4\u62ff\u634f\u5f97 \u5206\u6beb\u4e0d\u5dee\uff0c\u5176\u9980\u7684\u5012\u5e76\u6ca1\u6709\u4ec0\u9ebd\u7279\u522b\u56f0\u96be\u4e4b\u5904\u3002\u201d \u3000\u3000\u5c0f\u9ebb\u5b50\u4e5f\u957f\u957f\u6bb4\u51fa\u53e3\u6c14\uff0c\u7b11\u9053\uff1a\u201c\u542c\u4f60\u8fd9\u9ebd\u6837\u4e00\u8bf4\uff0c\u8fd9\u4ef6\u4e8b\u5012\u771f\u7684\u50cf\u662f\u7b80\u5355\u5f97\u5f88\uff0c\u53ea\u4e0d \u8fc7\u4f60\u82e5\u4e0d\u8bf4\uff0c\u6211\u662f\u4e00\u8f88\u5b50\u4e5f\u60f3\u4e0d\u901a\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u73b0\u5728\u4f60\u5df2\u60f3\u901a\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u5c0f\u9ebb\u5b50\u9053\uff1a\u201c\u8fd8\u6709\u70b9\u60f3\u4e0d\u901a\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u5c0f\u5e2d\u5b50\u9053\uff1a\u201c\u5de6\u59d1\u5a18\u65e2\u7136\u6839\u672c\u6ca1\u6709\u6b7b\uff0c\u5de6\u4e8c\u7237\u600e\u4f1a\u76f8\u4fe1\u5979\u6b7b\u4e86\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u8fd9\u81ea\u7136\u56e0\u4e3a\u5de6\u59cb\u5a18\u65e9\u5df2\u5c06\u90a3\u4e9b\u540d\u533b\u5168\u90fd\u4e70\u901a\u3002\u82e5\u662f\u627e\u5341\u4f4d\u540d\u533b\u90fd\u8bca\u65ad\u4f60\u5df2 \u75c5\u4eba\u818f\u8093\uff0c\u65e0\u53ef\u6551\u836f\u65f6\uff0c\u53ea\u6015\u8fde\u4f60\u81ea\u5df2\u90fd\u4f1a\u8ba4\u4e3a\u81ea\u5df2\u6b7b\u5b9a\u4e86\uff0c\u4f55\u51b5\u2026\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u5411\u7a97\u5916\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f55\u51b5\u90a3\u5176\u4e2d\u8fd8\u6709\u4f4d\u5f20\u7b80\u658b\u5148\u751f\uff0c\u5f20\u8001\u5148\u751f\u4e0b\u7684\u8bca\u65ad\uff0c\u53c8\u6709 \u8c01\u80fd\u4e0d\u4fe1\uff0c\u5f20\u8001\u5148\u751f\u82e5\u662f\u8bf4\u4e00\u4e2a\u4eba\u6b7b\u4e86\uff0c\u8c01\u6562\u76f8\u4fe1\u90a3\u4eba\u8fd8\u80fd\u6d3b\u5f97\u6210\uff1f\u201d \u3000\u3000\u53ea\u542c\u7a97\u5916\u4eba\u5927\u7b11\u9053\uff1a\u201c\u9a82\u5f97\u597d\uff0c\u9a82\u5f97\u597d\u6781\u4e86\uff0c\u53ea\u4e0d\u8fc7\u6211\u8001\u5934\u5b50\u65e2\u7136\u53f7\u79f0\u767e\u75c5\u7686\u6cbb\uff0c\u8fd8\u600e \u80fd\u4e0d\u6cbb\u6cbb\u4eba\u5bb6\u7684\u76f8\u601d\u75c5\uff0c\u6240\u4ee5\u8fd9\u6b21\u4e5f\u53ea\u597d\u8001\u4e0b\u8138\u6765\u9a97\u6b21\u4eba\u4e86\u3002\u201d \u3000\u3000\u957f\u7b11\u58f0\u4e2d\uff0c\u5f20\u7b80\u658b\u4e5f\u63a8\u95e8\u800c\u4eba\u3002 \u3000\u3000\u5de6\u660e\u73e0\u3001\u859b\u658c\u3001\u65bd\u8335\u3001\u65f6\u76db\u5170\u56db\u4e2a\u4eba\u7acb\u523b\u8d77\u62dc\u5012\u3002 \u3000\u3000\u695a\u7559\u9999\u5df2\u957f\u63d6\u7b11\u9053\uff1a\u201c\u8001\u5148\u751f\u4e0d\u4f46\u80fd\u6cbb\u767e\u75c5\uff0c\u6cbb\u76f8\u601d\u75c5\u7684\u624b\u6bb5\u66f4\u662f\u9ad8\u4eba\u4e00\u7b49\u3002\u201d \u3000\u3000\u5f20\u7b80\u658b\u6447\u5934\u7b11\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\u9999\u5e05\u65e5\u5f8c\u82e5\u4e5f\u5f97\u4e86\u76f8\u601d\u75c5\uff0c\u5207\u83ab\u5fd8\u4e86\u6765\u627e\u8001\u592b\u3002\u201d\u74dc \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u90a3\u662f\u4e07\u4e07\u4e0d\u7528\u4e86\u7684\u3002\u201d \u3000\u3000\u5f20\u7b80\u658b\u7b11\u772f\u772f\u9053\uff1a\u201c\u53ef\u60dc\u7684\u662f\uff0c\u82e5\u6709\u90a3\u5bb6\u7684\u5c11\u5973\u4e3a\u9999\u5e05\u5f97\u4e86\u76f8\u601d\u75c5\uff0c\u8001\u592b\u6015\u4e5f\u6cbb\u4e0d\u4e86\uff0c \u82e5\u8bf4\u9999\u5e05\u4e3a\u8c01\u5bb6\u5c11\u5973\u5f97\u4e86\u76f8\u601d\u75c5\uff0c\u90a3\u6015\u5929\u4e0b\u518d\u4e5f\u65e0\u4eba\u76f8\u4fe1\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u800c\u4e0d\u8bed\uff0c\u56e0\u4e3a\u4ed6\u53d1\u73b0\u77f3\u7ee3\u4e91\u6b63\u6014\u76ef\u7740\u4ed6\u3002 \u3000\u3000\u5f20\u7b80\u658b\u6276\u8d77\u4e86\u5de6\u660e\u73e0\uff0c\u542b\u7b11\u9053\uff1a\u201c\u8001\u592b\u8fd9\u6b21\u7b54\u5e94\u76f8\u52a9\uff0c\u9664\u4e86\u611f\u65bc\u4f60\u4eec\u7684\u75f4\u60c5\u5916\uff0c\u5b9e\u5728\u89c9 \u5f97\u4f60\u4eec\u7684\u8ba1\u5212\u975e\u4f46\u65b0\u5947\u6709\u8da3\uff0c\u800c\u4e14\u7684\u786e\u53ef\u7b97\u662f\u5929\u8863\u65e0\u7f1d\uff0c\u53ea\u53ef\u60dc\u4f60\u4eec\u4e3a\u4f55\u4e0d\u8fdf\u4e0d\u65e9\uff0c\u504f\u504f\u8981 \u7b49\u5230\u9999\u5e05\u6765\u65f6\u624d\u5b9e\u884c\uff0c\u96be\u9053\u4f60\u4eec\u60f3\u81ea\u627e\u9ebb\u9887\u4e0d\u6210\u3002\u201d \u3000\u3000\u5de6\u660e\u73e0\u7ea2\u7740\u8138\uff0c\u560e\u7740\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u539f\u56e0\u6211\u5012\u77e5\u9053\u3002\u201d \u3000\u3000\u5f20\u7b80\u658b\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u5c31\u662f\u8981\u7b49\u6211\u6765\uff0c\u597d\u6559\u6211\u53bb\u505a\u4ed6\u4eec\u7684\u8bf4\u5ba2\uff0c\u56e0\u4e3a\u6211\u65e2\u4eb2\u773c\u89c1\u5230\u6b64\u4e8b\uff0c \u5c31\u4e0d\u80fd\u4e0d\u7ba1\uff0c\u8c01\u90fd\u77e5\u9053\u6211\u662f\u4e2a\u6700\u597d\u7ba1\u95f9\u4e8b\u7684\u4eba\u3002\u201d \u3000\u3000\u4ed6\u53c8\u7b11\u9053\uff1a\u201c\u4ed6\u5012\u4e5f\u77e5\u9053\u6211\u82e5\u53bb\u505a\u8bf4\u5ba2\uff0c\u859b\u5927\u4fa0\u548c\u65bd\u4e3e\u4eba\u5bf9\u8fd9\u4ef6\u4e8b\u4e5f\u4e0d\u80fd\u4e0d\u4fe1\u4e86\uff0c\u56e0 \u4e3a\u2026\u3002\u201d \u3000\u3000\u5f20\u7b80\u658b\u622a\u53e3\u7b11\u9053\uff1a\u201c\u56e0\u4e3a\u6c5f\u6e56\u4e2d\u4eba\u4eba\u90fd\u77e5\u9053\u9999\u5e05\u4e00\u8a00\u4e5d\u9f0e\uff0c\u53ea\u8981\u662f\u695a\u9999\u5e05\u8bf4\u51fa\u6765\u7684\u8bdd\uff0c \u5c31\u4e07\u4e07\u4e0d\u4f1a\u662f\u5047\u7684\u3002\u201d \u3000\u3000\u4ed6\u53c8\u8f6c\u5411\u5de6\u660e\u73e0\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u7684\u5982\u610f\u7b97\u76d8\u6253\u7684\u5012\u4e0d\u9519\uff0c\u53ea\u53ef\u501f\u4f60\u4eec\u8fd8\u662f\u5fd8\u4e86\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u5de6\u660e\u73e0\u5782\u9996\u9053\uff1a\u201c\u524d\u8f88\u6307\u6559\u3002\u201d \u3000\u3000\u5f20\u7b52\u658b\u9053\uff1a\u201c\u4f60\u4eec\u7adf\u5fd8\u8bb0\u4e86\u695a\u9999\u7684\u662f\u8c01\u4e5f\u9a97\u4e0d\u8fc7\u7684\uff0c\u5982\u4eca\u79d8\u5bc6\u5df2\u88ab\u4ed6\u63ed\u7a7f\uff0c\u96be\u9053\u8fd8\u60f3\u4ed6 \u53bb\u4e3a\u4f60\u4eec\u505a\u8bf4\u5ba2\u9ebd\uff1f\u201d \u3000\u3000\u5de6\u660e\u73e0\u7b49\u56db\u4eba\u53c8\u4e00\u8d77\u62dc\u5012\u9053\uff1a\u201c\u6c42\u9999\u5e05\u6210\u5168\uff0c\u665a\u8f88\u611f\u6fc0\u4e0d\u5c3d\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u4f55\u5fc5\u6c42\u6211\u3002\u6211\u65e9\u5c31\u8bf4\u8fc7\uff0c\u6211\u662f\u4e2a\u6700\u559c\u6b22\u7ba1\u95f2\u4e8b\u7684\u4eba\uff0c\u800c\u4e14\u4ece\u6765\u4e0d\u559c \u6b22\u715e\u98ce\u666f\uff0c\u80fd\u89c1\u5230\u6709\u60c5\u4eba\u7ec8\u6210\u7737\u5c5e\uff0c\u8981\u6211\u505a\u4ec0\u9ebd\u90fd\u6ca1\u6709\u5173\u7cfb\u3002\u201d \u3000\u3000\u5f20\u7b80\u658b\u9053\uff1a\u201c\u695a\u9999\u5e05\u679c\u7136\u4e0d\u5080\u4e3a\u695a\u9999\u5e05\uff0c\u5176\u5b9e\u8001\u592b\u4e5f\u65e9\u5df2\u60f3\u8d77\uff0c\u9999\u5e05\u63ed\u7834\u8fd9\u79d8\u5bc6\uff0c\u53ea\u4e0d \u8fc7\u4e0d\u613f\u522b\u4eba\u5c06\u4f60\u770b\u505a\u7cca\u6d82\u866b\u800c\u5df2\u3002\u201d \u3000\u3000\u4ed6\u8f6c\u5411\u5de6\u660e\u73e0\u7b49\u4eba\uff0c\u63a5\u7740\u9053\uff1a\u201c\u5982\u4eca\u4f60\u4eec\u4e5f\u8be5\u5f97\u5230\u4e2a\u6559\u8bad\uff0c\u90a3\u5c31\u662f\u4f60\u4eec\u4ee5\u5f8c\u65e0\u8bba\u8981\u6c42\u9999 \u5e05\u505a\u4ec0\u9ebd\u4e8b\u6700\u597d\u90fd\u5148\u5411\u4ed6\u8bf4\u660e\uff0c\u65e0\u8bba\u8c01\u60f3\u8981\u8ba9\u9999\u5e05\u4e0a\u5f53\uff0c\u5230\u5f8c\u6765\u603b\u4f1a\u53d1\u73b0\u4e0a\u5f53\u7684\u662f\u81ea\u5df1\u3002\u201d \u3000\u3000\u5c0f\u79c3\u4e88\u548c\u5c0f\u9ebb\u5b50\u5e76\u4e0d\u7b97\u5f88\u5c0f\u4e86\uff0c\u6709\u65f6\u4ed6\u4eec\u751a\u81f3\u5df2\u5f88\u50cf\u5927\u4eba\uff0c\u81f3\u5c11\u4ed6\u4eec\u90fd\u4f1a\u88c5\u51fa\u5927\u4eba\u7684\u6a21 \u6837\u3002 \u3000\u3000\u4f46\u73b0\u5728\u4ed6\u4eec\u770b\u6765\u5374\u5f7b\u5934\u5f7b\u5c3e\u662f\u4e24\u4e2a\u5c0f\u5b69\u5b50\uff0c\u9762\u4e14\u662f\u4e24\u4e2a\u53d7\u4e86\u59d4\u5c48\u7684\u5c0f\u5b69\u5b50\uff0c\u65e0\u8bba\u4efb\u4f55\u5165 \u90fd\u53ef\u4ee5\u5f88\u5bb9\u6613\u7684\u5c31\u5728\u4ed6\u4eec\u5632\u8d77\u7684\u5634\u4e0a\u6302\u4e24\u4e2a\u6cb9\u74f6\u3002 \u3000\u3000\u65b9\u65bd\u8335\u548c\u6881\u5988\u575a\u6301\u8981\u8bf7\u5927\u5bb6\u5230\u201c\u5979\u4eec\u5bb6\u91cc\u201d\u53bb\u559d\u4e24\u676f\uff0c\u5f20\u7b80\u658b\u81ea\u7136\u6ca1\u6709\u53bb\uff0c\u56e0\u4e3a\u4ed6\u5df2\u591f \u8001\u4e86\uff0c\u800c\u4e14\u53c8\u662f\u4f4d\u201c\u540d\u533b\u201d\uff0c\u603b\u89c9\u5f97\u5403\u8fc7\u4e86\u665a\u996d\u5f8c\u82e5\u662f\u518d\u5403\u4e1c\u897f\u5c31\u662f\u548c\u81ea\u5df1\u7684\u80a0\u80c3\u8fc7\u4e0d\u53bb\u3002 \u3000\u3000\u201c\u559d\u9152\u201d\u5728\u4ed6\u773c\u4e2d\u770b\u6765\uff0c\u66f4\u597d\u50cf\u662f\u5728\u62fc\u547d\u3002 \u3000\u3000\u5de6\u660e\u73e0\u548c\u859b\u658c\u4e5f\u6ca1\u6709\u53bb\uff0c\u56e0\u4e3a\u4ed6\u4eec\u8981\u56de\u53bb\u7ee7\u7eed\u6f14\u4ed6\u4eec\u7684\u620f\uff0c\u81ea\u7136\u4e0d\u80fd\u5192\u9669\u88ab\u522b\u4eba\u89c1\u5230\u4ed6 \u4eec\u3002 \u3000\u3000\u6881\u5988\u548c\u65bd\u8335\u4e5f\u6ca1\u6709\u575a\u6301\u8981\u4ed6\u4eec\u53bb\u3002 \u3000\u3000\u53ef\u6068\u7684\u662f\uff0c\u5c0f\u79c3\u5b50\u548c\u5c0f\u9ebb\u5b50\u867d\u7136\u60f3\u53bb\uff0c\u5374\u6ca1\u6709\u4eba\u8bf7\u4ed6\u4eec\u3002\u8fd9\u5bf9\u4e24\u4e2a\u534a\u5927\u4e0d\u5c0f\u7684\u5b69\u5b50\u7684\u81ea \u5c0a\u5fc3\u5b9e\u5728\u662f\u79cd\u6253\u51fb\u3002 \u3000\u3000\u5c0f\u9ebb\u5b50\u7792\u7740\u5634\uff0c\u51b3\u5fc3\u4e0d\u63d0\u8fd9\u4ef6\u4e8b\u3002 \u3000\u3000\u5c0f\u79c3\u5b50\u8fde\u60f3\u90fd\u4e0d\u6562\u53bb\u60f3\u3002 \u3000\u3000\u4ed6\u5c3d\u91cf\u53bb\u60f3\u522b\u7684\u4e8b\uff0c\u5634\u91cc\u8d4c\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u8bc8\u75c5\uff0c\u53c8\u88c5\u6b7b\uff0c\u53c8\u626e\u9b3c\uff0c\u53c8\u8d39\u5fc3\u673a\uff0c\u53c8\u8d39\u5fc3 \u4e8b\uff0c\u53c8\u6d41\u773c\u6cea\uff0c\u4e3a\u7684\u5374\u53ea\u4e0d\u8fc7\u662f\u4e2a\u2018\u60c5\u2019\u5b57\uff0c\u3002\u3002\u3002\u201d\u74dc \u3000\u3000\u4ed6\u88c2\u5f00\u5634\u8f7b\u7b11\u4e86\u51e0\u58f0\uff0c\u624d\u5927\u58f0\u9053\uff1a\u201c\u6211\u771f\u4e0d\u61c2\u8fd9\u89c1\u9b3c\u7684\u2018\u60c5\u2019\u5b57\u6709\u4ec0\u9ebd\u9b54\u529b\uff0c\u7adf\u80fd\u4ee4\u8fd9 \u9ebd\u591a\u4eba\u4e3a\u4e86\u5b83\u53d1\u75af\u75c5\u3002\u201d \u3000\u3000\u5c0f\u9ebb\u5b50\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u61c2\uff0c\u6211\u53ea\u671b\u8fd9\u8f88\u5b50\u6c38\u8fdc\u83ab\u8981\u548c\u8fd9\u4e2a\u5b57\u53d1\u751f\u5173\u7cfb\u3002\u201d \u3000\u3000\u4ed6\u7528\u529b\u8e22\u8d77\u5757\u77f3\u5934\uff0c\u5c31\u597d\u50cf\u4e00\u811a\u5c31\u80fd\u7279\u8fd9\u201c\u60c5\u201d\u5b57\u6c38\u8fdc\u8e22\u8d70\u4f3c\u7684\uff0c\u5374\u4e0d\u77e5\u201c\u60c5\u201d\u5b57\u548c\u77f3 \u5934\u7edd\u4e0d\u6837\uff0c\u4f60\u65e0\u8bba\u7528\u591a\u5927\u529b\u6c14\uff0c\u90fd\u8e22\u4e0d\u8d70\u7684\uff0c\u4f60\u4ee5\u4e3a\u5df2\u5c06\u5b83\u8e22\u8d70\u65f6\uff0c\u5b83\u4e00\u4e0b\u5b50\u5374\u53c8\u5f39\u4e86\u56de \u6765\uff0c\u4f60\u7528\u7684\u529b\u6c14\u8d8a\u5927\uff0c\u5b83\u5f39\u56de\u6765\u4e5f\u8d8a\u5927\u3002\u4f60\u5149\u60f3\u4e00\u811a\u5c06\u5b83\u8e29\u788e\uff0c\u8fd9\u811a\u5f80\u5f80\u4f1a\u8e29\u5728\u4f60\u81ea\u5df1\u5fc3 \u4e0a\u3002 \u3000\u3000\u5c0f\u79c3\u5b50\u6c89\u9ed8\u4e86\u534a\u5929\uff0c\u5ffd\u7136\u53c8\u9053\uff1a\u201c\u5582\uff0c\u4f60\u770b\u5de6\u4e8c\u7237\u771f\u7684\u4f1a\u8ba9\u4ed6\u5973\u513f\u5ac1\u7ed9\u859b\u4e8c\u5c11\u5417\uff1f\u201d \u3000\u3000\u5c0f\u9ebb\u5b50\u9053\uff1a\u201c\u4ed6\u4e0d\u80af\u4e5f\u4e0d\u884c\uff0c\u56e0\u4e3a\u4ed6\u5973\u513f\u7684\u2018\u9b42\u2019\u5df2\u662f\u522b\u4eba\u7684\u4e86\u3002\u201d \u3000\u3000\u4ed6\u4f3c\u4e4e\u89c9\u5f97\u81ea\u5df1\u8fd9\u53e5\u201c\u53cc\u5173\u8bdd\u201d\u8bf4\u5f97\u5f88\u5999\uff0c\u5fcd\u4e0d\u4f4f\u5403\u5403\u7684\u7b11\u4e86\u8d77\u6765\uff0c\u809a\u5b50\u91cc\u7684\u6c14\u4e5f\u6d88\u4e86 \u4e00\u534a\u3002 \u3000\u3000\u5c0f\u79c3\u5b50\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f46\u859b\u5e84\u4e3b\u5462\uff1f\u4f1a\u4e0d\u4f1a\u8981\u8fd9\u5ab3\u5987\uff1f\u201d \u3000\u3000\u5c0f\u9ebb\u5b50\u9053\uff1a\u201c\u82e5\u662f\u6362\u4e86\u522b\u4eba\u53bb\u8bf4\uff0c\u859b\u5e84\u4e3b\u4e5f\u8bb8\u4e0d\u7b54\u5e94\uff0c\u4f46\u695a\u9999\u5e05\u53bb\u8bf4\uff0c\u4ed6\u4e5f\u662f\u6ca1\u6cd5\u5b50\u4e0d \u7b54\u5e94\u7684\u3002\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u6b20\u9999\u5e05\u60c5\uff0c\u597d\u50cf\u6bcf\u4e2a\u4eba\u90fd\u6b20\u695a\u9999\u5e05\u7684\u60c5\u3002\u201d \u3000\u3000\u5c0f\u9ebb\u5b50\u6487\u4e86\u6487\u5634\uff0c\u9053\uff1a\u201c\u6240\u4ee5\u90a3\u8001\u592a\u5a46\u624d\u6b7b\u62d6\u6d3b\u62c9\u7684\u8981\u8bf7\u4ed6\u53bb\u559d\u9152\u2026\u3002\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u5ffd\u7136\u201c\u5427\u201d\u7684\u7ed9\u4e86\u4ed6\u4e00\u5df4\u638c\u9053\uff1a\u201c\u4f60\u8fd9\u9ebb\u5b50\uff0c\u4f60\u4ee5\u4e3a\u5979\u771f\u662f\u60f3\u8bf7\u9999\u5e05\u559d\u9152\u5417\uff1f\u201d \u3000\u3000\u5c0f\u9ebb\u5b50\u88ab\u6253\u5f97\u7ffb\u767d\u773c\uff0c\u5403\u5403\u9053\uff1a\u201c\u4e0d\u662f\u8bf7\u559d\u9152\u662f\u5e72\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8bf4\u4f60\u662f\u9ebb\u5b50\uff0c\u4f60\u771f\u662f\u9ebb\u5b50\uff0c\u4f60\u96be\u9053\u770b\u4e0d\u51fa\u5979\u4eec\u8fd9\u662f\u5728\u66ff\u9999\u5e05\u505a \u5a92\u5417\uff1f\u201d \u3000\u3000\u5c0f\u9ebb\u5b50\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u505a\u5a92\uff1f\u505a\u4ec0\u9ebd\u5a92\uff1f\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u9053\uff1a\u201c\u81ea\u7136\u662f\u505a\u90a3\u77f3\u7ee3\u4e91\u59d1\u5a18\u7684\u5a92\uff0c\u5979\u4eec\u89c9\u5f97\u6b20\u4e86\u695a\u5927\u54e5\u7684\u60c5\uff0c\u6240\u4ee5\u5c31\u60f3\u5c06\u695a\u5927 \u54e5\u548c\u77f3\u59d1\u5a18\u62c9\u643a\u5230\u4e00\u8d77\u3002\u201d \u3000\u3000\u5c0f\u9ebb\u5b50\u4e00\u62cd\u5df4\u638c\uff0c\u7b11\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u6211\u672c\u5728\u5947\u602a\uff0c\u90a3\u4f4d\u77f3\u59d1\u5a18\u4e00\u4e2a\u6ca1\u51fa\u95e8\u7684\u95fa\u5973\u600e\u9ebd\u80af\u53c1 \u66f4\u534a\u7834\u7684\u8dd1\u5230\u4eba\u5bb6\u91cc\u53bb\u559d\u9152\u3002\u539f\u6765\u5979\u65e9\u5df2\u770b\u4e0a\u6211\u4eec\u695a\u5927\u54e5\u4e86\u3002\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u7b11\u9053\uff1a\u201c\u50cf\u695a\u5927\u54e5\u8fd9\u6837\u7684\u4eba\uff0c\u4eba\u6709\u4eba\u624d\uff0c\u50cf\u6709\u50cf\u8c8c\uff0c\u5973\u5b69\u5b50\u82e5\u770b\u4e0d\u4e0a\u4ed6\uff0c\u90a3\u624d\u771f \u662f\u602a\u4e8b\u3002\u201d \u3000\u3000\u5c0f\u9ebb\u5b50\u9053\uff1a\u201c\u53ef\u662f\u2026\u2026\u695a\u5927\u54e5\u770b\u5f97\u4e0a\u90a3\u4f4d\u77f3\u59d1\u5a18\u5417\uff1f\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u6478\u7740\u8111\u888b\uff0c\u9053\uff1a\u201c\u8fd9\u5012\u96be\u8bf4\u4e86\u2026\u3002\u2014\u4e0d\u8fc7\u90a3\u4f4d\u77f3\u59d1\u5a18\u5012\u4e5f\u53ef\u7b97\u662f\u4f4d\u7f8e\u4eba\u513f\uff0c\u4e5f\u53ef \u914d\u5f97\u695a\u5927\u54e5\u4e86\u3002\u6211\u5012\u5f88\u613f\u610f\u559d\u4ed6\u4eec\u8fd9\u676f\u559c\u9152\u3002\u201d \u3000\u3000\u5c0f\u9ebb\u5b50\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u8fd9\u4ef6\u4e8b\u7684\u7ed3\u5c40\u5230\u662f\u7686\u5927\u6b22\u559c\u53ea\u5269\u4e0b\u6211\u4eec\u4e24\u4e2a\uff0c\u53c1\u66f4\u534a\u591c\u7684\u8fd8\u50cf \u662f\u5b64\u9b42\u91ce\u9b3c\u822c\u5728\u8def\u4e0a\u7a77\u901b\uff0c\u809a\u5b50\u53c8\u997f\u5f97\u8981\u6b7b\u3002\u3002 \u3000\u3000\u5c0f\u79c3\u5b50\u201c\u5427\u201d\u7684\u53c8\u7ed9\u4e86\u4ed6\u4e00\u5df4\u638c\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u771f\u6ca1\u51fa\u606f\uff0c\u4eba\u5bb6\u4e0d\u8bf7\u54b1\u4eec\u5403\u5bb5\u591c\uff0c\u54b1\u4eec \u81ea\u5df1\u96be\u9053\u4e0d\u4f1a\u53bb\u5403\u3002\u90a3\u8fb9\u5c31\u6709\u4e2a\u644a\u5b50\u8fd8\u6ca1\u6709\u6253\u70ca\uff0c\u6211\u65e9\u5df2\u55c5\u5230\u9152\u9999\u4e86\u3002\u201d \u3000\u3000\u957f\u8857\u5c3d\u5934\u679c\u7136\u8fd8\u6709\u76cf\u5b64\u706f\u3002 \u3000\u3000\u706f\u5149\u4e0b\uff0c\u4e00\u6761\u731b\u864e\u822c\u7684\u5927\u6c49\u6b63\u7b95\u56e0\u5728\u957f\u677f\u51f3\u4e0a\u5f00\u6000\u7545\u996e\uff0c\u9762\u524d\u7684\u9152\u89d2\u5df2\u5806\u6ee1\u4e86\u4e00\u5927\u7247\u3002 \u3000\u3000\u5356\u9152\u7684\u8001\u5510\u65e9\u5df2\u54c8\u6b20\u8fde\u5929\uff0c\u6068\u4e0d\u5f97\u65e9\u4e9b\u6536\u644a\u5b50\uff0c\u5374\u53c8\u4e0d\u6562\u50ac\u8fd9\u5ba2\u4eba\u5feb\u8d70\uff0c\u4ed6\u5356\u4e86\u4e00\u8f88\u5b50 \u9152\uff0c\u4e5f\u6ca1\u6709\u89c1\u8fc7\u8fd9\u6837\u7684\u9152\u9b3c\u3002 \u3000\u3000\u867d\u5df2\u5165\u51ac\uff0c\u8fd9\u5927\u6c49\u5374\u4ecd\u7cbe\u8d64\u7740\u4e0a\u8eab\u9732\u51fa\u4e00\u8eab\u9ed1\u52d8\u7684\u7684\u76ae\u80a4\uff0c\u5c31\u50cf\u662f\u6233\u6253\u7684\u8001\u5510\u521a\u5c06\u4e24\u89d2 \u9152\u5012\u5728\u4e00\u4e2a\u5927\u6d77\u7897\u91cc\uff0c\u8fd9\u5927\u6c49\u957f\u5438\u6c34\u822c\u4e00\u5f20\u5634\u3002\u6574\u6574\u5341\u4e8c\u4e24\u4e0a\u597d\u9ec4\u9152\u7acb\u523b\u5c31\u70b9\u6ef4\u65e0\u5b58\u3002 \u3000\u3000\u8001\u5510\u7528\u4e24\u53ea\u624b\u5012\u9152\uff0c\u5374\u8fd8\u6ca1\u6709\u4ed6\u4e00\u5f20\u5634\u559d\u5f97\u5feb\u3002 \u3000\u3000\u5c0f\u79c3\u5b50\u548c\u5c0f\u9ebb\u5b50\u4e5f\u4e0d\u7981\u770b\u679c\u4e86\u3002 \u3000\u3000\u5c0f\u9ebb\u4e88\u5410\u4e86\u5410\u820c\u5934\uff0c\u6084\u58f0\u9053\uff1a\u201c\u597d\u5bb6\u4f19\uff0c\u8fd9\u4f4d\u4ec1\u5144\u53ef\u771f\u662f\u4e2a\u5927\u9152\u7f38\u3002\u201d\u74dc \u3000\u3000\u5c0f\u79c3\u5b50\u7728\u4e86\u7728\u773c\uff0c\u9053\uff1a\u201c\u4ed6\u9152\u91cf\u867d\u4e0d\u9519\uff0c\u4e5f\u672a\u5fc5\u5c31\u80fd\u6bd4\u5f97\u4e0a\u6211\u4eec\u7684\u695a\u5927\u54e5\u3002\u201d \u3000\u3000\u5c0f\u9ebb\u5b50\u7b11\u9053\uff1a\u201c\u90a3\u5f53\u7136\uff0c\u6c5f\u6e56\u4e2d\u8c01\u4e0d\u77e5\u9053\u695a\u5927\u54e5\u975e\u4f46\u8f7b\u529f\u65e0\u6bd4\uff0c\u9152\u91cf\u4e5f\u6ca1\u6709\u4eba\u6bd4\u5f97 \u4e0a\u3002\u201d \u3000\u3000\u4ed6\u4eec\u8bf4\u8bdd\u58f0\u97f3\u672c\u4e0d\u5927\uff0c\u8001\u5510\u5c31\u8fde\u4e00\u4e2a\u5b57\u6ca1\u6709\u542c\u5230\uff0c\u4f46\u90a3\u5927\u6c49\u7684\u8033\u6735\u5374\u50cf\u662f\u7279\u522b\u7075\uff0c\u5ffd\u7136 \u4e00\u62cd\u684c\u4e88\u7ad9\u4e86\u8d77\u6765\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4f60\u4eec\u7684\u695a\u5927\u54e5\u662f\u8c01\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u6d53\u7709\u5927\u773c\uff0c\u5c45\u7136\u662f\u4e00\u6761\u5f88\u82f1\u4fca\u7684\u6c49\u5b50\uff0c\u5c24\u5176\u662f\u4e00\u53cc\u773c\u775b\uff0c\u4eae\u5f97\u5c31\u597d\u50cf\u4e24\u9897\u5927\u661f\u661f\u4e00 \u6837\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u8bf4\u8bdd\u7684\u795e\u6c14\u5b9e\u5728\u592a\u51f6\uff0c\u5c0f\u79c3\u4e88\u5c31\u7b2c\u4e00\u4e2a\u4e0d\u670d\u6c14\uff0c\u4e5f\u77aa\u8d77\u773c\u9053\uff1a\u201c\u6211\u4eec\u7684\u5927\u54e5\u561b\uff0c \u65e0\u8bba\u662f\u8c01\u90fd\u7ba1\u4e0d\u7740\u3002\u201d \u3000\u3000\u4ed6\u8bdd\u8fd8\u672a\u8bf4\u5b8c\uff0c\u8fd9\u5927\u6c49\u5ffd\u7136\u5c31\u5230\u4e86\u4ed6\u4eec\u9762\u524d\uff0c\u4e5f\u4e0d\u77e5\u600e\u9ebd\u4f38\u624b\u4e00\u6293\uff0c\u5c31\u5c06\u4e24\u4e2a\u4eba\u5168\u6293\u4e86 \u8d77\u6765\u3002 \u3000\u3000\u5c0f\u79c3\u5b50\u548c\u5c0f\u9ebb\u5b50\u4e5f\u4e0d\u662f\u597d\u5bf9\u4ed8\u7684\uff0c\u4f46\u5728\u8fd9\u4eba\u624b\u91cc\uff0c\u5c31\u597d\u50cf\u53d8\u6210\u4e86\u4e24\u53ea\u5c0f\u9e21\uff0c\u8fde\u52a8\u90fd\u52a8\u4e0d \u4e86\u3002 \u3000\u3000\u548c\u8fd9\u5927\u6c49\u6bd4\u8d77\u6765\uff0c\u8fd9\u4e24\u4eba\u7684\u786e\u4e5f\u548c\u4e24\u53ea\u5c0f\u9e21\u5dee\u4e0d\u591a\u3002 \u3000\u3000\u4ed6\u5c06\u4ed6\u4eec\u63d0\u5f97\u79bb\u5730\u7ea6\u6478\u6709\u4e00\u5c3a\u591a\u9ad8\u770b\u770b\u4ed6\u4eec\u5728\u7a7a\u4e2d\u624b\u821e\u8db3\u8e48\uff0c\u90a3\u53cc\u53d1\u4eae\u7684\u773c\u775b\u91cc\uff0c\u4f3c\u4e4e \u8fd8\u5e26\u7740\u4e9b\u7b11\u610f\u3002 \u3000\u3000\u4f46\u4ed6\u7684\u58f0\u97f3\u8fd8\u662f\u51f6\u5f97\u5f88\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4e2a\u5c0f\u628a\u620f\u542c\u7740\uff0c\u4f60\u4eec\u65b9\u8bf4\u7684\u695a\u5927\u54e5\u82e5\u5c31\u662f\u695a \u7559\u9999\u90a3\u8001\u81ed\u866b\uff0c\u5c31\u5feb\u5e26\u6211\u53bb\u627e\u4ed6\u2026\u3002\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u5927\u9a7e\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u9ebd\u4e1c\u897f\uff0c\u6562\u9a82\u695a\u5927\u54e5\u662f\u8001\u684c\u866b\uff0c\u4f60\u624d\u662f\u4e2a\u5927\u81ed\u866b\u81ed\u866b\u3002\u201d \u3000\u3000\u5c0f\u9ebb\u5b50\u4e5f\u5927\u9a82\u9053\uff1a\u201c\u695a\u5927\u54e5\u53ea\u8981\u7528\u4e00\u53ea\u5c0f\u6307\u5934\uff0c\u5c31\u7684\u5c06\u4f60\u8fd9\u81ed\u866b\u5904\u6b7b\uff0c\u6211\u529d\u4f60\u8fd8\u662f\u2026\u2026 \u8fd8\u662f\u8854\u7740\u5c3e\u5df4\u9003\u5427\u3002\u201d \u3000\u3000\u5c0f\u79c3\u5b50\u9053\uff1a\u201c\u81ed\u866b\u90a3\u6709\u5c3e\u5df4\uff0c\u81ed\u866b\u7684\u5c3e\u5df4\u662f\u957f\u5728\u5934\u4e0a\u7684\uff0c\u6309\u4e5f\u6309\u4e0d\u4f4f\u3002\u201d \u3000\u3000\u4e24\u4eba\u529b\u6c14\u867d\u4e0d\u5927\uff0c\u80c6\u5b50\u5374\u4e0d\u5c0f\uff0c\u9a82\u4eba\u7684\u672c\u4e8b\u66f4\u6613\u662f\u4e00\u7b49\u4e00\u7684\u9ad8\u660e\uff0c\u6b64\u56e0\u5df2\u8c41\u51fa\u53bb\u4e86\uff0c\u7d22 \u6027\u9a82\u4e2a\u75db\u5feb\uff0c\u5c31\u7b97\u8111\u888b\u5f00\u82b1\u4e5f\u7b49\u9a82\u5b8c\u4e86\u518d\u8bf4\u3002 \u3000\u3000\u8c01\u77e5\u8fd9\u5927\u6c49\u53cd\u800c\u7b11\u4e86\uff0c\u5927\u7b11\u9053\uff1a\u201c\u597d\uff0c\u7b97\u4f60\u4eec\u4e24\u4e2a\u5c0f\u628a\u620f\u6709\u79cd\uff0c\u4f46\u522b\u4eba\u6015\u90a3\u8001\u81ed\u866b\uff0c\u6211 \u5374\u4e0d\u6015\uff0c\u82e5\u6bd4\u8d77\u9152\u6765\uff0c\u4ed6\u8fd8\u5dee\u5f97\u591a\uff0c\u4f60\u4eec\u82e5\u4e0d\u4fe1\uff0c\u4e3a\u4f55\u4e0d\u95ee\u4ed6\u53bb\u3002\u201d \u7b2c\u5341\u4e8c\u7ae0\u3000\u4e00\u591c\u7f20\u7ef5>> \u53e4\u9f99\u300a\u695a\u7559\u9999\u7cfb\u5217\xb7\u9b3c\u604b\u4f20\u5947\u300b \u7b2c\u5341\u4e8c\u7ae0\u3000\u4e00\u591c\u7f20\u7ef5 \u3000\u3000\u6c14\u9505\u9e21\u3001\u7ea2\u70e7\u9e2d\u3001\u72ee\u5b50\u5934\u3001\u6e05\u84b8\u9c7c\u2026\u2026\u8fd9\u4e9b\u90fd\u662f\u8981\u8bb2\u7a76\u706b\u5019\u7684\u529f\u592b\u540d\u83dc\u3002\u6881\u5988\u60f3\u5fc5\u65e9\u5df2 \u51c6\u5907\u4e86\u6574\u5929\u3002 \u3000\u3000\u4f46\u8fd9\u4e9b\u83dc\u73b0\u5728\u5374\u8fd8\u662f\u539f\u5c01\u4e0d\u52a8\u7684\u653e\u5728\u684c\u5b50\u4e0a\uff0c\u56e0\u4e3a\u684c\u4e0a\u53ea\u5269\u4e0b\u4e86\u4e24\u4e2a\u4eba\uff0c\u800c\u8fd9\u4e24\u4eba\u8fde\u4e00 \u70b9\u5403\u83b1\u7684\u610f\u601d\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u5ba2\u4eba\u5e76\u6ca1\u6709\u8d70\uff0c\u8d70\u7684\u53cd\u800c\u662f\u4e3b\u4eba\u3002\u6bcf\u4e2a\u4eba\u8d70\u7684\u65f6\u5019\uff0c\u90fd\u6709\u4e00\u5957\u5f88\u597d\u7684\u7406\u7531\u3002\u867d\u7136\u8c01\u90fd\u542c \u51fa\u90a3\u4e9b\u7406\u7531\u662f\u7f16\u7684\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u610f\u601d\u53ea\u4e0d\u8fc7\u662f\u5c06\u695a\u7559\u9999\u548c\u77f3\u7ee3\u4e91\u4e24\u4eba\u5355\u72ec\u7559\u4e0b\u6765\u800c\u5df2\uff0c\u8fd9\u610f\u601d\u975e\u4f46\u695a\u7559\u9999\u61c2\u5f97\uff0c \u77f3\u7ee3\u4e91\u4e5f\u61c2\u5f97\u3002 \u3000\u3000\u5999\u7684\u662f\u5979\u5e76\u6ca1\u6709\u8981\u522b\u4eba\u7559\u4e0b\u6765\uff0c\u81ea\u5df1\u4e5f\u6ca1\u6709\u8d70\u3002 \u3000\u3000\u5979\u62ff\u7740\u7b77\u5b50\uff0c\u8f7b\u8f7b\u6572\u7740\u9152\u676f\uff0c\u50cf\u662f\u60f3\u6572\u788e\u8111\u5b50\u91cc\u7684\u9759\u7530\uff0c\u53c8\u50cf\u662f\u89c9\u5f97\u8fd9\u53cc\u624b\u6ca1\u5904\u5b89\u653e\uff0c \u6240\u4ee5\u8981\u627e\u4e9b\u4e8b\u6765\u505a\u505a\u3002 \u3000\u3000\u5979\u8138\u4e0a\u6709\u8584\u8fab\u7684\u4e00\u5c42\u7ea2\u6655\uff0c\u53c8\u4e0d\u592a\u7ea2\uff0c\u5728\u6de1\u6de1\u7684\u706f\u5149\u4e0b\u770b\u6765\uff0c\u771f\u662f\u8bf4\u4e0d\u51fa\u7684\u5a07\u8273\uff0c\u8bf4\u4e0d \u51fa\u7684\u5996\u5a9a\u3002 \u3000\u3000\u5979\u4f4e\u5782\u7740\u773c\u957f\u957f\u7684\u776b\u6bdb\u5728\u773c\u5e18\u4e0a\uff0c\u5979\u767d\u7389\u822c\u7684\u7259\u9f7f\u8f7b\u8f7b\u54ac\u7740\u7ea2\u5507\uff0c\u54ac\u5f97\u5374\u53c8\u4e0d\u592a\u91cd\u3002 \u3000\u3000\u9662\u5b50\u91cc\u79cb\u98ce\u6b63\u5439\u7740\u68a7\u6850\u3002 \u3000\u3000\u9152\uff0c\u662f\u7fe0\u7eff\u8272\u7684\uff0c\u6d6e\u52a8\u7740\u9635\u9635\u5e7d\u9999\u3002 \u3000\u3000\u5982\u6b64\u4f73\u591c\uff0c\u5982\u6b64\u4f73\u4eba\uff0c\u5982\u6b64\u7f8e\u9152\uff0c\u7eb5\u7136\u4e0d\u996e\uff0c\u4e5f\u8be5\u9189\u4e86\u3002 \u3000\u3000\u5bf9\u4f73\u4eba\u548c\u7f8e\u9152\uff0c\u695a\u7559\u9999\u7684\u7ecf\u9a8c\u4e5f\u8bb8\u6bd4\u5927\u591a\u6570\u7684\u4eba\u90fd\u4e30\u5bcc\u5f97\u591a\uff0c\u4f46\u4e5f\u4e0d\u77e5\u4e3a\u4e86\u4ec0\u9ebd\uff0c\u6b64\u523b \u4ed6\u7684\u5fc3\u7adf\u4e5f\u5728\u8df3\u4e2a\u4e0d\u505c\u3002 \u3000\u3000\u4ed6\u5f88\u5c11\u542c\u5230\u81ea\u5df2\u5fc3\u8df3\u7684\u58f0\u97f3\u3002 \u3000\u3000\u77f3\u7ee3\u4e91\u5ffd\u7136\u62ac\u8d77\u773c\u775b\uff0c\u773c\u89d2\u4ece\u4ed6\u7684\u8111\u4e0a\u6ed1\u5230\u4ed6\u7684\u624b\uff0c\u4f46\u5979\u9762\u4e0a\u5c31\u9732\u51fa\u5bf9\u6d45\u7ebf\u7684\u9152\u6da1\u3002 \u3000\u3000\u5979\u8f7b\u8f7b\u7684\u95ee\uff1a\u201c\u4f60\u4e0d\u656c\u6211\u7684\u9152\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u4f1a\u559d\u9152\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u773c\u76ae\u6d41\u52a8\uff0c\u9053\uff1a\u201c\u4f60\u82e5\u6562\u8ddf\u6211\u62fc\uff0c\u6211\u4e00\u5b9a\u628a\u4f60\u704c\u9189\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u4e5f\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u597d\uff0c\u6211\u656c\u4f60\u4e00\u676f\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u6487\u4e86\u6487\u5634\uff0c\u9053\uff1a\u201c\u591a\u5c0f\u6c14\u8981\u656c\u5c31\u656c\u53c1\u676f\u4f60\u2026\u2026\u4f60\u6015\u6211\u4f1a\u9189\uff1f\u201d \u3000\u3000\u5979\u5f88\u5feb\u7684\u5012\u4e86\u53c1\u676f\u9152\uff0c\u5f88\u5feb\u7684\u5c31\u559d\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u4f1a\u4e0d\u4f1a\u559d\u9152\uff0c\u4ece\u4ed6\u4e3e\u676f\u7684\u59ff\u52bf\u955c\u53ef\u4ee5\u770b\u5f97\u51fa\uff0c\u695a\u7559\u9999\u4e00\u770b\u5979\u4e3e\u676f\u7684\u59ff\u52bf\uff0c\u5c31\u77e5\u9053 \u5979\u81f3\u5c11\u662f\u559d\u8fc7\u9152\u7684\u3002 \u3000\u3000\u4ed6\u4e5f\u559d\u4e86\u53c1\u676f\uff0c\u7b11\u9053\uff1a\u201c\u8001\u5b9e\u8bf4\uff0c\u6211\u5012\u771f\u672a\u60f3\u5230\u4f60\u4f1a\u559d\u9152\uff0c\u800c\u4e14\u9152\u91cf\u8fd8\u4e0d\u9519\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u7528\u773c\u89d2\u779f\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u600e\u9ebd\uff0c\u4f60\u770b\u6211\u50cf\u662f\u4e61\u4e0b\u4eba\uff0c\u662f\u4e0d\u662f\uff1f\u544a\u8bc9\u4f60\uff0c\u4e61\u4e0b\u4eba\u4e5f\u4f1a \u559d\u9152\u7684\u3002\u201d \u3000\u3000\u5979\u53c8\u5f00\u59cb\u5012\u9152\uff0c\u60a0\u60a0\u7684\u63a5\u7740\u9053\uff1a\u201c\u518d\u544a\u8bc9\u4f60\uff0c\u4eca\u5e74\u8fc7\u5e74\u7684\u65f6\u5019\uff0c\u6211\u4e00\u4e2a\u4eba\u5c31\u559d\u4e86\u2014\u7f50\uff0c \u4f60\u4fe1\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u5931\u7b11\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u6211\u5012\u771f\u8be5\u627e\u5c0f\u80e1\u6765\u8ddf\u4f60\u559d\u9152\u624d\u662f\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u9053\uff1a\u201c\u5c0f\u80e1\u662f\u8c01\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4ed6\u53eb\u80e1\u94c1\u82b1\uff0c\u662f\u6211\u7684\u8001\u670b\u53cb\uff0c\u4e5f\u662f\u6211\u7684\u597d\u670b\u53cb\uff0c\u4ed6\u7684\u9152\u91cf\u6bd4\u6211\u5f3a\u5f97\u591a\u3002\u201d \u3000\u3000\u201c\u4eca\u5929\u2026\u53ea\u8981\u8ddf\u4f60\u559d\u9152\u3002\u201d \u3000\u3000\u5979\u4e3e\u8d77\u676f\uff0c\u9053\uff1a\u201c\u6765\uff0c\u6211\u656c\u4f60\u2026\u2026\u4f60\u656c\u6211\u53c1\u676f\uff0c\u6211\u656c\u4f60\u516d\u676f\uff0c\u6211\u7684\u6c14\u6bd4\u4f60\u5927\u5f97\u591a\u4e86 \u5427\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6478\u4e86\u6478\u9f3b\u5b50\uff0c\u9053\uff1a\u201c\u516d\u676f\uff1f\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u201c\u5495\u7792\u201d\uff0c\u5c06\u7b2c\u4e00\u676f\u9152\u559d\u4e86\u4e0b\u53bb\uff0c\u9053\uff1a\u201c\u516d\u676f\uff0c\u4f60\u5acc\u5c11\uff1f\u8fd8\u662f\u591a\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u597d\u50cf\u662f\u591a\u4e86\u4e9b\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u77aa\u7740\u4ed6\uff0c\u5a07\u9053\uff1a\u201c\u600e\u9ebd\uff0c\u4f60\u6015\u6211\u559d\u9189\u662f\u4e0d\u662f\uff1f\u53ea\u8981\u4f60\u81ea\u5df1\u4e0d\u9189\u5c31\u597d\u4e86\uff0c\u83ab\u7ba1 \u6211\u3002\u2019 \u3000\u3000\u8fd9\u516d\u676f\u9152\u5979\u559d\u5f97\u66f4\u5feb\uff0c\u559d\u5b8c\u4e86\u5979\u7684\u8138\u5c31\u66f4\u7ea2\u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\u67d4\u58f0\u9053\uff1a\u201c\u6211\u559d\u5b8c\u4e86\u8fd9\u516d\u676f\uff0c\u5c31\u9001\u4f60\u56de\u53bb\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u773c\u8e29\u4e8e\u8f6c\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u5148\u559d\u5b8c\u518d\u8bf4\u3002\u201d \u3000\u3000\u516d\u676f\u9152\u5728\u695a\u7559\u9999\u8bf4\u6765\u81ea\u7136\u7b97\u4e0d\u4e86\u4ec0\u9ebd\u3002 \u3000\u3000\u4ed6\u559d\u5b8c\u4e86\u516d\u676f\u5c31\u95ee\u9053\uff1a\u201c\u73b0\u5728\u4f60\u8be5\u56de\u53bb\u4e86\u5427\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u54ac\u7740\u6a31\u5507\uff0c\u4f4e\u4e0b\u5934\uff0c\u6162\u6162\u7684\u5c06\u53cc\u65b0\u978b\u8131\u4e86\u4e0b\u6765\uff0c\u5374\u5c06\u4e00\u53cc\u767d\u751f\u751f\u7684\u5927\u8db3\u76d8\u5728\u6905 \u4e0a\uff0c\u7136\u5f8c\u53c8\u6162\u6162\u7684\u62ac\u8d77\u5934\uff0c\u51dd\u6ce8\u7740\u695a\u7559\u9999\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u6b7b\u4e5f\u4e0d\u56de\u53bb\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u4e0d\u56de\u53bb\uff1f\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u53c8\u5728\u5012\u9152\uff0c\u9053\uff1a\u201c\u6ca1\u6709\u4e3a\u4ec0\u9ebd\uff0c\u6211\u5c31\u662f\u4e0d\u60f3\u56de\u53bb\u3002\u201d \u3000\u3000\u5979\u773c\u6ce2\u5728\u695a\u7559\u9999\u8138\u4e0a\u4e00\u8f6c\uff0c\u8e1e\u7136\u9053\uff1a\u201c\u6765\uff0c\u73b0\u5728\u53c8\u8be5\u8f6e\u5230\u4f60\u656c\u6211\u9152\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53ea\u6709\u6478\u9f3b\u5b50\uff0c\u6478\u81ea\u5df1\u7684\u9f3b\u5b50\u3002 \u3000\u3000\u77f3\u7ee3\u4e91\u5782\u4e0b\u5934\uff0c\u5e7d\u5e7d\u7684\u9053\uff1a\u201c\u6211\u7684\u5fc3\u60c5\u4e0d\u597d\uff0c\u6211\u60f3\u559d\u9152\uff0c\u4f60\u96be\u9053\u5c31\u4e0d\u80af\u966a\u966a\u6211\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u6697\u4e2d\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u53ea\u8981\u4f60\u4e0d\u559d\u9189\uff0c\u6211\u966a\u4f60\u559d\u53c1\u5929\u90fd\u6ca1\u5173\u7cfb\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u9053\uff1a\u201c\u4f60\u6015\u6211\u559d\u9189\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u82e6\u7b11\u9053\uff1a\u201c\u8c01\u559d\u9189\u6211\u90fd\u6015\uff0c\u6211\u4ec0\u9ebd\u90fd\u4e0d\u6015\uff0c\u5c31\u6015\u559d\u9189\u9152\u7684\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u4e00\u7b11\u9053\uff1a\u201c\u6211\u4fdd\u8bc1\u7edd\u4e0d\u559d\u9189\uff0c\u884c\u4e0d\u884c\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u53ea\u6709\u4e3e\u676f\uff0c\u9053\uff1a\u201c\u597d\u6211\u656c\u4f60\u3002\u201d \u3000\u3000\u5176\u5b9e\u695a\u7559\u9999\u81ea\u7136\u4e5f\u77e5\u9053\u6ca1\u6709\u5165\u80fd\u4fdd\u8bc1\u81ea\u5df1\u4e0d\u559d\u9189\u7684\uff0c\u552f\u4e00\u80fd\u8981\u81ea\u5df1\u4e0d\u559d\u9189\u7684\u6cd5\u5b50\uff0c\u5c31\u662f \u6839\u672c\u4e0d\u559d\u3002 \u3000\u3000\u8fd9\u6cd5\u5b50\u771f\u4e0d\u7b97\u5999\uff0c\u4f46\u5374\u5f88\u6709\u6548\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u5f88\u591a\u4eba\u90fd\u4e0d\u80af\u7528\u8fd9\u6cd5\u5b50\uff0c\u6240\u4ee5\u6bcf\u5929\u559d\u9189\u9152\u7684\u4eba\u90fd\u5f88\u591a\u3002 \u3000\u3000\u695a\u7559\u9999\u77e5\u9053\u529d\u4eba\u559d\u9152\u56fa\u7136\u4e0d\u597d\uff0c\u529d\u4eba\u4e0d\u559d\u4e5f\u4e0d\u597d\uff0c\u56e0\u4e3a\u4f60\u8d8a\u529d\u4ed6\u4e0d\u559d\uff0c\u4ed6\u5f80\u5f80\u4f1a\u559d\u5f97\u8d8a \u591a\u3002 \u3000\u3000\u4ed6\u53ea\u5e0c\u671b\u77f3\u7ee3\u4e91\u7684\u9152\u91cf\u771f\u7684\u4e0d\u9519\u3002 \u3000\u3000\u77f3\u7ee3\u4e91\u9152\u91cf\u7684\u786e\u4e0d\u9519\uff0c\u53ea\u4e0d\u8fc7\u6ca1\u6709\u5979\u81ea\u5df1\u60f3\u50cf\u4e2d\u90a3\u9ebd\u597d\u800c\u5df2\u3002\u6bcf\u4e2a\u4eba\u7684\u9152\u91cf\u90fd\u6ca1\u6709\u81ea\u5df1 \u60f3\u50cf\u4e2d\u90a3\u9ebd\u597d\u7684\u3002 \u3000\u3000\u77f3\u7ee3\u4e91\u7684\u773c\u76ae\u5df2\u8fdc\u4e0d\u5982\u65b9\u90a3\u9ebd\u7075\u6d3b\u4e86\u3002 \u3000\u3000\u5979\u77aa\u7740\u695a\u7559\u9999\u7528\u7b77\u5b50\u6307\u7740\u695a\u7559\u9999\u7684\u9f3b\u5b50\u5403\u5403\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u597d\u4eba\uff0c\u6211\u65e9\u5c31\u77e5\u9053\u4f60\u4e0d\u662f\u597d \u4eba\u2026\u2026\u6211\u7b2c\u4e00\u6b21\u770b\u89c1\u4f60\u7684\u65f6\u5019\uff0c\u5c31\u77e5\u9053\u6211\u8981\u5012\u9709\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u82e6\u7b11\u9053\uff1a\u201c\u6211\u54ea\u70b9\u4e0d\u597d\uff1f\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u683c\u683c\u7b11\u9053\uff1a\u201c\u4f60\u628a\u6211\u704c\u9189\u4e86\u2026\u3002\u2019\u4f60\u628a\u6211\u704c\u9189\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53c8\u597d\u6c14\u53c8\u597d\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u8bf4\u4f60\u4e0d\u4f1a\u9189\u7684\u5417\uff1f\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u76b1\u4e86\u76b1\u9f3b\u5b50\u626c\u4e86\u4e2a\u9b3c\u8138\uff0c\u53c8\u628a\u811a\u653e\u4e86\u4e0b\u53bb\uff0c\u9053\uff1a\u201c\u8fd9\u9ebd\u95f7\u95f7\u6b7b\u4eba\uff0c\u8ba9\u6211\u51fa\u53bb\u8d70\u597d \u4e0d\u597d\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7acb\u523b\u7ad9\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u597d\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u5f2f\u4e0b\u8170\uff0c\u51e0\u4e4e\u5c06\u5934\u4f38\u5230\u684c\u5b50\u5e95\u4e0b\u4e86\uff0c\u9053\uff1a\u201c\u6211\u7684\u978b\u5b50\u3002\u2026\u6211\u7684\u978b\u5b50\u5462\uff1f\u201d \u3000\u3000\u5979\u7684\u978b\u5b50\u5df2\u8e22\u5230\u695a\u7559\u9999\u8fd9\u8fb9\u6765\u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\u53ea\u6709\u66ff\u5979\u6361\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8c01\u77e5\u77f3\u7ee3\u4e91\u62ac\u8d77\u811a\uff0c\u5403\u5403\u7b11\u9053\uff1a\u201c\u4f60\u66ff\u6211\u7a7f\u4e0a\uff0c\u2026\u2026\u4f60\u4e0d\u66ff\u6211\u7a7f\u4e0a\uff0c\u6211\u5c31\u4e0d\u8d70\u3002\u201d \u3000\u3000\u7ea4\u79c0\u7684\u811a\u76c8\u76c8\u4e00\u63e1\u3002 \u3000\u3000\u695a\u7559\u9999\u7684\u5fc3\u4e0d\u89c9\u53c8\u52a8\u3002 \u3000\u3000\u5bf9\u4ed6\u8fd9\u6837\u7684\u7537\u4eba\u8bf4\u6765\uff0c\u8fd9\u5c0f\u4e2b\u5934\u505a\u5f97\u5b9e\u5728\u672a\u514d\u592a\u8fc7\u4efd\u4e86\uff0c\u7b80\u76f4\u5c31\u597d\u50cf\u5728\u6b3a\u8d1f\u4ed6\u597d\u50cf\u8bf4\u4ed6 \u6c14\u4e0d\u6539\u4f3c\u7684\u3002 \u3000\u3000\u695a\u7559\u9999\u7b80\u76f4\u5fcd\u4e0d\u4f4f\u60f3\u7ed9\u5979\u70b9\u201c\u6559\u8bad\u201d\u4e86\u3002 \u3000\u3000\u53ef\u662f\u8fd9\u6b21\u695a\u7559\u9999\u5374\u4ec0\u9ebd\u4e5f\u6ca1\u6709\u505a\u53ea\u662f\u66ff\u5979\u7a7f\u4e0a\u978b\u5b50\uff0c\u6276\u5979\u51fa\u4e86\u95e8\uff0c\u5979\u4e24\u53ea\u624b\u63a5\u5728\u695a\u7559\u9999 \u80a9\u80f3\u4e0a\uff0c\u6574\u4e2a\u4eba\u90fd\u6302\u5728\u4ed6\u80a9\u8180\u4e0a\u3002 \u3000\u3000\u591c\u51c9\u5982\u6c34\u3002 \u3000\u3000\u661f\u5149\u6620\u5728\u9752\u77f3\u6273\u8def\u4e0a\uff0c\u9752\u77f3\u677f\u8def\u6620\u7740\u661f\u5149\u3002 \u3000\u3000\u79cb\u98ce\u6e29\u67d4\u5f97\u5c31\u50cf\u662f\u60c5\u4eba\u7684\u547c\u5438\u3002 \u3000\u3000\u695a\u7559\u9999\u5ffd\u7136\u89c9\u5f97\u81ea\u5df1\u4e5f\u6709\u4e9b\u9189\u4e86\u3002 \u3000\u3000\u4ed6\u5168\u672a\u770b\u5230\u9ed1\u6697\u4e2d\u8fd8\u6709\u53cc\u53d1\u5149\u7684\u773c\u775b\u5728\u76ef\u7740\u4ed6\u3002 \u3000\u3000\u6728\u5c4b\u91cc\u5e76\u4e0d\u592a\u6697\uff0c\u56e0\u4e3a\u661f\u5149\u4e5f\u6084\u6084\u7684\u6f5c\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u695a\u7559\u9999\u4e0d\u77e5\u81ea\u5df1\u4e3a\u4ec0\u9ebd\u8981\u542c\u77f3\u7ee3\u4e91\u7684\u8bdd\uff0c\u4e3a\u4ec0\u9ebd\u53c8\u5c06\u5979\u5e26\u6765\u8fd9\u91cc\u2026\u201c\u4e5f\u8bb8\u4ed6\u771f\u7684\u6709\u4e9b\u9189 \u4e86\u3002 \u3000\u3000\u77f3\u7ee3\u4e91\u5feb\u4e50\u5f97\u5c31\u50cf\u662f\u53ea\u4e91\u96c0\uff0c\u8f7b\u7075\u7684\u8f6c\u4e86\u4e2a\u8eab\uff0c\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u6211\u4e3a\u4ec0\u9ebd\u8981\u5230\u8fd9\u91cc \u6765\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u77f3\u7ee3\u4e91\u9053\uff1a\u201c\u56e0\u4e3a\u8fd9\u662f\u6211\u7b2c\u4e00\u773c\u770b\u5230\u4f60\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u8d70\u5427\u3002\u201d \u3000\u3000\u6b64\u65f6\u6b64\u523b\uff0c\u7a81\u7136\u8bf4\u51fa\u8fd9\u4e24\u4e2a\u5b57\u6765\uff0c\u5b9e\u5728\u5999\u5f97\u5f88\u3002 \u3000\u3000\u77f3\u7ee3\u4e91\u9053\uff1a\u201c\u8d70\uff1f\u4e3a\u4ec0\u9ebd\u8981\u8d70\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u82e5\u518d\u4e0d\u8d70\uff0c\u53ef\u77e5\u9053\u6211\u4f1a\u600e\u9ebd\u529e\uff1f\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u5a07\u7b11\u7740\uff0c\u64ad\u7740\u5934\u3002 \u3000\u3000\u695a\u7559\u9999\u5c3d\u91cf\u4f7f\u81ea\u5df1\u7684\u8868\u60c5\u770b\u6765\u51f6\u72e0\u4e9b\uff0c\u6c89\u7740\u58f0\u97f3\u9053\uff1a\u201c\u4f60\u65e2\u77e5\u9053\u6211\u4e0d\u662f\u597d\u4eba\uff0c\u4f60\u5c31\u8be5\u731c \u5f97\u51fa\u6211\u8981\u505a\u4ec0\u9ebd\u4e8b\u7684\uff0c\u5feb\u4e9b\u8d70\u662f\u4f60\u7684\u8fd0\u6c14\uff0c\u5426\u5219\u6211\u5c31\u8981\u6495\u7834\u4f60\u7684\u8863\u670d\uff0c\u7136\u5f8c\u2026\u3002\u201d \u3000\u3000\u4ed6\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u5b8c\uff0c\u77f3\u7ee3\u4e91\u7a81\u7136\u201c\u5401\u201d\u4e00\u58f0\uff0c\u6295\u5165\u4ed6\u6000\u91cc\u3002\u7d27\u7d27\u7684\u52fe\u4f4f\u4e86\u4ed6\u5f97\u8116\u5b50\uff0c\u9053\uff1a \u201c\u4f60\u771f\u574f\uff0c\u574f\u6b7b\u4e86\uff0c\u6211\u5c31\u77e5\u9053\u4f60\u603b\u6709\u4e00\u5929\u4f1a\u8fd9\u6837\u5bf9\u6211\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u4ed6\u53ea\u4e0d\u8fc7\u662f\u5728\u5634\u4e0a\u8bf4\u8bf4\uff0c\u60f3\u5413\u5413\u5979\u800c\u5df1\uff0c\u8c01\u77e5\u5979\u81ea\u5df1\u53cd\u800c\u201c\u5b9e\u884c\u201d\u4e86\u8d77\u6765\uff0c\u4ed6\u60f3\u63a8\u3002 \u3000\u3000\u4ed6\u63a8\u5728\u6700\u4e0d\u8be5\u63a8\u7684\u5730\u65b9\u3002 \u3000\u3000\u77f3\u7ee3\u4e91\u7684\u7b11\u58f0\u5982\u94f6\u94c3\uff0c\u65ad\u65ad\u7eed\u7eed\u7684\u94f6\u94c3\uff0c\u5979\u63e1\u8d77\u4e86\u4ed6\u7684\u624b\uff0c\u5c06\u4ed6\u968f\u624b\u585e\u4eba\u5979\u7684\u8863\u895f\u91cc\uff0c \u6084\u6084\u9053\uff1a\u201c\u4f60\u6478\u6a21\u6211\u8eab\u4e0a\u662f\u4e0d\u662f\u53d1\u70e7\uff1f\u201d \u3000\u3000\u5979\u8eab\u4e0a\u7684\u786e\u5728\u53d1\u70e7\u3002 \u3000\u3000\u695a\u7559\u9999\u867d\u7136\u6709\u4e9b\u53f0\u4e0d\u5f97\uff0c\u8fd8\u662f\u5f88\u5feb\u5c31\u5c06\u624b\u8896\u4e86\u51fa\u6765\uff0c\u8c01\u77e5\u77f3\u7ee3\u4e91\u5374\u53c8\u62ff\u8d77\u4ed6\u7684\u624b\uff0c\u72e0\u54ac \u4e86\u4e00\u53e3\u3002 \u3000\u3000\u5979\u54ac\u7740\u4ed6\u7684\u624b\u6307\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u4e2a\u574f\u4e1c\u897f\uff0c\u4f60\u4e00\u76f4\u5728\u52fe\u5f15\u6211\u4ece\u5934\u5230\u5c3e\u90fd\u5728\u52fe\u5f15\u6211\uff0c\u4f60\u4ee5\u4e3a \u6211\u4e0d\u77e5\u9053\uff1f\u73b0\u5728\u4f60\u53c8\u8981\u9003\u4e86\uff0c\u4f60\u82e5\u6562\u9003\u8d70\uff0c\u5c0f\u5fc3\u6211\u54ac\u65ad\u4f60\u7684\u624b\u6307\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u662f\u4e2a\u7537\u4eba\uff0c\u800c\u4e14\u6ca1\u6709\u6bdb\u75c5\u3002 \u3000\u3000\u4e00\u70b9\u6bdb\u75c5\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u592a\u9633\u5df2\u5347\u8d77\u3002 \u3000\u3000\u9633\u5149\u7167\u5165\u7a97\u6237\uff0c\u7167\u5728\u77f3\u7ee3\u4e91\u817f\u4e0a\u3002 \u3000\u3000\u5979\u7684\u817f\u4fee\u957f\u7b14\u633a\u3002 \u3000\u3000\u5c31\u7b97\u518d\u6311\u5254\u7684\u4eba\uff0c\u4e5f\u4e0d\u80fd\u4e0d\u627f\u8ba4\u8fd9\u53cc\u817f\u8bf1\u4eba\u5f97\u5f88\u3002 \u3000\u3000\u695a\u7559\u9999\u7684\u76ee\u5149\u4ece\u5979\u7684\u817f\uff0c\u6162\u6162\u7684\u79fb\u5230\u5979\u8138\u4e0a\uff0c\u5979\u8138\u4e0a\u8fd8\u7559\u7740\u4e00\u62b9\u7ea2\u6655\u547c\u5438\u662f\u90a3\u9ebd\u5b89\u7965\uff0c \u7761\u5f97\u5c31\u597d\u50cf\u5a74\u513f\u6837\u3002 \u3000\u3000\u671b\u7740\u8fd9\u5f20\u8138\uff0c\u695a\u7559\u9999\u5fc3\u91cc\u5ffd\u7136\u89c9\u5f97\u8bf4\u4e0d\u51fa\u7684\u5f8c\u6094\u3002 \u3000\u3000\u4ed6\u5e76\u4e0d\u662f\u201c\u67f3\u4e0b\u60e0\u201d\uff0c\u4e5f\u4ece\u6765\u4e0d\u60f3\u505a\u201c\u67f3\u4e0b\u60e0\u201d\uff0c\u53ef\u662f\u8fd9\u6b21\uff0c\u4ed6\u5374\u5e0c\u671b\u6628\u5929\u665a\u4e0a\u662f\u4e2a\u67f3 \u4e0b\u60e0\u3002 \u3000\u3000\u4ed6\u4e5f\u66fe\u7ecf\u548c\u522b\u7684\u5973\u5b69\u5b50\u5f88\u4eb2\u5bc6\uff0c\u4f46\u662f\u90a3\u90fd\u4e0d\u540c\u3002\u90a3\u4e9b\u5973\u5b69\u5b50\u90fd\u5f88\u575a\u5f3a\uff0c\u90fd\u5f88\u6709\u52c7\u6c14\u3002 \u3000\u3000\u77e5\u9053\u5979\u7eb5\u7136\u4f1a\u5bf9\u4ed6\u6000\u5ff5\uff0c\u4e5f\u4e0d\u4f1a\u4e3a\u4ed6\u75db\u82e6\u3002 \u3000\u3000\u800c\u73b0\u5728\u4f9d\u5728\u4ed6\u6000\u91cc\u8fd9\u5973\u5b69\u5b50\u5374\u4e0d\u540c\u3002\u8fd9\u5973\u5b69\u5982\u6b64\u7eaf\u771f\uff0c\u77e5\u6b64\u5e7c\u7a1a\uff0c\u5982\u6b64\u8f6f\u5f31\u2026\u3002\u4ed6\u4e0d\u6562 \u60f3\u50cf\u81ea\u5df1\u79bb\u5f00\u5979\u4e4b\u5f8c\uff0c\u5979\u4f1a\u600e\u9ebd\u6837\uff1f \u3000\u3000\u201c\u5979\u4f1a\u4e0d\u4f1a\u81ea\u6740\uff1f\u201d \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u695a\u7559\u9999\u771f\u6068\u4e0d\u5f97\u91cd\u91cd\u6253\u81ea\u5df1\u51e0\u4e2a\u8033\u5149\u4e86\u3002 \u3000\u3000\u77f3\u7ee3\u4e91\u7684\u817f\u8f7b\u8f7b\u7f29\u4e86\u7f29\uff0c\u8138\u4e0a\u9762\u6e10\u53c8\u9732\u51fa\u4e86\u9152\u6da1\u3002 \u3000\u3000\u7136\u5f8c\u5979\u7741\u5f00\u4e86\u773c\u3002 \u3000\u3000\u695a\u7559\u9999\u51e0\u4e4e\u4e0d\u6562\u63a5\u89e6\u5979\u7684\u773c\u6ce2\u3002 \u3000\u3000\u77f3\u7ee3\u4e91\u7ffb\u4e86\u4e2a\u8eab\uff0c\u5ffd\u7136\u8f7b\u8f7b\u7684\u547b\u541f\u4e86\u8d77\u6765\uff0c\u5e26\u7740\u7b11\u9053\uff1a\u201c\u6211\u7684\u5934\u597d\u75bc\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u67d4\u58f0\u9053\uff1a\u201c\u60f3\u5230\u7b2c\u4e8c\u5929\u7684\u5934\u75bc\uff0c\u4ee5\u5f8c\u4f60\u603b\u8be5\u5c11\u559d\u4e9b\u9152\u4e86\u5427\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u5403\u7729\u7b11\u9053\uff1a\u201c\u6211\u542c\u8bf4\u7231\u559d\u9152\u7684\u4eba\u8bb0\u6027\u90fd\u4e0d\u597d\uff0c\u8fc7\u4e24\u5929\u5c31\u4f1a\u5c06\u9152\u9189\u5f8c\u7684\u96be\u53d7\u5fd8\u5f97\u4e7e \u4e7e\u51c0\u51c0\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u4e5f\u4e0d\u7981\u5931\u7b11\u9053\uff1a\u201c\u4e00\u70b9\u4e5f\u4e0d\u9519\uff0c\u636e\u6211\u6240\u77e5\u5c0f\u80e1\u81f3\u5c11\u5c31\u6212\u4e86\u5343\u6b21\u9152\u4e86\uff0c\u6bcf\u6b21\u5934\u75bc\u65f6 \u4ed6\u90fd\u56b7\u7740\u8981\u6212\u9152\uff0c\u53ef\u662f\u4e0d\u5230\u534a\u5929\u5c31\u5f00\u4e86\u6212\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u5750\u4e86\u8d77\u6765\uff0c\u63c9\u63c9\u773c\u775b\u7b11\u9053\uff1a\u201c\u539f\u6765\u592a\u9633\u5df2\u5347\u5f97\u8fd9\u9ebd\u9ad8\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u65f6\u5019\u7684\u786e\u4e0d\u65e9\uff0c\u6211\u2026\u2026\u6211\u5b9e\u5728\u4e0d\u60f3\u8d70\u2026\u201d \u3000\u3000\u4e0b\u4e00\u53e5\u8bdd\u4ed6\u672c\u8981\u8bf4\u201c\u867d\u4e0d\u60f3\u8d70\uff0c\u5374\u975e\u8d70\u4e0d\u53ef\u3002\u201d \u3000\u3000\u53ef\u662f\u8fd9\u53e5\u8bdd\u65e0\u8bba\u5982\u4f55\u4e5f\u8bf4\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u8c01\u77e5\u77f3\u7ee3\u4e91\u5374\u9053\uff1a\u201c\u4f60\u4e0d\u60f3\u8d70\uff0c\u6211\u5374\u8981\u8d70\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u4f60\u2014\u2026\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4e5f\u8be5\u8d70\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u90a3\u9ebd\u2026\u2026\u90a3\u9ebd\u4ee5\u5f8c\u6211\u4eec\u3002\u201d \u3000\u3000\u77f3\u7ee3\u4e91\u9053\uff1a\u201c\u4ee5\u5f8c\uff1f\u6211\u4eec\u6ca1\u6709\u4ee5\u5f8c\uff0c\u56e0\u4e3a\u4ee5\u5f8c\u4e00\u5b9a\u518d\u4e5f\u89c1\u4e0d\u7740\u6211\u3002\u201d\u74dc \u3000\u3000\u695a\u7559\u9999\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u77f3\u7ee3\u4e91\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u9ebd\u5403\u60ca\uff1f\u4f60\u96be\u9053\u4ee5\u4e3a\u6211\u4f1a\u7f20\u4f4f\u4f60\uff0c\u4e0d\u653e\u4f60\u8d70\uff1f\u201d \u3000\u3000\u5979\u4eb2\u4e86\u4eb2\u695a\u7559\u9999\u7684\u8138\uff0c\u7ad9\u8d77\u6765\uff0c\u5f00\u59cb\u7a7f\u8863\u670d\u6df1\u6df1\u9053\uff1a\u201c\u6211\u548c\u4f60\u6839\u672c\u5c31\u4e0d\u662f\u4e00\u4e2a\u4e16\u754c\u91cc\u7684 \u4eba\uff0c\u6211\u5c31\u7b97\u80fd\u52c9\u5f3a\u7559\u4f4f\u4f60\uff0c\u6216\u8005\u4e00\u5b9a\u8981\u8ddf\u4f60\u8d70\uff0c\u4ee5\u5f8c\u4e5f\u4e0d\u4f1a\u5e78\u798f\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b80\u76f4\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u77f3\u7ee3\u4e91\u6e29\u67d4\u7684\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u662f\u4e2a\u5f88\u5e73\u51e1\u7684\u4eba\uff0c\u4ee5\u524d\u4e00\u76f4\u8fc7\u7684\u662f\u5f88\u5e73\u51e1\u7684\u65e5\u5b50\uff0c\u4ee5\u5f8c\u8fc7 \u7684\u4e5f\u4e00\u5b9a\u662f\u5f88\u5e73\u51e1\u7684\u65e5\u5b50\uff0c\u5728\u6211\u8fd9\u4e00\u751f\u4e2d\uff0c\u80fd\u591f\u8ddf\u4f60\u6709\u8fd9\u9ebd\u6837\u4e0d\u5e73\u51e1\u7684\u4e00\u5929\u2026\u2026\u53ea\u8981\u4e00\u5929\uff0c \u6211\u5df2\u5f88\u6ee1\u8db3\u4e86\uff0c\u4ee5\u5f8c\u5230\u6211\u5f88\u8001\u7684\u65f6\u5019\uff0c\u81f3\u5c11\u6211\u8fd8\u6709\u8fd9\u9ebd\u4e00\u5929\u751c\u871c\u7684\u56de\u5fc6\u3002\u201d \u3000\u3000\u5979\u6e29\u67d4\u7684\u51dd\u6ce8\u7740\u695a\u7559\u9999\uff0c\u6817\u58f0\u7f13\u9053\uff1a\u201c\u6240\u4ee5\u65e0\u8bba\u5982\u4f55\u90fd\u8be5\u611f\u6fc0\u4f60\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5750\u5728\u90a3\u91cc\uff0c\u5fc3\u91cc\u4e5f\u4e0d\u77e5\u662f\u4ec0\u9ebd\u6ecb\u5473\u3002 \u3000\u3000\u77f3\u7ee3\u4e91\u53c8\u4eb2\u4e86\u4eb2\u4ed6\uff0c\u7136\u5f8c\u5ffd\u7136\u5c31\u8f6c\u8eab\u5f88\u5feb\u7684\u8d70\u4e86\u51fa\u6765\uff0c\u751a\u81f3\u8fde\u5934\u90fd\u6ca1\u6709\u56de\u8fc7\u6765\u77a7\u4ed6\u4e00 \u773c\u3002 \u3000\u3000\u695a\u7559\u9999\u672c\u6765\u662f\u5e0c\u671b\u5979\u80fd\u597d\u597d\u8d70\u7684\uff0c\u4f46\u73b0\u5728\u5979\u771f\u7684\u597d\u597d\u8d70\u4e86\uff0c\u695a\u7559\u9999\u5fc3\u91cc\u53cd\u9762\u89c9\u5f97\u6709\u4e9b\u53d1 \u9178\uff0c\u53d1\u82e6\u3002 \u3000\u3000\u4ed6\u672c\u6765\u4e00\u5fc3\u5e0c\u671b\u5979\u8d70\uff0c\u73b0\u5728\u5374\u53c8\u5e0c\u671b\u5979\u4e0d\u8981\u8d70\u5f97\u8fd9\u9ebd\u5feb\u4e86\u4e00\u4e00 \u3000\u3000\u4eba\u4eba\u90fd\u8bf4\u5973\u5b50\u7684\u5fc3\u60c5\u4e0d\u53ef\u6349\u6478\u5176\u5b9e\u7537\u4eba\u53c8\u4f55\u5c1d\u4e0d\u5982\u6b64\u3002 \u3000\u3000\u695a\u7559\u9999\u76ef\u7740\u90a3\u6247\u95e8\uff0c\u597d\u50cf\u5e0c\u671b\u5979\u4f1a\u5ffd\u7136\u53c8\u63a8\u95e8\u8d70\u8fdb\u6765\u4f3c\u7684\u3002 \u3000\u3000\u95e8\u679c\u7136\u88ab\u63a8\u5f00\u4e86\u2026\u2019 \u3000\u3000\u4f46\u4ece\u95e8\u5916\u8d70\u8fdb\u6765\u7684\u5e76\u4e0d\u662f\u6e29\u67d4\u7f8e\u8c8c\u7684\u77f3\u7ee3\u4e91\uff0c\u800c\u662f\u6761\u9152\u6c14\u85b0\u4eba\uff0c\u521a\u751f\u51fa\u6ee1\u8138\u80e1\u6e23\u5b50\u7684\u8eab \u957f\u5927\u6c49\uff1b \u3000\u3000\u695a\u7559\u9999\u53eb\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u5c0f\u80e1\uff0c\u4f60\u600e\u9ebd\u4f1a\u627e\u5230\u8fd9\u91cc\u6765\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6ca1\u6709\u56de\u7b54\uff0c\u5374\u7b11\u9053\uff1a\u201c\u8001\u81ed\u866b\uff0c\u4f60\u5b9e\u5728\u6709\u4e24\u624b\u2026\u2026\u4f60\u662f\u7528\u4ec0\u9ebd\u6cd5\u5b50\u5c06\u90a3\u5973\u5b69\u5b50\u9a97 \u5f97\u80af\u4e56\u4e56\u8d70\u4e86\u7684\uff1f\u8fd9\u6cd5\u5b50\u4f60\u4e00\u5b9a\u5f97\u6559\u6559\u6211\uff01\u201d \u3000\u3000\u695a\u7559\u9999\u6ee1\u809a\u5b50\u82e6\u6c34\u5374\u5410\u4e0d\u51fa\u6765\uff0c\u9053\uff1a\u201c\u6211\u4f55\u5fc5\u6559\u4f60\u53cd\u6b63\u5973\u5b69\u5b50\u4e00\u770b\u5230\u4f60\u5c31\u9003\u5f97\u6bd4\u9a6c\u8dd1\u7684 \u8fd8\u6289\u3002\u201d \u3000\u3000\u4ed6\u867d\u662f\u5728\u6545\u610f\u6c14\u6c14\u80e1\u94c1\u82b1\u4f46\u4e5f\u77e5\u9053\u80e1\u94c1\u82b1\u7edd\u4e0d\u4f1a\u751f\u6c14\uff0c\u66f4\u4e0d\u4f1a\u96be\u53d7\u65e0\u8bba\u8c01\u60f3\u8981\u80e1\u94c1\u82b1\u96be \u53d7\uff0c\u90fd\u56f0\u96be\u5f97\u5f88\u3002 \u3000\u3000\u8c01\u77e5\u80e1\u94c1\u82b1\u542c\u4e86\u8fd9\u8bdd\u7acb\u523b\u54ed\u4e27\u4e86\u8138\uff0c\u7b11\u4e5f\u7b11\u4e0d\u51fa\u6765\u4e86\uff0c\u7ad9\u5728\u90a3\u91cc\u53d1\u4e86\u534a\u5929\u5446\uff0c\u7adf\u201c\u62cd\u201d \u7684\u7ed9\u81ea\u5df1\u4e2a\u8033\u522e\u5b50\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4e0d\u9519\u4f60\u8bf4\u7684\u4e00\u70b9\u4e5f\u4e0d\u9519\uff0c\u6211\u662f\u4e2a\u9152\u9b3c\u53c8\u662f\u4e2a\u7a77\u5149\u86cb\u3001\u53c8\u810f\u3001 \u53c8\u4e11\uff0c\u82e5\u6709\u5973\u5b69\u5b50\u89c1\u4e86\u6211\u4e0d\u9003\uff0c\u90a3\u624d\u662f\u602a\u4e8b\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u4e5f\u770b\u5446\u4e86\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u80e1\u94c1\u82b1\u5e76\u4e0d\u662f\u4e2a\u559c\u6b22\u5f00\u73a9\u7b11\u7684\u4eba\uff0c\u4ed6\u8ba4\u8bc6\u80e1\u94c1\u82b1\u4e8c\u5341\u591a\u5e74\u80e1\u94c1\u82b1\u6c38\u8fdc\u90fd\u662f\u9ad8\u9ad8\u5174 \u5174\u7684\u5f97\u610f\u626c\u626c\u7684\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u600e\u9ebd\u4f1a\u53d8\u5f97\u8fd9\u79cd\u6837\u5b50\uff1f\u96be\u9053\u4ed6\u6709\u4e86\u4ec0\u9ebd\u6bdb\u75c5\uff1f \u3000\u3000\u53ea\u89c1\u80e1\u94c1\u82b1\u773c\u775b\u7ea2\u7ea2\u7684\u5c45\u7136\u50cf\u8981\u6d41\u773c\u6cea\u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\u5fcd\u4e0d\u4f4f\u7b11\u4e86\u9053\uff1a\u201c\u8c01\u4f1a\u8bf4\u4f60\u4e11\uff0c\u90a3\u4eba\u773c\u6674\u2014\u5b9a\u778e\u4e86\u3002\u4f60\u770b\u4f60\u7684\u9f3b\u5b50\u3001\u7709\u6bdb\u3001\u773c \u775b\uff0e\u2026\u2014\u5c24\u5176\u662f\u4f60\u8fd9\u53cc\u773c\u775b\u4e00\u4e07\u4e2a\u7537\u4eba\u4e2d\u4e5f\u627e\u4e0d\u51fa\u4e00\u4e2a\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u4e0d\u7531\u81ea\u4e3b\u62ac\u8d77\u624b\u6478\u4e86\u6478\u81ea\u5df1\u7684\u773c\u775b\uff0c\u50cf\u662f\u89c9\u5f97\u9ad8\u5174\u4e86\u4e9b\uff0c\u4f46\u5ffd\u53c8\u6447\u5934\u6342\u7740\u8138\uff1a \u201c\u5c31\u7b97\u6211\u775b\u957f\u5f97\u4e0d\u9519\uff0c\u4e5f\u53ea\u662f\u4e2a\u7a77\u5149\u86cb\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u7537\u4e86\u6c49\u5927\u4e08\u592b\uff0c\u7a77\u4e00\u70b9\u6709\u4ec0\u9ebd\u5173\u7cfb\uff0c\u53ea\u8981\u4f60\u7a77\u5f97\u9aa8\u5934\u786c\u2026\u2026\u4e16\u4e0a\u7684\u5973\u5b69\u5b50 \u5e76\u975e\u4e2a\u4e2a\u90fd\u662f\u89c1\u94b1\u773c\u5f00\u7684\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u4e0d\u7531\u81ea\u4e3b\u633a\u8d77\u4e86\u80f8\u819b\uff0c\u4f46\u5ffd\u53c8\u7f29\u4e86\u4e0b\u53bb\uff0c\u6447\u5934\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u6211\u53c8\u662f\u4e2a\u9152\u9b3c\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5fcd\u4e0d\u4f4f\u7b11\u9053\uff1a\u201c\u559d\u9152\u53c8\u6709\u4ec0\u9ebd\u4e0d\u597d\uff1f\u559d\u9152\u7684\u4eba\u624d\u6709\u7537\u5b50\u6c14\u6982\uff0c\u53e4\u6765\u6709\u540d\u7684\u82f1\u96c4\uff0c \u5c06\u76f8\u3001\u8bd7\u4eba\uff0c\u54ea\u4e2a\u4e0d\u559d\u9152\uff0c\u5973\u5b69\u5b50\u89c1\u5230\u4f60\u559d\u9152\u7684\u8c6a\u6c14\uff0c\u4e00\u9897\u5fc3\u65e9\u5df2\u6389\u5728\u4f60\u9152\u676f\u91cc\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5374\u8fd8\u662f\u5728\u6447\u5934\uff0c\u9053\uff1a\u201c\u8fd9\u4e9b\u8bdd\u6ca1\u7528\uff0c\u5973\u5b69\u5b50\u89c1\u4e86\u6211\u8fd8\u662f\u8981\u9003\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u54ea\u4e2a\u5973\u5b69\u5b50\u89c1\u4f60\u4f1a\u9003\uff1f\u5979\u4eec\u8ffd\u4f60\u8fd8\u6765\u4e0d\u53ca\u54e9\u3002\u2026\uff0e\u4f60\u4e0d\u8bb0\u5f97\u534e\u5c71\u6d3e\u7684\u90a3\u4f4d \u2018\u6e05\u98ce\u5973\u5251\u5ba2\u2019\u9ad8\u4e9a\u7537\uff0c\u53ea\u4e3a\u4e86\u8981\u5ac1\u7ed9\u4f60\u3002\u4e00\u76f4\u8ffd\u4e86\u4f60\u4e24\u53c1\u5e74\u3002\u201d \u3000\u3000\u8fd9\u8bdd\u5012\u4e0d\u5047\u3002 \u3000\u3000\u90a3\u5e74\u590f\u5929\uff0c\u4ed6\u4eec\u5728\u83ab\u6101\u6e56\u4e0a\u559d\u9152\u80e1\u94c1\u82b1\u559d\u9189\u4e86\uff0c\u80e1\u91cc\u80e1\u6d82\u7684\u5c31\u7b54\u5e94\u4e86\u8981\u548c\u9ad8\u4e9a\u7537\u6210\u4eb2\u3002 \u3000\u3000\u4f46\u7b2c\u4e8c\u5929\u4ed6\u5c31\u5c06\u8fd9\u56de\u4e8b\u5fd8\u4e86\uff0c\u4e9a\u7537\u5374\u672a\u5fd8\uff0c\u786c\u903c\u7740\u4ed6\u8981\u5979\uff0c\u8fd8\u8bf4\u4ed6\u82e5\u8d56\u8d26\uff0c\u5979\u6ca1\u6709\u8138\u6d3b \u4e0b\u53bb\u4e86\u3002\u5979\u5c31\u8981\u81ea\u6740\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u5b50\u7acb\u523b\u5c06\u80e1\u94e3\u82b1\u5413\u5f97\u843d\u8352\u800c\u9003\uff0c\u9ad8\u4e9a\u7537\u5c31\u5728\u5f8c\u9762\u8ffd\uff0c\u636e\u80e1\u94c1\u82b1\u81ea\u5df1\u8bf4\uff0c\u5979\u7adf\u8ffd\u4e86 \u4e24\u53c1\u5e74\u3002 \u3000\u3000\u8fd9\u672c\u662f\u80e1\u94c1\u82b1\u7684\u5f97\u610f\u4e8b\uff0c\u695a\u7559\u9999\u4ee5\u4e3a\u603b\u53ef\u53eb\u80e1\u94c1\u82b1\u5f00\u5fc3\u4e9b\u4e86\uff0c\u8c01\u77e5\u80e1\u94c1\u82b1\u4e00\u542c\u201c\u9ad8\u4e9a \u7537\u201d\u8fd9\u540d\u5b57\uff0c\u4e00\u5f20\u8138\u7acb\u523b\u5c31\u53d8\u5f97\u50cf\u540a\u6b7b\u9b3c\u4e00\u6837\u3002 \u3000\u3000\u695a\u7559\u9999\u5947\u602a\uff0c\u8bd5\u63a2\u7740\u95ee\u9053\uff1a\u201c\u83ab\u975e\u4f60\u53c8\u89c1\u7740\u9ad8\u4e9a\u7537\u4e86\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u55ef\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u8bb6\u7136\u9053\u201c\u5979\u96be\u9053\u8fd8\u4e0d\u7406\u4f60\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u5979\u2026\u2026\u5979\u5c31\u662f\u4e0d\u7406\u6211\uff0c\u7b80\u76f4\u5c31\u597d\u50cf\u4e0d\u8ba4\u5f97\u6211\u8fd9\u4e2a\u3002\u201d \u3000\u3000\u8bf4\u51fa\u8fd9\u53e5\u8bdd\u4ed6\u66f4\u50cf\u4e2a\u521a\u53d7\u4e86\u59d4\u5c48\u7684\u5b69\u5b50\u3002 \u3000\u3000\u695a\u7559\u9999\u66f4\u5947\u602a\u4e86\uff0c\u62c9\u7740\u4ed6\u5750\u4e86\u4e0b\u6765\u9053\uff1a\u201c\u8fd9\u5230\u5e95\u662f\u600e\u9ebd\u56de\u4e8b\uff0c\u4f60\u5feb\u8bf4\u7ed9\u6211\u542c\u542c\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u6709\u5929\u6211\u5f97\u4e86\u4e24\u7f50\u597d\u9152\u5c31\u53bb\u627e\u5feb\u7f51\u5f20\u53c1\uff0c\u56e0\u4e3a\u4ed6\u70e4\u7684\u9c7c\u6700\u597d\u6211\u8bb0\u5f97\u4f60\u4e5f\u5f88\u7231 \u5403\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u4e0d\u9519\u53ea\u6709\u4ed6\u70e4\u7684\u9c7c\u4e0d\u8165\u4e0d\u8001\uff0c\u53c8\u4e0d\u4eba\u9c7c\u9c9c\u5473\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u6211\u548c\u4ed6\u6b63\u5750\u5728\u8239\u5934\u70e4\u9c7c\u5403\u9152\u5ffd\u7136\u6709\u6761\u8239\u5f88\u5feb\u7684\u4ece\u6211\u4eec\u65c1\u8fb9\u8fc7\u53bb\uff0c\u8239\u4e0a\u6709\u53c1 \u4e2a\u4eba\uff0c\u5176\u4e2d\u6709\u4e2a\u6211\u89c9\u5f97\u5f88\u9762\u719f\u3002\u3002 \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u9ad8\u4e9a\u7537\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u70b9\u7740\u5934\u9053\uff1a\u201c\u90a3\u65f6\u6211\u4e5f\u5927\u5403\u4e00\u60ca\uff0c\u5c31\u8ffd\u4e0b\u53bb\u60f3\u8ddf\u5979\u6253\u62db\u547c\uff0c\u8c01\u77e5\u5979\u6839\u672c\u4e0d\u7406\u6211\uff0c \u6211\u62fc\u547d\u5411\u5979\u62db\u624b\uff0c\u5979\u5c31\u50cf\u6ca1\u77a7\u89c1\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e5f\u8bb8\u2026\u4e5f\u8bb8\u5979\u771f\u7684\u6ca1\u6709\u770b\u5230\u4f60\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u8c01\u8bf4\u7684\uff1f\u5979\u5750\u5728\u7a97\u53e3\uff0c\u773c\u775b\u77aa\u4e86\u6211\u534a\u5929\uff0c\u5374\u5f3a\u662f\u77aa\u7740\u6839\u6728\u5934\u4f3c\u7684\uff0c\u6211\u4e00\u8def \u8ffd\u4e0b\u53bb\uff0c\u5979\u4e00\u8def\u5750\u5728\u7a97\u53e3\uff0c\u53ef\u5c31\u662f\u4e0d\u7406\u6211\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u9ebd\u4e0d\u7d22\u6027\u8df3\u4e0a\u5979\u7684\u8239\uff1f\u53bb\u95ee\u4e2a\u660e\u767d\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u82e6\u7740\u8138\u9053\uff1a\u201c\u6211\u4e0d\u6562\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5931\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u6562\uff1f\u4e3a\u4ec0\u9ebd\uff1f\u5979\u9876\u591a\u4e5f\u4e0d\u8fc7\u53ea\u80fd\u628a\u4f60\u8e22\u4e0b\u8239\u800c\u5df2\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u53f9\u9053\uff1a\u201c\u56e0\u4e3a\u5979\u7684\u5e08\u5085\uff0c\u534e\u5c71\u7684\u90a3\u8001\u5c3c\u59d1\u4e5f\u5728\u8239\u4e0a\uff0c\u6211\u5012\u771f\u6709\u70b9\u6015\u2026\u2026\u6211\u4e0d\u662f\u6015 \u5979\u522b\u7684\u5c31\u6015\u5979\u90a3\u5f20\u8138\u3002\u201d \u3000\u3000\u534e\u5c71\u5251\u6d3e\u5f53\u4ee3\u638c\u95e8\u4eba\uff0c\u201c\u67af\u6885\u5927\u5e08\u201d\uff0c\u5e84\u4e25\u6301\u91cd\uff0c\u636e\u8bf4\u5df2\u6709\u53c1\u5341\u5e74\u672a\u9732\u7b11\u5bb9\uff0c\u6c5f\u6e56\u4e2d\u4eba \u65e0\u8bba\u8c01\u89c1\u5230\u5979\u90fd\u96be\u514d\u6709\u4e9b\u5bb3\u6015\u7684\u3002 \u3000\u3000\u695a\u7559\u9999\u52a8\u5bb9\u9053\uff1a\u201c\u67af\u6885\u5927\u5e08\u5df2\u6709\u4e8c\u5341\u9980\u5e74\u672a\u5c65\u7ea2\u5c18\uff0c\u8fd9\u4e00\u6b21\u4e0b\u5c71\u6765\u4e86\uff1f\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u89c9\u5f97\u8fd9\u597d\u4e8b\u5f88\u6709\u8da3\u4e86\uff0c\u82e5\u6ca1\u6709\u5341\u5206\u91cd\u5927\u7684\u4e8b\u67af\u6885\u5927\u5e08\u7edd\u4e0d\u4f1a\u4e0b\u5c71\uff0c\u5979\u65e2\u5df2\u4e0b\u4e86\u5c71 \u534e\u5c71\u5c31\u5fc5\u5b9a\u6709\u5927\u4e8b\u8981\u53d1\u751f\u3002 \u3000\u3000\u695a\u7559\u9999\u5ffd\u7136\u7528\u529b\u4e00\u62cd\u80e1\u94c1\u82b1\u80a9\u5934\uff0c\u9053\uff1a\u201c\u4f60\u83ab\u96be\u53d7\uff0c\u7b49\u6211\u8fd9\u91cc\u7684\u4e8b\u529e\u5b8c\u4e86\u5c31\u966a\u4f60\u53bb\u627e\u5979 \u95ee\u95ee\u5979\u4e3a\u4f55\u4e0d\u7406\u4f60\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5634\u89d2\u52a8\u4e86\u52a8\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u89c1\u4e86\u67af\u6885\u5927\u5e08\u5b9a\u4e5f\u4f1a\u5927\u5403\u4e00\u60ca\u7684\u3002\u2019 \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u5df2\u8fd8\u4fd7\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53eb\u4e86\u8d77\u6765\u9053\uff1a\u201c\u67af\u6885\u5927\u5e08\u4f1a\u8fd8\u4fd7\u4f60\u89c1\u4e86\u9b3c\u5427\u3002\u201d \u3000\u3000\u67af\u6094\u5927\u5e08\u843d\u53d1\u51fa\u5bb6\u5df2\u6709\u56db\u5341\u9980\u5e74\u4fee\u4e3a\u529f\u6df1\u6212\u5f8b\u7cbe\u4e25\uff0c\u82e5\u8bf4\u5979\u4e5f\u4f1a\u8fd8\u4fd7\u3002\u7b80\u76f4\u6bd4\u8bf4\u695a\u7559\u9999 \u505a\u4e86\u548c\u5c1a\u8fd8\u8981\u4ee4\u4eba\u5403\u60ca\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u82e6\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\u8fd9\u4ef6\u4e8b\u65e0\u8bba\u8bf4\u7ed9\u8c01\u542c\uff0c\u90fd\u7edd\u6ca1\u6709\u4eba\u4f1a\u76f8\u4fe1\uff0c\u4f46\u5979\u7684\u7684\u786e\u786e\u662f\u8fd8 \u4fd7\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u6015\u662f\u770b\u9519\u4eba\u4e86\u5427\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u67af\u6885\u5927\u5e08\u7684\u5bb9\u8c8c\u4efb\u4f55\u4eba\u770b\u4e86\u4e00\u773c\u90fd\u4e0d\u4f1a\u5fd8\u8bb0\u4f55\u51b5\u662f\u6211\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u53ef\u662f\u2026\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u6211\u89c1\u7740\u5979\u65f6\uff0c\u5979\u7a7f\u7684\u662f\u4ef6\u7d2b\u7f0e\u56e2\u82b1\u7684\u82b1\u888d\uff0c\u624b\u91cc\u6276\u7740\u6839\u9f99\u5934\u6756\uff0c\u5934\u4e0a\u767d\u53d1 \u82cd\u82cd\uff0c\u770b\u6765\u5c31\u50cf\u662f\u4f4d\u5b50\u5b59\u6ee1\u5802\u7684\u9988\u547d\u592b\u4eba\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u8bf4\u4e0d\u51fa\u8bdd\u6765\u4e86\u3002 \u3000\u3000\u67af\u6885\u5927\u5e08\u5c45\u7136\u4e0b\u4e86\u534e\u5c71\uff0c\u5df2\u4ee4\u4eba\u5403\u60ca\uff0c\u5979\u4f1a\u8fd8\u4fd7\uff0c\u66f4\u4ee4\u4eba\u96be\u4fe1\uff0c\u8fd9\u5176\u4e2d\u5fc5\u5b9a\u53c8\u7275\u6d89\u5230\u4e00 \u4ef6\u7a00\u5947\u53e4\u602a\u7684\u5927\u4e8b\u3002\u695a\u7559\u9999\u7684\u5174\u8da3\u8d8a\u6765\u8d8a\u6d53\u539a\u4ed6\u5ffd\u7136\u8df3\u4e86\u8d77\u6765\uff0c\u98de\u5954\u51fa\u53bb\uff0c\u9053\uff1a\u201c\u4f60\u5728\u8fd9\u91cc \u7b49\u6211\uff0c\u5348\u65f6\u524d\u5f8c\uff0c\u6211\u4e00\u5b9a\u56de\u6765\u966a\u4f60\u53bb\u3002\u201d \u3000\u3000\u6c5f\u6e56\u4e2d\u7684\u786e\u53c8\u53d1\u751f\u4e86\u4ef6\u5927\u4e8b\uff0c\u65e0\u8bba\u8c01\u60f3\u7ba1\u8fd9\u4ef6\u95f9\u4e8b\uff0c\u90fd\u96be\u514d\u8981\u6709\u6740\u8eab\u4e4b\u7978\uff0c\u695a\u7559\u9999\u82e5\u662f \u806a\u660e\u4eba\u5c31\u8be5\u9003\u5f97\u8fdc\u8fdc\u7684\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u806a\u660e\u4eba\u6709\u65f6\u4e5f\u4f1a\u505a\u50bb\u4e8b\u3002"}),showOnHomepage:!0}]}}]);