"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[40266],{12915:(e,n,t)=>{t.r(n),t.d(n,{default:()=>a});var s=t(74848);const a=[{name:"\u7b2c\u4e00\u7ae0\u3000\u661f\u6708\u53cc\u5251",content:(0,s.jsx)(s.Fragment,{children:"\u6c5f\u5357\u6625\u65e9\uff0c\u8349\u957f\u9a7e\u98de\uff0c\u659c\u9633\u4e09\u6708\uff0c\u591c\u95f4\u4ecd\u6709\u8427\u7d22\u4e4b\u610f\uff0c\u79e3\u9675\u57ce\u90ca\uff0c\u7531\u56db\u767e\u6a2a\u8857\u5230\u592a\u5e73\u95e8 \u7684\u5927\u8def\u4e0a\uff0c\u884c\u4eba\u65e9\u6e3a\uff0c\u6811\u68a2\u6447\u62fd\uff0c\u5fae\u98ce\u98d3\u7136\uff0c\u5bc2\u9759\u5df2\u6781\u3002 \u3000\u3000\u86f0\u96c1\u60ca\u8d77\uff0c\u8fdc\u5904\u5ffd\u7136\u9690\u9690\u4f20\u6765\u8f66\u8f9a\u9a6c\u5636\uff0c\u7247\u523b\u95f4\uff0c\u8d70\u6765\u4e00\u8f66\u4e00\u9a6c\uff0c\u8f66\u9a6c\u8e7f\u884c\u751a\u6025\uff0c\u7272\u53e3 \u7684\u5634\u89d2\uff0c\u5df2\u55b7\u51fa\u6d53\u6d53\u7684\u767d\u6cab\u5b50\uff0c\u4e00\u671b\u800c\u77e5\uff0c\u662f\u8d76\u8fc7\u8fdc\u8def\u7684\uff0c\u9a6c\u4e0a\u4eba\u7a7f\u7740\u94f6\u767d\u8272\u7684\u957f\u886b\uff0c\u540e\u80cc \u957f\u5251\uff0c\u9762\u5b54\u7626\u524a\uff0c\u53cc\u76ee\u70af\u70af\u6709\u795e\uff0c\u987e\u76fc\u4e4b\u95f4\uff0c\u5b9b\u5982\u5229\u526a\uff0c\u53ea\u662f\u7709\u5fc3\u7d27\u76b1\uff0c\u6ee1\u8138\u4ff1\u662f\u8083\u6740\u4e4b \u6c14\u3002 \u3000\u3000\u6b64\u65f6\u94f6\u8f89\u6ee1\u5730\uff0c\u5df2\u662f\u4e2d\u79cb\uff0c\u4e07\u7c41\u65e0\u58f0\uff0c\u9a6c\u8e44\u8e0f\u5728\u5730\u4e0a\u7684\u58f0\u97f3\uff0c\u5728\u5bc2\u9759\u4e2d\u5206\u5916\u523a\u8033\uff0c\u9a6c\u4e0a \u7684\u94f6\u886b\u5ba2\u628a\u7f30\u7ef3\u5fae\u5fae\u4e00\u7d27\uff0c\u4fa7\u8138\u5bf9\u7740\u8d76\u8f66\u7684\u90a3\u4eba\u8bf4\uff1a\u201c\u8001\u4e8c\uff0c\u8f7b\u4e9b\uff0c\u6b64\u523b\u5df2\u8fd1\u6c5f\u5b81\u5e9c\u7684\u7701 \u57ce\uff0c\u8981\u5c0f\u5fc3\u624d\u662f\u3002\u201d \u3000\u3000\u8d76\u8f66\u7684\u4e5f\u662f\u4e2a\u904d\u4f53\u94f6\u886b\u7684\u4e2d\u5e74\u6c49\u5b50\uff0c\u8eab\u6750\u7565\u80d6\uff0c\u9762\u5982\u6ee1\u6708\uff0c\u8138\u4e0a\u603b\u662f\u5e26\u7740\u4e09\u5206\u7b11\u5bb9\uff0c\u542c \u4e86\u9a6c\u4e0a\u4eba\u6240\u8bf4\u7684\u8bdd\uff0c\u50cf\u662f\u5e76\u672a\u5341\u5206\u6ce8\u610f\uff0c\u8f66\u884c\u4ecd\u6025\uff0c\u53ea\u662f\u7b11\u7740\u8bf4\uff1a\u201c\u5927\u54e5\u4e5f\u662f\u5927\u8fc7\u8c28\u614e\uff0c\u54b1 \u4eec\u4ece\u5317\u4eac\u5230\u8fd9\u513f\uff0c\u5df2\u662f\u51e0\u5343\u91cc\u8def\u54a7\uff0c\u4e5f\u6ca1\u6709\u4e00\u70b9\u513f\u98ce\u5439\u8349\u52a8\uff0c\u6211\u771f\u4e0d\u77e5\u9053\u60a8\u6574\u5929\u62c5\u7684\u54ea\u95e8\u5b50 \u5fc3\u3002\u201d \u3000\u3000\u8bed\u97f3\u6e05\u8106\uff0c\u8bf4\u7684\u662f\u4e00\u53e3\u7eaf\u7cb9\u7684\u5b98\u8bdd\u3002 \u3000\u3000\u9a6c\u4e0a\u4eba\u5fae\u6447\u4e86\u6447\u5934\uff0c\u5f20\u53e3\u50cf\u662f\u60f3\u8bf4\u4ec0\u4e48\uff0c\u5411\u8d76\u8f66\u7684\u4fa7\u7768\u4e86\u4e00\u773c\uff0c\u53c8\u5fcd\u4f4f\u4e86\u3002 \u3000\u3000\u8d76\u8f66\u7684\u5ffd\u5730\u5c06\u9a6c\u97ad\u968f\u624b\u4e00\u62a1\uff0c\u5728\u7a7a\u4e2d\u5212\u4e86\u4e2a\u5708\u5b50\uff0c\u97ad\u5b50\u62a1\u5f97\u51fa\u5947\u5730\u6162\uff0c\u4f46\u7adf\u9690\u9690\u6709\u98ce\u96f7 \u4e4b\u58f0\uff0c\u6b64\u65f6\u4ed6\u7b11\u5bb9\u66f4\u89c1\u5f00\u6717\uff0c\u5927\u58f0\u4ed6\u8bf4\uff1a\u201c\u5c31\u7b97\u6709\u4e2a\u628a\u4e2a\u4e0d\u5f00\u773c\u7684\u72d7\u817f\u5b50\uff0c\u6765\u627e\u54b1\u4eec\u7684\u78b4\uff0c \u51ed\u54b1\u4eec\u624b\u91cc\u4e24\u628a\u5251\uff0c\u8fd8\u6015\u5bf9\u4ed8\u4e0d\u4e86\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u8bdd\u58f0\u65b9\u6b47\uff0c\u53ea\u542c\u5f97\u8fdc\u5904\u6709\u4eba\u51b7\u51b7\u4ed6\u8bf4\uff1a\u201c\u597d\u5927\u7684\u53e3\u6c14\u3002\u201d \u3000\u3000\u8bed\u97f3\u4e0d\u5927\uff0c\u9694\u7740\u90a3\u4e48\u8fdc\u7684\u8ddd\u79bb\uff0c\u5165\u8033\u5374\u6781\u6e05\u6670\uff0c\u4e00\u5b57\u4e00\u58f0\uff0c\u9abc\u7136\u8457\u9e23\u3002 \u3000\u3000\u9a6c\u4e0a\u4eba\u8138\u8272\u987f\u53d8\uff0c\u624b\u671d\u9a6c\u978d\u5fae\u6309\uff0c\u4eba\u5df2\u5982\u7bad\u822c\u76f4\u7a9c\u4e86\u51fa\u53bb\uff0c\u5bbd\u5927\u7684\u8863\u8882\uff0c\u968f\u98ce\u800c\u8d77\uff0c\u4eba \u5728\u7a7a\u4e2d\u5fae\u4e00\u987f\u632b\uff0c\u5c06\u624b\u91cc\u62ff\u7740\u7684\u9a6c\u97ad\uff0c\u5411\u4e0b\u4e00\u62a1\uff0c\u4eba\u5374\u53c8\u5411\u4e0a\u7a9c\u4e86\u4e08\u8bb8\u5bc4\uff0c\u653e\u773c\u4e00\u770b\uff0c\u53ea\u89c1 \u56db\u91ce\u5bc2\u7136\uff0c\u54ea\u6709\u534a\u6761\u4eba\u5f71\u3002 \u3000\u3000\u8d76\u8f66\u7684\u7aef\u5750\u672a\u52a8\uff0c\u56de\u5934\u5411\u8f66\u91cc\u770b\u4e86\u4e00\u773c\uff0c\u8f66\u91cc\u7684\u4eba\u547c\u5438\u751a\u91cd\uff0c\u90fd\u5df2\u7761\u719f\u4e86\u3002 \u3000\u3000\u6b64\u65f6\u9a6c\u4e0a\u4eba\u7528\u6781\u5feb\u7684\u8eab\u624b\u5728\u56db\u5468\u7565\u4e00\u5bdf\u770b\uff0c\u94f6\u767d\u8272\u7684\u8863\u670d\u5728\u6708\u5149\u4e0b\u5b9b\u5982\uff1d\u6761\u767d\u7ec3\u3001\u5ffd\u53c8 \u51b2\u5929\u800c\u8d77\uff0c\u98d8\u98d8\u5730\u843d\u5728\u9a6c\u4e0a\uff0c\u7709\u5fc3\u6512\u5f97\u66f4\u7d27\uff0c\u8bf4\u9053\uff1a\u201c\u6b64\u4eba\u6b66\u529f\u6df1\u4e0d\u53ef\u6d4b\uff0c\u82e5\u771f\u662f\u4eac\u91cc\u6d3e\u4e0b \u6765\u7684\uff0c\u53ea\u6015\u2026\u2026\u201d\u8d76\u8f66\u7684\u6b64\u65f6\u7b11\u5bb9\u5df2\u655b\uff0c\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u662f\u7978\u4e0d\u662f\u798f\uff0c\u53cd\u6b63\u8fd9\u526f \u5343\u65a4\u91cd\u62c5\uff0c\u5df2\u843d\u5728\u54b1\u4eec\u80a9\u4e0a\uff0c\u54b1\u4eec\u597d\u6b79\u5f97\u5bf9\u5730\u4e0b\u7684\u4eba\u6709\u4e2a\u4ea4\u5f85\uff0c\u53ea\u597d\u8d70\u7740\u77a7\u5427\u3002\u201d \u3000\u3000\u624b\u4e2d\u7ec4\u7ef3\u4e00\u7d27\uff0c\u8f66\u9a6c\u53c8\u5411\u524d\u8d76\u53bb\u3002 \u3000\u3000\u9a91\u5728\u9a6c\u4e0a\u7684\u540d\u53eb\u6234\u68a6\u5c27\uff0c\u8d76\u8f66\u7684\u662f\u4ed6\u7684\u5e08\u5f1f\u9646\u98de\u767d\uff0c\u4ed6\u4fe9\u4eba\u672c\u662f\u4e2d\u8868\u5144\u5f1f\uff0c\u540e\u6765\u5bb6\u8d25\u4eba \u4ea1\uff0c\u5144\u5f1f\u4fe9\u968f\u7740\u91c7\u4eba\u53c2\u7684\u836f\u8d29\u6d41\u4ea1\u5230\u5173\u5916\uff0c\u7ecf\u8fc7\u5c45\u5eb8\u5173\u65f6\uff0c\u5076\u5f97\u5947\u7f18\uff0c\u88ab\u9690\u5c45\u5728\u516b\u8fbe\u5cad\u9752\u9f99 \u6865\u7684\u4e00\u4f4d\u957f\u767d\u5251\u6d3e\u540d\u5bbf\u770b\u4e2d\uff0c\u6536\u4e3a\u5f1f\u5b50\u3002\u8fd9\u4f4d\u957f\u767d\u5251\u6d3e\u7684\u540d\u5bbf\u884c\u8f88\u751a\u9ad8\uff0c\u4ece\u4e0d\u793a\u4eba\u59d3\u540d\uff0c\u4e5f \u662f\u4ed6\u5144\u5f1f\u6709\u7f18\uff0c\u5728\u9752\u9f99\u6865\u4e00\u5446\u4e03\u5e74\u3002\u5eff\u5e74\u524d\u4ed6\u5144\u5f1f\u521d\u4eba\u6c5f\u6e56\uff0c\u5728\u7d2b\u8346\u5173\u5357\u7684\u897f\u9675\u65f7\u5730\u4e0a\uff0c\u53cc \u5251\u6b7c\u4e03\u715e\uff0c\u542c\u8bf4\u7d2b\u8346\u4e03\u715e\u7684\u4e03\u4ef6\u5916\u95e8\u5175\u5668\uff0c\u7adf\u672a\u80fd\u6321\u8fc7\u5341\u62db\u3002\u7d2b\u8346\u4e03\u715e\u96c4\u8e1e\u591a\u5e74\uff0c\u7adf\u88ab\u4e00\u4e3e \u800c\u706d\uff0c\u6ca1\u6709\u9003\u51fa\u4e00\u4e2a\u6d3b\u53e3\uff0c\u6c5f\u6e56\u95fb\u8baf\u5927\u60ca\uff0c\u90fd\u60f3\u4e00\u7779\u4e8c\u4eba\u771f\u9762\u76ee\u3002 \u3000\u3000\u4e0d\u4e45\u897f\u6cb3\u6c5f\u6e56\u9ed1\u767d\u4e24\u9053\u5728\u9ad8\u7891\u5e97\u7fa4\u96c4\u96c6\u4f1a\uff0c\u8c08\u5224\u8d70\u9556\u7684\u9053\u513f\uff0c\u81ea\u662f\u8d8a\u8c08\u8d8a\u50f5\uff0c\u6b64\u65f6\u4ed6\u5144 \u5f1f\u4fe9\u7a81\u7136\u51fa\u73b0\uff0c\u4ee5\u201c\u82cd\u7a79\u5341\u4e09\u5f0f\u201d\u9547\u4f4f\u5728\u573a\u7fa4\u96c4\uff0c\u8fd9\u624d\u626c\u540d\u5929\u4e0b\uff0c\u6c5f\u6e56\u4e0a\u4eba\u79f0\u661f\u6708\u53cc\u5251\uff0c\u82cd \u661f\u94f6\u6708\u4ece\u6b64\u996e\u8a89\u5357\u5317\u3002 \u3000\u3000\u540e\u6765\u8fd9\u4fe9\u4eba\u5ffd\u7136\u4e00\u9f50\u5931\u8e2a\uff0c\u6c5f\u6e56\u4e0a\u4f20\u8bf4\u7eb7\u575b\uff0c\u83ab\u8877\u4e00\u662f\u3002 \u3000\u3000\u6709\u4eba\u8bf4\u4ed6\u4eec\u88ab\u4ec7\u5bb6\u6bd2\u8ba1\u9677\u5bb3\uff0c\u5df2\u7ecf\u4ea1\u547d\uff0c\u8fd9\u6d88\u606f\u8d8a\u4f20\u8d8a\u5e7f\uff0c\u4f3c\u4e4e\u771f\u5b9e\u6027\u4e5f\u8d8a\u5927\uff0c\u4e8e\u662f\u6c5f \u6e56\u4e2d\u4eba\u4e2a\u4e2a\u62ca\u638c\u79f0\u5feb\u3002 \u3000\u3000\u661f\u6708\u53cc\u5251\u751f\u6027\u50b2\u5cb8\uff0c\u5f62\u8e2a\u98d8\u5ffd\uff0c\u7edd\u5c11\u771f\u5fc3\u7684\u670b\u53cb\uff0c\u800c\u4e14\u4ec7\u5bb6\u4e8b\u60c5\u505a\u5f97\u751a\u662f\u5e72\u51c0\uff0c\u4fa0\u4e49\u4e2d \u4eba\u867d\u66fe\u5021\u8a00\u590d\u4ec7\uff0c\u4f46\u65f6\u8fc7\u5883\u8fc1\uff0c\u9042\u5373\u6e10\u6e10\u6de1\u5fd8\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u88ab\u4ec7\u5bb6\u9677\u5bb3\u662f\u771f\uff0c\u4eba\u5374\u4fa5\u5e78\u672a\u6b7b\u3002\u4e24\u6cb3\u7eff\u6797\u9053\u7684\u603b\u74e2\u628a\u5b50\uff0c\u7b11\u9762\u4eba\u5c60\u7533\u4e00\u5e73\uff0c\u4e0d\u77e5 \u600e\u4e48\u5f97\u5230\u82d7\u7586\u79d8\u672f\uff0c\u8fdc\u8d74\u82d7\u5c71\uff0c\u91c7\u96c6\u5728\u6df1\u5c71\u4e2d\u8574\u90c1\u5343\u5e74\u7684\u6843\u82b1\u7634\u6bd2\uff0c\u51dd\u70bc\u6210\u4e00\u79cd\u6781\u5389\u5bb3\u7684\u6bd2 \u6c41\uff0c\u88c5\u5728\u4e00\u4e2a\u7528\u767e\u70bc\u7cbe\u94a2\u7145\u6210\u7684\u6781\u5c0f\u94a2\u7b52\u91cc\uff0c\u673a\u5173\u4e00\u5f00\uff0c\u6bd2\u6c41\u968f\u5373\u55b7\u51fa\uff0c\u53ea\u8981\u4e2d\u4e0a\u4e00\u6ef4\uff0c\u4e0d \u51fa\u5341\u4e8c\u4e2a\u65f6\u8fb0\uff0c\u5168\u8eab\u6e83\u70c2\u800c\u6b7b\uff0c\u7aef\u7684\u662f\u9738\u9053\u5df2\u6781\u3002 \u3000\u3000\u7b11\u9762\u4eba\u5c60\u7533\u4e00\u5e73\u548c\u7d2b\u8346\u4e03\u715e\u672c\u662f\u751f\u6b7b\u4e4b\u4ea4\uff0c\u5bf9\u661f\u6708\u53cc\u5251\u65e9\u5c31\u6068\u4e4b\u5207\u9aa8\uff0c\u5374\u60e7\u4e8e\u4ed6\u4eec\u7684\u6b66 \u529f\uff0c\u8fdf\u8fdf\u672a\u6562\u52a8\u624b\uff0c\u6b64\u65f6\u4ed7\u7740\u8fd9\u6b79\u6bd2\u7684\u6697\u5668\uff0c\u5b9a\u4e0b\u4e00\u6761\u6bd2\u8ba1\u3002 \u3000\u3000\u7533\u4e00\u5e73\u4e94\u5341\u5927\u5bff\u90a3\u5929\uff0c\u5728\u5317\u4eac\u57ce\u90ca\u7684\u9a6c\u9a79\u6865\u5927\u5bb4\u9ed1\u9053\u7fa4\u96c4\uff0c\u5374\u65e9\u5c31\u6d3e\u4eba\u4e13\u7a0b\u8d76\u5230\u5cf0\u5c71\u7554 \u53bb\u627e\u661f\u6708\u53cc\u5251\uff0c\u7b49\u4e86\u65ec\u65e5\uff0c\u624d\u627e\u5230\u4ed6\u4eec\uff0c\u8bf4\u662f\u7533\u4e00\u5e73\u51b3\u5b9a\u5728\u4e94\u5341\u5927\u5bff\u90a3\u5929\uff0c\u91d1\u76c6\u6d17\u624b\uff0c\u4ece\u6b64 \u606f\u5f71\u6c5f\u6e56\uff0c\u5e76\u4e14\u501f\u673a\u89e3\u6563\u4e24\u6cb3\u7eff\u6797\u9053\uff0c\u6240\u4ee5\u7279\u8bf7\u661f\u6708\u53cc\u5251\u524d\u5f80\u4e3b\u6301\u3002 \u3000\u3000\u661f\u6708\u53cc\u5251\u4e0d\u7591\u6709\u4ed6\uff0c\u4e8e\u662f\u6b23\u7136\u524d\u5f80\uff0c\u7533\u4e00\u5e73\u5374\u5728\u4e0a\u9152\u7684\u65f6\u5019\uff0c\u624b\u4e2d\u6697\u85cf\u6bd2\u6c41\u94a2\u7b52\uff0c\u6e85\u5728 \u4ed6\u4eec\u8eab\u4e0a\uff0c\u661f\u6708\u53cc\u5251\u5c31\u5728\u6beb\u65e0\u6240\u89c9\u4e4b\u4e0b\uff0c\u4e2d\u4e86\u4ed6\u7684\u9053\u513f\u3002 \u3000\u3000\u201c\u5bff\u5802\u4e0a\u5bbe\u670b\u6ee1\u5ea7\u3001\u70db\u5f71\u6447\u7ea2\uff0c\u9152\u8fc7\u6570\u5de1\uff0c\u661f\u6708\u53cc\u5251\u53d1\u89c9\u79bb\u53bb\u7684\u4eba\u8d8a\u6765\u8d8a\u591a\uff0c\u5bff\u5802\u4e0a\u5269 \u4e0b\u7684\uff0c\u4ff1\u90fd\u662f\u4e9b\u7533\u4e00\u5e73\u7684\u6b7b\u515a\uff0c\u9646\u98de\u767d\u53d1\u89c9\u60c5\u5f62\u5f02\u6837\uff0c\u628a\u9152\u676f\u4e00\u4e3e\uff0c\u671d\u7740\u7533\u4e00\u5e73\u7b11\u9053\uff1a\u201c\u54b1 \u5f1f\u5144\u627f\u603b\u74e2\u628a\u5b50\u7684\u62ac\u7231\uff0c\u80fd\u773c\u89c1\u603b\u74e2\u628a\u5b50\u89e3\u6563\u4e24\u6cb3\u7eff\u6797\u9053\uff0c\u9020\u798f\u884c\u65c5\u7684\u76db\u4e3e\uff0c\u5341\u5206\u4f69\u670d\u3002\u6b64 \u65f6\u9152\u8db3\u996d\u70ae\uff0c\u5e0c\u671b\u60a8\u5429\u5490\u4e00\u58f0\uff0c\u8ba9\u54b1\u4eec\u4e5f\u597d\u65e9\u70b9\u9ad8\u5174\u3002\u201d \u3000\u3000\u53ea\u89c1\u7533\u4e00\u5e73\u607b\u607b\u5730\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u60a8\u8bf4\u7684\u662f\u4ec0\u4e48\u8bdd\uff0c\u4e24\u6cb3\u7eff\u6797\u9053\u7684\u57fa\u4e1a\u521b\u529e\u5df2\u4e45\uff0c\u54ea\u80fd \u4ece\u6211\u7533\u4e00\u5e73\u624b\u4e0a\u6bc1\u53bb\uff0c\u6211\u770b\u9646\u4fa0\u5ba2\u60f3\u662f\u9189\u4e86\u3002\u201d \u3000\u3000\u5802\u4e0a\u7fa4\u8c6a\u54c4\u7136\u4e00\u7b11\uff0c\u7b11\u58f0\u4e2d\u5e26\u7740\u5f02\u6837\u7684\u8f7b\u8511\uff0c\u9646\u98de\u767d\u5927\u6012\uff0c\u5c06\u624b\u4e2d\u9152\u676f\u53ed\u5730\u4e00\u58f0\uff0c\u6253\u5f97 \u7c89\u788e\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u7533\u4e00\u5e73\uff0c\u4f60\u8fd9\u7b97\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u2018\u201d\u7b11\u9762\u4eba\u5c60\u54c8\u54c8\u72c2\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u661f \u6708\u53cc\u5251\u82f1\u96c4\u4e00\u65f6\uff0c\u73b0\u5728\u4e5f\u8be5\u6536\u6536\u624b\u4e86\uff0c\u6211\u7533\u4e00\u5e73\u5bbd\u5927\u4e3a\u6000\uff0c\u8ba9\u4f60\u4eec\u843d\u4e2a\u5168\u5c38\uff0c\u8001\u5b9e\u544a\u8bc9\u4f60\uff0c \u4f60\u4eec\u8eab\u4e0a\u5df2\u4e2d\u4e86\u6211\u7528\u5343\u5e74\u7634\u6bd2\u70bc\u6210\u7684\u6c41\uff0c\u4e00\u4e2a\u5bf9\u65f6\u4e4b\u5185\uff0c\u5168\u8eab\u5c06\u4f1a\u6e83\u70c2\u800c\u6b7b\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u53c8\u662f\u4e00\u9635\u5927\u7b11\uff0c\u5f97\u610f\u5df2\u6781\u3002 \u3000\u3000\u6234\u68a6\u5c27\u542c\u5b8c\u5168\u8eab\u4e00\u9707\uff0c\u4f4e\u5934\u4e00\u770b\uff0c\u819d\u4e0a\u7684\u8863\u670d\u5df2\u70c2\u4e86\u7897\u5927\u4e00\u5757\uff0c\u91cc\u9762\u9690\u9690\u4f20\u51fa\u6076\u81ed\u4e4b \u6c14\uff0c\u77e5\u9053\u7533\u4e00\u5e73\u6240\u8a00\u975e\u865a\uff0c\u7528\u624b\u4e00\u62c9\u9646\u98de\u767d\uff0c\u4f4e\u4f4e\u4ed6\u8bf4\uff1a\u201c\u8001\u4e8c\uff0c\u522b\u52a8\u6c14\u3002\u201d \u3000\u3000\u968f\u5373\u671d\u7740\u7533\u4e00\u5e73\u5c06\u624b\u4e00\u62f1\uff0c\u6717\u58f0\u7b11\u9053\uff1a\u201c\u7b11\u9762\u4eba\u5c60\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff0c\u6211\u4eec\u683d\u7684\u603b\u7b97\u4e0d\u51a4 \u6789\uff0c\u65e2\u7136\u603b\u74e2\u628a\u5b50\u7f51\u5f00\u4e00\u7ebf\uff0c\u6211\u5144\u5f1f\u4ece\u6b64\u522b\u8fc7\u3002\u201d \u3000\u3000\u9646\u98de\u767d\u6b64\u65f6\u4e5f\u81ea\u53d1\u89c9\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u968f\u7740\u6234\u68a6\u5c27\u5f80\u5916\u8d70\u53bb\uff0c\u7533\u4e00\u5e73\u5e76\u4e0d\u62e6\u963b\uff0c\u671d\u7740\u7fa4\u8c6a\u5927 \u58f0\u7b11\u9053\uff1a\u201c\u661f\u6708\u53cc\u5251\u679c\u7136\u806a\u660e\uff0c\u73b0\u5728\u5c31\u53bb\u51c6\u5907\u540e\u4e8b\u3002\u201d \u3000\u3000\u5927\u5802\u4e0a\u7b11\u58f0\u54c4\u7136\uff0c\u7533\u4e00\u5e73\u7b11\u58f0\u66f4\u5389\u3002 \u3000\u3000\u9646\u98de\u767d\u8eab\u4f53\u84e6\u7136\u5f80\u540e\u5012\u7eb5\uff0c\u957f\u5251\u987a\u52bf\u62bd\u51fa\uff0c\u5934\u4e5f\u4e0d\u56de\uff0c\u53cd\u624b\u523a\u53bb\uff0c\u957f\u5251\u5b9b\u5982\u4e00\u9053\u94f6\u8679\uff0c \u5e26\u7740\u51c4\u5389\u98ce\u58f0\u76f4\u53d6\u7533\u4e00\u5e73\uff0c\u8fd9\u6b63\u662f\u201c\u82cd\u7a79\u5341\u4e09\u5f0f\u201d\u7684\u7edd\u62db\u201c\u5929\u8679\u5012\u5212\u201d\u3002\u7533\u4e00\u5e73\u7b11\u58f0\u672a\u843d\uff0c \u5251\u5df2\u4e34\u5934\uff0c\u53ea\u5f97\u5f80\u684c\u4e0b\u7a9c\u4e86\u51fa\u53bb\uff0c\u9646\u98de\u767d\u5251\u52bf\u4e00\u8f6c\uff0c\u53f3\u817f\u5f80\u540e\u865a\u7a7a\u4e00\u8e74\uff0c\u201c\u661f\u4e34\u516b\u89d2\u201d\uff0c\u957f \u5251\u5316\u505a\u70b9\u70b9\u94f6\u661f\uff0c\u5411\u7533\u4e00\u5e73\u5f53\u5934\u7f69\u4e0b\uff0c\u7533\u4e00\u5e73\u5c31\u5730\u4e00\u6eda\uff0c\u51a0\u7f69\u5168\u5931\uff0c\u8eb2\u5f97\u72fc\u72c8\u5df2\u6781\u3002 \u3000\u3000\u8fd9\u79cd\u5730\u8eba\u6551\u547d\u7684\u62db\u6570\uff0c\u6b66\u6797\u4e2d\u591a\u4e0d\u5c51\u4e3a\uff0c\u7533\u4e00\u5e73\u4e43\u7eff\u6797\u76df\u4e3b\uff0c\u6b66\u529f\u672c\u81ea\u4e0d\u5f31\uff0c\u5374\u56e0\u6beb\u672a \u6599\u5230\u9646\u98de\u767d\u51fa\u624b\u4e4b\u5947\uff0c\u6545\u6b64\u624d\u5f62\u5bb9\u614c\u4e71\uff0c\u5f53\u7740\u624b\u4e0b\u5982\u8bb8\u591a\u4eba\uff0c\u7528\u51fa\u8fd9\u79cd\u8eab\u6cd5\uff0c\u5b9e\u662f\u4e07\u4e0d\u5f97 \u5df2\uff0c\u7136\u5374\u4e22\u8138\u5df2\u6781\uff0c\u5f53\u4e0b\u7533\u4e00\u5e73\u4e0d\u89c9\u5927\u6012\uff0c\u5389\u58f0\u9053\uff1a\u201c\u597d\u670b\u53cb\u4e0d\u5356\u9762\u5b50\uff0c\u5e76\u80a9\u5b50\u52a8\u5bb6\u4f19\u62db\u547c \u4ed6\u3002\u201d \u3000\u3000\u5802\u4e0a\u7fa4\u8c6a\u987f\u65f6\u5927\u4e71\uff0c\u62bd\u5175\u5203\uff0c\u629b\u957f\u886b\uff0c\u773c\u770b\u5c31\u662f\u4e00\u573a\u8840\u6218\uff0c\u5ffd\u5730\u6709\u4eba\u5389\u58f0\u4e00\u559d\uff1a\u201c\u90fd\u7ed9 \u6211\u4f4f\u624b\u3002\u201d \u3000\u3000\u7533\u4e00\u5e73\u4ed7\u4ee5\u6210\u540d\u7684\u4e00\u5bf9\u5947\u95e8\u5f27\u5f62\u5251\u6b63\u5f85\u51fa\u624b\uff0c\u542c\u89c1\u6709\u4eba\u53d1\u8bdd\uff0c\u4e0d\u7981\u4e00\u987f\uff0c\u9646\u98de\u767d\u5374\u4e0d\u7406 \u8fd9\u78b4\u513f\uff0c\u957f\u5251\u4e00\u70b9\u684c\u9762\uff0c\u4eba\u53c8\u501f\u52bf\u5411\u4e0a\u62d4\u4e86\u51e0\u5c3a\uff0c\u8eab\u5f62\u7565\u4e00\u987f\u632b\uff0c\u5251\u52bf\u7531\u7b2c\u4e94\u5f0f\u201c\u843d\u5730\u6d41 \u661f\u201d\u5316\u505a\u7b2c\u5341\u5f0f\u201c\u6cdb\u6e21\u94f6\u6cb3\u201d\uff0c\u94f6\u5149\u5982\u6ed4\u6ed4\u4e4b\u6c34\uff0c\u5f80\u7533\u4e00\u5e73\u8eab\u4e0a\u903c\u53bb\u3002 \u3000\u3000\u201c\u661f\u6708\u53cc\u5251\u201d\u4ee5\u201c\u82cd\u7a79\u5341\u4e09\u5f0f\u201d\u996e\u8a89\u6b66\u6797\uff0c\u5251\u5f0f\u81ea\u6709\u72ec\u5230\u4e4b\u5904\uff0c\u5b83\u4e0d\u4ec5\u5feb\uff0c\u6700\u5389\u5bb3\u7684\u662f \u8eab\u5f62\u4e0d\u987b\u843d\u5730\uff0c\u5251\u52bf\u4f55\u5728\u7a7a\u4e2d\u81ea\u7136\u8fd0\u7528\u3002\u7533\u4e00\u5e73\u4e0d\u4f46\u6ca1\u9047\u8fc7\u8fd9\u79cd\u5bf9\u624b\uff0c\u751a\u81f3\u8fde\u8fd9\u79cd\u5251\u6cd5\u90fd\u4e0d \u66fe\u89c1\u8fc7\uff0c\u53c8\u5982\u4f55\u80fd\u591f\u62b5\u6321\uff0c\u53ea\u5f97\u5927\u4ef0\u8eab\uff0c\u5f80\u540e\u6025\u7a9c\uff0c\u53c8\u662f\u4e00\u9635\u5fd9\u4e71\uff0c\u65b9\u624d\u8eb2\u8fc7\u8fd9\u5251\u3002 \u3000\u3000\u6234\u68a6\u5c27\u773c\u89c1\u9646\u98de\u767d\u8fde\u7528\u7edd\u62db\u903c\u4f4f\u7533\u4e00\u5e73\uff0c\u60f3\u7f6e\u4e4b\u4e8e\u6b7b\u5730\uff0c\u5fc3\u91cc\u81ea\u601d\u7d22\uff1a\u201c\u5373\u4f7f\u5c06\u7533\u4e00\u5e73 \u6740\u6b7b\uff0c\u81ea\u5df1\u6027\u547d\u4e5f\u662f\u96be\u4fdd\uff0c\u4f55\u4e0d\u5148\u8bbe\u6cd5\u51fa\u53bb\uff0c\u5982\u80fd\u4e07\u4e00\u6551\u5f97\u81ea\u5df1\u7684\u6027\u547d\uff0c\u65e5\u540e\u8fd8\u6015\u6ca1\u6709\u62a5\u4ec7 \u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u4e8e\u662f\u4ed6\u4e5f\u5927\u58f0\u559d\u8fc7\uff1a\u201c\u4e8c\u5f1f\u4f4f\u624b\u3002\u2018\u97f3\u5982\u6d2a\u949f\uff0c\u5165\u8033\u9535\u7136\uff0c\u9646\u98de\u767d\u8eab\u968f\u5251\u8d70\uff0c\u201d\u4e91\u5982\u5c71 \u6d8c\u201c\u53c8\u5f85\u5411\u7533\u4e00\u5343\u53d1\u62db\uff0c\u542c\u89c1\u6234\u68a6\u5c27\u7684\u559d\u58f0\uff0c\u786c\u751f\u751f\u5c06\u5df2\u53d1\u51fa\u7684\u5251\u62db\u6536\u4e86\u56de\u6765\uff0c\u6e38\u76ee\u56db\u987e\uff0c \u53ea\u89c1\u5927\u5802\u4e0a\u7684\u4eba\u867d\u90fd\u5df2\u62bd\u51fa\u5175\u5203\uff0c\u4f46\u5374\u6ca1\u6709\u4e00\u4e2a\u4eba\u51fa\u624b\u3002\u6b64\u65f6\uff0c\u521a\u521a\u53d1\u8bdd\u7684\u4eba\u5df2\u7f13\u6b65\u8d70\u4e86\u51fa \u6765\uff0c\u795e\u6001\u751a\u662f\u4ece\u5bb9\uff0c\u5374\u662f\u4e00\u4f4d\u4e2d\u5e74\u6587\u58eb\uff0c\u4ed6\u671d\u7533\u4e00\u5e73\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u4ed6\u4e8c\u4eba\u5df2\u4e2d\u4e86\u603b\u74e2\u628a\u5b50\u7684 \u6781\u6bd2\u6697\u5668\uff0c\u8c05\u4e5f\u6d3b\u4e0d\u8fc7\u660e\u665a\uff0c\u6211\u770b\u4f60\u8fd8\u662f\u9ad8\u9ad8\u624b\uff0c\u628a\u8fd9\u4e24\u4eba\u4ea4\u7ed9\u6211\u56de\u53bb\u7b97\u4e86\u3002\u201d\u8bdd\u867d\u662f\u8bf4\u5f97 \u5ba2\u6c14\uff0c\u795e\u60c5\u5374\u751a\u662f\u5028\u50b2\u3002 \u3000\u3000\u7533\u4e00\u5e73\u624b\u91cc\u62ff\u7740\u4e00\u5bf9\u5f27\u5f62\u5251\uff0c\u6014\u6014\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u751a\u662f\u72fc\u72c8\uff0c\u542c\u4e86\u8fd9\u4eba\u7684\u8bdd\uff0c\u975e\u4f46\u4e0d\u4ee5\u4e3a \u4ef6\uff0c\u4eff\u4f5b\u8fd9\u4eba\u5bf9\u4ed6\u5076\u505a\uff0c\u662f\u7406\u6240\u5f53\u7136\u7684\uff0c\u53ea\u662f\u60f3\u4e86\u4e00\u4f1a\uff0c\u4e2d\u5e74\u6587\u58eb\u5df2\u662f\u4e0d\u8010\uff0c\u62c2\u7136\u8bf4\u9053\uff1a \u201c\u60f3\u662f\u603b\u74e2\u628a\u5b50\u4e0d\u5356\u6211\u8fd9\u4e2a\u9762\u5b50\u4e86\u3002\u201d \u3000\u3000\u7533\u4e00\u5e73\u8fde\u5fd9\u5f2f\u4e0b\u8170\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u4f46\u51ed\u718a\u5e08\u5085\u7684\u5429\u5490\uff0c\u53ea\u662f\u4ee5\u540e\u2026\u2026\u201d \u3000\u3000\u4e2d\u5e74\u6587\u58eb\u7acb\u523b\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u4e24\u864e\u76f8\u4e89\uff0c\u5fc5\u6709\u4e00\u4f24\uff0c\u4f60\u4eec\u4e24\u5bb6\u7684\u4e8b\u4ece\u6b64\u5df2\u4e86\uff0c\u4ee5\u540e\u7684\u4e8b\uff0c \u5168\u5305\u5728\u6211\u7684\u8eab\u4e0a\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u540e\u8d70\u5411\u661f\u6708\u53cc\u5251\uff0c\u8bf4\u9053\uff1a\u201c\u679c\u7136\u76db\u540d\u4e4b\u4e0b\u65e0\u865a\u58eb\uff0c\u661f\u6708\u53cc\u5251\uff0c\u7684\u786e\u4e0d\u51e1\u3002\u201d\u7a81\u7136\u4ed6 \u628a\u8bdd\u58f0\u653e\u5f97\u6781\u4f4e\uff1a\u201c\u4e8c\u4f4d\u4f55\u5fc5\u8ddf\u8fd9\u73ed\u5c0f\u4eba\u52a8\u6012\uff0c\u2018\u6843\u82b1\u7634\u6bd2\u2019\u5b50\u4e0d\u8fc7\u5348\uff0c\u4e24\u4f4d\u4e0d\u5982\u8ddf\u5728\u4e0b\u540c \u53bb\uff0c\u4e5f\u8bb8\u8fd8\u6709\u6d3b\u8def\u53ef\u8d70\u3002\u201d \u3000\u3000\u6234\u68a6\u5c27\u867d\u89c9\u6b64\u4eba\u5927\u662f\u8be1\u5f02\uff0c\u4f46\u662f\u4eba\u5728\u6c42\u751f\u7684\u6b32\u671b\u4e4b\u4e0b\uff0c\u4e5f\u5c31\u7ba1\u4e0d\u5f97\u8fd9\u8bb8\u591a\u4e86\uff0c\u53ea\u5f97\u8bf4 \u9053\uff1a\u201c\u4e00\u5207\u9075\u547d\u3002\u201d \u3000\u3000\u90a3\u4eba\u542c\u4e86\uff0c\u5c55\u5bb9\u4e00\u7b11\uff0c\u4f3c\u4e4e\u5f88\u662f\u9ad8\u5174\uff0c\u5c06\u624b\u671d\u7533\u4e00\u5e73\u4e00\u62f1\uff0c\u773c\u5149\u671d\u56db\u5468\u7565\u4e00\u7765\u7768\uff0c\u7b11\u7740 \u8bf4\uff1a\u201c\u74e2\u628a\u5b50\u7684\u9ad8\u4e49\uff0c\u5144\u5f1f\u5fc3\u9886\uff0c\u4eca\u65e5\u5c31\u6b64\u522b\u8fc7\u4e86\u3002\u201d \u3000\u3000\u4ed6\u4eec\u8d70\u51fa\u95e8\u5916\uff0c\u661f\u6708\u53cc\u5251\u53ea\u89c9\u4e00\u9635\u6e05\u51c9\u4e4b\u6c14\u6251\u9762\u800c\u6765\uff0c\u591c\u5bd2\u5982\u6c34\uff0c\u9152\u610f\u5168\u6d88\uff0c\u4f46\u811a\u6b65\u5374 \u6108\u52a0\u6c89\u91cd\uff0c\u817f\u5f2f\u5df2\u7136\u9ebb\u6728\u3002\u4e2d\u5e74\u6587\u58eb\u624b\u4e00\u6446\uff0c\u4e00\u8f86\u88c5\u6f14\u534e\u4e3d\u7684\u5957\u8f66\u6025\u9a70\u800c\u6765\u3002 \u3000\u3000\u4ed6\u4eec\u4e0a\u8f66\u540e\uff0c\u90a3\u4e2d\u5e74\u6587\u58eb\u5e76\u672a\u548c\u4ed6\u4eec\u540c\u5750\u8f66\u5185\u3002\u8f66\u5185\u88c5\u7f6e\u534e\u7f8e\uff0c\u7a97\u5e18\u6905\u5957\uff0c\u5168\u662f\u7edd\u4e0a\u54c1 \u7684\u8d21\u7f0e\uff0c\u661f\u6708\u53cc\u5251\u4e0d\u89c9\u7591\u56e2\u66f4\u91cd\uff0c\u90a3\u4e2d\u5e74\u6587\u58eb\u7a76\u7adf\u662f\u4f55\u7b49\u8eab\u4efd\uff1f\u4e3a\u4f55\u7f18\u6545\u4ed7\u4e49\u4f38\u624b\u6765\u7ba1\u8fd9\u4ef6 \u95f2\u4e8b\u5462\uff1f \u3000\u3000\u8f66\u884c\u751a\u6025\uff0c\u6ca1\u591a\u4e45\uff0c\u4e24\u4eba\u4fbf\u6e10\u6e10\u6655\u53bb\u3002 \u3000\u3000\u9192\u6765\u65f6\uff0c\u5374\u662f\u8eba\u5728\u4e00\u5f20\u975e\u5e38\u67d4\u8f6f\u7684\u5e8a\u4e0a\uff0c\u8fd9\u548c\u4ed6\u4eec\u4ee5\u524d\u6240\u7761\u8fc7\u7684\u8fe5\u4e0d\u76f8\u540c\uff0c\u5c4b\u91cc\u7a97\u660e\u51e0 \u51c0\uff0c\u4e00\u5c18\u4e0d\u67d3\uff0c\u9760\u58c1\u653e\u7684\u662f\u5806\u5217\u6574\u9f50\u7684\u4e66\u67b6\uff0c\u4e00\u7434\u4e00\u51e0\uff0c\u5b89\u653e\u5f97\u4ff1\u90fd\u6070\u5230\u597d\u5904\uff0c\u4eff\u4f5b\u662f\u5bcc\u8d35 \u4eba\u5bb6\u7684\u4e66\u623f\uff0c\u7a97\u6237\u5411\u5916\u652f\u8d77\uff0c\u4ece\u7a97\u53e3\u770b\u51fa\u53bb\uff0c\u53ea\u89c9\u6797\u6728\u8471\u90c1\uff0c\u5ead\u9662\u5f88\u6df1\uff0c\u6e3a\u65e0\u4eba\u8ff9\uff0c\u5076\u6709\u9e1f \u8bed\u866b\u9e23\uff0c\u4ece\u8fdc\u5904\u4f20\u6765\uff0c\u4ee4\u4eba\u6709\u51fa\u5c18\u4e4b\u611f\u3002 \u3000\u3000\u6234\u68a6\u5c27\u9996\u5148\u9192\u6765\uff0c\u4e0d\u4e00\u4f1a\uff0c\u9646\u98de\u767d\u4e5f\u9192\u4e86\uff0c\u4ed6\u8eab\u4f53\u4e00\u5f13\uff0c\u521a\u60f3\u5750\u8d77\uff0c\u53c8\u6251\u5730\u5012\u5728\u5e8a\u4e0a\uff0c \u4e0d\u7981\u53f9\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u6843\u82b1\u6bd2\u7634\u6041\u7684\u5389\u5bb3\uff0c\u6211\u603b\u7b97\u5f00\u4e86\u773c\u754c\u4e86\u3002\u201d\u63a5\u7740\u53c8\u4f4e\u58f0\u95f4\u9053\uff1a\u201c\u8fd9\u662f \u4f55\u7b49\u6240\u5728\uff0c\u6211\u4eec\u600e\u4f1a\u5230\u4e86\u6b64\u5904\uff1f\u201d \u3000\u3000\u6234\u68a6\u5c27\u7709\u5934\u4e00\u76b1\uff0c\u4e5f\u4f4e\u58f0\u8bf4\u9053\uff0c\u201c\u4e8c\u5f1f\u5207\u83ab\u4e71\u52a8\uff0c\u6211\u4eec\u6b64\u523b\u51f6\u5409\u5c1a\u4e0d\u81ea\u77e5\uff0c\u6700\u597d\u8fd8\u662f\u5148 \u8bd5\u8bd5\u80fd\u5426\u8fd0\u6c14\u884c\u529f\uff0c\u4e07\u4e00\u6709\u53d8\uff0c\u4e5f\u597d\u5e94\u4ed8\u3002\u201d \u3000\u3000\u9646\u98de\u767d\u6b63\u60f3\u7b54\u8bdd\uff0c\u7a81\u7136\u95e8\u5e18\u4e00\u6380\uff0c\u8fdb\u6765\u4e00\u4eba\uff0c\u6b63\u662f\u90a3\u8be1\u5f02\u7684\u4e2d\u5e74\u6587\u58eb\uff0c\u7b11\u541f\u541f\u5730\u7ad9\u5728\u95e8 \u53e3\uff0c\u4e00\u8fdb\u6765\u5c31\u7b11\u7740\u8bf4\uff1a\u201c\u4e24\u4f4d\u6682\u4e14\u597d\u751f\u4f11\u517b\uff0c\u6258\u5929\u4e4b\u5e78\uff0c\u73b0\u5728\u603b\u7b97\u5df2\u8131\u79bb\u9669\u5883\uff0c\u8fd9\u7634\u6bd2\u6041\u7684 \u5389\u5bb3\uff0c\u4e24\u4f4d\u80fd\u5426\u8131\u9669\uff0c\u4e8b\u524d\u6211\u4e5f\u96be\u4ee5\u9884\u6599\u5462\uff01\u201d\u8bf4\u5b8c\u5fae\u7b11\u7740\u5411\u524d\u8d70\u4e86\u51e0\u6b65\u3002 \u3000\u3000\u6234\u68a6\u5c27\u6323\u624e\u7740\u60f3\u8981\u5750\u8d77\uff0c\u90a3\u4e2d\u5e74\u6587\u58eb\u8fde\u5fd9\u8d70\u4e0a\u5c06\u4ed6\u6276\u7740\u7761\u597d\uff0c\u6b63\u8272\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\u4e24\u4f4d \u6b64\u523b\u5fc5\u5728\u6000\u7591\u6211\u662f\u4f55\u7b49\u4eba\u7269\uff0c\u6709\u4f55\u5c45\u5fc3\uff0c\u53ea\u662f\u4e24\u4f4d\u73b0\u5728\u5c1a\u672a\u75ca\u6108\uff0c\u4e0d\u5b9c\u4f24\u795e\uff0c\u597d\u5728\u6765\u65e5\u65b9 \u957f\uff0c\u5f7c\u6b64\u5373\u662f\u4e00\u5bb6\u4eba\u4e86\uff0c\u4ec0\u4e48\u8bdd\u90fd\u597d\u8bf4\u3002\u201d \u3000\u3000\u6234\u68a6\u5c27\u9053\uff1a\u201c\u9601\u4e0b\u6551\u547d\u4e4b\u6069\uff0c\u5c0f\u5f1f\u5b9e\u4e0d\u6562\u8a00\u8c22\uff0c\u4e0d\u77e5\u53ef\u5426\u8bf7\u6559\u9601\u4e0b\u9ad8\u59d3\u5927\u540d\uff0c\u4e5f\u597d\u8ba9\u5c0f \u5f1f\u4eec\u94ed\u8bb0\u5728\u5fc3\u3002\u201d \u3000\u3000\u90a3\u4e2d\u5e74\u4e66\u751f\u8bf4\uff1a\u201c\u4f11\u518d\u63d0\u8d77\u9053\u8c22\u7684\u8bdd\uff0c\u65e5\u540e\u4e24\u4f4d\u75ca\u6108\u65f6\uff0c\u5c0f\u5f1f\u81ea\u4f1a\u5411\u4e24\u4f4d\u89e3\u91ca\u6e05\u695a\u3002\u201d \u8bf4\u5b8c\u7adf\u81ea\u8d70\u4e86\u3002 \u3000\u3000\u6b64\u540e\u90a3\u4e2d\u5e74\u6587\u58eb\u5374\u672a\u518d\u6765\uff0c\u53ea\u65f6\u4e0d\u65f6\u6709\u4e9b\u7a7f\u7740\u8273\u4e3d\u7684\u4fca\u7f8e\u5c0f\u7ae5\uff0c\u9001\u6765\u4e9b\u53c2\u6c64\u8865\u54c1\uff0c\u5374\u662f \u4e00\u8a00\u4e0d\u53d1\uff0c\u95ee\u4ed6\u4e8b\u60c5\uff0c\u4e5f\u662f\u4e00\u6982\u4e0d\u77e5\uff0c\u9646\u98de\u767d\u51e0\u6b21\u5fcd\u4e0d\u4f4f\u8981\u53d1\u706b\uff0c\u90fd\u88ab\u6234\u68a6\u5c27\u6b62\u4f4f\u4e86\u3002 \u3000\u3000\u8fd9\u6837\u8fc7\u4e86\u4e24\u4e09\u5929\uff0c\u4ed6\u4eec\u5df2\u80fd\u4e0b\u5e8a\u6d3b\u52a8\uff0c\u4f46\u5374\u4f7f\u4e0d\u51fa\u4e00\u4e1d\u529b\u6c14\uff0c\u9646\u98de\u81ea\u53c8\u60f3\u51fa\u53bb\u770b\u770b\uff0c\u6234 \u68a6\u5c27\u53c8\u662f\u529d\u963b\uff0c\u9646\u98de\u767d\u751f\u5e73\u6240\u670d\u81ba\u7684\u9664\u4e86\u4ed6\u4eec\u7684\u5e08\u5085\u5916\uff0c\u5c31\u53ea\u6234\u68a6\u5c27\u4e00\u4eba\uff0c\u53ea\u5f97\u7f62\u4e86\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u4e00\u65e5\uff0c\u90a3\u4e2d\u5e74\u6587\u58eb\u679c\u7136\u6765\u4e86\uff0c\u8fd9\u624d\u5c06\u4e8b\u60c5\u7684\u59cb\u672a\uff0c\u8bf4\u4e86\u6e05\u695a\u3002\u539f\u6765\u4ed6\u4eec\u6240\u4f4f\u7684\u5730 \u65b9\uff0c\u662f\u5f53\u4eca\u50a8\u541b\u80e4\u6254\u7684\u540e\u9662\uff0c\u90a3\u4e2d\u5e74\u6587\u58eb\uff0c\u5374\u662f\u80e4\u6254\u7684\u6559\u5e08\u718a\u8d50\u5c65\uff0c\u5eb7\u7199\u672a\u5e74\uff0c\u5404\u8d1d\u52d2\u4e89\u593a \u7687\u4f4d\uff0c\u624b\u6bb5\u5c42\u51fa\u4e0d\u7a77\uff0c\u80e4\u6254\u4e3a\u4e86\u5de9\u56fa\u81ea\u5df1\u7684\u7687\u4f4d\uff0c\u6781\u529b\u5730\u60f3\u62c9\u62e2\u6b66\u6797\u597d\u624b\u4f5c\u4e3a\u81ea\u5df1\u7684\u5e2e\u624b\uff0c \u6240\u4ee5\u7b11\u9762\u4eba\u5c60\u7533\u4e00\u5e73\u4e94\u5341\u5927\u5bff\u65f6\uff0c\u80e4\u6254\u5f97\u5230\u624b\u4e0b\u62a5\u544a\uff0c\u7279\u6d3e\u718a\u8d50\u5c65\u53bb\uff0c\u60f3\u76f8\u673a\u7269\u8272\u9ad8\u624b\uff0c\u4f5c \u4e3a\u81ea\u5df2\u7684\u62a4\u536b\uff0c\u53ea\u662f\u7eff\u6797\u9053\u4e0a\u7fa4\u8c6a\uff0c\u4e0d\u662f\u5931\u4e4b\u7c97\u91ce\uff0c\u5c31\u662f\u6ca1\u6709\u60ca\u4eba\u7684\u6b66\u529f\uff0c\u5e76\u65e0\u4e00\u4e2a\u88ab\u718a\u8d50 \u5c65\u770b\u4e2d\u7684\u3002\u540e\u6765\u94f6\u6708\u5251\u5ba2\u9646\u98de\u767d\u62d4\u5251\u52a8\u624b\uff0c\u718a\u8d50\u5c65\u81ea\u662f\u8bc6\u8d27\uff0c\u4e00\u773c\u4fbf\u770b\u51fa\u4ed6\u662f\u5185\u5bb6\u9ad8\u624b\uff0c\u518d \u52a0\u4e0a\u661f\u6708\u53cc\u5251\u540d\u6ee1\u6b66\u6797\uff0c\u4ed6\u77e5\u9053\u7533\u4e00\u5e73\u7eb5\u7136\u518d\u662f\u51f6\u6a2a\uff0c\u4e5f\u4e0d\u6562\u5f97\u7f6a\u80e4\u4ecd\uff0c\u8fd9\u624d\u4e0d\u60dc\u5f97\u7f6a\u7533\u4e00 \u5e73\uff0c\u5c06\u4ed6\u4eec\u6551\u4e86\u53e3\u6765\uff0c\u518d\u7528\u5927\u5185\u79d8\u65b9\u7528\u5c3d\u5fc3\u529b\u66ff\u4ed6\u4eec\u89e3\u4e86\u6bd2\uff0c\u76ee\u7684\u81ea\u7136\u662f\u60f3\u5229\u7528\u661f\u6708\u53cc\u5251\u7684 \u6b66\u529f\uff0c\u6765\u66ff\u817b\u6254\u6548\u529b\u3002 \u3000\u3000\u6c5f\u6e56\u4e2d\u4eba\u672c\u91cd\u6069\u6028\uff0c\u6234\u3001\u9646\u4e8c\u4eba\u611f\u6069\u56fe\u62a5\uff0c\u5c31\u5728\u738b\u5e9c\u7559\u4e86\u4e0b\u6765\uff0c\u80e4\u6254\u5bf9\u4ed6\u4eec\u4e5f\u662f\u4f18\u672d\u6709 \u52a0\uff0c\u6781\u529b\u5730\u62c9\u62e2\uff0c\u7279\u8f9f\u540e\u9662\u505a\u4ed6\u4eec\u7ec3\u529f\u9759\u4e60\u4e4b\u5904\uff0c\u4faf\u95e8\u6df1\u4f3c\u6d77\uff0c\u4f55\u51b5\u738b\u5e9c\uff0c\u4e8e\u662f\u6c5f\u6e56\u4e0a\u9042\u6709 \u4e86\u4ed6\u4eec\u5df2\u6b7b\u7684\u4f20\u8bf4\u3002 \u3000\u3000\u718a\u8d50\u5c65\u672c\u662f\u4e00\u4ecb\u4e66\u751f\uff0c\u4e1d\u6beb\u4e0d\u61c2\u6b66\u672f\uff0c\u4f46\u5374\u662f\u6ee1\u8179\u6587\u624d\uff0c\u8c08\u5410\u9ad8\u96c5\uff0c\u4e1d\u6beb\u6ca1\u6709\u9178\u8150\u4e4b \u6c14\uff0c\u661f\u6708\u53cc\u5251\u4e5f\u9887\u656c\u91cd\u4ed6\u7684\u4e3a\u4eba\uff0c\u518d\u52a0\u4e0a\u6551\u547d\u4e4b\u6069\uff0c\u6e10\u6e10\u4e0d\u89c9\u7ed3\u6210\u5960\u9006\u3002 \u3000\u3000\u540e\u6765\u80e4\u6254\u88ab\u5176\u5f1f\u80e4\u64b0\u3001\u80e4\u63d0\u7b49\u6240\u6536\u517b\u4e4b\u5587\u561b\u90aa\u672f\u6240\u4e71\uff0c\u5931\u5374\u4e86\u672c\u6027\uff0c\u53d8\u6210\u4e86\u4e00\u6deb\u8650\u7684\u75af \u5b50\uff0c\u5eb7\u7199\u53ec\u4ed6\u5230\u585e\u5916\uff0c\u5728\u7687\u8425\u4e2d\u88ab\u5e9f\uff0c\u718a\u8d50\u5c65\u77e5\u9053\u592a\u5b50\u65e2\u5e9f\uff0c\u592a\u5b50\u5e9c\u5fc5\u7136\u4e0d\u4fdd\uff0c\u80e4\u64b0\u7b49\u624b\u6bb5 \u6bd2\u8fa3\uff0c\u5fc5\u8c0b\u65a9\u8349\u9664\u6839\u4e4b\u8ba1\uff0c\u81ea\u5df1\u8eab\u53d7\u80e4\u6254\u77e5\u9047\u4e4b\u6069\u3001\u52bf\u5fc5\u5f97\u4e3a\u4ed6\u7559\u4e00\u540e\u4ee3\uff0c\u4f46\u81ea\u5df1\u624b\u65e0\u7f1a\u9e21 \u4e4b\u529b\uff0c\u4e8e\u662f\u624d\u5c06\u98ce\u6254\u7684\u957f\u5b50\u5c14\u8d6b\u53ca\u5ae1\u5973\u5c14\u683c\u6ccc\u4ea4\u6258\u7ed9\u661f\u6708\u4e8c\u4eba\uff0c\u4ed6\u81ea\u5df1\u5374\u51c6\u5907\u6cd5\u53e4\u4e4b\u8c6b\u8ba9\uff0c \u4e3a\u77e5\u5df1\u8005\u800c\u6b7b\u4e86\u3002\u6234\u9646\u4e8c\u4eba\u672c\u4e0d\u80af\u8ba9\u4ed6\u5c3d\u611a\u5fe0\u800c\u6b7b\uff0c\u4f46\u662f\u718a\u8d50\u5c65\u4e66\u751f\u56fa\u6267\uff0c\u4ed6\u4e8c\u4eba\u4e5f\u65e0\u6cd5\u529d \u963b\u3002 \u3000\u3000\u661f\u6708\u53cc\u5251\u672c\u662f\u5927\u6c49\u5b50\u6c11\uff0c\u6c11\u65cf\u89c2\u5ff5\u751a\u5f3a\uff0c\u5f53\u521d\u7559\u5728\u592a\u5b50\u5e9c\u91cc\uff0c\u4ea6\u662f\u8feb\u4e0d\u5f97\u5df2\uff0c\u73b0\u5728\uff0c\u600e \u80af\u4e3a\u4e00\u5f02\u65cf\u5356\u547d\uff0c\u4f46\u4fa0\u4e49\u4e2d\u4eba\uff0c\u53d7\u70b9\u6c34\u4e4b\u6069\u5fc5\u62a5\u6d8c\u6cc9\uff0c\u5144\u5f1f\u4fe9\u5546\u91cf\u4e86\u8bb8\u4e45\u7ec8\u4e8e\u7b54\u5e94\u3002\u4e86\u4e0b \u6765\u3002\u540e\u672a\u592a\u5b50\u5e9c\u91cc\u7684\u4eba\uff0c\u679c\u7136\u88ab\u6740\u7684\u88ab\u6740\uff0c\u53d1\u653e\u7684\u53d1\u653e\uff0c\u718a\u8d50\u5c65\u81ea\u662f\u4e0d\u514d\uff0c\u53ef\u662f\u661f\u6708\u53cc\u5251\u5374 \u5df2\u5e26\u7740\u4e24\u4e2a\u5728\u7687\u5ba4\u7684\u9634\u8c0b\u624b\u6bb5\u4e0b\u88ab\u6b8b\u5bb3\u7684\u5c0f\u5b69\u8fdc\u8d74\u6c5f\u5357\u4e86\u3002 \u3000\u3000\u661f\u6708\u53cc\u5251\u540d\u5934\u592a\u5927\uff0c\u6c5f\u6e56\u4e2d\u4e0a\u8bc6\u4e4b\u672c\u591a\uff0c\u4f55\u51b5\u5404\u8d1d\u52d2\u5e9c\u8033\u76ee\u904d\u5e03\uff0c\u98ce\u58f0\u5373\u523b\u4f20\u51fa\uff0c\u4e8e\u662f \u4eac\u4e2d\u9ad8\u624b\u7eb7\u7eb7\u5357\u4e0b\uff0c\u4f01\u56fe\u622a\u4f4f\u8fd9\u5e26\u7740\u98ce\u6254\u5b50\u5973\u6f5c\u9003\u7684\u661f\u6708\u53cc\u5251\uff0c\u4f46\u6234\u68a6\u5c27\u4eba\u6781\u673a\u667a\uff0c\u4e00\u8def\u4e0a \u6f5c\u5f62\u9690\u4f0f\uff0c\u8eb2\u8fc7\u4e0d\u77e5\u591a\u5c11\u6b21\u5371\u9669\uff0c\u4f46\u5374\u60f3\u4e0d\u5230\u5728\u8fd9\u8fdc\u79bb\u4eac\u57ce\u5df2\u6570\u5343\u91cc\u7684\u5730\u65b9\uff0c\u4f1a\u8ba9\u4eba\u7ed9\u7aa5\u7834 \u4e86\u884c\u8ff9\u3002 \u3000\u3000\u6b64\u65f6\u6234\u68a6\u5c27\u9a91\u5728\u9a6c\u4e0a\uff0c\u8111\u6d77\u4e2d\u601d\u6f6e\u5982\u6d8c\uff0c\u7d0a\u4e71\u5df2\u6781\uff0c\u4ed6\u6697\u81ea\u601d\u91cf\uff0c\u81ea\u5df1\u6240\u4f5c\u7684\u4e8b\uff0c\u7a76\u7adf \u8be5\u662f\u4e0d\u8be5\uff1f\u975e\u4f46\u4eac\u4e2d\u722a\u7259\uff0c\u5bf9\u81ea\u5df1\u662f\u5343\u65b9\u767e\u8ba1\uff0c\u6b32\u5f97\u4e4b\u800c\u7518\u5fc3\uff0c\u5c31\u662f\u6c5f\u6e56\u4e2d\u767d\u9053\u7684\u670b\u53cb\uff0c\u4e5f \u4f1a\u4e0d\u803b\u81ea\u5df1\u7684\u4e3a\u4eba\u3002\u987b\u77e5\u6ee1\u6e05\u521d\u5e74\uff0c\u6b66\u6797\u4e2d\u4eba\u4ff1\u662f\u53cd\u6e05\u590d\u660e\u7684\u5021\u62a4\u8005\uff0c\u600e\u4f1a\u540c\u60c5\u81ea\u5df1\u7684\u4e3a\u80e4 \u6254\u5356\u547d\uff0c\u53ef\u662f\u53c8\u6709\u8c01\u4f1a\u77e5\u9053\u81ea\u5df1\u7684\u82e6\u5fc3\u5462\u3002 \u3000\u3000\u4ed6\u60f3\u5230\u81ea\u5df1\u548c\u9646\u98de\u767d\u5c06\u80e4\u6254\u7684\u5b50\u5973\u5e26\u51fa\u7687\u57ce\uff0c\u53c8\u4e0d\u501f\u5192\u7740\u4e07\u9669\u5077\u56de\u5df2\u662f\u201c\u4f17\u5931\u4e4b\u7684\u201d\u7684 \u592a\u5b50\u5e9c\uff0c\u5c06\u718a\u8d50\u5c65\u7684\u5927\u513f\u5b50\u718a\u8c03\u6551\u4e86\u51fa\u6765\uff0c\u7136\u540e\u53c8\u72e0\u7740\u5fc3\u5c06\u80e4\u6254\u7684\u513f\u5b50\u629b\u5728\u5927\u7ea2\u95e8\u5916\u5c0f\u7ea2\u95e8 \u6751\u4e00\u95f4\u5c0f\u5c71\u795e\u4ed9\u5e99\u7684\u95e8\u53e3\uff0c\u542c\u7740\u4e00\u4e2a\u516b\u5c81\u7684\u5e7c\u513f\u5728\u5bd2\u591c\u91cc\u557c\u54ed\uff0c\u5374\u4e0d\u987e\u800c\u53bb\uff0c\u4ed6\u4eff\u4f5b\u89c9\u5f97\u90a3 \u5b69\u5b50\u5c16\u9510\u7684\u54ed\u58f0\u6b64\u523b\u4ecd\u7136\u505c\u7559\u5728\u4ed6\u7684\u8033\u8fb9\u3002 \u3000\u3000\u4ed6\u53c8\u60f3\u5230\u4e3a\u4e86\u6d3b\u53e3\uff0c\u5728\u7ecf\u8fc7\u9999\u6cb3\u53bf\u65f6\uff0c\u6740\u4e86\u4ece\u592a\u5b50\u5e9c\u5e26\u51fa\u7684\u5c14\u8d6b\u5976\u5988\uff0c\u5f53\u4ed6\u62d4\u51fa\u5251\u65f6\uff0c \u90a3\u5e74\u8f7b\u800c\u59a9\u5a9a\u7684\u773c\u775b\u6b63\u4e5e\u601c\u5730\u671b\u7740\u4ed6\uff0c\u7528\u5404\u79cd\u65b9\u6cd5\u4e5e\u6c42\u4e00\u547d\uff0c\u4f46\u4ed6\u5374\u4e0d\u987e\u4e00\u5207\uff0c\u5c06\u5251\u63d2\u5165\u5979 \u90a3\u575a\u5b9e\u800c\u4e30\u6ee1\u7684\u80f8\u812f\uff0c\u6740\u6b7b\u4e86\u4e00\u6761\u65e0\u8f9c\u7684\u6027\u547d\uff0c\u4ed6\u4e0d\u7981\u6df1\u6df1\u8d23\u5907\u81ea\u5df1\uff0c\u4e3a\u4e86\u81ea\u5df1\u7684\u6069\u6028\uff0c\u81ea \u5df1\u6240\u4f5c\u7684\u786e\u662f\u592a\u8fc7\u4efd\u4e86\u3002 \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u6234\u68a6\u5c27\u4e0d\u7981\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u4ef0\u9996\u671b\u5929\uff0c\u53ea\u662f\u4e1c\u65b9\u6e10\u767d\uff0c\u5df2\u8fd1\u9ece\u660e\uff0c\u4e8e\u662f\u4ed6\u56de\u987e \u6b63\u5728\u8d76\u7740\u8f66\u7684\u9646\u98de\u767d\uff0c\u53f9\u9053\uff1a\u201c\u54ce\uff01\u603b\u7b97\u53c8\u662f\u4e00\u5929\u3002\u201d \u3000\u3000\u8f66\u8fdb\u592a\u5e73\u95e8\uff0c\u53ea\u89c1\u91d1\u9675\u65e7\u90fd\uff0c\u6c14\u52bf\u679c\u4e0d\u51e1\uff0c\u65f6\u65b9\u6e05\u6668\uff0c\u8857\u9053\u4e0a\u5df2\u662f\u70ed\u95f9\u975e\u5e38\uff0c\u6234\u68a6\u5c27\u4e0d \u7981\u5fc3\u795e\u4e00\u677e\uff0c\u8d76\u7740\u8f66\u9a6c\u6df7\u5728\u6742\u4e71\u7684\u4eba\u7fa4\u4e2d\uff0c\u6b64\u65f6\u8f66\u5185\u4f20\u51fa\u513f\u557c\uff0c\u9646\u98de\u81ea\u7b11\u9053\uff1a\u201c\u662f\u5b69\u5b50\u4eec\u8be5 \u5403\u70b9\u4ec0\u4e48\u7684\u65f6\u5019\u4e86\uff0c\u54b1\u4eec\u4e5f\u8be5\u6253\u4e2a\u5c16\uff0c\u6b47\u606f\u6b47\u606f\u4e86\u3002\u201d \u3000\u3000\u6234\u68a6\u5c27\u56de\u987e\u5de6\u53f3\uff0c\u5e76\u65e0\u6ce8\u610f\u4ed6\u4eec\u7684\u4eba\uff0c\u4e5f\u7b11\u7740\u70b9\u4e86\u70b9\u5934\uff0c\u8f66\u5f80\u671d\u5357\u7684\u5927\u8857\u7f13\u7f13\u8d70\u53bb\uff0c\u505c \u5728\u4e00\u95f4\u5e76\u4e0d\u751a\u5927\u7684\u5ba2\u5e97\u95e8\u53e3\uff0c\u5e97\u91cc\u7684\u5c0f\u4e8c\u8d76\u7d27\u8fc7\u6765\u63a5\u9a6c\u62db\u547c\uff0c\u6ee1\u8138\u5e26\u7740\u7b11\u5bb9\u3002\u8f66\u5b50\u4e00\u505c\uff0c\u8f66 \u5e18\u4e00\u6380\uff0c\u5374\u8d70\u4e0b\u6765\u4e00\u4e2a\u5e74\u8f7b\u7684\u5987\u4eba\uff0c\u4e00\u8d70\u4e0b\u8f66\uff0c\u5c31\u4f38\u4e86\u4e2a\u61d2\u8170\uff0c\u773c\u775b\u4e00\u98de\uff0c\u7adf\u662f\u4e2a\u7f8e\u4eba\uff0c\u53ea \u662f\u7709\u76ee\u95ee\u5e26\u7740\u4e09\u5206\u6deb\u8361\u4e4b\u8272\uff0c\u5979\u671d\u6234\u68a6\u5c27\u5a07\u58f0\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6696\u5501\uff0c\u771f\u662f\u628a\u6211\u7d2f\u6b7b\u4e86\u3002\u201d\u63a5 \u7740\u671d\u56db\u5468\u7565\u4e00\u6253\u91cf\uff0c\u53c8\u7b11\u95ee\uff1a\u201c\u8fd9\u5c31\u662f\u6c5f\u5b81\u5e9c\u5417\uff1f\u602a\u4e0d\u5f97\u8fd9\u4e48\u70ed\u95f9\u3002\u201d \u3000\u3000\u6234\u68a6\u5c27\u53c8\u662f\u4e00\u76b1\u7709\u5934\uff0c\u5e76\u672a\u7b54\u8bdd\uff0c\u5374\u671d\u7740\u6b63\u5728\u5446\u671b\u7740\u7684\u5e97\u5c0f\u4e8c\u8bf4\uff1a\u201c\u5feb\u51c6\u5907\u4e24\u95f4\u4e0a\u623f\uff0c \u7ed9\u7272\u53e3\u597d\u597d\u7684\u4e0a\u6599\u3002\u201d \u3000\u3000\u9646\u98de\u767d\u8df3\u4e0b\u8f66\u6765\uff0c\u968f\u7740\u6234\u68a6\u5c27\u8d70\u8fdb\u5e97\u91cc\uff0c\u6b64\u65f6\u90a3\u4fcf\u5987\u4eba\u5df2\u5e26\u7740\u4e24\u4e2a\u5c0f\u5b69\u8d70\u8fdb\u5c4b\u91cc\uff0c\u6234\u68a6 \u5c27\u53e3\u5934\u4e00\u671b\u9646\u98de\u81ea\uff0c\u4f4e\u58f0\u57cb\u6028\u9053\uff1a\u201c\u6211\u65e9\u53eb\u4f60\u4e0d\u7528\u8fd9\u4e2a\u5973\u4eba\uff0c\u770b\u5979\u7684\u6837\u5b50\uff0c\u8fdf\u65e9\u603b\u8981\u751f \u4e8b\u3002\u201d \u3000\u3000\u9646\u98de\u767d\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u7528\u5979\u600e\u4e48\u529e\uff0c\u96be\u9053\u54b1\u4eec\u8fd8\u80fd\u62b1\u7740\u5b69\u5b50\uff0c\u9664\u4e86\u5979\u6709\u8c01\u80af\u8ddf\u54b1\u4eec \u8dd1\u8fd9\u4e48\u8fdc\u7684\u8def\u3002\u201d \u3000\u3000\u5ffd\u7136\u5916\u9762\u6709\u4eba\u5728\u5927\u58f0\u5406\u559d\uff0c\u63a5\u7740\u5c31\u6709\u4eba\u6765\u6572\u623f\u95e8\uff0c\u9646\u98de\u767d\u5f00\u4e86\u95e8\uff0c\u53ea\u89c1\u5916\u7ad9\u4e86\u4e24\u4e2a\u7682 \u96b6\uff0c\u4e00\u526f\u76db\u6c14\u51cc\u4eba\u7684\u6837\u5b50\uff0c\u51b2\u7740\u9646\u98de\u81ea\u5927\u58f0\u8bf4\uff1a\u201c\u4f60\u4eec\u662f\u5e72\u4ec0\u4e48\u7684\uff0c\u4ece\u54ea\u91cc\u6765\uff0c\u5230\u54ea\u91cc \u53bb\uff1f\u201d \u3000\u3000\u9646\u98de\u767d\u4e0d\u7981\u5927\u60ca\uff0c\u4ee5\u4e3a\u4ed6\u4eec\u5df2\u77e5\u81ea\u5df1\u7684\u8eab\u4efd\uff0c\u7565\u4e00\u8fdf\u7591\uff0c\u6b63\u5728\u5bfb\u6069\u5e94\u4ed8\u4e4b\u7b56\uff0c\u90a3\u5e97\u5c0f\u4e8c \u5374\u8d3c\u7709\u9f20\u773c\u5730\u8ddf\u4e86\u8fc7\u53bb\uff0c\u8d54\u7740\u7b11\u8bf4\uff1a\u201c\u7237\u4eec\u8bf7\u591a\u5305\u6db5\uff0c\u8fd9\u662f\u5e97\u91cc\u89c4\u77e9\uff0c\u89c1\u4e86\u751f\u5ba2\u4e0d\u6562\u4e0d\u62a5\u4e0a \u53bb\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u4e86\u53c8\u6253\u7740\u6746\u8d70\u4e86\u3002 \u3000\u3000\u9646\u98de\u767d\u8fd9\u624d\u677e\u4e86\u53e3\u6c14\uff0c\u77e5\u9053\u8fd9\u53c8\u662f\u4e9b\u60f3\u6253\u4e2a\u79cb\u98ce\u7684\u516c\u5dee\uff0c\u60f3\u5230\u201c\u8f66\u8239\u5e97\u811a\u8859\uff0c\u65e0\u7f6a\u4e5f\u8be5 \u6740\u201d\u8fd9\u8bdd\u7684\u786e\u4e0d\u5047\uff0c\u5634\u91cc\u5374\u8bf4\uff1a\u201c\u54b1\u4eec\u5e26\u7740\u5bb6\u7737\u5230\u5357\u8fb9\u53bb\u5bfb\u4eb2\uff0c\u8bf7\u4e24\u4f4d\u4e0a\u5dee\u591a\u5173\u7167\u3002\u201d \u3000\u3000\u54ea\u77e5\u90a3\u516c\u5dee\u5374\u5927\u58f0\u559d\u9053\uff1a\u201c\u5c14\u7b49\u8eab\u4e0a\u5e26\u7740\u5175\u5203\u8eb2\u8eb2\u85cf\u85cf\u7684\uff0c\u5206\u660e\u4e0d\u662f\u597d\u4eba\uff0c\u5feb\u8ddf\u6211\u5230\u8859 \u95e8\u91cc\u53bb\u95ee\u8bdd\u3002\u201d \u3000\u3000\u9646\u98de\u767d\u4e0d\u89c9\u5927\u6012\uff0c\u5251\u7709\u4e00\u7ad6\u521a\u60f3\u53d1\u4f5c\uff0c\u5ffd\u5730\u6709\u4eba\u8dd1\u6765\uff0c\u51b2\u7740\u4ed6\u8bf4\uff1a\u201c\u5440\uff0c\u8fd9\u4e0d\u662f\u9646\u4e8c\u7237 \u5417\uff0c\u600e\u4e48\u4f1a\u8dd1\u5230\u8fd9\u513f\u6765\uff0c\u201d\u63a5\u7740\u53c8\u5bf9\u90a3\u4e24\u4e2a\u516c\u5dee\u8bf4\uff1a\u201c\u8fd9\u4fe9\u7237\u4eec\u662f\u6211\u7684\u719f\u4eba\uff0c\u6211\u62c5\u4fdd\u4ed6\u4eec\u51fa \u4e0d\u4e86\u9519\u3002\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u516c\u5dee\u76f8\u4e92\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u65e2\u7136\u662f\u5b5f\u5927\u7237\u7684\u719f\u4eba\uff0c\u90a3\u5c31\u602a\u6211\u4eec\u591a\u4e8b\u4e86\u3002\u201d \u8bf4\u5b8c\u7adf\u7b11\u7740\u8d70\u4e86\u3002 \u3000\u3000\u6234\u68a6\u5c27\u7b11\u7740\u8bf4\uff1a\u201c\u539f\u6765\u662f\u5317\u4eac\u57ce\u91cc\u632f\u6b66\u9556\u5c40\u5927\u9556\u5934\u94f6\u94a9\u5b5f\u4ef2\u8d85\uff0c\u771f\u662f\u5e78\u4f1a\u5f97\u5f88\u3002\u201d \u3000\u3000\u4e09\u4eba\u5bd2\u6684\u4e00\u4f1a\uff0c\u5b5f\u4ef2\u8d85\u7a81\u7136\u8bf4\uff1a\u201c\u4e24\u4f4d\u65e2\u7136\u5230\u4e86\u5357\u4eac\uff0c\u4e0d\u53ef\u4e0d\u53bb\u770b\u770b\u5b9d\u9a6c\u795e\u97ad\uff0c\u6211\u4e5f\u77e5 \u9053\u4e8c\u4f4d\u6b64\u6b21\u5357\u6765\uff0c\u5b9e\u6709\u96be\u8a00\u4e4b\u9690\uff0c\u4f46\u5b9d\u9a6c\u795e\u97ad\u4e49\u91cd\u5982\u5c71\uff0c\u4e5f\u8bb8\u4e8c\u4f4d\u89c1\u4e86\u4ed6\u4e8b\u60c5\u66f4\u597d\u5546\u91cf\u3002\u201d \u3000\u3000\u6234\u68a6\u5c27\u95ee\u9053\uff1a\u201c\u8fd9\u5b9d\u9a6c\u795e\u97ad\u53c8\u662f\u4f55\u4eba\uff0c\u542c\u6765\u751a\u662f\u8033\u719f\u3002\uff0c\u76c2\u4ef2\u8d85\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e8c\u4f4d\u4e45\u9690 \u4eac\u57ce\uff0c\u60f3\u4e0d\u5230\u5bf9\u6c5f\u5357\u4fa0\u8ff9\u5982\u6b64\u751f\u758f\uff0c\u60a8\u96be\u9053\u4e0d\u77e5\u9053\u6c5f\u6e56\u4eba\u79f0\u2018\u5317\u5251\u5357\u97ad\uff0c\u795e\u9b3c\u4e0d\u5360\u5148\u2019\uff0c\u5357 \u97ad\u5c31\u6307\u7684\u662f\u5b9d\u9a6c\u795e\u97ad\u8428\u5929\u9aa5\u4e86\u3002\u201d \u3000\u3000\u9646\u98de\u767d\u9053\u3001\u201c\u90a3\u4e48\u5317\u5251\u53c8\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u5b5f\u4ef2\u8d85\u5927\u7b11\u9053\uff1a\u201c\u9664\u4e86\u661f\u6708\u53cc\u5251\uff0c\u8fd8\u6709\u8c01\u80fd\u5f53\u6b64\u8a89\u3002\u201d \u3000\u3000\u6234\u68a6\u5c27\u5fae\u7b11\u9053\uff1a\u201c\u5b5f\u5144\u8fc7\u5956\u4e86\uff0c\u5012\u662f\u6211\u53c8\u542c\u4eba\u8bf4\u8d77\uff0c\u5357\u4eac\u9e23\u8fdc\u9556\u5c40\u7684\u603b\u9576\u5934\u8428\u5929\u9aa5\u4e0d\u4f46 \u638c\u4e2d\u4e08\u56db\u957f\u97ad\u53e6\u6709\u7cbe\u5999\u62db\u6570\uff0c\u800c\u4e14\u9a91\u672f\u7cbe\u7edd\uff0c\u5584\u4e8e\u76f8\u9a6c\uff0c\u82e5\u771f\u662f\u6b64\u4eba\uff0c\u786e\u662f\u503c\u5f97\u4e00\u89c1\u3002\u201d \u3000\u3000\u5b5f\u4ef2\u8d85\u4e00\u62cd\u5927\u817f\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u5c31\u662f\u6b64\u4eba\uff0c\u6211\u770b\u4e8c\u4f4d\u4e0d\u5982\u642c\u5230\u3002\u201d \u3000\u3000\u9e23\u8fdc\u9556\u5c40\u9760\u8fd1\u6c34\u897f\u95e8\uff0c\u79bb\u516d\u671d\u91d1\u7c89\u6240\u805a\u7684\u79e6\u6dee\u6cb3\u4e5f\u4e0d\u592a\u8fdc\uff0c\u95e8\u671d\u5317\u5f00\uff0c\u95e8\u524d\u6302\u7740\u4e00\u5757\u9ed1 \u5e95\u91d1\u5b57\u7684\u5927\u62db\u724c\uff0c\u6c14\u6d3e\u679c\u81ea\u4e0d\u51e1\uff0c\u4ed6\u4eec\u5230\u4e86\u95e8\u53e3\uff0c\u65e9\u6709\u9556\u5c40\u91cc\u4f19\u8ba1\u8fc7\u6765\u63a5\u9a6c\u4f3a\u5019\uff0c\u8fdb\u4e86\u5927 \u5385\uff0c\u9152\u5bb4\u65e9\u5df2\u5907\u9f50\uff0c\u4ed6\u4eec\u90fd\u662f\u82f1\u96c4\u672c\u8272\uff0c\u4e5f\u4e0d\u591a\u8c26\u8ba9\u5c31\u5750\u4e0b\u559d\u8d77\u6765\u4e86\u3002 \u3000\u3000\u9152\u662f\u82b1\u96d5\uff0c\u867d\u548c\u5317\u65b9\u559d\u60ef\u7684\u9ad8\u7cb1\u98ce\u5473\u8fe5\u5f02\uff0c\u4f46\u5374\u9152\u529b\u9187\u539a\uff0c\u540e\u52b2\u6700\u8db3\uff0c\u661f\u6708\u53cc\u5251\u672c\u90fd\u597d \u9152\uff0c\u9152\u9022\u77e5\u5df1\u66f4\u662f\u8d8a\u559d\u8d8a\u591a\uff0c\u4e0d\u89c9\u90fd\u6709\u4e9b\u9189\u4e86\u3002 \u3000\u3000\u5b5f\u4ef2\u8d85\u5ffd\u7136\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5317\u5251\u5357\u97ad\uff0c\u4eca\u5f97\u4e00\u805a\uff0c\u6211\u5b5f\u4ef2\u8d85\u7684\u529f\u52b3\u4e0d\u5c0f\uff0c\u4f60\u4eec\u8be5\u600e\u4e48\u8c22\u8c22 \u6211\u3002\u201d\u6234\u68a6\u5c27\u63a5\u7740\u8bf4\uff1a\u201c\u4e45\u95fb\u8428\u5144\u4ee5\u72c2\u98da\u97ad\u6cd5\uff0c\u79f0\u9738\u6c5f\u5357\uff0c\u4eca\u65e5\u786e\u662f\u5e78\u4f1a\u3002\u201d \u3000\u3000\u5b5f\u4ef2\u8d85\u5ffd\u7136\u4e00\u62cd\u684c\u5b50\uff0c\u5927\u58f0\u8bf4\uff1a\u201c\u5bf9\u4e86\uff0c\u5bf9\u4e86\uff0c\u5317\u5251\u5357\u97ad\uff0c\u4ff1\u90fd\u540d\u91cd\u6b66\u6797\uff0c\u4eca\u5929\u4f60\u4eec\u4e0d \u5982\u628a\u5404\u4eba\u7684\u6b66\u529f\uff0c\u5c31\u5728\u5e2d\u524d\u5370\u8bc1\u4e00\u4e0b\uff0c\u8ba9\u6211\u4e5f\u597d\u5f00\u5f00\u773c\u754c\u3002\u201d \u3000\u3000\u8428\u5929\u9aa5\u6027\u672c\u7c97\u8c6a\uff0c\u53c8\u52a0\u4e03\u5206\u9152\u610f\uff0c\u542c\u4e86\u7acb\u523b\u8d5e\u6210\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u82cd\u7a79\u5341\u4e09\u5f0f\u5144\u5f1f\u542c\u5230\u5df2 \u4e45\uff0c\u4eca\u65e5\u5f97\u80fd\u4e00\u4f1a\uff0c\u6211\u771f\u662f\u592a\u9ad8\u5174\u4e86\u3002\u201d\u8bf4\u5b8c\u7adf\u81ea\u8131\u53bb\u957f\u886b\uff0c\u8d70\u5230\u5385\u524d\u7684\u7a7a\u5730\u4e0a\uff0c\u51c6\u5907\u52a8\u624b \u4e0b\u9646\u98de\u767d\u770b\u4e0a\u53bb\u867d\u751a\u548c\u6c14\uff0c\u4f46\u4e2a\u6027\u5374\u6700\u505a\uff0c\u770b\u4e86\u8428\u5929\u9aa5\u8fd9\u6837\uff0c\u4e5f\u5c06\u957f\u886b\u8131\u53bb\uff0c\u624b\u671d\u684c\u9762\u4e00 \u6309\uff0c\u4eba\u4ece\u5e2d\u9762\u7a9c\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u9646\u98de\u767d\u5c1a\u672a\u843d\u5730\uff0c\u8428\u5929\u9a87\u624b\u671d\u8170\u95f4\u4e00\u63a2\uff0c\u968f\u624b\u6325\u51fa\u4e00\u6761\u957f\u97ad\uff0c\u957f\u903e\u4e00\u4e08\uff0c\u97ad\u98ce\u547c\u547c\uff0c\u5b9b \u5982\u7075\u86c7\uff0c\u9646\u98de\u81ea\u817f\u4e00\u987f\u632b\uff0c\u4eba\u4ece\u97ad\u98ce\u4e0a\u8d8a\u4e86\u8fc7\u53bb\uff0c\u62bd\u51fa\u957f\u5251\uff0c\u5934\u90fd\u4e0d\u56de\uff0c\u53cd\u624b\u4e00\u5251\uff0c\u53c8\u662f\u4e00 \u5f0f\u201c\u5929\u8679\u5012\u5212\u201d\u3002 \u3000\u3000\u8428\u5929\u9aa5\u542c\u89c1\u98ce\u58f0\u5f80\u524d\u4e00\u4fef\uff0c\u582a\u582a\u907f\u8fc7\u8fd9\u5251\uff0c\u4e4c\u91d1\u957f\u97ad\u5f80\u53e3\u4e00\u62a1\uff0c\u201c\u72c2\u98ce\u843d\u53f6\u201d\uff0c\u9646\u98de\u767d \u4eba\u5728\u7a7a\u4e2d\uff0c\u62db\u5df2\u8fd9\u51fa\uff0c\u97ad\u98ce\u5df2\u7136\u5377\u5230\uff0c\u8eb2\u65e0\u53ef\u8eb2\uff0c\u5b5f\u4ef2\u8d85\u5728\u65c1\u60ca\u547c\u4e00\u58f0\uff0c\u4ee5\u4e3a\u6b64\u62db\u5df2\u53ef\u5206\u51fa \u80dc\u8d1f\u3002 \u3000\u3000\u54ea\u77e5\u9646\u98de\u767d\u957f\u5251\u4e71\u70b9\uff0c\u201c\u6f2b\u5929\u661f\u6597\u201d\uff0c\u5251\u5251\u90fd\u523a\u7740\u8428\u5929\u9aa5\u97ad\u8eab\uff0c\u6070\u597d\u5c06\u97ad\u52bf\u5316\u4e86\u5f00\u53bb\uff0c \u5b5f\u4ef2\u8d85\u4e0d\u7981\u53c8\u53eb\u8d77\u597d\u6765\u3002 \u3000\u3000\u8428\u5929\u9aa5\u89c9\u5f97\u97ad\u8eab\u4e00\u8f6f\uff0c\u957f\u97ad\u5f80\u4e0b\u4e00\u5782\uff0c\u5ffd\u5730\u97ad\u68a2\u53cd\u6311\uff0c\u642d\u4f4f\u9646\u98de\u767d\u7684\u957f\u5251\uff0c\u7ade\u81ea\u7f20\u4f4f\u3002 \u3000\u3000\u539f\u6765\u8428\u5929\u9aa5\u81ea\u5e7c\u7ae5\u8eab\uff0c\u4ece\u6765\u4ee5\u5185\u529b\u89c1\u957f\uff0c\u6b64\u756a\u4ed6\u53c8\u60f3\u4ee5\u5185\u529b\u6765\u514b\u4f4f\u9646\u98de\u81ea\u602a\u5f02\u5251\u6cd5\uff0c\u4f55 \u51b5\u9646\u98de\u767d\u4eba\u5c1a\u672a\u843d\u5730\uff0c\u81ea\u662f\u8f83\u96be\u8fd0\u529b\u3002 \u3000\u3000\u54ea\u77e5\u201c\u82cd\u7a79\u5341\u4e09\u5f0f\u201d\u5251\u6cd5\u81ea\u6210\u4e00\u5bb6\uff0c\u5929\u4e0b\u7684\u5251\u6d3e\u9664\u4e86\u5929\u5c71\u51b7\u5bb6\u5144\u59b9\u7684\u201c\u98de\u9f99\u4e03\u5f0f\u201d\u4e4b \u5916\uff0c\u5c31\u53ea\u661f\u6708\u53cc\u5251\u7684\u201c\u82cd\u7a79\u5341\u4e09\u5f0f\u201d\u80fd\u8eab\u4e0d\u843d\u5730\uff0c\u5728\u7a7a\u4e2d\u81ea\u7531\u53d8\u5316\u62db\u672f\uff0c\u5f53\u4e0b\u9646\u98de\u767d\u77e5\u9053\u81ea \u5df1\u8eab\u65e0\u843d\u811a\u4e4b\u5904\uff0c\u4e0e\u8428\u5929\u9aa5\u8f83\u91cf\u5185\u529b\uff0c\u81ea\u662f\u5927\u4e3a\u5403\u4e8f\uff0c\u7a81\u751f\u6025\u667a\uff0c\u5c06\u5251\u628a\u4e00\u677e\uff0c\u4eba\u5374\u501f\u7740\u4e00 \u6309\u4e4b\u529b\uff0c\u8d8a\u5230\u8428\u5929\u9a87\u7684\u8eab\u540e\uff0c\u5e76\u6307\u5982\u5251\uff0c\u201c\u843d\u5730\u6d41\u661f\u201d\uff0c\u76f4\u6307\u8428\u5929\u9aa5\u7684\u201c\u80a9\u4e95\u7a74\u201d\u3002 \u3000\u3000\u8428\u5929\u9aa5\u6b63\u81ea\u5168\u795e\u5bf9\u4ed8\u9646\u98de\u767d\u7531\u5251\u5c16\u6e17\u51fa\u7684\u5185\u529b\uff0c\u7a81\u89c9\u624b\u4e2d\u4e00\u677e\uff0c\u6b63\u89c9\u9986\u8bb6\uff0c\u53f3\u80a9\u5df2\u662f\u5fae \u5fae\u4e00\u9ebb\uff0c\u9ad8\u624b\u8fc7\u62db\uff0c\u5dee\u4e4b\u6beb\u5398\uff0c\u5931\u4e4b\u5343\u91cc\uff0c\u8428\u5929\u9aa5\u5fae\u4e00\u5931\u7740\uff0c\u5373\u5df2\u843d\u8d25\uff0c\u5fc3\u4e2d\u867d\u662f\u4e0d\u670d\uff0c\u4f46 \u4e5f\u65e0\u6cd5\uff0c\u957f\u97ad\u4e00\u6325\uff0c\u7f20\u5728\u97ad\u4e0a\u7684\u5251\u76f4\u98de\u51fa\u53bb\uff0c\u9646\u98de\u767d\u8ddf\u7740\u7a9c\u51fa\u53bb\uff0c\u53bb\u52bf\u7adf\u6bd4\u5251\u6025\uff0c\u5c06\u5251\u62ff\u5728 \u624b\u4e0a\u3001\u53c8\u659c\u98de\u51fa\u53bb\u6570\u5c3a\uff0c\u624d\u8f7b\u98d8\u98d8\u843d\u5230\u5730\u4e0a\uff0c\u8eab\u6cd5\u7f8e\u5999\u5f02\u5e38\uff0c\u5b9d\u9a6c\u795e\u97ad\u79f0\u9738\u6c5f\u5357\uff0c\u4e8c\u5341\u4f59\u5e74 \u672a\u9022\u654c\u624b\uff0c\u5982\u4eca\u5728\u5341\u62db\u4e4b\u5185\u5c31\u6b64\u843d\u8d25\uff0c\u5fc3\u4e2d\u5b9e\u662f\u96be\u53d7\u5df2\u6781\u3002 \u3000\u3000\u9646\u98de\u767d\u4ed7\u7740\u8eab\u6cd5\u5947\u8be1\uff0c\u4fa5\u5e78\u80dc\u4e86\u4e00\u62db\uff0c\u5bf9\u8428\u5929\u9aa5\u7684\u96be\u53d7\u4e4b\u8272\uff0c\u5e76\u672a\u89c9\u5bdf\uff0c\u62b1\u62f3\u5fae\u7b11\u9053\uff1a \u201c\u627f\u8ba9\uff0c\u627f\u8ba9\uff0c\u8428\u5144\u7684\u5185\u529f\u786e\u5b9e\u60ca\u4eba\u3002\u201d \u3000\u3000\u8428\u5929\u9aa5\u53ea\u5f97\u5f3a\u7b11\u4e86\u7b11\uff0c\u6ca1\u6709\u8bf4\u51fa\u8bdd\u6765\uff0c\u5b5f\u4ef2\u8d85\u5bdf\u8a00\u89c2\u8272\uff0c\u6050\u6015\u4ed6\u4e8c\u4eba\u7ed3\u4e0b\u6881\u5b50\uff0c\u5fd9\u8dd1\u6765 \u7b11\u7740\u8bf4\uff1a\u201c\u5357\u97ad\u4ee5\u96c4\u539a\u89c1\u957f\uff0c\u5317\u5251\u4ee5\u7075\u5de7\u89c1\u957f\uff0c\u6b63\u662f\u5404\u6709\u5343\u79cb\uff0c\u8ba9\u6211\u5927\u5f00\u4e86\u773c\u754c\uff0c\u6765\u6765\u6765\uff0c \u6211\u501f\u82b1\u732e\u4f5b\uff0c\u656c\u4e8c\u4f4d\u4e00\u676f\u3002\u201d \u3000\u3000\u6234\u68a6\u5c27\u4eba\u6700\u7cbe\u660e\uff0c\u77e5\u9053\u8428\u5929\u9aa5\u5df2\u7136\u4e0d\u5feb\uff0c\u518d\u5750\u4e0b\u53bb\u53cd\u4f1a\u5f04\u5f97\u6ee1\u5ea7\u4e0d\u6b22\uff0c\u5f53\u4e0b\u7ad9\u8d77\u6765\uff0c\u5fae \u7b11\u8bf4\u9053\uff1a\u201c\u6211\u5df2\u4e0d\u80dc\u9152\u529b\uff0c\u8fd8\u662f\u5404\u81ea\u4f11\u606f\u4e86\u5427\u3002\u201d \u3000\u3000\u6b64\u65f6\u7a81\u7136\u6709\u4e2a\u9556\u5c40\u7684\u4f19\u8ba1\u8dd1\u4e86\u8fdb\u6765\uff0c\u6253\u7740\u6746\u8bf4\uff1a\u201c\u4e24\u4f4d\u7684\u884c\u674e\u53ca\u5b9d\u7737\u90fd\u5df2\u5230\u4e86\uff0c\u73b0\u5728\u6b63 \u5728\u5357\u8de8\u9662\u91cc\u4f11\u606f\u3002\u201d \u3000\u3000\u6234\u68a6\u5c27\u6b63\u597d\u5c31\u6b64\u4e0b\u53f0\uff0c\u8bf4\u9053\uff1a\u201c\u4eca\u65e5\u6b22\u805a\uff0c\u5b9e\u662f\u5feb\u6170\u751f\u5e73\uff0c\u6b64\u523b\u9152\u8db3\u996d\u9971\uff0c\u53ef\u5426\u52b3\u9a7e\u8fd9 \u4f4d\uff0c\u5e26\u6211\u5230\u5357\u8de8\u9662\u53bb\u770b\u770b\u3002\u201d \u3000\u3000\u8bf4\u7740\u8d70\u4e86\u51fa\u6765\uff0c\u8428\u5929\u9aa5\u5927\u7b11\u4e86\u51e0\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u65f6\u5982\u679c\u6211\u7528\u2018\u65e9\u5730\u62d4\u8471\u2019\u8eb2\u8fc7\u6b64\u62db\uff0c\u518d \u7528\u2018\u5929\u98ce\u72c2\u98d3\u2019\u5f80\u4e0b\u6a2a\u626b\u53bb\uff0c\u9646\u5144\u5f1f\u5c82\u4e0d\u8f93\u4e86\u3002\u201d\u63a5\u7740\u53c8\u671d\u6234\u68a6\u5c27\u8bf4\uff1a\u201c\u672a\u6765\u6765\uff0c\u6211\u5e26\u4f60 \u53bb\u3002\u201d\u6234\u68a6\u5c27\u4e5f\u89c9\u5f97\u6b64\u4eba\u8c6a\u723d\u5f97\u53ef\u7231\uff0c\u7b11\u7740\u8ddf\u4ed6\u8d70\u4e86\u51fa\u53bb\uff0c\u5b5f\u4ef2\u8d85\u671d\u9646\u98de\u767d\u770b\u4e86\u4e00\u773c\uff0c\u5c06\u9646 \u98de\u767d\u8131\u4e0b\u7684\u957f\u886b\u629b\u8fc7\u53bb\u7ed9\u4ed6\uff0c\u4e8e\u662f\u5927\u5bb6\u90fd\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u9646\u98de\u767d\u5728\u623f\u5185\u5f00\u7a97\u5916\u671b\uff0c\u53ea\u89c1\u7fa4\u661f\u6ee1\u5929\uff0c\u867d\u65e0\u6708\u4eae\uff0c\u9662\u4e2d\u4ecd\u662f\u5149\u8f89\u6f2b\u5730\uff0c\u4ed6\u60f3\u8d77\u5386\u6765\u906d \u9645\uff0c\u4e0d\u7981\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u76d8\u819d\u5750\u5728\u5e8a\u4e0a\uff0c\u5c4f\u606f\u8fd0\u6c14\uff0c\u505a\u8d77\u5185\u529f\u6765\u3002 \u3000\u3000\u90a3\u5976\u5988\u59d3\u590f\u540d\u83b2\u8d1e\uff0c\u672c\u662f\u6deb\u5a03\uff0c\u5728\u9999\u6cb3\u53bf\u51e0\u4e4e\u591c\u65e0\u865a\u5915\uff0c\u5982\u4eca\u4e45\u65f7\uff0c\u4e00\u8def\u4e0a\u5954\u9a70\uff0c\u56e0\u4e3a \u592a\u7d2f\uff0c\u5012\u8fd8\u80fd\u5fcd\u8010\uff0c\u5982\u4eca\u4e00\u5f97\u5b89\u5b9a\uff0c\u518d\u52a0\u4e0a\u6c5f\u752b\u7684\u6625\u5929\uff0c\u767e\u7269\u4ff1\u90fd\u52a8\u60c5\uff0c\u4f55\u51b5\u5979\u5462\u3002 \u3000\u3000\u5979\u659c\u501a\u5e8a\u4fa7\uff0c\u8eab\u4e0a\u7a7f\u7740\u4e00\u4e2a\u9c9c\u7ea2\u7684\u809a\u515c\uff0c\u8eab\u65c1\u7684\u4e00\u53cc\u5b69\u5b50\uff0c\u9f3b\u606f\u5747\u5300\uff0c\u90fd\u5165\u7761\u4e86\uff0c\u5979\u53ea \u89c9\u6625\u601d\u64a9\u4eba\uff0c\u7ea2\u751f\u53cc\u988a\uff0c\u8dd1\u4e0b\u5e8a\u53bb\uff0c\u559d\u4e86\u4e00\u676f\u51b7\u8336\uff0c\u4ecd\u662f\u65e0\u6cd5\u5e73\u606f\u6625\u591c\u4e4b\u7eee\u5ff5\u3002 \u3000\u3000\u5ffd\u7136\uff0c\u5979\u542c\u5f97\u90bb\u623f\u4f3c\u6709\u54cd\u52a8\uff0c\u6e10\u6e10\u54cd\u58f0\u4e0d\u7edd\uff0c\u5979\u77e5\u9053\u90bb\u5ba4\u7684\u9646\u98de\u767d\u5b9a\u5c1a\u672a\u5165\u7761\uff0c\u5979\u60f3\u5230 \u9646\u98de\u767d\u5bf9\u5979\u548c\u6c14\u7684\u7b11\u5bb9\uff0c\u518d\u4e5f\u65e0\u6cd5\u63a7\u5236\u6b32\u5ff5\uff0c\u8d77\u5e8a\u62ab\u4e0a\u4e86\u4ef6\u8863\u88f3\uff0c\u6084\u6084\u5730\u5f00\u95e8\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u9646\u98de\u767d\u7a97\u6237\u672a\u5173\uff0c\u590f\u83b2\u8d1e\u4ece\u7a97\u53e3\u671b\u53bb\uff0c\u53ea\u89c1\u9646\u98de\u767d\u5916\u8863\u5df2\u8131\uff0c\u7aef\u5750\u5728\u5e8a\u4e0a\uff0c\u4f53\u5185\u53d1\u51fa\u4e00 \u8fde\u4e32\u8f7b\u96f7\u822c\u7684\u54cd\u58f0\uff0c\u77e5\u4ed6\u5c1a\u5728\u7ec3\u529f\uff0c\u5374\u5f84\u76f4\u63a8\u95e8\u8d70\u4e86\u8fdb\u53bb\uff1b\u8f7b\u58f0\u5a07\u7b11\u9053\uff1a\u201c\u8fd9\u4e48\u665a\u4e86\u4f60\u8fd8\u7ec3 \u529f\u592b\uff0c\u4e5f\u4e0d\u4f11\u606f\u4f11\u606f\u3002\u201d \u3000\u3000\u590f\u83b2\u8d1e\u626d\u7740\u8d70\u5230\u5e8a\u8fb9\uff0c\u4e24\u53cc\u5145\u6ee1\u4e86\u6b32\u5ff5\u7684\u4fcf\u773c\u72e0\u72e0\u76ef\u7740\u9646\u98de\u767d\u3002\u9646\u98de\u767d\u770b\u89c1\u5979\u6df1\u591c\u8d70\u4e86 \u8fdb\u6765\uff0c\u81ea\u662f\u60ca\u8be7\uff0c\u4f46\u4ecd\u672a\u5728\u610f\uff0c\u671d\u5979\u4e00\u7b11\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u4e8b\u5417\uff1f\u201d \u3000\u3000\u9646\u98de\u767d\u7684\u4e00\u7b11\uff0c\u662f\u4ed6\u7d20\u6027\u5982\u6b64\uff0c\u4ece\u6765\u90fd\u662f\u7b11\u8138\u5411\u4eba\uff0c\u4f46\u590f\u83b2\u8d1e\u5374\u6b32\u706b\u711a\u8eab\uff0c\u53ea\u89c9\u8fd9\u4e00\u7b11 \u6709\u5982\u6625\u65e5\u4e4b\u98ce\uff0c\u5439\u5f97\u5979\u6b32\u706b\u66f4\u76db\uff0c\u88c5\u4f5c\u65e0\u610f\u5c06\u62ab\u7740\u7684\u8863\u670d\u6389\u5230\u5730\u4e0a\uff0c\u7c89\u817f\u7389\u80a1\uff0c\u86ee\u8170\u4e30\u4e73\uff0c \u7acb\u523b\u5448\u73b0\u5728\u9646\u98de\u767d\u7684\u773c\u524d\u3002 \u3000\u3000\u9646\u98de\u767d\u867d\u662f\u94c1\u8840\u7537\u513f\uff0c\u4f46\u4ed6\u6b63\u503c\u58ee\u5e74\uff0c\u201c\u996e\u98df\u7537\u5973\u201d\u672c\u662f\u4eba\u4e4b\u5927\u6b32\uff0c\u5982\u4f55\u80fd\u591f\u7981\u5f97\uff0c\u518d \u52a0\u4e0a\u590f\u83b2\u8d1e\u988a\u5982\u6625\u82b1\uff0c\u5a9a\u76ee\u52a8\u60c5\uff0c\u4ed6\u53ea\u89c9\u5fc3\u795e\u4e00\u8361\u3002 \u3000\u3000\u590f\u83b2\u8d1e\u89c1\u4ed6\u672a\u52a8\uff0c\u7f13\u7f13\u5730\u8d70\u5411\u524d\u53bb\uff0c\u4e24\u53ea\u52fe\u9b42\u7684\u773c\u775b\uff0c\u7728\u4e5f\u4e0d\u7728\u5730\u671b\u7740\u4ed6\uff0c\u7a81\u5730\u5f80\u524d\u4e00 \u6251\uff0c\u4e00\u628a\u6402\u4f4f\u9646\u98de\u767d\u7684\u80a9\u8180\uff0c\u5a07\u5598\u5fae\u5fae\uff0c\u5f20\u53e3\u54ac\u4f4f\u9646\u98de\u767d\u7684\u9888\u5b50\u3002 \u3000\u3000\u9646\u98de\u767d\u4eba\u975e\u6728\u77f3\uff0c\u6b64\u523b\u4e5f\u662f\u56db\u80a2\u4e4f\u529b\uff0c\u8f7b\u8f7b\u4f38\u624b\u4e00\u63a8\uff0c\u5374\u6070\u5de7\u63a8\u5728\u590f\u83b2\u8d1e\u8eab\u4e0a\u6700\u67d4\u8f6f\u7684 \u5730\u65b9\uff0c\u5fc3\u795e\u53c8\u662f\u4e00\u8361\uff0c\u590f\u83b2\u8d1e\u5c31\u52bf\u4e00\u63a8\uff0c\u5c06\u4ed6\u538b\u5728\u5e8a\u4e0a\uff0c\u9646\u98de\u767d\u6b64\u523b\u6b63\u662f\u7406\u667a\u5c06\u6e83\uff0c\u591a\u5e74\u64cd \u5b88\u773c\u770b\u6bc1\u4e8e\u4e00\u65e6\u3002 \u3000\u3000\u4e24\u5165\u7ffb\u6eda\u4e4b\u95f4\uff0c\u653e\u5728\u5e8a\u8fb9\u7684\u5251\uff1a\u5ffd\u5730\u94db\u4e00\u58f0\uff0c\u6389\u5728\u5730\u4e0a\uff0c\u9646\u98de\u767d\u84e6\u5730\u4e00\u60ca\uff0c\u987b\u77e5\u4ed6\u6bd5\u7adf \u4e0d\u662f\u597d\u8272\u4e4b\u5f92\uff0c\u53d7\u6b64\u4e00\u60ca\uff0c\u7406\u667a\u7acb\u523b\u56de\u590d\uff0c\u968f\u624b\u4e00\u63a8\uff0c\u5c06\u590f\u83b2\u8d1e\u63a8\u5230\u5730\u4e0a\uff0c\u5389\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d \u8981\u80e1\u95f9\uff0c\u5feb\u56de\u623f\u53bb\uff0c\u4e0d\u7136\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u4ed6\u7a81\u7136\u60f3\u5230\u521a\u624d\u60c5\u51b5\uff0c\u89c9\u5f97\u81ea\u5df1\u4e5f\u975e\u5b8c\u5168\u65e0\u9519\uff0c \u51f6\u72e0\u7684\u8bdd\u518d\u4e5f\u8bf4\u4e0d\u51fa\u53e3\uff0c\u8d70\u4e0b\u5e8a\u6765\uff0c\u76f4\u5411\u95e8\u53e3\u8d70\u51fa\u3002 \u3000\u3000\u590f\u83b2\u8d1e\u6b32\u6027\u6b63\u81ea\u4e0d\u53ef\u6536\u62fe\uff0c\u88ab\u4ed6\u4e00\u63a8\uff0c\u5148\u8fd8\u832b\u7136\u4e0d\u77e5\u542c\u63aa\uff0c\u518d\u542c\u5f97\u4ed6\u5389\u58f0\u8bf4\u8bdd\uff0c\u4e0d\u7981\u53c8 \u7f9e\u53c8\u6012\uff0c\u4f38\u624b\u4e00\u6491\u5730\u4e0a\uff0c\u60f3\u8981\u7ad9\u8d77\uff0c\u5374\u6b63\u6309\u5230\u843d\u5728\u5730\u4e0a\u7684\u957f\u5251\uff0c\u4eba\u5728\u6027\u6b32\u51b2\u52a8\u4e4b\u65f6\uff0c\u6700\u65e0\u7406 \u6027\uff0c\u4efb\u4f55\u4e8b\u90fd\u53ef\u505a\u51fa\uff0c\u590f\u83b2\u8d1e\u54ac\u4e00\u54ac\u7259\uff0c\u5c06\u957f\u5251\u62bd\u51fa\uff0c\u4e24\u624b\u63e1\u4f4f\u5251\u628a\uff0c\u5411\u9646\u98de\u767d\u8fde\u4eba\u5e26\u5251\uff0c \u523a\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u9646\u98de\u767d\u5934\u8111\u4ea6\u662f\u6df7\u4e71\u5f02\u5e38\uff0c\u751a\u662f\u77db\u76fe\uff0c\u4ed6\u542c\u5f97\u8eab\u540e\u6709\u4eba\u6251\u6765\uff0c\u60f3\u4e0d\u5230\u590f\u83b2\u8d1e\u4f1a\u7528\u5251\u6765\u523a \u4ed6\uff0c\u4ee5\u4e3a\u5979\u53c8\u8981\u524d\u6765\u7ea0\u7f20\uff0c\u8f6c\u8eab\u6b63\u60f3\u9a82\u5979\uff0c\u54ea\u77e5\u590f\u83b2\u8d1e\u6b63\u597d\u6251\u4e0a\uff0c\u53c8\u662f\u7528\u5c3d\u5168\u8eab\u529b\u6c14\uff0c\u9646\u98de \u767d\u6beb\u65e0\u51c6\u5907\uff0c\u957f\u5251\u6b63\u597d\u7531\u4ed6\u5de6\u80f8\u523a\u5165\uff0c\u7a7f\u8fc7\u80f8\u819b\uff0c\u9c9c\u8840\u6e85\u5f97\u590f\u83b2\u8d1e\u6ee1\u8eab\uff0c\u9646\u98de\u767d\u51c4\u5389\u4e00\u53eb\uff0c \u4e00\u4ee3\u4eba\u6770\uff0c\u5374\u846c\u9001\u5728\u4e00\u4e2a\u6deb\u5987\u624b\u4e0a\u3002 \u3000\u3000\u6234\u68a6\u5c27\u6b63\u719f\u7761\uff0c\u88ab\u9646\u98de\u767d\u7684\u60e8\u53eb\u58f0\u60ca\u9192\uff0c\u5927\u4e3a\u60ca\u9a87\uff0c\u6025\u5fd9\u8dd1\u4e0b\u5e8a\u6765\uff0c\u5927\u58f0\u53eb\u95ee\u9053\uff1a\u201c\u8001 \u4e8c\uff0c\u4ec0\u4e48\u4e8b\uff1f\u201d\u6765\u4e0d\u53ca\u53bb\u5f00\u623f\u95e8\uff0c\u53cc\u81c2\u4e00\u632f\uff0c\u7a7f\u8fc7\u7eb8\u505a\u7684\u7a97\u6237\uff0c\u98de\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u590f\u83b2\u8d1e\u8981\u523a\u9646\u98de\u767d\u672c\u662f\u4e00\u65f6\u51b2\u52a8\uff0c\u5e76\u975e\u771f\u7684\u662f\u60f3\u6740\u4ed6\uff0c\u6b64\u523b\u53ea\u89c9\u53c8\u6094\u53c8\u6015\uff0c\u542c\u89c1\u6234\u68a6\u5c27 \u4e00\u53eb\uff0c\u66f4\u662f\u9a87\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u8fde\u722c\u5e26\u6eda\uff0c\u8eb2\u5230\u5e8a\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u6234\u68a6\u5c27\u4e00\u8fdb\u623f\u95e8\uff0c\u53ea\u89c1\u9646\u98de\u767d\u5012\u5728\u5730\u4e0a\uff0c\u9c9c\u8840\u6ee1\u8eab\uff0c\u8eab\u4e0a\u7684\u5251\uff0c\u5c1a\u672a\u62d4\u51fa\uff0c\u77e5\u9053\u4e8b\u60c5\u4e0d \u5999\uff0c\u6025\u5f97\u58f0\u6cea\u9f50\u4e0b\uff0c\u5c06\u4ed6\u4e00\u628a\u62b1\u8d77\uff0c\u5636\u58f0\u53eb\u7740\uff1a\u201c\u8001\u4e8c\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u9646\u98de\u767d\u6b64\u523b\u5df2\u547d\u82e5\u6e38\u4e1d\uff0c\u5f20\u773c\u770b\u5230\u6234\u68a6\u5c27\uff0c\u773c\u4e2d\u4e0d\u7981\u6d41\u4e0b\u6cea\u6765\uff0c\u4ed6\u53ea\u89c9\u547c\u5438\u6e10\u96be\uff0c\u5f20\u53e3 \u6b63\u60f3\u8bf4\u8bdd\uff0c\u5374\u53ea\u8bf4\u4e00\u4e2a\u201c\u590f\u201d\u5b57\uff0c\u53cc\u76ee\u4e00\u95ed\uff0c\u7adf\u81ea\u53bb\u4e86\u3002 \u3000\u3000\u661f\u6708\u53cc\u5251\u81ea\u5e7c\u5728\u4e00\u8d77\u957f\u5927\uff0c\u56db\u5341\u4f59\u5e74\uff0c\u60a3\u96be\u76f8\u4f9d\uff0c\u751f\u6b7b\u4e0e\u5171\uff0c\u6234\u68a6\u5c27\u518d\u662f\u6c89\u7a33\uff0c\u4e5f\u4e0d\u80fd \u4fdd\u6301\u51b7\u9759\uff0c\u4ed6\u4e0d\u7981\u653e\u58f0\u75db\u54ed\uff0c\u6367\u7740\u9646\u98de\u767d\u7684\u5c38\u8eab\uff0c\u53ea\u662f\u8bf4\uff1a\u201c\u8001\u4e8c\uff0c\u6211\u4e00\u5b9a\u4e3a\u4f60\u62a5\u4ec7\u3002\u201d \u3000\u3000\u4ed6\u5c06\u9646\u98de\u767d\u7684\u5c38\u8eab\uff0c\u8f7b\u8f7b\u5730\u5e73\u653e\u5728\u5e8a\u4e0a\uff0c\u5c06\u5c38\u8eab\u4e0a\u63d2\u7740\u7684\u5251\u62bd\u51fa\uff0c\u5446\u5446\u5730\u770b\u7740\u9646\u98de\u81ea\u7684 \u5c38\u8eab\uff0c\u8840\u6cea\u4ff1\u51fa\uff0c\u500f\u5730\u628a\u811a\u4e00\u987f\uff0c\u53cc\u624b\u4e00\u6325\uff0c\u5c06\u5e8a\u4e0a\u7684\u652f\u67f1\uff0c\u65a9\u65ad\u4e86\u4e00\u6839\uff0c\u545b\u8bf4\u9053\uff1a\u201c\u4eca\u591c \u6211\u4e0d\u6740\u8428\u5929\u9aa5\uff0c\u8a93\u4e0d\u4e3a\u4eba\u3002\u201d \u3000\u3000\u539f\u6765\u9646\u98de\u767d\u4e34\u6b7b\u524d\u7684\u8bed\u97f3\u4e0d\u6e05\uff0c\u6234\u68a6\u5c27\u8bef\u8ba4\u4e3a\u662f\u201c\u8428\u201d\u5b57\uff0c\u6234\u68a6\u5c27\u600e\u4f1a\u60f3\u5230\u590f\u83b2\u8d1e\u4e00\u4e2a \u6beb\u65e0\u62f3\u52c7\u7684\u5973\u4eba\u4f1a\u6740\u6b7b\u9646\u98de\u767d\uff0c\u987b\u77e5\u9646\u98de\u767d\u8eab\u6000\u7edd\u827a\uff0c\u5bfb\u5e38\u4eba\u6839\u672c\u4e0d\u80fd\u8fd1\u8eab\uff0c\u82e5\u975e\u9ad8\u624b\uff0c\u600e \u80fd\u5c06\u5251\u7531\u4ed6\u7684\u80f8\u524d\u523a\u4eba\u3002 \u3000\u3000\u5357\u8de8\u9662\u8fd9\u4e00\u756a\u4e71\u52a8\uff0c\u65e9\u5df2\u60ca\u52a8\u4e86\u591a\u4eba\uff0c\u6234\u68a6\u5c27\u8d70\u51fa\u623f\u95e8\uff0c\u521a\u597d\u6709\u4e00\u9556\u5c40\u91cc\u7684\u8d9f\u5b50\u624b\u95fb\u58f0 \u8dd1\u6765\uff0c\u770b\u89c1\u4ed6\u624b\u6267\u957f\u5251\uff0c\u6ee1\u9762\u6740\u6c14\uff0c\u4e0d\u7531\u5927\u60ca\uff0c\u8fde\u5fd9\u8dd1\u53bb\u544a\u8bc9\u8428\u5929\u9aa5\uff0c\u8428\u5929\u9a87\u81ea\u662f\u83ab\u540d\u5176\u5999 \u968f\u7740\u90a3\u8d9f\u5b50\u624b\u8d70\u5230\u5357\u8de8\u9662\uff0c\u4eba\u89c1\u6234\u68a6\u5c27\u8d64\u7740\u53cc\u8db3\uff0c\u8eab\u886b\u4e0d\u6574\uff0c\u770b\u89c1\u8428\u5929\u9aa5\u76ee\u7726\u4ff1\u88c2\uff0c\u8bdd\u90fd\u4e0d \u8bb2\uff0c\u957f\u5251\u8fde\u9012\u4e09\u62db\uff0c\u5251\u5251\u90fd\u662f\u671d\u7740\u8428\u5929\u9aa5\u7684\u8981\u5bb3\u52a8\u624b\u3002\u8428\u5929\u9aa5\u7cca\u91cc\u7cca\u6d82\u5403\u4e86\u4e09\u5251\uff0c\u5de6\u907f\u53f3 \u8eb2\uff0c\u5634\u91cc\u5927\u58f0\u559d\u9053\uff1a\u201c\u4f60\u5728\u4e8e\u4ec0\u4e48\uff0c\u75af\u4e86\u5417\uff1f\u201d \u3000\u3000\u6234\u68a6\u5c27\u53e3\u91cc\u7b54\u9053\uff1a\u201c\u8ddf\u4f60\u8fd9\u79cd\u65e0\u803b\u5c0f\u4eba\u8fd8\u6709\u4ec0\u4e48\u8bdd\u8bf4\uff1f\u201d \u3000\u3000\u624b\u91cc\u53ef\u4e0d\u95f2\u7740\uff0c\u957f\u5251\u7531\u4e0a\u5230\u4e0b\uff0c\u5e26\u7740\u98ce\u58f0\u76f4\u53d6\u8428\u5929\u9aa5\uff0c\u5251\u5230\u4e2d\u9014\u5ffd\u7136\u5316\u505a\u4e09\u4e2a\u5708\u5b50\uff0c\u5206 \u53d6\u8428\u5929\u9aa5\u516d\u9633\u3001\u4e73\u7a74\uff0c\u4e09\u4e2a\u8981\u5bb3\uff0c\u8fd9\u6b63\u662f\u201c\u82cd\u7a79\u5341\u4e09\u5f0f\u3001\u7684\u7edd\u62db\u201d\u9877\u523b\u98ce\u4e91\u201c\u3002\u8428\u5929\u9aa5\u4e0d\u89c9 \u5927\u6012\uff0c\u9a82\u9053\uff1a\u201c\u4f60\u8fd9\u738b\u516b\u86cb\uff0c\u600e\u4e48\u75af\u4e86\u3002\u201d \u3000\u3000\u53cc\u811a\u8e29\u7740\u65b9\u4f4d\uff0c\u201c\u5012\u8e29\u4e03\u661f\u6b65\u201d\u8eb2\u8fc7\u6b64\u62db\uff0c\u53f3\u638c\u4e00\u5708\uff0c\u638c\u98ce\u5c06\u6234\u68a6\u5c27\u7684\u5251\u52bf\u538b\u4f4f\uff0c\u5de6\u624b \u4e00\u62f3\uff0c\u62f3\u98ce\u547c\u547c\uff0c\u76f4\u6253\u9762\u95e8\uff0c\u6234\u68a6\u5c27\u4e5f\u89c9\u6b64\u4eba\u5185\u529b\u5b9e\u662f\u6df1\u539a\uff0c\u8eab\u4f53\u53f3\u65cb\uff0c\u5c06\u62f3\u51e4\u907f\u53bb\uff0c\u7a81\u5730 \u5251\u4ea4\u5de6\u624b\uff0c\u8428\u5929\u9aa5\u65b9\u624d\u4e00\u62f3\u4e00\u62f3\u4ff1\u90fd\u65e0\u529f\uff0c\u77e5\u9053\u4eca\u65e5\u6b64\u6218\uff0c\u5b9e\u975e\u6613\u4e8b\uff0c\u7a81\u89c1\u4ed6\u5251\u95ea\u5de6\u624b\uff0c\u5de6 \u624b\u4ea6\u53d8\u62f3\u4e3a\u638c\uff0c\u6025\u9510\u5730\u5411\u6234\u68a6\u5c27\u624b\u8155\u52ff\u53bb\u3002 \u3000\u3000\u6234\u68a6\u5c27\u5de6\u624b\u4e00\u7f29\u4e00\u4f38\uff0c\u4e0d\u4f46\u5316\u4e86\u6765\u52bf\uff0c\u800c\u4e14\u53cd\u53d6\u8428\u5929\u9aa5\u7684\u53f3\u4e73\uff0c\u8428\u5929\u9aa5\u957f\u5578\u4e86\u4e00\u58f0\uff0c\u53ea \u89c1\u4ed6\u62f3\u52bf\u4e00\u53d8\uff0c\u5ffd\u638c\u5ffd\u6307\uff0c\u548c\u6234\u68a6\u5c27\u5728\u5251\u5149\u4e2d\u9012\u62db\uff0c\u4e1d\u6beb\u4e0d\u89c1\u793a\u5f31\uff0c\u987b\u77e5\u5b9d\u9a6c\u795e\u97ad\u4eab\u540d\u591a \u5e74\uff0c\u5b9e\u975e\u4fa5\u5e78\uff0c\u8d25\u7ed9\u9646\u98de\u767d\uff0c\u53ea\u662f\u4e00\u65f6\u5927\u610f\uff0c\u6234\u68a6\u5c27\u867d\u7136\u5251\u6c14\u5982\u8679\uff0c\u62db\u62db\u4ff1\u4e0b\u6bd2\u624b\uff0c\u4f46\u4e5f\u4e00 \u65f6\u5948\u4f55\u4ed6\u4e0d\u5f97\u3002 \u3000\u3000\u6b64\u65f6\u9556\u5c40\u91cc\u7684\u9556\u5e08\u4ee5\u53ca\u8d9f\u5b50\u624b\u4e5f\u5168\u95fb\u58f0\u800c\u6765\uff0c\u56e2\u56e2\u56f4\u4f4f\u4ed6\u4eec\u4e24\u4eba\uff0c\u4f46\u662f\u4ff1\u90fd\u6ca1\u6709\u63d2\u624b\uff0c \u539f\u6765\u8428\u5929\u9a87\u6700\u6068\u7fa4\u6bb4\uff0c\u8bb2\u7a76\u7684\u662f\u5355\u6253\u72ec\u6597\uff0c\u8981\u6709\u4eba\u5e2e\u4ed6\uff0c\u4ed6\u53cd\u4f1a\u627e\u90a3\u4eba\u62fc\u547d\uff0c\u5927\u5bb6\u90fd\u77e5\u9053\u4ed6 \u7684\u813e\u6c14\uff0c\u518d\u52a0\u4e0a\u4e24\u4eba\u4ff1\u662f\u51a0\u7edd\u4e00\u65f6\u7684\u9ad8\u624b\uff0c\u52a8\u5f97\u624b\u6765\uff0c\u5206\u6beb\u5dee\u9519\u4e0d\u5f97\uff0c\u522b\u4eba\u5c31\u662f\u63d2\u624b\uff0c\u4e5f\u63d2 \u4e0d\u8fdb\u6765\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u6b63\u4f5c\u751f\u6b7b\u4e4b\u640f\u6597\uff0c\u8eb2\u5728\u5e8a\u4e0b\u7684\u590f\u83b2\u8d1e\u6084\u6084\u5730\u6e9c\u4e86\u51fa\u6765\uff0c\u795e\u4e0d\u77e5\u9b3c\u4e0d\u89c9\u5730\u5f80\u623f\u91cc \u53bb\uff0c\u9662\u4e2d\u7684\u4eba\u90fd\u88ab\u8fd9\u767e\u5e74\u96be\u5f97\u4e00\u89c1\u7684\u6bd4\u6597\u6240\u5438\u5f15\uff0c\u7adf\u65e0\u4e00\u4eba\u6ce8\u610f\u5230\u5979\u3002 \u3000\u3000\u5979\u8d70\u8fdb\u623f\u5185\u6084\u4fcf\u5730\u89e3\u4e0b\u809a\u515c\uff0c\u62b9\u51c0\u8eab\u4e0a\u7684\u8840\u8ff9\uff0c\u5c06\u6cbe\u6ee1\u7740\u8840\u7684\u809a\u515c\u585e\u5728\u5e8a\u540e\uff0c\u5ffd\u7136\u5979\u53d1 \u89c9\u6b63\u5728\u7761\u89c9\u7684\u4e24\u4e2a\u5b69\u5b50\u5374\u53ea\u5269\u4e0b\u4e00\u4e2a\uff0c\u4e09\u5c81\u5927\u7684\u5c14\u683c\u6c81\u5c1a\u5728\u719f\u7761\uff0c\u90a3\u6bd4\u5979\u5927\u56db\u5c81\u7684\u718a\u501c\u5374\u4e0d \u77e5\u53bb\u5411\u4e86\u3002\u84e6\u5730\u5916\u9762\u53c8\u662f\u4e00\u58f0\u60e8\u53eb\uff0c\u5979\u5954\u81f3\u7a97\u53e3\u4e00\u671b\uff0c\u53ea\u89c1\u9662\u4e2d\u5927\u4e71\uff0c\u6234\u68a6\u5c27\u5df2\u4e0d\u77e5\u53bb\u5411\uff0c \u8428\u5929\u9a87\u6014\u6014\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u4e24\u773c\u7a7a\u6d1e\u5730\u671b\u7740\u524d\u65b9\uff0c\u4e0a\u524d\u53bb\u6400\u6276\u4ed6\u7684\u4eba\uff0c\u90fd\u88ab\u4ed6\u6325\u624b\u8d76\u53bb\uff0c\u590f\u83b2 \u8d1e\u4e0d\u77e5\u5728\u8fd9\u8f6c\u77ac\u95f4\u53d1\u751f\u4e86\u4f55\u4e8b\uff0c\u53c8\u4e0d\u6562\u95ee\u3002 \u3000\u3000\u8428\u5929\u9aa5\u8111\u4e2d\u6b63\u5728\u601d\u7d22\uff1a\u201c\u4e3a\u4f55\u6234\u68a6\u5c27\u4e0d\u5206\u7682\u767d\u5c31\u6765\u627e\u6211\u62fc\u547d\uff0c\u800c\u9646\u98de\u767d\u5374\u59cb\u7ec8\u4e0d\u89c1\u5462\uff0c \u6234\u68a6\u5c27\u5728\u8fd9\u91cc\u4f5c\u6b8a\u6b7b\u4e4b\u6597\uff0c\u9646\u98de\u767d\u662f\u4e0d\u53ef\u80fd\u4e0d\u9732\u9762\u7684\u5440\uff0c\u83ab\u975e\u2026\u2026\u201d\u60f3\u5230\u8fd9\u91cc\uff0c\u8428\u5929\u9aa5\u5c06\u811a \u4e00\u987f\uff0c\u5306\u5306\u8dd1\u5230\u9646\u98de\u767d\u7684\u95e8\u53e3\uff0c\u63a8\u95e8\u4e00\u770b\uff0c\u706f\u5149\u6b63\u7167\u5728\u50f5\u5367\u5728\u5e8a\u4e0a\u7684\u9646\u98de\u767d\u7684\u5c38\u8eab\u4e0a\uff0c\u767d\u8272 \u7684\u8863\u670d\uff0c\u6cbe\u6ee1\u4e86\u8840\u6e0d\u3002 \u3000\u3000\u8428\u5929\u9a87\u53c8\u662f\u4e00\u987f\u811a\uff0c\u81ea\u8bed\u9053\uff1a\u201c\u6211\u771f\u8be5\u6b7b\uff0c\u9646\u98de\u767d\u600e\u4f1a\u6b7b\u5728\u8fd9\u91cc\uff0c\u6234\u68a6\u5c27\u5b9a\u662f\u4ee5\u4e3a\u6211\u6740 \u4e86\u4ed6\uff0c\u6211\u53c8\u600e\u4f1a\u90a3\u4e48\u6025\u8e81\uff0c\u6ca1\u95ee\u4e2a\u6e05\u695a\u5c31\u52a8\u4e0a\u624b\u5462\uff0c\u5982\u4eca\u8fd9\u4e48\u4e00\u6765\uff0c\u5927\u5bb6\u90fd\u4f1a\u7591\u60d1\u6211\u662f\u51f6\u624b \u4e86\uff0c\u53cd\u8ba9\u90a3\u771f\u7684\u51f6\u624b\u900d\u9065\u6cd5\u5916\u3002\u201d\u4ed6\u671b\u4e86\u9646\u98de\u767d\u7684\u5c38\u8eab\u4e00\u773c\uff0c\u6697\u5fd6\u9053\uff1a\u201c\u4f46\u53c8\u4f1a\u662f\u8c01\u6740\u4e86\u4ed6 \u5462\uff1f\u4ed6\u5185\u5916\u529f\u4ff1\u90fd\u81fb\u4e0a\u4e58\uff0c\u53c8\u6709\u8c01\u80fd\u6709\u8fd9\u529b\u91cf\uff0c\u96be\u602a\u6234\u68a6\u5c27\u4f1a\u7591\u5fc3\u6211\u3002\u73b0\u5728\u6234\u68a6\u5c27\u8eab\u53d7\u91cd \u4f24\uff0c\u53c8\u5e26\u7740\u4e00\u4e2a\u5c0f\u5b69\uff0c\u6050\u6015\u96be\u9003\u6e05\u547d\u4e86\uff0c\u8fd9\u96be\u9053\u662f\u6211\u7684\u8fc7\u5931\u5417\uff1f\u201d\u4ed6\u542c\u5f97\u5435\u58f0\u5f88\u5927\uff0c\u53e3\u5934\u770b \u5230\u95e8\u5916\u5df2\u6324\u6ee1\u4e86\u4eba\uff0c\u5927\u559d\u9053\uff1a\u201c\u4f60\u4eec\u770b\u4ec0\u4e48\u9e1f\uff0c\u90fd\u7ed9\u6211\u6eda\u5f00\u3002\u201d \u3000\u3000\u4eba\u90fd\u6e10\u6e10\u8d70\u4e86\uff0c\u9662\u4e2d\u53c8\u6062\u590d\u4e86\u5e73\u9759\uff0c\u8428\u5929\u9aa5\u4ecd\u7ad9\u5728\u623f\u4e2d\u601d\u7d22\uff0c\u591c\u5df2\u975e\u5e38\u6df1\uff0c\u9694\u58c1\u7684\u623f \u4e2d\uff0c\u5ffd\u7136\u6709\u5b69\u5b50\u7684\u54ed\u58f0\uff0c\u4ed6\u60f3\uff1a\u201c\u8fd9\u4e00\u5b9a\u662f\u4ed6\u4eec\u5e26\u6765\u7684\u53e6\u5916\u4e00\u4e2a\u5b69\u5b50\uff0c\u6211\u8be5\u53bb\u770b\u770b\u4ed6\u3002\u201d \u3000\u3000\u4e8e\u662f\u4ed6\u8d70\u4e86\u8fc7\u53bb\uff0c\u8f7b\u8f7b\u5730\u63a8\u5f00\u623f\u95e8\uff0c\u4ed6\u770b\u89c1\u590f\u83b2\u8d1e\u6b63\u5750\u5728\u5e8a\u4e0a\uff0c\u62b1\u7740\u90a3\u5973\u5b69\u5b50\uff0c\u590f\u83b2\u8d1e \u770b\u89c1\u4ed6\u8d70\u4e86\u8fdb\u6765\uff0c\u53ea\u671b\u4e86\u671b\u4ed6\uff0c\u6ca1\u6709\u8bf4\u8bf7\uff0c\u90a3\u5b69\u5b50\u54ed\u58f0\u4ecd\u7136\u672a\u4f4f\uff0c\u8428\u5929\u9aa5\u5ffd\u7136\u89c9\u5f97\u975e\u5e38\u6b49 \u759a\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u4e0d\u8be5\u4e58\u7740\u6234\u68a6\u5c27\u5fc3\u4e71\u800c\u758f\u5ffd\u7684\u65f6\u5019\uff0c\u91cd\u4f24\u4e86\u4ed6\uff0c\u5982\u4eca\u4ed6\u5e26\u7740\u53ea\u6709\u4e03\u3001\u516b \u5c81\u7684\u5b69\u5b50\u9003\u4ea1\uff0c\u82e5\u4ed6\u4e00\u6b7b\uff0c\u90a3\u5b69\u5b50\u600e\u4e48\u529e\uff1f\u73b0\u5728\u8fd8\u5269\u4e0b\u7684\u8fd9\u4e2a\uff0c\u6211\u8be5\u597d\u597d\u7684\u7167\u987e\u5979\u3002\u201d \u3000\u3000\u4ed6\u8d70\u5230\u5e8a\u8fb9\uff0c\u62cd\u7740\u6b63\u5728\u557c\u54ed\u7684\u5b69\u5b50\u7684\u5934\uff0c\u4eb2\u5207\u7684\u8bf4\uff1a\u201c\u4e0d\u8981\u54ed\u4e86\uff0c\u4ece\u4eca\u6211\u8981\u597d\u597d\u5730\u770b\u987e \u4f60\u3002\u201d\u4ed6\u4f4e\u7740\u5934\uff0c\u4ece\u590f\u83b2\u8d1e\u655e\u5f00\u7684\u8863\u895f\u91cc\uff0c\u770b\u5230\u3002\u4e00\u7247\u96ea\u767d\u7684\u76ae\u80a4\uff0c\u4ed6\u4e0d\u7981\u5fc3\u8df3\u4e86\uff0c\u56db\u5341\u4f59 \u5e74\u6765\u7684\u7ae5\u5b50\u4e4b\u8eab\uff0c\u7b2c\u4e00\u6b21\u5fc3\u8df3\u5f97\u8fd9\u4e48\u5389\u5bb3\uff0c\u4ed6\u5583\u5583\u5730\u53c8\u91cd\u590d\u4e86\u4e00\u904d\uff1a\u201c\u6211\u8981\u597d\u597d\u5730\u770b\u987e\u4f60 \u4eec\u3002\u201d \u3000\u3000\u539f\u6765\u521a\u624d\u8428\u5929\u9aa5\u548c\u6234\u68a6\u5c27\u6253\u5f97\u6b63\u662f\u6fc0\u70c8\u7684\u65f6\u5019\uff0c\u9662\u91cc\u7684\u58f0\u97f3\u5435\u9192\u4e86\u6b63\u5728\u719f\u7761\u7684\u718a\u501c\uff0c\u4ed6 \u722c\u4e86\u8d77\u6765\uff0c\u770b\u89c1\u7761\u5728\u8eab\u8fb9\u7684\u5976\u5988\u5df2\u4e0d\u89c1\u4e86\uff0c\u5c31\u8dd1\u4e86\u51fa\u6765\uff0c\u9662\u4e2d\u6b63\u56f4\u4f4f\u4e00\u5806\u4eba\uff0c\u4eba\u5806\u91cc\u5251\u6c14\u7eb5 \u6a2a\uff0c\u4ed6\u4ece\u5c0f\u5c31\u53d7\u7740\u592a\u5b50\u5e9c\u91cc\u6b66\u5e08\u7684\u718f\u9676\uff0c\u77e5\u9053\u6709\u4eba\u5728\u90a3\u91cc\u6bd4\u6597\uff0c\u5c31\u6084\u6084\u5730\u4ece\u4eba\u5806\u91cc\u6324\u4e86\u8fdb \u53bb\uff0c\u4e00\u770b\u5374\u662f\u4ed6\u6700\u559c\u6b22\u7684\u6234\u53d4\u53d4\u6b63\u548c\u4eba\u6253\u67b6\uff0c\u4ed6\u5c31\u8e72\u5728\u65c1\u8fb9\u770b\u3002 \u3000\u3000\u4ed6\u770b\u4e86\u4e00\u4f1a\uff0c\u89c9\u5f97\u4ed6\u6234\u53d4\u53d4\u8fd8\u6ca1\u6709\u6253\u8d25\u90a3\u4eba\uff0c\u5fc3\u91cc\u5f88\u6025\uff0c\u539f\u6765\u718a\u501c\u81ea\u5c0f\u5c31\u80c6\u5927\u5305\u5929\uff0c\u4e13 \u559c\u6b22\u505a\u4e9b\u5192\u9669\u7684\u52fe\u5f53\uff0c\u529b\u5927\u65e0\u7a77\uff0c\u53c8\u4ece\u661f\u6708\u53cc\u5251\u90a3\u513f\u5b66\u4e0a\u4e9b\u62f3\u811a\u4e0a\u7684\u57fa\u672c\u5de5\u592b\uff0c\u73b0\u5728\u4ed6\u60f3 \u5230\uff0c\u6234\u53d4\u53d4\u8fd8\u6253\u4e0d\u8d62\uff0c\u6211\u53bb\u5e2e\u4ed6\u5fd9\uff0c\u4ed6\u60f3\u5230\u5c31\u505a\uff0c\u7ad9\u4e86\u8d77\u6765\uff0c\u8fd9\u65f6\u8428\u5927\u9aa5\u6b63\u80cc\u7740\u4ed6\uff0c\u4ed6\u5c31\u8dd1 \u8fc7\u53bb\u60f3\u4e00\u628a\u62b1\u4f4f\u8428\u5929\u9aa5\u7684\u817f\uff0c\u8ba9\u6234\u53d4\u53d4\u597d\u6253\u5f97\u65b9\u4fbf\uff0c\u6b64\u65f6\u6234\u68a6\u5c27\u52bf\u5982\u731b\u72ee\uff0c\u5c06\u201c\u82cd\u7a79\u5341\u4e09 \u5f0f\u201d\u91cc\u5fae\u5999\u62db\u6570\u90fd\u4f7f\u4e86\u51fa\u6765\uff0c\u8428\u5929\u9aa5\u6b63\u611f\u4e0d\u652f\uff0c\u5ffd\u5730\u4ed6\u542c\u5f97\u80cc\u540e\u6709\u4eba\u6697\u7b97\uff0c\u53cc\u8098\u4e00\u6c89\uff0c\u8eab\u5f62 \u4e00\u5f13\uff0c\u7a9c\u4e86\u51fa\u53bb\uff0c\u718a\u8c03\u4e00\u4e2a\u6251\u7a7a\uff0c\u5f80\u524d\u51b2\u5230\u6234\u68a6\u5c27\u7684\u5251\u5708\u91cc\uff0c\u6234\u68a6\u5c27\u6b63\u662f\u4e00\u62db\u201c\u5317\u6597\u79fb \u8fb0\u201d\uff0c\u5251\u52bf\u7531\u5de6\u65b9\u5230\u6709\u65b9\u5212\u4e86\u534a\u4e2a\u5708\u5b50\uff0c\u5ffd\u4ece\u5708\u5b50\u91cc\u5c06\u5251\u523a\u4e86\u51fa\u6765\uff0c\u84e6\u5730\u770b\u89c1\u718a\u501c\u51b2\u4e86\u8fdb \u6765\uff0c\u4e0d\u7531\u5927\u60ca\uff0c\u5251\u5f0f\u5df2\u51fa\uff0c\u65e0\u6cd5\u6536\u56de\uff0c\u5de6\u624b\u4e00\u7528\u52b2\uff0c\u731b\u6253\u53f3\u624b\u7684\u624b\u8155\uff0c\u957f\u5251\u4e00\u677e\uff0c\u94db\u7136\u6389\u5728 \u5730\u4e0a\u3002 \u3000\u3000\u8428\u5929\u9a87\u6b63\u5728\u6234\u68a6\u5c27\u7684\u4e0a\u9762\uff0c\u770b\u89c1\u6234\u68a6\u5c27\u8fd9\u6837\uff0c\u5fc3\u751f\u6076\u5ff5\uff0c\u60f3\u5230\uff1a\u201c\u53cd\u6b63\u4eca\u5929\u4f60\u4e0d\u6740\u4e86 \u6211\uff0c\u5c31\u662f\u6211\u6740\u4e86\u4f60\u3002\u201d\u4e24\u811a\u4e00\u6c89\uff0c\u5f80\u5916\u4e00\u8e74\uff0c\u6234\u68a6\u5c27\u5fc3\u795e\u6b63\u4e71\uff0c\u9632\u907f\u4e0d\u53ca\uff0c\u8fd9\u4e24\u811a\u6b63\u8e22\u5728\u4ed6 \u7684\u540e\u5fc3\u4e0a\uff0c\u53ea\u89c9\u80f8\u53e3\u4e00\u751c\uff0c\u54d7\u5730\u5410\u51fa\u4e00\u53e3\u8840\u6c34\u3002 \u3000\u3000\u987b\u77e5\u8428\u5929\u9aa5\u7d20\u4ee5\u5185\u529f\u89c1\u957f\uff0c\u8fd9\u4e24\u811a\u66f4\u662f\u5e73\u751f\u529f\u529b\u6240\u805a\uff0c\u5c31\u7b97\u662f\u4e00\u5757\u5de8\u77f3\uff0c\u4e5f\u4f1a\u88ab\u8e22\u5f97\u7c89 \u788e\uff0c\u51b5\u8840\u8089\u4e4b\u8eaf\uff0c\u6234\u68a6\u5c27\u77e5\u9053\u5df2\u662f\u4e0d\u4fdd\uff0c\u60f3\u7740\u975e\u4f46\u9646\u98de\u767d\u7684\u4ec7\u5df2\u4e0d\u80fd\u62a5\uff0c\u81ea\u5df1\u4e5f\u773c\u89c1\u4e0d\u652f\uff0c \u60e8\u5578\u4e86\u4e00\u58f0\uff0c\u62b1\u8d77\u6b63\u5728\u60ca\u6115\u4e2d\u7684\u718a\u501c\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u9f13\u8d77\u6700\u540e\u4e00\u4e1d\u529b\u91cf\uff0c\u53cc\u811a\u4e00\u987f\uff0c\u98d3\u5730\u7a9c\u5230 \u5899\u5916\u3002 \u3000\u3000\u4ed6\u4e00\u9635\u6025\u7a9c\uff0c\u4e5f\u4e0d\u77e5\u8dd1\u4e86\u591a\u4e45\uff0c\u811a\u6b65\u6108\u6765\u6108\u6162\uff0c\u51fa\u4e86\u6c34\u897f\u95e8\uff0c\u5373\u662f\u83ab\u6101\u6e56\uff0c\u6b64\u523b\u4f46\u89c1\u6c34 \u6ce2\u9759\u4f0f\uff0c\u5df2\u65e0\u4eba\u8ff9\u3002\u6234\u68a6\u5c27\u653e\u4e0b\u718a\u501c\u5728\u6e56\u8fb9\u5750\u4e86\u4e0b\u6765\uff0c\u8bd5\u7740\u8fd0\u6c14\u884c\u529f\uff0c\u4f46\u662f\u771f\u6c14\u5df2\u4e0d\u80fd\u805a\uff0c \u4ed6\u77e5\u9053\u81ea\u5df1\u547d\u5728\u9877\u523b\uff0c\u4ed6\u552f\u4e00\u4e0d\u80fd\u7791\u76ee\u7684\u662f\u718a\u501c\uff0c\u60f3\u5230\u4ed6\u4e00\u4e2a\u7a1a\u9f84\u5b7a\u5b50\uff0c\u8fde\u906d\u60e8\u53d8\uff0c\u832b\u832b\u4eba \u6d77\uff0c\u4f55\u5904\u662f\u4ed6\u7684\u5f52\u5bbf\uff0c\u81ea\u5df1\u548c\u9646\u98de\u767d\u98d8\u6cca\u534a\u751f\uff0c\u843d\u5f97\u5982\u6b64\u6536\u573a\uff0c\u4e0d\u7981\u6d41\u4e0b\u6cea\u6765\u3002\u718a\u501c\u770b\u89c1\u4ed6 \u5982\u6b64\uff0c\u5b69\u5b50\u6c14\u7684\u8138\u4e0a\u4e5f\u6d41\u51fa\u6210\u4eba\u7684\u60b2\u54c0\uff0c\u6273\u7740\u6234\u68a6\u5c27\u7684\u624b\u9e23\u54bd\u7740\u95ee\u9053\uff1a\u201c\u53d4\u53d4\uff0c\u4f60\u600e\u4e48\u5566\uff0c \u662f\u4e0d\u662f\u501c\u513f\u4e0d\u597d\uff0c\u5bb3\u5f97\u53d4\u53d4\u96be\u8fc7\u3002\u201d \u3000\u3000\u6234\u68a6\u5c27\u82f1\u96c4\u672b\u8def\uff0c\u770b\u4e86\u718a\u501c\u4e00\u773c\uff0c\u53ea\u89c1\u4ed6\u4fca\u76ee\u5782\u9f3b\uff0c\u5927\u8033\u5782\u8f6e\uff0c\u77e5\u9053\u4ed6\u51b3\u975e\u592d\u6298\u4e4b\u50cf\uff0c \u5fc3\u4e2d\u4e0d\u7981\u4e00\u5bbd\uff0c\u62ff\u7740\u4ed6\u7684\u624b\uff0c\u6148\u7965\u4ed6\u8bf4\uff1a\u201c\u53d4\u53d4\u9a6c\u4e0a\u5c31\u8981\u6b7b\u4e86\uff0c\u4ece\u4eca\u4ee5\u540e\u4f60\u53ea\u6709\u4e00\u4e2a\u4eba\u4e86\uff0c \u4f60\u8981\u597d\u597d\u5730\u7167\u987e\u81ea\u5df1\uff0c\u4f60\u6015\u4e0d\u6015\uff1f\u201d \u3000\u3000\u718a\u501c\u6447\u4e86\u6447\u5934\u8bf4\uff1a\u201c\u6211\u4e0d\u6015\u3002\u201d\u60f3\u4e86\u60f3\uff0c\u5ffd\u7136\u6251\u5230\u6234\u68a6\u5c27\u7684\u6000\u91cc\uff0c\u54ed\u4e86\u8d77\u6765\uff0c\u8bf4\uff1a\u201c\u53d4 \u53d4\uff0c\u4f60\u4e0d\u8981\u6b7b\u561b\uff01\u4f60\u4e0d\u8981\u6b7b\u561b\uff01\u201d \u3000\u3000\u6234\u68a6\u5c27\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u628a\u718a\u501c\u6276\u7740\u5750\u597d\uff0c\u770b\u4e86\u5f88\u4e45\uff0c\u6b63\u8272\u8bf4\u9053\uff1a\u201c\u4f60\u7231\u4e0d\u7231\u7238\u7238\uff1f\u201d\u718a\u501c \u54ed\u7740\u70b9\u4e86\u70b9\u5934\uff0c\u6234\u68a6\u5c27\u63a5\u7740\u8bf4\uff1a\u201c\u4f60\u8981\u8bb0\u4f4f\uff0c\u4f60\u7684\u7238\u7238\u548c\u6234\u53d4\u53d4\u3001\u9646\u53d4\u53d4\u662f\u88ab\u6ee1\u5dde\u4eba\u548c\u4e00\u4e2a \u53eb\u5b9d\u9a6c\u795e\u97ad\u8428\u5929\u9aa5\u7684\u4eba\u5bb3\u6b7b\u7684\uff0c\u4f60\u957f\u5927\u4e86\uff0c\u4e00\u5b9a\u8981\u4e3a\u4ed6\u4eec\u62a5\u4ec7\u3002\u201d\u718a\u501c\u54ed\u5f97\u66f4\u5389\u5bb3\uff0c\u6234\u68a6\u5c27 \u5ffd\u5730\u5389\u58f0\u559d\u9053\uff1a\u201c\u4e0d\u8bb8\u54ed\uff0c\u7ed9\u6211\u8dea\u4e0b\u6765\u3002\u201d\u718a\u501c\u60ca\u614c\u5730\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u62bd\u6ce3\u7740\u6b62\u7740\u4e86\u54ed\uff0c\u8dea\u5728 \u4ed6\u9762\u524d\u3002\u6234\u68a6\u5c27\u6323\u624e\u7740\u4ece\u8d34\u8eab\u7684\u8863\u670d\u91cc\u638f\u51fa\u4e86\u4e8c\u672c\u518c\u5b50\uff0c\u614e\u91cd\u5730\u4ea4\u7ed9\u718a\u501c\u8083\u7136\u8bf4\u9053\uff1a\u201c\u4f60\u8981 \u53d1\u8a93\u8bb0\u5f97\uff0c\u8fd9\u4e24\u672c\u4e66\u662f\u6211\u548c\u4f60\u9646\u53d4\u53d4\u4e00\u751f\u6b66\u529f\u7684\u7cbe\u534e\uff0c\u4f60\u65e0\u8bba\u5728\u4efb\u4f55\u56f0\u96be\u7684\u60c5\u51b5\u4e0b\uff0c\u90fd\u8981\u628a \u5b83\u5b66\u4f1a\u3002\u201d\u8bb2\u5230\u8fd9\u91cc\uff0c\u4ed6\u60f3\u5230\u718a\u501c\u53ea\u4e0d\u8fc7\u662f\u4e2a\u4e5d\u5c81\u5927\u7684\u5b69\u5b50\uff0c\u8ba9\u4ed6\u5230\u4f55\u5904\u53bb\u6c42\u751f\u5462\uff0c\u4ed6\u4e0d\u7981 \u5c06\u53e3\u6c14\u8f6c\u53d8\u5f97\u975e\u5e38\u548c\u7f13\uff0c\u62cd\u7740\u718a\u501c\u8bf4\uff1a\u201c\u4f60\u61c2\u4e0d\u61c2\uff1f\u201d \u3000\u3000\u718a\u501c\u54ed\u7740\u8bf4\uff1a\u201c\u53d4\u53d4\u4e0d\u8981\u6c14\uff0c\u501c\u513f\u77e5\u9053\uff0c\u501c\u513f\u4e00\u5b9a\u4f1a\u628a\u6b66\u529f\u5b66\u4f1a\uff0c\u66ff\u53d4\u53d4\u548c\u7238\u7238\u62a5 \u4ec7\u3002\u201d \u3000\u3000\u6234\u68a6\u5c27\u6b64\u65f6\u547c\u5438\u5df2\u5f02\u5e38\u56f0\u96be\uff0c\u542c\u4e86\u718a\u501c\u7684\u8bdd\uff0c\u8138\u4e0a\u95ea\u8fc7\u4e00\u4e1d\u5b89\u6170\u7684\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u624d\u662f \u597d\u5b69\u5b50\uff0c\u4f60\u8bb0\u7740\uff0c\u662f\u6ee1\u5dde\u4eba\u548c\u8428\u5929\u9aa5\u5bb3\u5f97\u6211\u4eec\u8fd9\u6837\u7684\uff0c\u4f60\u8bb0\u5f97\u5417\uff1f\u201d\u718a\u501c\u575a\u5b9a\u5730\u70b9\u4e86\u70b9\u5934\uff0c \u4ed6\u7d27\u62b1\u7740\u90a3\u4e24\u672c\u518c\u5b50\uff0c\u5df2\u4e0d\u518d\u54ed\u4e86\uff0c\u4ed6\u89c9\u5f97\u4ed6\u597d\u50cf\u5df2\u957f\u5927\u4e86\u8bb8\u591a\uff0c\u5df2\u7ecf\u5927\u5f97\u8db3\u591f\u53bb\u8d1f\u8d77\u8fd9\u4efd \u8270\u5de8\u7684\u62c5\u5b50\u3002 \u3000\u3000\u6234\u68a6\u5c27\u8e09\u8dc4\u7740\u7ad9\u4e86\u8d77\u6765\uff0c\u8d70\u5230\u6e56\u8fb9\uff0c\u4fef\u4e0b\u8eab\u642c\u8d77\u4e86\u4e00\u4e2a\u5927\u77f3\u5757\uff0c\u8f6c\u8eab\u5bf9\u718a\u501c\u6325\u4e86\u6325\u624b\uff0c \u8bf4\uff1a\u201c\u4f60\u8d70\u5427\uff0c\u4e0d\u8981\u5fd8\u8bb0\u4e86\u53d4\u53d4\u7684\u8bdd\u3002\u201d \u3000\u3000\u718a\u501c\u53c8\u54ed\u4e86\u8d77\u6765\uff0c\u4f46\u5374\u4e0d\u6562\u54ed\u51fa\u58f0\uff0c\u4f4e\u4e0b\u4e86\u5934\u54ed\u9752\u8bf4\uff1a\u201c\u6211\u4e0d\u8d70\uff0c\u6211\u8981\u966a\u53d4\u53d4\u3002\u201d \u3000\u3000\u6234\u68a6\u5c27\u4ef0\u671b\u5929\uff0c\u4f46\u89c1\u82cd\u7a79\u6d69\u6d01\uff0c\u7fa4\u661f\u707f\u7136\uff0c\u5fc3\u4e2d\u51c4\u60e8\u5df1\u6781\uff0c\u7f13\u7f13\u5730\u5c06\u90a3\u5757\u5927\u77f3\u7cfb\u8fdb\u8863\u895f \u91cc\uff0c\u72e0\u4e86\u72e0\u5fc3\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u5feb\u8d70\uff0c\u5feb\u8d70\uff0c\u8d70\u5f97\u6108\u8fdc\u6108\u597d\uff0c\u4f60\u518d\u4e0d\u8d70\uff0c\u53d4\u53d4\u8981\u751f\u6c14\u4e86\u3002\u201d \u3000\u3000\u718a\u501c\u722c\u4e86\u8d77\u6765\uff0c\u8f6c\u8eab\u8d70\u4e86\u4e24\u6b65\uff0c\u53c8\u56de\u5934\u770b\u4e86\u6234\u68a6\u5c27\u4e00\u773c\uff0c\u6234\u68a6\u5c27\u671d\u4ed6\u6325\u4e86\u6325\u624b\uff0c\u770b\u7740\u90a3 \u5f31\u5c0f\u7684\u8eab\u5f71\u6e10\u6e10\u8d70\u8fdc\u3002\u6c34\u6d9b\u62cd\u5cb8\uff0c\u5982\u6028\u5987\u4f4e\u4f4d\uff0c\u6234\u68a6\u5c27\u8f6c\u8eab\u5411\u6e56\uff0c\u89c9\u662f\u5df2\u6709\u5bd2\u610f\uff0c\u80f8\u4e2d\u7684\u77f3 \u5757\uff0c\u66f4\u89c1\u6c89\u91cd\uff0c\u6c89\u91cd\u5f97\u5df2\u5c06\u4ed6\u7a92\u606f\uff0c\u4ed6\u53cc\u81c2\u4e00\u632f\uff0c\u53ea\u7a9c\u4e86\u4e08\u8bb8\uff0c\u5c31\u6251\u5730\u843d\u5165\u6e56\u91cc\uff0c\u6e56\u4e2d\u6c34\u82b1 \u56db\u6e85\uff0c\u53c8\u6e10\u6e10\u5f52\u4e8e\u6c89\u5bc2\u3002 \u3000\u3000\u5929\u4e0a\u7684\u94f6\u6708\u82cd\u661f\uff0c\u4e98\u53e4\u4e89\u7693\uff0c\u5730\u4e0b\u7684\u94f6\u6708\u82cd\u661f\uff0c\u5374\u6c38\u8fdc\u6b92\u843d\u4e86\u3002 \u3000\u3000\u718a\u501c\u65e0\u52a9\u5730\u5f80\u524d\u8d70\u7740\uff0c\u53ea\u89c9\u524d\u9014\u4e00\u7247\u9ed1\u6697\uff0c\u4ed6\u60f3\u56de\u5934\u8dd1\u53bb\uff0c\u62b1\u7740\u6234\u53d4\u53d4\u75db\u54ed\u4e00\u573a\uff0c\u4f46\u662f \u53c8\u4e0d\u6562\uff0c\u4ed6\u89c9\u5f97\u65e0\u4f9d\u65e0\u9760\uff0c\u7a1a\u5f31\u7684\u5fc3\u91cc\uff0c\u60e7\u6015\u5df2\u6781\u3002 \u3000\u3000\u53c8\u8d70\u4e86\u4e00\u4f1a\uff0c\u4ed6\u4eff\u4f5b\u770b\u89c1\u8fdc\u5904\u7adf\u6709\u706f\u706b\uff0c\u8fde\u5fd9\u52a0\u5feb\u5f80\u524d\u8d70\u53bb\uff0c\u4ed6\u62ed\u5e72\u4e86\u773c\u6cea\uff0c\u628a\u6234\u68a6\u5c27 \u7ed9\u4ed6\u7684\u4e24\u672c\u518c\u5b50\uff0c\u4ed4\u7ec6\u5730\u6536\u5728\u6000\u91cc\uff0c\u4ed6\u672c\u662f\u767e\u4e16\u96be\u9047\u7684\u7edd\u9876\u806a\u660e\u4e4b\u4eba\uff0c\u7ecf\u8fc7\u707e\u96be\uff0c\u53c8\u4f7f\u4ed6\u6210 \u719f\u4e86\u8bb8\u591a\uff0c\u4ed6\u77e5\u9053\u8981\u60f3\u4e3a\u81ea\u5df1\u7684\u7236\u4eb2\u548c\u6234\u53d4\u53d4\u62a5\u4ec7\uff0c\u5c31\u8981\u6d3b\u4e0b\u53bb\uff0c\u4e3a\u4e86\u201c\u751f\u5b58\u201d\uff0c\u4ed6\u613f\u610f\u505a \u51fa\u4efb\u4f55\u4e8b\uff0c\u867d\u7136\u4ed6\u4e0d\u77e5\u9053\u600e\u6837\u751f\u5b58\uff0c\u4f46\u662f\u4ed6\u53d1\u8a93\uff0c\u4ed6\u8981\u751f\u5b58\u4e0b\u53bb\u3002 \u7b2c\u4e8c\u7ae0\u3000\u52e4\u4fee\u82e6\u7ec3>> \u53e4\u9f99\u300a\u82cd\u7a79\u795e\u5251\u300b \u7b2c\u4e8c\u7ae0\u3000\u52e4\u4fee\u82e6\u7ec3 \u3000\u3000\u79e6\u6dee\u6cb3\u82b1\u822b\u7b19\u6b4c\uff0c\u805a\u516d\u671d\u91d1\u7c89\uff0c\u6b64\u65f6\u5df2\u662f\u5b50\u591c\uff0c\u4f46\u5bfb\u6b22\u9010\u4e50\u7684\u516c\u5b50\u9614\u5c11\u4ecd\u672a\u6563\u5c3d\uff0c\u718a\u501c \u8d70\u5230\u6cb3\u8fb9\uff0c\u770b\u5230\u6bcf\u53ea\u8239\u4e0a\u90fd\u6302\u7740\u724c\u5b50\uff0c\u4e0a\u9762\u5199\u7740\u540d\u5b57\uff0c\u6709\u4e9b\u8239\u706f\u706b\u4ecd\u4eae\uff0c\u91cc\u9762\u6709\u6684\u7b11\u58f0\uff0c\u6709 \u4e9b\u8239\u5374\u5df2\u7184\u4e86\u706f\u706b\u3002\u4ed6\u770b\u89c1\u6709\u4e00\u53ea\u8239\u505c\u5728\u8f83\u8fdc\u4e4b\u5904\uff0c\u4e0d\u50cf\u522b\u7684\u8239\u90a3\u6837\u4e00\u53ea\u8fde\u7740\u4e00\u53ea\uff0c\u800c\u4e14\u706f \u706b\u4ecd\u7136\u4eae\u7740\uff0c\u4ed6\u5c31\u8d70\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u90a3\u53ea\u8239\u7684\u7a97\u6237\u5411\u5916\u652f\u7740\uff0c\u4ed6\u7ad9\u5728\u5cb8\u8fb9\u770b\u4e86\u4e00\u4f1a\uff0c\u91cc\u9762\u5e76\u65e0\u54d7\u7b11\u4e4b\u58f0\uff0c\u505c\u4e86\u4e00\u4f1a\uff0c\u7a97\u53e3\u5ffd \u7136\u722c\u51fa\u4e00\u4e2a\u5c0f\u5973\u5b69\u7684\u5934\uff0c\u5927\u7ea6\u4e5f\u6709\u516b\u3001\u4e5d\u5c81\u3002\u8fd9\u665a\u6708\u8272\u751a\u660e\uff0c\u718a\u501c\u7ad9\u5728\u6708\u5149\u4e0b\uff0c\u88ab\u8239\u91cc\u7684\u5c0f \u5973\u5b69\u770b\u89c1\u4e86\uff0c\u79e6\u6dee\u6cb3\u9152\u8089\u5f81\u9010\uff0c\u5f88\u5c11\u6709\u5b69\u5b50\u4eec\u6765\uff0c\u90a3\u5c0f\u5973\u5b69\u770b\u89c1\u718a\u501c\uff0c\u5c31\u5173\u7740\u671d\u4ed6\u62db\u4e86\u62db \u624b\u3002\u718a\u501c\u8fdc\u8fdc\u770b\u5230\u5979\u4e24\u53ea\u773c\u775b\u53c8\u5927\u53c8\u4eae\uff0c\u7b11\u8d77\u6765\u50cf\u662f\u6709\u4e24\u4e2a\u5f88\u6df1\u7684\u9152\u7a9d\uff0c\u4e5f\u4e0d\u89c9\u5411\u524d\u8d70\u53bb\uff0c \u5ffd\u7136\u811a\u5e95\u4e00\u6ed1\uff0c\u4ed6\u60ca\u53eb\u4e86\u4e00\u58f0\uff0c\u8dcc\u4e0b\u6cb3\u53bb\uff0c\u90a3\u5c0f\u5973\u5b69\u770b\u4e86\uff0c\u4e5f\u5413\u5f97\u53eb\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8239\u91cc\u7684\u4eba\u90fd\u8dd1\u4e86\u51fa\u6765\uff0c\u90a3\u5c0f\u5973\u5b69\u5c16\u58f0\u53eb\u7740\u59d0\u59d0\uff0c\u4e0d\u4e00\u4f1a\u4ece\u540e\u8231\u8d70\u51fa\u4e00\u4e2a\u5e74\u7eaa\u4ea6\u4e0d\u592a\u5927\u7684 \u5c11\u5973\uff0c\u4e91\u9b13\u9ad8\u633d\uff0c\u8c8c\u7f8e\u5982\u82b1\uff0c\u8eab\u6750\u751a\u662f\u6e05\u7626\uff0c\u8138\u4e0a\u4f3c\u6709\u6101\u5bb9\uff0c\u98a6\u7709\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u5440\uff1f\u201d \u3000\u3000\u90a3\u5c0f\u5973\u5b69\u6307\u7740\u6c34\u9762\u8bf4\uff1a\u201c\u6709\u4e00\u4e2a\u5c0f\u5b69\u6389\u4e0b\u53bb\u4e86\uff0c\u59d0\u59d0\u8d76\u5feb\u53eb\u4eba\u53bb\u6551\u4ed6\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u63a2\u9996\u7a97\u5916\uff0c\u770b\u89c1\u4e00\u4e2a\u5c0f\u5b69\u7684\u5934\u79bb\u5cb8\u6e10\u8fdc\uff0c\u614c\u5fd9\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u600e\u4e48\u641e\u7684\uff0c\u5feb\u70b9\u4e0b\u53bb \u6551\u4eba\u5440\u3002\u201d \u3000\u3000\u8239\u4e0a\u6709\u513f\u4e2a\u5377\u7740\u88e4\u817f\u7684\u7c97\u6c49\uff0c\u8df3\u4e0b\u4e86\u6c34\uff0c\u6240\u5e78\u5cb8\u8fd1\u6c34\u5c1a\u4e0d\u6df1\uff0c\u4e0d\u4e00\u4f1a\uff0c\u5c31\u5c06\u718a\u501c\u6551\u4e86\u4e0a \u6765\u3002\u90a3\u4f4d\u7c97\u6c49\u628a\u718a\u501c\u5012\u7740\u653e\u5728\u819d\u4e0a\uff0c\u5410\u51fa\u4e86\u8bb8\u591a\u6c34\uff0c\u4e91\u9b13\u5c11\u5973\u548c\u90a3\u5c0f\u5973\u5b69\u4e5f\u8d70\u4e86\u51fa\u6765\uff0c\u718a\u501c \u6b63\u81ea\u6162\u6162\u8f6c\u9192\uff0c\u6b64\u65f6\u8231\u5185\u8d70\u51fa\u4e00\u4e2a\u56db\u5341\u51e0\u8bb8\u7684\u5987\u4eba\uff0c\u4e00\u8d70\u51fa\u6765\u5c31\u671d\u90a3\u5c11\u5973\u8bf4\uff1a\u201c\u90a3\u4e48\u665a\u4e86\u8fd8 \u7ad9\u5728\u8fd9\u513f\uff0c\u4e5f\u4e0d\u591a\u7a7f\u4ef6\u8863\u670d\uff0c\u5c0f\u5fc3\u7740\u4e86\u51c9\u3002\u201d\u53c8\u8f6c\u5934\u770b\u4e86\u770b\u718a\u501c\uff0c\u671d\u90a3\u4e9b\u4e8c\u6c49\u8bf4\uff1a\u201c\u8fd9\u5c0f\u5b69 \u662f\u54ea\u91cc\u6765\u7684\uff0c\u5f04\u5f97\u8239\u4e0a\u90fd\u810f\u6b7b\u4e86\uff0c\u5feb\u628a\u4ed6\u9001\u8d70\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u542c\u7740\u5fae\u4e00\u98a6\u7709\uff0c\u671d\u5987\u4eba\u8bf4\uff1a\u201c\u963f\u5988\u600e\u4e48\u8fd9\u6837\uff0c\u8fd9\u5b69\u5b50\u51bb\u5f97\u6d51\u8eab\u53d1\u6296\uff0c\u600e\u4e48\u80fd\u591f\u9001 \u4ed6\u8d70\u5462\u3002\u201d\u8bed\u8a00\u8106\u4e3d\uff0c\u5982\u9ec4\u9a7e\u51fa\u8c37\u3002 \u3000\u3000\u90a3\u5987\u4eba\u5c1a\u672a\u7b54\u8bdd\uff0c\u718a\u501c\u7a81\u7136\u8df3\u4e86\u8d77\u6765\uff0c\u671d\u90a3\u5c11\u5973\u53ca\u5c0f\u5973\u5b69\u4e00\u8dea\uff0c\u54c0\u6c42\u7740\u8bf4\uff1a\u201c\u8fd9\u4f4d\u963f\u59e8 \u548c\u8fd9\u4f4d\u59d0\u59d0\u6551\u6551\u6211\uff0c\u4e0d\u8981\u8d76\u6211\u8d70\uff0c\u6211\u6ca1\u6709\u5bb6\u4e86\uff0c\u60c5\u613f\u66ff\u4f60\u4eec\u505a\u4e8b\uff0c\u505a\u4ec0\u4e48\u4e8b\u90fd\u884c\u3002\u201d\u8bf4\u7740\u8bf4 \u7740\uff0c\u773c\u6cea\u5c31\u6d41\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u90a3\u5c0f\u5973\u5b69\u770b\u4e86\uff0c\u4e0d\u7981\u62c9\u4e86\u62c9\u5979\u59d0\u59d0\u7684\u8863\u89d2\uff0c\u4f4e\u58f0\u8bf4\u7740\uff1a\u201c\u59d0\u59d0\uff0c\u4f60\u4e0d\u8981\u8d76\u4ed6\u8d70\u561b\uff0c\u77a7\u4ed6 \u6837\u5b50\u602a\u53ef\u601c\u7684\u3002\u201d \u3000\u3000\u5c11\u5973\u770b\u4e86\u718a\u501c\u4e00\u773c\uff0c\u53ea\u89c1\u4ed6\u867d\u662f\u4ece\u6c34\u91cc\u635e\u51fa\u7684\uff0c\u8863\u670d\u6dcb\u6f13\uff0c\u975e\u5e38\u72fc\u72c8\uff0c\u4f46\u5374\u751f\u5f97\u4fca\u7f8e\u5df2 \u6781\uff0c\u4e00\u70b9\u90fd\u6ca1\u6709\u72fc\u7410\u7684\u6837\u5b50\uff0c\u5fc3\u91cc\u4e5f\u5f88\u559c\u6b22\uff0c\u4fa7\u8138\u5bf9\u90a3\u5987\u4eba\u8bf4\uff1a\u201c\u8fd9\u5c0f\u5b69\u65e2\u662f\u65e0\u5bb6\u53ef\u5f52\uff0c\u6211 \u4eec\u5c31\u628a\u4ed6\u6536\u4e0b\u6765\u5427\uff0c\u4e5f\u597d\u66ff\u6211\u6253\u6253\u6742\u3002\u201d \u3000\u3000\u90a3\u5987\u4eba\u8bf4\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u6709\u4e2b\u5934\u4eec\u670d\u4f8d\u4f60\u8fd8\u4e0d\u591f\u5417\uff1f\u8fd9\u5c0f\u5b69\u6765\u5386\u4e0d\u660e\uff0c\u600e\u4e48\u80fd\u6536\u4e0b\u4ed6 \u5462\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u4e00\u7529\u624b\uff0c\u751f\u6c14\u9053\uff1a\u201c\u4e0d\u884c\u5c31\u4e0d\u884c\uff0c\u6211\u6c42\u4f60\u505a\u4e00\u70b9\u4e8b\u90fd\u4e0d\u884c\uff0c\u770b\u4e0b\u6b21\u4f60\u8981\u662f\u6c42\u6211\uff0c \u6211\u4e5f\u4e0d\u7b54\u5e94\u4f60\u3002\u201d \u3000\u3000\u90a3\u5987\u4eba\u8fde\u5fd9\u8d54\u7740\u7b11\u9053\uff1a\u201c\u884c\u884c\u884c\uff0c\u59d1\u5a18\u7684\u8bdd\u6211\u600e\u6562\u4e0d\u542c\u3002\u201d \u3000\u3000\u53c8\u5927\u58f0\u5bf9\u7740\u6b63\u7ad9\u5728\u65c1\u8fb9\u7684\u4e24\u4e2a\u4e2b\u5934\u8bf4\uff1a\u201c\u5feb\u628a\u8fd9\u5c0f\u5b69\u5e26\u5230\u540e\u9762\u53bb\uff0c\u627e\u4ef6\u8863\u670d\u66ff\u4ed6\u6362\u4e0a\uff0c \u542c\u5230\u4e86\u6ca1\u6709\u3002\u201d \u3000\u3000\u90a3\u5c0f\u5973\u5b69\u9ad8\u5174\u5f97\u76f4\u7b11\uff0c\u7275\u7740\u5c11\u5973\u7684\u8863\u89d2\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u59d0\u59d0\u771f\u597d\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u542c\u4e86\uff0c\u53f9\u4e86\u53e3\u6c14\uff0c\u4f3c\u6709\u65e0\u9650\u5fc3\u4e8b\uff0c\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u8fd8\u4e0d\u90fd\u662f\u4e3a\u4e86\u4f60\u3002\u201d \u3000\u3000\u90a3\u5c0f\u5973\u5b69\u542c\u4e86\uff0c\u773c\u5708\u4e00\u7ea2\uff0c\u6251\u8fdb\u5c11\u5973\u7684\u6000\u91cc\uff0c\u4e24\u4eba\u7d27\u7d27\u62e5\u62b1\u7740\uff0c\u7adf\u90fd\u6d41\u4e0b\u6cea\u6765\u3002 \u3000\u3000\u539f\u6765\u6b64\u4e8c\u4eba\u906d\u9047\u4e5f\u662f\u5f02\u5e38\u51c4\u60e8\uff0c\u4ed6\u4eec\u7684\u7236\u4eb2\u539f\u6765\u662f\u4e00\u4e2a\u901a\u5112\uff0c\u867d\u7136\u624d\u9ad8\u516b\u6597\uff0c\u4f46\u5374\u6c14\u8d28 \u6e05\u9ad8\uff0c\u4e0d\u613f\u5e94\u8bd5\uff0c\u4e3a\u5f02\u65cf\u4f5c\u5974\u624d\uff0c\u5728\u57ce\u90ca\u4e00\u4e2a\u540d\u53eb\u91d1\u5bb6\u5e84\u7684\u5c0f\u6751\u843d\uff0c\u5f00\u8bbe\u4e00\u5bb6\u8499\u9986\uff0c\u9760\u4e00\u4e9b \u5fae\u8584\u7684\u675f\u4fee\u6765\u8ba8\u751f\u6d3b\uff0c\u59bb\u5b50\u65e9\u6b7b\uff0c\u819d\u4e0b\u65e0\u5b50\u3001\u53ea\u6709\u4e24\u4e2a\u5584\u89e3\u4eba\u610f\u7684\u5973\u513f\uff0c\u751f\u6d3b\u81ea\u662f\u6e05\u82e6\uff0c\u4f46 \u5374\u4e5f\u5f88\u5b89\u9759\u3002 \u3000\u3000\u8fd9\u4f4d\u8001\u5148\u751f\u59d3\u6731\uff0c\u5b57\u9e3f\u5112\uff0c\u672c\u662f\u5927\u660e\u540e\u88d4\uff0c\u5927\u5973\u513f\u82e5\u5170\uff0c\u5c0f\u5973\u513f\u82e5\u99a8\uff0c\u4ed6\u56e0\u4e3a\u6ca1\u6709\u513f \u5b50\uff0c\u4ece\u5c0f\u5c31\u628a\u4e24\u4e2a\u5973\u513f\u5f53\u505a\u7537\u5b50\uff0c\u6559\u4ee5\u8bd7\u4e66\uff0c\u7b49\u5230\u82e5\u5170\u5341\u516d\u5c81\u90a3\u5e74\uff0c\u6731\u8001\u5148\u751f\u5ffd\u7136\u5f97\u4e86\u91cd \u75be\uff0c\u7adf\u544a\u4e0d\u6cbb\uff0c\u4e34\u6b7b\u65f6\u671b\u7740\u4e24\u4e2a\u60b2\u51c4\u6b32\u7edd\u7684\u5973\u513f\uff0c\u81ea\u662f\u96be\u4ee5\u7791\u76ee\u3002 \u3000\u3000\u6731\u5bb6\u672c\u5c31\u8d2b\u5bd2\u5982\u6d17\uff0c\u6731\u9e3f\u5112\u4e00\u6b7b\uff0c\u6839\u672c\u5c31\u65e0\u6cd5\u8c0b\u751f\uff0c\u6731\u82e5\u99a8\u624d\u4e03\u5c81\uff0c\u6bcf\u5929\u996d\u90fd\u4e0d\u80fd\u5403 \u9971\uff0c\u997f\u5f97\u76ae\u5305\u9aa8\u5934\uff0c\u6731\u82e5\u5170\u59d0\u59b9\u60c5\u6df1\uff0c\u770b\u7740\u96be\u53d7\u5df2\u6781\uff0c\u8fd9\u624d\u843d\u5165\u70df\u82b1\uff0c\u505a\u4e86\u79e6\u6dee\u6cb3\u7554\u7684\u4e00\u4e2a \u6b4c\u5993\u3002\u201c\u6731\u82e5\u5170\u4e3d\u8d28\u5929\u751f\uff0c\u518d\u52a0\u4e0a\u672c\u662f\u4e66\u9999\u4e16\u5bb6\uff0c\u8bd7\u8bcd\u4e66\u753b\u65e0\u4e00\u4e0d\u7cbe\uff0c\u4e0d\u5230\u4e00\u5e74\uff0c\u5373\u8273\u540d\u5927 \u566a\uff0c\u6210\u4e86\u79e6\u6dee\u7fa4\u82b1\u91cc\u7684\u9b41\u9996\u3002\u6731\u82e5\u5170\u4eba\u82e5\u5176\u540d\uff0c\u5e7d\u5982\u7a7a\u8c37\u5170\u82b1\uff0c\u5f97\u80fd\u7a0d\u4eb2\u82b3\u6cfd\u7684\uff0c\u53ef\u8bf4\u5c11\u4e4b \u53c8\u5c11\uff0c\u53ef\u662f\u4eba\u6027\u672c\u8d31\uff0c\u5979\u8d8a\u662f\u8fd9\u6837\uff0c\u90a3\u4e9b\u8d70\u9a6c\u7ae0\u53f0\u7684\u82b1\u82b1\u516c\u5b50\u8d8a\u662f\u8d8b\u4e4b\u82e5\u9a9b\u3002\u79e6\u6dee\u7b19\u6b4c\u4f59 \u7c89\uff0c\u672c\u662f\u7b75\u5f00\u4e0d\u591c\uff0c\u4f46\u6731\u82e5\u5170\u5374\u7acb\u4e0b\u89c4\u77e9\uff0c\u8fc7\u5b50\u591c\u5373\u4e0d\u518d\u7559\u5ba2\uff0c\u8239\u4e0a\u7684\u8001\u9e28\u628a\u5979\u5f53\u6447\u94b1\u6811\uff0c \u54ea\u80fd\u4e0d\u542c\u5979\u7684\u3001\u6240\u4ee5\u718a\u501c\u665a\u4e0a\u6765\u7684\u65f6\u5019\uff0c\u5df2\u662f\u66f2\u7ec8\u4eba\u6563\u4e86\u3002\u6731\u82e5\u5170\u547d\u8584\u5982\u7eb8\uff0c\u77e5\u9053\u718a\u501c\u4e5f\u662f \u65e0\u5bb6\u53ef\u5f52\u7684\u5b64\u513f\uff0c\u540c\u75c5\u76f8\u4f36\uff0c\u5bf9\u718a\u501c\u7231\u62a4\u5907\u81f3\uff0c\u6731\u82e5\u99a8\u5e74\u7eaa\u5c1a\u5e7c\uff0c\u4e00\u5411\u90fd\u662f\u505a\u522b\u4eba\u7684\u59b9\u59b9\uff0c \u73b0\u5728\u6709\u4e86\u4e2a\u6bd4\u5979\u8fd8\u5c0f\u7684\u718a\u501c\uff0c\u4e5f\u662f\u4e00\u5929\u5230\u665a\u5fd9\u4e1c\u5fd9\u897f\u5730\uff0c\u7167\u6599\u7740\u718a\u501c\uff0c\u718a\u501c\u52ab\u540e\u4f59\u751f\uff0c\u5f97\u6b64 \u5bb9\u8eab\uff0c\u5b9e\u4e0d\u557b\u5982\u767b\u5929\u5802\u3002\u718a\u501c\u8fd9\u534a\u5e74\u6765\u7ecf\u8fc7\u7684\u5fe7\u60a3\u5927\u591a\uff0c\u4eba\u5728\u82e6\u96be\u4e2d\u603b\u662f\u6613\u4e8e\u6210\u957f\uff0c\u4ed6\u4e5f\u53d8 \u5f97\u6709\u4e00\u4e9b\u4e5d\u5c81\u5927\u7684\u5b69\u5b50\u6240\u4e0d\u5e94\u6709\u7684\u4e16\u6545\uff0c\u800c\u4e14\u4ed6\u77e5\u9053\u81ea\u5df1\u8eab\u4e16\u6781\u79d8\uff0c\u6240\u4ee5\u5bf9\u4e8e\u4ed6\u89c6\u5982\u624b\u8db3\u7684 \u6731\u5bb6\u59d0\u59b9\uff0c\u4e5f\u662f\u7edd\u53e3\u4e0d\u63d0\uff0c\u53ea\u8bf4\u81ea\u5df1\u7236\u6bcd\u53cc\u4ea1\uff0c\u53c8\u662f\u65e0\u5bb6\u53ef\u5f52\u7f62\u4e86\u3002\u6731\u82e5\u5170\u767d\u5929\u6ca1\u4e8b\uff0c\u5c31\u6559 \u7740\u82e5\u99a8\u3001\u718a\u501c\u4e24\u4e2a\u5b69\u5b50\u5ff5\u4e66\uff0c\u718a\u501c\u751f\u957f\u738b\u5e9c\uff0c\u542f\u8499\u6781\u65e9\uff0c\u6587\u5b57\u5df2\u6709\u6839\u57fa\uff0c\u518d\u52a0\u4e0a\u806a\u660e\u7edd\u9876\u8fc7 \u76ee\u6210\u8bf5\uff0c\u5f80\u5f80\u82e5\u99a8\u5ff5\u4e86\u597d\u51e0\u904d\uff0c\u8fd8\u4e0d\u80fd\u8bb0\u5f97\u7684\u4e66\uff0c\u718a\u501c\u4e00\u5ff5\u5c31\u4f1a\uff0c\u82e5\u5170\u66f4\u662f\u559c\u6b22\u3002\u6709\u65f6\u591c\u6df1 \u68a6\u53e3\uff0c\u718a\u501c\u60f3\u5230\u81ea\u5df1\u7684\u8840\u6d77\u6df1\u4ec7\uff0c\u5c31\u5077\u5077\u5730\u53d6\u51fa\u6234\u68a6\u5c27\u7ed9\u4ed6\u7684\u518c\u5b50\u6d41\u6cea\uff0c\u4e8e\u662f\u767d\u5929\u4ed6\u66f4\u52a0\u523b \u82e6\u5ff5\u4e66\uff0c\u53ea\u56e0\u90a3\u518c\u5b50\u4e0a\u6240\u8f7d\u5b57\u53e5\u5747\u751a\u6df1\u5965\uff0c\u4ed6\u8981\u6709\u66f4\u591a\u7684\u77e5\u8bc6\uff0c\u65b9\u80fd\u4e86\u89e3\u3002\u665a\u4e0a\uff0c\u524d\u8231\u6709 \u5ba2\uff0c\u5ea6\u66f2\u884c\u4ee4\uff0c\u70ed\u95f9\u5df2\u6781\uff0c\u718a\u501c\u661f\u4e5f\u5e74\u5e7c\u7231\u95f9\uff0c\u4f46\u4ed6\u5374\u7edd\u4e0d\u5230\u524d\u8231\u5f20\u671b\uff0c\u4ed6\u77e5\u9053\u4ed6\u6240\u5904\u7684\u5730 \u4f4d\u662f\u4e0d\u5141\u8bb8\u4ed6\u4eab\u6709\u6b22\u4e50\u7684\uff0c\u53ea\u662f\u4e00\u4e2a\u4eba\u8eb2\u5728\u540e\u9762\u5ff5\u4e66\uff0c\u6709\u65f6\u82e5\u99a8\u4e5f\u6765\u966a\u7740\u4ed6\u3002\u82e5\u5170\u5728\u524d\u8231\u966a \u5b8c\u9152\u53e3\u6765\uff0c\u81ea\u5df1\u611f\u6000\u8eab\u4e16\uff0c\u603b\u662f\u51c4\u7136\u843d\u6cea\uff0c\u6e10\u6e10\u718a\u501c\u4e5f\u77e5\u9053\u4e86\u8fd9\u662f\u4f55\u7b49\u6240\u5728\uff0c\u4e0d\u7981\u4e5f\u5728\u5fc3\u91cc \u4e3a\u82e5\u5170\u96be\u53d7\uff0c\u53d1\u8a93\u7b49\u81ea\u5df1\u957f\u5927\u6210\u4eba\uff0c\u4e00\u5b9a\u8981\u628a\u5979\u4eec\u4ece\u706b\u5751\u4e2d\u6551\u51fa\u6765\u3002\u8fd9\u6837\u8fc7\u4e86\u4e00\u5e74\uff0c\u718a\u501c\u975e \u4f46\u5c06\u300a\u5e7c\u5b66\u743c\u6797\u300b\u7b49\u4e66\u80cc\u5f97\u6eda\u74dc\u70c2\u719f\uff0c\u5c31\u7b97\u662f\u56db\u4e66\u4e94\u7ecf\uff0c\u4e5f\u80fd\u6717\u6717\u4e0a\u53e3\uff0c\u8fd9\u624d\u6361\u4e86\u4e00\u4e2a\u6708\u660e \u4e4b\u591c\uff0c\u5077\u5077\u6e9c\u5230\u5cb8\u4e0a\u8352\u51c9\u4e4b\u5904\uff0c\u5c06\u90a3\u4e24\u672c\u518c\u5b50\u653e\u5728\u524d\u9762\uff0c\u606d\u606d\u656c\u656c\u5730\u62dc\u4e86\u56db\u62dc\uff0c\u9ed8\u7977\u7236\u53d4\u5728 \u5929\u4e4b\u7075\uff0c\u52a9\u4ed6\u6210\u529f\u3002\u6b64\u65f6\u6708\u8272\u5982\u94f6\uff0c\u79e6\u6dee\u70df\u706b\uff0c\u6d69\u6e3a\u4e00\u7247\uff0c\u718a\u501c\u6781\u4ed4\u7ec6\u5730\u7ffb\u5f00\u90a3\u4e24\u672c\u518c\u5b50\uff0c \u90a3\u662f\u7528\u9ec4\u7ae3\u8ba2\u6210\u7684\u5c01\u9762\uff0c\u91cc\u9762\u7684\u767d\u7ee2\u4e0a\uff0c\u6574\u9f50\u5730\u5199\u7740\u5b57\u548c\u4e00\u4e9b\u56fe\u5f0f\uff0c\u718a\u501c\u7ffb\u5f00\u7b2c\u4e00\u672c\uff0c\u6b63\u662f \u661f\u6708\u53cc\u5251\u4ed7\u4ee5\u6210\u540d\u7684\u201d\u82cd\u7a79\u5341\u4e09\u5f0f\u201c\uff0c\u4f46\u201d\u82cd\u7a79\u5341\u4e09\u5f0f\u201c\u5185\u5c3d\u662f\u4e9b\u817e\u98de\u523a\u51fb\u7684\u7cbe\u5fae\u5251\u5f0f\uff0c\u718a \u501c\u65e2\u65e0\u5e08\u5085\u6307\u5bfc\uff0c\u53c8\u65e0\u6df1\u539a\u7684\u6b66\u529f\u6839\u57fa\uff0c\u5982\u4f55\u80fd\u591f\u5b66\u5f97\uff0c\u4ed6\u7ffb\u9605\u4e86\u4e00\u4f1a\uff0c\u4e0d\u7981\u5931\u671b\u5f97\u54ed\u4e86\uff0c \u4e8e\u662f\u4ed6\u518d\u7ffb\u5f00\u7b2c\u4e8c\u672c\u518c\u5b50\uff0c\u90a3\u662f\u5b66\u6b66\u4e4b\u4eba\u68a6\u5bd0\u4ee5\u6c42\u7684\u5185\u5bb6\u521d\u6b65\u529f\u592b\uff0c\u4e5f\u6b63\u662f\u661f\u6708\u53cc\u5251\u672a\u80fd\u767b \u5cf0\u9020\u6781\u7684\u5929\u96f7\u884c\u529f\u3002\u718a\u501c\u5c31\u7167\u7740\u6307\u793a\u7ec3\u4e86\u8d77\u6765\u3002\u6b64\u540e\uff0c\u6bcf\u65e5\u5929\u5c1a\u672a\u4eae\uff0c\u718a\u501c\u5c31\u5077\u5077\u722c\u4e86\u8d77 \u6765\uff0c\u72ec\u81ea\u8dd1\u5230\u9759\u50fb\u7684\u6cb3\u8fb9\uff0c\u8fce\u7740\u671d\u6c14\u5b66\u4e60\u5410\u7eb3\u4e4b\u672f\uff0c\u521d\u5b66\u65f6\uff0c\u4ed6\u81ea\u6709\u4e0d\u5c11\u56f0\u96be\uff0c\u4f46\u4ed6\u5374\u90fd\u4ee5 \u7edd\u5927\u6bc5\u529b\u53bb\u514b\u670d\u4e86\uff0c\u6709\u65f6\u9047\u7740\u96be\u89e3\u4e4b\u5904\uff0c\u7adf\u7ec8\u65e5\u538c\u538c\uff0c\u5076\u5f97\u4e00\u89e3\uff0c\u5374\u53c8\u96c0\u8dc3\u4e0d\u5df2\u3002\u4e24\u5e74\u7684\u82e6 \u7ec3\uff0c\u4ed6\u89c9\u5f97\u81ea\u5df1\u7684\u5468\u8eab\u808c\u9aa8\uff0c\u5df2\u80fd\u968f\u7740\u547c\u5438\u81ea\u7531\u6536\u7f29\uff0c\u800c\u4e14\u6c14\u529b\u500d\u589e\uff0c\u8eab\u4f53\u50cf\u662f\u8574\u85cf\u7740\u5343\u767e \u65a4\u529b\u91cf\uff0c\u53ea\u662f\u65e0\u6cd5\u53d1\u6cc4\u800c\u5df2\uff0c\u4ed6\u4e0d\u77e5\u9053\u4ed6\u8fd9\u4e9b\u65e5\u5b50\u7684\u82e6\u7ec3\uff0c\u5df2\u5230\u4e86\u5185\u529f\u4e2d\u6781\u6df1\u5965\u7684\u5883\u754c\uff0c\u6b63 \u662f\u6b66\u6797\u4e2d\u4eba\u7ec8\u751f\u60f3\u5f80\u7684\u201d\u4e09\u82b1\u805a\u9876\u201c\uff0c\u201d\u4e94\u6c14\u671d\u5143\u201c\uff0c\u6240\u5dee\u53ea\u662f\u201d\u7763\u201c\u3001\u201d\u4efb\u201c\u4e24\u8109\u5c1a\u672a\u80fd \u6253\u901a\uff0c\u5426\u5219\u5c31\u7b97\u6b66\u6797\u9ad8\u624b\uff0c\u4e5f\u90fd\u4e0d\u80fd\u4f24\u4ed6\u3002\u4e24\u5e74\u591a\u6765\uff0c\u82e5\u99a8\u4e5f\u5341\u4f59\u51e0\u4e86\uff0c\u51fa\u843d\u5f97\u81ea\u662f\u6e05\u4e3d\u5f02 \u5e38\uff0c\u718a\u501c\u672c\u662f\u548c\u5979\u4eec\u59d0\u59b9\u7761\u5728\u4e00\u8d77\uff0c\u73b0\u5728\u4e00\u6765\u56e0\u4e3a\u4eba\u90fd\u5927\u4e86\u4e9b\uff0c\u4e8c\u6765\u65e5\u4e3a\u718a\u501c\u665a\u4e0a\u8981\u7ec3\u529f\uff0c \u548c\u5979\u59d0\u59b9\u7761\u5728\u4e00\u8d77\u751a\u662f\u4e0d\u4fbf\uff0c\u5c31\u642c\u5230\u540e\u8231\uff0c\u4e00\u95f4\u89d2\u843d\u4e0a\u7684\u5c0f\u623f\u53bb\u7761\uff0c\u66f4\u662f\u5c3d\u591c\u4e0d\u606f\u5730\u7ec3\u7740\u8c03 \u606f\u4e4b\u672f\u3002\u4e00\u5929\u6e05\u6668\uff0c\u718a\u501c\u53c8\u6e9c\u4e86\u51fa\u6765\uff0c\u5230\u6cb3\u8fb9\u53bb\u7ec3\u529f\uff0c\u4ed6\u5fc3\u91cc\u6b63\u5728\u60f3\u7740\u201d\u5929\u96f7\u884c\u529f\u201c\u91cc\u7684\u7cbe \u5fae\u4e4b\u5904\uff0c\u6ca1\u6709\u6ce8\u610f\u5230\u524d\u9762\u7684\u8239\u677f\uff0c\u4e00\u811a\u8e0f\u7a7a\uff0c\u5168\u8eab\u5c06\u8981\u843d\u6c34\uff0c\u4ed6\u672c\u80fd\u5730\u5f80\u4e0a\u4e00\u63d0\u6c14\uff0c\u54ea\u77e5\u5374 \u51fa\u4e4e\u610f\u5916\u5730\u5168\u8eab\u4f3c\u6709\u5927\u529b\u5438\u5f15\uff0c\u5411\u4e0a\u62d4\u9ad8\u4e86\u6570\u5c3a\uff0c\u4ed6\u5fc3\u4e2d\u4e00\u559c\uff0c\u771f\u6c14\u4e00\u6563\uff0c\u5374\u53c8\u6251\u901a\u6389\u8fdb\u6c34 \u91cc\uff0c\u6240\u5e78\u79e6\u6dee\u6cb3\u4e50\u6237\u4e0d\u5230\u65e5\u4e0a\u4e09\u7aff\uff0c\u4e0d\u4f1a\u8d77\u5e8a\uff0c\u4e5f\u6ca1\u6709\u6ce8\u610f\u5230\u4ed6\u3002\u4f46\u662f\u4ed6\u5374\u77e5\u9053\u81ea\u5df1\u5df2\u80fd\u7ec3 \u4e60\u201d\u82cd\u7a79\u5341\u4e09\u5f0f\u201c\u4e86\u3002\u5c81\u6708\u500f\u7136\uff0c\u77ac\u53c8\u4e09\u5e74\uff0c\u718a\u501c\u5df2\u662f\u5341\u56db\u5c81\u4e86\uff0c\u4ed6\u524a\u7af9\u4e3a\u5251\uff0c\u5b66\u5251\u5df2\u6709\u4e09 \u5e74\uff0c\u201d\u82cd\u7a79\u5341\u4e09\u5f0f\u201c\u5df2\u80fd\u81ea\u7531\u8fd0\u7528\uff0c\u201d\u5929\u96f7\u884c\u529f\u201c\uff0c\u5374\u672a\u89c1\u8fdb\u6b65\uff0c\u4ed6\u9664\u4e86\u89c9\u5f97\u81ea\u5df1\u8fd0\u6c14\u65f6\uff0c \u4f53\u5185\u96f7\u54cd\u8f83\u524d\u7a0d\u5927\u4e4b\u5916\uff0c\u4f46\u6bcf\u6bcf\u7ec3\u5230\u7d27\u8981\u5173\u5934\uff0c\u4f53\u5185\u771f\u6c14\u603b\u4e0d\u80fd\u878d\u800c\u4e3a\u4e00\uff0c\u5fc3\u91cc\u61ca\u607c\u5df2\u6781\u3002 \u82e5\u99a8\u4e5f\u5df2\u5341\u4e94\u4e86\uff0c\u6c5f\u5357\u6625\u65e9\uff0c\u5341\u4e94\u5c81\u7684\u5973\u5b69\u5df2\u7ecf\u957f\u5f97\u50cf\u4e2a\u5927\u4eba\u4e86\uff0c\u6e10\u6e10\u5730\uff0c\u5979\u5bf9\u718a\u501c\u5f62\u8ff9\u4e0a \u53d8\u5f97\u751f\u758f\u8d77\u6765\uff0c\u53ef\u662f\u5728\u5185\u5fc3\u7684\u60c5\u611f\u4e0a\uff0c\u5374\u5bf9\u4ed6\u66f4\u662f\u5173\u6000\u3002\u8fd9\u5929\u6e05\u6668\uff0c\u718a\u501c\u53c8\u5230\u4e86\u5cb8\u8fb9\u7ec3\u529f\uff0c \u5f53\u4ed6\u6b63\u5728\u8fd0\u6c14\uff0c\u5c06\u4f53\u5185\u771f\u6c14\u901a\u5230\u5251\u5c16\u4e0a\u53d1\u51fa\u65f6\uff0c\u5ffd\u89c9\u80a9\u4e0a\u6709\u4eba\u4e00\u62cd\uff0c\u4ed6\u4e00\u60ca\u4e4b\u4e0b\uff0c\u672c\u80fd\u5730\u53cd \u624b\u4e00\u5251\uff0c\u5251\u52bf\u4e0a\u6311\uff0c\u867d\u662f\u7af9\u5251\uff0c\u4f46\u5728\u718a\u501c\u624b\u91cc\u8fd0\u7528\uff0c\u5df2\u53ef\u65a9\u654c\u4f24\u4eba\u3002\u718a\u501c\u5251\u65b9\u523a\u51fa\uff0c\u5ffd\u89c9\u53f3 \u52a9\u4e00\u8f6f\uff0c\u6d51\u8eab\u771f\u529b\u4ff1\u5931\uff0c\u5343\u91cc\u7684\u5251\u4e5f\u540c\u65f6\u5931\u53bb\uff0c\u7adf\u4f3c\u4ed6\u5c06\u5251\u4ea4\u7ed9\u522b\u4eba\u4e00\u6837\uff0c\u4ed6\u5c1a\u672a\u6709\u4efb\u4f55\u52a8 \u4f5c\uff0c\u773c\u524d\u4e00\u82b1\uff0c\u591a\u4e86\u4e00\u6761\u4eba\u5f71\uff0c\u51b2\u7740\u4ed6\u51b7\u51b7\u4ed6\u8bf4\uff1a\u201c\u4f60\u7684\u5251\u6cd5\u662f\u8c01\u6559\u7ed9\u4f60\u7684\uff1f\u201d\u3002 \u3000\u3000\u718a\u501c\u5927\u60ca\u4e4b\u4e0b\uff0c\u5b9a\u775b\u4e00\u770b\uff0c\u53ea\u89c1\u773c\u524d\u7ad9\u7740\u4e00\u4e2a\u4f3c\u4eba\u4f3c\u9b3c\u7684\u602a\u7269\uff0c\u901a\u4f53\u7eaf\u767d\uff0c\u975e\u4f46\u8863\u5c65\u662f \u767d\u7684\uff0c\u5c31\u8fde\u5934\u53d1\u3001\u7709\u6bdb\u4e5f\u5168\u662f\u767d\u7684\uff0c\u8138\u8272\u66f4\u662f\u82cd\u767d\u5f97\u6ca1\u6709\u4e00\u4e1d\u8840\u8272\uff0c\u50cf\u662f\u521a\u4ece\u575f\u5893\u91cc\u8dd1\u51fa\u6765 \u7684\u3002 \u3000\u3000\u718a\u501c\u5f3a\u715e\u4e5f\u662f\u4e2a\u5341\u56db\u5c81\u7684\u5b69\u5b50\uff0c\u89c1\u4e86\u8fd9\u79cd\u5f62\u540c\u9b3c\u9b45\u7684\u89d2\u8272\uff0c\u5413\u5f97\u8f6c\u8eab\u5c31\u8dd1\uff0c\u54ea\u77e5\u4ed6\u4eba\u521a \u7eb5\u51fa\uff0c\u6d51\u8eab\u53c8\u662f\u4e00\u8f6f\uff0c\u53c8\u4eff\u4f5b\u662f\u6709\u4ec0\u4e48\u4e1c\u897f\u5728\u4ed6\u8eab\u4e0a\u70b9\u4e86\u4e00\u4e0b\uff0c\u53ed\u5730\u4e00\u58f0\uff0c\u843d\u5230\u5730\u4e0a\uff0c\u8dcc\u5f97 \u81c0\u90e8\u9690\u9690\u4f5c\u75db\u3002 \u3000\u3000\u90a3\u4eba\u6839\u672c\u672a\u89c1\u8d70\u52a8\uff0c\u4eba\u5374\u79fb\u4e86\u8fc7\u6765\uff0c\u8fd8\u662f\u51b7\u51b7\u4ed6\u8bf4\uff1a\u201c\u4f60\u7684\u5251\u6cd5\u662f\u8c01\u6559\u7ed9\u4f60\u7684\uff1f\u201d \u3000\u3000\u90a3\u4eba\u95ee\u8fc7\u4e4b\u540e\uff0c\u5373\u52a8\u4e5f\u4e0d\u52a8\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u8138\u4e0a\u6ca1\u6709\u4e00\u4e1d\u8868\u60c5\uff0c\u718a\u501c\u4f0f\u5728\u5730\u4e0a\u8c03\u606f\u4e86\u4e00 \u4f1a\uff0c\u731b\u5730\u8170\u3001\u817f\u3001\u65f6\u4e00\u9f50\u7528\u529b\uff0c\u4eba\u50cf\u5f39\u7c27\u822c\u5f39\u4e86\u8d77\u6765\uff0c\u5f80\u524d\u4e00\u7a9c\uff0c\u5c31\u662f\u4e09\u3001\u56db\u4e08\uff0c\u4ed6\u6ee1\u4ee5\u4e3a \u8fd9\u6b21\u5b9a\u53ef\u9003\u51fa\u4e86\uff0c\u54ea\u77e5\u4ed6\u811a\u5c16\u521a\u4e00\u6cbe\u5730\uff0c\u90a3\u602a\u4eba\u5374\u53c8\u6b63\u6b63\u5730\u7ad9\u5728\u4ed6\u9762\u524d\uff0c\u4ed6\u6beb\u4e0d\u8003\u8651\uff0c\u53cc\u81c2 \u4e00\u632f\uff0c\u4eba\u5f80\u4e0a\u76f4\u62d4\u4e0a\u53bb\uff0c\u54ea\u77e5\u602a\u4eba\u4e5f\u540c\u65f6\u968f\u5730\u62d4\u8d77\uff0c\u5b8c\u5168\u540c\u6837\u5feb\u6162\uff0c\u4ed6\u62d4\u5230\u54ea\u91cc\uff0c\u90a3\u602a\u5165\u4e5f \u62d4\u5230\u54ea\u91cc\uff0c\u53ea\u8981\u718a\u501c\u5f80\u524d\u770b\uff0c\u90a3\u602a\u4eba\u51b7\u800c\u82cd\u767d\u7684\u9762\u5b54\u603b\u662f\u8d6b\u7136\u5728\u4ed6\u773c\u524d\u3002 \u3000\u3000\u718a\u501c\u4e0d\u7981\u6025\u4e86\uff0c\u8fde\u4eba\u5e26\u5934\uff0c\u5411\u90a3\u602a\u5165\u649e\u53bb\uff0c\u90a3\u602a\u4eba\u5374\u4e0d\u8eb2\u95ea\uff0c\u773c\u770b\u5373\u53ef\u649e\u4e0a\uff0c\u54ea\u77e5\u90a3\u602a \u4eba\u5374\u968f\u7740\u4ed6\u7684\u6765\u52bf\u5411\u540e\u98d8\u4e86\u5f00\u53bb\uff0c\u718a\u501c\u529b\u91cf\u7528\u5b8c\uff0c\u4ed6\u4e5f\u773c\u7740\u505c\u6b62\uff0c\u4ecd\u662f\u4fdd\u6301\u7740\u521a\u521a\u7684\u8ddd\u79bb\u3002 \u3000\u3000\u718a\u501c\u4e1c\u5954\u897f\u7a9c\uff0c\u5374\u59cb\u7ec8\u9003\u4e0d\u8fc7\u90a3\u602a\u4eba\uff0c\u4ed6\u60f3\u5230\u81ea\u5df1\u82e6\u7ec3\u4e94\u5e74\uff0c\u7b2c\u4e00\u6b21\u78b0\u5230\u7684\u4eba\uff0c\u53cd\u4f46\u6253 \u4e0d\u5230\u4ed6\uff0c\u7ade\u8fde\u9003\u90fd\u9003\u4e0d\u51fa\u53bb\uff0c\u8fd9\u6837\u600e\u80fd\u8c08\u5230\u62a5\u4ec7\u96ea\u6068\uff0c\u4e0d\u7981\u5750\u5230\u5730\u4e0a\u54ed\u4e86\u8d77\u6765\u3002 \u3000\u3000\u90a3\u602a\u4eba\u672c\u662f\u575a\u51b0\u822c\u7684\u9762\u5b54\uff0c\u770b\u89c1\u718a\u501c\u54ed\u4e86\u8d77\u6765\uff0c\u5374\u5f00\u59cb\u8d77\u4e86\u53d8\u5316\uff0c\u63a5\u7740\u6d51\u8eab\u626d\u52a8\uff0c\u50cf\u4e0d \u5b89\u5df2\u6781\uff0c\u5374\u6781\u529b\u5fcd\u8010\u7740\u7684\u6837\u5b50\u3002 \u3000\u3000\u718a\u501c\u54ed\u4e86\u4e00\u4f1a\uff0c\u60f3\u8d77\u6234\u68a6\u5c27\u4e34\u6b7b\u524d\u5bf9\u4ed6\u8bb2\u7684\u8bdd\uff0c\u54ed\u5f97\u66f4\u4f24\u5fc3\uff0c\u90a3\u602a\u4eba\u4f3c\u4e4e\u5fcd\u8010\u4e0d\u4f4f\uff0c\u4e5f \u5750\u5230\u5730\u4e0a\uff0c\u8ddf\u7740\u718a\u501c\u54ed\u4e86\u8d77\u6765\uff0c\u800c\u4e14\u54ed\u5f97\u6bd4\u718a\u501c\u8fd8\u8981\u4f24\u5fc3\u3002 \u3000\u3000\u539f\u6765\u90a3\u602a\u4eba\u672c\u662f\u5b64\u513f\uff0c\u51fa\u751f\u540e\u5c31\u88ab\u629b\u5728\u5c45\u5eb8\u5173\u5916\u7684\u516b\u8fbe\u5cad\u4e0a\uff0c\u5374\u88ab\u4ea7\u5728\u6df1\u5c71\u91cc\u7684\u4e00\u79cd\u5f02 \u79cd\u7334\u5b50\u6361\u4e86\u53bb\uff0c\u5582\u4ee5\u7334\u5976\uff0c\u90a3\u602a\u4eba\u957f\u5927\u540e\u8ddf\u7334\u5b50\u4e00\u6837\uff0c\u6ee1\u5c71\u4e71\u8dd1\uff0c\u904d\u4f53\u957f\u7740\u7c97\u6bdb\uff0c\u5431\u5431\u55b3\u55b3 \u4ed6\u8bf4\u7740\u7334\u8bed\uff0c\u6709\u4e00\u5929\u88ab\u4e00\u4e2a\u6e38\u5c71\u7684\u5251\u5ba2\u53d1\u73b0\uff0c\u628a\u4ed6\u5e26\u4e86\u56de\u53bb\uff0c\u7528\u836f\u6c34\u628a\u4ed6\u904d\u4f53\u7684\u6bdb\u76ae\u53bb\u4e86\u5e72 \u51c0\uff0c\u6388\u4ee5\u6b66\u6280\uff0c\u800c\u4e14\u8fd8\u66ff\u4ed6\u8d77\u4e86\u4e2a\u540d\u5b57\u53eb\u505a\u4faf\u751f\u3002 \u3000\u3000\u90a3\u5251\u5ba2\u5728\u516b\u8fbe\u5cad\u4e00\u803d\u5341\u5e74\uff0c\u4f20\u5f97\u4faf\u751f\u4e00\u8eab\u672c\u9886\uff0c\u4faf\u751f\u672c\u5c31\u751f\u6709\u5f02\u7980\uff0c\u5185\u5916\u529f\u592b\u5b66\u8d77\u6765\u6bd4 \u522b\u4eba\u4e8b\u534a\u529f\u500d\uff0c\u51fa\u5e08\u540e\u5373\u5e38\u5230\u5173\u5185\u6e38\u4fa0\uff0c\u4e0d\u8bba\u9ed1\u767d\u4e24\u9053\uff0c\u53ea\u8981\u60f9\u4ed6\u4e0d\u987a\u773c\uff0c\u4ed6\u5c31\u628a\u4eba\u5bb6\u5f04 \u6b7b\uff0c\u800c\u4e14\u884c\u8e2a\u98d8\u5ffd\uff0c\u8f7b\u529f\u9ad8\u5f97\u51fa\u5947\uff0c\u65e0\u4eba\u80fd\u5948\u5f97\u4ed6\u4f55\u3002 \u3000\u3000\u540e\u6765\u4ed6\u5e74\u7eaa\u5927\u4e86\uff0c\u6e10\u6e10\u61d2\u5f97\u8d70\u52a8\uff0c\u5c31\u5a36\u4e86\u4e2a\u592a\u592a\u5728\u516b\u8fbe\u5cad\u9690\u5c45\u8d77\u6765\uff0c\u661f\u6708\u53cc\u5251\u7684\u5e08\u5085\u90a3 \u65f6\u5728\u9752\u9f99\u6865\u9690\u5c45\uff0c\u4e24\u4eba\u90fd\u662f\u6b66\u6280\u9ad8\u5f3a\u6027\u60c5\u5b64\u50fb\u7684\u8001\u5934\uff0c\u4e00\u8c08\u4e4b\u4e0b\uff0c\u7adf\u662f\u975e\u5e38\u6295\u7f18\u3002 \u3000\u3000\u4faf\u751f\u5185\u5916\u529f\u4ff1\u90fd\u5df2\u81fb\u7edd\u9876\uff0c\u51e0\u4e4e\u5df2\u662f\u4e0d\u574f\u4e4b\u8eab\uff0c\u53ef\u662f\u5374\u6700\u6015\u542c\u89c1\u4eba\u54ed\uff0c\u53ea\u8981\u6709\u4eba\u4e00\u54ed\uff0c \u4ed6\u4e5f\u4f1a\u8ddf\u7740\u54ed\u4e86\u8d77\u6765\uff0c\u800c\u4e14\u54ed\u7684\u65f6\u5019\u6b66\u529f\u4ff1\u5931\uff0c\u548c\u5e38\u4eba\u5b8c\u5168\u4e00\u6837\uff0c\u53ea\u662f\u6c5f\u6e56\u4eba\u58eb\u4ece\u672a\u6709\u5728\u4ed6 \u9762\u524d\u54ed\u8fc7\u7684\uff0c\u6545\u4e5f\u65e0\u4eba\u77e5\u9053\u4ed6\u7684\u77ed\u5904\u3002 \u3000\u3000\u53ef\u662f\u4faf\u751f\u665a\u5e74\u5a36\u7684\u8fd9\u4f4d\u592a\u5927\u5374\u6700\u597d\u54ed\uff0c\u5979\u4e00\u54ed\u4faf\u751f\u4e5f\u8ddf\u7740\u54ed\u8d77\u6765\uff0c\u8981\u662f\u522b\u4eba\u4e0d\u505c\uff0c\u4ed6\u4e5f \u4e0d\u80fd\u505c\u6b62\uff0c\u540e\u6765\u4ed6\u592a\u592a\u53d1\u73b0\u8fd9\u4e2a\u6bdb\u75c5\uff0c\u6ca1\u4e8b\u5c31\u62ff\u54ed\u6765\u8981\u80c1\u4ed6\uff0c\u5f04\u5f97\u4ed6\u5b9e\u5728\u4e0d\u80fd\u5fcd\u8010\uff0c\u7adf\u9003\u4e86 \u51fa\u53bb\u3002 \u3000\u3000\u4ed6\u8dd1\u5230\u661f\u6708\u53cc\u5251\u7684\u5e08\u7236\u90a3\u91cc\uff0c\u4f4f\u4e86\u597d\u51e0\u4e2a\u6708\uff0c\u60f3\u5230\u5173\u5185\u4e00\u6e38\uff0c\u661f\u6708\u53cc\u5251\u7684\u5e08\u7236\u5c31\u6258\u4ed6\u7167 \u987e\u5f92\u5f1f\uff0c\u8fd9\u65f6\u521a\u597d\u661f\u6708\u53cc\u5251\u5e26\u7740\u718a\u501c\u53ca\u683c\u5c14\u6c81\u540c\u9003\uff0c\u4ed6\u5c31\u8ddf\u5728\u540e\u9762\u4fdd\u62a4\uff0c\u540e\u6765\u5728\u5357\u4eac\u57ce\u90ca\u9646 \u98de\u767d\u53e3\u53d1\u72c2\u8a00\uff0c\u4ed6\u4e00\u6012\u4e4b\u4e0b\uff0c\u51b7\u51b7\u4ed6\u8bf4\u53e5\u201c\u597d\u5927\u7684\u53e3\u6c14\u201d\u5c31\u4e0d\u7ba1\u8d70\u4e86\uff0c\u4ed6\u5374\u4e0d\u77e5\u9053\u661f\u6708\u53cc\u5251 \u90fd\u906d\u4e86\u6bd2\u624b\u3002 \u3000\u3000\u4ed6\u4e00\u4e2a\u4eba\u5404\u5904\u6e38\u73a9\u4e86\u597d\u51e0\u5e74\uff0c\u518d\u53e3\u5230\u6c5f\u5357\uff0c\u5374\u542c\u5f97\u4eba\u8bf4\u661f\u6708\u53cc\u5251\u5df2\u7ecf\u6b7b\u4e86\uff0c\u600e\u4e48\u6b7b\u7684\u5374 \u4eba\u8a00\u4eba\u6b8a\uff0c\u4ed6\u8fd9\u624d\u4e00\u6025\uff0c\u89c9\u5f97\u81ea\u5df1\u5bf9\u4e0d\u8d77\u661f\u6708\u53cc\u5251\u7684\u5e08\u5085\u3002 \u3000\u3000\u4ed6\u4e5f\u77e5\u9053\u661f\u6708\u53cc\u5251\u662f\u5e26\u7740\u4e24\u4e2a\u5b69\u5b50\u540c\u8d70\u7684\uff0c\u73b0\u5728\u661f\u6708\u53cc\u5251\u5df2\u6b7b\uff0c\u4ed6\u5c31\u60f3\u627e\u7740\u4e24\u4e2a\u5b69\u5b50\uff0c \u6765\u8865\u507f\u81ea\u5df1\u7684\u6b49\u759a\uff0c\u54ea\u77e5\u627e\u4e86\u8bb8\u4e45\uff0c\u4e5f\u65e0\u6cd5\u627e\u7740\u3002 \u3000\u3000\u8fd9\u5929\u6e05\u6668\u4ed6\u6b63\u5230\u83ab\u6101\u6e56\u53bb\u770b\u96fe\uff0c\u5076\u7136\u8d70\u5230\u79e6\u6dee\u6cb3\u8fb9\u770b\u89c1\u6709\u4eba\u6b63\u7528\u201c\u82cd\u7a79\u5341\u4e09\u5f0f\u201d\u91cc\u7684\u529f \u592b\u98de\u6e21\u79e6\u6dee\uff0c\u201c\u82cd\u7a79\u5341\u4e09\u5f0f\u201d\u6b66\u6797\u4e2d\u4f1a\u7684\u4eba\u53ef\u8bf4\u7edd\u65e0\u4ec5\u6709\uff0c\u4ed6\u624d\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u8ddf\u4e86\u8fc7\u53bb\uff0c \u4ed6\u770b\u522e\u718a\u501c\u662f\u4e2a\u5341\u51e0\u5c81\u7684\u5b69\u5b50\uff0c\u5fc3\u60f3\u4e5f\u8bb8\u5c31\u662f\u4ed6\u8981\u6211\u7684\u4eba\uff0c\u8fd9\u624d\u8dd1\u8fc7\u53bb\u95ee\uff0c\u4ed6\u4e2a\u6027\u5947\u50fb\uff0c\u559c \u6012\u65e0\u5e38\uff0c\u770b\u89c1\u718a\u501c\u60f3\u8d70\uff0c\u5c31\u903c\u7740\u718a\u501c\uff0c\u54ea\u77e5\u9053\u718a\u501c\u5374\u54ed\u4e86\u8d77\u6765\u3002 \u3000\u3000\u718a\u501c\u54ed\u4e86\u4e00\u4f1a\uff0c\u53d1\u73b0\u4faf\u751f\u4e5f\u5728\u54ed\uff0c\u800c\u4e14\u54ed\u7684\u6837\u5b50\u5f88\u6ed1\u7a3d\uff0c\u4e0d\u89c9\u6251\u54e7\u7b11\u4e86\u4e00\u58f0\uff0c\u4faf\u751f\u542c\u4ed6 \u7b11\uff0c\u4e5f\u4e0d\u54ed\u4e86\uff0c\u718a\u501c\u89c9\u5f97\u5f88\u597d\u73a9\uff0c\u5c31\u95ee\u9053\uff1a\u201c\u5582\uff0c\u600e\u4e48\u6211\u54ed\u4f60\u4e5f\u54ed\uff0c\u6211\u4e0d\u54ed\uff0c\u4f60\u4e5f\u4e0d\u54ed \u4e86\u3002\u201d \u3000\u3000\u4faf\u751f\u4e24\u773c\u4e00\u77aa\uff0c\u51b2\u718a\u501c\u8bf4\uff1a\u201c\u600e\u4e48\u4f60\u54ed\u5f97\u6211\u5c31\u54ed\u4e0d\u5f97\u5440\u3002\u201d \u3000\u3000\u718a\u501c\u89c1\u4ed6\u767d\u53d1\u767d\u9aef\uff0c\u5df2\u662f\u4e2a\u8001\u5934\u5b50\uff0c\u4f46\u8bf4\u8d77\u8bdd\u6765\uff0c\u5374\u50cf\u4e2a\u5c0f\u5b69\u4e00\u6837\uff0c\u4e0d\u7531\u54c8\u54c8\u5927\u7b11\u8d77 \u6765\u3002 \u3000\u3000\u4faf\u751f\u770b\u5230\u4ed6\u7b11\uff0c\u5c31\u7ad9\u4e86\u8d77\u6765\uff0c\u62cd\u62cd\u767d\u8863\u670d\u4e0a\u7684\u5c18\u58eb\uff0c\u60f3\u4e86\u4e00\u4f1a\uff0c\u95ee\u9053\uff1a\u201c\u661f\u6708\u53cc\u5251\u662f\u4f60 \u4ec0\u4e48\u4eba\uff1f\u201d\u718a\u501c\u7b11\u58f0\u987f\u4f4f\uff0c\u60ca\u5f02\u5730\u770b\u4e86\u4faf\u751f\u4e00\u773c\uff0c\u6ca1\u6709\u7b54\u8bdd\uff0c\u4faf\u751f\u770b\u4e86\u770b\u4ed6\uff0c\u89c9\u5f97\u4ed6\u5e74\u7eaa\u867d \u5e7c\uff0c\u4f46\u662f\u4e24\u773c\u795e\u5149\u9971\u6ee1\uff0c\u80a4\u5982\u575a\u7389\uff0c\u5185\u529f\u5df2\u6709\u6839\u575a\uff0c\u9042\u8d77\u4e86\u601c\u624d\u4e4b\u5ff5\uff0c\u4faf\u751f\u98d8\u5ffd\u6c5f\u6e56\uff0c\u8fa3\u624b \u6bd2\u5fc3\uff0c\u4eba\u79f0\u6bd2\u5fc3\u795e\u9b54\uff0c\u6570\u5341\u5e74\u6765\uff0c\u4ece\u672a\u5bf9\u4eba\u751f\u51fa\u5982\u6b64\u597d\u611f\uff0c\u4e5f\u786e\u662f\u5f02\u6570\u3002\u505c\u4e86\u4e00\u4f1a\uff0c\u4faf\u751f\u628a \u8bed\u58f0\u653e\u5f97\u548c\u7f13\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u6015\uff0c\u53ea\u7ba1\u8bf4\u51fa\u6765\uff0c\u6211\u4e0d\u4f1a\u5bb3\u4f60\u7684\u3002\u201d \u3000\u3000\u718a\u501c\u89c1\u4ed6\u8138\u4e0a\u5df2\u518d\u6ca1\u6709\u51b7\u9177\u4e4b\u8272\uff0c\u7a81\u7136\u5bf9\u4ed6\u4e5f\u8d77\u4e86\u4eb2\u5207\u4e4b\u611f\uff0c\u8fd9\u4e94\u5e74\u6765\u9664\u4e86\u6731\u5bb6\u59d0\u59b9\u4e4b \u5916\uff0c\u522b\u4eba\u5bf9\u4ed6\u90fd\u662f\u51b7\u773c\u76f8\u5f85\uff0c\u4faf\u751f\u867d\u662f\u884c\u8e2a\u8be1\u5f02\uff0c\u4ee4\u4ed6\u5bb3\u6015\uff0c\u4f46\u662f\u73b0\u5728\u4ed6\u8bed\u6c14\u5374\u5728\u4e25\u5389\u4e2d\u9732 \u51fa\u5173\u5207\uff0c\u718a\u501c\u60f3\u5230\u4ed6\u6700\u656c\u7231\u7684\u53d4\u53d4\u4e5f\u662f\u8fd9\u79cd\u6837\u5b50\uff0c\u4e0d\u7981\u53c8\u54ed\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4faf\u751f\u89c1\u718a\u501c\u4e00\u54ed\uff0c\u6025\u5f97\u53ea\u662f\u987f\u811a\uff0c\u4f46\u4ed6\u8840\u6db2\u91cc\u6709\u4e86\u516b\u8fbe\u5cad\u5f02\u79cd\u733f\u7334\u7684\u5929\u6027\uff0c\u53ea\u8981\u770b\u89c1\u4eba \u54ed\uff0c\u81ea\u5df1\u4e5f\u4e0d\u80fd\u63a7\u5236\u5730\u54ed\u4e86\u8d77\u6765\u3002 \u3000\u3000\u718a\u501c\u672c\u662f\u806a\u660e\u7edd\u9876\u4e4b\u4eba\uff0c\u89c1\u4ed6\u5982\u6b64\uff0c\u5fc3\u91cc\u660e\u767d\u4e86\u51e0\u5206\uff0c\u7a81\u7136\u798f\u4e8c\u5fc3\u7075\uff0c\u6b62\u4f4f\u4e86\u54ed\uff0c\u8bf4 \u9053\uff1a\u201c\u8fd9\u4f4d\u4f2f\u4f2f\uff0c\u6211\u4e0d\u54ed\u4e86\uff0c\u53ea\u56e0\u4e3a\u6211\u60f3\u8d77\u6b7b\u53bb\u7684\u53d4\u53d4\uff0c\u6240\u4ee5\u624d\u5fcd\u4e0d\u4f4f\u54ed\u4e86\u8d77\u6765\u3002\u201d \u3000\u3000\u4faf\u751f\u9053\uff1a\u201c\u6234\u68a6\u5c27\u662f\u4f60\u7684\u5e08\u5085\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u4faf\u751f\u9053\uff1a\u201c\u4f60\u628a\u6234\u68a6\u5c27\u6559\u4f60\u7684\u5929\u96f7\u884c\u529f\u7ec3\u4e00\u904d\u7ed9\u6211\u770b\u3002\u201d \u3000\u3000\u4faf\u751f\u770b\u7740\u4ed6\u7ec3\uff0c\u8138\u4e0a\u7adf\u6709\u559c\u8272\uff0c\u6b64\u65f6\u7a81\u7136\u8dd1\u4e86\u8fc7\u53bb\uff0c\u4e0d\u77e5\u600e\u7684\u5c06\u624b\u4e00\u4f38\uff0c\u5c06\u718a\u501c\u5012\u63d0\u4e86 \u8d77\u6765\uff0c\u5728\u4ed6\u8eab\u4e0a\u4e00\u9635\u4e71\u62cd\uff0c\u718a\u501c\u53ea\u89c9\u6d51\u8eab\u8212\u670d\uff0c\u4e1d\u6beb\u6ca1\u6709\u75db\u82e6\u4e4b\u611f\u3002 \u3000\u3000\u4ed6\u62cd\u6253\u4e86\u7ea6\u6709\u76cf\u8336\u65f6\u5019\uff0c\u624d\u5c06\u718a\u501c\u653e\u4e86\u4e0b\u53bb\uff0c\u4e24\u624b\u6273\u4f4f\u718a\u501c\u7684\u80a9\u8180\uff0c\u53eb\u718a\u501c\u5f20\u5f00\u5634\u6765\uff0c \u4ed6\u4e5f\u628a\u5634\u4e00\u5f20\uff0c\u5bf9\u7740\u718a\u501c\u5439\u51fa\u4e00\u80a1\u6c14\u6765\uff0c\u53ea\u89c1\u6709\u4e00\u6761\u5b9b\u5982\u5b9e\u8d28\u7684\u6c14\u4f53\uff0c\u6295\u5165\u718a\u501c\u7684\u5634\u4e2d\u3002 \u3000\u3000\u90a3\u6c14\u4f53\u4e00\u5165\u718a\u501c\u53e3\u4e2d\uff0c\u718a\u501c\u53ea\u89c9\u6d51\u8eab\u4e00\u51b7\uff0c\u6709\u4e00\u80a1\u5bd2\u6c14\u5728\u4ed6\u4f53\u5185\u8fd0\u8f6c\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u4faf\u751f \u989d\u4e0a\u5df2\u7136\u89c1\u6c57\uff0c\u718a\u501c\u89c9\u5f97\u90a3\u80a1\u5bd2\u6c14\u6e10\u6e10\u53d8\u5f97\u706b\u70ed\uff0c\u70eb\u5f97\u4ed6\u6d51\u8eab\u53c8\u9178\u53c8\u75db\uff0c\u4faf\u751f\u7684\u4e24\u53cc\u624b\u50cf\u94c1 \u7b8d\u4f3c\u7684\u6273\u4f4f\uff0c\u4ed6\u52a8\u4e5f\u52a8\u4e0d\u4e86\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u4e00\u4f1a\uff0c\u4faf\u751f\u5c06\u624b\u4e00\u677e\uff0c\u5374\u6251\u5730\u5750\u5230\u5730\u4e0a\uff0c\u7d2f\u5f97\u6c14\u5598\u4e0d\u5df2\u3002 \u3000\u3000\u718a\u501c\u56db\u80a2\u4e00\u677e\uff0c\u6d51\u8eab\u89c9\u5f97\u4ece\u672a\u6709\u8fc7\u7684\u8212\u6cf0\uff0c\u770b\u89c1\u4faf\u751f\u5df2\u5728\u5bf9\u9762\u7791\u76ee\u8c03\u606f\uff0c\u4fbf\u4e5f\u5750\u4e86\u4e0b \u6765\uff0c\u8bd5\u7740\u7a0d\u4e00\u8fd0\u6c14\uff0c\u771f\u6c14\u5373\u704c\u8fbe\u56db\u80a2\uff0c\u878d\u800c\u4e3a\u4e00\uff0c\u4e0d\u7981\u5927\u559c\u3002 \u3000\u3000\u6b64\u65f6\u5929\u5df2\u5927\u4eae\uff0c\u9633\u5149\u5347\u8d77\uff0c\u7167\u5f97\u79e6\u6dee\u6cb3\u6c34\uff0c\u7cbc\u7cbc\u91d1\u5149\uff0c\u4faf\u751f\u7ad9\u8d77\u6765\uff0c\u5bf9\u718a\u501c\u8bf4\uff1a\u201c\u6211\u5df2 \u4e3a\u4f60\u6253\u901a\u2018\u7763\u4efb\u2019\u4e24\u8109\uff0c\u6b64\u540e\u4f60\u7ec3\u529f\u5df2\u65e0\u963b\u788d\uff0c\u7b49\u5230\u4f60\u7ec3\u5f97\u4f53\u5185\u8f7b\u96f7\u4e0d\u518d\u54cd\u65f6\uff0c\u53ef\u5230\u5c45\u5eb8\u5173 \u6765\uff0c\u4f60\u4e5f\u4e0d\u5fc5\u627e\u6211\uff0c\u6211\u81ea\u4f1a\u627e\u4f60\u7684\u3002\u201d\u8bf4\u5b8c\u8eab\u5f62\u5e76\u672a\u89c1\u52a8\uff0c\u4eba\u5df2\u4e0d\u89c1\u3002 \u3000\u3000\u718a\u501c\u7ad9\u4e86\u8d77\u6765\uff0c\u5fc3\u91cc\u9ad8\u5174\u5f97\u65e0\u6cd5\u5f62\u5bb9\uff0c\u81ea\u601d\u9053\uff1a\u201c\u8fd9\u4eba\u600e\u5730\u5982\u6b64\u5947\u602a\uff0c\u50cf\u662f\u548c\u6211\u6234\u53d4\u53d4 \u662f\u670b\u53cb\uff0c\u6211\u8d77\u5148\u8fd8\u4ee5\u4e3a\u4ed6\u662f\u9b3c\u5462\u3002\u201d \u3000\u3000\u8f6c\u5ff5\u53c8\u60f3\u9053\uff1a\u201c\u5440\uff01\u6211\u8fde\u4ed6\u59d3\u540d\u90fd\u4e0d\u77e5\u9053\uff0c\u8fde\u8c22\u4e5f\u6ca1\u6709\u8c22\u8fc7\u4ed6\uff0c\u771f\u662f\u8be5\u6b7b\uff0c\u4e0b\u6b21\u89c1\u5230 \u4ed6\u2026\u2026\u201d\u4ed6\u6b63\u60f3\u5230\u8fd9\u91cc\uff0c\u5ffd\u7136\u767d\u5f71\u4e00\u6643\uff0c\u4faf\u751f\u53c8\u7ad9\u5230\u4ed6\u7684\u9762\u524d\uff0c\u718a\u501c\u4e0d\u7981\u5927\u559c\uff0c\u6b63\u60f3\u8dea\u4e0b\uff0c \u4faf\u751f\u628a\u624b\u4e00\u62e6\uff0c\u4ece\u80cc\u540e\u64a4\u51fa\u4e00\u628a\u5f62\u5f0f\u5947\u53e4\u7684\u957f\u5251\uff0c\u4f38\u624b\u62bd\u4e86\u51fa\u6765\uff0c\u53ea\u89c9\u5bd2\u6c14\u6c81\u4eba\uff0c\u4ed6\u628a\u5251\u5957 \u5f80\u718a\u501c\u624b\u4e0a\u4e00\u9012\uff0c\u53e3\u91cc\u8bf4\u9053\uff1a\u201c\u8bb0\u7740\u3002\u201d\u5c31\u865a\u7a7a\u523a\u4e86\u51e0\u4e2a\u5251\u5f0f\uff0c\u50cf\u662f\u6beb\u65e0\u8fde\u8d2f\uff0c\u5374\u53c8\u5251\u5251\u5947 \u8be1\uff0c\u718a\u501c\u90fd\u8bb0\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4faf\u751f\u628a\u5251\u4e00\u6536\uff0c\u4e5f\u5f80\u718a\u501c\u90a3\u4e00\u9012\uff0c\u8bf4\u9053\uff1a\u201c\u6b64\u5251\u6211\u5df2\u7528\u5b83\u4e0d\u7740\uff0c\u4f60\u53ef\u62ff\u53bb\uff0c\u53ea\u662f\u6b64\u5251\u5728 \u6c5f\u6e56\u4e0a\u592a\u624e\u773c\uff0c\u8f7b\u6613\u4e0d\u53ef\u663e\u9732\u3002\u201d\u4ed6\u60f3\u4e86\u4e00\u60f3\uff0c\u53c8\u8bf4\u9053\uff1a\u201c\u6b64\u540e\u4f60\u5982\u627e\u7740\u4f60\u7684\u59b9\u59b9\uff0c\u53ef\u628a\u6211 \u521a\u521a\u6559\u4f60\u7684\u5251\u62db\u6559\u7ed9\u5979\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u4f60\u90fd\u4e0d\u80fd\u6559\u7ed9\u4efb\u4f55\u4eba\uff0c\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u718a\u501c\u8d76\u7d27\u8dea\u4e86\u4e0b\u53bb\uff0c\u4f4e\u5934\u8bf4\u9053\uff1a\u201c\u5f1f\u5b50\u77e5\u9053\u3002\u201d\u7b49\u5230\u4ed6\u518d\u62ac\u5934\uff0c\u4faf\u751f\u5df2\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u718a\u501c\u624b\u91cc\u62ff\u7740\u90a3\u628a\u53e4\u5251\uff0c\u559c\u7231\u5df2\u6781\uff0c\u4ed6\u4ed4\u7ec6\u770b\u4e86\u8bb8\u4e45\uff0c\u53ea\u662f\u5251\u628a\u4e0a\u7528\u4e1d\u7f15\u6210\u201c\u501a\u5929\u201d\u4e24 \u5b57\uff0c\u968f\u624b\u4e00\u6325\uff0c\u5251\u5c3e\u7adf\u6709\u5bd2\u8292\uff0c\u77e5\u662f\u4e00\u628a\u5b9d\u5251\uff0c\u5c31\u7ad9\u5728\u5f53\u5730\uff0c\u5c06\u4faf\u751f\u6559\u4ed6\u7684\u5251\u62db\uff0c\u6309\u7740\u65b9 \u4f4d\uff0c\u7ec3\u4e86\u8d77\u6765\uff0c\u603b\u662f\u89c9\u5f97\u62db\u62db\u4eff\u4f5b\u4e0d\u80fd\u8fde\u8d2f\uff0c\u8fd0\u7528\u8d77\u6765\u7f13\u6162\u5df2\u6781\uff0c\u4f46\u4ed6\u77e5\u9053\uff0c\u4faf\u751f\u6b66\u529f\u6df1\u4e0d \u53ef\u6d4b\uff0c\u6559\u4ed6\u7684\u5251\u62db\uff0c\u5fc5\u4e5f\u662f\u6b66\u6797\u7edd\u5b66\uff0c\u6240\u4ee5\u7262\u7262\u8bb0\u5728\u5fc3\u91cc\u3002 \u3000\u3000\u718a\u501c\u9759\u6084\u6084\u5730\u56de\u5230\u8239\u4e0a\uff0c\u8239\u4e0a\u4eba\u5c1a\u9ad8\u5367\uff0c\u4ed6\u56de\u5230\u4ed6\u90a3\u95f4\u4ec5\u53ef\u5bb9\u819d\u7684\u5c0f\u623f\u8231\uff0c\u5c06\u5251\u6536\u4e86\u8d77 \u6765\uff0c\u624d\u51fa\u53bb\u6f31\u6d17\uff0c\u4ed6\u60f3\u5230\u4eca\u5929\u7684\u5947\u9047\uff0c\u5fc3\u91cc\u5c31\u9ad8\u5174\uff0c\u4ed6\u60f3\uff1a\u201c\u8981\u662f\u6234\u53d4\u53d4\u4ed6\u4eec\u8fd8\u5728\uff0c\u770b\u89c1\u6211 \u8fd9\u6837\u5b50\uff0c\u4e5f\u4e00\u5b9a\u4f1a\u4e3a\u6211\u9ad8\u5174\uff0c\u4eca\u5929\u90a3\u4f4d\u4f2f\u4f2f\u8bf4\u6211\u8fd8\u6709\u4e2a\u59b9\u59b9\uff0c\u6211\u771f\u8be5\u6b7b\uff0c\u8fd9\u4e48\u591a\u5e74\u6765\u6211\u7adf\u628a \u5979\u5fd8\u4e86\u3002\u73b0\u5728\u4e0d\u77e5\u5979\u600e\u4e48\u6837\u4e86\uff0c\u6211\u771f\u5947\u602a\uff0c\u4e3a\u4ec0\u4e48\u4ee5\u524d\u7adf\u4ece\u672a\u60f3\u8d77\u8fc7\u5979\u5462\uff0c\u5440\uff01\u6211\u8fd8\u8bb0\u5f97\u5979 \u90a3\u4e48\u5c0f\uff0c\u6574\u5929\u53ea\u4f1a\u54ed\uff0c\u73b0\u5728\u5979\u8be5\u4e5f\u957f\u5927\u4e86\u4e9b\u5427\uff0c\u6211\u771f\u5e0c\u671b\u4ee5\u540e\u80fd\u627e\u7740\u5979\uff0c\u628a\u6211\u5168\u90e8\u4f1a\u7684\u6b66\u529f \u90fd\u4ea4\u7ed9\u5979\uff0c\u8ba9\u5979\u4e5f\u53ef\u4ee5\u8ddf\u6211\u4e00\u8d77\u53bb\u62a5\u4ec7\u3002\u201d \u3000\u3000\u4ed6\u60f3\u7740\u60f3\u7740\uff0c\u8138\u4e0a\u9732\u51fa\u4e86\u7b11\u5bb9\uff0c\u6b64\u65f6\u5ffd\u7136\u82e5\u99a8\u4e5f\u8dd1\u4e86\u8fc7\u6765\uff0c\u770b\u89c1\u718a\u501c\u5c31\u5c06\u811a\u6b65\u4e00\u7f13\uff0c\u4f4e \u4f4e\u4ed6\u8bf4\uff1a\u201c\u4f60\u597d\u65e9\u5440\uff01\u201d \u3000\u3000\u718a\u501c\u770b\u5230\u5979\u6765\u4e86\uff0c\u5c31\u8f6c\u5934\u8dd1\u5f00\uff0c\u5634\u91cc\u8bf4\u9053\uff1a\u201c\u5c0f\u59d0\u59d0\u65e9\u3002\u201d \u3000\u3000\u8f6c\u77ac\u53c8\u662f\u4e24\u5e74\uff0c\u718a\u501c\u65e9\u5c06\u201c\u5929\u96f7\u884c\u529f\u201d\u7ec3\u81f3\u65e0\u58f0\u5883\u754c\uff0c\u201c\u82cd\u7a79\u5341\u4e09\u5f0f\u201d\u4ed6\u66f4\u7ec3\u5f97\u719f\u4e4b\u53c8 \u719f\uff0c\u53ea\u662f\u4faf\u751f\u6559\u7ed9\u4ed6\u7684\u602a\u5f02\u5251\u62db\uff0c\u4ed6\u5c1a\u672a\u80fd\u5b8c\u5168\u9886\u609f\uff0c\u4ed6\u672c\u65e9\u60f3\u8d70\u4e86\uff0c\u4f46\u5f53\u4ed6\u770b\u5230\u6731\u5bb6\u59d0\u59b9 \u65f6\uff0c\u4ed6\u4eff\u4f5b\u89c9\u5f97\u6709\u4e00\u6761\u65e0\u5f62\u7684\u7ebf\u5728\u7cfb\u7740\u4ed6\uff0c\u4f7f\u4ed6\u4e0d\u80fd\u79bb\u53bb\u3002 \u3000\u3000\u7b49\u5230\u718a\u501c\u5341\u516d\u5c81\u90a3\u5e74\uff0c\u4ed6\u957f\u5f97\u5b8c\u5168\u5df2\u50cf\u4e2a\u5927\u4eba\u4e86\uff0c\u806a\u660e\u4eba\u672c\u5c31\u591a\u534a\u65e9\u719f\uff0c\u4f55\u51b5\u4ed6\u81ea\u5e7c\u7ec3 \u6b66\uff0c\u8eab\u6750\u53c8\u9ad8\uff0c\u8138\u4e0a\u867d\u4ecd\u6709\u7ae5\u7a1a\u4e4b\u6c14\uff0c\u4f46\u5df2\u65e0\u6cd5\u518d\u5728\u79e6\u6dee\u6cb3\u7684\u82b1\u822b\u4e0a\u5446\u4e0b\u53bb\uff0c\u4ed6\u60f3\u4e86\u8bb8\u4e45\uff0c \u672c\u60f3\u5c31\u6b64\u5077\u5077\u6e9c\u8d70\uff0c\u514d\u5f97\u96be\u53d7\uff0c\u4f46\u60f3\u5230\u82e5\u5170\u4e03\u5e74\u6765\u7684\u6069\u60c5\uff0c\u5b9e\u662f\u4e0d\u5fcd\u3002 \u3000\u3000\u7ec8\u4e8e\u5728\u4e00\u5929\u591c\u91cc\uff0c\u8239\u4e0a\u7684\u4eba\u90fd\u7761\u4e86\uff0c\u4ed6\u4fcf\u6084\u5730\u8dd1\u8fdb\u6731\u5bb6\u59d0\u59b9\u4f4f\u7684\u90a3\u95f4\u8231\u623f\u5c06\u82e5\u5170\u53eb\u5230\u8239 \u8237\u65c1\u3002 \u3000\u3000\u591c\u5df2\u5f88\u6df1\uff0c\u6cb3\u8fb9\u5bd2\u610f\u751a\u91cd\uff0c\u82e5\u5170\u4e0d\u77e5\u6709\u4ec0\u4e48\u4e8b\uff0c\u4fbf\u8ddf\u7740\u718a\u501c\u8d70\u4e86\u51fa\u6765\uff0c\u95ee\u9053\uff1a\u201c\u5f1f\u5f1f\uff0c \u4f60\u6709\u4ec0\u4e48\u4e8b\u5440\uff1f\u201d \u3000\u3000\u718a\u501c\u5446\u5446\u5730\u671b\u7740\u5979\uff0c\u53ea\u89c1\u4e5f\u6ee1\u8138\u4ff1\u662f\u5173\u5207\u4e4b\u5bb9\uff0c\u8fd9\u4e03\u5e74\u6765\u5979\u7ec8\u65e5\u5fe7\u90c1\uff0c\u66f4\u662f\u6e05\u7626\u53ef\u601c\uff0c \u800c\u4e14\u6708\u79fb\u4eba\u6362\u5728\u82b8\u82b8\u91d1\u7c89\u4e2d\uff0c\u5979\u4e5f\u6ca1\u6709\u4ee5\u524d\u90a3\u4e48\u7ea2\u4e86\uff0c\u718a\u501c\u60f3\u5230\u5c31\u8981\u79bb\u5f00\u5979\uff0c\u5fc3\u91cc\u4e00\u9178\uff0c\u773c \u89d2\u6d41\u4e0b\u6cea\u6765\u3002 \u3000\u3000\u82e5\u5170\u89c1\u718a\u501c\u54ed\u4e86\uff0c\u5c31\u8dd1\u5230\u718a\u501c\u8ddf\u524d\uff0c\u8fd9\u65f6\u718a\u501c\u5df2\u6bd4\u5979\u9ad8\u4e86\u5f88\u591a\uff0c\u5979\u62ac\u5934\u671b\u7740\u718a\u501c\u9762\u5b54\uff0c \u8f7b\u8f7b\u4f38\u624b\u66ff\u718a\u501c\u64e6\u4e86\u64e6\u773c\u6cea\uff0c\u5173\u5207\u4ed6\u8bf4\uff1a\u201c\u5f1f\u5f1f\uff0c\u4f60\u54ed\u4ec0\u4e48\uff0c\u662f\u4e0d\u662f\u53c8\u53d7\u4e86\u8c01\u7684\u59d4\u5c48\u3002\u201d \u3000\u3000\u718a\u501c\u66f4\u662f\u96be\u53d7\uff0c\u56de\u8fc7\u5934\u53bb\uff0c\u53ea\u89c1\u79e6\u6dee\u6cb3\u6c34\uff0c\u5e73\u4f0f\u5982\u955c\uff0c\u5012\u6620\u7740\u5929\u4e0a\u7684\u70b9\u70b9\u661f\u5149\uff0c\u5fae\u98ce\u5439 \u6765\uff0c\u4eff\u4f5b\u7f6e\u8eab\u5e7f\u5bd2\u6df1\u5904\u3002 \u3000\u3000\u82e5\u5170\u53ea\u89c9\u5f97\u5bd2\u610f\u6e10\u91cd\uff0c\u8f7b\u8f7b\u5730\u9760\u8fd1\u718a\u501c\uff0c\u5979\u7b2c\u4e00\u6b21\u611f\u89c9\u5230\u4ed6\u5df2\u4e0d\u518d\u662f\u4e2a\u5b69\u5b50\u4e86\u3002 \u3000\u3000\u718a\u501c\u4f4e\u4e0b\u5934\u6765\uff0c\u832b\u7136\u8bf4\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u6211\u8981\u8d70\u4e86\u3002\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u773c\u6cea\u7c41\u7c41\u843d\u4e0b\u3002 \u3000\u3000\u82e5\u5170\u542c\u4e86\u4e00\u60ca\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u8981\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u718a\u501c\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u6211\u8981\u79bb\u5f00\u4f60\u4eec\uff0c\u5230\u5f88\u8fdc\u5f88\u8fdc\u7684\u5730\u65b9\uff0c\u56e0\u4e3a\u90a3\u91cc\u6709\u5f88\u591a\u4e8b\u60c5\u7b49\u7740\u6211\u505a\uff0c \u4f46\u662f\u6211\u4e00\u5b9a\u4f1a\u5f88\u5feb\u56de\u6765\u7684\uff0c\u6211\u4e00\u5b9a\u8981\u5c06\u59d0\u59d0\u63a5\u51fa\u53bb\u3002\u201d \u3000\u3000\u82e5\u5170\u5e7d\u5e7d\u4ed6\u8bf4\uff1a\u201c\u6211\u65e9\u5c31\u77e5\u9053\u4f60\u8981\u8d70\u7684\uff0c\u4f46\u4f60\u4e3a\u4ec0\u4e48\u8d70\u5f97\u90a3\u4e48\u5feb\uff0c\u53cd\u6b63\u59d0\u59d0\u4e5f\u6d3b\u4e0d\u4e86\u591a \u5c11\u5e74\u4e86\uff0c\u4f60\u96be\u9053\u4e0d\u80fd\u518d\u7b49\u4e00\u7b49\u5417\uff1f\u201d\u8bf4\u7740\u8bf4\u7740\uff0c\u5df2\u662f\u6cea\u5982\u96e8\u4e0b\u3002 \u3000\u3000\u718a\u501c\u7a81\u7136\u4e00\u628a\u5c06\u82e5\u5170\u62b1\u4f4f\uff0c\u54ed\u7740\u8bf4\uff1a\u201c\u59d0\u59d0\uff0c\u6211\u771f\u4e0d\u60f3\u79bb\u5f00\u4f60\uff0c\u53ea\u662f\u6211\u5b9e\u5728\u6709\u96be\u8a00\u4e4b \u9690\uff0c\u6709\u8bb8\u591a\u4e8b\u6211\u90fd\u8981\u53bb\u628a\u5b83\u505a\u597d\uff0c\u4f46\u662f\uff0c\u59d0\u59d0\uff0c\u6211\u53d1\u8a93\uff0c\u6211\u4e00\u5b9a\u4f1a\u56de\u6765\u7684\uff0c\u6211\u4f1a\u4e00\u76f4\u966a\u7740\u59d0 \u59d0\uff0c\u8ba9\u59d0\u59d0\u597d\u597d\u4eab\u53d7\u51e0\u5e74\uff0c\u4e0d\u8981\u518d\u5728\u8fd9\u79cd\u5730\u65b9\u803d\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u82e5\u5170\u54ed\u5f97\u5df2\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u505c\u4e86\u4e00\u4f1a\uff0c\u5979\u6b62\u4f4f\u4e86\u54ed\u63a8\u5f00\u718a\u501c\uff0c\u4f4e\u4f4e\u4ed6\u8bf4\uff1a\u201c\u4f60\u4ec0\u4e48\u65f6\u5019\u8d70 \u5440\uff1f\u201d \u3000\u3000\u718a\u501c\u53c8\u4f4e\u4e0b\u5934\u53bb\uff0c\u8bf4\uff1a\u201c\u6211\u8ddf\u59d0\u59d0\u8bf4\u8fc7\uff0c\u9a6c\u4e0a\u5c31\u8981\u8d70\uff0c\u82e5\u99a8\u59d0\u59d0\u90a3\u91cc\uff0c\u4f60\u4ee3\u6211\u8bf4\u4e00\u58f0\uff0c \u6211\u4e0d\u518d\u8ddf\u5979\u544a\u8f9e\u4e86\u3002\u201d \u3000\u3000\u82e5\u5170\u60f3\u5230\u4e03\u5e74\u76f8\u4f9d\u4e3a\u547d\u7684\u4eba\uff0c\u9a6c\u4e0a\u5c31\u8981\u8d70\u4e86\uff0c\u5fcd\u4e0d\u4f4f\u53c8\u54ed\u4e86\u8d77\u6765\uff0c\u8bf4\uff1a\u201c\u4f60\u96be\u9053\u4e0d\u80fd\u591a \u5446\u51e0\u5929\u5417\uff1f\u8ba9\u59d0\u59d0\u518d\u591a\u770b\u4f60\u51e0\u5929\u3002\u201d \u3000\u3000\u718a\u501c\u72e0\u7740\u5fc3\u6447\u4e86\u6447\u5934\uff0c\u8bf4\uff1a\u201c\u4e0d\uff0c\u6211\u9a6c\u4e0a\u5c31\u8d70\u4e86\uff0c\u591a\u5446\u51e0\u5929\uff0c\u6211\u5fc3\u91cc\u66f4\u662f\u96be\u53d7\uff0c\u59d0\u59d0\u5feb \u56de\u623f\u53bb\u5427\uff01\u5c0f\u5fc3\u7740\u51c9\u4e86\u3002\u201d \u3000\u3000\u82e5\u5170\u7a81\u5730\u4e00\u8f6c\u8eab\uff0c\u54ed\u7740\u8dd1\u4e86\u8fdb\u53bb\uff0c\u718a\u501c\u671b\u7740\u5979\u7684\u80cc\u5f71\u6d88\u5931\uff0c\u89c9\u5f97\u50cf\u662f\u5931\u53bb\u4e86\u4ec0\u4e48\uff0c\u843d\u5bde \u5730\u8d70\u53e3\u623f\uff0c\u6536\u62fe\u4e86\u51e0\u4ef6\u5e38\u6362\u7684\u8863\u670d\uff0c\u5c06\u5b9d\u5251\u4ed4\u7ec6\u5730\u7528\u5e03\u5305\u597d\uff0c\u659c\u80cc\u5728\u8eab\u540e\uff0c\u7559\u610f\u5730\u770b\u7740\u4ed6\u90a3 \u5c0f\u8231\uff0c\u8fd9\u5e73\u65e5\u4ee4\u4ed6\u96be\u4ee5\u5fcd\u53d7\u7684\u5730\u65b9\uff0c\u5982\u4eca\u4ed6\u5374\u89c9\u5f97\u65e0\u9650\u6e29\u6696\u3002 \u3000\u3000\u4ed6\u5446\u5446\u5730\u7ad9\u5728\u5e8a\u524d\uff0c\u623f\u95e8\u8f7b\u8f7b\u5730\u88ab\u63a8\u5f00\u4e86\uff0c\u82e5\u99a8\u6d41\u7740\u6cea\u8d70\u4e86\u8fdb\u6765\uff0c\u624b\u91cc\u62ff\u7740\u4e00\u4e2a\u624b\u7ee2\u5305 \u7684\u5c0f\u5305\uff0c\u770b\u89c1\u718a\u501c\u51fa\u795e\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u5f3a\u5fcd\u7740\u6cea\uff0c\u8d70\u5230\u718a\u501c\u7684\u8eab\u65c1\uff0c\u5c06\u624b\u91cc\u62ff\u7740\u7684\u5c0f\u5305\u653e\u5230\u5e8a \u4e0a\uff0c\u5782\u6cea\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u59d0\u59d0\u548c\u6211\u7684\u4e00\u70b9\u9996\u9970\uff0c\u8fd8\u6709\u4e00\u70b9\u513f\u94f6\u5b50\uff0c\u4f60\u62ff\u7740\u5427\uff0c\u8def\u4e0a\u603b\u8981\u7528 \u7684\u3002\u201d\u718a\u501c\u8f6c\u8138\u611f\u6fc0\u5730\u770b\u7740\u5979\uff0c\u53ea\u89c1\u5979\u957f\u957f\u7684\u776b\u6bdb\u4e0a\u6302\u6ee1\u4e86\u6cea\u73e0\uff0c\u5fc3\u91cc\u7a81\u7136\u6709\u4e00\u80a1\u8bf4\u4e0d\u51fa\u7684 \u6ecb\u5473\uff0c\u5f20\u53e3\u60f3\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u4e0d\u77e5\u8be5\u600e\u4e48\u8bf4\uff0c\u4e24\u4eba\u6c89\u9ed8\u4e86\u4e00\u4f1a\uff0c\u82e5\u99a8\u62ac\u773c\u51c4\u5a49\u5730\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u773c \u5149\u4e2d\u5145\u6ee1\u4e86\u60b2\u54c0\u7684\u60c5\u610f\uff0c\u6162\u6162\u8f6c\u8eab\u8d70\u4e86\u4e24\u6b65\u3002 \u3000\u3000\u718a\u501c\u538b\u96c6\u5728\u5fc3\u4e2d\u7684\u60c5\u611f\uff0c\u6b64\u65f6\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u54fd\u54bd\u7740\u53eb\u9053\uff1a\u201c\u5c0f\u59d0\u59d0\uff01\u201d\u82e5\u99a8\u542c\u4e86\u811a\u6b65 \u4e00\u505c\uff0c\u718a\u501c\u8d70\u4e0a\u4e00\u6b65\uff0c\u7ad9\u5728\u5979\u8eab\u540e\uff0c\u82e5\u99a8\u7a81\u7136\u4e00\u8f6c\u8eab\uff0c\u718a\u501c\u4e58\u52bf\u628a\u5979\u7d27\u7d27\u5730\u6402\u5728\u6000\u91cc\uff0c\u4e24\u4eba \u987f\u89c9\u5929\u5730\u4e4b\u95f4\uff0c\u9664\u4e86\u4ed6\u4fe9\u4e4b\u5916\uff0c\u4ec0\u4e48\u90fd\u4e0d\u5b58\u5728\u4e86\u3002 \u3000\u3000\u82e5\u99a8\u987a\u4ece\u5730\u4f9d\u504e\u5728\u4ed6\u6000\u91cc\uff0c\u4f0f\u5728\u80f8\u819b\u4e0a\uff0c\u4f4e\u4f4e\u4ed6\u8bf4\uff1a\u201c\u4f60\u8981\u8d70\u4e86\u4e5f\u4e0d\u6765\u8ddf\u6211\u8bf4\u4e00\u58f0\uff0c\u96be \u9053\u4f60\u9664\u4e86\u59d0\u59d0\u4e4b\u5916\uff0c\u5c31\u4e0d\u518d\u5173\u5fc3\u4efb\u4f55\u4eba\u4e86\u5417\uff1f\u201d \u3000\u3000\u718a\u501c\u6e29\u67d4\u5730\u6478\u7740\u5979\u7684\u79c0\u53d1\uff0c\u671f\u827e\u7740\u8bf4\uff1a\u201c\u6211\u8fd8\u4ee5\u4e3a\uff0c\u4f60\u4e0d\u82e5\u99a8\u62a2\u7740\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u8bf4 \u4e86\uff0c\u6211\u4e5f\u77e5\u9053\u4f60\u60f3\u7740\u4ec0\u4e48\uff0c\u4f60\u771f\u50bb\uff0c\u96be\u9053\u8fde\u4e00\u70b9\u4e5f\u770b\u4e0d\u51fa\u6211\u5bf9\u4f60\u7684\u60c5\u611f\u5417\uff1f\u201d \u3000\u3000\u5979\u8bf4\u5b8c\uff0c\u53c8\u89c9\u5f97\u5f88\u7f9e\u6da9\uff0c\u628a\u5934\u4e00\u94bb\uff0c\u6df1\u6df1\u5730\u57cb\u85cf\u5728\u718a\u501c\u5bbd\u5927\u7684\u80f8\u819b\u91cc\u3002 \u3000\u3000\u6b64\u65f6\u4e07\u7c41\u5bc2\u7136\uff0c\u53ea\u6709\u6c34\u6d9b\u62cd\u51fb\uff0c\u53d1\u51fa\u68a6\u822c\u7684\u58f0\u97f3\uff0c\u4e24\u4eba\u4e5f\u4e0d\u77e5\u76f8\u4e92\u504e\u4f9d\u4e86\u591a\u4e45\uff0c\u718a\u501c\u8f7b \u8f7b\u5730\u63a8\u5f00\u82e5\u99a8\u8bf4\uff1a\u201c\u6211\u8be5\u8d70\u4e86\uff0c\u518d\u4e0d\u8d70\u5929\u5c31\u8981\u4eae\u4e86\u3002\u201d\u82e5\u99a8\u773c\u5708\u4e00\u7ea2\uff0c\u53c8\u6d41\u4e0b\u6cea\u6765\uff0c\u5e7d\u6028\u4ed6 \u8bf4\uff1a\u201c\u4f60\u7b49\u5929\u4eae\u518d\u8d70\u4e0d\u597d\u5417\uff1f\u201d \u3000\u3000\u718a\u501c\u6447\u4e86\u6447\u5934\uff0c\u8bf4\uff1a\u201c\u6211\u8981\u4e58\u7740\u9ed1\u6697\u8d70\uff0c\u5230\u4e86\u767d\u5929\uff0c\u6211\u5c31\u518d\u4e5f\u6ca1\u6709\u8d70\u7684\u52c7\u6c14\u4e86\u3002\u201d \u3000\u3000\u82e5\u99a8\u62ff\u8d77\u90a3\u624b\u7ee2\u5305\uff0c\u64e6\u4e86\u64e6\u773c\u6cea\uff0c\u7d27\u7d27\u5730\u585e\u5728\u718a\u501c\u7684\u8863\u895f\u91cc\uff0c\u5782\u9996\u8bf4\uff1a\u201c\u4e0d\u8981\u5f04\u6389\u4e86\uff0c \u8fd9\u4e0a\u9762\u6709\u6211\u7684\u773c\u6cea\u3002\u201d \u3000\u3000\u718a\u501c\u4e00\u54ac\u7259\uff0c\u8f6c\u8eab\u62ff\u8d77\u5305\u88b1\uff0c\u5ffd\u7136\u770b\u89c1\u82e5\u5170\u4e5f\u7ad9\u5728\u95e8\u65c1\uff0c\u4ed6\u89c9\u5f97\u4ed6\u518d\u4e0d\u8d70\uff0c\u5c31\u6c38\u8fdc\u4e0d\u80fd \u8d70\u4e86\u3002 \u3000\u3000\u4ed6\u8d70\u5230\u82e5\u5170\u7684\u8ddf\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u59d0\u59d0\u6211\u8d70\u4e86\u3002\u201d \u3000\u3000\u82e5\u5170\u6162\u6162\u5730\u8ba9\u5f00\u8def\uff0c\u8bf4\u9053\uff1a\u201c\u8def\u4e0a\u8981\u5c0f\u5fc3\u5440\uff01\u201d \u3000\u3000\u718a\u501c\u56de\u5934\u53c8\u770b\u4e86\u82e5\u99a8\u4e00\u773c\uff0c\u5979\u5df2\u54ed\u5f97\u5982\u5e26\u96e8\u68a8\u82b1\uff0c\u718a\u501c\u5f3a\u5fcd\u4f4f\u60b2\u54c0\uff0c\u671d\u82e5\u5170\u8bf4\uff1a\u201c\u6211\u4f1a \u5c0f\u5fc3\uff0c\u59d0\u59d0\u653e\u5fc3\u597d\u4e86\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u4ed6\u5c31\u51b2\u51fa\u8231\u95e8\uff0c\u6d88\u5931\u5728\u9ed1\u6697\u91cc\uff0c\u82e5\u5170\u8d70\u8fc7\u53bb\u62c9\u8d77\u82e5\u99a8\u7684\u624b\uff0c\u50cf\u662f\u544a\u8bc9\u82e5\u99a8\uff0c\u53c8\u50cf\u662f \u544a\u8bc9\u81ea\u5df1\uff0c\u575a\u5b9a\u4ed6\u8bf4\u9053\uff1a\u201c\u4e0d\u8981\u54ed\u4e86\uff0c\u4ed6\u4f1a\u56de\u6765\u7684\u3002\u201d \u3000\u3000\u718a\u501c\u8d70\u4e0b\u8239\u7684\u65f6\u5019\uff0c\u5927\u5730\u4ecd\u7136\u4e00\u7247\u9ed1\u6697\uff0c\u6b64\u65f6\u56db\u91ce\u65e0\u4eba\uff0c\u4ed6\u672c\u53ef\u653e\u8db3\u72c2\u5954\uff0c\u4f46\u4ed6\u89c9\u5f97\u8111 \u6d77\u91cc\u6df7\u6df7\u7cca\u7cca\uff0c\u832b\u7136\u82e5\u5931\uff0c\u50cf\u662f\u6709\u8bb8\u591a\u4e8b\u8981\u601d\u7d22\uff0c\u53c8\u4e0d\u77e5\u9053\u4ece\u4f55\u5904\u5f00\u59cb\u3002 \u3000\u3000\u4ed6\u4fe1\u6b65\u5411\u524d\u8d70\u7740\uff0c\u5728\u9ed1\u6697\u91cc\uff0c\u4ed6\u89c9\u5f97\u5fc3\u7075\u8f83\u5b89\u5168\u4e9b\uff0c\u4e03\u5e74\u6765\uff0c\u4ed6\u8db3\u8ff9\u4ece\u672a\u79bb\u5f00\u79e6\u6dee\u6cb3\uff0c \u5916\u5728\u4e00\u5207\u4e8b\u7269\uff0c\u5bf9\u4ed6\u90fd\u662f\u6781\u964c\u751f\u4e86\uff0c\u9762\u5bf9\u7740\u832b\u832b\u4eba\u6d77\uff0c\u4ed6\u5fc3\u91cc\u6709\u4e9b\u5bb3\u6015\uff0c\u4e0d\u77e5\u9053\u8be5\u600e\u4e48\u53bb \u505a\u3002 \u3000\u3000\u4ed6\u60f3\u9053\uff1a\u201c\u6211\u8be5\u5148\u53bb\u83ab\u6101\u6e56\uff0c\u53bb\u770b\u770b\u6234\u53d4\u53d4\u548c\u6211\u5206\u522b\u7684\u5730\u65b9\uff0c\u7136\u540e\u5462\u2026\u2026\u201d\u4ed6\u629a\u6478\u7740\u5305 \u5728\u8863\u670d\u4e2d\u7684\u957f\u5251\uff0c\u601d\u7d22\u7740\uff1a\u201c\u6211\u5c31\u8981\u53bb\u627e\u6740\u6b7b\u6234\u53d4\u53d4\u7684\u4ec7\u4eba\u4e86\uff0c\u5b9d\u9a6c\u795e\u97ad\u8428\u5929\u9aa5\uff0c\u8fd9\u540d\u5b57\u6211 \u6c38\u8fdc\u90fd\u4e0d\u4f1a\u5fd8\u8bb0\uff0c\u76f4\u5230\u4ed6\u7684\u8840\u67d3\u7ea2\u6211\u7684\u5251\u4e3a\u6b62\u3002\u201d \u3000\u3000\u4ed6\u60f3\u9053\uff1a\u201c\u7136\u540e\u5462\uff0c\u6211\u5c31\u8981\u53bb\u627e\u6211\u7684\u59b9\u59b9\u4e86\uff0c\u8bb0\u5f97\u90a3\u65f6\u5979\u8fd8\u5c0f\uff0c\u603b\u662f\u597d\u54ed\uff0c\u6709\u4e2a\u5976\u5988\u603b \u662f\u966a\u7740\u5979\uff0c\u5979\u53eb\u4ec0\u4e48\u540d\u5b57\uff0c\u600e\u4e48\u8fde\u6211\u90fd\u60f3\u4e0d\u8d77\u6765\uff0c\u8bb0\u5f97\u9646\u53d4\u53d4\u66fe\u7ecf\u544a\u8bc9\u8fc7\u6211\u7684\uff0c\u90a3\u5929\u9646\u53d4\u53d4 \u5728\u665a\u4e0a\u628a\u6211\u5e26\u4e86\u51fa\u6765\u3001\u62b1\u6211\u5230\u4e00\u8f86\u9a6c\u8f66\u4e0a\uff0c\u544a\u8bc9\u6211\u7238\u7238\u5df2\u7ecf\u6b7b\u4e86\uff0c\u53eb\u6211\u8ddf\u7740\u4ed6\u8d70\uff0c\u4ed6\u8981\u6559\u6211 \u6b66\u529f\uff0c\u66ff\u7238\u7238\u62a5\u4ec7\uff0c\u4ed6\u53c8\u6307\u7740\u4e00\u4e2a\u5c0f\u5973\u5b69\uff0c\u5bf9\u6211\u8bf4\u662f\u6211\u59b9\u59b9\uff0c\u800c\u4e14\u8fd8\u628a\u5979\u540d\u5b57\u544a\u8bc9\u4e86\u6211\uff0c\u53eb \u6211\u8bb0\u4f4f\uff0c\u53ef\u662f\u6211\u73b0\u5728\u5374\u628a\u5979\u5fd8\u4e86\uff0c\u53eb\u6211\u600e\u4e48\u53bb\u627e\u5979\u5462\uff1f\u201d \u3000\u3000\u4ed6\u8f6c\u5ff5\u601d\u7d22\u7740\uff1a\u201c\u771f\u5947\u602a\uff0c\u600e\u4e48\u90a3\u65f6\u5728\u5bb6\u91cc\u7684\u65f6\u5019\uff0c\u6211\u597d\u50cf\u4ece\u4e0d\u77e5\u9053\u6211\u6709\u4e2a\u59b9\u59b9\uff0c\u4e5f\u4ece \u6765\u6ca1\u6709\u89c1\u8fc7\u5979\uff0c\u4e5f\u8bb8\u5979\u5927\u5c0f\u4e86\uff0c\u6240\u4ee5\u7238\u7238\u4e0d\u8ba9\u6211\u8ddf\u5979\u73a9\u5427\u3002\u201d \u3000\u3000\u4ed6\u4e00\u9635\u6025\u9a70\uff0c\u7247\u523b\u5df2\u81f3\u83ab\u6101\u6e56\uff0c\u4e03\u5e74\u4eba\u4e8b\u867d\u7136\u5168\u975e\uff0c\u4f46\u83ab\u6101\u6e56\u8fd8\u662f\u539f\u6765\u7684\u6837\u5b50\uff0c\u4ed6\u4ec1\u7acb \u6e56\u8fb9\u826f\u4e45\uff0c\u5fc3\u4e2d\u53cd\u590d\u601d\u7d22\uff0c\u6e10\u6e10\u8fdc\u5904\u5df2\u6709\u9e21\u557c\u3002 \u3000\u3000\u718a\u501c\u81ea\u6c89\u601d\u4e2d\u60ca\u8d77\uff0c\u6b64\u65f6\u5929\u5df2\u5fae\u660e\uff0c\u4ed6\u6574\u4e86\u6574\u8863\u670d\uff0c\u6697\u81ea\u60f3\u7740\uff1a\u201c\u6c5f\u5b81\u5e9c\u5982\u4f55\u8d70\u6cd5\uff0c\u6211 \u90fd\u4e0d\u77e5\u9053\uff0c\u8428\u5929\u9a87\u5728\u54ea\u6240\u9556\u5c40\uff0c\u6211\u4e5f\u5fd8\u4e86\uff0c\u6211\u53ea\u5f97\u5148\u627e\u4e2a\u884c\u4eba\u95ee\u95ee\u8def\uff0c\u5230\u4e86\u6c5f\u5b81\u5e9c\u4e4b\u540e\uff0c\u518d \u8bbe\u6cd5\u6253\u542c\u8428\u5929\u9aa5\u7684\u9556\u5c40\u4e86\u3002\u201d \u3000\u3000\u718a\u501c\u8d70\u5165\u57ce\u5185\uff0c\u8fdb\u4e86\u8336\u9986\uff0c\u5750\u4e0b\u540e\uff0c\u5c31\u5411\u5802\u503c\u95ee\u9053\uff1a\u201c\u5582\uff0c\u4f19\u8ba1\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\u6c5f\u5b81\u5e9c\u6709 \u4e2a\u53eb\u5b9d\u9a6c\u795e\u97ad\u8428\u5929\u9aa5\u7684\u4eba\uff1f\u201d \u3000\u3000\u90a3\u5802\u500c\u7b11\u9053\uff1a\u201c\u5357\u4eac\u57ce\u5185\u9e23\u8fdc\u9556\u5c40\u7684\u603b\u9556\u5934\uff0c\u5b9d\u9a6c\u795e\u97ad\u8428\u5929\u9aa5\u7684\u5927\u540d\uff0c\u8c01\u4eba\u4e0d\u77e5\uff1f\u201d \u3000\u3000\u718a\u501c\u9053\uff1a\u201c\u90a3\u9e23\u8fdc\u9556\u5c40\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u5802\u500c\u9053\uff1a\u201c\u4f60\u5bb6\u539f\u6765\u662f\u8981\u627e\u8428\u5929\u9aa5\u5440\uff01\u9e23\u8fdc\u9556\u5c40\u5012\u662f\u597d\u627e\uff0c\u4ece\u8fd9\u91cc\u8fc7\u4e24\u6761\u8857\u53e3\uff0c\u671d\u5de6\u4e00 \u8f6c\u5f2f\uff0c\u4f60\u5bb6\u5c31\u53ef\u4ee5\u770b\u5230\u9e23\u8fdc\u9556\u5c40\u7684\u5927\u62db\u724c\uff0c\u4e0d\u8fc7\u4f60\u8981\u627e\u8428\u5929\u9aa5\uff0c\u5374\u6765\u665a\u4e86\u4e94\u5e74\u3002\u201d \u3000\u3000\u718a\u501c\u60ca\u9053\uff1a\u201c\u96be\u9053\u4ed6\u5df2\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u5802\u503c\u9053\uff1a\u201c\u597d\u591a\u5e74\u524d\uff0c\u9e23\u8fdc\u9556\u5c40\u6765\u4e86\u4e24\u4e2a\u7537\u4eba\uff0c\u548c\u4e00\u4e2a\u5973\u4eba\u4e24\u4e2a\u5c0f\u5b69\uff0c\u542c\u8bf4\u90a3\u4e24\u4e2a\u7537\u4eba \u4e5f\u662f\u6709\u540d\u7684\u6b66\u5e08\uff0c\u540e\u6765\u4e0d\u77e5\u600e\u5730\uff0c\u8428\u5929\u9aa5\u628a\u90a3\u4e24\u4e2a\u7537\u4eba\u5f04\u6b7b\u4e86\uff0c\u5927\u7684\u5c0f\u5b69\u4e5f\u4e0d\u6653\u5f97\u8dd1\u5230\u54ea\u91cc \u53bb\u4e86\uff0c\u8428\u5929\u9aa5\u5374\u548c\u90a3\u4e2a\u5973\u4eba\u59d8\u4e0a\u4e86\uff0c\u672c\u6765\u5927\u5bb6\u8fd8\u4e0d\u77e5\u9053\uff0c\u54ea\u6653\u5f97\u8fc7\u4e86\u4e00\u5e74\uff0c\u8428\u5929\u9aa5\u7adf\u548c\u90a3\u5973 \u4eba\u7ed3\u5a5a\u3002\u9556\u5c40\u91cc\u7684\u90fd\u662f\u597d\u6c49\uff0c\u5927\u5bb6\u90fd\u4e0d\u6ee1\u610f\u4ed6\uff0c\u4e0d\u8fc7\u56e0\u4e3a\u9556\u5c40\u662f\u4ed6\u5f00\u7684\uff0c\u4e5f\u6ca1\u5f97\u529e\u6cd5\uff0c\u54ea\u6653 \u5f97\u8fc7\u4e0d\u4e45\uff0c\u8428\u5929\u9aa5\u628a\u9556\u5c40\u7684\u4e8b\u52a1\u5ffd\u7136\u90fd\u4ea4\u7ed9\u4e86\u4e8c\u9556\u5934\u91d1\u5200\u65e0\u6545\u9547\u4e09\u6c5f\u9a86\u6c38\u677e\uff0c\u81ea\u5df1\u5374\u5e26\u7740\u90a3 \u5973\u4eba\u548c\u5c0f\u5b69\u8d70\u4e86\u3002\u201d \u3000\u3000\u718a\u501c\u5fd9\u95ee\uff1a\u201c\u4f60\u53ef\u77e5\u4ed6\u4eec\u53bb\u4e86\u4f55\u5904\uff1f\u201d \u3000\u3000\u5802\u500c\u7b54\u9053\uff1a\u201c\u8fd9\u4e2a\u6211\u5374\u4e0d\u77e5\u9053\u4e86\uff0c\u4f60\u5012\u4e0d\u5982\u5230\u9e23\u8fdc\u9556\u5c40\u53bb\u6253\u542c\u6253\u542c\uff0c\u4e5f\u8bb8\u90a3\u91cc\u6709\u4eba\u77e5 \u9053\u3002\u201d \u3000\u3000\u718a\u501c\u6b64\u65f6\u60b2\u6124\u4ea4\u96c6\uff0c\u54ea\u91cc\u8fd8\u5403\u5f97\u4e0b\u4e1c\u897f\uff0c\u5306\u5306\u4ed8\u4e86\u5e10\uff0c\u5c31\u5f80\u9e23\u8fdc\u9556\u5c40\u8d70\u53bb\u3002 \u3000\u3000\u718a\u501c\u770b\u89c1\u9e23\u8fdc\u9556\u5c40\u4e24\u6247\u9ed1\u6f06\u7684\u5927\u95e8\u5c1a\u81ea\u7d27\u95ed\uff0c\u4ed6\u4e5f\u4e0d\u7ba1\uff0c\u8d70\u4e0a\u524d\u53bb\uff0c\u5927\u58f0\u6572\u8d77\u95e8\u6765\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u53ea\u542c\u91cc\u9762\u6709\u4eba\u561f\u561f\u54dd\u54dd\u5730\u9a82\u9053\uff1a\u201c\u662f\u54ea\u4e2a\u4e27\u6c14\u9b3c\uff0c\u8fd9\u4e48\u65e9\u5c31\u6765\u53eb\u4e27\u3002\u201d \u3000\u3000\u718a\u501c\u542c\u4e86\u5927\u6012\uff0c\u5927\u95e8\u5440\u5730\u4e00\u58f0\uff0c\u5f00\u4e86\u4e00\u6761\u5c0f\u7f1d\uff0c\u94bb\u51fa\u4e00\u4e2a\u4eba\u6765\uff0c\u7761\u773c\u60fa\u677e\u4ed6\u8bf4\uff1a\u201c\u662f\u8c01 \u5440\uff1f\u6765\u5e72\u4ec0\u4e48\u7684\uff1f\u201d \u3000\u3000\u718a\u501c\u6b63\u6ca1\u597d\u6c14\uff0c\u968f\u624b\u4e00\u63a8\uff0c\u95e8\u5440\u5730\u5f00\u4e86\uff0c\u90a3\u4eba\u4e5f\u968f\u7740\u8dcc\u8dcc\u51b2\u51b2\u5730\u5f80\u540e\u5012\u4e86\u53bb\uff0c\u718a\u501c\u5927\u58f0 \u5bf9\u90a3\u4eba\u8bf4\uff1a\u201c\u5feb\u628a\u4f60\u4eec\u603b\u9556\u5934\u627e\u51fa\u6765\u3002\u201d \u3000\u3000\u90a3\u4eba\u89c1\u718a\u501c\u5e74\u8f7b\uff0c\u4ee5\u4e3a\u597d\u6b3a\uff0c\u5634\u91cc\u9a82\u9053\uff1a\u201c\u4f60\u4ed6\u5988\u7684\u4e5f\u914d\u3002\u201d \u3000\u3000\u53cd\u624b\u4e00\u4e2a\u5df4\u638c\uff0c\u5411\u718a\u501c\u8138\u4e0a\u717d\u53bb\u3002 \u3000\u3000\u718a\u501c\u53f3\u624b\u4e00\u6325\uff0c\u5de6\u624b\u6293\u7740\u90a3\u4eba\u7684\u8863\u895f\uff0c\u4e00\u629b\u4e00\u9001\uff0c\u90a3\u4eba\u53ed\u5730\u4e00\u58f0\uff0c\u8fdc\u8fdc\u8dcc\u5728\u5730\u4e0a\u3002 \u3000\u3000\u4e0d\u4e00\u4f1a\uff0c\u5c4b\u5b50\u91cc\u51fa\u6765\u4e00\u5927\u7fa4\u4eba\uff0c\u4e00\u4e2a\u4e2a\u4ff1\u90fd\u662f\u8863\u51a0\u4e0d\u6574\uff0c\u7761\u773c\u60fa\u677e\u7684\u6837\u5b50\uff0c\u663e\u7136\u662f\u521a\u4ece \u88ab\u7a9d\u91cc\u62c9\u51fa\u6765\u7684\uff0c\u5176\u4e2d\u8d70\u5728\u524d\u9762\u7684\uff0c\u662f\u4e2a\u8eab\u6750\u7279\u522b\u9ad8\u5927\u7684\u6c49\u5b50\uff0c\u6d51\u8eab\u76ae\u80a4\u9ed1\u9edd\u9edd\u5730\uff0c\u8fdc\u770b\u6d3b \u50cf\u751f\u94c1\u94f8\u6210\u7684\u91d1\u521a\uff0c\u6b64\u4eba\u6b63\u662f\u9e23\u8fdc\u9576\u5c40\u91cc\u7684\u53f0\u67f1\u9556\u5934\u4e4b\u4e00\uff0c\u795e\u5200\u9738\u738b\u5f20\u4e49\u3002 \u3000\u3000\u4ed6\u8d70\u5230\u5c4b\u95e8\u53e3\uff0c\u7a81\u7136\u505c\u4e86\u4e0b\u6765\uff0c\u5c06\u4e24\u624b\u5927\u5927\u5730\u5206\u5f00\uff0c\u62e6\u4f4f\u4e86\u540e\u9762\u7684\u4eba\uff0c\u4e0a\u4e0a\u4e0b\u4e0b\u6253\u91cf\u7740 \u718a\u501c\uff0c\u84e6\u5730\u5927\u7b11\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u542c\u738b\u4e09\u8bf4\u6709\u4eba\u6765\u8e22\u9556\u5c40\u5b50\uff0c\u6211\u5f53\u662f\u4ec0\u4e48\u4e09\u5934\u516d\u81c2\u597d\u6c49\uff0c\u5374 \u539f\u6765\u662f\u8fd9\u6837\u4e2a\u5c0f\u5154\u5d3d\u5b50\u3002\u201d \u3000\u3000\u540e\u9762\u8ddf\u7740\u7684\uff0c\u4e5f\u54c4\u7740\u7b11\u4e86\u8d77\u6765\uff0c\u50cf\u662f\u5b8c\u5168\u6ca1\u80a9\u3001\u628a\u718a\u501c\u770b\u5728\u773c\u91cc\u3002 \u3000\u3000\u5f20\u4e49\u8f6c\u8eab\u5bf9\u8eab\u540e\u7684\u4eba\u8bf4\uff1a\u201c\u8fd9\u5154\u5d3d\u5b50\u957f\u5f97\u5012\u662f\u633a\u6807\u81f4\u7684\uff0c\u53ea\u53ef\u60dc\u53c8\u5c0f\u53c8\u5ae9\uff0c\u53ea\u6015\u6321\u4e0d\u4f4f \u5927\u7237\u6211\u4e00\u4e0b\u5b50\u3002\u201d \u3000\u3000\u540e\u9762\u90a3\u4e9b\u9ad8\u9ad8\u77ee\u77ee\u7684\u9c81\u83bd\u6c49\u5b50\uff0c\u542c\u4e86\u66f4\u662f\u7b11\u5f97\u524d\u4ef0\u540e\u5408\u3002 \u3000\u3000\u718a\u501c\u5ffd\u7136\u7a9c\u4e0a\u524d\u53bb\uff0c\u4e5f\u672a\u7528\u4ec0\u4e48\u62db\u5f0f\uff0c\u671d\u5f20\u4e49\u717d\u4e86\u6b63\u53cd\u4e24\u4e2a\u8033\u5149\uff0c\u5f20\u4e49\u53ea\u89c9\u5f97\u773c\u524d\u4e00 \u82b1\uff0c\u8138\u4e0a\u5df2\u7740\u4e86\u4e24\u8bb0\uff0c\u5f20\u53e3\u4e00\u55b7\uff0c\u8fde\u7259\u5e26\u8840\uff0c\u6e85\u4e86\u4e00\u5730\u3002 \u3000\u3000\u5f20\u4e49\u6012\u559d\u9053\uff1a\u201c\u597d\u4e2a\u5c0f\u5154\u5d3d\u5b50\uff0c\u8fde\u62db\u547c\u90fd\u4e0d\u6253\u5c31\u4e0b\u624b\u4e86\u3002\u201d \u3000\u3000\u521a\u8bf4\u5b8c\uff0c\u957f\u81c2\u4e00\u4f38\uff0c\u4e00\u62db\u201c\u91d1\u8c79\u9732\u722a\u201d\u5411\u718a\u501c\u6293\u53bb\u3002 \u3000\u3000\u718a\u501c\u51b7\u7b11\u4e00\u58f0\uff0c\u53f3\u81c2\u4e00\u6325\uff0c\u5de6\u624b\u524d\u63a2\uff0c\u51c6\u5907\u7167\u65b9\u6293\u836f\uff0c\u50cf\u521a\u624d\u4e00\u6837\uff0c\u6454\u4ed6\u4e2a\u56db\u811a\u671d\u5929\uff0c \u54ea\u77e5\u5f20\u4e49\u5374\u8fdc\u975e\u521a\u624d\u5f00\u95e8\u7684\u7389\u4e09\u53ef\u6bd4\uff0c\u4ed6\u7d20\u4ee5\u795e\u5200\u8457\u79f0\uff0c\u4f55\u51b5\u718a\u501c\u8fd9\u4e00\u6325\uff0c\u53ea\u7528\u4e86\u4e8c\u6210\u529b\uff0c \u7adf\u672a\u80fd\u5c06\u4ed6\u683c\u5f00\uff0c\u5f20\u4e49\u5c06\u62db\u5c31\u5f0f\uff0c\u53cd\u624b\u4e00\u62db\u201c\u91d1\u4e1d\u7ede\u526a\u201d\uff0c\u7adf\u5c06\u718a\u501c\u53f3\u624b\u5201\u4f4f\uff0c\u957f\u5927\u7684\u8eab \u8eaf\uff0c\u5fae\u5f80\u5916\u503e\uff0c\u201c\u9b41\u661f\u8e22\u6597\u201d\u53f3\u624b\u731b\u529b\u53e3\u5e26\uff0c\u75be\u7684\u4e00\u817f\uff0c\u671d\u718a\u501c\u8e22\u53bb\u3002 \u3000\u3000\u718a\u501c\u9aa4\u9022\u9669\u62db\uff0c\u53c8\u662f\u521d\u6b21\u51fa\u624b\uff0c\u4e0d\u514d\u6709\u4e9b\u5fc3\u614c\uff0c\u4f46\u4ed6\u6bd5\u7adf\u529f\u529b\u6df1\u539a\uff0c\u5fae\u4e00\u7528\u6c14\uff0c\u771f\u6c14\u5373 \u704c\u8fbe\u56db\u80a2\uff0c\u5de6\u638c\u5f2f\u5f0f\u5f80\u4e0b\u53bb\u524a\u8e22\u6765\u7684\u811a\uff0c\u53f3\u624b\u5fae\u4e00\u7528\u529b\uff0c\u5f20\u4e49\u5373\u89c9\u628a\u6301\u4e0d\u4f4f\uff0c\u84e6\u5730\u56de\u624b\u6536 \u817f\uff0c\u5de6\u811a\u8ddf\u4e00\u7528\u529b\uff0c\u201c\u91d1\u9ca4\u5012\u7a7f\u6ce2\u201d\uff0c\u5f80\u540e\u731b\u7a9c\uff0c\u4ee5\u6c42\u81ea\u4fdd\uff0c\u6b64\u65f6\u718a\u501c\u53ea\u8981\u987a\u52bf\u524d\u5f80\uff0c\u518d\u65bd \u4e00\u51fb\uff0c\u5373\u53ef\u7adf\u529f\uff0c\u4f46\u662f\u4ed6\u5230\u5e95\u4e34\u654c\u7ecf\u9a8c\u592a\u5c11\uff0c\u7adf\u672a\u80fd\u8fde\u73af\u7528\u62db\uff0c\u987b\u77e5\u4ed6\u7ec3\u529f\u5168\u662f\u72ec\u81ea\u4e00\u4eba\uff0c \u8fde\u5bf9\u624b\u8fc7\u62db\u7684\u90fd\u6ca1\u6709\uff0c\u81ea\u7136\u521d\u51fa\u624b\u65f6\uff0c\u96be\u514d\u6709\u6b64\u73b0\u8c61\u3002 \u3000\u3000\u5f20\u4e49\u8eab\u521a\u7acb\u5b9a\uff0c\u6c14\u867d\u5df2\u9981\uff0c\u4f46\u4ecd\u4e0d\u80af\u5c31\u6b64\u6536\u624b\uff0c\u6b63\u51c6\u5907\u518d\u5f80\u524d\u51b2\uff0c\u7a81\u5730\u53c8\u56de\u5ff5\u4e00\u60f3\uff1a \u201c\u6b64\u4eba\u5e74\u7eaa\u867d\u8f7b\uff0c\u6b66\u529f\u5374\u6df1\u4e0d\u53ef\u6d4b\uff0c\u4e0d\u77e5\u4f55\u95e8\u4f55\u6d3e\uff0c\u6765\u6b64\u53c8\u6709\u4f55\u4e8b\uff0c\u662f\u654c\u662f\u53cb\u5c1a\u672a\u5206\u660e\uff0c\u6211 \u4f55\u5fc5\u8fd9\u6837\u6614\u6491\uff0c\u5373\u4f7f\u4f24\u4e86\u6027\u547d\uff0c\u53c8\u6709\u4f55\u7528\u2026\u2026\u201d \u3000\u3000\u4e8e\u662f\u4ed6\u4e0d\u518d\u51fa\u62db\uff0c\u4f46\u4ed6\u662f\u4e2a\u83bd\u6c49\uff0c\u4e0d\u5584\u8a00\u8bcd\uff0c\u7ade\u4e5f\u672a\u51fa\u8a00\u76f8\u8be2\uff0c\u718a\u501c\u89c1\u4ed6\u6014\u6014\u5730\u7ad9\u5728\u5bf9 \u9762\uff0c\u4e0d\u89e3\u4f55\u6545\uff0c\u6697\u5fd6\u9053\uff1a\u201c\u5e38\u542c\u82e5\u5170\u59d0\u8bf4\uff0c\u4e16\u9053\u4eba\u5fc3\uff0c\u6700\u662f\u9669\u6076\uff0c\u4f60\u4e0d\u4f24\u4eba\uff0c\u4eba\u4fbf\u4f24\u4f60\uff0c\u73b0 \u5728\u4ed6\u867d\u662f\u5446\u7ad9\u5728\u6b64\uff0c\u4f46\u5fc3\u91cc\u5374\u4e0d\u77e5\u5728\u8f6c\u4ec0\u4e48\u574f\u5ff5\u5934\uff0c\u4e0d\u5982\u6211\u5148\u53d1\u5236\u4eba\uff0c\u5148\u6253\u53d1\u4e86\u4ed6\u518d\u8bf4\uff0c\u514d \u5f97\u53cd\u5403\u4eba\u4e8f\u3002\u201d \u3000\u3000\u6b64\u523b\u4ed6\u8f7b\u654c\u4e4b\u5fc3\u5df2\u6cef\uff0c\u4e00\u51fa\u624b\uff0c\u5c31\u662f\u201c\u82cd\u7a79\u5341\u4e09\u5f0f\u201d\u91cc\u7684\u7edd\u62db\uff0c\u8eab\u8eaf\u5fae\u4e00\u987f\u632b\uff0c\u4eba\u5df2\u5982 \u7bad\u822c\u79bb\u5730\u800c\u8d77\uff0c\u201c\u6cdb\u6e21\u94f6\u6cb3\u201d\u4ee5\u638c\u4e3a\u5251\uff0c\u5e26\u7740\u4e00\u822c\u52b2\u98ce\uff0c\u5411\u5f20\u4e49\u5f53\u5934\u6325\u4e0b\u3002 \u3000\u3000\u5f20\u4e49\u6b63\u81ea\u76d8\u7b97\u5982\u4f55\u5f00\u53e3\uff0c\u718a\u501c\u4eba\u5df2\u88ad\u5230\uff0c\u201c\u795e\u529b\u9738\u738b\u201d\u4e45\u5386\u6c5f\u6e56\uff0c\u77e5\u9053\u8fd9\u79cd\u8eab\u5728\u7a7a\u4e2d\uff0c \u5373\u5df2\u53d1\u51fa\u7684\u62db\u5f0f\uff0c\u4f60\u6108\u662f\u4f38\u624b\u683c\u62d2\uff0c\u6240\u53d7\u7684\u4e5f\u6108\u91cd\uff0c\u4e8e\u662f\u4ed6\u731b\u529b\u53f3\u65cb\uff0c\u60f3\u907f\u5f00\u6b64\u62db\uff0c\u4f46\u201c\u82cd \u7a79\u5341\u4e09\u5f0f\u201d\u4e00\u62db\u5373\u51fa\uff0c\u5176\u4f59\u7684\u62db\u5f0f\u81ea\u4f1a\u8fde\u73af\u8fd0\u7528\uff0c\u9664\u975e\u5bf9\u65b9\u4ea6\u6709\u6781\u9ad8\u7684\u6b66\u529f\uff0c\u5426\u5219\u7edd\u96be\u9003 \u51fa\uff0c\u718a\u501c\u53f3\u817f\u5916\u4f38\uff0c\u53cc\u624b\u9f50\u4e0b\uff0c\u5f20\u4e49\u53ea\u89c9\u6f2b\u5929\u4ff1\u662f\u718a\u501c\u7684\u638c\u5f71\uff0c\u8fde\u8eb2\u90fd\u65e0\u6cd5\u8eb2\u5f97\u3002 \u3000\u3000\u5ffd\u7136\u4e00\u4eba\u81ea\u5185\u5954\u51fa\uff0c\u5927\u559d\u9053\uff1a\u201c\u5feb\u5f80\u4e0b\u8eb2\u3002\u201d\u4f46\u5f20\u4e49\u5df2\u5728\u638c\u98ce\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u5df2\u662f\u8eab\u4e0d\u81ea \u4e3b\uff0c\u718a\u501c\u53f3\u624b\u659c\u524a\u201c\u843d\u5730\u6d41\u661f\u201d\uff0c\u5f20\u4e49\u53f3\u9888\u4e00\u9ebb\uff0c\u4eba\u5df2\u660f\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u90a3\u5954\u51fa\u6765\u7684\u4eba\u6717\u58f0\u7b11\u9053\uff1a\u201c\u597d\u8eab\u6cd5\uff0c\u597d\u8eab\u6cd5\uff0c\u60f3\u4e0d\u5230\u6614\u5e74\u5a01\u9547\u6c5f\u6e56\u2018\u82cd\u7a79\u5341\u4e09\u5f0f\u2019\uff0c\u53c8 \u5728\u6b64\u5730\u91cd\u73b0\u3002\u201d\u8bf4\u5b8c\u53c8\u6df1\u6df1\u4e00\u63d6\u8bf4\u9053\uff1a\u201c\u5c0f\u5f1f\u662f\u6b64\u95ee\u9556\u5c40\u7684\u7ba1\u4e8b\u7684\uff0c\u6c5f\u6e56\u4e0a\u670b\u53cb\u90fd\u53eb\u6211\u9526\u9762 \u82cf\u79e6\uff0c\u738b\u667a\u9011\u4f7f\u662f\u5728\u4e0b\uff0c\u5176\u5b9e\u5462\uff0c\u8fd9\u90fd\u662f\u670b\u53cb\u4eec\u7684\u62ac\u4e3e\u7f62\u4e86\u3002\u201d\u8bf4\u5b8c\u53c8\u5927\u58f0\u7b11\u8d77\u6765\uff0c\u7136\u540e\u63a5 \u7740\u8bf4\uff1a\u201c\u770b\u5144\u53f0\u7684\u8eab\u6cd5\uff0c\u60f3\u5fc5\u662f\u5f53\u5e74\u4ee5\u2018\u82cd\u7a79\u5341\u4e09\u5f0f\u2019\u996e\u8a89\u6c5f\u6e56\u7684\u661f\u6708\u53cc\u5251\u7684\u540e\u4eba\u4e86\uff0c\u60f3\u5f53 \u5e74\u6c5f\u6e56\u4e0a\u4eba\uff0c\u8c01\u4e0d\u5bf9\u6234\u3001\u9646\u4e24\u4f4d\u524d\u8f88\u666f\u4ef0\u5f97\u4e94\u4f53\u6295\u5730\uff0c\u53ea\u662f\u81ea\u4ece\u661f\u6708\u53cc\u5251\u6545\u53bb\u540e\uff0c\u2018\u82cd\u7a79\u5341 \u4e09\u5f0f\u2019\u7adf\u6210\u7edd\u54cd\uff0c\u60f3\u4e0d\u5230\u5144\u5f1f\u4eca\u65e5\u6709\u7f18\uff0c\u80fd\u518d\u7779\u5947\u6280\u3002\u201d\u718a\u501c\u9053\uff1a\u201c\u5c0f\u5f1f\u6b63\u662f\u661f\u6708\u53cc\u5251\u7684\u5ae1 \u4f20\u5f1f\u5b50\uff0c\u6b64\u523b\u5230\u8d35\u9556\u5c40\u6765\uff0c\u4fbf\u662f\u6709\u51e0\u4ef6\u5bb6\u5e08\u5f53\u5e74\u672a\u4e86\u4e4b\u4e8b\u60f3\u6765\u8bf7\u6559\uff0c\u53ea\u662f\u8d35\u9556\u5c40\u7684\u5927\u9556\u5934\u4eec \u5374\u6041\u5730\u5389\u5bb3\uff0c\u4e0d\u5206\u9752\u767d\u7682\u767d\uff0c\u5c31\u8981\u62ff\u5c0f\u5f1f\u8bd5\u624b\uff0c\u5c0f\u5f1f\u8fd9\u624d\u5f97\u7f6a\u4e86\uff0c\u8fd8\u8bf7\u603b\u9556\u5934\u591a\u539f\u8c05\u3002\u201d \u3000\u3000\u738b\u667a\u9011\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fd9\u90fd\u602a\u5c0f\u5f1f\u592a\u61d2\uff0c\u8d77\u5e8a\u592a\u665a\uff0c\u63a5\u5f85\u6765\u8fdf\u3002\u201d\u4ed6\u770b\u4e86\u4ecd\u7136\u5012\u5728\u5730\u4e0a\u7684 \u5f20\u4e49\u4e00\u773c\uff0c\u76ee\u5149\u91cc\u6d41\u9732\u51fa\u4e00\u4e1d\u5947\u5f02\u5149\u8292\uff0c\u4f46\u4e00\u95e8\u800c\u6ca1\uff0c\u53e3\u5934\u53c8\u62db\u4e86\u62db\u624b\uff0c\u53eb\u4f19\u8ba1\u4eec\u7167\u6599\u5f20\u4e49 \u8fdb\u53bb\uff0c\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8fd9\u4e2a\u5144\u5f1f\uff0c\u5c31\u662f\u8fd9\u6837\u9c81\u83bd\u813e\u6c14\uff0c\u60f3\u5fc5\u662f\u4ed6\u5f00\u7f6a\u4e86\u5144\u53f0\uff0c\u60a8\u624d\u60e9 \u6212\u60e9\u6212\u4ed6\uff0c\u8fd9\u662f\u4ed6\u548e\u7531\u81ea\u53d6\uff0c\u5982\u4f55\u80fd\u602a\u5f97\u522b\u4eba\uff0c\u5144\u53f0\u5982\u4e0d\u4ecb\u610f\uff0c\u8bf7\u91cc\u9762\u53d9\u8336\uff0c\u5144\u53f0\u5982\u6709\u4e8b\u5429 \u5490\uff0c\u5c0f\u5f1f\u82e5\u80fd\u529e\u5230\u7684\uff0c\u4e00\u5b9a\u6548\u52b3\u3002\u201d \u3000\u3000\u4e8e\u662f\u738b\u667a\u9011\u62f1\u624b\u8ba9\u5ba2\uff0c\u718a\u501c\u4e5f\u5766\u7136\u4eba\u5185\u3002 \u3000\u3000\u4e8c\u4eba\u4e92\u9053\u59d3\u540d\uff0c\u5ba2\u5957\u4e00\u756a\u540e\uff0c\u738b\u667a\u9011\u9053\uff1a\u201c\u718a\u5144\u53ea\u6015\u4e0d\u77e5\uff0c\u8fd9\u51e0\u5e74\u6765\u6c5f\u6e56\u4e0a\u4eba\u6750\u8f88\u51fa\uff0c \u65e0\u8bba\u9ed1\u767d\u4e24\u9053\uff0c\u90fd\u6709\u51e0\u4e2a\u5a01\u9707\u6b66\u6797\u7684\u540e\u8d77\u4e4b\u79c0\uff0c\u5176\u4e2d\u6700\u4f7f\u6c5f\u6e56\u4fa7\u76ee\u7684\uff0c\u6709\u5929\u5c71\u51b7\u5bb6\u5144\u59b9\u7684\u4f20 \u4eba\uff0c\u51b7\u5982\u6c34\uff0c\u51b7\u5982\u971c\u548c\u949f\u5929\u4ec7\uff0c\u5341\u4e09\u7701\u4e10\u90a6\u7684\u65b0\u9009\u9f99\u5934\u5e2e\u4e3b\uff0c\u84dd\u5927\u5148\u751f\uff0c\u56db\u5ddd\u5510\u95e8\u7684\u4e03\u6bd2\u4e66 \u751f\u5510\u7fbd\uff0c\u6c5f\u82cf\u864e\u90b1\u7684\u4e1c\u65b9\u5144\uff0c\u6b64\u5916\u5ce8\u5d4b\u7684\u5b64\u5cf0\u4e00\u5251\uff0c\u5ce8\u5d4b\u53cc\u5c0f\uff0c\u6b66\u5f53\u56db\u4eea\u5251\u5ba2\uff0c\u4ff1\u90fd\u662f\u767e\u5e74 \u96be\u89c1\u7684\u6b66\u6797\u4fca\u5f66\uff0c\u66f4\u53ef\u60ca\u662f\uff0c\u5c45\u8bf4\u6614\u5e74\u7eb5\u6a2a\u5929\u4e0b\u7684\u5929\u9634\u6559\u53c8\u5728\u5c71\u897f\u7684\u592a\u884c\u5c71\u5de6\u6b7b\u7070\u590d\u71c3\uff0c\u6559 \u4e3b\u662f\u4e00\u7537\u4e00\u5973\u4e24\u4e2a\u4e0d\u77e5\u59d3\u540d\u7684\u51fa\u8eab\u7684\u5e74\u8f7b\u7537\u5973\uff0c\u5982\u4f20\u95fb\u662f\u5b9e\uff0c\u53ea\u6015\u6b66\u6797\u53c8\u96be\u514d\u8499\u52ab\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u5b8c\u53c8\u662f\u54c8\u54c8\u4e00\u9635\u5927\u7b11\uff0c\u4e3e\u8d77\u5927\u62c7\u6307\u5411\u718a\u501c\u4e00\u626c\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u8fc7\u636e\u6211\u770b\u6765\uff0c\u8fd9\u4e9b\u4eba\u867d \u90fd\u662f\u6b66\u6797\u4e4b\u6770\uff0c\u4f46\u6bd4\u8d77\u718a\u5144\u6765\uff0c\u53ea\u6015\u90fd\u6709\u900a\u8272\uff0c\u718a\u5144\u6b64\u756a\u51fa\u6765\u95ef\u8361\u6c5f\u6e56\uff0c\u6211\u62c5\u4fdd\u4e0d\u51fa\u6570\u6708\uff0c \u5b9a\u7136\u540d\u52a8\u6c5f\u6e56\u3002\u201d \u3000\u3000\u718a\u501c\u7b11\u9053\uff1a\u201c\u603b\u9556\u5934\u8fc7\u5956\u4e86\uff0c\u53ea\u662f\u5c0f\u5f1f\u6b64\u756a\u524d\u6765\uff0c\u786e\u771f\u6709\u51e0\u4ef6\u5f02\u5e38\u91cd\u8981\u7684\u4e8b\uff0c\u5f85\u4e00\u4e00\u4e86 \u5374\u3002\u6b64\u95f4\u9556\u5c40\uff0c\u6614\u5e74\u662f\u8428\u5929\u9aa5\u6240\u521b\uff0c\u8fd1\u95fb\u4eba\u8a00\uff0c\u6b64\u4eba\u4eca\u5df2\u8fdc\u8d70\uff0c\u60f3\u603b\u9556\u5934\u5b9a\u5fc5\u77e5\u9053\u4ed6\u7684\u53bb \u5904\u3002\u201d \u3000\u3000\u738b\u667a\u9011\u9053\uff1a\u201c\u718a\u5144\u82e5\u6253\u542c\u522b\u7684\u4eba\u7269\uff0c\u53ea\u8981\u662f\u6c5f\u6e56\u4e0a\u7a0d\u6709\u540d\u6c14\uff0c\u5c0f\u5f1f\u4e0d\u6562\u8bf4\u4e86\u5982\u6307\u638c\uff0c\u4f46 \u4e5f\u7565\u77e5\u4e00\u4e8c\uff0c\u4f46\u662f\u8fd9\u8428\u5927\u9aa5\u4e48\u4ed6\u6545\u610f\u628a\u8bed\u58f0\u62d6\u957f\uff0c\u5077\u76ee\u4e00\u671b\u718a\u501c\uff0c\u89c1\u718a\u501c\u4e00\u63d0\u5230\u8428\u5929\u9aa5\uff0c\u5c31 \u663e\u5f97\u5f02\u5e38\u5fff\u6068\uff0c\u5fc3\u4e2d\u6697\u559c\uff0c\u77e5\u9053\u81ea\u5df1\u6240\u6599\u7684\u4e0d\u5dee\uff0c\u8fde\u5fd9\u63a5\u7740\u8bf4\uff1a\u201c\u6309\u8bf4\u5357\u97ad\u8428\u5929\u9aa5\uff0c\u4e5f\u662f\u6781 \u8d1f\u76db\u540d\u7684\u4eba\u7269\uff0c\u4f46\u81ea\u4ece\u4ed6\u5f53\u5e74\u624b\u521b\u661f\u6708\u53cc\u5251\u540e\uff0c\u60f3\u5fc5\u81ea\u5df1\u5fc3\u865a\uff0c\u57cb\u5934\u5f52\u9690\uff0c\u4ece\u6b64\u4f7f\u4e0d\u77e5\u53bb \u5411\uff0c\u8981\u627e\u4ed6\u5b9e\u662f\u56f0\u96be\u5df2\u6781\u3002\u201d \u3000\u3000\u718a\u501c\u542c\u4e86\uff0c\u5fcd\u4e0d\u4f4f\u9762\u8272\u7a7a\u7136\u53d8\u5f97\u5931\u671b\u548c\u60b2\u6124\uff0c\u7ad9\u8d77\u6765\u9053\uff1a\u201c\u8fd9\u59d3\u8428\u7684\u548c\u6211\u6709\u4e0d\u5171\u6234\u5929\u4e4b \u4ec7\uff0c\u6211\u5c31\u7b97\u662f\u4e0a\u5929\u5165\u5730\uff0c\u4e5f\u8981\u627e\u4ed6\u51fa\u6765\uff0c\u603b\u9556\u5934\u65e2\u7136\u4e0d\u77e5\u8fd9\u53ae\u7684\u53bb\u5904\uff0c\u90a3\u4e48\u5c0f\u5f1f\u5c31\u6b64\u522b \u8fc7\u3002\u201d \u3000\u3000\u738b\u667a\u9011\u8fde\u5fd9\u5c06\u4ed6\u62c9\u4f4f\uff0c\u8bf4\u9053\uff1a\u201c\u718a\u5144\u5207\u83ab\u592a\u8fc7\u6025\u8e81\uff0c\u60f3\u718a\u5144\u521d\u4eba\u6c5f\u6e56\uff0c\u670b\u53cb\u81ea\u5c11\uff0c\u5c0f\u5f1f \u867d\u4e0d\u6210\u6750\uff0c\u4fbf\u65e0\u8bba\u9ed1\u767d\u4e24\u9053\uff0c\u90fd\u8fd8\u6709\u4e2a\u4ea4\u60c5\uff0c\u718a\u5144\u82e5\u628a\u5c0f\u5f1f\u770b\u6210\u4e2a\u670b\u53cb\uff0c\u6b64\u4e8b\u81ea\u7ba1\u4ea4\u7ed9\u5c0f \u5f1f\uff0c\u5c0f\u5f1f\u51b3\u5b9a\u5c3d\u5168\u529b\u63a2\u8bbf\u51fa\u8428\u5929\u9aa5\u7684\u4e0b\u843d\uff0c\u5c82\u4e0d\u6bd4\u4f60\u72ec\u81ea\u63a2\u8bbf\u8981\u597d\u5f97\u591a\u4e48\u3002\u201d \u3000\u3000\u718a\u501c\u6b64\u523b\u65b9\u5bf8\u5df2\u4e71\uff0c\u95fb\u8a00\u4e00\u60f3\uff0c\u4e5f\u662f\u9053\u7406\uff0c\u6251\u5730\u62dc\u5012\uff0c\u542b\u60b2\u8bf4\u9053\uff1a\u201c\u5c0f\u5f1f\u4e3e\u76ee\u65e0\u4eb2\uff0c\u51e1 \u4e8b\u53ea\u6709\u4ef0\u4ed7\u603b\u9556\u5934\u4e86\uff0c\u65e5\u540e\u7c89\u8eab\u788e\u9aa8\uff0c\u5fc5\u62a5\u5927\u6069\u3002\u201d \u3000\u3000\u738b\u667a\u9011\u4e5f\u5bf9\u9762\u62dc\u5012\uff0c\u53cc\u624b\u6400\u6276\u718a\u501c\uff0c\u8bf4\u9053\uff1a\u201c\u718a\u5144\u5207\u83ab\u8fd9\u6837\uff0c\u6298\u715e\u5c0f\u5f1f\u4e86\uff0c\u6709\u8bdd\u6162\u6162\u5546 \u91cf\uff0c\u6211\u603b\u8981\u66ff\u718a\u5144\u60f3\u4e2a\u4e07\u5168\u4e4b\u8ba1\uff0c\u4f46\u5374\u5343\u4e07\u5fc3\u6025\u4e0d\u5f97\u3002\u201d \u3000\u3000\u738b\u667a\u9011\u628a\u718a\u501c\u6276\u5230\u6905\u5b50\u4e0a\uff0c\u718a\u501c\u4ecd\u7136\u542b\u60b2\u672a\u4f4f\uff0c\u738b\u667a\u9011\u8bf4\u9053\uff1a\u201c\u718a\u5144\u5355\u8eab\u4eba\u6c5f\u6e56\uff0c\u60f3\u5fc5 \u65e0\u751a\u7275\u6302\uff0c\u5982\u679c\u4e0d\u5acc\u6b64\u5730\u7b80\u964b\uff0c\u4e0d\u5982\u5c31\u642c\u6765\u4f4f\u4e0b\uff0c\u4e00\u6765\u7701\u5f97\u522b\u5904\u4e0d\u4fbf\uff0c\u4e8c\u6765\u65e5\u540e\u6709\u4e8b\uff0c\u4e5f\u597d \u5546\u91cf\u3002\u201d \u3000\u3000\u718a\u501c\u867d\u662f\u806a\u660e\u7edd\u9876\uff0c\u4f46\u7ec8\u7a76\u662f\u5386\u7ec3\u4e0d\u591f\uff0c\u7adf\u4e5f\u4e00\u53e3\u7b54\u5e94\u4e0b\u6765\u3002 \u3000\u3000\u738b\u667a\u9011\u89c1\u4ed6\u5df2\u7b54\u5e94\uff0c\u5fc3\u4e2d\u6697\u559c\uff0c\u5fd9\u9053\uff1a\u201c\u718a\u5144\u8fd8\u6709\u4ec0\u4e48\u884c\u674e\u3001\u8863\u7269\uff0c\u53ef\u8981\u4e00\u5e76\u53d6\u6765\uff0c\u718a \u5144\u65e5\u540e\u82e5\u6709\u6240\u9700\uff0c\u4e5f\u8bf7\u53ea\u7ba1\u5f00\u53e3\uff0c\u6b64\u540e\u4f60\u6211\u4fbf\u662f\u4e00\u5bb6\u4eba\u4e86\u3002\u201d \u7b2c\u4e09\u7ae0\u3000\u4eba\u5fc3\u96be\u6d4b>> \u53e4\u9f99\u300a\u82cd\u7a79\u795e\u5251\u300b \u7b2c\u4e09\u7ae0\u3000\u4eba\u5fc3\u96be\u6d4b \u3000\u3000\u539f\u6765\u738b\u667a\u9011\u3001\u5f20\u4e49\u548c\u53e6\u5916\u4e00\u4e2a\u53eb\u5434\u8bcf\u4e91\u7684\uff0c\u5e76\u79f0\u91d1\u9675\u4e09\u6770\uff0c\u5434\u8bcf\u4e91\u6b66\u529f\u6700\u9ad8\uff0c\u638c\u4e2d\u5251\u5f97 \u81ea\u70b9\u82cd\u6d3e\u7684\u771f\u4f20\uff0c\u4eba\u4e5f\u5f88\u6b63\u6d3e\uff0c\u5f20\u4e49\u4eba\u867d\u7c97\u9c81\uff0c\u4f46\u65e0\u5fc3\uff0c\u7a7a\u81ea\u529b\u5927\u65e0\u7a77\uff0c\u6b66\u529f\u5374\u4e0d\u751a\u9ad8\uff0c\u738b \u667a\u9011\u9664\u4e86\u8f7b\u529f\u5c1a\u53ef\u89c2\u5916\uff0c\u4e00\u65e0\u6240\u957f\uff0c\u53cd\u5c45\u91d1\u9675\u4e4b\u9996\uff0c\u6c5f\u6e56\u4e0a\u4eba\u4e00\u63d0\u8d77\u7c89\u9762\u82cf\u79e6\uff0c\u8c01\u90fd\u5934\u75db\u4e09 \u5206\uff0c\u7686\u56e0\u4ed6\u8be1\u8ba1\u591a\u7aef\uff0c\u773c\u76ae\u6742\uff0c\u624b\u9762\u5bbd\uff0c\u5b98\u7684\u3001\u79c1\u7684\u3001\u9ed1\u9053\u3001\u767d\u9053\uff0c\u53ea\u8981\u78b0\u7740\u4ed6\uff0c\u65e0\u4e0d\u88ab\u4ed6 \u5360\u4e86\u4fbf\u5b9c\u53bb\uff0c\u4f46\u5374\u65e0\u8bdd\u53ef\u8bf4\uff0c\u5f20\u4e49\u5bf9\u4ed6\u66f4\u662f\u5fc3\u670d\u53e3\u670d\uff0c\u5434\u8bcf\u4e91\u867d\u5bf9\u4ed6\u65f6\u6709\u4e0d\u6ee1\uff0c\u4f46\u4ed6\u4eec\u7ed3\u4e49 \u5728\u5148\uff0c\u4e5f\u53ea\u5f97\u7f62\u4e86\uff0c\u4ec0\u4e48\u4e5f\u656c\u4ed6\u4e09\u5206\u3002 \u3000\u3000\u4ed6\u4e4b\u6240\u4ee5\u7ed3\u4ea4\u718a\u501c\uff0c\u4ea6\u662f\u522b\u6709\u7528\u5fc3\u7684\u3002\u5f53\u5e74\u8428\u5929\u9aa5\u8d70\u65f6\uff0c\u5e76\u672a\u4ea4\u5f85\u4efb\u4f55\u4e8b\u60c5\uff0c\u662f\u6545\u5f53\u65f6 \u9556\u5c40\u7fa4\u9f99\u5143\u9996\uff0c\u5927\u5bb6\u90fd\u60f3\u593a\u53d6\u603b\u9556\u5934\u4e4b\u4f4d\uff0c\u8fd9\u65f6\u5434\u8bcf\u4e91\u3001\u5f20\u4e49\u90fd\u662f\u521d\u4eba\u9556\u5c40\uff0c\u738b\u667a\u8ff0\u4fbf\u5229\u7528 \u6b64\u4e8c\u4eba\uff0c\u53d6\u5f97\u603b\u9556\u5934\u4e4b\u4f4d\uff0c\u5176\u4f59\u7684\u9556\u5e08\u4e00\u6c14\u4e4b\u4e0b\uff0c\u4e5f\u6563\u4e86\u5927\u534a\u3002 \u3000\u3000\u4e8e\u662f\u9e23\u8fdc\u9556\u5c40\u504c\u5927\u4e00\u4efd\u57fa\u4e1a\uff0c\u773c\u770b\u5c31\u8981\u98ce\u6d88\u4e91\u6563\uff0c\u54ea\u77e5\u738b\u667a\u9011\u5374\u53e6\u6709\u624b\u8155\uff0c\u4ed6\u7adf\u53d6\u5f97\u5b98 \u5e9c\u5408\u4f5c\uff0c\u8fd9\u6837\u4e00\u6765\uff0c\u9e23\u8fdc\u9556\u5c40\u7684\u4e1a\u52a1\uff0c\u624d\u53c8\u84b8\u84b8\u65e5\u4e0a\u3002 \u3000\u3000\u5c31\u5728\u718a\u501c\u5230\u9556\u5c40\u524d\u4e0d\u4e45\uff0c\u5728\u6d59\u3001\u7696\u3001\u82cf\u4ea4\u5883\u5904\u7684\u8378\u5c71\u811a\u4e0b\uff0c\u5ffd\u7136\u51fa\u4e86\u4e00\u679d\u6210\u5f62\u9996\u4e4c\uff0c\u8fd9 \u79cd\u4e1c\u897f\u672c\u662f\u5929\u5730\u95f4\u7684\u81f3\u5b9d\uff0c\u54ea\u77e5\u88ab\u4e00\u6a35\u592b\u65e0\u610f\u95f4\u5f97\u5230\uff0c\u90a3\u6a35\u592b\u7ec8\u5e74\u52b3\u82e6\uff0c\u4e5f\u4e0d\u77e5\u9053\u6b64\u7269\u7a76\u7adf \u662f\u4ec0\u4e48\uff0c\u53ea\u60f3\u5230\u4e00\u5b9a\u503c\u94b1\uff0c\u8dd1\u5230\u836f\u94fa\u91cc\uff0c\u5356\u4e86\u51e0\u5341\u4e24\u94f6\u5b50\u3002 \u3000\u3000\u8fd9\u836f\u94fa\u8001\u677f\uff0c\u5374\u662f\u4e2a\u5b98\u8ff7\uff0c\u5f97\u4e86\u6b64\u7269\uff0c\u559c\u4e0d\u81ea\u80dc\uff0c\u5e26\u81f3\u6c5f\u5b81\u5e9c\u53bb\uff0c\u60f3\u732e\u7ed9\u7687\u4e0a\uff0c\u5e0c\u671b\u80fd \u535a\u5230\u4e00\u5b98\u534a\u804c\uff0c\u597d\u5149\u8000\u95e8\u76f8\u3002\u6c5f\u5b81\u5e9c\u4e5f\u60f3\u501f\u6b64\u5347\u5b98\uff0c\u4f46\u77e5\u9053\u6c5f\u6e56\u4eba\u58eb\u542c\u5230\u8fd9\u79cd\u6d88\u606f\uff0c\u6cbf\u9014\u52bf \u5fc5\u524d\u6765\u62a2\u593a\uff0c\u4ed6\u5c31\u628a\u8fd9\u96be\u9898\u4ea4\u7ed9\u9e23\u8fdc\u9556\u5c40\uff0c\u8ba9\u4ed6\u5c06\u6b64\u7269\u9001\u81f3\u5e1d\u4eac\u3002 \u3000\u3000\u9e23\u8fdc\u9556\u5c40\u7684\u9556\u65d7\u867d\u80fd\u5356\u51e0\u5206\u4ea4\u60c5\uff0c\u4f46\u8fd9\u79cd\u4e1c\u897f\u5374\u5927\u975e\u522b\u7269\u53ef\u6bd4\uff0c\u6d88\u606f\u521a\u4f20\u51fa\uff0c\u738b\u667a\u8ff0\u4fbf \u77e5\u9053\u6709\u8bb8\u591a\u4eba\u5728\u52a8\u8111\u7b4b\uff0c\u751a\u81f3\u6709\u4e9b\u5df2\u5f52\u9690\u7684\u524d\u8f88\uff0c\u4e5f\u90fd\u6765\u6405\u8fd9\u8d9f\u6d51\u6c34\uff0c\u7686\u56e0\u6b64\u7269\u4e8e\u7ec3\u6b66\u4e4b\u4eba \u5927\u6709\u4e3a\u76ca\uff0c\u738b\u667a\u9011\u5373\u662f\u518d\u591a\u8ba1\uff0c\u4e5f\u662f\u6025\u5f97\u5982\u70ed\u9505\u4e0a\u7684\u8682\u8681\uff0c\u5c24\u5176\u6b64\u7269\u5173\u7cfb\u592a\u5927\uff0c\u4e07\u4e00\u5931\u843d\uff0c \u771f\u662f\u4e0d\u582a\u8bbe\u60f3\u3002 \u3000\u3000\u662f\u6545\u4ed6\u4e00\u89c1\u718a\u501c\uff0c\u975e\u4f46\u6b66\u529f\u6df1\u5999\uff0c\u800c\u4e14\u521d\u51fa\u9053\uff0c\u662f\u4e2a\u96cf\u513f\uff0c\u5bb9\u6613\u7792\u54c4\uff0c\u5c31\u5fc3\u4e2d\u6709\u4e86\u8ba1 \u8f83\uff0c\u60f3\u5229\u7528\u718a\u501c\uff0c\u5c06\u8fd9\u4e2a\u81f3\u5b9d\u5b89\u9001\u81f3\u4eac\u5e08\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u5c31\u7528\u8a00\u8bed\u54c4\u9a97\u718a\u501c\uff0c\u8981\u4ed6\u4e00\u540c\u62bc\u9556\u4eba\u4eac\u3002 \u3000\u3000\u5f53\u665a\uff0c\u7389\u667a\u9011\u5927\u6392\u7b75\u5e2d\u3002\u91d1\u9675\u7684\u9e23\u8fdc\u9556\u5c40\u706f\u706b\u8f89\u714c\uff0c\u6c5f\u5b81\u5730\u9762\u6210\u540d\u7684\u82f1\u96c4\u8c6a\u6770\uff0c\u5dee\u4e0d\u591a \u5168\u88ab\u8bf7\u5230\u3002 \u3000\u3000\u5230\u573a\u7684\u8c6a\u6770\u4eec\u603b\u6709\u4e00\u3001\u4e8c\u5341\u4f4d\uff0c\u5176\u4e2d\u8f83\u8d1f\u76db\u540d\u7684\u6709\u4e1c\u5c71\u53cc\u6770\uff0c\u738b\u6c0f\u5144\u5f1f\uff0c\u957f\u6c5f\u7684\u6c34\u8def\u82f1 \u96c4\u6d6a\u91cc\u795e\u9ec4\u826f\u9a7f\uff0c\u56db\u901a\u9556\u5c40\u7684\u6b63\u526f\u9556\u5934\uff0c\u516b\u624b\u795e\u5200\u5ba2\u5f90\u8446\u7389\uff0c\u98de\u71d5\u5b50\u5f90\u6d9b\uff0c\u4ee5\u53ca\u6c5f\u5b81\u5e9c\u7701\u57ce \u5185\u5916\uff0c\u4e00\u4e07\u591a\u4e2a\u9760\u6a2a\u80f3\u8180\u6df7\u996d\u5403\u7684\u9f99\u5934\u8001\u5927\uff0c\u5c0f\u5c71\u795e\u848b\u6587\u4f1f\uff0c\u6b64\u5916\u8fd8\u6709\u4e00\u4e9b\uff0c\u4e5f\u90fd\u662f\u4e9b\u6210\u540d \u7684\u6c5f\u6e56\u9053\u3002 \u3000\u3000\u7c89\u9762\u82cf\u79e6\u5e26\u7740\u718a\u501c\u5c06\u8fd9\u822c\u4eba\u7269\u4e00\u4e00\u5f15\u89c1\u4e86\uff0c\u800c\u4e14\u5c06\u718a\u501c\u7684\u6b66\u529f\u5938\u5f97\u5929\u4e0a\u5c11\u6709\uff0c\u5730\u4e0a\u65e0 \u53cc\uff0c\u5927\u5bb6\u770b\u4ed6\u53ea\u662f\u5e74\u8f7b\u7684\u5c0f\u4f19\u5b50\uff0c\u867d\u7136\u77e5\u9053\u4ed6\u662f\u661f\u6708\u53cc\u5251\u7684\u8863\u4f53\u4f20\u4eba\uff0c\u4f46\u542c\u7740\u738b\u667a\u9011\u5982\u6b64\u5439 \u5618\uff0c\u5fc3\u91cc\u591a\u5c11\u6709\u4e9b\u6000\u7591\u548c\u85d0\u89c6\uff0c\u4f46\u770b\u5728\u91d1\u9675\u4e09\u6770\u7684\u9762\u4e0a\uff0c\u5bf9\u718a\u501c\u5374\u4e5f\u6781\u529b\u5730\u606d\u7ef4\u3002 \u3000\u3000\u9152\u6765\u9152\u5f80\uff0c\u5927\u5bb6\u559d\u5f97\u5174\u9ad8\u91c7\u70c8\u65f6\uff0c\u5c0f\u5c71\u795e\u848b\u6587\u4f1f\u5ffd\u7136\u7ad9\u4e86\u8d77\u6765\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u5404\u4f4d\u5144 \u5f1f\uff0c\u4eca\u65e5\u627f\u8499\u738b\u603b\u9556\u5934\u5ba0\u53ec\uff0c\u5f97\u5e78\u8bc6\u5f97\u4e86\u8fd9\u7b49\u5c11\u5e74\u82f1\u96c4\uff0c\u6211\u77e5\u9053\u5927\u5bb6\u4e00\u5b9a\u5f88\u75db\u5feb\uff0c\u53ea\u662f\u9152\u8272 \u76f8\u8fde\uff0c\u82f1\u96c4\u5b9a\u5fc5\u8981\u914d\u7f8e\u4eba\uff0c\u4f60\u6211\u4f17\u5bb6\u5144\u5f1f\u867d\u4e0d\u80fd\u79f0\u5f97\u4e0a\u82f1\u96c4\uff0c\u4f46\u4e5f\u5dee\u4e0d\u5230\u54ea\u91cc\u53bb\uff0c\u6211\u4e3b\u5f20\u98de \u67ec\u76f8\u4f20\uff0c\u628a\u79e6\u6dee\u6cb3\u4e0a\u90a3\u4e9b\u5a18\u513f\u4eec\u90fd\u53eb\u4e86\u6765\uff0c\u5927\u5bb6\u5728\u4e00\u5757\u4e50\u4e50\u3002\u201d \u3000\u3000\u4ed6\u8bdd\u521a\u8bf4\u5b8c\uff0c\u7acb\u523b\u5c31\u5f97\u5230\u4e00\u7247\u54c4\u7136\u9644\u4eea\u4e4b\u58f0\uff0c\u6709\u7684\u7adf\u9f13\u8d77\u638c\u6765\u3002 \u3000\u3000\u4e8e\u662f\u5c0f\u5c71\u795e\u66f4\u52a0\u5f97\u610f\uff0c\u53c8\u8bf4\u9053\uff1a\u201c\u542c\u8bf4\u90a3\u91cc\u7684\u82e5\u5170\u6709\u4e2a\u59b9\u59b9\u73b0\u5728\u4e5f\u51fa\u843d\u5f97\u50cf\u6735\u6c34\u8471\u82b1 \u4f3c\uff0c\u628a\u5979\u53eb\u6765\uff0c\u548c\u6211\u4eec\u8fd9\u4f4d\u718a\u8001\u5f1f\u6b63\u662f\u4e00\u5bf9\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u53c8\u662f\u4e00\u58f0\u5927\u7b11\u3002 \u3000\u3000\u7b11\u58f0\u672a\u843d\uff0c\u718a\u501c\u53ed\u5730\u4e00\u62cd\u684c\u5b50\uff0c\u7ad9\u8d77\u6765\u9053\uff1a\u201c\u4f60\u8bf4\u8bdd\u8981\u653e\u5c0a\u91cd\uff0c\u600e\u4e48\u81ea\u79f0\u662f\u82f1\u96c4\uff0c\u5374\u8bf4 \u51fa\u6765\u8fd9\u6837\u4e0d\u8981\u8138\u8bdd\u6765\uff1f\u201d \u3000\u3000\u5c0f\u5c71\u795e\u848b\u6587\u4f1f\uff0c\u5728\u6c5f\u5b81\u5e9c\u4e5f\u7b97\u5f97\u4e0a\u662f\u4e00\u9738\uff0c\u600e\u80fd\u53d7\u5f97\u4e86\u8fd9\u6837\u7684\u8bdd\uff0c\u4e5f\u662f\u4e00\u62cd\u684c\u5b50\uff0c\u7c89\u9762 \u82cf\u79e6\u4e00\u770b\u4e8b\u60c5\u8981\u50f5\uff0c\u8fde\u5fd9\u7ad9\u4e86\u8d77\u6765\uff0c\u9ad8\u58f0\u529d\u9053\uff1a\u201c\u7b97\u4e86\u7b97\u4e86\uff0c\u5927\u5bb6\u90fd\u662f\u81ea\u5df1\u4eba\uff0c\u4ec0\u4e48\u8bdd\u90fd\u597d \u8bf4\u3002\u201d \u3000\u3000\u54ea\u77e5\u848b\u6587\u4f1f\u53c8\u52a0\u4e0a\u4e00\u53e5\uff1a\u201c\u6731\u5bb6\u90a3\u4e24\u4e2a\u81ed\u5a18\u513f\u4eec\uff0c\u8001\u5b50\u6709\u4ec0\u4e48\u8bf4\u4e0d\u5f97\u7684\uff1f\u201d \u3000\u3000\u718a\u501c\u84e6\u5730\u4e00\u8dc3\uff0c\u8eab\u5b50\u4ece\u684c\u9762\u4e0a\u98de\u7eb5\u51fa\u6765\uff0c\u7adf\u4f7f\u51fa\u82cd\u7a79\u5341\u4e09\u5f0f\u4e2d\u7684\u7edd\u6280\uff0c\u8eab\u5f62\u987f\u632b\u4e4b\u4e0b\uff0c \u4ece\u4eba\u7fa4\u4e0a\u98de\u8dc3\u51fa\u53bb\uff0c\u843d\u5728\u5927\u5802\u95e8\u53e3\uff0c\u6307\u7740\u848b\u6587\u4f1f\u8bf4\uff1a\u201c\u4f60\u8fd9\u79cd\u4e0d\u8981\u8138\u7684\u4eba\uff0c\u6211\u4e5f\u4e0d\u7528\u548c\u4f60\u591a \u8bf4\uff0c\u8d76\u5feb\u8ddf\u6211\u6eda\u51fa\u6765\uff0c\u8ba9\u6211\u6559\u8bad\u6559\u8bad\u4f60\u3002\u201d \u3000\u3000\u718a\u501c\u521d\u663e\u8eab\u624b\uff0c\u5c31\u9707\u4f4f\u4e86\u6ee1\u5802\u7fa4\u8c6a\uff0c\u8fde\u7d20\u4ee5\u8f7b\u529f\u8457\u79f0\u7684\u7c89\u9762\u82cf\u79e6\u738b\u667a\u9011\uff0c\u548c\u98de\u71d5\u5b50\u5f90 \u6d9b\uff0c\u4e00\u770b\u718a\u501c\u7684\u8eab\u6cd5\uff0c\u90fd\u6697\u53f9\u5dee\u5f97\u592a\u8fdc\uff0c\u5c0f\u5c71\u795e\u848b\u6587\u4f1f\u770b\u4e86\u4e5f\u662f\u5fc3\u60ca\uff0c\u4f46\u4ed6\u5230\u5e95\u662f\u4e2a\u6210\u540d\u4eba \u7269\uff0c\u5728\u6c5f\u5b81\u5e9c\u4e5f\u662f\u8dfa\u8dfa\u811a\u56db\u57ce\u4e71\u98a0\u7684\u4eba\u7269\uff0c\u4eba\u5bb6\u6307\u540d\u9a82\u9635\uff0c\u600e\u80fd\u7f29\u5934\u4e0d\u51fa\u5462\uff1f\u5934\u76ae\u4e00\u786c\uff0c\u4ed6 \u53ef\u6ca1\u6709\u8fd9\u4efd\u529f\u529b\u98de\u8dc3\u51fa\u6765\uff0c\u4f17\u76ee\u6240\u6ce8\u4e4b\u4e0b\uff0c\u4e00\u811a\u8e22\u5f00\u684c\u5b50\uff0c\u9a82\u9053\uff1a\u201c\u6562\u60c5\u90a3\u5a4a\u5b50\u662f\u4f60\u7684\u5927\u59b9 \u5b50\u3002\u201d\u4eba\u4e5f\u968f\u7740\u7eb5\u4e86\u51fa\u53bb\u3002\u5c0f\u5c71\u795e\u521a\u7eb5\u51fa\u53bb\uff0c\u718a\u501c\u7684\u8eab\u8eaf\u5df2\u76d8\u65cb\u5728\u4ed6\u5934\u4e0a\uff0c\u4ed6\u614c\u4e71\u4e4b\u4e0b\uff0c\u8eab \u8eaf\u4e00\u77ee\uff0c\u4e3e\u624b\u4e00\u683c\uff0c\u4e00\u62db\u201c\u9738\u738b\u5378\u7532\u201d\uff0c\u4f46\u62db\u5f0f\u5c1a\u672a\u7528\u5b8c\uff0c\u5c31\u89c9\u5f97\u624b\u5df2\u88ab\u4eba\u64d2\u4f4f\uff0c\u63a5\u7740\u4e00\u9635 \u75db\u5f7b\u7684\u75db\u82e6\uff0c\u968f\u5373\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u738b\u667a\u9011\u8fd9\u624d\u8dd1\u4e86\u51fa\u53bb\uff0c\u4e00\u770b\u4e4b\u4e0b\uff0c\u5c0f\u5c71\u795e\u7684\u4e00\u6761\u53f3\u81c2\u88ab\u718a\u501c\u751f\u751f\u5730\u6298\u65ad\u4e86\uff0c\u4e0d\u7981\u7709\u5934\u4e00 \u76b1\uff0c\u770b\u4e86\u718a\u501c\u4e00\u773c\uff0c\u89c1\u718a\u501c\u4ecd\u7136\u6012\u76ee\u6ce8\u89c6\u7740\u5c0f\u5c71\u795e\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u6731\u5bb6\u59d0\u59b9\u5b9a\u662f\u548c \u718a\u501c\u6709\u7740\u6df1\u5207\u7684\u5173\u7cfb\uff0c\u4e0d\u7136\u4e0d\u4f1a\u522b\u4eba\u7a0d\u4e00\u4fae\u8fb1\u5230\u5979\u4eec\u4e24\u4eba\uff0c\u4ed6\u5c31\u4f1a\u5982\u6b64\u7684\u6124\u6068\u3002\u53ef\u662f\u6211\u4e45\u5728 \u91d1\u9675\uff0c\u6731\u5bb6\u59d0\u59b9\u90a3\u91cc\u6211\u4e5f\u5e38\u53bb\uff0c\u600e\u4f1a\u5bf9\u6b64\u6beb\u4e0d\u77e5\u60c5\u5462\uff1f\u8fd9\u5012\u8981\u4ed4\u7ec6\u6253\u542c\u6253\u542c\u3002\u201d \u3000\u3000\u5927\u5802\u91cc\u7684\u706f\u706b\uff0c\u628a\u9662\u5b50\u7167\u5f97\u5b9b\u5982\u767d\u663c\uff0c\u8fd9\u4e48\u591a\u4eba\u7ad9\u5728\u9662\u5b50\u91cc\uff0c\u7adf\u6ca1\u6709\u4e00\u4e2a\u51fa\u58f0\u53d1\u8bdd\u7684\uff0c \u738b\u667a\u8ff0\u770b\u7740\u5012\u5367\u5728\u5730\u4e0a\u7684\u5c0f\u5c71\u795e\uff0c\u60f3\u65e5\u540e\u957f\u7684\u7ea0\u7eb7\uff0c\u4f46\u4ed6\u4e3a\u4e86\u8981\u5c06\u6210\u5f62\u9996\u4e4c\u9001\u81f3\u4eac\u5e08\uff0c\u5176\u4ed6 \u7684\u4efb\u4f55\u4e8b\uff0c\u4ed6\u90fd\u4e0d\u80fd\u987e\u53ca\u4e86\uff0c\u4f55\u51b5\u4ed6\u5728\u6c5f\u5b81\u5e9c\uff0c\u5b98\u79c1\u670b\u53cb\u90fd\u6781\u591a\uff0c\u52bf\u529b\u53c8\u975e\u5c0f\u5c71\u795e\u80fd\u6bd4\uff0c\u4ed6 \u81ea\u4fe1\u8fdc\u80fd\u628a\u8fd9\u4ef6\u4e8b\u538b\u4e0b\u53bb\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u5fc3\u80f8\u4e00\u655e\uff0c\u5f00\u8a00\u7b11\u9053\uff1a\u201c\u848b\u6587\u4f1f\u81ea\u8ba8\u6ca1\u8da3\uff0c\u5403\u4e86\u82e6\u5934\uff0c\u53ef\u662f\u5404\u4f4d\u8fde\u5e26\u5728\u4e0b\u90fd\u6cbe\u4e86 \u4ed6\u7684\u5149\uff0c\u5f97\u4ee5\u80fd\u591f\u770b\u89c1\u6b66\u6797\u4e2d\u7f55\u89c1\u7684\u2018\u82cd\u7a79\u5341\u4e09\u5f0f\u2019\u7684\u7edd\u6280\uff0c\u5404\u4f4d\u522b\u626b\u4e86\u5174\uff0c\u8fd8\u662f\u559d\u6211\u4eec\u7684 \u9152\u5427\u3002\u201d \u3000\u3000\u4ed6\u53c8\u5429\u5490\u9556\u4f19\u9053\uff1a\u201c\u628a\u848b\u5927\u7237\u7528\u8f86\u8f66\u9001\u56de\u53bb\uff0c\u544a\u8bc9\u4ed6\u7684\u5f1f\u5144\uff0c\u4ec0\u4e48\u5e10\u90fd\u7b97\u5728\u6211\u59d3\u738b\u7684\u5e10 \u4e0a\uff0c\u201d\u4f17\u4eba\u4e00\u89c1\uff0c\u4e8b\u60c5\u5df2\u4e86\uff0c\u65e2\u7136\u4e8b\u4e0d\u5173\u5df1\uff0c\u800c\u4e14\u718a\u501c\u8fd9\u4e00\u65bd\u7edd\u6280\u540e\uff0c\u9a6c\u4e0a\u6210\u4e86\u7fa4\u8c6a\u4e89\u6b32\u7ed3 \u4ea4\u7684\u5bf9\u8c61\uff0c\u4e8e\u662f\u4ed6\u4eec\u8702\u62e5\u7740\u718a\u501c\uff0c\u91cd\u56de\u5230\u5802\u4e0a\uff0c\u4f17\u53e3\u7eb7\u7eb7\uff0c\u8c08\u7684\u83ab\u4e0d\u662f\u8d5e\u718a\u501c\u7684\u6b66\u529f\uff0c\u738b\u667a \u9011\u89c1\u8ba1\u5df2\u5f97\u6388\uff0c\u4e0d\u7981\u5fc3\u82b1\u6012\u653e\uff0c\u628a\u4e2a\u718a\u501c\u66f4\u662f\u6367\u4e0a\u4e86\u5929\u3002 \u3000\u3000\u5e2d\u7ec8\u4eba\u6563\u540e\uff0c\u718a\u501c\u72ec\u8eab\u8eba\u5728\u5e8a\u4e0a\uff0c\u53e3\u5fc6\u4ed6\u4e00\u5929\u7684\u906d\u9047\uff0c\u4ed6\u4ecd\u662f\u4e2a\u9ed8\u9ed8\u65e0\u95fb\u7684\u9752\u5e74\uff0c\u9664\u4e86 \u6731\u5bb6\u59d0\u59b9\u5916\uff0c\u4ed6\u7684\u884c\u4e3a\uff0c\u6ca1\u6709\u5f71\u54cd\u8fc7\u4efb\u4f55\u4eba\uff0c\u4e5f\u6ca1\u6709\u4efb\u4f55\u4eba\u5f71\u54cd\u8fc7\u4ed6\uff0c\u53ef\u662f\u6b64\u523b\uff0c\u4ed6\u5374\u6210\u4e86 \u4eba\u7fa4\u4e2d\u7684\u82f1\u96c4\uff0c\u5df2\u6709\u4e24\u4e2a\u4eba\u7684\u7ec8\u751f\uff0c\u5728\u4ed6\u7684\u624b\u4e2d\u6539\u53d8\u4e86\u547d\u8fd0\uff0c\u800c\u4ed6\u7684\u547d\u8fd0\uff0c\u4e5f\u88ab\u522b\u4eba\u67d3\u4e0a\u4e86 \u9c9c\u660e\u7684\u8272\u5f69\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u72ec\u81ea\u7b11\u4e86\u3002 \u3000\u3000\u6302\u5728\u58c1\u4e0a\u7684\u4e00\u76cf\u5e76\u4e0d\u5341\u5206\u660e\u4eae\u7684\u6cb9\u706f\uff0c\u660f\u9ec4\u7684\u706f\u5149\u900f\u8fc7\u7eb1\u5e10\u7167\u5728\u4ed6\u7684\u8138\u4e0a\uff0c\u7ecf\u8fc7\u8fd9\u591a\u5f69 \u7684\u4e00\u5929\uff0c\u4ed6\u7684\u9762\u5bb9\u597d\u50cf\u6210\u719f\u591a\u4e86\uff0c\u4ed6\u7ffb\u4e86\u4e2a\u8eab\uff0c\u5de6\u624b\u6380\u5f00\u5e10\u5b50\uff0c\u53f3\u624b\u671d\u90a3\u6cb9\u706f\u4e00\u6325\uff0c\u706f\u706b\u7acb \u5373\u7184\u4e86\u3002 \u3000\u3000\u5c4b\u91cc\u987f\u65f6\u6697\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u718a\u501c\u51fb\u4f24\u5c0f\u5c71\u795e\u7684\u4e8b\uff0c\u7b2c\u4e8c\u5929\u5c31\u4f20\u904d\u4e86\u5927\u6c5f\u5357\u5317\uff0c\u5927\u5bb6\u90fd\u77e5\u9053\u718a\u501c\u7684\u540d\u5b57\u3002 \u3000\u3000\u8fd9\u4e9b\u90fd\u662f\u738b\u667a\u9011\u65e9\u5df2\u6599\u5230\u7684\uff0c\u7b49\u5230\u8fd9\u6d88\u606f\u5df2\u7ecf\u6563\u5f00\u4e86\u7684\u65f6\u5019\uff0c\u4ed6\u5c31\u51b3\u5b9a\u52a8\u8eab\u542f\u7a0b\uff0c\u4ed6\u81ea \u7136\u5148\u548c\u718a\u501c\u8bf4\u597d\u4e86\u53ef\u662f\u4ed6\u7684\u4e00\u5207\u6253\u7b97\uff0c\u548c\u4ed6\u771f\u6b63\u7684\u8ba1\u7b56\uff0c\u9664\u4e86\u4ed6\u81ea\u5df1\u672c\u4eba\u4e4b\u5916\uff0c\u8c01\u4e5f\u65e0\u6cd5\u77e5 \u9053\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u4eec\u8981\u8d70\u7684\u5934\u4e00\u5929\uff0c\u6c5f\u5b81\u5e9c\u6765\u4e86\u4e24\u4e2a\u6c5f\u6e56\u4e0a\u6709\u540d\u6c14\u7684\u4eba\u7269\uff0c\u662f\u6c5f\u82cf\u864e\u90b1\u98de\u7075\u5821\u7684\u4e1c \u65b9\u5144\u59b9\uff0c\u51fa\u5c18\u5251\u5ba2\u4e1c\u65b9\u7075\uff0c\u548c\u4ed6\u7684\u59b9\u59b9\u7c89\u8776\u4e1c\u65b9\u745b\u3002\u7c89\u8776\u4e1c\u65b9\u745b\uff0c\u9664\u4e86\u5251\u6cd5\u4e0d\u5f31\uff0c\u8fd8\u51ed\u7740\u7075 \u5de7\u7684\u5fc3\u601d\uff0c\u6253\u9020\u4e86\u513f\u4e2a\u5947\u602a\u5916\u95e8\u6697\u5668\uff0c\u800c\u4e14\u75be\u6076\u5982\u4ec7\uff0c\u78b0\u5230\u5979\u624b\u5e95\u4e0b\u7684\u6076\u5f92\uff0c\u5341\u4e5d\u96be\u9003\u516c \u9053\uff0c\u4e0d\u50cf\u5979\u54e5\u54e5\uff0c\u4ec0\u4e48\u4e8b\u90fd\u662f\u4ec1\u4e49\u4e3a\u6000\uff0c\u5f97\u9976\u4eba\u5904\uff0c\u603b\u662f\u7f51\u5f00\u4e00\u7ebf\u3002 \u3000\u3000\u4ee5\u6b64\u4e24\u4eba\u4e4b\u58f0\u671b\uff0c\u5c45\u7136\u4f1a\u6765\u62dc\u8bbf\u718a\u501c\uff0c\u8fd9\u5012\u662f\u51fa\u4e4e\u7c89\u9762\u82cf\u79e6\u7684\u610f\u6599\u4e4b\u5916\uff0c\u4ed6\u5fc3\u4e2d\u4e00\u5219\u4ee5 \u559c\uff0c\u4e00\u5219\u4ee5\u60e7\uff0c\u559c\u7684\u662f\u718a\u501c\u5c45\u7136\u60ca\u52a8\u4e86\u5982\u6b64\u4eba\u7269\uff0c\u6015\u7684\u662f\u718a\u501c\uff0c\u4e00\u4e2a\u5e94\u4ed8\u4e0d\u6765\uff0c\u4ed6\u6240\u82e6\u5fc3\u7b56 \u5212\u7684\u4e00\u4e9b\u4e8b\u60c5\uff0c\u975e\u4f46\u4e0d\u80fd\u5b9e\u884c\uff0c\u800c\u4e14\u53cd\u800c\u5f04\u5de7\u6210\u62d9\u4e86\u3002 \u3000\u3000\u738b\u667a\u9011\u5f88\u614e\u91cd\u5730\u53bb\u627e\u718a\u501c\uff0c\u544a\u8bc9\u4ed6\u6709\u4e24\u4e2a\u5982\u6b64\u7684\u4eba\u7269\uff0c\u5c31\u8981\u6765\u770b\u4ed6\u4e86\uff0c\u800c\u4e14\u8fd8\u518d\u4e09\u53ee \u549b\uff0c\u5343\u4e07\u4e0d\u53ef\u4efb\u610f\u884c\u4e8b\u3002 \u3000\u3000\u9ec4\u660f\uff0c\u79cb\u9633\u5df2\u843d\uff0c\u665a\u971e\u7eda\u4e3d\uff0c\u707f\u70c2\u7684\u5927\u5730\u591a\u5f69\u8f89\u714c\uff0c\u4e1c\u65b9\u7075\u767d\u886b\u767d\u5c65\uff0c\u5e26\u7740\u4e00\u8eab\u7c89\u7ea2\u52b2 \u88c5\u7684\u4e1c\u65b9\u745b\uff0c\u8f7b\u9a91\u7b80\u4ece\uff0c\u6084\u7136\u6765\u5230\u9e23\u8fdc\u9576\u5c40\u3002 \u3000\u3000\u4e1c\u65b9\u7075\u548c\u7c89\u9762\u82cf\u79e6\u738b\u667a\u9011\u3001\u65ad\u9b42\u5251\u5434\u8bcf\u4e91\u90fd\u6709\u4e00\u9762\u4e4b\u7f18\uff0c\u6240\u5fc3\u4ee5\u4e00\u89c1\u9762\u5c31\u62f1\u624b\u5411\u738b\u667a\u9011 \u7b11\u9053\uff1a\u201c\u6709\u52b3\u603b\u9556\u5934\u8fdc\u8fce\uff0c\u5b9e\u662f\u4e2d\u96be\u5b89\uff0c\u5c0f\u5f1f\u4e5f\u5b9e\u662f\u5192\u6627\uff0c\u9aa4\u7136\u5c31\u6765\u6253\u6270\uff0c\u8fd8\u8bf7\u603b\u9556\u5934\u6d77 \u6db5\u3002\u201d \u3000\u3000\u738b\u667a\u9011\u9053\uff1a\u201c\u5821\u4e3b\u8fd1\u6765\u53ef\u5b89\u597d\uff0c\u600e\u4e48\u5bf9\u5c0f\u5f1f\u8bf4\u8fd9\u7b49\u8bdd\uff0c\u50cf\u5821\u4e3b\u8fd9\u6837\u8bf7\u90fd\u4e0d\u80fd\u8bf7\u5230\u7684\uff0c\u4eca \u65e5\u80fd\u5149\u4e34\u655d\u5c40\uff0c\u5c0f\u5f1f\u771f\u662f\u9ad8\u5174\u6781\u4e86\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u4ed6\u4e00\u770b\u7c89\u8776\u4e1c\u65b9\u745b\u8fd8\u8fdc\u8fdc\u7ad9\u5728\u90a3\u8fb9\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u90a3\u8fb9\u7ad9\u7684\uff0c\u60f3\u5fc5\u5c31\u662f\u4e1c\u65b9\u5973\u4fa0 \u4e86\uff0c\u8d76\u5feb\u8bf7\u8fc7\u6765\uff0c\u8ba9\u5c0f\u5f1f\u89c1\u89c1\u4e45\u4ef0\u5927\u540d\u7684\u5973\u82f1\u96c4\u3002\u201d \u3000\u3000\u4e1c\u65b9\u7075\u7b11\u7740\u8c26\u865a\uff0c\u62db\u624b\u5c06\u4e1c\u65b9\u745b\u53eb\u4e86\u8fc7\u6765\uff0c\u4e1c\u65b9\u672c\u662f\u4e16\u5bb6\uff0c\u5bb6\u6559\u6781\u4e25\uff0c\u4e1c\u65b9\u745b\u867d\u662f\u4e2a\u5929 \u4e0d\u6015\u3001\u5730\u4e0d\u6015\u7684\u5973\u5b69\u5b50\uff0c\u552f\u72ec\u5bf9\u4e8e\u54e5\u54e5\uff0c\u5374\u662f\u6015\u5f97\u8981\u6b7b\u3002 \u3000\u3000\u6b64\u523b\u5979\u7ad9\u5728\u4e1c\u65b9\u7075\u8eab\u540e\uff0c\u4e00\u526f\u89c4\u89c4\u77e9\u77e9\u7684\u6837\u5b50\uff0c\u8c01\u4e5f\u770b\u4e0d\u51fa\uff0c\u5979\u7adf\u662f\u6c5f\u6e56\u4e2d\u51fa\u540d\u96be\u60f9\u7684 \u4eba\u7269\u3002 \u3000\u3000\u8fdb\u5230\u5802\u4e0a\uff0c\u738b\u667a\u9011\u8fd9\u624d\u5c06\u718a\u501c\u5f15\u89c1\u7ed9\u4e1c\u65b9\u7075\u5144\u59b9\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u5c31\u662f\u6b66\u6797\u4e2d\u7684\u6cf0\u5c71\u5317 \u6597\uff0c\u6c5f\u82cf\u864e\u90b1\u98de\u7075\u5821\u7684\u4e1c\u65b9\u5821\u4e3b\u5144\u59b9\uff0c\u8fd9\u5c31\u662f\u8fd1\u65e5\u6765\u4f20\u540d\u6c5f\u6e56\u7684\u718a\u501c\uff0c\u5e0c\u671b\u4f60\u4eec\u591a\u4eb2\u8fd1\u4eb2 \u8fd1\u3002\u201d \u3000\u3000\u718a\u501c\u5f88\u8c28\u614e\uff0c\u4f46\u6beb\u4e0d\u614c\u5f20\u5730\u548c\u4ed6\u4eec\u5ba2\u5957\u4e00\u756a\uff0c\u4ed4\u7ec6\u5730\u6253\u91cf\u4e1c\u65b9\u5144\u59b9\uff0c\u89c1\u4e1c\u65b9\u7075\u624d\u4e09\u5341\u5c81 \u4e0d\u5230\uff0c\u751f\u5f97\u4fca\u79c0\u5df2\u6781\uff0c\u5c24\u5176\u662f\u4e30\u795e\u6f47\u6d12\uff0c\u771f\u662f\u98d8\u98d8\u6709\u51fa\u5c18\u4e4b\u6168\uff0c\u4e0d\u6127\u540d\u4e3a\u51fa\u5c18\u5251\u5ba2\u3002 \u3000\u3000\u800c\u4e1c\u65b9\u745b\u5374\u4e8c\u5341\u672a\u5230\uff0c\u718a\u501c\u89c1\u5979\u8eab\u6750\u70af\u5a1c\uff0c\u9762\u5b54\u5374\u4e0d\u6562\u4ed4\u7ec6\u6253\u91cf\uff0c\u53ea\u89c9\u5f97\u5979\u4e24\u9053\u773c\u5149\uff0c \u5b9b\u5982\u5229\u526a\uff0c\u53ea\u76ef\u7740\u81ea\u5df1\uff0c\u5413\u5f97\u4ed6\u8d76\u7d27\u4f4e\u4e0b\u5934\u53bb\u3002 \u3000\u3000\u4e1c\u65b9\u7075\u5c06\u718a\u501c\u4e5f\u4e0a\u4e0a\u4e0b\u4e0b\u770b\u4e86\u4e00\u904d\uff0c\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u5144\u5f1f\u8fd1\u65e5\u542c\u5f97\u6c5f\u6e56\u8fc7\u5ba2\u4f20\u8a00\uff0c\u8bf4\u6c5f\u5b81 \u5e9c\u51fa\u4e86\u4e2a\u5c11\u5e74\u82f1\u96c4\uff0c\u5fc3\u91cc\u9ad8\u5174\u5df2\u6781\uff0c\u6068\u4e0d\u5f97\u9a6c\u4e0a\u80fd\u5f97\u89c1\u9ad8\u4eba\uff0c\u50cf\u718a\u5144\u8fd9\u6837\u7684\u4eba\u7269\uff0c\u5144\u5f1f\u8d70\u904d \u5927\u6c5f\u5357\u5317\uff0c\u5012\u771f\u662f\u5934\u4e00\u6b21\u89c1\u5230\u3002\u201d\u8bf4\u5b8c\uff0c\u671d\u7740\u4e1c\u65b9\u745b\u4e00\u7b11\u3002 \u3000\u3000\u4e1c\u65b9\u745b\u5374\u4e5f\u8fde\u5fd9\u4f4e\u4e0b\u5934\u53bb\uff0c\u7ea2\u751f\u53cc\u988a\uff0c\u7adf\u50cf\u7f9e\u5f97\u62ac\u4e0d\u8d77\u5934\u6765\u3002 \u3000\u3000\u7c89\u9762\u82cf\u79e6\u662f\u4f55\u7b49\u4eba\u7269\uff0c\u4e24\u773c\u4e00\u8f6c\uff0c\u5fc3\u4e0b\u5f53\u65f6\u604d\u7136\u5927\u609f\uff0c\u6697\u7b11\u9053\uff1a\u201c\u597d\u4e2a\u51fa\u5c18\u5251\u5ba2\uff0c\u6211\u5f53 \u4ed6\u771f\u662f\u82f1\u96c4\u76f8\u60dc\u7279\u5730\u6765\u62dc\u8bbf\u718a\u501c\uff0c\u5374\u4e0d\u77e5\u4ed6\u662f\u66ff\u59b9\u59b9\u6765\u627e\u59b9\u4e08\u7684\uff0c\u4f60\u65e2\u6709\u6b64\u5fc3\uff0c\u6211\u4e5f\u4e0d\u59a8\u8d77 \u8d77\u54c4\uff0c\u843d\u5f97\u7686\u5927\u6b22\u559c\uff0c\u82e5\u718a\u501c\u771f\u6210\u4e86\u4e1c\u65b9\u5821\u4e3b\u7684\u597d\u59b9\u592b\uff0c\u90a3\u6211\u7684\u90a3\u8d9f\u9556\uff0c\u4e0d\u5fc5\u518d\u7528\u522b\u7684\u82b1 \u6837\uff0c\u5c31\u86ee\u4fdd\u9669\u7684\u4e86\u3002\u4ed6\u601d\u91cf\u81f3\u6b64\uff0c\u4e8e\u662f\u4ed6\u7b11\u7740\u9644\u548c\u9053\uff1a\u201c\u5821\u4e3b\u7684\u773c\u5149\u679c\u7136\u4e0d\u5dee\uff0c\u6211\u8fd9\u4f4d\u8d24\u5f1f \u4e0d\u4f46\u6b66\u529f\u6ca1\u5f97\u8bdd\u8bf4\uff0c\u800c\u4e14\u6587\u624d\u4e5f\u597d\uff0c\u771f\u53ef\u8bf4\u662f\u6587\u6b66\u53cc\u5168\u4e86\u3002\u201d \u3000\u3000\u4e1c\u65b9\u7075\u54e6\u4e86\u4e00\u58f0\uff0c\u76ef\u4e86\u4e1c\u65b9\u745b\u4e00\u773c\uff0c\u770b\u89c1\u5979\u90a3\u526f\u6837\u5b50\uff0c\u4e0d\u7981\u7b11\u4e86\uff0c\u4ed6\u4eec\u5144\u59b9\u611f\u60c5\u7d20\u597d\uff0c \u8fd9\u6b21\u6765\u8bbf\u718a\u501c\u5012\u771f\u88ab\u738b\u667a\u9011\u6599\u4e2d\u4e86\uff0c\u662f\u60f3\u66ff\u4ed6\u7684\u8fd9\u4f4d\u59b9\u59b9\u627e\u4e00\u4e2a\u5982\u610f\u7684\u90ce\u541b\u3002 \u3000\u3000\u7531\u4e8e\u4e1c\u65b9\u745b\u4eba\u65e2\u806a\u660e\uff0c\u6b66\u529f\u53c8\u9ad8\uff0c\u518d\u52a0\u4e0a\u662f\u51fa\u540d\u7684\u5201\u86ee\u6027\u5b50\uff0c\u5e73\u5e38\u7684\u4eba\uff0c\u5979\u4e0d\u4f1a\u770b\u5728\u773c \u91cc\uff0c\u4e1c\u65b9\u7075\u672c\u5c5e\u610f\u5929\u5c71\u7684\u795e\u9f99\u51b7\u5982\u6c34\uff0c\u53ea\u662f\u4e1c\u65b9\u745b\u5374\u4e00\u4e07\u4e2a\u4e0d\u613f\u610f\uff0c\u53ea\u8981\u5979\u770b\u5230\u51b7\u5982\u6c34\uff0c\u5c31 \u60f3\u5c3d\u65b9\u6cd5\u907f\u5f00\u4ed6\uff0c\u800c\u51b7\u5982\u6c34\uff0c\u4e5f\u6c38\u8fdc\u662f\u90a3\u526f\u51b7\u51b0\u51b0\u7684\u6837\u5b50\uff0c\u8fd9\u6837\u4e1c\u65b9\u7075\u4e5f\u662f\u65e0\u6cd5\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u542c\u5230\u6c5f\u5b81\u5e9c\u51fa\u4e86\u4e2a\u5c11\u5e74\u82f1\u96c4\uff0c\u7aef\u7684\u5341\u5206\u4e86\u5f97\uff0c\u4ed6\u9a6c\u4e0a\u5c31\u60f3\u8d77\u6765\u59b9\u59b9\u7684\u7ec8\u8eab\u5927\u4e8b\uff0c \u8fd9\u624d\u5e26\u7740\u4e1c\u65b9\u745b\u76f4\u5954\u6c5f\u5b81\u3002 \u3000\u3000\u4ed6\u4e00\u773c\u770b\u5230\u718a\u501c\uff0c\u5c31\u77e5\u786e\u975e\u51e1\u54c1\uff0c\u53ef\u662f\u4ed6\u5fc3\u91cc\u8fd8\u662f\u5728\u60f3\uff1a\u201c\u6b64\u4eba\u5e74\u7eaa\u592a\u8f7b\uff0c\u6700\u591a\u4e5f\u53ea\u6709 \u5341\u516d\u3001\u4e03\u5c81\uff0c\u53ea\u6015\u4e0d\u592a\u597d\u2026\u2026\u201d\u8f6c\u5ff5\u53c8\u60f3\uff1a\u201c\u4f46\u4e24\u4eba\u82e5\u662f\u76f8\u914d\uff0c\u770b\u6211\u59b9\u5b50\u7684\u6837\u5b50\uff0c\u53c8\u975e\u65e0 \u610f\uff0c\u90a3\u4e48\u5e74\u9f84\u53c8\u6709\u4f55\u59a8\uff1f\u201d \u3000\u3000\u987b\u77e5\u8d8a\u662f\u751f\u6027\u5014\u5f3a\u7684\u5973\u5b69\u5b50\uff0c\u53cd\u800c\u4f1a\u559c\u6b22\u8f83\u6e29\u67d4\u7684\u7537\u5b69\u5b50\uff0c\u4e1c\u65b9\u745b\u4e45\u5386\u6c5f\u6e56\uff0c\u6240\u89c1\u5230\u4e0d \u662f\u8d73\u8d73\u6b66\u592b\uff0c\u5c31\u662f\u4e9b\u751f\u5177\u5947\u50fb\u4e2a\u6027\u7684\u4eba\uff0c\u662f\u4ee5\u5979\u4e00\u89c1\u718a\u501c\uff0c\u5728\u6e29\u67d4\u4e2d\u4e0d\u5931\u7537\u513f\u672c\u8272\uff0c\u800c\u53c8\u662f \u4e2a\u82f1\u4fca\u7684\u5c11\u5e74\uff0c\u5c31\u4e00\u89c1\u800c\u503e\u5fc3\u4e86\uff0c\u8fd9\u5c31\u662f\u4eba\u7684\u7f18\u4efd\u3002 \u3000\u3000\u53ef\u662f\u718a\u501c\u5374\u832b\u7136\u4e0d\u77e5\u8fd9\u4e9b\uff0c\u4ed6\u7684\u5fc3\u91cc\uff0c\u5df2\u7ecf\u88ab\u82e5\u99a8\u5360\u53bb\u4e86\u4e00\u534a\uff0c\u53e6\u5916\u7684\u90a3\u534a\uff0c\u4e5f\u4ff1\u662f\u590d \u4ec7\u4e0e\u96ea\u6068\uff0c\u626c\u540d\u6c5f\u6e56\u7684\u58ee\u5fd7\uff0c\u5df2\u4e0d\u518d\u6709\u591a\u4f59\u7684\u5730\u65b9\uff0c\u6765\u5bb9\u7eb3\u4e1c\u65b9\u745b\u7684\u8fd9\u4e00\u4efd\u67d4\u60c5\u3002 \u3000\u3000\u4ed6\u5c3d\u91cf\u907f\u5f00\u4e1c\u65b9\u5144\u59b9\u5bf9\u4ed6\u6295\u6765\u7684\u76ee\u5149\uff0c\u5fc3\u4e2d\u6742\u4e71\u5730\u5728\u60f3\u4e00\u4e9b\u4e8b\uff0c\u8fde\u4ed6\u4eec\u6240\u8bf4\u7684\u8bdd\uff0c\u4e5f\u6ca1 \u7559\u5fc3\u542c\uff0c\u7c89\u9762\u82cf\u79e6\u53e3\u624d\u867d\u4f73\uff0c\u5374\u4e0d\u662f\u4e1c\u65b9\u7075\u8bf4\u8bdd\u7684\u5bf9\u8c61\uff0c\u8c08\u4e86\u4e00\u4f1a\uff0c\u4e1c\u65b9\u7075\u59cb\u7ec8\u672a\u80fd\u5c06\u8bdd\u8f6c \u5165\u6b63\u9898\uff0c\u8fd9\u624d\u6025\u574f\u4e86\u4e1c\u65b9\u745b\uff0c\u5979\u867d\u5bf9\u718a\u501c\u6709\u610f\uff0c\u4f46\u4e00\u4e2a\u5973\u5b69\u513f\u5bb6\uff0c\u603b\u4e0d\u80fd\u5148\u5411\u5bf9\u65b9\u5f00\u53e3\u3002 \u3000\u3000\u8fd9\u6837\u8c08\u4e86\u4e00\u4f1a\uff0c\u4e1c\u65b9\u7075\u60f3\u9053\uff1a\u201c\u8fd9\u79cd\u4e8b\u6700\u662f\u6027\u6025\u4e0d\u5f97\uff0c\u53cd\u6b63\u6765\u65e5\u65b9\u957f\uff0c\u65e5\u540e\u4e0d\u6015\u6ca1\u6709\u673a \u4f1a\uff0c\u4f55\u51b5\u7c89\u9762\u82cf\u79e6\u82e5\u679c\u77e5\u9053\uff0c\u4e5f\u5b9a\u4f1a\u5728\u6697\u4e2d\u4fc3\u6210\uff0c\u56e0\u4e3a\u8fd9\u5bf9\u4ed6\u4e5f\u662f\u6709\u5229\u7684\u4e8b\uff0c\u4e0d\u5982\u6682\u4e14\u56de \u53bb\uff0c\u65e5\u540e\u518d\u505a\u6253\u7b97\u3002\u201d \u3000\u3000\u4e8e\u662f\u4ed6\u7ad9\u8d77\u8eab\u6765\uff0c\u5411\u7c89\u9762\u82cf\u79e6\u8bf4\u9053\uff1a\u6253\u6270\u5df2\u4e45\uff0c\u4e5f\u8be5\u544a\u8f9e\u4e86\uff0c\u65e5\u540e\u5f97\u7a7a\uff0c\u5343\u4e07\u8bf7\u5230\u655d\u5904 \u5750\u5750\uff0c\u5c0f\u5f1f\u8fd8\u6709\u4e8b\u76f8\u6258\u3002\u201c\u4e1c\u65b9\u745b\u4e00\u542c\u54e5\u54e5\u8981\u8d70\uff0c\u5fc3\u91cc\u867d\u4e0d\u613f\u610f\uff0c\u4f46\u4e5f\u65e0\u6cd5\u3002\u53ea\u5f97\u4e5f\u7ad9\u4e86\u8d77 \u6765\uff0c\u72e0\u72e0\u76ef\u4e86\u718a\u501c\u4e00\u773c\uff0c\u6697\u60f3\u5230\uff1a\u201c\u4f60\u5012\u8bf4\u8bf4\u8bdd\u5440\uff0c\u6211\u5bf9\u4f60\u7684\u610f\u601d\uff0c\u4f60\u5c31\u662f\u4e0d\u77e5\u9053\uff0c\u4e5f\u8be5\u8bf4 \u8bf4\u8bdd\u5440\u3002\u201d \u3000\u3000\u738b\u667a\u9011\u8fde\u5fd9\u4e5f\u7ad9\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5821\u4e3b\u6b64\u523b\u600e\u5730\u5c31\u8981\u8d70\u4e86\uff0c\u5c0f\u5f1f\u9884\u5907\u5f97\u4e00\u4e9b\u6c34\u9152\uff0c\u5343\u4e07\u8bf7 \u5821\u4e3b\u8d4f\u5149\uff0c\u6b64\u523b\u5c31\u8d70\uff0c\u672a\u514d\u77a7\u4e0d\u8d77\u5c0f\u5f1f\u4e86\u3002\u201d \u3000\u3000\u4e1c\u65b9\u7075\u7b11\u8bf4\u9053\uff1a\u201c\u4e0d\u7528\u4e86\uff0c\u603b\u9556\u5934\u76db\u60c5\uff0c\u5728\u4e0b\u5fc3\u9886\uff0c\u53ea\u662f\u5c0f\u5f1f\u8fd8\u6709\u4e9b\u4fd7\u4e8b\uff0c\u4e0b\u6b21\u5b9a\u518d\u6765 \u6253\u6270\u3002\u201d\u8bf4\u5b8c\uff0c\u4ed6\u53c8\u671d\u718a\u501c\u4e00\u62f1\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u4eca\u65e5\u5f97\u4f1a\uff0c\u5b9e\u662f\u5feb\u6170\u751f\u5e73\uff0c\u718a\u5144\u5c11\u5e74\u82f1\u624d\uff0c\u82e5 \u4e0d\u5acc\u5f03\u611a\u5144\u59b9\uff0c\u65e5\u540e\u6211\u4eec\u5b9a\u8981\u4ea4\u4e2a\u670b\u53cb\uff0c\u5c0f\u5f1f\u8fd1\u65e5\u4e5f\u60f3\u5317\u4e0a\u4eac\u90fd\uff0c\u8bf4\u4e0d\u5b9a\u8def\u4e0a\u8fd8\u4f1a\u78b0\u5230 \u5462\u3002\u201d\u8bf4\u5b8c\u4ed6\u53c8\u770b\u4e86\u4e1c\u65b9\u745b\u4e00\u773c\u3002 \u3000\u3000\u718a\u501c\u8fde\u5fd9\u7ad9\u8d77\u6765\uff0c\u76ee\u5149\u5076\u7136\u548c\u4e1c\u65b9\u745b\u4e00\u89e6\uff0c\u4e1c\u65b9\u745b\u671d\u4ed6\u5ae3\u7136\u4e00\u7b11\uff0c\u8fd9\u4e00\u7b11\u7b11\u5f97\u718a\u501c\u987f\u65f6 \u624b\u8db3\u65e0\u63aa\uff0c\u7ea2\u7740\u8138\uff0c\u52c9\u5f3a\u8bf4\u9053\uff1a\u201c\u5c0f\u5f1f\u5e74\u8f7b\u8bc6\u6d45\uff0c\u4e00\u5207\u4e8b\u90fd\u8981\u5821\u4e3b\u591a\u6307\u6559\u624d\u662f\uff0c\u65e5\u540e\u5c0f\u5f1f\u8fd8 \u671b\u80fd\u5e38\u8bf2\u6559\u76ca\u3002\u201d \u3000\u3000\u738b\u667a\u9011\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u81ea\u53e4\u82f1\u96c4\u501f\u82f1\u96c4\uff0c\u6b64\u8bdd\u679c\u771f\u4e0d\u5047\uff0c\u4e24\u4f4d\u90fd\u662f\u6b66\u6797\u4e2d\u5343\u767e\u5e74\u96be\u89c1\u7684\u5947 \u624d\u4fca\u5f66\uff0c\u65e5\u540e\u771f\u8be5\u591a\u4eb2\u8fd1\u2026\u2026\u201d\u4ed6\u53c8\u7b11\u7740\u5411\u4e1c\u65b9\u745b\u659c\u7768\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\u82e5\u80fd\u7ed3\u6210\u4e00\u5bb6\uff0c \u90a3\u66f4\u662f\u6b66\u6797\u4f73\u8bdd\u4e86\u3002\u201d \u3000\u3000\u4e1c\u65b9\u745b\u987f\u65f6\u7c89\u9762\u98de\u971e\uff0c\u4e00\u4f4e\u5934\uff0c\u5148\u8d70\u4e86\u51fa\u53bb\uff0c\u4e1c\u65b9\u7075\u77e5\u9053\u8001\u4e8e\u4e16\u6545\u7684\u738b\u667a\u9011\u5df2\u77e5\u4ed6\u7684\u6765 \u610f\uff0c\u4ed6\u542b\u7b11\u5411\u738b\u667a\u9011\u5fae\u4e00\u9894\u9996\uff0c\u8ddf\u7740\u5f80\u5916\u8d70\u3002 \u3000\u3000\u53ea\u6709\u718a\u501c\uff0c\u4ed6\u4ecd\u7ad9\u5728\u5f53\u5730\uff0c\u7ec6\u7ec6\u5730\u73a9\u5473\u7740\u738b\u667a\u9011\u8bdd\uff0c\u60f3\u4e86\u4e00\u4f1a\uff0c\u4ed6\u603b\u89c9\u5f97\u8fd9\u4e9b\u90fd\u662f\u4e0d\u53ef \u80fd\u53d1\u751f\u7684\u4e8b\uff0c\u4e5f\u5c31\u6446\u5728\u4e00\u8fb9\u4e86\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u65e9\u4e0a\uff0c\u5929\u65b9\u7834\u6653\uff0c\u9e23\u8fdc\u9556\u5c40\u5185\u5c31\u5fd9\u788c\u8d77\u6765\uff0c\u5957\u8f66\u3001\u4e0a\u7272\u53e3\uff0c\u663e\u89c1\u5f97\u662f\u6709\u4e00\u8d9f\u6781\u8d35 \u91cd\u7684\u9556\u8981\u8d77\u7a0b\u4e86\uff0c\u9556\u4f19\u5168\u4f53\u51fa\u52a8\uff0c\u7adf\u6ca1\u6709\u4e00\u4e2a\u95f2\u7740\u7684\u3002 \u3000\u3000\u603b\u9556\u5934\u7c89\u9762\u82cf\u79e6\u738b\u667a\u9011\uff0c\u66f4\u50cf\u662f\u4e00\u591c\u672a\u7761\uff0c\u7cbe\u795e\u867d\u7136\u4e0d\u4f73\uff0c\u5728\u75b2\u60eb\u4e2d\uff0c\u5374\u663e\u5f97\u6709\u4e9b\u9ad8 \u5174\uff0c\u5c31\u50cf\u662f\u8fd9\u8d9f\u9556\u5b9a\u7136\u4f1a\u5b89\u5168\u9001\u5230\u7684\u6837\u5b50\u3002 \u3000\u3000\u4e0d\u4e00\u4f1a\uff0c\u4eba\u591a\u624b\u5feb\uff0c\u8bf8\u4e8b\u4ff1\u5df2\u5b8c\u6bd5\uff0c\u5947\u602a\u7684\u662f\uff0c\u9556\u8f66\u7adf\u5957\u4e86\u4e03\u8f86\u3002 \u3000\u3000\u987b\u77e5\u6b64\u8d9f\u9556\u6240\u4fdd\u7684\uff0c\u53ea\u662f\u4e00\u652f\u6210\u5f62\u9996\u4e4c\uff0c\u54ea\u7528\u5f97\u7740\u8fd9\u591a\u8f66\u8f86\uff0c\u8fd9\u662f\u6bcf\u4e2a\u4eba\u5fc3\u91cc\u90fd\u5728\u6697\u6697 \u5947\u602a\u7740\u7684\uff0c\u4f46\u5374\u65e0\u4eba\u95ee\u51fa\u6765\u4fbf\u662f\u4e86\u3002 \u3000\u3000\u738b\u667a\u9011\u5c06\u718a\u501c\u548c\u5434\u8bcf\u4e91\u6084\u4fcf\u5730\u53ec\u81f3\u5185\u5ba4\uff0c\u718a\u501c\u5165\u5185\u4e00\u770b\uff0c\u9759\u5ba4\u5185\u653e\u7740\u4e03\u53e3\u540c\u6837\u7684\u5c0f\u7ea2\u6728 \u7bb1\u5b50\uff0c\u88c5\u6f62\u4ff1\u90fd\u751a\u662f\u8003\u7a76\uff0c\u7bb1\u5b50\u7528\u94a2\u6761\u3001\u94c1\u7247\uff0c\u7d27\u7d27\u5730\u5305\u4f4f\uff0c\u4e0a\u4e86\u6781\u5927\u7684\u9501\uff0c\u8fd9\u4e03\u53e3\u7bb1\u5b50\uff0c \u552f\u4e00\u7684\u5206\u522b\uff0c\u5c31\u662f\u6bcf\u4e00\u4e2a\u7bb1\u5b50\uff0c\u90fd\u7cfb\u7740\u989c\u8272\u4e0d\u540c\u7684\u4e1d\u5e26\u3002 \u3000\u3000\u738b\u667a\u9011\u6781\u5c0f\u5fc3\u5730\u5c06\u95e8\u5173\u4e0a\uff0c\u6307\u7740\u90a3\u4e03\u53e3\u7bb1\u5b50\u5bf9\u718a\u501c\u3001\u5434\u8bcf\u4e91\u4e8c\u4eba\u8bf4\u9053\uff1a\u201c\u8fd9\u4e03\u53e3\u7bb1\u5b50\u5206 \u522b\u7528\u7ea2\u3001\u9ec4\u3001\u84dd\u3001\u767d\u3001\u9ed1\u3001\u8910\u3001\u7d2b\uff0c\u4e03\u79cd\u989c\u8272\u7684\u4e1d\u5e26\u7cfb\u7740\uff0c\u4e24\u4f4d\u8d24\u5f1f\u53ef\u8981\u8bb0\u4f4f\uff0c\u53ea\u6709\u7cfb\u4e0a\u9ed1 \u4e1d\u5e26\u7684\u8fd9\u53e3\uff0c\u624d\u662f\u771f\u7684\uff0c\u4e07\u4e00\u6709\u4eba\u5b88\u9556\uff0c\u5c31\u8981\u7279\u522b\u6ce8\u610f\u8fd9\u53e3\u7bb1\u5b50\uff0c\u4f46\u5e73\u65f6\u5374\u4e0d\u53ef\u663e\u9732\u51fa\u5bf9\u8fd9 \u53e3\u7bb1\u5b50\u7279\u522b\u5173\u5fc3\uff0c\u514d\u5f97\u6cc4\u9732\u98ce\u58f0\u3002\u201d \u3000\u3000\u738b\u667a\u9011\u53c8\u5bf9\u5434\u8bcf\u4e91\u8bf4\u9053\uff1a\u201c\u8def\u4e0a\u82e5\u9047\u5230\u670b\u53cb\uff0c\u6216\u8005\u8def\u8fc7\u9556\u5c40\uff0c\u5343\u4e07\u8bb0\u5f97\u6258\u4ed6\u4eec\u6253\u542c\u6253 \u542c\uff0c\u5b9d\u9a6c\u795e\u97ad\u8428\u5929\u9aa5\u7684\u4e0b\u843d\uff0c\u544a\u8bc9\u4ed6\u4eec\u4e00\u6709\u6d88\u606f\uff0c\u5c31\u98de\u9a91\u6765\u901a\u77e5\u6211\uff0c\u4e00\u523b\u4e5f\u803d\u8bef\u4e0d\u5f97\u3002\u201d \u3000\u3000\u718a\u501c\u542c\u4e86\u5fc3\u4e2d\u975e\u5e38\u611f\u6fc0\uff0c\u738b\u667a\u9011\u62c9\u7740\u4ed6\u8180\u5b50\uff0c\u6781\u5bc6\u5207\u4ed6\u8bf4\uff1a\u201c\u6b64\u8d9f\u9556\u5173\u7cfb\u7740\u9e23\u8fdc\u9556\u5c40\u7684 \u524d\u9014\uff0c\u4ee5\u53ca\u611a\u5144\u7684\u8eab\u5bb6\u6027\u547d\uff0c\u8fd9\u4e9b\u90fd\u5168\u9760\u8d24\u5f1f\uff0c\u8fd9\u8d9f\u9556\u6211\u5c31\u4ea4\u7ed9\u4e24\u4f4d\u8d24\u5f1f\u4e86\uff0c\u611a\u5144\u795e\u601d\u5df2 \u4e71\uff0c\u53bb\u4e86\u4e5f\u662f\u65e0\u76ca\uff0c\u518d\u8005\u9556\u5c40\u4e2d\u5c1a\u6709\u8bb8\u591a\u4e8b\u5f85\u4e86\u2026\u2026\u201d \u3000\u3000\u718a\u501c\u9053\uff1a\u201c\u60a8\u4e0d\u53bb\u600e\u4e48\u884c\uff0c\u8def\u4e0a\u7684\u4e00\u5207\uff0c\u975e\u60a8\u4e0d\u53ef\u5440\u3002\u201d \u3000\u3000\u738b\u667a\u9011\u9053\uff1a\u201c\u8def\u4e0a\u7684\u4e00\u5207\uff0c\u81ea\u6709\u6211\u90a3\u4e8c\u5f1f\u53ef\u4ee5\u7167\u6599\uff0c\u4ed6\u6bd4\u6211\u884c\uff0c\u8d24\u5f1f\u4e0d\u8981\u987e\u8651\uff0c\u53cd\u6b63\u751f \u6b7b\u6709\u547d\uff0c\u7740\u771f\u4e22\u4e86\u9556\uff0c\u4e5f\u662f\u65e0\u6cd5\uff0c\u611a\u5144\u5173\u5fc3\u592a\u8fc7\u4e86\uff0c\u53bb\u4e86\u5b9e\u662f\u767e\u635f\u65e0\u76ca\u3002\u201d \u3000\u3000\u718a\u501c\u8f6c\u773c\u4e00\u671b\u5434\u8bcf\u4e91\uff0c\u89c1\u4ed6\u4ecd\u7136\u50cf\u5f80\u5e38\u4e00\u6837\u5730\u6c89\u9ed8\uff0c\u4e1d\u6beb\u6ca1\u6709\u56e0\u4e3a\u738b\u667a\u9011\u7684\u4e0d\u53bb\uff0c\u9732\u51fa \u4e0d\u5b89\u6216\u662f\u60ca\u5f02\u795e\u8272\uff0c\u4e5f\u5c31\u4e0d\u518d\u8bf4\u8bdd\u3002 \u3000\u3000\u718a\u501c\u548c\u5434\u8bcf\u4e91\u5e76\u80a9\u9a91\u5728\u9556\u8f66\u7684\u884c\u5217\u4e4b\u540e\uff0c\u8d9f\u5b50\u624b\u5076\u800c\u558a\u7740\u9576\uff0c\u58f0\u97f3\u8212\u65f7\u5730\u6563\u5e03\u5728\u6797\u91ce\u4e4b \u95f4\uff0c\u4ed6\u671b\u7740\u90a3\u5a49\u8712\u5728\u524d\u7684\u884c\u5217\uff0c\u5fc3\u91cc\u6709\u4e00\u79cd\u96be\u4ee5\u5f62\u5bb9\u7684\u6ecb\u5473\uff0c\u4e8e\u662f\u4e00\u5939\u9a6c\u8179\uff0c\u5c06\u9a6c\u8fdc\u8fdc\u5730\u653e \u5230\u524d\u9762\u53bb\u3002\u9556\u8f66\u542f\u884c\u7684\u665a\u4e0a\uff0c\u738b\u667a\u9011\u5ffd\u7136\u7a7f\u7740\u4e00\u8eab\u884c\u8def\u5546\u8d3e\u7684\u670d\u8272\uff0c\u7531\u9e23\u8fdc\u9556\u5c40\u8d70\u4e86\u51fa\u6765\uff0c \u8eab\u4e0a\u659c\u80cc\u7740\u4e00\u4e2a\u5305\u88b1\uff0c\u9a91\u7684\u5374\u662f\u5339\u826f\u9a8f\uff0c\u5306\u5306\u5730\u7531\u5c0f\u897f\u95e8\u7a7f\u51fa\u57ce\u53bb\uff0c\u6ca1\u6709\u4eba\u77e5\u9053\u4ed6\u7684\u884c\u610f\u548c \u53bb\u5904\u3002 \u3000\u3000\u6387\u8f66\u7ed5\u8fc7\u90b5\u4f2f\u6e56\uff0c\u800c\u81f3\u9ad8\u90ae\u6e56\u6ee8\uff0c\u718a\u501c\u653e\u773c\u671b\u53bb\uff0c\u53ea\u89c1\u6e56\u3002 \u3000\u3000\u6c34\u6d69\u6e3a\uff0c\u6ce2\u5e73\u5982\u955c\uff0c\u4e00\u7247\u5343\u91cc\uff0c\u4e0e\u4ed6\u6240\u66fe\u770b\u5230\u7684\u83ab\u6101\u6e56\u76f8\u6bd4\uff0c\u5b9e\u662f\u4e0d\u53ef\u540c\u65e5\u800c\u8bed\u3002 \u3000\u3000\u4ed6\u4e0d\u7981\u6697\u81ea\u5728\u611f\u53f9\u7740\u5929\u5730\u4e4b\u5927\uff0c\u4e07\u7269\u4e4b\u5947\uff0c\u8fd9\u65f6\u8d9f\u5b50\u624b\u53c8\u5728\u524d\u9762\u9ad8\u558a\u9053\uff1a\u201c\u9e23\u8fdc\u2026\u2026\u626c \u5a01\u2026\u2026\u201d\u58f0\u97f3\u5728\u8fd9\u5bc2\u9759\u7684\u6e56\u6ee8\uff0c\u663e\u5f97\u5f02\u5e38\u54cd\u4eae\uff0c\u5fae\u98ce\u5439\u8fc7\uff0c\u8863\u8882\u98d8\u7136\uff0c\u718a\u501c\u53ea\u89c9\u6b64\u8eab\u53c8\u975e\u4ed6 \u5c5e\u3002 \u3000\u3000\u5ffd\u5730\u8fdc\u5904\u5c18\u571f\u5927\u8d77\uff0c\u5954\u6765\u51e0\u5339\u5065\u9a6c\uff0c\u5434\u8bcf\u4e91\u5c06\u624b\u4e00\u6325\uff0c\u9556\u8f66\u7acb\u5373\u505c\u4f4f\uff0c\u718a\u501c\u4ee5\u4e3a\u662f\u90a3\u6d3b \u513f\u6765\u4e86\uff0c\u6025\u5fd9\u5168\u795e\u6212\u5907\u7740\u3002 \u3000\u3000\u970e\u65f6\u9a6c\u5df2\u5954\u5230\uff0c\u4ece\u9a6c\u4e0a\u8df3\u4e0b\u51e0\u4e2a\u52b2\u88c5\u5927\u6c49\uff0c\u8fdc\u8fdc\u5c31\u5411\u5434\u8bcf\u4e91\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u8fd9\u6b21\u539f\u6765\u662f\u4e8c \u603b\u9556\u5934\u62bc\u7684\u9556\uff0c\u6211\u4eec\u74e2\u628a\u5b50\u5206\u6c34\u72e1\u502a\u5f53\u5bb6\u7684\uff0c\u542c\u5f97\u9e23\u8fdc\u7684\u9556\u53f7\uff0c\u7279\u9063\u6211\u4eec\u524d\u6765\u81f4\u610f\uff0c\u8bf7\u95ee\u4e8c \u9556\u5934\u6709\u4f55\u5429\u5490\uff0c\u8ba9\u6211\u4eec\u53e3\u8986\u4ed6\u8001\u4eba\u5bb6\u3002\u201d \u3000\u3000\u5434\u8bcf\u4e91\u5374\u4e95\u672a\u4e0b\u9a6c\uff0c\u53ea\u5728\u9a6c\u4e0a\u62b1\u62f3\u9053\uff1a\u201c\u502a\u5f53\u5bb6\u7684\u76db\u60c5\uff0c\u5728\u4e0b\u5fc3\u9886\uff0c\u8fd9\u6b21\u655d\u9556\u5c40\u501f\u9053\u9ad8 \u90ae\uff0c\u627f\u502a\u5f53\u5bb6\u7684\u9ad8\u624b\u653e\u8fc7\uff0c\u4e0b\u6b21\u5434\u67d0\u5b9a\u5fc5\u767b\u5be8\u9053\u8c22\u3002\u201d \u3000\u3000\u2018\u90a3\u4e3a\u9996\u7684\u5927\u6c49\u671d\u718a\u501c\u4e5f\u662f\u4e00\u62f1\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u60f3\u5fc5\u5c31\u662f\u540d\u52a8\u6c5f\u5b81\u7684\u718a\u82f1\u96c4\u4e86\uff0c\u6211\u4eec\u5f53 \u5bb6\u518d\u4e09\u5631\u5490\u6211\u4eec\uff0c\u89c1\u5230\u718a\u82f1\u96c4\u5b9a\u8981\u4ee3\u4ed6\u95ee\u597d\u3002\u201d \u3000\u3000\u718a\u501c\u5fd9\u5728\u9a6c\u4e0a\u62b1\u62f3\u4e3a\u793c\u3002 \u3000\u3000\u4e8e\u662f\u90a3\u52b2\u88c5\u5927\u6c49\u5c06\u624b\u4e00\u6325\uff0c\u5411\u800c\u4eba\u5fae\u4e00\u8eac\u8eab\uff0c\u7a9c\u4e0a\u9a6c\u80cc\uff0c\u8f6c\u5934\u800c\u53bb\u3002 \u3000\u3000\u718a\u501c\u8fd9\u624d\u77e5\u9053\u81ea\u5df1\u53ea\u4e0d\u8fc7\u662f\u4e00\u573a\u865a\u60ca\uff0c\u4e0d\u89c9\u53f9\u4e86\u53e3\u6c14\u3002\u5434\u8bcf\u4e91\u7b11\u9053\uff1a\u201c\u6b64\u5730\u672c\u5c5e\u9ad8\u90ae\u6c34 \u5be8\u7684\u5206\u6c34\u72e1\u8c8c\uff0c\u9e23\u8fdc\u9556\u5c40\u7684\u9556\u8f66\uff0c\u5230\u6b64\u5411\u662f\u901a\u884c\u65e0\u963b\uff0c\u5206\u6c34\u72e1\u8c8c\u4e0e\u6211\u5927\u54e5\u4ea4\u60c5\u751a\u597d\uff0c\u53ea\u662f\u6211 \u5374\u6709\u4e9b\u770b\u4e0d\u60ef\u4ed6\u3002\u201d\u505c\u4e86\u534a\u664c\uff0c\u4ed6\u53c8\u8bf4\u9053\uff1a\u201c\u6211\u8fd9\u6b21\u6240\u987e\u8651\u7684\uff0c\u5012\u4e0d\u662f\u8fd9\u4e9b\u5b89\u7a91\u7acb\u5be8\u7684\u74e2\u628a \u5b50\u548c\u90a3\u4e9b\u5403\u6a2a\u6881\u7684\u9ed1\u9053\u670b\u53cb\uff0c\u9e23\u8fdc\u9556\u5c40\u7684\u9556\uff0c\u8c05\u4ed6\u4eec\u4e5f\u6ca1\u6709\u8fd9\u4e2a\u80c6\u5b50\u52a8\uff0c\u6240\u6015\u7684\u53ea\u662f\u6b66\u6797\u4e2d \u7684\u513f\u4e2a\u624e\u624b\u7684\u4eba\u7269\u4e5f\u8981\u6765\u8d9f\u8fd9\u8d9f\u6d51\u6c34\u3002\u201d \u3000\u3000\u9556\u8f66\u6cbf\u7740\u5b98\u9053\u8d70\uff0c\u5929\u8fd8\u6ca1\u9ed1\u3002\u5c31\u6253\u5c16\u4f4f\u5e97\uff0c\u4e00\u5230\u5929\u4eae\uff0c\u4ed6\u4eec\u5c31\u5168\u529b\u8d76\u8def\uff0c\u8fd9\u6837\u8d70\u5f97\u5f88 \u5feb\uff0c\u6ca1\u51e0\u5929\uff0c\u4ed6\u4eec\u7ecf\u5357\u5317\u4ea4\u901a\u8981\u51b2\u3002 \u3000\u3000\u6dee\u3001\u8fd0\u4e24\u6cb3\u4ea4\u70b9\u4e4b\u6e05\u6c5f\u6d66\uff0c\u8fc7\u5bbf\u8fc1\uff0c\u7531\u53f0\u513f\u5e84\u5165\u5883\u5c71\u4e1c\u3002 \u3000\u3000\u9556\u8f66\u8fdb\u5165\u5c71\u4e1c\uff0c\u718a\u501c\u5c31\u611f\u89c9\u5230\u6709\u4e9b\u5730\u65b9\u751a\u662f\u4e0d\u4fbf\uff0c\u5c24\u5176\u662f\u8bed\u8a00\u65b9\u9762\uff0c\u4f46\u5e78\u597d\u5434\u8bcf\u4e91\u4ee5\u53ca \u9556\u5c40\u7684\u8d9f\u5b50\u624b\u7b49\uff0c\u90fd\u719f\u77e5\u5404\u7701\u65b9\u8a00\uff0c\u718a\u501c\u8fd9\u624d\u77e5\u9053\uff0c\u82e5\u8981\u53ea\u8eab\u8d70\u904d\u5929\u4e0b\uff0c\u662f\u5982\u4f55\u7684\u56f0\u96be\u3002 \u3000\u3000\u545c\u8fdc\u9556\u5c40\u7684\u6f02\u8f66\u66fe\u6765\u5f80\u4e34\u57ce\u591a\u6b21\uff0c\u5434\u8bcf\u4e91\u62db\u547c\u7740\u9556\u4f19\u5c06\u4e03\u53e3\u7bb1\u5b50\u5378\u5230\u5c4b\u91cc\uff0c\u5e97\u5c0f\u4e8c\u9001\u4e0a \u8336\u6c34\uff0c\u8fd9\u4e9b\u90fd\u662f\u60ef\u4f8b\uff0c\u5434\u8bcf\u4e91\u4e00\u770b\u5929\u8272\u5c1a\u65e9\uff0c\u6599\u60f3\u4e0d\u4f1a\u51fa\u4e8b\uff0c\u53eb\u8fc7\u5e97\u5c0f\u4e8c\u95ee\u9053\uff1a\u201c\u8fd9\u51e0\u5929\u4e34 \u57ce\u53ef\u6709\u4e9b\u751a\u4e48\u624e\u773c\u4eba\u7269\u7684\u884c\u8e2a\uff0c\u6709\u6ca1\u6709\u4ec0\u4e48\u7279\u522b\u4eba\u7269\u524d\u6765\u6295\u5e97\u3002\u201d \u3000\u3000\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u8fd9\u5c0f\u7684\u5012\u4e0d\u77e5\u9053\uff0c\u53ea\u662f\u8fd9\u4e24\u5929\u4e34\u57ce\u7684\u53eb\u5316\u5b50\u50cf\u662f\u7279\u522b\u591a\u3002\u201d \u3000\u3000\u5434\u8bcf\u4e91\u54e6\u4e86\u4e00\u58f0\uff0c\u4e5f\u672a\u5728\u610f\uff0c\u6325\u624b\u53eb\u5e97\u5c0f\u4e8c\u9000\u53bb\uff0c\u9042\u4e0e\u718a\u501c\u8bf4\u9053\uff1a\u201c\u5c71\u4e1c\u7684\u6252\u9e21\u70d9\u997c\uff0c \u6700\u662f\u6709\u540d\uff0c\u73b0\u5728\u53cd\u6b63\u65e0\u4e8b\uff0c\u4f60\u6211\u540c\u53bb\u8857\u4e0a\u770b\u770b\uff0c\u968f\u4fbf\u4e5f\u5c1d\u5c1d\u6252\u9e21\u70e7\u9152\u7684\u98ce\u5473\uff0c\u4f60\u770b\u53ef\u597d\uff1f\u201d \u3000\u3000\u718a\u501c\u5f53\u7136\u8bf4\u597d\uff0c\u4fbf\u968f\u7740\u5434\u8bcf\u4e91\u8d70\u5230\u8857\u4e0a\u3002\u8fd9\u4e34\u57ce\u5e76\u975e\u5927\u57ce\uff0c\u81ea\u4e0d\u80fd\u4e0e\u6c5f\u5b81\u3001\u626c\u5dde\u7b49\u5904\u76f8 \u6bd4\uff0c\u4f46\u5c0f\u57ce\u98ce\u5473\uff0c\u6bcf\u5e74\u6709\u9189\u4eba\u4e4b\u5904\u3002\u4ed6\u4eec\u4fe1\u6b65\u8d70\u5230\u8857\u4e0a\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u76ee\u7684\uff0c\u718a\u501c\u968f\u4fbf\u4e70\u4e86\u51e0 \u4ef6\u5c71\u4e1c\u7684\u4e0a\u4ea7\uff0c\u62ff\u5728\u624b\u4e0a\uff0c\u4ed6\u5c11\u5e74\u597d\u5947\uff0c\u89c9\u5f97\u6837\u6837\u4e1c\u897f\uff0c\u90fd\u6781\u6709\u8da3\u3002 \u3000\u3000\u95f2\u901b\u4e86\u4e00\u4f1a\u513f\uff0c\u5434\u8bcf\u4e91\u89c1\u524d\u9762\u6709\u4e2a\u9152\u697c\uff0c\u89c4\u6a21\u50cf\u662f\u8fd8\u5927\uff0c\u4e0e\u718a\u501c\u968f\u610f\u8d70\u4e0a\u4e86\u697c\u3002 \u3000\u3000\u867d\u7136\u6b63\u662f\u5403\u996d\u7684\u65f6\u5019\uff0c\u4f46\u8fd9\u91cc\u751f\u610f\u5e76\u4e0d\u592a\u597d\uff0c\u53ea\u758f\u758f\u843d\u843d\u5750\u4e86\u51e0\u4e2a\u5ba2\u4eba\u3002\u5434\u8bcf\u4e91\u76ee\u5149\u56db \u626b\uff0c\u89c1\u4ff1\u90fd\u662f\u4e9b\u5bfb\u5e38\u5ba2\uff0c\u9042\u4e0e\u718a\u501c\u6361\u4e86\u4e34\u8857\u9760\u7a97\u7684\u4f4d\u5b50\u5750\u4e0b\uff0c\u8dd1\u5802\u7684\u8fde\u5fd9\u8d70\u4e86\u8fc7\u6765\uff0c\u5f20\u7f62\u8336 \u6c34\uff0c\u5434\u8bcf\u4e91\u70b9\u4e86\u6252\u9e21\u3001\u70d9\u997c\u7b49\u7269\uff0c\u5c31\u548c\u718a\u501c\u95f2\u8c08\u8d77\u6765\u3002 \u3000\u3000\u8fd9\u65f6\u5ffd\u5730\u53c8\u8d70\u4e0a\u4e00\u4f4d\u5ba2\u4eba\uff0c\u706f\u706b\u4e0b\u53ea\u515a\u4ed6\u9762\u8272\u82cd\u767d\u3001\u6700\u5947\u602a\u7684\u662f\u5168\u8eab\u9ed1\u886b\u9ed1\u5c65\uff0c\u5934\u4e0a\u7684 \u8fab\u5b50\u68b3\u5f97\u66f4\u662f\u6f06\u9ed1\u53d1\u4eae\uff0c\u76d8\u5728\u9876\u4e0a\uff0c\u76f8\u89c6\u4e4b\u4e0b\uff0c\u663e\u5f97\u9762\u5b54\u66f4\u662f\u6ca1\u6709\u4e00\u4e1d\u8840\u8272\uff0c\u4ed6\u4e0a\u697c\u6765\u56db\u5468 \u7565\u4e00\u6253\u91cf\uff0c\u7adf\u5411\u718a\u501c\u7b49\u7684\u5750\u5904\u8d70\u4e86\u8fc7\u6765\uff0c\u5434\u8bcf\u4e91\u9762\u8272\u767b\u65f6\u4e00\u53d8\u3002 \u3000\u3000\u54ea\u77e5\u90a3\u4eba\u8d70\u5230\u4ed6\u4eec\u7684\u90bb\u684c\uff0c\u5c31\u5750\u4e0b\u4e86\uff0c\u62db\u624b\u5524\u8fc7\u5e97\u4f19\uff0c\u81ea\u7ba1\u547c\u9152\u53eb\u83b1\uff0c\u5434\u8bcf\u4e91\u770b\u89c1\u5982 \u6b64\uff0c\u624d\u50cf\u653e\u4e0b\u5fc3\u6765\uff0c\u4eff\u4f5b\u5bf9\u6b64\u4eba\u751a\u4e3a\u987e\u5fcc\u3002 \u3000\u3000\u718a\u501c\u89c1\u4e86\uff0c\u5fc3\u4e2d\u89c9\u5f97\u5947\u602a\uff0c\u4f46\u90a3\u4eba\u5750\u5728\u90bb\u684c\uff0c\u4e24\u53f0\u76f8\u9694\u5f88\u8fd1\uff0c\u4ed6\u53c8\u4e0d\u80fd\u95ee\u5434\u8bcf\u4e91\u7a76\u7adf\u6b64 \u4eba\u662f\u4f55\u8bb8\u4eba\u4e5f\uff0c\u53ea\u662f\u6697\u81ea\u7eb3\u95f7\u3002 \u3000\u3000\u9152\u83dc\u6765\u5f97\u5f88\u7094\uff0c\u5434\u8bcf\u4e91\u50cf\u662f\u6709\u7740\u6025\u4e8b\uff0c\u8bdd\u4e5f\u4e0d\u8bf4\u4e00\u53e5\uff0c\u5f88\u5feb\u5c31\u5403\u5b8c\u4e86\uff0c\u5bf9\u718a\u501c\u8f7b\u58f0\u8bf4\uff1a \u201c\u5403\u5b8c\u5feb\u8d70\uff0c\u4e0d\u7136\u51c6\u9ebb\u70e6\u3002\u201d \u3000\u3000\u718a\u501c\u6b63\u81ea\u5947\u602a\uff0c\u7a81\u7136\u90bb\u684c\u90a3\u9ed1\u8863\u4eba\u5927\u58f0\u7b11\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5012\u806a\u660e\uff0c\u53ea\u662f\u6b64\u523b\u60f3\u8d70\uff0c \u5374\u5df2\u6765\u4e0d\u53ca\u4e86\u3002\u201d\u7b11\u58f0\u542c\u6765\uff0c\u9634\u5bd2\u5f7b\u9aa8\uff0c\u76f4\u4e0d\u4f3c\u4eba\u7c7b\u6240\u53d1\u3002 \u3000\u3000\u90a3\u9ed1\u8863\u4eba\u8bf4\u5b8c\u4e4b\u540e\uff0c\u5434\u8bcf\u4e91\u7684\u8138\u8272\u53d8\u5f97\u66f4\u662f\u96be\u770b\uff0c\u4e00\u62c9\u718a\u501c\uff0c\u60f3\u4e00\u8d70\u4e86\u4e8b\uff0c\u90a3\u4eba\u5f71\u4e00 \u6643\uff0c\u90a3\u9ed1\u8863\u7684\u602a\u5ba2\u5df2\u663e\u7136\u7ad9\u5728\u773c\u524d\uff0c\u4ef2\u7740\u5434\u8bcf\u4e91\u51b7\u51b7\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u53ef\u8ba4\u8bc6\u6211\u662f\u8c01\uff1f\u201d \u3000\u3000\u5434\u8bcf\u4e91\u65b9\u5f85\u7b54\u8bdd\uff0c\u90a3\u4eba\u53c8\u51b7\u7b11\u4e86\u51e0\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u51ed\u6211\u7684\u7a7f\u7740\u6253\u626e\uff0c\u53ea\u8981\u5728\u6c5f\u6e56\u4e0a\u7a0d\u8d70\u52a8 \u4e00\u4e24\u5e74\u7684\u5c31\u7b97\u4e0d\u8ba4\u8bc6\uff0c\u4e5f\u8be5\u542c\u8bf4\u8fc7\uff0c\u4f55\u51b5\u9601\u4e0b\u5802\u5802\u9e23\u8fdc\u9556\u5c40\u7684\u4e8c\u9556\u5934\u5462\u3002\u201d\u8bf4\u5b8c\u53cc\u76ee\u4e00\u77aa\uff0c \u5bd2\u5149\u5916\u9732\u3002 \u3000\u3000\u5434\u8bcf\u4e91\u5e72\u7b11\u4e86\u51e0\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u5929\u5c71\u4e09\u9f99\uff0c\u6b66\u6797\u4e2d\u8c01\u4eba\u4e0d\u8bc6\uff0c\u53ea\u4e0d\u77e5\u949f\u5c11\u4fa0\u964d\u4e34\u6b64\u95f4\uff0c\u6709 \u4f55\u5429\u5490\uff1f\u201d \u3000\u3000\u718a\u501c\u4e00\u542c\uff0c\u84e6\u5730\u8bb0\u8d77\uff0c\u6b64\u4eba\u5fc5\u662f\u738b\u667a\u9011\u6240\u63d0\u53ca\u7684\uff0c\u5929\u5c71\u4e09\u9f99\u4e4b\u4e00\u58a8\u9f99\u949f\u5929\u4ec7\u4e86\uff0c\u5fc3\u91cc\u60f3 \u9053\uff1a\u201c\u6b64\u4eba\u600e\u5730\u5982\u6b64\u72c2\u50b2\uff0c\u8fd9\u6837\u770b\u6765\uff0c\u90a3\u51fa\u5c18\u5251\u5ba2\u4e1c\u65b9\u7075\uff0c\u5012\u662f\u4e0e\u4f17\u4e0d\u540c\uff0c\u65e0\u602a\u6b66\u6797\u4e2d\u4eba\u4eba \u666f\u4ef0\u4e86\u3002\u201d \u3000\u3000\u949f\u5929\u4ec7\u76ee\u5149\u4e00\u626b\u4e24\u4eba\uff0c\u8bf4\u9053\uff1a\u201c\u533a\u533a\u8fd9\u6b21\u5230\u4e34\u57ce\u6765\uff0c\u5c31\u662f\u4e13\u8bda\u606d\u5019\u4e24\u4f4d\u7684\u5927\u9a7e\uff0c\u60f3\u6765\u6b64 \u4f4d\u5fc5\u5b9a\u662f\u8fd1\u65e5\u95f9\u5f97\u8f70\u8f70\u70c8\u70c8\u7684\u5c11\u5e74\u82f1\u96c4\u718a\u501c\u4e86\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u4ed6\u53c8\u51b7\u7b11\u4e00\u58f0\uff0c\u795e\u8272\u95f4\u50cf\u662f\u5341\u5206\u4e0d\u5c51\uff0c\u718a\u501c\u4e0d\u7981\u6c14\u5f80\u4e0a\u649e\uff0c\u53cd\u53e3\u9053\uff1a\u201c\u662f\u53c8\u600e\u4e48\uff0c \u4e0d\u662f\u53c8\u600e\u4e48\uff0c\u4f60\u7ba1\u5f97\u7740\u5417\uff1f\u201d \u3000\u3000\u949f\u5929\u4ec7\u795e\u8272\u4e00\u53d8\uff0c\u8fde\u58f0\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u6b64\u5730\u4e5f\u975e\u8c08\u8bdd\u4e4b\u5904\uff0c\u949f\u67d0\u4eba\u867d\u7136\u4e0d\u624d\uff0c\u4f46\u4e5f\u4e95 \u975e\u7279\u4e3a\u90a3\u4e8c\u4f4d\u6240\u4fdd\u7684\u4e1c\u897f\u800c\u6765\uff0c\u53ea\u662f\u718a\u5c11\u4fa0\u5417\u2026\u2026\u201d\u4ed6\u7565\u505c\u4e86\u505c\uff0c\u5e72\u7b11\u4e86\u6570\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u949f \u67d0\u4eba\u5012\u8981\u9886\u6559\u9886\u6559\u3002\u201d \u3000\u3000\u5434\u8bcf\u4e91\u53cc\u7709\u4e00\u76b1\uff0c\u6b63\u60f3\u53d1\u8bdd\uff0c\u54ea\u77e5\u949f\u5929\u4ec7\u5df2\u8f6c\u8eab\u8d70\u4e86\uff0c\u4e34\u884c\u65f6\u8bf4\u9053\uff1a\u201c\u4eca\u591c\u4e09\u66f4\uff0c\u949f\u67d0 \u4eba\u5fc5\u5b9a\u7279\u6765\u62dc\u8bbf\uff0c\u8bf7\u4e8c\u4f4d\u7a0d\u5019\u3002\u201d \u3000\u3000\u5f85\u4ed6\u8d70\u4e0b\u697c\u68af\uff0c\u5434\u8bcf\u4e91\u624d\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u8d24\u5f1f\u6709\u6240\u4e0d\u77e5\uff0c\u8fd9\u5929\u5c71\u4e09\u9f99\u6700\u662f\u5fc3\u72e0\u624b \u8fa3\uff0c\u867d\u7136\u4ed6\u4eec\u5e76\u975e\u662f\u4ec0\u4e48\u90aa\u6d3e\u4eba\u7269\uff0c\u4f46\u53ea\u8981\u72af\u7740\u4ed6\u4eec\u7684\uff0c\u4ece\u6ca1\u6709\u4e00\u4e2a\u9003\u5f97\u51fa\u53bb\uff0c\u611a\u5144\u5e76\u975e\u6015 \u4e8b\uff0c\u53ea\u662f\u6211\u4eec\u73b0\u5728\u6709\u8981\u52a1\u5728\u8eab\uff0c\u53c8\u60f9\u4e0b\u8fd9\u4e2a\u9b54\u5934\uff0c\u5c82\u975e\u662f\u5929\u5927\u7684\u9ebb\u70e6\u3002\u201d \u3000\u3000\u718a\u501c\u8d4c\u6c14\u9053\uff1a\u201c\u8fd9\u662f\u6211\u60f9\u4e0b\u7684\u7978\uff0c\u4ec0\u4e48\u4e8b\u6211\u90fd\u4e00\u4eba\u62c5\u5f53\uff0c\u4f60\u653e\u5fc3\u597d\u4e86\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u4e5f\u4e0b\u697c\u53bb\u4e86\uff0c\u5434\u8bcf\u4e91\u77e5\u9053\u4ed6\u8bef\u4f1a\u4e86\u81ea\u5df1\u7684\u610f\u601d\uff0c\u4f46\u4e5f\u4e0d\u613f\u89e3\u91ca\u3002 \u3000\u3000\u591c\u6e10\u6df1\uff0c\u4e5f\u66f4\u5bc2\u9759\uff0c\u718a\u501c\u6570\u7740\u8fdc\u5904\u4f20\u6765\u7684\u66f4\u9f13\uff0c\u77e5\u9053\u5df2\u8fd1\u4e09\u66f4\uff0c\u4ed6\u629a\u6478\u7740\u8eab\u540e\u7684\u5251\u628a\uff0c \u51fa\u795e\u60f3\u7740\u3002 \u3000\u3000\u201c\u4eca\u5929\u665a\u4e0a\uff0c\u5c31\u8be5\u662f\u51b3\u5b9a\u6211\u547d\u8fd0\u7684\u65f6\u5019\u4e86\uff0c\u6211\u5982\u80fd\u5c06\u90a3\u949f\u5929\u4ec7\u51fb\u8d25\uff0c\u56fa\u662f\u4e07\u5e78\uff0c\u53ef\u662f\u6211 \u4e07\u4e00\u8d25\u4e86\uff0c\u5373\u4f7f\u4fa5\u5e78\u672a\u6b7b\uff0c\u90a3\u6211\u6240\u8ba1\u5212\u7684\u4e00\u5207\uff0c\u6240\u5e7b\u60f3\u7684\u4e00\u5207\uff0c\u4e5f\u90fd\u5b8c\u4e86\uff0c\u949f\u65e0\u4ec7\u80fd\u5728\u6c5f\u6e56 \u4e0a\u4eab\u53d7\u5982\u6b64\u5927\u7684\u58f0\u540d\uff0c\u6b66\u529f\u5f53\u7136\u4e0d\u662f\u5f20\u4e49\u7b49\u4eba\u6240\u80fd\u6bd4\u62df\u7684\uff0c\u6211\u82e6\u7ec3\u4e03\u5e74\uff0c\u4eca\u5929\u624d\u662f\u6211\u771f\u6b63\u7684 \u8003\u9a8c\uff0c\u6211\u8be5\u5c3d\u6211\u7684\u5168\u529b\uff0c\u53bb\u5e94\u4ed8\u5b83\uff0c\u5947\u602a\u7684\u662f\uff0c\u6211\u4ee5\u5f80\u7684\u81ea\u4fe1\uff0c\u4eca\u591c\u600e\u4e48\u90fd\u6d88\u5931\u4e86\u5462\uff1f\u201d \u3000\u3000\u66f4\u6572\u4e09\u54cd\uff0c\u718a\u501c\u7684\u5fc3\u795e\u968f\u7740\u7d27\u5f20\u8d77\u6765\uff0c\u4ed6\u7d27\u63e1\u7740\u62f3\u5934\uff0c\u89c6\u89c9\u548c\u542c\u89c9\u90fd\u5728\u5c3d\u529b\u641c\u7d22\u7740\uff0c\u4ed6 \u5f00\u59cb\u5e0c\u671b\u949f\u5929\u4ec7\u65e9\u4e9b\u524d\u6765\uff0c\u8ba9\u4e00\u5207\u4e8b\u65e9\u70b9\u4f5c\u4e86\u65ad\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u8fdc\u8fdc\u5df2\u6709\u591c\u884c\u4eba\u8863\u8882\u5e26\u98ce\u7684\u58f0\u54cd\uff0c\u4f46\u662f\u718a\u501c\u7684\u6c5f\u6e56\u5386\u7ec3\u592a\u5dee\uff0c\u4ed6\u4e1d\u6beb\u672a\u66fe\u542c\u51fa\uff0c \u4f46\u662f\u5434\u8bcf\u4e91\u7684\u623f\u95e8\u84e6\u5730\u5f00\u4e86\uff0c\u5434\u8bcf\u4e91\u50cf\u7bad\u4e00\u6837\u5730\u81ea\u5c4b\u4e2d\u7a9c\u4e86\u51fa\u6765\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6ce8\u610f\uff0c\u949f\u5929 \u4ec7\u5df2\u7ecf\u6765\u4e86\u3002\u201d \u3000\u3000\u679c\u7136\u4ed6\u7684\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u5b8c\uff0c\u949f\u5929\u4ec7\u7626\u524a\u800c\u7cbe\u7ec3\u7684\u8eab\u8eaf\u5df2\u5230\u5c4b\u9876\u8f6c\u6298\u73b0\u51fa\uff0c\u591c\u8272\u4e4b\u4e0b\uff0c\u53ea\u89c1 \u4ed6\u50cf\u662f\u4e00\u53ea\u82cd\u9e70\uff0c\u76d8\u65cb\u800c\u4e0b\u3002 \u3000\u3000\u949f\u5929\u4ec7\u98d8\u98d8\u843d\u5728\u5730\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\u4e45\u7b49\u4e86\uff0c\u6b64\u5730\u5012\u751a\u6e05\u9759\u3001\u5728\u4e0b\u6b63\u597d\u8ba8\u6559\u3002\u201d \u3000\u3000\u4ed6\u8bdd\u8bf4\u5f97\u867d\u7136\u5ba2\u6c14\uff0c\u58f0\u97f3\u5374\u662f\u51b7\u51b0\u51b0\u7684\uff0c\u50cf\u662f\u81ea\u575f\u5893\u4e2d\u6240\u53d1\u51fa\u6765\u7684\uff0c\u518d\u52a0\u4e0a\u4ed6\u90a3\u5982\u575a\u51b0 \u822c\u7684\u5bb9\u8c8c\uff0c\u771f\u662f\u4ee4\u4eba\u4e0d\u5bd2\u800c\u6817\u3002 \u3000\u3000\u5434\u8bcf\u4e91\u9053\uff1a\u201c\u949f\u5927\u4fa0\u4e0e\u6211\u7b49\u7d20\u65e0\u4ec7\u6028\uff0c\u4f46\u671b\u80fd\u70b9\u5230\u800c\u6b62\u3002\u201d \u3000\u3000\u949f\u5929\u4ec7\u9053\uff1a\u201c\u4f60\u5927\u6982\u5f04\u9519\u4e86\uff0c\u6211\u627e\u7684\u53ef\u4e0d\u662f\u4f60\uff0c\u4ec0\u4e48\u70b9\u5230\u4e0d\u70b9\u5230\uff0c\u4f60\u96be\u9053\u4e0d\u77e5\u9053\u5929\u5c71\u98de \u9f99\u7684\u813e\u6c14\uff0c\u6211\u949f\u67d0\u4eba\u8fd8\u7b97\u662f\u6700\u5ba2\u6c14\u4e86\u3002\u201d \u3000\u3000\u718a\u501c\u4e0d\u7981\u5927\u6012\uff0c\u5c06\u8eab\u4e00\u6a2a\uff0c\u62e6\u5728\u5434\u8bcf\u4e91\u7684\u524d\u9762\uff0c\u8bf4\u9053\uff1a\u201c\u59d3\u949f\u7684\uff0c\u4f60\u5356\u7684\u54ea\u95e8\u5b50\u72c2\uff0c\u6709 \u4eba\u6015\u4f60\u4eec\u5929\u5c71\u4e09\u9f99\uff0c\u5728\u6211\u773c\u91cc\u770b\u6765\uff0c\u4f60\u4eec\u53ea\u662f\u4e9b\u672a\u6210\u6c14\u5019\u7684\u5c0f\u6ce5\u9cc5\u7f62\u4e86\uff0c\u795e\u6c14\u4e9b\u4ec0\u4e48\uff01\u201d \u3000\u3000\u949f\u5929\u4ec7\u9053\uff1a\u201c\u6211\u4e8c\u5341\u62db\u5185\uff0c\u82e5\u4e0d\u80fd\u5c06\u4f60\u4f24\u5728\u5251\u4e0b\uff0c\u5c31\u7b97\u6211\u5b66\u827a\u4e0d\u7cbe\uff0c\u7acb\u523b\u78d5\u5934\u62dc\u4f60\u4e3a \u5e08\uff0c\u800c\u4e14\u4ece\u6b64\u6709\u4f60\u59d3\u718a\u7684\u5728\u7684\u5730\u65b9\uff0c\u5c31\u6ca1\u6709\u6211\u58a8\u9f99\u949f\u5929\u4ec7\u8fd9\u53f7\u4eba\u7269\u3002\u201d \u3000\u3000\u718a\u501c\u51b7\u7b11\u4e00\u58f0\uff0c\u62bd\u51fa\u5251\u6765\uff0c\u5728\u9ed1\u591c\u4e4b\u4e2d\uff0c\u5b9b\u5982\u7535\u95ea\uff0c\u957f\u5251\u53cd\u64a9\uff0c\u7531\u4e0b\u800c\u4e0a\uff0c\u4e00\u62db\u201c\u91d1\u9e1f \u521d\u5347\u201d\u9661\u7136\u5411\u949f\u5929\u4ec7\u523a\u53bb\u3002 \u3000\u3000\u949f\u5929\u4ec7\u4e00\u8eac\u8eab\uff0c\u7626\u957f\u7684\u8eab\u8eaf\u7b14\u76f4\u62d4\u4e86\u8d77\u6765\uff0c\u907f\u5f00\u718a\u501c\u653b\u6765\u7684\u4e00\u62db\uff0c\u5de6\u811a\u5f80\u540e\u4e00\u4f38\uff0c\u53f3\u811a \u6a2a\u8e22\uff0c\u5506\u3001\u5506\u3001\u5506\uff0c\u4e00\u8fde\u4e09\u5251\uff0c\u5e26\u8d77\u6597\u5927\u4e09\u6735\u5251\u82b1\uff0c\u76f4\u88ad\u718a\u501c\uff0c\u8fd9\u6b63\u662f\u201c\u98de\u9f99\u4e03\u5f0f\u201d\u4e2d\u7684\u7edd \u62db\u201c\u4e91\u9f99\u4e09\u73b0\u201d\u3002 \u3000\u3000\u718a\u501c\u4e0d\u907f\u4e0d\u95ea\uff0c\u5251\u52bf\u56de\u9886\uff0c\u62ff\u634f\u65f6\u5019\uff0c\u7adf\u662f\u53c8\u5feb\u53c8\u51c6\uff0c\u53cd\u5251\u76f4\u524a\u949f\u5929\u4ec7\u7684\u5251\u5149\uff0c\u949f\u5929\u4ec7 \u77e5\u9053\u82e5\u7136\u88ab\u4ed6\u64a9\u4e0a\uff0c\u81ea\u5df1\u7684\u5251\u5fc5\u5b9a\u8981\u65ad\uff0c\u5e73\u7740\u5251\u8eab\u4e00\u6390\uff0c\u731b\u7136\u4e00\u4e2a\u8f6c\u6298\uff0c\u201c\u795e\u9f99\u6446\u5c3e\u201d\uff0c\u76f4 \u523a\u718a\u501c\u5de6\u9762\u7684\u7a7a\u95e8\u3002 \u3000\u3000\u718a\u501c\u731b\u4e00\u63d0\u6c14\uff0c\u5f80\u53f3\u4e0a\u7a9c\uff0c\u521a\u597d\u907f\u8fc7\u6b64\u5251\uff0c\u949f\u5929\u4ec7\u5251\u4e00\u843d\u7a7a\uff0c\u6beb\u65e0\u518d\u53ef\u60dc\u529b\u4e4b\u5904\uff0c\u53cc\u811a \u4e00\u6c89\uff0c\u4ecd\u662f\u5934\u4e0a\u811a\u4e0b\u5730\u843d\u5728\u5730\u4e0a\uff0c\u6b64\u65f6\uff0c\u718a\u501c\u5df2\u53cd\u5bb9\u6613\u4e3b\uff0c\u201c\u9877\u523b\u98ce\u4e91\u201d\uff1a\u5237\u3001\u5237\u3001\u5237\uff0c\u4e5f \u662f\u4e09\u5251\uff0c\u5206\u53d6\u949f\u5929\u4ec7\u201c\u516d\u9633\u201d\u3001\u201c\u4e73\u7a74\u201d\u8981\u5bb3\uff0c\u65e2\u51c6\u53c8\u72e0\u3002 \u3000\u3000\u949f\u5929\u4ec7\u4e0d\u6562\u7528\u5251\u672a\u6321\uff0c\u4f4e\u5934\u4e00\u7a9c\uff0c\u4ece\u718a\u501c\u7684\u5251\u5149\u4e0b\u7a9c\u51fa\uff0c\u5251\u5149\u64e6\u5934\u800c\u8fc7\uff0c\u60ca\u5f97\u4e00\u8eab\u51b7 \u6c57\uff0c\u518d\u4e5f\u4e0d\u6562\u8f7b\u654c\uff0c\u6b65\u6b65\u4e3a\u8425\uff0c\u548c\u718a\u501c\u5927\u6218\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u5c0f\u5fc3\u53d1\u62db\uff0c\u624d\u53ef\u770b\u51fa\u201c\u98de\u9f99\u4e03\u5f0f\u201d\uff0c\u80fd\u79f0\u96c4\u6b66\u6797\uff0c\u7aef\u7684\u975e\u540c\u5c0f\u53ef\uff0c\u5251\u5f71\u5982\u8fa3\uff0c\u5251 \u5251\u72e0\u63a0\uff0c\u5b9b\u5982\u4e00\u6761\u9752\u9f99\uff0c\u5728\u7a7a\u4e2d\u5f20\u7259\u821e\u722a\u3002 \u3000\u3000\u6b64\u4e24\u4eba\u8fd9\u4e00\u756a\u5927\u6218\uff0c\u786e\u662f\u5434\u8bcf\u4e91\u524d\u6240\u672a\u89c1\u7684\uff0c\u53ea\u770b\u70b9\u70b9\u5251\u6cd5\uff0c\u5982\u6d41\u661f\u98de\u5760\uff0c\u81ea\u7a7a\u4e2d\u6d41\u5230 \u5730\u4e0a\uff0c\u53c8\u60a0\u7136\u81ea\u5730\u9762\u8dc3\u5230\u7a7a\u4e2d\u3002 \u3000\u3000\u718a\u501c\u5728\u62db\u5f0f\u4e0a\u672a\u80fd\u5360\u5f97\u4ec0\u4e48\u4fbf\u5b9c\uff0c\u7686\u56e0\u4ed6\u4e34\u654c\u592a\u5c11\uff0c\u5e38\u5e38\u5931\u53bb\u8bb8\u591a\u5343\u94a7\u4e00\u53d1\u4e4b\u673a\u4f1a\uff0c\u4f46 \u662f\u4ed6\u806a\u660e\u7edd\u9876\uff0c\u77e5\u9053\u949f\u5929\u4ec7\u7684\u957f\u5251\uff0c\u4e0d\u6562\u548c\u81ea\u5df1\u76f8\u78b0\uff0c\u4e8e\u662f\u6bcf\u5230\u8981\u7d27\u5173\u5934\uff0c\u62ff\u5251\u4e0d\u523a\u654c\u8eab\uff0c \u53cd\u627e\u949f\u5929\u4ec7\u7684\u957f\u5251\uff0c\u8fd9\u6837\u949f\u5929\u4ec7\u7a7a\u81ea\u5403\u4e86\u8bb8\u591a\u6697\u4e8f\uff0c\u4f46\u5374\u65e0\u6cd5\u53ef\u60f3\u3002 \u3000\u3000\u4e24\u4eba\u52bf\u5747\u529b\u654c\uff0c\u6253\u4e86\u4e0d\u8981\u4e8c\u5341\u5f0f\uff0c\u8fde\u56db\u5341\u5f0f\u4e5f\u6709\u4e86\uff0c\u5434\u8bcf\u4e91\u5fc3\u4e2d\u4e00\u52a8\uff0c\u731b\u7136\u53eb\u9053\uff1a\u201c\u718a \u8d24\u5f1f\u7094\u5feb\u4f4f\u624b\uff0c\u949f\u5927\u4fa0\u8bf4\u4e8c\u5341\u62db\u5185\uff0c\u4fbf\u89c1\u80dc\u8d1f\uff0c\u73b0\u5728\u4e8c\u5341\u62db\u5df2\u8fc7\uff0c\u60f3\u949f\u5927\u4fa0\u8a00\u800c\u6709\u4fe1\uff0c\u4e0d\u4f1a \u518d\u6253\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8fd9\u4e00\u8bb2\uff0c\u718a\u501c\u867d\u672a\u4f4f\u624b\uff0c\u949f\u5929\u4ec7\u8138\u4e0a\u53ef\u6302\u4e0d\u4f4f\u4e86\uff0c\u6b64\u65f6\u4ed6\u6b63\u7528\u5230\u201c\u91d1\u9f99\u63a2\u722a\u201d\u957f\u5251\u4e0b \u51fb\uff0c\u95fb\u8a00\u731b\u5730\u534a\u5251\u5f0f\u4e00\u6536\uff0c\u53cc\u811a\u4e00\u9762\u4e00\u4f38\uff0c\u957f\u5251\u5e73\u65cb\uff0c\u786c\u751f\u751f\u5c06\u8eab\u8eaf\u62d4\u4e86\u4e0a\u53bb\uff0c\u8f6c\u8eab\u843d\u5728\u5c4b \u9876\u4e4b\u4e0a\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u671d\u5c4b\u540e\u7684\u6697\u5f71\u91cc\u98d8\u7136\u800c\u9000\u3002 \u3000\u3000\u5434\u8bcf\u4e91\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u6211\u771f\u7684\u670d\u4e86\u4f60\uff0c\u4eca\u540e\u6b66\u6797\u9053\u4e2d\uff0c\u5168\u8981\u770b\u4f60\u7684\u8eab\u624b\u4e86\u3002\u201d \u3000\u3000\u8fd9\u65f6\u8fdc\u5904\u5df2\u6709\u9e21\u557c\u58f0\u54cd\u3002 \u3000\u3000\u9556\u8f66\u51fa\u4e86\u4e34\u57ce\uff0c\u65ad\u9b42\u5251\u5c31\u89c9\u5f97\u4e8b\u60c5\u4e0d\u5bf9\uff0c\u4e00\u8def\u4e0a\u4e0d\u7edd\u7684\u6709\u98de\u9a91\u5f80\u6765\uff0c\u9a6c\u4e0a\u7684\u4e5f\u4ff1\u662f\u4e9b\u75be \u88c5\u52b2\u670d\u7684\u7cbe\u58ee\u6c49\u5b50\uff0c\u773c\u8272\u5404\u5404\u4e0d\u540c\uff0c\u795e\u8272\u4e4b\u95f4\uff0c\u4e5f\u662f\u5404\u4e0d\u76f8\u5e72\uff0c\u4f46\u6ee1\u8138\u90fd\u662f\u98ce\u5c18\u4e4b\u8272\uff0c\u50cf\u662f \u90fd\u5954\u8fc7\u8fdc\u8def\u7684\u3002 \u3000\u3000\u5feb\u5230\u6ed5\u53bf\u7684\u65f6\u5019\uff0c\u7a81\u5730\u524d\u9762\u5954\u6765\u51e0\u4e2a\u9a91\u5065\u9a6c\uff0c\u7ea6\u6709\u4e03\u3001\u516b\u4e2a\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u5df2\u8fce\u7740\u9556\u961f\u98de \u5954\u800c\u6765\uff0c\u9a6c\u4e0a\u9a91\u58eb\uff0c\u6d51\u8eab\u9ed1\u8272\u52b2\u88c5\uff0c\u5934\u6234\u9ed1\u8272\u9a6c\u8fde\u5761\u5927\u8349\u5e3d\uff0c\u811a\u4e0a\u662f\u9ed1\u8272\u642c\u5c16\u6d12\u978b\uff0c\u6253\u7740\u5012 \u8d76\u5343\u5c42\u6d6a\u7684\u9ed1\u8272\u88f9\u817f\uff0c\u6700\u5999\u7684\u662f\u8fde\u9a6c\u90fd\u662f\u9ed1\u8272\u7684\uff0c\u800c\u4e14\u80cc\u4e0a\u4ff1\u90fd\u659c\u80cc\u7740\u4e00\u53e3\u4f3c\u5251\u975e\u5251\uff0c\u4f3c\u5200 \u975e\u5200\u7684\u5916\u95e8\u5175\u5668\uff0c\u9ed1\u4e4c\u4e4c\u7684\u6ca1\u6709\u4e00\u4e1d\u5149\u6cfd\uff0c\u975e\u94dc\u975e\u94c1\uff0c\u4e0d\u77e5\u662f\u4ec0\u4e48\u6253\u9020\u3002 \u3000\u3000\u4eba\u9a6c\u6025\u9a70\u800c\u6765\uff0c\u5bf9\u9762\u524d\u9556\u961f\u604d\u5982\u672a\u89c1\uff0c\u5206\u6210\u4e24\u961f\uff0c\u64e6\u7740\u9556\u961f\u7684\u4e24\u65c1\u8fc7\u53bb\uff0c\u5434\u8bcf\u4e91\u6697\u6697\u4e00 \u6570\uff0c\u4e0d\u591a\u4e0d\u5c11\uff0c\u6b63\u662f\u516b\u4eba\u3002 \u3000\u3000\u6b64\u523b\u8fde\u718a\u501c\u4e5f\u89c9\u5f97\u4e8b\u60c5\u4e0d\u5999\uff0c\u8d76\u7740\u9a6c\u8d70\u5230\u9556\u961f\u524d\u9762\uff0c\u7559\u610f\u63d0\u59a8\u3002 \u3000\u3000\u4e0d\u4e00\u4f1a\u529f\u592b\uff0c\u524d\u53c8\u6025\u9a70\u8fc7\u6765\u51e0\u9a91\uff0c\u8fd9\u6b21\u8fde\u9a6c\u5e26\u4eba\u5374\u662f\u901a\u4f53\u7eaf\u767d\uff0c\u9a6c\u4e0a\u7684\u9a91\u58eb\u5374\u4e2a\u4e2a\u90fd\u662f \u5973\u7684\uff0c\u4f46\u4e5f\u662f\u75be\u88c5\u52b2\u670d\uff0c\u4ece\u9556\u961f\u4e24\u65c1\u64e6\u8fc7\u3002 \u3000\u3000\u718a\u501c\u54a6\u4e86\u4e00\u58f0\uff0c\u6389\u5934\u4e00\u671b\u5434\u8bcf\u4e91\uff0c\u540e\u9762\u7684\u5434\u8bcf\u4e91\u4e5f\u89c9\u5f97\u4e8b\u60c5\u592a\u8fc7\u79bb\u5947\uff0c\u8fd9\u4e24\u961f\u7537\u5973\uff0c\u7b80 \u76f4\u770b\u4e0d\u51fa\u662f\u4ec0\u4e48\u9053\u8def\uff0c\u5434\u8bcf\u4e91\u4e0d\u7981\u5fc3\u4e2d\u6697\u81ea\u6253\u9f13\uff0c\u5e0c\u671b\u8fd9\u4e24\u961f\u9a91\u58eb\u548c\u81ea\u5df1\u7684\u9556\u8f66\u65e0\u5173\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u50ac\u9a6c\u8d76\u4e0a\u524d\u53bb\uff0c\u5bf9\u718a\u501c\u9053\uff1a\u201c\u6211\u4e5f\u770b\u8fd9\u5929\u7684\u8def\u9053\u4e0d\u5bf9\uff0c\u7b49\u4f1a\u5230\u4e86\u6ed5\u53bf\uff0c\u6700\u597d\u65e9\u4e9b \u6b47\u606f\u2026\u2026\u201d \u3000\u3000\u4ed6\u6b63\u8bf4\u7740\u8bdd\u65f6\uff0c\u6cfc\u5587\u5587\u4e00\u9635\u8e44\u58f0\uff0c\u65b9\u624d\u8fc7\u53bb\u7684\u90a3\u4e24\u961f\u9a91\u58eb\uff0c\u53c8\u7b56\u9a6c\u5954\u4e86\u56de\u6765\uff0c\u8fd9\u6b21\u4ed6\u4eec \u5374\u5341\u516d\u9a91\u4e00\u540c\u56de\u6765\uff0c\u800c\u4e14\u5954\u9a70\u7684\u65f6\u5019\uff0c\u9ed1\u9a6c\u4e0e\u767d\u9a6c\u76f8\u95f4\uff0c\u4e00\u6837\u4e00\u5339\uff0c\u53c8\u662f\u4ece\u9556\u961f\u4e24\u65c1\u6025\u9a70\u800c \u8fc7\u3002 \u3000\u3000\u5434\u8bcf\u4e91\u6697\u601d\u9053\uff1a\u201c\u8fd9\u53c8\u4e0d\u50cf\u662f\u9ed1\u9053\u4e2d\u8e29\u76d8\u5b50\u7684\uff0c\u800c\u4e14\u9644\u8fd1\u4e5f\u7edd\u65e0\u5b89\u7a91\u7acb\u5be8\u7684\uff0c\u90a3\u4e48\u8fd9\u4e9b \u7a76\u7adf\u662f\u4f55\u7b49\u4eba\u7269\uff0c\u6c14\u6d3e\u58f0\u52bf\uff0c\u53c8\u90fd\u5982\u6b64\u4e4b\u5927\u3002\u201d \u3000\u3000\u4ed6\u6b63\u81ea\u601d\u7d22\u95f4\uff0c\u524d\u9762\u8def\u4e0a\u73b0\u51fa\u4e00\u7247\u6811\u6797\uff0c\u6811\u6797\u867d\u7136\u4e0d\u592a\u5927\uff0c\u4f46\u9752\u7eb1\u5e10\u91cc\uff0c\u6b63\u662f\u5f3a\u6881\u51fa\u6ca1 \u7684\u53bb\u5904\uff0c\u65ad\u9b42\u5251\u4e0d\u7981\u7709\u5934\u4e00\u76b1\u3002 \u3000\u3000\u8f6c\u773c\u4e4b\u95f4\uff0c\u9556\u5df2\u8fd1\u6811\u6797\uff0c\u540e\u9762\u5ffd\u7136\u8e44\u58f0\u5927\u4f5c\uff0c\u524d\u9762\u7684\u6811\u6797\u4e00\u9635\u54cd\u52a8\uff0c\u7247\u523b\u8f6c\u51fa\u6570\u5341\u9a91\u5065 \u9a6c\uff0c\u6b64\u65f6\u540e\u9762\u7684\u9a6c\u961f\u4e5f\u6b63\u5305\u6284\u4e0a\u6765\uff0c\u4e8e\u662f\u545c\u8fdc\u7684\u9556\u961f\uff0c\u88ab\u767e\u6570\u5341\u5339\u5065\u9a6c\u5708\u5728\u6838\u5fc3\u3002 \u3000\u3000\u5434\u8bcf\u4e91\u8d76\u5fd9\u626c\u8d77\u5de6\u624b\uff0c\u545c\u8fdc\u9556\u5c40\u7684\u9556\u4f19\u4eec\u5012\u662f\u7ecf\u8fc7\u5927\u9635\u4ed7\u7684\uff0c\u5e76\u4e0d\u614c\u4e71\uff0c\u4ff1\u90fd\u7d27\u9760\u5728\u9556 \u8f66\u65c1\u8fb9\uff0c\u9759\u7b49\u5434\u8bcf\u4e91\u7684\u5429\u5490\u3002 \u3000\u3000\u5434\u8bcf\u4e91\u7565\u4e00\u6253\u91cf\u8fd9\u4e9b\u9a6c\u4e0a\u7684\u6c49\u5b50\uff0c\u5c31\u77e5\u9053\u4ff1\u662f\u624b\u4e0b\u55bd\u7f57\u4eec\uff0c\u6b63\u4e3b\u513f\u5c1a\u672a\u5230\u5462\uff0c\u4e8e\u662f\u508d\u7740 \u718a\u501c\u5e76\u9a91\u800c\u7acb\uff0c\u9759\u5f85\u53d8\u5316\u3002 \u3000\u3000\u718a\u501c\u4f4e\u58f0\u95ee\u5434\u8bcf\u4e91\u9053\uff1a\u201c\u600e\u4e48\u8fd9\u4e9b\u4eba\u5374\u90fd\u4e0d\u662f\u521a\u624d\u90a3\u4e9b\u9a91\u58eb\uff1f\u201d \u3000\u3000\u5434\u8bcf\u4e91\u5fc3\u4e2d\u4e5f\u81ea\u7eb3\u95f7\uff0c\u679c\u7136\u521a\u624d\u90a3\u9ed1\u767d\u4e24\u961f\u9a91\u58eb\uff0c\u6b64\u523b\u4e00\u4e2a\u4e5f\u6ca1\u6709\u770b\u89c1\u3002 \u3000\u3000\u4e0d\u4e00\u4f1a\u5de5\u592b\uff0c\u53c8\u6709\u6570\u5341\u5339\u9a6c\u81ea\u540e\u8d76\u4e86\u8fc7\u6765\uff0c\u5434\u8bcf\u4e91\u5fc3\u4e2d\u6697\u81ea\u53d1\u614c\uff0c\u7eff\u6797\u4e2d\u4eba\u5728\u9053\u4e0a\u593a \u9556\uff0c\u8fd8\u6ca1\u6709\u542c\u8bf4\u8fc7\u51fa\u52a8\u5982\u8bb8\u591a\u7684\u4eba\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u4e00\u4f1a\u5de5\u592b\uff0c\u6811\u6797\u80cc\u540e\u8f6c\u8fc7\u4e03\u5339\u9a6c\u6765\uff0c\u5f53\u5148\u90a3\u4eba\u5934\u5982\u5df4\u6597\uff0c\u8eab\u6750\u9ad8\u5927\uff0c\u9a91\u5728\u9a6c\u4e0a\u597d \u50cf\u9a91\u5728\u9a74\u4e0a\u4e00\u6837\uff0c\u4e24\u6761\u817f\u51e0\u4e4e\u591f\u7740\u5730\u4e0a\u3002 \u3000\u3000\u5434\u8bcf\u4e91\u4e00\u770b\u8ba4\u5f97\uff0c\u6b64\u4eba\u4fbf\u662f\u5305\u728a\u5c97\u7684\u74e2\u9a6c\u5b50\uff0c\u957f\u5854\u5929\u738b\u53f6\u5764\u7136\u3002 \u3000\u3000\u7b2c\u4e8c\u5339\u9a6c\u4e0a\u5750\u7684\u662f\u4e2a\u6234\u53d1\u5934\u9640\uff0c\u5434\u8bcf\u4e91\u4e5f\u8ba4\u5f97\u90a3\u662f\u6c5f\u6e56\u4e0a\u6709\u540d\u7684\u72ec\u884c\u76d7\u65e5\u6708\u5934\u9640\u3002 \u3000\u3000\u7b2c\u4e09\u3001\u56db\u4e24\u4eba\uff0c\u662f\u4e24\u4e2a\u9762\u8c8c\u5b8c\u5168\u4e00\u6837\u7684\u7626\u524a\u6c49\u5b50\uff0c\u5434\u8bcf\u4e91\u4e00\u60f3\uff0c\u8bb0\u5f97\u4fbf\u662f\u52b3\u5c71\u53cc\u9e64\uff0c\u5728 \u5c71\u4e1c\u534a\u5c9b\u5927\u5927\u6709\u540d\u90d1\u5251\u5e73\u3001\u90d1\u5251\u9752\u3002 \u3000\u3000\u7b2c\u4e94\u4eba\u5374\u662f\u4e2a\u6587\u58eb\u8863\u5c65\u7684\u5e74\u8f7b\u540e\u751f\uff0c\u5bb9\u8c8c\u5341\u5206\u6e05\u79c0\uff0c\u8d64\u624b\u7a7a\u62f3\uff0c\u53ea\u662f\u5de6\u8fb9\u6302\u7740\u4e00\u4e2a\u9e7f\u76ae \u9556\u56ca\uff0c\u53cc\u624b\u6234\u7740\u4e00\u53cc\u4f3c\u7eff\u975e\u7eff\uff0c\u4e4c\u5149\u95ea\u95ea\u7684\u624b\u5957\u3002 \u3000\u3000\u7b2c\u516d\u4eba\u66f4\u662f\u5947\u602a\uff0c\u5168\u8eab\u91d1\u8272\u7532\u80c4\uff0c\u8eab\u6750\u9ad8\u5927\uff0c\u7adf\u50cf\u4e2a\u9635\u4e0a\u7684\u5c06\u519b\u3002 \u3000\u3000\u7b2c\u4e03\u4eba\u662f\u4e2a\u67af\u7626\u7684\u8001\u8005\u3002 \u3000\u3000\u5434\u8bcf\u4e91\u53ea\u8ba4\u5f97\u524d\u9762\u56db\u4eba\uff0c\u4f46\u9e23\u8fdc\u9556\u5c40\u5374\u548c\u4ed6\u4eec\u7d20\u65e0\u51a4\u4ec7\uff0c\u4e0d\u77e5\u6b64\u6b21\u4e3a\u4f55\u8054\u624b\u6765\u593a\u9556\uff0c\u7686 \u56e0\u7eff\u6797\u4e2d\u9664\u975e\u53c8\u6709\u7740\u6df1\u4ec7\u5927\u6028\u7684\u4eba\uff0c\u4ece\u4e0d\u8054\u624b\u593a\u9556\u7684\u3002 \u3000\u3000\u4e03\u5339\u9a6c\u6765\u5230\u8fd1\u524d\uff0c\u90a3\u4e3a\u9996\u7684\u6258\u5854\u5929\u738b\u5fae\u4e00\u62b1\u62f3\uff0c\u8bf4\u9053\uff1a\u201c\u5434\u9556\u5934\u4e00\u5411\u53ef\u597d\uff0c\u8fd1\u6765\u5c11\u89c1\u5f97 \u5f88\uff0c\u5012\u6559\u5144\u5f1f\u975e\u5e38\u60f3\u5ff5\u3002\u201d\u8bf4\u5b8c\u54c8\u54c8\u4e00\u9635\u72c2\u7b11\u3002 \u3000\u3000\u5434\u8bcf\u4e91\u4e5f\u542b\u7b11\u70b9\u5934\u7b11\u9053\uff1a\u201c\u53f6\u5f53\u5bb6\u7684\u8fd9\u4e00\u5411\u4e5f\u597d\u5417\uff1f\u600e\u7684\u4e24\u4f4d\u90d1\u5f53\u5bb6\u7684\u4e5f\u548c\u65e5\u6708\u6cd5\u5e08\u4e00 \u9f50\u6765\u4e86\uff0c\u96be\u9053\u655d\u9556\u5c40\u6709\u4ec0\u4e48\u5730\u65b9\u793c\u8c8c\u4e0d\u5468\u5417\uff1f\u201d \u3000\u3000\u90a3\u65e5\u6708\u5934\u9640\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u8bdd\uff0c\u4ec0\u4e48\u8bdd\uff0c\u5f85\u8d2b\u50e7\u5148\u66ff\u4e8c\u9556\u5934\u5f15\u89c1\u51e0\u4f4d\u9ad8\u4eba\u3002\u201d \u3000\u3000\u4ed6\u6307\u7740\u7b2c\u4e94\u4eba\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u4fbf\u662f\u4eba\u79f0\u4e03\u6bd2\u4e66\u751f\u7684\u5510\u7fbd\u5510\u5927\u4fa0\uff0c\u8fd9\u4f4d\u4fbf\u662f\u9ed1\u6d77\u4e2d\u7684\u603b\u74e2\u628a \u5b50\u6d77\u9f99\u738b\u8d75\u4f69\u4fa0\uff0c\u8fd9\u4f4d\u4fbf\u662f\u6614\u5e74\u5a01\u9547\u8fb9\u9672\u7684\u751f\u6b7b\u6c64\u5224\u5b5d\u5b8f\u6c64\u5927\u4fa0\uff0c\u60f3\u5434\u9556\u5934\u5fc5\u6709\u4e2a\u8033\u95fb\uff0c\u201d \u5434\u8bcf\u4e91\u4e00\u542c\u8fd9\u4e09\u4eba\u7684\u540d\u53f7\uff0c\u4e0d\u7981\u5012\u62bd\u4e86\u53e3\u51c9\u6c14\uff0c\u6b64\u4e09\u4eba\u53ea\u8981\u6709\u4e00\u4e2a\u5728\u6b64\uff0c\u4fbf\u662f\u65e0\u6cd5\u6536\u62fe\u4e4b \u5c40\uff0c\u4f55\u51b5\u4e09\u4eba\u7adf\u5168\u90e8\u6765\u4e86\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u7acb\u5373\u62b1\u62f3\u62f1\u624b\u9053\uff1a\u201c\u4e45\u4ef0\u4e09\u4f4d\u7684\u5927\u540d\uff0c\u4eca\u65e5\u5f97\u89c1\uff0c\u5b9e\u662f\u5feb\u6170\u5e73\u751f\u3002\u201d \u3000\u3000\u90a3\u4e03\u6bd2\u4e66\u751f\u4e5f\u9a6c\u4e0a\u62b1\u62f3\u9053\uff1a\u201c\u9601\u4e0b\u60f3\u5fc5\u662f\u9e23\u8fdc\u9556\u5c40\u7684\u4e8c\u9556\u5934\u65ad\u9b42\u5251\u5434\u5927\u4fa0\u4e86\u3002\u201d\u4ed6\u659c\u773c \u4e00\u770b\u718a\u501c\u8bf4\uff1a\u201c\u8fd9\u4f4d\u5374\u964c\u751f\u5f97\u5f88\u3002\u201d \u3000\u3000\u5434\u8bcf\u4e91\u63a5\u7740\u8bf4\uff1a\u201c\u8fd9\u4f4d\u4fbf\u662f\u6614\u5e74\u661f\u6708\u53cc\u5251\u7684\u8863\u94b5\u4f20\u4eba\u718a\u501c\u3002\u201d \u3000\u3000\u5510\u7fbd\u54e6\u4e86\u4e00\u58f0\uff0c\u6ee1\u8138\u5806\u7b11\u9053\uff1a\u201c\u8fd9\u51e0\u5929\u5e38\u542c\u6c5f\u6e56\u670b\u53cb\u8bf4\u8d77\uff0c\u6c5f\u5b81\u5e9c\u51fa\u4e86\u4e86\u4e0d\u5f97\u7684\u82f1\u96c4\uff0c \u60f3\u4e0d\u5230\u4eca\u65e5\u5374\u6709\u7f18\u78b0\u5230\u4e86\u3002\u201d \u3000\u3000\u718a\u501c\u4e5f\u5728\u9a6c\u4e0a\u5fae\u4e00\u62f1\u624b\u3002 \u3000\u3000\u5510\u7fbd\u53c8\u8bf4\u9053\uff1a\u201c\u660e\u4eba\u4e0d\u8bf4\u6697\u8bdd\uff0c\u54b1\u4eec\u4eca\u5929\u7684\u6765\u610f\uff0c\u60f3\u4e24\u4f4d\u5fc5\u4e5f\u77e5\u9053\u4e86\uff0c\u672c\u6765\u53f6\u5f53\u5bb6\u7684\u548c \u4e24\u4f4d\u90d1\u5f53\u5bb6\u7684\u548c\u8d35\u9556\u5c40\u7684\u738b\u603b\u9556\u5934\u53e6\u6709\u6881\u5b50\uff0c\u4f46\u4eca\u65e5\u738b\u603b\u9556\u5934\u65e2\u7136\u4e0d\u5728\uff0c\u6b64\u4e8b\u4e5f\u5c31\u4e0d\u63d0\u7b97 \u4e86\uff0c\u4f46\u662f\u8d35\u9556\u5c40\u8fd9\u6b21\u6240\u62bc\u7684\u9556\uff0c\u5c0f\u5f1f\u548c\u8fd9\u51e0\u4f4d\u5374\u975e\u5e38\u6709\u5174\u8da3\uff0c\u5434\u9556\u5934\u82e5\u80fd\u5c06\u9556\u8f66\u7559\u4e0b\uff0c\u90a3\u6211 \u5510\u67d0\u4eba\u62c5\u4fdd\u4e0d\u635f\u8d35\u9556\u5c40\u7684\u4e00\u8349\u4e00\u6728\uff0c\u5982\u82e5\u4e0d\u7136\uff0c\u60f3\u5434\u9556\u5934\u662f\u4e2a\u806a\u660e\u4eba\uff0c\u4f60\u8bf7\u770b\u4eca\u65e5\u7684\u60c5\u52bf\uff0c \u4e5f\u7528\u4e0d\u7740\u5c0f\u5f1f\u591a\u8bf4\u4e86\uff0c\u8fd8\u671b\u5434\u9556\u5934\u4e09\u601d\u3002\u201d \u3000\u3000\u5434\u8bcf\u4e91\u6b64\u65f6\u65b9\u5bf8\u5df2\u4e71\uff0c\u989d\u4e0a\u7684\u6c57\u73e0\uff0c\u7c41\u7c41\u5f80\u4e0b\u76f4\u6d41\u3002\u4e00\u65f6\u7adf\u6014\u5728\u9a6c\u4e0a\uff0c\u4e0d\u77e5\u7a76\u7adf\u5e94\u8be5\u5982 \u4f55\u7b54\u5e94\u3002 \u3000\u3000\u718a\u501c\u867d\u7136\u4e0d\u77e5\u6d77\u9f99\u738b\u4e0e\u751f\u6b7b\u5224\u7684\u540d\u5934\uff0c\u4f46\u4e03\u6bd2\u4e66\u751f\u5510\u7fbd\uff0c\u4ed6\u5374\u542c\u738b\u667a\u8ff0\u8bf4\u8fc7\uff0c\u518d\u52a0\u4e0a\u8fd9 \u767e\u6570\u5341\u9a91\uff0c\u77e5\u9053\u4eca\u5929\u81ea\u5df1\u8fd9\u9762\u786e\u96be\u8ba8\u5f97\u597d\u53bb\uff0c\u4f46\u662f\u53d7\u4eba\u4e4b\u6258\uff0c\u5728\u6b64\u79cd\u60c5\u51b5\u4e4b\u4e0b\uff0c\u4e3a\u4eba\u4e3a\u5df1\uff0c \u52bf\u53c8\u4e0d\u80fd\u5c06\u9556\u8f66\u53cc\u624b\u5949\u9001\uff0c\u60f3\u4e86\u8bb8\u4e45\uff0c\u4ed6\u7adf\u633a\u8eab\u800c\u51fa\u3002 \u3000\u3000\u4ed6\u671d\u5bf9\u9762\u9a6c\u4e0a\u4e03\u4eba\u62b1\u62f3\u4e00\u62f1\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u5c0f\u5f1f\u5e74\u8f7b\u8bc6\u6d45\uff0c\u53c8\u4e0d\u61c2\u5f97\u6c5f\u6e56\u89c4\u77e9\uff0c\u4f46\u662f\u60f3 \u5404\u4f4d\u90fd\u662f\u6210\u540d\u7684\u82f1\u96c4\uff0c\u4eca\u65e5\u5373\u4f7f\u4ee5\u591a\u51cc\u5c11\uff0c\u5c06\u9556\u593a\u4e0b\uff0c\u65e5\u540e\u4f20\u5c06\u51fa\u53bb\uff0c\u4e8e\u5404\u4f4d\u7684\u989c\u9762\u5fc5\u751a\u6709 \u635f\uff0c\u4f46\u5404\u4f4d\u52bf\u5728\u5fc5\u5f97\uff0c\u5c0f\u5f1f\u53d7\u4eba\u4e4b\u6258\uff0c\u4e5f\u662f\u5b9a\u8981\u62fc\u6b7b\u4fdd\u62a4\uff0c\u90a3\u4e48\u5c0f\u5f1f\u5012\u6709\u4e00\u611a\u89c1\uff0c\u4e0d\u77e5\u5404\u4f4d \u53ef\u8d5e\u6210\u5426\uff1f\u201d \u3000\u3000\u4ed6\u8bf4\u5b8c\u5373\u9759\u5750\u5728\u9a6c\u4e0a\uff0c\u7b49\u5f85\u7b54\u8986\uff0c\u4f17\u4eba\u4ff1\u672a\u60f3\u5230\u718a\u501c\u4f1a\u633a\u8eab\u800c\u51fa\uff0c\u6014\u4e86\u534a\u664c\uff0c\u8fd8\u662f\u5510\u7fbd \u8bf4\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u4f4d\u718a\u82f1\u96c4\u5012\u771f\u662f\u5feb\u4eba\u5feb\u8bed\uff0c\u602a\u4e0d\u5f97\u80fd\u540d\u52a8\u6c5f\u5357\uff0c\u4e0d\u77e5\u718a\u82f1\u96c4\u6709\u4f55\u9ad8\u89c1\uff0c\u8bf7 \u8d76\u5feb\u8bf4\u51fa\u6765\uff0c\u82e5\u771f\u662f\u5408\u60c5\u5408\u7406\uff0c\u5c0f\u5f1f\u4eec\u4e00\u5b9a\u65e0\u8bdd\u53ef\u8bf4\u3002\u201d \u3000\u3000\u4e8e\u662f\u718a\u501c\u62db\u624b\u5c06\u4e03\u53e3\u7bb1\u5b50\u5b8c\u5168\u5378\u4e0b\u6765\uff0c\u653e\u5728\u5730\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u91cc\u5171\u6709\u4e03\u53e3\u7bb1\u5b50\uff0c\u4f46\u771f\u88c5 \u6709\u5b9d\u7269\u7684\u53ea\u6709\u4e00\u53e3\uff0c\u800c\u8bf8\u4f4d\u53c8\u6070\u597d\u662f\u4e03\u4eba\uff0c\u73b0\u5728\u6211\u5c06\u8fd9\u4e03\u53e3\u7bb1\u5b50\u653e\u5728\u5730\u4e0a\uff0c\u8bf8\u4f4d\u6bcf\u4eba\u53ef\u62ff\u4e00 \u53e3\uff0c\u8c01\u4eba\u8fd0\u6c14\u6700\u597d\uff0c\u8c01\u5c31\u5f97\u5230\u8fd9\u4ef6\u81f3\u5b9d\u3002\u201d \u3000\u3000\u718a\u501c\u8bdd\u4e00\u8bf4\u5b8c\uff0c\u65e5\u6708\u5934\u9640\u3001\u6258\u5854\u5929\u7389\u7b49\u4ff1\u90fd\u9f50\u58f0\u8d5e\u6210\uff0c\u800c\u5510\u7fbd\u53ca\u6c64\u5b5d\u5b8f\u5374\u4e0d\u53d1\u4e00\u8a00\u3002 \u3000\u3000\u987b\u77e5\u65e5\u6708\u5934\u9640\u3001\u52b3\u5c71\u53cc\u9e64\u3001\u6258\u5854\u5929\u738b\u7684\u6b66\u529f\uff0c\u6bd4\u8d77\u751f\u6b7b\u5224\u53ca\u5510\u7fbd\uff0c\u662f\u4e07\u4e07\u4e0d\u53ca\u7684\uff0c\u4ed6\u4eec \u8fd9\u6b21\u524d\u6765\u622a\u9556\uff0c\u662f\u56e0\u66fe\u7ecf\u5403\u8fc7\u7c89\u9762\u82cf\u79e6\u738b\u667a\u9011\u7684\u5927\u4e8f\uff0c\u6545\u6b64\u968f\u5510\u7fbd\u7b49\u524d\u6765\u62a5\u590d\uff0c\u81f3\u4e8e\u6210\u5f62\u9996 \u4e4c\uff0c\u4ed6\u4eec\u5374\u4e0d\u6562\u5984\u60f3\u5f97\u5230\uff0c\u800c\u6d77\u9f99\u738b\u6b64\u6b21\u4ec5\u662f\u9002\u9022\u5176\u4f1a\uff0c\u524d\u6765\u51d1\u51d1\u70ed\u95f9\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u60f3\u5f97\u5230 \u8fd9\u81f3\u5b9d\u7684\u91ce\u5fc3\u3002 \u3000\u3000\u73b0\u5728\u718a\u501c\u6240\u63d0\u51fa\u7684\u610f\u89c1\u5982\u6b64\uff0c\u4ed6\u4eec\u4e00\u60f3\u81ea\u5df1\u4e5f\u6709\u4e00\u5206\u673a\u4f1a\u5f97\u6b64\u81f3\u5b9d\uff0c\u5f53\u7136\u8d5e\u6210\u3002 \u3000\u3000\u4e8e\u662f\u718a\u501c\u53c8\u63a5\u7740\u8bf4\uff1a\u201c\u8fd9\u4e48\u9e23\u8fdc\u9556\u5c40\u65e2\u5c06\u5b9d\u7269\u53cc\u624b\u5949\u9001\uff0c\u5404\u4f4d\u5f53\u7136\u4ff1\u65e0\u8bdd\u8bf4\uff0c\u4e5f\u4e0d\u4f1a\u7559 \u96be\u9e23\u8fdc\u9556\u5c40\u7684\u4eba\u4e86\uff0c\u53ef\u662f\u5c0f\u5f1f\u53d7\u4eba\u6240\u6258\uff0c\u6765\u4fdd\u62a4\u8fd9\u4ef6\u5b9d\u7269\uff0c\u81ea\u4e5f\u4e0d\u7518\u767d\u767d\u88ab\u4eba\u62ff\u8d70\uff0c\u8bf8\u4f4d\u8c01 \u62ff\u4e86\u90a3\u7bb1\u771f\u7684\u5b9d\u7269\uff0c\u5c0f\u5f1f\u5374\u662f\u77e5\u9053\u7684\uff0c\u5c0f\u5f1f\u672c\u7740\u826f\u5fc3\uff0c\u81ea\u8981\u4ece\u90a3\u4eba\u624b\u4e2d\u5c06\u5b9d\u7269\u62a2\u56de\uff0c\u60f3\u5404\u4f4d \u4ff1\u662f\u6210\u540d\u82f1\u96c4\uff0c\u82e5\u7136\u88ab\u5c0f\u5f1f\u6253\u8d25\uff0c\u90a3\u81ea\u7136\u8be5\u5c06\u5b9d\u7269\u8fd8\u7ed9\u5c0f\u5f1f\uff0c\u5404\u4f4d\u60f3\u8fd9\u529e\u6cd5\u53ef\u884c\u5f97\u901a\u5417\uff1f\u201d \u3000\u3000\u5510\u7fbd\u7b49\u88ab\u718a\u501c\u7ed5\u7740\u5f2f\u5b50\u8bf4\u4e86\u4e00\u5927\u5806\uff0c\u7adf\u90fd\u9ed8\u7136\uff0c\u5510\u7fbd\u7a81\u7136\u5fc3\u4e00\u52a8\uff0c\u56de\u5934\u5411\u751f\u6b7b\u5224\u770b\u4e86\u4e00 \u773c\uff0c\u89c1\u4ed6\u6b63\u5728\u9894\u9996\u5fae\u7b11\uff0c\u9042\u7acb\u5373\u53e3\u7b54\u9053\uff1a\u201c\u8fd9\u529e\u6cd5\u751a\u662f\u516c\u5e73\uff0c\u5c31\u7167\u718a\u82f1\u96c4\u6240\u8bf4\u7684\u505a\u597d\u4e86\u3002\u201d \u3000\u3000\u4e8e\u662f\u718a\u501c\u5c06\u4e03\u53e3\u7bb1\u5b50\uff0c\u6781\u6574\u9f50\u5730\u6392\u5728\u4e00\u5217\uff0c\u653e\u5728\u4ed6\u4eec\u9762\u524d\u7684\u5730\u4e0a\u3002 \u3000\u3000\u6b64\u65f6\u7a81\u7136\u6811\u679d\u4e00\u58f0\u54cd\u52a8\uff0c\u4ece\u6811\u4e0a\u8dc3\u4e0b\u4e5d\u4eba\uff0c\u4e5f\u662f\u6781\u6574\u9f50\u6392\u6210\u4e00\u5217\uff0c\u8dc3\u81f3\u7bb1\u5b50\u524d\u9762\uff0c\u5708\u5b50 \u91cc\u7acb\u523b\u4e00\u9635\u9a9a\u52a8\uff0c\u718a\u501c\u4e5f\u7acb\u523b\u5927\u60ca\uff0c\u5b9a\u76ee\u4e00\u770b\uff0c\u8fd9\u4e5d\u4eba\u7adf\u662f\u9e3d\u8863\u767e\u7ed3\u7684\u4e5e\u4e10\uff0c\u7b11\u58f0\u5140\u81ea\u672a \u505c\u3002 \u3000\u3000\u8fd9\u4e5d\u4e2a\u4e5e\u4e10\u843d\u5728\u5730\u4e0a\u540e\uff0c\u672a\u7b49\u522b\u4eba\u5f00\u53e3\uff0c\u5f53\u4e2d\u7684\u90a3\u4e00\u4e2a\u5df2\u6717\u58f0\u7b11\u9053\uff1a\u201c\u8fd9\u4e3b\u610f\u786e\u662f\u597d\u6781 \u4e86\uff0c\u53ea\u662f\u6211\u4eec\u5f1f\u5144\u4e5f\u8981\u7b97\u4e0a\u4e00\u4efd\u201d\u718a\u501c\u5c1a\u672a\u7b54\u8bdd\uff0c\u5510\u7fbd\u5df2\u81ea\u9a6c\u4e0a\u8dc3\u51fa\uff0c\u8f7b\u98d8\u98d8\u5730\u843d\u5728\u7bb1\u5b50 \u4e0a\uff0c\u7b54\u9053\uff1a\u201c\u6211\u5f53\u662f\u8c01\uff0c\u539f\u6765\u662f\u84dd\u5927\u5148\u751f\u5230\u4e86\uff0c\u84dd\u5927\u5148\u751f\u65e2\u7136\u4e5f\u6709\u5174\u8da3\uff0c\u90a3\u4e48\u4e5f\u7b97\u4e0a\u4e00\u4efd\uff0c \u81ea\u7136\u662f\u5e94\u5f53\u7684\u4e86\uff0c\u53ea\u662f\u6211\u867d\u7b54\u5e94\uff0c\u522b\u4eba\u82e5\u4e0d\u7b54\u5e94\uff0c\u5c0f\u5f1f\u4ea6\u662f\u65e0\u6cd5\u3002\u201d\u8bf4\u5b8c\u4e00\u9635\u54af\u54af\u5927\u7b11\u3002 \u3000\u3000\u84dd\u5927\u5148\u751f\u542c\u4e86\uff0c\u4e24\u773c\u4e00\u77aa\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u7a77\u8981\u996d\u7684\u8fdc\u9053\u6765\u6b64\uff0c\u8c01\u597d\u610f\u601d\u8e22\u5f00\u6211\u5440\u3002\u201d \u3000\u3000\u6b64\u65f6\u90a3\u6d77\u9f99\u738b\u8d75\u4f69\u4fa0\u7b11\u5730\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u6b64\u4e8b\u5c0f\u5f1f\u672c\u89c9\u5f97\u65e0\u751a\u5927\u610f\u601d\uff0c\u65e2\u662f\u8fd9\u6837\uff0c\u5c0f\u5f1f\u9000 \u51fa\u597d\u4e86\uff0c\u5c0f\u5f1f\u7684\u8fd9\u4e00\u4efd\uff0c\u8ba9\u4e0e\u84dd\u5927\u5148\u751f\u5982\u4f55\uff1f\u201d \u3000\u3000\u718a\u501c\u4e0a\u4e0b\u6253\u91cf\u7740\u8fd9\u4f4d\u5728\u6c5f\u6e56\u4e0a\u52bf\u529b\u6781\u5927\u7684\u4e10\u5e2e\u5e2e\u4e3b\uff0c\u53ea\u89c1\u4ed6\u4e4d\u773c\u671b\u53bb\uff0c\u548c\u666e\u901a\u7684\u4e5e\u4e10\u5e76 \u65e0\u4e8c\u6837\uff0c\u53ea\u662f\u53cc\u76ee\u795e\u5149\u9971\u6ee1\uff0c\u8863\u670d\u867d\u662f\u5343\u75ae\u767e\u5b54\uff0c\u8865\u4e86\u53c8\u8865\uff0c\u6d17\u7684\u5374\u662f\u6781\u5e72\u51c0\uff0c\u5c24\u5176\u523a\u773c\u7684 \u662f\u53cc\u624b\u5b9b\u5982\u767d\u7389\uff0c\u53f3\u624b\u4e2d\u6307\u4e0a\u6234\u4e86\u4e2a\u5947\u5f62\u6212\u6307\uff0c\u5f0f\u6837\u5947\u53e4\u3002 \u3000\u3000\u84dd\u5927\u5148\u751f\u7b11\u7740\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u6709\u4eba\u5272\u7231\uff0c\u90a3\u662f\u6700\u597d\u7684\u4e86\uff0c\u6b64\u523b\u65f6\u5149\u5df2\u4e0d\u65e9\uff0c\u6211\u770b\u4e00\u4eba\u5148\u62ff \u4e00\u4e2a\u7bb1\u5b50\u518d\u8bf4\uff0c\u770b\u770b\u8c01\u7684\u798f\u5927\u547d\u5927\uff0c\u5f97\u5230\u8fd9\u4ef6\u4e1c\u897f\u3002\u201d \u3000\u3000\u5510\u7fbd\u8bf4\u9053\uff1a\u201c\u655d\u4eba\u4e5f\u6709\u6b64\u610f\uff0c\u65e9\u4e9b\u4e86\u65ad\u6700\u597d\u3002\u201d\u8bf4\u7740\u968f\u624b\u6361\u4e86\u4e00\u4e2a\u7bb1\u5b50\u3002 \u3000\u3000\u7fa4\u8c6a\u4e5f\u90fd\u4e0b\u9a6c\uff0c\u4e00\u4eba\u62ff\u4e86\u4e00\u53e3\u7bb1\u5b50\u3002 \u3000\u3000\u5510\u7fbd\u6240\u6361\u7684\u90a3\u53e3\uff0c\u662f\u7d2b\u8272\u4e1d\u5e26\u6240\u7f1a\u4f4f\u7684\uff0c\u84dd\u5927\u5148\u751f\u9009\u7684\u662f\u84dd\u7684\uff0c\u52b3\u5c71\u53cc\u9e64\u6240\u53d6\u7684\u662f\u9ec4\u3001 \u7ea2\u4e24\u8272\uff0c\u751f\u6b7b\u5224\u62ff\u7684\u662f\u767d\u8272\u7684\uff0c\u6258\u5854\u5929\u738b\u9009\u7684\u662f\u8910\u8272\u7684\uff0c\u90a3\u7cfb\u7740\u9ed1\u8272\u4e1d\u5e26\u7684\u4e00\u53e3\uff0c\u5374\u88ab\u65e5\u6708 \u5934\u9640\u53d6\u53bb\u3002 \u3000\u3000\u718a\u501c\u671d\u65e5\u6708\u5934\u9640\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u5f53\u5bb6\u7684\u6240\u53d6\u7684\uff0c\u6b63\u662f\u90a3\u53e3\u771f\u6b63\u7684\u85cf\u5b9d\u4e4b\u7bb1\uff0c\u73b0\u5728\u5e9f\u8bdd\u5c11 \u8bf4\uff0c\u4f60\u82e5\u80fd\u80dc\u5f97\u8fc7\u6211\uff0c\u8fd9\u53e3\u7bb1\u5b50\u7406\u5e94\u5f52\u4f60\u6240\u6709\uff0c\u5426\u5219\u7684\u8bdd\uff0c\u5c31\u8bf7\u5f53\u5bb6\u7684\u5c06\u7bb1\u5b50\u4ea4\u56de\uff0c\u8bf7\uff0c \u8bf7\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u4ed6\u5c31\u5168\u795e\u51dd\u89c6\u7740\u65e5\u6708\u5934\u9640\u3002 \u3000\u3000\u573a\u4e2d\u7acb\u523b\u53c8\u662f\u4e00\u9635\u9a9a\u52a8\uff0c\u6ca1\u6709\u5f97\u5230\u7684\u8138\u4e0a\u968f\u5373\u9732\u51fa\u5931\u671b\u4e4b\u8272\uff0c\u4f46\u5510\u7fbd\u53ca\u751f\u6b7b\u5224\u5374\u795e\u8272\u4e0d \u52a8\uff0c\u50cf\u662f\u5c06\u5f97\u5931\u5e76\u672a\u653e\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u8fd9\u7a81\u6765\u7684\u60ca\u559c\uff0c\u4f7f\u5f97\u65e5\u6708\u5934\u9640\u5446\u4e86\u8bb8\u4e45\uff0c\u624d\u5927\u58f0\u72c2\u7b11\u9053\uff1a\u201c\u6211\u548c\u5c1a\u771f\u662f\u4f5b\u7956\u4fdd\u4f51\uff0c\u504f\u504f \u5f97\u4e86\u5b9d\u7269\uff0c\u597d\uff0c\u597d\uff0c\u5c0f\u5f1f\u5f1f\uff0c\u6211\u5c31\u966a\u4f60\u8d70\u4e0a\u51e0\u62db\uff0c\u8ba9\u4f60\u6ca1\u5f97\u8bdd\u8bf4\u3002\u201d\u8bf4\u5b8c\u7b11\u58f0\u4e0d\u7edd\uff0c\u5f97\u610f\u5df2 \u6781\u3002 \u3000\u3000\u718a\u501c\u4ecd\u7136\u4ec1\u7acb\u51dd\u795e\uff0c\u5168\u795e\u6212\u5907\uff0c\u65e5\u6708\u5934\u9640\u5c06\u5bbd\u5927\u7684\u8888\u88df\u624e\u4e86\u624e\u7d27\uff0c\u5411\u4ed6\u8d70\u4e86\u8fc7\u6765\uff0c\u8bf4 \u9053\uff1a\u201c\u6d12\u5bb6\u5c31\u7a7a\u624b\u966a\u4f60\u73a9\u73a9\u3002\u201d \u3000\u3000\u4ed6\u8bdd\u5c1a\u672a\u8bf4\u5b8c\uff0c\u718a\u501c\u7a81\u5730\u65e0\u62db\u65e0\u5f0f\uff0c\u659c\u5288\u4e00\u638c\uff0c\u51fa\u638c\u7684\u4f4d\u7f6e\u6781\u4e3a\u5201\u635f\uff0c\u8fd9\u6b63\u662f\u4ece\u4faf\u751f\u6240 \u6559\u4ed6\u7684\u51e0\u4e2a\u5251\u5f0f\u53d8\u5316\u800c\u51fa\u7684\u3002 \u3000\u3000\u65e5\u6708\u5934\u9640\u672a\u66fe\u770b\u51fa\u5965\u5999\u968f\u4fbf\u4e00\u8eb2\uff0c\u4e3e\u624b\u4e00\u683c\uff0c\u4ed6\u5fc3\u4e2d\u8fd8\u5728\u60f3\uff1a\u201c\u8fd9\u5a03\u5a03\u628a\u4e8b\u60c5\u5168\u6405\u5728\u81ea \u5df1\u8eab\u4e0a\uff0c\u6211\u8fd8\u5f53\u4ed6\u771f\u6709\u4e24\u4e0b\u5b50\uff0c\u54ea\u77e5\u5374\u662f\u8fd9\u6837\u7684\u8822\u8d27\u2026\u2026\u201d\u4ed6\u5ff5\u5934\u5c1a\u672a\u8f6c\u5b8c\uff0c\u53ea\u89c9\u718a\u501c\u7684\u53f3 \u638c\u5ffd\u5730\u4e00\u987f\uff0c\u6781\u5de7\u5999\u5730\u4e1b\u4ed6\u65f6\u91cc\u7a7f\u4e86\u8fc7\u6765\uff0c\u5316\u638c\u4e3a\u62f3\uff0c\u7830\u5730\u51fb\u5728\u4ed6\u53f3\u80c1\u4e4b\u4e0b\uff0c\u4ed6\u8fde\u8eb2\u95ea\u7684\u5ff5 \u5934\u90fd\u672a\u53ca\u751f\u51fa\uff0c\u5df2\u7740\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u718a\u501c\u7b11\u9053\uff1a\u201c\u627f\u8ba9\u4e86\u3002\u201d \u3000\u3000\u6309\u8bf4\u6b66\u6797\u4e2d\u4eba\u8f83\u6280\uff0c\u534a\u62db\u4e4b\u5dee\uff0c\u4fbf\u5f97\u8ba4\u683d\uff0c\u4f55\u51b5\u4ed6\u8fd8\u7740\u7740\u5b9e\u5b9e\u6328\u4e86\u4e00\u62f3\uff0c\u4f46\u65e5\u6708\u5934\u9640\u4e3a \u4e86\u8fd9\u6210\u5f62\u9996\u4e4c\uff0c\u5374\u4e5f\u987e\u4e0d\u5f97\u989c\u9762\u4e86\uff0c\u5927\u559d\u9053\uff1a\u201c\u5c0f\u5b50\u6697\u4e2d\u53d6\u5de7\uff0c\u7b97\u4ec0\u4e48\u597d\u6c49\u3002\u201d\u62f3\u98ce\u864e\u864e\uff0c \u53c8\u653b\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u65e5\u6708\u5934\u9640\u672c\u662f\u5c11\u6797\u5bfa\u7684\u5f03\u5f92\uff0c\u6b64\u523b\u4ed6\u201c\u4f0f\u864e\u7f57\u6c49\u62f3\u201d\u4e00\u7ecf\u65bd\u51fa\uff0c\u5012\u4e5f\u62f3\u98ce\u5f3a\u52b2\uff0c\u9887\u89c1\u529f \u529b\uff0c\u4f46\u718a\u501c\u5374\u4e0d\u8fd8\u62db\uff0c\u53ea\u51ed\u7740\u5de7\u5999\u7684\u8eab\u5f62\uff0c\u56f4\u7740\u4ed6\u4e71\u8f6c\uff0c\u65e5\u6708\u5934\u9640\u7a7a\u81ea\u7740\u529b\uff0c\u5374\u8fde\u8863\u670d\u90fd\u78b0 \u4e0d\u5230\u4e00\u4e0b\u3002 \u3000\u3000\u573a\u4e2d\u8bf8\u4eba\u4ff1\u90fd\u662f\u6b66\u5b66\u9ad8\u660e\u4e4b\u8f88\uff0c\u6b64\u79cd\u60c5\u51b5\uff0c\u4e00\u76ee\u4fbf\u53ef\u4e86\u7136\uff0c\u77e5\u9053\u65e5\u6708\u5934\u9640\u51b3\u975e\u654c\u624b\uff0c\u84dd \u5927\u5148\u751f\u770b\u7740\u4e0d\u4f4f\u70b9\u5934\uff0c\u5510\u7fbd\u53ca\u751f\u6b7b\u5224\u66f4\u662f\u5168\u5fc3\u51dd\u4f4f\uff0c\u6781\u5c0f\u5fc3\u5730\u89c2\u770b\u718a\u501c\u7684\u8eab\u6cd5\u3002 \u3000\u3000\u534a\u664c\u8fc7\u540e\uff0c\u65e5\u6708\u5934\u9640\u5df2\u73b0\u75b2\u5026\uff0c\u987b\u77e5\u8fd9\u6837\u6253\u6cd5\uff0c\u6700\u8017\u7cbe\u795e\u3002 \u3000\u3000\u718a\u501c\u7a81\u7136\u957f\u5578\u4e00\u58f0\uff0c\u8eab\u5f62\u817e\u7a7a\u800c\u8d77\uff0c\u53cc\u81c2\u5982\u94c1\uff0c\u786c\u751f\u751f\u4ece\u65e5\u6708\u5934\u9640\u7684\u62f3\u5f71\u4e2d\u7a7f\u5c06\u8fc7\u53bb\uff0c \u7528\u4e86\u4e03\u6210\u529b\uff0c\u4e00\u638c\u6253\u5728\u65e5\u6708\u5934\u9640\u7684\u5934\u80a9\u4e0a\u3002 \u3000\u3000\u5e78\u597d\u65e5\u6708\u5934\u9640\u4e00\u8eab\u6a2a\u7ec3\uff0c\u4f46\u4e5f\u652f\u6301\u4e0d\u4f4f\uff0c\u5168\u8eab\u4e00\u8f6f\uff0c\u5012\u5728\u5730\u4e0a\u3002 \u3000\u3000\u718a\u501c\u811a\u5c16\u5fae\u4e00\u70b9\u5730\uff0c\u7a81\u53c8\u7a9c\u51fa\uff0c\u5c06\u65e5\u6708\u5934\u9640\u653e\u5728\u9a6c\u978d\u4e0a\u7684\u90a3\u53e3\u7cfb\u7740\u9ed1\u8272\u4e1d\u5e26\u7684\u7bb1\u5b50\u62ff\u5230 \u624b\u4e2d\uff0c\u53cc\u624b\u5fae\u4e00\u7528\u529b\uff0c\u4eba\u53c8\u501f\u529b\u7a9c\u4e86\u53e3\u6765\u3002 \u3000\u3000\u84dd\u5927\u5148\u751f\u987f\u65f6\u559d\u597d\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8001\u53eb\u5316\u5b50\u4eca\u5929\u867d\u7136\u6ca1\u798f\u5f97\u5230\u8fd9\u4ef6\u81f3\u5b9d\uff0c\u4f46\u603b\u7b97\u8ddf\u798f\u4e0d \u5dee\uff0c\u773c\u770b\u6b66\u6797\u4e2d\u51fa\u4e86\u8fd9\u7b49\u540e\u8d77\u4e4b\u79c0\uff0c\u771f\u662f\u6c5f\u5c71\u4ee3\u6709\u4eba\u624d\u51fa\uff0c\u4e00\u4ee3\u65b0\u4eba\u6362\u65e7\u4eba\u4e86\u3002\u201d\u8bf4\u5b8c\u53c8\u5927 \u7b11\u4e86\u6570\u58f0\uff0c\u5411\u5750\u5728\u90a3\u91cc\u7684\u95e8\u4e0b\u5f1f\u5b50\u9053\uff1a\u201c\u5c0f\u8981\u996d\uff0c\u620f\u5df2\u770b\u5b8c\u4e86\uff0c\u8fd8\u5750\u5728\u90a3\u91cc\u5e72\u5417\uff0c\u8fd8\u4e0d\u7ad9\u8d77 \u6765\u8d70\u8def\uff1f\u201d \u3000\u3000\u718a\u501c\u9053\uff1a\u201c\u627f\u8ba9\uff0c\u6b64\u4e8b\u8fc7\u540e\uff0c\u5c0f\u5f1f\u5fc5\u5230\u5404\u4f4d\u524d\u8f88\u5e9c\u4e0a\uff0c\u66ff\u5404\u4f4d\u8bf7\u5b89\uff0c\u4eca\u5929\u5c31\u8bf7\u5404\u4f4d\u653e\u5c0f \u5f1f\u4eec\u8fc7\u53bb\u5427\u3002\u201d \u3000\u3000\u5510\u7fbd\u9053\uff1a\u201c\u6162\u6765\uff0c\u6162\u6765\uff0c\u8fd9\u4f4d\u5144\u53f0\u521a\u624d\u6240\u8bb2\u7684\uff0c\u81ea\u662f\u6781\u6709\u9053\u7406\uff0c\u4f46\u5374\u672a\u8bf4\u660e\u4e0d\u51c6\u522b\u4eba\u518d \u4ece\u4f60\u624b\u4e0a\u62a2\u53e3\u5440\uff0c\u4f55\u51b5\u9601\u4e0b\u6240\u51fb\u8d25\u7684\u53ea\u662f\u65e5\u6708\u5934\u9640\u4e00\u4eba\u800c\u5df2\uff0c\u4e0e\u6211\u4eec\u65e0\u6d89\uff0c\u82e5\u9601\u4e0b\u80fd\u5c06\u6211\u7b49 \u5168\u90e8\u51fb\u8d25\uff0c\u6211\u7b49\u81ea\u662f\u65e0\u8bdd\u8bf4\uff0c\u5404\u4f4d\u770b\u6211\u8bf4\u7684\u53ef\u6709\u9053\u7406\u3002\u201d \u3000\u3000\u718a\u501c\u4e00\u542c\u6b64\u8a00\uff0c\u7adf\u7136\u6115\u5728\u90a3\u91cc\u3002 \u3000\u3000\u84dd\u5927\u5148\u751f\u7709\u5934\u4e00\u76b1\uff0c\u6b63\u51c6\u5907\u51fa\u6765\u8bf4\u51e0\u53e5\u516c\u9053\u8bdd\uff0c\u54ea\u77e5\u6811\u9876\u4e0a\u5374\u4f20\u6765\u94f6\u94c3\u822c\u4e00\u9635\u7b11\u58f0\uff0c\u63a5 \u7740\u4e00\u4e2a\u6e05\u8106\u7684\u5973\u5b69\u5b50\u53e3\u97f3\u9053\uff1a\u201c\u767d\u54e5\uff0c\u4f60\u8bf4\u8fd9\u4e9b\u4eba\u53ef\u7b11\u4e0d\u53ef\u7b11\uff0c\u8fd9\u4e48\u5927\u4e86\uff0c\u8fd8\u90fd\u8fd9\u4e48\u7b28\u3002\u201d \u3000\u3000\u53e6\u5916\u4e00\u4e2a\u7ae5\u97f3\u63a5\u7740\u4e5f\u7b11\u9053\uff1a\u201c\u662f\u7684\uff0c\u4e3a\u4e86\u51e0\u53e3\u7a7a\u7bb1\u5b50\uff0c\u5c45\u7136\u6253\u5f97\u4f60\u6b7b\u6211\u6d3b\u7684\u8fd8\u4e0d\u80af\u653e \u624b\uff0c\u771f\u662f\u597d\u7b11\u5440\u3002\u201d\u8bf4\u5b8c\u4e24\u4eba\u58f0\u97f3\u4e00\u8d77\u7b11\u4e4b\u4e0d\u5df2\u3002 \u3000\u3000\u4f17\u4eba\u542c\u4e86\u4ff1\u90fd\u4e00\u6115\uff0c\u4e03\u6bd2\u4e66\u751f\u7a81\u5730\u4e00\u63a2\u9556\u56ca\uff0c\u62ff\u51fa\u4e24\u9897\u4ed6\u90a3\u56ca\u4e2d\u552f\u4e00\u65e0\u6bd2\u6697\u5668\u201c\u98de\u714c \u77f3\u201d\uff0c\u53cd\u624b\u5411\u53d1\u58f0\u7684\u6811\u4e0a\u6253\u51fa\u3002 \u3000\u3000\u54ea\u77e5\u77f3\u5b50\u6253\u51fa\u540e\uff0c\u5374\u5982\u77f3\u6c89\u5927\u6d77\uff0c\u6beb\u65e0\u53cd\u5e94\uff0c\u90a3\u8f7b\u8106\u58f0\u97f3\u5973\u5b69\u5b50\u53c8\u8bf4\u9053\uff1a\u201c\u54ce\u54df\uff0c\u8fd9\u4e9b \u4eba\u4e0d\u8bc6\u597d\u4eba\u5fc3\uff0c\u6211\u4eec\u8fd8\u5df4\u5df4\u5730\u8dd1\u6765\u544a\u8bc9\u4ed6\u4eec\u90a3\u7bb1\u5b50\u662f\u7a7a\u7684\uff0c\u4ed6\u4eec\u5374\u62ff\u77f3\u5934\u6253\u5165\uff0c\u4f60\u8bf4\u53ef\u6068\u4e0d \u53ef\u6068\u3002\u201d \u3000\u3000\u90a3\u7537\u5b69\u5b50\u53c8\u63a5\u7740\u8bf4\uff1a\u201c\u662f\u5440\uff0c\u4ed6\u4eec\u518d\u4e0d\u5bb9\u5ba2\u6c14\u6c14\u7684\u8bf7\u6211\u4eec\u4e0b\u53bb\uff0c\u6211\u4eec\u7d20\u6027\u5c31\u4e0d\u7ba1\u8d70\u4e86\uff0c \u8ba9\u4ed6\u4eec\u6253\u7834\u5934\u53bb\uff0c\u4e5f\u4e0d\u5173\u6211\u4eec\u7684\u4e8b\u3002\u201d \u3000\u3000\u573a\u4e2d\u5404\u4eba\u4e00\u542c\u6b64\u8bdd\uff0c\u4ff1\u90fd\u795e\u8272\u5927\u53d8\uff0c\u77e5\u9053\u6b64\u4e2d\u5fc5\u5b9a\u5927\u6709\u6587\u7ae0\u3002 \u3000\u3000\u84dd\u5927\u5148\u751f\u9053\uff1a\u201c\u662f\u54ea\u4e00\u8def\u7684\u8c6a\u6770\uff0c\u4f55\u6545\u8eb2\u5728\u6811\u4e0a\u76f8\u620f\uff0c\u6709\u4ec0\u4e48\u8bdd\u8bf7\u4e0b\u6765\u8bf4\u660e\uff0c\u8981\u4e0d\u7136\u6211 \u8001\u8981\u996d\u7684\u53ef\u8981\u4eb2\u81ea\u6811\u4e0a\u53bb\u8bf7\u4e86\u3002\u201d \u3000\u3000\u53ea\u542c\u90a3\u5973\u5b69\u5b50\u53c8\u54af\u54af\u7b11\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u5e08\u7236\u8bf4\u5c31\u6570\u8fd9\u8001\u5316\u5b50\u6700\u96be\u60f9\u4e86\uff0c\u8981\u662f\u5f97\u7f6a\u4e86\u4ed6\uff0c\u88ab \u4ed6\u6253\u4e86\u5e08\u7236\u4e5f\u4e0d\u7ba1\uff0c\u6211\u770b\u6211\u4eec\u8fd8\u662f\u4e0b\u53bb\u5427\u3002\u201d \u3000\u3000\u8bed\u58f0\u521a\u5b8c\uff0c\u4f17\u4eba\u773c\u775b\u82b1\uff0c\u9762\u524d\u5df2\u591a\u4e86\u4e00\u9ed1\u4e00\u767d\u4e24\u4e2a\u5c0f\u5b69\uff0c\u767d\u8863\u7684\u662f\u5973\u5b69\u5b50\uff0c\u9ed1\u8863\u7684\u662f\u7537 \u5b69\u5b50\uff0c\u90fd\u957f\u5f97\u7c89\u96d5\u7389\u7422\uff0c\u53ef\u7231\u6781\u4e86\u3002 \u3000\u3000\u90a3\u5168\u8eab\u9ed1\u8863\u7684\u5c0f\u7537\u5b69\u4e00\u843d\u5730\u540e\uff0c\u62b1\u62f3\u4e3a\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u592a\u884c\u5c71\u5929\u9634\u6559\u4e3b\u575b\u53f8\u793c\u7ae5\u5b50\u767d\u666f \u7965\u3001\u53f6\u6e05\u6e05\uff0c\u5949\u6559\u4e3b\u6cd5\u65e8\uff0c\u7279\u5e26\u4e0a\u4fbf\u51fd\u4e00\u5c01\uff0c\u4e95\u5411\u5404\u524d\u8f88\u4eec\u95ee\u597d\u3002\u201d\u8bf4\u5b8c\u7f57\u5708\u4f5c\u4e86\u4e00\u4e2a\u5927 \u63d6\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u8bf4\u4e0d\u6253\u7d27\uff0c\u5012\u628a\u5728\u573a\u7684\u8fd9\u4e9b\u82f1\u96c4\u8c6a\u6770\uff0c\u5404\u5404\u5413\u5f97\u4e00\u8eab\u51b7\u6c57\u3002 \u3000\u3000\u90a3\u767d\u8863\u7684\u5973\u5b69\u5b50\u4e5f\u662f\u4e00\u8eac\u8eab\uff0c\u8bf4\uff1a\u201c\u6559\u4e3b\u5e76\u4e14\u8bf4\uff0c\u53eb\u6211\u4eec\u5c06\u8fd9\u91cc\u4e00\u4f4d\u53eb\u751f\u6b7b\u5224\u6c64\u5b5d\u5b8f \u7684\uff0c\u7acb\u523b\u5e26\u5f80\u6cf0\u5c71\uff0c\u6559\u4e3b\u6709\u4e8b\u9762\u5546\u3002\u201d \u3000\u3000\u9ed1\u8863\u7ae5\u5b50\u767d\u666f\u7965\uff0c\u968f\u5373\u81ea\u6000\u4e2d\u638f\u51fa\u4e00\u4fe1\uff0c\u84dd\u5927\u5148\u751f\u5fd9\u63a5\u8fc7\u53bb\uff0c\u6495\u5f00\u4fe1\u76ae\uff0c\u770b\u4e86\u4e4b\u540e\uff0c\u795e \u8272\u5927\u53d8\u3002 \u7b2c\u56db\u7ae0\u3000\u98d8\u7136\u8001\u4eba>> \u53e4\u9f99\u300a\u82cd\u7a79\u795e\u5251\u300b \u7b2c\u56db\u7ae0\u3000\u98d8\u7136\u8001\u4eba \u3000\u3000\u5927\u884c\u5c71\uff0c\u5357\u5317\u5a49\u8712\u4e8e\u5c71\u4e1c\u7701\u4e4b\u5317\u90e8\uff0c\u4e3a\u5c71\u4e1c\u4e0e\u6cb3\u5317\u4e4b\u754c\uff0c\u5c71\u52bf\u78c5\u7934\uff0c\u7eb5\u6a2a\u5343\u91cc\u3002 \u3000\u3000\u4e09\u5341\u5e74\u524d\uff0c\u592a\u884c\u5c71\u91cc\u5efa\u7acb\u4e86\u4e00\u4e2a\u5929\u9634\u6559\uff0c\u6559\u4e3b\u82cd\u865a\u4e0a\u4eba\u592b\u5987\uff1b\u6b66\u529f\u9738\u7edd\u6c5f\u6e56\uff0c\u624b\u4e0b\u7f57\u81f4 \u7684\u4e5f\u4ff1\u662f\u9ed1\u767d\u9053\u4e2d\u9876\u5c16\u513f\u7684\u9ad8\u624b\uff0c\u4e3b\u575b\u4e0b\u5206\u7384\u9f99\u3001\u767d\u51e4\u4e24\u5802\uff0c\u5404\u7edf\u4e09\u4e2a\u652f\u575b\uff0c\u652f\u575b\u53c8\u5206\u4e3a\u5341 \u516d\u4e2a\u5206\u5802\uff0c\u4e03\u5341\u4e8c\u4e2a\u8235\u4e3b\uff0c\u904d\u5e03\u4e8e\u5357\u4e03\u3001\u5317\u516d\u5341\u4e09\u7701\u3002 \u3000\u3000\u5f53\u65f6\u4e4b\u5929\u9634\u6559\u771f\u53ef\u8c13\u4e4b\u7eb5\u6a2a\u5929\u4e0b\uff0c\u6b66\u6797\u4fa7\u76ee\uff0c\u6c5f\u6e56\u4e2d\u7684\u4efb\u4f55\u7ea0\u7eb7\uff0c\u53ea\u8981\u6709\u5929\u9634\u6559\u6d89\u53ca\uff0c \u83ab\u4e0d\u8fce\u5203\u800c\u89e3\uff0c\u5929\u9634\u6559\u7684\u5f92\u515a\uff0c\u66f4\u662f\u7ed3\u4f17\u6a2a\u884c\uff0c\u505a\u51fa\u8bb8\u591a\u4e0d\u6cd5\u4e4b\u4e8b\uff0c\u4f46\u5bab\u5e9c\u4e5f\u83ab\u5948\u4ed6\u4f55\u3002 \u3000\u3000\u53ef\u662f\u591a\u884c\u4e0d\u4e49\u5fc5\u81ea\u6bd9\uff0c\u5f53\u65f6\u4fa0\u9053\u4e2d\u7684\u9886\u8896\uff0c\u94c1\u5251\u5148\u751f\u5c55\u7ffc\uff0c\u8fde\u7ed9\u5341\u4e09\u7701\u6b66\u6797\u597d\u624b\uff0c\u7531\u5357 \u81f3\u5317\uff0c\u5c06\u5929\u9634\u6559\u7684\u5206\u8235\u9010\u4e2a\u51fb\u8d25\uff0c\u540e\u6765\u5e76\u5f97\u5230\u4e00\u4f4d\u5f02\u4eba\u6240\u52a9\uff0c\u7adf\u5c06\u5929\u9634\u6559\u4e00\u4e3e\u800c\u706d\uff0c\u4f46\u5341\u4e09 \u7701\u6b66\u6797\u597d\u624b\uff0c\u51e0\u4e4e\u5168\u4f24\u5728\u6b64\u5f79\u4e4b\u4e2d\u3002 \u3000\u3000\u53ef\u662f\u5929\u9634\u6559\u7684\u4f59\u5a01\u4ecd\u5728\uff0c\u8fd9\u4e48\u591a\u5e74\u6765\uff0c\u6b66\u6797\u4e2d\u4eba\u63d0\u8d77\u5929\u9634\u6559\uff0c\u4ecd\u7136\u662f\u8c08\u864e\u8272\u53d8\u3002 \u3000\u3000\u662f\u4ee5\u65b9\u624d\u90a3\u9ed1\u767d\u4e24\u4e2a\u7ae5\u5b50\uff0c\u8bf4\u662f\u5929\u9634\u6559\u4e0b\u7684\u4eba\u7269\uff0c\u60f3\u5fc5\u662f\u5929\u9634\u6559\u53c8\u91cd\u632f\u6c5f\u6e56\uff0c\u5728\u573a\u8bf8 \u4eba\uff0c\u9664\u4e86\u718a\u501c\u4e4b\u5916\uff0c\u8c01\u4e0d\u77e5\u9053\u5929\u9634\u6559\u7684\u5a01\u98ce\uff1f \u3000\u3000\u5176\u4e2d\u5c24\u5176\u662f\u751f\u6b7b\u5224\u6c64\u5b5d\u5b8f\uff0c\u5f53\u5e74\u4ed6\u4ea6\u662f\u5929\u9634\u6559\u4e0b\u7684\u5206\u8235\u8235\u4e3b\uff0c\u4f46\u540e\u6765\u89c1\u5927\u52bf\u5df2\u53bb\uff0c\u4f7f\u6084 \u7136\u8fdc\u5f15\uff0c\u6b64\u523b\u542c\u53f6\u6e05\u6e05\u8bf4\uff0c\u5929\u9634\u6559\u4e3b\u8981\u627e\u4ed6\u9762\u8c08\uff0c\u4ed6\u6df1\u77e5\u5929\u9634\u6559\u6559\u89c4\u4e4b\u4e25\uff0c\u624b\u6bb5\u4e4b\u9177\uff0c\u66f4\u662f \u5413\u5f97\u9762\u5982\u571f\u8272\u3002 \u3000\u3000\u90a3\u84dd\u5927\u5148\u751f\u770b\u5b8c\u5b57\u6761\u540e\uff0c\u53c8\u5c06\u5b57\u6761\u4ea4\u7ed9\u5510\u7fbd\uff0c\u5510\u7fbd\u63a5\u8fc7\u5b57\u6761\uff0c\u9ad8\u58f0\u5ff5\u9053\uff1a\u201c\u6b66\u6797\u8bf8\u524d\u8f88 \u5927\u9274\uff1a\u8bf8\u4f4d\u4e1a\u5df2\u53d7\u611a\uff1b\u7c89\u9762\u82cf\u79e6\u91d1\u8749\u8131\u58f3\uff0c\u53ea\u8eab\u5e26\u7740\u6210\u5f62\u9996\u4e4c\u7531\u6c34\u8def\u4e0a\u4eac\uff0c\u6b64\u4e8b\u672c\u5c5e\u6781\u7aef\u79d8 \u5bc6\uff0c\u4f46\u611a\u592b\u5987\u5374\u5f97\u5df2\u77e5\u6089\uff0c\u73b0\u5df2\u5c06\u6b64\u4eba\u62ff\u4e0b\uff0c\u4e3a\u514d\u8bf8\u4f4d\u53d7\u5176\u611a\u5f04\uff0c\u7279\u6b64\u5949\u8fbe\u3002\u4e0b\u6708\u6708\u5706\u4e4b \u65f6\uff0c\u611a\u592b\u5987\u5019\u5404\u4f4d\u5927\u9a7e\u4e8e\u6cf0\u5c71\u7389\u7687\u9876\uff0c\u5230\u65f6\u6709\u8981\u4e8b\u76f8\u5546\uff0c\u671b\u5404\u4f4d\u51c6\u65f6\u5230\u8fbe\u52ff\u8bef\uff0c\u6b64\u95ee\u597d\uff0c\u7126 \u5f02\u884c\u3001\u6218\u74a7\u541b\u540c\u4e0a\u3002\u53c8\u53ca\uff0c\u751f\u6b7b\u5224\u6c64\u5b5d\u5b8f\u4e43\u6211\u6559\u4e2d\u53db\u5f92\uff0c\u4eca\u7279\u6d3e\u6559\u4e0b\u53f8\u793c\u7ae5\u5b50\u8bf7\u4e4b\u56de\u6559\uff0c\u5c4a \u65f6\u4e07\u671b\u5404\u4f4d\u8896\u624b\u800c\u89c2\uff0c\u76d6\u5929\u9634\u6559\u4e2d\u79c1\u4e8b\uff0c\u5c1a\u4e0d\u5bb9\u4eba\u8fc7\u95ee\u4e5f\u3002\u201d \u3000\u3000\u4e03\u6bd2\u4e66\u751f\u5510\u7fbd\u5ff5\u5b8c\u4fe1\u540e\uff0c\u573a\u4e2d\u5404\u4eba\u5fc3\u4ff1\u662f\u6026\u7136\u6253\u9f13\uff0c\u4e0d\u77e5\u5929\u9634\u6559\u4e3b\u5728\u6cf0\u5c71\u7edd\u9876\u76f8\u53ec\uff0c\u7a76 \u6709\u4f55\u4e8b\uff0c\u718a\u501c\u5fc3\u91cc\u66f4\u662f\u96be\u53d7\uff0c\u4ed6\u5fe0\u5fc3\u4e3a\u53cb\uff0c\u5374\u4e0d\u77e5\u53cd\u88ab\u738b\u667a\u9011\u6240\u73a9\u5f04\uff0c\u5434\u8bcf\u4e91\u4ea6\u662f\u5728\u5fc3\u4e2d\u76d8 \u7b97\uff0c\u600e\u6837\u6765\u5e94\u4ed8\u8fd9\u4ef6\u4e8b\u3002 \u3000\u3000\u718a\u501c\u53c8\u6c14\u53c8\u6094\uff0c\u5c06\u90a3\u7bb1\u5b50\u4e0a\u7684\u9501\u7528\u5200\u626d\u5f00\uff0c\u91cc\u9762\u679c\u7136\u7a7a\u7a7a\u5982\u4e5f\uff0c\u4e8e\u662f\u4ed6\u5411\u8bf8\u8c6a\u8bf4\uff1a\u201c\u6b64 \u6b21\u7c89\u9762\u82cf\u79e6\u6240\u65bd\u4e4b\u8ba1\u5c0f\u5f1f\u5b9e\u662f\u4e0d\u77e5\uff0c\u6240\u4ee5\u624d\u81f3\u5f04\u6210\u6b64\u5c40\u9762\uff0c\u8fd8\u671b\u5404\u4f4d\u591a\u591a\u89c1\u8c05\u3002\u201d \u3000\u3000\u6b64\u65f6\u90a3\u53f6\u6e05\u6e05\u7a81\u5730\u4e00\u58f0\u5a07\u559d\uff0c\u8bf4\u9053\uff1a\u201c\u60f3\u8d70\u7684\u53ef\u5c31\u662f\u751f\u6b7b\u5224\u6c64\u5b5d\u5b8f\uff0c\u6211\u4eec\u6559\u4e3b\u7279\u6765\u76f8 \u8bf7\uff0c\u96be\u9053\u4f60\u60f3\u656c\u9152\u4e0d\u5403\u5403\u7f5a\u9152\uff1f\u201d \u3000\u3000\u539f\u6765\u751f\u6b7b\u5224\u77e5\u9053\u5929\u9634\u6559\u4e3b\u76f8\u53ec\uff0c\u5b9a\u7136\u51f6\u591a\u5409\u5c11\uff0c\u7adf\u60f3\u4e58\u7740\u5927\u5bb6\u90fd\u4e0d\u6ce8\u610f\u7684\u65f6\u5019\uff0c\u6084\u6084\u4e00 \u6e9c\uff0c\u6b64\u523b\u4ed6\u542c\u5230\u53f6\u6e05\u6e05\u7684\u5a07\u559d\uff0c\u5fc3\u60f3\u6b64\u65f6\u4e0d\u8d70\uff0c\u66f4\u5f85\u4f55\u65f6\uff0c\u8c05\u4ed6\u4eec\u4e24\u4e2a\u5c0f\u5b69\uff0c\u4e5f\u4e0d\u80fd\u6349\u5230\u81ea \u5df1\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u731b\u4e00\u8eac\u8170\uff0c\u7adf\u81ea\u65bd\u51fa\u201c\u873b\u8713\u4e09\u6284\u6c34\u201d\u7684\u7edd\u9876\u8f7b\u529f\uff0c\u5f80\u5916\u9003\u8d70\u3002 \u3000\u3000\u9ed1\u8863\u7ae5\u5b50\u767d\u666f\u7965\u51b7\u7b11\u4e86\u4e00\u58f0\uff0c\u62f1\u62f3\u8bf4\u9053\uff1a\u201c\u90a3\u655d\u6559\u4e2d\u53db\u5f92\u5984\u60f3\u9003\u8dd1\uff0c\u5b9e\u662f\u81ea\u8ba8\u82e6\u5403\uff0c\u665a \u8f88\u4eec\u6709\u516c\u52a1\u5728\u8eab\uff0c\u6b64\u523b\u5148\u544a\u8f9e\u4e86\u3002\u201d \u3000\u3000\u8bf4\u7740\u4e0e\u53f6\u6e05\u6e05\u540c\u65f6\u4e00\u8eac\uff0c\u4e5f\u4e0d\u77e5\u7528\u7684\u4ec0\u4e48\u8eab\u6cd5\uff0c\u4e24\u6761\u8eab\u8eaf\u5982\u7bad\u4e00\u822c\u76f4\u7a9c\u800c\u51fa\uff0c\u4e00\u6643\u773c\u5931 \u4e86\u8e2a\u8ff9\uff0c\u771f\u662f\u4e2a\u8f7b\u5feb\u7edd\u4f26\u3002 \u3000\u3000\u84dd\u5927\u5148\u751f\u9053\uff1a\u201c\u6b64\u95f4\u7684\u4e8b\uff0c\u5df2\u7ecf\u544a\u4e00\u6bb5\u843d\uff0c\u6211\u4eec\u5148\u544a\u8f9e\u4e86\uff0c\u4e0b\u6708\u6708\u5706\u7389\u7687\u9876\u518d\u89c1\u3002\u201d\u8bf4 \u5b8c\u5e26\u7740\u95e8\u4e0b\u5f1f\u5b50\uff0c\u5f84\u81ea\u7a7f\u6797\u800c\u53bb\u3002 \u3000\u3000\u7fa4\u8c6a\u7eb7\u7eb7\u62f1\u624b\u6563\u53bb\uff0c\u53d7\u4f24\u7684\u65e5\u6708\u5934\u9640\uff0c\u4e5f\u88ab\u6258\u5854\u5929\u738b\u624b\u4e0b\u7684\u597d\u6c49\uff0c\u62ac\u8d77\u6551\u53bb\u3002 \u3000\u3000\u4e03\u53ea\u7cbe\u5de5\u6253\u505a\u7684\u7ea2\u6728\u7bb1\u5b50\uff0c\u96f6\u4e71\u5730\u6563\u5728\u5730\u4e0a\uff0c\u9556\u4f19\u4eec\u60ca\u9b42\u521d\u5b9a\uff0c\u718a\u501c\u7684\u5fc3\u91cc\u96be\u53d7\u5df2\u6781\uff0c \u4ed6\u6240\u4ed8\u51fa\u7684\u4e00\u4efd\u53cb\u60c5\uff0c\u7adf\u6d6a\u8d39\u5728\u4e00\u4e2a\u5b58\u5fc3\u5229\u7528\u4ed6\u7684\u4eba\u8eab\u4e0a\uff0c\u8fd9\u662f\u4ed6\u6700\u611f\u60b2\u54c0\u7684\u3002 \u3000\u3000\u5434\u8bcf\u4e91\u5fc3\u91cc\u66f4\u662f\u96be\u53d7\uff0c\u5728\u96be\u53d7\u5916\u8fd8\u52a0\u4e86\u4e00\u4efd\u60ed\u6127\uff0c\u4ed6\u548c\u7c89\u9762\u82cf\u79e6\u7ed3\u8bc6\u591a\u5e74\uff0c\u8fd9\u6b21\u7ade\u88ab\u51fa \u5356\uff0c\u60ed\u6127\u7684\u662f\u4ed6\u548c\u738b\u667a\u9011\u5230\u5e95\u662f\u7ed3\u4e49\u5144\u5f1f\uff0c\u738b\u667a\u9011\u6b3a\u9a97\u4e86\u718a\u501c\uff0c\u4ed6\u5fc3\u4e2d\u81ea\u4e5f\u96be\u53d7\uff0c\u518d\u52a0\u4e0a\u738b \u667a\u9011\u73b0\u5df2\u8eab\u843d\u5929\u9634\u6559\u4e4b\u624b\uff0c\u8c05\u5fc5\u6ca1\u6709\u4ec0\u4e48\u751f\u8fd8\u7684\u5e0c\u671b\uff0c\u9e23\u8fdc\u9556\u5c40\u7ecf\u8fc7\u8fd9\u4e00\u6b21\u6253\u51fb\uff0c\u4e5f\u65e0\u6cd5\u518d \u62ac\u8d77\u5934\u6765\uff0c\u524d\u9014\u5b9e\u662f\u4e0d\u582a\u8bbe\u60f3\u3002 \u3000\u3000\u4ed6\u60f3\u8d77\u4ed6\u521d\u4eba\u5e08\u95e8\uff0c\u62b1\u8d1f\u751a\u5927\uff0c\u6ee1\u60f3\u51ed\u7740\u4e00\u8eab\u6b66\u827a\uff0c\u521b\u51fa\u4e00\u756a\u4e8b\u4e1a\u6765\uff0c\u4f46\u73b0\u5728\u843d\u5f97\u5982 \u6b64\uff0c\u518d\u8005\u6280\u53c8\u4e0d\u5982\u5165\uff0c\u5c31\u90a3\u4e24\u4e2a\u5e7c\u7ae5\uff0c\u81ea\u5df1\u90fd\u4e0d\u80fd\u76f8\u6bd4\uff0c\u8fd8\u8bf4\u4ec0\u4e48\u95ef\u8361\u6c5f\u6e56\uff0c\u521b\u4e1a\u626c\u540d\u5462\u3002 \u3000\u3000\u4ed6\u6108\u60f3\u6108\u662f\u5fc3\u7070\uff0c\u5bf9\u718a\u501c\u8bf4\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4e8b\u60c5\u53d1\u5c55\u5230\u8fd9\u4e2a\u5730\u6b65\uff0c\u6211\u518d\u4e5f\u6ca1\u6709\u60f3\u5230\u738b\u667a\u9011 \u5c45\u7136\u5982\u6b64\uff0c\u53cd\u6b63\u65e5\u4e45\u89c1\u4eba\u5fc3\uff0c\u5f7c\u6b64\u7ec8\u6709\u4e92\u76f8\u4e86\u89e3\u7684\u4e00\u5929\uff0c\u73b0\u6211\u4e5f\u65e0\u989c\u518d\u53bb\u6cf0\u5c71\u4e0e\u5929\u4e0b\u82f1\u96c4\u76f8 \u4f1a\uff0c\u8d24\u5f1f\u5e74\u5c11\u82f1\u53d1\uff0c\u65e5\u540e\u5fc5\u6210\u5927\u5668\uff0c\u6211\u5e26\u7740\u9556\u961f\u56de\u8f6c\u6c5f\u5b81\u540e\uff0c\u51b3\u5b9a\u8fdc\u5f15\u6c5f\u6e56\uff0c\u518d\u7ec3\u6b66\u529f\uff0c\u4f60 \u6211\u540e\u4f1a\u6709\u671f\uff0c\u4f46\u671b\u8d24\u5f1f\u80fd\u5728\u6cf0\u5c71\u4e0a\uff0c\u51fa\u5165\u5934\u5730\uff0c\u626c\u540d\u5929\u4e0b\uff0c\u611a\u5144\u5f97\u77e5\uff0c\u4e5f\u5fc5\u66ff\u4f60\u6b22\u559c\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7740\u8bf4\u7740\uff0c\u5fc3\u9178\u4e0d\u5df2\uff0c\u718a\u501c\u4e5f\u975e\u5e38\u96be\u53d7\uff0c\u4f46\u4e5f\u8bf4\u4e0d\u51fa\u4ec0\u4e48\u529d\u89e3\u7684\u8bdd\u6765\uff0c\u4e24\u4eba\u9eef\u7136\u76f8 \u5bf9\uff0c\u5f7c\u6b64\u5fc3\u610f\u76f8\u901a\uff0c\u65e5\u540e\u7adf\u6210\u4e86\u597d\u53cb\u3002 \u3000\u3000\u5434\u8bcf\u4e91\u66ff\u718a\u501c\u7559\u4e0b\u4e00\u5339\u9a6c\u53ca\u8bb8\u591a\u94f6\u4e24\uff0c\u53c8\u518d\u4e09\u53ee\u549b\u4e86\u8bb8\u591a\u6c5f\u6e56\u4e0a\u7684\u5fcc\u7981\u548c\u4e60\u4fd7\uff0c\u624d\u4e92\u9053 \u73cd\u91cd\uff0c\u5e26\u7740\u9556\u8f66\u8fd4\u53e3\u6c5f\u5b81\u3002 \u3000\u3000\u718a\u501c\u72ec\u81ea\u9a91\u5728\u9a6c\u4e0a\uff0c\u832b\u7136\u5411\u524d\u884c\u8d70\uff0c\u8fd9\u8bb8\u591a\u5929\u6765\u4ed6\u867d\u5df2\u5b66\u4f1a\u4e86\u5f88\u591a\uff0c\u77e5\u9053\u4e86\u6c5f\u6e56\u7684\u9669 \u6076\uff0c\u4eba\u5fc3\u7684\u96be\u6d4b\uff0c\u4ed6\u4e5f\u77e5\u9053\uff0c\u53cb\u60c5\uff0c\u5728\u60a3\u96be\u4e2d\u5f97\u6765\u7684\u624d\u6700\u53ef\u8d35\uff0c\u53ef\u662f\u524d\u9014\u832b\u832b\uff0c\u4ed6\u8981\u72ec\u81ea\u53bb \u95ef\u4e86\u3002 \u3000\u3000\u4ed6\u6cbf\u9014\u95ee\u8def\uff0c\u77e5\u9053\u524d\u9762\u5c31\u662f\u66f2\u961c\uff0c\u66f2\u961c\u4e43\u6625\u79cb\u65e7\u90fd\uff0c\u5b54\u5b50\u8bde\u751f\u4e4b\u5730\uff0c\u718a\u501c\u719f\u8bfb\u8bd7\u4e66\uff0c\u81ea \u7136\u77e5\u9053\uff0c\u4ed6\u6b64\u65f6\u8ddd\u79bb\u6cf0\u5c71\u4e4b\u4f1a\u5c1a\u65e9\uff0c\u4f55\u4e0d\u5728\u66f2\u961c\u591a\u803d\u51e0\u5929\u77bb\u4ef0\u5b54\u592b\u5b50\u7684\u5723\u8ff9\u3002 \u3000\u3000\u5b54\u6797\u5728\u66f2\u961c\u57ce\u5916\uff0c\u4e3a\u6709\u540d\u7684\u80dc\u5730\uff0c\u5230\u66f2\u961c\u6765\u7684\uff0c\u5dee\u4e0d\u591a\u5168\u8981\u5230\u5b54\u6797\u53bb\u77bb\u4ef0\u4e00\u756a\uff0c\u6797\u5916\u7ed5 \u4ee5\u7ea2\u57a3\uff0c\u677e\u67cf\u53c2\u5929\uff0c\u7891\u8910\u751a\u591a\uff0c\u718a\u501c\u5230\u4e86\u6b64\u5904\uff0c\u53ea\u89c9\u5f97\u4eba\u4e16\u95f4\u7684\u8363\u8fb1\uff0c\u90fd\u4e0d\u518d\u662f\u4ed6\u6240\u8ba1\u8f83\u7684 \u4e86\u3002 \u3000\u3000\u4ed6\u968f\u5904\u89c2\u671b\uff0c\u5ffd\u89c1\u4e00\u4e2a\u9752\u886b\u8001\u8005\uff0c\u62c4\u6756\u800c\u6765\uff0c\u968f\u53e3\u6b4c\u9053\uff1a\u201c\u534e\u9b13\u661f\u661f\uff0c\u60ca\u58ee\u5fd7\u6210\u865a\uff0c\u6b64 \u8eab\u5982\u5bc4\u3002\u8427\u6761\u75c5\u9aa5\uff0c\u5411\u6697\u91cc\u6d88\u5c3d\u5f53\u5e74\u8c6a\u6c14\uff0c\u68a6\u65ad\u6545\u56fd\u5c71\u5ddd\uff0c\u9694\u91cd\u91cd\u70df\u6c34\u8eab\u4e07\u91cc\u3002\u65e7\u793e\u51cb\u96f6\uff0c \u9752\u95e8\u4fca\u6e38\u8c01\u8bb0\u3002\u5c3d\u9053\u9526\u91cc\u7e41\u534e\uff0c\u6b22\u5b98\u95f2\u663c\u6c38\uff0c\u67f4\u8346\u6dfb\u7761\uff0c\u6e05\u6101\u81ea\u9189\uff0c\u5ff5\u6b64\u9645\u4ed8\u4e0e\u4f55\u4eba\u5fc3\u4e8b\u3002 \u7eb5\u6709\u695a\u67cf\u5434\u6905\uff0c\u77e5\u4f55\u65f6\u4e1c\u901d\uff0c\u7a7a\u6005\u671b\uff0c\u810d\u7f8e\u82d1\u9999\uff0c\u79cb\u98ce\u53c8\u8d77\u3002\u201d \u3000\u3000\u6b64\u8bcd\u672c\u662f\u5357\u5b8b\u7231\u56fd\u8bcd\u4eba\u9646\u6e38\u6240\u4f5c\uff0c\u6b64\u523b\u8fd9\u91cc\u8001\u8005\u6b4c\u6765\uff0c\u4f46\u89c9\u82cd\u51c9\u60b2\u653e\uff0c\u8c6a\u6c14\u5e72\u4e91\u3002 \u3000\u3000\u718a\u501c\u89c1\u4ed6\u8001\u8005\u767d\u53d1\u5982\u971c\uff0c\u9762\u8272\u5374\u5f02\u5e38\u7ea2\u6da6\uff0c\u884c\u8d70\u5728\u53e4\u67cf\u82cd\u677e\u4e4b\u4e2d\uff0c\u8863\u8882\u98d8\u7136\uff0c\u76f4\u4f3c\u56fe\u753b \u4e2d\u4eba\uff0c\u4e0d\u89c9\u770b\u5f97\u75f4\u4e86\u3002 \u3000\u3000\u90a3\u8001\u8005\u6f2b\u6b65\u5230\u718a\u501c\u8ddf\u524d\uff0c\u671d\u718a\u501c\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u8001\u5f1f\u9a7b\u8db3\u8fd9\u91cc\uff0c\u60f3\u5fc5\u4e5f\u662f\u88ab\u6b64 \u95f4\u7684\u6d69\u7136\u4e4b\u6c14\u6240\u9189\u3002\u201d\u4ed6\u5fae\u4e00\u53f9\u6c14\uff0c\u53c8\u8bf4\uff1a\u201c\u4eba\u751f\u767e\u5e74\uff0c\u6643\u773c\u5373\u8fc7\uff0c\u8981\u843d\u5f97\u5e99\u795d\u5343\u79cb\uff0c\u771f\u662f \u8c08\u4f55\u5bb9\u6613\u3002\u201d\u718a\u501c\u793c\u4eea\u672c\u5468\uff0c\u5bf9\u8fd9\u8001\u8005\u53c8\u6709\u5947\u602a\u7684\u597d\u611f\uff0c\u95fb\u8a00\u8eac\u8eab\u79f0\u662f\u3002 \u3000\u3000\u90a3\u8001\u8005\u671d\u718a\u501c\u9762\u4e0a\u770b\u4e86\u534a\u664c\uff0c\u70b9\u5934\u9053\uff1a\u201c\u679c\u7136\u5e74\u8f7b\u82f1\u4fca\uff0c\u806a\u660e\u5fe0\u539a\uff0c\u517c\u800c\u6709\u4e4b\uff0c\u662f\u4e2a\u53ef \u9020\u4e4b\u6750\u3002\u201d\u8bf4\u7740\u53c8\u67f1\u6756\u9ad8\u6b4c\u6f2b\u6b65\u800c\u53bb\u3002 \u3000\u3000\u718a\u501c\u7ad9\u5728\u90a3\u91cc\u6123\u4e86\u8bb8\u4e45\uff0c\u60f3\u9053\uff1a\u201c\u4eba\u4eba\u90fd\u8bf4\u6211\u5e74\u8f7b\u6709\u4e3a\uff0c\u6211\u5b9a\u8981\u594b\u53d1\u56fe\u5f3a\uff0c\u4e0d\u53ef\u8f9c\u8d1f\u4e86 \u81ea\u5df1\uff0c\u4f55\u51b5\u6211\u6069\u6028\u4ff1\u5982\u5c71\u91cd\uff0c\u5982\u4e0d\u597d\u81ea\u4e3a\u4e4b\uff0c\u600e\u751f\u4e86\u5374\uff0c\u5c82\u53ef\u4e3a\u4e86\u4e9b\u987b\u4e8b\u6545\uff0c\u4fbf\u610f\u5fd7\u6d88\u6c89\u8d77 \u6765\u3002\u201d \u3000\u3000\u4e8e\u662f\u4ed6\u5f00\u59cb\u9762\u5bf9\u7740\u4e8b\u5b9e\uff0c\u4e0d\u518d\u60e7\u6015\u4e00\u4e9b\u672a\u6765\u7684\u4e8b\uff0c\u4ed6\u76f8\u4fe1\uff0c\u4e16\u4e0a\u4efb\u4f55\u4e00\u4ef6\u4e8b\uff0c\u90fd\u4f1a\u6709\u89e3 \u51b3\u7684\u529e\u6cd5\uff0c\u7a7a\u81ea\u53d1\u6101\uff0c\u53c8\u6709\u4f55\u7528\uff0c\u4ed6\u81ea\u77e5\u6b66\u529f\u3001\u7ecf\u9a8c\u4ff1\u90fd\u8fd8\u5dee\uff0c\u4f46\u4e8b\u5728\u4eba\u4e3a\uff0c\u53ea\u8981\u52aa\u529b\uff0c\u4f55 \u60a3\u65e0\u6210\uff1f \u3000\u3000\u5728\u66f2\u961c\u4ed6\u53c8\u803d\u8bef\u4e86\u51e0\u5929\uff0c\u624d\u52a8\u8eab\u6e21\u6cc5\u6c34\uff0c\u76f4\u5954\u6cf0\u5c71\u3002 \u3000\u3000\u6cf0\u5c71\u4e3a\u4e94\u5cb3\u4e4b\u957f\uff0c\u867d\u7136\u96c4\u4f1f\u6709\u4f59\uff0c\u4f46\u5374\u79c0\u6da6\u4e0d\u8db3\uff0c\u56e0\u4e3a\u591a\u77f3\u5c11\u571f\uff0c\u534a\u5c71\u4ee5\u4e0a\u6811\u6728\uff0c\u591a\u501f \u4e91\u6c14\u6cbe\u5112\u800c\u751f\uff0c\u4e0d\u6613\u7e41\u8302\uff0c\u53ea\u6709\u5bf9\u677e\u5c71\uff0c\u5f88\u591a\u6811\u7686\u751f\u4e8e\u4e24\u9762\u5ced\u58c1\u4e4b\u4e0a\uff0c\u8fdc\u671b\u9ed1\u7c07\u7c07\u4e00\u6392\uff0c\u6709 \u5982\u9a6c\u9aed\uff0c\u767d\u4e91\u51fa\u6ca1\u5176\u95f4\uff0c\u5b9e\u662f\u4e00\u5927\u80dc\u5904\uff0c\u718a\u501c\u5728\u6b64\u4ef0\u671b\u5357\u5929\u95e8\uff0c\u795e\u9704\u7edb\u9619\uff0c\u53bb\u5929\u5c3a\u4e94\uff0c\u77f3\u78f4 \u5a49\u8712\u4e00\u7ebf\uff0c\u4e0a\u63a5\u82cd\u7a79\uff0c\u8981\u4e0d\u662f\u718a\u501c\u8eab\u6000\u5947\u6280\uff0c\u6709\u6043\u65e0\u6050\uff0c\u771f\u4e0d\u514d\u671b\u800c\u5374\u6b65\u4e86\u3002 \u3000\u3000\u718a\u501c\u6b63\u5728\u51fa\u795e\uff0c\u5ffd\u5730\u8fdc\u5904\u53c8\u6709\u4eba\u4f5c\u6b4c\u800c\u6765\uff0c\u6b4c\u65e5\uff1a\u201c\u9189\u91cc\u6311\u706f\u770b\u5251\uff0c\u68a6\u56de\u5439\u89d2\u8fde\u8425\uff0c\u516b \u767e\u91cc\u5206\u9ebe\u4e0b\u7099\uff0c\u4e94\u5341\u5f26\u7ffb\u585e\u5916\u58f0\uff0c\u6c99\u573a\u79cb\u70b9\u5175\u3002\u9a6c\u4f5c\u7684\u5362\u98de\u7094\uff0c\u5f13\u5982\u9739\u96f3\u5f26\u60ca\uff0c\u4e86\u5374\u541b\u738b\u5929 \u4e0b\u4e8b\uff0c\u8d62\u5f97\u751f\u524d\u8eab\u540e\u540d\uff0c\u53ef\u601c\u767d\u53d1\u751f\u3002\u201d \u3000\u3000\u718a\u501c\u5b9a\u775b\u4e00\u770b\uff0c\u5374\u539f\u6765\u53c8\u662f\u5728\u5b54\u6797\u4e2d\u6240\u9047\u8001\u4eba\uff0c\u62c4\u6756\u98d8\u591a\u800c\u6765\u3002 \u3000\u3000\u90a3\u8001\u8005\u8d70\u81f3\u8fd1\u524d\uff0c\u770b\u5230\u718a\u501c\u7b11\u9053\uff1a\u201c\u771f\u662f\u4eba\u751f\u4f55\u5904\u4e0d\u76f8\u9022\u60f3\u4e0d\u5230\u6211\u4eec\u53c8\u5728\u6b64\u76f8\u89c1\u4e86\u3002\u201d \u3000\u3000\u718a\u501c\u4e5f\u8eac\u8eab\u95ee\u9053\uff1a\u201c\u8001\u4e08\u4f55\u5904\u53bb\u5440\uff1f\u201d \u3000\u3000\u90a3\u8001\u8005\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u6765\u5904\u672a\uff0c\u53bb\u5904\u53bb\uff0c\u98d8\u6d6a\u4eba\u95f4\uff0c\u5feb\u54c9\uff01\u65e5\u540e\u82e5\u518d\u76f8\u9022\uff0c\u90a3\u65f6\u4f60\u4fbf\u662f \u6211\u7684\u4e86\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u53c8\u81ea\u5927\u7b11\u9ad8\u6b4c\u800c\u53bb\u3002 \u3000\u3000\u718a\u501c\u773c\u671b\u4ed6\u80cc\u5f71\u6d88\u5931\uff0c\u90a3\u8001\u8005\u6240\u8bf4\u7684\u8bdd\uff0c\u4ee4\u4ed6\u89c9\u5f97\u5947\u602a\u53c8\u60ca\u5f02\uff0c\u4ed6\u6115\u4e86\u4e00\u4f1a\uff0c\u6e38\u73a9\u7684\u5fc3 \u60c5\u5df2\u5931\uff0c\u4fbf\u5f84\u81ea\u8fd4\u56de\u5c71\u4e1c\u65c5\u5e97\u3002 \u3000\u3000\u4e00\u8fdb\u65c5\u5e97\u5927\u95e8\uff0c\u5ffd\u89c1\u91cc\u9762\u8d70\u51fa\u4e09\u4e2a\u9ed1\u8863\u5927\u6c49\uff0c\u88c5\u675f\u548c\u524d\u89c1\u7684\u9ed1\u767d\u516b\u9a91\u5b8c\u5168\u4e00\u6837\u3002\u8d70\u51fa\u5e97 \u95e8\u65f6\uff0c\u72e0\u72e0\u76ef\u4e86\u718a\u501c\u51e0\u773c\uff0c\u5185\u4e2d\u4e00\u4eba\uff0c\u7a81\u5730\u8f6c\u56de\u8eab\u6765\uff0c\u671d\u718a\u501c\u8bf4\uff1a\u201c\u9601\u4e0b\u770b\u6765\u773c\u719f\uff0c\u53ef\u662f\u9e23 \u8fdc\u9556\u5c40\u7684\u82f1\u96c4\uff1f\u201d \u3000\u3000\u718a\u501c\u6014\u4e86\u6014\uff0c\u56de\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u718a\u501c\uff0c\u4e0d\u77e5\u9601\u4e0b\u6709\u4f55\u89c1\u6559\uff1f\u201d \u3000\u3000\u90a3\u5927\u6c49\u54e6\u4e86\u4e00\u58f0\uff0c\u7b54\u8bf4\uff1a\u201c\u539f\u6765\u9601\u4e0b\u5c31\u662f\u8fd1\u6765\u6c5f\u6e56\u4f20\u8a00\u7684\u718a\u501c\uff0c\u597d\u6781\u4e86\uff0c\u597d\u6781\u4e86\uff0c\u60f3\u6765 \u9601\u4e0b\u5fc5\u662f\u8d74\u655d\u6559\u6cf0\u5c71\u7389\u7687\u9876\u4e4b\u7ea6\u7684\uff0c\u73b0\u5728\u8ddd\u65f6\u8fd8\u6709\u4e00\u65e5\uff0c\u540e\u5929\u4fbf\u662f\u6b63\u65e5\uff0c\u9601\u4e0b\u4e07\u52ff\u5fd8\u8bb0\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u5c31\u62b1\u62f3\u8d70\u4e86\u3002 \u3000\u3000\u718a\u501c\u8fd9\u624d\u77e5\u9053\u8fd9\u5927\u6c49\u539f\u6765\u662f\u5728\u5929\u9634\u6559\u4e0b\u7684\u4eba\u7269\uff0c\u602a\u4e0d\u5f97\u8fd9\u7b49\u8be1\u5f02\u3002 \u3000\u3000\u718a\u501c\u56de\u5230\u623f\u4e2d\uff0c\u6b63\u89c9\u65e0\u804a\uff0c\u5524\u5c0f\u4e8c\u9001\u6765\u4e9b\u9152\u83dc\uff0c\u80e1\u4e71\u5403\u4e86\uff0c\u6b63\u60f3\u65e9\u4e9b\u5c31\u50f5\uff0c\u623f\u95e8\u4e00\u52a8\uff0c \u7a81\u5730\u4e00\u4eba\u8d70\u4e86\u8fdb\u6765\uff0c\u4e5f\u672a\u7b49\u53e3\u5e94\u3002 \u3000\u3000\u718a\u501c\u89c1\u90a3\u4eba\u5168\u8eab\u4e5f\u90fd\u7740\u9ed1\u8272\u8863\u670d\uff0c\u4f46\u5374\u4e0d\u662f\u52b2\u88c5\uff0c\u53ea\u662f\u666e\u901a\u957f\u886b\uff0c\u4e4d\u4e00\u770b\u4ed6\u8fd8\u4ee5\u4e3a\u662f\u58a8 \u9f99\u949f\u5929\u4ec7\uff0c\u8fde\u5fd9\u60ca\u8bb6\u5730\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u90a3\u4eba\u8d70\u8fc7\u6765\u5374\u6df1\u6df1\u4e00\u63d6\uff0c\u7b11\u5bf9\u718a\u501c\u8bf4\uff1a\u201c\u5192\u5473\u5f97\u5f88\uff0c\u524d\u6765\u6253\u6270\uff0c\u5728\u4e0b\u6c5f\u6e56\u5c0f\u5352\u5434\u94a9\u5251\u9f9a \u5929\u6770\uff0c\u73b0\u5728\u5929\u9634\u6559\uff0c\u7384\u9f99\u5802\u9f99\u987b\u652f\u575b\u4e0b\u6548\u529b\uff0c\u4eca\u756a\u542c\u8bf4\u718a\u5927\u4fa0\u5230\u6cf0\u5b89\uff0c\u6025\u5fd9\u8d76\u6765\u76f8\u4f1a\uff0c\u8fd8\u8bf7 \u539f\u8c05\u5510\u7a81\u4e4b\u7f6a\u3002\u201d \u3000\u3000\u718a\u501c\u8fd9\u624d\u770b\u51fa\u6b64\u4eba\u5e76\u975e\u949f\u5929\u4ec7\uff0c\u4e0d\u7981\u6697\u7b11\u81ea\u5df1\u7684\u7d27\u5f20\uff0c\u4f46\u6b64\u4eba\u662f\u5929\u9634\u6559\u4e0b\u7684\u4eba\u7269\uff0c\u4f46\u6837 \u5b50\u5374\u6bd4\u90a3\u4e9b\u9ed1\u8863\u5927\u6c49\u9ad8\u4e86\u4e00\u7ea7\uff0c\u5374\u4e0d\u77e5\u6765\u6b64\u4f55\u4e3a\uff0c\u9010\u8bf4\u9053\uff1a\u201c\u539f\u6765\u662f\u5929\u9634\u6559\u4e0b\u7684\u82f1\u96c4\u5230\u4e86\uff0c \u4e0d\u77e5\u6709\u4f55\u89c1\u6559\uff1f\u201d \u3000\u3000\u9f9a\u5929\u6770\u4e0d\u7b49\u62db\u547c\uff0c\u4fbf\u81ea\u7b11\u563b\u563b\u5730\u5750\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u5144\u53f0\u8fd9\u6b21\u5728\u6c5f\u5357\u786e\u5b9e\u8f70\u8f70\u70c8\u70c8\u7684\u505a\u4e86\u4e00 \u756a\u4e8b\u51fa\u6765\uff0c\u655d\u6559\u975e\u5e38\u666f\u4ef0\uff0c\u6545\u6b64\u7279\u5730\u53eb\u5c0f\u5f1f\u524d\u6765\u62dc\u8bbf\u3002\u201d \u3000\u3000\u539f\u6765\u8fd9\u5929\u9634\u6559\u7684\u7ec4\u7ec7\u751a\u662f\u4e25\u5bc6\uff0c\u6559\u4e3b\u5206\u4e3a\u7384\u9f99\u3001\u767d\u51e4\u4e24\u5802\uff0c\u7384\u9f99\u5802\u4e0b\u53c8\u5206\u9f99\u987b\u3001\u9f99\u722a\u3001 \u9f99\u5c3e\u4e09\u4e2a\u652f\u575b\uff0c\u767d\u51e4\u5802\u4e5f\u6709\u7a1a\u51e4\u3001\u51e4\u7ffc\u3001\u51e4\u9690\u4e09\u4e2a\u652f\u575b\uff0c\u8fd9\u4e09\u4e2a\u652f\u575b\u5404\u6709\u6240\u53f8\uff0c\u9f99\u987b\u575b\u662f\u4e13 \u53f8\u4e3a\u6559\u4e2d\u5438\u6536\u4eba\u624d\uff0c\u65b0\u6559\u5f92\u4eba\u6559\u7b49\u4e8b\uff0c\u9f99\u722a\u575b\u4e13\u53f8\u5211\u8d23\uff0c\u9f99\u5c3e\u575b\u638c\u7ba1\u5404\u7c7b\u8ba1\u5212\uff0c\u51e4\u7ffc\u575b\u4e13\u53f8 \u6559\u4e2d\u5404\u79cd\u796d\u793c\uff0c\u51e4\u9690\u575b\u662f\u4e3a\u6559\u4e2d\u5f52\u9690\u6216\u53d7\u4f24\u4e4b\u6559\u5f92\u800c\u8bbe\u3002 \u3000\u3000\u90a3\u7a1a\u51e4\u575b\u7ba1\u7684\u662f\u4e00\u5b97\u6781\u4e3a\u5947\u602a\u4e4b\u4e8b\uff0c\u539f\u6765\u5929\u9634\u6559\u5f92\u5fc5\u987b\u592b\u5987\u540c\u6559\uff0c\u82e5\u6709\u65b0\u4eba\u4eba\u6559\uff0c\u800c\u672a \u5a5a\u5a36\uff0c\u90a3\u7a1a\u98ce\u575b\u5728\u4e00\u5e74\u4e4b\u5185\uff0c\u5fc5\u5b9a\u8981\u4e3a\u4ed6\u4eec\u627e\u5230\u914d\u5076\uff0c\u5b8c\u6210\u5a5a\u5a36\uff0c\u6545\u6b64\u575b\u4e2d\u5927\u591a\u4ff1\u662f\u4e9b\u672a\u5a5a \u5c11\u5973\u3002 \u3000\u3000\u90a3\u5434\u94a9\u5251\u9f9a\u5929\u6770\u65e2\u662f\u9f99\u987b\u575b\u4e0b\u7684\u4eba\u7269\uff0c\u5230\u6b64\u4e0d\u95ee\u53ef\u77e5\u662f\u60f3\u5438\u6536\u718a\u501c\u5165\u6559\uff0c\u7686\u56fa\u718a\u501c\u867d\u4eba \u9053\u4e0d\u4e45\uff0c\u5728\u6c5f\u6e56\u4e2d\u5374\u5df2\u7565\u6709\u540d\u6c14\u3002 \u3000\u3000\u9f9a\u5929\u6770\u53c8\u8bf4\u9053\uff1a\u201c\u655d\u6559\u8fd9\u6b21\u81ea\u592a\u884c\u5c71\u4e3b\u575b\u5927\u4e3e\u800c\u51fa\uff0c\u4fbf\u662f\u60f3\u5728\u6c5f\u6e56\u521b\u4e00\u756a\u5927\u4e8b\uff0c\u540c\u65f6\u4e5f \u662f\u60f3\u627e\u771f\u6b63\u631f\u6709\u5947\u6280\u7684\u4eba\u7269\u5165\u6559\u3002\u201d \u3000\u3000\u4ed6\u7aef\u8d77\u718a\u501c\u653e\u5728\u684c\u4e0a\u7684\u8336\uff0c\u5477\u4e86\u4e00\u53e3\uff0c\u6ed4\u6ed4\u4e0d\u7edd\u5730\u5c06\u5929\u9634\u6559\u4e2d\u7684\u6897\u6982\uff0c\u5168\u8bf4\u4e86\u51fa\u6765\uff0c\u628a \u4e2a\u5929\u9634\u6559\uff0c\u66f4\u8bf4\u6210\u5929\u4e0a\u5c11\u6709\uff0c\u5730\u4e0b\u65e0\u53cc\uff0c\u800c\u4e14\u9664\u66b4\u5b89\u826f\uff0c\u9020\u798f\u751f\u6c11\uff0c\u662f\u4e2a\u6551\u4e16\u6551\u4eba\u7684\u7ec4\u7ec7\u3002 \u3000\u3000\u718a\u501c\u867d\u89c9\u4e0d\u8010\uff0c\u4f46\u4ed6\u5374\u662f\u5bf9\u5929\u9634\u6559\u4e00\u65e0\u6240\u77e5\uff0c\u4e5f\u4e0d\u77e5\u9053\u9f9a\u5929\u6770\u6b64\u8bdd\u7684\u771f\u5047\uff0c\u4e8e\u662f\u552f\u552f\u7b54 \u5e94\u7740\uff0c\u82e5\u4ed6\u77e5\u9053\u5929\u9634\u6559\u7684\u771f\u76f8\uff0c\u6015\u65e9\u5df2\u7ffb\u76ee\u76f8\u95ee\u4e86\uff0c\u54ea\u91cc\u4f1a\u5bb9\u5f97\u5434\u94a9\u5251\u9f9a\u5929\u6770\u4fe1\u53e3\u96cc\u9ec4\u3002 \u3000\u3000\u9f9a\u5929\u6770\u6b47\u4e86\u53e3\u6c14\u8bf4\u9053\uff1a\u201c\u73b0\u5728\u655d\u6559\u4e2d\u867d\u662f\u5947\u4eba\u8f88\u51fa\uff0c\u6559\u4e3b\u592b\u5987\u7684\u6b66\u529f\uff0c\u66f4\u662f\u5999\u7edd\u5929\u4e0b\uff0c \u6df1\u4e0d\u53ef\u6d4b\uff0c\u4f46\u50cf\u718a\u5144\u8fd9\u6837\u524d\u9014\u65e0\u91cf\u7684\u5c11\u5e74\u82f1\u96c4\uff0c\u6b63\u662f\u655d\u6559\u4e2d\u6e34\u6c42\u7684\uff0c\u718a\u5144\u82e5\u80fd\u52a0\u5165\u655d\u6559\uff0c\u4e0d \u4f46\u718a\u5144\u4ece\u6b64\u80fd\u501f\u6b64\u626c\u540d\u7acb\u4e07\uff0c\u79f0\u96c4\u6b66\u6797\uff0c\u4fbf\u662f\u655d\u6559\uff0c\u4e5f\u56e0\u80fd\u5f97\u5230\u9601\u4e0b\u7684\u8fd9\u6837\u7684\u4e00\u4f4d\u4eba\u7269\u4e3a \u5e78\uff0c\u4e0d\u77e5\u718a\u5144\u610f\u4e0b\u5982\u4f55\uff1f\u201d \u3000\u3000\u718a\u501c\u6c89\u541f\u4e86\u4e00\u4f1a\uff0c\u4ed6\u867d\u5bf9\u5929\u9634\u6559\u4e00\u65e0\u6240\u77e5\uff0c\u4f46\u4ed6\u7684\u76f4\u89c9\u544a\u8bc9\u4ed6\uff0c\u6b64\u6559\u603b\u662f\u592a\u8fc7\u8be1\u5f02\uff0c\u800c \u4e14\u5b9a\u8981\u592b\u5987\u540c\u6559\uff0c\u542c\u6765\u7b80\u76f4\u6709\u4e9b\u8352\u5510\uff0c\u4f46\u4ed6\u4e0d\u80af\u65e0\u7aef\u5f00\u7f6a\u4e8e\u4eba\uff0c\u8003\u8651\u4e86\u8bb8\u4e45\uff0c\u9042\u8bf4\u9053\uff1a\u201c\u9601 \u4e0b\u7684\u597d\u610f\uff0c\u5c0f\u5f1f\u81ea\u662f\u77e5\u9053\uff0c\u4f46\u5c0f\u5f1f\u8fd8\u8981\u8003\u8651\u51e0\u5929\uff0c\u7b49\u5230\u5c0f\u5f1f\u5728\u6cf0\u5c71\u7389\u7687\u9876\u89c1\u5230\u8d35\u6d3e\u6559\u4e3b\u4e4b \u540e\uff0c\u518d\u4f5c\u7b54\u590d\u597d\u4e86\u3002\u201d \u3000\u3000\u9f9a\u5929\u6770\u628a\u8138\u4e00\u6c89\uff0c\u5ffd\u53c8\u7b11\u7740\u8bf4\u9053\uff0c\u201c\u8fd9\u6837\u4e5f\u597d\uff0c\u90a3\u4e48\u5c0f\u5f1f\u5c31\u544a\u8f9e\u4e86\uff0c\u5165\u6559\u4e4b\u4e8b\uff0c\u8fd8\u671b\u718a \u5144\u4e09\u601d\uff0c\u6b64\u4e8b\u5bf9\u718a\u5144\u6765\u8bf4\uff0c\u5b9e\u662f\u6709\u76ca\u65e0\u635f\u7684\u3002\u8bf4\u5b8c\u53c8\u81ea\u662f\u957f\u63d6\u5230\u5730\uff0c\u7b11\u5bb9\u6ee1\u9762\uff0c\u544a\u8f9e\u800c\u53bb \u4e86\u3002\u8fd9\u665a\u4e0a\u718a\u501c\u53cd\u590d\u4e0d\u80fd\u6210\u7720\uff0c\u6697\u60f3\uff1a\u201c\u5929\u9634\u6559\u7ec4\u7ec7\u5e9e\u5927\uff0c\u5206\u5e03\u66f4\u5e7f\uff0c\u6211\u82e5\u52a0\u5165\uff0c\u60f3\u5fc5\u4e0e\u6211 \u590d\u4ec7\u4e4b\u4e8b\u6709\u5229\uff0c\u4ed6\u4eec\u6559\u5f92\u5404\u7701\u90fd\u6709\uff0c\u5bfb\u627e\u8d77\u8428\u5929\u9aa5\u6765\uff0c\u5fc5\u5b9a\u5bb9\u6613\u5f97\u591a\uff0c\u603b\u6bd4\u6211\u5b64\u8eab\u4e00\u4eba\u8981 \u597d\u2026\u2026\u201d \u3000\u3000\u4ed6\u8f6c\u5ff5\u53c8\u60f3\u9053\uff0c\u201c\u53ea\u662f\u6b64\u6559\u770b\u6765\u5374\u592a\u5df2\u4e0d\u6b63\uff0c\u6559\u89c4\u66f4\u662f\u79bb\u5947\uff0c\u82e5\u662f\u4e2a\u65e0\u6076\u4e0d\u4f5c\u7684\u90aa\u6559\uff0c \u6211\u52a0\u5165\u4e86\uff0c\u5374\u600e\u751f\u662f\u597d\u3002\u201d \u3000\u3000\u4ed6\u60f3\u6765\u60f3\u53bb\uff0c\u603b\u5f97\u4e0d\u5230\u4e00\u4e2a\u786e\u5207\u7684\u7b54\u6848\u3002 \u3000\u3000\u6643\u773c\u8fc7\u4e86\u4e00\u5929\uff0c\u5341\u4e94\u65e5\u51cc\u6668\uff0c\u4ed6\u5c31\u8d77\u8eab\u4e86\uff0c\u62fe\u6387\u597d\u4e00\u5207\uff0c\u5c31\u5f80\u6cf0\u5c71\u8d74\u4f1a\uff0c\u5fc3\u795e\u65e2\u7d27\u5f20\uff0c \u53c8\u5174\u594b\uff0c\u6697\u60f3\u9053\uff1a\u201c\u4eca\u65e5\u5c31\u662f\u6211\u51b3\u5b9a\u4eca\u540e\u7684\u91cd\u5927\u5173\u952e\u4e86\uff0c\u7740\u5929\u9634\u6559\u771f\u5982\u5434\u94a9\u5251\u6240\u8bf4\uff0c\u6211\u4e0d\u59a8 \u5c31\u52a0\u5165\uff0c\u518d\u6709\u6211\u8981\u662f\u89c1\u5230\u90a3\u7c89\u9762\u82cf\u79e6\u738b\u667a\u9011\uff0c\u5012\u8981\u770b\u770b\u4ed6\u5bf9\u81ea\u5df1\u6709\u4f55\u4ea4\u6301\u3002\u201d \u3000\u3000\u4ed6\u6cbf\u8def\u6beb\u672a\u803d\u8bef\uff0c\u8d70\u5f97\u6781\u5feb\uff0c\u8fc7\u4e86\u5cb1\u5b97\u574a\uff0c\u4e00\u8def\u53ea\u89c1\u6e38\u4eba\u7edd\u5c11\uff0c\u970e\u65f6\u4fbf\u8fc7\u4e86\u7ecf\u77f3\u5cea\uff0c\u76f4 \u4e0a\u5341\u516b\u76d8\uff0c\u4fbf\u662f\u5357\u5929\u95e8\u4e86\u3002 \u3000\u3000\u5230\u4e86\u5357\u5929\u95e8\uff0c\u718a\u501c\u8fdc\u8fdc\u5c31\u671b\u89c1\u6709\u5341\u6570\u4e2a\u9ed1\u8863\u6c49\u5b50\u4f4d\u5728\u90a3\u91cc\uff0c\u8d70\u5230\u8fd1\u524d\uff0c\u4e00\u4eba\u7b11\u7740\u8fc7\u6765\uff0c \u5374\u662f\u5434\u94a9\u5251\u9f9a\u5929\u6770\uff0c\u718a\u501c\u5fd9\u62b1\u62f3\u4e3a\u793c\uff0c\u9f9a\u592d\u6770\u4e5f\u62b1\u62f3\u7b11\u9053\uff1a\u201c\u718a\u5144\u6765\u5f97\u600e\u5730\u5982\u6b64\u4e4b\u665a\uff0c\u5c0f\u5f1f \u5df2\u606d\u5019\u597d\u4e45\u4e86\uff0c\u5c31\u8bf7\u8d76\u5feb\u4e0a\u5c71\uff0c\u7389\u7687\u9876\u4e0a\uff0c\u6b64\u523b\u5df2\u662f\u7fa4\u96c4\u6bd5\u96c6\u4e86\u3002\u201d \u3000\u3000\u8bf4\u7740\u62c9\u7740\u718a\u501c\u4fbf\u8d70\uff0c\u718a\u501c\u89c1\u90a3\u5341\u6570\u4e2a\u52b2\u88c5\u5927\u6c49\u4ecd\u7136\u5f98\u5f8a\u5728\u752b\u5929\u95e8\u5916\uff0c\u60f3\u662f\u963b\u6b62\u6e38\u4eba\u518d\u4e0a \u7684\u3002 \u3000\u3000\u718a\u501c\u8d70\u8fc7\u90a3\u6761\u5c0f\u8857\uff0c\u90a3\u4e9b\u5356\u6742\u7269\u7684\u94fa\u5b50\uff0c\u6b64\u523b\u4e5f\u662f\u53cc\u95e8\u7d27\u95ed\uff0c\u4e0d\u505a\u751f\u610f\u4e86\u3002 \u3000\u3000\u5feb\u5230\u7389\u7687\u9876\u65f6\uff0c\u6709\u51e0\u4e2a\u767d\u8863\u5987\u4eba\u8d70\u4e86\u4e0a\u6765\uff0c\u5434\u94a9\u5251\u5fd9\u8fce\u4e86\u8fc7\u53bb\uff0c\u4f4e\u58f0\u8bb2\u4e86\u51e0\u53e5\u8bdd\uff0c\u9042\u53eb \u718a\u501c\u8fc7\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u6211\u7684\u5185\u5b50\uff0c\u7389\u89c2\u97f3\u6c6a\u6dd1\u6c55\uff0c\u73b0\u5728\u6559\u4e2d\u7a1a\u51e4\u5802\u4e0b\uff0c\u8fd9\u4f4d\u5c31\u662f\u6211\u8bf4\u7684 \u5c11\u5e74\u82f1\u96c4\u718a\u501c\u4e86\u3002\u201d \u3000\u3000\u90a3\u5987\u4eba\u7b11\u7740\u5bf9\u718a\u501c\u798f\u4e86\u4e00\u798f\uff0c\u718a\u501c\u89c1\u5979\u751a\u662f\u7855\u767d\uff0c\u8eab\u540e\u90a3\u51e0\u4e2a\u5c11\u5973\u4e5f\u5747\u751a\u5a07\u7f8e\uff0c\u90a3\u4e9b\u5c11 \u5973\u89c1\u718a\u501c\u671b\u7740\u5979\u4eec\uff0c\u5747\u63a9\u53e3\u5a07\u7b11\u8d77\u6765\u3002 \u3000\u3000\u9f9a\u5929\u6770\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u718a\u5144\u65e5\u540e\u82e5\u52a0\u5165\u655d\u6559\uff0c\u5c0f\u5f1f\u5fc5\u53eb\u5185\u5b50\u66ff\u718a\u5144\u7269\u8272\u4e00\u4e2a\u56fd\u8272\u4f73 \u4eba\u3002\u201d \u3000\u3000\u718a\u501c\u542c\u4e86\u6b64\u8bdd\uff0c\u518d\u60f3\u8d77\u4ed6\u6240\u8bf4\u7684\u7a1a\u51e4\u5802\u6240\u53f8\u4e4b\u4e8b\uff0c\u4e0d\u7981\u7ea2\u751f\u6ee1\u9762\uff0c\u7389\u89c2\u97f3\u89c1\u4e86\uff0c\u4e5f\u7b11\u7740 \u6253\u8da3\u9053\uff1a\u201c\u4f60\u82e5\u8981\u627e\u4e2a\u597d\u592a\u592a\uff0c\u4e0d\u5148\u62cd\u62cd\u6211\uff0c\u90a3\u600e\u4e48\u6210\uff1f\u201d\u8bf4\u5b8c\u5a9a\u76ee\u6a2a\u76fc\uff0c\u8bcd\u8272\u66f4\u662f\u4e0d\u6b63\u3002 \u3000\u3000\u718a\u501c\u5fc3\u4e2d\u4e0d\u7981\u5927\u5fff\uff0c\u60f3\u9053\u8fd9\u4e9b\u5929\u9634\u6559\u4e0b\u7684\u4eba\u7269\uff0c\u679c\u771f\u4ff1\u90fd\u5982\u6b64\u4e0d\u6b63\uff0c\u4f46\u4ed6\u5230\u5e95\u9762\u5ae9\uff0c\u6b64 \u523b\u88ab\u90a3\u4e9b\u5c11\u5973\u4e00\u7b11\u4e00\u776c\uff0c\u66f4\u8fde\u8bdd\u90fd\u8bf4\u4e0d\u51fa\u6765\u4e86\uff0c\u60f9\u5f97\u9f9a\u5929\u6770\u66f4\u662f\u4e00\u9635\u5927\u7b11\uff0c\u4f46\u4ed6\u6015\u718a\u501c\u8138\u4e0a \u6302\u4e0d\u4f4f\uff0c\u65cb\u5373\u62d6\u7740\u718a\u501c\u76f4\u4e0a\u7389\u7687\u9876\u4e86\u3002 \u3000\u3000\u7389\u7687\u9876\u4fbf\u662f\u6cf0\u5c71\u7edd\u9876\uff0c\u524d\u9762\u6709\u4e00\u4e2a\u767b\u5c01\u53f0\uff0c\u718a\u501c\u5230\u4e86\u7389\u7687\u9876\u4e00\u770b\uff0c\u53ea\u770b\u9876\u4e0a\u5230\u5904\u90fd\u6563\u94fa \u7740\u9ed1\u767d\u4e24\u8272\u7684\u5ea7\u57ab\uff0c\u9ad8\u9ad8\u77ee\u77ee\uff0c\u80d6\u80d6\u7626\u7626\uff0c\u90fd\u662f\u6b66\u6797\u4eba\u7269\uff0c\u718a\u501c\u770b\u4e86\u4e00\u773c\uff0c\u90fd\u4e0d\u8ba4\u5f97\uff0c\u9f9a\u5929 \u6770\u5e26\u4ed6\u4e0a\u6765\u540e\uff0c\u4e5f\u5306\u5306\u8d70\u4e86\uff0c\u4e0d\u77e5\u53bb\u505a\u4ec0\u4e48\uff0c\u718a\u501c\u56db\u5468\u63a2\u671b\uff0c\u89c1\u7a7f\u9ed1\u886b\u7684\u4eba\u53ea\u6709\u4e09\u3001\u4e94\u4e2a\u5728 \u6765\u56de\u8d70\u52a8\uff0c\u5fc3\u60f3\u5927\u6982\u5929\u9634\u6559\u4e3b\u5c1a\u4e14\u672a\u6765\uff0c\u6b63\u60f3\u4e5f\u627e\u4e2a\u5ea7\u5b50\uff0c\u968f\u4fbf\u5750\u4e0b\uff0c\u5ffd\u5730\u542c\u89c1\u80cc\u540e\u6709\u4eba\u5728 \u53eb\u7740\u4ed6\u3002 \u3000\u3000\u4ed6\u56de\u5934\u4e00\u770b\uff0c\u89c1\u6709\u4e00\u4e2a\u7a7f\u7740\u9ed1\u886b\u7684\u4eba\u5411\u4ed6\u8d70\u4e86\u8fc7\u6765\uff0c\u4ed6\u539f\u4ee5\u4e3a\u53c8\u662f\u9f9a\u5929\u6770\uff0c\u4e0d\u60f3\u90a3\u4eba\u8d70 \u5c06\u8fc7\u6765\uff0c\u5374\u662f\u7c89\u9762\u82cf\u79e6\u738b\u667a\u9011\u3002 \u3000\u3000\u718a\u501c\u4e0d\u7981\u5fc3\u4e2d\u89c9\u5f97\u5947\u602a\uff0c\u8fd9\u738b\u667a\u9011\u600e\u5730\u505a\u4e86\u4e2a\u4e8f\u5fc3\u4e8b\u540e\uff0c\u8fd8\u6709\u8138\u524d\u6765\u62db\u547c\uff0c\u4f46\u4ed6\u4e5f\u4e0d\u613f \u592a\u8fc7\u7ed9\u738b\u667a\u9011\u96be\u582a\uff0c\u4e5f\u5c31\u8d70\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u738b\u667a\u9011\u4e00\u89c1\u5230\u4ed6\uff0c\u5c31\u7d27\u63e1\u7740\u4ed6\u7684\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u756a\u82e6\u4e86\u8d24\u5f1f\u4e86\uff0c\u4f46\u611a\u5144\u4e5f\u662f\u4e07\u4e0d\u5f97\u5df2\uff0c \u624d\u51fa\u6b64\u4e0b\u7b56\u7684\uff0c\u5b9e\u56e0\u4e3a\u4e22\u4e86\u6b64\u9556\uff0c\u5173\u7cfb\u5b9e\u5728\u592a\u5927\uff0c\u611a\u5144\u4e5f\u62c5\u5f53\u4e0d\u8d77\uff0c\u8fd8\u5e0c\u671b\u8d24\u5f1f\u80fd\u539f\u8c05\u611a \u5144\u3002\u201d\u718a\u501c\u4e00\u60f3\uff0c\u4e5f\u89c9\u738b\u667a\u9011\u5b9e\u6709\u82e6\u8877\uff0c\u9042\u4e5f\u7f62\u4e86\uff0c\u4ed6\u89c1\u738b\u667a\u9011\uff0c\u7adf\u4e5f\u662f\u5168\u8eab\u9ed1\u8272\u8863\u670d\uff0c\u5b9b \u5982\u5929\u9634\u6559\u5f92\uff0c\u4e0d\u7981\u95ee\u9053\uff1a\u201c\u60a8\u600e\u5730\u5982\u6b64\u6253\u626e\uff1f\u201d \u3000\u3000\u738b\u667a\u9011\u7b11\u9053\uff1a\u201c\u6b64\u4e8b\u8bf4\u6765\u8bdd\u957f\uff0c\u611a\u5144\u4e0d\u77e5\u600e\u5730\u8d70\u6f0f\u4e86\u6d88\u606f\uff0c\u88ab\u5929\u9634\u6559\u4e3b\u77e5\u9053\u6211\u7684\u8ba1\u5212\uff0c \u521a\u5230\u5c71\u4e1c\uff0c\u5c31\u88ab\u622a\u4f4f\uff0c\u611a\u5144\u600e\u662f\u90a3\u5929\u9634\u6559\u7684\u654c\u624b\uff0c\u4e0d\u4f46\u5b9e\u7269\u88ab\u593a\uff0c\u4eba\u4e5f\u88ab\u64d2\u4e86\uff0c\u597d\u5728\u6559\u4e3b\u751a \u662f\u770b\u5f97\u8d77\u611a\u5144\uff0c\u4e00\u5b9a\u8981\u611a\u5144\u5165\u6559\uff0c\u611a\u5144\u8003\u8651\u518d\u4e09\uff0c\u5fc3\u60f3\u5b9e\u7269\u5df2\u4e22\uff0c\u4e8b\u5df2\u4e0d\u4e86\uff0c\u5c31\u5165\u4e86\u5929\u9634\u6559 \u4e86\u3002\u201d \u3000\u3000\u8bf4\u7740\u4ed6\u53c8\u95ee\u8d77\u5434\u8bcf\u4e91\uff0c\u718a\u501c\u8bf4\u9053\uff1a\u201c\u5434\u4e8c\u54e5\u5df2\u56de\u9556\u5c40\u4e86\uff0c\u4ed6\u4f3c\u5bf9\u6c5f\u6e56\u4e0a\u4e8b\uff0c\u5df2\u7ecf\u538c\u5026\uff0c \u8bf4\u8981\u91cd\u8bbf\u540d\u5e08\uff0c\u518d\u6c42\u7edd\u6280\uff0c\u56de\u5230\u9556\u5c40\u540e\uff0c\u5c31\u8981\u6492\u624b\u4e00\u8d70\u4e86\u4e4b\u3002\u201d \u3000\u3000\u738b\u667a\u9011\u795e\u8272\u751a\u662f\u9eef\u7136\uff0c\u9694\u4e86\u4e00\u4f1a\u513f\uff0c\u4ed6\u624d\u8bf4\u9053\uff1a\u201c\u8fd9\u6837\u4e5f\u597d\uff0c\u4f46\u613f\u4ed6\u80fd\u507f\u6240\u613f\uff0c\u53ea\u662f\u90a3 \u8f9b\u82e6\u591a\u5e74\uff0c\u624d\u57f9\u517b\u51fa\u6765\u7684\u9e23\u8fdc\u9556\u5c40\uff0c\u5c31\u8fd9\u6837\u6bc1\u4e8e\u4e00\u65e6\u4e86\u3002\u201d\u8bf4\u5b8c\u4ed6\u53c8\u81ea\u6447\u5934\u53f9\u606f\u4e0d\u5df2\uff0c\u795e\u8272 \u96be\u53d7\u5df2\u6781\u3002 \u3000\u3000\u6b64\u65f6\u5ffd\u7136\u8fdc\u5904\u6709\u91d1\u9523\u58f0\u54cd\uff0c\u738b\u667a\u9011\u542c\u4e86\uff0c\u5fd9\u8bf4\u9053\uff1a\u201c\u91d1\u9523\u58f0\u54cd\uff0c\u6559\u4e3b\u5df2\u5feb\u6765\u4e86\uff0c\u611a\u5144\u8fd8 \u6709\u4e9b\u4e8b\uff0c\u8d24\u5f1f\u968f\u4fbf\u5750\u4e0b\u597d\u4e86\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u4ed6\u5306\u5306\u8d70\u4e86\u3002 \u3000\u3000\u718a\u501c\u9760\u5728\u4e00\u5835\u77f3\u57a3\u5750\u4e0b\uff0c\u7adf\u770b\u5230\u52b3\u5c71\u53cc\u9e64\u3001\u4e03\u6bd2\u4e66\u751f\u7b49\u4eba\u4ff1\u90fd\u65e9\u5df2\u6765\u5230\uff0c\u6563\u5750\u5728\u524d\u9762\uff0c \u90a3\u84dd\u5927\u5148\u751f\u4e5f\u9886\u7740\u51e0\u4e2a\u5f1f\u5b50\uff0c\u5750\u5728\u65c1\u8fb9\uff0c\u770b\u5230\u718a\u501c\u4e5f\u6765\u4e86\uff0c\u8fdc\u8fdc\u4e5f\u5411\u718a\u501c\u7b11\u7740\u6253\u4e86\u4e2a\u62db\u547c\u3002 \u3000\u3000\u718a\u501c\u62ac\u9996\u524d\u671b\uff0c\u89c1\u5230\u9ed1\u8863\u7ae5\u5b50\u767d\u666f\u7965\u548c\u53f6\u6e05\u6e05\u6f2b\u6b65\u8d70\u4e86\u4e0a\u6765\uff0c\u5404\u4eba\u624b\u4e0a\u638c\u4e00\u4e2a\u5c0f\u9523\uff0c\u91d1 \u5149\u707f\u70c2\uff0c\u50cf\u662f\u7eaf\u91d1\u6240\u9020\u3002 \u3000\u3000\u9523\u58f0\u94db\u94db\u6572\u4e86\u4e09\u4e0b\uff0c\u81ea\u666f\u7965\u5f00\u53e3\u8bf4\u9053\uff1a\u201c\u6559\u4e3b\u6cd5\u9a7e\u5df2\u6765\uff0c\u8bf7\u5404\u4f4d\u9759\u8083\u3002\u201d \u3000\u3000\u968f\u5373\u662f\u516b\u4e2a\u957f\u886b\u9ed1\u8863\u7537\u5b50\u548c\u516b\u4e2a\u767d\u8863\u5987\u5973\uff0c\u718a\u501c\u4e5f\u672a\u66fe\u770b\u5f97\u6e05\u695a\uff0c\u53ea\u89c9\u4e2a\u4e2a\u90fd\u662f\u795e\u60c5\u8be1 \u5f02\u4e4b\u4eba\uff0c\u4e0d\u7981\u5bf9\u5929\u9634\u6559\u4eba\u5927\u8d77\u4e86\u6076\u611f\u3002 \u3000\u3000\u6700\u540e\u8d70\u6765\u4e24\u4e2a\u8001\u8005\uff0c\u4e00\u7537\u4e00\u5973\uff0c\u5374\u4e0d\u662f\u9ed1\u767d\u8272\u7684\u886b\uff0c\u90a3\u8001\u8005\u6d51\u8eab\u674f\u9ec4\u888d\u670d\uff0c\u767d\u53d1\u767d\u7709\uff0c \u4e24\u773c\u795e\u5149\u5145\u8db3\uff0c\u663e\u5f97\u5f02\u6837\u5a01\u4e25\uff0c\u90a3\u5973\u5b50\u88c5\u675f\u5374\u66f4\u662f\u79bb\u5947\uff0c\u5979\u7adf\u7a7f\u7740\u5168\u7ea2\u8272\u7684\u5b98\u88c5\u957f\u88d9\uff0c\u5730\u751f \u59ff\uff0c\u8138\u4e0a\u5374\u53c8\u8102\u7c89\u6ee1\u8138\uff0c\u5728\u65e5\u5149\u4e4b\u4e0b\uff0c\u9762\u4e0a\u76b1\u7eb9\u9690\u7ea6\u53ef\u8fa8\uff0c\u770b\u4e0a\u53bb\u4e0d\u4f26\u4e0d\u7c7b\uff0c\u4e0d\u77e5\u50cf\u4e2a\u4ec0\u4e48 \u6837\u5b50\u3002 \u3000\u3000\u718a\u501c\u5fc3\u4e2d\u6697\u6697\u597d\u7b11\uff0c\u53ea\u89c1\u4f17\u4eba\u5bf9\u6b64\u4e24\u4eba\u4ff1\u751a\u606d\u656c\uff0c\u8fd8\u4ee5\u4e3a\u6b64\u4e24\u4eba\u5c31\u662f\u5929\u9634\u6559\u4e3b\u4e86\uff0c\u54ea\u77e5 \u4f17\u4eba\u5ffd\u7136\u5168\u8eac\u4e0b\u8eab\u53bb\uff0c\u63a5\u7740\u53c8\u8d70\u4e0a\u4e00\u7537\u4e00\u5973\uff0c\u4ff1\u90fd\u53ea\u6709\u4e09\u5341\u5c81\u5de6\u53f3\uff0c\u7537\u7684\u4e5f\u5168\u8eab\u9ed1\u8272\u8863\u88f3\uff0c \u4f46\u5374\u95ea\u95ea\u751f\u5149\uff0c\u4f3c\u4e1d\u975e\u4e1d\uff0c\u4f3c\u7ee2\u975e\u7ee2\uff0c\u4e0d\u77e5\u662f\u4ec0\u4e48\u6599\u5b50\uff0c\u5973\u7684\u5168\u8eab\u767d\u8272\u5bab\u7eb1\uff0c\u4ea6\u662f\u957f\u88d9\u88ad \u5730\uff0c\u518d\u52a0\u4e0a\u5b98\u9b13\u5982\u4e91\uff0c\u6865\u7f8e\u5982\u82b1\uff0c\u671b\u4e4b\u771f\u5982\u795e\u4ed9\u4e2d\u4eba\uff0c\u90a3\u7537\u7684\u4ea6\u662f\u5251\u7709\u864e\u76ee\uff0c\u4ea6\u662f\u53cc\u988a\u7626 \u524a\uff0c\u4f46\u770b\u8d77\u6765\u5374\u4ee4\u4eba\u89c9\u5f97\u66f4\u662f\u4e25\u5cfb\uff0c\u671b\u800c\u751f\u754f\u3002 \u3000\u3000\u6b64\u4e24\u4eba\u4e00\u8d70\u4e0a\u6765\uff0c\u718a\u501c\u4e0d\u7981\u6697\u4e2d\u559d\u91c7\u9053\uff1a\u201c\u597d\u4e00\u5bf9\u74a7\u4eba\u3002\u201d \u3000\u3000\u4f17\u4eba\u4e5f\u90fd\u773c\u775b\u4e00\u4eae\uff0c\u5929\u9634\u6559\u4f17\u66f4\u662f\u5c4f\u7740\u6c14\uff0c\u8fde\u5927\u6c14\u90fd\u4e0d\u6562\u51fa\uff0c\u718a\u501c\u77e5\u9053\uff0c\u8fd9\u624d\u662f\u6559\u4e3b\u5230 \u4e86\uff0c\u4ed6\u6697\u81ea\u5947\u602a\uff0c\u8fd9\u800c\u4eba\u4e00\u4e2a\u770b\u6765\u50cf\u662f\u6587\u58eb\uff0c\u4e00\u4e2a\u770b\u6765\u66f4\u662f\u5a07\u5f31\uff0c\u6709\u4ec0\u4e48\u672c\u4e8b\u964d\u4f0f\u5f97\u4f4f\u8fd9\u8bb8 \u591a\u5c71\u9b41\u9b3c\u602a\u3002 \u3000\u3000\u6b64\u4e8c\u4eba\u6b63\u662f\u5929\u9634\u6559\u4e3b\u7126\u5f02\u884c\uff0c\u6218\u74a7\u541b\u592b\u5987\uff0c\u4ed6\u4fe9\u672c\u662f\u5f53\u5e74\u592d\u9634\u6559\u4e0b\u7684\u53f8\u793c\u7ae5\u5b50\uff0c\u81ea\u5e7c\u4fbf \u4ece\u82cd\u865a\u4e0a\u4eba\u592b\u5987\u5904\u5b66\u5f97\u4e00\u8eab\u7edd\u9876\u6b66\u529f\uff0c\u540e\u6765\u5929\u9634\u6559\u88ab\u94c1\u5251\u5148\u751f\u7b49\u4eba\u6240\u706d\uff0c\u4ed6\u4fe9\u4eba\u5374\u4e58\u9699\u9003 \u51fa\uff0c\u593a\u5f97\u4e00\u9690\u79d8\u6240\u5728\uff0c\u82e6\u7ec3\u6b66\u529f\uff0c\u5c06\u8fd1\u4e8c\u5341\u5e74\u6765\uff0c\u4ed6\u4eec\u7684\u6b66\u529f\u5b9e\u5df2\u5230\u4e86\u51fa\u795e\u5165\u5316\u7684\u5730\u4f4d\uff0c\u8fd9 \u624d\u91cd\u5165\u6c5f\u6e56\uff0c\u593a\u5f97\u51e0\u4e2a\u9752\u5e74\u5929\u9634\u6559\u4e2d\u7684\u9b54\u5934\uff0c\u53ca\u4e00\u90e8\u5206\u5c1a\u672a\u6563\u5931\u7684\u79d8\u590d\uff0c\u4e8e\u662f\u53c8\u5728\u592a\u884c\u5c71\u91cc \u91cd\u632f\u65d7\u9f13\uff0c\u6253\u7b97\u518d\u7acb\u5929\u9634\u6559\u3002 \u3000\u3000\u6b64\u523b\u7126\u5f02\u884c\u3001\u6218\u74a7\u541b\u8d70\u5230\u9876\u4e0a\uff0c\u6210\u58c1\u541b\u54c8\u54c8\u5a07\u7b11\u9053\uff1a\u201c\u54df\uff0c\u4f60\u770b\u6765\u4e86\u8fd9\u4e48\u591a\u4f4d\u82f1\u96c4\u597d \u6c49\uff0c\u771f\u662f\u8d4f\u6211\u4eec\u7684\u5149\uff0c\u4e0d\u8fc7\u5b9e\u5728\u5927\u4e0d\u6562\u5f53\u4e86\u3002\u201d \u3000\u3000\u7126\u5f02\u884c\u4e5f\u4e00\u62f1\u624b\u7b11\u9053\uff1a\u201c\u655d\u592b\u5987\u8fd9\u6b21\u91cd\u7acb\u5929\u9634\u6559\uff0c\u8bb8\u591a\u5730\u65b9\u90fd\u5168\u9760\u6c5f\u6e56\u670b\u53cb\u7684\u5e2e\u52a9\uff0c\u8fd9 \u91cc\u5148\u8c22\u4e86\uff0c\u8fd9\u6b21\u655d\u6559\u5728\u6b64\u9080\u8bf7\u5404\u4f4d\u524d\u6765\uff0c\u4e5f\u4e0d\u8fc7\u662f\u5e0c\u671b\u5404\u4f4d\u5bf9\u655d\u6559\u7684\u4e00\u5207\u52a0\u4ee5\u8ba4\u8bc6\uff0c\u6b64\u523b\u655d \u6559\u5148\u5904\u7f6e\u51e0\u4e2a\u6559\u4e2d\u7684\u53db\u5f92\uff0c\u8bf7\u5404\u4f4d\u7a0d\u5019\u3002\u201d \u3000\u3000\u718a\u501c\u89c1\u5929\u9634\u6559\u4e3b\u592b\u5987\uff0c\u5374\u5ba2\u6c14\u5f97\u7d27\uff0c\u4e0d\u89c9\u53c8\u5bf9\u4ed6\u4eec\u8d77\u4e86\u597d\u611f\u3002 \u3000\u3000\u8c01\u77e5\u7126\u5f02\u884c\u628a\u8138\u5b54\u4e00\u677f\uff0c\u7acb\u65f6\u53c8\u662f\u4e00\u756a\u9762\u5bb9\uff0c\u5389\u58f0\u8bf4\u9053\uff1a\u201c\u9f99\u722a\u575b\u575b\u4e3b\u9ed1\u715e\u9b54\u638c\u5c1a\u6587\u658c \u4f55\u5728\u3002\u201d \u3000\u3000\u90a3\u5148\u6765\u7684\u5341\u6570\u4e2a\u9ed1\u886b\u4eba\u4e2d\uff0c\u7aef\u6b65\u8d70\u51fa\u4e00\u4eba\uff0c\u662f\u4e2a\u5f62\u5982\u673d\u7626\u7684\u8001\u5934\uff0c\u6700\u5947\u7684\u662f\u4e0d\u4f46\u8863\u5c65\u7686 \u9ed1\uff0c\u9762\u5b54\u80a4\u8272\uff0c\u4e5f\u662f\u9ed1\u7684\uff0c\u53cc\u76ee\u77b3\u7136\uff0c\u4ee4\u4eba\u671b\u800c\u751f\u754f\u3002 \u3000\u3000\u5728\u573a\u4f17\u4eba\u9664\u4e86\u718a\u501c\u56e0\u5bf9\u6b66\u6797\u7fa4\u9b54\uff0c\u4e00\u65e0\u6240\u77e5\uff0c\u53ea\u89c9\u5f97\u6b64\u4eba\u53ef\u6015\u8fd8\u4e0d\u89c9\u600e\u6837\u4e4b\u5916\uff0c\u5176\u4f59\u5404 \u4eba\uff0c\u542c\u4e86\u9ed1\u715e\u9b54\u638c\u7684\u540d\u5b57\uff0c\u4ff1\u90fd\u5934\u76ae\u53d1\u9ebb\u3002\u201c\u7686\u56e0\u8fd9\u9ed1\u715e\u9b54\u5934\u5728\u6b66\u6797\u4e4b\u4e2d\uff0c\u79f0\u5f97\u4e0a\u6700\u662f\u5fc3\u72e0 \u624b\u8fa3\uff0c\u6740\u4eba\u5982\u9ebb\uff0c\u5f53\u5e74\u4e0e\u6bd2\u5fc3\u795e\u9b54\u4faf\u751f\uff0c\u5e76\u79f0\u6b66\u6797\u53cc\u9b54\uff0c\u5374\u6bd4\u4faf\u751f\u66f4\u662f\u9634\u6bd2\uff0c\u540e\u6765\u4e5f\u662f\u6d17\u624b \u5f52\u9690\uff0c\u6b64\u523b\u5374\u53c8\u5728\u6b64\u73b0\u8eab\uff0c\u4e14\u662f\u5929\u9634\u6559\u4e0b\u7684\u575b\u4e3b\uff0c\u4e8e\u662f\u5728\u573a\u7684\u6bcf\u4e2a\u4eba\u5bf9\u5929\u9634\u6559\u7684\u5b9e\u529b\uff0c\u66f4\u89c9 \u53ef\u754f\u3002\u7126\u5f02\u884c\u53c8\u8bf4\u9053\uff1a\u201c\u8bf7\u9f99\u722a\u575b\u4e0b\uff0c\u5c06\u6c64\u5b5d\u5b8f\u3001\u9648\u6587\u9f99\u3001\u859b\u5149\u7956\u7b49\u53db\u5f92\u5e26\u4e0a\uff0c\u9759\u5f85\u88c1 \u51b3\u3002\u201d \u3000\u3000\u9ed1\u715e\u9b54\u638c\u8eac\u8eab\u79f0\u662f\uff0c\u8d70\u5f00\u4e86\u53bb\u3002 \u3000\u3000\u7126\u5f02\u884c\u9042\u53c8\u4e00\u6325\u624b\uff0c\u90a3\u53f8\u793c\u7ae5\u5b50\u767d\u666f\u7965\u3001\u53f6\u6e05\u6e05\u9f50\u58f0\u8bf4\u9053\uff1a\u201c\u606d\u8bf7\u7384\u9f99\u5802\u4e3b\u3001\u767d\u51e4\u5802\u4e3b \u4eba\u575b\u3002\u201d\u90a3\u9ec4\u8863\u8001\u8005\u4e0e\u7ea2\u670d\u5973\u5b50\u9f50\u8d70\u4e86\u51fa\u6765\uff0c\u5bf9\u7126\u5f02\u884c\u592b\u5987\u53ea\u662f\u5fae\u4e00\u62f1\u624b\uff0c\u4fbf\u81ea\u7ad9\u4f4f\u3002 \u3000\u3000\u4f17\u4eba\u4ff1\u77e5\u7384\u9f99\u3001\u767d\u98ce\u4e24\u5802\uff0c\u5728\u5929\u9634\u6559\u4e2d\uff0c\u5730\u4f4d\u6781\u9ad8\uff0c\u4ec5\u6b21\u4e8e\u6559\u4e3b\u592b\u5987\uff0c\u4f46\u5bf9\u6b64\u4e24\u4eba\u7fa4\u8c6a \u5374\u65e0\u4e00\u4eba\u8ba4\u5f97\uff0c\u5404\u5728\u8179\u4e2d\u7eb3\u95f7\u4e0d\u5df2\u3002 \u3000\u3000\u7247\u523b\u4e24\u4e2a\u9ed1\u8863\u52b2\u88c5\u5927\u6c49\uff0c\u5e26\u6765\u56db\u4eba\uff0c\u718a\u501c\u4e00\u770b\u751f\u6b7b\u5224\u5728\u5176\u4e2d\uff0c\u4f46\u90a3\u65f6\u9a84\u6c14\uff0c\u6b64\u523b\u534a\u70b9\u4e5f \u6ca1\u6709\u4e86\uff0c\u9762\u5b54\u770b\u53bb\uff0c\u50cf\u662f\u60e7\u6015\u5df2\u6781\uff0c\u53e6\u5916\u90a3\u4e09\u4eba\uff0c\u4e5f\u662f\u5782\u5934\u4e27\u6c14\uff0c\u800c\u4e14\u5168\u8eab\u53d1\u6296\uff0c\u6015\u5f97\u66f4\u662f \u5389\u5bb3\u3002 \u3000\u3000\u7126\u5f02\u884c\u89c1\u8fd9\u56db\u4eba\uff0c\u66f4\u662f\u9762\u5982\u79cb\u971c\uff0c\u5389\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u7b49\u56db\u4eba\u7684\u7f6a\u72b6\uff0c\u6211\u4e5f\u4e0d\u5fc5\u5f53\u7740\u5929\u4e0b\u82f1 \u96c4\u63ed\u9732\uff0c\u4f46\u95ee\u4f60\u7b49\u77e5\u7f6a\u4e0e\u5426\u3002\u201d \u3000\u3000\u90a3\u56db\u4eba\u4ff1\u90fd\u8dea\u5728\u5730\u4e0a\uff0c\u8fde\u5934\u90fd\u4e0d\u6562\u62ac\u8d77\u6765\uff0c\u53ea\u662f\u8fde\u8fde\u53e9\u9996\uff0c\u72b6\u751a\u53ef\u601c\u3002 \u3000\u3000\u7126\u5f02\u884c\u53c8\u8bf4\u9053\uff1a\u201c\u4f60\u7b49\u56db\u4eba\u65e2\u7136\u77e5\u7f6a\uff0c\u672c\u6559\u4e3b\u5bbd\u5927\u4e3a\u6000\uff0c\u5fc5\u5b9a\u4ece\u8f7b\u53d1\u843d\u3002\u201d\u4ed6\u9042\u53c8\u8f6c\u5934 \u5411\xb7\u90a3\u9ec4\u8863\u8001\u8005\u53ca\u7ea2\u670d\u5973\u5b50\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\u53ef\u6709\u610f\u89c1\u3002\u201d \u3000\u3000\u90a3\u4e24\u4eba\u9f50\u90fd\u8bf4\u9053\uff1a\u201c\u4f46\u51ed\u6559\u4e3b\u53d1\u843d\u3002\u201d \u3000\u3000\u7126\u5f02\u884c\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u6c64\u5b5d\u5b8f\u3001\u9648\u6587\u9f99\u3001\u8042\u91cd\u5f6c\u4e09\u4e2a\u7f6a\u72b6\u5c1a\u8f7b\uff0c\u524a\u53bb\u5de6\u624b\uff0c\u53d1\u5728\u51e4\u9690\u5802\u4e0b \u6548\u529b\uff0c\u5982\u65e5\u540e\u8868\u73b0\u826f\u597d\uff0c\u518d\u884c\u5f55\u7528\uff0c\u859b\u5149\u7956\u6b3a\u5e08\u53db\u6559\uff0c\u7f6a\u65e0\u53ef\u6055\uff0c\u9664\u5241\u53bb\u53cc\u8db3\u5916\uff0c\u53d1\u9001\u56de \u4e61\u3002\u201d \u3000\u3000\u718a\u501c\u89c1\u7126\u5f02\u884c\u8bf4\u4ece\u8f7b\u53d1\u843d\uff0c\u5fc3\u91cc\u4ee5\u4e3a\u6700\u591a\u6253\u4e2a\u51e0\u677f\uff0c\u6216\u662f\u7981\u95ed\u4e24\u5e74\uff0c\u6b64\u523b\u4e00\u542c\u5c45\u7136\u524a\u624b \u5241\u8db3\uff0c\u5413\u5f97\u6d51\u8eab\u51b7\u6c57\u76f4\u5192\u3002 \u3000\u3000\u7136\u800c\u66f4\u60e8\u7684\u8fd8\u5728\u540e\u5934\uff0c\u7126\u5f02\u884c\u8bdd\u521a\u8bf4\u5b8c\uff0c\u90a3\u9ed1\u715e\u9b54\u638c\u5df2\u8d70\u4e86\u8fc7\u6765\uff0c\u6781\u5feb\u5730\u5728\u56db\u4eba\u8eab\u65c1\u4e00 \u8f6c\uff0c\u7fa4\u8c6a\u5c1a\u672a\u770b\u6e05\u662f\u4ec0\u4e48\u8eab\u6cd5\uff0c\u90a3\u56db\u529e\u5374\u5df2\u4ff1\u90fd\u6655\u5012\uff0c\u539f\u6765\u5168\u88ab\u9ed1\u715e\u9b54\u638c\u70b9\u4e86\u6781\u91cd\u7684\u7a74\u9053\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u9ed1\u8863\u5927\u6c49\uff0c\u968f\u5373\u62bd\u51fa\u94a2\u5200\uff0c\u5506\u5506\u51e0\u5200\uff0c\u7247\u523b\u53ea\u89c1\u8840\u6d41\u6ee1\u5730\uff0c\u90a3\u56db\u4eba\u624b\u8db3\uff0c\u5df2\u88ab\u5241 \u4e86\u4e0b\u6765\uff0c\u5448\u5230\u7126\u5f02\u884c\u7684\u9762\u524d\u3002 \u3000\u3000\u7fa4\u8c6a\u54ea\u66fe\u89c1\u8fc7\u8fd9\u7b49\u573a\u9762\uff0c\u718a\u501c\u66f4\u662f\u6c57\u6d41\u4fa0\u80cc\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u5929\u9634\u6559\u4e3b\uff0c\u770b\u53bb\u6587\u79c0\u5df2\u6781\uff0c\u54ea \u77e5\u5374\u8fd9\u7b49\u6b8b\u5fcd\uff0c\u5c06\u4eba\u7684\u6027\u547d\u8eab\u4f53\uff0c\u53ea\u770b\u505a\u7caa\u571f\u4e00\u6837\uff0c\u7531\u6b64\u53ef\u89c1\u5929\u9634\u6559\u4e4b\u9634\u72e0\u6bd2\u8fa3\uff0c\u5e78\u597d\u90a3\u65f6 \u6ca1\u6709\u7b54\u5e94\u9f9a\u5929\u6770\uff0c\u4e0d\u7136\u5374\u600e\u4e48\u5f97\u4e86\u3002\u201d \u3000\u3000\u7126\u5f02\u884c\u6325\u624b\u547d\u4eba\u62ac\u8d70\u90a3\u56db\u4e2a\u5b9b\u5982\u5c38\u4f53\u7684\u4eba\uff0c\u7acb\u53c8\u6ee1\u9762\u6625\u98ce\u7b11\u9053\uff1a\u201c\u9002\u624d\u7684\u4e8b\uff0c\u5012\u6559\u5404\u4f4d \u89c1\u7b11\u4e86\uff0c\u6211\u5148\u66ff\u5404\u4f4d\u5f15\u89c1\u4e24\u4f4d\u5927\u5927\u6709\u540d\u7684\u4eba\u7269\uff0c\u5404\u4f4d\u4e5f\u8bb8\u751f\u5f97\u8f83\u665a\uff0c\u4f46\u8fd9\u4e24\u4f4d\u5148\u8f88\u7684\u540d\u5934\uff0c \u60f3\u5fc5\u4e00\u5b9a\u542c\u5230\u8fc7\u7684\u3002\u201d\u8bf4\u5b8c\u4ed6\u9042\u4e00\u6307\u90a3\u674f\u9ec4\u8863\u886b\u7684\u8001\u8005\u53ca\u7ea2\u670d\u5973\u5b50\u8bf4\u9053\uff1a\u201c\u8fd9\u4e24\u4f4d\u4fbf\u662f\u4e09\u5341 \u5e74\u524d\u5929\u4e0b\u77e5\u540d\u7684\u94c1\u9762\u9ec4\u886b\u5ba2\u4ec7\u4e0d\u53ef\u4ec7\u8001\u524d\u8f88\uff0c\u53ca\u4e5d\u5929\u4ed9\u5b50\u7f2a\u5929\u9706\u7f2a\u8001\u524d\u8f88\uff0c\u8fd9\u4e24\u4f4d\u524d\u8f88\u7684\u5947 \u4eba\u5947\u884c\uff0c\u5404\u4f4d\u867d\u7136\u6ca1\u6709\u770b\u5230\uff0c\u4f46\u603b\u542c\u5230\u8fc7\u5427\u3002\u201d \u3000\u3000\u8bf8\u8c6a\u4e00\u542c\uff0c\u8fd9\u4e00\u60ca\uff0c\u6bd4\u65b9\u624d\u542c\u5230\u9ed1\u715e\u9b54\u638c\u65f6\u66f4\u8981\u5389\u5bb3\u5341\u500d\uff0c\u6709\u7684\u751a\u81f3\u8c05\u547c\u51fa\u6765\uff0c\u8fd9\u4e8c\u4eba \u5f53\u65f6\u5728\u6b66\u6797\u4e2d\u7684\u540d\u5934\uff0c\u53ef\u79f0\u5f97\u4e0a\u662f\u7693\u6708\u5f53\u7a7a\uff0c\u9ed1\u715e\u9b54\u638c\u867d\u4e5f\u5927\u540d\u9f0e\u9f0e\uff0c\u6bd4\u8d77\u4ed6\u4eec\u6765\uff0c\u53ea\u662f\u7693 \u6708\u65c1\u8fb9\u7684\u5c0f\u661f\u7f62\u4e86\u3002 \u3000\u3000\u7126\u5f02\u884c\u89c1\u4f17\u4eba\u60ca\u60e7\u4e4b\u8272\uff0c\u6ea2\u4e8e\u8a00\u8868\uff0c\u5fc3\u4e2d\u66f4\u662f\u5f97\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5929\u9634\u6559\u521b\u4e8e\u592a\u884c\u5c71\uff0c\u8fdc \u6765\u5c71\u4e1c\uff0c\u4e00\u6765\u662f\u4e3a\u4e86\u5ba3\u626c\u6559\u5a01\uff0c\u518d\u8005\u4fbf\u662f\u5e0c\u671b\u6b66\u6797\u7fa4\u8c6a\uff0c\u80fd\u6295\u5165\u6211\u5929\u9634\u6559\u4e0b\uff0c\u6211\u4e4b\u4eca\u65e5\u9080\u8bf7 \u5404\u4f4d\u524d\u6765\u6cf0\u5c71\uff0c\u9664\u4e86\u4e10\u5e2e\u8bf8\u4fa0\u662f\u8bf7\u6765\u89c2\u793c\u4e0d\u5728\u6b64\u4f8b\u5916\uff0c\u4e5f\u662f\u4e3a\u7740\u8fd9\u4e2a\u7f18\u6545\uff0c\u8fd9\u70b9\u60f3\u655d\u6559\u9f99\u987b \u575b\u4e0b\u7684\u5f1f\u5b50\uff0c\u5728\u5404\u4f4d\u4e0a\u5c71\u4e4b\u524d\uff0c\u4e5f\u4ff1\u90fd\u5411\u5404\u4f4d\u89e3\u8bf4\u4e86\uff0c\u6b64\u523b\u8bf8\u4f4d\u5df2\u7b97\u662f\u5165\u5929\u9634\u6559\u4e0b\uff0c\u4f46\u5404\u4f4d \u4ff1\u90fd\u521b\u6709\u4e8b\u4e1a\uff0c\u6211\u81ea\u4e5f\u4e0d\u4f1a\u4f5c\u90a3\u4e0d\u901a\u4eba\u60c5\u4e4b\u4e8b\uff0c\u786c\u8981\u5404\u4f4d\u653e\u5f03\uff0c\u6545\u6211\u4e0d\u60dc\u7a0d\u6539\u6559\u89c4\uff0c\u5404\u4f4d\u5165 \u6211\u6559\u4e2d\u540e\uff0c\u53ea\u8981\u4e0d\u72af\u6559\u89c4\uff0c\u4e0d\u4f5c\u53db\u6559\u4e4b\u4e3e\u5916\uff0c\u4ecd\u53ef\u968f\u610f\u884c\u4e8b\uff0c\u54c8\u3001\u54c8\uff0c\u8fd9\u756a\u82e6\u5fc3\uff0c\u8fd8\u4e0d\u662f\u4e3a \u4e86\u7231\u60dc\u5404\u4f4d\uff0c\u5404\u4f4d\u53ef\u66fe\u660e\u767d\uff1f\u201d \u3000\u3000\u718a\u501c\u8d8a\u542c\u8d8a\u4e0d\u50cf\u8bdd\uff0c\u6b64\u4eba\u4e4b\u5f3a\u8bcd\u593a\u7406\uff0c\u53ef\u8c13\u5df2\u5230\u9876\u70b9\uff0c\u4ed6\u5f3a\u8feb\u5165\u6559\uff0c\u5374\u8fd8\u8bf4\u201c\u5f88\u6709\u4eba \u60c5\u201d\u3001\u201c\u8d39\u4e86\u82e6\u529b\u201d\uff0c\u771f\u662f\u4ee4\u4eba\u557c\u7b11\u7686\u975e\uff0c\u7b49\u7126\u5f02\u884c\u8bf4\u5b8c\uff0c\u718a\u501c\u4fbf\u60f3\u6297\u8bae\uff0c\u65b9\u5f85\u7ad9\u8d77\u8eab\u6765\u3002 \u3000\u3000\u8c01\u77e5\u6218\u74a7\u541b\u53c8\u54af\u54af\u7b11\u9053\uff1a\u201c\u54df\uff0c\u4f60\u8bf4\u5f97\u53ef\u597d\uff0c\u4f46\u662f\u4eba\u5bb6\u8981\u662f\u4e0d\u613f\u610f\u5462\u3002\u201d \u3000\u3000\u7126\u5f02\u884c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6b64\u8bdd\u6b63\u662f\uff0c\u53ea\u662f\u4e0a\u5c71\u5bb9\u6613\uff0c\u4e0b\u5c71\u5374\u96be\u4e86\uff0c\u5404\u4f4d\u8981\u6709\u4eba\u4e0d\u613f\u5165\u6211\u5929\u9634 \u6559\u4e0b\u3001\u4e5f\u8bf7\u7ad9\u51fa\u6765\uff0c\u53ea\u8981\u6709\u80fd\u6321\u5f97\u8fc7\u6211\u592b\u59bb\u5341\u62db\u7684\uff0c\u655d\u6559\u4e0d\u4f46\u606d\u9001\u4ed6\u4e0b\u5c71\uff0c\u800c\u4e14\u8fd8\u8981\u5c06\u4e00\u4ef6 \u81f3\u5b9d\u5949\u9001\uff0c\u53ef\u662f\u5404\u4f4d\u5374\u8981\u81ea\u95ee\u6709\u6ca1\u6709\u8fd9\u4e2a\u80fd\u529b\uff0c\u8981\u4e0d\u7136\u81ea\u9001\u4e86\u6027\u547d\uff0c\u5374\u662f\u5927\u5927\u7684\u4e0d\u503c\u5f97 \u5462\u3002\u201d\u8bf4\u5b8c\u4ed6\u53c8\u4e00\u62db\u624b\uff0c\u559d\u9053\uff1a\u201c\u5feb\u628a\u6210\u5f62\u9996\u4e4c\u53d6\u6765\uff0c\u653e\u5728\u6b64\u5904\uff0c\u770b\u770b\u6709\u54ea\u4f4d\u82f1\u96c4\u597d\u6c49\uff0c\u80fd \u591f\u53d6\u5f97\u3002\u201d\u8bf4\u5b8c\u54c8\u54c8\u72c2\u7b11\uff0c\u50b2\u6c14\u6bd5\u5448\u3002 \u3000\u3000\u718a\u501c\u542c\u4e86\u518d\u4e86\u8010\u4e0d\u4f4f\uff0c\u4ed6\u672c\u5750\u5728\u6700\u540e\uff0c\u6b64\u523b\u5374\u7ad9\u4e86\u8d77\u6765\uff0c\u8d8a\u4f17\u8d70\u4e86\u51fa\u6765\uff0c\u8bf8\u4eba\u4ff1\u90fd\u9762\u9762 \u60ca\u8bb6\u5730\u671b\u7740\uff0c\u5374\u518d\u4e5f\u6ca1\u6709\u4e00\u4eba\u7ad9\u8d77\u6765\u4e86\u3002 \u3000\u3000\u7126\u5f02\u884c\u89c1\u6709\u4eba\u7ad9\u8d77\u6765\u8d70\u4e86\u8fc7\u6765\uff0c\u4e0d\u7981\u53d8\u8272\u51b7\u7b11\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u8fd9\u662f\u54ea\u4e00\u4f4d\u82f1\u96c4\uff0c\u6709\u6b64\u80c6 \u91cf\uff0c\u6211\u7126\u5f02\u884c\u771f\u662f\u4f69\u670d\u5f97\u5f88\u3002\u201d \u3000\u3000\u718a\u501c\u8d70\u4e0a\u524d\u6765\uff0c\u5fae\u5fae\u4e00\u63d6\uff0c\u6602\u7136\u8bf4\u9053\uff1a\u201c\u5c0f\u5b50\u718a\u501c\uff0c\u672c\u662f\u6c5f\u6e56\u672b\u6d41\uff0c\u6559\u4e3b\u9ad8\u8bba\uff0c\u6211\u4e5f\u542c \u8fc7\u4e86\uff0c\u4f46\u662f\u4eba\u5404\u6709\u5fd7\uff0c\u8c01\u4e5f\u4e0d\u80fd\u76f8\u5f3a\uff0c\u7eb5\u7136\u6211\u6321\u4e0d\u8fc7\u6559\u4e3b\u5341\u62db\uff0c\u5c31\u7b97\u846c\u8eab\u6b64\u95f4\uff0c\u4e5f\u662f\u60c5\u613f\uff0c \u82e5\u662f\u5b9a\u8981\u5f3a\u8feb\u6211\u4f5c\u8fdd\u613f\u4e4b\u4e8b\uff0c\u5374\u662f\u4e07\u4e07\u4e0d\u884c\u3002\u201d \u3000\u3000\u4ed6\u8bdd\u5c1a\u672a\u8bf4\u5b8c\uff0c\u8fdc\u5904\u6709\u4eba\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u6709\u5fd7\u6c14\u3002\u201d \u3000\u3000\u58f0\u97f3\u5e76\u4e0d\u5927\uff0c\u4f46\u9707\u5f97\u4f17\u4eba\u8033\u6735\u55e1\u55e1\u4f5c\u54cd\uff0c\u7fa4\u8c6a\u4e0d\u7981\u5927\u60ca\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u4e00\u4eba\u76d8\u5750\u5728\u90a3 \u201c\u79e6\u7687\u6ca1\u5b57\u7891\u201d\u4e0a\uff0c\u7b11\u58f0\u5140\u81ea\u672a\u7edd\u3002 \u3000\u3000\u5728\u573a\u7684\u8fd9\u8bb8\u591a\u6b66\u6797\u4e2d\u4e00\u7b49\u4e00\u7684\u597d\u624b\uff0c\u7adf\u6ca1\u6709\u4e00\u4eba\u77e5\u9053\u6b64\u4eba\u4ece\u4f55\u800c\u6765\uff0c\u4f55\u65f6\u800c\u6765\u7684\u3002 \u3000\u3000\u7126\u5f02\u884c\u4ea6\u662f\u5927\u60ca\uff0c\u5389\u58f0\u8bf4\u9053\uff1a\u201c\u7891\u4e0a\u7684\u662f\u54ea\u8def\u9ad8\u4eba\uff0c\u8bf7\u4e0b\u6765\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u90a3\u4eba\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u65e2\u7136\u6559\u4e3b\u76f8\u53ec\uff0c\u6562\u4e0d\u4ece\u547d\u3002\u201d\u8bdd\u521a\u8bf4\u5b8c\uff0c\u7fa4\u8c6a\u773c\u775b\u4e00\u82b1\uff0c\u90a3\u4eba\u5df2 \u5230\u4e86\u9762\u524d\uff0c\u4ecd\u7136\u662f\u76d8\u819d\u800c\u5750\uff0c\u7adf\u4e0d\u77e5\u4ed6\u662f\u600e\u4e48\u6765\u7684\u3002 \u3000\u3000\u718a\u501c\u4e00\u89c1\uff0c\u6b64\u4eba\u7adf\u662f\u5728\u5b54\u6797\u9047\u5230\u7684\u7ea2\u9762\u8001\u4eba\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u77e5\u9053\u6551\u661f\u5230\u4e86\u3002 \u3000\u3000\u8001\u4eba\u51b2\u7740\u7126\u5f02\u884c\u592b\u5987\u9894\u9996\u7b11\u9053\uff1a\u201c\u6559\u4e3b\u8d24\u592b\u5987\u8fd8\u8ba4\u5f97\u6211\u8001\u5934\u5b50\u5427\uff0c\u4e8c\u5341\u5e74\u4e0d\u89c1\u4e86\uff0c\u8d24\u592b \u5987\u5c45\u7136\u51fa\u843d\u5f97\u5982\u6b64\u82f1\u4fca\uff0c\u771f\u6559\u6211\u8001\u5934\u6b22\u559c\u3002\u201d \u3000\u3000\u7126\u5f02\u884c\u3001\u6218\u74a7\u541b\u4e8c\u4eba\uff0c\u4e00\u89c1\u6b64\u8001\u4eba\u98d8\u7136\u800c\u843d\uff0c\u5148\u662f\u4e00\u60ca\uff0c\u5f85\u4ed4\u7ec6\u4e00\u770b\u4e4b\u540e\uff0c\u8138\u4e0a\u7684\u5028\u50b2 \u4e4b\u6c14\uff0c\u987f\u65f6\u6d88\u5931\u65e0\u8e2a\uff0c\u6362\u4e0a\u4e86\u60e7\u754f\u4e4b\u8272\uff0c\u4f46\u4ee5\u6559\u4e3b\u8eab\u4efd\uff0c\u867d\u7136\u5df2\u77e5\u9762\u524d\u662f\u4f55\u4eba\uff0c\u4e5f\u7edd\u4e0d\u80fd\u9732 \u51fa\u60ca\u60f6\u4e4b\u8272\u3002 \u3000\u3000\u7126\u5f02\u884c\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u539f\u6765\u662f\u98d8\u7136\u8001\u524d\u8f88\uff0c\u665a\u8f88\u4e45\u8fdd\u98ce\u8303\uff0c\u60f3\u4e0d\u5230\u8001\u524d\u8f88\u8fd8\u662f\u8fd9\u7b49\u77cd \u5065\u3002\u201d \u3000\u3000\u90a3\u8001\u4eba\u65e0\u4eba\u77e5\u4ed6\u59d3\u540d\uff0c\u4ff1\u79f0\u4ed6\u4e3a\u98d8\u7136\u8001\u4eba\uff0c\u6570\u5341\u5e74\u59cb\u7ec8\u72ec\u6765\u72ec\u5f80\uff0c\u4e5f\u65e0\u4eba\u77e5\u4ed6\u6765\u5904\u53bb \u5904\uff0c\u4eba\u4eec\u6570\u5341\u5e74\u524d\u770b\u89c1\u4ed6\u65f6\u662f\u8fd9\u6837\u5b50\uff0c\u6570\u5341\u5e74\u540e\u4ed6\u4f9d\u7136\u4e0d\u53d8\uff0c\u4eba\u4eec\u6708\u77e5\u4ed6\u7684\u6b66\u529f\u6df1\u4e0d\u53ef\u6d4b\uff0c \u6614\u5e74\u94c1\u5251\u5148\u751f\u82e5\u4e0d\u662f\u5f97\u5230\u98d8\u7136\u8001\u4eba\u4e4b\u52a9\uff0c\u72ec\u529b\u51fb\u6b7b\u4e86\u5929\u9634\u6559\u4e3b\u592b\u5987\uff0c\u4e5f\u4e0d\u80fd\u5c06\u5929\u9634\u6559\u74e6\u89e3\uff0c \u4ed6\u4e00\u522b\u4eba\u95f4\u4e8c\u5341\u5e74\uff0c\u6b64\u523b\u53c8\u91cd\u73b0\u4e86\u3002 \u3000\u3000\u98d8\u7136\u8001\u4eba\u542c\u7126\u5f02\u884c\u8bf4\u5b8c\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u8fd8\u8bb0\u5f97\u6211\u8001\u5934\u5b50\uff0c\u6211\u8001\u5934\u5b50\u8fd9\u756a\u524d\u6765\uff0c \u4e95\u975e\u8981\u7ba1\u6559\u4e3b\u7684\u95f2\u4e8b\uff0c\u4e00\u4e2a\u613f\u6253\uff0c\u4e00\u4e2a\u613f\u6328\uff0c\u4f60\u4eec\u60f3\u8981\u4ed6\u4eec\u5165\u6559\uff0c\u4ed6\u613f\u610f\uff0c\u6211\u8001\u5934\u5b50\u600e\u80fd\u7ba1 \u5f97\u3002\u201d \u3000\u3000\u4ed6\u54c8\u54c8\u53c8\u7b11\u4e86\u4e00\u9635\uff0c\u8bf4\u9053\uff1a\u201c\u53ea\u662f\u6709\u4e24\u4ef6\u4e8b\uff0c\u6211\u5374\u8981\u7ba1\u4e00\u7ba1\uff0c\u7b2c\u4e00\u4ef6\u4e8b\uff0c\u4fbf\u662f\u59d3\u718a\u7684\u8fd9 \u5c0f\u5b69\u5b50\uff0c\u6211\u770b\u7740\u751a\u662f\u6b22\u559c\uff0c\u6211\u8001\u5934\u5b50\u60f3\u5e26\u4ed6\u53bb\u505a\u5f92\u5f1f\uff0c\u5f53\u7136\u4ed6\u5c31\u4e0d\u80fd\u52a0\u5165\u4f60\u4eec\u7684\u6559\u4e86\u3002\u7b2c\u4e8c \u4ef6\u4e8b\uff0c\u6211\u8001\u5934\u5b50\u867d\u5f97\u6536\u5f92\u5f1f\uff0c\u7b2c\u4e00\u6b21\u6536\u5f92\u5f1f\uff0c\u603b\u8981\u7ed9\u89c1\u9762\u793c\uff0c\u60f3\u6765\u60f3\u53bb\uff0c\u8fd9\u4e2a\u6210\u5f62\u9996\u4e4c\u5012\u6ee1 \u5bf9\u6211\u7684\u80c3\u53e3\uff0c\u4f60\u5c31\u9001\u7ed9\u6211\u5427\u3002\u201d \u3000\u3000\u7126\u5f02\u884c\u9762\u6709\u96be\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u7b2c\u4e00\u4ef6\u4e8b\u5f53\u7136\u6ca1\u6709\u95ee\u9898\uff0c\u53ea\u662f\u8fd9\u7b2c\u4e8c\u4ef6\u4e8b\u4e48\u2026\u2026\u201d \u3000\u3000\u98d8\u7136\u8001\u4eba\u9053\uff1a\u201c\u600e\u6837\uff1f\u201d \u3000\u3000\u7126\u5f02\u884c\u9053\uff1a\u201c\u65e2\u7136\u8001\u524d\u8f88\u5f00\u53e3\uff0c\u6b64\u7269\u5c31\u5728\u6b64\u5904\uff0c\u8001\u524d\u8f88\u53ea\u7ba1\u53d6\u53bb\u4fbf\u662f\u3002\u201d \u3000\u3000\u718a\u501c\u8d70\u5230\u8001\u4eba\u9762\u524d\uff0c\u606d\u606d\u656c\u656c\u5730\u8dea\u4e86\u4e0b\u6765\uff0c\u8001\u4eba\u7b11\u9053\uff1a\u201c\u4f60\u6211\u603b\u662f\u6709\u7f18\uff0c\u8d77\u6765\uff0c\u8d77\u6765\uff0c \u628a\u90a3\u5323\u5b50\u62ff\u6765\uff0c\u6211\u4eec\u5c31\u8981\u8d70\u4e86\u3002\u201d \u3000\u3000\u62ff\u94c1\u9762\u9ec4\u886b\u5ba2\u59cb\u7ec8\u5bd2\u7740\u8138\u7ad9\u5728\u65c1\u8fb9\uff0c\u6b64\u523b\u7a81\u9053\uff1a\u201c\u6162\u6765\uff0c\u522b\u7684\u90fd\u65e0\u6240\u8c13\uff0c\u8fd9\u6210\u5f62\u9996\u4e4c\u5374 \u52a8\u4e0d\u5f97\u3002\u201d \u3000\u3000\u98d8\u7136\u8001\u4eba\u659c\u7768\u4e86\u4ed6\u4e00\u773c\uff0c\u8bf4\u9053\uff0c\u201c\u4f60\u8fd8\u6ca1\u6709\u6b7b\u5440\uff0c\u4e0d\u9519\uff0c\u4e0d\u9519\uff0c\u53ea\u662f\u4f60\u5374\u8fd8\u4e0d\u914d\u6765\u7ba1\u6211 \u7684\u4e8b\u3002\u201d \u3000\u3000\u4ec7\u4e0d\u53ef\u6012\u559d\u9053\uff1a\u201c\u6211\u7ba1\u5b9a\u4e86\u3002\u201d\u8eab\u4f53\u4e5f\u672a\u4f5c\u52bf\uff0c\u500f\u5730\u62d4\u4e86\u8d77\u6765\uff0c\u865a\u7a7a\u4e00\u638c\uff0c\u5411\u98d8\u7136\u8001\u4eba \u51fb\u53bb\u3002 \u3000\u3000\u8001\u4eba\u888d\u8896\u4e00\u5c55\uff0c\u4f17\u4eba\u53ea\u542c\u8f70\u7136\u4e00\u201c\u9635\u5927\u54cd\uff0c\u4ec7\u4e0d\u53ef\u5df2\u9707\u843d\u5730\u4e0a\u3002\u718a\u501c\u5df2\u5c06\u6210\u5f62\u9996\u4e4c\u53d6\u5230 \u624b\u4e2d\uff0c\u8001\u4eba\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5404\u4f4d\uff0c\u6211\u4eec\u544a\u8f9e\u4e86\u3002\u201d\u5de6\u624b\u7275\u7740\u718a\u501c\uff0c\u53f3\u624b\u888d\u8896\u4e00\u5c55\uff0c\u547c\u5730\u4e00\u58f0\u98ce \u54cd\uff0c\u4eba\u5df2\u81ea\u4f17\u4eba\u9876\u4e0a\u98d8\u7136\u800c\u53bb\u3002 \u3000\u3000\u6b63\u662f\u6cf0\u5c71\u7edd\u9876\uff0c\u5947\u4eba\u500d\u51fa\uff0c\u5929\u5916\u6709\u5929\uff0c\u4eba\u4e0a\u6709\u4eba\u3002 \u7b2c\u4e94\u7ae0\u3000\u518d\u5165\u6c5f\u6e56>> \u53e4\u9f99\u300a\u82cd\u7a79\u795e\u5251\u300b \u7b2c\u4e94\u7ae0\u3000\u518d\u5165\u6c5f\u6e56 \u3000\u3000\u56db\u5e74\uff0c\u597d\u50cf\u5728\u4e00\u6643\u773c\u95f4\u5c31\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u718a\u501c\u8ddf\u7740\u98d8\u7136\u8001\u4eba\uff0c\u9690\u5c45\u5728\u6cf0\u5c71\uff0c\u5df2\u7ecf\u82e6\u7ec3\u4e86\u56db\u5e74\u7684\u6b66\u529f\u4e86\u3002 \u3000\u3000\u56db\u5e74\uff0c\u6c5f\u6e56\u4e0a\u8d77\u4e86\u5f88\u5927\u7684\u53d8\u5316\u3002 \u3000\u3000\u6c5f\u5357\u7b2c\u4e00\u7684\u6c5f\u5b81\u5e9c\u9e23\u8fdc\u9556\u5c40\u74e6\u89e3\u4e86\uff0c\u91d1\u9675\u4e09\u6770\u4e2d\u7684\u65ad\u9b42\u5251\u4e0e\u795e\u5200\u9738\u738b\u5df2\u4e0d\u77e5\u53bb\u5411\u3002 \u3000\u3000\u5ce8\u5d4b\u7684\u5b64\u5cf0\u4e00\u5251\u8fb9\u6d69\uff0c\u81ea\u5ce8\u5e3d\u7edd\u9876\uff0c\u5de7\u5f97\u5931\u4f20\u5df2\u4e45\u7684\u201c\u7384\u5973\u5251\u6cd5\u201d\u79d8\u7b08\uff0c\u6210\u4e86\u6c5f\u6e56\u4e0a\u6570 \u4e00\u6570\u4e8c\u7684\u5251\u5ba2\uff0c\u548c\u6c5f\u82cf\u864e\u90b1\u98de\u7075\u5821\u7684\u51fa\u5c18\u5251\u5ba2\u4e1c\u65b9\u7075\uff0c\u88ab\u6b66\u6797\u4e2d\u5e76\u79f0\u201c\u53cc\u7edd\u5251\u201d\u3002 \u3000\u3000\u7c89\u8776\u4e1c\u65b9\u745b\uff0c\u591a\u6b21\u62d2\u7edd\u8bb8\u591a\u5e74\u8f7b\u8c6a\u6770\u7684\u5a5a\u8bae\uff0c\u4e0d\u77e5\u5979\u5728\u7b49\u5f85\u4ec0\u4e48\u3002 \u3000\u3000\u897f\u6cb3\u7eff\u6797\u9053\u7684\u603b\u74e2\u628a\u5b50\uff0c\u7b11\u9762\u4eba\u5c60\u7533\u4e00\u5e73\u5ffd\u7136\u4e2d\u6bd2\u800c\u6b7b\uff0c\u6cb3\u5317\u7eff\u6797\u9053\u7fa4\u9f99\u65e0\u9996\uff0c\u767b\u65f6\u5927 \u4e71\uff0c\u4e00\u4e2a\u540d\u53eb\u94c1\u80c6\u5c1a\u672a\u660e\u7684\u9752\u5e74\u8c6a\u5ba2\uff0c\u5728\u4e24\u6cb3\u7eff\u6797\u5927\u4f1a\u4e0a\uff0c\u6280\u538b\u5f53\u573a\uff0c\u53d6\u4ee3\u4e86\u7533\u4e00\u5e73\u751f\u524d\u7684 \u4f4d\u7f6e\u3002 \u3000\u3000\u767d\u5c71\u9ed1\u6c34\u4e4b\u95f4\uff0c\u51fa\u4e86\u4e2a\u8d29\u9a6c\u5927\u8c6a\uff0c\u4ed6\u7684\u201c\u843d\u65e5\u9a6c\u573a\u201d\u5360\u5730\u5343\u9877\uff0c\u6b64\u4eba\u522b\u4eba\u53ea\u79f0\u4ed6\u4e3a\u201c\u866c \u987b\u5ba2\u201d\uff0c\u4e0d\u77e5\u6765\u5386\u59d3\u540d\uff0c\u4ed6\u6709\u4e2a\u5973\u513f\uff0c\u53eb\u505a\u201c\u96ea\u5730\u98d8\u98ce\u590f\u82b8\u201d\uff0c\u66f4\u662f\u4e1c\u4e09\u7701\u65b0\u8fd1\u5d1b\u8d77\u7684\u6210\u540d \u5973\u4fa0\u3002 \u3000\u3000\u5317\u4eac\u8457\u540d\u7684\u8001\u9556\u5934\uff0c\u94f6\u94a9\u5b5f\u4ef2\u8d85\uff0c\u5728\u8d70\u9556\u5c71\u897f\u7684\u65f6\u5019\uff0c\u5f97\u7f6a\u4e86\u5929\u9634\u6559\uff0c\u88ab\u5929\u9634\u6559\u65b0\u624e\u8d77 \u7684\u9f99\u987b\u575b\u4e3b\u5355\u638c\u8ffd\u9b42\u5355\u98de\uff0c\u4e00\u638c\u51fb\u65ad\u53cc\u817f\uff0c\u4ea1\u547d\u5929\u6daf\u4e0d\u660e\u4e0b\u843d\u3002 \u3000\u3000\u6700\u4ee4\u6c5f\u6e56\u4eba\u8c08\u4e4b\u53d8\u8272\u7684\u662f\uff0c\u65e0\u9634\u6559\u7684\u52bf\u529b\u65e5\u76ca\u5e9e\u5927\uff0c\u5929\u9634\u6559\u5f92\u5145\u65a5\u6c5f\u6e56\uff0c\u9ed1\u767d\u4e24\u9053\uff0c\u90fd \u6709\u4ed6\u4eec\u7684\u52bf\u529b\uff0c\u6c5f\u6e56\u4e2d\u8f83\u6709\u540d\u6c14\u7684\u597d\u6c49\uff0c\u5982\u4e03\u6bd2\u4e66\u751f\u5510\u7fbd\uff0c\u91d1\u9675\u4e09\u6770\u4e4b\u7c89\u9762\u82cf\u79e6\u738b\u667a\u9011\uff0c\u6d77 \u4e0a\u79f0\u5c0a\u7684\u6d77\u9f99\u738b\u8d75\u4f69\u4fa0\uff0c\u5c71\u897f\u4e34\u6c7e\u7684\u5434\u94a7\u5251\u9f9a\u5929\u6770\uff0c\u6d1b\u9633\u5927\u8c6a\u4e94\u864e\u65ad\u95e8\u5200\u5f6d\u5929\u5bff\uff0c\u4ee5\u53ca\u52b3\u5c71 \u53cc\u9e64\uff0c\u6d1e\u5ead\u56db\u868a\uff0c\u9ec4\u6cb3\u4e00\u602a\uff0c\u548c\u4e00\u4e9b\u6b66\u6797\u4e2d\u4e45\u5df2\u5f52\u9690\u7684\u9b54\u5934\uff0c\u90fd\u88ab\u6536\u7f57\u6559\u4e0b\uff0c\u4e0d\u662f\u771f\u6709\u7edd\u5927 \u6765\u5934\u7684\u6b66\u6797\u4eba\u7269\uff0c\u6839\u672c\u65e0\u6cd5\u5728\u6c5f\u6e56\u7acb\u8db3\u3002 \u3000\u3000\u79cb\u5929\uff0c\u5f53\u718a\u501c\u91cd\u56de\u79e6\u6dee\u6cb3\u7684\u65f6\u5019\uff0c\u4eba\u4e8b\u5df2\u7136\u5168\u975e\u3002 \u3000\u3000\u6731\u82e5\u99a8\u65e9\u5c31\u53d7\u4e0d\u4e86\u70df\u82b1\u5ba2\u7684\u6467\u6b8b\uff0c\u81ea\u6740\u800c\u6b7b\u3002\u7559\u4e0b\u6731\u82e5\u5170\u4f36\u4ec3\u4e00\u4eba\uff0c\u4f9d\u7136\u5728\u5fcd\u53d7\u751f\u547d\u7684 \u82e6\u695a\u3002 \u3000\u3000\u718a\u501c\u60f3\u8d77\u51fa\u5c18\u5251\u5ba2\u4e1c\u65b9\u7075\uff0c\u662f\u4e2a\u4ed7\u4e49\u758f\u8d22\u7684\u4eba\uff0c\u4fbf\u60f3\u5230\u628a\u82e5\u5170\u6551\u51fa\u82e6\u6d77\uff0c\u5bc4\u6258\u7ed9\u4e1c\u65b9 \u7075\uff0c\u7136\u540e\u518d\u8d70\u904d\u5929\u6daf\uff0c\u4e86\u5374\u81ea\u5df1\u7684\u6069\u4ec7\u3002 \u3000\u3000\u56e0\u6b64\uff0c\u4ed6\u540c\u82e5\u5170\u5546\u91cf\u597d\uff0c\u8981\u82e5\u5170\u6536\u62fe\u4e9b\u7ec6\u8f6f\uff0c\u96c7\u8f66\u4e70\u9a6c\uff0c\u76f4\u5f80\u82cf\u5dde\u864e\u90b1\u5954\u53bb\u3002 \u3000\u3000\u864e\u90b1\u5c71\u672c\u662f\u82cf\u5dde\u7684\u540d\u80dc\uff0c\u6797\u6728\u8471\u7b3c\uff0c\u666f\u8272\u751a\u7f8e\uff0c\u90a3\u98de\u7075\u5821\u5c31\u5728\u864e\u90b1\u5c71\u4e0b\uff0c\u4f9d\u5c71\u508d\u6c34\uff0c\u5efa \u7740\u4e00\u5927\u7247\u9662\u843d\uff0c\u5916\u9762\u5efa\u7740\u56f4\u5899\uff0c\u4e09\u4e94\u58ee\u4e01\u3001\u6b64\u523b\u6b63\u7ad9\u5728\u5821\u95e8\u5916\uff0c\u770b\u89c1\u6709\u8f66\u6765\u4e86\uff0c\u4fbf\u8fce\u4e86\u4e0a \u6765\u3002 \u3000\u3000\u718a\u501c\u7b56\u9a6c\u8d70\u4e0a\u53bb\uff0c\u90a3\u58ee\u4e01\u8eac\u8eab\u9053\uff1a\u201c\u8fd9\u4f4d\u53ef\u662f\u6765\u82f1\u96c4\u4f1a\u7684\u3002\u201d \u3000\u3000\u718a\u501c\u7ffb\u8eab\u4e0b\u4e86\u9a6c\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u662f\u7684\uff0c\u6211\u7279\u6765\u6c42\u89c1\u5821\u4e3b\uff0c\u9ebb\u70e6\u4f60\u5165\u5185\u901a\u62a5\uff0c\u5c31\u8bf4\u6c5f\u5b81\u718a \u501c\uff0c\u8fdc\u9053\u6c42\u89c1\u5821\u4e3b\u3002\u201d \u3000\u3000\u90a3\u58ee\u4e01\u8d70\u4e86\u8fdb\u53bb\uff0c\u7247\u523b\uff0c\u4e00\u4e2a\u957f\u886b\u6c49\u5b50\u98de\u6b65\u800c\u51fa\uff0c\u8001\u8fdc\u4fbf\u62b1\u7740\u62f3\u8bf4\u9053\uff0c\u201c\u6765\u7684\u53ef\u662f\u6c5f\u5b81 \u5e9c\u7684\u718a\u501c\u5927\u4fa0\uff0c\u5feb\u8bf7\u5148\u8fdb\u53bb\uff0c\u5821\u4e3b\u5c31\u6765\u606d\u8fce\u5927\u9a7e\u3002\u201d \u3000\u3000\u987b\u77e5\u718a\u501c\u540d\u9707\u6c5f\u5b81\uff0c\u6cf0\u5c71\u4e00\u4f1a\u540e\uff0c\u66f4\u662f\u6b66\u6797\u4e2d\u5927\u5927\u6709\u540d\u7684\u4eba\u7269\uff0c\u90a3\u957f\u886b\u6c49\u5b50\u4e43\u662f\u98de\u7075\u5821 \u91cc\u7684\u7ba1\u4e8b\uff0c\u542c\u5f97\u718a\u501c\u6765\u4e86\uff0c\u8fde\u5fd9\u8fce\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8fc7\u4e0d\u4e00\u4f1a\u513f\uff0c\u51fa\u5c18\u5251\u5ba2\u4e1c\u65b9\u7075\u5e26\u7740\u51e0\u4e2a\u58ee\u4e01\u5927\u6b65\u800c\u51fa\uff0c\u89c1\u4e86\u718a\u501c\u5927\u7b11\u9053\uff1a\u201c\u4eca\u5929\u662f\u54ea\u9635 \u98ce\u628a\u5927\u9a7e\u5439\u6765\u4e86\uff0c\u60f3\u5f97\u5c0f\u5f1f\u597d\u82e6\u5440\u3002\u201d \u3000\u3000\u201c\u718a\u501c\u4e5f\u5fd9\u62f1\u624b\u4e3a\u793c\uff0c\u8bf4\u9053\uff0c\u201d\u4e45\u8fdd\u5821\u4e3b\u98ce\u8303\uff0c\u5c0f\u5f1f\u4e5f\u662f\u60f3\u5ff5\u5f97\u5f88\uff0c\u4e45\u60f3\u524d\u6765\u95ee\u5019\uff0c\u5374 \u82e6\u4e0d\u5f97\u4fbf\uff0c\u4eca\u756a\u60ed\u6127\uff0c\u5374\u662f\u6709\u4e8b\u8981\u76f8\u70e6\u5821\u4e3b\u4e86\u3002\u201c\u4e1c\u65b9\u7075\u63e1\u7740\u718a\u501c\u7684\u624b\u9053\uff1a\u201c\u5feb\u4e0d\u8981\u8bf4\u5ba2\u6c14 \u7684\u8bdd\uff0c\u8fd9\u6837\u8bf4\u4e0d\u514d\u89c1\u5916\u4e86\uff0c\u4f60\u6765\u5f97\u5230\u771f\u662f\u51d1\u5de7\uff0c\u6c5f\u5357\u7684\u8c6a\u6770\uff0c\u5dee\u4e0d\u591a\u5df2\u90fd\u5728\u6211\u5821\u4e2d\u4e86\u3002\u201d\u8bf4 \u5b8c\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u53c8\u770b\u4e86\u90a3\u8f66\u5b50\u4e00\u773c\uff0c\u7591\u60d1\u4ed6\u8bf4\u9053\uff1a\u201c\u5feb\u8bf7\u8fdb\u53bb\u8bf4\u8bdd\uff0c\u90a3\u8f66\u4e2d\u7684\u53ef\u662f\u5b9d\u7737\uff1f\u201d \u3000\u3000\u718a\u501c\u9053\uff1a\u201c\u8f66\u4e2d\u662f\u5c0f\u5f1f\u5bb6\u59d0\uff0c\u5c0f\u5f1f\u6d6a\u8ff9\u65e0\u5b9a\uff0c\u4e0d\u80fd\u7167\u987e\u5bb6\u59d0\uff0c\u5ffd\u7136\u60f3\u8d77\u5821\u4e3b\u9ad8\u4e49\uff0c\u6545\u6b64 \u4e0d\u5acc\u5192\u6627\uff0c\u60f3\u5c06\u5bb6\u59d0\u5bc4\u5c45\u5728\u6b64\uff0c\u5bb6\u59d0\u82e5\u80fd\u5f97\u5230\u5821\u4e3b\u7167\u987e\uff0c\u5c0f\u5f1f\u5c31\u53ef\u653e\u5fc3\u4e86\u3002\u201d \u3000\u3000\u4e1c\u65b9\u7075\u7591\u60d1\u987f\u89e3\uff0c\u5fd9\u8bf4\u9053\uff1a\u201c\u539f\u6765\u662f\u4ee4\u59d0\uff0c\u5feb\u8bf7\u8fdb\u53bb\uff0c\u4ee4\u59d0\u4e0d\u5c31\u7b49\u4e8e\u5c0f\u5f1f\u7684\u59d0\u59d0\u4e00\u6837\uff0c \u8fd9\u662f\u5c0f\u4e8b\uff0c\u5343\u4e07\u4e0d\u8981\u653e\u5728\u5fc3\u4e0a\u3002\u201d \u3000\u3000\u8bf4\u7740\u5c31\u53eb\u58ee\u4e01\u5c06\u8f66\u5b50\u8fce\u8fdb\u5821\u53bb\u3002 \u3000\u3000\u718a\u501c\u4e0e\u4e1c\u65b9\u7075\u8fdb\u5f97\u5821\u6765\uff0c\u53ea\u89c1\u623f\u5b87\u6809\u6bd4\uff0c\u6c14\u6d3e\u751a\u5927\u3002 \u3000\u3000\u8f6c\u8fc7\u4e24\u6392\u623f\u5b50\uff0c\u662f\u4e2a\u6781\u5927\u6781\u5927\u7684\u5e7f\u573a\uff0c\u6b64\u523b\u56db\u65c1\u4ff1\u7528\u5de8\u7af9\u642d\u8d77\u68da\u5b50\uff0c\u6b63\u4e2d\u662f\u4e00\u4e2a\u5927\u53f0\uff0c \u56db\u5468\u56f4\u4ee5\u680f\u6746\uff0c\u8fd9\u65f6\u68da\u91cc\u9ad8\u670b\u6ee1\u5750\uff0c\u4ff1\u662f\u8c6a\u58eb\u3002 \u3000\u3000\u718a\u501c\u8fdc\u8fdc\u5730\u770b\u89c1\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u91cc\u770b\u6765\uff0c\u60f3\u5fc5\u5c31\u662f\u5821\u4e3b\u7684\u82f1\u96c4\u5927\u4f1a\uff0c\u5c0f\u5f1f\u5728\u9053\u8def\u4e0a\u5df2\u542c \u4eba\u8bf4\u8fc7\uff0c\u53ea\u662f\u5c0f\u5f1f\u5374\u4e0d\u60f3\u8fdb\u53bb\uff0c\u4e0d\u77e5\u5821\u4e3b\u53ef\u5426\u5148\u5e26\u5c0f\u5f1f\u4eba\u5185\uff0c\u5b89\u987f\u5bb6\u59d0\u518d\u8bf4\u3002\u201d \u3000\u3000\u4e1c\u65b9\u7075\u8bf4\u9053\uff1a\u201c\u90a3\u4e2a\u81ea\u7136\uff0c\u6211\u5148\u5e26\u718a\u5144\u5230\u655d\u820d\u53bb\uff0c\u820d\u59b9\u5bf9\u718a\u5144\uff0c\u4e5f\u662f\u60f3\u5ff5\u5f97\u5f88\u5462\uff01\u201d\u7b11 \u4e86\u51e0\u58f0\uff0c\u53c8\u8bf4\u9053\uff1a\u201c\u53ea\u662f\u8fd9\u4e2a\u82f1\u96c4\u5927\u4f1a\uff0c\u718a\u5144\u4e00\u5b9a\u8981\u53c2\u52a0\u7684\uff0c\u6c5f\u6e56\u670b\u53cb\uff0c\u8c01\u4e0d\u5e0c\u671b\u80fd\u4e00\u89c1\u9601 \u4e0b\u7684\u98ce\u91c7\u54621\u201d\u718a\u501c\u542c\u4e86\uff0c\u4e5f\u89c9\u5f97\u6709\u4e9b\u5f97\u610f\uff0c\u5374\u4e0d\u597d\u7b54\u8bdd\u3002 \u3000\u3000\u4e1c\u65b9\u7075\u5e26\u7740\u4ed6\u4e09\u8f6c\u4e24\u8f6c\uff0c\u8d70\u5230\u4e00\u4e2a\u95e8\u524d\uff0c\u6307\u7740\u8bf4\uff1a\u201c\u8fd9\u5c31\u662f\u5bd2\u820d\u4e86\u3002\u201d \u3000\u3000\u201c\u718a\u501c\u8ddf\u7740\u4ed6\u8d70\u4e86\u8fdb\u53bb\uff0c\u53ea\u89c1\u90a3\u662f\u4e2a\u6781\u5927\u7684\u82b1\u56ed\uff0c\u524d\u9762\u662f\u4e09\u95f4\u5012\u8f69\uff0c\u88ab\u6811\u5f71\u906e\u5f97\u6697\u5c42\u5c42 \u7684\uff0c\u6cbf\u5899\u7684\u5047\u5c71\u77f3\uff0c\u79cd\u7740\u5404\u5f0f\u7684\u82b1\u6728\u3001\u53ea\u662f\u5df2\u8fdb\u6df1\u79cb\uff0c\u53ea\u6709\u83ca\u82b1\uff0c\u4ecd\u7136\u5728\u76db\u5f00\u7740\uff0c\u88ab\u659c\u9633\u7167 \u5f97\u4e00\u7247\u91d1\u9ec4\u3002\u4e1c\u65b9\u7075\u53c8\u6307\u7740\u90a3\u4e09\u95f4\u5012\u8f69\u8bf4\uff1a\u201c\u8fd9\u662f\u5c0f\u5f1f\u590f\u65e5\u8bfb\u4e66\u7684\u6240\u5728\uff0c\u6b63\u5385\u8fd8\u5728\u524d\u9762 \u5462\u3002\u201d \u3000\u3000\u8f6c\u8fc7\u5012\u8f69\uff0c\u5ffd\u89c1\u5341\u4ea9\u8377\u6c60\uff0c\u867d\u7136\u8377\u82b1\u5168\u90e8\u8c22\u4e86\uff0c\u671b\u53bb\u4eff\u4f5b\u4ecd\u6709\u7f15\u7f15\u6e05\u9999\u3002 \u3000\u3000\u8377\u6c60\u65c1\u67b6\u7740\u91cd\u53e0\u56de\u5eca\uff0c\u662f\u5ea7\u6781\u7cbe\u81f4\u53c8\u5bbd\u655e\u7684\u5c4b\u5b50\uff0c\u88ab\u4e00\u5ea7\u5927\u5047\u5c71\u5411\u897f\u6321\u4f4f\uff0c\u5047\u5c71\u4e0a\u68a7\u3001 \u6986\u76f8\u63a5\uff0c\u66ff\u623f\u5b50\u6321\u4f4f\u4e86\u897f\u6652\u7684\u9633\u5149\u3002 \u3000\u3000\u718a\u501c\u548c\u4e1c\u65b9\u7075\u8d70\u8fdb\u623f\u91cc\uff0c\u89c1\u4e1c\u65b9\u745b\u6b63\u966a\u7740\u6731\u82e5\u5170\u5750\u5728\u5385\u91cc\u8bf4\u8bdd\u5462\u3002 \u3000\u3000\u4e1c\u65b9\u745b\u7ea2\u7740\u8138\u5bf9\u718a\u501c\u7b11\u4e86\u4e00\u4e0b\uff0c\u5c31\u62c9\u8d77\u82e5\u5170\u6765\uff0c\u5bf9\u4e1c\u65b9\u7075\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u5c31\u662f\u6211\u54e5 \u54e5\u2026\u2026\u201d \u3000\u3000\u6731\u82e5\u5170\u7ea2\u7740\u8138\u798f\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4e1c\u65b9\u7075\u4e5f\u8eac\u8eab\u8bf4\u9053\uff1a\u201c\u718a\u2026\u2026\u201d \u3000\u3000\u4ed6\u7adf\u4e0d\u5378\u8be5\u600e\u4e48\u79f0\u547c\u624d\u597d\uff0c\u8bf4\u4e86\u4e2a\u718a\u5b57\uff0c\u5c31\u63a5\u4e0d\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u718a\u501c\u5fd9\u7b11\u9053\uff1a\u201c\u6b64\u662f\u5c0f\u5f1f\u7684\u4e49\u59d0\uff0c\u59d3\u6731\uff0c\u5374\u662f\u4ece\u5c0f\u5e26\u7740\u5c0f\u5f1f\u957f\u5927\u7684\u3002\u201d \u3000\u3000\u4e1c\u65b9\u7075\u5c34\u5c2c\u5730\u7b11\u9053\uff1a\u201c\u6731\u59d1\u5a18\u5343\u4e07\u4e0d\u8981\u5ba2\u6c14\uff0c\u718a\u5144\u548c\u6211\u4e0d\u662f\u5916\u4eba\uff0c\u6731\u59d1\u5a18\u5728\u6b64\uff0c\u5c31\u8bf7\u50cf \u5728\u5bb6\u91cc\u4e00\u6837\u597d\u4e86\u3002\u201d \u3000\u3000\u718a\u501c\u8bf4\u9053\uff1a\u201c\u5821\u4e3b\u7684\u9ad8\u4e49\uff0c\u5c0f\u5f1f\u4e5f\u66fe\u548c\u5bb6\u59d0\u8bf4\u8fc7\uff0c\u5bb6\u59d0\u4e5f\u656c\u4f69\u7684\u4e0d\u5f97\u4e86\uff0c\u662f\u4ee5\u5c0f\u5f1f\u624d\u4e0d \u5acc\u5192\u6627\u5730\u8dd1\u6765\u4e86\u3002\u201d \u3000\u3000\u4e1c\u65b9\u745b\u5a07\u7b11\u7740\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u522b\u5821\u4e3b\uff0c\u5c0f\u5f1f\uff0c\u718a\u5144\u5730\u79f0\u547c\u7740\u597d\u5427\uff0c\u542c\u5f97\u4eba\u602a\u4e0d\u8212\u670d\u7684\u3002\u201d \u3000\u3000\u4e1c\u65b9\u7075\u7b11\u9053\uff1a\u201c\u6b63\u662f\u5e94\u8be5\u5982\u6b64\uff0c\u6211\u4eec\u8fd8\u662f\u514d\u4e86\u8fd9\u4e9b\u865a\u5957\u6700\u597d\u3002\u201d \u3000\u3000\u6b64\u523b\u5ffd\u6709\u4e00\u4e2a\u5c0f\u7ae5\u8fc7\u6765\u8bf4\u9053\uff1a\u201c\u5916\u9762\u6709\u4e2a\u58ee\u4e01\uff0c\u8fdb\u6765\u8bf4\u82f1\u96c4\u4f1a\u4e0a\u7684\u82f1\u96c4\u4eec\u90fd\u7b49\u6025\u4e86\uff0c\u95ee \u5821\u4e3b\u600e\u4e48\u8fd8\u4e0d\u53bb\u5462\u3002\u201d \u3000\u3000\u4e1c\u65b9\u7075\u7b11\u9053\uff1a\u201c\u6211\u53ea\u7ba1\u7740\u548c\u4f60\u4eec\u8bf4\u8bdd\uff0c\u5374\u628a\u5916\u9762\u7684\u5ba2\u4eba\u90fd\u5fd8\u4e86\u3002\u201d \u3000\u3000\u4e1c\u65b9\u745b\u5a07\u7b11\u9053\uff1a\u201c\u8ba9\u4ed6\u4eec\u7b49\u7b49\u597d\u4e86\u3002\u201d \u3000\u3000\u718a\u501c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u81ea\u53bb\u65e0\u59a8\uff0c\u6211\u966a\u5bb6\u59d0\u5728\u8fd9\u91cc\u5750\u597d\u4e86\u3002\u201d \u3000\u3000\u4e1c\u65b9\u7075\u9053\uff1a\u201c\u8d24\u5f1f\u5374\u662f\u4e00\u5b9a\u4e5f\u8981\u53bb\u7684\uff0c\u6731\u59d1\u5a18\u82e5\u662f\u6709\u5174\uff0c\u80fd\u4e00\u8d77\u53bb\u66f4\u597d\u3002\u201d \u3000\u3000\u82e5\u5170\u521a\u60f3\u63a8\u8f9e\uff0c\u4e1c\u65b9\u745b\u5374\u4e00\u628a\u62c9\u4f4f\u5979\u8bf4\uff1a\u201c\u4e00\u9f50\u53bb\u770b\u770b\u6709\u4ec0\u4e48\u5173\u7cfb\uff0c\u6211\u966a\u7740\u4f60\u5c31\u662f \u4e86\u3002\u201d \u3000\u3000\u5e7f\u573a\u91cc\u7684\u7af9\u68da\u5206\u56db\u9762\u642d\u8d77\uff0c\u751a\u4e3a\u5bbd\u655e\uff0c\u6bcf\u4e00\u4e2a\u68da\u91cc\u6446\u7740\u5341\u4f59\u684c\u9152\u7b75\uff0c\u53ea\u8981\u6709\u4eba\u5750\u7740\uff0c\u4fbf \u7acb\u5373\u6446\u8ba9\u9152\u83dc\uff0c\u6b64\u523b\u4e09\u95ee\u655e\u68da\uff0c\u90fd\u51e0\u8fd1\u5750\u6ee1\u4e86\u3002 \u3000\u3000\u6b63\u4e2d\u671d\u5916\u7684\u90a3\u4e00\u68da\uff0c\u662f\u7559\u505a\u4e3b\u5ea7\uff0c\u548c\u62db\u5f85\u4e9b\u8f83\u4e3a\u77e5\u540d\u4e4b\u58eb\uff0c\u6b64\u523b\u5374\u53ea\u758f\u843d\u5730\u5750\u4e86\u51e0\u4e2a \u4eba\uff0c\u5176\u4e2d\u6709\u6b66\u5f53\u7684\u56db\u4eea\u5251\u5ba2\u51cc\u4f1a\u5b50\uff0c\u4e39\u9633\u5b50\uff0c\u7384\u673a\u5b50\uff0c\u98d8\u5c18\u5b50\uff0c\u6b66\u6797\u4e2d\u79f0\u4e4b\u4e3a\u6b66\u5f53\u56db\u5b50\uff0c\u6b64 \u56db\u4eba\uff0c\u884c\u4fa0\u6c5f\u6e56\uff0c\u751a\u662f\u6b63\u6d3e\uff0c\u6b64\u5916\u5c1a\u6709\u592a\u6e56\u4e09\u5341\u516d\u8235\u7684\u603b\u8235\u4e3b\u5c55\u7fc5\u91d1\u9e4f\u4e0a\u5b98\u4e88\uff0c\u56db\u5ddd\u5ce8\u5d4b\u5b64 \u5cf0\u4e00\u5251\u8fb9\u6d69\u7684\u4e24\u4e2a\u5e08\u59b9\uff0c\u5ce8\u5d4b\u53cc\u5c0f\u5f90\u5c0f\u5170\uff0c\u8c37\u5c0f\u9759\uff0c\u4f46\u5b64\u5cf0\u4e00\u5251\uff0c\u5929\u5c71\u4e09\u9f99\u5374\u672a\u89c1\u6765\u5230\u3002 \u3000\u3000\u4e1c\u65b9\u7075\u5411\u56db\u5468\u62b1\u62f3\u9053\uff1a\u201c\u5c0f\u5f1f\u8fd9\u6b21\u8bf7\u5404\u4f4d\u6765\uff0c\u5b9e\u5728\u4e5f\u6ca1\u6709\u4ec0\u4e48\u4e8b\uff0c\u53ea\u662f\u5c0f\u5f1f\u60f3\u7740\u4e0e\u6c5f\u5357 \u8bf8\u4fa0\uff0c\u8fd1\u65e5\u751a\u5c11\u8054\u7edc\uff0c\u7279\u5730\u8bf7\u5404\u4f4d\u6765\u805a\u4e00\u805a\u3002\u60f3\u4e0d\u5230\u7684\u662f\uff0c\u5c45\u7136\u60ca\u52a8\u4e86\u6b66\u5f53\uff0c\u5ce8\u5d4b\u4e24\u6d3e\u7684\u5251 \u5ba2\uff0c\u548c\u592a\u6e56\u7684\u603b\u8235\u4e3b\u4e0a\u5b98\u8001\u82f1\u96c4\uff0c\u5c0f\u5f1f\u65e2\u662f\u9ad8\u5174\uff0c\u53c8\u662f\u60f6\u6050\u3002\uff0c\u201d\u6b64\u5916\uff0c\u8fd8\u6709\u4e00\u4f4d\u5927\u5927\u6709\u540d \u7684\u82f1\u96c4\uff0c\u60f3\u4e0d\u5230\u4ed6\u4e5f\u5de7\u9002\u9022\u6b64\u4f1a\uff0c\u90a3\u5c31\u662f\u6614\u5e74\u6cf0\u5c71\u7edd\u9876\uff0c\u7fa4\u82f1\u5927\u4f1a\u4e0a\u72ec\u6297\u5929\u9634\u6559\uff0c\u540d\u4f20\u6c5f\u6e56 \u7684\u661f\u6708\u53cc\u5251\u548c\u98d8\u7136\u8001\u4eba\u7684\u8863\u94b5\u4f20\u4eba\u718a\u501c\uff0c\u5c0f\u5f1f\u66f4\u662f\u9ad8\u5174\u5f97\u5f88\u3002\u201c\u201d\u6b64\u6b21\u76db\u4f1a\u7fa4\u8c6a\uff0c\u5b9e\u662f\u6211\u98de \u7075\u5821\u5efa\u5821\u4ee5\u6765\uff0c\u6700\u5927\u7684\u5feb\u4e8b\uff0c\u5404\u4f4d\u82e5\u662f\u6709\u5174\uff0c\u4e0d\u59a8\u5728\u6b63\u4e2d\u7684\u82f1\u96c4\u53f0\u4e0a\u8bd5\u8bd5\u8eab\u624b\uff0c\u6587\u4eba\u9a9a\u5ba2 \u4eec\uff0c\u51fb\u9f13\u884c\u4ee4\u4ee5\u52a9\u9152\u5174\uff0c\u6211\u8f88\u6b66\u6797\u4e2d\u4eba\u53ea\u597d\u51fb\u5251\u884c\u62f3\u4e86\u3002\u201c\u201d\u4f46\u6b64\u4f1a\u53ea\u662f\u6b22\u53d9\u4e4b\u4f1a\uff0c\u8fc7\u62db\u4e5f \u662f\u70b9\u5230\u4e3a\u6b62\uff0c\u5404\u4f4d\u4e4b\u4e2d\u82e5\u6709\u4ec0\u4e48\u63ed\u4e0d\u5f00\u7684\u6881\u5b50\uff0c\u5374\u4e0d\u53ef\u5728\u6b64\u715e\u4e86\u5927\u5bb6\u7684\u98ce\u666f\u3002\u201c\u3001\u201d\u5c0f\u5f1f\u8bdd \u5df2\u8bf4\u5b8c\uff0c\u8bf7\u5404\u4f4d\u5c3d\u53ef\u80fd\u6b22\u996e\uff0c\u98de\u7075\u5821\u867d\u65e0\u957f\u7269\uff0c\u4f46\u6c34\u9152\u8fd8\u80fd\u4f9b\u5e94\u5f97\u8d77\u3002\u201c\u56db\u68da\u8bf8\u8c6a\uff0c\u4e00\u9635\u9f13 \u638c\u6b22\u547c\uff0c\u4fbf\u75db\u996e\u8d77\u6765\u3002\u718a\u501c\u5f6c\u5f6c\u6709\u793c\u548c\u6c89\u9ed8\u5be1\u8a00\u7684\u6027\u683c\uff0c\u5f15\u8d77\u6b66\u5f53\u56db\u5b50\u6781\u5927\u7684\u597d\u611f\uff0c\u575a\u6301\u8981 \u718a\u501c\u65e5\u540e\u5230\u6b66\u5f53\u5c71\u53bb\u4e00\u6e38\uff0c\u718a\u501c\u89c1\u80fd\u5f97\u6b66\u5f53\u56db\u5b50\u7684\u9080\u8bf7\uff0c\u4e5f\u662f\u9ad8\u5174\uff0c\u4f55\u51b5\u6b66\u5f53\u6d3e\uff0c\u4e45\u4e3a\u4e2d\u539f \u5185\u5bb6\u5251\u6d3e\u6b63\u5b97\uff0c\u6b66\u5f53\u5c71\u66f4\u662f\u6b66\u6797\u4e2d\u4eba\u4eba\u666f\u4ef0\u7684\u6240\u5728\uff0c\u4fbf\u4e00\u53e3\u7b54\u5e94\u4e86\u3002\u5ce8\u5d4b\u53cc\u5c0f\u5f90\u5c0f\u5170\uff0c\u8c37\u5c0f \u9759\uff0c\u548c\u7c89\u8776\u4e1c\u65b9\u745b\u672c\u662f\u597d\u53cb\uff0c\u8fd9\u6b21\u5979\u4eec\u524d\u6765\u98de\u7075\u5821\uff0c\u4e5f\u662f\u4e1c\u65b9\u745b\u9080\u6765\u7684\u3001\u6b64\u523b\u7b11\u8bdd\u98ce\u751f\uff0c\u5e2d \u4e0a\u53ea\u6709\u5979\u4eec\u8bb2\u8bdd\u7684\u4efd\u513f\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u82f1\u96c4\u53f0\u4e0a\u5c45\u7136\u6709\u51e0\u4e2a\u4eba\u4e0a\u53bb\u6253\u4e86\u4e24\u8d9f\u62f3\uff0c\u7ec3\u4e86\u4e00\u6bb5\u5251\uff0c \u4f46\u4ff1\u90fd\u662f\u4e9b\u666e\u901a\u6b66\u529f\uff0c\u54ea\u80fd\u5165\u5f97\u4e86\u8fd9\u4e9b\u4eba\u7684\u773c\u91cc\u3002\u539f\u6765\u51fa\u5c18\u5251\u5ba2\u4e1c\u65b9\u7075\u6b64\u6b21\u67ec\u9080\u82f1\u96c4\u4f1a\uff0c\u8fd8 \u771f\u4e2a\u662f\u4e3a\u4e86\u4ed6\u7684\u59b9\u59b9\u3002\u4ed6\u867d\u77e5\u9053\u4e1c\u65b9\u745b\u5fc3\u76ee\u4e2d\u6709\u4e86\u718a\u501c\uff0c\u4f46\u718a\u501c\u81ea\u6cf0\u5c71\u5927\u4f1a\u540e\uff0c\u6c5f\u6e56\u4e2d\u4ece\u6b64 \u6ca1\u6709\u6d88\u606f\uff0c\u800c\u81ea\u5df1\u7684\u59b9\u5b50\u7684\u5e74\u9f84\u5374\u4e00\u5929\u5927\u4f3c\u4e00\u5929\uff0c\u6765\u6c42\u5a5a\u7684\u4eba\u3001\u5979\u53c8\u591a\u4e0d\u4e2d\u610f\uff0c\u4ed6\u60f3\u603b\u4e0d\u80fd \u8fd9\u6837\u803d\u8bef\u4e0b\u53bb\u3002\u4ed6\u8fd9\u624d\u805a\u8bf8\u96c4\u4e8e\u98de\u7075\u5821\uff0c\u60f3\u5728\u5176\u4e2d\u7269\u8272\u4e00\u4e2a\u5e74\u5c11\u82f1\u4fca\u7684\u4eba\u7269\uff0c\u6765\u505a\u81ea\u5df1\u7684\u59b9 \u592b\uff0c\u6b64\u523b\u4e00\u770b\uff0c\u5374\u4ff1\u662f\u4e9b\u7b2c\u4e09\u6d41\u7684\u89d2\u8272\u3002\u201d\u4f46\u4ed6\u53cd\u800c\u9ad8\u5174\uff0c\u8fd9\u539f\u56e0\u662f\u718a\u501c\u5c45\u7136\u7a81\u7136\u6765\u4e86\uff0c\u4ed6 \u672c\u662f\u6700\u597d\u7684\u4eba\u9009\uff0c\u81ea\u7136\u4e0d\u5fc5\u518d\u53bb\u6311\u9009\u4e86\uff0c\u53ea\u662f\u718a\u501c\u5fc3\u91cc\u5982\u4f55\uff0c\u4ed6\u5374\u6ca1\u6709\u60f3\u5230\uff0c\u4ed6\u4ee5\u4e3a\u59b9\u59b9\u5141 \u6587\u5141\u6b66\uff0c\u4eba\u53c8\u7f8e\u8c8c\uff0c\u718a\u501c\u5c82\u6709\u4e0d\u80af\u4e4b\u7406\u3002 \u3000\u3000\u6b64\u523b\u82f1\u96c4\u53f0\u4e0a\uff0c\u6709\u4e24\u4e2a\u4eba\u6b63\u5728\u8fc7\u62db\uff0c\u4e00\u4e2a\u4f7f\u7684\u662f\u201c\u5288\u6302\u638c\u201d\uff0c\u4e00\u4e2a\u4f7f\u7684\u662f\u201c\u5c11\u6797\u62f3\u201d\uff0c \u4e00\u62db\u4e00\u5f0f\uff0c\u5012\u4e5f\u6709\u513f\u5206\u529f\u529b\u3002 \u3000\u3000\u4e1c\u65b9\u745b\u5a07\u7b11\u9053\uff1a\u201c\u4f60\u770b\u770b\u8fd9\u4e9b\u4eba\uff0c\u5012\u8fd8\u771f\u4e0a\u53f0\u53bb\u6253\uff0c\u8c37\u59d0\u59d0\uff0c\u5f90\u59d0\u59d0\uff0c\u6211\u4eec\u4e5f\u4e0a\u53bb\u7ec3\u4e00 \u6bb5\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u8c37\u5c0f\u9759\u54ce\u54df\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u53ef\u522b\u627e\u6211\uff0c\u6211\u53ef\u4e0d\u884c\u3002\u4f60\u8981\u771f\u6709\u672c\u4e8b\uff0c\u4e0d\u4f1a\u53bb\u627e\u522b\u4eba \u53bb\uff0c\u600e\u4e48\u5c31\u4f1a\u6b3a\u4fae\u6211\u5440\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u5979\u773c\u775b\u5374\u7785\u7740\u718a\u501c\uff0c\u610f\u601d\u662f\u53eb\u4e1c\u65b9\u745b\u53bb\u627e\u718a\u501c\uff0c\u539f\u6765\u4e1c\u65b9\u745b\u66fe\u7ecf\u5df2\u5c06\u5fc3\u4e8b\u6084\u4fcf\u5730 \u544a\u8bc9\u8fc7\u5979\u4eec\u3002\u4e1c\u65b9\u745b\u7c89\u9762\u7eef\u7ea2\uff1b\u4f38\u624b\u5c31\u8981\u6253\u5979\u3002 \u3000\u3000\u6731\u82e5\u5170\u4e45\u5386\u98ce\u5c18\uff0c\u4ec0\u4e48\u4e0d\u61c2\uff0c\u6b64\u523b\u4e00\u770b\uff0c\u4fbf\u77e5\u9053\u8fd9\u4f4d\u5c0f\u59d0\u5bf9\u718a\u501c\u65e9\u6709\u610f\u601d\uff0c\u5979\u4e5f\u751a\u662f\u559c \u6b22\u4e1c\u65b9\u745b\u7684\u5929\u771f\uff0c\u4fbf\u5e0c\u671b\u718a\u501c\u80fd\u548c\u5979\u7ed3\u5408\u3002 \u3000\u3000\u4e8e\u662f\u6731\u82e5\u5170\u8bf4\u9053\uff1a\u201c\u6211\u8fd9\u4e2a\u5f1f\u5f1f\uff0c\u4ec0\u4e48\u90fd\u597d\uff0c\u5c31\u662f\u5634\u4e25\u5f97\u5389\u5bb3\uff0c\u4ec0\u4e48\u90fd\u4e0d\u80af\u8bf4\uff0c\u6211\u8ddf\u4ed6 \u5728\u4e00\u8d77\u8fd9\u4e48\u4e45\uff0c\u8fde\u4ed6\u4f1a\u6b66\u529f\u90fd\u4e0d\u77e5\u9053\uff0c\u4eca\u5929\u975e\u7f5a\u4ed6\u7ec3\u7ed9\u6211\u4eec\u770b\u770b\u4e0d\u53ef\uff0c\u4ed6\u8981\u662f\u4e0d\u7ec3\uff0c\u6211\u7b2c\u4e00 \u4e2a\u5c31\u4e0d\u7b54\u5e94\u3002\u201d \u3000\u3000\u5f90\u5c0f\u5170\u7b54\u9053\uff1a\u201c\u8fd9\u6837\u6562\u60c5\u597d\uff0c\u6211\u4eec\u4e1c\u65b9\u5927\u59b9\u5b50\u4e5f\u6b63\u624b\u75d2\u5f97\u7d27\uff0c\u5c31\u8ba9\u4ed6\u4eec\u4e24\u4e2a\u4e00\u8d77\u4e0a\u53bb\u7ec3 \u7ed9\u6211\u4eec\u770b\u770b\uff0c\u4f60\u4eec\u53ef\u8d5e\u6210\u4e0d\uff1f\u201d \u3000\u3000\u4e1c\u65b9\u7075\u559c\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u54e6\u4e5f\u8d5e\u6210\uff0c\u6211\u8fd8\u51fa\u4e2a\u4e3b\u610f\uff0c\u4e09\u5341\u62db\u4e4b\u5185\uff0c\u8981\u662f\u8c01\u4e5f\u4e0d\u80fd\u8d62\u4e86 \u8c01\uff0c\u5c31\u7b97\u4e0d\u5206\u80dc\u8d1f\u597d\u4e86\u3002\u201d \u3000\u3000\u539f\u6765\u4ed6\u77e5\u9053\u718a\u501c\u662f\u5f53\u4ee3\u7b2c\u4e00\u5947\u4eba\u4e4b\u5f92\uff0c\u6015\u59b9\u5b50\u4e0d\u662f\u4ed6\u5bf9\u624b\uff0c\u82e5\u8d25\u4e86\u9762\u5b50\u4e0a\u4e0d\u597d\u770b\uff0c\u8fd9\u624d \u60f3\u51fa\u8fd9\u4e2a\u4e3b\u610f\uff0c\u4ed6\u60f3\u59b9\u5b50\u4e09\u5341\u62db\u603b\u53ef\u4ee5\u5e94\u4ed8\u4e86\u3002 \u3000\u3000\u718a\u501c\u542c\u4e86\uff0c\u5b9e\u662f\u4e00\u4e2a\u4e0d\u613f\u610f\uff0c\u671b\u7740\u6b66\u5f53\u56db\u5b50\uff0c\u5e0c\u671b\u4ed6\u4eec\u963b\u6b62\uff0c\u54ea\u77e5\u6b66\u5f53\u56db\u5b50\u4e5f\u662f\u7b11\u563b\u563b \u7684\u62ca\u638c\u8d5e\u6210\uff0c\u539f\u6765\u4ed6\u4eec\u4e5f\u60f3\u89c1\u89c1\u718a\u501c\u7684\u6b66\u529f\u3002 \u3000\u3000\u6b64\u65f6\u6bd4\u6b66\u53f0\u4e0a\uff0c\u52a8\u7740\u624b\u7684\u4e24\u4eba\uff0c\u5df2\u5206\u51fa\u4e86\u80dc\u8d1f\uff0c\u90a3\u4f7f\u201c\u5c11\u6797\u62f3\u201d\u7684\uff0c\u4e00\u62db\u201c\u9ed1\u5e95\u638f\u5fc3\u201d \u88ab\u5bf9\u65b9\u907f\u5f00\u4e86\uff0c\u62db\u5f0f\u7528\u4e86\uff0c\u80a9\u7740\u7740\u5b9e\u5b9e\u88ab\u5288\u4e86\u4e00\u638c\uff0c\u5012\u5728\u53f0\u4e0a\uff0c\u5e78\u4e8f\u4ed6\u8eab\u4f53\u7ed3\u5b9e\uff0c\u722c\u4e86\u4e5e \u6765\uff0c\u542b\u7f9e\u5e26\u6127\u5730\u8d70\u4e0b\u53f0\u3002 \u3000\u3000\u90a3\u4f7f\u201c\u5288\u6302\u638c\u201d\u7684\uff0c\u4e00\u62db\u5f97\u624b\uff0c\u5411\u56db\u5468\u4e00\u62f1\u62f3\uff0c\u7b97\u662f\u56de\u7b54\u4e86\u56db\u5904\u758f\u843d\u7684\u638c\u58f0\uff0c\u4ecd\u4e0d\u80af\u8d70 \u4e0b\u53f0\u53bb\uff0c\u610f\u601d\u662f\u8fd8\u60f3\u63a5\u4e2a\u4e24\u573a\u3002 \u3000\u3000\u4e1c\u65b9\u745b\u7d27\u4e86\u7d27\u8863\u670d\uff0c\u8dc3\u8dc3\u6b32\u8bd5\u3002 \u3000\u3000\u718a\u501c\u89c1\u4e86\u6697\u6697\u53eb\u82e6\uff0c\u4ed6\u5b9e\u4e0d\u613f\u51fa\u624b\uff0c\u5c24\u5176\u5bf9\u65b9\u662f\u4e2a\u5973\u5b50\uff0c\u53c8\u662f\u4e1c\u65b9\u7075\u4e4b\u59b9\uff0c\u80dc\u4e86\u56fa\u662f\u4e0d \u597d\uff0c\u8d25\u4e86\u5374\u53c8\u7b97\u4e2a\u4ec0\u4e48\u3002 \u3000\u3000\u54ea\u77e5\u53f0\u4e0a\u53c8\u8df3\u4e0a\u4e2a\u76f4\u7709\u6123\u773c\u7684\u6c49\u5b50\uff0c\u548c\u90a3\u4f7f\u5288\u6302\u638c\u7684\u52a8\u8d77\u624b\u6765\uff0c\u718a\u501c\u677e\u4e86\u53e3\u6c14\uff0c\u6682\u65f6\u603b \u7b97\u6709\u4eba\u66ff\u4ed6\u89e3\u4e86\u56f4\u3002 \u3000\u3000\u4ed6\u89c1\u4e0a\u53bb\u8fd9\u4eba\uff0c\u4e5f\u662f\u4e2a\u5bfb\u5e38\u628a\u5f0f\uff0c\u5fc3\u91cc\u6709\u4e9b\u5931\u671b\uff0c\u6697\u5fd6\uff1a\u201c\u6c5f\u5357\u504c\u5927\u4e2a\u5730\u65b9\uff0c\u96be\u9053\u5176\u4e2d \u7adf\u6ca1\u6709\u85cf\u9f99\u5367\u864e\u2026\u2026\u201d \u3000\u3000\u4ed6\u4e00\u773c\u671b\u53bb\uff0c\u89c1\u90a3\u4f7f\u201c\u5288\u6302\u638c\u201d\u7684\u53c8\u4ee5\u4e00\u62db\u201c\u7275\u7f18\u624b\u201d\u80dc\u4e86\u4e00\u573a\uff0c\u4ed6\u95f0\u5149\u5982\u70ac\uff0c\u89c1\u8fd9\u6c49 \u5b50\u7684\u8fd9\u4e00\u62db\u201c\u7275\u7f18\u624b\u201d\u7528\u5f97\u751a\u662f\u5de7\uff0c\u800c\u4e14\u542b\u52b2\u672a\u653e\uff0c\u4f3c\u4e4e\u6b64\u4eba\u6b66\u529f\u8fd8\u4e0d\u6b62\u6b64\uff0c\u53ea\u4e0d\u8fc7\u6ca1\u6709\u4f7f \u51fa\u6765\u7f62\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u6bd4\u6b66\u53f0\u4e0a\uff0c\u4e5f\u6709\u4eba\u8f7b\u8f7b\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u867d\u7136\u58f0\u97f3\u6781\u4e3a\u8f7b\u5fae\uff0c\u4f46\u718a\u501c\u8033\u76ee\u5f02\u4e8e\u5e38\u4eba\uff0c \u5728\u8fd9\u55a7\u95f9\u7684\u58f0\u97f3\u4e2d\uff0c\u5374\u542c\u5f97\u6e05\u6e05\u695a\u695a\u3002 \u3000\u3000\u8fd9\u6b21\u4e1c\u65b9\u745b\u53c8\u8981\u4e0a\u53f0\u65f6\uff0c\u5374\u88ab\u4e1c\u65b9\u7075\u4e00\u628a\u62c9\u4f4f\uff0c\u671d\u5979\u505a\u4e86\u4e2a\u773c\u8272\uff0c\u4e1c\u65b9\u745b\u5fc3\u4e2d\u7eb3\u95f7\uff0c\u4f46 \u53c8\u4e0d\u597d\u95ee\u51fa\u6765\u3002 \u3000\u3000\u8f6c\u773c\u53c8\u6709\u4e24\u4e2a\u88ab\u90a3\u4f7f\u201c\u5288\u6302\u638c\u201d\u7684\u4eba\u51fb\u4e0b\u53f0\u6765\u3002 \u3000\u3000\u6700\u602a\u7684\u662f\uff0c\u90a3\u4f7f\u201c\u5288\u6302\u638c\u201d\u7684\u6c49\u5b50\uff0c\u6b66\u529f\u56fa\u4f3c\u56e0\u4eba\u800c\u5f02\uff0c\u5982\u679c\u5bf9\u624b\u7684\u6b66\u529f\u53ea\u6709\u4e00\u6210\uff0c\u4ed6 \u5c31\u4f7f\u51fa\u4e00\u6210\u534a\u6765\uff0c\u5bf9\u624b\u7684\u6b66\u529f\u8457\u6709\u4e09\u6210\uff0c\u4ed6\u5c31\u4f7f\u51fa\u56db\u6210\u6765\uff0c\u6253\u4e86\u51e0\u573a\uff0c\u4ecd\u7136\u662f\u6c14\u5b9a\u795e\u8db3\uff0c\u6ee1 \u4e0d\u5f53\u4e00\u56de\u4e8b\u3002 \u3000\u3000\u5404\u68da\u4e2d\u7684\u8c6a\u5ba2\uff0c\u6b64\u523b\u5df2\u591a\u6570\u53d1\u73b0\uff0c\u6709\u7684\u7adf\u7a83\u7a83\u79c1\u8bae\u4e86\u8d77\u6765\u3002 \u3000\u3000\u51cc\u4e91\u5b50\u6c89\u4e0d\u4f4f\u6c14\uff0c\u4f4e\u58f0\u5411\u4e39\u9633\u5b50\u8bf4\u9053\uff1a\u201c\u6b64\u4eba\u770b\u6765\u6709\u4e9b\u53e4\u602a\uff0c\u6211\u5012\u60f3\u53bb\u63a5\u4ed6\u4e00\u573a\u8bd5 \u8bd5\u3002\u201d \u3000\u3000\u4e39\u9633\u5b50\u6447\u4e86\u6447\u5934\uff0c\u5374\u672a\u8bf4\u8bdd\u3002 \u3000\u3000\u5750\u5728\u65c1\u8fb9\u7684\u5c55\u7fc5\u91d1\u9e4f\u4e00\u6298\u957f\u987b\uff0c\u4f4e\u7b11\u9053\uff1a\u201c\u9053\u957f\u522b\u7740\u6025\uff0c\u4f9d\u6211\u770b\uff0c\u597d\u620f\u8fd8\u5728\u540e\u5934\u5462\u3002\u201d \u3000\u3000\u4e1c\u65b9\u7075\u4ea6\u5728\u4f4e\u5934\u6c89\u541f\u3002 \u3000\u3000\u4e1c\u65b9\u745b\u561f\u7740\u5634\uff0c\u602a\u54e5\u54e5\u600e\u4e48\u4e0d\u8ba9\u5979\u4e0a\u5408\u4e00\u8bd5\u8eab\u624b\uff0c\u5ce8\u5d4b\u53cc\u5c0f\u89c1\u4e86\uff0c\u5077\u5077\u5411\u5979\u53d6\u7b11\u7740\u3002 \u3000\u3000\u604d\u773c\uff0c\u90a3\u4f7f\u201c\u5288\u6302\u638c\u201d\u7684\u53c8\u80dc\u4e86\u4e24\u573a\uff0c\u524d\u540e\u7b97\u8d77\u6765\uff0c\u5df2\u7ecf\u6709\u516d\u4e2a\u8c6a\u5ba2\u8d25\u5728\u4ed6\u624b\u5e95\u4e0b\u3002 \u3000\u3000\u90a3\u516d\u4eba\u867d\u8bf4\u6b66\u529f\u5168\u4e0d\u751a\u9ad8\uff0c\u4f46\u6b64\u4eba\u8fde\u8d25\u516d\u4eba\uff0c\u4ecd\u7136\u7740\u65e0\u5176\u4e8b\uff0c\u529f\u592b\u7684\u6df1\u539a\uff0c\u4f7f\u5f97\u5927\u5bb6\u66f4 \u60ca\u5f02\u4e86\u3002 \u3000\u3000\u4e1c\u65b9\u7075\u4fa7\u9996\u5411\u5c55\u7fc5\u91d1\u9e4f\u95ee\u9053\uff1a\u201c\u4e0a\u5b98\u8001\u82f1\u96c4\u89c1\u591a\u8bc6\u5e7f\uff0c\u4e60\u66fe\u770b\u51fa\u6b64\u4eba\u662f\u4ec0\u4e48\u6765\u8def\u5417\uff1f\u201d \u3000\u3000\u5c55\u7fc5\u91d1\u9e4f\u6447\u5934\u7b54\u9053\uff1a\u201c\u4e0d\u7792\u5821\u4e3b\u8bf4\uff0c\u6211\u4e5f\u5728\u63e3\u6478\u6b64\u4eba\u7684\u6765\u8def\uff0c\u6b64\u4eba\u4f7f\u7684\u662f\u5288\u6302\u638c\uff0c\u672c\u662f \u6781\u4e3a\u666e\u901a\u7684\u638c\u6cd5\uff0c\u53ea\u662f\u5230\u4e86\u4ed6\u624b\u91cc\uff0c\u5374\u50cf\u4e0d\u4e00\u6837\u4e86\u3002\u201d \u3000\u3000\u4e39\u9633\u5b50\u63a5\u53e3\u8bf4\u9053\uff1a\u201c\u4f9d\u8d2b\u9053\u4e4b\u89c1\uff0c\u8fd9\u5288\u6302\u638c\u4f3c\u4e4e\u4e0d\u662f\u4ed6\u672c\u95e8\u6b66\u529f\uff0c\u82e5\u6709\u4e2a\u9ad8\u624b\u4e0a\u53bb\u903c\u4ed6 \u4f7f\u51fa\u672c\u95e8\u6b66\u529f\u6765\uff0c\u4ed6\u7684\u6765\u5386\u5c31\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u5c55\u7fc5\u91d1\u9e4f\u4e0a\u5b98\u4e88\u634b\u987b\u4e00\u7b11\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u8001\u9053\u5012\u6ed1\u5934\u5f97\u5f88\uff0c\u4e00\u70b9\u662f\u975e\u4e5f\u4e0d\u80af\u60f9\uff0c\u65b9\u624d\u4f60\u5e08 \u5f1f\u8981\u4e0a\u53bb\uff0c\u4f60\u963b\u6b62\u4e86\uff0c\u6b64\u523b\u5374\u60f3\u522b\u4eba\u53bb\u9876\u7f38\u3002\u201d \u3000\u3000\u718a\u501c\u4e00\u58f0\u4e0d\u54cd\uff0c\u5374\u770b\u51fa\u4e00\u5b97\u5f02\u4e8b\u6765\u3002 \u3000\u3000\u539f\u6765\u51e1\u662f\u88ab\u90a3\u4f7f\u201c\u5288\u6302\u638c\u201d\u7684\u6253\u4e0b\u53f0\u53bb\u7684\u6c49\u5b50\uff0c\u4e00\u4e0b\u53f0\u5c31\u6709\u4e00\u4e2a\u9ed1\u8863\u6c49\u5b50\u63a5\u8fc7\u53bb\uff0c\u8d70\u5230 \u4e00\u65c1\u8bb2\u8bdd\u3002 \u3000\u3000\u718a\u501c\u7709\u5934\u4e00\u76b1\uff0c\u5fd6\u9053\uff1a\u201c\u96be\u9053\u6b64\u4eba\u53c8\u4e0e\u5929\u9634\u6559\u6709\u4ec0\u4e48\u5173\u8fde\u5417\uff1f\u201d \u3000\u3000\u5c55\u7fc5\u91d1\u9e4f\u5ffd\u5730\u7b11\u9053\uff1a\u201c\u597d\uff0c\u5c45\u7136\u6b66\u80dc\u6587\u4e5f\u4e0a\u6765\u4e86\uff0c\u8fd9\u4e00\u4e0b\u603b\u53ef\u4ee5\u8bd5\u51fa\u4ed6\u7684\u529f\u592b\u6765\u4e86 \u5427\u3002\u201d \u3000\u3000\u4e1c\u65b9\u7075\u9053\uff1a\u201c\u600e\u5730\u5b50\u6bcd\u91d1\u68ad\u6b66\u5927\u4fa0\u6765\u4e86\uff0c\u6211\u90fd\u4e0d\u77e5\u9053\uff0c\u771f\u662f\u2026\u2026\u201d \u3000\u3000\u718a\u501c\u4e00\u671b\u53f0\u4e0a\uff0c\u4e0a\u53bb\u4e2a\u4e2d\u5e74\u7684\u7626\u524a\u6c49\u5b50\uff0c\u6b65\u5c65\u6c89\u7a33\uff0c\u4e24\u773c\u4ea7\u5149\u9887\u8db3\uff0c\u770b\u6765\u5185\u529f\u5df2\u4ff1\u706b \u5019\u3002 \u3000\u3000\u90a3\u7626\u524a\u6c49\u5b50\u4e00\u4e0a\u53f0\uff0c\u4fbf\u62b1\u62f3\u9053\uff1a\u201c\u670b\u53cb\u7aef\u7684\u597d\u8eab\u624b\uff0c\u6211\u6b66\u80dc\u6587\u4e0d\u81ea\u91cf\u529b\uff0c\u60f3\u6765\u9886\u6559\u670b\u53cb \u7684\u9ad8\u62db\uff0c\u53ea\u662f\u670b\u53cb\u80fd\u5426\u4eae\u4e2a\u4e07\u513f\uff0c\u4f7f\u5929\u4e0b\u597d\u6c49\u4e5f\u77e5\u9053\u670b\u53cb\u662f\u54ea\u4e00\u8def\u7684\u82f1\u96c4\u3002\u201d \u3000\u3000\u68da\u4e2d\u7684\u4e0a\u5b98\u4f4e\u7b11\u9053\uff1a\u201c\u679c\u7136\u8fd8\u662f\u4ed6\u5389\u5bb3\uff0c\u4e00\u4e0a\u53bb\u5c31\u60f3\u6296\u9732\u4eba\u5bb6\u7684\u6765\u5386\u3002\u201d \u3000\u3000\u54ea\u77e5\u90a3\u4f7f\uff1a\u201c\u5288\u6302\u638c\u201d\u7684\u6c49\u5b50\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u6c5f\u6e56\u5c0f\u5352\uff0c\u54ea\u6709\u4ec0\u4e48\u4e07\u513f\uff0c\u53ea\u662f \u5b50\u6bcd\u91d1\u68ad\u7684\u5927\u540d\uff0c\u5728\u4e0b\u5374\u4e45\u5df2\u95fb\u5f97\uff0c\u4eca\u65e5\u6709\u5e78\uff0c\u80fd\u5728\u9f0e\u9f0e\u5927\u540d\u7684\u82f1\u96c4\u638c\u4e0b\u8ba8\u6559\u771f\u662f\u5e78\u4f55\u4e4b \u5982\u3002\u201d \u3000\u3000\u4e39\u9633\u5b50\u5fae\u4e00\u76b1\u7709\uff0c\u8bf4\u9053\uff1a\u201c\u6b64\u4eba\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u4e2d\u6c14\u5f3a\u52b2\u5df2\u6781\uff0c\u770b\u6837\u5b50\u5185\u529f\u5df2\u6709\u5341\u5206\u706b \u5019\uff0c\u53ea\u662f\u8d2b\u9053\u60f3\u6765\u60f3\u53bb\uff0c\u5374\u60f3\u4e0d\u5230\u6b64\u4eba\u7684\u6765\u8def\u3002\u201d \u3000\u3000\u4e1c\u65b9\u7075\u4e5f\u6c89\u541f\u9053\uff1a\u201c\u6b64\u4eba\u5fc5\u662f\u5185\u5bb6\u9ad8\u624b\uff0c\u9690\u540d\u6765\u6b64\uff0c\u53ea\u662f\u4ed6\u5982\u6b64\u53c8\u6709\u4f55\u7528\u610f\u5462\uff1f\u201d \u3000\u3000\u53f0\u4e0a\u7684\u6b66\u80dc\u6587\u5374\u5df2\u7ecf\u52a8\u6012\uff0c\u559d\u9053\uff1a\u201c\u597d\u670b\u53cb\u65e2\u4e0d\u80af\u4eae\u4e07\u513f\uff0c\u6b66\u67d0\u4eba\u53ea\u5f97\u653e\u8086\u4e86\u3002\u201d \u3000\u3000\u8bdd\u672a\u8bf4\u5b8c\uff0c\u8eab\u5f62\u4e00\u9519\uff0c\u201c\u8e0f\u6d2a\u95e8\uff0c\u8d70\u4e2d\u5b98\u201d\u4e00\u624b\u6253\u53bb\uff0c\u7adf\u662f\u5c11\u6797\u7684\u201c\u4f0f\u864e\u62f3\u201d\u3002 \u3000\u3000\u54ea\u77e5\u5288\u6302\u638c\u7684\u6c49\u5b50\u53f3\u80a9\u4e00\u6c89\uff0c\u53f3\u638c\u4ece\u6b66\u80dc\u6587\u8098\u4e0b\u7a7f\u51fa\uff0c\u4e00\u62db\u201c\u62e8\u4e91\u89c1\u65e5\u201d\u76f4\u53d6\u5de6\u808b\uff0c\u5374 \u4ecd\u662f\u201c\u5288\u6302\u638c\u201d\u7684\u62db\u5f0f\u3002 \u3000\u3000\u6b66\u80dc\u6587\u5fae\u4e00\u5750\u9a6c\uff0c\u53cc\u638c\u4e00\u4ea4\uff0c\u5316\u5f00\u4e86\u6765\u52bf\uff0c\u53f3\u65f6\u4e00\u5f2f\uff0c\u4e00\u4e2a\u201c\u65f6\u62f3\u201d\u8fc7\u6765\uff0c\u90a3\u6c49\u5b50\u5fae\u5fae \u4e00\u7b11\uff0c\u811a\u6b65\u4e00\u9519\uff0c\u907f\u5f00\u4e86\u6b64\u62db\uff0c\u6b66\u80dc\u6587\u8eab\u8eaf\u4e00\u626d\uff0c\u53f3\u624b\u5237\u5730\u76f4\u70b9\u201c\u9501\u5589\u7a74\u201d\uff0c\u90a3\u6c49\u5b50\u559d\u9053\uff1a \u201c\u597d\u62f3\u6cd5\u201d\uff0c\u4e00\u9519\u638c\uff0c\u5237\u5237\u5237\uff0c\u4e00\u8fde\u4e09\u638c\uff0c\u867d\u4ea6\u662f\u201c\u5288\u6302\u638c\u201d\u91cc\u666e\u901a\u62db\u5f0f\uff0c\u4f46\u4ed6\u638c\u529b\u5e26\u98ce\uff0c \u98ce\u58f0\u547c\u547c\uff0c\u54ea\u8fd8\u662f\u4ec0\u4e48\u201c\u5e84\u7a3c\u628a\u5f0f\u201d\u3002 \u3000\u3000\u90a3\u201c\u5288\u6302\u638c\u201d\u5728\u6b66\u6797\u4e2d\u6781\u4e3a\u666e\u904d\uff0c\u4e61\u4e0b\u7684\u628a\u5f0f\u573a\u91cc\u7684\u6559\u6b66\u5e08\u5085\uff0c\u603b\u662f\u62ff\u8fd9\u5957\u638c\u6cd5\u6559\u4eba\uff0c \u4f46\u6b64\u523b\u5230\u4e86\u4ed6\u624b\u91cc\uff0c\u5374\u662f\u5927\u5927\u4e0d\u540c\u3002 \u3000\u3000\u603b\u77e5\u8d8a\u662f\u5728\u8fd9\u79cd\u666e\u901a\u7684\u62f3\u6cd5\u4e0a\uff0c\u8d8a\u662f\u89c1\u4e86\u771f\u529f\u592b\uff0c\u90a3\u6c49\u5b50\u4e00\u62db\u63a5\u7740\u4e00\u62db\uff0c\u5feb\u5f97\u4ee4\u4eba\u773c\u82b1 \u7f2d\u4e71\u3002 \u3000\u3000\u5c55\u7fc5\u91d1\u9e4f\u4e00\u770b\uff0c\u8bf4\u9053\uff1a\u201c\u6b64\u4eba\u7684\u786e\u6709\u4e24\u4e0b\u5b50\uff0c\u8fde\u6b66\u80dc\u6587\u7684\u2018\u4f0f\u864e\u62f3\u2019\u8fd8\u903c\u4e0d\u51fa\u4ed6\u7684\u771f\u62db \u6765\uff0c\u800c\u4e14\u770b\u6837\u5b50\u6b66\u80dc\u6587\u4e5f\u5feb\u4e0d\u884c\u5462\u3002\u201d \u3000\u3000\u4e1c\u65b9\u745b\u6b64\u523b\u5634\u4e5f\u4e0d\u561f\u4e86\uff0c\u4e00\u8fb9\u770b\u4e00\u8fb9\u8bf4\u9053\uff1a\u201c\u8fd9\u4eba\u7684\u638c\u6cd5\u6211\u770b\u4e5f\u6ca1\u6709\u4ec0\u4e48\u7279\u522b\u7684\u5730\u65b9\uff0c \u5c31\u662f\u6bd4\u4eba\u5bb6\u5feb\u70b9\u5c31\u662f\u4e86\u3002\u201d \u3000\u3000\u5c55\u7fc5\u91d1\u9e4f\u4e0a\u5b98\u4e88\u7b11\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u5c31\u8fd9\u5feb\u5c31\u591f\u4f60\u77a7\u7684\u3002\u6211\u770b\u6b66\u80dc\u6587\u4e0d\u51fa\u5341\u62db\u5c31\u8981\u4e0d\u6210 \u4e86\u3002\u201d \u3000\u3000\u4ed6\u62ff\u773c\u671b\u7740\u4e1c\u65b9\u7075\uff0c\u610f\u601d\u662f\u8981\u4e1c\u65b9\u7075\u4e0a\u53bb\u63a5\u4e0b\u6765\uff0c\u54ea\u77e5\u4e1c\u65b9\u7075\u4e0d\u95fb\u4e0d\u89c1\uff0c\u4ed6\u4eba\u6700\u6c89\u7a33\uff0c \u5728\u6ca1\u6709\u5f04\u6e05\u4eba\u5bb6\u6765\u5386\u4e4b\u524d\uff0c\u600e\u4f1a\u8dd1\u53bb\u8ddf\u4eba\u5bb6\u6253\u67b6\u3002 \u3000\u3000\u679c\u7136\u4e0d\u51fa\u4e0a\u5b98\u4e88\u6240\u6599\uff0c\u5b50\u6bcd\u91d1\u68f1\u989d\u4e0a\u5df2\u89c1\u6c57\uff0c\u6c14\u529b\u4e5f\u81ea\u4e0d\u53ca\uff0c\u8d8a\u6253\u8d8a\u5403\u529b\uff0c\u90a3\u4f7f\u201c\u5288\u6302 \u638c\u201d\u7684\u6c49\u5b50\u4e00\u58f0\u957f\u7b11\uff0c\u5237\u5730\u4e00\u638c\uff0c\u201c\u4e39\u51e4\u671d\u9633\u201d\u6b66\u80dc\u6587\u5c3d\u529b\u53f3\u503e\uff0c\u4f46\u80a9\u4e0a\u5df2\u88ab\u638c\u7f18\u626b\u4e2d\uff0c\u53ea \u89c9\u706b\u8fa3\u8fa3\u5730\u751f\u75db\u3002 \u3000\u3000\u5b50\u6bcd\u91d1\u68ad\u5728\u6c5f\u5357\u6b66\u6797\uff0c\u4e5f\u662f\u6210\u540d\u9732\u8138\u7684\u82f1\u96c4\uff0c\u6b64\u523b\u4e00\u62db\u843d\u8d25\uff0c\u4fbf\u81ea\u6536\u4e86\u624b\uff0c\u4e00\u8a00\u4e0d\u53d1\u8d70 \u4e0b\u53f0\u53bb\u3002 \u3000\u3000\u5c55\u7fc5\u91d1\u9e4f\u4e0a\u5b98\u4e88\u4e00\u58f0\u957f\u53f9\uff0c\u8bf4\u9053\uff1a\u201c\u5509\uff0c\u60f3\u4e0d\u5230\u4eca\u5929\u6b66\u80dc\u6587\u4e0d\u660e\u4e0d\u767d\u5730\u683d\u5728\u4eba\u5bb6\u624b\u4e0a\uff0c \u8fde\u4eba\u5bb6\u662f\u4ec0\u4e48\u6765\u8def\u90fd\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u4e1c\u65b9\u7075\u4e5f\u81ea\u6447\u5934\uff0c\u56de\u5934\u5631\u5490\u8eab\u540e\u7684\u5821\u4e01\uff0c\u53eb\u4ed6\u53bb\u5c06\u6b66\u80dc\u6587\u63a5\u6765\uff0c\u718a\u501c\u5374\u53d1\u89c9\u4e00\u4e2a\u9ed1\u8863\u5927 \u6c49\uff0c\u65e9\u5df2\u5c06\u6b66\u80dc\u6587\u5f15\u8d70\u4e86\u3002 \u3000\u3000\u90a3\u6c49\u5b50\u4e00\u638c\u51fb\u4e0b\u6b66\u80dc\u6587\uff0c\u68da\u91cc\u7fa4\u8c6a\u5927\u534a\u77e5\u9053\u5b50\u6bcd\u91d1\u68ad\u7684\u540d\u5934\uff0c\u89c1\u4ed6\u4e5f\u843d\u8d25\u6cca\u95ee\u8eab\u624b\uff0c\u4fbf \u6ca1\u6709\u518d\u4e0a\u53f0\uff0c\u90a3\u6c49\u5b50\u5353\u7acb\u53f0\u4e0a\uff0c\u7a81\u5730\u6717\u58f0\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u95fb\u5f97\u4e1c\u65b9\u5821\u4e3b\u6b64\u6b21\u805a\u7fa4\u8c6a\uff0c\u9664\u4e86\u4ee5\u6b66 \u4f1a\u53cb\u4e4b\u5916\uff0c\u8fd8\u58f0\u8a00\u82e5\u6709\u6280\u538b\u5f53\u573a\uff0c\u5e76\u4e14\u80fd\u80dc\u5f97\u4e86\u7c89\u8776\u4e1c\u65b9\u5973\u4fa0\u7684\uff0c\u5c31\u662f\u98de\u7075\u5821\u4f2a\u4e1c\u5e8a\u5feb\u5a7f\uff0c \u600e\u5730\u76f4\u5230\u73b0\u5728\uff0c\u7c89\u8776\u513f\u8fd8\u4e0d\u51fa\u6765\u4e00\u73b0\u8eab\u624b\u5462\uff1f\u201d\u8bf4\u5b8c\u662f\u4e00\u9635\u5927\u7b11\u3002 \u3000\u3000\u4e1c\u65b9\u7075\u4e00\u542c\uff0c\u53cc\u7709\u7acb\u7d27\u7d27\u76b1\u5230\u4e00\u8d77\uff0c\u4ed6\u7684\u786e\u662f\u6709\u8fc7\u6b64\u610f\uff0c\u4f46\u6b64\u523b\u4e3b\u610f\u5df2\u6539\uff0c\u5374\u60f3\u4e0d\u5230\u8fd9 \u6c49\u5b50\u9523\u5bf9\u9523\uff0c\u9f13\u5bf9\u9f13\uff0c\u5f53\u9762\u7ed9\u6296\u9732\u51fa\u6765\u3002 \u3000\u3000\u6b66\u6797\u4e2d\u4eba\u7d20\u91cd\u7136\u8bfa\uff0c\u5c24\u5176\u4ee5\u51fa\u5c18\u5251\u5ba2\u7684\u540d\u5934\uff0c\u5c82\u6709\u8bf4\u4e86\u4e0d\u7b97\u4e4b\u7406\uff0c\u4f46\u4ed6\u5374\u53c8\u4e0d\u613f\u8ba9\u81ea\u5df1 \u59b9\u5b50\u8ddf\u6b64\u4eba\u52a8\u624b\u3002 \u3000\u3000\u4e1c\u65b9\u7075\u5fc3\u4e2d\u53eb\u82e6\uff0c\u671d\u718a\u501c\u8fde\u4f7f\u4e86\u51e0\u4e2a\u773c\u8272\uff0c\u5e0c\u671b\u718a\u501c\u6253\u9000\u6b64\u4eba\uff0c\u54ea\u77e5\u718a\u501c\u6b63\u6015\u60f9\u7740\u4e1c\u65b9 \u745b\uff0c\u6b64\u523b\u542c\u4e86\u90a3\u6c49\u5b50\u7684\u8bdd\uff0c\u66f4\u6108\u53d1\u4e0d\u51fa\u624b\u4e86\u3002 \u3000\u3000\u7fa4\u8c6a\u6b64\u523b\u4e5f\u81ea\u54c4\u7136\uff0c\u90fd\u60f3\u4e0d\u5230\u8fd9\u6c49\u5b50\u5c45\u7136\u6562\u5f53\u9762\u53bb\u64a9\u62e8\u51fa\u5c18\u5251\u5ba2\uff0c\u6709\u7684\u66f4\u60f3\u770b\u70ed\u95f9\uff0c\u6068 \u4e0d\u5f97\u4e1c\u65b9\u5144\u59b9\u7acb\u523b\u51fa\u624b\uff0c\u6253\u4e2a\u70ed\u95f9\u597d\u770b\u7684\u3002 \u3000\u3000\u4e1c\u65b9\u7075\u6b63\u81ea\u65e0\u8bdd\u53ef\u7b54\uff0c\u54ea\u77e5\u897f\u68da\u7fa4\u8c6a\uff0c\u7a81\u7136\u98de\u8d77\u4e00\u6761\u4eba\u5f71\uff0c\u8f7b\u529f\u5999\uff0c\u8eab\u624b\u75be\uff0c\u663e\u89c1\u5f97\u53c8 \u662f\u4e2a\u9ad8\u624b\u3002 \u3000\u3000\u90a3\u4eba\u5f71\u8f7b\u98d8\u5730\u4e00\u843d\u5728\u53f0\u4e0a\uff0c\u4fbf\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u8981\u6025\u7740\u5a36\u8001\u5a46\uff0c\u5148\u63a5\u6211\u8001\u53eb\u82b1\u5b50\u51e0\u624b\u3002\u201d \u3000\u3000\u68da\u4e2d\u8bf8\u4eba\uff0c\u4e5f\u4e00\u9f50\u5927\u60ca\uff0c\u4e0a\u5b98\u4e88\u62cd\u7740\u684c\u5b50\uff0c\u5927\u58f0\u9053\uff1a\u201c\u54a6\uff0c\u60f3\u4e0d\u5230\uff0c\u60f3\u4e0d\u5230\uff0c\u5c45\u7136\u8fde\u84dd \u5927\u5148\u751f\u4e5f\u51fa\u624b\u4e86\u3002\u201d \u3000\u3000\u539f\u6765\u8fd9\u4eba\u6b63\u662f\u4e10\u5e2e\u7684\u9f99\u5934\u5e2e\u4e3b\uff0c\u6b66\u6797\u4e2d\u5927\u5927\u6709\u540d\u7684\u84dd\u5927\u5148\u751f\u3002 \u3000\u3000\u90a3\u4f7f\u201c\u5288\u6302\u638c\u201d\u7684\u6c49\u5b50\u4e5f\u662f\u4e00\u60ca\uff0c\u4f46\u968f\u5373\u5e73\u9759\u4e0b\u6765\uff0c\u62b1\u62f3\u7b11\u9053\uff1a\u201c\u539f\u6765\u84dd\u5927\u5148\u751f\u4e5f\u6765 \u4e86\uff0c\u96be\u9053\u9601\u4e0b\u4e5f\u60f3\u8981\u4e2a\u5ab3\u5987\u5417\uff1f\u201d \u3000\u3000\u84dd\u5927\u5148\u751f\u54c8\u54c8\u4e00\u9635\u72c2\u7b11\uff0c\u7a81\u5730\u76ee\u4e2d\u5c04\u51fa\u7cbe\u5149\uff0c\u9053\uff1a\u201c\u6211\u5ab3\u5987\u5012\u4e0d\u60f3\u5a36\uff0c\u4e0d\u8fc7\u60f3\u6765\u89c1\u89c1\u8001 \u670b\u53cb\u800c\u5df2\uff0c\u987a\u4fbf\u4e5f\u8ba8\u6559\u8ba8\u6559\u9ad8\u62db\u3002\u201d \u3000\u3000\u90a3\u6c49\u5b50\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u84dd\u5927\u5148\u751f\u5c45\u7136\u8fd8\u8bb0\u5f97\u5728\u4e0b\uff0c\u771f\u662f\u6559\u5728\u4e0b\u6709\u70b9\u89c9\u5f97\u53d7\u5ba0\u82e5\u60ca\u4e86\u3002\u201d \u3000\u3000\u84dd\u5927\u5148\u751f\u8fd9\u4e00\u51fa\u73b0\uff0c\u6b63\u5728\u6240\u8c13\u4eba\u7684\u540d\u513f\uff0c\u6811\u7684\u5f71\u513f\uff0c\u56db\u68da\u7fa4\u8c6a\u8c01\u4e0d\u6697\u6697\u79f0\u602a\u3002 \u3000\u3000\u5c55\u7fc5\u91d1\u9e4f\u4e0a\u5b98\u4e88\u6301\u987b\u9053\uff1a\u201c\u6b64\u4eba\u5c45\u7136\u548c\u84dd\u5927\u5148\u751f\u8fd8\u662f\u7d20\u8bc6\uff0c\u8fd9\u6837\u770b\u6765\uff0c\u6b64\u4eba\u66f4\u662f\u5927\u6709\u6765 \u5386\u4e86\u3002\u201d \u3000\u3000\u54ea\u77e5\u6b64\u523b\u53c8\u6781\u7094\u5730\u63a0\u8d77\u4e00\u6761\u8eab\u5f71\u5506\u5730\u7a9c\u5230\u53f0\u4e0a\uff0c\u5374\u539f\u6765\u53c8\u662f\u5b50\u6bcd\u91d1\u6838\u6b66\u80dc\u6587\u53bb\u800c\u590d\u8fd4 \u4e86\u3002 \u3000\u3000\u5b50\u6bcd\u91d1\u68f1\u6b66\u80dc\u6587\u8fd9\u4e00\u73b0\u8eab\uff0c\u7fa4\u8c6a\u66f4\u662f\u5484\u5484\u79f0\u602a\uff0c\u987b\u77e5\u65e0\u8bba\u4efb\u4f55\u573a\u5408\u6bd4\u6b66\uff0c\u54ea\u6709\u8d25\u7684\u4eba\u91cd \u53c8\u4e0a\u53f0\u7684\u9053\u7406\uff0c\u4f55\u51b5\u662f\u5b50\u6bcd\u91d1\u68ad\u8fd9\u6837\u7684\u6210\u540d\u4eba\u7269\u5462\uff1f \u3000\u3000\u90a3\u4f7f\u201c\u5288\u6302\u638c\u201d\u7684\u6c49\u5b50\u4e5f\u5927\u51fa\u610f\u5916\uff0c\u8bf4\u9053\uff1a\u201c\u96be\u9053\u6b66\u5927\u4fa0\u5df2\u4f11\u606f\u591f\u4e86\uff0c\u8fd8\u8981\u518d\u8d50\u6559 \u5417\uff1f\u201d \u3000\u3000\u4ed6\u8fd9\u8bdd\u660e\u867d\u5ba2\u6c14\uff0c\u9aa8\u5b50\u91cc\u5374\u53c8\u9634\u53c8\u635f\uff0c\u5b50\u6bcd\u91d1\u68ad\u54ea\u6709\u542c\u4e0d\u51fa\u6765\u7684\u9053\u7406\u3002 \u3000\u3000\u5c55\u7fc5\u91d1\u9e4f\u4e0a\u5b98\u4e88\u4e5f\u601d\u5fd6\u9053\uff1a\u201c\u4eca\u5929\u6b66\u80dc\u6587\u600e\u4e48\u641e\u7684\uff0c\u5ffd\u7136\u53c8\u8dd1\u4e0a\u53f0\u53bb\u4e86\uff0c\u96be\u9053\u8fd8\u60f3\u9732\u4e00 \u9732\u4ed6\u4e24\u624b\u2018\u5b50\u6bcd\u91d1\u68ad\u2019\u5417\uff1f\u5509\uff0c\u8fd9\u56de\u5c31\u7b97\u662f\u80fd\u591f\u80dc\u4e86\u4eba\u5bb6\uff0c\u53ef\u662f\u4e5f\u4e0d\u89c1\u5f97\u662f\u9732\u8138\u7684\u5440\u3002\u201d \u3000\u3000\u54ea\u77e5\u6b66\u80dc\u6587\u9762\u4e0d\u6539\u8272\uff0c\u51b7\u51b7\u4ed6\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u6b66\u80dc\u6587\u8d25\u5728\u9601\u4e0b\u7684\u638c\u4e0b\uff0c\u600e\u4f1a\u518d\u6709\u989c\u4e0a \u6765\u8ddf\u9601\u4e0b\u6bd4\u6b66\u3002\u201d \u3000\u3000\u7fa4\u8c6a\u4e00\u8d77\u66f4\u5947\uff0c\u6697\u5fd6\u9053\uff1a\u201c\u4f60\u4e0d\u4e0a\u6765\u6bd4\u6b66\uff0c\u8dd1\u4e0a\u53f0\u6765\u53c8\u662f\u4e3a\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u6b66\u80dc\u6587\u4ef0\u5929\u4e00\u58f0\u957f\u7b11\uff0c\u7b11\u58f0\u4e2d\u6ca1\u6709\u4e00\u70b9\u201c\u7b11\u201d\u7684\u5473\u9053\uff0c\u542c\u8d77\u6765\u53ea\u89c9\u5f97\u5982\u67ad\u9e1f\u591c\u557c\u51c4\u5389\u5df2 \u6781\u3002 \u3000\u3000\u5b50\u6bcd\u91d1\u68ad\u6b66\u80dc\u6587\u8bf4\u9053\uff1a\u201c\u53ef\u662f\u6211\u8fd9\u6b21\u4e0a\u6765\uff0c\u5374\u4e3a\u7684\u662f\u66ff\u6211\u7684\u4e00\u4e2a\u597d\u670b\u53cb\u62a5\u524a\u8db3\u4e4b\u4ec7\u3002\u201d \u3000\u3000\u4ed6\u6b64\u8bdd\u4e00\u51fa\uff0c\u7fa4\u8c6a\u9f50\u90fd\u54c4\u7136\u3002\u90a3\u6c49\u5b50\u4e5f\u81ea\u9762\u4e0a\u53d8\u8272\u3002 \u3000\u3000\u6b66\u80dc\u6587\u76ee\u5149\u4e00\u51b7\uff0c\u6307\u7740\u90a3\u6c49\u5b50\u8bf4\u9053\uff1a\u201c\u5404\u4f4d\u77e5\u9053\u6b64\u4eba\u662f\u8c01\uff1f\u4ed6\u5c31\u662f\u2026\u2026\uff0c\u4ed6\u8bdd\u672a\u8bf4\u5b8c\uff0c \u90a3\u6c49\u5b50\u53cc\u638c\u4e00\u9519\uff0c\u53f3\u624b\u5237\u5730\u4e00\u638c\uff0c\u5f53\u5934\u62cd\u53bb\uff0c\u5de6\u624b\u5e76\u6307\uff0c\u75be\u70b9\u80f8\u574e\u7684\u201d\u5e7d\u95e8\u201c\u91cd\u7a74\u3002\u4ed6\u4e00\u62db \u4e24\u5f0f\uff0c\u51fa\u624b\u5982\u51e4\uff0c\u6b66\u80dc\u6587\u5237\u5730\u5927\u4ef0\u8eab\uff0c\u582a\u582a\u907f\u8fc7\u6b64\u62db\uff0c\u4f46\u5634\u91cc\u7684\u8bdd\uff0c\u5374\u88ab\u903c\u4e86\u56de\u53bb\u3002\u90a3\u6c49\u5b50 \u559d\u9053\uff1a\u201c\u597d\u670b\u53cb\u8981\u52a8\u624b\u5c31\u52a8\u624b\uff0c\u522b\u591a\u5e9f\u8bdd\u3002\u201d\u624b\u5e95\u4e0b\u8fde\u73af\u7528\u638c\uff0c\u7740\u7740\u90fd\u662f\u6740\u624b\u3002 \u3000\u3000\u84dd\u5927\u5148\u751f\u7ad9\u5728\u4e00\u65c1\uff0c\u50f5\u4f4f\u4e86\uff0c\u4ed6\u81ea\u4e0d\u80fd\u521d\u5b50\u6bcd\u91d1\u6838\u6b66\u80dc\u6587\u4e00\u8d77\u52a8\u624b\uff0c\u53ea\u5f97\u8d70\u4e0b\u53f0\u53bb\uff0c\u4e3b \u68da\u7fa4\u4fa0\u4e00\u8d77\u7ad9\u8d77\u8eab\u6765\uff0c\u671d\u4ed6\u62db\u547c\u7740\uff0c\u4f46\u4ed6\u5fae\u4e00\u62b1\u62f3\uff0c\u5374\u53c8\u8d70\u56de\u897f\u68da\uff0c\u5e76\u4e0d\u8d70\u5230\u4e3b\u68da\u4e2d\u53bb\u3002 \u3000\u3000\u5c55\u7fc5\u91d1\u9e4f\u8bf4\u9053\uff1a\u201c\u4eca\u65e5\u771f\u662f\u602a\u4e8b\u5c42\u51fa\uff0c\u8fde\u6211\u8001\u5934\u5b50\u90e8\u6709\u70b9\u7cca\u6d82\u4e86\uff0c\u600e\u4e48\u597d\u597d\u7684\u6b66\u80dc\u6587\u53c8 \u66ff\u4eba\u62a5\u8d77\u4ec7\u6765\uff0c\u8fd9\u84dd\u5927\u5148\u751f\u663e\u7136\u662f\u8ba4\u5f97\u8fd9\u6c49\u5b50\uff0c\u600e\u4e48\u4e5f\u4e0d\u8d70\u8fc7\u6765\u8ddf\u6211\u4eec\u54e5\u513f\u513f\u4e2a\u804a\u804a\u3002\u201d \u3000\u3000\u53f0\u4e0a\u6b64\u523b\u7684\u8fd9\u573a\u6bd4\u6597\uff0c\u53c8\u548c\u65b9\u624d\u5927\u4e0d\u67ef\u540c\uff0c\u4e24\u4eba\u5168\u662f\u8fdb\u624b\u62db\u6570\uff0c\u800c\u4e14\u62db\u62db\u90fd\u5411\u81f4\u547d\u4e4b\u5904 \u4e0b\u624b\u3002 \u3000\u3000\u4e1c\u65b9\u7075\u5fae\u5fae\u82e6\u7b11\uff0c\u521a\u521a\u4ed6\u624d\u8bf4\u8fc7\u201c\u4ee5\u6b66\u4f1a\u53cb\u201d\uff0c\u201c\u70b9\u5230\u4e3a\u6b62\u201d\uff0c\u201c\u4e0d\u5f97\u5bfb\u4ec7\u201d\uff0c\u4f46\u9a6c\u4e0a \u5c31\u53c8\u62fc\u8d77\u547d\u6765\uff0c\u6b64\u60c5\u6b64\u666f\uff0c\u4ed6\u53c8\u4e0d\u80fd\u51fa\u5934\u529d\u89e3\uff0c\u662f\u4ee5\u4ed6\u53ea\u6709\u6447\u5934\u4f5c\u820d\u7b11\u4e4b\u72b6\u3002 \u3000\u3000\u4e24\u4eba\u77ac\u5373\u62c6\u4e86\u6570\u5341\u62db\uff0c\u6b66\u80dc\u6587\u4e00\u6d3e\u62fc\u547d\u7684\u6253\u6cd5\uff0c\u90a3\u6c49\u5b50\u89c1\u4e0d\u6613\u53d6\u80dc\uff0c\u5ffd\u5730\u65ad\u559d\u4e00\u58f0\uff0c\u638c \u6cd5\u4e00\u53d8\uff0c\u5374\u4e0d\u518d\u662f\u201c\u5288\u6302\u638c\u201d\u3002 \u3000\u3000\u4ed6\u638c\u6cd5\u4e00\u53d8\uff0c\u4e39\u9633\u5b50\u3001\u4e1c\u65b9\u7075\u3001\u4e0a\u5b98\u4e88\u4e09\u4eba\u9f50\u58f0\u60ca\u54e6\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u539f\u6765\u201c\u6b66\u5f53\u201d\u3001\u201c\u5d06\u5cd2\u201d\u201c\u5ce8\u5d4b\u201d\u3001\u201c\u6606\u4ed1\u201d\u3001\u201c\u70b9\u82cd\u201d\u4e43\u662f\u5185\u5bb6\u7684\u4e94\u5927\u5b97\u6d3e\uff0c\u662f\u4ee5\u90a3 \u6c49\u5b50\u4e00\u51fa\u624b\uff0c\u4e39\u9633\u5b50\u4fbf\u80fd\u8ba4\u51fa\u662f\u201c\u5d06\u5cd2\u201d\u6240\u4f20\u3002 \u3000\u3000\u5c55\u7fc5\u91d1\u9e4f\u62cd\u6848\u9053\uff1a\u201c\u6211\u5012\u60f3\u8d77\u4e00\u4eba\uff0c\u4ee5\u6b64\u4eba\u7684\u5e74\u7eaa\uff0c\u529f\u529b\u770b\u6765\uff0c\u4ed6\u4e00\u5b9a\u5c31\u662f\u5d06\u5cd2\u7684\u540e\u8d77 \u9ad8\u624b\uff0c\u5929\u9634\u6559\u7684\u9f99\u987b\u575b\u4e3b\u5355\u638c\u65ad\u9b42\u5355\u98de\u4e86\uff0c\u602a\u4e0d\u5f97\u6b66\u80dc\u6587\u62fc\u547d\uff0c\u4ed6\u7684\u5e08\u5144\u94f6\u94a9\u76c2\u4ef2\u8d85\u4fbf\u662f\u4f24 \u5728\u6b64\u4eba\u624b\u4e0b\u3002\u201d \u3000\u3000\u51fa\u5c18\u5251\u5bb9\u9762\u5982\u51dd\u971c\uff0c\u8bf4\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5929\u9634\u6559\u5c45\u7136\u8dd1\u5230\u98de\u7075\u5821\u91cc\u6765\u6492\u91ce\uff0c\u602a\u4e0d\u5f97\u5c0f\u5f1f\u4eca\u65e5 \u4e5f\u8981\u51fa\u624b\u4e86\u3002\u201d \u3000\u3000\u539f\u6765\u6b64\u4eba\u6b63\u662f\u5929\u9634\u6559\u4e0b\u7684\u9f99\u987b\u575b\u4e3b\u5355\u98de\u3002 \u3000\u3000\u5929\u9634\u6559\u5728\u6c5f\u6e56\u4e0a\u7f57\u81f4\u4eba\u624d\uff0c\u4e0d\u9057\u4f59\u529b\uff0c\u9f99\u987b\u575b\u4e3b\u66f4\u662f\u804c\u8d23\u6240\u5728\uff0c\u662f\u4ee5\u5355\u98de\u4e00\u542c\u98de\u7075\u5821\u4e3b \u4ee5\u6b66\u4f1a\u53cb\uff0c\u4e3a\u59b9\u62e9\u5a7f\uff0c\u4fbf\u8dd1\u4e86\u6765\uff0c\u4e00\u5219\u662f\u4e58\u673a\u7f51\u7f57\u4eba\u624d\uff0c\u518d\u5219\u5374\u662f\u60f3\u51ed\u7740\u81ea\u5df1\u7684\u4e00\u8eab\u827a\u4e1a\uff0c \u6280\u538b\u7fa4\u96c4\uff0c\u53ea\u8981\u81ea\u5df1\u80fd\u5a36\u5f97\u4e1c\u65b9\u7075\u7684\u59b9\u59b9\uff0c\u90a3\u4e48\u8fde\u51fa\u5c18\u5251\u5ba2\u90fd\u6210\u4e86\u5929\u9634\u6559\u4e0b\u7684\u4eba\u4e86\u3002 \u3000\u3000\u4f46\u4ed6\u77e5\u9053\u82e5\u5148\u8bf4\u51fa\u81ea\u5df1\u7684\u884c\u85cf\uff0c\u7edd\u5bf9\u4e0d\u80fd\u6210\u4e8b\uff0c\u662f\u4ee5\u9690\u7740\u8eab\u4efd\uff0c\u60f3\u5230\u4e86\u5df2\u6210\u4e8b\u5b9e\u7684\u65f6 \u5019\uff0c\u518d\u8bf4\u51fa\u81ea\u5df1\u7684\u8eab\u4efd\u3002 \u3000\u3000\u54ea\u77e5\u5b50\u6bcd\u91d1\u68ad\u6b66\u80dc\u6587\u4e00\u542c\u4ed6\u624b\u4e0b\u7684\u4eba\u62c9\u4ed6\u5165\u6559\uff0c\u53c8\u8bf4\u51fa\u4ed6\u7684\u6765\u5386\uff0c\u4ed6\u53ef\u4e0d\u540c\u4e8e\u5148\u524d\u88ab\u4ed6 \u6253\u5012\u7684\u90a3\u51e0\u4eba\uff0c\u5927\u6012\u4e4b\u4e0b\uff0c\u7adf\u4e0d\u987e\u4e00\u5207\u5730\u53c8\u4e0a\u4e86\u53f0\u6765\u3002 \u3000\u3000\u5355\u638c\u65ad\u9b42\u76db\u6012\u4e2d\uff0c\u65cb\u5c55\u51fa\u201c\u5d06\u5cd2\u201d\u7edd\u5b66\u201c\u65ad\u9b42\u638c\u201d\uff0c\u5c06\u5b50\u6bcd\u91d1\u68ad\u903c\u5f97\u6ca1\u6709\u56de\u624b\u4e4b\u529b\uff0c\u773c \u770b\u5c31\u8981\u4e27\u5728\u4ed6\u7684\u638c\u4e0b\u3002 \u3000\u3000\u54ea\u77e5\u9053\u4e3b\u68da\u4e0a\uff0c\u98de\u63a0\u800c\u53bb\u4e00\u6761\u6781\u5feb\u7684\u8eab\u5f71\uff0c\u66fc\u5999\u5728\u7a7a\u4e2d\u5fae\u4e00\u8f6c\u62c6\uff0c\u5934\u4e0a\u811a\u4e0a\uff0c\u5237\u5730\u4e00 \u638c\uff0c\u786c\u751f\u751f\u5730\u5c06\u4e24\u4eba\u5206\u5f00\u3002 \u3000\u3000\u56db\u5ea7\u7fa4\u8c6a\u89c1\u4e86\u8fd9\u7edd\u9876\u8f7b\u529f\uff0c\u8f70\u7136\u559d\u8d77\u91c7\u6765\uff0c\u5355\u98de\u88ab\u4ed6\u5148\u58f0\u542c\u593a\uff0c\u500f\u5730\u505c\u624b\u4e00\u770b\uff0c\u5374\u539f\u6765 \u662f\u4e2a\u6587\u8d28\u5f6c\u5f6c\u7684\u5e74\u8f7b\u4eba\u3002 \u3000\u3000\u5355\u638c\u65ad\u9b42\u4e0d\u7531\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u8fd9\u7b97\u4ec0\u4e48\u610f\u601d\uff0c\u9601\u4e0b\u786c\u67b6\u6a2a\u6881\uff0c\u662f\u54ea\u4e00\u8def\u7684\u82f1\u96c4\u597d\u6c49\uff1f\u201d \u3000\u3000\u90a3\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u718a\u501c\uff0c\u539f\u662f\u65e0\u540d\u5c0f\u5352\uff0c\u600e\u80fd\u548c\u9601\u4e0b\u540d\u626c\u56db\u6d77\u7684\u5355\u638c\u65ad\u9b42\u5355\u575b \u4e3b\u76f8\u6bd4\uff1f\u201d \u3000\u3000\u5355\u98de\u4e00\u542c\u201c\u718a\u501c\u201d\u4e24\u5b57\uff0c\u5df2\u7136\u8272\u53d8\uff0c\u518d\u542c\u4ed6\u4e00\u8bed\u559d\u7834\u81ea\u5df1\u7684\u6240\u85cf\uff0c\u66f4\u662f\u9762\u8272\u5982\u4e0a\u3002 \u3000\u3000\u718a\u501c\u4e00\u4eae\u8f7b\u529f\uff0c\u4e00\u62a5\u4e07\u513f\uff0c\u56db\u5ea7\u7fa4\u8c6a\uff0c\u5374\u9ad8\u58f0\u559d\u8d77\u91c7\u6765\uff0c\u5148\u524d\u5728\u5ba2\u6808\u4e2d\u8ddf\u718a\u501c\u5439\u725b\u7684\u90a3 \u4e2a\u5706\u8138\u6c49\u5b50\uff0c\u4e00\u4f38\u820c\u5934\uff0c\u8bf4\uff1a\u201c\u597d\u5bb6\u4f19\uff0c\u539f\u6765\u718a\u501c\u5c31\u662f\u4ed6\u5440\uff0c\u53ef\u771f\u6709\u4e24\u4e0b\u5b50\u3002\u201d\u53ef\u662f\u4e00\u542c\u53e6 \u5916\u4e00\u4e2a\u7adf\u662f\u5929\u9634\u6559\u4e0b\u65b0\u624e\u8d77\u7684\u5355\u638c\u65ad\u9b42\uff0c\u5934\u4e00\u7f29\uff0c\u53c8\u8bf4\u4e0d\u51fa\u6d3b\u6765\u4e86\u3002 \u3000\u3000\u718a\u501c\u6717\u58f0\u9053\uff1a\u201c\u5728\u4e0b\u539f\u4e0d\u62df\u6765\u8d9f\u6d51\u6c34\uff0c\u53ea\u4e0d\u8fc7\u89c1\u4e0d\u5f97\u5929\u9634\u6559\u4e0b\u5728\u98de\u7075\u5821\u6492\u91ce\uff0c\u4e5f\u60f3\u9886\u6559 \u9601\u4e0b\u7684\u65ad\u9b42\u638c\u7f62\u4e86\uff0c\u6b63\u5982\u9601\u4e0b\u6240\u8bf4\u7684\u8981\u52a8\u624b\u5c31\u52a8\u624b\uff0c\u6211\u4eec\u4e5f\u4e0d\u5fc5\u591a\u5e9f\u8bdd\uff0c\u5c31\u8bf7\u9601\u4e0b\u8d50\u62db \u5427\u3002\uff0c\u5355\u98de\u751f\u6027\u672c\u4e5f\u6781\u505a\uff0c\u4f46\u718a\u501c\u6bd4\u4ed6\u66f4\u505a\uff0c\u4e09\u53e5\u6ca1\u8bf4\u5b8c\uff0c\u5c31\u8981\u52a8\u624b\uff0c\u5355\u98de\u6c14\u5f80\u4e0a\u649e\uff0c\u559d \u9053\uff1a\u201c\u597d\u6781\u4e86\uff0c\u6211\u5355\u67d0\u4eba\u5012\u8981\u770b\u770b\u9601\u4e0b\u6709\u4ec0\u4e48\u529f\u592b\u3002\u201d \u3000\u3000\u4e24\u4eba\u5251\u62d4\u5f2f\u5f20\uff0c\u5c55\u7fc5\u91d1\u9e4f\u53f9\u9053\uff1a\u201c\u771f\u662f\u82f1\u96c4\u51fa\u5c11\u5e74\uff0c\u8fd9\u4f4d\u718a\u5c11\u4fa0\u4e0d\u8bf4\u522b\u7684\uff0c\u5355\u53ea\u8fd9\u4efd\u8f7b \u529f\u548c\u80c6\u6c14\uff0c\u5c31\u53eb\u6211\u8001\u5934\u5b50\u4f69\u670d\u5f97\u5f88\u3002\u201d \u3000\u3000\u5ce8\u5d4b\u53cc\u5c0f\u91cc\u7684\u5f90\u5c0f\u5170\u671d\u4e1c\u65b9\u745b\u4e00\u6280\u773c\uff0c\u5a07\u7b11\u7740\u9053\uff1a\u201c\u5e78\u597d\u4f60\u6ca1\u6709\u548c\u4eba\u5bb6\u52a8\u624b\uff0c\u8981\u662f\u771f\u52a8 \u4e0a\u624b\uff0c\u4eca\u5929\u4f60\u7684\u82e6\u5934\u5c31\u7b97\u5403\u5b9a\u4e86\u3002\u201d \u3000\u3000\u4e1c\u65b9\u745b\u4e5f\u53cd\u5507\u9053\uff1a\u201c\u6211\u6253\u4e0d\u8fc7\u4eba\u5bb6\u5c31\u7b97\u4e86\uff0c\u4e0d\u50cf\u4f60\uff0c\u6253\u4e0d\u8fc7\u4eba\u5bb6\u7684\u65f6\u5019\uff0c\u5c31\u8d56\u7740\u8981\u4f60\u90a3 \u4f4d\u597d\u5e08\u54e5\u5e2e\u5fd9\u3002\u201d \u3000\u3000\u539f\u6765\u5f90\u5c0f\u5170\u548c\u5979\u5e08\u5144\u5b64\u5cf0\u4e00\u5251\u8fb9\u6d69\uff0c\u5df2\u751f\u60c5\u612b\uff0c\u662f\u4ee5\u4e1c\u65b9\u745b\u624d\u8fd9\u6837\u8bf4\u6765\u7b11\u5979\uff0c\u8c37\u5c0f\u9759\u542c \u4e86\u7b11\u5f97\u524d\u4ef0\u540e\u5408\uff0c\u5f90\u5c0f\u5170\u5374\u8001\u5230\u5f97\u5f88\uff0c\u4e00\u70b9\u4e5f\u4e0d\u52a8\u58f0\u8272\uff0c\u8fde\u8138\u90fd\u4e0d\u7ea2\u4e00\u7ea2\uff0c\u539f\u6765\u5979\u65e9\u88ab\u4eba\u5bb6 \u53d6\u7b11\u60ef\u4e86\u3002 \u3000\u3000\u5b50\u6bcd\u91d1\u68f1\u81ea\u95ee\u6280\u827a\u4e0d\u5982\u4eba\uff0c\u9eef\u7136\u8d70\u4e0b\u53f0\u53bb\uff0c\u718a\u501c\u5fae\u4e00\u6311\u624b\uff0c\u4fbf\u8981\u52a8\u624b\uff0c\u7a81\u5730\u201c\u5f53\u3001\u5f53\u201d \u8fdc\u5904\u4f20\u6765\u51e0\u4e0b\u6781\u5947\u5f02\u7684\u9523\u58f0\uff0c\u5355\u638c\u65ad\u9b42\u5355\u98de\u542c\u4e86\u9762\u8272\u9aa4\u53d8\uff0c\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u4eca\u65e5\u7a81\u6709\u8981 \u4e8b\uff0c\u4e0d\u80fd\u9886\u6559\u9601\u4e0b\u7684\u9ad8\u62db\uff0c\u9752\u5c71\u4e0d\u6539\uff0c\u53ea\u597d\u6539\u65e5\u518d\u5949\u966a\u4e86\u3002\u201d \u3000\u3000\u8bdd\u672a\u8bf4\u5b8c\uff0c\u811a\u5c16\u4e00\u987f\uff0c\u4e09\u8d77\u4e09\u843d\u7adf\u4f7f\u51fa\u201c\u873b\u8713\u4e09\u6284\u6c34\u201d\u7684\u7edd\u9876\u8f7b\u529f\uff0c\u5982\u98de\u800c\u53bb\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u8d70\uff0c\u7fa4\u8c6a\u4ff1\u90fd\u6115\u7136\u3002 \u3000\u3000\u718a\u501c\u4e5f\u662f\u4e00\u6115\uff0c\u4f46\u4f3c\u968f\u5373\u4f1a\u8fc7\u610f\u6765\uff0c\u4ed6\u6015\u60f9\u51fa\u522b\u7684\u662f\u975e\uff0c\u5fae\u4e00\u4f5c\u52bf\uff0c\u8eab\u5f62\u5982\u957f\u7736\u7ecf\u7a7a\uff0c \u63a0\u56de\u4e3b\u68da\uff0c\u7fa4\u8c6a\u53c8\u54c4\u7136\u53eb\u8d77\u597d\u6765\u3002 \u3000\u3000\u6731\u82e5\u5170\u89c1\u718a\u501c\u5982\u6b64\u8eab\u624b\uff0c\u7b11\u5f97\u5634\u90fd\u5408\u4e0d\u62e2\u6765\uff0c\u4e1c\u65b9\u7075\u4e5f\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u8f7b\u529f\u5982\u6b64\u597d\uff0c \u53ea\u6015\u2026\u2026\u201d \u3000\u3000\u5c55\u7fc5\u91d1\u9e4f\u4e00\u4f38\u59c6\u6307\uff0c\u63a5\u53e3\u8bf4\u9053\uff1a\u201c\u53ea\u6015\u4eca\u65e5\u6b66\u6797\u4e2d\u8f7b\u529f\u80fd\u80dc\u8fc7\u718a\u5c11\u4fa0\u7684\u6ca1\u6709\u51e0\u4e2a\u4eba \u4e86\u3002\u201d \u3000\u3000\u5c55\u7fc5\u91d1\u9e4f\u4ea6\u4ee5\u8f7b\u529f\u95fb\u540d\u6c5f\u6e56\uff0c\u6b64\u523b\u770b\u89c1\u718a\u501c\u4e4b\u8f7b\u529f\uff0c\u4ea6\u4e0d\u7981\u81ea\u53f9\u4e0d\u5982\u3002 \u3000\u3000\u4e1c\u65b9\u7075\u5ffd\u4f3c\u60f3\u8d77\u4e00\u4e8b\uff0c\u8d70\u51fa\u68da\u53bb\u8f6c\u4e86\u4e00\u8f6c\uff0c\u56de\u6765\u7b11\u9053\uff1a\u201c\u90a3\u4f4d\u84dd\u5927\u5148\u751f\u771f\u662f\u4e2a\u5947\u4eba\uff0c\u884c \u4e8b\u5982\u795e\u9f99\u89c1\u9996\u4e0d\u89c1\u5c3e\uff0c\u98d8\u7136\u4e00\u73b0\u5f71\u8e2a\uff0c\u6b64\u523b\u5df2\u8d70\u5f97\u4e0d\u77e5\u53bb\u5411\u4e86\uff0c\u5c0f\u5f1f\u5728\u897f\u68da\u627e\u4e86\u534a\u5929\uff0c\u4e5f\u6ca1 \u6709\u627e\u5230\u3002\u201d \u3000\u3000\u6709\u4e86\u65b9\u624d\u7684\u51e0\u573a\u6bd4\u6597\uff0c\u56db\u5ea7\u7fa4\u8c6a\uff0c\u4e00\u4e2a\u4e5f\u6ca1\u6709\u518d\u51fa\u624b\u7684\u4e86\uff0c\u4f46\u662f\u5927\u5bb6\u7b11\u8bed\u5171\u996e\uff0c\u591a\u534a\u90fd \u662f\u4ee5\u8fd9\u4e8c\u6b21\u51fa\u73b0\u6c5f\u6e56\u7684\u718a\u501c\u4e3a\u8bdd\u9898\u3002 \u3000\u3000\u90a3\u5706\u8138\u6c49\u5b50\u6b64\u523b\u53c8\u6bd4\u624b\u5212\u811a\u5730\u5439\u8d77\u725b\u6765\u3002 \u3000\u3000\u591c\u8272\u6e10\u6ee1\uff0c\u597d\u620f\u5df2\u6563\uff0c\u9152\u8db3\u996d\u9971\uff0c\u8fd9\u4e9b\u6c5f\u6e56\u4e0a\u7684\u8c6a\u5ba2\uff0c\u867d\u662f\u52a8\u4e0d\u52a8\u5c31\u73a9\u547d\u7684\u670b\u53cb\uff0c\u4f46\u5728 \u98de\u7075\u5821\u91cc\uff0c\u5374\u4e5f\u4e0d\u6562\u6ecb\u4e8b\uff0c\u800c\u4e14\u7ecf\u8fc7\u65b9\u624d\u90a3\u4e00\u756a\u4ed7\uff0c\u8c01\u4e5f\u6ca1\u6709\u518d\u63d0\u201c\u62db\u4eb2\u201d\u7684\u4e8b\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u573a\u7fa4\u8c6a\u5feb\u805a\uff0c\u603b\u7b97\u6ca1\u6709\u4ec0\u4e48\u592a\u5927\u7684\u5c94\u5b50\uff0c\u4f46\u662f\u718a\u501c\u5fc3\u4e2d\u5374\u751f\u8d77\u51e0\u4e2a\u95ee\u9898\uff0c\u90a3\u84dd\u5927\u5148 \u751f\u5982\u4f55\u5306\u5306\u4e00\u73b0\uff1f\u90a3\u5355\u638c\u65ad\u9b42\u4e3a\u4f55\u4e00\u542c\u9523\u58f0\u4fbf\u8d70\u4e86\uff1f\u90a3\u9523\u58f0\u662f\u4e0d\u662f\u4ee3\u8868\u7740\u5929\u9634\u6559\u4e3b\u592b\u5987\u5df2\u5230 \u82cf\u5dde\uff1f\u82e5\u771f\u662f\u4ed6\u4eec\u524d\u6765\u82cf\u5dde\uff0c\u53c8\u4e3a\u7684\u4f55\u4e8b\uff1f\u8fd9\u4e9b\u95ee\u9898\u4e00\u65f6\u5374\u4e5f\u5f97\u4e0d\u5230\u7b54\u6848\u3002 \u3000\u3000\u4e1c\u65b9\u745b\u7b11\u8bed\u6b22\u7136\uff0c\u5f90\u5c0f\u5170\uff0c\u8c37\u5c0f\u9759\u4e0d\u65f6\u6253\u7740\u8da3\uff0c\u718a\u501c\u5782\u5934\u6c89\u601d\u7740\uff0c\u62ac\u8d77\u5934\u6765\uff0c\u5374\u89c1\u68da\u4e2d \u5df2\u7ecf\u7a7a\u8361\u8361\u5730\u6ca1\u6709\u591a\u5c11\u4eba\u4e86\u3002 \u3000\u3000\u7fa4\u8c6a\u9646\u7eed\u6563\u5c3d\uff0c\u4e1c\u65b9\u7075\u4eb2\u81ea\u9001\u5230\u5e84\u95e8\uff0c\u6700\u540e\u56db\u4eea\u5251\u5ba2\u548c\u592a\u6e56\u7684\u5c55\u7fc5\u91d1\u9e4f\u4e0a\u5b98\u4e88\u4e5f\u8981\u8d70 \u4e86\uff0c\u51fa\u5c18\u5251\u5ba2\u518d\u4e09\u5730\u633d\u7559\u4ed6\u4eec\u5728\u98de\u7075\u5821\u6b47\u4e2a\u4e24\u5929\uff0c\u4f46\u4e0a\u5b98\u4e88\u6025\u4e8e\u56de\u53bb\uff0c\u56db\u4eea\u5251\u5ba2\u4e5f\u53e6\u6709\u4e8b\uff0c \u90fd\u8981\u8fde\u591c\u8d76\u56de\u53bb\uff0c\u4e1c\u65b9\u7075\u89c1\u633d\u7559\u4e0d\u4f4f\uff0c\u53ea\u5f97\u7f62\u4e86\u3002 \u3000\u3000\u6b64\u65f6\u867d\u521a\u521a\u8d77\u66f4\uff0c\u4f46\u591c\u8272\u5df2\u662f\u751a\u6d53\uff0c\u4e1c\u65b9\u7075\u7ad9\u5728\u5821\u524d\u7684\u5c0f\u6865\u4e0a\uff0c\u671b\u7740\u7fa4\u8c6a\u8eab\u5f71\u9010\u6e10\u6d88 \u5931\uff0c\u7ec8\u4e8e\u4ecd\u7136\u662f\u4e00\u7247\u9ed1\u6697\u3002 \u3000\u3000\u4ed6\u9ed8\u7136\u4ec1\u7acb\u5728\u90a3\u91cc\uff0c\u5fc3\u4e2d\u751f\u51fa\u8bb8\u591a\u611f\u6168\uff0c\u4e00\u79cd\u6b22\u805a\u540e\u7a81\u751f\u7684\u5bc2\u5bde\uff0c\u4f7f\u4ed6\u751f\u51fa\u4e86\u83ab\u540d\u7684\u60c6 \u6005\uff0c\u4ed6\u6697\u81ea\u5728\u611f\u6000\u7740\u3002 \u3000\u3000\u8bb8\u591a\u5e74\u6765\uff0c\u4ed6\u4ee5\u4ed6\u7684\u5fe0\u8bda\u548c\u6177\u6168\u7684\u4e2a\u6027\uff0c\u4ee5\u53ca\u8fc7\u4eba\u7684\u6b66\u529f\uff0c\u5728\u6c5f\u6e56\u4e0a\u5efa\u7acb\u4e86\u5a01\u540d\uff0c\u201c\u51fa \u5c18\u5251\u5ba2\u4e1c\u65b9\u7075\u201d\uff0c\u5728\u6b66\u4f11\u4e2d\u51e0\u4e4e\u5df2\u53d6\u4ee3\u4e86\u6614\u5e74\u6b66\u5f53\u638c\u6559\u5999\u4e00\u771f\u4eba\u7684\u5730\u4f4d\uff0c\u4f46\u4ecd\u7136\u662f\u5bc2\u5bde\u7684\u3002 \u3000\u3000\u8ddf\u968f\u5728\u4ed6\u540e\u9762\u7684\uff0c\u6c38\u8fdc\u662f\u4e00\u7fa4\u9644\u548c\u4ed6\u7684\uff0c\u751a\u81f3\u662f\u963f\u8c00\u4ed6\u7684\u4eba\u4eec\uff0c\u4f7f\u4ed6\u6709\u4e86\u4e00\u79cd\u9ad8\u9ad8\u5728\u4e0a \u7684\u611f\u89c9\uff0c\u4f46\u8fd9\u611f\u89c9\u662f\u7a7a\u865a\u7684\u3002 \u3000\u3000\u4ed6\u6e34\u671b\u7740\u53cb\u8c0a\uff0c\u4f46\u751a\u81f3\u662f\u4e00\u4efd\u6700\u666e\u901a\u7684\u4eba\u90fd\u80fd\u5f97\u5230\u7684\u90a3\u79cd\u7eaf\u771f\u7684\u53cb\u8c0a\uff0c\u5728\u4ed6\u5374\u662f\u90a3\u4e48\u5730 \u56f0\u96be\uff0c\u4ed6\u53d8\u5f97\u5b64\u72ec\u4e86\uff0c\u4eba\u4eec\u4e5f\u5728\u8bf4\u7740\uff0c\u51fa\u5c18\u5251\u5ba2\u662f\u5b64\u50b2\u7684\u4eba\uff0c\u4e8e\u662f\u4eba\u4eec\u79bb\u4ed6\u66f4\u8fdc\u4e86\u3002 \u3000\u3000\u4ed6\u5e76\u672a\u5341\u5206\u957f\u6210\u7684\u65f6\u5019\uff0c\u4ed6\u7236\u6bcd\u5c31\u90fd\u53bb\u4e16\u4e86\uff0c\u4ed6\u7684\u4eb2\u4eba\uff0c\u53ea\u6709\u4ed6\u7684\u59b9\u59b9\uff0c\u4ed6\u4ee5\u4ed6\u7684\u5168 \u5fc3\uff0c\u5168\u529b\u5730\u7231\u5979\uff0c\u53bb\u7ef4\u62a4\u5979\uff0c\u4f46\u8fd9\u4efd\u611f\u60c5\u3001\u5e76\u4e0d\u80fd\u586b\u8865\u4ed6\u5fc3\u7075\u4e0a\u7684\u7a7a\u865a\uff0c\u4ed6\u6e34\u671b\u7740\u4e00\u4efd\u7231\u4e0e \u88ab\u7231\u7684\u60c5\u611f\u3002 \u3000\u3000\u5c0f\u6865\u4e0b\u7684\u6d41\u6c34\uff0c\u7ec6\u788e\u800c\u7f13\u6162\u5730\u6d41\u8fc7\uff0c\u53d1\u51fa\u4e00\u79cd\u60a6\u8033\u7684\u6dd9\u6dd9\u58f0\uff0c\u4ed6\u60f3\uff1a\u201c\u8fd9\u591a\u4e48\u50cf\u5979\u8bf4\u8bdd \u7684\u58f0\u97f3\u5440\uff0c\u90a3\u4e48\u5730\u8f7b\u5de7\u800c\u7f13\u6162\u2026\u2026\u201d \u3000\u3000\u4ed6\u60f3\u7740\uff1a\u201c\u8fd9\u96be\u9053\u5c31\u662f\u6211\u591a\u5e74\u6e34\u671b\u7684\u60c5\u611f\u5417\uff1f\u5f53\u5979\u7684\u76ee\u5149\u8f7b\u8f7b\u5730\u63a0\u8fc7\u6211\u65f6\uff0c\u6211\u5c31\u4f1a\u89c9\u5f97 \u6709\u4e00\u79cd\u8bf4\u4e0d\u51fa\u7684\u5145\u5b9e\uff0c\u662f\u591a\u4e48\u6e29\u67d4\u7684\u76ee\u5149\u5440\uff0c\u4e3a\u4ec0\u4e48\u6211\u5728\u522b\u7684\u5973\u4eba\u8eab\u4e0a\uff0c\u5c31\u89c9\u4e0d\u5230\u8fd9\u79cd\u6e29\u67d4 \u5462\uff1f\u201d \u3000\u3000\u4eba\u7c7b\u7684\u611f\u60c5\uff0c\u6c38\u8fdc\u662f\u96be\u4ee5\u89e3\u91ca\u7684\uff0c\u5343\u767e\u5e74\u6765\uff0c\u6709\u5c11\u8bb8\u4eba\u8bd5\u7740\u53bb\u4e86\u89e3\uff0c\u4f46\u53c8\u6709\u8c01\u80fd\u89e3\u91ca \u5462\uff01\u8fd9\u6c38\u8fdc\u662f\u4e2a\u65e0\u6cd5\u77e5\u9053\u7684\u8c1c\u3002 \u3000\u3000\u4e1c\u65b9\u7075\u591a\u5e74\u6765\u6240\u89c1\u5230\u7684\u5973\u6027\uff0c\u5df2\u7ecf\u5f88\u591a\u4e86\uff0c\u5728\u4ed6\u5fc3\u91cc\uff0c\u4ece\u672a\u6fc0\u8d77\u8fc7\u4e00\u7247\u6d9f\u6f2a\uff0c\u4f46\u4eca\u5929\uff0c \u4ed6\u89c1\u5230\u82e5\u5170\uff0c\u8fd9\u7ecf\u53d7\u4e86\u65e0\u6570\u6467\u6b8b\u548c\u78e8\u96be\u7684\u5973\u5b50\uff0c\u90a3\u4e00\u4efd\u5e7d\u6028\u7684\u6e29\u67d4\uff0c\u5374\u4f7f\u5f97\u4e1c\u65b9\u7075\u503e\u5012\u4e0d \u5df2\u3002 \u3000\u3000\u4ed6\u6162\u6162\u5730\u8d70\u8ff8\u5821\u91cc\uff0c\u8fd9\u4e00\u4efd\u60c5\u611f\u4f7f\u5f97\u4ed6\u65e2\u559c\u60a6\uff0c\u4e5f\u5fe7\u90c1\uff0c\u4ed6\u4e0d\u77e5\u9053\u8be5\u600e\u6837\u53bb\u5e94\u4ed8\u5b83\uff0c\u4ed6 \u81ea\u601d\u9053\uff1a\u201c\u6211\u5bf9\u5979\u77e5\u9053\u7684\u662f\u90a3\u4e48\u5c11\uff0c\u751a\u81f3\u8fde\u5979\u662f\u4e0d\u662f\u5df2\u5ac1\u4e86\u4eba\u90fd\u4e0d\u77e5\u9053\uff0c\u718a\u501c\u548c\u6211\u9053\u4e49\u76f8 \u4ea4\uff0c\u5c06\u5979\u6258\u4ed8\u7ed9\u6211\uff0c\u6211\u53c8\u600e\u80fd\u5c06\u8fd9\u5fc3\u610f\u5411\u4ed6\u8bf4\u51fa\u5462\uff0c\u4ed6\u53c8\u600e\u80fd\u76f8\u4fe1\u6211\u5bf9\u4e00\u4e2a\u7b2c\u4e00\u6b21\u76f8\u89c1\u7684\u5973 \u5b50\uff0c\u4f1a\u6709\u8fd9\u6837\u7684\u60c5\u611f\uff0c\u82e5\u7136\u4ed6\u8bef\u4f1a\u4e86\uff0c\u5c82\u975e\u5c06\u6211\u5f53\u6210\u4e00\u4e2a\u4e58\u4eba\u4e8e\u5371\u7684\u6deb\u5f92\u3002\u201d \u3000\u3000\u4ed6\u60f3\u7740\u60f3\u7740\uff0c\u5df2\u8d70\u8ff8\u56ed\u91cc\uff0c\u8fd9\u665a\u867d\u65e0\u6708\u8272\uff0c\u4f46\u661f\u661f\u6781\u4eae\uff0c\u623f\u5b50\u91cc\u7684\u706f\u5149\u4ecd\u7136\u901a\u660e\uff0c\u800c\u4e14 \u9690\u9690\u6709\u7b11\u8bed\u4e4b\u58f0\uff0c\u4ed6\u77e5\u9053\u4ed6\u4eec\u65e9\u5df2\u56de\u6765\u4e86\u3002 \u3000\u3000\u4ed6\u8d70\u4e0a\u53f0\u9636\uff0c\u4e1c\u65b9\u745b\u5df2\u8fce\u4e86\u51fa\u6765\uff0c\u5a07\u7b11\u7740\u8bf4\uff1a\u201c\u4f60\u600e\u4e48\u5728\u5916\u9762\u803d\u4e86\u8fd9\u4e48\u4e45\uff0c\u6211\u4eec\u90fd\u7b49\u5f97 \u6025\u6b7b\u4e86\uff0c\u90a3\u4e9b\u4eba\u90fd\u8d70\u4e86\u5427\u3002\u201d \u3000\u3000\u4e1c\u65b9\u7075\u7b11\u7740\u8bf4\uff1a\u201c\u5176\u5b9e\u4ed6\u4eec\u65e9\u8d70\u4e86\uff0c\u53ea\u4e0d\u8fc7\u6211\u5728\u5916\u9762\u60f3\u7740\u4e00\u4ef6\u4e8b\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bf4\u5230\u8fd9\u91cc\uff0c\u4e00\u671b\u82e5\u5170\uff0c\u6070\u6070\u82e5\u5170\u6b64\u65f6\u4e5f\u5728\u770b\u7740\u4ed6\uff0c\u90a3\u79cd\u6210\u719f\u7684\u5987\u4eba\u6240\u7279\u6709\u7684\u6e29\u67d4\u76ee \u5149\uff0c\u4f7f\u5f97\u4e1c\u65b9\u7075\u5fc3\u5934\u6fc0\u7136\u7684\u8d77\u4e86\u4e00\u9635\u6ce2\u6d6a\uff0c\u4ed6\u8bb7\u8bb7\u5730\u5446\u7740\u4e86\uff0c\u76ee\u5149\u518d\u4e5f\u820d\u4e0d\u5f97\u79fb\u5411\u4ed6\u5904\u3002 \u3000\u3000\u6b64\u65f6\u623f\u91cc\u7684\u4eba\uff0c\u6bcf\u4eba\u5fc3\u5934\u90fd\u6709\u4e00\u4efd\u5fc3\u4e8b\uff0c\u4e1c\u65b9\u7075\u662f\u604d\u7136\u5982\u5728\u68a6\u4e2d\u3002\u82e5\u5170\u88ab\u4ed6\u7684\u76ee\u5149\u8fd9\u4e48 \u4e00\u770b\uff0c\u5979\u4e45\u5386\u98ce\u5c18\uff0c\u7537\u4eba\u5fc3\u4e2d\u7684\u4e8b\uff0c\u5982\u4f55\u770b\u4e0d\u51fa\u6765\uff1f\u6b64\u523b\u53ea\u89c9\u5fc3\u5934\u9e7f\u649e\uff0c\u4e0d\u77e5\u662f\u559c\u662f\u60ca\u3002 \u3000\u3000\u718a\u501c\u672c\u5c31\u6c89\u9ed8\uff0c\u6b64\u65f6\u4ed6\u5728\u60f3\u7740\u65e5\u540e\u6253\u7b97\uff0c\u5bf9\u7740\u5170\u548c\u4e1c\u65b9\u7075\u7684\u60c5\u666f\uff0c\u6839\u672c\u6ca1\u6709\u7406\u4f1a\uff0c\u4e1c\u65b9 \u745b\u5168\u795e\u671b\u7740\u718a\u501c\uff0c\u5fc3\u91cc\u53ea\u76fc\u671b\u7740\u718a\u501c\u80fd\u5bf9\u5979\u4e00\u8a00\u4e00\u7b11\uff0c\u522b\u7684\u4e8b\u90fd\u4e0d\u5728\u5979\u5fc3\u4e0a\u3002 \u3000\u3000\u53ea\u662f\u623f\u4e2d\u5374\u522b\u6709\u4e24\u4eba\uff0c\u5979\u4eec\u65c1\u89c2\u8005\u6e05\uff0c\u770b\u4e86\u5fc3\u4e2d\u5374\u53e6\u6709\u6ecb\u5473\u3002 \u3000\u3000\u539f\u6765\u5ce8\u5d4b\u53cc\u5c0f\u5374\u672a\u66fe\u56de\u53bb\uff0c\u5979\u4eec\u867d\u7136\u5168\u662f\u4e00\u8eab\u6b66\u529f\uff0c\u4f46\u7ec8\u7a76\u662f\u4e2a\u5973\u5b50\uff0c\u665a\u4e0a\u884c\u8def\u751a\u662f\u4e0d \u4fbf\uff0c\u4e1c\u65b9\u745b\u5c31\u7559\u5979\u4eec\u4f4f\u4e0b\u4e86\u3002 \u3000\u3000\u5f90\u5c0f\u5170\u8fd8\u4e0d\u5927\u600e\u6837\uff0c\u90a3\u8c37\u5c0f\u9759\u5374\u6068\u4e0d\u5f97\u6c38\u8fdc\u5728\u98de\u7075\u5821\u4f4f\u4e0b\u624d\u5bf9\u5fc3\u601d\uff0c\u539f\u6765\u5979\u5bf9\u4e1c\u65b9\u7075\uff0c \u65e9\u5df2\u4e00\u5f80\u60c5\u6df1\uff0c\u5979\u548c\u4e1c\u65b9\u745b\u672c\u662f\u624b\u5e15\u4e4b\u4ea4\uff0c\u4e24\u4eba\u65f6\u76f8\u8fc7\u4ece\uff0c\u4e1c\u65b9\u7075\u4e5f\u5c06\u5979\u5f53\u4f5c\u59b9\u5b50\u822c\u770b\u5f85\uff0c \u867d\u7136\u5979\u8c8c\u7f8e\u5982\u82b1\uff0c\u4f46\u4ed6\u5fc3\u4e2d\u5374\u672a\u751f\u8fc7\u4e1d\u6beb\u90aa\u5ff5\uff0c\u8c37\u5c0f\u9759\u867d\u7136\u5982\u6b64\uff0c\u4f46\u5979\u5230\u5e95\u662f\u5973\u513f\u5bb6\uff0c\u600e\u80fd \u5c06\u5fc3\u4e8b\u544a\u8bc9\u522b\u4eba\u3002 \u3000\u3000\u5979\u89c1\u4e1c\u65b9\u7075\u6b64\u523b\u5982\u75f4\u5982\u5446\u7684\u60c5\u5f62\uff0c\u5fc3\u91cc\u4e5f\u81ea\u6709\u6570\uff0c\u4e0d\u7981\u6697\u6697\u4e3a\u81ea\u5df1\u4f24\u5fc3\uff0c\u4f46\u5979\u7d20\u6027\u5014 \u5f3a\uff0c\u9762\u4e0a\u5374\u4e0d\u80af\u9732\u51fa\u6765\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u5404\u4eba\u90fd\u5728\u60f3\u7740\u81ea\u5df1\u7684\u5fc3\u4e8b\uff0c\u8c01\u4e5f\u6ca1\u6709\u51fa\u58f0\uff0c\u5f90\u5c0f\u5170\u770b\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u6251\u54e7\u4e00 \u58f0\uff0c\u7b11\u4e86\u51fa\u6765\u3002 \u3000\u3000\u201c\u8fd9\u4e00\u7b11\uff0c\u53ea\u628a\u623f\u4e2d\u7684\u4e94\u4eba\uff0c\u90fd\u7b11\u5f97\u8138\u7ea2\u4e86\u8d77\u6765\uff0c\u4e1c\u65b9\u745b\u53ea\u5f53\u5979\u5728\u7b11\u81ea\u5df1\uff0c\u7ea2\u7740\u8138\u4e0d\u4f9d \u9053\uff1a\u201c\u4f60\u7b11\u4ec0\u4e48\uff0c\u770b\u6211\u7b49\u4f1a\u53ef\u4f1a\u9976\u4f60\uff0c\u201d\u5f90\u5c0f\u5170\u542c\u4e86\uff0c\u66f4\u662f\u7b11\u5f97\u5f2f\u4e0b\u8170\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u54ce\u54df\uff01 \u4f60\u4eec\u770b\u8fd9\u4e2a\u4eba\uff0c\u4eba\u5bb6\u53c8\u4e0d\u662f\u7b11\u5979\uff0c\u5979\u81ea\u5df1\u505a\u8d3c\u5fc3\u865a\u8d77\u6765\u4e86\uff0c\u201d\u4e1c\u65b9\u82f1\u987f\u7740\u811a\u8bf4\u9053\uff1a\u201c\u4f60\u8fd8 \u8bb2\uff0c\u4f60\u4e0d\u662f\u7b11\u6211\uff0c\u662f\u7b11\u8c01\u5440\uff1f\u201d \u3000\u3000\u5f90\u5c0f\u5170\u9053\uff1a\u201c\u4f60\u53ea\u5f53\u8fd9\u623f\u5b50\u91cc\u5c31\u53ea\u6709\u4f60\u4e00\u4e2a\u624d\u597d\u7b11\u5440\u3002\u201d \u3000\u3000\u4e1c\u65b9\u745b\u8138\u4e0a\u66f4\u662f\u98de\u7ea2\uff0c\u5e72\u54b3\u4e86\u4e24\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u7b11\u4ec0\u4e48\uff0c\u8bf4\u51fa\u6765\u8ba9\u5927\u5bb6\u542c\u542c\u3002\u201d \u3000\u3000\u5f90\u5c0f\u5170\u5598\u7740\u6c14\u8bf4\uff1a\u201c\u597d\uff0c\u6211\u8bf4\u7ed9\u4f60\u4eec\u542c\uff0c\u4ece\u524d\u6709\u4e00\u4e2a\u4eba\u5440\u2026\u2026\u201d \u3000\u3000\u718a\u501c\u59cb\u7ec8\u90fd\u5728\u6115\u6115\u5730\u60f3\u7740\uff0c\u4ed6\u7a81\u7136\u60f3\u8d77\u4ed6\u59b9\u59b9\uff08\u4ed6\u59cb\u7ec8\u8ba4\u4e3a\u90a3\u8ddf\u7740\u5b9d\u9a6c\u795e\u97ad\u8428\u5929\u9aa5\u53ca\u5976 \u5988\u590f\u83b2\u8d1e\u800c\u53bb\u7684\u5973\u5b69\u5b50\uff0c\u662f\u4ed6\u59b9\u59b9\uff09\uff0c\u4ed6\u60f3\u7740\uff1a\u201c\u4e3a\u4ec0\u4e48\u6211\u59cb\u7ec8\u6ca1\u6709\u60f3\u8d77\u8fc7\u5979\uff0c\u53ef\u601c\u5979\u6b64\u523b \u843d\u5728\u90a3\u6076\u5f92\u624b\u4e0a\uff0c\u4e0d\u77e5\u88ab\u6298\u78e8\u6210\u4ec0\u4e48\u6837\u5b50\u4e86\uff01\u201d \u3000\u3000\u4ed6\u8d8a\u60f3\u8d8a\u6c14\uff0c\u731b\u5730\u4e00\u62cd\u684c\u5b50\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u62cd\u684c\u5b50\uff0c\u628a\u623f\u4e2d\u7684\u4eba\uff0c\u5168\u60ca\u5f97\u5446\u4f4f\u4e86\uff0c\u5f90\u5c0f\u5170\u53e3\u4e2d\u7684\u8bdd\uff0c\u4e5f\u88ab\u60ca\u56de\u8179\u91cc\uff0c\u5927\u5bb6\u90fd \u60ca\u5f02\u5730\u770b\u7740\u718a\u501c\uff0c\u4e0d\u77e5\u4ed6\u4e3a\u4f55\u7a81\u7136\u751f\u6c14\u4e86\u3002 \u3000\u3000\u4e1c\u65b9\u745b\u5a07\u55d4\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u600e\u4e48\u641e\u7684\uff0c\u4e00\u4f1a\u513f\u62cd\u684c\u5b50\uff0c\u4e00\u4f1a\u513f\u53c8\u7b11\u4e86\u3002\u201d \u3000\u3000\u718a\u501c\u53c8\u89c9\u5931\u6001\uff0c\u4e00\u65f6\u4e0d\u77e5\u8be5\u600e\u4e48\u8bf4\u624d\u597d\uff0c\u5f90\u5c0f\u5170\u5374\u53c8\u7b11\u9053\uff1a\u201c\u4eba\u5bb6\u5728\u60f3\u7740\u4f60\u5462\u3002\u201d \u3000\u3000\u4e1c\u65b9\u745b\u505a\u7740\u8981\u6253\u5f90\u5c0f\u5170\u7684\u6837\u5b50\uff0c\u8bf4\uff1a\u201c\u4f60\u8fd9\u4e2b\u5934\uff0c\u53c8\u5728\u56bc\u820c\u5934\u3002\u201d\u5fc3\u91cc\u5374\u9ad8\u5174\u5df2\u6781\uff0c\u5fcd \u4e0d\u4f4f\u7b11\u4e86\u51fa\u6765\uff0c\u773c\u89d2\u4e00\u779f\u718a\u501c\u3002 \u3000\u3000\u718a\u501c\u4f4e\u4e0b\u5934\u53bb\u3002 \u3000\u3000\u5f90\u5c0f\u5170\u53c8\u8bf4\uff1a\u201c\u5582\uff0c\u4f60\u522b\u6015\u96be\u4e3a\u60c5\u5440\uff0c\u8fd9\u6709\u4ec0\u4e48\u5173\u7cfb\uff0c\u6211\u4eec\u8fd9\u4f4d\u5927\u59b9\u5b50\uff0c\u8fd8\u4e0d\u662f\u4e00\u5929\u5230 \u665a\u60f3\u7740\u4f60\uff0c\u90fd\u5feb\u60f3\u75af\u4e86\u3002\u201d \u3000\u3000\u4e1c\u65b9\u745b\u518d\u662f\u8138\u539a\uff0c\u4e5f\u7ecf\u4e0d\u4f4f\u5f90\u5c0f\u5170\u8fd9\u6837\u7684\u6253\u8da3\uff0c\u5624\u549b\u4e00\u58f0\uff0c\u8dd1\u5230\u540e\u9762\u53bb\u4e86\u3002 \u3000\u3000\u718a\u501c\u8fd9\u4e00\u60ca\uff0c\u5374\u975e\u540c\u5c0f\u53ef\uff0c\u4e1c\u65b9\u745b\u5bf9\u4ed6\u7684\u60c5\u610f\uff0c\u4ed6\u4e1d\u6beb\u4e0d\u77e5\uff0c\u6b64\u523b\u77e5\u9053\u4e86\uff0c\u5374\u4e0d\u77e5\u600e\u751f \u624d\u597d\uff0c\u4ed6\u6697\u81ea\u601d\u7d22\u7740\uff1a\u201c\u8fd9\u771f\u662f\u51fa\u4e4e\u6211\u610f\u6599\u4e4b\u5916\uff0c\u65e9\u77e5\u5982\u6b64\uff0c\u6211\u5c31\u4e0d\u4f1a\u5c06\u7740\u5170\u59d0\u9001\u5230\u6b64\u95f4\uff0c \u6211\u73b0\u5728\u5fc3\u60c5\u5982\u6b64\uff0c\u600e\u4e48\u6d88\u53d7\u5f97\u4e86\u5979\u8fd9\u756a\u60c5\u610f\u3002\u4e00\u4e2a\u5e94\u4ed8\u4e0d\u597d\uff0c\u5c82\u4e0d\u53c8\u662f\u9ebb\u70e6\uff0c\u6211\u548c\u5979\u76f8\u89c1\u4ec5 \u4ec5\u4e24\u9762\uff0c\u5979\u53c8\u600e\u4f1a\u5bf9\u6211\u5982\u6b64\u5462\uff1f\u6211\u867d\u7136\u5bf9\u5979\u6ca1\u6709\u6076\u611f\uff0c\u4f46\u662f\u7ecf\u8fc7\u82e5\u99a8\u7684\u53d8\u6545\uff0c\u60c5\u611f\u4e0a\u7684\u4e8b\uff0c \u6211\u5df2\u7ec8\u751f\u4e0d\u60f3\u7275\u7f20\u4e86\u3002\u201d \u3000\u3000\u5404\u4eba\u5750\u4e86\u4e00\u4f1a\uff0c\u5fc3\u4e2d\u5404\u6709\u5fc3\u4e8b\uff0c\u54ea\u6709\u5fc3\u60c5\u8c08\u8bdd\uff0c\u5404\u90fd\u5b89\u6b47\u4e86\u3002 \u3000\u3000\u718a\u501c\u56de\u5230\u4e1c\u65b9\u7075\u4e3a\u4ed6\u5b89\u6392\u7684\u623f\u91cc\uff0c\u60f3\u4e86\u8bb8\u4e45\uff0c\u89c9\u5f97\u4e8b\u5df2\u81f3\u6b64\uff0c\u60df\u6709\u4e00\u8d70\u4e86\u4e4b\uff0c\u672c\u60f3\u7559\u4e2a \u5b57\u67ec\u4f46\u53c8\u6614\u65e0\u7eb8\u7b14\uff0c\u53ea\u5f97\u7f62\u4e86\u3002 \u3000\u3000\u4ed6\u63a8\u5f00\u7a97\uff0c\u7a97\u5916\u661f\u5149\u4ecd\u4eae\uff0c\u4ed6\u77e5\u9053\u8fd9\u623f\u5b50\u91cc\u6240\u7761\u7684\uff0c\u4ff1\u662f\u8eab\u8d1f\u7edd\u827a\u7684\u9ad8\u4eba\uff0c\u53ea\u8981\u7a0d\u52a0\u54cd \u52a8\uff0c\u4fbf\u4f1a\u88ab\u4eba\u77e5\u6653\uff0c\u4f46\u4ed6\u81ea\u8d1f\u201c\u6f5c\u5f62\u9041\u5f71\u201d\u8f7b\u529f\u5999\u7edd\u5929\u4e0b\uff0c\u5168\u672a\u4efb\u4f55\u4f5c\u52bf\uff0c\u4eba\u5df2\u98d8\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4ed6\u65bd\u5c55\u8d77\u8eab\u6cd5\uff0c\u6781\u5feb\u5730\u79bb\u5f00\u4e86\u98de\u7075\u5821\uff0c\u522b\u8bf4\u6ca1\u6709\u4eba\u770b\u89c1\uff0c\u5373\u4f7f\u6709\u4eba\u89c1\u4e86\u3001\u4e5f\u53ea\u662f\u89c1\u5f97\u4e00 \u6761\u8f7b\u6de1\u7684\u5f71\u5b50\u3001\u604d\u773c\u4fbf\u65e0\u8e2a\u8ff9\u3002 \u3000\u3000\u6b64\u523b\u591c\u6b63\u6df1\uff0c\u56db\u91ce\u4e00\u7247\u9759\u5bc2\uff0c\u4ed6\u7a81\u7136\u60f3\u8d77\uff0c\u6b64\u523b\u6d6a\u8ff9\u5929\u6daf\uff0c\u4ed6\u8eab\u4e0a\u7684\u94f6\u4e24\uff0c\u8fd8\u662f\u5f53\u5e74\u82e5 \u99a8\u548c\u5434\u8bcf\u4e91\u5728\u79bb\u522b\u7684\u65f6\u6240\u8d60\u7684\uff0c\u73b0\u5df2\u6240\u5b58\u65e0\u51e0\uff0c\u800c\u4e14\u98d8\u6cca\u6c5f\u6e56\uff0c\u5fc5\u5b9a\u8981\u6709\u5339\u5750\u9a91\u624d\u884c\u3002 \u3000\u3000\u4ed6\u672c\u60f3\u518d\u8fd4\u56de\u5821\u91cc\uff0c\u53d6\u51fa\u4ed6\u6240\u9a91\u6765\u7684\u9a6c\uff0c\u4f46\u53c8\u6015\u60ca\u52a8\u4e86\u4eba\uff0c\u4ed6\u81ea\u601d\u9053\uff1a\u201c\u53cd\u6b63\u6b64\u540e\u662f\u771f \u6b63\u7684\u65e0\u542c\u7275\u6302\u4e86\uff0c\u5929\u4e0b\u4e4b\u5927\uff0c\u4f55\u5904\u6ca1\u6709\u5bb9\u8eab\u4e4b\u5904\uff0c\u53ea\u8981\u6211\u80fd\u5bfb\u7740\u8428\u5929\u9aa5\uff0c\u518d\u5bfb\u5f97\u6211\u7684\u59b9\u59b9\uff0c \u5c31\u662f\u518d\u5927\u7684\u6614\uff0c\u6211\u4e5f\u80fd\u53bb\u5fcd\u53d7\u5b83\uff0c\u4f60\u53c8\u4f55\u5fc5\u4e3a\u8d2a\u56fe\u65c5\u9014\u4e0a\u7684\u8212\u9002\uff0c\u800c\u53bb\u62db\u60f9\u9ebb\u70e6\u5462\uff01\u201d \u3000\u3000\u4ed6\u56de\u5934\u671b\u4e86\u5728\u9ed1\u6697\u4e2d\u663e\u5f97\u5f02\u5e38\u9759\u5bc2\u7684\u98de\u7075\u5821\u4e00\u773c\uff0c\u5fc3\u4e2d\u5374\u5728\u60f3\u7740\u6b64\u523b\u6015\u5df2\u719f\u7761\u4e86\u7684\u82e5 \u5170\uff0c\u4ed6\u60f3\u9053\uff1a\u201c\u73b0\u5728\u4e00\u522b\uff0c\u6211\u4e0d\u77e5\u4f55\u65f6\u518d\u80fd\u89c1\u4f60\uff0c\u51fa\u5c18\u5251\u5ba2\u4e1c\u65b9\u7075\uff0c\u4fa0\u58f0\u4f20\u9882\u6c5f\u5357\uff0c\u6211\u76f8\u4fe1 \u4ed6\u4f1a\u597d\u597d\u770b\u987e\u4f60\u7684\uff0c\u65e5\u540e\u82e5\u6709\u673a\u7f18\uff0c\u6211\u5fc5\u518d\u6765\u770b\u4f60\u3002\u201d \u3000\u3000\u4ed6\u4ef0\u5929\u957f\u957f\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u50cf\u662f\u89c9\u5f97\u65e0\u6bd4\u7684\u8f7b\u677e\uff0c\u53c8\u50cf\u662f\u5931\u843d\u4e86\u4ec0\u4e48\uff0c\u8bb8\u591a\u5e74\u6765\uff0c\u60c5\u611f\u4e0a \u7684\u7ea0\u7f20\uff0c\u867d\u5df2\u4e86\u5374\uff0c\u4f46\u5374\u7edd\u975e\u4ed6\u6240\u613f\u610f\u4e86\u5374\u7684\u3002 \u3000\u3000\u6b64\u523b\u56db\u91ce\u65e0\u4eba\uff0c\u6b63\u662f\u53ef\u4ee5\u65bd\u5c55\u8f7b\u8eab\u4e4b\u672f\u7684\u65f6\u5019\uff0c\u4f46\u4ed6\u5e76\u65e0\u76ee\u7684\u4e4b\u5730\uff0c\u65bd\u7136\u6cbf\u7740\u5927\u8def\u8d70 \u7740\uff0c\u5fc3\u4e2d\u7a7a\u8361\u8361\u7684\uff0c\u4e00\u65e0\u6240\u5ff5\u3002 \u3000\u3000\u4ed6\u7a7f\u7740\u7684\u539f\u662f\u5112\u751f\u88c5\u675f\uff0c\u968f\u8eab\u7684\u8863\u7269\uff0c\u4ed6\u5df2\u7528\u5e03\u5305\u8d77\uff0c\u8d70\u8fdb\u82cf\u5dde\u57ce\u65f6\uff0c\u5929\u5df2\u5feb\u4eae\u4e86\uff0c\u4ed6 \u5c06\u8eab\u540e\u7684\u957f\u5251\u64a4\u4e0b\uff0c\u4e5f\u7528\u5e03\u5305\u597d\u4e86\uff0c\u968f\u610f\u5728\u8857\u4e0a\u95f2\u8361\u7740\u3002 \u3000\u3000\u4ed6\u6e9c\u8fbe\u4e86\u4e00\u4f1a\uff0c\u8def\u4e0a\u884c\u4eba\u6e10\u591a\uff0c\u5e97\u94fa\u4e5f\u7eb7\u7eb7\u5f00\u95e8\uff0c\u4ed6\u81ea\u670d\u4e86\u201c\u6210\u5f62\u9996\u4e4c\u201d\u4e4b\u540e\uff0c\u9965\u5bd2\u4e24 \u5b57\uff0c\u5df2\u4e0d\u653e\u5728\u5fc3\u4e0a\uff0c\u662f\u4ee5\u4ed6\u867d\u884c\u8d70\u4e86\u4e00\u591c\uff0c\u4e5f\u4e0d\u89c9\u5f97\u75b2\u52b3\u3001\u9965\u997f\uff0c\u4ed6\u4e45\u95fb\u82cf\u5dde\u4e43\u9c7c\u7c73\u4e4b\u4e61\uff0c \u6b64\u523b\u4e00\u89c1\u679c\u7136\u5e02\u9762\u7e41\u8363\uff0c\u884c\u4eba\u6ee1\u5634\u5434\u4fac\u8f6f\u8bed\uff0c\u542c\u6765\u522b\u6709\u9189\u4eba\u4e4b\u5904\u3002 \u3000\u3000\u7a81\u7136\u8def\u8fb9\u7684\u8336\u9986\u91cc\uff0c\u51b2\u51fa\u6765\u4e00\u4eba\uff0c\u4e00\u628a\u62c9\u4f4f\u718a\u501c\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u627e\u5f97\u4f60\u597d\u82e6\u5440\uff01\u201d \u3000\u3000\u718a\u501c\u4e00\u60ca\uff0c\u8f6c\u8138\u4e00\u770b\uff0c\u5374\u539f\u6765\u662f\u65e5\u524d\u5728\u5ba2\u6808\u4e2d\u6240\u9047\u5230\u7684\u90a3\u4e2a\u5706\u8138\u6c49\u5b50\u3002 \u3000\u3000\u90a3\u4eba\u9047\u5230\u718a\u501c\uff0c\u4eff\u4f5b\u751a\u559c\uff0c\u7b11\u9053\uff1a\u201c\u771f\u662f\u4eba\u751f\u4f55\u5904\u4e0d\u76f8\u9022\uff1a\u6211\u518d\u4e5f\u60f3\u4e0d\u5230\u5144\u53f0\u5c31\u662f\u718a\u501c \u718a\u5927\u4fa0\uff0c\u4f60\u6211\u4e00\u89c1\u5982\u6545\uff0c\u4e5f\u771f\u7b97\u662f\u6709\u7f18\u4e86\u3002\u201d \u3000\u3000\u8bf4\u7740\u4ed6\u5c31\u5c06\u718a\u501c\u62c9\u8fdb\u8336\u9986\uff0c\u718a\u501c\u89c1\u4ed6\u81ea\u8a00\u81ea\u8bed\uff0c\u5fc3\u60f3\u6b64\u4eba\u5012\u771f\u662f\u5929\u771f\u6709\u8da3\uff0c\u65e2\u88ab\u4ed6\u62c9 \u7740\uff0c\u53cd\u6b63\u65e0\u4e8b\uff0c\u5c31\u968f\u4ed6\u8d70\u8fdb\u8336\u9986\u3002 \u3000\u3000\u54ea\u77e5\u90a3\u4eba\u4e00\u8fdb\u8336\u9986\uff0c\u5c31\u5927\u58f0\u56b7\u9053\uff1a\u201c\u6211\u7ed9\u5927\u5bb6\u4ecb\u7ecd\u4e00\u4e2a\u60ca\u5929\u52a8\u5730\u7684\u82f1\u96c4\uff0c\u5404\u4f4d\u770b\u7740\uff0c\u8fd9 \u4f4d\u662f\u6211\u7684\u597d\u670b\u53cb\uff0c\u540d\u626c\u56db\u6d77\u7684\u718a\u501c\uff0c\u5404\u4f4d\uff0c\u4e0d\u662f\u6211\u521a\u624d\u5439\u725b\uff0c\u6211\u5c0f\u871c\u8702\u9648\u4e30\u867d\u7136\u4e0d\u884c\uff0c\u4f46\u4ea4 \u7684\u5374\u5168\u90fd\u662f\u54cd\u5f53\u5f53\u7684\u597d\u6c49\u3002\u201d\u8bf4\u5b8c\u5f97\u610f\u5730\u5927\u7b11\u3002 \u3000\u3000\u718a\u501c\u7709\u5934\u4e00\u76b1\u77e5\u9053\u4ed6\u5fc5\u5b9a\u53c8\u5728\u8336\u9986\u4e2d\u5439\u725b\u4e86\uff0c\u82e5\u4e86\u7978\uff0c\u62ff\u81ea\u5df1\u6765\u5f53\u6321\u7bad\u724c\u4e86\u3002 \u3000\u3000\u679c\u7136\u4e0d\u51fa\u6240\u6599\uff0c\u6709\u4eba\u91cd\u91cd\u5730\u54fc\u4e86\u4e00\u58f0\uff0c\u718a\u501c\u4e00\u671b\uff0c\u53ea\u89c1\u4e34\u8857\u7684\u684c\u4e0a\uff0c\u5750\u4e86\u4e24\u4e2a\u9ed1\u8863\u5927 \u6c49\uff0c\u54fc\u58f0\u7684\u5c31\u662f\u6b64\u4e8c\u4eba\u3002 \u3000\u3000\u5c0f\u871c\u8702\u9648\u4e30\u89c1\u8fd9\u4e24\u4eba\u4e00\u54fc\uff0c\u50cf\u662f\u6709\u70b9\u5bb3\u6015\uff0c\u5fd9\u53c8\u62c9\u7740\u718a\u501c\u5750\u5230\u4f4d\u4e0a\uff0c\u53eb\u5802\u500c\u9001\u6765\u8bb8\u591a\u5403 \u98df\uff0c\u718a\u501c\u89c1\u4e8b\u5df2\u81f3\u6b64\uff0c\u4e5f\u8bf4\u4e0d\u4e0a\u4ec0\u4e48\u6765\u4e86\u3002 \u3000\u3000\u718a\u501c\u89c1\u90a3\u4e24\u4e2a\u9ed1\u8863\u5927\u6c49\uff0c\u867d\u4e5f\u662f\u5750\u5728\u90a3\u91cc\u559d\u8336\uff0c\u5374\u662f\u4e0e\u4f17\u4e0d\u540c\u7684\u559d\u6cd5\uff0c\u4ed6\u4eec\u4e24\u4eba\u559d\u8336\u7684 \u8336\u676f\uff0c\u7ade\u662f\u4e24\u4e2a\u8336\u676f\u53e0\u5728\u4e00\u8d77\uff0c\u5fc3\u4e2d\u4e0d\u7981\u602a\u9053\uff1a\u201c\u54ea\u6709\u4eba\u559d\u8336\u662f\u8fd9\u7b49\u559d\u6cd5\u7684\uff1f\u201d \u3000\u3000\u90a3\u4e24\u4eba\u6b63\u5728\u6076\u72e0\u72e0\u5730\u671b\u7740\u718a\u501c\uff0c\u5176\u4e2d\u4e00\u4eba\u5ffd\u5730\u7ad9\u4e86\u8d77\u6765\uff0c\u5306\u5306\u5411\u5916\u8d70\u53bb\u3002 \u3000\u3000\u5c0f\u871c\u8702\u89c1\u4e86\uff0c\u795e\u8272\u5927\u53d8\uff0c\u867d\u7136\u4ecd\u548c\u718a\u501c\u8c08\u592d\u8bf4\u5730\uff0c\u58f0\u97f3\u5374\u5fae\u5fae\u53d1\u98a4\u4e86\u3002 \u3000\u3000\u4e0d\u4e00\u4f1a\uff0c\u5148\u524d\u8d70\u51fa\u7684\u9ed1\u8863\u5927\u6c49\uff0c\u53c8\u9886\u4e86\u4e00\u4eba\u56de\u6765\uff0c\u90a3\u4eba\u6de1\u91d1\u8272\u7684\u9762\u5b54\uff0c\u50cf\u662f\u5927\u75c5\u521d\u6108\u4f3c \u7684\uff0c\u4e5f\u662f\u4e00\u8eab\u9ed1\u8863\uff0c\u795e\u8272\u5028\u50b2\u5df2\u6781\u3002 \u3000\u3000\u718a\u501c\u5ff5\u5934\u4e00\u8f6c\uff0c\u5fd6\u9053\uff1a\u201c\u96be\u9053\u53c8\u662f\u90a3\u8bdd\u513f\u2026\u2026\uff0c\u8336\u9986\u4e2d\u559d\u8336\u7684\u8336\u5ba2\uff0c\u89c1\u5230\u6b64\u4eba\u6765\u4e86\uff0c\u4ff1 \u90fd\u7a81\u7136\u95f7\u58f0\u4e0d\u54cd\uff0c\u90a3\u4eba\u5374\u66f4\u5947\u602a\uff0c\u53eb\u5802\u500c\u9001\u6765\u4e94\u53ea\u8336\u676f\uff0c\u53e0\u5728\u4e00\u8d77\uff0c\u5728\u6700\u4e0a\u9762\u7684\u4e00\u676f\u5012\u6ee1\u4e86 \u8336\uff0c\u65c1\u82e5\u65e0\u4eba\u7684\u559d\u8d77\u8336\u6765\uff0c\u559d\u6765\u55b7\u55b7\u6709\u58f0\u3002\u5c0f\u871c\u8702\u9648\u4e30\u614c\u5fd9\u5730\u7ad9\u8d77\u6765\uff0c\u62c9\u7740\u718a\u501c\u8bf4\uff1a\u201c\u718a\u5927 \u54e5\uff0c\u6211\u4eec\u8336\u559d\u5b8c\uff0c\u5750\u7740\u4e5f\u6ca1\u610f\u601d\uff0c\u8fd8\u662f\u8d70\u5427\uff01\u201d\u4ed6\u6108\u6765\u6108\u4eb2\u70ed\uff0c\u5c45\u7136\u53eb\u8d77\u5927\u54e5\u6765\u3002 \u3000\u3000\u4ed6\u8bdd\u521a\u8bb2\u5b8c\uff0c\u90a3\u4eba\u9634\u607b\u607b\u5730\u8bf4\u9053\uff1a\u201c\u522b\u8d70\uff0c\u4f60\u8fc7\u6765\uff0c\u6211\u95ee\u4f60\u51e0\u53e5\u8bdd\u3002\u201d \u3000\u3000\u5c0f\u871c\u8702\u9648\u4e30\u5413\u5f97\u4e24\u817f\u53d1\u8f6f\uff0c\u72ec\u81ea\u5634\u786c\u9053\uff1a\u201c\u6211\u4e0d\u8ba4\u8bc6\u79f0\uff0c\u4f60\u95ee\u6211\u4ec0\u4e48\u8bdd\uff1f\u201d \u3000\u3000\u90a3\u4eba\u4e00\u62cd\u684c\u5b50\uff0c\u5389\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u8fc7\u6765\u4e0d\u8fc7\u6765\uff1f\u201d \u3000\u3000\u5c0f\u871c\u8702\u6c42\u52a9\u5730\u671b\u4e86\u718a\u501c\u4e00\u773c\uff0c\u718a\u501c\u4e5f\u89c9\u5f97\u6b64\u4eba\u592a\u8fc7\u6a2a\u86ee\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e0d\u8fc7\u53bb\u53c8\u600e \u6837\uff1f\u201d \u3000\u3000\u90a3\u4eba\u9634\u607b\u607b\u5730\u5e72\u7b11\u4e86\u51e0\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u6781\u4e86\uff0c\u597d\u6781\u4e86\uff0c\u60f3\u4e0d\u5230\u82cf\u5dde\u57ce\u91cc\uff0c\u8fd8\u6709\u6562\u5411\u6211\u91d1 \u9762\u97e6\u9a6e\u4e8e\u660e\u53eb\u9635\u7684\u4eba\u7269\u3002\u201d \u3000\u3000\u718a\u501c\u4fca\u76ee\u4e00\u77aa\uff0c\u6012\u9053\uff1a\u201c\u7ba1\u4f60\u662f\u4ec0\u4e48\u73a9\u610f\uff0c\u5c0f\u7237\u4eca\u5929\u8981\u6559\u8bad\u6559\u8bad\u4f60\u3002\u201d \u3000\u3000\u5168\u9762\u97e6\u9a6e\u4e8e\u660e\u4e00\u62cd\u684c\u5b50\uff0c\u7ad9\u4e86\u8d77\u6765\uff0c\u90a3\u8336\u9986\u7684\u684c\u5b50\u672c\u6765\u4e0d\u7ed3\u5b9e\uff0c\u54d7\u5566\u4e00\u58f0\uff0c\u584c\u4e86\u4e0b\u6765\uff0c \u4e8e\u660e\u4e5f\u4e0d\u7ba1\uff0c\u6012\u559d\u9053\uff1a\u201c\u5c0f\u5b50\u4f60\u5012\u771f\u72c2\uff01\u201d \u3000\u3000\u718a\u501c\u9053\uff1a\u201c\u72c2\u53c8\u600e\u5730\uff1f\u201d \u3000\u3000\u8336\u9986\u91cc\u7684\u8336\u5ba2\uff0c\u4e00\u770b\u82d7\u5934\u4e0d\u5bf9\uff0c\u4e00\u4e2a\u4e2a\u811a\u5e95\u63e9\u6cb9\uff0c\u6e9c\u4e4b\u5927\u5409\u3002 \u3000\u3000\u4e8e\u660e\u4e00\u57ab\u6b65\uff0c\u7a9c\u51fa\u8336\u9986\uff0c\u8bf4\u9053\uff1a\u201c\u6765\u6765\uff0c\u6211\u5012\u8981\u770b\u770b\u4f60\u662f\u4ec0\u4e48\u53d8\u7684\u3002\u201d \u3000\u3000\u718a\u501c\u89c1\u4ed6\u4e0d\u4f46\u5168\u8eab\u9ed1\u8863\uff0c\u8fde\u978b\u90fd\u662f\u9ed1\u8272\u7684\uff0c\u66f4\u65ad\u5b9a\u4e86\u81ea\u5df1\u7684\u60f3\u6cd5\uff0c\u8bf4\u9053\uff1a\u201c\u76f8\u597d\u7684\uff0c\u77a7 \u4f60\u8fd9\u8eab\u6253\u626e\uff0c\u4e00\u5b9a\u53c8\u662f\u5929\u9634\u6559\u4e0b\u7684\u4e09\u6d41\u89d2\u8272\uff0c\u7237\u5012\u8981\u770b\u770b\u5929\u9634\u6559\u91cc\u7684\u4eba\u7269\uff0c\u7a76\u7adf\u662f\u600e\u6837\u7684\u8eab \u624b\uff0c\u5149\u5929\u5316\u65e5\u4e4b\u4e0b\uff0c\u5c31\u8bb8\u968f\u4fbf\u6b3a\u8d1f\u4eba\u3002\u201d \u3000\u3000\u4e8e\u660e\u4ef0\u5929\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5b50\u5012\u6709\u51e0\u5206\u773c\u529b\uff0c\u5927\u7237\u5c31\u662f\u5929\u9634\u6559\u82cf\u5dde\u8235\u7684\u8235\u4e3b\uff0c\u76f8 \u597d\u7684\u4e5f\u62a5\u4e2a\u4e07\u513f\u5427\u3002\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u9ed1\u8863\u5927\u6c49\u5728\u65c1\u8fb9\u8bf4\u9053\uff1a\u201c\u8235\u4e3b\uff0c\u8fd9\u4e2a\u5c31\u662f\u53eb\u718a\u501c\u7684\u5c0f\u5b50\u3002\u201d \u3000\u3000\u4e8e\u660e\u9053\uff1a\u201c\u54e6\uff01\u602a\u4e0d\u5f97\u4f60\u8fd9\u4e48\u72c2\uff0c\u539f\u6765\u4f60\u5c31\u662f\u718a\u501c\uff0c\u5f53\u5e74\u4f60\u867d\u7136\u5728\u6211\u5929\u9634\u6559\u4e0b\u6f0f\u7f51\uff0c\u4eca \u5929\u53ef\u5bb9\u4e0d\u5f97\u4f60\u6492\u91ce\u4e86\u3002\u201d \u3000\u3000\u718a\u501c\u5fae\u4e00\u6c89\u541f\uff1a\u201c\u770b\u6837\u5b50\uff0c\u90a3\u5929\u9634\u6559\u4e3b\u5374\u4f3c\u672a\u5728\u82cf\u5dde\uff0c\u4e0d\u7136\u60f3\u5fc5\u4e0d\u4f1a\u751f\u51fa\u6b64\u4e8b\u3002\u201d \u3000\u3000\u4ed6\u56db\u5468\u4e00\u671b\uff0c\u8857\u4e0a\u7a7a\u8361\u8361\u7684\uff0c\u884c\u4eba\u90fd\u7ed5\u8def\u800c\u884c\uff0c\u90a3\u5c0f\u871c\u8702\u9648\u4e30\uff0c\u4e5f\u4e58\u673a\u6e9c\u8d70\u4e86\uff0c\u5fc3\u4e2d\u4e0d \u7981\u53c8\u662f\u597d\u6c14\uff0c\u53c8\u662f\u597d\u7b11\uff0c\u81ea\u5df1\u4e3a\u4ed6\u5e73\u767d\u65e0\u6545\u5730\uff0c\u53c8\u60f9\u4e86\u4e00\u573a\u7ea0\u7eb7\uff0c\u4ed6\u5374\u7529\u624b\u4e00\u6e9c\u4e86\u4e4b\u3002 \u3000\u3000\u91d1\u9762\u97e6\u9a6e\u4e8e\u660e\uff0c\u4f38\u624b\u4e00\u63a2\u8170\u95ee\uff0c\u6492\u51fa\u4e00\u4ef6\u6781\u5947\u602a\u7684\u5916\u95e8\u5175\u5203\uff0c\u4f3c\u97ad\u975e\u97ad\uff0c\u4f3c\u5251\u975e\u5251\uff0c\u8fce \u98ce\u4e00\u6296\uff0c\u4f38\u5f97\u7b14\u76f4\uff0c\u7adf\u662f\u7529\u767e\u7ec3\u7cbe\u94a2\u6253\u9020\u7684\uff0c\u539f\u6765\u91d1\u9762\u97e6\u9a6e\u4e8e\u660e\uff0c\u5728\u6b66\u6797\u4e2d\u672c\u4e5f\u662f\u4e00\u7b49\u4e00\u7684 \u89d2\u8272\uff0c\u5f53\u521d\u5728\u6c5f\u6e56\u4e2d\u9887\u4eab\u76db\u540d\uff0c\u81ea\u88ab\u5929\u9634\u6559\u6536\u7f57\u540e\uff0c\u5374\u90c1\u90c1\u4e0d\u5f97\u5176\u5fd7\uff0c\u53ea\u88ab\u6d3e\u5230\u82cf\u5dde\u5206\u8235\uff0c \u505a\u4e2a\u5c0f\u5c0f\u7684\u8235\u4e3b\u3002 \u3000\u3000\u6b64\u4eba\u884c\u8d70\u6c5f\u6e56\u65f6\uff0c\u4e3a\u4eba\u5c1a\u8fd8\u6b63\u6d3e\uff0c\u4e0e\u4fa0\u4e49\u9053\u4e2d\u4eba\uff0c\u4e5f\u591a\u6709\u4ea4\u5f80\uff0c\u53ea\u56fa\u751f\u6027\u5b64\u7656\uff0c\u72ec\u65ad\u72ec \u884c\uff0c\u7ed3\u4e0b\u8bb8\u591a\u6781\u5389\u5bb3\u7684\u4ec7\u5bb6\uff0c\u88ab\u903c\u5f97\u65e0\u5904\u5bb9\u8eab\uff0c\u8fd9\u624d\u6258\u5e87\u4e8e\u5929\u9634\u6559\u4e0b\uff0c\u4ee5\u6c42\u907f\u7978\u3002 \u3000\u3000\u4ed6\u5c06\u624b\u4e2d\u7684\u5947\u5f62\u97ad\u5251\u4e00\u6643\uff0c\u8bf4\uff1a\u201c\u670b\u53cb\uff0c\u52a8\u624b\u5427\uff0c\u8fd9\u513f\u5c31\u5f88\u7a7a\u50fb\uff0c\u6211\u4eec\u4e5f\u4e0d\u5fc5\u62e3\u5730\u65b9 \u5566\u3002\u201d \u3000\u3000\u718a\u501c\u4fca\u76ee\u542b\u55d4\uff0c\u6717\u58f0\u9053\uff1a\u201c\u5c0f\u7237\u8ddf\u4f60\u4eec\u8fd9\u79cd\u4e0b\u4e09\u6d41\u7684\u89d2\u8272\u52a8\u624b\uff0c\u5411\u4f8b\u5148\u8ba9\u4e09\u62db\uff0c\u4f60\u5e9f\u8bdd \u5c11\u8bf4\uff0c\u53ea\u7ba1\u62db\u547c\u5c31\u662f\u4e86\u3002\u201d \u3000\u3000\u4e8e\u660e\u4ea6\u662f\u5927\u6012\uff0c\u97ad\u5251\u4e00\u70b9\uff0c\u7b14\u76f4\u5730\u70b9\u5411\u5589\u5934\u80f8\u8179\u4e24\u4e2a\u8981\u7a74\uff0c\u718a\u501c\u89c1\u6b64\u4eba\u5c45\u7136\u64c5\u80fd\u6253\u7a74\uff0c \u800c\u4e14\u4e00\u62db\u4e24\u5f0f\uff0c\u663e\u89c1\u529f\u529b\uff0c\u4e5f\u77e5\u4e0d\u53ef\u8f7b\u654c\uff0c\u8eab\u5f62\u6ef4\u6e9c\u6e9c\u4e00\u8f6c\uff0c\u8f7b\u6084\u5730\u907f\u5f00\u6b64\u62db\u3002 \u3000\u3000\u4e8e\u660e\u4e00\u632b\u8155\u65f6\uff0c\u97ad\u5251\u500f\u5730\u5212\u8d77\u4e00\u9053\u5149\u8292\uff0c\u201c\u957f\u9cb8\u5438\u6c34\u201d\uff0c\u907f\u5f00\u718a\u501c\u7684\u4e00\u62db\u3002 \u3000\u3000\u718a\u501c\u5fae\u4e00\u9976\u6b65\uff0c\u5251\u5149\u6070\u6070\u81ea\u8eab\u65c1\u63a0\u8fc7\uff0c\u90a3\u4e8e\u660e\u4e45\u7ecf\u5927\u654c\uff0c\u6b66\u529f\u4ea6\u81ea\u662f\u4e0d\u51e1\uff0c\u638c\u4e2d\u97ad\u907f\u53cd \u8fce\uff0c\u8eab\u8eaf\u4e0d\u626d\uff0c\u76f4\u6b3a\u4e0a\u6765\uff0c\u53c8\u6781\u5de7\u5999\u7684\u8eb2\u5f00\u6b64\u62db\u3002 \u3000\u3000\u91d1\u9762\u97e6\u9a6e\u53cc\u811a\u7528\u529b\uff0c\u5f80\u540e\u731b\u9000\uff0c\u5374\u89c1\u718a\u501c\u5e26\u7740\u4e00\u4e1d\u51b7\u7b11\uff0c\u4ecd\u7136\u7ad9\u5728\u90a3\u91cc\uff0c\u4ed6\u89c1\u718a\u501c\u8eab\u6cd5 \u592a\u5feb\uff0c\u5fc3\u6000\u6212\u5fc3\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u5c55\u5f00\u72ec\u95e8\u7684\u9634\u9633\u97ad\u5251\u8fde\u73af\u5f0f\uff0c\u70b9\u3001\u524a\u3001\u6311\u3001\u624e\u3001\u622a\u3001\u6253\u3001\u6572\uff0c \u5377\u8d77\u9752\u5149\u5982\u7ec3\uff0c\u62db\u62db\u5f0f\u5f0f\uff0c\u4e0d\u79bb\u718a\u501c\u7684\u8981\u5bb3\u3002 \u3000\u3000\u718a\u501c\u5374\u4ec1\u7acb\u5982\u5c71\uff0c\u6beb\u4e0d\u79fb\u52a8\uff0c\u53cc\u624b\u6216\u6293\u6216\u683c\uff0c\u90fd\u4ece\u610f\u60f3\u4e0d\u5230\u7684\u90e8\u4f4d\uff0c\u53bb\u5316\u89e3\u5bf9\u65b9\u7684\u5251 \u5f0f\uff0c\u90a3\u4e8e\u660e\u7684\u5251\u5149\u867d\u5982\u5343\u91cd\u6d6a\u6d9b\uff0c\u5230\u4e86\u718a\u501c\u773c\u524d\uff0c\u5374\u5982\u9047\u89c1\u4e86\u4e2d\u6d41\u7825\u67f1\uff0c\u5411\u4e24\u8fb9\u5206\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u4e8e\u660e\u81ea\u662f\u6697\u91cc\u5403\u60ca\uff0c\u4ed6\u53d1\u89c9\u718a\u501c\u7684\u6b66\u529f\uff0c\u8fd8\u5728\u4ed6\u610f\u6599\u4e4b\u5916\uff0c\u81ea\u5df1\u4eca\u65e5\uff0c\u53ea\u6015\u5fc5\u7136\u8ba8\u4e0d\u597d \u53bb\uff0c\u718a\u501c\u5374\u4e5f\u5fc3\u5934\u6253\u9f13\uff0c\u6697\u601d\u5929\u9634\u6559\u4e0b\u4e00\u4e2a\u5c0f\u5c0f\u5206\u8235\u7684\u8235\u4e3b\uff0c\u5df2\u662f\u5982\u6b64\u4e0d\u51e1\uff0c\u770b\u6b66\u529f\u7adf\u4f3c\u5728 \u90a3\u5434\u8bcf\u4e91\u4e4b\u4e0a\uff0c\u90a3\u5929\u9634\u6559\u4e2d\u7684\u5802\u4e3b\u3001\u575b\u4e3b\uff0c\u6b66\u529f\u5f53\u66f4\u60ca\u4eba\u4e86\uff0c\u602a\u4e0d\u5f97\u5929\u9634\u6559\u96c4\u89c6\u6c5f\u6e56\uff0c\u81ea\u6709 \u5176\u9053\u7406\u7684\u3002 \u3000\u3000\u53c8\u662f\u5341\u51e0\u4e2a\u7167\u9762\uff0c\u4ed6\u5fc3\u4e2d\u6709\u4e8b\uff0c\u53ea\u7ba1\u7559\u610f\u4e8e\u660e\u7684\u8eab\u624b\uff0c\u5e76\u4e0d\u8fdb\u51fb\u3002 \u3000\u3000\u7a81\u5730\u8857\u7684\u5c3d\u5934\uff0c\u4e00\u9a91\u5954\u6765\uff0c\u9a6c\u4e0a\u7684\u4eba\u5927\u58f0\u559d\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u4eba\u8fd9\u7b49\u5f20\u72c2\uff0c\u5149\u5929\u5316\u65e5\u4e4b\u4e0b\uff0c \u5728\u5927\u8857\u4e0a\u5c31\u52a8\u8d77\u624b\u6765\uff0c\u5feb\u7ed9\u6211\u4f4f\u624b\u3002\u201d \u3000\u3000\u4e8e\u660e\u95fb\u8a00\uff0c\u6b63\u597d\u4e0b\u53f0\uff0c\u4ed6\u5fd9\u505c\u4e0b\u62db\u5f0f\uff0c\u718a\u501c\u4e5f\u653e\u4e0b\u4e86\u624b\uff0c\u51b7\u773c\u6253\u91cf\u9a6c\u4e0a\u7684\u9a91\u58eb\uff0c\u53ea\u89c1\u4ed6 \u5168\u8eab\u9526\u7ee3\uff0c\u7a7f\u7740\u6253\u626e\uff0c\u50cf\u4e2a\u8d35\u80c4\u516c\u5b50\uff0c\u80cc\u4e0a\u7684\u5251\uff0c\u91d1\u5149\u707f\u7136\uff0c\u5251\u9798\u7adf\u662f\u7528\u9ec4\u91d1\u6253\u9020\u7684\uff0c\u6c14\u6d3e \u6840\u50b2\uff0c\u4e0d\u53ef\u4e00\u4e16\uff0c\u5750\u5728\u9a6c\u4e0a\u7528\u97ad\u68a2\u6307\u7740\u4e8e\u660e\u8bf4\uff1a\u201c\u4f60\u5927\u6982\u53c8\u662f\u5929\u9634\u6559\u4e0b\u7684\u4eba\u7269\uff0c\u602a\u4e0d\u5f97\u7adf\u6562 \u5728\u98de\u7075\u5821\u9644\u8fd1\u7684\u82cf\u5dde\u5730\u9762\u4e0a\uff0c\u968f\u8857\u6492\u91ce\u3001\u52a8\u6b66\uff0c\u4e1c\u65b9\u5821\u4e3b\u4e0d\u7ba1\uff0c\u6211\u5374\u8981\u66ff\u4ed6\u7ba1\u7ba1\u3002\u201d \u3000\u3000\u4ed6\u9a6c\u97ad\u4e00\u6b6a\uff0c\u53c8\u6307\u7740\u718a\u501c\u8bf4\uff1a\u201c\u4f60\u53c8\u662f\u4ec0\u4e48\u4eba\uff0c\u770b\u4f60\u65af\u65af\u6587\u6587\u7684\uff0c\u600e\u4e48\u8fd9\u6837\u4e0d\u61c2\u4e8b\uff0c\u5927 \u8857\u4e4b\u4e0a\uff0c\u5c82\u662f\u52a8\u624b\u4e4b\u5904\u3002\u201d \u3000\u3000\u718a\u501c\u867d\u89c9\u6b64\u4eba\u592a\u8fc7\u5028\u50b2\uff0c\u4f46\u4ed6\u63d0\u5230\u4e1c\u65b9\u5821\u4e3b\uff0c\u60f3\u5fc5\u662f\u4e1c\u65b9\u7075\u7684\u670b\u53cb\uff0c\u518d\u8005\u4ed6\u6240\u8bb2\u7684\u8bdd\u4ea6 \u975e\u65e0\u7406\uff0c\u662f\u4ee5\u5e76\u672a\u5982\u4f55\u751f\u5fff\u3002\u90a3\u91d1\u9762\u97e6\u9a6e\u751f\u6027\u5374\u4e5f\u6700\u662f\u6840\u50b2\uff0c\u54ea\u91cc\u53d7\u5f97\u4e86\u8fd9\u6837\u7684\u6559\u8bad\u7684\u53e3 \u543b\uff0c\u6012\u559d\u4e00\u58f0\uff1a\u201c\u51ed\u4f60\u4e5f\u914d\u7ba1\u5927\u7237\u7684\u95f2\u4e8b\uff0c\u4f60\u4e5f\u8ddf\u6211\u4e0b\u6765\u5427\u3002\u201d\u624b\u4e2d\u97ad\u5251\u201c\u9634\u9633\u4e4d\u5206\u201d\uff0c\u4e0d \u53d6\u4eba\u8eab\uff0c\u800c\u53d6\u9a6c\u817f\u3002 \u3000\u3000\u54ea\u77e5\u6b64\u4eba\u9a91\u672f\u7cbe\u7edd\uff0c\u6240\u9a91\u7684\u53c8\u662f\u5343\u4e2d\u9009\u4e00\u826f\u9a79\uff0c\u624b\u4e00\u7d27\u7ec4\u7ef3\uff0c\u90a3\u9a6c\u7adf\u4eba\u7acb\u8d77\u6765\uff0c\u4e8e\u660e\u4e00 \u62db\u8d70\u7a7a\uff0c\u9a6c\u8e44\u5df2\u671d\u4ed6\u5934\u9876\u8e39\u4e86\u4e0b\u6765\uff0c\u4ed6\u731b\u4e00\u64a4\u8eab\uff0c\u5251\u5f0f\u4e0a\u6311\uff0c\u76f4\u70b9\u9a6c\u9996\uff0c\u4ed6\u662f\u6210\u5fc3\u53eb\u9a6c\u4e0a\u7684 \u4eba\u4e0b\u6765\u3002 \u3000\u3000\u90a3\u4eba\u53cc\u817f\u4e00\u631f\uff0c\u786c\u751f\u751f\u5730\u5c06\u9a6c\u5411\u5de6\u4e00\u504f\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u7b97\u662f\u54ea\u95e8\u7684\u82f1\u96c4\uff0c\u7adf\u548c\u755c\u7272\u4e00 \u822c\uff0c\u6211\u82e5\u4e0d\u6559\u8bad\u4f60\uff0c\u4f60\u4e5f\u4e0d\u77e5\u9053\u6211\u662f\u4ec0\u4e48\u4eba\u3002\u201d\u8bf4\u7740\uff0c\u624b\u4e2d\u7684\u9a6c\u97ad\u5237\u5730\u63a0\u4e0b\uff0c\u5e26\u7740\u5c16\u9510\u7684\u98ce \u58f0\uff0c\u76f4\u53d6\u4e8e\u660e\u3002 \u3000\u3000\u718a\u501c\u4e00\u89c1\u4ed6\u51fa\u4e8e\uff0c\u5c31\u77e5\u6b64\u4eba\u5185\u529f\u9020\u8be3\u5f88\u6df1\uff0c\u800c\u4e14\u542c\u4ed6\u8bf4\u8bdd\u7684\u53e3\u6c14\u4eff\u4f5b\u5728\u6b66\u6797\u4e2d\u4eab\u6709\u76db \u540d\uff0c\u5fc3\u4e2d\u6697\u5fd6\u9053\uff1a\u201c\u8fd9\u4eba\u5e74\u7eaa\u4e5f\u548c\u6211\u5dee\u4e0d\u591a\uff0c\u6b66\u80c1\u5df1\u662f\u5982\u6b64\uff0c\u770b\u6765\u6b66\u6797\u4e2d\u786e\u662f\u4eba\u6750\u8f88\u51fa\uff0c\u53ea \u662f\u6b64\u4eba\u592a\u8fc7\u5028\u50b2\uff0c\u4e0d\u7136\uff0c\u6211\u5012\u771f\u60f3\u4ea4\u4ea4\u8fd9\u4e2a\u670b\u53cb\u3002\u201d \u3000\u3000\u6b64\u65f6\u90a3\u4eba\u5df2\u548c\u4e8e\u660e\u52a8\u8d77\u624b\u6765\uff0c\u4f46\u5374\u4ecd\u4e0d\u4e0b\u9a6c\uff0c\u51ed\u7740\u9a91\u672f\u6905\u7edd\u548c\u5185\u529b\u6df1\u539a\uff0c\u867d\u7136\u9a91\u5728\u9a6c\u4e0a \u6ca1\u6709\u4e8e\u660e\u7075\u4fbf\uff0c\u4f46\u4e8e\u660e\u4e5f\u5360\u4e0d\u4e86\u534a\u70b9\u597d\u5904\u3002 \u3000\u3000\u90a3\u8336\u9986\u9694\u58c1\u539f\u662f\u4e00\u5bb6\u5ba2\u6808\uff0c\u91cc\u9762\u672c\u6709\u4e9b\u4eba\u5728\u8fdc\u8fdc\u89c2\u671b\u7740\u3002 \u3000\u3000\u6b64\u65f6\u4eba\u4ece\u91cc\u5ffd\u5730\u53d1\u51fa\u4e00\u58f0\u51b7\u7b11\uff0c\u4e00\u4e2a\u5c11\u5e74\u5973\u5b50\u6781\u5feb\u7a9c\u4e86\u51fa\u6765\uff0c\u4f38\u624b\u5411\u90a3\u9526\u8863\u9a91\u58eb\u7684\u9a6c\u4e00 \u70b9\uff0c\u90a3\u9a6c\u7a81\u5730\u4eba\u7acb\u800c\u8d77\uff0c\u7adf\u88ab\u5236\u5f97\u5b9a\u5728\u90a3\u91cc\uff0c\u4e24\u817f\u524d\u7acb\uff0c\u5f62\u72b6\u751a\u662f\u53ef\u6016\u3002 \u3000\u3000\u9a6c\u4e0a\u7684\u9a91\u58eb\u548c\u4e8e\u660e\u4ff1\u662f\u672a\u60f3\u5230\u6709\u8fd9\u7b49\u53d8\u5316\uff0c\u5404\u81ea\u4e00\u60ca\uff0c\u9a6c\u4e0a\u7684\u9a91\u58eb\u89c1\u5750\u9a91\u7adf\u5982\u4e2d\u9b54\uff0c\u52a8 \u4e5f\u4e0d\u52a8\uff0c\u98d8\u8eab\u843d\u5230\u5730\u4e0a\uff0c\u4e24\u773c\u76f4\u77aa\u7740\u90a3\u5c11\u5e74\u5973\u5b50\uff0c\u50cf\u662f\u5728\u60ca\u5f02\u7740\u8fd9\u5c11\u5973\u7684\u8eab\u624b\uff0c\u53c8\u50cf\u662f\u5728\u60ca \u5f02\u7740\u8fd9\u5c11\u5973\u7684\u7f8e\u8c8c\u3002 \u3000\u3000\u4e8e\u660e\u4e5f\u88ab\u8fd9\u624b\u9707\u4f4f\uff0c\u4e00\u62f1\u53cc\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\u8bf7\u4e86\uff0c\u5728\u4e0b\u548c\u59d1\u5a18\u7d20\u6627\u751f\u5e73\uff0c\u59d1\u5a18\u7adf \u63d2\u624b\u76f8\u52a9\uff0c\u5728\u4e0b\u786e\u662f\u611f\u6fc0\u2026\u2026\u201d \u3000\u3000\u90a3\u5c11\u5973\u8f7b\u5550\u4e86\u4e00\u53e3\uff0c\u8bf4\u9053\uff1a\u201c\u8c01\u5728\u5e2e\u4f60\u5440\uff0c\u4e0d\u8fc7\u6211\u770b\u8fd9\u4e2a\u4eba\u592a\u65e0\u7406\uff0c\u4ed6\u53eb\u522b\u4eba\u4e0d\u8981\u5728\u8857 \u4e0a\u52a8\u624b\uff0c\u81ea\u5df1\u5374\u8ddf\u4eba\u6253\u8d77\u6765\u4e86\uff0c\u6211\u4e5f\u6765\u6559\u8bad\u6559\u8bad\u4ed6\u3002\u201d \u3000\u3000\u4e8e\u660e\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u4eca\u65e5\u4e4b\u4e8b\uff0c\u770b\u5728\u8fd9\u4f4d\u59d1\u5a18\u9762\u4e0a\uff0c\u6682\u4e14\u653e\u8fc7\uff0c\u9752\u5c71\u4e0d\u6539\uff0c\u7eff\u6c34\u957f\u6d41\uff0c\u4ed6 \u65e5\u6211\u91d1\u9762\u97e6\u9a6e\u82e5\u80fd\u518d\u89c1\u4e24\u4f4d\uff0c\u5374\u8981\u5f97\u7f6a\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7684\u539f\u662f\u573a\u9762\u8bdd\uff0c\u63a5\u7740\u4ed6\u53c8\u5411\u90a3\u9526\u8863\u5c11\u5e74\u8bf4\u9053\uff1a\u201c\u670b\u53cb\u597d\u4e00\u8eab\u6b66\u672f\uff0c\u4e5f\u8bf7\u4eae\u4e2a\u4e07 \u513f\u3002\u201d\u90a3\u9526\u8863\u5c11\u5e74\u51b7\u51b7\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e8f\u4f60\u8fd8\u5728\u6c5f\u6e56\u4e0a\u884c\u8d70\uff0c\u8fde\u6211\u5b64\u5cf0\u4e00\u5251\u8fb9\u6d69\u90fd\u4e0d\u8ba4\u5f97\uff0c \u4f60\u4e5f\u4e0d\u7528\u591a\u8bf4\u5e9f\u8bdd\uff0c\u660e\u7684\u6697\u7684\uff0c\u6211\u8fb9\u67d0\u4eba\u603b\u63a5\u7740\u4f60\u7684\u3002\u201d \u3000\u3000\u4e8e\u660e\u4e00\u542c\u6b64\u4eba\u7ade\u662f\u6b66\u6797\u4e2d\u4f20\u95fb\u7684\u201c\u53cc\u7edd\u5251\u201d\u4e4b\u4e00\uff0c\u9762\u8272\u4e00\u53d8\uff0c\u8bdd\u4e5f\u6ca1\u8bf4\uff0c\u6389\u5934\u5e26\u7740\u90a3\u4e24 \u4e2a\u9ed1\u8863\u5927\u6c49\u81ea\u7ba1\u8d70\u4e86\u3002 \u3000\u3000\u5b64\u5cf0\u4e00\u5251\u8fb9\u6d69\uff0c\u659c\u8131\u718a\u501c\u4e00\u773c\uff0c\u4ed6\u7684\u5750\u9a91\u867d\u88ab\u90a3\u5c11\u5973\u5236\u4f4f\uff0c\u4f46\u5bf9\u90a3\u5c11\u5973\u975e\u4f46\u6beb\u65e0\u6076\u611f\uff0c \u800c\u4e14\u5fc3\u4e2d\u6cb9\u7136\u751f\u51fa\u4e00\u79cd\u7231\u6155\u4e4b\u610f\uff0c\u5f02\u6027\u76f8\u5438\uff0c\u672c\u662f\u8840\u6c14\u65b9\u521a\u7684\u5e74\u8f7b\u6c49\u5b50\u7684\u5e38\u6001\uff0c\u4f46\u65b9\u624d\u718a\u501c \u548c\u90a3\u5c11\u5973\u76f8\u5bf9\u4e00\u7b11\uff0c\u4ed6\u5728\u65c1\u51b7\u773c\u65c1\u89c2\uff0c\u5374\u89c9\u751a\u4e0d\u662f\u6ecb\u5473\uff0c\u4ed6\u5e73\u65e5\u81ea\u89c6\u6700\u9ad8\uff0c\u628a\u522b\u4eba\u90fd\u4e0d\u770b\u5728 \u773c\u91cc\uff0c\u6b64\u523b\u6697\u81ea\u601d\u5fd6\u9053\uff1a\u201c\u770b\u8fd9\u5c0f\u5b50\u6123\u5934\u6123\u8111\uff0c\u5374\u4e0d\u6599\u4ed6\u7adf\u6709\u5982\u6b64\u4f73\u4eba\u76f8\u4f34\u2026\u2026\u201d \u3000\u3000\u6b64\u523b\u90a3\u5c11\u5973\u4e4b\u76ee\u5149\uff0c\u53c8\u6709\u610f\u65e0\u7adf\u95f4\u98d8\u5411\u718a\u501c\uff0c\u5b64\u5cf0\u4e00\u5251\u9f3b\u5b54\u91cc\u91cd\u91cd\u5730\u54fc\u4e86\u4e00\u58f0\uff0c\u51b7\u51b7 \u8bf4\uff1a\u201c\u602a\u4e0d\u5f97\u9601\u4e0b\u4fbf\u5c31\u6562\u5728\u82cf\u5dde\u8857\u5934\u4e0a\u52a8\u6b66\uff0c\u539f\u6765\u6709\u4e2a\u8fd9\u4e48\u597d\u7684\u5973\u5e2e\u624b\uff0c\u800c\u4e14\u8fd8\u4f1a\u5bf9\u4ed8\u755c \u7272\uff0c\u54c8\uff0c\u54c8\uff0c\u8fd9\u771f\u6559\u6211\u8fb9\u67d0\u4eba\u5f00\u4e86\u773c\u4e86\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u8d77\u5148\u542c\u5f97\u8fb9\u6d69\u7adf\u5c06\u5979\u548c\u718a\u501c\u8ba4\u505a\u4e00\u8def\uff0c\u773c\u89d2\u626b\u4e86\u718a\u501c\u4e00\u773c\uff0c\u5374\u4e5f\u4e0d\u5426\u8ba4\uff0c\u4f46\u540e\u6765 \u8fb9\u6d69\u8bdd\u5e26\u8ba5\u8bbd\uff0c\u5979\u5374\u5fcd\u4e0d\u4f4f\u4e86\uff0c\u5f53\u65f6\u674f\u76ee\u5706\u7741\uff0c\u5a07\u53f1\u9053\uff1a\u201c\u59d3\u8fb9\u7684\uff0c\u4f60\u8bf4\u8bdd\u53ef\u5f97\u653e\u6e05\u695a\u70b9\uff0c \u59d1\u5a18\u4e0d\u4f46\u5bf9\u4ed8\u755c\u7272\uff0c\u5bf9\u4ed8\u5bf9\u4ed8\u4f60\uff0c\u53ef\u4e5f\u5e76\u4e0d\u542b\u7cca\u3002\u201d \u3000\u3000\u5979\u51fa\u8bed\u8f7b\u8106\uff0c\u800c\u4e14\u662f\u4e00\u53e3\u5317\u65b9\u53e3\u97f3\uff0c\u867d\u662f\u9a82\u4eba\u7684\u8bdd\uff0c\u542c\u8d77\u6765\uff0c\u4ecd\u7136\u662f\u53c8\u751c\u53c8\u4fcf\uff0c\u4f46\u5b64\u5cf0 \u4e00\u5251\u81ea\u6210\u540d\u6c5f\u6e56\u4ee5\u6765\uff0c\u54ea\u91cc\u6709\u4eba\u5bf9\u4ed6\u8bf4\u8fc7\u8fd9\u6837\u7684\u8bdd\uff0c\u4e0d\u89c9\u5927\u6012\uff0c\u5389\u58f0\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u60f3\u4e0d \u5230\u4eca\u65e5\u7adf\u7136\u6709\u5411\u6211\u5b64\u5cf0\u4e00\u5251\u8fb9\u67d0\u4eba\u53eb\u9635\u7684\u4eba\uff0c\u800c\u4e14\u5c45\u7136\u662f\u4e2a\u5973\u5b50\uff0c\u6211\u8fb9\u6d69\u884c\u8d70\u6c5f\u6e56\u591a\u5e74\uff0c\u771f \u8fd8\u6ca1\u6709\u548c\u5973\u5b50\u4ea4\u8fc7\u624b\uff0c\u53ef\u662f\uff0c\u4eca\u65e5\u4e48\u2026\u201d\u4ed6\u76ee\u5149\u4e00\u77aa\u3002\u8bf4\u9053\uff1a\u201c\u5012\u8bf4\u4e0d\u5f97\u8981\u843d\u4e2a\u4ee5\u7537\u6b3a\u5973\u7684 \u8bdd\u5934\uff0c\u5411\u59d1\u5a18\u9886\u6559\u9886\u6559\u4e86\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u4fcf\u76ee\u4e00\u5f20\uff0c\u6b63\u60f3\u53d8\u8138\uff0c\u5ffd\u5730\u76ee\u5149\u4e00\u8f6c\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u613f\u610f\uff0c\u6211\u53ef\u4e0d\u613f\u610f\u5728\u8fd9\u5927\u8857\u4e0a \u548c\u4f60\u52a8\u624b\uff0c\u770b\u4f60\u65af\u65af\u6587\u6587\u7684\uff0c\u600e\u4e48\u4e5f\u8fd9\u4e48\u4e0d\u61c2\u4e8b\uff0c\u5927\u8857\u4e4b\u4e0a\uff0c\u600e\u4e48\u4f1a\u662f\u52a8\u624b\u4e4b\u5730\u5462\uff1f\u201d \u3000\u3000\u8fd9\u8bdd\u6b63\u662f\u8fb9\u6d69\u5148\u524d\u5bf9\u718a\u501c\u8bf4\u7684\uff0c\u73b0\u5728\u8fd9\u5c11\u5973\u7adf\u62ff\u5b83\u6765\u56de\u656c\u8fb9\u6d69\uff0c\u718a\u501c\u542c\u4e86\uff0c\u53c8\u662f\u4e00\u7b11\uff0c \u90a3\u5c11\u5973\u4e5f\u5f97\u610f\u7684\u770b\u4e86\u718a\u501c\u4e00\u773c\u3002 \u3000\u3000\u5b64\u5cf0\u4e00\u5251\u8138\u4e0a\u500f\u5730\u98de\u7ea2\uff0c\u4ed6\u5230\u5e95\u662f\u6c5f\u6e56\u4e0a\u77e5\u540d\u4eba\u7269\uff0c\u81ea\u5df1\u8bf4\u51fa\u7684\u8bdd\uff0c\u5c82\u6709\u54bd\u56de\u8179\u4e2d\u4e4b \u7406\uff0c\u4ed6\u6115\u4e86\u8bb8\u4e45\uff0c\u8bdd\u4e5f\u6ca1\u8bf4\u4e00\u53e5\uff0c\u6389\u5934\u8d70\u5230\u9a6c\u8fb9\uff0c\u60f3\u6273\u978d\u4e0a\u9a6c\uff0c\u4f46\u662f\u90a3\u9a6c\u5df2\u7136\u4e0d\u518d\u50cf\u4e00\u5339\u80fd \u9a91\u7684\u9a6c\u4e86\u3002 \u3000\u3000\u90a3\u5c11\u5973\u770b\u4e86\uff0c\u5634\u89d2\u4e00\u6487\uff0c\u50cf\u662f\u60f3\u7b11\u7684\u6837\u5b50\uff0c\u4f46\u662f\u5e76\u6ca1\u7b11\u51fa\u6765\uff0c\u8d70\u5230\u90a3\u9a6c\u65c1\uff0c\u4f38\u638c\u6781\u5feb\u5730 \u62cd\u4e86\u4e09\u638c\uff0c\u90a3\u9a6c\u4ef0\u9996\u4e00\u58f0\u957f\u5636\uff0c\u7adf\u80fd\u6d3b\u52a8\u4e86\u3002 \u3000\u3000\u8fb9\u6d69\u8138\u4e0a\u4e00\u7ea2\uff0c\u8981\u77e5\u9053\uff0c\u7ea2\u8138\u662f\u5fc3\u4e2d\u6709\u4e9b\u7f9e\u6127\u7684\u610f\u601d\uff0c\u800c\u7d20\u6027\u72c2\u50b2\u7684\u5b64\u5cf0\u4e00\u5251\uff0c\u80fd\u5fc3\u4e2d \u89c9\u5f97\u7f9e\u6127\uff0c\u7b80\u76f4\u6709\u4e9b\u8fd1\u4e8e\u4e0d\u53ef\u80fd\u4e86\uff0c\u4ed6\u5f3a\u81ea\u505a\u51fa\u5c0a\u4e25\u4e4b\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\uff0c\u771f\u662f\u4f4d\u9ad8 \u4eba\uff0c\u6211\u8fb9\u67d0\u4eba\u4eca\u65e5\u603b\u7b97\u8ba4\u683d\u4e86\uff0c\u9752\u5c71\u4e0d\u6539\uff0c\u7eff\u6c34\u957f\u6d41\uff0c\u6211\u8fb9\u67d0\u4eba\u65e5\u540e\u80fd\u78b0\u5230\u4e8c\u4f4d\uff0c\u5fc5\u6709\u8865\u62a5 \u4e4b\u5904\uff0c\u4eca\u65e5\u5c31\u6b64\u522b\u8fc7\u4e86\u3002\u201d \u3000\u3000\u4ed6\u72e0\u72e0\u5730\u770b\u4e86\u718a\u501c\u4e00\u773c\uff0c\u8de8\u4e0a\u9a6c\u80cc\uff0c\u53cd\u624b\u4e00\u97ad\uff0c\u6025\u9a70\u800c\u53bb\uff0c\u718a\u501c\u89c1\u90a3\u5c11\u5973\u4e09\u8a00\u4e24\u8bed\uff0c\u5c31 \u628a\u8fb9\u6d69\u8e69\u4e86\u56de\u53bb\uff0c\u4e0d\u7981\u53c8\u60f3\u4e00\u7b11\uff0c\u90a3\u5c11\u5973\u4e5f\u8f6c\u8fc7\u5934\u6765\uff0c\u5bf9\u718a\u501c\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5582\uff01\u4f60\u8fd9 \u4eba\u8fd8\u7ad9\u5728\u8fd9\u513f\u5e72\u5565\uff0c\u5feb\u8d70\u5440\u3002\u201d \u3000\u3000\u718a\u501c\u4e00\u62b1\u62f3\uff0c\u60f3\u8bf4\u53e5\u4ec0\u4e48\uff0c\u5374\u4e0d\u77e5\u9053\u600e\u4e48\u8bf4\u6cd5\uff0c\u90a3\u5c11\u5973\u5df2\u8885\u8885\u5a77\u5a77\u8d70\u4e86\u8fc7\u6765\uff0c\u6084\u8bf4\u9053\uff1a \u201c\u5582\uff0c\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\u5440\uff1f\u201d \u3000\u3000\u718a\u501c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u5c0f\u751f\u718a\u501c\uff0c\u201d\u8bf4\u5b8c\u53c8\u89c9\u5c0f\u751f\u8fd9\u4e24\u4e2a\u5b57\u7528\u5f97\u751a\u662f\u4e0d\u59a5\uff0c\u8138\u7ea2\u7740\u4f4e\u4e0b\u5934 \u53bb\u3002 \u3000\u3000\u90a3\u5c11\u5973\u54af\u54af\u7b11\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u54df\uff0c\u4f60\u5012\u771f\u6587\u7ec9\u7ec9\u7684\uff0c\u5582\uff0c\u6211\u8bf4\uff0c\u4f60\u600e\u4e48\u8fd8\u4e0d\u8d70\u5440\uff1f\u201d \u3000\u3000\u718a\u501c\u62ac\u8d77\u5934\u6765\uff0c\u548c\u5979\u7684\u76ee\u5149\u53c8\u4e00\u76f8\u5bf9\uff0c\u55eb\u5685\u7740\u8bf4\uff1a\u201c\u4e0d\u6562\u8bf7\u6559\u59d1\u5a18\u82b3\u540d\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u7b11\u5f97\u5982\u540c\u767e\u5408\u521d\u653e\uff0c\u8bf4\u9053\uff1a\u201c\u77a7\u4f60\u8fd9\u4eba\uff0c\u5728\u5927\u8857\u4e0a\u5c31\u95ee\u8d77\u4eba\u5bb6\u7684\u540d\u5b57\u6765\u4e86\uff0c\u6211\u504f \u4e0d\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u718a\u501c\u6115\u4e86\u4e00\u6115\uff0c\u4ed6\u672c\u4e0d\u5584\u8a00\u8bcd\uff0c\u6b64\u523b\u9762\u5bf9\u7740\u8fd9\u5c11\u5973\uff0c\u5982\u767e\u8f6c\u9ec4\u83ba\uff0c\u8bf4\u8d77\u8bdd\u6765\uff0c\u53c8\u4fcf\u53c8 \u8106\uff0c\u66f4\u662f\u65e0\u8a00\u53ef\u7b54\uff0c\u7ea2\u7740\u8138\u8bf4\uff1a\u201c\u90a3\u4e48\u2026\u2026\u5728\u4e0b\u544a\u8f9e\u4e86\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u8bf4\u9053\uff1a\u201c\u522b\u5fd9\u8d70\uff0c\u6211\u544a\u8bc9\u4f60\uff0c\u6211\u5440\uff0c\u53eb\u590f\u82b8\uff0c\u5582\uff0c\u4f60\u8bf4\u8fd9\u540d\u5b57\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u718a\u501c\u8fde\u58f0\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u597d\uff01\u201d \u3000\u3000\u590f\u82b8\u5446\u5446\u5730\u770b\u4e86\u718a\u501c\u8bb8\u4e45\uff0c\u7a81\u7136\u8bf4\u9053\uff1a\u201c\u6211\u8bf4\u718a\u501c\u5440\uff0c\u4f60\u8981\u5230\u54ea\u513f\u53bb\u5440\uff1f\u201d \u3000\u3000\u718a\u501c\u672c\u60f3\u968f\u5904\u98d8\u6cca\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u56fa\u5b9a\u53bb\u5904\uff0c\u88ab\u5979\u4e00\u95ee\uff0c\u7adf\u7b54\u4e0d\u51fa\u8bdd\u6765\u4e86\u3002 \u3000\u3000\u590f\u82b8\u5634\u4e00\u9f13\uff0c\u4fcf\u55d4\u9053\uff1a\u201c\u597d\uff0c\u6211\u77e5\u9053\u4f60\u4e0d\u544a\u8bc9\u6211\u3002\u201d \u3000\u3000\u718a\u501c\u614c\u8bf4\u9053\uff1a\u201c\u4e0d\u662f\u6211\u4e0d\u80af\u544a\u8bc9\u59d1\u5a18\u4f60\uff0c\u53ea\u662f\u6211\u73b0\u5728\u8fd8\u4e0d\u77e5\u9053\u8be5\u5230\u4ec0\u4e48\u5730\u65b9\u53bb\uff0c\u4e0d\u8fc7\u968f \u5904\u53bb\u8d70\u8d70\u5c31\u662f\u4e86\u3002\u201d \u3000\u3000\u90a3\u590f\u82b8\u81ea\u5e7c\u88ab\u6781\u6eba\u7231\u5730\u957f\u5927\uff0c\u4ed6\u5bb6\u91cc\u53c8\u662f\u5bb6\u624d\u4e07\u8d2f\uff0c\u201c\u843d\u65e5\u9a6c\u573a\u201d\u5728\u585e\u5916\u53ef\u79f0\u662f\u9996\u5c48\u4e00 \u6307\uff0c\u957f\u5927\u540e\u66f4\u662f\u517b\u5c0a\u5904\u4f18\uff0c\u4e00\u547c\u767e\u8bfa\uff0c\u5fc3\u91cc\u60f3\u505a\u4ec0\u4e48\uff0c\u9a6c\u4e0a\u5c31\u53bb\u505a\uff0c\u4ece\u6765\u4e0d\u66fe\u6709\u4eba\u62c2\u8fc7\u5979\u7684 \u610f\uff0c\u8fd9\u6b21\u5979\u4ece\u585e\u5916\u51fa\u6765\uff0c\u4e5f\u662f\u7d20\u4ef0\u6c5f\u5357\u98ce\u7269\uff0c\u5230\u5404\u5904\u73a9\u73a9\u7684\uff0c\u6b64\u523b\u542c\u718a\u501c\u8fd9\u6837\u8bf4\uff0c\u5927\u559c\u9053\uff1a \u201c\u90a3\u597d\u6781\u4e86\uff0c\u6211\u4e5f\u662f\u5230\u5404\u5730\u53bb\u8d70\u8d70\uff0c\u6211\u4e00\u4e2a\u5973\u5b69\u5b50\u5bb6\uff0c\u597d\u4e0d\u65b9\u4fbf\u5440\uff0c\u4f60\u80af\u966a\u7740\u6211\u4e00\u5757\u513f \u5417\u3002\u201d \u3000\u3000\u718a\u501c\u4e00\u60ca\uff0c\u4ed6\u4e07\u4e07\u60f3\u4e0d\u5230\u5979\u4f1a\u8fd9\u6837\u8bf4\u6cd5\uff0c\u4e3a\u96be\u9053\uff1a\u201c\u8fd9\u6837\u2026\u2026\u6050\u6015\u4e0d\u592a\u65b9\u4fbf\u5427\u3002\u201d \u3000\u3000\u718a\u501c\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u5b8c\uff0c\u5979\u5c31\u62a2\u7740\u8bf4\uff1a\u201c\u4ec0\u4e48\u65b9\u4fbf\u4e0d\u65b9\u4fbf\uff0c\u4f60\u5230\u5e95\u80af\u4e0d\u80af\uff1f\u201d \u3000\u3000\u718a\u501c\u5fc3\u91cc\u672a\u5c1d\u4e0d\u613f\u610f\uff0c\u53ea\u662f\u4ed6\u5e7c\u906d\u5b64\u9732\uff0c\u751f\u6027\u62d8\u8c28\u5f97\u5f88\uff0c\u5fc3\u91cc\u60f3\u505a\u7684\u4e8b\uff0c\u5e38\u5e38\u81ea\u5df1\u538b\u5236 \u81ea\u5df1\u800c\u4e0d\u53bb\u505a\uff0c\u6b64\u523b\u590f\u82b8\u8fd9\u6837\u95ee\u4ed6\uff0c\u201c\u662f\u201d\u6216\u662f\u201c\u5426\u201d\uff0c\u8fd9\u662f\u4ed6\u4ece\u672a\u7b54\u590d\u8fc7\u7684\u95ee\u9898\uff0c\u4ed6\u60f3\u4e86 \u8bb8\u4e45\uff0c\u8fd8\u6ca1\u6709\u56de\u7b54\u3002 \u3000\u3000\u590f\u82b8\u4e00\u8dfa\u811a\uff0c\u6c14\u607c\u4ed6\u8bf4\uff1a\u201c\u597d\uff0c\u4f60\u4e0d\u80af\u5c31\u7b97\u4e86\uff0c\u6211\u624d\u4e0d\u7a00\u7f55\u5462\u3002\u201d\u773c\u5708\u4e00\u7ea2\uff0c\u5f88\u5feb\u5c31\u8dd1 \u5230\u5ba2\u6808\u91cc\u53bb\u4e86\u3002 \u3000\u3000\u7ad9\u5728\u8857\u5934\uff0c\u718a\u501c\u6115\u4e86\u8bb8\u4e45\uff0c\u5fc3\u4e2d\u5347\u8d77\u4e00\u79cd\u5947\u5f02\u7684\u6ecb\u5473\u3002 \u3000\u3000\u7136\u540e\u4ed6\u56de\u8f6c\u8eab\uff0c\u6f2b\u6b65\u8d70\u56de\u8336\u9986\uff0c\u60f3\u53d6\u56de\u4ed6\u653e\u5728\u684c\u4e0a\u7684\u5305\u88b1\u548c\u5251\uff0c\u8336\u9986\u88ab\u4ed6\u4eec\u8fd9\u4e00\u95f9\uff0c\u91cc \u9762\u65e9\u5df2\u7a7a\u7a7a\u7684\u6ca1\u6709\u5ba2\u4eba\uff0c\u4ed6\u6e38\u76ee\u4e00\u770b\uff0c\u81ea\u5df1\u653e\u5728\u684c\u4e0a\u7684\u5305\u88b1\uff0c\u7adf\u4e0d\u77e5\u53bb\u5411\u4e86\uff0c\u6025\u5f97\u9a6c\u4e0a\u6cdb\u8d77 \u4e00\u8eab\u51b7\u6c57\u3002 \u3000\u3000\u8336\u9986\u91cc\u7684\u5802\u500c\u4e00\u89c1\u4ed6\u53c8\u8d70\u8fdb\u6765\uff0c\u5982\u540c\u89c1\u4e86\u51f6\u795e\u6076\u715e\uff0c\u8fde\u5fd9\u8dd1\u4e86\u8fc7\u53bb\uff0c\u5e26\u7740\u4e00\u8138\u52c9\u5f3a\u7684\u7b11 \u5bb9\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u7237\u8fd8\u6709\u4ec0\u4e48\u5429\u5490\uff1f\u201d \u3000\u3000\u718a\u501c\u6025\u9053\uff1a\u201c\u6211\u521a\u624d\u653e\u5728\u684c\u4e0a\u7684\u4e24\u4e2a\u5305\u88b1\uff0c\u4f60\u53ef\u89c1\u5230\uff1f\u201d \u3000\u3000\u5e97\u4f19\u614c\u5fd9\u6447\u624b\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u6ca1\u6709\u3002\u201d\u4ed6\u53c8\u624b\u6307\u7740\u5899\u4e0a\u7684\u4e00\u5f20\u5b57\u6761\u8bf4\uff1a\u201c\u6211\u4eec\u5e97\u91cc\u7684\u89c4 \u77e9\uff0c\u4e00\u5411\u662f\u94f6\u94b1\u7269\u54c1\uff0c\u8d35\u5ba2\u81ea\u7406\uff0c\u9057\u5931\u4e86\u6211\u4eec\u4e5f\u4e0d\u80fd\u8d1f\u8d23\uff0c\u8fd9\u4e2a\u8fd8\u8bf7\u5927\u7237\u83ab\u602a\u3002\u201d \u3000\u3000\u4ed6\u77e5\u9053\u8fd9\u79cd\u4e8b\u4ea6\u65e0\u6cd5\u5411\u5e97\u4e2d\u8ffd\u95ee\uff0c\u7a7a\u81ea\u7740\u6025\u4e86\u4e00\u4f1a\uff0c\u832b\u7136\u8d70\u51fa\u5e97\u53bb\uff0c\u6b64\u523b\u4ed6\u9664\u4e86\u4e00\u8eab\u8863 \u670d\u4e4b\u5916\uff0c\u771f\u662f\u8eab\u65e0\u957f\u7269\uff0c\u4ed6\u767e\u611f\u4ea4\u96c6\uff0c\u6101\u6000\u6d8c\u751f\uff0c\u53ea\u662f\u5728\u60f3\u5230\u590f\u82b8\u65f6\uff0c\u5fc3\u5934\u4e0d\u7981\u63a0\u8fc7\u4e00\u9635\u6e29 \u99a8\u3002 \u3000\u3000\u4ed6\u6f2b\u65e0\u76ee\u7684\u5730\u5411\u524d\u8d70\u7740\uff0c\u8fc7\u7740\u8352\u7960\u5e9f\u5e99\uff0c\u4fbf\u80e1\u4e71\u5730\u6b47\u4e0b\uff0c\u6709\u65f6\u82b1\u4e2a\u51e0\u6587\u94b1\uff0c\u4e70\u4e9b\u679c\u997c\u5145 \u9965\u3002 \u3000\u3000\u4e00\u65e5\uff0c\u4ed6\u8d70\u5230\u4e00\u4e2a\u6e21\u5934\uff0c\u770b\u5230\u4e00\u8258\u6e21\u8239\uff0c\u6b63\u7f13\u7f13\u9a76\u8fd1\uff0c\u6e21\u8239\u4e0a\u7684\u4eba\u867d\u4e0d\u591a\uff0c\u4f46\u7bb1\u7b3c\u5374\u6709 \u591a\u4ef6\uff0c\u6e21\u5934\u4e0a\u7684\u95f2\u6c49\u4e00\u6d8c\u800c\u4e0a\uff0c\u4e0d\u7ba1\u4eba\u5bb6\u613f\u4e0d\u613f\u610f\uff0c\u5c31\u63d0\u8d77\u4eba\u5bb6\u7684\u884c\u674e\uff0c\u625b\u4e0b\u8239\u6765\uff0c\u4f38\u624b\u5c31 \u8981\u94b1\uff0c\u8fd9\u539f\u662f\u811a\u592b\u6076\u4e60\uff0c\u5c24\u5176\u957f\u6c5f\u4e00\u5e26\uff0c\u8fd9\u79cd\u6076\u4e60\u6700\u662f\u7316\u7357\uff0c\u65c5\u5ba2\u4e5f\u65e0\u6cd5\u5236\u6b62\u3002 \u3000\u3000\u8239\u7684\u672b\u68a2\uff0c\u662f\u4e24\u4e2a\u6a21\u6837\u751a\u662f\u8001\u5b9e\u7684\u4e2d\u5e74\u5ba2\u5546\uff0c\u5b88\u7740\u4e24\u53ea\u5927\u7bb1\u5b50\uff0c\u90a3\u4e9b\u811a\u592b\u81ea\u662f\u4e5f\u8d70\u5230\u90a3 \u4e24\u4eba\u9762\u524d\uff0c\u8981\u66ff\u4ed6\u4eec\u642c\u90a3\u4e24\u53ea\u7bb1\u5b50\uff0c\u4f46\u90a3\u4e24\u4eba\u5374\u6b7b\u4e5f\u4e0d\u8ba9\u811a\u592b\u4eec\u642c\uff0c\u53ea\u662f\u7262\u7262\u5b88\u7740\u7bb1\u5b50\u3002 \u3000\u3000\u5176\u4e2d\u4e00\u4e2a\u6ee1\u8138\u9ebb\u5b50\u7684\u7a0d\u957f\u5927\u6c49\uff0c\u50cf\u662f\u811a\u592b\u91cc\u7684\u5934\u5b50\uff0c\u89c1\u90a3\u4e24\u4e2a\u5ba2\u5546\u5982\u6b64\uff0c\u5f20\u53e3\u9a82\u4e86\u4e00\u53e5 \u6781\u96be\u542c\u7684\u7c97\u8bdd\uff0c\u8dd1\u5230\u811a\u592b\u5806\u4e2d\uff0c\u53fd\u53fd\u5495\u5495\u8bf4\u4e86\u4e24\u53e5\uff0c\u5c31\u53c9\u7740\u4e24\u624b\u7ad9\u5728\u6e21\u8239\u7684\u5934\u4e0a\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u8001\u5b9e\u7684\u5ba2\u5546\uff0c\u7b49\u8239\u4e0a\u7684\u4eba\u5c06\u8fd1\u90fd\u8d70\u5b8c\u4e86\uff0c\u7b2c\u4e00\u4eba\u642c\u8d77\u4e00\u53e3\u7bb1\u5b50\uff0c\u8d70\u4e0b\u8239\u6765\uff0c\u4e0d\u6599 \u521a\u8d70\u5230\u8239\u53e3\u7684\u65f6\u5019\uff0c\u90a3\u6ee1\u8138\u9ebb\u5b50\u7684\u7a0d\u957f\u5927\u6c49\uff0c\u7a81\u7136\u4e00\u4e2a\u8e09\u8dc4\u5012\u5728\u4ed6\u4e24\u4eba\u8eab\u4e0a\u3002 \u3000\u3000\u90a3\u4e24\u4eba\u642c\u7740\u5374\u4f3c\u5341\u4efd\u6c89\u91cd\u7684\u7bb1\u5b50\uff0c\u5df2\u662f\u6446\u6446\u6643\u6643\u7684\uff0c\u54ea\u91cc\u7981\u5f97\u8d77\u8fd9\u5927\u6c49\u4e00\u649e\uff0c\u4e00\u58f0\u60ca \u547c\uff0c\u8fde\u4eba\u5e26\u7bb1\u5b50\uff0c\u671d\u8239\u5916\u8dcc\u53bb\u3002 \u3000\u3000\u718a\u501c\u6b63\u8e72\u5728\u6c5f\u5cb8\uff0c\u6781\u6709\u5174\u8da3\u7684\u671b\u7740\uff0c\u7a81\u770b\u89c1\u6b64\u4e8b\uff0c\u731b\u4e00\u957f\u8eab\uff0c\u4fbf\u5df2\u7a9c\u5230\u8239\u5934\uff0c\u5de6\u624b\u6a2a\u63a0 \u90a3\u53ea\u7bb1\u5b50\uff0c\u53f3\u624b\u6321\u4f4f\u90a3\u5ba2\u5546\u5df2\u8dcc\u5012\u7684\u8eab\u8eaf\uff0c\u4ed6\u65e0\u610f\u4e2d\u7adf\u4f7f\u51fa\u201c\u82cd\u7a79\u5341\u4e09\u5f0f\u201d\u4e2d\u7684\u4e00\u8bb0\u5999\u7740\uff0c \u201c\u65e5\u6708\u53cc\u5206\u201d\u4e86\u3002 \u3000\u3000\u54ea\u77e5\u4ed6\u8fd9\u4e00\u51fa\u624b\uff0c\u5374\u51fa\u4e86\u4e00\u5b97\u5947\u4e8b\uff0c\u4ed6\u5de6\u53f3\u53cc\u624b\uff0c\u672c\u662f\u4e00\u9f50\u51fa\u624b\uff0c\u800c\u4e14\u6240\u7528\u7684\u529b\u91cf\u4e5f\u5b8c \u5168\u76f8\u540c\uff0c\u56e0\u4e3a\u4ed6\u8ba4\u4e3a\u4e00\u4e2a\u5feb\u8981\u8dcc\u5012\u7684\u76f8\u5f53\u5b9e\u7684\u8eaf\u4f53\uff0c\u548c\u4e00\u4e2a\u7bb1\u5b50\uff0c\u6240\u9700\u7684\u529b\u9053\u5fc5\u662f\u6781\u4e3a\u76f8\u7433 \u7684\u3002 \u3000\u3000\u54ea\u77e5\u4ed6\u6a2a\u63a0\u7bb1\u5b50\u7684\u5de6\u624b\uff0c\u6240\u6293\u7684\u7bb1\u5b50\uff0c\u7adf\u662f\u610f\u5916\u5730\u6c89\u91cd\uff0c\u82e5\u4e0d\u662f\u4ed6\u5185\u529f\u5df2\u5230\u6781\u6df1\u7684\u706b \u5019\uff0c\u6f5c\u5728\u7684\u5185\u529b\uff0c\u968f\u7740\u7a81\u7136\u800c\u6765\u7684\u60ca\u5947\uff0c\u731b\u5730\u52a0\u5f3a\uff0c\u90a3\u7bb1\u5b50\u4fbf\u8981\u843d\u5165\u6c34\u4e2d\uff0c\u5140\u662f\u8fd9\u6837\uff0c\u90a3\u7bb1 \u5b50\u7684\u91cd\u91cf\u4ecd\u662f\u4ed6\u751f\u5e73\u672a\u9047\u7684\u3002 \u3000\u3000\u800c\u4ed6\u7684\u53f3\u624b\uff0c\u7adf\u89c9\u5f97\u4eff\u4f5b\u662f\u6a2a\u6321\u5728\u4e00\u56e2\u98d8\u8361\u7684\u68c9\u7d6e\u4e0a\uff0c\u662f\u90a3\u4e48\u5730\u8f7b\u98d8\u548c\u67d4\u8f6f\uff0c\u4ed6\u5fc3\u4e2d\u6781 \u5feb\u7684\u4e00\u8f6c\uff0c\u4fbf\u77e5\u9053\u8fd9\u770b\u6765\u8001\u5b9e\u7684\u4e2d\u5e74\u5ba2\u5546\uff0c\u5b9e\u662f\u6709\u7740\u975e\u5e38\u6b66\u529f\u7684\u5546\u4eba\uff0c\u800c\u4e14\u4ece\u4ed6\u548c\u8fd9\u7bb1\u5b50\u4e2d \u7684\u79cd\u79cd\u8ff9\u8c61\uff0c\u53ef\u770b\u51fa\u6b64\u4eba\u975e\u4f46\u6b66\u529f\u9ad8\u5f3a\uff0c\u800c\u4e14\u5b9e\u662f\u8be1\u79d8\u5f97\u5f88\u3002 \u3000\u3000\u718a\u501c\u8fd9\u7a81\u4e00\u51fa\u624b\uff0c\u975e\u4f46\u60ca\u9707\u4e86\u90a3\u8bb8\u591a\u56f4\u4f4f\u7740\u7684\u811a\u592b\uff0c\u4e5f\u60ca\u9707\u4e86\u90a3\u4fe9\u884c\u52a8\u8be1\u5f02\uff0c\u770b\u4f3c\u8fc2 \u5446\uff0c\u800c\u5b9e\u662f\u5927\u6709\u6765\u5934\u7684\u4e2d\u5e74\u5ba2\u5546\u3002 \u3000\u3000\u4ed6\u4eec\u6240\u6599\u60f3\u4e0d\u5230\u7684\u662f\uff0c\u5728\u8fd9\u8352\u50fb\u6e21\u5934\uff0c\u7adf\u4f1a\u6709\u8fd9\u6837\u7684\u5185\u5bb6\u9ad8\u624b\uff0c\u201c\u884c\u5bb6\u4e00\u4f38\u624b\uff0c\u4fbf\u77e5\u6709 \u6ca1\u6709\u201d\uff0c\u987b\u77e5\u90a3\u4e9b\u811a\u592b\u60ca\u5f02\u7684\uff0c\u4e0d\u8fc7\u4ec5\u662f\u718a\u501c\u7684\u8eab\u624b\u4e4b\u901f\u800c\u5df2\uff0c\u800c\u90a3\u4e24\u4e2a\u4e2d\u5e74\u5ba2\u5546\uff0c\u4e0d\u4ec5\u5982 \u6b64\uff0c\u800c\u4e14\u8fd8\u77e5\u9053\u718a\u501c\u6b64\u51fa\u624b\uff0c\u662f\u7528\u4e86\u6b66\u6797\u4e2d\u4e00\u79cd\u7f55\u89c1\u7684\u62db\u5f0f\uff0c\u800c\u4e14\u5185\u529b\u6df1\u6e5b\uff0c\u56e0\u4e3a\u4ed6\u4eec\u6df1\u77e5 \u81ea\u5df1\u7bb1\u5b50\u7684\u91cd\u91cf\uff0c\u82e5\u975e\u5185\u529b\u60ca\u4eba\uff0c\u600e\u80fd\u4eba\u60ac\u7a7a\u4e2d\uff0c\u4fbf\u80fd\u6284\u4f4f\u8fd9\u53e3\u7bb1\u5b50\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u4eec\u5e76\u4e0d\u9732\u51fa\u950b\u8292\uff0c\u4ecd\u7136\u88c5\u505a\u51fa\u8001\u5b9e\u800c\u8fdf\u7f13\u7684\u6837\u5b50\uff0c\u6781\u4e3a\u5c0f\u5fc3\u5730\u7ad9\u76f4\u4e86\u5c06\u8981\u8dcc\u5012\u7684 \u8eab\u8eaf\uff0c\u772f\u7740\u773c\uff0c\u63a9\u9970\u7740\u773c\u4e2d\u4e00\u79cd\u5185\u5bb6\u9ad8\u624b\u6240\u7279\u5177\u7684\u795e\u5149\uff0c\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u771f\u8c22\u8c22\u8fd9\u4f4d\u8001\u54e5\u4e86\uff0c \u82e5\u4e0d\u662f\u8fd9\u4f4d\u8001\u54e5\uff0c\u4eca\u5929\u6211\u4eec\u975e\u8dcc\u6b7b\u4e0d\u53ef\u3002\u201d \u3000\u3000\u718a\u501c\u773c\u73e0\u4e00\u8f6c\uff0c\u4ed6\u77e5\u9053\u8fd9\u7c7b\u6b66\u6797\u9ad8\u624b\uff0c\u8fd9\u6837\u5730\u63a9\u9970\u884c\u85cf\uff0c\u5fc5\u662f\u6709\u7740\u4e0d\u53ef\u544a\u4eba\u7684\u4e8b\uff0c\u82e5\u662f \u4ee5\u524d\uff0c\u4ed6\u5fc5\u8981\u5c06\u8fd9\u4e9b\u4e8b\u63a2\u4e2a\u6e05\u695a\uff0c\u4f46\u5728\u4ed6\u72ec\u81ea\u6f02\u6cca\u7684\u8bb8\u591a\u65e5\u5b50\u6765\uff0c\u4ed6\u5df2\u517b\u6210\u4e00\u79cd\u4e0e\u4eba\u65e0\u4e89\u7684 \u9676\u7136\u6027\u683c\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u7528\u5ba2\u6c14\uff0c\u8fd9\u7b97\u4e0d\u4e86\u4ec0\u4e48\u3002\u201d \u3000\u3000\u90a3\u5ba2\u5546\u9732\u51fa\u611f\u6fc0\u7684\u7b11\u5bb9\uff0c\u50cf\u662f\u611f\u6fc0\u718a\u501c\u7684\u51fa\u624b\u76f8\u52a9\uff0c\u53c8\u50cf\u662f\u611f\u6fc0\u718a\u501c\u7684\u4e0d\u63ed\u7834\u4ed6\u4eec\u7684\u884c \u85cf\uff0c\u5176\u4e2d\u4e00\u4eba\u4f38\u624b\u5165\u6000\uff0c\u60f3\u638f\u4e9b\u4ec0\u4e48\uff0c\u5ffd\u53c8\u6b62\u4f4f\u4e86\uff0c\u8c28\u614e\u5730\u62b1\u8d77\u90a3\u4e24\u53e3\u7bb1\u5b50\uff0c\u7f13\u6162\u5730\u8d70\u4e0b\u8239 \u53bb\u3002 \u3000\u3000\u90a3\u4e9b\u811a\u592b\uff0c\u90fd\u662f\u4e9b\u773c\u91cc\u4e0d\u63c9\u6c99\u5b50\u7684\u5149\u68cd\uff0c\u770b\u89c1\u718a\u501c\u7684\u8eab\u624b\uff0c\u4ed6\u4eec\u661f\u4e0d\u751a\u6e05\u695a\u5176\u4e2d\u7684\u5965 \u5999\uff0c\u4f46\u4e5f\u77e5\u9053\u90a3\u662f\u4e00\u79cd\u9ad8\u6df1\u7684\u6b66\u529f\uff0c\u9042\u90fd\u5728\u65c1\u773c\u7741\u7741\u5730\u770b\u7740\uff0c\u6ca1\u6709\u4e00\u4e2a\u4eba\u51fa\u6765\u5411\u718a\u501c\u5bfb\u4e8b\u3002 \u3000\u3000\u718a\u501c\u770b\u7740\u90a3\u4e24\u4e2a\u4eba\u6c89\u91cd\u7684\u811a\u6b65\u8d70\u4e86\u4e00\u6bb5\uff0c\u4ed6\u4eec\u88c5\u4f5c\u5f97\u975e\u5e38\u597d\uff0c\u5b8c\u5168\u4e0d\u50cf\u662f\u4e00\u4e2a\u8eab\u6000\u7edd\u6280 \u7684\u4eba\uff0c\u718a\u501c\u7b11\u4e86\u7b11\uff0c\u4ed6\u7b11\u81ea\u5df1\u8fd9\u56de\u5012\u771f\u662f\u201c\u591a\u7ba1\u95f2\u4e8b\u201d\u4e86\uff0c\u5176\u5b9e\u6b64\u4e24\u4eba\uff0c\u53c8\u4f55\u987b\u81ea\u5df1\u51fa\u624b \u5462\uff1f \u3000\u3000\u4ed6\u7ad9\u4e86\u4e00\u4f1a\uff0c\u77e5\u9053\u90a3\u7fa4\u811a\u592b\u5df2\u88ab\u81ea\u5df1\u9707\u4f4f\uff0c\u4fbf\u65bd\u7136\u8d70\u4e0b\u8239\u53bb\u3002 \u3000\u3000\u90a3\u5df2\u6e10\u884c\u6e10\u8fdc\u7684\u5ba2\u5546\uff0c\u5ffd\u5730\u56de\u8fc7\u5934\u6765\uff0c\u8d70\u4e86\u51e0\u8d70\uff0c\u4e00\u9f50\u4f38\u624b\u62db\u547c\u718a\u501c\u8fc7\u53bb\u3002 \u3000\u3000\u718a\u501c\u77e5\u9053\u5fc5\u5b9a\u6709\u4e8b\uff0c\u4fbf\u5927\u6b65\u8d70\u5230\u90a3\u4e24\u4eba\u7684\u8eab\u65c1\uff0c\u62f1\u624b\u9053\uff1a\u201c\u4e24\u4f4d\u6709\u4f55\u5429\u5490\uff1f\u201d \u3000\u3000\u90a3\u4e24\u4eba\u5176\u4e2d\u4e00\u4eba\u9762\u8272\u8d64\u7ea2\uff0c\u7565\u5e26\u5fae\u987b\u7684\u4e5f\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u5144\u53f0\u4ed7\u4e49\u51fa\u624b\uff0c\u6211\u5144\u5f1f\u611f\u6fc0\u5f97 \u5f88\uff0c\u770b\u5144\u53f0\u5982\u6b64\u8eab\u624b\uff0c\u5fc5\u5b9a\u662f\u4f4d\u9ad8\u4eba\uff0c\u5927\u5bb6\u5fc3\u7167\u4e0d\u5ba3\u4e4b\u5904\uff0c\u8fd8\u671b\u5144\u53f0\u80fd\u591a\u5305\u6db5\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7740\u4f38\u624b\u638f\u51fa\u4e00\u4e2a\u5947\u5f0f\u751a\u53e4\u7684\u5236\u94b1\uff0c\u7528\u4e00\u6839\u6de1\u9ec4\u7684\u4e1d\u5e26\u4e32\u4f4f\uff0c\u4f38\u624b\u9012\u7ed9\u718a\u501c\uff0c\u8bf4\u9053\uff1a \u201c\u8fd9\u662f\u6211\u5f1f\u5144\u4e00\u4ef6\u5c0f\u5c0f\u7684\u4fe1\u7269\uff0c\u5144\u53f0\u5728\u7696\u3001\u6d59\u3001\u6e58\u3001\u8d63\u4e00\u5e26\uff0c\u82e5\u6709\u4e9b\u4ec0\u4e48\u4e0d\u80fd\u89e3\u51b3\u7684\uff0c\u8d70\u5230 \u95e8\u9762\u8f83\u5927\u7684\u5e97\u5bb6\uff0c\u968f\u4fbf\u4e00\u63d0\uff0c\u5c31\u8bf4\u662f\u53f6\u5bb6\u5144\u5f1f\u7684\u597d\u53cb\uff0c\u5144\u53f0\u65e0\u8bba\u8981\u4ec0\u4e48\u5e2e\u52a9\uff0c\u5fc5\u5b9a\u6709\u4e2a\u7167 \u5e94\uff0c\u6211\u5f1f\u5144\u867d\u77e5\u5144\u53f0\u8eab\u6000\u7edd\u6280\uff0c\u4e0d\u5c51\u6c42\u4eba\uff0c\u4f46\u8fd9\u5374\u662f\u6211\u5144\u5f1f\u7684\u4e00\u756a\u5fc3\u610f\uff0c\u5144\u53f0\u5927\u540d\uff0c\u6211\u7b49\u867d \u4e0d\u77e5\u9053\uff0c\u4f46\u840d\u6c34\u76f8\u4ea4\uff0c\u53ea\u8981\u6295\u7f18\u4e5f\u5c31\u7f62\u4e86\uff0c\u201d\u718a\u501c\u4e00\u89c1\u6b64\u4e24\u4eba\u867d\u662f\u884c\u8e2a\u8be1\u5f02\uff0c\u4f46\u7687\u4e0a\u53bb\u5012\u4e5f \u4e0d\u4f3c\u574f\u4eba\uff0c\u4fbf\u7b11\u7740\u79f0\u8c22\u9053\uff1a\u201c\u4e24\u4f4d\u65e2\u7136\u5982\u6b64\uff0c\u5c0f\u5f1f\u4fbf\u5c31\u6b64\u8c22\u8fc7\u4e86\u3002\u3001\u90a3\u4e24\u4eba\u4fbf\u53c8\u4e00\u62f1\u624b\uff0c\u8bf4 \u9053\uff1a\u201c\u65e5\u540e\u6709\u7f18\uff0c\u82e5\u80fd\u518d\u9047\u5144\u53f0\uff0c\u5fc5\u5f53\u8c0b\u4e00\u5feb\u805a\uff0c\u4eca\u65e5\u5c31\u6b64\u522b\u8fc7\u4e86\u3002\u201d\u8bf4\u5b8c\uff0c\u4fbf\u8f6c\u8eab\u8d70\u4e86\uff0c \u718a\u501c\u89c1\u4e8b\u5df2\u4e86\uff0c\u968f\u624b\u5c06\u90a3\u53e4\u94b1\u63e3\u4eba\u6000\u4e2d\uff0c\u4e5f\u672a\u66fe\u5728\u610f\uff0c\u6b64\u6e21\u5934\u65e2\u7ecf\u6b64\u4e8b\uff0c\u4ed6\u4e5f\u4e0d\u613f\u518d\u7559\uff0c\u6ede \u6d12\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u8d70\u7740\u8d70\u7740\uff0c\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u5c11\u65f6\u5019\uff0c\u6709\u4e00\u5929\uff0c\u4ed6\u72ec\u81ea\u5750\u5728\u96ea\u5730\u91cc\uff0c\u5ffd\u7136\u8eab\u540e\u4f20\u6765\u4e00\u9635\u8e44 \u58f0\uff0c\u8e44\u58f0\u5728\u4ed6\u8eab\u540e\u505c\u4f4f\uff0c\u4e00\u4eba\u4e0b\u9a6c\uff0c\u843d\u5730\u4e4b\u58f0\u751a\u662f\u8f7b\u5fae\u3002 \u3000\u3000\u4e00\u4e2a\u8f7b\u4fcf\u7684\u5973\u5b50\u53e3\u97f3\u8bf4\u9053\uff1a\u201c\u8fd9\u4e48\u5927\u51b7\u7684\u5929\uff0c\u4f60\u4e00\u4e2a\u4eba\u5750\u5728\u8fd9\u91cc\u5e72\u5417\uff1f\u5927\u5e74\u521d\u4e00\uff0c\u53ef\u522b \u60f3\u81ea\u6740\u5440\uff0c\u4f60\u8981\u662f\u6709\u4ec0\u4e48\u56f0\u96be\uff0c\u53ef\u4ee5\u8bf4\u7ed9\u6211\u542c\uff0c\u4f60\u522b\u770b\u6211\u662f\u4e2a\u5973\u5b50\uff0c\u53ef\u4e5f\u5e2e\u5f97\u4e86\u4f60\u5fd9\uff0c\u4f60\u8863 \u670d\u7a7f\u5f97\u8fd9\u4e48\u5c11\uff0c\u5c0f\u5fc3\u51bb\u6b7b\u4e86\u3002\u201d \u3000\u3000\u8bf4\u7740\u90a3\u5973\u5b50\u5df2\u8d70\u5230\u8eab\u65c1\uff0c\u718a\u501c\u672c\u662f\u4f4e\u7740\u5934\uff0c\u53ea\u770b\u5230\u8fd9\u5973\u5b50\u7a7f\u7740\u4e00\u53cc\u767d\u76ae\u7684\u9774\u5b50\uff0c\u4e00\u8eab\u7d27 \u8eab\u7684\u8863\u7946\uff0c\u5916\u9762\u7f69\u96ea\u767d\u7684\u5154\u76ae\u98ce\u7bf7\uff0c\u4ed6\u62ac\u5934\u4e00\u770b\uff0c\u9762\u8272\u4e00\u53d8\uff0c\u539f\u6765\u8fd9\u5973\u5b50\u7adf\u662f\u590f\u82b8\u3002 \u3000\u3000\u90a3\u5973\u5b50\u89c1\u4ed6\u671b\u7740\u5979\uff0c\u5c31\u9053\uff1a\u201c\u4f60\u522b\u770b\u7740\u6211\uff0c\u6709\u4ec0\u4e48\u4e8b\u5c3d\u7ba1\u8bf4\u597d\u4e86\u3002\u201d \u3000\u3000\u718a\u501c\u7ad9\u4e86\u8d77\u6765\uff0c\u671d\u590f\u82b8\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u8ba4\u8bc6\u6211\u4e86\uff0c\u53ef\u662f\u6211\u5374\u8ba4\u8bc6\u4f60\u5462\u3002\u201d \u3000\u3000\u590f\u82b8\u671d\u4ed6\u4e0a\u4e0b\u770b\u4e86\u534a\u5929\uff0c\u518d\u671b\u7740\u4ed6\u7684\u773c\u775b\uff0c\u7a81\u5730\u5440\u7684\u4e00\u58f0\uff0c\u53c8\u53eb\u4e86\u51fa\u6765\uff0c\u559c\u9053\uff1a\u201c\u539f\u6765 \u662f\u4f60\u5440\uff0c\u771f\u60f3\u4e0d\u5230\u5728\u8fd9\u91cc\u78b0\u5230\u4f60\u3002\u201d \u3000\u3000\u5979\u53c8\u770b\u7740\u718a\u501c\u8bf4\uff1a\u201c\u600e\u4e48\u624d\u4e24\u3001\u4e09\u4e2a\u6708\u4e0d\u89c1\uff0c\u4f60\u53d8\u6210\u8fd9\u4e2a\u6837\u5b50\uff0c\u5dee\u70b9\u6211\u90fd\u4e0d\u8ba4\u8bc6\u4f60\u4e86\uff0c \u5582\uff01\u6211\u8bf4\u4f60\u5927\u5e74\u521d\u4e00\u7684\u6e05\u65e9\u5c31\u8dd1\u5230\u8fd9\u91cc\u6765\uff0c\u4e00\u4e2a\u4eba\u5750\u7740\uff0c\u53c8\u4e0d\u6015\u51b7\uff0c\u662f\u4e0d\u662f\u60f3\u81ea\u6740\u5440\u3002\u201d \u3000\u3000\u718a\u501c\u7b11\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u5927\u5e74\u521d\u4e00\u7684\u6e05\u65e9\uff0c\u4e0d\u4e5f\u8dd1\u5230\u8fd9\u91cc\u6765\u4e86\u5417\u3002\u201d \u3000\u3000\u590f\u82b8\u8138\u4e00\u7ea2\uff0c\u7b11\u9053\uff1a\u201c\u6211\u662f\u5acc\u5e97\u91cc\u592a\u5435\uff0c\u6211\u53c8\u662f\u4e00\u4e2a\u4eba\uff0c\u770b\u7740\u4eba\u5bb6\u90fd\u4e00\u5bb6\u4eba\u56e2\u805a\u7740\uff0c\u4e0d \u7981\u6709\u70b9\u60f3\u5bb6\u4e86\uff0c\u518d\u52a0\u4e0a\u6211\u4e5f\u542c\u8bf4\u8fd9\u91cc\u662f\u8bd7\u4ed9\u674e\u767d\u7684\u5893\u5730\uff0c\u5c31\u968f\u4fbf\u6765\u770b\u770b\uff0c\u60f3\u4e0d\u5230\u5374\u78b0\u89c1\u4e86 \u4f60\u3002\u201d \u3000\u3000\u5979\u8bf4\u5b8c\uff0c\u53c8\u5ae3\u7136\u4e00\u7b11\uff0c\u4f4e\u4e0b\u5934\u53bb\uff0c\u718a\u501c\u4e0d\u89c9\u770b\u5f97\u75f4\u4e86\u3002 \u3000\u3000\u590f\u82b8\u770b\u5230\u718a\u501c\u7684\u4e00\u53cc\u978b\u5b50\uff0c\u7834\u5f97\u4e03\u96f6\u516b\u843d\uff0c\u767d\u889c\u5b50\u5374\u53d8\u6210\u9ed1\u7684\u4e86\uff0c\u62ac\u8d77\u5934\u672a\uff0c\u5173\u5207\u5730\u95ee \u9053\uff1a\u201c\u4f60\u5230\u5e95\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u5f04\u5f97\u8fd9\u4e2a\u6837\u5b50\uff1f\u201d \u3000\u3000\u718a\u501c\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u6837\u5b50\u6709\u4ec0\u4e48\u4e0d\u597d\uff0c\u6211\u5012\u89c9\u5f97\u6ee1\u4e0d\u5728\u4e4e\u7684\u3002\u201d \u3000\u3000\u590f\u82b8\u9053\uff1a\u201c\u53ea\u662f\u2026\u2026\u53ea\u662f\u4f60\u7a7f\u5f97\u8fd9\u4e48\u5c11\uff0c\u5c82\u4e0d\u8981\u51bb\u574f\u4e86\u3002\u201d \u3000\u3000\u718a\u501c\u9053\uff1a\u201c\u6211\u4e00\u70b9\u4e5f\u4e0d\u51b7\u5440\u3002\u201d \u3000\u3000\u4e24\u4eba\u76f8\u5bf9\u7ad9\u7740\uff0c\u90fd\u89c9\u5f97\u6709\u4e00\u4efd\u65e0\u6cd5\u5f62\u5bb9\u7684\u4eb2\u5207\u4e4b\u611f\uff0c\u5728\u5927\u5e74\u521d\u4e00\u7684\u65e9\u4e0a\uff0c\u78b0\u5230\u4f60\u60f3\u89c1\u5230 \u7684\u4eba\uff0c\u8fd8\u6709\u4ec0\u4e48\u66f4\u53ef\u559c\u7684\u4e8b\u5462\uff1f \u3000\u3000\u5446\u4e86\u4e00\u4f1a\uff0c\u718a\u501c\u8bf4\uff1a\u201c\u6211\u771f\u7684\u4e0d\u51b7\uff0c\u4f60\u4e0d\u4fe1\u6478\u6478\u6211\u7684\u624b\uff0c\u8fd8\u662f\u70ed\u7684\u5462\u3002\u201d \u3000\u3000\u590f\u82b8\u4f4e\u7740\u5934\uff0c\u6084\u4fcf\u8131\u4e0b\u624b\u5957\uff0c\u718a\u501c\u4f38\u624b\u8fc7\u53bb\uff0c\u8f7b\u8f7b\u5730\u63e1\u7740\u5979\u7684\u624b\uff0c\u53ea\u89c9\u5f97\u6ee1\u624b\u6e29\u99a8\uff0c\u518d \u4e5f\u4e0d\u80af\u653e\u4e0b\uff0c\u53cd\u800c\u7d2b\u7d27\u5730\u63e1\u4f4f\u4e86\u3002 \u3000\u3000\u590f\u82b8\u7684\u624b\u8f7b\u8f7b\u6323\u624e\u4e86\u4e00\u4e0b\uff0c\u4e5f\u5c31\u8ba9\u4ed6\u63e1\u4f4f\u4e86\uff0c\u5979\u89c9\u5f97\u4e00\u79cd\u7537\u6027\u7684\u70ed\u529b\uff0c\u900f\u8fc7\u5979\u7684\u624b\uff0c\u76f4 \u5230\u5979\u5fc3\u5e95\u6df1\u5904\uff0c\u4f7f\u5979\u4e5f\u6c89\u9189\u4e86\u3002 \u3000\u3000\u96ea\u82b1\u4ecd\u5728\u98d8\u7740\uff0c\u5927\u5730\u663e\u5f97\u5bd2\u51b7\u800c\u5bc2\u9759\uff0c\u4f46\u4ed6\u4eec\u7684\u5fc3\u5374\u50cf\u706b\u4e00\u822c\u7684\u70ed\u3002 \u3000\u3000\u590f\u82b8\u4fcf\u4fcf\u5730\u504e\u5411\u718a\u501c\uff0c\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u544a\u8bc9\u6211\uff0c\u8fd9\u4e9b\u65e5\u5b50\u4f60\u6709\u6ca1\u6709\u60f3\u8fc7\u6211\uff1f\u201d \u3000\u3000\u718a\u501c\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u590f\u82b8\u9053\uff1a\u201c\u6709\u65f6\u6211\u771f\u6068\u4f60\uff0c\u90a3\u65f6\u6211\u53eb\u4f60\u966a\u7740\u6211\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u80af\uff1f\u201d \u3000\u3000\u718a\u501c\u63e1\u7740\u5979\u7684\u624b\uff0c\u63e1\u5f97\u66f4\u7d27\u4e86\u4e9b\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u6b21\u4f60\u518d\u53eb\u6211\uff0c\u6211\u5c31\u4e0d\u4f1a\u4e0d\u80af\u4e86\u3002\u201d \u3000\u3000\u590f\u82b8\u5e78\u798f\u5730\u7b11\u4e86\uff0c\u62ac\u5934\u671b\u7740\u718a\u501c\uff0c\u5ffd\u53c8\u98a6\u7709\u7b11\u9053\uff1a\u201c\u53ea\u662f\u4f60\u548c\u6211\u5728\u4e00\u5757\uff0c\u5374\u4e0d\u51c6\u8fd8\u662f\u526f \u6837\u5b50\uff0c\u4f60\u770b\u4f60\uff0c\u5f04\u5f97\u810f\u6b7b\u4e86\u3002\u201d \u3000\u3000\u718a\u501c\u82e6\u7b11\u9053\uff1a\u201c\u5176\u5b9e\u6211\u4e5f\u4e0d\u60f3\u5f04\u5f97\u8fd9\u6837\uff0c\u4e0d\u8fc7\u6211\u7684\u8863\u670d\u4e1c\u897f\u5168\u4e22\u4e86\uff0c\u6211\u53c8\u4e0d\u80fd\u53bb\u5077\u53bb \u62a2\uff0c\u53ea\u597d\u53d8\u6210\u4e86\u8fd9\u526f\u6837\u5b50\u4e86\u3002\u201d \u3000\u3000\u590f\u82b8\u5f20\u53e3\u60f3\u8bf4\u4ec0\u4e48\uff0c\u5ffd\u53c8\u8f6c\u53e3\u9053\uff1a\u201c\u8981\u662f\u6211\u5440\uff0c\u6211\u5c31\u53bb\u62a2\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u5657\u55e4\u4e00\u7b11\uff0c\u62c9\u7740\u718a\u501c\u8d70\u4e86\u51e0\u6b65\uff0c\u6307\u7740\u5979\u7684\u9a6c\u8bf4\uff1a\u201c\u4f60\u770b\u6211\u8fd9\u5339\u9a6c\u597d\u4e0d\u597d\uff1f\u201d\u718a\u501c\u89c1 \u90a3\u5339\u767d\u9a6c\uff0c\u6d51\u8eab\u6beb\u65e0\u6742\u8272\uff0c\u7ad9\u5728\u96ea\u91cc\u5730\uff0c\u663e\u5f97\u66f4\u662f\u795e\u9a8f\u3002 \u3000\u3000\u590f\u82b8\u53c8\u8bf4\uff1a\u201c\u90a3\u65f6\u5019\u6211\u9a91\u7740\u8fd9\u5339\u9a6c\uff0c\u50cf\u98ce\u4e00\u6837\u5730\u8dd1\u6765\u8dd1\u53bb\uff0c\u771f\u5feb\u6781\u4e86\uff0c\u5728\u96ea\u5730\u91cc\u8dd1\u5f97\u66f4 \u5feb\uff0c\u6240\u4ee5\u4eba\u5bb6\u90fd\u53eb\u6211\u96ea\u5730\u98d8\u98ce\u5462\u3002\u201d \u3000\u3000\u718a\u501c\u5fae\u7b11\u5730\u770b\u7740\u5979\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u81ea\u82e5\u99a8\u6b7b\u540e\uff0c\u672c\u6765\u5df2\u89c9\u5f97\u5fc3\u5982\u6b7b\u7070\u4e86\uff0c\u53ef\u662f\u4e0d\u77e5\u600e \u4e48\u56de\u4e8b\uff0c\u6211\u770b\u5230\u4e86\u5979\u53ea\u89c9\u5f97\u9ad8\u5174\u5f97\u5f88\uff0c\u53ea\u60f3\u8ddf\u5979\u5728\u4e00\u5757\u513f\uff0c\u522b\u7684\u4e8b\u5168\u60f3\u4e0d\u8d77\u6765\u4e86\u2026\u2026\u201d \u590f\u82b8\u8f7b\u8f7b\u4e00\u626d\uff0c\u4e0d\u4f9d\u9053\uff1a\u201c\u5582\uff0c\u4f60\u5728\u60f3\u4ec0\u4e48\u5440\uff0c\u4eba\u5bb6\u5728\u8ddf\u4f60\u8bb2\u8bdd\u5462\u3002\u201d \u3000\u3000\u718a\u501c\u8bf4\u9053\uff1a\u201c\u6211\u5728\u60f3\u7740\u4f60\uff0c\u6211\u770b\u5230\u4e86\u4f60\uff0c\u5fc3\u91cc\u5c31\u9ad8\u5174\u5f97\u5f88\u3002\u201d \u3000\u3000\u590f\u82b8\u9053\uff1a\u201c\u771f\u7684\u5417\uff1f\u201d \u3000\u3000\u718a\u501c\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u590f\u82b8\u504e\u4f9d\u5728\u718a\u501c\u80f8\u524d\uff0c\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u662f\u4e00\u770b\u5230\u4f60\u5c31\u89c9\u5f97\u5feb\u4e50\u3002\u201d \u3000\u3000\u718a\u501c\u53ea\u89c9\u5f97\u4ed6\u5df2\u662f\u4e16\u4e0a\u6700\u5e78\u798f\u7684\u4eba\uff0c\u4efb\u4f55\u4e0d\u5982\u610f\u7684\u4e8b\u4ed6\u90fd\u4e0d\u5728\u4e4e\u4e86\u3002 \u3000\u3000\u590f\u82b8\u7a81\u5730\u62c9\u7740\u718a\u501c\u7684\u624b\u8bf4\u9053\uff1a\u201c\u6211\u5e26\u4f60\u5230\u5f53\u6d82\u53bb\uff0c\u4f60\u4e0d\u77e5\u9053\uff0c\u90a3\u91cc\u4eca\u5929\u597d\u73a9\u6781\u4e86\uff0c\u672c\u6765 \u6211\u4e00\u4e2a\u4eba\u89c9\u5f97\u6ca1\u610f\u601d\uff0c\u73b0\u5728\u6709\u4f60\u966a\u6211\uff0c\u6211\u5c31\u8981\u597d\u597d\u73a9\u4e00\u73a9\u4e86\u3002\u201d \u3000\u3000\u5979\u6325\u5f00\u718a\u501c\u7684\u624b\uff0c\u9a91\u5230\u9a6c\u4e0a\uff0c\u8bf4\uff1a\u201c\u4f60\u4e5f\u4e0a\u6765\u5440\uff0c\u6211\u4eec\u4e24\u4eba\u9a91\u5728\u9a6c\u4e0a\uff0c\u4e00\u4f1a\u513f\u5c31\u5230\u4e86\uff0c \u4f60\u4e5f\u53ef\u4ee5\u8bd5\u8bd5\u6211\u7684\u5927\u767d\u7684\u811a\u529b\u3002\u201d \u3000\u3000\u718a\u501c\u62e7\u8eab\u4e5f\u4e0a\u4e86\u9a6c\uff0c\u4f38\u624b\u62b1\u7740\u590f\u82b8\u7684\u8170\uff0c\u9a6c\u547c\u54e8\u4e86\u4e00\u58f0\uff0c\u90a3\u9a6c\u4fbf\u653e\u5f00\u8e44\u8dd1\u4e86\uff0c\u718a\u501c\u53ea\u89c9 \u9a6c\u884c\u6108\u6765\u6108\u5feb\uff0c\u8def\u65c1\u7684\u6811\u6728\uff0c\u98de\u5feb\u5730\u5012\u9000\uff0c\u4f46\u5374\u5e73\u7a33\u5df2\u6781\uff0c\u4e0d\u7981\u8d5e\u9053\uff1a\u201c\u8fd9\u9a6c\u771f\u597d\u3002\u201d \u3000\u3000\u590f\u82b8\u542c\u4ed6\u4e5f\u559c\u6b22\u5927\u767d\uff0c\u5fc3\u91cc\u66f4\u9ad8\u5174\u8bf4\uff1a\u201c\u4f60\u4e5f\u559c\u6b22\u5b83\u5417\uff1f\u201d \u3000\u3000\u718a\u501c\u8bf4\uff1a\u201c\u5f53\u7136\u559c\u6b22\u3002\u201d \u3000\u3000\u590f\u82b8\u8bf4\uff1a\u201c\u4ee5\u540e\u4f60\u8981\u662f\u80fd\u5230\u6211\u7684\u9a6c\u573a\u53bb\uff0c\u6211\u4e00\u5b9a\u62e3\u4e00\u5339\u6700\u597d\u7684\u9a6c\u9001\u4f60\u3002\u201d \u3000\u3000\u718a\u501c\u95ee\u9053\uff1a\u201c\u4f60\u6709\u9a6c\u573a\uff1f\u201d \u3000\u3000\u590f\u82b8\u8bf4\uff1a\u201c\u4f60\u4e0d\u77e5\u9053\u5440\uff0c\u6211\u90a3\u4e2a\u9a6c\u573a\u53ef\u771f\u5927\uff0c\u4e00\u773c\u671b\u8fc7\u53bb\uff0c\u8fde\u8fb9\u90fd\u770b\u4e0d\u5230\uff0c\u6211\u7238\u7238\u5988\u5988 \u6700\u75bc\u6211\uff0c\u4f60\u4e5f\u4e00\u5b9a\u4f1a\u559c\u6b22\u4ed6\u4eec\u7684\u3002\u201d \u3000\u3000\u718a\u501c\u5e78\u798f\u4ed6\u8bf4\u9053\uff1a\u201c\u53ea\u8981\u4f60\u559c\u6b22\u7684\uff0c\u6211\u90fd\u4f1a\u559c\u6b22\u3002\u201d \u3000\u3000\u590f\u82b8\u5f00\u5fc3\u5730\u7b11\u4e86\u3002 \u7b2c\u516d\u7ae0\u3000\u7231\u60c5\u7684\u5e7c\u82d7>> \u53e4\u9f99\u300a\u82cd\u7a79\u795e\u5251\u300b \u7b2c\u516d\u7ae0\u3000\u7231\u60c5\u7684\u5e7c\u82d7 \u3000\u3000\u9a6c\u4e00\u8fdb\u5f53\u6d82\uff0c\u5c31\u8d70\u5f97\u6162\u4e86\uff0c\u718a\u501c\u89c1\u5bb6\u5bb6\u6237\u6237\uff0c\u90fd\u8d34\u7740\u5927\u7ea2\u6625\u8054\uff0c\u5e97\u94fa\u867d\u90fd\u5173\u8d77\u4e86\u95e8\u4e0d\u505a \u751f\u610f\uff0c\u4f46\u95e8\u53e3\u90fd\u7ad9\u7740\u5927\u4eba\u5c0f\u5b69\uff0c\u653e\u97ad\u70ae\uff0c\u5403\u6625\u997c\uff0c\u7a7f\u7684\u662f\u65b0\u505a\u7684\u8863\u88f3\u3002 \u3000\u3000\u718a\u501c\u548c\u590f\u82b8\u9a91\u5728\u9a6c\u4e0a\uff0c\u590f\u82b8\u6307\u4e1c\u6307\u897f\uff0c\u53fd\u53fd\u5495\u5495\u8bb2\u4e2a\u4e0d\u505c\uff0c\u53c8\u8bf4\u53c8\u7b11\uff0c\u5f15\u7740\u8def\u4e0a\u7684\u4eba\u90fd \u9a7b\u8db3\u800c\u671b\uff0c\u5947\u602a\u8fd9\u7f8e\u8c8c\u7684\u5c11\u5973\u600e\u4f1a\u548c\u8fd9\u50cf\u53eb\u5316\u5b50\u4f3c\u7684\u4eba\u540c\u4e58\u4e00\u9a91\uff0c\u800c\u53c8\u90a3\u4e48\u4eb2\u70ed\u3002 \u3000\u3000\u590f\u82b8\u5a07\u55d4\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u574f\u6b7b\u4e86\uff0c\u6b7b\u76ef\u7740\u6211\u4eec\u770b\uff0c\u6211\u771f\u6068\u4e0d\u5f97\u6253\u4ed6\u4eec\u4e00\u987f\u3002\u201d \u3000\u3000\u718a\u501c\u7b11\u9053\uff1a\u201c\u4ed6\u770b\u4ed6\u4eec\u7684\uff0c\u5173\u6211\u4eec\u4ec0\u4e48\u4e8b\uff0c\u4ed6\u4eec\u8981\u770b\u53ea\u7ba1\u770b\u597d\u4e86\u3002\u201d \u3000\u3000\u590f\u82b8\u8bf4\uff1a\u201c\u5582\uff0c\u6211\u8bf4\u4f60\u6362\u4ef6\u8863\u670d\u597d\u4e0d\u597d\uff0c\u4e0d\u8981\u8001\u662f\u8fd9\u6837\u561b\u3002\u201d \u3000\u3000\u718a\u501c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u4f60\u8bf4\u4ec0\u4e48\u5c31\u4ec0\u4e48\uff0c\u53ea\u662f\u4f60\u770b\uff0c\u4eba\u5bb6\u5e97\u90fd\u4e0a\u4e86\u95e8\uff0c\u6211\u4eec\u5230\u54ea\u91cc\u53bb\u4e70 \u8863\u670d\uff1f\u201d \u3000\u3000\u590f\u82b8\u9053\uff1a\u201c\u4eba\u5bb6\u4e0a\u4e86\u95e8\uff0c\u6211\u4eec\u4e0d\u4f1a\u53bb\u6572\u4ed6\u4eec\u7684\u95e8\u5417\uff1f\u201d \u3000\u3000\u4e24\u4eba\u9a91\u7740\u9a6c\u5728\u8857\u4e0a\u8f6c\u4e86\u4e00\u5468\uff0c\u627e\u7740\u4e00\u5bb6\u5356\u6210\u8863\u7684\u4f30\u8863\u5e97\uff0c\u90a3\u95e8\u53e3\u4e5f\u6b63\u6709\u4e09\u4e24\u4e2a\u5e74\u8f7b\u7684\u4f19 \u8ba1\u7ad9\u5728\u90a3\u91cc\u653e\u7740\u97ad\u70ae\uff0c\u770b\u89c1\u590f\u82b8\u8df3\u4e0b\u4e86\u9a6c\uff0c\u90fd\u88ab\u5979\u7684\u7f8e\u8c8c\u60ca\u4f4f\u4e86\uff0c\u63a5\u7740\u53c8\u770b\u89c1\u718a\u501c\u4e5f\u8df3\u4e0b\u4e86 \u9a6c\uff0c\u4e00\u4e2a\u4e2a\u90fd\u77aa\u4e86\u5927\u773c\u775b\uff0c\u5947\u602a\u4ed6\u4eec\u662f\u4f55\u6765\u8def\u3002 \u3000\u3000\u590f\u82b8\u8d70\u8fc7\u53bb\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u60f3\u4e70\u51e0\u4ef6\u8863\u670d\uff0c\u8981\u7279\u522b\u597d\u7684\u3002\u201d \u3000\u3000\u5176\u4e2d\u4e00\u4e2a\u5e74\u7eaa\u8f83\u5927\u7684\u4f19\u8ba1\u8bf4\u9053\uff1a\u201c\u4eca\u5929\u5927\u5e74\u521d\u4e00\uff0c\u6211\u4eec\u5e97\u91cc\u4e0d\u505a\u751f\u610f\uff0c\u4f60\u5bb6\u8fc7\u4e24\u5929\u518d\u6765 \u5149\u987e\u5427\u3002\u201d \u3000\u3000\u590f\u82b8\u8bf4\uff1a\u201c\u4e0d\u5356\u4e5f\u5f97\u5356\uff0c\u6211\u51fa\u53cc\u500d\u7684\u4ef7\u94b1\uff0c\u8fd8\u4e0d\u884c\u5417\uff1f\u201d \u3000\u3000\u90a3\u4f19\u8ba1\u773c\u775b\u4e00\u77aa\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u600e\u4e48\u8fd9\u6837\u4e0d\u8bb2\u7406\uff0c\u4e0d\u5356\u5c31\u662f\u4e0d\u5356\uff0c\u4f60\u51fa\u516b\u500d\u7684\u4ef7\u94b1\uff0c \u4eca\u5929\u6211\u4eec\u8fd9\u91cc\u5c31\u662f\u4e0d\u5356\u5b9a\u4e86\uff0c\u53c8\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u590f\u82b8\u5927\u6012\uff0c\u4e00\u4e2a\u7bad\u6b65\u7a9c\u524d\uff0c\u626c\u624b\u5c31\u7ed9\u90a3\u5e97\u4f19\u8ba1\u4e00\u8bb0\u8033\u5149\u3002 \u3000\u3000\u5176\u4ed6\u5e97\u4f19\u4e00\u6d8c\u800c\u6765\uff0c\u9ad8\u58f0\u9053\uff1a\u201c\u597d\u5bb6\u4f19\uff0c\u9752\u5929\u767d\u65e5\u4e4b\u4e0b\uff0c\u7adf\u6562\u4f38\u624b\u6253\u5165\uff0c\u4f60\u4ed7\u7740\u4ec0\u3002\u4e48 \u52bf\u529b\uff0c\u7adf\u6562\u8fd9\u6837\u7316\u72c2\u3002\u201d \u3000\u3000\u8bf4\u7740\u8bf4\u7740\uff0c\u6709\u7684\u5c31\u52a8\u8d77\u624b\u6765\uff0c\u52a8\u4e86\u4e00\u4f1a\u624b\uff0c\u90a3\u4e9b\u5e97\u4f19\u8ba1\u5df2\u88ab\u590f\u82b8\u6253\u5f97\u4e03\u8364\u516b\u7d20\uff0c\u56f4\u529d\u7684 \u4eba\u8d8a\u6765\u8d8a\u591a\uff0c\u6709\u7684\u7adf\u7136\u62cd\u624b\u53eb\u8d77\u6765\uff0c\u6b63\u5f53\u6b64\u65f6\uff0c\u5e97\u4e2d\u5ffd\u7136\u8d70\u51fa\u4e00\u4e2a\u80a5\u80d6\u7684\u4eba\uff0c\u6ee1\u8138\u6cb9\u5149\u5149 \u7684\uff0c\u624b\u91cc\u62ff\u7740\u4e24\u4e2a\u6838\u6843\uff0c\u6413\u5f97\u683c\u683c\u53d1\u54cd\u3002 \u3000\u3000\u90a3\u4eba\u91cd\u91cd\u5730\u54b3\u55fd\u4e86\u51e0\u58f0\uff0c\u90a3\u5e97\u4f19\u8ba1\u4e00\u542c\uff0c\u4fbf\u90fd\u4f4f\u624b\u4e86\u3002 \u3000\u3000\u590f\u82b8\u89c1\u90a3\u4e9b\u5e97\u4f19\u7a81\u5730\u4e00\u9f50\u505c\u624b\uff0c\u60ca\u5f02\u5730\u671d\u56db\u5468\u7565\u4e00\u5f20\u671b\uff0c\u4fbf\u770b\u89c1\u90a3\u80d6\u5b50\u7ad9\u5728\u95e8\u53e3\uff0c\u5979\u4e5f \u662f\u73b2\u73d1\u5fc3\u5de7\u7684\u4eba\uff0c\u5f53\u7136\u731c\u51fa\u90a3\u80d6\u5b50\u662f\u4e2a\u9996\u8111\u4eba\u7269\uff0c\u4fbf\u8d70\u4e0a\u524d\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u5582\uff0c\u4f60\u4eec\u7684\u5e97\u4f19\u90fd \u662f\u4e9b\u4ec0\u4e48\u4eba\u7269\uff0c\u600e\u4e48\u8fd9\u6837\u5b50\u5bf9\u5f85\u4e3b\u987e\u3002\u201d \u3000\u3000\u90a3\u80d6\u5b50\u7b11\u563b\u563b\u4ed6\u8bf4\uff1a\u201c\u8fd9\u4e5f\u4e0d\u602a\u4ed6\u4eec\uff0c\u4eca\u5929\u5927\u5e74\u521d\u4e00\uff0c\u5c0f\u53f7\u672c\u6765\u5c31\u4e0d\u5356\u4e1c\u897f\u7684\u3002\u201d \u3000\u3000\u590f\u82b8\u89c1\u8fd9\u80d6\u5b50\u4e5f\u662f\u8fd9\u6837\u7684\u8bf4\u6cd5\uff0c\u6c14\u5f80\u4e0a\u51b2\uff0c\u8bf4\uff1a\u201c\u4eca\u5929\u59d1\u5a18\u662f\u4e70\u5b9a\u4e86\u3002\u201d \u3000\u3000\u90a3\u80d6\u5b50\u4ecd\u7136\u7b11\u563b\u563b\u4ed6\u8bf4\uff1a\u201c\u4e70\u4e0d\u4e70\u662f\u4f60\u7684\u4e8b\uff0c\u5356\u4e0d\u5356\u53ef\u5c31\u662f\u6211\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u590f\u82b8\u5389\u58f0\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5f53\u6d82\u53bf\u7684\u751f\u610f\u4eba\uff0c\u90fd\u50cf\u5f3a\u76d7\u4e00\u6837\uff0c\u4eca\u5927\u59d1\u5a18\u5012\u8981\u6559\u8bad\u6559\u8bad\u4f60 \u4eec\u3002\u201d \u3000\u3000\u90a3\u80d6\u5b50\u542c\u590f\u82b8\u8bf4\u4ed6\u662f\u5f3a\u76d7\uff0c\u7b11\u5bb9\u4e00\u655b\uff0c\u53cc\u65e5\u7acb\u523b\u5c04\u51fa\u51cc\u4eba\u7684\u5149\u8292\uff0c\u7a81\u53c8\u54c8\u54c8\u72c2\u7b11\u9053\uff1a \u201c\u5c31\u51ed\u4f60\u90a3\u4e24\u624b\uff0c\u8981\u6559\u8bad\u6211\u53f6\u8001\u4e09\uff0c\u53ea\u6015\u6ca1\u6709\u90a3\u4e48\u5bb9\u6613\u3002\u201d \u3000\u3000\u4ed6\u7684\u7b11\u58f0\u6643\u5f97\u90a3\u4e48\u54cd\u5f7b\uff0c\u4f7f\u4eba\u6709\u4e00\u79cd\u523a\u8033\u7684\u611f\u89c9\uff0c\u4f46\u718a\u501c\u89c9\u5f97\u523a\u8033\u7684\uff0c\u5012\u4e0d\u662f\u4ed6\u7684\u7b11 \u58f0\uff0c\u800c\u662f\u4ed6\u53e3\u4e2d\u7684\u201c\u53f6\u8001\u4e09\u201d\u4e09\u5b57\uff0c\u718a\u501c\u6697\u5fd6\u9053\uff1a\u201c\u8fd9\u80d6\u5b50\u83ab\u975e\u662f\u957f\u6c5f\u6e21\u5934\u90a3\u4e24\u4e2a\u8be1\u5f02\u5ba2\u5546 \u7684\u5144\u5f1f\u2026\u2026\u201d \u3000\u3000\u4ed6\u4e00\u5ff5\u81f3\u6b64\uff0c\u4fbf\u8d70\u4e0a\u524d\u53bb\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u638c\u67dc\u7684\uff0c\u53ef\u662f\u59d3\u53f6\uff1f\u201d \u3000\u3000\u90a3\u53f6\u8001\u4e09\u7a81\u89c1\u4e2a\u8863\u886b\u8934\u697c\u7684\u6c49\u5b50\uff0c\u8d70\u8fc7\u6765\u8bf4\u8bdd\uff0c\u4ed6\u4e45\u5386\u6c5f\u6e56\uff0c\u76ee\u5149\u81ea\u662f\u9510\u5229\uff0c\u4e00\u773c\u4fbf\u770b \u51fa\u718a\u501c\u8eab\u6000\u6b66\u529f\uff0c\u4fbf\u4e5f\u4e0d\u6562\u6020\u6162\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u6562\u5f53\uff0c\u5144\u5f1f\u6b63\u662f\u59d3\u53f6\uff0c\u5144\u53f0\u6709\u4f55\u89c1\u6559\uff1f\u201d \u3000\u3000\u718a\u501c\u81ea\u7ba1\u4ece\u6000\u4e2d\u638f\u51fa\u90a3\u679a\u53e4\u94b1\uff0c\u5411\u90a3\u80d6\u5b50\u8bf4\uff1a\u201c\u638c\u67dc\u7684\u53ef\u8ba4\u5f97\u6b64\u7269\u3002\u201d \u3000\u3000\u90a3\u80d6\u5b50\u89c1\u4e86\u6b64\u7269\uff0c\u5b9a\u6674\u6ce8\u89c6\u4e86\u4e00\u4f1a\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u539f\u6765\u5144\u53f0\u662f\u5bb6\u5144\u597d\u53cb\uff0c\u8fd9\u771f\u662f\u5927\u6c34\u51b2 \u4e86\u9f99\u738b\u5e99\uff0c\u8fde\u81ea\u5bb6\u4eba\u90fd\u4e0d\u8ba4\u5f97\u4e86\u3002\u201d \u3000\u3000\u4ed6\u671d\u590f\u82b8\u4e00\u62f1\u624b\uff0c\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u4e5f\u522b\u751f\u6c14\u4e86\uff0c\u5feb\u8bf7\u91cc\u9762\u5750\uff0c\u4e24\u4f4d\u65e2\u662f\u5bb6\u5144\u597d\u53cb\uff0c\u522b\u8bf4\u4e70 \u8863\u670d\uff0c\u5c31\u662f\u62c6\u4e86\u8fd9\u5e97\uff0c\u4e5f\u6ca1\u5f97\u8bdd\u8bf4\u3002\u201d \u3000\u3000\u90a3\u80d6\u5b50\u7edd\u53e3\u4e0d\u63d0\u4ed6\u7684\u5144\u957f\u548c\u718a\u501c\u662f\u4f55\u4ea4\u60c5\uff0c\u77e5\u9053\u718a\u501c\u8981\u9009\u8863\u670d\uff0c\u4fbf\u9009\u4e86\u51e0\u5957\u7cbe\u7f8e\u534e\u4e3d \u7684\uff0c\u8fd8\u5e26\u7740\u5185\u8863\u88e4\u4e00\u8d77\u9001\u7ed9\u718a\u501c\uff0c\u600e\u4e48\u6837\u4e5f\u4e0d\u80af\u6536\u94b1\uff0c\u718a\u501c\u5fc3\u4e2d\u5374\u66f4\u5947\u602a\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u53f6\u5bb6 \u5144\u5f1f\u771f\u662f\u5947\u602a\uff0c\u4e0d\u77e5\u7a76\u7adf\u662f\u4f55\u6765\u8def\uff1f\u65e5\u540e\u6709\u673a\u4f1a\uff0c\u6211\u5012\u8981\u5f04\u4e2a\u6e05\u695a\u3002\u201d \u3000\u3000\u5750\u4e86\u4e00\u4f1a\uff0c\u53f6\u80d6\u5b50\u7edd\u53e3\u4e0d\u8c08\u6c5f\u6e56\u4e4b\u4e8b\uff0c\u590f\u82b8\u4fbf\u62c9\u7740\u718a\u501c\u8981\u8d70\u4e86\uff0c\u53f6\u80d6\u5b50\u518d\u4e09\u633d\u7559\u4e0d\u4f4f\uff0c \u4fbf\u4fcf\u58f0\u5bf9\u718a\u501c\u8bf4\uff1a\u201c\u5bb6\u5144\u65e2\u7136\u5c06\u6b64\u4fe1\u7269\u4ea4\u7ed9\u5144\u53f0\uff0c\u5144\u53f0\u4fbf\u662f\u6211\u53f6\u5bb6\u5144\u5f1f\u7684\u597d\u53cb\uff0c\u65e5\u540e\u65e0\u8bba\u4ec0 \u4e48\u4e8b\uff0c\u53ea\u8981\u7528\u5f97\u7740\u6211\u53f6\u8001\u4e09\u7684\uff0c\u53ea\u7ba1\u5230\u8fd9\u513f\u6765\uff0c\u5343\u4e07\u4e0d\u8981\u89c1\u5916\u3002\u201d \u3000\u3000\u4e24\u4e2a\u8d70\u51fa\u5e97\u5916\uff0c\u590f\u82b8\u4fbf\u5bf9\u718a\u60c6\u8bf4\u9053\uff1a\u201c\u4f60\u600e\u4e48\u4f1a\u8ba4\u8bc6\u8fd9\u6837\u7684\u4eba\uff1f\u201d \u3000\u3000\u718a\u501c\u53ea\u7ba1\u7b11\uff0c\u4e5f\u4e0d\u7b54\u590d\uff0c\u590f\u82b8\u9f13\u8d77\u5634\uff0c\u751f\u4e86\u534a\u5929\u7684\u6c14\uff0c\u5ffd\u53c8\u5657\u5730\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4ee5 \u540e\u4f60\u4e0d\u613f\u610f\u544a\u8bc9\u6211\u7684\u4e8b\uff0c\u6211\u4e5f\u4e0d\u95ee\u4f60\uff0c\u53ea\u662f\u6709\u4ef6\u4e8b\uff0c\u4f60\u5374\u4e00\u5b9a\u8981\u542c\u6211\u7684\u8bdd\uff0c\u4e0d\u7136\u6211\u5c31\u4e0d\u7406\u4f60 \u4e86\u3002\u201d \u3000\u3000\u718a\u501c\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u5440\uff1f\u201d \u3000\u3000\u590f\u82b8\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\uff0c\u5c31\u662f\u8d76\u7d27\u56de\u5230\u5ba2\u5e97\uff0c\u6362\u4e0a\u8863\u670d\uff0c\u628a\u4f60\u8eab\u4e0a\u7684\u8fd9\u5957\uff0c\u6254\u5f97\u8fdc\u8fdc\u7684\uff0c\u201d \u8bf4\u7740\u5979\u9f3b\u5b50\u4e00\u76b1\u53c8\u9053\uff1a\u201c\u8fd8\u8981\u6d17\u4e2a\u6fa1\u3002\u201d \u3000\u3000\u718a\u501c\u9053\uff1a\u201c\u786e\u5b9e\u4e5f\u8be5\u6d17\u4e2a\u6fa1\u4e86\uff0c\u6211\u7b97\u7b97\u770b\uff0c\u5df2\u7ecf\u6709\u4e09\u4e2a\u6708\u6ca1\u6709\u6d17\u6fa1\u4e86\u3002\u201d \u3000\u3000\u590f\u82b8\u5403\u5403\u7b11\u51fa\u58f0\u6765\uff0c\u4e00\u6478\u989d\u89d2\uff0c\u4f5c\u6655\u5012\u72b6\u8bf4\uff1a\u201c\u5929\u5440\uff0c\u4f60\u8eab\u4e0a\u7684\u6ce5\uff0c\u8be5\u6709\u5341\u65a4\u4e86\u3002\u201d \u3000\u3000\u718a\u501c\u75db\u75db\u5feb\u5feb\u5730\u6d17\u4e86\u4e2a\u6fa1\uff0c\u5c06\u6362\u4e0b\u7684\u8863\u670d\u5377\u6210\u4e00\u5305\uff0c\u53ea\u7a7f\u7740\u5e03\u889c\u8d70\u51fa\u6765\uff0c\u53eb\u8fc7\u5e97\u5c0f\u4e8c\uff0c \u9053\uff1a\u201c\u9ebb\u70e6\u4f60\uff0c\u66ff\u6211\u4e70\u53cc\u978b\u5b50\u6765\uff0c\u5927\u5c0f\u5dee\u4e0d\u591a\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u54ce\u5440\uff0c\u5e74\u521d\u4e00\u53ef\u4e70\u4e0d\u5230\u978b\u5b50\uff0c\u8fd9\u4e48\u7740\uff0c\u6211\u521a\u4e70\u4e86\u53cc\u65b0\u978b\uff0c\u5927\u5c0f\u4e5f\u5408\u9002\uff0c\u4f60 \u5bb6\u5c31\u5c06\u5c31\u7740\u5148\u7a7f\u5427\u3002\u201d \u3000\u3000\u718a\u501c\u9053\uff0c\u201c\u8fd9\u6837\u4e5f\u597d\u3002\u201d \u3000\u3000\u718a\u501c\u4ee5\u524d\u6240\u7a7f\u7684\uff0c\u4ff1\u662f\u6781\u4e3a\u6734\u7d20\u7684\u8863\u886b\uff0c\u6b64\u523b\u6362\u4e0a\u4e86\u590f\u82b8\u6240\u8d2d\u7684\u8863\u670d\uff0c\u66f4\u663e\u5f97\u82f1\u4fca\u633a \u62d4\uff0c\u98d8\u9038\u51fa\u7fa4\uff0c\u590f\u82b8\u89c1\u4e86\uff0c\u5f00\u5fc3\u4ed6\u8bf4\u9053\uff1a\u201c\u4f60\u77a7\u8fd9\u6837\u591a\u597d\uff0c\u4ee5\u540e\u6211\u53ef\u4e0d\u51c6\u4f60\u518d\u5f04\u5f97\u810f\u516e\u516e\u7684 \u4e86\u3002\u201d \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5e97\u4f19\u9001\u6765\u4e9b\u5e74\u83dc\uff0c\u6c5f\u5357\u65e7\u4fd7\uff0c\u6bcf\u5bb6\u6bcf\u6237\uff0c\u8fc7\u5e74\u65f6\u90fd\u8981\u51c6\u5907\u5e74\u83dc\uff0c\u5bb6\u91cc\u672c\u6765\u53ea \u662f\u5341\u4eba\uff0c\u4e5f\u8981\u51c6\u5907\u5341\u4e00\u4e2a\u4eba\u7684\u83dc\uff0c\u5ba2\u6808\u91cc\u81ea\u7136\u66f4\u662f\u5982\u6b64\uff0c\u4ed6\u4eec\u4e5f\u77e5\u9053\u5916\u9762\u65e0\u5904\u53bb\u5403\uff0c\u5e97\u4f19\u9001 \u6765\u65f6\uff0c\u4ed6\u6536\u4e0b\u4e86\uff0c\u53c8\u7ed9\u4e86\u5e97\u5c0f\u4e8c\u4e00\u4e9b\u94f6\u5b50\u3002 \u3000\u3000\u751f\u957f\u5728\u5317\u65b9\u7684\u4eba\uff0c\u5927\u591a\u5e73\u65e5\u90fd\u4f1a\u559d\u4e2a\u4e24\u676f\uff0c\u5fa1\u5fa1\u5bd2\u6c14\uff0c\u718a\u501c\u867d\u7136\u4f1a\u559d\uff0c\u5374\u4e0d\u5584\u996e\uff0c\u90a3\u590f \u82b8\u7684\u9152\u91cf\u5374\u597d\uff0c\u718a\u60c6\u7b11\u8bf4\uff1a\u201c\u60f3\u4e0d\u5230\uff0c\u4f60\u8fd8\u4f1a\u559d\u9152\u3002\u201d \u3000\u3000\u590f\u82b8\u628a\u9152\u676f\u653e\u4e0b\uff0c\u8bf4\uff1a\u201c\u6211\u5e73\u5e38\u4e5f\u4e0d\u559d\u7684\uff0c\u4eca\u5929\u5fc3\u91cc\u9ad8\u5174\uff0c\u624d\u966a\u4f60\u559d\u4e00\u70b9\uff0c\u4f60\u8fd8\u8981\u7b11 \u6211\uff0c\u90a3\u6211\u5c31\u4e0d\u559d\u4e86\uff0c\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u718a\u501c\u8d76\u7d27\u8bf4\u9053\uff1a\u201c\u4f60\u559d\u561b\uff0c\u6211\u53c8\u6ca1\u6709\u7b11\u4f60\uff0c\u53ea\u4e0d\u8fc7\u6709\u70b9\u5947\u602a\u4f60\u4f1a\u559d\u9152\u800c\u5df2\u3002\u201d \u3000\u3000\u590f\u82b8\u8bf4\uff1a\u201c\u6211\u5341\u5c81\u7684\u65f6\u5019\uff0c\u5c31\u4f1a\u559d\u9152\u4e86\uff0c\u90a3\u65f6\u6211\u966a\u7740\u7236\u4eb2\u5403\u996d\uff0c\u6211\u7239\u6bcf\u987f\u90fd\u8981\u559d\u9152\uff0c\u559d \u4e86\u9152\u4e4b\u540e\u5c31\u53f9\u6c14\u3001\u96be\u53d7\uff0c\u6211\u5988\u770b\u4e86\u4e5f\u4e0d\u7ba1\u3002\u201d\u5979\u8bf4\u7740\u773c\u5708\u90fd\u7ea2\u4e86\uff0c\u53c8\u8bf4\uff1a\u201c\u6211\u7239\u5e38\u8bf4\u4e00\u4e2a\u4eba \u4e00\u751f\u4e0d\u80fd\u505a\u9519\u4e00\u4ef6\u4e8b\uff0c\u53ea\u8981\u4ed6\u505a\u9519\u4e00\u6b21\uff0c\u4ed6\u7684\u4e00\u8f88\u5b50\u90fd\u4f1a\u75db\u82e6\u7684\u3002\u201d \u3000\u3000\u718a\u501c\u8bf4\uff1a\u201c\u8fd9\u4e2a\u5012\u4e0d\u7136\uff0c\u4eba\u975e\u5723\u8d24\uff0c\u7109\u80fd\u65e0\u8fc7\uff0c\u53ea\u8981\u505a\u9519\u4e8b\u540e\uff0c\u77e5\u9053\u4e0d\u5bf9\uff0c\u4e5f\u5c31\u7b97 \u4e86\u3002\u201d \u3000\u3000\u590f\u82b8\u8bf4\uff1a\u201c\u662f\u5440\uff0c\u6211\u4e5f\u4e0d\u77e5\u9053\u6211\u7239\u4e3a\u4ec0\u4e48\u5e38\u8fd9\u6837\u8bf4\uff0c\u6211\u4e5f\u50cf\u4f60\u7684\u8bf4\u6cd5\uff0c\u4ed6\u8001\u4eba\u5bb6\u5c31\u8bf4\u6211 \u5e74\u7eaa\u5c0f\uff0c\u8fd8\u4e0d\u61c2\uff0c\u4ee5\u540e\u5c31\u4f1a\u77e5\u9053\uff0c\u6211\u7239\u8bf4\u4ed6\u5c31\u662f\u4ee5\u524d\u505a\u4e86\u4e9b\u9519\u4e8b\uff0c\u5f04\u6210\u4e00\u8f88\u5b50\u5fc3\u91cc\u90fd\u4e0d\u8212 \u670d\u3002\u201d \u3000\u3000\u5979\u4f4e\u4e0b\u4e86\u5934\uff0c\u50cf\u662f\u5728\u4e3a\u90a3\u8001\u4eba\u96be\u53d7\uff0c\u718a\u501c\u4f38\u8fc7\u624b\u53bb\uff0c\u6e29\u67d4\u5730\u63e1\u4f4f\u5979\u7684\u624b\u3002 \u3000\u3000\u590f\u82b8\u4f4e\u4f4e\u4ed6\u8bf4\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u4e3a\u4ec0\u4e48\uff0c\u628a\u4ec0\u4e48\u8bdd\u90fd\u544a\u8bc9\u4f60\u4e86\u3002\u201d \u3000\u3000\u5ffd\u7136\u5979\u53c8\u62ac\u8d77\u5934\u6765\uff0c\u5c55\u989c\u7b11\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u8c08\u8fd9\u4e9b\uff0c\u8fd8\u662f\u8c08\u8c08\u522b\u7684\u9ad8\u5174\u7684\u4e8b\uff0c\u6211\u544a\u8bc9\u4f60\u8fd9 \u4e48\u591a\uff0c\u4f60\u4e5f\u6838\u5bf9\u6211\u8bf4\u8bf4\u4f60\u7684\u4e86\u3002\u201d \u3000\u3000\u718a\u501c\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u7684\u8eab\u4e16\uff0c\u8bf4\u8d77\u6765\u66f4\u96be\u53d7\uff0c\u8fd8\u662f\u4ee5\u540e\u518d\u8bf4\u5427\u3002\u201d \u3000\u3000\u590f\u82b8\u8bf4\uff1a\u201c\u597d\uff0c\u4eca\u5929\u6211\u4eec\u4e0d\u8bf4\u626b\u5174\u7684\u8bdd\uff0c\u6211\u8981\u4eca\u5929\u6210\u4e3a\u6211\u6700\u5feb\u4e50\u7684\u4e00\u5929\u3002\u201d \u3000\u3000\u5979\u4e3e\u8d77\u676f\u6765\uff0c\u548c\u718a\u501c\u559d\u4e86\u4e00\u676f\uff0c\u53c8\u8bf4\uff1a\u201c\u4f60\u600e\u4e48\u4f1a\u5728\u8def\u4e0a\u8ddf\u4eba\u6253\u67b6\uff1f\u6211\u542c\u4eba\u8bf4\u8def\u4e0a\u6709\u4eba \u6253\u67b6\uff0c\u8d70\u51fa\u6765\u4f60\u5df2\u7ecf\u7ad9\u5728\u65c1\u8fb9\u770b\u4e86\uff0c\u90a3\u4e2a\u9a91\u9a6c\u7684\u4eba\u6b63\u5728\u8000\u6b66\u626c\u5a01\u5730\u6307\u7740\u4f60\u8bf4\u8bdd\uff0c\u4f60\u4e5f\u4e0d\u56de \u5634\uff0c\u6211\u53ea\u5728\u66ff\u4f60\u751f\u6c14\uff0c\u63a5\u7740\u4ed6\u81ea\u5df1\u4e5f\u5728\u8857\u4e0a\u6253\u8d77\u67b6\u672a\uff0c\u90a3\u4eba\u6b66\u529f\u5012\u4e0d\u9519\uff0c\u5176\u5b9e\u6211\u4e5f\u4e0d\u89c1\u5f97\u6253 \u5f97\u8fc7\u4ed6\u3002\u201d \u3000\u3000\u539f\u6765\u5979\u4e45\u5c45\u585e\u5916\uff0c\u4e2d\u539f\u7684\u8c6a\u6770\uff0c\u5979\u6839\u672c\u4e00\u4e2a\u4e5f\u4e0d\u77e5\u9053\uff0c\u662f\u4ee5\u5b64\u5cf0\u4e00\u5251\u867d\u7136\u4eab\u6709\u76db\u540d\uff0c\u5979 \u4e5f\u6ca1\u6709\u542c\u8bf4\u8fc7\u3002 \u3000\u3000\u590f\u82b8\u53c8\u8bf4\u9053\uff1a\u201c\u770b\u4f60\u7684\u6837\u5b50\uff0c\u5927\u6982\u8fde\u6211\u4e5f\u6253\u4e0d\u8fc7\uff0c\u4ee5\u540e\u4f60\u8981\u966a\u6211\u56de\u5bb6\u53bb\uff0c\u6211\u5c31\u53eb\u6211\u7239\u6559 \u4f60\u529f\u592b\uff0c\u4ee5\u540e\u5c31\u4e0d\u4f1a\u7ed9\u522b\u4eba\u6b3a\u8d1f\u4e86\u3002\u201d \u3000\u3000\u5979\u4ee5\u4e3a\u718a\u501c\u90a3\u5929\u53d7\u4e86\u522b\u4eba\u7684\u6c14\uff0c\u5403\u4e86\u4e8f\uff0c\u5979\u4e5f\u4e0d\u77e5\u9053\u718a\u501c\u7684\u6b66\u529f\uff0c\u8fde\u5979\u7684\u7239\u7239\u4e5f\u4e0d\u884c\uff0c \u53fd\u53fd\u5471\u5471\u4ed6\u8bf4\u4e86\u534a\u5929\uff0c\u718a\u501c\u5fae\u7b11\u542c\u7740\uff0c\u4e5f\u4e0d\u9053\u7834\uff0c\u5fc3\u60f3\uff1a\u201c\u4ee5\u540e\u5979\u89c1\u4e86\u6211\u7684\u6b66\u529f\uff0c\u4e00\u5b9a\u8981\u66f4 \u6b22\u559c\u4e86\u3002\u201d \u3000\u3000\u8bf4\u7740\u8bf4\u7740\uff0c\u590f\u82b8\u8138\u4e0a\u9732\u51fa\u6625\u82b1\u822c\u7684\u7b11\u5bb9\uff0c\u773c\u5149\u8f7b\u8f7b\u5730\u63a0\u8fc7\u718a\u501c\u5bbd\u5927\u800c\u5f3a\u58ee\u7684\u80f8\u819b\uff0c\u505c\u7559 \u5728\u4ed6\u7684\u8138\u4e0a\uff0c\u8f7b\u8f7b\u4ed6\u8bf4\uff1a\u201c\u4e0d\u8fc7\u6211\u73b0\u5728\u53ef\u4e0d\u8981\u56de\u5bb6\uff0c\u6211\u8981\u4f60\u966a\u7740\u6211\uff0c\u9ad8\u9ad8\u5174\u5174\u5730\u73a9\u4e00\u6bb5\u65f6 \u5019\u3002\u201d \u3000\u3000\u5979\u8138\u4e0a\u73b0\u51fa\u5e78\u798f\u7684\u61a7\u61ac\u8bf4\uff1a\u201c\u6211\u4eec\u987a\u7740\u957f\u6c5f\u8d70\uff0c\u8d70\u5230\u54ea\u513f\uff0c\u73a9\u5230\u54ea\u513f\uff0c\u4f60\u4e5f\u8981\u4e70\u5339\u597d \u9a6c\uff0c\u6211\u4eec\u53ef\u4ee5\u5728\u539f\u91ce\u4e0a\u4e00\u8d77\u5954\u9a70\uff0c\u7d2f\u4e86\uff0c\u6211\u4eec\u5c31\u6b47\u4e0b\u6765\u804a\u5929\uff0c\u6211\u771f\u559c\u6b22\u6c5f\u5357\uff0c\u8fd9\u91cc\u7684\u4e00\u5207\uff0c \u90fd\u50cf\u662f\u8fd9\u4e48\u7f8e\uff0c\u65e0\u8bba\u6625\u3001\u590f\u3001\u79cb\u3001\u51ac\uff0c\u90fd\u53ef\u7231\u6781\u4e86\u3002\u201d \u3000\u3000\u718a\u501c\u63e1\u7740\u5979\u7684\u624b\uff0c\u6ca1\u6709\u8bf4\u8bdd\uff0c\u4f46\u4ece\u4ed6\u7684\u773c\u5149\u91cc\uff0c\u53ef\u4ee5\u770b\u51fa\u4ed6\u4e5f\u662f\u90a3\u4e48\u5e78\u798f\uff0c\u4eba\u4eec\u5728\u5e78\u798f \u7684\u65f6\u5019\uff0c\u8bf4\u8bdd\u53cd\u662f\u591a\u4f59\u7684\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u5728\u5f53\u6d82\uff0c\u4e00\u803d\u5c31\u662f\u597d\u51e0\u5929\uff0c\u5f53\u6d82\u9644\u8fd1\u4e4b\u91c7\u77f3\u77fe\uff0c\u672c\u76db\u4ea7\u94c1\uff0c\u718a\u501c\u7684\u501a\u5929\u5251\u4e22\u4e86\uff0c\u5c31 \u5728\u5f53\u6d82\u9009\u4e86\u628a\u5251\uff0c\u5012\u4e5f\u751a\u662f\u950b\u5229\u3002 \u3000\u3000\u5979\u53c8\u5728\u5f53\u6d82\u7684\u9a6c\u5e02\u91cc\uff0c\u66ff\u718a\u501c\u9009\u4e86\u5339\u9a6c\uff0c\u914d\u4e0a\u9c9c\u660e\u7684\u978d\u5b50\uff0c\u661f\u975e\u826f\u9a79\uff0c\u4f46\u770b\u4e0a\u53bb\u4e5f\u86ee\u9a8f \u7684\uff0c\u8fd9\u6837\u4e00\u6765\uff0c\u718a\u501c\u7adf\u50cf\u662f\u51fa\u6765\u6e38\u5386\u7684\u5bcc\u5bb6\u516c\u5b50\uff0c\u718a\u501c\u5fc3\u4e2d\u6697\u81ea\u597d\u7b11\uff0c\u8fd9\u51e0\u4e2a\u6708\u6765\uff0c\u4ed6\u7684\u8eab \u4efd\u53d8\u5f97\u591a\u5feb\u5440\uff0c\u50cf\u6f14\u620f\u4e00\u6837\uff0c\u5176\u5b9e\u4eba\u751f\uff0c\u4e0d\u4e5f\u5c31\u662f\u6f14\u620f\u5417\uff01 \u3000\u3000\u4ed6\u4eec\u4ece\u5f53\u6d82\uff0c\u5230\u829c\u6e56\uff0c\u8fc7\u9c81\u6e2f\u3001\u83b7\u6e2f\uff0c\u5230\u94dc\u9675\uff0c\u4e00\u8def\u4e0a\uff0c\u4eba\u4eec\u4e0d\u518d\u4ee5\u60ca\u5947\u7684\u773c\u5149\u770b\u7740\u4ed6 \u4eec\uff0c\u800c\u662f\u4ee5\u7fa1\u6155\u7684\u795e\u8272\uff0c\u7537\u7684\u5b9b\u5982\u51cc\u98ce\u7389\u6811\uff0c\u5973\u7684\u4e5f\u662f\u5a07\u7f8e\u5982\u82b1\uff0c\u518d\u52a0\u4e0a\u826f\u9a79\u8f7b\u88d8\uff0c\u8863\u5c65\u9c9c \u660e\uff0c\u600e\u4e0d\u53eb\u4eba\u7fa1\u715e\u3002 \u3000\u3000\u51ac\u5929\u5230\u4e86\uff0c\u6625\u5929\u4e5f\u5c31\u5feb\u4e86\uff0c\u4ed6\u4eec\u8d70\u5f97\u6781\u6162\uff0c\u5230\u6e56\u5317\u7684\u65f6\u5019\uff0c\u5df2\u662f\u6625\u5929\u4e86\u3002 \u3000\u3000\u6e56\u5317\u672c\u4e3a\u53e4\u4e91\u68a6\u5927\u6cfd\u65e7\u8ff9\uff0c\u6e56\u6cca\u6781\u591a\uff0c\u8fd9\u4e5f\u662f\u585e\u5916\u6240\u6ca1\u6709\u7684\u3002\u590f\u82b8\u4e00\u8def\u4e0a\u6307\u6307\u70b9\u70b9\uff0c\u9ad8 \u5174\u5f97\u5f88\u3002\u6625\u5929\u5230\u4e86\uff0c\u4ed6\u4eec\u7684\u5fc3\u91cc\u4e5f\u67d3\u4e0a\u6625\u7684\u6c14\u606f\u4e86\u3002 \u3000\u3000\u8d70\u8fc7\u9102\u57ce\u7684\u65f6\u5019\uff0c\u4ed6\u4eec\u770b\u5230\u4e00\u961f\u9556\u8f66\uff0c\u9556\u5934\u662f\u4e2a\u4e2d\u5e74\u80d6\u5b50\u3001\u9a91\u5728\u9a6c\u4e0a\uff0c\u987e\u76fc\u81ea\u96c4\uff0c\u5012\u4e5f \u795e\u6c14\u5f97\u5f88\uff0c\u9556\u8f66\u5f88\u591a\uff0c\u770b\u6837\u5b50\u4fdd\u7684\u662f\u4e00\u8d9f\u91cd\u9556\uff0c\u4f46\u9556\u5c40\u91cc\u8fde\u9556\u5934\u5e26\u4f19\u8ba1\uff0c\u4e00\u4e2a\u4e2a\u6837\u5b50\u90fd\u8f7b\u677e \u5f97\u5f88\uff0c\u50cf\u662f\u660e\u77e5\u4e0d\u4f1a\u6709\u4eba\u6765\u593a\u9556\u7684\u6837\u5b50\u3002 \u3000\u3000\u718a\u501c\u659c\u773c\u671b\u7740\u90a3\u9556\u5934\uff0c\u53ea\u89c1\u4ed6\u76ee\u5149\u677e\u6563\uff0c\u8eab\u4e0a\u7684\u8089\uff0c\u4e5f\u80d6\u5f97\u53d1\u677e\u4e86\uff0c\u5fc3\u60f3\uff1a\u201c\u6b64\u4eba\u5c31\u662f \u6709\u6b66\u529f\uff0c\u4e5f\u597d\u4e0d\u5230\u54ea\u91cc\u53bb\uff0c\u9556\u884c\u91cc\u600e\u4f1a\u8981\u4ed6\u6765\u4fdd\u8fd9\u8d9f\u91cd\u9556\uff0c\u96be\u9053\u6e56\u5317\u6b66\u6797\u4e2d\uff0c\u6ca1\u6709\u80fd\u4eba \u5417\uff1f\u201d \u3000\u3000\u90a3\u9556\u961f\u548c\u718a\u501c\u53ca\u590f\u82b8\uff0c\u540c\u5bbf\u4e00\u4e2a\u5ba2\u6808\u91cc\uff0c\u665a\u4e0a\uff0c\u9556\u4f19\u4eec\u4e00\u4e2a\u4e2a\u653e\u60c5\u75db\u996e\uff0c\u53c8\u8d4c\u53c8\u95f9\uff0c\u90a3 \u9556\u5934\u4e5f\u4e0d\u53bb\u7ba1\uff0c\u718a\u501c\u66f4\u662f\u5947\u602a\u3002 \u3000\u3000\u90a3\u590f\u82b8\u89c1\u4e86\u9556\u961f\uff0c\u5374\u9ad8\u5174\u5f97\u5f88\uff0c\u8ddf\u718a\u501c\u8bf4\uff1a\u201c\u4f60\u770b\uff0c\u66ff\u4eba\u4fdd\u9556\u4e5f\u633a\u597d\u7684\uff0c\u8fd8\u53ef\u4ee5\u4e58\u6b64\u5230 \u5404\u5904\u53bb\u6e38\u73a9\uff0c\u53ef\u60dc\u6211\u662f\u4e2a\u5973\u7684\uff0c\u9556\u5c40\u91cc\u53c8\u6ca1\u6709\u5973\u9556\u5934\uff0c\u4e0d\u7136\uff0c\u6211\u4e5f\u8981\u53bb\u4fdd\u9556\u4e86\u3002\u201d \u3000\u3000\u5979\u671b\u4e86\u718a\u501c\u4e00\u773c\uff0c\u53c8\u8bf4\uff1a\u201c\u6211\u7239\u7239\u8bf4\u5f53\u5e74\u4ed6\u4e5f\u662f\u4fdd\u9556\u7684\u5462\u3002\u201d \u3000\u3000\u5403\u5b8c\u4e86\u996d\uff0c\u590f\u82b8\u62d6\u7740\u718a\u501c\u5230\u5e97\u95e8\u53e3\u53bb\uff0c\u770b\u90a3\u63d2\u5728\u95e8\u53e3\u7684\u9556\u65d7\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u9556\u65d7\u7ee3\u5f97\u751a\u662f\u7c97\u52a3\uff0c\u8d28\u91cf\u4e5f\u4e0d\u597d\uff0c\u4e0a\u9762\u6709\u201c\u6b66\u5a01\u201d\u4e24\u4e2a\u5927\u5b57\uff0c\u65c1\u8fb9\u7ee3\u7740\u4e5d\u628a\u5c0f \u5251\uff0c\u6bcf\u628a\u7684\u5934\u5c3e\uff0c\u90fd\u8fde\u5728\u4e00\u8d77\u3002 \u3000\u3000\u90a3\u9556\u5c40\u6709\u4e2a\u4f19\u8ba1\u7ad9\u5728\u95e8\u53e3\uff0c\u770b\u89c1\u6709\u4eba\u5728\u6ce8\u610f\u9556\u65d7\uff0c\u518d\u4e00\u770b\u53ea\u662f\u4e24\u4e2a\u5e74\u8f7b\u7537\u5973\uff0c\u6837\u5b50\u53c8\u6587 \u6c14\u5f97\u5f88\uff0c\u80c6\u5b50\u4e5f\u7acb\u523b\u5c31\u5927\u4e86\u8d77\u6765\uff0c\u5927\u6a21\u5927\u6837\u5730\uff0c\u8d70\u4e86\u8fc7\u6765\uff0c\u5927\u58f0\u53eb\u559d\u9053\uff1a\u201c\u5582\uff0c\u4f60\u4eec\u770b\u4ec0 \u4e48\uff1f\u201d \u3000\u3000\u590f\u82b8\u8bf4\uff1a\u201c\u770b\u770b\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d \u3000\u3000\u90a3\u9556\u4f19\u8bf4\uff1a\u201c\u4ec0\u4e48\u4e1c\u897f\u90fd\u53ef\u4ee5\u770b\uff0c\u5c31\u662f\u8fd9\u9556\u65d7\uff0c\u5374\u968f\u4fbf\u770b\u4e0d\u5f97\u3002\u201d \u3000\u3000\u590f\u82b8\u5374\u751f\u6c14\u9053\uff1a\u201c\u6211\u504f\u770b\u4e0d\u53ef\uff01\u201d \u3000\u3000\u90a3\u9556\u4f19\u7c97\u5406\u4e86\u4e00\u58f0\uff0c\u8bf4\uff1a\u201c\u770b\u4e0d\u51fa\u4f60\u8fd9\u5c0f\u599e\u513f\uff0c\u6c14\u5012\u662f\u6ee1\u58ee\u7684\uff0c\u6211\u529d\u4f60\u4e58\u65e9\u8ddf\u4f60\u8001\u516c\u8dd1 \u8d70\uff0c\u4e0d\u7136\u5927\u7237\u4e00\u751f\u6c14\uff0c\u628a\u4f60\u4eec\u4e24\u4e2a\u5a03\u5a03\u90fd\u6253\u6241\u4e86\u3002\u201d \u3000\u3000\u590f\u82b8\u6c14\u5f80\u4e0a\u649e\uff0c\u6b63\u8981\u53d8\u8138\uff0c\u718a\u501c\u4e00\u60f3\uff0c\u5728\u95f9\u5e02\u4e4b\u4e2d\uff0c\u4f55\u5fc5\u4e3a\u4e86\u5c0f\u4e8b\uff0c\u8ddf\u8fd9\u7b49\u4eba\u95f9\u6c14\uff0c\u786c \u62c9\u7740\u590f\u82b8\uff0c\u5728\u91cc\u9762\u8d70\u4e86\u3002 \u3000\u3000\u590f\u82b8\u4f4e\u58f0\u6c14\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u62c9\u6211\uff0c\u6211\u4e00\u5b9a\u8981\u6559\u8bad\u6559\u8bad\u90a3\u5bb6\u4f19\u3002\u201d \u3000\u3000\u718a\u501c\u529d\u9053\uff1a\u201c\u7b97\u4e86\uff0c\u7b97\u4e86\uff0c\u6211\u4eec\u53c8\u4f55\u5fc5\u8ddf\u90a3\u79cd\u4eba\u4e00\u822c\u89c1\u8bc6\u3002\u201d \u3000\u3000\u590f\u82b8\u9053\uff1a\u201c\u90a3\u9556\u4f19\u771f\u662f\u53ef\u6068\u6781\u4e86\uff0c\u60f3\u4e0d\u5230\u9556\u5c40\u91cc\u7684\u4eba\uff0c\u8fd9\u6837\u4e0d\u662f\u73a9\u610f\u513f\u3002\u201d \u3000\u3000\u8fd9\u65f6\u90a3\u80d6\u5b50\u9556\u5934\u6b63\u597d\u8d70\u51fa\u6765\uff0c\u521a\u597d\u542c\u5230\u4e86\u590f\u82b8\u7684\u8fd9\u8bdd\uff0c\u4ed6\u770b\u4e86\u4e24\u4eba\u4e00\u773c\uff0c\u89c1\u662f\u4e24\u4e2a\u8863\u5c65 \u534e\u7f8e\u7684\u5c11\u5e74\uff0c\u4f46\u4ed6\u6bd5\u7adf\u4e45\u8d70\u6c5f\u6e56\uff0c\u773c\u5149\u5389\u5bb3\uff0c\u89c1\u8fd9\u4e24\u9752\u5e74\u867d\u7136\u6587\u79c0\uff0c\u4f46\u5374\u5e26\u7740\u4e00\u80a1\u82f1\u6c14\uff0c\u5c24 \u5176\u7537\u7684\u66f4\u662f\u795e\u6c14\u5185\u8574\uff0c\u53cc\u76ee\u5e26\u91c7\uff0c\u4e24\u592a\u9633\u7a74\u9ad8\u9ad8\u9f13\u8d77\uff0c\u663e\u89c1\u662f\u5185\u529f\u5df2\u6709\u6781\u539a\u6839\u5e95\u7684\uff0c\u7740\u7136\u88ab \u9556\u4f19\u65e0\u5fc3\u5f97\u7f6a\u4e86\uff0c\u603b\u662f\u4e0d\u597d\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u7b11\u563b\u563b\u5730\u8d70\u8fc7\u53bb\uff0c\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\u8bf7\u4e86\uff0c\u4e0d\u77e5\u9053\u655d\u9556\u884c\u7684\u54ea\u4e2a\u8822\u624d\uff0c\u60f9\u4e86\u4e24\u4f4d \u7684\u6c14\uff0c\u5728\u4e0b\u4e00\u5b9a\u8981\u597d\u597d\u544a\u8beb\u4ed6\u4eec\u3002\u201d \u3000\u3000\u718a\u501c\u89c1\u4ed6\u751a\u662f\u5ba2\u6c14\uff0c\u4e5f\u8bf4\u9053\uff1a\u201c\u6ca1\u6709\u4ec0\u4e48\uff0c\u53ea\u662f\u4e00\u70b9\u5c0f\u4e8b\u7f62\u4e86\u3002\u201d \u3000\u3000\u590f\u82b8\u5374\u62a2\u7740\u8bf4\uff1a\u201c\u4f60\u4eec\u9556\u884c\u7684\u4f19\u8ba1\u600e\u4e48\u90a3\u4e48\u51f6\uff0c\u4eba\u5bb6\u770b\u9556\u65d7\u90fd\u4e0d\u884c\u3002\u201d \u3000\u3000\u90a3\u80d6\u5b50\u9556\u5934\u7b11\u5475\u5475\u4ed6\u8bf4\u9053\uff1a\u201c\u8fd9\u5012\u8981\u602a\u6211\u4e86\uff0c\u53ea\u56e0\u90a3\u9556\u65d7\u662f\u6b66\u5f53\u5c71\u4e0a\u4f20\u4e0b\u6765\u7684\uff0c\u655d\u9556\u5c40 \u4ed7\u7740\u90a3\u9556\u65d7\uff0c\u884c\u8d70\u5404\u7701\u90fd\u6ca1\u6709\u51fa\u8fc7\u4e8b\u513f\uff0c\u6240\u4ee5\u5728\u4e0b\u624d\u53eb\u9556\u4f19\u4eec\u7279\u522b\u5b88\u7740\u90a3\u65d7\u5b50\u3002\u201d \u3000\u3000\u4ed6\u54c8\u54c8\u7b11\u4e86\u58f0\uff0c\u8bf4\uff1a\u201c\u4e0d\u8fc7\uff0c\u6211\u6ca1\u60f3\u5230\u90a3\u9556\u4f19\u600e\u5730\u4e0d\u61c2\u4e8b\uff0c\u50cf\u4e24\u4f4d\u8fd9\u6837\u7684\u4eba\u7269\uff0c\u4e0d\u8981\u8bf4 \u770b\u4e0a\u4e24\u773c\uff0c\u5c31\u662f\u8981\u5c06\u9556\u65d7\u62ff\u53bb\uff0c\u6211\u53f2\u8001\u4e09\u4e5f\u53ea\u6709\u62f1\u624b\u5949\u9001\u7684\u3002\u201d \u3000\u3000\u590f\u82b8\u4e00\u542c\u8fd9\u4eba\u8bb2\u8bdd\u5012\u662f\u5ba2\u6c14\u5f97\u5f88\uff0c\u5979\u4e0d\u77e5\u9053\u4ed6\u8bdd\u4e2d\u4e5f\u5e26\u523a\u7684\uff0c\u53cd\u800c\u6c14\u5e73\u4e86\u3002 \u3000\u3000\u718a\u501c\u4e00\u542c\u8fd9\u9556\u65d7\u662f\u6b66\u5f53\u5c71\u4e0a\u7684\uff0c\u4fbf\u7559\u4e86\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u77e5\u539f\u6765\u8d35\u9556\u5934\u662f\u6b66\u5f53\u5c71\u4e0a\u6765\u7684\uff0c \u4e0d\u77e5\u9601\u4e0b\u4e0e\u6b66\u5f53\u56db\u5b50\u662f\u600e\u4e2a\u79f0\u547c\uff1f\u201d \u3000\u3000\u90a3\u53f2\u80d6\u5b50\u8fd8\u662f\u6ee1\u9762\u5e26\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u54ea\u91cc\u9ad8\u6500\u5f97\u4e0a\u56db\u4eea\u5251\u5ba2\uff0c\u53ea\u4e0d\u8fc7\u655d\u9556\u5c40\u7684\u603b\u9556\u5934 \u4e5d\u5bab\u8fde\u73af\u5251\u738b\u9521\u4e5d\uff0c\u662f\u6b66\u5f53\u56db\u5b50\u7684\u5c0f\u5e08\u5f1f\u7f62\u4e86\u3002\u201d \u3000\u3000\u718a\u501c\u8bf4\uff1a\u201c\u539f\u6765\u8d35\u9556\u5c40\u7684\u603b\u9556\u5934\u4e43\u6b66\u5f53\u56db\u5b50\u7684\u5e08\u5f1f\uff0c\u5c0f\u5f1f\u4e0e\u6b66\u5f53\u56db\u4f4d\u9053\u957f\u4e5f\u662f\u7d20\u8bc6\uff0c\u65e5 \u540e\u8fd8\u8bf7\u66ff\u5c0f\u5f1f\u5411\u8d35\u9556\u5934\u95ee\u597d\uff0c\u201d\u53f2\u80d6\u5b50\u5475\u5475\u7b11\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\u6765\u5927\u5bb6\u539f\u90fd\u662f\u4e00\u5bb6\u4eba\u4e86\uff0c\u4e0d\u77e5\u5144 \u53f0\u9ad8\u59d3\u5927\u540d\uff0c\u8fd8\u671b\u89c1\u544a\u3002\u201d \u3000\u3000\u718a\u501c\u9053\uff1a\u201c\u5728\u4e0b\u718a\u501c\u3002\u201d \u3000\u3000\u53f2\u80d6\u5b50\u773c\u775b\u7acb\u523b\u77aa\u5f97\u8001\u5927\uff0c\u8bf4\uff1a\u201c\u6211\u53f2\u8001\u4e09\u603b\u7b97\u773c\u775b\u6ca1\u778e\uff0c\u770b\u51fa\u9601\u4e0b\u662f\u4e2a\u9ad8\u4eba\uff0c\u53ef\u662f\u4e0d \u7792\u4f60\u5bb6\u8bf4\uff0c\u6211\u53ef\u6ca1\u6709\u770b\u51fa\u9601\u4e0b\u7adf\u662f\u8fd1\u6708\u6765\u6b66\u6797\u8f70\u4f20\u7684\u6c5f\u6e56\u4e09\u79c0\u4e4b\u4e00\uff0c\u718a\u501c\u718a\u5927\u4fa0\u3002\u201d \u3000\u3000\u718a\u501c\u542c\u4e86\u4e00\u6123\uff0c\u5fc3\u60f3\u81ea\u5df1\u600e\u4e48\u6210\u4e86\u6c5f\u6e56\u4e09\u79c0\uff0c\u5fd9\u8bf4\uff1a\u201c\u5c0f\u5f1f\u600e\u4e48\u540d\u5217\u6c5f\u6e56\u4e09\u79c0\u4e86\uff0c\u8fd9\u4e2a \u8fde\u5c0f\u5f1f\u81ea\u5df1\u90fd\u672a\u542c\u5230\u8fc7\u3002\u201d \u3000\u3000\u53f2\u80d6\u5b50\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u5012\u5947\u602a\u4e86\uff0c\u6c5f\u6e56\u4e2d\u4eba\uff0c\u8c01\u6ca1\u6709\u542c\u5230\u8fc7\uff1a\u2018\u6b66\u6797\u7fa4\u8c6a\uff0c\u4ee3\u6709\u65b0\u4eba\uff0c\u6c5f \u6e56\u4e09\u79c0\uff0c\u79c0\u51fa\u7fa4\u4f26\u3002\u6b66\u6797\u5f97\u5f02\u624d\uff0c\u5404\u4ff1\u6709\u5947\uff0c\u4e00\u5f02\u5e76\u53cc\u7edd\uff0c\u4e09\u79c0\u52a0\u56db\u4eea\u2019\uff0c\u9601\u4e0b\u5728\u6b66\u6797\u4e2d\uff0c \u5df2\u662f\u5927\u5927\u6709\u540d\u7684\u4eba\u7269\uff0c\u5c0f\u5f1f\u5982\u4f55\u4e0d\u77e5\u9053\uff1f\u201c\u590f\u82b8\u4fa7\u8138\u72e0\u72e0\u5730\u77aa\u4e86\u718a\u501c\u4e00\u773c\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u4e9b\u90fd \u662f\u4e9b\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u53f2\u80d6\u5b50\u5982\u6570\u5bb6\u73cd\u4ed6\u8bf4\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u90fd\u662f\u8fd1\u5e74\u6765\u5728\u6b66\u6797\u4e2d\u8d6b\u8d6b\u4e00\u65f6\u7684\u4eba\u7269\u2018\u4e00\u5f02\u2019\u5c31\u662f\u5929 \u9634\u6559\u4e3b\u7126\u5f02\u884c\u592b\u5987\uff0c\u2018\u53cc\u7edd\u2019\u662f\u5ce8\u5d4b\u7684\u5b64\u5cf0\u4e00\u5251\u8fb9\u6d69\u548c\u6c5f\u82cf\u864e\u90b1\u7684\u51fa\u5c18\u5251\u5ba2\uff0c\u98de\u7075\u5821\u4e3b\u4e1c\u65b9 \u7075\u2026\u2026\u201d \u3000\u3000\u590f\u82b8\u51b7\u7b11\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4e2a\u4ec0\u4e48\u5b64\u5cf0\u4e00\u5251\u6211\u5012\u9886\u6559\u8fc7\uff0c\u4e5f\u672a\u89c1\u5f97\u5982\u4f55\u51fa\u8272\u3002\u201d \u3000\u3000\u53f2\u80d6\u5b50\u6115\u4e86\u4e00\u4e0b\uff0c\u63a5\u7740\u9053\uff1a\u201c\u4e09\u79c0\u5c31\u662f\u4e24\u6cb3\u7eff\u6797\u9053\u7684\u603b\u74e2\u628a\u5b50\u94c1\u80c6\u5c1a\u672a\u660e\uff0c\u5929\u9634\u6559\u4e0b\u7684 \u62a4\u6cd5\u9ed1\u8863\u6469\u52d2\u767d\u8863\u9f99\u5973\uff0c\u548c\u8fd9\u4f4d\u718a\u5927\u4fa0\uff0c\u56db\u4eea\u5c31\u662f\u6211\u4eec\u6b66\u5f53\u5c71\u7684\u56db\u4eea\u5251\u5ba2\u4e86\u3002\u201d \u3000\u3000\u53f2\u80d6\u5b50\u8bf4\u5f97\u53e3\u6cab\u6a2a\u98de\uff0c\u6709\u58f0\u6709\u8272\uff0c\u53c8\u8bf4\u9053\uff1a\u201c\u8fd9\u51e0\u4f4d\u4e0d\u4f46\u6b66\u529f\u9ad8\u5f3a\uff0c\u800c\u4e14\u5e74\u7eaa\u4e5f\u8f7b\uff0c\u90fd \u662f\u4e9b\u4e86\u4e0d\u8d77\u7684\u4eba\u7269\u3002\u201d \u3000\u3000\u590f\u82b8\u5374\u51b7\u7b11\u4ed6\u8bf4\uff1a\u201c\u6211\u770b\u4e0d\u89c1\u5f97\uff0c\u636e\u6211\u6240\u77e5\uff0c\u5c31\u6709\u8bb8\u591a\u4eba\u6bd4\u4ed6\u4eec\u5f3a\u5f97\u591a\u3002\u201d \u3000\u3000\u718a\u501c\u9053\uff1a\u201c\u6bd4\u5982\u8bf4\u8fd1\u5e74\u767d\u5c71\u9ed1\u6c34\u95f4\uff0c\u51fa\u4e86\u4e2a\u5973\u4fa0\uff0c\u96ea\u5730\u98d8\u98ce\u590f\u82b8\uff0c\u6b66\u529f\u5c31\u51fa\u8272\u5f97\u5f88\uff0c\u4e0d \u8bf4\u6bd4\u522b\u4eba\uff0c\u6bd4\u6211\u718a\u501c\u5c31\u5f3a\u5f97\u591a\u3002\u201d \u3000\u3000\u53f2\u80d6\u5b50\u5947\u9053\uff1a\u201c\u771f\u7684\u5417\uff1f\u8fd9\u4e2a\u6211\u5012\u4e0d\u77e5\u9053\uff0c\u4e0d\u8fc7\u6211\u60f3\u8fd9\u4e9b\u90fd\u662f\u4f20\u8bf4\uff0c\u4e0d\u8db3\u4e3a\u4fe1\u7684\uff0c\u60f3\u90a3 \u96ea\u5730\u98d8\u98ce\u5373\u4f7f\u6709\u4e9b\u6b66\u529f\uff0c\u5374\u600e\u6bd4\u5f97\u718a\u5927\u4fa0\uff0c\u98de\u7075\u5821\u4e00\u4f1a\uff0c\u6c5f\u6e56\u7fa4\u8c6a\u90fd\u8bf4\u718a\u5927\u4fa0\u6b66\u529f\u76d6\u4e16\uff0c\u9601 \u4e0b\u4e5f\u4e0d\u5fc5\u592a\u8c26\u865a\u4e86\uff0c\u201d\u590f\u82b8\u54fc\u4e86\u4e00\u58f0\uff0c\u4e5f\u4e0d\u7406\u4ed6\u4eec\u4e24\u4eba\uff0c\u4e00\u626d\u5934\uff0c\u8d70\u8fdb\u53bb\u4e86\u3002 \u3000\u3000\u53f2\u80d6\u5b50\u5bdf\u8a00\u89c2\u8272\uff0c\u4e5f\u731c\u7740\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u96be\u9053\u8fd9\u4f4d\u4fbf\u662f\u96ea\u5730\u98d8\u98ce\u5417\u3002\u201d \u3000\u3000\u718a\u501c\u7b11\u7740\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5f1f\u8fd8\u6709\u4e9b\u4e8b\u60c5\uff0c\u5c11\u966a\u4e86\u3002\u201d \u3000\u3000\u53f2\u80d6\u5b50\u671d\u4ed6\u505a\u4e86\u4e2a\u9b3c\u8138\uff0c\u7b11\u8bf4\u9053\uff1a\u201c\u5f53\u7136\uff0c\u5f53\u7136\uff0c\u655d\u9556\u5c40\u5c31\u8bbe\u5728\u6b66\u660c\uff0c\u5c0f\u5f1f\u8fd9\u6b21\u4fdd\u7740\u4e00 \u6279\u76d0\u6b3e\u5230\u6c5f\u5357\uff0c\u65e5\u540e\u6709\u7f18\uff0c\u8fd8\u671b\u80fd\u4e00\u7779\u98ce\u91c7\uff0c\u655d\u5c40\u7684\u738b\u603b\u9556\u5934\uff0c\u5bf9\u9601\u4e0b\u4e5f\u4ef0\u6155\u5f97\u5f88\u3002\u201d \u3000\u3000\u718a\u501c\u4e00\u62f1\u624b\uff0c\u4e5f\u8fde\u5fd9\u8ddf\u590f\u82b8\u8d70\u8fdb\u623f\u53bb\uff0c\u4ed6\u77e5\u9053\u590f\u82b8\u4e00\u5b9a\u751f\u6c14\u4e86\u3002 \u3000\u3000\u679c\u7136\u590f\u82b8\u77e5\u9053\u4ed6\u8fdb\u4e86\u623f\u95f4\uff0c\u6389\u8fc7\u5934\uff0c\u4e5f\u4e0d\u7406\u4ed6\uff0c\u718a\u501c\u4fbf\u62fc\u547d\u5730\u54b3\u55fd\u3002 \u3000\u3000\u590f\u82b8\u5fcd\u4e0d\u4f4f\uff0c\u5657\u5473\u7b11\u4e86\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u54b3\u4ec0\u4e48\u55fd\uff0c\u518d\u54b3\u6211\u4e5f\u4e0d\u7406\u4f60\u3002\u201d \u3000\u3000\u968f\u53c8\u751f\u6c14\u9053\uff1a\u201c\u50cf\u4f60\u8fd9\u6837\u7684\u5927\u82f1\u96c4\uff0c\u7406\u6211\u5e72\u4ec0\u4e48\uff0c\u5582\uff0c\u6211\u8bf4\u718a\u5927\u82f1\u96c4\uff0c\u4f60\u53ef\u771f\u662f\u771f\u4eba\u4e0d \u9732\u76f8\u5440\uff0c\u8981\u4e0d\u662f\u90a3\u80d6\u5b50\u4e00\u606d\u7ef4\uff0c\u5230\u4eca\u5929\u6211\u8fd8\u8499\u5728\u9f13\u91cc\u5462\u3002\u201d\u8bf4\u7740\u5c0f\u5634\u4e00\u561f\uff0c\u53c8\u6389\u8fc7\u5934\u53bb\u3002 \u3000\u3000\u718a\u501c\u8fc7\u53bb\uff0c\u7528\u624b\u629a\u7740\u590f\u82b8\u7684\u80a9\u8180\uff0c\u9053\uff1a\u201c\u4f60\u542c\u90a3\u80d6\u5b50\u7684\u778e\u606d\u7ef4\u5e72\u4ec0\u4e48\uff0c\u5176\u5b9e\u6211\u7684\u6b66\u529f\u6bd4 \u8d77\u4f60\u6765\uff0c\u771f\u5dee\u5f97\u8fdc\u5462\u3002\u201d \u3000\u3000\u590f\u82b8\u80a9\u8180\u6447\u4e86\u4e00\u6447\uff0c\u9053\uff1a\u201c\u4f60\u522b\u9a97\u6211\uff0c\u4e0b\u6b21\u6211\u518d\u4e5f\u4e0d\u53d7\u4f60\u7684\u9a97\u4e86\u3002\u201d \u3000\u3000\u718a\u501c\u7b11\u9053\uff1a\u201c\u6211\u771f\u7684\u4e0d\u9a97\u4f60\uff0c\u4f60\u770b\u8fde\u5b64\u5cf0\u4e00\u5251\u90fd\u6015\u4f60\uff0c\u6211\u66f4\u4e0d\u884c\u5566\uff0c\u4f60\u4e5f\u522b\u751f\u6c14\uff0c\u4f60\u5728 \u6c5f\u5357\u6b66\u6797\u4e2d\u53c8\u6ca1\u9732\u8fc7\u8138\uff0c\u4e5f\u96be\u602a\u53f2\u80d6\u5b50\u4ed6\u4eec\u4e0d\u77e5\u9053\u4f60\uff0c\u8981\u662f\u4ed6\u4eec\u770b\u5230\u4f60\u7684\u6b66\u529f\uff0c\u6211\u62c5\u4fdd\u4ed6\u4eec \u66f4\u8981\u4f69\u670d\u5f97\u4e0d\u5f97\u4e86\u3002\u201d \u3000\u3000\u590f\u82b8\u9ad8\u5174\u5730\u9053\uff1a\u201c\u771f\u7684\u5417\uff1f\u201d \u3000\u3000\u718a\u501c\u7b11\u7740\u9053\uff1a\u201c\u5f53\u7136\u5566\u3002\u201d \u3000\u3000\u590f\u82b8\u53c8\u4e0d\u597d\u610f\u601d\u8d77\u6765\uff0c\u9053\uff1a\u201c\u5176\u5b9e\u6211\u4e5f\u4e0d\u662f\u6c14\u4ed6\u4eec\uff0c\u6211\u53ea\u6068\u4f60\uff0c\u660e\u660e\u6709\u4e00\u8eab\u597d\u6b66\u529f\uff0c\u8fd8 \u9a97\u6211\uff0c\u88c5\u51fa\u4e00\u526f\u4e66\u751f\u6837\u5b50\u3002\u201d \u3000\u3000\u718a\u501c\u7b11\u9053\uff1a\u201c\u6211\u53c8\u6ca1\u6709\u8ddf\u4f60\u8bf4\u8fc7\u6211\u4e0d\u4f1a\u6b66\u529f\uff0c\u662f\u4f60\u81ea\u5df1\u8bf4\u6211\u4e0d\u884c\u7684\u5440\u3002\u201d \u3000\u3000\u590f\u82b8\u60f3\u4e86\u4e00\u60f3\uff0c\u57cb\u5934\u5230\u684c\u5b50\u4e0a\uff0c\u9053\uff1a\u201c\u6211\u56f0\u6b7b\u4e86\uff0c\u53ea\u60f3\u7761\uff0c\u4f60\u56de\u623f\u53bb\u5427\u3002\u201d \u3000\u3000\u718a\u501c\u9053\uff1a\u201c\u4f60\u4e0d\u602a\u6211\u5566\uff1f\u201d \u3000\u3000\u590f\u82b8\u54fc\u4e86\u4e00\u58f0\uff0c\u4f0f\u5728\u684c\u4e0a\uff0c\u4e5f\u4e0d\u518d\u8bf4\u8bdd\u4e86\uff0c\u718a\u501c\u5f53\u5979\u771f\u8981\u7761\u4e86\uff0c\u4e5f\u56de\u5230\u623f\u7761\u4e86\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u65e9\u4e0a\uff0c\u53f2\u80d6\u5b50\u4e00\u65e9\u5c31\u6c14\u547c\u547c\u5730\u8dd1\u5230\u718a\u501c\u7684\u623f\u91cc\u6765\uff0c\u718a\u501c\u89c1\u4ed6\u8fd9\u4e48\u51b7\u7684\u5929\u6c14\uff0c\u989d\u4e0a \u7684\u6c57\u73e0\u5374\u4e00\u9897\u9897\u5f80\u4e0b\u76f4\u6389\u3002 \u3000\u3000\u53f2\u80d6\u5b50\u4e00\u8fdb\u95e8\uff0c\u5c31\u9053\uff1a\u201c\u718a\u5144\u5343\u4e07\u6551\u6211\u4e00\u6551\uff0c\u655d\u9556\u5c40\u7684\u4e5d\u5b98\u8fde\u73af\u65d7\uff0c\u6628\u591c\u7adf\u88ab\u4eba\u62d4\u4e86 \u53bb\uff0c\u8fd9\u4e8b\u5173\u7cfb\u592a\u5927\uff0c\u5c0f\u5f1f\u5b9e\u5728\u62c5\u4e0d\u8d77\u3002\u201d \u3000\u3000\u718a\u501c\u4e5f\u60ca\u9053\uff1a\u201c\u771f\u7684\u5417\uff1f\u201d \u3000\u3000\u53f2\u80d6\u5b50\u9053\uff1a\u201c\u718a\u5144\u522b\u5f00\u73a9\u7b11\u4e86\uff0c\u718a\u5144\u82e5\u4e0d\u77e5\u9053\uff0c\u8fd8\u6709\u8c01\u4eba\u77e5\u9053\u3002\u201d \u3000\u3000\u718a\u501c\u4e00\u542c\uff0c\u6c89\u4e0b\u8138\u6765\u8bf4\u9053\uff1a\u201c\u53f2\u5144\u8fd9\u8bdd\u5374\u600e\u4ed6\u8bb2\u6cd5\uff1f\u201d \u3000\u3000\u53f2\u80d6\u5b50\u4ece\u6000\u91cc\u62ff\u51fa\u5f20\u7eb8\u6761\u6765\uff0c\u718a\u501c\u63a5\u8fc7\u4e00\u770b\uff0c\u53ea\u89c1\u4e0a\u9762\u201c\u8981\u627e\u9556\u65d7\uff0c\u53bb\u95ee\u718a\u501c\u3002\u201d \u3000\u3000\u5b57\u8ff9\u6e05\u79c0\u5f97\u5f88\uff0c\u718a\u501c\u6c89\u541f\u4e86\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u9556\u65d7\u7684\u4e8b\uff0c\u6211\u662f\u5b9e\u5728\u4e0d\u77e5\u9053\uff0c\u4e0d\u8fc7\u6211\u60f3\uff0c \u5927\u7ea6\u662f\u6211\u90a3\u670b\u53cb\u590f\u59d1\u5a18\u4e00\u65f6\u6c14\u6124\u4e4b\u4e0b\uff0c\u624d\u53bb\u62ff\u7684\uff0c\u53f2\u5144\u8bf7\u653e\u5fc3\uff0c\u4f60\u6211\u4e00\u8d77\u53bb\u5979\u623f\u91cc\uff0c\u53f2\u5144\u53ea \u8981\u7a0d\u4e3a\u606d\u7ef4\u5979\u4e24\u53e5\uff0c\u6211\u62c5\u4fdd\u9556\u65d7\u4e00\u5b9a\u62ff\u5f97\u56de\u6765\u3002\u201d \u3000\u3000\u53f2\u80d6\u5b50\u4f38\u624b\u62ed\u53bb\u989d\u4e0a\u7684\u6c57\uff0c\u8fde\u58f0\u8bf4\uff1a\u201c\u8fd9\u53ef\u771f\u5413\u6b7b\u6211\u4e86\u3002\u201d \u3000\u3000\u4e24\u4eba\u8d70\u5230\u590f\u82b8\u623f\u91cc\uff0c\u53ea\u89c1\u590f\u82b8\u6b63\u5bf9\u7740\u955c\u5b50\u5728\u7406\u5934\u53d1\uff0c\u770b\u89c1\u4e24\u4eba\u8fdb\u6765\uff0c\u7406\u4e5f\u4e0d\u7406\uff0c\u718a\u501c\u671d \u53f2\u80d6\u5b50\u505a\u4e86\u773c\u8272\uff0c\u53f2\u80d6\u5b50\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u4ed6\u8d70\u5230\u590f\u82b8\u8eab\u65c1\uff0c\u4e00\u63d6\u5230\u5730\uff0c\u8bf4\uff1a\u201c\u6628\u5929\u53f2\u67d0\u4eba\u8be5\u6b7b\uff0c\u4e0d\u77e5\u9053\u59d1\u5a18\u662f\u4f4d\u9ad8\u4eba\uff0c\u8a00\u6de1\u4e2d\u65e0 \u610f\u5f97\u7f6a\u4e86\uff0c\u8fd8\u8bf7\u59d1\u5a18\u83ab\u602a\u3002\u201d \u3000\u3000\u590f\u82b8\u773c\u89d2\u4e5f\u4e0d\u8198\u4e00\u4e0b\uff0c\u51b7\u7740\u8138\u9053\uff1a\u201c\u5406\uff0c\u53f2\u5927\u9556\u5934\uff0c\u8fd9\u53ef\u4e0d\u6562\u5f53\uff0c\u4e00\u5927\u6e05\u65e9\u8dd1\u5230\u6211\u623f\u91cc \u6765\uff0c\u53c8\u662f\u4f5c\u63d6\uff0c\u53c8\u662f\u8d54\u7f6a\uff0c\u5e72\u4ec0\u4e48\u5440\uff1f\u201d \u3000\u3000\u53f2\u80d6\u5b50\u9053\uff1a\u201c\u4e0d\u77e5\u8005\u4e0d\u7f6a\uff0c\u8bf7\u59d1\u5a18\u9ad8\u62ac\u8d35\u624b\uff0c\u628a\u90a3\u9556\u65d7\u8fd8\u7ed9\u6211\u4eec\uff0c\u4e0d\u4f46\u6211\u53f2\u80d6\u5b50\u611f\u6fc0\u4e0d \u5c3d\uff0c\u5c31\u662f\u8fde\u6211\u4eec\u738b\u603b\u9556\u5934\u4e5f\u4f1a\u4eb2\u6765\u9053\u8c22\u7684\u3002\u201d \u3000\u3000\u590f\u82b8\u6545\u610f\u5662\u4e86\u58f0\uff0c\u82e5\u65e0\u5176\u4e8b\u9053\uff1a\u201c\u539f\u6765\u4f60\u8bf4\u90a3\u65d7\u5b50\u5440\uff0c\u6628\u5929\u6211\u8fd8\u6ca1\u770b\u6e05\u695a\uff0c\u5c31\u88ab\u8d35\u9556\u5c40 \u7684\u4f19\u8ba1\u8bad\u4e86\u4e00\u987f\uff0c\u665a\u4e0a\u6211\u5c31\u5230\u4f60\u90a3\u513f\u53bb\uff0c\u60f3\u501f\u6765\u770b\u770b\uff0c\u54ea\u77e5\u9053\u4f60\u4eec\u5168\u7761\u5f97\u719f\u5f97\u5f88\uff0c\u6211\u53ea\u597d\u81ea \u5df1\u62ff\u56de\u6765\u4e86\uff0c\u770b\u4e86\u534a\u5929\uff0c\u5b9e\u5728\u559c\u6b22\u5f97\u5f88\uff0c\u771f\u4e0d\u60f3\u8fd8\u7ed9\u4f60\u4eec\uff0c\u4e0d\u8fc7\u53f2\u5927\u9556\u5934\u65e2\u7136\u4eb2\u81ea\u6765\u4e86\uff0c\u6211 \u4e5f\u4e0d\u5f97\u4e0d\u5356\u8fd9\u4e2a\u9762\u5b50\u3002\u201d \u3000\u3000\u5979\u987f\u4e86\u4e00\u987f\uff0c\u53f2\u80d6\u5b50\u8fde\u5fd9\u9053\uff1a\u201c\u90a3\u771f\u592a\u597d\u4e86\uff0c\u6211\u5148\u8c22\u8c22\u59d1\u5a18\u3002\u201d \u3000\u3000\u590f\u82b8\u8138\u4e00\u677f\uff0c\u8bf4\u9053\uff1a\u201c\u53ea\u662f\u6211\u65e2\u7136\u62ff\u4e86\u6765\uff0c\u603b\u4e0d\u80fd\u5c31\u8fd9\u6837\u7684\u8ba9\u4f60\u62ff\u8d70\u5440\uff0c\u522b\u4eba\u4e0d\u77e5\u9053\uff0c \u8fd8\u5f53\u6211\u6015\u4f60\u4eec\u5462\u3002\u201d \u3000\u3000\u53f2\u80d6\u5b50\u4e00\u542c\uff0c\u6025\u5f97\u521a\u64e6\u4e8e\u7684\u6c57\uff0c\u53c8\u5f80\u4e0b\u76f4\u6389\u4e86\uff0c\u56de\u5934\u6c42\u52a9\u5730\u671b\u7740\u718a\u501c\u3002 \u3000\u3000\u718a\u501c\u4e5f\u8d70\u8fc7\u6765\u8bf4\u9053\uff1a\u201c\u4eba\u5bb6\u65e2\u7136\u5df2\u7ecf\u6765\u8d54\u8bdd\u4e86\uff0c\u4f60\u5c31\u8fd8\u7ed9\u4eba\u5bb6\u5427\u3002\u201d \u3000\u3000\u590f\u82b8\u8fde\u7406\u90fd\u4e0d\u7406\u4ed6\uff0c\u5140\u81ea\u51b7\u7b11\u7740\u8bf4\u9053\uff1a\u201c\u8981\u6211\u628a\u9556\u65d7\u8fd8\u7ed9\u4f60\u4eec\u4e5f\u4e0d\u96be\uff0c\u53ea\u8981\u4f60\u4eec\u9556\u5c40\u91cc \u7684\u603b\u9556\u5934\u4eb2\u81ea\u524d\u6765\uff0c\u6211\u8981\u548c\u4ed6\u6bd4\u5212\u6bd4\u5212\uff0c\u770b\u770b\u8fd9\u56db\u4eea\u5251\u5ba2\u7684\u5e08\u5f1f\uff0c\u7a76\u7adf\u6709\u4ec0\u4e48\u672c\u4e8b\uff0c\u6211\u7740\u662f \u8d25\u4e86\uff0c\u81ea\u7136\u5c06\u9556\u65d7\u53cc\u624b\u5949\u8fd8\uff0c\u6211\u82e5\u662f\u4fa5\u5e78\u80dc\u4e86\uff0c\u4e5f\u5c06\u9556\u65d7\u8fd8\u7ed9\u4f60\u4eec\uff0c\u4e0d\u8fc7\u501f\u4f60\u4eec\u7684\u53e3\u4f20\u8a00\u6c5f \u6e56\uff0c\u6b66\u6797\u4e2d\u8fd8\u6709\u6211\u8fd9\u4e48\u4e00\u53f7\u4eba\u7269\u3002\u201d \u3000\u3000\u5979\u8d8a\u8bf4\uff0c\u718a\u501c\u8d8a\u89c9\u5f97\u4e0d\u50cf\u8bdd\uff0c\u53f2\u80d6\u5b50\u542c\u4e86\uff0c\u4e5f\u6c14\u5f97\u6d51\u8eab\u53d1\u6296\uff0c\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u59d1\u5a18\u8fd9\u4e48 \u8bf4\uff0c\u6211\u53f2\u67d0\u4eba\u53ea\u6709\u5411\u4e0a\u56de\u7980\uff0c\u53ea\u662f\u59d1\u5a18\u4f11\u602a\uff0c\u6211\u53f2\u67d0\u4eba\u8bf4\u53e5\u76f4\u8bdd\uff0c\u50cf\u59d1\u5a18\u8fd9\u6837\uff0c\u5c31\u662f\u6b66\u529f\u518d \u597d\uff0c\u6211\u53f2\u67d0\u4eba\u4e5f\u4e0d\u4f1a\u4f69\u670d\u7684\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u5b8c\u6389\u5934\u5c31\u8d70\uff0c\u590f\u82b8\u4e00\u62cd\u684c\u5b50\uff0c\u7ad9\u4e86\u8d77\u6765\uff0c\u60f3\u8ffd\u51fa\u53bb\uff0c\u718a\u501c\u6a2a\u8eab\u4e00\u62e6\uff0c\u6321\u5728\u5979\u9762\u524d\uff0c \u8bf4\u9053\uff1a\u201c\u4f60\u8981\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u590f\u82b8\u8bf4\uff1a\u201c\u4f60\u522b\u62e6\u7740\u6211\u3002\u201d \u3000\u3000\u718a\u501c\u8bf4\uff1a\u201c\u4f60\u4e5f\u662f\u7684\uff0c\u4eba\u5bb6\u2026\u2026\u201d \u3000\u3000\u590f\u82b8\u6ca1\u7b49\u4ed6\u8bf4\u5b8c\uff0c\u5c31\u62a2\u7740\u8bf4\u9053\uff1a\u201c\u597d\u4e86\uff0c\u597d\u4e86\uff0c\u4f60\u522b\u8bf4\u4e86\uff0c\u6709\u4eba\u6b3a\u8d1f\u6211\uff0c\u4f60\u975e\u4f46\u4e0d\u5e2e \u6211\uff0c\u8fd8\u966a\u7740\u4eba\u5bb6\u4e00\u8d77\u6c14\u6211\u4e86\u3002\u201d \u3000\u3000\u8bf4\u7740\u8bf4\u7740\uff0c\u5979\u773c\u5708\u90fd\u7ea2\u4e86\u3002 \u3000\u3000\u718a\u501c\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u771f\u662f\u5c0f\u5b69\u5b50\u813e\u6c14\uff0c\u5176\u5b9e\u4eba\u5bb6\u4e5f\u6ca1\u6709\u600e\u6837\u5f97\u7f6a\u6211\u4eec\uff0c\u4f60\u53c8\u4f55\u5fc5\u8fd9 \u6837\u3002\u201d \u3000\u3000\u590f\u82b8\u6c14\u9053\uff1a\u201c\u6211\u7684\u4e8b\u4e0d\u7528\u4f60\u7ba1\uff0c\u4f60\u88ab\u4eba\u5bb6\u606d\u7ef4\u4e86\u51e0\u53e5\uff0c\u5c31\u5e2e\u4ed6\u4eec\u6765\u6b3a\u8d1f\u6211\u3002\u201d \u3000\u3000\u718a\u501c\u4e5f\u6c14\u9053\uff1a\u201c\u4e0d\u7ba1\u5c31\u4e0d\u7ba1\uff0c\u50cf\u4f60\u8fd9\u6837\u7684\u813e\u6c14\uff0c\u65e9\u665a\u603b\u8981\u5403\u6b21\u5927\u4e8f\u3002\u201d \u3000\u3000\u590f\u82b8\u6c14\u5f97\u6d41\u4e0b\u6cea\u6765\uff0c\u59d4\u5c48\u5730\u9053\uff1a\u201c\u6211\u5403\u4e8f\u4e5f\u4e0d\u5173\u4f60\u7684\u4e8b\uff0c\u4f60\u662f\u5927\u82f1\u96c4\uff0c\u6211\u53ea\u662f\u4e0d\u8bb2\u7406\u7684 \u5c0f\u59d1\u5a18\uff0c\u4f60\u522b\u7406\u6211\u3002\u201d \u3000\u3000\u718a\u501c\u9053\uff1a\u201c\u4f60\u672c\u6765\u5c31\u662f\u4e0d\u8bb2\u7406\u7684\u59d1\u5a18\uff0c\u53ef\u662f\u6211\u8fd8\u6ca1\u6709\u60f3\u5230\u4f60\u8fd9\u6837\u4e0d\u8bb2\u7406\u3002\u201d \u3000\u3000\u590f\u82b8\u6d41\u7740\u6cea\u9053\uff1a\u201c\u4f60\u8d70\uff0c\u4f60\u8d70\uff0c\u6211\u6c38\u8fdc\u4e0d\u8981\u542c\u4f60\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u4e24\u4eba\u8d8a\u8bf4\u8d8a\u50f5\uff0c\u718a\u501c\u6b63\u5728\u6c14\u5934\u4e0a\uff0c\u542c\u5979\u5982\u6b64\u8bf4\uff0c\u6012\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u4ee5\u540e\u4f60\u8d70\u4f60\u7684\uff0c\u6211\u8d70 \u6211\u7684\uff0c\u6211\u4eec\u8c01\u4e5f\u4e0d\u8981\u7ba1\u8c01\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u6389\u5934\u5c31\u8d70\u4e86\u3002 \u3000\u3000\u590f\u82b8\u89c1\u4ed6\u771f\u7684\u4e00\u6012\u800c\u53bb\uff0c\u54c7\u5730\u4e00\u58f0\uff0c\u54ed\u4e86\u51fa\u6765\u3002\u5979\u4ece\u6765\u90fd\u662f\u88ab\u4eba\u767e\u4f9d\u767e\u987a\uff0c\u53d7\u4e86\u8fd9\u4e2a \u6c14\uff0c\u8d8a\u60f3\u8d8a\u96be\u53d7\uff0c\u8d8a\u96be\u53d7\u8d8a\u54ed\u3002 \u3000\u3000\u5bf9\u718a\u501c\u5979\u66f4\u6068\u5f97\u5389\u5bb3\uff0c\u4f46\u53c8\u6709\u4e00\u822c\u8bf4\u4e0d\u51fa\u7684\u60c5\u611f\uff0c\u5e0c\u671b\u4ed6\u8fd8\u662f\u8dd1\u56de\u6765\uff0c\u5411\u81ea\u5df1\u9053\u6b49\u3002 \u3000\u3000\u718a\u501c\u82e5\u771f\u7684\u56de\u6765\uff0c\u8981\u5979\u5c06\u9556\u65d7\u8fd8\u7ed9\u6b66\u5a01\u9556\u5c40\uff0c\u5979\u7acb\u523b\u4e5f\u4f1a\u8fd8\u7684\u3002 \u3000\u3000\u4f46\u718a\u501c\u4e5f\u662f\u4e2a\u5014\u5f3a\u7684\u813e\u6c14\uff0c\u4ed6\u8dd1\u51fa\u590f\u82b8\u7684\u623f\u91cc\uff0c\u672c\u60f3\u4e00\u8d70\u4e86\u4e4b\uff0c\u4f46\u4ed6\u5230\u5e95\u662f\u4e2a\u60c5\u79cd\uff0c\u5bf9 \u590f\u82b8\u4ecd\u7136\u653e\u5fc3\u4e0d\u4e0b\uff0c\u53c8\u6015\u90a3\u4e5d\u5b98\u8fde\u73af\u5251\u738b\u9521\u4e5d\u6765\u65f6\uff0c\u590f\u82b8\u62b5\u6321\u4e0d\u4f4f\uff0c\u4e00\u5b9a\u8981\u5403\u5927\u4e8f\u3002 \u3000\u3000\u4ed6\u95f7\u5750\u5728\u623f\u91cc\uff0c\u60f3\u4e86\u8bb8\u4e45\uff0c\u5ffd\u5730\u623f\u95e8\u4e00\u52a8\uff0c\u4ed6\u8fd8\u4ee5\u4e3a\u662f\u590f\u82b8\u6765\u4e86\uff0c\u559c\u5f97\u8d76\u5feb\u8dd1\u53bb\u5f00\u95e8\uff0c \u54ea\u77e5\u8fdb\u6765\u7684\u5374\u662f\u53f2\u80d6\u5b50\u3002 \u3000\u3000\u53f2\u80d6\u5b50\u671d\u718a\u501c\u8bf4\u9053\uff1a\u201c\u4ee4\u53cb\u590f\u59d1\u5a18\u8fd9\u6837\u505a\uff0c\u5b9e\u5728\u592a\u4efb\u6027\u4e86\uff0c\u5979\u4e0d\u77e5\u9053\u9556\u65d7\u88ab\u62d4\uff0c\u4e43\u662f\u9556 \u5c40\u7684\u5947\u803b\u5927\u8fb1\uff0c\u5c24\u5176\u662f\u8fd9\u4e5d\u5b98\u8fde\u73af\u65d7\u4e43\u662f\u5f53\u5e74\u6b66\u5f53\u638c\u6559\u7384\u5316\u771f\u4eba\u672a\u51fa\u5bb6\u524d\u7684\u51ed\u4fe1\uff0c\u6b64\u540e\u4ee3\u4ee3 \u76f8\u4f20\uff0c\u6b66\u6797\u4e2d\u90fd\u606d\u656c\u5f97\u5f88\uff0c\u6b64\u756a\u751f\u51fa\u8fd9\u6837\u7684\u4e8b\u6765\uff0c\u540e\u679c\u5b9e\u5728\u4e25\u91cd\u5f97\u5f88\uff0c\u5c0f\u5f1f\u4e5f\u65e0\u6cd5\u5904\u7406\uff0c\u53ea \u5f97\u9063\u4eba\u98de\u9a6c\u56de\u62a5\u655d\u9556\u884c\u7684\u738b\u603b\u9556\u5934\u53bb\u4e86\uff0c\u5c0f\u5f1f\u53ea\u5e0c\u671b\u718a\u5144\u80fd\u591f\u4e0d\u8981\u63d2\u8db3\u6b64\u4e8b\uff0c\u4e0d\u7136\u65e5\u540e\u718a\u5144 \u89c1\u4e86\u6b66\u5f53\u56db\u5b50\uff0c\u4e5f\u5b9a\u5fc5\u4e0d\u597d\u76f8\u89c1\u3002\u201d \u3000\u3000\u718a\u501c\u6c89\u541f\u4e86\u534a\u664c\uff0c\u53f9\u6c14\u9053\uff1a\u201c\u5979\u65e2\u7136\u8fd9\u6837\uff0c\u6211\u4e5f\u7ba1\u4e0d\u5f97\u4e86\uff0c\u53ea\u662f\u5979\u5b9e\u662f\u5c0f\u5b69\u813e\u6c14\uff0c\u8fd8\u671b \u53f2\u5144\u80fd\u770b\u5728\u5c0f\u5f1f\u8584\u9762\uff0c\u8f6c\u544a\u738b\u603b\u9556\u5934\uff0c\u51e1\u4e8b\u90fd\u9ad8\u62ac\u8d35\u624b\uff0c\u4e0d\u8981\u592a\u7ed9\u5979\u96be\u770b\u3002\u201d \u3000\u3000\u53f2\u80d6\u5b50\u8bf4\uff1a\u201c\u8fd9\u5f53\u7136\uff0c\u738b\u603b\u9556\u5934\u5927\u7ea6\u65e5\u5185\u5c31\u80fd\u8d76\u5230\u4e86\uff0c\u4ed6\u5bf9\u718a\u5144\u4e5f\u662f\u4ef0\u6155\u5f97\u5f88\uff0c\u4f60\u4eec\u4e24 \u4f4d\u90fd\u662f\u4e86\u4e0d\u8d77\u7684\u4eba\u7269\uff0c\u6211\u5012\u5e0c\u671b\u65e5\u540e\u80fd\u591a\u4eb2\u8fd1\u3001\u4eb2\u8fd1\uff0c\u53ea\u8981\u590f\u59d1\u5a18\u4e0d\u8ba4\u771f\uff0c\u6211\u60f3\u51e1\u4e8b\u90fd\u597d\u5546 \u91cf\u3002\u201d \u3000\u3000\u4ed6\u505c\u4e86\u505c\u53c8\u76b1\u7709\u9053\uff1a\u201c\u4e0d\u8fc7\u4e07\u4e00\u8fd9\u4e8b\u88ab\u6b66\u5f53\u5c71\u7684\u4eba\u77e5\u9053\u4e86\uff0c\u90a3\u4e9b\u9053\u7237\u867d\u662f\u51fa\u5bb6\u4eba\uff0c\u4f46\u4e00 \u4e2a\u4e2a\u6027\u5982\u70c8\u706b\uff0c\u5bf9\u90a3\u9556\u65d7\u66f4\u662f\u606d\u656c\u5f97\u5f88\uff0c\u82e5\u662f\u77e5\u9053\u9556\u65d7\u88ab\u76d7\u4e86\uff0c\u4e00\u5b9a\u4e0d\u80af\u5584\u7f62\u7518\u4f11\u7684\u3002\u201d \u3000\u3000\u718a\u501c\u9053\uff1a\u201c\u8fd9\u4e2a\u53ea\u6709\u5230\u65f6\u5019\u518d\u8bf4\u4e86\u3002\u201d \u3000\u3000\u8fd9\u5929\u665a\u4e0a\uff0c\u6b66\u5a01\u9556\u5c40\u7684\u603b\u9556\u5934\u8fd8\u6ca1\u6709\u8d76\u6765\uff0c\u718a\u501c\u5728\u623f\u91cc\u8f6c\u6765\u8f6c\u53bb\uff0c\u51e0\u6b21\u60f3\u8dd1\u5230\u590f\u82b8\u7684\u623f \u91cc\u53bb\uff0c\u529d\u5979\u62ff\u51fa\u9556\u65d7\uff0c\u7136\u540e\u4e24\u4eba\u8a00\u5f52\u4e8e\u597d\uff0c\u4f46\u51e0\u6b21\u90fd\u88ab\u4ed6\u7537\u5b50\u7684\u81ea\u5c0a\u6240\u963b\u6b62\u4e86\uff0c\u867d\u7136\u60f3\u505a\uff0c \u5374\u6ca1\u6709\u505a\u3002 \u3000\u3000\u590f\u82b8\u4e5f\u662f\u5982\u6b64\uff0c\u4ed6\u4eec\u4e24\u4eba\u5728\u623f\u4e2d\u5404\u6709\u5fc3\u4e8b\uff0c\u5fc3\u91cc\u90fd\u88ab\u5bf9\u65b9\u7684\u5f71\u5b50\u6574\u4e2a\u76d8\u8e1e\u4e86\uff0c\u5bf9\u5373\u5c06\u53d1 \u751f\u7684\u540e\u679c\uff0c\u53cd\u800c\u4e0d\u53bb\u60f3\u4e86\u3002 \u3000\u3000\u65f6\u7ea6\u4e09\u66f4\uff0c\u5ba2\u6808\u91cc\u7684\u4eba\u90fd\u7761\u4e86\uff0c\u5ba2\u6808\u5916\u5ffd\u6709\u516b\u9a91\u6025\u9a70\u800c\u6765\uff0c\u6bcf\u5339\u9a6c\u90fd\u8dd1\u5f97\u53e3\u89d2\u767d\u6cab\u6a2a \u98de\uff0c\u60f3\u662f\u9a6c\u4e3b\u56fa\u6709\u6025\u4e8b\u8d76\u8def\uff0c\u4e5f\u987e\u4e0d\u5f97\u7272\u53e3\u4e86\u3002 \u3000\u3000\u9a6c\u5230\u5ba2\u6808\u4fbf\u500f\u5730\u505c\u4f4f\uff0c\u5176\u4e2d\u4e00\u4eba\u8bf4\u9053\uff1a\u201c\u4fbf\u662f\u8fd9\u5bb6\u4e86\u3002\u201d \u3000\u3000\u53e6\u4e00\u4eba\u8bf4\u9053\uff1a\u201c\u5ba2\u6808\u91cc\u706f\u706b\u4ff1\u65e0\uff0c\u60f3\u5fc5\u90fd\u7761\u4e86\uff0c\u8001\u8d75\uff0c\u4f60\u53bb\u6572\u95e8\u5427\u3002\u201d \u3000\u3000\u53c8\u6709\u4e00\u4e2a\u5973\u5b50\u8bf4\u9053\uff1a\u201c\u8fd8\u6572\u4ec0\u4e48\u95e8\uff0c\u5927\u5bb6\u4e00\u8d77\u8d8a\u5899\u800c\u5165\u597d\u4e86\u3002\u201d \u3000\u3000\u90a3\u4eba\u4fbf\u9053\uff1a\u201c\u8fd9\u6837\u4e5f\u597d\uff0c\u53cd\u6b63\u5c0f\u5f1f\u73b0\u5728\u5fc3\u6025\u5f97\u5f88\uff0c\u4e5f\u987e\u4e0d\u5f97\u8fd9\u4e9b\uff0c\u8001\u8d75\uff0c\u4f60\u5728\u8fd9\u91cc\u770b\u5b88 \u7740\u7272\u53e3\uff0c\u6211\u4eec\u8d70\u5427\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u8bdd\uff0c\u4e03\u4eba\u51e0\u4e4e\u662f\u540c\u4e00\u52a8\u4f5c\uff0c\u5168\u662f\u6781\u7094\u7684\u8eab\u624b\uff0c\u98d5\u5730\u4e00\u58f0\uff0c\u4ece\u9a6c\u5339\u4e0a\u5c31\u98de\u8eab\u800c\u51fa\u3002\u4e03 \u4eba\u5728\u5c4b\u9876\u4e0a\uff0c\u4ee5\u6781\u5feb\u7684\u8eab\u6cd5\u76d8\u65cb\u4e86\u4e00\u5468\uff0c\u627e\u5230\u9556\u8f66\u505c\u653e\u7684\u9662\u5b50\uff0c\u98d8\u7136\u800c\u4e0b\uff0c\u5168\u7136\u6ca1\u6709\u4e00\u4e1d\u58f0 \u97f3\uff0c\u663e\u89c1\u8fd9\u4e03\u4eba\u4ff1\u662f\u6b66\u6797\u9ad8\u624b\u3002 \u3000\u3000\u5176\u4e2d\u4e00\u4eba\u4f38\u624b\u6572\u4e86\u6572\u623f\u95e8\uff0c\u5e78\u597d\u90a3\u53f2\u80d6\u5b50\u56e0\u4e3a\u591c\u751f\u4e86\u4e8b\u6545\uff0c\u6b63\u81ea\u5fc3\u4e2d\u7126\u6025\uff0c\u4e0d\u80fd\u6210\u7720\uff0c \u95fb\u58f0\u6025\u5fd9\u62ab\u8863\u8d77\u5e8a\uff0c\u6253\u5f00\u623f\u95e8\u4e00\u770b\uff0c\u4e0d\u7981\u5927\u559c\u9053\uff1a\u201c\u603b\u9556\u5934\uff0c\u4f60\u5c45\u7136\u6765\u5f97\u90a3\u4e48\u7094\uff01\u201d \u3000\u3000\u90a3\u4eba\u6b63\u662f\u201c\u6b66\u5a01\u9556\u5c40\u201d\u7684\u603b\u9556\u5934\uff0c\u4e5d\u4e2d\u8fde\u73af\u5251\u738b\u9521\u4e5d\uff0c\u95fb\u8a00\u8bf4\u9053\uff1a\u201c\u6211\u542c\u5f97\u9556\u65d7\u88ab\u62d4\uff0c \u5fc3\u91cc\u6025\u5f97\u4e00\u584c\u7cca\u6d82\uff0c\u8fde\u591c\u4fbf\u8d76\u4e86\u672a\u3002\u201d \u3000\u3000\u4ed6\u53c8\u8bf4\u9053\uff1a\u201c\u6211\u7684\u5e08\u5144\u6b66\u5f53\u56db\u5b50\u548c\u4e1c\u65b9\u5821\u4e3b\u5144\u59b9\uff0c\u6070\u597d\u4e5f\u5728\u9556\u5c40\uff0c\u542c\u8bf4\u8fd9\u9556\u65d7\u4e43\u4e00\u5973\u5b50 \u6240\u62d4\uff0c\u800c\u8fd9\u5973\u5b50\u53c8\u662f\u4e0e\u718a\u501c\u540c\u884c\u7684\uff0c\u4e5f\u966a\u7740\u6211\u8fde\u591c\u8d76\u6765\uff0c\u73b0\u5728\u5e9f\u8bdd\u5c11\u8bf4\uff0c\u4f60\u8d76\u5feb\u53bb\u5c06\u90a3\u5973\u5b50 \u5524\u6765\uff0c\u6211\u5012\u8981\u770b\u770b\u5979\u662f\u4ec0\u4e48\u4eba\u7269\uff0c\u7adf\u6562\u62d4\u6211\u4eec\u6b66\u5f53\u5c71\u7684\u9556\u65d7\u3002\u201d \u3000\u3000\u53f2\u80d6\u5b50\u4e00\u542c\u5c45\u7136\u60ca\u52a8\u4e86\u8fd9\u8bb8\u591a\u6b66\u6797\u4e2d\u9876\u5c16\u513f\u7684\u4eba\u7269\uff0c\u5fc3\u4e2d\u4e5f\u81ea\u6253\u9f13\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u5404\u4f4d \u5148\u8bf7\u623f\u91cc\u5750\uff0c\u6211\u9a6c\u4e0a\u53bb\u53eb\u5979\u6765\u3002\u201d \u3000\u3000\u539f\u6765\u81ea\u4ece\u718a\u501c\u6e9c\u51fa\u98de\u7075\u5821\u540e\uff0c\u4e1c\u65b9\u745b\u7adf\u54ed\u4e86\u597d\u51e0\u5929\uff0c\u5ce8\u5d4b\u53cc\u5c0f\u53c8\u5728\u65c1\u8fb9\u6572\u8fb9\u9f13\uff0c\u8bf4\u718a\u501c \u5b9e\u5728\u5982\u4f55\u5982\u4f55\u4e0d\u5e94\u8be5\uff0c\u4e00\u5b9a\u8981\u627e\u4ed6\u56de\u6765\u95ee\u4e2a\u660e\u767d\u3002 \u3000\u3000\u4e1c\u65b9\u745b\u4fbf\u78e8\u7740\u4e1c\u65b9\u7075\uff0c\u5979\u4e00\u5b9a\u8981\u5979\u54e5\u966a\u7740\u5979\u53bb\u5bfb\u627e\u718a\u501c\uff0c\u4e1c\u65b9\u7075\u5fc3\u91cc\u4e5f\u60f3\u627e\u56de\u718a\u501c\uff0c\u5c06 \u81ea\u5df1\u5bf9\u6731\u82e5\u5170\u7684\u5fc3\u610f\u8bf4\u660e\uff0c\u8fd9\u51e0\u65e5\u6765\uff0c\u82e5\u5170\u548c\u4e1c\u65b9\u7075\u5df2\u60c5\u612b\u6697\u901a\uff0c\u53ea\u4e0d\u8fc7\u5927\u5bb6\u7f9e\u4e8e\u542f\u53e3\u800c \u5df2\u3002 \u3000\u3000\u4e8e\u662f\u4e1c\u65b9\u7075\u8fd9\u624d\u5e26\u7740\u4e1c\u65b9\u745b\uff0c\u79bb\u5f00\u98de\u7075\u5821\uff0c\u56db\u5904\u6253\u542c\u718a\u501c\u7684\u4e0b\u843d\uff0c\u4f46\u718a\u501c\u8fd9\u65f6\u6b63\u5728\u6d41\u6d6a \u4e4b\u4e2d\uff0c\u6c5f\u6e56\u4eba\u7269\u5982\u4f55\u77e5\u9053\u3002 \u3000\u3000\u4ed6\u4eec\u5bfb\u627e\u4e86\u8bb8\u4e45\uff0c\u4e5f\u672a\u89c1\u5230\u718a\u501c\u7684\u4e0b\u843d\uff0c\u4e1c\u65b9\u7075\u5ffd\u7136\u60f3\u8d77\uff0c\u6b66\u5f53\u56db\u5b50\u66fe\u575a\u7ea6\u718a\u501c\u5230\u6b66\u5f53 \u5c71\u4e00\u6e38\uff0c\u4e5f\u8bb8\u718a\u501c\u662f\u5230\u6b66\u5f53\u5c71\u53bb\u4e86\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u4eec\u5144\u59b9\u4e8c\u4eba\uff0c\u53c8\u542f\u7a0b\u5165\u9102\uff0c\u4ed6\u4eec\u8d70\u5f97\u81ea\u6bd4\u718a\u501c\u8981\u5feb\uff0c\u5230\u4e86\u6b66\u5f53\u5c71\uff0c\u89c1\u4e86\u56db\u4eea\u5251 \u5ba2\uff0c\u90fd\u8bf4\u4e0d\u77e5\u9053\u718a\u501c\u7684\u4e0b\u843d\uff0c\u4e1c\u65b9\u745b\u4fbf\u7740\u6025\u8d77\u6765\uff0c\u6015\u718a\u501c\u53ef\u80fd\u906d\u4e86\u522b\u4eba\u7684\u6bd2\u624b\uff0c\u8fd8\u662f\u6b66\u5f53\u56db \u5b50\u6781\u529b\u529d\u6170\u5979\u8bf4\u718a\u501c\u4e00\u8eab\u7edd\u6280\uff0c\u53c8\u6709\u8c01\u80fd\u8f7b\u6613\u4f24\u5f97\u4e86\u4ed6\u3002 \u3000\u3000\u4ed6\u4eec\u5728\u6b66\u5f53\u6b47\u4e86\u51e0\u5929\uff0c\u56db\u4eea\u5251\u5ba2\u8bf4\u8981\u53bb\u627e\u738b\u9521\u4e5d\uff0c\u4ed6\u4eec\u4e00\u60f3\u738b\u9521\u4e38\u5750\u9547\u9102\u4e2d\uff0c\u6d88\u606f\u5fc5\u7136 \u7075\u901a\uff0c\u4fbf\u4e5f\u8ddf\u7740\u53bb\u4e86\u3002 \u3000\u3000\u65e0\u5de7\u4e0d\u5de7\uff0c\u4ed6\u4eec\u521a\u5230\u6b66\u5a01\u9556\u5c40\uff0c\u90a3\u53f2\u80d6\u5b50\u9063\u53bb\u9001\u4fe1\u7684\u8d9f\u5b50\u624b\u8001\u8d75\u4fbf\u4e5f\u5230\u4e86\uff0c\u5c06\u5728\u9102\u57ce\u53d1 \u751f\u7684\u4e8b\uff0c\u5982\u4f55\u5982\u4f55\u4e00\u8bf4\uff0c\u6b66\u5f53\u56db\u5b50\u548c\u738b\u9521\u4e5d\u90fd\u5927\u6012\uff0c\u4e1c\u65b9\u5144\u59b9\u4e00\u542c\u6b64\u4e8b\u7adf\u662f\u718a\u501c\u540c\u884c\u4e4b\u4eba\u6240 \u4e3a\uff0c\u800c\u4e14\u8fd8\u662f\u4e2a\u5973\u5b50\uff0c\u4e1c\u65b9\u745b\u6bd4\u8c01\u90fd\u751f\u6c14\uff0c\u8fde\u591c\u4fbf\u8d76\u6765\u4e86\u3002 \u3000\u3000\u8fd9\u91cc\u4e14\u8bf4\u53f2\u80d6\u5b50\uff0c\u4ed6\u5306\u5fd9\u7a7f\u597d\u8863\u670d\uff0c\u8dd1\u53bb\u718a\u501c\u548c\u590f\u82b8\u6240\u4f4f\u7684\u8de8\u9662\uff0c\u53c8\u4e0d\u6572\u590f\u82b8\u7684\u95e8\u9762\u53bb \u6572\u718a\u501c\u7684\uff0c\u521a\u8d70\u5230\u95e8\u53e3\uff0c\u718a\u501c\u5df2\u63a8\u95e8\u800c\u51fa\u3002 \u3000\u3000\u539f\u6765\u718a\u501c\u8fd9\u591c\u4e5f\u6ca1\u7761\uff0c\u738b\u9521\u4e5d\u7b49\u4eba\u5728\u623f\u4e0a\u76d8\u65cb\u4e4b\u9645\uff0c\u867d\u7136\u7edd\u65e0\u811a\u6b65\u58f0\uff0c\u4f46\u718a\u501c\u542c\u89c9\u5f02\u4e8e \u5e38\u4eba\uff0c\u4ed6\u4eec\u8863\u8882\u5e26\u98ce\u4e4b\u58f0\uff0c\u5df2\u88ab\u718a\u501c\u542c\u89c1\uff0c\u800c\u4e14\u8fd8\u542c\u51fa\u4e0d\u6b62\u4e00\u4eba\uff0c\u5e76\u8fd8\u4ff1\u662f\u6b66\u6797\u4e2d\u6781\u4f73\u7684\u8eab \u624b\u3002 \u3000\u3000\u718a\u501c\u5fc3\u60f3\uff1a\u201c\u8fd9\u51e0\u4eba\u7684\u8f7b\u529f\uff0c\u90fd\u5df2\u767b\u5802\u4eba\u5ba4\uff0c\u60f3\u8fd9\u9102\u57ce\u5c0f\u5c0f\u7684\u5730\u65b9\uff0c\u600e\u4f1a\u6709\u8fd9\u8bb8\u591a\u597d \u624b\uff0c\u4e00\u5b9a\u662f\u6b66\u5a01\u9556\u5c40\u7684\u603b\u9556\u5934\u5e26\u4eba\u6765\u4e86\u3002\u201d \u3000\u3000\u4e8e\u662f\u4ed6\u7a7f\u4e0a\u8863\u670d\uff0c\u679c\u7136\uff0c\u53f2\u80d6\u5b50\u6c89\u91cd\u7684\u811a\u6b65\u4fbf\u8d70\u6765\u4e86\uff0c\u718a\u501c\u63a8\u95e8\u800c\u51fa\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u8d35\u9556 \u5c40\u7684\u738b\u603b\u9556\u5934\u5230\u4e86\u5417\uff0c\u600e\u4e48\u8fd8\u6709\u522b\u4eba\u5462\uff1f\u201d \u3000\u3000\u53f2\u80d6\u5b50\u5fc3\u5fd6\u9053\uff1a\u201c\u8fd9\u718a\u501c\u679c\u7136\u5389\u5bb3\uff0c\u7adf\u5df2\u77e5\u9053\u4e86\u3002\u201d\u4fbf\u9053\uff1a\u201c\u9664\u4e86\u738b\u603b\u9556\u5934\u4e4b\u5916\uff0c\u8fd8\u6709 \u6b66\u5f53\u56db\u5b50\u548c\u51fa\u5c18\u5251\u5ba2\u4e1c\u65b9\u5144\u59b9\uff0c\u679c\u7136\u4e0d\u51fa\u6211\u6240\u6599\uff0c\u6b64\u4e8b\u95f9\u5f97\u5927\u4e86\uff0c\u53ea\u6015\u4e0d\u53ef\u6536\u62fe\u5462\uff01\u201d \u3000\u3000\u718a\u501c\u542c\u8bf4\u4e1c\u65b9\u7075\u5144\u59b9\u4e5f\u6765\u4e86\uff0c\u4e5f\u5403\u4e86\u4e00\u60ca\uff0c\u4ed6\u5fc3\u60f3\uff1a\u201c\u8fd9\u5374\u96be\u529e\u4e86\uff0c\u6211\u82e5\u7ba1\u8fd9\u4e8b\u4e5f\u4e0d \u597d\uff0c\u4e0d\u7ba1\uff0c\u53c8\u600e\u653e\u5fc3\u590f\u82b8\uff01\u201d \u3000\u3000\u4ed6\u4eec\u8fd9\u91cc\u7684\u8bf4\u8bdd\u4e4b\u58f0\uff0c\u548c\u53f2\u80d6\u5b50\u6c89\u91cd\u7684\u811a\u6b65\u58f0\uff0c\u5374\u4e5f\u88ab\u623f\u4e2d\u7684\u590f\u82b8\u542c\u5230\uff0c\u5979\u672c\u6765\u8131\u8863\u5c31 \u5bdd\uff0c\u6b64\u65f6\u8d70\u4e86\u51fa\u6765\uff0c\u773c\u89d2\u671d\u718a\u501c\u4e00\u77aa\uff0c\u51b2\u53f2\u80d6\u5b50\u51b7\u51b7\u4ed6\u8bf4\uff1a\u201c\u738b\u603b\u9556\u5934\u6765\u5f97\u5012\u771f\u5feb\u3002\u201d \u3000\u3000\u718a\u501c\u8d70\u4e0a\u4e00\u6b65\uff0c\u521a\u60f3\u8bf4\u8bdd\uff0c\u590f\u82b8\u53c8\u8bf4\u9053\uff1a\u201c\u542c\u8bf4\u8fd8\u6709\u522b\u4eba\u540c\u6765\uff0c\u90a3\u6837\u66f4\u597d\uff0c\u53cd\u6b63\u4e0d\u8bba\u591a \u5c11\u4eba\uff0c\u6211\u603b\u4e00\u4e2a\u63a5\u4f4f\u4fbf\u662f\u4e86\u3002\u201d \u3000\u3000\u53f2\u80d6\u5b50\u8bf4\u9053\uff1a\u201c\u590f\u59d1\u5a18\u771f\u662f\u5feb\u4eba\u5feb\u8bdd\uff0c\u90a3\u4e48\u5c31\u8bf7\u59d1\u5a18\u8ddf\u7740\u6211\u6765\u5427\u3002\u201d \u3000\u3000\u590f\u82b8\u671b\u4e5f\u4e0d\u671b\u718a\u501c\u4e00\u773c\uff0c\u8ddf\u7740\u53f2\u80d6\u5b50\u4fbf\u8d70\uff0c\u5176\u5b9e\u5979\u662f\u591a\u4e48\u5e0c\u671b\u718a\u501c\u80fd\u8ddf\u7740\u5979\uff0c\u4fdd\u62a4\u7740 \u5979\uff0c\u5979\u5012\u4e0d\u662f\u60e7\u6015\uff0c\u53ea\u662f\u6e34\u671b\u7740\u90a3\u4e00\u4efd\u6e29\u6696\u7684\u529b\u91cf\u7f62\u4e86\u3002 \u3000\u3000\u4f46\u5979\u56de\u5934\u4e00\u671b\uff0c\u718a\u501c\u5e76\u6ca1\u6709\u8ddf\u6765\uff0c\u5979\u5f3a\u5fcd\u4f4f\u773c\u6cea\uff0c\u60f3\u9053\uff1a\u201c\u597d\uff0c\u8fd9\u6837\u4e5f\u597d\uff0c\u4ed6\u4e0d\u6765\u5c31\u7b97 \u4e86\uff0c\u4ee5\u540e\u6211\u6c38\u8fdc\u4e0d\u8981\u89c1\u4ed6\u4e86\u3002\u201d \u3000\u3000\u8d70\u5230\u9662\u4e2d\uff0c\u738b\u9521\u4e5d\u53ca\u4e1c\u65b9\u5144\u59b9\uff0c\u6b66\u5f53\u56db\u5b50\u5df2\u7ad9\u5728\u9662\u4e2d\uff0c\u738b\u9521\u4e5d\u4e00\u89c1\u53f2\u80d6\u5b50\u5e26\u7740\u4e00\u4e2a\u5973\u5b50 \u540c\u6765\uff0c\u5c31\u77e5\u662f\u6b63\u4e3b\u513f\u5230\u4e86\uff0c\u8d8a\u524d\u51e0\u6b65\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u4fbf\u662f\u6b66\u5a01\u9556\u5c40\u7684\u738b\u9521\u4e5d\uff0c\u59d1\u5a18\u60f3\u5fc5 \u662f\u96ea\u5730\u98d8\u98ce\u4e86\uff0c\u53ea\u662f\u655d\u9556\u5c40\u548c\u59d1\u5a18\u4e95\u6c34\u4e0d\u72af\u6cb3\u6c34\uff0c\u4e0d\u77e5\u59d1\u5a18\u4e3a\u4f55\u62d4\u4e86\u655d\u9556\u5c40\u7684\u9556\u65d7\uff0c\u8fd8\u8bf7\u59d1 \u5a18\u6307\u6559\u3002\u201d\u2018\u590f\u82b8\u4e00\u770b\u7adf\u6709\u90a3\u4e48\u591a\u4eba\u7ad9\u5728\u9662\u4e2d\uff0c\u5fc3\u91cc\u4e00\u6a2a\uff0c\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u4e5f\u4e0d\u4e3a\uff0c\u6211\u5c31\u662f\u770b \u4e0d\u987a\u773c\uff0c\u60f3\u9886\u6559\u9886\u6559\u4f60\u7684\u6b66\u5f53\u5251\u6cd5\u3002\u201d \u3000\u3000\u9662\u4e2d\u8bf8\u4eba\uff0c\u95fb\u8a00\u4ff1\u90fd\u5927\u6012\uff0c\u5fc3\u60f3\u8fd9\u59d1\u5a18\u600e\u5730\u4e0d\u8bb2\u7406\uff0c\u6b66\u5f53\u56db\u5b50\u91cc\uff0c\u51cc\u4e91\u5b50\u5e74\u7eaa\u6700\u8f7b\uff0c\u624d \u4e09\u5341\u51fa\u5934\uff0c\u813e\u6c14\u4e5f\u6700\u8e81\uff0c\u8f7b\u98d8\u98d8\u4e00\u95ea\u8eab\uff0c\u5df2\u63a0\u5728\u738b\u9521\u4e5d\u524d\u9762\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u60f3\u89c1\u8bc6\u89c1 \u8bc6\u6211\u4eec\u7684\u6b66\u5f53\u5251\u6cd5\uff0c\u90a3\u5bb9\u6613\u5f97\u5f88\uff0c\u53ea\u7ba1\u52a8\u624b\u4fbf\u662f\u4e86\u3002\u201d \u3000\u3000\u590f\u82b8\u51b7\u51b7\u5bf9\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff0c\u59d1\u5a18\u6211\u7684\u53ef\u4e0d\u662f\u4f60\uff0c\u4f60\u8981\u52a8\u624b\uff0c\u4e5f\u5bb9\u6613\u5f97\u5f88\uff0c\u4e0d\u8fc7\u8981 \u7b49\u6211\u5148\u9886\u6559\u4e86\u59d3\u738b\u7684\u9ad8\u62db\uff0c\u518d\u6765\u6536\u62fe\u4f60\u3002\u201d \u3000\u3000\u5979\u8bdd\u8d8a\u8bb2\u8d8a\u4e0d\u5ba2\u6c14\uff0c\u8fde\u4e00\u5411\u813e\u6c14\u6700\u597d\u7684\u4e1c\u65b9\u7075\uff0c\u95fb\u8a00\u4e5f\u4f5c\u8272\uff0c\u4e1c\u65b9\u745b\u89c1\u5979\u5e74\u8f7b\u8c8c\u7f8e\uff0c\u6708 \u5149\u4e0b\u770b\u7740\uff0c\u8863\u8882\u98d8\u98d8\uff0c\u7adf\u5982\u5e7f\u5bd2\u4ed9\u5b50\uff0c\u4f3c\u60f3\uff1a\u201c\u602a\u4e0d\u5f97\u718a\u501c\u8ddf\u5979\u5728\u4e00\u5757\u513f\uff0c\u539f\u6765\u5979\u8fd9\u4e48 \u7f8e\u3002\u201d \u3000\u3000\u4e1c\u65b9\u745b\u5fc3\u91cc\u53c8\u5992\u53c8\u6012\uff0c\u4e00\u4e2a\u7bad\u6b65\uff0c\u7a9c\u5230\u524d\u9762\u53bb\uff0c\u590f\u82b8\u53c8\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u8fd8\u6709\u591a\u5c11\u4eba\uff0c\u5e72 \u8106\u4e00\u9f50\u4e0a\u6765\u5427\uff0c\u7701\u5f97\u4e00\u4e2a\u4e00\u4e2a\u5730\u8d39\u4e8b\u3002\u201d \u3000\u3000\u738b\u9521\u4e5d\u6012\u559d\u9053\uff1a\u201c\u6536\u62fe\u4f60\u8fd9\u79cd\u9ec4\u6bdb\u4e2b\u5934\uff0c\u8fd8\u7528\u5f97\u7740\u522b\u4eba\u8d39\u4e8b\uff1f\u201d \u3000\u3000\u4ed6\u76db\u6012\u4e4b\u4e0b\uff0c\u5df2\u4e0d\u518d\u5ba2\u5957\u4e86\uff0c\u64a4\u51fa\u957f\u5251\uff0c\u4fbf\u8981\u52a8\u624b\u3002 \u3000\u3000\u5ffd\u5730\u4e1c\u65b9\u7075\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u738b\u5144\u4e14\u6162\u52a8\u624b\uff0c\u6211\u8fd8\u6709\u51e0\u53e5\u95ee\u5979\u3002\u201d \u3000\u3000\u8bf4\u7740\u4ed6\u7f13\u6b65\u8d70\u5230\u524d\u9762\u6765\uff0c\u671d\u590f\u82b8\u62f1\u624b\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\u8bf7\u4e86\uff0c\u5728\u4e0b\u662f\u6c5f\u82cf\u864e\u90b1\u98de \u7075\u5821\u7684\u4e1c\u65b9\u7075\uff0c\u8fd9\u662f\u820d\u59b9\u4e1c\u65b9\u745b\u3002\u59d1\u5a18\u548c\u6b66\u5f53\u5c71\u7684\u7ea0\u7eb7\uff0c\u6211\u4eec\u4e5f\u65e0\u6743\u8fc7\u95ee\uff0c\u66f4\u4e0d\u4f1a\u548c\u59d1\u5a18\u52a8 \u624b\uff0c\u8fd9\u8bf7\u59d1\u5a18\u653e\u5fc3\uff0c\u53ea\u662f\u5728\u4e0b\u5374\u6709\u4e00\u4e8b\u8981\u8bf7\u6559\u59d1\u5a18\u3002\u201d \u3000\u3000\u590f\u82b8\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u4e1c\u65b9\u7075\u8bf4\u9053\uff1a\u201c\u542c\u8bf4\u59d1\u5a18\u548c\u718a\u501c\u672c\u662f\u540c\u4f34\uff0c\u4e0d\u77e5\u59d1\u5a18\u548c\u718a\u501c\u662f\u4f55\u5173\u7cfb\uff0c\u90a3\u718a\u501c\u73b0\u5728\u4f55 \u5904\uff0c\u5728\u4e0b\u6709\u4e9b\u4e8b\uff0c\u8981\u548c\u4ed6\u8bf4\u3002\u201d \u3000\u3000\u590f\u82b8\u4e00\u542c\u718a\u501c\uff0c\u53c8\u662f\u67f3\u7709\u5012\u7ad6\uff0c\u6068\u58f0\u8bf4\u9053\uff1a\u201c\u4ed6\u8dd1\u5230\u54ea\u91cc\u53bb\u4e86\uff0c\u6211\u600e\u4e48\u77e5\u9053\uff0c\u6211\u548c\u4ed6\u840d \u6c34\u76f8\u9022\uff0c\u4e00\u70b9\u5173\u7cfb\u4e5f\u6ca1\u6709\u3002\u201d \u3000\u3000\u4e1c\u65b9\u7075\u542c\u4e86\uff0c\u5fae\u5fae\u4e00\u6115\uff0c\u968f\u5373\u8bf4\uff1a\u201c\u8fd9\u5c31\u662f\u4e86\uff0c\u6211\u548c\u59d1\u5a18\u867d\u662f\u7d20\u6627\u5e73\u751f\uff0c\u4e0d\u8fc7\u6211\u770b\u59d1\u5a18 \u4e5f\u4e0d\u50cf\u662f\u4e2a\u4e3a\u975e\u4f5c\u6b79\u4e4b\u4eba\uff0c\u4eca\u65e5\u4e4b\u4e8b\uff0c\u5b9e\u5728\u9519\u5728\u59d1\u5a18\uff0c\u4f9d\u6211\u611a\u89c1\uff0c\u59d1\u5a18\u6700\u597d\u8fd8\u662f\u5c06\u9556\u65d7\u9001 \u56de\uff0c\u6211\u4e1c\u65b9\u7075\u62c5\u4fdd\u6b66\u5f53\u6d3e\u7684\u9053\u5144\uff0c\u4e5f\u4e0d\u4f1a\u96be\u4e3a\u4f60\u4e00\u4e2a\u5c0f\u59d1\u5a18\u3002\u201d \u3000\u3000\u4e1c\u65b9\u7075\u5728\u6b66\u6797\u4e4b\u4e2d\uff0c\u5730\u4f4d\u6781\u9ad8\uff0c\u6545\u6b64\u4ed6\u624d\u8bb2\u51fa\u8fd9\u4e9b\u8bdd\uff0c\u4e5f\u662f\u4e00\u756a\u597d\u610f\uff0c\u54ea\u77e5\u590f\u82b8\u5374\u4e1d\u6beb \u4e0d\u9886\u60c5\uff0c\u8bf4\u9053\uff1a\u201c\u5821\u4e3b\u7684\u8bdd\u65e2\u7136\u95ee\u5b8c\u4e86\uff0c\u5c31\u8bf7\u7ad9\u8fc7\u4e00\u8fb9\uff0c\u6211\u4e0d\u7ba1\u8c01\u5bf9\u8c01\u9519\uff0c\u4eca\u5929\u662f\u5b9a\u8981\u5411\u6b66 \u5f53\u6d3e\u8ba8\u6559\u7684\uff0c\u201d\u5979\u5728\u718a\u501c\u90a3\u91cc\uff0c\u53d7\u4e86\u4e00\u809a\u5b50\u7684\u59d4\u5c48\uff0c\u73b0\u5728\u5374\u90fd\u53d1\u6cc4\u5728\u8fd9\u91cc\uff0c\u4e1c\u65b9\u7075\u4e5f\u6709\u4e9b\u751f \u6c14\uff0c\u4fbf\u8d70\u5f00\u4e86\uff0c\u4ed6\u77e5\u9053\u590f\u82b8\u7684\u6b66\u529f\u518d\u9ad8\uff0c\u5728\u6b66\u5f53\u56db\u5b50\u624b\u91cc\uff0c\u4e5f\u8ba8\u4e0d\u4e86\u597d\u53bb\uff0c\u5b9a\u8981\u5403\u4e2a\u5927\u4e8f\u3002 \u3000\u3000\u4e1c\u65b9\u745b\u4e5f\u8ddf\u4e86\u8fc7\u53bb\uff0c\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u90a3\u718a\u501c\u4e00\u5b9a\u8fd8\u5728\u5e97\u91cc\uff0c\u4f60\u53bb\u627e\u627e\u561b\u3002\u201d \u3000\u3000\u4e1c\u65b9\u7075\u76b1\u7709\u8bf4\u9053\uff1a\u201c\u9ed1\u591c\u4e4b\u4e2d\uff0c\u6211\u600e\u80fd\u5728\u4eba\u5e97\u4e2d\u4e71\u95ef\uff0c\u6211\u60f3\u4ed6\u4e00\u5b9a\u8fd8\u4e0d\u4f1a\u8d70\uff0c\u6211\u4eec\u603b\u627e \u5f97\u5230\u7684\uff0c\u4f60\u5148\u522b\u7740\u6025\u3002\u201d \u3000\u3000\u8fd9\u8fb9\u4e5d\u5b98\u8fde\u73af\u5251\u738b\u9521\u4e5d\uff0c\u5df2\u5c06\u5251\u64a4\u5728\u624b\u4e2d\uff0c\u8f6c\u8eab\u5bf9\u53f2\u80d6\u5b50\u8bf4\u9053\uff1a\u201c\u4f60\u53bb\u5173\u7167\u5e97\u5bb6\uff0c\u53eb\u4f4f \u5e97\u7684\u5ba2\u4eba\u4e0d\u8981\u968f\u4fbf\u51fa\u6765\u4e71\u95ef\u3002\u201d \u3000\u3000\u738b\u9521\u4e5d\u53c8\u9053\uff1a\u201c\u5c31\u8bf7\u59d1\u5a18\u4eae\u5175\u5203\u52a8\u624b\uff0c\u6211\u4eec\u8bdd\u4e5f\u4e0d\u5fc5\u591a\u8bf4\u4e86\uff0c\u8fd8\u662f\u624b\u5e95\u4e0b\u89c1\u4e2a\u771f\u7ae0 \u5427\uff01\u201d \u3000\u3000\u590f\u82b8\u66f4\u4e0d\u7b54\u8bdd\uff0c\u4f38\u624b\u5411\u8eab\u8fb9\u6302\u7740\u7684\u888b\u5b50\u4e00\u6478\uff0c\u6478\u51fa\u4e00\u56e2\u94f6\u8272\u7684\u5706\u7403\uff0c\u5979\u968f\u624b\u4e00\u6296\uff0c\u7adf\u662f \u6761\u6781\u957f\u7684\u94f6\u97ad\uff0c\u539f\u6765\u5979\u662f\u5973\u5b69\u5b50\uff0c\u4eba\u53c8\u7231\u7f8e\uff0c\u8f6f\u5175\u5203\u4e0d\u4fbf\u7f20\u5728\u8170\u4e0a\uff0c\u4fbf\u653e\u5728\u8eab\u65c1\u7684\u9556\u888b\u91cc\u3002 \u3000\u3000\u738b\u9521\u4e5d\u89c1\u5979\u5175\u5668\u5df2\u4eae\u51fa\uff0c\u4fbf\u8bf4\u9053\uff1a\u201c\u5feb\u52a8\u624b\u5427\uff0c\u770b\u4f60\u662f\u4e2a\u59d1\u5a18\uff0c\u5148\u8ba9\u4f60\u4e09\u62db\u3002\u201d \u3000\u3000\u590f\u82b8\u5a07\u559d\u4e00\u58f0\uff1a\u201c\u8c01\u8981\u4f60\u8ba9\uff0c\u4f60\u82e5\u4e0d\u52a8\u624b\uff0c\u6211\u4e5f\u4e0d\u52a8\u624b\u3002\u201d \u3000\u3000\u738b\u9521\u4e5d\u559d\u4e00\u58f0\uff1a\u201c\u90a3\u4e48\u5c0f\u5fc3\u4e86\u3002\u201d\u957f\u5251\u4e00\u6296\uff0c\u633d\u8d77\u6597\u5927\u4e2a\u5251\u82b1\uff0c\u5251\u52bf\u5ffd\u5730\u4e00\u504f\uff0c\u659c\u659c\u5730 \u523a\u5411\u590f\u82b8\u7684\u5de6\u80a9\uff0c\u8fd9\u62db\u662f\u6b66\u5f53\u5251\u6cd5\u91cc\uff0c\u6700\u57fa\u672c\u7684\u4e00\u5f0f\uff0c\u5251\u5f0f\u672c\u5e94\u76f4\u70b9\u524d\u80f8\uff0c\u4f46\u738b\u9521\u4e5d\u5230\u5e95\u662f \u6b63\u6d3e\u51fa\u8eab\uff0c\u600e\u80fd\u5411\u5973\u5b69\u5b50\u524d\u80f8\u70b9\u53bb\uff0c\u6545\u6b64\u7a0d\u7a0d\u4e00\u504f\uff0c\u523a\u5411\u5de6\u80a9\u3002 \u3000\u3000\u590f\u82b8\u5fae\u4e00\u503e\u8eab\uff0c\u638c\u4e2d\u7684\u94f6\u97ad\uff0c\u5b9b\u5982\u7075\u86c7\u53cd\u566c\uff0c\u500f\u5730\u6d3b\u4e86\u8d77\u6765\uff0c\u97ad\u5934\u4e00\u629b\u4e00\u70b9\uff0c\u4e00\u62db\u201c\u9f99 \u5377\u51e4\u5934\u201d\u8fde\u524a\u5e26\u6253\uff0c\u5e26\u8d77\u4e00\u9053\u94f6\u8292\uff0c\u76f4\u627e\u738b\u9521\u4e5d\u7684\u9501\u8170\u7a74\uff0c\u7adf\u662f\u201c\u72c2\u97ad\u6cd5\u201d\u91cc\u7684\u7edd\u62db\u3002 \u3000\u3000\u738b\u9521\u4e5d\u54a6\u4e86\u4e00\u58f0\uff0c\u559d\u9053\uff1a\u201c\u4f60\u662f\u5b9d\u9a6c\u795e\u97ad\u8428\u5929\u9a87\u7684\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u590f\u82b8\u9053\uff1a\u201c\u8c01\u8ba4\u8bc6\u8428\u5929\u9aa5\u3002\u201d \u3000\u3000\u738b\u9521\u4e5d\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u624b\u91cc\u4e0d\u95f2\u7740\uff0c\u5251\u5f0f\u4e00\u541e\u4e00\u5410\uff0c\u968f\u5373\u4f7f\u51fa\u6b66\u5f53\u5c71\u9547\u5c71\u5251\u6cd5\u201c\u4e5d\u5bab\u8fde\u73af\u516b \u5341\u4e00\u5f0f\u201d\uff0c\u53ea\u89c1\u5251\u5149\u5982\u8679\uff0c\u62db\u62db\u4ff1\u662f\u8fde\u524a\u5e26\u6253\u7684\u5999\u7740\u3002 \u3000\u3000\u590f\u82b8\u624b\u5e87\u4e5f\u81ea\u4e0d\u5f31\uff0c\u957f\u97ad\u98ce\u58f0\u864e\u864e\uff0c\u76f4\u5982\u540c\u72c2\u9f99\u5377\uff0c\u58f0\u52bf\u60ca\u4eba\u3002 \u3000\u3000\u4e24\u4eba\u8eab\u5f62\u90fd\u6781\u7094\uff0c\u6643\u773c\u4fbf\u5df2\u76f4\u8d70\u4e86\u4e8c\u5341\u4f59\u4e2a\u7167\u9762\uff0c\u738b\u9521\u4e5d\u5fc3\u91cc\u4e0d\u7981\u6025\u8e81\u9053\uff1a\u201c\u600e\u5730\u8fd9\u5973 \u5b50\u5982\u6b64\u4e86\u5f97\uff0c\u6211\u6210\u540d\u6c5f\u6e56\u591a\u5e74\uff0c\u4eca\u591c\u82e5\u4e0d\u80fd\u80dc\u5f97\u8fd9\u65e0\u540d\u7684\u5c0f\u59d1\u5a18\uff0c\u5c82\u4e0d\u8981\u88ab\u4eba\u7b11\u6b7b\uff0c\u201d\u4ed6\u5fc3 \u795e\u4e00\u5206\uff0c\u4fbf\u843d\u8d25\u50cf\uff0c\u590f\u82b8\u4e00\u8fde\u51e0\u4e0b\u7edd\u62db\uff0c\u903c\u5f97\u738b\u9521\u4e5d\u6b65\u6b65\u540e\u9000\uff0c\u5979\u5f97\u7406\u4e0d\u8ba9\u4eba\uff0c\u8f7b\u5578\u4e00\u58f0\uff0c \u201c\u6d77\u62d4\u5c71\u6447\u201d\uff0c\u201c\u4e91\u6d8c\u5982\u5c71\u201d\u97ad\u5f71\u6f2b\u5929\uff0c\u5e26\u7740\u904d\u5730\u8000\u773c\u7684\u5149\u8292\uff0c\u76f4\u53d6\u738b\u9521\u4e5d\u3002 \u3000\u3000\u738b\u9521\u4e5d\u8fde\u9047\u9669\u62db\uff0c\u8feb\u4e0d\u5f97\u5df2\uff0c\u5251\u5f0f\u4e00\u633a\uff0c\u60f3\u4ece\u97ad\u5f71\u4e2d\u6b3a\u8eab\u8fdb\u53bb\uff0c\u590f\u82b8\u51b7\u7b11\u4e00\u58f0\uff0c\u624b\u8155\u4e00 \u7528\u529b\uff0c\u957f\u97ad\u56de\u5e26\uff0c\u5e73\u626b\u5934\u9876\uff0c\u738b\u9521\u4e5d\u9000\u6b65\u4ef0\u77e5\uff0c\u9976\u662f\u8fd9\u6837\uff0c\u53f3\u8033\u4ecd\u88ab\u97ad\u7a0d\u5e26\u7740\u4e00\u70b9\uff0c\u706b\u523a\u523a \u7684\u751f\u75db\u3002 \u3000\u3000\u590f\u82b8\u957f\u97ad\u4e00\u6536\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u6b66\u5f53\u7684\u5251\u6cd5\uff0c\u6211\u4e5f\u9886\u6559\u4e86\uff0c\u4e5f\u4e0d\u8ba8\u5982\u6b64\u3002\u201d\u5979\u53c8\u81ea\u8eab\u65c1\u888b \u4e2d\u638f\u51fa\u4e00\u7269\uff0c\u5374\u662f\u90a3\u201c\u4e5d\u5bab\u8fde\u73af\u65d7\u201d\u3002 \u3000\u3000\u5979\u968f\u624b\u629b\u5728\u5730\u4e0a\uff0c\u8bf4\uff1a\u201c\u8fd9\u73a9\u610f\u4f60\u4eec\u62ff\u53bb\uff0c\u6211\u624d\u4e0d\u8981\u5462\u3002\u201d \u3000\u3000\u738b\u9521\u4e5d\u6ee1\u8138\u901a\u7ea2\uff0c\u7f9e\u6127\u5730\u7ad9\u5728\u90a3\u513f\uff0c\u4e1c\u65b9\u7075\u5fc3\u4e2d\u6697\u81ea\u5403\u60ca\uff0c\u60f3\u4e0d\u5230\u8fd9\u5973\u5b50\u7adf\u80dc\u5f97\u4e86\u9102\u4e2d \u4e45\u8d1f\u76db\u540d\u7684\u738b\u9521\u4e5d\u3002 \u3000\u3000\u6b66\u5f53\u56db\u5b50\u4ea6\u662f\u53c8\u60ca\u53c8\u6012\uff0c\u51cc\u4e91\u5b50\u95ea\u8eab\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\u7aef\u7684\u597d\u97ad\u6cd5\uff0c\u53ea\u662f\u6b66\u5f53\u6d3e\u7684\u5251 \u6cd5\uff0c\u8981\u770b\u5728\u8c01\u624b\u4e2d\u4f7f\uff0c\u82e5\u5728\u8d2b\u9053\u7684\u624b\u4e0a\uff0c\u4e8c\u5341\u62db\u5185\uff0c\u6211\u82e5\u4e0d\u53eb\u59d1\u5a18\u8ba4\u8f93\uff0c\u6211\u5c31\u8dea\u4e0b\u78d5\u5934\u3002\u201d \u3000\u3000\u539f\u6765\u51cc\u4e91\u5b50\u5929\u4efd\u6781\u9ad8\uff0c\u6b66\u5f53\u8bf8\u5b50\u91cc\uff0c\u4ee5\u4ed6\u7684\u5251\u6cd5\u6700\u662f\u5389\u5bb3\uff0c\u518d\u52a0\u4e0a\u4ed6\u521a\u624d\u5728\u65c1\u8fb9\u7559\u5fc3\u590f \u82b8\u7684\u97ad\u6cd5\uff0c\u89c9\u5f97\u96c4\u539a\u6709\u4f59\uff0c\u7ec6\u817b\u4e0d\u8db3\uff0c\u770b\u4e0a\u53bb\u58f0\u52bf\u751a\u662f\u60ca\u4eba\uff0c\u4f46\u7834\u7efd\u4ecd\u591a\uff0c\u800c\u4e14\u590f\u82b8\u5185\u529b\u4e0d \u8db3\uff0c\u66f4\u662f\u4f7f\u7528\u8fd9\u79cd\u97ad\u6cd5\u7684\u5927\u5fcc\uff0c\u6240\u4ee5\u4ed6\u624d\u8bf4\u4e8c\u5341\u62db\u91cc\u53eb\u590f\u82b8\u843d\u8d25\u3002 \u3000\u3000\u590f\u82b8\u542c\u4e86\uff0c\u5fc3\u91cc\u5374\u4e0d\u670d\u6c14\uff0c\u51b7\u7b11\uff1a\u201c\u6253\u8f66\u8f6e\u6218\u4e0d\u8981\u627e\u501f\u53e3\uff0c\u8981\u4e0a\u5c31\u4e0a\u5427\u3002\u201d \u3000\u3000\u51cc\u4e91\u5b50\u8bf4\uff1a\u201c\u6211\u662f\u6559\u8bad\u6559\u8bad\u4f60\uff0c\u8ba9\u4f60\u77e5\u9053\u4eba\u5916\u6709\u4eba\uff0c\u4e0d\u8981\u5356\u72c2\u3002\u201d \u3000\u3000\u590f\u82b8\u559d\u9053\uff1a\u201c\u4f60\u5c11\u7f57\u54b3\u3002\u201d\u957f\u97ad\u539f\u5f0f\u800c\u8d77\uff0c\u53c8\u662f\u4e00\u62db\u201c\u4e91\u5982\u5c71\u6d8c\u3002\u201d \u3000\u3000\u51cc\u4e91\u5b50\u4fa7\u8eab\u4e00\u6b3a\uff0c\u5de6\u624b\u4f38\u6307\u5982\u671d\uff0c\u76f4\u70b9\u590f\u82b8\u7684\u201c\u80a9\u4e95\u7a74\u201d\u53f3\u624b\u53cd\u64a4\u957f\u5251\u3002 \u3000\u3000\u4ed6\u907f\u62db\u3001\u4fa7\u8eab\u3001\u8fdb\u51fb\u3001\u64a4\u5251\uff0c\u51e0\u4e4e\u662f\u540c\u4e00\u52a8\u4f5c\uff0c\u5e72\u51c0\u5229\u843d\u6f02\u4eae\u5df2\u6781\u3002 \u3000\u3000\u4e1c\u65b9\u7075\u5728\u65c1\u6697\u6697\u559d\u91c7\uff0c\u5fc3\u60f3\u8fd9\u51cc\u4e91\u5b50\u679c\u7136\u540d\u4e0d\u865a\u4f20\u3002 \u3000\u3000\u51cc\u4e91\u5b50\u9e64\u8863\u7384\u51a0\uff0c\u8863\u895f\u98d8\u98d8\uff0c\u957f\u5251\u968f\u610f\u6325\u51fa\uff0c\u6f47\u6f47\u6d12\u6d12\uff0c\u9880\u957f\u7684\u8eab\u5f71\u8f6c\u7740\u590f\u82b8\u76f4\u8f6c\uff0c\u590f \u82b8\u7684\u957f\u97ad\u653b\u8fdc\u4e0d\u653b\u8fd1\uff0c\u7adf\u4f7f\u4e0d\u51fa\u62db\u6765\uff0c\u5a01\u529b\u5927\u5927\u5730\u51cf\u5f31\u4e86\u3002 \u3000\u3000\u590f\u82b8\u7684\u6b66\u529f\uff0c\u672c\u4e5f\u662f\u4e00\u7b49\u4e00\u7684\u8eab\u624b\uff0c\u4f46\u6b64\u523b\u88ab\u51cc\u4e91\u5b50\u4e00\u62db\u5236\u5148\uff0c\u53ea\u89c9\u5f97\u7f1a\u624b\u7f1a\u811a\uff0c\u5979\u6781 \u5feb\u5730\u632a\u52a8\u7740\u8eab\u5b50\uff0c\u60f3\u8df3\u51fa\u51cc\u4e91\u5b50\u7684\u5708\u5b50\u3002 \u3000\u3000\u5ffd\u5730\u51cc\u4e91\u5b50\u6a2a\u7740\u4e00\u5251\uff0c\u5251\u8eab\u5e73\u7740\u62cd\u6765\uff0c\u590f\u82b8\u4e00\u6115\uff0c\u5fc3\u60f3\u54ea\u6709\u8fd9\u6837\u8ff8\u62db\u7684\uff0c\u4f46\u4ecd\u7136\u811a\u4e0b\u53d8 \u6b65\uff0c\u201c\u5012\u8e29\u4e03\u661f\uff0c\u5f80\u540e\u731b\u9000\uff0c\u54ea\u77e5\u51cc\u4e91\u5b50\u5982\u5f71\u9644\u968f\uff0c\u957f\u5251\u4ecd\u7136\u6a2a\u5728\u5979\u9762\u524d\uff0c\u5979\u4e00\u6025\uff0c\u97ad\u8eab\u56de \u5e26\uff0c\u5de6\u624b\u53d8\u638c\u4e3a\u6293\uff0c\u4f38\u624b\u60f3\u53bb\u593a\u5251\uff0c\u51cc\u4e91\u5b50\u5389\u559d\u4e00\u58f0\uff1a\u201c\u8eba\u4e0b\u201d\u5ffd\u5730\u5de6\u624b\u634f\u7740\u5251\u5c16\uff0c\u5251\u628a\u5f53 \u505a\u5251\u5c16\uff0c\u76f4\u70b9\u201c\u808b\u9ebb\u7a74\u3002\u201d \u3000\u3000\u590f\u82b8\u518d\u4e5f\u60f3\u4e0d\u5230\u4ed6\u4f1a\u65bd\u51fa\u8fd9\u7b49\u602a\u62db\uff0c\u907f\u65e0\u53ef\u907f\uff0c\u5de6\u808b\u4e00\u9ebb\uff0c\u957f\u97ad\u201c\u5f53\u201d\u5730\u6389\u5728\u5730\u4e0a\uff0c\u4eba \u4e5f\u5012\u4e0b\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u5239\u90a3\uff0c\u5979\u8111\u6d77\u91cc\u60f3\u8d77\u8bb8\u591a\u4e8b\uff0c\u5979\u60f3\u81ea\u5df1\u771f\u662f\u6c42\u8363\u53cd\u8fb1\uff0c\u81ea\u5df1\u4ee5\u4e3a\u81ea\u5df1\u6b66\u529f\u5df2\u662f\u5c11\u6709 \u654c\u624b\u4e86\uff0c\u54ea\u77e5\u4e8c\u5341\u62db\u5185\uff0c\u5c31\u8d25\u5728\u522b\u4eba\u624b\u91cc\u3002 \u3000\u3000\u718a\u501c\u7684\u8bdd\uff0c\u53c8\u5728\u5979\u8033\u8fb9\u54cd\u4e86\u8d77\u6765\uff0c\u201c\u50cf\u4f60\u8fd9\u6837\u7684\u813e\u6c14\uff0c\u65e9\u665a\u8981\u5403\u5927\u4e8f\u2026\u2026\u201d \u3000\u3000\u5979\u73b0\u5728\u591a\u60f3\u718a\u501c\u80fd\u5728\u5979\u8eab\u8fb9\uff0c\u4fdd\u62a4\u7740\u5979\uff0c\u5979\u89c9\u5f97\u718a\u501c\u662f\u5979\u6240\u552f\u4e00\u80fd\u4f9d\u9760\u7684\u4eba\u4e86\u3002 \u3000\u3000\u51cc\u4e91\u5b50\u6162\u6162\u5730\u5c06\u5251\u6536\u56de\u5251\u9798\u91cc\uff0c\u8f6c\u773c\u4e00\u671b\u4e1c\u65b9\u7075\uff0c\u4e1c\u65b9\u7075\u4e5f\u81ea\u542b\u7b11\u671b\u7740\u4ed6\u3002 \u3000\u3000\u4e1c\u65b9\u745b\u89c1\u590f\u82b8\u8d1f\u4f24\u5012\u5730\uff0c\u5230\u5e95\u540c\u662f\u5973\u5b50\uff0c\u7269\u4f24\u5176\u7c7b\uff0c\u800c\u4e14\u5979\u542c\u8bf4\u590f\u82b8\u548c\u718a\u501c\u4e2d\u95f4\u5b9e\u65e0\u74dc \u845b\uff0c\u6c14\u5df2\u6d88\u4e86\u5927\u534a\uff0c\u6b64\u65f6\u5979\u8d70\u4e0a\u524d\u53bb\uff0c\u4fef\u8eab\u95ee\u5367\u5728\u5730\u4e0a\u7684\u590f\u82b8\u9053\uff1a\u201c\u4f60\u4f24\u7684\u4e0d\u8981\u7d27\u5427\uff1f\u201d \u3000\u3000\u590f\u82b8\u51c4\u60cb\u5730\u6447\u4e86\u6447\u5934\uff0c\u6b64\u65f6\u5979\u53c8\u60b2\u53c8\u601c\uff0c\u6ee1\u8154\u8c6a\u6c14\uff0c\u8d70\u5f97\u65e0\u5f71\u65e0\u8e2a\u3002 \u3000\u3000\u51cc\u4e91\u5b50\u56de\u5934\u5411\u4e39\u9633\u5b50\u95ee\u9053\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\u5e94\u8be5\u600e\u4e48\u53d1\u843d\uff1f\u201d \u3000\u3000\u4e39\u9633\u5b50\u9053\uff1a\u201c\u8fd9\u4e2a\u5973\u5b50\u5192\u72af\u4e86\u4e5d\u5b98\u8fde\u73af\u65d7\uff0c\u7167\u7406\u8bb2\u8be5\u5c06\u5979\u5e9f\u4e86\u3002\u201d \u3000\u3000\u590f\u82b8\u542c\u4e86\uff0c\u673a\u4f36\u4f36\u6253\u4e86\u4e00\u4e2a\u51b7\u6545\uff0c\u5979\u6b64\u523b\u751f\u6b7b\u4f24\u5e9f\uff0c\u90fd\u63e1\u5728\u522b\u4eba\u624b\u91cc\u3002 \u3000\u3000\u4e39\u9633\u5b50\u53c8\u63a5\u7740\u8bf4\uff1a\u201c\u4e0d\u8fc7\u59d1\u5ff5\u5979\u5e74\u5e7c\u65e0\u77e5\uff0c\u73b0\u53c8\u8d1f\u4e86\u6781\u91cd\u7684\u5185\u4f24\uff0c\u6743\u5c06\u5979\u5e26\u56de\u6b66\u5f53\u5c71 \u53bb\uff0c\u7f5a\u5979\u5728\u7956\u5e08\u795e\u50cf\u524d\uff0c\u5f53\u4f17\u53e9\u5934\u8ba4\u9519\u3002\u201d \u3000\u3000\u4e1c\u65b9\u7075\u5fc3\u4e2d\u6697\u601d\u9053\uff1a\u201c\u4eba\u8a00\u6b66\u5f53\u56db\u5b50\uff0c\u6700\u662f\u96be\u7f20\uff0c\u6b64\u8a00\u679c\u771f\u4e0d\u865a\uff0c\u4eba\u5bb6\u5df2\u7ecf\u53d7\u4e86\u4f24\uff0c\u8fd8 \u8981\u5e26\u4eba\u5bb6\u5230\u5c71\u4e0a\u53bb\u78d5\u5934\uff1a\u201c\u51cc\u4e91\u5b50\u89c1\u590f\u82b8\u542b\u6cea\u4ecd\u7136\u534a\u5367\u5728\u5730\u4e0a\uff0c\u5fc3\u4e2d\u4e5f\u751a\u89c9\u4e0d\u5fcd\uff0c\u4ed6\u706b\u6c14\u867d \u5927\uff0c\u5fc3\u80a0\u5374\u8f6f\uff0c\u6447\u4e86\u6447\u5934\uff0c\u53f9\u6c14\u8bf4\u9053\uff1a\u201c\u5176\u5b9e\u6211\u4e5f\u4e0d\u613f\u4f24\u4f60\uff0c\u53ea\u662f\u6211\u90a3\u62db\u2018\u9633\u706d\u9634\u751f\u2019\u5a01\u529b \u592a\u5927\uff0c\u5bf9\u65b9\u8d8a\u662f\u95ea\u907f\uff0c\u8d8a\u89c1\u5a01\u529b\uff0c\u4f60\u4e0d\u660e\u5176\u4e2d\u8bc0\u8981\uff0c\u4fbf\u5984\u81ea\u95ea\u907f\uff0c\u6545\u6b64\u53d7\u4e86\u5185\u4f24\u3002\u201d \u3000\u3000\u590f\u82b8\u53ea\u89c9\u808b\u95ee\u9635\u9635\u4f5c\u75db\uff0c\u6323\u624e\u7740\u60f3\u722c\u8d77\u6765\uff0c\u53c8\u6d51\u8eab\u65e0\u529b\u3002 \u3000\u3000\u51cc\u4e91\u5b50\u53c8\u8bf4\u9053\uff1a\u201c\u4f60\u8ddf\u6211\u4eec\u56de\u6b66\u5f53\u5c71\u53bb\uff0c\u5185\u4f24\u4e5f\u53ef\u901f\u6108\uff0c\u4e0d\u7136\u666e\u5929\u4e4b\u4e0b\uff0c\u80fd\u533b\u5f97\u4e86\u8fd9\u79cd \u649e\u7a74\u4e4b\u4f24\u7684\u4eba\uff0c\u6050\u6015\u5c11\u4e4b\u53c8\u5c11\u3002\u201d \u3000\u3000\u590f\u82b8\u773c\u542b\u75db\u6cea\uff0c\u547b\u541f\u9053\uff1a\u201c\u6211\u5c31\u662f\u6b7b\u4e86\uff0c\u4e5f\u4e0d\u8ddf\u4f60\u4eec\u4e00\u8d77\u53bb\u3002\u201d \u3000\u3000\u4e1c\u65b9\u745b\u5fc3\u91cc\u770b\u5f97\u96be\u53d7\uff0c\u4e5f\u5e2e\u7740\u8bf4\uff1a\u201c\u5404\u4f4d\u5c31\u9976\u4e86\u5979\u5427\u3002\u201d \u3000\u3000\u4e39\u9633\u5b50\u6b63\u8272\u8bf4\u9053\uff1a\u201c\u8fd9\u7b49\u4e8b\u5173\u7cfb\u7740\u6b66\u5f53\u5a01\u540d\uff0c\u8d2b\u9053\u4e5f\u4f5c\u4e0d\u5f97\u4e3b\uff0c\u8fd8\u5f97\u8981\u56de\u5c71\u53bb\uff0c\u8bf7\u638c\u6559 \u5e08\u5c0a\u4eb2\u81ea\u53d1\u843d\uff0c\u4e0d\u8fc7\u6211\u4fdd\u8bc1\u4e0d\u4f1a\u96be\u4e3a\u5979\u5c31\u662f\u4e86\u3002\u201d \u3000\u3000\u8fd9\u65f6\u5df2\u8fd1\u4e94\u9f13\uff0c\u6653\u8272\u5df2\u8d77\uff0c\u4f17\u4eba\u6b63\u60f3\u7ed3\u675f\u8fd9\u4ef6\u4e8b\uff0c\u5ffd\u5730\u773c\u524d\u4e00\u82b1\uff0c\u4e00\u6761\u9b3c\u9b45\u4f3c\u7684\u5f71\u5b50\uff0c \u8f7b\u98d8\u98d8\u5730\u81ea\u773c\u524d\u98de\u8fc7\u3002 \u3000\u3000\u5927\u5bb6\u518d\u4e00\u770b\uff0c\u5730\u4e0a\u7684\u590f\u82b8\u5374\u5df2\u4e0d\u77e5\u53bb\u5411\u4e86\uff0c\u4ed6\u4eec\u4ff1\u662f\u6b66\u6797\u4e2d\u9876\u5c16\u7684\u4eba\u7269\uff0c\u6b64\u523b\u7adf\u7136\u5f53\u7740 \u4ed6\u4eec\u9762\u524d\uff0c\u4e22\u4e86\u4e2a\u6d3b\u4eba\uff0c\u5404\u4e2a\u5fc3\u4e2d\u4ff1\u662f\u53c8\u60ca\u53c8\u6012\u3002 \u3000\u3000\u4e39\u9633\u5b50\u5e72\u54b3\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4eba\u8eab\u6cd5\u4e4b\u5feb\uff0c\u6211\u8d70\u904d\u6c5f\u6e56\uff0c\u5b9e\u8fd8\u672a\u66fe\u89c1\u8fc7\uff0c\u53ea\u4e0d\u77e5\u9053\u6b64 \u662f\u4f55\u4eba\uff0c\u6709\u8fd9\u6837\u7384\u5999\u7684\u8eab\u6cd5\uff0c\u800c\u53c8\u548c\u6b66\u5f53\u4e3a\u654c\u3002\u201d \u3000\u3000\u4e1c\u65b9\u745b\u7d27\u7cfb\u9edb\u7709\uff0c\u8bf4\uff1a\u201c\u770b\u4ed6\u7684\u8eab\u6cd5\u5f71\u5b50\uff0c\u6211\u60f3\u4e00\u5b9a\u662f\u718a\u501c\u3002\u201d \u3000\u3000\u4e39\u9633\u5b50\u4f4e\u4f4e\u5730\u5ff5\u4e86\u4e24\u58f0\uff1a\u201c\u718a\u501c\uff0c\u718a\u501c\u2026\u2026\u201d \u3000\u3000\u591c\u4ecd\u6df1\uff0c\u6000\u62b1\u7740\u53d7\u4e86\u4f24\u7684\u590f\u82b8\uff0c\u718a\u501c\u65e0\u52a9\u5730\u4ec1\u7acb\u5728\u8def\u65c1\u4e00\u4e00\u4e2a\u660f\u6697\u7684\u89d2\u843d\u91cc\uff0c\u4e0d\u77e5\u600e\u4e48 \u529e\u624d\u597d\uff0c\u4ed6\u4e0d\u613f\u548c\u6b66\u5f53\u7684\u56db\u4eea\u5251\u5ba2\u6b63\u9762\u4e3a\u654c\uff0c\u662f\u4ee5\u9664\u4e86\u5fc5\u987b\u5148\u6cbb\u597d\u590f\u82b8\u7684\u4f24\u52bf\u5916\uff0c\u8fd8\u8981\u7559\u610f \u5730\u8eb2\u907f\u7740\u4ed6\u4eec\u7684\u8ffd\u8e2a\uff0c\u7136\u800c\u5728\u8fd9\u4eba\u5730\u751f\u758f\u4e4b\u5904\uff0c\u4ed6\u8be5\u4f55\u53bb\u4f55\u4ece\uff1f \u3000\u3000\u501f\u7740\u661f\u5149\uff0c\u4ed6\u671b\u7740\u6000\u62b1\u4e2d\u7684\u590f\u82b8\u7684\u9762\u5b54\uff0c\u751a\u81f3\u53ef\u4ee5\u770b\u51fa\u5979\u9762\u4e0a\u7684\u989c\u8272\uff0c\u90a3\u662f\u4e00\u79cd\u5931\u8840\u7684 \u82cd\u767d\u8272\uff0c\u548c\u5979\u4ee5\u524d\u90a3\u79cd\u5a07\u7f8e\u7684\u5ae3\u7ea2\uff0c\u5b8c\u5168\u4e0d\u540c\u3002 \u3000\u3000\u5fae\u4e00\u8f6c\u4fa7\uff0c\u590f\u82b8\u5fae\u542f\u661f\u7738\uff0c\u50cf\u662f\u9192\u4e86\uff0c\u718a\u501c\u8fde\u5fd9\u4fef\u4e0b\u5934\u53bb\uff0c\u6e29\u67d4\u4ed6\u8bf4\u9053\uff1a\u201c\u4f60\u96be\u53d7 \u5417\uff1f\u201d \u3000\u3000\u590f\u82b8\u5f20\u76ee\u4e00\u770b\uff0c\u89c1\u662f\u718a\u501c\uff0c\u8138\u4e0a\u7efd\u5f00\u4e86\u7b11\u5bb9\uff0c\u4f38\u624b\u52fe\u7740\u718a\u501c\u7684\u8116\u5b50\uff0c\u8f7b\u8f7b\u4ed6\u8bf4\uff1a\u201c\u6211\u5f88 \u96be\u53d7\uff0c\u80f8\u53e3\u5f88\u75bc\uff0c\u201d\u718a\u501c\u5b89\u6170\u5730\u629a\u6478\u7740\u5979\uff0c\u8bf4\uff1a\u201c\u4e0d\u8981\u7d27\u7684\uff0c\u7b49\u4e00\u4f1a\u5c31\u597d\u4e86\u3002\u201d \u3000\u3000\u7136\u800c\u5fc3\u4e2d\u5374\u77e5\u9053\uff0c\u8fd9\u79cd\u88ab\u5185\u5bb6\u9ad8\u624b\u6240\u521b\u4e4b\u4f24\u52bf\uff0c\u51b3\u4e0d\u662f\u4e00\u4f1a\u513f\u5c31\u4f1a\u597d\u7684\uff0c\u4ed6\u5fc5\u987b\u5148\u5bfb\u5f97 \u4e00\u4e2a\u5b89\u9759\u800c\u9690\u50fb\u7684\u5904\u6240\uff0c\u6765\u68c0\u9a8c\u590f\u82b8\u7684\u4f24\u52bf\uff0c\u7136\u540e\u518d\u4ee5\u81ea\u5df1\u7684\u5185\u529b\uff0c\u6765\u52a9\u5979\u590d\u539f\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u5fc3\u7eea\u7d0a\u4e71\u5982\u9ebb\uff0c\u76ee\u5149\u7231\u601c\u5730\u770b\u590f\u82b8\uff0c\u89c1\u5979\u6b63\u8981\u8bf4\u8bdd\uff0c\u5ffd\u5730\u7a7a\u4e2d\u4f20\u6765\u591c\u884c\u9ad8\u624b\u8863\u8882 \u5e26\u98ce\u4e4b\u58f0\uff0c\u5fd9\u7528\u624b\u63a9\u4f4f\u590f\u82b8\u7684\u98ce\u58f0\u5230\u4e86\u718a\u501c\u542c\u5b58\u8eab\u4e4b\u5904\u7684\u623f\u6a90\u4e0a\uff0c\u5ffd\u5730\u987f\u4f4f\uff0c\u718a\u501c\u6599\u5b9a\u5fc5\u662f \u6b66\u5f53\u56db\u5b50\u6765\u5bfb\u4eba\uff0c\u5fd9\u5c4f\u606f\u9760\u5899\u800c\u7acb\uff0c\u4ed6\u5b9e\u4e0d\u613f\u4e0e\u4ed6\u4eec\u9762\u5bf9\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u542c\u5230\u5c4b\u4e0a\u4e00\u4e2a\u5973\u5b50\u53e3\u97f3\u6781\u4f4e\u58f0\u5730\u57cb\u6028\u9053\uff1a\u201c\u90fd\u662f\u5927\u54e5\uff0c\u6211\u770b\u4ed6\u671d\u8fd9\u9762\u8d70\u7684\uff0c\u53eb\u4f60 \u5feb\u70b9\u8ffd\uff0c\u4f60\u53c8\u4e0d\u80af\uff0c\u73b0\u5728\u518d\u4e5f\u627e\u4e0d\u5230\u4e86\u3002\u201d \u3000\u3000\u718a\u501c\u8bc6\u5f97\u90a3\u662f\u4e1c\u65b9\u745b\u7684\u53e3\u97f3\uff0c\u5fc3\u4e2d\u66f4\u662f\u6253\u9f13\uff0c\u82e5\u7136\u88ab\u5979\u53d1\u73b0\uff0c\u81ea\u5df1\u60f3\u8d70\u90fd\u4e0d\u597d\u610f\u601d\u3002 \u3000\u3000\u63a5\u7740\u4ed6\u53c8\u542c\u5230\u4e1c\u65b9\u7075\u8bf4\u9053\uff1a\u201c\u4e0d\u8981\u602a\u4eba\u4e86\uff0c\u51ed\u4f60\u8fd9\u6837\u7684\u8f7b\u529f\uff0c\u5c31\u662f\u518d\u65e9\u8ffd\uff0c\u4e5f\u8ffd\u4e0d\u4e0a\u4eba \u5bb6\uff0c\u5e73\u65e5\u53eb\u4f60\u7528\u529f\uff0c\u4f60\u603b\u662f\u4e0d\u80af\uff0c\u73b0\u5728\u8be5\u77e5\u9053\u4e86\u5427\uff0c\u4ee5\u540e\u82e5\u8981\u901e\u5f3a\uff0c\u5c31\u5f97\u591a\u4e0b\u6614\u529f\u3002\u201d \u3000\u3000\u4e1c\u65b9\u745b\u8f7b\u8f7b\u4e00\u8dfa\u811a\uff0c\u5979\u53ef\u5fd8\u4e86\u8fd9\u662f\u6df1\u591c\u5728\u4eba\u5bb6\u5c4b\u9876\u4e0a\uff0c\u5a07\u55d4\u9053\uff1a\u201c\u5927\u54e5\u771f\u662f\u7684\uff0c\u73b0\u5728\u4eba \u5bb6\u6025\u5f97\u8981\u6b7b\uff0c\u4f60\u8fd8\u8981\u6559\u8bad\u4eba\u3002\u201d \u3000\u3000\u54ea\u77e5\u5979\u4e00\u8dfa\u811a\uff0c\u5c4b\u6a90\u4e0a\u7684\u79ef\u5c18\uff0c\u843d\u5728\u4ef0\u7740\u9762\u7684\u590f\u82b8\u8138\u4e0a\uff0c\u5979\u4e0b\u610f\u8bc6\u5730\u6664\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u8fd9\u4e00\u58f0\u628a\u718a\u501c\u6664\u51fa\u4e00\u8eab\u51b7\u6c57\uff0c\u4ed6\u77e5\u9053\u8fd9\u7edd\u7792\u4e0d\u8fc7\u4e1c\u65b9\u5144\u59b9\u7684\u8033\u76ee\u3002 \u3000\u3000\u679c\u7136\uff0c\u4e1c\u65b9\u745b\u6025\u901f\u5730\u8f6c\u4e86\u4e2a\u8eab\uff0c\u5411\u4e1c\u65b9\u7075\u8bf4\u9053\uff1a\u201c\u597d\u50cf\u4ed6\u4eec\u8fd8\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u4e1c\u65b9\u7075\u4f55\u5c1d\u4e0d\u542c\u5f97\u66f4\u6e05\u695a\uff0c\u4f46\u5374\u56e0\u8fd1\u65e5\u60c5\u611f\u4e0a\u7684\u8bad\u7ec3\uff0c\u77e5\u9053\u60c5\u4e4b\u4e00\u5b57\uff0c\u6700\u662f\u4e0d\u80fd\u52c9\u5f3a\uff0c \u5373\u4f7f\u8ffd\u4e0a\u718a\u501c\uff0c\u53c8\u4f55\u82e6\u53bb\u7834\u574f\u522b\u4eba\u5462\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u4e00\u62c9\u4e1c\u65b9\u745b\u7684\u624b\u81c2\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u771f\u662f\u6709\u70b9\u8fc7\u5206\u7d27\u5f20\u4e86\uff0c\u4eba\u5bb6\u6b64\u523b\u6015\u4e0d\u65e9\u5df2\u8d70\u5f97\u8fdc \u8fdc\u7684\uff0c\u8fd8\u4f1a\u803d\u5728\u8fd9\u91cc\u7b49\u4f60\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u5fae\u4e00\u4f5c\u52bf\uff0c\u62c9\u7740\u4e1c\u65b9\u745b\u98de\u8eab\u800c\u53bb\u3002 \u3000\u3000\u718a\u501c\u5728\u4e0b\u9762\u677e\u4e86\u4e00\u53e3\u6c14\uff0c\u5fc3\u91cc\u6697\u4e2d\u611f\u6fc0\u7740\u4e1c\u65b9\u7075\uff0c\u4ed6\u5f53\u7136\u4e86\u89e3\u8fd9\u662f\u4e1c\u65b9\u7075\u6697\u52a9\u4ed6\uff0c\u4e0d\u7136 \u51ed\u4e1c\u65b9\u7075\u7684\u8033\u76ee\uff0c\u8fd8\u4f1a\u542c\u672c\u51fa\u8fd9\u58f0\u97f3\u3002 \u3000\u3000\u590f\u82b8\u5374\u5fcd\u4e0d\u4f4f\u8bf4\u9053\uff1a\u201c\u8fd9\u5973\u4e8e\u662f\u8c01\u5440\uff0c\u597d\u50cf\u5bf9\u4f60\u5173\u5fc3\u5f97\u5f88\uff0c\u521a\u624d\u6211\u5c31\u770b\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u718a\u501c\u7b11\u4e86\u4e00\u7b11\uff0c\u4ed6\u6697\u5fd6\u9053\uff1a\u201c\u5973\u5b50\u7684\u5fc3\u5883\u771f\u662f\u5947\u602a\u5f97\u5f88\uff0c\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u5c45\u7136\u8fd8\u4f1a\u5403 \u918b\u3002\u201d\u4f46\u662f\u4ed6\u81ea\u7136\u4e0d\u6562\u5c06\u8fd9\u610f\u601d\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u8fdc\u5904\u5df2\u6709\u9e21\u557c\uff0c\u8f6c\u77ac\u5929\u5c31\u8981\u4eae\u4e86\uff0c\u718a\u501c\u4e0d\u7981\u66f4\u662f\u7740\u6025\uff0c\u4ed6\u5f88\u60f3\u8d76\u5feb\u627e\u5230\u9690\u85cf\u7684\u5730\u65b9\u3002 \u3000\u3000\u4ed6\u601d\u524d\u60f3\u540e\uff0c\u7a81\u7136\u60f3\u8d77\u4e00\u5904\u53ef\u4ee5\u5bb9\u8eab\u7684\u5730\u65b9\u6765\uff0c\u4ed6\u5fc3\u4e2d\u6253\u7b97\uff1a\u201c\u90a3\u65f6\u5bb6\u5144\u5f1f\uff0c\u884c\u8e2a\u867d\u662f \u8be1\u5f02\uff0c\u4f46\u5374\u662f\u4e2a\u4e49\u6c14\u4e3a\u5148\u7684\u597d\u6c49\uff0c\u4ed6\u7b49\u6709\u80b2\u5728\u5148\uff0c\u8bf4\u5982\u679c\u6709\u4e8b\u9700\u8981\u5e2e\u52a9\uff0c\u53ef\u5230\u5404\u5927\u57ce\u5e02\u7684\u5546 \u94fa\u6c42\u52a9\uff0c\u53ea\u8981\u53d6\u51fa\u90a3\u679a\u53e4\u94b1\uff0c\u4fbf\u53ef\u4ee5\u5f97\u5230\u5e2e\u52a9\u3002\u201d \u3000\u3000\u4ed6\u8f6c\u5ff5\u53c8\u5fd6\u9053\uff1a\u201c\u4f46\u8fd9\u57ce\u4e2d\u5546\u94fa\u5982\u6b64\u4e4b\u591a\uff0c\u6211\u600e\u77e5\u54ea\u4e00\u5bb6\u4e0e\u53f6\u6c0f\u5144\u5f1f\u662f\u6709\u5173\u5462\uff1f\u201d \u3000\u3000\u4e8e\u662f\u4ed6\u8fb9\u8d70\u8fb9\u6253\u91cf\u7740\u8857\u9053\u3002 \u3000\u3000\u590f\u82b8\u89c1\u4ed6\u4e45\u4e0d\u8bf4\u8bdd\uff0c\u6084\u4fcf\u5730\u626d\u52a8\u4e86\u4e0b\u8170\uff0c\u6664\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u5582\uff0c\u4f60\u5728\u60f3\u4ec0\u4e48\uff0c\u6211\u95ee\u4f60 \u7684\u8bdd\uff0c\u4f60\u4e5f\u4e0d\u56de\u7b54\u3002\u201d \u3000\u3000\u718a\u501c\u9053\uff1a\u201c\u6211\u662f\u5728\u60f3\u6211\u4eec\u8be5\u5230\u4f55\u5904\u53bb\uff0c\u6211\u53c8\u60f3\u8d77\u6211\u4eec\u5728\u6b64\u5904\u4eba\u5730\u751f\u758f\uff0c\u53c8\u8981\u8eb2\u5f00\u6b66\u5f53\u56db \u5b50\u7684\u8ffd\u8e2a\uff0c\u60f3\u6765\u60f3\u53bb\uff0c\u4f3c\u4e4e\u53ea\u6709\u90a3\u53f6\u6c0f\u5144\u5f1f\u4e4b\u5904\uff0c\u53ef\u4ee5\u5f97\u5230\u5e2e\u5fd9\uff0c\u4f46\u6b64\u5904\u5546\u5e97\u5982\u6b64\u591a\uff0c\u6211\u53c8 \u600e\u4e48\u53bb\u627e\uff1f\u201d \u3000\u3000\u590f\u82b8\u9053\uff1a\u201c\u4ed6\u4e0d\u662f\u66fe\u7ecf\u7ed9\u4f60\u4e00\u679a\u53e4\u94b1\u4e3a\u8bb0\u5417\uff1f\u201d \u3000\u3000\u718a\u501c\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u590f\u82b8\u9053\uff1a\u201c\u90a3\u5929\u6211\u5728\u5f53\u6d82\u90a3\u5bb6\u8863\u94fa\u7684\u5e97\u62db\u4e0a\uff0c\u5c31\u66fe\u770b\u5230\u6709\u4e00\u5904\u53e4\u94b1\u6807\u8bb0\uff0c\u4f60\u5728\u8fd9\u6761\u8857\u4e0a \u77a7\u77a7\uff0c\u8bf4\u4e0d\u5b9a\u4e5f\u6709\u6b64\u6807\u8bb0\u3002\u201d \u3000\u3000\u718a\u501c\u679c\u7136\u770b\u5230\u8def\u5934\u7b2c\u56db\u5bb6\u7684\u5e97\u4e0a\uff0c\u5c31\u6709\u4e00\u679a\u53e4\u94b1\u6807\u8bb0\uff0c\u800c\u4e14\u4e5f\u662f\u5bb6\u8863\u94fa\uff0c\u5fc3\u77e5\u6b64\u5904\u5fc5\u5b9a \u662f\u4e86\u3002 \u3000\u3000\u4ed6\u7565\u4e00\u601d\u91cf\uff0c\u89c9\u5f97\u591c\u6df1\u62cd\u95e8\uff0c\u8fd8\u4e0d\u5982\u8d8a\u5899\u800c\u5165\uff0c\u53cd\u6b63\u53f6\u5bb6\u5144\u5f1f\u4e5f\u662f\u6c5f\u6e56\u4e2d\u4eba\uff0c\u60f3\u5fc5\u4e0d\u4f1a \u8d23\u602a\u81ea\u5df1\uff0c\u4e8e\u662f\u4ed6\u5fae\u4e00\u7eb5\u8eab\uff0c\u8d8a\u8fc7\u4e86\u56f4\u5899\u548c\u524d\u9762\u7684\u5c4b\u9876\uff0c\u843d\u5728\u540e\u9662\u91cc\uff0c\u5374\u53d1\u73b0\u540e\u9662\u4e2d\u7684\u4e00\u6392 \u623f\u5b50\u91cc\uff0c\u4ecd\u7136\u70b9\u7740\u706f\u706b\uff0c\u800c\u4e14\u4eba\u5f71\u61a7\u61a7\uff0c\u50cf\u6709\u8bb8\u591a\u4eba\u5728\u91cc\u9762\u3002 \u3000\u3000\u718a\u501c\u7528\u624b\u6307\u7532\u5728\u7a97\u6846\u8fb9\u7684\u7a97\u7eb8\u4e0a\u70b9\u4e86\u4e00\u4e2a\u6708\u7259\u5c0f\u5b54\uff0c\u63a2\u76ee\u5411\u91cc\u661f\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u671b\uff0c\u9976\u4ed6\u518d\u662f\u9547\u5b9a\uff0c\u5374\u4e5f\u60ca\u51fa\u4e00\u8eab\u51b7\u6c57\uff0c\u624b\u5fae\u4e00\u6296\uff0c\u6000\u4e2d\u7684\u590f\u82b8\u9669\u4e9b\u5760\u4e0b\u3002 \u3000\u3000\u8fd9\u5c4b\u5185\u5171\u6709\u516d\u4eba\uff0c\u9664\u4e86\u8001\u4e09\u4e4b\u5916\uff0c\u4ed6\u5728\u957f\u6c5f\u6e21\u5934\u6240\u9047\u7684\u53f6\u8001\u5927\uff0c\u53f6\u8001\u4e8c\u4e5f\u90fd\u5728\u5ea7\uff0c\u53e6\u5916 \u8fd8\u6709\u4e8c\u4e2a\u5546\u8d3e\u6a21\u6837\u7684\u4e2d\u5e74\u4eba\uff0c\u548c\u4e00\u4e2a\u4e30\u795e\u4fca\u6717\u7684\u5e74\u8f7b\u4eba\uff0c\u684c\u4e0a\u53ca\u5730\u4e0a\u653e\u7740\u56db\u53ea\u7bb1\u5b50\uff0c\u5176\u4e2d\u4e09 \u53ea\u7bb1\u5b50\uff0c\u91d1\u5149\u7480\u707f\uff0c\u5168\u662f\u91d1\u94f6\u73e0\u5b9d\u4e4b\u7c7b\u3002 \u3000\u3000\u53e6\u4e00\u53e3\u7bb1\u5b50\u5374\u662f\u718a\u501c\u60ca\u60b8\u7684\u539f\u56e0\uff0c\u539f\u6765\u90a3\u7bb1\u5b50\u4e2d\u7adf\u6ee1\u6ee1\u5806\u7740\u4eba\u5934\uff0c\u800c\u4e14\u4e00\u4e2a\u4e2a\u53d1\u9afb\u4ff1 \u5168\uff0c\u9762\u76ee\u5982\u751f\uff0c\u50cf\u662f\u7ecf\u8fc7\u836f\u7269\u6ce1\u5236\u3002 \u3000\u3000\u5c4b\u4e2d\u516d\u4eba\u6b63\u5c06\u7bb1\u5b50\u7684\u4eba\u5934\u4e00\u4e2a\u4e2a\u53d6\u51fa\uff0c\u653e\u5728\u684c\u4e0a\uff0c\u800c\u4e14\u9762\u8272\u90fd\u4e25\u8083\u5f97\u5f88\uff0c\u718a\u501c\u867d\u4e5f\u7b97\u5f97 \u4e0a\u89c1\u8fc7\u4e0d\u5c11\u4e16\u9762\uff0c\u5374\u4ece\u672a\u89c1\u8fc7\u8fd9\u7b49\u5947\u4e8b\u3002 \u3000\u3000\u4ed6\u6b64\u6765\u672c\u662f\u60f3\u83b7\u4e00\u85cf\u8eab\u4e4b\u5730\uff0c\u4f46\u89c1\u4e86\u8fd9\u5b97\u5947\u4e8b\uff0c\u5fc3\u4e2d\u987f\u65f6\u53c8\u6ca1\u4e3b\u610f\uff0c\u4ed6\u832b\u7136\u4e4b\u4e2d\uff0c\u4fbf\u60f3 \u62bd\u8eab\u79bb\u53bb\uff0c\u987b\u77e5\u6c5f\u6e56\u4e2d\u4eba\u6700\u72af\u5fcc\u7684\uff0c\u4fbf\u662f\u6709\u4eba\u7aa5\u7834\u4e86\u4ed6\u4eec\u7684\u9690\u79d8\uff0c\u718a\u501c\u4e5f\u77e5\u9053\u6b64\u70b9\uff0c\u662f\u4ee5\u62bd \u8eab\u79bb\u53bb\uff0c\u60f3\u5c3d\u65e9\u79bb\u5f00\u8fd9\u662f\u975e\u4e4b\u5730\u518d\u505a\u6253\u7b97\u3002 \u3000\u3000\u4ed6\u65b9\u81ea\u5728\u79fb\u52a8\u7740\u811a\u6b65\uff0c\u7a97\u5185\u706f\u706b\u7a81\u5730\u706d\u4e86\uff0c\u718a\u501c\u4e00\u60ca\uff0c\u77e5\u9053\u5df2\u88ab\u4eba\u53d1\u89c9\uff0c\u53f3\u811a\u4e00\u70b9\uff0c\u4eba \u4fbf\u8c61\u71d5\u5b50\u822c\u79bb\u5730\u800c\u8d77\uff0c\u5728\u7a7a\u4e2d\u7565\u4e00\u8f6c\u6298\uff0c\u843d\u5728\u5c4b\u810a\u4e0a\u3002 \u3000\u3000\u4ed6\u6b63\u7a9c\u5230\u623f\u4e0a\u4e4b\u9645\uff0c\u55d6\u5730\uff0c\u5c4b\u4e0a\u53c8\u591a\u4e86\u4e00\u9053\u4eba\u5f71\uff0c\u63a5\u7740\u5c4b\u9876\u7684\u53e6\u4e09\u9762\u4e5f\u8fde\u7eed\u7a9c\u4e0a\u4e09\u4eba\u3002 \u3000\u3000\u6700\u5148\u7a9c\u4e0a\u5c4b\u9876\u90a3\u4eba\uff0c\u5355\u638c\u4e00\u7acb\uff0c\u6c89\u58f0\u53d1\u8bdd\u9053\uff1a\u201c\u597d\u4e2a\u9e70\u722a\u5b59\uff0c\u62db\u5b50\u5012\u771f\u4eae\uff0c\u5c45\u7136\u7f00\u5230\u8fd9 \u513f\u6765\u4e86\uff0c\u4eca\u5929\u5012\u8981\u89c1\u8bc6\u89c1\u8bc6\u4f60\u6709\u591a\u5927\u80fd\u4e3a\u3002\u201d \u3000\u3000\u8bdd\u672a\u8bf4\u5b8c\uff0c\u5de6\u638c\u4e00\u5f15\uff0c\u53f3\u638c\u659c\u524a\uff0c\u4e00\u62db\u201c\u7384\u9e1f\u5212\u6c99\u201d\u5e26\u7740\u98ce\u58f0\u76f4\u5288\u718a\u501c\u7684\u9888\u5b50\u3002 \u3000\u3000\u718a\u501c\u5728\u661f\u5149\u4e0b\u4e00\u770b\u6b64\u4eba\uff0c\u5374\u662f\u90a3\u5c4b\u4e2d\u7684\u4fca\u6717\u540e\u751f\uff0c\u5fc3\u4e2d\u6781\u5feb\u5730\u7b97\u8ba1\u7740\uff1a\u201c\u6b64\u4eba\u8f7b\u529f\u3001\u638c \u529b\uff0c\u90fd\u4e0d\u4fd7\uff0c\u6211\u6000\u4e2d\u62b1\u7740\u590f\u82b8\uff0c\u600e\u80fd\u4e0e\u4ed6\u4eec\u786c\u62fc\uff0c\u800c\u4e14\u4e8b\u51fa\u6709\u8bef\u4f1a\uff0c\u6211\u5728\u6ca1\u6709\u67e5\u660e\u4ed6\u4eec\u6765\u8def \u4e4b\u524d\uff0c\u8fd8\u662f\u80fd\u4e58\u65e9\u8131\u8eab\u4e3a\u4e0a\uff0c\u4e0d\u5fc5\u4e45\u7f20\u3002\u201d \u3000\u3000\u4ed6\u5fc3\u5728\u7b97\u8ba1\u7740\uff0c\u624b\u4e2d\u53ef\u6ca1\u95f2\u7740\uff0c\u8f6c\u773c\u95f4\uff0c\u5de6\u638c\u8fde\u524a\u5e26\u6253\uff0c\u5df2\u548c\u5bf9\u65b9\u63a5\u4e86\u4e09\u638c\u3002 \u3000\u3000\u4ed6\u8fd9\u4e09\u638c\uff0c\u867d\u662f\u968f\u610f\u62e9\u51fa\uff0c\u4f46\u4ed6\u591a\u5e74\u7684\u82e6\u7ec3\uff0c\u638c\u4e0a\u81ea\u7136\u5c31\u6709\u5a01\u529b\uff0c\u800c\u4e14\u62db\u5f0f\u4e4b\u7cbe\u5999\uff0c\u66f4 \u975e\u666e\u901a\u6b66\u6797\u4e2d\u4eba\u53ef\u4ee5\u60f3\u89c1\u7684\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u4e43\u662f\u8fd1\u65e5\u6c5f\u6e56\u4e2d\u58f0\u540d\u751a\u5927\u7684\u540e\u8d77\u4e4b\u79c0\uff0c\u638c\u6cd5\u81ea\u4ea6\u4e0d\u4fd7\uff0c\u4f46\u4ed6\u201c\u7384\u9e1f\u5212\u6c99\u201d\u4e4b\u540e\uff0c \u8ddf\u7740\u201c\u624b\u6325\u4e94\u5f26\u201d\uff0c\u201c\u9519\u9aa8\u5206\u7b4b\u201d\u4e09\u62db\u4ff1\u90fd\u88ab\u718a\u501c\u770b\u4f3c\u975e\u5e38\u8f7b\u6613\u5730\u5316\u89e3\u4e86\u53bb\uff0c\u518d\u4e00\u770b\uff0c\u718a\u501c \u624b\u4e2d\u7adf\u8fd8\u62b1\u7740\u4e00\u4eba\uff0c\u5fc3\u4e2d\u4e0d\u7981\u6fc0\u8d77\u597d\u80dc\u4e4b\u5fc3\uff0c\u53cc\u638c\u4e00\u9519\uff0c\u731b\u4e00\u6536\u52bf\u3002 \u3000\u3000\u718a\u501c\u89c1\u5bf9\u65b9\u7a81\u5730\u6536\u52bf\uff0c\u5374\u5927\u51fa\u610f\u6599\uff0c\u90a3\u5c11\u5e74\u5374\u51b7\u7b11\u9053\uff1a\u201c\u670b\u53cb\u771f\u597d\u8eab\u624b\uff0c\u60f3\u4e0d\u5230\u5374\u4f1a\u66ff \u6ee1\u4eba\u5f53\u5974\u624d\uff0c\u771f\u6559\u6211\u53ef\u60dc\u3002\u201d \u3000\u3000\u4ed6\u53cc\u76ee\u4e00\u77aa\uff0c\u773c\u4e2d\u5a01\u51cc\u56db\u73b0\uff0c\u90a3\u4f3c\u4e4e\u4e0d\u662f\u4e00\u4e2a\u5c11\u5e74\u6240\u80fd\u6709\u7684\u5a01\u51cc\uff0c\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u9601\u4e0b\u6b64 \u523b\u6000\u4e2d\u62b1\u7740\u4e00\u4eba\uff0c\u52a8\u8eab\u81ea\u662f\u4e0d\u4fbf\uff0c\u5c31\u8bf7\u9601\u4e0b\u5148\u5c06\u62b1\u7740\u7684\u4eba\u653e\u5728\u4e00\u8fb9\uff0c\u6211\u5c1a\u67d0\u4eba\u4fdd\u8bc1\u4e0d\u635f\u5979\u4e00 \u6839\u6beb\u6bdb\uff0c\u4eca\u5929\u597d\u670b\u53cb\u82e5\u4e0d\u89c1\u4e2a\u771f\u7ae0\uff0c\u60f3\u6d3b\u7740\u56de\u53bb\u662f\u529e\u4e0d\u5230\u7684\u4e86\u3002\u201d \u3000\u3000\u718a\u501c\u773c\u529b\u7279\u4f73\uff0c\u89c1\u6b64\u4eba\u76ee\u6e05\u795e\u6717\uff0c\u8bf4\u8bdd\u5149\u660e\u78ca\u843d\uff0c\u800c\u4e14\u53e3\u53e3\u58f0\u58f0\u5c06\u81ea\u5df1\u8ba4\u505a\u6ee1\u6e05\u7684\u722a \u7259\uff0c\u60f3\u5fc5\u662f\u4e2a\u53cd\u6e05\u5fd7\u58eb\uff0c\u81ea\u5df1\u66f4\u4e0d\u613f\u548c\u4ed6\u52a8\u624b\uff0c\u4f46\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u4ed6\u53c8\u4e0d\u613f\u89e3\u91ca\u3002 \u3000\u3000\u4ed6\u4e3b\u610f\u5df2\u5b9a\uff0c\u51b3\u5b9a\u5148\u95ef\u6b64\u6b64\u5730\u518d\u8bf4\uff0c\u66f4\u4e0d\u7b54\u8bdd\uff0c\u53f3\u624b\u7d27\u62b1\u590f\u82b8\uff0c\u5de6\u638c\u5fae\u626c\uff0c\u5148\u5929\u771f\u6c14\uff0c \u968f\u638c\u800c\u51fa\uff0c\u51c6\u5907\u786c\u95ef\u51fa\u53bb\u3002 \u3000\u3000\u90a3\u4eba\u6012\u53f1\u9053\uff1a\u201c\u597d\u670b\u53cb\u5c45\u7136\u4e0d\u5356\u5e10\u3002\u201d\u53f3\u638c\u4e00\u5708\u4e00\u53d1\uff0c\u5c45\u7136\u786c\u63a5\u4e86\u718a\u501c\u4e00\u638c\uff0c\u968f\u53e9\u53cc\u638c \u8fde\u53d1\uff0c\u201c\u79cb\u96e8\u843d\u67ab\u201d\u3001\u201c\u843d\u82f1\u98d8\u98de\u201d\uff0c\u53cc\u638c\u5982\u6f2b\u5f00\u82b1\u96e8\uff0c\u6781\u5feb\u5730\u5411\u718a\u501c\u62cd\u51fa\u6570\u638c\u3002 \u3000\u3000\u718a\u501c\u89c1\u4ed6\u638c\u6cd5\u7279\u5f02\uff0c\u662f\u4ed6\u524d\u6240\u672a\u89c1\u7684\u7cbe\u5999\uff0c\u7ade\u4f3c\u4e0d\u662f\u672c\u4e0a\u6240\u4f20\u7684\u638c\u6cd5\uff0c\u4f46\u4ed6\u638c\u62db\u867d\u662f\u51cc \u5389\uff0c\u4f46\u5374\u7edd\u672a\u62cd\u5411\u6000\u4e2d\u7684\u590f\u82b8\uff0c\u4e0d\u7981\u5bf9\u6b64\u4eba\u66f4\u751f\u51fa\u597d\u611f\uff0c\u4f46\u5bf9\u653b\u6765\u4e4b\u638c\uff0c\u53c8\u4e0d\u5f97\u4e0d\u63a5\uff0c\u5fd9\u81ea \u51dd\u795e\uff0c\u65bd\u5c55\u51fa\u98d8\u7136\u8001\u4eba\u82e6\u7814\u800c\u6210\u7684\u65e0\u540d\u638c\u6cd5\u548c\u7edd\u9876\u8f7b\u529f\uff0c\u5316\u89e3\u4e86\u8fd9\u7cbe\u5999\u7684\u653b\u52bf\uff0c\u53ea\u89c1\u4eba\u5f71\u98d8 \u5ffd\uff0c\u4e24\u4eba\u5df2\u62c6\u4e86\u5341\u6570\u62db\u3002 \u3000\u3000\u6b64\u523b\u5929\u5df2\u73b0\u66d9\u8272\uff0c\u6668\u55b7\u6e10\u660e\uff0c\u718a\u501c\u5fae\u4e00\u8f6c\u8138\uff0c\u5bf9\u7740\u8eab\u540e\u90a3\u4eba\uff0c\u90a3\u4eba\u7a81\u5730\u4e00\u58f0\u9ad8\u547c\u9053\uff1a \u201c\u5440\uff0c\u600e\u5730\u662f\u4f60\uff0c\u5c1a\u5f53\u5bb6\u7684\u5feb\u4e9b\u4f4f\u624b\uff0c\u90fd\u662f\u81ea\u5df1\u4eba\u3002\u201d \u3000\u3000\u718a\u501c\u773c\u89d2\u5fae\u659c\uff0c\u89c1\u53d1\u8bdd\u7684\u6b63\u662f\u90a3\u957f\u6c5f\u6e21\u5934\u9047\u5230\u7684\u602a\u8d3e\u53f6\u8001\u5927\uff0c\u5fc3\u77e5\u884c\u85cf\u5df2\u663e\uff0c\u81ea\u5df1\u65e0\u610f \u4e2d\u7aa5\u89c1\u522b\u4eba\u7684\u9690\u79d8\uff0c\u867d\u975e\u6709\u610f\u4f46\u4e5f\u4e0d\u597d\u610f\u601d\uff0c\u4f46\u4e8b\u5df2\u81f3\u6b64\uff0c\u8bf4\u4e0d\u5f97\u53ea\u597d\u5f53\u9762\u89e3\u91ca\u3002 \u3000\u3000\u90a3\u52a8\u7740\u624b\u7684\u5c11\u5e74\u542c\u5230\u53f6\u8001\u5927\u7684\u53eb\u58f0\uff0c\u811a\u5c16\u5fae\u70b9\uff0c\u8eab\u5f62\u5012\u7eb5\u51fa\u53bb\uff0c\u8be7\u5f02\u5730\u671b\u7740\u718a\u501c\u3002 \u3000\u3000\u718a\u501c\u5f53\u7136\u4e5f\u81ea\u505c\u624b\uff0c\u4f46\u5374\u4e0d\u77e5\u9053\u8be5\u5982\u4f55\u5e94\u4ed8\u8fd9\u573a\u9762\uff0c\u53f6\u8001\u5927\u6717\u58f0\u7b11\uff1a\u201c\u957f\u6c5f\u4e00\u522b\uff0c\u9601\u4e0b \u5374\u50cf\u5b8c\u5168\u6362\u4e86\u4e00\u4eba\uff0c\u8981\u4e0d\u662f\u5728\u4e0b\u8fd8\u8bb0\u5f97\u9601\u4e0b\u7684\u98ce\u59ff\uff0c\u6b64\u523b\u771f\u8ba4\u4e0d\u51fa\u6765\u4e86\u3002\u201d\u4ed6\u6717\u58f0\u53c8\u662f\u4e00 \u7b11\uff0c\u7a81\u53c8\u6b63\u5bb9\u8bf4\u9053\uff1a\u201c\u9601\u4e0b\u591c\u6df1\u6765\u8bbf\uff0c\u60f3\u5fc5\u6709\u4e8b\uff0c\u5148\u8bf7\u4e0b\u53bb\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u718a\u501c\u522b\u65e0\u4ed6\u6cd5\uff0c\u4fbf\u62b1\u7740\u590f\u82b8\u7eb5\u4e0b\u623f\u53bb\uff0c\u4ed6\u4f4e\u5934\u4e00\u770b\u590f\u82b8\uff0c\u54ea\u77e5\u4ed6\u521a\u624d\u4e00\u756a\u6253\u6597\uff0c\u590f\u82b8\u7adf \u53c8\u660f\u8ff7\u8fc7\u53bb\u4e86\uff0c\u4ed6\u5fc3\u4e2d\u81ea\u662f\u7740\u6025\u3002 \u3000\u3000\u6b64\u65f6\uff0c\u53f6\u8001\u5927\u548c\u90a3\u5c11\u5e74\u4ee5\u53ca\u53e6\u5916\u4e8c\u4eba\uff0c\u4e5f\u4ff1\u90fd\u4e0b\u4e86\u623f\u3001\u53f6\u8001\u5927\u53f3\u624b\u5fae\u4e3e\uff0c\u8bf7\u5ba2\u4eba\u5c4b\uff0c\u718a \u501c\u7f13\u6b65\u8d70\u4e86\u8fdb\u53bb\uff0c\u89c1\u5c4b\u4e2d\u5df2\u7a7a\u65e0\u4e00\u7269\uff0c\u90a3\u56db\u53e3\u7bb1\u5b50\u90fd\u4e0d\u77e5\u6536\u5230\u4f55\u5904\u53bb\u4e86\uff0c\u53f6\u8001\u4e8c\u548c\u53f6\u8001\u4e09\u5374 \u7aef\u5750\u5728\u5c4b\u4e2d\uff0c\u4e00\u89c1\u662f\u718a\u501c\u8fdb\u6765\uff0c\u4ff1\u90fd\u5c06\u624b\u62f1\u4e86\u62f1\uff0c\u542b\u7b11\u62db\u547c\u3002 \u3000\u3000\u718a\u501c\u603b\u89c9\u5f97\u4ed6\u4eec\u7684\u7b11\u5bb9\u91cc\u6709\u4e9b\u6545\u610f\uff0c\u5fc3\u77e5\u4eba\u5bb6\u4e5f\u6478\u4e0d\u6e05\u81ea\u5df1\u7684\u6765\u8def\uff0c\u5f53\u7136\u4f1a\u6000\u7591\u81ea\u5df1\u7684 \u6765\u610f\uff0c\u90a3\u5c11\u5e74\u6700\u540e\u8fdb\u95e8\uff0c\u5e76\u4e14\u968f\u624b\u5c06\u95e8\u63a9\u4e0a\u3002 \u3000\u3000\u5c4b\u4e2d\u4f17\u4eba\uff0c\u90fd\u773c\u6014\u6014\u5730\u770b\u7740\u718a\u501c\u548c\u4ed6\u6000\u4e2d\u7684\u590f\u82b8\u3002 \u3000\u3000\u53f6\u8001\u5927\u8d70\u5230\u684c\u65c1\uff0c\u5012\u4e86\u4e00\u676f\u8336\uff0c\u9001\u5230\u718a\u501c\u9762\u524d\uff0c\u9053\uff1a\u201c\u5bd2\u591c\u5ba2\u6765\u8336\u4f5c\u9152\uff0c\u5144\u53f0\u957f\u591c\u5954 \u6ce2\uff0c\u60f3\u5fc5\u751a\u662f\u52b3\u7d2f\uff0c\u6743\u996e\u4e00\u676f\uff0c\u518d\u8bf4\u6765\u610f\u5427\u3002\u201d \u3000\u3000\u718a\u501c\u8003\u8651\u4e86\u5f88\u4e45\uff0c\u624d\u8bf4\u9053\uff1a\u201c\u6df1\u591c\u6253\u6270\u3001\u5b9e\u975e\u5f97\u5df2\uff0c\u7686\u56e0\u655d\u53cb\u65e0\u610f\u4e2d\u5f97\u7f6a\u4e86\u6b66\u5f53\u56db\u5b50\uff0c \u53d7\u4e86\u91cd\u4f24\uff0c\u5c0f\u5f1f\u53c8\u56e0\u6545\u4e0d\u80fd\u548c\u6b66\u5f53\u56db\u5b50\u7167\u9762\uff0c\u662f\u4ee5\u5fc5\u987b\u5bfb\u4e00\u59a5\u5f53\u4e4b\u5904\uff0c\u4e3a\u655d\u53cb\u7597\u4f24\uff0c\u5c0f\u5f1f\u5728 \u6b64\u4eba\u5730\u751f\u758f\uff0c\u7a81\u7136\u60f3\u8d77\u8d35\u5144\u5f1f\u4e49\u8584\u4e91\u5929\uff0c\u6545\u6b64\u4e0d\u5acc\u5192\u6627\u5c31\u95ef\u6765\u4e86\u3002\u201d \u3000\u3000\u53f6\u8001\u5927\u54e6\u4e86\u4e00\u58f0\uff0c\u4fbf\u4f4e\u7740\u5934\u6c89\u601d\u8d77\u6765\uff0c\u50cf\u662f\u4e5f\u5728\u60f3\u7740\u5e94\u4ed8\u4e4b\u7b56\u3002 \u3000\u3000\u90a3\u59d3\u5c1a\u7684\u5c11\u5e74\u5251\u7709\u4e00\u626c\uff0c\u8bf4\u9053\uff1a\u201c\u9601\u4e0b\u65e2\u662f\u6709\u56e0\u6765\u8bbf\uff0c\u4f55\u4ee5\u5374\u9b3c\u9b3c\u795f\u795f\u5730\u7ad9\u5728\u7a97\u4e0b\u63a2\u542c \u522b\u4eba\u7684\u9690\u79d8\uff0c\u8fd9\u70b9\u8fd8\u8bf7\u9601\u4e0b\u89e3\u91ca\u660e\u767d\u3002\u201d \u3000\u3000\u718a\u501c\u59d4\u5b9e\u7b54\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u53f6\u8001\u5927\u5374\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u5144\u53f0\u8bb8\u662f\u65e0\u610f\u7684\uff0c\u53ea\u662f\u5144\u53f0\u5230\u5e95\u8d35\u59d3\u5927\u540d\uff0c\u8d35\u53cb\u53c8\u600e\u4f1a\u548c\u540d\u4f20\u6c5f \u6e56\u7684\u56db\u4eea\u5251\u5ba2\u7ed9\u4e0b\u6881\u5b50\uff1f\u201d \u3000\u3000\u718a\u501c\u4f46\u7136\u9053\uff1a\u201c\u5728\u4e0b\u718a\u501c\uff0c\u655d\u53cb\u590f\u82b8\u56e0\u4e3a\u5e74\u8f7b\u6c14\u76db\uff0c\u4e3a\u4e86\u70b9\u5c0f\u4e8b\u7adf\u548c\u6b66\u5f53\u6d3e\u7ed3\u4e0b\u6881\u5b50\uff0c \u8bf4\u6765\u8bf4\u53bb\uff0c\u8fd8\u8981\u8bf7\u53f6\u5f53\u5bb6\u7684\u591a\u5e2e\u5fd9\u3002\u201d \u3000\u3000\u53f6\u8001\u5927\u4e00\u542c\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u65e9\u5c31\u77e5\u9053\u9601\u4e0b\u5fc5\u975e\u5e38\u4eba\uff0c\u679c\u7136\u6211\u8001\u773c\u4e0d\u82b1\uff0c\u9601\u4e0b\u7adf\u662f\u4e0e\u53cc \u7edd\u3001\u56db\u4eea\u9f50\u540d\u7684\u718a\u501c\uff0c\u8fd1\u6765\u9601\u4e0b\u7684\u79cd\u79cd\u4f20\u8bf4\uff0c\u5728\u4e0b\u542c\u5f97\u591a\u4e86\uff0c\u8bf4\u8001\u5b9e\u8bdd\uff0c\u6211\u518d\u4e5f\u6ca1\u6709\u60f3\u5230\u957f \u6c5f\u6e21\u5934\u7684\u5c11\u5e74\u4e10\u8005\uff0c\u7adf\u4f1a\u662f\u4e09\u79c0\u5e76\u56db\u4eea\u7684\u4e09\u79c0\uff0c\u54c8\uff0c\u54c8\u3002\u201d\u8bf4\u7740\uff0c\u53c8\u662f\u4e00\u9635\u5f97\u610f\u7684\u5927\u7b11\u3002 \u3000\u3000\u53f6\u8001\u4e8c\uff0c\u53f6\u8001\u4e09\u4e5f\u9762\u9732\u559c\u8272\uff0c\u53f6\u8001\u4e8c\u7a81\u7136\u95ee\u9053\uff1a\u201c\u8d35\u53cb\u590f\u82b8\uff0c\u53ef\u5c31\u662f\u4f20\u8bf4\u4e2d\u8fd1\u5e74\u626c\u540d\u767d \u5c71\u9ed1\u6c34\u95f4\u7684\u5973\u4fa0\uff0c\u843d\u65e5\u9a6c\u573a\u573a\u4e3b\u7684\u7231\u5973\uff0c\u96ea\u5730\u98d8\u98ce\u590f\u5973\u4fa0\u5417\uff1f\u82e5\u679c\u771f\u662f\u5979\uff0c\u90a3\u6211\u5f1f\u5144\u8fd9\u5c0f\u5c0f \u5730\u65b9\uff0c\u4e00\u591c\u4e4b\u4e2d\uff0c\u7adf\u6765\u4e86\u4e09\u4f4d\u9ad8\u4eba\uff0c\u771f\u662f\u6211\u5144\u5f1f\u7684\u4e00\u5927\u5feb\u4e8b\u4e86\u3002\u201d \u3000\u3000\u53f6\u8001\u5927\u5fae\u4e00\u62cd\u638c\uff0c\u7b11\u9053\uff1a\u201c\u6211\u81ea\u987e\u9ad8\u5174\uff0c\u7adf\u5fd8\u4e86\u66ff\u4f60\u4eec\u51e0\u4f4d\u5f15\u89c1\u4e86\u3002\u201d \u3000\u3000\u4ed6\u7528\u624b\u6307\u7740\u90a3\u4e24\u4f4d\u4e5f\u662f\u5546\u8d3e\u6a21\u6837\u7684\u4e2d\u5e74\u4eba\u8bf4\u9053\uff1a\u201c\u8fd9\u4e24\u4f4d\u662f\u6211\u7684\u751f\u6b7b\u4e4b\u4ea4\uff0c\u9a6c\u9e9f\u3001\u9a6c\u9aa5 \u5144\u5f1f\uff0c\u4e0d\u6015\u718a\u5144\u89c1\u7b11\uff0c\u6211\u5144\u5f1f\u51e0\u4eba\u90bb\u4e0d\u8fc7\u662f\u6c5f\u6e56\u7684\u65e0\u540d\u5c0f\u5352\u7f62\u4e86\u3002\u201d\u4ed6\u53c8\u624b\u6307\u7740\u90a3\u5c11\u5e74\u8bf4 \u9053\uff1a\u201c\u558f\uff0c\u8fd9\u4f4d\u5374\u4e5f\u662f\u6c5f\u6e56\u4e2d\u5927\u540d\u9f0e\u9f0e\u7684\u4eba\u7269\uff0c\u6b66\u6797\u4e2d\u63d0\u8d77\u94c1\u80c6\u5c1a\u672a\u660e\u6765\uff0c\u4e5f\u8bf4\u5f97\u4e0a\u4eba\u4eba\u7686 \u77e5\u4e86\uff0c\u4f60\u4eec\u4e24\u4f4d\u5c11\u5e74\u82f1\u6770\uff0c\u5012\u771f\u8981\u591a\u4eb2\u8fd1\u4eb2\u8fd1\u3002\u201d\u4ed6\u8bf4\u8bdd\u603b\u662f\u5e26\u7740\u4e09\u5206\u7b11\u5bb9\uff0c\u4ee4\u4eba\u4e0d\u671f\u800c\u751f \u4e00\u79cd\u4eb2\u5207\u4e4b\u611f\uff0c\u8fd9\u4e5f\u8bb8\u5c31\u662f\u4ed6\u80fd\u521b\u7acb\u5927\u4e1a\u7684\u5730\u65b9\u5427\u3002 \u3000\u3000\u94c1\u80c6\u5c1a\u672a\u660e\u7b11\u9053\uff1a\u201c\u53f6\u8001\u5927\u53c8\u5f80\u6d45\u8138\u4e0a\u8d34\u91d1\u4e86\uff0c\u5012\u662f\u718a\u5144\u771f\u662f\u6211\u7d20\u6240\u4ef0\u6155\u7684\u4eba\u7269\uff0c\u5c0f\u5f1f \u9002\u624d\u591a\u6709\u5f97\u7f6a\uff0c\u8fd8\u8981\u8bf7\u718a\u5144\u6055\u7f6a\u3002\u201d \u3000\u3000\u718a\u501c\u4e00\u542c\uff0c\u6084\u7136\u60f3\u8d77\u5e38\u542c\u4eba\u8bf4\u8fd1\u5e74\u897f\u6cb3\u7eff\u6797\u9053\u51fa\u4e86\u4e2a\u5927\u5927\u7684\u8c6a\u6770\uff0c\u521d\u51fa\u6c5f\u6e56\uff0c\u4fbf\u6210\u4e3a\u897f \u6cb3\u7eff\u6797\u9053\u7684\u603b\u74e2\u628a\u5b50\uff0c\u5374\u4e5f\u662f\u4e2a\u5982\u6b64\u82f1\u4fca\u7684\u5c11\u5e74\u82f1\u96c4\uff0c\u4e0d\u7531\u751f\u51fa\u60fa\u60fa\u76f8\u60dc\u4e4b\u5fc3\uff0c\u8d70\u4e0a\u524d\u53bb\u63e1 \u7740\u4ed6\u7684\u624b\u9053\uff1a\u201c\u5c1a\u5144\u5343\u4e07\u4e0d\u8981\u5ba2\u6c14\uff0c\u65b9\u624d\u90fd\u662f\u5c0f\u5f1f\u7684\u4e0d\u662f\uff0c\u5c0f\u5f1f\u6b63\u8981\u8bf7\u5c1a\u5144\u6055\u7f6a\uff0c\u4f60\u6211\u4e00\u89c1 \u5982\u6545\uff0c\u4ee5\u540e\u8fd8\u8bf7\u4e0d\u8981\u89c1\u5916\u624d\u597d\u3002\u201d \u3000\u3000\u4ed6\u8fd9\u4e00\u4e0a\u53bb\u63e1\u7740\u5c1a\u672a\u660e\u7684\u624b\uff0c\u5174\u594b\u4e4b\u4e0b\uff0c\u5374\u5fd8\u8bb0\u676f\u4e2d\u5c1a\u62b1\u7740\u590f\u82b8\uff0c\u662f\u4ee5\u590f\u82b8\u521a\u597d\u963b\u5728\u4e24 \u4eba\u4e2d\u95f4\uff0c\u4e00\u773c\u671b\u53bb\uff0c\u597d\u50cf\u4e24\u4eba\u90fd\u5728\u62b1\u7740\u590f\u82b8\u4f3c\u7684\u3002 \u3000\u3000\u53f6\u8001\u4e8c\u5323\u7b11\u9053\uff1a\u201c\u718a\u5144\u4e0d\u8981\u5ba2\u6c14\u4e86\uff0c\u8fd8\u662f\u5148\u5c06\u8d35\u53cb\u5b89\u7f6e\u597d\uff0c\u4f60\u6211\u5f1f\u5144\u518d\u8c08\u4e5f\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u718a\u501c\u6717\u7136\u7b11\u9053\uff1a\u201c\u5c0f\u5f1f\u9aa4\u7136\u4e4b\u95f4\uff0c\u4ea4\u5230\u8fd9\u8bb8\u591a\u597d\u670b\u53cb\uff0c\u672a\u514d\u559c\u6781\u5fd8\u5f62\u4e86\u3002\u201d\u4ed6\u4f4e\u7740\u5934\u770b \u7740\u590f\u82b8\uff0c\u8138\u8272\u6108\u53d1\u574f\u4e86\uff0c\u4e0d\u7981\u53c8\u53cc\u7709\u76b1\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u655d\u53cb\u7684\u4f24\u52bf\u975e\u8f7b\uff0c\u4ed6\u662f\u88ab\u6b66\u5f53\u56db\u5b50 \u4e2d\u7684\u51cc\u4e91\u5b50\u5185\u529b\u6240\u4f24\uff0c\u6050\u6015\u4e00\u65f6\u8fd8\u5f88\u96be\u590d\u539f\uff0c\u8fd8\u8bf7\u53f6\u5f53\u5bb6\u7684\u627e\u95f4\u9759\u5ba4\uff0c\u4ee5\u540e\u6050\u6015\u8981\u9ebb\u70e6\u53f6\u5f53 \u5bb6\u7684\u4e00\u6bb5\u65f6\u5019\u4e86\u3002\u201d \u3000\u3000\u53f6\u8001\u5927\u5fd9\u8bf4\u9053\uff1a\u201c\u4f60\u6211\u4eca\u540e\u5c31\u662f\u81ea\u5df1\u5f1f\u5144\u4e86\uff0c\u8fd8\u8bf4\u4ec0\u4e48\u9ebb\u70e6\u4e0d\u9ebb\u70e6\uff0c\u6211\u8fd9\u91cc\u867d\u7136\u662f\u4f4d\u4e8e \u95f9\u5e02\uff0c\u4f46\u540e\u9662\u5374\u6e05\u9759\u5f97\u5f88\uff0c\u6b64\u95f4\u7edd\u4e0d\u4f1a\u6709\u4eba\u8fdb\u6765\u7684\uff0c\u590f\u5973\u4fa0\u8981\u517b\u4f24\uff0c\u518d\u597d\u4e5f\u6ca1\u6709\u4e86\u3002\u201d \u3000\u3000\u4ed6\u4fa7\u8138\u5411\u53f6\u8001\u4e8c\u8bf4\uff1a\u201c\u4f60\u628a\u671d\u5357\u7684\u90a3\u95f4\u4e66\u623f\u6536\u62fe\u4e00\u4e0b\uff0c\u590f\u5973\u4fa0\u5c31\u6682\u65f6\u4f4f\u5728\u90a3\u91cc\u597d\u4e86\uff0c\u4e66 \u623f\u7684\u65c1\u90a3\u95f4\u623f\u95f4\uff0c\u5c31\u6682\u65f6\u59d4\u5c48\u718a\u5144\u4e00\u4e0b\uff0c\u6b63\u597d\u7167\u5e94\u590f\u5973\u4fa0\u3002\u201d\u53f6\u8001\u4e8c\u5e94\u58f0\u53bb\u4e86\u3002"}),showOnHomepage:!0},{name:"\u7b2c\u4e03\u7ae0\u3000\u82f1\u96c4\u8bc6\u82f1\u96c4",content:(0,s.jsx)(s.Fragment,{children:"\u7247\u523b\uff0c\u53f6\u8001\u4e8c\u5c31\u56de\u6765\u4e86\uff0c\u5e26\u7740\u718a\u501c\u8d70\u5230\u91cc\u9762\uff0c\u7a7f\u8fc7\u8d70\u5eca\uff0c\u4fbf\u5230\u4e86\u90a3\u95ee\u4e66\u623f\u3002 \u3000\u3000\u53f6\u8001\u4e8c\u5230\u4e86\u4e66\u623f\u540e\u8bf4\u9053\uff1a\u201c\u4f60\u6211\u81ea\u5df1\u5f1f\u5144\uff0c\u4e5f\u4e0d\u8981\u518d\u5ba2\u6c14\u3002\u9700\u8981\u4ec0\u4e48\uff0c\u7b49\u4f1a\u6211\u53eb\u4e00\u4e2a\u5c0f \u7ae5\u7ad9\u5728\u95e8\u53e3\uff0c\u4f60\u5c31\u5bf9\u4ed6\u8bf4\u597d\u4e86\uff0c\u718a\u5144\u6b64\u523b\u770b\u770b\u590f\u5973\u4fa0\u7684\u4f24\u52bf\uff0c\u7136\u540e\u518d\u5230\u524d\u9762\u6765\u8c08\u8c08\u3002\u201d \u3000\u3000\u718a\u501c\u68c0\u67e5\u4e86\u590f\u82b8\u7684\u4f24\u52bf\uff0c\u7528\u5185\u529b\u628a\u5979\u4f24\u52bf\u6b62\u4f4f\uff0c\u7136\u540e\u8d70\u51fa\u623f\u5916\u3002 \u3000\u3000\u4ed6\u4fbf\u8d70\u5230\u524d\u623f\uff0c\u770b\u89c1\u53f6\u5bb6\u5144\u5f1f\u4ee5\u53ca\u9a6c\u6c0f\u5144\u5f1f\uff0c\u5c1a\u672a\u660e\u7b49\u4eba\uff0c\u6b63\u56f4\u5750\u5728\u4e00\u5f20\u516b\u4ed9\u684c\u5b50\u56db \u5468\uff0c\u4ed6\u8d70\u5230\u524d\u9762\uff0c\u53c8\u662f\u4e00\u60ca\uff0c\u90a3\u5f20\u5f88\u5927\u7684\u516b\u4ed9\u684c\u4e0a\uff0c\u7adf\u5bc6\u5bc6\u6ee1\u6ee1\u5730\u653e\u4e86\u4e00\u684c\u5b50\u4eba\u5934\u3002 \u3000\u3000\u53f6\u8001\u5927\u770b\u89c1\u718a\u501c\u7684\u795e\u8272\uff0c\u54c8\u54c8\u5927\u7b11\uff1a\u201c\u4eca\u65e5\u4f60\u6211\u5f1f\u5144\u6b22\u805a\uff0c\u5b9e\u5e94\u75db\u996e\u4e09\u676f\u3002\u201d\u4ed6\u4e00\u4e3e\u53f3 \u624b\uff0c\u624b\u4e2d\u7adf\u62ff\u7740\u6ee1\u6ee1\u5730\u4e00\u5de8\u676f\u9152\uff0c\u53c8\u9053\uff1a\u201c\u6765\u6765\u6765\uff0c\u8fd9\u4e9b\u4e71\u81e3\u8d3c\u5b50\u7684\u5934\u9885\uff0c\u4e0d\u6b63\u662f\u4f60\u6211\u7684\u5927 \u597d\u4e0b\u9152\u4e4b\u7269\uff0c\u8001\u4e09\uff0c\u5feb\u66ff\u718a\u5144\u4e5f\u659f\u6ee1\u4e00\u676f\u3002\u201d \u3000\u3000\u718a\u501c\u62a2\u6b65\u8fc7\u53bb\uff0c\u63a5\u8fc7\u53f6\u8001\u4e09\u9012\u6765\u7684\u5de8\u89e5\uff0c\u4ef0\u5934\u4e00\u996e\u800c\u5e72\uff0c\u6717\u58f0\u7b11\u9053\uff1a\u201c\u53e4\u4eba\u8d5e\u540d\u82b1\u800c\u996e \u9187\u9152\uff0c\u54ea\u53ca\u5f97\u4e0a\u6211\u4eec\u8d5e\u5934\u9885\u800c\u996e\u70c8\u9152\uff0c\u6765\u6765\uff0c\u53f6\u5144\u518d\u7ed9\u6211\u4e00\u676f\uff0c\u5c0f\u5f1f\u9152\u91cf\u867d\u6d45\uff0c\u4eca\u65e5\u4e5f\u8981\u559d \u4e2a\u75db\u5feb\u3002\u201d \u3000\u3000\u5c1a\u672a\u660e\u9f13\u638c\u7b11\u9053\uff1a\u201c\u718a\u5144\u679c\u7136\u662f\u4e2a\u771f\u6b63\u7684\u82f1\u8c6a\u4e4b\u4e0a\uff0c\u6211\u5c1a\u672a\u660e\u5f97\u53cb\u5982\u6b64\uff0c\u592b\u590d\u4f55\u61be\uff0c\u4eca \u65e5\u4f60\u6211\u540c\u996e\u6b64\u9152\uff0c\u4ed6\u65e5\u5fc5\u5b9a\u751f\u6b7b\u5171\u4e4b\u3002\u201d \u3000\u3000\u53f6\u8001\u5927\u731b\u5730\u5c06\u624b\u4e2d\u9152\u676f\u7830\u5728\u671d\u684c\u4e0a\u4e00\u653e\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4f4d\u4ff1\u662f\u6b66\u6797\u4e2d\u6570\u4e00\u6570\u4e8c\u7684\u5c11\u5e74 \u82f1\u96c4\uff0c\u96be\u5f97\u662f\u53c8\u90fd\u610f\u5fd7\u76f8\u6295\uff0c\u4f9d\u6211\u4e4b\u610f\uff0c\u4f55\u4e0d\u5c31\u6b64\u62dc\u4e3a\u5144\u5f1f\uff0c\u90a3\u6211\u4eec\u4eca\u65e5\u4e4b\u4e8b\u5c31\u66f4\u662f\u5927\u5927\u7684 \u5feb\u4e8b\u4e86\u3002\u201d \u3000\u3000\u718a\u501c\u9996\u5148\u540c\u610f\uff0c\u5c1a\u672a\u660e\u4e5f\u81ea\u8d5e\u6210\uff0c\u4e24\u4eba\u4e00\u53d9\u5e74\u9f84\uff0c\u718a\u501c\u6bd4\u5c1a\u672a\u660e\u5927\u4e86\u4e00\u5c81\uff0c\u4e24\u4eba\u4e5f\u6ca1\u6709 \u4ec0\u4e48\u9999\u70db\uff0c\u5373\u5e2d\u5c31\u7ed3\u6210\u5144\u5f1f\u4e86\u3002 \u3000\u3000\u53f6\u8001\u5927\u7a81\u7136\u95ee\u718a\u501c\u9053\uff1a\u201c\u718a\u5144\u5f1f\uff0c\u4f60\u6211\u867d\u7136\u76f8\u77e5\u4e0d\u6df1\uff0c\u4f60\u751a\u81f3\u8fde\u6211\u5f1f\u5144\u53eb\u4ec0\u4e48\u540d\u5b57\u90fd\u4e0d \u77e5\u9053\uff0c\u4f46\u4f60\u6211\u4e00\u89c1\u6295\u7f18\uff0c\u6211\u53f6\u67d0\u867d\u7136\u4e0d\u624d\uff0c\u5374\u770b\u5f97\u51fa\u5144\u5f1f\u4f60\u662f\u4e2a\u4e86\u4e0d\u8d77\u7684\u4eba\u7269\u3002\u201d \u3000\u3000\u4ed6\u5c06\u676f\u4e2d\u7684\u9152\uff0c\u4e00\u996e\u800c\u5c3d\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u7792\u4f60\u8bf4\uff0c\u6211\u5f1f\u5144\u54ea\u91cc\u662f\u4ec0\u4e48\u5546\u4eba\uff0c\u5176\u5b9e\u8fd9\u70b9\u4e0d\u7528 \u6211\u8bf4\uff0c\u4f60\u4e5f\u65e9\u77e5\u9053\u4e86\uff0c\u6211\u5f1f\u5144\u773c\u770b\u7740\u6ee1\u5974\u4e00\u5929\u6bd4\u4e00\u5929\u66f4\u751a\u6b3a\u51cc\u7740\u6211\u4eec\u708e\u9ec4\u5b50\u5b59\uff0c\u4f46\u53cd\u6e05\u590d\u660e \u7684\u82f1\u4fa0\uff0c\u5374\u4e00\u5929\u5c11\u4f3c\u4e00\u5929\uff0c\u5c31\u8fde\u5f53\u65e5\u540d\u503e\u671d\u91ce\u7684\u6c5f\u5357\u516b\u4fa0\uff0c\u73b0\u5728\u90fd\u5df2\u98ce\u6d88\u4e91\u6563\u4e86\uff0c\u9664\u4e86\u542c\u8bf4 \u6c5f\u5357\u5927\u4fa0\u7518\u51e4\u6c60\uff0c\u548c\u5415\u56db\u5a18\u7b49\u5c11\u6570\u4eba\u5c1a\u5728\u4eba\u95f4\u5916\uff0c\u5176\u4f59\u7684\u6015\u90fd\u5df2\u906d\u4e86\u6bd2\u624b\u3002\u201d \u3000\u3000\u4ed6\u4e00\u62cd\u684c\u5b50\uff0c\u8c6a\u6c14\u5e72\u4e91\u4ed6\u8bf4\u9053\uff1a\u201c\u6211\u5f1f\u5144\u867d\u7136\u4e0d\u6210\u6750\uff0c\u5374\u89c1\u4e0d\u5f97\u5f02\u65cf\u7684\u7316\u6485\uff0c\u867d\u7136\u8868\u9762 \u4e0a\u662f\u751f\u610f\u4eba\uff0c\u4e0d\u8fc7\u662f\u63a9\u62a4\u6211\u4eec\u8eab\u4efd\u5e4c\u5b50\u7f62\u4e86\uff0c\u6211\u5f1f\u5144\u5904\u601d\u79ef\u8651\uff0c\u5341\u6570\u5e74\uff0c\u5728\u5927\u6c5f\u5357\u5317\uff0c\u4e24\u6cb3 \u4e24\u5cb8\uff0c\u4e5f\u7ed3\u4ea4\u4e86\u4e0d\u5c11\u5fd7\u540c\u9053\u5408\u7684\u597d\u6c49\uff0c\u5f53\u7136\u6211\u4e5f\u77e5\u9053\uff0c\u51ed\u6211\u7b49\u4e09\u3001\u4e94\u4e07\u4eba\uff0c\u8981\u60f3\u63a8\u7ffb\u6ee1\u6e05\u504c \u5927\u7684\u57fa\u4e1a\uff0c\u662f\u4e07\u4e0d\u53ef\u80fd\uff0c\u4f46\u6211\u603b\u4e0d\u8ba9\u90a3\u4e9b\u5974\u624d\u8fc7\u5f97\u79f0\u5fc3\u5c31\u662f\u4e86\u3002\u201d \u3000\u3000\u4ed6\u4e00\u6307\u684c\u4e0a\u7684\u4eba\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u5934\uff0c\u4e0d\u662f\u5265\u524a\u826f\u6c11\u7684\u6ee1\u5974\uff0c\u4fbf\u662f\u5168\u5143\u6c14\u8282\u7684\u6c49\u5978\uff0c \u8fd9\u4e9b\u4eba\u867d\u7136\u6740\u4e4b\u4e0d\u5b8c\uff0c\u4f46\u6211\u4eec\u80fd\u6740\u4e00\u4e2a\uff0c\u5c31\u6740\u4e00\u4e2a\uff0c\u8fd9\u4e9b\u91d1\u94b1\uff0c\u662f\u4ed6\u4eec\u53d6\u4e4b\u4e8e\u6c11\u7684\uff0c\u6211\u4eec\u5c31 \u8981\u7528\u4e4b\u4e8e\u6c11\uff0c\u718a\u5144\u5f1f\uff0c\u4f60\u5982\u6b64\u4e00\u8eab\u7edd\u827a\uff0c\u603b\u4e0d\u80fd\u5c31\u6b64\u6e6e\u6ca1\u4e86\u5427\uff0c\u4e0d\u505a\u4e9b\u9876\u5929\u7acb\u5730\u7684\u4e8b\uff0c\u5c82\u4e0d \u662f\u5728\u6ca1\u4e00\u751f\u3002\u201d \u3000\u3000\u4ed6\u7ad9\u8d77\u6765\u5411\u718a\u501c\u6df1\u6df1\u4e00\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u82e5\u6709\u5fd7\u4e8e\u6b64\uff0c\u4f60\u6211\u5144\u5f1f\u4e0d\u59a8\u4e00\u8d77\u505a\u4e00\u756a\u8f70\u8f70\u70c8\u70c8 \u7684\u4e8b\u6765\uff0c\u6211\u53f6\u8001\u5927\u611f\u6fc0\u4e0d\u5c3d\u3002\u201d \u3000\u3000\u8fd9\u756a\u8bdd\u5c06\u718a\u501c\u8bf4\u5f97\u8840\u8109\u8d23\u5f20\uff0c\u96c4\u5fd7\u8c6a\u98de\uff0c\u8fde\u5fd9\u4e00\u628a\u62c9\u4f4f\u53f6\u8001\u5927\u7684\u81c2\u8180\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c \u4ece\u4eca\u65e5\u8d77\uff0c\u6211\u718a\u501c\u5c31\u662f\u5927\u54e5\u624b\u4e0b\u7684\u5f1f\u5144\uff0c\u5927\u54e5\u6709\u7528\u5f97\u7740\u6211\u7684\u5730\u65b9\uff0c\u6211\u718a\u501c\u4e07\u6b7b\u4e0d\u8f9e\u3002\u201d \u3000\u3000\u6b63\u662f\u201c\u9152\u9022\u77e5\u5df1\u5343\u676f\u5c11\u201d\uff0c\u4ed6\u4eec\u6108\u8c08\u6108\u6b22\uff0c\u53f6\u8001\u5927\u6536\u8d77\u4eba\u5934\uff0c\u6362\u4e0a\u9152\u83dc\uff0c\u8bf8\u4eba\u8c6a\u6c14\u9038 \u98de\uff0c\u5929\u5357\u5730\u5317\u65e0\u6240\u4e0d\u8c08\u3002 \u3000\u3000\u718a\u501c\u7b2c\u4e00\u6b21\u4ea4\u7ed3\u5230\u771f\u6b63\u610f\u5fd7\u76f8\u6295\u7684\u670b\u53cb\uff0c\u591a\u65e5\u90c1\u79ef\u5728\u5fc3\u4e2d\u7684\u5fc3\u4e8b\uff0c\u90fd\u4e00\u4e00\u53d1\u6cc4\u4e86\u51fa\u6765\uff0c \u8c08\u53ca\u81ea\u5df1\u7684\u8eab\u4e16\uff0c\u4f17\u4eba\u90fd\u557c\u5618\u4e0d\u5df2\u3002 \u3000\u3000\u5c1a\u672a\u660e\u8fde\u5e72\u51e0\u676f\u9152\uff0c\u53f9\u9053\uff1a\u201c\u8bf4\u8d77\u6765\uff0c\u6211\u7684\u8eab\u4e16\u6bd4\u5927\u54e5\u66f4\u60e8\u3002\u201d \u3000\u3000\u53f6\u8001\u5927\u8bf4\u9053\uff1a\u201c\u5c1a\u8001\u5f1f\u7684\u8eab\u4e16\uff0c\u5230\u4eca\u65e5\u5728\u6b66\u6797\u4e2d\u8fd8\u662f\u4e2a\u8c1c\uff0c\u4eca\u5929\u6211\u4eec\u521d\u9022\u77e5\u5df1\uff0c\u5c1a\u8001\u5f1f \u53c8\u7ed3\u4e86\u4e2a\u5f02\u59d3\u9aa8\u8089\uff0c\u603b\u8be5\u5c06\u8eab\u4e16\u8bf4\u7ed9\u6211\u4eec\u542c\u542c\u5427\u3002\u201d \u3000\u3000\u5c1a\u672a\u660e\u5495\u5730\u53c8\u5e72\u4e86\u4e00\u676f\u9152\u9053\uff1a\u201c\u5176\u5b9e\u8fde\u6211\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u81ea\u5df1\u7684\u8eab\u4e16\uff0c\u6211\u53ea\u77e5\u9053\u5728\u6211\u6781\u5e7c \u7684\u65f6\u5019\uff0c\u5c31\u88ab\u4eba\u4ece\u5bb6\u4e2d\u5e26\u51fa\u6765\u4e86\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u5374\u53c8\u628a\u6211\u629b\u5728\u4e00\u4e2a\u8352\u6797\u91cc\uff0c\u540e\u6765\u6211\u624d\u542c\u5148\u5e08\u8bf4 \u90a3\u5730\u65b9\u53eb\u5c0f\u7ea2\u95e8\u6751\uff0c\u662f\u5317\u5e73\u57ce\u90ca\u4e00\u4e2a\u8352\u6797\uff0c\u5148\u5e08\u672c\u662f\u897f\u57ce\u7684\u4e00\u4e2a\u6e38\u65b9\u50e7\u4eba\uff0c\u90a3\u5929\u51d1\u5de7\u5728\u5c0f\u7ea2 \u95e8\u6751\u7684\u7ea2\u95e8\u5bfa\u6302\u5355\uff0c\u542c\u5230\u6709\u5c0f\u5b69\u7684\u54ed\u58f0\uff0c\u89c1\u6211\u5b64\u8eab\u4e00\u4eba\uff0c\u5c31\u5c06\u6211\u6536\u7559\u4e86\uff0c\u5148\u5e08\u5c06\u4ed6\u4e00\u8eab\u7edd \u827a\uff0c\u90fd\u4f20\u7ed9\u4e86\u6211\uff0c\u5374\u59cb\u7ec8\u4e0d\u8bb8\u843d\u53d1\u4e3a\u4ed6\u7684\u5f1f\u5b50\uff0c\u5148\u5e08\u603b\u8bf4\u6211\u8eab\u4e16\u4e0d\u51e1\uff0c\u4f46\u662f\u7a76\u7adf\u5982\u4f55\uff0c\u5374\u53c8 \u4e0d\u80af\u544a\u8bc9\u6211\uff0c\u53ea\u53eb\u6211\u597d\u597d\u7ec3\u529f\u592b\uff0c\u5c06\u6765\u603b\u6709\u6c34\u843d\u77f3\u51fa\u7684\u4e00\u5929\u3002\u201d \u3000\u3000\u8bf4\u81f3\u6b64\u65f6\uff0c\u4ed6\u53cc\u76ee\u4e2d\u9eef\u7136\u7adf\u6709\u6cea\u5149\uff0c\u4e00\u4e3e\u676f\uff0c\u53c8\u5e72\u4e86\u4e00\u676f\u9152\u3002 \u3000\u3000\u5ea7\u4e2d\u4f17\u4eba\u4ff1\u90fd\u51dd\u795e\u542c\u4ed6\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u53ef\u662f\u6ca1\u7b49\u5230\u90a3\u4e00\u5929\uff0c\u5148\u5e08\u5c31\u6b7b\u4e86\u3002\u4e34\u6b7b\u7684\u65f6\u5019\u544a\u8bc9 \u6211\uff0c\u8981\u6211\u7ec8\u751f\u4e3a\u53cd\u6e05\u6548\u547d\uff0c\u4e8e\u662f\u6211\u5c31\u7528\u5148\u5e08\u66ff\u6211\u8d77\u7684\u540d\u5b57\uff0c\u95ef\u8361\u6c5f\u6e56\uff0c\u54ea\u77e5\u673a\u7f18\u51d1\u5de7\uff0c\u521d\u51fa \u9053\u4fbf\u505a\u4e86\u4e24\u6cb3\u7eff\u6797\u7684\u603b\u74e2\u628a\u5b50\uff0c\u6211\u867d\u4e0d\u613f\u7f6e\u8eab\u7eff\u6797\uff0c\u4f46\u5fc3\u4e2d\u5374\u8bb0\u7740\u5148\u5e08\u7684\u9057\u547d\uff0c\u60f3\u5c06\u4e24\u6cb3\u7684 \u8c6a\u6770\u805a\u6210\u4e00\u80a1\u53cd\u6e05\u7684\u529b\u91cf\uff0c\u53ef\u662f\u5230\u73b0\u5728\u4e3a\u6b62\uff0c\u6211\u8fde\u81ea\u5df1\u7684\u4eb2\u751f\u7236\u6bcd\u90fd\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u4ed6\u8fd9\u756a\u8bdd\uff0c\u76f4\u8bf4\u5f97\u6ee1\u5ea7\u4ff1\u90fd\u9eef\u7136\uff0c\u5c24\u5176\u662f\u8eab\u4e16\u76f8\u540c\u7684\u718a\u501c\uff0c\u542c\u4e86\u66f4\u662f\u96be\u53d7\u3002 \u3000\u3000\u53f6\u8001\u5927\u731b\u5730\u51fb\u9ad8\u6b4c\u9053\uff1a\u201c\u83ab\u7b49\u95ed\uff0c\u767d\u4e86\u5c11\u5e74\u5934\uff0c\u7a7a\u60b2\u5207\u3002\u201d \u3000\u3000\u6b4c\u58f0\u6b47\u5904\uff0c\u53f6\u8001\u5927\u4e3e\u676f\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u597d\u7537\u513f\u80f8\u6000\u5927\u5fd7\uff0c\u718a\u5144\u5f1f\uff0c\u5c1a\u5144\u5f1f\uff0c\u4f60\u4eec\u600e\u4e48\u4e5f\u6548 \u8d77\u5973\u513f\u6001\u6765\u4e86\uff0c\u8be5\u7f5a\u4e00\u676f\u3002\u201d \u3000\u3000\u718a\u501c\u3001\u5c1a\u672a\u660e\u5c06\u9762\u524d\u7684\u9152\u4e00\u996e\u800c\u5c3d\uff0c\u53f6\u8001\u5927\u6717\u58f0\u7b11\u9053\uff1a\u201c\u8fd9\u624d\u5bf9\u4e86\uff0c\u4eca\u671d\u6709\u9152\u4e14\u9189\uff0c\u597d \u7537\u513f\u8be5\u62ff\u7740\u6ee1\u5974\u7684\u5934\u9885\u5f53\u9152\u5668\uff0c\u4ee5\u540e\u518d\u4e5f\u4e0d\u8bb8\u7a7a\u81ea\u611f\u6000\u8eab\u4e16\u3002\u201d \u3000\u3000\u8fd9\u4e00\u987f\u9152\u76f4\u7531\u6e05\u6668\uff0c\u5403\u5230\u508d\u665a\uff0c\u5c1a\u672a\u660e\u65e9\u5df2\u7389\u5c71\u9893\u5012\uff0c\u718a\u501c\u4e5f\u662f\u660f\u7136\u6b32\u7761\u4e86\u3002 \u3000\u3000\u4ed6\u6643\u8d70\u56de\u4e66\u623f\uff0c\u590f\u82b8\u6b63\u561f\u7740\u5634\u5728\u7b49\u4ed6\uff0c\u4e00\u770b\u89c1\u4ed6\u4fbf\u5a07\u55d4\u9053\uff1a\u201c\u4f60\u770b\u4f60\uff0c\u559d\u6210\u8fd9\u4e2a\u6837\u5b50\uff0c \u628a\u6211\u4e22\u5728\u8fd9\u513f\u4e5f\u4e0d\u7ba1\u3002\u201d \u3000\u3000\u718a\u501c\u6b64\u523b\u8111\u4e2d\u5df2\u662f\u4e0d\u6e05\uff1b\u53ea\u7ba1\u7b11\u3002 \u3000\u3000\u590f\u82b8\u53c8\u55d4\u9053\uff1a\u201c\u5feb\u53bb\u7761\u5427\uff0c\u4f60\u77a7\u4f60\u8fd9\u6837\u5b50\uff0c\u6211\u770b\u7740\u90fd\u751f\u6c14\u3002\u201d \u3000\u3000\u718a\u501c\u8fde\u58f0\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u597d\u3002\u201d\u8d70\u5230\u81ea\u5df1\u623f\u4e2d\uff0c\u5e26\u4e0a\u623f\u95e8\uff0c\u4fbf\u7761\u53bb\u4e86\u3002 \u3000\u3000\u4ed6\u4e00\u89c9\u7761\u5f97\u6781\u6c89\uff0c\u7761\u68a6\u4e2d\u5ffd\u5730\u6709\u4eba\u5427\u3001\u5427\u6253\u4e86\u81ea\u5df1\u51e0\u4e2a\u8033\u5149\uff0c\u7741\u5f00\u773c\u6765\uff0c\u8ff7\u7cca\u4e2d\u770b\u5230\u4e00 \u6761\u4eba\u5f71\u7ad9\u5728\u5e8a\u524d\u3002 \u3000\u3000\u718a\u501c\u987f\u89c9\u5f97\u6d51\u8eab\u7684\u6839\u6839\u6c57\u6bdb\uff0c\u90fd\u5bd2\u6035\u8d77\u6765\uff0c\u60ca\u5f97\u8179\u4e2d\u7684\u9152\u90fd\u5316\u505a\u4e86\u51b7\u6c57\u3002 \u3000\u3000\u90a3\u4eba\u89c1\u718a\u501c\u9192\u6765\uff0c\u51b7\u51b7\u5730\u54fc\u4e86\u4e00\u58f0\uff0c\u56de\u8f6c\u8eab\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6df7\u86cb\uff0c\u8fd8\u4e0d\u8ddf\u6211\u6765\u3002\u201d \u3000\u3000\u8bf4\u9053\u8eab\u5f62\u4e00\u95ea\uff0c\u4fbf\u7531\u7a97\u4e2d\u98d8\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u718a\u501c\u672c\u662f\u8fde\u8863\u5367\u5012\uff0c\u6b64\u523b\u8fde\u978b\u5b50\u90fd\u987e\u4e0d\u5f97\u7a7f\uff0c\u53cc\u65f6\u4e00\u652f\u5e8a\u677f\uff0c\u817f\u3001\u8170\u4e00\u9f50\u7528\u529b\uff0c\u81ea\u5e8a\u4e0a \u98de\u8eab\u800c\u51fa\uff0c\u4f46\u4ed6\u7a7a\u81ea\u65bd\u51fa\u201c\u6f5c\u5f62\u9041\u5f71\u201d\u7684\u7edd\u9876\u8f7b\u529f\uff0c\u5374\u59cb\u7ec8\u65e0\u6cd5\u8ffd\u4e0a\u90a3\u4eba\u3002 \u3000\u3000\u4e00\u6643\u773c\u4e4b\u95f4\uff0c\u5230\u4e86\u57ce\u90ca\u7684\u7530\u91ce\u4e0a\uff0c\u6b64\u65f6\u4e07\u7c41\u4ff1\u5bc2\uff0c\u5fae\u98ce\u8d77\u5904\uff0c\u5439\u7740\u90a3\u4eba\u7eaf\u81ea\u7684\u8863\u886b\uff0c\u671b \u4e4b\u76f4\u5982\u9b3c\u9b45\u3002 \u3000\u3000\u718a\u501c\u731b\u5730\u60f3\u8d77\u4e00\u4eba\uff0c\u4ed6\u770b\u89c1\u90a3\u4eba\u6d51\u767d\u8272\u7684\u957f\u886b\uff0c\u968f\u98ce\u800c\u52a8\uff0c\u6ee1\u5934\u94f6\u767d\u8272\u7684\u5934\u53d1\uff0c\u76f4\u5782\u5230 \u80a9\u4e0a\uff0c\u66f4\u8bc1\u5b9e\u4e86\u81ea\u5df1\u7684\u60f3\u6cd5\u3002 \u3000\u3000\u718a\u501c\u5148\u524d\u6ee1\u8154\u7684\u60ca\u60b8\u548c\u6124\u6012\uff0c\u6b64\u523b\u987f\u7136\u5316\u4e3a\u4e4c\u6709\uff0c\u90a3\u4eba\u505c\u4e0b\u8eab\u5f62\u4e4b\u540e\uff0c\u4ecd\u7136\u80cc\u5411\u7740\u4ed6\uff0c \u6ca1\u6709\u8f6c\u56de\u8eab\u6765\u671b\u4e00\u773c\u3002 \u3000\u3000\u718a\u501c\u5446\u4e86\u4e00\u4f1a\uff0c\u6574\u4e86\u6574\u8863\u88f3\uff0c\u518d\u4e5f\u4e0d\u6562\u65bd\u5c55\u8eab\u6cd5\uff0c\u606d\u606d\u656c\u656c\u5730\u7ed5\u5230\u90a3\u4eba\u8eab\u524d\uff0c\u6084\u6084\u4e00 \u671b\uff0c\u89c1\u90a3\u4eba\u767d\u53d1\uff0c\u767d\u7709\uff0c\u8138\u8272\u5982\u971c\uff0c\u679c\u7136\u662f\u4e00\u522b\u591a\u5e74\u7684\u6bd2\u5fc3\u795e\u9b54\u4faf\u751f\uff0c\u8fde\u5fd9\u8dea\u4e0b\u53bb\uff0c\u53e9\u4e86\u4e00 \u4e2a\u5934\uff0c\u60f6\u6050\u4ed6\u8bf4\uff1a\u201c\u5e08\u7236\u8fd9\u4e00\u5411\u53ef\u597d\uff0c\u5f1f\u5b50\u8fd9\u91cc\u62dc\u89c1\u5e08\u7236\u3002\u201d \u3000\u3000\u6bd2\u5fc3\u795e\u9b54\u9f3b\u5b54\u91cc\u51b7\u54fc\u4e00\u58f0\uff0c\u6012\u9053\uff1a\u201c\u755c\u7272\uff0c\u8c01\u662f\u4f60\u7684\u5e08\u7236\u3002\u201d \u3000\u3000\u4ed6\u795e\u8272\u51b7\u5cfb\u5df2\u6781\uff0c\u58f0\u97f3\u66f4\u662f\u51b0\u51b7\uff0c\u718a\u501c\u5934\u4e5f\u4e0d\u6562\u62ac\uff0c\u4ecd\u7136\u8dea\u5728\u5730\u4e0a\u3002 \u3000\u3000\u6bd2\u5fc3\u795e\u9b54\u51b7\u7136\u53c8\u9053\uff1a\u201c\u4f60\u53ef\u522b\u8dea\u5728\u5730\u4e0a\uff0c\u6211\u53ef\u62c5\u5f53\u4e0d\u8d77\uff0c\u6211\u53ef\u53d7\u4e0d\u4e86\u540d\u4f20\u6c5f\u6e56\u7684\u4e09\u79c0\uff0c \u5929\u4e0b\u7b2c\u4e00\u5947\u4eba\u98d8\u7136\u81fe\u9ad8\u8db3\u8fd9\u6837\u7684\u5927\u793c\u3002\u201d \u3000\u3000\u718a\u501c\u77e5\u9053\u4faf\u751f\u5df2\u7136\u52a8\u6012\uff0c\u66f4\u4e0d\u6562\u7b54\u8154\uff0c\u4ecd\u8001\u8001\u5b9e\u5b9e\u5730\u8dea\u5728\u5730\u4e0a\u3002 \u3000\u3000\u6bd2\u5fc3\u795e\u9b54\u867d\u7136\u4ecd\u65e0\u8868\u60c5\uff0c\u4f46\u76ee\u5149\u5df2\u4e0d\u4f3c\u65b9\u624d\u7684\u4e25\u5cfb\uff0c\u8bf4\u9053\uff1a\u201c\u8d77\u6765\uff0c\u8d77\u6765\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c \u4f60\u5df2\u7ecf\u6210\u4e86\u6709\u540d\u7684\u597d\u6c49\uff0c\u628a\u6211\u7684\u8bdd\u65e9\u5df2\u5fd8\u5230\u4e5d\u9704\u4e91\u5916\u4e86\uff0c\u65e2\u4e0d\u5230\u5173\u5916\u6765\u627e\u6211\uff0c\u628a\u6211\u9001\u4f60\u7684 \u5251\uff0c\u4e5f\u4e22\u5230\u4e0d\u77e5\u54ea\u91cc\u53bb\u4e86\uff0c\u60f3\u5fc5\u662f\u4f60\u7684\u6b66\u529f\u5df2\u7ecf\u9ad8\u51fa\u6211\u751a\u591a\uff0c\u518d\u4e5f\u7528\u4e0d\u7740\u6211\u6559\u4f60\u4e86\u3002\u201d \u3000\u3000\u4ed6\u987f\u4e00\u987f\uff0c\u53c8\u8bf4\u9053\uff1a\u201c\u53ef\u662f\u6211\u5929\u751f\u7684\u602a\u813e\u6c14\uff0c\u5012\u8981\u770b\u770b\u4f60\u5728\u5929\u4e0b\u7b2c\u4e00\u5947\u4eba\u90a3\u91cc\u5b66\u4e86\u4e00\u4e9b \u4ec0\u4e48\u8d85\u51e1\u5165\u5723\u7684\u672c\u4e8b\uff0c\u6765\uff0c\u6765\uff0c\u5feb\u7ad9\u8d77\u6765\uff0c\u628a\u4f60\u90a3\u4e9b\u672c\u4e8b\u638f\u51fa\u6765\uff0c\u548c\u6211\u6bd4\u5212\uff0c\u6bd4\u5212\u3002\u201d \u3000\u3000\u718a\u501c\u9053\uff1a\u201c\u5f1f\u5b50\u4e0d\u6562\u3002\u201d \u3000\u3000\u6bd2\u5fc3\u795e\u9b54\u9053\uff1a\u201c\u4ec0\u4e48\u6562\u4e0d\u6562\u7684\uff0c\u4f60\u8fde\u6211\u7684\u8bdd\u90fd\u6562\u4e0d\u542c\u4e86\uff1f\u201d \u3000\u3000\u718a\u501c\u5fc3\u4e2d\u5b9e\u662f\u96be\u53d7\u5df2\u6781\uff0c\u4ed6\u4e5f\u5728\u8d23\u602a\u7740\u81ea\u5df1\uff0c\u59d4\u5b9e\u5bf9\u4e0d\u4f4f\u8fd9\u7b2c\u4e00\u4e2a\u5bf9\u4ed6\u6709\u6069\u7684\u4eba\uff0c\u5f53\u7136 \u4ed6\u66f4\u4e0d\u6562\u548c\u6bd2\u5fc3\u795e\u9b54\u6bd4\u5212\uff0c\u4f46\u662f\u4ed6\u5374\u77e5\u9053\u6bd2\u5fc3\u795e\u9b54\u5411\u6765\u884c\u4e8b\u5947\u602a\uff0c\u8bf4\u51fa\u6765\u7684\u8bdd\u66f4\u4e0d\u8bb8\u522b\u4eba\u66f4 \u6539\u7684\u3002 \u3000\u3000\u4ed6\u4e3a\u96be\u5730\u62ac\u8d77\u5934\u6765\uff0c\u5077\u5077\u5730\u671b\u4e86\u4faf\u751f\u4e00\u773c\uff0c\u89c1\u4faf\u751f\u773c\u4e2d\u6d41\u9732\u7684\u76ee\u5149\uff0c\u5e76\u4e0d\u662f\u4ed6\u6240\u60f3\u8c61\u7684 \u6124\u6012\uff0c\u800c\u51e0\u4e4e\u662f\u5f53\u5e74\u5728\u4e3a\u4ed6\u6253\u901a\u201c\u7763\u201d\u201c\u4efb\u201d\u4e24\u8109\u65f6\u90a3\u6837\u7684\u6148\u7231\uff0c\u718a\u501c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6697\u5fd6\u9053\uff1a \u201c\u5e08\u7236\u4e00\u5411\u5bf9\u6211\u6781\u597d\uff0c\u83ab\u4e0d\u662f\u4ed6\u5728\u501f\u6bd4\u6b66\u8003\u9a8c\u6211\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6bd2\u5fc3\u795e\u9b54\u89c1\u718a\u501c\u4ecd\u8dea\u5728\u90a3\u91cc\u4e0d\u52a8\uff0c\u5475\u53f1\u9053\uff1a\u201c\u6211\u7684\u8bdd\u4f60\u542c\u89c1\u6ca1\u6709\uff1f\u201d \u3000\u3000\u718a\u501c\u606d\u656c\u5730\u9053\uff1a\u201c\u5f1f\u5b50\u542c\u89c1\u4e86\uff01\u53ea\u662f\u4e00\u4e00\u201d\u4faf\u751f\u9053\uff1a\u201c\u6ca1\u6709\u4ec0\u4e48\u53ea\u662f\u4e0d\u53ea\u662f\u7684\uff0c\u5feb\u7ad9\u8d77 \u6765\u548c\u6211\u52a8\u624b\u3002\u201d \u3000\u3000\u718a\u501c\u65e0\u6cd5\uff0c\u53ea\u5f97\u7f13\u7f13\u7ad9\u4e86\u8d77\u6765\uff0c\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u5f1f\u5b50\u542c\u4ece\u5e08\u7236\u7684\u5429\u5490\u3002\u201d \u3000\u3000\u4ed6\u8fd8\u6ca1\u6709\u5b8c\u5168\u7ad9\u76f4\u8eab\u8eaf\uff0c\u4faf\u751f\u5df2\u4e00\u638c\u62cd\u6765\uff0c\u5feb\u5230\u8eab\u4e0a\u7684\u65f6\u5019\uff0c\u5ffd\u53c8\u6539\u62cd\u4e3a\u6325\uff0c\u624b\u638c\u4e00 \u53cd\uff0c\u4ee5\u624b\u659c\u659c\u62cd\u4e0b\uff0c\u90a3\u5de6\u638c\u5374\u540e\u53d1\u5148\u5e76\uff0c\u6025\u901f\u5730\u6325\u5411\u718a\u501c\u9762\u95e8\uff0c\u8fd9\u4e00\u62db\u201c\u626d\u8f6c\u9634\u9633\u201d\u770b\u4f3c\u8f7b \u6613\uff0c\u5a01\u529b\u5374\u975e\u540c\u5c0f\u53ef\uff0c\u718a\u501c\u7109\u6709\u4e0d\u8bc6\u5389\u5bb3\u4e4b\u7406\u3002 \u3000\u3000\u718a\u501c\u4e0d\u6562\u76f4\u63a5\u53bb\u907f\u6b64\u62db\uff0c\u4ed6\u811a\u4e0b\u6025\u8fd9\u8e0f\u7740\u4e94\u884c\u65b9\u4f4d\uff0c\u4fa7\u8eab\u907f\u5f00\u6b64\u62db\u540e\uff0c\u53c8\u5de7\u5999\u5730\u6643\u52a8\u81ea \u5df1\u7684\u8eab\u8eaf\uff0c\u4ee5\u671f\u6270\u4e71\u5bf9\u65b9\u7684\u76ee\u5149\u3002 \u3000\u3000\u6bd2\u5fc3\u795e\u9b54\u4e00\u638c\u843d\u7a7a\uff0c\u53cc\u638c\u6325\u5904\uff0c\u968f\u5373\u53d1\u51fa\u4e09\u62db\u201c\u8ffd\u9b42\u7d22\u547d\u201d\uff0c\u201c\u540d\u767b\u9b3c\u5f55\u201d\uff0c\u201c\u5341\u6bbf\u6e38 \u6208\u201d\uff0c\u4ed6\u51fa\u624b\u5982\u98ce\uff0c\u718a\u501c\u53ea\u89c9\u5f97\u50cf\u662f\u5341\u4f59\u53cc\u624b\u638c\u4e00\u9f50\u5411\u4ed6\u62cd\u6765\uff0c\u4f46\u718a\u501c\u773c\u5149\u52a8\u5904\uff0c\u5374\u53d1\u89c9\u4e00 \u5b97\u5947\u4e8b\u3002 \u3000\u3000\u539f\u6765\u6bd2\u5fc3\u795e\u9b54\u7684\u638c\u5f71\uff0c\u867d\u5982\u6f2b\u5929\u82b1\u96e8\uff0c\u4f46\u5728\u638c\u5f71\u4e0e\u638c\u5f71\u4e4b\u95f4\uff0c\u5374\u6709\u4e00\u6761\u7a7a\u9699\uff0c\u9ad8\u624b\u51fa \u62db\uff0c\u5ff5\u52a8\u5373\u53d1\uff0c\u718a\u501c\u968f\u624b\u4e00\u638c\uff0c\u5411\u7a7a\u9699\u62cd\u53bb\uff0c\u800c\u4e14\u90e8\u4f4d\u5999\u5230\u6beb\u98a0\uff0c\u6b63\u653b\u5230\u6bd2\u5fc3\u795e\u9b54\u7684\u5fc5\u6551\u4e4b \u5904\u3002 \u3000\u3000\u718a\u501c\u4e00\u638c\u62cd\u51fa\uff0c\u624d\u604d\u53d1\u73b0\u51fa\u62db\u6b63\u662f\u6bd2\u5fc3\u795e\u9b54\u6570\u5e74\u524d\u6240\u6388\u81ea\u5df1\u7684\u5341\u6570\u5f0f\u5947\u602a\u7684\u5251\u5f0f\u4e4b\u4e00\uff0c \u4ed6\u8fd9\u624d\u4e86\u89e3\u4e86\u6bd2\u5fc3\u795e\u9b54\u903c\u4ed6\u52a8\u624b\u4e4b\u610f\u3002 \u3000\u3000\u6bd2\u5fc3\u795e\u9b54\u89c1\u4ed6\u8fd9\u638c\u53d1\u51fa\uff0c\u65e0\u8bba\u65f6\u95f4\u3001\u90e8\u4f4d\u3001\u52b2\u529b\uff0c\u90fd\u6070\u5230\u597d\u5904\uff0c\u5634\u89d2\u7ade\u9690\u9690\u6cdb\u51fa\u7b11\u610f\uff0c \u4f46\u8fd9\u7b11\u610f\u4ec5\u5b9b\u82e5\u6f2b\u5929\u51b0\u96ea\u4e2d\u4e00\u4e1d\u706b\u82b1\u800c\u5df2\uff0c\u82e5\u662f\u4e0d\u7559\u5fc3\u7684\u8bdd\uff0c\u662f\u7edd\u5bf9\u96be\u4ee5\u53d1\u89c9\u7684\u3002 \u3000\u3000\u6bd2\u5fc3\u795e\u9b54\u7a81\u5730\u53e3\u4e2d\u53d1\u51fa\u4e00\u4e1d\u4e1d\u5c16\u9510\u7684\u5578\u58f0\uff0c\u638c\u5f71\u5982\u5c71\uff0c\u65bd\u5c55\u51fa\u6c5f\u6e56\u5c11\u89c1\u7684\u201c\u50ac\u9b42\u9634 \u638c\u201d\uff0c\u90a3\u662f\u4e00\u79cd\u6781\u7e41\u590d\u7684\u638c\u5f0f\u548c\u6781\u9634\u67d4\u7684\u638c\u529b\uff0c\u6bcf\u4e00\u62db\u90fd\u5bc6\u5207\u5730\u8fde\u8d2f\u7740\uff0c\u50cf\u662f\u6709\u8bb8\u591a\u624b\u638c\u4e00 \u9f50\u7528\u62db\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u62db\u4e0e\u62db\u4e4b\u95f4\uff0c\u5374\u6c38\u8fdc\u7559\u51fa\u4e00\u6761\u7a7a\u9699\uff0c\u718a\u501c\u773c\u660e\u5fc3\u7075\uff0c\u5f53\u7136\u4e86\u89e3\u4ed6\u7684\u7528\u610f\uff0c\u4e8e\u662f\u6beb \u4e0d\u72b9\u7591\u5730\u8fde\u73af\u4f7f\u51fa\u90a3\u5341\u4f59\u62db\u5947\u5f02\u7684\u5251\u5f0f\u3002 \u3000\u3000\u6e10\u6e10\uff0c\u718a\u501c\u5fc3\u9886\u795e\u4f1a\uff0c\u5df2\u80fd\u5c06\u90a3\u5341\u4f59\u5f0f\u602a\u62db\uff0c\u5bc6\u5207\u7684\u5951\u5408\u4e86\u3002 \u3000\u3000\u4ed6\u8fd9\u624d\u53d1\u73b0\u8fd9\u5341\u4f59\u62db\u5f0f\uff0c\u975e\u4f46\u5185\u4e2d\u7684\u53d8\u5316\u4e0d\u53ef\u601d\u8bae\uff0c\u800c\u4e14\u8fd8\u6709\u4e00\u79cd\u4e13\u7834\u9634\u67d4\u638c\u529b\u7684\u5a01\u529b \u5999\u7528\uff0c\u90a3\u662f\u4efb\u4f55\u638c\u6cd5\u80fd\u65e0\u6240\u4f01\u53ca\u7684\u3002 \u3000\u3000\u6bd2\u5fc3\u795e\u9b54\u5c06\u201c\u50ac\u9b42\u9634\u638c\u201d\u53cd\u590d\u65bd\u5c55\u4e86\u597d\u51e0\u904d\uff0c\u718a\u501c\u4e5f\u5c06\u90a3\u5341\u4f59\u62db\u5f0f\u602a\u62db\u7528\u5f97\u5f97\u5fc3\u5e94\u624b \u4e86\uff0c\u4ed6\u5fc3\u4e2d\u7684\u559c\u60a6\u662f\u4e0d\u53ef\u8a00\u55bb\u7684\u3002 \u3000\u3000\u6bd2\u5fc3\u795e\u9b54\u731b\u4e00\u6536\u62db\uff0c\u98d8\u98d8\u5730\u5c06\u8eab\u632a\u5f00\u4e86\u4e08\u4f59\uff0c\u51b7\u51b7\u5730\u671b\u7740\u718a\u501c\u3002 \u3000\u3000\u718a\u501c\u53c8\u6251\u5730\u8dea\u5728\u5730\u4e0a\uff0c\u4ed6\u662f\u5728\u611f\u6fc0\u7740\u6bd2\u5fc3\u795e\u9b54\u7684\u6089\u5fc3\u6559\u5bfc\u3002 \u3000\u3000\u6bd2\u5fc3\u795e\u9b54\u7684\u9762\u5bb9\u4ecd\u5982\u5e7d\u5c71\u91cc\u7684\u51b0\u5ca9\uff0c\u53ea\u6709\u96ea\u767d\u7684\u987b\u7709\u5728\u591c\u8272\u4e2d\u663e\u5f97\u6709\u5c11\u8bb8\u6e29\u67d4\uff0c\u4ed6\u8bf4 \u9053\uff1a\u201c\u4e8f\u4f60\u8fd8\u8bb0\u5f97\u8fd9\u51e0\u62db\u3002\u201d \u3000\u3000\u718a\u501c\u9053\uff1a\u201c\u5f1f\u5b50\u600e\u4f1a\u5fd8\u8bb0\uff0c\u5c31\u662f\u5e08\u7236\u7684\u6bcf\u4e00\u53e5\u8bdd\uff0c\u5f1f\u5b50\u90fd\u662f\u8bb0\u5728\u5fc3\u91cc\u7684\u3002\u201d \u3000\u3000\u6bd2\u5fc3\u795e\u9b54\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u7684\u8bdd\u4f60\u5fd8\u4e86\u6ca1\u6709\uff0c\u5012\u6ca1\u6709\u4ec0\u4e48\u592a\u5927\u7684\u5173\u7cfb\uff0c\u53ea\u662f\u4f60\u5c06\u6211 \u90a3\u67c4\u501a\u5929\u5251\u4e22\u4e86\uff0c\u5374\u771f\u662f\u8be5\u6b7b\u3002\u201d \u3000\u3000\u718a\u501c\u542c\u4e86\uff0c\u4ece\u80cc\u810a\u5fc3\u5192\u51fa\u4e00\u4e1d\u5bd2\u610f\uff0c\u4ed6\u4e0d\u77e5\u9053\u8be5\u600e\u4e48\u5f25\u8865\u4ed6\u7684\u758f\u5ffd\u3002 \u3000\u3000\u4faf\u751f\u671b\u7740\u718a\u501c\u60f6\u6050\u7684\u795e\u8272\uff0c\u4ed6\u77e5\u9053\u7edd\u4e0d\u662f\u53ef\u4ee5\u4f2a\u5192\u7684\uff0c\u5fc3\u91cc\u4e0d\u7981\u8f6f\u4e86\u8bb8\u591a\uff0c\u8bf4\u9053\uff1a\u201c\u6211 \u5076\u6e38\u592a\u884c\uff0c\u5374\u65e0\u610f\u4e2d\u542c\u5f97\u5929\u9634\u6559\u4e3b\u7126\u5f02\u884c\uff0c\u4ece\u624b\u4e0b\u5904\u5f97\u5230\u67c4\u540d\u5251\uff0c\u5251\u540d\u2018\u501a\u5929\u2019\uff0c\u6211\u8fd8\u4ee5\u4e3a \u4f60\u53ef\u80fd\u906d\u4e86\u5929\u9634\u6559\u7684\u6bd2\u624b\uff0c\u903c\u7740\u5929\u9634\u6559\u91cc\u7684\u4e00\u4e2a\u5c0f\u5934\u76ee\u4e00\u95ee\uff0c\u624d\u77e5\u9053\u90a3\u67c4\u5251\u662f\u6c5f\u82cf\u5206\u8235\u91cc\u7684 \u4e00\u4eba\u5728\u8336\u9986\u4e2d\u62fe\u5f97\u7684\uff0c\u6211\u542c\u4e86\u4e0d\u89c9\u5927\u6012\uff0c\u4f60\u8981\u77e5\u9053\u90a3\u67c4\u5251\u9664\u4e86\u672c\u8eab\u7684\u4ef7\u503c\u4e4b\u5916\uff0c\u91cc\u9762\u8fd8\u5173\u7cfb \u7740\u4e00\u4ef6\u6781\u5927\u7684\u79d8\u5bc6\uff0c\u6570\u5341\u5e74\u524d\uff0c\u6b66\u6797\u5c31\u76db\u4f20\u6b64\u4e8b\uff0c\u6211\u4ed4\u7ec6\u5730\u7814\u7a76\u4e86\u6570\u5341\u5e74\uff0c\u4e5f\u6ca1\u6709\u53d1\u73b0\uff0c\u8fd9 \u624d\u5c06\u5b83\u4ea4\u7ed9\u4f60\uff0c\u8fd9\u4e5f\u56e0\u4e3a\u6211\u770b\u4f60\u5fc3\u601d\u7075\u654f\uff0c\u800c\u4e14\u5468\u540e\u798f\u7f18\u751a\u591a\uff0c\u5e0c\u671b\u4f60\u80fd\u65e0\u610f\u53d1\u73b0\uff0c\u5374\u4e0d\u6599 \u4f60\u770b\u6765\u806a\u660e\uff0c\u5176\u5b9e\u5374\u662f\u4e2a\u5446\u86cb\uff0c\u7adf\u7136\u5c06\u5251\u7ed9\u4e22\u4e86\u3002\u201d \u3000\u3000\u6bd2\u5fc3\u795e\u9b54\u968f\u53c8\u8bf4\u9053\uff1a\u201c\u6211\u4e00\u6c14\u4e4b\u4e0b\uff0c\u4e00\u638c\u5c31\u90a3\u5c06\u90a3\u5bb6\u4f19\u5288\u4e86\uff0c\u5230\u5904\u627e\u4f60\uff0c\u4e5f\u627e\u4e0d\u5230\uff0c\u4e8e \u662f\u6211\u8dd1\u5230\u6b66\u5f53\u5c71\u53bb\uff0c\u6211\u60f3\u90a3\u513f\u7684\u8001\u9053\u4e5f\u8bb8\u77e5\u9053\u4f60\u7684\u4e0b\u843d\uff0c\u5374\u60f3\u4e0d\u5230\u4f60\u7adf\u8ddf\u7740\u4e00\u4e2a\u5973\u5a03\u5a03\u53c8\u95ef \u4e0b\u5927\u7978\uff0c\u540e\u6765\u4f60\u81ea\u5e97\u4e2d\u6551\u51fa\u90a3\u4e2a\u59d3\u590f\u7684\u5973\u5a03\u5a03\uff0c\u6211\u770b\u7740\u90a3\u6b66\u5f53\u8001\u9053\u4ee5\u5927\u6b3a\u5c0f\uff0c\u800c\u4e14\u4e00\u8138\u50b2 \u6c14\uff0c\u5fc3\u91cc\u6709\u6c14\uff0c\u968f\u624b\u7ed9\u4ed6\u5403\u4e86\u4e2a\u82e6\u5934\uff0c\u5c31\u8dd1\u6765\u8ddf\u7740\u4f60\uff0c\u4f60\u5374\u5fc3\u91cc\u53ea\u8bb0\u7740\u90a3\u4e2a\u5973\u5a03\u5a03\uff0c\u8fde\u6709\u4eba \u5728\u540e\u9762\u8ddf\u7740\u90fd\u4e0d\u77e5\u9053\uff0c\u54fc\uff0c\u50cf\u4f60\u8fd9\u6837\uff0c\u4ee5\u540e\u9047\u5230\u5f3a\u654c\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u6bd2\u5fc3\u795e\u9b54\u8bed\u6c14\u6e10\u7f13\uff0c\u8bf4\u9053\uff1a\u201c\u5e78\u597d\uff0c\u4f60\u8fd8\u6709\u70b9\u7537\u5b50\u6c14\u6982\uff0c\u53c8\u4ea4\u4e86\u51e0\u4e2a\u670b\u53cb\uff0c\u4f46\u662f\u4ee5\u540e\u559d \u9152\u5374\u662f\u4e0d\u80fd\u8fc7\u91cf\uff0c\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u6bd2\u5fc3\u795e\u9b54\u53c8\u8bf4\u9053\uff1a\u201c\u53ea\u662f\u4f60\u81ea\u5df1\u4e22\u7684\u5251\uff0c\u4e00\u5b9a\u8981\u4f60\u81ea\u5df1\u53bb\u62ff\u56de\u6765\uff0c\u6211\u7ed9\u4f60\u4e00\u5e74\u7684\u9650\u671f\uff0c \u4e00\u5e74\u4e4b\u5185\u82e5\u4e0d\u80fd\u5230\u592a\u884c\u5c71\u53bb\u628a\u5251\u62ff\u56de\u6765\u7684\u8bdd\uff0c\u54fc\uff01\u4e00\u5e74\u4e4b\u540e\uff0c\u6211\u518d\u6765\u627e\u4f60\u3002\u201d \u3000\u3000\u6bd2\u5fc3\u795e\u9b54\u8bdd\u521a\u8bf4\u5b8c\uff0c\u4eba\u5c31\u98d8\u7136\u79bb\u53bb\u3002 \u3000\u3000\u718a\u501c\u7ad9\u8d77\u8eab\u6765\uff0c\u62cd\u62cd\u819d\u4e0a\u7684\u6ce5\u571f\uff0c\u770b\u770b\u5929\u8272\uff0c\u5374\u5728\u4e0d\u77e5\u4e0d\u89c9\u95f4\u53c8\u662f\u6e05\u6668\u4e86\u3002 \u3000\u3000\u4ed6\u770b\u4e86\u770b\u811a\u4e0b\uff0c\u978b\u5b50\u65e2\u6ca1\u6709\u7a7f\uff0c\u4e00\u53cc\u767d\u889c\u5b50\uff0c\u867d\u7136\u4ed6\u7684\u8f7b\u529f\u4f73\u5999\uff0c\u811a\u4e0d\u6cbe\u5730\u5728\u8dea\u7740\u65f6\uff0c \u4e5f\u6cbe\u4e86\u4e0d\u5c11\u5c18\u4e0a\u3002 \u3000\u3000\u4ed6\u82e6\u7b11\u4e86\u4e00\u4e0b\uff0c\u4f46\u4e5f\u5e76\u672a\u5341\u5206\u5728\u610f\uff0c\u4fbf\u5927\u6b65\u5411\u57ce\u5185\u8d70\u53bb\u3002 \u3000\u3000\u4ed6\u5728\u8def\u4e0a\u8f6c\u4e86\u51e0\u4e2a\u5f2f\uff0c\u5374\u53c8\u8ff7\u4e86\u8def\uff0c\u627e\u4e0d\u7740\u53f6\u59d3\u5144\u5f1f\u90a3\u5e97\u7684\u65b9\u5411\u3002 \u3000\u3000\u6b63\u5f53\u4ed6\u76f4\u5230\u8857\u7684\u5c3d\u5934\uff0c\u4e00\u53ea\u9ed1\u6bdb\u8338\u8338\u7684\u7c97\u624b\uff0c\u7a81\u5730\u5728\u4ed6\u80a9\u4e0a\u4e00\u62cd\u3002 \u3000\u3000\u5728\u5927\u8857\u4e0a\uff0c\u4ed6\u52bf\u4e0d\u80fd\u95ea\u5c55\u817e\u632a\uff0c\u6765\u907f\u5f00\u6b64\u4e00\u62cd\uff0c\u53ea\u5f97\u8ba9\u4ed6\u62cd\u4e86\u4e0b\uff0c\u4fa7\u8138\u4e00\u770b\uff0c\u89c1\u662f\u4e24\u4e2a \u7a7f\u7740\u77ed\u6253\u7684\u7c97\u6c49\u3002 \u3000\u3000\u718a\u501c\u4e00\u6115\uff0c\u4e0d\u77e5\u9053\u8fd9\u4e24\u7c97\u6c49\u4e3a\u4ec0\u4e48\u7a81\u7136\u62cd\u4ed6\u4e00\u4e0b\uff0c\u5176\u4e2d\u4e00\u4e2a\u6563\u7740\u8863\u895f\u7684\u7c97\u6c49\uff0c\u6c99\u54d1\u7740\u5589 \u5499\u9053\uff1a\u201c\u6211\u4eec\u5f53\u5bb6\u7684\u8bf7\u4f60\u53bb\u4e00\u8d9f\u3002\u201d \u3000\u3000\u718a\u501c\u66f4\u662f\u5947\u602a\uff0c\u4ed6\u5728\u6b64\u5730\u4e00\u4eba\u4e0d\u8bc6\uff0c\u600e\u4f1a\u6709\u4eba\u6765\u8bf7\u4ed6\uff0c\u4fbf\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u90a3\u4e2a\u6c99\u54d1\u5589\u5499\u7684\u7c97\u6c49\u597d\u50cf\u5f88\u4e0d\u8010\u70e6\u4ed6\u8bf4\u9053\uff1a\u201c\u4f60\u5230\u4e86\u90a3\u91cc\u5c31\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u718a\u501c\u60f3\u4e86\u60f3\uff0c\u4ed6\u76f8\u4fe1\u4ee5\u4ed6\u7684\u6b66\u529f\uff0c\u8d70\u5230\u90a3\u91cc\u4e5f\u4e0d\u4f1a\u5403\u4e8f\uff0c\u5766\u7136\u5730\u8ddf\u7740\u90a3\u7c97\u6c49\u5c31\u8d70\u3002 \u3000\u3000\u53f6\u5bb6\u5144\u5f1f\u7684\u5e97\u94fa\u662f\u5411\u5de6\u8f6c\uff0c\u90a3\u4e24\u4e2a\u7c97\u6c49\u5374\u5e26\u4ed6\u5f80\u53f3\u8f6c\uff0c\u90a3\u4e24\u4eba\u811a\u6b65\u4ea6\u751a\u77eb\u5065\uff0c\u50cf\u662f\u4e5f\u6709 \u6b66\u529f\u5e95\u5b50\uff0c\u8d70\u4e86\u4e00\u4f1a\uff0c\u5230\u4e86\u4e00\u4e2a\u5f88\u5927\u7684\u5b85\u9662\uff0c\u6f06\u9ed1\u7684\u5927\u95e8\uff0c\u94dc\u505a\u7684\u628a\u624b\u64e6\u5f97\u96ea\u4eae\uff0c\u95e8\u662f\u5f00\u7740 \u7684\u3002 \u3000\u3000\u95e8\u53e3\u672c\u6765\u805a\u7740\u4e00\u5806\u95f2\u6c49\uff0c\u5176\u4e2d\u4e00\u4e2a\u8d70\u6765\u7b11\u9053\uff1a\u201c\u559d\uff01\u5230\u5e95\u662f\u8001\u8d75\u6709\u672c\u4e8b\uff0c\u5c45\u7136\u627e\u5230\u4e86\uff0c \u8fd9\u4e00\u56de\u53ef\u5c11\u4e0d\u4e86\u5341\u4e24\u94f6\u5b50\u7684\u9152\u94b1\u4e86\u3002\u201d \u3000\u3000\u90a3\u6c99\u54d1\u5589\u5499\u7684\u7c97\u6c49\uff0c\u88c2\u5f00\u4e00\u5634\u9ec4\u7259\u7b11\u9053\uff1a\u201c\u597d\u8bf4\uff0c\u597d\u8bf4\uff0c\u5f53\u5bb6\u7684\u82e5\u771f\u7684\u8d4f\u4e0a\u94f6\u5b50\uff0c\u4f60\u6211 \u5144\u5f1f\u4eca\u5929\u665a\u4e0a\u53c8\u53ef\u4ee5\u5230\u5c0f\u6768\u82b1\u90a3\u91cc\u4e50\u4e00\u4e50\u4e86\u3002\u201d \u3000\u3000\u718a\u501c\u542c\u4e86\u8fd9\u4e9b\u7c97\u6c49\u6240\u8bb2\u7684\u8bdd\uff0c\u66f4\u662f\u83ab\u540d\u5176\u5999\uff0c\u4f46\u4ed6\u4ecd\u7136\u5fcd\u53d7\u7740\uff0c\u5e0c\u671b\u77e5\u9053\u5230\u5e95\u662f\u4e3a\u4e86\u4ec0 \u4e48\uff0c\u4ed6\u4eec\u7684\u5f53\u5bb6\u7684\u53c8\u662f\u4f55\u8bb8\u4eba\u4e5f\u3002 \u3000\u3000\u90a3\u53eb\u505a\u8001\u8d75\u7684\u5e26\u7740\u718a\u501c\u6602\u5934\u8fdb\u95e8\u53bb\uff0c\u718a\u501c\u89c1\u9662\u5b50\u91cc\uff0c\u4e5f\u805a\u7740\u5341\u6570\u4e2a\u58ee\u6c49\uff0c\u770b\u89c1\u8001\u8d75\u4e5f\u8bf4 \u7740\u540c\u6837\u65e0\u804a\u7684\u8bdd\u3002 \u3000\u3000\u8001\u8d75\u627e\u4e86\u4e00\u4e2a\u9752\u5e74\u5c0f\u53ae\u5495\u5495\u561f\u561f\u4ed6\u8bf4\u4e86\u534a\u5929\uff0c\u90a3\u5c0f\u53ae\u8dd1\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u4e00\u4f1a\uff0c\u91cc\u9762\u8d70\u51fa\u4e00\u4e2a\u767d\u91cc\u767d\u51c0\u7684\uff0c\u4f46\u5374\u5996\u5f62\u602a\u72b6\u7684\u5e74\u8f7b\u540e\u751f\uff0c\u89c1\u4e86\u8001\u8d75\u8bf4\uff1a\u201c\u559d\uff0c\u8001\u8d75 \u771f\u6709\u4f60\u7684\uff0c\u5934\u5b50\u6b63\u5728\u91cc\u9762\u5938\u5956\u4f60\u5462\uff0c\u7b49\u4f1a\u5230\u5e10\u623f\u53bb\u9886\u4e94\u4e24\u94f6\u5b50\u559d\u9152\u53bb\uff0c\u8fd9\u4e2a\u4eba\u4ea4\u7ed9\u6211\u5427\u3002\u201d \u8001\u8d75\u54c8\u54c8\u6253\u4e86\u4e2a\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u674e\u4e8c\u7237\uff0c\u60a8\u597d\uff0c\u5f53\u5bb6\u7684\u90a3\u91cc\u8fd8\u8bf7\u591a\u7167\u987e\u3002\u201d \u3000\u3000\u90a3\u4e2a\u674e\u4e8c\u7237\u7b11\u9053\u70b9\u4e86\u70b9\u5934\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u600e\u4e48\u627e\u5230\u4ed6\u7684\u5440\uff1f\u201d \u3000\u3000\u8001\u8d75\u5df4\u7ed3\u5730\u7b11\u7740\u8bf4\u9053\uff1a\u201c\u6211\u89c1\u8fd9\u4eba\u6ca1\u7a7f\u978b\u5b50\uff0c\u8d70\u8def\u53c8\u614c\u614c\u5f20\u5f20\uff0c\u5c31\u77e5\u9053\u51c6\u662f\u4ed6\uff0c\u679c\u7136\u8fd9 \u5c0f\u5b50\u505a\u8d3c\u5fc3\u865a\uff0c\u5c31\u8ddf\u7740\u6765\u4e86\u3002\u201d \u3000\u3000\u718a\u501c\u8d8a\u542c\u8d8a\u5947\u602a\uff0c\u5fc3\u60f3\uff1a\u8fd9\u83ab\u975e\u53c8\u662f\u8bef\u4f1a\uff0c\u5509\uff0c\u8fd9\u4e9b\u65e5\u5b50\u6765\u6211\u600e\u4e48\u8001\u78b0\u89c1\u8fd9\u4e9b\u4e0d\u660e\u4e0d\u81ea \u7684\u9ebb\u70e6\uff0c\u771f\u662f\u5012\u9709\u5f97\u5f88\uff01 \u3000\u3000\u90a3\u4e2a\u674e\u4e8c\u7237\u5374\u7b11\u4e86\u7b11\u62c9\u7740\u718a\u501c\u7684\u8180\u5b50\uff0c\u602a\u91cc\u602a\u6c14\u4ed6\u8bf4\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u8ddf\u6211\u6765\u5427\uff0c\u7b49\u4f1a\u5934\u5b50 \u771f\u8981\u600e\u4e48\u6837\u5bf9\u4f60\uff0c\u90fd\u6709\u6211\u5462\uff0c\u53ea\u8981\u4ee5\u540e\u5144\u5f1f\u4f60\u4e0d\u8981\u5fd8\u4e86\u54e5\u54e5\u7684\u597d\u5904\u5c31\u884c\u4e86\u81ea\u201d\u718a\u501c\u89c1\u6b64\u4eba\u8bf4 \u8bdd\u5996\u91cc\u5996\u6c14\u7684\u50cf\u4e2a\u5973\u4eba\uff0c\u5fc3\u91cc\u8ba8\u538c\u5f97\u5f88\uff0c\u4e5f\u4e0d\u613f\u591a\u8bf4\u8bdd\uff0c\u6697\u60f3\u89c1\u4e86\u8fd9\u4ec0\u4e48\u201c\u5934\u5b50\u3002\u518d\u8bf4\u5427\uff0c \u9042\u8ddf\u7740\u4ed6\u8d70\u8fdb\u5927\u5385\u3002\u90a3\u674e\u4e8c\u7237\u8d70\u8fdb\u5927\u5385\u540e\uff0c\u5e76\u4e0d\u505c\u7559\uff0c\u5e26\u7740\u718a\u501c\u4e03\u8f6c\u516b\u8f6c\uff0c\u8d70\u5230\u4e00\u6392\u6781\u60c5\u81f4 \u7684\u5e73\u8f69\uff0c\u9694\u7740\u95e8\u8f7b\u8f7b\u53eb\u4e86\u58f0\uff1a\u201c\u6765\u4e86\u3002\u201d \u3000\u3000\u718a\u501c\u5c31\u542c\u5f97\u91cc\u9762\u4e00\u4e2a\u4e2d\u6c14\u751a\u8db3\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u5e26\u4ed6\u8fdb\u6765\u3002\u201d \u3000\u3000\u718a\u501c\u4e00\u542c\u6b64\u4eba\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u5c31\u77e5\u9053\u6b64\u4eba\u6709\u4e9b\u6b66\u529f\u6839\u57fa\uff0c\u8ddf\u7740\u674e\u4e8c\u7237\u8d70\u8fdb\u90a3\u5e73\u8f69\uff0c\u53ea\u89c1\u4e00 \u4e2a\u8eab\u6750\u751a\u662f\u9ad8\u5927\u7684\u6c49\u5b50\u6b63\u8d1f\u7740\u624b\u5728\u8f69\u91cc\u6765\u56de\u8d70\u7740\u3002 \u3000\u3000\u90a3\u6c49\u5b50\u89c1\u718a\u501c\u8d70\u4e86\u8fdb\u6765\uff0c\u773c\u91cc\u7a81\u73b0\u715e\u6c14\uff0c\u4ece\u5934\u5230\u811a\u6253\u91cf\u4e86\u718a\u501c\u51e0\u773c\uff0c\u53c8\u72e0\u72e0\u5730\u76ef\u4e86\u51e0\u773c \u718a\u501c\u90a3\u6ca1\u6709\u7a7f\u978b\u7684\u811a\u3002 \u3000\u3000\u7a81\u7136\uff0c\u4ed6\u8bf4\u9053\uff1a\u201c\u5c0f\u674e\uff0c\u5c06\u90a3\u53cc\u978b\u5b50\u62ff\u8fc7\u6765\u3002\u201d \u3000\u3000\u5c0f\u674e\u5e94\u58f0\u62ff\u6765\u4e00\u53cc\u751a\u662f\u8bb2\u7a76\u7684\u978b\u5b50\uff0c\u6700\u5999\u7684\u662f\u90a3\u978b\u5b50\u7684\u989c\u8272\u7adf\u4e5f\u548c\u718a\u501c\u7684\u8863\u670d\u76f8\u914d\u3002 \u3000\u3000\u90a3\u6c49\u5b50\u6307\u7740\u90a3\u53cc\u978b\u5b50\uff0c\u5bf9\u718a\u501c\u8bf4\u9053\uff1a\u201c\u7a7f\u4e0a\u3002\u201d\u718a\u501c\u6108\u6765\u6108\u4e0d\u660e\u767d\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u4f46\u5374 \u597d\u5947\u4e4b\u5fc3\u5927\u8d77\uff0c\u60f3\u770b\u770b\u8fd9\u4e9b\u4eba\u5230\u5e95\u5728\u5f04\u4ec0\u4e48\u540d\u5802\uff0c\u9042\u4e00\u8a00\u4e0d\u53d1\u5730\u7a7f\u4e0a\u90a3\u53cc\u978b\u5b50\uff0c\u53c8\u6781\u4e3a\u5408 \u811a\u3002 \u3000\u3000\u90a3\u6c49\u5b50\u4f3c\u4e4e\u975e\u5e38\u751f\u6c14\uff0c\u8138\u4e0a\u7684\u9752\u7b4b\uff0c\u90fd\u6839\u6839\u663e\u9732\u4e86\u51fa\u6765\uff0c\u6012\u6781\u51b7\u7b11\u9053\uff1a\u201c\u670b\u53cb\u771f\u662f\u4e2a\u89d2 \u8272\uff0c\u7adf\u6562\u5728\u6211\u9762\u524d\u5f04\u9b3c\u3002\u201d \u3000\u3000\u718a\u501c\u7b11\u4e86\u4e00\u4e0b\uff0c\u8f7b\u677e\u4ed6\u8bf4\u9053\uff1a\u201c\u6211\u548c\u5f53\u5bb6\u7684\u7d20\u6627\u5e73\u751f\uff0c\u5f04\u8fc7\u4ec0\u4e48\u9b3c\u5462\uff1f\u201d \u3000\u3000\u90a3\u6c49\u5b50\u95fb\u8a00\u66f4\u662f\u6c14\u5f97\u6ee1\u8138\u901a\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u4e08\u592b\u6562\u505a\u6562\u4e3a\uff0c\u670b\u53cb\u65e2\u7136\u6709\u80c6\u5b50\u722c\u4e0a\u6211\u8001\u5a46 \u7684\u5e8a\uff0c\u600e\u4e48\u73b0\u5728\u53c8\u6ca1\u6709\u80c6\u5b50\u627f\u8ba4\uff1f\u201d \u3000\u3000\u7126\u501c\u542c\u4e86\uff0c\u5012\u771f\u662f\u5403\u4e86\u4e00\u4e2a\u5927\u60ca\uff0c\u5fc3\u60f3\uff0c\u201c\u8fd9\u73a9\u7b11\u5012\u771f\u5f00\u5f97\u5927\u5927\u4e86\uff0c\u82e5\u4e0d\u89e3\u91ca\u6e05\u695a\uff0c\u770b \u6837\u5b50\u8fd9\u6c49\u5b50\u4e00\u5b9a\u4e0d\u4f1a\u548c\u6211\u5584\u7f62\u7518\u4f11\u7684\u3002\u535c\u4ed6\u6697\u91cc\u5728\u8f6c\u7740\u5fc3\u4e8b\uff0c\u4e00\u65f6\u7adf\u6ca1\u6709\u7b54\u90a3\u6c49\u5b50\u7684\u8bdd\uff0c\u90a3 \u6c49\u5b50\u5374\u4ee5\u4e3a\u4ed6\u9ed8\u8ba4\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u770b\u4f60\u6587\u8d28\u5f6c\u5f6c\u7684\u6837\u5b50\uff0c\u800c\u4e14\u4e00\u8868\u4eba\u6750\uff0c\u771f\u60f3\u4e0d\u5230\u4f60\u4f1a\u505a\u51fa\u8fd9 \u7b49\u4e0d\u8981\u8138\u7684\u4e8b\uff0c\u867d\u7136\u548e\u975e\u5728\u4f60\u4e00\u4eba\uff0c\u4f46\u6211\u5df2\u5c06\u90a3\u5a3c\u5987\u6740\u6b7b\u4e86\uff0c\u4f60\u6b63\u597d\u5230\u9b3c\u95e8\u5173\u53bb\u966a\u966a\u5979\u3002\u201d \u3000\u3000\u4ed6\u6d53\u7709\u4e00\u7ad6\uff0c\u53c8\u53eb\u9053\uff1a\u201c\u5c0f\u674e\uff0c\u53bb\u628a\u6211\u7684\u90a3\u67c4\u5251\u62ff\u6765\uff0c\u4eba\u5bb6\u65e2\u7136\u75db\u75db\u5feb\u5feb\u5730\u6765\u4e86\uff0c\u6211\u4eec \u4e5f\u8be5\u75db\u75db\u5feb\u5feb\u5730\u9001\u4ed6\u56de\u53bb\u3002\u201d \u3000\u3000\u718a\u501c\u5df2\u77e5\u6b64\u4e8b\u6108\u641e\u6108\u7cdf\uff0c\u4f3c\u975e\u4e09\u8a00\u4e24\u8bed\u53ef\u4ee5\u89e3\u91ca\u660e\u767d\u7684\uff0c\u5fd9\u6b63\u5bb9\u8bf4\u9053\uff1a\u201c\u5f53\u5bb6\u7684\u60f3\u5fc5\u662f \u8bef\u4f1a\u4e86\uff0c\u6709\u8bdd\u6162\u6162\u8bf4\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u4ed6\u6b63\u8bf4\u81f3\u6b64\u5904\uff0c\u5ffd\u5730\u4e00\u773c\u775b\u89c1\u90a3\u201c\u674e\u4e8c\u7237\u201d\u62ff\u6765\u7684\u5251\uff0c\u5fc3\u4e2d\u4e00\u8df3\u3002 \u3000\u3000\u539f\u6765\u4ed6\u770b\u89c1\u90a3\u201c\u674e\u4e8c\u7237\u201d\u6240\u53d6\u6765\u7684\u5251\uff0c\u5251\u8eab\u7279\u957f\uff0c\u5f62\u5f0f\u5947\u53e4\uff0c\u7adf\u662f\u81ea\u5df1\u6240\u9057\u5931\u7684\u90a3\u67c4 \u201c\u501a\u5929\u5251\u201d\u3002 \u3000\u3000\u5927\u60ca\u4e4b\u4e0b\uff0c\u5c06\u6240\u8981\u8bf4\u7684\u8bdd\u7adf\u54bd\u56de\u8179\u4e2d\u3002 \u3000\u3000\u90a3\u6c49\u5b50\u62ff\u8fc7\u201c\u674e\u4e8c\u7237\u201d\u53d6\u6765\u7684\u5251\uff0c\u6ee1\u8138\u715e\u6c14\u8bf4\u9053\uff1a\u201c\u4f60\u8fd8\u6709\u4ec0\u4e48\u540e\u4e8b\uff0c\u7094\u70b9\u8bf4\u51fa\u6765\uff0c\u6211 \u770b\u4f60\u6587\u8d28\u5f6c\u5f6c\uff0c\u5356\u4f60\u8fd9\u4e2a\u51a4\u9b42\u4e00\u6761\u4ea4\u60c5\uff0c\u53ea\u8981\u4f60\u8bf4\u51fa\u6765\u7684\u8bdd\uff0c\u6211\u4f1a\u66ff\u4f60\u505a\u5230\u5c31\u662f\u4e86\u3002\u201d \u3000\u3000\u718a\u501c\u6697\u4e2d\u6b63\u5728\u601d\u7d22\u7740\uff1a\u4ed6\u8fd9\u4e24\u5929\u542c\u5230\u7684\u5168\u662f\u5947\u4e8b\uff0c\u800c\u6700\u5947\u602a\u7684\u4e8b\uff0c\u5c31\u662f\u81ea\u5df1\u6240\u9057\u5931\u7684 \u201c\u501a\u5929\u5251\u201d\uff0c\u660e\u660e\u7684\u8bf4\u843d\u5728\u5929\u9634\u6559\u4e2d\uff0c\u600e\u5730\u53c8\u4f1a\u5728\u8fd9\u5c0f\u57ce\u91cc\u4e00\u4e2a\u770b\u4f3c\u571f\u8c6a\u822c\u7684\u89d2\u8272\u624b\u91cc\u53d1 \u73b0\u3002 \u3000\u3000\u4ed6\u8111\u4e2d\u6240\u60f3\u7684\uff0c\u5c3d\u662f\u6709\u5173\u201c\u501a\u5929\u5251\u201d\u7684\u4e8b\uff0c\u5374\u628a\u773c\u524d\u8fd9\u79cd\u5251\u62d4\u5938\u5f20\u7684\u60c5\u51b5\uff0c\u5168\u7136\u6ca1\u6709\u653e \u5728\u5fc3\u91cc\uff0c\u8fd9\u81ea\u662f\u4ed6\u5bf9\u201c\u501a\u5929\u5251\u201d\u5173\u5fc3\u592a\u8fc7\uff0c\u800c\u4e14\u6043\u8eab\u624b\uff0c\u76f8\u4fe1\u4f1a\u5c06\u8fd9\u8bef\u4f1a\u5316\u89e3\u7684\u3002 \u3000\u3000\u90a3\u6c49\u5b50\u89c1\u4ed6\u5982\u6b64\uff0c\u6012\u559d\u4e00\u58f0\uff0c\u968f\u624b\u62d4\u51fa\u5251\uff0c\u7adf\u5411\u718a\u501c\u5f53\u5934\u5288\u4e0b\u3002 \u3000\u3000\u718a\u501c\u8fd9\u624d\u4e00\u60ca\uff0c\u4f46\u90a3\u6c49\u5b50\u867d\u7136\u6b66\u529f\u4e0d\u5f31\uff0c\u4f46\u600e\u4e48\u5288\u5f97\u7740\u718a\u501c\u3002 \u3000\u3000\u4ed6\u7a0d\u4e3a\u4e00\u4fa7\u81ea\u5df1\u7684\u8eab\u8eaf\uff0c\u4fbf\u8f7b\u6613\u5730\u907f\u5f00\u4e86\u8fd9\u770b\u4f3c\u51cc\u5389\u7684\u4e00\u5251\u3002 \u3000\u3000\u90a3\u6c49\u4e8e\u4e00\u5251\u8d70\u7a7a\uff0c\u559d\u9053\uff1a\u201c\u597d\uff0c\u670b\u53cb\u5c45\u7136\u4e5f\u662f\u4e2a\u7ec3\u5bb6\u5b50\u3002\u201d\u957f\u5251\u5f80\u56de\u4e00\u5e26\u5251\u5c3e\u7adf\u4e5f\u6709\u5bd2 \u8292\u66b4\u8d77\uff0c\u6a2a\u8d77\u4e00\u5251\uff0c\u5411\u718a\u501c\u6a2a\u8170\u65a9\u53bb\u3002 \u3000\u3000\u718a\u501c\u4e00\u89c1\u6b64\u5251\u5c3e\u5e26\u5bd2\u8292\uff0c\u4fbf\u8ba4\u5b9a\u662f\u81ea\u5df1\u6240\u9057\u5931\u4e4b\u7269\uff0c\u518d\u89c1\u8fd9\u6c49\u5b50\u4e0d\u5206\u9752\u7ea2\u7682\u81ea\uff0c\u5728\u5bb6\u4e2d \u5c31\u6562\u968f\u4fbf\u6740\u4eba\uff0c\u60f3\u5fc5\u5e73\u65e5\u662f\u4e2a\u6a2a\u884c\u4e61\u91cc\u7684\u571f\u8c6a\uff0c\u5927\u6012\u4e4b\u4e0b\uff0c\u5f80\u524d\u731b\u4e00\u8fc8\u6b65\uff0c\u90a3\u5251\u4fbf\u523a\u7a7a\u4e86\u3002 \u3000\u3000\u718a\u501c\u5e76\u6307\u5982\u94a9\uff0c\u5728\u90a3\u6c49\u5b50\u5251\u52bf\u5df2\u5230\u5c3e\u58f0\u7684\u65f6\u5019\uff0c\u7a81\u5730\u7528\u98df\u4e2d\u4e24\u6307\uff0c\u631f\u7740\u5251\u8eab\uff0c\u53ea\u89c9\u5f97\u5165 \u624b\u5982\u51b0\uff0c\u786e\u662f\u4e00\u628a\u5b9d\u5251\u3002 \u3000\u3000\u90a3\u6c49\u5b50\u5374\u5927\u5403\u4e00\u60ca\uff0c\u9ad8\u5927\u7684\u8eab\u8eaf\uff0c\u5f80\u4e0b\u4e00\u5750\u9a6c\uff0c\u60f3\u4ece\u718a\u501c\u5b50\u4e2d\u593a\u56de\u6b64\u5251\u3002 \u3000\u3000\u718a\u501c\u51b7\u7b11\u4e00\u58f0\uff0c\u5de6\u638c\u659c\u659c\u5730\u524a\u51fa\uff0c\u90a3\u6c49\u5b50\u5fd9\u7f29\u5934\u85cf\u5c3e\uff0c\u60f3\u907f\u5f00\u6b64\u62db\uff0c\u718a\u501c\u600e\u8ba9\u4ed6\u79f0\u5fc3\uff0c \u5ffd\u5730\u6539\u638c\u4e3a\u6307\uff0c\u6025\u70b9\u5728\u4ed6\u9f3b\u8fb9\u201c\u6c89\u9999\u201d\u8981\u7a74\u4e0a\u3002 \u3000\u3000\u90a3\u674e\u4e8c\u7237\u89c1\u4eba\u5bb6\u4e00\u51fa\u624b\uff0c\u5c31\u5c06\u5934\u5b50\u5236\u4f4f\uff0c\u811a\u5e95\u63e9\u6cb9\uff0c\u4fbf\u60f3\u6e9c\u51fa\u53bb\u8ba8\u6551\u5175\uff0c\u718a\u501c\u8eab\u8d77\u5982 \u98ce\uff0c\u6a2a\u8d8a\u8fc7\u53bb\uff0c\u7528\u5251\u5728\u4ed6\u5934\u4e0a\u5e73\u7740\u4e00\u62cd\uff0c\u90a3\u674e\u4e8c\u7237\uff0c\u7adf\u549a\u5730\u4e00\u58f0\uff0c\u6655\u5012\u5728\u5730\u4e0a\u3002 \u3000\u3000\u718a\u501c\u968f\u5373\u5c06\u8fd9\u5e73\u8f69\u7684\u623f\u95e8\u5e26\u8d77\uff0c\u4ed6\u5ffd\u89c9\u5f97\u624b\u4e2d\u7684\u5251\uff0c\u4f3c\u4e4e\u8981\u6bd4\u4ed6\u81ea\u5df1\u539f\u5148\u90a3\u67c4\u8f7b\u4e86\u4e9b\uff0c \u4e8e\u662f\u4ed6\u5c06\u5251\u62ff\u8d77\u4ed4\u7ec6\u4e00\u770b\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u770b\uff0c\u624d\u77e5\u9053\u8fd9\u5251\u867d\u7136\u548c\u81ea\u5df1\u90a3\u67c4\u201c\u501a\u5929\u5251\u201d\uff0c\u5f62\u5f0f\u3001\u5927\u5c0f\uff0c\u751a\u81f3\u950b\u5229\u5168\u90fd\u5b8c\u5168\u4e00 \u6837\uff0c\u4f46\u5374\u5e76\u4e0d\u662f\u81ea\u5df1\u6240\u5931\u7684\u90a3\u67c4\u201c\u501a\u5929\u5251\u201d\u3002 \u3000\u3000\u90a3\u5251\u67c4\u4e0a\uff0c\u4e5f\u7528\u9014\u7ebf\u7f15\u6210\u4e24\u5b57\uff0c\u5374\u662f\u201c\u8d2f\u65e5\u201d\u4e24\u5b57\u3002 \u3000\u3000\u4ed6\u8d70\u5230\u90a3\u6c49\u5b50\u8eab\u4fa7\uff0c\u8f7b\u8f7b\u7528\u624b\u62cd\u5f00\u90a3\u6c49\u5b50\u7684\u7a74\u9053\uff0c\u8bf4\u9053\uff1a\u201c\u5582\uff0c\u6211\u548c\u4f60\u5f80\u65e5\u65e0\u51a4\uff0c\u8fd1\u65e5 \u65e0\u4ec7\uff0c\u4f60\u600e\u7684\u7528\u5251\u5c31\u8981\u6740\u6211\uff1f\u201d \u3000\u3000\u90a3\u6c49\u5b50\u4e00\u52a8\u624b\uff0c\u5c31\u88ab\u4eba\u5bb6\u5236\u4f4f\uff0c\u5fc3\u77e5\u81ea\u5df1\u6b66\u529f\u6bd4\u4eba\u5bb6\u5dee\u5f97\u592a\u8fdc\uff0c\u4f46\u80f8\u4e2d\u4e4b\u6c14\uff0c\u5374\u662f\u96be \u5e73\uff0c\u54ac\u7259\u9053\uff1a\u201c\u6211\u5c0f\u4e27\u95e8\u6280\u4e0d\u5982\u4eba\uff0c\u4ec0\u4e48\u8bdd\u90fd\u6ca1\u6709\u8bf4\uff0c\u670b\u53cb\u662f\u597d\u7684\uff0c\u5c31\u8bf7\u7559\u4e0b\u4e2a\u4e07\u513f\uff0c\u8bdd\u8bf4 \u5728\u524d\u5934\uff0c\u4eca\u65e5\u4f60\u82e5\u4e0d\u6740\u6211\uff0c\u4ed6\u65e5\u6211\u5374\u8981\u6740\u4f60\u4e86\u3002\u201d \u3000\u3000\u718a\u501c\u5947\u9053\uff1a\u201c\u90a3\u4e48\u6211\u5230\u5e95\u548c\u4f60\u6709\u4ec0\u4e48\u6df1\u4ec7\u5927\u6028\uff0c\u4f60\u975e\u8981\u6740\u6211\u4e0d\u53ef\u3002\u201d \u3000\u3000\u90a3\u5c0f\u4e27\u95e8\u95fb\u8a00\u6c14\u5f97\u53d1\u6296\u8bf4\u9053\uff1a\u201c\u670b\u53cb\uff0c\u4f60\u8fd9\u6837\u5c31\u4e0d\u662f\u597d\u6c49\u5b50\u4e86\uff0c\u6211\u8001\u5a46\u867d\u4e0d\u597d\uff0c\u4f46\u4f60\u5802 \u5802\u7537\u5b50\u6c49\uff0c\u600e\u5730\u4e5f\u5982\u6b64\uff0c\u6211\u5c0f\u4e27\u95e8\u7684\u8001\u5a46\u4e0e\u4f60\u79c1\u901a\uff0c\u96be\u9053\u6211\u5c31\u505a\u77aa\u773c\u4e4c\u9f9f\u5417\uff1f\u201d \u3000\u3000\u718a\u501c\u9053\uff1a\u201c\u4f60\u53c8\u51ed\u4ec0\u4e48\u77e5\u9053\u6211\u548c\u4f60\u8001\u5a46\u79c1\u901a\u5462\uff1f\u4f60\u6839\u672c\u5c31\u4e0d\u8ba4\u8bc6\u6211\u3002\u201d \u3000\u3000\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u6628\u665a\u4e0a\u4f60\u4e58\u6211\u51fa\u5916\uff0c\u548c\u6211\u8001\u5a46\u82df\u548c\uff0c\u88ab\u649e\u89c1\uff0c\u6ca1\u7a7f\u978b\u5b50\u5c31\u4ece\u7a97\u5b50\u8dd1\u4e86\u3002\u4eca \u6668\u88ab\u6211\u624b\u4e0b\u5f1f\u5144\u6349\u4f4f\uff0c\u4f60\u8fd8\u6765\u6c14\u6211\uff0c\u6211\u867d\u6280\u4e0d\u5982\u4f60\uff0c\u4f46\u6b64\u4ec7\u6211\u662f\u975e\u62a5\u4e0d\u53ef\u3002\u201d \u3000\u3000\u718a\u501c\u66f4\u662f\u54ed\u7b11\u4e0d\u5f97\uff0c\u4ed6\u77e5\u9053\u8fd9\u6c49\u5b50\u867d\u7136\u770b\u6765\u662f\u4e2a\u89d2\u8272\uff0c\u5176\u5b9e\u5374\u662f\u4e2a\u4efb\u4e8b\u4e0d\u61c2\u7684\u83bd\u6c49\uff0c\u5fcd \u7740\u6c14\u8bf4\u9053\uff1a\u201c\u4f60\u77e5\u9053\u6211\u662f\u8c01\u5417\uff1f\u4f60\u600e\u80fd\u51ed\u7740\u6211\u6ca1\u7a7f\u978b\u5b50\u5c31\u8ba4\u5b9a\u6211\u662f\u548c\u4f60\u8001\u5a46\u79c1\u901a\u4e4b\u4eba\uff0c\u96be\u9053 \u4e16\u4e0a\u51e1\u662f\u4e0d\u7a7f\u978b\u7684\u4eba\uff0c\u90fd\u662f\u4f60\u90a3\u8001\u5a46\u7684\u59d8\u5934\uff1f\u201d \u3000\u3000\u90a3\u6c49\u5b50\u53eb\u5c0f\u4e27\u95e8\uff0c\u662f\u5f53\u5730\u7684\u4e00\u9738\uff0c\u624b\u5e95\u4e0b\u4e5f\u6765\u5f97\u4e24\u4e0b\u5b50\uff0c\u4e3a\u4eba\u5374\u4e0d\u6298\u4e0d\u6263\u5730\u662f\u4e2a\u83bd\u6c49\uff0c \u5012\u4e5f\u65e0\u751a\u52a3\u8ff9\uff0c\u95fb\u8a00\u7adf\u6014\u6014\u5730\u7b54\u4e0d\u4e0a\u8bdd\u6765\u3002 \u3000\u3000\u718a\u501c\u4f4e\u5934\u89c1\u90a3\u978b\u5b50\u751a\u662f\u534e\u4e3d\uff0c\u4e0d\u662f\u4eba\u4eba\u90fd\u80fd\u7a7f\u7740\u7684\uff0c\u8131\u4e0b\u4e00\u770b\uff0c\u89c1\u978b\u5e95\u4e0a\u5199\u7740\u201c\u5b89\u5fbd\u8001 \u4ecb\u798f\u978b\u5e97\u7279\u5236\u201d\u51e0\u4e2a\u5b57\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u53c8\u95ee\u5c0f\u4e27\u95e8\u9053\uff1a\u201c\u8fd9\u8001\u4ecb\u798f\u978b\u5e97\u53ef\u662f\u5728\u5f53\u6d82\u57ce\u91cc\uff1f\u201d \u3000\u3000\u5c0f\u4e27\u95e8\u70b9\u4e86\u5934\u3002 \u3000\u3000\u718a\u501c\u7528\u978b\u5e95\u4e00\u62cd\u5c0f\u4e27\u95e8\u7684\u80a9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4e0d\u5c31\u597d\u529e\u4e86\u5417\uff0c\u4f60\u4f7f\u7740\u8fd9\u53cc\u978b\u5230\u8001\u4ecb\u798f\u53bb\u95f4 \u4e00\u95ee\uff0c\u8fd9\u79cd\u978b\u7a7f\u7684\u4eba\u4e0d\u591a\u800c\u4e14\u8fd9\u978b\u6709\u4e5d\u6210\u65b0\uff0c\u4e00\u5b9a\u662f\u521a\u4e70\u7684\uff0c\u4f60\u770b\u662f\u8c01\u4e70\u7684\uff0c\u518d\u53bb\u627e\u90a3\u4eba\u7b97 \u5e10\u597d\u4e86\u3002\u201d \u3000\u3000\u5c0f\u4e27\u95e8\u4e24\u6761\u6d53\u7709\u51e0\u4e4e\u76b1\u5230\u4e00\u8d77\uff0c\u60f3\u4e86\u534a\u5929\uff0c\u624d\u4f1a\u8fc7\u610f\u6765\uff0c\u559c\u9053\uff1a\u201c\u8fd9\u5012\u662f\u4e2a\u597d\u529e\u6cd5\u3002\u201d \u62ac\u5934\u671b\u7740\u718a\u501c\uff0c\u53c8\u60ed\u6127\u5730\u4f4e\u4e0b\u5934\u53bb\u3002 \u3000\u3000\u718a\u501c\u77e5\u9053\u8fd9\u79cd\u83bd\u6c49\u76f4\u80a0\u76f4\u809a\uff0c\u201c\u4ec0\u4e48\u90fd\u4e0d\u4f1a\u62d0\u5f2f\uff0c\u4fbf\u7b11\u9053\uff1a\u201c\u6211\u8001\u5b9e\u544a\u8bc9\u4f60\uff0c\u6211\u59d3\u718a\uff0c \u53eb\u718a\u501c\uff0c\u4f60\u542c\u8fc7\u8fd9\u540d\u5b57\u5427\uff0c\u4f60\u770b\u6211\u4f1a\u505a\u8fd9\u79cd\u4e8b\u5417\uff1f\u201d \u3000\u3000\u90a3\u5c0f\u4e27\u95e8\u672c\u4e5f\u662f\u6c5f\u6e56\u4e2d\u4eba\uff0c\u800c\u4e14\u5bb6\u4e2d\u6765\u5f80\u7684\uff0c\u591a\u662f\u884c\u8d70\u6c5f\u6e56\u7684\u597d\u6c49\uff0c\u718a\u501c\u8fd1\u5e74\u6765\u540d\u4f20\u6c5f \u6e56\uff0c\u5c0f\u4e27\u95e8\u7109\u6709\u6ca1\u6709\u542c\u5230\u8fc7\u4e4b\u7406\u3002 \u3000\u3000\u4ed6\u4e00\u542c\u8fd9\u4eba\u7adf\u662f\u718a\u501c\uff0c\u8fde\u5fd9\u7ad9\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5b9e\u5728\u6ca1\u6709\u60f3\u5230\u662f\u718a\u5927\u4fa0\uff0c\u5b9e\u5728\u8be5 \u6b7b\u3002\u201d\u53c8\u9a82\u9053\uff1a\u201c\u8001\u8d75\u90a3\u738b\u516b\u86cb\uff0c\u505a\u4e8b\u4e0d\u957f\u773c\u775b\uff0c\u4ee5\u540e\u5929\u975e\u6559\u8bad\u6559\u8bad\u4ed6\uff0c\u514d\u5f97\u603b\u51fa\u4e8b\u3002\u201d \u3000\u3000\u718a\u501c\u5fc3\u4e2d\u6697\u7b11\u5fd6\u9053\uff1a\u201c\u5176\u5b9e\u8001\u5144\u4e5f\u4e0d\u89c1\u5f97\u6bd4\u8001\u8d75\u9ad8\u660e\u591a\u5c11\u3002\u201d\u5634\u91cc\u5374\u8bf4\u9053\uff1a\u201c\u8fd9\u4e5f\u6ca1\u6709 \u4ec0\u4e48\u5173\u7cfb\uff0c\u53ea\u662f\u4f60\u5bb3\u6211\u9669\u4e9b\u6328\u4e86\u4e00\u5251\uff0c\u5374\u8be5\u5bf9\u6211\u8865\u5c1d\u4e00\u756a\u624d\u662f\uff0c\u4f60\u8bf4\u8be5\u4e0d\u8be5\u5462\uff1f\u201d \u3000\u3000\u5c0f\u4e27\u95e8\u5fd9\u7b54\u9053\uff1a\u201c\u8be5\uff0c\u8be5\uff0c\u718a\u5927\u4fa0\u600e\u4e48\u8bf4\u600e\u4e48\u529e\u597d\u4e86\u3002\u201d \u3000\u3000\u80fd\u501c\u629a\u5f04\u7740\u624b\u4e2d\u7684\u5251\uff0c\u6c89\u541f\u4e0d\u8bed\uff0c\u4ed6\u60f3\u6b64\u5251\u867d\u975e\u201c\u501a\u5929\u5251\u201d\uff0c\u4f46\u5fc5\u548c\u201c\u501a\u5929\u5251\u201d\u6709\u7740\u751a \u5927\u7684\u5173\u7cfb\uff0c\u751a\u81f3\u548c\u6bd2\u5fc3\u795e\u9b54\u6240\u8bf4\u7684\u90a3\u4ef6\u79d8\u5bc6\uff0c\u6709\u7740\u5173\u7cfb\u4e5f\u672a\u53ef\u77e5\uff0c\u662f\u4ee5\u4ed6\u60f3\u83b7\u5f97\u6b64\u5251\u3002 \u3000\u3000\u4f46\u4ed6\u7a76\u7adf\u4e0d\u662f\u5f3a\u53d6\u4e4b\u5f92\uff0c\u4ed6\u60f3\u8fd9\u79cd\u5229\u5668\u795e\u5175\uff0c\u5b9a\u4e5f\u662f\u4eba\u5bb6\u5fc3\u7231\u4e4b\u7269\uff0c\u5c31\u7b97\u81ea\u5df1\u5c31\u6301\u5f3a\u53d6 \u6765\uff0c\u4e5f\u4e0d\u662f\u4fa0\u4e49\u9053\u5e94\u505a\u7684\u4e8b\u3002 \u3000\u3000\u56e0\u4e4b\u4ed6\u6c89\u541f\u518d\u4e09\uff0c\u90a3\u60f3\u95ee\u4eba\u5bb6\u8981\u5251\u7684\u8bdd\uff0c\u5374\u8bf4\u4e0d\u51fa\u53e3\u3002 \u3000\u3000\u54ea\u77e5\u5c0f\u4e27\u95e8\u6b64\u523b\u5374\u7a81\u7136\u806a\u660e\u8d77\u6765\uff0c\u62a2\u7740\u8bf4\u9053\uff1a\u201c\u718a\u5927\u4fa0\u60f3\u662f\u559c\u6b22\u8fd9\u67c4\u5251\u5427\uff1f\u5b9d\u5251\u7406\u5e94\u8d60 \u7ed9\u82f1\u96c4\uff0c\u50cf\u6211\u8fd9\u6837\u7684\uff0c\u8fd8\u771f\u4e0d\u914d\u8fd9\u628a\u5251\u3002\u201d \u3000\u3000\u718a\u501c\u5927\u559c\u9053\uff1a\u201c\u8fd9\u5012\u771f\u8c22\u8c22\u4e86\u3002\u201d\u8f6c\u5ff5\u53c8\u95ee\u9053\uff1a\u201c\u8fd9\u628a\u5251\u662f\u600e\u4e48\u5f97\u6765\u7684\uff0c\u82e5\u662f\u4f60\u7684\u4f20\u5bb6 \u4e4b\u7269\uff0c\u90a3\u6211\u5012\u4e0d\u597d\u610f\u601d\u593a\u4eba\u6240\u597d\u4e86\u3002\u201d \u3000\u3000\u5c0f\u4e27\u95e8\u5374\u6447\u624b\u9053\uff1a\u201c\u8fd9\u54ea\u91cc\u662f\u6211\u4f20\u5bb6\u4e4b\u7269\uff0c\u90a3\u5929\u6211\u624b\u4e0b\u7684\u5144\u5f1f\u5230\u94dc\u5c71\u53bb\u4e70\u4e00\u6279\u65e7\u5175\u5668\uff0c \u8fd9\u67c4\u5b9d\u5251\u5c31\u662f\u5728\u90a3\u4e9b\u5175\u5668\u91cc\u88ab\u4e00\u9f50\u4e70\u6765\u4e86\uff0c\u6211\u770b\u7740\u8fd8\u950b\u5229\u53ef\u7528\uff0c\u81ea\u5df1\u5c31\u7559\u6765\u7528\u4e86\u3002\u201d \u3000\u3000\u4ed6\u7b11\u4e86\u4e00\u7b11\u53c8\u8bf4\u9053\uff1a\u201c\u5176\u5b9e\u6211\u4e5f\u662f\u6446\u5728\u90a3\u91cc\u505a\u6837\u5b50\uff0c\u5012\u771f\u6ca1\u7528\u8fc7\u3002\u201d \u3000\u3000\u718a\u501c\u559c\u9053\uff1a\u201c\u65e2\u662f\u8fd9\u6837\uff0c\u6211\u5c31\u6536\u4e0b\u4e86\u3002\u201d\u4ed6\u5c06\u90a3\u5b9d\u5251\u6536\u5230\u9798\u91cc\uff0c\u53c8\u8bf4\u9053\uff1a\u201c\u8fd9\u91cc\u65e2\u7136\u6ca1 \u4e8b\uff0c\u6211\u5c31\u544a\u8f9e\u4e86\u3002\u201d \u3000\u3000\u90a3\u718a\u501c\u8d70\u5230\u8857\u4e0a\uff0c\u5f97\u5230\u8fd9\u67c4\u5b9d\u5251\uff0c\u5fc3\u4e2d\u751a\u662f\u9ad8\u5174\uff0c\u8fde\u811a\u6b65\u90fd\u663e\u5f97\u8f7b\u5feb\u4e86\u4e9b\uff0c\u4ed6\u6697\u7b11\u9053\uff1a \u201c\u8fd9\u771f\u53eb\u505a\u56e0\u7978\u5f97\u798f\u4e86\u3002\u201d \u3000\u3000\u6b64\u6b21\u4ed6\u5012\u8ba4\u6e05\u4e86\u65b9\u5411\uff0c\u6cbf\u7740\u5927\u8857\u4e0d\u4e00\u4f1a\uff0c\u5c31\u5230\u4e86\u53f6\u5bb6\u5144\u5f1f\u7684\u5e97\u91cc\u3002 \u3000\u3000\u6b64\u65f6\u5e97\u65b9\u5f00\u95e8\uff0c\u90a3\u4e9b\u5e97\u4f19\u6b63\u7761\u773c\u60fa\u677e\u5730\u62b9\u7740\u684c\u6905\uff0c\u718a\u501c\u6602\u7136\u8d70\u8fdb\u53bb\u4e86\u3002 \u3000\u3000\u90a3\u4e9b\u5e97\u4f19\u89c1\u718a\u501c\u6602\u7136\u76f4\u4eba\uff0c\u53c8\u4e0d\u77e5\u4ed6\u6765\u8def\uff0c\u4f46\u5e97\u4e2d\u6c5f\u6e56\u4eba\u6765\u5f80\u672c\u591a\uff0c\u5fc3\u60f3\u8fd9\u6ca1\u6709\u7a7f\u978b\u5b50 \u7684\u4eba\uff0c\u4e5f\u8bb8\u662f\u5e97\u4e3b\u4e4b\u53cb\uff0c\u9042\u4e5f\u4e0d\u6562\u95ee\uff0c\u718a\u501c\u89c1\u4e86\u90a3\u4e9b\u5e97\u4f19\u9762\u4e0a\u7684\u8868\u60c5\uff0c\u809a\u91cc\u89c9\u5f97\u597d\u7b11\uff0c\u4ed6\u4e5f \u4e0d\u7ba1\uff0c\u76f4\u5f80\u540e\u9662\u8d70\u53bb\u3002 \u3000\u3000\u90a3\u5c1a\u672a\u660e\u50cf\u662f\u5bbf\u9152\u672a\u9192\uff0c\u8fd9\u65f6\u6b63\u5728\u9662\u4e2d\u8fce\u7740\u671d\u6c14\u5410\u7eb3\uff0c\u4e00\u89c1\u718a\u501c\u8fd9\u4e2a\u6837\u5b50\u4ece\u5e97\u5916\u8dd1\u4e86\u56de \u6765\uff0c\u4e5f\u89c9\u5f97\u5947\u602a\uff0c\u95ee\u9053\uff1a\u201c\u5927\u54e5\u5230\u54ea\u53bb\u4e86\uff0c\u600e\u4e48\u978b\u5b50\u4e5f\u6ca1\u6709\u7a7f\uff0c\u624b\u91cc\u8fd8\u62ff\u7740\u67c4\u5251\uff1f\u201d \u3000\u3000\u718a\u501c\u7b11\u7740\u5c06\u65b9\u624d\u6240\u9047\u7684\u4e8b\uff0c\u5411\u5c1a\u672a\u660e\u7b80\u5355\u8bf4\u4e86\u4e00\u4e0b\uff0c\u5c1a\u672a\u660e\u4e5f\u89c9\u5f97\u6709\u8da3\uff0c\u7b11\u9053\uff1a\u201c\u50cf\u8fd9 \u6837\u7684\u8bef\u4f1a\uff0c\u6211\u5012\u4e5f\u613f\u610f\u9047\u4e0a\u51e0\u6b21\u3002\u201d \u3000\u3000\u4e24\u4eba\u6b63\u8c08\u7b11\u95f4\uff0c\u90a3\u53f6\u8001\u5927\u4e5f\u8d70\u4e86\u51fa\u6765\uff0c\u795e\u6001\u751a\u662f\u614c\u5f20\uff0c\u4f46\u89c1\u4e86\u718a\u501c\uff0c\u5374\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60 \u5df2\u7ecf\u8dd1\u5230\u9662\u5b50\u91cc\u6765\u4e86\uff0c\u6628\u5929\u53ef\u559d\u9189\u4e86\u5427\uff1f\u201d \u3000\u3000\u718a\u501c\u7b11\u7740\u8bf4\u9053\uff1a\u201c\u4e0b\u6b21\u6211\u518d\u4e5f\u4e0d\u559d\u90a3\u4e48\u591a\u9152\u4e86\uff0c\u73b0\u5728\u8fd8\u6709\u70b9\u9152\u6c14\u5462\u3002\u201d \u3000\u3000\u53f6\u8001\u5927\u53c8\u7b11\u7740\u8bf4\u9053\uff1a\u201c\u6211\u8bf4\u4f60\u4e5f\u662f\uff0c\u4eca\u5929\u65e9\u4e0a\u5c0f\u4e2b\u5934\u9001\u4e1c\u897f\u5230\u4f60\u4eec\u623f\u4e2d\u53bb\uff0c\u770b\u89c1\u4f60\u4eec\u4fe9 \u5168\u4e0d\u5728\uff0c\u6211\u8fd8\u4ee5\u4e3a\u4f60\u4eec\u5931\u8e2a\u4e86\u3002\u201d \u3000\u3000\u718a\u501c\u4ee5\u4e3a\u4ed6\u6240\u8bf4\u7684\u201c\u4f60\u4eec\u4fe9\u201d\uff0c\u662f\u6307\u4ed6\u548c\u5c1a\u672a\u660e\u4e24\u4eba\uff0c\u4fbf\u8bf4\uff1a\u201c\u4ed6\u867d\u6ca1\u6709\u5931\u8e2a\uff0c\u6211\u53ef\u771f \u5931\u8e2a\u4e86\u8001\u534a\u5929\uff0c\u5dee\u70b9\u513f\u56de\u4e0d\u6765\u5462\u3002\u201d \u3000\u3000\u53f6\u8001\u5927\u8bf4\u9053\uff1a\u201c\u6211\u771f\u4f69\u670d\u4f60\u4e86\uff0c\u4f60\u5230\u5e95\u5f04\u4ec0\u4e48\u7384\u865a\uff0c\u6628\u5929\u4f60\u521a\u8bf4\u590f\u59d1\u5a18\u4f24\u52bf\u5f88\u91cd\uff0c\u4eca\u5929 \u4e00\u5927\u65e9\u4f60\u5c31\u628a\u4eba\u5bb6\u5e26\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u718a\u501c\u542c\u4e86\uff0c\u8fd9\u4e00\u60ca\u5374\u975e\u540c\u5c0f\u53ef\uff0c\u5fd9\u95ee\u9053\uff1a\u201c\u600e\u4e48\uff0c\u5979\u4e0d\u5728\u5c4b\u91cc\uff1f\u201d \u3000\u3000\u53f6\u8001\u5927\u4e5f\u5947\u9053\uff1a\u201c\u600e\u4e48\uff0c\u5979\u6ca1\u6709\u548c\u4f60\u5728\u4e00\u8d77\uff1f\u5c4b\u91cc\u6ca1\u6709\u4eba\u5440\uff01\u201d \u3000\u3000\u718a\u501c\u8bdd\u4e5f\u4e0d\u8bf4\u3001\u7acb\u523b\u4fbf\u5f80\u590f\u82b8\u6240\u4f4f\u7684\u623f\u4e2d\u51b2\u53bb\u3002 \u3000\u3000\u590f\u82b8\u7684\u5e8a\u8925\u4ecd\u7136\u51cc\u4e71\u7740\uff0c\u4f46\u662f\u5e8a\u4e0a\u5df2\u65e0\u4eba\u8ff9\u3002 \u3000\u3000\u5c1a\u672a\u660e\u4e0e\u53f6\u8001\u5927\u4e5f\u8d76\u8fdb\u623f\u6765\uff0c\u53f6\u8001\u5927\u4e5f\u7740\u6025\u4ed6\u8bf4\uff1a\u201c\u600e\u4e48\uff0c\u590f\u59d1\u5a18\u771f\u7684\u5931\u8e2a\u4e86\uff1f\u201d \u3000\u3000\u5c1a\u672a\u660e\u773c\u795e\u56db\u626b\uff0c\u5ffd\u7136\u77a5\u89c1\u5c4b\u9876\u6b63\u6881\u4e0a\uff0c\u98d8\u52a8\u7740\u4e00\u5f20\u674f\u9ec4\u8272\u7684\u7eb8\u6761\uff0c\u5fd9\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60 \u770b\u90a3\u662f\u4ec0\u4e48\uff0c\u4f1a\u4e0d\u4f1a\u662f\u590f\u59d1\u5a18\u7559\u4e0b\u7684\u7eb8\u6761\uff1f\u201d \u3000\u3000\u718a\u501c\u660e\u77e5\u9053\u7edd\u4e0d\u53ef\u80fd\uff0c\u590f\u82b8\u8eab\u53d7\u91cd\u4f24\uff0c\u600e\u80fd\u7a9c\u5230\u6881\u4e0a\u53bb\u8d34\u8fd9\u5f20\u6761\u5b50\uff0c\u800c\u4e14\u66f4\u65e0\u6b64\u5fc5\u8981\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u6447\u4e86\u6447\u5934\uff0c\u4ed6\u539f\u60f3\u8bf4\u8fd9\u53ef\u80fd\u662f\u5c4b\u4e2d\u65e9\u6709\u7684\uff0c\u4f46\u662f\u53f6\u8001\u5927\u7a81\u7136\u8bf4\uff1a\u201c\u8fd9\u6761\u5b50\u6211\u770b\u5012 \u6765\u5f97\u975e\u5e38\u8e4a\u8df7\uff0c\u5c4b\u4e2d\u5148\u524d\u4e95\u6ca1\u6709\u7684\u3002\u201d \u3000\u3000\u5c1a\u672a\u660e\u4e00\u542c\uff0c\u66f4\u4e0d\u7b54\u8bdd\uff0c\u5fae\u4e00\u7eb5\u8eab\uff0c\u5411\u90a3\u7eb8\u6761\u5904\u7a9c\u53bb\u3002\u54ea\u77e5\u4ed6\u4eba\u5728\u7a7a\u4e2d\uff0c\u5374\u53d1\u73b0\u718a\u501c\u6b63 \u4e5f\u7535\u5149\u706b\u77f3\u822c\u5411\u90a3\u7eb8\u6761\u7a9c\u53bb\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u5728\u7a7a\u4e2d\u731b\u7136\u505c\u987f\uff0c\u4e00\u6362\u771f\u6c14\uff0c\u4eba\u4fbf\u98d8\u7136\u5411\u4e0b\u9762\u843d\uff0c\u4ed6\u8eab\u5f62\u867d\u4e0d\u5982\u718a\u501c\u90a3\u4e48\u5b89\u7965\u800c \u4f73\u5999\uff0c\u4f46\u5374\u8f7b\u7075\u65e0\u6bd4\uff0c\u8eab\u4f53\u6bcf\u4e00\u90e8\u4efd\u90fd\u88ab\u6781\u5468\u5bc6\u5730\u8fd0\u7528\u7740\uff0c\u50cf\u662f\u4e00\u53ea\u7075\u96c0\u3002 \u3000\u3000\u4ed6\u843d\u5728\u5730\u4e0a\u540e\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u5374\u89c1\u718a\u501c\u4ecd\u7136\u505c\u7559\u5728\u6881\u4e0a\uff0c\u4ed6\u4e00\u53ea\u624b\u642d\u5728\u6881\u4e0a\uff0c\u8eab\u4f53\u4fbf\u5e73\u7a33 \u5730\u5782\u76f4\u5728\u7a7a\u4e2d\uff0c\u53e6\u4e00\u53ea\u624b\u5374\u6b63\u62ff\u7740\u90a3\u674f\u9ec4\u8272\u7684\u7eb8\u7b3a\u5728\u7ec6\u7eab\u5730\u770b\u7740\uff0c\u9762\u8272\u663e\u5f97\u751a\u662f\u5fe7\u8651\uff0c\u4f46\u5374 \u4e0d\u60ca\u60f6\u4e86\u3002 \u3000\u3000\u7247\u65f6\uff0c\u718a\u501c\u50cf\u4e00\u56e2\u98de\u843d\u7684\u67f3\u7d6e\uff0c\u843d\u5230\u5730\u4e0a\uff0c\u773c\u4e2d\u6ee1\u662f\u601d\u8651\u4e4b\u8272\uff0c\u65e0\u8a00\u5730\u5c06\u5b57\u6761\u9012\u7ed9\u53f6\u8001 \u5927\uff0c\u5c1a\u672a\u660e\u5fd9\u4e5f\u51d1\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u5c1a\u672a\u660e\u4e00\u89c1\u90a3\u5b57\u6761\u4e0a\u7684\u5b57\u7adf\u662f\u7528\u6731\u7b14\u5199\u4e0a\u7684\uff0c\u5fc3\u4e2d\u4fbf\u660e\u767d\u4e86\u51e0\u5206\uff0c\u4ed6\u53ea\u89c1\u4e0a\u9762\u5199\u7740\uff1a \u201c\u5179\u6709\u5973\u5b50\u59d3\u590f\u540d\u82b8\u8005\uff0c\u64c5\u81ea\u53d6\u6211\u6b66\u5f53\u638c\u6559\u5386\u4ee3\u6240\u4f20\u4e4b\u2018\u4e5d\u5b98\u8fde\u73af\u65d7\uff0c\u4f3c\u6709\u610f\u5bf9\u6211\u6b66\u5f53\u4e0d \u656c\uff0c\u4eca\u5df2\u5c06\u8be5\u5973\u5b50\u64d2\u83b7\uff0c\u5f97\u6c5f\u5357\u5973\u4fa0\u4e1c\u65b9\u745b\u4e4b\u52a9\uff0c\u89e3\u4e0a\u6b66\u5f53\uff0c\u542c\u5019\u638c\u6559\u771f\u4eba\u53d1\u843d\uff0c\u7279\u6b64\u5b57 \u8c15\u3002\u201d\u4e00\u9762\u7684\u5177\u540d\u662f\u5199\u7740\u201c\u6b66\u5f53\u5c71\uff0c\u638c\u6559\u771f\u4eba\u5ea7\u524d\u56db\u5927\u62a4\u6cd5\u201d\u3002\u5c1a\u672a\u660e\u7709\u5fc3\u4e00\u76b1\uff0c\u6b63\u60f3\u53d1 \u8bdd\uff0c\u90a3\u53f6\u8001\u5927\u5374\u4e00\u6311\u53cc\u7709\u6012\u9053\uff1a\u201c\u8fd9\u6b66\u5f53\u56db\u5b50\u4e5f\u672a\u514d\u6b3a\u4eba\u592a\u751a\uff0c\u5c31\u7b97\u5b98\u5e9c\u62ff\u4eba\uff0c\u4e5f\u6ca1\u6709\u542c\u8bf4 \u534a\u591c\u91cc\u5c06\u4e00\u4e2a\u53d7\u4e86\u4f24\u7684\u5973\u5b50\u4ece\u5e8a\u4e0a\u67b6\u8d70\u4e86\uff0c\u4ed6\u6b66\u5f53\u6d3e\u7b97\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d \u3000\u3000\u5c1a\u672a\u660e\u4e0e\u53f6\u8001\u5927\u76f8\u8bc6\u4ee5\u6765\uff0c\u5c1a\u672a\u89c1\u8fc7\u4ed6\u5982\u6b64\u8bf4\u8bdd\uff0c\u77e5\u9053\u4ed6\u4e5f\u52a8\u4e86\u771f\u6012\u3002 \u3000\u3000\u90a3\u53f6\u8001\u5927\u53cc\u624b\u4e00\u5206\uff0c\u5c06\u90a3\u5b57\u6761\u6495\u5f97\u7c89\u788e\uff0c\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u5b57\u8c15\u4e0d\u5b57\u8c15\uff0c\u6b66\u5f53\u56db\u5b50\u51ed\u7740\u4ec0\u4e48 \u5c31\u6562\u5982\u6b64\u9a84\u72c2\uff0c\u6211\u53f6\u8001\u5927\u5012\u8981\u89c1\u8bc6\u89c1\u8bc6\u3002\u201d \u3000\u3000\u90a3\u718a\u501c\u4e00\u76f4\u6ca1\u6709\u8bf4\u8bdd\uff0c\u6b64\u523b\u7a81\u7136\u9053\uff1a\u201c\u5176\u5b9e\u82b8\u59b9\u88ab\u89e3\u6b66\u5f53\u5c71\uff0c\u6211\u5012\u653e\u5fc3\u4e9b\u4e86\uff0c\u5148\u524d\u6211\u8fd8 \u6015\u5979\u906d\u4e86\u4ec0\u4e48\u4e0d\u6d4b\uff0c\u60f3\u90a3\u6b66\u5f53\u6d3e\uff0c\u5230\u5e95\u662f\u6b66\u6797\u6b63\u5b97\uff0c\u8c05\u4e5f\u4e0d\u4f1a\u5bf9\u4e00\u5973\u5b50\u5982\u4f55\u7684\uff0c\u5509\uff0c\u4e8b\u60c5\u591a \u4e48\u51d1\u5de7\uff0c\u6211\u82e5\u4e0d\u662f\u90a3\u65f6\u51fa\u53bb\u4e86\uff0c\u4e5f\u4e0d\u4f1a\u6709\u8fd9\u79cd\u4e8b\u53d1\u751f\u3002\u201d \u3000\u3000\u5c1a\u672a\u660e\u8138\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u5c0f\u5f1f\u4e5f\u60ed\u6127\u5f97\u5f88\uff0c\u5c31\u5728\u8fd9\u680b\u623f\u5b50\u91cc\uff0c\u53d1\u751f\u4e86\u6b64\u4e8b\uff0c\u5c0f\u5f1f\u7adf\u7761\u5f97\u50cf \u6b7b\u4eba\u4f3c\u7684\uff0c\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u718a\u501c\u5fd9\u9053\uff1a\u201c\u8d24\u5f1f\u4e5f\u4e0d\u7528\u8bf4\u8fd9\u6837\u7684\u8bdd\uff0c\u73b0\u5728\u552f\u4e00\u9700\u8981\u505a\u7684\u4e8b\uff0c\u5c31\u662f\u8be5\u60f3\u529e\u6cd5\u53bb\u89e3\u51b3\u6b64 \u4e8b\uff0c\u5509\uff0c\u8bf4\u826f\u5fc3\u8bdd\uff0c\u82b8\u59b9\u5f53\u65e5\u4e5f\u786e\u6709\u4e0d\u662f\u4e4b\u5904\uff0c\u4f46\u4ed6\u4eec\u6b66\u5f53\u6d3e\u4e5f\u672a\u514d\u592a\u72e0\u4e86\uff0c\u65e2\u7136\u5c06\u4eba\u51fb \u4f24\uff0c\u8fd8\u8981\u6765\u8fd9\u4e48\u4e00\u5957\uff0c\u8bf4\u4e0d\u5f97\u5230\u65f6\u5019\u53ea\u6709\u548c\u4ed6\u4eec\u53cd\u8138\u4e86\u3002\u201d \u3000\u3000\u53f6\u8001\u5927\u9053\uff1a\u201c\u90a3\u4e0a\u6240\u5199\u7684\u6c5f\u5357\u5927\u4fa0\uff0c\u662f\u4e0d\u5c31\u662f\u90a3\u98de\u7075\u5821\u4e3b\u4e1c\u65b9\u7075\u7684\u59b9\u59b9\uff0c\u600e\u4e48\u5979\u4e5f\u6765\u8d9f \u4e0a\u8fd9\u4e00\u8d9f\u6d51\u6c34\u3002\u201d \u3000\u3000\u718a\u501c\u82e6\u7b11\u4e86\u4e00\u4e0b\uff0c\u4ed6\u77e5\u9053\u91cc\u9762\u5fc5\u7136\u53c8\u5939\u7f20\u7740\u4e00\u4e9b\u513f\u5973\u79c1\u60c5\uff0c\u4f46\u4ed6\u60f3\u4e1c\u65b9\u7075\u4e00\u5411\u4e16\u6545\uff0c\u600e \u7684\u8ba9\u4ed6\u59b9\u59b9\u505a\u51fa\u6b64\u4e8b\u3002 \u3000\u3000\u4ed6\u54ea\u91cc\u77e5\u9053\u4e1c\u65b9\u7075\u5374\u6839\u672c\u4e0d\u77e5\u6b64\u4e8b\u3002 \u3000\u3000\u539f\u6765\u5f53\u665a\u4e1c\u65b9\u7075\u5144\u59b9\u5728\u5c4b\u9876\u4e0a\u7684\u65f6\u5019\uff0c\u590f\u82b8\u55ef\u4e86\u4e00\u58f0\uff0c\u4e1c\u65b9\u7075\u606f\u4e8b\u5b81\u4eba\uff0c\u5f3a\u7740\u5c06\u59b9\u59b9\u62c9 \u8d70\u4e86\u3002 \u3000\u3000\u4f46\u90a3\u4e1c\u65b9\u745b\u5374\u4e5f\u662f\u4e2a\u4e03\u7a8d\u73b2\u73d1\u4e4b\u4eba\uff0c\u5fc3\u77e5\u5c4b\u4e0b\u5fc5\u6709\u53e4\u602a\uff0c\u4e24\u4eba\u987a\u5230\u5e97\u623f\u65f6\uff0c\u90a3\u6b66\u5f53\u56db\u5b50 \u6b63\u5728\u5927\u6012\uff0c\u58f0\u8a00\u5fc5\u8981\u627e\u7740\u718a\u501c\u3001\u590f\u82b8\u4e24\u4eba\u3002 \u3000\u3000\u539f\u6765\u718a\u501c\u6551\u8d70\u590f\u82b8\u540e\uff0c\u4e1c\u65b9\u5144\u59b9\u968f\u5373\u8ffd\u53bb\uff0c\u6b66\u5f53\u56db\u5b50\u5374\u89c9\u5f97\u4eba\u5bb6\u65e2\u5df2\u53d7\u4e86\u91cd\u4f24\uff0c\u6b64\u4e8b\u4e5f \u7b97\u53ef\u4ee5\u626f\u8fc7\u4e86\uff0c\u9042\u4ecd\u7559\u5728\u9662\u4e2d\u3002 \u3000\u3000\u51cc\u4e91\u5b50\u6027\u60c5\u672c\u505a\uff0c\u4eba\u53c8\u597d\u80dc\uff0c\u6b64\u523b\u56de\u8eab\u5bf9\u4e39\u9633\u5b50\u9053\uff1a\u201c\u5e08\u5144\uff0c\u4f60\u770b\u6211\u7684\u5251\u6cd5\u53ef\u53c8\u8fdb\u6b65\u4e86 \u4e9b\uff0c\u8fd9\u4e00\u62db\u7528\u5f97\u8fd8\u4e0d\u9519\u5427\uff1f\u201d \u3000\u3000\u4ed6\u8bdd\u521a\u8bf4\u5b8c\uff0c\u5ffd\u89c9\u8eab\u540e\u4f3c\u6709\u6697\u5668\u7834\u7a7a\u4e4b\u58f0\uff0c\u4f46\u624b\u6cd5\u5374\u751a\u62d9\u52a3\u3002 \u3000\u3000\u51cc\u4e91\u5b50\u6b66\u529f\u9ad8\u5f3a\uff0c\u5bf9\u6697\u5668\u4e5f\u662f\u5927\u5927\u7684\u884c\u5bb6\uff0c\u6b64\u523b\u542c\u90a3\u98ce\u58f0\uff0c\u6765\u52bf\u751a\u7f13\uff0c\u800c\u4e14\u65e0\u751a\u52b2\u529b\uff0c \u624b\u6cd5\u666e\u901a\u5f97\u5f88\uff0c\u600e\u4f1a\u653e\u5728\u5fc3\u4e0a\uff0c\u968f\u624b\u888d\u8896\u4e00\u62c2\uff0c\u4fbf\u5c06\u90a3\u4e9b\u6697\u5668\u62c2\u5f00\uff0c\u8f6c\u8eab\u6b63\u60f3\u53d1\u8bdd\u3002 \u3000\u3000\u54ea\u77e5\u4ed6\u521a\u4e00\u8eab\uff0c\u5374\u53c8\u6709\u4e00\u7c92\u77f3\u5b50\u5411\u4ed6\u9762\u95e8\u6253\u6765\uff0c\u90a3\u77f3\u5b50\u975e\u4f46\u65e0\u58f0\u65e0\u97f3\uff0c\u6765\u52bf\u4e4b\u5feb\uff0c\u66f4\u662f \u60ca\u4eba\uff0c\u662f\u88ab\u4eba\u7528\u4e00\u79cd\u5185\u5bb6\u7684\u7edd\u9876\u9634\u67d4\u4e4b\u529b\u6240\u53d1\u51fa\u7684\uff0c\u800c\u4e14\u90e8\u4f4d\u751a\u5201\uff0c\u597d\u50cf\u65e9\u5c31\u77e5\u9053\u51cc\u4e91\u5b50\u4f1a \u8f6c\u8138\u5230\u8fd9\u91cc\u6765\uff0c\u8fd9\u7c92\u77f3\u5b50\u5c31\u5728\u90a3\u5730\u65b9\u7b49\u7740\u4f3c\u7684\u3002 \u3000\u3000\u51cc\u4e91\u5b50\u5927\u610f\u4e4b\u4e0b\uff0c\u53d1\u6697\u5668\u4e4b\u4eba\u624b\u6cd5\u53c8\u8d85\u51e1\u5165\u5723\uff0c\u8fd9\u4e9b\u60c5\u51b5\uff0c\u51cc\u4e91\u5b50\u7109\u80fd\u518d\u8eb2\uff0c\u5427\u7684\u4e00 \u58f0\uff0c\u9f3b\u6881\u4e0a\u88ab\u90a3\u77f3\u5b50\u6253\u4e2a\u6b63\u7740\u3002 \u3000\u3000\u5c4b\u9876\u4e0a\u51b7\u51b7\u4e00\u7b11\uff0c\u4e00\u4e2a\u6781\u4e3a\u8f7b\u8511\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u5c11\u8bf4\u5927\u8bdd\u3002\u201d \u3000\u3000\u8fd9\u9662\u4e2d\u4ff1\u662f\u8eab\u6000\u7edd\u6280\u4e4b\u4eba\uff0c\u53cd\u5e94\u672c\u5feb\uff0c\u8eab\u5f62\u52a8\u5904\u5168\u4e0a\u4e86\u5c4b\u9876\uff0c\u4f46\u89c1\u661f\u6708\u5728\u5929\uff0c\u56db\u91ce\u832b \u7136\uff0c\u8fde\u6761\u4eba\u5f71\u90fd\u6ca1\u6709\u770b\u89c1\u3002\u3001\u6b66\u5f53\u56db\u5b50\u5728\u6c5f\u6e56\u4e2d\u5730\u4f4d\u6781\u5c0a\uff0c\u6b66\u5f53\u6d3e\u53c8\u662f\u4e2d\u539f\u5251\u6d3e\u4e4b\u9996\uff0c\u4ed6\u4eec \u54ea\u91cc\u5403\u8fc7\u8fd9\u79cd\u5927\u4e8f\uff0c\u5c24\u5176\u662f\u51cc\u4e91\u5b50\uff0c\u7d20\u6765\u5fc3\u9ad8\u6c14\u505a\uff0c\u76ee\u4e2d\u65e0\u4eba\uff0c\u5982\u4eca\u4e0d\u660e\u4e0d\u767d\u5403\u4e86\u82e6\u5934\uff0c\u8fde \u4eba\u5bb6\u7684\u5f71\u5b50\u90fd\u6ca1\u6709\u770b\u201c\u5230\u3002\u4ed6\u4eec\u81ea\u662f\u4e0d\u77e5\u8fd9\u662f\u6bd2\u5fc3\u795e\u9b54\u7684\u4faf\u751f\u6240\u4e3a\uff0c\u4e39\u9633\u5b50\u66f4\u6b66\u65ad\u4ed6\u8bf4\u9053\uff1a \u201c\u6b64\u5730\u4e00\u591c\u4e4b\u95f4\uff0c\u7edd\u4e0d\u4f1a\u6765\u5982\u8bb8\u591a\u9ad8\u4eba\uff0c\u60f3\u6b64\u4eba\u8eab\u624b\u4e4b\u901f\uff0c\u5185\u529b\u4e4b\u5999\uff0c\u6211\u770b\u9664\u4e86\u718a\u501c\u4e4b\u5916\uff0c \u7edd\u975e\u4ed6\u4eba\u3002\u201d \u3000\u3000\u51cc\u4e91\u5b50\u6012\u9053\uff1a\u201c\u8d77\u5148\u6211\u89c1\u90a3\u718a\u501c\u5e74\u8f7b\u6b63\u6d3e\uff0c\u6b66\u529f\u53c8\u5f97\u81ea\u771f\u4f20\uff0c\u5bf9\u4ed6\u751a\u662f\u7231\u60dc\uff0c\u60f3\u4e0d\u5230\u4ed6 \u7adf\u5982\u6b64\u5351\u9119\uff0c\u5bf9\u6211\u65bd\u4e0b\u4e86\u8fd9\u6837\u7684\u6697\u7b97\uff0c\u8fd9\u6837\u4e00\u6765\uff0c\u6211\u82e5\u4e0d\u5c06\u4ed6\u6574\u4e2a\u60e8\u7684\uff0c\u4ed6\u4e0d\u77e5\u9053\u6211\u6b66\u5f53\u56db \u5b50\u7684\u5389\u5bb3\u3002\u8fd9\u6b66\u5f53\u56db\u5b50\u867d\u662f\u51fa\u5bb6\u4eba\uff0c\u4f46\u8eab\u5728\u6b66\u6797\uff0c\u54ea\u91cc\u8fd8\u6709\u51fa\u5bb6\u4eba\u7684\u98ce\u5ea6\uff0c\u4e1c\u65b9\u7075\u5144\u59b9\u56de\u6765 \u65f6\uff0c\u4ed6\u4eec\u6b63\u5728\u6012\u9a82\u7740\u718a\u501c\u548c\u590f\u82b8\u3002\u4e1c\u65b9\u745b\u5bf9\u718a\u501c\u60c5\u6df1\u4e00\u5f80\uff0c\u4f46\u718a\u501c\u5374\u5904\u5904\u8eb2\u7740\u5979\uff0c\u800c\u5177\u4e5f\u770b \u7740\u718a\u501c\u548c\u590f\u82b8\u540c\u884c\uff0c\u53c8\u5192\u7740\u6781\u5927\u7684\u5371\u9669\u5c06\u590f\u82b8\u6551\u4e86\u51fa\u6765\uff0c\u5973\u5b69\u5b50\u5fc3\u773c\u672c\u7a84\uff0c\u7231\u6781\u751f\u6068\uff0c\u6068\u4e0d \u5f97\u6b66\u5f53\u56db\u5b50\u8fde\u718a\u501c\u4e5f\u4e00\u5757\u513f\u5bf9\u4ed8\u4e86\uff0c\u590f\u82b8\u66f4\u662f\u88ab\u5979\u6068\u5f97\u75d2\u75d2\u7684\uff0c\u56e0\u7231\u751f\u5992\uff0c\u539f\u662f\u5e38\u7406\u3002\u6b64\u65f6 \u5979\u4fbf\u4fcf\u6084\u5730\u53c8\u6e9c\u4e86\u51fa\u6765\uff0c\u518d\u9002\u624d\u542c\u89c1\u201d\u6664\u201c\u4e86\u4e00\u58f0\u7684\u5730\u65b9\u53bb\u67e5\u770b\u3002\u8fd9\u65f6\u5019\u6b63\u662f\u718a\u501c\u548c\u590f\u82b8\u5728 \u627e\u7740\u5e97\u62db\u4e4b\u9645\uff0c\u4e1c\u65b9\u745b\u8fd8\u770b\u5230\u718a\u501c\u7d27\u7d27\u62b1\u7740\u590f\u82b8\uff0c\u590f\u82b8\u7684\u4e00\u53cc\u624b\u8fd8\u52fe\u7740\u718a\u501c\u7684\u8116\u5b50\uff0c\u66f4\u662f\u6c14 \u5f97\u8981\u6b7b\u3002\u4f46\u5979\u5374\u4e0d\u6562\u518d\u5f80\u524d\u8d70\uff0c\u4e5f\u4e0d\u6562\u53d1\u51fa\u4e00\u4e1d\u58f0\u54cd\uff0c\u6015\u60ca\u52a8\u4e86\u718a\u501c\u3002\u63a5\u7740\u5979\u770b\u5230\u718a\u501c\u7eb5\u8eab \u8fdb\u4e86\u4e00\u5bb6\u5e97\u94fa\uff0c\u5c31\u672a\u518d\u51fa\uff0c\u6b64\u65f6\u5929\u8272\u5df2\u4eae\uff0c\u5979\u8fdc\u8fdc\u671b\u6e05\u4e86\u90a3\u5e97\u7684\u62db\u724c\uff0c\u624d\u56de\u5230\u5ba2\u6808\u53bb\u3002\u81ea \u7136\uff0c\u4e1c\u65b9\u7075\u5c11\u4e0d\u5f97\u8981\u95ee\u5979\u8dd1\u5230\u4ec0\u4e48\u5730\u65b9\u53bb\u4e86\uff0c\u4e1c\u65b9\u745b\u5fc3\u7075\u5634\u5de7\uff0c\u8bf4\u4e86\u4e00\u4e2a\u8c0e\uff0c\u4e1c\u65b9\u7075\u4e5f\u6ca1\u6709 \u60f3\u5230\u4f1a\u751f\u4ec0\u4e48\u4e8b\u6545\uff0c\u4fbf\u4e5f\u7f62\u4e86\u3002\u5f53\u5929\u4e0b\u5348\uff0c\u4e1c\u65b9\u7075\u6025\u7740\u56de\u53bb\u770b\u82e5\u5170\uff0c\u4fbf\u8981\u4e1c\u65b9\u745b\u4e00\uff0c\u8d77\u56de \u53bb\uff0c\u4e1c\u65b9\u745b\u5374\u8bf4\u8981\u53bb\u627e\u5ce8\u5e3d\u53cc\u5c0f\u73a9\u73a9\uff0c\u53eb\u4e1c\u65b9\u7075\u4e00\u4eba\u5148\u56de\u53bb\u3002\u4e1c\u65b9\u7075\u62ff\u4ed6\u8fd9\u4f4d\u59b9\u59b9\u4e00\u5411\u65e0\u751a \u529e\u6cd5\uff0c\u800c\u4e14\u4e1c\u65b9\u745b\u7684\u6b66\u529f\u9632\u8eab\u7edd\u65e0\u95ee\u9898\uff0c\u518d\u52a0\u4e0a\u81ea\u5df1\u5728\u6b66\u6797\u4e2d\u7684\u5730\u4f4d\u9762\u5b50\uff0c\u4e8e\u662f\u4ed6\u5c31\u653e\u5fc3\u4e00 \u4eba\u8d70\u4e86\u3002\u4e1c\u65b9\u7075\u4e00\u8d70\uff0c\u4e1c\u65b9\u745b\u5c31\u5c06\u590f\u82b8\u3001\u718a\u501c\u85cf\u8eab\u7684\u5730\u65b9\uff0c\u544a\u8bc9\u4e86\u6b66\u5f53\u56db\u5b50\u3002\u665a\u4e0a\uff0c\u4e1c\u65b9\u745b \u5e26\u7740\u6b66\u5f53\u56db\u5b50\u5230\u718a\u501c\u548c\u590f\u82b8\u7684\u5b58\u8eab\u4e4b\u5904\uff0c\u5728\u8def\u4e0a\uff0c\u4ed6\u4eec\u7a81\u7136\u770b\u89c1\u4e24\u6761\u4eba\u5f71\uff0c\u4ee5\u65e0\u6bd4\u7684\u901f\u5ea6\u8d70 \u5411\u57ce\u5916\uff0c\u4e39\u9633\u4e8e\u6697\u53f9\u9053\uff1a\u201c\u770b\u6765\u6b66\u6797\u4e4b\u4e2d\uff0c\u771f\u662f\u5927\u6709\u5947\u4eba\uff0c\u5c31\u5728\u8fd9\u5c0f\u5c0f\u7684\u5730\u65b9\uff0c\u5c45\u7136\u53c8\u53d1\u73b0 \u6b64\u7b49\u4eba\u7269\uff0c\u8eab\u624b\u5374\u6bd4\u6211\u7b49\u9ad8\u51fa\u51e0\u8bb8\u4e86\u3002\u201d \u3000\u3000\u65e0\u5de7\u4e0d\u5de7\u5730\uff0c\u90a3\u4e24\u6761\u4eba\u5f71\u5374\u6b63\u662f\u6bd2\u5fc3\u795e\u9b54\u548c\u718a\u501c\u4e24\u4eba\uff0c\u662f\u4ee5\u4ed6\u4eec\u5230\u65f6\uff0c\u718a\u501c\u5df2\u4e0d\u5728\u5e97\u4e2d \u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u5728\u53f6\u6c0f\u5144\u5f1f\u7684\u5e97\u4e2d\uff0c\u6781\u5c0f\u5fc3\u7684\u63a2\u5bdf\u4e86\u4e00\u904d\uff0c\u5c1a\u672a\u660e\u53ca\u53f6\u6c0f\u5144\u5f1f\u3001\u9a6c\u6c0f\u53cc\u6770\uff0c\u6b63\u56e0\u9152 \u9189\u800c\u719f\u7761\uff0c\u5e76\u672a\u53d1\u89c9\u8fd9\u51e0\u4eba\u7684\u884c\u52a8\u3002 \u3000\u3000\u751a\u81f3\u5f53\u51cc\u4e91\u5b50\u6545\u610f\u5f04\u51fa\u58f0\u97f3\u7684\u65f6\u5019\uff0c\u5c4b\u91cc\u4e5f\u6ca1\u6709\u4efb\u4f55\u53cd\u5e94\uff0c\u51cc\u4e91\u5b50\u5947\u602a\u9053\uff1a\u201c\u718a\u501c\u6b66\u529f \u6781\u9ad8\uff0c\u600e\u7684\u8033\u76ee\u5374\u8fd9\u6837\u8fdf\u949d\uff1f\u201d \u3000\u3000\u6b64\u65f6\u504c\u5927\u7684\u4e00\u680b\u623f\u5c4b\u91cc\uff0c\u9664\u4e86\u4e2b\u5934\u5c0f\u53ae\u5916\uff0c\u552f\u4e00\u6e05\u9192\u7684\u53ea\u6709\u590f\u82b8\u4e00\u4eba\uff0c\u5979\u542c\u5230\u5916\u9762\u7684\u4eba \u58f0\uff0c\u5374\u4ee5\u4e3a\u662f\u718a\u501c\u3002 \u3000\u3000\u4e8e\u662f\u5979\u6311\u4eae\u4e86\u706f\uff0c\u6b63\u51fa\u53bb\u770b\u770b\uff0c\u4f46\u80f8\u8179\u4e4b\u95f4\u4ecd\u5728\u9690\u9690\u53d1\u75db\u3002 \u3000\u3000\u5979\u770b\u89c1\u7a97\u5b50\u4ecd\u7136\u5f00\u7740\u672a\u5173\uff0c\u53c8\u60f3\u53bb\u5173\u7a97\u5b50\uff0c\u54ea\u77e5\u98ce\u58f0\u5506\u7136\uff0c\u51cc\u4e91\u5b50\u548c\u4e1c\u65b9\u745b\u5df2\u7531\u7a97\u53e3\u7a9c \u4e86\u8fdb\u6765\uff0c\u5979\u5927\u5403\u4e00\u60ca\uff0c\u8eab\u53d7\u91cd\u4f24\uff0c\u52a8\u5f39\u4e0d\u5f97\u3002 \u3000\u3000\u6b64\u523b\u5979\u552f\u4e00\u80fd\u505a\u7684\u4e8b\uff0c\u5c31\u662f\u5f20\u53e3\u547c\u5524\uff0c\u54ea\u77e5\u5979\u58f0\u97f3\u8fd8\u672a\u53d1\u51fa\uff0c\u4e1c\u65b9\u745b\u5a07\u8eaf\u4e00\u95ea\uff0c\u7535\u4e5f\u4f3c \u5730\u51fa\u624b\u70b9\u4e86\u5979\u7684\u8033\u65c1\u7684\u201c\u7075\u98de\u201d\u7a74\u3002 \u3000\u3000\u51cc\u4e91\u5b50\u968f\u5373\u95ea\u5165\u53e6\u4e00\u95f4\u5c4b\u5b50\u91cc\uff0c\u90a3\u6b63\u662f\u718a\u501c\u6240\u7761\u7684\uff0c\u51cc\u4e91\u5b50\u89c1\u5e8a\u4e0b\u653e\u7740\u53cc\u978b\uff0c\u5e8a\u4e0a\u7684\u4eba \u5374\u4e0d\u77e5\u53bb\u5411\u4e86\uff0c\u4ed6\u6697\u5fd6\u6b64\u5c4b\u5fc5\u662f\u718a\u501c\u6240\u7761\uff0c\u4f46\u4ed6\u4eba\u5462\uff1f \u3000\u3000\u4e1c\u65b9\u745b\u8fde\u88ab\u4e00\u5377\uff0c\u5c06\u590f\u82b8\u5a07\u602f\u602f\u7684\u8eab\u5b50\u6a2a\u653e\u5728\u80a9\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u8d70\u5427\u3002\u201d \u3000\u3000\u51cc\u4e91\u5b50\u9053\uff1a\u201c\u8fd8\u6709\u718a\u501c\u3002\u201d \u3000\u3000\u4e1c\u65b9\u745b\u9053\uff1a\u201c\u53ea\u8981\u6349\u4e86\u590f\u82b8\uff0c\u718a\u501c\u8fd8\u6015\u4e0d\u6765\u627e\u5979\u5417\uff1f\u201d \u3000\u3000\u51cc\u4e91\u5b50\u5fc3\u60f3\uff1a\u201c\u8fd9\u7c89\u8776\u679c\u7136\u5fc3\u601d\u7075\u654f\uff0c\u201d\u9042\u53d6\u51fa\u674f\u7eb8\u6731\u7b14\u5199\u4e0b\u4e86\u8fd9\u5f20\u6761\u5b50\uff0c\u4e5f\u6b63\u56e0\u4e3a\u662f \u4ed6\u5199\u7684\uff0c\u6240\u4ee5\u8bed\u6c14\u624d\u4f1a\u90a3\u4e48\u72c2\u5984\u3002 \u3000\u3000\u718a\u501c\u7b49\u4eba\u770b\u4e86\u4ed6\u4eec\u7559\u4e0b\u7684\u7eb8\u6761\uff0c\u53f6\u8001\u5927\u4e00\u95ee\u4e1c\u65b9\u745b\uff0c\u718a\u501c\u5c31\u60f3\u5230\u5176\u4e2d\u53c8\u53ef\u80fd\u7275\u6d89\u5230\u81ea\u5df1 \u548c\u4e1c\u65b9\u745b\u7684\u60c5\u611f\uff0c\u4e00\u65f6\u6ca1\u6709\u7b54\u8bdd\u3002 \u3000\u3000\u5c1a\u672a\u660e\u89c1\u4e86\uff0c\u6697\u5fd6\u9053\uff1a\u201c\u6211\u8fd9\u4e2a\u5927\u54e5\uff0c\u82f1\u4fca\u501c\u50a5\uff0c\u771f\u662f\u4eba\u5982\u5176\u540d\uff0c\u770b\u8fd9\u4e2a\u60c5\u5f62\uff0c\u4e1c\u65b9\u745b \u6a2a\u52a0\u4e00\u811a\uff0c\u8bf4\u4e0d\u5b9a\u662f\u5728\u5403\u590f\u82b8\u7684\u918b\u3002\u201d \u3000\u3000\u4e8e\u662f\u4ed6\u671b\u7740\u718a\u501c\u4e00\u7b11\u3002 \u3000\u3000\u718a\u501c\u88ab\u4ed6\u8fd9\u4e00\u7b11\uff0c\u6709\u4e9b\u8baa\u8baa\u7684\u4e0d\u597d\u610f\u601d\uff0c\u4f46\u4ed6\u968f\u5373\u60f3\u5230\u6b64\u4e8b\u7684\u4e25\u91cd\uff0c\u5c31\u8bf4\u9053\uff1a\u201c\u770b\u672a\u4e0d \u7ba1\u4f1a\u60f9\u51fa\u4ec0\u4e48\u540e\u679c\uff0c\u6211\u90fd\u8981\u5230\u6b66\u5f53\u5c71\u4e00\u884c\u7684\u4e86\u3002\u201d \u3000\u3000\u5c1a\u672a\u660e\u9053\uff1a\u201c\u8fd9\u4e2a\u5f53\u7136\uff0c\u6211\u4e5f\u4e0d\u5fc5\u8981\u8d76\u53e3\u4e24\u6cb3\uff0c\u6b63\u597d\u966a\u5927\u54e5\u4e00\u8d77\u53bb\u3002\u201d \u3000\u3000\u53f6\u8001\u5927\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u662f\u5728\u6211\u5144\u5f1f\u5904\u53d1\u751f\u7684\uff0c\u6211\u5144\u5f1f\u4e5f\u8981\u7b97\u4e0a\u4e00\u89d2\u3002\u201d \u3000\u3000\u718a\u501c\u9053\uff1a\u201c\u8fd9\u5012\u4e0d\u7528\u4e86\uff0c\u6709\u6211\u548c\u5c1a\u8d24\u5f1f\u4e00\u8d77\u53bb\uff0c\u5df2\u7ecf\u8db3\u591f\u5e94\u4ed8\u4e86\uff0c\u4f55\u51b5\u4f60\u7684\u4e8b\u60c5\u53c8\u591a\uff0c \u600e\u53ef\u4e3a\u8fd9\u5c0f\u4e8b\uff0c\u800c\u803d\u8bef\u4e86\u6b63\u4e8b\u201d\u53f6\u8001\u5927\u9053\uff1a\u201c\u8fd9\u6837\u4e5f\u597d\uff0c\u53ea\u662f\u4f60\u4e8c\u4eba\u4e07\u4e00\u6709\u4ec0\u4e48\u5e94\u4ed8\u4e0d\u5468\u7684 \u4e8b\uff0c\u53ef\u5343\u4e07\u8981\u9a6c\u4e0a\u901a\u77e5\u6211\uff0c\u51e1\u662f\u6709\u53e4\u94b1\u4e3a\u8bb0\u4e4b\u5904\uff0c\u90fd\u53ef\u7559\u8bdd\u3002\u201d \u3000\u3000\u718a\u501c\u5fc3\u6025\u5982\u711a\uff0c\u7b80\u5355\u5730\u5305\u4e86\u51e1\u4ef6\u8863\u670d\u548c\u4e00\u4e9b\u94f6\u4e24\uff0c\u56e0\u4e3a\u6b66\u5f53\u5c71\u5c31\u5728\u6e56\u5317\u5883\u5185\uff0c\u8def\u9014\u4e0d \u8fdc\uff0c\u662f\u4ee5\u4e5f\u672a\u9a91\u9a6c\uff0c\u5c31\u548c\u5c1a\u672a\u660e\u5306\u5306\u8d70\u4e86\u3002 \u3000\u3000\u8d76\u5230\u6e21\u5934\uff0c\u5374\u53d1\u73b0\u8fde\u4e00\u6761\u7a7a\u8239\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u5c1a\u672a\u660e\u89c1\u718a\u501c\u7126\u6025\u5f97\u5f88\uff0c\u5b89\u6170\u4ed6\u8bf4\u9053\uff1a\u201c\u5927\u54e5\u4f55\u5fc5\u7740\u6025\u5462\u3002\u53cd\u6b63\u6211\u4eec\u4e5f\u4e0d\u5dee\u8fd9\u4e00\u65f6\uff0c\u6211 \u4eec\u4e0d\u5982\u5230\u524d\u9762\u53bb\u770b\u770b\uff0c\u4e5f\u8bb8\u90a3\u91cc\u5012\u6709\u8239\u3002\u201d \u3000\u3000\u718a\u501c\u9053\uff1a\u201c\u4e0d\u662f\u6211\u8981\u4e89\u8fd9\u4e00\u65f6\u534a\u523b\uff0c\u5b9e\u4e0d\u7792\u8d24\u5f1f\u8bf4\uff0c\u6b64\u523b\u6211\u771f\u662f\u5fc3\u4e2d\u65e0\u4e3b\u3002\u201d \u3000\u3000\u5c1a\u672a\u660e\u7b11\u9053\uff1a\u201c\u90a3\u81ea\u7136\u4e86\uff0c\u8981\u662f\u6211\u5fc3\u7231\u7684\u4eba\u88ab\u4eba\u63b3\u4e86\uff0c\u6211\u4f1a\u66f4\u7740\u6025\u5462\u3002\u201d \u3000\u3000\u8d70\u4e86\u4e00\u4f1a\uff0c\u5df2\u662f\u6e21\u5934\u4e4b\u5916\u4e86\uff0c\u5cb8\u8fb9\u4e5f\u6ca1\u6709\u4ec0\u4e48\u4eba\u8ff9\u3002\u718a\u501c\u4e0d\u7981\u57cb\u6028\u5c1a\u672a\u660e\u9053\uff1a\u201c\u8fd9\u79cd\u8352 \u50fb\u7684\u5730\u65b9\uff0c\u66f4\u627e\u4e0d\u5230\u6e21\u8239\uff0c\u6211\u60f3\u8fd8\u662f\u56de\u5934\u5427\u3002\u201d \u3000\u3000\u5c1a\u672a\u660e\u9053\uff1a\u201c\u53cd\u6b63\u90a3\u8fb9\u4e5f\u6ca1\u6709\u8239\uff0c\u800c\u4e14\u90a3\u4e9b\u8239\u4e0a\u7684\u5973\u5b50\u89c1\u6211\u4eec\u50cf\u662f\u602a\u4eba\u4f3c\u7684\uff0c\u4e00\u76f4\u770b \u7740\uff0c\u8ba8\u538c\u5f97\u5f88\uff0c\u5012\u662f\u8fd9\u79cd\u5730\u65b9\uff0c\u53ea\u8981\u6709\u8239\uff0c\u5fc5\u5b9a\u80af\u642d\u6211\u4eec\u8fc7\u6c5f\u7684\uff0c\u6700\u591a\u591a\u7ed9\u8239\u8d44\u5c31\u662f\u4e86\u3002\u201d \u3000\u3000\u718a\u501c\u65e0\u53ef\u65e0\u4e0d\u53ef\u5730\u8ddf\u7740\u5c1a\u672a\u660e\u5f80\u524d\u53bb\uff0c\u5fc3\u4e2d\u5374\u5728\u60f3\u7740\u5fc3\u4e8b\uff0c\u4ed6\u76d8\u7b97\u7740\u5230\u4e86\u6b66\u5f53\u5c71\uff0c\u6700\u597d \u80fd\u591f\u4e0d\u52a8\u5e72\u6208\uff0c\u5c31\u5c06\u590f\u82b8\u5e26\u56de\u3002 \u3000\u3000\u5c1a\u672a\u660e\u7a81\u7136\u7b11\u9053\uff1a\u201c\u600e\u4e48\u6837\uff0c\u6211\u8bf4\u6709\u8239\u5427\u3002\u201d \u3000\u3000\u718a\u501c\u5f80\u524d\u4e00\u671b\uff0c\u679c\u7136\u6709\u8258\u5c0f\u8239\u6cca\u5728\u524d\u9762\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u4eec\u5feb\u6b65\u4e0a\u524d\u53bb\uff0c\u89c1\u90a3\u8239\u7684\u540e\u68a2\u8e72\u5750\u4e2a\u8239\u592b\uff0c\u4fbf\u558a\u9053\uff1a\u201c\u5582\uff0c\u8239\u5bb6\uff0c\u5e2e\u5e2e\u5fd9\uff0c\u5feb\u70b9 \u6e21\u6211\u4eec\u8fc7\u6c5f\uff0c\u8239\u94b1\u4e0d\u4f1a\u5c11\u7ed9\u4f60\u7684\u3002\u201d \u3000\u3000\u90a3\u8239\u5bb6\u6c89\u7740\u8138\u8bf4\uff1a\u201c\u5bf9\u4e0d\u8d77\uff0c\u8fd9\u8258\u8239\u5df2\u7ecf\u4e3a\u524d\u9762\u7684\u76f8\u516c\u5305\u4e86\uff0c\u4e0d\u80fd\u642d\u522b\u7684\u5ba2\u3002\u201d \u3000\u3000\u5c1a\u672a\u660e\u9053\uff1a\u201c\u53ef\u4e0d\u53ef\u4ee5\u627e\u90a3\u4f4d\u76f8\u516c\u5546\u91cf\u4e00\u4e0b\uff0c\u8239\u94b1\u6211\u4eec\u51fa\u4e86\u3002\u201d \u3000\u3000\u54ea\u77e5\u8231\u4e2d\u7a81\u6709\u4e00\u4eba\u4e0d\u8010\u70e6\u4ed6\u8bf4\uff1a\u201c\u4ec0\u4e48\u4eba\u8fd9\u6837\u55dc\u568f\uff0c\u8fd9\u8239\u6211\u5df2\u4e00\u4e2a\u4eba\u5305\u4e86\uff0c\u4efb\u4f60\u8c01\u90fd\u4e0d \u80fd\u518d\u4e0a\u6765\uff0c\u4f60\u542c\u89c1\u4e86\u5417\uff1f\u201d \u3000\u3000\u5c1a\u672a\u660e\u4e00\u542c\u6b64\u4eba\u8bf4\u8bdd\u8fd9\u4e48\u86ee\u6a2a\uff0c\u4e0d\u7981\u6709\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u5582\uff0c\u670b\u53cb\u4f60\u5ba2\u6c14\u70b9\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u8239\u8231\u90a3\u4eba\u597d\u50cf\u6c14\u66f4\u5927\uff0c\u53f1\u9053\uff1a\u201c\u6211\u4e0d\u5ba2\u6c14\u53c8\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u4eba\u4e5f\u8ddf\u7740\u8d70\u4e86\u51fa\u6765\uff0c\u662f\u4e2a\u8863\u7740\u975e\u5e38\u534e\u4e3d\u7684\u5c11\u5e74\u516c\u5b50\uff0c\u718a\u501c\u4e00\u89c1\u6115\u4e86\uff0c\u8ba4\u5f97\u662f\u5b64\u5cf0\u4e00\u5251\u8fb9 \u6d69\uff0c\u4fbf\u77e5\u9053\u8fd9\u53c8\u662f\u573a\u9ebb\u70e6\u3002 \u3000\u3000\u8fb9\u6d69\u4e00\u8d70\u51fa\u8231\uff0c\u6a2a\u8eab\u4e00\u671b\u4e24\u4eba\uff0c\u7a81\u7136\u770b\u89c1\u718a\u501c\uff0c\u51b7\u51c4\u51c4\u4e00\u58f0\u957f\u7b11\u9053\uff1a\u201c\u597d\u6781\u4e86\uff0c\u597d\u6781 \u4e86\uff0c\u4eca\u5929\u53c8\u78b0\u5230\u9601\u4e0b\u4e86\u3002\u201d \u3000\u3000\u4ed6\u53c8\u6a2a\u773c\u4e00\u671b\u5c1a\u672a\u660e\uff0c\u8bf4\u9053\uff1a\u201c\u600e\u4e48\u9601\u4e0b\u90a3\u4f4d\u5973\u4fdd\u9556\u5462\uff0c\u73b0\u5728\u5374\u6362\u4e2a\u7537\u7684\uff1f\u201d \u3000\u3000\u5c1a\u672a\u660e\u5012\u771f\u7684\u6115\u4f4f\u4e86\uff0c\u4ed6\u4ee5\u4e3a\u4e24\u4eba\u4ece\u672c\u662f\u7d20\u8bc6\uff0c\u4f46\u542c\u6b64\u4eba\u8bdd\u4e2d\u5374\u5e26\u7740\u8ba5\u8bee\u3002 \u3000\u3000\u718a\u501c\u867d\u89c9\u8fb9\u6d69\u72c2\u505a\u592a\u751a\uff0c\u4f46\u4ed6\u60f3\u8fb9\u6d69\u65e2\u80fd\u4e0e\u4e1c\u65b9\u7075\u9f50\u540d\uff0c\u88ab\u5e76\u79f0\u4e3a\u201c\u5357\u5317\u53cc\u7edd\u201d\uff0c\u800c\u4e14 \u4e0e\u4e1c\u65b9\u7075\u53c8\u662f\u670b\u53cb\uff0c\u60f3\u5fc5\u6b64\u4eba\u9664\u4e86\u72c2\u50b2\u4e4b\u5916\uff0c\u7edd\u65e0\u6076\u8ff9\uff0c\u4fbf\u4e5f\u4e0d\u60f3\u548c\u4ed6\u7ed3\u4ec7\uff0c\u662f\u4ee5\u5e76\u672a\u53cd\u5507 \u76f8\u8ba5\u3002 \u3000\u3000\u8fb9\u6d69\u4ee5\u4e3a\u718a\u501c\u6015\u4e86\u4ed6\uff0c\u800c\u4e14\u4ed6\u65e9\u5bf9\u718a\u501c\u4e0d\u6ee1\uff0c\u53c8\u4e0d\u77e5\u9053\u718a\u501c\u7684\u59d3\u540d\u6765\u5386\uff0c\u662f\u4ee5\u72c2\u6001\u66f4 \u4f5c\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5f53\u662f\u8c01\u6562\u786c\u8981\u642d\u4eba\u7684\u8239\uff0c\u5374\u539f\u6765\u662f\u9601\u4e0b\uff0c\u53ea\u662f\u9601\u4e0b\u7684\u90a3\u4f4d\u5973\u5e2e\u624b\u4e0d\u6765\uff0c\u6211\u770b \u9601\u4e0b\u8fd8\u662f\u7701\u7701\u4e8b\u5427\u3002\u201d \u3000\u3000\u5c1a\u672a\u660e\u89c1\u4ed6\u8d8a\u8bb2\u8d8a\u4e0d\u50cf\u8bdd\uff0c\u4fbf\u5411\u718a\u501c\u8bf4\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u8ba4\u8bc6\u4ed6\uff1f\u201d \u3000\u3000\u8fb9\u6d69\u4e00\u9635\u72c2\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8ba4\u5f97\u53c8\u600e\u6837\uff0c\u4e0d\u8ba4\u5f97\u53c8\u600e\u6837\uff0c\u96be\u9053\u4f60\u60f3\u67b6\u4e2a\u6a2a\u6881\u5b50\uff1f\u201d \u3000\u3000\u718a\u501c\u6b64\u523b\u4e5f\u6c89\u4e0d\u4f4f\u6c14\u4e86\uff0c\u53f1\u9053\uff1a\u201c\u59d3\u8fb9\u7684\uff0c\u4f60\u6700\u597d\u5c11\u8bf4\u72c2\u8bdd\uff0c\u6211\u4e0d\u8fc7\u770b\u4f60\u662f\u6211\u4e1c\u65b9\u5144\u4e4b \u53cb\uff0c\u624d\u8ba9\u4f60\u4e09\u5206\uff0c\u4f60\u5374\u522b\u4ee5\u4e3a\u6211\u718a\u501c\u6015\u4e86\u4f60\u3002\u201d \u3000\u3000\u8fb9\u6d69\u4e00\u542c\u201c\u718a\u501c\u201d\u4e24\u5b57\uff0c\u771f\u662f\u6240\u8c13\u201c\u4eba\u7684\u540d\uff0c\u6811\u7684\u5f71\u201d\uff0c\u5fae\u5fae\u4e00\u6115\uff0c\u968f\u5373\u7b11\u9053\uff1a\u201c\u539f\u6765 \u9601\u4e0b\u5c31\u662f\u718a\u501c\uff0c\u770b\u6765\u4eca\u65e5\u6211\u7684\u5251\u5012\u771f\u7684\u53ef\u4ee5\u8fc7\u763e\u4e86\u3002\u201d\u8bf4\u7f62\u53c8\u662f\u4e00\u9635\u72c2\u7b11\u3002 \u3000\u3000\u5c1a\u672a\u660e\u7b49\u4ed6\u7b11\u8fc7\uff0c\u7a81\u5730\u54c8\u54c8\u4e5f\u7b11\u4e86\u8d77\u6765\uff0c\u800c\u4e14\u7b11\u7684\u58f0\u97f3\u66f4\u5927\u3002 \u3000\u3000\u8fb9\u6d69\u6115\u7136\u9053\uff1a\u201c\u9601\u4e0b\u4f55\u4eba\uff1f\u4e3a\u4f55\u53d1\u7b11\uff1f\u201d \u3000\u3000\u5c1a\u672a\u660e\u51b7\u51b7\u9053\uff1a\u201c\u6211\u7b11\u4f60\u7684\u5251\u4eca\u65e5\u53ea\u6015\u771f\u8981\u8fc7\u75d5\u4e86\u3002\u201d \u3000\u3000\u8fb9\u6d69\u6012\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u4e5f\u914d\u5411\u6211\u53eb\u9635\uff1f\u201d \u3000\u3000\u5c1a\u672a\u660e\u9053\uff1a\u201c\u6211\u6b63\u8981\u95ee\u4f60\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u4e5f\u914d\u5411\u6211\u5927\u54e5\u53eb\u9635\uff1f\u201d \u3000\u3000\u4ed6\u671d\u8fb9\u6d69\u8f7b\u7a9c\u5730\u62db\u4e86\u62db\u624b\uff0c\u53c8\u9053\uff1a\u201c\u50cf\u4f60\u8fd9\u6837\u7684\u4e1c\u897f\uff0c\u53ea\u914d\u548c\u6211\u8fd9\u6837\u7684\u4e1c\u897f\u8f83\u91cf\uff0c\u6765 \u6765\uff0c\u6211\u4fdd\u9669\u8ba9\u4f60\u8fc7\u75db\u5c31\u662f\u4e86\u3002\u201d \u3000\u3000\u718a\u501c\u5fd9\u9053\uff1a\u201c\u8d24\u5f1f\u4e0d\u8981\u5305\u63fd\uff0c\u8fd9\u4eba\u662f\u6211\u7684\uff0c\u4e0d\u7ba1\u4f60\u7684\u4e8b\u3002\u201d \u3000\u3000\u8fb9\u6d69\u89c1\u4e24\u4eba\u62a2\u6765\u62a2\u53bb\uff0c\u7adf\u5c06\u81ea\u5df1\u770b\u6210\u6d88\u9063\u4f3c\u7684\uff0c\u518d\u4e5f\u6446\u4e0d\u51fa\u540d\u5bb6\u7684\u67b6\u5b50\uff0c\u6012\u9053\uff1a\u201c\u4f60\u4eec \u4e24\u4e2a\u4e00\u9f50\u6765\u597d\u4e86\uff0c\u8ba9\u8fb9\u5927\u7237\u6559\u8bad\u6559\u4f60\u8bad\u4eec\u3002\u201d \u3000\u3000\u5c1a\u672a\u660e\u9053\uff1a\u201c\u53ea\u6015\u4eca\u65e5\u662f\u8c01\u6559\u8bad\u6559\u8bad\u8c01\u8fd8\u4e0d\u4e00\u5b9a\u5462\uff01\u201d \u3000\u3000\u8fb9\u6d69\u53f1\u9053\uff1a\u201c\u6211\u5148\u6559\u8bad\u6559\u8bad\u4f60\u3002\u201d \u3000\u3000\u4ed6\u96c6\u638c\u9f50\u51fa\uff0c\u4fbf\u5411\u5c1a\u672a\u660e\u51fb\u4e0b\uff0c\u5b64\u5cf0\u4e00\u5251\u5f97\u4ee5\u4eab\u540d\u6c5f\u6e56\uff0c\u540d\u5217\u201c\u53cc\u7edd\u201d\uff0c\u6b66\u529f\u5b9e\u662f\u4e0d \u51e1\uff0c\u4ed6\u4e00\u65bd\u5c55\u638c\u529b\uff0c\u53ea\u515a\u98ce\u58f0\u864e\u864e\uff0c\u6ee1\u5730\u98de\u7802\uff0c\u58f0\u52bf\u786e\u60ca\u4eba\u5f97\u5f88\u3002 \u3000\u3000\u94c1\u80c6\u5c1a\u672a\u660e\u4e5f\u77e5\u9053\u6b64\u638c\u975e\u540c\u5c0f\u53ef\uff0c\u4f46\u4ed6\u81ea\u5e7c\u9047\u5e08\uff0c\u82e6\u7ec3\u591a\u5e74\uff0c\u62db\u5f0f\u4e5f\u8bb8\u6ca1\u6709\u718a\u501c\u4ee5\u53ca\u8fb9 \u6d69\u4e24\u4eba\u56e0\u5404\u6709\u540d\u5e08\u5947\u7f18\u800c\u65bd\u51fa\u7684\u5de7\u5999\uff0c\u4f46\u638c\u529b\u786e\u7edd\u4e0d\u900a\u8272\uff0c\u662f\u4ee5\u4ed6\u66fe\u548c\u718a\u501c\u5bf9\u4e00\u638c\uff0c\u4e5f\u662f\u626f \u4e2a\u5e73\u624b\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u5fae\u4e00\u632b\u8179\uff0c\u53cc\u638c\u84e6\u7ffb\uff0c\u5410\u6c14\u5f00\u58f0\uff0c\u53c8\u786c\u751f\u751f\u63a5\u4e86\u8fb9\u6d69\u4e00\u638c\u3002 \u3000\u3000\u8fd9\u4e00\u638c\u4e24\u4eba\u4ff1\u662f\u5168\u529b\u800c\u65bd\uff0c\u6bd4\u8d77\u718a\u501c\u548c\u4ed6\u7684\u4e00\u638c\uff0c\u53c8\u81ea\u4e0d\u540c\uff0c\u53ea\u542c\u4e00\u58f0\u5927\u9707\u4e4b\u540e\uff0c\u5c1a\u672a \u660e\u56fa\u662f\u8fde\u9000\u6570\u6b65\uff0c\u8fb9\u6d69\u5728\u7a7a\u4e2d\u4e00\u7ffb\u8eab\uff0c\u9669\u4e9b\u8dcc\u5728\u5730\u4e0a\u3002 \u3000\u3000\u718a\u501c\u7a81\u5730\u4e00\u6b65\u7ad9\u5728\u4ed6\u4fe9\u4eba\u5f53\u4e2d\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4fe9\u4eba\u4e0d\u80fd\u52a8\u624b\u3002\u201d \u3000\u3000\u5c1a\u672a\u660e\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u718a\u501c\u6307\u7740\u8fb9\u6d69\u95ee\u5c1a\u672a\u660e\u9053\uff1a\u201c\u4f60\u8ba4\u5f97\u6b64\u4eba\u5417\uff1f\u201d \u3000\u3000\u5c1a\u672a\u660e\u6447\u6447\u5934\u3002 \u3000\u3000\u718a\u501c\u53c8\u6307\u7740\u5c1a\u672a\u660e\u5411\u8fb9\u6d69\u95ee\u9053\uff1a\u201c\u4f60\u53c8\u77e5\u9053\u4ed6\u662f\u4ec0\u4e48\u4eba\u5417\uff1f\u201d \u3000\u3000\u8fb9\u6d69\u81ea\u4e5f\u6447\u5934\u3002 \u3000\u3000\u718a\u501c\u7b11\u9053\uff1a\u201c\u8fd9\u5c31\u597d\u7684\uff0c\u4f60\u4e24\u4eba\u65e2\u7136\u4e92\u4e0d\u76f8\u8bc6\uff0c\u600e\u80fd\u968f\u4fbf\u52a8\u624b\uff1f\u201d \u3000\u3000\u4ed6\u8fd9\u4e00\u756a\u6b6a\u7406\uff0c\u5012\u5c06\u4e24\u4eba\u90e8\u95ee\u4f4f\u4e86\u3002 \u3000\u3000\u4e8e\u662f\u718a\u501c\u53c8\u5bf9\u8fb9\u6d69\u8bf4\u9053\uff1a\u201c\u53ef\u662f\u4f60\u6211\u4e24\u4eba\u53c8\u4e0d\u540c\u4e86\uff0c\u4f60\u81ea\u7136\u8ba4\u5f97\u6211\uff0c\u6211\u4e5f\u77e5\u9053\u4f60\u5c31\u662f\u9f0e \u9f0e\u5927\u540d\u7684\u8fb9\u6d3d\uff0c\u6211\u4eec\u52a8\u624b\uff0c\u5c31\u5408\u7406\u5f97\u5f88\u4e86\u3002\u201d \u3000\u3000\u8fb9\u6d69\u88ab\u95ee\u5f97\u557c\u7b11\u7686\u975e\uff0c\u6b63\u4e0d\u77e5\u5982\u4f55\u7b54\u8bdd\u624d\u597d\uff0c\u5c1a\u672a\u660e\u5373\u53c8\u6a2a\u8eab\u4e00\u63a0\uff0c\u62a2\u5230\u718a\u501c\u524d\u9762\uff0c\u5bf9 \u8fb9\u6d69\u8bf4\u9053\uff1a\u201c\u539f\u6765\u9601\u4e0b\u5c31\u662f\u5b64\u5cf0\u4e00\u5251\uff1f\u201d \u3000\u3000\u8fb9\u6d69\u9053\uff1a\u201c\u4f60\u4e5f\u77e5\u9053\uff1f\u201d \u3000\u3000\u5c1a\u672a\u660e\u9053\uff1a\u201c\u5f53\u7136\uff0c\u5f53\u7136\u3002\u201d\u4ed6\u53c8\u6307\u7740\u81ea\u5df1\u7684\u9f3b\u5b50\u8bf4\u9053\uff1a\u201c\u6211\u5c31\u662f\u94c1\u80c6\u5c1a\u672a\u660e\uff0c\u4f60\u77e5\u9053 \u5417\uff1f\u94c1\u5c31\u662f\u94a2\u94c1\u7684\u94c1\uff0c\u80c6\u5c31\u662f\u6708\u5b57\u65c1\u5982\u4e2a\u65e6\u5b57\u3002\u201d \u3000\u3000\u6b64\u756a\u8f6e\u5230\u8fb9\u6d69\u548c\u718a\u501c\u4e24\u4eba\u4e0d\u77e5\u4ed6\u5728\u5f04\u4ec0\u4e48\u7384\u865a\u4e86\uff0c\u8fb9\u6d69\u81ea\u7136\u4e5f\u542c\u5230\u4e86\u5c1a\u672a\u660e\u7684\u540d\u5b57\uff0c \u8bf4\uff1a\u201c\u8fd9\u6837\u770b\u6765\uff0c\u4eca\u65e5\u4e4b\u4f1a\u771f\u7684\u6709\u610f\u601d\u4e86\uff0c\u539f\u6765\u9601\u4e0b\u5c31\u662f\u4e24\u7eff\u6797\u9053\u7684\u603b\u74e2\u628a\u5b50\u3002\u201d \u3000\u3000\u5c1a\u672a\u660e\u9053\uff1a\u201c\u5c82\u6562\uff0c\u5c82\u6562\uff0c\u6b63\u662f\u533a\u533a\u5728\u4e0b\u3002\u201d \u3000\u3000\u4ed6\u53c8\u56de\u5934\u5bf9\u718a\u501c\u9053\uff1a\u201c\u73b0\u5728\u4ed6\u8ba4\u5f97\u4e86\u6211\uff0c\u6211\u4e5f\u8ba4\u5f97\u4e86\u4ed6\uff0c\u6211\u548c\u4ed6\u52a8\u4e86\u624b\uff0c\u4e5f\u5f88\u5408\u7406\u4e86 \u5427\uff1f\u201d \u3000\u3000\u4ed6\u8bdd\u65b9\u8bf4\u5b8c\uff0c\u8eab\u5f62\u4e00\u5e4c\u4e00\u4e2a\u7bad\u6b65\u7a9c\u4e86\u4e0a\u53bb\uff0c\u5de6\u624b\u66f2\u5f26\u800c\u4fa7\uff0c\u53f3\u638c\u4e00\u6296\uff0c\u7adf\u50cf\u5316\u6210\u4e86\u4e09\u4e2a \u5708\u5b50\uff0c\u8fd9\u672c\u662f\u5251\u6cd5\u4e2d\u7684\u201c\u6885\u82b1\u4e09\u5f04\u201d\uff0c\u4f46\u4ed6\u5374\u7528\u4e4b\u4e8e\u638c\u4e0a\uff0c\u5a01\u529b\u4ecd\u89c1\u5f02\u5e38\u7edd\u5999\u3002 \u3000\u3000\u8fb9\u6d69\u89c1\u4ed6\u8bdd\u5230\u4eba\u5230\uff0c\u800c\u4e14\u4e00\u51fa\u624b\u5c31\u662f\u7edd\u62db\uff0c\u4e1d\u6beb\u4e5f\u4e0d\u6562\u758f\u5ffd\u3002\u8fb9\u6d69\u7b49\u5230\u638c\u5df2\u4e34\u5934\uff0c\u4e0d\u9000 \u4e0d\u95ea\uff0c\u8eab\u5f62\u5374\u7a81\u5730\u4e00\u659c\uff0c\u811a\u8ddf\u7262\u7262\u5730\u9489\u5728\u5730\u4e0a\uff0c\u4eba\u5374\u5f80\u5de6\u4fa7\u659c\u6210\u5761\uff0c\u53f3\u624b\u4e58\u52bf\u6325\u53bb\u201c\u5929\u5973\u6563 \u82b1\u201d\u4ea6\u662f\u5ce8\u5d4b\u5fc3\u6cd5\u3002 \u3000\u3000\u5c1a\u672a\u660e\u89c1\u8fb9\u6d69\u95ea\u907f\u548c\u51fa\u51fb\uff0c\u786e\u662f\u548c\u4e00\u822c\u4eba\u5927\u4e0d\u76f8\u540c\uff0c\u54fc\u4e86\u4e00\u58f0\uff0c\u53cc\u638c\u4e00\u9519\uff0c\u8fde\u73af\u62cd\u51fa\u6570 \u638c\uff0c\u987f\u65f6\u4f46\u89c1\u638c\u5f71\u5982\u7f24\u7eb7\u4e4b\u843d\u82f1\uff0c\u6f2b\u5929\u98de\u821e\u3002\u3001\u4ed6\u6240\u65bd\u7684\u6b63\u662f\u897f\u57df\u5f02\u50e7\u7684\u5947\u95e8\u201c\u585e\u5916\u98de\u82b1\u4e09 \u5343\u5f0f\u201d\uff0c\u540d\u4e3a\u4e09\u5343\u5f0f\uff0c\u5176\u638c\u6cd5\u7684\u7e41\u590d\u53d8\u5316\uff0c\u53ef\u60f3\u800c\u77e5\uff0c\u8fb9\u6d3d\u5374\u9759\u5982\u5c71\u5cb3\uff0c\u5c55\u51fa\u53d1\u6325\u5ce8\u5e3d\u5fc3 \u6cd5\uff0c\u4ee5\u4e0d\u53d8\u5e94\u4e07\u53d8\uff0c\u6765\u5e94\u5c1a\u672a\u660e\u7684\u638c\u5f0f\u3002 \u3000\u3000\u604d\u773c\uff0c\u4e24\u4eba\u5df2\u62c6\u4e86\u6570\u5341\u4f59\u62db\uff0c\u8fb9\u6d69\u867d\u662f\u5b88\u591a\u653b\u5c11\uff0c\u4f46\u5374\u6bcf\u4e00\u51fa\u624b\uff0c\u5fc5\u662f\u8981\u7a74\u3002 \u3000\u3000\u4e24\u5165\u77ac\u606f\u53c8\u6298\u4e86\u5341\u4f59\u5f0f\uff0c\u8fb9\u6d69\u7a81\u5730\u4e00\u58f0\u957f\u5578\uff0c\u638c\u5f0f\u4e00\u53d8\uff0c\u7adf\u81ea\u65bd\u51fa\u5ce8\u5d4b\u65c1\u652f\u7684\u4e00\u5957\uff0c\u4ea6 \u662f\u62db\u5f0f\u53d8\u5e7b\u751a\u591a\u7684\u201c\u56de\u51e4\u821e\u67f3\u201d\u7684\u638c\u6cd5\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u4e24\u4eba\u7684\u638c\u5f0f\u4ff1\u662f\u4ee5\u5feb\u5236\u5feb\uff0c\u8eab\u5f62\u53d8\u5e7b\u4e0d\u606f\uff0c\u62db\u5f0f\u4ea6\u662f\u7f24\u7eb7\u591a\u91c7\uff0c\u53ea\u89c1\u638c\u5f71\u6f2b\u98de \u821e\uff0c\u548c\u65b9\u624d\u53c8\u662f\u53e6\u5916\u4e00\u756a\u666f\u50cf\u3002 \u3000\u3000\u718a\u501c\u77e5\u9053\u8fb9\u6d69\u8fd9\u4e00\u53d8\u6362\u638c\u5f0f\uff0c\u7247\u523b\u4fbf\u8981\u5206\u51fa\u80dc\u8d1f\uff0c\u4e0d\u7981\u66f4\u4e3a\u7559\u610f\u5730\u89c2\u770b\u7740\uff0c\u5e0c\u671b\u80fd\u591f\u5728 \u6700\u7d27\u8981\u7684\u5173\u5934\uff0c\u52a0\u4ee5\u5316\u89e3\u3002 \u3000\u3000\u6b64\u65f6\u6b63\u662f\u9633\u5149\u6700\u70c8\u7684\u65f6\u5019\uff0c\u4f46\u5728\u6b64\u8352\u50fb\u7684\u6c5f\u5cb8\uff0c\u53ef\u8bf4\u662f\u7edd\u4e0d\u4f1a\u53e6\u6709\u4eba\u6765\u3002 \u3000\u3000\u54ea\u77e5\u6b64\u65f6\u6eda\u6eda\u6c5f\u6c34\uff0c\u5374\u7a81\u7136\u5192\u51fa\u4e24\u4e2a\u4eba\u6765\uff0c\u800c\u4e14\u4ff1\u662f\u5e74\u7eaa\u751a\u5927\u7684\u8001\u8005\u3002 \u3000\u3000\u5c1a\u672a\u660e\u3001\u8fb9\u6d3d\u4e24\u4eba\u6b63\u5728\u51dd\u795e\u52a8\u624b\uff0c\u5e76\u672a\u66fe\u7559\u610f\uff0c\u4f46\u718a\u501c\u548c\u90a3\u8239\u592b\u5374\u770b\u89c1\u6b64\u4e24\u4eba\uff0c\u90a3\u8239\u592b \u66f4\u662f\u60ca\u5f97\u4e00\u58f0\u602a\u53eb\uff0c\u8fde\u6eda\u5e26\u722c\uff0c\u8dcc\u56de\u8239\u91cc\u3002 \u3000\u3000\u8239\u592b\u8fd9\u4e00\u58f0\u602a\u53eb\uff0c\u5012\u4f7f\u6b63\u5728\u9163\u6597\u7684\u5b64\u5cf0\u4e00\u5251\u548c\u94c1\u80c6\u5c1a\u672a\u660e\u4e24\u4eba\u4e00\u60ca\uff0c\u4e24\u4eba\u4e0d\u7ea6\u800c\u540c\u7684\u51fb \u51fa\u4e00\u638c\u4e4b\u540e\uff0c\u4fbf\u659c\u659c\u5206\u5f00\uff0c\u4e0d\u77e5\u53d1\u751f\u4f55\u4e8b\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u8001\u8005\uff0c\u4ff1\u90fd\u987b\u53d1\u4ff1\u767d\uff0c\u5c11\u8bf4\u4e9b\u4e5f\u5728\u516d\u5341\u4ee5\u4e0a\uff0c\u6162\u6162\u81ea\u6c5f\u6c34\u4e2d\u8d70\u4e0a\u5cb8\u6765\uff0c\u50cf\u662f\u5bf9\u4efb \u4f55\u4eba\u90fd\u4e0d\u4f1a\u6ce8\u610f\u4e00\u773c\uff0c\u5373\u4f7f\u662f\u90a3\u6837\u8f7b\u5fae\u5730\u4e00\u773c\u3002 \u3000\u3000\u6700\u602a\u7684\u662f\u4e24\u4eba\u7a7f\u7740\u7684\u7adf\u90fd\u662f\u957f\u886b\uff0c\u4f46\u81ea\u6c34\u4e2d\u722c\u51fa\u540e\uff0c\u5374\u4ecd\u7136\u662f\u5e72\u5e72\u7684\uff0c\u6ca1\u6709\u4e00\u7c92\u6c34\u73e0\uff0c \u8fde\u5934\u53d1\u3001\u80e1\u5b50\u90fd\u662f\u5e72\u7684\u3002 \u3000\u3000\u718a\u501c\u3001\u5c1a\u672a\u660e\u3001\u8fb9\u6d69\u90fd\u662f\u806a\u660e\u7edd\u9876\u4e4b\u4eba\uff0c\u4e00\u773c\u4fbf\u770b\u51fa\u4e86\u4e24\u4e2a\u8001\u8005\u7684\u5f02\u5904\uff0c\u82e5\u4e0d\u662f\u5149\u5929\u5316 \u65e5\uff0c\u4ed6\u4eec\u771f\u8981\u5c06\u6b64\u4e24\u4eba\u89c6\u4e3a\u9b3c\u602a\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u8001\u8005\u4e00\u9ad8\u4e00\u77ee\uff0c\u4f46\u90fd\u7626\u5f97\u53ea\u5269\u4e00\u628a\u9aa8\u5934\uff0c\u6b64\u4e24\u4eba\u6162\u6162\u8d70\u5230\u5cb8\u4e0a\uff0c\u5f80\u5730\u4e0a\u4e00\u5750\uff0c\u624d \u5c06\u773c\u5149\u5411\u4e09\u4eba\u770b\u4e86\u4e00\u770b\u3002 \u3000\u3000\u90a3\u8f83\u77ee\u7684\u8001\u8005\u4fa7\u8138\u5bf9\u53e6\u4e00\u8001\u8005\u8bf4\u9053\uff1a\u201c\u8fd9\u51e0\u4e2a\u5c0f\u5a03\u5a03\u5728\u8fd9\u91cc\u53c8\u5435\u53c8\u95f9\u5730\uff0c\u628a\u6211\u4eec\u4e24\u4f4d\u8001 \u4eba\u5bb6\u7684\u5348\u89c9\u90fd\u5435\u9192\u4e86\uff0c\u4f60\u8bf4\u8be5\u6253\u4e0d\uff1f\u201d \u3000\u3000\u90a3\u8eab\u4f53\u8f83\u9ad8\u7684\u8001\u8005\uff0c\u8138\u4e0a\u50cf\u662f\u50f5\u786c\u5df2\u6781\uff0c\u773c\u5149\u4e5f\u662f\u7a7a\u6d1e\u6d1e\u7684\uff0c\u542c\u4e86\u90a3\u77ee\u8001\u8005\u7684\u8bdd\uff0c\u4f4e\u5934 \u60f3\u4e86\u534a\u5929\uff0c\u624d\u8bf4\u9053\uff1a\u201c\u8be5\u6253\uff0c\u8be5\u6253\u3002\u201d \u3000\u3000\u90a3\u77ee\u8001\u8005\u968f\u5373\u7b11\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u786e\u5b9e\u8be5\u6253\u3002\u201d \u3000\u3000\u8fd9\u8fb9\u4e09\u4eba\u88ab\u8fd9\u4e24\u4e2a\u8001\u8005\u7684\u5947\u5f02\u7684\u51fa\u73b0\u6df1\u6df1\u60ca\u5f02\u4e86\uff0c\u9762\u9762\u76f8\u89c2\uff0c\u4f5c\u58f0\u4e0d\u5f97\u3002 \u3000\u3000\u90a3\u77ee\u8001\u5934\u6307\u7740\u4e09\u4eba\u8bf4\u9053\uff1a\u201c\u5582\uff0c\u4f60\u4eec\u4e09\u4e2a\u5c0f\u5b50\uff0c\u5728\u8001\u4eba\u5bb6\u7761\u5348\u89c9\u7684\u65f6\u5019\uff0c\u90fd\u4e0d\u80af\u5b89\u9759\u4e00 \u70b9\uff0c\u5728\u8fd9\u91cc\u53c8\u53eb\u53c8\u95f9\u7684\u3002\u8d76\u7d27\u8131\u4e0b\u88e4\u5b50\uff0c\u722c\u5728\u6211\u8001\u4eba\u5bb6\u7684\u9762\u524d\uff0c\u6bcf\u4eba\u6253\u4e94\u5341\u4e0b\u5c41\u80a1\u3002\u201d \u3000\u3000\u718a\u501c\u7b49\u4eba\u542c\u8fd9\u77ee\u8001\u8005\u6240\u8bf4\u7684\u8bdd\uff0c\u4e0d\u7981\u557c\u7b11\u7686\u975e\uff0c\u5b64\u5cf0\u4e00\u5251\u8138\u4e0a\uff0c\u5df2\u9732\u51fa\u96be\u770b\u7684\u795e\u8272\u6765\uff0c \u867d\u7136\u4ed6\u5e76\u4e0d\u6562\u8bf4\u51fa\u96be\u542c\u7684\u8bdd\u3002 \u3000\u3000\u90a3\u77ee\u8001\u5934\u4f3c\u4e4e\u5df2\u770b\u51fa\u4ed6\u7684\u4e0d\u6ee1\uff0c\u7b11\u6307\u7740\u4ed6\u8bf4\u9053\uff0c\u201c\u4f60\u8fd9\u4e2a\u5a03\u5a03\u50cf\u662f\u4e0d\u5927\u5356\u6211\u8001\u4eba\u5bb6\u7684\u5e10 \u561b\uff0c\u5582\u3002\u201d\u4ed6\u53c8\u4fa7\u8138\u5bf9\u53e6\u4e00\u8001\u8005\u8bf4\u9053\uff1a\u201c\u6709\u4e00\u4e2a\u5a03\u5a03\u5c45\u7136\u4e0d\u5356\u6211\u8001\u4eba\u5bb6\u7684\u5e10\uff0c\u4f60\u8bf4\u8be5\u600e\u4e48 \u529e\uff1f\u201d \u3000\u3000\u53e6\u4e00\u8001\u8005\uff0c\u5168\u8eab\u90fd\u4f3c\u4e4e\u662f\u9ebb\u75f9\u7684\uff0c\u559c\u3001\u6012\u3001\u54c0\u3001\u4e50\u3001\u75db\u3001\u75d2\u3001\u9178\uff0c\u8fd9\u7b49\u4eba\u7c7b\u7684\u611f\u89c9\uff0c\u4f3c \u4e4e\u90fd\u5b8c\u5168\u4e0d\u80fd\u5f71\u54cd\u4ed6\u3002 \u3000\u3000\u4ed6\u542c\u4e86\u90a3\u77ee\u8001\u8005\u7684\u8bdd\uff0c\u53c8\u4f4e\u4e0b\u5934\u53bb\uff0c\u6df1\u6df1\u5730\u601d\u7d22\u7740\uff0c\u50cf\u662f\u8fd9\u4e00\u53e5\u6781\u7b80\u5355\u7684\u8bdd\uff0c\u4ed6\u90fd\u8981\u6c89 \u601d\u5f88\u4e45\uff0c\u624d\u80fd\u4e86\u89e3\u3002 \u3000\u3000\u4ed6\u60f3\u4e86\u8bb8\u4e45\uff0c\u8bf4\u9053\uff1a\u201c\u5148\u6253\u4ed6\u7684\u5c41\u80a1\u3002\u2018\u8bf4\u7740\uff0c\u597d\u50cf\u4ed6\u8eab\u4f53\u4e0b\u9762\uff0c\u6709\u4ec0\u4e48\u4e1c\u897f\u6253\u7740\u7684\u4f3c \u7684\uff0c\u4ecd\u7136\u5750\u7740\uff0c\u5c31\u5e73\u5e73\u98de\u843d\u5230\u8fb9\u6d69\u7684\u8eab\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u5feb\u8131\u88e4\u5b50\uff0c\u6211\u8001\u4eba\u5bb6\u8981\u5148\u6253\u4f60\u7684\u5c41 \u80a1\u3002\u201d \u3000\u3000\u718a\u501c\u548c\u94c1\u80c6\u5c1a\u672a\u660e\u89c1\u4e86\u8fd9\u8001\u5934\u5b50\u7684\u8fd9\u4e00\u624b\uff0c\u53c8\u60ca\u53c8\u4e50\uff0c\u60ca\u7684\u662f\u8fd9\u8001\u8005\u7684\u8f7b\u529f\uff0c\u7adf\u4f3c\u53c8\u7ec3 \u5012\u4f20\u8bf4\u4e2d\u7684\u201c\u9a6d\u6c14\u800c\u884c\u201d\u7684\u5883\u754c\uff0c\u4e50\u7684\u5374\u662f\u8fd9\u8001\u8005\u7adf\u627e\u8fb9\u6d69\u7684\u9ebb\u70e6\uff0c\u4e0d\u77e5\u8fb9\u6d69\u600e\u4e48\u8131\u8eab\u3002 \u3000\u3000\u8fb9\u6d69\u89c1\u90a3\u8001\u8005\u7684\u8fd9\u4e00\u624b\uff0c\u5fc3\u4e2d\u66f4\u662f\u60ca\u9a87\uff0c\u4ed6\u60f3\uff1a\u201c\u4e07\u4e00\u8fd9\u8001\u5934\u5b50\u771f\u6293\u4e0b\u6211\u7684\u88e4\u5b50\uff0c\u90a3\u6211 \u4ee5\u540e\u8fd8\u80fd\u505a\u4eba\u5417\uff1f\u201d\u4ed6\u8d8a\u60f3\u8d8a\u6015\u3002 \u3000\u3000\u4ed6\u770b\u7740\u90a3\u8001\u8005\u4ecd\u7aef\u5750\u5728\u5730\u4e0a\uff0c\u4e24\u773c\u5fae\u95ed\u7740\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u8001\u5934\u5b50\u7684\u529f\u592b\uff0c\u6211\u82e5\u975e\u4eb2\u89c1\uff0c\u522b \u4eba\u544a\u8bc9\u6211\uff0c\u6211\u5374\u4e0d\u4f1a\u76f8\u4fe1\uff0c\u8fd9\u79cd\u4eba\u7269\u6211\u600e\u80fd\u5bf9\u4ed8\uff0c\u4e09\u5341\u516d\u7740\uff0c\u8d70\u4e3a\u4e0a\u7740\uff0c\u8fd8\u662f\u4e58\u4ed6\u4e0d\u6ce8\u610f\uff0c \u6e9c\u4e86\u5427\u3002\u201d \u3000\u3000\u4e8e\u662f\u4ed6\u518d\u4e0d\u6562\u8fdf\u7591\uff0c\u5168\u8eab\u731b\u529b\u4e00\u62d4\uff0c\u7adf\u7a9c\u8d77\u4e09\u3001\u56db\u4e08\u9ad8\uff0c\u53cc\u81c2\u4e00\u6296\uff0c\u201c\u98de\u71d5\u6295\u6797\u201d\u5411\u5916 \u53c8\u662f\u4e00\u7a9c\uff0c\u53c8\u662f\u56db\u3001\u4e94\u4e08\u8fdc\u8fd1\u3002 \u3000\u3000\u90a3\u77ee\u8001\u8005\u53c8\u7b11\u9053\uff1a\u201c\u54df\uff0c\u8fd9\u5a03\u5a03\u4f1a\u98de\uff0c\u54ce\u5440\uff0c\u7cdf\u7cd5\uff0c\u8ba9\u4ed6\u8dd1\u4e86\uff0c\u6211\u8001\u4eba\u5bb6\u4e5f\u8ffd\u4e0d\u7740\u3002\u201d \u3000\u3000\u718a\u501c\u548c\u94c1\u80c6\u5c1a\u672a\u660e\u770b\u8fd9\u8001\u8005\u6ed1\u7a3d\u7684\u6837\u5b50\uff0c\u90fd\u5fcd\u4e0d\u4f4f\u7b11\u4e86\u8d77\u6765\u3002 \u3000\u3000\u90a3\u77ee\u8001\u8005\u671d\u4ed6\u4e24\u4eba\u8bf4\u9053\uff1a\u201c\u4ed6\u8dd1\u4e86\uff0c\u4ed6\u4eec\u7b11\u4ec0\u4e48\uff0c\u662f\u4e0d\u662f\u60f3\u5750\u4ed6\u7684\u8239\uff1f\u53ef\u662f\u4f60\u4eec\u522b\u5fd8 \u4e86\uff0c\u4ed6\u8dd1\u4e86\uff0c\u6211\u8001\u4eba\u5bb6\u5c31\u8981\u6253\u4f60\u4eec\u4e24\u4eba\u7684\u5c41\u80a1\u4e86\u3002\u201d \u3000\u3000\u505c\u4e86\u4e00\u505c\uff0c\u4ed6\u53c8\u8bf4\uff0c\u201c\u4f60\u4eec\u4e24\u4e2a\u4f1a\u4e0d\u4f1a\u98de\uff0c\u8981\u662f\u4e5f\u4f1a\u98de\uff0c\u90a3\u6211\u8001\u4eba\u5bb6\u4e00\u4eba\u7684\u5c41\u80a1\u90fd\u6253\u4e0d \u7740\u4e86\u3002\u201d \u3000\u3000\u718a\u501c\u548c\u94c1\u80c6\u5c1a\u672a\u660e\u4e24\u4eba\uff0c\u81ea\u662f\u77e5\u9053\u8fd9\u4e24\u4e2a\u8001\u8005\u5b9a\u662f\u4e16\u5916\u9ad8\u4eba\uff0c\u9042\u4e00\u9f50\u671d\u524d\u8d70\uff0c\u606d\u656c\u5730\u5f2f \u8eab\u65bd\u793c\u3002 \u3000\u3000\u90a3\u77ee\u8001\u5934\u5374\u53eb\u9053\uff1a\u201c\u54ce\u54df\uff0c\u5343\u4e07\u522b\u6765\u8fd9\u4e00\u624b\uff0c\u8fd9\u4e00\u624b\u6bd4\u4f1a\u98de\u8fd8\u5389\u5bb3\uff0c\u6211\u8001\u4eba\u5bb6\u4e0d\u6253\u4f60\u4eec \u7684\u5c41\u80a1\u4e86\uff0c\u4f60\u4eec\u4e5f\u522b\u6765\u8fd9\u4e00\u624b\u3002\u201d \u3000\u3000\u718a\u501c\u3001\u5c1a\u672a\u660e\u53ea\u89c9\u773c\u524d\u4e00\u82b1\uff0c\u90a3\u77ee\u8001\u5934\u5b50\u4e0d\u77e5\u600e\u5730\u53c8\u5750\u56de\u53e6\u4e00\u8001\u8005\u8eab\u4fa7\u3002 \u3000\u3000\u4ed6\u4e24\u4eba\u77e5\u9053\uff0c\u8fd9\u7c7b\u5947\u4eba\uff0c\u591a\u534a\u4e5f\u6709\u5947\u50fb\uff0c\u5c24\u5176\u718a\u501c\uff0c\u66f4\u8054\u60f3\u5230\u6bd2\u5fc3\u795e\u9b54\u6015\u54ed\u7684\u6bdb\u75c5\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u4e24\u4eba\u8d70\u5230\u90a3\u4e24\u4e2a\u8001\u8005\u9762\u524d\uff0c\u718a\u501c\u8bf4\u9053\uff1a\u201c\u8001\u524d\u8f88\u65e2\u662f\u4e0d\u559c\u591a\u793c\uff0c\u665a\u8f88\u5c31\u4ece\u547d \u4e86\uff0c\u201d\u90a3\u77ee\u8001\u5934\u5b50\u4e0a\u4e0a\u4e0b\u4e0b\u671d\u4e24\u4eba\u6ce8\u76ee\u4e86\u534a\u664c\uff0c\u53c8\u8f6c\u5411\u53e6\u4e00\u8001\u8005\u8bf4\u9053\uff1a\u201c\u4f60\u770b\u8fd9\u4e24\u4e2a\u5a03\u5a03\u5982 \u4f55\uff1f\u201d \u3000\u3000\u90a3\u9ad8\u7626\u7684\u8001\u8005\uff0c\u6de1\u6de1\u5730\u4e00\u62ac\u76ee\u5149\uff0c\u671b\u7740\u4ed6\u4eec\u4e24\u4eba\uff0c\u718a\u501c\u4e5f\u770b\u4e86\u90a3\u8001\u8005\u4e00\u773c\u3002 \u3000\u3000\u4ed6\u53ea\u89c9\u5f97\u90a3\u9ad8\u7626\u7684\u8001\u8005\u7684\u50f5\u786c\u9762\u5b54\uff0c\u770b\u6765\u5374\u5341\u5206\u4eb2\u5207\uff0c\u4ed6\u6697\u5fd6\u9053\uff1a\u201c\u8fd9\u5012\u602a\u4e86\uff0c\u6211\u4ee5\u524d \u5e76\u672a\u89c1\u8fc7\u8fd9\u4e24\u4f4d\u5947\u4eba\u5440\uff0c\u600e\u5730\u770b\u6765\u5374\u5982\u6b64\u4eb2\u5207\uff1f\u201d \u3000\u3000\u4e8e\u662f\u4ed6\u66f4\u606d\u656c\u5730\u95ee\u9053\uff1a\u201c\u665a\u8f88\u4e0d\u6562\u95ee\u4e24\u4f4d\u8001\u524d\u8f88\u7684\u5c0a\u53f7\uff1f\u201d \u3000\u3000\u90a3\u77ee\u8001\u8005\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u5a03\u5a03\u771f\u6709\u610f\u601d\uff0c\u6211\u8001\u4eba\u5bb6\u8fd8\u6709\u95ee\u4f60\u7684\u540d\u5b57\uff0c\u4f60\u5012\u95ee\u8d77\u6211\u4eec\u4e24 \u4f4d\u8001\u4eba\u5bb6\u7684\u540d\u5b57\u6765\u4e86\u3002\u201d \u3000\u3000\u718a\u501c\u9053\uff1a\u201c\u665a\u8f88\u718a\u501c\uff0c\u8fd9\u4e00\u4e2a\u662f\u665a\u8f88\u7684\u76df\u5f1f\u5c1a\u672a\u660e\u3002\u201d \u3000\u3000\u90a3\u77ee\u8001\u5934\u5b50\u7b11\u9053\uff0c\u201c\u5c1a\u672a\u660e\uff0c\u8fd9\u4e2a\u540d\u5b57\u5012\u771f\u6709\u610f\u601d\u3002\u201d \u3000\u3000\u4ed6\u53c8\u5411\u8eab\u65c1\u7684\u8001\u8005\u8bf4\uff1a\u201c\u5582\uff0c\u4f60\u8bf4\u5c1a\u672a\u660e\u8fd9\u4e2a\u540d\u5b57\u6709\u6ca1\u6709\u610f\u601d\uff1f\u201d \u3000\u3000\u90a3\u7626\u957f\u8001\u4eba\u5374\u50cf\u662f\u6ca1\u6709\u542c\u89c1\u4ed6\u7684\u8bdd\uff0c\u4f4e\u7740\u5934\u8f7b\u58f0\u5ff5\u7740\uff1a\u201c\u718a\u501c\u3001\u718a\u501c\u2026\u2026\u201d \u3000\u3000\u718a\u501c\u548c\u94c1\u80c6\u5c1a\u672a\u660e\u606d\u656c\u5730\u7ad9\u5728\u8fd9\u4e24\u4e2a\u5947\u4eba\u9762\u524d\uff0c\u90a3\u77ee\u8001\u8005\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4e2a\u5a03\u5a03\uff0c\u90fd\u6709 \u70b9\u610f\u601d\uff0c\u6211\u8001\u4eba\u5bb6\u9ad8\u5174\u5f97\u5f88\uff0c\u60f3\u9001\u70b9\u4e1c\u897f\u7ed9\u4f60\u4eec\u4e24\u4e2a\u5a03\u5a03\uff0c\u4f60\u4eec\u8bf4\uff0c\u9001\u4ec0\u4e48\u597d\uff1f\u201d \u3000\u3000\u90a3\u77ee\u8001\u8005\u53c8\u7b11\u9053\uff1a\u201c\u6211\u95ee\u4f60\u4eec\u4e24\u4e2a\u4e5f\u662f\u767d\u95ee\uff0c\u5176\u5b9e\u6211\u8001\u4eba\u5bb6\u8eab\u4e0a\uff0c\u4ec0\u4e48\u4e5f\u6ca1\u6709\uff0c\u53ea\u6709\u51e0 \u5f20\u82b1\u82b1\u7eff\u7eff\u9a97\u5c0f\u5b69\u5b50\u7684\u7eb8\uff0c\u4f60\u4eec\u8981\u4e0d\u8981\uff1f\u201d \u3000\u3000\u718a\u501c\u3001\u5c1a\u672a\u660e\u5fd9\u4e00\u8d77\u7b54\u9053\uff1a\u201c\u591a\u8c22\u8001\u524d\u8f88\u3002\u201d \u3000\u3000\u77ee\u8001\u5934\u54c8\u54c8\u4e00\u7b11\uff0c\u4ece\u6000\u4e2d\u53d6\u51fa\u4e8c\u5f20\u63c9\u5f97\u76b1\u76b1\u7684\u7eb8\uff0c\u4e0a\u9762\u7a00\u5947\u53e4\u602a\u4f2a\u753b\u7740\u82b1\u7eb9\uff0c\u8bf4\u9053\uff1a \u201c\u4e00\u4eba\u4e00\u5f20\uff0c\u8c01\u4e5f\u4e0d\u8bb8\u5c06\u81ea\u5df1\u7684\u7684\u90a3\u5f20\u7ed9\u522b\u4eba\u770b\uff0c\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u90a3\u77ee\u8001\u5934\u4ef0\u5929\u6253\u4e86\u4e00\u4e2a\u54c8\u6b20\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4e2a\u5c0f\u5a03\u5a03\u53ef\u4ee5\u8d70\u4e86\uff0c\u6211\u8001\u4eba\u5bb6\u8981\u7761\u89c9 \u4e86\u3002\u201d \u3000\u3000\u718a\u501c\u3001\u5c1a\u672a\u660e\u4e0d\u6562\u518d\u803d\uff0c\u5c31\u8eac\u8eab\u8d70\u4e86\u3002 \u3000\u3000\u4e34\u8d70\u7684\u65f6\u5019\uff0c\u718a\u501c\u89c1\u90a3\u7626\u957f\u8001\u8005\u4ecd\u5728\u4f4e\u5934\u5ff5\u7740\uff0c\u5fc3\u91cc\u66f4\u89c9\u5947\u602a\u3002 \u3000\u3000\u4ed6\u4eec\u4e24\u4eba\u8d70\u5230\u8fb9\u6d69\u7684\u8239\u4e0a\uff0c\u90a3\u8239\u592b\u5413\u5f97\u9762\u65e0\u4eba\u8272\uff0c\u770b\u89c1\u4e24\u4eba\u4e0a\u8239\uff0c\u8fde\u8bdd\u90fd\u4e0d\u6562\u518d\u95ee\u8d76\u7d27 \u89e3\u7f06\u8d70\u4e86\u3002 \u3000\u3000\u6c5f\u6c34\u6025\u6d41\uff0c\u98ce\u5e06\u6ee1\u5f15\uff0c\u7247\u523b\u95f4\u90a3\u8239\u5df2\u8d70\u51fa\u8001\u8fdc\u3002 \u3000\u3000\u4e00\u76f4\u5728\u4f4e\u7740\u5934\u6c89\u601d\u7740\u7684\u7626\u957f\u8001\u5934\uff0c\u5ffd\u62ac\u8d77\u5934\u6765\uff0c\u7a7a\u6d1e\u7684\u76ee\u5149\u4e2d\uff0c\u6ee1\u805a\u5149\u91c7\uff0c\u50cf\u662f\u7ec8\u4e8e\u60f3 \u8d77\u4e86\u4ec0\u4e48\uff0c\u4f46\u56db\u987e\u65e0\u4eba\uff0c\u718a\u501c\u548c\u5c1a\u672a\u660e\u65e9\u5df2\u8d70\u4e86\u3002 \u3000\u3000\u6c5f\u6c34\u4e1c\u6d41\uff0c\u9e23\u54bd\u4f4e\u8bed\uff0c\u4f3c\u4e4e\u5728\u8bc9\u8bf4\u7740\u4eba\u7684\u805a\u6563\u65e0\u5e38\uff0c\u60b2\u6b22\u79bb\u5408\uff0c\u90fd\u592a\u77ed\u4fc3\u4e86\u3002 \u3000\u3000\u9102\u7701\u4e00\u5730\uff0c\u6e56\u6cca\u72ec\u591a\uff0c\u672c\u4e3a\u53e4\u4e91\u68a6\u5927\u6cfd\u65e7\u8ff9\uff0c\u4ed6\u4e24\u4eba\u9047\u7740\u6e56\u6cca\uff0c\u4fbf\u4e0d\u514d\u8981\u7ed5\u8fdc\u4e9b\u8def\u9014\uff0c \u4f55\u51b5\u4ed6\u4e24\u4eba\u6e56\u5317\u5883\u7684\u5317\u90e8\uff0c\u4ff1\u672a\u5230\u8fc7\uff0c\u6cbf\u9014\u95ee\u5411\uff0c\u4e5f\u4e0d\u514d\u803d\u8bef\u4e86\u65f6\u5019\uff0c\u5c1a\u672a\u660e\u77e5\u9053\u718a\u501c\u5fc3 \u6025\uff0c\u4fbf\u63d0\u8bae\u663c\u4f0f\u591c\u884c\uff0c\u4ee5\u4fbf\u591c\u95f4\u53ef\u4ee5\u65bd\u5c55\u8f7b\u529f\uff0c\u718a\u501c\u81ea\u5927\u559c\u79f0\u662f\u3002 \u3000\u3000\u8fc7\u6c49\u6c34\uff0c\u4e24\u4eba\u8fde\u591c\u8d76\u524d\uff0c\u591c\u8272\u82cd\u832b\u4e2d\uff0c\u718a\u501c\u8fdc\u8fdc\u671b\u89c1\u524d\u9762\u5c71\u52bf\u6a2a\u4e98\uff0c\u4ed6\u4e24\u4eba\u8f7b\u529f\u8d85\u7edd\uff0c \u827a\u9ad8\u80c6\u5927\uff0c\u4e5f\u4e0d\u987e\u5fcc\u4ec0\u4e48\uff0c\u9ed1\u591c\u4e2d\u4fbf\u95ef\u4e0a\u5c71\u5cad\u3002 \u3000\u3000\u5ffd\u7136\u4e24\u5cf0\u5939\u5cd9\uff0c\u4e2d\u95f4\u53ea\u7559\u51fa\u4e00\u4e2a\u4e24\u5c3a\u6765\u5bbd\u8fc7\u9053\u3002\u718a\u501c\u201c\u55d6\u201d\u7684\u4e00\u58f0\uff0c\u5df2\u7ecf\u95ef\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u5c1a\u672a\u660e\u4e3a\u4eba\u4ed4\u7ec6\uff0c\u6c5f\u6e56\u5386\u7ec3\u4ea6\u4e30\uff0c\u4e0d\u6562\u5192\u7136\u95ef\u8fdb\uff0c\u505c\u4f4f\u8eab\u5f62\u56db\u4e0b\u4e00\u6253\u91cf\uff0c\u89c1\u901a\u9053\u65c1\u7acb\u7740 \u4e00\u5757\u77f3\u7891\uff0c\u8fde\u5fd9\u8d70\u4e86\u8fc7\u53bb\uff0c\u4f38\u624b\u8981\u638f\u706b\u6298\u5b50\uff0c\u60f3\u7167\u7740\u770b\u4e00\u770b\u8fd9\u7891\u4e0a\u523b\u7740\u7684\u662f\u4e9b\u4ec0\u4e48\u5b57\u3002 \u3000\u3000\u54ea\u77e5\u706b\u6298\u5b50\u5374\u6839\u672c\u6ca1\u6709\u5e26\u7740\uff0c\u4ed6\u7075\u673a\u4e00\u52a8\uff0c\u4f38\u51fa\u53f3\u624b\u53bb\u6478\u90a3\u77f3\u7891\u4e0a\u7684\u5b57\uff0c\u4e00\u6478\u4e4b\u4e0b\uff0c\u638c \u5fc3\u4e0d\u89c9\u5fae\u5fae\u6c81\u51fa\u51b7\u6c57\uff0c\u4e00\u9635\u51b7\u6c14\uff0c\u76f4\u5192\u5230\u5934\u9876\u4e0a\u3002 \u3000\u3000\u539f\u6765\u90a3\u7891\u4e0a\u53ea\u523b\u7740\u56db\u4e2a\u5b57\uff1a\u201c\u5165\u8c37\u8005\u6740\u201d\u8fd9\u65f6\u8c37\u91cc\u7a81\u5730\u4f20\u51fa\u4e00\u58f0\u6012\u559d\uff0c\u5c1a\u672a\u660e\u4e00\u542c\u4e4b \u4e0b\uff0c\u5c31\u77e5\u9053\u5b9a\u662f\u718a\u501c\u7684\u58f0\u97f3\u3002 \u3000\u3000\u4ed6\u4e00\u4e2a\u201c\u9f99\u5f62\u4e00\u5f0f\u201d\uff0c\u8eab\u5f62\u5b9b\u5982\u6e38\u9c7c\uff0c\u4ece\u5939\u7f1d\u4e2d\u7a7f\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4ed6\u76ee\u5149\u4e00\u52a8\uff0c\u89c1\u5230\u718a\u501c\u6b63\u7ad9\u5728\u8c37\u53e3\u4e0d\u8fdc\u4e4b\u5730\uff0c\u5fd9\u98de\u63a0\u4e86\u8fc7\u53bb\uff0c\u54ea\u77e5\u773c\u524d\u7a81\u5730\u5b9b\u5982\u6253\u4e86\u4e2a \u7535\u95ea\uff0c\u4e00\u9053\u5251\u5149\u9f50\u7709\uff0c\u6311\u76ee\u3001\u524a\u9f3b\uff0c\u5206\u4e09\u5904\u523a\u4e86\u8fc7\u6765\uff0c\u5251\u5149\u4e4b\u5389\uff0c\u5251\u62db\u4e4b\u5feb\uff0c\u65e0\u4e0e\u4f26\u6bd4\u3002 \u3000\u3000\u4ed6\u5927\u60ca\u4e4b\u4e0b\uff0c\u53ca\u65f6\u540e\u6c89\uff0c\u5927\u4ef0\u8eab\uff0c\u671d\u540e\u6025\u7a9c\uff0c\u4f46\u89c9\u9762\u4e0a\u4e00\u51c9\uff0c\u5251\u5149\u81ea\u4ed6\u5934\u4e0a\u5bf8\u8bb8\u5904\u524a\u4e86 \u8fc7\u53bb\uff0c\u4ed6\u60ca\u9b42\u521d\u5b9a\uff0c\u5413\u51fa\u4e00\u8eab\u51b7\u6c57\u3002 \u3000\u3000\u4ed6\u65b9\u624d\u907f\u5f00\u6b64\u62db\uff0c\u5374\u89c1\u4e00\u6761\u4eba\u5f71\u53c8\u4ee5\u65e0\u6bd4\u7684\u901f\u5ea6\u7a9c\u4e86\u8fc7\u6765\uff0c\u4ed6\u56de\u65f6\u6c89\u8155\uff0c\u5168\u795e\u6212\u5907\uff0c\u54ea \u77e5\u90a3\u4eba\u5f71\u5728\u4ed6\u9762\u524d\u731b\u5730\u505c\u4f4f\uff0c\u6fc0\u5f97\u7a7a\u6c14\u65cb\u8d77\u4e00\u4e2a\u6c14\u6da1\uff0c\u90a3\u4eba\u5f71\u4f4e\u559d\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\u5440\u3002\u201d \u3000\u3000\u5c1a\u672a\u660e\u4ed4\u7ec6\u5730\u4e00\u770b\uff0c\u90a3\u4eba\u5f71\u7adf\u662f\u718a\u501c\uff0c\u6b64\u523b\u6b63\u9759\u9759\u5cd9\u7acb\u5728\u81ea\u5df1\u9762\u524d\uff0c\u5c31\u50cf\u65b9\u624d\u662f\u5728\u7f13\u6b65 \u4e2d\u505c\u4f4f\u8eab\u5f62\u4f3c\u7684\u3002 \u3000\u3000\u82e5\u7136\u5c1a\u672a\u660e\u4e5f\u6709\u718a\u501c\u76ee\u529b\uff0c\u4ed6\u6b64\u523b\u5fc5\u53ef\u770b\u51fa\u718a\u501c\u8138\u4e0a\u7684\u60ca\u9a87\u3002 \u3000\u3000\u718a\u501c\u53f3\u624b\u62ff\u7740\u90a3\u67c4\u5de7\u4e2d\u5f97\u6765\u7684\u201c\u5251\u201d\uff0c\u5de6\u624b\u4e00\u628a\u62c9\u7740\u5c1a\u672a\u660e\u7684\u624b\u8155\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u8c37 \u4e2d\u597d\u50cf\u4e0d\u5927\u5bf9\u3002\u201d \u3000\u3000\u5c1a\u672a\u660e\u5fd9\u95ee\uff1a\u201c\u600e\u4e48\uff1f\u201d\u3002 \u3000\u3000\u718a\u501c\u9053\uff1a\u201c\u65b9\u624d\u6211\u614c\u5fd9\u4e2d\u7a9c\u8fdb\u8fd9\u5c71\u8c37\u2026\u2026\u201d \u3000\u3000\u5c1a\u672a\u660e\u622a\u4f4f\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u6709\u6ca1\u6709\u770b\u5230\u8c37\u53e3\u7684\u77f3\u7891\uff1f\u201d \u3000\u3000\u718a\u501c\u8be7\u9053\uff1a\u201c\u8c37\u53e3\u8fd8\u6709\u4e2a\u77f3\u7891\uff1f\u201d \u3000\u3000\u5c1a\u672a\u660e\u9053\uff1a\u201c\u5feb\u671d\u6765\u8def\u9000\u3002\u201d \u3000\u3000\u62d6\u7740\u718a\u501c\uff0c\u731b\u4e00\u957f\u8eab\uff0c\u718a\u501c\u4e5f\u89c9\u4e8b\u60c5\u6709\u5f02\uff0c\u4e0d\u53ca\u591a\u95ee\uff0c\u8eab\u5f62\u5b9b\u5982\u4e24\u53ea\u8fde\u8882\u98de\u8d77\u7684\u71d5\u5b50\uff0c \u63a0\u81f3\u5939\u7f1d\u7684\u51fa\u53e3\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u970e\u773c\u4e4b\u95f4\uff0c\u8c37\u53e3\u7a81\u7136\u591a\u4e86\u4e00\u4eba\uff0c\u51b7\u51b7\u4e00\u7b11\u3002 \u3000\u3000\u718a\u501c\u62c9\u7740\u5c1a\u672a\u660e\u731b\u5730\u987f\u4f4f\u8eab\u5f62\u3002 \u3000\u3000\u90a3\u4eba\u53c8\u51b7\u51b7\u9053\uff1a\u201c\u4e24\u4e2a\u5a03\u5a03\u8dd1\u5230\u6211\u8fd9\u751c\u751c\u8c37\u6765\uff0c\u8fd8\u60f3\u51fa\u53bb\u5417\uff1f\u201d \u3000\u3000\u718a\u501c\u5c06\u624b\u4e2d\u7684\u5251\u4e00\u7d27\uff0c\u5251\u5f0f\u659c\u6311\uff0c\u5bd3\u653b\u4e8e\u5b88\uff0c\u5c1a\u672a\u660e\u501f\u7740\u5251\u5149\u4e00\u770b\uff0c\u6d1e\u53e3\u7ad9\u7740\u7684\u90a3\u4eba\uff0c \u884c\u5bb9\u4e4b\u5947\u8be1\u8fde\u753b\u90fd\u753b\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u718a\u501c\u81ea\u4e5f\u5728\u6253\u91cf\u7740\u90a3\u4eba\uff0c\u89c1\u4ed6\u5168\u8eab\u90fd\u662f\u8d64\u88f8\u88f8\u7684\uff0c\u4ec0\u4e48\u90fd\u6ca1\u6709\u7a7f\uff0c\u5934\u4e0a\u7684\u5934\u53d1\uff0c\u957f\u5f97\u5413 \u4eba\uff0c\u62d6\u5728\u8eab\u4e0a\uff0c\u56f4\u7740\u8eab\u5b50\u6253\u4e86\u51e0\u4e2a\u7ed3\uff0c\u8eab\u4f53\u81c3\u80bf\u5f97\u3001\u50cf\u53ea\u80a5\u732a\uff0c\u4f46\u8eab\u5f62\u5374\u53c8\u7075\u5de7\u5f97\u5b9b\u5982\u98de \u71d5\u3002 \u3000\u3000\u518d\u4e00\u770b\u4ed6\u8138\u4e0a\uff0c\u5706\u997c\u4f3c\u7684\u8138\uff0c\u8fde\u9f3b\u5b50\u90fd\u770b\u4e0d\u51fa\u6765\uff0c\u5168\u8eab\u4e0a\u4e0b\uff0c\u552f\u4e00\u7a0d\u5177\u4eba\u5f62\u7684\uff0c\u5c31\u662f\u90a3 \u4e24\u53ea\u773c\u775b\uff0c\u5728\u9ed1\u6697\u4e2d\u4e00\u95ea\u4e00\u95ea\u5730\u653e\u6625\u5149\u3002 \u3000\u3000\u6df1\u5c71\u5e7d\u8c37\uff0c\u9661\u7136\u89c1\u4e86\u8fd9\u6837\u4f3c\u4eba\u975e\u4eba\u7684\u602a\u7269\uff0c\u718a\u501c\u3001\u5c1a\u672a\u660e\u4e24\u4eba\u4e5f\u4e0d\u7981\u9b42\u98de\u9b44\u6563\uff0c\u5f80\u540e\u9000 \u4e86\u4e00\u6b65\uff0c\u9f50\u58f0\u9053\uff1a\u201c\u4f60\u662f\u4eba\u662f\u9b3c\uff1f\u201d \u3000\u3000\u90a3\u4eba\u7a81\u7136\u5403\u5403\u7b11\u4e86\u8d77\u6765\uff0c\u7b11\u58f0\u53c8\u5a07\u53c8\u5ae9\uff0c\u8ddf\u4ed6\u7684\u5916\u5f62\uff0c\u7b80\u76f4\u662f\u4e24\u4e2a\u6781\u7aef\uff0c\u82e5\u6709\u4eba\u4e00\u542c\u5f97 \u8fd9\u7b11\u58f0\uff0c\u4e00\u5b9a\u4f1a\u4ee5\u4e3a\u9762\u524d\u7ad9\u7740\u7684\u662f\u4e2a\u5343\u5a07\u767e\u5a9a\u7684\u5973\u5b50\uff0c\u718a\u501c\u7b49\u4e24\u4eba\u542c\u4e86\u8fd9\u7b11\u58f0\uff0c\u5413\u5f97\u811a\u90fd\u6709 \u70b9\u53d1\u8f6f\u4e86\u3002 \u3000\u3000\u4ed6\u4e24\u4eba\u7684\u8eab\u5f62\u4e0d\u89c9\u6709\u4e9b\u98a4\u6296\uff0c\u8138\u4e0a\u7684\u8868\u60c5\u4e5f\u5e26\u7740\u60ca\u9a87\u7684\u6837\u5b50\uff0c\u88ab\u5251\u4e0a\u7684\u9752\u7eff\u8272\u7684\u5149\u8292\u4e00 \u7167\uff0c\u663e\u5f97\u751a\u662f\u96be\u770b\u3002 \u3000\u3000\u90a3\u4eba\u89c1\u4e86\uff0c\u773c\u4e2d\u6d41\u9732\u51fa\u5f97\u610f\u795e\u8272\uff0c\u563f\uff0c\u563f\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4eba\u8fd8\u662f\u5feb\u4e9b\u81ea\u88c1\u5427\u3002\u201d\u4ed6\u4e0d\u4f46 \u7b11\u58f0\u5a07\u5ae9\uff0c\u8fde\u8bf4\u8bdd\u90fd\u662f\u8f6f\u8f6f\u7684\uff0c\u4f46\u662f\u718a\u501c\u548c\u5c1a\u672a\u660e\u5374\u4e1d\u6beb\u6ca1\u6709\u53d1\u89c9\u4ed6\u58f0\u97f3\u7684\u597d\u542c\u3002 \u3000\u3000\u5c24\u5176\u5f53\u4ed6\u8bf4\u51fa\u53eb\u718a\u501c\u548c\u5c1a\u672a\u660e\u81ea\u88c1\u7684\u65f6\u5019\u3002 \u3000\u3000\u718a\u501c\u6697\u5fd6\uff1a\u201c\u8fd9\u53ae\u600e\u5730\u8fd9\u6837\u5947\u8be1\uff0c\u6211\u867d\u7136\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\u7684\u65f6\u5019\u4e0d\u591a\uff0c\u4f46\u662f\u738b\u667a\u9011\u3001\u5434\u8bcf \u4e91\u548c\u6211\u7684\u6069\u5e08\u90fd\u66fe\u7ecf\u8be6\u7ec6\u5730\u5c06\u6b66\u6797\u4e2d\u7684\u5389\u5bb3\u89d2\u8272\u544a\u8bc9\u8fc7\u6211\uff0c\u53ef\u662f\u6211\u5374\u4ece\u672a\u542c\u8bf4\u8fc7\u4e16\u4e0a\u8fd8\u6709\u4e2a \u8fd9\u6837\u7684\u4eba\u7269\u3002\u201d \u3000\u3000\u5c1a\u672a\u660e\u5fd6\u9053\uff1a\u201c\u8fd9\u5bb6\u4f19\u7684\u8f7b\u529f\u529f\u592b\u771f\u6709\u70b9\u7384\uff0c\u4ed6\u600e\u4e48\u6765\u5230\u8fd9\u91cc\u7684\uff0c\u6211\u8fde\u770b\u90fd\u6ca1\u6709\u770b \u5230\u3002\u201d \u3000\u3000\u201c\u8fd9\u53ae\u867d\u7136\u4e0d\u662f\u9b3c\u602a\uff0c\u53ef\u4e5f\u5dee\u4e0d\u591a\u4e86\uff0c\u6211\u4eec\u72af\u4e0d\u4e0a\u548c\u4ed6\u591a\u5939\u7f20\uff0c\u8d70\u4e3a\u4e0a\u7b56\u3002\u201d\u4ed6\u4e24\u4eba\u5fc3 \u4e2d\u4e0d\u7981\u540c\u6837\u5730\u6709\u6b64\u60f3\u6cd5\uff0c\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u811a\u4e00\u987f\uff0c\u8eab\u5f62\u731b\u5730\u7a81\u9ad8\uff0c\u5fae\u4e00\u8f6c\u6298\uff0c\u5411\u540e\u6025\u7a9c\u3002 \u3000\u3000\u90a3\u4eba\u5374\u672a\u89c1\u8ffd\u8d76\u3002 \u3000\u3000\u718a\u501c\u548c\u5c1a\u672a\u660e\u8eab\u5f62\u5982\u98de\uff0c\u9690\u7a33\u7ea6\u7ea6\u542c\u89c1\u90a3\u67d4\u8f6f\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u5230\u4e86\u751c\u751c\u8c37\u91cc\uff0c\u8fd8\u60f3 \u9003\u8d70\uff0c\u7b80\u76f4\u662f\u505a\u68a6\u3002\u201d \u3000\u3000\u4ed6\u4e24\u4eba\u5934\u4e5f\u4e0d\u56de\uff0c\u718a\u501c\u7528\u529b\u6293\u7740\u5c1a\u672a\u660e\u7684\u624b\u8155\uff0c\u4e24\u6761\u4eba\u5f71\u5982\u7535\u95ea\u800c\u53bb\u3002 \u3000\u3000\u53ef\u662f\u5f53\u4ed6\u4eec\u8eab\u5f62\u8d77\u843d\u4e86\u6570\u6b21\u7684\u65f6\u5019\uff0c\u5c31\u4e0d\u7981\u505c\u4e86\u4e0b\u6765\uff0c\u8fd9\u5012\u4e0d\u662f\u4ed6\u4eec\u4e0d\u613f\u610f\u518d\u9003\uff0c\u800c\u662f \u4ed6\u4eec\u53d1\u89c9\u8fd9\u5c71\u8c37\u7adf\u662f\u4e00\u4e2a\u7edd\u5730\uff0c\u56db\u9762\u90fd\u662f\u5343\u4ede\u9ad8\u5c71\uff0c\u62ac\u5934\u671b\u53bb\uff0c\u6839\u672c\u8fde\u5cf0\u5934\u5728\u54ea\u91cc\u90fd\u770b\u4e0d \u5230\u3002 \u3000\u3000\u800c\u4e14\u8fd9\u4e9b\u5c71\u5cf0\u76f4\u4e0a\u76f4\u843d\uff0c\u7b80\u76f4\u8fde\u4e00\u70b9\u659c\u5761\u90fd\u6ca1\u6709\uff0c\u4ed4\u7ec6\u4e00\u770b\uff0c\u4ed6\u4e24\u4eba\u66f4\u4e0d\u7981\u53eb\u82e6\u3002 \u3000\u3000\u4e24\u4eba\u6cbf\u7740\u5c71\u811a\u67e5\u770b\u4e86\u4e00\u904d\uff0c\u8fd9\u5c71\u8c37\u679c\u7136\u662f\u4e2a\u7edd\u5730\uff0c\u718a\u501c\u8bf4\u9053\uff1a\u201c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u6211\u4eec\u518d\u5411 \u90a3\u8c37\u53e3\u95ef\u4e00\u95ef\uff0c\u90a3\u5bb6\u4f19\u53ea\u8981\u662f\u4eba\uff0c\u6211\u5c31\u4e0d\u4fe1\u4ee5\u6211\u4eec\u4e24\u4eba\u7684\u529f\u529b\uff0c\u8fd8\u5bf9\u4ed8\u4e0d\u4e86\u4ed6\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u4e24\u4eba\u53c8\u6765\u5230\u90a3\u5939\u9699\uff0c\u4f46\u5374\u89c1\u9699\u53e3\u7a7a\u8361\u8361\u7684\uff0c\u5c45\u7136\u6ca1\u6709\u4eba\u5f71\uff0c\u90a3\u602a\u4eba\u5df2\u4e0d\u5728\u4e86\u3002 \u3000\u3000\u5c1a\u672a\u660e\u5927\u559c\u9053\uff1a\u201c\u5feb\u8d70\u3002\u201d \u3000\u3000\u4ed6\u89c1\u90a3\u5939\u9699\uff0c\u72ed\u53ea\u4e24\u5c3a\uff0c\u4e24\u4eba\u65e0\u6cd5\u5e76\u80a9\u800c\u51fa\uff0c\u4fbf\u9053\uff1a\u201c\u5927\u54e5\u5148\u8d70\u3002\u201d\u718a\u501c\u55ef\u4e86\u4e00\u58f0\uff0c\u4fbf \u7a9c\u5165\u9699\u4e2d\uff0c\u4ed6\u77e5\u9053\u5c1a\u672a\u660e\u7684\u8c26\u8ba9\u7edd\u4e0d\u4f1a\u56e0\u81ea\u5df1\u7684\u8bdd\u800c\u6539\u53d8\u7684\uff0c\u4e3a\u4e86\u8282\u7701\u65f6\u5019\uff0c\u5c31\u5148\u8fdb\u4e86\u53bb\u3002 \u3000\u3000\u5c1a\u672a\u660e\u4e5f\u4e0d\u6562\u8fdf\u7591\uff0c\u521a\u7a9c\u5165\u8c37\u4e2d\uff0c\u7a81\u7136\u542c\u89c1\u5939\u7f1d\u4e2d\u201c\u53ee\u53ee\u5f53\u5f53\u201d\uff0c\u4e00\u8fde\u4e32\u58f0\u54cd\uff0c\u811a\u6b65\u5fae \u4e00\u8fdf\u7591\uff0c\u718a\u501c\u5df2\u66b4\u9000\u4e86\u51fa\u6765\uff0c\u4e00\u628a\u62c9\u4f4f\u4e86\u4ed6\uff0c\u4f4e\u58f0\u559d\u9053\uff1a\u201c\u5feb\u9000\u3002\u201d\u53c8\u7a9c\u4eba\u8c37\u91cc\uff0c\u5c1a\u672a\u660e\u77e5 \u9053\u53c8\u751f\u51fa\u65b0\u7684\u53d8\u5316\uff0c\u8d76\u7d27\u95ee\u9053\uff1a\u201c\u5927\u54e5\uff0c\u53c8\u51fa\u4e86\u4ec0\u4e48\u4e8b\uff1f\uff0c\u718a\u501c\u4e00\u58f0\u4e0d\u54cd\uff0c\u4e24\u773c\u7d27\u7d27\u76ef\u7740\u8c37 \u53e3\uff0c\u8138\u4e0a\u7adf\u9732\u51fa\u6050\u6016\u7684\u795e\u8272\u6765\u3002\u539f\u6765\u718a\u501c\u7a9c\u5165\u5939\u7f1d\u4e4b\u4e2d\uff0c\u4fbf\u542c\u5230\u98ce\u58f0\u55d6\u7136\uff0c\u53c8\u662f\uff1a\u4e00\u5927\u7247\u6697 \u5668\u98de\u6765\uff0c\u4ed6\u53cc\u638c\u62a4\u8eab\uff0c\u54ea\u77e5\u9053\u90a3\u4e9b\u6697\u5668\u5e76\u4e0d\u662f\u671d\u4ed6\u8eab\u4e0a\u6253\u6765\u7684\uff0c\u5374\u5206\u4e24\u8fb9\u5411\u5c71\u58c1\u98de\u53bb\uff0c\u718a\u501c \u5fae\u5fae\u4e00\u6115\uff0c\u54ea\u77e5\u201d\u53ee\u53ee\u201c\u4e00\u7247\u58f0\u54cd\uff0c\u90a3\u4e9b\u6697\u5668\u7a81\u5730\u81ea\u58c1\u4e0a\u53cd\u58f0\u800c\u51fa\uff0c\u718a\u501c\u5927\u9a87\uff0c\u731b\u5f80\u540e\u9000\uff0c \u8eb2\u8fc7\u8fd9\u4e00\u9635\u50cf\u96e8\u4e00\u6837\u7684\u6697\u5668\uff0c\u521a\u4e00\u62b9\u6c57\uff0c\u811a\u5e95\u53c8\u6709\u98ce\u58f0\u5506\u7136\uff0c\u4ed6\u518d\u5f80\u4e0a\u4e00\u62d4\uff0c\u539f\u6765\u90a3\u4e9b\u6697\u5668 \u81ea\u58c1\u4e0a\u843d\u5230\u5730\u4e0b\u4e4b\u540e\uff0c\u53c8\u4ece\u5730\u4e0a\u53cd\u6fc0\u800c\u4e0a\uff0c\u8ddf\u7740\u5411\u718a\u501c\u5c04\u53bb\uff0c\u7adf\u4f3c\u957f\u7740\u773c\u775b\u4f3c\u7684\u3002\u4e24\u4eba\u56db\u53ea \u773c\u775b\uff0c\u9f50\u90fd\u77aa\u4f4f\u5939\u7f1d\uff0c\u7a81\u5730\u5939\u7f1d\u4e2d\u7f13\u7f13\u5730\u8e31\u51fa\u4e00\u4eba\uff0c\u5168\u8eab\u5c3d\u81ea\uff0c\u957f\u886b\u98d8\u98d8\uff0c\u6f47\u6d12\u5df2\u6781\uff0c\u54ea\u91cc \u662f\u524d\u89c1\u90a3\u4eba\u7684\u4e11\u6001\u3002\u4e24\u4eba\u66f4\u662f\u4e00\u60ca\uff0c\u718a\u501c\u671d\u90a3\u4eba\u7684\u8138\u4e0a\u4e00\u671b\uff0c\u89c1\u90a3\u4eba\u5251\u7709\u661f\u76ee\uff0c\u4e30\u795e\u51b2\u5937\uff0c \u662f\u4e2a\u6781\u82f1\u4fca\u7684\u7537\u5b50\uff0c\u5c24\u5176\u662f\u4ed6\u5507\u8fb9\u5df2\u6709\u4e86\u4e9b\u77ed\u987b\uff0c\u4f7f\u4ed6\u770b\u8d77\u6765\u66f4\u6709\u4e00\u79cd\u5438\u5f15\u4eba\u7684\u529b\u91cf\uff0c\u53ea\u662f \u4ed6\u7709\u5fc3\u5fae\u76b1\uff0c\u795e\u60c5\u663e\u5f97\u5341\u5206\u5fe7\u90c1\u3002\u6b64\u65f6\u5df2\u8fd1\u9ece\u660e\uff0c\u4e1c\u65b9\u5df2\u9732\u51fa\u5fae\u767d\uff0c\u501f\u7740\u8fd9\u4e9b\u8bb8\u5fae\u5149\uff0c\u7ec3\u6b66 \u4eba\u7684\u76ee\u529b\u5df2\u4e0d\u96be\u770b\u51fa\u5bf9\u65b9\u7684\u9762\u76ee\u3002\u662f\u4ee5\u5c1a\u672a\u660e\u80fd\u770b\u51fa\u4ed6\u7684\u9762\u8c8c\uff0c\u4ed6\u4e5f\u80fd\u770b\u51fa\u718a\u501c\u548c\u5c1a\u672a\u660e\u4e24 \u4eba\u7684\u9762\u8c8c\uff0c\u4e00\u89c1\u4e4b\u4e0b\uff0c\u4e5f\u4e0d\u89c9\u8d77\u4e86\u60fa\u60fa\u76f8\u60dc\u4e4b\u5fc3\uff0c\u4fbf\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\u6562\u60c5\u662f\u9ed1\u591c\u4e4b\u4e2d\uff0c\u8ff7\u5931\u4e86 \u8def\u9014\u5417\uff1f\u201d\u8bed\u6c14\u4e4b\u4e2d\u5e76\u65e0\u6076\u610f\u5c0f\u718a\u501c\u5fd9\u8bf4\u9053\uff1a\u201c\u6b63\u662f\uff0c\u5728\u4e0b\u718a\u501c\u548c\u76df\u5f1f\u5c1a\u672a\u660e\uff0c\u6df1\u5c71\u5931\u5411\u3002 \u8bef\u95ef\u8d35\u8c37\uff0c\u8fd8\u671b\u9601\u4e0b\u80fd\u6055\u5728\u4e0b\u7b49\u8bef\u4eba\u4e4b\u7f6a\u3002\u201d \u3000\u3000\u90a3\u4eba\u7709\u5934\u76b1\u5f97\u66f4\u7d27\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u7a81\u5730\u4eba\u5f71\u4e00\u604d\uff0c\u90a3\u8be1\u5f02\u7684\u4e11\u4eba\u5df2\u7ad9\u5728\u4ed6\u65c1\u8fb9\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u4e0d\u884c\u3002\u201d \u3000\u3000\u4e24\u4eba\u4fca\u7684\u6781\u4fca\uff0c\u4e11\u7684\u6781\u4e11\uff0c\u76f8\u5f62\u4e4b\u4e0b\uff0c\u66f4\u663e\u5f97\u90a3\u602a\u4eba\u4e11\u5f97\u9a87\u4eba\uff0c\u718a\u501c\u53ea\u89c9\u89c1\u4e86\u6b64\u4eba\u540e\uff0c \u5fc3\u4e2d\u8bf4\u4e0d\u51fa\u7684\u96be\u53d7\uff0c\u50cf\u662f\u8981\u5410\u3002 \u3000\u3000\u53ef\u662f\u90a3\u82f1\u4fca\u7537\u5b50\u89c1\u4e86\u4ed6\uff0c\u8138\u4e0a\u5374\u6d41\u9732\u51fa\u4e00\u79cd\u6e29\u67d4\u4e4b\u8272\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u654f\u654f\uff0c\u4f60\u7b49\u4e00\u4f1a\u518d\u8bf4 \u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u201c\u654f\u654f\u201d\u6c14\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u53c8\u6765\u4e86\uff0c\u4f60\u2026\u2026\u4f60\u662f\u4e0d\u662f\u60f3\u6211\u7684\u8fd9\u526f\u6837\u5b50\u7ed9\u522b\u4eba\u770b\u4e86\u2026\u2026\u201d \u53e3\u97f3\u4ecd\u7136\u53c8\u5a07\u53c8\u5ae9\uff0c\u800c\u4e14\u7adf\u7136\u5e26\u7740\u4e9b\u51c4\u695a\u7684\u5473\u9053\uff0c\u53ef\u662f\u4ed6\u7684\u8138\u5374\u4ecd\u7136\u662f\u5e73\u5e73\u677f\u677f\uff0c\u51b7\u5f97\u5165\u9aa8 \u7684\u6837\u5b50\u3002 \u3000\u3000\u90a3\u82f1\u4fca\u7537\u5b50\u957f\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\u4e24\u4f4d\u6b64\u523b\u5fc5\u5b9a\u7591\u56e2\u91cd\u91cd\uff0c\u800c\u4e14\u6211\u770b\u4e24\u4f4d\u4ff1\u90fd\u8eab \u6000\u7edd\u6280\uff0c\u53ef\u662f\u8bb8\u591a\u5e74\u6765\uff0c\u53ea\u8981\u5165\u6b64\u8c37\u4e2d\u7684\u4eba\uff0c\u4ece\u6ca1\u6709\u4e00\u4e2a\u80fd\u5168\u8eab\u800c\u9000\uff0c\u4e24\u4f4d\u81ea\u4e5f\u4e0d\u4f8b \u5916\u2026\u2026\u201d \u3000\u3000\u90a3\u201c\u654f\u654f\u201d\u51b7\u7b11\u4e00\u58f0\uff0c\u62ac\u5934\u5411\u90a3\u82f1\u4fca\u7537\u5b50\u9053\uff1a\u201c\u4f60\u518d\u4e0d\u52a8\u624b\uff0c\u6211\u2026\u2026\u6211\u5c31\u6b7b\u7ed9\u4f60\u770b\u3002\u201d \u3000\u3000\u90a3\u82f1\u4fca\u7537\u5b50\u601c\u60dc\u5730\u671b\u4e86\u4ed6\u4e00\u773c\uff0c\u53c8\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u8f6c\u8138\u5411\u718a\u501c\u8bf4\uff1a\u201c\u4e24\u4f4d\u90fd\u662f\u5c11\u5e74\u82f1\u96c4\uff0c \u8fd9\u6837\u6b7b\u53bb\u786e\u662f\u53ef\u60dc\uff0c\u6211\u867d\u591a\u5e74\u6765\u672a\u66fe\u8d70\u52a8\u6c5f\u6e56\uff0c\u53ef\u662f\u5374\u4e5f\u770b\u5f97\u51fa\u4e24\u4f4d\u5fc5\u5b9a\u662f\u9ad8\u4eba\u5b50\u5f1f\uff0c\u4e24\u4f4d \u53ef\u66fe\u542c\u4eba\u8bf4\u8fc7\uff0c\u5341\u5e74\u4e4b\u524d\uff0c\u6709\u4f4d\u53eb\u505a\u5e38\u6f2b\u5929\u7684\u4eba\uff1f\u201d \u3000\u3000\u718a\u501c\u8111\u6d77\u4e2d\u6781\u5feb\u5730\u641c\u7d22\u7740\u8bb0\u5fc6\uff0c\u65b9\u81ea\u60f3\u8d77\u4e00\u4eba\uff0c\u5c1a\u672a\u660e\u5df2\u60ca\u9053\uff1a\u201c\u96be\u9053\u9601\u4e0b\u7adf\u662f\u5341\u516d\u5c81 \u4fbf\u5df2\u63a5\u638c\u897f\u5357\u7b2c\u4e00\u5251\u6d3e\u70b9\u82cd\u95e8\u6237\uff0c\u6c5f\u6e56\u4eba\u79f0\u7389\u9762\u795e\u5251\u7684\u5e38\u5927\u4fa0\u5417\uff1f\u201d\u4ed6\u6362\u4e86\u4e00\u53e3\u6c14\uff0c\u53c8\u8bf4 \u9053\uff1a\u201c\u5e38\u5927\u4fa0\u4e5d\u5e74\u524d\u7a81\u7136\u5931\u8e2a\uff0c\u5374\u539f\u6765\u662f\u9690\u5c45\u81f3\u6b64\u4e86\u3002\u201d \u3000\u3000\u5e38\u6f2b\u65e0\u5fae\u5fae\u70b9\u5934\uff0c\u9762\u4e0a\u7684\u5fe7\u90c1\u4e4b\u8272\u66f4\u6d53\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u65e2\u662f\u77e5\u9053\u6211\u7684\u540d\u5b57\uff0c\u90a3\u518d\u597d\u4e5f\u6ca1 \u6709\uff0c\u6211\u4eca\u65e5\u6743\u4e14\u4f5c\u4e3b\uff0c\u53ea\u8981\u4e24\u4f4d\u7559\u4e0b\u4e24\u6837\u4e1c\u897f\u6765\uff0c\u4fbf\u53ef\u8d70\u51fa\u6b64\u8c37\u2026\u2026\u201d\u718a\u501c\u63a5\u53e3\u9053\uff1a\u201c\u4ec0\u4e48 \u4e1c\u897f\uff1f\u201d \u3000\u3000\u201c\u4fbf\u662f\u4e24\u4f4d\u7684\u773c\u775b\u548c\u820c\u5934\u3002\u201d \u3000\u3000\u718a\u501c\u53ca\u5c1a\u672a\u660e\u90fd\u4ee5\u4e3a\u8fd9\u7389\u9762\u795e\u5251\u751a\u4e3a\u901a\u8fbe\u60c5\u7406\uff0c\u518d\u4e5f\u60f3\u4e0d\u5230\u4ed6\u4f1a\u8bf4\u51fa\u8fd9\u53e5\u8bdd\u6765\uff0c\u4e00\u6115\u4e4b \u4e0b\u4e0d\u7981\u6c14\u5f80\u4e0a\u649e\uff0c\u51b7\u51b7\u9f50\u58f0\u9053\uff1a\u201c\u4e0d\u7136\u5462\uff1f\u201d \u3000\u3000\u201c\u654f\u654f\u201d\u51b7\u7b11\u9053\uff1a\u201c\u4e0d\u7136\uff0c\u4f60\u4eec\u5c31\u5f97\u628a\u547d\u7559\u4e0b\u3002\u201d \u3000\u3000\u718a\u501c\u6717\u58f0\u7b54\u9053\uff1a\u201c\u6211\u4e24\u4eba\u867d\u7136\u662f\u6b66\u6797\u540e\u8f88\uff0c\u4f46\u81ea\u51fa\u4e16\u4ee5\u6765\uff0c\u53ef\u8fd8\u6ca1\u6709\u89c1\u5230\u50cf\u9601\u4e0b\u8fd9\u6837\u7684 \u4eba\u7269\uff0c\u6765\uff0c\u6765\uff0c\u6211\u4e24\u4eba\u7684\u773c\u775b\u548c\u820c\u5934\u90e8\u5728\u6b64\uff0c\u9601\u4e0b\u53ea\u7ba1\u6765\u53d6\u5c31\u662f\u4e86\u3002\u201d\u4ed6\u53c8\u6717\u58f0\u957f\u7b11\uff0c\u4e00\u626c \u5251\uff0c\u9053\uff1a\u201c\u53ea\u662f\u5149\u51ed\u4e09\u4e24\u53e5\u8bdd\uff0c\u5374\u4e5f\u4e0d\u884c\u5462\u3002\u201d \u3000\u3000\u5e38\u6f2b\u5929\u4e00\u6014\u9053\uff1a\u201c\u4f60\u8981\u52a8\u624b\uff1f\u201d \u3000\u3000\u4ed6\u5341\u516d\u5c81\u4fbf\u540d\u6ee1\u5929\u4e0b\uff0c\u6b64\u523b\u867d\u4ec5\u5345\u4f59\u5c81\uff0c\u4f46\u8f88\u4efd\u6781\u9ad8\uff0c\u5341\u5e74\u524d\u6c5f\u6e56\u4e2d\u4eba\uff0c\u53ea\u8981\u542c\u5230\u4ed6\u7684 \u540d\u5934\uff0c\u83ab\u4e0d\u5934\u76ae\u53d1\u9ebb\u3002 \u3000\u3000\u4ed6\u6210\u540d\u5728\u661f\u6708\u53cc\u5251\u4e4b\u540e\uff0c\u5374\u53c8\u5728\u718a\u501c\u827a\u6210\u4e4b\u524d\uff0c\u662f\u4ee5\u4ed6\u5e76\u4e0d\u77e5\u9053\u8fd9\u4e24\u4e2a\u5c11\u5e74\uff0c\u7adf\u662f\u6c5f\u6e56 \u4e2d\u58f0\u540d\u8d6b\u8d6b\u7684\u4eba\u7269\uff0c\u542c\u5230\u4ed6\u4eec\u5c45\u7136\u6ca1\u6709\u88ab\u81ea\u5df1\u7684\u540d\u5934\u6240\u60e7\uff0c\u4e0d\u7981\u60ca\u5f02\uff0c\u718a\u501c\u5374\u5df2\u63a5\u53e3\u9053\uff1a \u201c\u6b63\u662f\u3002\u201d \u3000\u3000\u201c\u654f\u654f\u201d\u9053\uff1a\u201c\u5927\u54e5\uff0c\u7094\u52a8\u624b\u561b\uff0c\u8fd8\u8ddf\u4ed6\u7f57\u5506\u4ec0\u4e48\u3002\u201d \u3000\u3000\u5e38\u6f2b\u5929\u8f6c\u8138\u5411\u4ed6\u8bf4\u9053\uff1a\u201c\u4f60\u5148\u8ba9\u6211\u4e00\u4e2a\u4eba\u8bd5\u8bd5\u3002\u201d \u3000\u3000\u201c\u654f\u654f\u201d\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\u8fd9\u51e0\u5e74\u4f60\u618b\u5f97\u614c\uff0c\u624b\u5728\u53d1\u75d2\u662f\u4e0d\u662f\uff1f\u201d\u7b11\u5f97\u4ecd\u662f\u90a3\u4e48\u52a8\u542c\u3002 \u3000\u3000\u5e38\u6f2b\u5929\u56de\u8fc7\u5934\u53bb\uff0c\u6084\u6084\u95ed\u8d77\u4e86\u773c\u775b\uff0c\u4f3c\u4e4e\u5c06\u201c\u654f\u654f\u201d\u7684\u7b11\u58f0\u770b\u4f5c\u4e16\u4e0a\u6700\u5999\u7684\u97f3\u4e50\u3002 \u3000\u3000\u7136\u540e\uff0c\u4ed6\u773c\u5e18\u4e0a\u4eff\u4f5b\u6302\u4e86\u4e00\u9897\u6cea\u73e0\uff0c\u4ed6\u4f38\u624b\u62b9\u53bb\uff0c\u53cd\u8155\u64a4\u51fa\u8eab\u540e\u7684\u957f\u5251\uff0c\u9752\u6c14\u68ee\u7136\uff0c\u4e5f \u662f\u53e3\u5229\u5668\uff0c\u4ed6\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\u8bf7\u52a8\u624b\u5427\u3002\u201d \u3000\u3000\u718a\u501c\u50b2\u7136\u4e00\u7b11\uff0c\u4e5f\u5411\u5c1a\u672a\u660e\u9053\uff1a\u201c\u4e8c\u5f1f\uff0c\u4f60\u4e5f\u8ba9\u6211\u5148\u8bd5\u8bd5\uff0c\u6211\u4e0d\u6210\u4f60\u518d\u4e0a\u3002\u201d \u3000\u3000\u7389\u9762\u795e\u5251\u5e38\u6f2b\u65e0\u5f53\u5251\u5e73\u80f8\uff0c\u4e00\u5f39\u5251\u8eab\uff0c\u201c\u545b\u201d\u5730\u53d1\u51fa\u4e00\u58f0\u9f99\u541f\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u8fd8\u662f \u4e00\u8d77\u4e0a\u5427\u3002\u8fd9\u662f\u6027\u547d\u76f8\u640f\uff0c\u53ef\u4e0d\u662f\u6bd4\u6b66\uff0c\u4e24\u4f4d\u4e5f\u7528\u4e0d\u7740\u5ba2\u6c14\u3002\u201d\u8bed\u6c14\u4e4b\u4e2d\uff0c\u663e\u7136\u81ea\u8d1f\u5df2\u6781\u3002 \u3000\u3000\u718a\u501c\u7d27\u95ed\u7740\u5507\uff0c\u53f3\u624b\u6301\u5251\uff0c\u5de6\u624b\u5fae\u634f\u5251\u8bc0\uff0c\u4e00\u62db\u201c\u91d1\u9e1f\u521d\u5347\u201d\u5251\u5c16\u4e0b\u5782\uff0c\u6162\u6162\u53f3\u624b\u5e73 \u4f38\uff0c\u7a81\u5730\u5411\u4e0a\u659c\u524a\uff0c\u6b63\u662f\u201c\u82cd\u7a79\u5341\u4e09\u5f0f\u201d\u91cc\u7684\u8d77\u624b\u4e4b\u5f0f\uff0c\u4ed6\u8fd9\u4e00\u62db\u795e\u5b9a\u6c14\u8db3\uff0c\u610f\u5728\u5251\u5148\uff0c\u52b2 \u5f0f\uff0c\u529f\u529b\uff0c\u65e0\u4e00\u4e0d\u662f\u6070\u5230\u597d\u5904\uff0c\u6bd4\u5728\u4e34\u57ce\u521d\u9047\u5f3a\u654c\u5929\u5c71\u4e09\u9f99\u949f\u5929\u4ec7\u65f6\uff0c\u529f\u529b\u53c8\u589e\u8fdb\u4e86\u4e0d\u5c11\u3002 \u3000\u3000\u4ed6\u6b64\u62db\u770b\u6765\u5e73\u5e73\u65e0\u5947\uff0c\u4f46\u5176\u4e2d\u5374\u5305\u542b\u7740\u65e0\u7a77\u53d8\u5316\uff0c\u7389\u9762\u795e\u5251\u81ea\u662f\u8bc6\u8d27\uff0c\u8131\u53e3\u8d5e\u9053\uff1a\u201c\u597d \u5251\u6cd5\u3002\u201d \u3000\u3000\u718a\u501c\u5fae\u5fae\u4e00\u7b11\uff0c\u5251\u5c16\u5e26\u8d77\u4e00\u6e9c\u9752\u5149\uff0c\u76f4\u5230\u5e38\u6f2b\u5929\u7684\u9762\u95e8\u3002 \u3000\u3000\u7389\u9762\u795e\u5251\u8eab\u5f62\u8d70\uff0c\u5e73\u5251\u6a2a\u524a\uff0c\u5239\u90a3\u95f4\u4f46\u89c1\u5251\u5f71\u6f2b\u5929\u3002\u5c1a\u672a\u660e\u4e00\u65c1\u70b9\u5934\u5fd6\u9053\uff1a\u201c\u70b9\u82cd\u5251 \u6cd5\uff0c\u7aef\u7684\u540d\u4e0d\u865a\u4f20\u3002\u201d \u3000\u3000\u718a\u501c\u4e8c\u6b21\u51fa\u5e08\uff0c\u6ee1\u8154\u58ee\u5fd7\uff0c\u6b64\u65f6\u6597\u9022\u5f3a\u654c\uff0c\u5f53\u4e0b\u6296\u64de\u7cbe\u795e\u5168\u529b\u5e94\u4ed8\uff0c\u201c\u82cd\u7a79\u5341\u4e09\u5f0f\u201d\u91cc \u52a0\u4e0a\u201c\u98d8\u7136\u8001\u4eba\u201d\u4eb2\u4f20\u7684\u5251\u6cd5\uff0c\u8eab\u5f62\u7eb5\u6a2a\u8d77\u843d\uff0c\u5251\u5149\u5982\u82b1\u96e8\u7f24\u7eb7\uff0c\u4e24\u4eba\u62c6\u4e86\u4e09\u6570\u5341\u62db\uff0c\u5c45\u7136 \u672a\u5206\u80dc\u8d1f\u3002 \u3000\u3000\u5e38\u6f2b\u5929\u6697\u6697\u5fc3\u60ca\uff1a\u201c\u6b66\u6797\u4e2d\u600e\u5730\u51fa\u4e86\u8fd9\u6837\u7684\u597d\u624b\uff1f\u201d \u3000\u3000\u5c1a\u672a\u660e\u5728\u65c1\u8fb9\u770b\u5f97\u7709\u98de\u8272\u821e\uff0c\u5374\u53c8\u4e0d\u514d\u63d0\u5fc3\u540a\u80c6\uff0c\u751f\u6015\u718a\u501c\u52a8\u624b\u65f6\u95f4\u4e00\u957f\uff0c\u4fbf\u62b5\u654c\u4e0d\u4f4f \u8fd9\u4e2a\u540d\u6ee1\u6b66\u6797\u7684\u70b9\u82cd\u540d\u5251\u624b\u3002 \u3000\u3000\u201c\u654f\u654f\u201d\u7684\u4e00\u53cc\u773c\u795e\uff0c\u4e5f\u968f\u7740\u8fd9\u4e24\u4eba\u7684\u8eab\u5f62\u8f6c\u52a8\uff0c\u4f46\u662f\u4ed6\u7684\u8138\uff0c\u5374\u4ecd\u7136\u6ca1\u6709\u4e00\u4e1d\u8868\u60c5\u3002 \u3000\u3000\u5f53\u5e74\u7389\u9762\u795e\u5251\u63a5\u638c\u70b9\u82cd\u95e8\u6237\u65f6\uff0c\u5929\u4e0b\u6b66\u6797\u90fd\u8ba4\u4e3a\u4ed6\u5e74\u7eaa\u592a\u8f7b\uff0c\u800c\u6709\u8f7b\u89c6\u7684\u610f\u601d\u3002 \u3000\u3000\u987b\u77e5\u90a3\u70b9\u82cd\u6d3e\u4e43\u4e94\u5927\u5251\u6d3e\u4e4b\u4e00\uff0c\u597d\u624b\u81ea\u662f\u6781\u591a\uff0c\u5927\u5bb6\u5374\u662f\u7531\u8fd9\u4e00\u5e74\u4ec5\u5341\u516d\u5c81\u7684\u5c11\u5e74\u6765\u4efb \u638c\u95e8\uff0c\u5fc3\u4e2d\u4e0d\u670d\u3002 \u3000\u3000\u5e38\u6f2b\u5929\u5f53\u65f6\u5c11\u5e74\u6027\u50b2\uff0c\u91cd\u9080\u6b66\u6797\u5404\u6d3e\u5251\u624b\uff0c\u96c6\u4f1a\u70b9\u82cd\u5c71\uff0c\u5f53\u4f17\u58f0\u8a00\u53ea\u8981\u6709\u4eba\u80fd\u80dc\u5f97\u4ed6\u4e00 \u62db\uff0c\u6b64\u4eba\u82e5\u662f\u70b9\u82cd\u95e8\u5f92\uff0c\u4ed6\u4fbf\u5c06\u638c\u95e8\u4e4b\u4f4d\u76f8\u8ba9\uff0c\u6b64\u4eba\u82e5\u975e\u70b9\u82cd\u95e8\u4eba\uff0c\u4ed6\u4fbf\u7acb\u523b\u62dc\u6b64\u4eba\u4e3a\u5e08\uff0c \u9000\u51fa\u70b9\u82cd\u6d3e\uff0c\u7531\u70b9\u82cd\u95e8\u4eba\u91cd\u9009\u638c\u95e8\u3002 \u3000\u3000\u70b9\u82cd\u95e8\u96c6\u4f1a\u4e09\u5929\uff0c\u6b66\u6797\u4e2d\u7a0d\u6709\u540d\u6c14\u7684\u5251\u624b\uff0c\u90fd\u4e0d\u8fdc\u5343\u91cc\u6765\u5230\u4e91\u5357\uff0c\u53c2\u4e0e\u6b64\u76db\u4f1a\u3002 \u3000\u3000\u7389\u9762\u795e\u5251\u5728\u8fd9\u4e09\u5929\u91cc\uff0c\u8fde\u8d25\u5341\u4e00\u4e2a\u540d\u5bb6\u5251\u624b\uff0c\u6b66\u6797\u4e2d\u8fd9\u624d\u5927\u4e3a\u9707\u60ca\uff0c\u7389\u9762\u795e\u5251\u4e4b\u540d\uff0c\u9042 \u4e5f\u4f20\u904d\u4e86\u6b66\u6797\u3002 \u3000\u3000\u4ed6\u6b64\u523b\u548c\u718a\u501c\u52a8\u624b\u6570\u5341\u62db\uff0c\u5374\u5e76\u672a\u5206\u80dc\u8d1f\uff0c\u6697\u4ed8\u9053\uff1a\u201c\u8fd9\u5c11\u5e74\u5251\u6cd5\u602a\u5f02\uff0c\u7adf\u4f3c\u4e0d\u5728\u5f53\u5e74 \u6211\u95ef\u8361\u6c5f\u6e56\u65f6\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u4ed6\u6fc0\u8d77\u597d\u80dc\u4e4b\u5fc3\uff0c\u8eab\u6cd5\u7a81\u5730\u4e00\u53d8\uff0c\u4f46\u89c1\u4eba\u5f71\u95ea\u52a8\uff0c\u5251\u5149\u6216\u5de6\u6216\u53f3\uff0c\u56db\u9762\u516b\u65b9\u5730\u63a0\u4e86\u8fc7 \u6765\u3002 \u3000\u3000\u4e24\u4eba\u8f6c\u77ac\u6597\u4e86\u6570\u5341\u62db\uff0c\u718a\u501c\u4e1d\u6beb\u6ca1\u6709\u8d25\u8c61\u3002 \u3000\u3000\u201c\u654f\u654f\u201d\u5ffd\u7136\u8f7b\u8f7b\u4e00\u7b11\uff0c\u6162\u6162\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u521a\u521a\u8bf4\u8fd9\u4e0d\u662f\u6bd4\u6b66\uff0c\u6240\u4ee5\u7528\u4e0d\u7740\u5ba2\u6c14\u662f \u4e0d\u662f\uff1f\u201d \u3000\u3000\u201c\u654f\u654f\u201d\u7f13\u7f13\u53c8\u8bf4\uff1a\u201c\u90a3\u4e48\uff0c\u6211\u5c31\u51fa\u624b\u4e86\u3002\u201d \u3000\u3000\u8bdd\u58f0\u624d\u843d\uff0c\u7a81\u63a2\u624b\u5165\u56ca\uff0c\u6293\u7740\u4e00\u628a\u7cbe\u5149\u8000\u76ee\u7684\u6781\u5c0f\u7684\u5f39\u4e38\uff0c\u53cc\u624b\u4e00\u6325\uff0c\u90a3\u4e9b\u5f39\u4e38\u4fbf\u500f\u5730 \u98de\u51fa\uff0c\u7a7f\u4eba\u770b\u4f3c\u70b9\u6c34\u96be\u5165\u7684\u5251\u5f71\u4e2d\uff0c\u4e13\u5411\u718a\u501c\u8eab\u4e0a\u62db\u547c\uff0c\u6709\u7684\u6253\u5728\u5730\u4e0a\u7684\uff0c\u7a81\u5730\u8df3\u4e86\u8d77\u6765\uff0c \u88ad\u5411\u718a\u501c\u3002\u5c1a\u672a\u660e\u5927\u60ca\u4e4b\u4e0b\uff0c\u4e0d\u5047\u601d\u7d22\uff0c\u4e5f\u64a4\u5251\u8fdb\u8eab\uff0c\u8eab\u968f\u5251\u8d77\u3002 \u3000\u3000\u521a\u521a\u4e00\u5251\u523a\u5411\u5e38\u6f2b\u5929\uff0c\u7a81\u5730\u98ce\u58f0\u5506\u7136\uff0c\u5df2\u6709\u4e09\u4e94\u7c92\u4e38\u4e0a\u4e0b\u5de6\u53f3\u5411\u81ea\u5df1\u88ad\u6765\uff0c\u4ed6\u4e0d\u5f97\u4e0d\u64a4 \u5251\u81ea\u4fdd\uff0c\u4f46\u8fd9\u65f6\u5e38\u6f2b\u5929\u5df2\u4e00\u5251\u523a\u6765\u3002 \u3000\u3000\u718a\u501c\u53ca\u5c1a\u672a\u660e\u4e0d\u7981\u624b\u5fd9\u811a\u4e71\uff0c\u8fd9\u79cd\u6697\u5668\u548c\u5251\u5f0f\u4e92\u76f8\u914d\u5408\u7684\u6253\u6cd5\uff0c\u4ed6\u4fe9\u4eba\u8fde\u542c\u90fd\u6ca1\u6709\u542c \u8fc7\uff0c\u4f55\u51b5\u662f\u4eb2\u81ea\u5bf9\u654c\uff0c\u53ea\u6709\u5c06\u5251\u5148\u5728\u81ea\u5df1\u8eab\u524d\u6392\u8d77\u4e00\u7247\u5251\u5f71\uff0c\u6682\u6c42\u81ea\u4fdd\u3002\u201c\u5e38\u6f2b\u5929\u201c\u5237\uff0c \u5237\u201d\u4e24\u5251\uff0c\u4e0a\u6311\u7709\u5fc3\uff0c\u4e2d\u523a\u7384\u5173\u3002 \u3000\u3000\u718a\u501c\u4e00\u5251\u659c\u524a\uff0c\u4ece\u4ed6\u5251\u5149\u7684\u7a7a\u9699\u4e2d\u7a7f\u4e86\u8fc7\u53bb\uff0c\u8eab\u5f62\u5de6\u4fa7\uff0c\u907f\u8fc7\u6765\u62db\uff0c\u672c\u6765\u8fde\u524a\u5e26\u6253\u7684\u5999 \u7740\uff0c\u54ea\u77e5\u7a81\u7684\u51e0\u7c92\u5f39\u4e38\uff0c\u88ad\u5728\u81ea\u5df1\u548c\u5e38\u6f2b\u5929\u7684\u5251\u4e0a\uff0c\u55d6\u5730\uff0c\u53c8\u53cd\u6fc0\u800c\u51fa\uff0c\u5206\u88ad\u718a\u501c\u53f3\u816e\u3001\u54bd \u5589\u3001\u524d\u80f8\u3001\u808b\u4e0b\u3001\u4e0b\u9634\u7b49\u516d\u5904\u8981\u5bb3\uff0c\u98ce\u58f0\u5506\u7136\uff0c\u663e\u89c1\u5f97\u52b2\u529b\u60ca\u4eba\u3002 \u3000\u3000\u5e38\u6f2b\u5929\u4e5f\u4e58\u52bf\u4e24\u5251\uff0c\u523a\u5411\u718a\u501c\u81c2\u5f2f\u7684\u201c\u66f2\u6c60\u201d\uff0c\u592a\u9633\u7a74\u4e0a\u7684\u201c\u795e\u5c01\u201d\u4e24\u5904\u5927\u7a74\u3002\u718a\u501c\u4f46 \u89c9\u5168\u8eab\u4e0a\u4e0b\uff0c\u65e0\u4e00\u5904\u4e0d\u5728\u5bf9\u65b9\u7684\u653b\u51fb\u4e4b\u4e2d\uff0c\u7ade\u4f3c\u6709\u516b\u4e2a\u6b66\u6797\u597d\u624b\uff0c\u540c\u65f6\u6301\u5203\u5411\u81ea\u5df1\u88ad\u6765\uff0c\u5c1a \u672a\u660e\u773c\u89d2\u5fae\u52a8\uff0c\u4e5f\u81ea\u53d1\u89c9\uff0c\u4f46\u6b64\u523b\u6ee1\u5929\u5f39\u96e8\uff0c\u4ed6\u81ea\u5df1\u4fdd\u5168\u5c1a\u4e0d\u6687 \u3000\u3000\uff0c\u4e5f\u65e0\u6cd5\u51fa\u624b\u63f4\u6551\u3002 \u3000\u3000\u591a\u5e74\u6765\u6b66\u529f\u7684\u953b\u70bc\uff0c\u591a\u5c11\u6b21\u52a8\u624b\u7684\u7ecf\u9a8c\u4ee5\u53ca\u4ed6\u672c\u8eab\u90a3\u4e00\u4efd\u8fc7\u4eba\u7684\u806a\u9896\uff0c\u90fd\u544a\u8bc9\u718a\u501c\u4ed6\u65e0 \u8bba\u5de6\u907f\uff0c\u53f3\u95ea\uff0c\u6291\u6216\u662f\u4e0a\u62d4\uff0c\u90fd\u65e0\u6cd5\u8eb2\u5f00\u8fd9\u516b\u5904\u653b\u51fb\uff0c\u9664\u4e86\u2026\u2026\u201c\u9664\u4e86\u4e0b \u3000\u3000\u907f\u201d\u3002\u5c31\u5728\u8fd9\u95f4\u4e0d\u5bb9\u53d1\u7684\u4e00\u5239\u90a3\uff0c\u4ed6\u51b3\u5b9a\u4e86\u5e94\u8be5\u505a\u7684\u65b9\u6cd5\u3002 \u3000\u3000\u4ed6\u8eab\u5f62\u6025\u5267\u5730\u4e0b\u5012\uff0c\u624b\u4e2d\u7684\u5251\uff0c\u4e58\u52bf\u4e0a\u6311\uff0c\u683c\u4f4f\u4e86\u5e38\u6f2b\u5929\u6765\u7684\u4e00\u5251\uff0c\u524a\u5f00\u4e86\u88ad\u5411\u989d\u89d2\uff0c \u53f3\u816e\u7684\u4e24\u7c92\u5f39\u4e38\u3002\u5176\u4f59\u7684\u56db\u7c92\u5f39\u4e38\uff0c\u4ee5\u53ca\u5e38\u6f2b\u5929\u540e\u53d1\u7684\u4e00\u5251\uff0c\u90fd\u5728\u4ed6\u8eab\u5f62\u5012\u4e0b\u7684\u90a3\u4e00\u523b\u6253 \u7a7a\uff0c\u7136\u800c\u5374\u5df2\u90fd\u5feb\u89e6\u7740\u718a\u501c\u7684\u8863\u670d\u4e86\uff0c\u7740\u4ed6\u7a0d\u4e3a\u8e0c\u8e87\u6216\u8eab\u5f62\u7a0d\u6162\uff0c \u3000\u3000\u90fd\u7edd\u4e0d\u53ef\u80fd\u907f\u5f00\u8fd9\u516b\u5904\u7684\u653b\u51fb\u3002 \u3000\u3000\u4ed6\u66b4\u559d\u4e00\u58f0\uff0c\u5de6\u624b\u626c\u8d77\u4e00\u80a1\u52b2\u51e4\uff0c\u5411\u5e38\u6f2b\u5929\u5288\u53bb\uff0c\u53f3\u65f6\u4ee5\u53ca\u811a\u8ddf\uff0c\u731b\u4e00\u70b9\u5730\uff0c\u5411\u540e\u6025 \u7a9c\u3002\u7136\u540e\uff0c\u4ed6\u5de6\u81c2\u5411\u53f3\u4e00\u5212\uff0c\u8eab\u5f62\u7ffb\u8f6c\uff0c\u500f\u5730\u53d8\u4e86\u4e2a\u65b9\u5411\uff0c\u5411\u4e0a\u7a9c\u4e86\u4e08\u8bb8\uff0c\u817f\u8098\u5fae\u4e00\u66f2\u4f38\uff0c \u53c8\u4e00\u8f6c\u62c6\uff0c\u5251\u5149\u524d\u5f15\uff0c\u6b63\u662f\u201d\u82cd\u7a79\u5341\u4e09\u5f0f\u201c\u91cc\u7684\u7b2c\u4e94\u5f0f\u7684\u201c\u843d\u5730\u6d41\u661f\u201d\uff0c\u5e26\u8d77\u4e00\u7f15\u9510\u98ce\uff0c\u76f4 \u53d6\u7ad9\u5728\u65c1\u8fb9\u7684\u4e11\u4eba\u201c\u654f\u654f\u201d\u3002 \u3000\u3000\u201c\u82cd\u7a79\u5341\u4e09\u5f0f\u201d\u7684\u7edd\u5999\u62db\u5f0f\uff0c\u518d\u52a0\u4e0a\u201c\u6f5c\u5f62\u9041\u5f71\u201d\u7684\u65e0\u4e0a\u8f7b\u529f\uff0c\u5c31\u5728\u77ac\u606f\u95f4\uff0c\u4ed6\u53d8\u5e7b\u4e24 \u4e2a\u65b9\u5411\uff0c\u5168\u529b\u4e00\u51fb\uff0c\u5251\u5c3e\u7684\u5bf8\u8bb8\u5bd2\u8292\uff0c\u5728\u5fae\u5f31\u7684\u6668\u66e6\u91cc\uff0c\u4eff\u4f5b\u662f\u4e00\u9053\u7535\u95f4 \u3000\u3000\uff0c\u524d\u540e\u5341\u4e8c\u5e74\u7684\u82e6\u7ec3\uff0c\u5df2\u4f7f\u718a\u501c\u6210\u4e86\u7a7a\u524d\u808b\u5251\u624b\uff0c\u8d85\u8fc7\u4e86\u6570\u5341\u5e74\u6765\u8bb8\u591a\u5728\u6b66\u6797\u4e2d\u4eab\u6709\u76db \u8a89\u7684\u4eba\u7269\u3002\u4ece\u5c71\u8c37\u7684\u5e73\u9699\u91cc\u5c04\u51fa\u7684\u4e00\u9053\u65ed\u65e5\u7684\u91d1\u5149\uff0c\u7167\u7740\u718a\u501c\u7684\u5251\u5149\u4e00\u95ea\uff0c\u201c \u3000\u3000\u654f\u654f\u201d\u7684\u8ddf\u5149\u91cc\uff0c\u7a81\u7136\u6709\u4e00\u79cd\u5947\u5f02\u7684\u5149\u8292\uff0c\u50cf\u662f\u4e5f\u4f5c\u4e86\u4e2a\u91cd\u5927\u7684\u51b3\u5b9a\uff0c\u671b\u7740\u5251\u5149\u7684\u6765 \u52bf\uff0c\u975e\u4f46\u4e0d\u907f\uff0c\u53cd\u6709\u8fce\u4e0a\u53bb\u7684\u610f\u601d\u3002 \u3000\u3000\u718a\u501c\u201d\u55d6\u201c\u5730\u4e00\u5251\uff0c\u5df2\u523a\u4e2d\u201c\u654f\u654f\u201d\u7684\u80a9\u4e0b\u4e0e\u524d\u80f8\u4e4b\u95f4\uff0c\u5374\u201c\u535c\u201d\u4e00\u58f0\uff0c\u53d1\u51fa\u4e00\u79cd\u6781\u5947 \u602a\u7684\u58f0\u97f3\u3002\u8fd9\u79cd\u58f0\u97f3\uff0c\u7edd\u4e0d\u662f\u5f53\u4e00\u67c4\u5229\u5251\uff0c\u88ab\u6301\u5728\u4e00\u4e2a\u5185\u5bb6\u9ad8\u624b\u91cc\uff0c\u800c\u523a \u3000\u3000\u4e2d\u4eba\u7684\u58f0\u97f3\u3002\u800c\u8fd9\u65f6\u5019\u718a\u501c\u7684\u611f\u89c9\uff0c\u4e5f\u662f\u5947\u5f02\u7684\u3002 \u3000\u3000\u90a3\u5c31\u597d\u50cf\u4ed6\u6240\u523a\u4e2d\u7684\u4e00\u79cd\u6781\u539a\u7684\uff0c\u800c\u6beb\u65e0\u77e5\u89c9\u7684\u4e1c\u897f\uff0c\u4ed6\u672c\u80fd\u5730\u624b\u4e0a\u731b\u6ce8\u771f\u529b\u3002 \u3000\u3000\u4f46\u662f\u624b\u4e0a\u7684\u5251\uff0c\u5374\u53ea\u5728\u201d\u654f\u654f\u201c\u8eab\u4e0a\u7f13\u7f13\u5730\u5212\u4e0b\u5bf8\u8bb8\u3002\u4ed6\u5927\u9a87\u4e4b\u4e0b\uff0c\u731b\u5730\u62d4\u51fa\u957f\u5251\uff0c\u8fdc \u8fdc\u843d\u5728\u5730\u4e0a\uff0c\u77a0\u76ee\u770b\u7740\u8fd9\u602a\u5f02\u7684\u201d\u654f\u654f\u201c\uff0c\u53ea\u89c1\u4ed6\u9762\u4e0a\u4ecd\u662f\u6beb\u65e0\u8868\u60c5\uff0c\u8eab\u4e0a\u7684\u521b\u53e3\uff0c\u4e5f\u7edd\u65e0 \u4e00\u4e1d\u8840\u6c34\u6e17\u51fa\uff0c\u53ea\u6709\u4e00\u5bf9\u5927\u773c\u775b\uff0c\u4ecd\u5728\u4e00\u95ea\u4e00\u95ee\u5730\u671b\u7740\u718a\u501c\u3002 \u3000\u3000\u7389\u9762\u795e\u5251\u4e5f\u4e0d\u7406\u5c1a\u672a\u660e\uff0c\u63a0\u4e86\u8fc7\u6765\uff0c\u770b\u7740\u201d\u654f\u654f\u201c\u7684\u4f24\u53e3\uff0c\u6ee1\u9762\u559c\u8272\u7684\u8bf4\u9053\uff1a\u201c\u523a\u8fdb\u53bb \u4e86\uff1f\u201d \u3000\u3000\u201c\u523a\u8fdb\u53bb\u4e86\u3002\u201d\u8fd9\u4e00\u65e0\u8868\u60c5\u7684\u201c\u654f\u654f\u201d\uff0c\u58f0\u97f3\u4e5f\u6ee1\u542b\u559c\u60a6\u3002 \u3000\u3000\u718a\u501c\u53ca\u5c1a\u672a\u660e\uff0c\u770b\u7740\u8fd9\u4e00\u5bf9\u602a\u4eba\u7684\u5947\u602a\u8868\u60c5\u4e5f\u77a0\u76ee\u7ed3\u820c\uff0c\u4e0d\u77e5\u6240\u4ee5\u3002 \u3000\u3000\u7389\u9762\u795e\u5251\u5374\u8d70\u5230\u718a\u501c\u9762\u524d\uff0c\u6df1\u6df1\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u8fd9\u4f4d\u5144\u5f1f\u53ef\u662f\u59d3\u718a\uff1f\u201d \u3000\u3000\u718a\u501c\u6014\u7136\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u5c0f\u5f1f\u6b63\u662f\u718a\u501c\u3002\u201d \u3000\u3000\u7389\u9762\u795e\u5251\u655e\u58f0\u5927\u7b11\uff0c\u4eff\u4f5b\u5fc3\u60c5\u751a\u662f\u5f00\u6717\u9762\u4e0a\u7684\u79ef\u90c1\u4e5f\u4e00\u626b\u800c\u7a7a\uff0c\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u4e0d\u77e5\u5144 \u53f0\u53ef\u5426\u79fb\u7389\u5bd2\u820d\u4e00\u5750\uff0c\u5c0f\u5f1f\u6709\u4e9b\u987b\u4e8b\uff0c\u8fd8\u8981\u8bf7\u5144\u53f0\u6307\u6559\u3002\u201d \u3000\u3000\u718a\u501c\u9053\uff1a\u201c\u5144\u53f0\u5ba0\u53ec\uff0c\u6562\u4e0d\u4ece\u547d\u3002\u201d \u3000\u3000\u7389\u9762\u795e\u5251\u5e38\u6f2b\u5929\u53c8\u8fde\u58f0\u5927\u7b11\uff0c\u6b22\u7136\u9053\uff1a\u201c\u5144\u53f0\u7684\u786e\u662f\u4e00\u4e2a\u8c6a\u8fc8\u82f1\u96c4\uff0c\u90a3\u4e48\u5c31\u8bf7\u5144\u53f0\u5230\u5bd2 \u820d\u4e00\u53d9\u5427\u3002\u201d \u3000\u3000\u718a\u501c\u5fae\u4e00\u70b9\u5934\u3002 \u3000\u3000\u5e38\u6f2b\u5929\u4e0e\u201c\u654f\u654f\u201d\u5df2\u8fde\u8882\u63a0\u8d77\uff0c\u718a\u501c\u4e5f\u968f\u5373\u5c55\u52a8\u8eab\u5f62\uff0c\u8d70\u5230\u5c1a\u672a\u660e\u8eab\u524d\u65f6\uff0c\u5fae\u5fae\u4e00\u987f\uff0c \u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u4e5f\u53bb\u770b\u4e2a\u7a76\u7adf\u3002\u201d \u3000\u3000\u6b64\u8c37\u5185\u65b9\u5706\u4e0d\u8fc7\u6570\u4ea9\uff0c\u4e00\u773c\u671b\u53bb\uff0c\u5c3d\u6536\u773c\u4e0b\uff0c\u718a\u501c\u6697\u5fd6\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u602a\u5f02\u89d2\u8272\uff0c\u4e0d\u77e5\u4f4f \u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u4ed6\u8fd9\u5ff5\u5934\u65b9\u5373\u594b\u8d77\uff0c\u56db\u4eba\u8eab\u5f62\u4fbf\u5df2\u5230\u4e86\u5cf0\u811a\u3002 \u3000\u3000\u7389\u9762\u795e\u5251\u56de\u5934\u5fae\u7b11\u9053\uff1a\u201c\u5230\u4e86\u3002\u201d \u3000\u3000\u718a\u501c\u53ca\u5c1a\u672a\u660e\u89c1\u524d\u9762\u53ea\u662f\u5bf8\u8349\u4e0d\u751f\u7684\u5371\u5ca9\u524a\u58c1\uff0c\u54ea\u6709\u534a\u95f4\u623f\u95f4\uff0c\u65b9\u81ea\u4e00\u6014\uff0c\u5e38\u6f2b\u5929\u5374\u5df2 \u4f38\u624b\u5728\u4e00\u5757\u7a81\u51fa\u7684\u5ca9\u77f3\u4e0a\u5de6\u53f3\u63a8\u52a8\u4e86\u4e24\u4e0b\uff0c\u90a3\u5757\u5ca9\u77f3\u7adf\u7136\u5e26\u8d77\u4e00\u5927\u7247\u5c71\u77f3\uff0c\u7f13\u7f13\u5411\u540e\u6e9c\u53bb\u3002 \u3000\u3000\u201c\u654f\u654f\u201d\u94bb\u4e86\u8fdb\u53bb\uff0c\u7389\u9762\u795e\u5251\u5e38\u6f2b\u5929\u4f38\u624b\u8083\u5ba2\uff0c\u718a\u501c\u53ca\u5c1a\u672a\u660e\u5fae\u4e00\u8fdf\u7591\uff0c\u4e5f\u5927\u6b65\u8e0f\u5165\u6d1e \u91cc\u3002 \u3000\u3000\u5c71\u6d1e\u91cc\u7a81\u7136\u706b\u5149\u4e00\u95ea\uff0c\u706b\u5149\u540e\u6709\u4e00\u5f20\u975e\u5e38\u82f1\u4fca\u7684\u9762\u5b54\u6b63\u5e26\u7740\u5fae\u7b11\u5728\u770b\u7740\u4ed6\u4eec\u3002 \u3000\u3000\u5e38\u6f2b\u5929\u5df2\u70b9\u4e86\u706b\u6298\u5b50\uff0c\u5728\u524d\u9762\u9886\u8def\u3002 \u3000\u3000\u6e10\u6e10\uff0c\u90a3\u706b\u6298\u5b50\u7684\u706b\u7130\u50cf\u662f\u7a81\u7136\u5c0f\u4e86\uff0c\u5e38\u6f2b\u5929\u7b11\u4e86\u7b11\uff0c\u5657\u5730\u4e00\u53e3\u5c06\u706b\u6298\u5b50\u5439\u706d\uff0c\u54ea\u77e5\u9053 \u706b\u6298\u5b50\u5439\u706d\u540e\uff0c\u6d1e\u91cc\u7684\u5149\u7ebf\u53cd\u66f4\u660e\u4eae\uff0c\u4eae\u5f97\u7adf\u50cf\u662f\u5728\u767d\u5929\uff0c\u5c1a\u672a\u660e\u5927\u5947\uff0c\u718a\u501c\u4e5f\u56de\u8fc7\u5934\u6765 \u671b\uff0c\u539f\u6765\u6d1e\u91cc\u7684\u5c71\u58c1\u4e0a\uff0c\u5d4c\u7740\u4e00\u9897\u4e00\u9897\u6eda\u5706\u7684\u73cd\u73e0\u3002 \u3000\u3000\u7389\u9762\u795e\u5251\u9053\uff1a\u201c\u8fd9\u91cc\u4fbf\u662f\u5bd2\u820d\u4e86\u3002\u201d\u8bf4\u7740\u8bdd\uff0c\u624b\u53c8\u5728\u5c71\u58c1\u4e0a\u63a8\u4e86\u4e24\u63a8\u3002 \u3000\u3000\u718a\u501c\u53ca\u5c1a\u672a\u660e\u4e0d\u7981\u90fd\u76f4\u7740\u773c\u770b\u7740\uff0c\u5ffd\u5730\u773c\u524d\u7167\u6765\u4e00\u9053\u731b\u70c8\u7684\u5149\u7ebf\uff0c\u4e00\u9053\u5f3a\u5149\u659c\u659c\u7167\u5728\u5730 \u4e0a\u3002 \u3000\u3000\u8d70\u51fa\u5c71\u58c1\uff0c\u662f\u4e2a\u6781\u5927\u7684\u6d1e\u7a74\uff0c\u56db\u58c1\u6302\u6ee1\u4e86\u5404\u79cd\u73cd\u5b9d\uff0c\u51e0\u4e4e\u5c06\u5c71\u58c1\u94fa\u6ee1\uff0c\u770b\u4e0d\u5230\u4e00\u7247\u7070\u8272 \u7684\u77f3\u5934\uff0c\u73cd\u5b9d\u4e0a\u53d1\u51fa\u7684\u5149\u8292\uff0c\u7167\u8000\u5f97\u4eba\u51e0\u4e4e\u7741\u4e0d\u5f00\u773c\u6765\u3002 \u3000\u3000\u5e38\u6f2b\u65e0\u7b11\u9053\uff1a\u201c\u4e24\u4f4d\u7a0d\u5019\uff0c\u6211\u53bb\u53bb\u5c31\u6765\u3002\u201d\u4ed6\u6ee1\u8138\u559c\u8272\uff0c\u4f3c\u4e4e\u6709\u4ec0\u4e48\u975e\u5e38\u4ee4\u4ed6\u9ad8\u5174\u7684\u4e8b \u60c5\u53d1\u751f\u4e86\u4e00\u6837\u3002 \u3000\u3000\u63a5\u7740\uff0c\u4ed6\u8d70\u5230\u4e00\u4e2a\u7528\u9f99\u773c\u822c\u5927\u7684\u73cd\u73e0\u7ec7\u6210\u7684\u5e18\u5e55\u524d\uff0c\u8d70\u4e86\u8fdb\u53bb\uff0c\u5c06\u6ee1\u6000\u9519\u6115\u3001\u60ca\u5f02\u7684\u718a \u501c\u53ca\u5c1a\u672a\u660e\u7559\u5728\u8fd9\u5c71\u6d1e\u91cc\u3002 \u3000\u3000\u8fd9\u5c71\u7a74\u975e\u4f46\u56db\u58c1\u6ee1\u6302\u73cd\u5b9d\uff0c\u8fde\u684c\u51e0\u90fd\u50cf\u662f\u7389\u77f3\u6240\u5236\uff0c\u6563\u4e71\u5730\u653e\u5728\u5730\u4e0a\uff0c\u6700\u602a\u7684\u662f\u5728\u8fd9\u5c71 \u5cf0\u91cc\uff0c\u7adf\u4f3c\u6709\u7a7a\u6c14\u5728\u6d41\u901a\u7740\u3002 \u3000\u3000\u518d\u4e00\u671b\u9876\u4e0a\uff0c\u4e5f\u6ee1\u6302\u7740\u73cd\u5b9d\u7b49\u7269\uff0c\u6709\u4e00\u5904\u6302\u7684\u662f\u4e00\u7247\u706b\u7ea2\u8272\u7684\u739b\u7459\uff0c\u4f3c\u4e4e\u5728\u5fae\u5fae\u52a8\u7740\uff0c \u539f\u6765\u90a3\u91cc\u6709\u4e00\u4e00\u9053\u5f88\u6df1\u7684\u88c2\u9699\uff0c\u7a7a\u6c14\u4fbf\u7531\u6b64\u5165\u3002 \u3000\u3000\u5c1a\u672a\u660e\u8d70\u5230\u4e00\u4e2a\u89d2\u843d\u91cc\uff0c\u770b\u4e86\u8bb8\u4e45\uff0c\u5ffd\u7136\u53eb\u9053\uff1a\u201c\u5927\u54e5\uff0c\u6765\u770b\u3002\u201d\u718a\u501c\u8d70\u4e86\u8fc7\u53bb\uff0c\u53ea\u770b \u90a3\u8fb9\u58c1\u4e0a\u5e76\u6392\u6302\u7740\u5341\u4f59\u67c4\u5251\uff0c\u957f\u77ed\u4e0d\u4e00\uff0c\u5251\u9798\u7684\u5f0f\u6837\u548c\u8d28\u5730\uff0c\u4e5f\u5404\u6709\u4e0d\u540c\u3002 \u3000\u3000\u5c1a\u672a\u660e\u62bd\u51fa\u4e00\u67c4\u6765\u770b\uff0c\u201c\u545b\u201d\u7136\u4e00\u58f0\u9f99\u541f\uff0c\u5c45\u7136\u4e5f\u662f\u53e3\u7389\u5251\u3002\u4ed6\u65b9\u81ea\u628a\u73a9\uff0c\u5e38\u6f2b\u5929\u4e5f\u8d70 \u4e86\u51fa\u6765\uff0c\u6717\u58f0\u7b11\u9053\uff1a\u201c\u770b\u8fc7\u718a\u5144\u7684\u2018\u501a\u5929\u5251\u2019\uff0c\u8fd9\u4e9b\u5251\u7b80\u76f4\u90fd\u50cf\u5e9f\u94c1\u4e86\u3002\u201d \u3000\u3000\u5e38\u6f2b\u5929\u53c8\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\u4e24\u4f4d\u6b64\u523b\u5fc5\u5b9a\u7591\u56e2\u751a\u591a\uff0c\u5c0f\u5f1f\u4f46\u671b\u4e24\u4f4d\u5fd8\u5374\u65b9\u624d\u7684\u4e8b\uff0c\u4e24\u4f4d\u6709 \u6240\u4e0d\u77e5\uff0c\u5c0f\u5f1f\u5b9e\u6709\u96be\u8a00\u7684\u82e6\u8877\u3002\u201d \u3000\u3000\u8bf4\u5230\u6b64\u5904\uff0c\u4ed6\u8138\u4e0a\u53c8\u6c89\u9732\u51fa\u5148\u524d\u90a3\u79cd\u5fe7\u90c1\u7684\u795e\u8272\uff0c\u4f46\u77ac\u5373\u56de\u590d\uff0c\u9053\uff1a\u201c\u53ea\u662f\u73b0\u5728\u597d\u4e86\uff0c \u53ea\u8981\u4e24\u4f4d\u4e3e\u624b\u4e4b\u52b3\uff0c\u5c0f\u5f1f\u591a\u5e74\u6765\u7684\u75db\u82e6\uff0c\u4e0d\u96be\u8fce\u5203\u800c\u89e3\uff0c\u5c0f\u5f1f\u53ea\u5e0c\u671b\u4e24\u4f4d\u5ff5\u5728\u540c\u662f\u6b66\u6797\u4e00 \u8109\uff0c\u80fd\u4ed7\u4e49\u76f8\u52a9\u3002\u201d \u3000\u3000\u5e38\u6f2b\u5929\u53c8\u9053\uff1a\u201c\u4e24\u4f4d\u53ef\u66fe\u542c\u8bf4\u8fc7\u4e09\u5341\u5e74\u524d\uff0c\u6b66\u6797\u4e2d\u6709\u4e2a\u6781\u5389\u5bb3\u7684\u4eba\u7269\uff0c\u8fde\u5f53\u5e74\u9738\u7edd\u6c5f\u6e56 \u7684\u5929\u9634\u6559\u4e3b\u82cd\u865a\u4e0a\u4eba\u592b\u5987\uff0c\u6b66\u6797\u4e2d\u4fa0\u4e49\u9053\u7684\u9886\u8896\u94c1\u5251\u5148\u751f\u5c55\u7ffc\uff0c\u5bf9\u6b64\u4eba\u90fd\u8ba9\u4e86\u4e09\u5206\uff0c\u53ea\u56e0\u4ed6 \u4e0d\u4f46\u6b66\u529f\u9ad8\u5f3a\uff0c\u52b2\u529f\u6697\u5668\u66f4\u662f\u5999\u7edd\u4eba\u8870\u3002\u201d\u4ed6\u5fae\u4e00\u505c\u987f\uff0c\u66f4\u52a0\u5f3a\u4e86\u4e9b\u8bed\u6c14\uff0c\u9053\uff1a\u201c\u5c24\u5176\u5389\u5bb3 \u7684\u662f\u4ed6\u6613\u5bb9\u4e4b\u672f\uff0c\u5929\u4e0b\u65e0\u53cc\uff0c\u968f\u65f6\u53ef\u4ee5\u6539\u6362\u81ea\u5df1\u7684\u5bb9\u8c8c\uff0c\u751a\u81f3\u8fde\u8eab\u6750\u90fd\u80fd\u6539\u53d8\u3002\u201d \u3000\u3000\u718a\u501c\u84e6\u5730\u63a5\u53e3\u9053\uff1a\u201c\u9601\u4e0b\u6240\u8bf4\u4e4b\u4eba\uff0c\u662f\u5426\u5c31\u662f\u9752\u5e74\u53f7\u79f0\u4e07\u76f8\u771f\u4eba\u7684\u7530\u82cd\uff1f\u201d \u3000\u3000\u5e38\u6f2b\u5929\u9053\uff1a\u201c\u6b63\u662f\u4e07\u76f8\u771f\u4eba\u7530\u82cd\u3002\u65b9\u624d\u4e24\u4f4d\u89c1\u5230\u7684\u90a3\u4f4d\uff0c\u4fbf\u662f\u4e07\u76f8\u771f\u4eba\u552f\u4e00\u7684\u7231\u5973\uff0c\u6563 \u82b1\u4ed9\u5b50\u7530\u654f\u654f\uff0c\u4e5f\u662f\u5c0f\u5f1f\u59bb\u5b50\uff0c\u5c0f\u5f1f\u591a\u5e74\u8db3\u8ff9\u672a\u73b0\u6c5f\u6e56\uff0c\u4e5f\u662f\u4e3a\u4e86\u5979\u3002\u201d \u3000\u3000\u63a5\u7740\uff0c\u5e38\u6f2b\u5929\u8bf4\u51fa\u4e00\u5b97\u5f88\u60ca\u4eba\u7684\u602a\u4e8b\u6765\u3002 \u3000\u3000\u539f\u6765\u7389\u9762\u795e\u5251\u867d\u7136\u51ed\u7740\u81ea\u8eab\u7684\u5251\u672f\uff0c\u9547\u4f4f\u4e86\u5929\u4e0b\u6b66\u6797\u7684\u5f02\u8a00\uff0c\u4e5f\u9547\u4f4f\u4e86\u672c\u6d3e\u4e2d\u4eba\u7684\u4e0d \u6ee1\uff0c\u7136\u800c\u70b9\u82cd\u91cc\u6709\u4e0d\u5c11\u6bd4\u4ed6\u957f\u4e86\u4e00\u8f88\u7684\u5251\u5ba2\uff0c\u5bf9\u4ed6\u4ecd\u662f\u5c61\u6709\u95f2\u8a00\uff0c\u8bf4\u4ed6\u65e0\u8bba\u5a01\u671b\u548c\u6b66\u529f\uff0c\u90fd \u4e0d\u8db3\u4ee5\u655e\u8fd9\u6b66\u6797\u4e94\u5927\u5b97\u6d3e\u4e4b\u4e00\u7684\u638c\u95e8\uff0c\u8fd9\u4e9b\u95f2\u8a00\uff0c\u81ea\u7136\u6709\u4e0d\u5c11\u4f1a\u6d41\u5165\u4ed6\u7684\u8033\u4e2d\u3002 \u3000\u3000\u8fd9\u6837\u8fc7\u4e86\u51e0\u5e74\uff0c\u95f2\u8a00\u4ecd\u662f\u4e0d\u6b47\uff0c\u4ed6\u7d20\u6027\u6de1\u6cca\uff0c\u5e74\u7eaa\u53c8\u957f\u4e86\u51e0\u5c81\uff0c\u6e10\u6e10\u89c9\u5f97\u6c5f\u6e56\u4e0a\u7684\u4e89\u540d \u597d\u80dc\uff0c\u6781\u4e3a\u65e0\u804a\uff0c\u8003\u8651\u4e86\u8bb8\u4e45\uff0c\u7d22\u6027\u5c06\u6d3e\u4e2d\u7684\u4e8b\uff0c\u90fd\u4ea4\u7ed9\u4ed6\u5e73\u65e5\u76f8\u5904\u751a\u597d\u7684\u4e00\u4f4d\u5e08\u53d4\u6765\u638c \u7ba1\uff0c\u81ea\u5df2\u5374\u5b64\u8eab\u4e00\u5251\uff0c\u98d8\u6e38\u56db\u6d77\uff0c\u5bc4\u60c5\u4e8e\u5c71\u6c34\u4e4b\u4e2d\u3002 \u3000\u3000\u4ed6\u672c\u65e0\u76ee\u7684\u56db\u5904\u884c\u8d70\uff0c\u65e0\u5de7\u4e0d\u5de7\uff0c\u4e5f\u8ba9\u4ed6\u95ef\u5165\u8fd9\u5927\u6d2a\u5c71\u661f\u7684\u5e7d\u8c37\u6765\u3002 \u3000\u3000\u5728\u8c37\u53e3\uff0c\u4ed6\u5c31\u53d1\u89c9\u90a3\u5757\u201c\u5165\u8c37\u8005\u6740\u201d\u7684\u77f3\u7891\uff0c\u4ed6\u81ea\u8d1f\u6b66\u529f\uff0c\u975e\u4f46\u4e0d\u60e7\uff0c\u53cd\u800c\u60f3\u4e00\u63a2\u8fd9\u8c37 \u4e2d\u7684\u79d8\u5bc6\u3002 \u3000\u3000\u539f\u6765\u8fd9\u201c\u751c\u751c\u8c37\u201d\u672c\u662f\u6570\u767e\u5e74\u524d\u7684\u4e00\u4e2a\u76d7\u7a9f\u85cf\u5b9d\u4e4b\u5730\uff0c\u5185\u4e2d\u73cd\u5b9d\u5806\u79ef\u65e0\u6570\uff0c\u4e0d\u77e5\u600e\u5730\uff0c \u767e\u5341\u5e74\u6765\u5927\u7ea6\u90a3\u4e9b\u76d7\u515a\u76f8\u7ee7\u7269\u5316\uff0c\u5374\u88ab\u201c\u4e07\u76f8\u771f\u4eba\u201d\u53d1\u89c9\u4e86\u8fd9\u4e2a\u6240\u5728\uff0c\u4ed6\u89c1\u8fd9\u4e9b\u8d22\u7269\uff0c\u4e5f\u4e0d \u81ea\u89c9\u76ee\u7729\u795e\u9a70\uff0c\u7adf\u7136\u5e26\u4e86\u81ea\u5df1\u7684\u5973\u513f\u7530\u654f\u654f\uff0c\u4f4f\u5728\u8fd9\u7edd\u8c37\u91cc\u4e86\u3002 \u3000\u3000\u4e07\u76f8\u771f\u4eba\u813e\u6027\u6781\u5b64\u50fb\uff0c\u7231\u59bb\u6b7b\u540e\uff0c\u51fa\u5bb6\u505a\u4e86\u9053\u58eb\uff0c\u4f46\u662f\u201c\u8d2a\u3001\u55d4\u201d\u4e4b\u5ff5\uff0c\u4ecd\u6781\u6d53\u539a\uff0c\u5f97 \u4e86\u8fd9\u4e9b\u8d22\u5b9d\u540e\uff0c\u53d8\u5f97\u66f4\u662f\u53e4\u602a\uff0c\u89c1\u4e86\u4efb\u4f55\u4eba\u90fd\u4ee5\u4e3a\u662f\u8981\u6765\u62a2\u4ed6\u7684\u8d22\u7269\u7684\u3002 \u3000\u3000\u7389\u9762\u795e\u5251\u4e0d\u77e5\u7a76\u7adf\uff0c\u95ef\u5165\u8c37\u53bb\uff0c\u9047\u5230\u4e86\u4e07\u76f8\u771f\u4eba\uff0c\u4e09\u8a00\u4e24\u8bed\u4e4b\u4e0b\uff0c\u4fbf\u52a8\u8d77\u624b\u6765\uff0c\u4ed6\u6b66\u529f \u867d\u9ad8\uff0c\u5374\u8fdc\u8fdc\u4e0d\u662f\u4e07\u76f8\u771f\u4eba\u7684\u5bf9\u624b\uff0c\u88ab\u4e07\u76f8\u771f\u4eba\u70b9\u4f4f\u7a74\u9053\uff0c\u5173\u5728\u5c71\u8c37\u91cc\u60f3\u6d3b\u751f\u997f\u6b7b\u4ed6\u3002 \u3000\u3000\u6563\u82b1\u4ed9\u5b50\u7530\u654f\u654f\uff0c\u6b64\u65f6\u4ea6\u6709\u5341\u4e5d\u5c81\u4e86\uff0c\u51fa\u843d\u5f97\u8273\u4e3d\u975e\u51e1\uff0c\u4f46\u5374\u88ab\u7236\u4eb2\u5173\u5728\u8fd9\u5e7d\u8c37\u91cc\u3002 \u3000\u3000\u5979\u60c5\u7aa6\u521d\u5f00\uff0c\u5e73\u65e5\u672c\u5c31\u5e38\u5e38\u611f\u6000\uff0c\u89c1\u4e86\u82f1\u4fca\u6f47\u6d12\u7684\u5e38\u6f2b\u5929\uff0c\u4e00\u9897\u70bd\u70ed\u7684\u5fc3\uff0c\u7adf\u65e0\u6cd5\u6291 \u5236\uff0c\u5c45\u7136\u7792\u4e86\u7236\u4eb2\uff0c\u5c06\u5e38\u6f2b\u5929\u5077\u5077\u653e\u8d70\u3002 \u3000\u3000\u4e0d\u4f46\u5982\u6b64\uff0c\u5979\u81ea\u5df1\u4e5f\u8ddf\u7740\u5e38\u6f2b\u5929\u9003\u51fa\u5c71\u8c37\u4e86\u3002 \u3000\u3000\u6b63\u662f\u6240\u8c13\u201c\u7a88\u7a95\u6dd1\u5973\uff0c\u541b\u5b50\u597d\u9011\u3002\u201d\u4e24\u4eba\u4e00\u89c1\u503e\u5fc3\uff0c\u4e00\u8def\u4e0a\u60c5\u4e0d\u81ea\u7981\uff0c\u5728\u4e00\u4e2a\u6708\u660e\u4e4b \u591c\uff0c\u60c5\u611f\u5954\u53d1\u4fbf\u6210\u4e86\u597d\u4e8b\u3002\u826f\u591c\u6c89\u6c89\uff0c\u957f\u7a7a\u5982\u6d17\uff0c\u6708\u8272\u6ee1\u7a97\uff0c\u866b\u58f0\u522e\u8033\uff0c\u5e38\u6f2b\u5929\u4e00\u89c9\u9192\u6765\uff0c \u53d1\u89c9\u6000\u4e2d\u7684\u4e0d\u518d\u662f\u5343\u5a07\u767e\u5a9a\u7684\u5fc3\u4e0a\u4eba\uff0c\u800c\u662f\u4e2a\u4e11\u602a\u7edd\u4f26\u7684\u602a\u7269\u3002 \u3000\u3000\u4ed6\u5927\u60ca\u4e4b\u4e0b\uff0c\u4e00\u8dc3\u800c\u8d77\uff0c\u773c\u524d\u5149\u534e\u707f\u70c2\uff0c\u81ea\u5df1\u5374\u53c8\u56de\u5230\u201c\u751c\u751c\u8c37\u201d\u7684\u5e7d\u7a74\u4e86\u3002 \u3000\u3000\u90a3\u4e11\u602a\u7684\u602a\u7269\u60f3\u4e5f\u662f\u9192\u4e86\uff0c\u671b\u7740\u5e38\u6f2b\u5929\u4f4e\u8bed\u9053\uff1a\u201c\u5e38\u54e5\u54e5\uff0c\u4f60\u8d77\u6765\u5566\uff01\u201d\u5e38\u6f2b\u5929\u4e00\u542c\u8fd9 \u58f0\u97f3\uff0c\u5168\u8eab\u7acb\u523b\u51b0\u51b7\u3002 \u3000\u3000\u4ed6\u60f6\u6025\u53eb\u9053\uff1a\u201c\u654f\u59b9\uff0c\u4f60\u600e\u4e48\u2026\u2026\u201d \u3000\u3000\u6b64\u523b\u73e0\u5e18\u540e\u7f13\u7f13\u8d70\u51fa\u4e0a\u4eba\uff0c\u9634\u7b11\u9053\uff1a\u201c\u6211\u7d20\u6027\u6210\u5168\u4e86\u4f60\u4eec\uff0c\u8ba9\u4f60\u4eec\u5728\u4e00\u8d77\uff0c\u53ef\u662f\u4e5f\u522b\u60f3 \u8d70\u51fa\u8fd9\u2018\u751c\u751c\u8c37\u2019\u4e00\u6b65\u3002\u201d \u3000\u3000\u90a3\u4e11\u4eba\u5927\u559c\u8dc3\u8d77\uff0c\u53eb\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u771f\u597d\u2026\u2026\u201d \u3000\u3000\u8bdd\u5c1a\u672a\u5b8c\uff0c\u4f4e\u5934\u770b\u89c1\u81ea\u5df1\u7684\u8eab\u4e0a\uff0c\u5374\u5df2\u5b8c\u5168\u53d8\u4e86\u4e2a\u6837\u5b50\u3002 \u3000\u3000\u539f\u6765\u4e07\u76f8\u771f\u4eba\u53d1\u89c9\u81ea\u5df1\u7684\u5973\u513f\u80cc\u53db\u4e86\u81ea\u5df1\uff0c\u5fff\u6012\u5f97\u51e0\u4e4e\u5931\u53bb\u4e86\u7406\u6027\uff0c\u4f7f\u4e0d\u987e\u4e00\u5207\u5730\u8ffd\u8ff9 \u51fa\u5c71\uff0c\u5728\u4e00\u4e2a\u6781\u5c0f\u7684\u6751\u843d\u91cc\uff0c\u53d1\u73b0\u4e86\u5e38\u6f2b\u5929\u548c\u7530\u654f\u654f\u7684\u8e2a\u8ff9\uff0c\u4e8e\u662f\u5f53\u665a\uff0c\u4ed6\u4fbf\u4e0b\u4e86\u6bd2\u624b\u3002 \u3000\u3000\u4ed6\u751f\u6027\u5947\u50fb\uff0c\u76db\u6012\u4e4b\u4e0b\uff0c\u505a\u4e8b\u66f4\u4e0d\u62e9\u624b\u6bb5\uff0c\u5bf9\u81ea\u5df1\u7684\u5973\u513f\uff0c\u7adf\u7528\u4e86\u4e00\u79cd\u6781\u5389\u5bb3\u7684\u8ff7\u836f\uff0c \u628a\u5979\u548c\u5e38\u6f2b\u5929\u5e26\u56de\u8c37\u53bb\u3002 \u3000\u3000\u7136\u540e\u4ed6\u4e0d\u60dc\u5c06\u8d39\u4e86\u591a\u5e74\u5fc3\u8840\u5f97\u6765\u7684\u5343\u5e74\u7280\u89d2\uff0c\u518d\u6d74\u4ee5\u94bb\u7c89\u3001\u73cd\u672a\uff0c\u4ee5\u53ca\u4e00\u4e9b\u4ed6\u7684\u5947\u65b9\u79d8 \u836f\uff0c\u6e17\u5408\u6210\u4e00\u79cd\u5947\u602a\u7684\u6eb6\u5242\u3002 \u3000\u3000\u5c31\u7528\u8fd9\u6eb6\u5242\uff0c\u4ed6\u4f7f\u81ea\u5df1\u7f8e\u4e3d\u7684\u5973\u513f\u53d8\u6210\u4e86\u6781\u4e11\u7684\u602a\u7269\u3002 \u3000\u3000\u7389\u9762\u795e\u5251\u89c1\u4e86\u8fd9\u60c5\u5f62\uff0c\u5fc3\u4e0b\u4fbf\u4e5f\u604d\u7136\uff0c\u4ed6\u53c8\u6025\uff0c\u53c8\u6012\uff0c\u63a0\u4e86\u8fc7\u53bb\u53c8\u8981\u548c\u4e07\u76f8\u771f\u4eba\u62fc\u547d\u3002 \u3000\u3000\u4e07\u76f8\u771f\u4eba\u5374\u51b7\u7b11\u9053\uff1a\u201c\u5929\u4e0b\u4e4b\u5927\uff0c\u54ea\u6709\u5973\u5a7f\u8981\u627e\u5cb3\u7236\u62fc\u547d\u7684\u3002\u201d \u3000\u3000\u53c8\u9053\uff1a\u201c\u4f55\u51b5\u6211\u8001\u4eba\u5bb6\u5df2\u5141\u8bfa\u4e86\u4f60\u4eec\u7684\u5a5a\u4e8b\uff0c\u96be\u9053\u4f60\u7231\u7684\u53ea\u662f\u6211\u5973\u513f\u7684\u9762\u8c8c\uff0c\u5982\u4eca\u89c1\u5979 \u4e11\u4e86\uff0c\u4fbf\u505a\u51fa\u8fd9\u7b49\u5f20\u81f4\u6765\u3002\u201d \u3000\u3000\u987b\u77e5\u7530\u82cd\u81ea\u5e7c\u6df7\u8ff9\u7eff\u6797\uff0c\u8bf4\u51fa\u8bdd\u6765\uff0c\u4e5f\u5b8c\u5168\u662f\u5f3a\u76d7\u53e3\u6c14\uff0c\u4f46\u5374\u53c8\u8a00\u8bcd\u950b\u5229\uff0c\u7389\u9762\u795e\u5251\u7adf \u6014\u4f4f\u4e86\u3002 \u3000\u3000\u7530\u654f\u654f\u545c\u54bd\u9053\uff1a\u201c\u7239\u7239\uff0c\u5973\u513f\u4ece\u6b64\u4e00\u5b9a\u542c\u4f60\u8001\u4eba\u5bb6\u7684\u8bdd\uff0c\u7239\u7239\u4f60\u2026\u2026\u201d \u3000\u3000\u4e07\u76f8\u771f\u4eba\u51b7\u51c4\u51c4\u4e00\u7b11\uff1a\u201c\u6211\u77e5\u9053\u4f60\u662f\u5acc\u4f60\u7684\u6837\u5b50\u4e0d\u597d\u770b\uff0c\u4f46\u5929\u4e0b\u4e4b\u5927\uff0c\u80fd\u4f7f\u4f60\u6062\u590d\u672c\u8272 \u7684\u4eba\uff0c\u518d\u4e5f\u6ca1\u6709\u4e86\uff0c\u4fbf\u662f\u6211\u8001\u4eba\u5bb6\u81ea\u5df1\uff0c\u54c8\uff0c\u4e5f\u529e\u4e0d\u5230\uff0c\u6211\u770b\u4f60\u8fd8\u662f\u6b7b\u4e86\u8fd9\u6761\u5fc3\u5427\u3002\u201d \u3000\u3000\u7530\u654f\u654f\u4e00\u5411\u81ea\u8d1f\u5bb9\u8c8c\uff0c\u4e00\u4e2a\u7f8e\u8c8c\u7684\u5c11\u5973\uff0c\u7a81\u7136\u53d8\u6210\u4e2a\u5176\u4e11\u65e0\u6bd4\u7684\u602a\u7269\uff0c\u5fc3\u91cc\u7684\u96be\u53d7\uff0c\u4e0d \u96be\u60f3\u89c1\u3002 \u3000\u3000\u4f55\u51b5\u5979\u770b\u5230\u5fc3\u4e0a\u4eba\u671b\u81ea\u5df1\u7684\u90a3\u526f\u6837\u5b50\uff0c\u5fc3\u77e5\u5c31\u662f\u4ee5\u540e\u52c9\u5f3a\u751f\u6d3b\u4e0b\u53bb\uff0c\u4e5f\u662f\u5f92\u7136\u589e\u52a0\u5f7c\u6b64 \u7684\u75db\u82e6\uff0c\u5979\u67d4\u80a0\u767e\u8f6c\uff0c\u5fc3\u4e00\u6a2a\uff0c\u51b3\u5b9a\u4ee5\u6b7b\u6b89\u4e4b\uff0c\u8ba9\u7239\u7239\u89c1\u5230\u81ea\u5df1\u7684\u5973\u513f\u6b7b\u5728\u9762\u524d\u3002 \u3000\u3000\u201c\u90a3\u4e48\uff0c\u4ed6\u4e5f\u603b\u8be5\u843d\u51e0\u6ef4\u773c\u6cea\u5427\u3002\u201d\u5979\u51c4\u7136\u4e00\u7b11\uff0c\u5fc3\u91cc\u4e0d\u77e5\u662f\u4ec0\u4e48\u6ecb\u5473\uff0c\u63a0\u5230\u89d2\u843d\u91cc\uff0c \u6781\u5feb\u5730\u4ece\u4e07\u76f8\u771f\u4eba\u591a\u5e74\u641c\u96c6\u7684\u5b9d\u5251\u548c\u8fd9\u76d7\u7a9f\u91cc\u539f\u6709\u7684\u540d\u5251\u91cc\uff0c\u62bd\u4e86\u4e00\u67c4\uff0c\u6a2a\u5200\u5411\u9888\u4e0a\u62b9\u53bb\u3002 \u3000\u3000\u7389\u9762\u795e\u5251\u5927\u60ca\u5931\u8272\uff0c\u4f46\u963b\u622a\u5df2\u662f\u4e0d\u53ca\u3002 \u3000\u3000\u4e07\u76f8\u771f\u4eba\u5374\u6f20\u4e0d\u5173\u5fc3\u5730\u671b\u7740\uff0c\u50cf\u662f\u6839\u672c\u65e0\u52a8\u4e8e\u8877\u3002 \u3000\u3000\u7530\u654f\u654f\u5f15\u9888\u81ea\u51b3\uff0c\u54ea\u77e5\u90a3\u67c4\u88c2\u77f3\u65ad\u94c1\u7684\u5229\u5203\uff0c\u524a\u5230\u81ea\u5df1\u9888\u5b50\u4e0a\uff0c\u5c31\u50cf\u4e00\u67c4\u949d\u5200\uff0c\u5728\u524a\u4e00 \u5757\u6781\u7d27\u786c\u7684\u725b\u76ae\uff0c\u4e1d\u6beb\u6ca1\u6709\u53cd\u5e94\u3002 \u3000\u3000\u4e07\u76f8\u771f\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u82e5\u662f\u6709\u80fd\u524a\u5f97\u8fc7\u6211\u8fd9\u672c\u4e8b\u7684\u5251\uff0c\u90a3\u4f60\u4e5f\u4e0d\u5fc5\u81ea\u6740\u5566\uff0c\u6211\u770b\u4f60\u8fd8\u662f\u542c \u7239\u7239\u7684\u8bdd\uff0c\u8001\u8001\u5b9e\u5b9e\u5730\u966a\u7740\u5c0f\u4e08\u592b\u8fc7\u65e5\u5b50\u5427\u3002\u201d\u4ed6\u751f\u6027\u5947\u50fb\uff0c\u7b80\u76f4\u5c06\u7236\u5973\u4e4b\u95f4\u7684\u5929\u6027\u5168\u78e8 \u706d\u3002 \u3000\u3000\u81ea\u6b64\u5e38\u6f2b\u5929\u5728\u751c\u751c\u8c37\u4e00\u803d\u516b\u5e74\u3002 \u3000\u3000\u8fd9\u516b\u5e74\u6765\uff0c\u4e16\u4e8b\u7684\u53d8\u5316\u771f\u5927\uff0c\u4ed6\u4eec\u8fd9\u5c0f\u5c0f\u7684\u751c\u751c\u8c37\u91cc\u4e5f\u662f\u5386\u7ecf\u53d8\u8fc1\u3002 \u3000\u3000\u8eab\u5177\u4e0a\u4e58\u5185\u529f\u7684\u4e07\u76f8\u771f\u4eba\uff0c\u56e0\u4e3a\u5fc3\u6027\u592a\u50fb\uff0c\u7ec3\u529f\u65f6\u8d70\u706b\u5165\u9b54\uff0c\u7adf\u4e27\u4e86\u6027\u547d\uff0c\u5982\u6b64\u4e00\u4e2a\u5947 \u4eba\uff0c\u5c31\u8fd9\u6837\u65e0\u58f0\u65e0\u55c5\u5730\u6b7b\u4e86\u3002 \u3000\u3000\u7530\u654f\u654f\u8fd9\u516b\u5e74\u6765\uff0c\u6027\u60c5\u4ea6\u662f\u5927\u53d8\uff0c\u5728\u5979\u5fc3\u5e95\u6df1\u5904\uff0c\u6709\u4e00\u79cd\u6d53\u539a\u7684\u81ea\u5351\u611f\uff0c\u4f7f\u5f97\u5979\u4e0d\u65f6\u5730 \u60f3\u8981\u6298\u78e8\u5e38\u6f2b\u5929\u3002 \u3000\u3000\u5e38\u6f2b\u5929\u5f15\u548e\u81ea\u8d23\uff0c\u8ba4\u4e3a\u90fd\u662f\u81ea\u5df1\u624d\u4f7f\u8fd9\u4e2a\u7f8e\u8c8c\u7684\u5c11\u5973\u53d8\u6210\u4eca\u65e5\u8fd9\u79cd\u5730\u6b65\uff0c\u662f\u4ee5\u5904\u5904\u5bb9 \u5fcd\uff0c\u51b3\u5b9a\u7ec8\u8eab\u53ae\u5b88\u7740\u5979\uff0c\u6709\u65f6\u4ed6\u4e86\u5c71\u53bb\u4e70\u4e9b\u7cae\u98df\u7528\u5177\uff0c\u4e5f\u662f\u9a6c\u4e0a\u5c31\u56de\u6765\uff0c\u4e0d\u6562\u5728\u5c71\u5916\u505c\u7559\u4e00 \u523b\u3002 \u3000\u3000\u516b\u5e74\u6765\u6709\u8bef\u4eba\u751c\u751c\u8c37\u7684\u4eba\uff0c\u65e0\u8bba\u662f\u8c01\uff0c\u6ca1\u6709\u4e00\u4e2a\u80fd\u9003\u51fa\u6027\u547d\u7684\uff0c\u6709\u65f6\u5e38\u6f2b\u5929\u89c1\u7740\u4e0d\u5fcd\uff0c \u7530\u654f\u654f\u5374\u6c14\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u597d\u770b\uff0c\u559c\u6b22\u4eba\u5bb6\u770b\u4f60\uff0c\u4f46\u662f\u6211\u4e11\uff0c\u770b\u8fc7\u6211\u7684\u4eba\uff0c\u6211\u90fd\u8981\u6740\u6b7b \u4ed6\u3002\u201d \u3000\u3000\u5e38\u6f2b\u5929\u4e3a\u60c5\u6240\u7d2f\uff0c\u7ec8\u65e5\u90c1\u90c1\uff0c\u53ea\u6709\u5728\u542c\u7740\u5979\u7684\u58f0\u97f3\u7684\u65f6\u5019\uff0c\u624d\u80fd\u5f97\u5230\u4e00\u4e1d\u5b89\u6170\uff0c\u4f46\u6709\u65f6 \u7530\u654f\u654f\u5374\u7ec8\u65e5\u4e00\u8a00\u4e0d\u53d1\u3002 \u3000\u3000\u4e24\u4eba\u5c71\u5c45\u516b\u8f7d\uff0c\u5929\u804a\u4e2d\uff0c\u5374\u7ec3\u6210\u4e00\u79cd\u4efb\u4f55\u4eba\u90fd\u6ca1\u6709\u8fd9\u4efd\u5fc3\u601d\u7ec3\u6210\u7684\u6697\u5668\u4e0e\u5251\u5f0f\u914d\u5408\u7684\u9635 \u6cd5\u3002 \u3000\u3000\u8fd9\u79cd\u9635\u6cd5\uff0c\u5929\u4e0b\u9664\u4ed6\u4e24\u4eba\u4e4b\u5916\uff0c\u518d\u4e5f\u6ca1\u6709\u4eba\u77e5\u9053\uff0c\u7530\u654f\u654f\u5e73\u65e5\u65e0\u6240\u4e8b\u4e8b\uff0c\u5c31\u6614\u7ec3\u6b66\u529f\u81ea \u9063\uff0c\u7ec3\u529f\u3001\u6697\u5668\uff0c\u65e9\u5df2\u7089\u706b\u7eaf\u9752\uff0c\u4e0d\u5728\u5176\u7236\u4e07\u76f8\u771f\u4eba\u4e4b\u4e0b\uff0c\u82e5\u5979\u80fd\u51fa\u6c5f\u6e56\uff0c\u6015\u4e0d\u591a\u4e45\u5c31\u80fd\u5927 \u5927\u626c\u540d\u3002 \u3000\u3000\u718a\u501c\u53ca\u5c1a\u672a\u660e\u542c\u4ed6\u5a13\u5a13\u9053\u6765\uff0c\u4e0d\u7981\u611f\u53f9\u7740\u4e07\u76f8\u771f\u4eba\u7684\u51b7\u9177\uff0c\u7530\u654f\u654f\u7684\u53ef\u601c\uff0c\u5bf9\u8fd9\u4f4d\u7389\u9762 \u795e\u5251\u7684\u60c5\u6df1\u4e00\u4f4f\uff0c\u66f4\u662f\u79f0\u8d3a\u4e0d\u5df2\u3002 \u3000\u3000\u5e38\u6f2b\u5929\u89e6\u52a8\u5f80\u4e8b\uff0c\u53c8\u4e0d\u7981\u9eef\u7136\u795e\u4f24\u3002 \u3000\u3000\u826f\u4e45\uff0c\u4ed6\u65b9\u8bf4\u9053\uff1a\u201c\u521a\u521a\u718a\u5144\u90a3\u4e00\u5251\uff0c\u5374\u80fd\u5c06\u62d9\u8346\u7684\u76ae\u80a4\u5212\u5f00\u4e00\u9053\u53e3\u5b50\uff0c\u662f\u4ee5\u5c0f\u5f1f\u731c \u60f3\uff0c\u4ee5\u718a\u5144\u8fd9\u67c4\u5251\u7684\u5f62\u72b6\u770b\u6765\uff0c\u83ab\u4e0d\u662f\u6c5f\u6e56\u4f20\u8bf4\u7684\u2018\u501a\u5929\u5251\u2019\u5417\uff1f\u5982\u4eca\u82cd\u5929\u76f8\u4f51\uff0c\u6709\u4e86\u8fd9 \u5251\uff0c\u62d9\u8346\u7684\u591a\u5e74\u82e6\u75db\uff0c\u4e5f\u8bb8\u80fd\u591f\u4ece\u6b64\u89e3\u8131\u4e5f\u672a\u53ef\u77e5\uff0c\u6240\u4ee5\u5c0f\u5f1f\u8fd9\u624d\u4e0d\u5acc\u5192\u6627\uff0c\u4f46\u671b\u718a\u5144\u80fd\u5c06 \u6b64\u5251\u501f\u4e0e\u5c0f\u5f1f\u4e00\u7528\u3002\u201d \u3000\u3000\u718a\u501c\u501c\u6168\u7136\u7b54\u5e94\u4e86\uff0c\u53cd\u624b\u5c06\u5251\u9798\u4e5f\u89e3\u4e86\u4e0b\u6765\uff0c\uff0d\u5e76\u4ea4\u7ed9\u4e86\u5e38\u6f2b\u5929\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u53ea\u7ba1\u62ff\u53bb \u7528\u4fbf\u662f\u3002\u201d\u5e38\u6f2b\u5929\u5927\u559c\u4e4b\u4e0b\uff0c\u63a5\u8fc7\u4e86\u5251\uff0c\u624b\u5374\u50cf\u56e0\u8fc7\u5ea6\u7684\u5174\u594b\uff0c\u800c\u6709\u4e9b\u5fae\u5fae\u98a4\u6296\u4e86\u3002 \u3000\u3000\u718a\u501c\u53ca\u5c1a\u672a\u660e\u4e5f\u4e0d\u7981\u76f8\u5bf9\u557c\u5618\uff0c\u4ed6\u4eec\u672c\u662f\u591a\u60c5\u4e4b\u4eba\uff0c\u718a\u501c\u542c\u4e86\u8fd9\u4e00\u5bf9\u4e45\u7ecf\u60a3\u96be\uff0c\u53d7\u5c3d\u6298 \u78e8\u7684\u513f\u5973\u82f1\u96c4\u4e8b\u8ff9\uff0c\u4e0d\u7981\u60f3\u8d77\u590f\u82b8\u6765\uff0c\u957f\u957f\u53f9\u53e3\u6c14\uff0c\u5fd6\u9053\uff1a\u201c\u6211\u8fd9\u771f\u662f\u6b32\u901f\uff0c\u53cd\u800c\u4e0d\u8fbe \u4e86\u3002\u201d \u3000\u3000\u5c1a\u672a\u660e\u4e5f\u77e5\u9053\u4ed6\u7684\u5fc3\u5883\uff0c\u9042\u9053\uff1a\u201c\u5927\u54e5\u4e0d\u8981\u7740\u6025\uff0c\u6211\u60f3\u590f\u59d1\u5a18\u7edd\u5bf9\u4e0d\u4f1a\u51fa\u4ec0\u4e48\u4e8b\u7684\u3002\u201d \u3000\u3000\u718a\u501c\u70b9\u5934\u9053\uff1a\u201c\u4f46\u613f\u5982\u6b64\u3002\u201d \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u91cc\u9762\u4eff\u4f5b\u6709\u5973\u5b50\u547b\u541f\u4e4b\u58f0\u3002 \u3000\u3000\u53c8\u542c\u5230\u5e38\u6f2b\u5929\u50cf\u662f\u5728\u4f4e\u58f0\u5b89\u6170\u7740\uff0c\u63a5\u7740\uff0c\u5e38\u6f2b\u5929\u98de\u6b65\u800c\u51fa\uff0c\u559c\u8272\u6ee1\u9762\uff0c\u9053\uff1a\u201c\u597d\u4e86\u597d \u4e86\uff0c\u771f\u662f\u82cd\u5929\u6709\u773c\u3002\u201d \u3000\u3000\u718a\u501c\u3001\u5c1a\u672a\u660e\u4e00\u8d77\u7ad9\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u606d\u559c\u5e38\u5144\u3002\u201d \u3000\u3000\u5e38\u6f2b\u5929\u53c8\u5306\u5306\u8dd1\u4e86\u8fdb\u53bb\uff0c\u4ed6\u6b22\u559c\u8fc7\u5ea6\uff0c\u7adf\u5931\u4e86\u5e38\u6001\uff1a\u4f3c\u4e4e\u56de\u5230\u5e7c\u7ae5\u65f6\uff0c\u5f97\u5230\u4e86\u7cd6\u679c\u65f6\u7684 \u90a3\u4e00\u4efd\u6b22\u559c\u3002 \u3000\u3000\u7247\u523b\uff0c\u5e38\u6f2b\u5929\u53c8\u8dd1\u4e86\u51fa\u6765\uff1a\u201c\u62d9\u8346\u5b9a\u8981\u9762\u8c22\u5404\u4f4d\uff0c\u5979\u8fd9\u5c31\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u8bdd\u672a\u8bf4\u5b8c\uff0c\u73e0\u5e18\u4e00\u6380\uff0c\u718a\u501c\u53ca\u5c1a\u672a\u660e\u773c\u524d\u4ff1\u90e8\u4e00\u4eae\uff0c\u4e00\u4e2a\u7edd\u4ee3\u4f73\u4eba\uff0c\u6620\u5f97\u6ee1\u5ba4\u73e0\u5149\uff0c\u4fcf \u751f\u751f\u5730\u8d70\u4e86\u51fa\u6765\uff0c\u7f8e\u8273\u4e0d\u53ef\u65b9\u7269\u3002 \u3000\u3000\u5e38\u6f2b\u5929\u5f97\u610f\u5730\u7b11\u7740\uff0c\u6b64\u523b\uff0c\u4ed6\u4e3a\u4ed6\u7684\u59bb\u5b50\u6df1\u6df1\u5730\u9a84\u50b2\u7740\uff0c\u773c\u775b\u4e5f\u4eae\u4e86\u3002 \u3000\u3000\u7530\u654f\u654f\u671d\u718a\u501c\u5c1a\u672a\u660e\u6df1\u6df1\u4e00\u798f\uff0c\u8138\u5c45\u7136\u7ea2\u4e86\uff0c\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u4ed6\u4eec\u89c1\u5979\u7684\u5a07\u7f9e\u4e4b\u6001\uff0c\u60f3\u8d77\u65b9\u624d\u90a3\u81c3\u80bf\u4e11\u964b\u3001\u51f6\u6076\u7684\u602a\u7269\uff0c\u5fc3\u4e2d\u6697\u6697\u597d\u7b11\uff0c\u5bf9\u4e07\u76f8\u771f\u4eba \u5947\u5999\u7684\u6613\u5f62\u4e4b\u672f\uff0c\u53c8\u4e0d\u514d\u60ca\u5f02\u3002 \u3000\u3000\u7389\u9762\u795e\u5251\u6367\u7740\u90a3\u67c4\u4ed6\u4ee5\u4e3a\u662f\u7684\u201c\u501a\u5929\u5251\u201d\uff0c\u4ea4\u8fd8\u718a\u501c\uff0c\u7b11\u9053\uff1a\u201c\u82f1\u96c4\u5b9d\u5251\uff0c\u76f8\u5f97\u76ca\u5f70\uff0c \u4e24\u4f4d\u4ff1\u662f\u5c11\u5e74\u82f1\u4fa0\uff0c\u524d\u9014\u81ea\u662f\u4e0d\u53ef\u9650\u91cf\u3002\u201d \u3000\u3000\u4ed6\u6717\u58f0\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u65e5\u540e\u4e24\u4f4d\u82e5\u6709\u7528\u5f97\u7740\u6211\u592b\u5987\u5904\uff0c\u53ea\u7ba1\u5429\u5490\u4fbf\u662f\u3002\u201d \u3000\u3000\u718a\u501c\u53ca\u5c1a\u672a\u660e\u5fd9\u4e0d\u8fed\u5730\u79f0\u8c22\u7740\u3002 \u3000\u3000\u718a\u501c\u6697\u5fd6\uff1a\u201c\u8fd9\u867d\u7136\u56e0\u6b64\u803d\u8bef\u4e86\u4e9b\u65f6\u5019\uff0c\u53c8\u9669\u4e9b\u9001\u547d\uff0c\u4f46\u80fd\u4ea4\u7740\u8fd9\u7b49\u4eba\u7269\uff0c\u4e5f\u7b97\u4e0d\u865a\u6b64 \u884c\u4e86\u3002\u201d \u3000\u3000\u5e38\u6f2b\u5929\u548c\u7530\u654f\u654f\u56db\u76ee\u76f8\u5bf9\uff0c\u5f80\u4e8b\u5982\u70df\uff0c\u6076\u68a6\u5df2\u901d\uff0c\u4e24\u4eba\u6b22\u559c\u5f97\u776b\u6bdb\u4e0a\u90fd\u6302\u7740\u6cea\u73e0\uff0c\u50cf\u662f \u6709\u7740\u4e07\u5343\u5fc3\u8bed\uff0c\u5374\u53c8\u4e0d\u77e5\u4ece\u4f55\u8bf4\u8d77\u3002 \u3000\u3000\u5c1a\u672a\u660e\u4e0d\u7981\u611f\u53f9\uff1a\u201c\u60c5\u4e4b\u4e00\u5b57\uff0c\u98a0\u5012\u4f17\u751f\uff0c\u771f\u4e0d\u53ef\u601d\u8bae\uff0c\u4efb\u4f60\u662f\u518d\u5927\u7684\u82f1\u96c4\u597d\u6c49\uff0c\u4e5f\u96be \u9003\u6b64\u5173\u3002\u201d\u671b\u4e86\u718a\u501c\u4e00\u773c\uff0c\u89c1\u4ed6\u6b63\u5728\u6014\u6014\u5730\u60f3\u7740\u5fc3\u601d\uff0c\u6697\u7b11\u9053\uff1a\u201c\u770b\u6765\u5927\u54e5\u4e5f\u60f3\u7740\u590f\u59d1\u5a18 \u5462\u3002\u201d \u3000\u3000\u4e8e\u662f\u4ed6\u7b11\u9053\uff1a\u201c\u5927\u54e5\uff0c\u6211\u4eec\u8be5\u8d70\u4e86\u5427\u3002\u201d \u3000\u3000\u5e38\u6f2b\u5929\u614c\u9053\uff1a\u201c\u4e24\u4f4d\u5343\u4e07\u8981\u5728\u6b64\u76d8\u6853\u4e9b\u65f6\u65e5\uff0c\u600e\u5730\u73b0\u5728\u5c31\u8981\u8d70\u5462\uff1f\u201d \u3000\u3000\u4e8e\u662f\u5c1a\u672a\u660e\u624d\u5c06\u590f\u82b8\u88ab\u63b3\uff0c\u718a\u501c\u7126\u6025\uff0c\u73b0\u5728\u6b64\u95f4\u4e8b\u4e86\uff0c\u4e00\u5b9a\u8981\u8fde\u591c\u8d76\u53bb\uff0c\u8fd9\u4e9b\u8bdd\u8bf4\u4e86\u51fa \u6765\u3002 \u3000\u3000\u5e38\u6f2b\u5929\u4e00\u542c\uff0c\u8bf4\u9053\uff1a\u201c\u65e2\u6709\u8fd9\u7b49\u4e8b\uff0c\u5c0f\u5f1f\u4e5f\u4e0d\u6562\u518d\u591a\u7559\u4e24\u4f4d\u3002\u201d \u3000\u3000\u4ed6\u5fae\u4e00\u76b1\u7709\uff0c\u53c8\u9053\uff1a\u201c\u90a3\u6b66\u5f53\u56db\u5b50\uff0c\u4e0e\u5c0f\u5f1f\u4e5f\u6709\u6570\u9762\u4e4b\u7f18\uff0c\u5374\u60f3\u4e0d\u5230\u4ed6\u4eec\u662f\u8fd9\u6837\u4e0d\u901a\u60c5 \u7406\u7684\u8001\u9053\uff0c\u4e24\u4f4d\u6b64\u53bb\u6b66\u5f53\u5c71\uff0c\u5374\u5343\u4e07\u5c0f\u5fc3\u4e86\uff0c\u522b\u4eba\u5c24\u5728\u5176\u6b21\uff0c\u6b66\u5f53\u7684\u638c\u95e8\u5927\u4fa0\u5999\u4e00\u771f\u4eba\uff0c\u7aef \u7684\u975e\u540c\u5c0f\u53ef\uff0c\u4e0d\u4f46\u5251\u672f\u901a\u7384\uff0c\u5185\u529f\u4e5f\u5df2\u5230\u4e86\u98de\u82b1\u4f24\u4eba\u7684\u5730\u6b65\u3002\u201d \u7b2c\u516b\u7ae0\u3000\u6b66\u5f53\u4e4b\u884c>> \u53e4\u9f99\u300a\u82cd\u7a79\u795e\u5251\u300b \u7b2c\u516b\u7ae0\u3000\u6b66\u5f53\u4e4b\u884c \u3000\u3000\u4e24\u4eba\u7565\u4e3a\u5c06\u606f\uff0c\u4fbf\u8f9e\u522b\u4e86\u5e38\u6f2b\u5929\u592b\u5987\uff0c\u8d76\u5f80\u6b66\u5f53\u5c71\u4e0a\u53bb\u3002 \u3000\u3000\u6b66\u5f53\u5dde\u672c\u662f\u695a\u5317\u6700\u6709\u540d\u7684\u4e00\u5904\u5c71\u5cb3\uff0c\u5c71\u5c5e\u5df4\u5c71\u652f\u8109\uff0c\u5468\u56f4\u516b\u767e\u591a\u91cc\uff0c\u6709\u4e09\u5341\u516d\u60ac\u5d16\uff0c\u4e8c \u5341\u4e03\u9ad8\u5cf0\u3002\u6700\u9ad8\u4e4b\u5904\uff0c\u540d\u5929\u67f1\u5cf0\uff0c\u90a3\u5c31\u662f\u771f\u6b66\u4fee\u70bc\u4e4b\u5730\u3002\u6b64\u5916\u8fd8\u6709\u5357\u5d16\u3001\u4e94\u9f99\u5cf0\u3001\u7d2b\u9704\u5cf0\u3001 \u5c55\u65d7\u5cf0\u7b49\uff0c\u90fd\u662f\u9053\u5bb6\u6e05\u4fee\u4e4b\u5904\u3002 \u3000\u3000\u9ad8\u5cf0\u767d\u4e91\u6df1\u5904\uff0c\u4e09\u4e24\u82cd\u9e70\u5728\u4f4e\u4f4e\u76d8\u65cb\u7740\uff0c\u5730\u4e0a\u7684\u91ce\u5154\uff0c\u6025\u5267\u5730\u5728\u91ce\u8349\u4e1b\u4e2d\u98de\u5954\uff0c\u6e05\u9634\u6251 \u9f3b\uff0c\u6668\u9732\u8fce\u9762\uff0c\u7aef\u7684\u662f\u4e2a\u597d\u53bb\u5904\u3002 \u3000\u3000\u718a\u501c\u53ca\u5c1a\u672a\u660e\u4e0d\u89c9\u5fc3\u795e\u4e3a\u4e4b\u4e00\u723d\uff0c\u53ea\u89c1\u904d\u5c71\u5f25\u9053\uff0c\u90fd\u662f\u4e9b\u82cd\u677e\u78a7\u7af9\uff0c\u5341\u5206\u5730\u5e7d\u9759\uff0c\u8fde\u4e2a \u6a35\u592b\u90fd\u770b\u4e0d\u5230\u3002\u8d8a\u8fc7\u4e00\u9053\u5e76\u4e0d\u592a\u9ad8\u7684\u5c71\u5cad\uff0c\u5ffd\u89c1\u5bf9\u9762\u4e00\u5ea7\u9ad8\u5d16\uff0c\u9ad8\u5d16\u4e0a\u6d41\u4e0b\u4e00\u80a1\u7011\u5e03\uff0c\u50cf\u662f \u4e00\u6761\u6781\u957f\u7684\u81ea\u7ec3\uff0c\u6447\u66f3\u65e0\u9645\uff0c\u6f8e\u6e43\u6e85\u7389\uff0c\u51fb\u5728\u5c71\u77f3\u4e0a\uff0c\u6e85\u8d77\u65e0\u6570\u6c34\u73e0\uff0c\u53c8\u8f7b\u8f7b\u7f13\u7f13\u5730\u8f7b\u8f7b\u5f2f \u66f2\u7740\u6d41\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4e0b\u9762\u662f\u4e00\u6761\u5f88\u5bbd\u5f88\u6df1\u7684\u5c71\u6da7\uff0c\u6da7\u6c34\u4e5f\u5728\u5954\u817e\u7740\uff0c\u4ed6\u4e24\u4eba\u4e3e\u5934\u4e00\u770b\uff0c\u5c31\u89c1\u9ad8\u5d16\u4e0a\u523b\u7740\u4e09\u4e2a \u5927\u5b57\u201c\u89e3\u5251\u6cc9\u201d\uff0c\u7b14\u529b\u96c4\u6d51\uff0c\u4e0d\u77e5\u662f\u4f55\u4eba\u624b\u7b14\u3002 \u3000\u3000\u5c1a\u672a\u660e\u9053\uff1a\u201c\u8fd9\u91cc\u5c31\u662f\u89e3\u5251\u6cc9\u4e86\uff0c\u60f3\u6765\u7384\u89c2\u3001\u771f\u6b66\u5e99\u4e5f\u5c31\u5728\u524d\u9762\u4e86\uff0c\u600e\u5730\u5374\u8fd8\u4e0d\u89c1\u4eba \u5f71\uff1f\u201d \u3000\u3000\u718a\u501c\u624b\u4e00\u6307\u9053\uff0c\u201c\u90a3\u4e0d\u662f\u5417\uff1f\u201d \u3000\u3000\u524d\u9762\u7f13\u7f13\u884c\u6765\u4e24\u4e2a\u8eab\u7a7f\u6df1\u84dd\u8272\u9063\u888d\u7684\u5e74\u8f7b\u9053\u4eba\uff0c\u718a\u501c\u53ca\u5c1a\u672a\u660e\u8fce\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u4e24\u4e2a\u9053\u4eba\u4e2d\u8eab\u6750\u8f83\u77ee\u7684\u9053\uff1a\u201c\u4e24\u4f4d\u65bd\u4e3b\u53ef\u662f\u5230\u7384\u771f\u89c2\u53bb\u66ff\u771f\u6b66\u7237\u7237\u4e0a\u9999\uff0c\u65bd\u4e3b\u8eab\u4e0a\u82e5\u6709 \u4f69\u5251\uff0c\u5c31\u8bf7\u5728\u6b64\u5904\u89e3\u4e0b\u3002\u201d \u3000\u3000\u718a\u501c\u9053\uff1a\u201c\u5728\u4e0b\u4e13\u8bda\u6765\u62dc\u8bbf\u6b66\u5f53\u7684\u56db\u4eea\u5251\u5ba2\u7684\uff0c\u5c31\u8bf7\u4e24\u4f4d\u9053\u5144\u4ee3\u4e3a\u8f6c\u7980\u4e00\u58f0\u3002\u201d \u3000\u3000\u90a3\u9053\u4eba\u9053\uff1a\u201c\u539f\u6765\u4e24\u4f4d\u65bd\u4e3b\u662f\u6765\u627e\u62a4\u6cd5\u7684\u56db\u4f4d\u5e08\u53d4\u7684\u3002\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u718a\u501c\u5df2\u81ea\u4f1a\u610f\u3002\u9053\uff1a\u201c\u5728\u4e0b\u8eab\u4e0a\u7684\u5251\uff0c\u672c\u5e94\u7acb\u523b\u89e3\u4e0b\uff0c\u53ea\u662f\u6b64\u5251\u4e0d\u662f\u51e1\u54c1\uff0c\u4e0d\u77e5\u4e24\u4f4d\u9053 \u5144\u80fd\u5426\u901a\u878d\u4e00\u4e0b\uff0c\u7b49\u5728\u4e0b\u89c1\u4e86\u56db\u4eea\u5251\u5ba2\u518d\u8bf4\uff1f\u201d \u3000\u3000\u90a3\u9053\u4eba\u5fae\u4e00\u6c89\u541f\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u8d2b\u9053\u5012\u4e0d\u6562\u505a\u4e3b\u3002\u201d \u3000\u3000\u53e6\u4e00\u9053\u4eba\u9053\uff1a\u201c\u6700\u597d\u8bf7\u4e24\u4f4d\u5c31\u5728\u6b64\u7a0d\u5019\u4e00\u4e0b\uff0c\u7b49\u6211\u53bb\u7980\u8fc7\u5e08\u53d4\u518d\u8bf4\u3002\u201d\u53c8\u9053\uff1a\u201c\u4e03\u5e08 \u5f1f\uff0c\u4f60\u5c31\u5728\u8fd9\u91cc\u966a\u4ed6\u4eec\u4e00\u4e0b\u3002\u201d \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u8fdc\u8fdc\u6765\u4e86\u4e09\u4e2a\u84dd\u888d\u9053\u4eba\u3002\u9664\u4e86\u65b9\u624d\u90a3\u5e74\u8f7b\u9053\u4eba\u5916\uff0c\u53e6\u5916\u4e24\u4e2a\u90fd\u7559\u7740\u957f\u9aef\uff0c\u5176 \u4e2d\u4e00\u4eba\u9053\uff1a\u201c\u4e24\u4f4d\u65bd\u4e3b\u53ef\u662f\u6765\u627e\u4e39\u9633\u3001\u7384\u673a\u3001\u51cc\u4e91\u3001\u51fa\u5c18\u56db\u4f4d\u5e08\u5f1f\u7684\uff1f\u201d \u3000\u3000\u718a\u501c\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d \u3000\u3000\u90a3\u9053\u4eba\u7684\u795e\u8272\u6781\u4e3a\u50b2\u6162\uff0c\u51b7\u51b7\u4ed6\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u56db\u4eba\u5df2\u7ecf\u4e91\u6e38\u53bb\u4e86\uff0c\u65bd\u4e3b\u6709\u4ec0\u4e48\u4e8b\uff0c\u8ddf\u8d2b \u9053\u8bf4\u4e5f\u662f\u4e00\u6837\u3002\u201d \u3000\u3000\u718a\u501c\u9053\uff1a\u201c\u56db\u4eea\u5251\u5ba2\u96be\u9053\u5168\u51fa\u53bb\u4e86\u5417\uff1f\u201d \u3000\u3000\u90a3\u9053\u4eba\u9053\uff1a\u201c\u51fa\u5bb6\u4eba\u4e0d\u6253\u8c0e\u8bed\u3002\u201d \u3000\u3000\u5148\u524d\u7684\u9053\u4eba\u8bf4\uff1a\u201c\u82e5\u662f\u5341\u5206\u91cd\u8981\u7684\u4e8b\uff0c\u8ddf\u8d2b\u9053\u8bf4\u4e5f\u4e00\u6837\u3002\u201d \u3000\u3000\u718a\u501c\u9053\uff1a\u201c\u56db\u4eea\u5251\u5ba2\u65e2\u4e0d\u5728\uff0c\u5c31\u8bf7\u9053\u957f\u4eec\u5e26\u5728\u4e0b\u53bb\u53c2\u62dc\u5999\u4e00\u76f4\u4eba\uff0c\u5728\u4e0b\u2026\u2026\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u957f\u9aef\u9053\u4eba\u4e00\u8d77\u4ef0\u5929\u957f\u7b11\uff0c\u6253\u65ad\u4e86\u718a\u501c\u7684\u8bdd\u3002 \u3000\u3000\u5934\u4e00\u4e2a\u9053\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u65bd\u4e3b\u672a\u514d\u5c06\u4e8b\u60c5\u770b\u5f97\u5927\u5bb9\u6613\u4e86\u5427\uff0c\u638c\u6559\u771f\u4eba\uff0c\u5c82\u662f\u4f60\u4eec\u968f\u4fbf\u89c1\u5f97 \u7684\uff1f\u201d \u3000\u3000\u5c1a\u672a\u660e\u6012\u9053\uff1a\u201c\u8981\u600e\u6837\u624d\u80fd\u89c1\u5f97\uff1f\u201d \u3000\u3000\u90a3\u9053\u4eba\u53c8\u957f\u957f\u4e00\u58f0\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4f4d\u65bd\u4e3b\u5012\u6a2a\u5f97\u7d27\uff0c\u53ef\u662f\u5c06\u6211\u4eec\u6b66\u5f53\u6d3e\u4e0d\u770b\u5728\u773c\u91cc\uff1f\u201d \u3000\u3000\u5c1a\u672a\u660e\u9886\u8896\u4e24\u6cb3\u7eff\u6797\u9053\uff0c\u5728\u6b66\u6797\u4e2d\u53ef\u7b97\u4e00\u7b49\u4e00\u7684\u4eba\u7269\uff1a\u6b64\u523b\u542c\u4e86\u8fd9\u9053\u4eba\u50b2\u6162\u800c\u65e0\u7406\u7684 \u8bdd\uff0c\u4e0d\u7981\u5927\u6012\u9053\uff1a\u201c\u770b\u5728\u773c\u91cc\u5982\u4f55\uff0c\u4e0d\u770b\u5728\u773c\u91cc\u53c8\u5982\u4f55\uff1f\u201d\u201c\u3001\u90a3\u9053\u4eba\u6012\u9053\uff1a\u201c\u4e24\u767e\u5e74\u6765\uff0c \u8fd8\u6ca1\u6709\u4eba\u6562\u5728\u6b66\u5f53\u5c71\u53d1\u6a2a\u7684\uff0c\u6211\u770b\u4f60\u6050\u6015\u6d3b\u5f97\u4e0d\u592a\u8010\u70e6\u4e86\u5427\uff1f\u201d\u201c\u5c1a\u672a\u660e\u54c8\u54c8\u9053\uff1a\u201c\u597d\u4e00\u4e2a \u51fa\u5bb6\u4eba\uff0c\u4e00\u5f00\u53e3\u8bf4\u8bdd\uff0c\u5374\u50cf\u5f3a\u76d7\u4e00\u6837\u3002\u201d \u3000\u3000\u718a\u501c\u4e5f\u89c9\u8fd9\u4e24\u4e2a\u4eba\u592a\u8fc7\u65e0\u7406\uff0c\u6b63\u60f3\u53d1\u8bdd\uff0c\u773c\u89d2\u4e00\u659c\uff0c\u5374\u89c1\u65b9\u624d\u90a3\u5e74\u8f7b\u9053\u4eba\u53c8\u5954\u5411\u5c71\u4e0a \u53bb\uff0c\u5fc3\u5fd6\uff1a\u201c\u96be\u9053\u4ed6\u53c8\u53bb\u53eb\u4eba\uff1f\u201d \u3000\u3000\u518d\u4e00\u60f3\uff1a\u201c\u90a3\u56db\u4eea\u5251\u5ba2\u51fa\u5c71\u4e0d\u77e5\u662f\u771f\u662f\u5047\uff0c\u82b8\u59b9\u59b9\u4e0d\u77e5\u88ab\u8fd9\u4e9b\u9053\u4eba\u600e\u6837\u4e86\uff0c\u770b\u6765\u4eca\u65e5\u6211 \u4eec\u4e0d\u95ef\u4e0a\u5c71\u53bb\uff0c\u4e0d\u4f1a\u5f97\u5230\u7ed3\u679c\u3002\u201d \u3000\u3000\u4ed6\u5fc3\u4e00\u6a2a\uff0c\u559d\u9053\uff1a\u201c\u4e8c\u5f1f\uff0c\u8fd9\u4e24\u4f4d\u9053\u957f\u65e2\u7136\u6709\u610f\u6307\u6559\u6211\u4eec\uff0c\u6211\u4eec\u4e5f\u4e0d\u5fc5\u8f9c\u8d1f\u4eba\u5bb6\u7684\u597d \u610f\u3002\u201d \u3000\u3000\u8bf4\u7740\u8bdd\uff0c\u4ed6\u8fdb\u6b65\u53f3\u524a\u4e00\u638c\uff0c\u780d\u4e0b\u53bb\u5374\u5288\u5411\u90a3\u9053\u4eba\u7684\u5de6\u9888\uff0c\u559d\u9053\uff1a\u201c\u6211\u5c31\u5148\u966a\u9053\u957f\u8d70\u513f \u62db\u3002\u201d \u3000\u3000\u4ed6\u4e00\u51fa\u624b\u4fbf\u662f\u6740\u7740\uff0c\u610f\u601d\u662f\u60f3\u5feb\u4e9b\u89e3\u51b3\u8fd9\u4e24\u4f4d\u9053\u4eba\uff0c\u95ef\u4e0a\u5c71\u53bb\u3002 \u3000\u3000\u90a3\u9053\u4eba\u8fde\u58f0\u51b7\u7b11\u4e2d\uff0c\u907f\u5f00\u6b64\u62db\uff0c\u8eab\u624b\u4ea6\u81ea\u4e0d\u5f31\uff0c\u718a\u501c\u81f4\u654c\u673a\u5148\u3001\u8fde\u73af\u8fd0\u638c\uff0c\u5c06\u4ed6\u903c\u5f97\u7f13 \u4e0d\u8fc7\u6c14\u6765\u3002 \u3000\u3000\u5c1a\u672a\u660e\u4e00\u770b\u718a\u501c\u52a8\u624b\uff0c\u4ed6\u5c82\u80af\u95f2\u7740\uff0c\u5bfb\u7740\u53e6\u4e00\u4e2a\u9053\u4eba\u6253\u4e86\u8d77\u6765\u3002 \u3000\u3000\u90a3\u5e74\u8f7b\u9053\u4eba\u5728\u65c1\u770b\u7740\uff0c\u5374\u4e0d\u52a8\uff0c\u7adf\u50cf\u662f\u6709\u70b9\u4e8b\u4e0d\u5173\u5df2\u7684\u6837\u5b50\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u957f\u9aef\u9053\u4eba\uff0c\u672c\u662f\u7384\u771f\u89c2\u85cf\u7ecf\u9601\u7684\u9ad8\u624b\uff0c\u53ea\u56e0\u4ed6\u4e24\u4eba\u813e\u6c14\u592a\u66b4\uff0c\u5728\u5916\u9762\u72af\u4e86\u6740\u6212\uff0c \u662f\u4ee5\u6b66\u5f53\u638c\u6559\u4fbf\u4ee4\u4ed6\u4e24\u4eba\u5728\u85cf\u7ecf\u9601\u91cc\u95ed\u95e8\u601d\u8fc7\uff0c\u54ea\u77e5\u4eca\u65e5\u53c8\u72af\u4e86\u8001\u6bdb\u75c5\uff0c\u4e09\u8a00\u4e24\u8bed\uff0c\u4fbf\u548c\u4eba \u5bb6\u52a8\u8d77\u624b\u6765\u3002 \u3000\u3000\u4f46\u8fd9\u5176\u4e2d\u4ea6\u6709\u7f18\u6545\u3002 \u3000\u3000\u539f\u6765\u590f\u82b8\u88ab\u56db\u4eea\u5251\u5ba2\u548c\u4e1c\u65b9\u745b\u9001\u5230\u6b66\u5f53\u5c71\u540e\uff0c\u5fc3\u4e2d\u53c8\u6c14\u53c8\u6025\uff0c\u53c8\u5728\u602a\u718a\u501c\uff1a\u201c\u4f60\u96be\u9053\u5728 \u9694\u58c1\u90a3\u95ee\u623f\u91cc\u5374\u4e0d\u77e5\u9053\u6211\u88ab\u4eba\u52ab\u8d70\u4e86\uff1f\u201d\u53c8\u4e0d\u7981\u6709\u70b9\u540e\u6094\uff1a\u201c\u6211\u771f\u4e0d\u8be5\u60f9\u6765\u8fd9\u4e9b\u9ebb\u70e6\u3002\u201d \u3000\u3000\u4e1c\u65b9\u745b\u8fd8\u6ca1\u6709\u4e0a\u5c71\uff0c\u4fbf\u8d70\u4e86\uff0c\u5979\u4e5f\u4e0d\u65e0\u540e\u6094\uff1a\u201c\u5176\u5b9e\u6211\u771f\u4e0d\u8be5\u505a\u8fd9\u4ef6\u4e8b\uff0c\u88ab\u54e5\u54e5\u77e5\u9053 \u4e86\uff0c\u4e00\u5b9a\u8981\u9a82\u6b7b\u6211\u4e86\uff0c\u5509\uff0c\u6211\u8fd8\u4e0d\u662f\u4e3a\u4e86\u4ed6\uff0c\u53ef\u662f\u4ed6\u77e5\u9053\u4e86\uff0c\u6050\u6015\u4f1a\u66f4\u4e0d\u559c\u6b22\u6211\u4e86\u5427\u3002\u201d \u3000\u3000\u56db\u4eea\u5251\u5ba2\u5374\u662f\u626c\u626c\u5f97\u610f\uff0c\u8ba4\u4e3a\u5df2\u66ff\u6b66\u5f53\u6d3e\u6211\u56de\u9762\u5b50\u6765\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u56de\u5230\u7384\u771f\u89c2\u5bfa\uff0c\u638c\u6559\u771f\u4eba\u6b63\u5728\u5750\u7740\uff0c\u4ed6\u4eec\u5c31\u5c06\u590f\u82b8\u8f6f\u7981\u5728\u85cf\u7ecf\u9601\u91cc\uff0c\u8bf7\u90a3\u4e24\u4f4d\u957f\u9aef \u9053\u4eba\uff0c\u4e5f\u5c31\u662f\u56db\u4eea\u5251\u5ba2\u7684\u5e08\u5144\uff0c\u82cd\u7384\u3001\u82cd\u8346\u4e24\u4eba\u770b\u5b88\u7740\uff0c\u82cd\u7384\u3001\u82cd\u8346\u867d\u662f\u56db\u4eea\u5251\u5ba2\u7684\u5e08\u5144\uff0c \u4f46\u662f\u5728\u6d3e\u4e2d\u7684\u5730\u4f4d\uff0c\u5374\u4e0d\u53ca\u56db\u4eea\u5251\u5ba2\uff0c\u6b66\u529f\u4e5f\u6bd4\u56db\u4eea\u5251\u5ba2\u5dee\u4e9b\uff0c\u4ed6\u4e24\u4eba\u89c1\u56db\u4eea\u5251\u5ba2\u8981\u4ed6\u4eec\u770b \u5b88\u4e00\u4e2a\u5973\u5b50\uff0c\u867d\u662f\u4e0d\u613f\uff0c\u4f46\u4e5f\u65e0\u6cd5\u63a8\u6258\uff0c\u4f46\u6697\u4e2d\u5374\u4e0d\u514d\u8981\u57cb\u6028\u51e0\u53e5\uff0c\u9053\uff1a\u201c\u8fd9\u6837\u4e00\u4e2a\u5c0f\u4e2b \u5934\uff0c\u4e5f\u8981\u6211\u4eec\u6765\u5b88\u7740\uff0c\u771f\u662f\u4f55\u82e6\uff1f\u201d \u3000\u3000\u590f\u82b8\u806a\u660e\u7edd\u9876\uff0c\u542c\u4e86\u8fd9\u8bdd\uff0c\u4fbf\u505a\u51fa\u5a07\u602f\u602f\u7684\u6837\u5b50\u6765\u3002 \u3000\u3000\u4e8e\u662f\u82cd\u7384\u3001\u82cd\u8346\u4e24\u4e2a\u9053\u4eba\u66f4\u52a0\u758f\u5ffd\uff0c\u8d8a\u53d1\u4e0d\u5c06\u590f\u82b8\u770b\u5728\u773c\u91cc\uff0c\u53ea\u968f\u4fbf\u5c06\u5979\u5173\u5728\u4e00\u4e2a\u9601\u697c \u91cc\uff0c\u8fde\u5b88\u90fd\u4e0d\u5b88\u7740\u3002 \u3000\u3000\u590f\u82b8\u5fc3\u91cc\u9ad8\u5174\uff0c\u5f53\u5929\u665a\u4e0a\uff0c\u4fbf\u5077\u5077\u5730\u6e9c\u8d70\u4e86\uff0c\u987b\u77e5\u5979\u6b66\u529f\u4ea6\u975e\u5f31\u624b\uff0c\u518d\u52a0\u4e0a\u5fc3\u601d\u7075\u654f\uff0c \u7ade\u4ece\u9ad8\u624b\u5982\u4e91\u7684\u6b66\u5f53\u5c71\u9003\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u56db\u4eea\u5251\u5ba2\u77e5\u9053\u6b64\u4e8b\uff0c\u6c14\u5f97\u8e29\u811a\uff0c\u76f4\u57cb\u6028\u82cd\u7384\u3001\u82cd\u8346\u800c\u5165\uff0c\u51cc\u4e91\u5b50\u6c14\u9053\uff1a\u201c\u5e08\u5144\u4eec \u4e5f\u662f\u592a\u4e0d\u5c0f\u5fc3\u4e86\uff0c\u8ba9\u8fd9\u6837\u4e2a\u5c0f\u59d1\u5a18\u5c06\u6b66\u5f53\u5c71\u770b\u4f5c\u65e0\u4eba\u4e4b\u5883\uff0c\u65e5\u540e\u4f20\u51fa\u6c5f\u6e56\uff0c\u5c82\u4e0d\u662f\u4e2a\u7b11 \u8bdd\u3002\u201d \u3000\u3000\u82cd\u7384\u3001\u82cd\u8346\u4e5f\u662f\u6c14\u5f97\u53d8\u8272\uff0c\u53d7\u4e86\u5e08\u5f1f\u7684\u57cb\u6028\uff0c\u5374\u53c8\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u5f53\u5929\u56db\u4eea\u5251\u5ba2\u53c8\u5306\u5306\u4e0a\u5c71\uff0c\u58f0\u8a00\u975e\u5c06\u590f\u82b8\u627e\u56de\u6765\u4e0d\u53ef\uff0c\u4e34\u8d70\u65f6\u5982\u6b64\u8fd9\u822c\u5c06\u4e8b\u60c5\u7684\u59cb\u672a\u4e00 \u8bf4\uff0c\u4ed6\u4eec\u77e5\u9053\u718a\u501c\u65e5\u5185\u4fbf\u4f1a\u5bfb\u6765\uff0c\u4e39\u9633\u5b50\u9053\uff1a\u201c\u4ed6\u82e5\u5bfb\u884c\u6765\u65f6\uff0c\u5e08\u5144\u4eec\u5c31\u5c06\u8fd9\u4e8b\u544a\u8bc9\u4ed6\uff0c\u5e76 \u4e14\u8fd8\u544a\u8bc9\u4ed6\uff0c\u590f\u82b8\u867d\u7136\u8dd1\u4e86\uff0c\u4f46\u6211\u4eec\u5374\u4e00\u5b9a\u8981\u5c06\u5979\u6293\u56de\u6765\uff0c\u718a\u501c\u82e5\u518d\u8981\u6765\u7ba1\u8fd9\u4e8b\uff0c\u4fbf\u662f\u6211\u4eec \u6b66\u5f53\u6d3e\u7684\u4ec7\u654c\u3002\u201d \u3000\u3000\u51cc\u4e91\u5b50\u5374\u9053\uff1a\u201c\u8fd9\u4e8b\u82e5\u8981\u544a\u8bc9\u718a\u501c\uff0c\u4ed6\u5c82\u975e\u8981\u7b11\u6211\u6b66\u5f53\u6d3e\u65e0\u7528\uff1f\u201d \u3000\u3000\u4e39\u9633\u5b50\u8003\u8651\u4e86\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u5176\u5b9e\u8457\u4e0d\u544a\u8bc9\u4ed6\u4e5f\u662f\u4e00\u6837\uff0c\u4f60\u8fd8\u6015\u65e5\u540e\u6c5f\u6e56\u4e0a\u6ca1\u6709\u4eba\u77e5 \u9053\uff1f\u201d \u3000\u3000\u51cc\u4e91\u5b50\u770b\u4e86\u82cd\u7384\u3001\u82cd\u8346\u4e00\u773c\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u4fbf\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u82cd\u7384\u3001\u82cd\u8346\u53c8\u6c14\u53c8\u60ed\uff0c\u7b49\u56db\u4eea\u5251\u5ba2\u4e0b\u5c71\u540e\uff0c\u4fbf\u4e00\u5fc3\u60f3\u5bfb\u718a\u501c\u6765\u51fa\u6c14\uff0c\u8fd9\u65e5\u4ed6\u4eec\u8d70\u5230\u89c2\u95e8 \u53e3\u65f6\uff0c\u542c\u5230\u6709\u4e24\u4e2a\u5e74\u8f7b\u4eba\u6b66\u5f53\u5c71\u6765\u627e\u56db\u4eea\u5251\u5ba2\uff0c\u4fbf\u77e5\u9053\u4e00\u5b9a\u662f\u718a\u501c\u6765\u4e86\uff0c\u6240\u4ee5\u5c31\u5306\u5306\u8d76\u6765 \u4e86\uff0c\u52a8\u8d77\u624b\u6765\u3002 \u3000\u3000\u54ea\u77e5\u9053\u4ed6\u4eec\u4e00\u5411\u81ea\u6043\u7684\u6b66\u529f\uff0c\u5374\u4e0d\u662f\u8fd9\u4e24\u4e2a\u5e74\u8f7b\u4eba\u7684\u5bf9\u624b\uff0c\u8eab\u5f62\u5168\u88ab\u5c01\u5f97\u7f13\u4e0d\u5f00\u624b\u6765\u3002 \u3000\u3000\u4ed6\u4eec\u5728\u89c2\u91cc\u4e00\u5411\u4eba\u7f18\u4e0d\u597d\uff0c\u540e\u4e00\u8f88\u7684\u5f1f\u5b50\uff0c\u66f4\u5168\u90e8\u5bf9\u4ed6\u4eec\u4e0d\u597d\uff0c\u662f\u4ee5\u90a3\u5e74\u8f7b\u9053\u4eba\u5728\u65c1\u770b \u7740\uff0c\u6839\u672c\u4e0d\u7ba1\uff0c\u795e\u8272\u91cc\u53cd\u800c\u6709\u4e9b\u5e78\u707e\u4e50\u7978\u7684\u6837\u5b50\u3002 \u3000\u3000\u718a\u501c\u53ca\u5c1a\u672a\u660e\u7acb\u8eab\u5148\u5c06\u8fd9\u4e24\u4e2a\u50b2\u6162\u7684\u9053\u4eba\u4f24\u5728\u638c\u4e0b\uff0c\u638c\u5f71\u7ffb\u98de\uff0c\u773c\u770b\u4fbf\u8981\u5f97\u624b\uff0c\u5374\u4e0d\u6599 \u5c71\u4e0a\u53c8\u8dd1\u4e0b\u4e00\u4eba\uff0c\u718a\u501c\u5e94\u4ed8\u82cd\u7384\uff0c\u672c\u662f\u7ef0\u7ef0\u6709\u4f59\uff0c\u4e00\u770b\u6765\u4e86\u4eba\uff0c\u6697\u5fd6\u9053\uff1a\u201c\u8fd9\u6b66\u5f53\u6d3e\u5012\u7684\u786e \u662f\u4e0d\u597d\u95ef\u7684\uff0c\u9a6c\u4e0a\u4fbf\u6765\u4e86\u5e2e\u624b\u3002\u201d \u3000\u3000\u54ea\u77e5\u9053\u9053\u4eba\u534a\u8def\u4e0a\u4fbf\u9ad8\u53eb\u9053\uff1a\u201c\u82cd\u7384\u3001\u82cd\u8346\u4e24\u4f4d\u5e08\u5144\u5feb\u4f4f\u624b\uff0c\u638c\u6559\u771f\u4eba\u8bf7\u8fd9\u4f4d\u65bd\u4e3b\u5230\u89c2 \u4e2d\u4e00\u89c1\uff0c\u8bf4\u662f\u6709\u8bdd\u8981\u8bf4\u5462\u3002\u201d \u3000\u3000\u82cd\u7384\u3001\u82cd\u8346\u4e00\u542c\u638c\u6559\u771f\u4eba\u7684\u5429\u5490\uff0c\u54ea\u91cc\u6562\u6709\u4e00\u4e1d\u8fdd\u6297\u7684\u610f\u601d\u3002 \u3000\u3000\u718a\u501c\u53ca\u5c1a\u672a\u660e\u4e8c\u4eba\uff0c\u4e5f\u7acb\u523b\u4f4f\u4e86\u624b\u3002 \u3000\u3000\u540e\u6765\u90a3\u9053\u4eba\u6765\u5230\u4ed6\u4e8c\u4eba\u9762\u524d\uff0c\u5355\u624b\u6253\u4e86\u4e2a\u95ee\u8baf\uff0c\u8bf4\u9053\uff1a\u201c\u655d\u6d3e\u638c\u6559\u771f\u4eba\u60c5\u4e8c\u4f4d\u5230\u7384\u771f\u89c2 \u4e00\u53d9\u3002\u201d \u3000\u3000\u90a3\u9053\u4eba\u53c8\u9053\uff1a\u201c\u6570\u767e\u5e74\u6765\uff0c\u655d\u6d3e\u90fd\u8c28\u5b88\u7740\u771f\u6b66\u7237\u7237\u7684\u6559\u8bad\uff0c\u6ca1\u6709\u4eba\u5e26\u7740\u5251\u4e0a\u5c71\u53bb\uff0c\u8fd9\u4e0d \u662f\u655d\u6d3e\u72c2\u50b2\u81ea\u5927\uff0c\u8fd8\u5e0c\u671b\u65bd\u4e3b\u4e5f\u80fd\u4f53\u8c05\u6211\u4eec\u7684\u82e6\u8877\uff0c\u5c06\u5251\u7559\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u8fd9\u9053\u4eba\u8bf4\u5f97\u6781\u4e3a\u5ba2\u6c14\u800c\u5706\u6ed1\uff0c\u718a\u501c\u65e0\u6cd5\u63a8\u6258\uff0c\u53ea\u5f97\u5c06\u5251\u89e3\u4e0b\u6765\u3002 \u3000\u3000\u718a\u501c\u53cc\u624b\u5c06\u5251\u9001\u5230\u90a3\u9053\u4eba\u9762\u524d\u3002 \u3000\u3000\u90a3\u9053\u4eba\u63a5\u8fc7\u5251\u6765\uff0c\u7b11\u9053\uff1a\u201c\u65bd\u4e3b\u8bf7\u653e\u5fc3\uff0c\u8fd9\u67c4\u5251\u60f3\u5fc5\u662f\u795e\u7269\u5229\u5668\uff0c\u8d2b\u9053\u4e00\u5b9a\u547d\u4eba\u5728\u6b64\u597d \u597d\u770b\u5b88\u3002\u201d \u3000\u3000\u4ed6\u9762\u4e0a\u5fae\u9732\u51fa\u4e00\u4e1d\u72c2\u505a\u7684\u5149\u8292\uff0c\u63a5\u7740\u8bf4\uff1a\u201c\u6211\u60f3\u8fd8\u6ca1\u6709\u4eba\u6709\u8fd9\u80c6\u5b50\u5230\u6b66\u5f53\u5c71\u6765\u62a2\u5251 \u7684\u3002\u201d \u3000\u3000\u718a\u501c\u77e5\u9053\u8fd9\u6b66\u5f53\u6d3e\u7684\u786e\u5728\u6b66\u6797\u4e2d\u4eab\u6709\u76db\u540d\uff0c\u662f\u4ee5\u5e76\u4e0d\u602a\u90a3\u9053\u4eba\u7684\u72c2\u50b2\u3002 \u3000\u3000\u90a3\u9053\u4eba\u53c8\u5bf9\u82cd\u7384\u3001\u82cd\u8346\u4e24\u9053\u4eba\u8bf4\u9053\uff1a\u201c\u5e08\u5144\u4eec\u4e5f\u8bf7\u56de\u89c2\u53bb\uff0c\u7b49\u4e00\u4f1a\u638c\u6559\u771f\u4eba\u4e5f\u6709\u8bdd\u5429\u5490 \u54e9\u3002\u201d \u3000\u3000\u82cd\u7384\u3001\u82cd\u8346\u7b54\u5e94\u7740\uff0c\u9762\u4e0a\u96be\u770b\u5df2\u6781\uff0c\u90a3\u9053\u4eba\u5374\u4e0d\u7406\u4f1a\uff0c\u5c06\u5251\u4ea4\u7ed9\u90a3\u4e24\u4e2a\u5e74\u8f7b\u4eba\uff0c\u9053\uff1a \u201c\u4f60\u4eec\u597d\u597d\u5728\u6b64\u770b\u5b88\u7740\u3002\u201d \u3000\u3000\u718a\u501c\u89c1\u9053\u4eba\u767d\u9762\u65e0\u987b\uff0c\u770b\u8d77\u6765\u53ea\u6709\u4e09\u5341\u5de6\u53f3\uff0c\u4f46\u795e\u6001\u5e84\u91cd\u4e2d\u5374\u53c8\u5e26\u7740\u4e9b\u5a01\u4e25\uff0c\u4e0d\u7981\u8d77\u4e86 \u597d\u611f\uff0c\u95ee\u9053\uff1a\u201c\u9053\u957f\u6cd5\u53f7\u5f1f\u5b50\u5c1a\u672a\u5f97\u77e5\u3002\u201d \u3000\u3000\u90a3\u9053\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8d2b\u9053\u98de\u9e64\u5b50\uff0c\u867d\u7136\u4e0d\u66fe\u5728\u6c5f\u6e56\u4e2d\u8d70\u52a8\uff0c\u5374\u4e5f\u66fe\u95fb\u5f97\u718a\u5927\u4fa0\u7684\u82f1 \u540d\u3002\u201d \u3000\u3000\u718a\u501c\u6697\u9053\uff1a\u201c\u4ed6\u5012\u6653\u5f97\u6211\u7684\u59d3\u540d\u4e86\u3002\u201d \u3000\u3000\u98de\u9e64\u9053\u4eba\u53c8\u7528\u773c\u775b\u770b\u7740\u5c1a\u672a\u660e\uff0c\u9053\uff1a\u201c\u8fd9\u4f4d\u65bd\u4e3b\u795e\u91c7\u7167\u4eba\uff0c\u60f3\u5fc5\u4e5f\u662f\u6b66\u6797\u4e2d\u6210\u540d\u7684\u4eba\u7269 \u4e86\u3002\u201d \u3000\u3000\u5c1a\u672a\u660e\u89c1\u8fd9\u98de\u9e64\u5b50\u5e73\u6613\u8fd1\u4eba\uff0c\u4fbf\u7b11\u9053\uff1a\u201c\u5f1f\u5b50\u5c1a\u672a\u660e\uff0c\u53ea\u662f\u6c5f\u6e56\u5c0f\u5352\u7f62\u4e86\uff0c\u54ea\u91cc\u8bf4\u5f97\u4e0a \u662f\u6210\u540d\u7684\u82f1\u96c4\u3002\u201d \u3000\u3000\u4ed6\u4ee5\u4e3a\u98de\u9e64\u5b50\u5fc5\u4e5f\u77e5\u9053\u4ed6\u7684\u540d\u5934\uff0c\u54ea\u77e5\u9053\u8fd9\u98de\u9e64\u5b50\u662f\u6b66\u5f53\u638c\u95e8\u7684\u5f92\u5f1f\uff0c\u4e00\u76f4\u968f\u5728\u5999\u4e00\u771f \u4eba\u7684\u8eab\u4fa7\uff0c\u7684\u786e\u672a\u5728\u6c5f\u6e56\u4e2d\u8d70\u52a8\u8fc7\uff0c\u5c1a\u672a\u660e\u6210\u540d\u4e8e\u4e24\u6cb3\uff0c\u4ed6\u4e5f\u4e0d\u77e5\u9053\uff0c\u53ea\u8bf4\u4e86\u58f0\u201c\u4e45\u4ef0\u201d\u3002 \u3000\u3000\u98de\u9e64\u5b50\u9886\u7740\u4ed6\u4eec\u7f13\u7f13\u5411\u5c71\u4e0a\u8d70\u53bb\uff0c\u6b64\u65f6\u65ed\u65e5\u5df2\u5347\uff0c\u4f46\u5c71\u9053\u4e0a\u4ecd\u662f\u9634\u51c9\u5f97\u5f88\uff0c\u4e00\u8def\u4e0a\u98de\u9e64 \u5b50\u548c\u718a\u501c\u53ca\u5c1a\u672a\u660e\u968f\u610f\u8c08\u7b11\uff0c\u4e1d\u6beb\u6ca1\u6709\u654c\u610f\u3002 \u3000\u3000\u4ed6\u6b65\u5c65\u5b89\u8be6\uff0c\u811a\u4e0b\u5c18\u571f\u4e0d\u5174\uff0c\u4e24\u773c\u7684\u795e\u5149\uff0c\u4e5f\u662f\u655b\u800c\u4e0d\u9732\uff0c\u718a\u501c\u6697\u5fd6\uff1a\u201c\u770b\u6765\u6b66\u5f53\u6d3e\uff0c \u5012\u7684\u786e\u6709\u51e0\u4e2a\u9ad8\u4eba\u3002\u201d \u3000\u3000\u5a49\u8712\u5730\u5411\u4e0a\u8d70\u4e86\u534a\u523b\uff0c\u524d\u9762\u4e00\u5927\u7247\u677e\u6797\u4e2d\uff0c\u9690\u9690\u9732\u51fa\u4e00\u6392\u7ea2\u5899\uff0c\u98de\u9e64\u5b50\u811a\u4e0b\u52a0\u5feb\uff0c\u5230\u4e86 \u89c2\u95e8\u524d\uff0c\u718a\u501c\u62ac\u5934\u4e00\u671b\uff0c\u89c1\u89c2\u95e8\u4e0a\u7684\u6a2a\u989d\u4e0a\uff0c\u5199\u7740\u4e09\u4e2a\u6597\u5927\u7684\u91d1\u5b57\uff1a\u201c\u7384\u771f\u89c2\u201d\u3002 \u3000\u3000\u89c2\u95e8\u5f00\u4e86\u534a\u6247\uff0c\u677e\u6797\u91cc\u9e1f\u8bed\u8c03\u557e\uff0c\u677e\u7c38\u9e23\u7136\uff0c\u770b\u53bb\u771f\u662f\u4e2a\u4ed9\u5883\u3002\u4ee4\u4eba\u4fd7\u8651\u4e3a\u4e4b\u4e00\u6e05\u3002 \u3000\u3000\u718a\u501c\u53ca\u5c1a\u672a\u660e\u968f\u7740\u98de\u9e64\u9053\u4eba\u8d70\u8fdb\u89c2\u95e8\uff0c\u9662\u4e2d\u6253\u626b\u5f97\u4e00\u5c18\u4e0d\u67d3\uff0c\u5e72\u51c0\u5df2\u6781\uff0c\u6709\u51e0\u4e2a\u9053\u4eba\u5728 \u5927\u6bbf\u4e0a\u70e7\u7740\u9999\uff0c\u8bf5\u7740\u7ecf\u3002 \u3000\u3000\u98de\u9e64\u5b50\u5f15\u7740\u4ed6\u4eec\u4e24\u4eba\u8d70\u8fdb\u4e1c\u914d\u6bbf\uff0c\u82cd\u7384\u3001\u82cd\u8346\u5374\u8f6c\u5230\u540e\u9762\u53bb\u4e86\u3002 \u3000\u3000\u4e1c\u914d\u6bbf\u4e0a\u4f9b\u7684\u795e\u50cf\uff0c\u6b63\u662f\u5f20\u4e09\u4e30\u771f\u4eba\uff0c\u624b\u91cc\u62ff\u7740\u62c2\u5c18\uff0c\u51dd\u76ee\u8fdc\u671b\uff0c\u6829\u6829\u5982\u751f\uff0c\u60f3\u6765\u5851\u9020 \u8fd9\u795e\u50cf\u7684\u5fc5\u4e5f\u662f\u4e2a\u540d\u5320\u3002 \u3000\u3000\u718a\u501c\u53ca\u5c1a\u672a\u660e\u770b\u5230\u8fd9\u5185\u5bb6\u6b66\u672f\u7684\u5b97\u7956\uff0c\u4e0d\u7981\u6cb9\u7136\u800c\u751f\u656c\u610f\uff0c\u8d70\u5230\u62db\u57ab\u524d\uff0c\u8083\u7136\u8dea\u4e86\u4e0b \u53bb\u3002 \u3000\u3000\u8f6c\u51fa\u4e1c\u914d\u6bbf\uff0c\u53c8\u662f\u91cd\u9662\u5b50\uff0c\u518d\u8f6c\u51fa\u8fd9\u9662\u5b50\uff0c\u662f\u4e00\u4e2a\u5e76\u4e0d\u5927\u5927\u7684\u56ed\u5b50\u3002 \u3000\u3000\u56ed\u5b50\u91cc\u79cd\u7740\u7684\u90fd\u662f\u677e\u68a7\u67f3\u67cf\uff0c\u548c\u7fe0\u7af9\u4e4b\u7c7b\u7684\u6811\u6728\uff0c\u6ca1\u6709\u82b1\u7684\u70b9\u7f00\uff0c\u4f7f\u8fd9\u4e2a\u56ed\u5b50\u770b\u8d77\u6765\u66f4 \u5e7d\u96c5\u5f97\u5f88\u3002 \u3000\u3000\u8d70\u8fdb\u8fd9\u56ed\u5b50\u540e\uff0c\u98de\u9e64\u5b50\u7684\u6001\u5ea6\u66f4\u606d\u8083\u4e86\u3002 \u3000\u3000\u4ed6\u8f7b\u58f0\u5bf9\u718a\u501c\u7b49\u9053\uff1a\u201c\u8d2b\u50e7\u53bb\u56de\u7980\u5bb6\u5e08\u4e00\u58f0\uff0c\u4e24\u4f4d\u5728\u6b64\u7a0d\u5019\u3002\u201d \u3000\u3000\u7247\u523b\uff0c\u98de\u9e64\u9053\u4eba\u53c8\u8d70\u51fa\u6765\uff0c\u7b11\u9053\uff1a\u201c\u5bb6\u5e08\u8bf7\u4e24\u4f4d\u8fdb\u53bb\u3002\u201d \u3000\u3000\u7a7f\u51fa\u4e00\u5927\u7247\u7af9\u6797\uff0c\u8ff8\u524d\u662f\u51e0\u95ee\u6781\u7cbe\u81f4\u7684\u623f\u5b50\uff0c\u95e8\u7a97\u90fd\u662f\u6302\u7740\u9752\u8272\u7684\u7af9\u5e18\u5b50\u3002 \u3000\u3000\u98de\u9e64\u9053\u4eba\u8f7b\u8f7b\u5730\u8d70\u5230\u95e8\u53e3\uff0c\u4f3c\u4e4e\u6ca1\u6709\u4e00\u70b9\u58f0\u97f3\uff0c\u95e8\u91cc\u5374\u6709\u4e00\u4e2a\u6e05\u6717\u7684\u53e3\u97f3\u8bf4\u9053\uff1a\u201c\u8fdb \u6765\u3002\u201d \u3000\u3000\u718a\u501c\u53ca\u5c1a\u672a\u660e\u8d70\u4e0a\u4e24\u6b65\uff0c\u98de\u9e64\u9053\u4eba\u6380\u8d77\u7af9\u5e18\u5b50\uff0c\u9053\uff1a\u201c\u8bf7\u8fdb\u3002\u201d \u3000\u3000\u623f\u4e2d\u6563\u53d1\u51fa\u4e00\u822c\u8885\u8885\u6e05\u9999\uff0c\u718a\u501c\u53ca\u5c1a\u672a\u660e\u606d\u8c28\u8d70\u4e86\u8fdb\u53bb\uff0c\u89c1\u671d\u95e8\u653e\u7740\u7684\u584c\u524d\uff0c\u542b\u7b11\u7ad9\u7acb \u7740\u4e00\u4e2a\u7fbd\u8863\u661f\u51a0\u7684\u9053\u4eba\u3002 \u3000\u3000\u4ed6\u4eec\u77e5\u9053\u8fd9\u5c31\u662f\u6b66\u6797\u7684\u6700\u5927\u5b97\u6d3e\u7684\u638c\u95e8\u5999\u4e00\u771f\u4eba\u4e86\uff0c\u53ea\u89c1\u4ed6\u6e05\u77cd\u7684\u8138\u4e0a\uff0c\u5e26\u7740\u7684\u662f\u6e29\u548c \u7684\u7b11\u5bb9\uff0c\u5e76\u6ca1\u6709\u4e00\u70b9\u50b2\u6162\u6216\u662f\u51b7\u5cfb\u7684\u6837\u5b50\uff0c\u8fd9\u548c\u4ed6\u4eec\u7684\u60f3\u6cd5\u5927\u4e0d\u76f8\u540c\uff0c\u4f46\u662f\u4ed6\u5374\u53e6\u6709\u4e00\u79cd\u529b \u91cf\uff0c\u4f7f\u8fd9\u4e24\u4e2a\u8eab\u6000\u7edd\u6280\u7684\u4fa0\u58eb\uff0c\u5728\u4ed6\u9762\u524d\uff0c\u4e0d\u89c9\u611f\u5230\u81ea\u8eab\u7684\u8c26\u5351\u3002 \u3000\u3000\u5999\u4e00\u771f\u4eba\u7684\u76ee\u5149\uff0c\u95ea\u7535\u822c\u5728\u4ed6\u4eec\u8138\u4e0a\u4e00\u8f6c\uff0c\u718a\u501c\u53ca\u5c1a\u672a\u660e\u4f4e\u4e0b\u4e86\u5934\uff0c\u4fbf\u8981\u4e0b\u62dc\uff0c\u5374\u88ab\u4ed6 \u8f7b\u8f7b\u62e6\u4f4f\u4e86\uff0c\u53ea\u53d7\u4e86\u534a\u793c\u3002 \u3000\u3000\u5999\u4e00\u771f\u4eba\u5fae\u7b11\u9053\uff1a\u201c\u771f\u662f\u6c5f\u5c71\u4ee3\u6709\u4eba\u624d\u51fa\uff0c\u4e24\u4f4d\u679c\u7136\u90fd\u662f\u7ec3\u6b66\u4eba\u4e2d\u5343\u767e\u5e74\u96be\u89c1\u7684\u5947\u624d\uff0c \u602a\u4e0d\u5f97\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u5c31\u540d\u52a8\u6c5f\u6e56\u4e86\u3002\u201d \u3000\u3000\u718a\u501c\u6781\u8c28\u614e\u800c\u5c0f\u5fc3\u5730\u5c06\u4ed6\u4eec\u7684\u6765\u610f\u8bf4\u51fa\uff0c\u5e76\u4e14\u8bf4\u9053\uff1a\u201c\u590f\u82b8\u5927\u5e74\u8f7b\uff0c\u4e0d\u61c2\u4e16\u6545\uff0c\u8fd8\u671b\u524d \u8f88\u80fd\u5ff5\u5979\u65e0\u77e5\uff0c\u9976\u6055\u5979\u8fd9\u4e00\u6b21\u3002\u201d \u3000\u3000\u201c\u539f\u6765\u4f60\u8fd8\u4e0d\u77e5\u9053\u3002\u201d\u5999\u4e00\u771f\u4eba\u5fae\u7b11\u7740\u9053\uff1a\u201c\u90a3\u4f4d\u590f\u59d1\u5a18\uff0c\u8d2b\u9053\u6839\u672c\u6ca1\u6709\u89c1\u8fc7\u5979\uff0c\u98de \u9e64\uff0c\u4f60\u8fc7\u6765\uff0c\u5c06\u8fd9\u4e8b\u8bf4\u7ed9\u4e24\u4f4d\u542c\u3002\u201d \u3000\u3000\u98de\u9e64\u9053\u4eba\u8fd9\u624d\u5c06\u590f\u82b8\u5982\u4f55\u9003\u51fa\uff0c\u56db\u4eea\u5251\u5ba2\u5982\u4f55\u5927\u6012\u8ffd\u53bb\uff0c\u8bf4\u7ed9\u718a\u501c\u542c\u3002 \u3000\u3000\u5999\u4e00\u771f\u4eba\u8138\u4e0a\uff0c\u4eff\u4f5b\u6c38\u8fdc\u662f\u5fae\u7b11\u7740\u7684\uff0c\u8bf4\u9053\uff1a\u201c\u5176\u5b9e\u8fd9\u70b9\u5c0f\u4e8b\uff0c\u8d2b\u9053\u5e76\u672a\u653e\u5728\u5fc3\u4e0a\uff0c\u53ea \u662f\u51e0\u4e2a\u5c0f\u5f92\u5728\u90a3\u91cc\u95f9\u7f62\u4e86\u3002\u201d \u3000\u3000\u4ed6\u9762\u5bb9\u4e00\u6574\uff0c\u76ee\u4e2d\u9732\u51fa\u54b8\u4e25\u7684\u5149\u8292\uff0c\u53c8\u9053\uff1a\u201c\u4ed6\u4eec\u51e0\u4e2a\u8fd1\u5e74\u5728\u6c5f\u6e56\u91cc\u4e5f\u95f9\u5f97\u592a\u5389\u5bb3\u4e86\uff0c \u4e9b\u8bb8\u5c0f\u4e8b\uff0c\u4fbf\u542b\u6028\u5fc5\u62a5\uff0c\u54ea\u91cc\u8fd8\u6709\u51fa\u5bb6\u4eba\u7684\u6837\u5b50\uff0c\u5c24\u5176\u662f\u82cd\u7384\u3001\u82cd\u8346\u90a3\u4e24\u4f4d\u5b7d\u969c\u3002\u201d \u3000\u3000\u718a\u501c\u542c\u89c1\u590f\u82b8\u9003\u53bb\uff0c\u53c8\u60ca\u53c8\u559c\uff0c\u559c\u7684\u662f\u5979\u5c45\u7136\u6ca1\u6709\u5403\u5230\u4efb\u4f55\u82e6\u5934\uff0c\u60ca\u7684\u5374\u662f\u6015\u5979\u53c8\u88ab\u56db \u4eea\u5251\u5ba2\u8ffd\u5230\u624b\uff0c\u4f46\u662f\u4ed6\u8868\u9762\u4e0a\u4ecd\u5728\u77dc\u6301\u7740\uff0c\u6781\u529b\u5730\u4f7f\u81ea\u5df1\u7684\u60c5\u611f\uff0c\u4e0d\u9732\u51fa\u4e00\u5206\u5230\u8868\u9762\u4e0a\u6765\u3002 \u3000\u3000\u5999\u4e00\u771f\u4eba\u5bf9\u8fd9\u4e24\u4e2a\u5e74\u8f7b\u9ad8\u624b\u4eff\u4f5b\u751a\u52a0\u9752\u7750\uff0c\u6bb7\u6bb7\u5782\u5631\uff0c\u95ee\u53ca\u4e24\u4eba\u7684\u5e08\u627f\uff0c\u4ed6\u53c8\u9053\uff1a\u201c\u98d8 \u7136\u8001\u524d\u8f88\u6211\u5728\u4e8c\u5341\u51e0\u5c81\uff0c\u4e91\u6e38\u56db\u6d77\u65f6\uff0c\u89c1\u8fc7\u4ed6\u8001\u4eba\u5bb6\u4e00\u9762\uff0c\u4e00\u522b\u6570\u5341\u5e74\uff0c\u4e0d\u77e5\u4ed6\u8001\u4eba\u5bb6\u600e\u6837 \u4e86\u3002\u201d \u3000\u3000\u718a\u501c\u4f4d\u7136\u9053\uff1a\u201c\u5bb6\u5e08\u5df2\u4ed9\u53bb\u4e86\u3002\u201d \u3000\u3000\u5999\u4e00\u771f\u4eba\u53f9\u606f\u9053\uff1a\u201c\u4ee4\u5e08\u4eba\u4e0a\u4e4b\u4eba\uff0c\u6df9\u7559\u4eba\u95f4\u767e\u6570\u5341\u5e74\uff0c\u7ec8\u4e8e\u4ed9\u53bb\u4e86\u3002\u60f3\u6765\u4e16\u4eba\u8425\u8425\u540d \u5229\uff0c\u53c8\u662f\u4e3a\u7740\u4f55\u6765\u5462\uff1f\u201d \u3000\u3000\u718a\u501c\u53ca\u5c1a\u672a\u660e\u4e24\u4eba\uff0c\u5728\u7cbe\u820d\u91cc\u9017\u7559\u4e86\u7ea6\u83ab\u4e00\u4e2a\u65f6\u8fb0\uff0c\u624d\u544a\u8f9e\u51fa\u6765\u3002 \u3000\u3000\u5999\u4e00\u771f\u4eba\u9001\u5230\u95e8\u53e3\uff0c\u7b11\u9053\uff1a\u201c\u4e24\u4f4d\u5c0f\u53cb\uff0c\u4ed6\u65e5\u6709\u6687\uff0c\u4e0d\u59a8\u518d\u4e00\u6664\uff0c\u8d2b\u9053\u548c\u4e24\u4f4d\u867d\u7136\u5306\u5306 \u4e00\u9762\uff0c\u4f46\u5374\u53ef\u770b\u51fa\u4e24\u4f4d\u5fc5\u975e\u6c60\u4e2d\u4eba\u7269\u3002\u201d \u3000\u3000\u4ed6\u4eec\u53c8\u8c26\u8c22\u7740\uff0c\u968f\u7740\u98de\u9e64\u9053\u4eba\u8d70\u51fa\u56ed\u5b50\uff0c\u501f\u5927\u7684\u7384\u771f\u89c2\uff0c\u9759\u4fcf\u4fcf\u5730\u6ca1\u6709\u4e1d\u6beb\u4eba\u58f0\uff0c\u718a\u501c \u6697\u81ea\u611f\u53f9\uff1a\u201c\u4e16\u4e8b\u7684\u786e\u6bcf\u96be\u9884\u6599\uff0c\u4f60\u9884\u6599\u4e2d\u7684\u51f6\u9669\uff0c\u5f80\u5f80\u5374\u662f\u5b89\u8be6\uff0c\u800c\u4f60\u6240\u6ca1\u6709\u9884\u6599\u5f97\u5230 \u7684\uff0c\u5f80\u5f80\u5374\u53c8\u662f\u6781\u5927\u7684\u51f6\u9669\uff0c\u4eba\u7b97\u53c8\u600e\u80fd\u654c\u5f97\u8fc7\u5929\u7b97\uff1f\u201d \u3000\u3000\u98de\u9e64\u9053\u4eba\u4e00\u8def\u76f8\u968f\uff0c\u8d70\u51fa\u7384\u771f\u89c2\uff0c\u718a\u501c\u8111\u6d77\u4e2d\u6df7\u6df7\u6c8c\u6c8c\uff0c\u90fd\u662f\u590f\u82b8\u7684\u5f71\u5b50\uff1a\u201c\u5979\u6b64\u523b\u5728 \u54ea\u91cc\u5462\uff1f\u201d\u4ed6\u5185\u5fc3\u4e0d\u65ad\u60f3\u7740\u3002 \u3000\u3000\u9686\u9686\u7684\u6c34\u58f0\u4f20\u6765\uff0c\u4ed6\u4eec\u53c8\u5feb\u5230\u89e3\u5251\u6cc9\u4e86\uff0c\u98de\u9e64\u9053\u4eba\u7b11\u9053\uff1a\u201c\u89e3\u5251\u6cc9\u4e00\u5230\uff0c\u4fbf\u662f\u8d2b\u9053\u548c\u4e24 \u4f4d\u5206\u624b\u7684\u65f6\u523b\u4e86\uff0c\u4f46\u671b\u4e24\u4f4d\u524d\u9014\u73cd\u91cd\u3002\u201d \u3000\u3000\u8f6c\u8fc7\u4e00\u9053\u5c71\u5f2f\uff0c\u89e3\u5251\u6cc9\u4fbf\u5df2\u5728\u671b\uff0c\u98de\u9e64\u9053\u4eba\u7a81\u7136\u547c\u4e86\u4e00\u58f0\uff0c\u53cc\u811a\u987f\u5904\uff0c\u8eab\u5f62\u63a0\u8d77\u4e09\u4e08\u4f59 \u9ad8\uff0c\u55d6\u5730\u671d\u89e3\u5251\u6cc9\u6c60\u65c1\u7684\u5de8\u77f3\u5954\u53bb\u3002 \u3000\u3000\u718a\u501c\u4e5f\u662f\u4e00\u60ca\uff0c\u4ed6\u770b\u5230\u5148\u524d\u5b88\u7740\u81ea\u5df1\u90a3\u67c4\u5251\u7684\u4e24\u4e2a\u5e74\u8f7b\u9053\u4eba\uff0c\u90fd\u5367\u5012\u5728\u5730\uff0c\u6765\u4e0d\u53ca\u62db\u547c \u5c1a\u672a\u660e\uff0c\u4e5f\u63a0\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u679c\u7136\uff0c\u90a3\u4e8c\u4e2a\u5e74\u8f7b\u9053\u4eba\u50cf\u662f\u88ab\u4eba\u70b9\u4e86\u7a74\u9053\uff0c\u6655\u8ff7\u7740\u5012\u5728\u5730\u4e0a\u3002 \u3000\u3000\u98de\u9e64\u9053\u4eba\u7565\u4e00\u67e5\u770b\uff0c\u4fbf\u77e5\u9053\u8fd9\u4e8c\u4eba\u6b64\u523b\u6240\u70b9\u7684\uff0c\u4e00\u662f\u80cc\u5fc3\u7684\u201c\u9633\u5173\u201d\u7a74\uff0c\u4e00\u662f\u8111\u540e\u7684 \u201c\u7389\u6795\u201d\u7a74\uff0c\u9042\u4f38\u624b\u4e00\u62cd\u4e00\u634f\u3002 \u3000\u3000\u54ea\u77e5\u9053\u90a3\u5e74\u8f7b\u9053\u4eba\u52a8\u4e5f\u4e0d\u52a8\uff0c\u98de\u9e64\u5927\u60ca\uff0c\u201c\u600e\u5730\u8fde\u6211\u89e3\u7a74\u624b\u6cd5\u90fd\u4e0d\u80fd\u89e3\u5f00\u6b64\u4eba\u6240\u70b9\u7684\u7a74 \u9053\uff0c\u4f46\u662f\u6b66\u6797\u5404\u95e8\u5404\u6d3e\u4e2d\uff0c\u6211\u5c1a\u672a\u542c\u6ca1\u6709\u6211\u4e0d\u80fd\u89e3\u7684\u7a74\u9053\uff0c\u6b64\u4eba\u6562\u5230\u6b66\u5f53\u5c71\u4e0a\u6492\u91ce\uff0c\u53c8\u662f \u8c01\uff1f\u201d \u3000\u3000\u718a\u501c\u63a0\u5230\u8eab\u540e\uff0c\u770b\u5230\u81ea\u5df1\u7684\u5b9d\u5251\u8fde\u5f71\u5b50\u90fd\u6ca1\u6709\u4e86\uff0c\u518d\u8bd5\u7740\u53bb\u89e3\u90a3\u4e24\u4e2a\u9053\u4eba\u7684\u7a74\u9053\uff0c\u54ea\u77e5 \u9053\u8fd9\u70b9\u7a74\u4e4b\u4eba\u6240\u7528\u7684\u624b\u6cd5\uff0c\u7adf\u4e0d\u662f\u5929\u4e0b\u6b66\u6797\u4e2d\u4efb\u4f55\u4e00\u4e2a\u5b97\u6d3e\u6240\u6709\u3002 \u3000\u3000\u7a7a\u5c71\u5bc2\u5bc2\uff0c\u6c34\u58f0\u6dd9\u6dd9\uff0c\u9664\u4e86\u8fd9\u4e24\u4e2a\u5e74\u8f7b\u9053\u4eba\u4e4b\u5916\uff0c\u8c01\u4e5f\u65e0\u6cd5\u8bf4\u51fa\u8fd9\u4e8b\u7684\u771f\u76f8\uff0c\u4f46\u662f\u8fd9\u4e24 \u4e2a\u5e74\u8f7b\u9053\u4eba\u7a74\u9053\u88ab\u70b9\uff0c\u53e3\u4e0d\u80fd\u8a00\uff0c\u624b\u4e0d\u80fd\u52a8\uff0c\u5df2\u7ecf\u5f62\u5982\u5e9f\u4eba\uff0c\u53c8\u600e\u80fd\u81ea\u4ed6\u4eec\u53e3\u4e2d\u95f4\u5f97\u771f\u76f8\u3002 \u3000\u3000\u98de\u9e64\u5b50\u89c1\u5230\u81ea\u5df1\u66fe\u7ecf\u5938\u53e3\u66ff\u4eba\u5bb6\u4fdd\u5b58\u7684\u5251\uff0c\u73b0\u5728\u65e0\u5f71\u65e0\u8e2a\uff0c\u81ea\u5df1\u7684\u4e24\u4e2a\u5e08\u4f84\uff0c\u4e5f\u88ab\u5236 \u4f4f\u3002 \u3000\u3000\u6700\u96be\u582a\u7684\u662f\u70b9\u4f4f\u8fd9\u4e24\u4e2a\u5e08\u4f84\u7684\u70b9\u7a74\u624b\u6cd5\uff0c\u7adf\u4e0d\u662f\u81ea\u5df1\u80fd\u5f97\u89e3\u5f00\u7684\u3002 \u3000\u3000\u718a\u501c\u6b64\u523b\u7684\u5fc3\u5883\uff0c\u66f4\u662f\u61ca\u607c\u4e07\u5206\uff0c\u4ed6\u5927\u610f\u4e4b\u4e0b\uff0c\u5931\u53bb\u4e86\u201c\u501a\u5929\u5251\u201d\uff0c\u90a3\u662f\u5b8c\u5168\u548e\u5728\u81ea \u5df1\uff0c\u73b0\u5728\u201c\u8d2f\u65e5\u5251\u201d\u7684\u5931\u53bb\uff0c\u5374\u662f\u4ed6\u81ea\u5df1\u6ca1\u6709\u534a\u70b9\u8d23\u4efb\u7684\u3002 \u3000\u3000\u98de\u9e64\u5b50\u5411\u718a\u501c\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u8d2b\u9053\u5b9e\u5728\u6ca1\u6709\u60f3\u5230\uff0c\u4f1a\u6709\u8fd9\u79cd\u4e8b\u53d1\u751f\u5728\u6b66\u5f53\u5c71\u4e0a\uff0c\u770b\u6765\u6c5f\u6e56 \u4e0a\u672a\u5c06\u6b66\u5f53\u6d3e\u770b\u5728\u773c\u91cc\u7684\uff0c\u5927\u6709\u4eba\u5728\uff0c\u8d2b\u9053\u9664\u4e86\u5bf9\u9601\u4e0b\u6df1\u81f4\u6b49\u610f\u5916\uff0c\u522b\u65e0\u8bdd\u8bf4\u3002\u201d \u3000\u3000\u718a\u501c\u6697\u54fc\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u4f60\u6df1\u81f4\u6b49\u610f\uff0c\u53c8\u6709\u4f55\u7528\u3002\u201d\u51b7\u51b7\u5730\u671b\u7740\u4ed6\uff0c\u4e5f\u4e0d\u8bf4\u8bdd\u3002 \u3000\u3000\u98de\u9e64\u5b50\u76ee\u5149\u56db\u8f6c\uff0c\u718a\u501c\u5fc3\u4e2d\u7684\u4e0d\u6ee1\uff0c\u4ed6\u5df2\u7ecf\u89c9\u5bdf\u5230\u4e86\u3002 \u3000\u3000\u8fd9\u79cd\u65e0\u8a00\u7684\u4e0d\u6ee1\uff0c\u751a\u81f3\u8fd8\u5176\u4e2d\u5e26\u7740\u4e9b\u8f7b\u8511\uff0c\u98de\u9e64\u5b50\u4e0d\u7981\u4e5f\u5fae\u5fae\u4f5c\u8272\uff0c\u9053\uff1a\u201c\u7b49\u5230\u6211\u8fd9\u4e24 \u4e2a\u4e0d\u6210\u6750\u7684\u5e08\u4f84\u7684\u8840\u8109\u6d3b\u8f6c\u7684\u65f6\u5019\uff0c\u8d2b\u9053\u53ea\u8981\u4e00\u77e5\u9053\u593a\u5251\u4eba\u7684\u6765\u5386\u53bb\u8def\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u4e5f\u4f1a\u5c06 \u9601\u4e0b\u7684\u5251\u53d6\u56de\u3002\u201d\u4ed6\u8bed\u58f0\u4e5f\u53d8\u5f97\u6709\u4e9b\u4e0d\u5ba2\u6c14\u4e86\uff0c\u201c\u4e09\u4e2a\u6708\u4e4b\u5185\uff0c\u8d2b\u9053\u82e5\u4e0d\u80fd\u593a\u56de\u6b64\u5251\uff0c\u90a3 \u4e48\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bdd\u58f0\u5c1a\u672a\u8bf4\u5b8c\uff0c\u7a81\u5730\u4f20\u6765\u51e0\u58f0\u6781\u6e05\u6717\u7684\u9523\u58f0\uff0c\u5728\u6df1\u5c71\u4e4b\u4e2d\uff0c\u58f0\u97f3\u4f20\u51fa\u8001\u8fdc\u3002 \u3000\u3000\u8fd9\u9523\u58f0\u5bf9\u718a\u501c\u6765\u8bf4\uff0c\u5e76\u4e0d\u662f\u751f\u758f\u7684\uff0c\u4ed6\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6697\u5fd6\u9053\uff1a\u201c\u96be\u9053\u8fd9\u8d2f\u65e5\u5251\u4e5f\u843d\u5230\u4ed6\u7684 \u624b\u4e0a\uff1f\u201d\u8f6c\u5ff5\u53c8\u5fd6\u9053\uff1a\u201c\u4ed6\u8fe2\u8fe2\u5343\u91cc\uff0c\u8dd1\u5230\u6b66\u5f53\u5c71\u6765\uff0c\u53c8\u662f\u4e3a\u4ec0\u4e48\uff0c\u96be\u9053\u4ed6\u771f\u662f\u4e95\u541e\u5404\u6d3e\uff0c \u72ec\u5c0a\u6b66\u6797\u5417\uff1f\u201d \u3000\u3000\u98de\u9e64\u5b50\u867d\u7136\u88ab\u9523\u58f0\u6253\u65ad\u4e86\u6b63\u5728\u8bf4\u7684\u8bdd\uff0c\u53ef\u662f\u4ed6\u5e76\u4e0d\u77e5\u9053\u8fd9\u9523\u58f0\u7684\u6765\u5386\uff0c\u671b\u5230\u718a\u501c\u8138\u4e0a\u60ca \u7591\u4e4b\u8272\uff0c\u6697\u5fd6\uff1a\u201c\u8fd9\u9523\u58f0\u53c8\u6709\u4ec0\u4e48\u53e4\u602a\uff1f\u201d\u9042\u4e5f\u4e0d\u7981\u8f6c\u8fc7\u5934\u53bb\uff0c\u671b\u7740\u8fd9\u9523\u58f0\u4f20\u6765\u7684\u65b9\u5411\u3002 \u3000\u3000\u5c1a\u672a\u660e\u867d\u7136\u4ee5\u524d\u5e76\u6ca1\u6709\u4eb2\u8033\u542c\u89c1\u8fc7\u8fd9\u5947\u5f02\u7684\u9523\u58f0\uff0c\u4f46\u662f\u4ed6\u6c5f\u6e56\u9605\u5386\u8f83\u4e30\uff0c\u773c\u76ae\u53c8\u6742\uff0c\u4eff \u4f5b\u5fc6\u8d77\u8fd9\u9523\u58f0\u7684\u6765\u5386\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u8f6c\u8138\u5411\u718a\u501c\u6084\u6084\u4ed6\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u8fd9\u662f\u4e0d\u662f\u5929\u9634\u6559\uff1f\u201d \u3000\u3000\u718a\u501c\u4e00\u6446\u624b\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u76ee\u5149\u7728\u4e5f\u4e0d\u7728\u5730\u671b\u7740\u90a3\u6761\u5411\u5c71\u4e0b\u5a49\u8712\u800c\u53bb\u7684\u5c71\u8def\u3002\u201c\u9523\u58f0\u54cd \u8fc7\uff0c\u4ed6\u4e5f\u8be5\u51fa\u73b0\u4e86\u5427\uff01\u201d\u4ed6\u5728\u8b66\u6212\u7740\u3002 \u3000\u3000\u98de\u9e64\u5b50\u5374\u63a5\u7740\u5c1a\u672a\u660e\u7684\u8bdd\u95ee\u9053\uff1a\u201c\u5929\u9634\u6559\uff1f\u201d \u3000\u3000\u4f46\u662f\u4ed6\u4e5f\u89c9\u5bdf\u5230\u4e8b\u60c5\u7684\u6eaa\u8df7\uff0c\u63a2\u624b\u5165\u6000\uff0c\u53d6\u51fa\u4e00\u7c92\u77f3\u5b50\uff0c\u4e00\u626c\u624b\uff0c\u5411\u6c60\u7554\u7684\u4e00\u682a\u6811\u4e0a\u6253 \u51fa\u3002 \u3000\u3000\u77f3\u5b50\u51fb\u4e2d\u6811\u53f6\u6216\u6811\u76ae\uff0c\u5e94\u8be5\u53d1\u51fa\u201c\u5427\u201d\u7684\u4e00\u58f0\u3002 \u3000\u3000\u54ea\u77e5\u77f3\u5b50\u98de\u5230\u6811\u4e0a\u540e\uff0c\u7adf\u7136\u201c\u5f53\u201d\u5730\u53d1\u51fa\u4e00\u58f0\u5de8\u54cd\uff0c\u58f0\u97f3\u6e05\u8d8a\u60a0\u957f\uff0c\u6bd4\u9523\u58f0\u4f20\u5f97\u8fdc\u3002 \u3000\u3000\u718a\u501c\u53ca\u5c1a\u672a\u660e\uff0c\u60ca\u5f02\u5730\u671d\u90a3\u68f5\u6811\u4e0a\u671b\u53bb\uff0c\u968f\u5373\u4e86\u7136\u3002 \u3000\u3000\u539f\u6765\u90a3\u682a\u6811\u7684\u6860\u679d\u4e4b\u95f4\uff0c\u6302\u7740\u4e00\u4e2a\u94dc\u949f\uff0c\u77f3\u5b50\u51fb\u5728\u949f\u4e0a\uff0c\u81ea\u7136\u4f1a\u53d1\u51fa\u90a3\u79cd\u8d8a\u800c\u60a0\u957f\u7684\u58f0 \u97f3\u3002 \u3000\u3000\u201c\u60f3\u6765\u8fd9\u5c31\u662f\u6b66\u5f53\u5c71\u7684\u4f20\u8b66\u4e4b\u6cd5\u4e86\u3002\u201d \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u58f0\u949f\u54cd\u4e4b\u540e\uff0c\u5c71\u8def\u4e0a\u53c8\u4f20\u6765\u4e09\u58f0\u9523\u54cd\uff0c\u58f0\u97f3\u6bd4\u8d77\u4e0a\u4e00\u6b21\u66f4\u663e\u5f97\u6e05\u660e\uff0c\u60f3\u662f\u53d1\u58f0 \u4e4b\u5904\u6bd4\u8f83\u4e0a\u6b21\u8fd1\u4e86\u4e9b\u3002 \u3000\u3000\u718a\u501c\u76b1\u7709\u9053\uff1a\u201c\u679c\u7136\u6765\u4e86\uff0c\u6050\u6015\u593a\u5251\u4e4b\u4eba\uff0c\u5c31\u662f\u6b64\u4eba\u3002\u201d \u3000\u3000\u98de\u9e64\u5b50\u9053\uff1a\u201c\u8c01\uff1f\u201d \u3000\u3000\u718a\u501c\u5251\u7709\u4e00\u8f69\uff0c\u671d\u5c71\u9053\u4e00\u6307\uff0c\u98de\u9e64\u5b50\u51dd\u795e\u671b\u53bb\uff0c\u5c71\u9053\u4e0a\u7f13\u7f13\u8d70\u51fa\u4eba\u6765\u3002 \u3000\u3000\u90a3\u662f\u56db\u4e2a\u7a7f\u7740\u9ed1\u8272\u957f\u886b\u7684\u4e2d\u5e74\u6c49\u5b50\uff0c\u6b65\u5c65\u77eb\u5065\uff0c\u76ee\u5149\u5982\u9e70\uff0c\u663e\u89c1\u6b66\u529f\u90fd\u5df2\u5f88\u6df1\u7684\u6839\u57fa\u3002 \u3000\u3000\u518d\u671d\u540e\u671b\u53bb\uff0c\u662f\u56db\u4e2a\u767d\u7f57\u8863\u88d9\u7684\u4e2d\u5e74\u7f8e\u5987\u3002 \u3000\u3000\u8fd9\u516b\u4e2a\u4eba\u4ff1\u90fd\u7b11\u5bb9\u4ece\u5bb9\uff0c\u50cf\u662f\u6e38\u5c71\u73a9\u666f\u800c\u6765\uff0c\u98de\u9e64\u5b50\u5fc3\u4e2d\u5927\u7591\uff1a\u201c\u8fd9\u4e9b\u4eba\u662f\u4f55\u6765\u8def\uff1f\u201d \u3000\u3000\u718a\u501c\u4e00\u773c\u671b\u53bb\uff0c\u89c1\u524d\u9762\u90a3\u56db\u4e2a\u9ed1\u8863\u6c49\u5b50\u5185\uff0c\u7adf\u6709\u5434\u94a9\u5251\u9f9a\u5929\u6770\uff0c\u65b9\u81ea\u4e00\u76b1\u7709\uff0c\u773c\u5149\u52a8 \u5904\uff0c\u770b\u5230\u4e00\u4eba\u5411\u81ea\u5df1\u70b9\u5934\u5fae\u7b11\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u5b9a\u6674\u4e00\u770b\uff0c\u8138\u4e0a\u7684\u989c\u8272\u53d8\u5f97\u66f4\u5389\u5bb3\u4e86\u3002 \u3000\u3000\u539f\u6765\u90a3\u5411\u4ed6\u70b9\u5934\u5fae\u7b11\u7684\u4eba\uff0c\u7adf\u662f\u7c89\u9762\u82cf\u79e6\u738b\u667a\u9011\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u4e5f\u8fdc\u8fdc\u4e00\u62b1\u62f3\u3002 \u3000\u3000\u98de\u9e64\u5b50\u7591\u4e91\u66f4\u91cd\uff1a\u201c\u539f\u6765\u4ed6\u4eec\u7adf\u662f\u8ba4\u5f97\u7684\uff0c\u4f46\u662f\u4ed6\u4e3a\u4f55\u53c8\u8bf4\u593a\u5251\u7684\u5c31\u662f\u8fd9\u4e9b\u4eba\u5462\uff1f\u201d \u3000\u3000\u6b64\u4e2d\u7684\u771f\u76f8\uff0c\u4ed6\u4e1d\u6beb\u4e0d\u660e\u4e86\uff0c\u5c31\u662f\u94c1\u80c6\u5c1a\u672a\u660e\uff0c\u53c8\u4f55\u5c1d\u4e0d\u5728\u5947\u602a\u3002 \u3000\u3000\u8fd9\u7537\u5973\u516b\u4e2a\u4eba\u4e00\u8d70\u51fa\u6765\uff0c\u5c31\u50cf\u662f\u6f2b\u4e0d\u7ecf\u5fc3\uff0c\u5206\u6563\u5728\u56db\u5468\u3002 \u3000\u3000\u63a5\u7740\uff0c\u5c71\u8def\u4e0a\u5927\u8e0f\u6b65\u6765\u4e00\u4e2a\u9ed1\u886b\u8001\u4eba\uff0c\u5c1a\u672a\u660e\u9a87\u7136\u5fd6\u9053\uff1a\u201c\u6b64\u4eba\u7684\u529f\u529b\u597d\u6df1\u3002\u201d \u3000\u3000\u539f\u6765\u90a3\u8001\u8005\u6bcf\u4e00\u4e3e\u6b65\uff0c\u5c71\u8def\u4e0a\u7adf\u7136\u7559\u4e0b\u4e86\u4e00\u4e2a\u5f88\u6df1\u7684\u811a\u5370\u3002 \u3000\u3000\u718a\u501c\u5fae\u4e00\u601d\u5fc6\uff0c\u4e5f\u81ea\u60f3\u8d77\uff0c\u6b64\u4eba\u5c31\u662f\u90a3\u65e5\u5728\u6cf0\u5c71\u7edd\u9876\u4e0a\uff0c\u4ee5\u6781\u5feb\u7684\u624b\u6cd5\uff0c\u70b9\u4e2d\u751f\u6b7b\u5224\u6c64 \u5b5d\u5b8f\u7b49\u4eba\u7a74\u9053\u7684\u9ed1\u715e\u9b54\u638c\u5c1a\u6587\u658c\u3002 \u3000\u3000\u4ed6\u5fc3\u91cc\u4e5f\u4e0d\u514d\u6709\u4e9b\u6026\u7136\u4e0d\u5b9a\uff0c\u65b9\u81ea\u8f6c\u7740\u8be5\u600e\u6837\u5e94\u4ed8\u7684\u5ff5\u5934\u3002 \u3000\u3000\u7a81\u5730\u773c\u524d\u4eff\u4f5b\u4e00\u4eae\uff0c\u5c71\u8def\u4e0a\u8f6c\u51fa\u4e00\u53cc\u7edd\u7f8e\u7684\u5c11\u5e74\u7537\u5973\uff0c\u4ed6\u4f9d\u7a00\u89c9\u5f97\u5f88\u9762\u719f\uff0c\u518d\u4e00\u7ec6\u60f3\uff0c \u76ee\u5c04\u5947\u5149\uff0c\u604d\u7136\u609f\u9053\uff1a\u201c\u539f\u6765\u662f\u4ed6\u4e24\u4eba\u3002\u201d \u3000\u3000\u98de\u9e64\u5b50\u53ca\u5c1a\u672a\u660e\uff0c\u4e5f\u88ab\u4e00\u53cc\u7537\u5973\u5438\u5f15\u4f4f\u4e86\u76ee\u5149\uff0c\u65b9\u81ea\u6697\u91cc\u79f0\u8d5e\u7740\u8fd9\u4e00\u53cc\u5c11\u5e74\u7537\u5973\u7684\u98ce \u59ff\uff0c\u5c71\u8def\u4e0a\u53c8\u8f6c\u51fa\u4e24\u9876\u5c71\u8f7f\u6765\u3002 \u3000\u3000\u8fd9\u4e24\u9876\u5c71\u8f7f\uff0c\u5f62\u72b6\u548c\u666e\u901a\u7684\u722c\u5c71\u864e\u5dee\u4e0d\u591a\uff0c\u4f46\u662f\u62ac\u8f7f\u5b50\u7684\u4eba\uff0c\u5374\u548c\u666e\u901a\u7684\u5927\u4e0d\u76f8\u540c\uff0c\u539f \u6765\u8fd9\u62ac\u8f7f\u7684\u8f7f\u592b\uff0c\u7adf\u662f\u4e24\u7537\u4e24\u5973\u3002 \u3000\u3000\u518d\u5f80\u8f7f\u4e0a\u4e00\u770b\uff0c\u718a\u501c\u4e0d\u7981\u66f4\u662f\u53d8\u8272\u3002 \u3000\u3000\u5c1a\u672a\u660e\u4e00\u62c9\u718a\u501c\u7684\u8863\u895f\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u679c\u7136\u5c31\u662f\u8fd9\u4e2a\u5c0f\u5b50\u593a\u7684\u5251\u3002\u201d \u3000\u3000\u6d41\u6c34\u4f9d\u7136\uff0c\u7fa4\u5c71\u4ecd\u65e7\uff0c\u5c71\u6c34\u5e76\u672a\u56e0\u8fd9\u4e9b\u4eba\u7684\u5230\u6765\u800c\u6709\u4e1d\u6beb\u6539\u53d8\uff0c\u4f9d\u7136\u662f\u9759\u5bc2\u7684\u3002 \u3000\u3000\u4f46\u662f\u718a\u501c\u3001\u5c1a\u672a\u660e\uff0c\u4ee5\u53ca\u98de\u9e64\u5b50\u6b64\u523b\u7684\u5fc3\u5883\uff0c\u5374\u5728\u6781\u5f3a\u70c8\u5730\u6fc0\u8361\u7740\u3002 \u3000\u3000\u867d\u7136\u6bcf\u4e2a\u4eba\u7684\u5fc3\u4e2d\u6240\u60f3\u7684\u5e76\u4e0d\u76f8\u540c\u3002 \u3000\u3000\u201c\u8fd9\u4e24\u4e2a\u7537\u5973\u662f\u8c01\uff0c\u770b\u6765\u6c14\u6d3e\u8fd9\u4e48\u5927\uff0c\u8fd9\u7537\u7684\u624b\u91cc\u62ff\u7740\u7684\u5251\uff0c\u5149\u8292\u707f\u7136\uff0c\u50cf\u662f\u67c4\u5b9d\u5251\uff0c \u4e0d\u77e5\u9053\u662f\u5426\u5c31\u662f\u718a\u501c\u90a3\u67c4\uff0c\u6b64\u4ece\u7adf\u6562\u5728\u6b66\u5f53\u5c71\u89e3\u5251\u6c60\u7554\u593a\u5251\uff0c\u800c\u53c8\u4ece\u5bb9\u5730\u8d70\u56de\u6765\uff0c\u6b66\u529f\u5fc5\u5b9a \u4e0d\u5f31\uff0c\u6c5f\u6e56\u4e2d\u53c8\u6709\u8c01\u6562\u8fd9\u4e48\u85d0\u89c6\u6211\u6b66\u5f53\u6d3e\u5462\uff1f\u201d \u3000\u3000\u98de\u9e64\u5b50\u867d\u7136\u672a\u542c\u5230\u8fc7\u5929\u9634\u6559\u7684\u540d\u5934\uff0c\u4f46\u662f\u4ed6\u4ecd\u7136\u5e76\u672a\u5728\u610f\uff0c\u4ed6\u4e45\u5c45\u6df1\u5c71\uff0c\u5bf9\u6b66\u6797\u4e2d\u7684\u4e8b \u77e5\u9053\u7684\u5e76\u4e0d\u591a\uff0c\u662f\u4ee5\u5c31\u7b97\u662f\u89c1\u4e86\u8fd9\u4e48\u7684\u9635\u4ed7\uff0c\u4e5f\u6ca1\u6709\u60f3\u5230\u8fd9\u5c71\u8f7f\u4e0a\u5750\u7740\u7684\u4e00\u53cc\u7537\u5973\uff0c\u5c31\u662f\u4f7f \u6b66\u6797\u4eba\u95fb\u800c\u8272\u53d8\uff0c\u5c71\u897f\u592a\u884c\u5c71\u5929\u9634\u6559\u7684\u6559\u4e3b\uff0c\u6218\u74a7\u541b\u3001\u7126\u5f02\u884c\u592b\u5987\u3002 \u3000\u3000\u201c\u8fd9\u5c71\u8f7f\u4e0a\u5750\u7740\u7684\uff0c\u60f3\u5fc5\u5c31\u662f\u5929\u9634\u6559\u4e3b\u592b\u5987\u4e86\uff0c\u82e5\u975e\u6211\u4eb2\u89c1\uff0c\u6211\u771f\u96be\u76f8\u4fe1\u5929\u9634\u6559\u4e3b\u7adf\u662f \u4e2a\u8fd9\u4e48\u5e74\u8f7b\u7684\u4e66\u751f\u3002\u201d \u3000\u3000\u5018\u672a\u660e\u867d\u7136\u5df2\u7ecf\u731c\u5230\u8fd9\u5c31\u662f\u5929\u9634\u6559\u4e3b\u592b\u5987\uff0c\u53ef\u662f\u5fc3\u4e2d\u4ecd\u7136\u6709\u4e00\u4efd\u6000\u7591\u3002 \u3000\u3000\u8fd9\u6000\u7591\u662f\u5408\u7406\u7684\uff0c\u82e5\u662f\u4f60\u53d1\u89c9\u4e00\u4e2a\u4ee4\u6b66\u6797\u4e2d\u90a3\u4e48\u591a\u5728\u5200\u53e3\u62b5\u996d\u5403\u7684\u670b\u53cb\u4e00\u542c\u4e86\u5c31\u5934\u76ae\u53d1 \u80c0\u7684\u89d2\u8272\uff0c\u7ade\u662f\u4e00\u4e2a\u8fd9\u4e48\u7684\u4eba\u7269\u7684\u65f6\u5019\uff0c\u4f60\u4e5f\u4f1a\u6709\u548c\u4ed6\u4e00\u6837\u7684\u611f\u89c9\uff0c\u8ba4\u4e3a\u8fd9\u51e0\u4e4e\u6709\u4e9b\u4e0d\u53ef \u80fd\u3002 \u3000\u3000\u53ea\u6709\u718a\u501c\u7684\u60f3\u6cd5\u662f\u80af\u5b9a\u7684\uff1a\u201c\u8fd9\u5929\u9634\u6559\u4e3b\u592b\u5987\uff0c\u51e0\u5e74\u6765\u975e\u4f46\u6ca1\u6709\u663e\u5f97\u8001\uff0c\u4ed6\u4eec\u597d\u50cf\u8fd8\u5e74 \u8f7b\u4e86\u4e9b\uff0c\u770b\u6765\u4ed6\u4eec\u7684\u5185\u529f\u9020\u8be3\u7684\u786e\u5f88\u6df1\u3002\u201d\u770b\u5230\u7126\u5f02\u884c\u624b\u4e2d\u629a\u7740\u7684\u957f\u5251\uff0c\u8138\u8272\u9634\u6c89\u5982\u94c1\u3002 \u3000\u3000\u6218\u74a7\u541b\u9762\u5982\u94f6\u4e39\uff0c\u660e\u7738\u5584\u776c\uff0c\u4f9d\u65e7\u8c8c\u7f8e\u5982\u82b1\uff0c\u4e5f\u4f9d\u65e7\u662f\u672a\u8bed\u5148\u7b11\uff0c\u5e26\u7740\u4e00\u8fde\u4e32\u94f6\u94c3\u822c\u7684 \u7b11\u58f0\u9053\uff1a\u201c\u5582\uff0c\u4f60\u770b\u4eba\u5bb6\u6b66\u5f53\u5c71\u98ce\u666f\u591a\u597d\uff0c\u4e0d\u50cf\u54b1\u4eec\u5c71\u4e0a\uff0c\u4e0d\u662f\u5149\u79c3\u79c3\u5730\u6ca1\u6709\u6811\uff0c\u5c31\u662f\u751f\u4e9b \u96be\u770b\u6b7b\u4e86\u7684\u5c0f\u6811\u3002\u201d \u3000\u3000\u7126\u5f02\u884c\u8f7b\u8f7b\u5730\u6478\u7740\u624b\u4e2d\u7684\u5251\uff0c\u50cf\u662f\u5bf9\u8fd9\u67c4\u5251\u559c\u6b22\u5df2\u6781\uff0c\u542c\u4e86\u6218\u74a7\u541b\u7684\u8bdd\uff0c\u6717\u7136\u4e00\u58f0\u957f \u7b11\u3002 \u3000\u3000\u8fd9\u7b11\u58f0\u8d85\u8d8a\u4e86\u677e\u6d9b\u58f0\u3001\u866b\u9e1f\u58f0\u3001\u6d41\u6c34\u58f0\uff0c\u5728\u56db\u91ce\u98d8\u8361\u7740\u3002 \u3000\u3000\u5c71\u8f7f\u505c\u4e0b\uff0c\u4ed6\u8de8\u4e0b\u8f7f\u5b50\u6765\uff0c\u884c\u52a8\u548c\u4efb\u4f55\u4e00\u4e2a\u666e\u901a\u4eba\u6beb\u65e0\u4e8c\u81f4\u3002 \u3000\u3000\u4ed6\u4f38\u624b\u4e00\u633d\uff0c\u6218\u58c1\u541b\u6276\u7740\u4ed6\u7684\u624b\uff0c\u8885\u8885\u5a77\u5a77\u8d70\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u718a\u501c\u671b\u7740\u4ed6\u4eec\u6c14\u6001\u4e4b\u4ece\u5bb9\uff0c\u800c\u516c\u7136\u5c06\u81ea\u5df1\u7684\u5251\u62ff\u5728\u624b\u4e0a\uff0c\u4e00\u65f6\u5012\u771f\u4e0d\u77e5\u9053\u8be5\u600e\u4e48\u5e94\u4ed8\uff0c \u600e\u4e48\u542f\u53e3\u3002 \u3000\u3000\u7126\u5f02\u884c\u8c28\u614e\u5730\u5c06\u5251\u63d2\u5165\u9798\u91cc\uff0c\u4ed6\u7684\u76ee\u5149\u4e00\u6a2a\uff0c\u6070\u5de7\u548c\u718a\u501c\u7684\u76ee\u5149\u76f8\u5bf9\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u5e76\u6ca1\u6709\u9732\u51fa\u4efb\u4f55\u8868\u60c5\u6765\uff0c\u5fae\u5fae\u62db\u4e86\u62db\u624b\uff0c\u90a3\u4e24\u4e2a\u7edd\u7f8e\u7684\u5c11\u5e74\u7537\u5973\u4fbf\u8d70\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u5634\u76ae\u52a8\u4e86\u52a8\uff0c\u58f0\u97f3\u4f4e\u5f97\u53ea\u6709\u5bf9\u9762\u7684\u4eba\u624d\u542c\u5f97\u89c1\uff0c\u7136\u540e\u4f38\u624b\u4eba\u6000\uff0c\u638f\u51fa\u4e00\u5f20\u70eb\u91d1\u540d\u8d34\uff0c \u4ea4\u7ed9\u90a3\u4e00\u53cc\u7edd\u7f8e\u7684\u5c11\u5e74\u7537\u5973\u3002 \u3000\u3000\u718a\u501c\u89c1\u4ed6\u8fd9\u4e00\u756a\u505a\u4f5c\uff0c\u5012\u771f\u4e0d\u77e5\u9053\u4ed6\u846b\u82a6\u91cc\u5356\u7684\u4ec0\u4e48\u836f\uff0c\u6697\u6697\u5bfb\u601d\uff1a\u201c\u4ed6\u5df4\u5df4\u5730\u8dd1\u5230\u6b66 \u5f53\u6765\uff0c\u96be\u9053\u53ea\u662f\u4e3a\u4e86\u6295\u8d34\u62dc\u8bbf\u5417\uff1f\u201d \u3000\u3000\u8fd9\u65f6\u90a3\u4e00\u53cc\u7edd\u7f8e\u7684\u5c11\u5e74\u7537\u5973\u5df2\u8d70\u4e86\u8fc7\u6765\uff0c\u5728\u7ecf\u8fc7\u718a\u501c\u8eab\u524d\u7684\u65f6\u5019\uff0c\u90a3\u4fca\u7f8e\u7684\u5c11\u5e74\u7adf\u7136\u671d \u718a\u501c\u5fae\u5fae\u4e00\u7b11\uff0c\u4f4e\u58f0\u8bf4\u4e86\u53e5\uff1a\u201c\u522b\u6765\u65e0\u6059\u3002\u201d\u718a\u501c\u4e00\u6115\uff0c\u90a3\u5c11\u5e74\u5df2\u81ea\u64e6\u8fc7\u8eab\u4fa7\uff0c\u8d70\u5411\u540e\u9762\u7684 \u98de\u9e64\u5b50\u3002 \u3000\u3000\u90a3\u4fca\u7f8e\u7684\u5c11\u5e74\u671b\u7740\u90a3\u5c11\u5973\u76f8\u89c6\u4e00\u7b11\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u5c71\u897f\u5929\u9634\u6559\u53f8\u793c\u6cd5\u575b\u62a4\u6cd5\u9ed1\u8863\u6469\u52d2\u767d \u666f\u7965\uff0c\u767d\u8863\u9f99\u5973\u53f6\u6e05\u6e05\uff0c\u5949\u6559\u4e3b\u4e4b\u547d\uff0c\u6295\u8d34\u62dc\u5c71\u3002\u201d\u8bf4\u7740\u4ed6\u5c06\u90a3\u70eb\u91d1\u4e2d\u5e16\u9ad8\u4e3e\u8fc7\u9876\uff0c\u4ea4\u5411\u98de \u9e64\u5b50\u3002 \u3000\u3000\u9ed1\u8863\u6469\u52d2\u53c8\u9053\uff1a\u201c\u5c31\u70e6\u9053\u957f\u901a\u62a5\u8d35\u6d3e\u638c\u6559\uff0c\u5c31\u8bf4\u5929\u9634\u6559\u4e3b\u6709\u4e8b\u6c42\u89c1\u3002\u201d\u90a3\u81ea\u8863\u9f99\u5973\u63a5\u53e3 \u7b11\u9053\uff1a\u201c\u8fd8\u671b\u8d35\u6d3e\u638c\u6559\u771f\u4eba\uff0c\u62e8\u5197\u4e00\u89c1\u3002\u201d \u3000\u3000\u98de\u9e64\u5b50\u6574\u5bb9\u9053\uff1a\u201c\u8d35\u5ba2\u8fdc\u6765\uff0c\u8bf7\u5728\u6b64\u7a0d\u5019\uff0c\u8d2b\u9053\u5c31\u53bb\u901a\u62a5\u638c\u6559\u5e08\u5c0a\u3002\u201d \u3000\u3000\u5c1a\u672a\u660e\u51b7\u7136\u9053\uff1a\u201c\u8fd9\u4f4d\u6562\u60c5\u5c31\u662f\u540d\u4f20\u56db\u6d77\u7684\u5929\u9634\u6559\u7126\u6559\u4e3b\u4e86\u3002\u201d \u3000\u3000\u7126\u5f02\u884c\u9053\uff1a\u201c\u4e0d\u6562\u3002\u201d \u3000\u3000\u201c\u5144\u5f1f\u4e45\u95fb\u7126\u6559\u4e3b\u7684\u5927\u540d\uff0c\u771f\u53ef\u8bf4\u5f97\u4e0a\u5982\u96f7\u704c\u8033\u3002\u201d\u5c1a\u672a\u660e\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u4eca\u65e5\u4e00 \u89c1\uff0c\u54c8\u54c8\uff0c\u5374\u4e5f\u4e0d\u8fc7\u5982\u6b64\u3002\u201d \u3000\u3000\u4ed6\u6b64\u8bdd\u4e00\u51fa\uff0c\u5728\u573a\u4f17\u4eba\u83ab\u4e0d\u5927\u5403\u4e00\u60ca\uff0c\u987b\u77e5\u5929\u9634\u6559\u5728\u4eca\u65e5\u6b66\u6797\u4e2d\uff0c\u771f\u53ef\u4ee5\u8bf4\u5f97\u4e0a\u662f\u58f0\u5a01 \u8d6b\u8d6b\uff0c\u4ece\u6765\u6ca1\u6709\u4eba\u6562\u4e00\u634b\u864e\u987b\uff0c\u6b64\u65f6\u89c1\u4e00\u4e2a\u5e74\u8f7b\u4eba\u7adf\u6562\u5f53\u7740\u6559\u4e3b\u7684\u9762\u8bf4\u51fa\u8fd9\u6837\u8f7b\u8511\u7684\u8bdd\uff0c\u7109 \u6709\u4e0d\u60ca\u5947\u4e4b\u7406\u3002 \u3000\u3000\u7126\u5f02\u884c\u81ea\u662f\u5927\u6012\uff0c\u4f46\u4ed6\u6446\u7740\u4e00\u6d3e\u5b97\u4e3b\u7684\u8eab\u4efd\uff0c\u6545\u610f\u505a\u51fa\u4e0d\u5c51\u7684\u6837\u5b50\uff0c\u655e\u58f0\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u8fd9\u4f4d\u670b\u53cb\u5634\u4e0a\u8fd8\u662f\u7559\u4e9b\u795e\u7684\u597d\u3002\u201d \u3000\u3000\u5c1a\u672a\u660e\u4f55\u5c1d\u4e0d\u77e5\u9053\u81ea\u5df1\u5df2\u7ecf\u8eab\u5728\u5371\u9669\uff0c\u4ed6\u5168\u795e\u6212\u5907\u7740\uff0c\u773c\u89d2\u5fae\u659c\uff0c\u770b\u89c1\u90a3\u529f\u529b\u6df1\u539a\u7684\u9ed1 \u886b\u8001\u8005\uff0c\u6b63\u6ee1\u8138\u715e\u6c14\u5730\u671d\u81ea\u5df1\u8d70\u4e86\u8fc7\u6765\uff0c\u4e24\u9053\u773c\u5149\uff0c\u50cf\u5200\u4e00\u6837\u5730\u76ef\u5728\u81ea\u5df1\u8eab\u4e0a\uff0c\u8d70\u5f97\u867d\u7136\u4e0d \u5feb\uff0c\u4f46\u58f0\u52bf\u715e\u662f\u60ca\u4eba\u3002 \u3000\u3000\u5176\u4f59\u7684\u5929\u9634\u6559\u4f17\uff0c\u4e5f\u6b63\u4ee5\u4e00\u79cd\u5e78\u707e\u4e50\u7978\u7684\u773c\u5149\u770b\u7740\u81ea\u5df1\uff0c\u4eff\u4f5b\u81ea\u5df1\u7684\u4e00\u5207\uff0c\u90fd\u5df2\u60ac\u5728\u90a3 \u9ed1\u886b\u8001\u8005\u7684\u638c\u4e0b\u4f3c\u7684\u3002 \u3000\u3000\u7a7a\u6c14\u9aa4\u7136\u7d27\u5f20\u4e86\u8d77\u6765\uff0c\u5c1a\u672a\u660e\u5374\u6f2b\u4e0d\u5728\u610f\u5730\u54c8\u54c8\u4e00\u7b11\u3002 \u3000\u3000\u4ed6\u80c6\u6c14\u5b9e\u6709\u8fc7\u4eba\u4e4b\u5904\uff0c\u5426\u5219\u5f53\u5e74\u600e\u6562\u5b64\u8eab\u4e00\u4eba\uff0c\u95ef\u5165\u4e24\u6cb3\u7eff\u6797\u9053\u7684\u7fa4\u96c4\u4e4b\u4f1a\u3002 \u3000\u3000\u4ed6\u773c\u89d2\u751a\u81f3\u518d\u4e5f\u4e0d\u5411\u90a3\u9ed1\u886b\u8001\u8005\u98d8\u4e00\u773c\uff0c\u773c\u5149\u4e2d\u5e26\u7740\u4e9b\u51b7\u7b11\uff0c\u671d\u7126\u5f02\u884c\u9053\uff1a\u201c\u5144\u5f1f\u867d\u7136 \u53ea\u662f\u4e2a\u6c5f\u6e56\u4e0a\u7684\u65e0\u540d\u5c0f\u5352\uff0c\u4f46\u662f\u5374\u4e5f\u4e0d\u6562\u5fd8\u5374\u6c5f\u6e56\u4e2d\u7684\u9053\u4e49\uff0c\u66f4\u4e0d\u4f1a\u505a\u51fa\u4e9b\u5077\u9e21\u6478\u72d7\u7684\u52fe \u5f53\u3002\u201d \u3000\u3000\u7126\u5f02\u884c\u9762\u5b54\u4e00\u677f\uff0c\u51db\u7136\u8bf4\u9053\uff1a\u201c\u670b\u53cb\u8bf4\u8bdd\u53ef\u8981\u653e\u6e05\u695a\u4e9b\u3002\u201d \u3000\u3000\u5c1a\u672a\u660e\u7684\u65e5\u5149\u6beb\u4e0d\u9000\u7f29\u5730\u4ecd\u77aa\u5728\u4ed6\u8138\u4e0a\uff0c\u9053\uff1a\u201c\u5144\u5f1f\u5012\u60f3\u8bf4\u6e05\u695a\u4e9b\uff0c\u53ea\u6015\u8bf4\u6e05\u695a\u4e86\uff0c\u9601 \u4e0b\u2026\u2026\u201d\u4ed6\u51b7\u7b11\u8fde\u8fde\uff0c\u81ea\u5df1\u987f\u4f4f\u4e86\u8bdd\u3002 \u3000\u3000\u90a3\u9ed1\u886b\u8001\u8005\u6b64\u65f6\u5df2\u8d70\u5230\u4ed6\u8eab\u4fa7\uff0c\u9634\u7b11\u9053\uff1a\u201c\u53ea\u6015\u9601\u4e0b\u4ee5\u540e\u518d\u4e5f\u65e0\u6cd5\u8bf4\u8bdd\u4e86\u3002\u201d\u8bed\u58f0\u65b9\u843d \u53cc\u638c\u9f50\u51fa\uff0c\u98ce\u58f0\u864e\u864e\uff0c\u76f4\u51fb\u5c1a\u672a\u660e\u7684\u808b\u4e0b\u3002 \u3000\u3000\u5c1a\u672a\u660e\u867d\u7136\u505a\u51fa\u6f2b\u4e0d\u5728\u610f\u7684\u6837\u5b50\uff0c\u53ef\u662f\u4ed6\u5fc3\u4e2d\u54ea\u91cc\u6709\u534a\u70b9\u677e\u61c8\u3002 \u3000\u3000\u9ed1\u886b\u8001\u8005\u7684\u53cc\u638c\u582a\u582a\u51fb\u5230\u4ed6\u7684\u808b\u4e0b\uff0c\u4ed6\u731b\u4e00\u9519\u6b65\uff0c\u8eab\u5f62\u5411\u540e\u6ed1\u5f00\u4e86\u5c3a\u8bb8\uff0c\u5728\u9ed1\u886b\u8001\u8005\u7684 \u53cc\u638c\u65b9\u81ea\u907d\u7a7a\u7684\u90a3\u4e00\u5239\u90a3\uff0c\u53f3\u624b\u4e94\u6307\u73af\u6263\uff0c\u75be\u5730\u53bb\u9501\u90a3\u9ed1\u886b\u8001\u8005\u7684\u8109\u95e8\uff0c\u5de6\u638c\u5411\u5916\u53cd\u524a\uff0c\u7a81 \u53c8\u53d8\u4e86\u4e2a\u65b9\u5411\uff0c\u62c7\u6307\u5916\u4f38\uff0c\u7adf\u7136\u4ee5\u62c7\u6307\u70b9\u5411\u9ed1\u886b\u8001\u8005\u8170\u4e0b\u7684\u201c\u7b11\u8170\u7a74\u201d\u3002 \u3000\u3000\u4ed6\u975e\u4f46\u907f\u5f97\u6070\u5230\u597d\u5904\uff0c\u8fd9\u6263\u8109\u3001\u53cd\u524a\u3001\u70b9\u7a74\uff0c\u4e00\uff0c\u62db\u4e09\u5f0f\uff0c\u4e0d\u4f46\u51fa\u624b\u5982\u98ce\uff0c\u62db\u5f0f\u66f4\u662f\u8be1 \u5f02\u5df2\u6781\u3002 \u3000\u3000\u90a3\u9ed1\u886b\u8001\u8005\u6b63\u662f\u5929\u9634\u6559\u91cc\uff0c\u638c\u9f99\u722a\u575b\u4e0b\u7684\u575b\u4e3b\uff0c\u6c5f\u6e56\u4e0a\u65e9\u5df2\u95fb\u540d\u7684\u9ed1\u715e\u9b54\u638c\u5c1a\u6587\u658c\u3002 \u3000\u3000\u5c1a\u672a\u660e\u8fd9\u4e00\u62db\u7684\u8fd0\u7528\uff0c\u5b9e\u5728\u8fdc\u51fa\u90a3\u9ed1\u715e\u9b54\u638c\u7684\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u4f46\u4ed6\u7a76\u7adf\u662f\u4e0d\u540c\u51e1\u54cd\u7684\u4eba\u7269\uff0c\u5de6\u638c\u731b\u5730\u5212\u4e86\u4e2a\u534a\u5708\uff0c\u7adf\u4ee5\u201c\u91d1\u4e1d\u526a\u201d\u7684\u624b\u6cd5\u53bb\u53cd\u526a\u5c1a\u672a \u660e\u7684\u624b\u8155\u3002 \u3000\u3000\u53f3\u65f6\u4e00\u6c89\uff0c\u649e\u5411\u94c1\u80c6\u5c1a\u672a\u660e\u5de6\u81c2\u81c2\u5f2f\u7684\u201c\u66f2\u6c60\u7a74\u201d\u3002 \u3000\u3000\u4e24\u4eba\u8fd9\u4e00\u4ea4\u624b\uff0c\u5728\u5feb\u5982\u95ea\u7535\u5149\u77f3\u7684\u4e00\u523b\u91cc\uff0c\u4fbf\u5df2\u5404\u5404\u53d1\u51fa\u6570\u62db\uff0c\u5c1a\u672a\u660e\u95f7\u54fc\u4e00\u58f0\uff0c\u53cc\u81c2 \u5411\u5185\u5708\u4e86\u56de\u6765\uff0c\u731b\u5730\u5410\u6c14\u5f00\u58f0\uff0c\u811a\u4e0b\u53c8\u4e00\u6362\u6b65\uff0c\u53cc\u638c\u9f50\u53d1\uff0c\u51fb\u5411\u5c1a\u6587\u658c\u7684\u524d\u80f8\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u62db\u5b8c\u5168\u662f\u4ee5\u786c\u640f\u786c\uff0c\u4e1d\u6beb\u6ca1\u6709\u5c06\u5bf9\u65b9\u90a3\u79cd\u60ca\u4eba\u7684\u5185\u529b\u653e\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u9ed1\u715e\u9b54\u638c\u4e00\u58f0\u51b7\u7b11\uff0c\u53cc\u638c\u4e5f\u81ea\u63a8\u51fa\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u523b\u91cc\uff0c\u6bcf\u4e2a\u4eba\u5fc3\u91cc\u90fd\u6cdb\u8d77\u4e00\u4e2a\u5ff5\u5934\uff1a\u201c\u8fd9\u5c0f\u5b50\u7adf\u6562\u548c\u4ee5\u9ed1\u715e\u638c\u529b\u79f0\u96c4\u6b66\u6797\u7684\u9ed1 \u715e\u9b54\u638c\u8f83\u91cf\u638c\u529b\uff0c\u771f\u662f\u627e\u6b7b\u3002\u201d \u3000\u3000\u53ea\u6709\u718a\u501c\u4ecd\u7136\u5b89\u8be6\u5730\u7ad9\u7740\uff0c\u4ed6\u548c\u5c1a\u672a\u660e\u5bf9\u8fc7\u4e24\u638c\uff0c\u77e5\u9053\u5c1a\u672a\u660e\u7684\u638c\u529b\uff0c\u5e76\u4e0d\u5728\u81ea\u5df1\u4e4b \u4e0b\uff0c\u9ed1\u715e\u9b54\u638c\u867d\u7136\u5a01\u540d\u663e\u8d6b\uff0c\u5185\u529b\u60ca\u4eba\uff0c\u4f46\u662f\u81ea\u5df1\u81ea\u5fd6\u529f\u529b\uff0c\u4e5f\u4e0d\u60e7\u4ed6\uff0c\u90a3\u4e48\u4ee5\u6b64\u7c7b\u63a8\uff0c\u5c1a \u672a\u660e\u5f53\u7136\u4e5f\u4e0d\u4f1a\u5403\u4e8f\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u5bf9\u5c1a\u672a\u660e\u7684\u8fd9\u4e00\u756a\u4e3e\u52a8\uff0c\u5e76\u4e0d\u5341\u5206\u8d5e\u6210\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5fc3\u4e2d\u6240\u76d8\u7b97\u7740\u7684\u662f\uff1a\u5c06\u8fd9\u6b21\u201c\u8d2f\u65e5\u5251\u201d\u88ab\u593a\u7684\u8d23\u4efb\uff0c\u5168\u653e\u5728\u6b66\u5f53\u6d3e\u8eab\u4e0a\u3002 \u3000\u3000\u8fd9\u5e76\u975e\u4ed6\u7684\u6015\u4e8b\uff0c\u800c\u662f\u6709\u597d\u51e0\u79cd\u7684\u7406\u7531\uff0c\u4f7f\u4ed6\u6709\u8fd9\u79cd\u60f3\u6cd5\u3002 \u3000\u3000\u7b2c\u4e00\uff0c\u4ed6\u8ba4\u4e3a\u8fd9\u4ef6\u4e8b\u7684\u53d1\u751f\uff0c\u6b66\u5f53\u6d3e\u672c\u5e94\u8d1f\u8d77\u5168\u8d23\uff0c\u81ea\u5df1\u53c8\u4f55\u82e6\u591a\u8d39\u529b\u6c14\uff0c\u4f55\u51b5\u4ed6\u5728\u5c06 \u81ea\u5df1\u548c\u5bf9\u65b9\u7684\u5b9e\u529b\u4f30\u8ba1\u8fc7\u540e\uff0c\u77e5\u9053\u8457\u7136\u52a8\u624b\uff0c\u5403\u4e8f\u7684\u7edd\u662f\u81ea\u5df1\u8fd9\u65b9\uff0c\u4ed6\u4e34\u4e8b\u4e00\u591a\uff0c\u81ea\u7136\u5c06\u4e8b \u60c5\u7684\u5229\u5bb3\u5206\u6790\u5f97\u8f83\u4e3a\u6e05\u695a\u3002 \u3000\u3000\u5176\u6b21\uff0c\u4ed6\u4e5f\u60f3\u5230\u81ea\u5df1\u5728\u6b66\u5f53\u5c71\u603b\u7b97\u662f\u5ba2\uff0c\u5c31\u662f\u7167\u6c5f\u6e56\u7684\u89c4\u77e9\uff0c\u4e5f\u4e0d\u5e94\u8be5\u5728\u6b66\u5f53\u5c71\u4e0a\u548c\u4eba \u52a8\u624b\u3002 \u3000\u3000\u4ed6\u867d\u7136\u4e0d\u514d\u5c06\u5bf9\u65b9\u7684\u5b9e\u529b\u4f30\u5f97\u9ad8\u4e86\u4e9b\uff0c\u4f46\u8fd9\u662f\u4ed6\u591a\u6b21\u7ecf\u9a8c\u9020\u6210\u7684\u8c28\u614e\uff0c\u987b\u77e5\u4ed6\u7b2c\u4e8c\u6b21\u51fa \u5e08\u4e4b\u540e\uff0c\u771f\u6b63\u52a8\u624b\u7684\u4e00\u6b21\uff0c\u5c31\u662f\u5728\u751c\u751c\u8c37\u91cc\u548c\u7389\u9762\u795e\u5251\u5e38\u6f2b\u5929\uff0c\u6563\u82b1\u4ed9\u5b50\u7530\u654f\u654f\u6240\u4ea4\u624b\u7684\u4e00 \u6b21\u3002 \u3000\u3000\u800c\u90a3\u4e00\u6b21\uff0c\u4ed6\u5e76\u6ca1\u6709\u5360\u5230\u534a\u5206\u4fbf\u5b9c\u3002 \u3000\u3000\u662f\u4ee5\u4ed6\u5bf9\u81ea\u5df1\u7684\u5b9e\u529b\uff0c\u53c8\u4e0d\u514d\u4f30\u8ba1\u5f97\u4f4e\u4e86\u4e9b\uff0c\u4ed6\u54ea\u91cc\u77e5\u9053\uff0c\u7389\u9762\u795e\u5251\u7684\u5251\u672f\uff0c\u5728\u5341\u5e74\u524d \u5df2\u53ef\u79f0\u5f97\u4e0a\u662f\u7edd\u9876\u9ad8\u624b\uff0c\u800c\u7389\u9762\u795e\u5251\uff0c\u6563\u82b1\u4ed9\u5b50\u90a3\u79cd\u6697\u5668\u548c\u5251\u672f\u914d\u5408\u7684\u9635\u6cd5\uff0c\u66f4\u662f\u72ec\u6b65\u5929 \u4e0b\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u7684\u5ff5\u5934\uff0c\u4e00\u77ac\u5373\u8fc7\u3002 \u3000\u3000\u90a3\u94c1\u80c6\u5c1a\u672a\u660e\uff0c\u4e5f\u9020\u51fa\u4e00\u4ef6\u4eca\u5929\u9634\u6559\u91cc\u7684\u6bcf\u4e00\u4e2a\u4eba\u90fd\u5927\u4e3a\u5403\u60ca\u7684\u4e8b\u5b9e\u3002 \u3000\u3000\u539f\u6765\u4ed6\u548c\u9ed1\u715e\u9b54\u638c\u76f8\u4ea4\uff0c\u4e24\u4eba\u90fd\u9000\u540e\u4e86\u51e0\u6b65\uff0c\u867d\u7136\u662f\u4e0d\u5206\u80dc\u8d1f\uff0c\u4f46\u662f\u5df2\u4f7f\u90a3\u4e9b\u5bf9\u9ed1\u715e\u9b54 \u638c\u7684\u638c\u529b\u62b1\u7740\u4fe1\u5fc3\u7684\u4eba\uff0c\u60ca\u5f02\u5f97\u53eb\u51fa\u58f0\u6765\u3002 \u3000\u3000\u6218\u74a7\u541b\u53c8\u201c\u54df\u201d\u4e86\u4e00\u58f0\uff0c\u76ee\u5149\u751c\u751c\u5730\u8f6c\u7740\u5c1a\u672a\u660e\u7684\u8eab\u5b50\u6253\u8f6c\u3002 \u3000\u3000\u718a\u501c\u6697\u7b11\u5fd6\u9053\uff1a\u201c\u8fd9\u4f4d\u5929\u9634\u6559\u4e3b\u770b\u8d77\u4eba\u6765\uff0c\u53ef\u771f\u8ba9\u4eba\u5403\u4e0d\u6d88\u3002\u201d \u3000\u3000\u94c1\u80c6\u5c1a\u672a\u660e\u62a5\u51fa\u540d\u53f7\u540e\uff0c\u6bcf\u4e2a\u4eba\u5fc3\u91cc\u5404\u6709\u4e0d\u540c\u7684\u60f3\u6cd5\u3002 \u3000\u3000\u7126\u5f02\u884c\u5fd6\u9053\uff1a\u201c\u6b64\u4eba\u82e5\u80fd\u62c9\u5165\u6211\u6559\uff0c\u5012\u662f\u4e2a\u5f97\u529b\u5e2e\u624b\uff0c\u770b\u4ed6\u6b66\u529f\uff0c\u7adf\u4e0d\u5728\u6211\u6559\u7684\u51e0\u4f4d\u575b \u4e3b\u4e4b\u4e0b\u3002\u201d\u8f6c\u5ff5\u53c8\u9053\uff1a\u201c\u53ea\u662f\u4ed6\u548c\u90a3\u59d3\u718a\u7684\u5728\u4e00\u8d77\uff0c\u82e5\u60f3\u62c9\u4ed6\u4eba\u6559\uff0c\u5b9a\u7136\u56f0\u96be\u5f97\u5f88\u3002\u201d \u3000\u3000\u9ed1\u715e\u9b54\u638c\u5c1a\u6587\u658c\u548c\u5c1a\u672a\u660e\u6362\u4e86\u4e00\u638c\uff0c\u5fc3\u4e2d\u53c8\u60ca\u53c8\u6012\uff0c\u6b64\u523b\u542c\u5230\u4ed6\u4e5f\u662f\u6c5f\u6e56\u4e2d\u6210\u540d\u7684\u4eba \u7269\uff0c\u5fc3\u91cc\u53cd\u800c\u597d\u53d7\u4e86\u4e9b\u3002 \u3000\u3000\u9ed1\u8863\u6469\u52d2\u548c\u767d\u8863\u9f99\u5973\uff0c\u5bf9\u5c1a\u672a\u660e\u4e0d\u7981\u66f4\u52a0\u7684\u6ce8\u610f\uff0c\u5fc3\u91cc\u60f3\u7740\uff1a\u201c\u539f\u6765\u4ed6\u4e5f\u662f\u548c\u6211\u4eec\u5e76\u5217 \u2018\u4e09\u79c0\u2019\u7684\u4eba\u7269\u3002\u201d\u518d\u4e00\u671b\u718a\u501c\uff0c\u6697\u5fd6\uff1a\u201c\u8fd9\u4e48\u4e00\u6765\uff0c\u2018\u6b66\u6797\u4e09\u79c0\u2019\u5c45\u7136\u5168\u805a\u5728\u6b66\u5f53\u5c71 \u4e86\u3002\u201d \u3000\u3000\u7126\u5f02\u884c\u4e5f\u8de8\u4e0a\u4e00\u6b65\uff0c\u671d\u5c1a\u672a\u660e\u9053\uff1a\u201c\u539f\u6765\u9601\u4e0b\u5c31\u662f\u5c1a\u5f53\u5bb6\u7684\uff0c\u4e45\u4ef0\u5f97\u5f88\uff0c\u6211\u5929\u9634\u6559\u867d\u7136 \u548c\u5c1a\u5f53\u5bb6\u7684\u751a\u5c11\u8054\u7edc\uff0c\u4f46\u603b\u7b97\u540c\u5904\u4e24\u6cb3\u3002\u201d\u4ed6\u5fae\u5fae\u4e00\u7b11\uff0c\u76ee\u5149\u5728\u718a\u501c\u8eab\u4e0a\u8f6c\u4e86\u4e24\u773c\uff0c\u53c8\u9053\uff1a \u201c\u4eca\u65e5\u5c1a\u5f53\u5bb6\u7684\u4eff\u4f5b\u5bf9\u655d\u6559\u751a\u4e3a\u4e0d\u6ee1\uff0c\u8fd9\u4e2a\u5012\u8981\u8bf7\u6559\u4e86\u3002\u201d \u3000\u3000\u6218\u74a7\u541b\u63a5\u53e3\u7b11\u9053\uff1a\u201c\u662f\u5440\uff0c\u5c1a\u8001\u5f1f\uff0c\u54b1\u4eec\u53ef\u6ca1\u6709\u5bf9\u4e0d\u4f4f\u4f60\u5440\uff0c\u4f60\u5e72\u5417\u5bf9\u54b1\u4eec\u90a3\u6837\uff1f\u201d \u3000\u3000\u7126\u5f02\u884c\u81ea\u6301\u8eab\u4efd\uff0c\u8bdd\u8bf4\u603b\u5f97\u7559\u51e0\u5206\u4f59\u5730\uff0c\u6218\u74a7\u541b\u5374\u558b\u558b\u5471\u5471\uff0c\u4fcf\u8bed\u751c\u7b11\uff0c\u8ba9\u4f60\u731c\u4e0d\u900f\u5979 \u5fc3\u4e2d\u5230\u5e95\u5728\u60f3\u7740\u4ec0\u4e48\u3002 \u3000\u3000\u5c1a\u672a\u660e\u51b7\u7b11\u9053\uff1a\u201c\u5144\u5f1f\u65e0\u540d\u5c0f\u5352\uff0c\u54ea\u91cc\u9ad8\u6500\u5f97\u4e0a\u4e24\u4f4d\uff0c\u66f4\u4e0d\u6562\u5bf9\u4e24\u4f4d\u6709\u4ec0\u4e48\u4e0d\u6ee1\u3002\u201d \u3000\u3000\u4ed6\u76ee\u5149\u7d27\u7d27\u77aa\u7740\u7126\u5f02\u884c\uff0c\u8bf4\u9053\uff1a\u201c\u6559\u4e3b\u8bf4\u5f97\u597d\uff0c\u5144\u5f1f\u548c\u8d35\u6559\u603b\u7b97\u540c\u5904\u4e24\u6cb3\uff0c\u6559\u4e3b\u82e5\u80fd\u8d4f \u5144\u5f1f\u4e00\u4e2a\u9762\u5b50\u2026\u2026\u201d \u3000\u3000\u6218\u74a7\u541b\u63a5\u53e3\u7b11\u9053\uff1a\u201c\u54ce\u54df\uff0c\u4ec0\u4e48\u7ed9\u4e0d\u7ed9\u9762\u5b50\u561b\uff0c\u5c1a\u8001\u5f1f\u6709\u5429\u5490\uff0c\u53ea\u7ba1\u8bf4\u51fa\u6765\u597d\u4e86\u3002\u201d \u3000\u3000\u5c1a\u672a\u660e\u4e00\u76b1\u7709\uff0c\u4ed6\u5bf9\u8fd9\u5de7\u7b11\u5584\u8a00\u7684\u6218\u74a7\u541b\uff0c\u8d77\u4e86\u4e00\u79cd\u5f02\u6837\u7684\u611f\u89c9\uff0c\u4e0d\u89c9\u5c06\u538c\u6076\u5929\u9634\u6559\u7684 \u5fc3\u7406\uff0c\u51cf\u53bb\u4e86\u5927\u534a\uff0c\u4f46\u662f\u4ed6\u6781\u7aef\u4e0d\u613f\u610f\u8ba9\u4efb\u4f55\u4eba\u77e5\u9053\u4ed6\u5fc3\u4e2d\u7684\u611f\u89c9\uff0c\u662f\u4ee5\u501f\u7740\u76b1\u7709\u6765\u63a9\u9970\u9762 \u4e0a\u7684\u53ef\u80fd\u53d1\u751f\u7684\u53d8\u5316\u3002 \u3000\u3000\u4ed6\u62ac\u5934\u4e00\u671b\uff0c\u6218\u74a7\u541b\u7684\u4e00\u53cc\u6c34\u6dcb\u6dcb\u7684\u773c\u775b\uff0c\u4ecd\u5e26\u7740\u751c\u7b11\u5728\u671b\u7740\u4ed6\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u66f4\u4e71\uff0c\u4e0d\u7981\u6697\u81ea\u8d23\u5907\u7740\u81ea\u5df1\uff0c\u6b63\u5f3a\u81ea\u6536\u6444\u4f4f\u5fc3\u795e\uff0c\u60f3\u8981\u7b54\u8bdd\u3002 \u3000\u3000\u5ffd\u5730\u542c\u5230\u8eab\u540e\u98ce\u58f0\u55d6\u7136\uff0c\u4ed6\u672c\u80fd\u5730\u9519\u638c\u6362\u6b65\uff0c\u5411\u540e\u4e00\u8f6c\u3002 \u3000\u3000\u6765\u7684\u5374\u662f\u98de\u9e64\u5b50\u3002 \u3000\u3000\u98de\u9e64\u5b50\u8eab\u5f62\u597d\u5feb\uff0c\u98de\u63a0\u800c\u6765\uff0c\u64e6\u8fc7\u718a\u501c\uff0c\u731b\u5730\u505c\u987f\u5728\u5c1a\u672a\u660e\u7684\u8eab\u4fa7\u3002\u4e00\u53d1\u4e00\u505c\uff0c\u4e1d\u6beb\u6ca1 \u6709\u52c9\u5f3a\u505a\u4f5c\u7684\u795e\u6001\u3002 \u3000\u3000\u98de\u9e64\u5b50\u8eab\u5f62\u505c\u5728\u5c1a\u672a\u660e\u7684\u8eab\u4fa7\uff0c\u4e5f\u5c31\u662f\u7126\u5f02\u884c\u7684\u9762\u524d\u3002 \u3000\u3000\u6b64\u65f6\u4ed6\u8138\u5982\u79cb\u971c\uff0c\u5df2\u4e0d\u4e3b\u65b9\u624d\u7684\u548c\u853c\uff0c\u51b7\u51b7\u5411\u7126\u5f02\u884c\u9053\uff1a\u201c\u8d35\u6559\u8fdc\u6765\uff0c\u655d\u6559\u6d3e\u638c\u6559\u771f\u4eba \u611f\u6fc0\u5f97\u5f88\uff0c\u53ea\u56e0\u638c\u6559\u771f\u4eba\u5df2\u7ecf\u5750\u5173\uff0c\u5b9e\u5728\u4e0d\u80fd\u591f\u63a5\u5f85\u5404\u4f4d\uff0c\u8d2b\u9053\u6301\u547d\u524d\u6765\u6df1\u81f4\u6b49\u610f\u3002\u201d \u3000\u3000\u7126\u5f02\u884c\u5251\u7709\u4e00\u7ad6\uff0c\u5df2\u7136\u6709\u4e9b\u53d8\u8272\u3002 \u3000\u3000\u98de\u9e64\u5b50\u773c\u5149\u968f\u7740\u4ed6\u7684\u7709\u6bdb\u4e00\u626c\uff0c\u63a5\u7740\u9053\uff1a\u201c\u53ea\u662f\u6559\u4e3b\u60f3\u8981\u7684\u4e1c\u897f\uff0c\u5bb6\u5e08\u6ca1\u6709\uff0c\u5c31\u662f\u6709\u7684 \u8bdd\uff0c\u4e5f\u4e07\u4e07\u4e0d\u80fd\u4ea4\u7ed9\u9601\u4e0b\u3002\u201d \u3000\u3000\u7126\u5f02\u884c\u795e\u8272\u5927\u53d8\uff0c\u5389\u58f0\u9053\uff1a\u201c\u5c31\u53eb\u9601\u4e0b\u8f6c\u544a\u4ee4\u5e08\uff0c\u4e00\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u5c31\u662f\u4ee4\u5e08\u4e0d\u613f\u63a5\u89c1\u6211 \u7b49\u3002\u6211\u7b49\u5374\u4e5f\u8bf4\u4e0d\u5f97\u8981\u786c\u95ef\u4e00\u95ef\u4e86\u3002\u201d \u3000\u3000\u98de\u9e64\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u53ea\u6015\u6ca1\u6709\u90a3\u4e48\u5bb9\u6613\u3002\u201d \u3000\u3000\u8bdd\u58f0\u65b9\u843d\uff0c\u6df1\u5904\u4f20\u6765\u513f\u58f0\u949f\u58f0\uff0c\u5165\u8033\u55e1\u7136\uff0c\u4f59\u97f3\u8885\u8885\uff0c\u4e45\u4e45\u4e0d\u6563\u3002 \u3000\u3000\u5c71\u9053\u4e0a\u5ffd\u7136\u4e00\u9053\u8d70\u6765\u56db\u4e2a\u9053\u4eba\uff0c\u4e00\u8272\u84dd\u5e03\u9053\u888d\uff0c\u624b\u4e2d\u6a2a\u6367\u7740\u957f\u5251\u3002\u5c71\u9053\u4e0a\u81f3\u5c11\u8fd8\u8d70\u51fa\u56db \u4e94\u5341\u4e2a\u84dd\u888d\u9053\u4eba\u3002 \u3000\u3000\u7126\u5f02\u884c\u8fde\u8fde\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u5c31\u51ed\u7740\u8fd9\u4e9b\u4eba\uff0c\u5c31\u60f3\u80fd\u62e6\u5f97\u4f4f\u6211\u5417\uff1f\u201d \u3000\u3000\u98de\u9e64\u5b50\u4e5f\u51b7\u7b11\u9053\uff1a\u201c\u8bd5\u8bd5\u770b\u3002\u201d \u3000\u3000\u7126\u5f02\u884c\u4ecd\u672a\u653e\u4e0b\u624b\u4e2d\u7684\u5251\uff0c\u6b64\u65f6\u4ed6\u5f39\u7740\u5251\u9798\u9053\uff1a\u201c\u8fd9\u4e2a\u5012\u771f\u8981\u8bd5\u4e00\u8bd5\uff0c\u770b\u770b\u6b66\u5f53\u6d3e\u7684\u56db \u4eea\u5251\u9635\u5230\u5e95\u6709\u4ec0\u4e48\u7384\u5999\u3002\u201d \u3000\u3000\u7c89\u9762\u82cf\u79e6\u738b\u667a\u9011\u5ffd\u7136\u6025\u6b65\u8d70\u4e86\u8fc7\u6765\uff0c\u9644\u7740\u7126\u5f02\u884c\u7684\u8033\u6735\u8bf4\u4e86\u4e24\u53e5\u8bdd\uff0c\u7126\u5f02\u884c\u4e0d\u4f4f\u5730\u70b9 \u5934\uff0c\u4eff\u4f5b\u5bf9\u7389\u667a\u9011\u7684\u8bdd\u8d5e\u6210\u5f97\u5f88\u3002 \u3000\u3000\u7126\u5f02\u884c\u7a81\u7136\u6717\u58f0\u7b11\u9053\uff1a\u201c\u6b66\u5f53\u6d3e\u679c\u7136\u662f\u540d\u95e8\u5927\u6d3e\uff0c\u4e0d\u540c\u51e1\u54cd\uff0c\u65e2\u7136\u4e0d\u51c6\u655d\u6559\u4e0a\u5c71\u62dc\u6e34\uff0c \u90a3\u655d\u6559\u5c31\u544a\u8f9e\u4e86\u3002\u201d \u3000\u3000\u9ed1\u715e\u9b54\u638c\u9762\u5e26\u6012\u5bb9\uff0c\u53eb\u9053\uff1a\u201c\u6559\u4e3b\u4e00--\u201d\u9ed1\u8863\u6469\u52d2\u767d\u666f\u7965\u5e94\u4e86\u4e00\u58f0\uff0c\u4e00\u4f38\u624b\uff0c\u81ea\u6000\u4e2d\u638f \u51fa\u4e00\u9762\u91d1\u5149\u707f\u7136\u7684\u5c0f\u9523\uff0c\u53f3\u624b\u5e76\u6307\uff0c\u65b9\u8981\u6572\u4e0b\u3002 \u3000\u3000\u718a\u501c\u5ffd\u7136\u5389\u559d\uff1a\u201c\u4e14\u6162\u3002\u3001\u6218\u74a7\u541b\u54af\u54af\u7b11\u7740\u5411\u7126\u5f02\u884c\u9053\uff1a\u201c\u5582\uff0c\u4f60\u770b\u4eba\u5bb6\u624d\u51e0\u5e74\u4e0d\u89c1\uff0c \u5df2\u7ecf\u957f\u5f97\u8fd9\u4e48\u5927\u4e86\u3002\u201d \u3000\u3000\u7126\u5f02\u884c\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e0d\u9519\uff0c\u679c\u7136\u51fa\u843d\u5f97\u4e00\u8868\u4eba\u6750\u3002\u201d \u3000\u3000\u4ed6\u4e00\u8f6c\u8138\uff0c\u5411\u7c89\u9762\u82cf\u79e6\u738b\u667a\u9011\u4e00\u62db\u624b\uff0c\u9053\uff1a\u201c\u738b\u8235\u4e3b\uff0c\u4f60\u966a\u8fd9\u4f4d\u8001\u5f1f\u804a\u804a\uff0c\u6211\u4eec\u8981\u5148\u8d70 \u4e86\u3002\u201d \u3000\u3000\u7126\u5f02\u884c\u8bf4\u5b8c\u4e86\u8bdd\uff0c\u7528\u8896\u62c2\u4e86\u62c2\u8863\u88f3\uff0c\u5de6\u624b\u4ecd\u62ff\u7740\u5251\uff0c\u7f13\u7f13\u5730\u8d70\u5411\u5c71\u8f7f\u3002 \u3000\u3000\u54ea\u77e5\u773c\u524d\u7a81\u7136\u4e00\u82b1\uff0c\u808b\u4e0b\u98ce\u58f0\u55d6\u7136\uff0c\u4ed6\u6b66\u529f\u8be1\u5f02\uff0c\u4e0d\u907f\u53cd\u8fce\uff0c\u5de6\u624b\u5251\u9798\u5012\u8f6c\uff0c\u53f3\u624b\u52a8\u4e5f \u4e0d\u52a8\u3002\u718a\u501c\u4e00\u53cd\u8155\u653b\u51fb\u7684\u53f3\u624b\u5708\u56de\u6765\u6293\u7126\u5f02\u884c\u7684\u624b\u4e2d\u5251\uff0c\u5de6\u624b\u524d\u524a\uff0c\u95f7\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u731b\u8fd0 \u771f\u6c14\u51fb\u5411\u7126\u5f02\u884c\u7684\u53f3\u80f8\u7684\u7a7a\u95e8\u3002 \u3000\u3000\u7126\u5f02\u884c\u5fae\u4e00\u5927\u610f\uff0c\u89c9\u88ad\u5411\u53f3\u80f8\u638c\u98ce\u7684\u5f3a\u52b2\uff0c\u8fdc\u51fa\u4e4e\u4ed6\u610f\u6599\u4e4b\u5916\uff0c\u800c\u4e14\u51fa\u624b\u4e4b\u5feb\uff0c\u62ff\u634f\u65f6 \u95f4\u4e4b\u51c6\uff0c\u90fd\u9a87\u4eba\u542c\u95fb\u3002 \u3000\u3000\u4ed6\u6b64\u65f6\u5de6\u624b\u624b\u4e2d\u7684\u5251\u9798\uff0c\u5df2\u88ab\u718a\u501c\u6293\u7740\uff0c\u5982\u679c\u4ed6\u60f3\u907f\u5f00\u51fb\u5411\u53f3\u80f8\u7684\u90a3\u4e00\u62db\uff0c\u52bf\u5fc5\u975e\u8981\u6492 \u5251\u4e0d\u53ef\uff0c\u4f46\u4ed6\u4e00\u6d3e\u5b97\u4e3b\uff0c\u540d\u6151\u5929\u4e0b\uff0c\u5b9e\u5728\u4e0d\u613f\u610f\u5931\u6b64\u4e00\u62db\uff0c\u4e0d\u8fc7\u9664\u6b64\u4ee5\u5916\uff0c\u53c8\u5b9e\u5728\u522b\u65e0\u4ed6\u6cd5 \u89e3\u6551\u3002 \u3000\u3000\u6218\u74a7\u541b\u592b\u5987\u8fde\u5fc3\uff0c\u8eab\u5f62\u5fae\u52a8\uff0c\u7389\u6307\u659c\u98de\uff0c\u53e3\u4e2d\u5a07\u7b11\u7740\u9053\uff1a\u201c\u559d\uff0c\u5c0f\u5144\u5f1f\u771f\u52a8\u624b\u5440\u3002\u201d \u3000\u3000\u5c1a\u672a\u660e\u5fc3\u4e2d\u4e00\u51b7\uff0c\u6697\u5fd6\uff1a\u201c\u539f\u6765\u5979\u5728\u5bf9\u654c\u52a8\u624b\u65f6\u90fd\u4f1a\u7b11\u7684\u3002\u201d \u3000\u3000\u4f46\u6b64\u65f6\u718a\u501c\u5df2\u5728\u9669\u5883\uff0c\u4ed6\u4e5f\u65e0\u6687\u518d\u53bb\u5bfb\u601d\u8fd9\u4e9b\u79c1\u60c5\u3002\u5251\u7709\u4e00\u5f20\uff0c\u4e5f\u7a9c\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u54ea\u77e5\u773c\u524d\u9ed1\u5f71\u4e00\u52a8\uff0c\u9ed1\u715e\u9b54\u638c\u53c8\u62e6\u5728\u4ed6\u8eab\u524d\uff0c\u4ed6\u51b7\u7b11\u559d\u9053\uff1a\u201c\u597d\u3002\u201d\u9519\u6b65\u56e2\u638c\uff0c\u53cc\u638c\u53c8 \u5c3d\u529b\u800c\u51fa\uff0c\u9ed1\u715e\u9b54\u638c\u524d\u80f8\u731b\u51fb\u3002 \u3000\u3000\u90a3\u8fb9\u7126\u5f02\u884c\u65e0\u53ef\u5948\u4f55\uff0c\u5728\u6027\u547d\u540d\u8a89\u7684\u6743\u8861\u4e4b\u4e0b\uff0c\u7a76\u7adf\u662f\u524d\u8005\u66f4\u91cd\u8981\u5f97\u591a\uff0c\u5fc3\u6025\u4e00\u51b3\uff0c\u5de6 \u624b\u6492\u5251\uff0c\u8eab\u5f62\u5411\u540e\u98d8\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u718a\u501c\u4e00\u62db\u5f97\u624b\uff0c\u65b9\u6697\u559c\u4fa5\u5e78\uff0c\u4e00\u53cc\u51dd\u7389\u822c\u7684\u6625\u8471\uff0c\u5df2\u968f\u7740\u5a07\u7b11\u800c\u6765\uff0c\u75be\u6307\u81ea\u5df1\u53f3\u81c2\u7684 \u201c\u66f2\u6c60\u201d\uff0c\u80a9\u4e0b\u7684\u201c\u80a9\u771f\u201d\u4e24\u5904\u5927\u7a74\uff0c\u51fa\u624b\u4e4b\u72e0\u3001\u8fc5\u3001\u51c6\uff0c\u4ee4\u4eba\u60ca\u7136\u800c\u60ca\u3002 \u3000\u3000\u718a\u501c\u4e00\u60ca\u4e4b\u4e0b\uff0c\u9000\u6b65\u53d8\u65f6\uff0c\u66f2\u8170\u9519\u638c\uff0c\u65b9\u624d\u907f\u5f00\u6b64\u62db\u3002 \u3000\u3000\u7126\u5f02\u884c\u540e\u9000\u7684\u8eab\u5f62\uff0c\u53c8\u50cf\u884c\u4e91\u6d41\u6c34\uff0c\u63a0\u4e0a\u524d\u6765\uff0c\u5de6\u624b\u7b95\u5f20\uff0c\u53f3\u638c\u659c\u51fb\uff0c\u4e0a\u51fb\u9762\u95e8\uff0c\u4e0b\u6253 \u80f8\u8179\uff0c\u4e00\u62db\u4e24\u5f0f\uff0c\u7aef\u7684\u975e\u540c\u5c0f\u53ef\u3002 \u3000\u3000\u5929\u9634\u6559\u4e3b\u592b\u5987\u4e24\u4eba\u5408\u529b\u8054\u638c\uff0c\u5a01\u529b\u5c82\u662f\u7b49\u95f2\uff0c\u718a\u501c\u53ea\u89c9\u5f97\u5de6\u53f3\u4e0a\u4e0b\uff0c\u5168\u8eab\u90fd\u5728\u5bf9\u65b9\u638c\u529b \u4e4b\u5185\u3002 \u3000\u3000\u5c1a\u672a\u660e\u4e0e\u9ed1\u715e\u9b54\u638c\u518d\u6b21\u5bf9\u638c\uff0c\u8fd9\u4e00\u4e0b\u4e24\u4eba\u5168\u529b\u800c\u65bd\uff0c\u60c5\u51b5\u66f4\u662f\u60ca\u4eba\u3002 \u3000\u3000\u638c\u98ce\u65b9\u81ea\u76f8\u63a5\uff0c\u4e24\u4eba\u8eab\u5f62\u90fd\u5df2\u7ad9\u7acb\u4e0d\u7a33\uff0c\u659c\u659c\u56de\u540e\u5012\u4e0b\u3002 \u3000\u3000\u718a\u501c\u8eab\u968f\u610f\u52a8\uff0c\u53f3\u624b\u5251\u9798\u6a2a\u626b\uff0c\u5de6\u624b\u7acb\u638c\u5982\u5200\uff0c\u8eab\u5f62\u5374\u5411\u5de6\u540e\u65b9\u6ed1\u4e86\u51fa\u53bb\uff0c\u4f46\u9976\u662f\u8fd9 \u6837\uff0c\u4ecd\u7136\u6162\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u4ed6\u867d\u7136\u5e76\u6ca1\u6709\u53d7\u5230\u4efb\u4f55\u4f24\u635f\uff0c\u4f46\u662f\u53f3\u624b\u6240\u6301\u7684\u5251\uff0c\u5374\u53c8\u88ab\u7126\u5f02\u884c\u593a\u56de\u53bb\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u7b2c\u4e00\u6279\u81ea\u5c71\u4e0a\u4e0b\u6765\u7684\u56db\u9053\u4eba\uff0c\u7a81\u7136\u9f50\u4e00\u987f\u8db3\uff0c\u56db\u6761\u8eab\u8eaf\u5b8c\u5168\u4e00\u4e2a\u52a8\u4f5c\uff0c\u8fde\u8882\u800c\u8d77\uff0c \u9053\u888d\u98d8\u98d8\uff0c\u5251\u5149\u95ee\u95ea\uff0c\u65e5\u5149\u4e0b\u5b9b\u5982\u98de\u4ed9\u3002\u201c\u8fd9\u56db\u4e2a\u9053\u4eba\u4e0d\u4f46\u63a0\u8d77\u65f6\u5b8c\u5168\u5728\u540c\u4e00\u65f6\u95f4\u4e4b\u5185\uff0c\u843d \u5730\u65f6\u4ea6\u5206\u6beb\u4e0d\u5dee\uff0c\u663e\u89c1\u5f97\u662f\u7ecf\u8fc7\u957f\u65f6\u671f\u7684\u953b\u70bc\uff0c\u624d\u80fd\u591f\u70bc\u5230\u8fd9\u79cd\u5b8c\u7f8e\u7684\u9ed8\u5951\u3002\u90a3\u56db\u4e2a\u9053\u4eba\u53f3 \u81c2\u4e00\u4f38\uff0c\u5c06\u624b\u4e2d\u7684\u5251\u5e73\u4f38\u800c\u51fa\uff0c\u624b\u4e00\u6296\uff0c\u633d\u8d77\u56db\u4e2a\u6597\u5927\u7684\u5251\u82b1\uff0c\u7136\u540e\u5de7\u5999\u5730\u5c06\u56db\u67c4\u5251\u642d\u5728\u4e00 \u8d77\u3002\u90a3\u4e9b\u7531\u5c71\u4e0a\u8d70\u4e0b\u7684\u6570\u5341\u4e2a\u9053\u58eb\uff0c\u4e5f\u4ff1\u90fd\u5e73\u4f38\u7740\u5251\u3002\u5251\u5149\u95ea\u707f\uff0c\u88ab\u65e5\u5149\u4e00\u6620\uff0c\u66f4\u663e\u5f97\u9752\u8292 \u7d2b\u7535\uff0c\u5149\u91c7\u593a\u76ee\u3002\u7126\u5f02\u884c\u76ee\u5149\u56db\u8f6c\uff0c\u4ed6\u867d\u7136\u89c1\u591a\u8bc6\u5e7f\uff0c\u5374\u731c\u4e0d\u51fa\u8fd9\u4e9b\u9053\u58eb\u4eec\u7684\u7528\u610f\u3002\u6218\u74a7\u541b \u54af\u54af\u4e00\u7b11\uff0c\u4f46\u7b11\u58f0\u4e2d\u5df2\u9690\u9690\u900f\u51fa\u4e0d\u81ea\u7136\u7684\u5473\u9053\u6765\u3002\u5979\u5a9a\u76ee\u6a2a\u98de\uff0c\u5728\u5148\u524d\u90a3\u56db\u4e2a\u84dd\u888d\u9053\u4eba\u7684\u8138 \u4e0a\u626b\u8fc7\uff0c\u8bf4\u9053\uff1a\u201c\u54df\uff0c\u9053\u7237\u4eec\uff0c\u8fd9\u662f\u5e72\u4ec0\u4e48\u5440\uff1f\u201d \u3000\u3000\u5979\u8bdd\u58f0\u4e00\u843d\uff0c\u5374\u6ca1\u6709\u4efb\u4f55\u58f0\u97f3\u6765\u56de\u7b54\u5979\u7684\u8bdd\uff0c\u6df1\u5c71\u6d41\u6c34\uff0c\u9664\u4e86\u6c34\u58f0\u4e4b\u5916\uff0c\u8fd9\u4e48\u591a\u4eba\u7adf\u6ca1 \u6709\u4e00\u4e2a\u53d1\u51fa\u58f0\u97f3\u6765\u3002 \u3000\u3000\u5c71\u6df1\u5904\u5ffd\u7136\u4f20\u6765\u4e00\u8fde\u4e32\u6e05\u6717\u7684\u949f\u58f0\u3002 \u3000\u3000\u90a3\u4e9b\u56db\u4eba\u4e00\u7ec4\u7684\u84dd\u888d\u9053\u4eba\uff0c\u638c\u4e2d\u672c\u662f\u63a5\u8fde\u5728\u4e00\u5904\u7684\u5251\uff0c\u6b64\u65f6\u7a81\u7136\u5c55\u4e86\u5f00\u6765\uff0c\u5728\u5f3a\u70c8\u7684\u9633 \u5149\u4e0b\uff0c\u5212\u51fa\u4e00\u9053\u8000\u4eba\u773c\u76ee\u7684\u5251\u5149\u3002 \u3000\u3000\u98de\u9e64\u5b50\u5355\u638c\u671d\u56db\u5468\u6253\u4e86\u4e2a\u95ee\u8baf\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u655d\u6d3e\u5348\u8bfe\u65f6\u95f4\u5df2\u5230\uff0c\u8bf7\u65bd\u4e3b\u4eec\u5c31\u6b64\u4e0b\u5c71 \u5427\u3002\u201d \u3000\u3000\u7126\u5f02\u884c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6b63\u662f\uff0c\u6b63\u662f\uff0c\u5927\u5bb6\u90fd\u8be5\u4e0b\u5c71\u4e86\u3002\u201d \u3000\u3000\u5c1a\u672a\u660e\u9053\uff1a\u201c\u4e14\u6162\u3002\u201d \u3000\u3000\u6301\u5251\u7684\u6b66\u5f53\u9053\u4eba\uff0c\u51e0\u5341\u53ea\u773c\u775b\uff0c\u90fd\u51db\u7136\u77aa\u5728\u5c1a\u672a\u660e\u7684\u8138\u4e0a\uff0c\u5c1a\u672a\u660e\u5374\u50cf\u6ee1\u4e0d\u5728\u4e4e\uff0c\u6717\u58f0 \u9053\uff1a\u201c\u9053\u957f\u4eec\u82e5\u8981\u529f\u8bfe\uff0c\u5c31\u8bf7\u4e0a\u5c71\u53bb\uff0c\u5728\u4e0b\u7b49\u6709\u4e9b\u4e8b\u5c1a\u672a\u4e86\uff0c\u8fd8\u8981\u5728\u6b64\u76d8\u6853\u4e00\u4e0b\u3002\u201d \u3000\u3000\u98de\u9e64\u5b50\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u9601\u4e0b\u672a\u514d\u592a\u72c2\u4e86\u4e9b\uff0c\u96be\u9053\u8fd9\u6b66\u5f53\u5c71\u7adf\u662f\u4efb\u4eba\u6765\u53bb\u7684\u5730\u65b9\uff1f\u201d \u3000\u3000\u6218\u74a7\u541b\u5a07\u7b11\u9053\uff1a\u201c\u662f\u5440\uff0c\u8fd9\u6b66\u5f53\u5c71\u5c82\u662f\u4efb\u4eba\u6765\u53bb\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u201c\u6b66\u5f53\u5c71\u5f53\u7136\u4e0d\u662f\u4efb\u4eba\u6765\u53bb\u7684\u5730\u65b9\u3002\u201d\u5c1a\u672a\u660e\u51b7\u7b11\u7740\u9053\uff1a\u201c\u53ef\u662f\u5374\u8ba9\u5728\u6b66\u5f53\u5c71\u4e0a\u62a2\u4e1c\u897f \u7684\u4eba\u4efb\u610f\u6765\u53bb\uff0c\u5012\u771f\u4ee4\u5728\u4e0b\u6709\u4e9b\u4e0d\u61c2\u4e86\u3002\u201d \u3000\u3000\u98de\u9e64\u5b50\u53d8\u8272\u76f8\u8be2\u9053\uff1a\u201c\u9601\u4e0b\u6b64\u8bf7\u4f55\u610f\uff1f\u201d \u3000\u3000\u6218\u74a7\u541b\u7b11\u9053\uff1a\u201c\u5501\uff0c\u53c8\u6709\u8c01\u5728\u6b66\u5f53\u5c71\u4e0a\u62a2\u4e86\u4e1c\u897f\u5440\uff1f\u201d \u3000\u3000\u5c1a\u672a\u660e\u4e00\u62ac\u5934\uff0c\u76ee\u5149\u63a5\u89e6\u5230\u5979\u90a3\u6c38\u8fdc\u5e26\u7740\u7b11\u610f\u7684\u773c\u775b\uff0c\u5fc3\u4e2d\u7a81\u7136\u8d77\u4e86\u4e00\u79cd\u5f02\u6837\u7684\u611f\u89c9\uff0c \u8fd9\u662f\u4ed6\u4ece\u6765\u672a\u66fe\u6709\u8fc7\u7684\u611f\u89c9\u3002 \u3000\u3000\u4ed6\u52aa\u529b\u5730\u5c06\u8fd9\u611f\u89c9\u538b\u5236\u4e86\u4e0b\u53bb\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5c31\u662f\u9601\u4e0b\u3002\u201d \u3000\u3000\u7126\u5f02\u884c\u5389\u58f0\u9053\uff1a\u201c\u670b\u53cb\u8bf4\u8bdd\u53ef\u653e\u6e05\u695a\u4e9b\u3002\u201d \u3000\u3000\u5c1a\u672a\u660e\u9053\uff1a\u201c\u5802\u5802\u5929\u9634\u6559\u4e3b\uff0c\u505a\u4e8b\u53c8\u4f55\u5fc5\u63a8\u4e09\u8bff\u56db\u3002\u201d \u3000\u3000\u4ed6\u8f6c\u8138\u5411\u98de\u9e64\u5b50\u9053\uff1a\u201c\u98de\u9e64\u9053\u5144\uff0c\u8bf7\u770b\u770b\u8fd9\u4f4d\u5929\u9634\u6559\u4e3b\u624b\u4e0a\u7684\u5251\uff0c\u662f\u5426\u5c31\u662f\u65b9\u624d\u5931\u53bb \u7684\u3002\u201d\u8bdd\u58f0\u4e00\u987f\uff0c\u53c8\u51b7\u7b11\u9053\uff1a\u201c\u5236\u4f4f\u90a3\u4e24\u4f4d\u9053\u957f\u7684\u70b9\u7a74\u624b\u6cd5\uff0c\u53ea\u6015\u4e5f\u662f\u5929\u9634\u6559\u7684\u72ec\u95e8\u4f20 \u6388\u3002\u201d \u3000\u3000\u98de\u9e64\u5b50\u9053\uff1a\u201c\u6559\u4e3b\u5c45\u7136\u5728\u6b66\u5f53\u5c71\u4f24\u4eba\u593a\u5251\uff0c\u672a\u514d\u592a\u770b\u4e0d\u8d77\u6211\u6b66\u5f53\u6d3e\u4e86\u3002\u201d \u3000\u3000\u7126\u5f02\u884c\u9053\uff1a\u201c\u9053\u957f\u4f55\u4ee5\u89c1\u5f97\u6211\u5728\u8d35\u5904\u4f24\u4eba\u593a\u5251\uff0c\u96be\u9053\u6709\u4eba\u770b\u5230\u4e86\uff1f\u201d \u3000\u3000\u5c1a\u672a\u660e\u9053\uff1a\u201c\u539f\u6765\u9601\u4e0b\u4e0d\u4f46\u6b66\u529f\u9ad8\u5f3a\uff0c\u5f3a\u8bcd\u593a\u7406\u7684\u529f\u592b\u4e5f\u662f\u9ad8\u4eba\u4e00\u7b49\uff0c\u53ef\u662f\u9601\u4e0b\u624b\u4e2d\u7684 \u8fd9\u67c4\u2018\u8d2f\u65e5\u5251\u2019\uff0c\u5374\u662f\u6700\u597d\u7684\u8bc1\u636e\uff0c\u5374\u4e0d\u5bb9\u9601\u4e0b\u5de7\u8fa9\u3002\u201d \u3000\u3000\u6218\u74a7\u541b\u7b11\u9053\uff1a\u201c\u8d2f\u65e5\u5251\uff1f\u201d \u3000\u3000\u7126\u5f02\u884c\u4ef0\u5929\u957f\u7b11\uff1a\u201c\u8d2f\u65e5\u5251\uff0c\u54c8\uff0c\u54c8\uff0c\u539f\u6765\u8fd9\u67c4\u662f\u8d2f\u65e5\u5251\u3002\u201d \u3000\u3000\u7126\u5f02\u884c\u671d\u98de\u9e64\u5b50\u8d70\u8fd1\u4e86\u4e24\u6b65\uff0c\u5c06\u5251\u67c4\u9012\u5230\u98de\u9e64\u5b50\u773c\u524d\uff0c\u9053\uff1a\u201c\u9053\u957f\u8bf7\u770b\u770b\u8fd9\u67c4\u662f\u4e0d\u662f\u8d2f \u65e5\u5251\uff1f\u201d \u3000\u3000\u98de\u9e64\u5b50\u9053\uff1a\u201c\u9601\u4e0b\u8fd9\u67c4\u5251\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u7126\u5f02\u884c\u9053\uff1a\u201c\u8fd9\u662f\u6c5f\u6e56\u4e0a\u4f20\u95fb\u591a\u5e74\u7684\u2018\u501a\u5929\u5251\u2019\u3002\u201d \u3000\u3000\u98de\u9e64\u5b50\u201c\u5662\u201d\u4e86\u4e00\u58f0\uff0c\u5ffd\u7136\u8eab\u5f62\u4e00\u52a8\uff0c\u5c06\u5251\u4ea4\u7ed9\u4e86\u718a\u501c\u3002 \u3000\u3000\u7126\u5f02\u884c\u5389\u58f0\u9053\uff1a\u201c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u98de\u9e64\u5b50\u9053\uff1a\u201c\u8fd9\u67c4\u5251\u7684\u5251\u67c4\u4e0a\u660e\u660e\u5199\u7684\u662f\u2018\u8d2f\u65e5\u2019\u4e24\u5b57\uff0c\u5f53\u7136\u4e0d\u662f\u9601\u4e0b\u7684\u5251\u4e86\u3002\u201d \u3000\u3000\u7126\u5f02\u884c\u6012\u9053\uff1a\u201c\u4f60\u2026\u2026\u201d\u5c45\u7136\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u8eab\u5f62\u5982\u6d41\u6c34\uff0c\u4fbf\u5411\u718a\u501c\u6251\u53bb\uff0c\u4e00\u8fb9\u559d\u9053\uff1a \u201c\u5c06\u5251\u8fd8\u6211\u3002\u201d \u3000\u3000\u718a\u501c\u771f\u6c14\u731b\u805a\uff0c\u65bd\u5c55\u51fa\u201c\u6f5c\u5f62\u9041\u5f71\u201d\u7684\u8eab\u6cd5\u3002 \u3000\u3000\u7126\u5f02\u884c\u5982\u5f71\u9644\u5f62\uff0c\u8ddf\u4e86\u4e0a\u53bb\uff0c\u7a81\u7136\u773c\u524d\u5251\u5149\u8000\u76ee\uff0c\u539f\u6765\u90a3\u56db\u4e2a\u59cb\u7ec8\u5c79\u7acb\u6ca1\u6709\u4efb\u4f55\u52a8\u4f5c\u7684 \u84dd\u888d\u9053\u4eba\uff0c\u5728\u4ed6\u7684\u8eab\u4e0a\u6392\u8d77\u4e86\u4e00\u9635\u5251\u5f71\u3002 \u3000\u3000\u4ed6\u4e00\u63d0\u6c14\uff0c\u8eab\u5f62\u81ea\u5251\u5149\u4e0a\u98d8\u4e86\u8fc7\u53bb\uff0c\u5374\u89c1\u718a\u501c\u5df2\u7ad9\u5728\u4e00\u5757\u5de8\u77f3\u4e4b\u4e0a\uff0c\u638c\u4e2d\u5149\u534e\u7729\u76ee\uff0c\u5df2 \u5c06\u5251\u64a4\u5728\u624b\u4e0a\u4e86\u3002 \u3000\u3000\u4ed6\u65b9\u624d\u5df2\u91cf\u5ea6\u51fa\u718a\u501c\u6b66\u529f\u7684\u6df1\u6d45\uff0c\u6b64\u65f6\u5012\u4e5f\u4e0d\u6562\u8f7b\u6613\u6251\u4e0a\u53bb\uff0c\u987f\u4f4f\u8eab\u5f62\uff0c\u8138\u4e0a\u7684\u795e\u8272\uff0c \u5927\u5931\u5e38\u6001\uff0c\u518d\u4e5f\u6ca1\u6709\u4e00\u6d3e\u5b97\u4e3b\u7684\u6837\u5b50\u3002 \u3000\u3000\u60f4\u5fd6\u60c5\u51b5\uff0c\u6b66\u5f53\u6d3e\u7684\u9053\u4eba\u5df2\u548c\u718a\u501c\u53ca\u5c1a\u672a\u660e\u7ad9\u5728\u4e00\u8fb9\uff0c\u7c89\u9762\u79e6\u738b\u667a\u9011\u7709\u5fc3\u4e00\u76b1\uff0c\u6717\u58f0\u8bf4 \u9053\uff1a\u201c\u6559\u4e3b\uff0c\u8bf7\u7b49\u4e00\u7b49\u3002\u201d \u3000\u3000\u7c89\u9762\u82cf\u79e6\u6ee1\u9762\u7b11\u5bb9\uff0c\u8d8a\u524d\u4e86\u51e0\u6b65\uff0c\u5411\u98de\u9e64\u5b50\u9053\uff1a\u201c\u8fd9\u67c4\u5251\u679c\u7136\u662f\u2018\u8d2f\u65e5\u2019\u5251\u5417\uff1f\u201d \u3000\u3000\u98de\u9e64\u5b50\u6b63\u8272\u9053\uff1a\u201c\u51fa\u5bb6\u4eba\u7109\u80fd\u8c0e\u8bed\u3002\u201d \u3000\u3000\u7126\u5f02\u884c\u5fc3\u4e2d\u767e\u601d\u4e0d\u89e3\uff1a\u201c\u96be\u9053\u4e16\u4e0a\u771f\u6709\u4e00\u67c4\u548c\u2018\u501a\u5929\u5251\u2019\u540c\u6837\u7684\u5251\uff0c\u90a3\u4e48\u501a\u5929\u5251\u53c8\u843d\u5165 \u8c01\u624b\u5462\uff1f\u201d\u539f\u6765\u4ed6\u5f97\u800c\u53c8\u5931\uff0c\u4e5f\u5c06\u501a\u5929\u5251\u4e22\u4e86\u3002 \u3000\u3000\u718a\u501c\u5927\u610f\u5730\u5c06\u201c\u501a\u5929\u5251\u201d\u9057\u7559\u5728\u8336\u9986\u91cc\uff0c\u54ea\u77e5\u5929\u9634\u6559\u773c\u7ebf\u5bc6\u5e03\uff0c\u5c06\u718a\u501c\u7684\u5305\u88b1\u548c\u201c\u501a\u5929 \u5251\u201d\u5168\u62ff\u8d70\u4e86\u3002 \u3000\u3000\u4e8e\u662f\u8fd9\u67c4\u201c\u501a\u5929\u5251\u201d\u5c31\u7531\u82cf\u5dde\u5206\u8235\uff0c\u53c8\u843d\u5165\u5f53\u5e74\u8fd8\u5728\u6c5f\u5357\u7684\u7126\u5f02\u884c\u624b\u91cc\uff0c\u7ec3\u6b66\u4e4b\u4eba\u54ea\u4e2a \u4e0d\u7231\u540d\u5251\uff0c\u7126\u5f02\u884c\u5f97\u5251\u4e4b\u540e\uff0c\u559c\u4e4b\u4e0d\u80dc\u3002 \u3000\u3000\u5e74\u4f59\u524d\u7126\u5f02\u884c\u4e3a\u4e86\u6269\u5145\u5929\u9634\u6559\u7684\u52bf\u529b\uff0c\u752b\u4e0b\u6c5f\u5357\uff0c\u51c6\u5907\u5c06\u6b66\u6797\u4e2d\u7684\u597d\u624b\uff0c\u4e00\u7f51\u6253\u5c3d\uff0c\u662f \u4ee5\u624d\u6709\u5355\u638c\u65ad\u9b42\u5355\u98de\u4e54\u88c5\u9690\u59d3\uff0c\u5728\u98de\u7075\u5821\u7fa4\u96c4\u4f1a\u4e0a\u7684\u90a3\u4e00\u756a\u4e8b\u8ff9\uff0c\u4f46\u662f\u540e\u6765\u5355\u98de\u7684\u884c\u8e2a\u8d25 \u9732\uff0c\u8fd9\u6d88\u606f\u88ab\u6f5c\u5165\u98de\u7075\u5821\u7684\u5929\u9634\u6559\u5f92\u8f6c\u544a\u7ed9\u7126\u5f02\u884c\u3002 \u3000\u3000\u7126\u5f02\u884c\u77e5\u9053\u98de\u7075\u5821\u7684\u80fd\u624b\u751a\u591a\uff0c\u800c\u5927\u591a\u6570\u90fd\u662f\u5bf9\u5929\u9634\u6559\u6ca1\u6709\u597d\u611f\u7684\uff0c\u4e8e\u662f\u4ed6\u5728\u5821\u5916\u9e23\u9523 \u793a\u8b66\uff0c\u5355\u98de\u624d\u5306\u5306\u8d70\u4e86\u3002 \u3000\u3000\u7126\u5f02\u884c\u592b\u5987\u6f2b\u6e38\u6c5f\u5357\uff0c\u5012\u4e5f\u6536\u7f57\u4e86\u4e0d\u5c11\u6c5f\u6e56\u8c6a\u58eb\uff0c\u53c8\u5f97\u4e86\u4e00\u67c4\u4e45\u9e23\u6c5f\u6e56\u7684\u540d\u5251\uff0c\u6536\u83b7\u53ef \u8c13\u4e0d\u4e30\uff0c\u4ed6\u5026\u6e38\u601d\u5f52\uff0c\u672c\u6b32\u56de\u5c71\u3002 \u3000\u3000\u54ea\u77e5\u9053\u8fd9\u65f6\u5019\u4ed6\u542c\u8bf4\u6b66\u5f53\u6d3e\u7684\u5999\u4e00\u771f\u4eba\u5f97\u4e86\u4e00\u90e8\u5bf9\u4fee\u4e60\u5185\u529f\u6700\u6709\u8865\u76ca\u7684\u5947\u4e66\u3002 \u3000\u3000\u5f53\u5e74\u82cd\u865a\u4e0a\u4eba\u6b66\u529f\u7384\u5999\uff0c\u4f46\u662f\u6240\u4e60\u7684\u5185\u529f\uff0c\u5374\u975e\u7384\u529f\u6b63\u5b97\uff0c\u6b67\u8def\u751a\u591a\uff0c\u662f\u4ee5\u5927\u5927\u963b\u788d\u4e86 \u4ed6\u6b66\u529f\u7684\u8fdb\u5c55\uff0c\u7126\u5f02\u884c\u592b\u5987\u6b66\u529f\u4f20\u81ea\u82cd\u865a\u4e0a\u4eba\uff0c\u81ea\u7136\u548c\u82cd\u865a\u4e0a\u4eba\u4e00\u6837\uff0c\u56e0\u7740\u5185\u529f\u800c\u963b\u788d\u4e86\u6b66 \u529f\u7684\u8fdb\u5c55\uff0c\u6b64\u65f6\u542c\u5230\u6709\u6b64\u5947\u4e66\uff0c\u8d2a\u5fc3\u5927\u8d77\uff0c\u9042\u6b32\u5f97\u4e4b\u800c\u7518\u5fc3\u3002 \u3000\u3000\u4ed6\u8fd9\u624d\u60f3\u5165\u6b66\u5f53\uff0c\u54ea\u77e5\u8d70\u5728\u8def\u4e0a\uff0c\u4ed6\u90a3\u67c4\u201c\u501a\u5929\u5251\u201d\u7adf\u65e0\u58f0\u65e0\u606f\u5730\u5931\u53bb\u4e86\uff0c\u800c\u4e14\u9976\u662f\u5929 \u9634\u6559\u773c\u7ebf\u90a3\u4e48\u591a\uff0c\u5374\u4e5f\u8fde\u4e00\u70b9\u7ebf\u7d22\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u7126\u5f02\u884c\u81ea\u662f\u7591\u4ff1\u4ea4\u52a0\uff0c\u4ed6\u5b9e\u5728\u60f3\u4e0d\u51fa\u6709\u8c01\u6709\u8fd9\u4e48\u5927\u7684\u80c6\u5b50\uff0c\u53c8\u8c01\u6709\u8fd9\u4e48\u597d\u7684\u6b66\u529f\uff0c\u987b\u77e5 \u6562\u81ea\u5929\u9634\u6559\u4e3b\u5904\u5077\u8d70\u90a3\u67c4\u5251\u7684\u4eba\uff0c\u4e0d\u4f46\u6b66\u529f\u4e00\u5b9a\u6df1\u6e5b\uff0c\u80c6\u5b50\u4e5f\u7684\u786e\u5927\u5f97\u60ca\u4eba\u5462\u3002 \u3000\u3000\u54ea\u77e5\u9053\u9ed1\u8863\u4eba\u9b54\u52d2\u548c\u767d\u8863\u9f99\u5973\u4e00\u5165\u6b66\u5f53\u5c71\uff0c\u5c31\u770b\u5230\u6709\u4e24\u4e2a\u5e74\u8f7b\u4eba\u6367\u7740\u5251\u7ad9\u5728\u89e3\u5251\u6cc9\u7554\uff0c \u4ed6\u4e24\u4eba\u672c\u672a\u5728\u610f\uff0c\u8c01\u77e5\u90a3\u4e24\u4e2a\u5e74\u8f7b\u4eba\u5374\u5c06\u5251\u62bd\u4e86\u51fa\u6765\uff0c\u6478\u629a\u89c2\u8d4f\uff0c\u81ea\u662f\u8d5e\u4e0d\u7edd\u53e3\u3002 \u3000\u3000\u4ed6\u4e24\u4eba\u8fd9\u4e00\u62bd\u51fa\u5251\u6765\uff0c\u9ed1\u8863\u9b54\u52d2\u548c\u767d\u8863\u9f99\u5973\u76f8\u987e\u4e00\u60ca\u3002 \u3000\u3000\u4e0d\u7ea6\u800c\u540c\u7684\u5fd6\u9053\uff1a\u201c\u600e\u5730\u5e08\u5085\u9057\u5931\u7684\u5251\uff0c\u7adf\u843d\u5728\u6b66\u5f53\u6d3e\u624b\u91cc\uff1f\u201d\u4ed6\u4eec\u81ea\u7136\u4e5f\u6ca1\u6709\u60f3\u5230\u4e16 \u4e0a\u7adf\u7136\u8fd8\u6709\u4e00\u67c4\u548c\u201c\u501a\u5929\u5251\u201d\u5b8c\u5168\u76f8\u540c\u7684\u5251\u3002 \u3000\u3000\u662f\u4ee5\u4ed6\u4eec\u7a81\u65bd\u715e\u624b\uff0c\u4ee5\u5929\u9634\u6559\u4e00\u8109\u76f8\u4f20\u7684\u72ec\u95e8\u70b9\u7a74\u624b\u6cd5\uff0c\u70b9\u4f4f\u4e86\u90a3\u4e24\u4e2a\u60ca\u6115\u7684\u9053\u4eba\u3002 \u3000\u3000\u8c01\u77e5\u4e8b\u60c5\u7684\u53d1\u5c55\uff0c\u5b8c\u5168\u4e0d\u4f9d\u5bfb\u5e38\u7684\u8f68\u8ff9\uff0c\u4e0d\u7981\u4f7f\u5f97\u7126\u5f02\u884c\u5927\u611f\u610f\u5916\u3002 \u3000\u3000\u7ad9\u5728\u5de8\u77f3\u4e0a\u7684\u718a\u501c\uff0c\u5c06\u638c\u4e2d\u7684\u5251\u7565\u4e00\u821e\u52a8\uff0c\u5e26\u8d77\u4e00\u6e9c\u707f\u94f6\u5149\u534e\uff0c\u5438\u5f15\u4e86\u6bcf\u4e00\u4e2a\u4eba\u7684\u6ce8\u610f \u529b\u3002 \u3000\u3000\u7136\u540e\u4ed6\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u5c31\u7b97\u6211\u624b\u4e0a\u7684\u8fd9\u67c4\u5251\u662f\u501a\u5929\u5251\uff0c\u90a3\u4e5f\u672c\u662f\u5c5e\u6211\u7684\u4e1c\u897f\u3002\u201d \u3000\u3000\u4ed6\u54fc\u4e86\u4e00\u58f0\uff0c\u53c8\u9053\uff1a\u201c\u597d\u4e2a\u81ea\u547d\u4e0d\u51e1\u7684\u5929\u9634\u6559\u4e3b\uff0c\u6084\u6084\u5730\u5077\u4e86\u4eba\u5bb6\u7684\u4e1c\u897f\uff0c\u8fd8\u786c\u8bf4\u662f\u81ea \u5df1\u7684\u3002\u201d\u6218\u74a7\u541b\u5a9a\u76ee\u4e00\u8f6c\uff0c\u54af\u54af\u7b11\u9053\uff1a\u201c\u5537\uff0c\u5e72\u5417\u8fd9\u4e48\u751f\u6c14\u5440\uff0c\u8fd9\u5251\u662f\u4f60\u7684\uff0c\u8fd8\u7ed9\u4f60\u5c31\u662f \u4e86\uff0c\u4f55\u5fc5\u5927\u60ca\u5c0f\u602a\u5462\u3002\u201d \u3000\u3000\u7c89\u9762\u82cf\u79e6\u738b\u667a\u9011\u9053\uff1a\u201c\u6559\u4e3b\u65e2\u7136\u5982\u6b64\u8bf4\uff0c\u8fd9\u67c4\u5251\u5f53\u7136\u662f\u7269\u5f52\u539f\u4e3b\u4e86\u3002\u201d\u53c8\u5411\u98de\u9e64\u4e8e\u62b1\u62f3 \u9053\uff1a\u201c\u5728\u8d35\u5c71\u6253\u6270\u4e86\u8fd9\u4e48\u4e45\uff0c\u53c8\u803d\u8bef\u4e86\u9053\u957f\u4eec\u529f\u8bfe\u7684\u65f6\u95f4\uff0c\u771f\u662f\u62b1\u6b49\u5f97\u5f88\u3002\u201d \u3000\u3000\u4ed6\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u53c8\u9053\uff1a\u201c\u53ea\u662f\u6b64\u4e8b\u539f\u672c\u51fa\u4e8e\u8bef\u4f1a\uff0c\u73b0\u5728\u8bef\u4f1a\u65e2\u7136\u5df2\u7ecf\u89e3\u91ca\u6e05\u695a\u4e86\uff0c\u6211\u4eec \u4fbf\u8981\u544a\u8f9e\u4e86\uff0c\u9053\u957f\u4eec\u81ea\u53bb\u8bf7\u4fee\u5427\u3002\u201d \u3000\u3000\u98de\u9e64\u5b50\u9053\uff1a\u201c\u65bd\u4e3b\u4eec\u81ea\u53bb\u65e0\u59a8\uff0c\u53ea\u662f\u655d\u6559\u8fd9\u4e24\u4e2a\u2026\u2026\u201d \u3000\u3000\u4ed6\u7528\u624b\u6307\u7740\u4ecd\u50f5\u5367\u5728\u89e3\u5251\u6c60\u7554\u7684\u4e24\u4e2a\u9053\u4eba\u3002 \u3000\u3000\u767d\u8863\u9f99\u5973\u53f6\u6e05\u6e05\uff0c\u9ed1\u8863\u6469\u52d2\u767d\u666f\u7965\u8d70\u4e86\u8fc7\u53bb\uff0c\u51fa\u638c\u5982\u98ce\uff0c\u6781\u5feb\u5730\u5728\u90a3\u4e24\u4e2a\u9053\u4eba\u8eab\u4e0a\u62cd\u4e86 \u6570\u638c\uff0c\u90a3\u4e24\u4e2a\u9053\u5165\u4e00\u9635\u6025\u5598\uff0c\u201c\u54b3\u201d\u5730\u4e00\u58f0\uff0c\u5410\u51fa\u4e00\u53e3\u6d53\u75f0\uff0c\u56db\u80a2\u5df2\u80fd\u6d3b\u52a8\u3002 \u3000\u3000\u7126\u5f02\u884c\u5fae\u4e00\u51fb\u638c\uff0c\u9053\uff1a\u201c\u6b64\u95f4\u4e8b\u65e2\u5df2\u4e86\u2026\u2026\u3001\u5c1a\u672a\u660e\u9053\uff1a\u201c\u53ea\u6015\u6b64\u95f4\u4e8b\u8fd8\u672a\u4e86\u3002\u201d \u3000\u3000\u6218\u74a7\u541b\u9053\uff0c\u201c\u5c0f\u5144\u5f1f\uff0c\u8fd8\u6709\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5c1a\u672a\u660e\u6717\u58f0\u9053\uff1a\u201c\u6211\u5927\u54e5\u8fd8\u6709\u67c4\u2018\u501a\u5929\u5251\u2019\uff0c\u4e5f\u5728\u8d35\u6559\u4e3b\u624b\u4e2d\uff0c\u6b64\u65f6\u4e5f\u8be5\u7269\u5f52\u539f\u4e3b \u4e86\u3002\u201d \u3000\u3000\u201c\u5662\uff0c\u539f\u6765\u2018\u501a\u5929\u5251\u2019\u4e5f\u662f\u9601\u4e0b\u7684\u3002\u201d\u7126\u5f02\u884c\u5fc3\u4e2d\u6697\u5730\u53eb\u82e6\uff0c\u53e3\u4e0a\u5374\u4e0d\u613f\u5931\u53bb\u81ea\u5df1\u7684\u5a01 \u98ce\uff0c\u51b7\u7b11\u7740\u9053\uff1a\u201c\u4f46\u662f\u9601\u4e0b\u6709\u4ec0\u4e48\u8bc1\u636e\uff0c\u4e0d\u7136\uff0c\u4efb\u4f55\u4eba\u90fd\u53ef\u4ee5\u8bf4\u5251\u662f\u4ed6\u7684\u4e86\u3002\u201d \u3000\u3000\u5c1a\u672a\u660e\u671b\u7740\u4ed6\uff0c\u5fc3\u4e2d\u7a81\u7136\u6cdb\u8d77\u4e86\u538c\u6076\u7684\u611f\u89c9\uff0c\u90a3\u611f\u89c9\u4e2d\u751a\u81f3\u5e26\u7740\u4e9b\u5ac9\u5992\u7684\u610f\u5473\uff0c\u4f46\u662f\u4ed6 \u81ea\u5df1\u662f\u4e0d\u4f1a\u89c9\u5bdf\u5230\u7684\u3002 \u3000\u3000\u5c31\u56e0\u7740\u8fd9\u4e00\u4efd\u538c\u6076\uff0c\u4f7f\u5f97\u5c1a\u672a\u660e\u53d8\u5f97\u5206\u5916\u66b4\u8e81\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u8bc1\u636e\u5c31\u662f\u6709\uff0c\u4e5f\u4e0d\u80fd\u7ed9\u4f60 \u770b\u3002\u201d\u4ed6\u54fc\u4e86\u4e00\u58f0\uff0c\u53c8\u9053\uff1a\u201c\u5929\u4e0b\u867d\u5927\uff0c\u6211\u8fd8\u6ca1\u6709\u542c\u5230\u8fc7\u5931\u4e3b\u8981\u7ed9\u5c0f\u5077\u770b\u8bc1\u636e\u7684\u9053\u7406\u3002\u201d \u3000\u3000\u7126\u5f02\u884c\u9053\uff1a\u201c\u6211\u7126\u67d0\u4eba\u51fa\u9053\u4ee5\u6765\uff0c\u8fd8\u6ca1\u6709\u4eba\u6562\u5728\u6211\u9762\u524d\u8fd9\u6837\u5f20\u72c2\u7684\uff0c\u6765\uff0c\u6765\uff0c\u670b\u53cb\u65e2\u7136 \u80fd\u8bf4\u8fd9\u79cd\u8bdd\u6765\uff0c\u5fc5\u5b9a\u662f\u4ed7\u7740\u624b\u5e95\u4e0b\u7684\u529f\u592b\uff0c\u6211\u7126\u67d0\u4eba\u4e0d\u624d\uff0c\u5012\u771f\u8981\u9886\u6559\u9886\u6559\u3002\u2018\u98de\u9e64\u5b50\u5ffd\u7136 \u4e00\u58f0\u957f\u5578\uff0c\u8eab\u8eaf\u98d8\u7136\u800c\u8d77\uff0c\u7ad9\u5728\u5c1a\u672a\u660e\u4e0e\u7126\u5f02\u884c\u4e2d\u95f4\u3002\u90a3\u6570\u5341\u4e2a\u6301\u5251\u7684\u84dd\u888d\u9053\u4eba\uff0c\u4e5f\u6574\u6574\u9f50 \u9f50\u5730\u5728\u81ea\u5df1\u548c\u5929\u9634\u6559\u4f17\u7684\u5916\u9762\u8f6c\u4e86\u4e00\u4e2a\u5708\u5b50\uff0c\u6bcf\u4e2a\u4eba\u638c\u4e2d\u7684\u5251\uff0c\u5251\u5c16\u671d\u4e0a\uff0c\u5411\u5916\u659c\u4f38\u3002\u8fd9\u65f6 \u5019\uff0c\u53ea\u6709\u7ad9\u5728\u5de8\u77f3\u4e0a\u7684\u718a\u501c\uff0c\u662f\u5728\u8fd9\u5708\u5b50\u5916\u9762\uff0c\u4ed6\u5c45\u4e0a\u4e34\u4e0b\uff0c\u770b\u5230\u8fd9\u4e9b\u9053\u4eba\u56db\u4eba\u4e00\u7ec4\uff0c\u5171\u5b58 \u4e09\u5341\u56db\u4eba\uff0c\u7adf\u662f\u4f9d\u7740\u516b\u5366\u65b9\u4f4d\u800c\u7ad9\uff0c\u518d\u52a0\u4e0a\u98de\u9e64\u5b50\uff0c\u6b63\u662f\u4e38\u5bab\u516b\u5366\u9635\u5f0f\u7684\u65b9\u4f4d\u3002\u8fd9\u6837\u4e00\u6765\uff0c \u60c5\u52bf\u53c8\u53d8\uff0c\u7adf\u50cf\u5929\u9634\u6559\u548c\u5c1a\u672a\u660e\u8054\u624b\uff0c\u800c\u6b66\u5f53\u6d3e\u5374\u662f\u53e6\u4e00\u8fb9\u4e86\u3002\u98de\u9e64\u5b50\u76ee\u5149\u95ea\u52a8\uff0c\u50cf\u662f\u5728\u60f3 \u8bf4\u8bdd\uff0c\u53c8\u4e0d\u77e5\u8be5\u600e\u4e48\u63aa\u8bcd\u7684\u6837\u5b50\u3002\u5374\u6709\u4e00\u4e2a\u84dd\u888d\u9053\u4eba\uff0c\u5df2\u6717\u58f0\u9053\uff1a\u201c\u65bd\u4e3b\u4eec\u79c1\u4e0b\u82e5\u6709\u6069\u6028\uff0c \u5c31\u8bf7\u5230\u4e86\u5c71\u5916\u518d\u8f83\u91cf\u3002\u201d\u98de\u9e64\u5b50\u63a5\u53e3\u9053\uff1a\u201c\u65bd\u4e3b\u4eec\u79c1\u4e0b\u7684\u4e8b\uff0c\u65e2\u7136\u4e0e\u655d\u6d3e\u65e0\u5173\uff0c\u655d\u6d3e\u4e5f\u4e0d\u613f \u53c2\u4e0e\uff0c\u8bf7\u5404\u4f4d\u5c31\u6b64\u4e0b\u5c71\u5427\u3002\u201d \u3000\u3000\u5c1a\u672a\u660e\u4e0e\u7126\u5f02\u884c\u4e00\u58f0\u6012\u53f1\uff0c\u53cc\u638c\u4e00\u7ffb\uff0c\u9519\u8fc7\u98de\u9e64\u5b50\uff0c\u5c31\u60f3\u52a8\u624b\u3002 \u3000\u3000\u4ee5\u4ed6\u4e24\u4eba\u8fd9\u79cd\u8eab\u624b\uff0c\u82e5\u7136\u53d1\u52a8\uff0c\u8fd8\u6709\u8c01\u80fd\u963b\u6b62\u5f97\u5f00\uff0c\u5c1a\u672a\u660e\u624b\u6325\u4e94\u5f26\uff0c\u76ee\u9001\u98de\u9e64\uff0c\u6781\u4e3a \u6ede\u6d12\u5730\u5c55\u5f00\u201c\u585e\u5916\u98de\u82b1\u4e09\u5343\u5f0f\u201d\uff0c\u4ed6\u6ee1\u8179\u6012\u6c14\uff0c\u4e00\u51fa\u624b\u4fbf\u81ea\u4e0d\u540c\uff0c\u638c\u5f71\u7f24\u7eb7\uff0c\u8fde\u73af\u62cd\u51fa\u6570 \u638c\u3002 \u3000\u3000\u7126\u5f02\u884c\u9886\u5bfc\u5929\u9634\u6559\uff0c\u6b66\u529f\u81ea\u662f\u8d85\u7edd\uff0c\u53cc\u5708\u5316\u4e86\u4e2a\u534a\u5708\uff0c\u6839\u672c\u4e0d\u7406\u4f1a\u5c1a\u672a\u660e\u7684\u90a3\u79cd\u7e41\u590d\u7684 \u865a\u62db\uff0c\u53f3\u65f6\u4e00\u6c89\uff0c\u5de6\u638c\u75be\u8d77\uff0c\u4e24\u4eba\u77ac\u5373\u62c6\u4e86\u4e09\u638c\u3002 \u3000\u3000\u98de\u9e64\u5b50\u7709\u5fc3\u4e00\u76b1\uff0c\u4e00\u58f0\u957f\u5578\uff0c\u4e09\u5341\u4e8c\u4e2a\u84dd\u888d\u9053\u957f\u638c\u4e2d\u7684\u957f\u5251\uff0c\u4e00\u9f50\u53d1\u52a8\u3002 \u3000\u3000\u970e\u65f6\u95f4\u5149\u534e\u6f2b\u5728\uff0c\u8fdc\u8fdc\u7ad9\u7740\u7684\u516b\u4e2a\u62fe\u7740\u5c71\u8f7f\u7684\u5929\u9634\u6559\u5f92\uff0c\u53ea\u89c9\u5f97\u4eff\u4f5b\u662f\u4e00\u4e2a\u6781\u5927\u7684\u5149 \u5e62\uff0c\u88ab\u65e5\u5149\u4e00\u6620\uff0c\u66f4\u662f\u5f69\u8272\u7f24\u7eb7\uff0c\u597d\u770b\u5df2\u6781\u3002 \u3000\u3000\u5149\u5e62\u5185\u9664\u4e86\u98de\u9e64\u5b50\u4ee5\u53ca\u6b63\u5728\u52a8\u7740\u624b\u7684\u7126\u5f02\u884c\uff0c\u5c1a\u672a\u660e\u4e4b\u5916\uff0c\u8fd8\u6709\u5c1a\u6587\u658c\u3001\u9f9a\u5929\u6770\u3001\u738b\u667a \u9011\u3001\u6c5f\u6dd1\u4ed9\uff0c\u4ee5\u53ca\u6570\u5341\u4e2a\u5929\u9634\u6559\u4e0b\u7684\u8235\u4e3b\uff0c\u6b66\u5f53\u9053\u4eba\u7684\u5251\u9635\u4e00\u53d1\u52a8\uff0c\u7adf\u7136\u4e0d\u5206\u7682\u767d\u9752\u7ea2\u5730\u5251 \u70b9\u4e71\u64a4\uff0c\u4e0d\u8bba\u662f\u8c01\uff0c\u90fd\u671d\u4ed6\u8eab\u4e0a\u62db\u547c\uff0c\u738b\u667a\u9011\u5fc3\u4e2d\u4e00\u6025\uff0c\u6697\u5fd6\uff1a\u201c\u771f\u7cdf\u3002\u201d\u5251\u5149\u4e00\u63a0\uff0c\u5df2\u6709 \u4e00\u67c4\u5251\u671d\u4ed6\u8eab\u4e0a\u523a\u6765\u3002 \u3000\u3000\u4e8e\u662f\u5929\u9634\u6559\u4e0b\u7684\u6bcf\u4e00\u4e2a\u4eba\uff0c\u4e5f\u53ea\u6709\u62bd\u51fa\u5175\u5203\uff0c\u5c55\u5f00\u6df7\u6218\uff0c\u4f46\u662f\u8fd9\u4e9b\u6b66\u5f53\u9053\u4eba\u7684\u5251\u9635\uff0c\u50cf \u662f\u5e73\u65e5\u8bad\u7ec3\u6709\u7d20\uff0c\u5251\u62db\u4e0e\u5251\u62db\u95f4\uff0c\u914d\u5408\u5f97\u5f02\u5e38\u4f73\u5999\uff0c\u8ff8\u9000\u4e5f\u662f\u6309\u7740\u516b\u5366\u65b9\u4f4d\uff0c\u8fd9\u4e09\u5341\u4e8c\u4e2a\u84dd \u888d\u9053\u4eba\u6b66\u529f\u867d\u4e0d\u751a\u9ad8\uff0c\u4f46\u56e0\u6b64\u4e00\u6765\uff0c\u5a01\u529b\u4f55\u6b62\u589e\u52a0\u4e86\u4e00\u500d\u3002 \u3000\u3000\u6218\u74a7\u541b\u5a07\u7b11\u8fde\u8fde\uff0c\u50cf\u7a7f\u82b1\u7684\u8774\u8776\uff0c\u5728\u5251\u9635\u4e2d\u98d8\u98d8\u98de\u821e\u3002 \u3000\u3000\u9ed1\u715e\u9b54\u638c\u5c1a\u6587\u658c\u5c79\u7acb\u5982\u5c71\uff0c\u638c\u98ce\u864e\u864e\uff0c\u521b\u5149\u5230\u4e86\u4ed6\u8eab\u4fa7\uff0c\u90fd\u88ab\u8f7b\u6613\u5730\u5316\u5f00\u4e86\u53bb\u3002 \u3000\u3000\u9ed1\u8863\u9b54\u52d2\uff0c\u767d\u8863\u9f99\u5973\uff0c\u7adf\u624b\u643a\u7740\u624b\uff0c\u50cf\u662f\u4e24\u53ea\u8fde\u636e\u98de\u7fd4\u7684\u71d5\u5b50\uff0c\u6781\u4e3a\u8f7b\u6613\u5730\u5316\u89e3\u7740\u5251 \u62db\uff0c\u59ff\u52bf\u8eab\u6cd5\u66fc\u5999\u65e0\u6bd4\u3002 \u3000\u3000\u4f46\u662f\u98de\u9e64\u5b50\u5c45\u4e2d\u7b56\u5e94\uff0c\u8eab\u5f62\u56db\u4e0b\u6d41\u8d70\uff0c\u8fd9\u4e9b\u9ad8\u624b\u4eec\u975e\u4f46\u65e0\u6cd5\u7834\u53bb\u8fd9\u5251\u9635\uff0c\u800c\u4e14\u7247\u523b\u4e4b \u95f4\uff0c\u5929\u9634\u6559\u4e0b\u7684\u4e24\u4e2a\u8f83\u5f31\u7684\u5206\u8235\u8235\u4e3b\uff0c\u5df2\u88ab\u5251\u4f24\uff0c\u4e00\u4e2a\u80a9\u5934\u8840\u6d41\u5982\u6ce8\uff0c\u4e00\u4e2a\u808b\u4e0b\u4e2d\u5251\uff0c\u5df2\u7ecf \u8eba\u5728\u5730\u4e0a\u3002 \u3000\u3000\u738b\u667a\u9011\u5fc3\u4e2d\u5ffd\u7136\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u201c\u6211\u4eec\u82e5\u56f4\u6210\u4e00\u4e2a\u5708\u5b50\uff0c\u5927\u5bb6\u9762\u90e8\u5411\u5916\uff0c\u5bf9\u4ed8\u8fd9\u5251\u9635\u5c82\u4e0d \u5927\u5999\u3002\u201d\u773c\u89d2\u52a8\u5904\uff0c\u671b\u89c1\u98de\u9e64\u5b50\u5de6\u51fb\u4e00\u638c\uff0c\u53f3\u70b9\u4e00\u6307\uff0c\u8eab\u5f62\u98d8\u5ffd\uff0c\u6697\u4e2d\u4e0d\u7981\u53eb\u82e6\uff1a\u201c\u8fd9\u6837\u4e5f \u4f7f\u4e0d\u5f97\uff0c\u4ed6\u4eec\u5251\u5708\u91cc\uff0c\u8fd8\u6709\u4e00\u4e2a\u6b66\u529f\u6700\u5f3a\u7684\u4eba\u3002\u201d \u3000\u3000\u718a\u501c\u7ad9\u5728\u5de8\u77f3\u4e0a\uff0c\u671b\u7740\u8fd9\u4e00\u573a\u522b\u5f00\u751f\u9762\u7684\u6df7\u6218\uff0c\u6700\u5999\u7684\u662f\u6709\u65f6\u660e\u660e\u4e00\u5251\u523a\u5411\u5c1a\u672a\u660e\uff0c\u4e0d \u77e5\u600e\u7684\uff0c\u7126\u5f02\u884c\u5374\u66ff\u4ed6\u89e3\u4e86\u8fd9\u62db\uff0c\u5c1a\u672a\u660e\u7684\u4e00\u638c\u62cd\u5411\u7126\u5f02\u884c\u65f6\uff0c\u4e5f\u4f1a\u4e2d\u9014\u8f6c\u53d8\u65b9\u5411\uff0c\u5288\u5411\u4e00 \u4e2a\u6b66\u5f53\u9053\u4eba\uff0c\u4e4d\u4e00\u89c1\u6b64\uff0c\u771f\u770b\u4e0d\u5230\u5176\u4e2d\u6709\u4f55\u7384\u5999\u3002 \u3000\u3000\u4f46\u662f\u718a\u501c\u5bf9\u8fd9\u4e9b\uff0c\u975e\u4f46\u4e0d\u80fd\u62b1\u7740\u6b23\u8d4f\u7684\u6001\u5ea6\uff0c\u5fc3\u91cc\u53cd\u800c\u7740\u6025\u4e07\u5206\uff0c\u6697\u6697\u62c5\u5fc3\u7740\u5c1a\u672a\u660e\u7684 \u5b89\u5168\uff0c\u4f46\u60f3\u6765\u60f3\u53bb\uff0c\u4e5f\u6beb\u65e0\u4ed6\u6cd5\u89e3\u6551\uff0c\u4ed6\u6697\u5fd6\uff1a\u201c\u6211\u82e5\u6b64\u523b\u5728\u5916\u9762\u51fb\u7834\u8fd9\u4e9b\u6b66\u5f53\u9053\u4eba\u7684\u5251 \u9635\uff0c\u539f\u4e5f\u53ef\u80fd\uff0c\u53ea\u662f\u8fd9\u4e48\u4e00\u6765\uff0c\u53cd\u6210\u4e86\u6211\u66ff\u5929\u9634\u6559\u5f92\u89e3\u56f4\uff0c\u53c8\u52bf\u5fc5\u8981\u548c\u6b66\u5f53\u6d3e\u7ed3\u4e0b\u6df1\u4ec7\uff0c\u4f46 \u662f\u6211\u82e5\u7f6e\u8eab\u4e8b\u5916\uff0c\u4e8c\u5f1f\u6b64\u523b\u7684\u60c5\u52bf\uff0c\u5374\u662f\u5371\u9669\u5df2\u6781\uff0c\u8fd9\u771f\u53eb\u6211\u4e3a\u96be\u5f97\u5f88\u3002\u201d \u3000\u3000\u98de\u9e64\u5b50\u53c8\u662f\u4e00\u58f0\u957f\u5578\uff0c\u90a3\u5251\u9635\u7a81\u7136\u8f6c\u52a8\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8fd9\u4e48\u4e00\u6765\uff0c\u5149\u5e62\u91cc\u7684\u4eba\u60c5\u5f62\u66f4\u662f\u5371\u6025\uff0c\u5c24\u5176\u662f\u7126\u5f02\u884c\uff0c\u5c1a\u672a\u660e\u800c\u4eba\uff0c\u9664\u4e86\u5f7c\u6b64\u5f97\u4e92\u76f8\u7559 \u610f\u7740\u5bf9\u65b9\u7684\u62db\u5f0f\u5916\uff0c\u8fd8\u5f97\u5e94\u4ed8\u90a3\u4e09\u5341\u4e8c\u4e2a\u6b66\u5f53\u84dd\u888d\u9053\u5165\u624b\u4e2d\u7684\u4e09\u5341\u4e8c\u67c4\u5251\u8fde\u7ef5\u4e0d\u65ad\u7684\u62db\u5f0f\u3002 \u3000\u3000\u56db\u5341\u51e0\u4e2a\u7167\u9762\u4e0b\u6765\uff0c\u5c1a\u672a\u660e\u5df2\u6e10\u611f\u4e0d\u652f\uff0c\u65b9\u624d\u4ed6\u548c\u9ed1\u715e\u9b54\u638c\u5c1a\u6587\u658c\u5bf9\u4e86\u4e24\u638c\uff0c\u771f\u6c14\u5df2\u5fae \u53d7\u635f\uff0c\u4f55\u51b5\u4ed6\u529f\u529b\u672c\u5c31\u4e0d\u53ca\u7126\u5f02\u884c\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u989d\u89d2\uff0c\u9f3b\u4fa7\u5f00\u59cb\u6c81\u51fa\u4e86\u4e9b\u6c57\u73e0\uff0c\u4f46\u662f\u4e00\u79cd\u5f02\u4e8e\u5bfb\u5e38\u7684\u52c7\u6c14\u4ecd\u652f\u6301\u7740\u4ed6\uff0c\u4e00\u65f6\u534a\u523b \u4e4b\u95f4\uff0c\u4e5f\u4e0d\u81f4\u843d\u8d25\u3002 \u3000\u3000\u7126\u5f02\u884c\u662f\u4f55\u7b49\u89d2\u8272\uff0c\u5bf9\u4ed6\u8fd9\u79cd\u5916\u529b\u5185\u5728\u7684\u60c5\u51b5\uff0c\u54ea\u4f1a\u770b\u4e0d\u51fa\u6765\uff0c\u638c\u4e0a\u518d\u53d1\u6325\u4e86\u5341\u4e8c\u5206\u7684 \u529f\u529b\uff0c\u51b3\u5fc3\u5c06\u8fd9\u4e2a\u5fc3\u9ad8\u6c14\u50b2\u7684\u5bf9\u624b\uff0c\u8d25\u5728\u638c\u4e0b\u3002 \u3000\u3000\u718a\u501c\u76ee\u5149\u968f\u7740\u5c1a\u672a\u660e\u7684\u8eab\u5f62\u6253\u8f6c\uff0c\u89c1\u4ed6\u5df2\u5fc3\u4f59\u529b\u62d9\uff0c\u5fc3\u4e2d\u7684\u7126\u6025\uff0c\u751a\u81f3\u8fd8\u5728\u5c1a\u672a\u660e\u81ea\u5df1 \u4e4b\u4e0a\u3002 \u3000\u3000\u65e5\u5df2\u897f\u659c\uff0c\u718a\u501c\u4e00\u4f4e\u5934\uff0c\u9633\u5149\u81ea\u5251\u9635\u53cd\u5c04\u5230\u4ed6\u7684\u5251\u4e0a\u3002 \u3000\u3000\u4ed6\u4e00\u54ac\u7259\uff0c\u6697\u5fd6\uff1a\u201c\u8bf4\u4e0d\u5f97\u53ea\u6709\u5982\u6b64\u4e86\u3002\u201d\u771f\u6c14\u731b\u63d0\uff0c\u7626\u524a\u7684\u8eab\u8eaf\uff0c\u51b2\u5929\u800c\u4e0a\uff0c\u5fae\u4e00\u8f6c \u6298\uff0c\u5251\u5149\u5982\u8679\uff0c\u5411\u6b66\u5f53\u9053\u58eb\u6240\u5e03\u7684\u5251\u9635\u964d\u4e0b\u3002 \u3000\u3000\u4ed6\u6781\u4e3a\u5c0f\u5fc3\u5730\u9009\u62e9\u4e86\u4e00\u4e2a\u6700\u9002\u5f53\u7684\u4f4d\u7f6e\uff0c\u4e00\u5251\u523a\u4e0b\uff0c\u201c\u556e\u8e09\u201d\u4e00\u58f0\uff0c\u4e00\u4e2a\u84dd\u888d\u9053\u4eba\u638c\u4e2d \u7684\u5251\uff0c\u5df2\u7ecf\u88ab\u4ed6\u524a\u65ad\u4e86\u3002 \u3000\u3000\u501f\u7740\u53cc\u5251\u76f8\u4ea4\u65f6\u7684\u90a3\u4e00\u4efd\u529b\u91cf\uff0c\u4ed6\u671d\u5de6\u4e0a\u65b9\u53c8\u62d4\u8d77\u4e86\u5bf8\u8bb8\uff0c\u957f\u5251\u518d\u4e00\u4e0b\u63a0\uff0c\u53c8\u662f\u4e00\u67c4\u5251 \u65ad\u4e86\uff0c\u4ed6\u53c8\u501f\u7740\u8fd9\u4e00\u51fb\u4e4b\u529b\uff0c\u5347\u8d77\u5c3a\u8bb8\u3002 \u3000\u3000\u6b66\u5f53\u9053\u4eba\u7684\u5251\u9635\u672c\u662f\u7531\u5de6\u800c\u53f3\u5730\u5728\u8f6c\u52a8\u7740\uff0c\u9635\u6cd5\u7684\u8fd0\u8f6c\uff0c\u5feb\u5f97\u60ca\u4eba\u3002 \u3000\u3000\u718a\u501c\u5374\u662f\u7531\u53f3\u800c\u5de6\uff0c\u671d\u76f8\u53cd\u7684\u65b9\u5411\u8fce\u4e86\u4e0a\u53bb\uff0c\u4ee5\u6781\u5de7\u5999\u7684\u5251\u62db\uff0c\u77ac\u606f\u4e4b\u95f4\uff0c\u4fbf\u6709\u5341\u6570\u4e2a \u84dd\u888d\u9053\u4eba\u638c\u4e2d\u7684\u5251\uff0c\u5df2\u88ab\u524a\u65ad\u3002 \u3000\u3000\u5251\u9635\u56e0\u6b64\u800c\u663e\u51fa\u96f6\u4e71\uff0c\u800c\u7ec8\u4e8e\u505c\u6ce8\u4e86\uff0c\u4e0d\u518d\u7ee7\u7eed\u8f6c\u52a8\u3002 \u3000\u3000\u6bcf\u4e00\u4e2a\u89c1\u4e86\u718a\u501c\u8fd9\u4eba\u60ca\u4e16\u9a87\u4fd7\u7684\u6b66\u529f\uff0c\u90fd\u60ca\u5f02\u5f97\u751a\u81f3\u8131\u53e3\u8d5e\u8d77\u597d\u6765\uff0c\u5c31\u8fde\u5929\u9634\u6559\u91cc\u7684\u8c6a \u58eb\uff0c\u4e5f\u90fd\u88ab\u8fd9\u79cd\u795e\u97f5\u7684\u6b66\u529f\u6240\u76ee\u7729\u4e86\u3002 \u3000\u3000\u718a\u501c\u518d\u6b21\u4e00\u98de\u51b2\u5929\uff0c\u53cc\u811a\u4e92\u6263\uff0c\u5de7\u5999\u5730\u53f3\u8eab\u8eaf\u5fae\u5fae\u4e0b\u6c89\uff0c\u6362\u4e86\u4e00\u53e3\u6c14\uff0c\u53f3\u81c2\u731b\u5f20\uff0c\u8eab\u5f62 \u518d\u4e00\u8f6c\u6298\u63a0\u4e0b\uff0c\u201c\u6f2b\u5929\u661f\u6597\u201d\u5251\u5149\u5982\u70b9\u94f6\u661f\uff0c\u6eda\u5411\u5251\u5708\u91cc\u7684\u5929\u9634\u6559\u4e0b\u7684\u9053\u58eb\u3002 \u3000\u3000\u4ed6\u7adf\u4e0d\u8003\u8651\u5730\u8fd0\u7528\u7740\u4ed6\u6240\u77e5\u9053\u7684\u6700\u6bd2\u8fa3\u7684\u62db\u5f0f\uff0c\u8033\u4e2d\u542c\u5230\u4e8c\u58f0\u60e8\u547c\uff0c\u4ed6\u671b\u90fd\u6ca1\u6709\u518d\u671b\u4e00 \u773c\uff0c\u201c\u4e91\u5982\u51fa\u6d8c\u201d\uff0c\u5251\u8eab\u5fae\u53d8\u65b9\u5411\uff0c\u545b\u7136\u4e00\u58f0\u957f\u9e23\uff0c\u9f9a\u5929\u6770\u638c\u4e2d\u767e\u70bc\u7cbe\u94a2\u6253\u5c31\u7684\u5434\u94a9\u5251\uff0c\u5df2 \u88ab\u524a\u65ad\u4e86\u3002 \u3000\u3000\u63a5\u7740\uff0c\u4ed6\u89c9\u5f97\u773c\u524d\u5251\u5149\u6d41\u52a8\uff0c\u6839\u672c\u65e0\u6cd5\u77e5\u9053\u718a\u501c\u7684\u5251\uff0c\u7a76\u7adf\u662f\u671d\u54ea\u4e00\u4e2a\u65b9\u5411\u523a\u6765\u3002 \u3000\u3000\u731b\u5730\u671d\u5730\u4e0a\u4e00\u6eda\uff0c\u5434\u94a9\u5251\u9f9a\u5929\u6770\u518d\u4e5f\u4e0d\u987e\u8eab\uff0c\u4f46\u7eb5\u7136\u4ed6\u8fd9\u4e48\u52aa\u529b\u5730\u4f01\u671b\u80fd\u591f\u907f\u5343\u6b64\u62db\uff0c \u53f3\u811a\u4e0a\u4ecd\u7136\u88ab\u5212\u4e86\u957f\u957f\u4e00\u9053\u53e3\u5b50\uff0c\u5012\u5728\u5730\u4e0a\uff0c\u5931\u53e3\u800c\u547c\uff0c\u7389\u89c2\u97f3\u592b\u5987\u8fde\u5fc3\uff0c\u5fd9\u53cd\u63a0\u8fc7\u6765\uff0c\u63a2 \u67e5\u4f24\u52bf\u3002 \u3000\u3000\u718a\u501c\u7b2c\u4e00\u6b21\u4f7f\u7528\u8fd9\u4e48\u6bd2\u8fa3\u7684\u65b9\u6cd5\uff0c\u8fd9\u4e00\u51fb\u4e4b\u540e\uff0c\u6beb\u4e0d\u505c\u7559\uff0c\u5251\u5149\u4e00\u95ea\uff0c\u770b\u89c1\u5251\u4e0b\u90a3\u5f20\u5e26 \u7740\u60ca\u4ff1\u7684\u9762\u5b54\uff0c\u5374\u662f\u7c89\u9762\u82cf\u79e6\u738b\u667a\u9011\u7684\uff0c\u60f3\u8d77\u4ece\u524d\u7684\u90a3\u4e00\u4e1d\u201c\u60c5\u4efd\u201d\uff0c\u5251\u5c16\u4e00\u8f6f\uff0c\u81ea\u4ed6\u8138\u65c1 \u6ed1\u5f00\u3002 \u3000\u3000\u718a\u501c\u518d\u4e00\u7eb5\u8eab\uff0c\u770b\u5230\u9ed1\u715e\u9b54\u638c\u9762\u5bd2\u5982\u6c34\uff0c\u6b63\u5411\u4ed6\u63a0\u6765\u3002 \u3000\u3000\u4ed6\u672c\u4e0d\u613f\u5728\u6b64\u7f20\u6218\uff0c\u8eab\u968f\u5251\u8d70\uff0c\u5251\u52a8\u5982\u98ce\uff0c\u659c\u659c\u4e00\u5251\uff0c\u201c\u5317\u6597\u79fb\u8fb0\u201d\uff0c\u524a\u5411\u8fde\u638c\u8fc5\u901f\u7684 \u7126\u5f02\u884c\u3002 \u3000\u3000\u7b49\u5230\u7126\u5f02\u884c\u64a4\u638c\u56de\u8eab\uff0c\u9519\u6b65\u81ea\u4fdd\u7684\u65f6\u5019\uff0c\u4ed6\u75be\u4f38\u5de6\u624b\uff0c\u4e00\u628a\u62c9\u4f4f\u5c1a\u672a\u660e\uff0c\u4f4e\u559d\u9053\uff1a\u201c\u5feb \u8d70\u3002\u201d\u8eab\u968f\u58f0\u52a8\uff0c\u65bd\u5c55\u5f00\u6f5c\u5f62\u9041\u5f71\u7684\u8eab\u6cd5\uff0c\u5de6\u624b\u7528\u529b\u62c9\u7740\u5c1a\u672a\u660e\uff0c\u604d\u773c\u800c\u6ca1\u3002 \u3000\u3000\u5728\u6781\u77ed\u7684\u4e00\u5239\u90a3\u91cc\uff0c\u718a\u501c\u4ee5\u65e0\u6bd4\u7684\u901f\u5ea6\u548c\u8eab\u6cd5\uff0c\u7528\u51fa\u82cd\u7a79\u5341\u4e09\u5f0f\u91cc\u6700\u7cbe\u5999\u7684\u62db\u5f0f\uff0c\u6781\u5feb \u5730\u81ea\u8bb8\u591a\u9ad8\u624b\u4e2d\uff0c\u62c9\u51fa\u5c1a\u672a\u660e\u3002 \u3000\u3000\u5728\u7126\u5f02\u884c\u5fc6\u8d77\u4ed6\u8be5\u8ffd\u8d76\u4ee5\u524d\uff0c\u718a\u501c\u548c\u5c1a\u672a\u660e\u5df2\u6d88\u5931\u5728\u7fa4\u5c71\u91cc\u3002 \u3000\u3000\u7fa4\u5c71\u4f9d\u65e7\uff0c\u6d41\u6c34\u5982\u6545\uff0c\u9664\u4e86\u5730\u4e0a\uff0c\u5e73\u6dfb\u4e86\u51e0\u6ee9\u8840\u8ff9\u4e4b\u5916\uff0c\u4e00\u5207\u90fd\u6beb\u65e0\u53d8\u5316\u3002 \u3000\u3000\u590f\u82b8\u4ee5\u8fc7\u4eba\u7684\u673a\u667a\uff0c\u9a97\u8fc7\u4e86\u9a84\u72c2\u81ea\u5927\u7684\u82cd\u7384\u3001\u82cd\u8346\uff0c\u9003\u51fa\u6b66\u5f53\u5c71\u3002 \u3000\u3000\u5979\u5185\u4f24\u5c1a\u672a\u75ca\u6108\uff0c\u80f8\u8179\u4e4b\u95f4\u4e00\u9635\u9635\u5730\u89c9\u5f97\u65e0\u6bd4\u7684\u75bc\u75db\u3002 \u3000\u3000\u56db\u91ce\u866b\u58f0\u7785\u7136\uff0c\u677e\u6d9b\u88ab\u5c71\u98ce\u5439\u5f97\u53d1\u51fa\u4e00\u79cd\u9e23\u54bd\u822c\u7684\u58f0\u97f3\uff0c\u4e00\u9635\u51e4\u5439\u6765\uff0c\u590f\u82b8\u673a\u4f36\u4f36\u5730\u6253 \u4e86\u4e2a\u5bd2\u5664\uff0c\u5fc3\u91cc\u89c9\u5f97\u6709\u4e9b\u5bb3\u6015\u3002 \u3000\u3000\u597d\u5bb9\u6613\uff0c\u9003\u5230\u5c71\u4e0b\uff0c\u7ecf\u8fc7\u8fd9\u4e00\u756a\u52c9\u5f3a\u7684\u5954\u9a70\uff0c\u80f8\u53e3\u75bc\u5f97\u66f4\u662f\u96be\u53d7\uff0c\u591c\u9732\u6cbe\u5230\u8863\u4e0a\uff0c\u5979\u89c9 \u5f97\u6709\u4e9b\u51b7\uff0c\u8179\u4e2d\u7a7a\u7a7a\uff0c\u53c8\u89c9\u5f97\u6709\u4e9b\u997f\u3002 \u3000\u3000\u4f46\u662f\u6b64\u5730\u8352\u91ce\u5bc2\u7136\uff0c\u54ea\u91cc\u627e\u5f97\u5230\u4efb\u4f55\u4e00\u79cd\u5979\u6240\u9700\u8981\u7684\u4e1c\u897f\uff0c\u5979\u53ea\u5f97\u53c8\u52c9\u5f3a\u5730\u6323\u624e\u7740\u671d\u524d \u9762\u8d70\uff0c\u5e0c\u671b\u80fd\u627e\u5230\u4e00\u4e2a\u5c71\u811a\u4e0b\u4f4f\u7684\u597d\u5fc3\u4eba\u5bb6\u3002 \u3000\u3000\u5934\u4e5f\u5f00\u59cb\u4e00\u9635\u9635\u5730\u6655\u6697\u8d77\u6765\uff0c\u5979\u51e0\u4e4e\u518d\u4e5f\u652f\u6301\u4e0d\u4f4f\u3002 \u3000\u3000\u731b\u4e00\u62ac\u5934\uff0c\u5ffd\u7136\u770b\u5230\u524d\u9762\u5c45\u7136\u6709\u706f\u5149\uff0c\u8fd9\u4e00\u4e1d\u65b0\u751f\u7684\u5e0c\u671b\uff0c\u7acb\u523b\u4f7f\u5979\u589e\u52a0\u4e86\u4e0d\u5c11\u529b\u6c14\uff0c \u5c45\u7136\u65bd\u5c55\u5f00\u8f7b\u529f\uff0c\u671d\u524d\u9762\u63a0\u53bb\u3002 \u3000\u3000\u8fdc\u8fdc\u5730\u5c31\u542c\u5230\u90a3\u95f4\u6709\u706f\u5149\u7684\u5c0f\u5c4b\u91cc\uff0c\u53d1\u51fa\u4e00\u9635\u9635\u63a8\u52a8\u77f3\u78e8\u7684\u58f0\u97f3\uff0c\u539f\u6765\u90a3\u662f\u95ee\u5c71\u8def\u8fb9\u7684 \u8c46\u6d46\u5e97\uff0c\u4e13\u95e8\u505a\u6e05\u6668\u4e0a\u5c71\u7684\u9999\u5ba2\u7684\u751f\u610f\u7684\u3002 \u3000\u3000\u53c8\u997f\u53c8\u5bd2\u53c8\u6e34\u7684\u590f\u82b8\uff0c\u60f3\u5230\u6eda\u70ed\u7684\u8c46\u6d46\u88ab\u559d\u8fdb\u5634\u91cc\u7684\u90a3\u79cd\u8212\u9002\u7684\u611f\u89c9\uff0c\u7cbe\u795e\u66f4\u662f\u5927\u632f\uff0c \u4e09\u6b65\u5e76\u505a\u4e24\u6b65\uff0c\u8d70\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u78e8\u8c46\u6d46\u7684\u662f\u4e00\u4e2a\u7761\u773c\u60fa\u677e\u7684\u8001\u5934\u5b50\uff0c\u767d\u53d1\u87e0\u7136\uff0c\u8eab\u4f53\u867d\u7136\u8fd8\u5f88\u786c\u6717\uff0c\u4f46\u662f\u518d\u4e5f\u63a9\u9970\u4e0d\u4f4f \u5c81\u6708\u7684\u6d88\u5931\u6240\u5e26\u7ed9\u4ed6\u7684\u82cd\u8001\u3002 \u3000\u3000\u8fd8\u6709\u4e00\u4e2a\u5e74\u7eaa\u548c\u4ed6\u76f8\u4eff\u7684\u8001\u592a\u5a46\uff0c\u6b63\u811a\u6b65\u8e52\u8dda\u5730\u5728\u5e2e\u7740\u4e3a\u7740\u751f\u6d3b\uff0c\u8fd9\u4e00\u5bf9\u672c\u5e94\u4f11\u517b\u7684\u8001 \u5e74\u4eba\uff0c\u4ecd\u8f9b\u82e6\u5730\u5728\u505a\u7740\u5de5\uff0c\u5fcd\u53d7\u7740\u6df1\u591c\u7684\u5bd2\u9732\u548c\u6e05\u6668\u7684\u6653\u98ce\uff0c\u6240\u6c42\u7684\u53ea\u662f\u4e00\u65e5\u7684\u6e29\u9971\u800c\u5df2\uff0c \u751f\u547d\u4e2d\u8bb8\u591a\u7f8e\u597d\u7684\u4e8b\uff0c\u5728\u4ed6\u4eec\u4ec5\u4ec5\u53ea\u4e0d\u8fc7\u662f\u4e00\u4e2a\u68a6\u800c\u5df2\u3002 \u3000\u3000\u590f\u82b8\u5fc3\u4e2d\u607b\u7136\uff0c\u4fcf\u6084\u5730\u8d70\u4e86\u4e0a\u53bb\uff0c\u90a3\u8001\u5934\u5b50\u62ac\u5934\u770b\u5230\u4e00\u4e2a\u5934\u53d1\u84ec\u677e\u3001\u8863\u5c65\u4e0d\u6574\u7684\u5999\u9f84\u5c11 \u5973\uff0c\u6df1\u591c\u7a81\u7136\u5728\u4ed6\u9762\u524d\u51fa\u73b0\uff0c\u5413\u5f97\u60ca\u547c\u4e86\u8d77\u6765\u3002 \u3000\u3000\u590f\u82b8\u8fde\u5fd9\u8bf4\uff1a\u201c\u8001\u7237\u5b50\u4e0d\u8981\u6015\uff0c\u6211\u53ea\u662f\u6765\u8ba8\u7897\u8c46\u6d46\u559d\u7684\u3002\u201d \u3000\u3000\u5979\u6e29\u67d4\u7684\u58f0\u8c03\u8bed\u6c14\u5e73\u9759\u4e86\u90a3\u8001\u5934\u5b50\u7684\u60ca\u60e7\uff0c\u4ed6\u60ca\u7591\u5730\u671b\u7740\u590f\u82b8\u3002 \u3000\u3000\u8001\u5927\u5a46\u4e5f\u8e52\u8dda\u5730\u8d70\u4e86\u8fc7\u6765\uff0c\u706f\u5149\u4e0b\u770b\u5230\u590f\u82b8\u6c14\u5598\u5401\u5401\uff0c\u8138\u8272\u4e5f\u82cd\u767d\u5f97\u53ef\u6015\uff0c\u5fd9\u9053\uff1a\u201c\u59d1 \u5a18\uff0c\u4f60\u600e\u4e48\u4e86\uff0c\u6709\u4ec0\u4e48\u4e0d\u8212\u670d\u5417\uff1f\u201d \u3000\u3000\u8001\u5e74\u4eba\u6c38\u8fdc\u6709\u4e00\u4efd\u6148\u5584\u7684\u5fc3\u80a0\uff0c\u4e5f\u8bb8\u4ed6\u662f\u5728\u4e3a\u81ea\u5df1\u5c06\u8981\u901d\u53bb\u7684\u751f\u547d\uff0c\u505a\u4e00\u9996\u7f8e\u4e3d\u7684\u633d\u6b4c \u5427\u3002 \u3000\u3000\u590f\u82b8\u7f16\u4e86\u4e2a\u5e76\u4e0d\u5341\u5206\u52a8\u542c\u7684\u8c0e\u8a00\uff0c\u5728\u8fd9\u4e24\u4e2a\u597d\u5fc3\u7684\u8001\u5e74\u4eba\u5bb6\u91cc\u4f4f\u4e86\u4e94\u5929\uff0c\u8eab\u4e0a\u6240\u53d7\u7684 \u4f24\uff0c\u7ecf\u8fc7\u718a\u501c\u771f\u6c14\u7684\u6cbb\u7597\uff0c\u53c8\u4f11\u517b\u4e86\u8fd9\u4e48\u591a\u5929\uff0c\u6e10\u6e10\u5df2\u5b8c\u5168\u75ca\u6108\u4e86\uff0c\u7cbe\u795e\u4e5f\u5927\u4e3a\u677e\u6da3\u3002 \u3000\u3000\u6b66\u5f53\u5c71\u4e0a\u53d1\u751f\u7684\u4e8b\u5979\u4e00\u70b9\u513f\u4e5f\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u718a\u501c\u548c\u5c1a\u672a\u660e\u4e24\u6b21\u4ece\u5c0f\u5c4b\u524d\u8d70\u8fc7\uff0c\u8c01\u4e5f\u6ca1\u6709\u671d\u91cc\u770b\u4e00\u773c\u3002 \u3000\u3000\u8fd9\u5c31\u662f\u9020\u5316\u7684\u6349\u5f04\u4eba\u3002 \u3000\u3000\u4e94\u5929\u4e4b\u540e\uff0c\u590f\u82b8\u4f9d\u4f9d\u4e0d\u820d\u5730\u79bb\u5f00\u4e86\u90a3\u4e24\u4e2a\u597d\u5fc3\u7684\u8001\u5e74\u4eba\uff0c\u5728\u56ca\u7a7a\u5982\u6d17\uff0c\u65e0\u4ee5\u4e3a\u62a5\u7684\u60c5\u51b5 \u4e0b\uff0c\u5979\u89e3\u4e0b\u4e86\u9888\u5b50\u4e0a\u7684\u91d1\u94fe\u5b50\u3002 \u3000\u3000\u4e8e\u662f\u5979\u5f00\u59cb\u611f\u5230\u4e00\u79cd\u7a7a\u524d\u7684\u6050\u60e7\uff0c\u5728\u4eba\u4eec\u56ca\u7a7a\u5982\u6d17\u65f6\u6240\u53d1\u751f\u7684\u90a3\u79cd\u6050\u60e7\u7684\u611f\u89c9\uff0c\u6709\u65f6\u51e0 \u4e4e\u548c\u201c\u6b7b\u201d\u4e00\u6837\u5f3a\u70c8\u3002 \u3000\u3000\u590f\u82b8\u4e00\u56de\u8d70\uff0c\u4e00\u9762\u76d8\u7b97\u7740\u5979\u8be5\u8d70\u7684\u8def\u3002 \u3000\u3000\u5ffd\u7136\uff0c\u8fdc\u5904\u6709\u8e44\u58f0\u4f20\u6765\uff0c\u5979\u8fdc\u8fdc\u770b\u5230\u8fc7\u6765\u7684\u4e24\u5339\u9a6c\u3002 \u3000\u3000\u90a3\u4e24\u5339\u9a6c\u8d70\u5f97\u5f88\u6162\uff0c\u53c8\u8d70\u8fd1\u4e86\u4e00\u70b9\uff0c\u590f\u82b8\u770b\u5230\u9a6c\u4e0a\u5750\u7684\u662f\u4e00\u7537\u4e00\u5973\uff0c\u8eab\u4e0a\u7a7f\u5f97\u82b1\u56e2\u9526 \u65cf\u3002 \u3000\u3000\u9a6c\u4e0a\u90a3\u5973\u7684\u4e00\u8def\u6307\u70b9\u7740\u5411\u90a3\u7537\u7684\u8bf4\u7b11\uff0c\u4e0d\u65f6\u8fd8\u4f38\u51fa\u624b\u53bb\u642d\u90a3\u7537\u7684\u80a9\u5934\uff0c\u663e\u5f97\u751a\u662f\u4eb2\u70ed\u3002 \u3000\u3000\u590f\u82b8\u89c1\u4e86\u4e0d\u7981\u4e00\u9635\u5fc3\u9178\uff0c\u60f3\u8d77\u81ea\u5df1\u548c\u718a\u501c\u9a6c\u4e0a\u9080\u6e38\uff0c\u5e76\u80a9\u9a70\u9aa4\u7684\u60c5\u51b5\uff0c\u5386\u5386\u5982\u5728\u76ee\u524d\uff0c \u4f46\u662f\u6b64\u523b\u81ea\u5df1\u5374\u662f\u5b64\u96f6\u96f6\u7684\u3002 \u3000\u3000\u5979\u5728\u8def\u4e2d\u592e\u9685\u8e0a\u72ec\u884c\uff0c\u9a6c\u4e0a\u7684\u4e00\u7537\u4e00\u5973\uff0c\u90fd\u7528\u5947\u602a\u7684\u76ee\u5149\u671b\u7740\u5979\u3002 \u3000\u3000\u5979\u4f4e\u7740\u5934\uff0c\u7b49\u5230\u90a3\u4e24\u5339\u9a6c\u6162\u6162\u8d70\u5230\u81ea\u5df1\u8eab\u4fa7\uff0c\u7a81\u5730\u53cc\u624b\u75be\u4f38\uff0c\u5728\u90a3\u4e24\u5339\u9a6c\u8eab\u4e0a\u70b9\u4e86\u4e24 \u4e0b\u3002 \u3000\u3000\u90a3\u4e24\u5339\u9a6c\u4e00\u58f0\u957f\u5636\uff0c\u4eba\u7acb\u8d77\u6765\uff0c\u52a8\u4e5f\u4e0d\u52a8\u3002 \u3000\u3000\u53ef\u662f\u9a6c\u4e0a\u7684\u4e24\u4eba\uff0c\u4ecd\u7136\u7aef\u5750\u5728\u9a6c\u978d\u4e0a\uff0c\u50cf\u662f\u9489\u5728\u4e0a\u9762\uff0c\u795e\u8272\u867d\u7136\u5fae\u5fae\u9732\u51fa\u60ca\u6115\u7684\u8868\u60c5\uff0c \u4f46\u4ecd\u662f\u4ece\u5bb9\u7684\uff0c\u4eff\u4f5b\u590f\u82b8\u8fd9\u79cd\u4e2d\u539f\u6b66\u6797\u7f55\u89c1\u7684\u5236\u9a6c\u624b\u6cd5\uff0c\u4e95\u672a\u5f15\u8d77\u4ed6\u4eec\u5927\u5927\u7684\u60ca\u5f02\u3002 \u3000\u3000\u82e5\u7136\u590f\u82b8\u7a0d\u4e3a\u66f4\u5177\u6709\u4e00\u4e9b\u6c5f\u6e56\u4e0a\u7684\u5386\u7ec3\uff0c\u5979\u7acb\u523b\u4fbf\u53ef\u4ee5\u77e5\u9053\u6b64\u4e24\u4eba\u5fc5\u975e\u5e38\u4eba\uff0c\u987b\u77e5\u4ee5\u5b64 \u5cf0\u4e00\u5251\u90a3\u6837\u7684\u58f0\u540d\u5730\u4f4d\uff0c\u5c1a\u4e14\u5bf9\u5979\u7684\u5236\u9a6c\u624b\u6cd5\u5927\u8868\u60ca\u5f02\uff0c\u90a3\u4e48\u8fd9\u4e24\u4eba\u5c82\u975e\u53c8\u6bd4\u5b64\u5cf0\u4e00\u5251\u9ad8\u4e86 \u4e00\u7b79\u3002 \u3000\u3000\u9a6c\u4e0a\u7684\u7537\u5973\u5fae\u4e00\u60ca\u6115\u4e4b\u540e\uff0c\u76f8\u89c6\u4e00\u7b11\uff0c\u4f3c\u4e4e\u89c9\u5f97\u5f88\u6709\u8da3\u3002 \u3000\u3000\u90a3\u5973\u7684\u7b11\u5f97\u53c8\u4fcf\u53c8\u5a07\uff0c\u590f\u82b8\u6697\u5fd6\uff1a\u201c\u8fd9\u5973\u7684\u597d\u7f8e\u3002\u201d\u81ea\u987e\u81ea\u5df1\u8934\u6402\u7684\u5916\u8868\uff0c\u4e0d\u7981\u6709\u4e9b\u81ea \u5351\u7684\u611f\u89c9\uff0c\u5979\u5411\u6765\u81ea\u8bb8\u7f8e\u8c8c\uff0c\u8fd9\u79cd\u611f\u89c9\u5728\u5979\u5fc3\u4e2d\uff0c\u5c1a\u662f\u7b2c\u4e00\u6b21\u53d1\u751f\uff0c\u5f53\u7136\uff0c\u5979\u8863\u886b\u7684\u4e0d\u6574\uff0c \u4e5f\u662f\u4f7f\u5979\u751f\u51fa\u8fd9\u79cd\u5bf9\u5979\u800c\u8a00\u662f\u65b0\u5947\u7684\u611f\u89c9\u7684\u4e3b\u8981\u539f\u56e0\u3002 \u3000\u3000\u5979\u5fae\u4e00\u8fdf\u7591\uff0c\u731b\u60f3\u8d77\u5979\u62e6\u4f4f\u4ed6\u4eec\u7684\u76ee\u7684\uff0c\u662f\u60f3\u62a2\u52ab\u4ed6\u4eec\uff0c\u8138\u4e0a\u4e0d\u89c9\u6709\u4e9b\u7ea2\uff0c\u60f3\u8bf4\u51fa\u81ea\u5df1 \u7684\u76ee\u7684\uff0c\u60f3\u6765\u60f3\u53bb\uff0c\u5374\u4e0d\u77e5\u9053\u8be5\u5982\u4f55\u642d\u8bcd\u3002 \u3000\u3000\u9a6c\u4e0a\u7684\u7537\u5973\u4ee5\u4e00\u79cd\u5947\u602a\u7684\u773c\u5149\u770b\u7740\u5979\uff0c\u8fd9\u773c\u5149\u4e2d\u5305\u542b\u7740\u7684\u5927\u591a\u662f\u5632\u5f04\u7684\u610f\u5473\uff0c\u867d\u7136\u6ca1\u6709 \u8bf4\u8bdd\uff0c\u4f46\u662f\u8fd9\u79cd\u610f\u5473\u5df2\u5f88\u660e\u663e\u5730\u8868\u9732\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4e8e\u662f\u7d20\u6027\u9a84\u50b2\u7684\u590f\u82b8\uff0c\u5f00\u59cb\u751f\u6c14\uff0c\u800c\u751f\u6c14\u53c8\u4f7f\u5979\u5fd8\u8bb0\u4e86\u81ea\u5df1\u5bf9\u4eba\u5bb6\u7684\u5b58\u5fc3\u662f\u6781\u7aef\u4e0d\u6b63 \u7684\uff0c\u7adf\u7136\u6beb\u4e0d\u8003\u8651\u4ed6\u8bf4\u51fa\u4e86\u81ea\u5df1\u7684\u4f01\u56fe\u3002 \u3000\u3000\u201c\u4f60\u4eec\u2014\u2014\u201d\u5979\u77ac\u5373\u60f3\u8d77\u4e86\u53e6\u4e24\u4e2a\u66f4\u9002\u4e8e\u6b64\u65f6\u60c5\u51b5\u7684\u5b57\u53e5\uff0c\u7acb\u523b\u6539\u53e3\u9053\uff1a\u201c\u670b\u53cb\u2014\u2014\u201d \u4f46\u662f\u4e0b\u9762\u7684\u8bdd\uff0c\u5979\u4f9d\u7136\u4e0d\u77e5\u9053\u8be5\u600e\u4e48\u8bf4\u3002 \u3000\u3000\u5fc3\u4e00\u6a2a\uff0c\u5979\u7d22\u6027\u5f00\u95e8\u89c1\u5c71\uff0c\u9053\uff1a\u201c\u628a\u8eab\u4e0a\u7684\u94f6\u5b50\u5206\u4e00\u534a\u51fa\u6765\uff0c\u59d1\u5a18\u8981\u7528\u3002\u201d \u3000\u3000\u9a6c\u4e0a\u7684\u7537\u5973\uff0c\u201c\u5657\u55e4\u201d\u4e00\u58f0\uff0c\u7b11\u4e86\u51fa\u6765\uff0c\u90a3\u7537\u7684\u76ee\u5149\u4e2d\u5632\u5f04\u7684\u610f\u5473\uff0c\u53d8\u5f97\u66f4\u6d53\u4e86\u4e9b\uff0c\u5fcd \u4f4f\u7b11\u9053\uff1a\u201c\u5927\u738b\u2014\u2014\u201d\u201c\u5927\u738b\u201d\u8fd9\u4e24\u4e2a\u5b57\u4e00\u51fa\u53e3\uff0c\u65c1\u8fb9\u90a3\u5973\u5b50\u7b11\u5f97\u5982\u767e\u5408\u521d\u653e\u3002 \u3000\u3000\u8fd9\u79cd\u7b11\u58f0\u548c\u8fd9\u79cd\u79f0\u547c\uff0c\u4f7f\u5f97\u590f\u82b8\u7684\u8138\u66f4\u7ea2\u5f97\u597d\u50cf\u719f\u900f\u4e86\u7684\u82f9\u679c\u3002 \u3000\u3000\u201c\u5927\u738b\u6562\u60c5\u662f\u8981\u94f6\u5b50\uff0c\u6211\u8eab\u4e0a\u4ec0\u4e48\u90fd\u6709\uff0c\u5c31\u662f\u6ca1\u6709\u94f6\u5b50\uff0c\u600e\u4e48\u529e\u5462\uff1f\u201d\u7537\u7684\u6781\u529b\u5fcd\u4f4f\u5632 \u7b11\uff0c\u4e00\u672c\u6b63\u7ecf\u4ed6\u8bf4\u9053\u3002 \u3000\u3000\u590f\u82b8\u6697\u5fd6\uff1a\u201c\u4ed6\u4eec\u5927\u6982\u4e0d\u77e5\u9053\u6211\u8eab\u6000\u6b66\u529f\uff0c\u662f\u4ee5\u624d\u4f1a\u6709\u8fd9\u79cd\u8868\u60c5\u3002\u201d \u3000\u3000\u201c\u4f60\u4eec\u4e0d\u8981\u7b11\uff0c\u8981\u77e5\u9053\u59d1\u5a18\u4e0d\u662f\u8ddf\u4f60\u4eec\u5f00\u73a9\u7b11\u7684\uff0c\u4f60\u4eec\u4e0d\u62ff\u51fa\u6765\uff0c\u6211\u2014\u2014\u201d\u590f\u82b8\u81ea\u4ee5\u4e3a \u975e\u5e38\u5f97\u4f53\u4ed6\u8bf4\u51fa\u8fd9\u51e0\u65ec\u8bdd\u4ee5\u540e\uff0c\u8eab\u5f62\u7a81\u7136\u7a9c\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5979\u6b66\u529f\u4e0d\u5f31\uff0c\u8fd9\u4e00\u7a9c\u5c11\u8bf4\u4e5f\u6709\u4e00\u4e08\u4e94\u516d\uff0c\u5728\u6b66\u6797\u4e2d\u5df2\u7ecf\u53ef\u7b97\u662f\u96be\u89c1\u7684\u8eab\u624b\uff0c\u7136\u540e\u8eab\u5f62\u98d8 \u98d8\u843d\u4e86\u4e0b\u6765\u5c11\u4f9d\u7136\u7ad9\u5728\u539f\u5730\u3002 \u3000\u3000\u5979\u4ee5\u4e3a\u5979\u9732\u51fa\u7684\u8fd9\u4e00\u624b\u4e0a\u4e58\u7684\u8f7b\u529f\uff0c\u4e00\u5b9a\u53ef\u4ee5\u9707\u4f4f\u8fd9\u4e24\u4e2a\u7537\u5973\u3002 \u3000\u3000\u54ea\u77e5\u9053\u90a3\u7537\u7684\u7a81\u7136\u4ef0\u5929\u957f\u7b11\uff0c\u7b11\u58f0\u6e05\u6717\u9ad8\u4ea2\uff0c\u9707\u5f97\u8033\u9f13\u55e1\u55e1\u4f5c\u54cd\u3002 \u3000\u3000\u590f\u82b8\u867d\u7136\u5bf9\u6c5f\u6e56\u95e8\u69db\u4e00\u65e0\u6240\u77e5\uff0c\u4f46\u542c\u4e86\u8fd9\u7537\u7684\u7b11\u5bb9\uff0c\u5fc3\u4e2d\u4e5f\u5927\u5403\u4e00\u60ca\uff0c\u77e5\u9053\u8fd9\u7537\u5b50\u7684\u5185 \u529f\uff0c\u5fc5\u5b9a\u5728\u81ea\u5df1\u4e4b\u4e0a\u3002 \u3000\u3000\u5979\u4e0d\u7981\u8fde\u8fde\u53eb\u82e6\uff0c\u6697\u5fd6\uff1a\u201c\u6211\u771f\u5012\u9709\uff0c\u4e00\u51fa\u624b\u4fbf\u78b0\u5230\u8fd9\u79cd\u4eba\u3002\u201d \u3000\u3000\u4f46\u662f\u4e8b\u5df2\u81f3\u6b64\uff0c\u5979\u9a91\u864e\u96be\u4e0b\uff0c\u7ad9\u5728\u90a3\u91cc\uff0c\u8138\u4e0a\u5df2\u6709\u7a98\u6025\u7684\u795e\u8272\uff0c\u672c\u6765\u5df2\u7ecf\u7ea2\u7740\u7684\u8138\uff0c\u73b0 \u5728\u7ea2\u5f97\u66f4\u5389\u5bb3\u4e86\u3002 \u3000\u3000\u957f\u7b11\u987f\u4f4f\uff0c\u90a3\u7537\u7684\u7a81\u7136\u9762\u5b54\u4e00\u677f\uff0c\u9053\uff1a\u201c\u4f60\u771f\u7684\u60f3\u62e6\u8def\u52ab\u8d22\uff1f\u201d \u3000\u3000\u201c\u662f\u53c8\u600e\u6837\uff1f\u4e0d\u662f\u53c8\u600e\u6837\uff1f\u201d \u3000\u3000\u201c\u5c31\u51ed\u4f60\u8eab\u4e0a\u7684\u90a3\u70b9\u6b66\u529f\uff0c\u548c\u8fd9\u70b9\u4ece\u5173\u5916\u9a6c\u8d3c\u90a3\u91cc\u5b66\u6765\u7684\u5077\u9a6c\u624b\u6cd5\uff0c\u5c31\u60f3\u62e6\u8def\u52ab\u8d22\uff0c\u53ea \u6015\u8fd8\u5dee\u5f97\u8fdc\u54e9\uff01\u201d \u3000\u3000\u590f\u82b8\u9053\uff1a\u201c\u4f60\u8bd5\u8bd5\u770b\u3002\u201d \u3000\u3000\u90a3\u7537\u7684\u53c8\u957f\u7b11\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u4e0d\u670d\u6c14\uff0c\u8fd9\u6837\u597d\u4e86\uff0c\u4f60\u4ece\u4e00\u6570\u5230\u4e09\uff0c\u6211\u4eec\u8fd8 \u4e0d\u80fd\u8ba9\u4f60\u8eba\u4e0b\uff0c\u5c31\u5c06\u8eab\u4e0a\u7684\u94f6\u5b50\u5168\u90e8\u9001\u7ed9\u4f60\u3002\u201d\u968f\u624b\u5c06\u6302\u5728\u9a6c\u978d\u4e0a\u7684\u5305\u88b1\u89e3\u4e0b\uff0c\u6253\u5f00\u6765\uff0c\u7a81 \u89c1\u5149\u534e\u4e86\u76ee\uff0c\u5305\u88b1\u91cc\u7adf\u7136\u5168\u662f\u4ef7\u503c\u4e0d\u83f2\u7684\u73cd\u5b9d\u3002 \u3000\u3000\u90a3\u7537\u7684\u975e\u4f46\u8863\u7740\u534e\u8d35\uff0c\u4eba\u4e5f\u6f47\u6d12\u82f1\u4fca\u5f97\u5f88\uff0c\u968f\u624b\u5c06\u90a3\u5305\u88b1\u671d\u5730\u4e0a\u4e00\u4e22\uff0c\u771f\u50cf\u5c06\u8fd9\u4e9b\u73e0 \u5b9d\uff0c\u770b\u6210\u4e00\u6587\u4e0d\u503c\u4f3c\u7684\u3002 \u3000\u3000\u590f\u82b8\u867d\u7136\u4e5f\u662f\u51fa\u8eab\u8c6a\u5bcc\uff0c\u4f46\u89c1\u4e86\u8fd9\u4eba\u7684\u6001\u5ea6\uff0c\u4e5f\u6709\u4e9b\u5403\u60ca\u3002 \u3000\u3000\u5374\u542c\u90a3\u534e\u670d\u7537\u5b50\u9053\uff1a\u201c\u4f60\u5f00\u59cb\u6570\u5427\u3002\u201d \u3000\u3000\u590f\u82b8\u5634\u4e00\u561f\uff0c\u6697\u5fd6\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u6211\u5c31\u4e0d\u76f8\u4fe1\u6570\u5230\u4e09\u65f6\u4f60\u5c31\u80fd\u600e\u4e48\u6837\u6211\uff1f\u201d \u3000\u3000\u201c\u4e00\u3002\u201d\u590f\u82b8\u5f00\u53e3\u53eb\u9053\uff0c\u8eab\u5f62\u4e00\u63a0\uff0c\u53cc\u638c\u62a2\u51fa\uff0c\u5411\u9a6c\u4e0a\u7684\u7537\u5b50\u653b\u53bb\u3002 \u3000\u3000\u90a3\u7537\u5b50\u53c8\u662f\u4e00\u58f0\u957f\u7b11\uff0c\u624b\u4e2d\u9a6c\u97ad\u201c\u5236\u201d\u5730\u98de\u51fa\uff0c\u50cf\u4e00\u6761\u98de\u821e\u7740\u7684\u7075\u86c7\u4f3c\u7684\uff0c\u97ad\u68a2\u5fae\u6296\uff0c \u70b9\u5411\u590f\u82b8\u201c\u80a9\u4e95\u201d\u3001\u201c\u80a9\u8d1e\u201d\u3001\u201c\u7384\u5173\u201d\u3001\u201c\u592a\u767d\u201d\u56db\u5904\u5927\u7a74\u3002 \u3000\u3000\u590f\u82b8\u4e00\u60ca\uff0c\u53e3\u4e2d\u558a\u51fa\u201c\u4e8c\u201d\u3002 \u3000\u3000\u53cc\u817f\u4e00\u767b\uff0c\u8eab\u8eaf\u4e00\u626d\uff0c\u52aa\u529b\u5730\u907f\u5f00\u4e86\u8fd9\u51cc\u5389\u7684\u4e00\u97ad\u3002 \u3000\u3000\u5979\u53e3\u4e2d\u624d\u60f3\u558a\u51fa\u201c\u4e09\u201d\uff0c\u54ea\u77e5\u97ad\u68a2\u5982\u9644\u9aa8\u4e4b\u86c6\uff0c\u53c8\u8ddf\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u5979\u518d\u5411\u5de6\u4e00\u626d\uff0c\u54ea\u77e5\u80c1\u4e0b\u7a81\u7136\u4e00\u9ebb\uff0c\u4e00\u4ef6\u6697\u5668\u65e0\u58f0\u65e0\u606f\u5730\u51fb\u5728\u80c1\u4e0b\u7684\u201c\u5c06\u53f0\u201d\u7a74\uff0c\u50cf\u662f \u65e9\u5c31\u5728\u90a3\u91cc\u7b49\u7740\uff0c\u800c\u5979\u81ea\u5df1\u5374\u5c06\u8eab\u5b50\u9001\u4e0a\u88ab\u51fb\u4f3c\u7684\uff0c\u53e3\u4e2d\u7684\u201c\u4e09\u201d\u5c1a\u672a\u558a\u51fa\uff0c\u8eab\u5b50\u5df2\u7ecf\u5012 \u4e0b\u3002 \u3000\u3000\u90a3\u5973\u7684\u4f3c\u4e4e\u5fc3\u80a0\u5f88\u8f6f\uff0c\u67d4\u58f0\u5411\u90a3\u534e\u670d\u7537\u5b50\u9053\uff1a\u201c\u4f60\u53bb\u5c06\u8fd9\u59d1\u5a18\u7684\u7a74\u9053\u89e3\u5f00\u5427\uff0c\u6211\u65b9\u624d\u51fa \u624b\u91cd\u4e86\u4e9b\uff0c\u4e0d\u8981\u4f24\u7740\u4eba\u5bb6\u3002\u201d \u3000\u3000\u7537\u7684\u9053\uff1a\u201c\u4f60\u7684\u813e\u6c14\u600e\u4e48\u7a81\u7136\u53d8\u5f97\u90a3\u4e48\u597d\uff0c\u4ee5\u524d\u4e0d\u662f\u52a8\u4e0d\u52a8\u5c31\u8981\u6740\u4eba\u5417\uff1f\u201d \u3000\u3000\u201c\u6b7b\u9b3c\u3002\u201d\u90a3\u5973\u7684\u5a07\u7b11\u7740\u9a82\u7740\uff0c\u5fc3\u60c5\u50cf\u662f\u9ad8\u5174\u5df2\u6781\u3002 \u3000\u3000\u534e\u670d\u7537\u5b50\u4e5f\u672a\u89c1\u5982\u4f55\u4f5c\u52bf\uff0c\u8eab\u5f62\u98d8\u7136\u81ea\u9a6c\u978d\u4e0a\u98de\u8d77\uff0c\u8863\u8882\u5fae\u8361\uff0c\u7b11\u58f0\u672a\u7edd\uff0c\u843d\u5728\u590f\u82b8\u8eab \u4e0a\uff0c\u6781\u5feb\u5730\u5728\u5979\u8eab\u4e0a\u62cd\u4e86\u4e00\u638c\u3002 \u3000\u3000\u590f\u82b8\u751a\u81f3\u8fd8\u6ca1\u6709\u611f\u89c9\u5230\u4ed6\u8fd9\u4e00\u638c\u62cd\u4e0b\uff0c\u4f46\u662f\u5979\u4f53\u5185\u771f\u6c14\u53c8\u731b\u7136\u6062\u590d\u4e86\u6b63\u5e38\u7684\u8fd0\u884c\uff0c\u624b\u4e00 \u52a8\uff0c\u7a74\u9053\u5df2\u7ecf\u88ab\u4eba\u5bb6\u89e3\u5f00\u6765\u4e86\u3002 \u3000\u3000\u5979\u53cc\u65f6\u4e00\u652f\u5730\uff0c\u8df3\u4e86\u8d77\u6765\uff0c\u7ad9\u76f4\u8eab\u5b50\uff0c\u5374\u89c1\u90a3\u7537\u7684\u6b63\u7b11\u563b\u563b\u5730\u671b\u7740\u81ea\u5df1\u3002 \u3000\u3000\u5979\u8d8a\u60f3\u8d8a\u6c14\uff0c\u89c9\u5f97\u81ea\u5df1\u53d7\u90a3\u4e48\u591a\u59d4\u5c48\uff0c\u800c\u4e14\u4eba\u5bb6\u53cc\u53cc\u5bf9\u5bf9\uff0c\u81ea\u5df1\u5374\u662f\u5f62\u5355\u5f71\u5b64\uff0c\u611f\u6000\u8eab \u4e16\uff0c\u4e0d\u7981\u60b2\u4ece\u4e2d\u6765\uff0c\u7adf\u653e\u58f0\u54ed\u8d77\u6765\u3002 \u3000\u3000\u5979\u672c\u662f\u4e0d\u61c2\u4e16\u4e8b\uff0c\u5014\u5f3a\u4efb\u6027\u7684\u5973\u5b69\u5b50\uff0c\u60f3\u7b11\u7684\u65f6\u5019\u5c31\u7b11\uff0c\u60f3\u54ed\u7684\u65f6\u5019\u5c31\u54ed\uff0c\u4e1d\u6beb\u4e0d\u4f1a\u505a \u4f5c\uff0c\u4e5f\u4e00\u70b9\u4e0d\u907f\u5fcc\u4efb\u4f55\u4e8b\u3002 \u3000\u3000\u90a3\u7537\u7684\u89c1\u5979\u7a81\u7136\u54ed\u4e86\u8d77\u6765\uff0c\u5012\u771f\u7684\u89c9\u5f97\u6709\u4e9b\u610f\u5916\u548c\u60ca\u9519\u4e86\u3002 \u3000\u3000\u4ed6\u6697\u5fd6\uff1a\u201c\u8fd9\u4e2a\u5c0f\u59d1\u5a18\u5230\u5e95\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u60f3\u5230\u81ea\u5df1\u7684\u592a\u592a\uff0c\u4e5f\u662f\u8fd9\u79cd\u8bf4\u7b11\u5c31\u7b11\u3001\u8bf4\u54ed\u5c31 \u54ed\u7684\u6027\u5b50\uff0c\u5fc3\u4e2d\u4e0d\u89c9\u5bf9\u590f\u82b8\u8d77\u4e86\u597d\u611f\u3002 \u3000\u3000\u9a6c\u4e0a\u7684\u5c11\u5973\u89c1\u590f\u82b8\u54ed\u4e86\u8d77\u6765\uff0c\u5fc3\u4e2d\u4e5f\u6cdb\u8d77\u540c\u60c5\u7684\u611f\u89c9\uff0c\u5fd8\u5374\u4e86\u590f\u82b8\u65b9\u624d\u60f3\u62e6\u8def\u52ab\u8d22\u7684\u884c \u4e3a\u3002 \u3000\u3000\u539f\u6765\u8fd9\u9a6c\u4e0a\u7684\u5c11\u5973\u6700\u8fd1\u89e3\u5f00\u4e86\u5fc3\u4e0a\u7684\u6b7b\u7ed3\uff0c\u5bf9\u4e16\u4e8b\u770b\u5f97\u90fd\u662f\u90a3\u4e48\u4e50\u89c2\u548c\u53ef\u7231\uff0c\u5bf9\u4e16\u4e0a\u7684 \u4eba\u4eec\u4e5f\u8d77\u4e86\u5f88\u5927\u7684\u540c\u60c5\u5fc3\u3002 \u3000\u3000\u4e8e\u662f\u5979\u4e5f\u98d8\u8eab\u4e0b\u4e86\u9a6c\uff0c\u773c\u524d\u5fae\u82b1\uff0c\u5979\u5df2\u7ad9\u5728\u590f\u82b8\u8eab\u4fa7\uff0c\u8eab\u6cd5\u7684\u66fc\u5999\uff0c\u901f\u5ea6\u7684\u60ca\u4eba\uff0c\u66f4\u662f \u4ee4\u4eba\u4e0d\u671f\u7136\u800c\u89c9\u5f97\u795e\u5999\u3002 \u3000\u3000\u201c\u5c0f\u59d1\u5a18\uff0c\u4f60\u6709\u4ec0\u4e48\u96be\u53d7\u7684\u4e8b\uff0c\u53ea\u7ba1\u5bf9\u6211\u8bb2\u597d\u4e86\u3002\u201d\u5979\u629a\u7740\u590f\u82b8\u7684\u80a9\uff0c\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u53ea \u8981\u662f\u6211\u80fd\u505a\u5230\u7684\uff0c\u6211\u4e00\u5b9a\u5e2e\u5fd9\u3002\u201d \u3000\u3000\u5979\u4e0d\u4f46\u8bed\u610f\u5584\u826f\uff0c\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u66f4\u662f\u90a3\u4e48\u751c\u871c\u3001\u4fcf\u5a07\u3002 \u3000\u3000\u4f46\u662f\u590f\u82b8\u5374\u662f\u5014\u5f3a\u800c\u597d\u80dc\u7684\uff0c\u4eba\u5bb6\u8d8a\u662f\u5bf9\u5979\u8868\u793a\u601c\u60af\uff0c\u5979\u8d8a\u662f\u89c9\u5f97\u96be\u53d7\uff0c\u80a9\u5934\u4e00\u6447\uff0c\u6447 \u5f00\u4e86\u90a3\u5973\u5b50\u7684\u624b\uff0c\u6068\u58f0\u9053\uff1a\u201c\u4e0d\u8981\u4f60\u7ba1\u3002\u201d \u3000\u3000\u5979\u8fd9\u79cd\u6beb\u4e0d\u9886\u60c5\u7684\u53e3\u543b\uff0c\u4e0d\u4f46\u6ca1\u6709\u6fc0\u6012\u90a3\u5973\u5b50\uff0c\u53cd\u800c\u5f15\u8d77\u90a3\u5973\u5b50\u7684\u540c\u60c5\u3002 \u3000\u3000\u201c\u8fd9\u4e2a\u5973\u5b50\u4e00\u5b9a\u6709\u5f88\u5927\u7684\u59d4\u5c48\uff0c\u4f46\u662f\u5979\u4e00\u5b9a\u4e5f\u662f\u4e2a\u5014\u5f3a\u7684\u5973\u5b50\u3002\u5fc3\u4e2d\u6709\u82e6\u75db\uff0c\u5374\u4e0d\u613f\u610f \u544a\u8bc9\u4eba\u5bb6\u77e5\u9053\uff0c\u201d\u9a6c\u4e0a\u7684\u5973\u5b50\u53f9\u6c14\u5fd6\u9053\uff1a\u201c\u5509\uff0c\u5979\u8fd9\u79cd\u813e\u6c14\uff0c\u5012\u771f\u662f\u548c\u6211\u6709\u4e9b\u76f8\u50cf\u3002\u201d \u3000\u3000\u539f\u6765\u8fd9\u5c11\u5973\u4e5f\u662f\u8fd9\u79cd\u4e2a\u6027\uff0c\u6240\u4ee5\u5979\u5bf9\u590f\u82b8\u9664\u4e86\u540c\u60c5\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u5c42\u6df1\u6df1\u7684\u4e86\u89e3\u3002 \u3000\u3000\u201c\u5c0f\u59d1\u5a18\uff0c\u4f60\u542c\u6211\u8bf4\u3002\u201d\u90a3\u5973\u5b50\u4ee5\u66f4\u6e29\u67d4\u7684\u8bed\u58f0\u9053\uff1a\u201c\u65e0\u8bba\u6709\u4ec0\u4e48\u4e8b\uff0c\u4f60\u90fd\u544a\u8bc9\u6211\u597d \u4e86\uff0c\u6211\u66ff\u4f60\u4f5c\u4e3b\u51fa\u6c14\u3002\u201d \u3000\u3000\u5979\u8bf4\u5f97\u90a3\u4e48\u6b66\u65ad\uff0c\u4eff\u4f5b\u771f\u662f\u5c06\u5929\u4e0b\u4eba\u90fd\u6ca1\u6709\u653e\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u4f46\u662f\u590f\u82b8\u4ecd\u7136\u62b1\u7740\u5934\u54ed\u7740\uff0c\u6ca1\u6709\u56de\u7b54\u8fd9\u5973\u5b50\u597d\u5fc3\u7684\u95ee\u9898\u3002 \u3000\u3000\u8def\u7684\u90a3\u4e00\u5934\uff0c\u7a81\u7136\u8e44\u58f0\u6742\u4e71\u3002 \u3000\u3000\u604d\u773c\uff0c\u98de\u5feb\u5730\u5954\u8fc7\u6765\u51e0\u5339\u5065\u9a6c\uff0c\u9a6c\u8e44\u7ffb\u98de\uff0c\u5e26\u8d77\u4e00\u7247\u5c18\u4e0a\u3002 \u3000\u3000\u9a6c\u4e0a\u7684\u662f\u56db\u4e2a\u8eab\u7a7f\u84dd\u888d\u7684\u9053\u4eba\uff0c\u770b\u5230\u8def\u4e0a\u6709\u4e24\u5973\u4e00\u7537\u7ad9\u7740\uff0c\u5176\u4e2d\u6709\u4e00\u4e2a\u5c11\u5973\u50cf\u662f\u5728\u54ed\uff0c \u4e0d\u7981\u90fd\u89c9\u5f97\u8be7\u5f02\u5f97\u5f88\u3002 \u3000\u3000\u590f\u82b8\u542c\u5230\u9a6c\u8e44\u58f0\uff0c\u4e0b\u610f\u8bc6\u5730\u62ac\u8d77\u5934\u6765\u3002 \u3000\u3000\u5176\u4e2d\u6709\u4e00\u4e2a\u9053\u4eba\u6b63\u597d\u56de\u8fc7\u5934\u6765\uff0c\u548c\u590f\u82b8\u7684\u76ee\u5149\u78b0\u4e2a\u6b63\u7740\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u4e00\u52a8\uff0c\u7a81\u7136\u9ad8\u559d\u9053\uff1a\u201c\u505c\u4e0b\u6765\u3002\u201d \u3000\u3000\u5176\u4f59\u7684\u4e09\u5339\u9a6c\u4fbf\u4e00\u9f50\u52d2\u4f4f\u9a6c\u91cc\uff0c\u98de\u5954\u7740\u7684\u9a6c\u9aa4\u7136\u505c\u4e0b\uff0c\u524d\u8e44\u63ed\u8d77\uff0c\u5636\u7136\u957f\u9e23\uff0c\u4f46\u662f\u9a6c\u4e0a \u7684\u9053\u4eba\u4e2a\u4e2a\u8eab\u624b\u4e86\u5f97\uff0c\u53cc\u817f\u7d27\u7d27\u5730\u6276\u7740\u9a6c\u7f30\uff0c\u4e00\u70b9\u4e5f\u6ca1\u6709\u614c\u5f20\u5931\u63aa\u7684\u6837\u5b50\u3002 \u3000\u3000\u5176\u4e2d\u4e00\u4eba\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u4e24\u773c\u76ef\u5728\u90a3\u4e24\u5339\u88ab\u590f\u82b8\u5236\u4f4f\u7684\u9a6c\u4e0a\u3002 \u3000\u3000\u4f46\u662f\u90a3\u4e00\u4e2a\u770b\u6765\u6c14\u5ea6\u6700\u4ece\u5bb9\uff0c\u4e30\u795e\u6700\u51b2\u5937\u7684\u9053\u4eba\uff0c\u773c\u5149\u5374\u662f\u77aa\u5728\u590f\u82b8\u8138\u4e0a\u3002 \u3000\u3000\u90a3\u534e\u670d\u5973\u5b50\u51b7\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u6697\u5fd6\uff1a\u201c\u8fd9\u4e2a\u9053\u58eb\u4e24\u4e2a\u773c\u775b\u770b\u8d77\u4eba\u6765\u8d3c\u516e\u516e\u7684\uff0c\u4e00\u5b9a\u4e0d\u662f\u597d \u4eba\uff0c\u6211\u771f\u60f3\u6559\u8bad\u6559\u8bad\u4ed6\u2026\u2026\u201d \u3000\u3000\u5ff5\u5934\u5c1a\u672a\u8f6c\u8fc7\uff0c\u5374\u89c1\u90a3\u9053\u4eba\u7ffb\u8eab\u8df3\u4e0b\u9a6c\u6765\uff0c\u8eab\u624b\u7684\u77eb\u5065\uff0c\u8fe5\u51e1\u5f02\u4fd7\u3002 \u3000\u3000\u90a3\u534e\u670d\u7537\u5b50\u89c1\u8fd9\u56db\u4e2a\u9053\u4eba\u7684\u88c5\u675f\u548c\u4ed6\u4eec\u80cc\u4e0a\u659c\u6302\u7740\u7684\u5e26\u7740\u9ec4\u8272\u7684\u7a57\u5b50\u7684\u957f\u5251\uff0c\u7709\u5934\u4e00 \u76b1\uff0c\u6697\u5fd6\uff1a\u201c\u6b66\u5f53\u6d3e\u7684\u3002\u201d \u3000\u3000\u90a3\u9053\u4eba\u679c\u7136\u5c31\u662f\u6b66\u5f53\u6d3e\u7684\u7b2c\u4e8c\u4ee3\u5f1f\u5b50\u4e2d\u7684\u4f7c\u4f7c\u8005\uff0c\u6b66\u5f53\u638c\u6559\u5173\u5c71\u95e8\u7684\u5f1f\u5b50\uff0c\u521d\u4e0b\u6b66\u5f53\u6b65 \u4eba\u6c5f\u6e56\u7684\u98de\u9e64\u9053\u4eba\u3002 \u3000\u3000\u98de\u9e64\u5b50\u770b\u5230\u590f\u82b8\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6697\u5fd6\uff1a\u201c\u8fd9\u5973\u5b50\u4e0d\u5c31\u662f\u81ea\u85cf\u7ecf\u9601\u9003\u51fa\u7684\u5c11\u5973\u5417\uff1f\u201d\u9a6c\u7f30\u4e00 \u52d2\uff0c\u9053\uff1a\u201c\u53eb\u5979\u8f6c\u544a\u718a\u501c\u6700\u597d\u3002\u201d \u3000\u3000\u539f\u6765\u718a\u501c\uff0c\u5c1a\u672a\u660e\u4e58\u9699\u9041\u53bb\uff0c\u5929\u9634\u6559\u4e3b\u4e5f\u968f\u5373\u4e0b\u5c71\u3002 \u3000\u3000\u4e34\u884c\u65f6\uff0c\u4ed6\u4eec\u8fd8\u518d\u4e09\u9053\u7740\u6b49\uff0c\u98de\u9e64\u5b50\u60f3\u7740\uff1a\u201c\u8fd9\u5929\u9634\u6559\u5f92\uff0c\u5012\u6ca1\u6709\u4f20\u8bf4\u4e2d\u7684\u90a3\u4e48\u574f\u3002\u201d \u3000\u3000\u54ea\u77e5\u5f53\u5929\u665a\u4e0a\uff0c\u4e00\u5411\u9759\u5bc2\u5b89\u8be6\u7684\u6b66\u5f53\u5c71\uff0c\u7a81\u7136\u53d1\u73b0\u4e86\u6570\u5341\u6761\u591c\u884c\u4eba\u7684\u5f71\u5b50\u3002 \u3000\u3000\u8fd9\u662f\u6570\u5341\u5e74\u6765\uff0c\u88ab\u6b66\u6797\u5c0a\u4e3a\u5723\u5730\u7684\u6b66\u5f53\u5c71\uff0c\u6240\u4ece\u6765\u6ca1\u6709\u53d1\u751f\u8fc7\u7684\u4e8b\u3002 \u3000\u3000\u90a3\u6570\u5341\u6761\u7684\u4eba\u5f71\uff0c\u8eab\u6cd5\u90fd\u8fc5\u901f\u5f97\u5f88\uff0c\u90fd\u50cf\u662f\u6b66\u6797\u4e2d\u7684\u80fd\u624b\u3002 \u3000\u3000\u6b66\u5f53\u6d3e\u6570\u5341\u5e74\u6765\uff0c\u88ab\u6b66\u6797\u89c6\u4e3a\u6cf0\u5c71\u5317\u6597\uff0c\u5f53\u7136\u4e0d\u4f1a\u60f3\u5230\u6b64\u756a\u6709\u4eba\u6562\u6765\u6b66\u5f53\u5c71\u4fb5\u72af\uff0c\u66f4\u6ca1 \u6709\u60f3\u5230\u4f1a\u805a\u96c6\u4e86\u8fd9\u4e48\u591a\u7684\u6b66\u6797\u9ad8\u624b\u3002 \u3000\u3000\u4f46\u662f\u6b66\u5f53\u9053\u4eba\u7adf\u4e2a\u4e2a\u4ff1\u90fd\u662f\u8bad\u7ec3\u6709\u7d20\uff0c\u6709\u4e9b\u6b66\u529f\u867d\u7136\u4e0d\u751a\u9ad8\uff0c\u4f46\u5bf9\u9053\u5bb6\u7684\u201c\u4e5d\u5bab\u516b\u5366\u5251 \u9635\u201d\uff0c\u90fd\u914d\u5408\u5f97\u975e\u5e38\u7eaf\u719f\u3002 \u3000\u3000\u8fd9\u79cd\u4e25\u5bc6\u914d\u5408\u7684\u5251\u9635\uff0c\u6b64\u65f6\u53d1\u6325\u4e86\u6700\u5927\u7684\u5a01\u529b\uff0c\u6765\u72af\u6b66\u5f53\u5c71\u7684\u6570\u5341\u9ad8\u624b\uff0c\u4e00\u65f6\u4e5f\u4e0d\u80fd\u5c06 \u8fd9\u79cd\u9053\u5bb6\u65e0\u4e0a\u7684\u5251\u9635\u7834\u53bb\u3002 \u3000\u3000\u98de\u9e64\u5b50\u5251\u5f71\u7ffb\u98de\uff0c\u7a81\u7136\u77a5\u89c1\u8fd9\u4e9b\u591c\u884c\u4eba\u5176\u4e2d\u6570\u4eba\u7684\u9762\u5bb9\uff0c\u5fc3\u4e2d\u5927\u6012\uff1a\u201c\u539f\u6765\u8fd9\u4e9b\u90fd\u662f\u5929 \u9634\u6559\u5f92\u3002\u201d\u5237\u5237\u5237\u6570\u5251\uff0c\u624b\u5e95\u66f4\u4e0d\u5bb9\u60c5\u3002 \u3000\u3000\u658c\u5f53\u638c\u6559\u5999\u4e00\u771f\u4eba\uff0c\u6b66\u529f\u6df1\u539a\uff0c\u751a\u81f3\u8fd8\u5728\u6c5f\u6e56\u4e2d\u7684\u4f20\u8bf4\u4e4b\u4e0a\u3002 \u3000\u3000\u6b64\u65f6\u4ed6\u52a8\u4e86\u771f\u6012\uff0c\u6301\u5251\u5fa1\u654c\u3002 \u3000\u3000\u4e00\u573a\u5927\u6218\uff0c\u5929\u9634\u6559\u5f92\u867d\u7136\u4f24\u4e4b\u4e0d\u5c11\uff0c\u4f46\u6b66\u5f53\u6d3e\u7684\u5f1f\u5b50\u4ea6\u662f\u5927\u6709\u4e8f\u635f\u3002 \u3000\u3000\u8fd9\u8fd8\u662f\u5929\u9634\u6559\u4e2d\u6700\u8fa3\u624b\u7684\u4e24\u4e2a\u4eba\u7269\u2014\u2014\u94c1\u9762\u9ec4\u886b\u5ba2\u4ec7\u4e0d\u53ef\u548c\u4e5d\u5929\u7384\u5973\u7f2a\u5929\u96ef\u7559\u5b88\u592a\u884c\u5c71 \u603b\u575b\uff0c\u6ca1\u6709\u968f\u540c\u524d\u6765\uff0c\u4e0d\u7136\u6b66\u5f53\u5c71\u5c31\u66f4\u5371\u9669\u4e86\u3002 \u3000\u3000\u7126\u5f02\u884c\u60f3\u5f97\u5230\u90a3\u672c\u5185\u529f\u79d8\u7b08\u7684\u5fc3\u662f\u90a3\u4e48\u6df1\u5207\uff0c\u662f\u4ee5\u4e0d\u60dc\u503e\u5e08\u800c\u51fa\uff0c\u66f4\u4e0d\u60dc\u6811\u6b64\u5f3a\u654c\uff0c\u4e0d \u62e9\u624b\u6bb5\u7684\uff0c\u5c45\u7136\u591c\u5165\u6b66\u5f53\uff0c\u60f3\u4ee5\u5f3a\u529b\u53d6\u5f97\u6b64\u4e66\u3002 \u3000\u3000\u4ed6\u539f\u4ee5\u4e3a\u6b66\u5f53\u9053\u4eba\u731d\u4e0d\u53ca\u9632\uff0c\u600e\u80fd\u62b5\u654c\u5f97\u4f4f\u81ea\u5df1\u548c\u6559\u4e0b\u5982\u8bb8\u591a\u9ad8\u624b\u3002 \u3000\u3000\u54ea\u77e5\u9053\u6b66\u5f53\u6d3e\u6f5c\u5728\u7684\u5b9e\u529b\uff0c\u7adf\u51fa\u4e4e\u4ed6\u60f3\u50cf\u4e4b\u5916\uff0c\u4ed6\u4e45\u6218\u4e0d\u4e0b\uff0c\u5999\u4e00\u771f\u4eba\u638c\u4e2d\u7684\u9752\u840d\u5251\uff0c \u51fa\u795e\u4eba\u5316\uff0c\u65bd\u5c55\u5f00\u6b66\u5f53\u9547\u5c71\u5251\u6cd5\u2014\u2014\u4e5d\u5bab\u8fde\u73af\u5251\uff0c\u5251\u6263\u8fde\u73af\uff0c\u5982\u62bd\u8327\u5265\u4e1d\uff0c\u5c42\u5c42\u4e0d\u7edd\u3002 \u3000\u3000\u4ed6\u5f53\u673a\u7acb\u65ad\uff0c\u7acb\u523b\u53d1\u73b0\u5982\u679c\u8fd9\u6837\u76f8\u6301\u4e0b\u53bb\uff0c\u5fc5\u5b9a\u662f\u843d\u4e2a\u4e24\u8d25\u4ff1\u4f24\u7684\u7ed3\u679c\u3002 \u3000\u3000\u987b\u77e5\u4ed6\u6b64\u6b21\u591c\u5165\u6b66\u5f53\u5c71\u7684\uff0c\u51e0\u4e4e\u662f\u5929\u9634\u6559\u4e0b\u5927\u534a\u7684\u9ad8\u624b\uff0c\u5168\u90e8\u51fa\u52a8\uff0c\u867d\u7136\u4ed6\u6e34\u5207\u7684\u5e0c\u671b \u80fd\u5360\u6709\u90a3\u90e8\u5947\u4e66\uff0c\u4f46\u662f\u82e5\u7136\u4e3a\u6b64\u800c\u4f24\u4e86\u81ea\u5df1\u5929\u9634\u6559\u7684\u4e3b\u529b\uff0c\u4ed6\u8fd8\u662f\u4e0d\u4f1a\u613f\u610f\u7684\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u4e00\u58f0\u957f\u5578\u3002 \u3000\u3000\u9ed1\u8863\u6469\u52d2\u4e00\u7a9c\u51b2\u5929\uff0c\u638f\u51fa\u91d1\u9523\u6765\u6572\u4e86\u51e0\u4e0b\uff0c\u6e05\u6717\u7684\u9523\u58f0\uff0c\u4f20\u51fa\u5f88\u8fdc\u3002 \u3000\u3000\u5929\u9634\u6559\u4e0b\u7684\u6570\u5341\u9ad8\u624b\uff0c\u6765\u5982\u6f6e\u6c34\u4e4b\u6da8\uff0c\u53bb\u4e5f\u5982\u6f6e\u6c34\u4e4b\u9000\u3002 \u3000\u3000\u7247\u523b\u4e4b\u95f4\uff0c\u8fde\u672a\u53d7\u4f24\u7684\u5e26\u53d7\u4f24\u7684\uff0c\u90fd\u8d70\u5f97\u5e72\u5e72\u51c0\u51c0\u4e86\u3002 \u3000\u3000\u660e\u6708\u50cf\u5f80\u524d\u4e00\u6837\uff0c\u7167\u5f97\u8fd9\u6d77\u5185\u540d\u5c71\u7684\u5916\u8868\uff0c\u6cdb\u8d77\u8ff7\u8499\u7684\u94f6\u8272\u3002 \u3000\u3000\u7384\u771f\u89c2\u5927\u6bbf\u524d\u7684\u9662\u5b50\u91cc\uff0c\u5012\u5367\u7740\u5341\u6570\u5177\u5c38\u4f11\uff0c\u5176\u4e2d\u6709\u6b66\u5f53\u6d3e\u7684\u5f1f\u5b50\uff0c\u4e5f\u6709\u5929\u9634\u6559\u7684\u3002 \u3000\u3000\u4e3a\u7740\u4e00\u4e2a\u4eba\u7684\u91ce\u5fc3\uff0c\u8fd9\u4e48\u591a\u65e0\u8f9c\u7684\u751f\u547d\u6b7b\u4ea1\u4e86\u3002 \u3000\u3000\u5999\u4e00\u771f\u4eba\u8fd9\u624d\u9707\u6012\uff0c\u786e\u5b9a\u4ee5\u81ea\u5df1\u5728\u6b66\u6797\u4e2d\u7684\u5730\u4f4d\uff0c\u904d\u6492\u82f1\u96c4\u8d34\u3001\u60f3\u52a8\u5458\u6240\u6709\u6c5f\u6e56\u4e2d\u7684\u7cbe \u9510\uff0c\u518d\u6b21\u6d88\u706d\u5929\u9634\u6559\u7684\u52bf\u529b\u3002 \u3000\u3000\u4e8e\u662f\u98de\u9e64\u5b50\u8854\u547d\u4e0b\u5c71\uff0c\u8d1f\u8d77\u901a\u77e5\u6b66\u6797\u5404\u95e8\u5404\u6d3e\u7684\u8c6a\u58eb\u7684\u4efb\u52a1\u3002 \u3000\u3000\u4ed6\u5728\u8def\u4e0a\u770b\u5230\u590f\u82b8\uff0c\u60f3\u5230\u718a\u501c\u548c\u5c1a\u672a\u660e\u7684\u6b66\u529f\uff0c\u4e5f\u60f3\u5230\u4ed6\u4eec\u5fc5\u5b9a\u4e50\u4e8e\u53c2\u52a0\u8fd9\u4e00\u4e2a\u884c\u52a8\uff0c \u4e8e\u662f\u4ed6\u52d2\u4f4f\u9a6c\uff0c\u60f3\u5c06\u8fd9\u6d88\u606f\u544a\u8bc9\u590f\u82b8\uff0c\u8ba9\u5979\u8f6c\u544a\u718a\u501c\u3002 \u3000\u3000\u590f\u82b8\u671b\u89c1\u4ed6\uff0c\u60ca\u60f6\u5730\u60f3\u8d77\u4ed6\u662f\u8c01\uff1a\u201c\u54ce\u5440\uff0c\u6b66\u5f53\u6d3e\u7684\u9053\u58eb\u8ffd\u4e0b\u6765\u4e86\u3002\u201d\u5979\u4ee5\u4e3a\u98de\u9e64\u5b50\u548c \u53e6\u5916\u4e09\u4e2a\u6b66\u5f53\u6d3e\u7684\u7b2c\u4e8c\u4ee3\u5f1f\u5b50\uff0c\u6765\u6349\u5979\u56de\u5c71\u7684\u3002 \u3000\u3000\u54ea\u77e5\u98de\u9e64\u5b50\u7684\u6001\u5ea6\uff0c\u7edd\u4e0d\u662f\u5979\u6240\u60f3\u8c61\u7684\u51f6\u6076\uff0c\u5ba2\u6c14\u4ed6\u8bf4\u4e86\u6765\u610f\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u534e\u670d\u7684\u7537\u5973\uff0c\u542c\u5230\u718a\u501c\u7684\u540d\u5b57\u65f6\uff0c\u53cc\u76ee\u4e00\u5f20\uff0c\u7d27\u7d27\u5730\u76ef\u5728\u590f\u82b8\u8138\u4e0a\uff0c\u6697\u5fd6\uff1a\u201c\u539f \u6765\u8fd9\u4e2a\u59d1\u5a18\u5c31\u662f\u718a\u8001\u5f1f\u7684\u7231\u4fa3\u3002\u201d \u3000\u3000\u4e0d\u95ee\u53ef\u77e5\uff0c\u8fd9\u4e24\u4eba\u534e\u670d\u7537\u5973\uff0c\u5c31\u662f\u907f\u5c45\u201c\u751c\u751c\u8c37\u201d\u91cc\u7684\u70b9\u82cd\u5927\u4fa0\uff0c\u7389\u9762\u795e\u5251\u5e38\u6f2b\u5929\uff0c\u548c \u4ed6\u5e78\u5f97\u56de\u590d\u539f\u8c8c\u7684\u5a07\u59bb\u6563\u82b1\u4ed9\u5b50\u7530\u654f\u654f\u3002 \u3000\u3000\u4ed6\u4e24\u4eba\u9759\u6781\u601d\u52a8\uff0c\u7565\u4e3a\u6536\u62fe\u4e86\u4e00\u4e0b\uff0c\u4ed7\u7740\u5c71\u58c1\u7684\u673a\u5173\u5de7\u5999\uff0c\u4e5f\u4e0d\u6015\u6709\u4eba\u4f1a\u53d1\u73b0\u90a3\u7a00\u4e16\u7684 \u5b9d\u7a9f\uff0c\u4fbf\u8fde\u8882\u51fa\u5c71\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u9996\u5148\u5173\u5fc3\u5230\u7684\u5c31\u662f\u718a\u501c\uff0c\u7530\u654f\u654f\u5bf9\u718a\u501c\u66f4\u662f\u611f\u6fc0\uff0c\u56e0\u4e3a\u4ed6\u4f7f\u5979\u91cd\u5f97\u4e86\u5979\u6700\u73cd\u60dc\u7684\u4e1c \u897f\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u4eec\u7b2c\u4e00\u4e2a\u76ee\u7684\u5730\uff0c\u4fbf\u662f\u60f3\u5230\u6b66\u5f53\u5c71\u53bb\u770b\u770b\u718a\u501c\u7684\u7ed3\u679c\u3002 \u3000\u3000\u54ea\u77e5\u65e0\u610f\u4e4b\u4e2d\uff0c\u5374\u9047\u89c1\u7684\u590f\u82b8\u3002 \u3000\u3000\u98de\u9e64\u5b50\u4f83\u4f83\u800c\u8bf4\uff0c\u5e38\u6f2b\u5929\u4e0d\u7981\u8be7\u5f02\uff1a\u201c\u600e\u5730\u5929\u9634\u6559\u53c8\u6b7b\u7070\u590d\u71c3\u4e86\uff1f\u201d\u4ed6\u9690\u5728\u6df1\u5c71\u6709\u51e0\u5341 \u8f7d\uff0c\u5929\u9634\u6559\u7684\u91cd\u8d77\uff0c\u4ed6\u6839\u672c\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u5e76\u6ca1\u6709\u5c06\u5fc3\u4e2d\u7684\u6000\u7591\u95ee\u51fa\u6765\uff0c\u4ed6\u6839\u672c\u4e00\u8a00\u672a\u53d1\uff0c\u56e0\u4e3a\u4ed6\u6b64\u65f6\u8fd8\u4e0d\u60f3\u5c06\u81ea\u5df1\u7684\u8eab\u4efd \u8bf4\u51fa\u6765\u3002 \u3000\u3000\u98de\u9e64\u5b50\u518d\u4e09\u5631\u5490\u7740\u590f\u82b8\uff0c\u89c1\u5230\u590f\u82b8\u70b9\u9996\u540e\uff0c\u4fbf\u4e0a\u9a6c\u8d70\u4e86\u3002 \u3000\u3000\u4ed6\u4e5f\u66fe\u5411\u5e38\u6f2b\u5929\u592b\u5987\u5fae\u4e00\u9894\u9996\uff0c\u4f46\u662f\u4ed6\u5374\u7edd\u672a\u60f3\u5230\u8fd9\u4e2a\u5112\u96c5\u82f1\u4fca\u7684\u534e\u670d\u6587\u58eb\uff0c\u5c31\u662f\u5f53\u5e74 \u540d\u9707\u5929\u4e0b\u7684\u70b9\u82cd\u638c\u95e8\u7389\u9762\u795e\u5251\u5e38\u6f2b\u5929\u3002 \u3000\u3000\u56db\u5339\u5065\u9a6c\uff0c\u53c8\u5e26\u8d77\u5c18\u4e0a\u7edd\u5c18\u800c\u53bb\u3002 \u3000\u3000\u7ad9\u5728\u4e0a\u5348\u6e29\u7166\u9633\u5149\u4e0b\uff0c\u590f\u82b8\u6115\u4e86\u8bb8\u4e45\u3002 \u3000\u3000\u7530\u654f\u654f\u4e00\u8fde\u4e32\u5a07\u4fcf\u7684\u7b11\u58f0\uff0c\u4f7f\u5f97\u5979\u81ea\u8ff7\u60af\u7684\u5fc6\u5ff5\u4e2d\u56de\u5230\u73b0\u5b9e\u91cc\u6765\u3002 \u3000\u3000\u5979\u6240\u5fc6\u5ff5\u7684\uff0c\u81ea\u7136\u53ea\u6709\u718a\u501c\uff0c\u65b9\u624d\u5979\u542c\u4e86\u98de\u9e64\u5b50\u7684\u8bdd\uff0c\u77e5\u9053\u718a\u501c\u679c\u7136\u5192\u7740\u4e07\u96be\uff0c\u8d76\u5230\u6b66 \u5f53\u5c71\u53bb\u63f4\u6551\u5979\uff0c\u5fc3\u4e2d\u7684\u60b2\u75db\uff0c\u970e\u65f6\u4e4b\u95f4\uff0c\u5c31\u88ab\u751c\u871c\u7684\u6e29\u99a8\u6240\u66ff\u4ee3\uff0c\u718a\u501c\u7684\u4e00\u8a00\u4e00\u7b11\uff0c\u5189\u5189\u81ea \u5fc3\u5e95\u5347\u8d77\u3002 \u3000\u3000\u7530\u654f\u654f\u5bdf\u5fae\u77e5\u8457\uff0c\u89c1\u5979\u5634\u89d2\u6cdb\u8d77\u7684\u751c\u610f\uff0c\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u5728\u60f3\u7740\u6211\u4eec\u90a3\u4f4d\u718a\u8001\u5f1f\u5427\u3002\u201d \u3000\u3000\u590f\u82b8\u4e00\u60ca\uff0c\u8d77\u5148\u5979\u60ca\u7684\u662f\u88ab\u4eba\u8bf4\u4e2d\u4e86\u5fc3\u4e8b\uff0c\u540e\u6765\u5979\u5374\u662f\u5947\u602a\u8fd9\u4e2a\u6b66\u529f\u9ad8\u7edd\u7684\u7f8e\u8c8c\u5973\u5b50\uff0c \u4f55\u4ee5\u4f1a\u79f0\u547c\u718a\u501c\u4e3a\u201c\u8001\u5f1f\u201d\u3002 \u3000\u3000\u5979\u6697\u5fd6\uff1a\u201c\u96be\u9053\u5979\u4e5f\u8ba4\u5f97\u718a\u501c\uff1f\u201d\u5fc3\u91cc\u5bbd\u5fae\u5fae\u6cdb\u8d77\u4e00\u9635\u751c\u610f\uff0c\u773c\u5149\u5c04\u5230\u7530\u654f\u654f\u8eab\u4e0a\uff0c\u5374 \u89c1\u7530\u654f\u654f\u7684\u624b\uff0c\u88ab\u63e1\u5728\u5e38\u6f2b\u5929\u7684\u624b\u91cc\uff0c\u5fc3\u4e2d\u7acb\u523b\u5766\u7136\uff0c\u53cd\u800c\u6709\u70b9\u597d\u7b11\uff1a\u201c\u6211\u600e\u4e48\u8fd9\u4e48\u591a \u7591\u3002\u201d \u3000\u3000\u5973\u5b69\u5b50\u7684\u5fc3\u91cc\uff0c\u6c38\u8fdc\u662f\u6700\u96be\u731c\u6d4b\u7684\uff0c\u5bf9\u4e8e\u5979\u4eec\u6240\u559c\u7231\u7684\u4e1c\u897f\uff0c\u5979\u4eec\u6709\u4e00\u79cd\u5f3a\u70c8\u7684\u5360\u6709 \u6b32\uff0c\u4e0d\u5141\u8bb8\u4efb\u4f55\u4eba\u5206\u4eab\u4e00\u70b9\u3002 \u3000\u3000\u9633\u5149\u4ece\u4e1c\u9762\u7167\u8fc7\u6765\uff0c\u7167\u5728\u590f\u82b8\u5de6\u9762\u7684\u8138\u988a\u4e0a\uff0c\u590f\u82b8\u8138\u7ea2\u7ea2\u7684\uff0c\u663e\u5f97\u90a3\u4e48\u7f8e\u4e3d\u800c\u53ef\u7231\u3002 \u3000\u3000\u7530\u654f\u654f\u6e29\u67d4\u5730\u53cd\u63e1\u4f4f\u5e38\u6f2b\u5929\u7684\u624b\u638c\uff0c\u7b11\u9053\uff1a\u201c\u96be\u602a\u718a\u8001\u5f1f\u8fd9\u4e48\u60f3\u4f60\uff0c\u5c31\u662f\u6211\u89c1\u4e86\uff0c\u5fc3\u91cc \u4e5f\u559c\u6b22\u7684\u4e0d\u5f97\u4e86\uff0c\u4f55\u51b5\u4ed6\u5462\u3002\u201d \u3000\u3000\u590f\u82b8\u8138\u66f4\u7ea2\u4e86\uff0c\u5fc3\u4e2d\u5374\u53c8\u90a3\u4e48\u8212\u670d\uff0c\u4f4e\u7740\u542b\u7f9e\u8bf4\u9053\uff1a\u201c\u4f60\u4e5f\u8ba4\u5f97\u501c\u2026\u2026\u201d\u5979\u7ec8\u7a76\u4e0d\u597d\u610f \u601d\u8bf4\u51fa\u201c\u54e5\u54e5\u201d\u4e24\u5b57\uff0c\u987f\u4f4f\u4e86\u8bdd\u3002 \u3000\u3000\u7530\u654f\u654f\u671d\u5979\u4e00\u5939\u773c\uff0c\u5a07\u7b11\u7740\u9053\uff1a\u201c\u662f\u5440\uff0c\u6211\u4e5f\u8ba4\u5f97\u4f60\u7684\u501c\u54e5\u54e5\u3002\u201d \u3000\u3000\u5e38\u6f2b\u65e0\u5fae\u7b11\u5730\u671b\u7740\u5a07\u59bb\u548c\u8fd9\u4e2a\u5929\u771f\u7f8e\u8c8c\u7684\u5c11\u5973\u6253\u8da3\uff0c\u5fc3\u91cc\u89c9\u5f97\u90a3\u4e48\u5e78\u798f\u3002 \u3000\u3000\u56e0\u4e3a\u5df2\u7ecf\u5f97\u5230\u4e86\u7231\u7684\u4eba\uff0c\u4e5f\u603b\u662f\u5e0c\u671b\u522b\u4eba\u4e5f\u5f97\u5230\u5e78\u798f\u3002 \u3000\u3000\u590f\u82b8\u4e0d\u5b89\u7684\u626d\u6029\u7740\uff0c\u5bb3\u7740\u7f9e\uff0c\u7136\u800c\u5979\u5bf9\u8fd9\u4e00\u53cc\u672c\u662f\u5979\u6253\u52ab\u7684\u5bf9\u8c61\uff0c\u5374\u6cdb\u8d77\u4e86\u4eb2\u5207\u4e4b\u611f\uff0c \u5c24\u5176\u662f\u5728\u5979\u51e0\u4e4e\u5df2\u662f\u5c71\u7a77\u6c34\u5c3d\u7684\u65f6\u5019\uff0c\u8fd9\u79cd\u4eb2\u5207\u7684\u611f\u89c9\u66f4\u662f\u5f3a\u70c8\u800c\u6d53\u539a\uff0c\u56e0\u4e3a\u5979\u89c9\u5f97\u53ea\u8981\u662f \u718a\u501c\u7684\u670b\u53cb\uff0c\u4e0d\u4e5f\u5c31\u7b49\u4e8e\u81ea\u5df1\u7684\u670b\u53cb\u4e00\u6837\u5417\u3002 \u3000\u3000\u5979\u4f4e\u7740\u5934\uff0c\u7559\u5fc3\u5730\u503e\u542c\u7740\u4e0d\u5fcd\u89c1\u5979\u592a\u7a98\u7684\u5e38\u6f2b\u5929\u8bf4\u7740\u4ed6\u4eec\u548c\u718a\u501c\u76f8\u8bc6\u7684\u7ecf\u8fc7\u3002 \u3000\u3000\u90a3\u4e9b\u4e8b\u90fd\u662f\u90a3\u4e48\u7684\u65b0\u5947\u800c\u6709\u8da3\uff0c\u5979\u62ac\u5934\u671b\u4e86\u7530\u654f\u654f\u4e00\u773c\uff0c\u5fc3\u91cc\u5728\u60f3\u7740\uff1a\u201c\u96be\u9053\u8fd9\u4e48\u6f02\u4eae \u7684\u4eba\u4ee5\u524d\u771f\u4f1a\u90a3\u4e48\u4e11\u5417\uff1f\u5982\u6b64\u8bf4\u6765\uff0c\u90a3\u79cd\u795e\u79d8\u7684\u6613\u5bb9\u672f\u53c8\u662f\u591a\u4e48\u5947\u5999\u5440\u3002\u201d\u7530\u654f\u654f\u50cf\u6c38\u8fdc\u90fd \u80fd\u770b\u900f\u5c11\u5973\u7eaf\u6d01\u800c\u591a\u53d8\u7684\u5fc3\uff0c\u7b11\u9053\uff1a\u201c\u6211\u4ee5\u524d\u771f\u7684\u90a3\u4e48\u4e11\uff0c\u4f60\u76f8\u4e0d\u76f8\u4fe1\u5440\uff1f\u201d \u3000\u3000\u590f\u82b8\u4f4e\u5934\u4e00\u7b11\uff0c\u6697\u5fd6\uff1a\u201c\u600e\u4e48\u6211\u7684\u5fc3\u4e8b\u8001\u662f\u88ab\u5979\u8bf4\u4e2d\u5462\u3002\u201d \u3000\u3000\u201c\u59d1\u5a18\u662f\u4e0d\u662f\u60f3\u627e\u718a\u8001\u5f1f\uff1f\u201d\u5e38\u6f2b\u5929\u95ee\u9053\u3002 \u3000\u3000\u590f\u82b8\u4e0d\u597d\u610f\u601d\u5730\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u4e8e\u662f\u5e38\u6f2b\u5929\u6168\u7136\u9053\uff1a\u201c\u6211\u4eec\u4e5f\u60f3\u627e\u718a\u8001\u5f1f\uff0c\u59d1\u5a18\u4e0d\u5982\u5c31\u548c\u6211\u4eec\u4e00\u8d77\u8d70\u5427\u3002\u201d \u3000\u3000\u8fd9\u5f53\u7136\u662f\u590f\u82b8\u6c42\u4e4b\u4e0d\u5f97\u7684\u3002 \u3000\u3000\u7530\u654f\u654f\u5a07\u7b11\u7740\u6307\u7740\u90a3\u4e24\u5339\u9a6c\u8bf4\uff1a\u201c\u4e0d\u8fc7\u4f60\u53ef\u5f97\u5148\u5c06\u8fd9\u4e24\u5339\u9a6c\u5f04\u597d\u3002\u201d \u3000\u3000\u60f3\u8d77\u65b9\u624d\u5979\u5bf9\u4eba\u5bb6\u7684\u4e3e\u52a8\u548c\u5bf9\u4eba\u5bb6\u6240\u8bf4\u7684\u8bdd\uff0c\u590f\u82b8\u521a\u521a\u56de\u590d\u7684\u6b63\u5e38\u7684\u8138\u8272\uff0c\u53c8\u7ea2\u4e86\u8d77 \u6765\uff0c\u8baa\u8baa\u5730\u8d70\u4e86\u8fc7\u53bb\uff0c\u4f38\u624b\u5728\u9a6c\u8179\u80cc\u4e0a\u62cd\u4e86\u4e24\u4e0b\u3002 \u3000\u3000\u90a3\u4e24\u5339\u9a6c\u88ab\u5236\u4e86\u90a3\u4e48\u4e45\uff0c\u4f46\u662f\u7acb\u523b\u4fbf\u53c8\u795e\u9a8f\u5f02\u5e38\uff0c\u590f\u82b8\u6697\u5fd6\uff1a\u201c\u679c\u7136\u662f\u4e24\u5339\u597d\u9a6c\uff0c\u201d\u53c8 \u60f3\u5230\u81ea\u5df1\u7684\u90a3\u5339\u201c\u5927\u767d\u201d\u73b0\u5728\u4e0d\u77e5\u4e0b\u843d\uff0c\u5fc3\u4e2d\u53c8\u4e0d\u7981\u4fa7\u7136\u3002 \u3000\u3000\u987b\u77e5\u7231\u9a6c\u7684\u4eba\uff0c\u5f80\u5f80\u5c06\u81ea\u5df1\u7684\u5750\u9a91\u770b\u5f97\u5f02\u5e38\u73cd\u8d35\uff0c\u4f55\u51b5\u90a3\u5339\u201c\u5927\u767d\u201d\u7684\u786e\u662f\u5339\u540d\u9a79\uff0c\u590f \u82b8\u201c\u96ea\u5730\u98d8\u98ce\u201d\u7684\u5916\u53f7\uff0c\u4e5f\u662f\u56e0\u6b64\u800c\u6765\u5462\u3002 \u3000\u3000\u201c\u59d1\u5a18\u53ef\u662f\u5173\u5916\u957f\u5927\u7684\uff1f\u201d\u5e38\u6f2b\u5929\u5bf9\u5979\u8fd9\u79cd\u7eaf\u719f\u7684\u5236\u9a6c\u624b\u6cd5\uff0c\u4e5f\u5fae\u89c9\u5947\u602a\uff0c\u4e8e\u662f\u8bd5\u63a2\u95ee \u9053\u3002 \u3000\u3000\u590f\u82b8\u7b11\u7740\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5bb6\u5728\u5173\u5916\u6709\u4e2a\u9a6c\u573a\u2026\u2026\u201d\u5979\u8bdd\u4e2d\u542b\u610f\uff0c\u81ea\u662f\u544a\u8bc9\u5e38\u6f2b\u5929 \u5979\u4e0d\u662f\u9a6c\u8d3c\uff0c\u5e38\u6f2b\u5929\u4e00\u7b11\u4e86\u7136\u3002 \u3000\u3000\u4ed6\u518d\u6b21\u63a2\u8be2\uff0c\u5728\u54ea\u91cc\u6700\u53ef\u80fd\u627e\u5230\u718a\u501c\uff1f\u590f\u82b8\u6beb\u4e0d\u8003\u8651\u4ed6\u8bf4\uff1a\u201c\u9102\u57ce\u3002\u201d \u3000\u3000\u4e8e\u662f\u4ed6\u4eec\u53c8\u6e21\u5357\u6cb3\uff0c\u7ecf\u8944\u9633\u3001\u9102\u57ce\uff0c\u6cbf\u7740\u6c49\u6c34\u5357\u4e0b\u3002 \u3000\u3000\u7136\u800c\uff0c\u4ed6\u4eec\u5728\u9102\u57ce\u5e76\u6ca1\u6709\u627e\u5230\u718a\u501c\u3002 \u3000\u3000\u4ed6\u4eec\u53ea\u6709\u7ee7\u7eed\u7b56\u9a6c\u800c\u884c\u3002 \u3000\u3000\u7530\u654f\u654f\u548c\u5e38\u6f2b\u5929\u7f13\u7f13\u5e76\u884c\uff0c\u4e24\u4e2a\u4eba\u5e76\u80a9\u4f4e\u8bed\uff0c\u590f\u82b8\u89e6\u666f\u4f24\u6e05\uff0c\u7d22\u6027\u8dd1\u5728\u524d\u9762\u3002 \u3000\u3000\u8d70\u7740\uff0c\u8d70\u7740\uff0c\u7530\u654f\u654f\u5ffd\u7136\u53d1\u73b0\u590f\u82b8\u7684\u8e2a\u5f71\u4e0d\u89c1\u4e86\u3002\u4e0d\u7981\u7740\u6025\uff1a\u201c\u5979\u4eba\u5462\uff1f\u201d \u3000\u3000\u8bdd\u65b9\u8bf4\u5b8c\uff0c\u7a81\u7136\u542c\u5230\u524d\u9762\u6709\u5657\u54a4\u7684\u58f0\u97f3\uff0c\u5979\u5fc3\u6025\u4e4b\u4e0b\uff0c\u5c06\u9a6c\u52a0\u7d27\u6253\u4e86\u51e0\u97ad\uff0c\u8d76\u5230\u524d\u8fb9\uff0c \u89c1\u8def\u65c1\u6709\u4e2a\u6811\u6797\u4e8e\uff0c\u5657\u54a4\u7684\u58f0\u97f3\uff0c\u5c31\u662f\u4ece\u8fd9\u4e2a\u6811\u6797\u5b50\u91cc\u53d1\u51fa\u6765\u7684\uff0c\u9042\u52d2\u8f6c\u9a6c\u5934\uff0c\u8f6c\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u53ef\u662f\u5c31\u5728\u5979\u52d2\u8f6c\u9a6c\u5934\u7684\u90a3\u4e00\u5239\u90a3\u2026\u2026 \u3000\u3000\u6811\u6797\u91cc\u7a81\u7136\u5b8c\u5168\u5bc2\u9759\u4e0b\u6765\uff0c\u5979\u66f4\u6025\uff0c\u56e0\u4e3a\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u65e0\u58f0\u8fdc\u6bd4\u6709\u58f0\u66f4\u53ef\u6015\u3002 \u3000\u3000\u4e8e\u662f\u5979\u5e73\u5e73\u5730\u4ece\u9a6c\u978d\u4e0a\u63a0\u4e86\u8d77\u6765\uff0c\u8eab\u5f62\u4e00\u604d\uff0c\u4fbf\u8fdb\u4e86\u6811\u6797\u3002 \u3000\u3000\u5e38\u6f2b\u5929\u4e5f\u65bd\u5c55\u5f00\u8eab\u6cd5\uff0c\u4ece\u9a6c\u4e0a\u98de\u8eab\u800c\u8d77\uff0c\u5230\u4e86\u6811\u6797\u5b50\u4e00\u770b\uff0c\u98ce\u58f0\u5bc2\u7136\uff0c\u54ea\u91cc\u6709\u534a\u6761\u4eba \u5f71\u3002 \u3000\u3000\u7530\u654f\u654f\u7740\u6025\u5730\u5c06\u76ee\u5149\u5728\u56db\u5468\u641c\u7d22\u7740\uff0c\u5ffd\u7136\u770b\u5230\u5730\u4e0a\u6709\u4e9b\u53d1\u4eae\u7684\u4e1c\u897f\u3002 \u3000\u3000\u5979\u62fe\u8d77\u4e00\u770b\uff0c\u4e0d\u7531\u5730\u60ca\u53eb\u51fa\u58f0\u6765\uff0c\u811a\u5c16\u4e00\u52a8\uff0c\u95ea\u7535\u4f3c\u5730\u7a7f\u51fa\u6811\u6797\u7684\u53e6\u4e00\u7aef\uff0c\u5e38\u6f2b\u5929\u8ddf\u51fa \u53bb\u4e00\u770b\uff0c\u56db\u91ce\u832b\u832b\uff0c\u7530\u91cc\u7684\u7a3b\u5b50\uff0c\u88ab\u9633\u5149\u6620\u6210\u4e00\u7247\u91d1\u9ec4\u8272\uff0c\u5374\u6ca1\u6709\u4efb\u4f55\u4eba\u7684\u5f71\u3002 \u3000\u3000\u7530\u654f\u654f\u6025\u5f97\u9762\u76ee\u53d8\u8272\uff0c\u8fde\u8fde\u8bf4\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u529e\uff1f\u8fd9\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u201c\u4f60\u770b\u3002\u201d\u7530\u654f\u654f\u644a\u5f00\u624b\u638c\uff0c\u5e38\u6f2b\u5929\u89c1\u5979\u624b\u638c\u4e0a\u7684\u4e1c\u897f\uff0c\u4e5f\u81ea\u53d8\u8272\u3002 \u3000\u3000\u7a81\u5730\uff0c\u6811\u6797\u4e2d\u9690\u9690\u4f3c\u6709\u4e24\u4e2a\u4eba\u8bf4\u8bdd\u7684\u58f0\u97f3\u3002 \u3000\u3000\u7389\u9762\u795e\u5251\u3001\u6563\u82b1\u4ed9\u5b50\uff0c\u4e0d\u7ea6\u800c\u540c\u5730\u65bd\u5c55\u51fa\u7edd\u9876\u7684\u8f7b\u529f\uff0c\u63a0\u5411\u6811\u6797\u3002 \u3000\u3000\u54ea\u77e5\u6811\u6797\u4e2d\u4e5f\u6709\u4e24\u6761\u4eba\u5f71\u7535\u5c04\u800c\u51fa\uff0c\u7530\u654f\u654f\u6beb\u4e0d\u8003\u8651\uff0c\u4f4e\u559d\u9053\uff1a\u201c\u8eba\u4e0b\u3002\u201d\u968f\u624b\u4e00\u63d6\u638c \u4e2d\u53d1\u51fa\u4e00\u7247\u94f6\u661f\uff0c\u98ce\u5f3a\u529b\u52b2\uff0c\u518d\u52a0\u4e0a\u8fd9\u53cc\u65b9\u90fd\u662f\u7edd\u5feb\u7684\u8eab\u6cd5\uff0c\u90a3\u4e9b\u94f6\u661f\u773c\u770b\u5c31\u8981\u51fb\u5728\u90a3\u4e24\u4e2a \u4eba\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u54ea\u77e5\u5176\u4e2d\u4e00\u4eba\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u62c9\u7740\u65c1\u8fb9\u7684\u4eba\u5411\u5de6\u731b\u9000\uff0c\u5c31\u50cf\u9c7c\u5728\u6c34\u4e2d\u4e00\u6837\uff0c\u8eab\u8eaf\u7531\u6025\u8fdb \u53d8\u4e3a\u5de6\u9000\u65f6\uff0c\u90a3\u79cd\u5f97\u610f\u7684\u8fd0\u8f6c\uff0c\u51e0\u662f\u532a\u5937\u6240\u601d\u7684\u3002 \u3000\u3000\u7530\u654f\u654f\u518d\u4e5f\u60f3\u4e0d\u5230\u6697\u5668\u5c45\u7136\u4f1a\u843d\u7a7a\uff0c\u89c1\u4e86\u8fd9\u4eba\u8fd9\u79cd\u7384\u4e4b\u53c8\u7384\u7684\u8f7b\u529f\uff0c\u5fc3\u4e2d\u4e00\u52a8\u3002 \u3000\u3000\u5979\u731b\u52a8\u8eab\u5f62\uff0c\u4e5f\u662f\u90a3\u4e48\u66fc\u5999\u5730\u987f\u4f4f\u4e86\u524d\u51b2\u7684\u529b\u9053\u3002 \u3000\u3000\u5e38\u6f2b\u5929\u7a81\u7136\u98d8\u98d8\u800c\u8d77\uff0c\u4e58\u52bf\u62bd\u51fa\u957f\u5251\uff0c\u5251\u6c14\u5982\u8679\uff0c\u8eab\u5f62\u5982\u71d5\u3002 \u3000\u3000\u90a3\u81ea\u6797\u4e2d\u63a0\u51fa\u7684\u4e24\u6761\u4eba\u5f71\uff0c\u7a81\u7136\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u5e38\u5927\u54e5\u3002\u201d \u3000\u3000\u5e38\u6f2b\u5929\u4e00\u6115\uff0c\u7530\u654f\u654f\u5df2\u9ad8\u5174\u5730\u53eb\u7740\uff1a\u201c\u5440\uff0c\u679c\u7136\u662f\u4f60\u3002\u201d \u3000\u3000\u90a3\u4e24\u4eba\u4e00\u63a0\u800c\u524d\uff0c\u56db\u4eba\u9762\u9762\u76f8\u5bf9\uff0c\u7adf\u90fd\u9ad8\u5174\u5f97\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u539f\u6765\u4e24\u4eba\uff0c\u4e00\u4e2a\u5c31\u662f\u5e38\u6c0f\u592b\u5987\u82e6\u82e6\u5bfb\u8bbf\uff0c\u590f\u82b8\u68a6\u9b42\u96be\u5fd8\u65f6\u718a\u501c\u3002 \u3000\u3000\u53e6\u4e00\u4eba\u81ea\u662f\u5c1a\u672a\u660e\u4e86\u3002 \u3000\u3000\u56db\u4eba\u60ca\u559c\u4ea4\u96c6\uff0c\u4e00\u65f6\u7adf\u9f50\u90fd\u6115\u4f4f\u4e86\u3002 \u3000\u3000\u7530\u654f\u654f\u5fc3\u91cc\u7a81\u7136\u4e00\u9635\u96be\u53d7\uff0c\u6697\u5fd6\uff1a\u201c\u8fd9\u600e\u4e48\u529e\uff0c\u501c\u54e5\u54e5\u6765\u4e86\uff0c\u82b8\u59b9\u59b9\u5374\u53c8\u4e0d\u89c1\u4e86\uff0c\u5509\uff0c \u8fd9\u6559\u6211\u600e\u4e48\u5bf9\u718a\u501c\u8bf4\u5462\uff1f\u201d \u3000\u3000\u80fd\u501c\u4e5f\u81ea\u53d1\u73b0\u5e38\u6f2b\u5929\u592b\u5987\u9762\u8272\u7684\u96be\u770b\uff0c\u4e0d\u77e5\u600e\u5730\uff0c\u5fc3\u91cc\u7a81\u7136\u7d27\u5f20\u8d77\u6765\uff0c\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053 \u662f\u4ec0\u4e48\u539f\u56e0\u7740\u6025\u5730\u95ee\u9053\uff1a\u201c\u5e38\u5927\u54e5\uff0c\u96be\u9053\u6709\u4ec0\u4e48\u4e8b\u60c5\u53d1\u751f\u4e86\uff1f\u201d\u4eba\u7c7b\u7684\u5fc3\u7406\uff0c\u6709\u65f6\u7684\u786e\u5947\u5999 \u5f97\u5f88\uff0c\u5e38\u5e38\u4f1a\u6709\u4e00\u79cd\u7a81\u6765\u7684\u611f\u89c9\uff0c\u9884\u5146\u7740\u4e00\u4e9b\u81ea\u5df1\u5fc3\u91cc\u6700\u5173\u6000\u7684\u4e8b\uff0c\u8fd9\u662f\u4efb\u4f55\u4eba\u90fd\u65e0\u6cd5\u89e3\u91ca \u7684\u3002 \u3000\u3000\u5e38\u6f2b\u5929\u55eb\u5685\u7740\uff0c\u7ec8\u4e8e\u8bf4\u4e86\u51fa\u6765\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u6765\u665a\u4e86\u4e00\u6b65\u3002\u201d \u3000\u3000\u718a\u501c\u4e00\u542c\uff0c\u5fc3\u60c5\u66f4\u50cf\u662f\u62c9\u7d27\u7684\u5f13\u5f26\uff0c\u5fd9\u9053\uff1a\u201c\u5e38\u5927\u54e5\uff0c\u5230\u5e95\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u201c\u82b8\u59b9\u59b9\u4e0d\u77e5\u88ab\u4ec0\u4e48\u63b3\u53bb\u4e86\u3002\u201d\u7530\u654f\u654f\u65e0\u6cd5\u518d\u5fcd\u4f4f\u5fc3\u91cc\u7684\u8bdd\uff0c\u4e00\u4e94\u4e00\u5341\u5730\u5c06\u4ed6\u4eec\u5982\u4f55\u78b0 \u5230\u590f\u82b8\uff0c\u5982\u4f55\u4e00\u9f50\u627e\u718a\u501c\uff0c\u5982\u4f55\u5728\u8def\u4e0a\u590f\u82b8\u4e00\u4eba\u5148\u8d70\uff0c\u5982\u4f55\u542c\u5230\u5657\u54a4\u4e4b\u58f0\uff0c\u7b49\u5230\u81ea\u5bb6\u8d76\u6765 \u65f6\uff0c\u5df2\u5931\u53bb\u4e86\u590f\u82b8\u7684\u8e2a\u8ff9\uff0c\u5168\u544a\u8bc9\u4e86\u718a\u501c\u3002 \u3000\u3000\u201c\u672c\u6765\u6211\u4e5f\u4e0d\u80fd\u786e\u5b9a\u82b8\u59b9\u59b9\u662f\u4e0d\u662f\u7ed9\u4eba\u63b3\u8d70\u4e86\u3002\u201d\u7530\u654f\u654f\u7d27\u98a6\u7740\u7709\uff0c\u8bf4\u9053\uff1a\u201c\u540e\u6765\u6211\u770b \u5230\u6211\u9001\u7ed9\u82b8\u59b9\u59b9\u7684\u5c0f\u94a2\u4e38\uff0c\u96f6\u843d\u5730\u6389\u5728\u5730\u4e0a\uff0c\u8fd9\u79cd\u5c0f\u94a2\u4e38\u8fd8\u662f\u5148\u7236\u5236\u4f5c\u7684\uff0c\u5f62\u5f0f\u3001\u529f\u7528\u5374\u4e0d \u548c\u666e\u901a\u7684\u94a2\u4e38\u4e00\u6837\uff0c\u6c5f\u6e56\u4e0a\u518d\u4e5f\u6ca1\u6709\u7b2c\u4e8c\u4e2a\u4eba\u6709\u8fd9\u79cd\u94a2\u4e38\uff0c\u6240\u4ee5\u6211\u624d\u80fd\u786e\u5b9a\u8fd9\u70b9\u3002\u201d \u3000\u3000\u718a\u501c\u4e00\u9762\u542c\uff0c\u989d\u4e0a\u7684\u6c57\u73e0\u5f80\u4e0b\u7c41\u7c41\u800c\u843d\uff0c\u4ed6\u7126\u6025\u7684\u795e\u8272\uff0c\u4f7f\u5f97\u5e38\u6f2b\u5929\u592b\u5987\u66f4\u4e0d\u5b89\u4e86\u3002 \u3000\u3000\u56db\u4eba\u4e4b\u4e2d\uff0c\u5c1a\u672a\u660e\u6b64\u523b\u7684\u5934\u8111\u53ef\u7b97\u662f\u6700\u51b7\u9759\u7684\uff0c\u4ed6\u9759\u542c\u7740\uff0c\u6c89\u601d\u4e86\u534a\u664c\uff0c\u7136\u540e\u8bf4\uff1a\u201c\u5927 \u54e5\uff0c\u6211\u770b\u8fd9\u4e8b\u597d\u529e\u5f97\u5f88\u3002\u201d \u3000\u3000\u7530\u654f\u654f\u9053\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u529e\u6cd5\uff1f\u201d \u3000\u3000\u5c1a\u672a\u660e\u9053\uff1a\u201c\u9664\u4e86\u6b66\u5f53\u56db\u5b50\u4e4b\u5916\uff0c\u8c01\u4e5f\u4e0d\u4f1a\u5c06\u5979\u63b3\u8d70\uff0c\u6211\u4eec\u53ea\u8981\u518d\u53bb\u4e00\u8d9f\u6b66\u5f53\u5c71\uff0c\u4e0d\u5c31 \u4e00\u5b9a\u53ef\u4ee5\u77e5\u9053\u5979\u7684\u4e0b\u843d\u4e86\u3002\u201d \u3000\u3000\u4ed6\u7684\u8bdd\u7acb\u523b\u5f97\u5230\u4e86\u718a\u501c\u7b49\u4e09\u4eba\u7684\u540c\u610f\u3002 \u3000\u3000\u5e38\u6f2b\u5929\u5ffd\u7136\u60f3\u8d77\u4e86\u90a3\u5929\u5728\u8def\u4e0a\u78b0\u5230\u6b66\u5f53\u9053\u4eba\u98de\u9e64\u5b50\u7684\u4e8b\uff0c\u9042\u4e5f\u5bf9\u718a\u501c\u8bf4\u4e86\u3002 \u3000\u3000\u718a\u501c\u6b64\u523b\u5168\u5fc3\u5168\u610f\u90fd\u653e\u5728\u590f\u82b8\u8eab\u4e0a\uff0c\u5bf9\u5176\u4ed6\u4efb\u4f55\u4e8b\u90fd\u4e0d\u5728\u610f\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u718a\u501c\u7b49\u56db\u4eba\uff0c\u5fc3\u76ee\u4e2d\u90fd\u51e0\u4e4e\u5df2\u786e\u5b9a\u4e86\u4e00\u4e2a\u89c2\u5ff5\uff0c\u90a3\u5c31\u662f\uff1a\u590f\u82b8\u6beb\u65e0\u7591\u95ee\u5730\u4e00\u5b9a\u662f\u88ab \u6b66\u5f53\u56db\u5b50\u52ab\u8d70\u4e86\u3002 \u3000\u3000\u8fd9\u5c31\u662f\u4eba\u7c7b\u601d\u60f3\u7684\u5f31\u70b9\uff0c\u5728\u5f77\u5fa8\u65e0\u8ba1\u7684\u65f6\u5019\uff0c\u53ea\u8981\u6709\u4e00\u4e2a\u60f3\u6cd5\u63a5\u8fd1\u4e8b\u5b9e\uff0c\u90a3\u4e48\u65e0\u8bba\u8fd9\u60f3 \u6cd5\u662f\u5426\u6b63\u786e\uff0c\u4ed6\u90fd\u4f1a\u56fa\u6267\u5730\u786e\u4fe1\u4e0d\u7591\u3002 \u3000\u3000\u8fd9\u5c31\u5982\u540c\u4e00\u4e2a\u4e0d\u4f1a\u6c34\u7684\u4eba\u843d\u5165\u6c34\u4e2d\uff0c\u6323\u624e\u4e4b\u9645\u53ea\u8981\u6293\u7740\u4efb\u4f55\u4e00\u7247\u4e1c\u897f\uff0c\u4ed6\u5c31\u4e0d\u7ba1\u90a3\u4e1c\u897f \u662f\u5426\u6551\u5f97\u4ed6\u7684\u751f\u547d\uff0c\u4ed6\u4e5f\u4f1a\u7d27\u6293\u4e0d\u653e\u7684\u3002 \u3000\u3000\u718a\u501c\u7b49\u4eba\u6b64\u523b\u4e5f\u6b63\u662f\u8fd9\u79cd\u5fc3\u7406\u3002 \u3000\u3000\u4f55\u51b5\u5b9e\u9645\u4e0a\uff0c\u82e5\u4ee5\u60c5\u7406\u6765\u8bba\uff0c\u590f\u82b8\u7684\u5931\u8e2a\u4e5f\u53ea\u6709\u8fd9\u4e00\u79cd\u63a8\u6d4b\u6700\u5408\u7406\u4e86\u3002 \u3000\u3000\u54ea\u77e5\u9053\u4e8b\u5b9e\u5374\u5927\u8c2c\u4e0d\u7136\u2026\u2026 \u3000\u3000\u5728\u5e38\u6f2b\u5929\u592b\u5987\u6069\u7231\u5730\u6253\u60c5\u9a82\u4fcf\u7684\u65f6\u5019\uff0c\u590f\u82b8\u5fc3\u60c5\u7684\u843d\u5bde\uff0c\u662f\u53ef\u60f3\u800c\u77e5\u7684\uff0c\u5979\u9664\u4e86\u6709\u4e9b\u96be \u53d7\u4e4b\u5916\uff0c\u751a\u81f3\u8fd8\u5f00\u59cb\u6709\u4e86\u60f3\u5bb6\u5ff5\u5934\uff0c\u53ea\u662f\u5979\u7684\u601d\u4eb2\u4e4b\u60c5\uff0c\u8fd8\u4e0d\u5982\u601d\u5ff5\u718a\u501c\u6765\u5f97\u5f3a\u70c8\u800c\u5df2\u3002 \u3000\u3000\u4e8e\u662f\u5979\u5b64\u96f6\u5730\u7b56\u7740\u9a6c\uff0c\u8fdc\u8fdc\u8d70\u5728\u524d\u9762\u3002 \u3000\u3000\u6e10\u6e10\uff0c\u5979\u5c06\u5e38\u6f2b\u5929\u592b\u5987\u629b\u5f97\u5f88\u8fdc\uff0c\u5979\u4e5f\u5e76\u4e0d\u5728\u610f\uff0c\u56e0\u4e3a\u8def\u662f\u7b14\u76f4\u7684\uff0c\u800c\u4e14\u53ea\u6709\u4e00\u6761\uff0c\u6ca1 \u6709\u6b67\u8def\u3002 \u3000\u3000\u90a3\u4e48\u5e38\u6f2b\u5929\u592b\u5987\u9664\u4e86\u6cbf\u7740\u8fd9\u6761\u8def\u8d70\u4e4b\u5916\uff0c\u522b\u65e0\u5176\u4ed6\u7684\u9009\u62e9\u3002 \u3000\u3000\u5979\u81ea\u5e7c\u9a91\u9a6c\uff0c\u5bf9\u9a6c\u6027\u7684\u719f\u6089\uff0c\u5b9b\u5982\u5979\u719f\u6089\u81ea\u5df1\u7684\u817f\u4e00\u6837\u3002 \u3000\u3000\u662f\u4ee5\u5979\u9a91\u5728\u9a6c\u4e0a\u7684\u59ff\u52bf\uff0c\u770b\u8d77\u6765\u90a3\u4e48\u5b89\u8be6\u800c\u8212\u9002\u3002 \u3000\u3000\u9a6c\u97ad\u6325\u8d77\uff0c\u53c8\u843d\u4e0b\uff0c\u5176\u5b9e\u5e76\u6ca1\u6709\u843d\u5728\u9a6c\u7684\u8eab\u4e0a\uff0c\u53ea\u662f\u5979\u5728\u53d1\u6cc4\u5fc3\u4e2d\u5806\u79ef\u7684\u5fe7\u90c1\u800c\u5df2\u3002 \u3000\u3000\u8fd9\u6761\u8def\u867d\u7136\u662f\u9102\u57ce\u901a\u5f80\u6b66\u6c49\u7684\u8981\u6d25\uff0c\u4f46\u5947\u602a\u7684\u662f\uff0c\u6b64\u523b\u8def\u4e0a\u7adf\u7136\u6ca1\u6709\u4ec0\u4e48\u884c\u4eba\u3002 \u3000\u3000\u5979\u5b64\u5bc2\u5730\u8d70\u7740\uff0c\u54fc\u8d77\u4e00\u6bb5\u5979\u7ae5\u5e74\u6240\u719f\u6089\u7684\u5c0f\u8c03\uff0c\u6253\u53d1\u8fd9\u96be\u5fcd\u7684\u5c91\u5bc2\u3002 \u3000\u3000\u84e6\u5730\uff0c\u8fdc\u8fdc\u4f20\u6765\u4e00\u9635\u6025\u8fd9\u7684\u8e44\u58f0\u3002 \u3000\u3000\u63a5\u7740\uff0c\u8def\u5934\u5c18\u571f\u98de\u626c\uff0c\u5b9b\u5982\u4e00\u6761\u7070\u9f99\uff0c\u5a49\u8712\u800c\u6765\u3002 \u3000\u3000\u201c\u8fd9\u9a6c\u8d70\u5f97\u597d\u5feb\uff01\u201d\u5979\u5fc3\u91cc\u601d\u5fd6\u7740\uff0c\u5bf9\u4e8e\u9a6c\uff0c\u5979\u53ef\u4ee5\u8bf4\u662f\u4e86\u89e3\u5f97\u5927\u6e05\u695a\u4e86\uff0c\u662f\u4ee5\u5bf9\u4e8e\u597d \u9a6c\uff0c\u65e0\u8bba\u90a3\u9a6c\u662f\u8c01\u7684\uff0c\u5979\u90fd\u4f1a\u6709\u4e00\u4efd\u7231\u62a4\u7684\u60c5\u611f\uff0c\u8fd9\u6b63\u5982\u7231\u624d\u7684\u4eba\u7231\u62a4\u6709\u624d\u6c14\u7684\u4eba\u4e00\u6837\u3002 \u3000\u3000\u5979\u7559\u610f\u5730\u671b\u7740\u90a3\u5339\u9a6c\u7684\u6765\u52bf\u2026\u2026 \u3000\u3000\u90a3\u9a6c\u604d\u773c\u4fbf\u6765\u5230\u8fd1\u524d\uff0c\u604d\u773c\u4fbf\u7535\u95ea\u800c\u8fc7\u2026\u2026 \u3000\u3000\u5979\u4eff\u4f5b\u89c9\u5f97\u9a6c\u4e0a\u7684\u9a91\u58eb\u9762\u5bb9\u719f\u6089\u5df2\u6781\uff0c\u4f46\u662f\u5979\u5374\u8bb0\u4e0d\u5f97\u662f\u5728\u54ea\u91cc\u89c1\u8fc7\u7684\u4e86\u3002 \u3000\u3000\u5979\u6b63\u5728\u4e0b\u610f\u8bc6\u5730\u601d\u7d22\u7740\u90a3\u5339\u9a6c\u4e0a\u7684\u9a91\u58eb\uff0c\u662f\u5728\u4f55\u5904\u89c1\u9762\u7684\u65f6\u5019\u3002 \u3000\u3000\u54ea\u77e5\u90a3\u5339\u9a6c\u5954\u8dd1\u4e86\u4e0d\u8fdc\uff0c\u6253\u4e86\u4e2a\u5708\u5b50\uff0c\u7ed5\u4e86\u56de\u6765\u3002 \u3000\u3000\u5979\u89c9\u5f97\u5947\u602a\uff0c\u66f4\u4ee4\u5979\u5947\u602a\u7684\u662f\u90a3\u5339\u9a6c\u5954\u5230\u5979\u9762\u524d\u65f6\uff0c\u7adf\u500f\u5730\u505c\u4f4f\u4e86\u3002 \u3000\u3000\u5979\u77dc\u6301\u5730\u5c06\u5934\u4fa7\u5230\u53e6\u4e00\u65b9\uff0c\u6697\u9a7e\u8fd9\u4eba\u597d\u751f\u65e0\u7406\uff0c\u5979\u7740\u4e0d\u662f\u6b64\u523b\u6101\u601d\u767e\u7ed3\uff0c\u6015\u4e0d\u65e9\u5c31\u56de\u8fc7 \u5934\u53bb\u7ed9\u8fd9\u65e0\u7406\u7684\u9a91\u58eb\u4e00\u4e2a\u6559\u8bad\u4e86\u3002 \u3000\u3000\u9a6c\u4e0a\u7684\u9a91\u58eb\u50cf\u662f\u9a84\u72c2\u5df2\u6781\uff0c\u7adf\u4fa7\u8fc7\u4e86\u5934\u6ce8\u610f\u7aef\u8be6\u590f\u82b8\u7684\u4fa7\u9762\u3002 \u3000\u3000\u590f\u82b8\u67f3\u7709\u4e00\u7ad6\uff0c\u5fcd\u4e0d\u4f4f\u8981\u60f3\u53d1\u4f5c\u3002 \u3000\u3000\u54ea\u77e5\u90a3\u9a6c\u4e0a\u7684\u9a91\u58eb\u7a81\u7136\u9ad8\u58f0\u7b11\u4e86\u8d77\u6765\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u771f\u6559\u4eba\u751f\u4f55\u5904\u4e0d\u76f8\u9022\uff0c\u5c0f\u53ef\u5b9e\u5728 \u60f3\u4e0d\u5230\u4eca\u65e5\u7adf\u80fd\u5728\u6b64\u5904\u9047\u5230\u59d1\u5a18\u3002\u201d \u3000\u3000\u590f\u82b8\u4e00\u60ca\uff0c\u6697\u5fd6\uff1a\u201c\u8fd9\u4eba\u7adf\u8ba4\u5f97\u6211\u3002\u201d\u597d\u5947\u5fc3\u5927\u8d77\uff0c\u6012\u706b\u5012\u6d88\u5931\u4e86\u4e0d\u5c11\uff0c\u6389\u56de\u4e86\u5934\uff0c\u770b \u5230\u90a3\u9a6c\u4e0a\u9a91\u58eb\u7684\u9762\u8c8c\uff0c\u201c\u54e6\u201d\u5730\u4e00\u58f0\uff0c\u53eb\u51fa\u58f0\u6765\u3002 \u3000\u3000\u201c\u539f\u6765\u662f\u4f60\u3002\u201d\u5979\u53d1\u73b0\u8fd9\u9a6c\u4e0a\u7684\u9a91\u58eb\u5c31\u662f\u66fe\u7ecf\u88ab\u5979\u5236\u4f4f\u8fc7\u5750\u9a91\u7684\u534e\u670d\u4f69\u5251\u7684\u9a84\u72c2\u5c11\u5e74\u3002 \u3000\u3000\u539f\u6765\u9a6c\u4e0a\u7684\u5c11\u5e74\uff0c\u5c31\u662f\u5b64\u5cf0\u4e00\u5251\u8fb9\u6d69\u3002 \u3000\u3000\u4ed6\u5728\u6c5f\u8fb9\u4e0e\u5c1a\u672a\u660e\u4e00\u756a\u5267\u6218\u4e4b\u540e\uff0c\u53c8\u9047\u5230\u90a3\u4e24\u4f4d\u5947\u8be1\u800c\u6b66\u529f\u9ad8\u6df1\u7684\u8001\u5e74\u4eba\u3002 \u3000\u3000\u4ed6\u806a\u660e\u7edd\u9876\uff0c\u77e5\u9053\u81ea\u5df1\u7684\u6b66\u529f\uff0c\u7edd\u4e0d\u662f\u8fd9\u4e24\u4f4d\u8001\u5e74\u4eba\u7684\u654c\u624b\u3002 \u3000\u3000\u7ecf\u8fc7\u4e00\u756a\u6743\u8861\u4e4b\u540e\uff0c\u4ed6\u843d\u8352\u800c\u9003\uff0c\u8c01\u77e5\u90a3\u8001\u5e74\u4eba\u5e76\u6ca1\u6709\u8ffd\u8d76\u4ed6\uff0c\u4ed6\u624d\u957f\u957f\u5730\u5598\u4e86\u4e00\u53e3 \u6c14\u3002 \u3000\u3000\u800c\u4ed6\u6765\u5230\u6c5f\u5357\u4e4b\u540e\uff0c\u4e0d\u51fa\u6570\u6708\uff0c\u51e0\u6b21\u9047\u5230\u5f3a\u52b2\u7684\u5bf9\u624b\uff0c\u72c2\u50b2\u4e4b\u6c14\uff0c\u4e0d\u514d\u4e3a\u4e4b\u7a0d\u7a0d\u524a\u51cf\uff0c \u4f46\u662f\u4ed6\u4e0e\u751f\u800c\u6765\u7684\u6027\u683c\uff0c\u5374\u5e76\u672a\u56e0\u6b64\u800c\u5927\u6709\u6539\u53d8\uff0c\u53ea\u4e0d\u8fc7\u9047\u4eba\u9047\u4e8b\uff0c\u53d8\u5f97\u66f4\u4e3a\u8be1\u8bc8\u800c\u5df2\u3002 \u3000\u3000\u5bf9\u4e8e\u718a\u501c\uff0c\u4ed6\u6068\u4eba\u5207\u9aa8\uff0c\u8fd9\u6000\u6068\u7684\u539f\u56fa\uff0c\u7edd\u5927\u90e8\u5206\u662f\u56e0\u4e3a\u5ac9\u5992\u3002 \u3000\u3000\u987b\u77e5\u4efb\u4f55\u4e00\u4e2a\u72c2\u50b2\u7684\u4eba\uff0c\u4ed6\u7684\u5ac9\u5992\u4e4b\u5fc3\uff0c\u7edd\u5bf9\u6bd4\u5e38\u4eba\u5f3a\u70c8\uff0c\u6c38\u8fdc\u4e0d\u80fd\u5fcd\u53d7\u4efb\u4f55\u4e00\u4e2a\u4eba\uff0c \u6709\u4efb\u4f55\u5730\u65b9\u5f3a\u8fc7\u4e8e\u81ea\u5df1\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u5bf9\u4e8e\u718a\u501c\u662f\u65e0\u53ef\u5948\u4f55\u7684\u2026\u2026 \u3000\u3000\u5076\u7136\u5730\uff0c\u4ed6\u7ecf\u8fc7\u8fd9\u4eb2\u81ea\u6b66\u6c49\u901a\u5f80\u9102\u57ce\u7684\u9053\u8def\uff0c\u9a70\u9a6c\u5954\u817e\u4e2d\uff0c\u4ed6\u770b\u5230\u5bf9\u9762\u8e48\u8e48\u7b56\u9a6c\u72ec\u884c\u7684 \u5c11\u5973\uff0c\u7adf\u662f\u90a3\u5929\u5728\u82cf\u5dde\u8857\u5934\u5236\u4f4f\u4ed6\u7684\u5750\u9a91\u548c\u718a\u501c\u540c\u884c\u7684\u5c11\u5973\uff0c\u4e8e\u662f\u4ed6\u53c8\u7b56\u8f6c\u9a6c\u5934\uff0c\u7ed5\u4e86\u56de \u6765\u3002 \u3000\u3000\u4ed6\u770b\u5230\u590f\u82b8\u5c45\u7136\u8fd8\u8bb0\u5f97\u4ed6\uff0c\u5fc3\u4e2d\u4e0d\u7981\u6709\u4e9b\u9ad8\u5174\uff0c\u56e0\u4e3a\u4ed6\u81ea\u7b2c\u4e00\u773c\u671b\u89c1\u590f\u82b8\u7684\u65f6\u5019\uff0c\u5c31\u5bf9 \u590f\u82b8\u8d77\u4e86\u975e\u5e38\u5927\u7684\u597d\u611f\u3002 \u3000\u3000\u201c\u718a\u501c\u718a\u5927\u4fa0\u600e\u5730\u6ca1\u6709\u548c\u59d1\u5a18\u4e00\u8def\uff1f\u201d\u4ed6\u806a\u660e\u7684\u6253\u5f00\u4e86\u8bdd\u9898\u3002 \u3000\u3000\u679c\u7136\u590f\u82b8\u4e00\u542c\u5230\u718a\u501c\u7684\u540d\u5b57\uff0c\u6d51\u7136\u5fd8\u5374\u4e86\u4e00\u5207\uff0c\u5fd8\u5f62\u4ed6\u8bf4\uff1a\u201c\u600e\u4e48\uff0c\u4f60\u770b\u5230\u501c\u54e5\u54e5\uff1f\u201d \u7126\u6025\u548c\u5fc6\u5ff5\u7684\u60c5\u611f\uff0c\u6ea2\u4e8e\u8a00\u8868\u3002 \u3000\u3000\u5b64\u5cf0\u4e00\u5251\u8fb9\u6d69\u5fc3\u91cc\uff0c\u7acb\u65f6\u8d77\u4e86\u4e00\u9635\u9178\u6e9c\u6e9c\u7684\u611f\u89c9\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u6781\u529b\u5730\u5fcd\u8010\u7740\uff0c\u8bd5\u63a2\u7740\u8bf4\uff1a\u201c\u59d1\u5a18\u96be\u9053\u8981\u627e\u4ed6\uff1f\u201d \u3000\u3000\u8fb9\u6d69\u773c\u73e0\u4e00\u8f6c\uff0c\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\u4e0d\u662f\u4e00\u4e2a\u4eba\u5417\uff1f\u201d \u3000\u3000\u590f\u82b8\u9053\uff1a\u201c\u8fd8\u6709\u4eba\u5728\u540e\u9762\u3002\u201d \u3000\u3000\u8fb9\u6d69\u9053\uff1a\u201c\u59d1\u5a18\u8981\u627e\u718a\u5144\u5f1f\uff0c\u78b0\u5230\u6211\u662f\u518d\u597d\u6ca1\u6709\u4e86\u2026\u2026\u3001\u590f\u82b8\u9ad8\u5174\u5730\u95ee\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4ed6 \u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u8fb9\u6d69\u671d\u56db\u5468\u770b\u4e86\u770b\uff0c\u770b\u5230\u8def\u7684\u65c1\u8fb9\u5c31\u662f\u5c0f\u5c0f\u7684\u6811\u6797\u5b50\uff0c\u6545\u4f5c\u795e\u79d8\u4ed6\u8bf4\uff1a\u201c\u8fd9\u91cc\u4e0d\u662f\u8bf4\u8bdd \u4e4b\u5904\uff0c\u59d1\u5a18\u5982\u679c\u65b9\u4fbf\u7684\u8bdd\uff0c\u6700\u597d\u5230\u90a3\u8fb9\u7684\u6811\u6797\u91cc\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u590f\u82b8\u4eba\u4e16\u592a\u6d45\uff0c\u867d\u7136\u5403\u8fc7\u4e0d\u5c11\u4e8f\uff0c\u4f46\u662f\u5979\u4ecd\u7136\u5bf9\u4e16\u4e8b\u662f\u758f\u5ffd\u7684\uff0c\u5634\u91cc\u8bf4\u9053\uff1a\u201c\u4ed6\u5230\u5e95\u5728 \u54ea\u91cc\uff1f\u201d\u624b\u4e2d\u9a6c\u7f30\u5411\u5de6\u4e00\u5e26\uff0c\u5374\u8ddf\u7740\u5b64\u5cf0\u4e00\u5251\u8fb9\u6d69\uff0c\u8d70\u8fdb\u4e86\u6811\u6797\u3002 \u3000\u3000\u90a3\u6811\u6797\u5e76\u4e0d\u592a\u5bc6\uff0c\u9633\u5149\u81ea\u679d\u53f6\u4e2d\uff0c\u4ecd\u53ef\u4ee5\u758f\u758f\u5730\u7167\u8fdb\u6765\uff0c\u6811\u6797\u4e2d\u5374\u6e3a\u65e0\u4eba\u8e2a\uff0c\u5076\u95fb\u9e1f\u8bed \u8c03\u7785\uff0c\u663e\u5f97\u751a\u662f\u5bc2\u5bde\u3002 \u3000\u3000\u8fb9\u6d69\u9053\uff1a\u201c\u59d1\u5a18\u8bb8\u4e45\u4e0d\u89c1\uff0c\u5374\u8d8a\u6765\u8d8a\u6f02\u4eae\u4e86\u3002\u201d \u3000\u3000\u590f\u82b8\u9053\uff1a\u201c\u5582\uff0c\u501c\u54e5\u54e5\u5230\u5e95\u5728\u54ea\u91cc\uff0c\u4f60\u5012\u662f\u5feb\u8bf4\u5440\u3002\u201d \u3000\u3000\u8fb9\u6d69\u9053\uff1a\u201c\u59d1\u5a18\u5012\u771f\u6027\u6025\u5f97\u5f88\u3002\u201d \u3000\u3000\u590f\u82b8\u62ac\u5934\u4e00\u671b\uff0c\u9633\u5149\u4ece\u6811\u6797\u7684\u4e0a\u9762\u5c04\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u9633\u5149\u7167\u5f97\u5979\u9762\u5b54\u4e00\u7247\u5ae3\u7ea2\uff0c\u5b64\u5cf0\u4e00\u5251\u8fb9\u6d69\u5fc3\u5934\u6026\u7136\u5927\u52a8\uff0c\u4ed6\u672c\u975e\u597d\u8272\u4e4b\u5f92\uff0c\u4f46\u6b64\u65f6\u5fc3\u4e2d \u5374\u4e0d\u77e5\u600e\u5730\u5347\u8d77\u4e00\u79cd\u90aa\u6076\u7684\u6b32\u671b\u3002 \u3000\u3000\u590f\u82b8\u518d\u4e00\u62ac\u5934\uff0c\u671b\u89c1\u8fd9\u534e\u670d\u5c11\u5e74\u2014\u2014\u5b64\u5cf0\u4e00\u5251\u8fb9\u6d69\u7684\u4e24\u53ea\u773c\u775b\u8fd8\u76f4\u52fe\u52fe\u5730\u671b\u7740\u81ea\u5df1\u3002 \u3000\u3000\u5979\u5929\u771f\u672a\u6cef\uff0c\u7adf\u672a\u80fd\u5206\u8fa8\u51fa\u4ed6\u773c\u4e2d\u7684\u6deb\u90aa\u3002 \u3000\u3000\u4e24\u4eba\u76ee\u5149\u76f8\u5bf9\uff0c\u5b64\u5cf0\u4e00\u5251\u8fb9\u6d69\u66f4\u662f\u7d27\u7d27\u5730\u631a\u4f4f\u5979\u7684\u76ee\u5149\uff0c\u518d\u4e5f\u820d\u4e0d\u5f97\u653e\u677e\u4e00\u65f6\u534a\u523b\u3002 \u3000\u3000\u590f\u82b8\u4e00\u4fa7\u8138\uff0c\u4e5f\u5fae\u5fae\u6709\u4e9b\u53d1\u89c9\u4e86\u4ed6\u76ee\u5149\u4e2d\u7684\u5f02\u6837\uff0c\u6025\u5fd9\u907f\u5f00\u4e86\uff0c\u5a07\u55d4\u9053\uff1a\u201c\u5582\uff0c\u4f60\u5230\u5e95 \u5728\u73a9\u4ec0\u4e48\u82b1\u6837\uff1f\u201d \u3000\u3000\u5b64\u5cf0\u4e00\u5251\u5fae\u5fae\u6709\u4e9b\u53d1\u7a98\uff0c\u652f\u543e\u4ed6\u8bf4\u9053\uff1a\u201c\u718a\u2014\u2014\u718a\u5927\u54e5\u2014\u2014\u6b64\u523b\u4ed6\u53ea\u6015\u5df2\u2014\u2014\u201d\u590f\u82b8\u62a2 \u7740\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff0c\u96be\u9053\u501c\u54e5\u54e5\u4ed6\u2014\u2014\u4ed6\u5df2\u7ecf\u906d\u4e86\u8c01\u7684\u6bd2\u624b\u4e86\u5417\uff1f\u201d \u3000\u3000\u8fb9\u6d69\u6545\u4f5c\u4e3a\u96be\u5730\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u590f\u82b8\u8033\u7554\u987f\u7136\u55e1\u7136\u4e00\u58f0\uff0c\u50cf\u662f\u7a81\u7136\u5931\u53bb\u4e86\u91cd\u5fc3\uff0c\u51e0\u4e4e\u518d\u4e5f\u7a33\u4e0d\u4f4f\u5750\u5728\u9a6c\u80cc\u4e0a\u7684\u8eab\u8eaf\u4e86\u3002 \u3000\u3000\u8fb9\u6d69\u770b\u89c1\u5979\u5931\u9b42\u843d\u9b44\u7684\u6a21\u6837\uff0c\u5fc3\u91cc\u9ad8\u5174\uff1a\u201c\u5979\u771f\u7684\u76f8\u4fe1\u4e86\u3002\u201d\u5374\u53c8\u4e0d\u514d\u96be\u8fc7\uff1a\u201c\u718a\u501c\u90a3 \u5c0f\u5b50\u771f\u6709\u798f\u6c14\uff0c\u5509\uff01\u82e5\u662f\u5979\u80fd\u5bf9\u6211\u6709\u5982\u6b64\u5173\u5fc3\uff0c\u90a3\u4e48\u6211\u5c31\u662f\u771f\u7684\u6b7b\u4e86\uff0c\u4e5f\u662f\u5fc3\u7518\u60c5\u613f\u7684\u3002\u201d \u3000\u3000\u826f\u4e45\uff0c\u590f\u82b8\u65b9\u81ea\u4ece\u8ff7\u60d8\u4e2d\u9192\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u5979\u82b3\u5fc3\u5927\u4e71\uff0c\u4e0d\u77e5\u600e\u751f\u662f\u597d\uff0c\u4e00\u62ac\u5934\uff0c\u671b\u89c1\u8fb9\u6d69\u8138\u4e0a\u7684\u90a3\u79cd\u5947\u5f02\u7684\u795e\u8272\uff0c\u7a81\u5730\u5fc3\u4e2d\u4e00 \u52a8\u3002 \u3000\u3000\u201c\u4f60\u8bf4\u7684\u8bdd\u662f\u771f\u662f\u5047\uff1f\u201d\u5979\u5389\u58f0\u95ee\u7740\u3002 \u3000\u3000\u5b64\u5cf0\u4e00\u5251\u4e00\u60ca\uff0c\u4ed6\u5230\u5e95\u4e8f\u5fc3\u4e4b\u4e8b\u505a\u5f97\u4e0d\u591a\uff0c\u8fd8\u4e0d\u80fd\u5b8c\u5168\u63a7\u5236\u7740\u81ea\u5df1\u795e\u8272\u7684\u4e0d\u5b89\u3002 \u3000\u3000\u4e8e\u662f\u60ca\u60f6\u4e4b\u8272\uff0c\u4e0d\u671f\u7136\u5730\u800c\u4ece\u4ed6\u9762\u4e0a\u6d41\u9732\u4e86\u51fa\u6765\u3002 \u3000\u3000\u590f\u82b8\u5230\u5e95\u4e0d\u662f\u5446\u5b50\uff0c\u5fc3\u91cc\u7684\u7591\u5fc3\u8d8a\u6765\u8d8a\u91cd\uff0c\u4f38\u624b\u5165\u6000\uff0c\u6697\u6697\u5730\u638f\u51fa\u51e0\u7c92\u5999\u8ba4\u7530\u654f\u654f\u5904\u53d6 \u6765\u7684\u7279\u5236\u5f39\u4e38\u3002"}),showOnHomepage:!0},{name:"\u7b2c\u4e5d\u7ae0\u3000\u6b66\u5f53\u5927\u4f1a\u76df",content:(0,s.jsx)(s.Fragment,{children:"\u4e09\u7c92\u8000\u773c\u7684\u94a2\u73e0\uff0c\u8131\u624b\u98de\u51fa\uff0c\u624b\u6cd5\u867d\u4e0d\u53ca\u7530\u654f\u654f\u90a3\u4e48\u5947\u5999\u83ab\u6d4b\uff0c\u4f46\u662f\u8fd1\u5728\u98d3\u5c3a\uff0c\u8df3\u4e38\u98de \u661f\uff0c\u800c\u89d2\u5ea6\u53c8\u90a3\u4e48\u5947\u5de7\uff0c\u50cf\u6709\u529b\u91cf\u64cd\u7eb5\u7740\uff0c\u8fc2\u56de\u6298\u5c04\u3002 \u3000\u3000\u8fb9\u6d69\u4e00\u9886\u9a6c\u7f30\uff0c\u62cd\u9a6c\u7a9c\u51fa\u4e08\u4f59\uff0c\u8eab\u4f53\u4e5f\u731b\u7136\u4e00\u4fef\uff0c\u5e73\u8d34\u9a6c\u80cc\uff0c\u8eb2\u8fc7\u653b\u51fb\u7684\u94a2\u73e0\uff0c\u5e76\u4e14\u6545 \u610f\u5730\u62cd\u9a6c\u9a70\u53bb\u3002\u4ed6\u5fc3\u4e2d\u6709\u4e2a\u7b97\u8ba1\uff0c\u8fd9\u4e00\u5e26\u6811\u6797\u5c31\u5728\u5b98\u9053\u65c1\uff0c\u591a\u5c11\u6709\u788d\u4ed6\u7684\u4e3e\u52a8\uff0c\u4e07\u4e00\u66f4\u4e0d\u5de7 \u718a\u501c\u5728\u6b64\u65f6\u51fa\u73b0\uff0c\u90a3\u53ef\u66f4\u4f7f\u4ed6\u53d7\u7a98\u4e86\u3002\u590f\u82b8\u5e76\u6ca1\u6709\u89c9\u5bdf\u5371\u673a\uff0c\u4e00\u5473\u62cd\u9a6c\u76f4\u8ffd\u3002 \u3000\u3000\u53cc\u9a91\u4e00\u524d\u4e00\u540e\uff0c\u6e10\u6e10\u79bb\u5f00\u4e86\u7ef5\u5ef6\u534a\u91cc\u591a\u7684\u6811\u6797\uff0c\u4ee5\u4ed6\u4eec\u7684\u9a91\u672f\u4e4b\u7cbe\uff0c\u4e0d\u8fc7\u6781\u77ed\u7684\u65f6\u95f4\u3002 \u6240\u4ee5\u540e\u6765\u718a\u501c\u5c1a\u672a\u660e\u4e0e\u5e38\u6f2b\u5929\u7530\u654f\u654f\u76f8\u9047\uff0c\u672a\u80fd\u5728\u9644\u8fd1\u627e\u7740\u590f\u82b8\uff0c\u53c8\u8fd9\u6837\u8f7b\u6613\u5730\u5931\u4e4b\u4ea4\u81c2 \u4e86\u3002 \u3000\u3000\u524d\u9762\u662f\u4e00\u7247\u8352\u51c9\uff0c\u6881\u5b50\u6e56\u7554\u4e00\u7247\u82a6\u82c7\u5730\u5e26\uff0c\u6e56\u6c34\u767d\u832b\u832b\u4e00\u671b\u65e0\u9645\uff0c\u51e0\u7247\u5e06\u5f71\u70b9\u7f00\u5728\u78a7\u6ce2 \u4e0a\u9762\u3002 \u3000\u3000\u6700\u8fd1\u5904\u6e14\u6751\u8305\u820d\uff0c\u4e5f\u5728\u4e00\u4e8c\u91cc\u5916\uff0c\u8fd9\u5730\u65b9\u5bf9\u4e8e\u4ed6\u662f\u975e\u5e38\u7406\u60f3\u7684\u3002 \u3000\u3000\u8fb9\u6d69\u62e8\u8f6c\u9a6c\u5934\uff0c\u62b1\u5251\u63d0\u9632\u7740\u8fd9\u4f4d\u59d1\u5a18\uff0c\u5fae\u98ce\u5439\u62c2\u7740\u590f\u82b8\u7684\u79c0\u53d1\uff0c\u5728\u9a6c\u4e0a\u82b1\u679d\u98a4\u6447\uff0c\u76ca\u589e \u59a9\u5a9a\u3002 \u3000\u3000\u8fb9\u6d69\u8fd9\u91cc\u51e0\u4e4e\u7eaf\u662f\u620f\u5f04\u7684\u6001\u5ea6\uff0c\u5411\u5979\u8bf4\uff1a\u201c\u59d1\u5a18\uff0c\u6211\u4eec\u518d\u8c08\u8c08\uff0c\u5c0f\u53ef\u5b64\u5cf0\u4e00\u5251\u8fb9\u6d69\uff0c\u53ea \u8fd8\u672a\u8bf7\u8fc7\u4f60\u7684\u5c0a\u59d3\u82b3\u540d\uff01\u4ee5\u59d1\u5a18\u7684\u63a7\u9a6c\u4e4b\u672f\uff0c\u60f3\u5fc5\u662f\u585e\u5916\u4e00\u9897\u660e\u73e0\u4e86\u3002\u201d \u3000\u3000\u590f\u82b8\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u62a5\u51fa\u59d3\u540d\u6765\uff0c\u96be\u9053\u6211\u5c31\u4e0d\u6562\u6597\u4f60\u8fd9\u5357\u5317\u53cc\u7edd\u5251\u4e48\uff1f\u201d \u3000\u3000\u8fb9\u6d69\u79bb\u6a59\u4e0b\u9a6c\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u90a3\u5c0f\u53ef\u5c31\u5949\u966a\u59d1\u5a18\u73a9\u73a9\uff01\u542c\u8bf4\u59d1\u5a18\u6012\u62d4\u6b66\u5f53\u6d3e\u4e38\u5bab\u8fde\u73af\u65d7\uff0c \u4f7f\u6211\u94a6\u4f69\u83ab\u540d\u5462\u3002\u201d \u3000\u3000\u590f\u82b8\u661f\u7738\u4e00\u51db\uff0c\u559d\u9053\uff1a\u201c\u5c11\u8bf4\u5e9f\u8bdd\u3002\u201d \u3000\u3000\u590f\u82b8\u4ece\u9a6c\u80cc\u65cb\u843d\u5730\u4e0a\uff0c\u624b\u4e2d\u76ae\u97ad\u4e00\u629b\u4e00\u6253\uff0c\u4f7f\u51fa\u201c\u72c2\u98d9\u97ad\u6cd5\u201d\uff0c\u5b9b\u5982\u534a\u622a\u4e4c\u9f99\uff0c\u98ce\u58f0\u864e \u864e\uff0c\u4e98\u53d6\u8fb9\u6d69\u3002 \u3000\u3000\u8fb9\u6d69\u5251\u5f71\u7f24\u7eb7\uff0c\u4f7f\u51fa\u751f\u5e73\u7edd\u6280\u7384\u5973\u5251\u6cd5\u3002 \u3000\u3000\u590f\u82b8\u97ad\u5f71\u4e1d\u4e1d\uff0c\u6f2b\u5929\u98ce\u96e8\uff0c\u4e00\u8fde\u4e32\u201c\u4e91\u5982\u5c71\u6d8c\u201d\u3001\u201c\u96e8\u6d12\u84ec\u83b1\u201d\uff0c\u51e0\u62db\u731b\u653b\uff0c\u4f7f\u8fb9\u6d69\u4e5f \u4e3a\u4e4b\u548b\u820c\uff0c\u6478\u4e0d\u6e05\u5979\u7684\u95e8\u8def\u3002 \u3000\u3000\u8fb9\u6d69\u5251\u843d\u5982\u540c\u98ce\u96e8\u9aa4\u81f3\uff0c\u6392\u7a7a\u8361\u6c14\uff0c\u5251\u5f71\u521d\u65f6\u8499\u8499\u6d12\u6d12\uff0c\u745e\u96ea\u7eb7\u98d8\uff0c\u7ee7\u800c\u5982\u540c\u75be\u96f7\u5954\u7535 \u5149\u6c14\u8427\u68ee\uff0c\u590f\u82b8\u7adf\u88ab\u4ed6\u88f9\u5728\u4e00\u56e2\u5251\u5f71\u91cc\u3002 \u3000\u3000\u8fb9\u6d69\u5251\u6cd5\u72ec\u5f97\u79d8\u4f20\uff0c\u58f0\u52bf\u4e0d\u900a\u4e8e\u56db\u4eea\u5251\u5ba2\u4e4b\u9996\u7684\u51cc\u4e91\uff0c\u4e0d\u8fc7\u4ed6\u6ca1\u5b58\u5fc3\u4f24\u5979\uff0c\u4e0b\u624b\u8ba9\u7740\u8bb8 \u591a\uff0c\u590f\u82b8\u65b9\u80fd\u52c9\u5f3a\u652f\u6301\u3002\u81ea\u7136\u8fd9\u79cd\u5c40\u52bf\u662f\u4e0d\u4f1a\u6c38\u4e45\u7ef4\u6301\u4e0b\u53bb\u7684\uff0c\u8fb9\u6d69\u9762\u5bf9\u7740\u5979\uff0c\u5a07\u8eaf\u5b9b\u8f6c\uff0c \u67f3\u8170\u6b3e\u6b3e\uff0c\u66f4\u53ef\u4ee5\u9971\u9910\u79c0\u8272\u3002 \u3000\u3000\u8fb9\u6d69\u7ec8\u4e8e\u627e\u5230\u4e86\u673a\u4f1a\uff0c\u4e58\u5979\u6325\u97ad\u731b\u70b9\u4ed6\u8170\u8179\u4e4b\u9645\uff0c\u64a4\u5251\u73af\u81c2\uff0c\u6b3a\u8eab\u659c\u8fdb\uff0c\u4e00\u62db\u201c\u6625\u96e8\u7ef5 \u7ef5\u201d\uff0c\u5251\u5149\u6e9c\u5411\u590f\u82b8\u7389\u8155\uff0c\u4e00\u56e2\u8000\u773c\u4e91\u82b1\uff0c\u75be\u63a3\u800c\u4e0b\u3002 \u3000\u3000\u590f\u82b8\u62fc\u4e86\u51e0\u5341\u62db\uff0c\u5fc3\u91cc\u6697\u8bf4\uff1a\u201c\u53f7\u79f0\u5357\u5317\u53cc\u7edd\u5251\u7684\uff0c\u4e5f\u4e0d\u8fc7\u5982\u6b64\u7f62\u4e86\uff01\u8ba9\u4f60\u77e5\u9053\u6211\u96ea\u5730 \u98d8\u98ce\u4e5f\u975e\u5f31\u8005\uff01\u201d \u3000\u3000\u4f46\u4eba\u5bb6\u8fd9\u6b21\u5251\u82b1\u903c\u6765\uff0c\u5982\u4e0d\u6492\u624b\u4e22\u97ad\uff0c\u5c31\u65e0\u6cd5\u95ee\u8ba9\uff0c\u590f\u82b8\u8fc7\u5206\u5014\u5f3a\uff0c\u9a84\u8eaf\u5f80\u5de6\u65b9\u98d8\u65cb\uff0c \u867d\u8db3\u95ea\u8fc7\u8fb9\u6d69\u8fd9\u4e00\u7edd\u5982\uff0c\u5374\u6070\u597d\u628a\u5de6\u8fb9\u8eab\u5b50\u51d1\u8fd1\u4e86\u4ed6\uff0c\u8fb9\u6d69\u733f\u81c2\u8f7b\u4f38\uff0c\u94c1\u8155\u5df2\u84e6\u5730\u63e1\u4f4f\u4e86\u5979 \u7684\u5de6\u81c2\u3002 \u3000\u3000\u590f\u82b8\u61ca\u6094\u6ca1\u6709\u7528\u7530\u59d0\u59d0\u6240\u6388\u6697\u5668\u5bf9\u4ed8\u4ed6\uff0c\u8fd9\u65f6\u5df2\u843d\u5165\u8fb9\u6d69\u638c\u63e1\u4e4b\u4e2d\uff0c\u6025\u5f97\u4e00\u58f0\u5c16\u53eb\uff0c\u60f3 \u6454\u81c2\u6323\u8131\uff0c\u66f4\u6015\u4ed6\u8fdb\u4e00\u6b65\u6765\u4ec0\u4e48\u82b1\u6837\uff0c\u731b\u4e00\u56de\u97ad\u6a2a\u62bd\u8fb9\u6d69\u90a3\u53ea\u8ba8\u538c\u7684\u624b\u3002 \u3000\u3000\u8fb9\u6d69\u5251\u5f71\u53c8\u8d77\uff0c\u6323\u7684\u4e00\u58f0\u628a\u90a3\u77ed\u77ed\u7684\u9a6c\u97ad\u53c8\u524a\u53bb\u534a\u622a\uff0c\u5251\u82b1\u5728\u590f\u82b8\u8138\u4e0a\u5212\u4e86\u5708\u513f\uff0c\u590f\u82b8 \u53ea\u6709\u95ed\u76ee\u7b49\u4eba\u5bb0\u5272\u4e86\uff0c\u53ef\u662f\u4ed6\u53c8\u5f88\u5feb\u7684\u628a\u5b9d\u5251\u64ce\u56de\u3002 \u3000\u3000\u8fb9\u6d69\u563b\u563b\u7b11\u4e86\uff0c\u7b11\u5f97\u975e\u5e38\u5f97\u610f\uff0c\u6e14\u7fc1\u94a9\u4e0a\u4e86\u5927\u9c7c\uff0c\u9c7c\u513f\u5df2\u7ecf\u4e0a\u94a9\uff0c\u53ea\u770b\u4ed6\u613f\u610f\u5982\u4f55\u5904\u6cbb \u635e\u83b7\u5230\u624b\u7684\u730e\u7269\u3002 \u3000\u3000\u8fb9\u6d69\u6001\u5ea6\u66f4\u4f7f\u5979\u96be\u582a\uff0c\u5df2\u7d27\u63e1\u590f\u82b8\u7389\u81c2\uff0c\u7528\u529b\u4e00\u5e26\uff0c\u590f\u82b8\u51e0\u4e4e\u8981\u6251\u8dcc\u4eba\u8fd9\u8ba8\u538c\u7537\u4eba\u6000 \u4e2d\uff0c\u5982\u4f55\u4e0d\u53c8\u7f9e\u53c8\u6025\uff0c\u8fb9\u6d69\u53cd\u800c\u67d4\u58f0\u7ec6\u6c14\u7684\u8bf4\uff1a\u201c\u59d1\u5a18\u7d2f\u4e86\u5427\uff01\u50cf\u59d1\u5a18\u8fd9\u4e00\u5957\u5947\u5999\u7684\u97ad\u6cd5\uff0c \u5c0f\u53ef\u8fd8\u662f\u521d\u6b21\u78b0\u4e0a\u5462\u3002\u59d1\u5a18\u53ef\u522b\u751f\u6c14\uff0c\u8d25\u5728\u5b64\u5cf0\u4e00\u5251\u624b\u4e2d\uff0c\u4e5f\u662f\u5f88\u5149\u8363\u7684\u5440\uff01\u201d \u3000\u3000\u590f\u82b8\u81ea\u5165\u5173\u4ee5\u6765\uff0c\u8fd9\u5df2\u662f\u7b2c\u4e09\u6b21\u5403\u4eba\u7684\u4e8f\uff0c\u800c\u6700\u4f7f\u5979\u96be\u582a\u7684\u5c31\u662f\u8fb9\u6d69\u90a3\u526f\u8d2a\u5a6a\u7684\u773c\u5149\uff0c \u548c\u90a3\u79cd\u5b58\u5fc3\u73a9\u5f04\u7684\u6001\u5ea6\u3002 \u3000\u3000\u8fd9\u65f6\u8fd1\u4fa7\u82a6\u82c7\u5bdf\u5bdf\u54cd\u8d77\uff0c\u86ee\u82cd\u8001\u7684\u7b11\u58f0\u5927\u4f5c\uff0c\u6559\u8bad\u5c0f\u5b69\u4f3c\u7684\u53e3\u543b\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u4e2a\u5201\u94bb \u5a03\u5a03\uff1a\u600e\u4e48\u5728\u6b64\u6b3a\u4fae\u5973\u5a03\u513f\uff1f\u6211\u8001\u5934\u5b50\u4e0a\u6b21\u6c5f\u8fb9\u8981\u6253\u4f60\u7684\u5c41\u80a1\uff0c\u88ab\u4f60\u5a03\u5a03\u98de\u4e86\uff01\u8fd9\u6b21\u53ef\u4e0d\u80fd \u8f7b\u9976\u4e86\uff01\u7167\u6253\uff01\u201d \u3000\u3000\u4e24\u4eba\u6b63\u5728\u53ae\u626d\u4e4b\u9645\uff0c\u7a81\u7136\u6bdb\u8017\u7ed5\u7684\u98de\u6765\u4e00\u56e2\u9ec4\u5f69\uff0c\u62cd\u7684\u4e00\u58f0\uff0c\u6070\u597d\u6253\u4e2d\u4e86\u5b64\u5cf0\u4e00\u5251\u8fb9\u6d69 \u6293\u4f4f\u590f\u82b8\u7684\u4e00\u53ea\u624b\uff0c\u8fb9\u6d69\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\uff0c\u90a3\u4ef6\u4e1c\u897f\u5ffd\u5566\u6563\u843d\u5730\u4e0a\uff0c\u5374\u662f\u4e00\u76d6\u67af\u5e72\u7684\u82c7\u53f6\uff0c \u7eb7\u98d8\u56db\u6563\u3002 \u3000\u3000\u53ef\u662f\u8fb9\u6d69\u8fd9\u53ea\u624b\u7adf\u5982\u6328\u4e0a\u4e00\u8bb0\u6781\u6c89\u91cd\u7684\u5927\u94f6\u9524\uff0c\u75db\u4eba\u9aa8\u9ad3\uff0c\u76ae\u8089\u6b32\u88c2\uff0c\u4ed6\u624b\u81c2\u5f88\u81ea\u7136\u7684 \u4e00\u677e\u4e00\u7f29\uff0c\u590f\u82b8\u4e58\u673a\u5f80\u65c1\u8fb9\u95ea\u51fa\u4e08\u4f59\u3002 \u3000\u3000\u4e0d\u8bf4\u4f55\u65f6\u9762\u524d\u5df2\u51fa\u73b0\u4e86\u4e00\u9ad8\u4e00\u77ee\u4e24\u4e2a\u67af\u7626\u5982\u67f4\u7684\u8001\u5934\u513f\uff0c\u800c\u90a3\u77ee\u8001\u5934\uff0c\u76d8\u819d\u5750\u5728\u6c99\u4e0a\uff0c \u6b63\u626c\u8d77\u53f3\u624b\u5411\u8fb9\u6d69\u62db\u547c\u9053\uff1a\u201c\u4f60\u8fd9\u5a03\u5a03\uff0c\u5feb\u8fc7\u6765\u9886\u6253\uff0c\u4e0d\u6298\u4e0d\u6263\u4e0a\u6b21\u7684\u4e00\u767e\u4e0b\u5c41\u80a1\uff0c\u4ee5\u540e\u4f60 \u8981\u8bb0\u4f4f\uff0c\u4e0d\u8bb8\u6b3a\u4fae\u5973\u5a03\u513f\uff01\u201d \u3000\u3000\u8fb9\u6d69\u6025\u5fd9\u8df3\u4e0a\u9a6c\u80cc\uff0c\u62e9\u97ad\u75be\u8d70\uff0c\u4ecd\u5411\u90a3\u7247\u6811\u6797\u7a7f\u6797\u5200\u6ca1\u3002 \u3000\u3000\u5750\u7740\u7684\u8001\u5934\u5411\u90a3\u9ad8\u4e2a\u5b50\u8001\u5934\u8bf4\u9053\uff1a\u201c\u8fd9\u5973\u5a03\u751f\u5f97\u6a21\u6837\u602a\u53ef\u601c\u7684\uff0c\u4f60\u8bf4\u8be5\u600e\u4e48\u5904\u6cbb\u5979\uff1f\u4e0d \u8fc7\u4e0d\u80fd\u6253\u5c41\u80a1\uff0c\u53e6\u5916\u8fd8\u6709\u4ec0\u4e48\u529e\u6cd5\uff1f\u201d \u3000\u3000\u8eab\u6750\u9ad8\u4e9b\u8001\u5934\u4e5f\u53d1\u6101\u8bf4\uff1a\u201c\u6211\u4e5f\u60f3\u4e0d\u51fa\u597d\u529e\u6cd5\uff0c\u59d1\u4e14\u9976\u5979\u8fd9\u4e00\u6b21\uff0c\u5979\u662f\u65e0\u5fc3\u51b2\u72af\u4e86\u6211 \u4eec\uff1a\u5148\u95ee\u95ee\u8bdd\uff0c\u522b\u8ba9\u5979\u4e5f\u8dd1\u6389\u4e86\uff01\u201d \u3000\u3000\u590f\u82b8\u88ab\u4ed6\u4e24\u4e00\u95ee\u4e00\u7b54\uff0c\u5f04\u5f97\u557c\u7b11\u7686\u975e\uff0c\u5fc3\u8bf4\uff1a\u201c\u8c01\u51b2\u72af\u4e86\u4f60\uff1f\u518d\u65e0\u7406\u53d6\u95f9\uff0c\u62bd\u4f60\u8fd9\u4e24\u4e2a \u8001\u5bb6\u4f19\u4e00\u987f\u97ad\u5b50\uff01\u8c01\u8010\u70e6\u7406\u4f60\uff01\u201d \u3000\u3000\u77ee\u8001\u5934\u5b50\u53cc\u624b\u4e00\u6325\uff0c\u4ecd\u662f\u5750\u7740\u7684\u59ff\u52bf\uff0c\u5df2\u98d8\u82e5\u98de\u7d6e\uff0c\u62e6\u4f4f\u4e86\u5979\u3002\u590f\u82b8\u64ae\u53e3\u8f7b\u5618\uff0c\u628a\u5979\u8fd9 \u5339\u79f0\u5fc3\u7684\u9a6c\u62db\u6765\u8eab\u7554\uff0c\u590f\u82b8\u731b\u89c1\u77ee\u8001\u5934\u65bd\u5c55\u4e0a\u4e58\u201c\u6d41\u661f\u79fb\u4f4d\u201d\u8f7b\u529f\u98de\u6765\uff0c\u5fc3\u5934\u4e00\u9707\uff0c\u614c\u5fd9\u5411 \u9a6c\u80cc\u7eb5\u4e0a\uff0c\u51c6\u5907\u4e00\u6e9c\u4e86\u4e4b\u3002 \u3000\u3000\u77ee\u8001\u5934\u53c8\u968f\u624b\u4e00\u62c9\uff0c\u76f8\u9694\u4e03\u516b\u5c3a\u8fdc\uff0c\u4e00\u80a1\u65e0\u5f62\u6f5c\u529b\uff0c\u88f9\u5f80\u5979\u7684\u5a07\u8eaf\uff0c\u4e0d\u7531\u5f80\u4e0b\u4e00\u6c89\uff0c\u901a \u7684\u53c8\u8dcc\u843d\u5730\u4e0a\u3002 \u3000\u3000\u590f\u82b8\u53ef\u4e0d\u6562\u5341\u5206\u5014\u5f3a\uff0c\u773c\u91cc\u6cdb\u51fa\u6cea\u5149\uff0c\u6068\u6068\u8bf4\uff1a\u201c\u8001\u602a\u7269\uff01\u4f60\u4f7f\u4ec0\u4e48\u574f\uff01\u4e3a\u4ec0\u4e48\u4e0d\u8ba9\u6211 \u8d70\uff1f\u6211\u8981\u8d76\u5feb\u627e\u6211\u7684\u718a\u501c\u54e5\u54e5\u3002\u201d \u3000\u3000\u8001\u5934\u504f\u7740\u5934\u601d\u7d22\u4e00\u9635\uff0c\u7b11\u9053\uff1a\u201c\u718a\u501c\uff1f\u8fd9\u4eba\u8001\u5934\u5b50\u4f3c\u66fe\u76f8\u8bc6\uff0c\u6b63\u6709\u53e5\u8bdd\u8ba9\u4f60\u5e26\u4e2a\u53e3\u4fe1\u7ed9 \u4ed6\uff0c\u53ef\u662f\u5973\u5a03\u5a03\uff0c\u4f60\u8ba4\u8bc6\u7684\u5c0f\u4f19\u5b50\u5012\u4e0d\u5c11\u5462\uff01\u201d \u3000\u3000\u8fd9\u8bdd\u4e00\u8bf4\u51fa\uff0c\u590f\u82b8\u600e\u4e48\u53d7\u5f97\u4f4f\uff0c\u4e00\u76f4\u7ea2\u5230\u8033\u6839\uff0c\u5fc3\u91cc\u6697\u9a82\uff1a\u201c\u7f3a\u5fb7\u7684\u8001\u9b3c\uff01\u8d4f\u4f60\u51e0\u7c92\u94a2 \u4e38\uff0c\u8ba9\u4f60\u518d\u6562\u8d2a\u5634\u80e1\u56bc\uff01\u201d \u3000\u3000\u590f\u82b8\u4e00\u63d0\u8d77\u718a\u501c\uff0c\u90a3\u53ef\u7231\u7684\u4fca\u5f71\uff0c\u7acb\u65f6\u4f7f\u5979\u5fc3\u5934\u4e00\u751c\uff0c\u751c\u7f8e\u7684\u56de\u5fc6\uff0c\u7adf\u4f7f\u5979\u4e0d\u80dc\u6005\u60c6\uff0c \u5fd8\u8bb0\u4e86\u5bf9\u4ed8\u8fd9\u53ef\u538c\u7684\u8001\u5934\uff0c\u590f\u82b8\u53c8\u5982\u4f55\u80af\u865a\u5fc3\u4e0b\u6c14\u548c\u4ed6\u4eec\u7b54\u8bdd\u3002 \u3000\u3000\u9ad8\u4e9b\u7684\u8001\u5934\u76b1\u76b1\u7709\u7b11\u8bf4\uff1a\u201c\u8ba9\u5979\u8d70\u5427\uff01\u4e0a\u6b21\u5df2\u7ecf\u628a\u91cd\u8981\u8def\u7ebf\u56fe\u5f53\u9762\u4ea4\u7ed9\u718a\u501c\u90a3\u5a03\u5a03\uff0c\u4e0d \u8fc7\u8d2f\u65e5\u5251\u4e5f\u662f\u6606\u4ed1\u65e7\u7269\uff0c\u5e94\u8be5\u4e0e\u501a\u5929\u5251\u540c\u5f52\u7384\u6e05\u6d1e\u5e9c\uff0c\u59d1\u5ff5\u5929\u9634\u6559\u5927\u60a3\u672a\u9664\uff0c\u5e94\u8be5\u6682\u65f6\u4ea4\u4ed6 \u4fdd\u5b58\u4e00\u6bb5\u65f6\u95f4\uff0c\u8bdd\u5f97\u8bf4\u660e\u767d\uff0c\u6bd2\u5fc3\u795e\u9b54\u867d\u77e5\u9053\u501a\u5929\u5251\u5173\u7cfb\u7740\u6b66\u6797\u7684\u52ab\u8fd0\uff0c\u4ed6\u8fd8\u672a\u660e\u4e86\u53cc\u5251\u7684 \u6765\u5386\u5462\uff01\u201d \u3000\u3000\u77ee\u4e9b\u7684\u8001\u5934\u4e5f\u76b1\u7709\u53d1\u6101\u8bf4\uff1a\u201c\u90a3\u5a03\u5a03\u4eba\u6781\u806a\u660e\uff0c\u53ef\u662f\u6ca1\u6709\u9002\u5f53\u7684\u4f34\u4fa3\uff0c\u914d\u4e0a\u4ed6\u4e00\u5757\u513f\u7ec3 \u5251\uff0c\u7edd\u96be\u53d1\u6325\u8fd9\u4e24\u4eea\u548c\u5408\u7684\u5999\u7528\uff0c\u53c8\u600e\u80fd\u62c5\u5f53\u8fd9\u4e00\u4efd\u91cd\u4efb\uff0c\u8fd9\u4e8b\u8fd8\u5f97\u8d39\u6211\u4eec\u65e0\u9650\u5fc3\u673a\u3002\u201d \u3000\u3000\u9ad8\u8001\u5934\u5bf9\u590f\u82b8\u8bf4\uff1a\u201c\u5973\u5a03\u5a03\uff01\u8bb0\u4f4f\u89c1\u4e86\u718a\u501c\uff0c\u5c31\u8bf4\u6c5f\u5e72\u4e8c\u8001\u5429\u5490\uff0c\u8d76\u5feb\u53bb\u5ce8\u5d4b\u53d6\u56de\u501a\u5929 \u5251\u6765\uff0c\u7136\u540e\u643a\u5e26\u53cc\u5251\uff0c\u5230\u6606\u4ed1\u8bbf\u6664\u94f6\u6756\u5a46\u5a46\u5b66\u4e60\u5408\u5251\uff0c\u5973\u5a03\u513f\u4f60\u4e5f\u8ddf\u7740\u53bb\u4e00\u8d9f\uff0c\u770b\u770b\u4f60\u6709\u7f18 \u8fd8\u662f\u65e0\u7f18\u3002\u201d \u3000\u3000\u4e8c\u8001\u8bf4\u5b8c\uff0c\u626d\u5934\u5411\u81ea\u832b\u832b\u7684\u6e56\u4e2d\u8d70\u53bb\u3002 \u3000\u3000\u590f\u82b8\u5728\u659c\u9633\u53e4\u9053\u4e0a\uff0c\u62cd\u9a6c\u6765\u56de\u5954\u9a70\u5bfb\u627e\u7530\u654f\u654f\uff0c\u5374\u672a\u80fd\u9047\u4e0a\uff0c\u4e00\u8d4c\u6c14\uff0c\u653e\u9a6c\u4e00\u76f4\u6cbf\u5927\u9053 \u9a70\u53bb\u3002 \u3000\u3000\u5f53\u665a\u6295\u5bbf\u5c71\u9547\u4e0a\u4e00\u5bb6\u5c0f\u5ba2\u5e97\uff0c\u4f4e\u77ee\u7684\u74e6\u623f\uff0c\u80ae\u810f\u7684\u5e8a\u88ab\uff0c\u4f7f\u5979\u5fc3\u91cc\u66f4\u6dfb\u4e00\u5c42\u70e6\u607c\u3002 \u3000\u3000\u7a81\u7136\u5e97\u95e8\u5916\u9a6c\u8e44\u58f0\u5982\u6f6e\u6d8c\u81f3\uff0c\u5e97\u91cc\u4f19\u8ba1\u8fce\u8fdb\u6765\u4e09\u4f4d\u9ed1\u8272\u52b2\u88c5\u7684\u6c49\u5b50\uff0c\u7b11\u8bed\u55a7\u5929\uff0c\u65c1\u82e5\u65e0 \u4eba\uff0c\u4e00\u76f4\u8d70\u5165\u4e09\u5927\u95ee\u4e0a\u623f\u91cc\u3002 \u3000\u3000\u4f19\u8ba1\u5982\u540c\u63a5\u4e0b\u8d22\u795e\uff0c\u5fd9\u4e0d\u8fed\u7a7f\u68f1\u4e00\u822c\u4f3a\u5e94\u3002 \u3000\u3000\u8fd9\u4e09\u4f4d\u8c6a\u6c14\u4e8e\u4e91\uff0c\u8bf4\u8bdd\u58f0\u97f3\u5f88\u9ad8\uff0c\u590f\u82b8\u75b2\u5026\u5730\u8eba\u5728\u94fa\u4e0a\uff0c\u5374\u88ab\u4ed6\u4eec\u4e00\u756a\u8bdd\u60ca\u9192\u8d77\u6765\u3002 \u3000\u3000\u53ea\u542c\u5f97\u5176\u4e2d\u4e00\u4eba\u72c2\u7b11\u8bf4\uff0c\u201c\u5355\u5927\u54e5\uff0c\u4e09\u6e58\u8c6a\u6770\uff0c\u6211\u6d1e\u5ead\u56db\u86df\u53f7\u53ec\u4e00\u4e0b\uff0c\u54ea\u4e00\u4e2a\u6562\u4e0d\u6295\u8bda \u54cd\u5e94\uff1f\u4f55\u5fc5\u5355\u5355\u8981\u6536\u7f57\u62c9\u62e2\u8fd9\u4e2a\u59d3\u718a\u7684\u5c0f\u5b50\uff1f\u201d \u3000\u3000\u53e6\u4e00\u4eba\u6c89\u541f\u9053\uff1a\u201c\u6559\u4e3b\u8fd9\u4e48\u5206\u6d3e\u4e0b\u6765\uff0c\u5fc5\u6709\u4ed6\u7684\u7528\u610f\uff01\u5434\u5927\u54e5\u77e5\u4f1a\u672c\u6559\u5404\u5904\u7684\u4eba\uff0c\u6ce8\u610f \u4e00\u4e0b\u718a\u501c\u7684\u884c\u8e2a\u3002\u201d \u3000\u3000\u5148\u90a3\u4eba\u53c8\u54c8\u54c8\u5927\u7b11\u8bf4\uff1a\u201c\u5c0f\u5f1f\u82e5\u78b0\u4e0a\u4ed6\uff0c\u5012\u8981\u5148\u4f1a\u4f1a\u4ed6\u8fd9\u4f4d\u6b66\u6797\u4e09\u79c0\uff01\u201d\u53c8\u95ee\u8bf4\uff1a\u201c\u7384 \u9f99\u5802\u4e3b\u4ec7\u8001\u524d\u8f88\u73b0\u5728\u5750\u9547\u6d1e\u5ead\uff0c\u636e\u8bf4\u8fd8\u51c6\u5907\u4e00\u6b21\u5927\u89c4\u6a21\u4e3e\u52a8\uff0c\u5355\u5927\u54e5\u662f\u81ea\u603b\u5802\u6765\u7684\u5417\uff1f\u5176\u8be6 \u53ef\u5f97\u89c1\u793a\u4e00\u4e8c\u5417\uff1f\u201d \u3000\u3000\u7b54\u8bdd\u90a3\u4eba\u7b11\u9053\uff1a\u201c\u501a\u5929\u5251\u5f97\u800c\u590d\u5931\uff0c\u82e5\u4e0d\u628a\u8fd9\u53e3\u5251\u6211\u56de\u6765\uff0c\u672c\u6559\u7684\u58f0\u5a01\u4ece\u6b64\u626b\u5730\uff01\u8fd9\u6b21 \u591c\u88ad\u6b66\u5f53\uff0c\u53c8\u4e0d\u80fd\u5f97\u624b\uff0c\u6240\u4ee5\u9f99\u51e4\u5404\u5802\u5802\u4e3b\u575b\u4e3b\uff0c\u9f50\u96c6\u6b64\u95f4\uff0c\u91cd\u4f5c\u4e00\u756a\u90e8\u7f72\uff0c\u4e8b\u5173\u673a\u5bc6\uff0c\u5c1a \u672a\u4f5c\u6700\u540e\u51b3\u5b9a\u3002\u201d \u3000\u3000\u590d\u82b8\u4e00\u542c\u522b\u4eba\u63d0\u8d77\u718a\u501c\uff0c\u4e0d\u7531\u7ad6\u8d77\u53cc\u8033\uff0c\u7559\u5fc3\u8c1b\u542c\u5e95\u4e0b\u7684\u8bdd\uff0c\u5374\u4f7f\u5979\u9887\u4e3a\u5931\u671b\uff0c\u663e\u7136\u8fd9 \u4e9b\u4eba\u4e5f\u4e0d\u77e5\u9053\u718a\u501c\u7684\u884c\u8e2a\u3002\u590f\u82b8\u751f\u957f\u5173\u5916\uff0c\u5317\u65b9\u5929\u9634\u6559\u5d1b\u8d77\uff0c\u9887\u6709\u6240\u95fb\uff0c\u5979\u7236\u4eb2\u866c\u987b\u5ba2\u5374\u95ed \u95e8\u8c22\u5ba2\uff0c\u7edd\u4e0d\u4e0e\u6c5f\u6e56\u8c6a\u6770\u5f80\u6765\u3002 \u3000\u3000\u590f\u82b8\u65e2\u542c\u51fa\u8fd9\u4e09\u4f4d\u662f\u5929\u9634\u6559\u4e0b\u722a\u7259\uff0c\u5929\u9634\u6559\u52bf\u529b\u5f25\u6f2b\u5357\u5317\u5404\u5730\uff0c\u866c\u987b\u5ba2\u529b\u6212\u5979\u5165\u5173\u4ee5 \u540e\uff0c\u4e0d\u53ef\u548c\u4ed6\u4eec\u51b2\u7a81\u3002 \u3000\u3000\u590f\u82b8\u53c8\u6cdb\u8d77\u4e86\u4e00\u4e2a\u9519\u89c9\uff0c\u5979\u4ee5\u4e3a\u5929\u9634\u6559\u4e0b\u8fd9\u4e09\u4e2a\u6c49\u5b50\u65e2\u7136\u662f\u8bbf\u5bfb\u718a\u501c\uff0c\u4ed6\u4eec\u773c\u7ebf\u53c8\u591a\uff0c \u4e0d\u6bd4\u81ea\u5df1\u5b64\u96f6\u96f6\u4e00\u4e2a\u4eba\u8bef\u8d70\u8bef\u649e\uff0c\u6765\u5f97\u5bb9\u6613\u5417\uff1f\u8ddf\u7740\u4ed6\u4eec\u8d70\uff0c\u4e0d\u662f\u501c\u54e5\u54e5\u5f88\u5bb9\u6613\u7684\u53ef\u4ee5\u627e \u7740\uff1f \u3000\u3000\u6b21\u6668\uff0c\u68b3\u5986\u5c31\u9053\uff0c\u5979\u5c3e\u968f\u5728\u90a3\u4e09\u4e2a\u9ed1\u8863\u4eba\u9a6c\u540e\u3002\u800c\u8fd9\u4e09\u4f4d\u53c8\u662f\u5411\u5317\u5954\u9a70\uff0c\u4f9d\u7136\u53c8\u628a\u5979\u5f15 \u5411\u6628\u5929\u90a3\u6761\u8def\u4e0a\u6765\uff0c\u9ed1\u8863\u4eba\u4e2d\u4e00\u4f4d\u5e74\u7eaa\u7565\u5927\u4e9b\u7684\uff0c\u866c\u7b4b\u6817\u8089\u7684\u6c49\u5b50\uff0c\u6709\u610f\u65e0\u610f\u5730\u4e0d\u65f6\u56de\u5934\u671b \u5979\u4e00\u773c\u3002 \u3000\u3000\u6881\u5b50\u6e56\u767d\u832b\u832b\u7684\u6c34\u8272\uff0c\u53c8\u5728\u8fdc\u5904\u6d6e\u73b0\uff0c\u800c\u90a3\u7247\u6811\u6797\uff0c\u4e5f\u5728\u67d4\u98ce\u62ab\u62c2\u4e2d\u3002 \u3000\u3000\u590f\u82b8\u968f\u7740\u4e09\u4eba\uff0c\u884c\u884c\u590d\u884c\u884c\uff0c\u79cb\u9633\u7693\u7693\uff0c\u7167\u5c04\u7740\u5b98\u9053\u4e0a\u98ce\u5c18\u6251\u9762\u7684\u884c\u65c5\u3002 \u3000\u3000\u8fd9\u79cd\u65e0\u610f\u4e49\u7684\u8ffd\u9010\uff0c\u4e5f\u53ef\u8bf4\u662f\u76f2\u65e0\u76ee\u7684\u7684\u5954\u6ce2\uff0c\u7a81\u7136\u88ab\u540e\u9762\u9a70\u6765\u7684\u4e00\u7247\u94c1\u9a91\u58f0\uff0c\u9707\u98a4\u4e86 \u5979\u7684\u5fc3\u5f26\u3002 \u3000\u3000\u590f\u82b8\u65e0\u610f\u4e2d\u626d\u5934\u671b\u53bb\uff0c\u4e00\u8fde\u4e32\u5339\u5339\u9a8f\u9a6c\u626c\u5c18\u800c\u6765\uff0c\u7acb\u65f6\u4f7f\u5979\u5927\u4e3a\u9707\u60ca\u3002\u6765\u7684\u7adf\u662f\u98de\u7075\u5821 \u51fa\u5c18\u5251\u4e1c\u65b9\u7075\u548c\u4ed6\u7684\u59b9\u59b9\u4e1c\u65b9\u745b\uff0c\u53e6\u5916\u4e24\u4f4d\u7384\u51a0\u7fbd\u8863\uff0c\u9ec4\u7a57\u5b50\u5b9d\u5251\u5728\u8eab\u7684\u9053\u58eb\uff0c\u5c24\u5176\u4f7f\u5979\u9b42 \u4e0d\u9644\u4f53\uff0c\u6b63\u662f\u56db\u4eea\u5251\u5ba2\u51cc\u4e91\u5b50\u548c\u4e39\u9633\u5b50\u3002 \u3000\u3000\u590f\u82b8\u5982\u60ca\u5f13\u4e4b\u9e1f\uff0c\u6025\u5fd9\u65bd\u5c55\u5979\u7cbe\u6e5b\u7684\u9a91\u672f\uff0c\u77ed\u97ad\u4e00\u626c\uff0c\u7ea4\u8db3\u4e00\u5939\u9a6c\u8179\uff0c\u5979\u6df1\u6089\u9a6c\u6027\uff0c\u7eb5 \u8f94\u98de\u9a70\uff0c\u8131\u79bb\u540e\u9762\u8fd9\u56db\u4f4d\u624e\u624b\u654c\u4eba\u7684\u8ffd\u88ad\u3002 \u3000\u3000\u800c\u8fd9\u51fa\u5c18\u5251\u5ba2\u5144\u59b9\u5374\u5e76\u4e0d\u662f\u4e13\u95e8\u6765\u627e\u5979\u4e3a\u96be\u7684\uff0c\u51cc\u4e91\u5b50\u548c\u4e39\u9633\u5b50\u4e8c\u9a6c\u5728\u524d\uff0c\u8fdc\u8fdc\u65e9\u770b\u6e05 \u4e86\u662f\u4ed6\u4eec\u4e8c\u6b21\u4e0b\u5c71\u6e38\u5927\u7684\u730e\u7269\u3002 \u3000\u3000\u53ef\u6076\u7684\u524d\u9762\u4e09\u4f4d\u9ed1\u8863\u4eba\uff0c\u5374\u628a\u5750\u9a91\u4e00\u6392\u513f\u6a2a\u5217\uff0c\u5e76\u8f94\u800c\u9a70\uff0c\u51e0\u4e4e\u5360\u5b8c\u4e86\u5168\u90e8\u9053\u8def\uff0c\u4f7f\u540e \u6765\u7684\u5979\u65e0\u6cd5\u98de\u8d8a\u800c\u524d\u3002\u590f\u82b8\u628a\u9a6c\u5934\u4e00\u5e26\u3002 \u3000\u3000\u5979\u82e5\u4e0d\u662f\u7cbe\u4e8e\u9a6d\u9a6c\uff0c\u65e9\u548c\u4e09\u4e2a\u9ed1\u8863\u4eba\u649e\u5728\u4e00\u8d77\u4e86\u3002 \u3000\u3000\u540e\u9762\u7684\u9a91\u58f0\u8d8a\u6765\u8d8a\u8fd1\uff0c\u4e39\u9633\u5b50\u5df2\u8fdc\u8fdc\u559d\u9053\uff1a\u201c\u590f\u59d1\u5a18\u6162\u8d70\uff0c\u8d2b\u9053\u8fd8\u8981\u5c48\u5c0a\u82b3\u9a7e\u56de\u5c71\u4e00\u8d9f \u5462\uff01\u4f60\u4e0d\u60f3\u89c1\u89c1\u718a\u501c\u4e48\uff1f\u4ed6\u6b63\u5728\u6b66\u5f53\u606d\u5019\u4f60\u5462\uff01\u201d \u3000\u3000\u590f\u82b8\u6c14\u5f97\u82b1\u5bb9\u60e8\u53d8\uff0c\u773c\u524d\u53c8\u88ab\u5929\u9634\u6559\u4e09\u4f4d\u62e6\u4f4f\u53bb\u8def\uff0c\u5403\u8fc7\u4e00\u4e00\u6b21\u4e8f\uff0c\u81ea\u7136\u5b66\u4e00\u6b21\u4e56\uff0c\u4ee5 \u9003\u8d70\u4e3a\u6700\u4e0a\u7684\u5999\u7b56\u3002 \u3000\u3000\u5979\u5bf9\u4e8e\u51cc\u4e91\u5b50\u7684\u5251\u6cd5\uff0c\u4ecd\u7136\u5fc3\u4e2d\u4e0d\u670d\uff0c\u53ea\u662f\u81ea\u5df1\u5355\u8eab\u4e00\u4eba\uff0c\u8fde\u4e2a\u8d81\u624b\u7684\u5175\u5668\u90fd\u6ca1\u6709\uff0c\u600e \u4e48\u8fce\u654c\u8fd9\u56db\u4eea\u5251\u5ba2\u4e2d\u4e24\u4f4d\u624e\u624b\u654c\u4eba\uff1f \u3000\u3000\u5979\u6478\u6478\u888b\u4e2d\u7530\u59d0\u59d0\u7684\u94a2\u4e38\u5947\u5999\u6697\u5668\uff0c\u5979\u4e0d\u76f8\u4fe1\u5367\u5df1\u51ed\u8fd9\u5c0f\u5c0f\u73e0\u4e38\uff0c\u53ef\u4ee5\u5236\u654c\u3002 \u3000\u3000\u6025\u5f97\u5979\u5411\u524d\u4e09\u4eba\u56b7\u9053\uff1a\u201c\u8bf7\u4f60\u4eec\u8ba9\u5f00\u70b9\uff0c\u540e\u9762\u6709\u4ec7\u4eba\u8ffd\u62ff\u6211\uff01\u201d \u3000\u3000\u4e39\u9633\u5b50\u4e00\u9a6c\u5f53\u5148\u51b2\u6765\uff0c\u524d\u9762\u4e09\u4f4d\u5929\u9634\u6559\u4e0b\u9f99\u987b\u575b\u4e3b\u5355\u638c\u65ad\u9b42\u5355\u98de\uff0c\u6d1e\u5ead\u56db\u86df\u795e\u773c\u868a\u8881 \u5b99\uff0c\u94c1\u7fc5\u86df\u9f99\u5316\u5b87\uff0c\u4e00\u9f50\u6cfc\u523a\u523a\u62e8\u8f6c\u4e86\u9a6c\u5934\uff0c\u4ed6\u4eec\u542c\u89c1\u8eab\u540e\u5a07\u6ef4\u6ef4\u5973\u5b69\u5b50\u7684\u53eb\u5524\uff0c\u90fd\u6389\u8f6c\u5934 \u6765\u770b\u770b\u662f\u4ec0\u4e48\u56de\u4e8b\u3002 \u3000\u3000\u9f99\u5316\u5b87\u548c\u8881\u5b99\u88ab\u5979\u8fd9\u79c0\u7f8e\u65e0\u4f26\u7684\u4e30\u59ff\u7167\u773c\u751f\u82b1\uff0c\u6115\u7136\u4e00\u6014\uff0c\u5355\u638c\u65ad\u9b42\u5355\u98de\u4e5f\u9aa4\u7136\u8c05\u8273\uff0c \u8273\u7edd\u5c18\u5bf0\u590f\u82b8\uff0c\u4f7f\u4ed6\u4e5f\u611f\u5230\u610f\u5916\u3002 \u3000\u3000\u4e39\u9633\u5b50\u50ac\u9a6c\u6025\u9a76\uff0c\u8f6c\u773c\u5c31\u5feb\u5230\u773c\u524d\uff0c\u590f\u82b8\u5598\u5401\u4e0d\u6b62\uff0c\u6025\u5f97\u4e00\u626c\u624b\uff0c\u5148\u98de\u51fa\u56db\u7c92\u5de7\u5999\u7684\u94a2 \u4e38\uff0c\u7cbe\u5149\u5c04\u76ee\uff0c\u55e1\u55e1\u55e1\u5411\u4e39\u9633\u5b50\u98de\u53bb\u3002 \u3000\u3000\u4e39\u9633\u5b50\u6ca1\u9632\u8fd9\u59d1\u5a18\u7a81\u4e0b\u8fa3\u624b\uff0c\u56db\u9897\u6676\u5149\u5c04\u773c\u7684\u94a2\u4e38\uff0c\u5206\u4e0a\u4e0b\u4e24\u8def\uff0c\u5415\u5b57\u5f62\u98de\u88ad\u8fc7\u6765\uff0c\u5fd9 \u5728\u9a6c\u978d\u9f99\u5f62\u4e00\u5f0f\uff0c\u4fef\u8eab\u8eb2\u907f\uff0c\u4e0a\u9762\u4e24\u4e38\u64e6\u80cc\u800c\u8fc7\uff0c\u5176\u95f4\u4e0d\u5bb9\u4e00\u53d1\u3002 \u3000\u3000\u4e0b\u9762\u5c04\u6765\u4e24\u9897\u94a2\u4e38\uff0c\u5374\u7a81\u7136\u4e92\u76f8\u4e00\u649e\uff0c\u5999\u5728\u4e00\u649e\u4e4b\u540e\uff0c\u5404\u5212\u4e2a\u534a\u5706\u5f27\u5f62\uff0c\u5206\u81ea\u5de6\u53f3\u4e24\u65b9 \u6298\u5c04\u800c\u4e0b\u3002 \u3000\u3000\u4e39\u9633\u5b50\u6ca1\u6599\u5230\u590f\u82b8\u7ade\u6709\u8fd9\u4e00\u624b\u7edd\u6280\uff0c\u4ed6\u9661\u7136\u5730\u52d2\u7f30\u4f4f\u9a6c\uff0c\u4e24\u679a\u94a2\u4e38\u5411\u4ed6\u659c\u63a3\u800c\u4e0b\uff0c\u547c\u547c \u5e26\u8d77\u4e24\u7f15\u5bd2\u98ce\uff0c\u8981\u7ffb\u8eab\u600e\u80fd\u6765\u5f97\u53ca\u5462\uff1f \u3000\u3000\u6240\u5e78\u7b2c\u4e8c\u5339\u9a6c\u4e0a\u7684\u51cc\u4e91\u5b50\uff0c\u4e5f\u5df2\u51b2\u5230\u9644\u8fd1\uff0c\u4ed6\u5c31\u9a6c\u4e0a\u4e00\u4e2a\u7a7f\u4e91\u7eb5\u8eab\u5f62\u79bb\u978d\uff0c\u659c\u659c\u8dc3\u8d77\uff0c \u624b\u4e2d\u9a6c\u97ad\u4e00\u6325\uff0c\u6323\u6323\u4e24\u58f0\u54cd\uff0c\u628a\u4e24\u9897\u94a2\u4e5d\u4e00\u9f50\u78d5\u98de\uff0c\u53ef\u662f\u4e39\u9633\u4e8e\u5df2\u5413\u5f97\u5192\u51fa\u4e00\u8eab\u51b7\u6c57\uff0c\u53cd\u624b \u62d4\u5251\u5df2\u9632\u5979\u518d\u6b21\u98de\u4e38\u88ad\u51fb\u3002 \u3000\u3000\u51cc\u4e91\u5b50\u8df3\u843d\u9a6c\u524d\uff0c\u5389\u58f0\u559d\u9053\uff1a\u201c\u59d1\u5a18\u4f11\u4f7f\u6697\u5668\u4f24\u4eba\uff0c\u8d2b\u9053\u4eca\u5929\u8981\u8ba9\u4f60\u9886\u6559\u51e0\u624b\u672c\u6d3e\u9547\u5c71 \u5251\u6cd5\uff0c\u5feb\u4eae\u4f60\u7684\u5175\u5203\u5427\uff01\u201d \u3000\u3000\u51fa\u5c18\u5251\u5ba2\u5144\u59b9\u4e5f\u50ac\u9a6c\u6765\u524d\uff0c\u4e1c\u65b9\u745b\u770b\u51fa\u6b63\u662f\u5979\u5fc3\u76ee\u4e2d\u7684\u4e00\u4e2a\u8ba8\u538c\u7684\u60c5\u654c\uff0c\u5979\u61ca\u6068\u6b66\u5f53\u56db \u5b50\u8fc7\u4e8e\u758f\u5ffd\uff0c\u8ba9\u5979\u81ea\u6b66\u5f53\u9003\u8d70\u4e0b\u5c71\uff0c\u6ca1\u7ed9\u5979\u4e00\u70b9\u82e6\u5934\u5403\u3002 \u3000\u3000\u4f46\u773c\u524d\u53c8\u6709\u4e09\u4f4d\u9ed1\u8863\u7537\u5b50\uff0c\u5e76\u6392\u513f\u5217\u9a6c\u5728\u590f\u82b8\u8eab\u524d\uff0c\u5176\u4e2d\u5355\u638c\u65ad\u9b42\u5355\u98de\uff0c\u53c8\u662f\u5728\u98de\u7075\u5821 \u5927\u663e\u8fc7\u4e00\u756a\u8eab\u624b\u7684\u5929\u9634\u6559\u9ad8\u624b\uff0c\u96be\u9053\u590f\u82b8\u5df2\u6295\u8eab\u4e8e\u5929\u9634\u6559\u4e0b\u4e86\u5417\uff1f \u3000\u3000\u51fa\u5c18\u5251\u5ba2\u4e1c\u65b9\u7075\u9a6c\u4e0a\u4e00\u62b1\u62f3\u8bf4\uff1a\u201c\u5355\u5f53\u5bb6\u7684\uff0c\u4e0a\u6b21\u8fb1\u4e34\u98de\u7075\u5821\uff0c\u5728\u4e0b\u5c1a\u4e0d\u77e5\u5d06\u5cd2\u540d\u624b\uff0c \u7adf\u5217\u8eab\u5929\u9634\u6559\u4e0b\uff0c\u8fd9\u4f4d\u96ea\u5730\u98d8\u98ce\u590f\u59d1\u5a18\uff0c\u662f\u655d\u53cb\u718a\u501c\u4e4b\u53cb\uff0c\u7f18\u4f55\u4e0e\u5f53\u5bb6\u7684\u8d70\u5728\u4e00\u8d77\uff1f\u590f\u59d1\u5a18 \u548c\u56db\u4eea\u5251\u5ba2\u53e6\u6709\u6881\u5b50\uff0c\u5728\u4e0b\u7279\u5148\u8868\u660e\uff01\u201d \u3000\u3000\u4ed6\u53c8\u5411\u590f\u82b8\u65bd\u793c\u8bf4\uff1a\u201c\u542c\u8bf4\u718a\u501c\u8001\u5f1f\u4e3a\u4f60\u5927\u95f9\u6b66\u5f53\u6d3e\u6cd5\u5730\uff0c\u59d1\u5a18\u4f55\u6545\u53cd\u4e0e\u5929\u9634\u6559\u4eba\u4e3a \u4f0d\uff1f\u51cc\u4e91\u9053\u957f\u8bf7\u4f60\u518d\u53bb\u4e00\u8d9f\u6b66\u5f53\uff0c\u4e0d\u8fc7\u628a\u4e0a\u6b21\u7684\u4e8b\u5927\u5bb6\u5f00\u8bda\u4e00\u8c08\uff0c\u8bf7\u52ff\u8bef\u4f1a\uff01\u201d \u3000\u3000\u4e1c\u65b9\u7075\u5e76\u6ca1\u6709\u4ee3\u59b9\u59b9\u6d88\u9664\u60c5\u654c\u4e4b\u610f\uff0c\u4ed6\u5185\u5fc3\u771f\u662f\u7231\u601c\u8fd9\u5c0f\u59d1\u5a18\uff0c\u6015\u5979\u8bef\u5165\u6b67\u9014\uff0c\u51fa\u5c18\u5251 \u5ba2\u7528\u60c5\u4e4b\u4e13\uff0c\u8fd9\u4e9b\u65e5\u5b50\u4e2d\uff0c\u5bf9\u6731\u82e5\u5170\u5df2\u60c5\u4e1d\u81ea\u7f1a\uff0c\u66f4\u613f\u5929\u4e0b\u6709\u60c5\u4eba\u90fd\u6210\u7737\u5c5e\uff0c\u65e2\u548c\u718a\u501c\u7ed3\u4e3a \u83ab\u9006\uff0c\u5c31\u63a8\u7231\u5230\u590f\u82b8\u8eab\u4e0a\u3002 \u3000\u3000\u4e1c\u65b9\u745b\u5fc3\u91cc\u5374\u6b63\u5e78\u707e\u4e50\u7978\uff0c\u82e5\u590f\u82b8\u548c\u5929\u9634\u6559\u4eba\u7ed3\u4e3a\u4e00\u515a\uff0c\u65e0\u7591\u5c06\u4f7f\u718a\u501c\u5fc3\u60c5\u6fc0\u53d8\uff0c\u628a\u7231 \u6155\u590f\u82b8\u4e4b\u5fc3\u53d8\u6210\u538c\u618e\uff0c\u800c\u5979\u81ea\u5df1\u5c31\u5c45\u4e8e\u7edd\u5bf9\u6709\u5229\u5730\u4f4d\u4e86\u3002 \u3000\u3000\u4e1c\u65b9\u745b\u5e74\u4e8b\u7a0d\u957f\uff0c\u4f46\u4e00\u60f3\u5230\u718a\u501c\uff0c\u4e5f\u662f\u82b3\u5fc3\u5bf8\u7ed5\uff0c\u60df\u6050\u8fd9\u79c0\u7f8e\u65e0\u4ed6\u7684\u590f\u82b8\uff0c\u6c38\u4e45\u5360\u636e\u4e86 \u718a\u9614\u4e3a\u4e00\u9897\u5fc3\uff0c\u718a\u501c\u53c2\u52a0\u98de\u7075\u5821\u82f1\u5806\u4f1a\uff0c\u5e2d\u4e0a\u9732\u51fa\u90a3\u4e00\u624b\u8f7b\u529f\u201c\u6f5c\u5f62\u9041\u5f71\u201d\uff0c\u9707\u60ca\u4e86\u5728\u5ea7\u7684 \u540d\u5bb6\u80fd\u624b\uff0c\u53ea\u6068\u54e5\u54e5\u4e0d\u4e86\u89e3\u5979\u7684\u5fc3\u4e8b\uff0c\u8f7b\u6613\u5730\u628a\u718a\u501c\u653e\u8d70\uff0c\u800c\u53c8\u65e0\u7f18\u65e0\u6545\u534a\u8def\u6740\u51fa\u4e2a\u7a0b\u54ac \u91d1\uff0c\u8ba9\u96ea\u5730\u98d8\u98ce\u62d4\u4e86\u5934\u7b49\uff0c\u5148\u5979\u800c\u53d6\u5f97\u4e86\u718a\u501c\u7684\u6b22\u5fc3\uff01 \u3000\u3000\u4e1c\u65b9\u745b\u53c8\u600e\u4e0d\u8be5\u61ca\u6094\u81ea\u5df1\uff0c\u4e0d\u5584\u4e8e\u730e\u53d6\u7537\u5b50\u5462\uff1f\u8fd9\u662f\u4e1c\u65b9\u82f1\u6bd4\u8f83\u6e29\u67d4\u5e84\u91cd\u4e0d\u82df\u8a00\u7b11\u7684\u7f8e \u5fb7\u98ce\u8303\uff0c\u4f46\u4e5f\u79cd\u4e0b\u4e86\u5979\u5931\u8d25\u60c5\u573a\u7684\u56e0\u5b50\u3002 \u3000\u3000\u7537\u5973\u95f4\u7684\u5173\u7cfb\uff0c\u7075\u7280\u4e00\u70b9\u65e0\u7531\u76f8\u901a\uff0c\u5f80\u5f80\u4f1a\u57cb\u6068\u7ec8\u8eab\uff0c\u800c\u5bf9\u65b9\u53c8\u4f55\u5c1d\u660e\u4e86\u4f60\u90a3\u4e00\u4efd\u513f\u60c5 \u610f\uff1f\u81ea\u7136\u4eba\u4e0e\u4eba\u95f4\u603b\u8fd8\u6709\u4e9b\u906d\u9645\u673a\u7f18\u7684\u51d1\u5408\uff0c\u90a3\u65f6\u7684\u718a\u501c\u6b63\u8fd8\u60bc\u4ea1\u4e3a\u4ed6\u6b89\u60c5\u7684\u82e5\u99a8\uff01\u7eb5\u6709\u7b2c \u4e09\u4eba\u5728\u4fa7\uff0c\u4e5f\u96be\u5b89\u6170\u4ed6\u7684\u5fc3\u7075\u7a7a\u865a\uff01 \u3000\u3000\u5355\u638c\u65ad\u9b42\u5355\u98de\u4e43\u5929\u9634\u6559\u7384\u9f99\u5802\u9f99\u987b\u575b\u8235\u4e3b\uff0c\u4e3a\u4eba\u673a\u667a\u591a\u8c0b\uff0c\u8d1f\u8d23\u7f51\u7f57\u5404\u65b9\u597d\u624b\uff0c\u542c\u51fa\u5c18 \u5251\u5ba2\u4e00\u8bf4\uff0c\u65b9\u77e5\u5728\u4ed6\u4eec\u8fd9\u8fb9\u7684\u79c0\u7f8e\u7684\u59d1\u5a18\uff0c\u7adf\u662f\u843d\u65e5\u9a6c\u573a\u540d\u6ee1\u4e1c\u5317\u7684\u5973\u4fa0\u96ea\u5730\u98d8\u51e4\uff0c\u5fc3\u91cc\u66f4 \u52a0\u5174\u594b\u4e86\u3002 \u3000\u3000\u5c24\u5176\u662f\u51fa\u5c18\u5251\u5ba2\u9053\u51fa\u590f\u82b8\u548c\u718a\u501c\u4e0d\u5e73\u51e1\u7684\u53cb\u8c0a\uff0c\u8fd9\u4f4d\u5d06\u5cd2\u540d\u624b\uff0c\u7acb\u65f6\u660e\u4e86\u4e86\u4ed6\u5e94\u8be5\u91c7\u53d6 \u7684\u6b65\u9aa4\u3002 \u3000\u3000\u82e5\u80fd\u628a\u96ea\u5730\u98d8\u98ce\u62c9\u5165\u5929\u9634\u6559\uff0c\u4e0d\u6015\u718a\u501c\u81ea\u5df1\u4e0d\u9001\u4e0a\u95e8\uff0c\u773c\u524d\u590f\u82b8\u53c8\u53d7\u56db\u4eea\u5251\u5ba2\u7684\u5a01\u903c\uff0c \u6b63\u597d\u4ee3\u5979\u63a5\u4e0b\u8fd9\u4e2a\u6881\u5b50\uff0c\u8fd8\u6015\u5979\u4e0d\u611f\u6069\u56fe\u62a5\uff0c\u4e56\u4e56\u5c31\u8303\uff1f \u3000\u3000\u5355\u98de\u8fd9\u4e2a\u5ff5\u5934\uff0c\u5982\u7535\u4e00\u95ea\uff0c\u4eba\u5df2\u50ac\u9a6c\u62a2\u7740\u62e6\u5728\u590f\u82b8\u524d\u9762\uff0c\u4e5f\u4e00\u62b1\u62f3\u5411\u51fa\u5c18\u5251\u5ba2\u4e3a\u793c\u8bf4\uff1a \u201c\u590f\u59d1\u5a18\u4eba\u54c1\u6b66\u529f\uff0c\u8a89\u6ee1\u4e00\u65b9\uff0c\u672c\u6559\u6b63\u5728\u6b22\u8fce\u5979\u5462\uff01\u98de\u7075\u5821\u5306\u5306\u4e00\u522b\uff0c\u672a\u53ca\u5411\u5821\u4e3b\u591a\u591a\u8ba8 \u6559\uff0c\u81f3\u4eca\u5185\u5fc3\u6b49\u759a\u3002\u201d \u3000\u3000\u4ed6\u53c8\u5411\u7c89\u8776\u4e1c\u65b9\u82f1\u65bd\u4e86\u4e0b\u793c\uff0c\u88c5\u51fa\u5f88\u8c26\u548c\u7684\u6001\u5ea6\uff0c\u800c\u4ed6\u8fd9\u79cd\u4e3e\u52a8\uff0c\u4e5f\u6b63\u662f\u60f3\u628a\u4e1c\u65b9\u7075\u5144 \u59b9\u4e00\u9f50\u62c9\u4eba\u6559\u4e0b\uff0c\u5018\u82e5\u80fd\u5f97\u8fd9\u4f4d\u5973\u5251\u5ba2\u5782\u9752\uff0c\u53c8\u662f\u4f55\u7b49\u7684\u5e78\u8fd0\u5462\u3002 \u3000\u3000\u5355\u98de\u906d\u53d7\u5230\u7684\u53ea\u662f\u7c89\u8776\u4e1c\u65b9\u745b\u51b7\u51b7\u7684\u4e00\u77a5\uff0c\u4e1c\u65b9\u745b\u4e0d\u5c51\u548c\u4ed6\u65bd\u793c\uff0c\u79c0\u76ee\u5fae\u8f6c\uff0c\u6b63\u5728\u601d\u5fd6 \u590f\u82b8\u548c\u5929\u9634\u6559\u6709\u4e9b\u4ec0\u4e48\u5173\u7cfb\uff1f \u3000\u3000\u5355\u98de\u600e\u4f1a\u4e3a\u5979\u633a\u8eab\u800c\u6765\u627f\u4f46\u4e00\u5207\uff1f \u3000\u3000\u90a3\u5355\u98de\u5411\u51cc\u4e91\u5b50\u62f1\u624b\u8bf4\uff1a\u201c\u6b66\u5f53\u56db\u4eea\u62a4\u6cd5\uff0c\u5728\u4e0b\u4e45\u4ef0\u76db\u540d\uff0c\u6606\u4ed1\u5d06\u5cd2\u6b66\u5f53\u6b66\u6797\u4e94\u5927\u6b63\u5b97 \u95e8\u6d3e\uff0c\u96ea\u5730\u98d8\u98ce\u590f\u82b8\u59d1\u5a18\uff0c\u7a76\u7adf\u4e0e\u8d35\u6d3e\u6709\u4f55\u8fc7\u8282\uff0c\u9053\u957f\u4e0d\u53ef\u6b3a\u5979\u4e00\u4e2a\u5f31\u5973\u5b50\uff0c\u6211\u5355\u98de\u613f\u66ff\u5979 \u5411\u9053\u957f\u9886\u60c5\uff01\u201d \u3000\u3000\u6d1e\u5ead\u53cc\u86df\u8881\u5b99\u9f99\u5316\u5b87\uff0c\u4e43\u662f\u4e24\u4e2a\u52c7\u592b\uff0c\u5947\u602a\u5355\u98de\u7adf\u4e3a\u4e2a\u7d20\u4e0d\u76f8\u8bc6\u7684\u5973\u5b50\uff0c\u51fa\u9762\u627f\u62c5\u4e00 \u5207\u3002\u5929\u9634\u6559\u548c\u6b66\u5f53\u8fd9\u4e00\u6b21\u51b3\u6597\uff0c\u5df2\u7ecf\u7ed3\u4e0b\u4e86\u6c38\u4e45\u4e0d\u53ef\u89e3\u7684\u6881\u5b50\uff0c\u5355\u98de\u65e2\u53ef\u62c9\u6270\u96ea\u5730\u98d8\u98ce\uff0c\u4e5f \u53ef\u6253\u51fb\u6b66\u5f53\u6d3e\u7684\u58f0\u671b\uff0c\u4f55\u4e50\u4e0d\u4e3a\u3002 \u3000\u3000\u6d1e\u5ead\u53cc\u86df\u6027\u70c8\u5982\u706b\uff0c\u65e9\u5c31\u5404\u62d4\u5175\u5203\uff0c\u864e\u89c6\u7708\u7708\uff0c\u51c6\u5907\u6740\u4e2a\u75db\u5feb\uff0c\u6c5f\u6e56\u4e0a\u8fd9\u79cd\u597d\u6c49\uff0c\u6210\u5e74 \u662f\u548c\u4eba\u51f6\u6740\u6076\u6597\uff0c\u53ea\u8981\u5355\u98de\u4f5c\u4e86\u4e3b\uff0c\u4ed6\u4eec\u662f\u52c7\u5f80\u76f4\u524d\u594b\u4e0d\u987e\u8eab\u7684\u3002 \u3000\u3000\u5c40\u52bf\u4e00\u53d8\uff0c\u53d8\u6210\u4e86\u5929\u9634\u6559\u548c\u6b66\u5f53\u6d3e\u7684\u6076\u6597\uff0c\u51fa\u5c18\u5251\u5ba2\u80fd\u5426\u7f6e\u8eab\u4e8b\u5916\uff1f\u800c\u8fd9\u4e8b\u6b63\u4e3a\u7740\u96ea\u5730 \u98d8\u98ce\u800c\u8d77\u3002 \u3000\u3000\u5929\u9634\u6559\u52bf\u529b\u8ffd\u5e03\u5927\u6c5f\u5357\u5317\uff0c\u6b66\u5f53\u6d3e\u4eba\u8fd8\u6ca1\u9080\u8bf7\u5230\u5404\u6d3e\u540d\u7f55\uff0c\u65b0\u5d1b\u8d77\u7684\u9ad8\u624b\uff0c\u4e0d\u80fd\u7acb\u5373\u53d1 \u96be\uff0c\u800c\u5929\u9634\u6559\u4eba\u515a\u7fbd\u6108\u96c6\u6108\u591a\uff0c\u51e0\u4e4e\u6784\u6210\u4e86\u5305\u56f4\u6b66\u5f53\u7684\u5f62\u52bf\u3002 \u3000\u3000\u51cc\u4e91\u5b50\u4e0d\u628a\u4ec0\u4e48\u6d1e\u5ead\u56db\u868a\u653e\u5728\u773c\u91cc\uff0c\u4f46\u662f\u5d06\u5cd2\u6d3e\u4e0b\u5355\u638c\u65ad\u9b42\uff0c\u80cc\u540e\u8fd8\u6709\u8bb8\u591a\u5d06\u5cd2\u80fd\u624b\u505a \u80cc\u666f\uff0c\u98de\u7075\u5821\u6218\u8d25\u4e86\u6b66\u80dc\u6587\uff0c\u9732\u51fa\u5d06\u5cd2\u9547\u5c71\u638c\u6cd5\u201c\u65ad\u9b42\u638c\u201d\u529f\u529b\u4e5f\u81ea\u4e0d\u5f31\uff0c\u6700\u5947\u602a\u7684\u662f\u590f\u82b8 \u53d1\u653e\u6697\u5668\u7684\u5947\u5999\u624b\u6cd5\uff0c\u5982\u679c\u51fa\u5c18\u5251\u5ba2\u4eca\u513f\u4e0d\u8d9f\u8fd9\u4e00\u8d9f\u6df7\u6c34\uff0c\u4ed6\u548c\u4e39\u9633\u5b50\u80fd\u5426\u7a33\u64cd\u80dc\u7b97\uff0c\u53ef\u4e5f \u5f88\u96be\u8bf4\u3002 \u3000\u3000\u4f46\u5929\u9634\u6559\u65e2\u516c\u7136\u4e0e\u6b66\u5f53\u6d3e\u4e3a\u654c\uff0c\u9047\u4e0a\u4e86\u8fd8\u6709\u4ec0\u4e48\u8bdd\u8bf4\uff0c\u51cc\u4e91\u5b50\u62ff\u8bdd\u6324\u5151\u4e1c\u65b9\u7075\u8bf4\uff1a\u201c\u4e1c \u65b9\u5821\u4e3b\uff0c\u4eca\u513f\u72ed\u8def\u76f8\u9022\uff0c\u5929\u9634\u6559\u8fd9\u4f4d\u5355\u5f53\u5bb6\u7684\u65e0\u7aef\u8892\u62a4\u96ea\u5730\u98d8\u98ce\uff0c\u8fd9\u5c40\u52bf\u663e\u7136\u8981\u7d2f\u53ca\u5821\u4e3b\u5144 \u59b9\u4e86\uff01\u6b8a\u4ee4\u8d2b\u9053\u4e8e\u5fc3\u4e0d\u5b89\u3002\u201d \u3000\u3000\u4ed6\u8fd9\u4e00\u756a\u8bdd\uff0c\u662f\u60f3\u628a\u4e1c\u65b9\u7075\u903c\u4f4f\uff0c\u4f7f\u4ed6\u5144\u59b9\u4e0d\u5f97\u4e0d\u51fa\u624b\u76f8\u52a9\uff0c\u4ed6\u53c8\u5411\u5355\u98de\u51b7\u7b11\u559d\u9053\uff1a \u201c\u96ea\u5730\u98d8\u98ce\u4fae\u8fb1\u672c\u6d3e\u4e5d\u5bab\u8fde\u73af\u65d7\uff0c\u4e0e\u4f60\u5929\u9634\u6559\u6709\u4f55\u76f8\u5e72\uff1f\u5979\u4e5f\u4e0d\u662f\u4f60\u4eec\u6559\u4e0b\u7684\u4eba\uff0c\u5982\u679c\u5355\u5144 \u627e\u56db\u4eea\u5251\u5ba2\uff0c\u8d2b\u9053\u53e6\u5b9a\u671f\u5728\u655d\u5c71\u5019\u6559\u5c31\u662f\uff01\u201d \u3000\u3000\u5355\u98de\u5374\u4e0d\u80af\u653e\u8fc7\u8fd9\u4e2a\u597d\u673a\u4f1a\uff0c\u53cd\u800c\u51b7\u7b11\u563f\u563f\u9053\uff1a\u201c\u590f\u59d1\u5a18\u548c\u718a\u501c\uff0c\u90fd\u662f\u672c\u6559\u6b22\u8fce\u643a\u624b\u7684 \u6b66\u6797\u82f1\u624d\uff0c\u4e3a\u4e86\u718a\u501c\uff0c\u6211\u4eec\u66f4\u4e0d\u80fd\u4f7f\u590f\u59d1\u5a18\u53d7\u7a98\uff01\u201d \u3000\u3000\u53c8\u5411\u590f\u82b8\u65bd\u793c\u9053\uff1a\u201c\u59d1\u5a18\u4e43\u5173\u5916\u6210\u540d\u5973\u4fa0\uff0c\u5728\u4e0b\u5d06\u5cd2\u5355\u638c\u65ad\u9b42\u5355\u98de\uff0c\u94a6\u4f69\u5df2\u4e45\uff0c\u59d1\u5a18\u548c \u6b66\u5f53\u8fd9\u4e2a\u6881\u5b50\uff0c\u5728\u4e0b\u613f\u62d4\u5200\u76f8\u52a9\uff0c\u4ee5\u5c3d\u6c5f\u6e56\u6b66\u6797\u9053\u4e49\uff01\u201d \u3000\u3000\u8f6c\u8fc7\u8eab\u53c8\u5411\u4e1c\u65b9\u7075\u9053\uff1a\u201c\u5821\u4e3b\u4e5f\u662f\u5728\u4e0b\u548c\u672c\u6559\u7d20\u65e5\u94a6\u4f69\u7684\u5927\u4fa0\uff0c\u7d20\u65e0\u6069\u6028\uff0c\u4eca\u65e5\u5e94\u4e3a\u96ea \u5730\u98d8\u98ce\uff0c\u4e00\u540c\u6276\u5f31\u6291\u5f3a\uff01\u201d \u3000\u3000\u5355\u98de\u4e0d\u6127\u4e3a\u9f99\u987b\u575b\u4e3b\uff0c\u8bf4\u7684\u9762\u9762\u5468\u5230\uff0c\u5360\u4f4f\u4e86\u7406\u3002 \u3000\u3000\u590f\u82b8\u4e0d\u660e\u4e86\u5929\u9634\u6559\u662f\u4ec0\u4e48\u5185\u5e55\uff0c\u773c\u524d\u603b\u4e0d\u80fd\u8c22\u7edd\u4eba\u5bb6\u5e2e\u52a9\u7684\u597d\u610f\uff0c\u4e0d\u8fc7\u5979\u8fd8\u662f\u5634\u786c\uff0c\u6bc5 \u7136\u62e8\u9a6c\u800c\u524d\u8bf4\uff1a\u201c\u6211\u81ea\u5df1\u7684\u4e8b\uff0c\u6211\u4e00\u4e2a\u4eba\u63a5\u7740\u4ed6\u4eec\u5c31\u662f\u4e86\u3002\u201d \u3000\u3000\u4e1c\u65b9\u7075\u8001\u4e8e\u4e16\u6545\uff0c\u65e2\u4e0d\u613f\u5f00\u7f6a\u718a\u501c\uff0c\u53c8\u4e0d\u613f\u4f7f\u6b66\u5f53\u56db\u4eea\u5931\u671b\uff0c\u800c\u4e14\u8fd9\u6b21\u4e5f\u5e94\u6b66\u5f53\u4e4b\u9080\uff0c \u524d\u5f80\u5171\u5546\u6f84\u6e05\u6b66\u6797\u5371\u673a\u7684\u5927\u8ba1\uff0c\u53c8\u600e\u80fd\u7f6e\u8eab\u4e8b\u5916\u3002 \u3000\u3000\u4e1c\u65b9\u82f1\u5219\u53e6\u662f\u4e00\u79cd\u60f3\u6cd5\uff0c\u590f\u82b8\u7684\u786e\u662f\u592a\u7f8e\u4e86\uff0c\u7f8e\u5230\u4f7f\u5979\u65e0\u6cd5\u4e0e\u590f\u82b8\u5728\u60c5\u573a\u4e0a\u4e00\u8f83\u8eab\u624b\uff0c \u53ea\u6709\u4fc3\u4f7f\u590f\u82b8\u53d7\u5929\u9634\u6559\u9a97\u8bf1\uff0c\u53ef\u4ee5\u6bc1\u4e86\u96ea\u5730\u98d8\u98ce\u7684\u4e00\u751f\u5e78\u798f\u3002 \u3000\u3000\u4e39\u9633\u5b50\u9996\u5148\u88ab\u5355\u98de\u8fd9\u51e0\u5957\u6311\u62e8\u79bb\u95f4\u7684\u8bdd\uff0c\u95f9\u5f97\u6c14\u6124\u586b\u81ba\uff0c\u4e00\u6309\u5251\u9798\uff0c\u545b\u561f\u62d4\u51fa\u957f\u5251\uff0c\u8dc3 \u4e0b\u9a6c\u6765\uff0c\u5251\u5c16\u4e00\u6307\u5355\u98de\u8bf4\uff1a\u201c\u5355\u5f53\u5bb6\u7684\uff0c\u4f60\u65e2\u51fa\u5934\u6405\u4e8b\uff0c\u5c11\u4e0d\u5f97\u5148\u6253\u53d1\u4e86\u4f60\uff01\u7528\u4e0d\u7740\u82b1\u8a00\u5de7 \u8bed\uff0c\u9a97\u8bf1\u96ea\u5730\u98d8\u98ce\uff01\u201d \u3000\u3000\u90a3\u8fb9\u53cc\u86df\u2014\u2014\u795e\u773c\u86df\u8881\u5b99\u4eae\u51fa\u4e00\u67c4\u94a9\u9570\u5200\uff0c\u94c1\u7fc5\u86df\u5c24\u5316\u5b87\u4e5f\u4ece\u8170\u95f4\u89e3\u4e0b\u94fe\u5b50\u53cc\u9524\uff0c\u4e24\u4eba \u8fd9\u79cd\u77ed\u8f6f\u5916\u95e8\u5175\u5203\uff0c\u4e43\u662f\u4e3a\u5728\u6c34\u4e2d\u4f7f\u7528\u65f6\u65b9\u4fbf\uff0c\u800c\u4e24\u4eba\u4e5f\u786e\u5404\u6709\u4e00\u5957\u5947\u7279\u62db\u6cd5\u3002\u5c24\u5316\u5b87\u7684\u94fe \u5b50\u9524\u4e0a\u4e0b\u7ffb\u98de\uff0c\u5148\u81ea\u5411\u4e39\u9633\u5b50\u731b\u653b\u3002 \u3000\u3000\u4e39\u9633\u5b50\u5fc3\u60f3\u6d1e\u5ead\u56db\u768e\uff0c\u6b66\u529f\u4f1a\u9ad8\u5230\u54ea\u91cc\u53bb\uff1f\u4fe1\u624b\u6325\u5251\u4e00\u6311\uff0c\u60f3\u515c\u4f4f\u94fe\u5b50\uff0c\u6311\u98de\u53cc\u9524\uff0c\u5c82 \u77e5\u5c24\u5316\u5b87\u91cd\u624b\u786c\u529f\u592b\u5206\u91cf\u4e0d\u8f7b\uff0c\u53cd\u51e0\u4e4e\u628a\u4ed6\u7684\u5b9d\u5251\u7ede\u4f4f\u3002 \u3000\u3000\u51fa\u5c18\u5251\u5ba2\u51b3\u5b9a\u4e86\u4e3b\u610f\uff0c\u5148\u6a2a\u5251\u800c\u524d\uff0c\u5411\u5355\u98de\u62db\u547c\u9053\uff1a\u201c\u4e45\u4ef0\u5d06\u5cd2\u9ad8\u6280\uff0c\u4e0a\u6b21\u8fb1\u4e34\u655d\u5821\uff0c \u672a\u80fd\u9886\u6559\uff01\u73b0\u5728\u53ef\u4e58\u673a\u5207\u78cb\u4e00\u4e0b\u6b66\u6280\uff01\u201d\u8bf4\u7740\uff0c\u957f\u5251\u4e00\u51fa\uff0c\u864e\u5578\u9f99\u541f\uff0c\u5bd2\u6c14\u68ee\u68ee\uff0c\u5411\u5355\u98de\u5f53 \u5934\u7f69\u4e0b\u3002 \u3000\u3000\u4e1c\u65b9\u7075\u7684\u5fc3\u7406\uff0c\u8ba9\u51cc\u4e91\u5b50\u7a7a\u95f2\u624b\uff0c\u53ef\u4ee5\u5355\u72ec\u5236\u670d\u590f\u82b8\u3001\u800c\u590f\u82b8\u90a3\u79cd\u9a84\u6a2a\u4e0d\u53ef\u4e00\u4e16\u7684\u6c14 \u7130\uff0c\u4e1c\u65b9\u7075\u6709\u4e9b\u770b\u4e0d\u987a\u773c\u3002 \u3000\u3000\u4e1c\u65b9\u7075\u65e2\u5df2\u51fa\u624b\uff0c\u5355\u638c\u65ad\u9b42\u81ea\u4e0d\u80fd\u793a\u602f\uff0c\u4ed6\u4ed7\u7740\u65ad\u9b42\u638c\u548c\u6df1\u539a\u7684\u5185\u529f\uff0c\u751f\u5e73\u53ea\u662f\u4ee5\u8089\u638c \u4e0e\u4eba\u76f8\u6597\uff0c\u51fa\u5c18\u5251\u5ba2\u5251\u6cd5\u4f55\u7b49\u51cc\u5389\uff0c\u800c\u529f\u529b\u4e5f\u975e\u5e38\u9187\u539a\uff0c\u4e00\u67c4\u5251\u821e\u8d77\u6765\uff0c\u51e4\u8d77\u4e91\u6d8c\uff0c\u5251\u8679\u95ea \u95ea\uff0c\u5982\u5f71\u968f\u5f62\u3002 \u3000\u3000\u4efb\u4f60\u5355\u638c\u65ad\u9b42\u6b65\u5730\u5982\u4f55\u7f8e\u6cd5\uff0c\u7ec8\u9003\u4e0d\u51fa\u5251\u5f71\u5708\u5185\u3002 \u3000\u3000\u4e1c\u65b9\u745b\u5219\u542b\u7b11\u76c8\u76c8\uff0c\u770b\u5979\u54e5\u54e5\u4f7f\u51fa\u5e73\u751f\u7edd\u6280\uff0c\u4e00\u9762\u66f4\u53ef\u4eb2\u773c\u518d\u770b\u770b\u590f\u82b8\u683d\u4e86\u4e0b\u53bb\uff0c\u8bf4\u4e0d \u5b9a\u6b66\u5f53\u56db\u4eea\u62a4\u6cd5\uff0c\u8fd9\u6b21\u66f4\u4f1a\u7ed9\u590f\u82b8\u4e00\u4e2a\u96be\u582a\u3002 \u3000\u3000\u4e1c\u65b9\u745b\u517b\u5c0a\u5904\u4f18\uff0c\u5979\u54e5\u9664\u975e\u4e0d\u5f97\u5df2\u662f\u4e0d\u80af\u8ba9\u5979\u51fa\u624b\u7684\u3002\u51cc\u4e91\u5b50\u5219\u62b1\u5251\u7f13\u6b65\u8d70\u5411\u590f\u82b8\uff0c\u6566 \u6307\u9053\uff1a\u201c\u590f\u59d1\u5a18\uff0c\u4e0a\u6b21\u4e8c\u5341\u62db\u5185\u5df1\u8f93\u4e0e\u8d2b\u9053\uff0c\u4f55\u987b\u518d\u8bd5\uff01\u8bf7\u968f\u8d2b\u9053\u524d\u5f80\u6b66\u5f53\u8d70\u4e00\u8d9f\u5427\uff01\u201d\u590f \u82b8\u88ab\u4ed6\u8bf4\u5f97\u5192\u706b\uff0c\u4e0a\u6b21\u53d7\u8fb1\u7684\u60c5\u5f62\uff0c\u76f4\u4f7f\u5979\u6124\u4e0b\u6b32\u751f\uff0c\u53ef\u662f\u786e\u6709\u4e9b\u5bd2\u5fc3\uff0c\u4f46\u662f\u53c8\u600e\u80fd\u5411\u8fd9\u9053 \u58eb\u4f4e\u5934\u53d7\u8fb1\u5462\u3002 \u3000\u3000\u5979\u8f7b\u8f7b\u5730\u6325\u52a8\u624b\u4e2d\u9a6c\u97ad\uff0c\u53ea\u89c9\u8fd9\u4ef6\u5bfb\u5e38\u9a6c\u97ad\u9887\u4e0d\u8d81\u624b\uff0c\u54ac\u4e00\u54ac\u94f6\u7259\uff0c\u4ecd\u7136\u60f3\u4fa5\u5e78\u53d6\u80dc\uff0c \u5979\u6b63\u8fce\u4e0a\u524d\u53bb\uff0c\u6070\u597d\u795e\u773c\u86df\u8881\u5b99\u540c\u65f6\u94a9\u9570\u5200\u9012\u4e86\u4e0a\u6765\uff0c\u4e00\u97ad\u4e00\u5200\uff0c\u53cc\u53cc\u540c\u65f6\u6251\u5411\u51cc\u4e91\u5b50\uff0c\u590f \u82b8\u77ed\u97ad\u4e00\u629b\u4e00\u70b9\uff0c\u6539\u6362\u4e86\u4e00\u5957\u6d41\u661f\u7b14\u6cd5\uff0c\u4e13\u627e\u51cc\u4e91\u5b50\u7684\u91cd\u8981\u7a74\u9053\uff0c\u8fd9\u662f\u5979\u80fd\u820d\u77ed\u53d6\u957f\u7684\u5730 \u65b9\u3002 \u3000\u3000\u77ed\u97ad\u5982\u4f55\u80fd\u53d1\u6325\u72c2\u98d3\u97ad\u6cd5\u7684\u5a01\u529b\u5462\uff1f \u3000\u3000\u51cc\u4e91\u5b50\u5251\u6cd5\u7cbe\u5999\uff0c\u5728\u4ed6\u624b\u4e2d\u7684\u9547\u5c71\u5251\u6cd5\u4e5d\u5bab\u8fde\u73af\u516b\u5341\u4e00\u5f0f\uff0c\u62db\u62db\u5982\u5929\u9a6c\u884c\u7a7a\uff0c\u53d8\u5316\u83ab \u6d4b\uff0c\u5bf9\u4ed8\u5979\u548c\u8881\u5b99\u4e24\u4eba\u7684\u77ed\u97ad\u94a9\u9570\u5200\uff0c\u786e\u662f\u5e94\u4ed8\u88d5\u5982\uff0c\u597d\u6574\u4ee5\u6687\u3002\u4f46\u51cc\u4e91\u5b50\u591a\u5c11\u53d7\u5230\u795e\u773c\u868a \u94a9\u9570\u5200\u7684\u7275\u5236\uff0c\u4e0d\u80fd\u77ed\u4fc3\u65f6\u95f4\u5236\u670d\u4e86\u5979\u3002 \u3000\u3000\u590f\u82b8\u4e5f\u662f\u7ecf\u8fc7\u4e43\u7236\u866c\u987b\u5ba2\u591a\u5e74\u8c03\u6559\uff0c\u8f7b\u8e6c\u5de7\u7eb5\uff0c\u98d8\u5ffd\u5982\u98ce\uff0c\u624b\u4e0a\u52b2\u529b\u4e5f\u81ea\u4e0d\u5f31\uff0c\u8fd9\u7b2c\u4e8c \u6b21\u4ea4\u624b\uff0c\u53c8\u52a0\u500d\u5c0f\u5fc3\uff0c\u6050\u9632\u7740\u4e86\u4eba\u5bb6\u9053\u513f\uff0c\u5979\u6ed1\u6e9c\u5f97\u50cf\u4e00\u6761\u7f8e\u4eba\u9c7c\uff0c\u6b65\u6cd5\u7f8e\u5999\u5df2\u6781\uff0c\u771f\u4e0d\u6127 \u4e3a\u96ea\u5730\u98d8\u98ce\u3002 \u3000\u3000\u51cc\u4e91\u5b50\u867d\u7136\u6068\u8fd9\u5973\u5b69\u987d\u5f3a\uff0c\u5374\u53ea\u5b58\u7a98\u8fb1\u5979\u7684\u5fc3\uff0c\u4e0d\u613f\u7740\u5b9e\u4f24\u5979\u592a\u91cd\uff0c\u8fd9\u662f\u770b\u5728\u718a\u501c\u7684\u9762 \u4e0a\uff0c\u5bf9\u4e8e\u795e\u773c\u86df\u8881\u5b99\uff0c\u53ef\u5c31\u624b\u4e0a\u4e0d\u7559\u4f59\u5730\uff0c\u7740\u7740\u72e0\u8fa3\uff0c\u903c\u5f97\u8881\u5b99\u9669\u8c61\u73af\u751f\uff0c\u51e0\u6b21\u90e8\u9669\u906d\u6bd2 \u624b\u3002 \u3000\u3000\u82e5\u6ca1\u6709\u590f\u82b8\u4ece\u65c1\u9012\u62db\uff0c\u8e48\u6687\u62b5\u9699\uff0c\u4e58\u865a\u800c\u653b\uff0c\u795e\u773c\u868a\u53c8\u600e\u80fd\u652f\u6301\u5f97\u4e86\u4e09\u5341\u4f59\u62db\uff0c\u590f\u82b8\u82e5 \u662f\u5979\u94f6\u97ad\u5728\u4e8e\uff0c\u90a3\u53ef\u6bd4\u8881\u5b99\u8981\u9ad8\u660e\u5f97\u591a\u3002 \u3000\u3000\u5355\u638c\u65ad\u9b42\u5355\u98de\uff0c\u4e00\u8def\u9634\u68ee\u68ee\u53ef\u6016\u7684\u5d06\u5cd2\u9547\u5c71\u65ad\u9b42\u638c\u6cd5\uff0c\u624b\u638c\u8fc7\u5904\uff0c\u5bd2\u98ce\u523a\u9aa8\uff0c\u5403\u4e8f\u662f\u8089 \u638c\u603b\u4e0d\u80fd\u548c\u5b9d\u5251\u786c\u78b0\uff0c\u800c\u51fa\u5c18\u5251\u5ba2\u8fd9\u4e00\u5957\u79cb\u6c34\u51fa\u5c18\u5251\u6cd5\uff0c\u505a\u89c6\u6c5f\u6e56\uff0c\u6df1\u5965\u83ab\u6d4b\uff0c\u5904\u5904\u5360\u7740\u4e0a \u98ce\uff0c\u65ad\u9b42\u638c\u98ce\u6240\u8fc7\uff0c\u4ed6\u4e0d\u6d4b\u80fd\u5426\u4f24\u53ca\u8eab\u4f53\uff0c\u7565\u6709\u4e9b\u987e\u8651\uff0c\u5426\u5219\u5355\u98de\u662f\u4e0d\u80fd\u652f\u6301\u4e0b\u53bb\u3002 \u3000\u3000\u5c24\u5316\u5b87\u94fe\u5b50\u9524\uff0c\u62ff\u6765\u548c\u5251\u6cd5\u7cbe\u5965\u7684\u56db\u5b50\u4e39\u9633\u5b50\u5bf9\u654c\uff0c\u65e0\u5f02\u4ee5\u536f\u51fb\u77f3\uff0c\u5fc3\u91cc\u4e00\u53d1\u614c\uff0c\u51b7\u6c57 \u6d94\u6d94\u5730\u8eab\u4e0a\u76f4\u5192\uff0c\u800c\u8eab\u6bb5\u6b65\u6cd5\u8d8a\u6765\u8d8a\u6c89\u91cd\uff0c\u6bcf\u8eb2\u907f\u4e39\u9633\u5b50\u4e00\u62db\uff0c\u6270\u5f97\u4ed8\u51fa\u5f88\u5927\u7684\u529b\u91cf\uff0c\u5df2\u5230 \u4e86\u5c71\u7a77\u6c34\u5c3d\u7684\u5730\u6b65\u3002 \u3000\u3000\u590f\u82b8\u4e0d\u613f\u81ea\u5df1\u8d25\uff0c\u4e5f\u5c31\u4e0d\u613f\u5929\u9634\u6559\u7684\u4eba\u8d25\u4e0b\u53bb\uff0c\u4e09\u4eba\u90fd\u662f\u81ea\u544a\u594b\u52c7\uff0c\u633a\u8eab\u5e2e\u52a9\u5979\u7684\uff0c\u5979 \u5df2\u770b\u51fa\u5c24\u5316\u5b57\u5904\u5883\u6700\u52a3\uff0c\u547c\u5438\u95f4\u5c31\u4e34\u5371\u6025\uff0c\u731b\u7136\u60f3\u8d77\u888b\u3002\uff1a\u94a2\u4e5d\uff0c\u51b7\u4e0d\u9632\u6478\u51fa\u51e0\u7c92\uff0c\u7528\u6781\u5feb \u7684\u624b\u6cd5\u5411\u4e39\u9633\u5b50\u6253\u51fa\u3002 \u3000\u3000\u94a2\u4e38\u867d\u4ec5\u6570\u7c92\uff1a\u800c\u5c04\u51fa\u7684\u65b9\u5411\u4f4d\u7f6e\u5374\u6781\u4e3a\u5947\u5999\uff0c\u5176\u4e2d\u4e24\u679a\u662f\u5411\u94fe\u5b50\u9524\u4e0a\u78b0\uff0c\u53cd\u5c04\u800c\u51fa\uff0c \u53e6\u5916\u4e24\u679a\u5219\u662f\u98de\u5411\u4e39\u9633\u5b50\u5934\u9876\uff0c\u81ea\u7a7a\u4e2d\u4ea4\u649e\u800c\u4e0b\uff0c\u8fd8\u6709\u4e00\u679a\u662f\u671d\u7740\u4e39\u9633\u5b50\u5fc3\u53e3\u76f4\u5c04\uff0c\u8fd9\u79cd\u624b \u6cd5\uff0c\u6b66\u6797\u4e2d\u786e\u662f\u7a7a\u524d\u672a\u6709\u3002 \u3000\u3000\u4e39\u9633\u5b50\u6b63\u5168\u795e\u8d2f\u6ce8\uff0c\u8fd0\u5251\u5982\u8679\uff0c\u7a81\u7136\u773c\u524d\u661f\u98de\u4e38\u5c04\uff0c\u5bd2\u5149\u9aa4\u8d77\uff0c\u65b9\u6325\u5251\u4e0a\u4e0b\u626b\u78d5\uff0c\u800c\u5934 \u4e0a\u7684\u94a2\u4e38\u5df2\u7ffb\u98de\u800c\u4e0b\uff0c\u5403\u5403\u4e24\u58f0\u54cd\uff0c\u7a7f\u8863\u88c2\u8089\uff0c\u4f7f\u4ed6\u53cc\u80a9\u4e00\u9635\u5267\u75db\uff0c\u957f\u5251\u51e0\u4e4e\u628a\u63e1\u4e0d\u7262\uff0c\u8eab \u8eaf\u6447\u6643\u4e86\u4e0a\u4e0b\uff0c\u5411\u540e\u4fbf\u9000\u3002 \u3000\u3000\u590f\u82b8\u8fd9\u65f6\u5fc3\u91cc\u6cdb\u8d77\u5f97\u610f\u7684\u5fae\u7b11\uff0c\u81ea\u89c9\u7530\u59d0\u59d0\u4f20\u6388\u7684\u662f\u795e\u6280\uff0c\u5145\u6ee1\u4e86\u5fa1\u654c\u7684\u81ea\u4fe1\uff0c\u53ef\u662f\u5979 \u8fd9\u4e00\u5206\u5fc3\uff0c\u5979\u7684\u5e2e\u624b\u795e\u773c\u86df\u8881\u5b99\u7adf\u4e00\u4e2a\u5931\u7740\uff0c\u88ab\u51cc\u4e91\u5b50\u5251\u5c16\u81ea\u5de6\u988a\u5212\u8fc7\uff0c\u4e00\u9897\u5de6\u773c\u73e0\uff0c\u8840\u6dcb \u6dcb\u7684\u6311\u51fa\u773c\u7736\u5916\u3002 \u3000\u3000\u795e\u773c\u86df\u7adf\u6210\u4e86\u7a7a\u773c\u86df\u4e86\u3002 \u3000\u3000\u8881\u5b99\u60e8\u568e\u5982\u55e5\uff0c\u4e00\u624b\u63a9\u76ee\uff0c\u5374\u4ecd\u821e\u52a8\u94a9\u9570\u5200\u6b7b\u62fc\uff0c\u4f46\u662f\u7acb\u523b\u6c14\u6563\u795e\u4e8f\uff0c\u518d\u9f13\u4e0d\u8d77\u4ee5\u524d\u7684 \u52c7\u6c14\u4e86\u3002 \u3000\u3000\u51cc\u4e91\u5b50\u4e00\u5251\u201c\u63a8\u7a97\u9001\u6708\u201d\uff0c\u628a\u8881\u5b99\u624b\u4e2d\u94a9\u9570\u5200\u4e5f\u7ed9\u6311\u98de\u4e00\u4e08\u4ee5\u5916\uff0c\u8881\u5b99\u75db\u5165\u9aa8\u9ad3\u518d\u4e5f\u5fcd \u4e0d\u4f4f\u4e86\uff0c\u53ea\u6709\u62d4\u6b65\u98de\u9003\u3002 \u3000\u3000\u51cc\u4e91\u4e8e\u4e0d\u53bb\u8ffd\u6740\u8fd9\u53ea\u7a7a\u773c\u795e\u86df\uff0c\u5374\u8fd0\u5251\u5982\u8679\u5411\u590f\u82b8\u903c\u6765\uff0c\u590f\u82b8\u5931\u53bb\u4e86\u5e2e\u624b\uff0c\u5927\u5927\u5403\u60ca\uff0c \u5979\u5fc3\u60f3\uff1a\u201c\u8fd8\u662f\u8d76\u5feb\u9003\u8d70\u5427\uff01\u5929\u9634\u6559\u7684\u670b\u53cb\uff0c\u4e5f\u652f\u6301\u4e0d\u4f4f\u3002\u201d \u3000\u3000\u590f\u82b8\u4e0d\u518d\u548c\u51cc\u4e91\u5b50\u786c\u62fc\uff0c\u8fd9\u662f\u5979\u5386\u7ecf\u8270\u82e6\u5b66\u6765\u7684\u4e56\u3002 \u3000\u3000\u5979\u5148\u53d1\u51fa\u4e09\u7c92\u94a2\u4e38\uff0c\u963b\u4f4f\u51cc\u4e91\u5b50\u7684\u653b\u52bf\uff0c\u5750\u9a6c\u5c31\u5728\u4e00\u65c1\uff0c\u4e00\u7eb5\u8eab\u5c31\u8df3\u4e0a\u9a6c\u80cc\uff0c\u4ee5\u5979\u9a91\u672f \u7684\u7cbe\u5999\uff0c\u90a3\u9a6c\u867d\u975e\u795e\u9a79\uff0c\u4ecd\u7136\u6307\u6325\u5982\u610f\uff0c\u56db\u8e44\u626c\u5c18\uff0c\u72c2\u5954\u800c\u53bb\u3002 \u3000\u3000\u81f3\u4e8e\u5929\u9634\u6559\u7684\u4eba\uff0c\u843d\u4e2a\u4ec0\u4e48\u7ed3\u679c\uff0c\u8fd9\u53c8\u4e0e\u5979\u4f55\u5e72\u5462\uff1f \u3000\u3000\u590f\u82b8\u4e5f\u987e\u4e0d\u53ca\u8fd9\u4e9b\uff0c\u5979\u7b56\u9a6c\u9a70\u51fa\u767e\u6b65\u4ee5\u5916\uff0c\u8033\u91cc\u542c\u89c1\u90a3\u7247\u6218\u573a\u4e0a\u53c8\u6709\u6e05\u8106\u5a07\u5ae9\u5c11\u5e74\u4eba\u58f0 \u7684\u559d\u53f1\uff0c\u8eab\u540e\u542c\u4e0d\u89c1\u8ffd\u9a91\u4e4b\u58f0\uff0c\u4f46\u5979\u4ecd\u4e0d\u6562\u7247\u523b\u8fdf\u5ef6\uff0c\u6025\u6025\u62cd\u9a6c\u72c2\u5954\u3002 \u3000\u3000\u590f\u82b8\u9a70\u9a8b\u5728\u659c\u9633\u53e4\u9053\u4e0a\uff0c\u5954\u8fc7\u4e86\u4e00\u6bb5\u91cc\u7a0b\uff0c\u5fc3\u91cc\u5b89\u5b9a\u4e0b\u6765\uff0c\u8138\u4e0a\u5df2\u7c89\u6c57\u6d0b\u6d0b\uff0c\u800c\u8fd9\u5339\u5bfb \u5e38\u7684\u9a6c\uff0c\u5df2\u5c3d\u4e86\u5b83\u6700\u5927\u7684\u529b\u91cf\uff0c\u6d8e\u6cab\u55b7\u98de\u5468\u8eab\u51fa\u6c34\uff0c\u5df2\u4e0d\u80fd\u518d\u5954\u8dd1\u4e0b\u53bb\u4e86\uff0c\u6240\u5e78\u524d\u9762\u5c31\u662f\u4e00 \u7247\u9ed1\u538b\u538b\u7684\u5927\u9547\u3002 \u3000\u3000\u590f\u82b8\u4e0d\u5f97\u4e0d\u5148\u5582\u9971\u8fd9\u5339\u9a6c\uff0c\u5426\u5219\u662f\u65e0\u6cd5\u8d76\u8def\u7684\uff0c\u8fd9\u513f\u65e5\u65e0\u76ee\u7684\u7684\u5954\u9a70\uff0c\u4ec5\u4ec5\u662f\u80fd\u81ea\u6b66\u5f53 \u56db\u5b50\u624b\u4e0b\u9003\u51fa\u800c\u5df2\uff0c\u53c8\u5411\u4f55\u65b9\u627e\u5bfb\u4e45\u522b\u82e6\u601d\u7684\u501c\u54e5\u54e5\uff1f \u3000\u3000\u4e00\u6709\u4e86\u7a7a\u95f2\uff0c\u5fc3\u91cc\u5c31\u6d6e\u8d77\u4e86\u718a\u501c\u7684\u5f71\u5b50\uff0c\u82e5\u6709\u718a\u501c\u504e\u4f9d\u8eab\u4fa7\uff0c\u90a3\u8be5\u662f\u591a\u4e48\u7f8e\u5999\u7684\u5b89\u6170\uff01 \u800c\u8fd9\u5c31\u662f\u652f\u6301\u5979\u52c7\u6c14\u7684\u552f\u4e00\u6765\u6e90\uff0c\u5426\u5219\u5929\u6daf\u6e38\u5b50\uff0c\u65e9\u5e94\u8be5\u5026\u6e38\u601d\u4eb2\uff0c\u5979\u5728\u6c5f\u5357\u6e38\u8e2a\u5e74\u4f59\uff0c\u51ed \u4e00\u8eab\u6b66\u529f\uff0c\u6240\u6536\u83b7\u7684\u53c8\u662f\u4ec0\u4e48\uff1f \u3000\u3000\u5979\u4e0b\u9a6c\u8e0f\u4eba\u4e00\u5bb6\u5ba2\u6808\uff0c\u628a\u9a6c\u5339\u4ea4\u4e0e\u4f19\u8ba1\u53bb\u5582\u6599\u3002 \u3000\u3000\u75b2\u4e4f\u5df2\u6781\u7684\u8eab\u8eaf\uff0c\u6682\u65f6\u627e\u5230\u4e86\u61a9\u606f\u4e4b\u5904\uff0c\u8eba\u5728\u5e8a\u4e0a\uff0c\u4ef0\u671b\u7740\u5c4b\u6881\uff0c\u601d\u6f6e\u8d77\u4f0f\uff0c\u5979\u4e0d\u4f1a\u81ea \u6028\u81ea\u827e\uff0c\u800c\u53ea\u662f\u607c\u6068\u718a\u501c\u600e\u4e0d\u53ca\u65f6\u8ffd\u5bfb\u5979\u3002 \u3000\u3000\u5979\u5c82\u77e5\u718a\u501c\u4e5f\u4e3a\u5979\u5954\u6ce2\u5f80\u8fd4\uff0c\u5c3d\u4e86\u6781\u5927\u529b\u91cf\uff0c\u4e24\u4e0a\u6b66\u5f53\uff0c\u5f15\u8d77\u4e86\u5929\u9634\u6559\u4e0e\u6b66\u5f53\u95f4\u7684\u4e0d\u89e3 \u6df1\u4ec7\uff0c\u7b2c\u4e8c\u6b21\u51e0\u4e4e\u548c\u6b66\u5f53\u53cd\u76ee\uff0c\u66f4\u6311\u8d77\u4e94\u5927\u6b63\u6d3e\u95f4\u7684\u7ea0\u7eb7\u4e89\u6267\uff01 \u3000\u3000\u8fd9\u81ea\u7136\u662f\u5979\u59cb\u6599\u4e0d\u53ca\u7684\u3002 \u3000\u3000\u718a\u501c\u3001\u5c1a\u672a\u660e\u4e0e\u7389\u9762\u795e\u5251\u5e38\u6f2b\u5929\uff0c\u6563\u82b1\u4ed9\u5b50\u7530\u654f\u654f\u76f8\u9047\u4e4b\u540e\uff0c\u56e0\u590f\u82b8\u8d70\u5931\uff0c\u800c\u4f5c\u4e86\u4e00\u756a \u731c\u6d4b\uff0c\u5f97\u4e86\u4e2a\u9519\u8bef\u7ed3\u8bba\u3002 \u3000\u3000\u56db\u4eba\u7adf\u53cd\u5411\u6b66\u5f53\u9a70\u53bb\u3002 \u3000\u3000\u6570\u65e5\u53c8\u6765\u81f3\u8c37\u57ce\u57ce\u5185\uff0c\u627e\u5e72\u51c0\u5ba2\u5e97\u6295\u5bbf\u3002 \u3000\u3000\u5c1a\u672a\u660e\u628a\u4e0a\u6b21\u5728\u6b66\u5f53\u60c5\u5f62\uff0c\u7ec6\u8bf4\u4e0e\u5e38\u6f2b\u5929\u592b\u5987\uff0c\u4f46\u4ed6\u548c\u718a\u501c\u5374\u4e0d\u77e5\u9053\u5929\u9634\u6559\u548c\u6b66\u5f53\u6d3e\u8fd8 \u6709\u4e00\u6b21\u6fc0\u70c8\u60e8\u6597\u3002 \u3000\u3000\u5929\u9634\u6559\u5f88\u5927\u65b9\u5730\u8fd8\u7ed9\u718a\u501c\u8d2f\u65e5\u5251\uff0c\u53c8\u5043\u65d7\u606f\u9f13\u9000\u51fa\u6b66\u5f53\u5c71\uff0c\u4f7f\u718a\u501c\u7b49\u6349\u6478\u4e0d\u5b9a\u4ed6\u4eec\u7a76\u7adf \u5b58\u7740\u4ec0\u4e48\u4f01\u56fe\u3002 \u3000\u3000\u7530\u654f\u654f\u5bf9\u4e8e\u6b66\u5f53\u90a3\u79cd\u58f0\u52bf\u5413\u4eba\u7684\u5251\u9635\uff0c\u975e\u5e38\u611f\u5230\u5174\u8da3\uff0c\u996d\u540e\u5728\u5ba4\u4e2d\u805a\u8c08\uff0c\u5979\u529d\u718a\u501c\u4e0d\u5fc5 \u81ea\u884c\u8ba8\u4eba\uff0c\u7531\u5979\u592b\u5987\u591c\u95f4\u5148\u53bb\u4e00\u63a2\u3002 \u3000\u3000\u718a\u501c\u5728\u6b66\u5f53\u5c71\u9887\u53d7\u5999\u4e00\u771f\u4eba\u793c\u9047\uff0c\u800c\u4e14\u98de\u9e64\u5b50\u4ee4\u590f\u82b8\u4f20\u8bdd\uff0c\u8bf7\u4ed6\u53bb\u5c71\u4e0a\u5171\u5546\u8ba8\u4f10\u5929\u9634\u6559 \u5927\u8ba1\uff0c\u663e\u7136\u5f88\u770b\u91cd\u4ed6\uff0c\u81ea\u4e0d\u4fbf\u9aa4\u7136\u7ffb\u8138\uff0c\u53ef\u662f\u53c8\u4e0d\u80fd\u4ee4\u590f\u82b8\u53d7\u5230\u59d4\u5c48\uff0c\u6563\u82b1\u4ed9\u5b50\u60f3\u6cd5\u662f\u5148\u628a \u590f\u82b8\u6551\u51fa\u6765\uff0c\u6b63\u5408\u718a\u501c\u5fc3\u610f\u3002 \u3000\u3000\u4f46\u662f\u4e8b\u4e0d\u5173\u5df1\uff0c\u5173\u5df1\u5219\u4e71\uff0c\u718a\u501c\u4e5f\u4e0d\u80fd\u514d\u3002 \u3000\u3000\u4ed6\u51b3\u5b9a\u4e0d\u4e86\u5e94\u8be5\u91c7\u53d6\u4ec0\u4e48\u6b65\u9aa4\uff0c\u660e\u77e5\u6563\u82b1\u4ed9\u5b50\u592b\u5987\u4e00\u53bb\uff0c\u4e8b\u6001\u4f9d\u7136\u6269\u5927\uff0c\u4ed6\u6551\u5c1a\u672a\u660e\u4e8e \u5251\u9635\u4e4b\u4e2d\uff0c\u4e5f\u66fe\u4f24\u4e86\u6b66\u5f53\u95e8\u4e0b\u51e0\u4e2a\u9053\u58eb\uff0c\u4eba\u5bb6\u7adf\u6beb\u4e0d\u8bb0\u6028\uff0c\u4f9d\u718a\u501c\u8fd8\u662f\u5149\u660e\u6b63\u5927\u62dc\u8c12\u5999\u4e00\u771f \u4eba\u6bd4\u8f83\u59a5\u5f53\u4e9b\u3002 \u3000\u3000\u65e5\u654f\u654f\u5374\u5df2\u770b\u51fa\u718a\u501c\u5916\u9a70\u5185\u5f20\uff0c\u7126\u6025\u5728\u5fc3\u91cc\u4e0d\u9732\u51fa\u6765\u800c\u5df2\u3002\u5e38\u6f2b\u5929\u4e8c\u6b21\u91cd\u73b0\u6c5f\u6e56\uff0c\u66f4\u4e0d \u628a\u4e00\u822c\u4eba\u770b\u5728\u773c\u91cc\u3002 \u3000\u3000\u5e38\u6f2b\u5929\u89c1\u718a\u501c\u6709\u6240\u987e\u5fcc\uff0c\u6c89\u541f\u4e0d\u7edd\uff0c\u6b63\u5f85\u8bf4\u51fa\u4e00\u5207\u7531\u4ed6\u592b\u5987\u636e\u627f\u7684\u8bdd\uff0c\u7a81\u7136\u5ba4\u5916\u723d\u6717\u7684 \u7b11\u58f0\u9694\u7a97\u53eb\u9053\uff1a\u201c\u718a\u8001\u5f1f\uff0c\u4f55\u671f\u5728\u6b64\u76f8\u4f1a\uff0c\u771f\u662f\u5de7\u6781\u4e86\uff01\u201d \u3000\u3000\u718a\u501c\u542c\u51fa\u662f\u719f\u670b\u53cb\u7684\u53e3\u6c14\uff0c\u5fd9\u5f00\u95e8\u76f8\u8fce\u3002 \u3000\u3000\u6b63\u662f\u98de\u7075\u5821\u4e3b\u51fa\u5c18\u5251\u5ba2\u4e1c\u65b9\u7075\u5144\u59b9\uff0c\u8fd8\u6709\u51cc\u4e91\u5b50\uff0c\u4e39\u9633\u4e86\u4e24\u4f4d\u6b66\u5f53\u56db\u4eea\u5251\u5ba2\u3002 \u3000\u3000\u4e1c\u65b9\u7075\u662f\u65e7\u53cb\u76f8\u9022\uff0c\u4e00\u8138\u6e34\u6155\u4e4b\u8272\uff0c\u800c\u51cc\u4e91\u5b50\u3001\u4e39\u9633\u5b50\u5219\u9762\u8272\u51b7\u9177\uff0c\u975e\u590d\u98de\u7075\u5821\u5ea7\u4e2d\u6001 \u5ea6\uff0c\u800c\u4e1c\u65b9\u745b\u5219\u4e8e\u6109\u5feb\u5fc3\u60c5\u4e4b\u5916\uff0c\u5fae\u9732\u63f6\u63c4\u7684\u773c\u5149\u3002 \u3000\u3000\u5e38\u6f2b\u5929\u592b\u5987\u5c1a\u672a\u660e\u4e09\u4eba\uff0c\u867d\u6599\u51fa\u4e24\u4e2a\u84dd\u8863\u7384\u51a0\u9053\u58eb\uff0c\u5fc5\u662f\u6b66\u5f53\u95e8\u4e2d\uff0c\u5bf9\u4e8e\u51fa\u5c18\u5251\u5ba2\u5144\u59b9 \u4e00\u6837\u90fd\u4e0d\u8ba4\u8bc6\u3002 \u3000\u3000\u4e1c\u65b9\u7075\u4e3a\u4eba\u7b03\u539a\uff0c\u4e0d\u559c\u63ed\u4eba\u9690\u79c1\uff0c\u800c\u4e14\u4ed6\u8ba4\u4e3a\u60c5\u53d1\u4e4e\u4e2d\uff0c\u5404\u5bfb\u6240\u597d\uff0c\u4e0d\u80fd\u4e00\u4e1d\u52c9\u5f3a\uff0c\u4ed6 \u5e76\u4e0d\u4e3a\u4ed6\u59b9\u59b9\u6253\u7b97\uff0c\u800c\u53cd\u540c\u60c5\u718a\u501c\u548c\u590f\u82b8\u4e00\u53cc\u60c5\u4fa3\u3002 \u3000\u3000\u4ed6\u5f88\u70ed\u8bda\u7684\u63e1\u4f4f\u718a\u9600\u7684\u624b\u8bf4\uff1a\u201c\u8001\u5f1f\u81ea\u79bb\u655d\u5821\uff0c\u4ee4\u6211\u601d\u5ff5\u81f3\u4eca\uff01\u201d\u53c8\u4e00\u77a5\u773c\u524d\u8fd9\u4e09\u4f4d\u4e0d \u5e73\u51e1\u7684\u4eba\u7269\u7b11\u95ee\uff1a\u201c\u8fd9\u51e0\u4f4d\u90fd\u5668\u5b87\u4e0d\u51e1\uff0c\u5feb\u66ff\u6211\u4ecb\u7ecd\u4e00\u4e0b\u4f60\u7684\u65b0\u4ea4\uff01\u201d \u3000\u3000\u4e1c\u65b9\u82f1\u655b\u887d\u4e3a\u793c\uff0c\u82e5\u6709\u60c5\u82e5\u65e0\u60c5\u7684\u659c\u7768\u4e86\u718a\u501c\u4e00\u773c\uff0c\u5979\u6ca1\u6709\u590f\u82b8\u90a3\u4e48\u5929\u771f\u800c\u8d64\u8bda\u7684\u6d41 \u9732\uff0c\u5c31\u662f\u6709\u4e9b\u6d41\u9732\u51fa\u6765\u7684\uff0c\u4e5f\u662f\u5728\u65e0\u610f\u6709\u610f\u4e4b\u95f4\u3002 \u3000\u3000\u7c89\u8776\u9ed8\u9ed8\u65e0\u8a00\uff0c\u5947\u602a\u7684\u5979\u7c89\u988a\u7adf\u5fae\u5fae\u751f\u6655\uff0c\u8fd9\u662f\u7531\u4e8e\u5185\u5fc3\u6f3e\u8d77\u4e00\u79cd\u5947\u5999\u7684\u611f\u89c9\uff0c\u81ea\u7136\u800c \u7136\u4f7f\u5979\u5fc3\u91cc\u6709\u4e9b\u8df3\u52a8\u3002 \u3000\u3000\u6b66\u5f53\u4e8c\u5b50\u5219\u52c9\u5f3a\u5404\u65bd\u4e00\u793c\uff0c\u51b7\u51b7\u7684\u76ee\u5149\uff0c\u4ecd\u6ce8\u89c6\u7740\u718a\u501c\uff0c\u4f3c\u8981\u4ece\u4ed6\u8eab\u4e0a\u627e\u51fa\u4ec0\u4e48\u6765\u3002 \u3000\u3000\u51cc\u4e91\u5b50\u64d2\u670d\u590f\u82b8\u4e4b\u540e\uff0c\u5f53\u573a\u4e0d\u4f46\u590f\u82b8\u88ab\u718a\u501c\u6551\u8d70\uff0c\u53cd\u800c\u5403\u4e86\u4e00\u6b21\u6697\u4e8f\uff0c\u4ed6\u81f3\u4eca\u8fd8\u4ee5\u4e3a\u662f \u718a\u501c\u7684\u6076\u4f5c\u5267\u3002 \u3000\u3000\u98de\u9e64\u5b50\u7b49\u5ef6\u63fd\u718a\u501c\uff0c\u4ee5\u53ca\u6b66\u5f53\u5c71\u4e0a\u6240\u8d77\u7684\u53d8\u6545\uff0c\u51cc\u4e91\u5b50\u56fa\u66fe\u4e0e\u98de\u9e64\u4e8e\u9082\u9005\u8c08\u53ca\uff0c\u800c\u51fa\u5c18 \u5251\u5ba2\u5144\u59b9\u4e5f\u5c31\u662f\u4ed6\u7ea6\u6765\u6b66\u5f53\u5c71\u7684\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u4ed6\u8fd8\u662f\u607c\u6068\u7740\u718a\u501c\uff0c\u590f\u82b8\u7adf\u4e0e\u5929\u9634\u6559\u4eba\u4e3a\u4f0d\uff0c \u4e95\u80a9\u4f5c\u6218\uff0c\u5c24\u5176\u4f7f\u4ed6\u4e0d\u6ee1\u718a\u501c\u3002 \u3000\u3000\u4e0d\u6ee1\u5c3d\u7ba1\u4e0d\u6ee1\uff0c\u5374\u603b\u4e0d\u80fd\u8fdd\u6297\u5999\u4e00\u638c\u95e8\u5e08\u8c15\uff0c\u4ed6\u4e00\u89c1\u9762\u672c\u5c31\u60f3\u63ed\u53d1\u590f\u82b8\u7684\u4e8b\uff0c\u4f46\u718a\u501c\u6b63 \u70ed\u5fc3\u66ff\u53cc\u65b9\u4ecb\u7ecd\u76f8\u89c1\u3002 \u3000\u3000\u51cc\u4e91\u5b50\u542c\u8bf4\u5f53\u5e74\u7684\u70b9\u82cd\u638c\u95e8\u7389\u9762\u795e\u5251\u5e38\u6f2b\u5929\uff0c\u548c\u6563\u82b1\u4ed9\u5b50\u7530\u654f\u654f\u65f6\uff0c\u4e0d\u7531\u4e3a\u8fd9\u4e24\u4eba\u7684\u7edd \u4e16\u4e30\u91c7\u800c\u5fc3\u6298\u3002 \u3000\u3000\u94c1\u80c6\u5c1a\u672a\u660e\u5728\u5317\u51e0\u7701\u7684\u58f0\u540d\uff0c\u5927\u5f97\u60ca\u4eba\uff0c\u8fd9\u4e09\u4f4d\u7684\u540d\u5934\uff0c\u4f7f\u4e1c\u65b9\u7075\u5144\u59b9\u5982\u83b7\u81f3\u5b9d\uff0c\u51cc\u4e91 \u5b50\u4e5f\u4e9f\u613f\u6b66\u5f53\u6d3e\u80fd\u7f57\u81f4\u5230\u8fd9\u6837\u4e09\u4f4d\u4e86\u4e0d\u8d77\u7684\u4eba\u7269\uff0c\u56e0\u800c\u51cc\u4e91\u5b50\u4e39\u9633\u5b50\u6001\u5ea6\u4e0a\u90fd\u7565\u7565\u53d8\u4e86\u4e9b\uff0c \u5f88\u8c26\u865a\u7684\u5ba2\u5957\u4e00\u756a\u3002 \u3000\u3000\u70db\u5f71\u6447\u7ea2\uff0c\u516b\u4f4d\u6b66\u6797\u8c6a\u58eb\uff0c\u805a\u9996\u4e00\u5802\uff0c\u5e94\u8be5\u662f\u6c34\u4e73\u4ea4\u878d\u809d\u80c6\u76f8\u7167\u4e86\uff0c\u800c\u7c89\u8776\u4e1c\u65b9\u745b\u5219\u8ba1 \u5212\u7740\u5982\u4f55\u66ff\u81ea\u5df1\u5b89\u6392\u4e00\u4e0b\uff0c\u718a\u501c\u7684\u5fc3\u7406\uff0c\u4e5f\u6b63\u6e34\u6b32\u4e00\u8be2\u590f\u82b8\u7684\u7740\u843d\u7a76\u7adf\u3002 \u3000\u3000\u6563\u82b1\u4ed9\u5b50\u7530\u654f\u654f\u5df2\u6025\u4e0d\u53ef\u8010\uff0c\u5979\u4ee5\u51b7\u5bd2\u58f0\u53e3\uff0c\u8fd1\u4e4e\u53d1\u6c14\u7684\u8bed\u8c03\u53d1\u95ee\uff1a\u201c\u51cc\u4e91\u9053\u957f\uff0c\u718a\u8001 \u5f1f\u4ed6\u7684\u5973\u53cb\u96ea\u5730\u98d8\u98ce\u590f\u59d1\u5a18\uff0c\u60f3\u5fc5\u88ab\u4f60\u4eec\u5b89\u7f6e\u5728\u6b66\u5f53\u5c71\u4e0a\u4e86\uff01\u96ea\u5730\u98d8\u98ce\u53ea\u662f\u4e2a\u4efb\u6027\u7684\u5973\u5b69 \u5b50\uff0c\u4f60\u4eec\u505a\u4e8b\u672a\u514d\u8fc7\u5206\u4e86\u70b9\uff01\u201d \u3000\u3000\u51cc\u4e91\u5b50\u989c\u8272\u4e00\u53d8\uff0c\u6ca1\u60f3\u5230\u7530\u654f\u654f\u9aa4\u5174\u95ee\u96be\u4e4b\u8a00\u3002 \u3000\u3000\u4ed6\u767d\u4e86\u6563\u82b1\u4ed9\u5b50\u4e00\u773c\uff0c\u53cd\u5411\u7740\u718a\u501c\u8bf4\uff1a\u201c\u590f\u59d1\u5a18\u7684\u4e8b\uff0c\u8d2b\u9053\u731c\u60f3\u53f0\u7aef\u8fd8\u4f1a\u4e0d\u77e5\u6653\uff1f\u5929\u9634 \u6559\u5355\u638c\u65ad\u9b42\u5355\u98de\uff0c\u6d1e\u5ead\u56db\u86df\u90fd\u662f\u5979\u7684\u62a4\u536b\uff0c\u4e0d\u6298\u4e0d\u6263\u5979\u5df2\u662f\u5929\u9634\u6559\u4e0b\u7684\u4e00\u4f4d\u4e86\u4e0d\u8d77\u7684\u4eba\u7269\uff01 \u718a\u5927\u4fa0\u81ea\u7136\u8868\u9762\u4e0a\u81ea\u547d\u6e05\u9ad8\uff0c\u548c\u5929\u9634\u6559\u4e5f\u662f\u6709\u4e9b\u9ed8\u5951\u5462\uff01\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8bed\u60ca\u56db\u5ea7\uff0c\u4e0d\u4f46\u718a\u501c\u4e08\u4e8c\u91d1\u521a\u6478\u4e0d\u7740\u5934\u8111\uff0c\u800c\u8fd9\u79cd\u5f62\u540c\u6316\u82e6\u7684\u8bdd\uff0c\u4f7f\u718a\u501c\u600e\u80fd\u4e0d \u65e0\u540d\u706b\u9ad8\u8d77\u5343\u4e08\u3002 \u3000\u3000\u6563\u82b1\u4ed9\u5b50\u5219\u66f4\u4e0d\u76f8\u4fe1\u590f\u82b8\u4f1a\u6295\u5165\u5929\u9634\u6559\u4e0b\uff0c\u590f\u82b8\u548c\u5979\u662f\u65e0\u8bdd\u4e0d\u8c08\uff0c\u503e\u56ca\u5012\u7b50\uff0c\u7530\u654f\u654f\u6c14 \u5f97\u4e00\u62cd\u684c\u5b50\u5927\u58f0\u559d\u9053\uff1a\u201c\u7b80\u76f4\u662f\u80e1\u8bf4\uff01\u82b8\u59b9\u59b9\u5b9b\u5982\u4e00\u5934\u6d3b\u6cfc\u7684\u767e\u7075\u9e1f\uff0c\u4ece\u4e0d\u4e0e\u6c5f\u6e56\u90aa\u95e8\u4eba\u5f80 \u6765\uff0c\u4f60\u4fae\u8fb1\u5979\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u51cc\u4e91\u5b50\u53cd\u5507\u76f8\u8ba5\u8bf4\uff1a\u201c\u6b63\u56e0\u4e3a\u5e74\u5e7c\u65e0\u77e5\uff0c\u624d\u5206\u8fa8\u4e0d\u51fa\u5929\u9634\u6559\u7684\u5584\u6076\uff01\u73b0\u6709\u4e8b\u5b9e\u6709\u4e3a\u8bc1\uff0c \u8d2b\u9053\u6b63\u82e6\u4e8e\u65e0\u6cd5\u6551\u5979\u4e8e\u9677\u6eba\u4e4b\u4e2d\uff0c\u70b9\u82cd\u6d3e\u9ad8\u624b\u8bf7\u5148\u5f04\u6e05\u695a\u662f\u975e\uff0c\u518d\u8d23\u602a\u8d2b\u9053\uff0c\u8d2b\u9053\u6562\u4e0d\u9886 \u7f6a\uff01\u201d \u3000\u3000\u8fd9\u4e00\u5e2d\u8bdd\uff0c\u4f7f\u878d\u6d3d\u4e0d\u4e45\u7684\u7a7a\u6c14\uff0c\u5feb\u8981\u7206\u70b8\u8d77\u6765\u4e86\u3002 \u3000\u3000\u718a\u501c\u76ee\u5c04\u795e\u5149\uff0c\u6ce8\u89c6\u7740\u6b66\u5f53\u4e8c\u5b50\uff0c\u4ed6\u867d\u672a\u7acb\u5373\u53d1\u96be\u8d23\u65a5\uff0c\u4f46\u663e\u7136\u590f\u82b8\u8fd9\u6b21\u662f\u6ca1\u6709\u5403\u4ed6\u4eec \u7684\u4e8f\u4e86\u3002 \u3000\u3000\u590f\u82b8\u662f\u4e0d\u662f\u4e2a\u5e26\u6709\u795e\u79d8\u6027\u7684\u5973\u5b69\u5b50\uff1f \u3000\u3000\u4e1c\u65b9\u7075\u8001\u6210\u6301\u91cd\uff0c\u5148\u628a\u53cc\u65b9\u529d\u4f4f\uff0c\u4ed6\u5f88\u5feb\u7684\u628a\u5f53\u65e5\u5b98\u9053\u4e0a\u60c5\u5f62\u7565\u8ff0\u4e00\u904d\uff0c\u9053\uff1a\u201c\u590f\u59d1\u5a18 \u4ece\u672a\u6c42\u52a9\u5355\u638c\u65ad\u9b42\uff0c\u800c\u8fd9\u4e09\u4eba\u4e3a\u5979\u62fc\u547d\u82e6\u6597\uff0c\u786e\u662f\u4e8b\u5b9e\uff0c\u540e\u6765\u5929\u9634\u6559\u4e24\u4e2a\u53f8\u793c\u7ae5\u5b50\uff0c\u9ed1\u8863\u6469 \u52d2\u767d\u666f\u7965\uff0c\u767d\u8863\u9f99\u5973\u53f6\u6e05\u6e05\u4e5f\u51fa\u9762\u4ea4\u624b\uff0c\u5426\u5219\u590f\u59d1\u5a18\u5c82\u80fd\u4ece\u5bb9\u9003\u8d70\uff1f\u5355\u98de\u7b49\u53c8\u600e\u80fd\u4e0d\u8840\u6e85\u5c18 \u571f\u5462\uff1f\u201d \u3000\u3000\u718a\u501c\u957f\u957f\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u4ed6\u5fc3\u91cc\u7eb7\u4e71\u5982\u9ebb\uff0c\u590f\u82b8\u771f\u7684\u4e0e\u5929\u9634\u6559\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u5979\u53c8\u9003\u5f80\u4f55 \u5904\uff1f\u5929\u9634\u6559\u4eba\u4f55\u6545\u62fc\u6027\u547d\u4fdd\u62a4\u5979\uff1f \u3000\u3000\u4e00\u8fde\u4e32\u7684\u7591\u95ee\uff0c\u4f7f\u4ed6\u9677\u5165\u8ff7\u60d8\u3002 \u3000\u3000\u6563\u82b1\u4ed9\u5b50\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u53ef\u89c1\u51cc\u4e91\u9053\u957f\u662f\u4fe1\u53e3\u8bec\u8511\u4e86\uff01\u5929\u9634\u6559\u4eba\u8892\u62a4\u5979\uff0c\u6216\u8bb8\u522b\u6709\u7528 \u610f\uff0c\u4f46\u662f\u9053\u957f\u4eec\u4ee5\u591a\u6b3a\u5be1\uff0c\u6043\u5f3a\u51cc\u5f31\uff0c\u6211\u6563\u82b1\u4ed9\u5b50\u5f53\u65f6\u5728\u573a\uff0c\u4e5f\u4e0d\u80fd\u5bb9\u4f60\u4eec\u8fd9\u6837\u80e1\u95f9\uff01\u8001\u5b9e \u8bf4\u6211\u770b\u5f85\u5979\u65e0\u5f02\u4eb2\u59b9\u59b9\uff01\u4f60\u4eec\u518d\u8bf4\u8fd9\u79cd\u65e0\u7a3d\u8bec\u8511\u7684\u8bdd\uff0c\u6211\u53ef\u4e0d\u80fd\u653e\u8fc7\uff01\u201d \u3000\u3000\u4e1c\u65b9\u7075\u4e3a\u4e86\u987e\u5168\u5927\u5c40\uff0c\u8bbe\u82e5\u8fd9\u56db\u4f4d\u6b66\u529f\u9876\u513f\u5c16\u513f\u7684\u4eba\uff0c\u4e0e\u6b66\u5f53\u53cd\u76ee\u6210\u4ec7\uff0c\u90a3\u53cd\u4f7f\u5929\u9634\u6559 \u5f97\u4ee5\u4ece\u4e2d\u6e14\u5229\uff0c\u6b66\u6797\u5c40\u9762\u66f4\u65e0\u6cd5\u6536\u62fe\u4e86\uff0c\u4ed6\u6025\u5f97\u6ee1\u5934\u5927\u6c57\uff0c\u5411\u53cc\u65b9\u4e00\u518d\u529d\u8bf4\uff0c\u4ece\u6b64\u5f7c\u6b64\u90fd\u518d \u4e0d\u8bb8\u5e72\u6d89\u590f\u82b8\u3002 \u3000\u3000\u4ed6\u8bf4\uff1a\u201c\u6b66\u6797\u6b63\u6d3e\u6b63\u5e94\u540c\u5fc3\u5408\u529b\uff0c\u5bf9\u4ed8\u5929\u9634\u6559\uff01\u4e0d\u53ef\u56fa\u5c0f\u5c0f\u8bef\u4f1a\uff0c\u4f7f\u4eb2\u8005\u75db\u800c\u4ec7\u8005\u79f0 \u5feb\uff0c\u70b9\u82cd\u7530\u59d1\u5a18\u6280\u62df\u5929\u4eba\uff0c\u8d24\u4f09\u4fea\u8a89\u6ee1\u6b66\u6797\uff0c\u718a\u8001\u5f1f\u540e\u8d77\u4e4b\u79c0\uff0c\u7765\u7768\u7fa4\u96c4\uff0c\u5c1a\u5927\u4fa0\u9886\u8896\u4e24\u6cb3 \u7eff\u6797\u8c6a\u6770\uff0c\u4e0d\u4f1a\u4ee5\u6211\u7684\u8bdd\u4e3a\u65e0\u7406\u5427\uff1f\u201d \u3000\u3000\u51cc\u4e91\u5b50\u8c6a\u6c14\u51cc\u4e91\uff0c\u672c\u4e0d\u80af\u76f8\u4e0b\uff0c\u4f46\u4e5f\u6709\u4e9b\u987e\u5fcc\uff0c\u6b66\u5f53\u6d3e\u904d\u6492\u82f1\u96c4\u5e16\uff0c\u8058\u8bf7\u5404\u6d3e\u540d\u5bbf\uff0c\u4e3a \u7684\u4ec0\u4e48\uff1f\u50cf\u8fd9\u56db\u4f4d\u9ad8\u624b\uff0c\u8bf7\u8fd8\u8bf7\u4e0d\u5230\uff0c\u771f\u662f\u4e00\u80a1\u96c4\u539a\u7684\u751f\u529b\u519b\uff0c\u8db3\u591f\u4e3e\u8db3\u8f7b\u91cd\uff0c\u5f71\u54cd\u5230\u672a\u6765 \u6b66\u6797\u7684\u5927\u5c40\uff01 \u3000\u3000\u51cc\u4e91\u5b50\u5728\u6c14\u5934\u4e0a\u4e0d\u80af\u4f4e\u5934\u8ba4\u9519\uff0c\u8fd9\u4e5f\u662f\u4eba\u4e4b\u5e38\u60c5\u3002 \u3000\u3000\u4e39\u9633\u5b50\u548c\u4ed6\u4e00\u6837\u88ab\u4e1c\u65b9\u7075\u4e00\u7bc7\u8bdd\uff0c\u8bf4\u5f97\u9ed8\u9ed8\u65e0\u8a00\u3002 \u3000\u3000\u5ba4\u4e2d\u7684\u7a7a\u6c14\u5f02\u5e38\u6c89\u91cd\uff0c\u82e5\u5c31\u8fd9\u6837\u4e0d\u6b22\u800c\u6563\uff0c\u718a\u501c\u8fd9\u56db\u4f4d\u4e5f\u7edd\u4e0d\u4f1a\u518d\u4e0a\u6b66\u5f53\uff0c\u548c\u6b66\u5f53\u4e00\u6d3e \u5408\u4f5c\u4e86\u3002 \u3000\u3000\u4e1c\u65b9\u7075\u53c8\u518d\u4e09\u529d\u89e3\uff0c\u628a\u8fd9\u56de\u4e8b\u7b97\u4e3a\u4e00\u573a\u5c0f\u5c0f\u8bef\u4f1a\u3002 \u3000\u3000\u94c1\u80c6\u5c1a\u672a\u660e\u672c\u662f\u706b\u70c8\u6027\u5b50\uff0c\u53c8\u5c61\u5c61\u6012\u7709\u6a2a\u76ee\uff0c\u51c6\u5907\u6765\u4e2a\u60ca\u4eba\u52a8\u4f5c\uff0c\u4ed6\u770b\u89c1\u718a\u501c\u9677\u4e8e\u6c89\u601d \u72b6\u6001\uff0c\u53c8\u6709\u6563\u82b1\u4ed9\u5b50\u4e0d\u5ba2\u6c14\u5730\u53d1\u4f5c\u51fa\u6765\uff1b\u89c9\u5f97\u975e\u5e38\u6dcb\u6f13\u75db\u5feb\uff0c\u5728\u4e1c\u65b9\u7075\u7aed\u529b\u65a1\u65cb\u4e4b\u4e0b\uff0c\u6b66\u5f53 \u4e8c\u5b50\u4e0d\u518d\u5014\u5f3a\uff0c\u5012\u4e5f\u672a\u4fbf\u53d1\u4f5c\u4e86\u3002 \u3000\u3000\u7530\u654f\u654f\u662f\u4f55\u7b49\u5fc3\u9ad8\u6c14\u505a\uff0c\u51b7\u7b11\u5411\u718a\u501c\u8bf4\uff1a\u201c\u718a\u8001\u5f1f\uff0c\u65e2\u7136\u662f\u8fd9\u4e48\u4e00\u56de\u4e8b\uff0c\u6211\u4eec\u660e\u5929\u518d\u53bb \u9102\u57ce\u4e00\u5e26\u4ed4\u7ec6\u5bfb\u4e00\u4e0b\u82b8\u59b9\u59b9\uff0c\u627e\u7740\u65f6\u5e26\u4e86\u82b8\u59b9\u4e00\u540c\u518d\u5411\u6b66\u5f53\u56db\u4eea\u5251\u5ba2\uff0c\u89c1\u89c1\u771f\u7ae0\u5206\u6653\uff0c\u51ed\u4ec0 \u4e48\u5c61\u6b21\u6b3a\u4fae\u6211\u7684\u82b8\u59b9\u59b9\uff1f\u201d \u3000\u3000\u8fd9\u8bdd\u4e00\u8bf4\uff0c\u6025\u574f\u4e86\u4e1c\u65b9\u7075\u3002 \u3000\u3000\u540c\u65f6\u7c89\u8776\u4e1c\u65b9\u82f1\u5fc3\u7075\u4e0a\u8499\u4e0a\u4e00\u5c42\u9634\u5f71\uff0c\u718a\u501c\u591a\u5c11\u56e0\u51cc\u4e91\u5b50\u7684\u8bdd\uff0c\u6000\u7591\u7740\u590f\u82b8\uff0c\u7136\u800c\u4ed6\u4f4e \u5934\u7b49\u601d\uff0c\u663e\u7136\u4e0d\u80fd\u5fd8\u60c5\u4e8e\u5979\uff0c\u800c\u4e14\u5e76\u975e\u56e0\u6b64\u6df1\u604b\u75db\u7edd\u4e86\u5979\u3002 \u3000\u3000\u56db\u4eba\u5982\u7167\u6563\u82b1\u4ed9\u5b50\u4e3b\u5f20\u4e00\u8d70\u4e86\u4e4b\uff0c\u90a3\u540e\u679c\u6b8a\u96be\u9884\u6599\uff0c\u5982\u4f55\u4e0d\u4f7f\u4e1c\u65b9\u7075\u5fc3\u6025\u3002\u4ed6\u5fd9\u8bf4\uff1a \u201c\u7530\u59d1\u5a18\uff0c\u8bf7\u52ff\u63a8\u6ce2\u52a9\u6d6a\uff0c\u6b66\u5f53\u56db\u4eea\u5251\u5ba2\u7edd\u4e0d\u4e3a\u5df2\u751a\uff0c\u59d1\u5a18\u4f55\u82e6\u6269\u5927\u8fd9\u4ef6\u4e8b\u5462\uff1f\u51b5\u4e14\u5343\u91cc\u8fe2 \u8fe2\u6765\u6b64\uff0c\u600e\u53ef\u4e0d\u4e0e\u5999\u4e00\u771f\u4eba\u524d\u8f88\u4e00\u6664\uff1f\u201d \u3000\u3000\u51cc\u4e91\u5b50\u6743\u8861\u5229\u5bb3\uff0c\u4e5f\u6050\u56de\u5c71\u53d7\u638c\u95e8\u65a5\u8d23\uff0c\u52c9\u5f3a\u9644\u548c\u7740\u8bf4\uff1a\u201c\u5f80\u4e8b\u4e00\u7b14\u52fe\u9500\uff0c\u7530\u59d1\u5a18\u53ea\u77e5 \u602a\u8d2b\u9053\uff0c\u4e0d\u8bf4\u590f\u82b8\u4fae\u672c\u6d3e\u4e5d\u5b98\u8fde\u73af\u65d7\uff0c\u4f7f\u672c\u6d3e\u4f53\u9762\u4f55\u5b58\u3001\u8d2b\u9053\u82e5\u77e5\u590f\u82b8\u662f\u718a\u4fa0\u58eb\u7684\u7231\u4fa3\uff0c\u65e9 \u5c31\u653e\u5f00\u624b\u4e86\u3002\u201d \u3000\u3000\u5176\u5b9e\u8fd9\u662f\u4ed6\u4e00\u79cd\u9041\u8f9e\uff0c\u4ed6\u5e76\u975e\u4e0d\u77e5\u590f\u82b8\u662f\u548c\u718a\u501c\u5728\u4e00\u8d77\u7684\uff0c\u8fd9\u53e5\u8bdd\u591a\u5c11\u9001\u7ed9\u718a\u501c\u70b9\u9762 \u5b50\uff0c\u786e\u662f\u56db\u4eea\u5251\u5ba2\u59d4\u66f2\u6c42\u5168\u7684\u4e8b\u3002 \u3000\u3000\u4e1c\u65b9\u7075\u4e58\u673a\u53c8\u7b11\u9053\uff1a\u201c\u718a\u8001\u5f1f\u7edd\u4e0d\u80fd\u8d70\uff01\u6211\u8fd8\u8981\u5411\u56db\u4f4d\u591a\u591a\u8ba8\u6559\uff0c\u6765\u5427\uff01\u51cc\u4e91\u9053\u5144\u5df2\u7ecf \u8ba4\u4e86\u9519\uff0c\u5f7c\u6b64\u63e1\u63e1\u624b\u628a\u4ee5\u524d\u5acc\u9699\u4e00\u9f50\u629b\u5f00\u5427\uff01\u201d\u4ed6\u786c\u628a\u51cc\u4e91\u5b50\u63a8\u5411\u718a\u501c\u9762\u524d\uff0c\u4f7f\u8fd9\u4e00\u5929\u4e4c \u4e91\uff0c\u5316\u4e3a\u6674\u7a7a\uff0c\u8ba9\u4ed6\u4fe9\u6781\u4e0d\u81ea\u7136\u5730\u63e1\u4e86\u63e1\u624b\u3002 \u3000\u3000\u718a\u501c\u867d\u7136\u6025\u4e8e\u5bfb\u627e\u590f\u82b8\uff0c\u5374\u88ab\u8fd9\u79cd\u573a\u9762\u62d8\u4f4f\uff0c\u771f\u8981\u6492\u624b\u4e00\u8d70\uff0c\u6b66\u5f53\u6d3e\u9762\u5b50\u4e0a\u53c8\u600e\u4e48\u4e0b\u5f97 \u53bb\u5462\uff1f \u3000\u3000\u5c1a\u672a\u660e\u5374\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u5999\u4e00\u771f\u4eba\u5982\u70ed\u8bda\u6b3e\u5ba2\uff0c\u5e94\u8be5\u628a\u90a3\u4e9b\u4e0d\u8bb8\u5e26\u5251\u4e0a\u5c71\u4e4b\u7c7b\u7684\u81ed\u89c4\u77e9\u6682 \u65f6\u53d6\u6d88\uff0c\u4e0a\u6b21\u5728\u89e3\u5251\u6c60\u7554\uff0c\u51e0\u4e4e\u628a\u718a\u5927\u54e5\u8d2f\u65e5\u5251\u4fbf\u5b9c\u4e86\u5929\u9634\u6559\u4e3b\uff0c\u5982\u8fd8\u662f\u5e9e\u7136\u81ea\u5927\uff0c\u60df\u6211\u72ec \u5c0a\uff0c\u5c1a\u67d0\u53ef\u65e0\u989c\u518d\u4e0a\u6b66\u5f53\u3002\u201d \u3000\u3000\u8fd9\u4e2a\u96be\u9898\uff0c\u51e0\u4e4e\u6fc0\u6012\u4e86\u51cc\u4e91\u3001\u4e39\u9633\u4e8c\u5b50\uff0c\u4f46\u4e1c\u65b9\u7075\u5f88\u5de7\u5999\u7684\u8c03\u505c\u8bf4\uff1a\u201c\u6b66\u5f53\u6d3e\u65e2\u7136\u8058\u9080 \u5404\u65b9\u8c6a\u6770\uff0c\u5fc5\u81ea\u6709\u53d8\u901a\u529e\u6cd5\uff01\u51b5\u4e14\u5c1a\u5f53\u5bb6\u7684\u524d\u6b21\u4e5f\u66fe\u88ab\u9080\u81f3\u7384\u771f\u89c2\uff0c\u4ee5\u793c\u76f8\u5f85\u3002\u5c82\u53ef\u56e0\u5c0f\u5c0f \u7684\u8bef\u4f1a\uff0c\u6c38\u8bb0\u5728\u5fc3\uff1f\u201d \u3000\u3000\u7530\u654f\u654f\u7b11\u5f97\u82b1\u679d\u4e71\u98a4\u8bf4\uff1a\u201c\u6211\u8fd8\u4e0d\u6653\u5f97\u6709\u8fd9\u79cd\u89c4\u77e9\u5462\uff0c\u6211\u662f\u5251\u4e0d\u79bb\u8eab\u60ef\u4e86\u7684\uff0c\u90a3\u53e6\u6709\u4e0d \u5f97\u5176\u95e8\u800c\u5165\u4e86\u3002\u201d \u3000\u3000\u4e1c\u65b9\u7075\u6050\u4f7f\u4e8c\u5b50\u96be\u582a\uff0c\u8d76\u5feb\u53e6\u5bfb\u8bdd\u5934\u5c94\u8fc7\u53bb\u3002 \u3000\u3000\u4e00\u5915\u6e05\u8c08\uff0c\u603b\u7b97\u5316\u5e72\u6208\u4e3a\u7389\u5e1b\uff0c\u800c\u4e0d\u6109\u5feb\u7684\u6c14\u6c1b\uff0c\u59cb\u7ec8\u4e0d\u80fd\u4e00\u626b\u800c\u7a7a\uff0c\u4e1c\u65b9\u745b\u591a\u5c11\u662f\u5f97 \u4e86\u4e9b\u673a\u4f1a\uff0c\u5979\u548c\u7530\u654f\u654f\u633d\u81c2\u957f\u8c08\uff0c\u5341\u5206\u6295\u5408\u3002\u7c89\u8776\u513f\u6293\u4f4f\u4e86\u8fd9\u4e2a\u673a\u4f1a\uff0c\u4e5f\u53ef\u8bf4\u662f\u4e00\u6761\u8def\u7ebf\uff0c \u56e0\u4e4b\u80fd\u5f97\u4eb2\u8fd1\u718a\u501c\u4e00\u6b65\u3002 \u3000\u3000\u6b21\u65e5\uff0c\u4e1c\u65b9\u745b\u548c\u7530\u654f\u654f\u5df2\u65e0\u8bdd\u4e0d\u8c08\uff0c\u4e1c\u65b9\u745b\u53e6\u5177\u4e00\u79cd\u6e29\u67d4\u5a34\u9759\u7684\u7f8e\uff0c\u6563\u82b1\u4ed9\u5b50\u51b7\u773c\u770b \u6765\uff0c\u5df2\u770b\u51fa\u7c89\u8776\u7684\u5fc3\u4e8b\u91cd\u91cd\uff0c\u7c89\u8776\u806a\u660e\u4e4b\u5904\uff0c\u662f\u4e0d\u518d\u8bcb\u6bc1\u590f\u82b8\uff0c\u53cd\u800c\u540c\u60c5\u5979\uff0c\u62c5\u5fc3\u5979\u53d7\u5929\u9634 \u6559\u7684\u8bf1\u9a97\u3002 \u3000\u3000\u4e1c\u65b9\u745b\u5e84\u91cd\u800c\u5a34\u9759\u7684\u7f8e\uff0c\u4f7f\u7530\u654f\u654f\u4e5f\u5341\u5206\u5668\u91cd\u5979\u3002 \u3000\u3000\u4e1c\u65b9\u7075\u6050\u51cc\u4e91\u5b50\u4e39\u9633\u5b50\u518d\u548c\u4ed6\u4eec\u5f15\u8d77\u4e0d\u6109\u5feb\u7684\u4e89\u8bba\uff0c\u5506\u4ee4\u4ed6\u4fe9\u5148\u884c\u79bb\u53bb\uff0c\u8fd4\u5c71\u6e34\u89c1\u5999\u4e00 \u771f\u4eba\uff0c\u53e6\u6d3e\u540c\u95e8\u6765\u8fce\u8fd9\u56db\u4f4d\uff0c\u5c82\u77e5\u51cc\u4e91\u5b50\u4e39\u9633\u5b50\u4e00\u56de\u5230\u5c71\u4e0a\uff0c\u7adf\u53d7\u5230\u5999\u4e00\u771f\u4eba\u7684\u4e00\u756a\u8d23\u65a5\uff0c \u4e0d\u8bb8\u4ed6\u4eec\u518d\u4e0b\u5c71\u6ecb\u4e8b\u3002 \u3000\u3000\u53e6\u7531\u6b66\u5f53\u6d3e\u4e0b\u82cd\u7a79\u5b50\u82cd\u677e\u5b50\u4e24\u4f4d\u9053\u58eb\uff0c\u4e0b\u5c71\u6765\u8fce\u63a5\u718a\u501c\u56db\u4eba\u548c\u4e1c\u65b9\u5821\u4e3b\u5144\u59b9\u767b\u5c71\uff0c\u4e1c\u65b9 \u7075\u4e0a\u4e16\u5e08\u627f\u4e0e\u6b66\u5f53\u6d3e\u6e0a\u6e90\u9887\u6df1\uff0c\u5426\u5219\u4e0d\u4f1a\u4e13\u66ff\u6b66\u5f53\u8bbe\u60f3\u7684\u3002 \u3000\u3000\u82cd\u7a79\u82cd\u677e\u6b66\u529f\u4e0e\u56db\u5b50\u76f8\u5dee\u4e0d\u591a\uff0c\u8001\u6210\u6301\u91cd\uff0c\u662f\u89c2\u91cc\u8d1f\u8d23\u62db\u5f85\u5404\u65b9\u8c6a\u6770\u7684\u4eba\uff0c\u90fd\u5df2\u9b13\u53d1\u82cd \u82cd\uff0c\u5e74\u903e\u4e94\u5341\u4e86\u3002 \u3000\u3000\u82cd\u7a79\u82cd\u677e\u4ee5\u793c\u6765\u9080\uff0c\u6001\u5ea6\u4e5f\u4e0e\u51cc\u4e91\u5b50\u7b49\u4e0d\u540c\uff0c\u4f7f\u6563\u82b1\u4ed9\u5b50\u53ca\u5c1a\u672a\u660e\u65e0\u6cd5\u501f\u9898\u53d1\u6325\u3002 \u3000\u3000\u718a\u501c\u9ed8\u9ed8\u968f\u7740\u4f17\u4eba\uff0c\u4e00\u540c\u4e0a\u4e86\u6b66\u5f53\u5c71\u3002 \u3000\u3000\u5feb\u8d70\u8fd1\u89e3\u5251\u6c60\u7554\uff0c\u53c8\u6709\u56db\u4e2a\u84dd\u888d\u9053\u58eb\uff0c\u624b\u63d0\u53bb\u62c2\u8fce\u4e0a\u524d\u6765\u3002\u82cd\u7a79\u82cd\u677e\uff0c\u5411\u56db\u9053\u58eb\u4e00\u4f7f\u773c \u8272\uff0c\u9886\u8def\u5f53\u5148\uff0c\u4e0d\u4ece\u89e3\u5251\u6cc9\u524d\u8d70\u8fc7\uff0c\u5374\u53e6\u5bfb\u4e00\u6761\u5c0f\u8def\uff0c\u8f6c\u843d\u5d16\u4e0b\uff0c\u77f3\u78b4\u53c2\u5dee\uff0c\u677e\u5f71\u8ff7\u79bb\uff0c\u6e21 \u6eaa\u8d8a\u58d1\uff0c\u53e6\u5411\u4e00\u5ea7\u5cf0\u8d70\u53bb\u3002 \u3000\u3000\u539f\u6765\u6b66\u5f53\u638c\u95e8\uff0c\u53e6\u9009\u62e9\u5c55\u65d7\u5cf0\u4e0b\u7389\u771f\u4e0b\u9662\uff0c\u62db\u5f85\u5404\u65b9\u9ad8\u624b\uff0c\u65e2\u53ef\u4fdd\u6301\u7384\u771f\u89c2\u6e05\u51c0\u9762\u76ee\uff0c \u4e5f\u4f7f\u5404\u65b9\u9ad8\u624b\uff0c\u5c11\u4e86\u8bb8\u591a\u8bef\u4f1a\uff0c\u8fd9\u662f\u6b66\u5f53\u5c71\u4e2d\u8f83\u4e3a\u5e7d\u50fb\u4e4b\u5904\uff0c\u718a\u501c\u7b49\u4e00\u8def\u968f\u82cd\u7a79\u82cd\u677e\u4e8c\u9053\u884c \u6765\uff0c\u6e05\u6eaa\u5e7d\u957f\uff0c\u5947\u77f3\u73b2\u73d1\uff0c\u65e2\u4e0d\u7ecf\u89e3\u5251\u6cc9\uff0c\u6563\u82b1\u4ed9\u5b50\u4e5f\u5c31\u65e0\u4ece\u501f\u9898\u53d1\u6325\u4e86\u3002 \u3000\u3000\u7ed5\u8fc7\u4e00\u5ea7\u5cf0\u8170\uff0c\u524d\u9762\u5bf9\u5d16\u4e0a\u7eff\u6811\u5982\u4e91\uff0c\u5fae\u9732\u51fa\u4e00\u7247\u9053\u89c2\u517d\u80cc\uff0c\u82cd\u7a79\u56de\u8eab\u7b11\u8bf4\uff1a\u201c\u524d\u9762\u662f \u7389\u771f\u4e0b\u9662\uff0c\u656c\u8bf7\u5927\u4fa0\u4eec\u6b22\u805a\u6570\u65e5\uff0c\u6606\u4ed1\u5ce8\u5d4b\u4e24\u6d3e\u90fd\u5df2\u6709\u4eba\u964d\u4e34\uff0c\u7ed9\u655d\u5c71\u589e\u5149\u4e0d\u5c11\uff01\u62db\u5f85\u7b80\u6162 \u4e4b\u5904\uff0c\u5c1a\u8bf7\u6d77\u6db5\uff01\u201d \u3000\u3000\u6563\u82b1\u4ed9\u5b50\u672c\u60f3\u5728\u6b66\u5f53\u5c71\u4e0a\u95f9\u4ed6\u4e2a\u75db\u5feb\uff0c\u56db\u4eea\u5251\u5ba2\u6b3a\u4fae\u5230\u590f\u82b8\u5934\u4e0a\uff0c\u5979\u603b\u662f\u6068\u5728\u5fc3\u5934\uff0c\u5e38 \u6f2b\u5929\u5c31\u4e0d\u540c\u4e86\uff0c\u4ed6\u77e5\u9053\u590f\u82b8\u90a3\u79cd\u8f7b\u72c2\u81ea\u8d1f\uff0c\u6b66\u5f53\u6d3e\u4eba\u7684\u884c\u52a8\u4e5f\u672a\u53ef\u539a\u975e\u3002\u73b0\u5728\u6293\u4e0d\u7740\u4e00\u70b9\u9898 \u76ee\uff0c\u4f7f\u7530\u654f\u654f\u4e5f\u65e0\u4ece\u53d1\u6cc4\u3002 \u3000\u3000\u718a\u501c\u5219\u5fc3\u91cc\u60e6\u5ff5\u7740\u590f\u82b8\uff0c\u9762\u4e0a\u4ecd\u7b11\u7740\u5411\u4e1c\u65b9\u7075\u8c08\u7b11\uff0c\u7c89\u8776\u4e1c\u65b9\u82f1\u5219\u6709\u610f\u5730\u8ddf\u968f\u7740\u54e5\u54e5\u8eab \u7554\uff0c\u4e0d\u65f6\u53d1\u51fa\u94f6\u94c3\u822c\u7684\u5a07\u7b11\uff0c\u4e0e\u7530\u654f\u654f\u6328\u80a9\u4ea4\u81c2\uff0c\u7b11\u8bed\u5982\u73e0\u3002 \u3000\u3000\u82e5\u8bf4\u718a\u501c\u5bf9\u8fd9\u4e2a\u7aef\u5e84\u9753\u4e3d\u7684\u5973\u5b50\uff0c\u6beb\u4e0d\u52a8\u5fc3\uff0c\u90a3\u662f\u77eb\u60c5\u7684\u8bdd\uff0c\u4f55\u51b5\u4e1c\u65b9\u82f1\u7684\u79c0\u76ee\uff0c\u4e0d\u65f6 \u6697\u6697\u5077\u779f\u7740\u4ed6\uff01 \u3000\u3000\u7530\u654f\u654f\u5219\u4e00\u5473\u9017\u7740\u7c89\u8776\uff0c\u7adf\u542b\u7740\u65e0\u9650\u6df1\u610f\u8bf4\uff1a\u201c\u6028\u4e0d\u5f97\u4f60\u5916\u53f7\u53eb\u7c89\u8776\uff0c\u5029\u5f71\u7fe9\u7fe9\uff0c\u4f7f\u4eba \u773c\u82b1\u7f2d\u4e71\u5462\uff1f\u4f60\u6084\u6084\u544a\u8bc9\u59d0\u59d0\uff0c\u5fc3\u4e0a\u4eba\u513f\u662f\u54ea\u4e00\u4e2a\uff1f\u201d \u3000\u3000\u4e1c\u65b9\u745b\u7f9e\u751f\u53cc\u988a\uff0c\u5550\u4e86\u4e00\u53e3\u9053\uff1a\u201c\u80e1\u8bf4\uff0c\u6211\u4e0d\u8ddf\u4f60\u597d\u4e86\uff01\u201d \u3000\u3000\u7530\u654f\u654f\u53c8\u7b11\u6307\u7740\u718a\u501c\u9053\uff1a\u201c\u6211\u718a\u8001\u5f1f\u5982\u4f55\uff1b\u53ef\u4ee5\u914d\u5f97\u4e0a\u4f60\u7c89\u8776\u5427\uff01\u201d\u4e1c\u65b9\u745b\u66f4\u5a07\u7f9e\u65e0 \u8bed\uff0c\u4f46\u65e9\u5728\u56db\u5e74\u524d\u91d1\u9675\u521d\u4f1a\uff0c\u5979\u5df2\u7ecf\u82b3\u5fc3\u9ed8\u8bb8\u4e86\u8fd9\u4f4d\u6f47\u6d12\u82f1\u4fca\u7684\u5c11\u5e74\uff0c\u6b64\u65f6\u5e74\u7eaa\u8d8a\u5927\uff0c\u8d8a\u53d1 \u7a98\u5f97\u4e0d\u80fd\u62ac\u5934\u3002 \u3000\u3000\u94c1\u80c6\u5c1a\u672a\u660e\uff0c\u5219\u6df1\u6df1\u7fa1\u6155\u718a\u501c\uff0c\u7adf\u80fd\u535a\u5f97\u8bb8\u591a\u7f8e\u4eba\u5782\u9752\uff0c\u4ed6\u843d\u62d3\u6c5f\u6e56\uff0c\u8fd8\u4ece\u672a\u9047\u5230\u4e00\u4f4d \u53ef\u610f\u7684\u82f1\u96cc\u3002 \u3000\u3000\u8d8a\u8fc7\u6da7\u6eaa\uff0c\u9999\u98ce\u5439\u9001\uff0c\u5fae\u95fb\u677e\u6797\u91cc\u6709\u4e2a\u5973\u5b50\u5462\u8c03\u7b11\u8bed\uff0c\u60c5\u5f71\u53cc\u53cc\uff0c\u95ea\u51fa\u4e00\u5bf9\u513f\u4fcf\u751f\u7684\u5c11 \u5973\u3002 \u3000\u3000\u5374\u662f\u5ce8\u5d4b\u53cc\u5c0f\uff0c\u5f90\u5c0f\u5170\u548c\u8c37\u5c0f\u9759\u3002 \u3000\u3000\u5979\u4fe9\u968f\u7740\u5e08\u5085\u6d41\u4e91\u5e08\u592a\uff0c\u5e94\u9080\u6765\u6b64\u3002\u5e74\u524d\u98de\u7075\u5821\u4e00\u4f1a\uff0c\u5f90\u5c0f\u5170\u7559\u4f4f\u4e86\u534a\u6708\uff0c\u8c37\u5c0f\u9759\u5fc3\u4eea \u51fa\u5c18\u5251\u5ba2\uff0c\u504f\u504f\u5c94\u51fa\u4e2a\u6731\u82e5\u5170\uff0c\u628a\u4e1c\u65b9\u7075\u7684\u4e00\u9897\u5fc3\u5360\u636e\u4e86\uff0c\u4f7f\u5979\u767d\u767d\u62c5\u4e86\u4e00\u4efd\u5fc3\u4e8b\uff0c\u4e1c\u65b9\u7075 \u5f88\u5ba2\u6c14\u548c\u5979\u5468\u65cb\uff0c\u4f7f\u5979\u843d\u4e2a\u7a7a\u865a\u65e0\u53ef\u635e\u6478\u7684\u5883\u5730\uff0c\u4e00\u5e74\u6765\u79cb\u98ce\u6613\u901d\uff0c\u66f4\u589e\u65e0\u9650\u6101\u6005\u3002 \u3000\u3000\u5c0f\u5170\u563b\u7b11\u7740\u628a\u5979\u62d6\u51fa\u6811\u6797\u5b50\u6765\uff0c\u6084\u58f0\u9053\uff1a\u201c\u4e1c\u65b9\u5821\u4e3b\u5144\u59b9\u90fd\u6765\u4e86\uff0c\u90a3\u4e0d\u662f\u4f60\u7684\u4ed6\u4e48\uff1f\u201d \u5c0f\u9759\u4f3c\u559c\u4f3c\u55d4\uff0c\u548c\u5c0f\u5170\u4e00\u9635\u53ae\u95f9\uff0c\u800c\u718a\u501c\u4e00\u884c\u4eba\u5df2\u7fe9\u7fe9\u800c\u81f3\u3002 \u3000\u3000\u51fa\u5c18\u5251\u5ba2\u7389\u4eea\u6e05\u59ff\uff0c\u604d\u5982\u7389\u5c71\u743c\u6811\u6d8c\u73b0\u773c\u524d\uff0c\u8fd9\u4f7f\u5c0f\u9759\u9aa4\u7136\u773c\u4e2d\u4e00\u4eae\uff0c\u5fc3\u5934\u5c0f\u9e7f\u649e\u4e86\u51e0 \u4e0b\uff0c\u7565\u6709\u4e9b\u513f\u6005\u60c6\u3002 \u3000\u3000\u4ed6\u4fe9\u548c\u7c89\u8776\u81ea\u5e7c\u624b\u5e15\u8ba2\u4ea4\uff0c\u719f\u6089\u5f97\u4e00\u9f50\u8df3\u8fc7\u6765\u548c\u4e1c\u65b9\u745b\u51d1\u81f3\u4e00\u5904\uff0c\u7fa4\u96cc\u7ca5\u7ca5\uff0c\u71d5\u8bed\u9a7e \u58f0\uff0c\u55a7\u7b11\u6210\u4e00\u7247\u7eee\u8272\u3002 \u3000\u3000\u8fd9\u65f6\u6797\u4e2d\u53c9\u8f6c\u51fa\u6765\u4e00\u4f4d\u9ed1\u77cd\u8001\u5c3c\uff0c\u624b\u6276\u9521\u6756\uff0c\u5c3c\u888d\u7d20\u5c65\uff0c\u4ece\u5979\u70af\u70af\u7167\u4eba\u7684\u76ee\u5149\u91cc\uff0c\u4efb\u4f55 \u884c\u5bb6\u4e5f\u53ef\u770b\u51fa\u5979\u5185\u529f\u4e0d\u51e1\u3002\u8001\u5c3c\u65e9\u5728\u6697\u5904\u6ce8\u89c6\u4e86\u534a\u664c\u3002 \u3000\u3000\u5979\u4e0d\u5f85\u82cd\u7a79\u82cd\u677e\u66ff\u5979\u5411\u8fd9\u51e0\u4f4d\u5e74\u8f7b\u7684\u8c6a\u6770\u4ecb\u7ecd\uff0c\u4e00\u4e2a\u7bad\u6b65\u5411\u718a\u501c\u8eab\u7554\u7eb5\u6765\uff0c\u82cd\u52b2\u7684\u58f0\u8c03 \u5927\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u672c\u6d3e\u9547\u5c71\u795e\u5251\uff0c\u7adf\u88ab\u4f60\u76d7\u53bb\uff1a\u201c\u8001\u5c3c\u8fd9\u53e5\u8bdd\uff0c\u4e0d\u4f46\u4f7f\u718a\u501c\u6478\u4e0d\u7740\u5934\u8111\uff0c \u6563\u82b1\u4ed9\u5b50\u592b\u5987\u4e5f\u6123\u4f4f\u4e86\uff0c\u53ea\u94c1\u80c6\u5c1a\u672a\u660e\u77e5\u9053\u718a\u501c\u8fd9\u53e3\u5251\u7684\u6765\u6e90\u3002\u8001\u5c3c\u4e0a\u4e58\u8eab\u6cd5\uff0c\u8f7b\u5982\u4e00\u7f15\u98de \u7d6e\uff0c\u95ea\u95ea\u800c\u6765\uff0c\u5de6\u624b\u5411\u718a\u501c\u80cc\u4e0a\u53e4\u5251\u6293\u53bb\uff0c\u624b\u6cd5\u4e4b\u5feb\uff0c\u4f7f\u4eba\u76ee\u7729\u795e\u79fb\u3002\u540c\u65f6\u5979\u53c8\u53f1\u9053\uff1a\u201c\u8001 \u8eab\u5148\u6536\u56de\u795e\u5251\uff0c\u518d\u4ece\u8f7b\u5904\u6cbb\u4f60\u8fd9\u80c6\u5927\u5305\u5929\u7684\u5c0f\u5b50\uff01\u201d \u3000\u3000\u4e8b\u51fa\u610f\u5916\uff0c\u718a\u501c\u4e07\u60f3\u4e0d\u5230\u5979\u4f1a\u98de\u6765\u593a\u5251\uff0c\u800c\u4e14\u53e3\u53e3\u58f0\u58f0\u8ba4\u5b9a\u662f\u5077\u4e86\u5979\u7684\u9547\u5c71\u795e\u5251\uff0c\u8fd9\u771f \u4f7f\u4ed6\u557c\u7b11\u7686\u975e\u3002 \u3000\u3000\u718a\u501c\u6765\u4e0d\u53ca\u8fa9\u9a73\u5979\uff0c\u5fd9\u65bd\u5c55\u201c\u6f5c\u5f62\u9041\u5f71\u201d\u8f7b\u529f\uff0c\u6643\u8eab\u98de\u4e86\u4e00\u4e08\u591a\u8fdc\uff0c\u4ed6\u53cc\u8db3\u5c1a\u672a\u6cbe\u5730\uff0c \u8001\u5c3c\u53c8\u65cb\u8dc3\u6251\u672a\u3002 \u3000\u3000\u51fa\u5c18\u5251\u5ba2\u8ba4\u5f97\u5979\u662f\u5ce8\u5d4b\u53cc\u5c0f\u4e4b\u5e08\u6d41\u4e91\u5e08\u592a\uff0c\u6025\u6025\u5730\u53eb\u9053\uff1a\u201c\u6d41\u4e91\u5e08\u592a\uff0c\u8bf7\u6682\u4e14\u606f\u6012\uff0c\u4e0d \u8981\u8ba4\u9519\u4e86\u5b9d\u5251\uff01\u201d \u3000\u3000\u4e1c\u65b9\u745b\u5219\u66ff\u718a\u501c\u634f\u4e86\u4e00\u628a\u6c57\uff0c\u6d41\u4e91\u5e08\u592a\u4ee5\u6d41\u4e91\u98de\u8896\u529f\u5a01\u9707\u897f\u5357\u5404\u7701\uff0c\u6570\u5341\u5e74\u82e6\u884c\u4fee\u70bc\u51fa \u6765\u7684\u5185\u529f\uff0c\u7a0d\u4e00\u4e0d\u614e\uff0c\u718a\u501c\u5c82\u4e0d\u5403\u4e8f\uff1f\u5979\u4e5f\u6025\u5f97\u5c16\u53eb\u9053\uff1a\u201c\u6d41\u4e91\u5e08\u592a\uff01\u4e8b\u60c5\u8fd8\u6ca1\u6709\u5f04\u6e05\u695a\uff0c \u81ea\u5df1\u4eba\u4e0d\u53ef\u51b2\u7a81\uff01\u201d \u3000\u3000\u94c1\u80c6\u5c1a\u672a\u660e\u662f\u51b7\u51b7\u4e00\u7b11\uff0c\u5389\u58f0\u9053\uff1a\u201c\u8001\u79c3\u5a46\uff01\u4f60\u4e5f\u6709\u4e00\u53e3\u7834\u94dc\u5e9f\u94c1\u4e48\uff1f\u4f60\u4ed4\u7ec6\u770b\u770b\uff0c\u662f \u4e0d\u662f\u4f60\u90a3\u4ef6\u7834\u5bb6\u4f19\uff01\u201d \u3000\u3000\u718a\u501c\u5df2\u88ab\u8001\u5c3c\u903c\u5f97\u95ea\u7eb5\u4e86\u4e09\u6b21\uff0c\u8001\u5c3c\u4e0d\u7531\u54a6\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u679c\u7136\u6709\u4e24\u624b\uff0c\u5426\u5219\u4f60 \u4e5f\u4e0d\u80fd\u81ea\u5ce8\u5d4b\u65ad\u4e91\u5d16\u5077\u5230\u8fd9\u53e3\u795e\u5251\uff01\u5c0f\u5b50\u4f60\u518d\u4e0d\u5c06\u5b9d\u5251\u53cc\u624b\u732e\u4e0a\uff0c\u8001\u8eab\u53ef\u8981\u5f00\u4e09\u5341\u5e74\u672a\u52a8\u7684 \u6740\u6212\u4e86\uff01\u201d \u3000\u3000\u5979\u8fd9\u4e48\u4e00\u8bf4\u8bdd\u7684\u7a7a\u513f\uff0c\u4e1c\u65b9\u82f1\u5df2\u594b\u4e0d\u987e\u8eab\uff0c\u98de\u8dc3\u8fc7\u53bb\u62e6\u4f4f\u4e86\u5979\uff0c\u800c\u4f17\u4eba\u4e5f\u90fd\u4e00\u9f50\u56f4\u62e2\uff0c \u82cd\u7a79\u82cd\u677e\u5fd9\u4e0d\u8fed\u4ece\u4e2d\u8c03\u89e3\u3002 \u3000\u3000\u718a\u501c\u6602\u7136\u800c\u7acb\uff0c\u795e\u6001\u60a0\u95f2\uff0c\u7528\u4e0d\u4f7f\u5979\u592a\u96be\u582a\u7684\u8bed\u6c14\u8bf4\uff1a\u201c\u8001\u5c3c\u59d1\u4e0d\u8981\u80e1\u8bf4\uff0c\u5728\u4e0b\u718a\u501c\uff0c \u4ece\u672a\u5c65\u8db3\u5ce8\u5d4b\uff01\u6b64\u5251\u4e43\u6b66\u660c\u4e00\u4f4d\u670b\u53cb\u6240\u8d60\uff0c\u53e6\u6709\u5bb6\u5e08\u6240\u8d50\u501a\u706d\u5251\uff0c\u81f3\u4eca\u8fd8\u88ab\u4eba\u76d7\u53bb\uff0c\u6ca1\u67e5\u8bbf \u56de\u6765\uff01\u201d \u3000\u3000\u718a\u501c\u5fc3\u4e8b\u4e2d\uff0c\u6700\u91cd\u8981\u800c\u68d8\u624b\u7684\uff0c\u8fd8\u662f\u6bd2\u5fc3\u795e\u9b54\u7ed9\u4ed6\u4e00\u5e74\u9650\u671f\uff0c\u8bbe\u6cd5\u627e\u56de\u6765\u501a\u5929\u5251\u8fd9\u4e00\u6869 \u4e8b\u3002 \u3000\u3000\u718a\u501c\u8bed\u6c14\u4e2d\uff0c\u591a\u534a\u5e26\u7740\u4e9b\u6c14\u6124\uff0c\u5947\u602a\u7684\u662f\u8fd9\u4f4d\u6d41\u4e91\u5e08\u592a\uff0c\u7adf\u607c\u7f9e\u6210\u6012\uff0c\u63a8\u5f00\u56f4\u7ed5\u5728\u5979\u8eab \u7554\u7684\u4e8c\u5f92\u5c0f\u5170\u5c0f\u9759\u548c\u4e1c\u65b9\u745b\uff0c\u4e00\u6325\u957f\u8896\uff0c\u4e00\u80a1\u5185\u5bb6\u6f5c\u529b\uff0c\u7834\u7a7a\u547c\u5578\uff0c\u5411\u718a\u501c\u5377\u53bb\u3002\u5979\u6012\u559d \u9053\uff1a\u201c\u80e1\u8bf4\uff01\u59d3\u718a\u7684\u5c0f\u5b50\uff0c\u4f60\u662f\u5929\u9634\u6559\u4e0b\u7684\u89d2\u8272\u4e48\uff1f\u201d \u3000\u3000\u718a\u501c\u5929\u96f7\u884c\u529f\uff0c\u5df2\u81f3\u7089\u706b\u7eaf\u9752\u7684\u5730\u6b65\uff0c\u53c8\u5f97\u4e86\u98d8\u7136\u8001\u4eba\u7684\u7cbe\u9ad3\uff0c\u5185\u529f\u706b\u5019\u4e5f\u6781\u6df1\uff0c\u5fd9\u8fd0 \u5185\u529f\u62a4\u4f53\uff0c\u4e5f\u6325\u624b\u76f8\u6297\u3002 \u3000\u3000\u4e24\u4eba\u76f8\u8ddd\u7ea6\u4e03\u516b\u5c3a\u8fdc\uff0c\u8f70\u7136\u4e00\u58f0\u75be\u98ce\u9707\u54cd\uff0c\u718a\u501c\u521d\u6b21\u4f7f\u51fa\u672c\u8eab\u5185\u529f\u6f5c\u529b\uff0c\u548c\u5979\u76f8\u6297\uff0c\u53ea \u89c9\u5982\u540c\u649e\u4e0a\u4e86\u94dc\u5899\u94c1\u58c1\uff0c\u9707\u5f39\u4e4b\u529b\uff0c\u4f7f\u4ed6\u4e00\u76f4\u8eab\u4f53\u6447\u6447\u6643\u6643\u6536\u6869\u4e0d\u4f4f\uff0c\u8eab\u4f53\u81ea\u7136\u5012\u9000\u4e86\u51e0 \u6b65\u3002 \u3000\u3000\u800c\u8fd9\u4f4d\u6d41\u4e91\u5e08\u592a\u5462\uff1f\u4e5f\u53d7\u5230\u4e86\u540c\u6837\u7684\u9707\u529b\uff0c\u8e09\u8dc4\u5012\u9000\uff0c\u8fd9\u4f7f\u6d41\u4e91\u5e08\u592a\u77a0\u76ee\u7ed9\u820c\u4e0d\u5df2\uff0c\u5bf9 \u4e8e\u718a\u501c\u611f\u89c9\u65e0\u9650\u60ca\u5947\u3002 \u3000\u3000\u82cd\u7a79\u82cd\u677e\u505a\u4e3b\u4eba\u7684\uff0c\u53ea\u6015\u8fd9\u51b2\u7a81\u6269\u5927\u5f97\u4e0d\u53ef\u6536\u62fe\uff0c\u614c\u5fd9\u4e0a\u524d\u62e6\u529d\u53cc\u65b9\u4f4f\u624b\u3002 \u3000\u3000\u4f17\u4eba\u89c1\u6d41\u4e91\u5e08\u592a\u98de\u8896\u795e\u529f\uff0c\u7adf\u4e0d\u80fd\u4f24\u53ca\u718a\u501c\u4e00\u6beb\u4e00\u53d1\uff0c\u90fd\u5341\u5206\u60ca\u5947\u718a\u501c\uff0c\u5185\u529f\u9020\u8be3\u7684\u7a0b \u5ea6\uff0c\u5df2\u81fb\u4e0a\u4e58\u3002 \u3000\u3000\u6563\u82b1\u4ed9\u5b50\u592b\u5987\uff0c\u5219\u4e0d\u4e3a\u8fd9\u4e2a\u573a\u9762\u611f\u5230\u51fa\u5947\uff0c\u4ed6\u4fe9\u662f\u8bd5\u8fc7\u718a\u501c\u672c\u9886\u7684\uff0c\u53ea\u4e0d\u89e3\u4f55\u4ee5\u8001\u5c3c\u8981 \u786c\u8bec\u718a\u501c\u5077\u5979\u7684\u5251\uff1f \u3000\u3000\u8001\u5c3c\u53c8\u903c\u95ee\u718a\u501c\u662f\u5426\u5929\u9634\u6559\u4e0b\uff0c\u7530\u654f\u654f\u548c\u5c1a\u672a\u660e\u90fd\u89c9\u5f97\u8fd9\u662f\u8ff9\u8fd1\u4fae\u8fb1\u7684\u8bdd\uff0c\u5c1a\u672a\u660e\u51b7\u7b11 \u9053\uff1a\u201c\u82cd\u7a79\u9053\u5144\uff0c\u8ba9\u5979\u628a\u8bdd\u8bf4\u6e05\u695a\u70b9\uff0c\u5979\u5ce8\u5d4b\u6d3e\u6709\u4ec0\u4e48\u9547\u5c71\u795e\u5251\uff0c\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u65e0\u7406\u53d6\u95f9\uff0c \u8fd8\u8981\u683d\u8bec\u718a\u5927\u54e5\u662f\u5929\u9634\u6559\u4eba\uff01\u8fd9\u771f\u662f\u4ece\u4f55\u8bf4\u8d77\uff01\u8bdd\u4e0d\u8bf4\u660e\u767d\uff0c\u4eca\u513f\u5979\u8fd9\u4e00\u756a\u72c2\u5984\u7684\u4e3e\u52a8\uff0c\u5c1a \u67d0\u662f\u770b\u4e0d\u4e0b\u53bb\u7684\uff01\u201d\u3002 \u3000\u3000\u6563\u82b1\u4ed9\u5b50\u4e5f\u5fff\u5fff\u9053\uff1a\u201c\u8001\u79c3\u5a46\u501a\u8001\u5356\u8001\uff0c\u5c31\u7b97\u4f60\u6709\u4e00\u53e3\u5251\uff0c\u4eba\u5bb6\u5c31\u4e0d\u8bb8\u6709\u540c\u4e2a\u5f0f\u6837\u7684\u5b9d \u5251\u4e48\uff1f\u201d \u3000\u3000\u6d41\u4e91\u5e08\u592a\u56e0\u4e3a\u718a\u501c\u80cc\u4e0a\u7684\u5251\uff0c\u786e\u5b9e\u662f\u592a\u76f8\u4f3c\uff0c\u62ff\u5728\u624b\u91cc\u4e5f\u672a\u5fc5\u80fd\u7acb\u523b\u5206\u8fa8\u51fa\u6765\uff0c\u800c\u5979\u5929 \u751f\u71e5\u70c8\u7684\u6027\u5b50\uff0c\u662f\u4e0d\u80fd\u5fcd\u8010\u4e00\u523b\u7684\uff0c\u6240\u4ee5\u624d\u95f9\u51fa\u8fd9\u4e2a\u573a\u9762\uff0c\u7ecf\u4f17\u4eba\u529d\u89e3\uff0c\u53c8\u5728\u4e8c\u4eba\u8ba5\u8bbd\u65a5\u8d23 \u4e4b\u4e0b\uff0c\u624d\u4f3c\u611f\u81ea\u5df1\u8fc7\u4e8e\u6027\u6025\u3002 \u3000\u3000\u6d41\u4e91\u5e08\u5927\u5fff\u6012\u9053\uff1a\u201c\u672c\u6d3e\u638c\u95e8\u6b8b\u4e91\u5c0a\u8005\uff0c\u65b0\u8fd1\u81ea\u5929\u9634\u6559\u4e2d\u593a\u6765\u7684\u4e00\u53e3\u795e\u5251\uff0c\u4e43\u662f\u4e09\u5341\u5e74 \u524d\u6b66\u6797\u9a70\u540d\u7684\u501a\u5929\u5251\uff01\u201d \u3000\u3000\u5979\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u5b8c\uff0c\u5df2\u8db3\u4f7f\u718a\u501c\u60ca\u559c\u4e07\u5206\u4e86\uff01\u8fd9\u4e00\u6765\u6bd2\u5fc3\u795e\u9b54\u7559\u7ed9\u4ed6\u7684\u96be\u9898\uff0c\u603b\u7b97\u6709\u4e2a\u7740 \u843d\uff0c\u7cbe\u795e\u4e3a\u4e4b\u4e00\u632f\u3002 \u3000\u3000\u5c1a\u672a\u660e\u542c\u8bf4\u8fc7\u718a\u501c\u5931\u4e86\u501a\u5929\u5251\uff0c\u5fc3\u60f3\uff1a\u201c\u539f\u6765\u662f\u5ce8\u5d4b\u6d3e\u4eba\u53c8\u4ece\u5929\u9634\u6559\u5077\u53bb\u6b64\u5251\uff0c\u4f60\u8fd8\u5411 \u4eba\u5bb6\u7d22\u5251\uff0c\u53ea\u6015\u8bf4\u660e\u4ee5\u540e\uff0c\u4f60\u8fd9\u8d3c\u8d43\u4e5f\u4fdd\u4e0d\u4f4f\u5462\uff01\u201d \u3000\u3000\u6d41\u4e91\u5e08\u592a\u53c8\u6307\u7740\u8bf4\uff1a\u201c\u8fd9\u4f4d\u670b\u53cb\u80cc\u4e0a\u7684\u5251\uff0c\u786e\u5b9e\u592a\u76f8\u50cf\u4e86\u2026\u2026\u201d\u5979\u6b63\u5728\u81ea\u5706\u5176\u8bf4\uff0c\u4f17\u4eba \u591a\u534a\u4e0d\u660e\u539f\u59d4\u3002 \u3000\u3000\u7a81\u7136\u95f4\u82cd\u52b2\u7b11\u58f0\u5927\u4f5c\uff0c\u81ea\u78a7\u5d16\u4e0a\u65b9\u7684\u6797\u4e2d\uff0c\u95ea\u98de\u51fa\u6765\u4e24\u4f4d\u4e94\u5341\u5de6\u53f3\u7684\u5947\u9038\u4eba\u6765\uff0c\u5de6\u8fb9\u9ec4 \u8863\u9ec4\u51a0\u7684\u7b11\u8bf4\uff1a\u201c\u672c\u6d3e\u7684\u795e\u7269\uff0c\u8fd9\u53ef\u4e00\u9f50\u6709\u4e86\u7740\u843d\u4e86\uff01\u539f\u6765\u6d41\u4e91\u79c3\u5a46\u540c\u95e8\u4eba\uff0c\u4e5f\u4e0d\u8fc7\u662f\u9e21\u9e23 \u72d7\u76d7\u4e4b\u6d41\uff01\u771f\u8be5\u6309\u5f8b\u95ee\u7f6a\u5462\uff01\u201d \u3000\u3000\u5de6\u8fb9\u9614\u8896\u84dd\u886b\u7684\u4e5f\u7b11\u8bf4\uff1a\u201c\u8d2f\u65e5\u5251\u600e\u4f1a\u843d\u5728\u8fd9\u59d3\u718a\u7684\u624b\u4e2d\uff1f\u800c\u4e14\u501a\u5929\u5251\u548c\u4ed6\u8fd8\u6709\u4ec0\u4e48\u5173 \u7cfb\uff0c\u771f\u662f\u8d39\u89e3\uff01\u201d \u3000\u3000\u8fd9\u4e24\u4f4d\u4e43\u662f\u6606\u4ed1\u6d3e\u94c1\u5251\u5148\u751f\u95e8\u5f92\uff0c\u585e\u5916\u611a\u592b\u5c27\u6743\u4e0e\u5e08\u5f1f\u7b11\u5929\u81fe\u65b9\u89c9\u3002\u94c1\u5251\u5148\u751f\u5f53\u5e74\u4e0e\u5e08 \u5f1f\u94dc\u5251\u4e66\u751f\u5408\u7528\u501a\u5929\u8d2f\u65e5\u53cc\u5251\uff0c\u626b\u8361\u5929\u9634\u6559\uff0c\u624b\u8bdb\u82cd\u865a\u4e0a\u4eba\uff0c\u800c\u4ed6\u81ea\u5df1\u4e5f\u91cd\u4f24\u5728\u592a\u884c\u5c71\u4e0b\uff0c \u94dc\u5251\u4e66\u751f\u8fdc\u6e38\u6c5f\u5357\uff0c\u4eba\u5251\u4ff1\u4e0d\u77e5\u4e0b\u843d\u3002 \u3000\u3000\u6bd2\u5fc3\u795e\u9b54\u5728\u90a3\u91cc\u4e5f\u7ad9\u5728\u6b63\u6d3e\u8fd9\u4e00\u9762\uff0c\u4ed6\u53bb\u5f97\u8f83\u665a\uff0c\u592a\u884c\u5c71\u4e0b\u5929\u9634\u6559\u5de2\u7a74\u4e2d\uff0c\u5c38\u6a2a\u904d\u5730\uff0c \u4ed6\u5374\u53d1\u73b0\u4e86\u8fd9\u53e3\u501a\u5929\u5251\uff0c\u540d\u5251\u5c82\u80fd\u65e0\u4e3b\uff0c\u800c\u5f53\u65f6\u6b66\u6797\uff0c\u4ee5\u6606\u4ed1\u6d3e\u529b\u91cf\u6700\u4e3a\u96c4\u539a\uff0c\u7ecf\u8fc7\u592a\u884c\u4e00 \u5f79\uff0c\u540d\u624b\u6b7b\u4f24\u7d2f\u7d2f\uff0c\u5374\u6781\u5c11\u51fa\u73b0\u4e86\u3002 \u3000\u3000\u5c27\u6743\u548c\u65b9\u89c9\u5f53\u5e74\u5e78\u514d\u4e8e\u96be\uff0c\u9690\u5c45\u4e1c\u6606\u4ed1\uff0c\u6f5c\u4fee\u672c\u95e8\u5185\u529f\uff0c\u56e0\u95fb\u5929\u9634\u6559\u518d\u5ea6\u5174\u8d77\uff0c\u624d\u51fa\u73b0 \u4e2d\u539f\uff0c\u65e0\u610f\u4e2d\u4e0e\u98de\u9e64\u5b50\u76f8\u9047\uff0c\u9042\u6566\u8bf7\u8fd9\u4e24\u4f4d\u6606\u4ed1\u4ec5\u5b58\u7684\u7855\u679c\uff0c\u524d\u6765\u5171\u5546\u5927\u8ba1\u3002\u5ce8\u5d4b\u6d41\u4e91\u5e08\u592a \u5e08\u5f92\uff0c\u4e5f\u662f\u6b66\u5f53\u6d3e\u793c\u8058\u6765\u7684\u3002 \u3000\u3000\u4e94\u5927\u6b63\u6d3e\u4e4b\u5916\u7684\u6c5f\u6e56\u8c6a\u6770\u6709\u5934\u6709\u8138\u7684\uff0c\u6b66\u5f53\u6d3e\u65e0\u4e0d\u6d3e\u4eba\u9001\u5e16\u5b50\u9080\u6765\u52a9\u5a01\uff0c\u4f46\u662f\u5404\u65b9\u8c6a \u6770\uff0c\u5df2\u5927\u591a\u6570\u88ab\u5929\u9634\u6559\u4eba\u5a01\u903c\u5229\u8bf1\uff0c\u6536\u7f57\u5728\u6559\u4e0b\uff0c\u5c11\u6570\u6b63\u6d3e\u7684\u4eba\uff0c\u53ea\u6709\u57cb\u5934\u4e0d\u51fa\uff0c\u6d01\u8eab\u81ea \u7231\uff0c\u56db\u5e74\u6765\u6b66\u6797\u5f62\u5f71\u4e3a\u4e4b\u5927\u53d8\u3002 \u3000\u3000\u5e08\u95e8\u65e7\u7269\uff0c\u585e\u5916\u611a\u592b\u4fe9\u600e\u4e0d\u8ba4\u8bc6\uff0c\u501a\u5929\u8d2f\u65e5\u53cc\u5251\uff0c\u6b63\u662f\u4ed6\u4fe9\u4e45\u60f3\u8bbf\u5bfb\u6536\u56de\u4e4b\u7269\u3002\u6d41\u4e91\u5e08 \u592a\u51b2\u53e3\u8bf4\u51fa\u501a\u5929\u5251\u4e0b\u843d\uff0c\u7adf\u56e0\u6b64\u5728\u6b66\u6797\u6b63\u6d3e\u95f4\u917f\u6210\u4e86\u83ab\u5927\u7684\u7ea0\u7eb7\uff0c\u6606\u4ed1\u8fd9\u4e24\u4f4d\u9ad8\u624b\u73b0\u8eab\u51fa \u6765\uff0c\u6d41\u4e91\u5e08\u592a\u662f\u8ba4\u8bc6\u7684\uff0c\u4ed6\u4eec\u4fe9\u90fd\u5df2\u6765\u7389\u771f\u89c2\u4e09\u65e5\uff0c\u5f7c\u6b64\u5404\u6000\u503e\u6155\u4e4b\u5fc3\u3002 \u3000\u3000\u585e\u5916\u611a\u592b\u8fd9\u65f6\u5a01\u4eea\u693d\u693d\uff0c\u773c\u795e\u4e00\u626b\u7531\u5c71\u4e0b\u65b0\u6765\u7684\u51e0\u4f4d\uff0c\u6606\u4ed1\u53cc\u6770\u6700\u60ca\u8bb6\u7684\u662f\u5e38\u6f2b\u5929\u592b\u5987 \u91cd\u73b0\u6c5f\u6e56\u3002 \u3000\u3000\u4e8c\u5341\u5e74\u524d\u70b9\u82cd\u6d3e\u7684\u738b\u9762\u795e\u5251\uff0c\u786e\u9707\u6151\u4e86\u672c\u6d3e\u96c4\u82f1\uff0c\u4e5f\u4f7f\u5404\u6d3e\u4e3a\u4e4b\u4fa7\u76ee\u3002\u65b0\u81ea\u5c71\u4e0b\u7684\u516d\u4f4d \u4e2d\uff0c\u4ed6\u4fe9\u53ea\u8bc6\u5e38\u6f2b\u5929\u592b\u5987\u4e8c\u4eba\uff0c\u5176\u4f59\u90fd\u5f88\u964c\u751f\uff0c\u718a\u501c\u7684\u59d3\u540d\uff0c\u662f\u81ea\u8001\u5c3c\u548c\u4ed6\u7684\u95ee\u7b54\u65f6\u624d\u542c\u51fa \u6765\u7684\uff0c\u5bf9\u718a\u501c\u4e5f\u7d20\u4e0d\u76f8\u8bc6\u3002 \u3000\u3000\u540c\u6837\u7389\u9762\u795e\u5251\u592b\u5987\uff0c\u4e5f\u56e0\u8fd9\u6606\u4ed1\u6d3e\u4e24\u4e2a\u8fc7\u53bb\u7684\u5947\u6770\uff0c\u51fa\u73b0\u5728\u6b66\u5f53\u5c71\u4e2d\uff0c\u800c\u611f\u5230\u4e86\u975e\u5e38\u60ca \u5f02\u3002 \u3000\u3000\u56db\u4f4d\u672c\u6765\u76f8\u8bc6\u7684\u4eba\uff0c\u53cd\u800c\u5404\u5404\u4ea4\u6362\u4e86\u56db\u9053\u60ca\u5f02\u7684\u76ee\u5149\uff0c\u4e95\u672a\u7acb\u5373\u5bd2\u6684\u5ba2\u5957\u3002 \u3000\u3000\u6d41\u4e91\u8001\u5c3c\u5374\u4e3a\u6606\u4ed1\u53cc\u6770\u4e00\u642d\u4e00\u6321\u90a3\u51e0\u53e5\u8bdd\uff0c\u611f\u5230\u4e86\u5f02\u5e38\u7684\u4e0d\u5b89\uff0c\u5979\u662f\u660e\u767d\u501a\u5929\u5251\u539f\u6765\u7684 \u4e3b\u4eba\u7fc1\u662f\u8c01\u7684\u3002 \u3000\u3000\u6d41\u4e91\u8001\u5c3c\u4ee5\u5ce8\u5e3d\u8001\u8f88\u8eab\u5206\uff0c\u521a\u624d\u9519\u8ba4\u718a\u501c\u62ff\u8d70\u5ce8\u5e3d\u6d3e\u4eba\u788d\u81ea\u5929\u9634\u6559\u7684\u5b87\u5185\u540d\u5251\uff0c\u5e08\u51fa\u65e0 \u540d\uff0c\u53cd\u800c\u718a\u501c\u7adf\u662f\u501a\u5929\u5251\u7684\u540e\u6765\u6240\u6709\u4eba\uff0c\u867d\u4e0d\u4f1a\u4fbf\u5b9c\u4e86\u718a\u501c\uff0c\u4f46\u662f\u7ec8\u5fc5\u5f15\u8d77\u573a\u4e0d\u5927\u4e0d\u5c0f\u7684\u7ea0 \u7eb7\uff0c\u770b\u6765\u53cd\u800c\u591a\u6b64\u4e00\u4e3e\u4e86\u3002 \u3000\u3000\u82cd\u7a79\u82cd\u677e\uff0c\u5219\u4ee5\u4e3b\u4eba\u7684\u8eab\u5206\uff0c\u5411\u53cc\u65b9\u9042\u4e00\u4ecb\u7ecd\u8bf4\uff1a\u201c\u8fd9\u4f4d\u662f\u70b9\u82cd\u638c\u95e8\u7389\u9762\u795e\u5251\u5e38\u6f2b\u5929\uff0c \u6563\u82b1\u4ed9\u5b50\u7530\u59d1\u5a18\uff0c\u8a89\u6ee1\u6c5f\u5357\u98d8\u7136\u8001\u4eba\u7684\u9ad8\u8db3\u718a\u501c\uff0c\u4e24\u6cb3\u603b\u74e2\u628a\u5b50\u94c1\u80c6\u5c1a\u672a\u660e\uff0c\u5357\u5317\u53cc\u7edd\u5251\u51fa \u5c18\u5251\u5ba2\u4e1c\u65b9\u7075\uff0c\u4e1c\u65b9\u59d1\u5a18\u5144\u59b9\u2026\u2026\u201d\u4e8c\u9053\u58eb\u6ed4\u6ed4\u4e0d\u7edd\uff0c\u5982\u6570\u5bb6\u73cd\u3002 \u3000\u3000\u81ea\u7136\u4e0d\u591a\u4e0d\u5c11\uff0c\u5374\u4f7f\u6d41\u4e91\u5e08\u592a\u53d7\u5230\u4e9b\u9707\u60ca\u3002 \u3000\u3000\u602a\u4e0d\u5f97\u8fd9\u56db\u4f4d\u5c11\u5e74\uff0c\u6001\u5ea6\u72c2\u50b2\uff0c\u5012\u4e5f\u7b97\u662f\u65b0\u8fd1\u5d1b\u8d77\u6b66\u6751\u540d\u5b57\u54cd\u5f53\u5f53\u7684\u4eba\u7269\u5440\uff01\u6606\u4ed1\u53cc \u6770\uff0c\u4e5f\u5fae\u6709\u6240\u95fb\u3002 \u3000\u3000\u585e\u5916\u611a\u592b\u4e0d\u8010\u70e6\u7531\u82cd\u7a79\u9053\u58eb\u4ee3\u4ed6\u4eec\u4ecb\u7ecd\uff0c\u5148\u81ea\u63a5\u53e3\u9053\uff1a\u201c\u5728\u4e0b\u6606\u4ed1\u5c27\u6743\uff0c\u4e0e\u611a\u5e08\u5f1f\u7b11\u5929 \u81fe\u65b9\u89c9\u3002\u201d \u3000\u3000\u7d27\u63a5\u7740\u5411\u718a\u501c\u80cc\u4e0a\u8d2f\u65e5\u5251\u6ce8\u89c6\u4e86\u51e0\u773c\uff0c\u53f9\u606f\u9053\uff1a\u201c\u718a\u5c11\u4fa0\u8fd9\u53e3\u540d\u5251\uff0c\u5f97\u81ea\u4f55\u4eba\uff1f\u201d \u3000\u3000\u718a\u501c\u51b7\u9759\u7684\u6001\u5ea6\uff0c\u660e\u77e5\u9053\u4f60\u4eec\u5fc5\u4e0e\u501a\u5929\u8d2f\u65e5\u53cc\u5251\uff0c\u6781\u6709\u5173\u8fde\uff0c\u5374\u4ecd\u795e\u8272\u5937\u7136\uff0c\u8bf4\u660e\u4e86\u53d7 \u4eba\u8d60\u5251\u7684\u7ecf\u8fc7\uff0c\u66f4\u723d\u5feb\u5730\u628a\u6bd2\u5fc3\u795e\u9b54\u6570\u5e74\u524d\u8d60\u5251\uff0c\u82cf\u5dde\u5e9c\u65e0\u5fc3\u5931\u5251\u79cd\u79cd\u90fd\u8bf4\u660e\uff0c\u603b\u4e4b\u4ed6\u662f\u548c \u76d8\u6258\u51fa\uff0c\u76f4\u8a00\u65e0\u9690\u3002 \u3000\u3000\u6700\u540e\u718a\u501c\u53c8\u8865\u5145\u4e86\u4e00\u53e5\u8bdd\uff1a\u201c\u5c27\u8001\u5148\u751f\u6709\u4f55\u8d60\u6559\uff1f\u6211\u786e\u4e0d\u77e5\u6bd2\u5fc3\u795e\u9b54\u91cd\u89c6\u501a\u5929\u5251\u91cd\u4e8e\u751f \u547d\u7684\u7406\u7531\u4f55\u5728\uff1f\u201d \u3000\u3000\u7b11\u5929\u53f2\u5934\u8138\u4ef0\u5929\uff0c\u7eb5\u58f0\u5927\u7b11\uff0c\u58f0\u51fa\u4e39\u7530\uff0c\u54cd\u9707\u6797\u8d8a\uff1a\u4f7f\u6563\u82b1\u4ed9\u5b50\u548c\u5c1a\u672a\u660e\uff0c\u90fd\u89c9\u5f97\u4ed6\u7b11 \u5f97\u5341\u5206\u5730\u7a81\u5140\u3002 \u3000\u3000\u7b11\u5929\u53df\u8fd9\u79cd\u5947\u5f02\u7684\u72c2\u7b11\u59ff\u52bf\uff0c\u662f\u4ed6\u4e00\u751f\u602a\u7656\u4e4b\u4e00\u3002 \u3000\u3000\u7b11\u58f0\u65b9\u7f62\u3002\u4ed6\u53c8\u4ee5\u5f88\u6c89\u91cd\u7684\u8bed\u8c03\u8bf4\uff1a\u201c\u90a3\u4e48\u4faf\u751f\u8001\u5bb6\u4f19\u7684\u4f7f\u547d\uff0c\u6211\u5f1f\u5144\u4eec\u53ef\u66ff\u4f60\u6211\u56de\u8fd9 \u53e3\u501a\u5929\u5251\uff0c\u8ba9\u4f60\u6709\u8bdd\u5411\u4ed6\u4ea4\u5f85\uff01\u718a\u5c0f\u4fa0\u7f18\u5206\u4e0d\u6d45\uff0c\u7adf\u4f5c\u4e86\u672c\u6d3e\u5148\u5e08\u9057\u7269\u501a\u5929\u8d2f\u65e5\u53cc\u5251\u7684\u4e00\u5ea6 \u4e3b\u4eba\uff01\u201d \u3000\u3000\u8fd9\u8bdd\u91cc\u542b\u4e49\uff0c\u81ea\u4e0d\u7528\u8bf4\uff0c\u4ed6\u4e8c\u4f4d\u8981\u6536\u56de\u501a\u5929\u8d2f\u65e5\u5251\u5462\uff0c\u5219\u8bed\u610f\u8fd8\u4e0d\u5341\u5206\u660e\u6717\uff0c\u4f46\u4e5f\u8db3\u4f7f \u718a\u501c\u4e3a\u4e4b\u8272\u53d8\u4e86\u3002 \u3000\u3000\u6d41\u4e91\u8001\u5c3c\u9762\u5bf9\u7740\u8fd9\u79cd\u5c34\u5c2c\u5c40\u52bf\uff0c\u6fc0\u6012\u4e86\u5979\uff0c\u4e5f\u4f3c\u51b2\u72af\u4e86\u5ce8\u5d4b\u7684\u4e00\u6d3e\u5c0a\u4e25\uff0c\u5979\u5fcd\u4e0d\u4f4f\u5148\u633a \u8eab\u51fa\u6765\uff0c\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u6606\u4ed1\u53cc\u65b9\uff1a\u501a\u5929\u5251\u51fa\u4e8e\u4f55\u4eba\u94f8\u9020\uff0c\u8f97\u8f6c\u7ecf\u8fc7\u4f55\u4eba\u4e4b\u624b\uff0c\u8fd9\u90fd\u662f\u8fc7\u53bb \u4e00\u6bb5\u9648\u8ff9\uff0c\u53ea\u602a\u81ea\u5df1\u4e0d\u8096\uff0c\u628a\u4e1c\u897f\u4e22\u6389\uff0c\u4e0d\u80fd\u628a\u5408\u6cd5\u7684\u5f97\u4e3b\uff0c\u5e94\u4eab\u7684\u6743\u5229\u62b9\u715e\uff0c\u6539\u671d\u6362\u5e1d\uff0c \u5c71\u6cb3\u4f9d\u65e7\uff0c\u8c01\u53c8\u80fd\u53bb\u8ffd\u6eaf\u8fc7\u53bb\u7684\u4ea7\u4e1a\u5462\uff1f\u201d \u3000\u3000\u5979\u8fd9\u4e00\u756a\u8bdd\uff0c\u62d2\u7edd\u4e86\u585e\u5916\u611a\u592b\u7b49\u8981\u51fa\u53e3\u7684\u8981\u6c42\uff0c\u4e5f\u5f88\u8f7b\u677e\u7684\u6392\u65a5\u4e86\u718a\u501c\u7684\u5ff5\u5934\uff0c\u7a76\u7adf\u5360 \u4e86\u591a\u5c11\u7406\uff1f\u662f\u5426\u5f3a\u8bcd\u593a\u7406\uff1f\u53ea\u80fd\u5c5e\u4e8e\u5404\u6267\u4e00\u8bcd\uff0c\u516c\u8bf4\u516c\u6709\u7406\u5a46\u8bf4\u5a46\u6709\u7406\u5427\uff01\u56e0\u4e3a\u501a\u5929\u5251\u7ec8\u4e0d \u662f\u94c1\u5251\u5148\u751f\u81ea\u5df1\u613f\u610f\u653e\u5f03\u7684\u4e1c\u897f\u3002 \u3000\u3000\u585e\u5916\u611a\u592b\u4ee5\u6781\u51b7\u9177\u7684\u53e3\u543b\uff0c\u575a\u51b3\u4ed6\u8bf4\uff1a\u201c\u6d41\u4e91\u5e08\u592a\u7adf\u80fd\u8bf4\u51fa\u8fd9\u79cd\u4e0d\u8fd1\u60c5\u7406\u7684\u8bdd\u672a\uff0c\u4f7f\u5c27 \u6743\u4e5f\u4e0d\u76f8\u4fe1\u81ea\u5df1\u7684\u53cc\u8033\uff01\u6b66\u6797\u5404\u6d3e\u540d\u5bbf\uff0c\u53ea\u6015\u65e0\u4eba\u4e0d\u4e3a\u4f60\u9f7f\u51b7\uff01\u51b5\u4e14\u4f60\u5ce8\u5d4b\u6d3e\u5e76\u975e\u6b63\u5f53\u624b\u6bb5 \u83b7\u5f97\u6b64\u5251\uff0c\u60a6\u6765\u4e4b\u7269\uff0c\u7b97\u5f97\u4e86\u6570\u4e48\uff1f\u5c27\u67d0\u5919\u627f\u5148\u5e08\u9057\u547d\uff0c\u7ec8\u5fc5\u4eb2\u4e0a\u5ce8\u5d4b\u65ad\u5d16\u53bb\u8bc4\u4e00\u8bc4\u7406\uff01\u201d \u3000\u3000\u6d41\u4e91\u5e08\u592a\u6da8\u7ea2\u4e86\u534a\u8fb9\u8138\uff0c\u53eb\u8d77\u6765\u9053\uff1a\u201c\u6765\u5427\uff01\u4f60\u5ce8\u5d4b\u540c\u95e8\u968f\u65f6\u606d\u5019\u5927\u9a7e\uff0c\u501a\u5929\u5251\u5c31\u6c38\u6302 \u5728\u5149\u660e\u6d1e\u77f3\u58c1\u4e4b\u4e0a\uff0c\u7b49\u5019\u4f60\u6606\u4ed1\u53cc\u6770\u524d\u6765\u6536\u53d6\u3002\u201d \u3000\u3000\u4e09\u4eba\u5df2\u5251\u62d4\u5f2f\u5f20\uff0c\u7ee7\u820c\u5251\u5507\u67aa\u4e4b\u540e\uff0c\u5f53\u7136\u662f\u514d\u4e0d\u4e86\u4e00\u573a\u6076\u6597\uff0c\u4f46\u77e5\u8da3\u7684\u4e3b\u4eba\uff0c\u82cd\u7a79\u82cd\u677e \u53cc\u9053\uff0c\u60df\u6050\u56e0\u6b64\u628a\u8058\u8bf7\u6765\u7684\u7fa4\u82f1\uff0c\u6405\u5f97\u7a00\u4e71\uff0c\u5b8c\u6210\u4e0d\u4e86\u5bf9\u4ed8\u5929\u9634\u6559\u7684\u8ba1\u5212\uff0c\u614c\u5fd9\u5206\u5411\u53cc\u65b9\u529d \u89e3\uff0c\u82cd\u7a79\u9053\u58eb\u8bf4\uff1a\u201c\u501a\u5929\u5251\u7684\u4e8b\uff0c\u7531\u8d35\u4e24\u6d3e\u53e6\u884c\u89e3\u51b3\uff01\u76ee\u524d\u5929\u9634\u6559\u6a2a\u884c\u4e0d\u6cd5\uff0c\u96be\u5f97\u5404\u65b9\u540d\u5bbf \u9ad8\u624b\uff0c\u4e00\u9f50\u964d\u4e34\u8352\u5c71\uff0c\u5bb6\u5e08\u5b9a\u4e8e\u660e\u65e5\u5348\u65f6\uff0c\u4e0e\u5404\u4f4d\u4f1a\u8c08\u6b64\u4e8b\uff0c\u4e07\u671b\u6682\u5fcd\u5c0f\u5fff\uff0c\u5171\u5fa1\u5f3a\u654c\uff0c\u4e3a \u6b66\u6797\u5927\u5c40\u7740\u60f3\uff0c\u8d2b\u9053\u4e0d\u80fd\u4e8b\u5148\u6d88\u9664\u8bef\u4f1a\uff0c\u786e\u5b9e\u62b1\u6b49\u5df2\u6781\uff01\u201d \u3000\u3000\u718a\u501c\u575a\u51b3\u7684\u795e\u6001\uff0c\u8fc8\u524d\u4e00\u6b65\uff0c\u62b1\u62f3\u5f53\u80f8\u8bf4\u9053\uff1a\u201c\u6606\u4ed3\u53cc\u6770\uff1a\u501a\u5929\u5251\u5931\u81ea\u5728\u4e0b\u624b\u4e2d\uff0c\u718a\u501c \u4e5f\u8981\u7b97\u4e0a\u4e00\u4efd\uff0c\u5f85\u628a\u540d\u5251\u4ea4\u8fd8\u6bd2\u5fc3\u795e\u9b54\u4e4b\u540e\uff0c\u5728\u4e0b\u65b9\u80fd\u5fc3\u5b89\uff0c\u540d\u5251\u8c01\u5c5e\uff0c\u5c0f\u5b50\u4e0d\u6562\u8fc7\u95ee\uff0c\u5e76 \u4e14\u4e5f\u65e0\u5fc3\u4e45\u5360\uff01\u201d \u3000\u3000\u585e\u5916\u611a\u592b\u70af\u70af\u51fa\u795e\u7684\u76ee\u5149\uff0c\u626b\u89c6\u7740\u4ed6\u7b11\u8bf4\uff1a\u201c\u53f0\u7aef\u5012\u5f88\u6709\u4e9b\u62b1\u8d1f\u548c\u81ea\u4fe1\uff01\u53cc\u5251\u5173\u7cfb\u7740\u6b66 \u6797\u76db\u8870\uff0c\u5c0f\u4fa0\u53ef\u77e5\u9053\u53cc\u5251\u4f5c\u7528\u6240\u5728\u4e48\uff1f\u201d \u3000\u3000\u718a\u501c\u5f7c\u4eba\u51b7\u51b7\u7684\u95ee\u4f4f\uff0c\u81ea\u7136\u4ed6\u7b54\u4e0d\u4e0a\u8bdd\u6765\u3002 \u3000\u3000\u7b11\u5929\u53df\u53c8\u4ef0\u5929\u54c8\u54c8\u5927\u7b11\u8bf4\uff1a\u201c\u4faf\u751f\u8001\u9b54\uff0c\u4e0e\u4f60\u4ec0\u4e48\u5173\u7cfb\uff1f\u6700\u597d\u4f60\u53bb\u8bf7\u793a\u4e00\u4e0b\u6bd2\u5fc3\u795e\u9b54\uff0c \u770b\u4ed6\u62ff\u4ec0\u4e48\u8bdd\u5429\u5490\u4f60\uff01\u201d \u3000\u3000\u718a\u501c\u4e0d\u80af\u5fd8\u672c\uff0c\u9042\u6297\u58f0\u9053\uff1a\u201c\u718a\u501c\u5e7c\u65f6\uff0c\u5f97\u661f\u6708\u53cc\u5251\u9646\u98de\u767d\u6234\u68a6\u5c27\u4e24\u4f4d\u79d8\u6388\u5929\u96f7\u884c\u529f\u82cd \u7a79\u5341\u4e09\u5f0f\uff0c\u7ecf\u6bd2\u5fc3\u6069\u5e08\u52a0\u4ee5\u6df1\u9020\uff0c\u590d\u5728\u6cf0\u5c71\u53d7\u4e1a\u98d8\u7136\u8001\u4eba\u95e8\u4e0b\u4e09\u8f7d\u3002\u201d\u4ed6\u53c8\u65a9\u9489\u622a\u94c1\u4ed6\u8bf4\uff1a \u201c\u501a\u5929\u5251\u6211\u718a\u501c\u5fc5\u4eb2\u624b\u6536\u56de\uff01\u4ee5\u8c22\u4faf\u6069\u5e08\u3002\u201d \u3000\u3000\u585e\u5916\u611a\u592b\u548c\u7b11\u5929\u53df\u88ab\u8fd9\u5c11\u5e74\u6177\u6168\u9648\u8bcd\uff0c\u7a81\u7136\u4e92\u76f8\u4ea4\u6362\u4e86\u4e0b\u795e\u79d8\u7684\u773c\u5149\uff0c\u540c\u65f6\u5475\u5475\u5927\u7b11 \u8bf4\uff1a\u201c\u539f\u6765\u662f\u4ed6\u7684\u5b89\u6392\uff0c\u718a\u5c0f\u4fa0\u7f18\u5206\u4e0d\u6d45\uff01\u201d \u3000\u3000\u585e\u5916\u611a\u592b\u53c8\u6b63\u8272\u9053\uff1a\u201c\u718a\u5c0f\u4fa0\uff0c\u4f60\u53ef\u77e5\u9053\u4f60\u9646\u53d4\u53d4\u7684\u5e08\u627f\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u8fd9\u81ea\u7136\u53c8\u662f\u718a\u501c \u65e0\u6cd5\u56de\u7b54\u7684\u8bdd\u3002 \u3000\u3000\u6606\u4ed1\u53cc\u6770\u7684\u95ee\u8bdd\uff0c\u4f7f\u718a\u501c\u6709\u4e9b\u6000\u7591\uff0c\u96be\u9053\u6606\u4ed1\u53cc\u6770\uff0c\u548c\u81ea\u5df1\u7684\u9646\u53d4\u53d4\u4eec\u8fd8\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f \u4f46\u662f\u585e\u5916\u611a\u592b\u4e8c\u4eba\uff0c\u5bf9\u718a\u501c\u7684\u6001\u5ea6\uff0c\u663e\u7136\u548c\u521d\u89c1\u9762\u65f6\u5927\u4e3a\u4e0d\u540c\uff0c\u7531\u89c6\u5982\u8def\u4eba\u8f6c\u53d8\u6210\u5341\u5206\u4eb2\u5207 \u4e4b\u8272\u3002 \u3000\u3000\u7b11\u5929\u53df\u8bf4\uff1a\u201c\u718a\u5c0f\u4fa0\uff0c\u4f60\u518d\u5411\u4faf\u8001\u9b54\u8bf7\u95ee\u4e00\u4e0b\uff0c\u8fd9\u67c4\u8d2f\u65e5\u5251\uff0c\u6682\u65f6\u5bc4\u5b58\u5728\u4f60\u8eab\u4e0a\uff0c\u5343\u4e07 \u5c0f\u5fc3\uff0c\u4e0d\u53ef\u4f7f\u5b83\u518d\u4e3a\u5bb5\u5c0f\u6240\u4e58\uff01\u5ce8\u5d4b\u4e00\u884c\uff0c\u52bf\u6240\u4e0d\u514d\uff0c\u4f60\u4e5f\u4e0d\u59a8\u53bb\u4f1a\u4f1a\u5f02\u6d3e\u7684\u540d\u5bbf\u9ad8\u624b\uff01\u81f3 \u4e8e\u2026\u2026\u201d \u3000\u3000\u4ed6\u6ca1\u8bf4\u4e0b\u53bb\uff0c\u7b11\u7b11\u9053\uff1a\u201c\u4ee5\u540e\u518d\u8c08\u5427\uff01\u201d \u3000\u3000\u6606\u4ed1\u53cc\u6770\u4e0d\u5411\u718a\u501c\u7d22\u56de\u8d2f\u65e5\u5251\uff0c\u4f7f\u5728\u573a\u7684\u4eba\uff0c\u611f\u5230\u4ed6\u4fe9\u5fc5\u4e0e\u718a\u501c\u6709\u4ec0\u4e48\u7279\u6b8a\u7684\u5173\u7cfb\uff0c\u4f46 \u4f55\u4ee5\u8fd8\u8981\u718a\u501c\u53bb\u5ce8\u5d4b\u5462\uff1f\u5c31\u662f\u718a\u501c\u672c\u4eba\uff0c\u4e5f\u832b\u7136\u4e0d\u89e3\u3002 \u3000\u3000\u718a\u501c\u6014\u6014\u5730\u8bf4\uff1a\u201c\u5728\u4e0b\u8fd8\u8981\u7acb\u5de8\u201d\u5bfb\u8bbf\u4e00\u4f4d\u670b\u53cb\uff0c\u5ce8\u5d4b\u4e4b\u884c\uff0c\u65e9\u665a\u8fd8\u4e0d\u80fd\u5b9a\u51c6\u65e5\u671f\uff0c\u6700 \u597d\u5404\u884c\u5176\u4e8b\uff0c\u5c1a\u8bf7\u539f\u8c05\uff01\u201c\u7b11\u5929\u53df\u548c\u585e\u5916\u611a\u592b\u76f8\u89c6\u4e00\u7b11\uff0c\u6ca1\u6709\u518d\u8bf4\u4ec0\u4e48\u3002\u6d41\u4e91\u8001\u5c3c\u628a\u4e24\u4e2a\u5f92 \u5f1f\u4e00\u62db\u624b\uff0c\u7adf\u81ea\u98de\u6b65\u4e0b\u5c71\uff0c\u5979\u5df2\u5fcd\u4e86\u4e00\u809a\u5b50\u6076\u6c14\uff0c\u4ee5\u79bb\u5f00\u8fd9\u4e2a\u4f7f\u5979\u96be\u582a\u7684\u573a\u5408\u4e3a\u5999\u3002\u4f46\u82cd\u7a79 \u82cd\u677e\u4e24\u4e2a\u9053\u58eb\uff0c\u5374\u7b11\u5bb9\u53ef\u63ac\uff0c\u8d76\u8fc7\u53bb\u62e6\u4f4f\u4e86\u5979\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u8bf7\u5979\u660e\u5929\u5f00\u5b8c\u4e86\u4f1a\u518d\u8d70\u3002\u6d41\u4e91\u8001 \u5c3c\u867d\u7136\u6027\u60c5\u66b4\u70c8\uff0c\u4f46\u773c\u524d\u70b9\u82cd\u53cc\u4fa0\u6606\u4ed1\u53cc\u6770\uff0c\u65e0\u4e00\u4e0d\u662f\u786c\u5bf9\u5934\uff0c\u5bf9\u65b9\u4eba\u591a\u52bf\u4f17\uff0c\u4e0d\u80fd\u5403\u773c\u524d \u4e8f\uff0c\u56de\u5230\u5ce8\u5d4b\u4ee5\u540e\uff0c\u6709\u8bf8\u540c\u95e8\u5171\u8d77\u5fa1\u4fae\uff0c\u4e0d\u6015\u718a\u501c\u548c\u6606\u4ed1\u53cc\u6770\u4e0d\u5403\u4e0a\u4e2a\u5927\u4e8f\uff0c\u6240\u4ee5\u5979\u6ca1\u6709\u7acb \u65f6\u518d\u53d1\u4f5c\u51fa\u6765\u3002\u7ecf\u8fc7\u82cd\u7a79\u82cd\u677e\u4e24\u4f4d\u9053\u58eb\u6614\u53e3\u529d\u89e3\uff0c\u603b\u7b97\u628a\u8fd9\u4f4d\u5ce8\u5d4b\u602a\u6770\u52c9\u5f3a\u7559\u4e0b\uff0c\u4f17\u4eba\u5728\u5f7c \u6b64\u6781\u4e0d\u878d\u6d3d\u7684\u6c14\u6c1b\u4e2d\uff0c\u91cd\u53c8\u5411\u7389\u771f\u4e0b\u9662\u8d70\u53bb\u3002\u6606\u4ed1\u53cc\u6770\uff0c\u5219\u548c\u718a\u501c\u53d9\u8ff0\u8d77\u6765\uff0c\u7ec6\u95ee\u4ed6\u5b66\u827a\u7684 \u7ecf\u8fc7\uff0c\u51fa\u8eab\u6765\u5386\u7b49\u7b49\u3002\u718a\u501c\u5bf9\u4e8e\u81ea\u8eab\u6765\u5386\uff0c\u4f9d\u7136\u61f5\u61c2\u65e0\u77e5\uff0c\u53ea\u6653\u5f97\u6709\u4e2a\u59b9\u59b9\uff0c\u4e0d\u77e5\u4e0b\u843d\uff0c\u800c \u4ec7\u5bb6\u5b9d\u9a6c\u795e\u97ad\u8428\u5929\u9aa5\u7684\u540d\u5b57\uff0c\u6570\u5e74\u6765\uff0c\u6df1\u6df1\u5370\u5d4c\u5728\u4ed6\u8111\u6d77\u91cc\u3002\u70b9\u82cd\u53cc\u4fa0\u6563\u82b1\u4ed9\u5b50\u592b\u5987\uff0c\u4e5f\u548c \u585e\u5916\u611a\u592b\u7b49\u4e92\u76f8\u4ea4\u8c08\uff0c\u56e0\u4e4b\u4f7f\u6d41\u4e91\u8001\u5c3c\u81ea\u89c9\u5f62\u52bf\u975e\u5e38\u5b64\u7acb\uff0c\u5e78\u4e8f\u51fa\u5c18\u5251\u5ba2\u5144\u59b9\uff0c\u548c\u5979\u662f\u719f\u8bc6 \u7684\uff0c\u8c08\u53ca\u5929\u9634\u6559\u76ee\u524d\u7316\u6485\u7684\u5f62\u52bf\uff0c\u5d06\u5cd2\u6d3e\u4eba\uff0c\u5df2\u6709\u5f52\u4e8e\u5929\u9634\u6559\u65d7\u5e1c\u4e0b\u7684\u8d8b\u52bf\u3002\u4f17\u4eba\u8c08\u864e\u8272 \u53d8\uff0c\u5bf9\u4e8e\u5929\u9634\u6559\uff0c\u5927\u5bb6\u662f\u540c\u4ec7\u654c\u5ffe\uff0c\u4e00\u81f4\u6df1\u6076\u75db\u7edd\u7684\u3002\u7389\u771f\u4e0b\u9662\u5728\u4e00\u7247\u677e\u6749\u6797\u4e2d\uff0c\u5883\u754c\u5e7d \u96c5\uff0c\u78a7\u5d16\u73af\u62b1\uff0c\u4fee\u7bc1\u655d\u65e5\uff0c\u800c\u89c2\u5b87\u5374\u4e0d\u5f88\u5927\uff0c\u53ea\u6709\u4e94\u95f4\u4e09\u6e05\u6bbf\uff0c\u4e24\u9762\u90fd\u662f\u5e7d\u96c5\u51fa\u5c18\u7684\u9759\u9662\u3002 \u5404\u65b9\u9ad8\u624b\uff0c\u5148\u540e\u4e91\u96c6\uff0c\u6b63\u6bbf\u5df2\u6253\u626b\u5f97\u975e\u5e38\u6d01\u51c0\uff0c\u5e03\u7f6e\u4e86\u4e00\u4e2a\u5404\u6b63\u6d3e\u805a\u4f1a\u7684\u573a\u6240\uff0c\u800c\u5404\u65b9\u9ad8 \u624b\uff0c\u5206\u4f4f\u5728\u4e24\u4fa7\u9759\u9662\u5185\uff0c\u82cd\u7a79\u82cd\u677e\u5f15\u8fd9\u51e0\u4f4d\u5c11\u5e74\u82f1\u96c4\uff0c\u81ea\u6708\u6d1e\u95e8\u8fdb\u5165\u5de6\u4fa7\u9053\u9662\u3002\u4e24\u6392\u5f88\u6574\u6d01 \u7684\u4e39\u623f\uff0c\u82b1\u6728\u6276\u758f\u3002\u53e6\u6709\u7167\u5e94\u7684\u5c0f\u9053\u58eb\uff0c\u4f3a\u5019\u8336\u6c34\u7d20\u658b\u3002\u718a\u501c\u7b49\u88ab\u8fce\u4eba\u6781\u6d01\u7684\u4e39\u623f\uff0c\u4ed6\u4eec\u516d \u4f4d\u5206\u4f4f\u4e86\u4e09\u5927\u95f4\u623f\u5b50\uff0c\u540c\u5728\u4e00\u6392\uff0c\u4e2d\u95f4\u662f\u4e86\u9e64\u8f69\u655e\u5385\uff0c\u4f17\u4eba\u6682\u96c6\u5385\u4e0a\u6b3e\u8336\u3002\u6d41\u4e91\u5e08\u592a\u5219\u643a\u4e86 \u4e8c\u5f92\uff0c\u95f7\u95f7\u56de\u5230\u53f3\u4fa7\u9662\u4e2d\u3002\u8c37\u5c0f\u9759\u53ae\u7f20\u7740\u4e1c\u65b9\u745b\uff0c\u5979\u53c8\u6084\u6084\u6e9c\u6765\uff0c\u5176\u76ee\u7684\u4e0d\u5f85\u8bf4\u662f\u60f3\u548c\u51fa\u5c18 \u5251\u5ba2\u591a\u4eb2\u8fd1\u4e9b\uff0c\u770b\u770b\u4e1c\u65b9\u7075\u7a76\u7adf\u6709\u60c5\u8fd8\u662f\u65e0\u60c5\uff1f\u655e\u5385\u4e0a\u6606\u4ed1\u53cc\u6770\uff0c\u718a\u501c\u5c1a\u672a\u660e\uff0c\u6563\u82b1\u4ed9\u5b50\u592b \u5987\u516d\u4eba\u52a0\u4e0a\u4e1c\u65b9\u7075\uff0c\u7531\u82cd\u7a79\u9053\u58eb\u966a\u5750\u95f2\u8c08\uff0c\u4f46\u8c08\u7684\u8fd8\u662f\u79bb\u4e0d\u5f00\u5929\u9634\u6559\u7684\u95ee\u9898\u3002\u4e1c\u65b9\u745b\u5219\u4e0e\u8c37 \u5c0f\u9759\u5728\u4e39\u623f\u4e2d\u5bc6\u8bed\uff0c\u540c\u662f\u5c0f\u59d1\u65e0\u9632\uff0c\u65e0\u7591\u7684\u8981\u54c1\u8bc4\u4e00\u4e0b\u718a\u501c\u548c\u5c1a\u672a\u660e\u7684\u4eba\u54c1\u6b66\u6280\u3002\u718a\u501c\u5fc3\u91cc \u7684\u91cd\u62c5\uff0c\u51cf\u8f7b\u4e86\u4e00\u534a\uff0c\u501a\u5929\u5251\u4e0d\u81f3\u4e8e\u832b\u65e0\u5934\u7eea\u4e86\uff0c\u4f46\u662f\u82b8\u59b9\u59b9\u5462\uff1f\u4f0a\u4eba\u5982\u6709\u4ec0\u4e48\u95ea\u5931\uff0c\u66f4\u662f \u4f7f\u718a\u501c\u5fc3\u788e\uff0c\u4f55\u51b5\u5979\u6781\u6709\u88ab\u5929\u9634\u6559\u4eba\u8bf1\u9a97\u7684\u53ef\u80fd\uff01\u8fd9\u4f7f\u718a\u501c\u5fc3\u91cc\uff0c\u6c89\u91cd\u5f97\u50cf\u5760\u7740\u4e00\u5927\u5757\u4e1c \u897f\u3002\u718a\u501c\u4ecd\u548c\u6606\u4ed1\u53cc\u6770\u7b49\u7b11\u8bed\uff0c\u4ed6\u4e0d\u80fd\u7f3a\u4e86\u793c\u6570\u3002\u7a81\u7136\u81ea\u6708\u6d1e\u95e8\u6d8c\u8fdb\u6765\u4e09\u4f4d\u6c14\u6168\u4e0d\u51e1\u7684\u4eba\uff0c \u5176\u4e2d\u4e00\u4f4d\u5e74\u6ee1\u4e09\u5341\u7684\u6c49\u5b50\u5de8\u543c\u5982\u96f7\uff0c\u8fdc\u8fdc\u5c31\u5411\u718a\u501c\u559d\u9053\uff1a\u201c\u718a\u501c\uff01\u5929\u5c71\u4e09\u9f99\uff0c\u4e0e\u4f60\u6709\u7f18\u76f8 \u4f1a\uff01\u4eca\u513f\u6211\u949f\u5929\u4ec7\u518d\u6765\u4f1a\u4f1a\u4f60\uff01\u201d \u3000\u3000\u4f17\u4eba\u90fd\u4e3a\u8fd9\u4e09\u4f4d\u6d8c\u8ff8\u9759\u9662\u6765\u7684\u4eba\u7269\u8d77\u4e86\u7eb7\u6270\u3002 \u3000\u3000\u6606\u4ed1\u53cc\u6770\u4e45\u5904\u897f\u5317\uff0c\u8ba4\u5f97\u8fd9\u79c9\u6027\u6b8b\u9177\u7684\u5929\u5c71\u4e09\u9f99\u7236\u5b50\uff0c\u6700\u79f0\u6bd2\u8fa3\u7684\u662f\u8001\u4fa0\u949f\u95ee\u5929\uff0c\u4e0d\u77e5 \u81ea\u4f55\u5904\u5f97\u6765\u4e00\u5957\u79d8\u4e66\uff0c\u5148\u540e\u5316\u4e86\u5341\u56db\u5e74\u7684\u9762\u58c1\u82e6\u529f\uff0c\u70bc\u6210\u4e00\u79cd\u5a01\u529b\u5f3a\u5927\u7684\u9634\u715e\u624b\uff0c\u662f\u5426\u548c\u5929 \u9634\u6559\u79d8\u7c4d\u6709\u4ec0\u4e48\u5173\u7cfb\uff0c\u65e0\u4eba\u5f97\u77e5\u3002 \u3000\u3000\u4f46\u8fd9\u79cd\u9634\u715e\u624b\uff0c\u8fd8\u4ece\u672a\u5411\u6b66\u6797\u4e2d\u8868\u9732\u8fc7\u3002 \u3000\u3000\u5927\u4fa0\u949f\u5929\u5b87\uff0c\u5c0f\u4fa0\u949f\u5929\u4ec7\uff0c\u7236\u5b50\u4e09\u4eba\u4ec5\u5e74\u9f84\u4e0a\u7565\u6709\u5dee\u5f02\uff0c\u800c\u4e00\u8272\u9ed1\u886b\u9ed1\u5c65\uff0c\u4f7f\u4eba\u770b\u89c1\u6709 \u4e9b\u523a\u76ee\uff0c\u4e00\u6837\u662f\u82cd\u767d\u51c4\u60e8\u7684\u8138\u8272\uff0c\u53ea\u949f\u95ee\u5929\u591a\u4e86\u51e0\u7edc\u82cd\u987b\u3002 \u3000\u3000\u56db\u5e74\u524d\u718a\u501c\u548c\u9e23\u8fdc\u9556\u5c40\u4e8c\u9556\u5934\u5434\u8bcf\u4e91\uff0c\u62a4\u9001\u4f55\u9996\u4e4c\u5728\u4e34\u57ce\u9053\u4e0a\u4e0e\u5c11\u4fa0\u949f\u5927\u4ec7\uff0c\u66fe\u4f5c\u8fc7\u4e00 \u6b21\u610f\u6c14\u76f8\u4e89\u7684\u640f\u6597\uff0c\u800c\u949f\u5929\u4ec7\u4ee5\u98de\u9f99\u4e03\u5f0f\u5251\u6cd5\uff0c\u6ca1\u6709\u8ba8\u5230\u4e00\u4e1d\u4fbf\u5b9c\uff0c\u4fbf\u751f\u8d77\u4e86\u6000\u6068\u718a\u501c\u7684 \u5fc3\uff0c\u76f4\u5230\u4ed6\u57cb\u9996\u82e6\u7ec3\uff0c\u81ea\u4ee5\u4e3a\u8db3\u53ef\u62a5\u590d\u718a\u501c\u4e86\uff0c\u5bf9\u201c\u7fe9\u7136\u91cd\u4eba\u6c5f\u6e56\uff0c\u540c\u65f6\u4e5f\u662f\u8001\u4fa0\u949f\u95ee\u5929\u60f3 \u8981\u79f0\u96c4\u6b66\u6797\uff0c\u73b0\u9732\u9634\u715e\u624b\u7684\u65f6\u673a\uff0c\u7236\u5b50\u4e09\u4eba\u6e38\u8e2a\u904d\u53ca\u6c5f\u5357\u3002\u4ed6\u4e09\u4eba\u6000\u6709\u83ab\u5927\u7684\u91ce\u5fc3\uff0c\u60f3\u5148\u5728 \u6b66\u6797\u7b2c\u4e00\u5927\u5b97\u6d3e\u7684\u5723\u5730\u6b66\u5f53\u5c71\uff0c\u6811\u7acb\u5a01\u540d\uff0c\u4e0e\u98de\u9e64\u5b50\u76f8\u9047\uff0c\u6b63\u9022\u98de\u9e64\u5b50\u5728\u7f51\u7f57\u5404\u65b9\u597d\u624b\uff0c\u9042 \u628a\u4ed6\u7236\u5b50\u9080\u4e0a\u5c71\u6765\uff0c\u7aed\u8bda\u6b3e\u5f85\uff0c\u4e86\u53ef\u4ee5\u8bf4\u662f\u5f00\u95e8\u63d6\u76d7\uff0c\u5f15\u72fc\u4eba\u5ba4\u4e86\u3002\u5929\u5c71\u4e09\u9f99\u7684\u91ce\u5fc3\uff0c\u4e0d\u5728 \u5929\u9634\u6559\u7126\u5f02\u884c\u592b\u5987\u4e4b\u4e0b\uff0c\u800c\u4ed6\u4eec\u8fdf\u8fdf\u672a\u5411\u6b66\u5f53\u6d3e\u4eba\u793a\u4ee5\u989c\u8272\uff0c\u662f\u60f3\u501f\u6b66\u5f53\u6d3e\u9080\u9f50\u4e86\u5404\u65b9\u5404\u6d3e \u9ad8\u624b\uff0c\u7136\u540e\u65bd\u5c55\u7edd\u6280\uff0c\u4e00\u8b66\u9707\u6151\u7fa4\u82f1\uff0c\u8fbe\u5230\u4ed6\u7236\u5b50\u79f0\u96c4\u4e00\u4e16\u7684\u76ee\u7684\u3002\u949f\u5929\u4ec7\u5374\u53d1\u73b0\u4e86\u718a\u501c\uff0c \u6614\u5e74\u90a3\u4e00\u6bb5\u8fc7\u8282\uff0c\u5728\u4ed6\u5f15\u4e3a\u5947\u803b\u5927\u8fb1\uff0c\u7adf\u672a\u80fd\u628a\u718a\u501c\u6253\u8d25\uff0c\u4eff\u4f5b\u5931\u4e86\u5f88\u5927\u7684\u9762\u5b50\uff0c\u53c8\u542c\u8bf4\u70b9 \u82cd\u6d3e\u7684\u540d\u624b\u540c\u6765\uff0c\u6073\u6c42\u7236\u5144\uff0c\u4ee3\u4ed6\u627e\u56de\u4ee5\u524d\u7684\u9762\u5b50\uff0c\u800c\u718a\u501c\u81ea\u7136\u662f\u4ed6\u7236\u5b50\u501f\u4ee5\u53d1\u6325\u7684\u597d\u9898 \u76ee\u3002\u718a\u501c\u7684\u540d\u671b\uff0c\u5217\u5165\u4e09\u79c0\uff0c\u786e\u5b9e\u66f4\u4f7f\u5929\u5c71\u4e09\u9f99\u6c14\u6124\u3002\u5385\u4e0a\u4f17\u4eba\u90fd\u6115\u7136\u63a0\u8d77\uff0c\u718a\u501c\u5219\u4ee5\u66f4\u5b89 \u8be6\u7684\u795e\u8272\uff0c\u5411\u949f\u5929\u4ec7\u5fae\u7b11\u62f1\u624b\u9053\uff1a\u201c\u949f\u5c11\u4fa0\uff0c\u4e34\u57ce\u6bd4\u5251\uff0c\u53d7\u76ca\u532a\u6d45\uff01\u5c11\u4fa0\u5982\u8fd8\u4e0d\u80fd\u5fd8\u6000\u90a3\u591c \u7684\uff0c\u718a\u501c\u656c\u5019\u8d50\u6559\u5c31\u662f\uff01\u201d \u3000\u3000\u82cd\u7a79\u82cd\u677e\u4ee5\u4e3b\u4eba\u7684\u8eab\u4efd\uff0c\u820c\u655d\u5507\u7126\uff0c\u51fa\u9762\u65a1\u65cb\u3002 \u3000\u3000\u7389\u9762\u795e\u5251\u4e5f\u4e45\u95fb\u5929\u5c71\u4e09\u9f99\u51f6\u66b4\u7684\u540d\u6c14\uff0c\u4f46\u4ed6\u5728\u70b9\u82cd\u6bd4\u5251\u65f6\uff0c\u4e09\u9f99\u5374\u8fd8\u9690\u5c45\u5929\u5c71\uff0c\u8bfe\u6388\u5929 \u5b87\u5929\u4ec7\u7684\u6b66\u6280\uff0c\u672a\u66fe\u4e0e\u4f1a\u3002 \u3000\u3000\u5e38\u6f2b\u5929\u548c\u6563\u82b1\u4ed9\u5b50\u76f8\u89c6\u4e00\u7b11\uff0c\u4e24\u4eba\u4f3c\u90fd\u4ee5\u6b66\u5f53\u6d3e\u5ef6\u8058\u8fd9\u79cd\u4f3c\u90aa\u975e\u90aa\u8bf4\u6b63\u4e0d\u6b63\u7684\u4eba\u7269\uff0c\u6b8a \u4e3a\u9057\u61be\u3002 \u3000\u3000\u6b66\u5f53\u98de\u9e64\u5b50\u662f\u6709\u6df1\u610f\u7684\uff0c\u6b63\u6d3e\u65b9\u9762\u589e\u52a0\u4e00\u822c\u529b\u91cf\uff0c\u5c31\u53ef\u591a\u64cd\u4e00\u5206\u80dc\u7b97\uff0c\u8ba9\u5929\u5c71\u4e09\u9f99\u88ab\u5929 \u9634\u6559\u62c9\u8fc7\u53bb\u90a3\u5c31\u5927\u4e0d\u5408\u7b97\uff0c\u5b81\u80af\u59d4\u5c48\u5c06\u5c31\u4ed6\u4eec\u4e9b\u3002 \u3000\u3000\u94c1\u80c6\u5c1a\u672a\u660e\uff0c\u4e8c\u6b21\u6765\u6b66\u5f53\u5c71\uff0c\u6606\u4ed1\u53cc\u6770\u3001\u5ce8\u5d4b\u6d41\u4e91\u5e08\u592a\u7b49\u90fd\u4f3c\u5bf9\u4ed6\u9732\u51fa\u4e00\u4e1d\u8f7b\u89c6\u4e4b\u610f\uff0c \u518d\u8bf4\u4ed6\u662f\u7eff\u6797\u603b\u74e2\u628a\u5b50\uff0c\u6c5f\u6e56\u4e0a\u628a\u5f0f\uff0c\u600e\u80fd\u4e0e\u4e94\u5927\u540d\u95e8\u6b63\u6d3e\u76f8\u63d0\u5e76\u8bba\uff1f\u5c1a\u672a\u660e\u76ee\u65e0\u4f59\u5b50\uff0c\u65e9 \u5c31\u60f3\u81ea\u6211\u8868\u73b0\u4e00\u756a\u3002 \u3000\u3000\u5c1a\u672a\u660e\u8f7b\u8f7b\u4e00\u95ea\uff0c\u5df2\u8dc3\u5728\u718a\u501c\u524d\u9762\uff0c\u4ed6\u53cc\u624b\u62b1\u62f3\u8bf4\uff1a\u201c\u6211\u4e24\u6cb3\u94c1\u80c6\u5c1a\u672a\u660e\uff0c\u4e45\u4ef0\u5929\u5c71\u4e09 \u9f99\u82f1\u540d\uff0c\u65e0\u7f18\u9886\u6559\uff0c\u4eca\u65e5\u5374\u6b63\u9042\u4e86\u5e73\u751f\u4e4b\u613f\uff0c\u4f46\u4e09\u9f99\u6709\u4e09\u4f4d\uff0c\u718a\u5927\u54e5\u4e5f\u65e0\u6cd5\u5206\u8eab\u5949\u966a\uff0c\u6211\u5c1a \u67d0\u5012\u613f\u8ddf\u4e09\u9f99\u4e2d\u4e00\u4e24\u4f4d\u73a9\u73a9\uff01\u201d \u3000\u3000\u5c1a\u672a\u660e\u8fd9\u51e0\u53e5\u8bdd\uff0c\u8f7b\u677e\u3001\u72c2\u505a\uff0c\u517c\u800c\u6709\u4e4b\uff0c\u4f7f\u5929\u5c71\u4e09\u9f99\u51e0\u4e4e\u6c14\u70b8\u4e86\u80f8\u819b\u3002\u5929\u5c71\u4e09\u9f99\u771f\u6ca1 \u60f3\u5230\u4e00\u4e2a\u7eff\u6797\u8c6a\u6770\uff0c\u7adf\u6562\u5728\u4ed6\u7236\u5b50\u9762\u524d\uff0c\u5982\u6b64\u653e\u8086\u3002 \u3000\u3000\u5927\u4fa0\u949f\u5929\u5b87\u82cd\u767d\u7684\u8138\u4e0a\uff0c\u9752\u7b4b\u5fae\u5fae\u7275\u52a8\uff0c\u6beb\u65e0\u8868\u60c5\u53ea\u900f\u715e\u6c14\u7684\u76ee\u5149\u4e00\u8f6c\uff0c\u4ee5\u6781\u4e0d\u5c51\u7684\u6001 \u5ea6\uff0c\u76ee\u5149\u4e0a\u63a0\uff0c\u53ea\u5fae\u5fae\u9894\u4e86\u4e00\u4e0b\u9996\uff0c\u9053\uff1a\u201c\u96be\u5f97\u96be\u5f97\uff01\u4f60\u5c1a\u5f53\u5bb6\u7684\u8fd8\u6709\u8fd9\u4efd\u513f\u80c6\u91cf\uff01\u5929\u5c71\u4e09 \u9f99\uff0c\u8981\u7834\u4f8b\u6559\u8bf2\u4e00\u4e0b\u6c5f\u6e56\u540e\u8f88\u4e86\uff01\u201d \u3000\u3000\u949f\u95ee\u5929\u5219\u628a\u718a\u501c\u5c1a\u672a\u660e\uff0c\u4ee5\u53ca\u6563\u82b1\u4ed9\u5b50\u592b\u5987\uff0c\u7528\u9119\u5937\u4e0d\u5c51\u7684\u773c\u5149\u626b\u89c6\u4e00\u904d\uff0c\u4ed6\u81ea\u7136\u662f\u4e0d \u80af\u548c\u8fd9\u4e9b\u5e74\u8f7b\u4eba\u52a8\u624b\u7684\u3002 \u3000\u3000\u6563\u82b1\u4ed9\u5b50\u7530\u654f\u654f\u5a07\u7b11\u7740\uff0c\u7b11\u5f97\u5982\u540c\u82b1\u6735\u513f\u6447\u5934\u3002 \u3000\u3000\u5979\u5411\u7389\u9762\u795e\u5251\u8bf4\uff1a\u201c\u90a3\u8fb9\u8fd8\u6709\u4e2a\u8001\u5934\u5b50\u5462\uff0c\u8be5\u6211\u4fe9\u53bb\u6253\u53d1\u4e86\u4ed6\uff01\u201d\u6606\u4ed1\u53cc\u6770\u585e\u5916\u611a\u592b\u89c1 \u5feb\u95f9\u5f97\u4e0d\u53ef\u6536\u62fe\uff0c\u4ed6\u987a\u7740\u4e3b\u4eba\u7684\u610f\u601d\u5411\u53cc\u65b9\u62e6\u529d\uff0c\u8bf4\uff1a\u201c\u6211\u4eec\u4e0d\u80fd\u4e8f\u8d1f\u4e86\u4e3b\u4eba\uff0c\u4e2d\u95f4\u79c1\u4e0b\u91cc \u7684\u6881\u5b50\uff0c\u5e94\u8be5\u53e6\u627e\u673a\u4f1a\u53bb\u89e3\u51b3\uff0c\u6700\u597d\u5728\u660e\u5929\u4e3b\u4eba\u4e3b\u6301\u7684\u5927\u4f1a\u4e4b\u540e\uff0c\u8001\u592b\u60f3\u718a\u5c0f\u4fa0\u4e0d\u4f1a\u4e00\u8d70\u4e86 \u4e4b\uff0c\u754f\u9996\u754f\u5c3e\u7684\uff01\u95ee\u5929\u5144\u4ee5\u4e3a\u6211\u8fd9\u53e5\u53ef\u4ee5\u91c7\u7eb3\u4e48\uff1f\u201d \u3000\u3000\u949f\u95ee\u5929\u591a\u5c11\u5bf9\u4e8e\u6606\u4ed1\u53cc\u6770\uff0c\u6709\u4e9b\u754f\u5fcc\uff0c\u4f46\u662f\u72c2\u5984\u6545\u6001\uff0c\u4f9d\u7136\u8f7b\u8f7b\u7b54\u9053\uff1a\u201c\u65e9\u665a\u603b\u662f\u4e00 \u6837\uff0c\u5c0f\u513f\u4e0e\u718a\u501c\u8c08\u4e0d\u4e0a\u4ec0\u4e48\u6df1\u4ec7\u5927\u6068\uff0c\u4f46\u662f\u4e92\u5370\u8bc1\u4e00\u6b21\u6b66\u5b66\uff0c\u4e5f\u4e0d\u81f3\u4e8e\u6709\u8d1f\u4e3b\u4eba\u76db\u610f\uff0c\u8001\u592b \u53ef\u5429\u5490\u5c0f\u513f\u5929\u5b87\u5929\u4ec7\uff0c\u70b9\u5230\u4e3a\u6b62\uff0c\u7565\u7565\u544a\u8beb\u4e00\u4e0b\u8fd9\u4e9b\u4e0d\u8bc6\u8fdb\u9000\u7684\u540e\u751f\u5c0f\u5b50\uff0c\u8001\u592b\u8896\u624b\u65c1\u89c2\u5c31 \u662f\u4e86\u3002\u201d \u3000\u3000\u4ed6\u628a\u8bdd\u8bf4\u8fc7\u4e86\u706b\u3002\u4f3c\u4e4e\u4ed6\u4e24\u4e2a\u513f\u5b50\uff0c\u80fd\u4fdd\u6709\u80dc\u65e0\u8d25\uff0c\u800c\u6606\u4ed1\u53cc\u6770\u4e5f\u89c9\u5f97\u8fd9\u4e9b\u5927\u8bdd\uff0c\u592a\u8fc7 \u523a\u8033\uff0c\u81f3\u4e8e\u5c1a\u672a\u660e\u548c\u718a\u501c\uff0c\u66f4\u662f\u65e0\u6cd5\u5fcd\u53d7\u4e86\u3002 \u3000\u3000\u6563\u82b1\u4ed9\u5b50\u5374\u7ea4\u624b\u4e00\u6307\u949f\u95ee\u5929\u8bf4\u9053\uff1a\u201c\u949f\u8001\u5934\u513f\uff0c\u4f60\u4e5f\u8131\u4e0d\u4e86\u624b\uff0c\u51ed\u4f60\u90a3\u4e24\u5934\u6076\u5927\uff0c\u662f\u4e0d \u503c\u4eba\u5bb6\u4e00\u51fb\u7684\uff0c\u542c\u8bf4\u4f60\u7ec3\u4e86\u4ec0\u4e48\u9b3c\u628a\u620f\u9634\u715e\u624b\uff0c\u6211\u7530\u654f\u654f\u5012\u60f3\u89c1\u8bc6\u89c1\u8bc6\uff01\u201d \u3000\u3000\u5929\u5c71\u8001\u9f99\u949f\u95ee\u5929\uff0c\u591a\u5c11\u4e3a\u6563\u82b1\u4ed9\u5b50\u5201\u94bb\u5028\u50b2\u7684\u8bdd\uff0c\u611f\u5230\u65e0\u9650\u60ca\u5947\uff0c\u5439\u5f39\u5f97\u7834\u7684\u82b1\u6837\u7f8e \u4eba\uff0c\u7adf\u6562\u4e00\u634b\u864e\u987b\uff1f \u3000\u3000\u6b66\u5f53\u4e24\u4f4d\u9053\u58eb\uff0c\u751f\u6050\u4e8b\u6001\u6108\u52a0\u6269\u5927\uff0c\u660e\u5929\u8fd9\u4e2a\u4f1a\u4e5f\u5c31\u88c2\u75d5\u767e\u51fa\uff0c\u6606\u4ed1\u6d3e\u5df2\u4e0e\u5ce8\u5d4b\u6d3e\u5f04\u5f97 \u6781\u4e0d\u6109\u5feb\uff0c\u90a3\u8fd9\u4e00\u6b21\u5ef6\u8058\u5404\u65b9\u9ad8\u624b\uff0c\u53cd\u800c\u4fc3\u6210\u4e86\u81ea\u76f8\u706b\u62fc\uff0c\u5f92\u52b3\u65e0\u529f\u3002\u4f46\u662f\u4efb\u4ed6\u4fe9\u820c\u4e0a\u751f \u83b2\uff0c\u53c8\u600e\u80fd\u6253\u52a8\u5929\u5c71\u4e09\u9f99\u5462\uff1f \u3000\u3000\u5929\u5c71\u4e09\u9f99\u56fa\u7136\u72c2\u6001\u903c\u4eba\uff0c\u5c1a\u672a\u660e\u7b49\u53c8\u4f55\u5c1d\u4e0d\u662f\u6c14\u7130\u51b2\u5929\uff0c\u8fd9\u79cd\u5c40\u9762\uff0c\u8c01\u4e5f\u4e0d\u80fd\u5148\u4f0f\u5f31\u5f15 \u9000\u3002 \u3000\u3000\u949f\u5929\u4ec7\u5219\u4ee5\u56db\u5e74\u6765\u529f\u592b\u5df2\u8fdb\u6b65\u4e0d\u5c11\uff0c\u81ea\u6301\u72ec\u95e8\u7edd\u6280\uff0c\u4e0d\u4fe1\u718a\u501c\u8fd8\u80fd\u5728\u4ed6\u5251\u4e0b\u8ba8\u5de7\uff0c\u4ed6\u6025 \u7206\u7684\u6027\u5b50\u5947\u7094\u7684\u8eab\u6cd5\uff0c\u5df2\u4eae\u5251\u98de\u6b65\u800c\u51fa\uff0c\u4e0d\u6599\u5374\u662f\u94c1\u80c6\u5c1a\u672a\u660e\u63a5\u4f4f\u4e86\u4ed6\u3002 \u3000\u3000\u949f\u5929\u4ec7\u5f85\u559d\u4ed6\u95ea\u5f00\uff0c\u7ed5\u6251\u718a\u501c\uff0c\u800c\u5c1a\u672a\u670b\u7ade\u4ee5\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\u7684\u624b\u6cd5\uff0c\u6325\u52a8\u4e00\u53cc\u8089\u638c\uff0c\u638c \u5f71\u5982\u96e8\uff0c\u8fce\u9762\u6251\u81f3\u3002 \u3000\u3000\u949f\u5929\u4ec7\u4ee5\u4e3a\u81ea\u5df1\u591a\u4e86\u4e00\u53e3\u950b\u5229\u7684\u5b9d\u5251\uff0c\u80dc\u4e4b\u4e0d\u6b66\uff0c\u5fd9\u5148\u7a9c\u5230\u4fa7\u9762\uff0c\u6536\u5251\u5165\u9798\uff0c\u4e5f\u4ee5\u53cc\u638c \u76f8\u654c\u3002 \u3000\u3000\u949f\u5929\u5b87\u5374\u6682\u65f6\u6536\u4f4f\u67b6\u52bf\uff0c\u4ed6\u5e76\u975e\u602f\u654c\uff0c\u53ea\u662f\u60f3\u5148\u4f30\u4e00\u4f30\u8fd9\u4e9b\u5c11\u5e74\u4eec\u7684\u4efd\u91cf\uff0c\u7a76\u7adf\u6709\u4e9b\u4ec0 \u4e48\u672c\u9886\uff01 \u3000\u3000\u949f\u5929\u4ec7\u548c\u5c1a\u672a\u660e\u4e24\u4eba\u7684\u8eab\u6d45\uff0c\u90fd\u5999\u5230\u6beb\u7aef\uff0c\u5feb\u65e0\u4f26\u6bd4\uff0c\u6b66\u5f53\u6d3e\u82cd\u7a79\u82cd\u677e\u4e24\u4f4d\u9053\u58eb\u60f3\u51fa\u624b \u62e6\u963b\u5374\u518d\u4e5f\u6765\u4e0d\u53ca\u4e86\uff0c\u53ea\u6709\u5206\u529d\u5176\u4f59\u672a\u52a8\u624b\u7684\u4eba\uff0c\u6682\u4e14\u606f\u6012\u3002 \u3000\u3000\u5c1a\u672a\u660e\u4e00\u4e0a\u624b\uff0c\u5c31\u5c55\u5f00\u585e\u5916\u98de\u82b1\u4e09\u5343\u5f0f\uff0c\u62db\u5f0f\u5947\u5e7b\u83ab\u6d4b\uff0c\u4f7f\u6606\u4ed1\u53cc\u6770\u4e0d\u7531\u54e6\u4e86\u4e00\u58f0\uff0c \u9053\uff1a\u201c\u539f\u6765\u8fd9\u5c11\u5e74\u679c\u7136\u6709\u4e9b\u6765\u5386\u5462\uff01\u201d\u540c\u6837\uff0c\u5929\u5c71\u8001\u9f99\u949f\u95ee\u5929\u4e5f\u4e0d\u7981\u795e\u60c5\u4e00\u8083\uff0c\u4ed6\u9887\u4e3a\u7231\u5b50 \u62c5\u5fc3\uff0c\u56e0\u4e3a\u949f\u5929\u4ec7\u8fd8\u6ca1\u6709\u7ec3\u6210\u9634\u715e\u624b\u529f\u3002 \u3000\u3000\u5c1a\u672a\u660e\u8fd9\u5957\u7edd\u5b66\uff0c\u4e00\u5f0f\u91cc\u5343\u53d8\u4e07\u5316\u638c\u5f71\u7f24\u7eb7\uff0c\u4e0a\u4e0b\u56db\u65b9\u5f62\u6210\u5343\u6761\u5e7b\u5f71\uff0c\u9976\u662f\u949f\u5929\u4ec7\u672c\u8eab \u529f\u592b\u4e0d\u4f4e\uff0c\u4f46\u4ed6\u90a3\u98de\u9f99\u4e03\u5f0f\u62f3\u62db\uff0c\u5374\u4e00\u70b9\u4f7f\u4e0d\u51fa\u6765\uff0c\u56f0\u4e3a\u5c1a\u672a\u660e\u5df2\u7adf\u5360\u4e86\u5148\u7740\uff0c\u4ed6\u5904\u5904\u53d7\u5236 \u4e8e\u4eba\uff0c\u843d\u5f97\u53ea\u6709\u6328\u6253\u7684\u4efd\u513f\u3002 \u3000\u3000\u6253\u5230\u540e\u6765\u4e09\u5341\u62db\u4ee5\u540e\uff0c\u949f\u5929\u4ec7\u8d39\u5c3d\u5403\u5976\u6c14\u529b\uff0c\u4e00\u5473\u8eb2\u95ea\uff0c\u6c57\u51fa\u5982\u96e8\uff0c\u82cd\u767d\u7684\u8138\u8272\u53cd\u800c\u6da8 \u51fa\u4e9b\u7d2b\u8272\u3002 \u3000\u3000\u8001\u9f99\u949f\u95ee\u5929\u5fc3\u75bc\u513f\u5b50\u53d7\u7a98\uff0c\u518d\u4e5f\u987e\u4e0d\u5f97\u4ec0\u4e48\u9053\u4e49\uff0c\u4e5f\u6697\u65bd\u8fa3\u624b\uff0c\u4f38\u51fa\u4e4c\u9ed1\u53d1\u4eae\u7684\u53f3\u638c\uff0c \u9ed1\u7b4b\u66b4\u8d77\uff0c\u628a\u5341\u5e74\u5fc3\u8840\u7ec3\u6210\u7684\u9634\u715e\u624b\uff0c\u7a81\u7136\u81ea\u4fa7\u9762\u659c\u659c\u5411\u5c1a\u672a\u660e\uff0c\u731b\u5982\u5c71\u5d29\u96f7\u9707\uff0c\u7834\u7a7a\u9707 \u54cd\uff0c\u6253\u51fa\u4e00\u8bb0\u5288\u7a7a\u638c\u3002 \u3000\u3000\u5c1a\u672a\u660e\u8ddd\u4ed6\u53d1\u638c\u4e4b\u5904\uff0c\u4e0d\u8fc7\u4e00\u4e08\u6765\u8fdc\uff0c\u6b66\u6797\u4e2d\u80fd\u5728\u8fd9\u4e48\u8fdc\u7684\u8ddd\u79bb\uff0c\u53d1\u638c\u4f24\u4eba\uff0c\u6b63\u662f\u6240\u8c13 \u9694\u5c71\u6253\u725b\u7684\u4e0a\u4e58\u529f\u529b\uff0c\u786e\u5b9e\u6ca1\u6709\u51e0\u4eba\u3002\u718a\u501c\u5929\u96f7\u884c\u529f\u5df2\u81f3\u65e0\u58f0\u65e0\u606f\u7684\u9636\u6bb5\uff0c\u4f46\u662f\u5e73\u7d20\u8fd8\u6ca1\u6709 \u70bc\u8fc7\u8fd9\u79cd\u624b\u6cd5\u3002 \u3000\u3000\u4ed6\u65e0\u610f\u8fce\u62d2\u5929\u5c71\u8001\u9f99\uff0c\u5bf9\u62cd\u4e00\u638c\uff0c\u81ea\u5df1\u4e5f\u4e0d\u61c2\u5176\u4e2d\u5965\u5999\uff0c\u4f46\u662f\u6606\u4ed1\u53cc\u6770\u5374\u662f\u6b64\u4e2d\u8001\u624b\uff0c \u4e0d\u7981\u5927\u60ca\uff0c\u4ee5\u4e3a\u5c1a\u672a\u660e\u5fc5\u906d\u6bd2\u624b\u3002 \u3000\u3000\u5355\u51ed\u5929\u5c71\u8001\u9f99\u53d1\u638c\u65f6\u624b\u4e0a\u9ed1\u5149\u8ff8\u73b0\uff0c\u53d1\u51fa\u90a3\u4e00\u79cd\u5947\u5f02\u7684\u5578\u97f3\uff0c\u8fd9\u9634\u715e\u624b\u5fc5\u7136\u6076\u6bd2\u65e0\u6bd4\uff0c \u4f46\u662f\u6606\u4ed1\u53cc\u6770\u4e5f\u6765\u4e0d\u53ca\u8d8b\u524d\u62a2\u6551\u3002\u53cd\u800c\u662f\u7389\u9762\u795e\u5251\u5e38\u6f2b\u5929\uff0c\u4e5f\u61c2\u5f97\u8fd9\u624b\u6cd5\u7684\u9634\u6bd2\uff0c\u4e0d\u7531\u56b7 \u9053\uff1a\u201c\u654f\u59b9\u5feb\u4e9b\u51fa\u624b\uff01\u201d\u53c8\u5927\u559d\u9053\uff1a\u201c\u5c1a\u4fa0\u58eb\u5feb\u5feb\u8eb2\u907f\uff0c\u949f\u8001\u5934\u9634\u715e\u624b\u4e0d\u53ef\u8f7b\u654c\uff01\u201d\u800c\u6563\u82b1 \u4ed9\u5b50\u773c\u660e\u624b\u5feb\uff0c\u4e00\u5927\u628a\u7cbe\u5999\u5947\u8be1\u7684\u94a2\u4e38\uff0c\u5df2\u6f2b\u5929\u82b1\u96e8\uff0c\u5411\u949f\u95ee\u5929\u63b7\u53bb\u3002\uff5e\u661f\u98de\u6708\u8df3\uff0c\u94f6\u5f62\u7fe9 \u7fe9\uff0c\u800c\u4e14\u56db\u9762\u516b\u65b9\uff0c\u4ee5\u4e0d\u540c\u7684\u89d2\u5ea6\uff0c\u9f50\u5411\u949f\u95ee\u5929\u90a3\u53ea\u53f3\u624b\u4e0a\u9762\u5c04\u53bb\uff0c\u5929\u5c71\u8001\u9f99\u4e0d\u5f97\u4e0d\u62bd\u4e86\u4e00 \u53e3\u6c14\uff0c\u5fc3\u4e2d\u4e00\u9707\uff0c\u5411\u540e\u5012\u9000\u4e08\u4f59\uff0c\u56e0\u4e4b\u4ed6\u53d1\u51fa\u7684\u638c\u529b\uff0c\u81ea\u7136\u662f\u5411\u540e\u4e00\u7f29\uff0c\u4e0d\u80fd\u8fbe\u5230\u5c1a\u672a\u660e\u8eab \u7554\u4e86\u3002 \u3000\u3000\u6563\u82b1\u4ed9\u5b50\u8fd9\u79cd\u5947\u5999\u7684\u624b\u6cd5\uff0c\u5929\u5c71\u8001\u9f99\u7a9c\u9000\u4e08\u4f59\uff0c\u94a2\u4e38\u8fd8\u4ece\u5730\u4e0a\u8dc3\u5c04\u8fc7\u53bb\uff0c\u51e0\u4e4e\u4f7f\u4ed6\u65e0\u6cd5 \u5e94\u4ed8\u3002 \u3000\u3000\u800c\u540c\u65f6\u949f\u5929\u4ec7\uff0c\u4e5f\u56e0\u4fa7\u9762\u94f6\u5f71\u7eb7\u9a70\uff0c\u7740\u5b9e\u6709\u4e9b\u60ca\u614c\uff0c\u88ab\u5c1a\u672a\u660e\u4e94\u6307\u63a0\u8fc7\u80a9\u5934\uff0c\u4e00\u9635\u5267 \u75db\uff0c\u4ed6\u5f3a\u54ac\u7259\u5fcd\u53d7\uff0c\u4e5f\u4e0d\u7531\u8d25\u9000\u4e0b\u53bb\uff0c\u5c1a\u672a\u660e\u6536\u4f4f\u62db\u5f0f\uff0c\u5140\u7acb\u5982\u5c71\uff0c\u6012\u559d\u9053\uff1a\u201c\u5929\u5c71\u4e09\u9f99\uff0c \u6697\u4e0b\u6bd2\u624b\uff0c\u672a\u514d\u592a\u4e0d\u5149\u660e\u78ca\u843d\uff01\u201d\u3001\u53c8\u9053\uff1a\u201c\u4efb\u4f60\u90a3\u4f4d\uff0c\u6211\u5c1a\u67d0\u518d\u5949\u966a\u4e00\u573a\uff01\u201d \u3000\u3000\u5929\u5c71\u4e09\u9f99\uff0c\u4e8c\u4fa0\u949f\u5929\u5b87\u81ea\u95ee\u4e5f\u672a\u5fc5\u80fd\u80dc\u8fc7\u5c1a\u672a\u660e\uff0c\u53ea\u6709\u671b\u7740\u7236\u4eb2\u51fa\u624b\u4e86\uff0c\u949f\u95ee\u5929\u5219\u56e0\u521a \u624d\u6563\u82b1\u4ed9\u5b50\u7edd\u6280\uff0c\u4f7f\u4ed6\u4e0d\u5bd2\u800c\u51db\uff0c\u4e00\u65f6\u75bc\u60dc\u7231\u5b50\uff0c\u6697\u4e2d\u4f24\u4eba\uff0c\u504f\u53c8\u627e\u4e86\u4e2a\u6ca1\u8da3\uff0c\u5bf9\u65b9\u4eba\u624d\u6d4e \u6d4e\uff0c\u8fd8\u4e0d\u77e5\u522b\u4eba\u662f\u4ec0\u4e48\u95e8\u8def\u3002 \u3000\u3000\u949f\u95ee\u5929\u7a7a\u6709\u4e00\u8154\u62b1\u8d1f\uff0c\u4e0d\u6599\u5374\u5728\u6b66\u5f53\u5c71\u4e0a\u5f92\u81ea\u53d6\u8fb1\u3002\u5929\u5c71\u4e09\u9f99\uff0c\u7236\u5b50\u540c\u4e00\u5014\u5f3a\u6027\u683c\uff0c\u8d62 \u4e0d\u4e86\u4eba\uff0c\u4fbf\u7acb\u5373\u5f52\u5c71\u82e6\u7ec3\uff0c\u6240\u8c13\u6709\u4ec7\u5fc5\u62a5\uff0c\u7ec8\u751f\u5fd8\u4e0d\u6389\u4e00\u9897\u829d\u9ebb\u5927\u5c0f\u7684\u8fc7\u8282\uff0c\u5e38\u4eba\u60f9\u607c\u4e86\u4e09 \u9f99\uff0c\u975e\u6b7b\u5373\u4f24\uff0c\u65e0\u4e00\u5e78\u5154\u3002 \u3000\u3000\u81f3\u4e8e\u7a76\u7adf\u6709\u4ec0\u4e48\u6076\u6027\uff0c\u5374\u4e5f\u96be\u8bf4\u3002 \u3000\u3000\u949f\u95ee\u5929\u81ea\u4fe1\u4ee5\u4ed6\u7684\u9634\u715e\u624b\uff0c\u6253\u8d25\u5c1a\u672a\u660e\u8fd8\u4e0d\u6210\u95ee\u9898\uff0c\u4f55\u51b5\u5c1a\u672a\u660e\u8fd8\u5728\u90a3\u91cc\u53eb\u9635\uff0c\u4ed6\u607c\u7f9e \u6210\u6012\uff0c\u970d\u5730\u7eb5\u8eab\u800c\u524d\uff0c\u5411\u5c1a\u672a\u660e\u559d\u9053\uff1a\u201c\u59d3\u5c1a\u7684\u5c0f\u5b50\uff01\u63a5\u8001\u592b\u51e0\u62db\uff0c\u4f60\u8fd9\u5c0f\u5b50\u672a\u514d\u592a\u72c2\u5984 \u4e86\uff01\u201d\u4ed6\u5df2\u5fcd\u4e0d\u4f4f\u4e00\u8154\u5fff\u6012\u3002 \u3000\u3000\u4f46\u662f\u6b66\u5f53\u4e24\u4f4d\u9053\u58eb\uff0c\u600e\u80af\u8ba9\u53cc\u65b9\u518d\u6253\u4e0b\u53bb\uff0c\u90a3\u53ef\u5c31\u8981\u53d8\u6210\u62fc\u547d\u4e86\uff0c\u82cd\u7a79\u82cd\u677e\u53cc\u53cc\u6b7b\u62d6\u6d3b \u62c9\uff0c\u62e6\u4f4f\u4e86\u949f\u95ee\u5929\uff0c\u6bd4\u5c71\u4e0b\u718a\u501c\u5bf9\u6d41\u4e91\u5e08\u592a\uff0c\u6606\u4ed1\u6d3e\u4e0e\u5ce8\u5d4b\u6d3e\u820c\u5251\u5507\u67aa\u90a3\u5e55\uff0c\u8fd8\u8981\u6076\u52a3\u6570 \u500d\u3002 \u3000\u3000\u6606\u4ed1\u53cc\u6770\u79f0\u8d5e\u4e86\u5c1a\u672a\u670b\u4e24\u53e5\uff0c\u4e5f\u7acb\u523b\u628a\u718a\u501c\u5c1a\u672a\u660e\u529d\u56de\u5385\u4e0a\uff0c\u4e0d\u8ba9\u518d\u6253\u4e0b\u53bb\uff0c\u949f\u95ee\u5929\u621f \u6012\u53f1\u9053\uff1a\u201c\u59d3\u5c1a\u7684\u5c0f\u5b50\uff0c\u8fd8\u6709\u718a\u501c\uff0c\u8eb2\u4e86\u4eca\u5929\u3001\u8eb2\u4e0d\u4e86\u660e\u671d\uff01\u660e\u5929\u4f1a\u7f62\uff0c\u5c31\u5728\u7389\u771f\u9662\u5916\uff0c\u4f5c \u4e2a\u6700\u540e\u4e86\u65ad\uff01\u201d \u3000\u3000\u718a\u501c\u70b9\u70b9\u5934\u8bf4\uff1a\u201c\u5f88\u597d\uff0c\u4e0d\u5e72\u5c1a\u8d24\u5f1f\u7684\u4e8b\uff0c\u6211\u718a\u501c\u4e00\u4eba\u63a5\u7740\u4f60\uff01\u60f3\u4e0d\u5230\u5929\u5c71\u4e09\u9f99\uff0c\u7adf\u662f \u86ee\u4e0d\u8bb2\u7406\u7684\u4eba\uff01\u201d \u3000\u3000\u82cd\u7a79\u82cd\u677e\u518d\u4e09\u82e6\u529d\uff0c\u65b9\u628a\u8fd9\u573a\u98ce\u6ce2\u6682\u65f6\u7ed3\u675f\u3002 \u3000\u3000\u4e8e\u662f\u8fd9\u51e1\u4f4d\u4fa0\u58eb\u53c8\u589e\u52a0\u4e86\u4e00\u9879\u8bdd\u9898\uff0c\u5c31\u662f\u5929\u5c71\u4e09\u9f99\u7684\u4e3a\u4eba\u884c\u4e8b\uff0c\u4ee5\u53ca\u4ed6\u4eec\u6240\u70bc\u7684\u9634\u715e\u638c \u6027\u80fd\u5a01\u529b\u7b49\u7b49\u3002 \u3000\u3000\u718a\u501c\u56e0\u501a\u5929\u5251\u6709\u4e86\u7740\u843d\uff0c\u5fc3\u60c5\u7a0d\u4e3a\u5f00\u6717\uff0c\u4ed6\u4eec\u53c8\u8c08\u53ca\u8d74\u5ce8\u5d4b\u4e4b\u7ea6\uff0c\u6563\u82b1\u4ed9\u5b50\u5a07\u7b11\u8bf4\uff1a \u201c\u8001\u79c3\u5a46\u53e3\u6c14\u4e0d\u5c0f\uff0c\u6211\u5012\u8981\u53bb\u770b\u770b\u4ed6\u4eec\u5ce8\u5d4b\u6d3e\u5de2\u7a74\uff0c\u7b97\u5f97\u4e0a\u9f99\u6f6d\u864e\u7a74\uff1f\u201d\u7389\u9762\u795e\u5251\u8f83\u4e3a\u6301 \u91cd\uff0c\u4ed6\u70b9\u70b9\u5934\u8bf4\uff1a\u201c\u6211\u4eec\u81ea\u7136\u8981\u966a\u718a\u8001\u5f1f\u53bb\u4e00\u8d9f\uff0c\u8d4f\u73a9\u4e00\u4e0b\u5ce8\u5d4b\u80dc\u666f\uff0c\u4f46\u51ed\u6606\u4ed1\u53cc\u6770\u548c\u718a\u8001 \u5f1f\u7684\u8eab\u624b\uff0c\u5012\u7528\u4e0d\u7740\u522b\u4eba\u5e2e\u52a9\uff0c\u4f46\u4e0d\u77e5\u718a\u8001\u5f1f\u5b9a\u4e8e\u4f55\u65f6\u524d\u5f80\uff0c\u201d\u8fd9\u53ef\u628a\u718a\u501c\u7ed9\u95ee\u4f4f\u4e86\uff0c\u4ed6\u4e0d \u80fd\u62ff\u51c6\u4f55\u65f6\u80fd\u627e\u7740\u590f\u82b8\uff0c\u718a\u501c\u7565\u4e00\u6c89\u541f\uff0c\u5e38\u6f2b\u5929\u5475\u5475\u5927\u7b11\u9053\uff1a\u201c\u6211\u7adf\u628a\u8001\u5f1f\u627e\u82b8\u59b9\u7684\u4e8b\u5fd8 \u4e86\uff01\u4e0d\u59a8\u628a\u65f6\u95f4\u62d6\u8fdc\u4e00\u70b9\uff0c\u611a\u5144\u56de\u751c\u751c\u8c37\u4e00\u884c\uff0c\u7136\u540e\u675f\u88c5\u897f\u4e0a\uff0c\u53ea\u8981\u5929\u9634\u6559\u4e0d\u518d\u8822\u52a8\uff0c\u611a\u5144 \u770b\u4f3c\u65e0\u9700\u903c\u5f97\u4ed6\u4eec\u633a\u800c\u8d70\u9669\u3002\u201d\u6606\u4ed1\u53cc\u6770\u548c\u4ed6\u4eec\u610f\u89c1\u76f8\u5dee\uff0c\u8ba4\u4e3a\u4ee5\u4ece\u901f\u527f\u706d\u4e3a\u5b89\u3002 \u3000\u3000\u718a\u501c\u6b63\u5728\u8003\u8651\u8fd9\u8bb8\u591a\u95ee\u9898\uff0c\u7a81\u7136\u9662\u95e8\u4e2d\u8d70\u8fdb\u6765\u7384\u51a0\u7fbd\u8863\u7684\u98de\u9e64\u5b50\uff0c\u8fd8\u6709\u4e00\u8001\u4e00\u5c11\u4e24\u4f4d\u8896 \u8863\u548c\u5c1a\uff0c\u5e76\u80a9\u800c\u5165\u3002 \u3000\u3000\u718a\u501c\u770b\u90a3\u5e74\u7ea6\u56db\u5341\u7684\u8910\u8863\u50e7\u4eba\uff0c\u9762\u76ee\u5341\u5206\u719f\u6089\uff0c\u53ea\u4e00\u65f6\u60f3\u4e0d\u8d77\u662f\u8c01\u3002\u800c\u90a3\u4f4d\u8001\u50e7\uff0c\u9053\u8c8c \u5cb8\u7136\uff0c\u76ee\u5c04\u5947\u5149\uff0c\u663e\u7136\u662f\u4e00\u4f4d\u5185\u529f\u5f88\u9187\u539a\u7684\u4eba\u7269\u3002 \u3000\u3000\u718a\u501c\u518d\u4e00\u7ec6\u770b\uff0c\u8111\u6d77\u4e2d\u6d6e\u73b0\u4e86\u56db\u5e74\u524d\u7684\u5f80\u4e8b\uff0c\u90a3\u4e0d\u662f\u9e23\u8fdc\u9556\u5c40\u6258\u4ed6\u5317\u4e0a\u4fdd\u62a4\u4f55\u9996\u4e4c\uff0c\u540c \u884c\u7684\u5434\u8bcf\u4e91\u9556\u5934\u4e48\uff1f \u3000\u3000\u98de\u9e64\u5b50\u5df2\u9080\u4e86\u4e8c\u50e7\uff0c\u4e0a\u5f97\u5385\u6765\u3002 \u3000\u3000\u98de\u9e64\u5b50\u5148\u4f5c\u4e86\u4e00\u756a\u5ba2\u5957\uff0c\u5e76\u56e0\u70b9\u82cd\u53cc\u4fa0\u3001\u6606\u4ed1\u53cc\u6770\u3001\u718a\u501c\u3001\u5c1a\u672a\u660e\uff0c\u51fa\u5c18\u5251\u5ba2\u5144\u59b9\u7684\u8385 \u4e34\uff0c\u5f15\u4e3a\u83ab\u5927\u8363\u5e78\u3002 \u3000\u3000\u6b66\u5f53\u6d3e\u5bf9\u4e8e\u5ba2\u4eba\uff0c\u662f\u5f6c\u5f6c\u6709\u793c\u7684\u3002 \u3000\u3000\u98de\u9e64\u5b50\u4ecb\u7ecd\u4e8c\u50e7\uff0c\u8bf4\u662f\uff1a\u201c\u5173\u5916\u5e3d\u513f\u5c71\u5927\u96c4\u6cd5\u5e08\uff0c\u548c\u4ed6\u7684\u9ad8\u8db3\u8bcf\u4e91\u548c\u5c1a\u3002\u201d\u81ea\u7136\u53ef\u4ee5\u5b9a \u51c6\u662f\u5434\u8bcf\u4e91\u4e86\u3002 \u3000\u3000\u8bcf\u4e91\u548c\u5c1a\u8d8b\u524d\u4e0e\u718a\u501c\u4e92\u76f8\u63e1\u624b\uff0c\u6b22\u7136\u9053\u6545\uff0c\u718a\u501c\u60ca\u8bb6\u4ed6\u4e3a\u4ec0\u4e48\u8981\u62ab\u5243\u51fa\u5bb6\uff0c\u5434\u8bcf\u4e91\u5374\u6709 \u4ed6\u7684\u4e00\u756a\u6614\u8877\u3002 \u3000\u3000\u9556\u8d27\u8f7b\u6613\u5730\u843d\u4eba\u5929\u9634\u6559\u4eba\u4e4b\u624b\uff0c\u6700\u53ef\u803b\u7684\u662f\u7531\u4e8e\u7c89\u9762\u82cf\u79e6\u738b\u667a\u9011\u7684\u5356\u8eab\u6295\u9760\uff0c\u9556\u5c40\u540d\u8a89 \u626b\u5730\u4e86\uff0c\u5434\u8bcf\u4e91\u662f\u65e0\u6cd5\u518d\u5403\u8fd9\u4e00\u884c\u996d\uff0c\u53c8\u5728\u4e34\u57ce\u4e00\u5e26\uff0c\u9047\u89c1\u65e0\u6570\u6b66\u6797\u9ad8\u624b\uff0c\u81ea\u5df1\u8d8a\u53d1\u611f\u5230\u6e3a \u5c0f\u5fae\u4e0d\u8db3\u9053\u3002 \u3000\u3000\u4ed6\u672c\u60f3\u4ece\u6b64\u9690\u59d3\u57cb\u540d\uff0c\u4e00\u751f\u518d\u4e0d\u63d0\u6b66\u6280\u4e8c\u5b57\uff0c\u5374\u65e0\u610f\u4e2d\u9047\u89c1\u4e86\u5173\u5916\u9690\u4e16\u9ad8\u624b\u5927\u96c4\u6cd5\u5e08\uff0c \u7ec3\u6b66\u529f\u7684\u4eba\u662f\u5f97\u4e86\u673a\u4f1a\u51b3\u4e0d\u4f1a\u653e\u677e\u7684\uff0c\u5927\u96c4\u6cd5\u5e08\u4e00\u751f\u7edd\u6280\u672a\u5f97\u4f20\u4eba\uff0c\u770b\u4e0a\u4e86\u5434\u8bcf\u4e91\uff0c\u4e8e\u662f\u4e3a \u4ed6\u62ab\u5243\uff0c\u4f5c\u4e3a\u8863\u94b5\u4f20\u4eba\u3002 \u3000\u3000\u56db\u5e74\u4e4b\u540e\uff0c\u5434\u8bcf\u4e91\u7684\u6b66\u529f\uff0c\u786e\u5b9e\u6709\u4e86\u957f\u8db3\u7684\u8fdb\u6b65\uff0c\u800c\u5927\u96c4\u6cd5\u5e08\u95fb\u77e5\u5929\u9634\u6559\u5174\u8d77\uff0c\u4ed6\u5ac9\u6076 \u5982\u4ec7\uff0c\u5f53\u5e74\u527f\u706d\u65e0\u9634\u6559\uff0c\u4ed6\u4e5f\u662f\u6700\u51fa\u529b\u7684\u4eba\uff0c\u5c82\u80fd\u5bb9\u4ed6\u4eec\u518d\u5ea6\u6d82\u70ad\u751f\u7075\uff0c\u9042\u643a\u5f92\u5357\u4e0b\uff0c\u8bbf\u67e5 \u4e8c\u6b21\u91cd\u5174\u7684\u5929\u9634\u6559\u7684\u52a3\u8ff9\u3002 \u3000\u3000\u4ed6\u5e08\u5f92\u81ea\u5f90\u5dde\u5357\u4e0b\uff0c\u8fd9\u65f6\u5317\u9053\u4e0a\u82f1\u96c4\u3001\u4e03\u6bd2\u4e66\u751f\u5510\u7fbd\u3001\u6d77\u9f99\u738b\u8d75\u4f69\u4fa0\u3001\u4e94\u864e\u65ad\u95e8\u5200\u5f6d\u5929 \u5bff\u3001\u52b3\u5c71\u53cc\u9e64\u3001\u9ec4\u6cb3\u4e00\u602a\u90fd\u5df2\u88ab\u5929\u9634\u6559\u641c\u7f57\u52fe\u7ed3\uff0c\u5c1a\u672a\u660e\u5d1b\u5174\u4e24\u6cb3\u7eff\u6797\u9053\uff0c\u4ed6\u6240\u80fd\u9886\u5bfc\u7684\u5df2 \u662f\u4e00\u4e9b\u4e8c\u4e09\u6d41\u89d2\u8272\u4e86\u3002 \u3000\u3000\u5927\u96c4\u6cd5\u5e08\u5728\u626c\u5dde\u4e0e\u98de\u9e64\u5b50\u76f8\u9047\uff0c\u6b66\u5f53\u6d3e\u4eba\u662f\u5206\u6279\u56db\u51fa\u6492\u5e16\u5b50\u7684\uff0c\u800c\u98de\u9e64\u5b50\u904d\u5386\u82cf\u676d\u6c5f\u5de6 \u5404\u5730\uff0c\u9042\u4e0e\u5927\u96c4\u6cd5\u5e08\u5e08\u5f92\u7ed3\u4f34\u800c\u8fd4\u3002 \u3000\u3000\u5434\u8bcf\u4e91\u548c\u718a\u501c\u6bb7\u6bb7\u8bdd\u65e7\uff0c\u4ed6\u8198\u4e86\u5728\u5750\u8bf8\u4fa0\u4e00\u773c\uff0c\u53f9\u606f\u4e00\u58f0\u8bf4\uff1a\u201c\u6211\u4e0d\u60f3\u738b\u667a\u9011\u53d8\u8282\u51fa\u5356 \u9556\u5c40\uff0c\u6295\u8eab\u5929\u9634\u6559\u4e0b\uff0c\u518d\u78b0\u9762\u5c31\u662f\u4ec7\u654c\u52bf\u5982\u6c34\u706b\u4e86\uff01\u201d\u4ed6\u53c8\u4f7f\u4e2a\u773c\u8272\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u6211\u4eec\u627e\u4e2a \u50fb\u9759\u5730\u65b9\u4e00\u8c08\u5427\uff01\u201d \u3000\u3000\u5434\u8bcf\u4e91\u4e00\u8138\u91cd\u8981\u800c\u673a\u5bc6\u7684\u795e\u6c14\uff0c\u4f7f\u718a\u501c\u5927\u4e3a\u5403\u60ca\u3002 \u3000\u3000\u4e24\u4e2a\u9042\u6682\u65f6\u544a\u9000\uff0c\u643a\u624b\u81f3\u89d2\u843d\u4e00\u95f4\u4e39\u623f\u91cc\u3002 \u3000\u3000\u718a\u501c\u4e0d\u77e5\u4ed6\u8981\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u552f\u4e00\u5e0c\u671b\u7684\u5c31\u662f\u4ed6\u80fd\u591f\u62a5\u544a\u82b8\u59b9\u59b9\u7684\u884c\u8e2a\uff0c\u800c\u7ed3\u679c\u5374\u662f\u53e6\u4e00\u4ef6 \u4f7f\u4ed6\u60ca\u559c\u7684\u4e8b\u3002\u718a\u501c\u7531\u91d1\u9675\u57ce\u95ef\u5165\u9e23\u8fdc\u9556\u5c40\uff0c\u8bbf\u95ee\u4ec7\u4eba\u5b9d\u9a6c\u795e\u97ad\u8428\u5929\u9aa5\uff0c\u7c89\u9762\u82cf\u79e6\u7389\u667a\u9011\u662f \u552f\u4e00\u8428\u5929\u9aa5\u7684\u5fc3\u8179\uff0c\u53ea\u662f\u738b\u667a\u9011\u4e0d\u80af\u6cc4\u6f0f\u51fa\u6765\u795e\u97ad\u5927\u4fa0\u7684\u884c\u85cf\uff0c\u53cd\u800c\u4e58\u673a\u5229\u7528\u8fd9\u4e2a\u521d\u51fa\u8305\u5e90 \u7684\u5c0f\u4f19\u5b50\uff0c\u66ff\u4ed6\u7ecf\u5386\u6c5f\u6e56\u4e0a\u6781\u9669\u6076\u7684\u98ce\u6ce2\u3002 \u3000\u3000\u5434\u8bcf\u4e91\u662f\u4e2a\u8840\u6027\u6c49\u5b50\uff0c\u4e5f\u5f88\u540c\u60c5\u718a\u501c\u3002 \u3000\u3000\u4e24\u4eba\u8fdb\u5165\u623f\u5185\uff0c\u5434\u8bcf\u4e91\u6168\u7136\u8bf4\u9053\uff1a\u201c\u6211\u8fd9\u51e0\u5e74\u5728\u5173\u5916\u5b66\u827a\uff0c\u98ce\u95fb\u843d\u65e5\u9a6c\u573a\u7684\u4e3b\u4eba\u866c\u987b \u5ba2\uff0c\u662f\u4e00\u4f4d\u9690\u59d3\u7684\u602a\u6770\uff0c\u7ec8\u4e8e\u6709\u4e00\u6b21\u5f97\u5230\u673a\u7f18\uff0c\u7aa5\u7834\u4e86\u4ed6\u7684\u5e90\u5c71\u771f\u9762\u76ee\uff0c\u4f60\u77e5\u9053\u8fd9\u4f4d\u5728\u5173\u5916 \u95fb\u540d\u8d6b\u8d6b\u7684\u602a\u6770\u662f\u4ec0\u4e48\u4eba\u5417\uff1f\u201d \u3000\u3000\u718a\u501c\u6447\u6447\u5934\uff0c\u4f46\u4ed6\u5374\u77e5\u9053\u866c\u987b\u5ba2\u5c31\u662f\u6240\u7231\u7684\u82b8\u59b9\u59b9\u7684\u7236\u4eb2\u3002\u5434\u8bcf\u4e91\u4e49\u6124\u586b\u81ba\u7684\u8bf4\uff1a\u201c\u5341 \u4e09\u5e74\u524d\u7684\u4e8b\u4e86\uff0c\u8428\u5929\u7ffc\u5bf9\u4e0d\u4f4f\u6b66\u6797\u670b\u53cb\uff0c\u6740\u5bb3\u4e86\u65e5\u6708\u53cc\u5251\uff0c\u4f7f\u9556\u5c40\u91cc\u670b\u53cb\uff0c\u4eba\u4eba\u7686\u4fa7\u76ee\u5bd2 \u5fc3\uff01\u201d \u3000\u3000\u53c8\u5389\u58f0\u9053\uff1a\u201c\u8c01\u77e5\u4ed6\u7adf\u505a\u4e86\u843d\u65e5\u9a6c\u573a\u7684\u5173\u5916\u67ad\u96c4\uff01\u201d \u3000\u3000\u8fd9\u4e00\u53e5\u77f3\u7834\u5929\u60ca\u98de\u6765\u7684\u559c\u8baf\uff0c\u4f7f\u718a\u501c\u9707\u9a87\u5f97\u7b54\u4e0d\u4e0a\u8bdd\u6765\u3002 \u3000\u3000\u4ed6\u8fd9\u65f6\u70ed\u6cea\u76c8\u7736\uff0c\u8111\u6d77\u91cc\u8fd4\u56de\u91d1\u9675\u57ce\u5916\u6234\u53d4\u53d4\u4e34\u6b7b\u90a3\u4e00\u5e55\uff0c\u6570\u5e74\u6765\uff0c\u4ed6\u4e00\u76f4\u6ca1\u6709\u6562\u5fd8\u6000 \u7684\u5927\u4e8b\uff0c\u7ec8\u4e8e\u5230\u4e86\u773c\u524d\uff0c\u6b63\u662f\u4ed6\u66ff\u6234\u53d4\u53d4\u4f38\u62a5\u8840\u4ec7\u7684\u826f\u673a\uff01 \u3000\u3000\u53ef\u662f\u718a\u501c\u5185\u5fc3\u786e\u5b9e\u8d77\u4e86\u9519\u7efc\u590d\u6742\u7684\u53d8\u5316\uff0c\u8fd9\u4e0d\u662f\u5c40\u5916\u4eba\u6240\u80fd\u628a\u5b83\u63cf\u7ed8\u51fa\u6765\u7684\u3002 \u3000\u3000\u773c\u524d\u653e\u7740\u4e09\u6869\u987b\u4ed6\u7acb\u5373\u53bb\u529e\u7684\u5927\u4e8b\uff1a\u627e\u5bfb\u590f\u82b8\uff0c\u5ce8\u5d4b\u8d74\u7ea6\u593a\u56de\u501a\u5929\u5251\uff0c\u4e0e\u627e\u90a3\u5b9d\u9a6c\u795e\u97ad \u62a5\u96ea\u6d77\u6df1\u4ec7\u3002 \u3000\u3000\u718a\u501c\u4e0d\u662f\u4e3a\u8fd9\u4e09\u4ef6\u4e8b\u5b70\u5148\u5b70\u540e\uff0c\u65e0\u6cd5\u51b3\u5b9a\u800c\u7126\u8651\uff0c\u5374\u662f\u5343\u4e07\u6599\u60f3\u4e0d\u5230\u590f\u82b8\u7adf\u662f\u5927\u4ec7\u4eba\u7684 \u5973\u513f\uff0c\u5c06\u6765\u662f\u591a\u4e48\u523a\u4f24\u82b8\u59b9\u59b9\u7684\u82b3\u5fc3\uff01\u51b5\u4e14\u518d\u60f3\u548c\u5979\u7ed3\u5408\uff0c\u662f\u5426\u53ef\u80fd\uff1f\u6069\u6069\u6028\u6028\uff0c\u513f\u5973\u60c5\u957f \u82f1\u96c4\u6c14\u77ed\uff0c\u718a\u501c\u6bd5\u7adf\u4e0d\u80fd\u5927\u4e0a\u5fd8\u60c5\u554a\uff01 \u3000\u3000\u53c8\u52a0\u4e0a\u590f\u82b8\u76ee\u524d\u884c\u8e2a\u98d8\u5ffd\uff0c\u5f88\u53ef\u80fd\u6295\u5165\u5929\u9634\u6559\u4e2d\uff0c\u4e00\u6735\u767d\u83b2\u82b1\u65e0\u4e0a\u9ad8\u8d35\u7684\u6c14\u8d28\uff0c\u8ba9\u5b83\u9677 \u5165\u6c61\u79fd\u800c\u4e0d\u80fd\u81ea\u62d4\uff0c\u53c8\u662f\u4f55\u7b49\u6b8b\u9177\u800c\u75db\u5fc3\u7684\u4e8b\u3002 \u3000\u3000\u718a\u501c\u5c3d\u7ba1\u5185\u5fc3\u4eff\u60f6\u3001\u714e\u71ac\u3001\u7126\u8651\uff0c\u79cd\u79cd\u9178\u751c\u8f9b\u8fa3\u7684\u6ecb\u5473\uff0c\u4f7f\u4ed6\u9677\u5165\u4e00\u79cd\u65e0\u6cd5\u6446\u8131\u7684\u82e6\u607c \u91cc\uff0c\u4f46\u662f\u4ed6\u4ecd\u6614\u7b11\u7740\u5411\u5434\u8bcf\u4e91\u9053\u8c22\uff0c\u8c22\u4ed6\u5173\u6000\u548c\u76db\u60c5\uff0c\u4ee5\u575a\u51b3\u5982\u5c71\u7684\u53e3\u6c14\u8bf4\uff1a\u201c\u718a\u501c\u5982\u4e0d\u5728 \u6700\u77ed\u671f\u5185\uff0c\u5b8c\u9042\u590d\u4ec7\u5fc3\u613f\uff0c\u4f55\u4ee5\u5bf9\u661f\u6708\u53cc\u5251\u5728\u5929\u4e4b\u7075\uff1a\u5434\u5927\u54e5\uff0c\u6211\u7edd\u4e0d\u628a\u4f60\u4eca\u65e5\u8bf4\u7684\u8bdd\uff0c\u6cc4 \u6f0f\u51fa\u53bb\uff0c\u4f7f\u5927\u54e5\u6709\u5931\u5bf9\u4e8e\u8428\u5929\u9aa5\u7684\u60c5\u8c0a\uff01\u201d \u3000\u3000\u5434\u8bcf\u4e91\u9053\uff1a\u201c\u8001\u5f1f\u8fd9\u8bdd\u662f\u591a\u4f59\u7684\uff0c\u8428\u5929\u9aa5\u8d1f\u5fc3\u4e0d\u4e49\uff0c\u6211\u5434\u8bcf\u4e91\u4e5f\u975e\u5e38\u75db\u6068\u4ed6\uff01\u8fd8\u6709\u8001\u5f1f \u987b\u591a\u52a0\u8003\u8651\u7684\uff0c\u843d\u65e5\u9a6c\u573a\u4e0a\u5df2\u51fa\u73b0\u5929\u9634\u6559\u4eba\u8e2a\u8ff9\uff0c\u5f88\u53ef\u80fd\u8428\u5929\u9aa5\u5df2\u548c\u5929\u9634\u6559\u4eba\uff0c\u642d\u4e0a\u4e86\u7ebf\uff0c \u62a5\u4ec7\u7684\u4e8b\u4e0d\u514d\u987b\u591a\u8d39\u5468\u6298\u4e86\u3002\u201d \u3000\u3000\u718a\u501c\u6168\u7136\u8bf4\uff1a\u201c\u53ea\u8981\u6211\u6653\u5f97\u4ed6\u5728\u54ea\u91cc\uff0c\u5c31\u662f\u706b\u5751\uff0c\u6211\u4e5f\u8981\u8df3\u8fdb\u53bb\uff01\u548c\u4ed6\u4e00\u62fc\uff01\u201d\u4ed6\u4fe9\u53c8 \u4e92\u8c08\u522b\u540e\u7684\u60c5\u5f62\uff0c\u5306\u5306\u8fd4\u56de\u5385\u4e0a\uff0c\u4e0e\u4f17\u4eba\u6b22\u805a\u3002 \u3000\u3000\u5927\u96c4\u6cd5\u5e08\u7684\u6027\u683c\uff0c\u7ade\u975e\u5e38\u7206\u70c8\uff0c\u4ed6\u628a\u4e8c\u4e09\u5341\u5e74\u524d\u7684\u592d\u9634\u6559\u4eba\uff0c\u89c6\u4e3a\u6bd2\u86c7\u731b\u517d\uff0c\u800c\u4eca\u5728\u7126 \u5f02\u884c\u592b\u5987\u9886\u5bfc\u4e0b\u7684\u5929\u9634\u6559\uff0c\u4ed6\u8ba4\u4e3a\u662f\u6b7b\u7070\u590d\u71c3\u4e0d\u582a\u4e00\u51fb\u7684\uff0c\u4f46\u662f\u7ecf\u8fc7\u98de\u9e64\u5b50\u53d9\u8ff0\u5929\u9634\u6559\u4eba\u5077 \u88ad\u6b66\u5f53\uff0c\u5b9e\u529b\u6781\u4e3a\u96c4\u539a\u65f6\uff0c\u4f17\u4eba\u65b9\u77e5\u9053\u95ee\u9898\u5e76\u4e0d\u662f\u90a3\u4e48\u7b80\u5355\uff0c\u5f88\u53ef\u80fd\u5929\u9634\u6559\u8fd8\u7ed3\u5408\u4e86\u4e0d\u5c11\u5389 \u5bb3\u7684\u9b54\u5934\uff01 \u3000\u3000\u8fd9\u4e00\u4e2a\u4e0b\u5348\uff0c\u4ed6\u4eec\u90fd\u6d88\u78e8\u5728\u8ba8\u8bba\u8fd9\u4ef6\u5927\u4e8b\u4e0a\u9762\u3002 \u3000\u3000\u4e1c\u65b9\u7075\u5bf9\u4e8e\u718a\u501c\uff0c\u672c\u60f3\u8868\u660e\u4ed6\u7231\u6155\u6731\u82e5\u5170\u7684\u5fc3\u4e8b\uff0c\u4f46\u662f\u5374\u53c8\u8bf4\u4e0d\u51fa\u53e3\uff0c\u718a\u501c\u6700\u8fd1\u53c8\u6ca1\u6709 \u89c1\u8fc7\u82e5\u5170\uff0c\u66f4\u4e0d\u77e5\u6089\u4e24\u4eba\u95f4\u4ea7\u751f\u4e86\u60c5\u612b\uff0c\u5728\u4e1c\u65b9\u7075\u63d0\u8d77\u82e5\u5170\u5728\u98de\u7075\u5821\u5b89\u5c45\u65e0\u6059\u65f6\uff0c\u4ed6\u70ed\u8bda\u5730 \u8fde\u8fde\u81f4\u8c22\u3002 \u3000\u3000\u7c89\u8776\u4e1c\u65b9\u82f1\u4e5f\u5939\u5728\u4e2d\u95f4\u3002\u5f88\u5927\u65b9\u4ed6\u8bf4\u7b11\uff0c\u53ef\u538c\u7684\u5ce8\u5d4b\u8c37\u5c0f\u9759\u4e5f\u968f\u4f34\u5728\u5979\u8eab\u65c1\uff0c\u6240\u4ee5\u8fd9\u56db \u4eba\u867d\u7136\u907f\u5f00\u4e86\u4f17\u4eba\uff0c\u4e8e\u659c\u9633\u4e00\u62b9\u65f6\uff0c\u5728\u6e05\u6cc9\u78a7\u6811\u4e4b\u95f4\uff0c\u6d41\u8fde\u95f2\u6b65\uff0c\u800c\u7ec8\u90fd\u6ca1\u6709\u4e00\u4e2a\u8f83\u597d\u7684\u673a \u4f1a\uff0c\u8bf4\u4e00\u4e24\u53e5\u8bdd\u3002 \u3000\u3000\u81ea\u7136\u718a\u501c\u662f\u6101\u80a0\u4e5d\u56de\uff0c\u6323\u624e\u7740\u966a\u4e1c\u65b9\u7075\u5144\u59b9\u8bf4\u8bdd\uff0c\u5185\u5fc3\u4f3c\u4e4e\u8f7b\u677e\uff0c\u800c\u5b9e\u9645\u4e0a\u662f\u6c89\u91cd\u5598\u4e0d \u51fa\u4e00\u53e3\u6c14\u6765\u3002 \u3000\u3000\u4ed6\u4e0e\u4e1c\u65b9\u745b\u95f4\uff0c\u662f\u6ca1\u6709\u4ec0\u4e48\u62d8\u5fcc\u7684\uff0c\u56e0\u4e3a\u718a\u501c\u5e76\u6ca1\u6709\u4ec0\u4e48\u5fc3\u4e8b\uff0c\u718a\u501c\u81ea\u7136\u975e\u5e38\u5927\u65b9\uff0c\u800c \u7c89\u8776\u5219\u6bd4\u4ed6\u5927\u4e00\u4e24\u5c81\uff0c\u82b3\u5fc3\u7262\u7262\u7cfb\u5728\u718a\u501c\u8eab\u4e0a\uff0c\u5df2\u7ecf\u56db\u5e74\u591a\u4e86\uff0c\u82e5\u975e\u6709\u8c37\u5c0f\u9759\u5728\u65c1\uff0c\u5979\u53ef\u8981 \u63a7\u5236\u4e0d\u4f4f\u5feb\u5954\u653e\u7684\u611f\u60c5\u4e86\u3002 \u3000\u3000\u7537\u5973\u4e4b\u95f4\u7684\u4e8b\u662f\u6781\u7aef\u5fae\u5999\u7684\uff0c\u4e45\u522b\u91cd\u9022\u4e4b\u4e0b\uff0c\u90a3\u4e00\u8154\u60f3\u5410\u51fa\u6765\u7684\u8bdd\uff0c\u5f80\u5f80\u53d8\u4e3a\u65e0\u8bdd\u53ef \u8bf4\uff0c\u4e8e\u662f\u7075\u7280\u4e00\u70b9\u5c31\u5b8c\u5168\u663e\u73b0\u5728\u4e00\u53cc\u7738\u5b50\u4e4b\u4e2d\uff0c\u4e0d\u4f46\u7c89\u8776\u662f\u542b\u60c5\u8109\u8109\uff0c\u53ea\u662f\u78b0\u4e0a\u718a\u501c\u7684\u76ee \u5149\uff0c\u5c31\u9732\u51fa\u65e0\u9650\u5149\u8f89\uff0c\u795e\u79d8\u7684\u610f\u5473\u662f\u975e\u4e2a\u4e2d\u4eba\u4e3a\u80fd\u9886\u4f1a\u7684\uff0c\u800c\u8c37\u5c0f\u9759\u66f4\u6bd4\u5979\u662f\u8868\u73b0\u5f97\u9732\u9aa8\u4e00 \u70b9\u3002 \u3000\u3000\u4e1c\u65b9\u7075\u662f\u6545\u610f\u7528\u8bdd\u9898\u7f20\u4f4f\u718a\u501c\uff0c\u81ea\u7136\u4ed6\u751a\u81f3\u6709\u4e9b\u8fc7\u5206\uff0c\u90a3\u51b7\u6de1\u7684\u7a0b\u5ea6\u52a0\u4e8e\u8c37\u5c0f\u9759\u6295\u6765\u7684 \u773c\u6ce2\uff0c\u51e0\u4e4e\u4f7f\u8c37\u5c0f\u9759\u4f24\u900f\u4e86\u82b3\u5fc3\uff0c\u4f46\u662f\u5979\u8fd8\u662f\u4e0d\u5fcd\u79bb\u53bb\uff0c\u7c89\u8776\u5acc\u5979\u4e0d\u81ea\u77e5\u8da3\uff0c\u4e3a\u4f55\u4e0d\u8d70\u5f00\uff0c \u800c\u975e\u5728\u4e2d\u95f4\u5939\u7f20\u5462\uff1f \u3000\u3000\u540c\u6837\u8c37\u5c0f\u9759\u4e5f\u5df4\u4e0d\u5f97\u718a\u501c\u81ea\u5df1\u8bc6\u76f8\uff0c\u9000\u51fa\u8fd9\u4e2a\u573a\u5408\u3002 \u3000\u3000\u8c01\u4e5f\u4e0d\u613f\u63d0\u8bae\u65e9\u4e9b\u56de\u53bb\uff0c\u76f4\u81f3\u534a\u8f6e\u660e\u6708\u659c\u6302\u5728\u4e24\u5cf0\u7f3a\u53e3\uff0c\u4f9d\u7136\u5a13\u5a13\u5fd8\u8fd4\uff0c\u53ef\u662f\u718a\u501c\u53ea\u662f \u4fe1\u53e3\u916c\u9162\uff0c\u7adf\u4e0d\u77e5\u4ed6\u81ea\u5df1\u8bf4\u4e86\u4e9b\u4ec0\u4e48\uff0c\u6700\u540e\u7ec8\u88ab\u6563\u82b1\u4ed9\u5b50\u592b\u5987\u51fa\u6765\u4e00\u6405\uff0c\u5927\u5bb6\u624d\u610f\u5174\u9611\u73ca\uff0c \u5026\u610f\u4fc3\u4f7f\u4ed6\u4eec\u63d0\u6b65\u56de\u53bb\u3002 \u3000\u3000\u718a\u501c\u7a81\u7136\u770b\u89c1\u9eef\u6de1\u7684\u6708\u5149\u4e0b\uff0c\u6da7\u6c34\u5bf9\u9762\u677e\u6797\u4e4b\u4e2d\uff0c\u4f3c\u6709\u4e00\u9ed1\u4e00\u767d\u4e24\u9053\u7626\u5c0f\u5a40\u5a1c\u7684\u8eab\u5f71\uff0c \u5728\u773c\u524d\u4e00\u6643\uff0c\u4f46\u7acb\u5373\u77a5\u7136\u5931\u53bb\uff0c\u4ed6\u4e0d\u76f8\u4fe1\u90a3\u662f\u4e00\u65f6\u773c\u82b1\uff0c\u4ed6\u731b\u7136\u63d0\u8eab\u7eb5\u53bb\u5927\u559d\u9053\uff1a\u201c\u4ec0\u4e48 \u4eba\uff1f\u4f55\u65b9\u540c\u9053\uff0c\u8bf7\u51fa\u6765\u4e00\u8c08\uff01\u201d \u3000\u3000\u4f17\u4eba\u56e0\u4ed6\u8fd9\u79cd\u52a8\u4f5c\uff0c\u800c\u7acb\u65f6\u7eb7\u6270\u8d77\u6765\uff0c\u4f46\u662f\u6b66\u5f53\u6d3e\u4eba\u81ea\u5c71\u53e3\u8d77\u5230\u5904\u90fd\u8bbe\u6709\u4f0f\u6869\uff0c\u718a\u501c\u76f8 \u4fe1\u5fc5\u662f\u5929\u9634\u6559\u4eba\uff0c\u56e0\u4e3a\u90a3\u79cd\u8863\u670d\u989c\u8272\u662f\u592a\u53ef\u7591\u4e86\uff0c\u4ed6\u4ee5\u6781\u5feb\u7684\u8eab\u6cd5\uff0c\u5728\u6797\u4e2d\u641c\u5bfb\u4e00\u904d\uff0c\u5374\u6ca1 \u627e\u89c1\u4ec0\u4e48\u8e2a\u5f71\u3002 \u3000\u3000\u6563\u82b1\u4ed9\u5b50\uff0c\u4e1c\u65b9\u7075\u7b49\uff0c\u4e5f\u5728\u5404\u5904\u641c\u7d22\uff0c\u7ec8\u4e8e\u53c8\u4f1a\u5408\u5728\u4e00\u5904\uff0c\u5e38\u6f2b\u5929\u8ba4\u4e3a\u5929\u9634\u6559\u4eba\uff0c\u7edd\u6ca1 \u8fd9\u4e48\u5de7\uff0c\u6070\u5728\u6b64\u65f6\u6765\u5077\u63a2\u865a\u5b9e\uff0c\u4e1c\u65b9\u7075\u5219\u540c\u610f\u718a\u501c\u7684\u770b\u6cd5\uff0c\u8ba4\u4e3a\u5929\u9634\u6559\u4e2d\u4e0d\u4e4f\u597d\u624b\uff0c\u6b66\u5f53\u6d3e \u5927\u5f20\u65d7\u9f13\u9080\u8058\u5404\u65b9\u8c6a\u6770\u600e\u80fd\u4e0d\u6cc4\u6f0f\u98ce\u58f0\uff1f \u3000\u3000\u63a5\u7740\u53c8\u770b\u89c1\u6b66\u5f53\u6d3e\u5de1\u67e5\u7684\u4eba\uff0c\u56db\u4e2a\u9053\u58eb\u4e00\u8d77\u513f\u5728\u5ca9\u5cf0\u5e7d\u6da7\u4e2d\u51fa\u6ca1\uff0c\u786e\u5b9e\u6b66\u5f53\u6d3e\u7684\u4eba\u4e5f\u5e03 \u7f6e\u5f97\u975e\u5e38\u5468\u5bc6\u3002 \u3000\u3000\u718a\u501c\u4e0d\u613f\u628a\u8fd9\u4e8b\u544a\u8bc9\u98de\u9e64\u5b50\uff0c\u56e0\u4e3a\u6015\u662f\u83ab\u987b\u6709\u7684\u4e8b\uff0c\u5eb8\u4eba\u81ea\u6270\uff0c\u53cd\u800c\u6dfb\u4e86\u4e00\u4ef6\u7b11\u67c4\uff0c\u4ed6 \u4eec\u9042\u5404\u56de\u4e39\u623f\u5c31\u5bdd\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u4f9d\u7136\u6e05\u6de1\u4e86\u534a\u65e5\u3002 \u3000\u3000\u4f1a\u573a\u5306\u5306\u5e03\u7f6e\uff0c\u803d\u8bef\u5230\u7533\u6b63\u65f6\u5206\uff0c\u65b9\u624d\u7531\u98de\u9e64\u5b50\u82cd\u7a79\u82cd\u677e\u7b49\u5206\u522b\u5bfc\u5f15\u4ed6\u4eec\u5165\u5e2d\u3002\u5999\u4e00\u771f \u4eba\u5df2\u661f\u51a0\u7fbd\u670d\uff0c\u542b\u7b11\u5728\u6b63\u6bbf\u9636\u524d\u8fce\u5019\u3002 \u3000\u3000\u4ee5\u6b66\u5f53\u6d3e\u638c\u95e8\u4e4b\u5c0a\uff0c\u4eb2\u81ea\u8fce\u63a5\uff0c\u8fd9\u662f\u5f88\u5c11\u6709\u7684\u4e8b\u3002 \u3000\u3000\u6bbf\u5185\u5e03\u7f6e\u5f97\u5f02\u5e38\u6d01\u51c0\u7cbe\u5fae\uff0c\u591a\u534a\u662f\u4e24\u4eba\u4e00\u5e2d\uff0c\u9762\u524d\u4e00\u5f20\u6761\u684c\uff0c\u672c\u5c71\u7684\u96c0\u7259\u9999\u8317\u6bcf\u4eba\u9762\u524d \u653e\u4e86\u4e2a\u76d6\u949f\u513f\u3002\u4e2d\u6709\u56db\u4eea\u5251\u5ba2\u548c\u82cd\u7a79\u82cd\u677e\u7b49\u4e00\u6d41\u5f1f\u5b50\uff0c\u4f8d\u7acb\u6bbf\u5916\u5eca\u4e0a\uff0c\u7167\u5e94\u56db\u5468\uff0c\u5c0f\u9053\u58eb\u4eec \u8083\u7136\u5f80\u6765\u4f3a\u5e94\uff0c\u4e0e\u4f1a\u7684\u9ed1\u538b\u538b\u5750\u6ee1\u4e86\u8fd9\u5ea7\u6b63\u6bbf\uff0c\u8db3\u6709\u56db\u5341\u4f59\u4f4d\u5404\u65b9\u540d\u5bbf\u9ad8\u624b\u3002 \u3000\u3000\u5e2d\u6b21\u7684\u4e0a\u4e0b\uff0c\u662f\u542b\u6709\u5d07\u656c\u7684\u610f\u601d\uff0c\u81ea\u7136\u5ce8\u5d4b\u3001\u6606\u4ed1\u3001\u70b9\u82cd\u4e09\u5927\u6b63\u6d3e\uff0c\u8981\u5360\u7740\u91cd\u8981\u7684\u4f4d \u7f6e\uff0c\u718a\u501c\u548c\u5c1a\u672a\u660e\u4e5f\u88ab\u6392\u5217\u5728\u8f83\u9760\u4e0a\u5e2d\u4ec5\u6b21\u6563\u82b1\u4ed9\u5b50\u592b\u5987\u7684\u4f4d\u7f6e\uff0c\u800c\u4e1c\u65b9\u7075\u5144\u59b9\u53c8\u5728\u4ed6\u4eec\u7684 \u4e0b\u624b\uff0c\u8db3\u89c1\u6b66\u5f53\u6d3e\u5982\u4f55\u5668\u91cd\u4ed6\u4eec\u56db\u4f4d\u3002 \u3000\u3000\u5ce8\u5d4b\u6d3e\u6d41\u4e91\u5e08\u592a\u5e08\u5f92\u4e09\u4eba\u5916\uff0c\u53c8\u591a\u4e86\u5b64\u5cf0\u4e00\u5251\u8fb9\u6d69\uff0c\u5b64\u5cf0\u4e00\u5251\u7adf\u548c\u5f90\u5c0f\u5170\u5e76\u80a9\u800c\u5750\uff0c\u4ed6 \u6709\u4e9b\u6127\u5bf9\u718a\u501c\uff0c\u4f46\u662f\u4e3a\u4e86\u4e89\u593a\u501a\u5929\u5251\uff0c\u66f4\u607c\u6012\u8fd9\u5c11\u5e74\uff0c\u6240\u4ee5\u4ed6\u4e00\u76f4\u4ee5\u6700\u6124\u6012\u7684\u76ee\u5149\uff0c\u77aa\u89c6\u7740 \u718a\u501c\u548c\u5c1a\u672a\u660e\u3002 \u3000\u3000\u70b9\u82cd\u6d3e\u4e5f\u53e6\u6709\u4e24\u4f4d\u6210\u540d\u7684\u5251\u5ba2\u5217\u5e2d\uff0c\u6b64\u5916\u53d7\u5c0a\u91cd\u7684\u5c31\u662f\u5927\u96c4\u6cd5\u5e08\u5e08\u5f92\uff0c\u4e10\u5e2e\u9f99\u5934\u84dd\u5927\u5148 \u751f\u4ee5\u53ca\u4ed6\u7684\u4f19\u4f34\u516d\u4eba\uff0c\u5929\u5c71\u4e09\u9f99\u5e2d\u6b21\uff0c\u6392\u5728\u5ce8\u5d4b\u6d3e\u4fa7\u9762\uff0c\u4e5f\u7b97\u5f88\u5360\u8981\u4f4d\uff0c\u5176\u4ed6\u4eba\u4e2d\uff0c\u718a\u501c\u53ea \u8ba4\u5f97\u5b50\u6bcd\u91d1\u9675\u6b66\u80dc\u6587\uff0c\u5c55\u7fc5\u91d1\u9e4f\u4e0a\u5b98\u4e88\u6570\u4eba\u3002 \u3000\u3000\u6c5f\u5357\u4e00\u5e26\u8457\u540d\u7684\u8001\u5c11\u6b66\u5e08\uff0c\u8bf7\u6765\u7684\u4e0d\u5728\u5c11\u6570\u3002 \u3000\u3000\u5999\u4e00\u771f\u4eba\u7f13\u7f13\u8d77\u7acb\uff0c\u4ee5\u5f88\u6c89\u91cd\u7684\u8bed\u8c03\uff0c\u8bf4\u660e\u6b64\u6b21\u96c6\u4f1a\u7684\u610f\u4e49\uff0c\u4e3b\u5f20\u4e00\u81f4\u5bf9\u4ed8\u5929\u9634\u6559\uff0c\u4ed6 \u6177\u6168\u9648\u8f9e\uff0c\u5728\u573a\u7684\u4eba\u65e0\u4e0d\u611f\u52a8\uff0c\u800c\u5929\u9634\u6559\u52bf\u529b\u6da8\u6f2b\u6c5f\u6cb3\u5357\u5317\uff0c\u5df2\u903c\u5f97\u6b66\u6797\u6b63\u6d3e\u7684\u4eba\uff0c\u51e0\u4e4e\u65e0 \u6cd5\u7acb\u8db3\u3002 \u3000\u3000\u8fd9\u662f\u6bcf\u4e2a\u4eba\u672c\u8eab\u751f\u6b7b\u5b58\u4ea1\u7684\u95ee\u9898\uff0c\u4e0d\u4ec5\u662f\u6b66\u5f53\u6606\u4ed1\u5ce8\u5d4b\u70b9\u82cd\u56db\u5927\u6b63\u6d3e\u7684\u7978\u798f\u6538\u5173\uff0c\u4eba\u4eba \u52bf\u6240\u96be\u514d\uff0c\u4e0d\u8054\u5408\u8d77\u6765\uff0c\u786e\u4e0d\u5bb9\u6613\u6251\u706d\u8fd9\u6f2b\u5929\u5996\u6c14\u5462\u3002 \u3000\u3000\u5404\u4eba\u5bf9\u4e8e\u5999\u4e00\u771f\u4eba\u7684\u8bdd\uff0c\u65e0\u4e0d\u6b22\u7136\u9996\u80af\uff0c\u76ee\u524d\u53ea\u662f\u7f3a\u5c11\u4e2a\u9886\u8896\u7684\u4eba\uff0c\u5728\u5750\u5404\u4f4d\u90fd\u4e00\u81f4\u9ed8 \u8ba4\u6b66\u5f53\u4e3a\u6b66\u6797\u6700\u5927\u5b97\u6d3e\uff0c\u5b9e\u529b\u5145\u8db3\uff0c\u5999\u4e00\u771f\u4eba\u5fb7\u9ad8\u671b\u91cd\uff0c\u81ea\u7136\u662f\u6700\u7406\u60f3\u7684\u4eba\u9009\uff0c\u4e0d\u5f85\u63a8\u6234\uff0c \u8fd9\u5df2\u6210\u5b9a\u5c40\u3002 \u3000\u3000\u7a76\u7adf\u5e94\u8be5\u91c7\u53d6\u4ec0\u4e48\u6b65\u9aa4\uff0c\u5148\u628a\u5404\u5730\u5929\u9634\u6559\u4eba\u6d88\u706d\uff1f\u6291\u6216\u662f\u805a\u800c\u6b7c\u4e4b\u7281\u5ead\u626b\u7a74\uff1f\u53d7\u5929\u9634\u6559 \u52fe\u7ed3\u717d\u52a8\uff0c\u662f\u5426\u53ef\u4ee5\u8bbe\u6cd5\u79bb\u95f4\u5206\u5316\uff0c\u4ee5\u51cf\u5f31\u5929\u9634\u6559\u7684\u5b9e\u529b\uff0c\u8fd9\u4e00\u8fde\u4e32\u7684\u95ee\u9898\uff0c\u6ca1\u6709\u4e00\u4e2a\u4eba\u6307 \u6325\u82e5\u5b9a\uff0c\u6b65\u4f10\u4e0d\u6613\u4e00\u81f4\u5462\u3002 \u3000\u3000\u84dd\u5927\u5148\u751f\u89c1\u666f\u751f\u60c5\uff0c\u7acb\u523b\u7ad9\u8d77\u6765\u9996\u5148\u63d0\u8bae\uff0c\u7531\u6b66\u5f53\u5999\u4e00\u771f\u4eba\u4f5c\u4e3b\uff0c\u4e3b\u6301\u8fd9\u6b21\u5bf9\u4ed8\u5929\u9634\u6559 \u7684\u5927\u8ba1\u3002 \u3000\u3000\u4f17\u4eba\u8f70\u7136\u8d5e\u540c\uff0c\u5999\u4e00\u771f\u4eba\u7565\u4f5c\u8c26\u900a\uff0c\u7531\u4e8e\u5927\u5bb6\u70ed\u8bda\u62e5\u62a4\uff0c\u5999\u4e00\u771f\u4eba\u53ea\u6709\u4e49\u4e0d\u5bb9\u8f9e\u7684\u9996 \u80af\u3002 \u3000\u3000\u90fd\u662f\u6b66\u6797\u540d\u5bbf\u9ad8\u624b\uff0c\u4e5f\u7528\u4e0d\u7740\u6b43\u8840\u5b9a\u76df\uff0c\u84dd\u5927\u5148\u751f\u628a\u4e10\u5e2e\u63a2\u542c\u5f97\u6765\u5929\u9634\u6559\u7684\u6d88\u606f\uff0c\u8be6\u7ec6 \u5730\u9648\u8ff0\u4e86\u4e00\u756a\uff0c\u5404\u4eba\u90fd\u4fa6\u77e5\u5929\u9634\u6559\u4e00\u4e8c\u52a8\u9759\uff0c\u4e8e\u662f\u7ecf\u8fc7\u4e00\u756a\u4e92\u76f8\u7814\u8ba8\uff0c\u8ba4\u4e3a\u5929\u9634\u6559\u52bf\u529b\u7fbd\u7ffc \u5df2\u6210\uff0c\u518d\u4e0d\u8bbe\u6cd5\u6d88\u706d\uff0c\u6b66\u6797\u6b63\u6d3e\u4eba\u58eb\uff0c\u5c31\u4e0d\u514d\u53d7\u4ed6\u4eec\u6076\u52bf\u529b\u652f\u914d\u63a7\u5236\u4e86\u3002 \u3000\u3000\u7efc\u5408\u5927\u5bb6\u6240\u5f97\u7684\u6d88\u606f\uff0c\u5929\u9634\u6559\u4eba\u5df2\u503e\u5de2\u800c\u51fa\uff0c\u4ee5\u6c5f\u4e2d\u4e0b\u6e38\u7696\u82cf\u6e58\u9102\u8bf8\u7701\uff0c\u4f5c\u4e3a\u6839\u636e\u5730\uff0c \u4e89\u96c4\u4e2d\u539f\uff0c\u800c\u7f51\u7f57\u7684\u4e11\u7c7b\u4e5f\u8d8a\u6765\u8d8a\u591a\u4e86\u3002 \u3000\u3000\u6b66\u6797\u4e94\u5927\u6b63\u5b97\u6d3e\uff0c\u552f\u72ec\u5d06\u5cd2\u4e00\u6d3e\u65e0\u4eba\u51fa\u573a\uff0c\u8fd9\u65e0\u5f02\u6697\u793a\u7740\u5d06\u5cd2\u6d3e\u4eba\u5df2\u548c\u5929\u9634\u6559\u6709\u4e86\u9ed8 \u5951\uff0c\u81ea\u7136\u8fd9\u662f\u6781\u4e0d\u5e78\u7684\u4e8b\u3002 \u3000\u3000\u5927\u96c4\u6cd5\u5e08\u63d0\u8bae\u5927\u5bb6\u6350\u5f03\u5df2\u5f80\u7684\u5acc\u9699\uff0c\u5148\u4ee5\u5927\u5c40\u4e3a\u91cd\uff0c\u5728\u6d88\u706d\u4e86\u5929\u9634\u6559\u4e4b\u540e\uff0c\u518d\u5404\u4e86\u7ed3\u79c1 \u4e0b\u7684\u516c\u6848\u3002 \u3000\u3000\u8fd9\u8bdd\u53ef\u5c31\u6709\u4e9b\u4eba\u9ed8\u9ed8\u4e0d\u8bed\uff0c\u5c24\u4ee5\u5929\u5c71\u4e09\u9f99\uff0c\u5ce8\u5d4b\u6d41\u4e91\u5e08\u592a\u3001\u5b64\u5cf0\u4e00\u5251\u9762\u9732\u60bb\u60bb\u4e0d\u4e4e\u4e4b \u8272\uff0c\u5999\u4e00\u771f\u4eba\u6168\u7136\u53f9\u606f\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u627f\u5404\u6d3e\u5404\u65b9\u9ad8\u624b\uff0c\u8fb1\u4e34\u8352\u5c71\uff0c\u826f\u673a\u4e00\u7eb5\u5373\u5931\uff0c\u5148\u53d1\u5236\u4eba \u65b9\u4e3a\u4e0a\u7b56\uff0c\u5982\u4f55\u5c31\u6b64\u5f00\u59cb\u6211\u4eec\u7684\u884c\u52a8\uff01\u201d \u3000\u3000\u81ea\u7136\u5999\u4e00\u771f\u4eba\u662f\u6015\u591c\u957f\u68a6\u591a\uff0c\u840d\u8e2a\u56db\u6563\uff0c\u518d\u53ec\u96c6\u5c31\u4e0d\u5bb9\u6613\u4e86\u3002\u4f17\u4eba\u5404\u6709\u6069\u6028\uff0c\u867d\u5728\u6b63\u4e49 \u65d7\u5e1c\u4e0b\u4e0d\u5bb9\u63a8\u8bff\uff0c\u4f46\u8fd8\u6709\u8bb8\u591a\u4eba\u672a\u80fd\u7acb\u5373\u9996\u80af\u3002\u6070\u5728\u8fd9\u65f6\uff0c\u98de\u9e64\u5b50\u81ea\u5916\u9762\u98de\u8eab\u800c\u5165\uff0c\u795e\u8272\u663e \u5f97\u975e\u5e38\u7d27\u5f20\u3002 \u3000\u3000\u4f17\u4eba\u7acb\u523b\u795e\u60c5\u968f\u4e4b\u4e0d\u5b89\uff0c\u98de\u9e64\u5b50\u8eac\u8eab\u7980\u544a\uff1a\u201c\u5929\u9634\u6559\u4eba\u5df2\u6d3e\u53f8\u793c\u7ae5\u5b50\u9ed1\u8863\u6469\u52d2\u767d\u666f\u7965\uff0c \u81ea\u8863\u9f99\u5973\u53f6\u6e05\u6e05\u9001\u6765\u4e00\u51fd\u3002\u53c8\u770b\u4e86\u718a\u501c\u4e00\u773c\u8bf4\uff1a\u201c\u8fd8\u6709\u4e00\u4fe1\uff0c\u662f\u7ed9\u718a\u5c0f\u4fa0\u7684\uff0c\u662f\u8f6c\u6765\u96ea\u5730\u98d8 \u98ce\u7684\u4fe1\uff01\u201d \u3000\u3000\u718a\u501c\u795e\u8272\u4e3a\u4e4b\u4e00\u53d8\uff0c\u90a3\u4e9b\u4e0d\u660e\u4e86\u718a\u501c\u6765\u5386\u7684\u4eba\uff0c\u90fd\u7eb7\u7eb7\u8d77\u4e86\u6000\u7591\uff0c\u800c\u5929\u5c71\u4e09\u9f99\uff0c\u6d41\u4e91\u5e08 \u592a\uff0c\u66f4\u662f\u5bf9\u718a\u501c\u8868\u793a\u7740\u9119\u5937\u4e0d\u5c51\u4e4b\u8272\uff0c\u65e0\u7591\u7684\u5927\u534a\u4eba\u90fd\u6000\u7591\u5230\u718a\u501c\uff0c\u662f\u5426\u4e0e\u5929\u9634\u6559\u6709\u7740\u7279\u6b8a \u5173\u7cfb\uff1f \u3000\u3000\u590f\u82b8\u7684\u4fe1\uff0c\u7531\u5929\u9634\u6559\u4eba\u8f6c\u6765\uff0c\u4e0d\u662f\u8bc1\u660e\u590f\u82b8\u5df2\u7ecf\u5931\u8db3\u4e86\u4e48\uff1f\u65e0\u8bba\u51fa\u4e8e\u81ea\u613f\u4e0e\u5426\uff0c\u8fd9\u662f\u591a \u4e48\u4e0d\u8be6\u7684\u4e8b\u554a\u3002 \u3000\u3000\u6563\u82b1\u4ed9\u5b50\u8877\u5fc3\u66ff\u590f\u82b8\u60cb\u60dc\u7740\u3002 \u3000\u3000\u718a\u501c\u4ee5\u6781\u60b2\u75db\u7684\u5fc3\u60c5\uff0c\u4ecd\u80fd\u6491\u6301\u7740\u51b7\u9759\u7684\u6001\u5ea6\uff0c\u4f38\u624b\u63a5\u8fc7\u98de\u9e64\u5b50\u4ea4\u6765\u7684\u4e00\u5c01\u4fe1\uff0c\u590f\u82b8\u7ee2 \u79c0\u7ea4\u5f31\u7684\u5b57\u8ff9\uff0c\u8fd9\u4e0d\u662f\u522b\u4eba\u53ef\u4ee5\u4f5c\u5047\u7684\u3002 \u3000\u3000\u6563\u82b1\u4ed9\u5b50\u6fc0\u52a8\u7740\uff0c\u538b\u4e0d\u4f4f\u6025\u4fc3\u7684\u547c\u5438\uff0c\u4e0d\u77e5\u590f\u82b8\u7a76\u7adf\u5199\u7740\u4ec0\u4e48\u523a\u6fc0\u718a\u501c\u7684\u8bdd\uff0c\u5979\u79c0\u76ee\u4e00 \u76f4\u6ce8\u89c6\u7740\u718a\u501c\u53d1\u6296\u7684\u624b\u3002 \u3000\u3000\u800c\u4e0e\u4f1a\u7684\u4f17\u4eba\uff0c\u4e5f\u4ee5\u6fc0\u52a8\u7684\u5fc3\u60c5\uff0c\u671f\u5f85\u5999\u4e00\u771f\u4eba\u5ba3\u5e03\u5929\u9634\u6559\u7684\u6765\u4e66\uff0c\u5929\u9634\u6559\u65e0\u5b54\u4e0d\u5165\uff0c \u5c45\u7136\u628a\u6b66\u5f53\u6d3e\u53ec\u96c6\u7fa4\u96c4\u7684\u65f6\u671f\u62ff\u5f97\u5f88\u51c6\uff0c\u6070\u597d\u5728\u6b64\u65f6\u9012\u6765\u5e16\u5b50\uff0c\u8db3\u89c1\u4ed6\u4eec\u8033\u76ee\u722a\u7259\uff0c\u904d\u5e03\u5728 \u8fd9\u4e00\u5e26\u4e86\u3002 \u3000\u3000\u5999\u4e00\u771f\u4eba\u4e0d\u62c6\u4fe1\uff0c\u51b7\u4f3c\u4e25\u971c\u7684\u8138\u8272\u95f4\u9053\uff1a\u201c\u5929\u9634\u6559\u6765\u4eba\u8fd8\u6ca1\u8d70\u4e48\uff1f\u201d\u98de\u9e64\u5b50\u4f4e\u58f0\u8bf4\uff1a \u201c\u4ed6\u4eec\u8fd8\u8981\u4e00\u58f0\u56de\u8bdd\uff01\u800c\u4e14\u2026\u2026\u201d\u4ed6\u53c8\u770b\u4e86\u718a\u501c\u4e00\u773c\uff0c\u8bf4\uff1a\u201c\u8fd8\u8bf7\u718a\u5c0f\u4fa0\u51fa\u5916\u4e00\u8c08\u5462\uff01\u201d\u8fd9 \u8bdd\u8bf4\u51fa\u4e4b\u540e\uff0c\u718a\u501c\u4e0d\u557b\u6210\u4e86\u4f17\u77e2\u4e4b\u7684\u4e86\u3002 \u3000\u3000\u6606\u4ed1\u53cc\u6770\u4ee5\u6781\u51b7\u9177\u7684\u773c\u5149\uff0c\u6ce8\u610f\u89c2\u5bdf\u718a\u501c\u7684\u8868\u60c5\u3002\u4eba\u8a00\u66fe\u53c2\u6740\u4eba\uff0c\u66fe\u6bcd\u5c1a\u4e14\u7591\u5b50\uff0c\u6240\u4ee5 \u867d\u5723\u8d24\u4e5f\u4e0d\u80fd\u514d\u4e8e\u4f17\u53e3\u70c1\u91d1\uff0c\u4f7f\u4eba\u751f\u7591\uff0c\u4f55\u51b5\u4e00\u5927\u534a\u4eba\u5bf9\u4e8e\u718a\u501c\u662f\u4e0d\u4e86\u89e3\u5462\uff01 \u3000\u3000\u65e0\u5c71\u4e09\u9f99\u5df2\u6012\u76ee\u53d1\u51fa\u6781\u96be\u542c\u4e86\u67ad\u7b11\u4e4b\u58f0\u3002 \u3000\u3000\u84dd\u5927\u5148\u751f\u5374\u529d\u4f17\u4eba\u6682\u65f6\u4fdd\u6301\u51b7\u9759\uff0c\u5c1a\u672a\u660e\u624b\u63e1\u5251\u628a\uff0c\u4ed6\u8840\u6027\u7206\u70c8\uff0c\u5018\u82e5\u6709\u4eba\u8bec\u8511\u718a\u501c\uff0c \u90a3\u4ed6\u662f\u7acb\u523b\u5c31\u8981\u62d4\u5251\u800c\u8d77\uff0c\u53ea\u6709\u6b66\u5f53\u6d3e\u660e\u4e86\u718a\u501c\u4e0e\u5929\u9634\u6559\u7684\u4e0d\u7766\uff0c\u4e0a\u6b21\u89e3\u5251\u6cc9\u7554\uff0c\u4e3a\u8d2f\u65e5\u5251 \u4e00\u573a\u640f\u6597\uff0c\u53ef\u4e3a\u4f50\u8bc1\u3002 \u3000\u3000\u5999\u4e00\u771f\u4eba\u628a\u5929\u9634\u6559\u7126\u5f02\u884c\u592b\u5987\u793a\u540d\u7684\u4e00\u5c01\u4fe1\uff0c\u6717\u58f0\u8bfb\u4e86\u4e00\u904d\uff0c\u5927\u610f\u662f\u8ba5\u8bbd\u6b66\u5f53\u6d3e\u6492\u82f1\u96c4 \u5e16\uff0c\u5b57\u91cc\u884c\u95f4\u662f\u5145\u6ee1\u4e9b\u8f7b\u8511\u7684\u8bdd\uff0c\u8868\u793a\u5929\u9634\u6559\u6682\u65f6\u51b3\u4e0d\u9000\u51fa\u957f\u6c5f\u4e00\u5e26\uff0c\u8fdb\u4e00\u6b65\u4ee5\u6d1e\u5ead\u541b\u5c71\u4e3a \u5927\u672c\u8425\uff0c\u7aed\u529b\u4e0e\u81ea\u547d\u6b63\u6d3e\u7684\u4eba\u5468\u65cb\u5230\u5e95\u3002 \u3000\u3000\u4fe1\u672a\u8fd8\u8868\u793a\u7740\u53cc\u65b9\u51b0\u70ad\u4e0d\u80fd\u76f8\u5bb9\uff0c\u4e0d\u59a8\u5728\u660e\u6625\u8349\u957f\u83ba\u98de\u4e4b\u9645\uff0c\u6765\u4e00\u6b21\u5927\u89c4\u6a21\u7684\u8f83\u91cf\uff0c\u8fd9 \u7b80\u76f4\u662f\u6311\u6218\u4e86\uff01 \u3000\u3000\u5999\u4e00\u771f\u4eba\u8083\u7136\u53d8\u8272\uff0c\u4f17\u4eba\u4e5f\u90fd\u975e\u5e38\u7d27\u5f20\u4e0d\u5b89\uff0c\u6b66\u5f53\u6d3e\u4e0d\u80fd\u5411\u6076\u52bf\u529b\u4f4e\u5934\uff0c\u53ea\u6709\u4e0e\u5929\u9634\u6559 \u786c\u62fc\u4e4b\u4e00\u9014\u3002 \u3000\u3000\u81ea\u7136\u8fd9\u79cd\u91cd\u8981\u7684\u51b3\u5b9a\uff0c\u5999\u4e00\u771f\u4eba\u8981\u5f81\u8be2\u4e00\u4e0b\u5927\u5bb6\u7684\u610f\u89c1\uff0c\u7ed3\u679c\u662f\u4e00\u81f4\u540c\u610f\uff0c\u660e\u6625\u5982\u7ea6\u548c \u5929\u9634\u6559\u51b3\u4e00\u96cc\u96c4\uff0c\u53ea\u8fd9\u51b3\u6597\u5730\u70b9\uff0c\u8fd8\u672a\u80fd\u51b3\u5b9a\uff0c\u800c\u4e14\u4e5f\u987b\u901a\u77e5\u5929\u9634\u6559\uff0c\u8fd9\u5c31\u662f\u5929\u9634\u6559\u6765\u4eba\u7b49 \u5019\u7b54\u8986\u7684\u95ee\u9898\u3002 \u3000\u3000\u718a\u501c\u5219\u628a\u90a3\u539a\u539a\u7684\u5c01\u5957\u62c6\u5f00\uff0c\u800c\u4fe1\u91cc\u5e76\u6ca1\u6709\u5199\u7740\u4e00\u4e2a\u5b57\uff0c\u4ec5\u4ec5\u4e00\u679a\u53e4\u94b1\uff0c\u8fd9\u662f\u590f\u82b8\u5f97\u81ea \u718a\u501c\uff0c\u53f6\u8001\u5927\u5144\u5f1f\u7ed9\u718a\u501c\u7684\u4e1c\u897f\uff0c\u8fd9\u7a76\u7adf\u8868\u793a\u4ec0\u4e48\u610f\u4e49\u5462\uff1f\u4f7f\u718a\u501c\u5982\u5760\u5165\u4e91\u91cc\u96fe\u4e2d\uff0c\u5c1a\u672a\u660e \u4e5f\u4e0d\u77e5\u4ed6\u548c\u590f\u82b8\u6709\u4ec0\u4e48\u9ed8\u5951\uff01 \u3000\u3000\u4f46\u90a3\u679a\u53e4\u94b1\u5c1a\u672a\u660e\u662f\u8ba4\u8bc6\u7684\u3002 \u3000\u3000\u4f17\u4eba\u4e5f\u53ea\u770b\u89c1\u590f\u82b8\u4fe1\u4e2d\uff0c\u4ec5\u4ec5\u662f\u4e00\u679a\u53e4\u94b1\uff0c\u6d41\u4e91\u5e08\u5927\u81ea\u4f5c\u806a\u660e\uff0c\u563b\u563b\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u8fd9 \u70b9\u513f\u7384\u865a\uff0c\u8fd9\u4e00\u5b9a\u662f\u5929\u9634\u6559\u4eba\u7684\u6697\u53f7\u4e86\uff01\u201d\u8fd9\u65ec\u8bdd\u6c14\u607c\u4e86\u94c1\u80c6\u5c1a\u672a\u660e\uff0c\u970d\u5730\u8dc3\u8d77\u5389\u58f0\u559d\u9053\uff1a \u201c\u8fd9\u662f\u5728\u4e0b\u670b\u53cb\u53f6\u6c0f\u4e09\u82f1\u7684\u6807\u8bb0\uff01\u79c3\u5a46\u4e0d\u8981\u4fe1\u53e3\u96cc\u9ec4\uff01\u201d \u3000\u3000\u5999\u4e00\u771f\u4eba\u4e5f\u89c9\u6b64\u4e8b\u975e\u5e38\u6eaa\u8df7\uff0c\u5fd9\u529d\u4fe9\u4eba\u6682\u65f6\u7f62\u624b\u3002 \u3000\u3000\u5999\u4e00\u771f\u4eba\u4e25\u8083\u7684\u795e\u6001\u9053\uff1a\u201c\u9664\u6076\u52a1\u5c3d\uff0c\u6211\u4eec\u5c31\u53bb\u5929\u9634\u6559\u6c5f\u5357\u603b\u5802\u6d1e\u5ead\u541b\u5c71\u4f1a\u4f1a\u4ed6\u4eec\uff0c\u5404 \u4f4d\u4ee5\u4e3a\u5982\u4f55\u5462\uff1f\u201d \u3000\u3000\u6606\u4ed1\u53cc\u6770\u90fd\u7b49\u65e0\u5f02\u8bae\uff0c\u65f6\u95f4\u5c31\u5b9a\u4e86\u660e\u6625\u6e05\u660e\u8282\u3002 \u3000\u3000\u5999\u4e00\u771f\u4eba\u8bf4\uff1a\u201c\u98de\u9e64\u5b50\u4f60\u53bb\u5907\u4e00\u5f20\u7b52\u5e16\uff0c\u7528\u56db\u6d3e\u53ca\u6b66\u6797\u5404\u4f4d\u540d\u4e49\uff0c\u5199\u660e\u65e5\u671f\u8d74\u541b\u5c71\u5019 \u6559\uff0c\u4ea4\u4ed8\u6765\u4eba\u5c31\u662f\u4e86\u3002\u201d \u3000\u3000\u98de\u9e64\u5b50\u5e94\u4e86\u8bfa\uff0c\u7acb\u5373\u51c6\u5907\u4e86\u62dc\u5e16\u6587\u5177\uff0c\u5728\u573a\u7684\u4eba\u4e2a\u4e2a\u4e49\u5f62\u4e8e\u8272\u90fd\u7b7e\u4e86\u540d\u5b57\uff0c\u4e8e\u662f\u8fd9\u4e00\u6869 \u6b66\u6797\u7a7a\u524d\u6ca1\u6709\u7684\u6d69\u52ab\uff0c\u7ec8\u4e8e\u5728\u8fd9\u6b21\u4f1a\u8bae\u4e2d\u9020\u6210\uff01\u98de\u9e64\u5b50\u5c01\u597d\u4e86\u6ce5\u91d1\u7b80\u5e16\uff0c\u8fdf\u8fdf\u672a\u53bb\uff0c\u5374\u5411\u718a \u501c\u9053\uff1a\u201c\u718a\u5c0f\u4fa0\u8981\u4e0d\u8981\u4e00\u540c\u53bb\u89c1\u89c1\u5929\u9634\u6559\u4eba\uff1f\u201d \u3000\u3000\u718a\u501c\u5fc3\u6025\u590f\u82b8\u7684\u5b89\u5371\uff0c\u5306\u5306\u8d77\u8eab\u800c\u51fa\uff0c\u8bf4\uff1a\u201c\u6b63\u8981\u95ee\u95ee\u4ed6\u4eec\uff0c\u4e3a\u4f55\u52ab\u63b3\u4e00\u4e2a\u5f31\u5973\u5b50\uff01\u201d \u5c1a\u672a\u660e\u4e5f\u968f\u7740\u51fa\u4e86\u6b63\u6bbf\u3002 \u3000\u3000\u5929\u5c71\u8001\u9f99\u949f\u95ee\u5929\u51b7\u7b11\u4eba\u4e91\uff0c\u970d\u5730\u7ad9\u7acb\u8bf4\u9053\uff1a\u201c\u5f85\u8001\u592b\u51fa\u53bb\u770b\u4e00\u770b\u662f\u600e\u6837\u4e24\u4e2a\u9b54\u5d3d\u5b50\uff01\u201d \u53c8\u4ee5\u6781\u96be\u582a\u7684\u8bed\u6c14\u8bf4\uff1a\u201c\u8001\u592b\u5012\u8981\u770b\u770b\u4ed6\u4eec\u5356\u4ec0\u4e48\u5173\u5b50\uff01\u522b\u8ba9\u5403\u91cc\u6252\u5916\u7684\u4eba\uff0c\u628a\u5927\u5bb6\u51fa\u5356 \u4e86\uff01\u201d\u8fd9\u8bdd\u672a\u514d\u8bf4\u5f97\u592a\u8fc7\u4efd\u70b9\uff0c\u5e78\u800c\u718a\u501c\u7b49\u5df2\u8d70\u51fa\u4e0b\u9662\uff0c\u672a\u53ca\u7559\u5fc3\u542c\u5230\uff0c\u5426\u5219\u5c1a\u672a\u660e\u7684\u706b\u70c8 \u6027\u5b50\uff0c\u662f\u4e0d\u4f1a\u5bb9\u5fcd\u4e0b\u53bb\u7684\u3002 \u3000\u3000\u8fd9\u6b21\u4f1a\u4e2d\u7684\u51b3\u5b9a\uff0c\u662f\u975e\u5e38\u6c89\u91cd\u7684\u3002 \u3000\u3000\u8fd8\u6709\u4e9b\u4eba\u5728\u54cd\u5495\u7740\uff0c\u4f4e\u58f0\u8bae\u8bba\u7740\u718a\u501c\u548c\u5c1a\u672a\u660e\u3002 \u3000\u3000\u5929\u5c71\u4e09\u9f99\u7236\u5b50\uff0c\u4e00\u54c4\u800c\u51fa\uff0c\u5999\u4e00\u771f\u4eba\u6050\u518d\u751f\u662f\u975e\uff0c\u7acb\u5373\u6446\u624b\u4ee4\u82cd\u7a79\u82cd\u677e\uff0c\u4e5f\u968f\u540c\u53bb\u4e00 \u8d9f\uff0c\u6b66\u5f53\u6d3e\u4eba\u5907\u4e86\u6781\u4e30\u76db\u7684\u9152\u7b75\uff0c\u52a1\u8bf7\u8fd9\u4e94\u4f4d\u518d\u56de\u6765\u6b22\u5bb4\u3002 \u3000\u3000\u718a\u501c\u5374\u65e9\u5df2\u5fc3\u98de\u5728\u590f\u82b8\u8eab\u7554\u4e86\uff0c\u4f1a\u5df2\u5f00\u8fc7\uff0c\u4ed6\u53ea\u60f3\u95ee\u51fa\u590f\u59be\u6240\u5728\uff0c\u7acb\u5373\u517c\u7a0b\u5c31\u9053\u3002\u5c1a\u672a \u660e\u4e5f\u6025\u4e8e\u5f04\u660e\u81ea\u8fd9\u56de\u4e8b\uff0c\u6025\u6027\u7684\u4eba\uff0c\u4ec0\u4e48\u4e8b\u8bf4\u4f5c\u5c31\u4f5c\uff0c\u6ca1\u6709\u8003\u8651\u7684\u4f59\u5730\u3002\u5c1a\u672a\u660e\u4f55\u4ee5\u4e5f\u5982\u6b64 \u5173\u5fc3\u590f\u82b8\uff0c\u8fde\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u660e\u767d\u3002 \u3000\u3000\u718a\u501c\u5c1a\u672a\u660e\uff0c\u4e0e\u98de\u9e64\u5b50\u9a70\u62b5\u89e3\u5251\u6cc9\u7554\u3002 \u3000\u3000\u53ea\u89c1\u6b66\u5f53\u516b\u4f4d\u84dd\u888d\u9053\u58eb\uff0c\u4ed7\u5251\u800c\u7acb\uff0c\u5bf9\u9762\u5374\u662f\u4e00\u53cc\u4fca\u7f8e\u5c11\u5e74\u7537\u5973\uff0c\u82e5\u65e0\u5176\u4e8b\u7684\u5728\u5c71\u5f84\u4e0a \u5f98\u5f8a\u89c2\u8d4f\u98ce\u666f\u3002 \u3000\u3000\u4ed6\u4eec\u90fd\u8ba4\u8bc6\u662f\u5929\u9634\u6559\u4e0b\u4e24\u4f4d\u53f8\u793c\u62a4\u6cd5\u2014\u2014\u9ed1\u8863\u6469\u52d2\u767d\u666f\u7965\u3001\u548c\u767d\u8863\u9f99\u5973\u53f6\u6e05\u6e05\u3002\u8fd9\u4e24\u4f4d \u8eab\u624b\u662f\u4e0d\u51e1\u7684\uff0c\u4e0a\u6b21\u5077\u88ad\u6b66\u5f53\u5c31\u8868\u73b0\u51fa\u6765\u4ed6\u4eec\u7684\u60ca\u4eba\u7edd\u827a\uff0c\u800c\u8fd9\u6b21\u6df1\u5165\u864e\u7a74\uff0c\u6295\u4e0b\u6218\u4e66\uff0c\u4e5f \u663e\u7136\u662f\u6709\u8d85\u4eba\u7684\u80c6\u91cf\u3002 \u3000\u3000\u718a\u501c\u4e00\u770b\u8fd9\u4e24\u4f4d\u5c11\u5e74\uff0c\u5c31\u8054\u60f3\u8d77\u6765\u6628\u5929\u6708\u4e0b\u7684\u4e24\u6761\u8eab\u5f71\uff0c\u4e0d\u662f\u4ed6\u4eec\u8fd8\u6709\u8c01\u5462\uff1f \u3000\u3000\u5929\u9634\u6559\u679c\u7136\u5389\u5bb3\uff0c\u722a\u7259\u5df2\u6ee1\u5e03\u6b66\u5f53\u56db\u5468\uff0c\u6b66\u5f53\u6d3e\u4eba\u4e00\u4e3e\u4e00\u52a8\uff0c\u4ed6\u4eec\u90fd\u5df2\u4e0d\u662f\u63a2\u542c\u5f97\u5f88\u660e \u81ea\u4e48\uff1f \u3000\u3000\u767d\u666f\u7965\u548c\u53f6\u6e05\u6e05\uff0c\u90fd\u9762\u8272\u5341\u5206\u548c\u5584\uff0c\u5fae\u7b11\u65bd\u793c\u9053\uff1a\u201c\u718a\u5927\u4fa0\u4e45\u8fdd\u4e86\uff01\u655d\u6559\u6559\u4e3b\u4e00\u76f4\u5728\u656c \u7b49\u7740\u9601\u4e0b\uff0c\u53ef\u5de7\u590f\u59d1\u5a18\u53c8\u5230\u4e86\u6211\u4eec\u90a3\u8fb9\uff0c\u4e3a\u4e86\u590f\u59d1\u5a18\u5e78\u798f\u7740\u60f3\uff0c\u6559\u4e3b\u7aed\u8bda\u76fc\u9601\u4e0b\u524d\u5f80\u4e00\u8c08 \u5462\u3002\u201d\u8fd9\u4e9b\u8bdd\u662f\u4f55\u7b49\u7684\u52a8\u542c\uff0c\u5145\u6ee1\u4e86\u8bf1\u60f9\u7684\u6c14\u5473\uff0c\u800c\u8fd8\u631f\u6301\u7740\u718a\u501c\u7684\u7231\u4fa3\uff01 \u3000\u3000\u718a\u501c\u4e5f\u7565\u8fd8\u4e00\u793c\uff0c\u6b63\u8272\u9053\uff1a\u201c\u590f\u59d1\u5a18\u73b0\u5728\u4f55\u5904\uff1f\u8bf7\u901f\u660e\u8bf4\u3002\u5176\u4ed6\u4e0d\u5fc5\u591a\u8d39\u5507\u820c\uff01\u590f\u59d1\u5a18 \u5982\u7cfb\u88ab\u4f60\u4eec\u52ab\u63b3\uff0c\u6211\u718a\u501c\u53ef\u4e0d\u80fd\u653e\u8fc7\u4efb\u4f55\u4e00\u4e2a\u574f\u86cb\uff01\u201d \u3000\u3000\u53f6\u6e05\u6e05\u5a07\u7b11\u4e00\u58f0\uff0c\u7b11\u5f97\u90a3\u4e48\u751c\uff0c\u53c8\u67d4\u58f0\u9053\uff1a\u201c\u718a\u5927\u4fa0\u5927\u8a00\u91cd\u4e86\uff0c\u655d\u6559\u4f55\u81f3\u96be\u4e3a\u4e00\u4e2a\u5973 \u5b50\uff0c\u590f\u59ca\u59ca\u4eba\u751f\u5f97\u7f8e\u4e3d\u7edd\u9876\uff0c\u6211\u4eec\u8c08\u5f97\u6765\u5462\uff01\u5979\u6b63\u5728\u662f\u6211\u7684\u670b\u53cb\uff0c\u4e5f\u5982\u540c\u9601\u4e0b\u4e00\u6837\u662f\u655d\u6559\u613f \u610f\u7ed3\u8bc6\u7684\u670b\u53cb\u5440\uff01\u6b66\u5f53\u6d3e\u4eba\u624d\u662f\u4e00\u9762\u518d\u7684\u8981\u63b3\u52ab\u5979\uff0c\u4e0d\u662f\u6211\u548c\u767d\u54e5\u53ca\u65f6\u8d76\u5230\uff0c\u590f\u59d1\u5a18\u5230\u771f\u7684 \u5371\u5982\u7d2f\u5375\u5462\uff01\u5982\u8499\u9601\u4e0b\u4e0d\u5f03\uff0c\u6211\u4eec\u5c31\u4e00\u540c\u9a70\u5f80\u8346\u5dde\u5e9c\uff0c\u9601\u4e0b\u4f1a\u89c1\u4e86\u590f\u59d1\u5a18\uff0c\u4e00\u5207\u81ea\u7136\u660e \u4e86\u3002\u201d \u3000\u3000\u718a\u501c\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u718a\u67d0\u6b63\u8981\u53bb\u89c1\u5979\uff0c\u4efb\u4f60\u9f99\u6f6d\u864e\u7a74\uff0c\u6709\u4f55\u754f\u60e7\uff01\u7528\u4e0d\u7740\u70e6\u52b3\u4e8c\u4f4d\u5e26\u8def\uff0c \u8bf7\u628a\u5730\u5740\u7559\u4e0b\uff0c\u6211\u718a\u501c\u81ea\u4f1a\u524d\u5f80\u3002\u201d \u3000\u3000\u4efb\u662f\u718a\u501c\u4e00\u518d\u6076\u58f0\u76f8\u4f24\uff0c\u4e24\u4e2a\u5c11\u5e74\u5374\u6beb\u4e0d\u52a8\u6012\uff0c\u4f9d\u7136\u662f\u6781\u548c\u6c14\u7684\u795e\u6001\uff0c\u8fde\u5c1a\u672a\u660e\u7684\u706b\u70c8 \u813e\u6c14\uff0c\u4e5f\u53d1\u4f5c\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u53ef\u662f\u5728\u540e\u9762\u7aa5\u542c\u7684\u5929\u5c71\u4e09\u9f99\uff0c\u5df2\u6293\u4f4f\u4e86\u628a\u67c4\uff0c\u4e09\u6761\u8eab\u5f71\u731b\u7136\u7a9c\u51fa\u5f53\u5730\uff0c\u949f\u95ee\u5929\u6012\u4e0d\u53ef\u904f \u621f\u6307\u53f1\u9053\uff1a\u201c\u718a\u501c\uff0c\u8fd8\u6709\u59d3\u5c1a\u7684\uff0c\u5206\u660e\u662f\u9a91\u5899\u6d3e\uff0c\u4e24\u9762\u5012\u7684\u6b66\u6797\u8d25\u7c7b\uff01\u6628\u5929\u7684\u4e8b\u8fd8\u6ca1\u6709\u4e86\uff0c \u8001\u592b\u5c82\u80fd\u8ba9\u4f60\u7b49\u4ece\u5bb9\u9003\u53bb\u4e86\uff1a\u201c\u718a\u501c\u51b7\u7b11\u8bf4\uff1a\u201c\u5929\u5c71\u4e09\u9f99\uff0c\u4fe1\u53e3\u80e1\u56bc\uff0c\u6211\u6709\u8981\u4e8b\u5728\u8eab\uff0c\u5c82\u662f \u6545\u610f\u754f\u907f\u4f60\u4e86\uff01\u4f60\u628a\u8bdd\u8bf4\u660e\u767d\u70b9\uff01\u718a\u501c\u5728\u6cf0\u5c71\u9876\u4e0a\uff0c\u72ec\u6297\u5929\u9634\u6559\uff0c\u6709\u76ee\u5171\u7779\uff0c\u4f60\u522b\u60f3\u501f\u7aef\u6ecb \u6270\uff0c\u6211\u4e00\u5207\u9075\u547d\uff0c\u7edd\u4e0d\u542b\u7cca\uff0c\u5728\u54ea\u513f\u4e86\u65ad\uff0c\u4efb\u51ed\u4f60\u5212\u51fa\u9053\u513f\uff01\u201d \u3000\u3000\u5c1a\u672a\u660e\u66f4\u662f\u6c14\u5f97\u53d8\u4e86\u8138\u8272\uff0c\u957f\u5251\u4e00\u6325\uff0c\u585e\u5916\u98de\u82b1\u4e09\u5343\u5f0f\uff0c\u6781\u5947\u8be1\u53d8\u5bd2\u7684\u62db\u5f0f\uff0c\u5df2\u8dc3\u8fc7\u53bb \u76f4\u6251\u949f\u95f4\u5929\u3002 \u3000\u3000\u5c1a\u672a\u660e\u5251\u82b1\u4e71\u98a4\uff0c\u95ea\u6210\u65e0\u6570\u5bd2\u661f\uff0c\u88f9\u4f4f\u4e86\u949f\u95ee\u5929\u7684\u8eab\u5f62\uff0c\u949f\u95ee\u5929\u8d64\u624b\u7a7a\u62f3\uff0c\u8eab\u5f62\u98d8\u5ffd\u5982 \u98ce\uff0c\u5c31\u4ee5\u4e00\u53cc\u5185\u638c\u6765\u8fce\u654c\u5c1a\u672a\u660e\uff0c\u5929\u5c71\u8001\u9f99\u529f\u529b\u9187\u539a\uff0c\u800c\u8eab\u624b\u5f02\u5e38\u5947\u8be7\uff0c\u65cb\u7ed5\u5728\u5c1a\u672a\u660e\u56db \u5468\uff0c\u5251\u5f71\u7adf\u6cbe\u4e0d\u4e0a\u4ed6\u7684\u8863\u89d2\u3002 \u3000\u3000\u8001\u9f99\u4e8c\u5b50\u82cd\u9f99\u949f\u5929\u5b87\u58a8\u9f99\u949f\u5929\u4ec7\uff0c\u672c\u60f3\u62d4\u5251\u56f4\u653b\u718a\u501c\uff0c\u8eab\u540e\u82cd\u7a79\u82cd\u677e\u9053\u58eb\u8d76\u81f3\uff0c\u7aed\u529b\u963b \u62e6\uff0c\u800c\u98de\u9e64\u5b50\u628a\u56de\u8d34\u9012\u4e0e\u5929\u9634\u6559\u4e24\u4e2a\u5c11\u5e74\u7537\u5973\u4ee5\u540e\uff0c\u4e5f\u56de\u8eab\u82e6\u529d\uff0c\u4f46\u5c1a\u672a\u660e\u548c\u949f\u95ee\u5929\u5df2\u7ecf\u5728 \u4e00\u8d77\uff0c\u65e0\u6cd5\u628a\u4ed6\u4fe9\u5206\u5f00\u3002 \u3000\u3000\u718a\u501c\u4e0d\u613f\u5c1a\u672a\u660e\u4e3a\u4ed6\u53d7\u7d2f\uff0c\u672c\u5f85\u65bd\u5c55\u6f5c\u5f62\u9041\u5f71\u4e4b\u6cd5\uff0c\u4e0a\u524d\u628a\u4e24\u4eba\u5206\u5f00\uff0c\u4f46\u98de\u9e64\u5b50\u5df2\u4e34\u8eab \u7554\u633d\u4f4f\u4ed6\u7684\u80f3\u818a\u8bf4\uff1a\u201c\u718a\u5c0f\u4fa0\u5343\u4e07\u4e0d\u8981\u52a8\u624b\uff0c\u4e0d\u53ef\u4f7f\u81ea\u5df1\u4eba\u8bef\u4f1a\u52a0\u6df1\uff01\u201d \u3000\u3000\u718a\u501c\u8f6c\u5411\u767d\u666f\u7965\u53f6\u6e05\u6e05\u53f1\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u8981\u5984\u60f3\u501f\u7aef\u8981\u631f\uff0c\u718a\u67d0\u7edd\u4e0d\u53d7\u9a97\uff01\u6709\u80c6\u91cf\u5c31\u628a\u590f \u59d1\u5a18\u5730\u5740\u8bf4\u51fa\uff0c\u5426\u5219\u6d45\u718a\u501c\u5c31\u9762\u89c1\u4f60\u4eec\u6559\u4e3b\u592b\u5987\uff0c\u5f53\u9762\u7d22\u4eba\uff01\u201d \u3000\u3000\u4f46\u662f\u5929\u9634\u6559\u8fd9\u4e24\u4e2a\u5c11\u5e74\uff0c\u5374\u548c\u949f\u5929\u5b57\u5144\u5f1f\u4fe9\u4e92\u76f8\u4ea4\u6362\u4e86\u4e00\u4e0b\u795e\u79d8\u7684\u773c\u5149\uff0c\u9ed1\u8863\u6469\u52d2\u767d\u666f \u7965\u5411\u949f\u95ee\u5929\u559d\u9053\uff1a\u201c\u5929\u5c71\u949f\u524d\u8f88\uff0c\u600e\u4e48\u8fd9\u6837\u83bd\u649e\u627e\u718a\u501c\u548c\u5c1a\u5f53\u5bb6\u4ea4\u624b\uff1f\u4f60\u4eec\u8fd9\u4e0d\u662f\u540c\u6c14\u76f8 \u8fde\uff0c\u53cd\u81ea\u76f8\u6b8b\u6740\u5427\uff1f\u201d\u53c8\u5411\u718a\u501c\u8bf4\uff1a\u201c\u96ea\u5730\u98d8\u98ce\u539f\u662f\u8d35\u76f8\u77e5\uff0c\u655d\u6559\u5c82\u6562\u6020\u6162\u9519\u5f85\u4e86\u5979\uff01\u8346\u5dde \u5e9c\u5730\u9762\u4e0d\u5927\uff0c\u655d\u6559\u968f\u65f6\u6709\u4eba\u4e13\u8bda\u63a5\u5f85\uff0c\u718a\u5927\u4fa0\u4f55\u5fc5\u518d\u95ee\u5730\u5740\uff0c\u6211\u4eec\u5728\u524d\u9014\u4e13\u5019\u5927\u9a7e\u5c31\u662f \u4e86\uff01\u201d \u3000\u3000\u767d\u666f\u7965\u8bf4\u7684\u8bdd\uff0c\u8bed\u610f\u53cc\u5173\uff0c\u53ea\u6709\u4e2a\u4e2d\u4eba\u624d\u80fd\u4f53\u4f1a\u5f97\u51fa\u6240\u542b\u610f\u5473\uff0c\u949f\u5929\u5b87\u548c\u949f\u5929\u4ec7\u8198\u4e86\u8fd9 \u4e24\u4e2a\u5c11\u5e74\u4e00\u773c\uff0c\u867d\u4ecd\u7136\u6323\u624e\u7740\u8981\u6446\u8131\u4e8c\u9053\u62e6\u963b\uff0c\u4e0a\u524d\u53ae\u6597\uff0c\u4f46\u5374\u53ea\u662f\u865a\u5f20\u58f0\u52bf\u800c\u5df2\uff0c\u800c\u540c\u65f6 \u53c8\u5f88\u6ce8\u610f\u718a\u501c\u7684\u6001\u5ea6\u3002 \u3000\u3000\u767d\u666f\u7965\u548c\u53f6\u6e05\u6e05\u4f7f\u547d\u5df2\u8fbe\uff0c\u4e3a\u4f55\u8fd8\u4e0d\u79bb\u53bb\uff0c\u662f\u5426\u7b49\u5019\u6b66\u5f53\u6d3e\u4e0b\u4ee4\u9010\u5ba2\uff1f\u718a\u501c\u7684\u795e\u8272\u53c8\u90a3 \u4e48\u51b3\u7edd\uff0c\u90a3\u4e48\u4ed6\u4fe9\u53c8\u7737\u604b\u7740\u4ec0\u4e48\uff1f\u663e\u7136\u4ed6\u4fe9\u662f\u4ee5\u6781\u5173\u5207\u7684\u795e\u6001\uff0c\u6ce8\u89c6\u5c1a\u672a\u660e\u548c\u949f\u95ee\u5929\u7684\u62fc\u6597 \u4e86\u3002 \u3000\u3000\u53f6\u6e05\u6e05\u5a07\u7b11\u5f97\u975e\u5e38\u751c\u871c\uff0c\u79c0\u76ee\u9012\u8fc7\u53bb\u4e00\u79cd\u542b\u610f\u4e0d\u660e\u7684\u773c\u8272\uff0c\u5979\u662f\u671d\u7740\u5929\u5c71\u8001\u9f99\u800c\u53d1\uff0c\u54af \u54af\u7b11\u9053\uff0c\u201c\u4f60\u4eec\u4fe9\u8fd9\u4e48\u65e0\u610f\u4e49\u7684\u6253\u6597\uff0c\u6253\u5230\u51e0\u65f6\u624d\u5b8c\uff01\u4f60\u4eec\u4fe9\u90fd\u662f\u81ea\u5df1\u4eba\uff01\u8fd9\u4e0d\u662f\u8ba9\u655d\u6559\u540c \u4eba\u770b\u7740\u6709\u8da3\u4e48\uff1f\u201d\u53c8\u9053\uff1a\u201c\u53ef\u7b11\u6b66\u5f53\u6d3e\u8bf7\u6765\u7684\u5ba2\uff0c\u7adf\u4e0d\u77e5\u9053\u600e\u6837\u62db\u5f85\u522b\u4eba\uff01\u529d\u4e00\u529d\u6253\u7834\u4e86 \u5934\uff0c\u4ece\u6b64\u8c01\u4e5f\u4e0d\u80af\u518d\u5149\u987e\u4f60\u4eec\u6b66\u5f53\u540d\u5c71\u4e86\uff01\u201d \u3000\u3000\u5979\u8fd9\u4e9b\u8bdd\uff0c\u542b\u6709\u8bbd\u523a\u610f\u5473\uff0c\u5374\u53c9\u4f3c\u8bed\u4e49\u53cc\u5173\uff0c\u5e76\u4e14\u6709\u4e9b\u4e0d\u4f26\u4e0d\u7c7b\uff0c\u5929\u9634\u6559\u4e0e\u6b66\u5f53\u6559\u5982\u540c \u6c34\u706b\uff0c\u6b63\u5e94\u8be5\u5e78\u707e\u4e50\u7978\uff0c\u4f55\u5fc5\u53c8\u5047\u60fa\u60fa\u732b\u54ed\u8017\u5b50\u5462\uff1f\u53f6\u6e05\u6e05\u628a\u8fd9\u4e9b\u8bdd\u8bf4\u5b8c\uff0c\u624d\u626d\u8f6c\u5a07\u8eaf\uff0c\u62c9 \u4e86\u767d\u666f\u7965\u4e00\u540c\u5411\u5c71\u4e0b\u8d70\u53bb\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u4fe9\u4e34\u53bb\u65f6\uff0c\u4ecd\u7136\u5f6c\u5f6c\u6709\u793c\u7684\u5411\u718a\u501c\u62f1\u624b\u544a\u522b\uff0c\u5bf9\u4e8e\u6b66\u5f53\u6d3e\u7684\u9053\u58eb\uff0c\u5219\u8fde\u6b63\u773c\u4e5f\u6ca1 \u6709\u770b\u3002 \u3000\u3000\u949f\u95ee\u5929\u6e38\u8eab\u79fb\u6b65\uff0c\u548c\u5c1a\u672a\u660e\u62f3\u5251\u76f8\u4e89\uff0c\u5374\u6001\u5ea6\u7565\u7565\u53d8\u4e86\u4e9b\uff0c\u4ed6\u7adf\u820d\u5f03\u4e86\u4ed6\u64c5\u957f\u7684\u9634\u715e \u638c\uff0c\u6ca1\u6709\u4e0b\u4e00\u62db\u6bd2\u624b\u3002 \u3000\u3000\u98de\u9e64\u5b50\u89c1\u4ed6\u4fe9\u6253\u5f97\u6e10\u6e10\u51fa\u62db\u7f13\u6162\u4e86\u4e9b\uff0c\u6709\u673a\u53ef\u4e58\uff0c\u628a\u5929\u5c71\u8001\u9f99\u4f38\u624b\u62c9\u8fc7\u4e00\u8fb9\uff0c\u56de\u8eab\u62e6\u4f4f \u5c1a\u672a\u660e\u7684\u5251\uff0c\u53e3\u4e2d\u8fde\u56b7\uff1a\u201c\u5c1a\u5f53\u5bb6\u7684\u5feb\u6536\u62db\uff01\u201d \u3000\u3000\u718a\u501c\u5fc3\u601d\u6781\u7ec6\uff0c\u4ed6\u611f\u89c9\u51fa\u5929\u9634\u6559\u90a3\u4e24\u4e2a\u5c11\u5e74\u521a\u624d\u51fa\u8bed\u9887\u6709\u795e\u79d8\u610f\u5473\uff0c\u6b63\u5728\u51dd\u795e\u601d\u8003\uff0c\u4f46 \u4e5f\u968f\u7740\u98de\u9e64\u5b50\u8d70\u8fc7\u53bb\u529d\u4f4f\u4e86\u5c1a\u672a\u660e\uff0c\u949f\u95ee\u5929\u5219\u4ecd\u662f\u505a\u5cb8\u81ea\u8d1f\u7684\u795e\u8272\uff0c\u5411\u718a\u501c\u5c1a\u672a\u660e\u51b7\u7b11\u4e00\u58f0 \u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u4e24\u4e2a\u5c0f\u5b50\uff01\u4e3a\u987e\u5168\u5927\u5c40\uff0c\u6743\u4e14\u628a\u6881\u5b50\u8bb0\u4e0b\u6765\uff0c\u5f85\u660e\u6625\u541b\u5c71\u5927\u6218\u4e4b\u540e\uff0c\u518d\u884c\u7ed3 \u7b97\uff01\u8001\u592b\u8fd9\u8fd8\u662f\u770b\u5728\u6b66\u5f53\u6d3e\u4e3b\u4eba\u9762\u4e0a\u5462\uff01\u201d \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u5929\u5c71\u8001\u9f99\u7adf\u7136\u7387\u9886\u4ed6\u4e24\u4e2a\u513f\u5b50\uff0c\u7fe9\u7136\u91cd\u8fd4\u7389\u771f\u9053\u9662\uff0c\u4e0d\u9700\u8981\u6b66\u5f53\u6d3e\u9053\u58eb\u4eec\u529d \u89e3\u4e86\u3002 \u3000\u3000\u98de\u9e64\u5b50\u7b49\u5b89\u6170\u4e86\u5c1a\u672a\u660e\u4e00\u756a\uff0c\u529b\u52a0\u89e3\u91ca\u53cc\u65b9\u4e0d\u53ef\u8bef\u4f1a\uff0c\u5e76\u9080\u718a\u501c\u4fe9\u56de\u738b\u771f\u9053\u9662\u8d74\u5bb4\uff0c\u8a00 \u8bcd\u6781\u4e3a\u8bda\u6073\u3002 \u3000\u3000\u718a\u501c\u5374\u5fc3\u91cc\u8bf4\u4e0d\u51fa\u7684\u5f77\u5fa8\u3001\u7126\u8651\uff0c\u6068\u4e0d\u5f97\u7acb\u65f6\u53bb\u89c1\u7740\u590f\u82b8\u3001\u628a\u4e00\u5207\u5e94\u8be5\u8c08\u7684\u5411\u4f0a\u4eba\u8868\u767d \u4e00\u4e0b\uff0c\u53ef\u4ee5\u8bf4\u4ed6\u5df2\u5fc3\u4e71\u5982\u9ebb\u3002 \u3000\u3000\u4ed6\u6fc0\u52a8\u7684\u62c9\u7740\u5c1a\u672a\u660e\u7684\u624b\u8bf4\uff1a\u201c\u6211\u81ea\u5df1\u7684\u4e8b\uff0c\u4e0d\u5fc5\u518d\u9ebb\u70e6\u5c1a\u5927\u54e5\u4e86\uff0c\u8bf7\u56de\u53bb\u548c\u5404\u4f4d\u524d \u8f88\uff0c\u5404\u6d3e\u9ad8\u624b\u6b22\u805a\uff0c\u718a\u67d0\u5c1a\u6709\u8981\u4e8b\uff0c\u70e6\u4ee3\u6211\u5411\u5999\u4e00\u524d\u8f88\u544a\u7f6a\uff1a\u660e\u6625\u2026\u2026\u201d\u718a\u501c\u4f3c\u4e4e\u4e0d\u80fd\u51b3\u5b9a \u65e5\u671f\uff0c\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u5411\u98de\u9e64\u5b50\u9053\uff1a\u201c\u65e0\u8bba\u5982\u4f55\uff0c\u660e\u6625\u6211\u4e00\u5b9a\u8d76\u56de\u6b66\u5f53\uff0c\u542c\u5019\u5999\u4e00\u524d\u8f88\u9a71\u4f7f\uff0c \u5171\u8d74\u541b\u5c71\u4e4b\u4f1a\uff01\u6055\u6211\u4e0d\u518d\u5411\u5404\u4f4d\u9053\u957f\u4e00\u4e00\u544a\u8f9e\u4e86\u3002\u201d \u3000\u3000\u718a\u501c\u628a\u65f6\u95f4\u62d6\u5f97\u8fd9\u4e48\u957f\uff0c\u90a3\u4e48\u4ed6\u8981\u53bb\u5f88\u8fdc\u7684\u5730\u65b9\u4e48\uff1f\u53c8\u53bb\u505a\u4e9b\u4ec0\u4e48\uff1f\u4f7f\u5c1a\u672a\u660e\u5927\u4e3a\u5403 \u60ca\uff0c\u4ed6\u548c\u718a\u501c\u76f8\u8bc6\u4ee5\u6765\uff0c\u809d\u80c6\u76f8\u7167\uff0c\u65e0\u5f02\u9aa8\u8089\uff0c\u600e\u5fcd\u4e00\u523b\u4ed6\u79bb\uff1f\u53c8\u6050\u718a\u501c\u4e3a\u4e86\u590f\u82b8\uff0c\u72ec\u95ef\u5929 \u9634\u6559\u7f51\u7f57\uff0c\u5403\u4e86\u5927\u4e8f\uff0c\u4e0d\u7531\u8bf4\u9053\uff1a\u201c\u718a\u501c\u5927\u54e5\u4e0d\u8ba9\u6211\u540c\u53bb\uff0c\u4f7f\u6211\u5fc3\u5b9e\u4e0d\u5b89\uff01\u5c1a\u67d0\u6d6a\u8ff9\u6c5f\u6e56\uff0c \u96be\u5f97\u77e5\u5df1\uff0c\u4f60\u7684\u4e8b\u4e5f\u5c31\u662f\u6211\u7684\u4e8b\u3002\u4f60\u4e0d\u613f\u5728\u8fd9\u513f\u803d\u5ef6\uff0c\u6211\u56de\u53bb\u544a\u8bc9\u5e38\u5927\u54e5\u7530\u59d0\u59d0\u4e00\u58f0\uff0c\u6211\u4eec \u4e00\u540c\u5e2e\u4f60\u4e9b\u5fd9\uff0c\u603b\u6bd4\u4f60\u4e00\u4eba\u53ef\u591a\u51d1\u4e9b\u610f\u89c1\u529e\u6cd5\uff0c\u4f60\u5728\u8c37\u57ce\u5ba2\u5e97\u4e2d\u7b49\u5019\u5427\uff01\u201d \u3000\u3000\u718a\u501c\u8bf4\uff1a\u201c\u8fd9\u4e0d\u662f\u5927\u54e5\u4eec\u6240\u80fd\u5e2e\u5fd9\u7684\u4e8b\uff0c\u6b64\u65f6\u65e0\u6687\u8be6\u8bf4\uff0c\u7ea6\u5b9a\u65e5\u671f\u867d\u8fdc\u5728\u660e\u6625\uff0c\u4f46\u5929\u9634 \u6559\u6709\u4ec0\u4e48\u4fe1\u4e49\u53ef\u8a00\uff0c\u968f\u65f6\u53ef\u80fd\u8822\u52a8\uff0c\u5927\u54e5\u4eec\u4e0e\u6b66\u5f53\u6d3e\u540c\u5fc3\u534f\u529b\uff0c\u6f84\u6e05\u5a07\u6c1b\uff0c\u65b9\u4e3a\u4e0a\u7b56\uff01\u201d\u53c8 \u53f9\u606f\u9053\uff1a\u201c\u6211\u4e0d\u662f\u62bd\u8eab\u907f\u4e8b\uff01\u800c\u662f\u53e6\u6709\u672c\u8eab\u4e00\u5b97\u79c1\u4ec7\u672a\u4e86\uff0c\u5e76\u4e14\u4e0e\u590f\u59d1\u5a18\u6709\u5173\uff0c\u5927\u54e5\u4eec\u80fd\u53c2 \u52a0\u5728\u91cc\u9762\u4e48\uff1f\u5927\u54e5\u76db\u610f\uff0c\u6211\u662f\u975e\u5e38\u611f\u6fc0\u7684\uff0c\u6700\u8fdf\u660e\u6625\u91cd\u5728\u6b66\u5f53\u76f8\u4f1a\uff0c\u5927\u54e5\u53c8\u4f55\u5fc5\u4f9d\u4f9d\u60dc\u522b \u5462\uff1a\u201c\u5c1a\u672a\u660e\u5fc3\u91cc\u65e9\u6253\u5b9a\u4e86\u4e3b\u610f\uff0c\u5411\u718a\u501c\u4ea4\u6362\u4e86\u4e00\u4e0b\u773c\u5149\uff0c\u6073\u5207\u5730\u63e1\u7740\u718a\u501c\u7684\u624b\u8bf4\uff1a\u201c\u524d\u9014 \u518d\u89c1\u3002\u201d \u7b2c\u5341\u7ae0\u3000\u5927\u6218\u5929\u9634\u6559>> \u53e4\u9f99\u300a\u82cd\u7a79\u795e\u5251\u300b \u7b2c\u5341\u7ae0\u3000\u5927\u6218\u5929\u9634\u6559 \u3000\u3000\u718a\u501c\u5fc3\u7406\u7eb7\u4e71\u7684\u60c5\u5f62\uff0c\u6b63\u5982\u4e00\u56e2\u4e71\u9ebb\u3002 \u3000\u3000\u718a\u501c\u8349\u8349\u4e0e\u98de\u9e64\u5b50\u7b49\u522b\u8fc7\uff0c\u72ec\u81ea\u5f1b\u4e0b\u5c71\u53bb\uff0c\u6700\u4f7f\u4ed6\u60ca\u5f02\u7684\u662f\u5c71\u4e0b\u7adf\u4e0d\u65f6\u9047\u89c1\u9ed1\u8863\u52b2\u88c5\u7684 \u6c49\u5b50\uff0c\u5206\u660e\u90fd\u662f\u5929\u9634\u6559\u7684\u722a\u7259\uff0c\u4f7f\u718a\u501c\u6df1\u6df1\u5730\u5438\u4e86\u53e3\u6c14\uff0c\u89c9\u51fa\u6b66\u5f53\u5c71\u5b9e\u662f\u5904\u4e8e\u6781\u4e0d\u5229\u7684\u5730 \u4f4d\u3002 \u3000\u3000\u718a\u501c\u60f4\u60f4\u4e0d\u5b89\u7684\u56de\u81f3\u8c37\u57ce\u5ba2\u6808\u3002 \u3000\u3000\u591c\u8272\u6c89\u6c89\u5730\u5782\u4e0b\u4e86\u4e00\u5c42\u9ed1\u5f71\uff0c\u718a\u501c\u7684\u5fc3\u5feb\u8981\u71c3\u70e7\u8d77\u6765\uff0c\u672c\u60f3\u8fde\u591c\u8d76\u5f80\u6c5f\u9675\uff0c\u800c\u602a\u5f02\u7684\u4e8b \u53c8\u53d1\u751f\u4e86\uff0c\u718a\u501c\u8981\u4e9b\u83dc\u996d\u72fc\u541e\u864e\u54bd\uff0c\u751a\u81f3\u4ed6\u4e0d\u77e5\u81ea\u5df1\u5403\u4e0b\u4e9b\u4ec0\u4e48\uff0c\u4f55\u51b5\u83dc\u7684\u6ecb\u5473\u5462\uff1f\u5e97\u4f19\u8ba1 \u5219\u63a2\u8eab\u8fdb\u6765\u8bf4\uff1a\u201c\u718a\u5ba2\u5b98\uff0c\u4f60\u5bb6\u8fd8\u6709\u4e24\u4f4d\u719f\u670b\u53cb\u5417\uff1f\u201d\u718a\u501c\u6014\u4e86\u4e00\u6014\uff0c\u4ed6\u60f3\u4e0d\u51fa\u8fd8\u6709\u4ec0\u4e48\u670b \u53cb\uff0c\u4f19\u8ba1\u81ea\u4f5c\u806a\u660e\u7684\u6324\u6324\u773c\u775b\uff0c\u795e\u79d8\u5730\u7b11\u9053\uff1a\u201c\u4f60\u5bb6\u8fd9\u4e24\u4f4d\u670b\u53cb\uff0c\u6bd4\u4f60\u5e74\u8f7b\uff0c\u5979\u4fe9\u6682\u501f\u4f60\u5bb6 \u548c\u5c1a\u5ba2\u5b98\u7684\u5750\u9a6c\u4e00\u7528\uff0c\u660e\u5929\u4e00\u65e9\u5c31\u9001\u56de\u672a\u7684\u3002\u201d\u4e49\u4f4e\u58f0\u8bf4\uff1a\u201c\u597d\u6f02\u4eae\u7684\u4e24\u4e2a\u5c0f\u599e\u513f\uff0c\u4f60 \u5bb6\u2026\u2026\u201d \u3000\u3000\u4f19\u8ba1\u4e0d\u77e5\u8fd8\u60f3\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u718a\u501c\u5927\u51fa\u610f\u5916\uff0c\u81ea\u7136\u4ed6\u4f1a\u8054\u60f3\u5230\u590f\u82b8\u8eab\u4e0a\uff0c\u96be\u9053\u5979\u5df2\u7ecf\u6765\u81f3\u8c37 \u57ce\uff01 \u3000\u3000\u4f46\u662f\u53e6\u4e00\u4f4d\u5973\u5b50\u53c8\u662f\u4ec0\u4e48\u4eba\u5462\uff1f\u718a\u501c\u9762\u601d\u4ecd\u4e0d\u5f97\u5176\u89e3\uff0c\u4ed6\u5fd9\u8ffd\u95ee\u4f19\u8ba1\uff0c\u8fd9\u4e24\u4e2a\u5973\u5b50\u7684\u5bb9 \u8c8c\u8863\u7740\u59d3\u540d\u7b49\u3002 \u3000\u3000\u4f19\u8ba1\u4e5f\u6115\u4e86\u9053\uff1a\u201c\u65e2\u662f\u4f60\u5bb6\u7684\u670b\u53cb\uff0c\u4f60\u5bb6\u8fd8\u4e0d\u6653\u5f97\u5417\uff1f\u201d \u3000\u3000\u8fd9\u4e00\u8bf4\u53c8\u628a\u718a\u501c\u50f5\u5f97\u65e0\u8bdd\u53ef\u8bf4\u3002 \u3000\u3000\u8fd9\u4e2a\u4f19\u8ba1\u9876\u7231\u778e\u4e09\u8bdd\u56db\uff0c\u800c\u5f97\u610f\u5730\u6ed4\u6ed4\u4e0d\u7edd\u8bb2\u4e86\u4e0b\u53bb\uff1a\u201c\u4e24\u4e2a\u599e\u513f\uff0c\u90fd\u7a7f\u7684\u4e00\u8eab\u96ea\u767d\u8863 \u670d\uff0c\u5c0f\u7684\u53ef\u4e0d\u6562\u4ed4\u7ec6\u76ef\u4f4f\u4eba\u5bb6\u77a7\uff0c\u6211\u662f\u9876\u8001\u5b9e\u7684\u4eba\u5440\uff01\u4e00\u4e2a\u5934\u4e0a\u5305\u7740\u9752\u8272\u7ee2\u5e15\uff0c\u8fd9\u4f4d\u59d1\u5a18\u662f \u4e2a\u51b7\u9762\u5b54\uff0c\u4e0d\u5927\u7231\u7406\u4eba\u7684\u3002\u201d \u3000\u3000\u4f19\u8ba1\u53c8\u9053\uff1a\u201c\u53e6\u4e00\u4f4d\u59d1\u5a18\uff0c\u5634\u89d2\u8001\u662f\u5e26\u7740\u751c\u751c\u7684\u5fae\u7b11\uff0c\u5934\u4e0a\u7528\u7ea2\u7ee2\u5305\u624e\uff0c\u90fd\u50cf\u5b98\u5ba6\u4eba\u5bb6 \u5c0f\u59d0\uff0c\u5c0a\u8d35\u65e0\u6bd4\u3002\u201d \u3000\u3000\u8fd9\u4f7f\u718a\u501c\u66f4\u52a0\u9677\u5165\u8ff7\u9635\uff0c\u542c\u53bb\u90fd\u4e0d\u50cf\u590f\u82b8\uff0c\u4f46\u8fd9\u53c8\u662f\u4ec0\u4e48\u6765\u5386\u7684\u4eba\u7269\uff1f\u660e\u660e\u7d20\u4e0d\u76f8\u8bc6\uff0c \u5374\u8981\u81ea\u79f0\u662f\u4ed6\u548c\u5c1a\u672a\u660e\u7684\u670b\u53cb\uff0c\u718a\u501c\u7591\u5fc3\u91cd\u91cd\uff0c\u597d\u5728\u660e\u65e9\u4eba\u5bb6\u4f1a\u628a\u9a6c\u5339\u9001\u56de\u6765\uff0c\u5230\u65f6\u81ea\u53ef\u770b \u770b\u662f\u4ec0\u4e48\u6765\u8def\u3002 \u3000\u3000\u718a\u501c\u95ee\u9053\uff1a\u201c\u5979\u4fe9\u65e2\u7136\u77e5\u9053\u6211\u4eec\u7684\u59d3\u540d\uff0c\u5979\u4eec\u6709\u6ca1\u6709\u7559\u4e0b\u4ec0\u4e48\u8bdd\uff1f\u5979\u4fe9\u7684\u59d3\u6c0f\u53ef\u66fe\u544a\u8bc9 \u4f60\uff1f\u8bf7\u4f60\u8be6\u7ec6\u8bf4\u4e00\u4e0b\uff0c\u8ba9\u6211\u60f3\u60f3\u662f\u54ea\u513f\u6765\u7684\u670b\u53cb\uff1f\u201d \u3000\u3000\u718a\u501c\u8bf4\u5f97\u975e\u5e38\u8f7b\u677e\uff0c\u5e97\u4f19\u8ba1\u7b11\u9053\uff1a\u201c\u5c82\u4f46\u77e5\u9053\u4e24\u4f4d\u7684\u59d3\u540d\uff0c\u800c\u4e14\u8fd8\u8bf4\u8fc7\uff0c\u7b49\u4f60\u5bb6\u81ea\u6b66\u5f53 \u5c71\u56de\u6765\uff0c\u518d\u8f6c\u8fbe\u4e00\u58f0\uff0c\u4e34\u65f6\u501f\u7528\u5750\u9a6c\uff0c\u4e0d\u53ca\u5f53\u9762\u81f4\u8c22\u5462\u3002\u53ef\u662f\u4e24\u4f4d\u59d1\u5a18\u5374\u4e0d\u66fe\u81ea\u5df1\u8868\u660e\u59d3 \u540d\uff0c\u8fd9\u5c0f\u7684\u4e5f\u4e0d\u6562\u591a\u95f4\uff0c\u4f60\u5bb6\u4e45\u8d70\u6c5f\u6e56\uff0c\u8c05\u6765\u4ea4\u7ed3\u7684\u670b\u53cb\u5f88\u591a\uff0c\u4e00\u65f6\u8bb0\u4e0d\u8d77\u6765\u3002\u201d \u3000\u3000\u718a\u501c\u6258\u4ed6\u660e\u6668\u9001\u56de\u9a6c\u5339\u65f6\uff0c\u52a1\u5fc5\u628a\u4e24\u4e2a\u767d\u8863\u5973\u5b50\uff0c\u7559\u4f4f\u89c1\u89c1\u9762\uff0c\u4f19\u8ba1\u6ee1\u53e3\u7684\u5e94\u8bfa\uff0c\u53c8\u795e \u79d8\u5730\u4e00\u7b11\uff0c\u8bf4\uff1a\u201c\u7f8e\u6781\u4e86\uff0c\u753b\u4e5f\u753b\u4e0d\u51fa\u6765\uff0c\u548c\u4f60\u5bb6\u6765\u7684\u90a3\u4fe9\u5802\u5ba2\uff0c\u4e00\u6837\u7684\u7f8e\uff0c\u800c\u4e14\u8fd8\u5e74\u8f7b\u5f97 \u591a\u3002\u201d\u4f19\u8ba1\u89c1\u718a\u501c\u6001\u5ea6\u5e84\u91cd\uff0c\u4f3c\u4e4e\u628a\u8bb8\u591a\u6e9c\u5230\u53e3\u8fb9\u7684\u8bdd\uff0c\u90fd\u54bd\u4e86\u56de\u53bb\uff0c\u6700\u540e\u4ecd\u7136\u8865\u4e0a\u4e00\u53e5\uff1a \u201c\u4e0d\u8fc7\u5979\u4eec\u90fd\u50cf\u662f\u8001\u8d70\u6c5f\u6e56\u7684\u4eba\u5462\u3002\u201d \u3000\u3000\u718a\u501c\u7531\u590f\u82b8\u8eab\u4e0a\u60f3\u8d77\uff0c\u60f3\u53ca\u751f\u5e73\u6240\u9047\u89c1\u8fc7\u7684\u5c11\u5973\uff0c\u53ea\u6709\u4e1c\u65b9\u745b\u3001\u6563\u82b1\u4ed9\u5b50\u6570\u4eba\uff0c\u4f7f\u4ed6\u53c8 \u91cd\u65b0\u52a0\u5165\u4e00\u79cd\u7591\u8651\u3002 \u3000\u3000\u6b21\u6668\u65e5\u4e0a\u4e09\u7aff\uff0c\u718a\u501c\u65b9\u624d\u8d77\u8eab\u6f31\u5b8c\uff0c\u4ed6\u60df\u6050\u8bef\u4e86\u90a3\u4e24\u4f4d\u8fd8\u9a6c\u5973\u5b50\u6765\u4e34\u7684\u673a\u4f1a\uff0c\u4f46\u662f\u4ed6\u7ec8 \u4e8e\u5931\u671b\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u5e76\u6ca1\u6709\u5982\u4ed6\u610f\u6599\uff0c\u4e24\u4e2a\u767d\u8863\u5c11\u5973\u7684\u5029\u5f71\uff0c\u59cb\u7ec8\u672a\u5728\u5ba2\u6808\u524d\u518d\u73b0\uff0c\u5e97\u4f19\u8ba1\u634f\u7740\u4e00\u4e00\u628a \u6c57\uff0c\u60df\u6050\u662f\u9047\u4e0a\u4e86\u9a97\u5b50\uff0c\u800c\u591a\u5c11\u4ed6\u987b\u62c5\u627f\u8fd9\u4e2a\u62c5\u5b50\uff0c\u8981\u8d54\u5ba2\u4eba\u88ab\u9a97\u7684\u9a6c\u5440\uff01 \u3000\u3000\u718a\u501c\u7b49\u5019\u4e86\u534a\u5929\uff0c\u4ee3\u66ff\u8fd8\u9a6c\u5973\u5b50\u800c\u6765\u7684\u5374\u662f\u5c1a\u672a\u660e\u3002 \u3000\u3000\u5c1a\u672a\u660e\u6628\u591c\u8fd4\u56de\u7389\u771f\u9053\u9662\uff0c\u6b66\u5f53\u6d3e\u4eba\u4ee5\u6781\u7cbe\u7f8e\u4e30\u76db\u7684\u5bb4\u5e2d\u548c\u7279\u917f\u7684\u836f\u9152\uff0c\u62db\u5f85\u5404\u65b9\u8c6a\u6770 \u6b22\u547c\u7545\u996e\u3002 \u3000\u3000\u5929\u9634\u6559\u4eba\u51fa\u6ca1\u65e0\u5e38\uff0c\u4f7f\u5999\u4e00\u771f\u4eba\u4e3a\u4e4b\u8c08\u864e\u8272\u53d8\uff0c\u4f17\u4eba\u4e5f\u90fd\u51db\u51db\u81ea\u5371\uff0c\u5927\u591a\u6570\u6c5f\u5357\u6b66\u5e08\u90fd \u6050\u5355\u72ec\u884c\u52a8\u906d\u53d7\u88ad\u51fb\uff0c\u6b66\u5f53\u6d3e\u66f4\u5df4\u4e0d\u5f97\u4f17\u4eba\u90fd\u7559\u5728\u5c71\u4e0a\uff0c\u4e8e\u662f\u91cd\u65b0\u4f5c\u4e86\u4e00\u79cd\u90e8\u7f72\uff0c\u51b3\u5b9a\u5148\u8083 \u6e05\u8944\u9633\u5e9c\u9644\u8fd1\u7684\u5996\u6c1b\u3002 \u3000\u3000\u5c1a\u672a\u660e\u548c\u6563\u82b1\u4ed9\u5b50\u5bc6\u8c08\u4e4b\u540e\uff0c\u5e38\u6f2b\u5929\u4ee5\u4e3a\u718a\u501c\u5fc5\u6709\u9690\u60c5\uff0c\u65e0\u987b\u5e72\u9884\u4ed6\u7684\u9690\u79c1\uff0c\u662f\u6545\u4ed6\u592b \u5987\u9664\u4e86\u51c6\u5907\u4e00\u73b0\u8eab\u624b\u4e4b\u5916\uff0c\u4ecd\u62df\u6682\u65f6\u56de\u751c\u751c\u8c37\u4e00\u884c\uff0c\u56e0\u4e3a\u5374\u4e0d\u8fc7\u6b66\u5f53\u6d3e\u4eba\u7684\u6bb7\u52e4\u6b3e\u5f85\u4e4b\u60c5\uff0c \u51b3\u5b9a\u6682\u7559\u4e00\u65e5\u3002 \u3000\u3000\u5c1a\u672a\u660e\u9042\u5411\u98de\u9e64\u5b50\u7b49\u544a\u522b\uff0c\u6765\u8ffd\u718a\u501c\u3002 \u3000\u3000\u5931\u9a6c\u7684\u4e8b\uff0c\u4e5f\u5927\u51fa\u5c1a\u672a\u660e\u7684\u610f\u5916\uff0c\u4ed6\u5f88\u673a\u8b66\u5730\u5224\u65ad\u51fa\u6765\u662f\u5929\u9634\u6559\u4eba\u6240\u8bbe\u4e0b\u7684\u9677\u9631\uff0c\u4e0d\u8fc7 \u731c\u4e0d\u51fa\u7528\u610f\u6240\u5728\u3002 \u3000\u3000\u718a\u501c\u65e0\u6cd5\u6291\u5236\u7126\u6025\u7684\u5fc3\uff0c\u9042\u4e0e\u5c1a\u672a\u660e\u5c31\u5728\u5f53\u5730\u53e6\u9009\u8d2d\u4e86\u4e24\u5339\u5757\u5934\u9ad8\u5927\u7684\u9a6c\uff0c\u5373\u65e5\u542f\u7a0b\u5357 \u4e0b\u3002 \u3000\u3000\u5c1a\u672a\u660e\u4e43\u4e24\u6cb3\u603b\u74e2\u628a\u5b50\uff0c\u968f\u8eab\u643a\u5e26\u73e0\u5b9d\uff0c\u90fd\u4ef7\u503c\u8fde\u57ce\uff0c\u5931\u53bb\u4e24\u5339\u9a6c\u539f\u53ea\u4ed8\u8bf8\u4e00\u7b11\uff0c\u4f46\u8fd9 \u4e8b\u6bd5\u7adf\u6765\u5f97\u592a\u7a81\u5140\u4e86\uff0c\u9042\u6210\u4e3a\u4ed6\u4fe9\u7814\u7a76\u7684\u4e00\u9879\u95ee\u9898\u3002 \u3000\u3000\u5f53\u65e5\u62b5\u8fbe\u8944\u9633\uff0c\u6b21\u6668\u6cbf\u6c49\u6c34\u5411\u5b9c\u57ce\u8fdb\u53d1\u3002 \u3000\u3000\u79cb\u9ad8\u6c14\u723d\uff0c\u6cbf\u9014\u4ecd\u7136\u6797\u6728\u8471\u7b3c\uff0c\u91ce\u82b1\u7eb7\u5217\uff0c\u4e24\u4eba\u7b56\u9a6c\u9a70\u51fa\u56db\u5341\u4f59\u91cc\uff0c\u773c\u524d\u51fa\u73b0\u4e86\u81ea\u897f\u800c \u6765\u7684\u4e00\u6761\u53c8\u9053\uff0c\u67ab\u6749\u4ea4\u5e03\uff0c\u7fe0\u8272\u8fce\u4eba\uff0c\u8fd9\u6761\u8def\u4ed6\u4fe9\u5df2\u5f80\u8fd4\u4e86\u4e24\u8d9f\uff0c\u65e0\u5fc3\u53bb\u8d4f\u73a9\u666f\u8272\uff0c\u5374\u81ea\u53c9 \u8def\u4e0a\u9e3e\u94c3\u54cd\u5904\uff0c\u5e76\u5217\u9a70\u6765\u53cc\u9a91\u3002 \u3000\u3000\u9a6c\u4e0a\u4e00\u53cc\u5341\u516d\u516b\u5c81\u5a07\u67d4\u660e\u5a9a\u7684\u767d\u8863\u52b2\u88c5\u5c11\u5973\uff0c\u6b63\u5982\u90a3\u5e97\u4f19\u8ba1\u6240\u8ff0\uff0c\u7f8e\u8273\u7edd\u4f26\uff0c\u800c\u5934\u5305\u9752 \u7ee2\u7684\u9762\u7f69\u79cb\u971c\uff0c\u795e\u8272\u6781\u4e3a\u51b7\u8083\uff0c\u7ea2\u7ee2\u5e15\u5305\u5934\u7684\u5219\u6d45\u7b11\u76c8\u76c8\uff0c\u79c0\u76ee\u76fc\u7750\uff0c\u4f3c\u9732\u51fa\u65e0\u9650\u52a8\u4eba\u7684\u98ce \u81f4\u3002 \u3000\u3000\u5947\u602a\u7684\u4e24\u4e2a\u5c11\u5973\u7adf\u7b56\u9a6c\u76f4\u5411\u4ed6\u4fe9\u51b2\u6765\uff0c\u9752\u7ee2\u5305\u5934\u7684\u5c11\u5973\u5411\u4ed6\u4fe9\u7528\u79c0\u76ee\u4e0d\u5728\u610f\u5730\u8f7b\u8f7b\u4e00 \u63a0\uff0c\u800c\u90a3\u4e00\u4f4d\u5c11\u5973\uff0c\u5374\u6ee1\u9762\u6625\u8272\uff0c\u5148\u63a0\u4e86\u718a\u501c\u4e00\u773c\uff0c\u53c8\u628a\u76ee\u5149\u79fb\u5411\u5c1a\u672a\u660e\uff0c\u800c\u5979\u7684\u79cb\u6ce2\uff0c\u4e00 \u76f4\u95ea\u95ea\u653e\u5149\uff0c\u76ef\u7740\u5c1a\u672a\u660e\u3002 \u3000\u3000\u718a\u501c\u548c\u5c1a\u672a\u660e\u8840\u6c14\u65b9\u521a\uff0c\u81ea\u7136\u773c\u524d\u4e00\u4eae\u4e4b\u4e0b\uff0c\u89e6\u76ee\u7adf\u6709\u4e9b\u5fc3\u65cc\u6447\u6447\uff0c\u5979\u4fe9\u90a3\u5339\u9a6c\u53c8\u7bad\u4e00 \u822c\u76f4\u51b2\u8fc7\u6765\uff0c\u82e5\u4e0d\u6536\u52d2\u5750\u9a6c\uff0c\u56db\u4eba\u56db\u9a91\u4f1a\u649e\u5728\u4e00\u5806\u4e86\u3002 \u3000\u3000\u5999\u5728\u4e24\u4e2a\u5c11\u5973\u9a91\u672f\u6bd4\u4ed6\u4fe9\u8fd8\u6765\u5f97\u9ad8\u660e\uff0c\u6070\u597d\u51b2\u81f3\u4ed6\u4fe9\u8eab\u8fb9\uff0c\u76f8\u8ddd\u4e0d\u53ca\u4e09\u5c3a\uff0c\u628a\u9a6c\u5934\u52d2 \u6ce8\u3002 \u3000\u3000\u7ea2\u5e15\u5c11\u5973\u5a07\u7b11\u7740\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u5979\u7b11\u5f97\u90a3\u4e48\u751c\uff0c\u800c\u79c0\u76ee\u4e00\u76f4\u548c\u5c1a\u672a\u660e\u5728\u76f8\u5bf9\u51dd\u89c6\uff0c\u5979\u7b11\u5f97 \u5982\u540c\u82b1\u679d\u6447\u98a4\uff0c\u55d4\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4e2a\u4eba\u6beb\u6ca1\u9053\u7406\uff0c\u4e0d\u662f\u6211\u52d2\u4f4f\u9a6c\uff0c\u65e9\u649e\u5728\u4e00\u8d77\u4e86\uff01\u771f\u628a\u4eba\u5413\u4e86 \u4e00\u5927\u8df3\uff01\u201d\u9752\u7ee2\u5e15\u5c11\u5973\u5219\u7565\u540e\u6570\u5c3a\uff0c\u5979\u4f3c\u770b\u4e0d\u8d2f\u5979\u7684\u540c\u4f34\u7684\u5a07\u5a06\u4e3e\u52a8\uff0c\u5411\u5979\u80cc\u4e0a\u72e0\u72e0\u76f8\u4e86\u4e00 \u773c\uff0c\u7adf\u81ea\u62cd\u9a6c\u6a2a\u8d8a\u5b98\u9053\uff0c\u6b63\u597d\u6321\u5728\u718a\u501c\u5c1a\u672a\u660e\u9a6c\u524d\u3002 \u3000\u3000\u4ed6\u4fe9\u60f3\u8d70\u4e5f\u8d70\u4e0d\u6210\u4e86\uff0c\u800c\u5c1a\u672a\u660e\u6b63\u4e3a\u90a3\u7ea2\u5e15\u5c11\u5973\u7684\u4e30\u59ff\u6115\u4f4f\u4e86\uff0c\u8ddd\u79bb\u592a\u8fd1\uff0c\u4f7f\u4ed6\u5f97\u4ee5\u9971 \u9910\u79c0\u8272\u3002 \u3000\u3000\u7ea2\u5e15\u5c11\u5973\u53c8\u7b11\u9053\uff1a\u201c\u554a\u5440\uff01\u539f\u6765\u662f\u718a\u5927\u4fa0\u548c\u5c1a\u5f53\u5bb6\u7684\uff0c\u6055\u6211\u773c\u62d9\u8fd8\u6ca1\u6709\u770b\u6e05\u5462\uff01\u4e24\u4f4d\u4e0d \u8981\u5c0a\u9a91\u4e86\u4e48\uff1f\u6211\u548c\u7709\u59b9\u6b63\u662f\u9001\u8fd8\u4e8c\u4f4d\u5927\u4fa0\u7684\u5b9d\u9a6c\uff0c\u82e5\u662f\u60dc\u8fc7\u4e86\u90a3\u66f4\u9ebb\u70e6\uff0c\u522b\u8ba9\u5c1a\u5f53\u5bb6\u7684\u7591\u5fc3 \u6211\u59ca\u59b9\u662f\u9a6c\u9a97\u5b50\uff01\u201d \u3000\u3000\u718a\u501c\u548c\u5c1a\u672a\u660e\u540c\u65f6\u4e00\u60ca\uff0c\u65b9\u770b\u51fa\u4e24\u4e2a\u6b65\u5973\u6b63\u9a91\u7740\u4ed6\u4eec\u7684\u9a6c\uff0c\u663e\u7136\u8fd9\u5176\u4e2d\u5927\u6709\u6587\u7ae0\u4e86\uff01\u718a \u501c\u6beb\u4e0d\u5728\u610f\u5730\u62f1\u624b\u8bf4\uff1a\u201c\u4e24\u4f4d\u59d1\u5a18\uff0c\u718a\u67d0\u7d20\u6627\u5e73\u751f\uff0c\u533a\u533a\u4e24\u5339\u52a3\u9a6c\uff0c\u4f55\u5fc5\u8ba4\u771f\u8d77\u6765\u4ea4\u8fd8 \u5462\uff1f\u201d \u3000\u3000\u7ea2\u5e15\u5c11\u5973\u655b\u887d\u4e00\u798f\u9053\uff1a\u201c\u4e0d\u7792\u4e24\u4f4d\u4fa0\u58eb\uff0c\u6211\u4e43\u5929\u9634\u6559\u81ea\u51e4\u5802\u7a1a\u51e4\u575b\u4e3b\u6731\u6b22\uff0c\u5979\u662f\u6211\u7684\u52a9 \u624b\uff0c\u5d06\u5cd2\u5973\u6770\u67f3\u7709\uff0c\u5916\u53f7\u4e91\u4e2d\u9752\u51e4\u3002\u718a\u5927\u4fa0\u548c\u5c1a\u5f53\u5bb6\u7684\uff0c\u96be\u9053\u8fd8\u4e0d\u660e\u81ea\u6211\u4eec\u7684\u6765\u610f\u5417\uff1f\u201d\u8bf4 \u5b8c\uff0c\u5411\u7740\u5c1a\u672a\u660e\u5ae3\u7136\u4e00\u7b11\u3002 \u3000\u3000\u5c1a\u672a\u660e\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\u4eec\u4e13\u7a0b\u6765\u8fd8\u9a6c\uff0c\u5176\u5b9e\u8fd9\u662f\u591a\u4f59\u7684\uff0c\u4e24\u5339\u9a6c\u6240\u503c\u51e0\u4f55\uff0c\u53ea\u662f\u59d1\u5a18\u4eec\u8eab \u5217\u5929\u9634\u6559\u6559\u4e0b\uff0c\u5012\u4f7f\u5c1a\u67d0\u4e0d\u80dc\u5a49\u60dc\uff01\u201d \u3000\u3000\u7ea2\u5e15\u5c11\u5973\u9053\uff1a\u201c\u5c1a\u5f53\u5bb6\u7684\u72ec\u9738\u4e24\u6cb3\u9053\u4e0a\uff0c\u81ea\u7136\u770b\u4e0d\u8d77\u8fd9\u4e24\u5339\u9a6c\uff0c\u4f46\u662f\u6211\u4eec\u501f\u4e86\u53ef\u4e0d\u80fd\u4e0d \u8fd8\uff0c\u5929\u9634\u6559\u4e3a\u6b66\u6797\u540c\u9053\u8c0b\u53d6\u798f\u5229\uff0c\u51e1\u662f\u5f52\u5165\u6559\u4e0b\u7684\uff0c\u524d\u9014\u4e8b\u4e1a\u4e0a\u90fd\u53d7\u5230\u4e00\u91cd\u6781\u5927\u7684\u4fdd\u969c\u548c\u534f \u52a9\u3002\u201d \u3000\u3000\u5979\u53c8\u795e\u79d8\u5730\u970e\u970e\u773c\u8bf4\uff1a\u201c\u4e24\u4f4d\u5927\u4fa0\uff0c\u8bf7\u52ff\u591a\u7591\uff0c\u6211\u4eec\u4e0d\u4f1a\u5411\u60a8\u8bf4\u6559\u7684\uff0c\u5c1a\u5f53\u5bb6\u7684\u66ff\u6211\u4eec \u60cb\u501f\u4ec0\u4e48\uff1f\u5c1a\u5f53\u5bb6\u7684\u662f\u4e24\u6cb3\u603b\u74e2\u628a\u4e8e\uff0c\u529d\u4f60\u56de\u53bb\u770b\u770b\uff0c\u4e24\u6cb3\u9053\u4e0a\u53ea\u6015\u65e9\u5df2\u58c1\u5792\u4e00\u65b0\uff0c\u65cc\u65d7\u6613 \u8272\u4e86\u5462\uff01\u201d \u3000\u3000\u6731\u6b22\u53c8\u54af\u54af\u7b11\u9053\uff1a\u201c\u5c1a\u5f53\u5bb6\u7684\u65ec\u53e5\u4e0d\u79bb\u8fd8\u9a6c\uff0c\u5176\u5b9e\u6211\u59ca\u59b9\u4e5f\u4e0d\u662f\u4e0d\u6653\u5f97\u5c1a\u5f53\u5bb6\u7684\u5a01\u540d\u9707 \u670d\u4e24\u6cb3\u7eff\u6797\u8c6a\u6770\uff0c\u8fd8\u5728\u4e4e\u8fd9\u533a\u533a\u4e4b\u7269\u3002\u5c1a\u5f53\u5bb6\u7684\u518d\u731c\u4e0a\u4e00\u731c\u6211\u4eec\u7684\u6765\u610f\u5427\uff1f\u201d \u3000\u3000\u5c1a\u672a\u660e\u5fc3\u4e2d\u7a81\u7136\u751f\u4e86\u4e00\u4e1d\u8b66\u89c9\uff0c\u672c\u80fd\u5730\u53f3\u624b\u6276\u6478\u4e86\u4e00\u4e0b\u5251\u67c4\uff0c\u4fca\u7709\u4e00\u626c\u6717\u58f0\u9053\uff1a\u201c\u96be\u9053 \u5929\u9634\u6559\u8ba9\u4f60\u4e24\u4f4d\u59d1\u5a18\u6765\u5bf9\u4ed8\u6211\u4eec\u4e0d\u6210\uff1f\u72ed\u8def\u76f8\u9022\uff0c\u7528\u4e0d\u7740\u591a\u8bf4\uff0c\u5c31\u8bf7\u52a8\u624b\u5427\uff01\u201d \u3000\u3000\u7ea2\u5e15\u5c11\u5973\u659c\u8155\u4e86\u4ed6\u4e00\u773c\uff0c\u5de7\u7b11\u76c8\u76c8\u9053\uff1a\u201c\u5c1a\u5f53\u5bb6\u7684\u592a\u8a00\u91cd\u4e86\uff01\u655d\u6559\u656c\u4ef0\u4e24\u5927\u4fa0\uff0c\u8bf7\u8fd8\u8bf7 \u4e0d\u6765\u5462\uff01\u54ea\u6709\u628a\u5ba2\u4eba\u9519\u5f85\u4e4b\u7406\uff0c\u6211\u4eec\u662f\u5949\u767d\u51e4\u5802\u5802\u4e3b\u7f2a\u8001\u524d\u8f88\u4e4b\u610f\uff0c\u7279\u6765\u8fce\u63a5\u4e24\u4f4d\u5c11\u4fa0\u5927\u9a7e \u7684\uff01\u201d \u3000\u3000\u718a\u501c\u62e8\u8f6c\u9a6c\u5934\uff0c\u62a2\u7740\u8bf4\uff1a\u201c\u7d20\u4e0d\u76f8\u8bc6\u767d\u51e4\u5802\u4e3b\uff0c\u4f55\u52b3\u8fdc\u9053\u6d3e\u4eba\u8fce\u8fd3\uff0c\u53ea\u655d\u53cb\u590f\u82b8\u59d1\u5a18\uff0c \u73b0\u5728\u4f55\u5904\uff0c\u59d1\u5a18\u82e5\u5177\u544a\u77e5\uff0c\u718a\u67d0\u4e0d\u80dc\u611f\u6fc0\uff01\u201d \u3000\u3000\u7ea2\u5e15\u5c11\u5973\u773c\u5149\u8fd8\u4e0d\u80af\u81ea\u5c1a\u672a\u660e\u8eab\u4e0a\u79fb\u5f00\uff0c\u7565\u504f\u8fc7\u5934\u6765\u6de1\u6de1\u5411\u718a\u501c\u4e00\u7b11\uff0c\u5a07\u58f0\u9053\uff1a\u201c\u8fd8\u662f \u718a\u5927\u4fa0\u8bf4\u8bdd\u723d\u4fa0\uff0c\u5176\u5b9e\u6211\u4eec\u9664\u4e86\u5949\u7f2a\u8001\u524d\u8f88\u6307\u793a\uff0c\u4e00\u591a\u534a\u8fd8\u662f\u53d7\u590f\u59d1\u5a18\u4e4b\u6258\u6765\u6566\u8bf7\u718a\u5927\u4fa0 \u5462\uff0c\u4e0d\u5fc5\u803d\u8bef\u65f6\u95f4\uff0c\u4e00\u540c\u4e0a\u9053\u5427\uff01\u201d \u3000\u3000\u7ea2\u5e15\u5c11\u5973\u53c8\u9732\u51fa\u6781\u987d\u76ae\u7684\u6837\u5b50\u7b11\u8bf4\uff1a\u201c\u590f\u59ca\u59ca\u5929\u5929\u5df4\u671b\u60a8\uff0c\u82e5\u4e0d\u662f\u5979\u2026\u2026\u201d \u3000\u3000\u718a\u501c\u60ca\u95ee\uff1a\u201c\u5979\u600e\u4e48\u4e86\uff1f\u201d \u3000\u3000\u6731\u6b22\u6545\u4f5c\u795e\u79d8\uff0c\u4e00\u62e6\u5634\u9053\uff1a\u201c\u770b\u4f60\u6025\u6210\u4ec0\u4e48\u6837\u5b50\uff01\u6211\u5305\u7ed9\u4f60\u4e00\u4e2a\u6d3b\u6cfc\u5a07\u7eb5\u7684\u82b8\u59d0\u59d0\u4e0d\u6210 \u4e48\uff1f\u201d \u3000\u3000\u7ea2\u5e15\u5c11\u5973\u770b\u51fa\u718a\u501c\u9762\u4e0a\u795e\u8272\u604d\u7136\uff0c\u4e0d\u7531\u5a07\u7b11\u8bf4\uff1a\u201c\u718a\u5927\u4fa0\u8c05\u662f\u4e0d\u6ee1\u610f\u6211\u7684\u7b54\u590d\uff0c\u8be5\u4e0d\u662f \u6015\u6211\u59ca\u59b9\u5b58\u6709\u6b79\u5ff5\uff01\u201d \u3000\u3000\u718a\u501c\u50b2\u6c14\u5982\u4e91\uff0c\u626d\u5934\u77aa\u89c6\u5979\u4e00\u773c\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u718a\u67d0\u5728\u6cf0\u5c71\u529b\u6297\u8d35\u6559\u7fa4\u96c4\uff0c\u6b64\u5fc3\u575a\u5982\u94c1 \u77f3\uff0c\u66f4\u4f55\u6015\u4ec0\u4e48\u9f99\u6f6d\u864e\u7a74\uff01\u53ea\u662f\u590f\u59d1\u5a18\u2026\u2026\u201d\u7ea2\u5e15\u5c11\u5973\u62bf\u5634\u7b11\u9053\uff1a\u201c\u82b8\u59ca\u59ca\u597d\u597d\u7684\uff0c\u7b49\u7740\u4f60 \u5462\uff01\u4f60\u8bf7\u653e\u5fc3\u5427\uff01\u201d \u3000\u3000\u718a\u501c\u51b7\u51b7\u9053\uff1a\u201c\u82e5\u662f\u6709\u4eba\u96be\u4e3a\u5979\uff0c\u718a\u67d0\u53ef\u4e0d\u80fd\u5584\u7f62\u5e72\u4f11\uff01\u201d \u3000\u3000\u7ea2\u5e15\u5c11\u5973\u548c\u4ed6\u4fe9\u5e76\u9a6c\u800c\u884c\uff0c\u5979\u5e7d\u5e7d\u5730\u53f9\u606f\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u82b8\u59ca\u59ca\u9996\u5148\u548c\u53f6\u6e05\u6e05\u4ea4\u6210\u597d\u53cb\uff0c \u53c8\u8925\u4e5d\u5929\u4ed9\u5b50\u7231\u987e\uff0c\u8c01\u6562\u6765\u96be\u4e3a\u5979\uff0c\u53c8\u662f\u4f60\u718a\u5927\u4fa0\u7684\u2026\u2026\u201d\u5979\u60f3\u4e86\u534a\u5929\u7ee7\u7eed\u8bf4\uff1a\u201c\u603b\u4e4b\uff0c\u4f60 \u718a\u5927\u4fa0\u653e\u4e00\u767e\u4e2a\u5bbd\u5fc3\u5c31\u662f\u4e86\uff01\u4e24\u5929\u540e\u4f60\u5c31\u89c1\u4e0a\u4e86\u5979\uff0c\u4f55\u82e6\u8bf4\u8fd9\u4e9b\u72e0\u8bdd\u3002\u201d \u3000\u3000\u5230\u4e86\u8346\u5dde\u5e9c\uff0c\u5929\u9634\u6559\u9f99\u987b\u575b\u4e3b\u5355\u638c\u65ad\u9b42\u5355\u98de\uff0c\u5df2\u7387\u9886\u56db\u540d\u9ed1\u8863\u4eba\u8fce\u5019\u9053\u65c1\uff0c\u718a\u501c\u5728\u98de\u7075 \u5821\u770b\u8fc7\u5355\u638c\u65ad\u9b42\u7684\u529f\u592b\uff0c\u5f53\u65f6\u4ed6\u4e00\u95fb\u9523\u58f0\uff0c\u98d8\u7136\u79bb\u53bb\uff0c\u81f4\u672a\u80fd\u4e00\u8f83\u8eab\u624b\uff0c\u4f46\u8fd9\u4eba\u65e2\u662f\u5d06\u5cd2\u6d3e \u4e0b\uff0c\u9677\u8eab\u5929\u9634\u6559\u4e0d\u662f\u5f88\u53ef\u60dc\u4e48\uff1f \u3000\u3000\u5355\u98de\u542b\u7b11\u4e3a\u793c\u9053\uff1a\u201c\u718a\u4fa0\u58eb\u4e45\u8fdd\u4e86\uff01\u8fd9\u6b21\u9a7e\u4e34\u8346\u5dde\uff0c\u76fc\u80fd\u591a\u76d8\u6853\u51e0\u65e5\uff0c\u82e5\u718a\u4fa0\u58eb\u4e0d\u541d\u8d50 \u6559\uff0c\u5355\u67d0\u51b3\u5fc3\u5949\u966a\uff0c\u4f46\u73b0\u5728\u60c5\u52bf\u548c\u98de\u7075\u5821\u5927\u4e0d\u76f8\u540c\u4e86\uff01\u201d \u3000\u3000\u4ed6\u8fd9\u4e9b\u8bdd\uff0c\u8868\u793a\u4ed6\u9887\u81ea\u8d1f\uff0c\u800c\u4e14\u6709\u4e0e\u718a\u501c\u4e00\u8f83\u77ed\u957f\u4e4b\u610f\uff0c\u718a\u501c\u867d\u4e0d\u4e3a\u4ef6\uff0c\u5374\u4ecd\u62a5\u4e0d\u5c51\u7684 \u795e\u8272\u9053\uff1a\u201c\u6731\u59d1\u5a18\u548c\u67f3\u59d1\u5a18\u8fdc\u9053\u76f8\u8fce\uff0c\u96be\u9053\u5c31\u662f\u9601\u4e0b\u8981\u548c\u718a\u67d0\u4e00\u8f83\u8eab\u624b\u4e48\uff1f\u201d \u3000\u3000\u5355\u98de\u8d25\u4e8e\u51cc\u4e91\u5b50\u5251\u4e0b\uff0c\u5e73\u65e5\u505a\u7a0d\u4e3a\u51cf\u715e\u4e9b\uff0c\u4fbf\u6362\u53e3\u6c14\u9053\uff1a\u201c\u718a\u4fa0\u58eb\u8bef\u4f1a\u4e86\uff0c\u6211\u6b63\u4ee5\u4e0a\u6b21 \u98de\u7075\u5821\u4e2d\u672a\u80fd\u9886\u6559\u7edd\u6280\u4e3a\u61be\u5462\u3002\u6b64\u6b21\u51fa\u4e8e\u7f2a\u8001\u524d\u8f88\u4e4b\u547d\uff0c\u6b63\u662f\u4e3a\u53f0\u9a7e\u548c\u590f\u59d1\u5a18\u53cc\u53cc\u5e78\u798f\u7740 \u60f3\uff0c\u8bf7\u9762\u8c12\u7f2a\u5802\u4e3b\uff0c\u4fbf\u77e5\u5176\u8be6\u3002\u201d \u3000\u3000\u7ea2\u5e15\u5c11\u5973\u5411\u5355\u98de\u81ea\u4e86\u4e00\u773c\u9053\uff1a\u201c\u5355\u575b\u4e3b\uff0c\u8fd9\u662f\u4f8b\u5916\uff0c\u7f2a\u5802\u4e3b\u8981\u4eb2\u81ea\u63a5\u5f85\uff0c\u7a1a\u51e4\u575b\u6055\u4e0d\u80fd \u8ba9\u4f60\u4f34\u966a\u4ed6\u4e8c\u4f4d\uff0c\u7528\u4e0d\u7740\u575b\u4e3b\u8d39\u795e\u4e86\uff01\u201d \u3000\u3000\u201c\u8bf4\u5b8c\u8bdd\uff0c\u5c31\u5f15\u9886\u4ed6\u4eec\u9a70\u5411\u5b85\u7b2c\u3002\u9752\u5e15\u5c11\u5973\u5ffd\u7136\u7528\u6781\u5feb\u800c\u6781\u4f4e\u7684\u58f0\u8c03\u5bf9\u5c1a\u672a\u660e\u9053\uff1a\u201c\u5c1a \u4fa0\u58eb\uff0c\u524d\u9014\u5c0f\u5fc3\uff0c\u8336\u9152\u5207\u52ff\u5165\u53e3\uff01\u201d \u3000\u3000\u5979\u4e00\u8bf4\u5b8c\uff0c\u7389\u988a\u5fae\u8d66\uff0c\u5a07\u8eaf\u633a\u8d77\u6765\uff0c\u4e00\u9886\u9a6c\u7f30\uff0c\u8fbe\u8fbe\u8fbe\u9a70\u51fa\u597d\u8fdc\u3002\u5c1a\u672a\u660e\u63a5\u53d7\u4e86\u67f3\u7709\u7684 \u8fd9\u4e00\u756a\u76db\u610f\uff0c\u81ea\u7136\u4e0d\u514d\u5fc3\u795e\u5927\u9707\u3001\u5fd9\u9644\u8033\u628a\u539f\u8bdd\u8f6c\u544a\u4e86\u718a\u501c\u3002 \u3000\u3000\u718a\u501c\u6602\u7136\u7b56\u9a6c\u81f3\u82b1\u7167\u58c1\u540e\u9762\uff0c\u548c\u5c1a\u672a\u660e\u4e00\u540c\u4e0b\u9a6c\uff0c\u516b\u5b57\u7f29\u5165\u7684\u5927\u95e8\uff0c\u7adf\u51b7\u6e05\u6e05\u5730\u63a9\u95ed\uff0c \u800c\u9644\u8fd1\u4e5f\u6781\u5c11\u4f4f\u5bb6\uff0c\u7ea2\u5e15\u5c11\u5973\u62db\u547c\u8bf4\uff1a\u201c\u9a6c\u5339\u81ea\u6709\u6211\u4eec\u7167\u6599\uff0c\u4e24\u4f4d\u5927\u4fa0\u4e0d\u5fc5\u7ba1\u4e86\u3002\u201d \u3000\u3000\u5979\u4e0a\u524d\u8f7b\u6263\u95e8\u4e0a\u94dc\u73af\uff0c\u5e94\u95e8\u7684\u662f\u4e24\u4e2a\u5782\u9afb\u767d\u8863\u5e7c\u5973\uff0c\u9038\u7136\u663e\u5f97\u6e05\u96c5\u7edd\u5c18\uff0c\u4f46\u5374\u4e0e\u8fd9\u4e48\u9ad8 \u5927\u7684\u5b85\u7b2c\u4e0d\u76f8\u79f0\u3002 \u3000\u3000\u718a\u501c\u548c\u5c1a\u672a\u660e\uff0c\u88ab\u9080\u8d70\u524d\u53bb\uff0c\u4e0d\u77e5\u4f55\u65f6\u9752\u5e15\u5c11\u5973\u5df2\u662f\u65e0\u5f71\u65e0\u8e2a\uff0c\u53e6\u6709\u4e24\u4f4d\u5341\u516b\u4e5d\u5c81\u81ea\u8863 \u5c11\u5973\uff0c\u50cf\u662f\u6731\u6b22\u7684\u59ca\u59b9\uff0c\u5979\u4eec\u4e00\u89c1\u9762\u5c31\u83ba\u55d4\u71d5\u54a4\uff0c\u55a7\u95f9\u6210\u4e00\u7247\u3002 \u3000\u3000\u5b85\u5185\u5385\u5802\u76f8\u671b\uff0c\u697c\u9601\u8fde\u4e91\uff0c\u4e0d\u77e5\u6709\u591a\u5c11\u5c42\u9662\u843d\u3002 \u3000\u3000\u4ed6\u4eec\u968f\u7740\u7a7f\u5802\u8fc7\u9662\uff0c\u95e8\u6237\u91cd\u91cd\uff0c\u5947\u602a\u7684\u6bcf\u4e00\u5904\u90fd\u662f\u9e26\u96c0\u65e0\u58f0\uff0c\u5076\u7136\u6709\u4e00\u4e8c\u81ea\u8863\u5c11\u5973\u8d70 \u52a8\uff0c\u5bc2\u9759\u5f97\u50cf\u4e00\u5ea7\u5c3c\u59d1\u5eb5\u3002 \u3000\u3000\u4ed6\u4fe9\u88ab\u5f15\u81f3\u4e00\u9762\u534e\u70db\u9ad8\u5f20\u7684\u5927\u5385\u524d\uff0c\u5eca\u4e0a\u9759\u8083\u5730\u7ad9\u7740\u56db\u5bf9\u513f\u767d\u8863\u98d8\u98d8\u7684\u5782\u9afb\u5c11\u5973\uff0c\u6625\u5170 \u79cb\u83ca\uff0c\u5404\u6781\u5176\u7f8e\uff0c\u71d5\u7626\u73af\u80a5\uff0c\u8102\u5149\u7c89\u817b\uff0c\u4ed6\u4fe9\u52a0\u5165\u4f17\u9999\u56fd\u91cc\uff0c\u76ee\u4e0d\u6687\u63a5\uff0c\u5947\u602a\u7684\u59cb\u7ec8\u6ca1\u6709\u770b \u89c1\u4e00\u4e2a\u7537\u5b50\u3002 \u3000\u3000\u5eca\u67f1\u4e0a\u4e00\u5217\u7ea2\u7eb1\u5bab\u706f\uff0c\u76c6\u5170\u96cf\u83ca\uff0c\u88c5\u9970\u5f97\u5b9b\u5982\u738b\u4faf\u5de8\u5e9c\uff0c\u800c\u5385\u4e2d\u7684\u9648\u8bbe\u66f4\u662f\u5149\u602a\u9646\u79bb\uff0c \u91d1\u8ff7\u7eb8\u9189\u3002\u7ea2\u5e15\u5c11\u5973\u5411\u73e0\u5e18\u5185\u5624\u5b81\u8eac\u8eab\u7980\u544a\uff1a\u201c\u7f2a\u5802\u4e3b\uff0c\u718a\u5927\u4fa0\u548c\u5c1a\u5f53\u5bb6\u7684\u9a7e\u5230\u3002\u201d \u3000\u3000\u5e18\u5185\u5987\u4eba\u5e94\u58f0\u8bf4\u9053\uff1a\u201c\u5feb\u8bf7\u8fdb\u6765\uff01\u201d \u3000\u3000\u7acb\u523b\u73e0\u5e18\u9ad8\u5377\uff0c\u773c\u524d\u73e0\u7fe0\u7f24\u7eb7\uff0c\u65e9\u6709\u4e00\u4f4d\u64e6\u80ed\u8102\u62b9\u7c89\uff0c\u6ee1\u5934\u66ff\u82b1\u7684\u7ea2\u8863\u8001\u5987\uff0c\u542b\u7b11\u51fa \u8fce\uff09\u718a\u501c\u5728\u6cf0\u5c71\u65f6\u4f1a\u89c1\u8fc7\u8fd9\u4e5d\u5929\u4ed9\u5b50\u7f2a\u5929\u96ef\u4e00\u9762\uff0c\u773c\u524d\u8fd8\u662f\u8fd9\u4e2a\u4e0d\u53ef\u601d\u8bae\u7684\u8001\u602a\u7269\uff0c\u56db\u5468\u6709 \u4e03\u516b\u4f4d\u767d\u8863\u7f8e\u5973\u7c07\u7ed5\u3002 \u3000\u3000\u5c1a\u672a\u660e\u51e0\u66fe\u89c1\u8fc7\u8fd9\u79cd\u602a\u573a\u9762\uff0c\u4f46\u662f\u4ed6\u5934\u4e00\u773c\u7559\u5fc3\u770b\u5230\u7684\uff0c\u662f\u90a3\u9752\u5e15\u5c11\u5973\u67f3\u7709\uff0c\u7adf\u4e5f\u7f57\u8882 \u98d8\u626c\uff0c\u4f8d\u7acb\u8001\u5987\u8eab\u65c1\u3002 \u3000\u3000\u53ea\u662f\u5c11\u5973\u67f3\u7709\u9891\u8e59\uff0c\u4f3c\u671b\u7740\u4ed6\u548c\u718a\u501c\u53e6\u542b\u6df1\u610f\u3002 \u3000\u3000\u7ea2\u8863\u8001\u5987\u7c89\u9762\u4e0a\u5806\u51fa\u7b11\u5bb9\uff0c\u4e00\u4f38\u624b\u8bf4\uff1a\u201c\u540d\u6ee1\u6c5f\u5357\u7684\u718a\u5c0f\u5f1f\u5f1f\uff0c\u5a01\u9707\u4e24\u6cb3\u7684\u5c1a\u5c0f\u5f1f\u5f1f\uff0c \u60e0\u7136\u6765\u4e34\u655d\u5802\uff0c\u5feb\u8bf7\u91cc\u9762\u7545\u8c08\u4e00\u4e0b\uff0c\u8001\u8eab\u8fd9\u4e9b\u5c0f\u59b9\u59b9\u4eec\u62db\u5f85\u4e0d\u5468\uff0c\u4e24\u4f4d\u90fd\u662f\u81ea\u5df1\u4eba\u591a\u5305\u6db5 \u4e86\u3002\u201d \u3000\u3000\u7ea2\u5e15\u5973\u5b50\u628a\u4ed6\u4fe9\u5b89\u7f6e\u5728\u516b\u6247\u6c34\u6676\u5c4f\u98ce\u524d\u5ea7\u4f4d\u4e0a\uff0c\u4e5d\u5929\u4ed9\u5b50\u5bf9\u5750\u76f8\u966a\uff0c\u7fa4\u5973\u5219\u56f4\u7ed5\u56db\u5468\uff0c \u5947\u602a\u7684\u53ea\u6709\u7ea2\u5e15\u5c11\u5973\u4e00\u4eba\u5934\u4e0a\u88f9\u7740\u7ea2\u5e15\uff0c\u67f3\u7709\u5934\u4e0a\u7684\u9752\u5e15\uff0c\u5374\u4e0d\u77e5\u4f55\u65f6\u4e1a\u5df2\u89e3\u53bb\uff0c\u9732\u51fa\u4e00\u5934 \u9497\u7737\u9ad8\u5806\u7684\u4e91\u73af\u3002 \u3000\u3000\u7ee2\u5e15\u4ee3\u8868\u7740\u4ec0\u4e48\u610f\u4e49\uff0c\u53ea\u6709\u5929\u9634\u6559\u4eba\u81ea\u5df1\u660e\u767d\uff0c\u5c1a\u672a\u660e\u4f3c\u4e4e\u53c8\u7aa5\u7834\u4e86\u9752\u5e15\u5c11\u5973\u5fae\u542b\u5e7d\u6028 \u4e4b\u8272\uff0c\u81ea\u7136\u4ed6\u53c8\u548c\u67f3\u7709\u56db\u76ee\u76f8\u63a5\u89e6\u8fc7\u4e00\u6b21\u4e86\u3002 \u3000\u3000\u4e5d\u5929\u4ed9\u5b50\u7f2a\u5929\u96ef\u5185\u529f\u4e4b\u6df1\uff0c\u4e0d\u96be\u81ea\u5979\u7684\u773c\u795e\u4e2d\u89c2\u5bdf\u51fa\u6765\uff0c\u4f46\u662f\u5929\u9634\u6559\u5bf9\u4ed8\u4ed6\u4fe9\uff0c\u5374\u53e6\u662f \u4e00\u79cd\u4e0d\u53ef\u601d\u8bae\u7684\u624b\u6bb5\uff01 \u3000\u3000\u4e5d\u5929\u4ed9\u4e8e\u7b11\u8bed\u5a46\u59ff\u9053\uff1a\u201c\u718a\u5c0f\u5f1f\u5f1f\uff0c\u6211\u8bf4\u590f\u5c0f\u59b9\u59b9\u51e0\u751f\u4fee\u6765\u7684\uff0c\u4f60\u4e00\u5b9a\u8336\u601d\u996d\u60f3\u4e00\u523b\u5fd8 \u4e0d\u4e86\u5979\uff0c\u6709\u60c5\u4eba\u90fd\u6210\u7737\u5c5e\uff0c\u8fd9\u662f\u655d\u6559\u552f\u4e00\u7684\u613f\u671b\uff0c\u548c\u4e50\u4e8e\u4fc3\u6210\u7684\u4e8b\uff0c\u5426\u5219\u4f60\u718a\u5c0f\u5f1f\u5f1f\u4e00\u4e2a\u4eba \u4e5f\u4e0d\u5408\u672c\u6559\u5165\u95e8\u7684\u89c4\u77e9\u5440\uff01\u201d \u3000\u3000\u5979\u8fd9\u4e00\u8bf4\uff0c\u50cf\u662f\u718a\u501c\u5df2\u4e50\u610f\u5165\u6559\uff0c\u800c\u4e14\u8fd8\u8981\u611f\u6fc0\u5979\u7389\u6210\u597d\u4e8b\u5462\uff01\u718a\u501c\u81ea\u7136\u5fc3\u5934\u6cdb\u8d77\u4e00\u4e1d \u618e\u538c\uff0c\u6717\u6717\u56de\u7b54\u9053\uff1a\u201c\u590f\u59d1\u5a18\u73b0\u5728\u54ea\u91cc\uff0c\u8bf7\u5e26\u6211\u53bb\u5148\u548c\u5979\u4f1a\u4f1a\u9762\uff0c\u81f3\u4e8e\u8d35\u6559\u5b97\u65e8\u6211\u8fd8\u4e0d\u6df1 \u6089\uff0c\u4eba\u5404\u6709\u5fd7\uff0c\u718a\u67d0\u6cf0\u5c71\u4e00\u4f1a\u5df2\u51b3\u5b9a\u6b64\u5fd7\u7ec8\u8eab\u4e0d\u53d8\u3002\u82e5\u8d35\u6559\u771f\u80fd\u9020\u798f\u6b66\u6797\uff0c\u4e0d\u4ee5\u5f81\u670d\u5404\u5927\u5b97 \u6d3e\u5404\u5927\u8c6a\u6770\u4e3a\u76ee\u7684\uff0c\u5f7c\u6b64\u5404\u884c\u5176\u662f\uff0c\u6211\u662f\u4e50\u4e8e\u8c03\u505c\u8d35\u6559\u548c\u522b\u4eba\u4e4b\u95f4\u4e89\u7aef\u7684\uff0c\u81f3\u4e8e\u590f\u59d1\u5a18\u6211\u4e5f \u4e0d\u80fd\u52c9\u5f3a\u5979\u505a\u8fdd\u5fc3\u7684\u4e8b\uff01\u201d \u3000\u3000\u4e5d\u5929\u4ed9\u5b50\u9053\uff1a\u201c\u6211\u65e9\u77e5\u9053\u718a\u5c0f\u5f1f\u5f1f\u548c\u6211\u4eec\u662f\u5fd7\u540c\u9053\u5408\u7684\uff0c\u5c0f\u5f1f\u5f1f\u81ea\u7136\u5343\u91cc\u5954\u9a70\u6025\u4e8e\u4e00 \u89c1\uff0c\u4f46\u8001\u8eab\u4e0d\u80fd\u4e0d\u5148\u5c3d\u70b9\u4e1c\u9053\u4e4b\u8c0a\uff0c\u96be\u9053\u4e00\u676f\u8336\u4e00\u53e3\u996d\u90fd\u541d\u4e8e\u62db\u5f85\u4e48\uff1f\u51b5\u4e14\u718a\u5c0f\u5f1f\u5f1f\u4e0e\u590f\u59b9 \u59b9\u4ece\u6b64\u4fea\u5f71\u6210\u53cc\uff0c\u4e0d\u80fd\u4e0d\u66ff\u4f60\u4eec\u795d\u8d3a\u4e00\u4e0b\u5440\uff01\u201d \u3000\u3000\u5979\u5411\u5de6\u53f3\u7684\u5973\u5b50\u7565\u4e00\u6325\u624b\uff0c\u7acb\u523b\u6709\u4e24\u4e2a\u767d\u8863\u5c11\u5973\u8d8b\u51fa\u6367\u8336\u76f8\u656c\uff0c\u4e38\u5929\u4ed9\u5b50\u5475\u5475\u7b11\u9053\uff1a \u201c\u5c1a\u5c0f\u5f1f\uff0c\u6211\u4e5f\u66ff\u4f60\u9009\u62e9\u4e00\u4f4d\u6700\u9017\u4eba\u601c\u7231\u7684\u5c0f\u59b9\u59b9\uff0c\u505a\u4f60\u7ec8\u8eab\u7684\u4f34\u4fa3\uff0c\u6211\u60f3\u4f60\u4e00\u5b9a\u731c\u5f97\u51fa\u6765 \u662f\u54ea\u4e00\u4e2a\uff0c\u5c31\u662f\u8fdc\u9053\u8fce\u63a5\u4f60\u7684\u4eba\u513f\uff01\u201d \u3000\u3000\u5c1a\u672a\u660e\u9a70\u9a8b\u6c5f\u6e56\uff0c\u5bb0\u4e86\u4e0d\u5c11\u8d2a\u5b98\u6c61\u540f\uff0c\u76ee\u524d\u5374\u767e\u70bc\u94a2\u5316\u4e3a\u7ed5\u6307\u67d4\uff0c\u4e5d\u5929\u4ed9\u5b50\u7adf\u5f53\u9762\u66ff\u4ed6 \u505a\u8d77\u5a92\u6765\uff0c\u96be\u9053\u5929\u9634\u6559\u4eba\u662f\u60f3\u7528\u7f8e\u4eba\u6765\u7b3c\u7edc\u4ed6\u7684\u5fc3\uff1f\u5c1a\u672a\u660e\u7acb\u523b\u611f\u5230\u6781\u4e3a\u5c34\u5c2c\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u6781\u76fc\u671b\u4e5d\u5929\u4ed9\u5b50\u80fd\u628a\u9752\u5e15\u5c11\u5973\u66ff\u4ed6\u64ae\u5408\u3002 \u3000\u3000\u5c1a\u672a\u660e\u8138\u4e0a\u706b\u8fa3\u8fa3\u7684\uff0c\u53c8\u4e0d\u597d\u7acb\u5373\u5e94\u5141\uff0c\u4ed6\u6025\u4e8e\u660e\u4e86\u8fd9\u5929\u9634\u6559\u77a9\u610f\u4e8e\u4ed6\u7684\u4eba\u513f\uff0c\u5374\u4e0d\u597d \u542f\u9f7f\u53bb\u95ee\u3002 \u3000\u3000\u5c1a\u672a\u660e\u9690\u4e8e\u745f\u745f\u4e0d\u5b89\u7684\u5730\u6b65\uff0c\u867d\u660e\u77e5\u9053\u8fd9\u662f\u4e2a\u6e29\u67d4\u7684\u9677\u9631\uff0c\u5374\u7ec8\u6ca1\u6709\u52c7\u6c14\uff0c\u575a\u51b3\u62d2\u7edd\u4e5d \u5929\u4ed9\u5b50\u7684\u8bdd\u3002 \u3000\u3000\u5c1a\u672a\u660e\u5bf9\u4e8e\u9752\u5e15\u7684\u5c11\u5973\uff0c\u786e\u662f\u4e00\u89c1\u503e\u5fc3\uff0c\u5c24\u5176\u5728\u6700\u540e\u4e00\u6bb5\u884c\u7a0b\u4e2d\uff0c\u9752\u5e15\u5c11\u5973\u503e\u8eab\u5bc6\u8bed\uff0c \u4e0d\u662f\u542b\u65e0\u9650\u6df1\u60c5\u4e48\uff1f \u3000\u3000\u72e1\u733e\u7684\u4e5d\u5929\u4ed9\u5b50\uff0c\u4f3c\u5df2\u770b\u51fa\u5c1a\u672a\u660e\u7684\u5fc3\u4e8b\uff0c\u5374\u6545\u610f\u73a9\u5f04\u8fd9\u4e2a\u5c11\u5e74\u82f1\u96c4\uff0c\u53c8\u7b11\u7740\u8bf4\uff1a\u201c\u5c1a \u5c0f\u5f1f\u5f1f\u8bf7\u76f8\u4fe1\u6211\uff0c\u6211\u7edd\u4e0d\u4f1a\u4f7f\u4f60\u5931\u671b\u3002\u201d \u3000\u3000\u79c0\u4e3d\u6de1\u88c5\u7684\u5c11\u5973\uff0c\u5206\u9001\u7ed9\u4ed6\u4fe9\u5404\u4e00\u676f\u78a7\u8272\u6e5b\u6e5b\u7684\u9999\u8317\uff0c\u718a\u501c\u7565\u4e00\u6b20\u8eab\u63a5\u4f4f\uff0c\u4ed6\u5df2\u770b\u51fa\u5c1a \u672a\u660e\u795e\u5fd7\u604d\u60da\uff0c\u5fd9\u5411\u5c1a\u672a\u660e\u9012\u8fc7\u4e00\u9053\u773c\u8272\uff0c\u610f\u601d\u8bf4\uff1a\u201c\u8fd9\u8336\u53ef\u4e0d\u80fd\u5403\uff01\u201d \u3000\u3000\u540c\u65f6\uff0c\u5df2\u53bb\u4e86\u9752\u5e15\u7684\u5c11\u5973\u67f3\u7709\uff0c\u4e5f\u5411\u5c1a\u672a\u660e\u4e22\u4e86\u4e2a\u773c\u8272\uff0c\u4f7f\u5c1a\u672a\u660e\u9661\u7136\u5fc3\u60c5\u4e00\u9707\uff0c\u65b9\u7b97 \u628a\u5c0f\u9e7f\u4e71\u649e\u7684\u5fc3\u6682\u65f6\u6536\u6444\u4f4f\u3002 \u3000\u3000\u90a3\u7ea2\u5e15\u5c11\u5973\uff0c\u5374\u5a07\u7b11\u5f97\u66f4\u52a0\u59a9\u5a9a\uff0c\u5979\u5fc3\u7545\u795e\u7094\uff0c\u9732\u51fa\u65e0\u9650\u5f97\u610f\u4e4b\u8272\uff1b\u548c\u90a3\u67f3\u7709\u5e7d\u8272\uff0c\u6070 \u6210\u4e2a\u76f8\u53cd\u7684\u5bf9\u7167\u3002 \u3000\u3000\u718a\u501c\u63ed\u5f00\u76d6\u76c5\uff0c\u55c5\u7740\u90a3\u8336\u8317\u4e4b\u4e2d\uff0c\u53e6\u6709\u4e00\u79cd\u8bf4\u4e0d\u51fa\u7684\u5947\u5999\u82b3\u99a8\uff0c\u7565\u718f\u4eba\u9f3b\u5b54\uff0c\u5c31\u4f7f\u4eba\u6d51 \u8eab\u865a\u98d8\u98d8\u7684\u8212\u9002\u65e0\u6bd4\uff0c\u4ed6\u865a\u865a\u5f20\u53e3\u555c\u5f04\u51fa\u58f0\u54cd\uff0c\u5374\u6697\u6697\u628a\u8336\u6c41\u5410\u5728\u4e5f\u4e0a\u3002 \u3000\u3000\u53c8\u7528\u624b\u5e15\u62ed\u62b9\u4e00\u4e0b\u5507\u543b\uff0c\u8fde\u53e3\u8d5e\u7f8e\u4e3b\u4eba\u6240\u8d50\u7684\u9999\u8317\u3002 \u3000\u3000\u4e5d\u5929\u4ed9\u5b50\u4e00\u58f0\u5429\u5490\uff0c\u4f17\u5c11\u5973\u7acb\u5373\u62ac\u4e0a\u5e2d\u9762\uff0c\u6c34\u9646\u6742\u9648\uff0c\u800c\u4e14\u90fd\u662f\u6781\u7cbe\u7f8e\u7684\u676f\u7b4b\u5668\u76bf\uff0c\u83dc \u80b4\u66f4\u662f\u6d3b\u8272\u751f\u9999\u3002 \u3000\u3000\u4e5d\u5929\u4ed9\u5b50\u7acb\u8bf7\u4ed6\u4eec\u4eba\u5ea7\uff0c\u5e76\u4e14\u7b11\u76c8\u76c8\u8bf4\uff1a\u201c\u8ba9\u6211\u8fd9\u51e0\u4e2a\u5c0f\u59b9\u59b9\uff0c\u5404\u656c\u4e24\u4f4d\u4e00\u676f\uff0c\u7136\u540e\u5c31 \u9001\u718a\u5c0f\u5f1f\u5f1f\u548c\u590f\u59b9\u59b9\u2026\u2026\u201d \u3000\u3000\u7a81\u7136\u4e5d\u5929\u4ed9\u5b50\u4e00\u6536\u7b11\u5bb9\uff0c\u6b63\u8272\u5411\u718a\u501c\u8bf4\uff1a\u201c\u590f\u59b9\u59b9\u7684\u4ee4\u5c0a\u2014\u2014\u866c\u987b\u5ba2\uff0c\u4f60\u8fd8\u6ca1\u6709\u4f1a\u8fc7\u9762 \u5427\uff1f\u201d \u3000\u3000\u718a\u501c\u795e\u8272\u4e00\u8083\uff0c\u817e\u53e3\u95ee\u9053\uff1a\u201c\u866c\u987b\u5ba2\uff0c\u4ec0\u4e48\uff0c\u4ed6\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u4e5d\u5929\u4ed9\u5b50\u9053\uff1a\u201c\u718a\u5c0f\u5f1f\u5f1f\u4e0d\u8981\u6027\u6025\uff0c\u65e9\u665a\u53ef\u4ee5\u89c1\u9762\uff01\u4f46\u662f\u4f60\u8c05\u8fd8\u4e0d\u77e5\u9053\u4ed6\u5c31\u662f\u5f53\u5e74\u5317\u5251 \u5357\u97ad\u5b9d\u9a6c\u795e\u97ad\u8428\u5929\u9aa5\u5427\uff01\u201d \u3000\u3000\u718a\u501c\u9053\uff1a\u201c\u590f\u59d1\u5a18\u53ea\u8eab\u653e\u6d6a\u6c5f\u6e56\uff0c\u866c\u987b\u5ba2\u81ea\u7136\u4e0d\u4f1a\u653e\u5fc3\u5979\u7684\u3002\u201d \u3000\u3000\u4e5d\u5929\u4ed9\u5b50\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u8fd8\u6ca1\u6709\u8bf7\u4ed6\u6765\u6c5f\u5357\uff0c\u5173\u5916\u672c\u6559\u7684\u4e8b\u52a1\uff0c\u90fd\u6258\u4ed6\u529e\u7406\uff0c\u590f\u59b9\u59b9\u6027\u60c5 \u5012\u662f\u5014\u5f3a\u5f97\u5f88\uff0c\u5979\u8fd8\u4e0d\u76f8\u4fe1\u5979\u4ee4\u5c0a\u52a0\u5165\u4e86\u672c\u6559\uff0c\u6211\u8bf4\u718a\u5c0f\u5f1f\u5f1f\u4f60\u8981\u597d\u597d\u89c4\u529d\u5979\uff0c\u600e\u80fd\u591f\u4e0d\u5b5d \u987a\u7236\u6bcd\uff0c\u548c\u7236\u4eb2\u80cc\u9053\u800c\u9a70\u5462\uff01\u201d \u3000\u3000\u5b9d\u9a6c\u795e\u97ad\u8428\u5929\u9aa5\u52a0\u5165\u5929\u9634\u6559\uff0c\u718a\u501c\u5e76\u4e0d\u5341\u5206\u91cd\u89c6\uff0c\u5929\u9634\u6559\u672c\u5c31\u662f\u6b63\u6d3e\u4eba\u58eb\u6240\u8981\u6d88\u706d\u7684\u5bf9 \u50cf\uff0c\u800c\u662f\u590f\u82b8\u80fd\u4e0d\u53d7\u5929\u9634\u6559\u4eba\u7684\u5a01\u80c1\u5229\u8bf1\uff0c\u786e\u4f7f\u718a\u501c\u884c\u4e3a\u4e0a\u5149\u8363\uff0c\u5047\u82e5\u590f\u82b8\u6295\u5165\u5929\u9634\u6559\uff0c\u90a3 \u8be5\u662f\u591a\u4e48\u9ebb\u70e6\u7684\u4e8b\u3002 \u3000\u3000\u718a\u501c\u7709\u98de\u8272\u821e\uff0c\u4e3a\u590f\u82b8\u4e0e\u4ed6\u6709\u76f8\u7b49\u7684\u4e0d\u5c48\u4e0d\u6320\u9aa8\u6c14\uff0c\u800c\u795e\u60c5\u5206\u5916\u5174\u594b\uff0c\u4f46\u662f\u773c\u524d\u83ba\u98de\u8776 \u7ed5\uff0c\u8fd9\u4e00\u7fa4\u767d\u8863\u4ed9\u5b50\u6267\u58f6\u76f8\u656c\uff0c\u9887\u4f7f\u718a\u501c\u548c\u5c1a\u672a\u660e\u5341\u5206\u4e3a\u96be\uff0c\u5929\u9634\u6559\u4e0b\u7684\u5973\u5b69\u5b50\uff0c\u5e76\u6ca1\u6709\u4e1d \u6beb\u8361\u68c0\u8d8a\u793c\u7684\u5730\u65b9\uff0c\u53cd\u800c\u4e88\u4eba\u7684\u662f\u6de1\u7d20\u6d01\u96c5\u7684\u9ad8\u5c1a\u4e4b\u611f\u3002 \u3000\u3000\u718a\u501c\u53c8\u55c5\u51fa\u676f\u4e2d\u7684\u9152\u9999\uff0c\u548c\u8336\u676f\u91cc\u7684\u540c\u6837\u4e00\u79cd\u5f02\u99a8\u3002\u5c1a\u672a\u660e\u4e5f\u4e0d\u6562\u653e\u6000\u7545\u996e\uff0c\u56e0\u4e3a\u67f3\u7709 \u7684\u5e7d\u6028\u7684\u773c\u5149\uff0c\u4e0d\u65f6\u5077\u5077\u7aa5\u89c6\u8fc7\u6765\uff0c\u4f46\u662f\u591a\u5c11\u5e94\u4e2a\u666f\uff0c\u80fd\u4e0d\u7565\u6cbe\u6e4d\u4e86\u5507\u820c\uff0c\u4ed6\u53ef\u6ca1\u6709\u718a\u501c\u7684 \u673a\u53d8\uff0c\u5584\u4e8e\u5e94\u4ed8\u3002 \u3000\u3000\u5947\u602a\u7684\u9152\u9999\u4eba\u809a\uff0c\u5e76\u4e0d\u89c9\u51fa\u4ec0\u4e48\u5f02\u6837\u6ecb\u5473\uff0c\u53cd\u800c\u8eab\u4f53\u4e4b\u5185\u5f02\u5e38\u8212\u9002\uff0c\u5934\u8111\u91cc\u4e5f\u6ca1\u6709\u9519\u6655 \u7684\u73b0\u8c61\u3002 \u3000\u3000\u53ef\u662f\u9752\u5e15\u5c11\u5973\uff0c\u5219\u5e7d\u6028\u4e00\u58f0\u8f7b\u53f9\u3001\u6df1\u6df1\u5782\u4e0b\u5934\u53bb\u3002 \u3000\u3000\u9152\u8fc7\u4e86\u4e09\u5de1\uff0c\u4e5d\u5929\u4ed9\u5b50\u4f3c\u5df2\u89c9\u5f97\u80dc\u5229\u5728\u63e1\uff0c\u5979\u624d\u6ed4\u6ed4\u4e0d\u7edd\u8bc9\u8bf4\u5929\u9634\u6559\u7684\u5b97\u65e8\uff0c\u65e0\u975e\u8bf4\u4ed6 \u4eec\u6559\u4e49\u53ea\u5728\u8054\u7edc\u6b66\u6797\u540c\u9053\uff0c\u4e3b\u5f20\u628a\u6b66\u6797\u5404\u6d3e\u7684\u7edd\u6280\uff0c\u7efc\u5408\u7efc\u5408\u8d77\u6765\u516c\u8bf8\u540c\u9053\u3002\u5927\u5bb6\u4e00\u540c\u7814 \u7a76\uff0c\u4e8e\u662f\u5c31\u628a\u4e00\u5207\u8fc7\u9519\u5b89\u5728\u6b66\u5f53\u6d3e\u5934\u4e0a\u3002 \u3000\u3000\u6b66\u5f53\u6d3e\u6709\u4e00\u79cd\u5185\u529f\u79d8\u4e66\uff0c\u5173\u8d77\u95e8\u6765\u81ea\u5df1\u7ec3\u4e60\uff0c\u8fd9\u662f\u4e0d\u591f\u5927\u65b9\u7684\uff0c\u4e0a\u6b21\u5c31\u4e3a\u7d22\u53d6\u6b64\u4e66\uff0c\u8d77 \u4e86\u4e0d\u5927\u4e0d\u5c11\u7684\u51b2\u7a81\u3002 \u3000\u3000\u4e5d\u5929\u4ed9\u5b50\u8fd9\u79cd\u5f3a\u8bcd\u593a\u7406\u7684\u8bdd\uff0c\u718a\u501c\u7b49\u542c\u53bb\u9887\u89c9\u523a\u8033\u3002 \u3000\u3000\u4e5d\u5929\u4ed9\u5b50\u4e5f\u72e1\u733e\u7684\u770b\u51fa\u4e24\u4e2a\u5c11\u5e74\uff0c\u4e0d\u6ee1\u610f\u5979\u7684\u8bdd\uff0c\u597d\u5728\u5979\u7684\u8ba1\u5212\u5c31\u7eea\uff0c\u730e\u7269\u5df2\u5165\u7f51\u7f57\uff0c \u4fbf\u50ac\u4fc3\u4ed6\u4fe9\u7528\u996d\uff0c\u8bf4\uff1a\u201c\u8fd9\u662f\u672c\u5802\u7b2c\u4e00\u6b21\u7834\u4f8b\u7684\u4e8b\uff0c\u627f\u4e24\u4f4d\u5c0f\u5f1f\u5f1f\u8fdc\u9053\u800c\u6765\uff0c\u4e0d\u80fd\u8d76\u5ba2\u4eba \u8d70\uff0c\u6743\u4e14\u8bf7\u5728\u672c\u5802\u7559\u5bbf\u4e00\u5bb5\uff0c\u718a\u5c0f\u5f1f\u5f1f\u4e0e\u590f\u59b9\u59b9\u53ef\u4ee5\u7545\u8ff0\u79bb\u60c5\u4e86\uff0c\u660e\u65e9\u76fc\u80fd\u7ed9\u8001\u8eab\u4e00\u4e2a\u6073\u5207 \u7684\u7b54\u590d\uff01\u5c31\u662f\u4e0d\u80fd\u5165\u6559\uff0c\u8fd9\u4e8b\u6211\u4eec\u4e5f\u4e0d\u52c9\u5f3a\uff0c\u4f46\u603b\u53ef\u4ee5\u643a\u624b\u5408\u4f5c\u5427\uff01\u201d \u3000\u3000\u718a\u501c\u8138\u4e2d\u4e00\u4eae\uff0c\u660e\u4e86\u4ed6\u4eec\u7684\u6b65\u9aa4\u662f\u975e\u5e38\u7f1c\u5bc6\u7684\uff0c\u53ea\u8981\u4e00\u6b65\u8d70\u9519\uff0c\u4e0b\u9762\u5c31\u4f1a\u4f7f\u4f60\u6309\u7167\u4ed6\u4eec \u7684\u6b65\u4f10\uff0c\u4e00\u6b65\u6b65\u5815\u843d\u4e0b\u53bb\uff01\u4ed6\u4e3a\u4e86\u590f\u82b8\uff0c\u6682\u65f6\u4e0d\u80fd\u53cd\u8138\uff0c\u800c\u4e14\u4e38\u5929\u4ed9\u5b50\u6bb7\u52e4\u6b3e\u5f85\uff0c\u60c5\u7406\u4e0a\u4e5f \u4e0d\u80fd\u8fd9\u6837\u505a\u3002 \u3000\u3000\u800c\u5c1a\u672a\u660e\u5462\uff0c\u4ed6\u5374\u9677\u5165\u4e86\u60c5\u7f51\uff0c\u552f\u4e00\u5e0c\u671b\u7684\uff0c\u662f\u80fd\u548c\u4f0a\u4eba\u591a\u901a\u6b3e\u66f2\uff0c\u81f3\u4e8e\u5165\u6559\u7684\u4e8b\uff0c\u4ed6 \u8ba4\u4e3a\u90a3\u662f\u7b11\u8bdd\uff0c\u5929\u9634\u6559\u4eba\u518d\u8bf4\u5f97\u5929\u82b1\u4e71\u5760\uff0c\u8fd8\u80fd\u6539\u53d8\u4e86\u4ed6\u7684\u521d\u8877\u4e48\uff1f \u3000\u3000\u5c1a\u672a\u660e\u548c\u718a\u501c\uff0c\u9042\u5728\u8fd9\u79cd\u4e0d\u540c\u5fc3\u7406\u4e4b\u4e0b\uff0c\u63a5\u53d7\u4e86\u5929\u9634\u6559\u4eba\u4e3a\u4ed6\u4eec\u5b89\u6392\u7684\u4e8b\u3002 \u3000\u3000\u5c1a\u672a\u660e\u7531\u4e24\u4e2a\u5782\u8f94\u5c11\u5973\uff0c\u6253\u7740\u5bf9\u513f\u7ea2\u7eb1\u5b98\u706f\uff0c\u5f15\u5bfc\u4ed6\u53bb\u5411\u4fa7\u8fb9\u4e00\u5ea7\u6781\u5e7d\u96c5\u7684\u504f\u9662\u91cc\uff0c\u59a5 \u4e3a\u5b89\u7f6e\u3002 \u3000\u3000\u718a\u501c\u5219\u7531\u7ea2\u5e15\u5c11\u5973\u548c\u53e6\u5916\u4e24\u540d\u63d0\u706f\u5c11\u5973\uff0c\u9001\u4eba\u4e0e\u5c1a\u672a\u660e\u5411\u76f8\u53cd\u7684\u5bf9\u9762\u504f\u9662\u91cc\uff0c\u706f\u5f71\u6447 \u7ea2\uff0c\u82b1\u5f84\u66f2\u6298\uff0c\u5bfc\u81f3\u4e94\u95f4\u6781\u7cbe\u5de7\u7684\u82b1\u5385\u4e4b\u524d\u3002\u7ea2\u5e15\u5c11\u5973\u7b11\u9053\uff1a\u201c\u718a\u5927\u4fa0\u81ea\u5df1\u8fdb\u53bb\u5427\uff01\u83ab\u4f7f\u590f \u59d1\u5a18\u671b\u7a7f\u79cb\u6c34\uff01\u6211\u4e0d\u6253\u62e2\u4f60\u4eec\u4e86\uff01\u590f\u59d1\u5a18\u5c0f\u6027\u513f\u6211\u60f9\u4e0d\u8d77\uff0c\u795d\u798f\u4f60\u4eec\u82b1\u597d\u6708\u5706\uff01\u201d \u3000\u3000\u5979\u8bf4\u5b8c\uff0c\u5ae3\u7136\u4e00\u7b11\uff0c\u4f9d\u7136\u662f\u8def\u4e0a\u90a3\u79cd\u653e\u6d6a\u4e0d\u62d8\u7684\u795e\u6001\uff0c\u800c\u4e14\u5979\u8fd8\u6709\u66f4\u5927\u7684\u5e78\u798f\uff0c\u5728\u7b49\u5f85 \u7740\u5979\u53bb\u4eab\u53d7\u5462\u3002 \u3000\u3000\u63d0\u706f\u5c11\u5973\u4e5f\u8f6c\u79fb\u83b2\u6b65\uff0c\u968f\u7740\u5979\u6298\u56de\u53bb\u3002 \u3000\u3000\u718a\u501c\u8fd9\u65f6\u5374\u5fc3\u91cc\u5934\u7eea\u7eb7\u575b\uff0c\u6599\u60f3\u590f\u82b8\u5fc5\u5728\u671f\u5f85\u7740\u4ed6\uff0c\u800c\u4ed6\u5462\uff0c\u5374\u7adf\u8981\u624b\u5203\u4e8e\u7231\u4fa3\u4e4b\u7236\u7684 \u80f8\u8154\uff0c\u4ee5\u5feb\u79ef\u6068\uff01 \u3000\u3000\u718a\u501c\u5fc3\u5f26\u9707\u8361\uff0c\u51e0\u4e4e\u65e0\u6cd5\u81ea\u5236\u3002 \u3000\u3000\u718a\u501c\u4e00\u54ac\u7259\uff0c\u62c9\u5f00\u95e8\u51b2\u5165\u5ba4\u4e2d\u3002 \u3000\u3000\u718a\u501c\u4e00\u8df3\u8fdb\u53bb\uff0c\u719f\u6089\u7684\u5c11\u5973\u60ca\u547c\u58f0\u5df2\u9707\u5165\u8033\u9f13\uff0c\u773c\u524d\u5df1\u98de\u8dc3\u8fc7\u6765\u4ed6\u7684\u82b8\u59b9\u3002 \u3000\u3000\u4e24\u4eba\u90fd\u88ab\u8fd9\u7a81\u5982\u5176\u6765\u7684\u4f1a\u6664\u8ff7\u60d8\u800c\u6123\u4f4f\u4e86\u3002 \u3000\u3000\u590f\u82b8\u679c\u7136\u4e30\u59ff\u4e00\u5982\u5f80\u65e5\uff0c\u800c\u4e14\u88ab\u5b89\u7f6e\u5728\u8fd9\u6837\u4e00\u9762\u73e0\u73af\u7fe0\u7ed5\u7684\u9999\u95fa\u91cc\uff0c\u718a\u501c\u4e00\u773c\u63a0\u8fc7\u4e4b \u4e0b\uff0c\u88ab\u8fd9\u8fc7\u4e8e\u8c6a\u534e\u7684\u9648\u8bbe\u6123\u4f4f\uff0c\u590f\u82b8\u53d7\u5230\u8fd9\u6837\u9686\u91cd\u62db\u5f85\uff0c\u4f7f\u4ed6\u683c\u5916\u5b89\u6170\u3002 \u3000\u3000\u590f\u82b8\u7684\u7b2c\u4e00\u53e5\u8bdd\u662f\uff1a\u201c\u501c\u54e5\uff0c\u4f60\u600e\u4e0d\u65e9\u4e9b\u6765\u770b\u6211\uff1f\u201d \u3000\u3000\u5979\u5e7d\u6028\u800c\u542b\u7740\u6068\u6068\u7684\u773c\u5149\uff0c\u51e0\u4e4e\u95ea\u51fa\u8bb8\u591a\u6cea\u82b1\uff0c\u8fd9\u662f\u4e45\u522b\u91cd\u9022\u65f6\u6781\u73cd\u8d35\u7684\u60c5\u8c0a\u6d41\u9732\uff0c\u53cd \u800c\u4f7f\u718a\u501c\u8d77\u4e86\u8bef\u4f1a\u3002 \u3000\u3000\u4ed6\u4e0d\u81ea\u89c9\u7684\u53cc\u624b\u63e1\u4f4f\u82b8\u7684\u67d4\u5937\uff0c\u60ca\u95ee\u9053\uff1a\u201c\u4f60\u600e\u4e48\u4e86\uff1f\u5929\u9634\u6559\u4eba\u96be\u9053\u4f7f\u4f60\u53d7\u4e86\u59d4\u5c48\uff1f\u6211 \u4e24\u6b21\u4e0a\u6b66\u5f53\uff0c\u5f80\u8fd4\u5954\u6ce2\uff0c\u90fd\u662f\u4e3a\u4e86\u590f\u82b8\u9a84\u505a\u7684\u6027\u5b50\u4e00\u655d\u5634\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u6b66\u5f53\u6d3e\u4eba\u80fd\u518d\u5ea6\u5236\u670d \u6211\u5417\uff1f\u51cc\u4e91\u5b50\u4e0d\u8fc7\u662f\u7528\u5de7\u62db\u80dc\u6211\u4e00\u6b21\uff0c\u6211\u6839\u672c\u770b\u4e0d\u8d77\u4ed6\u4eec\u4ec0\u4e48\u4e5d\u5b98\u8fde\u73af\u5251\u5462\uff01\u201d \u3000\u3000\u5979\u53c8\u9053\uff1a\u201c\u5929\u9634\u6559\u4eba\uff0c\u5e76\u4e0d\u5982\u4eba\u4eec\u60f3\u50cf\u4e2d\u90a3\u4e48\u90aa\u6076\u3001\u53ef\u6015\uff0c\u4ed6\u4eec\u4e0d\u6562\u5bf9\u6211\u5931\u672d\uff0c\u636e\u8bf4\u662f \u4e3a\u4e86\u94a6\u4f69\u4f60\u7684\u672c\u9886\u3002\u4ed6\u4eec\u613f\u610f\u548c\u4f60\u7ed3\u4ea4\uff0c\u6211\u4e5f\u6b63\u62ff\u4e0d\u5b9a\u4e3b\u610f\uff0c\u6211\u7236\u4eb2\u5df2\u7ecf\u6295\u8eab\u6559\u4e0b\uff0c\u53ea\u5f85\u4f60 \u6765\u51b3\u5b9a\uff0c\u51b3\u5b9a\u4f60\u548c\u6211\u5e94\u5426\u548c\u4ed6\u4eec\u5408\u4f5c\u3002\u201d \u3000\u3000\u590f\u82b8\u4e00\u63d0\u8d77\u5979\u7684\u7236\u4eb2\u866c\u987b\u5ba2\uff0c\u4e5f\u5c31\u662f\u5b9d\u9a6c\u795e\u97ad\u8428\u5929\u9aa5\uff0c\u4f7f\u718a\u501c\u5982\u540c\u826f\u5fc3\u53d7\u5230\u4e86\u6bd2\u866b\u7684\u5543 \u566c\uff0c\u4ed6\u5f20\u5927\u4e86\u773c\u3002 \u3000\u3000\u718a\u501c\u6291\u538b\u4e0d\u4f4f\u5fc3\u4e2d\u611f\u60c5\u7684\u8d77\u4f0f\u53d8\u5316\u3002 \u3000\u3000\u718a\u501c\u53c8\u4f5c\u4e86\u4e2a\u9519\u8bef\u7684\u51b3\u5b9a\uff0c\u4ed6\u51b3\u5b9a\u6682\u65f6\u4eab\u53d7\u7740\u590f\u82b8\u7ee7\u7eed\u589e\u52a0\u7684\u5c11\u5973\u7684\u70ed\u60c5\uff0c\u9676\u9189\u5728\u4e24\u79cd \u4e0d\u76f8\u5bb9\u7684\u7231\u4e0e\u6068\u6f29\u6da1\u91cc\uff0c\u4e8e\u662f\u4ed6\u4fe9\u70ed\u70c8\u5730\u4f9d\u504e\u5728\u4e00\u8d77\u3002 \u3000\u3000\u4ed6\u4fe9\u5e76\u80a9\u5750\u5728\u6700\u7f8e\u4e3d\u7684\u5e8a\u5934\uff0c\u6b3e\u6b3e\u5728\u4e92\u8bc9\u522b\u540e\u7684\u60c5\u5f62\u3002 \u3000\u3000\u718a\u501c\u542c\u590f\u82b8\u8bf4\u5979\u6587\u7406\u4e0d\u6df1\uff0c\u6240\u4ee5\u90a3\u5c01\u4fe1\u53ea\u5c01\u4e86\u90a3\u679a\u53e4\u94b1\uff0c\u53ea\u8868\u793a\u5979\u5728\u7b49\u5019\u718a\u501c\u76f8\u89c1\u800c \u5df2\uff0c\u800c\u4e14\u5343\u8a00\u4e07\u8bed\u4e5f\u5199\u4e0d\u51fa\u65e0\u9650\u76f8\u601d\uff01\u590f\u82b8\u63d0\u51fa\u6765\u5173\u4e8e\u5929\u9634\u6559\u7684\u95ee\u9898\uff0c\u6682\u65f6\u8fd8\u4e0d\u7b54\u590d\uff0c\u56e0\u4e3a \u4ed6\u660e\u4e86\u590f\u82b8\u5929\u771f\u65e0\u90aa\uff0c\u5bf9\u5979\u597d\u7684\uff0c\u5979\u4e0d\u514d\u8981\u8ba4\u4e3a\u662f\u597d\u4eba\u4e86\u3002 \u3000\u3000\u590f\u82b8\u9996\u5148\u53d9\u8ff0\u4e0e\u5e38\u6f2b\u5929\u592b\u5987\u76f8\u8bc6\u7684\u7ecf\u8fc7\uff0c\u5979\u6ca1\u6709\u9690\u85cf\u4ec0\u4e48\uff0c\u5979\u8ba4\u4e3a\u7530\u59d0\u59d0\u7684\u672c\u9886\u786e\u5b9e\u503c \u5f97\u94a6\u4f69\uff0c\u8fd9\u662f\u718a\u501c\u54d1\u7136\u5931\u7b11\u7684\u4e8b\uff0c\u8fd9\u5c0f\u59ae\u5b50\u5c45\u7136\u4e5f\u6709\u5979\u656c\u670d\u7684\u4eba\uff01\u718a\u501c\u628a\u5979\u7684\u624b\u63e1\u5f97\u66f4\u7d27\uff0c \u590f\u82b8\u611f\u89c9\u4e00\u79cd\u65e0\u6bd4\u7684\u70ed\u6d41\uff0c\u6d78\u904d\u5168\u8eab\uff0c\u4f7f\u5979\u5fc3\u7075\u4e4b\u6249\uff0c\u655e\u5f00\u7740\u63a5\u53d7\u8fd9\u5c11\u5e74\u6240\u5e26\u6765\u7684\u6e29\u6696\u3002 \u3000\u3000\u590f\u82b8\u53c8\u8bf4\u4e86\u89c1\u51cc\u4e91\u5b50\u4e1c\u65b9\u7075\u5144\u59b9\u640f\u6597\u7684\u4e8b\uff0c\u7709\u98de\u8272\u821e\u63cf\u7ed8\u5979\u600e\u6837\u628a\u51cc\u4e91\u5b50\u7528\u94a2\u4e38\u5413\u9000\uff0c \u8868\u793a\u5979\u5df2\u4e0d\u662f\u4ee5\u524d\u7684\u5979\u53ef\u6bd4\u4e86\uff0c\u5176\u6b21\u5979\u662f\u5728\u90a3\u5ba2\u5e97\u91cc\u67d3\u4e0a\u4e86\u4e0d\u8f7b\u4e0d\u91cd\u7684\u75c5\uff0c\u5fc3\u60c5\u7684\u90c1\u7ed3\uff0c\u4e5f \u662f\u81f4\u75c5\u7684\u4e3b\u56e0\u3002 \u3000\u3000\u75c5\u4e2d\uff0c\u5929\u9634\u6559\u5355\u638c\u65ad\u9b42\u5355\u98de\uff0c\u548c\u53f8\u793c\u7ae5\u5b50\u767d\u666f\u7965\u53f6\u6e05\u6e05\u7adf\u81ea\u52a8\u627e\u6765\u7167\u6599\u5979\uff0c\u5c24\u5176\u662f\u65f6\u6e05 \u6e05\u4e5f\u662f\u4e2a\u6d3b\u6cfc\u5c11\u5973\uff0c\u5bf9\u5979\u7167\u5e94\u5f97\u65e0\u5fae\u4e0d\u81f3\uff0c\u4ee5\u540e\u5c31\u9080\u5979\u79fb\u5f80\u8346\u5dde\u5e9c\u5929\u9634\u6559\u767d\u51e4\u603b\u5802\uff0c\u4e5d\u5929\u4ed9 \u5b50\u7f2a\u5929\u96ef\u66f4\u5341\u5206\u601c\u7231\u5979\uff0c\u5c31\u50cf\u5988\u5988\u4e00\u822c\u3002 \u3000\u3000\u590f\u82b8\u53c8\u8ba4\u8bc6\u4e86\u4e0d\u5c11\u7684\u5929\u9634\u6559\u7f8e\u4e3d\u59ca\u59b9\uff0c\u90fd\u5f85\u5979\u5f53\u4eb2\u4eba\u770b\u5f85\uff0c\u590f\u82b8\u7684\u75c5\u9b54\u4e5f\u5f00\u59cb\u64a4\u9000\uff0c\u5f53 \u5979\u8981\u79bb\u53bb\u627e\u718a\u501c\u65f6\uff0c\u4e5d\u5929\u4ed9\u5b50\u5411\u5979\u5ba3\u5e03\u4e86\u4e00\u9879\u60ca\u4eba\u7684\u6d88\u606f\uff0c\u5df2\u6d3e\u4eba\u53bb\u8fce\u63a5\u718a\u501c\u6765\u6b64\uff0c\u800c\u5979\u66f4 \u4e0d\u6562\u4e5f\u4e0d\u613f\u518d\u53bb\u6b66\u5f53\u81ea\u53d6\u5176\u8fb1\u4e86\u3002 \u3000\u3000\u590f\u82b8\u4ece\u7a1a\u51e4\u5802\u4e24\u575b\u59ca\u59b9\u53e3\u4e2d\uff0c\u5f97\u6089\u5929\u9634\u6559\u4e0b\u8bb8\u591a\u89c4\u77e9\uff0c\u51e1\u662f\u4e5d\u5929\u4ed9\u5b50\u6559\u4eba\u4ee3\u4e3a\u884c\u4f7f\uff0c\u5973 \u5b69\u5b50\u662f\u6ca1\u6709\u62d2\u7edd\u4f59\u5730\u7684\u3002 \u3000\u3000\u51e1\u662f\u5934\u4e0a\u5305\u5e15\u7684\u5973\u5b69\u5b50\uff0c\u4e5f\u5c31\u662f\u8868\u660e\u843d\u82b1\u6709\u4e3b\u53ea\u7b49\u7740\u7ed3\u5a5a\u4e86\uff0c\u5929\u9634\u6559\u4eba\u4ece\u6765\u6ca1\u6709\u653e\u5f03\u5bf9 \u590f\u82b8\u8bf4\u6559\u7684\u673a\u4f1a\uff0c\u4f46\u662f\u9047\u4e0a\u8fd9\u4e2a\u5014\u5f3a\u65e0\u6bd4\u7684\u5973\u5b69\u5b50\uff0c\u4e5f\u6ca1\u6709\u597d\u529e\u6cd5\u6765\u5bf9\u4ed8\uff0c\u6700\u540e\u624d\u4ee5\u866c\u987b\u5ba2 \u5df2\u5217\u8eab\u6559\u4e0b\u4f5c\u4e3a\u7406\u7531\u3002 \u3000\u3000\u4e5d\u5929\u4ed9\u5b50\u66f4\u63e3\u6478\u900f\u590f\u82b8\u7684\u5fc3\uff0c\u5929\u9634\u6559\u4eba\u65e9\u5df2\u4fa6\u51fa\u6b66\u5f53\u6d3e\u4ee5\u53ca\u5404\u6b63\u6d3e\u4eba\u58eb\u7684\u4e3e\u52a8\uff0c\u56e0\u4e4b\u60f3 \u628a\u718a\u501c\u5c1a\u672a\u660e\u8bf1\u81f3\u8346\u5dde\u5e9c\u767d\u51e4\u5802\uff0c\u9975\u4ee5\u7f8e\u5973\uff0c\u6536\u7f57\u5728\u5929\u9634\u6559\u4e0b\u3002\u5bf9\u4e8e\u5176\u4ed6\u5404\u6d3e\u7684\u4eba\uff0c\u4ed6\u4eec\u4e5f \u90fd\u6709\u79bb\u95f4\u5206\u5316\u7684\u6bd2\u8ba1\u9634\u8c0b\u3002 \u3000\u3000\u4e5d\u5929\u4ed9\u5b50\u65e2\u5b89\u7f6e\u4e0b\u590f\u82b8\uff0c\u4ee5\u4e3a\u718a\u501c\u4e0d\u4f1a\u4e0d\u5165\u8c37\u4e2d\uff0c\u4e0d\u6599\u590f\u82b8\u7adf\u540c\u6837\u7684\u975e\u5e38\u5014\u5f3a\uff0c\u4f46\u662f\u590f \u82b8\u591a\u5c11\u5bf9\u5929\u9634\u6559\u4eba\u53d1\u751f\u597d\u611f\uff0c\u662f\u65e0\u53ef\u8bb3\u8a00\u7684\uff0c\u8fd9\u5bf9\u8fdb\u884c\u62c9\u62e2\u718a\u501c\u662f\u683c\u5916\u6709\u5229\uff0c\u718a\u501c\u65e9\u5df2\u5728\u6b66 \u5f53\u5c71\u593a\u5251\u65f6\uff0c\u4fbf\u662f\u7126\u5f02\u884c\u592b\u5987\u6025\u4e8e\u4e89\u53d6\u7684\u4eba\u7269\u4e86\uff01\u5c1a\u672a\u660e\u9886\u5bfc\u4e24\u6cb3\u7eff\u6797\u4e4b\u58eb\uff0c\u66f4\u662f\u4e0d\u5bb9\u5ffd\u89c6 \u7684\u4eba\u7269\u3002 \u3000\u3000\u718a\u501c\u542c\u5b8c\u4f0a\u4eba\u5410\u6c14\u5982\u5170\u4e00\u904d\u7ec6\u8bc9\uff0c\u5fc3\u91cc\u9887\u4e3a\u590f\u82b8\u6b23\u6170\uff0c\u4e8e\u662f\u590f\u82b8\u95ee\u4ed6\uff1a\u201c\u4f60\u5462\uff1f\u4f60\u548c\u6b66 \u5f53\u6d3e\u4eba\u53c8\u600e\u6837\u6405\u5728\u4e00\u8d77\uff1f\u201d \u3000\u3000\u718a\u501c\u77e5\u9053\u5979\u6068\u900f\u4e86\u6b66\u5f53\u56db\u4eea\u5251\u5ba2\uff0c\u4e0e\u5176\u591a\u8d39\u5507\u820c\u89e3\u91ca\uff0c\u4e0d\u5982\u987a\u7740\u5979\u7684\u6027\u513f\u597d\u4e9b\uff0c\u65e5\u540e\u6563 \u82b1\u4ed9\u5b50\u4f1a\u4ee5\u5927\u4e49\u6653\u55bb\u5979\uff0c\u800c\u4e14\u590f\u82b8\u4f1a\u542c\u5979\u7684\u7530\u59d0\u59d0\u7684\u8bdd\u7684\u3002 \u3000\u3000\u718a\u501c\u5148\u8ff0\u53ca\u521d\u4e0a\u6b66\u5f53\u60c5\u5f62\uff0c\u5939\u7740\u751c\u751c\u8c37\u4e2d\u4e00\u5e55\u60ca\u9669\u573a\u9762\uff0c\u590f\u82b8\u542c\u8bf4\u4ed6\u548c\u5c1a\u672a\u660e\u51e0\u4e4e\u4f24\u5728 \u6563\u82b1\u4ed9\u5b50\u94a2\u4e38\u4e4b\u4e0b\uff0c\u4e0d\u7531\u4e00\u6487\u5634\u5f97\u610f\u5730\u7b11\u9053\uff1a\u201c\u554a\u5440\uff0c\u6211\u7684\u718a\u5927\u4fa0\uff0c\u4f60\u4e5f\u78b0\u4e0a\u786c\u70b9\u5b50\u4e86\uff01\u7530 \u59d0\u59d0\u90a3\u79cd\u624b\u6cd5\uff0c\u6211\u5df2\u7ecf\u5b66\u4f1a\u4e86\uff01\u201d\u5979\u81ea\u7136\u8981\u8868\u793a\u5979\u8eab\u624b\u66f4\u52a0\u4e0d\u51e1\u3002 \u3000\u3000\u718a\u501c\u4e50\u4e8e\u606d\u7ef4\u7530\u654f\u654f\u4e00\u756a\uff0c\u95f4\u63a5\u4e5f\u5c31\u662f\u606d\u7ef4\u590f\u82b8\uff0c\u4f7f\u590f\u82b8\u5fc3\u5934\u975e\u5e38\u5f97\u610f\uff0c\u4f46\u662f\u6563\u82b1\u4ed9\u5b50 \u7ecf\u718a\u501c\u4e00\u5251\u5212\u7834\u76ae\u80a4\uff0c\u800c\u836f\u6027\u987f\u5931\u6062\u590d\uff0c\u5979\u7684\u82b1\u5bb9\u7389\u8c8c\uff0c\u8fd9\u662f\u591a\u4e48\u4e00\u79cd\u4f7f\u4eba\u60ca\u5947\u7684\u4e8b\u5440\uff01\u590f \u82b8\u5bf9\u6b64\u63d0\u51fa\u8bb8\u591a\u95ee\u9898\uff0c\u718a\u501c\u5374\u53c8\u600e\u80fd\u7b54\u590d\u5462\uff1f \u3000\u3000\u7b2c\u4e8c\u6b21\u6b66\u5f53\u5927\u4f1a\u6b63\u6d3e\u4eba\u58eb\u7684\u4e8b\uff0c\u718a\u501c\u7565\u800c\u4e0d\u8c08\uff0c\u53ea\u8bf4\u548c\u5c1a\u672a\u660e\uff0c\u5e38\u6f2b\u5929\u592b\u5987\uff0c\u53bb\u8d28\u95ee\u6b66 \u5f53\u56db\u4eea\u5251\u5ba2\u7684\u3002 \u3000\u3000\u590f\u82b8\u542c\u89c1\u4ed6\u4eec\u90fd\u4e3a\u5979\u5954\u6ce2\uff0c\u5fc3\u91cc\u975e\u5e38\u5feb\u6170\uff0c\u5979\u95ee\u9053\uff1a\u201c\u5c1a\u672a\u660e\u8fd9\u4eba\u5947\u602a\uff0c\u600e\u4e48\u59d3\u540d\u7684\u542b \u610f\uff0c\u662f\u81ea\u5df1\u5c1a\u4e0d\u660e\u767d\u5440\uff01\u201d\u5979\u4e89\u5f3a\u597d\u80dc\u4e4b\u5ff5\uff0c\u4f7f\u5979\u8ffd\u95ee\u8fd9\u5c1a\u672a\u660e\u672c\u9886\u5982\u4f55\u3002 \u3000\u3000\u718a\u501c\u7b11\u8bf4\uff1a\u201c\u5c1a\u5927\u54e5\u662f\u4e24\u6cb3\u7eff\u6797\u603b\u74e2\u628a\u5b50\uff0c\u548c\u6211\u4e00\u89c1\u83ab\u9006\uff0c\u6781\u5bcc\u8c6a\u4fa0\u809d\u80c6\uff0c\u4e0a\u6b21\u4f60\u5c31\u662f \u5728\u4eba\u5bb6\u94fa\u53f7\u91cc\u517b\u4f24\u7684\u3002\u201d \u3000\u3000\u718a\u501c\u6ca1\u6709\u79f0\u8d5e\u5c1a\u672a\u660e\u7684\u6b66\u529f\uff0c\u662f\u6015\u8fd9\u5c0f\u59d0\u513f\u4efb\u6027\u4e0d\u670d\u6c14\uff0c\u590f\u82b8\u542c\u8bf4\u5c1a\u672a\u660e\u4e5f\u6765\u81f3\u767d\u51e4 \u5802\uff0c\u6b22\u7136\u8bf4\u9053\uff1a\u201c\u6211\u60f3\u4ed6\u672c\u9886\u9519\u4e0d\u4e86\uff0c\u5426\u5219\u600e\u80fd\u8ddf\u4f60\u718a\u5927\u4fa0\u7ed3\u4e3a\u597d\u53cb\u5440\uff01\u201d \u3000\u3000\u718a\u501c\u7b11\u8bf4\uff1a\u201c\u4f60\u8fd8\u662f\u5634\u4e0a\u4e0d\u9976\u4eba\uff0c\u8bda\u5fc3\u6316\u82e6\u627e\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u590f\u82b8\u5a07\u55d4\u9053\uff1a\u201c\u7b97\u4e86\uff01\u96be\u9053\u5927\u5bb6\u4e0d\u79f0\u4f60\u662f\u6b66\u6797\u4e09\u79c0\uff1f\u201d \u3000\u3000\u590f\u82b8\u4e00\u9897\u82b3\u5fc3\u4f55\u5c1d\u4e0d\u4ee5\u718a\u501c\u6b66\u529f\u8d85\u4eba\uff0c\u5f15\u4e3a\u5979\u7684\u5149\u8363\u5462\u3002 \u3000\u3000\u7a81\u7136\u7a97\u524d\u4eba\u5f71\u4e00\u95ea\uff0c\u5c1a\u672a\u660e\u7684\u53e3\u97f3\uff0c\u8f7b\u58f0\u4e00\u5618\uff0c\u9053\uff1a\u201c\u718a\u5927\u54e5\u4ed4\u7ec6\uff01\u6709\u5979\u4eec\u4eba\u4f0f\u5728\u6697\u5904 \u5077\u542c\u4f60\u4eec\u7684\u8bdd\uff01\u201d \u3000\u3000\u718a\u501c\u6050\u590f\u82b8\u4e0d\u613f\u5728\u5979\u623f\u91cc\u63a5\u89c1\u5c1a\u672a\u660e\uff0c\u6b63\u9732\u51fa\u4e3a\u96be\u4e4b\u8272\uff0c\u590f\u82b8\u5df2\u5a07\u58f0\u547c\u9053\uff1a\u201c\u5c1a\u5927\u54e5\uff0c \u8bf7\u8fdb\u5c4b\u91cc\u4e00\u8c08\uff01\u201d \u3000\u3000\u718a\u501c\u8fd9\u624d\u6b23\u7136\u5f00\u95e8\u76f8\u8fce\uff0c\u4f46\u662f\u4ed6\u5947\u602a\u5c1a\u672a\u660e\u600e\u4f1a\u534a\u591c\u627e\u4ed6\u4eec\uff1f\u6bd4\u53ca\u5c1a\u672a\u660e\u8bf4\u660e\u7684\u906d\u9047\uff0c \u718a\u501c\u4e0d\u80dc\u5feb\u6170\uff0c\u800c\u5929\u9634\u6559\u4eba\u4e00\u5207\u7684\u8ba1\u5212\uff0c\u4e5f\u5f52\u4e4b\u6ce1\u5f71\u4e86\u3002 \u3000\u3000\u5c1a\u672a\u660e\u591a\u5c11\u5403\u4e86\u51e0\u676f\u9152\uff0c\u5e2d\u6563\u4e4b\u540e\uff0c\u88ab\u4e8c\u5973\u5bfc\u4eba\u4e86\u5317\u9762\u4fa7\u9662\u4e2d\u4e00\u5ea7\u7cbe\u81f4\u82b1\u5385\u91cc\uff0c\u8fd9\u5385\u4e2d \u7684\u9648\u8bbe\uff0c\u5bf9\u4ed6\u592a\u4e0d\u9002\u5b9c\uff0c\u7b80\u76f4\u662f\u5927\u5bb6\u5c0f\u59d0\u95fa\u9601\uff0c\u9e33\u88f3\u7ee3\u88ab\uff0c\u9526\u5e10\u6d41\u82cf\uff0c\u800c\u68b3\u5986\u53f0\u4e0a\u9ad8\u70e7\u7740\u4e00 \u5bf9\u513f\u81c2\u7c97\u7ec6\u7684\u9f99\u51e4\u82b1\u70db\u3002 \u3000\u3000\u58c1\u4e0a\u7684\u5b57\u753b\uff0c\u5982\u592a\u771f\u51fa\u6d74\uff0c\u6d1b\u795e\u620f\u6c34\u4e4b\u7c7b\uff0c\u6bcf\u4ef6\u4e1c\u897f\u90fd\u5e26\u6709\u4e00\u79cd\u8272\u60c5\u523a\u6fc0\uff0c\u8fd9\u4f7f\u5c1a\u672a\u660e \u5927\u4e3a\u60f6\u60d1\u4e0d\u5b89\u3002 \u3000\u3000\u4e00\u76cf\u70ed\u8336\u5165\u809a\u4e4b\u540e\uff0c\u5c1a\u672a\u660e\u9152\u91cf\u662f\u6781\u5927\u7684\uff0c\u8fd9\u51e0\u676f\u9152\u5e73\u65f6\u53ea\u80fd\u6da6\u6da6\u5589\u5499\uff0c\u8fd9\u65f6\u5374\u718f\u718f\u9676 \u9676\uff0c\u5468\u8eab\u6e10\u6e10\u8d77\u4e86\u706b\u8fa3\u8fa3\u4e4b\u611f\uff0c\u800c\u5934\u8111\u4f3c\u6709\u4e00\u670d\u529b\u91cf\u4fc3\u4f7f\u4ed6\u5411\u8089\u6b32\u65b9\u9762\u51b2\u52a8\u7740\uff0c\u5c1a\u672a\u660e\u795e\u5fd7 \u867d\u6e05\u695a\uff0c\u5374\u6291\u5236\u4e0d\u4f4f\u8fd9\u79cd\u51b2\u52a8\u3002 \u3000\u3000\u4eba\u7c7b\u5929\u8d4b\u7684\u672c\u80fd\uff0c\u52a0\u8fdb\u4e00\u79cd\u836f\u7269\u7684\u529b\u91cf\uff0c\u4f7f\u5c1a\u672a\u660e\u72ec\u5b88\u8fd9\u89e6\u76ee\u523a\u6fc0\u7684\u7a7a\u95fa\uff0c\u51e0\u4e4e\u5feb\u8fbe\u4e00 \u79cd\u75af\u72c2\u7684\u7a0b\u5ea6\u3002 \u3000\u3000\u5c1a\u672a\u660e\u60f3\u8d77\u4e86\u9752\u5e15\u5c11\u5973\uff0c\u7ee2\u7ee2\u6e05\u5f71\uff0c\u5982\u5728\u76ee\u524d\uff0c\u5c1a\u672a\u660e\u81c2\u4e00\u62b1\uff0c\u7a7a\u98d8\u98d8\u7684\u4ed6\u53c8\u80fd\u6402\u62b1\u4f4f \u4ed6\u7684\u5e7b\u89c9\u4e48\uff1f \u3000\u3000\u5c1a\u672a\u660e\u89c9\u5f97\u5fc3\u91cc\u975e\u5e38\u70e6\u8e81\uff0c\u5507\u820c\u67af\u7126\u4f7f\u4ed6\u4e0d\u5f97\u4e0d\u5403\u70b9\u8336\u6c34\uff0c\u800c\u8fd9\u6070\u5982\u996e\u9e29\u6b62\u6e34\uff0c\u8d8a\u5403\u4e0b \u5f97\u591a\uff0c\u8d8a\u53d1\u5468\u8eab\u53d1\u8d77\u71e5\u70ed\uff0c\u8840\u7ba1\u91cc\u7684\u8840\u6db2\u5954\u5f1b\u52a0\u901f\uff0c\u53c8\u65e0\u7591\u5730\u589e\u52a0\u4e86\u8eab\u4f53\u4e0a\u67d0\u90e8\u5206\u7684\u51b2\u52a8\uff0c \u7a97\u5916\u672c\u5c31\u6709\u5929\u9634\u6559\u4eba\u6f5c\u4f0f\u3002\u800c\u5c1a\u672a\u660e\u5374\u4e00\u70b9\u4e5f\u4e0d\u5bdf\u89c9\uff0c\u7a81\u7136\u5657\u7684\u4e00\u58f0\u5a07\u7b11\uff0c\u53d1\u81ea\u7a97\u524d\uff0c\u5355\u662f \u8fd9\u5973\u5b69\u5b50\u5a07\u5ae9\u7684\u7b11\u58f0\uff0c\u5df2\u8db3\u591f\u4f7f\u4ed6\u795e\u9a70\u9b42\u9500\u4e86\u3002 \u3000\u3000\u5c1a\u672a\u660e\u5982\u540c\u5236\u670d\u4e0d\u4e86\u7684\u8131\u7f30\u91ce\u9a6c\uff0c\u7adf\u4e00\u4e2a\u7bad\u6b65\u7a7f\u5e18\u800c\u51fa\uff0c\u5411\u90a3\u53d1\u58f0\u4e4b\u5904\u6251\u53bb\uff0c\u8fd9\u65f6\u7eb5\u4ee4 \u662f\u4e2a\u5aeb\u6bcd\u65e0\u76d0\uff0c\u5c1a\u672a\u660e\u4e5f\u4f1a\u9965\u4e0d\u62e9\u98df\uff0c\u5411\u5979\u53d1\u6cc4\u4e00\u4e0b\u7684\u3002 \u3000\u3000\u5c1a\u672a\u660e\u5374\u6251\u4e86\u4e2a\u7a7a\uff0c\u5e26\u6709\u5bd2\u610f\u7684\u591c\u98ce\uff0c\u62c2\u9762\u751f\u51c9\uff0c\u4f7f\u4ed6\u5934\u8111\u6e05\u9192\u4e86\u4e00\u4e8c\u5206\uff0c\u4ed6\u832b\u832b\u6ce8\u89c6 \u7740\u9662\u4e2d\u82b1\u5f71\u968f\u98ce\u6447\u52a8\uff0c\u662f\u4e0d\u662f\u7389\u4eba\u73ca\u73ca\u6765\u5462\uff1f \u3000\u3000\u5c1a\u672a\u660e\u7ec8\u4e8e\u5931\u671b\u6b65\u56de\u5ba4\u4e2d\uff0c\u4e00\u9635\u9635\u7b4b\u8089\u6124\u5f20\uff0c\u800c\u4e3e\u76ee\u90fd\u662f\u4e9b\u523a\u6fc0\u4ed6\u7684\u88f8\u5973\u56fe\u753b\uff0c\u53c8\u4f7f\u4ed6 \u4e00\u9897\u5fc3\u718a\u718a\u70e7\u8d77\u6765\u3002 \u3000\u3000\u4e00\u5239\u90a3\u95f4\uff0c\u7a97\u5916\u90a3\u7ea2\u5e15\u5c11\u5973\u5a07\u7b11\u4e4b\u58f0\u9707\u8033\uff0c\u8f7b\u67d4\u5a07\u5a49\u7684\u58f0\u97f3\u9053\uff1a\u201c\u5c1a\u5f53\u5bb6\u7684\u8fd8\u6ca1\u5c31\u5bdd\uff1f \u4e00\u8def\u978d\u9a6c\u52b3\u987f\uff0c\u8be5\u65e9\u65e9\u5b89\u6b47\u4e86\uff01\u201d \u3000\u3000\u5c1a\u592b\u660e\u4e8e\u4e5f\u8010\u4e0d\u4f4f\uff0c\u731b\u6380\u5e18\u8dc3\u51fa\uff0c\u53e3\u91cc\u592e\u6c42\u8bf4\u9053\uff1a\u201c\u597d\u59b9\u59b9\uff0c\u8bf7\u8fdb\u5c4b\u91cc\u6765\u8c08\u8c08\uff0c\u6211\u4e00\u4e2a \u4eba\u70e6\u95f7\u5f97\u8981\u6b7b\uff01\u201d \u3000\u3000\u4f46\u662f\u5374\u53ea\u542c\u5f97\u6251\u5657\u4e00\u7b11\uff0c\u60c5\u5f71\u6643\u52a8\uff0c\u54ea\u91cc\u8fd8\u6709\u90a3\u7ea2\u5e15\u5c11\u5973\u7684\u5f71\u5b50\u3002 \u3000\u3000\u5c1a\u672a\u660e\u671b\u7740\u5929\u7a7a\u94f6\u6cb3\u5982\u9526\u75f4\u75f4\u7ad9\u7740\uff0c\u800c\u5a07\u7b11\u58f0\u53c8\u8d77\u81ea\u5ba4\u4e2d\uff0c\u9053\uff1a\u201c\u5c1a\u5f53\u5bb6\u7684\uff01\u4f60\u8bf7\u6211\u8fdb \u6765\uff0c\u4f60\u600e\u4e48\u5728\u5916\u9762\u5462\uff1f\u201d \u3000\u3000\u5c1a\u672a\u660e\u5fc3\u82b1\u4ff1\u653e\uff0c\u8dc3\u4eba\u5ba4\u4eba\uff0c\u90a3\u7ea2\u5e15\u5c11\u5973\u7684\u6731\u6b22\uff0c\u679c\u7136\u7aef\u5e84\u5f97\u50cf\u4e00\u5c0a\u795e\u50cf\uff0c\u7aef\u5750\u6905\u4e0a\uff0c \u79c0\u76ee\u76c8\u76c8\u6ce8\u89c6\u7740\u4ed6\u3002 \u3000\u3000\u5979\u50cf\u6015\u8fd9\u4e00\u5934\u91ce\u517d\uff0c\u505a\u51fa\u4ec0\u4e48\u53ef\u6015\u7684\u4e3e\u52a8\uff0c\u5979\u968f\u65f6\u51c6\u5907\u7740\u9003\u8d70\u3002\u5c1a\u672a\u660e\u773c\u775b\u67af\u6da9\uff0c\u4e5f\u6ce8 \u89c6\u7740\u5979\u72c2\u7b11\u4e0d\u5df2\u3002 \u3000\u3000\u5c1a\u672a\u660e\u5927\u80c6\u4ed6\u8bf4\uff1a\u201c\u7f28\u5802\u4e3b\u5df2\u628a\u4f60\u8bb8\u7ed9\u5728\u4e0b\u4e86\uff0c\u4f55\u5fc5\u8fd8\u5047\u60fa\u60fa\u8eb2\u907f\u6211\uff1f\u201d \u3000\u3000\u7ea2\u5e15\u5c11\u5973\u5550\u4e86\u4e00\u53e3\u8bf4\u9053\uff1a\u201c\u80e1\u8bf4\uff01\u7f2a\u5802\u4e3b\u968f\u4fbf\u8bf4\u53e5\u4f7f\u4f60\u5f00\u5fc3\u7684\u8bdd\uff0c\u4f60\u5c31\u5f53\u771f\u4e86\uff0c\u4f60\u53c8\u6ca1 \u6709\u53c2\u52a0\u5929\u9634\u6559\uff0c\u8fd9\u662f\u4e0d\u53ef\u80fd\u7684\u4e8b\uff01\u201d \u3000\u3000\u5c1a\u672a\u660e\u731b\u7136\u60ca\u9192\u4e86\u4e9b\uff0c\u663e\u7136\u8fd9\u662f\u4e00\u79cd\u6b32\u64d2\u6545\u7eb5\u7684\u9677\u9631\uff0c\u4f46\u662f\u5c1a\u672a\u660e\u5df2\u8499\u6627\u4e86\u4e00\u534a\u5fc3\u7a8d\uff0c \u4ed6\u6d51\u8eab\u98a4\u52a8\u7740\uff0c\u4f3c\u4e4e\u50cf\u4e00\u5934\u997f\u72ee\uff0c\u6068\u4e0d\u5f97\u6251\u4e0a\u53bb\u64d2\u83b7\u8fd9\u53ef\u7231\u7684\u5c11\u5973\uff0c\u7406\u667a\u4f7f\u4ed6\u7f29\u9000\u4e86\u534a\u6b65\uff0c \u5583\u5583\u592e\u6c42\u9053\uff1a\u201c\u8fd9\u6709\u4ec0\u4e48\u5173\u7cfb\uff0c\u7f28\u524d\u8f88\u4e0d\u4f1a\u89c1\u602a\u6731\u59d1\u5a18\u7684\u3002\u201d \u3000\u3000\u7ea2\u5e15\u5c11\u5973\u6545\u610f\u77dc\u6301\u7740\uff0c\u548c\u5979\u4e00\u8def\u4e0a\u90a3\u79cd\u653e\u8361\u4e0d\u62d8\u7684\u6001\u5ea6\uff0c\u8fe5\u7136\u4e0d\u540c\uff0c\u4ee5\u4f4e\u6c89\u800c\u575a\u51b3\u7684\u58f0 \u97f3\u8bf4\uff1a\u201c\u4e0d\u884c\uff01\u4e0d\u8bb8\u4f60\u4e71\u6765\uff01\u9664\u975e\u4f60\u7acb\u523b\u53bb\u9999\u5802\u7acb\u8a93\u4eba\u6559\uff0c\u5426\u5219\u4f60\u4eca\u540e\u6c38\u8fdc\u4e0d\u80fd\u518d\u6765\u767d\u51e4\u603b \u5802\uff01\u50bb\u5b50\uff0c\u4f60\u5446\u60f3\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c1a\u672a\u660e\u88ab\u8fd9\u79cd\u51b7\u6c34\u6d47\u5934\u7684\u8bdd\uff0c\u60ca\u5446\u5728\u90a3\u91cc\u3002 \u3000\u3000\u5c1a\u672a\u660e\u8272\u5ff5\u52c3\u8d77\uff0c\u4f46\u662f\u8981\u4ed6\u7acb\u523b\u5ba3\u8a93\u6295\u5165\u5929\u9634\u6559\uff0c\u4ecd\u7136\u662f\u4ed6\u4e0d\u80af\u505a\u7684\u4e8b\uff0c\u4ed6\u5598\u5401\u7740\uff0c\u8eab \u4f53\u4e0a\u70ed\u529b\u6d8c\u6ce8\uff0c\u4f7f\u4ed6\u4f1a\u7acb\u5373\u505a\u51fa\u4e00\u4ef6\u7ec8\u8eab\u9057\u61be\u7684\u4e8b\uff0c\u771f\u7684\u4ed6\u8fd9\u6837\u75af\u72c2\u505a\u53bb\uff0c\u90a3\u540e\u679c\u662f\u4e0d\u96be\u60f3 \u8c61\u7684\u3002 \u3000\u3000\u800c\u7ea2\u5e15\u5c11\u5973\uff0c\u51b3\u5fc3\u8981\u9a6f\u670d\u4ed6\u8fd9\u4e00\u5934\u731b\u72ee\uff0c\u4e1d\u6beb\u4e0d\u5047\u4ee5\u989c\u8272\uff0c\u4ee5\u6025\u5feb\u7684\u8eab\u6cd5\u98d8\u51fa\u4e86\u5ba4\u5916\uff0c \u51b7\u51b7\u7684\u8bf4\u9053\uff1a\u201c\u6211\u7ed9\u4f60\u4e00\u6bb5\u65f6\u95f4\u81ea\u5df1\u8003\u8651\u5427\uff0c\u56de\u5934\u6211\u518d\u6765\u542c\u53d6\u4f60\u7684\u7b54\u590d\uff0c\u65e9\u4e9b\u51b3\u5b9a\uff0c\u65e9\u4e00\u523b \u4eba\u6559\uff0c\u5c31\u65e9\u4f7f\u6211\u5b89\u6170\u5462\uff01\u201d \u3000\u3000\u5c1a\u672a\u660e\u4e0d\u518d\u80fd\u6291\u5236\u81ea\u5df1\u4e86\uff0c\u4ed6\u731b\u4e00\u65cb\u8eab\uff0c\u8ddf\u7740\u51b2\u51fa\u5ba4\u5916\u3001\u4ee5\u6781\u5feb\u7684\u624b\u5730\uff0c\u6251\u4e0a\u53bb\u60f3\u628a\u6731 \u6b22\u4e00\u628a\u6402\u5728\u6000\u91cc\u3002 \u3000\u3000\u7ea2\u5e15\u5c11\u5973\u65e9\u6709\u9632\u5907\uff0c\u800c\u4e14\u6b66\u529f\u4e5f\u662f\u5929\u9634\u6559\u4e00\u4e8c\u6d41\u597d\u624b\uff0c\u5a07\u8eaf\u4e00\u6643\uff0c\u5df2\u7eb5\u51fa\u4e24\u4e08\u591a\u8fdc\u3002\u5979 \u6beb\u4e0d\u8e0c\u8e87\u5730\u9a70\u51fa\u8fd9\u504f\u9662\u95e8\u5916\u3002 \u3000\u3000\u5c1a\u672a\u660e\u4e24\u4e2a\u8d77\u843d\uff0c\u4ecd\u6ca1\u628a\u7389\u4eba\u8ffd\u4e0a\uff0c\u66f4\u52a0\u5730\u610f\u9a6c\u5fc3\u733f\uff0c\u96be\u4ee5\u7981\u53d7\uff0c\u4eba\u4eec\u5728\u9965\u6e34\u96be\u5f53\u4e4b \u9645\uff0c\u770b\u7740\u6446\u5728\u9762\u524d\u7684\u98df\u7269\uff0c\u800c\u4e0d\u80fd\u5230\u624b\uff0c\u600e\u80fd\u4e0d\u5782\u6d8e\u4e09\u5c3a\uff1f\u5c1a\u672a\u660e\u7126\u71e5\u7740\uff0c\u53c8\u4e0d\u80fd\u51b2\u4eba\u6b63\u9662 \u53bb\uff0c\u6b63\u50cf\u7334\u5b50\u4e00\u822c\u6293\u8033\u6320\u816e\u3002 \u3000\u3000\u5374\u542c\u7a7a\u4e2d\u60a0\u60a0\u4f20\u6765\u4e00\u58f0\u5973\u5b50\u53f9\u606f\u4e4b\u58f0\u3002 \u3000\u3000\u9759\u591c\u5bc2\u5bc2\uff0c\u8fd9\u79cd\u51c4\u51c9\u54c0\u6028\u4e4b\u97f3\uff0c\u4f7f\u4eba\u6bdb\u53d1\u60ca\u7136\u3002 \u3000\u3000\u5c1a\u672a\u660e\u7565\u4e00\u9547\u5b9a\u5fc3\u795e\uff0c\u62d4\u6b65\u53c8\u8dc3\u4eba\u623f\u4e2d\uff0c\u4ed6\u4ee5\u4e3a\u53c8\u662f\u7ea2\u5e15\u5c11\u5973\u6349\u5f04\u4ed6\uff0c\u5374\u4e0d\u6599\u5ba4\u5185\u7a7a\u7a7a \u5982\u4e5f\uff0c\u54ea\u6709\u4ec0\u4e48\u4eba\u5f71\uff0c\u53ea\u7a7a\u6c14\u4e2d\u9057\u7559\u4e0b\u4e00\u80a1\u5170\u9e9d\u4e4b\u99a8\u3002 \u3000\u3000\u5c1a\u672a\u660e\u5c06\u8981\u71c3\u8d77\u4e86\u6124\u6012\u4e4b\u706b\uff0c\u4ed6\u5c06\u4e0d\u987e\u4e00\u5207\uff0c\u53ea\u8981\u6709\u4e2a\u7f8e\u8c8c\u5c11\u5973\u6b64\u65f6\u51fa\u73b0\uff0c\u4ed6\u4f1a\u505a\u4ed6\u8981 \u505a\u7684\u4e8b\u3002 \u3000\u3000\u7a97\u5916\u53c8\u662f\u4e00\u58f0\u5e7d\u6028\u7684\u5fae\u53f9\uff0c\u4f7f\u4ed6\u80af\u5b9a\u4e86\u5fc5\u662f\u7ea2\u5e15\u5c11\u5973\uff0c\u4ed6\u6b63\u4ee5\u6781\u6124\u6012\u7684\u5fc3\u60c5\uff0c\u5411\u5ba4\u5916\u51b2 \u53bb\u3002 \u3000\u3000\u7a81\u7136\u773c\u524d\u81ea\u6643\u6643\u4e00\u56e2\u4e1c\u897f\uff0c\u671d\u7740\u4ed6\u9762\u4e0a\u98de\u6765\uff0c\u5c1a\u672a\u660e\u63a5\u6697\u5668\u7684\u624b\u6cd5\u4e5f\u662f\u6781\u6709\u7814\u7a76\u7684\uff0c\u4ed6 \u5fd9\u4e00\u7f29\u624b\uff0c\u4f38\u624b\u63a5\u4f4f\u4e86\u98de\u6765\u7684\u4e1c\u897f\uff1b\u53ea\u89c9\u4eba\u624b\u8f6f\u523a\u523a\u7684\uff0c\u4f3c\u662f\u4e00\u4e2a\u7eb8\u56e2\u3002 \u3000\u3000\u5c1a\u672a\u660e\u5fc3\u5934\u4e00\u751c\uff0c\u4ee5\u4e3a\u662f\u7ea2\u5e15\u5c11\u5973\u629b\u6765\u4e4b\u7269\uff0c\u8d76\u5feb\u51d1\u8fd1\u7ea2\u70db\uff0c\u628a\u7eb8\u56e2\u627e\u5f00\uff0c\u5df2\u6298\u76b1\u4e86\u7684 \u7eb8\u4e0a\uff0c\u8d6b\u7136\u73b0\u51fa\u51e0\u4e2a\u5b57\uff1a\u201c\u901f\u670d\u89e3\u836f\uff0c\u5e78\u52ff\u81ea\u8bef\uff1a\u201c\u800c\u7eb8\u56e2\u5185\u6b63\u597d\u5305\u7740\u4e09\u7c92\u6de1\u7eff\u8272\u7684\u836f\u4e38\uff0c \u6674\u5929\u9739\u96f3\uff0c\u9707\u9192\u4e86\u4ed6\u4e00\u573a\u7eee\u68a6\u3002\u5c1a\u672a\u660e\u65b9\u624d\u60ca\u89c9\u81ea\u5df1\u6d89\u8eab\u6781\u53ef\u6015\u7684\u9677\u9631\u8fb9\u7f18\u3002\u4ed6\u628a\u4e09\u7c92\u7eff\u4e38 \u56bc\u788e\u553e\u6cab\u54bd\u4e0b\uff0c\u7528\u684c\u4e0a\u7389\u77f3\u9547\u7eb8\uff0c\u51b7\u51b0\u51b0\u7684\u71a8\u8d34\u989d\u4e0a\uff0c\u4e00\u8f6c\u773c\u95ee\u51c9\u610f\u4eba\u8111\uff0c\u4eba\u5df2\u6e05\u9192\u4e86\u8bb8 \u591a\uff0c\u836f\u529b\u4e5f\u9010\u6e10\u751f\u6548\uff0c\u4e00\u8154\u90aa\u5ff5\u6b32\u706b\uff0c\u987f\u65f6\u964d\u843d\u4e0b\u53bb\uff0c\u4ed6\u4e0d\u80dc\u611f\u6fc0\u8fd9\u9001\u836f\u7684\u4eba\uff0c\u4f46\u662f\u8fd9\u4eba\u53c8 \u662f\u8c01\u5462\uff1f\u5c1a\u672a\u660e\u6728\u7136\u7acb\u5728\u5ba4\u5185\uff0c\u56de\u5fc6\u521a\u624d\u7ecf\u8fc7\u7684\u60c5\u4e8b\uff0c\u51b7\u6c57\u81ea\u5468\u8eab\u76f4\u5192\uff0c\u51e0\u4e4e\u4e00\u5931\u8db3\u6210\u7ec8\u8eab \u5927\u6068\uff0c\u591a\u4e48\u53ef\u6015\u7684\u4e8b\uff01\u7a81\u7136\u8eab\u540e\u9999\u98ce\u5fae\u52a8\uff0c\u4f3c\u6709\u5973\u5b50\u6765\u81f3\u8eab\u540e\uff0c\u5c1a\u672a\u660e\u4ee5\u4e3a\u662f\u90a3\u4e2a\u7ea2\u5e15\u5c11 \u5973\uff0c\u4ed6\u5fc3\u91cc\u6e05\u9192\u4e4b\u540e\uff0c\u5bf9\u5979\u618e\u538c\u5230\u4e86\u5341\u4e8c\u5206\uff0c\u6bd4\u53ca\u4ed6\u626d\u8eab\u770b\u65f6\uff0c\u4e0d\u7531\u773c\u524d\u4e00\u4eae\uff0c\u559c\u51fa\u671b\u5916\uff0c \u7adf\u662f\u4ed6\u4e00\u8def\u4e0a\u5f97\u4e0d\u7740\u9752\u7750\u7684\u9752\u5e15\u5c11\u5973\u3002\u9752\u5e15\u5c11\u5973\u9762\u8272\u5341\u5206\u6c89\u91cd\uff0c\u76b1\u76b1\u7709\u95ee\u9053\uff1a\u201c\u4f60\u670d\u4e0b\u90a3\u4e09 \u7c92\u89e3\u836f\u4e86\u5427\uff01\u5c1a\u5927\u4fa0\uff0c\u6211\u8b66\u544a\u4f60\uff0c\u5feb\u4e9b\u79bb\u5f00\u6b64\u5730\u3002\u201d \u3000\u3000\u9752\u5e15\u5c11\u5973\u4e00\u798f\u8fd8\u793c\uff0c\u4ecd\u7136\u51b7\u51b7\u50ac\u4ed6\u8bf4\uff1a\u201c\u5c1a\u5927\u4fa0\u52ff\u70e6\u8a00\u8c22\uff0c\u6b64\u5730\u5343\u4e07\u4e0d\u53ef\u4e45\u7559\uff0c\u4ece\u901f\u77e5 \u4f1a\u718a\u5927\u4fa0\uff0c\u4e00\u540c\u8d70\u5427\uff01\u201d \u3000\u3000\u5c1a\u672a\u660e\u6599\u77e5\u4e8b\u6001\u4e25\u91cd\uff0c\u4f46\u662f\u4ed6\u4ee5\u4e3a\u5929\u9634\u6559\u4e0d\u66fe\u7acb\u5373\u7ffb\u8138\uff0c\u800c\u9752\u5e15\u5c11\u5973\u52b3\u8e2a\u964d\u4e34\uff0c\u6b63\u662f\u4ed6 \u6e34\u671b\u4e0d\u5230\u7684\u4e8b\u3002 \u3000\u3000\u5c1a\u672a\u660e\u656c\u91cd\u9752\u5e15\u5c11\u5973\uff0c\u4e0d\u6562\u7a0d\u9732\u4e9b\u8f7b\u6311\u4e4b\u6001\uff0c\u6545\u610f\u8bf4\u9053\uff1a\u5c1a\u67d0\u7b49\u8499\u7f2a\u5802\u4e3b\u7aed\u8bda\u6b3e\u7559\uff0c\u5c82 \u53ef\u4e0d\u8f9e\u800c\u53bb\uff1f\u201c\u9752\u5e15\u5c11\u5973\u5fae\u5fae\u53f9\u606f\u8bf4\uff1a\u201c\u5c31\u662f\u73b0\u5728\u4f60\u4eec\u60f3\u8d70\uff0c\u4e5f\u672a\u5fc5\u8d70\u5f97\u6389\uff01\u5929\u9634\u6559\u767d\u51e4\u603b \u5802\u662f\u4ec0\u4e48\u5730\u65b9\uff0c\u4f60\u660e\u81ea\u4e48\uff1f\u201d \u3000\u3000\u5c1a\u672a\u660e\u832b\u832b\u7136\u70b9\u9996\u8fde\u8fde\u5e94\u662f\uff0c\u4f46\u4ed6\u81ea\u6043\u4e00\u8eab\u7edd\u6280\uff0c\u8fd9\u9662\u4e2d\u4e0d\u8fc7\u4e00\u7fa4\u5728\u5f31\u5c11\u5973\uff0c\u5fc3\u4e2d\u672a\u514d \u4e0d\u5927\u76f8\u4fe1\uff0c\u9042\u4fca\u7709\u4e00\u6311\u8bf4\uff1a\u201c\u8d70\u8fd8\u4e0d\u5bb9\u6613\uff0c\u718a\u5927\u54e5\u5728\u6cf0\u5c71\u7edd\u9876\u4e5f\u66fe\u53d7\u903c\uff0c\u6b66\u5f53\u5c71\u524d\uff0c\u8d35\u6559\u6559 \u4e3b\u7387\u9886\u7740\u90a3\u4e48\u591a\u9ad8\u624b\uff0c\u6211\u5c1a\u672a\u660e\u4e0d\u662f\u4ece\u5bb9\u6765\u53bb\uff01\u201d \u3000\u3000\u9752\u5e15\u5c11\u5973\u86fe\u7709\u52a0\u8e59\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u90a3\u662f\u6559\u4e3b\u4ee5\u524d\u8bda\u5fc3\u5ef6\u63fd\u4f60\u4eec\uff0c\u4e5f\u53ef\u4ee5\u8bf4\u662f\u7f51\u5f00\u4e00\u9762\uff01 \u4e0d\u7136\u4f1a\u597d\u7aef\u7aef\u7684\u628a\u8d2f\u65e5\u5251\u8fd8\u7ed9\u718a\u501c\uff1f\u8fd9\u6b21\u662f\u4ed6\u4eec\u6700\u540e\u4e00\u7740\u624b\u6bb5\uff0c\u56e0\u4e3a\u4f60\u4fe9\u786e\u6709\u4e00\u8eab\u624b\u672c\u9886\uff0c \u582a\u4e3a\u672c\u6559\u7fbd\u7ffc\uff0c\u82e5\u8fd8\u4e0d\u53d7\u7262\u7b3c\uff0c\u90a3\u5c82\u80fd\u653e\u4f60\u4fe9\u8d70\u6389\uff1f\u201d \u3000\u3000\u5c1a\u672a\u660e\u5fc3\u91cc\u81ea\u7136\u4e0d\u4f1a\u4fe1\u670d\uff0c\u5c11\u5e74\u82f1\u6770\u58ee\u5fd7\u51cc\u4e91\uff0c\u7edd\u4e0d\u4e3a\u5a01\u6b66\u6240\u5c48\uff0c\u51b5\u4e14\u4ed6\u5177\u6709\u4e00\u526f\u4e0d\u5e73 \u51e1\u7684\u8eab\u624b\uff0c\u5982\u4f55\u80fd\u4f7f\u4ed6\u53e3\u4e2d\u8ba4\u670d\uff1f\u4f46\u9752\u5e15\u5c11\u5973\u8fd9\u4e00\u756a\u597d\u610f\uff0c\u603b\u4e0d\u80fd\u8bf4\u4e9b\u5f97\u7f6a\u5979\u7684\u8bdd\uff0c\u5c1a\u672a\u660e \u6ee1\u4e0d\u4ee5\u4e3a\u662f\u7684\u795e\u6001\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u65e2\u662f\u67f3\u59d1\u5a18\u6307\u793a\uff0c\u6211\u5c31\u53bb\u901a\u77e5\u718a\u5927\u54e5\u4e00\u58f0\uff0c\u81f3\u4e8e\u5929\u9634 \u6559\u2026\u2026\u201d \u3000\u3000\u4ed6\u6ca1\u8bf4\u4e0b\u53bb\uff0c\u6362\u4e86\u53e3\u6c14\u8bf4\u9053\uff1a\u201c\u718a\u5927\u54e5\u73b0\u5728\u4f55\u5904\uff0c\u8bf7\u59d1\u5a18\u793a\u77e5\uff01\u8fd8\u6709\u5929\u9634\u6559\u65e2\u4e0d\u662f\u4ec0\u4e48 \u6b63\u6d3e\u7ec4\u7ec7\uff0c\u67f3\u59d1\u5a18\u4ee5\u5d06\u5cd2\u9ad8\u5f1f\uff0c\u4f55\u6545\u5728\u4ed6\u4eec\u6559\u4e0b\u53ae\u6df7\uff1f\u5c1a\u67d0\u4e0d\u80dc\u66ff\u59d1\u5a18\u60cb\u60dc\u5462\uff1f\u201d \u3000\u3000\u9752\u5e15\u5c11\u5973\u9765\u5fae\u6cdb\u7ea2\u6655\uff0c\u4f46\u4f3c\u6709\u96be\u8a00\u4e4b\u9690\uff0c\u76b1\u7709\u6447\u6447\u5934\u53f9\u606f\u8bf4\uff1a\u201c\u8fd9\u4f60\u4e0d\u660e\u767d\uff0c\u4e0d\u8fc7\u4eca\u591c \u4f60\u548c\u718a\u5927\u4fa0\u4e00\u8d70\uff0c\u6211\u53ea\u6709\u4e5f\u4e00\u8d70\u4e86\u4e4b\uff01\u201d\u3001\u5c1a\u672a\u660e\u5fc3\u91cc\u975e\u5e38\u6b23\u6170\uff0c\u4f46\u4e0d\u4fbf\u95ee\u5979\u8d70\u5411\u54ea\u91cc\u3002 \u3000\u3000\u9752\u5e15\u5c11\u5973\u95ea\u8eab\u5411\u5ba4\u5916\u9000\u51fa\uff0c\u53c8\u4e00\u76f4\u5728\u503e\u8033\u8c1b\u542c\u5916\u9762\u7684\u52a8\u9759\uff0c\u4f3c\u4e4e\u53d1\u89c9\u4e86\u4ec0\u4e48\u58f0\u97f3\uff0c\u5f88\u7094 \u7684\u4f4e\u58f0\u8bf4\u4e86\u51e0\u53e5\u8bdd\uff0c\u6307\u660e\u718a\u501c\u548c\u590f\u82b8\u7684\u4f4f\u6240\uff0c\u7acb\u5373\u77a5\u7136\u901d\u53bb\u3002 \u3000\u3000\u5c1a\u672a\u660e\u7b49\u5f85\u9752\u5e15\u5c11\u5973\u4e00\u53bb\uff0c\u82b3\u8e2a\u98d8\u6e3a\uff0c\u4e0d\u80dc\u6005\u60d8\uff0c\u4ed6\u5fc3\u5934\u4ecd\u7136\u6f3e\u52a8\u7740\u4e00\u7247\u5fae\u6f2a\uff0c\u9752\u5e15\u5c11 \u5973\u867d\u7136\u4e30\u795e\u51b7\u8273\uff0c\u5374\u663e\u7136\u7684\u662f\u5c5e\u610f\u4ed6\uff0c\u800c\u4e14\u8981\u4e3a\u4e86\u4ed6\u8131\u79bb\u5929\u9634\u6559\uff0c\u4f46\u662f\u4eba\u6d77\u832b\u832b\uff0c\u5c11\u5973\u82b3\u8e2a \u4f55\u5904\uff0c\u8fd9\u8db3\u4f7f\u5c1a\u672a\u660e\u9b42\u68a6\u76f8\u601d\u4e86\u3002 \u3000\u3000\u5c1a\u672a\u660e\u65b9\u5f85\u643a\u5251\u79bb\u53bb\uff0c\u90a3\u4f4d\u7ea2\u5e15\u5c11\u5973\u6731\u6b22\uff0c\u53c8\u8885\u8885\u5a77\u5a77\u5730\u8d70\u4e86\u8fc7\u6765\uff0c\u5c1a\u672a\u660e\u770b\u89c1\u5979\u90a3\u79cd \u67d4\u5a9a\u5165\u9aa8\u7684\u59ff\u6001\uff0c\u4e0d\u7531\u6d51\u8eab\u4e0d\u5bd2\u800c\u51db\uff0c\u5fc3\u4e2d\u538c\u715e\u5979\u5230\u4e86\u6781\u70b9\uff0c\u6068\u4e0d\u5f97\u4e00\u5251\u7ed3\u679c\u4e86\u6731\u6b22\uff0c\u4f46\u662f \u5bf9\u65b9\u7ec8\u662f\u4e2a\u5973\u5b69\u5b50\uff0c\u4ed6\u4e0d\u80fd\u8fd9\u6837\u505a\u3002 \u3000\u3000\u5c1a\u672a\u660e\u610f\u8bc6\u5230\u7ea2\u5e15\u5c11\u5973\u8fd9\u4e8c\u6b21\u524d\u6765\uff0c\u5fc5\u8981\u7ea0\u7f20\u4ed6\u6295\u8eab\u5929\u9634\u6559\uff0c\u7a0d\u4e00\u5e94\u4ed8\u4e0d\u5584\uff0c\u5929\u9634\u6559\u4eba \u5c06\u4e0d\u4f1a\u5229\u4e8e\u4ed6\u3002 \u3000\u3000\u56e0\u4e4b\u4e0d\u80fd\u628a\u521a\u624d\u836f\u8ff7\u540e\u7684\u6001\u5ea6\u9aa4\u7136\u6539\u53d8\uff0c\u53cd\u800c\u4fc3\u4f7f\u5979\u8d77\u7591\uff0c\u4f46\u662f\u76ee\u524d\u901a\u77e5\u718a\u501c\u4e3a\u8981\uff0c\u5c1a \u672a\u660e\u539f\u662f\u78ca\u843d\u5149\u660e\u7684\u6c49\u5b50\uff0c\u66f4\u4e0d\u80af\u518d\u548c\u5979\u80e1\u7f20\uff0c\u60f3\u6765\u60f3\u53bb\uff0c\u53ea\u6709\u628a\u5979\u5236\u670d\uff0c\u4ee5\u514d\u59a8\u788d\u81ea\u5df1\u7684 \u884c\u52a8\u3002 \u3000\u3000\u70b9\u7a74\u624b\u6cd5\uff0c\u4ed6\u867d\u7136\u8ddf\u90a3\u756a\u50e7\u7ec3\u8fc7\uff0c\u5374\u5e76\u4e0d\u5341\u5206\u9ad8\u660e\uff0c\u6309\u7740\u6c14\u8840\u6d41\u884c\u7684\u65f6\u8fb0\uff0c\u5e94\u8be5\u70b9\u7740\u5979 \u7684\u6c14\u95e8\u5546\u66f2\u7a74\uff0c\u8f83\u4e3a\u548c\u5e73\uff0c\u4e5f\u4e0d\u81f3\u4e8e\u4f24\u5979\uff0c\u540c\u65f6\u4e0b\u624b\u65f6\u4e5f\u8f83\u4e3a\u4fbf\u5229\uff0c\u5c1a\u672a\u660e\u8fd9\u4e48\u4e00\u7b79\u601d\uff0c\u7ea2 \u5e15\u5c11\u5973\u5df2\u6d45\u7b11\u76c8\u76c8\u7acb\u4e8e\u706f\u4e0b\u3002 \u3000\u3000\u5c1a\u672a\u660e\u6545\u610f\u4e5f\u659c\u7740\u773c\uff0c\u7f13\u7f13\u8fce\u7740\u5979\u8d70\u8fd1\u3002 \u3000\u3000\u4ed6\u8eab\u4f53\u6545\u610f\u6447\u6446\u7740\uff0c\u800c\u7ea2\u5e15\u5c11\u5973\u4f9d\u7136\u6545\u6001\uff0c\u603b\u662f\u5411\u4e00\u8fb9\u95ea\u907f\u5c1a\u672a\u660e\u6781\u4e0d\u81ea\u7136\u7684\u7b11\u7740\uff0c \u9053\uff1a\u201c\u8fd9\u6709\u4ec0\u4e48\u96be\u4e8e\u51b3\u5b9a\uff0c\u53ea\u5f85\u544a\u8bc9\u540c\u4f34\u718a\u501c\u4e00\u4e0b\uff0c\u6211\u4eec\u603b\u4e0d\u80fd\u4e0d\u4e00\u81f4\u884c\u52a8\u5440\uff01\u518d\u8bf4\u7ecf\u8fc7\u6731 \u59d1\u5a18\u70ed\u5fc3\u542f\u793a\uff0c\u5c1a\u67d0\u5c82\u6562\u6267\u62d7\uff01\u201d\u4ed6\u53e3\u91cc\u5583\u5583\u7684\u7c7b\u4f3c\u68a6\u5453\uff0c\u800c\u90a3\u7ea2\u5e15\u5c11\u5973\u795e\u8272\u7a81\u7136\u4e00\u53d8\uff0c\u53d8 \u5f97\u7709\u98de\u8272\u821e\uff0c\u663e\u7136\u662f\u60ca\u559c\u5979\u81ea\u5df1\u5c06\u83b7\u5f97\u4e86\u82f1\u4fca\u7684\u6a80\u90ce\u3002 \u3000\u3000\u7ea2\u5e15\u5c11\u5973\u539f\u5148\u662f\u6b32\u64d2\u6545\u7eb5\uff0c\u4f7f\u5c1a\u672a\u660e\u5fc3\u75d2\u96be\u6414\uff0c\u5728\u836f\u6027\u50ac\u52a8\u4e4b\uff0c\u4fef\u9996\u5c31\u8303\uff0c\u8fd9\u65f6\u5c1a\u672a\u660e \u5df2\u7adf\u4f4e\u9996\u79f0\u81e3\uff0c\u62dc\u5012\u77f3\u69b4\u88d9\u4e0b\uff0c\u5979\u51cf\u5c11\u4e86\u8bb8\u591a\u987e\u5fcc\uff0c\u8981\u77e5\u5929\u9634\u6559\u4e0b\u7537\u5b50\u867d\u591a\uff0c\u5e74\u8c8c\u76f8\u5f53\u800c\u662f \u6709\u5927\u597d\u8eab\u624b\u7684\u90a3\u5c31\u5c11\u4e4b\u53c8\u5c11\u4e86\u3002 \u3000\u3000\u600e\u4e0d\u4f7f\u5979\u4e00\u9897\u82b3\u5fc3\uff0c\u5feb\u8981\u8df3\u51fa\u53e3\u8154\u4ee5\u5916\u4e86\u5462\u3002 \u3000\u3000\u56e0\u4e4b\u7ea2\u5e15\u5c11\u5973\u4e0d\u613f\u4e5f\u4e0d\u5fcd\u4f7f\u5c1a\u672a\u660e\u8fc7\u4e8e\u843d\u5bde\u5931\u671b\uff0c\u5f97\u4e0d\u7740\u4e00\u70b9\u513f\u5b89\u6170\uff0c\u5411\u672a\u660e\u8eab\u4f53\u6e10\u6e10 \u79fb\u8fd1\uff0c\u5979\u4e5f\u4e0d\u5fcd\u518d\u9003\u907f\u4e86\u3002 \u3000\u3000\u4e8b\u51fa\u610f\u5916\uff0c\u5c1a\u672a\u660e\u63a5\u8fd1\u4e86\u5979\u8170\u4fa7\uff0c\u5374\u4e0d\u662f\u63a5\u62b1\u5979\u7684\u8170\u80a2\uff0c\u800c\u662f\u91cd\u91cd\u7684\u70b9\u4e0b\uff0c\u7ea2\u5e15\u5c11\u5973\u55ef \u54fc\u4e86\u4e00\u58f0\uff0c\u7a74\u9053\u7acb\u523b\u95ed\u8fc7\u53bb\uff0c\u5979\u60f3\u53eb\u5524\u4e5f\u53eb\u5524\u4e0d\u51fa\u6765\u4e86\u3002 \u3000\u3000\u7ea2\u5e15\u5c11\u5973\u4e0d\u77e5\u5c1a\u672a\u660e\u662f\u4f55\u5c45\u5fc3\uff0c\u7acb\u5373\u7f9e\u6ee1\u68a8\u7a9d\uff0c\u4ee5\u4e3a\u4ed6\u5fc5\u8981\u5bf9\u5979\u65bd\u884c\u4e00\u79cd\u72c2\u98ce\u66b4\u96e8\u822c\u7684 \u6467\u6b8b\uff0c\u5979\u5fc3\u60f3\uff1a\u201c\u6211\u65e9\u5df2\u5c5e\u4e8e\u4f60\u4e86\uff0c\u4f55\u5fc5\u7528\u8fd9\u79cd\u624b\u6bb5\u5bf9\u4ed8\u6211\u5462\uff1f\u96be\u9053\u4f60\u8fd8\u4e0d\u4e86\u89e3\u6211\u5bf9\u4f60\u7684\u60c5 \u610f\u3002\u201d \u3000\u3000\u7ea2\u5e15\u5c11\u5973\u6e34\u671b\u7740\u5c1a\u672a\u660e\uff0c\u7ed9\u5979\u4e00\u79cd\u6e29\u5b58\uff0c\u53ea\u4e0d\u8981\u592a\u8f7b\u72c2\u4e86\uff0c\u53ea\u89c1\u5979\u79cb\u6c34\u76c8\u76c8\uff0c\u4e00\u76f4\u671b\u7740 \u5c1a\u672a\u660e\uff0c\u800c\u4ed6\u7adf\u4ee5\u6781\u8fc5\u5feb\u7684\u8eab\u6cd5\uff0c\u629b\u5f03\u4e86\u5979\uff0c\u6d88\u5931\u5728\u9ed1\u6697\u4e2d\uff0c\u7ea2\u5e15\u5c11\u5973\u8fd9\u624d\u9e33\u68a6\u6210\u7a7a\uff0c\u9192\u609f \u8fd9\u5c11\u5e74\u5fc5\u5df2\u4e86\u89e3\u4e86\u4ed6\u4eec\u7684\u9634\u8c0b\u3002 \u3000\u3000\u5c1a\u672a\u660e\u662f\u8fd9\u6837\u5728\u6e29\u67d4\u4e61\u4e2d\uff0c\u6253\u4e86\u51e0\u4e2a\u6eda\u513f\uff0c\u6765\u627e\u718a\u501c\u7684\u3002 \u3000\u3000\u4ed6\u88ab\u718a\u501c\u8fce\u5165\u5ba4\u5185\uff0c\u81ea\u7136\u8981\u8d61\u4ef0\u4e00\u4e0b\u718a\u501c\u7684\u817b\u53cb\u4e86\u3002 \u3000\u3000\u5c1a\u672a\u660e\u671b\u89c1\u590f\u82b8\u7684\u5bb9\u8c8c\u8f6e\u5ed3\uff0c\u5fc3\u4e2d\u5440\u7136\u4e00\u58f0\uff0c\u600e\u4e48\u8fd9\u6837\u7684\u719f\u6089\uff0c\u4ed6\u60f3\u4e0d\u8d77\u6765\u4f55\u65f6\u89c1\u8fc7 \u5979\uff0c\u800c\u4e14\u6700\u5947\u602a\u7684\u662f\u773c\u524d\u8fd9\u4f4d\u7389\u4eba\uff0c\u7adf\u548c\u81ea\u5df1\u5341\u5206\u76f8\u50cf\uff0c\u6240\u5dee\u7684\u53ea\u662f\u7537\u5973\u4e4b\u522b\uff0c\u7684\u786e\u5bb9\u8c8c\u662f \u592a\u76f8\u4f3c\u4e86\u3002 \u3000\u3000\u590f\u82b8\u4e00\u773c\u89c1\u5c1a\u672a\u660e\uff0c\u4e5f\u662f\u4e00\u79cd\u540c\u6837\u7684\u79bb\u5947\u611f\u89c9\uff0c\u4f7f\u5979\u4e5f\u60ca\u8bb6\u5f97\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u4e24\u4eba\u90fd\u52aa\u529b \u5728\u56de\u5fc6\u7740\u8fc7\u53bb\u3002 \u3000\u3000\u53ef\u60dc\u51e0\u65f6\u7684\u5370\u8c61\u4e0d\u591f\u6e05\u6670\uff0c\u4f46\u662f\u4ed6\u4eec\u4fe9\u6781\u81ea\u7136\u7684\u5404\u6cb9\u7136\u800c\u751f\u4e00\u79cd\u4eb2\u5207\u4e4b\u611f\uff0c\u662f\u4e3a\u4e86\u5bb9\u8c8c \u592a\u76f8\u50cf\u4e86\u4e48\uff1f\u8fd8\u662f\u4e3a\u4e86\u522b\u7684\uff0c\u4ed6\u4fe9\u81ea\u7136\u7684\u611f\u5e94\u662f\u65e0\u6cd5\u7406\u89e3\u7684\u3002 \u3000\u3000\u718a\u501c\u6b63\u4e3a\u5c1a\u672a\u660e\u548c\u590f\u82b8\u4e92\u76f8\u4ecb\u7ecd\uff0c\u800c\u4ed6\u4fe9\u5374\u53cd\u6014\u4f4f\u4e86\u3002\u5c1a\u672a\u660e\u79bb\u5f00\u738b\u5e9c\u65f6\uff0c\u5e74\u5df2\u516b\u5c81 \u4e86\uff0c\u4e0d\u80fd\u8bf4\u4e00\u70b9\u8bb0\u4e0d\u8d77\u6765\uff0c\u6240\u4ee5\u4ed6\u81ea\u8be9\u662f\u9f99\u51e4\u9601\u751f\u957f\u5927\u7684\u4eba\uff0c\u4e0d\u8fc7\u4e0d\u660e\u8eab\u4e16\uff0c\u51e0\u65f6\u6709\u4e2a\u53ef\u7231 \u7684\u59b9\u59b9\uff0c\u4e00\u540c\u88ab\u4eba\u643a\u51fa\u738b\u5e9c\uff0c\u591a\u5c11\u5728\u4ed6\u5fc3\u91cc\u6709\u70b9\u5f71\u5b50\uff0c\u4ee5\u540e\u5462\uff0c\u4ed6\u5c31\u6ca6\u843d\u4e86\uff0c\u4ed6\u4e0d\u6562\u60f3\u8c61\u590f \u82b8\u5c31\u662f\u4ed6\u7684\u59b9\u59b9\u3002 \u3000\u3000\u5c1a\u672a\u660e\u5c1a\u65e6\u8bb0\u5fc6\u4e0d\u6e05\uff0c\u590f\u82b8\u90a3\u65f6\u66f4\u5c0f\u66f4\u522b\u8bf4\u4e86\uff0c\u4f46\u662f\u4e24\u4eba\u5374\u59cb\u7ec8\u90fd\u89c9\u5f97\u5bf9\u65b9\u975e\u5e38\u53ef\u4eb2\u3002 \u718a\u501c\u95ee\u5c1a\u672a\u660e\uff1a\u201c\u5c1a\u5927\u54e5\u534a\u591c\u627e\u6211\uff0c\u6709\u8981\u4e8b\u4e48\uff1f\u201d \u3000\u3000\u8fd9\u4e00\u95ee\u624d\u628a\u5c1a\u672a\u660e\u4ece\u521a\u624d\u90a3\u4e2a\u573a\u9762\u4e2d\u5524\u9192\u6765\uff0c\u5c1a\u672a\u660e\u5306\u5306\u5730\u53d9\u8bf4\u4e86\u4e0a\u8ff0\u7684\u7ecf\u8fc7\uff0c\u718a\u501c\u4e3a \u4e4b\u52c3\u7136\u53d8\u8272\u3002 \u3000\u3000\u590f\u82b8\u5374\u7b11\u9053\uff1a\u201c\u522b\u542c\u90a3\u59d3\u67f3\u7684\u59d1\u5a18\u80e1\u626f\uff0c\u5929\u9634\u6559\u4eba\u5bf9\u6211\u662f\u633a\u597d\u7684\uff0c\u96be\u9053\u4ed6\u4eec\u628a\u501c\u54e5\u548c\u5c1a \u5927\u54e5\u9a97\u6765\uff0c\u8981\u6697\u5bb3\u4f60\u4fe9\uff1f\u201d \u3000\u3000\u718a\u501c\u77e5\u9053\u4e8b\u6001\u4e25\u91cd\uff0c\u73b0\u5728\u4f55\u5fc5\u8d39\u8bb8\u591a\u8bdd\u5411\u590f\u82b8\u89e3\u91ca\uff0c\u4ed6\u4ee5\u7948\u6c42\u7684\u53e3\u543b\uff0c\u5411\u590f\u82b8\u8bf4\uff1a \u201c\u82b8\uff01\u8ba9\u6211\u4eec\u5148\u79bb\u5f00\u81ea\u51e4\u603b\u5802\uff0c\u6709\u8bdd\u6162\u6162\u518d\u8bf4\u3002\u201d \u3000\u3000\u590f\u82b8\u51b7\u7b11\u9053\uff1a\u201c\u770b\u4f60\u4f55\u5fc5\u6015\u6210\u8fd9\u4e2a\u6837\u5b50\uff0c\u6211\u4eec\u8bf4\u8d70\u5c31\u8d70\uff0c\u8c01\u80fd\u62e6\u5f97\u4f4f\u6211\u4eec\uff01\u201d \u3000\u3000\u590f\u82b8\u5306\u5306\u6253\u53e0\u8d77\u6765\uff0c\u628a\u94f6\u97ad\u7ef0\u5728\u624b\u91cc\uff0c\u718a\u501c\u548c\u5c1a\u672a\u660e\u66f4\u4e00\u65e0\u957f\u7269\uff0c\u5404\u81ea\u7126\u6025\u7684\uff0c\u7b49\u5f85\u5979 \u6536\u62fe\u597d\uff01\u7acb\u5373\u91c7\u53d6\u884c\u52a8\u3002 \u3000\u3000\u590f\u82b8\u671b\u7740\u718a\u501c\u80cc\u4e0a\u7684\u5b9d\u5251\uff0c\u60f3\u8d77\u4e86\u9047\u89c1\u4e86\u6c5f\u5e72\u4e8c\u8001\u7684\u4e8b\uff0c\u5979\u5411\u718a\u501c\u8eab\u8fb9\u51d1\u8fd1\u4e9b\uff0c\u65e5\u6ce8\u4ed6 \u80cc\u4e0a\u5b9d\u5251\u95ee\u4ed6\uff1a\u201c\u501c\u54e5\uff0c\u8fd9\u662f\u4f60\u7684\u8d2f\u65e5\u5251\uff0c\u8fd8\u662f\u501a\u5929\u5251\uff1f\u201d \u3000\u3000\u718a\u501c\u4e0d\u80dc\u8be7\u5f02\uff0c\u590f\u82b8\u600e\u4f1a\u6653\u5f97\u8fd9\u4e24\u53e3\u5251\u7684\u540d\u5b57\uff1f \u3000\u3000\u590f\u82b8\u628a\u6c5f\u5e72\u4e8c\u8001\u7684\u8bdd\uff0c\u8bf4\u4e86\u4e00\u904d\uff0c\u5979\u60f3\u8d77\u90a3\u4e24\u4e2a\u8bed\u65e0\u4f26\u6b21\u7684\u8001\u5934\u5b50\uff0c\u89c9\u5f97\u6ed1\u7a3d\u53ef\u7b11\uff0c\u800c \u718a\u501c\u5374\u5927\u5927\u5403\u60ca\u3002 \u3000\u3000\u5c1a\u672a\u660e\u4e5f\u66fe\u89c1\u8fc7\u90a3\u4e24\u4e2a\u8001\u5934\uff0c\u4e8e\u662f\u4ed6\u4eec\u4e3a\u6b64\u53c8\u803d\u5ef6\u4e86\uff0c\u76cf\u8336\u65f6\u3002\u6bd4\u53ca\u4ed6\u4e09\u4eba\u51c6\u5907\u51fa\u5ba4\uff0c \u9662\u4e2d\u7a81\u7136\u706f\u706b\u9f50\u660e\u3002 \u3000\u3000\u9662\u4e2d\u4e5d\u5929\u4ed9\u5b50\u7f2a\u5929\u96ef\u534a\u8001\u5f90\u5a18\u7684\u58f0\u53e3\uff0c\u53d1\u51fa\u4e00\u9635\u72de\u7b11\u4e4b\u58f0\uff0c\u7b11\u58f0\u523a\u8033\u96be\u542c\uff0c\u63a5\u7740\u542c\u89c1\u5979 \u6717\u58f0\u9053\uff1a\u201c\u718a\u5c0f\u5f1f\u5f1f\u548c\u590f\u59b9\u59b9\u90fd\u8981\u8d70\u5417\uff1f\u6df1\u66f4\u534a\u591c\u5306\u5306\u6765\u53bb\uff0c\u4f55\u4e0d\u660e\u65e5\u6210\u884c\u5462\uff0c\u96be\u9053\u662f\u602a\u8001 \u8eab\u4e0d\u66fe\u62db\u5f85\u5ba2\u4eba\u4e48\uff1f\u201d \u3000\u3000\u4e09\u4eba\u7acb\u5373\u4eae\u51fa\u5b9d\u5251\u957f\u97ad\uff0c\u718a\u501c\u4e00\u811a\u8e22\u5f00\u4e86\u95e8\uff0c\u5148\u540e\u9c7c\u8d2f\u7eb5\u51fa\u5ba4\u5916\uff0c\u53ea\u89c1\u9662\u4e2d\u4e00\u7c07\u767d\u8863\u5c11 \u5973\uff0c\u6216\u6267\u706b\u628a\u6216\u624b\u63d0\u5bab\u706f\uff0c\u628a\u9662\u4e2d\u7167\u8000\u5f97\u5982\u540c\u81ea\u663c\u3002 \u3000\u3000\u5947\u602a\u7684\u8fd9\u4e9b\u5973\u5b69\u5b50\uff0c\u7adf\u6ca1\u6709\u4e00\u4eba\u624b\u4e2d\u6301\u7740\u5175\u5203\u3002 \u3000\u3000\u4e5d\u5929\u4ed9\u5b50\u8fd8\u662f\u7b11\u5bb9\u53ef\u63ac\uff0c\u7531\u4e03\u516b\u4e2a\u767d\u8863\u5c11\u5973\u7c07\u62e5\u7740\uff0c\u7ea2\u5e15\u5c11\u5973\u4e5f\u5728\u5176\u4e2d\uff0c\u4ee5\u6781\u6124\u6012\u7684\u76ee \u5149\uff0c\u8fdc\u8fdc\u77aa\u89c6\u7740\u5c1a\u672a\u660e\u3002 \u3000\u3000\u800c\u4e5d\u5929\u4ed9\u5b50\u5219\u5b9b\u7136\u662f\u63a5\u5f85\u4ed6\u4eec\u65f6\u7684\u795e\u60c5\uff0c\u53ea\u7b11\u58f0\u91cc\u4f3c\u542b\u6709\u4e00\u80a1\u9707\u4eba\u5fc3\u5f26\u7684\u610f\u5473\uff0c\u5979\u671b\u7740 \u4ed6\u4eec\u9053\uff1a\u201c\u600e\u4e48\uff0c\u82b8\u59b9\u59b9\u4e5f\u8981\u8d70\u4e86\uff01\u4f60\u7236\u4eb2\u6765\u65f6\uff0c\u6559\u8001\u8eab\u62ff\u4ec0\u4e48\u8bdd\u4ea4\u4ee3\u5462\uff1f\u718a\u5c0f\u5f1f\u5f1f\u4ed4\u7ec6\u8003 \u8651\u8fc7\u6ca1\u6709\uff1f\u771f\u4e2a\u8001\u8eab\u62db\u5f85\u4e4b\u8c0a\uff0c\u4e0d\u503c\u4e00\u987e\u4e48\uff1f\u8fd8\u6709\u5c1a\u5c0f\u5f1f\u5f1f\uff0c\u7adf\u5bf9\u4e8e\u6731\u59b9\u59b9\u4e0d\u80fd\u8c05\u89e3\uff0c\u8fd9\u662f \u591a\u4e48\u9057\u61be\u7684\u4e8b\u5440\uff01\u201d \u3000\u3000\u718a\u501c\u9053\uff1a\u201c\u590f\u59d1\u5a18\u610f\u6b32\u56de\u5173\u5916\u7701\u4eb2\uff0c\u4e0d\u4fbf\u4e45\u6270\u8d35\u5802\uff0c\u718a\u67d0\u548c\u5c1a\u5f53\u5bb6\u7684\u4e5f\u8981\u53bb\u5ce8\u5d4b\u8bbf\u53cb\uff0c \u81f3\u4e8e\u4eca\u665a\u6216\u660e\u6668\u51fa\u53d1\uff0c\u90a3\u662f\u6ca1\u6709\u4ec0\u4e48\u5dee\u5f02\u7684\uff0c\u7f2a\u5802\u4e3b\u76db\u60c5\u76f8\u9080\uff0c\u6211\u4eec\u8877\u5fc3\u6c38\u8bb0\u7740\u8fd9\u4e00\u4efd\u513f\u60c5 \u8c0a\u7684\u3002\u201d \u3000\u3000\u4e5d\u5929\u4ed9\u5b50\u7b11\u5f97\u683c\u5916\u52a8\u542c\uff0c\u5979\u4f9d\u7136\u4e0d\u9732\u4e1d\u6beb\u607c\u6012\u4e4b\u8272\uff0c\u9053\uff1a\u201c\u65e2\u662f\u4e24\u4f4d\u5c0f\u5f1f\u5f1f\u90fd\u7ecf\u8fc7\u4e00\u756a \u4ed4\u7ec6\u8003\u8651\uff0c\u90a3\u8001\u8eab\u7684\u8bdd\u7b49\u4e8e\u767d\u8d39\u4e86\uff0c\u4e09\u4f4d\u51b3\u5fc3\u5c31\u8d70\uff0c\u8001\u8eab\u4eb2\u81ea\u9001\u4f60\u4eec\u8d70\u8def\uff01\u201d \u3000\u3000\u5979\u6700\u540e\u8fd9\u4e24\u4e2a\u5b57\uff0c\u4f3c\u523a\u8033\u5f97\u5f88\uff0c\u4f46\u662f\u5979\u53c8\u5f88\u5feb\u5730\u6446\u6446\u624b\u5429\u5490\u4f17\u5c11\u5973\uff1a\u201c\u5feb\u4e9b\u5f00\u95e8\u9001 \u5ba2\uff01\u201d \u3000\u3000\u7acb\u5373\u6709\u5341\u4f59\u5c11\u5973\uff0c\u5343\u6267\u706b\u628a\u9c7c\u8d2f\u800c\u51fa\u3002 \u3000\u3000\u4e5d\u5929\u4ed9\u5b50\u53c8\u4f38\u4e86\u5de6\u624b\uff0c\u8bf4\uff1a\u201c\u90a3\u4e48\u4e09\u4f4d\u8bf7\u5427\uff01\u201d \u3000\u3000\u4ed6\u4e09\u4eba\u4e5f\u5c31\u4e0d\u518d\u5ba2\u5957\uff0c\u5411\u5927\u95e8\u5916\u8d70\u53bb\uff0c\u5c1a\u672a\u660e\u8fd8\u6050\u5929\u9634\u6559\u4eba\u57cb\u4f0f\u7740\u4eba\u6697\u7b97\u4ed6\u4eec\uff0c\u4f46\u662f\u5404 \u5904\u5ead\u9662\u4ecd\u662f\u9759\u6084\u6084\u7684\uff0c\u4e24\u5bf9\u513f\u63d0\u706f\u5c11\u5973\uff0c\u5728\u524d\u5f15\u5bfc\uff0c\u5e73\u5b89\u65e0\u4e8b\u8d70\u51fa\u516b\u5b57\u5927\u95e8\u4e4b\u5916\u3002\u4f46\u662f\u5374\u4e0d \u89c1\u4ed6\u4fe9\u6765\u65f6\u7684\u9a6c\u5339\u3002 \u3000\u3000\u7167\u58c1\u5899\u5916\u706b\u628a\u9ad8\u5f20\uff0c\u4f3c\u6709\u5f88\u591a\u7684\u4eba\u9ad8\u4e3e\u7740\u706b\u628a\u3002 \u3000\u3000\u5c1a\u672a\u660e\u8be7\u5f02\u8bf4\uff1a\u201c\u600e\u4e0d\u89c1\u6211\u4eec\u7684\u9a6c\u5339\uff1f\u201d \u3000\u3000\u4f46\u662f\u4e5d\u5929\u4ed9\u5b50\u53ea\u9001\u5230\u95e8\u8fb9\uff0c\u54af\u54af\u72c2\u7b11\u4e0d\u5df2\u8bf4\uff1a\u201c\u90a3\u4e48\u5c31\u8bf7\u8d70\u4f60\u4eec\u7684\u8def\uff0c\u8fd9\u662f\u6700\u540e\u7ed9\u4f60\u4eec \u9009\u62e9\u7684\u4e00\u4e2a\u673a\u4f1a\uff01\u672c\u6559\u5bf9\u4e8e\u5404\u65b9\u540c\u9053\uff0c\u4ece\u6765\u4e0d\u5fcd\u4e0d\u6559\u800c\u8bdb\uff0c\u4e09\u4f4d\u5feb\u5feb\u56de\u5934\u731b\u9192\uff01\u201d \u3000\u3000\u4e5d\u5929\u4ed9\u5b50\u53c8\u4e00\u6325\u624b\uff0c\u90a3\u7ea2\u5e15\u5c11\u5973\u5df2\u7ecf\u7ef0\u8d77\u4e00\u9762\u5c0f\u91d1\u9523\uff0c\u5f53\u5f53\u5f53\u7684\u6572\u4e86\u4e09\u58f0\uff0c\u7830\u7684\u4e00\u58f0\uff0c \u5408\u4f4f\u4e24\u6247\u5927\u95e8\u3002 \u3000\u3000\u590f\u82b8\u7b11\u8bf4\uff1a\u201c\u9001\u5ba2\u5c31\u9001\u5ba2\uff0c\u4e3a\u4ec0\u4e48\u6572\u9523\u5462\uff1f\u201d \u3000\u3000\u8c03\u5c1a\u672a\u660e\u4e5f\u53d1\u89c9\u60c5\u5f62\u4e0d\u5999\uff0c\u4ed6\u8bf4\u4e00\u58f0\uff1a\u201c\u5feb\u8d70\uff01\u201d\u4eba\u5df2\u5148\u81ea\u7167\u58c1\u5899\u5de6\u4fa7\u7eb5\u51fa\uff0c\u718a\u501c\u7d27\u7d27 \u4f34\u7740\u590f\u82b8\uff0c\u81ea\u53f3\u4fa7\u7eb5\u53bb\u3002 \u3000\u3000\u4e09\u4eba\u90fd\u88ab\u773c\u524d\u8fd9\u7247\u5e7f\u573a\u4e0a\u7684\u60c5\u5f62\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u624b\u6267\u706b\u628a\u7684\u9ed1\u8863\u5927\u6c49\uff0c\u5bc6\u5bc6\u5e03\u4e86\u4e2a\u56de\u5708\u5f62\u7684\u9635\u52bf\uff0c\u4e2d\u95f4\u7684\u4eba\u5b9b\u5982\u633a\u7acb\u7740\u5341\u4f59\u5c0a\u77f3\u50cf\uff0c\u5175 \u5668\u5728\u706b\u5149\u4e2d\u95ea\u95ea\u751f\u8f89\u3002 \u3000\u3000\u4ed6\u4e09\u4eba\u5f88\u5feb\u5730\u626b\u89c6\u4e00\u531d\uff0c\u81ea\u7136\u5929\u9634\u6559\u7684\u9ad8\u624b\uff0c\u718a\u501c\u8ba4\u8bc6\u7684\u8f83\u4e3a\u591a\u4e9b\uff0c\u6700\u4e2d\u95f4\u4e00\u4f4d\u9886\u8896\u4eba \u7269\uff0c\u767d\u53d1\u767d\u7709\uff0c\u5a01\u6b66\u65e0\u4f26\uff0c\u8eab\u7a7f\u674f\u9ec4\u8272\u957f\u886b\u7684\u4e43\u662f\u94c1\u9762\u9ec4\u886b\u5ba2\u4ec7\u4e0d\u53ef\u3002\u53f8\u793c\u53cc\u7ae5\u81ea\u666f\u7965\u53f6\u6e05 \u6e05\u7d27\u6328\u7740\u4ed6\u6301\u5251\u800c\u7acb\u3002 \u3000\u3000\u53e6\u6709\u4e00\u4f4d\u8eab\u6750\u9b41\u68a7\u7684\u4eba\uff0c\u9876\u4e0a\u767d\u53d1\u82cd\u82cd\uff0c\u9762\u4e0a\u5374\u906e\u4e86\u4e00\u5f20\u9875\u5177\uff0c\u671b\u4e0d\u6e05\u4ed6\u7684\u5e90\u5c71\u771f\u9762 \u76ee\u3002 \u3000\u3000\u4f7f\u718a\u501c\u60ca\u8bb6\u7684\u662f\u56db\u5e74\u524d\uff01\u5c71\u4e1c\u9053\u4e0a\u6240\u9047\u7684\u62b1\u728a\u5c97\u74e2\u628a\u5b50\u6258\u5854\u5929\u738b\u53f6\u5764\u7136\uff0c\u72ec\u884c\u76d7\u65e5\u6708\u5934 \u9640\uff0c\u7626\u524a\u800c\u7cbe\u608d\u7684\u52b3\u5c71\u53cc\u9e64\u90d1\u5251\u5e73\uff0c\u90d1\u5251\u9752\u5144\u5f1f\u4e5f\u5728\u573a\uff0c\u800c\u4e14\u90fd\u7a7f\u4e86\u4e00\u8272\u9ed1\u8863\uff0c\u663e\u7136\u90fd\u5df2\u6295 \u8eab\u5929\u9634\u6559\u4e0b\u4e86\u3002 \u3000\u3000\u6b64\u5916\u5982\u5355\u638c\u65ad\u9b42\u5355\u98de\uff0c\u6d1e\u5ead\u53cc\u868a\uff0c\u8fd9\u90fd\u662f\u590f\u82b8\u6240\u9047\u89c1\u8fc7\u7684\u3002\u603b\u4e4b\uff0c\u6ca1\u4e00\u4e2a\u4e0d\u662f\u6b66\u6797\u4e2d\u4e45 \u5df2\u6210\u540d\u7684\u597d\u624b\u3002 \u3000\u3000\u9ec4\u886b\u5ba2\u4ec7\u4e0d\u53ef\u53d1\u58f0\u5982\u540c\u6d2a\u949f\u9707\u8033\uff0c\u8138\u4e0a\u7f69\u7740\u51db\u51db\u8083\u6740\u4e4b\u6c14\uff0c\u5389\u58f0\u53f1\u9053\uff1a\u201c\u718a\u501c\u3001\u5c1a\u672a \u660e\uff0c\u4e24\u4e2a\u5c0f\u5b50\u649e\u4eba\u672c\u6559\u767d\u51e4\u603b\u5802\uff0c\u975e\u7acb\u65f6\u5ba3\u8a93\u5165\u6559\uff0c\u4fbf\u987b\u7acb\u6bd9\u5f53\u573a\uff0c\u4e0d\u80fd\u653e\u4e00\u4e2a\u6d3b\u53e3\u8d70\u6389\uff01 \u4ece\u901f\u81ea\u5df1\u659f\u914c\u5229\u5bb3\uff0c\u751f\u6b7b\u4e24\u6761\u8def\u81ea\u884c\u9009\u62e9\u5427\uff01\u201d \u3000\u3000\u4ec7\u4e0d\u53ef\u8bf4\u5b8c\uff0c\u51dd\u5982\u5c71\u5cb3\uff0c\u9759\u5019\u7740\u8fd9\u4e09\u4e2a\u5c11\u5e74\u7b54\u590d\uff0c\u5929\u9634\u6559\u8fd9\u5341\u4f59\u4f4d\u9ad8\u624b\uff0c\u90fd\u9762\u4e0a\u4e25\u8083\u5f97 \u4e0d\u9732\u4e00\u4e1d\u7eb9\u7f1d\u3002 \u3000\u3000\u718a\u501c\u5411\u5c1a\u672a\u660e\u4e22\u4e86\u4e2a\u773c\u8272\u8bf4\uff1a\u201c\u4e0d\u5fc5\u8d39\u8bdd\uff0c\u51b2\u51fa\u53bb\u5c31\u662f\u4e86\uff01\u6211\u53ef\u8981\u987e\u82b8\u59b9\uff0c\u5927\u54e5\u4e0d\u53ef\u8f7b \u654c\u3002\u201d \u3000\u3000\u8bdd\u97f3\u672a\u6b47\uff0c\u9ec4\u886b\u5ba2\u72de\u7b11\u4e00\u58f0\uff0c\u5927\u8896\u4e00\u6325\uff0c\u65e9\u6709\u9ed1\u8863\u6469\u52d2\u767d\u666f\u7965\uff0c\u767d\u8863\u9f99\u5973\u53f6\u6e05\u6e05\uff0c\u5355\u638c \u65ad\u9b42\u5355\u98de\u7b49\u4e94\u4eba\u8eab\u5f62\u98d8\u98de\u5728\u90a3\u8fb9\u5c06\u5c1a\u672a\u660e\u56e2\u56e2\u56f4\u4f4f\u3002 \u3000\u3000\u767d\u666f\u7965\u548c\u53f6\u6e05\u6e05\uff0c\u56db\u81c2\u7eb7\u6325\uff0c\u8f7b\u529f\u5feb\u901f\uff0c\u800c\u62db\u6cd5\u5341\u5206\u8001\u8fa3\uff0c\u5355\u662f\u8fd9\u4e24\u4e2a\u5c11\u5e74\uff0c\u5c1a\u672a\u660e\u4e5f \u4e0d\u5bb9\u6613\u5360\u4e0a\u4f18\u52bf\uff0c\u4f55\u51b5\u5355\u98de\u7b49\u5176\u4f59\u4e09\u4f4d\uff0c\u4e5f\u90fd\u975e\u5f31\u8005\uff0c\u5c1a\u672a\u660e\u60f3\u4ece\u8fd9\u4e94\u4f4d\u9ad8\u624b\u5408\u56f4\u4e4b\u4e2d\u8131\u8eab \u800c\u51fa\u3002\u51e0\u4e4e\u662f\u4e0d\u53ef\u80fd\u7684\u4e8b\u3002 \u3000\u3000\u5929\u9634\u6559\u8fd9\u79cd\u7fa4\u6253\u7fa4\u6bb4\u7684\u624b\u6cd5\uff0c\u7684\u786e\u6beb\u65e0\u6b66\u6797\u4fe1\u4e49\u53ef\u8a00\uff0c\u4f46\u662f\u4ed6\u4eec\u51b3\u5b9a\u4e86\u91c7\u53d6\u8fd9\u79cd\u6bd2\u8fa3\u624b \u6bb5\uff0c\u5408\u4e4e\u4ed6\u4eec\u5404\u4e2a\u6d88\u706d\u7684\u9634\u8c0b\uff0c\u5929\u9634\u6559\u4eba\u662f\u4e0d\u987e\u4e00\u5207\u7684\u3002 \u3000\u3000\u718a\u501c\u548c\u590f\u82b8\uff0c\u4e5f\u540c\u65f6\u88ab\u4e38\u4f4d\u5929\u9634\u6559\u6b66\u529f\u5353\u7edd\u7684\u4eba\uff0c\u56db\u9762\u56f4\u56f0\u4f4f\uff0c\u52b3\u5c71\u53cc\u9e64\u7684\u53cc\u5251\uff0c\u65e5\u6708 \u5934\u9640\u7684\u4e00\u53cc\u96ea\u82b1\u6448\u94c1\u6212\u5200\uff0c\u4e00\u9f50\u6d8c\u5411\u590f\u82b8\u8eab\u7554\uff0c\u9ec4\u886b\u4ec7\u4e0d\u53ef\u548c\u90a3\u9762\u7f69\u9762\u5177\u7684\uff0c\u5374\u5404\u4ee5\u4e00\u53cc\u8089 \u638c\uff0c\u5411\u718a\u501c\u8fdb\u62db\u3002 \u3000\u3000\u5176\u4f59\u7684\u51e0\u4f4d\uff0c\u90fd\u5728\u7565\u8fdc\u5904\uff0c\u821e\u8d77\u5404\u79cd\u4e0d\u540c\u7684\u5175\u5203\uff0c\u51b7\u4e0d\u9632\u88ad\u51fb\u4ed6\u4eec\u7684\u80cc\u90e8\u548c\u4fa7\u9762\uff0c\u603b\u4e4b \u4ed6\u4eec\u914d\u5408\u5f97\u975e\u5e38\u5de7\u5999\uff0c\u867d\u6ca1\u6709\u56fa\u5b9a\u7684\u9635\u6cd5\uff0c\u5374\u5f7c\u6b64\u547c\u5e94\uff0c\u4f7f\u718a\u501c\u548c\u590f\u82b8\u56db\u9762\u53d7\u654c\uff0c\u5f7c\u6b64\u4e0d\u80fd \u76f8\u987e\u3002 \u3000\u3000\u56e0\u4e3a\u9ec4\u886b\u5ba2\u4ec7\u4e0d\u53ef\uff0c\u53d1\u638c\u5341\u5206\u7f13\u6162\uff0c\u4f46\u62db\u6cd5\u8be1\u5f02\u65e0\u4f26\uff0c\u800c\u4e14\u624b\u4e0a\u5e26\u51fa\u547c\u5578\u7684\u4e1d\u4e1d\u98ce\u58f0\uff0c \u53ef\u4ee5\u8868\u793a\u51fa\u4ed6\u5185\u529b\u5341\u5206\u96c4\u539a\uff0c\u4ec7\u4e0d\u53ef\u7528\u7684\u662f\u5929\u9634\u6559\u4e94\u9634\u5bd2\u9aa8\u638c\u6cd5\uff0c\u4e00\u8fde\u4e09\u62db\u201c\u626d\u8f6c\u9634\u9633\u201d\uff0c \u201c\u8ffd\u9b54\u7d22\u547d\u201d\uff0c\u201c\u9b42\u65ad\u9634\u5c71\u201d\u3002 \u3000\u3000\u4ec7\u4e0d\u53ef\u8fd9\u79cd\u5947\u5999\u638c\u6cd5\uff0c\u662f\u5929\u9634\u6559\u82cd\u865a\u4e0a\u4eba\u72ec\u64c5\u4e4b\u6280\uff0c\u8fd1\u4e9b\u5e74\u6765\u6b66\u6797\u4e2d\u4eba\u4e45\u5df2\u4e0d\u89d1\u5176\u5999\uff0c \u800c\u4e14\u51fa\u624b\u5982\u51e4\uff0c\u95ea\u6643\u51fa\u5341\u4f59\u53cc\u624b\u638c\uff0c\u4f7f\u718a\u501c\u4e3a\u4e4b\u773c\u82b1\u7efc\u4e71\uff0c\u539f\u6765\u5929\u9634\u6559\u8fd9\u5957\u7edd\u6280\uff0c\u5728\u6b66\u5f53\u5c71 \u4ea4\u624b\u65f6\uff0c\u8fd8\u672a\u8f7b\u6613\u9732\u8fc7\u5462\u3002 \u3000\u3000\u53ef\u662f\u718a\u501c\u7ecf\u8fc7\u6bd2\u5fc3\u795e\u9b54\u7528\u6b64\u79cd\u609f\u62db\u903c\u4ed6\u4ea4\u624b\uff0c\u4e5f\u540c\u65f6\u6307\u793a\u4e86\u4ed6\u5e94\u64cd\u4ec0\u4e48\u6b65\u9aa4\u7834\u8fd9\u4e9b\u62db \u5f0f\uff0c\u4ed6\u8fd9\u65f6\u66f4\u52a0\u604d\u7136\u5927\u609f\uff1b\u6bd2\u5fc3\u795e\u9b54\u6559\u7ed9\u4ed6\u7684\u5341\u6570\u5f0f\u5947\u602a\u7684\u5251\u62db\uff0c\u53ef\u4ee5\u540c\u6837\u7528\u5728\u4e8e\u638c\u4e0a\uff0c\u4e5f \u6b63\u662f\u5929\u9634\u6559\u4e94\u9634\u5bd2\u9aa8\u638c\u6cd5\u7684\u514b\u661f\u3002 \u3000\u3000\u56e0\u4e4b\u718a\u501c\uff0c\u6bcf\u4e00\u638c\u8fce\u7740\u62cd\u51fa\uff0c\u6070\u597d\u80fd\u6293\u4f4f\u4e86\u4ec7\u4e0d\u53ef\u7684\u7a7a\u9699\uff0c\u653b\u5176\u5fc5\u6551\uff0c\u4e8e\u662f\u4ec7\u4e0d\u53ef\u8fd9\u79cd \u7edd\u6280\uff0c\u65e0\u5f62\u4e2d\u88ab\u4ed6\u6de1\u5199\u8f7b\u63cf\u5730\u5316\u89e3\u4e86\uff0c\u800c\u4e14\u8fd8\u51e0\u4e4e\u5403\u4e86\u4e8f\uff0c\u8fd9\u4f7f\u94c1\u9762\u9ec4\u886b\u9707\u9a87\u6781\u4e86\uff01\u4ed6\u4e0d\u6d4b \u8fd9\u5c11\u5e74\u600e\u80fd\u7834\u4ed6\u4eec\u4e94\u9634\u5bd2\u9aa8\u638c\u6cd5\u3002 \u3000\u3000\u4f46\u662f\u718a\u501c\u5982\u53ea\u5bf9\u654c\u4ec7\u4e0d\u53ef\u4e00\u4eba\uff0c\u90a3\u4ed6\u662f\u4ece\u5bb9\u4e0d\u8feb\u6e38\u5203\u6709\u4f59\u4e86\uff0c\u53ef\u662f\u6234\u7740\u9762\u5177\u7684\u90a3\u4eba\uff0c\u624b \u5fc3\u91cc\u9ed1\u6c14\u8ff8\u73b0\uff0c\u638c\u98ce\u522e\u8fc7\u4e4b\u5904\uff0c\u51b7\u98ce\u523a\u9aa8\uff0c\u800c\u4e14\u529b\u9053\u5a01\u529b\u65e0\u4f26\uff0c\u718a\u501c\u7528\u5c3d\u4e86\u5929\u96f7\u5185\u529f\u6240\u751f\u7684 \u6f5c\u529b\uff0c\u4ec5\u4ec5\u53ea\u80fd\u628a\u4ed6\u62b5\u6297\u4f4f\uff0c\u800c\u65e0\u6cd5\u83b7\u80dc\u3002 \u3000\u3000\u6234\u9762\u5177\u7684\u4eba\u6700\u521d\u4f7f\u7684\u4e0d\u8fc7\u662f\u4e9b\u5c11\u6797\u7f57\u6c49\u62f3\u3001\u5288\u6302\u638c\u3001\u5d06\u5cd2\u5c11\u9633\u638c\uff0c\u62db\u5f0f\u975e\u5e38\u9a73\u6742\uff0c\u5076\u5c14 \u95ee\u6742\u7740\u4e00\u4e24\u5f0f\u7279\u6b8a\u7684\u62db\u5f0f\uff0c\u718a\u501c\u731b\u7136\u53d1\u89c9\u8fd9\u662f\u5929\u5c71\u4e09\u9f99\u7684\u98de\u9f99\u4e03\u5f0f\u4e2d\u7684\u62db\u5f0f\uff0c\u4e0d\u7531\u5927\u4e3a\u60ca \u5f02\uff0c\u8fd9\u4eba\u53c8\u662f\u8c01\u5462\uff1f \u3000\u3000\u718a\u501c\u5728\u8fd9\u4e24\u4eba\u5408\u653b\u4e4b\u4e0b\uff0c\u867d\u7136\u501a\u4ed7\u4faf\u751f\u6240\u4f20\u7684\u5947\u62db\uff0c\u8db3\u4ee5\u5e94\u4ed8\uff0c\u4f46\u4e5f\u4ed8\u51fa\u4e86\u6240\u6709\u7684\u529b \u91cf\uff0c\u800c\u4ec5\u4ec5\u80fd\u514d\u4e8e\u843d\u8d25\u800c\u5df2\u3002\u5916\u52a0\u4e0a\u6d1e\u5ead\u53cc\u86df\u8881\u5b99\u7b49\u8fd9\u4e9b\u4e0d\u76f8\u5e72\u7684\u62db\u5f0f\uff0c\u56fa\u53ef\u968f\u65f6\u628a\u4ed6\u4eec\u51fb \u9000\uff0c\u4f46\u53c8\u4e0d\u514d\u591a\u8d39\u8bb8\u591a\u624b\u811a\u3002 \u3000\u3000\u773c\u524d\u7684\u5c40\u52bf\uff0c\u663e\u7136\u5bf9\u4ed6\u4eec\u4e09\u4eba\u5f88\u4e0d\u5229\u4e86\uff0c\u56e0\u4e3a\u5c1a\u672a\u660e\u90a3\u4e00\u5957\u585e\u5916\u98de\u82b1\u4e09\u5343\u5f0f\u638c\u6cd5\uff0c\u6ca1\u6709 \u53d1\u6325\u5a01\u529b\u7684\u4f59\u5730\uff0c\u53f8\u793c\u53cc\u7ae5\u65bd\u51fa\u4e94\u9634\u5bd2\u9aa8\u638c\u6cd5\u540e\uff0c\u4ed6\u5df2\u624b\u5fd9\u811a\u4e71\uff0c\u5de6\u652f\u53f3\u62d9\uff0c\u51e0\u4e4e\u96be\u4e8e\u81ea \u4fdd\uff0c\u82e5\u975e\u4ed6\u8f7b\u529f\u5353\u8d8a\uff0c\u95ea\u7eb5\u7075\u5de7\uff0c\u65e9\u5df2\u88ab\u767d\u666f\u7965\u53f6\u6e05\u6e05\u6240\u4e58\u4e86\u3002 \u3000\u3000\u518d\u52a0\u4e0a\u5355\u98de\u5d06\u5cd2\u9547\u5c71\u65ad\u9b42\u638c\u6cd5\uff0c\u4e5f\u662f\u5947\u5999\u65e0\u6bd4\uff0c\u7eb5\u6a2a\u5f00\u9616\uff0c\u62db\u62db\u4e0d\u79bb\u4ed6\u8eab\u4e0a\u91cd\u8981\u7a74\u9053\uff0c \u6cbe\u4e0a\u4e00\u6839\u6307\u5934\uff0c\u4e5f\u5c31\u5fc5\u987b\u88ab\u4eba\u5bb6\u5236\u4f4f\uff0c\u5c1a\u672a\u660e\u62d4\u51fa\u5b9d\u5251\uff0c\u60f3\u5728\u5175\u5203\u4e0a\u627e\u4e9b\u4fbf\u5b9c\uff0c\u53ef\u662f\u4f9d\u7136\u65bd \u5c55\u4e0d\u5f00\uff0c\u767d\u666f\u7965\u548c\u53f6\u6e05\u6e05\u4e24\u53e3\u5251\uff0c\u6bd4\u4ed6\u66f4\u4e3a\u8f7b\u5999\u3002 \u3000\u3000\u5c1a\u672a\u660e\u56db\u9762\u8fce\u6218\uff0c\u4e94\u5341\u591a\u62db\u4ee5\u540e\uff0c\u5468\u8eab\u5192\u51fa\u6c57\u73e0\u513f\uff0c\u5de6\u80a9\u5934\u4e5f\u88ab\u5355\u98de\u63a0\u4e2d\u4e00\u638c\uff0c\u518d\u4e0d\u8bbe \u6cd5\u9003\u8d70\uff0c\u90a3\u5c31\u7b49\u4e8e\u675f\u624b\u5c31\u64d2\u3002 \u3000\u3000\u5c1a\u672a\u660e\u62fc\u8d77\u5468\u8eab\u4e4b\u529b\uff0c\u4f5c\u8fd9\u5782\u8d25\u4ee5\u524d\u7684\u56f0\u517d\u4e4b\u6597\u3002 \u3000\u3000\u590f\u82b8\u7684\u51e0\u4e2a\u5bf9\u624b\uff0c\u4e5f\u90fd\u975e\u5f31\u8005\uff0c\u5f53\u5e74\u718a\u501c\u4e5f\u4ec5\u9669\u80dc\u8fc7\u65e5\u6708\u5934\u9640\u4e00\u62db\uff0c\u73b0\u5728\u4e0e\u52b3\u5c71\u53cc\u9e64\u8054 \u624b\u5408\u653b\uff0c\u590f\u82b8\u4e00\u6761\u94f6\u97ad\uff0c\u94f6\u9f99\u76d8\u98de\u5377\u821e\u65bd\u5c55\u5f00\u72c2\u98d3\u97ad\u6cd5\uff0c\u8fd8\u662f\u5904\u5904\u53d7\u903c\uff0c\u52b3\u5c71\u53cc\u9e64\u591a\u5e74\u6210\u540d \u7684\u597d\u624b\uff0c\u7adf\u628a\u5979\u8fd9\u5957\u97ad\u5730\u62ff\u634f\u5f97\u5f88\u51c6\u3002 \u3000\u3000\u590f\u82b8\u4e00\u53cc\u5de6\u624b\u4e5f\u6ca1\u5730\u7a7a\u95f2\uff0c\u56e0\u4e3a\u654c\u65b9\u662f\u4e09\u4ef6\u5175\u5203\uff0c\u62db\u5f0f\u53c8\u4e2a\u4e2a\u51cc\u5389\u8001\u8fa3\uff0c\u4e00\u6839\u94f6\u97ad\u662f\u65e0 \u6cd5\u5e94\u4ed8\u5f97\u5f00\u7684\uff0c\u5979\u51e0\u6b21\u60f3\u53d1\u51fa\u94a2\u4e5d\uff0c\u90fd\u817e\u4e0d\u51fa\u529f\u5927\u53bb\u888b\u4e2d\u6478\u53d6\u3002 \u3000\u3000\u5728\u5c1a\u672a\u660e\u582a\u582a\u5371\u6b86\u4e4b\u9645\uff0c\u7a81\u7136\u81ea\u9662\u4e2d\u98de\u843d\u4e0b\u6765\u90a3\u4e2a\u7ea2\u5e15\u5c11\u5973\uff0c\u5979\u867d\u7136\u52a0\u5165\u4f5c\u6218\uff0c\u5374\u5a07\u58f0 \u547c\u8bf7\u53f8\u793c\u53cc\u7ae5\u9ed1\u8863\u6469\u52d2\u767d\u8863\u9f99\u5973\u7b49\uff0c\u4e0d\u8981\u91cd\u4f24\u4e86\u5c1a\u672a\u660e\uff0c\u56e0\u4e4b\u4f17\u4eba\u62db\u6cd5\u4e00\u7f13\uff0c\u5c1a\u672a\u660e\u5f97\u7740\u5598 \u606f\u7684\u673a\u4f1a\u3002 \u3000\u3000\u7ea2\u5e15\u5c11\u5973\u6a2a\u5200\u5a9a\u89c6\u7740\u5c1a\u672a\u660e\uff0c\u5979\u5a07\u58f0\u559d\u9053\uff1a\u201c\u5c1a\u5f53\u5bb6\u7684\uff0c\u4f60\u771f\u662f\u81ea\u8d8b\u6b7b\u8def\uff0c\u8fd8\u4e0d\u89c9\u609f \u4e48\uff1f\u5feb\u4e9b\u653e\u4e0b\u6b66\u5668\uff0c\u6295\u5954\u5929\u9634\u6559\u4e0b\uff0c\u6211\u4eec\u4e0d\u4f1a\u4e8f\u5f85\u4f60\u7684\uff01\u201d \u3000\u3000\u5c1a\u672a\u660e\u8fd9\u65f6\u5df2\u6210\u4e86\u5f3a\u5f2f\u4e4b\u672a\uff0c\u5b9d\u5251\u5288\u51fa\u53bb\u90fd\u51cf\u5f31\u4e86\u4e00\u534a\u529b\u91cf\uff0c\u5fc3\u91cc\u6124\u6012\u5df2\u6781\uff0c\u52a0\u4e0a\u4ed6\u706b \u70c8\u6027\u5b50\uff0c\u4ed6\u77e5\u9053\u82e5\u662f\u88ab\u5929\u9634\u6559\u6349\u4f4f\uff0c\u5c06\u4f1a\u843d\u4e2a\u4ec0\u4e48\u7ed3\u679c\uff0c\u6c42\u751f\u7684\u672c\u80fd\uff0c\u4f7f\u4ed6\u4e0d\u5f97\u4e0d\u505a\u5192\u9669\u7a81 \u56f4\u4e4b\u4e3e\u3002 \u3000\u3000\u5c1a\u672a\u660e\u731b\u7136\u60f3\u8d77\u8fd9\u7ea2\u5e15\u5c11\u5973\uff0c\u75f4\u60c5\u672a\u65ad\uff0c\u800c\u4e14\u4e5f\u662f\u56db\u5468\u6700\u5f31\u7684\u73af\uff0c\u82e5\u8981\u9003\u8d70\uff0c\u53ea\u6709\u4ece\u5979 \u8eab\u4e0a\u60f3\u529e\u6cd5\u4e86\u3002 \u3000\u3000\u5c1a\u672a\u660e\u5982\u540c\u4e00\u5934\u75af\u72c2\u7684\u91ce\u517d\uff0c\u731b\u5411\u7ea2\u5e15\u5c11\u5973\uff0c\u5237\u5237\u5237\u4e00\u8fde\u731b\u5288\u4e86\u4e09\u5251\uff0c\u679c\u7136\u4ed6\u4e3b\u610f\u6536\u5230 \u4e86\u6548\u679c\uff0c\u7ea2\u5e15\u5c11\u5973\u662f\u4e0d\u5fcd\u8fd8\u4ed6\u4ee5\u6bd2\u62db\u7684\uff0c\u56e0\u4e4b\u7ea2\u5e15\u5c11\u5973\u95ea\u8eab\u907f\u8ba9\uff0c\u773c\u524d\u9732\u51fa\u4e00\u9053\u7f1d\u9699\uff0c\u6b63\u662f \u4ed6\u51b2\u51fa\u7684\u826f\u673a\u3002 \u3000\u3000\u5c1a\u672a\u660e\u628a\u63e1\u4f4f\u8fd9\u5927\u597d\u673a\u4f1a\uff0c\u731b\u7136\u81ea\u8fd9\u9762\u7a7a\u9699\u8dc3\u51fa\uff0c\u4ed6\u81ea\u987e\u4e0d\u5f97\u548c\u718a\u501c\u7b49\u6253\u4ec0\u4e48\u62db\u547c\uff0c\u6025 \u6025\u5411\u5357\u8fb9\u5954\u9a70\u3002 \u3000\u3000\u540e\u9762\u516d\u4e2a\u654c\u4eba\uff0c\u4e5f\u7acb\u8d77\u76f4\u8ffd\uff0c\u8f6c\u773c\u90fd\u6ca1\u6709\u5f71\u5b50\u3002 \u3000\u3000\u5e7f\u573a\u4e0a\u8fd9\u4e00\u89d2\u6682\u544a\u9759\u5bc2\u3002 \u3000\u3000\u6d1e\u5ead\u53cc\u86df\u548c\u53e6\u4e00\u4e2a\u5317\u9053\u7eff\u6797\u597d\u624b\uff0c\u5374\u5df2\u88ab\u718a\u501c\u4f24\u5728\u5251\u4e0b\uff0c\u718a\u501c\u65e0\u6cd5\u6218\u8d25\u5f3a\u654c\uff0c\u53ea\u6709\u62d4\u51fa \u8d2f\u65e5\u5251\uff0c\u4f5c\u6700\u540e\u4e00\u62fc\uff0c\u4ed6\u662f\u4e0d\u5927\u613f\u610f\u627f\u8ba4\u4e0d\u654c\u5c31\u6b64\u9003\u8d70\u7684\uff0c\u4f55\u51b5\u590f\u82b8\u80fd\u5426\u6551\u51fa\uff0c\u8fd8\u662f\u5927\u6210\u95ee \u9898\u5462\uff01 \u3000\u3000\u718a\u501c\u65bd\u5c55\u82cd\u7a79\u5341\u4e09\u5f0f\u5251\u6cd5\uff0c\u679c\u7136\u4f7f\u90a3\u9ec4\u886b\u5ba2\u4ec7\u4e0d\u53ef\u5927\u4e3a\u9707\u60ca\uff0c\u4ed6\u9707\u60ca\u7684\u662f\u5f53\u5e74\u7684\u5929\u9634\u6559 \u5c31\u6bc1\u5728\u8fd9\u79cd\u5251\u6cd5\u4e4b\u4e0b\uff0c\u4e0d\u8fc7\u5355\u51ed\u8fd9\u5341\u4e09\u5f0f\u662f\u4e0d\u80fd\u53d1\u6325\u5a01\u529b\u7684\uff0c\u800c\u718a\u501c\u53c8\u6070\u597d\u7528\u7684\u662f\u5f53\u5e74\u94c1\u5251 \u5148\u751f\u7684\u8d2f\u65e5\u5251\u5462\u3002 \u3000\u3000\u4ec7\u4e0d\u53ef\u662f\u4ee5\u524d\u7855\u679c\u4ec5\u5b58\u5929\u9634\u6559\u9057\u8001\u4e4b\u4e00\uff0c\u4ed6\u591a\u5e74\u6765\u51c6\u5907\u597d\u4e00\u4ef6\u80fd\u62b5\u6321\u501a\u5929\u8d2f\u65e5\u53cc\u5251\u7684\u6b66 \u5668\uff0c\u662f\u7528\u91d1\u7ebf\u868a\u7b4b\u4ee5\u53ca\u6700\u575a\u97e7\u7684\u6811\u6c41\u5408\u94f8\u800c\u6210\u7684\u8f6f\u97ad\uff0c\u53cc\u5251\u518d\u950b\u5229\uff0c\u4e5f\u6ca1\u6cd5\u524a\u65ad\u8fd9\u79cd\u5bcc\u6709\u80f6 \u7740\u6027\u7684\u7269\u4ef6\u3002 \u3000\u3000\u4ec7\u4e0d\u53ef\u4e5f\u7acb\u5373\u81ea\u8170\u95f4\u89e3\u4e0b\u4ed6\u8fd9\u4ef6\u72ec\u95e8\u4e4c\u9f99\u7d22\uff0c\u4ee5\u72ec\u7279\u7684\u62db\u6cd5\uff0c\u8fce\u5377\u7ede\u7f20\u718a\u501c\u7684\u957f\u5251\uff0c\u65e0 \u5982\u82cd\u7a79\u5341\u4e09\u5f0f\uff0c\u5927\u534a\u662f\u5728\u7a7a\u4e2d\u53d1\u62db\uff0c\u5c24\u5176\u53d8\u5316\u795e\u901f\u83ab\u6d4b\uff0c\u8f85\u4ee5\u718a\u501c\u6f5c\u5f62\u9041\u5f71\u7684\u7edd\u9876\u8f7b\u529f\uff0c\u5176 \u5a01\u529b\u786e\u4e4e\u4e0d\u540c\u51e1\u54cd\u3002 \u3000\u3000\u4f46\u662f\u4ec7\u4e0d\u53ef\u5403\u8fc7\u8fd9\u79cd\u5251\u6cd5\u7684\u4e8f\uff0c\u56e0\u4e4b\u4ed6\u591a\u5e74\u7cbe\u5fc3\u63e3\u6e38\uff0c\u609f\u89e3\u4e86\u4e00\u90e8\u5206\u89e3\u5316\u5251\u62db\u7684\u7d22\u5f0f\uff0c \u718a\u501c\u8fde\u653b\u4e86\u6570\u62db\uff0c\u201c\u843d\u5730\u6d41\u661f\u201d\uff0c\u201c\u5927\u8679\u5012\u5212\u201d\uff0c\u201c\u6cdb\u6e21\u94f6\u6cb3\u201d\uff0c\u201c\u5927\u81ea\u7ecf\u5929\u201d\uff0c\u5feb\u662f\u5feb\u5230 \u4e86\u6781\u70b9\uff0c\u5374\u4e0d\u80fd\u4f24\u7740\u94c1\u9762\u9ec4\u886b\u5ba2\u3002 \u3000\u3000\u6234\u9762\u5177\u7684\u4eba\uff0c\u5374\u59cb\u7ec8\u6ca1\u62ff\u51fa\u5175\u5203\uff0c\u56e0\u4e4b\u718a\u501c\u5bf9\u4ed6\u66f4\u591a\u53d1\u6325\u8f83\u5927\u7684\u5a01\u529b\uff0c\u4f46\u662f\u6234\u9762\u5177\u7684 \u4eba\uff0c\u529f\u529b\u5374\u5206\u5916\u96c4\u539a\uff0c\u4ed6\u751a\u81f3\u4ee5\u638c\u4e0a\u7684\u653b\u529b\uff0c\u5728\u4e00\u4e8c\u5c3a\u8fdc\u5904\uff0c\u5c31\u628a\u718a\u501c\u7684\u957f\u5251\u9707\u4e86\u5f00\u53bb\uff0c\u6240 \u4ee5\u718a\u501c\u4ecd\u4e0d\u80fd\u5360\u7edd\u5bf9\u7684\u4f18\u52bf\u3002 \u3000\u3000\u4f46\u6d1e\u5ead\u53cc\u86df\u4e4b\u7c7b\u7684\u7eff\u6797\u82f1\u96c4\uff0c\u5374\u5c31\u4e0d\u514d\u5403\u4e9b\u82e6\u5934\uff0c\u56e0\u4e3a\u4ed6\u4eec\u4ece\u6ca1\u89c1\u8fc7\u8fd9\u79cd\u5251\u6cd5\uff0c\u718a\u501c\u8fde \u4eba\u5e26\u5251\uff0c\u4f3c\u4e4e\u5728\u4ed6\u4eec\u5934\u4e0a\u76d8\u65cb\uff0c\u65e0\u6cd5\u731c\u6d4b\u718a\u501c\u8fd9\u4e00\u5251\u5288\u5411\u4f55\u5904\u3002\u82e5\u4e0d\u662f\u9ec4\u886b\u5ba2\u548c\u6234\u9762\u5177\u7684\u4eba \u53ca\u65f6\u63f4\u6551\uff0c\u4ed6\u4eec\u4f1a\u591a\u4f24\u51e0\u4eba\u7684\u3002 \u3000\u3000\u718a\u501c\u662f\u4e3a\u4e86\u89e3\u9664\u590f\u82b8\u6240\u7684\u538b\u529b\uff0c\u4e0d\u5f97\u4e0d\u4e0b\u6bd2\u624b\u3002 \u3000\u3000\u52b3\u5c71\u53cc\u9e64\u65e5\u6708\u5934\u9640\u8fd9\u4e09\u4f4d\uff0c\u5df2\u4f7f\u590f\u82b8\u624b\u5fd9\u811a\u4e71\u4e86\uff0c\u4f55\u51b5\u6d1e\u5ead\u53cc\u86df\u9f99\u5316\u5b87\u7b49\u8fd8\u7a7a\u51b7\u5b50\u6765\u4e00 \u4e24\u4e0b\u6bd2\u62db\uff0c\u600e\u80fd\u4e0d\u4f7f\u718a\u501c\u4e3a\u4e4b\u7126\u6025\uff0c\u6240\u4ee5\u4ed6\u4e0d\u5f97\u5df2\u731b\u7136\u6487\u5f00\u4ec7\u4e0d\u53ef\u7b49\uff0c\u8eab\u5f62\u98d8\u95ea\u8fc7\u53bb\uff0c\u8d4f\u8fd9 \u4e9b\u4eba\u4e00\u5251\u3002 \u3000\u3000\u4f46\u662f\u9ec4\u886b\u5ba2\u548c\u6234\u9762\u5177\u7684\u4eba\uff0c\u5c82\u80af\u653e\u677e\uff0c\u5728\u718a\u501c\u5251\u4f24\u5c24\u5316\u5b87\u7b49\u4e4b\u540e\uff0c\u4ed6\u4fe9\u66f4\u662f\u5982\u5f71\u968f\u5f62\uff0c \u7d27\u7d27\u7684\u628a\u718a\u501c\u7f20\u4f4f\u3002 \u3000\u3000\u718a\u501c\u4e0d\u65f6\u98de\u4e34\u590f\u82b8\u8eab\u65c1\u52a9\u6218\uff0c\u4f7f\u590f\u82b8\u66f4\u589e\u52a0\u4e86\u52c7\u6c14\uff0c\u5728\u718a\u501c\u5251\u4f24\u4e09\u4e2a\u5929\u9634\u6559\u4eba\u4e4b\u540e\uff0c\u5979 \u4e5f\u6478\u51fa\u51e0\u7c92\u94a2\u4e38\uff0c\u4ee5\u6781\u8f7b\u5de7\u7684\u624b\u6cd5\u53d1\u51fa\u3002 \u3000\u3000\u4e8e\u662f\u65e5\u6708\u5934\u9640\u4e5f\u4e2d\u4e86\u94a2\u4e38\u5012\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u6765\u718a\u501c\u548c\u590f\u82b8\u4f1a\u5408\u5728\u4e00\u8d77\u4e86\uff0c\u8d2f\u65e5\u957f\u8679\u95ea\u7ed5\uff0c\u4f50\u4ee5\u590f\u82b8\u7684\u96cf\u97ad\uff0c\u5e76\u80a9\u4f5c\u6218\uff0c\u58f0\u52bf\u5927 \u4e3a\u6539\u89c2\u3002 \u3000\u3000\u5bf9\u65b9\u53c8\u5c11\u4e86\u56db\u4e2a\u80fd\u624b\uff0c\u5f62\u4e0d\u6210\u5305\u56f4\u7684\u9635\u52bf\uff0c\u770b\u6765\u718a\u501c\u548c\u590f\u82b8\u5df2\u8131\u9669\u5883\uff0c\u53ef\u662f\u718a\u501c\u53c8\u987e\u8651 \u5230\u5c1a\u672a\u660e\uff0c\u518d\u4e00\u770b\u5c1a\u672a\u660e\u548c\u9ed1\u591c\u6469\u52d2\u7b49\u4e00\u6279\u654c\u4eba\uff0c\u5747\u5df2\u79bb\u5f00\u73b0\u573a\uff0c\u4f7f\u718a\u501c\u5927\u4e3a\u5403\u60ca\uff0c\u4f46\u82e6\u5e72 \u672a\u53ca\u6ce8\u610f\u5c1a\u672a\u660e\u9003\u8d70\u7684\u65b9\u5411\u3002 \u3000\u3000\u9ec4\u886b\u5ba2\u4ec7\u4e0d\u53ef\u89c1\u5f62\u52bf\u9006\u8f6c\uff0c\u4e45\u6218\u65e0\u529f\uff0c\u4ed6\u64ae\u53e3\u4e00\u53e0\u957f\u5578\uff0c\u628a\u52b3\u5c71\u53cc\u9e64\u7b49\u4e00\u9f50\u62db\u547c\u7565\u4e3a\u540e \u9000\uff0c\u4ed6\u4eec\u4e94\u4f4d\u7ad9\u6210\u4e00\u7ebf\uff0c\u628a\u5bf9\u9762\u4e00\u53cc\u5c11\u5e74\u7537\u5973\u7684\u8eab\u4f53\u90e8\u4f4d\u4eae\u540c\u6765\uff0c\u5728\u4ed6\u53c8\u4e00\u6325\u4e4b\u4e0b\uff0c\u5de6\u53f3\u540e \u4e09\u65b9\u7acb\u523b\u4e1d\u4e1d\u4e4b\u58f0\u4e0d\u7edd\u3002 \u3000\u3000\u5929\u9634\u6559\u4eba\u65e9\u5b89\u7f6e\u4e0b\u56db\u5468\u6570\u5341\u6761\u83bd\u6c49\uff0c\u5404\u5f00\u5f29\u5323\uff0c\u4e09\u5bf8\u4f59\u957f\u5582\u6709\u5947\u6bd2\u7684\u8fde\u73e0\u8f7b\u5f2f\u7bad\uff0c\u96e8\u70b9 \u4e00\u822c\u5c04\u6765\u2026 \u3000\u3000\u718a\u501c\u6ca1\u6709\u9632\u5907\u4ed6\u4eec\u8fd8\u6709\u8fd9\u79cd\u6076\u6bd2\u624b\u6bb5\uff0c\u4e00\u65f6\u628a\u8d2f\u65e5\u5251\u821e\u4e2a\u98ce\u96e8\u4e0d\u900f\uff0c\u800c\u590f\u82b8\u4e5f\u97ad\u5f71\u76d8\u65cb \u531d\u7ed5\uff0c\u94f6\u9f99\u95ea\u51fa\u65e0\u8fb9\u971e\u5149\uff0c\u950b\u6323\u6b64\u58f0\u4e0d\u7edd\u4ed6\u4fe9\u8eab\u65c1\uff0c\u843d\u4e86\u4e00\u5730\u7684\u5f2f\u7bad\u3002 \u3000\u3000\u800c\u9ec4\u886b\u5ba2\u4ec7\u4e0d\u53ef\u548c\u6234\u9762\u5177\u7684\u4eba\uff0c\u4e5f\u4e58\u9699\u53d1\u62db\uff0c\u4f7f\u4ed6\u4fe9\u5904\u52bf\u6781\u4e3a\u5371\u6b86\uff0c\u4f46\u718a\u501c\u4eba\u6781\u777f\u667a\uff0c \u4ed6\u60f3\u53ea\u6709\u548c\u654c\u7f20\u6597\u5728\u4e00\u8d77\uff0c\u51b7\u7bad\u81ea\u751f\u987e\u5fcc\uff0c\u4ed6\u7acb\u523b\u65bd\u5c55\u6f5c\u5f62\u9041\u5f71\u4e4b\u6cd5\uff0c\u7a7f\u82b1\u8774\u8776\u4e00\u822c\uff0c\u53cd\u6251 \u5165\u654c\u4eba\u884c\u5217\u91cc\u3002 \u3000\u3000\u679c\u7136\u56db\u5468\u51b7\u7bad\u4e0d\u6562\u53d1\u5c04\u4e86\uff0c\u590f\u82b8\u4e5f\u770b\u51fa\u718a\u501c\u7684\u7528\u610f\uff0c\u5979\u65bd\u5c55\u4e00\u79cd\u6d41\u661f\u6b65\u6cd5\uff0c\u56f4\u7ed5\u7740\u52b3\u5c71 \u53cc\u9e64\uff0c\u957f\u97ad\u65cb\u821e\uff0c\u5047\u82e5\u5929\u9634\u6559\u4eba\u518d\u653e\u5f2f\u7bad\uff0c\u8bf4\u4e0d\u5b9a\u662f\u8c01\u78b0\u4e0a\u5462\u3002\u56e0\u6b64\uff0c\u94c1\u9762\u9ec4\u886b\u5ba2\u4e0d\u5f97\u4e0d\u53d1 \u5578\u5236\u6b62\u3002 \u3000\u3000\u718a\u501c\u867d\u7136\u4ee5\u5de7\u8ba1\uff0c\u4f7f\u4ed6\u4eec\u6bd2\u5f2f\u65e0\u529f\uff0c\u4f46\u662f\u4e00\u65f6\u8fd8\u662f\u4e0d\u80fd\u5bf9\u4ed8\u6389\u4ec7\u4e0d\u53ef\u7b49\u513f\u4f4d\u6b66\u529f\u6781\u9ad8\u7684 \u4eba\u7269\u3002 \u3000\u3000\u590f\u82b8\u5374\u5728\u4e45\u6218\u4e4b\u4e0b\uff0c\u8eab\u4f53\u6e10\u6e10\u4e0d\u652f\uff0c\u7a81\u7136\u957f\u5578\u97f3\u957f\uff0c\u5657\u5657\u5657\u53c8\u81ea\u8fdc\u5904\u98de\u7eb5\u6765\u5929\u9634\u6559\u4e09\u4f4d \u9ad8\u624b\u3002 \u3000\u3000\u6b63\u662f\u5355\u638c\u65ad\u9b42\u5355\u98de\u3001\u9ed1\u8863\u6469\u52d2\u767d\u666f\u7965\u548c\u767d\u8863\u9f99\u5973\u53f6\u6e05\u6e05\u3002 \u3000\u3000\u8fd9\u4e09\u4eba\u80dc\u5229\u5f52\u6765\uff0c\u5355\u638c\u65ad\u9b42\u51b7\u7b11\u7740\uff01\u559d\u9053\uff1a\u201c\u718a\u501c\u4f60\u4fe9\u8fd8\u4e0d\u653e\u4e0b\u5175\u5203\u5ef6\u9888\u53d7\u622e\uff0c\u4f60\u90a3\u540c \u4f34\u65e9\u5df2\u88ab\u6211\u4eec\u751f\u64d2\u6d3b\u6349\u4e86\uff01\u201d \u3000\u3000\u718a\u501c\u53ef\u5403\u4e86\u5927\u60ca\uff0c\u4ed6\u66f4\u4ee5\u6781\u5de7\u5999\u7684\u82cd\u7a79\u5341\u4e09\u5f0f\uff0c\u5206\u6251\u5355\u98de\u4e09\u4eba\uff0c\u4ed6\u6050\u6015\u5c1a\u672a\u660e\u5df2\u906d\u6bd2 \u624b\uff0c\u4ed6\u773c\u91cc\u90fd\u7094\u5192\u51fa\u4e86\u8840\u4e1d\uff0c\u4ed6\u8981\u4e3a\u5c1a\u672a\u660e\u590d\u4ec7\uff0c\u4ed6\u53c8\u4f7f\u51fa\u201c\u661f\u4e34\u516b\u89d2\u201d\u3001\u201c\u4e91\u5982\u5c71\u6d8c\u201d\u4e24 \u4e0b\u7edd\u62db\uff0c\u5e0c\u671b\u628a\u5355\u98de\u7b49\u5148\u6536\u62fe\u6389\u3002 \u3000\u3000\u718a\u501c\u8eab\u6cd5\u795e\u901f\u5f97\u4f7f\u4eba\u76ee\u7729\uff0c\u679c\u7136\u5355\u638c\u65ad\u9b42\u5355\u98de\uff0c\u8eb2\u907f\u4e5f\u8eb2\u4e0d\u8fc7\uff0c\u4ed6\u60f3\u56de\u540e\u593a\u5251\uff0c\u800c\u718a\u501c \u5251\u8679\u98de\u821e\u8d77\u6765\uff0c\u5b9b\u5982\u4e00\u6761\u9752\u9f99\uff0c\u77e2\u5a07\u83ab\u6d4b\uff0c\u5657\u7684\u4e00\u58f0\uff0c\u5df2\u81ea\u4ed6\u624b\u81c2\u62c2\u8fc7\uff0c\u5212\u4e86\u4e00\u9053\u8840\u69fd\uff0c\u4f7f \u4ed6\u8e09\u8e09\u8dc4\u8dc4\u5730\u8dcc\u62a2\u8fc7\u4e00\u8fb9\u53bb\u3002 \u3000\u3000\u767d\u666f\u7965\u548c\u53f6\u6e05\u6e05\u52a8\u529b\u53ef\u6bd4\u5355\u98de\u8fd8\u9ad8\u660e\u4e9b\uff0c\u4e24\u4eba\u8054\u5251\u4ea4\u903c\uff0c\u800c\u718a\u501c\u8eab\u540e\u4ec7\u4e0d\u53ef\u548c\u6234\u9762\u5177\u7684 \u4eba\uff0c\u53c8\u53cc\u638c\u4ea4\u81f3\uff0c\u718a\u501c\u663e\u7136\u53c8\u4eba\u4e86\u91cd\u56f4\uff01 \u3000\u3000\u8fd9\u5ea7\u5927\u7b2c\u5b85\uff0c\u4e95\u975e\u6781\u8352\u51c9\u7684\u5730\u5e26\uff0c\u53ef\u662f\u591c\u9759\u66f4\u6df1\uff0c\u4eba\u4eec\u90fd\u5df2\u5b89\u8be6\u5730\u5165\u4e86\u7761\u4e61\uff0c\u66f4\u6709\u8c01\u6765 \u6b23\u8d4f\u8fd9\u4e00\u5e55\u8840\u8089\u4ea4\u7ec7\u7684\u6076\u6597\u5462\uff01 \u3000\u3000\u718a\u501c\u548c\u4ec7\u4e0d\u53ef\u7b49\u8fc7\u4e86\u4e24\u767e\u591a\u62db\uff0c\u6d88\u8017\u771f\u6c14\u4e0d\u5c11\uff0c\u518d\u52a0\u4e0a\u4e24\u540d\u52b2\u654c\uff0c\u786e\u5b9e\u4f7f\u4ed6\u7a77\u4e8e\u5e94\u4ed8\uff0c \u718a\u501c\u81ea\u51fa\u4e16\u4ee5\u6765\uff0c\u8fd9\u7b97\u662f\u4ed6\u7b2c\u4e00\u6b21\u628a\u5168\u8eab\u6c14\u529b\u90fd\u5feb\u7528\u5c3d\u4e86\uff0c\u800c\u654c\u4eba\u653b\u52bf\u8d8a\u6765\u8d8a\u7d27\uff0c\u4ed6\u5ff5\u53ca\u5c1a \u672a\u660e\u597d\u53cb\u9047\u96be\uff0c\u66f4\u662f\u6124\u4e0d\u6b32\u751f\u3002 \u3000\u3000\u590f\u82b8\u6b64\u65f6\u66f4\u663e\u5f97\u75b2\u4e4f\u4e0d\u582a\uff0c\u5598\u6c14\u5401\u5401\u3002 \u3000\u3000\u718a\u501c\u6709\u77e5\u604b\u6218\u4e0b\u53bb\uff0c\u4ed6\u548c\u590f\u82b8\u96be\u514d\u4f5c\u540c\u547d\u9e33\u9e2f\uff0c\u4f46\u662f\u76ee\u524d\u5f62\u52bf\uff0c\u9003\u8d70\u5374\u4e5f\u4e0d\u6613\u3002\u53ea\u662f\u4ed6 \u4fe9\u5f80\u5916\u9762\u4e00\u7eb5\uff0c\u56db\u5468\u7684\u5f29\u5f13\u624b\uff0c\u5fc5\u4f1a\u7ed9\u4ed6\u4fe9\u4e00\u4e2a\u7bad\u5982\u96e8\u4e0b\u3002\u4f55\u51b5\u4ec7\u4e0d\u53ef\u7b49\u56db\u4eba\uff0c\u6ca1\u6709\u653e\u8fc7\u4e00 \u4e1d\u673a\u4f1a\uff0c\u603b\u662f\u6076\u72e0\u72e0\u5730\u5411\u7740\u4ed6\u8eab\u4e0a\u62db\u547c\u3002 \u3000\u3000\u718a\u501c\u8003\u8651\u4e86\u4e00\u9635\uff0c\u603b\u662f\u627e\u4e0d\u7740\u673a\u4f1a\u7a81\u56f4\u3002 \u3000\u3000\u5947\u8ff9\u53c8\u53d1\u751f\u4e86\uff0c\u7ad9\u5728\u8fdc\u5904\u7684\u56db\u5468\u83bd\u6c49\uff0c\u7a81\u7136\u9635\u5f62\u5927\u4e71\uff0c\u53ee\u5440\u554a\u5440\u7684\u602a\u568e\u58f0\uff0c\u5939\u7740\u6251\u901a\u6251 \u901a\u8eab\u8eaf\u5012\u5730\u4e4b\u58f0\uff0c\u7adf\u6709\u4e00\u6392\u513f\u83bd\u6c49\uff0c\u7eb7\u7eb7\u5012\u5730\uff0c\u800c\u4e14\u7531\u4e8e\u81ea\u5df1\u6240\u6301\u7684\u706b\u628a\uff0c\u5f15\u7740\u4e86\u8863\u670d\uff0c\u66f4 \u70e7\u5f97\u72fc\u568e\u9b3c\u53eb\uff0c\u5728\u5730\u4e0a\u7ffb\u6765\u6eda\u53bb\u3002 \u3000\u3000\u4ece\u8fd9\u4e00\u6392\u4eba\u7684\u7f3a\u53e3\u91cc\uff0c\u5df2\u95ea\u95ea\u98de\u7eb5\u8fdb\u6765\u4e24\u4f4d\u7eee\u5e74\u7389\u8c8c\uff0c\u795e\u5ea6\u4e0d\u51e1\u7684\u4eba\u6765\uff0c\u6b63\u662f\u751c\u751c\u8c37\u7684 \u70b9\u82cd\u53cc\u4fa0\u5e38\u6f2b\u5929\u592b\u5987\u3002 \u3000\u3000\u5929\u9634\u6559\u8fd9\u6570\u5341\u540d\u6bd2\u5f29\u624b\uff0c\u6b63\u662f\u88ab\u6563\u82b1\u4ed9\u5b50\u7530\u654f\u654f\u7684\u6563\u82b1\u624b\u6cd5\uff0c\u6253\u5f97\u7eb7\u7eb7\u53d7\u4f24\u5012\u5730\uff0c\u8fd9\u4e9b \u54ea\u91cc\u80fd\u8eb2\u5f97\u5f00\u5979\u7684\u5947\u5999\u7684\u94a2\u4e38\u5462\u3002 \u3000\u3000\u590f\u82b8\u8fdc\u8fdc\u671b\u89c1\u4e86\u6563\u82b1\u4ed9\u5b50\uff0c\u559c\u6781\u800c\u547c\uff1a\u201c\u7530\u59d0\u59d0\uff01\u5feb\u5e2e\u52a9\u6211\u4eec\uff0c\u5929\u9634\u6559\u4eba\u771f\u662f\u86ee\u4e0d\u8bb2\u7406 \u7684\uff01\u201d \u3000\u3000\u590f\u82b8\u9ad8\u5174\u6781\u4e86\uff0c\u53ef\u662f\u5fc3\u795e\u4e0d\u514d\u4e3a\u4e4b\u4e00\u61c8\uff0c\u672c\u6765\u5979\u5df2\u7cbe\u75b2\u529b\u5c3d\uff0c\u4e0d\u8fc7\u662f\u4e00\u79cd\u5f3a\u70c8\u7684\u6c42\u751f\u6b32 \u652f\u6301\u7740\u5979\u7684\u8eab\u4f53\u3002 \u3000\u3000\u4eba\u5728\u60ca\u559c\u4e4b\u4e0b\uff0c\u7cbe\u795e\u4e5f\u4f1a\u8f7b\u677e\u6da3\u6563\u4e0b\u6765\u3002 \u3000\u3000\u800c\u66f4\u53ef\u6076\u7684\uff0c\u90a3\u4e2a\u6234\u9762\u5177\u7684\u4eba\uff0c\u7adf\u4ee5\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\u7684\u624b\u6cd5\uff0c\u5077\u5077\u5411\u590f\u82b8\u80cc\u4e0a\u62cd\u4e0b\u4e00\u638c\uff0c \u8fd9\u662f\u4ed6\u8ba4\u6e05\u4e86\u590f\u82b8\u7684\u672c\u9886\u4e0d\u9ad8\uff0c\u5bb9\u6613\u4e0b\u624b\uff0c\u800c\u53ea\u8981\u52ab\u593a\u4e86\u590f\u82b8\u3002\u4ecd\u53ef\u8981\u631f\u718a\u501c\uff0c\u6240\u4ee5\u8fd9\u4eba\u7684 \u7528\u5fc3\u662f\u975e\u5e38\u9634\u9669\u4e86\u3002 \u3000\u3000\u6563\u82b1\u4ed9\u5b50\u4ee5\u6781\u5feb\u7684\u8eab\u6cd5\uff0c\u5411\u590f\u82b8\u8eab\u8fb9\u9a70\u63f4\uff0c\u5e76\u4e14\u4ee5\u7b11\u58f0\u56de\u7b54\u590f\u82b8\u8bf4\uff1a\u201c\u4e0d\u8981\u614c\uff0c\u59d0\u59d0\u6765 \u5e2e\u4f60\u4e86\uff01\u201d \u3000\u3000\u6563\u82b1\u4ed9\u5b50\u5982\u540c\u5f69\u971e\u7f24\u7eb7\uff0c\u81ea\u7a7a\u4e2d\u7fe9\u7fe9\u800c\u964d\uff0c\u5979\u8eab\u5728\u9ad8\u5904\uff0c\u65e9\u5df2\u53d1\u73b0\u6234\u9762\u5177\u7684\u4eba\u731b\u4e0b\u6bd2 \u624b\uff0c\u53ea\u662f\u9694\u5f97\u8fdc\u4e9b\uff0c\u65e0\u6cd5\u62a2\u4e0a\u53bb\u62d4\u6551\uff0c\u5979\u4e00\u671b\u9ad8\u547c\uff1a\u201c\u82b8\u59b9\u59b9\u5feb\u8eb2\uff01\u80cc\u540e\u6709\u4eba\u6697\u7b97\u4f60\uff01\u201d\u540c \u65f6\u5979\u90a3\u5947\u5999\u65e0\u6bd4\u7684\u94a2\u4e38\uff0c\u53c8\u5927\u628a\u98de\u5c04\u800c\u4e0b\u3002 \u3000\u3000\u590f\u82b8\u6b63\u5728\u6b23\u559c\u5fd8\u5f62\u4e4b\u9645\uff0c\u8eab\u540e\u7684\u7a81\u88ad\u539f\u4e0d\u4f1a\u7559\u5fc3\u5bdf\u89c9\uff0c\u4f46\u662f\u7530\u654f\u654f\u90a3\u4e48\u5927\u58f0\u63d0\u9192\uff0c\u5979\u624d \u4f1a\u672c\u80fd\u5730\u5a07\u8eaf\u5411\u524d\u95ea\u8eb2\u3002 \u3000\u3000\u53ef\u662f\u5df2\u7ecf\u8fdf\u4e86\uff0c\u5979\u5e78\u597d\u7b97\u662f\u8eb2\u5f00\u4e86\u90a3\u4eba\u7684\u624b\u638c\uff0c\u4f46\u662f\u638c\u4e0a\u5bd2\u98ce\uff0c\u4f9d\u65e7\u4f7f\u5979\u6cdb\u9aa8\u751f\u51c9\uff0c\u81a8 \u7684\u4e00\u58f0\uff0c\u80cc\u4e0a\u75db\u5f97\u76ae\u8089\u6b32\u88c2\uff0c\u800c\u4e14\u6d51\u8eab\u8d77\u7c9f\uff0c\u4e0d\u81ea\u4e3b\u7684\u98a4\u6296\u8d77\u6765\uff0c\u8eab\u4f53\u518d\u4e5f\u652f\u6301\u4e0d\u4f4f\uff0c\u5411\u524d \u722c\u8dcc\u4e0b\u53bb\u3002 \u3000\u3000\u6563\u82b1\u4ed9\u5b50\u94a2\u4e38\uff0c\u5219\u5df2\u5982\u6f2b\u5929\u82b1\u96e8\uff0c\u540c\u65f6\u6253\u4e2d\u4e86\u52b3\u5c71\u53cc\u9e64\u548c\u90a3\u6234\u9762\u5177\u7684\u4eba\uff0c\u4e09\u4eba\u90fd\u9f50\u58f0\u602a \u547c\uff0c\u5411\u4e00\u65c1\u95ea\u907f\u3002 \u3000\u3000\u6563\u82b1\u4ed9\u5b50\u98d8\u98d8\u800c\u964d\uff0c\u4e00\u628a\u62b1\u8d77\u590f\u82b8\u3002 \u3000\u3000\u4f46\u590f\u82b8\u5df2\u660f\u8ff7\u5f97\u4e0d\u7701\u4eba\u4e8b\uff0c\u800c\u6ee1\u53e3\u7259\u9f7f\u8fd8\u5431\u5431\u6253\u7740\u5bd2\u98a4\u3002 \u3000\u3000\u7389\u9762\u795e\u5251\u4e5f\u540c\u65f6\u843d\u5730\uff0c\u718a\u501c\u5df2\u4e00\u5251\u903c\u9000\u53f6\u6e05\u6e05\uff0c\u4ed6\u614c\u5f97\u8df3\u81f3\u4e09\u4eba\u8eab\u65c1\uff0c\u53ea\u53eb\u4e86\u4e00\u58f0\uff1a \u201c\u5e38\u5927\u54e5\uff0c\u7530\u59d0\u59d0\u3002\u201d \u3000\u3000\u4ed6\u5c31\u4fef\u4e0b\u5934\u53bb\uff0c\u5bdf\u770b\u590f\u82b8\u7684\u4f24\u52bf\u3002 \u3000\u3000\u94c1\u9762\u9ec4\u886b\u5ba2\u4ec7\u4e0d\u53ef\uff0c\u4e00\u770b\u89c1\u662f\u70b9\u82cd\u6d3e\u7389\u9762\u795e\u5251\u592b\u5987\u6765\u5230\uff0c\u4ed6\u9762\u4e0a\u795e\u8272\u4e00\u53d8\uff0c\u5bf9\u65b9\u53c8\u6765\u4e86 \u8fd9\u4e48\u4e24\u4f4d\u9a87\u4eba\u542c\u95fb\u7684\u9ad8\u624b\uff0c\u4eca\u591c\u662f\u5f88\u96be\u8ba8\u7740\u4fbf\u5b9c\u4e86\u3002 \u3000\u3000\u4ec7\u4e0d\u53ef\u548c\u9ed1\u8863\u6469\u52d2\u7b49\u7ad9\u5728\u4e00\u8fb9\uff0c\u4ed6\u53c8\u64ae\u53e3\u957f\u5578\uff0c\u5927\u8896\u6325\u52a8\u5904\uff0c\u4e09\u9762\u6bd2\u5f29\uff0c\u5982\u540c\u6f2b\u5929\u7684\u82b1 \u96e8\uff0c\u5506\u55d6\u800c\u81f3\u3002 \u3000\u3000\u718a\u501c\u5fd9\u548c\u5e38\u6f2b\u5929\u76f8\u80cc\u800c\u7acb\uff0c\u628a\u5251\u5149\u821e\u8d77\u201c\u516b\u65b9\u98ce\u96e8\u201d\u7684\u5999\u62db\uff0c\u628a\u6563\u82b1\u4ed9\u5b50\u590f\u82b8\u4e8c\u4eba\u4e00\u9f50 \u63a9\u62a4\u4f4f\uff0c\u6563\u82b1\u4ed9\u5b50\u6c14\u5f97\u53d8\u4e86\u989c\u8272\uff0c\u5979\u53ef\u4e5f\u987e\u4e0d\u5f97\u591a\u4f24\u4eba\uff0c\u53c8\u65bd\u5c55\u6563\u82b1\u4ed9\u5b50\u65e0\u6570\u94a2\u4e38\uff0c\u5411\u56db\u5468 \u90a3\u4e9b\u6bd2\u5f29\u624b\u7eb7\u7eb7\u6253\u53bb\u3002 \u3000\u3000\u4e00\u5239\u90a3\u95ee\uff0c\u661f\u5149\u98de\u6cfb\uff0c\u5939\u6742\u7740\u9ed1\u8863\u4eba\u7684\u547b\u541f\u4e4b\u58f0\uff0c\u90a3\u4e9b\u5929\u9634\u6559\u7684\u6bd2\u5f29\u624b\uff0c\u4e5f\u4e0d\u662f\u4e0d\u6015\u6b7b \u7684\uff0c\u4e00\u9635\u7eb7\u6270\u4e4b\u540e\uff0c\u6ca1\u6709\u53d7\u4f24\u7684\u6240\u4f59\u65e0\u51e0\uff0c\u4e5f\u90fd\u6492\u817f\u8dd1\u5f97\u8fdc\u8fdc\u7684\u3002 \u3000\u3000\u4ec7\u4e0d\u53ef\u89c1\u4ed6\u4eec\u7684\u4eba\u8d1f\u4f24\u7d2f\u7d2f\uff0c\u8fd9\u4e00\u4ed7\u4e0d\u80fd\u518d\u6253\u4e0b\u53bb\uff0c\u8fde\u52b3\u5c71\u53cc\u9e64\u65e5\u6708\u5934\u9640\u6d1e\u5ead\u53cc\u868a\u4ee5\u53ca \u6234\u9762\u5177\u7684\u4eba\u90fd\u53d7\u4e86\u4f24\uff0c\u771f\u662f\u5929\u9634\u6559\u7a7a\u524d\u672a\u6709\u7684\u60e8\u8d25\uff0c\u4ec7\u4e0d\u53ef\u4ee5\u6781\u6c89\u75db\u7684\u8bed\u8c03\uff0c\u5411\u718a\u501c\u5e38\u6f2b\u5929 \u62f1\u62f1\u624b\u8bf4\uff1a\u201c\u70b9\u82cd\u53cc\u6770\u3001\u718a\u5c0f\u4fa0\uff0c\u4f60\u4eec\u8bf7\u5427\uff01\u5e38\u6f2b\u5929\u592b\u5987\u7adf\u6765\u67b6\u8d77\u8fd9\u4e2a\u6881\u5b50\uff0c\u8001\u592b\u51b3\u62a5\u7980\u672c \u6559\u4e3b\u6559\uff0c\u6539\u65e5\u60e9\u7f5a\u4f60\u4eec\u8fd9\u4e9b\u8086\u65e0\u5fcc\u60ee\u4e4b\u5f92\uff01\u660e\u6625\u6e05\u660e\u8282\uff0c\u628a\u4ee5\u5f80\u6240\u6709\u7684\u8fc7\u8282\uff0c\u90fd\u5728\u541b\u5c71\u4e0b\u4f5c \u4e2a\u6700\u540e\u4e86\u65ad\uff01\u8001\u592b\u51b3\u5fc3\u90a3\u65f6\u5949\u966a\u4f60\u4eec\u4e09\u767e\u62db\uff01\u201d \u3000\u3000\u6563\u82b1\u4ed9\u5b50\u7530\u654f\u654f\u5a07\u7b11\u8bf4\u9053\uff1a\u201c\u9ec4\u886b\u5ba2\uff0c\u4f60\u8bdd\u8bf4\u7684\u5f88\u786c\uff0c\u90a3\u53c8\u4f55\u4e0d\u76ee\u524d\u5c31\u8f83\u91cf\u4e00\u4e0b\u5462\uff1f\u201d \u5e38\u6f2b\u5929\u7acb\u963b\u5979\uff0c\u5411\u4ec7\u4e0d\u53ef\u62f1\u624b\u8fd8\u793c\u8bf4\uff1a\u201c\u8d35\u6559\u8fd9\u4e48\u591a\u7684\u5f13\u5f29\uff0c\u6055\u5e38\u67d0\u592b\u5987\u4e0d\u80fd\u4e0d\u591a\u4f24\u51e0\u4e2a\u4eba \u4e86\uff01\u4ec7\u4e0d\u53ef\u65e2\u5212\u51fa\u9053\u513f\uff0c\u5e38\u67d0\u7109\u80fd\u5931\u7ea6\uff01\u53ea\u662f\u718a\u8001\u5f1f\u8fd8\u6709\u4e2a\u670b\u53cb\u94c1\u80c6\u5c1a\u672a\u660e\uff0c\u8bf7\u8d35\u6559\u4ee5\u793c\u9001 \u56de\uff0c\u514d\u5f97\u518d\u4f24\u548c\u6c14\uff01\u201d \u3000\u3000\u4ec7\u4e0d\u53ef\u795e\u6001\u4ecd\u7136\u505a\u5cb8\u5982\u6545\uff0c\u72c2\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u70b9\u82cd\u53cc\u4fa0\u4f24\u4e86\u6211\u4eec\u8fd9\u4e48\u591a\u5144\u5f1f\uff0c\u548c\u6b66\u6797\u670b \u53cb\uff0c\u4ec7\u67d0\u53c8\u5411\u4f55\u4eba\u8981\u56de\u516c\u9053\uff1f\u5c1a\u5f53\u5bb6\u7684\u4e5f\u662f\u7eff\u6797\u6709\u540d\u7684\u74e2\u628a\u5b50\uff0c\u53ea\u8981\u4ed6\u80af\u771f\u5fc3\u6295\u5165\u672c\u6559\uff0c\u7edd \u4e0d\u4f24\u4ed6\u4e00\u6beb\u4e00\u53d1\uff0c\u5426\u5219\u672c\u6559\u8fd8\u6709\u7eb5\u864e\u5f52\u5c71\uff0c\u81ea\u8d3b\u4f0a\u621a\u4e4b\u7406\uff01\u201d \u3000\u3000\u4ed6\u53c8\u9053\u4e86\u4e00\u58f0\u201c\u518d\u4f1a\u201d\uff0c\u5c31\u548c\u53f8\u793c\u53cc\u7ae5\uff0c\u53bb\u6551\u6cbb\u90a3\u4e9b\u53d7\u4f24\u7684\u4eba\u53bb\u4e86\u3002 \u3000\u3000\u718a\u501c\u548c\u5e38\u6f2b\u5929\u592b\u5987\uff0c\u7531\u6563\u82b1\u4ed9\u5b50\u80cc\u7740\u590f\u82b8\uff0c\u4e00\u540c\u6d88\u5931\u5728\u9ed1\u6697\u91cc\u3002\u8fd9\u7247\u5e7f\u573a\u4e0a\uff0c\u4e00\u5207\u53c8\u5f52 \u4e8e\u5bc2\u9759\uff0c\u53ea\u8bb8\u591a\u4eba\u547b\u541f\u54c0\u547c\u58f0\uff0c\u4e0e\u79cb\u866b\u537f\u537f\u4e4b\u58f0\uff0c\u9065\u76f8\u547c\u5e94\uff0c\u718a\u501c\u8fd9\u624d\u7b2c\u4e8c\u6b21\u4eb2\u8eab\u7ecf\u5386\u4e86\u5929 \u9634\u6559\u7684\u6076\u6bd2\u9634\u9669\u3002 \u3000\u3000\u4ed6\u4e09\u4eba\u4ee5\u6781\u5feb\u7684\u8eab\u6cd5\uff0c\u5954\u56de\u8346\u5dde\u57ce\u5185\uff0c\u5929\u5149\u5df2\u5feb\u5927\u4eae\uff0c\u9042\u627e\u4e86\u4e2a\u5ba2\u5e97\u6b47\u4e86\u4e0b\u6765\uff0c\u4e3a\u590f\u82b8 \u533b\u6cbb\u6240\u53d7\u7684\u4f24\u3002 \u3000\u3000\u718a\u501c\u7684\u5fc3\u60c5\uff0c\u4e3a\u7740\u590f\u82b8\u4e00\u523b\u4e0d\u80fd\u5e73\u9759\uff0c\u4ed6\u7126\u6025\u4e4b\u8272\u6ea2\u4e8e\u7709\u5b57\uff0c\u5176\u5b9e\u7530\u654f\u654f\u4e5f\u975e\u5e38\u7740\u6025 \u5462\u3002 \u3000\u3000\u590f\u82b8\u4f24\u5728\u80cc\u4e0a\uff0c\u6709\u5df4\u638c\u5927\u4e00\u56e2\u7d2b\u9ed1\u8272\u80bf\u5757\uff0c\u5e38\u6f2b\u5929\u4e45\u5386\u6c5f\u6e56\uff0c\u4ed6\u5440\u4e86\u4e00\u58f0\u8bf4\uff1a\u201c\u8fd9\u662f\u6076 \u6bd2\u7684\u9634\u715e\u638c\u4f24\u5475\uff1a\u201c\u718a\u501c\u60ca\u95ee\uff1a\u201c\u600e\u4e48\uff1f\u8fd9\u79cd\u638c\u4f24\u8be5\u600e\u4e48\u533b\u6cbb\u5462\uff1f\u4f1a\u4e0d\u4f1a\u4f24\u53ca\u5185\u8151\uff1f\u5e38\u5927\u54e5 \u8eab\u65c1\u5e26\u6709\u7684\u533b\u4f24\u7684\u836f\u4e48\uff1f\u201d \u3000\u3000\u5e38\u6f2b\u5929\u6050\u718a\u501c\u5fc3\u788e\uff0c\u52c9\u5f3a\u7b11\u4e86\u7b11\uff0c\u4ee4\u7530\u654f\u654f\u5728\u4f24\u52bf\u56db\u5468\u7f13\u7f13\u634b\u6309\u7a74\u9053\uff0c\u76b1\u76b1\u7709\u8bf4\uff1a\u201c\u8fd9 \u81ea\u7136\u4e0d\u662f\u666e\u901a\u4f24\u836f\u6240\u80fd\u7597\u6cbb\u7684\uff0c\u53d7\u4e86\u8fd9\u79cd\u6bd2\u638c\u88ad\u51fb\uff0c\u5bd2\u9634\u4e4b\u6c14\u4fb5\u5165\u9aa8\u9ad3\uff0c\u82e5\u6ca1\u6709\u4e0a\u597d\u7684\u76ca\u6c14 \u6e05\u8840\u4e4b\u836f\u2026\u2026\u201d \u3000\u3000\u718a\u501c\u9053\uff1a\u201c\u9700\u8981\u4ec0\u4e48\u73cd\u8d35\u7684\u836f\uff1f\u201d \u3000\u3000\u5e38\u6f2b\u5929\u9053\uff1a\u201c\u6211\u89c6\u5bdf\u8fc7\u5979\u7684\u8109\u52bf\uff0c\u5979\u53d7\u4f24\u5e76\u4e0d\u91cd\uff0c\u53ea\u8981\u4e00\u4e2a\u6708\u5185\u627e\u5230\u5343\u5e74\u9996\u4e4c\u3001\u6210\u5f62\u8001 \u53c2\u3001\u5929\u5c71\u96ea\u83b2\u7b49\u5176\u4e2d\u4e00\u6837\uff0c\u5c31\u53ef\u4f7f\u5979\u5b8c\u597d\u5982\u521d\u3002\u201d \u3000\u3000\u6563\u82b1\u4ed9\u5b50\u9053\uff1a\u201c\u6211\u770b\u4f60\u6700\u597d\u628a\u5979\u9001\u56de\u843d\u65e5\u9a6c\u573a\u4f11\u517b\u3002\u201d \u3000\u3000\u718a\u501c\u9053\uff1a\u201c\u5c1a\u5927\u54e5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u5e38\u6f2b\u5929\u8bf4\u9053\uff1a\u201c\u4f60\u7559\u5728\u8fd9\u91cc\uff0c\u5148\u4ee5\u771f\u6c14\u538b\u5236\u4f4f\u5979\u7684\u4f24\u52bf\uff0c\u6211\u4e00\u4e2a\u4eba\u5148\u63a2\u542c\u4e00\u4e0b\uff0c\u56de\u6765\u518d \u7814\u7a76\u5bf9\u7b56\u3002\u201d \u3000\u3000\u718a\u501c\u770b\u770b\u590f\u82b8\u82cd\u767d\u7684\u8138\u8272\uff0c\u53ea\u5f97\u70b9\u5934\uff0c\u548c\u6563\u82b1\u4ed9\u5b50\u7559\u4e0b\u7167\u6599\u590f\u82b8\u3002 \u3000\u3000\u5e38\u6f2b\u5929\u518d\u5230\u767d\u51e4\u603b\u5802\u7684\u5927\u5b85\u7b2c\u65f6\uff0c\u53d1\u73b0\u4e3b\u4eba\u5df2\u6362\uff0c\u5929\u9634\u6559\u4eba\u65e9\u5df2\u8d70\u5f97\u65e0\u5f71\u65e0\u8e2a\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u5e38\u6f2b\u5929\u4fbf\u8981\u718a\u501c\u9001\u590f\u82b8\u56de\u843d\u65e5\u9a6c\u573a\uff0c\u81ea\u5df1\u548c\u6563\u82b1\u4ed9\u5b50\u8ffd\u67e5\u5929\u9634\u6559\u8e2a\u8ff9\uff0c\u4f3a\u673a\u6551\u56de \u5c1a\u672a\u660e\u3002 \u3000\u3000\u56db\u4eba\u9eef\u7136\u60dc\u522b\uff0c\u7ea6\u5b9a\u4e5d\u6708\u4e0b\u65ec\u5728\u6b66\u5f53\u5c71\u76f8\u4f1a\u3002 \u3000\u3000\u9a6c\u8e44\u8f7b\u626c\uff0c\u8f66\u8f6e\u8f98\u8f98\uff0c\u843d\u65e5\u9a6c\u573a\u5df2\u7ecf\u9010\u6e10\u63a5\u8fd1\u4e86\u3002 \u3000\u3000\u6108\u63a5\u8fd1\u9a6c\u573a\uff0c\u590f\u82b8\u7684\u5fc3\u60c5\u6108\u5f97\u6109\u5feb\u3002 \u3000\u3000\u4f46\u662f\u718a\u501c\u5374\u5feb\u4e50\u4e0d\u8d77\u6765\uff0c\u4ed6\u5e76\u4e0d\u662f\u4e0d\u5feb\u4e50\uff0c\u53ea\u662f\uff0c\u4ed6\u5fc3\u4e2d\u5ffd\u7136\u5174\u8d77\u4e00\u822c\u83ab\u540d\u7684\u9884\u611f\uff0c\u89c9 \u5f97\u6108\u8fd1\u843d\u65e5\u9a6c\u573a\uff0c\u60b2\u5267\u4e5f\u6108\u6765\u6108\u63a5\u8fd1\u53d1\u751f\u7684\u8fb9\u7f18\u3002 \u3000\u3000\u4e3a\u4ec0\u4e48\u4f1a\u6709\u8fd9\u79cd\u5ff5\u5934\uff1f\u4ed6\u4e0d\u77e5\u9053\uff0c\u4ed6\u552f\u4e00\u77e5\u9053\u7684\uff0c\u5c31\u662f\u4ed6\u7684\u5fc3\u60c5\uff0c\u5b8c\u5168\u8ddf\u5929\u8272\u4e00\u6837\uff0c\u9634 \u6697\u5f02\u5e38\u3002 \u3000\u3000\u8fdc\u5904\u5df2\u51fa\u73b0\u6210\u7fa4\u7684\u9a8f\u9a6c\uff0c\u5929\u8272\u66f4\u9634\u6697\uff0c\u66b4\u96e8\u4eff\u4f5b\u968f\u65f6\u90fd\u4f1a\u843d\u4e0b\u3002 \u3000\u3000\u4e00\u9635\u5f3a\u52b2\u7684\u98ce\u5ffd\u7136\u5377\u8d77\uff0c\u6c99\u5c18\u98de\u626c\uff0c\u9a6c\u5636\u4e0d\u5df2\u3002 \u3000\u3000\u5ffd\u7136\uff0c\u4e00\u5339\u5168\u8eab\u96ea\u767d\u7684\u9a6c\u5411\u7740\u718a\u501c\u7684\u9a6c\u8f66\u6025\u9a70\u800c\u6765\u3002 \u3000\u3000\u201c\u7239\uff01\u201d\u590f\u82b8\u4e00\u770b\u5230\u90a3\u9a6c\uff0c\u5c31\u9ad8\u5174\u5f97\u5927\u53eb\u8d77\u6765\u3002 \u3000\u3000\u718a\u501c\u770b\u5230\u81ea\u9a6c\u4e0a\u7684\u4eba\u65f6\uff0c\u8138\u8272\u5ffd\u7136\u5927\u53d8\u3002 \u3000\u3000\u201c\u4ed6\u662f\u4f60\u7239\uff1f\u201d \u3000\u3000\u201c\u662f\u5440\uff01\u201d \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u59d3\u8428\uff1f\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u7239\u59d3\u8428\uff1f\u201d\u590f\u82b8\u8be7\u5f02\u5730\u95ee\u3002 \u3000\u3000\u201c\u4f60\u8bf4\uff0c\u4f60\u4e3a\u4ec0\u4e48\u59d3\u590f\uff0c\u4e0d\u59d3\u8428\uff1f\u201d\u718a\u501c\u7684\u58f0\u97f3\u975e\u5e38\u7126\u6025\u3002 \u3000\u3000\u201c\u8428\u8ddf\u590f\u58f0\u97f3\u5f88\u8fd1\uff0c\u6240\u4ee5\u6211\u4e00\u5165\u6c5f\u6e56\uff0c\u5c31\u6539\u540d\u53eb\u590f\u82b8\u3002\u201d \u3000\u3000\u718a\u501c\u8138\u8272\u5927\u53d8\uff0c\u5fc3\u4e2d\u601d\u6f6e\u8d77\u4f0f\uff0c\u9a91\u5728\u767d\u9a6c\u4e0a\u7684\u4eba\uff0c\u5c31\u662f\u4ed6\u7684\u4ec7\u4eba\uff0c\u5b9d\u9a6c\u795e\u97ad\u8428\u5929\u9aa5\uff0c\u800c \u590f\u82b8\u504f\u504f\u5c31\u662f\u4ed6\u7684\u513f\u5973\u3002 \u3000\u3000\u201c\u4f60\u600e\u4e48\u4e86\uff1f\u201d\u590f\u82b8\u60ca\u95ee\u3002 \u3000\u3000\u8fd9\u4e2a\u65f6\u5019\uff0c\u5b9d\u9a6c\u795e\u97ad\u7684\u9a6c\u5df2\u7ecf\u7acb\u5728\u9a6c\u8f66\u524d\u9762\u4e86\u3002 \u3000\u3000\u8428\u5929\u9aa5\u770b\u5230\u5973\u513f\uff0c\u8138\u4e0a\u7684\u5174\u594b\u795e\u8272\u8fdc\u6ca1\u6709\u7ef4\u6301\u591a\u4e45\uff0c\u5ffd\u7136\u770b\u5230\u718a\u501c\u6076\u72e0\u72e0\u7684\u76ef\u7740\u4ed6\u770b\uff0c \u8138\u8272\u9a6c\u4e0a\u5927\u53d8\u3002 \u3000\u3000\u72c2\u98ce\u522e\u5f97\u66f4\u6025\uff0c\u6c99\u77f3\u7eb7\u98de\u3002 \u3000\u3000\u8f70\u9686\u9686\uff0c\u5929\u4e0a\u5ffd\u7136\u54cd\u8d77\u4e86\u96f7\u58f0\uff0c\u4e00\u9053\u95ea\u7535\u5728\u4e4c\u4e91\u4e2d\u5212\u8fc7\uff0c\u4eff\u4f5b\u5929\u5ffd\u7136\u5f20\u5f00\u773c\u775b\u4f3c\u7684\u5927\u4eae \u4e86\u4e00\u4e0b\uff0c\u7136\u540e\uff0c\u8c46\u5927\u7684\u96e8\u503e\u76c6\u800c\u4e0b\u3002 \u3000\u3000\u201c\u4f60\u4eec--\u4e00\u201d\u590f\u82b8\u770b\u5230\u4ed6\u4eec\u7684\u795e\u8272\uff0c\u5fc3\u4e2d\u5ffd\u7136\u5174\u4e00\u9635\u6050\u60e7\u3002 \u3000\u3000\u718a\u501c\u5ffd\u7136\u62bd\u51fa\u8d2f\u65e5\u5251\uff0c\u4e00\u7ffb\u8eab\uff0c\u4eba\u5728\u7a7a\u4e2d\u731b\u7136\u5411\u8428\u5929\u9a87\u523a\u53bb\u3002 \u3000\u3000\u201c\u501c\u54e5\u54e5\u2014\u2014\u201d\u590f\u82b8\u4ee5\u6781\u9ad8\u6602\u6781\u53ef\u6015\u7684\u58f0\u97f3\u5927\u53eb\u3002 \u3000\u3000\u201c\u82cd\u7a79\u795e\u5251\uff1f\uff01\u201d\u8428\u5929\u9aa5\u7ad9\u5728\u5730\u4e0a\uff0c\u60ca\u9b42\u7a0d\u5b9a\u7684\u95ee\u3002 \u3000\u3000\u718a\u501c\u4ec0\u4e48\u8bdd\u4e5f\u6ca1\u6709\u8bf4\uff0c\u4e00\u6325\u624b\u4e2d\u5251\uff0c\u53c8\u5411\u8428\u5929\u9aa5\u523a\u53bb\u3002\u718a\u501c\u4e0d\u8bf4\u8bdd\u7684\u539f\u56e0\uff0c\u662f\u6015\u4e00\u8bf4\u4e86 \u8bdd\uff0c\u590f\u82b8\u4e00\u5b9a\u4f1a\u4f7f\u4ed6\u4e27\u5931\u590d\u4ec7\u7684\u51b3\u5fc3\u3002 \u3000\u3000\u4ed6\u53ea\u6709\u731b\u653b\u3002 \u3000\u3000\u4ed6\u53ea\u80fd\u8ba9\u5fc3\u4e2d\u7684\u6068\uff0c\u5316\u6210\u70b9\u70b9\u5251\u82b1\uff0c\u5c04\u5411\u4e0d\u5171\u6234\u5929\u7684\u4ec7\u4eba\u8428\u5929\u9aa5\u3002 \u3000\u3000\u8428\u5929\u9aa5\u62bd\u4ed6\u7684\u795e\u97ad\uff0c\u53ef\u662f\u6ca1\u6709\u4e24\u4e09\u4e0b\uff0c\u8d2f\u65e5\u5251\u5c31\u5df2\u7ecf\u628a\u795e\u97ad\u524a\u6210\u6570\u6bb5\u3002 \u3000\u3000\u96e8\u66f4\u6025\u66f4\u5927\uff0c\u98ce\u66f4\u72c2\u66f4\u66b4\u3002 \u3000\u3000\u718a\u501c\u7684\u6012\u706b\u66f4\u70bd\u3002 \u3000\u3000\u590f\u82b8\u5413\u5f97\u5446\u4f4f\u4e86\u3002 \u3000\u3000\u5ffd\u7136\uff0c\u590f\u82b8\u770b\u5230\u718a\u501c\u6b63\u731b\u529b\u4e00\u5251\u523a\u5411\u8428\u5929\u9aa5\u7684\u80f8\u53e3\u3002 \u3000\u3000\u8428\u5929\u9aa5\u77aa\u77aa\u9000\u540e\u6570\u6b65\uff0c\u624d\u907f\u8fc7\u6740\u7740\uff0c\u8fd9\u65f6\uff0c\u8428\u5929\u9aa5\u7684\u540e\u80cc\uff0c\u5df2\u7ecf\u8d34\u5728\u9a6c\u8f66\u8fb9\u6cbf\u4e0a\uff0c\u718a\u501c \u4e3e\u8d77\u5251\u2014\u2014 \u3000\u3000\u201c\u501c\u54e5\u54e5\u2014\u2014\u201d\u590f\u82b8\u51c4\u5389\u7684\u9ad8\u558a\u3002 \u3000\u3000\u8428\u5929\u9aa5\u95ed\u8d77\u53cc\u773c\u3002 \u3000\u3000\u718a\u501c\u7684\u5251\u706b\u901f\u523a\u53bb\u3002 \u3000\u3000\u590f\u82b8\u5ffd\u7136\u4e00\u4e2a\u7ffb\u8eab\uff0c\u62b1\u4f4f\u8428\u5929\u9aa5\u3002 \u3000\u3000\u4f46\u662f\uff0c\u4e00\u5207\u5df2\u7ecf\u592a\u8fdf\u4e86\u3002 \u3000\u3000\u718a\u501c\u7684\u5251\u5df2\u7ecf\u523a\u7a7f\u4e86\u590f\u82b8\u7684\u80f8\u53e3\uff0c\u76f4\u5165\u8428\u5929\u9aa5\u7684\u5fc3\u810f\u3002 \u3000\u3000\u201c\u82b8\u59b9\u2014\u2014\u201d\u718a\u501c\u6123\u4f4f\uff0c\u624b\u677e\u5f00\uff0c\u5251\u4f9d\u65e7\u63d2\u5728\u4e8c\u4eba\u8eab\u4e0a\u3002 \u3000\u3000\u8f70\u9686\u9686\uff0c\u53c8\u662f\u96f7\u54cd\uff0c\u4e00\u9053\u95ea\u7535\u53c8\u5212\u7834\u4e86\u9ed1\u6697\u3002 \u3000\u3000\u8428\u5929\u9aa5\u7684\u53e3\u89d2\u6709\u8840\u6e17\u51fa\uff0c\u51c4\u7136\u5730\u9732\u51fa\u4e00\u4e2a\u60b2\u60e8\u7684\u7b11\u5bb9\uff0c\u5411\u7740\u718a\u501c\u8bf4\u9053\uff1a\u201c\u5979\u2026\u2026\u5979\u2026\u2026 \u4e0d\u662f\u2026\u2026\u6211\u2026\u2026\u7684\u4eb2\u751f\u5973\u513f\u3002\u201d \u3000\u3000\u718a\u501c\u7684\u5fc3\u4e2d\u54cd\u8d77\u65e0\u6570\u4e2a\u9739\u96f3\uff0c\u4ed6\u60ca\u9a87\u5f97\u5168\u8eab\u53d1\u6296\u3002 \u3000\u3000\u201c\u82b8\u59b9\uff01\u201d \u3000\u3000\u65e0\u8bba\u591a\u51c4\u5389\u7684\u53eb\u58f0\uff0c\u4e5f\u53eb\u4e0d\u9192\u590f\u82b8\u90a3\u4e00\u7f15\u82b3\u9b42\u4e86\u3002 \u3000\u3000\u5f53\u96e8\u70b9\u9010\u6e10\u7ec6\u5c0f\u4e0b\u6765\u65f6\uff0c\u718a\u501c\u5df2\u7ecf\u6316\u597d\u4e24\u5ea7\u65b0\u575f\u3002 \u3000\u3000\u4ed6\u5c06\u590f\u82b8\u7684\u5c38\u4f53\u653e\u5165\u53f3\u8fb9\u7684\u575f\u5751\u5185\uff0c\u6ce8\u89c6\u7740\u5979\u7684\u5bb9\u8c8c\uff0c\u826f\u4e45\uff0c\u624d\u7528\u624b\u628a\u6ce5\u571f\u6162\u6162\u5408\u4e0a\u3002 \u3000\u3000\u7136\u540e\uff0c\u4ed6\u780d\u4e0b\u4e00\u6bb5\u7c97\u5927\u7684\u6811\u5e72\uff0c\u7528\u5251\u524a\u6210\u4e00\u5757\u6728\u677f\uff0c\u5728\u4e0a\u9762\u6162\u6162\u523b\u4e0a\u5b57\u3002 \u3000\u3000\u4ed6\u628a\u523b\u597d\u7684\u6728\u677f\uff0c\u653e\u5728\u4e24\u4e2a\u575f\u5751\u7684\u4e2d\u95f4\u3002 \u3000\u3000\u4ed6\u5f00\u59cb\u60f3\uff1a\u5927\u4ec7\u5df2\u7ecf\u62a5\u4e86\uff0c\u8fd8\u6709\u4ec0\u4e48\u672a\u4e86\u7684\u4e8b\uff1f \u3000\u3000\u501a\u5929\u5251\u548c\u8d2f\u65e5\u5251\u7684\u79d8\u5bc6\uff0c\u4ed6\u6839\u672c\u5c31\u65e0\u610f\u53bb\u77e5\u9053\u3002 \u3000\u3000\u5c1a\u5927\u54e5\u7684\u751f\u6b7b\u4e0b\u843d\u5462\uff1f \u3000\u3000\u5e38\u6f2b\u5929\u592b\u5987\u4e00\u5b9a\u53ef\u4ee5\u6551\u51fa\u5c1a\u5927\u54e5\u7684\u3002 \u3000\u3000\u5929\u9634\u6559\u5462\uff1f \u3000\u3000\u4ee5\u5e38\u6f2b\u5929\u592b\u5987\u7684\u529f\u529b\uff0c\u52a0\u4e0a\u91cd\u5165\u6c5f\u6e56\u7684\u53f7\u53ec\uff0c\u8fdf\u65e9\u603b\u4f1a\u6d88\u706d\u5929\u9634\u6559\u7684\uff0c\u6bd5\u7adf\u90aa\u6076\u7edd\u5bf9\u6218 \u80dc\u4e0d\u4e86\u6b63\u4e49\u3002 \u3000\u3000\u8fd8\u6709\u4ec0\u4e48\u4e8b\uff1f\u6c5f\u6e56\u4e0a\u7684\u6069\u6028\uff0c\u5bf9\u4ed6\u8bf4\u6765\uff0c\u5df2\u7ecf\u4e86\u65e0\u610f\u4e49\u3002 \u3000\u3000\u590f\u82b8\u5df2\u7ecf\u6b7b\u4e86\uff0c\u6c5f\u6e56\u8fd8\u6709\u4f55\u6b22\u4e50\uff1f \u3000\u3000\u4ed6\u51c4\u7136\u4e00\u7b11\uff0c\u62ac\u5934\u671b\u5929\uff0c\u5929\u8272\u5ffd\u7136\u8f6c\u6674\uff0c\u9732\u51fa\u4e00\u62b9\u84dd\u8272\uff0c\u5927\u5730\u53c8\u590d\u82cf\u4e86\uff0c\u7136\u800c\uff0c\u5bf9\u4ed6\u6765 \u8bf4\uff0c\u5e76\u4e0d\u4ee3\u8868\u4efb\u4f55\u610f\u4e49\u3002 \u3000\u3000\u4e8e\u662f\u4e00\u4e00\u4e00 \u3000\u3000\u4ed6\u7eb5\u8eab\u4e00\u8df3\uff0c\u8df3\u5165\u590f\u82b8\u5de6\u8fb9\u7684\u575f\u5751\u5185\u3002 \u3000\u3000\u4ed6\u62ff\u8d77\u8d2f\u65e5\u5251\uff0c\u5411\u81ea\u5df1\u8116\u5b50\u4e0a\u4e00\u62b9\u4e00\u5728\u96e8\u540e\u65b0\u9701\u6e5b\u84dd\u5927\u7a7a\u4e0b\uff0c\u4e24\u4e2a\u65b0\u575f\u9ed8\u9ed8\u5806\u5728\u5927\u5730 \u4e0a\uff0c\u65b0\u575f\u4e2d\u95f4\uff0c\u523b\u7740\u4e24\u884c\u5b57\uff1a \u3000\u3000\u4ea1\u59bb\u82b8 \u3000\u3000\u4ea1\u592b\u501c\u4e4b\u5893 \u3000\u3000\uff08\u5168\u4e66\u5b8c\uff09"}),showOnHomepage:!0}]}}]);