"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[19720],{3244:(e,n,s)=>{s.r(n),s.d(n,{default:()=>a});var t=s(74848);const a=[{name:"\u8fd8\u5251\u5947\u60c5\u5f55",content:(0,t.jsx)(t.Fragment,{children:"\u7b2c\u4e00\u56de\u3000\u5251\u5f71\u6b4c\u58f0 \u3000\u3000\u843d\u65e5\u4f59\u971e\u6563\u7eee\uff0c\u665a\u98ce\u5439\u9001\u8f7b\u6b4c\uff0c\u6b4c\u58f0\u5982\u6028\u5982\u6155\uff0c\u5982\u6ce3\u5982\u8bc9\uff0c\u6295\u6797\u5026\u9e1f\uff0c\u4e5f\u4f3c\u4e3a\u8fd9\u6b4c\u58f0\u76d8 \u65cb\uff0c\u5728\u6797\u5b50\u4e0a\u7a7a\u56de\u7fd4\u4e0d\u4e0b\uff1b\u4f46\u7740\u51c4\u5a49\u7684\u6b4c\u58f0\uff0c\u5374\u7559\u4e0d\u4f4f\u5c71\u8c37\u4e2d\u4e00\u5339\u7edd\u5c18\u800c\u53bb\u7684\u9a8f\u9a6c\u3002 \u3000\u3000\u9a6c\u4e0a\u7684\u9a91\u5ba2\u662f\u4e00\u4e2a\u767d\u8863\u5c11\u5e74\uff0c\u4ed6\u4f55\u5c1d\u4e0d\u77e5\u9053\u540e\u9762\u8fd9\u4e2a\u7b56\u9a6c\u8ffd\u8e2a\u7684\u5c11\u5973\u662f\u4e3a\u4ed6\u800c\u6b4c\uff0c\u4f46\u4ed6 \u8fd8\u662f\u72e0\u4e86\u5fc3\u80a0\uff0c\u7eb5\u9a6c\u72c2\u5954\uff0c\u76f4\u5230\u6b4c\u58f0\u6d88\u6563\uff0c\u4f46\u89c1\u7a7a\u5c71\u5bc2\u5bc2\uff0c\u66ae\u972d\u6c89\u6c89\u4e4b\u9645\uff0c\u8fd9\u624d\u8c13\u7136\u53f9\u606f\uff0c \u6717\u58f0\u541f\u9053\uff1a\u201c\u6613\u6c34\u8427\u8427\u897f\u98ce\u51b7\uff0c\u58ee\u58eb\u4e00\u53bb\u4e0d\u590d\u8fd8\uff01\u62fc\u6b7b\u4f46\u51ed\u4e09\u5c3a\u5251\uff0c\u6df1\u60c5\u552f\u6709\u8d1f\u7ea2\u989c\uff01\u201d\u52d2 \u9a6c\u56de\u5934\uff0c\u540e\u9762\u6773\u65e0\u4eba\u5f71\uff0c\u4ed6\u7684\u9a6c\u662f\u4e00\u5339\u9010\u7535\u9a70\u98ce\u7684\u5b9d\u9a6c\uff0c\u8fd9\u4e00\u9635\u72c2\u5954\uff0c\u65e9\u5df2\u628a\u90a3\u5c11\u5973\u9694\u5728\u51e0 \u91cd\u5c71\u5916\u4e86\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u540d\u53eb\u9648\u7384\u673a\uff0c\u4ed6\u8d1f\u4e86\u5e08\u53cb\u7684\u91cd\u6258\uff0c\u8981\u53bb\u523a\u6740\u4e00\u4e2a\u5728\u8d3a\u5170\u5c71\u9690\u59d3\u57cb\u540d\u6b66\u529f\u9ad8\u7edd\u7684\u9ad8 \u624b\uff0c\u4fee\u8bf4\u4ed6\u5bf9\u90a3\u5c11\u5973\u672c\u5c31\u65e0\u5fc3\uff0c\u5373\u7b97\u662f\u6709\u539a\u610f\u6df1\u60c5\uff0c\u6b64\u5373\u6b64\u65f6\uff0c\u4e5f\u51b3\u4e0d\u80fd\u4e3a\u8fd9\u6b4c\u58f0\u6240\u963b\u3002 \u3000\u3000\u7136\u800c\u90a3\u6b4c\u58f0\u8fd8\u662f\u62e8\u52a8\u4e86\u4ed6\u7684\u5fc3\u5f26\uff0c\u53ef\u60dc\u90a3\u5c11\u5973\u963b\u5728\u51e0\u91cd\u5c71\u5916\uff0c\u542c\u4e0d\u5230\u4ed6\u90a3\u4e00\u58f0\u957f\u53f9\uff0c\u770b \u4e0d\u5230\u4ed6\u773c\u89d2\u90a3\u4e24\u6ef4\u6676\u83b9\u7684\u6cea\u73e0\u3002 \u3000\u3000\u65e5\u843d\u98ce\u5bd2\uff0c\u9ec4\u660f\u7684\u666f\u8272\u8d8a\u6765\u8d8a\u6d53\u4e86\u3002\u9648\u7384\u673a\u62ac\u5934\u4e00\u770b\u8d3a\u5170\u5c71\u7684\u4e3b\u5cf0\u5df2\u9690\u9690\u5728\u671b\uff0c\u5fc3\u4e2d\u4e0d \u7531\u7684\u4e00\u9635\u7d27\u5f20\uff0c\u7acb\u5373\u62e8\u8f6c\u9a6c\u5934\uff0c\u626c\u97ad\u897f\u8fdb\u3002 \u3000\u3000\u8dd1\u51fa\u8c37\u53e3\uff0c\u767b\u4e0a\u4e86\u4e00\u6761\u5d0e\u5c96\u7684\u5c71\u9053\uff0c\u9648\u7384\u673a\u5fc3\u91cc\u8e0c\u8e87\uff0c\u4ed6\u7684\u5750\u9a91\u867d\u8bf4\u662f\u4e00\u5339\u5b9d\u9a6c\uff0c\u4f46\u5728 \u8fd9\u9669\u9661\u7684\u5c71\u8def\u591c\u884c\uff0c\u5f3a\u654c\u53c8\u5728\u9644\u8fd1\uff0c\u7a76\u7adf\u4e0d\u80fd\u65e0\u6240\u987e\u5fcc\u6b63\u81ea\u62ff\u4e0d\u5b9a\u4e3b\u610f\uff0c\u5ffd\u542c\u5f97\u5feb\u9a6c\u98de\u9a70\u7684 \u6025\u805a\u8e44\u58f0\uff0c\u500f\u5ffd\u4e4b\u95f4\uff0c\u4fbf\u5230\u8ddf\u524d\uff0c\u773c\u770b\u7740\u4e24\u5339\u9a6c\u5934\u4fbf\u8981\u95ef\u5728\u4e00\u8d77\uff0c\u524d\u9762\u90a3\u5339\u9a6c\u7684\u9a91\u5ba2\uff0c\u4e00\u4e2a \u7ffb\u8eab\uff0c\u8df3\u4e0b\u9a6c\u80cc\uff0c\u4f38\u624b\u4e00\u62e6\uff0c\u9648\u7384\u673a\u90a3\u5339\u5b9d\u9a6c\uff0c\u4e00\u58f0\u957f\u5636\uff0c\u524d\u8e44\u4eba\u7acb\uff0c\u7adf\u662f\u95ef\u4e0d\u8fc7\u53bb\u3002\u5728\u8fd9 \u4e00\u77ac\u4e4b\u95f4\uff0c\u9648\u7384\u673a\u4e5f\u5df2\u8df3\u4e0b\u9a6c\u6765\uff0c\u4f46\u89c1\u6234\u7740\u9a6c\u5934\u7684\u662f\u4e00\u4e2a\u6d53\u7709\u5927\u773c\u7684\u7c97\u8c6a\u5c11\u5e74\uff0c\u4e00\u5f20\u9762\u5b54\u51b7 \u68ee\u68ee\u7684\u6beb\u65e0\u8868\u60c5\uff0c\u5728\u9ec4\u660f\u666f\u8272\u4e4b\u4e2d\uff0c\u66f4\u663e\u5f97\u9634\u6c89\u6050\u6016\u3002 \u3000\u3000\u9648\u7384\u673a\u6014\u4e86\u4e00\u6014\uff0c\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u4e0a\u5b98\u5144\uff0c\u5e78\u4f1a\u5e78\u4f1a\u3002\u201d\u90a3\u7c97\u8c6a\u5c11\u5e74\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u51b7\u51b7 \u8bf4\u9053\uff1a\u201c\u662f\u5440\uff0c\u7aef\u7684\u662f\u5e78\u4f1a\u4e86\u3002\u97f5\u5170\u5462\uff1f\u201d\u9648\u7384\u673a\u9053\uff1a\u201c\u5979\u5728\u540e\u9762\uff0c\u4f60\u7a7f\u8fc7\u8fd9\u4e2a\u5c71\u8c37\uff0c\u4e5f\u8bb8 \u5c31\u80fd\u89c1\u7740\u3002\u201d\u90a3\u5c11\u5e74\u5251\u7709\u4e00\u626c\uff0c\u8138\u8272\u8d8a\u53d1\u9634\u68ee\uff0c\u9053\uff1a\u201c\u90a3\u4e48\u5979\u662f\u8ffd\u7740\u4f60\u6765\u4e86\uff1f\u201d\u9648\u7384\u673a\u8138\u4e0a \u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u4e0a\u5b98\u5144\u4f11\u5f97\u53d6\u7b11\u3002\u201d\u90a3\u5c11\u5e74\u52c3\u7136\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u8c01\u548c\u4f60\u8bf4\u7b11\uff0c\u6211\u53ea\u95ee\u4f60\uff0c\u4f60\u662f \u8981\u5979\u8fd8\u662f\u4e0d\u8981\uff1f\u201d \u3000\u3000\u9648\u7384\u673a\u53eb\u9053\uff1a\u201c\u4e0a\u5b98\u5144\uff0c\u8fd9\u8bdd\u662f\u6253\u90a3\u91cc\u8bf4\u8d77\uff1f\u6211\u5bf9\u97f5\u5170\u59d0\u59d0\u4ece\u6765\u6ca1\u6709\u8d77\u8fc7\u5f02\u5fc3\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u53ea\u662f\u5bf9\u5979\u620f\u5f04\uff0c\u5f15\u8bf1\u4e86\u5979\uff0c\u5982\u4eca\u53c8\u5c06\u5979\u7529\u4e86\uff1f\u201d \u3000\u3000\u9648\u7384\u673a\u8138\u4e0a\u53d8\u8272\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u4e0a\u5b98\u5144\uff0c\u4f60\u628a\u5c0f\u5f1f\u770b\u4f5c\u4f55\u7b49\u6837\u4eba\uff1f\u6211\u5bf9\u97f5\u5170\u53ea\u6709\u59d0\u5f1f\u7684 \u60c5\u8c0a\uff0c\u90a3\u8c08\u5f97\u4e0a\u4ec0\u4e48\u620f\u5f04\uff0c\u5f15\u8bf1\uff1f\u201d\u90a3\u5c11\u5e74\u51b7\u7b11\u9053\uff1a\u201c\u4f9d\u4f60\u8bf4\u7adf\u662f\u97f5\u5170\u5f15\u8bf1\u4f60\u4e86\uff1f\u201d\u9648\u7384\u673a \u7709\u5934\u4e00\u76b1\uff0c\u8427\u97f5\u5170\u786e\u662f\u7ea0\u7f20\u4e8e\u4ed6\uff0c\u4f46\u82e5\u4f9d\u5b9e\u8bf4\u6765\uff0c\u5c82\u4e0d\u4f24\u4e86\u5979\u5c11\u5973\u7684\u540d\u8a89\u3002 \u3000\u3000\u90a3\u590d\u59d3\u4e0a\u5b98\uff0c\u53cc\u540d\u5929\u91ce\u7684\u5c11\u5e74\u8ffd\u4e0a\u4e24\u6b65\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u9648\u7384\u673a\uff0c\u4f60\u7ed9\u6211\u56de\u53bb\uff01\u201d\u9648\u7384\u673a \u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u4f60\u5bf9\u97f5\u5170\u966a\u4e2a\u4e0d\u662f\uff0c\u53d1\u8a93\u4ece\u4eca\u4ee5\u540e\uff0c\u6c38\u4e0d\u8d1f\u5979\uff01\u6211\u7ed9\u4f60\u76d1 \u8a93\uff0c\u4e0d\u51c6\u80cc\u76df\u3002\u201d\u7c97\u8c6a\u7684\u8bdd\u8bed\u4e00\u53d8\u800c\u4e3a\u5f02\u6837\u7684\u51c4\u51c9\uff0c\u7adf\u597d\u50cf\u662f\u5411\u9648\u7384\u673a\u54c0\u6c42\u8d77\u6765\u4e86\u3002 \u3000\u3000\u9648\u7384\u673a\u518d\u9000\u4e86\u4e24\u6b65\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4e0a\u5b98\u5144\uff0c\u6211\u660e\u767d\u4f60\u7684\u5fc3\u610f\uff0c\u4f60\u559c\u6b22\u97f5\u5170\u59d0\u59d0\uff0c\u4f55\u82e6\u95f7 \u5728\u5fc3\u5934\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b63\u56e0\u4e3a\u5979\u662f\u6211\u559c\u6b22\u7684\u4eba\uff0c\u6211\u51b3\u4e0d\u80fd\u89c1\u5979\u4f24\u5fc3\uff0c\u51b3\u4e0d\u80fd\u89c1\u4f60 \u5c06\u5979\u629b\u5f03\uff01\u201d\u9648\u7384\u673a\u82e6\u7b11\u9053\uff1a\u201c\u6211\u4f46\u613f\u505a\u4e2a\u7a7f\u9488\u5f15\u7ebf\u7684\u7ea2\u5a18\uff0c\u5374\u4e0d\u662f\u5f04\u7434\u5bc4\u7b80\u7684\u5f20\u541b\u745e\u3002\u6211 \u8877\u5fc3\u795d\u4f60\u4eec\u6210\u5c31\u7f8e\u6ee1\u59fb\u7f18\u3002\u4e0a\u5b98\u5144\uff0c\u4f60\u4f55\u5fc5\u6709\u6240\u731c\u7591\uff0c\u4ee4\u5c0f\u5f1f\u96be\u582a\uff01\u201d \u3000\u3000\u9648\u7384\u673a\u81ea\u4ee5\u4e3a\u8fd9\u662f\u638f\u5fc3\u5256\u8179\u4e4b\u8a00\uff0c\u5c82\u77e5\u666e\u5929\u4e4b\u4e0b\u7684\u5355\u601d\u7537\u5b50\uff0c\u65e0\u4e0d\u628a\u5bf9\u65b9\u89c6\u4f5c\u4e0d\u53ef\u4eb5\u6e0e \u7684\u4ed9\u5973\uff0c\u4f55\u51b5\u662f\u4e0a\u5b98\u5929\u91ce\u8fd9\u6837\u5fc3\u9ad8\u6c14\u50b2\u7684\u4eba\uff0c\u4ed6\u4e00\u542c\u9648\u7384\u673a\u7684\u8bf4\u8bdd\uff0c\u7adf\u4f3c\u628a\u4ed6\u5c0a\u656c\u5230\u4e86\u6781\u70b9 \u7684\u4eba\u5f53\u505a\u4e00\u4ef6\u53ef\u4ee5\u2018\u51fa\u8ba9\u2019\u7684\u8d27\u7269\uff0c\u5df2\u662f\u6012\u4e0d\u53ef\u6291\uff0c\u66f4\u4f55\u51b5\u9648\u7384\u673a\u867d\u7136\u8bf4\u5f97\u8bda\u6073\uff0c\u5728\u4ed6\u542c \u6765\uff0c\u5374\u8ba4\u4f5c\u662f\u2018\u80dc\u5229\u8005\u2019\u7684\u5632\u5f04\u3002\u8fd9\u79cd\u5355\u601d\u75c5\u60a3\u8005\u7684\u5fae\u5999\u5fc3\u7406\uff0c\u9648\u7384\u673a\u90a3\u80fd\u61c2\u5f97\uff1f \u3000\u3000\u4f46\u89c1\u4e0a\u5b98\u5929\u91ce\u9762\u8272\u4e00\u6c89\uff0c\u53cc\u76ee\u500f\u5f20\uff0c\u5386\u58f0\u559d\u9053\uff1a\u201c\u9648\u7384\u673a\uff0c\u5e9f\u8bdd\u5c11\u8bf4\uff0c\u4f60\u56de\u4e0d\u56de\u53bb\uff1f\u201d \u9648\u7384\u673a\u4e00\u671b\u5929\u8272\uff0c\u5fc3\u4e2d\u70e6\u607c\u4e4b\u6781\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5144\u4e0d\u8c05\uff0c\u5f1f\u4e5f\u65e0\u8a00\u3002\u4f46\u5c0f\u5f1f\u6709\u4e8b\u5728\u8eab\uff0c\u4f46\u6c42\u6211 \u5144\u8ba9\u8def\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u4f46\u542c\u5f97\u5f97\u970d\u7684\u4e00\u58f0\uff0c\u4e0a\u5b98\u5929\u91ce\u62d4\u51fa\u4e86\u4e00\u5bf9\u62a4\u624b\u94a9\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u6211\u504f \u4e0d\u653e\u8fc7\u4f60\u8fd9\u65e0\u60c5\u65e0\u4e49\u7684\u7537\u5b50\uff01\u201d \u3000\u3000\u9648\u7384\u673a\u90a3\u6709\u5fc3\u60c5\u4e89\u6597\uff0c\u5fc3\u4e2d\u6697\u9a82\uff1a\u201c\u6211\u6709\u60c5\u65e0\u60c5\uff0c\u5e72\u4f60\u4f55\u4e8b\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u53cc\u94a9\u4e00\u4e2a\u76d8 \u65cb\uff0c\u91d1\u5149\u95ea\u95ea\uff0c\u8ffd\u5230\u9762\u95e8\uff0c\u559d\u9053\uff1a\u201c\u8fd8\u4e0d\u4eae\u5251\u4e48\uff1f\u201d\u9648\u7384\u673a\u98de\u8eab\u95ea\u8fc7\uff0c\u53eb\u9053\uff1a\u201c\u4e0a\u5b98\u5144\u4e14 \u6162\uff0c\u542c\u5f1f\u4e00\u8a00\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u51b7\u7b11\u9053\uff1a\u201c\u6709\u4f55\u5e9f\u8bdd\uff1f\u5c1a\u5f85\u591a\u8a00\u3002\u201d\u9648\u7384\u673a\u9053\uff1a\u201c\u543e\u5144\u5b9a\u8981\u8d50\u6559\uff0c\u5c0f\u5f1f\u539f\u4e0d\u6562 \u63a8\u8f9e\u3002\u53ea\u662f\u4eca\u65e5\u5b9e\u662f\u6709\u4e8b\u5728\u8eab\uff0c\u5341\u65e5\u4e4b\u540e\uff0c\u82e5\u662f\u5230\u671f\u5c0f\u5f1f\u4e0d\u6765\uff0c\u90a3\u5c31\u662f\u5c0f\u5f1f\u5df2\u88ab\u4eba\u6240\u6740\uff0c\u4e0d \u5fc5\u518d\u52b3\u543e\u5144\u8d35\u624b\u4e86\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u542c\u4ed6\u8bf4\u5f97\u5947\u602a\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u968f\u5373\u559d\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u529f\u592b\uff0c\u6211\u5c31\u6709\u529f\u592b\u7b49\u4f60\u5417\uff1f\u5feb \u5feb\u52a8\u624b\uff0c\u80dc\u8d25\u7acb\u51b3\uff0c\u514d\u5f97\u97f5\u5170\u6765\u4e86\u4f24\u5fc3\u3002\u201d\u53cc\u94a9\u4e00\u5206\uff0c\u4e00\u62db\u2018\u7535\u7ffc\u6469\u4e91\u2019\uff0c\u5de6\u53f3\u5408\u56f4\uff0c\u9648\u7384 \u673a\u4e0d\u5f97\u5df2\u62d4\u5251\u76f8\u8fce\uff0c\u4f46\u542c\u5f97\u53ee\u5f53\u4e24\u58f0\uff0c\u94a9\u5251\u76f8\u4ea4\uff0c\u9648\u7384\u673a\u7684\u5251\u51e0\u4e4e\u7ed9\u4ed6\u593a\u51fa\u624b\u53bb\u3002 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u97f5\u5170\u5c06\u4f60\u7684\u5251\u6cd5\u6367\u4e0a\u4e09\u5341\u4e09\u5929\uff0c\u539f\u6765\u4e0d\u8fc7\u5982\u65af\uff01\u201d\u9648\u7384\u673a\u53c8\u597d\u6c14 \u53c8\u597d\u7b11\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4f60\u4e0d\u8fc7\u60f3\u8d4c\u4e00\u53e3\u6c14\uff0c\u6211\u4fbf\u8ba9\u4f60\u4f55\u9632\uff1f\u201d\u957f\u5251\u4e00\u6296\uff0c\u8fd8\u4e86\u4e00\u62db\uff0c\u62bd\u7a7a\u4fbf \u60f3\u94bb\u51fa\u3002\u90a3\u77e5\u4e0a\u5b98\u5929\u91ce\u7684\u5434\u94a9\u517c\u6709\u94a9\u5251\u4e4b\u957f\uff0c\u4e00\u5360\u4e0a\u98ce\uff0c\u540e\u7740\u7ef5\u7ef5\u4e0d\u65ad\uff0c\u94a9\u5149\u95ea\u95ea\uff0c\u7adf\u628a\u9648 \u7384\u673a\u7684\u9000\u8def\u5168\u90fd\u5c01\u4f4f\uff0c\u54ea\u80fd\u8f7b\u6613\u8131\u8eab\uff1f \u3000\u3000\u5929\u8fb9\u7684\u665a\u972d\u6162\u6162\u6d88\u892a\uff0c\u591c\u8272\u66f4\u6d53\u4e86\u3002\u5ffd\u542c\u5f97\u540e\u9762\u8e44\u58f0\u5f97\u5f97\uff0c\u9690\u9690\u53ef\u95fb\uff0c\u9648\u7384\u673a\u5fc3\u9053\uff1a \u201c\u6b64\u65f6\u4e0d\u95ef\u8fc7\u53bb\uff0c\u97f5\u5170\u4e00\u6765\uff0c\u90a3\u5c31\u66f4\u9ebb\u70e6\u4e86\uff01\u201d\u9661\u7684\u7cbe\u795e\u4e00\u632f\uff0c\u957f\u5251\u4e00\u5708\uff0c\u8eab\u968f\u5251\u52bf\uff0c\u6ef4\u6e9c \u6e9c\u7684\u8f6c\u4e86\u534a\u4e2a\u5706\u5708\uff0c\u4f46\u89c1\u56db\u9762\u516b\u65b9\uff0c\u5251\u5149\u98d8\u98de\uff0c\u4e0a\u5b98\u5929\u91ce\u5403\u4e86\u4e00\u60ca\uff0c\u60f3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u5170\u59b9\u4f1a \u559c\u6b22\u8fd9\u4e2a\u81ed\u5c0f\u5b50\uff0c\u539f\u6765\u679c\u771f\u6709\u70b9\u771f\u529f\u592b\uff01\u201d\u6025\u80dc\u4e4b\u5ff5\u4e00\u8d77\uff0c\u53cc\u94a9\u970d\u970d\uff0c\u62db\u6570\u51cc\u5389\u65e0\u524d\u3002 \u3000\u3000\u9a6c\u8e44\u58f0\u81ea\u8fdc\u800c\u8fd1\uff0c\u9648\u7384\u673a\u53cd\u624b\u4e00\u5251\uff0c\u5c06\u4e0a\u5b98\u5929\u91ce\u7684\u53cc\u94a9\u8feb\u624b\u4e00\u4fa7\uff0c\u8fc8\u524d\u4e00\u6b65\uff0c\u4f4e\u58f0\u559d \u9053\uff1a\u201c\u8fd8\u4e0d\u8ba9\u8def\uff01\u201d\u591c\u8272\u82cd\u832b\u4e2d\uff0c\u90a3\u5339\u9a6c\u5df2\u5954\u51fa\u5c71\u8170\uff0c\u9a6c\u4e0a\u5c11\u5973\u626c\u58f0\u53eb\u9053\uff1a\u201c\u7384\u673a\uff0c\u4f60\u548c\u8c01 \u52a8\u624b\uff1f\u55ef\uff0c\u4ec0\u4e48\uff0c\u662f\u5929\u91ce\u5417\uff1f\u4f60\u4eec\u8fd8\u4e0d\u8d76\u5feb\u7ed9\u6211\u4f4f\u624b\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u53eb\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u4e0d\u80af\u89c1\u4f60\uff0c\u5f85\u6211\u64d2\u4ed6\u7ed9\u4f60\u4fbf\u662f\uff01\u201d\u9648\u7384\u673a\u90a3\u4e00\u5251\u5df2\u628a\u53cc\u94a9\u5c01\u5230 \u5916\u5708\uff0c\u4f46\u4e0a\u5b98\u5929\u91ce\u575a\u4e0d\u53ef\u9000\uff0c\u5c71\u8def\u72ed\u7a84\uff0c\u4e0d\u4e0b\u6740\u624b\uff0c\u5c06\u4ed6\u51fb\u5012\uff0c\u5b9e\u662f\u96be\u4ee5\u593a\u8def\u5916\u95ef\uff0c\u4e3b\u610f\u672a \u51b3\uff0c\u5ffd\u542c\u5f97\u4e0a\u5b98\u5929\u91ce\u4e4b\u8a00\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u6211\u82e5\u5728\u97f5\u5170\u9762\u524d\u5c06\u4ed6\u523a\u5012\uff0c\u4ed6\u4eec\u7684\u59fb\u7f18\u5c31\u6c38 \u65e0\u64ae\u5408\u4e4b\u671b\u4e86\uff01\u201d \u3000\u3000\u9ad8\u624b\u6bd4\u5212\uff0c\u53ea\u4e89\u77ac\u606f\u4e4b\u95f4\uff0c\u90a3\u8bb8\u72b9\u7591\uff0c\u500f\u7136\u95f4\uff0c\u5ffd\u89c1\u94a9\u5149\u4e00\u95ea\uff0c\u4e0a\u5b98\u5929\u91ce\u4e24\u6746\u91d1\u94a9\u8131\u624b \u800c\u51fa\uff0c\u2018\u767b\u2019\u7684\u4e00\u638c\u62cd\u4e0b\uff0c\u6b63\u4e2d\u9648\u7384\u673a\u80f8\u53e3\u8981\u5bb3\uff0c\u4fbf\u542c\u5f97\u9648\u7384\u673a\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u8dcc\u51fa\u4e00\u4e08\u5f00 \u5916\u3002 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u8fd9\u4e00\u62db\u672c\u662f\u8d25\u4e2d\u6c42\u80dc\u4e4b\u62db\uff0c\u629b\u94a9\u88ad\u654c\uff0c\u6325\u638c\u51fb\u4eba\uff0c\u867d\u8bf4\u795e\u5999\u975e\u5e38\uff0c\u4f46\u4ee5\u9648\u7384\u673a \u90a3\u8d85\u5353\u7684\u6b66\u529f\uff0c\u4f30\u91cf\u6700\u591a\u53ea\u80fd\u5c06\u4ed6\u51fb\u9000\uff0c\u633d\u56de\u9762\u5b50\uff0c\u4e07\u4e07\u6599\u4e0d\u5230\u4ed6\u7adf\u4f3c\u4e0d\u52a0\u9632\u5907\uff0c\u7adf\u7ed9\u81ea\u5df1 \u4e00\u638c\u51fb\u4e2d\u80f8\u819b\u3002\u8fd9\u5239\u90a3\u95f4\uff0c\u4e0a\u5b98\u5929\u91ce\u4e5f\u4e0d\u7981\u5446\u4e86\u3002\u53ea\u542c\u5f97\u8427\u97f5\u5170\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u5929\u91ce\uff0c\u5929\u91ce\u4f60 \u5e72\u4ec0\u4e48\uff1f\u4f60\u600e\u80fd\u4e0b\u8fd9\u4e2a\u91cd\u624b\u3002\u5feb\uff0c\u5feb\uff0c\u4f60\u8fd8\u4e0d\u5feb\u628a\u4ed6\u6276\u8d77\u6765\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u5b9a\u4e86\u5b9a\u795e\uff0c\u521a\u521a\u8fc8\u51fa\u811a\u6b65\uff0c\u9661\u542c\u5f97\u4e00\u58f0\u9a6c\u5636\uff0c\u4e00\u6761\u9ed1\u5f71\u51cc\u7a7a\u98de\u8d77\uff0c\u4e0a\u5b98\u5929\u91ce\u600e \u4e48\u4e5f\u6599\u60f3\u4e0d\u5230\u9648\u7384\u673a\u53d7\u4e86\u91cd\u4f24\uff0c\u5c45\u7136\u8fd8\u80fd\u98de\u8eab\u4e0a\u9a6c\uff0c\u4f46\u89c1\u4ed6\u53cd\u624b\u4e00\u62cd\u9a6c\u81c0\uff0c\u968f\u5373\u4f4e\u547c\u4e00\u58f0\uff0c \u90a3\u58f0\u97f3\u90c1\u95f7\u4e4b\u6781\uff0c\u4f3c\u662f\u53d7\u4f24\u4e4b\u540e\uff0c\u6de4\u8840\u5df2\u585e\u5230\u54bd\u5589\uff0c\u4e0a\u5b98\u5929\u91ce\u98de\u8eab\u75be\u63a0\uff0c\u4e00\u624b\u6293\u53bb\uff0c\u79bb\u4e86\u9a6c \u5c3e\u4e09\u5bf8\uff0c\u6ca1\u6709\u6293\u7740\uff0c\u53ea\u89c1\u9648\u7384\u673a\u7d27\u62b1\u9a6c\u9888\uff0c\u6574\u4e2a\u8eab\u5b50\u4fef\u4f0f\u5728\u9a6c\u80cc\u4e0a\uff0c\u8fd9\u5339\u9a6c\u662f\u4e45\u7ecf\u8bad\u7ec3\u7684\u6218 \u9a6c\uff0c\u88ab\u4e3b\u4eba\u4e00\u50ac\uff0c\u653e\u5f00\u56db\u8e44\u75be\u8dd1\uff0c\u4e0a\u5b98\u5929\u91ce\u4e00\u6293\u6ca1\u6709\u6293\u7740\u8fd9\u5339\u9a6c\u5df2\u8f6c\u8fc7\u5c71\u5773\u53bb\u4e86\u3002\u5c31\u5728\u8fd9\u4e00 \u77ac\u95f4\uff0c\u53ea\u542c\u5f97\u2018\u5530\u2019\u7684\u9a6c\u97ad\u4e00\u54cd\uff0c\u8427\u97f5\u5170\u98de\u9a6c\u8d76\u5230\uff0c\u4e00\u97ad\u7529\u4e0b\uff0c\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u8ba9\u5f00\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u70ed\u8840\u4e0a\u6d8c\uff0c\u540e\u6094\u7f9e\u6127\uff0c\u5992\u6068\u6c14\u607c\uff0c\u79cd\u79cd\u60c5\u7eea\uff0c\u7ea0\u7ed3\u5fc3\u5934\uff0c\u4ed6\u8fd9\u6837\u7684\u4e3a\u7740\u8427\u97f5 \u5170\uff0c\u8427\u97f5\u5170\u7adf\u7528\u9a6c\u97ad\u62bd\u4ed6\uff01\u4ed6\u60f3\u628a\u8427\u97f5\u5170\u62c9\u4e0b\u9a6c\u6765\uff0c\u4ed6\u60f3\u6253\u8427\u97f5\u5170\u7684\u8033\u5149\uff0c\u4ed6\u60f3\u62b1\u7740\u8427\u97f5\u5170 \u75db\u54ed\uff0c\u7136\u800c\u4ed6\u8fd8\u662f\u8ba9\u8427\u97f5\u5170\u8fc7\u53bb\u4e86\uff0c\u800c\u4e14\u4ed6\u8fd8\u8eab\u4e0d\u7531\u5df1\u7684\u8ffd\u5728\u8427\u97f5\u5170\u7684\u9a6c\u540e\u3002 \u3000\u3000\u6c89\u6c89\u591c\u8272\uff0c\u5c71\u77f3\u5d99\u5ccb\uff0c\u8427\u97f5\u5170\u53ea\u987e\u50ac\u9a6c\u6025\u8dd1\uff0c\u521a\u8f6c\u8fc7\u5c71\u5773\uff0c\u5750\u9a91\u7a81\u7136\u4e00\u8dc3\uff0c\u95ef\u5728\u4e00\u5757\u51f8 \u51fa\u6765\u7684\u5c71\u77f3\u4e0a\uff0c\u5c06\u8427\u97f5\u5170\u629b\u4e86\u8d77\u6765\uff0c\u4e0a\u5b98\u5929\u91ce\u5927\u5403\u4e00\u60ca\uff0c\u6025\u5fd9\u62a2\u4e0a\u53bb\u63a5\uff0c\u8427\u97f5\u5170\u5728\u534a\u7a7a\u4e2d\u7ffb \u4e86\u4e00\u4e2a\u7b4b\u6597\uff0c\u843d\u4e0b\u5730\u6765\uff0c\u521a\u597d\u548c\u4e0a\u5b98\u5929\u91ce\u6253\u4e2a\u7167\u9762\uff0c\u53ea\u542c\u5f97\u8427\u97f5\u5170\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff1a\u201c\u4f60 \u597d\uff01\u201d\u4e00\u638c\u5c06\u4e0a\u5b98\u5929\u91ce\u63a8\u5f00\uff0c\u4fef\u9996\u4e00\u770b\uff0c\u5ffd\u89c1\u638c\u5fc3\u5e26\u8840\uff0c\u539f\u6765\u4e0a\u5b98\u5929\u91ce\u5728\u638c\u51fb\u9648\u7384\u673a\u4e4b\u65f6\uff0c \u78b0\u7740\u4e86\u9648\u7384\u673a\u7684\u5251\u950b\uff0c\u4ed6\u7684\u624b\u81c2\u4e5f\u7ed9\u62c9\u5f00\u4e86\u4e00\u9053\u957f\u957f\u7684\u53e3\u5b50\u3002 \u3000\u3000\u8427\u97f5\u5170\u5446\u4e86\u4e00\u5446\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u4e0a\u5b98\u5929\u91ce\u5931\u60ca\u65e0\u795e\u7684\u501a\u5728\u4e00\u5757\u5c71\u77f3\u4e0a\uff0c\u8138\u4e0a\u6ee1\u662f\u6cea \u75d5\uff0c\u8427\u97f5\u5170\u53f9\u4e86\u53e3\u6c14\uff0c\u5ffd\u5730\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u4e48\u5927\u4e2a\u4eba\uff0c\u8fd8\u6d41\u773c\u6cea\uff0c\u4e0d\u5bb3\u81ca\u5417\uff1f\u8ba9\u6211\u770b\u770b\uff0c\u4f60 \u4f24\u5728\u90a3\u513f\uff1f\u201d\u8f7b\u8f7b\u7684\u6495\u4e0b\u4e00\u7247\u8863\u895f\uff0c\u66ff\u4e0a\u5b98\u5929\u91ce\u5305\u624e\u4f24\u53e3\uff0c\u4e0a\u5b98\u5929\u91ce\u53cd\u624b\u4e00\u63a8\uff0c\u624b\u81c2\u4e3e\u8d77\uff0c \u8f6f\u7ef5\u7ef5\u6beb\u65e0\u529b\u6c14\uff0c\u4f46\u89c9\u8427\u97f5\u5170\u7389\u624b\u629a\u6765\uff0c\u7adf\u662f\u65e0\u6cd5\u6297\u62d2\uff0c\u53ea\u597d\u8f6c\u8fc7\u5934\u6765\uff0c\u5728\u5fc3\u4e2d\u6697\u9a82\u81ea\u5df1\u3002 \u3000\u3000\u8427\u97f5\u5170\u5401\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5e78\u597d\u6ca1\u6709\u4f24\u7740\u9aa8\u5934\u3002\u201d\u4e0a\u5b98\u5929\u91ce\u51b7\u7b11\u9053\uff1a\u201c\u6211\u6b7b\u4e86\u4e5f\u6ca1\u6709\u4ec0\u4e48 \u6253\u7d27\uff01\u201d\u8427\u97f5\u5170\u9053\uff1a\u201c\u5440\uff0c\u4f60\u4eec\u4f55\u82e6\u4e3a\u6211\u53ae\u62fc\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u500f\u7684\u56de\u8fc7\u5934\u6765\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5170\u59b9\uff0c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u7684\u5fc3\uff1f\u6211\u662f\uff0c\u6211\u662f\u2026\u2026\u54b3\uff0c \u6211\u662f\u4e3a\u4f60\u4eec\u597d\uff01\u6211\u90a3\u4e00\u638c\u867d\u7136\u6253\u5f97\u4e0d\u8f7b\uff0c\u4ee5\u4ed6\u7684\u6b66\u529f\uff0c\u6599\u60f3\u4e5f\u4e0d\u81f3\u4e8e\u4e27\u547d\uff0c\u53ea\u8981\u4f60\u597d\uff0c\u6211\u4e0a \u5b98\u5929\u91ce\u7c89\u8eab\u788e\u9aa8\u53c8\u6709\u4f55\u9632\uff01\u201d \u3000\u3000\u8427\u97f5\u5170\u53f9\u9053\uff1a\u201c\u8fd9\u4e2a\u65f6\u5019\u4f60\u8fd8\u8bf4\u8fd9\u79cd\u6c14\u8bdd\u505a\u4ec0\u4e48\uff1f\u4f60\u90a3\u4e00\u638c\u6253\u4e0d\u6b7b\u4ed6\uff0c\u4f46\u4ed6\u53d7\u4e86\u6b64\u4f24\uff0c \u5374\u600e\u80fd\u9003\u51fa\u522b\u4eba\u638c\u4e0b\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u8427\u97f5\u5170\u9053\uff1a\u201c\u4ed6\u8981\u53bb\u523a\u6740\u4e00\u4e2a\u4eba\uff0c\u8fd9\u4e2a \u4eba\u5728\u6c5f\u6e56\u4e0a\u7edd\u8ff9\u5df2\u6709\u4e8c\u5341\u5e74\u4e86\uff0c\u4e8c\u5341\u5e74\u524d\u5df2\u662f\u540d\u9707\u4e00\u65f6\uff0c\u7ecf\u8fc7\u4e86\u8fd9\u4e8c\u5341\u5e74\uff0c\u6b66\u529f\u66f4\u662f\u6df1\u4e0d\u53ef \u6d4b\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u6014\u4e86\u4e00\u6014\uff0c\u731b\u7136\u60f3\u8d77\u9648\u7384\u673a\u6240\u8bf4\uff0c\u5341\u65e5\u4e0d\u6765\uff0c\u5c31\u662f\u88ab\u4eba\u6240\u6740\u7684\u8bdd\uff0c\u5931\u58f0\u95ee\u5230\uff1a \u201c\u8fd9\u4eba\u662f\u8c01\uff1f\u201d\u8427\u97f5\u5170\u9053\uff1a\u201c\u4f60\u542c\u8bf4\u8fc7\u4e91\u821e\u9633\u8fd9\u4e2a\u540d\u5b57\u4e48\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u662f\u4e91 \u821e\u9633\uff01\u201d \u3000\u3000\u8138\u4e0a\u6d41\u9732\u51fa\u975e\u5e38\u5947\u5f02\u7684\u795e\u8272\uff0c\u8427\u97f5\u5170\u5fc3\u4e2d\u7eb3\u95f7\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u8ba4\u5f97\u4ed6\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a \u201c\u4e8c\u5341\u5e74\u524d\uff0c\u6211\u8fd8\u662f\u4e00\u4e2a\u4e09\u5c81\u5b69\u5b50\uff0c\u600e\u80fd\u8ba4\u5f97\u4ed6\uff1f\u4f60\u8bf4\uff0c\u4ed6\u4e3a\u4ec0\u4e48\u8981\u523a\u6740\u8fd9\u4e2a\u4e91\u821e\u9633\uff1f\u201d \u3000\u3000\u8427\u97f5\u5170\u9053\uff1a\u201c\u8bf4\u6765\u8bdd\u957f\uff0c\u73b0\u5728\u662f\u6d2a\u6b66\u51e0\u5e74\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u4eca\u5e74\u662f\u6d2a\u6b66\u5341\u4e09\u5e74\uff0c\u4f60\u600e \u80fd\u4e0d\u77e5\uff1f\u201d\u8427\u97f5\u5170\u9053\uff1a\u201c\u6211\u81ea\u7136\u77e5\u9053\uff0c\u53ef\u662f\u6709\u4e00\u73ed\u5b64\u81e3\u5b7d\u5b50\uff0c\u76f4\u5230\u5982\u4eca\u8fd8\u4e0d\u80af\u7528\u6d2a\u6b66\u7eaa \u5e74\u3002\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u90a3\u5927\u7ea6\u53ea\u6709\u9648\u53cb\u8c05\u548c\u5f20\u58eb\u8bda\u7684\u65e7\u90e8\u4e86\u3002\u201d\u8427\u97f5\u5170\u9053\uff1a\u201c\u4e0d\u9519\u3002\u54b1\u4eec\u867d \u7136\u51fa\u4e16\u5f97\u665a\uff0c\u4f46\u4e5f\u542c\u7236\u5144\u8bf4\u8fc7\uff0c\u5f53\u5e74\u548c\u6d2a\u6b66\u7237\u4e89\u5929\u4e0b\u6700\u6fc0\u70c8\u7684\u5c31\u662f\u8fd9\u4e24\u4e2a\u4eba\u3002\u4ed6\u4eec\u90fd\u66fe\u5efa\u7acb \u56fd\u53f7\uff0c\u4e00\u4e2a\u53f7\u79f0\u5927\u6c49\uff0c\u4e00\u4e2a\u53f7\u79f0\u5927\u5468\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u8fd9\u4e0e\u9648\u7384\u673a\u8981\u53bb\u523a\u6740\u4e91\u821e\u9633\u53c8\u6709\u4ec0\u4e48\u76f8\u5e72\uff1f\u201d\u8427\u97f5\u5170\u9053\uff1a\u201c\u5f20\u58eb\u8bda\u5f53\u5e74 \u6709\u51e0\u4e2a\u5929\u4e0b\u95fb\u540d\u7684\u6b66\u6797\u5947\u58eb\u6276\u52a9\u4ed6\uff0c\u4f60\u53ef\u77e5\u9053\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u5934\u4e00\u4e2a\u662f\u5f6d\u548c\u5c1a\uff0c\u4fd7\u5bb6\u540d \u5b57\u53eb\u5f6d\u83b9\u7389\uff0c\u542c\u542c\u8bf4\u5185\u529f\u4e4b\u6df1\uff0c\u5929\u4e0b\u65e0\u5339\u3002\u201d\u8427\u97f5\u5170\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd8\u6709\u5462\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a \u201c\u7b2c\u4e8c\u4e2a\u662f\u77f3\u5929\u94ce\uff0c\u542c\u8bf4\u4ed6\u66fe\u51ed\u7740\u4e00\u53cc\u94c1\u638c\uff0c\u6253\u904d\u4e2d\u539f\u3002\u201d \u3000\u3000\u8427\u97f5\u5170\u9053\uff1a\u201c\u8fd8\u6709\u5462\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u4e0a\u4e00\u4ee3\u6b66\u6797\u540d\u624b\uff0c\u6211\u90a3\u91cc\u8bb0\u5f97\u90a3\u4e48\u591a\uff1f\u201d\u773c\u775b \u4e00\u772f\uff0c\u4f3c\u662f\u60f3\u8bf4\u4ec0\u4e48\u5374\u53c8\u5fcd\u7740\u3002\u8427\u97f5\u5170\u9053\uff1a\u201c\u7b2c\u4e09\u4e2a\u5c31\u662f\u8fd9\u4e2a\u4e91\u821e\u9633\uff01\u201d\u770b\u4e0a\u5b98\u5929\u91ce\uff0c\u53ea\u89c1 \u4e0a\u5b98\u5929\u91ce\u6728\u7136\u6beb\u65e0\u8868\u60c5\u3002\u770b\u90a3\u60c5\u5f62\uff0c\u4ed6\u4f3c\u4e4e\u65e9\u5df2\u77e5\u9053\uff0c\u5374\u504f\u8981\u8427\u97f5\u5170\u5148\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u8427\u97f5\u5170\u9053\uff1a\u201c\u5f20\u58eb\u8bda\u5728\u4e8c\u5341\u5e74\u524d\u4e0e\u6d2a\u6b66\u7237\u5728\u957f\u6c5f\u51b3\u6218\uff0c\u5175\u8d25\u88ab\u64d2\uff0c\u5f53\u65e5\u5c31\u88ab\u6c89\u5c38\u957f\u6c5f\u3002 \u53ef\u662f\u4ed6\u7684\u90e8\u4e0b\u9003\u51fa\u7684\u4e0d\u5728\u5c11\u6570\uff0c\u4ed6\u7684\u513f\u5b50\u542c\u8bf4\u4e5f\u88ab\u77f3\u5929\u94ce\u6551\u51fa\u53bb\u4e86\u3002\u8fd9\u5341\u591a\u5e74\u6765\u5f20\u58eb\u8bda\u7684\u90e8 \u4e0b\u90fd\u9690\u59d3\u57cb\u540d\uff0c\u56fe\u8c0b\u518d\u8d77\u3002\u9648\u7384\u673a\u7684\u8eab\u4e16\u4ece\u6765\u6ca1\u6709\u5bf9\u6211\u63d0\u8fc7\uff0c\u53ef\u662f\u6211\u77e5\u9053\u4ed6\u7684\u5148\u4eba\u4e5f\u662f\u5f20\u58eb \u8bda\u7684\u90e8\u4e0b\u3002\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u9648\u7384\u673a\u7406\u8be5\u5c0a\u79f0\u4e91\u821e\u9633\u4e00\u58f0\u4e16\u4f2f\uff0c\u4f55\u6545\u8fd8\u8981\u53bb\u523a\u6740 \u4ed6\uff1f\u201d\u8427\u97f5\u5170\u9053\uff1a\u201c\u542c\u8bf4\u4e91\u821e\u9633\u53db\u4e3b\u6c42\u8363\uff0c\u9648\u7384\u673a\u8d1f\u4e86\u5e08\u53cb\u7684\u91cd\u6258\uff0c\u975e\u628a\u4ed6\u523a\u6740\u4e0d\u53ef\uff01\u5176\u4e2d \u8be6\u60c5\uff0c\u6211\u4e5f\u4e0d\u77e5\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u4e91\u821e\u9633\u82e5\u771f\u4e3a\u4e86\u8fd9\u4e2a\u539f\u56e0\u800c\u7ed9\u523a\u6b7b\uff0c\u8c05\u4ed6\u6b7b\u4e86\u4e5f\u4e0d\u5fc3\u670d\uff01\u201d \u8427\u97f5\u5170\u9053\uff1a\u201c\u600e\u6837\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u4e91\u821e\u9633\u7684\u7b2c\u4e00\u4e2a\u59bb\u5b50\u5c31\u662f\u5728\u90a3\u6b21\u957f\u6c5f\u4e4b\u6218\u4e2d\u6218\u6b7b\u7684\uff0c \u4ed6\u5c82\u80af\u53cd\u8fc7\u6765\u6276\u52a9\u5f53\u4eca\u7687\u4e0a\uff1f\u201d\u8427\u97f5\u5170\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u4e91\u821e\u9633\u7684\u7b2c\u4e8c \u4e2a\u59bb\u5b50\u5c31\u662f\u6211\u7684\u5e08\u59d1\u3002\u201d\u8427\u97f5\u5170\u5927\u4e3a\u5947\u602a\uff0c\u53eb\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4f60\u539f\u6765\u662f\u6b66\u5f53\u95e8\u4e0b\uff1f\u600e\u4e48\u4ece\u4e0d\u89c1 \u4f60\u63d0\u8d77\uff0c\u4e5f\u4ece\u4e0d\u89c1\u4f60\u9732\u8fc7\u4e00\u624b\u6b66\u5f53\u5251\u6cd5\uff1f\u201d\u591c\u8272\u82cd\u832b\u4e2d\u4f46\u89c1\u4e0a\u5b98\u5929\u91ce\u53cc\u76ee\u70af\u70af\uff0c\u5634\u5507\u5f00\u9616\uff0c \u5374\u6ca1\u6709\u8bf4\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u4e91\u821e\u9633\u7684\u7eed\u5f26\u59bb\u5ba4\uff0c\u4e43\u662f\u4e09\u5341\u5e74\u524d\u53f7\u79f0\u5929\u4e0b\u7b2c\u4e00\u5251\u7684\u6b66\u5f53\u6d3e\u638c\u95e8\u4eba\u725f\u72ec\u9038\u7684\u5973\u513f\uff0c\u4e0a\u5b98 \u5929\u91ce\u79f0\u5979\u505a\u5e08\u59d1\uff0c\u90a3\u4e48\u725f\u72ec\u9038\u81ea\u7136\u662f\u4ed6\u7684\u5e08\u7956\u4e86\u3002 \u3000\u3000\u53ef\u662f\u8427\u97f5\u5170\u7ed3\u8bc6\u4e0a\u5b98\u5929\u91ce\u591a\u5e74\uff0c\u5374\u4ece\u672a\u89c1\u4ed6\u9732\u8fc7\u4e00\u624b\u6b66\u5f53\u7684\u5251\u6cd5\uff0c\u800c\u4eca\u5ffd\u7684\u542c\u4ed6\u63d0\u8d77\uff0c \u5fc3\u4e2d\u7591\u60d1\u4e4b\u6781\uff0c\u53ea\u89c1\u4e0a\u5b98\u5929\u91ce\u6b32\u8bf4\u8fd8\u4f11\uff0c\u8fc7\u4e86\u534a\u54cd\uff0c\u8fd9\u624d\u82e6\u7b11\u9053\uff1a\u201c\u6211\u53ea\u5b66\u5230\u4e00\u70b9\u6b66\u5f53\u5251\u6cd5 \u7684\u76ae\u6bdb\uff0c\u600e\u6562\u5728\u4eba\u524d\u70ab\u8000\uff0c\u4e0d\u6015\u8fb1\u6ca1\u5e08\u95e8\u4e48\uff1f\u201d \u3000\u3000\u8427\u97f5\u5170\u4f55\u7b49\u806a\u660e\uff0c\u4e00\u89c1\u4ed6\u8fd9\u8a00\u8bed\u795e\u60c5\uff0c\u4fbf\u77e5\u9053\u4ed6\u5b9a\u662f\u6709\u96be\u8a00\u4e4b\u9690\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4e0a\u5b98\u5929 \u91ce\u7d20\u6765\u662f\u5bf9\u6211\u65e0\u8bdd\u4e0d\u8bf4\uff0c\u4f55\u4ee5\u8fd9\u4ef6\u4e8b\u60c5\u5374\u8981\u7792\u6211\uff1f\u8fd9\u53c8\u4e0d\u662f\u4ec0\u4e48\u503c\u5f97\u9690\u7792\u7684\u4e8b\u60c5\u3002\u201d\u4f46\u51b3\u4e8b \u60c5\u51fa\u4e4e\u5e38\u7406\u4e4b\u5916\uff0c\u600e\u6837\u4e5f\u731c\u60f3\u4e0d\u900f\uff0c\u867d\u7136\u4e0d\u53d8\u518d\u95ee\uff0c\u5fc3\u4e0a\u7684\u7591\u4e91\u5374\u662f\u8d8a\u6765\u8d8a\u91cd\u3002 \u3000\u3000\u591c\u8272\u66f4\u6d53\uff0c\u5c71\u95f4\u660e\u6708\u5189\u5189\u5347\u8d77\uff0c\u8427\u97f5\u5170\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u7384\u673a\u53d7\u4e86\u91cd\u4f24\uff0c\u5728\u8fd9\u8352\u5c71\u9759\u591c\uff0c\u8c01 \u4eba\u7ed9\u4ed6\u770b\u62a4\uff1f\u201d\u6708\u5149\u4e4b\u4e0b\uff0c\u5ffd\u89c1\u4e0a\u5b98\u5929\u91ce\u9762\u8272\u60e8\u767d\uff0c\u4e24\u53ea\u53c8\u5706\u53c8\u5927\u7684\u773c\u775b\uff0c\u5374\u662f\u7ea2\u4e1d\u6ee1\u5e03\uff0c \u597d\u4f3c\u51fa\u8840\u4e00\u6837\uff0c\u8427\u97f5\u5170\u6253\u4e86\u4e00\u4e2a\u5bd2\u98a4\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u662f\u602a\u4f60\uff0c\u6211\u53ea\u662f\u62c5\u5fc3\u7384\u673a\u3002\u201d\u4e0a\u5b98 \u5929\u91ce\u5ffd\u9053\uff1a\u201c\u4f60\u521a\u624d\u8bf4\u7384\u673a\u8981\u53bb\u884c\u523a\u4e91\u821e\u9633\uff0c\u4e91\u821e\u9633\u7a76\u7adf\u5728\u90a3\u513f\uff1f\u201d\u8427\u97f5\u5170\u9053\uff1a\u201c\u542c\u8bf4\u5c31\u5728 \u524d\u9762\u7684\u8d3a\u5170\u5c71\u4e2d\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u521a\u521a\u51fa\u53e3\uff0c\u53ea\u89c1\u4e0a\u5b98\u5929\u91ce\u4e00\u8dc3\u800c\u8d77\uff0c\u53eb\u9053\uff1a\u201c\u5170\u59b9\u653e\u5fc3\uff0c\u6211\u82e5\u4e0d\u628a\u7384\u673a\u627e\u5230\uff0c\u6c38 \u4e0d\u56de\u6765\uff01\u201d\u7728\u773c\u4e4b\u95f4\uff0c\u6500\u4e0a\u9ad8\u5cf0\u6377\u82e5\u733f\u7334\uff0c\u80cc\u5f71\u6d88\u5931\u5728\u9ed1\u591c\u5bc6\u6797\u4e4b\u4e2d\uff0c\u8427\u97f5\u5170\u8981\u8ffd\u4e5f\u8ffd\u4e0d\u4e0a \u4e86\u3002 \u3000\u3000\u51b7\u6708\u7a7a\u5c71\uff0c\u51c4\u51c4\u5bc2\u5bc2\uff0c\u8427\u97f5\u5170\u5f98\u5f8a\u987e\u5f71\uff0c\u4e00\u7247\u832b\u7136\uff0c\u9648\u7384\u673a\u8d70\u4e86\uff0c\u4e0a\u5b98\u5929\u91ce\u53c8\u8d70\u4e86\uff0c\u82e5 \u5927\u7684\u5c71\u4e2d\uff0c\u53ea\u5269\u4e0b\u81ea\u5df1\u7684\u5f71\u5b50\uff0c\u5979\u7684\u9a6c\u4e5f\u5df2\u8dcc\u6b7b\u4e86\uff0c\u8fd9\u5c71\u8c37\u9759\u5f97\u6015\u4eba\uff01 \u3000\u3000\u51ed\u501f\u6708\u5149\uff0c\u8fd8\u4f9d\u7a00\u5206\u522b\u5f97\u51fa\u524d\u9762\u7684\u9a6c\u8e44\u75d5\u8ff9\uff0c\u8fd9\u662f\u9648\u7384\u673a\u6240\u6d41\u4e0b\u7684\u5f81\u5c18\u9a6c\u8ff9\uff0c\u8427\u97f5\u5170\u53eb \u9053\uff1a\u201c\u7384\u673a\uff01\u7384\u673a\uff01\u4f60\u5728\u90a3\u513f\uff1f\u7b49\u7b49\u6211\u5440\uff01\u201d\u5979\u660e\u77e5\u9648\u7384\u673a\u7684\u9a6c\u662f\u4e00\u5339\u5b9d\u9a6c\uff0c\u8fd9\u65f6\u5df2\u4e0d\u77e5\u8dd1 \u81f3\u4f55\u65b9\uff0c\u7136\u800c\u5979\u8fd8\u662f\u5faa\u7740\u8e44\u75d5\u9a6c\u8ff9\uff0c\u4f5c\u7740\u6beb\u65e0\u5e0c\u671b\u7684\u8ffd\u8e2a\u5bfb\u89c5\u3002 \u3000\u3000\u9648\u7384\u673a\u8fd9\u65f6\u5374\u5230\u4e86\u4e00\u4e2a\u610f\u60f3\u4e0d\u5230\u7684\u6240\u5728\uff0c\u4ed6\u88ab\u4e0a\u5b98\u5929\u91ce\u90a3\u4e00\u638c\u6253\u5f97\u5b9e\u5728\u4e0d\u8f7b\uff0c\u53c8\u6323\u624e\u4e0a \u9a6c\uff0c\u4e0a\u8def\u5954\u9a70\uff0c\u4f46\u89c9\u80f8\u53e3\u95ed\u585e\uff0c\u5934\u75db\u6b32\u88c2\uff0c\u6e10\u6e10\u795e\u667a\u660f\u8ff7\uff0c\u8111\u6d77\u4e2d\u6cdb\u51fa\u8bb8\u591a\u5e7b\u5f71\uff1b\u4ed6\u5fc6\u8d77\u4e86 \u5e08\u53cb\u7ed9\u4ed6\u7f6e\u9152\u8f9e\u884c\uff0c\u90a3\u2018\u6ee1\u5ea7\u8863\u51a0\u4f3c\u96ea\u2019\u7684\u60c5\u666f\uff1b\u4ed6\u8033\u8fb9\u54cd\u8d77\u4e86\u8427\u97f5\u5170\u90a3\u51c4\u5a49\u7684\u6b4c\u58f0\uff0c\u4f3c\u4e4e \u5979\u4e00\u76f4\u5c31\u5728\u81ea\u5df1\u80cc\u540e\u3002 \u3000\u3000\u4ed6\u5728\u5fc3\u4e2d\u53eb\u9053\uff1a\u201c\u6211\u4e0d\u80fd\u6b7b\uff0c\u6211\u4e0d\u80fd\u6b7b\uff01\u201d\u9661\u7136\u95f4\uff0c\u5ffd\u542c\u5f97\u9a6c\u513f\u4e00\u58f0\u5636\u9e23\uff0c\u81ea\u5df1\u597d\u50cf\u7ed9 \u629b\u4e0a\u4e86\u4e07\u4e08\u4e91\u7aef\uff0c\u53c8\u5411\u7740\u65e0\u5e95\u7684\u6df1\u6e0a\u98de\u5760\uff0c\u7a81\u7136\u611f\u5230\u5f02\u6837\u7684\u5bd2\u51b7\u3002\u539f\u6765\u662f\u4ed6\u7684\u9a6c\u4e00\u4e2a\u5931\u8e44\uff0c \u5c06\u4ed6\u629b\u843d\u5c71\u6da7\u4e2d\u4e86\u3002 \u3000\u3000\u660f\u8ff7\u4e2d\u597d\u4f3c\u6709\u4e00\u4e2a\u5c11\u5973\u7684\u7389\u624b\u8f7b\u8f7b\u7684\u629a\u6478\u4ed6\u7684\u80f8\u819b\uff0c\u8fd9\u662f\u8427\u97f5\u5170\u5417\uff1f\u4ed6\u4e0d\u77e5\u9053\uff01\u4ed6\u60f3\u7741 \u5f00\u773c\u775b\uff0c\u7136\u800c\u529b\u4e0d\u4ece\u5fc3\uff0c\u53ea\u89c9\u5728\u5bd2\u51b7\u4e4b\u4e2d\uff0c\u5fc3\u5934\u5347\u8d77\u4e00\u80a1\u6696\u610f\uff0c\u975e\u5e38\u8212\u9002\uff0c\u8ff7\u8ff7\u7cca\u7cca\u7684\u7761\u7740 \u4e86\uff01 \u3000\u3000------------- \u3000\u3000\u91cd\u5e86\u96ea\u513f \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u8fd8\u5251\u5947\u60c5\u5f55\u300b\u2014\u2014\u7b2c\u4e8c\u56de\u3000\u8f7b\u601c\u871c\u7231 \u6881\u7fbd\u751f\u300a\u8fd8\u5251\u5947\u60c5\u5f55\u300b \u7b2c\u4e8c\u56de\u3000\u8f7b\u601c\u871c\u7231 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u9648\u7384\u673a\u597d\u4f3c\u4ece\u4e00\u4e2a\u6076\u68a6\u4e2d\u9192\u6765\u3002\u4e07\u91cc\u98de\u9a91\uff0c\u8352\u5c71\u591c\u6597\uff0c\u524d\u5c18\u5386\u5386\u3002\u6cdb \u4e0a\u5fc3\u6765\u3002\u9648\u7384\u673a\u7ffb\u4e86\u4e2a\u8eab\uff0c\u5fc3\u4e2d\u5947\u602a\u4e4b\u6781\uff1a\u201c\u54a6\uff0c\u6211\u5728\u90a3\u513f\uff1f\u4e0a\u5b98\u5929\u91ce\u5462\uff1f\u8427\u97f5\u5170\u5462\uff1f\u6211\u7684 \u4e4c\u690e\u9a6c\u5462\uff1f\u8fd9\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u70ab\u76ee\u7684\u671d\u9633\u4ece\u7409\u7483\u7a97\u683c\u900f\u5165\uff0c\u5fae\u98ce\u8f7b\u62c2\uff0c\u7f15\u7f15\u5e7d\u9999\uff0c\u6c81\u4eba\u5fc3\u813e\u3002 \u3000\u3000\u9648\u7384\u673a\u7cbe\u795e\u4e00\u723d\uff0c\u970d\u7684\u5750\u4e86\u8d77\u6765\uff0c\u5ffd\u7684\u5931\u58f0\u53eb\u9053\uff1a\u201c\u6211\u600e\u4e48\u56de\u5230\u5bb6\u4e86\uff1f\u201d \u3000\u3000\u8fd9\u771f\u662f\u4e0d\u53ef\u601d\u8bae\u4e4b\u4e8b\uff01\u4ed6\u63c9\u63c9\u773c\u775b\uff0c\u54ac\u54ac\u624b\u6307\uff0c\u8fd9\u4e0d\u662f\u68a6\u5440\uff01 \u3000\u3000\u4ed6\u660e\u660e\u8bb0\u5f97\u81ea\u5df1\u5df2\u7ecf\u6765\u5230\u4e86\u8d3a\u5170\u5c71\u4e0b\uff0c\u548c\u81ea\u5df1\u7684\u5bb6\u4e61\u76f8\u8ddd\u4e07\u91cc\uff0c\u96be\u9053\u81ea\u5df1\u4e00\u7761\u767e\u5929\uff0c\u5728 \u68a6\u4e2d\u88ab\u4eba\u642c\u56de\u4e86\u6545\u4e61\uff1f \u3000\u3000\u96be\u9053\u662f\u4e16\u4e0a\u7adf\u6709\u795e\u4ed9\uff0c\u65bd\u5c55\u4e86\u957f\u623f\u7f29\u5730\u4e4b\u672f\uff1f\u5728\u4e00\u591c\u4e4b\u95f4\u5c06\u81ea\u5df1\u4ece\u8d3a\u5170\u5c71\u4e0b\u5e26\u56de\u4e86\u5ddd\u5317 \u7684\u6545\u5bb6\uff1f \u3000\u3000\u8fd9\u662f\u7edd\u5bf9\u4e0d\u53ef\u80fd\u7684\u4e8b\u60c5\u5440\uff0c\u7136\u800c\u8fd9\u53c8\u4e0d\u662f\u68a6\uff01\u4e00\u6392\u5411\u5357\u5f00\u7684\u7a97\u6237\uff0c\u7a97\u6237\u4e0a\u7684\u7409\u7483\u7a97\u683c\uff0c \u7a97\u5b50\u5916\u7684\u6885\u5f71\u6a2a\u659c\uff0c\uff0c\u5c4b\u4e2d\u95f4\u4e66\u6a71\u7684\u4f4d\u7f6e\uff0c\u8fd9\u660e\u660e\u662f\u81ea\u5df1\u7684\u4e66\u623f\uff01 \u3000\u3000\u623f\u5916\u9762\u4f20\u6765\u4e86\u811a\u6b65\u58f0\uff0c\u9648\u7384\u673a\u6323\u624e\u7740\u8d70\u4e0b\u5e8a\u6765\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5a18\uff01\u201d\u5ffd\u542c\u5f97\u2018\u5657\u55e4\u2019\u4e00 \u58f0\uff0c\u4e00\u4e2a\u5c11\u5973\u6380\u5e18\u800c\u5165\uff0c\u7709\u5982\u65b0\u6708\uff0c\u5634\u4f3c\u6a31\u6843\uff0c\u5728\u671d\u9633\u6e32\u67d3\u4e4b\u4e0b\uff0c\u8138\u86cb\u513f\u7ea2\u6251\u6251\u7684\uff0c\u66f4\u663e\u5f97 \u660e\u8273\u7167\u4eba\uff0c\u800c\u53c8\u6709\u51e0\u5206\u7a1a\u6c14\uff0c\u987f\u65f6\u628a\u9648\u7384\u673a\u770b\u5446\u4e86\u3002 \u3000\u3000\u53ea\u542c\u5f97\u90a3\u5c11\u5973\u7b11\u9053\uff1a\u201c\u597d\u5566\uff0c\u80fd\u8d77\u5e8a\u4e86\uff0c\u600e\u4e48\u3002\u5f88\u60f3\u5bb6\u5417\uff1f\u201d \u3000\u3000\u9648\u7384\u673a\u6014\u4e86\u4e00\u6014\uff0c\u5fc3\u4e2d\u5947\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u91cc\u4e0d\u662f\u6211\u7684\u5bb6\u3002\u201d\u90a3\u5c11\u5973\u7f13\u7f13\u884c\u6765\uff0c\u5410\u6c14\u5982\u5170\uff0c \u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u770b\u4f60\u5e26\u7740\u5b9d\u5251\uff0c\u9a91\u7740\u9a8f\u9a6c\uff0c\u5374\u539f\u6765\u662f\u4e2a\u5927\u5b69\u5b50\uff0c\u4e00\u9192\u6765\u5c31\u8981\u53eb\u5a18\uff01\u201d\u9648\u7384\u673a \u9053\uff1a\u201c\u59d1\u5a18\u8d35\u59d3\uff0c\u6211\u662f\u600e\u4e48\u6765\u5230\u8fd9\u513f\u7684\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u6b63\u8981\u95ee\u4f60\u5462\uff01\u4f60\u600e\u4e48\u7ed9\u4eba\u6253\u4f24\u6210\u8fd9\u4e2a\u6837\u5b50\uff0c\u8981\u4e0d\u662f\u6211\u5bb6\u85cf\u6709\u5c11\u9633\u5c0f \u8fd8\u4e39\uff0c\u53ea\u6015\u4f60\u8fd9\u4f24\u6700\u5c11\u4fee\u517b\u534a\u5e74\u3002\u201d \u3000\u3000\u9648\u7384\u673a\u5fd9\u9053\uff1a\u201c\u591a\u8c22\u59d1\u5a18\u6551\u547d\u4e4b\u6069\uff0c\u8bf7\u95ee\u59d1\u5a18\u8fd9\u91cc\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u5c11\u5973\u683c\u683c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u6211\u5bb6\u5440\u3002\u4f60\u5acc\u8fd9\u5730\u65b9\u4e0d\u597d\u4e48\uff1f\u201d \u3000\u3000\u9648\u7384\u673a\u7741\u5927\u773c\u775b\uff0c\u518d\u770b\u4e00\u770b\uff0c\u5899\u58c1\u4e0a\u6302\u6709\u4e00\u5e45\u957f\u6c5f\u79cb\u591c\u56fe\uff0c\u6c5f\u4e0a\u660e\u660e\u9ad8\u60ac\uff0c\u6c5f\u9762\u6218\u8239\u4e09 \u4e94\uff0c\u540e\u9762\u57ce\u6c60\u90bb\u6c5f\uff0c\u6c14\u9b44\u751a\u5927\uff0c\u753b\u9762\u4e0a\u9898\u6709\u4e00\u9996\u8bd7\u9053\uff1a\u201c\u8c01\u628a\u82cf\u676d\u66f2\u5b50\u8bb4\uff0c\u8377\u82b1\u5341\u91cc\u6842\u4e09 \u79cb\uff0c\u8c01\u77e5\u5349\u6728\u65e0\u60c5\u7269\uff0c\u7275\u52a8\u957f\u6c5f\u4e07\u53e4\u6101\uff01\u201d\u58c1\u4e0a\u8fd8\u6302\u6709\u4e00\u628a\u5f62\u5f0f\u5947\u53e4\u7684\u5b9d\u5251\uff0c\u8fd9\u4e24\u6837\u4e1c\u897f\uff0c \u90fd\u662f\u81ea\u5df1\u7684\u4e66\u623f\u6ca1\u6709\u7684\u3002\u518d\u4ed4\u7ec6\u5206\u522b\uff0c\u8fd9\u623f\u95f4\u7684\u6446\u8bbe\uff0c\u4e5f\u6709\u4e00\u4e9b\u4e0e\u81ea\u5df1\u7684\u4e66\u623f\u4e0d\u540c\u3002\u7136\u800c\u90a3 \u7409\u7483\u7a97\u6237\uff0c\u7a97\u5916\u6885\u679d\uff0c\u5374\u53c8\u662f\u4f55\u5176\u76f8\u4f3c\u3002 \u3000\u3000\u90a3\u5c11\u5973\u89c1\u9648\u7384\u673a\u5982\u75f4\u4f3c\u9189\uff0c\u62bf\u5634\u7b11\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u9648\u7384\u673a\u9053\uff1a\u201c\u8fd9\u623f\u95f4\u96c5\u81f4\u6781\u4e86\uff0c\u4e3a\u4f55 \u5f00\u4e86\u8fd9\u4e00\u6392\u7a97\u6237\uff1f\u201d\u8981\u77e5\u53e4\u65f6\u7684\u5927\u5c4b\uff0c\u7a97\u6237\u90fd\u5f00\u5f97\u5f88\u5c0f\uff0c\u7528\u5317\u4eac\u7684\u7fe1\u7fe0\u7409\u7483\u505a\u7a97\u683c\u5b50\u7684\uff0c\u66f4 \u662f\u9664\u4e86\u6c5f\u5357\u4e4b\u5916\uff0c\u522b\u5904\u5c11\u89c1\u3002\u90a3\u5c11\u5973\u89c1\u9648\u7384\u673a\u521a\u9192\u8f6c\u5c31\u95ee\u8fd9\u4e2a\u623f\u95f4\uff0c\u9887\u4e3a\u5947\u602a\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a \u201c\u8fd9\u662f\u6211\u7239\u7239\u5e03\u7f6e\u7684\u3002\u201d \u3000\u3000\u9648\u7384\u673a\u6276\u7740\u5899\u58c1\uff0c\u7f13\u7f13\u8d70\u8fd1\u7a97\u524d\uff0c\u5ead\u9662\u91cc\u7684\u51e0\u679d\u814a\u6885\u6b63\u5728\u76db\u5f00\uff0c\u5e7d\u9999\u6de1\u96c5\uff0c\u4e2d\u4eba\u5982\u9152\u3002 \u9648\u7384\u673a\u60a0\u7136\u795e\u5f80\uff0c\u8f7b\u58f0\u8bf4\u9053\uff1a\u201c\u7a97\u5f00\u8fce\u6653\u65e5\uff0c\u5e18\u5377\u63d6\u6e05\u82ac\u3002\u6709\u8fd9\u6ee1\u56ed\u6885\u82b1\uff0c\u81ea\u8be5\u5f00\u8fd9\u4e00\u6392\u7a97 \u6237\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u7684\u5fc3\u601d\u7adf\u662f\u548c\u6211\u7239\u7239\u4e00\u822c\u3002\u6211\u7239\u7239\u4e5f\u662f\u8fd9\u6837\u8bf4\uff0c\u591a\u5f00\u7a97 \u6237\uff0c\u8ba9\u9633\u5149\u901a\u900f\uff0c\u82b1\u9999\u6ee1\u5ba4\uff0c\u53ef\u4ee5\u4ee4\u4eba\u5fc3\u795e\u8212\u7545\u3002\u201d \u3000\u3000\u9648\u7384\u673a\u5fc3\u4e2d\u5947\u602a\u81f3\u6781\uff0c\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u6211\u7684\u5fc3\u601d\uff0c\u8fd9\u8d29\u8d29\u8d29\u201d\u90a3\u5c11\u5973\u9053\uff1a\u201c\u600e\u4e48\u6837\uff1f\u201d\u9648 \u7384\u673a\u505c\u4e86\u4e00\u505c\uff0c\u6709\u70b9\u4e0d\u597d\u610f\u601d\u7684\u8bf4\u9053\uff1a\u201c\u6211\u7684\u4e66\u623f\u548c\u4f60\u7684\u4e66\u623f\u4e5f\u5dee\u4e0d\u591a\u4e00\u6837\uff0c\u90a3\u662f\u6211\u5a18\u5e03\u7f6e \u7684\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u7fa1\u6155\u7684\u8bf4\u9053\uff1a\u201c\u4f60\u6709\u8fd9\u6837\u4e2a\u597d\u6bcd\u4eb2\uff0c\u771f\u662f\u798f\u6c14\u3002\u201d\u9648\u7384\u673a\u81ea\u5c0f\u4e0e\u6bcd\u4eb2\u76f8\u4f9d\u4e3a\u547d\uff0c \u542c\u90a3\u5c11\u5973\u79f0\u8d5e\u81ea\u5df1\u7684\u6bcd\u4eb2\uff0c\u751a\u662f\u9ad8\u5174\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u6211\u7684\u6b66\u529f\u4e5f\u662f\u6bcd\u4eb2\u6559\u7684\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u53ef\u60dc\u6211\u7684\u5988\u5988\u957f\u5e74\u8eb2\u5728\u5c4b\u5b50\u91cc\uff0c\u4e00\u5e74\u96be\u5f97\u6709\u51e0\u65e5\u89c1\u7740\u9633\u5149\u3002\u201d\u9648\u7384\u673a\u9053\uff1a \u201c\u5475\uff01\u539f\u6765\u4f2f\u6bcd\u5728\u91cc\u9762\uff0c\u6211\u8fd8\u672a\u62dc\u89c1\u5979\u5462\u3002\u201d\u90a3\u5c11\u5973\u9053\uff1a\u201c\u6211\u5988\u5988\u8eab\u5b50\u4e0d\u597d\uff0c\u4e00\u5e74\u5230\u5934\u5728\u5c4b \u517b\u75c5\uff0c\u5979\u8fde\u5927\u95e8\u4e5f\u61d2\u5f97\u51fa\uff0c\u66f4\u4e0d\u7528\u8bf4\u89c1\u5ba2\u4eba\u4e86\u3002\u201d\u9648\u7384\u673a\u89c1\u5979\u7709\u5934\u6df1\u9501\uff0c\u751a\u89c9\u62b1\u6b49\u3002\u5e78\u559c\u90a3 \u5c11\u5973\u8fc7\u4e86\u4e00\u9635\u53c8\u5c55\u5f00\u7b11\u9765\u8bf4\u9053\uff1a\u201c\u539f\u6765\u4f60\u7684\u6b66\u529f\u662f\u4f60\u6bcd\u4eb2\u6559\u7684\uff0c\u90a3\u4e48\u4f60\u7684\u7236\u4eb2\u5462\uff1f\u201d\u9648\u7384\u673a \u9eef\u7136\u8bf4\u9053\uff1a\u201c\u6211\u7239\u7239\u5728\u6211\u51fa\u751f\u4e4b\u524d\uff0c\u65e9\u5df2\u6b7b\u4e86\uff01\u201d\u90a3\u5c11\u5973\u2018\u554a\u5440\u2019\u4e00\u58f0\uff0c\u767b\u65f6\u4e0d\u5728\u8a00\u8bed\u3002 \u3000\u3000\u9648\u7384\u673a\u8d8a\u60f3\u8d8a\u89c9\u5f97\u8fd9\u513f\u900f\u5f97\u53e4\u602a\uff0c\u7981\u4e0d\u4f4f\u53c8\u95ee\u9053\uff1a\u201c\u6211\u53eb\u9648\u7384\u673a\uff0c\u8bf7\u95ee\u59d1\u5a18\u8d35\u59d3\uff0c\u4ee4\u5c0a \u5927\u4eba\u5728\u5bb6\u5417\uff1f\u201d\u90a3\u5c11\u5973\u53c8\u662f\u2018\u5657\u54e7\u2019\u4e00\u7b11\u9053\uff1a\u201c\u6211\u53c8\u4e0d\u56fe\u4f60\u4ec0\u4e48\u62a5\u7b54\uff0c\u4f60\u4f55\u5fc5\u7d6e\u7d6e\u4e0d\u4f11\u7684\u76d8 \u6839\u95ee\u5e95\uff1f\u201d\u9648\u7384\u673a\u9762\u4e0a\u4e00\u7ea2\uff0c\u8981\u77e5\u6c5f\u6e56\u4e0a\u672c\u591a\u907f\u5fcc\uff0c\u5411\u4e00\u4e2a\u964c\u751f\u7684\u5c11\u5973\u76d8\u95ee\u59d3\u540d\u66f4\u662f\u7a00\u6709\u4e4b \u4e8b\uff0c\u4ed6\u4e3a\u4e86\u597d\u5947\uff0c\u95ee\u4e86\u51fa\u6765\uff0c\u786e\u78b0\u4e86\u4e00\u4e2a\u4e0d\u5927\u4e0d\u5c0f\u7684\u9489\u5b50\u3002 \u3000\u3000\u90a3\u5c11\u5973\u62ac\u5934\u4e00\u770b\u65e5\u5149\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5df2\u6c89\u7761\u4e86\u4e00\u5929\u4e00\u591c\uff0c\u8fd9\u65f6\u5019\u809a\u5b50\u5927\u6982\u4e5f\u997f\u4e86\uff0c\u4f60\u4e14\u7b49 \u4e00\u4f1a\u513f\u3002\u201d\u4e00\u7b11\u6380\u5e18\uff0c\u7fe9\u7136\u800c\u51fa\uff0c\u5230\u4e86\u95e8\u53e3\uff0c\u5374\u5ffd\u7684\u56de\u5934\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u544a\u8bc9\u4f60\u5427\uff0c\u6211\u59d3 \u4e91\u3002\u201d \u3000\u3000\u9648\u7384\u673a\u5fc3\u4e2d\u4e00\u51db\uff0c\u8fd9\u5c11\u5973\u7adf\u662f\u59d3\u4e91\uff01\u96be\u9053\uff0c\u96be\u9053\u8d29\u8d29\u8d29\u5fc3\u4e2d\u53c8\u81ea\u884c\u89e3\u9053\uff1a\u201c\u5929\u4e0b\u59d3\u4e91\u7684 \u4eba\u4e0d\u5c11\uff0c\u90a3\u80fd\u6709\u8fd9\u822c\u51d1\u5de7\u7684\u4e8b\u513f\uff1f\u201d \u3000\u3000\u867d\u7136\u81ea\u884c\u5f00\u89e3\uff0c\u5fc3\u5934\u4ecd\u662f\u90c1\u95f7\u4e0d\u5b89\uff0c\u8bd5\u7740\u6325\u62f3\u8e22\u8db3\uff0c\u53ea\u89c9\u4f53\u529b\u5df2\u6062\u590d\u4e86\u51e0\u6210\uff0c\u5fc3\u4e2d\u60f3 \u9053\uff1a\u201c\u4e0a\u5b98\u5929\u91ce\u90a3\u4e00\u62f3\u6253\u5f97\u5b9e\u5728\u4e0d\u8f7b\uff0c\u8fd9\u5c11\u5973\u7684\u4e39\u836f\u7adf\u5982\u6b64\u7075\u6548\uff0c\u60f3\u6765\u5b9a\u662f\u6b66\u6797\u4e16\u5bb6\u3002\u201d\u4e00 \u62ac\u5934\u89c1\u58c1\u4e0a\u6302\u7740\u7684\u90a3\u628a\u5f62\u5f0f\u5947\u53e4\u7684\u5b9d\u5251\uff0c\u5fcd\u4e0d\u4f4f\u5c06\u5b83\u6458\u4e86\u4e0b\u6765\uff0c\u62d4\u5251\u51fa\u9798\uff0c\u4f46\u89c1\u5251\u8eab\u9690\u9690\u900f \u7740\u4e00\u5c42\u9752\u5149\uff0c\u9648\u7384\u673a\u81ea\u662f\u8bc6\u8d27\u7684\u884c\u5bb6\uff0c\u4e00\u770b\u4fbf\u77e5\u5230\u8fd9\u662f\u4e16\u4e0a\u7f55\u89c1\u7684\u795e\u7269\u5229\u5668\uff0c\u4e0d\u7981\u5446\u4e86\uff0c\u5fc3 \u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u4f4d\u4e91\u59d1\u5a18\u5c45\u7136\u5982\u6b64\u4fe1\u8d56\u4e8e\u6211\uff0c\u5b9d\u5251\u60ac\u5728\u6b64\u95f4\uff0c\u4e0d\u6015\u88ab\u6211\u628a\u5b83\u5077\u53bb\uff01\u201d\u4f4e\u5934\u4e00 \u77a7\uff0c\u5251\u67c4\u4e0a\u523b\u6709\u4e24\u4e2a\u5947\u5f62\u602a\u72b6\u7684\u53e4\u4ee3\u6587\u5b57\uff0c\u8fd9\u4e00\u77a7\u66f4\u4ee4\u5f97\u9648\u7384\u673a\u5982\u5760\u5165\u4e94\u91cc\u4e91\u96fe\u4e2d\uff01 \u3000\u3000\u5251\u67c4\u4e0a\u90a3\u4e24\u4e2a\u53e4\u5b57\u4e43\u662f\u201c\u949f\u9f0e\u6587\u201d\uff0c\u9648\u7384\u673a\u672c\u6765\u4e0d\u8ba4\u8bc6\u949f\u9f0e\u6587\uff0c\u4f46\u8fd9\u4e24\u4e2a\u5b57\u5374\u5728\u4ed6\u5916\u7956 \u7236\u7684\u8bd7\u96c6\u91cc\u89c1\u8fc7\uff0c\u4ed6\u6bcd\u4eb2\u544a\u8bc9\u4ed6\u8fd9\u4e24\u4e2a\u5b57\u5ff5\u505a\u2018\u6606\u543e\u2019\uff0c\u4e43\u662f\u4e00\u628a\u53e4\u4ee3\u5b9d\u5251\u7684\u540d\u5b57\u3002 \u3000\u3000\u9648\u7384\u673a\u7684\u5916\u7956\u7236\u6ca1\u6709\u513f\u5b50\uff0c\u6240\u4ee5\u9648\u7384\u673a\u51fa\u751f\u4ee5\u540e\uff0c\u5c31\u505a\u4e3a\u2018\u59d1\u5b50\u5f52\u5b97\u2019\uff0c\u6539\u4f9d\u6bcd\u59d3\uff0c\u7ee7 \u627f\u9648\u5bb6\u7684\u9999\u706b\u3002\u4ed6\u5916\u7956\u7236\u540d\u53eb\u9648\u5b9a\u65b9\uff0c\u662f\u5143\u672b\u4e00\u4e3a\u51fa\u540d\u7684\u8bd7\u4eba\uff0c\u6587\u6b66\u5168\u624d\uff0c\u53f7\u79f0\u6b66\u6797\u53cc\u7edd\uff0c \u4ed6\u7684\u8bd7\u96c6\u91cc\u4fbf\u6709\u4e00\u9996\u662f\u548f\u8fd9\u6606\u543e\u5b9d\u5251\u7684\uff0c\u8bd7\u9053\uff1a\u201c\u4f20\u5bb6\u6127\u6211\u65e0\u73e0\u7389\uff0c\u5251\u5323\u8bd7\u56ca\u73cd\u91cd\u5b58\u3002\u4f46\u613f \u4eba\u95f4\u7559\u4fa0\u6c14\uff0c\u4e0d\u6559\u72d0\u9f20\u6562\u76f8\u4fb5\u3002\u201d\u770b\u8fd9\u8bd7\u610f\uff0c\u4f3c\u4e4e\u8fd9\u628a\u6606\u543e\u5b9d\u5251\uff0c\u4e43\u662f\u5916\u7956\u7236\u7684\u5bb6\u4f20\u5b9d\u7269\uff0c \u4f46\u95ee\u4ed6\u6bcd\u4eb2\uff0c\u4ed6\u6bcd\u4eb2\u5374\u8bf4\u6ca1\u6709\u89c1\u8fc7\uff0c\u4e0d\u8fc7\u4ed6\u6bcd\u4eb2\u56de\u7b54\u4ed6\u7684\u95ee\u8bdd\u65f6\uff0c\u5374\u6709\u70b9\u652f\u652f\u543e\u543e\uff0c\uff0c\u800c\u4e14 \u8138\u4e0a\u8fd8\u6d41\u9732\u51fa\u60b2\u4f24\u7684\u795e\u8272\u3002\u8fd9\u4e8b\u60c5\u9648\u7384\u673a\u81ea\u77e5\u4e8b\u4ee5\u6765\u4fbf\u4e00\u76f4\u95f7\u5728\u5fc3\u5934\u3002 \u3000\u3000\u4e0d\u60f3\u5982\u4eca\u5374\u5728\u8fd9\u4e2a\u53e4\u602a\u7684\u5730\u65b9\u89c1\u4e86\u8fd9\u628a\u5b9d\u5251\uff0c\u8fd9\u662f\u5916\u7956\u7236\u90a3\u628a\u5bb6\u4f20\u5b9d\u5251\u5417\uff1f\u8fd8\u662f\u5c4b\u4e3b\u4eba \u4ece\u522b\u5904\u5f97\u6765\u7684\uff1f\u6b63\u5728\u6c89\u601d\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u811a\u6b65\u58f0\u54cd\uff0c\u9648\u7384\u673a\u614c\u5fd9\u628a\u5b9d\u5251\u6302\u56de\u5899\u4e0a\u3002\u53ea\u89c1\u90a3\u5c11\u5973 \u6367\u7740\u4e00\u4e2a\u6258\u76d8\uff0c\u76d8\u4e2d\u6709\u4e00\u9505\u70ed\u7ca5\uff0c\u8fd8\u6709\u4e24\u5f0f\u5c0f\u83dc\u3002 \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u4f60\u521a\u521a\u4f24\u6108\uff0c\u559d\u4e00\u70b9\u7a00\u996d\u5427\u3002\u54a6\uff0c\u4f60\u5728\u60f3\u4e9b\u4ec0\u4e48\uff1f\u201d\u987a\u7740\u9648\u7384\u673a\u7684\u773c\u5149\u77a7 \u53bb\uff0c\u5ffd\u7684\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u770b\u4e0a\u6211\u8fd9\u628a\u5b9d\u5251\u3002\u201d \u3000\u3000\u9648\u7384\u673a\u9762\u7ea2\u8033\u70ed\uff0c\u5c34\u5c2c\u7b11\u9053\uff1a\u201c\u6211\u77a7\u8fd9\u628a\u5251\u6709\u70b9\u5947\u602a\u3002\u201d\u90a3\u5c11\u5973\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u9648\u7384\u673a \u9053\uff1a\u201c\u8fd9\u4f3c\u4e4e\u662f\u4e00\u628a\u53e4\u4ee3\u7684\u5b9d\u5251\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u7239\u7239\u8bf4\u662f\u6218\u56fd\u65f6\u5019\u7ec3\u5251\u5e08\u6b27\u51b6\u5b50\u6d41\u4e0b\u6765\u7684\u5b9d\u7269\u5462\uff0c\u4f60\u5012\u597d\u773c \u529b\u3002\u201d \u3000\u3000\u9648\u7384\u673a\u9053\uff1a\u201c\u8fd9\u628a\u5251\u662f\u59d1\u5a18\u5bb6\u4f20\u7684\u5b9d\u7269\u5417\uff1f\u201d\u90a3\u5c11\u5973\u7b11\u9053\uff1a\u201c\u5f53\u7136\u662f\u6211\u5bb6\u4f20\u7684\u4e1c\u897f\uff0c\u8981 \u4e0d\u7136\u600e\u4f1a\u6302\u5728\u8fd9\u91cc\uff0c\u6211\u7238\u7238\u624d\u5b9d\u8d1d\u5b83\u5462\uff0c\u5e73\u65f6\u522b\u4eba\u6478\u4e00\u6478\u4ed6\u90fd\u4e0d\u8bb8\uff0c\u8fd8\u662f\u6211\u4e0a\u4e2a\u6708\u5341\u516b\u5c81\u751f \u65e5\u90a3\u4e00\u5929\uff0c\u4ed6\u624d\u80af\u4f20\u7ed9\u6211\u7684\u3002\u201d\u8bf4\u4e86\u4e4b\u540e\uff0c\u5ffd\u7136\u8138\u4e0a\u4e00\u7ea2\uff0c\u4f3c\u4e4e\u540e\u6094\u53eb\u9648\u7384\u673a\u77e5\u9053\u4e86\u5979\u5c11\u5973 \u7684\u5e74\u9f84\u3002 \u3000\u3000\u9648\u7384\u673a\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4e91\u59d1\u5a18\u4e00\u5b9a\u662f\u4f1a\u5bb6\u5b50\u4e86\u3002\u201d\u90a3\u5c11\u5973\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u4f1a\u5bb6\u5b50\uff1f\u6211\u7239 \u7239\u8bf4\uff0c\u6211\u8fd8\u672a\u5b66\u5230\u4ed6\u7684\u4e09\u6210\u5462\uff01\u201d\u9648\u7384\u673a\u89c1\u90a3\u5c11\u5973\u5929\u771f\u70c2\u6f2b\uff0c\u5927\u80c6\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\u592a\u5ba2\u6c14\u4e86\u3002 \u53ef\u4ee5\u8ba9\u6211\u5f00\u5f00\u773c\u754c\u5417\uff1f\u201d\u90a3\u5c11\u5973\u7b11\u9053\uff1a\u201c\u4f60\u6b66\u529f\u80dc\u6211\u5341\u500d\uff0c\u6211\u600e\u6562\u5728\u4e13\u5bb6\u9762\u524d\u732e\u4e11\uff1f\u201d\u9648\u7384 \u673a\u9053\uff1a\u201c\u4f60\u51e0\u65f6\u89c1\u8fc7\u6211\u7684\u6b66\u529f\uff1f\u201d\u90a3\u5c11\u5973\u9053\uff1a\u201c\u4f60\u53d7\u4e86\u91cd\u4f24\uff0c\u5c45\u7136\u4e00\u65e5\u4e00\u591c\u4fbf\u590d\u539f\u4e86\uff0c\u867d\u8bf4 \u662f\u5c11\u9633\u5c0f\u8fd8\u4e39\u4e4b\u529f\uff0c\u4f46\u82e5\u6ca1\u6709\u6df1\u6e5b\u7684\u5185\u529f\u6839\u67e2\uff0c\u90a3\u91cc\u80fd\u591f\u8fd9\u4e48\u5feb\u590d\u5143\uff1f\u770b\u6765\u4f60\u4e0e\u6211\u7684\u7239\u7239\u53ea \u6015\u4e5f\u5dee\u4e0d\u591a\u3002\u53ef\u60dc\u4ed6\u51fa\u95e8\u53bb\u4e86\uff0c\u8981\u4e0d\u7136\u4f60\u5012\u53ef\u4e0e\u4ed6\u8c08\u8bba\u8c08\u8bba\u3002\u201d \u3000\u3000\u9648\u7384\u673a\u9053\uff1a\u201c\u6211\u867d\u65e0\u7f18\u62dc\u89c1\u4ee4\u5c0a\uff0c\u542c\u59d1\u5a18\u7684\u8bf4\u8bdd\uff0c\u4e5f\u8bb8\u4ee4\u5c0a\u5927\u4eba\u662f\u6b66\u5b66\u540d\u5bb6\uff0c\u8d8a\u53d1\u8981\u8bf7 \u59d1\u5a18\u4e0d\u541d\u8d50\u6559\u3002\u201d\u90a3\u5c11\u5973\u4e0d\u597d\u610f\u601d\u7684\u7b11\u9053\uff1a\u201c\u6211\u6ca1\u6709\u89c1\u8fc7\u4e16\u9762\uff0c\u6240\u4ee5\u53ea\u77e5\u9053\u81ea\u5df1\u7684\u7236\u4eb2\uff0c\u5938 \u8d5e\u81ea\u5bb6\uff0c\u53eb\u4f60\u89c1\u7b11\u4e86\u3002\u4e5f\u7f62\uff0c\u6211\u6ca1\u6709\u597d\u83dc\u7ed9\u4f60\u9001\u7ca5\uff0c\u5c31\u7ed9\u4f60\u821e\u4e00\u4f1a\u513f\u5251\u5427\uff0c\u4f60\u53ef\u8981\u4e0d\u541d\u6307\u6559 \u554a\uff01\u201d \u3000\u3000\u9648\u7384\u673a\u559c\u9053\uff1a\u201c\u53e4\u4eba\u8bf4\u8bfb\u6c49\u4e66\u53ef\u6d6e\u5927\u767d\uff0c\u6211\u800c\u4eca\u5f97\u770b\u59d1\u5a18\u821e\u5251\uff0c\u90a3\u66f4\u662f\u7fa1\u715e\u53e4\u4eba\u7684 \u4e86\u3002\u201d\u90a3\u5c11\u5973\u9053\uff1a\u201c\u4f60\u771f\u4f1a\u8bf4\u8bdd\u3002\u201d\u76c8\u76c8\u4e00\u7b11\uff0c\u67f3\u8170\u4e00\u6298\uff0c\u633d\u4e86\u4e00\u4e2a\u5251\u82b1\uff0c\u8f7b\u8f7b\u523a\u51fa\uff0c\u6538\u7136 \u95f4\u4f46\u89c1\u5251\u5149\u6ee1\u5ba4\uff0c\u51c9\u6c14\u6c81\u4eba\u3002 \u3000\u3000\u9648\u7384\u673a\u5403\u4e86\u4e00\u60ca\uff0c\u8fd9\u5b9d\u5251\u56fa\u7136\u7f55\u89c1\uff0c\u5251\u6cd5\u66f4\u662f\u9a87\u4eba\uff0c\u770b\u5979\u6f2b\u4e0d\u7ecf\u610f\u7684\u968f\u624b\u6325\u6d12\uff0c\u6bcf\u4e00\u62db \u90fd\u85cf\u7740\u6781\u7cbe\u5fae\u7684\u53d8\u5316\uff0c\u5999\u5230\u6beb\u5dc5\uff0c\u821e\u5230\u6025\u5904\uff0c\u90a3\u5c11\u5973\u5c31\u4f3c\u9661\u7136\u95f4\u5e7b\u51fa\u4e86\u65e0\u6570\u5316\u8eab\uff0c\u5251\u5149\u56db \u5c04\uff0c\u7aef\u7684\u5982\u6c34\u94f6\u6cfb\u5730\uff0c\u82b1\u96e8\u7f24\u7eb7\u3002\u9648\u7384\u673a\u4e0d\u7531\u7684\u5012\u5438\u4e86\u4e00\u53e3\u51c9\u6c14\uff0c\u5fc3\u4e2d\u81ea\u4ed8\uff1a\u5e08\u53cb\u95e8\u90fd\u8bf4\u81ea \u5df1\u7684\u5251\u672f\u5df2\u7ecf\u5b66\u6210\uff0c\u82e5\u548c\u8fd9\u4e2a\u5c11\u5973\u6bd4\u5251\uff0c\u53ea\u6015\u8fd8\u672a\u5fc5\u80fd\u591f\u80dc\u5979\u3002 \u3000\u3000\u9648\u7384\u673a\u867d\u7136\u5e74\u8f7b\uff0c\u5bf9\u6b66\u6797\u4e2d\u5404\u8457\u540d\u7684\u5251\u6d3e\uff0c\u5374\u90fd\u719f\u6089\uff0c\u7adf\u770b\u4e0d\u51fa\u8fd9\u5c11\u5973\u7684\u5b97\u6d3e\u6765\uff0c\u4f46\u89c9 \u8eab\u6cd5\u6b65\u6cd5\uff0c\u4e0e\u6b66\u5f53\u6d3e\u6709\u4e9b\u76f8\u4f3c\uff0c\u4f46\u51fa\u624b\u7684\u5947\u5999\u8fc5\u901f\uff0c\u5374\u8fdc\u80dc\u4e8e\u81ea\u5df1\u66fe\u89c1\u8fc7\u7684\u6b66\u5f53\u5251\u6cd5\u4e86\u3002\u5ffd \u542c\u5f97\u90a3\u5c11\u5973\u5728\u5251\u5149\u7f2d\u7ed5\u4e2d\u66fc\u58f0\u5531\u9053\uff1a\u201c\u6e3a\u7a7a\u70df\uff0c\u56db\u8fdc\u662f\u4f55\u5e74\uff0c\u9752\u5929\u5760\u957f\u661f\uff01\u5e7b\u82cd\u5ca9\u4e91\u6811\uff0c\u540d \u5a03\u91d1\u5c4b\uff0c\u6b8b\u9738\u5bab\u57ce\u3002\u7bad\u52b2\u9178\u98ce\u5c04\u773c\uff0c\u5251\u6c34\u67d3\u82b1\u8165\u3002\u65f6\u97e7\u53cc\u9e33\u54cd\uff0c\u5eca\u53f6\u79cb\u58f0\u3002\u5bab\u91cc\u5434\u738b\u6c89\u9189\uff0c \u5029\u4e94\u6e56\u5026\u5ba2\uff0c\u72ec\u9493\u9192\u9192\u3002\u95ee\u82cd\u6ce2\u65e0\u8bed\uff0c\u534e\u53d1\u5948\u9752\uff1f\u201d\u5065\uff2b\ue191\uff1f\u201d\u7a7a\u9601\u51ed\u9ad8\u5904\uff0c\u9001\u4e71\u9e26\u659c\u65e5\u843d \u6e14\u6c40\u3002\u8fde\u547c\u9152\uff0c\u7434\u53f0\u53bb\uff0c\u79cb\u4e0e\u4e91\u5e73\u3002\u201d \u3000\u3000\u5251\u5f71\u6b4c\u58f0\uff0c\u4e24\u7686\u5999\u7edd\uff0c\u9648\u7384\u673a\u4e0d\u7981\u542c\u5f97\u75f4\u4e86\u3002\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u9615\u516b\u58f0\u7518\u5dde\u4f3c\u662f\u611f\u548f\u53f2 \u4e8b\uff0c\u53c8\u4f3c\u60b2\u6b4c\u8eab\u4e16\uff0c\u8bcd\u4e2d\u2018\u5bab\u91cc\u5434\u738b\u6c89\u9189\u2019\u662f\u6307\u6218\u56fd\u65f6\u7684\u5434\u738b\u592b\u5dee\u5462\uff0c\u8fd8\u662f\u6307\u66fe\u4e0e\u6731\u5143\u748b\u4e89 \u593a\u5929\u4e0b\uff0c\u66fe\u5728\u82cf\u5dde\u79f0\u5e1d\u7684\u5f20\u58eb\u57ce\u5462\uff1f\u201d\u518d\u4e00\u770b\u5899\u4e0a\u6302\u7740\u7684\u957f\u6c5f\u79cb\u6708\u56fe\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4e00\u53e5\u8bdd\u5feb \u5230\u53e3\u8fb9\u53c8\u541e\u56de\u53bb\u4e86\u3002 \u3000\u3000\u90a3\u5c11\u5973\u5251\u5149\u4e00\u6536\uff0c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u68a6\u7a97\u8bcd\u4eba\u8bd7\u5982\u4e03\u5b9d\u697c\u53f0\uff0c\u62c6\u4e0b\u6765\u4e0d\u6210\u7247\u6bb5\uff0c\u8fd9\u4e00\u9615\u516b\u58f0 \u7518\u5dde\u5374\u5c1a\u6709\u610f\u5883\u3002\u201d\u9648\u7384\u673a\u9762\u4e0a\u4e00\u7ea2\uff0c\u81ea\u6127\u8bd7\u8bcd\u8bfb\u5f97\u592a\u5c11\uff0c\u539f\u6765\u8fd9\u662f\u5357\u5b8b\u8bd7\u4eba\u5434\u6587\u82f1\u7684\u8bcd\uff0c \u4f46\u5fc3\u4e2d\u4ecd\u662f\u60f3\u9053\uff1a\u201c\u5434\u68a6\u7a97\u5728\u8bcd\u5bb6\u4e4b\u4e2d\uff0c\u4e0d\u7b97\u9f0e\u9f0e\u6709\u540d\uff0c\u8fd9\u4f4d\u4e91\u59d1\u5a18\u504f\u62e3\u4ed6\u8fd9\u9996\u8bcd\u6765\u5531\uff0c\u800c \u53c8\u6697\u542b\u8fd1\u4e16\u7684\u53f2\u4e8b\uff0c\u4e0d\u77e5\u662f\u6709\u610f\u8fd8\u662f\u65e0\u610f\uff1f\u82e5\u662f\u6709\u5fc3\u7528\u8bcd\u8bd5\u6211\uff0c\u90a3\u4e5f\u7b97\u5f97\u662f\u806a\u660e\u7edd\u9876\u7684 \u4e86\u3002\u201d \u3000\u3000\u9648\u7384\u673a\u6781\u529b\u6309\u637a\uff0c\u9762\u4e0a\u4e0d\u9732\u4e1d\u6beb\u795e\u8272\uff0c\u53ea\u542c\u5f97\u90a3\u5c11\u5973\u53c8\u683c\u683c\u7b11\u9053\uff1a\u201c\u6211\u821e\u5251\u7ed9\u4f60\u9001\u7ca5\uff0c \u4f60\u5374\u8fde\u7b77\u5b50\u4e5f\u672a\u66fe\u4e00\u52a8\u3002\u201d \u3000\u3000\u9648\u7384\u673a\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u5251\u672f\u5999\u7edd\u5929\u4e0b\uff0c\u6211\u770b\u5f97\u5fd8\u4e4e\u6240\u4ee5\u4e86\u3002\u201d \u3000\u3000\u4f4e\u4e0b\u5934\u6765\uff0c\u62ff\u8d77\u7b77\u5b50\uff0c\u4f46\u89c1\u76d8\u4e2d\u4e24\u789f\u5c0f\u83dc\uff0c\u4e00\u8364\u4e00\u7d20\uff0c\u8364\u7684\u662f\u677e\u9999\u718f\u8089\uff0c\u8fd9\u662f\u4e00\u5473\u56db\u5ddd \u7cbe\u7f8e\u7684\u5bb6\u5e38\u5c0f\u83dc\uff0c\u628a\u80a5\u7626\u5404\u534a\u7684\u4e94\u82b1\u8089\uff0c\u7528\u677e\u679d\u6765\u718f\u7684\uff1b\u53e6\u4e00\u79cd\u7d20\u83dc\u4e43\u662f\u6ce1\u83dc\uff0c\u4e5f\u662f\u56db\u5ddd\u8457 \u540d\u7684\u5bb6\u5e38\u5c0f\u83dc\uff0c\u8d3a\u5170\u5c71\u8fdc\u5728\u5b81\u590f\uff0c\u4e0e\u56db\u5ddd\u76f8\u8ddd\u6570\u5343\u91cc\u4e4b\u9065\uff0c\u5728\u8fd9\u91cc\u5403\u5230\u56db\u5ddd\u7684\u5bb6\u5e38\u5c0f\u83dc\u5df2\u662f \u4e00\u5947\uff0c\u66f4\u5947\u7684\u662f\u8fd9\u4e24\u5473\u5c0f\u83dc\u662f\u81ea\u5df1\u81ea\u5e7c\u6700\u7231\u5403\u7684\u4e1c\u897f\uff0c\u9648\u7384\u673a\u4e0d\u7981\u53c8\u6014\u7740\u4e86\u3002 \u3000\u3000\u90a3\u5c11\u5973\u7b11\u9053\uff1a\u201c\u600e\u4e48\uff0c\u5acc\u83dc\u4e0d\u597d\u5403\u4e48\uff1f\u201d\u9648\u7384\u673a\u6bcf\u6837\u631f\u4e86\u4e00\u7bb8\uff0c\u5c11\u5973\u8138\u6cdb\u7ea2\u6f6e\uff0c\u9053\uff1a \u201c\u8fd9\u662f\u6211\u505a\u7684\uff0c\u600e\u4e48\u4f60\u53c8\u60f3\u8d77\u6bcd\u4eb2\u6765\u4e86\u3002\u5feb\u5403\u5427\uff0c\u7ca5\u8981\u51c9\u5566\uff01\u201d\u5c0f\u7c73\u7ca5\u78a7\u7eff\u7518\u9999\uff0c\u914d\u4e0a\u8fd9\u4e24 \u5473\u5bb6\u4e61\u98ce\u5473\u7684\u5c0f\u83dc\uff0c\u9648\u7384\u673a\u4e0d\u7981\u98df\u6b32\u5927\u52a8\uff0c\u4e00\u8fde\u5403\u4e86\u4e09\u7897\u3002 \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u4f60\u5728\u5c71\u6da7\u4e2d\u6d78\u4e86\u8bb8\u4e45\uff0c\u800c\u4eca\u521d\u6108\uff0c\u518d\u559d\u4e00\u676f\u9152\u76ca\u6c14\u884c\u8840\u5427\u3002\u201d\u5728\u9542\u82b1\u7684\u94f6 \u58f6\u4e2d\u5012\u4e86\u6ee1\u6ee1\u7684\u4e00\u76cf\u7f8e\u9152\uff0c\u9152\u8272\u4e5f\u662f\u78a7\u7eff\u53ef\u7231\uff0c\u9999\u6c14\u8bf1\u4eba\uff0c\u9648\u7384\u673a\u4e0d\u5584\u996e\u9152\uff0c\u5374\u4ef0\u8d77\u8116\u5b50\uff0c \u4e00\u996e\u800c\u5c3d\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u6837\u7f8e\u9152\uff0c\u9189\u6b7b\u4e86\u4ea6\u81ea\u7518\u5fc3\uff01\u201d \u3000\u3000\u90a3\u5c11\u5973\u5ffd\u7684\u63a9\u53e3\u800c\u7b11\uff0c\u9648\u7384\u673a\u5ffd\u89c9\u6709\u4e9b\u5f02\u6837\uff0c\u8df3\u8d77\u6765\u9053\uff1a\u201c\u4f60\uff0c\u4f60\uff0c\u4f60\u8fd9\u662f\u5e72\u4ec0\u4e48\uff1f\u201d \u4f46\u89c9\u56db\u80a2\u7ef5\u8f6f\uff0c\u7761\u610f\u88ad\u4eba\uff0c\u6253\u4e86\u4e00\u4e2a\u5475\u6b20\uff0c\u820c\u5934\u4e5f\u6709\u70b9\u786c\u4e86\u3002\u90a3\u5c11\u5973\u8f7b\u8f7b\u4e00\u63a8\uff0c\u9648\u7384\u673a\u2018\u5495 \u549a\u2019\u4e00\u58f0\u5012\u5728\u5e8a\u4e0a\uff0c\u7761\u773c\u6726\u80e7\u4e2d\uff0c\u4f46\u89c9\u90a3\u5c11\u5973\u7684\u811a\u6b65\u58f0\u79bb\u5f00\u4e86\u623f\u95f4\uff0c\u9690\u7ea6\u8fd8\u542c\u5f97\u5979\u2018\u683c\u683c\u2019 \u7b11\u9053\uff1a\u201c\u4f60\u601d\u8651\u592a\u591a\uff0c\u7ed9\u6211\u597d\u597d\u7684\u7761\u4e00\u4e2a\u5927\u89c9\u3002\u201d \u3000\u3000\u8fd9\u4e00\u89c9\u76f4\u7761\u5230\u9ec4\u660f\u4e4b\u540e\uff0c\u9648\u7384\u673a\u4e00\u9192\u8fc7\u6765\uff0c\u7591\u5e7b\u7591\u68a6\uff0c\u4f46\u89c9\u6885\u68a2\u6708\u4e0a\uff0c\u5ba4\u5185\u7089\u9999\u8885\u8885\uff0c \u5e8a\u5934\u7684\u8336\u51e0\u4e0a\u65e9\u653e\u4e86\u4e00\u58f6\u70ed\u8336\uff0c\u81ea\u5df1\u4ecd\u7136\u662f\u5728\u8fd9\u53e4\u602a\u7684\u623f\u95f4\u3002\u9648\u7384\u673a\u8bd5\u4e00\u8fd0\u6c14\uff0c\u4f46\u89c9\u6beb\u65e0\u963b \u6cc4\uff0c\u7cbe\u795e\u4f53\u529b\uff0c\u6bd4\u65e5\u95f4\u53c8\u6062\u590d\u4e86\u51e0\u5206\uff0c\u8fd9\u624d\u604d\u7136\u5927\u609f\uff0c\u5fc3\u4e2d\u611f\u6fc0\uff0c\u60f3\u9053\uff1a\u201c\u539f\u6765\u8fd9\u4f4d\u4e91\u59d1\u5a18 \u7adf\u7cbe\u901a\u533b\u9053\uff0c\u770b\u51fa\u6211\u5fc3\u6709\u6240\u601d\uff0c\u6015\u788d\u4e86\u6211\u7684\u590d\u539f\u3002\u6545\u6b64\u7ed9\u6211\u559d\u4e86\u8fd9\u4e00\u76cf\u836f\u9152\uff0c\u7075\u4e39\u5999\u836f\uff0c\u4e0d \u8fc7\u5982\u65af\uff0c\u54b3\uff0c\u6211\u8fd8\u7591\u5fc3\u5b83\u662f\u6bd2\u9152\uff0c\u771f\u662f\u5927\u5927\u7684\u4e0d\u8be5\u3002\u201d\u623f\u95f4\u5916\u53c8\u4f20\u6765\u4e86\u811a\u6b65\u58f0\uff0c\u9648\u7384\u673a\u53ea\u9053 \u90a3\u5c11\u5973\u6765\u4e86\uff0c\u6b63\u5f85\u8d77\u8eab\u8fce\u63a5\uff0c\u72d0\u542c\u5f97\u90a3\u811a\u6b65\u58f0\u4e0d\u53ea\u4e00\u4eba\uff0c\u9648\u7384\u673a\u671b\u5916\u4e00\u77a7\uff0c\u4f46\u89c1\u90a3\u7409\u7483\u7a97\u683c \u4e0a\u6620\u51fa\u4e24\u4e2a\u9ad8\u5927\u7684\u5f71\u5b50\uff0c\u5176\u4e2d\u4e00\u4eba\u7b11\u9053\uff1a\u201c\u821e\u9633\u5144\uff0c\u4f60\u8fd9\u91cc\u771f\u4f3c\u795e\u4ed9\u6d1e\u5e9c\uff0c\u602a\u4e0d\u5f97\u4f60\u9690\u5c45\u5341 \u591a\u5e74\u8db3\u4e0d\u4e0b\u5c71\u3002\u6211\u8f88\u788c\u788c\u98ce\u5c18\uff0c\u6bd4\u8d77\u8001\u5144\uff0c\u96c5\u4fd7\u662f\u4e0d\u53ef\u9053\u7406\u8ba1\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4eba\u8bf4\u8bdd\u8bf4\u5f97\u6781\u8f7b\uff0c\u4f46\u542c\u5728\u9648\u7384\u673a\u7684\u8033\u4e2d\uff0c\u5374\u4f3c\u7126\u96f7\u8f70\u9876\u3002 \u3000\u3000\u539f\u6765\u5916\u9762\u7684\u4e24\u4e2a\u4eba\u4e4b\u4e2d\uff0c\u6709\u4e00\u4e2a\u7adf\u7136\u662f\u81ea\u5df1\u6240\u8981\u523a\u6740\u7684\u4e91\u821e\u9633\uff0c\u6562\u60c5\u8fd9\u91cc\u5c31\u662f\u4e91\u821e\u9633\u7684 \u5bb6\uff01 \u3000\u3000\u4f46\u542c\u5f97\u4e00\u4e2a\u82cd\u8001\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u5341\u4f59\u5e74\u6765\u5c0f\u5f1f\u6beb\u65e0\u5bf8\u8fdb\uff0c\u600e\u6bd4\u5f97\u543e\u5144\u6276\u52a9\u660e\u4e3b\uff0c\u5c61\u5efa\u5947 \u529f\uff1f\u201d\u9648\u7384\u673a\u5fc3\u5934\u4e00\u6c89\uff0c\u542c\u8fd9\u8bdd\u8bed\uff0c\u4e91\u821e\u9633\u679c\u7136\u662f\u80cc\u53db\u6545\u4e3b\uff0c\u548c\u671d\u5ef7\u7684\u663e\u8d35\u52fe\u642d\u4e0a\u4e86\uff0c\u53ea\u4e0d \u77e5\u8fd9\u6765\u8005\u5374\u662f\u4f55\u4eba\uff1f \u3000\u3000\u7a97\u5916\u706f\u5149\u4e00\u95ea\uff0c\u90a3\u5c11\u5973\u63d0\u7740\u706f\u7b3c\u8fce\u4e86\u51fa\u6765\uff0c\u53eb\u9053\uff1a\u201c\u7239\uff0c\u4f60\u56de\u6765\u5566\uff01\u201d\u4e91\u821e\u9633\u9053\uff1a \u201c\u6664\uff0c\u56de\u5f97\u665a\u4e86\u3002\u8fd9\u4f4d\u662f\u7f57\u4f2f\u4f2f\uff0c\u9526\u8863\u536b\u603b\u6307\u6325\u7f57\u91d1\u5cf0\u7f57\u5927\u4eba\uff01\u201d\u90a3\u5c11\u5973\u4e0d\u61c2\u9526\u8863\u536b\u5230\u5e95\u662f \u4ec0\u4e48\uff0c\u6de1\u6de1\u7684\u798f\u4e86\u4e00\u798f\u3002\u9648\u7384\u673a\u53ef\u662f\u5fc3\u4e2d\u6253\u9f13\uff0c\u539f\u6765\u8fd9\u4eba\u7adf\u662f\u6731\u5143\u748b\u624b\u4e0b\u7684\u7b2c\u4e00\u9ad8\u624b\uff0c\u5f53\u5e74 \u957f\u6c5f\u4e4b\u6218\uff0c\u5f20\u4e16\u8bda\u5c31\u662f\u7ed9\u4ed6\u4eb2\u624b\u64d2\u83b7\u7684\u3002\u56e0\u6b64\u5efa\u6b64\u5947\u529f\uff0c\u6240\u4ee5\u624d\u505a\u5230\u4e13\u95e8\u902e\u6355\u72af\u4eba\u7684\u9526\u8863\u536b \u603b\u6307\u6325\uff0c\u8fd9\u970e\u90a3\u95f4\u9648\u7384\u673a\u4f46\u89c9\u8840\u8109\u6124\u5f20\uff0c\u6124\u6012\u4e2d\u5374\u53c8\u6709\u4e9b\u60f6\u6050\uff01 \u3000\u3000\u9648\u7384\u673a\u53d7\u4e86\u5e08\u53cb\u91cd\u6258\uff0c\u51b3\u610f\u524d\u6765\u884c\u523a\u4e91\u821e\u9633\u7684\u65f6\u5019\uff0c\u672c\u5c31\u77e5\u9053\u4e91\u821e\u9633\u6b66\u529f\u9ad8\u5f3a\uff0c\u5e76\u4e0d\u6253 \u7b97\u6d3b\u7740\u56de\u53bb\uff0c\u4eca\u65e5\u89c1\u4e86\u4ed6\u5973\u513f\u7684\u5251\u6cd5\uff0c\u66f4\u662f\u5403\u60ca\uff0c\u539f\u6765\u4e91\u821e\u9633\u6b66\u529f\u4e4b\u5f3a\uff0c\u6bd4\u81ea\u5df1\u60f3\u8c61\u7684\uff0c\u8fd8 \u8981\u9ad8\u51fa\u4e0d\u77e5\u51e0\u500d\uff1f \u3000\u3000\u4f55\u51b5\u4ed6\u8fd8\u548c\u5927\u5185\u7684\u7b2c\u4e00\u9ad8\u624b\u540c\u6765\uff0c\u53ea\u6015\u5c31\u662f\u62fc\u4e86\u6027\u547d\uff0c\u4e5f\u672a\u5fc5\u884c\u523a\u7684\u6210\u4e86\u3002 \u3000\u3000\u4f46\u4ee4\u9648\u7384\u673a\u5185\u5fc3\u98a4\u6144\uff0c\u60f6\u6050\u4e0d\u5b89\u7684\uff0c\u8fd9\u5e76\u4e0d\u662f\u4e3a\u4e86\u5bb3\u6015\u4e91\u821e\u9633\u6b66\u529f\u7684\u9ad8\u5f3a\uff0c\u800c\u662f\uff0c\u5440\uff0c \u4ed6\u7adf\u662f\u90a3\u4e2a\u59d1\u5a18\u7684\u7236\u4eb2\uff01\u90a3\u4e2a\u6551\u4e86\u81ea\u5df1\u6027\u547d\uff0c\u800c\u53c8\u662f\u90a3\u6837\u5929\u771f\u70c2\u6f2b\uff0c\u751c\u871c\u53ef\u7231\u7684\u59d1\u5a18\u7684\u7236 \u4eb2\uff01 \u3000\u3000\u8ff7\u832b\u4e2d\u5ffd\u542c\u5f97\u4e91\u821e\u9633\u95ee\u9053\uff1a\u201c\u8c01\u5728\u8fd9\u4e66\u623f\u91cc\u9762\uff1f\u201d\u8fd9\u4e00\u95ee\u767b\u65f6\u628a\u9648\u7384\u673a\u5413\u5f97\u8df3\u4e86\u8d77\u6765\uff0c \u6025\u5fd9\u6293\u8d77\u4e86\u538b\u5728\u6795\u5934\u4e0b\u9762\u7684\u957f\u5251\uff0c\u4f46\u542c\u5f97\u90a3\u4e2a\u5c11\u5973\u7684\u58f0\u97f3\u7b54\u9053\uff1a\u201c\u662f\u4e00\u4e2a\u53d7\u4e86\u91cd\u4f24\u7684\u5c11\u5e74\uff0c \u8dcc\u5728\u5c71\u6da7\u4e4b\u4e2d\uff0c\u65e0\u4eba\u6599\u7406\uff0c\u662f\u5973\u513f\u5c06\u4ed6\u5e26\u56de\u6765\u7684\u3002\u201d\u4e91\u821e\u9633\u8bf4\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u6837\u7684\u5c11\u5e74\uff0c\u600e\u4e48 \u53d7\u7684\u4f24\uff1f\u201d\u90a3\u5c11\u5973\u9053\uff1a\u201c\u4ed6\u7761\u4e86\u4e00\u5929\u4e00\u591c\uff0c\u4eca\u65e9\u521a\u521a\u9192\u8f6c\u3002\u5973\u513f\u8fd8\u672a\u53ca\u5411\u4ed6\u591a\u95ee\u3002\u201d\u4e91\u821e\u9633 \u9053\uff1a\u201c\u7d20\u7d20\uff0c\u4f60\u771f\u591a\u4e8b\u3002\u201d\u9648\u7384\u673a\u8fd9\u624d\u77e5\u9053\u8fd9\u4e2a\u5c11\u5973\u53eb\u4e91\u7d20\u7d20\uff0c\u5fc3\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u6f02\u4eae\u7684\u540d \u5b57\u3002\u201d \u3000\u3000\u4f46\u542c\u5f97\u4e91\u7d20\u7d20\u597d\u50cf\u53d7\u4e86\u65e0\u9650\u59d4\u5c48\u7684\u53eb\u8d77\u6765\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u5e73\u65e5\u4e0d\u662f\u5e38\u548c\u6211\u8bf4\u884c\u4fa0\u4ed7\u4e49\u7684 \u4e8b\u4e48\uff1f\u773c\u89c1\u4e00\u4e2a\u964c\u751f\u7684\u5f02\u4e61\u5ba2\u4eba\uff0c\u53d7\u4e86\u91cd\u4f24\uff0c\u4e5f\u6b65\u7ba1\u4e48\uff1f\u201d\u4e91\u821e\u9633\u9053\uff1a\u201c\u4e5f\u4e0d\u5fc5\u5c06\u4ed6\u5b89\u7f6e\u5728 \u4e66\u623f\u91cc\u5440\u3002\u201d\u4e91\u7d20\u7d20\u9053\uff1a\u201c\u5988\u5988\u6015\u5608\uff0c\u96be\u9053\u5c06\u4ed6\u5b89\u7f6e\u5728\u5185\u8fdb\u623f\u4e48\uff1f\u201d \u3000\u3000\u4e91\u821e\u9633\u9053\uff1a\u201c\u53d7\u7684\u4ec0\u4e48\u4f24\uff1f\u201d\u4e91\u7d20\u7d20\u9053\uff1a\u201c\u597d\u50cf\u662f\u5185\u5bb6\u638c\u529b\u7684\u91cd\u4f24\u3002\u201d\u4e91\u821e\u9633\u9053\uff1a\u201c\u600e \u4e48\u53ea\u4e00\u5929\u4e00\u591c\u5c31\u4f1a\u597d\u4e86\uff1f\u201d\u4e91\u7d20\u7d20\u9053\uff1a\u201c\u662f\u5973\u513f\u5c06\u4e09\u9897\u5c11\u9633\u5c0f\u8fd8\u4e39\u7ed9\u4ed6\u5403\u4e86\uff0c\u4eca\u671d\u9192\u6765\u4e4b \u540e\uff0c\u5973\u513f\u53c8\u5c06\u7236\u4eb2\u917f\u7684\u4e5d\u5929\u743c\u82b1\u56de\u9633\u9152\u7ed9\u4ed6\u559d\u4e86\u4e00\u76cf\uff0c\u53ea\u6015\u5982\u4eca\u8fd8\u7761\u7740\u672a\u9192\u5462\uff01\u201d\u4e91\u821e\u9633 \u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u90a3\u5c0f\u8fd8\u4e39\u662f\u6211\u5411\u5f52\u85cf\u5927\u5e08\u518d\u4e09\u6c42\u6765\u7684\uff0c\u4e00\u5171\u624d\u8ba8\u5f97\u516d\u7c92\uff0c\u4f60\u4e00\u4e0b\u5b50\u5c31\u7ed9\u6211\u9001\u51fa \u4e86\u4e00\u534a\uff0c\u90a3\u4e5d\u5929\u743c\u82b1\u56de\u9633\u9152\uff0c\u4e5f\u662f\u82b1\u4e86\u4e94\u5e74\u529f\u592b\uff0c\u624d\u91c7\u9f50\u914d\u6599\u917f\u51fa\u6765\u7684\uff0c\u4f60\u77e5\u9053\u4e48\uff1f\u201d \u3000\u3000\u4e91\u7d20\u7d20\u9053\uff1a\u201c\u5973\u513f\u77e5\u9053\uff0c\u7239\uff0c\u4f60\u602a\u6211\u5566\uff1f\u201d\u90a3\u526f\u6492\u5a07\u7684\u795e\u60c5\uff0c\u9648\u7384\u673a\u867d\u662f\u53ea\u542c\u5176\u58f0\uff0c\u4ea6 \u53ef\u60f3\u8c61\u5f97\u51fa\u3002\u4e0d\u7531\u5f97\u5fc3\u5934\u4e00\u8361\uff0c\u66f4\u66fe\u60f6\u6050\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u6211\u4e0e\u5979\u7d20\u4e0d\u76f8\u8bc6\uff0c\u5979\u7adf\u7136\u5982\u6b64\u5f85 \u6211\uff01\u201d\u4e16\u95f4\u771f\u6709\u6599\u60f3\u4e0d\u5230\u4e4b\u4e8b\uff0c\u8427\u97f5\u5170\u5bf9\u4ed6\u70ed\u60c5\u5982\u706b\uff0c\u4ed6\u4ece\u672a\u52a8\u5fc3\uff0c\u5982\u4eca\u867d\u7136\u53ea\u662f\u548c\u4e91\u7d20\u7d20 \u624d\u89c1\u4e00\u9762\uff0c\u5374\u5df2\u88ab\u5979\u7684\u67d4\u60c5\u6240\u56f0\u6270\u4e86\u3002 \u3000\u3000\u53ea\u542c\u5f97\u4e91\u821e\u9633\u7b11\u9053\uff1a\u201c\u5f85\u4ed6\u660e\u65e5\u9192\u6765\uff0c\u6211\u5012\u8981\u4e0e\u4ed6\u8c08\u8bba\u8c08\u8bba\uff0c\u8003\u5bdf\u4ed6\u7684\u4eba\u54c1\u6b66\u529f\uff0c\u770b\u662f \u5426\u503c\u5f97\u7ed9\u4ed6\u8fd9\u4e09\u9897\u5c0f\u8fd8\u4e39\u3002\u201d\u4e00\u822c\u4eba\u559d\u4e86\u4e5d\u5929\u743c\u82b1\u56de\u9633\u9152\u4e4b\u540e\uff0c\u603b\u5f97\u7761\u4e00\u5929\u4e00\u591c\uff0c\u662f\u4ee5\u4e91\u821e \u9633\u6709\u201c\u5f85\u4ed6\u660e\u65e5\u9192\u6765\u201d\u4e4b\u8bed\uff0c\u5c82\u77e5\u9648\u7384\u673a\u5185\u529f\u6df1\u539a\uff0c\u670d\u4e86\u5c0f\u8fd8\u4e39\u4e4b\u540e\uff0c\u4f24\u52bf\u53c8\u597d\u4e86\u4e00\u534a\uff0c\u53ea \u7761\u4e86\u4e00\u5929\uff0c\u5c31\u9192\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u9648\u7384\u673a\u5fc3\u4e2d\u5fd0\u5fd1\u4e0d\u5b89\uff0c\u8fd9\u4e00\u665a\u662f\u4e58\u673a\u5c06\u4ed6\u6740\u6b7b\u5462\uff1f\u8fd8\u662f\u4e58\u673a\u9003\u8d70\u5462\uff1f\u5fc3\u4e2d\u5140\u81ea\u62ff\u4e0d\u5b9a\u4e3b \u610f\u3002 \u3000\u3000\u53ea\u542c\u5f97\u4e91\u821e\u9633\u95ee\u9053\uff1a\u201c\u4f60\u5a18\u8fd9\u51e0\u5929\u600e\u4e48\u6837\uff1f\u201d\u4e91\u7d20\u7d20\u9053\uff1a\u201c\u8fd8\u4e0d\u662f\u8001\u6837\u5b50\u3002\u201d\u4e91\u821e\u9633 \u9053\uff1a\u201c\u6211\u7559\u7ed9\u5979\u7684\u65b9\u5b50\uff0c\u4f60\u6bcf\u5929\u7ed9\u5979\u7172\u4e86\u836f\u8336\u4e48\uff1f\u201d\u4e91\u7d20\u7d20\u9053\uff1a\u201c\u5a18\u8bf4\u8fd9\u836f\u5403\u4e86\u4e5f\u662f\u90a3\u4e2a \u6837\uff0c\u5934\u4e24\u5929\u8fd8\u559d\u534a\u7897\uff0c\u540e\u6765\u5c31\u53eb\u6211\u4e0d\u7528\u714e\u4e86\u3002\u7239\uff0c\u5a18\u7684\u75c5\u4e3a\u4ec0\u4e48\u603b\u533b\u4e0d\u597d\uff1f\u201d \u3000\u3000\u7f57\u91d1\u5cf0\u9053\uff1a\u201c\u5ac2\u5b50\u8eab\u5b50\u4e0d\u8212\u670d\u4e48\uff1f\u201d\u4e91\u821e\u9633\u9053\uff1a\u201c\u4e5f\u4e0d\u662f\u4ec0\u4e48\u5927\u75c5\uff0c\u5c31\u662f\u5e38\u5e38\u95f9\u5934\u75db\uff0c \u4e0d\u559c\u6b22\u8d70\u52a8\u3002\u55ef\uff0c\u7d20\u7d20\uff0c\u4f60\u8fdb\u53bb\u8bf4\u7ed9\u4f60\u5a18\u542c\uff0c\u8bf4\u6211\u660e\u65e9\u518d\u8fc7\u53bb\u770b\u5979\u3002\u201d \u3000\u3000\u9648\u7384\u673a\u4e8b\u6bcd\u6700\u5b5d\uff0c\u542c\u4e86\u4e91\u821e\u9633\u8fd9\u8bdd\uff0c\u53ea\u89c9\u6709\u70b9\u523a\u8033\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u59bb\u5b50\u6709\u75c5\uff0c\u4e08\u592b\u5f52 \u5bb6\uff0c\u5374\u4e0d\u5148\u53bb\u770b\u5979\uff0c\u5c82\u975e\u6709\u70b9\u4e0d\u8fd1\u4eba\u60c5\uff1f\u542c\u6b66\u529f\u524d\u8f88\u8bf4\uff0c\u8fd9\u4e91\u821e\u9633\u7684\u59bb\u5b50\u4e43\u662f\u6b66\u5f53\u6d3e\u8001\u638c\u95e8 \u725f\u72ec\u9038\u7684\u5973\u513f\uff0c\u5341\u591a\u5e74\u524d\uff0c\u4e91\u821e\u9633\u80cc\u53db\u6545\u4e3b\u7684\u75d5\u8ff9\u672a\u9732\uff0c\u6b66\u6797\u4e2d\u4eba\u90fd\u8fd8\u7fa1\u6155\u4ed6\u4eec\u662f\u4e00\u5bf9\u96be\u5f97 \u7684\u98ce\u5c18\u4fa0\u4fa3\u5462\uff01\u5c82\u77e5\u4ed6\u4eec\u592b\u59bb\u4e4b\u60c5\u7adf\u662f\u5982\u6b64\u51b7\u6f20\uff0c\u8fd9\u4f4d\u4e91\u592a\u592a\u4e5f\u5947\u602a\uff0c\u867d\u8bf4\u8eab\u5b50\u4e0d\u9002\uff0c\u4e0d\u559c \u8d70\u52a8\uff0c\u4f46\u65e2\u7136\u4e0d\u662f\u75c5\u5230\u4e0d\u80fd\u8d77\u5e8a\uff0c\u4f55\u4ee5\u4e08\u592b\u56de\u5bb6\u4e86\u4e5f\u4e0d\u51fa\u6765\u3002\u201d \u3000\u3000\u4e91\u7d20\u7d20\u5e94\u4e86\u4e00\u58f0\uff0c\u8e51\u7740\u811a\u6b65\uff0c\u8f7b\u8f7b\u8d70\u51fa\uff0c\u4f46\u89c1\u7409\u7483\u7a97\u4e0a\uff0c\u4eba\u5f71\u4e00\u95ea\uff0c\u9648\u7384\u673a\u6025\u5fd9\u88c5\u7761\uff0c \u6697\u4e2d\u5408\u773c\u5077\u7aa5\uff0c\u53ea\u89c1\u4e91\u7d20\u7d20\u90a3\u5f20\u4fcf\u8138\uff0c\u8d34\u5728\u7409\u7483\u7a97\u4e0a\uff0c\u6708\u591c\u5e7d\u5ead\uff0c\u6a2a\u659c\u6885\u5f71\uff0c\u7f8e\u5973\u4e00\u4eba\uff0c\u4e34 \u7a97\u7aa5\u7761\uff0c\u8fd9\u60c5\u666f\u771f\u662f\u9ad8\u624b\u753b\u5e08\u4e5f\u753b\u4e0d\u51fa\uff0c\u9648\u7384\u673a\u5fcd\u4e0d\u4f4f\u795e\u98d8\u610f\u8361\uff0c\u4f46\u542c\u5f97\u4e91\u7d20\u7d20\u5728\u7a97\u5916\u8f7b\u8f7b \u4e00\u7b11\uff0c\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u5c0f\u4e56\u4e56\uff0c\u597d\u597d\u7761\u5427\uff0c\u4f60\u8fd9\u6837\u60f3\u5bb6\uff0c\u5728\u68a6\u4e2d\u53bb\u89c1\u4f60\u7684\u5988\u5988\u5427\u3002\u6211\u4e5f\u8981\u53bb \u4f3a\u5019\u6bcd\u4eb2\u5566\u3002\u201d\u9648\u7384\u673a\u542c\u5979\u53eb\u81ea\u5df1\u505a\u201c\u5c0f\u4e56\u4e56\u201d\uff0c\u54d1\u7136\u5931\u7b11\uff0c\u4f46\u5fc3\u4e2d\u5374\u662f\u5145\u6ee1\u65e0\u9650\u67d4\u60c5\uff0c\u542c \u5f97\u4e91\u7d20\u7d20\u7684\u811a\u6b65\u58f0\u6e10\u8fdc\u6e10\u9690\uff0c\u51e0\u4e4e\u60f3\u5c06\u5979\u5524\u4f4f\u3002 \u3000\u3000\u4f46\u4e91\u821e\u9633\u7684\u4e00\u53e5\u8bdd\u5374\u5c06\u4ed6\u5728\u5982\u68a6\u5982\u9189\u4e2d\u5524\u9192\u8fc7\u6765\uff0c\u53ea\u542c\u5f97\u4e91\u821e\u9633\u8bf4\u9053\uff1a\u201c\u7f57\u5144\u4e0d\u5728\u4eac\u4e2d \u7eb3\u798f\uff0c\u60e0\u4e34\u5c71\u5e84\uff0c\u6562\u662f\u5f53\u4eca\u5723\u4e0a\u6709\u4f55\u5dee\u9063\u4e48\uff1f\u201d\u7f57\u91d1\u5cf0\u9053\uff1a\u201c\u543e\u5144\u5584\u4f53\u4e3b\u5fc3\uff0c\u5c0f\u5f1f\u81ea\u5f53\u660e \u8bf4\u3002\u60f3\u5f53\u4eca\u5723\u4e0a\u4e0e\u5f20\u4e16\u8bda\u539f\u662f\u516b\u62dc\u4e4b\u4ea4\uff0c\u53ea\u53ef\u60dc\u5f20\u4e16\u8bda\u4e0d\u80af\u5f52\u987a\uff0c\u5929\u65e0\u4e8c\u65e5\uff0c\u6c11\u65e0\u4e8c\u4e3b\uff0c\u5723 \u4e0a\u4e0d\u5f97\u5df2\u5c06\u4ed6\u8d50\u6b7b\uff0c\u8fd9\u4e5f\u662f\u65e0\u53ef\u5948\u4f55\u4e4b\u4e8b\uff1b\u4e0d\u60f3\u5f20\u4e16\u8bda\u90e8\u5c5e\uff0c\u5374\u6709\u591a\u4eba\u4e0d\u670d\uff0c\u5982\u4eca\u5929\u4e0b\u5df2 \u5b9a\uff0c\u6d2a\u6b66\u5f00\u57fa\u4e5f\u5df2\u5341\u6709\u4e09\u5e74\uff0c\u4ed6\u4eec\u8fd8\u5728\u8349\u6cfd\u4e4b\u4e2d\uff0c\u4f3a\u673a\u5f85\u8d77\uff0c\u8fd9\u5c82\u4e0d\u662f\u592a\u4e0d\u8bc6\u65f6\u52a1\u4e86\u4e48\uff1f\u201d \u3000\u3000\u4e91\u821e\u9633\u9053\uff1a\u201c\u662f\u5440\uff0c\u4e3a\u4e00\u5bb6\u4e00\u59d3\uff0c\u4e89\u593a\u6c5f\u5c71\uff0c\u82e6\u5bb3\u9ece\u6c11\uff0c\u8fd9\u53c8\u4f55\u5fc5\uff1f\u6240\u4ee5\u6211\u770b\u900f\u4e86\uff0c\u8fd9 \u624d\u7518\u613f\u8001\u6b7b\u8352\u5c71\u3002\u201d\u9648\u7384\u673a\u4e00\u9707\uff0c\u60f3\u9053\uff1a\u201c\u4e3a\u4e00\u5bb6\u4e00\u59d3\uff0c\u4e89\u593a\u6c5f\u5c71\uff0c\u82e6\u5bb3\u9ece\u6c11\uff0c\u8fd9\u53c8\u4f55 \u5fc5\uff1f\u201d\u8fd9\u79cd\u8bdd\uff0c\u4ece\u672a\u6709\u4eba\u5411\u4ed6\u8bf4\u8fc7\uff0c\u53ea\u89c9\u4e91\u821e\u9633\u8bf4\u7684\u4e5f\u672a\u5c1d\u6ca1\u6709\u9053\u7406\uff0c\u5fc3\u4e2d\u518d\u60f3\u9053\uff1a\u201c\u53ea\u8981 \u4e91\u821e\u9633\u771f\u662f\u7518\u5fc3\u8001\u6b7b\u8352\u5c71\uff0c\u6211\u53c8\u4f55\u5fc5\u8981\u884c\u523a\u4ed6\uff1f\u201d \u3000\u3000\u53ea\u542c\u5f97\u7f57\u91d1\u5cf0\u7b11\u9053\uff1a\u201c\u543e\u5144\u660e\u8fbe\u8fc7\u4eba\uff0c\u5c0f\u5f1f\u4f69\u670d\u3002\u53ea\u662f\u90a3\u4e9b\u4eba\u65e2\u7136\u4e0e\u5723\u4e0a\u4f5c\u5bf9\uff0c\u7978\u80ce\u672a \u9664\uff0c\u5723\u4e0a\u5c82\u80fd\u5b89\u5fc3\u3002\u543e\u5144\u6b66\u529f\u7edd\u4e16\uff0c\u4fd7\u8bed\u4e91\uff1a\u8c79\u6b7b\u7559\u76ae\uff0c\u4eba\u6b7b\u7559\u540d\u3002\u543e\u5144\u7518\u8001\u8352\u5c71\uff0c\u8fd9\u4e0d\u592a \u53ef\u60dc\u4e86\u4e48\uff1f\u201d \u3000\u3000\u4e91\u821e\u9633\u9053\uff1a\u201c\u6b66\u529f\u9ad8\u7edd\u7684\u79f0\u8a89\uff0c\u53ea\u6709\u7f57\u5144\u53ef\u4ee5\u53d7\u4e4b\u65e0\u6127\uff0c\u5c0f\u5f1f\u90a3\u91cc\u6562\u5f53\uff1f\u5723\u4e0a\u6709\u543e\u5144\u8f85 \u4f50\uff0c\u4f55\u987b\u7528\u5230\u5c0f\u5f1f\u5eb8\u52a3\u4e4b\u624d\uff1f\u201d \u3000\u3000\u7f57\u91d1\u5cf0\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e91\u5144\u6b64\u8a00\uff0c\u592a\u89c1\u5916\u4e86\u3002\u53ea\u56e0\u671d\u4e0a\u65e0\u4eba\uff0c\u5c0f\u5f1f\u624d\u6562\u6ee5\u7afd\u5145\u6570\u8fd9\u9526\u8863\u536b \u603b\u6307\u6325\u4e4b\u804c\uff0c\u5c0f\u5f1f\u53ea\u662f\u6682\u4ee3\uff0c\u7b49\u5019\u8001\u5144\u51fa\u5c71\u5462\u3002\u201d \u3000\u3000\u4e91\u821e\u9633\u9053\uff1a\u201c\u7f57\u5144\u5c3d\u662f\u5f80\u5c0f\u5f1f\u8138\u4e0a\u8d34\u91d1\uff0c\u66f4\u662f\u53eb\u5c0f\u5f1f\u6127\u715e\u4e86\u3002\u5c0f\u5f1f\u80fd\u505a\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7f57\u91d1\u5cf0\u9053\uff1a\u201c\u60f3\u5f20\u4e16\u8bda\u7684\u90e8\u5c5e\uff0c\u5341\u4e5d\u90fd\u662f\u4e91\u5144\u65e7\u4ea4\uff0c\u5723\u4e0a\u60f3\u8bf7\u4e91\u5144\u53bb\u529d\u529d\u4ed6\u4eec\u3002\u201d\u4e91\u821e \u9633\u9053\uff1a\u201c\u82e5\u662f\u4ed6\u4eec\u4e0d\u80af\u542c\u5462\uff1f\u201d \u3000\u3000\u7f57\u91d1\u5cf0\u7b11\u9053\uff1a\u201c\u8001\u5144\u662f\u660e\u767d\u4eba\uff0c\u4f55\u987b\u5c0f\u5f1f\u591a\u8bf4\uff1f\u8001\u5144\u82e5\u662f\u788d\u4e8e\u6545\u4ea4\u4e4b\u60c5\uff0c\u4e0d\u613f\u52a8\u624b\uff0c\u53ea \u8bf7\u8001\u5144\u5c06\u4ed6\u4eec\u7684\u8e2a\u8ff9\u544a\u77e5\u5c0f\u5f1f\uff0c\u529f\u52b3\u5f53\u7136\u8fd8\u7b97\u662f\u8001\u5144\u7684\u3002\u201d \u3000\u3000\u9648\u7384\u673a\u5fc3\u5934\u9707\u6817\uff0c\u8fc7\u4e00\u9635\uff0c\u53ea\u542c\u5f97\u4e91\u821e\u9633\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u6211\u9690\u5c45\u591a\u5e74\uff0c\u5bf9\u4ed6\u4eec\u7684\u884c\u6b62\u4e5f\u5e76 \u4e0d\u662f\u5c3d\u90fd\u6e05\u695a\uff0c\u8fd9\u6837\u5427\uff0c\u8bf7\u543e\u5144\u4ee5\u4e09\u6708\u4e3a\u671f\uff0c\u4e09\u6708\u4e4b\u540e\uff0c\u8bf7\u518d\u60e0\u4e34\u5c71\u5e84\uff0c\u5c0f\u5f1f\u81ea\u5f53\u6709\u4ee5\u8986 \u547d\u3002\u201d \u3000\u3000\u8a00\u4e0b\u4e4b\u610f\uff0c\u4ed6\u5728\u8fd9\u4e09\u4e2a\u6708\u4e2d\uff0c\u4fbf\u53ef\u5c06\u5f20\u4e16\u8bda\u65e7\u90e8\u7684\u884c\u85cf\u67e5\u4e2a\u6e05\u695a\uff0c\u51c6\u5907\u6362\u4e2a\u9ad8\u5b98\u539a\u7235 \u4e86\u3002\u9648\u7384\u673a\u4e0d\u7981\u6012\u6c14\u53c8\u751f\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4ef7\u7b97\u4f60\u4e0d\u8d5e\u540c\u4e3a\u4e00\u5bb6\u4e00\u59d3\u4e89\u593a\u6c5f\u5c71\uff0c\u7f6e\u8eab\u4e16\u5916\uff0c\u4e5f \u8fd8\u7f62\u4e86\u3002\u4f60\u82e5\u6697\u4e2d\u544a\u5bc6\uff0c\u90a3\u53ef\u5bb3\u4e86\u4e0d\u77e5\u591a\u5c11\u82f1\u96c4\uff01\u201d \u3000\u3000\u7f57\u91d1\u5cf0\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e09\u6708\u4e4b\u540e\uff0c\u5c0f\u5f1f\u51c6\u5b9a\u4f9d\u65f6\u5230\u8bbf\u3002\u6b64\u5730\u6211\u4e0d\u4fbf\u4e45\u7559\uff0c\u544a\u8f9e\u4e86\u3002\u201d\u4f46\u542c \u5f97\u4e91\u821e\u9633\u5c06\u4ed6\u9001\u51fa\u95e8\u53e3\uff0c\u53c8\u6298\u56de\u5ead\u9662\uff0c\u541f\u58f0\u6e05\u60a6\uff0c\u6fc0\u6602\u6177\u6168\u4e4b\u4e2d\u53c8\u4f3c\u542b\u6709\u96be\u4ee5\u540d\u8bf4\u7684\u54c0\u4f24\uff0c \u9648\u7384\u673a\u6014\u4e86\u4e00\u6014\uff0c\u7ec6\u7ec6\u7422\u78e8\uff0c\u5374\u662f\u4e0d\u89e3\u8bd7\u4e2d\u4e4b\u610f\u3002 \u3000\u3000\u72d0\u542c\u90a3\u89d2\u95e8\u2018\u5440\u2019\u7684\u4e00\u58f0\u88ab\u4eba\u63a8\u5f00\uff0c\u811a\u6b65\u58f0\u81ea\u5916\u8d70\u5165\uff0c\u9648\u7384\u673a\u5947\u9053\uff1a\u201c\u600e\u4e48\u90a3\u7f57\u91d1\u5cf0\u53c8 \u56de\u6765\u4e86\u3002\u201d\u62ac\u8d77\u5934\u6765\uff0c\u5f80\u7a97\u5916\u4e00\u77a7\uff0c\u8fd9\u5239\u90a3\u95f4\uff0c\u9648\u7384\u673a\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u775b\uff0c\u8fd9\u4e2a\u4ece\u5916 \u9762\u8d70\u8fdb\u6765\u7684\u4eba\u7adf\u7136\u662f\u4e0a\u5b98\u5929\u91ce\uff01 \u3000\u3000\u4e91\u821e\u9633\u4e5f\u4f3c\u6709\u4e9b\u60ca\u8be7\uff0c\u4f46\u4ed6\u7a76\u662f\u6b66\u5b66\u5927\u5e08\u7684\u8eab\u4efd\uff0c\u770b\u4e86\u4e0a\u5b98\u5929\u91ce\u4e00\u773c\uff0c\u4e0d\u52a8\u58f0\u8272\uff0c\u6de1\u6de1 \u95ee\u9053\uff1a\u201c\u5c0a\u9a7e\u4f55\u4eba\uff1f\u4f55\u4ee5\u6df1\u591c\u5230\u6b64\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u725f\u4e00\u6817\u8c34\u5f1f\u5b50\u4e0a\u5b98\u5929\u91ce\u95ee\u5019\u4e91 \u8001\u524d\u8f88\uff01\u201d\u4e91\u821e\u9633\u9762\u8272\u4e00\u53d8\uff0c\u5ffd\u7684\u51b7\u7b11\u9053\uff1a\u201c\u5c0a\u9a7e\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u600e\u4e48\u4fbf\u5b66\u4f1a\u4e86\u8bf4\u8c0e\uff0c\u725f\u4e00\u6817\u4e0d \u662f\u4eca\u5e74\u516b\u6708\u624d\u8fc7\u4e16\u7684\u4e48\uff1f\u201d \u3000\u3000\u8fd9\u725f\u4e00\u6817\u662f\u725f\u72ec\u9038\u7684\u4f84\u513f\uff0c\u7ee7\u725f\u72ec\u9038\u4e4b\u540e\uff0c\u62c5\u4efb\u6b66\u5f53\u6d3e\u7684\u638c\u95e8\uff0c\u9648\u7384\u673a\u542c\u4e86\uff0c\u4e0d\u7981\u5927\u4e3a \u5403\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u539f\u6765\u4e0a\u5b98\u5929\u91ce\u7adf\u662f\u6b66\u5f53\u6d3e\u7684\u5ae1\u4f20\u5f1f\u5b50\uff0c\u600e\u7684\u4ece\u4e0d\u89c1\u4ed6\u63d0\u8d77\uff1f\u8fd9\u4e91\u821e\u9633\u4f4f\u5728\u6df1 \u5c71\uff0c\u6d88\u606f\u4e5f\u771f\u7075\u901a\uff0c\u8fde\u6211\u4e5f\u4e0d\u77e5\u9053\u725f\u4e00\u6817\u4ee5\u7ecf\u53bb\u4e16\u3002\u201d \u3000\u3000\u53ea\u542c\u5f97\u4e0a\u5b98\u5929\u91ce\u51b7\u51b7\u7684\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b63\u56e0\u5bb6\u5e08\u6545\u4e16\uff0c\u6240\u4ee5\u5c0f\u8f88\u624d\u6562\u9886\u53d7\u9057\u547d\u524d\u6765\u3002\u4e0d \u77e5\u5e08\u59d1\u662f\u5426\u5c1a\u5065\u5728\u4eba\u95f4\uff0c\u53ef\u5426\u5bb9\u5c0f\u8f88\u62dc\u89c1\uff1f\u201d \u3000\u3000\u4e91\u821e\u9633\u51b7\u7b11\u9053\uff1a\u201c\u5185\u5b50\u4e0e\u5916\u5bb6\u65e9\u5df2\u65ad\u7edd\u6765\u5f80\uff0c\u4e0d\u52b3\u4f60\u6765\u63a2\u8bbf\u3002\u518d\u8bf4\u82e5\u662f\u725f\u5bb6\u6709\u5fc3\uff0c\u725f\u4e00 \u6817\u751f\u524d\u4f55\u4ee5\u4e0d\u6765\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u4e5f\u51b7\u7b11\u9053\uff1a\u201c\u4e91\u8001\u524d\u8f88\uff0c\u4f60\u8fd9\u662f\u660e\u77e5\u6545\u95ee\uff0c\u5148\u5e08\u987e\u5ff5\u5144\u59b9\u4e4b \u60c5\uff0c\u4e0d\u613f\u524d\u6765\u8ba8\u56de\u5251\u8c31\uff0c\u4f46\u90a3\u7ec8\u662f\u6b66\u5f53\u6d3e\u4e4b\u7269\uff0c\u5c82\u53ef\u6c38\u5b58\u5916\u4eba\u4e4b\u624b\uff0c\u8001\u524d\u8f88\u501f\u53bb\u4e86\u4e8c\u5341\u5e74\uff0c \u60f3\u6765\u4e5f\u65e9\u5df2\u80cc\u719f\u4e86\u3002\u201d \u3000\u3000\u4e91\u821e\u9633\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u539f\u6765\u725f\u4e00\u6817\u7684\u9057\u547d\uff0c\u662f\u53eb\u4f60\u505a\u638c\u95e8\u4e48\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a \u201c\u5929\u91ce\u4e0d\u624d\uff0c\u627f\u5148\u5e08\u539a\u7231\uff0c\u4e0d\u6562\u63a8\u8f9e\uff0c\u4f46\u5f85\u53d6\u56de\u5251\u8c31\uff0c\u4fbf\u5230\u6b66\u5f53\u5c71\u9886\u53d7\u8863\u94b5\u3002\u201d \u3000\u3000\u4e91\u821e\u9633\u53c8\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u9664\u4f60\u4e4b\u5916\uff0c\u8fd8\u6709\u8c01\u77e5\u9053\u5251\u8c31\u5728\u6211\u624b\u4e2d\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a \u201c\u6211\u4e5f\u53ea\u662f\u4e09\u6708\u4e4b\u524d\uff0c\u624d\u77e5\u6089\u5bb6\u5e08\u7684\u9057\u547d\u3002\u5148\u5e08\u4e3a\u4e86\u987e\u5ff5\u4eb2\u621a\u7684\u9762\u5b50\uff0c\u8fd9\u4e8b\u5305\u85cf\u4e86\u5c06\u8fd1\u4e8c\u5341 \u5e74\uff0c\u4e5f\u603b\u7b97\u5bf9\u5f97\u8d77\u4e91\u8001\u524d\u8f88\u4e86\u3002\u201d\u4e91\u821e\u9633\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u5251\u8c31\u867d\u662f\u725f\u5bb6\u4e4b\u7269\uff0c\u5374\u4e0d\u662f\u6b66\u5f53\u6d3e\u7684 \u4e1c\u897f\uff0c\u4f60\u53ef\u77e5\u9053\uff0c\u4f60\u5e08\u7236\u4e5f\u6ca1\u6709\u89c1\u8fc7\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u90a3\u662f\u5e08\u7956\u5f97\u4e86\u8fbe\u6469\u5251\u8c31\u4e4b \u540e\uff0c\u6240\u521b\u51fa\u6765\u7684\u5251\u6cd5\uff0c\u4f46\u5e08\u7956\u662f\u6b66\u5f53\u638c\u95e8\uff0c\u90a3\u8def\u5251\u6cd5\u4e5f\u91c7\u5408\u4e86\u6b66\u5f53\u7684\u5251\u6cd5\uff0c\u5e08\u7956\u7684\u539f\u610f\u672c\u6765 \u5c31\u662f\u8981\u4f20\u7ed9\u6b66\u5f53\u5f1f\u5b50\u7684\u3002\u4e91\u821e\u9633\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u542c\u8fc7\u5e08\u7956\u7684\u8bdd\u4e48\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u4e91\u8001\u524d \u8f88\uff0c\u4f60\u5728\u6b66\u6797\u4e2d\u4e5f\u7b97\u5f97\u662f\u9876\u5c16\u513f\u7684\u4eba\u7269\uff0c\u600e\u8bf4\u5f97\u51fa\u5982\u6b64\u800d\u8d56\u7684\u8bdd\u6765\uff1f\u96be\u9053\u5f53\u8fd9\u662f\u6b7b\u65e0\u5bf9\u8bc1 \u4e48\uff1f\u201d\u4e91\u821e\u9633\u9762\u4e0a\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u4f60\u82e5\u662f\u6709\u6211\u5cb3\u7236\u72ec\u9038\u8001\u4eba\u7684\u9057\u4e66\uff0c\u524d\u6765\u7d22\u53d6\uff0c\u6216\u8bb8\u6211\u8fd8\u80fd\u7ed9 \u4f60\u3002\u90a3\u662f\u725f\u5bb6\u4e4b\u7269\uff0c\u6211\u5cb3\u7236\u6ca1\u6709\u513f\u5b50\uff0c\u5373\u7b97\u662f\u4e00\u6817\u5728\u751f\uff0c\u4e5f\u4e0d\u80fd\u4e0e\u6211\u4e89\u8bba\u3002\u4e0a\u5b98\u5929\u91ce\u7eb5\u58f0\u5927 \u7b11\uff0c\u9053\uff1a\u201c\u539f\u6765\u4e8c\u5341\u5e74\u524d\uff0c\u5c31\u5df2\u540d\u9707\u5929\u4e0b\u7684\u4e91\u821e\u9633\uff0c\u7adf\u662f\u8fd9\u822c\u65e0\u8d56\uff01\u201d\u4e91\u821e\u9633\u607c\u7f9e\u6210\u6012\uff0c\u51b7 \u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u5e08\u7236\u5230\u6b64\uff0c\u4e5f\u4e0d\u6562\u5982\u6b64\u65e0\u793c\uff0c\u4f60\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u6562\u5728\u6211\u9762\u524d\u653e\u8086\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u8bf4\u9053\uff1a\u201c\u6211\u672c\u6765\u5c31\u6ca1\u6253\u7b97\u6d3b\u7740\u56de\u53bb\uff0c\u4f46\u53ea\u6015\u6211\u6b7b\u8baf\u4f20\u51fa\u4e4b\u540e\uff0c\u6b66\u5f53\u5c71\u7684\u667a\u5706\u957f \u8001\u4fbf\u4f1a\u62c6\u5f00\u6211\u7684\u9057\u4e66\uff0c\u90a3\u65f6\u6b66\u5f53\u95e8\u4e0b\uff0c\u90fd\u4f1a\u77e5\u5230\u5176\u4e2d\u539f\u6545\uff0c\u6b66\u5f53\u6d3e\u4e5f\u8bb8\u4e0d\u8db3\u4ee4\u4f60\u9707\u60e7\uff0c\u5929\u4e0b \u6b66\u6797\u7684\u516c\u65ad\uff0c\u53ea\u6015\u4e91\u8001\u524d\u8f88\u4f60\u4e5f\u53d7\u4e0d\u8d77\u554a\uff01\u201d \u3000\u3000\u4e91\u821e\u9633\u5fc3\u4e2d\u4e00\u9707\uff0c\u4ecd\u662f\u4e0d\u80af\u5728\u4e0a\u5b98\u5929\u91ce\u9762\u524d\u793a\u5f31\uff0c\u53c8\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e91\u67d0\u4e00\u751f\uff0c \u4ece\u4e0d\u53d7\u522b\u4eba\u5a01\u80c1\uff0c\u6211\u82e5\u975e\u89c1\u4f60\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u9020\u5c31\u4e0d\u6613\uff0c\u65e9\u5df2\u628a\u4f60\u6bd9\u4e86\uff0c\u54fc\uff0c\u4f60\u662f\u5f53\u771f\u60f3\u8981\u90a3\u672c \u5251\u8c31\u4e48\uff1f\u201d\u8fd9\u53e5\u8bdd\u5916\u521a\u5185\u67d4\uff0c\u9648\u7384\u673a\u53ea\u9053\u4e0a\u5b98\u5929\u91ce\u5b9a\u7136\u8d81\u52bf\u575a\u6301\uff0c\u90a3\u6599\u4e0a\u5b98\u5929\u91ce\u53e3\u98ce\u4e00\u53d8\uff0c \u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u6211\u65e9\u77e5\u9053\u4f60\u8981\u72ec\u9738\u5929\u4e0b\uff0c\u6210\u4e3a\u6b66\u6797\u7684\u7b2c\u4e00\u5251\u5ba2\uff0c\u90a3\u5251\u8c31\u5c82\u80af\u8f7b\u6613\u4ea4\u8fd8\uff1f\u201d\u8fd9\u53e5 \u8bdd\u6b63\u6253\u4e2d\u4e91\u821e\u9633\u5fc3\u574e\uff0c\u8fd8\u8c31\u4e4b\u610f\uff0c\u500f\u7684\u6253\u6d88\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u65e2\u7136\u77e5\u9053\uff0c\u8fd8\u6765\u8fd9\u91cc\u5e72\u4ec0 \u4e48\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u4f60\u8981\u4e0d\u8fd8\u5251\u8c31\uff0c\u90a3\u4e5f\u53ef\u4ee5\uff0c\u4f46\u5f97\u7ed9\u6211\u653e\u51fa\u4e00\u4e2a\u4eba\uff01\u6211\u51fa\u53bb\u4e4b\u540e\uff0c\u7edd\u4e0d \u4f1a\u5c06\u5251\u8c31\u4e4b\u4e8b\uff0c\u5411\u4efb\u4f55\u4eba\u63d0\u8d77\u4e00\u53e5\uff01\u201d \u3000\u3000\u4e91\u821e\u9633\u542c\u4e86\uff0c\u5927\u4e3a\u60ca\u8be7\uff0c\u60f3\u4e0d\u5230\u4e0a\u5b98\u5929\u91ce\u7adf\u80af\u7528\u5251\u8c31\u6765\u4ea4\u6362\u4e00\u4e2a\u4eba\uff0c\u800c\u4e14\u8fd8\u8981\u727a\u7272\u4e86\u638c \u95e8\u7684\u5730\u4f4d\uff0c\u4ec0\u4e48\u4eba\u503c\u5f97\u4ed6\u5982\u6b64\u5173\u5fc3\uff0c\u60f3\u4e86\u4e00\u60f3\uff0c\u4e0d\u89c9\u9762\u8272\u53d8\u4e86\uff01 \u3000\u3000\u4e91\u821e\u9633\u773c\u775b\u4e00\u7741\uff0c\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u4e0d\u6012\u800c\u5a01\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u7ed9\u6211\u8bf4\uff0c\u662f\u4ec0\u4e48\u4eba\uff1f\u82e5\u6709 \u534a\u53e5\u65e0\u793c\u4e4b\u8a00\uff0c\u6559\u4f60\u7acb\u6bd9\u638c\u4e0b\uff01\u201d \u3000\u3000\u539f\u6765\u4e91\u821e\u9633\u6000\u6709\u5fc3\u75c5\uff1a\u83ab\u975e\u662f\u725f\u5bb6\u7684\u65cf\u4eba\u53eb\u4ed6\u6765\u63a5\u56de\u5e08\u59d1\uff1f \u3000\u3000\u83ab\u975e\u662f\u4ed6\u770b\u4e0a\u4e86\u6211\u5973\u513f\uff0c\u56e0\u6b64\u63d0\u51fa\u4e86\u8981\u5c06\u5251\u8c31\u4e0e\u5979\u4ea4\u6362\uff1f \u3000\u3000\u90a3\u77e5\u4ed6\u6240\u6599\u7684\u5b8c\u5168\u4e0d\u5bf9\uff0c\u53ea\u89c1\u4e0a\u5b98\u5929\u91ce\u867d\u7136\u4e3a\u4ed6\u7684\u7cbe\u795e\u6240\u5413\uff0c\u6115\u7136\u7684\u9000\u4e86\u4e00\u6b65\uff0c\u4ecd\u662f\u9547 \u5b9a\u7684\u7b54\u9053\uff1a\u201c\u8bf7\u4f60\u628a\u9648\u7384\u673a\u653e\u51fa\u6765\uff01\u201d \u3000\u3000\u4e91\u821e\u9633\u8be7\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u8c01\u662f\u9648\u7384\u673a\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u4f60\u8fd8\u4f5c\u4ec0\u4e48\u5047\u60fa\u60fa\uff0c\u4ed6\u7684\u9a6c\u8fd8\u5728 \u4f60\u7684\u95e8\u5916\u3002\u7eb5\u7136\u4ed6\u4e0e\u4f60\u4f5c\u5bf9\uff0c\u96be\u9053\u4ee5\u4f60\u7684\u8eab\u4efd\u5a01\u540d\uff0c\u4e5f\u597d\u610f\u601d\u5411\u4e00\u4e2a\u53d7\u4e86\u91cd\u4f24\u7684\u4eba\u4e0b\u624b\uff1f\u201d \u3000\u3000\u4e91\u821e\u9633\u7591\u5fc3\u5927\u8d77\uff0c\u731b\u7684\u60f3\u8d77\uff1a\u201c\u8fd9\u4e2a\u9648\u7384\u673a\u83ab\u975e\u5c31\u662f\u7d20\u7d20\u6551\u56de\u6765\uff0c\u73b0\u5728\u8eba\u5728\u6211\u4e66\u623f\u91cc\u7684 \u90a3\u4e2a\u5c11\u5e74\uff0c\u6211\u8fde\u8fd9\u4e2a\u540d\u5b57\u4e5f\u6ca1\u6709\u542c\u8fc7\uff0c\u4ed6\u4e3a\u4e86\u4ec0\u4e48\u4e8b\u60c5\u8981\u4e0e\u6211\u4f5c\u5bf9\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u5982\u4f55\uff1f\u4e00\u90e8\u6b66\u6797\u79d8\u7b08\u6362\u4e00\u4e2a\u75c5\u4eba\uff0c\u5bf9\u4f60\u7edd\u4e0d\u5403\u4e8f\uff01\u201d\u4e91\u821e\u9633\u53cc\u773c\u4e00\u7741\uff0c \u7738\u5b50\u7cbe\u5149\u7535\u5c04\uff0c\u6253\u91cf\u7740\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u8fd9\u9648\u7384\u673a\u662f\u4ec0\u4e48\u4eba\uff1f\u4f60\u4f55\u4ee5\u80af\u820d\u4e86\u5251\u8c31\u3001\u820d\u4e86\u638c\u95e8\uff0c \u6c42\u6211\u653e\u4ed6\u56de\u53bb\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u90a3\u91cc\u77e5\u9053\u4e91\u821e\u9633\u6839\u672c\u8fd8\u6ca1\u6709\u89c1\u8fc7\u9648\u7384\u673a\uff0c\u542c\u4e86\u6b64\u8a00\uff0c\u53c8\u662f\u4e00\u6115\uff1a\u600e\u4e48\u4ed6\u8fd8\u672a\u77e5 \u9053\u9648\u7384\u673a\u7684\u8eab\u4efd\uff1f\u5728\u4e91\u821e\u9633\u7684\u6ce8\u5c04\u4e4b\u4e0b\uff0c\u90ce\u58f0\u8bf4\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u662f\u6211\u6253\u4f24\u7684\uff0c\u82e5\u7136\u4ed6\u6709\u751a\u4ec0\u4e48 \u4e0d\u6d4b\uff0c\u6216\u8005\u662f\u56e0\u53d7\u4e86\u65e0\u6cd5\u654c\u4f60\uff0c\u7ed9\u4f60\u6cbb\u6b7b\uff0c\u6559\u6211\u6709\u4f55\u9762\u76ee\u4ee5\u5bf9\u6b66\u6797\u4e2d\u4eba\uff1f\u201d \u3000\u3000\u9648\u7384\u673a\u5728\u4e66\u623f\u4e4b\u4e2d\u542c\u4e86\uff0c\u5927\u4e3a\u611f\u52a8\u3002\u4e91\u821e\u9633\u542c\u4e86\uff0c\u5374\u662f\u8d8a\u53d1\u7cca\u6d82\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e91\u67d0\u4e00 \u751f\uff0c\u8fd8\u662f\u7b2c\u4e00\u6b21\u542c\u5230\u8fd9\u6837\u5947\u602a\u7684\u4e8b\u60c5\uff0c\u4f60\u4e5f\u53ef\u7b97\u5f97\u662f\u4e2a\u82f1\u96c4\u4e86\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u4e0d\u6562\u3002\u6211\u4e0d\u4f46\u662f\u820d\u4e86\u638c\u95e8\uff0c\u800c\u4e14\u662f\u820d\u4e86\u6027\u547d\u6765\u7684\u3002\u201d\u4e91\u821e\u9633\u9053\uff1a\u201c\u597d\uff0c \u90a3\u5c31\u5c06\u4f60\u7684\u6027\u547d\u4ea4\u51fa\u6765\uff01\u201d \u3000\u3000\u84e6\u7136\u53cc\u6307\u4e00\u5f39\uff0c\u6316\u5230\u4e86\u4e0a\u5b98\u5929\u91ce\u7684\u9762\u95e8\uff0c\u4e0a\u5b98\u5929\u91ce\u505a\u68a6\u4e5f\u6599\u4e0d\u5230\u4ed6\u5728\u8bf4\u8bdd\u4e4b\u95f4\u7a81\u7136\u53d1 \u52a8\uff0c\u5fc3\u4e2d\u4e00\u51db\uff0c\u4f46\u89c1\u4e91\u821e\u9633\u51fa\u6307\u5982\u7535\uff0c\u6307\u5c16\u5df2\u89e6\u5230\u4e86\u4ed6\u7684\u773c\u5e18\uff0c\u53ea\u8981\u8f7b\u8f7b\u4e00\u6316\uff0c\u4e0a\u5b98\u5929\u91ce\u7684 \u4e24\u9897\u773c\u73e0\u5c31\u8981\u8131\u7736\u800c\u51fa\uff01 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u65e0\u6687\u601d\u91cf\uff0c\u62fc\u7740\u778e\u4e86\u773c\u775b\uff0c\u2018\u7830\u2019\u7684\u4e00\u638c\u6253\u51fa\uff0c\u4e24\u4eba\u5bf9\u9762\u800c\u7acb\uff0c\u76f8\u8ddd\u4e0d\u5230\u4e09\u5c3a \u4e4b\u5730\uff0c\u6309\u8bf4\u4e0a\u5b98\u5929\u91ce\u7684\u773c\u73e0\u975e\u7ed9\u6316\u6389\uff0c\u800c\u4e91\u821e\u9633\u4e5f\u975e\u7ed9\u6253\u4e2d\u4e0d\u53ef\uff0c\u90a3\u77e5\u4e00\u638c\u6253\u51fa\uff0c\u500f\u7136\u95f4\u5374 \u4e0d\u89c1\u4e86\u4e91\u821e\u9633\u7684\u8eab\u5f71\uff0c\u4f46\u542c\u7684\u2018\u7830\u2019\u7684\u4e00\u58f0\uff0c\u8fd9\u4e00\u638c\u5374\u6253\u5728\u8001\u6885\u6811\u4e0a\uff0c\u6ee1\u6811\u6885\u82b1\uff0c\u7eb7\u843d\u5982 \u96e8\uff0c\u4e24\u679d\u6885\u679d\u4e5f\u6298\u4e86\uff0c\u800c\u4e0a\u5b98\u5929\u91ce\u7684\u4e24\u9897\u773c\u73e0\uff0c\u4e5f\u4ecd\u662f\u6beb\u65e0\u4f24\u635f\u3002\u4e0a\u5b98\u5929\u91ce\u6014\u4e86\u4e00\u6014\uff0c\u6025\u5fd9 \u64a4\u638c\u56de\u8eab\uff0c\u53ea\u542c\u5f97\u4e91\u821e\u9633\u5728\u4ed6\u8033\u8fb9\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u679c\u7136\u662f\u6b66\u5f53\u6d3e\u7684\u5ae1\u4f20\u624b\u6cd5\uff0c\u518d\u8bd5\u6211\u8fd9\u4e00 \u62db\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u60ca\u9b42\u672a\u5b9a\uff0c\u4f46\u89c9\u4e91\u821e\u9633\u51b0\u51b7\u7684\u624b\u6307\u53c8\u5df2\u89e6\u5230\u4ed6\u7684\u9762\u988a\uff0c\u6025\u5fd9\u4e00\u4e2a\u76d8\u9f99\u7ed5\u6b65\uff0c\u53cc \u638c\u9f50\u63a8\uff0c\u8fd9\u4e00\u62db\u540d\u53eb\u201c\u76d8\u9f99\u53cc\u53cc\u649e\u638c\u201d\uff0c\u6b63\u662f\u6b66\u5f53\u638c\u6cd5\u7684\u7cbe\u534e\u6240\u5728\uff0c\u4e0a\u5b98\u5929\u91ce\u62fc\u6b7b\u53d1\u638c\uff0c\u638c \u529b\u4f55\u6b62\u5343\u65a4\uff0c\u7a81\u7136\u95f4\uff0c\u4f46\u89c9\u638c\u5fc3\u6240\u89e6\u4e4b\u5904\uff0c\u8f6f\u7ef5\u7ef5\u8f7b\u5982\u65e0\u7269\uff0c\u8fd9\u5343\u65a4\u638c\u529b\uff0c\u7adf\u7136\u7ed9\u4e91\u821e\u9633\u8f7b \u63cf\u6de1\u5199\u7684\u4e00\u4e3e\u5316\u5f00\uff0c\u4e0a\u5b98\u5929\u91ce\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u521a\u60f3\u9000\u6b65\u62bd\u8eab\uff0c\u808b\u4e0b\u7684\u7ae0\u95e8\u7a74\u5df2\u7ed9\u4e91\u821e\u9633\u4e00 \u6307\u5c01\u95ed\uff0c\u201c\u5495\u549a\u201d\u4e00\u58f0\uff0c\u5012\u5728\u5730\u4e0a\u3002 \u3000\u3000\u8fd9\u51e0\u4e0b\u8fc5\u5982\u7535\u5149\u77f3\u706b\uff0c\u4f46\u5728\u9648\u7384\u673a\u773c\u4e2d\uff0c\u5374\u5df2\u77a7\u7684\u660e\u660e\u767d\u767d\uff1b\u4e91\u821e\u9633\u4e0d\u4f46\u8f7b\u529f\u7edd\u9876\uff0c\u5251 \u6cd5\u60ca\u4eba\uff0c\u800c\u4e14\u8fd8\u7ec3\u6210\u4e86\u6b66\u6797\u7f55\u89c1\u7684\u4e00\u6307\u7985\u529f\uff0c\u9648\u7384\u673a\u5438\u4e86\u4e00\u53e3\u51c9\u6c14\uff0c\u5fc3\u4e2d\u8bf4\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4eca \u665a\u5c31\u662f\u6211\u6bd9\u547d\u4e4b\u671f\uff01\u201d\u62fe\u8d77\u957f\u5251\uff0c\u4fbf\u5f85\u5f00\u95e8\u51fa\u53bb\u4e0e\u4e91\u821e\u9633\u62fc\u547d\u3002\u4ed6\u867d\u7136\u77e5\u9053\u81ea\u5df1\u7684\u6b66\u529f\u4e0e\u4e91 \u821e\u9633\u5dee\u5f97\u592a\u8fdc\uff0c\u4f46\u4e0a\u5b98\u5929\u91ce\u65e2\u662f\u4e3a\u4ed6\u800c\u6765\uff0c\u4ed6\u53c8\u7109\u80fd\u820d\u4e86\u4e0a\u5b98\u5929\u91ce\u72ec\u81ea\u9003\u8d70\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u5ffd\u542c\u5f97\u4e91\u7d20\u7d20\u7684\u811a\u6b65\u58f0\u53c8\u8d70\u4e86\u51fa\u6765\uff0c\u8fdc\u8fdc\u8bf4\u9053\uff1a\u201c\u7239\uff0c\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u4e91\u821e\u9633\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u4e00\u4e2a\u5c0f\u5077\u4e71\u95ef\u4e86\u8fdb\u6765\uff0c\u7ed9\u6211\u62ff\u4f4f\u4e86\u3002\u201d \u3000\u3000\u4e91\u7d20\u7d20\u683c\u683c\u7b11\u9053\uff1a\u201c\u7adf\u6709\u8fd9\u6837\u7684\u7b28\u5c0f\u5077\u4f1a\u95ef\u8fdb\u5230\u54b1\u4eec\u5bb6\u6765\uff0c\u90a3\u4ed6\u771f\u6d3b\u8be5\u4e86\uff01\u201d\u773c\u5149\u4e00 \u77a5\uff0c\u89c1\u4e0a\u5b98\u5929\u91ce\u6c14\u5b87\u975e\u51e1\uff0c\u867d\u7136\u7ed9\u95ed\u4e86\u7a74\u9053\uff0c\u4e0d\u80fd\u8bf4\u8bdd\uff0c\u773c\u775b\u4e2d\u5374\u9732\u51fa\u6124\u6012\u4e4b\u8272\uff0c\u6beb\u65e0\u745f\u7f29 \u4e0d\u5b89\u4e4b\u6001\uff0c\u4e0d\u50cf\u5c0f\u5077\uff0c\u5fc3\u4e2d\u5927\u5947\uff0c\u6b63\u5f85\u53d1\u95ee\uff0c\u773c\u5149\u4e00\u89e6\uff0c\u5ffd\u89c9\u7236\u4eb2\u7684\u8138\u8272\u4e5f\u662f\u6781\u4e3a\u8be7\u5f02\uff0c\u84e6 \u7136\u98a4\u58f0\u95ee\u9053\uff1a\u201c\u7d20\u7d20\uff0c\u4f60\u624b\u4e0a\u62ff\u7684\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e91\u7d20\u7d20\u624b\u4e0a\u62ff\u7684\u662f\u4e24\u4ef6\u8863\u670d\uff0c\u4e00\u4ef6\u5916\u8863\uff0c\u4e00\u4ef6\u5185\u8863\uff0c\u90fd\u662f\u4ed6\u5728\u9648\u7384\u673a\u660f\u8ff7\u4e4b\u65f6\uff0c\u66ff\u4ed6\u6362 \u4e0b\u6765\u7684\u3002\u6d17\u6389\u8840\u6c61\uff0c\u667e\u5e72\u4e4b\u540e\uff0c\u73b0\u5728\u6b63\u51c6\u5907\u5077\u5077\u9001\u56de\u4ed6\u7684\u623f\u95f4\uff0c\u7ed9\u7236\u4eb2\u4e00\u95ee\uff0c\u4e0d\u89c9\u7ea2\u4e86\u53cc \u988a\uff0c\u4f4e\u5782\u7c89\u9888\uff0c\u8f7b\u58f0\u8bf4\u9053\uff1a\u201c\u662f\u90a3\u4e2a\u4eba\u7684\u3002\u201d \u3000\u3000\u4e91\u821e\u9633\u9053\uff1a\u201c\u5c31\u662f\u90a3\u4e2a\u9648\u7384\u673a\u7684\u5417\uff1f\u201d\u4e91\u7d20\u7d20\u9053\uff1a\u201c\u7239\uff0c\u4f60\u600e\u4e48\u77e5\u9053\u4ed6\u7684\u540d\u5b57\uff1f\u4f60\u548c\u4ed6 \u8c08\u8fc7\u4e86\u5417\uff1f\u201d\u4e91\u821e\u9633\u6c89\u7740\u8138\u8bf4\u9053\uff1a\u201c\u4f60\u628a\u90a3\u5c0f\u5b50\u53eb\u9192\uff0c\u5524\u4ed6\u51fa\u6765\uff01\u201d \u3000\u3000\u4e91\u7d20\u7d20\u4e00\u6ce1\u773c\u6cea\uff0c\u5658\u7740\u5c0f\u5634\u513f\u8bf4\u9053\uff1a\u201c\u5b69\u513f\u6536\u7559\u7684\u96be\u9053\u662f\u4ec0\u4e48\u574f\u4eba\u5417\uff1f\u7239\u4e3a\u4ec0\u4e48\u8fd9\u6837\u751f \u6c14\uff1f\u6709\u8bdd\u660e\u5929\u518d\u95ee\u4ed6\u4e0d\u884c\u5417\uff1f\u201d\u8bdd\u521a\u8bf4\u5b8c\uff0c\u53ea\u542c\u5f97\u623f\u95e8\u4e00\u54cd\uff0c\u9648\u7384\u673a\u8d70\u4e86\u51fa\u6765\uff0c\u6717\u58f0\u8bf4\u9053\uff1a \u201c\u4e0d\u52b3\u76f8\u5524\uff0c\u9648\u7384\u673a\u6765\u4e86\uff01\u201d \u3000\u3000\u8fd9\u665a\u6b63\u662f\u6b63\u6708\u5341\u4e03\uff0c\u6708\u660e\u5982\u955c\uff0c\u4e91\u821e\u9633\u6253\u91cf\u4e86\u9648\u7384\u673a\u4e00\u773c\uff0c\u5fc3\u5934\u4e00\u9707\uff0c\uff1a\u201c\u8fd9\u4eba\u597d\u50cf\u662f \u5728\u90a3\u91cc\u89c1\u8fc7\u4f3c\u7684\u3002\u201d\u4f46\u81ea\u5df1\u591a\u5e74\u4e0d\u4e0e\u5916\u4eba\u6765\u5f80\uff0c\u66f4\u4f55\u51b5\u8fd9\u4e73\u81ed\u672a\u5e72\u7684\u5c11\u5e74\uff0c\u4e91\u7d20\u7d20\u6025\u9053\uff1a \u201c\u7239\uff0c\u4f60\u597d\u597d\u95ee\u4eba\uff0c\u4e0d\u8981\u5413\u552c\u4ed6\uff0c\u4ed6\u521a\u521a\u4f24\u6108\u3002\u201d\u4e91\u821e\u9633\u9053\uff1a\u201c\u7d20\u513f\uff0c\u4f60\u8d70\u8fc7\u4e00\u8fb9\uff0c\u4e0d\u8981\u591a \u5634\uff01\u201d\u4e91\u7d20\u7d20\u4ece\u6765\u672a\u66fe\u89c1\u8fc7\u7236\u4eb2\u7528\u8fd9\u6837\u96be\u770b\u7684\u8138\u8272\u5bf9\u5979\uff0c\u6ee1\u8154\u59d4\u5c48\uff0c\u9760\u5728\u4e00\u514b\u8001\u6885\u6811\u4e0a\uff0c\u51e0 \u4e4e\u8981\u54ed\u51fa\u6765\uff0c\u5ffd\u542c\u5f97\u4e91\u821e\u9633\u6c89\u58f0\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u597d\u751f\u5927\u80c6\uff0c\u662f\u8c01\u6d3e\u4f60\u6765\u7684\uff1f\u201d \u3000\u3000\u9648\u7384\u673a\u9053\uff1a\u201c\u662f\u4f60\u7684\u4e00\u73ed\u8001\u670b\u53cb\uff0c\u6211\u7684\u53d4\u4f2f\u8f88\u53eb\u6211\u6765\u7684\uff01\u201d \u3000\u3000\u4e91\u821e\u9633\u773c\u5149\u4e00\u626b\uff0c\u76ef\u7740\u9648\u7384\u673a\u95ee\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4ee4\u5c0a\u5927\u4eba\u4e43\u662f\u6211\u6614\u65e5\u7684\u540c\u50da\u4e86\u3002\u5484\uff0c \u4f60\u7236\u4eb2\u53eb\u4ec0\u4e48\u540d\u5b57\uff0c\u4ed6\u5728\u5f20\u4e16\u8bda\u90e8\u4e0b\u662f\u4ec0\u4e48\u5b98\u804c\uff1f\u201d\u4e91\u7d20\u7d20\u5927\u611f\u60ca\u5947\uff1a\u600e\u4e48\u7236\u4eb2\u4e00\u773c\u4fbf\u77a7\u51fa \u9648\u7384\u673a\u7684\u6765\u5386\uff1f\u5979\u4e0d\u77e5\u9053\u9648\u7384\u673a\u90a3\u4ef6\u5185\u8863\u4e0a\u7ee3\u6709\u4e00\u4e2a\u96c4\u9e70\u6807\u5fd7\uff0c\u5f53\u5e74\u5f20\u4e16\u8bda\u7684\u8fd1\u8eab\u4f8d\u536b\uff0c\u8863 \u670d\u4e0a\u90fd\u662f\u7ee3\u6709\u8fd9\u4e2a\u6807\u8bb0\u7684\u3002 \u3000\u3000\u9648\u7384\u673a\u6014\u4e86\u4e00\u6014\uff0c\u624b\u6276\u5251\u67c4\uff0c\u9000\u4e86\u4e00\u6b65\uff0c\u4ed6\u7ed9\u4e91\u821e\u9633\u770b\u7834\u4e86\u6765\u5386\uff0c\u65e9\u5c31\u51c6\u5907\u4e91\u821e\u9633\u4f1a\u7a81 \u7136\u52a8\u624b\u3002\u5374\u4e0d\u6599\u4ed6\u7528\u8fd9\u6837\u7684\u53e3\u543b\u4e0e\u81ea\u5df1\u8bf4\u8bdd\uff0c\u4f3c\u4e4e\u5e76\u672a\u5b58\u6709\u4e1d\u6beb\u654c\u610f\u3002\u53ef\u662f\u8fd9\u4e00\u95ee\u5374\u628a\u4ed6\u95ee \u4f4f\u4e86\uff0c\u4ed6\u7684\u6bcd\u4eb2\u4ece\u4e0d\u66fe\u4e0e\u4ed6\u8c08\u8d77\u7236\u4eb2\u7684\u4e8b\u60c5\uff0c\u4ed6\u53ea\u77e5\u9053\u4ed6\u7236\u4eb2\u66fe\u66ff\u5f20\u4e16\u8bda\u6253\u8fc7\u6c5f\u5c71\uff0c\u5728\u6700\u540e \u7684\u4e00\u6b21\u957f\u6c5f\u6218\u5f79\u4e2d\u6218\u6b7b\u7684\uff0c\u81f3\u4e8e\u66fe\u4efb\u4f55\u5b98\u804c\uff0c\u5e73\u751f\u8f76\u4e8b\uff0c\u4ed6\u4e00\u6982\u4e0d\u77e5\uff0c\u4ed6\u6015\u60f9\u8d77\u6bcd\u4eb2\u7684\u60b2 \u4f24\uff0c\u4e5f\u4ece\u6765\u4e0d\u6562\u591a\u95ee\u3002 \u3000\u3000\u4e91\u821e\u9633\u7591\u5fc3\u5927\u8d77\uff0c\u8feb\u524d\u4e00\u6b65\uff0c\u6c89\u58f0\u559d\u9053\uff1a\u201c\u5c0f\u4f19\u5b50\uff0c\u4f60\u5feb\u8bf4\u5b9e\u8bdd\uff0c\u6211\u770b\u5728\u6614\u65e5\u540c\u50da\u7684\u4efd \u4e0a\uff0c\u4e5f\u8bb8\u80fd\u9976\u4f60\u4e0d\u6b7b\uff01\u201d\u9648\u7384\u673a\u6012\u6c14\u9661\u751f\uff0c\u4e00\u58f0\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u6709\u4ec0\u4e48\u540c\u50da\u4e4b\u60c5\uff1f\u4e09\u4e2a\u6708\u4e4b \u540e\uff0c\u4f60\u7b49\u7740\u4e0a\u4eac\u9886\u8d4f\u53bb\u5427\uff01\u201d \u3000\u3000\u4e91\u821e\u9633\u9762\u8272\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u6211\u548c\u7f57\u5927\u4eba\u7684\u8c08\u8bdd\uff0c\u4f60\u80c6\u6562\u5077\u542c\uff1f\u201d \u3000\u3000\u9648\u7384\u673a\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e00\u4e2a\u5b57\u4e5f\u4e0d\u6f0f\uff0c\u90fd\u542c\u89c1\u4e86\uff1f\u201d\u4e91\u821e\u9633\u559d\u9053\uff1a\u201c\u4f60\u5230\u6b64\u610f\u6b32\u4f55\u4e3a\uff1f\u201d \u9648\u7384\u673a\u9053\uff1a\u201c\u6211\u53d7\u4e86\u5e08\u53cb\u7684\u91cd\u6258\u8981\u6740\u4f60\u8fd9\u4e70\u53cb\u6c42\u8363\u7684\u4e0d\u4e49\u4e4b\u4eba\uff01\u201d \u3000\u3000\u4e91\u7d20\u7d20\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u4f60\u8981\u523a\u6740\u6211\u7239\u7239\uff01\u201d \u3000\u3000\u4f46\u542c\u7684\u4e91\u821e\u9633\u4ef0\u5929\u5927\u7b11\uff1a\u201c\u4f60\u8981\u523a\u6740\u6211\u7239\u3002\u201d\u9648\u7384\u673a\u9053\uff1a\u201c\u4f60\u72c2\u4ec0\u4e48\uff0c\u6211\u7eb5\u7136\u4e0d\u662f\u4f60\u7684 \u5bf9\u624b\uff0c\u4e5f\u8981\u4ee4\u4f60\u77e5\u9053\uff0c\u5929\u4e0b\u6709\u7684\u662f\u4e0d\u6015\u6b7b\u7684\u4eba\uff0c\u4f60\u82e5\u4e70\u53cb\u6c42\u8363\uff0c\u5b9a\u4e3a\u6b66\u6797\u5171\u5f03\uff0c\u53ea\u6015\u5728\u6211\u4e4b \u540e\uff0c\u8fd8\u6709\u4e0d\u5c11\u4eba\u8981\u6765\u884c\u523a\uff0c\u4f60\u90fd\u6740\u5f97\u5c3d\u4e48\uff1f\u201d \u3000\u3000\u4e91\u821e\u9633\u6253\u4e86\u4e00\u4e2a\u5bd2\u98a4\uff0c\u5374\u4ecd\u662f\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e00\u665a\u4e4b\u95f4\uff0c\u7adf\u6709\u4e24\u4e2a\u4e0d\u6015\u6b7b\u7684\u50bb\u5c0f\u5b50\u5bfb\u4e0a \u95e8\uff0c\u82f1\u96c4\u51fa\u4e8e\u5e74\u5c11\uff0c\u679c\u7136\u4e0d\u5047\u3002\u54c8\uff0c\u4f60\u65e2\u8981\u884c\u523a\uff0c\u4e3a\u4f55\u4e0d\u62d4\u5251\uff1f\u201d\u9648\u7384\u673a\u9053\uff1a\u201c\u4eca\u665a\u4e4b\u4e8b\uff0c \u6211\u4e0e\u4f60\u81ea\u884c\u4e86\u65ad\u3002\u8fd9\u4f4d\u4e0a\u5b98\u4e49\u58eb\uff0c\u8981\u5c06\u6211\u6765\u4ea4\u6362\u5251\u8c31\uff0c\u73b0\u5728\u5df2\u7528\u4e0d\u7740\u5566\uff0c\u4f60\u89e3\u5f00\u4ed6\u7684\u7a74\u9053\uff0c \u5c06\u5251\u8c31\u8fd8\u4ed6\uff0c\u6211\u7518\u613f\u820d\u4e86\u6027\u547d\uff0c\u4e0e\u4f60\u4e00\u6218\uff01\u201d \u3000\u3000\u4e91\u821e\u9633\u53c8\u76ef\u4e86\u9648\u7384\u673a\u4e00\u773c\uff0c\u5ffd\u7684\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u7740\u4f24\u662f\u7ed9\u6b66\u5f53\u5185\u5bb6\u638c\u529b\u6240\u9707\u4f24\u7684\uff0c\u8fd9 \u4e2a\u50bb\u5c0f\u5b50\u6ca1\u6709\u9a97\u6211\u3002\u8fd9\u5230\u5947\u4e86\uff0c\u4ed6\u548c\u4f60\u82e5\u65e0\u6df1\u4ec7\u5927\u6068\uff0c\u4e5f\u4e0d\u81f3\u4e8e\u4e0b\u8fd9\u91cd\u624b\uff0c\u600e\u7684\u4f60\u4eec\u5374\u5f7c\u6b64 \u4e3a\u5bf9\u65b9\u6c42\u60c5\uff1f\u201d \u3000\u3000\u9648\u7384\u673a\u9053\uff1a\u201c\u522b\u7684\u4e8b\uff0c\u4e0d\u7528\u4f60\u7ba1\uff0c\u6211\u53ea\u95ee\u4f60\uff0c\u4f60\u653e\u4e0d\u653e\u4ed6\uff1f\u201d \u3000\u3000\u4e91\u821e\u9633\u51b7\u7b11\u9053\uff1a\u201c\u522b\u4eba\u7684\u4e8b\uff0c\u4e5f\u4e0d\u7528\u4f60\u7ba1\uff01\u201d\u53cc\u76ee\u4e00\u5f20\uff0c\u6740\u6c14\u9661\u9732\uff0c\u4e91\u7d20\u7d20\u4e00\u8dc3\u800c\u8d77\uff0c \u5c16\u58f0\u53eb\u9053\uff1a\u201c\u7239\uff01\u201d\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u9648\u7384\u673a\u4f46\u89c9\u638c\u98ce\u98d2\u7136\uff0c\u5df2\u5230\u80cc\u540e\uff0c\u6025\u5fd9\u7ffb\u8eab\u62d4\u5251\uff0c\u5ffd \u89c9\u624b\u6240\u89e6\u5904\uff0c\u7a7a\u65e0\u4e00\u7269\uff0c\u53ea\u89c1\u4e91\u821e\u9633\u624b\u4e2d\u591a\u4e86\u4e00\u628a\u957f\u5251\uff0c\u5012\u6301\u5251\u67c4\uff0c\u731b\u7684\u585e\u5230\u81ea\u5df1\u7684\u624b\u4e2d\uff01 \u3000\u3000\u8fd9\u4e00\u4e0b\u624b\u6cd5\u5feb\u5230\u6781\u70b9\uff0c\u9648\u7384\u673a\u5fc3\u5ff5\u65b9\u52a8\uff0c\u90a3\u628a\u5251\u5df2\u9012\u5230\u81ea\u5df1\u7684\u624b\u4e2d\uff0c\u53ea\u542c\u5f97\u4e91\u821e\u9633\u4f4e\u58f0 \u559d\u9053\uff1a\u201c\u5251\u5df2\u9001\u5230\uff0c\u8fd8\u4e0d\u52a8\u624b\u4e48\uff1f\u7d20\u7d20\uff0c\u9000\u5f00\uff01\u201d\u8863\u8896\u4e00\u62c2\uff0c\u5c06\u5973\u513f\u62c2\u51fa\u4e00\u4e08\u5f00\u5916\uff0c\u4e91\u7d20\u7d20 \u4ece\u6765\u672a\u89c1\u8fc7\u7236\u4eb2\u5982\u6b64\u751f\u6c14\uff0c\u5413\u5f97\u5446\u4e86\uff01 \u3000\u3000\u9648\u7384\u673a\u5230\u5e95\u662f\u540d\u5bb6\u5b50\u5f1f\uff0c\u8eab\u624b\u4e0d\u51e1\uff0c\u4e91\u821e\u9633\u867d\u662f\u5148\u58f0\u593a\u4eba\uff0c\u5374\u4e5f\u5e76\u672a\u4ee4\u4ed6\u754f\u7f29\uff0c\u4ed6\u5fc3\u795e \u4e00\u5b9a\uff0c\u5251\u8bc0\u4e00\u9886\uff0c\u7acb\u523b\u4e00\u62db\u201c\u4e58\u9f99\u5f15\u51e4\u201d\uff0c\u523a\u54bd\u5589\uff0c\u6302\u53cc\u80a9\uff0c\u5530\u7684\u626b\u5c06\u8fc7\u53bb\u3002\u4e0d\u6599\u4e91\u821e\u9633\u53cc \u8896\u4e00\u62c2\uff0c\u8eab\u968f\u638c\u8d70\uff0c\u8fc5\u82e5\u72c2\u98ce\uff0c\u9648\u7384\u673a\u4e00\u5251\u523a\u51fa\uff0c\u624e\u7a7a\uff0c\u6697\u547c\u4e0d\u5999\uff0c\u987f\u89c9\u8111\u540e\u751f\u98ce\uff0c\u4e91\u821e\u9633 \u5728\u8033\u8fb9\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u5251\u6cd5\u662f\u8c01\u6559\u7684\uff1f\u201d\u9648\u7384\u673a\u54ac\u5b9e\u7259\u6839\uff0c\u90a3\u80af\u4e0e\u4ed6\u6253\u8bed\uff0c\u5de6\u624b\u4e00\u9886\u5251\u950b\uff0c \u201c\u9f99\u5f62\u98de\u6b65\u201d\u4ece\u654c\u4eba\u638c\u98ce\u4e4b\u4e0b\u63a0\u51fa\uff0c\u731b\u7684\u53cd\u624b\u4e00\u5251\uff0c\u201c\u91d1\u9e4f\u5c55\u7fc5\u201d\u3001\u201c\u731b\u9e21\u593a\u6817\u201d\u3001\u201c\u767d\u733f \u6302\u679d\u201d\u3001\u201c\u91ce\u9a6c\u8df3\u6da7\u201d\u4e00\u62db\u63a5\u7740\u4e00\u62db\uff0c\u72b9\u5982\u957f\u6c5f\u5927\u6cb3\uff0c\u6eda\u6eda\u800c\u4e0a\uff0c\u5251\u5251\u6307\u5411\u4e91\u821e\u9633\u7684\u8981\u5bb3\uff0c \u9648\u7384\u673a\u7684\u5251\u6cd5\u5b66\u5f97\u751a\u6742\uff0c\u5341\u4e09\u5c81\u4e4b\u524d\uff0c\u662f\u4ed6\u6bcd\u4eb2\u6559\u7684\uff0c\u5341\u4e09\u5c81\u4e4b\u540e\uff0c\u662f\u4ed6\u53d4\u4f2f\u8f88\u6559\u7684\uff0c\u90a3\u4e9b \u4eba\u90fd\u662f\u4ed6\u7236\u4eb2\u6614\u65e5\u7684\u540c\u50da\uff0c\u5f20\u4e16\u8bda\u624b\u4e0b\u7684\u6b66\u58eb\uff0c\u6bcf\u4eba\u90fd\u4e0d\u540c\u51e1\u54cd\u3002 \u3000\u3000\u4e91\u821e\u9633\u53cc\u8896\u6325\u821e\uff0c\u628a\u9648\u7384\u673a\u7684\u5251\u62db\u4e00\u4e00\u5316\u5f00\uff0c\u6ee1\u8179\u72d0\u7591\uff0c\u5947\u95ee\u9053\uff1a\u201c\u4f60\u7684\u6b66\u529f\u6bd4\u4e0a\u5b98\u5929 \u91ce\u9ad8\u5f97\u591a\uff0c\u4f55\u4ee5\u53cd\u88ab\u4ed6\u6240\u4f24\uff1f\u201d\u9648\u7384\u673a\u4e0d\u7406\u4e0d\u776c\uff0c\u4e00\u67c4\u957f\u5251\u970d\u970d\u5c55\u5f00\uff0c\u5bd2\u5149\u95ea\u95ea\uff0c\u76f4\u5982\u9a87\u7535 \u60ca\u6d9b\uff0c\u534a\u70b9\u4e5f\u4e0d\u653e\u677e\u3002\u4f46\u542c\u5f97\u4e91\u821e\u9633\u8ddf\u7740\u4ed6\u7684\u5251\u62db\u53eb\u9053\uff1a\u201c\u4e94\u79bd\u5251\u6cd5\uff0c\u9752\u9633\u5251\u6cd5\uff0c\u5514\uff0c\u8fd9\u4e00 \u62db\u53c8\u662f\u5d06\u5cd2\u5251\u6cd5\u4e86\uff0c\u53ef\u60dc\u8fd8\u672a\u5230\u5bb6\uff01\u8fd9\u4e00\u62db\u5929\u9f99\u5251\u6cd5\u7684\u795e\u5316\u9f99\u6389\u5c3e\uff0c\u5251\u950b\u53cd\u524a\u4e4b\u65f6\uff0c\u8fd8\u5e94\u7a0d \u6162\u4e00\u4e9b\uff0c\u540e\u52b2\u624d\u80fd\u957f\u4e45\uff01\u201d \u3000\u3000\u9648\u7384\u673a\u6bcf\u53d1\u4e00\u62db\uff0c\u4ed6\u90fd\u80fd\u8bf4\u51fa\u6d3e\u522b\u62db\u540d\uff0c\u9648\u7384\u673a\u4e00\u80a1\u9510\u6c14\uff0c\u4e5f\u4e0d\u7981\u4e3a\u4ed6\u6240\u6298\uff0c\u6597\u4e86\u4e09\u4e94 \u5341\u62db\uff0c\u4e91\u821e\u9633\u5ffd\u7684\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u539f\u6765\u662f\u6211\u7684\u4e00\u73ed\u8001\u670b\u53cb\u5408\u8d77\u6765\u6559\u4f60\uff0c\u602a\u4e0d\u5f97 \u4ed6\u4eec\u6d3e\u8c34\u4f60\u6765\u3002\u53ea\u662f\u5f6d\u548c\u5c1a\u5df2\u6b7b\uff0c\u77f3\u5929\u94ce\u9003\u7684\u65e0\u5f71\u65e0\u8e2a\uff0c\u5c31\u662f\u4ed6\u4eec\u8054\u624b\u6597\u6211\uff0c\u6211\u4ea6\u4f55\u60e7\uff01\u4f60 \u7684\u5251\u6cd5\uff0c\u5728\u5e74\u8f7b\u4e00\u8f88\u4e2d\u8fd8\u7b97\u5f97\u662f\u51fa\u7c7b\u62d4\u8403\u7684\u4e86\uff0c\u53ef\u60dc\u6bd4\u8d77\u6211\u6765\uff0c\u90a3\u8fd8\u5dee\u7684\u8fdc\u5462\uff1f\u201d \u3000\u3000\u4e91\u7d20\u7d20\u89c1\u5979\u7236\u4eb2\u4e00\u9762\u8bf4\u8bdd\uff0c\u795e\u6c14\u8d8a\u6765\u8d8a\u4e0d\u5bf9\u4e86\uff0c\u6025\u5fd9\u53eb\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u4e00\u5411\u7231\u60dc\u4eba\u624d\uff0c \u5c31\u770b\u5728\u4ed6\u8fd9\u4e00\u624b\u5251\u6cd5\u4e0a\uff0c\u9976\u4e86\u4ed6\u5427\uff01\u201d \u3000\u3000\u4e91\u821e\u9633\u53c8\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8fd9\u73ed\u4eba\u5904\u5fc3\u79ef\u8651\u7684\u8c0b\u6740\u6211\uff0c\u6211\u6211\u4eca\u65e5\u82e5\u9976\u4e86\u4ed6\uff0c \u518d\u8fc7\u5341\u5e74\uff0c\u5f85\u4ed6\u7fbd\u7ffc\u5df2\u957f\uff0c\u672a\u5fc5\u80af\u9976\u4e86\u6211\uff01\u201d \u3000\u3000\u84e6\u5730\u8eab\u5f62\u4e00\u6643\uff0c\u547c\u7684\u4e00\u638c\u62cd\u5230\u9648\u7384\u673a\u9762\u95e8\uff0c\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4e91\u7d20\u7d20\u5df2\u662f\u548c\u8eab\u6251\u4e0a\uff0c\u5c16\u58f0 \u53eb\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u6b66\u529f\u65e0\u654c\u5929\u4e0b\uff0c\u539f\u6765\u5374\u6015\u4ed6\u5341\u5e74\u4e4b\u540e\u8d62\u4f60\uff01\u201d \u3000\u3000\u9648\u7384\u673a\u4f46\u611f\u4e91\u821e\u9633\u638c\u5fc3\u6cbe\u5230\u81ea\u5df1\u7684\u592a\u9633\u7a74\uff0c\u5374\u5ffd\u7684\u638c\u529b\u4e00\u677e\uff0c\u53ea\u542c\u5f97\u4e91\u821e\u9633\u5927\u58f0\u559d\u9053\uff1a \u201c\u9976\u4f60\u8fd9\u6b21\uff0c\u4f60\u5341\u5e74\u4e4b\u540e\u518d\u6765\u4e0e\u6211\u4e00\u51b3\u96cc\u96c4\u5427\u3002\u82e5\u7136\u4e0d\u8bc6\u65f6\u52a1\uff0c\u529f\u592b\u8fd8\u672a\u7ec3\u6210\uff0c\u5c31\u6562\u518d\u6765\u884c \u523a\uff0c\u90a3\u5c31\u662f\u81ea\u5bfb\u6b7b\u8def\u4e86\uff01\u201d \u3000\u3000\u731b\u7136\u95f4\u53ea\u542c\u5f97\u4e91\u821e\u9633\u53f1\u54a4\u4e00\u58f0\uff0c\u5927\u624b\u4e00\u4f38\uff0c\u628a\u9648\u7384\u673a\u6293\u4e86\u8d77\u6765\uff0c\u65cb\u98ce\u6025\u821e\uff0c\u559d\u9053\uff1a\u201c\u53bb \u5427\uff01\u201d\u671b\u5916\u4e00\u7529\uff0c\u9648\u7384\u673a\u7ed9\u4ed6\u4e00\u629b\uff0c\u5c24\u5982\u817e\u4e91\u9a7e\u96fe\u4e00\u822c\uff0c\u4f46\u611f\u5929\u65cb\u5730\u8f6c\uff0c\u767b\u65f6\u5931\u4e86\u77e5\u89c9\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u9648\u7384\u673a\u60a0\u60a0\u9192\u8f6c\uff0c\u773c\u775b\u5c1a\u672a\u7741\u5f00\uff0c\u4e00\u80a1\u9189\u4eba\u7684\u5e7d\u9999\uff0c\u5df2\u900f\u5165\u9f3b\u7aef\uff0c\u9648 \u7384\u673a\u6025\u5fd9\u53eb\u9053\uff1a\u201c\u7d20\u7d20\uff0c\u7d20\u7d20\uff01\u201d \u3000\u3000\u4e00\u8f6c\u8eab\u53ea\u89c9\u6240\u7761\u4e4b\u5904\u51b0\u51b7\u575a\u786c\uff0c\u5168\u8eab\u9aa8\u8282\uff0c\u9690\u9690\u4f5c\u75db\uff0c\u90a3\u91cc\u662f\u4e91\u5bb6\u623f\u4e2d\u7684\u88ab\u8f6f\u9999\u6e29\u53ef \u6bd4\uff1f\u9648\u7384\u673a\u5403\u4e86\u4e00\u60ca\uff0c\u7741\u5f00\u773c\u65f6\uff0c\u53ea\u542c\u5f97\u4e00\u4e2a\u67d4\u5a9a\u7684\u5c11\u5973\u58f0\u97f3\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u7d20\u7d20\uff1f\u4f60\u68a6\u89c1\u8c01 \u5566\uff1f\u201d\u8fd9\u5c11\u5973\u662f\u8427\u97f5\u5170\u3002 \u3000\u3000\u9648\u7384\u673a\u8fd9\u624d\u53d1\u89c9\u662f\u5904\u8eab\u77f3\u6d1e\u4e4b\u4e2d\uff0c\u5947\u800c\u95ee\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u5728\u4e91\u5bb6\uff1f\u201d\u8427\u97f5\u5170\u9053\uff1a \u201c\u6211\u8ddf\u7740\u4f60\u7684\u8e44\u75d5\u9a6c\u8ff9\uff0c\u6765\u5230\u90a3\u513f\uff0c\u6b63\u5de7\u4f60\u7ed9\u4eba\u629b\u51fa\u5899\u5916\u3002\u5475\uff0c\u539f\u6765\u90a3\u662f\u4e91\u5bb6\uff0c\u90a3\u8001\u5934\u513f\u60f3 \u5fc5\u5c31\u662f\u4e91\u821e\u9633\u4e86\uff1f\u4f60\u771f\u5927\u80c6\uff0c\u5413\u6b7b\u6211\u4e86\uff01\u4f60\u548c\u4ed6\u4ea4\u624b\u4e86\uff1f\u201d \u3000\u3000\u9648\u7384\u673a\u892a\u7136\u5367\u5230\uff0c\u53f9\u4e86\u53e3\u6c14\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u60f3\u8d77\u81ea\u5df1\u4ece\u53d4\u4f2f\u8f88\u7684\u6089\u5fc3\u6307\u70b9\u4e4b\u4e0b\uff0c\u5b66\u4e86\u5341\u591a \u5e74\u7684\u6b66\u529f\uff0c\u4eba\u4eba\u90fd\u5938\u8d5e\u81ea\u5df1\u662f\u540e\u8d77\u4e4b\u79c0\uff0c\u5374\u4e0d\u6599\u548c\u4e91\u821e\u9633\u6bd4\u8d77\u6765\u7adf\u662f\u4e0d\u582a\u4e00\u51fb\uff0c\u5fc3\u4e2d\u60f6\u6127\u4e4b \u6781\uff0c\u4f46\u542c\u7684\u8427\u97f5\u5170\u7b11\u76c8\u76c8\u7684\u8d5e\u9053\uff1a\u201c\u4f60\u771f\u4e86\u5f97\uff0c\u7740\u4e86\u4e0a\u5b98\u5929\u91ce\u90a3\u4e00\u638c\uff0c\u5c45\u7136\u6ca1\u6709\u53d7\u4f24\uff0c\u8fd8\u80fd \u591f\u548c\u4e91\u821e\u9633\u4ea4\u624b\uff0c\u55ef\uff0c\u522b\u52a8\uff0c\u522b\u52a8\uff0c\u4f60\u867d\u7136\u6ca1\u6709\u6454\u574f\uff0c\u4e5f\u53d7\u4e86\u4e00\u70b9\u5916\u4f24\uff0c\u7600\u79ef\u8fd8\u6ca1\u6709\u5b8c\u5168\u5316 \u5f00\uff0c\u5f85\u6211\u7ed9\u4f60\u6413\u6413\uff01\u201d \u3000\u3000\u9648\u7384\u673a\u9762\u4e0a\u4e00\u7ea2\uff0c\u63b0\u5f00\u4e86\u5979\u7684\u7389\u624b\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u7528\u5566\uff01\u201d \u3000\u3000\u8427\u97f5\u5170\u4e0d\u63d0\u8d77\u4ed6\u7684\u4f24\u8fd8\u597d\uff0c\u4e00\u63d0\u8d77\u8fd9\u4e8b\uff0c\u4e0d\u7531\u7684\u4ed6\u53c8\u60f3\u8d77\u4e91\u7d20\u7d20\u6765\u3002\u60f3\u8d77\u5979\u7528\u7236\u4eb2\u6700\u73cd \u8d35\u7684\u7075\u4e39\u6551\u4e86\u81ea\u5df1\u7684\u6027\u547d\uff0c\u60f3\u8d77\u5979\u7ed9\u81ea\u5df1\u505a\u5c0f\u83dc\u548c\u7389\u7c73\u7ca5\uff0c\u60f3\u8d77\u5979\u5bf9\u81ea\u5df1\u4fe1\u4efb\u4e0d\u7591\uff0c\u7adf\u7136\u628a \u4e16\u95f4\u6700\u7f55\u89c1\u7684\u5b9d\u5251\u6302\u5728\u623f\u4e2d\uff0c\u8fd9\u4e00\u5207\u90fd\u5df2\u4ee4\u4eba\u611f\u52a8\u66f4\u96be\u5fd8\u6000\u7684\u662f\u90a3\u8574\u85cf\u4e0d\u9732\u3002 \u3000\u3000\u53ea\u80fd\u53e6\u4eba\u5fc3\u9886\u795e\u4f1a\u7684\u8109\u8109\u67d4\u60c5\u3002 \u3000\u3000\u8427\u97f5\u5170\u8d8a\u662f\u5bf9\u4ed6\u4eb2\u70ed\uff0c\u5c31\u8d8a\u53d1\u4ee4\u4ed6\u5bf9\u4e91\u7d20\u7d20\u601d\u5ff5\u4e0d\u5fd8\uff01\u4e91\u7d20\u7d20\u5c31\u50cf\u5e7d\u8c37\u5bd2\u6885\uff0c\u53ea\u6de1\u6de1\u7684 \u6e05\u9999\uff0c\u4fbf\u5df2\u80dc\u4f3c\u592d\u6843\u8273\u674e\u3002\u8427\u97f5\u5170\u5bdf\u89c9\u5230\u4ed6\u51b7\u6f20\u7684\u795e\u60c5\uff0c\u8be7\u7136\u95ee\u9053\uff1a\u201c\u4f60\u60f3\u4ec0\u4e48\uff1f\u201d\u9648\u7384\u673a \u5b9a\u4e86\u4e00\u4e0b\u5fc3\u795e\uff0c\u6005\u7136\u7b54\u9053\uff1a\u201c\u6211\u5728\u60f3\u5ff5\u4e0a\u5b98\u5929\u91ce\u3002\u201d \u3000\u3000\u8427\u97f5\u5170\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4e2a\u771f\u662f\u771f\u662f\u4e00\u5bf9\u51a4\u5bb6\uff0c\u89c1\u4e86\u9762\u6253\u67b6\uff0c\u79bb\u5f00\u4e86\u5374\u53c8\u5f7c\u6b64\u601d \u5ff5\uff0c\u55ef\uff0c\u4e0a\u5b98\u5929\u91ce\u4e5f\u6b63\u5728\u627e\u5bfb\u4f60\u5462\uff01\u201d\u9648\u7384\u673a\u9053\uff1a\u201c\u6211\u5df2\u89c1\u7740\u4ed6\u4e86\u3002\u201d\u8427\u97f5\u5170\u6025\u58f0\u95ee\u9053\uff1a \u201c\u5728\u90a3\u513f\uff1f\u201d\u9648\u7384\u673a\u9053\uff1a\u201c\u5c31\u5728\u4e91\u821e\u9633\u7684\u5bb6\u4e2d\u3002\u5440\uff0c\u6211\u800c\u4eca\u624d\u77e5\u9053\u4ed6\u662f\u4e2a\u81f3\u6027\u81f3\u60c5\u7684\u7537 \u5b50\uff01\u201d \u3000\u3000\u5c06\u6628\u665a\u7684\u4e8b\u60c5\uff0c\u4e00\u4e00\u5bf9\u8427\u97f5\u5170\u8bf4\u4e86\uff0c\u8427\u97f5\u5170\u63a9\u53e3\u7b11\u9053\uff1a\u201c\u53ef\u60dc\u4e0a\u5b98\u5929\u91ce\u6ca1\u542c\u5230\u4f60\u8fd9\u6837\u5938 \u4ed6\uff0c\u66f4\u53ef\u60dc\u4f60\u4e0d\u662f\u4e00\u4e2a\u5973\u5b50\uff01\u201d\u9648\u7384\u673a\u6b63\u8272\u9053\uff1a\u201c\u662f\u5440\uff0c\u6211\u82e5\u662f\u5973\u5b50\uff0c\u4e00\u5b9a\u4f1a\u559c\u6b22\u4ed6\uff01\u201d\u628a \u773c\u5077\u7aa5\u8427\u97f5\u5170\u7684\u795e\u8272\u3002\u4f46\u89c1\u8427\u97f5\u5170\u4f4e\u5782\u7c89\u9888\uff0c\u8584\u6012\u4f6f\u55d4\uff0c\u5550\u4e86\u4e00\u53e3\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u771f\u662f\uff0c\u522b\u4eba \u5bf9\u4f60\u3001\u5bf9\u4f60\u8d29\u8d29\u8d29\u4f60\u5374\u3001\u4f60\u5374\u8d29\u8d29\u8d29\u201d\u9648\u7384\u673a\u6025\u5fd9\u6253\u65ad\u5979\u7684\u8bdd\u9053\uff1a\u201c\u6211\u771f\u7684\u5728\u60f3\u5ff5\u4e0a\u5b98\u5929 \u91ce\uff0c\u4ed6\u4e3a\u6211\u800c\u843d\u5728\u4e91\u821e\u9633\u7684\u624b\u4e2d\uff0c\u53eb\u6211\u600e\u80fd\u5b89\u5fc3\uff1f\u201d\u8427\u97f5\u5170\u9053\uff1a\u201c\u4e91\u821e\u9633\u8fd9\u6837\u5389\u5bb3\uff0c\u54b1\u4eec\u5c31 \u662f\u820d\u4e86\u6027\u547d\uff0c\u4e5f\u6597\u4e0d\u8fc7\u4ed6\u3002\u4f60\u4e0d\u5982\u5b89\u5fc3\u9759\u517b\uff0c\u597d\u56de\u5230\u6b66\u5f53\u53bb\u62a5\u4fe1\u5440\uff0c\u5c31\u8ba9\u90a3\u4e9b\u6b66\u5f53\u7684\u8001\u9053\u58eb \u6597\u4e00\u6597\u4e91\u821e\u9633\u5427\uff0c\u4f60\u4e0d\u53ef\u5728\u5192\u9669\u884c\u523a\u4e86\uff01\u201d \u3000\u3000\u9648\u7384\u673a\u6697\u4e3a\u4e0a\u5b98\u5929\u91ce\u53f9\u606f\uff0c\u5fc3\u9053\uff1a\u201c\u4e0a\u5b98\u5929\u91ce\u5bf9\u4f60\u75f4\u5fc3\u4e00\u7247\uff0c\u96be\u9053\u4f60\u7adf\u65e0\u52a8\u4e8e\u8877\uff1f\u201d\u8427 \u97f5\u5170\u89c1\u9648\u7384\u673a\u4e45\u4e45\u4e0d\u8bed\uff0c\u5446\u4e86\u4e00\u4f1a\uff0c\u67d4\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u809a\u5b50\u997f\u5417\uff1f\u6211\u7ed9\u4f60\u70e4\u4e24\u53ea\u91ce\u5154\u3002\u201d\u9648\u7384 \u673a\u6b20\u8eab\u8981\u8d77\uff0c\u6b63\u60f3\u8981\u8bf4\u81ea\u5df1\u8eab\u4f53\u6ca1\u4e8b\uff0c\u4e0d\u5fc5\u52b3\u70e6\uff0c\u89c1\u8427\u97f5\u5170\u5df2\u8d70\u51fa\u6d1e\u53e3\uff0c\u60f3\u4e86\u4e00\u60f3\uff0c\u7ec8\u4e8e\u8ba9 \u5979\u53bb\u4e86\u3002 \u3000\u3000\u90a3\u5c71\u6d1e\u662f\u4e24\u5757\u5927\u77f3\u5408\u62b1\u800c\u6210\uff0c\u4ece\u6d1e\u53e3\u671b\u51fa\uff0c\u4f46\u89c1\u660e\u6708\u768e\u768e\uff0c\u539f\u6765\u53c8\u662f\u7b2c\u4e8c\u5929\u7684\u665a\u4e0a\u4e86\uff0c \u9648\u7384\u673a\u7ad9\u4e86\u8d77\u6765\uff0c\u6d3b\u52a8\u4e00\u4e0b\u7b4b\u9aa8\uff0c\u7f13\u6b65\u63cd\u51fa\u77f3\u6d1e\uff0c\u501a\u7740\u5ca9\u77f3\uff0c\u7591\u671b\u5c71\u9876\u90a3\u51e0\u680b\u623f\u5c4b\uff0c\u4e91\u7d20\u7d20 \u7684\u6b4c\u58f0\u821e\u5f71\u91cd\u6cdb\u5fc3\u5934\uff0c\u53c8\u604d\u4f3c\u5979\u5c31\u5728\u90a3\u5cf0\u5dc5\u4e0a\u5411\u81ea\u5df1\u8fdc\u8fdc\u62db\u624b\u3002 \u3000\u3000\u9648\u7384\u673a\u53f9\u4e86\u4e00\u53e3\u957f\u6c14\uff0c\u5fc3\u9053\uff1a\u201c\u53ef\u60dc\u5979\u662f\u4e91\u821e\u9633\u7684\u5973\u513f\uff0c\u5440\uff0c\u6211\u8fd8\u60f3\u7740\u5979\u5e72\u4ec0\u4e48\uff1f\u6211\u6b66 \u529f\u82e5\u672a\u7ec3\u6210\uff0c\u600e\u80fd\u8e0f\u8fdb\u90a3\u5ea7\u623f\u5b50\uff1f\u5440\uff0c\u96be\u9053\u771f\u662f\u8981\u5341\u5e74\u4e4b\u540e\u624d\u80fd\u89c1\u9762\uff1f\u201d\u60f3\u8d77\u5341\u5e74\u4e4b\u540e\uff0c\u81ea \u5df1\u4e5f\u672a\u5fc5\u6597\u5f97\u8fc7\u4e91\u821e\u9633\uff0c\u5fc3\u4e2d\u66f4\u4e3a\u60c6\u6005\uff0c\u5ffd\u7684\u53c8\u60f3\u9053\uff1a\u201c\u4e0d\u77e5\u5979\u53ef\u601d\u5ff5\u4e8e\u6211\uff1f\u82e5\u662f\u5979\u4e5f\u601d\u5ff5 \u4e8e\u6211\uff0c\u6211\u771f\u613f\u610f\u518d\u5192\u6027\u547d\u4e4b\u5371\uff01\u201d\u9ec4\u4ef2\u5219\u8bd7\u9053\uff1a\u201c\u5982\u6b64\u661f\u8fb0\u975e\u6628\u591c\uff0c\u4e3a\u8c01\u98ce\u9732\u7acb\u4e2d\u5bb5\uff1f\u201d\u9648 \u7384\u673a\u6bd4\u9ec4\u4ef2\u5219\uff08\u6e05\u8bd7\u4eba\uff09\u65e9\u751f\u4e86\u4e09\u767e\u591a\u5e74\uff0c\u5f53\u7136\u6ca1\u6709\u5ff5\u8fc7\u8fd9\u4e24\u53e5\u8bd7\uff0c\u53ef\u662f\u8fd9\u611f\u60c5\u4eca\u53e4\u76f8\u901a\uff0c \u9648\u7384\u673a\u8fd9\u65f6\u5fc3\u4e2d\u6240\u60f3\u7684\uff0c\u9664\u4e86\u4e91\u7d20\u7d20\u5916\uff0c\u66f4\u65e0\u6742\u5ff5\uff0c\u4ed6\u4e2d\u5bb5\u72ec\u7acb\uff0c\u4e00\u70b9\u4e5f\u4e0d\u89c9\u5f97\uff0c\u6562\u60c5\u7adf\u662f \u60f3\u5f97\u75f4\u4e86\u3002 \u3000\u3000\u5ffd\u542c\u7684\u4e00\u58f0\u957f\u5578\uff0c\u8fdc\u8fdc\u4f20\u6765\uff0c\u6709\u4eba\u5728\u5c71\u5cf0\u4e0a\u653e\u58f0\u6b4c\u9053\uff1a\u201c\u767e\u6218\u5f52\u6765\u9152\u5c1a\u6e29\uff0c\u7e41\u971c\u4fb5\u9b13\u8f6c \u6d88\u6c89\uff0c\u91d1\u6208\u94c1\u9a6c\u5f53\u5e74\u6068\uff0c\u8f9c\u8d1f\u6885\u82b1\u4e00\u7247\u5fc3\uff01\u201d \u3000\u3000\u9648\u7384\u673a\u5403\u4e86\u4e00\u60ca\uff0c\u8fd9\u662f\u4e91\u821e\u9633\u7684\u6b4c\u58f0\uff0c\u6fc0\u6602\u800c\u53c8\u6c89\u90c1\u7684\u6b4c\u58f0\uff0c\u8fd9\u4e48\u665a\u4e86\uff0c\u4ed6\u8fd8\u672a\u7761\uff1f\u96be \u9053\u4ed6\u4e5f\u5728\u60f3\u4ec0\u4e48\u5fc3\u4e8b\u4e48\uff1f\u4e00\u62ac\u5934\u53ea\u89c1\u4e00\u6761\u4eba\u5f71\uff0c\u5411\u5357\u9762\u75be\u9a70\u800c\u4e0b\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u5c31\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u9648\u7384\u673a\u5446\u4e86\u4e00\u4f1a\uff0c\u60f3\u4e0d\u900f\u4e91\u821e\u9633\u4f55\u4ee5\u6df1\u591c\u4e0b\u5c71\u3002\u4ed6\u8eab\u4e0d\u7531\u5df1\u7684\u5411\u7740\u5c71\u4e0a\u7684\u4e91\u5bb6\u8d70\u53bb\uff0c\u5ffd \u53c8\u542c\u5f97\u7434\u58f0\u9635\u9635\uff0c\u4ece\u5c71\u5cf0\u4e0a\u98d8\u4e0b\u6765\uff0c\u5440\uff0c\u90a3\u7adf\u662f\u4e91\u7d20\u7d20\u7684\u6b4c\u58f0\uff01\u665a\u98ce\u5439\u6765\uff0c\u6b4c\u58f0\u9690\u7ea6\u53ef\u8fa8\uff0c \u5979\u5531\u7684\u662f\uff1a\u201c\u768e\u768e\u767d\u9a79\uff0c\u98df\u6211\u573a\u82d7\u3002\u7d77\u4e4b\u7ef4\u4e4b\uff0c\u4ee5\u6c34\u4eca\u671d\u3002\u6240\u8c13\u4f0a\u4eba\uff0c\u4e8e\u7109\u900d\u9065\u3002\u768e\u768e\u767d \u9a79\uff0c\u5728\u5f7c\u7a7a\u8c37\u3002\u751f\u520d\u4e00\u675f\uff0c\u5176\u4eba\u5982\u7389\u3002\u6bcb\u91d1\u7389\u5c14\u97f3\uff0c\u800c\u6709\u9050\u5fc3\uff01\u201d\u8fd9\u662f\u8bd7\u7ecf\u4e2d\u300a\u5c0f\u96c5\u767d\u9a79\u300b \u4e00\u7ae0\u4e2d\u7684\u4e24\u8282\uff0c\u4e43\u662f\u9001\u5ba2\u60dc\u522b\u7684\u8bd7\uff0c\u4e0a\u4e00\u8282\u662f\u5ba2\u5df2\u5230\u800c\u633d\u7559\uff0c\u4e0b\u4e00\u8282\u662f\u5ba2\u5df2\u53bb\u800c\u76f8\u5fc6\u3002 \u3000\u3000\u9648\u7384\u673a\u542c\u5f97\u50bb\u4e86\uff01 \u3000\u3000------------- \u3000\u3000\u91cd\u5e86\u96ea\u513f \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u8fd8\u5251\u5947\u60c5\u5f55\u300b\u2014\u2014\u7b2c\u4e09\u56de\u3000\u8352\u5c71\u5251\u6c14 \u6881\u7fbd\u751f\u300a\u8fd8\u5251\u5947\u60c5\u5f55\u300b \u7b2c\u4e09\u56de\u3000\u8352\u5c71\u5251\u6c14 \u3000\u3000\u8fd9\u4e24\u8282\u8bd7\u7ecf\u7ffb\u8bd1\u6210\u767d\u8bdd\u8bd7\u5c31\u662f\uff1a \u3000\u3000\u201c\u767d\u767d\u7684\u5c0f\u9a6c\u513f\uff0c\u5403\u6211\u573a\u4e0a\u7684\u9752\u82d7\u3002\u62f4\u8d77\u5b83\u62f4\u8d77\u5b83\u554a\uff0c\u5ef6\u957f\u6b22\u4e50\u7684\u4eca\u671d\u3002\u90a3\u4e2a\u4eba\u90a3\u4e2a\u4eba \u554a\uff0c\u66fe\u5728\u8fd9\u513f\u548c\u6211\u6b22\u4e50\u900d\u9065\u3002\u767d\u767d\u7684\u5c0f\u9a6c\u513f\uff0c\u56de\u5230\u5c71\u8c37\u53bb\u4e86\u3002\u5480\u56bc\u7740\u4e00\u6346\u9752\u8349\u3002\u90a3\u4eba\u513f\u554a\u7389 \u4e00\u822c\u7f8e\u597d\u3002\u522b\u5fd8\u4e86\u7ed9\u6211\u634e\u4e2a\u4fe1\u554a\uff01\u522b\u6709\u758f\u8fdc\u6211\u7684\u5fc3\u554a\uff01\u201d \u3000\u3000\u542c\u8fd9\u7434\u58f0\u6b4c\u610f\uff0c\u4e91\u7d20\u7d20\u7adf\u662f\u5728\u6df1\u6df1\u7684\u601d\u5ff5\u4ed6\uff0c\u9648\u7384\u673a\u7136\u53f9\u9053\uff1a\u201c\u6211\u90a3\u767d\u9a6c\u513f\u8fd8\u5728\u4f60\u5bb6\uff0c \u660e\u671d\u8fd8\u4f1a\u5480\u56bc\u4f60\u95e8\u8c15\u7684\u9752\u8349\u3002\u5440\uff0c\u6211\u53ea\u6015\u4e0d\u80fd\u518d\u8e0f\u8fdb\u4f60\u7684\u5bb6\u95e8\u4e86\uff01\u201d\u62ac\u5934\u51dd\u671b\uff1a\u7389\u5b57\u65e0\u5c18\uff0c \u94f6\u6cb3\u6cfb\u5f71\uff0c\u6708\u5149\u5982\u6c34\uff0c\u826f\u591c\u8fe2\u8fe2\uff0c\u53ea\u662f\u5fc3\u4e0a\u7684\u4eba\u513f\uff0c\u5374\u5728\u53ef\u671b\u4e0d\u53ef\u5373\u7684\u6885\u82b1\u6df1\u5904\uff01 \u3000\u3000\u6b4c\u58f0\u8885\u8885\uff0c\u98d8\u8361\u5c71\u5dc5\u6c34\u6daf\uff0c\u9648\u7384\u673a\u4e00\u7247\u832b\u7136\uff0c\u4e5f\u4f3c\u968f\u7740\u90a3\u7434\u97f5\u6b4c\u58f0\uff0c\u795e\u98d8\u610f\u8361\uff0c\u4e91\u7d20\u7d20 \u5a07\u75f4\u7684\u60c5\u5f71\u6cdb\u4e0a\u5fc3\u5934\uff0c\u4e0a\u5b98\u5929\u91ce\u7c97\u8c6a\u7684\u7b11\u58f0\u7d22\u56de\u8033\u7554\uff0c\u201c\u4e3a\u4e86\u8fd9\u4e24\u4e2a\u4eba\uff0c\u6211\u4f55\u60dc\u518d\u5192\u4e00\u6b21\u751f \u547d\u7684\u5371\u9669\uff1f\u201d\u9648\u7384\u673a\u4e0b\u4e86\u51b3\u5fc3\uff0c\u7ec8\u4e8e\u53c8\u518d\u4e0a\u5c71\u5cf0\u53bb\u4e86\u3002\u7434\u58f0\u5212\u7136\u800c\u6b62\uff0c\u7a7a\u5c71\u7edd\u54cd\uff0c\u53c8\u590d\u5f52\u4e8e \u9759\u5bc2\u3002\u9648\u7384\u673a\u5fc3\u4e2d\u4e00\u52a8\uff0c\u505c\u4e0b\u6b65\u6765\uff0c\u53ea\u542c\u5f97\u6709\u6781\u8f7b\u5fae\u7684\u51e0\u4e0b\u64e6\u64e6\u4e4b\u58f0\uff0c\u76f4\u98d8\u8033\u9f13\uff0c\u82e5\u975e\u9648\u7384 \u673a\u81ea\u5c0f\u5c31\u7ec3\u8fc7\u6536\u53d1\u6697\u5668\u7684\u4e0a\u4e58\u529f\u592b\uff0c\u8fd8\u771f\u542c\u4e0d\u51fa\u6765\uff01\u90a3\u58f0\u97f3\u8d8a\u6765\u8d8a\u8fd1\u4e86\uff0c\u9648\u7384\u673a\u8fd9\u65f6\u66f4\u542c\u5f97 \u6e05\u695a\u4e86\uff0c\u6765\u7684\u4e0d\u6b62\u4e00\u4eba\uff0c\u524d\u9762\u90a3\u4e2a\u4eba\u7684\u811a\u6b65\u58f0\u548c\u540e\u9762\u90a3\u51e0\u4e2a\u4eba\u7684\u811a\u6b65\u58f0\uff0c\u76f8\u8ddd\u7ea6\u6709\u6570\u5341\u4e08\u4e4b \u9065\uff0c\u500f\u5ffd\u4e4b\u95f4\uff0c\u5c31\u5230\u4e86\u9648\u7384\u673a\u524d\u9762\uff0c\u5f53\u771f\u662f\u5feb\u5230\u6781\u70b9\uff0c\u7adf\u7136\u90fd\u662f\u201c\u8e0f\u96ea\u65e0\u75d5\u201d\u7684\u8f7b\u8eab\u529f\u592b\uff0c \u9648\u7384\u673a\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u6025\u5fd9\u8eba\u5728\u5927\u6811\u7684\u80cc\u540e\u3002 \u3000\u3000\u53ea\u89c1\u524d\u9762\u90a3\u4e2a\u9ed1\u8863\u6c49\u5b50\uff0c\u4e00\u58f0\u957f\u5578\uff0c\u66ae\u7136\u6b62\u6b65\uff0c\u51b7\u7136\u53d1\u8bdd\u9053\uff1a\u201c\u77f3\u67d0\u987e\u5ff5\u591a\u5e74\u60c5\u4efd\uff0c\u8bf8 \u5144\u5374\u4f55\u6545\u7a77\u8ffd\u4e0d\u820d\uff1f\u96be\u9053\u5f53\u771f\u8981\u8ffd\u5230\u4e91\u5bb6\uff0c\u8feb\u5c0f\u5f1f\u51b3\u88c2\u4e48\uff1f\u201d\u968f\u5373\u542c\u5f97\u4e00\u4e2a\u7834\u9523\u822c\u7684\u58f0\u97f3\u559d \u9053\uff1a\u201c\u77f3\u5929\u94ce\u4f60\u4f11\u8981\u81ea\u6043\u6b66\u529f\uff0c\u8fde\u5c11\u4e3b\u7684\u91d1\u724c\u4e5f\u4e0d\u653e\u5728\u773c\u5185\uff01\u4f60\u5230\u4e91\u5bb6\u610f\u6b32\u4f55\u4e3a\uff1f\u201d \u3000\u3000\u8bdd\u58f0\u5165\u8033\uff0c\u9648\u7384\u673a\u66f4\u662f\u60ca\u5f97\u5446\u4e86\uff0c\u60f3\u4e0d\u5230\u8fd9\u4e2a\u9ed1\u8863\u6c49\u5b50\u7adf\u7136\u5c31\u662f\u6614\u5e74\u540d\u9707\u5929\u4e0b\u3001\u5728\u6b66\u6797 \u4e2d\u58f0\u540d\u4ec5\u6b21\u4e8e\u5f6d\u548c\u5c1a\u800c\u5728\u4e91\u821e\u9633\u4e4b\u4e0a\u7684\u77f3\u5929\u94ce\uff0c\u81ea\u5f20\u58eb\u8bda\u88ab\u6731\u5143\u6f33\u64d2\u6740\u4e4b\u540e\uff0c\u5f6d\u548c\u5c1a\u6b89\u96be\uff0c \u77f3\u5929\u94ce\u4e0d\u77e5\u6240\u7ec8\uff0c\u6709\u4eba\u4f20\u8bf4\u4ed6\u4fdd\u62a4\u5f20\u58eb\u8bda\u7684\u513f\u5b50\u9003\u5230\u6f20\u5317\uff0c\u4e5f\u4e0d\u77e5\u662f\u771f\u662f\u5047\uff0c\u5374\u4e0d\u6599\u4f1a\u5728\u8fd9 \u4e2a\u6df1\u591c\uff0c\u51fa\u73b0\u5728\u8d3a\u5170\u5c71\u4e0a\uff0c\u800c\u4e14\u542c\u6765\u8fd8\u662f\u53bb\u627e\u4e91\u821e\u9633\uff01 \u3000\u3000\u9648\u7384\u673a\u5927\u662f\u7591\u60d1\uff0c\u60f3\u8fd9\u77f3\u5929\u94ce\u4e49\u80c6\u5fe0\u5fc3\uff0c\u5f53\u5e74\u66fe\u820d\u4e86\u6027\u547d\uff0c\u5728\u5f20\u4e0a\u8bda\u56fd\u7834\u5bb6\u4ea1\u4e4b\u65e5\uff0c\u5c06 \u4ed6\u7684\u513f\u5b50\u62a2\u6551\u51fa\u6765\uff0c\u77f3\u5929\u94ce\u7684\u519b\u4e2d\u65e7\u4fa3\uff0c\u4ea6\u5373\u9648\u7384\u673a\u7684\u5e08\u957f\u53d4\u4f2f\u8f88\uff0c\u6bcf\u4e00\u8c08\u53ca\uff0c\u65e0\u4e0d\u94a6\u4f69\uff0c \u4f55\u4ee5\u8fd9\u4e2a\u4eba\u5374\u9a82\u4ed6\u81ea\u6043\u6b66\u529f\uff0c\u8fde\u5c11\u4e3b\u7684\u91d1\u724c\u4e5f\u4e0d\u653e\u5728\u773c\u5185\uff1f\u96be\u9053\u8fd9\u4e2a\u4eba\u53e3\u4e2d\u7684\u201c\u5c11\u4e3b\u201d\u4e0d\u662f \u5927\u5468\uff08\u5f20\u58eb\u8bda\u6240\u5efa\u56fd\u53f7\uff09\u7684\u4ea1\u56fd\u592a\u5b50\u4e48\uff1f \u3000\u3000\u90a3\u7834\u9523\u822c\u7684\u58f0\u97f3\u521a\u4e00\u5165\u8033\uff0c\u4eba\u5df2\u5230\u4e86\u8ddf\u524d\uff0c\u9648\u7384\u673a\u5728\u6811\u540e\u6109\u7aa5\uff0c\u4f46\u89c1\u8ffd\u8e2a\u77f3\u5929\u94ce\u800c\u6765\u7684 \u5171\u6709\u4e09\u4eba\uff0c\u4e2a\u4e2a\u88c5\u675f\u53e4\u602a\uff0c\u4e00\u4e2a\u9053\u58eb\uff0c\u4e00\u4e2a\u6253\u626e\u5f97\u7c7b\u4f3c\u4e61\u4e0b\u8001\u519c\uff0c\u624b\u957f\u8fc7\u819d\uff0c\u7126\u9ec4\u7684\u8138\u4e0a\u6beb \u65e0\u8868\u60c5\uff0c\u8fd8\u6709\u4e00\u4e2a\u5374\u662f\u4f5c\u8499\u53e4\u88c5\u675f\u7684\u6b66\u58eb\uff0c\u90a3\u7834\u9523\u822c\u7684\u58f0\u97f3\u4e43\u662f\u9053\u58eb\u6240\u53d1\u3002 \u3000\u3000\u8fd9\u9053\u58eb\u76f8\u8c8c\u597d\u719f\uff0c\u4f46\u542c\u5f97\u77f3\u5929\u94ce\u5e94\u9053\uff1a\u201c\u4e03\u4fee\u9053\u5144\uff0c\u4f60\u82e5\u95ee\u5c0f\u5f1f\u5230\u4e91\u5bb6\u4e4b\u610f\uff0c\u5148\u8bf7\u95ee\u4f60 \u81ea\u5df1\u4f55\u4ee5\u8981\u8ffd\u8e2a\u81f3\u6b64\uff01\u201d\u9648\u7384\u673a\u5fc3\u5934\u4e00\u9707\uff0c\u679c\u7136\u662f\u4ed6\uff01 \u3000\u3000\u8fd9\u4e03\u4fee\u9053\u4eba\u4e43\u662f\u5f53\u5e74\u5f20\u4e0a\u8bda\u6240\u5ef6\u8058\u7684\u5ba2\u537f\uff0c\u8bf7\u6765\u6559\u5927\u5b50\u5f20\u590d\u521d\u7684\u5251\u672f\u7684\uff0c\u5f20\u58eb\u8bda\u6700\u5c0a\u5d07 \u7684\u5ba2\u537f\u5171\u6709\u4e09\u4eba\uff0c\u4e43\u662f\u4e00\u50e7\u4e00\u9053\u4e00\u4e10\u3002\u201c\u50e7\u201d\u662f\u5f6d\u83b9\u7389\u5f6d\u548c\u5c1a\uff0c\u201c\u4e10\u201d\u662f\u5317\u65b9\u7684\u4e10\u5e2e\u5e2e\u4e3b\u6bd5 \u51cc\u865a\uff0c\u201c\u9053\u201d\u5c31\u662f\u8fd9\u4f4d\u4e03\u4fee\u9053\u957f\uff01\u5f53\u65f6\u6b66\u6797\u54b8\u5c0a\u5f6d\u548c\u5c1a\u6b66\u529f\u5929\u4e0b\u7b2c\u4e00\uff0c\u81f3\u4e8e\u77f3\u5929\u94ce\u3001\u4e91\u821e \u9633\u3001\u6bd5\u51cc\u865a\u3001\u4e03\u4fee\u9053\u4eba\u7b49\u4eba\u5219\u5404\u6709\u4e13\u957f\uff0c\u96be\u4e8e\u54c1\u5b9a\uff0c\u56fa\u77f3\u3001\u4e91\u4e8c\u4eba\u5747\u662f\u5f20\u58eb\u8bda\u6700\u4eb2\u8fd1\u7684\u6b66 \u58eb\uff0c\u4e0e\u5f6d\u83b9\u7389\u5e38\u5728\u4e00\u8d77\uff0c\u6240\u4ee5\u53c8\u6709\u4eba\u5c06\u77f3\u3001\u4e91\u4e8c\u4eba\u4e0e\u5f6d\u548c\u5c1a\u5e76\u5217\uff0c\u79f0\u4e3a\u5f20\u58eb\u8bda\u519b\u4e2d\u7684\u201c\u9f99\u864e \u51e4\u201d\u4e09\u6770\u3002\u9648\u7384\u673a\u5c0f\u65f6\u5019\u66fe\u89c1\u8fc7\u4e03\u4fee\u9053\u957f\u4e00\u9762\uff0c\u4e0d\u8fc7\u90a3\u65f6\u9648\u7384\u673a\u53ea\u6709\u4e03\u5c81\uff0c\u6240\u4ee5\u4e00\u65f6\u4e0d\u80fd\u8bb0 \u8d77\u3002 \u3000\u3000\u6708\u5149\u4e4b\u4e0b\uff0c\u53ea\u89c1\u516d\u4fee\u9053\u4eba\u626c\u8d77\u4e00\u9762\u91d1\u724c\uff0c\u53eb\u9053\uff1a\u201c\u6211\u662f\u5949\u4e86\u5c11\u4e3b\u4e4b\u547d\u8ffd\u4f60\u56de\u53bb\uff01\u516c\u4e49\u79c1 \u60c5\uff0c\u90fd\u4e0d\u8bb8\u4f60\u53db\u4e3b\u6c42\u8363\uff01\u201d\u77f3\u5929\u94ce\u51b7\u7b11\u9053\uff1a\u201c\u6211\u82e5\u8981\u53db\u4e3b\u6c42\u8363\uff0c\u4e5f\u4e0d\u5fc5\u5f85\u4eca\u65e5\u4e86\u3002\u60f3\u5f53\u5e74\u4e3b \u4e0a\u5175\u8d25\u957f\u6c5f\uff0c\u6211\u62a4\u9001\u5148\u592a\u5b50\u5355\u9a91\u6e21\u6c5f\uff0c\u8fdc\u9003\u5f02\u57df\uff0c\u4e00\u8def\u4e4b\u4e0a\uff0c\u8fde\u6bd9\u6731\u5143\u6f33\u624b\u4e0b\u7684\u5341\u516b\u540d\u6b66 \u58eb\uff0c\u6211\u82e5\u60f3\u5728\u6731\u5143\u6f33\u624b\u4e0b\u6c42\u53d6\u5bcc\u8d35\uff0c\u90a3\u9526\u8863\u536b\u603b\u6307\u6325\u7684\u4f4d\u5b50\uff0c\u4e5f\u8f6e\u4e0d\u5230\u90a3\u4e2a\u4ec0\u4e48\u7f57\u91d1\u5cf0\u6765\u5750 \u5566\uff01\u201d \u3000\u3000\u4e03\u4fee\u9053\u4eba\u9053\uff1a\u201c\u6211\u8f88\u540c\u53d7\u5148\u5e1d\u539a\u6069\uff0c\u820d\u8eab\u62a5\u4e3b\uff0c\u4efd\u6240\u5e94\u5f53\u3002\u4f60\u4e3a\u5148\u5e1d\u4fdd\u5b58\u8840\u8109\uff0c\u6211\u81ea\u662f \u4f69\u670d\u5f97\u5f88\u3002\u4f46\u5927\u4e08\u592b\u7406\u5f53\u6709\u59cb\u6709\u7ec8\uff0c\u4f60\u65e2\u653e\u51fa\u5148\u592a\u5b50\u4e8e\u524d\uff0c\u4f55\u4ee5\u53c8\u629b\u5f03\u4ed6\u7684\u9057\u5b64\u4e8e\u540e\uff1f\u4f55\u51b5 \u5c11\u4e3b\u5e74\u9752\u6709\u4e3a\uff0c\u6b63\u8be5\u4f60\u6211\u622e\u529b\u540c\u5fc3\uff0c\u52a9\u4ed6\u590d\u56fd\uff01\u4f60\u79c1\u81ea\u9003\u8d70\uff0c\u8fd8\u8981\u5230\u6b64\u5730\u627e\u4e91\u821e\u9633\uff0c\u8bf7\u95ee\u4f60 \u6000\u7684\u662f\u4ec0\u4e48\u5fc3\u610f\uff1f \u3000\u3000\u9648\u7384\u673a\u8fd9\u624d\u77e5\u9053\u4ed6\u53d4\u4f2f\u8f88\u6240\u9065\u5949\u7684\u201c\u592a\u5b50\u201d\u5df2\u5ba2\u6b7b\u5f02\u57df\uff0c\u77f3\u5929\u94ce\u4e03\u4fee\u9053\u4eba\u7b49\u53e3\u4e2d\u6240\u8bf4\u7684 \u201c\u5c11\u4e3b\u201d\u5df2\u662f\u5f20\u58eb\u8bda\u7684\u5b59\u5b50\u4e86\u3002\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6731\u5f20\u4e8c\u59d3\u4e89\u593a\u6c5f\u5c71\uff0c\u8fd9\u98ce\u6ce2\u5df2\u5ef6\u81f3\u7b2c\u4e09\u4ee3\u4e86\uff0c \u5c06\u6765\u8fd8\u4e0d\u77e5\u4f55\u65f6\u4e86\u7ed3\uff1f\u90a3\u4e91\u821e\u9633\u610f\u6b32\u5356\u53cb\u6c42\u8363\uff0c\u662f\u4e0d\u4e49\u4e4b\u4eba\u3002\u4f46\u4ed6\u6240\u8bf4\u7684\u4e3a\u4e00\u5bb6\u4e00\u59d3\u4e89\u593a\u6c5f \u5c71\u6b8a\u5c5e\u65e0\u8c13\u7684\u8bdd\uff0c\u5374\u4e5f\u672a\u5c1d\u6ca1\u6709\u9053\u7406\u3002\u201d\u4e00\u65f6\u601d\u6f6e\u6df7\u4e71\uff0c\u5bf9\u81ea\u5df1\u5377\u5165\u8fd9\u6f29\u6da1\u4e4b\u4e2d\uff0c\u7a76\u7adf\u662f\u5bf9 \u8fd8\u662f\u4e0d\u5bf9\uff0c\u4e5f\u611f\u5230\u832b\u7136\u4e86\u3002 \u3000\u3000\u53ea\u542c\u5f97\u77f3\u5929\u94ce\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u6b63\u56e0\u4e3a\u5c11\u4e3b\u5e74\u9752\u6709\u4e3a\uff0c\u6211\u624d\u4e0d\u613f\u4f60\u4eec\u5c06\u4ed6\u6bc1\u4e86\u3002\u60f3\u5f53\u5e74\u5148\u592a \u5b50\u8d50\u540d\u5c11\u4e3b\uff0c\u53f7\u4e3a\u2018\u5b97\u5468\u2019\uff0c\u4e43\u662f\u8981\u4ed6\u7ee7\u627f\u5148\u5e1d\uff0c\u6bcb\u5fd8\u6545\u56fd\uff0c\u53ef\u4e0d\u662f\u8981\u4ed6\u4ee5\u74e6\u523a\u4e3a\u5b97\uff0c\u5949\u86ee \u5937\u4e4b\u541b\u4e3a\u4e3b\uff01\u201d\u9648\u7384\u673a\u6014\u4e86\u4e00\u5f81\uff0c\u4ec0\u4e48\u201c\u74e6\u523a\u201d\uff1f\u4ec0\u4e48\u201c\u86ee\u5937\u4e4b\u541b\u201d\uff1f\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u90a3\u65f6 \u201c\u74e6\u523a\u201d\u4e43\u662f\u8499\u53e4\u5730\u65b9\u7684\u4e00\u4e2a\u90e8\u843d\uff0c\u5c1a\u672a\u5efa\u6210\u56fd\u5bb6\uff0c\u8fd9\u540d\u5b57\u5728\u4e2d\u56fd\u4e00\u822c\u4eba\u5747\u4e0d\u77e5\u6653\u3002 \u3000\u3000\u4e03\u4fee\u9053\u4eba\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u5c1a\u672a\u53d1\u8bdd\uff0c\u77f3\u5929\u94ce\u53c8\u9053\uff1a\u201c\u6211\u4e0e\u821e\u9633\u5144\u4e00\u522b\u4e8c\u5341\u5e74\uff0c\u4e0d\u77e5\u4ed6\u5fc3 \u610f\u5982\u4f55\uff1f\u4f46\u6211\u603b\u5f53\u5c3d\u529b\u529d\u4ed6\uff0c\u4e0d\u8ba9\u4ed6\u4e5f\u968f\u4f60\u4eec\u540c\u9677\u6c61\u6dd6\uff01\u201d\u90a3\u8001\u6c49\u84e6\u5730\u4e00\u58f0\u559d\u9053\uff1a\u201c\u77f3\u5929\u94ce \u4f60\u53cd\u4e86\uff0c\u79c1\u9003\u4e4b\u7f6a\u5df2\u662f\u4e0d\u8f7b\uff0c\u4f60\u8fd8\u60f3\u7834\u574f\u6211\u4eec\u7684\u5927\u4e8b\u4e48\uff1f\u201d\u90a3\u8499\u53e4\u6b66\u58eb\u559d\u9053\uff1a\u201c\u8fd8\u4e0e\u4ed6\u591a\u8bf4 \u4ec0\u4e48\uff1f\u56fd\u4e3b\u6709\u547d\uff0c\u6b64\u4eba\u82e5\u4e0d\u5c31\u8303\uff0c\u5c31\u5c06\u4ed6\u6bd9\u4e86\uff01\u201d\u5ffd\u5730\u6296\u8d77\u957f\u97ad\uff0c\u5237\u7684\u4e00\u97ad\uff0c\u4fbf\u5411\u77f3\u5929\u94ce\u62e6 \u8170\u75be\u626b\uff01 \u3000\u3000\u97ad\u98ce\u8fc7\u5904\uff0c\u6811\u53f6\u7eb7\u843d\uff0c\u6c99\u98de\u77f3\u8d70\uff0c\u201c\u5361\u5566\u201d\u4e00\u58f0\uff0c\u9648\u7384\u673a\u8eab\u4fa7\u7684\u4e00\u68f5\u5927\u6811\uff0c\u7ade\u88ab\u957f\u97ad\u626b \u65ad\u4e86\u4e24\u679d\u7c97\u5982\u513f\u81c2\u7684\u6811\u679d\uff0c\u52b2\u529b\u4e4b\u5927\uff0c\u5b9e\u662f\u60ca\u4eba\u3002\u77f3\u5929\u94ce\u53eb\u9053\uff1a\u201c\u5ff5\u5728\u4f60\u5904\u591a\u5e74\uff0c\u4e5f\u6709\u4e3b\u5ba2 \u4e4b\u8c0a\uff0c\u8ba9\u4f60\u4e09\u97ad\uff01\u201d\u5237\uff0c\u5237\uff0c\u5237\u4e09\u97ad\u8fc7\u5904\uff0c\u77f3\u5929\u94ce\u5728\u4e00\u56e2\u97ad\u5f71\u4e4b\u4e0b\uff0c\u84e6\u5730\u4e00\u58f0\u957f\u5578\uff0c\u4e00\u4e2a \u201c\u71d5\u5b50\u94bb\u4e91\u201d\uff0c\u5237\u5730\u51ed\u7a7a\u8df3\u8d77\u4e24\u4e08\u591a\u9ad8\uff0c\u51cc\u7a7a\u4e0b\u51fb\uff0c\u90a3\u8499\u53e4\u6b66\u58eb\u957f\u97ad\u76f4\u6296\uff0c\u53ea\u542c\u5f97\u201c\u7830\u201d\u7684 \u4e00\u58f0\uff0c\u80a9\u5934\u5df2\u662f\u4e2d\u4e86\u4e00\u638c\uff01\u77f3\u5929\u94ce\u4ee5\u94c1\u638c\u795e\u7b14\uff0c\u53f7\u79f0\u6b66\u6797\u53cc\u7edd\uff0c\u8fd9\u4e00\u638c\u81ea\u662f\u6253\u5f97\u4e0d\u8f7b\uff0c\u4f46\u8499 \u53e4\u6b66\u58eb\u5c45\u7136\u4e5f\u633a\u5f97\u4f4f\uff0c\u95f7\u58f0\u4e0d\u54cd\u7684\u7528\u4e2a\u201c\u602a\u87d2\u7ffb\u8eab\u201d\uff0c\u8fde\u4eba\u5e26\u97ad\u6025\u65cb\u56de\u6765\uff0c\u671d\u7740\u77f3\u5929\u94ce\u7acb\u8eab \u4e4b\u5904\u53c8\u662f\u731b\u7684\u4e00\u97ad\u626b\u53bb\uff01 \u3000\u3000\u8fd9\u4e00\u97ad\u8fc5\u5982\u9a87\u7535\uff0c\u95f4\u4e0d\u5bb9\u53d1\uff0c\u5c31\u5728\u8fd9\u7535\u5149\u77f3\u706b\u7684\u970e\u90a3\u4e4b\u95f4\uff0c\u4f46\u89c1\u77f3\u5929\u94ce\u75be\u7684\u4e00\u584c\u8eab\uff0c\u957f \u97ad\u6ef4\u6e9c\u6e9c\u7684\uff0c\u4ece\u4ed6\u80cc\u4e0a\u5377\u8fc7\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u77f3\u5929\u94ce\u8d81\u7740\u90a3\u8499\u53e4\u6b66\u58eb\u52b2\u9053\u51cf\u5f31\uff0c\u65b0\u529b\u672a\u53d1 \u4e4b\u9645\uff0c\u731b\u559d\u4e00\u58f0\uff0c\u4e00\u624b\u626f\u8fc7\u957f\u97ad\uff0c\u90a3\u8499\u53e4\u6b66\u58eb\u672a\u53ca\u64a4\u624b\uff0c\u7adf\u662f\u8fde\u4eba\u5e26\u97ad\uff0c\u88ab\u4ed6\u6325\u5230\u7a7a\u4e2d\uff0c\u4e00 \u4eba\u626f\u7740\u97ad\u7684\u4e00\u7aef\uff0c\u4f46\u77f3\u5929\u94ce\u7ad9\u5728\u5730\u4e0a\uff0c\u8499\u53e4\u6b66\u58eb\u8eab\u5b50\u60ac\u7a7a\uff0c\u65e0\u4ece\u7740\u529b\uff0c\u77f3\u5929\u94ce\u6325\u52a8\u957f\u97ad\uff0c\u65cb \u98ce\u75be\u821e\uff0c\u90a3\u8499\u53e4\u6b66\u58eb\u4e0d\u6562\u820d\u97ad\u8df3\u4e0b\uff0c\u7ed9\u4ed6\u8f6c\u5f97\u5934\u6655\u773c\u82b1\uff0c\u5927\u547c\u5c0f\u53eb\uff01 \u3000\u3000\u4e03\u4fee\u9053\u5165\u53eb\u9053\uff1a\u201c\u5929\u94ce\uff0c\u54b1\u4eec\u5bc4\u5c45\u522b\u4eba\u7bf1\u4e0b\uff0c\u4f60\u5c82\u53ef\u5bf9\u74e6\u523a\u7684\u5df4\u56fe\u9c81\uff08\u52c7\u58eb\u5c01\u53f7\uff09\u5982\u6b64 \u65e0\u793c\uff01\u201d\u77f3\u5929\u94ce\u9053\uff1a\u201c\u597d\uff0c\u6211\u529d\u4e0d\u6765\u4f60\u4eec\uff0c\u4f60\u4eec\u4e5f\u529d\u4e0d\u56de\u6211\uff0c\u54b1\u4eec\u5404\u8d70\u5404\u8def\uff0c\u4f60\u4eec\u901f\u79bb\u6b64 \u5730\uff0c\u6211\u5c31\u9976\u4e86\u8fd9\u53ae\u4e00\u547d\u3002\u201d\u90a3\u72b6\u4f3c\u4e61\u519c\u7684\u8001\u8005\u559d\u9053\uff1a\u201c\u77f3\u5929\u94ce\u4f60\u81ea\u6043\u6b66\u529f\u8fdd\u6297\u4e3b\u547d\uff0c\u7834\u574f\u590d \u56fd\u5927\u8ba1\uff0c\u4fae\u8fb1\u5c45\u505c\u4e3b\u4eba\uff0c\u4e0d\u7ba1\u4e03\u4fee\u9053\u5144\u5982\u4f55\uff0c\u6211\u5148\u653e\u4f60\u4e0d\u8fc7\uff01\u201d\u98de\u8eab\u4e00\u6251\uff0c\u53cc\u81c2\u4e00\u4f38\uff0c\u9648\u7384 \u673a\u5728\u6811\u540e\u5077\u77a7\uff0c\u4e5f\u5413\u4e86\u4e00\u8df3\uff0c\u8fd9\u8001\u8005\u7684\u53cc\u81c2\u957f\u5f02\u5e38\uff0c\u4eba\uff0c\u8fd9\u8fd8\u4e0d\u7b97\u53e4\u602a\uff0c\u5341\u53ea\u624b\u6307\uff0c\u7adf\u5982\u9e1f \u722a\u4e00\u822c\uff0c\u6307\u7532\u957f\u8fbe\u51e0\u5bf8\uff0c\u4e4c\u9ed1\u5149\u4eae\uff0c\u53ea\u89c1\u4ed6\u58f0\u53d1\u4eba\u5230\uff0c\u5341\u6307\u957f\u7532\uff0c\u63d2\u5230\u4e86\u77f3\u5929\u94ce\u7684\u8111\u540e\uff01 \u3000\u3000\u77f3\u5929\u94ce\u201c\u54fc\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u84b2\u575a\uff0c\u591a\u5e74\u624b\u8db3\uff0c\u4f60\u5fcd\u5fc3\u4e0b\u5f97\u8fd9\u4e2a\u6bd2\u624b\uff0c\u90a3\u53ef\u522b\u602a\u5c0f\u5f1f\u65e0\u793c \u5566\uff01\u201d\u5934\u4e5f\u4e0d\u56de\uff0c\u201c\u547c\u201d\u7684\u5c31\u662f\u53cd\u624b\u4e00\u638c\uff0c\u90a3\u84b2\u575a\u8eab\u6cd5\u597d\u5feb\uff0c\u5341\u6307\u4e00\u4f38\u4e00\u7f29\uff0c\u9661\u7684\u907f\u51fa\u4e86\u4e00 \u4e08\u5f00\u5916\uff0c\u53c9\u518d\u6251\u4e0a\uff0c\u53cc\u81c2\u7b95\u5f20\uff0c\u5341\u6307\u731b\u63d2\uff0c\u771f\u5982\u4e00\u53ea\u5927\u9e1f\u4e00\u822c\u3002 \u3000\u3000\u201c\u5495\u549a\u201d\u4e00\u58f0\uff0c\u90a3\u8499\u53e4\u6b66\u58eb\u8dcc\u5012\u5730\u4e0a\uff0c\u539f\u6765\u662f\u77f3\u5929\u94ce\u8981\u5e94\u4ed8\u84b2\u575a\u7684\u653b\u52bf\uff0c\u6545\u6b64\u4e0d\u5f97\u4e0d\u628a \u90a3\u957f\u97ad\u653e\u5f00\u3002\u90a3\u8499\u53e4\u6b66\u58eb\u4e5f\u771f\u4e86\u5f97\uff0c\u8eab\u5b50\u60ac\u7a7a\uff0c\u88ab\u77f3\u5929\u94ce\u8f6c\u4e86\u8fd9\u8bb8\u4e45\uff0c\u5c45\u7136\u8dcc\u5230\u5730\u4e0a\uff0c\u4e00\u4e2a \u201c\u9ca4\u9c7c\u6253\u633a\u201d\uff0c\u4fbf\u8df3\u4e86\u8d77\u6765\uff0c\u62fe\u8d77\u957f\u97ad\uff0c\u53c8\u5411\u77f3\u5929\u94ce\u731b\u626b\uff1a\u77f3\u5929\u94ce\u53cc\u638c\u4e00\u5206\uff0c\u5de6\u638c\u4e00\u62db\uff0c\u987a \u7740\u97ad\u52bf\uff0c\u5411\u4e0a\u4e00\u62d6\uff0c\u5c06\u957f\u97ad\u5f15\u5f00\uff0c\u53f3\u638c\u4e00\u62db\u201c\u62d8\u864e\u5f52\u5c71\u201d\uff0c\u4e00\u7c98\u4e00\u5f15\uff0c\u500f\u7684\u5316\u592a\u201c\u91d1\u9e4f\u5c55 \u7fc5\u201d\uff0c\u5411\u5916\u4e00\u63a8\uff0c\u53c8\u5c06\u84b2\u575a\u7684\u653b\u52bf\u5316\u89e3\u4e86\u3002\u4f46\u89c1\u4ed6\u5f62\u5982\u864e\u6251\uff0c\u638c\u4f3c\u5954\u96f7\uff0c\u529b\u654c\u4e24\u4eba\uff0c\u4ecd\u662f\u653b \u591a\u5b88\u5c11\u3002\u4e0d\u8fc7\uff0c\u4ed6\u5bf9\u84b2\u575a\u90a3\u5341\u6307\u957f\u7532\u4e5f\u4f3c\u9887\u4e3a\u987e\u5fcc\uff0c\u4e0d\u6562\u8ba9\u5b83\u6cbe\u8eab\u5c31\u7528\u638c\u529b\u9707\u5f00\uff0c\u5982\u6b64\u4e00 \u6765\uff0c\u90a3\u8499\u53e4\u6b66\u58eb\u7684\u957f\u97ad\u5012\u6709\u4e86\u65bd\u5c55\u7684\u673a\u4f1a\uff0c\u5ffd\u800c\u5377\u5730\u731b\u626b\uff0c\u5ffd\u800c\u62e6\u8170\u75be\u5377\uff0c\u6296\u8d77\u4e00\u56e2\u97ad\u5f71\uff0c \u4e0e\u84b2\u575a\u8054\u624b\u56f4\u653b\uff0c\u987f\u65f6\u95f4\u4e0e\u77f3\u5929\u94ce\u6253\u5f97\u4e2a\u96be\u5206\u96be\u89e3\u3002 \u3000\u3000\u9648\u7384\u673a\u4e5f\u66fe\u4ece\u53d4\u4f2f\u8f88\u7684\u53e3\u4e2d\u542c\u8fc7\u84b2\u575a\u7684\u540d\u5b57\uff0c\u4ed6\u662f\u897f\u51c9\u7684\u5f5d\u4eba\uff0c\u66fe\u5728\u897f\u51c9\u5c71\u4e2d\u8ddf\u4e00\u4e2a\u5f02 \u4eba\u5b66\u6280\uff0c\u7ec3\u6210\u4e86\u4e94\u79bd\u638c\u6cd5\uff0c\u90a3\u5341\u6307\u957f\u7532\u542b\u6709\u5267\u6bd2\uff0c\u82e5\u88ab\u4ed6\u63d2\u5165\u76ae\u8089\uff0c\u5341\u4e8c\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u4fbf\u8981 \u8840\u574f\u8eab\u4ea1\uff0c\u5f53\u5e74\u5f20\u58eb\u8bda\u7f57\u81f4\u4e86\u4ed6\uff0c\u9887\u4e3a\u91cd\u7528\u3002\u4f46\u56e0\u4ed6\u6b66\u529f\u4e0d\u5927\u6b63\u6d3e\uff0c\u540d\u5934\u4e5f\u8fdc\u4e0d\u5982\u5f6d\u548c\u5c1a\u77f3 \u5929\u94ce\u7b49\u4eba\u54cd\u4eae\uff0c\u6545\u6b64\u77e5\u9053\u4ed6\u7684\u4eba\u4e0d\u591a\u3002 \u3000\u3000\u90a3\u84b2\u575a\u81ea\u6043\u6709\u72ec\u95e8\u7edd\u6280\uff0c\u5728\u5f20\u58eb\u8bda\u5e10\u4e0b\u4e4b\u65f6\uff0c\u672c\u6765\u5c31\u5bf9\u77f3\u5929\u94ce\u7b49\u4eba\u4e0d\u5927\u670d\u6c14\uff0c\u800c\u4eca\u6495\u5f00 \u4e86\u9762\uff0c\u4e00\u52a8\u4e0a\u624b\uff0c\u5b58\u5fc3\u8f83\u4e2a\u5f3a\u5f31\uff0c\u62db\u62db\u72e0\u6bd2\uff0c\u51cc\u5389\u975e\u5e38\u3002\u4f46\u89c1\u77f3\u5929\u94ce\u5728\u5341\u722a\u6251\u51fb\u3001\u957f\u97ad\u98de\u821e \u4e4b\u4e0b\uff0c\u4e1d\u6beb\u4e0d\u4ff1\uff0c\u638c\u529b\u53d1\u51fa\uff0c\u9690\u9690\u6709\u98ce\u96f7\u4e4b\u58f0\uff0c\u84b2\u575a\u8981\u4e0d\u662f\u95ea\u907f\u5f97\u5b9c\uff0c\u597d\u51e0\u6b21\u9669\u4e9b\u88ab\u4ed6\u638c\u529b \u9707\u5012\uff0c\u800c\u4e14\u4e0d\u8bba\u84b2\u575a\u8eab\u6cd5\u5982\u4f55\u602a\u5f02\uff0c\u8ff8\u62db\u6377\u5982\u9b3c\u5162\uff0c\u77f3\u5929\u94ce\u5374\u50cf\u5468\u8eab\u957f\u6ee1\u773c\u775b\uff0c\u4e0d\u8bba\u84b2\u575a\u4ece \u90a3\u4e00\u65b9\u9762\u7a81\u7136\u6251\u6765\uff0c\u4ed6\u90fd\u80fd\u4ece\u5bb9\u5316\u89e3\uff0c\u4e0d\u6559\u84b2\u575a\u8fd1\u8eab\uff0c\u84b2\u575a\u4e0d\u7531\u5f97\u5012\u5438\u4e00\u53e3\u51c9\u6c14\uff0c\u8fd9\u624d\u6697\u6697 \u4f69\u670d\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u77f3\u5929\u94ce\u5f53\u5e74\u7684\u540d\u6c14\u4ec5\u6b21\u4e8e\u5f6d\u548c\u5c1a\uff0c\u679c\u7136\u4e0d\u662f\u6d6a\u5f97\u865a\u540d\uff01\u201d \u3000\u3000\u6218\u5230\u5206\u9645\uff0c\u53ea\u542c\u5f97\u77f3\u5929\u94ce\u5927\u559d\u4e00\u58f0\uff0c\u201c\u547c\u201d\u7684\u4e00\u638c\u626b\u8fc7\uff0c\u4e00\u68f5\u677e\u6751\u5e94\u624b\u800c\u6298\uff0c\u5c31\u5728\u679d\u53f6 \u98de\u821e\uff0c\u5c18\u7802\u8ff7\u773c\u4e4b\u9645\uff0c\u731b\u7684\u817e\u8d77\u4e00\u817f\uff0c\u5c06\u90a3\u4e2a\u8499\u53e4\u6b66\u58eb\u8e22\u4e86\u4e00\u4e2a\u65a4\u6597\uff0c\u84b2\u575a\u6025\u5fd9\u8d70\u907f\uff0c\u77f3\u5929 \u94ce\u53cd\u638c\u4e00\u6325\uff0c\u638c\u950b\u642d\u4e0a\u4e86\u84b2\u575a\u7684\u80a9\u5934\uff0c\u84b2\u575a\u767b\u5bf9\u89c9\u5f97\u6709\u5982\u70d9\u8fc7\u4e00\u822c\uff0c\u706b\u8fa3\u8fa3\u4f5c\u75db\uff0c\u8e09\u8e09\u8dc4\u8dc4 \u7684\u5012\u9000\u4e86\u5341\u4f59\u6b65\uff0c\u77f3\u5929\u94ce\u6b63\u60f3\u53d1\u8bdd\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea\u89c1\u9752\u5149\u4e00\u95ea\uff0c\u4e03\u4fee\u9053\u4eba\u5df2\u62d4\u51fa\u957f \u5251\uff0c\u633a\u8eab\u653b\u4e0a\uff0c\u77f3\u5929\u94ce\u6006\u7136\u8bf4\u9053\uff0c\u201c\u4e03\u4fee\u9053\u5144\uff0c\u4f60\u4e5f\u6765\u4e86\u4e48\uff1f\u201d\u4e03\u4fee\u9053\u4eba\u9053\uff1a\u201c\u4e8b\u5df2\u5982\u6b64\uff0c \u6211\u5949\u4e86\u5c11\u4e3b\u7684\u91d1\u724c\uff0c\u53ea\u6709\u548c\u4f60\u62fc\u547d\u4e86\uff01\u201d\u5237\u7684\u4e00\u5251\uff0c\u8fde\u523a\u77f3\u5929\u94ce\u7684\u4e03\u5904\u5927\u7a74\uff01 \u3000\u3000\u77f3\u5929\u94ce\u8eab\u5f62\u4e00\u77ee\uff0c\u9a88\u6307\u4e00\u5f39\uff0c\u500f\u7684\u957f\u8eab\u6251\u8d77\uff0c\u53ea\u542c\u5f97\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u4e03\u4fee\u906d\u4eba\u7684\u957f\u5251\u7ed9 \u4ed6\u5f39\u5f00\uff0c\u518d\u5ea6\u6251\u4e0a\u7684\u84b2\u575a\uff0c\u4e5f\u7ed9\u4ed6\u7684\u638c\u529b\u9707\u9000\uff0c\u77f3\u5929\u94ce\u8fd9\u4e00\u62db\u4f7f\u5f97\u9669\u6781\uff0c\u8fde\u8eb2\u5728\u5927\u6811\u80cc\u540e\u5077 \u770b\u7684\u9648\u7384\u673a\uff0c\u4e5f\u5413\u51fa\u4e86\u4e00\u8eab\u51b7\u6c57\uff01 \u3000\u3000\u4e03\u4fee\u8fdb\u4eba\u8d5e\u9053\uff1a\u201c\u597d\u529f\u592b\uff0c\u770b\u5251\uff01\u201d\u5251\u67c4\u4e00\u6296\uff0c\u767b\u65f6\u5377\u8d77\u4e86\u4e00\u7247\u5bd2\u5149\uff0c\u5251\u82b1\u9519\u843d\uff0c\u604d\u5982 \u9ed1\u591c\u7e41\u661f\uff0c\u5343\u70b9\u4e07\u70b9\uff0c\u6d12\u843d\u4e0b\u6765\uff01\u4e03\u4fee\u9053\u4eba\u5f53\u5e74\u4e0e\u5f6d\u548c\u5c1a\u6bd5\u51cc\u865a\u4e8c\u4eba\u9f50\u540d\uff0c\u6b8a\u975e\u5e78\u81f3\u3002\u9002\u624d \u7167\u9762\u4e00\u62db\uff0c\u867d\u7136\u7ed9\u77f3\u5929\u94ce\u4fe1\u624b\u5316\u89e3\uff0c\u90a3\u53ea\u662f\u53cc\u65b9\u521d\u6b21\u8bd5\u62db\u4e4b\u6545\uff0c\u8fd9\u4e00\u4e0b\u4ed6\u5c55\u51fa\u5e73\u751f\u7edd\u6280\uff0c\u5251 \u6cd5\u786e\u662f\u60ca\u4eba\uff0c\u6bcf\u4e00\u62db\u90fd\u85cf\u6709\u4e03\u79cd\u4e0d\u540c\u7684\u53d8\u5316\uff0c\u4ed6\u7684\u9053\u53f7\u5c31\u662f\u56e0\u5251\u6cd5\u800c\u5f97\u540d\uff0c\u8fd9\u4e03\u4fee\u5251\u6cd5\u636e\u5f53 \u5e74\u5f6d\u548c\u5c1a\u7684\u8c08\u8bba\uff0c\u867d\u53ca\u4e0d\u4e0a\u6b66\u5f53\u6d3e\u725f\u72ec\u9038\u7684\u8fbe\u6469\u5251\u6cd5\u795e\u5999\uff0c\u4f46\u5947\u8be1\u4e4b\u5904\uff0c\u5374\u6709\u8fc7\u4e4b\uff0c\u9664\u4e86\u725f \u72ec\u9038\u4e4b\u5916\uff0c\u6c5f\u7322\u4e0a\u7684\u5251\u5ba2\u8981\u6570\u4ed6\u7b2c\u4e00\u4e86\u3002 \u3000\u3000\u77f3\u5929\u94ce\u53ea\u51ed\u4e00\u53cc\u8089\u638c\uff0c\u5355\u662f\u5bf9\u4ed8\u4e03\u4fee\u9053\u4eba\uff0c\u5df2\u611f\u6709\u70b9\u5403\u529b\uff0c\u4f55\u51b5\u8fd8\u6709\u4e00\u4e2a\u8eab\u6cd5\u602a\u5f02\u3001\u6377 \u5982\u9b3c\u9b45\u7684\u84b2\u575a\u52a9\u653b\uff0c\u800c\u90a3\u8499\u7384\u6b66\u58eb\uff0c\u8dcc\u4e86\u4e00\u8de4\u4e4b\u540e\uff0c\u4ed6\u76ae\u7c97\u8089\u539a\uff0c\u6ca1\u6709\u6454\u574f\uff0c\u6b47\u4e86\u4e00\u59a5\uff0c\u6296 \u52a8\u957f\u97ad\uff0c\u5c45\u7136\u53c8\u6251\u4e86\u4e0a\u6765\u3002\u77f3\u5929\u94ce\u5728\u4e09\u4e2a\u5f3a\u654c\u56f4\u653b\u4e4b\u4e0b\uff0c\u9677\u4e8e\u82e6\u6218\uff0c\u5e94\u4ed8\u6e10\u611f\u8270\u96be\u3002\u9648\u7384\u673a \u6109\u770b\u8fd9\u4e00\u573a\u6076\u6218\uff0c\u76f4\u94ed\u60ca\u5fc3\u52a8\u9b44\uff0c\u6309\u8bf4\u8fd9\u51e0\u4e2a\u4eba\u90fd\u662f\u4ed6\u7236\u4eb2\u65e7\u65e5\u7684\u540c\u50da\uff0c\u4f46\u4ed6\u4e0d\u4eec\u8c01\u662f\u8c01 \u975e\uff0c\u96be\u4e8e\u6392\u89e3\uff0c\u4e5f\u4e0d\u6562\u51fa\u58f0\u547c\u5524\u3002 \u3000\u3000\u731b\u542c\u5f97\u77f3\u5929\u94ce\u4e00\u58f0\u957f\u5578\uff0c\u51cc\u7a7a\u98de\u8d77\uff0c\u843d\u4e0b\u5730\u65f6\uff0c\u624b\u4e2d\u5df2\u591a\u4e86\u4e00\u652f\u4e8c\u5c3a\u6765\u957f\u7684\u5224\u5b98\u7b14\uff0c\u53eb \u9053\uff1a\u201c\u4e03\u4fee\u9053\u5144\uff0c\u4f60\u903c\u5f97\u5c0f\u5f1f\u548c\u4f60\u4eec\u62fc\u4e86\uff01\u201d\u58f0\u97f3\u9887\u662f\u82cd\u51c9\uff0c\u53c8\u5e26\u7740\u51e0\u5206\u6fc0\u6124\u3002 \u3000\u3000\u4f46\u89c1\u4ed6\u201c\u547c\u201d\u7684\u4e00\u638c\uff0c\u5224\u5b98\u7b14\u5728\u638c\u5e95\u659c\u7a7f\u51fa\u6765\uff0c\u4e03\u4fee\u9053\u4eba\uff0c\u957f\u5251\u4e00\u5c01\uff0c\u5224\u5b98\u7b14\u7b14\u950b\u4e00 \u8f6c\uff0c\u70b9\u5230\u4e86\u84b2\u575a\u7684\u7709\u5fc3\uff0c\u84b2\u575a\u4e00\u58f0\u602a\u53eb\uff0c\u5012\u9000\u51e0\u6b65\uff0c\u90a3\u8499\u53e4\u6b66\u58eb\u649e\u4e86\u4e0a\u6765\uff0c\u88ab\u4ed6\u7b14\u5934\u4e00\u622e\uff0c \u6b63\u4e2d\u624b\u8155\uff0c\u767b\u65f6\u8840\u6d41\u5982\u6ce8\u3002\u77f3\u5929\u94ce\u53ea\u53d1\u4e00\u62db\uff0c\u8fde\u88ad\u4e09\u4eba\uff0c\u5e76\u4f24\u4e86\u8499\u53e4\u6b66\u58eb\uff0c\u770b\u5f97\u9648\u7384\u673a\u65e2\u662f \u60ca\u5947\uff0c\u53c8\u662f\u4f69\u670d\u3002\u4e03\u4fee\u9053\u4eba\u89c1\u4ed6\u638c\u7b14\u517c\u65bd\uff0c\u66f4\u662f\u5168\u795e\u5e94\u4ed8\uff0c\u4e00\u67c4\u957f\u5251\u98d8\u5ffd\u5982\u98ce\uff0c\u6307\u4e1c\u6253\u897f\uff0c \u6307\u5357\u6253\u5317\uff0c\u65f6\u800c\u7eb5\u9ad8\uff0c\u5b9b\u5982\u9e70\u96bc\u51cc\u7a7a\uff0c\u65f6\u800c\u6251\u4f4e\uff0c\u5b9b\u5982\u8776\u821e\u82b1\u5f71\uff0c\u653b\u5982\u96f7\u9706\u75be\u53d1\uff0c\u5b88\u5982\u6c5f\u6d77 \u51dd\u5149\uff0c\u7aef\u7684\u662f\u795e\u5999\u65e0\u65b9\uff0c\u53d8\u5316\u4e07\u72b6\uff0c\u96be\u4ee5\u6069\u8bae\uff0c\uff0c\u96be\u4ee5\u6349\u6478\u3002 \u3000\u3000\u77f3\u5929\u94ce\u7684\u201c\u94c1\u638c\u795e\u7b14\u201d\u66f4\u662f\u540d\u4e0d\u865a\u4f20\uff0c\u6b66\u6797\u4e2d\u51e1\u7528\u5224\u5b98\u7b14\u7684\u4eba\u90fd\u662f\u4e24\u652f\u5408\u4f7f\uff0c\u4e00\u652f\u62e6\u51fb \u654c\u4eba\u5175\u5668\uff0c\u4e00\u652f\u70b9\u6253\u654c\u4eba\u7a74\u9053\uff0c\u77f3\u5929\u94ce\u5374\u53ea\u7528\u4e00\u652f\u3002\u4f46\u4ed6\u7684\u94c1\u638c\u5374\u80dc\u4e8e\u4efb\u4f55\u5175\u5668\uff0c\u4e00\u628a\u654c\u4eba \u9707\u6b6a\uff0c\u5224\u5b98\u7b14\u5c31\u7acb\u523b\u4e58\u9699\u800c\u8fdb\uff01\u672c\u6765\u7cbe\u4e8e\u7528\u5224\u5b98\u7b14\u70b9\u7a74\u7684\u4eba\uff0c\u5927\u90fd\u662f\u56e0\u5185\u529b\u4e0d\u5f3a\uff0c\u6240\u4ee5\u624d\u7528 \u957f\u820d\u77ed\uff0c\u5728\u6b66\u529f\u4e0a\u6bd4\u8f83\u800c\u8a00\uff0c\u5c5e\u4e8e\u9634\u67d4\u65b9\u9762\u3002\u4f46\u77f3\u5929\u94ce\u5374\u662f\u5408\u9633\u521a\u9634\u67d4\u800c\u4e3a\u4e00\uff0c\u638c\u529b\u96c4\u52b2\uff0c \u4e16\u7f55\u5176\u4f26\uff0c\u70b9\u7a74\u7684\u624b\u6cd5\uff0c\u66f4\u662f\u795e\u51fa\u9b3c\u6ca1\uff0c\u4ee5\u4e03\u4fee\u9053\u4eba\u5251\u6cd5\u7684\u5947\u5999\uff0c\u53c8\u6709\u4e24\u4e2a\u597d\u624b\u52a9\u653b\uff0c\u7adf\u7136 \u4ea6\u662f\u65e0\u5948\u4ed6\u4f55\uff0c\u6253\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u4ecd\u662f\u96be\u5206\u96be\u89e3\uff01 \u3000\u3000\u6fc0\u6218\u4e2d\u53ea\u542c\u5f97\u4e00\u7247\u65ad\u91d1\u788e\u7389\u4e4b\u58f0\uff0c\u9707\u5f97\u4eba\u8033\u9f13\u55e1\u55e1\u4f5c\u54cd\uff0c\u9648\u7384\u673a\u5e7c\u4e60\u542c\u98ce\u8fa8\u5668\u4e4b\u672f\uff0c\u8033 \u6735\u5ba1\u97f3\u6781\u662f\u7075\u654f\uff0c\u867d\u662f\u4e00\u4e32\u8fde\u7eed\u4e0d\u65ad\u7684\u54cd\u58f0\uff0c\u4ed6\u5df1\u542c\u51fa\u90a3\u662f\u53cc\u65b9\u7684\u5175\u5668\u76f8\u4ea4\uff0c\u5728\u970e\u90a3\u4e4b\u95f4\uff0c \u5c31\u78b0\u51fb\u4e86\u4e03\u4e0b\uff01\u5fc3\u4e2d\u4e0d\u9a87\u7981\u7136\uff1b\u4e03\u4fee\u9053\u4eba\u7684\u5251\u6cd5\u5728\u4e00\u62db\u4e4b\u95f4\uff0c\u80fd\u53d1\u51fa\u4e03\u79cd\u4e0d\u540c\u7684\u53d8\u5316\uff0c\u8fd9\u6b66 \u529f\u5df2\u662f\u4e0d\u53ef\u601d\u8bae\uff1b\u800c\u77f3\u5929\u94ce\u5c45\u7136\u4e5f\u5728\u540c\u4e00\u7684\u65f6\u95f4\u5185\uff0c\u8fde\u6321\u4ed6\u7684\u4e00\u62db\u4e03\u5f0f\uff0c\u800c\u4e14\u542c\u90a3\u5251\u7b14\u78b0\u51fb \u7684\u58f0\u97f3\uff0c\u4f3c\u4e4e\u8fd8\u662f\u77f3\u5929\u94ce\u5360\u4e86\u4e0a\u98ce\uff01 \u3000\u3000\u4e03\u4fee\u9053\u4eba\u8fde\u53d1\u8ffd\u9b42\u593a\u547d\u7684\u8fde\u73af\u4e09\u5251\uff0c\u77ac\u606f\u4e4b\u95f4\uff0c\u4fbf\u662f\u4e09\u62db\u4e8c\u5341\u4e00\u5f0f\uff0c\u90fd\u7ed9\u77f3\u5929\u94ce\u7684\u4e00\u652f \u795e\u7b14\u786c\u78b0\u56de\u53bb\uff0c\u5fc3\u4e2d\u6697\u6697\u53f9\u670d\u3002\u53ea\u542c\u5f97\u77f3\u5929\u94ce\u7b11\u9053\uff1a\u201c\u4e03\u4fee\u9053\u5144\uff0c\u8fd8\u4e0d\u8ba9\u5c0f\u5f1f\u8d70\u4e48\uff1f\u201d\u4e03\u4fee \u9053\u4eba\u54ac\u4e00\u54ac\u7259\uff0c\u6c89\u58f0\u559d\u9053\uff1a\u201c\u518d\u63a5\u6211\u8fd9\u4e24\u62db\uff01\u201d\u957f\u5251\u4e00\u4e2a\u76d8\u65cb\uff0c\u5de6\u53f3\u5e76\u53d1\uff0c\u5de6\u4e00\u62db\u201c\u9f99\u95e8\u6025 \u6d6a\u201d\uff0c\u53f3\u4e00\u62db\u201c\u5927\u6f20\u98de\u7802\u201d\uff0c\u8fd9\u4e24\u62db\u63a5\u8fde\u4f7f\u7528\uff0c\u4e43\u662f\u4e03\u4fee\u5251\u6cd5\u4e2d\u7684\u6740\u624b\u795e\u62db\uff0c\u4e24\u62db\u4e00\u5341\u56db \u5f0f\uff0c\u8fde\u523a\u77f3\u5929\u94ce\u7684\u5341\u56db\u9053\u5927\u7a74\uff01 \u3000\u3000\u77f3\u5929\u94ce\u53eb\u9053\uff1a\u201c\u9053\u5144\u5982\u6b64\u76f8\u8feb\uff0c\u6211\u53ea\u6709\u820d\u547d\u966a\u541b\u5b50\u4e86\uff01\u201d\u547c\u7684\u4e00\u638c\u53d1\u51fa\uff0c\u5224\u5b98\u7b14\u5f80\u4e0a\u4e00 \u5c01\uff0c\u638c\u98ce\u5251\u5f71\u4e4b\u4e2d\uff0c\u53ea\u542c\u5f97\u53ee\u53ee\u5f53\u5f53\u4e00\u4e32\u8fde\u73e0\u5bc6\u54cd\uff0c\u4e03\u4fee\u9053\u4eba\u98de\u8eab\u5012\u8dc3\uff0c\u4fef\u9996\u4e00\u671b\uff0c\u957f\u5251\u5df1 \u662f\u5d29\u4e86\u4e00\u5904\u7f3a\u53e3\uff0c\u4e03\u4fee\u9053\u4eba\u6b63\u60f3\u53d1\u8bdd\uff0c\u731b\u542c\u5f97\u84b2\u575a\u4e00\u58f0\u72de\u7b11\uff0c\u957f\u81c2\u4e00\u4f38\uff0c\u58f0\u5982\u88c2\u5e1b\uff0c\u539f\u6765\u4ed6 \u8d81\u7740\u77f3\u592d\u950b\u5168\u795e\u62b5\u5fa1\u4e03\u4fee\u9053\u4eba\u8fd9\u4e24\u62db\u6740\u624b\u4e4b\u9645\uff0c\u731d\u7136\u5077\u88ad\uff0c\u5de6\u624b\u4e94\u6307\u957f\u7532\uff0c\u5df2\u5212\u7834\u4e86\u77f3\u5929\u94ce \u80a9\u5934\u7684\u8863\u670d\uff01 \u3000\u3000\u84b2\u575a\u5927\u559c\u53eb\u9053\uff1a\u201c\u77f3\u5929\u94ce\uff0c\u660e\u5e74\u4eca\u65e5\uff0c\u4fbf\u662f\u4f60\u7684\u5fcc\u8fb0\u4e86\u3002\u4e03\u4fee\u9053\u5144\uff0c\u5e76\u80a9\u5b50\u518d\u4e0a\uff0c\u5c06\u4ed6 \u5bb0\u4e86\uff01\u201d\u4e03\u4fee\u9053\u4eba\u58f0\u97f3\u5636\u54d1\uff0c\u957f\u5251\u4e00\u6536\uff0c\u53eb\u9053\uff1a\u201c\u54b1\u4eec\u5728\u6b66\u6797\u4e2d\u603b\u7b97\u662f\u4e00\u53f7\u4eba\u7269\uff0c\u5982\u6b64\u80dc \u4ed6\uff0c\u867d\u80dc\u4e0d\u6b66\uff0c\u84b2\u575a\u8001\u5f1f\uff0c\u8d70\u5427\uff01\u201d\u8bdd\u72b9\u6765\u4e86\uff0c\u731b\u542c\u5f97\u77f3\u5929\u94ce\u4e00\u58f0\u957f\u5578\uff0c\u90a3\u5578\u58f0\u7a7f\u4e91\u88c2\u77f3\uff0c \u663e\u51fa\u4e86\u6781\u5176\u6df1\u539a\u7684\u5185\u529f\uff0c\u4f55\u5c1d\u6709\u534a\u70b9\u53d7\u4f24\u7684\u8ff9\u8c61\uff0c\u84b2\u575a\u521a\u521a\u6251\u4e0a\uff0c\u542c\u8fd9\u5578\u58f0\uff0c\u5927\u60ca\u5931\u8272\uff0c\u53ea \u542c\u5f97\u77f3\u5927\u8bd1\u5927\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u90a3\u6bd2\u722a\u5982\u4f55\u4f24\u5f97\u4e86\u6211\uff01\u201d\u53cd\u624b\u4e00\u638c\uff0c\u201c\u6f8e\u201d\u7684\u4e00\u58f0\uff0c\u5c06\u84b2\u575a\u6253\u51fa \u4e86\u4e09\u4e08\u5f00\u5916\uff0c\u90a3\u8499\u53e4\u6b66\u58eb\u4e0d\u77e5\u6b7b\u6d3b\uff0c\u6b63\u5728\u6b64\u9645\uff0c\u970d\u5730\u4e00\u97ad\u626b\u6765\uff0c\u77f3\u5929\u94ce\u53eb\u9053\uff1a\u201c\u5ff5\u5728\u65e7\u65e5\u540c \u50da\u60c5\u4efd\uff0c\u6211\u653e\u84b2\u575a\u56de\u53bb\u3002\u8fd9\u53ae\u53ef\u4e0d\u8bb8\u8d70\u5566\uff1a\u201c\u8bdd\u672a\u8bf4\u5b8c\uff0c\u4f46\u89c1\u8499\u53e4\u6b66\u58eb\u90a3\u6761\u957f\u97ad\u7ed9\u4ed6\u5288\u624b\u593a \u8fc7\uff0c\u63a5\u7740\u5bd2\u5149\u4e00\u95ea\uff0c\u201c\u6ce2\u201d\u7684\u4e00\u58f0\uff0c\u5224\u5b98\u7b14\u5f80\u524d\u4e00\u9001\u76f4\u63d2\u5165\u4e86\u90a3\u8499\u53e4\u6b66\u58eb\u7684\u80f8\u819b\uff01 \u3000\u3000\u4e03\u4fee\u9053\u4eba\u5927\u53eb\u9053\uff1a\u201c\u7f62\u4e86\uff0c\u7f62\u4e86\uff01\u4f60\u6740\u4e86\u6b64\u4eba\uff0c\u5c11\u4e3b\u5fc3\u610f\u66f4\u96be\u633d\u56de\uff0c\u54b1\u4eec\u5144\u5f1f\u4e4b\u60c5\uff0c\u4eca \u65e5\u65ad\u7edd\uff01\u201d\u80cc\u8d77\u84b2\u575a\uff0c\u5982\u98de\u4e0b\u5c71\u3002\u77f3\u5929\u94ce\u53f9\u4e86\u4e00\u53e3\u957f\u6c14\uff0c\u9eef\u7136\u81ea\u8bed\uff1a\u201c\u4e8b\u5df2\u5982\u6b64\uff0c\u592b\u590d\u4f55 \u8a00\uff0c\u4e5f\u53ea\u597d\u5404\u884c\u5176\u662f\u4e86\uff01\u201d \u3000\u3000\u6076\u6218\u6536\u573a\uff0c\u8352\u5c71\u53c8\u5f52\u9759\u5bc2\u3002\u9648\u7384\u673a\u4e00\u9897\u5fc3\u5140\u662f\u8df3\u4e2a\u4e0d\u4f11\u3002\u6708\u5149\u4e0b\u53ea\u89c1\u77f3\u5929\u91ca\u51dd\u671b\u5c71\u5934\uff0c \u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u8c01\u60f3\u5f97\u5230\u6211\u8fd9\u4e00\u751f\u8fd8\u4f1a\u8d70\u8fdb\u4e91\u5bb6\uff0c\u5440\uff0c\u6211\u53bb\u5462\uff0c\u8fd8\u662f\u4e0d\u53bb\uff1f\u201d\u9648\u7384\u673a\u542c\u4e86\uff0c\u5927 \u4e3a\u5947\u602a\u3002\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u9002\u624d\u4ed6\u820d\u6b7b\u5fd8\u751f\uff0c\u4e0d\u8bb8\u522b\u4eba\u963b\u62e6\uff0c\u5982\u4eca\u5f3a\u654c\u5df2\u9000\uff0c\u4f55\u6545\u4ed6\u53c8\u8e0c\u8e87\uff1f\u201d \u3000\u3000------------- \u3000\u3000\u91cd\u5e86\u96ea\u513f \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u8fd8\u5251\u5947\u60c5\u5f55\u300b\u2014\u2014\u7b2c\u56db\u56de\u3000\u6df1\u9662\u6885\u82b1 \u6881\u7fbd\u751f\u300a\u8fd8\u5251\u5947\u60c5\u5f55\u300b \u7b2c\u56db\u56de\u3000\u6df1\u9662\u6885\u82b1 \u3000\u3000\u9648\u7384\u673a\u5728\u7e41\u679d\u5bc6\u53f6\u4e2d\u5077\u77a7\u51fa\u6765\uff0c\u4f46\u89c1\u77f3\u5929\u94ce\u795e\u8272\u5947\u5f02\uff0c\u597d\u8c61\u5341\u5206\u9893\u4e27\uff0c\u7adf\u662f\u6ca1\u6709\u4e1d\u6beb\u80dc \u5229\u7684\u559c\u60a6\u3002\u6708\u8272\u5982\u94f6\uff0c\u4ed6\u5728\u6708\u5149\u4e0b\u8fce\u98ce\u5446\u7acb\uff0c\u597d\u534a\u664c\u4e0d\u8a00\u4e0d\u52a8\uff0c\u5b9b\u5982\u4e00\u5c0a\u5927\u7406\u77f3\u7684\u96d5\u50cf\u3002\u9648 \u7384\u673a\u4e0d\u7981\u6697\u6697\u6253\u4e86\u4e00\u4e2a\u5bd2\u6218\uff0c\u4f46\u89c9\u77f3\u5927\u950b\u6b64\u9645\u7684\u795e\u60c5\uff0c\u6bd4\u9002\u624d\u6076\u6218\u4e4b\u65f6\uff0c\u66f4\u4e3a\u53ef\u6015\uff01 \u3000\u3000\u8fc7\u4e86\u534a\u664c\uff0c\u53ea\u542c\u5f97\u77f3\u5929\u94ce\u53c8\u662f\u4e00\u58f0\u957f\u53f9\uff0c\u8f7b\u58f0\u5ff5\u9053\uff1a\u201c\u5eff\u5e74\u6e56\u6d77\u98d8\u84ec\u540e\uff0c\u51b7\u843d\u6885\u82b1\u5317\u56fd \u6625\u201d\u6478\u51fa\u4e00\u5b97\u7269\u4e8b\uff0c\u8fce\u98ce\u4e00\u5c55\uff0c\u9648\u7384\u673a\u4f9d\u7a00\u770b\u51fa\uff0c\u90a3\u662f\u4e00\u4e2a\u7ee3\u8377\u5305\uff0c\u53ea\u542c\u5f97\u77f3\u5929\u94ce\u53c8\u7ee7\u7eed\u5ff5 \u9053\uff1a\u201c\u8377\u5305\u7a7a\u7ee3\u9e33\u9e2f\u5b57\uff0c\u7eff\u53f6\u6210\u9634\u5bf9\u65e7\u4eba\uff01\u201d\u9648\u7384\u673a\u5fc3\u5934\u4e00\u9707\uff0c\u7136\u4e0d\u89e3\u8bd7\u4e2d\u4e4b\u610f\uff0c\u542c\u6765\u5374\u662f \u9690\u6709\u65e0\u9650\u5e7d\u60c5\u3002\u96be\u9053\u8fd9\u4f4d\u9002\u624d\u8fd8\u662f\u90a3\u7b49\u8c6a\u6c14\u96c4\u98ce\u3001\u540d\u9707\u5929\u4e0b\u7684\u5927\u4fa0\uff0c\u5374\u6709\u4ec0\u4e48\u96be\u4ee5\u8a00\u8bf4\u7684\u54c0 \u4f24\uff1f \u3000\u3000\u6708\u5149\u4e0b\u53ea\u89c1\u77f3\u5929\u94ce\u5c06\u8377\u5305\u85cf\u8d77\uff0c\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u4e16\u4e8b\u6ca7\u6851\uff0c\u4e91\u70df\u8fc7\u773c\uff0c\u8fd8\u60f3\u8fd9\u4e9b\u524d\u65e5\u5f80 \u4e8b\u505a\u751a\uff1f\u201d\u8eab\u5f62\u4e00\u6643\uff0c\u503e\u523b\u4e4b\u95f4\uff0c\u6ca1\u4e86\u8e2a\u8ff9\uff0c\u4e5f\u4e0d\u77e5\u4ed6\u662f\u4e0a\u4e91\u5bb6\u8fd8\u662f\u5f80\u56de\u5934\u8def\uff1f \u3000\u3000\u9648\u7384\u673a\u4ece\u6811\u540e\u8d70\u51fa\uff0c\u6708\u4eae\u5df2\u8fc7\u4e2d\u5929\u3002\u9664\u4e86\u90a3\u4e2a\u5df2\u65ad\u4e86\u6c14\u7684\u8499\u53e4\u6b66\u58eb\u5916\uff0c\u6781\u76ee\u56db\u671b\uff0c\u9752\u65e0 \u4eba\u5f71\uff0c\u9759\u5f97\u6015\u4eba\u3002\u9648\u7384\u673a\u53c8\u60f3\u8d77\u4e86\u4e91\u7d20\u7d20\u6765\uff0c\u8fd9\u4e2a\u65f6\u4efd\uff0c\u60f3\u6765\u5979\u65e9\u5df2\u7761\u4e86\u3002\u5979\u53ef\u77e5\u8fd9\u5c71\u4e0b\u66fe \u6709\u4e00\u573a\u6076\u6218\uff1f\u8fd9\u65f6\u9648\u7384\u673a\u7684\u5fc3\u4e2d\uff0c\u9664\u4e86\u60f3\u53bb\u5077\u4f1a\u4e91\u7d20\u7d20\uff0c\u6697\u63a2\u4e0a\u5b98\u5929\u91ce\u4e4b\u5916\uff0c\u8fd8\u5145\u6ee1\u4e86\u597d\u5947 \u7684\u5fc3\u60c5\uff0c\u660e\u77e5\u5371\u673a\u56db\u4f0f\uff0c\u4e5f\u60f3\u53bb\u770b\u77f3\u5929\u94ce\u662f\u5426\u524d\u5f80\u4e91\u5bb6\uff0c\u800c\u4ed6\u627e\u4e91\u821e\u9633\u53c8\u662f\u4e3a\u4e86\u4f55\u4e8b\uff1f \u3000\u3000\u4e0d\u6d88\u534a\u4e2a\u65f6\u8fb0\uff0c\u9648\u7384\u673a\u53c8\u5230\u4e86\u4e91\u5bb6\u95e8\u5916\uff0c\u542c\u4e86\u4e00\u542c\uff0c\u91cc\u9762\u6beb\u65e0\u58f0\u606f\uff0c\u4e91\u821e\u9633\u4f3c\u4e4e\u8fd8\u6ca1\u6709 \u56de\u6765\u3002\u9648\u7384\u673a\u7565\u4e00\u7574\u8e87\uff0c\u66ae\u5730\u628a\u5fc3\u4e00\u6a2a\uff0c\u811a\u5c16\u70b9\u5730\uff0c\u4f7f\u4e2a\u201c\u4e00\u9e64\u51b0\u5929\u201d\u4e4b\u52bf\uff0c\u98de\u8d8a\u8fc7\u90a3\u7247\u77ed \u5899\u3002 \u3000\u3000\u5ead\u9662\u91cc\u6885\u679d\u63a9\u6708\uff0c\u82b1\u9999\u88ad\u4eba\uff0c\u8fd8\u662f\u6628\u665a\u7684\u60c5\u666f\uff0c\u53ea\u662f\u4e0d\u89c1\u6628\u665a\u7684\u4eba\u3002\u9648\u7384\u673a\u5fc3\u5934\u6005\u60c6\uff0c \u4ed6\u4e58\u7740\u4e00\u80a1\u50bb\u52b2\u800c\u6765\uff0c\u8fd9\u65f6\u5374\u6ca1\u4e86\u4e3b\u610f\uff0c\u60f3\u9053\uff1a\u96be\u9053\u6211\u5728\u8fd9\u6837\u7684\u6df1\u591c\uff0c\u76f4\u95ef\u4eba\u5bb6\u7684\u95fa\u9601\u4e48\uff1f \u5440\uff0c\u7d20\u7d20\u5440\u7d20\u7d20\uff0c\u4f46\u613f\u795e\u4ed9\u80fd\u591f\u6258\u68a6\u7ed9\u4f60\uff0c\u53eb\u4f60\u77e5\u9053\u6211\u6765\u3002\u80e1\u601d\u4e71\u60f3\uff0c\u81ea\u5df1\u4e5f\u4e0d\u7981\u54d1\u7136\u5931 \u7b11\uff0c\u60f3\u90a3\u5929\u4e0a\u7eb5\u6709\u795e\u4ed9\u4e5f\u672a\u5fc5\u80fd\u77e5\u6089\u4ed6\u7684\u5fc3\u4e8b\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u4e00\u58f0\u8f7b\u8f7b\u7684\u53f9\u606f\uff0c\u8fdc\u8fdc\u98d8\u6765\uff0c\u5e7d\u6028\u51c4\u51c9\uff0c\u6709\u5982\u6df1\u5bb5\u9b3c\u54ed\uff0c\u4ee4\u4eba\u4e0d\u5bd2\u800c\u6817\uff0c\u8fd9\u4e0d\u50cf \u662f\u4e91\u7d20\u7d20\uff0c\u4e5f\u4e0d\u50cf\u662f\u77f3\u5929\u94ce\u3002\u9648\u7384\u673a\u6025\u5fd9\u8eb2\u5165\u4e66\u623f\uff0c\u8fd8\u672a\u85cf\u597d\uff0c\u53ea\u89c1\u7409\u7483\u7a97\u5916\u4eba\u5f71\u4e00\u95fd\uff0c\u4e00 \u4e2a\u4eba\u4ece\u4e1c\u9762\u7684\u77ed\u57a3\u98de\u8eab\u800c\u5165\uff0c\u4e1c\u9762\u77ed\u57a3\u4e43\u662f\u63a5\u8fde\u5185\u8fdb\u4e0a\u623f\u7684\u3002\u8fd9\u4eba\u663e\u7136\u662f\u5728\u4e91\u5bb6\u91cc\u8fb9\u51fa\u6765\u800c \u4e0d\u662f\u4ece\u5916\u95f4\u5077\u5165\u7684\u4e86\u3002\u9648\u7384\u673a\u6014\u4e86\u4e00\u6014\uff0c\u8d34\u7740\u7a97\u683c\uff0c\u5b9a\u775b\u770b\u65f6\u5413\u5f97\u5446\u4e86\uff01 \u3000\u3000\u53ea\u89c1\u90a3\u68f5\u8001\u6885\u6811\u4e0b\uff0c\u7acb\u7740\u4e00\u4e2a\u957f\u53d1\u62ab\u80a9\u3001\u9762\u5bb9\u82cd\u767d\u7684\u4e2d\u5e74\u5987\u4eba\uff0c\u4fa7\u7740\u534a\u8eab\uff0c\u51dd\u7738\u5bf9\u6708\uff0c \u90a3\u795e\u6c14\u4f3c\u662f\u4e00\u4e2a\u5931\u5ba0\u7684\u5c11\u5987\uff0c\u66f4\u4f3c\u4e00\u4e2a\u542b\u6068\u7684\u5e7d\u7075\u3002\u518d\u770b\u6e05\u695a\u65f6\uff0c\u53ea\u89c1\u5979\u7684\u5546\u5bb9\u8f6e\u5ed3\uff0c\u7adf\u662f \u6709\u51e0\u5206\u4e0e\u4e91\u7d20\u7d20\u76f8\u4f3c\uff0c\u60f3\u6765\u9664\u6389\u662f\u4e91\u821e\u9633\u7684\u592b\u4eba\uff0c\u4e0d\u53ef\u80fd\u662f\u65c1\u4eba\u4e86\uff01 \u3000\u3000\u9648\u7384\u673a\u6253\u4e86\u4e00\u4e2a\u5bd2\u6218\uff0c\u4f46\u89c9\u6709\u65e0\u6570\u7591\u56e2\uff0c\u76d8\u585e\u80f8\u4e2d\uff0c\u767e\u601d\u83ab\u89e3\u3002\u4e91\u821e\u9633\u7684\u592b\u4eba\u5728\u81ea\u5df1\u7684 \u5bb6\u4e2d\uff0c\u4e3a\u4f55\u8981\u8fd9\u6837\u5077\u5077\u6478\u6478\u7684\u903e\u57a3\u800c\u5165\uff1f\u90a3\u91cc\u50cf\u662f\u4e00\u5bb6\u7684\u4e3b\u5987\uff0c\u5012\u50cf\u662f\u6c5f\u6e56\u4e0a\u6df1\u5bb5\u63a2\u79d8\u7684\u591c \u884c\u4eba\u4e86\u3002\u66f4\u5947\u602a\u7684\u662f\uff1a\u5728\u4e91\u821e\u9633\u7236\u5973\u7684\u53e3\u4e2d\uff0c\u5979\u4e43\u662f\u4e00\u4e2a\u957f\u5e74\u5367\u75c5\u7684\u5987\u4eba\uff0c\u8fde\u5927\u95e8\u4e5f\u61d2\u5f97\u51fa \u7684\uff0c\u7136\u800c\u5979\u5374\u5728\u8fd9\u4e2a\u591c\u6df1\u5165\u9759\u7684\u65f6\u5019\u51fa\u6765\uff0c\u96be\u9053\u53ea\u662f\u4e3a\u4e86\u89c2\u82b1\u8d4f\u6708\uff1f\u800c\u4e14\u770b\u5979\u903e\u57a3\u800c\u5165\u7684\u77eb \u6377\u8eab\u624b\uff0c\u53c8\u90a3\u6709\u534a\u70b9\u75c5\u5bb9\uff1f \u3000\u3000\u500f\u7136\u95f4\u4f46\u89c1\u6709\u51e0\u6735\u6885\u82b1\u98d8\u843d\uff0c\u4e00\u6761\u4eba\u5f71\u4ece\u6811\u4e0a\u8dc3\u4e0b\uff0c\u7aef\u7684\u4f3c\u4e00\u53f6\u98d8\u5815\uff0c\u843d\u5904\u65e0\u58f0\uff0c\u8fde\u9648 \u7384\u673a\u4e5f\u542c\u4e0d\u51fa\u4ed6\u662f\u4f55\u65f6\u8fdb\u6765\u7684\u3002\u8fd9\u4eba\u662f\u77f3\u5929\u94ce\u3002 \u3000\u3000\u4e91\u592b\u4eba\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u5929\u94ce\uff0c\u679c\u7136\u662f\u4f60\uff1f\u201d\u77f3\u5929\u94ce\u9053\uff1a\u201c\u5b9d\u73e0\uff0c\u4f60\u5728\u8fd9\u91cc\u7b49\u6211\uff1f\u201d\u867d\u7136\u5c3d \u91cf\u538b\u4f4e\u58f0\u97f3\uff0c\u8fd8\u662f\u63a9\u4e0d\u4f4f\u90a3\u5fc3\u4e2d\u7684\u6fc0\u52a8\u4e4b\u60c5\u3002\u4e91\u592b\u4eba\u9053\uff1a\u201c\u55ef\uff0c\u6211\u542c\u5230\u5c71\u4e0b\u6253\u6597\u7684\u58f0\u606f\uff0c\u80fd \u51fb\u8d25\u4e03\u4fee\u9053\u4eba\u90a3\u4e00\u62db\u4e03\u5f0f\u5251\u6cd5\u7684\uff0c\u5f53\u4eca\u4e4b\u4e16\uff0c\u9664\u4e86\u821e\u9633\u548c\u4f60\uff0c\u6050\u6015\u4e5f\u4e0d\u4f1a\u6709\u7b2c\u4e09\u4e2a\u4eba\u4e86\u3002\u201d \u9648\u7384\u673a\u5403\u4e86\u4e00\u60ca\uff1a\u8fd9\u4e91\u592b\u4eba\u771f\u597d\u8033\u529b\uff0c\u8fdc\u8fdc\u7684\u542c\u5175\u5203\u78b0\u51fb\u4e4b\u58f0\uff0c\u5c31\u5206\u8fa8\u5f97\u51fa\u662f\u4ec0\u4e48\u9ad8\u624b\uff0c\u542c \u5f97\u51fa\u8c01\u80dc\u8c01\u8d25\uff0c\u8fd9\u4efd\u529f\u592b\u6bd4\u81ea\u5df1\u7684\u201c\u542c\u98ce\u8fa8\u5668\u201d\u4e4b\u672f\uff0c\u9ad8\u660e\u5f97\u4e0d\u53ef\u4ee5\u9053\u91cc\u8ba1\u4e86\u3002 \u3000\u3000\u77f3\u5929\u94ce\u6006\u7136\u4e00\u7b11\u9053\uff1a\u201c\u591a\u627f\u5938\u8d5e\u3002\u55ef\uff0c\u539f\u6765\u821e\u9633\u5144\u4e0d\u5728\u5bb6\u4e2d\u3002\u201d\u4e91\u592b\u4eba\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u78b0 \u89c1\u4ed6\uff1f\u201d\u77f3\u5929\u94ce\u9053\uff1a\u201c\u6211\u6b63\u662f\u8981\u6765\u627e\u4ed6\u3002\u6211\u731c\uff0c\u82e5\u662f\u4ed6\u5728\u5bb6\u4e2d\uff0c\u4ed6\u4e5f\u65e9\u8be5\u542c\u5c71\u662f\u6211\u6765\u5566\u3002\u201d \u4e91\u592b\u4eba\u9053\uff1a\u201c\u4ed6\u5348\u591c\u65f6\u4efd\uff0c\u5c31\u4e0b\u5c71\u53bb\u4e86\u3002\u4ec0\u4e48\u4e8b\u60c5\uff0c\u8fde\u6211\u4e5f\u6ca1\u6709\u544a\u8bc9\u3002\u6211\u8fd8\u4ee5\u4e3a\u4ed6\u662f\u77e5\u9053\u4f60 \u4e0a\u5c71\uff0c\u4e0b\u53bb\u8fce\u63a5\u5462\u3002\u201d\u77f3\u5929\u94ce\u8fdf\u7591\u534a\u664c\uff0c\u82e6\u7b11\u8bf4\u9053\uff1a\u201c\u821e\u9633\u5144\u65e2\u7136\u4e0d\u5728\uff0c\u6211\u4e0d\u4fbf\u5728\u6b64\u4e45\u7559\uff0c \u8fd8\u662f\u660e\u65e5\u518d\u6765\u62dc\u8bbf\u5427\u3002\u201d\u8bdd\u662f\u8bf4\u4e86\uff0c\u4f46\u5374\u6ca1\u6709\u79fb\u52a8\u811a\u6b65\u3002 \u3000\u3000\u4e91\u592b\u4eba\u5ffd\u5730\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u65e2\u7136\u6765\u4e86\uff0c\u4f55\u5fc5\u5c31\u8d70\uff1f\u54b1\u4eec\u4e5f\u90fd\u8001\u5566\uff0c\u96be\u9053\u8fd8\u7528\u907f\u5acc\u3002\u4f60 \u8fd9\u4e00\u8d70\uff0c\u53ea\u6015\u8fd9\u4e00\u751f\u518d\u6ca1\u6709\u5355\u72ec\u89c1\u9762\u7684\u673a\u4f1a\u5566\uff01\u201d\u58f0\u97f3\u5fae\u7ec6\uff0c\u4f4e\u4e86\u5934\u4e0d\u6562\u548c\u77f3\u5929\u94ce\u7684\u773c\u5149\u76f8 \u89e6\uff0c\u597d\u50cf\u4e0d\u662f\u5bf9\u4ed6\u8bf4\u8bdd\uff0c\u800c\u662f\u81ea\u8a00\u81ea\u8bed\u4e00\u822c\u3002 \u3000\u3000\u77f3\u5929\u94ce\u5fc3\u60c5\u6fc0\u8361\uff0c\u4e0d\u81ea\u7981\u7684\u8fc8\u524d\u4e00\u6b65\uff0c\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u5b9d\u73e0\uff0c\u4f60\u2014\u2014\u201d\u4e91\u592b\u4eba\u8f7b\u8f7b\u4e00 \u201c\u5618\u201d\uff0c\u9053\uff1a\u201c\u5c0f\u58f0\u70b9\u513f\uff0c\u522b\u60ca\u9192\u4e86\u7d20\u7d20\uff01\u201d\u77f3\u5929\u94ce\u9762\u4e0a\u4e00\u7ea2\uff0c\u9000\u56de\u539f\u5904\uff0c\u501a\u7740\u6885\u6811\u9053\uff1a \u201c\u7d20\u7d20\uff1f\u201d\u4e91\u592b\u4eba\u9053\uff1a\u201c\u7d20\u7d20\u662f\u6211\u7684\u5973\u513f\uff0c\u4eca\u5e74\u5341\u516b\u5c81\u5566\u3002\u201d\u77f3\u5929\u94ce\u6e2d\u7136\u53f9\u9053\uff1a\u201c\u5341\u516b\u5e74 \u5566\uff0c\u5440\uff0c\u65e5\u5b50\u8fc7\u5f97\u771f\u5feb\uff0c\u54b1\u4eec\u7684\u5b50\u5973\u4e5f\u90fd\u957f\u5927\u5566\uff01\u201d\u4e91\u592b\u4eba\u9053\uff1a\u201c\u4f60\u662f\u51e0\u65f6\u7ed3\u5a5a\u7684\uff1f\u5c0a\u592b\u4eba \u4f55\u4ee5\u4e0d\u6765\uff1f\u201d\u77f3\u5929\u94c3\u9053\uff1a\u201c\u6211\u542c\u5230\u4f60\u548c\u821e\u9633\u5144\u7684\u559c\u8baf\uff0c\u90a3\u65f6\u6211\u6b63\u5728\u8499\u53e4\uff0c\u75c5\u4e86\u4e00\u573a\u3002\u75c5\u4e2d\u591a \u5f97\u5979\u670d\u4f8d\uff0c\u6211\u672c\u6765\u65e0\u6b64\u5ff5\u5934\uff0c\u4f46\u60f3\u5230\u6d41\u4ea1\u5728\u5916\uff0c\u603b\u5f97\u4e3a\u7956\u5b97\u7559\u4e0b\u4e00\u70b9\u8840\u8109\uff0c\u7b2c\u4e8c\u5e74\u4e5f\u5c31\u9a6c\u9a6c \u864e\u864e\u7684\u7ed3\u4e86\u5a5a\u5566\uff0c\u5185\u5b50\u4e0d\u61c2\u6b66\u529f\uff0c\u6211\u5728\u9003\u51fa\u74e6\u523a\u4e4b\u524d\uff0c\u5df2\u5c06\u5979\u4eec\u6bcd\u5b50\u9001\u56de\u5c71\u897f\u539f\u7c4d\u4e86\u3002\u55ef\uff0c \u5b9d\u73e0\uff0c\u4f60\u4e0d\u602a\u6211\uff1f\u201d\u4e91\u592b\u4eba\u9053\uff1a\u201c\u6211\u600e\u80fd\u602a\u4f60\u3002\u90a3\u4e48\u4ee4\u90ce\u4e5f\u957f\u5927\u5566\uff1f\u201d\u9648\u7384\u673a\u65e0\u610f\u4e2d\u5077\u542c\u4e86 \u4ed6\u4eec\u7684\u8c08\u8bdd\uff0c\u542c\u662f\u95f2\u8bdd\u5bb6\u5e38\uff0c\u5374\u5206\u660e\u85cf\u6709\u65e0\u9650\u9690\u60c5\uff01 \u3000\u3000\u7591\u56e2\u585e\u80f8\uff0c\u8d8a\u53d1\u91cd\u4e86\u3002\u9648\u7384\u673a\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u4e91\u592b\u4eba\u4e43\u662f\u5973\u4e2d\u8c6a\u6770\uff0c\u5f53\u5e74\u82e5\u4e0d\u662f\u5979\u5fc3\u4e2d \u60c5\u613f\u7684\u8bdd\uff0c\u8c01\u80fd\u903c\u5f97\u5979\u5ac1\u4e91\u821e\u9633\uff1f\u65e2\u5df2\u5ac1\u4e86\uff0c\u53c8\u4f55\u4ee5\u597d\u4f3c\u5bf9\u77f3\u5929\u94ce\u82e5\u6709\u60c5\u612b\uff1f\u201d\u60f3\u8d77\u8fd9\u4e24\u4f4d \u5e76\u9a7e\u9f50\u540d\u4e00\u65f6\u745c\u4eae\u7684\u6b66\u6797\u9ad8\u624b\uff0c\u5176\u95f4\u5374\u6709\u8fd9\u4e48\u4e00\u6bb5\u4e0d\u53ef\u544a\u4eba\u7684\u9690\u79d8\uff0c\u4e0d\u77e5\u4e91\u821e\u9633\u53ef\u5426\u77e5\u9053\u4ed6 \u7684\u59bb\u5b50\u5fc3\u4e2d\u53e6\u6709\u60c5\u4eba\uff1f\u4f46\u89c9\u8fd9\u91cc\u9762\u5305\u542b\u7740\u6781\u5927\u7684\u5371\u673a\uff0c\u9648\u7384\u673a\u7981\u4e0d\u4f4f\u4e3a\u4ed6\u4eec\u62c5\u5fc3\uff0c\u5fd8\u8bb0\u4e86\u81ea \u5df1\u4e5f\u662f\u7f6e\u8eab\u5b50\u6781\u5371\u9669\u4e4b\u5730\u3002 \u3000\u3000\u53ea\u542c\u5f97\u77f3\u5929\u94ce\u8bf4\u9053\uff1a\u201c\u6211\u90a3\u4e2a\u5b69\u5b50\u4eca\u5e74\u4e5f\u6709\u5341\u516d\u5c81\u5566\uff0c\u540d\u53eb\u77f3\u82f1\uff0c\u813e\u6c14\u66b4\u71e5\u5f97\u5f88\uff0c\u65f6\u5e38 \u7ed9\u6211\u60f9\u4e8b\uff0c\u4ed6\u7684\u5c0f\u53cb\u4eec\u53eb\u4ed6\u505a\u8f70\u5929\u96f7\u3002\u201d\u4e91\u592b\u4eba\u7b11\u9053\uff1a\u201c\u6211\u7684\u7d20\u7d20\u5012\u8fd8\u6587\u9759\uff0c\u53ea\u662f\u6709\u65f6\u4e5f\u4f1a \u6dd8\u6c14\u3002\u6027\u60c5\u5374\u662f\u51fa\u5979\u7236\u4eb2\uff0c\u60f3\u4e86\u5c31\u505a\uff0c\u7eb5\u7136\u9519\u4e86\uff0c\u4ea6\u4e0d\u53cd\u6094\u3002\u201d\u77f3\u5927\u950b\u9053\uff1a\u201c\u55ef\uff0c\u4f60\u6bd4\u6211\u6709 \u798f\u6c14\u5f97\u591a\u3002\u4e08\u592b\u82f1\u96c4\uff0c\u5973\u513f\u8d24\u6dd1\uff0c\u8fd9\u91cc\u53c8\u5e03\u7f6e\u5f97\u795e\u4ed9\u6d1e\u5e9c\u4e00\u822c\uff0c\u540d\u5c71\u80dc\u666f\uff0c\u5408\u85c9\u53cc\u4fee\uff0c\u4eba\u751f \u81f3\u6b64\uff0c\u592b\u590d\u4f55\u6c42\u3002\u6211\u6765\u4e86\u8fd9\u4e00\u8d9f\uff0c\u4e5f\u653e\u4e86\u5fc3\u4e86\u3002\u201d\u4e00\u62ac\u5934\uff0c\u4f46\u89c1\u4e91\u592b\u4eba\u7b11\u5bb9\u672a\u655b\uff0c\u773c\u89d2\u5374\u5df2 \u6302\u7740\u6676\u83b9\u7684\u6cea\u73e0\u3002 \u3000\u3000\u77f3\u5929\u94ce\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u821e\u9633\u5144\u96be\u9053\u5bf9\u4f60\u4e0d\u597d\uff1f\u201d\u4e91\u592b\u4eba\u62bd\u54bd\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u592a\u597d\u4e86\uff0c\u5929 \u5929\u8feb\u6211\u5403\u836f\u3002\u201d\u77f3\u5929\u94ce\u5947\u9053\uff1a\u201c\u8feb\u4f60\u5403\u836f\uff1f\u4f60\u4ec0\u4e48\u75c5\uff1f\u201d\u4e91\u592b\u4eba\u9053\uff1a\u201c\u6211\u5ac1\u4ed6\u4e4b\u540e\uff0c\u5934\u51e0\u5e74 \u8fd8\u597d\uff0c\u8fd9\u5341\u51e0\u5e74\u6765\uff0c\u5fc3\u75db\u65f6\u53d1\u65f6\u6b62\uff0c\u6ca1\u6709\u4e00\u4e2a\u4eba\u53ef\u4e0e\u8c08\u8bf4\uff0c\u5916\u95f4\u6625\u53bb\u6625\u6765\uff0c\u82b1\u5f00\u82b1\u843d\uff0c\u6211\u90fd \u65e0\u5fc3\u987e\u95ee\u3002\u4eca\u5e74\u8fd8\u662f\u6211\u7b2c\u4e00\u6b21\u51fa\u8fd9\u5ead\u9662\u6765\u5462\uff01\u201d\u77f3\u5929\u94ce\u5446\u4e86\u534a\u664c\u9053\uff1a\u201c\u5374\u662f\u4e3a\u4f55\uff1f\u201d\u4e91\u592b\u4eba \u9053\uff1a\u201c\u5440\uff0c\u6211\u540e\u6765\u624d\u77e5\u9053\u821e\u9633\u5e76\u4e0d\u662f\u771f\u7684\u4e3a\u4e86\u6b22\u559c\u6211\u624d\u5a36\u6211\u7684\u3002\u201d\u77f3\u5929\u94ce\u9053\uff1a\u201c\u662f\u4e0d\u662f\u4f60\u5927 \u591a\u7591\u4e86\uff1f\u201d\u4e91\u592b\u4eba\u9053\uff1a\u201c\u4ed6\uff0c\u4ed6\uff0c\u4ed6\u8fd9\u5341\u591a\u5e74\u6765\u4e00\u76f4\u601d\u5ff5\u4ed6\u7684\u524d\u59bb\u3002\u4ed6\u524d\u59bb\u7684\u5c0f\u540d\u4e2d\u6709\u4e00\u4e2a \u6885\u5b57\uff0c\u8fd9\u6ee1\u9662\u6885\u82b1\uff0c\u5c31\u662f\u4ed6\u4e3a\u4e86\u5fc6\u5ff5\u524d\u59bb\u800c\u683d\u690d\u7684\u3002\u201d\u77f3\u5929\u94ce\u9053\uff1a\u201c\u821e\u9633\u7684\u524d\u59bb\u5728\u957f\u6c5f\u6218\u6b7b \u4e5f\u6709\u4e8c\u5341\u5e74\u5566\uff0c\u8fd9\u4e48\u8bf4\u6765\uff0c\u6211\u5012\u94a6\u656c\u821e\u9633\u4e86\u3002\u201d\u4e91\u592b\u4eba\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u77f3\u5929\u94ce\u5f3a\u7b11\u9053\uff1a\u201c\u82e5 \u662f\u4ed6\u601d\u5ff5\u522b\u4eba\uff0c\u5c31\u96be\u602a\u4f60\u6c14\u607c\u3002\u4ed6\u601d\u5ff5\u524d\u59bb\uff0c\u5c82\u4e0d\u6b63\u8db3\u89c1\u4ed6\u7528\u60c5\u4e13\u4e00\uff0c\u751f\u6b7b\u4e0d\u6e1d\uff1f\u8fd8\u5c06\u65e7\u65f6 \u610f\uff0c\u601c\u53d6\u773c\u524d\u4eba\u3002\u7eed\u5f26\u7684\u7537\u5b50\uff0c\u82e5\u5f88\u5feb\u5c31\u5c06\u524d\u59bb\u5fd8\u4e86\uff0c\u5bf9\u540e\u59bb\u7684\u60c5\u7231\u4e5f\u672a\u5fc5\u80fd\u591f\u4fdd\u6301\u3002\u201d\u8fd9 \u8bdd\u5f53\u7136\u662f\u77f3\u5929\u94ce\u6709\u610f\u6170\u89e3\u5979\u7684\u3002\u4f46\u542c\u6765\u5374\u4e5f\u6709\u51e0\u5206\u9053\u7406\u3002 \u3000\u3000\u60f3\u4e0d\u5230\u4e91\u592b\u4eba\u7684\u6cea\u73e0\u8d8a\u6ef4\u8d8a\u591a\uff0c\u77f3\u5929\u94ce\u9053\uff1a\u201c\u6211\u4e0d\u4f1a\u8bf4\u8bdd\uff0c\u8bf4\u9519\u4e86\u4f60\u522b\u89c1\u602a\u3002\u201d\u4e91\u592b\u4eba \u9053\uff1a\u201c\u4f60\u77e5\u9053\u4ed6\u4e3a\u4ec0\u4e48\u5a36\u6211\uff1f\u201d\u77f3\u5929\u94ce\u9053\uff1a\u201c\u4f60\u7684\u6b66\u529f\u4eba\u54c1\uff0c\u624d\u8c8c\u98ce\u534e\uff0c\u81ea\u662f\u5dfe\u5e3c\u4e2d\u7684\u65e0\u53cc \u56fd\u58eb\u3002\u821e\u9633\u5144\u5728\u4ed6\u524d\u59bb\u8fd8\u5728\u7684\u65f6\u5019\uff0c\u8c08\u8d77\u4f60\u65f6\uff0c\u4e5f\u662f\u4f69\u670d\u5f97\u5f88\u7684\uff01\u201d\u4e91\u592b\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u4ed6\u90a3 \u91cc\u662f\u4e3a\u4e86\u5bf9\u6211\u6b23\u60a6\uff0c\u662f\u4e3a\u4e86\u6211\u7236\u4eb2\u90a3\u672c\u5251\u8c31\u5a36\u6211\u7684\u3002\u201d\u77f3\u5929\u94ce\u201c\u554a\u201d\u4e86\u4e00\u58f0\uff0c\u4e0d\u6562\u7b54\u8bdd\uff0c\u53ea \u542c\u5f97\u4e91\u592b\u4eba\u65ad\u65ad\u7eed\u7eed\u7684\u8bf4\u9053\uff1a\u201c\u6211\u7239\u7239\u5bfb\u56de\u4e86\u6b66\u5f53\u6d3e\u4e45\u5df2\u5931\u4f20\u7684\u8fbe\u6469\u53e4\u8c31\uff0c\u8fd8\u672a\u7ec3\u6210\uff0c\u5c31\u88ab \u4ed6\u5077\u8d70\u4e86\u3002\u6211\u4e0d\u607c\u4ed6\u601d\u5ff5\u524d\u59bb\uff0c\u4e5f\u8981\u607c\u4ed6\u4f7f\u6211\u7236\u5973\u5206\u79bb\uff0c\u6c38\u8fdc\u4e0d\u80fd\u89c1\u4ed6\uff01\u54fc\uff0c\u4ed6\u8fd9\u4eba\u81ea\u79c1\u5f97 \u5f88\uff0c\u4e3a\u4e86\u81ea\u5df1\u6210\u4e3a\u5929\u4e0b\u7b2c\u4e00\u5251\u5ba2\uff0c\u4ee4\u6211\u53d7\u4e86\u591a\u5c11\u6298\u78e8\uff01\u201d \u3000\u3000\u4e91\u592b\u4eba\u7684\u8bf4\u8bdd\u5176\u5b9e\u4e5f\u8fd8\u6709\u906e\u7792\uff0c\u4e0d\u9519\u4e91\u821e\u9633\u662f\u5904\u5fc3\u79ef\u8651\u60f3\u5f97\u4ed6\u5cb3\u7236\u90a3\u672c\u5251\u8c31\uff0c\u4f46\u5374\u662f\u4e91 \u592b\u4eba\u4eb2\u81ea\u5077\u7684\u3002\u90a3\u65f6\u6b63\u662f\u65b0\u5a5a\u4e4b\u540e\u4e0d\u4e45\uff0c\u5979\u6df1\u7231\u7740\u4e08\u592b\uff0c\u4e08\u592b\u53eb\u5979\u505a\u4ec0\u4e48\u5979\u5c31\u505a\u4ec0\u4e48\uff0c\u90a3\u91cc \u4f1a\u60f3\u5230\u540e\u6765\u7684\u53d8\u6545\u3002 \u3000\u3000\u539f\u6765\u5728\u4e8c\u5341\u5e74\u524d\u7684\u65f6\u5019\uff0c\u725f\u5b9d\u73e0\u6b63\u5f85\u5b57\u95fa\u4e2d\uff0c\u77f3\u5929\u94ce\u548c\u4e91\u821e\u9633\u90fd\u662f\u5979\u7236\u4eb2\u7684\u665a\u8f88\uff0c\u65f6\u5e38 \u6765\u5f80\uff0c\u5979\u7236\u4eb2\u5bf9\u77f3\u4e91\u4e8c\u4eba\u90fd\u662f\u4e00\u6837\u7740\u91cd\uff0c\u4f46\u4e91\u821e\u9633\u5df2\u6709\u59bb\u5b50\uff0c\u77f3\u592d\u62e9\u5c1a\u672a\u5a36\u59bb\uff0c\u725f\u5b9d\u73e0\u5012\u662f \u548c\u77f3\u5929\u94ce\u5728\u4e00\u8d77\u7684\u65f6\u5019\u8fd8\u591a\u3002\u540e\u6765\u4e91\u821e\u9633\u7684\u59bb\u5b50\u6218\u6b7b\u957f\u6c5f\uff0c\u4e91\u821e\u9633\u5230\u725f\u5bb6\u66f4\u52e4\u4e86\uff0c\u4e91\u821e\u9633\u662f \u6709\u8fc7\u59bb\u5b50\u7684\u4eba\uff0c\u81ea\u7136\u66f4\u61c2\u5f97\u5bf9\u5973\u4eba\u6e29\u67d4\u4f53\u8d34\uff0c\u52a0\u4ee5\u4ed6\u76f8\u8c8c\u51fa\u4f17\uff0c\u6f47\u6d12\u4e0d\u7fa4\uff0c\u6e29\u6587\u5112\u96c5\uff0c\u80fd\u6b66 \u80fd\u6587\uff0c\u4e0d\u5355\u725f\u72ec\u9038\u770b\u4e0a\u4e86\u4ed6\uff0c\u4e5f\u6e10\u6e10\u83b7\u5f97\u4e86\u725f\u5b9d\u73e0\u7684\u6b22\u5fc3\u3002\u7ec8\u4e8e\u725f\u5b9d\u73e0\u5c06\u77f3\u5929\u94ce\u4e22\u4e8e\u8eab\u540e\uff0c \u4e0b\u5ac1\u4e86\u4e91\u821e\u9633\u3002 \u3000\u3000\u725f\u5b9d\u73e0\u5e2e\u4e91\u821e\u9633\u5077\u4e86\u5251\u8c31\u4e4b\u540e\uff0c\u540c\u9003\u5230\u8d3a\u5170\u5c71\u4e2d\u3002\u521d\u65f6\u5979\u9676\u9189\u5728\u65b0\u5a5a\u7684\u751c\u871c\u4e2d\u8fd8\u4e0d\u89c9\u5f97 \u4ec0\u4e48\uff0c\u6e10\u6e10\u5c31\u60f3\u8d77\u4e86\u5bb6\u6765\uff0c\u968f\u7740\u5c81\u6708\u7684\u6d88\u901d\uff0c\u53c8\u53d1\u89c9\u4e86\u4e08\u592b\u5bf9\u4ed6\u7684\u6e29\u67d4\u8d34\u4f53\u6e10\u6e10\u6d88\u892a\uff0c\u50cf\u662f \u505a\u4f5c\u51fa\u6765\u4f3c\u7684\uff0c\u800c\u4ed6\u5bf9\u524d\u59bb\u7684\u5fc6\u5ff5\u65e5\u76ca\u52a0\u6df1\uff0c\u66f4\u4ee4\u725f\u5b9d\u73e0\u611f\u5230\u4f24\u5fc3\uff0c\u611f\u5230\u4e0d\u503c\uff0c\u4e8e\u662f\u4fbf\u4e0d\u65f6 \u7684\u60f3\u8d77\u77f3\u592d\u62e9\u6765\uff0c\u611f\u5230\u77f3\u5929\u94ce\u5f53\u5e74\u5bf9\u5979\u7684\u631a\u7231\u771f\u60c5\uff0c\u5b9e\u662f\u8fdc\u5728\u4e91\u821e\u9633\u4e4b\u4e0a\u3002 \u3000\u3000\u77f3\u5929\u94ce\u90a3\u91cc\u77e5\u9053\u4e91\u592b\u4eba\u8fd9\u756a\u611f\u60c5\u7684\u53d8\u5316\uff0c\u542c\u4e86\u5979\u7684\u503e\u8bc9\uff0c\u53ea\u5f53\u4e91\u592b\u4eba\u81ea\u59cb\u81f3\u7ec8\u7231\u7684\u662f \u4ed6\uff0c\u53ea\u56e0\u4e3a\u81ea\u5df1\u5949\u5c11\u4e3b\u9003\u4ea1\u585e\u5916\uff0c\u8fd9\u624d\u548c\u4e91\u821e\u9633\u7ed3\u5a5a\u7684\uff0c\u5fc3\u4e2d\u5927\u662f\u6fc0\u52a8\u3002\u53ea\u542c\u5f97\u4e91\u592b\u4eba\u786c\u54bd \u8bf4\u9053\uff1a\u201c\u6211\u7236\u4eb2\u5931\u4e86\u5251\u8c31\uff0c\u5bb6\u4e11\u4e0d\u4fbf\u5916\u626c\uff0c\u4e00\u76f4\u6ca1\u6709\u53d1\u4f5c\uff0c\u53ef\u662f\u81ea\u6b64\u4fbf\u4e0e\u6211\u65ad\u4e86\u7236\u5973\u4e4b\u60c5\uff0c \u4ed6\u540e\u6765\u4e5f\u77e5\u9053\u4e86\u6211\u4eec\u9690\u5c45\u4e4b\u5904\uff0c\u4ece\u6ca1\u6d3e\u4eba\u63a2\u95ee\u3002\u4ed6\u53ea\u6709\u6211\u8fd9\u4e2a\u72ec\u751f\u5973\u513f\uff0c\u800c\u6211\u5374\u4e0d\u5ff5\u517b\u80b2\u4e4b \u6069\uff0c\u5e2e\u52a9\u5916\u59d3\u5077\u4e86\u4ed6\u4f20\u6d3e\u4e4b\u5b9d\u7684\u5251\u8c31\uff0c\u60f3\u662f\u4ed6\u4e3a\u4e86\u6b64\u4e8b\u4f24\u5fc3\u4e4b\u6781\uff0c\u6ca1\u4e24\u5e74\u4fbf\u53bb\u4e16\u4e86\u3002\u53ef\u601c\u6211 \u4eec\u7236\u5973\u7ade\u6ca1\u80fd\u518d\u89c1\u4e00\u9762\uff01\u73b0\u5728\u7ee7\u627f\u6211\u7236\u4eb2\u638c\u95e8\u4eba\u4e4b\u4f4d\u7684\u5802\u5144\u4e5f\u6b7b\u4e86\uff0c\u6211\u624d\u7b2c\u4e00\u6b21\u89c1\u5230\u4ece\u5916\u5bb6 \u6765\u7684\u4eba\u3002\u201d \u3000\u3000\u9648\u7384\u673a\u5077\u542c\u81f3\u6b64\uff0c\u5fc3\u5934\u7830\u7136\u9707\u52a8\uff0c\u77e5\u9053\u5979\u8bf4\u7684\u662f\u725f\u4e00\u7c9f\u6d3e\u6765\u7684\u4e0a\u5b98\u5929\u91ce\uff0c\u4e0a\u5b98\u5929\u91ce\u7a76\u7adf \u5982\u4f55\u4e86\u5462\uff1f\u4e0d\u60f3\u4e91\u592b\u4eba\u63a5\u4e0b\u53bb\u5374\u5e76\u4e0d\u8bf4\u4e0a\u5b98\u5929\u91ce\uff0c\u8f7b\u8f7b\u7684\u53f9\u4e86\u53e3\u6c14\uff0c\u81ea\u6028\u81ea\u827e\u7684\u8bf4\u9053\uff1a\u201c\u7ecf \u8fc7\u4e86\u5341\u516b\u5e74\uff0c\u821e\u9633\u7684\u5251\u6cd5\u65e9\u5df2\u7ec3\u6210\uff0c\u8fd9\u672c\u5251\u8c31\u4ed6\u8fd8\u662f\u4e0d\u613f\u4ea4\u8fd8\uff0c\u4ed6\u53ea\u987e\u81ea\u5df1\u6210\u4e3a\u5929\u4e0b\u7b2c\u4e00\u5251 \u5ba2\uff0c\u4ece\u6765\u4e0d\u4e3a\u6211\u60f3\uff0c\u53ea\u6015\u6211\u4eca\u751f\u4eca\u4e16\uff0c\u518d\u4e5f\u4e0d\u80fd\u4e3a\u5bb6\u4eba\u6240\u8c05\u4e86\uff1a\u5440\uff01\u662f\u6211\u505a\u9519\u4e86\u4e8b\uff0c\u8fd9\u5341\u516b \u5e74\u6765\u7684\u5fc3\u5934\u9690\u75db\uff0c\u8fde\u503e\u5410\u7684\u4eba\u4e5f\u627e\u4e0d\u5230\uff0c\u4ed6\u5929\u5929\u8feb\u6211\u5403\u836f\uff0c\u6211\u8fd9\u5fc3\u75c5\u5c82\u662f\u836f\u6240\u80fd\u533b\uff1f\u5176\u5b9e\u4ed6 \u8feb\u6211\u5403\u836f\u53ea\u6015\u4e5f\u662f\u505a\u7ed9\u5973\u513f\u770b\u7684\uff0c\u4ed6\u65e0\u65f6\u65e0\u523b\u4e0d\u5728\u601d\u5ff5\u524d\u59bb\uff0c\u8fd8\u5f53\u6211\u4e0d\u77e5\u9053\uff01\u201d \u3000\u3000\u4e91\u592b\u4eba\u7684\u6ee1\u8154\u5e7d\u6028\u53d1\u6cc4\u51fa\u6765\uff0c\u542c\u5f97\u77f3\u5929\u94ce\u5fc3\u75db\u5982\u5272\uff0c\u5ffd\u5730\u6251\u4e0a\u524d\u9053\uff1a\u201c\u5b9d\u73e0\uff0c\u5b9d\u73e0\uff01\u201d \u4e91\u592b\u4eba\u9762\u8272\u4e00\u53d8\uff0c\u63a8\u5f00\u4ed6\u7684\u624b\u9053\uff1a\u201c\u5929\u94ce\uff0c\u4f60\u5feb\u8d70\u5427\uff01\u821e\u9633\u82e5\u662f\u56de\u6765\uff0c\u77a7\u89c1\u54b1\u4eec\u8fd9\u4e2a\u6837\u5b50\uff0c \u53ea\u6015\u4ed6\u4f1a\u628a\u4f60\u6740\u6b7b\uff01\u201d \u3000\u3000\u77f3\u5929\u94ce\u5fae\u201c\u568f\u201d\u4e00\u58f0\uff0c\u53c8\u9000\u56de\u4e86\u539f\u5904\uff0c\u4f46\u4ecd\u7136\u4e0d\u8d70\uff0c\u4e91\u592b\u4eba\u9053\uff1a\u201c\u4f60\u867d\u7136\u5e76\u4e0d\u6015\u4ed6\uff0c \u4f46\uff0c\u4f46\u2026\u2026\u201d\u60f3\u8bf4\uff1a\u201c\u4f46\u4f24\u4e86\u4f60\u4eec\u4efb\u4f55\u4e00\u4eba\uff0c\u6211\u90fd\u8981\u7ec8\u751f\u96be\u53d7\u3002\u201d\u8bdd\u5230\u53e3\u8fb9\uff0c\u5374\u6ca1\u6709\u8bf4\u51fa \u53e3\u3002 \u3000\u3000\u77f3\u5929\u94ce\u9053\uff1a\u201c\u89c1\u5230\u4e86\u4f60\u7684\u4e00\u9762\uff0c\u6211\u672c\u8be5\u5fc3\u6ee1\u610f\u8db3\uff0c\u5c31\u6b64\u8d70\u5f00\uff0c\u4f46\u6211\u4e0d\u80fd\u8d70\uff0c\u6211\u4e00\u5b9a\u8981\u89c1 \u821e\u9633\u3002\u201d\u4e91\u592b\u4eba\u9053\uff1a\u201c\u554a\uff0c\u4f60\u771f\u662f\u4e3a\u4e86\u627e\u821e\u9633\u6765\u7684\uff1f\u201d\u77f3\u592d\u62e9\u9053\uff1a\u201c\u55ef\uff0c\u4e3a\u4e86\u627e\u4f60\uff0c\u4e5f\u4e3a\u4e86 \u627e\u821e\u9633\u3002\u201d\u638f\u51fa\u4e86\u90a3\u4e2a\u7ee3\u8377\u5305\uff0c\u53f9\u53e3\u6c14\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u4ee5\u5f80\u7684\u4e8b\u4e0d\u5fc5\u518d\u63d0\u5566\uff0c\u8fd9\u4e2a\u7ed9\u56de\u4f60\u3002\u4eba \u751f\u4e0d\u5982\u610f\u4e8b\u5e38\u516b\u4e5d\uff0c\u4f55\u51b5\u821e\u9633\u5144\u6587\u624d\u6b66\u7565\uff0c\u90fd\u51a0\u7edd\u5f53\u4eca\uff0c\u4f60\u5c31\u5305\u542b\u4ed6\u4e00\u70b9\u5427\u3002\u201d \u3000\u3000\u4e91\u592b\u4eba\u63a5\u8fc7\u8377\u5305\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u6cea\u73e0\u513f\u53c8\u7981\u4e0d\u795d\u4fca\u9971\u7c41\u800c\u843d\uff0c\u60f3\u9053\uff1a\u201c\u82e5\u5f97\u821e\u9633\u4f3c\u4f60\u4e00\u6837 \u4f53\u8d34\u5bbd\u5bb9\uff0c\u6211\u53c8\u4f55\u81f3\u4e8e\u5bc2\u5bde\u81ea\u82e6\u3002\u201d\u77f3\u5929\u94ce\u53eb\u5979\u4e0d\u8981\u518d\u60f3\u5f80\u4e8b\uff0c\u4f46\u524d\u5c18\u5f80\u4e8b\uff0c\u5374\u504f\u504f\u6d8c\u5230\u5fc3 \u5934\u3002 \u3000\u3000\u6b47\u4e86\u4e00\u4f1a\uff0c\u53ea\u542c\u5f97\u77f3\u5929\u94ce\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u6211\u4e0e\u821e\u9633\u5144\u4e5f\u662f\u5341\u516b\u5e74\u6ca1\u6709\u89c1\u9762\u4e86\uff0c\u4e0d\u77e5\u4ed6\u5fc3\u610f \u5982\u4f55\uff0c\u4f46\u603b\u5f97\u89c1\u4ed6\u4e00\u9762\u3002\u201d\u4e91\u592b\u4eba\u9053\uff1a\u201c\u662f\u554a\uff0c\u6211\u8fd8\u6ca1\u6709\u95ee\u4f60\u5341\u516b\u5e74\u6765\u7684\u7ecf\u8fc7\u3002\u201d \u3000\u3000\u77f3\u5929\u94ce\u9053\uff0c\u201c\u4f60\u4e0d\u95ee\u6211\u4e5f\u8981\u5bf9\u4f60\u8bf4\u3002\u90a3\u4e00\u5e74\u5148\u5e1d\u5728\u957f\u6c5f\u6218\u8d25\uff0c\u88ab\u63b3\u8eab\u4ea1\u3002\u6211\u5949\u5148\u592a\u5b50\u9003 \u5230\u8499\u53e4\uff0c\u5e78\u5f97\u6709\u4e00\u4e2a\u5927\u90e8\u843d\u7684\u914b\u957f\u6536\u5bb9\uff0c\u8fd9\u4e2a\u90e8\u843d\u53eb\u505a\u9791\u8892\uff0c\u914b\u957f\u963f\u9c81\u53f0\u9887\u6709\u96c4\u56fe\uff0c\u6536\u5bb9\u4e86 \u6211\u4eec\u8fd9\u73ed\u4eba\u66ff\u4ed6\u51fa\u529b\uff0c\u4e0d\u5230\u5341\u5e74\uff0c\u4ed6\u5c31\u541e\u5e76\u4e86\u5468\u56f4\u7684\u90e8\u843d\uff0c\u5efa\u56fd\u53f7\u74e6\u523a\u3002\u4e09\u5e74\u524d\u963f\u9c81\u53f0\u6b7b \u4e86\uff0c\u7531\u4ed6\u7684\u513f\u5b50\u8131\u8131\u4e0d\u82b1\u7ee7\u4f4d\uff0c\u8131\u8131\u4e0d\u82b1\u5e74\u8f7b\uff0c\u4ed6\u7684\u53d4\u7236\u8131\u6b22\u81ea\u5c01\u592a\u5e08\uff0c\u4e3a\u4ed6\u76d1\u56fd\u3002\u8131\u6b22\u548c \u8131\u8131\u4e0d\u82b1\u90fd\u662f\u96c4\u624d\u5927\u7565\u3001\u4e0d\u53ef\u4e00\u4e16\u7684\u4eba\u7269\uff0c\u51e0\u5e74\u6765\u6574\u519b\u7ecf\u6b66\uff0c\u65e5\u8d8b\u5f3a\u5927\uff0c\u770b\u6765\u7edf\u4e00\u8499\u53e4\uff0c\u53ea \u5728\u6307\u987e\u4e4b\u95f4\u3002\u201d \u3000\u3000\u4e91\u592b\u4eba\u9053\uff1a\u201c\u8499\u53e4\u9694\u5f97\u8fd9\u4e48\u8fdc\uff0c\u4ed6\u4eec\u4e4b\u95f4\u7684\u90e8\u843d\u541e\u5e76\uff0c\u6211\u65e0\u5fc3\u7ec6\u542c\uff0c\u65f6\u95f4\u65e0\u591a\uff0c\u4f60\u8bf4\u8bf4 \u4f60\u4eec\u7684\u4e8b\u3002\u201d \u3000\u3000\u77f3\u5929\u94ce\u9053\uff1a\u201c\u8499\u53e4\u867d\u7136\u9694\u5f97\u8fdc\uff0c\u53ea\u6015\u8131\u6b22\u7edf\u4e00\u4e4b\u540e\uff0c\u5c31\u8981\u548c\u54b1\u4eec\u6c49\u4eba\u4e2a\u4e2a\u6709\u5173\u3002\u597d\uff0c\u6211 \u5c31\u8bf4\u6211\u4eca\u665a\u4e3a\u4f55\u800c\u6765\u3002\u201d \u3000\u3000\u201c\u5148\u592a\u5b50\u5230\u4e86\u8499\u53e4\u4e4b\u540e\uff0c\u751f\u4e0b\u4e00\u4e2a\u513f\u5b50\uff0c\u53eb\u505a\u5f20\u5b97\u5468\uff0c\u4eca\u5e74\u4e5f\u6709\u5341\u4e03\u5c81\u4e86\uff0c\u6b63\u597d\u4e0e\u8131\u8131 \u4e0d\u82b1\u5927\u53ef\u6c57\u540c\u5e74\u3002 \u3000\u3000\u201c\u5148\u592a\u5b50\u5ba2\u6b7b\u5f02\u57df\uff0c\u6211\u4eec\u4fbf\u5949\u5b97\u5468\u505a\u5e7c\u4e3b\uff0c\u5e7c\u4e3b\u806a\u660e\u7edd\u9876\uff0c\u800c\u4e14\u5177\u6709\u96c4\u5fc3\u5927\u5fd7\uff0c\u66f4\u80dc\u5148 \u4eba\uff0c\u6211\u4eec\u9f50\u5fc3\u8f85\u52a9\u4ed6\uff0c\u6587\u5b66\u6b66\u529f\uff0c\u4e86\u6559\u4fbf\u4f1a\uff0c\u6211\u79c1\u81ea\u5e86\u5e78\uff0c\u5148\u5e1d\u603b\u7b97\u6709\u4e86\u540e\u4eba\uff0c\u5c06\u6765\u590d\u56fd\u6709 \u671b\u3002\u201d \u3000\u3000\u201c\u4e0d\u60f3\u5e7c\u4e3b\u592a\u806a\u660e\u4e86\uff0c\u590d\u56fd\u5fc3\u5207\uff0c\u6211\u62c5\u5fc3\u4ed6\u53ea\u6015\u4f1a\u8bef\u5165\u6b67\u9014\uff0c\u90a3\u8131\u8131\u4e0d\u82b1\u5e74\u7eaa\u867d\u8f7b\uff0c\u96c4 \u5fc3\u6781\u5927\u3002\u4ed6\u4fbf\u548c\u5e7c\u4e3b\u6df1\u76f8\u7ed3\u7eb3\uff0c\u5141\u8bb8\u7edf\u4e00\u8499\u53e4\u4e4b\u540e\uff0c\u66ff\u4ed6\u590d\u56fd\u3002\u5176\u5b9e\u5374\u662f\u57f9\u690d\u529b\u91cf\uff0c\u538b\u4f4e\u4ed6 \u53d4\u7236\u7684\u6c14\u7130\u3002\u540c\u65f6\u60f3\u7edf\u4e00\u8499\u53e4\u4e4b\u540e\uff0c\u518d\u95ee\u9f0e\u4e2d\u539f\u3002\u6211\u9ed8\u5bdf\u5f62\u52bf\uff0c\u6df1\u611f\u5371\u673a\u4e25\u91cd\uff0c\u53e4\u5f80\u4eca\u6765\uff0c \u4ece\u6765\u6ca1\u6709\u501f\u5916\u56fd\u4e4b\u5175\uff0c\u53ef\u6210\u5e1d\u4e1a\u7684\u3002\u7eb5\u8bb8\u6210\u4e86\uff0c\u4e5f\u4e0d\u8fc7\u662f\u513f\u7687\u5e1d\u800c\u5df2\u3002\u53ef\u53f9\u6211\u7684\u65e7\u65e5\u540c\u50da\uff0c \u5374\u65e0\u4e00\u773c\u5149\u8fdc\u5927\u4e4b\u4eba\uff0c\u53cd\u800c\u4eba\u4eba\u79f0\u5e86\uff0c\u4e0e\u5e7c\u4e3b\u540c\u4e00\u5fc3\u610f\uff0c\u68a6\u60f3\u5c06\u6765\u80fd\u501f\u74e6\u523a\u4e4b\u529b\uff0c\u518d\u4e0e\u6731\u5143 \u6f33\u4e89\u593a\u6c5f\u5c71\uff01\u201d \u3000\u3000\u9648\u7384\u673a\u6697\u4e2d\u5077\u542c\uff0c\u5403\u60ca\u975e\u5c0f\uff0c\u60f3\u9053\uff1a\u201c\u5f20\u5b97\u5468\u5982\u679c\u771f\u7684\u501f\u4e86\u5916\u5175\uff0c\u6253\u56de\u4e2d\u539f\uff0c\u8fd9\u5c82\u4e0d\u662f \u5f00\u95e8\u7eb3\u864e\uff0c\u53ea\u6015\u590d\u56fd\u4e0d\u6210\uff0c\u4e2d\u534e\u7684\u9526\u7ee3\u6cb3\u5c71\u5148\u81ea\u65ad\u9001\u4e86\uff01\u5440\uff0c\u6211\u7684\u53d4\u4f2f\u5e08\u957f\u8f88\uff0c\u4e8c\u5341\u5e74\u6765\uff0c \u4e00\u76f4\u6000\u7740\u5b64\u81e3\u5b7d\u5b50\u4e4b\u5fc3\uff0c\u60f3\u66ff\u5927\u5468\u518d\u6253\u5929\u4e0b\uff0c\u82e5\u662f\u4ed6\u4eec\u77e5\u9053\u4e86\u8fd9\u4e2a\u6d88\u606f\uff0c\u4e0d\u77e5\u5982\u4f55\uff1f\u201d \u3000\u3000\u53ea\u542c\u5f97\u77f3\u5929\u94ce\u53f9\u4e86\u53e3\u6c14\uff0c\u5f80\u4e0b\u8bf4\u9053\uff1a\u201c\u5e7c\u4e3b\u7684\u5fc3\u610f\u65e0\u53ef\u633d\u56de\uff0c\u4ed6\u5df1\u53d1\u4e0b\u4e86\u5148\u5e1d\u7684\u91d1\u724c\uff0c \u4ea4\u7ed9\u4e86\u4e03\u4fee\u9053\u4eba\uff0c\u6d3e\u4ed6\u4e0e\u84b2\u575a\u6f5c\u56de\u4e2d\u56fd\uff0c\u53ec\u96c6\u5148\u5e1d\u65e7\u90e8\uff0c\u90fd\u5230\u74e6\u523a\u53bb\u5171\u56fe\u5927\u4e8b\u3002\u7b2c\u4e00\u4e2a\u8981\u5ba3 \u53ec\u7684\u4fbf\u662f\u4e91\u821e\u9633\u5144\uff01\u8fd9\u4e8b\u60c5\u5173\u7cfb\u91cd\u5927\uff0c\u6211\u6b64\u6765\u4fbf\u662f\u60f3\u529d\u963b\u821e\u9633\u5144\uff0c\u5e76\u8bf7\u4ed6\u8fc5\u5373\u8f6c\u544a\u56fd\u4e2d\u65e7 \u53cb\uff0c\u5171\u8c0b\u5bf9\u7b56\u3002\u4e0d\u77e5\u821e\u9633\u5144\u8fd9\u4e9b\u5e74\u6765\u666f\u51b5\u5982\u4f55\uff1f\u6253\u7b97\u600e\u6837\uff1f\u201d \u3000\u3000\u4e91\u592b\u4eba\u9053\uff1a\u201c\u821e\u9633\u8fd9\u5341\u591a\u5e74\u6765\u9690\u5c45\u6b64\u5c71\uff0c\u4e0e\u65e7\u65e5\u670b\u53cb\u90fd\u5df2\u65ad\u7edd\u4e86\u6765\u5f80\u3002\u4e0d\u8fc7\uff0c\u4ed6\u770b\u6765\u867d \u4f3c\u4e0d\u95ee\u4e16\u4e8b\uff0c\u5176\u5b9e\u4ed6\u7684\u5251\u672f\u7ec3\u6210\u4e4b\u540e\uff0c\u5374\u65e0\u65f6\u4e0d\u60f3\u518d\u5ea6\u51fa\u5c71\uff0c\u8981\u6b66\u6797\u627f\u8ba4\u4ed6\u5929\u4e0b\u7b2c\u4e00\u5251\u5ba2\u7684 \u79f0\u53f7\u3002\u53ea\u56e0\u6211\u7684\u5802\u5144\u8fd8\u5728\uff0c\u4ed6\u6709\u6240\u987e\u8651\uff0c\u6545\u6b64\u8fdf\u8fdf\u672a\u52a8\u3002\u5982\u4eca\u6211\u7684\u7236\u5144\u76f8\u7ee7\u53bb\u4e16\uff0c\u4ed6\u518d\u5ea6\u51fa \u5c71\uff0c\u5c06\u662f\u65e6\u5915\u4e4b\u4e8b\u4e86\u3002\u201d\u77f3\u5929\u94ce\u9053\uff1a\u201c\u8c79\u6b7b\u7559\u76ae\uff0c\u4eba\u6b7b\u7559\u540d\uff0c\u821e\u9633\u5144\u7ec3\u6210\u8fbe\u6469\u5251\u672f\uff0c\u6b32\u4e3a\u4e16 \u6240\u77e5\uff0c\u8fd9\u4e5f\u662f\u4eba\u60c5\u4e4b\u5e38\u3002\u821e\u9633\u5144\u6709\u610f\u51fa\u5c71\uff0c\u90a3\u662f\u6700\u597d\u4e0d\u8fc7\u3002\u201d\u4e91\u592b\u4eba\u9053\uff1a\u201c\u4ed6\u5fd7\u4e0d\u5728\u5c0f\u3002\u53ea \u6015\u4ed6\u65e2\u4e0d\u4f1a\u63a5\u5e7c\u4e3b\u7684\u91d1\u724c\u524d\u5f80\u74e6\u523a\uff0c\u4e5f\u4e0d\u4f1a\u4f9d\u4f60\u4e4b\u529d\uff0c\u66ff\u4f60\u9001\u4fe1\u7ed9\u8001\u670b\u53cb\u4eec\u3002\u201d\u77f3\u5929\u94ce\u9053\uff1a \u201c\u8fd9\u5374\u662f\u4e3a\u4f55\uff1f\u201d\u4e91\u592b\u4eba\u9053\uff1a\u201c\u6731\u5143\u6f33\u7684\u9526\u8863\u536b\u603b\u6307\u6325\uff0c\u4eac\u90fd\u7b2c\u4e00\u9ad8\u624b\u7f57\u91d1\u5cf0\u524d\u51e0\u65e5\u66fe\u5230\u8fc7 \u6b64\u95f4\u4e0e\u4ed6\u5546\u8c08\u3002\u201d\u77f3\u5929\u94ce\u8be7\u9053\uff1a\u201c\u6709\u8fd9\u7b49\u4e8b\uff1f\u201d\u4e91\u592b\u4eba\u9053\uff1a\u201c\u6211\u9690\u9690\u95fb\u77e5\uff0c\u4ed6\u5c06\u63a5\u53d7\u6731\u5143\u748b \u7684\u793c\u8058\uff0c\u529d\u5148\u5e1d\u7684\u65e7\u90e8\u964d\u987a\u65b0\u671d\u3002\u201d\u77f3\u5929\u94ce\u9053\uff1a\u201c\u90a3\u73ed\u4eba\u5fe0\u5fc3\u803f\u803f\uff0c\u53ea\u6015\u4ed6\u8981\u767d\u8d39\u5fc3\u673a\u3002\u201d \u4e91\u592b\u4eba\u9053\uff1a\u201c\u82e5\u7136\u4e0d\u80af\u964d\u987a\uff0c\u7f57\u91d1\u5cf0\u5c31\u8981\u6309\u5740\u641c\u6355\u4e86\u3002\u201d\u77f3\u5929\u94ce\u6014\u4e86\u4e00\u6014\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u8fd9 \u5c82\u4e0d\u662f\u5356\u53cb\u6c42\u8363\uff1f\u201d\u4e91\u592b\u4eba\u9053\uff1a\u201c\u821e\u9633\u548c\u6211\u4e5f\u4e0d\u80af\u8bf4\u5fc3\u8179\u8bdd\uff0c\u6211\u4fa7\u95fb\u8fd9\u4e2a\u6d88\u606f\uff0c\u90a3\u662f\u7d20\u7d20\u542c \u6765\u7684\u3002\u6211\u63a2\u95ee\u4ed6\uff0c\u4ed6\u5374\u4e0d\u9732\u534a\u53e5\u53e3\u98ce\uff0c\u8fd9\u51e0\u65e5\u6765\u4f46\u89c1\u4ed6\u597d\u4f3c\u5fc3\u4e8b\u91cd\u91cd\u7684\u6837\u5b50\uff0c\u8fde\u6211\u4e5f\u4e0d\u77e5\u4ed6 \u5fc3\u4e2d\u7684\u771f\u610f\u3002\u201d \u3000\u3000\u77f3\u5929\u94ce\u9053\uff1a\u201c\u4f46\u613f\u821e\u9633\u5144\u4e0d\u8981\u4e0a\u94a9\u624d\u597d\u3002\u4e5f\u671b\u4f60\u529d\u4e00\u529d\u4ed6\u3002\u201d\u4e91\u592b\u4eba\u82e6\u7b11\u9053\uff1a\u201c\u6211\u4e0e\u4ed6 \u867d\u662f\u592b\u59bb\uff0c\u5b9e\u540c\u964c\u8def\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u5f7c\u6b64\u90fd\u662f\u6577\u884d\u7740\u8fc7\u65e5\u5b50\u7f62\u4e86\u3002\u201d\u77f3\u5929\u94ce\u5fc3\u4e2d\u51c4\u607b\uff0c\u8f7b\u58f0\u53eb \u9053\uff1a\u201c\u5b9d\u73e0\uff0c\u4f60\u2014\u2014\u201d\u4e91\u592b\u4eba\u5ffd\u5730\u62ac\u8d77\u5934\u9053\uff1a\u201c\u821e\u9633\u4eca\u665a\u53ea\u6015\u4e0d\u56de\u6765\u4e86\uff0c\u73b0\u5728\u5df2\u662f\u56db\u66f4\u65f6 \u4efd\uff0c\u7d20\u7d20\u6bcf\u665a\u4e94\u66f4\u8981\u8d77\u8eab\u7ec3\u5251\uff0c\u518d\u63a5\u7740\u505a\u9ece\u660e\u7684\u65e9\u8bfe\uff0c\u4f60\uff0c\u4f60\u8fd8\u662f\u8d70\u5427\uff0c\u660e\u5929\u518d\u6765\u3002\u201d \u3000\u3000\u77f3\u5929\u94ce\u4f9d\u4f9d\u4e0d\u820d\uff0c\u8d70\u4e86\u4e24\u6b65\uff0c\u5ffd\u4f3c\u60f3\u8d77\u4e00\u4e8b\uff0c\u53e3\u5934\u95ee\u9053\uff1a\u201c\u5b9d\u73e0\uff0c\u4f60\u6709\u6ca1\u6709\u89c1\u8fc7\u4e00\u5e45\u957f \u6c5f\u79cb\u6708\u7684\u56fe\u753b\uff1f\u201d\u4e91\u592b\u4eba\u9053\uff1a\u201c\u4f60\u95ee\u8fd9\u5e45\u753b\u505a\u4ec0\u4e48\uff1f\u8fd9\u5e45\u753b\u5c31\u5728\u8fd9\u95f4\u4e66\u623f\u91cc\u9762\u3002\u201d\u77f3\u5929\u94ce \u9053\uff1a\u201c\u662f\u4e48\uff1f\u8bb8\uff0c\u5f85\u6211\u8fdb\u53bb\u770b\u770b\u3002\u201d\u4e91\u592b\u4eba\u5927\u4e3a\u5947\u602a\uff0c\u53ea\u597d\u8ddf\u7740\u4ed6\u8d70\u8fdb\u4e66\u623f\u3002 \u3000\u3000\u9648\u7384\u673a\u6025\u5fd9\u95ea\u5165\u4e66\u6a71\u540e\u9762\uff0c\u53ea\u542c\u5f97\u77f3\u5929\u94ce\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u8c01\u5728\u8fd9\u4e66\u623f\u91cc\u9762\uff1f\u201d\u9648\u7384\u673a\u8fd9\u4e00 \u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u6b63\u5f85\u633a\u8eab\u800c\u51fa\uff0c\u5374\u542c\u5f97\u4e91\u592b\u4eba\u7b11\u9053\uff1a\u201c\u4e66\u623f\u91cc\u90a3\u4f1a\u6709\u4eba\uff0c\u821e\u9633\u5c31\u662f\u56de\u6765\uff0c\u4e5f\u4e0d \u4f1a\u85cf\u5728\u4e66\u623f\u91cc\u5077\u542c\u54b1\u4eec\u8bf4\u8bdd\u3002\u201d\u77f3\u5929\u94ce\u9053\uff1a\u201c\u6211\u597d\u50cf\u542c\u5230\u4ec0\u4e48\u58f0\u606f\u3002\u201d\u4e91\u592b\u4eba\u9053\uff1a\u201c\u4e5f\u8bb8\u662f \u89c5\u98df\u7684\u9f20\u513f\u3002\u201d\u9648\u7384\u673a\u4ece\u7a97\u8fb9\u95ea\u4eba\u4e66\u6a71\u80cc\u540e\uff0c\u4e0d\u8fc7\u662f\u51e0\u6b65\u4e4b\u9694\uff0c\u4ed6\u79fb\u52a8\u811a\u6b65\uff0c\u53c8\u662f\u8f7b\u5230\u4e86\u6781 \u70b9\uff0c\u77f3\u5929\u94ce\u867d\u7136\u5fc3\u6709\u6240\u7591\uff0c\u542c\u4e91\u592b\u4eba\u4e00\u8bf4\uff0c\u4e5f\u5c31\u4e0d\u518d\u8a00\u8bed\uff0c\u201c\u78b0\u201d\u7684\u4e00\u58f0\uff0c\u71c3\u4e86\u706b\u77f3\uff0c\u70b9\u7740 \u4e86\u6848\u5934\u7684\u70db\u53f0\u3002 \u3000\u3000\u9648\u7384\u673a\u65b9\u81ea\u677e\u4e86\u53e3\u6c14\uff0c\u5ffd\u542c\u5f97\u4e91\u592b\u4eba\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u8fd9\u4e66\u623f\u524d\u4e24\u5929\u5012\u6709\u4eba\u4f4f\u8fc7\u3002\u201d\u77f3\u5929\u94ce \u9053\uff1a\u201c\u8c01\uff1f\u201d\u4e91\u592b\u4eba\u9053\uff1a\u201c\u662f\u7d20\u7d20\u6551\u56de\u6765\u7684\u4e00\u4e2a\u5c0f\u4f19\u5b50\uff1b\u542c\u8bf4\u4ed6\u7684\u7236\u4eb2\u4e5f\u662f\u4f60\u4eec\u6614\u65e5\u7684\u540c\u50da \u5462\u3002\u4ed6\u4e0d\u77e5\u4f55\u6545\uff0c\u88ab\u4eba\u6240\u4f24\uff0c\u7d20\u7d20\u5c06\u5979\u7236\u4eb2\u6700\u73cd\u60dc\u7684\u5c0f\u8fd8\u4e39\u4e5f\u7ed9\u4ed6\u5403\u4e86\uff1b\u8fd9\u5c0f\u5b50\u80c6\u5927\u5305\u5929\uff0c \u7adf\u60f3\u884c\u523a\u821e\u9633\uff0c\u821e\u9633\u56de\u6765\u5c31\u5c06\u4ed6\u64b5\u8d70\u4e86\u3002\u53ef\u60dc\u6211\u6ca1\u6709\u89c1\u8fc7\u4ed6\uff0c\u7d20\u7d20\u5bf9\u4ed6\u597d\u4f3c\u9887\u4e3a\u601d\u5ff5\uff0c\u5728\u6211 \u9762\u524d\u5c31\u5938\u8d5e\u8fc7\u4ed6\uff0c\u8bf4\u4ed6\u6e29\u6587\u5112\u96c5\uff0c\u6b66\u529f\u53c8\u597d\u5f97\u51fa\u5947\uff0c\u8fde\u821e\u9633\u4e5f\u79f0\u9053\u4ed6\u7684\u5251\u6cd5\u5440\uff0c\u7d20\u7d20\u8fd9\u5b69\u5b50 \u6beb\u65e0\u673a\u5fc3\uff0c\u5bf9\u964c\u751f\u4eba\u4e5f\u8fd9\u6837\u597d\u6cd5\u3002\u4f60\u77a7\uff0c\u5979\u7684\u5251\u8fd8\u6302\u5728\u8fd9\u91cc\uff0c\u5f53\u65f6\u82e5\u662f\u90a3\u5c0f\u4f19\u5b50\u5077\u53bb\u4e86\uff0c\u5979 \u7236\u4eb2\u624d\u4e0d\u80af\u8f7b\u9976\u5462\uff01\u201d\u77f3\u5929\u94ce\u7b11\u9053\uff1a\u201c\u90a3\u4e48\u5012\u662f\u7d20\u7d20\u5927\u6709\u773c\u5149\uff0c\u82e5\u7136\u90a3\u5c0f\u4f19\u5b50\u4e0d\u662f\u6b63\u4eba\u541b \u5b50\uff0c\u5979\u7109\u80af\u5c06\u4ed6\u6b3e\u5f85\uff1f\u201d\u9648\u7384\u673a\u4ece\u4e91\u592b\u4eba\u7684\u8bdd\u4e2d\uff0c\u8bc1\u5b9e\u4e86\u7d0a\u7d20\u5077\u662f\u5bf9\u4ed6\u601d\u5ff5\uff0c\u5fc3\u4e2d\u751c\u7545\u4e4b \u6781\u3002 \u3000\u3000\u4e91\u592b\u4eba\u6b63\u60f3\u8bf4\u8bdd\uff0c\u89c1\u77f3\u5929\u94ce\u51dd\u795e\u770b\u753b\uff0c\u795e\u60c5\u6709\u5f02\uff0c\u5947\u800c\u95ee\u9053\uff1a\u201c\u600e\u4e48\uff0c\u8fd9\u5e45\u753b\u8fd8\u6709\u4ec0\u4e48 \u53e4\u602a\u4e0d\u6210\uff1f\u201d\u77f3\u5929\u94ce\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u6b63\u662f\u8fd9\u4e00\u5e45\u753b\u3002\u201d\u53f9\u4e86\u53e3\u6c14\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u5148 \u5e1d\u5728\u6b89\u56fd\u7684\u524d\u5915\u53eb\u4eba\u753b\u7684\u3002\u753b\u4e2d\u6240\u85cf\u7684\u79d8\u5bc6\uff0c\u53ea\u6709\u6211\u4e0e\u821e\u9633\u77e5\u9053\u3002\u542c\u4f60\u7684\u53e3\u6c14\uff0c\u4f3c\u4e4e\u4ed6\u8fd8\u6ca1 \u6709\u5bf9\u4f60\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u4e91\u592b\u4eba\u9053\uff1a\u201c\u8bb8\u591a\u4e8b\u60c5\u821e\u9633\u90fd\u7792\u7740\u6211\uff0c\u5c82\u6b62\u53ea\u6b64\u4e00\u6869\u3002\u201d\u77f3\u5929\u94ce\u9053\uff1a\u201c\u4e8c\u5341\u5e74\u524d\u5728\u957f\u6c5f \u51b3\u6218\u7684\u524d\u5915\uff0c\u5148\u5e1d\u81ea\u77e5\u4e0d\u514d\uff0c\u5c06\u6240\u79ef\u805a\u7684\u73cd\u5b9d\u5168\u90fd\u85cf\u5728\u82cf\u5dde\u4e00\u4e2a\u9690\u50fb\u7684\u5730\u65b9\uff0c\u73cd\u5b9d\u4e5f\u8fd8\u7f62 \u4e86\uff0c\u8fd8\u6709\u5f6d\u548c\u5c1a\u6240\u7ed8\u7684\u4e00\u5e45\u519b\u7528\u7684\u5929\u4e0b\u8be6\u56fe\uff0c\u8c01\u5f97\u6b64\u56fe\uff0c\u4fbf\u53ef\u56fe\u738b\u9738\u4e4b\u4e1a\u3002\u73cd\u5b9d\u5730\u56fe\u7684\u85cf \u5904\uff0c\u4fbf\u5728\u8fd9\u753b\u4e0a\u505a\u4e0b\u4e86\u8bb0\u53f7\u3002\u201d\u4e91\u592b\u4eba\u201c\u554a\u201d\u4e86\u4e00\u58f0\uff0c\u60f3\u4e0d\u5230\u4ed6\u4eec\u4ea1\u56fd\u541b\u81e3\uff0c\u5728\u5175\u8d25\u524d\u5915\uff0c \u8fd8\u662f\u8fd9\u6837\u6df1\u8c0b\u8fdc\u8651\u3002\u77f3\u5929\u94ce\u7eed\u9053\uff1a\u201c\u5f53\u65f6\u5148\u5e1d\u672c\u6765\u8981\u6211\u5e26\u8fd9\u5e45\u753b\u8d70\uff0c\u821e\u9633\u5144\u8bf4\uff1a\u4f60\u5949\u592a\u5b50\u9003 \u4ea1\uff0c\u8d23\u4efb\u91cd\u5927\uff0c\u4fdd\u5168\u6b64\u753b\uff0c\u8fd8\u662f\u8ba9\u6211\u5206\u52b3\u5427\u3002\u5e7c\u4e3b\u6b64\u6b21\u5ba3\u53ec\u821e\u9633\uff0c\u56fa\u7136\u4e3a\u4e86\u4ed6\u8fd9\u4e2a\u4eba\u6750\uff0c\u4f46 \u8fd9\u5e45\u753b\u60f3\u6765\u4e5f\u662f\u4e00\u56e0\u3002\u201d \u3000\u3000\u4e91\u592b\u4eba\u9053\uff1a\u201c\u6211\u770b\u821e\u9633\u591a\u534a\u4e0d\u4f1a\u524d\u5f80\u74e6\u523a\uff0c\u8fd9\u5e45\u753b\uff0c\u8fd9\u5e45\u753b\u2026\u2026\u201d\u77f3\u5929\u94ce\u7acb\u5373\u60f3\u5230\uff1a\u4e91 \u821e\u9633\u82e5\u771f\u7684\u63a5\u53d7\u4e86\u6731\u5143\u6f33\u7684\u793c\u8058\uff0c\u8fd9\u5e45\u753b\u5c31\u662f\u4e00\u4efd\u65e0\u53ef\u6bd4\u62df\u7684\u8d21\u793c\uff01\u4e0d\u7981\u5446\u4e86\u3002\u4e91\u592b\u4eba\u5401\u4e86 \u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u770b\u821e\u9633\u65e2\u4e0d\u4f1a\u53bb\u74e6\u523a\uff0c\u4e5f\u4e0d\u4f1a\u542c\u4f60\u7684\u7b56\u5212\u4e86\uff0c\u8fd9\u5e45\u753b\u4f60\u5e26\u8d70\u4e86\u5427\u3002\u201d\u8bdd\u672a\u8bf4 \u5b8c\uff0c\u5ffd\u542c\u5f97\u201c\u563f\u201d\u7684\u4e00\u58f0\u51b7\u7b11\uff0c\u4e24\u4eba\u56de\u5934\u4e00\u671b\uff0c\u5374\u89c1\u4e91\u821e\u9633\u5df2\u7ad9\u5728\u95e8\u524d\uff01 \u3000\u3000------------- \u3000\u3000\u91cd\u5e86\u96ea\u513f \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u8fd8\u5251\u5947\u60c5\u5f55\u300b\u2014\u2014\u7b2c\u4e94\u56de\u3000\u9f99\u4e89\u864e\u6597 \u6881\u7fbd\u751f\u300a\u8fd8\u5251\u5947\u60c5\u5f55\u300b \u7b2c\u4e94\u56de\u3000\u9f99\u4e89\u864e\u6597 \u3000\u3000\u70db\u5f71\u6447\u66f3\uff0c\u4f46\u89c1\u4e91\u821e\u9633\u5634\u89d2\u6302\u7740\u51b7\u7b11\uff0c\u7f13\u7f13\u7684\u8d70\u8fdb\u4e66\u623f\uff0c\u4e91\u592b\u4eba\u671b\u4e86\u4e08\u592b\u4e00\u773c\uff0c\u5fc3\u4e2d\u6fc0 \u52a8\u4e4b\u6781\uff0c\u8138\u4e0a\u5374\u662f\u6728\u7136\u7684\u6beb\u65e0\u8868\u60c5\uff0c\u4ecd\u5728\u5377\u90a3\u5e45\u753b\u3002 \u3000\u3000\u77f3\u5929\u94ce\u9053\uff1a\u201c\u821e\u9633\u5144\uff0c\u4f60\u56de\u6765\u4e86\uff01\u201d\u4e91\u821e\u9633\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u6599\u4e0d\u5230\u6211\u56de\u6765\u5f97\u8fd9\u6837\u5feb\u5427\uff1f\u201d \u77f3\u5929\u94ce\u9053\uff1a\u201c\u6211\u6709\u8981\u4e8b\u627e\u4f60\uff0c\u5df2\u7b49\u4f60\u591a\u65f6\u4e86\u3002\u821e\u9633\u5144\uff0c\u4f60\uff0c\u4f60\u542c\u6211\u8bf4\u2026\u2026\u201d \u3000\u3000\u4e91\u821e\u9633\u8de8\u4e0a\u4e00\u6b65\uff0c\u5229\u7bad\u822c\u7684\u773c\u5149\u5728\u4ed6\u592b\u4eba\u7684\u8138\u4e0a\u6ef4\u6e9c\u6e9c\u4e00\u8f6c\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5b9d\u73e0\uff0c\u591a\u8c22 \u4f60\u66ff\u6211\u62db\u5f85\u8d35\u5bbe\uff0c\u628a\u8fd9\u5e45\u753b\u653e\u4e0b\uff0c\u4f60\u8be5\u8fdb\u53bb\u5403\u836f\u5566\u3002\u201d \u3000\u3000\u4e91\u592b\u4eba\u4ecd\u662f\u4e0d\u8a00\u4e0d\u8bed\uff0c\u653e\u4e0b\u4e86\u753b\u5377\uff0c\u5374\u5e76\u672a\u79fb\u52a8\u811a\u6b65\u3002\u4e91\u821e\u9633\u53c8\u77a7\u4e86\u5979\u4e00\u773c\uff0c\u51b7\u51b7\u8bf4 \u9053\uff1a\u201c\u597d\uff0c\u4f60\u65e2\u62b1\u6028\u6211\u6837\u6837\u7792\u4f60\uff0c\u4f60\u4e0d\u8d70\u4e5f\u7f62\uff0c\u5c31\u8ba9\u4f60\u5728\u8fd9\u91cc\u542c\u4e2a\u660e\u767d\u3002\u201d \u3000\u3000\u77f3\u5929\u94ce\u6025\u5fd9\u53eb\u9053\uff1a\u201c\u821e\u9633\u5144\uff0c\u4f60\u542c\u6211\u8bf4\u2026\u2026\u201d\u4e91\u821e\u9633\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u8bf4\u4e86\uff0c\u4f60\u7684\u6765\u610f\uff0c\u6211 \u65e9\u5df2\u77e5\u9053\u3002\u201d\u77f3\u5929\u94ce\u9053\uff1a\u201c\u821e\u9633\u5144\uff0c\u4e0d\u53ef\u591a\u7591\uff0c\u77f3\u67d0\u4e5f\u662f\u4e2a\u9876\u5929\u7acb\u5730\u7684\u52c7\u58eb\uff0c\u7edd\u4e0d\u4f1a\u505a\u51fa\u5bf9 \u4e0d\u8d77\u670b\u53cb\u7684\u4e8b\u60c5\u3002\u201d\u4e91\u821e\u9633\u9053\uff1a\u201c\u6211\u591a\u8c22\u4f60\u7684\u597d\u610f\u5566\u3002\u4f60\u662f\u4e0d\u662f\u4e3a\u8fd9\u4e2a\u6765\u7684\uff1f\u201d \u3000\u3000\u53ea\u89c1\u91d1\u5149\u707f\u70c2\uff0c\u4e91\u821e\u9633\u638f\u51fa\u4e86\u4e00\u9762\u91d1\u724c\uff0c\u77f3\u5929\u94ce\u53eb\u9053\uff1a\u201c\u4f60\u89c1\u8fc7\u4e86\u4e03\u4fee\u9053\u5144\u548c\u84b2\u575a\u4e86\uff1f\u201d \u4e91\u821e\u9633\u9053\uff1a\u201c\u6211\u5df2\u628a\u4ed6\u4eec\u9a71\u9010\u4e0b\u5c71\u53bb\u4e86\u3002\u8fd9\u9762\u91d1\u813e\u662f\u6211\u6263\u4e86\u4e0b\u6765\uff0c\u514d\u5f97\u4ed6\u4eec\u518d\u53bb\u6e38\u8bf4\u5176\u4ed6\u670b \u53cb\u3002\u201d\u77f3\u5929\u94ce\u559c\u9053\uff1a\u201c\u821e\u9633\u5144\uff0c\u4f60\u679c\u7136\u90fd\u77e5\u9053\u4e86\u3002\u4f60\u505a\u5f97\u5bf9\u6781\uff0c\u5c0f\u5f1f\u4f69\u670d\uff01\u201d \u3000\u3000\u4e91\u821e\u9633\u51b7\u51b7\u4e00\u7b11\uff0c\u53cc\u638c\u4e00\u6413\uff0c\u628a\u90a3\u9762\u56db\u56db\u65b9\u65b9\u7684\u91d1\u724c\uff0c\u6413\u6210\u4e86\u4e00\u6839\u91d1\u6761\uff0c\u968f\u624b\u4e00\u63b7\uff0c\u91d1 \u6761\u6ca1\u5165\u571f\u4e2d\uff0c\u638c\u529b\u60ca\u4eba\uff0c\u9648\u7384\u673a\u5728\u4e66\u6a71\u540e\u9762\u5077\u77a7\uff0c\u7981\u4e0d\u4f4f\u6026\u7136\u5fc3\u8df3\uff0c\u60f3\u9053\uff1a\u201c\u524d\u5929\u8981\u4e0d\u662f\u7d20 \u7d20\u62e6\u963b\uff0c\u4ed6\u90a3\u4e00\u638c\u5c31\u80fd\u4ee4\u6211\u7c89\u8eab\u788e\u9aa8\u3002\u201d \u3000\u3000\u53ea\u542c\u5f97\u4e91\u821e\u9633\u51b7\u7b11\u9053\uff1a\u201c\u5f53\u5e74\u7fa4\u96c4\u9010\u9e7f\uff0c\u6211\u4e0e\u5148\u5e1d\u5d1b\u8d77\u8349\u83bd\u4e4b\u4e2d\uff0c\u8eab\u7ecf\u767e\u6218\uff0c\u629a\u5fc3\u8f67 \u53ef\uff0c\u65e0\u6127\u524d\u671d\u3002\u5982\u4eca\u6ca7\u6851\u5df2\u53d8\uff0c\u7269\u6362\u661f\u79fb\uff0c\u4e91\u67d0\u4ea6\u5df2\u538c\u5026\u5e72\u6208\uff0c\u4f46\u613f\u4ee5\u95f2\u4e91\u91ce\u9e64\u4e4b\u8eab\uff0c\u5578\u505a \u70df\u971e\uff0c\u8fc7\u592a\u5e73\u5c81\u6708\u3002\u5148\u5e1d\u5df2\u6b7b\uff0c\u5927\u5468\u65e9\u706d\uff0c\u6211\u4e0e\u5f20\u5bb6\u65e9\u65e0\u541b\u81e3\u540d\u4efd\uff0c\u5f20\u5b97\u5468\u9ec4\u53e3\u5c0f\u513f\uff0c\u6562\u7528 \u91d1\u724c\u53ec\u6211\uff01\u201d \u3000\u3000\u77f3\u5929\u94ce\u5403\u4e86\u4e00\u60ca\uff0c\u4ed6\u867d\u7136\u4e0d\u8d5e\u540c\u5e7c\u4e3b\u6240\u4e3a\uff0c\u4f46\u5bf9\u5f20\u58eb\u8bda\u7684\u5b50\u5b59\u8fd8\u662f\u81ea\u5c45\u4e8e\u81e3\u5b50\u7684\u5730\u4f4d\uff0c \u672a\u6562\u50cf\u4e91\u821e\u9633\u90a3\u6837\u51b3\u7edd\u7684\u3002\u542c\u4e91\u821e\u9633\u8fd9\u4e48\u8bf4\u6cd5\uff0c\u5fc3\u4e2d\u4e00\u51db\uff0c\u51b2\u53e3\u95ee\u9053\uff1a\u201c\u90a3\u4e48\u6731\u5143\u748b\u53ec\u4f60\uff0c \u4f60\u4e5f\u662f\u4e0d\u53bb\u7684\u4e86\uff1f\u201d \u3000\u3000\u4e91\u821e\u9633\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u6211\u81ea\u4f5c\u4e3b\u5bb0\uff0c\u72ec\u6765\u72ec\u5f80\uff0c\u53bb\u4e5f\u7531\u6211\uff0c\u4e0d\u53bb\u4e5f\u7531\u6211\uff0c\u8981\u4f60\u591a\u7ba1\u505a\u4ec0 \u4e48\uff1f\u201d\u77f3\u5929\u94ce\u9053\uff1a\u201c\u90a3\u4e48\u6614\u65e5\u7684\u4e00\u73ed\u8001\u670b\u53cb\uff0c\u4f60\u662f\u987e\u4ed6\u4eec\u8fd8\u662f\u4e0d\u987e\u4e86\u3002\u201d\u4e91\u821e\u9633\u53cc\u76ee\u4e00\u5f20\uff0c \u7cbe\u5149\u7535\u5c04\uff0c\u9053\uff1a\u201c\u5b9d\u73e0\u5bf9\u4f60\u8bf4\u4e86\u4e9b\u4ec0\u4e48\u6765\uff1f\u201d\u77f3\u5929\u94ce\u9053\uff1a\u201c\u542c\u8bf4\u4f60\u89c1\u8fc7\u7f57\u91d1\u5cf0\u4e86\uff1f\u201d\u4e91\u821e\u9633 \u9053\uff1a\u201c\u6211\u559c\u6b22\u89c1\u8c01\u4fbf\u89c1\u8c01\uff0c\u6211\u4eca\u665a\u5c31\u4e0d\u559c\u6b22\u89c1\u4f60\uff01\u201d \u3000\u3000\u77f3\u5929\u94ce\u82e6\u7b11\u9053\uff1a\u201c\u4e91\u5144\u65e2\u662f\u8ba8\u538c\u5c0f\u5f1f\uff0c\u5c0f\u5f1f\u544a\u8f9e\uff01\u201d\u4e91\u821e\u9633\u51b7\u7b11\u9053\uff1a\u201c\u4e14\u6162\uff0c\u8fd9\u5e45\u753b\u4f60 \u4e0d\u8981\u4e86\u5417\uff1f\u201d \u3000\u3000\u77f3\u5929\u94ce\u80f8\u812f\u4e00\u633a\uff0c\u5239\u90a3\u4e4b\u95f4\uff0c\u5fc3\u4e2d\u8f6c\u4e86\u597d\u51e0\u4e2a\u5ff5\u5934\uff0c\u6bc5\u7136\u8bf4\u9053\uff1a\u201c\u8fd9\u5e45\u753b\u5148\u5e1d\u5f53\u5e74\u66fe\u90d1 \u91cd\u4ea4\u6258\u5c0f\u5f1f\uff0c\u4e91\u5144\u4eca\u65e5\u65e2\u5df2\u72ec\u884c\u5176\u662f\uff0c\u4e0e\u5927\u5468\u6069\u65ad\u4e49\u7edd\uff0c\u90a3\u4e48\u8fd9\u5e45\u753b\u4ea4\u56de\u5c0f\u5f1f\uff0c\u7559\u5f85\u5e7c\u4e3b\uff0c \u5b9e\u662f\u6700\u597d\u4e0d\u8fc7\u3002\u201d \u3000\u3000\u4e91\u821e\u9633\u4fa7\u76ee\u56de\u5934\uff0c\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u5b9d\u73e0\uff0c\u628a\u753b\u7ed9\u6211\uff01\u201d\u58f0\u8c03\u5e73\u9759\uff0c\u773c\u5149\u5374\u662f\u5145\u6ee1\u6740\u673a\uff01\u4e91 \u592b\u4eba\u6253\u4e86\u4e00\u4e2a\u5bd2\u5664\uff0c\u53eb\u9053\uff1a\u201c\u821e\u9633\uff0c\u4f60\u2014\u2014\u201d\u4e91\u821e\u9633\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u628a\u753b\u9001\u7ed9\u4ed6\u5417\uff1f\u4e0d \u8fc7\uff0c\u8fd9\u753b\u662f\u6211\u5f53\u5e74\u4eb2\u81ea\u53d6\u6765\uff0c\u800c\u4eca\u4e5f\u8be5\u7531\u6211\u4eb2\u624b\u9001\u56de\u624d\u662f\u3002\u201d\u5288\u624b\u593a\u8fc7\u753b\u5377\uff0c\u201c\u563f\u563f\u201d\u4e24\u58f0 \u51b7\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u62ff\u53bb\u5440\uff0c\u5f6d\u548c\u5c1a\u5df2\u6b7b\uff0c\u4f60\u77f3\u5929\u94ce\u800c\u4eca\u5df2\u662f\u5929\u4e0b\u7b2c\u4e00\u6761\u597d\u6c49\uff0c\u6709\u80c6\u6df1\u591c\u5230\u6b64\uff0c \u96be\u9053\u53cd\u800c\u65e0\u80c6\u62ff\u8fd9\u5377\u753b\uff1f\u201d \u3000\u3000\u77f3\u5929\u94ce\u53cc\u773c\u4e00\u7741\uff0c\u95ee\u9053\uff1a\u201c\u821e\u9633\u5144\uff0c\u4f60\u8bf4\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u54b1\u4eec\u5144\u5f1f\u6570\u5341\u5e74\u4ea4\u60c5\uff0c\u8fd9\u753b \u4f60\u82e5\u8981\u7559\u4e0b\uff0c\u5c0f\u5f1f\u4e5f\u4e0d\u613f\u52c9\u5f3a\u3002\u201d\u4e91\u821e\u9633\u6253\u4e86\u4e00\u4e2a\u54c8\u54c8\uff0c\u9053\uff1a\u201c\u4e8f\u4f60\u8fd8\u63d0\u5230\u4ea4\u60c5\u4e24\u4e2a\u5b57\uff0c\u6211 \u4e91\u821e\u9633\u5fc3\u9886\u4e86\uff0c\u4f60\u65e2\u4e0d\u53d6\u753b\uff0c\u53c8\u4e0d\u80af\u8d70\uff0c\u563f\uff0c\u4f60\u5f97\u77e5\u9053\uff0c\u8fd9\u91cc\u53ef\u662f\u6211\u5bb6\uff0c\u4f60\u8d56\u5728\u8fd9\u91cc\u4e0d\u8d70\uff0c \u5f53\u6211\u662f\u597d\u6b3a\u8d1f\u7684\u4e48\uff1f\u201d \u3000\u3000\u77f3\u5929\u94ce\u6012\u9053\uff1a\u201c\u821e\u9633\uff0c\u4f60\u628a\u8bdd\u8bf4\u5f97\u660e\u767d\u4e00\u4e9b\uff0c\u6211\u9a6c\u4e0a\u4fbf\u8d70\u3002\u54fc\uff0c\u54fc\uff01\u4f60\u6c61\u8fb1\u6211\u4e5f\u8fd8\u7f62 \u4e86\uff0c\u4f60\uff0c\u4f60\u2014\u2014\u201d\u60f3\u8bf4\u201c\u4f60\u5c82\u53ef\u635f\u4e86\u5b9d\u73e0\u3002\u201d\u8bdd\u5230\u53e3\u8fb9\uff0c\u7a76\u7adf\u4e0d\u4fbf\u51fa\u53e3\uff0c\u7a81\u89c1\u4e91\u821e\u9633\u9762\u8272\u4e00 \u6c89\uff0c\u8bf4\u9053\uff1a\u201c\u4eca\u65e5\u4e4b\u4e8b\uff0c\u8bf4\u8bdd\u591a\u4f59\u3002\u4f60\u65e2\u4e0d\u8d70\uff0c\u4e91\u67d0\u6b64\u523b\u4fbf\u8981\u5411\u4f60\u9886\u6559\u94c1\u638c\u795e\u7b14\u7684\u529f\u592b\uff01\u201d \u201c\u556a\u201d\u7684\u4e00\u58f0\uff0c\u63b7\u5f00\u753b\u5377\uff0c\u6458\u4e0b\u5899\u4e0a\u7684\u6606\u543e\u5b9d\u5251\u3002 \u3000\u3000\u4e91\u592b\u4eba\u5c16\u53eb\u9053\uff1a\u201c\u821e\u9633\uff01\u4e24\u864e\u76f8\u6597\uff0c\u5fc5\u6709\u4e00\u4f24\uff0c\u4f60\u4eec\u6ca1\u6765\u7531\u53ae\u62fc\u505a\u751a\uff1f\u201d\u4e91\u821e\u9633\u4ef0\u5929\u5927 \u7b11\u9053\uff1a\u201c\u5b9d\u73e0\uff0c\u4f60\u5c45\u7136\u8fd8\u5173\u5fc3\u6211\uff1f\u5927\u4e08\u592b\u65e2\u4e0d\u80fd\u53d6\u4fe1\u4e8e\u59bb\u5b50\uff0c\u53c8\u89c1\u7591\u4e8e\u670b\u53cb\uff0c\u6d3b\u7740\u4e5f\u6ca1\u6709\u4ec0 \u4e48\u610f\u601d\u3002\u6211\u5012\u613f\u610f\u6b7b\u5728\u4ed6\u7684\u638c\u4e0b\uff0c\u53ea\u6015\u4ed6\u5374\u672a\u5fc5\u4f24\u5f97\u4e86\u6211\uff01\u5929\u94ce\uff0c\u4eae\u51fa\u7b14\u6765\uff0c\u51fa\u9662\u5b50\u53bb\uff01\u201d \u4e91\u592b\u4eba\u9762\u8272\u60e8\u767d\uff0c\u60f3\u8d77\u4e08\u592b\u5341\u591a\u5e74\u6765\u5bf9\u5979\u7684\u51b7\u6f20\uff0c\u800c\u4eca\u53c8\u7adf\u662f\u4e0d\u51c9\u5982\u65af\uff01\u987f\u65f6\u6c14\u585e\u80f8\u4e2d\uff0c\u7126 \u6025\u6028\u6124\u4e4b\u60c5\u7ea0\u7ed3\u4e00\u7247\uff0c\u4f46\u89c9\u5fc3\u5934\u9ebb\u6728\uff0c\u56db\u811a\u9178\u8f6f\uff0c\u8bf4\u4e0d\u51fa\u534a\u53e5\u8bdd\u6765\u3002 \u3000\u3000\u53ea\u542c\u5f97\u77f3\u5929\u94ce\u4e5f\u6253\u4e86\u4e00\u4e2a\u54c8\u54c8\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u5df2\u7ec3\u6210\u8fbe\u6469\u5251\u672f\uff0c\u65e2\u7136\u82e6\u82e6\u8981\u4f30 \u91cf\u5c0f\u5f1f\uff0c\u77f3\u67d0\u6ca1\u5948\u4f55\u53ea\u597d\u820d\u547d\u966a\u541b\u5b50\u4e86\uff01\u201d\u8981\u77e5\u77f3\u5929\u94ce\u4e5f\u662f\u4e00\u4ee3\u5927\u4fa0\uff0c\u82e5\u518d\u9000\u8ba9\uff0c\u90a3\u5c31\u4e0e\u8eab \u5206\u60a0\u5173\u3002\u4e91\u821e\u9633\u5df2\u628a\u4ed6\u903c\u5230\u4e86\u4e0d\u80fd\u56de\u8f6c\u7684\u5730\u6b65\u3002 \u3000\u3000\u6708\u4eae\u6c89\u897f\uff0c\u5df2\u662f\u4e94\u66f4\u65f6\u5206\uff0c\u4e24\u4eba\u4e00\u58f0\u4e0d\u54cd\u7684\u8d70\u51fa\u9662\u5b50\uff0c\u4f46\u542c\u5f97\u201c\u55d6\u201d\u7684\u4e00\u58f0\uff0c\u4e91\u821e\u9633\u5b9d \u5251\u51fa\u9798\uff0c\u5bd2\u5149\u7535\u5c04\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5929\u94ce\uff0c\u6211\u4e0d\u662f\u6210\u5fc3\u5360\u4f60\u7684\u4fbf\u5b9c\uff0c\u638c\u4e0a\u7684\u529f\u592b\u662f\u4f60\u8fdc\u80dc\u4e8e\u6211\uff0c \u6211\u53ea\u597d\u52a8\u7528\u5175\u5203\u3002\u8fd9\u628a\u5251\u4e43\u662f\u65ad\u91d1\u5207\u7389\u7684\u5b9d\u5251\uff0c\u4f60\u5c0f\u5fc3\u4e86\u3002\u201d\u77f3\u5929\u94ce\u9053\uff1a\u201c\u591a\u8c22\u63d0\u70b9\uff0c\u5ba2\u4e0d \u6f5c\u4e3b\uff0c\u8bf7\u8fdb\u62db\u5427\uff01\u201d \u3000\u3000\u4e91\u821e\u9633\u624b\u634f\u5251\u51b3\uff0c\u84e6\u5730\u4e00\u58f0\u957f\u5578\uff0c\u6606\u4ed1\u5251\u626c\u7a7a\u4e00\u95ea\uff0c\u4e00\u62db\u201c\u6d41\u661f\u8d76\u6708\u201d\uff0c\u5251\u5c16\u6643\u52a8\uff0c\u6296 \u51fa\u4e86\u4e09\u6735\u5251\u82b1\uff0c\u5de6\u523a\u201c\u767d\u6d77\u7a74\u201d\uff0c\u53f3\u523a\u201c\u4e73\u7a81\u7a74\u201d\uff0c\u4e2d\u523a\u201c\u73e0\u98d2\u7a74\u201d\uff0c\u867d\u7136\u8fd8\u53ca\u4e0d\u4e0a\u4e03\u4fee\u9053 \u4eba\u4e00\u62db\u4e03\u5f0f\uff0c\u8fde\u523a\u4e03\u5904\u7a74\u9053\u7684\u5feb\u6377\u65e0\u4f26\uff0c\u4f46\u8fd9\u4e00\u5251\u523a\u51fa\uff0c\u98d8\u5ffd\u83ab\u6d4b\uff0c\u4f3c\u5de6\u4f3c\u53f3\u4f3c\u4e2d\uff0c\u53eb\u4eba\u65e0 \u53ef\u6349\u6478\uff0c\u90a3\u5947\u8be1\u53d8\u5e7b\uff0c\u5374\u53c8\u8fdc\u5728\u4e03\u4fee\u5251\u6cd5\u4e4b\u4e0a\u4e86\u3002 \u3000\u3000\u77f3\u5929\u94ce\u5403\u4e86\u4e00\u60ca\uff0c\u60f3\u9053\uff1a\u201c\u8fbe\u6469\u5251\u6cd5\uff0c\u679c\u7136\u975e\u540c\u5c0f\u53ef\uff01\u201d\u6a2a\u7b14\u4e00\u5c01\uff0c\u4f46\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00 \u58f0\uff0c\u706b\u661f\u56db\u6e85\uff0c\u77f3\u5929\u94ce\u7528\u4e86\u4e00\u4e2a\u201c\u8f74\u201d\u5b57\u8bc0\uff0c\u7b14\u5c16\u4e00\u642d\u5251\u8eab\uff0c\u5c06\u4e91\u821e\u9633\u7684\u5b9d\u5251\u5f39\u51fa\u5916\u95e8\uff0c\u4f46 \u89c9\u864e\u53e3\u53d1\u70ed\uff0c\u5224\u5b98\u7b14\u51e0\u4e4e\u62ff\u634f\u4e0d\u4f4f\uff01\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u4e91\u821e\u9633\u957f\u5251\u4e00\u5708\uff0c\u6d88\u53bb\u4e86\u77f3\u5929\u94ce\u529b \u52b2\uff0c\u4e00\u62db\u201c\u7389\u5e26\u56f4\u8170\u201d\uff0c\u5251\u5149\u5982\u73af\uff0c\u62e6\u8170\u75be\u5377\uff0c\u77f3\u5929\u94ce\u53cd\u624b\u4e00\u638c\uff0c\u94c1\u7b14\u6a2a\u5c01\uff0c\u4ee5\u653b\u4e3a\u5b88\uff0c\u594b \u65b9\u8fde\u89e3\u4e09\u62db\u3002 \u3000\u3000\u53ea\u542c\u5f97\u98d2\u98d2\u8fde\u58f0\uff0c\u4e0e\u98ce\u58f0\u76f8\u5e94\uff0c\u7cbe\u8292\u51b7\u7535\uff0c\u76d6\u8fc7\u4e86\u661f\u6708\u5fae\u5149\u3002\u4e91\u592b\u4eba\u501a\u7a97\u89c2\u6218\uff0c\u7aef\u7684\u662f \u900f\u9aa8\u4f24\u5fc3\uff0c\u4e00\u4e2a\u662f\u540c\u5e8a\u5171\u6795\u7684\u4e08\u592b\uff0c\u4e00\u4e2a\u662f\u9752\u6885\u7af9\u9a6c\u7684\u597d\u53cb\uff0c\u800c\u4ee4\u5374\u7adf\u7136\u4ee5\u6027\u547d\u76f8\u62fc\uff0c\u66f4\u96be \u582a\u7684\u662f\uff1a\u81ea\u5df1\u4e5f\u88ab\u5377\u5165\u65cb\u6da1\uff0c\u82e5\u7136\u4e0a\u524d\u529d\u89e3\uff0c\u65e0\u5f02\u706b\u4e0a\u52a0\u6cb9\uff0c\u4e8b\u60c5\u53ea\u6709\u66f4\u574f\uff01\u4e91\u592b\u4eba\u8d8a\u770b\u8d8a \u662f\u4f24\u5fc3\uff0c\u4f24\u5fc3\u5230\u4e86\u6781\u70b9\uff0c\u53cd\u800c\u4e00\u7247\u832b\u7136\uff0c\u8111\u5b50\u91cc\u7a7a\u8361\u8361\uff0c\u4e5f\u4e0d\u77e5\u60f3\u4e9b\u4ec0\u4e48\uff0c\u4f46\u89c9\u8eab\u5982\u69c1\u6728\uff0c \u5fc3\u4f3c\u6b7b\u7070\uff0c\u7d22\u6027\u95ed\u4e86\u773c\u775b\uff0c\u4efb\u7531\u4ed6\u9f99\u4e89\u864e\u6597\uff01 \u3000\u3000\u5ffd\u542c\u5f97\u4e00\u7247\u65ad\u91d1\u621b\u7389\u4e4b\u58f0\uff0c\u53ee\u5f53\u5bc6\u54cd\uff0c\u4e91\u592b\u4eba\u4e0d\u7531\u81ea\u5df1\u7684\u53c8\u7741\u5f00\u4e86\u773c\u775b\uff0c\u4f46\u89c1\u4e91\u821e\u9633\u4e00 \u62db\u201c\u795e\u9f99\u5165\u6d77\u201d\uff0c\u5251\u5149\u6a2a\u63a0\uff0c\u903c\u5f97\u77f3\u5929\u94ce\u7684\u5224\u5b98\u7b14\u786c\u63a5\u786c\u67b6\uff0c\u867d\u7136\u77f3\u5929\u94ce\u5728\u786c\u63a5\u786c\u67b6\u4e4b\u4e2d\uff0c \u8fd8\u662f\u7528\u4e86\u6781\u4e0a\u4e58\u7684\u5378\u529b\u5de7\u52b2\uff0c\u5251\u5149\u63a0\u8fc7\uff0c\u90a3\u7cbe\u94c1\u6240\u94f8\u7684\u5224\u5b98\u7b14\u5df2\u662f\u65ad\u4e86\u4e09\u5904\u7f3a\u53e3\u3002 \u3000\u3000\u4e0d\u4f46\u4e91\u592b\u4eba\u770b\u5f97\u60ca\u5fc3\u52a8\u9b44\uff0c\u9648\u7384\u673a\u6697\u4e2d\u5077\u542c\uff0c\u4e5f\u51e0\u4e4e\u8981\u900f\u4e0d\u8fc7\u6c14\u6765\u3002\u7d27\u5f20\u4e2d\u6697\u6697\u60cb\u60dc\uff1a \u8fd9\u4e24\u4eba\u4e43\u662f\u5f53\u4eca\u4e4b\u4e16\u6570\u4e00\u6570\u4e8c\u7684\u9ad8\u624b\uff0c\u77f3\u5929\u94ce\u4ee5\u524d\u7684\u540d\u6c14\u5728\u4e91\u821e\u9633\u4e4b\u4e0a\uff0c\u4f46\u4e91\u821e\u9633\u5982\u4eca\u5df2\u7ec3 \u6210\u4e86\u8fbe\u6469\u5251\u6cd5\uff0c\u8c01\u9ad8\u8c01\u4f4e\uff0c\u975e\u7ecf\u8f83\u91cf\uff0c\u5c31\u4e0d\u5f97\u800c\u77e5\u4e86\u3002\u8fd9\u4e24\u5927\u9ad8\u624b\u7684\u62fc\u6597\uff0c\u4e00\u751f\u4e2d\u4e5f\u672a\u5fc5\u6709 \u6b64\u673a\u7f18\u80fd\u89c1\u4e00\u6b21\uff0c\u53ef\u60dc\u9648\u7384\u673a\u5374\u53ea\u80fd\u4ee5\u8033\u4ee3\u76ee\uff0c\u90a3\u4e9b\u795e\u5947\u7684\u62db\u6570\uff0c\u53ea\u80fd\u5728\u5175\u5668\u78b0\u51fb\u58f0\u4e2d\u60f3\u50cf \u5f97\u4e4b\u3002 \u3000\u3000\u4e91\u592b\u4eba\u5fc3\u4e0d\u6b32\u89c2\uff0c\u773c\u775b\u5374\u662f\u4e0d\u7531\u81ea\u4e3b\uff0c\u4f46\u89c1\u4e91\u821e\u9633\u4e00\u5251\u5feb\u4f3c\u4e00\u5251\uff0c\u4e09\u5341\u62db\u8fc7\u540e\uff0c\u7adf\u628a\u77f3 \u5929\u94ce\u5168\u8eab\u7b3c\u7f69\u5728\u5251\u5149\u4e4b\u4e0b\u3002\u4e91\u592b\u4eba\u5927\u662f\u5403\u60ca\uff0c\u53c8\u7981\u4e0d\u4f4f\u6697\u6697\u8be7\u5f02\u3002\u8fd9\u4e24\u4eba\u6b66\u529f\u7684\u6df1\u6d45\u53ea\u6709\u5979 \u6700\u4e3a\u77e5\u9053\uff0c\u8bba\u7406\u8bf4\u6765\uff0c\u5979\u4e08\u592b\u7684\u5251\u6cd5\u867d\u6781\u795e\u5999\uff0c\u77f3\u5929\u94ce\u4e5f\u4e0d\u8be5\u5728\u4e09\u5341\u62db\u4e4b\u5185\uff0c\u4fbf\u9893\u52bf\u6bd5\u5448\uff01 \u3000\u3000\u53ea\u542c\u5f97\u53c8\u662f\u51e0\u58f0\u53ee\u53ee\u5f53\u5f53\u7684\u8fde\u58f0\u54cd\uff0c\u4e91\u821e\u9633\u5251\u52bf\u5982\u73af\uff0c\u5c06\u77f3\u5929\u950b\u7684\u94c1\u7b14\u53c8\u524a\u53bb\u4e86\u4e00\u7247\uff0c \u5ffd\u5730\u8df3\u51fa\u5708\u5b50\uff0c\u6309\u5251\u559d\u9053\uff1a\u201c\u77f3\u5929\u94ce\u4f60\u4e0a\u6765\u4e4b\u65f6\u672a\u7528\u5168\u529b\uff0c\u5982\u4eca\u4f60\u89c1\u8bc6\u4e86\u6211\u7684\u5251\u6cd5\uff0c\u6211\u4e91\u821e \u9633\u53ef\u662f\u8981\u4f60\u8ba9\u7684\u4e48\uff1f\u518d\u6765\u6253\u8fc7\uff0c\u4ed4\u7ec6\u63a5\u62db\uff01\u201d\u957f\u5251\u4e00\u8d77\uff0c\u6162\u6162\u5730\u5212\u4e86\u534a\u4e2a\u5706\u5f27\uff0c\u5f85\u5f97\u8bdd\u58f0\u8bf4 \u5b8c\uff0c\u201c\u5237\u201d\u7684\u53c8\u662f\u4e00\u5251\u523a\u51fa\uff01 \u3000\u3000\u77f3\u5929\u94ce\u4e0e\u4e91\u821e\u9633\u540c\u65f6\u51fa\u8eab\uff0c\u540c\u4e8b\u4e00\u4e3b\uff0c\u4e8c\u5341\u5e74\u524d\u5e76\u80a9\u4f5c\u6218\uff0c\u66fe\u7ecf\u60a3\u96be\uff0c\u60c5\u5982\u624b\u8db3\uff1b\u8fd9\u6b21 \u867d\u7136\u88ab\u4e91\u821e\u9633\u903c\u5f97\u52a8\u624b\uff0c\u5fc3\u4e2d\u4f24\u75db\u4e4b\u6781\uff0c\u4e0a\u6765\u4e4b\u65f6\uff0c\u786e\u662f\u672a\u5c3d\u5168\u529b\uff0c\u54ea\u77e5\u4e91\u821e\u9633\u7684\u5251\u672f\u5df2\u7ecf \u7ec3\u6210\uff0c\u5a01\u529b\u5947\u5927\uff0c\u9ad8\u624b\u6bd4\u6597\uff0c\u5c82\u5bb9\u5f97\u4e1d\u6beb\u9000\u8ba9\uff1f\u521d\u63a5\u62db\u65f6\uff0c\u7a0d\u7a0d\u8e0c\u8e87\uff0c\u4fbf\u88ab\u4e91\u821e\u9633\u62a2\u5c3d\u4e0a \u98ce\uff0c\u51e0\u4e4e\u4f24\u5728\u4ed6\u7684\u6606\u543e\u5251\u4e0b\uff01 \u3000\u3000\u773c\u89c1\u4e91\u821e\u9633\u7684\u5251\u950b\u53c8\u5230\uff0c\u5bd2\u6c14\u6c81\u808c\uff0c\u9510\u98ce\u523a\u9aa8\uff0c\u5251\u52bf\u6bd4\u9002\u624d\u8fd8\u51cc\u5389\u51e0\u5206\u3002\u77f3\u5929\u94ce\u77e5\u9053\u82e5 \u8fd8\u9000\u8ba9\uff0c\u90a3\u5c31\u662f\u6709\u6b7b\u65e0\u751f\uff0c\u53ea\u597d\u594b\u53d1\u5168\u529b\uff0c\u201c\u547c\u201d\u7684\u4e00\u638c\u626b\u51fa\uff0c\u540c\u65f6\u5224\u5b98\u7b14\u4e00\u4e2a\u76d8\u65cb\uff0c\u5e7b\u51fa \u4e86\u5343\u91cd\u7b14\u5f71\uff0c\u516d\u62db\u4e00\u8fc7\uff0c\u8fde\u70b9\u4e91\u821e\u9633\u7684\u4e09\u5341\u9053\u5927\u7a74\uff0c\u53cc\u65b9\u90fd\u662f\u62a2\u653b\uff0c\u767b\u65f6\u6740\u5f97\u4e2a\u96be\u5206\u96be\u89e3\uff01 \u3000\u3000\u9648\u7384\u673a\u5fc3\u4e2d\u6697\u6697\u53f9\u670d\uff1a\u8fd9\u4e91\u821e\u9633\u786e\u662f\u4e00\u4ee3\u6b66\u5b66\u5927\u5e08\u7684\u8eab\u4efd\uff0c\u5728\u6b64\u7b49\u6027\u547d\u76f8\u6251\u4e4b\u9645\uff0c\u4e5f\u4e0d \u80af\u5360\u5f3a\u654c\u7684\u4e1d\u6beb\u4fbf\u5b9c\u3002\u5176\u5b9e\u9648\u7384\u673a\u4ee5\u8033\u4ee3\u76ee\uff0c\u4ed6\u54ea\u91cc\u77e5\u9053\u4e91\u821e\u9633\u8fd9\u756a\u505a\u4f5c\uff0c\u5176\u4e2d\u522b\u542b\u6df1\u610f\u3002 \u3000\u3000\u4e91\u592b\u4eba\u8fd9\u65f6\u4e5f\u770b\u51fa\u4e86\u77f3\u5929\u94ce\u521d\u4e0a\u6765\u65f6\u662f\u6709\u610f\u76f8\u8ba9\uff0c\u5979\u6df1\u77e5\u4e08\u592b\u7684\u4e3a\u4eba\uff0c\u7981\u4e0d\u4f4f\u5fc3\u4e2d\u611f \u6168\uff0c\u60f3\u9053\uff1a\u201c\u77f3\u5929\u94ce\u5012\u662f\u8fd8\u6709\u65e7\u65f6\u60c5\u4efd\uff0c\u4ed6\u5374\u62db\u62db\u72e0\u8fa3\uff0c\u5206\u660e\u662f\u6709\u7740\u2018\u53cc\u96c4\u4e0d\u5e76\u7acb\u2019\u7684\u5ff5 \u5934\uff0c\u5b58\u5fc3\u8981\u628a\u77f3\u5929\u94ce\u7f6e\u4e8e\u6b7b\u5730\u4e86\u3002\u4ed6\u65e2\u7136\u770b\u51fa\u77f3\u5929\u94ce\u4e0a\u624b\u4e4b\u65f6\u76f8\u8ba9\uff0c\u4f55\u4ee5\u8981\u7b49\u5230\u4e09\u5341\u62db\u4e4b\u540e \u65b9\u624d\u63ed\u7834\uff0c\u8fd9\u5206\u660e\u662f\u505a\u7ed9\u6211\u770b\u7684\uff0c\u5440\uff0c\u60f3\u4e0d\u5230\u4ed6\u5bf9\u59bb\u5b50\u4e5f\u85cf\u6709\u673a\u5fc3\u3002\u201d \u3000\u3000\u8981\u77e5\u4e91\u821e\u9633\u5728\u5360\u5c3d\u4e0a\u98ce\u4e4b\u9645\uff0c\u65b9\u8ba9\u5bf9\u65b9\u5598\u606f\uff0c\u91cd\u632f\u65d7\u9f13\uff0c\u8868\u9762\u770b\u6765\u662f\u6177\u6168\u5927\u65b9\uff0c\u5b9e\u5219\u4ed6 \u5728\u4e09\u5341\u62db\u4e4b\u4e2d\uff0c\u4e58\u7740\u77f3\u5929\u94ce\u4e0a\u624b\u4e4b\u65f6\u7684\u9000\u8ba9\u52a3\u52bf\uff0c\u5f3a\u653b\u731b\u6253\uff0c\u4e0d\u4f46\u6d88\u7c4d\u4e86\u77f3\u5929\u94ce\u7684\u4e0d\u5c11\u771f \u529b\uff0c\u800c\u4e14\u5c06\u4ed6\u7684\u5224\u5b98\u7b14\u635f\u4e86\u51e0\u5904\u7f3a\u53e3\uff0c\u53c8\u524a\u949d\u4e86\u4ed6\u7684\u7b14\u950b\uff0c\u6559\u4ed6\u7684\u201c\u795e\u7b14\u201d\u5a01\u529b\u6253\u4e86\u6298\u6263\uff0c \u5df2\u662f\u5360\u4e86\u5927\u5927\u7684\u4fbf\u5b9c\uff0c\u4ed6\u8fd9\u6837\u505a\u4f5c\uff0c\u786e\u662f\u60f3\u505a\u7ed9\u59bb\u5b50\u770b\u7684\u3002\u4ed6\u4e5f\u77e5\u9053\u59bb\u5b50\u8fdf\u65e9\u4f1a\u770b\u51fa\u77f3\u5929\u94ce \u6709\u610f\u76f8\u8ba9\uff0c\u6545\u6b64\u5728\u5360\u4e86\u4fbf\u5b9c\u4e4b\u540e\uff0c\u4fbf\u903c\u77f3\u5929\u94ce\u5168\u529b\u65bd\u4e3a\uff0c\u7136\u540e\u6740\u4e86\u4ed6\uff0c\u8fd9\u624d\u663e\u5f97\u51fa\u81ea\u5df1\u786e\u662f \u5929\u4e0b\u7b2c\u4e00\u6761\u597d\u6c49\uff0c\u624d\u53eb\u59bb\u5b50\u4f69\u670d\u3002\u5c82\u6599\u4ed6\u8fd9\u756a\u673a\u5fc3\uff0c\u5374\u53cd\u88ab\u59bb\u5b50\u770b\u7834\uff01 \u3000\u3000\u4e91\u821e\u9633\u81ea\u6043\u5251\u6cd5\uff0c\u53c8\u517c\u5360\u4e86\u4fbf\u5b9c\u4e4b\u540e\uff0c\u7cbe\u795e\u500d\u957f\uff0c\u539f\u4ee5\u4e3a\u53d6\u80dc\u5e76\u4e0d\u8270\u96be\u3002\u54ea\u77e5\u77f3\u5929\u94ce\u8fd9 \u5341\u591a\u5e74\u6765\uff0c\u6b66\u529f\u4e5f\u5e76\u672a\u66fe\u4e22\u4e0b\uff0c\u94c1\u638c\u795e\u7b14\u7684\u62db\u6570\u6bd4\u4e8c\u5341\u5e74\u524d\u8d8a\u53d1\u795e\u5999\u4e86\uff01\u4f46\u89c1\u4ed6\u638c\u631f\u52b2\u98ce\uff0c \u7b14\u70b9\u7a74\u9053\uff0c\u638c\u529b\u9633\u521a\uff0c\u7b14\u6cd5\u9634\u67d4\uff0c\u9976\u662f\u4e91\u821e\u9633\u4e00\u5251\u5feb\u4f3c\u4e00\u5251\uff0c\u4ed6\u62db\u62db\u6263\u5f97\u7d27\u5bc6\uff0c\u4e91\u821e\u9633\u7adf\u65e0 \u6cd5\u518d\u5360\u534a\u70b9\u4fbf\u5b9c\u3002 \u3000\u3000\u6597\u8f6c\u661f\u79fb\uff0c\u7389\u514d\u897f\u5815\uff0c\u9662\u5b50\u5185\u5df2\u662f\u66d9\u5149\u5fae\u73b0\uff0c\u4e24\u5927\u9ad8\u624b\u6597\u4e86\u4e00\u767e\u6765\u62db\uff0c\u5140\u662f\u65d7\u9f13\u76f8\u5f53\uff0c \u534a\u65a4\u516b\u4e24\uff0c\u8fd9\u65f6\u5927\u5bb6\u90fd\u6740\u5f97\u6027\u8d77\uff0c\u9ad8\u547c\u9163\u6597\uff0c\u518d\u6ca1\u6709\u534a\u70b9\u5bb9\u60c5\uff01\u9661\u7136\u95f4\u4f46\u89c1\u77f3\u5929\u94ce\u638c\u6cd5\u4e00 \u53d8\uff0c\u4e00\u638c\u63a5\u7740\u4e00\u638c\uff0c\u521a\u731b\u65e0\u4f26\uff0c\u638c\u529b\u50ac\u7d27\uff0c\u52bf\u5982\u6392\u5c71\u5012\u6d77\uff0c\u8361\u5f97\u4e91\u821e\u9633\u7684\u5251\u5149\u56db\u5904\u6d41\u6563\uff0c\u800c \u90a3\u652f\u5224\u5b98\u7b14\u4e5f\u5982\u72c2\u98ce\u9aa4\u96e8\u822c\u7684\u75be\u70b9\u75be\u6233\uff0c\u76f4\u628a\u4e91\u592b\u4eba\u4e5f\u770b\u5f97\u80c6\u6218\u5fc3\u60ca\uff01 \u3000\u3000\u4e91\u821e\u9633\u4e00\u58f0\u957f\u5578\uff0c\u53eb\u9053\uff1a\u201c\u4e09\u5341\u516d\u624b\u5929\u7f61\u638c\u6cd5\uff0c\u5a01\u529b\u679c\u662f\u96c4\u5947\uff01\u4f46\u82e5\u8bf4\u4fbf\u80fd\u51a0\u7edd\u6b66\u6797\uff0c \u53ea\u6015\u4e5f\u8fd8\u672a\u5fc5\uff01\u201d\u53cd\u5251\u4e00\u5288\uff0c\u52b2\u98ce\u6fc0\u8361\uff0c\u58f0\u5982\u88c2\u5e1b\uff0c\u90a3\u6d41\u6563\u7684\u5251\u5149\uff0c\u91cd\u53c8\u51dd\u805a\u8d77\u6765\uff0c\u5339\u7ec3\u822c \u7684\u6a2a\u5377\u8fc7\u53bb\u3002\u4f46\u89c1\u4ed6\u5251\u5c16\u4e0a\u597d\u50cf\u9876\u7740\u5343\u65a4\u91cd\u7269\u4e00\u6837\uff0c\u4e1c\u4e00\u6307\uff0c\u897f\u4e00\u5212\uff0c\u4f3c\u662f\u5403\u529b\u975e\u5e38\uff0c\u5251\u52bf \u4e5f\u6bd4\u524d\u7f13\u6162\u4e86\u8bb8\u591a\uff0c\u4f46\u5251\u950b\u6240\u5230\u4e4b\u5904\uff0c\u529b\u9053\u5374\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u77f3\u5929\u950b\u8fd0\u638c\u6210\u98ce\uff0c\u914d\u4ee5\u795e\u7b14\u7684\u8fde \u73af\u5de7\u6253\uff0c\u4e5f\u4e0d\u8fc7\u582a\u582a\u62b5\u6321\u5f97\u4f4f\u3002 \u3000\u3000\u77f3\u5929\u94ce\u7684\u201c\u5929\u7f61\u4e09\u5341\u516d\u638c\u201d\uff0c\u8d8a\u5230\u540e\u6765\uff0c\u8d8a\u4e3a\u5389\u5bb3\uff0c\u4e91\u821e\u9633\u7684\u5251\u6cd5\u4e5f\u6108\u51fa\u6108\u5947\uff0c\u771f\u529b\u8d2f \u6ce8\u5251\u5c16\uff0c\u970d\u970d\u5c55\u5f00\uff0c\u7adf\u9690\u9690\u4f3c\u95fb\u98ce\u96f7\u4e4b\u58f0\u3002\u4f46\u89c1\u638c\u98ce\u5251\u5f71\uff0c\u6b64\u5f80\u5f7c\u6765\uff0c\u679d\u53f6\u7eb7\u98de\uff0c\u6885\u82b1\u96e8 \u843d\uff0c\u4e0d\u6d88\u591a\u4e45\uff0c\u90a3\u51e0\u6811\u76db\u5f00\u7684\u6885\u82b1\uff0c\u90fd\u53ea\u5269\u4e0b\u4e86\u5149\u79c3\u79c3\u7684\u682a\u5e72\uff01\u4e0d\u662f\u7ed9\u638c\u98ce\u626b\u843d\uff0c\u4fbf\u662f\u7ed9\u5251 \u950b\u524a\u65ad\u4e86\u3002 \u3000\u3000\u4e91\u592b\u4eba\u5438\u4e86\u4e00\u53e3\u51c9\u6c14\uff0c\u60f3\u9053\uff1a\u201c\u4ed6\u5bf9\u8fd9\u51e0\u682a\u6885\u82b1\uff0c\u73cd\u7231\u4e4b\u6781\uff0c\u800c\u4eca\u7adf\u4e0d\u60dc\u4f7f\u51fa\u8fbe\u6469\u5251\u6cd5 \u4e2d\u6700\u5a01\u731b\u7684\u4f0f\u9b54\u5f0f\uff0c\u6467\u6bc1\u6885\u679d\uff0c\u663e\u89c1\u662f\u6740\u673a\u5df2\u8d77\u4e86\uff01\u201d\u5fc3\u4e2d\u7126\u6025\uff0c\u5374\u662f\u6beb\u65e0\u529e\u6cd5\u963b\u6b62\uff01 \u3000\u3000\u8f6c\u773c\u4e4b\u95f4\uff0c\u53c8\u6597\u4e86\u4e8c\u5341\u6765\u62db\uff0c\u77f3\u5929\u94ce\u5df2\u4f7f\u5230\u5929\u7f61\u638c\u6cd5\u6700\u540e\u4e09\u638c\uff0c\u8fd9\u4e09\u638c\u662f\u5929\u7f61\u638c\u6cd5\u4e2d\u7684 \u7cbe\u534e\u6240\u805a\uff0c\u5a01\u731b\u65e0\u4f26\uff0c\u53ea\u542c\u5f97\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u6761\u7c97\u5982\u4eba\u81c2\u6811\u679d\u5e94\u624b\u800c\u6298\uff0c\u638c\u529b\u5954\u96f7\u822c\u5288 \u81f3\uff0c\u5251\u5149\u500f\u7684\u53c8\u88ab\u9707\u6563\uff0c\u6709\u5982\u6ce2\u5fc3\u8361\u6708\uff0c\u95ea\u8d77\u4e86\u70b9\u70b9\u94f6\u5149\uff0c\u53c8\u5982\u9ed1\u591c\u7e41\u57a6\uff0c\u6b92\u843d\u5982\u96e8\uff01 \u3000\u3000\u4e91\u821e\u9633\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u5f6d\u548c\u5c1a\u5f53\u5e74\u548c\u4ed6\u6700\u4e3a\u77e5\u5df2\uff0c\u542c\u8bf4\u66fe\u4f20\u6388\u4ed6\u7384\u529f\u8981\u8bc0\uff0c\u770b\u6765\u8fd9 \u4e00\u638c\u7684\u5a01\u529b\uff0c\u4e0d\u900a\u4e8e\u5f6d\u548c\u5c1a\u5f53\u5e74\uff01\u201d\u5fc3\u5ff5\u672a\u5df2\uff0c\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u77f3\u5929\u94ce\u7684\u7b2c\u4e8c\u638c\u53c8\u5df2\u5288\u5230\uff0c \u4e91\u821e\u9633\u56de\u5251\u9632\u8eab\uff0c\u4f46\u542c\u5f97\u55e1\u55e1\u4e4b\u58f0\uff0c\u4e0d\u7edd\u4e8e\u8033\uff0c\u5251\u5c16\u7adf\u662f\u88ab\u638c\u529b\u9707\u8361\u5f97\u6643\u52a8\u4e0d\u4f11\uff01 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u77f3\u5929\u94ce\u7684\u7b2c\u4e09\u638c\u53c8\u81f3\uff0c\u9002\u624d\u90a3\u4e24\u638c\u5a01\u52bf\u731b\u6781\uff0c\u8fd9\u4e00\u638c\u6253\u51fa\uff0c\u5374\u662f\u65e0\u58f0 \u65e0\u606f\uff0c\u4e91\u821e\u9633\u6014\u4e86\u4e00\u6014\uff0c\u7a81\u5730\u5fc3\u4e2d\u4e00\u51db\uff0c\u4f46\u89c9\u90a3\u638c\u529b\u6709\u5982\u6697\u6d41\u6025\u6e4d\uff0c\u529b\u53ef\u541e\u821f\uff0c\u9976\u662f\u7528\u4e86\u5343 \u65a4\u5760\u7684\u201c\u91cd\u8eab\u6cd5\u201d\uff0c\u4e5f\u7981\u4e0d\u4f4f\u8ddf\u7740\u4ed6\u7684\u638c\u529b\u65cb\u8f6c\uff0c\u811a\u6b65\u4e00\u556c\u8dcc\u5012\u5730\u4e0a\uff01 \u3000\u3000\u4e91\u592b\u4eba\u5927\u60ca\u5931\u8272\uff0c\u60ca\u53eb\u4e4b\u58f0\uff0c\u8fd8\u672a\u53ca\u547c\u51fa\uff0c\u4f46\u89c1\u4e91\u821e\u9633\u95ea\u7535\u822c\u7684\u5728\u5730\u4e0a\u6253\u4e86\u51e0\u4e2a\u76d8\u65cb\uff0c \u5251\u5c16\u500f\u7684\u4e0a\u6311\uff0c\u9661\u7136\u95f4\u4e00\u8dc3\u800c\u8d77\uff0c\u53ea\u8fd9\u4e00\u8d77\u4e00\u4f0f\u7684\u5239\u90a3\u4e4b\u95f4\uff0c\u4ed6\u5df2\u63a5\u8fde\u4f7f\u51fa\u4e03\u624b\u602a\u62db\uff0c\u5c06\u77f3 \u5929\u94ce\u7684\u6781\u521a\u731b\u7684\u638c\u52bf\u5c3d\u90fd\u6d88\u89e3\u3002 \u3000\u3000\u518d\u770b\u4e4b\u65f6\uff0c\u5f62\u52bf\u5927\u53d8\uff0c\u4f46\u89c1\u4e91\u821e\u9633\u6d3b\u50cf\u4e00\u4e2a\u9189\u6c49\uff0c\u811a\u6b65\u8dc4\u8dc4\u8e09\u8e09\uff0c\u4e00\u628a\u5b9d\u5251\u6307\u4e1c\u6253\u897f\uff0c \u6307\u5357\u6253\u5317\uff0c\u770b\u4f3c\u6beb\u65e0\u7ae0\u6cd5\uff0c\u5b9e\u662f\u5947\u5999\u7edd\u4f26\uff0c\u500f\u7136\u800c\u6765\uff0c\u5bc2\u7136\u800c\u53bb\uff0c\u5f53\u771f\u662f\u5230\u4e86\u610f\u5728\u5251\u5148\uff0c\u52a8 \u5982\u8131\u5154\uff0c\u9759\u5982\u5904\u5b50\u7684\u6781\u4e0a\u4e58\u5883\u754c\uff01\u4e91\u592b\u4eba\u4e5f\u61c2\u5f97\u8fbe\u6469\u5251\u6cd5\uff0c\u4e5f\u6599\u4e0d\u5230\u4e08\u592b\u7adf\u7136\u7ec3\u5f97\u7cbe\u5999\u5982 \u65af\uff01\u539f\u6765\u4ed6\u8fd9\u79cd\u5251\u6cd5\u4e43\u662f\u7a77\u5341\u516b\u5e74\u4e4b\u529b\uff0c\u5728\u7cbe\u719f\u4e86\u8fbe\u6469\u5251\u6cd5\u4e4b\u540e\uff0c\u63e3\u6469\u53d8\u5316\u51fa\u6765\u7684\uff0c\u8fde\u59bb\u5b50 \u9762\u524d\uff0c\u4e5f\u4ece\u6ca1\u6709\u4f7f\u8fc7\uff01 \u3000\u3000\u77f3\u5929\u94ce\u7adf\u88ab\u8fd9\u5251\u6cd5\u903c\u5f97\u8fde\u8fde\u540e\u9000\uff0c\u4f46\u4ed6\u867d\u5c45\u52a3\u52bf\uff0c\u6b65\u6cd5\u638c\u6cd5\uff0c\u4ecd\u7136\u4e1d\u6beb\u4e0d\u4e71\uff0c\u53ea\u89c1\u4ed6\u8e0f \u7740\u4e94\u884c\u516b\u5366\u65b9\u4f4d\uff0c\u8fdb\u9000\u8d8b\u907f\uff0c\u4e2d\u89c4\u4e2d\u77e9\uff0c\u6a2a\u638c\u62a4\u80f8\uff0c\u51fa\u7b14\u653b\u654c\uff0c\u5239\u90a3\u4e4b\u95f4\uff0c\u53c8\u8fc7\u4e86\u4e09\u4e8c\u5341 \u62db\uff01 \u3000\u3000\u539f\u6765\u4e91\u821e\u9633\u719f\u77e5\u77f3\u5929\u94ce\u7684\u6b66\u529f\u5e95\u7ec6\uff0c\u77e5\u9053\u4ed6\u66fe\u5f97\u5f6d\u548c\u5c1a\u7684\u4f20\u6388\uff0c\u5728\u5185\u529b\u7684\u6df1\u539a\u4e0a\uff0c\u81ea\u5df1 \u6b8a\u96be\u4e0e\u4e4b\u76f8\u6bd4\uff0c\u6240\u4ee5\u4e00\u5f00\u59cb\u672a\u6562\u4f7f\u51fa\u8fd9\u8def\u5251\u6cd5\uff0c\u53ea\u662f\u60f3\u5c3d\u529e\u6cd5\u6d88\u8017\u4ed6\u7684\u771f\u529b\uff0c\u5f85\u5230\u77f3\u5929\u94ce\u7684 \u4e09\u5341\u516d\u624b\u5929\u7f61\u638c\u6cd5\u582a\u582a\u4f7f\u5c3d\uff0c\u9510\u6c14\u5df2\u6298\uff0c\u6e10\u8fd1\u8870\u7aed\u4e4b\u9645\uff0c\u8fd9\u624d\u7a81\u7136\u800c\u8d77\uff0c\u4f7f\u51fa\u6740\u624b\u7edd\u62db\uff01 \u3000\u3000\u4e09\u5341\u62db\u8fc7\u540e\uff0c\u4e91\u821e\u9633\u7684\u5251\u62db\u8d8a\u903c\u8d8a\u7d27\uff0c\u77f3\u5929\u94ce\u7684\u638c\u529b\u5708\u5b50\u4e5f\u8d8a\u6765\u8d8a\u6536\u7f16\u4e86\u3002\u4f46\u8fd9\u4e24\u4eba\u90fd \u662f\u7edd\u9876\u9ad8\u624b\uff0c\u5728\u6b64\u6d88\u5f7c\u957f\u7684\u53d8\u5316\u65f6\u673a\uff0c\u5fae\u5999\u4e4b\u6781\uff0c\u8fde\u4e91\u592b\u4eba\u4e5f\u672a\u66fe\u770b\u5f97\u51fa\u6765\u3002\u4f46\u89c1\u4e08\u592b\u7684\u5251 \u52bf\u5982\u8679\uff0c\u4f3c\u5df2\u7acb\u4e8e\u4e0d\u8d25\u4e4b\u5730\uff0c\u4f46\u8fd8\u6599\u4e0d\u5230\u77f3\u5929\u94ce\u5df2\u662f\u5371\u673a\u6697\u4f0f\uff0c\u6027\u547d\u5df2\u60ac\u4e8e\u547c\u5438\u4e4b\u95f4\u3002 \u3000\u3000\u518d\u8fc7\u6570\u62db\uff0c\u53ea\u542c\u5f97\u201c\u5237\u201d\u7684\u4e00\u58f0\uff0c\u77f3\u5929\u94ce\u80a9\u5934\u4e2d\u4e86\u4e00\u5251\uff0c\u63a5\u7740\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u4e91\u821e\u9633\u4e5f \u4e2d\u4e86\u4ed6\u7684\u4e00\u638c\uff0c\u4e91\u592b\u4eba\u9a87\u6781\u800c\u547c\uff0c\u4ee5\u4e3a\u8fd9\u4e24\u4eba\u5fc5\u5b9a\u540c\u53d7\u91cd\u4f24\uff0c\u800c\u77f3\u5929\u94ce\u7684\u638c\u529b\u6709\u5f00\u7891\u88c2\u77f3\u4e4b \u80fd\uff0c\u4e08\u592b\u6240\u53d7\u7684\u4f24\u5fc5\u7136\u66f4\u91cd\u3002\u54ea\u77e5\u8f6c\u773c\u4e4b\u95f4\uff0c\u8fd9\u4e24\u4eba\u53c8\u5df2\u6597\u5728\u4e00\u8d77\uff0c\u4e91\u821e\u9633\u7684\u5251\u6cd5\u867d\u7136\u7a0d \u5f31\uff0c\u800c\u77f3\u5929\u94ce\u7684\u638c\u6cd5\u5374\u66f4\u4e3a\u8fdf\u6ede\uff0c\u770b\u6765\u7adf\u662f\u77f3\u5929\u94ce\u6240\u53d7\u7684\u4f24\u8f83\u91cd\uff01\u539f\u6765\u77f3\u5929\u94ce\u8fd9\u65f6\u5df2\u662f\u6c14\u8870 \u529b\u7aed\uff0c\u638c\u529b\u65e9\u5df2\u51cf\u4e86\u4e00\u534a\uff0c\u4e91\u821e\u9633\u662f\u6545\u610f\u62fc\u7740\u53d7\u4ed6\u4e00\u638c\uff0c\u4e58\u673a\u5c06\u4ed6\u523a\u4f24\u7684\u3002 \u3000\u3000\u8fd9\u4e00\u573a\u6076\u6597\u770b\u6765\u5df2\u662f\u6e10\u8fd1\u5c3e\u58f0\uff0c\u8fdc\u4e0d\u53ca\u5148\u524d\u7684\u731b\u70c8\u4e86\u3002\u4f46\u4e91\u592b\u4eba\u5fc3\u4e2d\u7684\u7740\u6025\uff0c\u90a3\u5374\u662f\u6bd4 \u5148\u524d\u6c89\u91cd\u5f97\u591a\uff0c\u53ea\u6015\u4e0d\u4e45\u4e4b\u524d\u8fd8\u4e0e\u81ea\u5df1\u503e\u5410\u8c08\u5fc3\u7684\u77f3\u5929\u94ce\u4e0d\u4e45\u5c31\u8981\u8840\u67d3\u5c18\u571f\uff0c\u60f3\u8d77\u9002\u624d\u77f3\u5929 \u94ce\u7684\u95f2\u8bdd\u5bb6\u5e38\uff0c\u5ffd\u7136\u8d77\u4e86\u4e00\u4e2a\u6781\u5947\u602a\u7684\u5ff5\u5934\uff1a\u201c\u6211\u53ea\u9053\u6211\u5df2\u53ef\u601c\uff0c\u90a3\u77f3\u5929\u94ce\u7684\u59bb\u5b50\uff0c\u4ece\u6765\u672a \u5f97\u8fc7\u4e08\u592b\u7684\u60c5\u7231\uff0c\u8fde\u4e08\u592b\u7684\u5fc3\u4e8b\u4e5f\u534a\u70b9\u4e0d\u77e5\u3002\u82e5\u7136\u77f3\u5929\u950b\u4eca\u665a\u6b7b\u4e86\uff0c\u5979\u8fd8\u8981\u66ff\u4ed6\u629a\u5b64\u517b\u5bb6\uff0c \u72ec\u5b88\u7a7a\u95fa\uff0c\u671f\u5f85\u5b9e\u5df2\u6beb\u65e0\u5e0c\u671b\u7684\u4e08\u592b\u7684\u97f3\u8baf\uff0c\u5c82\u4e0d\u662f\u6bd4\u6211\u66f4\u53ef\u601c\uff0c\u4f55\u51b5\u5979\u53c8\u4e0d\u61c2\u6b66\u529f\uff0c\u77f3\u5929 \u94ce\u7684\u513f\u5b50\u8c01\u4e3a\u4ed6\u629a\u517b\u6210\u6750\u3002\u201d\u5fc3\u4e2d\u6253\u4e86\u4e00\u4e2a\u5bd2\u5664\uff0c\u6b63\u5f85\u4e0d\u987e\u4e00\u5207\uff0c\u594b\u8eab\u800c\u51fa\uff0c\u5ffd\u542c\u5f97\u4e91\u821e\u9633 \u4e00\u58f0\u53f1\u54a4\uff0c\u77f3\u5929\u94ce\u7684\u90a3\u652f\u5224\u5b98\u7b14\u65ad\u4e3a\u4e24\u622a\uff0c\u4e91\u592b\u4eba\u521a\u53eb\u5f97\u4e00\u58f0\u201c\u821e\u9633\uff01\u201d\u77f3\u5929\u94ce\u5df2\u662f\u7ffb\u8eab\u4ec6 \u5730\uff0c\u518d\u722c\u8d77\u65f6\uff0c\u8eab\u4e0a\u6ee1\u662f\u8840\u82b1\uff01\u4e91\u821e\u9633\u90a3\u4e00\u9053\u5251\u5149\u8fc7\u5904\uff0c\u7adf\u5728\u4ed6\u8eab\u4e0a\u523a\u4e86\u5341\u516b\u9053\u4f24\u53e3\uff01 \u3000\u3000\u4f46\u89c1\u77f3\u5929\u94ce\u98a4\u5dcd\u5dcd\u7684\u8d70\u4e86\u4e24\u6b65\uff0c\u60e8\u7136\u7b11\u9053\uff1a\u201c\u821e\u9633\u5144\uff0c\u4ece\u4eca\u4e4b\u540e\uff0c\u4f60\u7684\u6b66\u529f\u5929\u4e0b\u7b2c\u4e00\uff0c \u4e16\u4e0a\u65e0\u4eba\u518d\u53ef\u4e0e\u4f60\u4e89\u950b\uff0c\u5c0f\u5f1f\u795d\u8d3a\u5566\uff01\u201d\u529b\u7aed\u58f0\u5636\uff0c\u8bdd\u4e00\u8bf4\u5b8c\uff0c\u7acb\u523b\u53c8\u683d\u5012\u4e86\uff01\u4e91\u821e\u9633\u773c\u5149 \u4e00\u77a5\uff0c\u5ffd\u89c1\u4ed6\u80a9\u5934\u4e0a\u8863\u670d\u88ab\u5251\u5c16\u6311\u5f00\u4e4b\u5904\uff0c\u904d\u5e03\u9ed1\u70b9\uff0c\u7981\u4e0d\u4f4f\u5931\u58f0\u53eb\u9053\uff1a\u201c\u54a6\uff0c\u539f\u6765\u4f60\u53d7\u4e86 \u84b2\u575a\u7684\u6bd2\u722a\u4e4b\u4f24\uff01\u201d\u8fd9\u624d\u77e5\u9053\u77f3\u5929\u94ce\u662f\u53d7\u4f24\u4e4b\u540e\uff0c\u5f3a\u8fd0\u5185\u529f\uff0c\u4e00\u9762\u62b5\u5fa1\u4f53\u5185\u7684\u6bd2\u6c14\uff0c\u4e00\u9762\u4e0e \u81ea\u5df1\u52a8\u624b\u7684\uff0c\u82e5\u7136\u4ed6\u672a\u53d7\u4f24\uff0c\u8fd9\u80dc\u8d1f\u8fd8\u771f\u96be\u6599\uff01 \u3000\u3000\u4e91\u821e\u9633\u53eb\u4e86\u4e24\u58f0\uff0c\u53ef\u662f\u77f3\u5929\u94ce\u5df2\u6c38\u8fdc\u4e0d\u4f1a\u7b54\u5e94\u4e86\uff01\u4e91\u821e\u9633\u624b\u628a\u5b9d\u5251\uff0c\u6014\u6014\u7684\u8bf4\u4e0d\u51fa\u534a\u53e5 \u8bdd\u6765\uff0c\u4ed6\u9664\u4e86\u5fc3\u76ee\u4e2d\u6700\u5927\u7684\u52b2\u654c\uff0c\u6362\u6765\u7684\u5374\u53ea\u662f\u5185\u759a\u4e0e\u51c4\u51c9\uff01 \u3000\u3000------------- \u3000\u3000\u91cd\u5e86\u96ea\u513f \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u8fd8\u5251\u5947\u60c5\u5f55\u300b\u2014\u2014\u7b2c\u516d\u56de\u3000\u51e4\u6cca\u9e33\u98d8 \u6881\u7fbd\u751f\u300a\u8fd8\u5251\u5947\u60c5\u5f55\u300b \u7b2c\u516d\u56de\u3000\u51e4\u6cca\u9e33\u98d8 \u3000\u3000\u6b8b\u661f\u660e\u706d\uff0c\u6653\u9732\u6cbe\u8863\uff0c\u9662\u5b50\u91cc\u9759\u5bc2\u5982\u6b7b\uff0c\u5468\u56f4\u7684\u7a7a\u6c14\u90fd\u597d\u4f3c\u51b7\u5f97\u8981\u51dd\u7ed3\u8d77\u6765\uff0c\u5ffd\u542c\u5f97\u5624 \u5624\u7684\u54ed\u6ce3\u4e4b\u58f0\uff0c\u4f3c\u5229\u9488\u4e00\u6837\u523a\u7a7f\u4e86\u5bc2\u9759\u7684\u7a7a\u6c14\uff0c\u4e91\u821e\u9633\u773c\u5149\u4e00\u77a5\uff0c\u53ea\u89c1\u4ed6\u7684\u59bb\u5b50\u6367\u7740\u753b\u5377\uff0c \u4e00\u6b65\u4e00\u6b65\u7684\u8d70\u51fa\u8001\u6885\u6811\u8fb9\u7684\u6708\u7259\u6d1e\u95e8\uff0c\u8fde\u770b\u4e5f\u4e0d\u770b\u4ed6\u4e00\u773c\uff01 \u3000\u3000\u8fd9\u5239\u90a3\u95f4\uff0c\u4e91\u821e\u9633\u5fc3\u5934\u98a4\u6817\uff0c\u597d\u50cf\u7075\u9b42\u4e5f\u8131\u79bb\u4e86\u8eaf\u58f3\uff0c\u201c\u5b9d\u73e0\u201d\u8fd9\u4e24\u4e2a\u5b57\u5728\u820c\u5c16\u4e0a\u6253\u6eda \u4e86\u6570\u5341\u767e\u904d\uff0c\u5374\u662f\u53eb\u4e0d\u51fa\u6765\u3002\u4e91\u592b\u4eba\u4ece\u77f3\u5929\u950b\u7684\u5c38\u4f53\u65c1\u8fb9\u8d70\u8fc7\uff0c\u8bf4\u9053\uff1a\u201c\u5929\u94ce\uff0c\u4f60\u653e\u5fc3\uff0c\u8fd9 \u5377\u753b\u6211\u5fc5\u5b9a\u9001\u5230\u4f60\u7684\u5bb6\u4e2d\uff0c\u6211\u8981\u770b\u5f85\u4f60\u7684\u513f\u5b50\uff0c\u5c31\u50cf\u770b\u5f85\u7d20\u7d20\u4e00\u6837\u3002\u201d\u8bf4\u8bdd\u7684\u58f0\u97f3\u5f88\u8f7b\u5f88 \u8f7b\uff0c\u4f3c\u662f\u6015\u60ca\u9192\u4e86\u77f3\u5929\u94ce\u4e00\u6837\uff0c\u4f46\u542c\u5728\u4e91\u821e\u9633\u5fc3\u4e2d\uff0c\u6bcf\u4e00\u4e2a\u5b57\u90fd\u597d\u4f3c\u4e00\u6839\u5229\u7bad\uff0c\u4e91\u821e\u9633\u832b\u7136 \u5931\u63aa\uff0c\u62ac\u8d77\u5934\u6765\uff0c\u4ed6\u59bb\u5b50\u7684\u80cc\u5f71\u5df2\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u597d\u4e45\uff0c\u597d\u4e45\uff0c\u4e91\u821e\u9633\u624d\u53eb\u51fa\u58f0\u6765\uff0c\u90a3\u662f\u5145\u6ee1\u4e86\u5931\u610f\u4e0e\u6050\u60e7\u7684\u53eb\u58f0\uff0c\u4f46\u8fd8\u6709\u6bd4\u59bb\u5b50\u51fa\u8d70\u4ee4 \u4ed6\u66f4\u6050\u60e7\u7684\u4e8b\u60c5\u53d1\u751f\uff0c\u4ed6\u521a\u521a\u79fb\u52a8\u811a\u6b65\uff0c\u5374\u89c1\u4ed6\u7684\u5973\u513f\u4e0d\u77e5\u662f\u4ec0\u4e48\u65f6\u5019\u51fa\u6765\u7684\uff0c\u8fd9\u65f6\u6b63\u501a\u5728 \u8001\u6885\u6811\u4e0a\uff0c\u90a3\u4e00\u53cc\u660e\u5982\u79cb\u6c34\u7684\u773c\u775b\uff0c\u4e5f\u662f\u5145\u6ee1\u4e86\u6050\u60e7\uff0c\u773c\u5149\u548c\u795e\u60c5\u90fd\u5947\u602a\u6781\u4e86\uff0c\u5c31\u50cf\u4ece\u6765\u4e0d \u8ba4\u8bc6\u4ed6\u4f3c\u7684\uff01 \u3000\u3000\u4e91\u821e\u9633\u5403\u529b\u53eb\u9053\uff1a\u201c\u7d20\u7d20\uff01\u201d\u4e91\u7d20\u7d20\u7684\u773c\u5149\u5728\u4ed6\u9762\u4e0a\u4e00\u63a0\u800c\u8fc7\uff0c\u597d\u50cf\u770b\u5230\u4e86\u4ec0\u4e48\u4ee4\u4eba\u5bb3 \u6015\u7684\u4e1c\u897f\uff0c\u5012\u9000\u4e09\u6b65\uff0c\u5ffd\u5730\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u6211\u90fd\u542c\u89c1\u5566\uff0c\u6211\u90fd\u77e5\u9053\u5566\uff01\u4e0d\u8981\u8fd1\u6211\uff01\u201d\u4e91\u821e\u9633\u5168 \u8eab\u6218\u6296\uff0c\u84e6\u7136\u53f9\u4e86\u53e3\u6c14\uff0c\u72c2\u6b4c\u4f3c\u54ed\uff1a\u201c\u5ff5\u5929\u5730\u4e4b\u60a0\u60a0\u516e\uff0c\u77e5\u6211\u5176\u8c01\uff1f\u53f9\u82f1\u96c4\u4e4b\u8fdf\u66ae\u516e\uff0c\u80dc\u4ea6 \u4f55\u559c\uff1f\u8d25\u4ea6\u4f55\u60b2\uff1f\u4f24\u6d6e\u751f\u4e4b\u6613\u901d\u516e\uff0c\u51e4\u6cca\u9e33\u98d8\u516e\u6211\u8c01\u4e0e\u968f\uff1f\u201d\u6b4c\u58f0\u6e10\u8fdc\u6e10\u5bc2\uff0c\u4e91\u7d20\u7d20\u5fc3\u9178\u6cea \u54bd\uff0c\u4e0d\u7531\u81ea\u5df1\u7684\u5931\u58f0\u53eb\u9053\uff1a\u201c\u7239\u7239\uff0c\u7239\u7239\uff01\u201d\u4f46\u4ed6\u7239\u7239\u5df2\u542c\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u4e91\u7d20\u7d20\u501a\u7740\u6885\u679d\uff0c\u4f24\u5fc3\u75db\u54ed\uff0c\u5ffd\u5730\u611f\u5230\u6709\u4e00\u53ea\u6e29\u6696\u7684\u624b\u638c\u8f7b\u8f7b\u7684\u629a\u6478\u5979\u7684\u5934\u53d1\uff0c\u4e00\u4e2a\u6781 \u6e29\u67d4\u7684\u58f0\u97f3\u5728\u5979\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u7d20\u7d20\uff0c\u7d20\u7d20\uff0c\u4f60\u522b\u54ed\u5566\uff01\u201d\u4e91\u7d20\u7d20\u62ac\u8d77\u5934\u6765\u53eb\u4e86\u58f0\u201c\u7384\u673a\uff01\u201d \u6cea\u4e0b\u5f97\u66f4\u591a\u4e86\u3002 \u3000\u3000\u9648\u7384\u673a\u4e5f\u4e0d\u77e5\u8bf4\u4e9b\u4ec0\u4e48\u8bdd\u597d\uff0c\u53ea\u6709\u638f\u51fa\u4e1d\u5dfe\uff0c\u8f7b\u8f7b\u7ed9\u5979\u62ed\u6cea\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u4e91\u7d20\u7d20\u62bd\u564e\u8bf4 \u9053\uff1a\u201c\u5440\uff0c\u6211\u7684\u7239\u7239\uff01\u53ef\u6068\u7684\u7239\u7239\uff0c\u53ef\u601c\u7684\u7239\u7239\uff01\u7384\u673a\uff0c\u4f60\u4e0d\u77e5\u9053\uff0c\u6211\u81ea\u5c0f\u5c31\u628a\u7239\u7239\u5f53\u505a\u8fd9 \u4e16\u4e0a\u72ec\u4e00\u65e0\u4e8c\u7684\u82f1\u96c4\uff01\u201d\u9648\u7384\u673a\u9053\uff1a\u201c\u5f53\u4eca\u4e4b\u4e16\uff0c\u7684\u786e\u65e0\u4eba\u662f\u4f60\u7239\u7239\u654c\u624b\uff01\u201d \u3000\u3000\u4e91\u7d20\u7d20\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ece\u4eca\u65e5\u8d77\uff0c\u6211\u7239\u7239\u6b66\u529f\u786e\u662f\u5929\u4e0b\u7b2c\u4e00\u3002\u4f46\u6211\u5fc3\u76ee\u4e2d\u7684\u5076\u50cf\u5df2\u7ecf\u7834\u788e \u65e0\u9057\uff01\u4ed6\u518d\u4e0d\u662f\u6211\u6614\u65e5\u6240\u60f3\u50cf\u7684\u82f1\u96c4\u4e86\u3002\u4ed6\u5077\u4e86\u5916\u7956\u7236\u7684\u5251\u8c31\uff0c\u903c\u8d70\u4e86\u6211\u7684\u6bcd\u4eb2\uff0c\u6740\u4e86\u4ed6\u7684 \u597d\u53cb\uff0c\u56da\u7981\u4e86\u4e0a\u5b98\u5929\u91ce\uff0c\u8fd8\u8981\u66ff\u90a3\u4e2a\u4ec0\u4e48\u9526\u8863\u536b\u6307\u6325\u6349\u62ff\u4ed6\u65e7\u65e5\u7684\u540c\u50da\uff0c\u8fd9\u4e9b\u4e8b\u60c5\u6211\u90fd\u77e5\u9053 \u5566\uff01\u201d \u3000\u3000\u9648\u7384\u673a\u9053\uff1a\u201c\u56da\u7981\u4e0a\u5b98\u5929\u91ce\uff1f\u55ef\uff0c\u4e0a\u5b98\u5929\u91ce\u73b0\u5728\u54ea\u513f\uff1f\u201d\u4e91\u7d20\u7d20\u9053\uff1a\u201c\u6211\u6628\u665a\u5df2\u89c1\u8fc7\u4e0a \u5b98\u5929\u91ce\u4e86\uff0c\u8bb8\u591a\u4e8b\u60c5\u5c31\u662f\u4ed6\u544a\u8bc9\u6211\u7684\uff01\u8fd9\u4e24\u65e5\u6765\u6211\u4e5f\u89c1\u5230\u542c\u5230\u4e86\u4e00\u4e9b\u4e8b\u60c5\uff0c\u6211\u76f8\u4fe1\u4e0a\u5b98\u5929\u91ce \u6ca1\u6709\u9a97\u6211\u3002\u55ef\uff0c\u6211\u7239\u7239\u771f\u662f\u90a3\u6837\u4e00\u4e2a\u574f\u4eba\uff1f\u201d \u3000\u3000\u9648\u7384\u673a\u5c06\u4e91\u7d20\u7d20\u7d27\u7d27\u62b1\u7740\uff0c\u4f46\u89c1\u5979\u773c\u5149\u4e2d\u5145\u6ee1\u51c4\u82e6\u3002\u5440\uff0c\u8fd9\u4e16\u754c\u4e0a\u8fd8\u6709\u4ec0\u4e48\u4e8b\u60c5\u6bd4\u513f\u5973 \u5bf9\u7236\u6bcd\u5931\u671b\u66f4\u4e3a\u4ee4\u4eba\u5fc3\u75db\uff1f\u9648\u7384\u673a\u65e0\u6cd5\u6170\u89e3\uff0c\u7981\u4e0d\u4f4f\u4eb2\u4e86\u4e00\u4e0b\u5979\u7684\u8138\u988a\uff0c\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u4e5f\u4e0d \u5168\u662f\u4f60\u7236\u4eb2\u7684\u9519\u3002\u201d\u4e91\u7d20\u7d20\u8be7\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u8981\u884c\u523a\u4ed6\u7684\uff1f\u201d\u9648\u7384\u673a\u53f9\u9053\uff1a\u201c\u8fd9\u4e9b\u662f\u662f\u975e\u975e\uff0c \u53ea\u6015\u4e00\u65f6\u4e4b\u95f4\u5b9e\u662f\u96be\u660e\u3002\u201d\u62ac\u5934\u4e00\u671b\uff0c\u9633\u5149\u5df2\u7ecf\u7167\u8fdb\u9662\u5b50\uff0c\u9648\u7384\u673a\u6ee1\u5fc3\u6005\u60d8\uff0c\u8f7b\u8f7b\u653e\u5f00\u4e86\u4e91 \u7d20\u7d20\u7684\u53cc\u624b\uff0c\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4e91\u7d20\u7d20\u9053\uff1a\u201c\u6211\u5988\u5988\u8d70\u4e86\uff0c\u6211\u7239\u7239\u8d70\u4e86\uff0c\u4f60\u4e5f\u8981\u8d70\u4e86\u3002\u201d\u9648\u7384\u673a\u9053\uff1a\u201c\u55ef\uff0c\u4f60\u53eb\u6211\u8d70\u6211 \u4fbf\u8d70\uff01\u201d\u4e91\u7d20\u7d20\u7a81\u7136\u53c8\u62bd\u564e\u53eb\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8d70\u5427\uff01\u201d\u9648\u7384\u673a\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u7d20\u7d20\uff0c\u4f60\u771f\u7684 \u8981\u6211\u8d70\uff1f\u201d\u4e91\u7d20\u7d20\u9053\uff1a\u201c\u6211\u4e0d\u613f\u4f60\u8d70\uff0c\u4f46\u6211\u66f4\u4e0d\u613f\u522b\u4eba\u6068\u6211\uff01\u201d \u3000\u3000\u9648\u7384\u673a\u8be7\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u4e91\u7d20\u7d20\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u6709\u4e00\u4f4d\u5fc3\u4e0a\u7684\u4eba\u513f\uff0c\u90a3\u662f\u4e00\u4f4d\u4e16\u4e0a\u9876\u9876 \u53ef\u7231\u7684\u59d1\u5a18\u3002\u201d\u9648\u7384\u673a\u5931\u58f0\u7b11\u9053\uff1a\u201c\u4e16\u4e0a\u54ea\u80fd\u6709\u6bd4\u4f60\u66f4\u53ef\u7231\u7684\u59d1\u5a18\uff1f\u8fd9\u8bdd\u5927\u7ea6\u662f\u4e0a\u5b98\u5929\u91ce\u8bf4 \u7684\u3002\u201d\u4e91\u7d20\u7d20\u9053\uff1a\u201c\u4e0a\u5b98\u5929\u91ce\u4f55\u5fc5\u8981\u5bf9\u6211\u8bf4\u5047\u3002\u201d \u3000\u3000\u9648\u7384\u673a\u7b11\u9053\uff1a\u201c\u90a3\u4f4d\u59d1\u5a18\u662f\u4e0a\u5b98\u5929\u91ce\u5fc3\u76ee\u4e2d\u9876\u9876\u53ef\u7231\u7684\u59d1\u5a18\uff0c\u6211\u5fc3\u76ee\u4e2d\u9876\u53ef\u7231\u7684\u59d1\u5a18\u53ea \u6709\u4f60\uff01\u201d\u4e91\u7d20\u7d20\u773c\u775b\u5145\u6ee1\u7591\u60d1\uff0c\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u771f\u7684\uff1f\u201d\u9648\u7384\u673a\u9053\uff1a\u201c\u4e0a\u5b98\u5929\u91ce\u7231\u90a3\u4f4d\u59d1\u5a18\u80dc \u4e8e\u7231\u4ed6\u81ea\u5df1\u3002\u4ed6\u5374\u4ee5\u4e3a\u6211\u548c\u90a3\u4f4d\u59d1\u5a18\u7ed3\u5408\u4f1a\u662f\u4e00\u6bb5\u7f8e\u6ee1\u59fb\u7f18\uff0c\u5176\u5b9e\u6211\u538b\u6839\u513f\u5c31\u6ca1\u6709\u8fd9\u4e48\u60f3 \u8fc7\u3002\u6211\u5c61\u6b21\u5bf9\u4ed6\u8bf4\u4ed6\u90fd\u4e0d\u4fe1\uff0c\u7d20\u7d20\uff0c\u96be\u9053\u4f60\u4e5f\u4e0d\u76f8\u4fe1\u6211\u4e48\uff1f\u201d \u3000\u3000\u4e91\u7d20\u7d20\u773c\u4e2d\u95ea\u51fa\u559c\u60a6\u7684\u5149\u5f69\uff0c\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4e0a\u5b98\u5929\u91ce\u9a82\u6211\uff0c\u539f\u6765\u4ed6\u662f\u6015\u6211\u7834\u574f\u4e86\u4f60\u4eec\u7f8e \u6ee1\u7684\u59fb\u7f18\u3002\u201d\u9648\u7384\u673a\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u4e00\u540c\u53bb\u89c1\u4ed6\uff0c\u5c06\u4ed6\u653e\u51fa\u6765\u3002\u201d\u4e91\u7d20\u7d20\u9053\uff1a\u201c\u4e0d\uff0c\u4ed6\u4e0d\u80af \u8d70\uff01\u201d\u9648\u7384\u673a\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u4ed6\u4e0d\u80af\u8d70\uff1f\u201d\u4e91\u7d20\u7d20\u9053\uff1a\u201c\u662f\u5440\uff0c\u4ed6\u6628\u665a\u8bf4\uff0c\u5c31\u662f\u6211\u7239\u7239\u8bf7\u4ed6\u51fa \u6765\uff0c\u4ed6\u4e5f\u4e0d\u8d70\u3002\u201d \u3000\u3000\u9648\u7384\u673a\u5fc3\u4e2d\u5927\u7591\uff0c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4f60\u653e\u4ed6\u4ed6\u4e5f\u4e0d\u8d70\uff1f\u8fd9\u4eba\u7684\u813e\u6c14\u771f\u602a\u3002\u201d\u4e91\u7d20\u7d20\u5ffd\u5730\u4f4e\u5934 \u8bf4\u9053\uff1a\u201c\u6211\u559c\u6b22\u4ed6\u8fd9\u4e2a\u813e\u6c14\u3002\u55ef\uff0c\u7384\u673a\uff0c\u4f60\u4e5f\u80fd\u50cf\u4ed6\u4e00\u6837\u4e48\uff1f\u201d\u9648\u7384\u673a\u8be7\u9053\uff1a\u201c\u8981\u6211\u50cf \u4ed6\uff1f\u201d\u84e6\u5730\u5fc3\u4e2d\u96ea\u4eae\uff0c\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u662f\u7684\uff0c\u6211\u4e5f\u4f1a\u50cf\u4ed6\u5bf9\u90a3\u4f4d\u59d1\u5a18\u4e00\u6837\u5bf9\u4f60\u3002\u6211\u7231\u4f60\u80dc\u4e8e\u7231 \u6211\u81ea\u5df1\u3002\u8981\u4e0d\u7136\u6211\u6628\u665a\u4e5f\u4e0d\u4f1a\u5077\u5077\u7684\u6765\u5566\uff01\u201d \u3000\u3000\u4e91\u7d20\u7d20\u53c8\u559c\u53c8\u7f9e\uff0c\u5a07\u547c\u4e00\u58f0\uff0c\u88ab\u9648\u7384\u673a\u7d27\u7d27\u6402\u5728\u6000\u91cc\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u4e91\u7d20\u7d20\u55d4\u9053\uff1a\u201c\u6211\u5598 \u4e0d\u8fc7\u6c14\u6765\u5566\u3002\u201d\u9648\u7384\u673a\u4e00\u7b11\u653e\u677e\u4e86\u624b\uff0c\u9053\uff1a\u201c\u7d20\u7d20\uff0c\u8bf7\u4f60\u5e26\u6211\u4e00\u540c\u770b\u4e0a\u5b98\u5929\u91ce\u53bb\u3002\u201d \u3000\u3000\u4e91\u7d20\u7d20\u6574\u4e86\u4e00\u4e0b\u8863\u88f3\uff0c\u7275\u4e86\u9648\u7384\u673a\u7684\u624b\uff0c\u8d70\u51fa\u540e\u95e8\uff0c\u7ecf\u8fc7\u4e86\u4e00\u6761\u66f2\u66f2\u6298\u6298\u7684\u5c0f\u5f84\uff0c\u6ca1\u591a \u4e45\u5c31\u8d70\u5230\u4e00\u4e2a\u5c71\u6d1e\u7684\u524d\u9762\uff0c\u6d1e\u53e3\u4e24\u6247\u539a\u6728\u5927\u95e8\u7d27\u7d27\u5173\u7740\u3002\u4e91\u7d20\u7d20\u9053\uff1a\u201c\u8fd9\u4e2a\u5c71\u6d1e\u6211\u7239\u7239\u5c06\u5b83 \u5e03\u7f6e\u4f5c\u7ec3\u529f\u7684\u9759\u5ba4\uff0c\u6211\u4e5f\u662f\u6628\u665a\u624d\u7b2c\u4e00\u6b21\u5077\u8fdb\u53bb\u7684\u3002\u4e0a\u5b98\u5929\u91ce\u5c31\u88ab\u56da\u7981\u5728\u91cc\u9762\u3002\u201d\u8d70\u5230\u524d \u9762\uff0c\u4e91\u7d20\u7d20\u9053\uff1a\u201c\u4f60\u5c06\u95e8\u4e0a\u7684\u94c1\u73af\u5de6\u8f6c\u4e09\u8f6c\uff0c\u53f3\u8f6c\u4e09\u8f6c\uff0c\u95e8\u5c31\u5f00\u4e86\u3002\u201d\u9648\u7384\u673a\u6b63\u60f3\u4f9d\u6cd5\u65bd \u4e3a\uff0c\u624b\u89e6\u6728\u95e8\uff0c\u5ffd\u89c9\u6728\u8d28\u6709\u5f02\uff0c\u8f7b\u8f7b\u4e00\u63a8\uff0c\u90a3\u4e24\u6247\u5927\u95e8\u7adf\u7136\u5012\u4e0b\uff0c\u788e\u88c2\u6210\u65e0\u6570\u5c0f\u5757\uff0c\u5c31\u50cf\u7eb8 \u7cca\u7684\u4e00\u822c\uff01\u4e91\u7d20\u7d20\u5931\u58f0\u53eb\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u662f\u600e\u4e48\u641e\u7684\uff1f\u201d \u3000\u3000\u8fd9\u4e24\u6247\u6728\u95e8\u4e43\u662f\u7528\u8d3a\u5170\u5c71\u4e2d\u7684\u6a61\u6728\u6240\u5236\uff0c\u6728\u8d28\u575a\u539a\uff0c\u5c31\u662f\u7528\u5200\u65a7\u6765\u65ab\u4e5f\u8981\u8d39\u5f88\u5927\u7684\u529b \u6c14\uff0c\u7136\u800c\u73b0\u5728\u7adf\u662f\u8f7b\u8f7b\u4e00\u63a8\u4fbf\u50cf\u7eb8\u7cca\u822c\u7684\u5012\u584c\u4e86\uff0c\u800c\u4e14\u4e91\u7d20\u7d20\u6628\u665a\u6765\u8fc7\uff0c\u8fd9\u95e8\u8fd8\u662f\u4e1d\u6beb\u6ca1\u6709 \u5f02\u72b6\u3002 \u3000\u3000\u4e91\u7d20\u7d20\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u775b\uff0c\u5f80\u9648\u7384\u673a\u9762\u4e0a\u4e00\u77a7\uff0c\u53ea\u89c1\u4ed6\u7684\u8138\u4e0a\u4e5f\u662f\u5145\u6ee1\u4e86\u9a87\u5f02\u7684 \u795e\u8272\uff01\u4e24\u4eba\u540c\u65f6\u4f38\u624b\u89e6\u90a3\u788e\u88c2\u7684\u95e8\u677f\uff0c\u4f46\u89c9\u6728\u8d28\u677e\u8f6f\uff0c\u7a0d\u4e00\u7528\u529b\uff0c\u4fbf\u88ab\u634f\u6210\u7c89\u5c51\u3002\u9648\u7384\u673a \u9053\uff1a\u201c\u8fd9\u662f\u88ab\u5185\u5bb6\u638c\u529b\u6240\u9707\u8361\u7684\u3002\u5f04\u574f\u5927\u95e8\u7684\u8fd9\u4e2a\u4eba\u60f3\u662f\u6709\u610f\u663e\u9732\u795e\u901a\uff0c\u628a\u539a\u6728\u7684\u5185\u90e8\u90fd\u7834 \u574f\u4e86\uff0c\u5916\u8868\u5374\u4e00\u70b9\u4e5f\u770b\u4e0d\u51fa\u6765\u3002\u201d\u4e91\u7d20\u7d20\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u662f\u88ab\u5185\u5bb6\u638c\u529b\u6240\u9707\u8361\u7684\u3002\u7136\u800c\u5f53\u4eca \u5929\u4e0b\uff0c\u8c01\u6709\u8fd9\u79cd\u5185\u5bb6\u638c\u529b\uff1f\u201d \u3000\u3000\u9648\u7384\u673a\u4e00\u60f3\uff1a\u8981\u662f\u77f3\u5929\u94ce\u6ca1\u6b7b\uff0c\u8fd9\u4e8b\u60c5\u77f3\u5929\u94ce\u4e5f\u53ef\u4ee5\u505a\u5f97\u5230\uff0c\u7136\u800c\u542c\u77f3\u5929\u94ce\u6628\u665a\u4e0e\u4e91\u592b \u4eba\u6240\u8bf4\u7684\u8bdd\uff0c\u4ed6\u4e43\u662f\u4e00\u5fc3\u4e3a\u5c11\u4e3b\u4e4b\u4e8b\u800c\u6765\uff0c\u800c\u4e14\u6839\u672c\u5c31\u4e0d\u77e5\u9053\u6709\u4e0a\u5b98\u5929\u91ce\u5176\u4eba\uff0c\u8fd9\u91cc\u7684\u4e8b\u60c5 \u65ad\u4e0d\u4f1a\u662f\u4ed6\u6240\u505a\u3002 \u3000\u3000\u4e91\u7d20\u7d20\u9053\uff1a\u201c\u7384\u673a\uff0c\u4f60\u60f3\u4ec0\u4e48\uff1f\u201d\u9648\u7384\u673a\u9053\uff1a\u201c\u7d20\u7d20\uff0c\u4f60\u6628\u665a\u662f\u4ec0\u4e48\u65f6\u5019\u6765\u7684\uff1f\u201d \u3000\u3000\u4e91\u7d20\u7d20\u9053\uff1a\u201c\u5927\u7ea6\u662f\u9760\u8fd1\u56db\u66f4\u7684\u65f6\u5206\u6765\u7684\u3002\u201d \u3000\u3000\u9648\u7384\u673a\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u55ef\uff0c\u90a3\u4e2a\u65f6\u5019\u4ed6\u5df2\u7ecf\u548c\u77f3\u5929\u94ce\u5f00\u59cb\u52a8\u624b\u4e86\u3002\u201d\u4e91\u7d20\u7d20\u5947\u9053\uff1a\u201c\u4f60 \u662f\u600e\u4e48\u4e2a\u60f3\u6cd5\uff1f\u7adf\u4f1a\u60f3\u5230\u6211\u7239\u7239\u7684\u5934\u4e0a\u6765\u3002\u96be\u9053\u4ed6\u8fd8\u4f1a\u5f04\u574f\u4ed6\u81ea\u5df1\u7684\u7ec3\u529f\u9759\u5ba4\u7684\u5927\u95e8\u3002\u518d\u8bf4 \u4ed6\u82e5\u8981\u653e\u4eba\u4ed6\u4e0d\u4f1a\u5f00\u4e48\uff1f\u201d \u3000\u3000\u9648\u7384\u673a\u9053\uff1a\u201c\u662f\u5440\uff0c\u6240\u4ee5\u8fd9\u624d\u5947\u602a\uff01\u201d\u4e91\u7d20\u7d20\u4e00\u60f3\uff0c\u8fd9\u4e24\u6247\u95e8\u65e2\u4e0d\u662f\u77f3\u5929\u94ce\u5f04\u574f\u7684\uff0c\u90a3 \u4e48\uff0c\u8fd9\u5c82\u4e0d\u662f\u5f53\u4eca\u5929\u4e0b\u8fd8\u6709\u4e00\u4e2a\u4eba\u8db3\u53ef\u4e0e\u81ea\u5df1\u7684\u7236\u4eb2\u6297\u624b\u4e89\u950b\uff0c\u800c\u4e14\u4ed6\u8fd9\u756a\u505a\u4f5c\u66f4\u5206\u660e\u662f\u5411 \u81ea\u5df1\u7684\u7236\u4eb2\u6311\u8845\u3002 \u3000\u3000\u9648\u7384\u673a\u9053\uff1a\u201c\u54b1\u4eec\u8fdb\u53bb\u518d\u8bf4\u3002\u5440\uff0c\u5929\u91ce\u53ef\u4e0d\u77e5\u600e\u6837\u4e86\uff1f\u5582\uff0c\u5929\u91ce\uff0c\u5929\u91ce\uff0c\u4e0a\u5b98\u5144\uff0c\u4f60\uff0c \u4f60\u600e\u4e48\u5566\uff1f\u201d\u77f3\u6d1e\u91cc\u6773\u65e0\u4eba\u606f\uff0c\u9648\u7384\u673a\u5fc3\u6025\u5982\u711a\uff0c\u8fd8\u4ee5\u4e3a\u662f\u4e0a\u5b98\u5929\u91ce\u53d7\u4e86\u91cd\u4f24\uff0c\u8d76\u5fd9\u4e09\u6b65\u5e76 \u4f5c\u4e24\u6b65\u7684\u8d70\u8fdb\u91cc\u9762\u641c\u7d22\uff0c\u8fd9\u5c71\u6d1e\u867d\u7136\u4e5f\u9887\u5e7d\u6df1\uff0c\u4f46\u6d1e\u53e3\u5927\u95e8\u5df2\u7834\uff0c\u671d\u9633\u5c04\u8fdb\u6d1e\u6765\uff0c\u4e00\u5207\u666f\u7269 \u90fd\u53ef\u770b\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u54ea\u91cc\u6709\u4e0a\u5b98\u5929\u91ce\u7684\u5f71\u5b50\uff01 \u3000\u3000\u4e91\u7d20\u7d20\u8fd9\u4e00\u60ca\u6bd4\u9002\u624d\u66f4\u751a\uff0c\u5583\u5583\u8bf4\u9053\uff1a\u201c\u4ed6\u8bf4\u8fc7\u7684\uff0c\u82e5\u4e0d\u662f\u4ed6\u81ea\u5df1\u6253\u51fa\u6b64\u6d1e\uff0c\u8c01\u4e5f\u8bf7\u4e0d \u52a8\u4ed6\uff0c\u5c31\u662f\u57cb\u9aa8\u8352\u5c71\u4e5f\u51b3\u4e0d\u53d7\u4eba\u601c\u60af\uff01\u201d\u9648\u7384\u673a\u5fc3\u5934\u4e00\u52a8\uff0c\u62ac\u5934\u770b\u65f6\uff0c\u4f46\u89c1\u56db\u9762\u77f3\u58c1\u90fd\u753b\u6709 \u5404\u79cd\u5404\u6837\u7684\u51fb\u5251\u59ff\u52bf\u4e0e\u7ec3\u529f\u56fe\u5f0f\uff0c\u4ee5\u9648\u7384\u673a\u8fd9\u6837\u7684\u6b66\u529f\uff0c\u770b\u4e0a\u53bb\u4ea6\u81ea\u89c9\u5f97\u6df1\u5965\u96be\u660e\u3002 \u3000\u3000\u5373\u7b97\u4e0a\u5b98\u5929\u91ce\u6ca1\u6709\u8bf4\u8fc7\u90a3\u6837\u7684\u8bdd\uff0c\u4ed6\u4e5f\u7edd\u5bf9\u6ca1\u6709\u8fd9\u7b49\u529f\u529b\u53ef\u4ee5\u7834\u95e8\u800c\u51fa\uff0c\u90a3\u4e48\uff0c\u8fd9\u4e24\u6247 \u95e8\u7a76\u7adf\u662f\u8c01\u5f04\u574f\u7684\uff1f\u9648\u7384\u673a\u6014\u4e86\u6014\u7684\u770b\u7740\u58c1\u4e0a\u7684\u56fe\u5f0f\uff0c\u597d\u50cf\u8981\u4ece\u56fe\u5f0f\u4e2d\u53c2\u900f\u4ec0\u4e48\uff0c\u5ffd\u5730\u95ee \u9053\uff1a\u201c\u7d20\u7d20\uff0c\u4f60\u662f\u600e\u4e48\u89c1\u7740\u4e0a\u5b98\u5929\u91ce\u7684\uff0c\u4ed6\u8fd8\u548c\u4f60\u8bf4\u4e86\u4e9b\u4ec0\u4e48\u8bdd\u6765\uff1f\u201d \u3000\u3000\u4e91\u7d20\u7d20\u9053\uff1a\u201c\u6211\u81ea\u5e7c\u751f\u957f\u5c71\u4e2d\uff0c\u9664\u4e86\u7236\u6bcd\u4e4b\u5916\uff0c\u5f88\u5c11\u548c\u751f\u4eba\u89c1\u9762\uff0c\u5c31\u662f\u6709\u65f6\u4e0b\u53bb\u6253\u730e\uff0c \u8db3\u8ff9\u4e5f\u4e0d\u51fa\u5468\u56f4\u4e94\u5341\u91cc\u5185\uff0c\u5374\u4e0d\u77e5\u600e\u7684\uff0c\u81ea\u4ece\u90a3\u5929\u89c1\u4e86\u4f60\u540e\uff0c\u5c31\u597d\u50cf\u4f60\u662f\u6211\u7684\u4eb2\u4eba\u4e00\u822c\u3002\u201d \u3000\u3000\u9648\u7384\u673a\u9053\uff1a\u201c\u5947\u602a\uff0c\u90a3\u54b1\u4eec\u7684\u5fc3\u601d\u7adf\u662f\u4e00\u6837\uff0c\u90a3\u65e5\u6211\u9192\u6765\u4e4b\u540e\uff0c\u53ea\u77a7\u4e86\u4f60\u4e00\u773c\uff0c\u5c31\u89c9\u5f97 \u4f60\u597d\u50cf\u662f\u6211\u4e00\u4e2a\u672a\u66fe\u89c1\u8fc7\u9762\u7684\u59b9\u5b50\u3002\u201d\u4e91\u7d20\u7d20\u7c89\u8138\u5fae\u7ea2\uff0c\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u6628\u665a\u6211\u5582\u4e86\u4f60\u7684\u767d \u9a6c\uff0c\u60f3\u8d77\u4f60\u6765\uff0c\u8dd1\u5230\u5c71\u4e0a\u5f39\u7434\uff0c\u4f60\u542c\u5f97\u89c1\u4e48\uff1f\u201d\u9648\u7384\u673a\u9053\uff1a\u201c\u6211\u5c31\u662f\u88ab\u4f60\u7684\u6b4c\u58f0\u5f15\u6765\u7684\u3002\u539f \u6765\u4f60\u5bf9\u6211\u7684\u5fc6\u5ff5\u6df1\u539a\u5982\u65af\uff0c\u4f46\u613f\u4ece\u4eca\u4e4b\u540e\uff0c\u54b1\u4eec\u6c38\u4e0d\u518d\u5206\u5f00\u4e86\u3002\u201d \u3000\u3000\u4e91\u7d20\u7d20\u8f7b\u63a0\u4e91\u9b03\uff0c\u4f4e\u7709\u4e00\u7b11\uff0c\u907f\u5f00\u4e86\u9648\u7384\u673a\u7684\u7684\u76ee\u5149\uff0c\u5f80\u4e0b\u8bf4\u9053\uff1a\u201c\u6211\u4e00\u9762\u5f39\u7434\uff0c\u4e00\u9762 \u60f3\u8d77\u4f60\u6765\u3002\u60f3\u8d77\u4f60\u8981\u884c\u523a\u6211\u7239\u7239\uff0c\u6211\u5fc3\u4e2d\u65e0\u9650\u6050\u60e7\u3002\u6211\u4e0d\u662f\u6015\u4f60\u4f24\u5bb3\u4e86\u4ed6\uff0c\u6211\u7239\u7239\u8bf4\u7684\uff0c\u4f60 \u82e5\u8981\u548c\u4ed6\u6253\u4e2a\u5e73\u624b\uff0c\u6700\u5c11\u4e5f\u8fd8\u5f97\u5341\u5e74\u3002\u6211\u662f\u5bb3\u6015\uff0c\u5bb3\u6015\u6211\u4e00\u5411\u5d07\u62dc\u7684\u7239\u7239\uff0c\u83ab\u975e\u771f\u662f\u4e2a\u574f \u4eba\u3002\u6211\u53c8\u5bb3\u6015\u4f60\u65e5\u540e\u78b0\u89c1\u4e86\u4ed6\uff0c\u82e5\u7136\u6211\u4e0d\u5728\u65c1\u8fb9\uff0c\u4ed6\u5c31\u4f1a\u6740\u4e86\u4f60\uff0c\u6211\u53c8\u8054\u60f3\u8d77\u8fd9\u4e24\u5929\u6765\u770b\u89c1 \u542c\u89c1\u7684\u4e00\u4e9b\u4e8b\u60c5\uff0c\u6211\u7239\u7239\u505a\u7684\u90fd\u597d\u50cf\u51fa\u4e4e\u5e38\u7406\u4e4b\u5916\uff0c\u5c24\u5176\u662f\u4e0d\u5f52\u8fd8\u5251\u8c31\u8fd8\u8981\u628a\u4e0a\u5b98\u5929\u91ce\u56da\u7981 \u8d77\u6765\u3002\u201d \u3000\u3000\u201c\u5440\uff0c\u6211\u7239\u7239\u5bf9\u4f60\u4e0d\u597d\uff0c\u6211\u5fc3\u91cc\u5934\u4e5f\u611f\u5230\u7f9e\u6127\uff0c\u6211\u6000\u7740\u8d4e\u7f6a\u7684\u5fc3\u60c5\u603b\u60f3\u505a\u4e00\u4e9b\u4ee4\u4f60\u559c\u6b22 \u7684\u4e8b\u60c5\u3002\u6211\u559c\u6b22\u4f60\uff0c\u4e5f\u5c31\u8fde\u5e26\u559c\u6b22\u90a3\u4e9b\u5bf9\u4f60\u597d\u7684\u4eba\u3002\u6211\u7981\u4e0d\u4f4f\u53c8\u60f3\u8d77\u4e0a\u5b98\u5929\u91ce\u6765\uff0c\u4ed6\u5192\u4e86\u90a3 \u4e48\u5927\u7684\u9669\uff0c\u8fd8\u5b81\u613f\u820d\u4e86\u638c\u95e8\uff0c\u4e0d\u8981\u5251\u8c31\uff0c\u5c06\u4f60\u4ea4\u6362\u51fa\u6765\uff0c\u6211\u60f3\u4f60\u4e5f\u4e00\u5b9a\u60f3\u6551\u4ed6\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u9648\u7384\u673a\u9053\uff1a\u201c\u4e0a\u5b98\u5929\u91ce\u662f\u6211\u751f\u5e73\u7684\u7b2c\u4e00\u77e5\u5df2\uff0c\u4f46\u4ed6\u8fd8\u4e0d\u80fd\u50cf\u4f60\u4e00\u6837\u7684\u770b\u5f97\u900f\u6211\u7684\u5fc3\u3002\u771f \u5947\u602a\uff0c\u4f60\u6837\u6837\u7684\u60f3\u6cd5\u90fd\u4e0e\u6211\u76f8\u540c\uff0c\u597d\u50cf\u54b1\u4eec\u7684\u5fc3\u91cc\u662f\u8fde\u5728\u4e00\u8d77\u7684\u3002\u201d\u4e24\u4eba\u7684\u624b\u4e0d\u77e5\u4e0d\u89c9\u7684\u53c8 \u7d27\u63e1\u8d77\u6765\uff0c\u90a3\u662f\u4e24\u5fc3\u76f8\u77e5\u7684\u559c\u60a6\u3002 \u3000\u3000\u53ea\u542c\u5f97\u4e91\u7d20\u7d20\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\u5f80\u4e0b\u8bf4\u9053\uff1a\u201c\u6211\u7239\u7239\u6781\u662f\u7231\u6211\uff0c\u6211\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u6211\u8981\u53cd\u5bf9 \u4ed6\u3002\u7136\u800c\u6628\u665a\u6211\u5c31\u505a\u4e86\u3002\u6211\u5077\u5077\u8dd1\u6765\u6253\u5f00\u4e86\u8fd9\u4e24\u6247\u5927\u95e8\u3002\u6211\u8981\u653e\u4e0a\u5b98\u5929\u91ce\u51fa\u53bb\u3002\u6211\u4e5f\u5bb3\u6015\u4ed6 \u90a3\u80a1\u51f6\u9738\u9738\u7684\u795e\u6c14\uff0c\u4f46\u6211\u5df2\u6253\u5b9a\u4e3b\u610f\uff0c\u5c31\u7b97\u4ed6\u6709\u6240\u8bef\u4f1a\uff0c\u52a8\u624b\u6253\u6211\uff0c\u6211\u4e5f\u51b3\u4e0d\u8fd8\u624b\u6253\u4ed6\u3002\u201d \u9648\u7384\u673a\u9053\uff1a\u201c\u59b9\u5b50\uff0c\u4f60\u771f\u597d\uff01\u201d\u4f46\u89c9\u666e\u5929\u4e4b\u4e0b\uff0c\u9664\u4e86\u81ea\u5df1\u7684\u6bcd\u4eb2\u4e4b\u5916\uff0c\u518d\u4e5f\u6ca1\u6709\u50cf\u5979\u8fd9\u6837\u6b63 \u76f4\u65e0\u90aa\u7684\u5973\u4eba\u3002 \u3000\u3000\u4e91\u7d20\u7d20\u7eed\u9053\uff1a\u201c\u4ed6\u521d\u89c1\u6211\u65f6\uff0c\u679c\u7136\u5bf9\u6211\u5f88\u51f6\uff0c\u4f46\u5374\u6ca1\u6709\u52a8\u624b\u6253\u6211\u3002\u4ed6\u542c\u4e86\u6211\u7684\u8bdd\u540e\uff0c\u5ffd \u7136\u98a4\u6296\u8d77\u6765\uff0c\u8bf4\u662f\u6599\u4e0d\u5230\u6211\u4f1a\u8fd9\u6837\u559c\u6b22\u4f60\u3002\u4ed6\u8bf4\u7740\u8fd9\u8bdd\u7684\u65f6\u5019\uff0c\u8d77\u521d\u5e26\u7b11\uff0c\u63a5\u7740\u5c31\u54ed\u8d77\u6765\uff0c \u8ddf\u7740\u4fbf\u9a82\u6211\uff0c\u95ee\u6211\u77e5\u4e0d\u77e5\u9053\u4f60\u5df2\u7ecf\u6709\u4e86\u5fc3\u4e0a\u7684\u4eba\u513f\uff1f\u201d \u3000\u3000\u9648\u7384\u673a\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u8bef\u4f1a\u521a\u624d\u6211\u5df2\u8bf4\u5f97\u5f88\u6e05\u695a\u4e86\u3002\u4ed6\u8fd8\u6709\u4e9b\u4ec0\u4e48\u8bdd\uff1f\u201d\u4e91\u7d20\u7d20\u9053\uff1a\u201c\u6211 \u5fcd\u7740\u59d4\u5c48\uff0c\u5fcd\u7740\u60b2\u75db\uff0c\u8ba9\u4ed6\u9a82\u4e86\u4e00\u987f\uff0c\u4ecd\u662f\u597d\u597d\u7684\u8ddf\u4ed6\u8bf4\uff1a\u4f60\u60f3\u8981\u5251\u8c31\uff0c\u6211\u5077\u7ed9\u4f60\u3002\u4f60\u8d70\u4e86 \u5427\uff0c\u6211\u8fd8\u544a\u8bc9\u4ed6\u4f60\u5df2\u7ecf\u5e73\u5b89\u65e0\u4e8b\u8131\u8eab\u4e86\uff0c\u4ed6\u7559\u5728\u8fd9\u91cc\u4e5f\u6ca1\u6709\u4ec0\u4e48\u610f\u601d\uff0c\u4e0d\u5982\u53d6\u4e86\u5251\u8c31\u8d81\u6211\u7239 \u7239\u6ca1\u6709\u56de\u6765\uff0c\u9a6c\u4e0a\u4fbf\u8d70\u3002\u54ea\u6599\u4ed6\u53c8\u5927\u53d1\u813e\u6c14\u3002\u201d \u3000\u3000\u9648\u7384\u673a\u7b11\u9053\uff1a\u201c\u4e0a\u5b98\u5929\u91ce\u5c31\u662f\u8fd9\u4e2a\u706b\u7206\u7684\u6027\u513f\u4e0d\u597d\u3002\u201d\u4e91\u7d20\u7d20\u9053\uff1a\u201c\u4ed6\u8bf4\u5251\u8c31\u672c\u6765\u662f\u4ed6 \u4eec\u6b66\u5f53\u6d3e\u7684\uff0c\u4e3a\u4ec0\u4e48\u8981\u5077\u5077\u6478\u6478\u7684\u7a83\u53d6\uff1f\u4ed6\u8bf4\u9664\u975e\u662f\u4ed6\u6253\u8d62\u4e86\u6211\u7684\u7239\u7239\uff0c\u8981\u6211\u7239\u7239\u5fc3\u60a6\u8bda\u670d \u7684\u8fd8\u4ed6\uff0c\u5426\u5219\u6211\u9001\uff0c\u4ed6\u4e5f\u4e0d\u8981\u3002\u8981\u4ed6\u5077\u8d70\uff0c\u90a3\u66f4\u8a93\u6b7b\u4e0d\u4e3a\u3002\u9664\u975e\u662f\u6709\u671d\u4e00\u65e5\uff0c\u4ed6\u51ed\u7740\u81ea\u5df1\u7684 \u529f\u529b\u6253\u51fa\u53bb\u3002\u4ed6\u8fd8\u51b7\u7b11\u9053\uff1a\u2018\u4f60\u7239\u7239\u6545\u610f\u505a\u51fa\u6177\u6168\u5927\u65b9\uff0c\u597d\u50cf\u662f\u6709\u610f\u8981\u6210\u5168\u6211\uff0c\u6211\u53ef\u4e0d\u9886\u4ed6 \u7684\u60c5\uff0c\u8fd9\u5251\u8c31\u672c\u6765\u5c31\u662f\u6211\u7684\u2019\u3002\u6211\u53ef\u4e0d\u660e\u767d\u4ed6\u7684\u610f\u601d\u3002\u201d \u3000\u3000\u9648\u7384\u673a\u542c\u5230\u6b64\u5916\uff0c\u5fc3\u4e2d\u65e9\u5df2\u4e86\u7136\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u770b\u56db\u58c1\u6240\u753b\u7684\u56fe\u5f0f\uff0c\u662f\u4e0d\u662f\u8fbe\u6469\u5251\u5f0f\uff1f\u201d \u4e91\u7d20\u7d20\u9053\uff1a\u201c\u8fbe\u6469\u5251\u6cd5\u6211\u53ea\u5b66\u4e86\u4e09\u6210\uff0c\u770b\u6765\u6211\u6240\u5b66\u7684\u62db\u5f0f\u8fd9\u58c1\u4e0a\u90fd\u6709\uff0c\u60f3\u5fc5\u662f\u4e86\u3002\u8fd8\u6709\u8fd9\u4e9b \u7ec3\u529f\u7684\u56fe\u5f0f\uff0c\u6211\u4e5f\u53ea\u8ba4\u5f97\u4e00\u6307\u7985\u7684\u529f\u592b\u3002\u55ef\uff0c\u6211\u660e\u767d\u5566\uff0c\u6211\u7239\u7239\u7adf\u662f\u5c06\u4ed6\u6bd5\u751f\u82e6\u5b66\u7684\u5fc3\u5f97\uff0c \u90fd\u5199\u5728\u8fd9\u4e0a\u9762\u5566\uff0c\u82e5\u80fd\u53c2\u900f\u8fd9\u58c1\u4e0a\u7684\u6b66\u529f\uff0c\u5b9e\u80dc\u4e8e\u4ec5\u5f97\u4e00\u90e8\u8fbe\u6469\u5251\u8c31\u3002\u6562\u60c5\u4ed6\u5c06\u4e0a\u5b98\u5929\u91ce\u5173 \u5728\u8fd9\u91cc\uff0c\u5c31\u662f\u6709\u610f\u8ba9\u4ed6\u5b66\u7684\u3002\u602a\u4e0d\u5f97\u4e0a\u5b98\u5929\u91ce\u4ed6\u3001\u4ed6\u4e0d\u80af\u5b9a\u3002\u201d\u8bf4\u5230\u6b64\u5904\uff0c\u76ca\u589e\u7591\u60d1\uff0c\u56e0\u4e3a \u4e0a\u5b98\u5929\u91ce\u6bd5\u7adf\u8fd8\u662f\u8d70\u4e86\u3002 \u3000\u3000\u9648\u7384\u673a\u4e5f\u9053\uff1a\u201c\u7167\u4e0a\u5b98\u5929\u91ce\u7684\u6027\u683c\uff0c\u4ed6\u65e2\u7136\u8bf4\u8fc7\u8a93\u6b7b\u4e0d\u8d70\uff0c\u90a3\u5c31\u7b97\u5c71\u5d29\u5730\u88c2\uff0c\u8fd9\u77f3\u6d1e\u584c \u4e86\uff0c\u4ed6\u4e5f\u51b3\u8ba1\u4e0d\u80af\u51fa\u6765\u3002\u5982\u4eca\u4ed6\u5374\u7a81\u7136\u4e0d\u89c1\uff0c\u8fd9\u4e8b\u60c5\u5f53\u771f\u5947\u602a\u3002\u201d\u4e24\u4eba\u8c08\u4e86\u4e00\u4f1a\uff0c\u767e\u601d\u83ab\u5f97 \u5176\u89e3\uff0c\u9648\u7384\u673a\u95f7\u95f7\u4e0d\u4e50\uff0c\u4e91\u7d20\u7d20\u9053\uff1a\u201c\u4ed6\u65e2\u7136\u8d70\u4e86\uff0c\u54b1\u4eec\u803d\u5728\u8fd9\u513f\u4e5f\u662f\u65e0\u76ca\uff0c\u4e0d\u5982\u56de\u5bb6\u53bb \u5427\uff0c\u4f60\u809a\u5b50\u4e5f\u8be5\u997f\u5566\u3002\u201d \u3000\u3000\u4e24\u4eba\u53c8\u56de\u5230\u90a3\u5ead\u9662\u4e4b\u4e2d\uff0c\u4f46\u89c1\u65ad\u7816\u788e\u77f3\uff0c\u8d25\u53f6\u6b8b\u679d\uff0c\u4e71\u7ea2\u6df7\u6eb7\uff0c\u98de\u7d6e\u6cbe\u6ce5\uff0c\u628a\u4e00\u4e2a\u666f\u81f4 \u6e05\u5e7d\u7684\u5ead\u9662\uff0c\u7adf\u53d8\u6210\u4e86\u9669\u98ce\u60e8\u60e8\uff0c\u8352\u829c\u6742\u4e71\u7684\u5730\u65b9\uff0c\u9648\u7384\u673a\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u501f\u4e00\u628a\u82b1\u9504\u7ed9 \u6211\u3002\u201d\u4e91\u7d20\u7d20\u9012\u8fc7\u82b1\u9504\uff0c\u65e9\u77e5\u5176\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u52b3\u70e6\u4f60\u4e86\uff0c\u6211\u6362\u8fc7\u8863\u88f3\u518d\u7ed9\u4f60\u5f04\u4e24\u6837\u5c0f\u83dc\u3002\u201d \u3000\u3000\u9648\u7384\u673a\u6398\u5f00\u6ce5\u571f\uff0c\u5c06\u77f3\u5929\u94ce\u8349\u8349\u63a9\u57cb\uff0c\u53c8\u628a\u90a3\u4e9b\u6b8b\u679d\u8d25\u53f6\u843d\u82b1\u90fd\u626b\u4f5c\u4e00\u5806\uff0c\u4e5f\u4e00\u5e76\u846c \u4e86\uff0c\u60f3\u8d77\u77f3\u5929\u94ce\u4e00\u4ee3\u6b66\u5b66\u5927\u5e08\uff0c\u7adf\u5c14\u57cb\u9aa8\u8352\u5c71\uff0c\u5fc3\u4e2d\u65e0\u9650\u611f\u6168\u3002 \u3000\u3000\u629b\u4e0b\u82b1\u9504\uff0c\u56de\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u4e91\u7d20\u7d20\u5df2\u6362\u4e86\u4e00\u8eab\u65b0\u8863\uff0c\u501a\u5728\u95e8\u8fb9\uff0c\u5ffd\u5730\u201c\u5657\u55e4\u201d\u7b11\u9053\uff1a \u201c\u4f60\u5446\u5446\u7684\u770b\u7740\u6211\u5e72\u5417\uff1f\u96be\u9053\u8fd8\u4e0d\u8ba4\u8bc6\u6211\u4e48\uff1f\u201d\u9648\u7384\u673a\u9053\uff1a\u201c\u4f60\u8fd9\u8eab\u88c5\u675f\u2014\u2014\u5440\uff0c\u771f\u7f8e\uff01\u201d \u4f3c\u662f\u8d5e\u53f9\uff0c\u8bed\u8c03\u4e4b\u4e2d\u5374\u5145\u6ee1\u60f6\u60d1\u3002 \u3000\u3000\u4e91\u7d20\u7d20\u9053\uff1a\u201c\u600e\u4e48\uff1f\u6211\u8fd9\u8eab\u8863\u88f3\u662f\u7239\u7239\u753b\u4e86\u56fe\u6837\uff0c\u6559\u6211\u88c1\u526a\u7684\uff0c\u542c\u8bf4\u662f\u4e09\u5341\u5e74\u524d\u6d41\u884c\u7684 \u88c5\u675f\u3002\u8fd9\u53cc\u51e4\u5934\u9576\u73e0\u7684\u978b\u5b50\uff0c\u542c\u8bf4\u73b0\u5728\u4e5f\u5f88\u5c11\u4eba\u7a7f\u4e86\u3002\u201d\u9648\u7384\u673a\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u6211\u6bcd\u4eb2\u4e5f\u6709\u8fd9 \u6837\u7684\u8863\u88f3\u978b\u5b50\uff0c\u5979\u6536\u62fe\u7bb1\u7b3c\u65f6\u6211\u66fe\u7ecf\u89c1\u8fc7\uff0c\u6211\u4e5f\u4ece\u672a\u89c1\u5979\u7a7f\u8fc7\u3002\u201d\u4e91\u7d20\u7d20\u6014\u4e86\u4e00\u6014\uff0c\u597d\u4e45\u624d \u8bf4\u9053\uff1a\u201c\u65e2\u7136\u662f\u4e09\u5341\u5e74\u524d\u6d41\u884c\u7684\u670d\u88c5\uff0c\u90a3\u4e48\u4e0e\u4f60\u6bcd\u4eb2\u7684\u76f8\u540c\u4e5f\u5e76\u4e0d\u51fa\u5947\u3002\u201d\u8bf4\u662f\u8fd9\u6837\u8bf4\u4e86\uff0c \u5176\u5b9e\u5979\u7684\u5fc3\u4e2d\u4ea6\u81ea\u8d77\u4e86\u7591\u4e91\u3002 \u3000\u3000\u4e91\u7d20\u7d20\u5c06\u996d\u83dc\u7aef\u5230\u4e66\u623f\uff0c\u90a3\u4e24\u6837\u5c0f\u83dc\u53c8\u662f\u9648\u7384\u673a\u5e73\u7d20\u7231\u5403\u7684\uff0c\u9648\u7384\u673a\u672c\u6765\u662f\u8981\u79f0\u8d5e\u5979 \u7684\uff0c\u8fd9\u65f6\u4f46\u89c9\u5fc3\u70e6\u610f\u4e71\uff0c\u7adf\u8fde\u201c\u591a\u8c22\u201d\u8fd9\u4e24\u4e2a\u5b57\u4e5f\u5fd8\u8bb0\u8bf4\u4e86\u3002 \u3000\u3000\u4e91\u7d20\u7d20\u9053\uff1a\u201c\u4f60\u60f3\u4ec0\u4e48\uff1f\u201d\u9648\u7384\u673a\u832b\u7136\u7684\u62ac\u8d77\u5934\u6765\uff0c\u6da9\u58f0\u8bf4\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\u3002\u201d\u4e91\u7d20\u7d20\u683c \u683c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u60f3\u6bcd\u4eb2\uff0c\u90a3\u4e00\u5929\u4f60\u5728\u7761\u68a6\u4e2d\u4e5f\u53eb\u5979\u5462\u3002\u4f60\u6bcd\u4eb2\u771f\u597d\u798f\u6c14\uff0c\u6709\u4f60\u8fd9\u6837 \u4e00\u4e2a\u5b5d\u987a\u7684\u513f\u5b50\u3002\u201d\u7a81\u7136\u60f3\u8d77\u81ea\u5df1\u51fa\u8d70\u4e86\u7684\u6bcd\u4eb2\uff0c\u60b2\u4ece\u4e2d\u6765\uff0c\u6ce3\u4e0d\u6210\u58f0\u3002 \u3000\u3000\u9648\u7384\u673a\u8f7b\u8f7b\u629a\u7740\u5979\u7684\u5934\u9053\uff1a\u201c\u6211\u6bcd\u4eb2\u5979\u4e00\u5b9a\u559c\u6b22\u4f60\u3002\u4ece\u4eca\u4e4b\u540e\uff0c\u6211\u5728\u8fd9\u4e16\u754c\u4e0a\u6709\u4e24\u4e2a\u81f3 \u4eb2\u81f3\u7231\u7684\u4eba\uff0c\u4e00\u4e2a\u662f\u6bcd\u4eb2\uff0c\u4e00\u4e2a\u5c31\u662f\u4f60\u3002\u201d\u4e91\u7d20\u7d20\u6cea\u73e0\u6eda\u6eda\u800c\u4e0b\uff0c\u53c8\u662f\u6b22\u559c\uff0c\u53c8\u662f\u60b2\u4f24\uff0c\u7f9e \u6da9\u7b11\u9053\uff1a\u201c\u521a\u521a\u6362\u8fc7\u8863\u88f3\uff0c\u53c8\u7ed9\u6cea\u75d5\u6cbe\u6c61\u4e86\u3002\u201d\u9648\u7384\u673a\u9053\uff1a\u201c\u662f\u554a\uff0c\u8c01\u53eb\u4f60\u8fd9\u4e48\u7231\u54ed\uff0c\u8c08\u4e9b \u5927\u5bb6\u559c\u6b22\u7684\u8bdd\u5427\u3002\u201d\u4e91\u7d20\u7d20\u9053\uff1a\u201c\u55ef\uff0c\u4f60\u90a3\u5929\u8bf4\u4f60\u5bb6\u4e2d\u7684\u4e66\u623f\u4e5f\u50cf\u6211\u5bb6\u4e00\u6837\uff0c\u53ef\u60dc\u5982\u4eca\u6211\u5bb6 \u4e2d\u7684\u6885\u6811\u53ea\u5269\u4e0b\u5149\u79c3\u79c3\u7684\u6811\u5e72\u4e86\u3002\u4e0d\u77e5\u51e0\u65f6\u6211\u6709\u798f\u4efd\u5230\u4f60\u5bb6\u53bb\u770b\u770b\u3002\u201d \u3000\u3000\u9648\u7384\u673a\u5fc3\u5934\u4e00\u9707\uff0c\u8bb0\u5f97\u90a3\u4e00\u5929\u5728\u8fd9\u4e66\u623f\u4e2d\u521a\u521a\u9192\u6765\u7684\u65f6\u5019\uff0c\u4ee5\u4e3a\u662f\u81ea\u5df1\u7684\u5bb6\uff0c\u4f46\u90a3\u4e00\u5929 \u4ec5\u4ec5\u662f\u5fc3\u4e2d\u7591\u60d1\u800c\u5df2\uff0c\u8fd9\u6b21\u542c\u4e91\u7d20\u7d20\u518d\u5ea6\u63d0\u8d77\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u5fc3\u4e2d\u7adf\u81ea\u611f\u5230\u6709\u4e00\u79cd\u83ab\u540d\u5176\u5999\u7684 \u4e0d\u7965\u4e4b\u5146\uff0c\u8d8a\u770b\u8d8a\u611f\u5230\u8fd9\u4e66\u623f\u91cc\u900f\u7740\u53e4\u602a\uff0c\u5fc3\u5934\u4e0a\u597d\u50cf\u6709\u4e00\u5c42\u9634\u5f71\uff0c\u538b\u5f97\u4ed6\u900f\u4e0d\u8fc7\u6c14\u6765\u3002\u4e91 \u7d20\u7d20\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u597d\u4f3c\u5bb3\u6015\u4ec0\u4e48\uff1f\u201d\u9648\u7384\u673a\u5ffd\u5730\u8df3\u8d77\u6765\u9053\uff1a\u201c\u5728\u4f60\u7684\u5bb6\u4e2d\uff0c\u6211\u771f\u662f\u6709\u70b9\u5bb3 \u6015\u3002\u7d20\u7d20\uff0c\u4f60\u613f\u610f\u8ddf\u6211\u8d70\u4e48\uff1f\u201d\u4e91\u7d20\u7d20\u62bf\u5634\u7b11\u9053\uff1a\u201c\u6211\u81ea\u7136\u8ddf\u4f60\u3002\u201d\u9648\u7384\u673a\u5401\u4e86\u53e3\u6c14\uff0c\u53ea\u89c9 \u4e91\u7d20\u7d20\u8f6f\u7ef5\u7ef5\u7684\u8eab\u8eaf\u5df2\u5012\u8fdb\u4ed6\u7684\u6000\u4e2d\u3002 \u3000\u3000\u9648\u7384\u673a\u6b63\u81ea\u9676\u9189\uff0c\u5ffd\u542c\u5f97\u6709\u4e00\u4e2a\u6781\u5176\u51b7\u5cfb\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u653e\u5f00\u6211\u7684\u5973\u513f\uff01\u201d\u4e91\u7d20\u7d20\u8fd9\u4e00\u60ca \u975e\u540c\u5c0f\u53ef\uff0c\u8df3\u8d77\u6765\u4e00\u770b\uff0c\u53ea\u89c1\u5979\u7684\u7236\u4eb2\u4e0d\u77e5\u662f\u4ec0\u4e48\u65f6\u5019\u8fdb\u6765\u7684\uff0c\u5c31\u7ad9\u5728\u4ed6\u4eec\u8eab\u524d\u4e0d\u5230\u4e09\u5c3a\u4e4b \u5730\uff0c\u8138\u4e0a\u51b7\u51b0\u51b0\u7684\u6ca1\u6709\u4e00\u70b9\u8840\u8272\uff0c\u4ed6\u7684\u53f3\u638c\u5df2\u5728\u6162\u6162\u7684\u4e3e\u8d77\u3002 \u3000\u3000\u4e91\u7d20\u7d20\u53eb\u9053\uff1a\u201c\u4f60\u8981\u6740\u4ed6\uff0c\u5c31\u8fde\u6211\u4e5f\u6740\u4e86\u5427\uff01\u201d\u4e91\u821e\u9633\u90a3\u53ea\u624b\u638c\u505c\u5728\u7a7a\u4e2d\uff0c\u8fc7\u4e86\u534a\u664c\uff0c \u53c8\u6162\u6162\u653e\u4e0b\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6211\u8fd8\u6709\u4ec0\u4e48\u5fc3\u60c5\u6740\u4eba\uff1f\u7d20\u7d20\uff0c\u4f60\u53eb\u4ed6\u51fa\u53bb\uff0c\u6211\u6709\u8bdd\u8981\u548c\u4f60\u8bf4\u3002\u201d \u90a3\u8bed\u8c03\u4e1d\u6beb\u4e0d\u50cf\u7236\u4eb2\u547d\u4ee4\u5973\u513f\uff0c\u5374\u50cf\u662f\u592e\u6c42\u4e00\u4e2a\u670b\u53cb\u3002\u4e91\u7d20\u7d20\u7a81\u7136\u89c9\u5f97\u5728\u4ed6\u7236\u4eb2\u90a3\u5f20\u597d\u50cf\u6f20 \u65e0\u8868\u60c5\u7684\u9762\u4e0a\uff0c\u900f\u51fa\u4e86\u6148\u7231\u7684\u5149\u8f89\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u4e00\u9178\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u7384\u673a\uff0c\u4f60\u5c31\u51fa\u53bb\u4e00\u4f1a \u513f\u3002\u201d \u3000\u3000\u4e66\u623f\u4e2d\u4e24\u7236\u5973\u9762\u9762\u76f8\u5bf9\uff0c\u4e92\u76f8\u51dd\u89c6\uff0c\u672c\u6765\u662f\u6700\u719f\u8bc6\u7684\u4eba\uff0c\u5374\u6479\u5730\u6709\u4e86\u964c\u751f\u7684\u611f\u89c9\uff0c\u8fc7\u4e86 \u7247\u523b\uff0c\u4e24\u4eba\u773c\u5149\u90fd\u8d8a\u6765\u8d8a\u67d4\u6da6\u4e86\u3002\u4e91\u821e\u9633\u9053\uff1a\u201c\u6211\u8fd9\u4e00\u751f\u4e2d\u53ea\u6709\u4f60\u662f\u6211\u6700\u75bc\u7231\u7684\u4eba\uff0c\u6211\u53ef\u4ee5 \u820d\u6389\u4e00\u5207\uff0c\u820d\u4e0d\u4e86\u4f60\u3002\u201d\u4e91\u7d20\u7d20\u9053\uff1a\u201c\u7239\uff0c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u4e91\u821e\u9633\u9053\uff1a\u201c\u4f60\u6bcd\u4eb2\u8d70\u4e86\uff0c\u8fd9\u5341\u51e0\u5e74\u6765\u6211\u77e5\u9053\u5979\u7684\u5fc3\u91cc\u96be\u53d7\uff0c\u5176\u5b9e\u6211\u7684\u5fc3\u4e5f\u4f55\u5c1d\u4e0d\u96be \u53d7\u3002\u8fd9\u4e2a\u5bb6\u6211\u672c\u6765\u4e5f\u4e0d\u60f3\u8981\u4e86\uff0c\u6709\u4e00\u4e9b\u8bdd\uff0c\u5982\u679c\u6211\u4e0d\u5bf9\u4f60\u8bf4\uff0c\u6211\u6b7b\u4e86\u4e5f\u4e0d\u5fc3\u5b89\u3002\u8bf4\u4e86\u4e4b\u540e\uff0c \u4f60\u8ba4\u6211\u662f\u4f60\u7236\u4eb2\u4e5f\u597d\uff0c\u4e0d\u8ba4\u6211\u662f\u4f60\u7236\u4eb2\u4e5f\u597d\uff0c\u90fd\u7531\u5f97\u4f60\u3002\u201d\u4e91\u7d20\u7d20\u62ac\u8d77\u5934\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u8bf4 \u5427\u3002\u5973\u513f\u4e5f\u820d\u4e0d\u5f97\u4f60\u3002\u201d"}),showOnHomepage:!0},{name:"\u7b2c\u4e03\u56de\u3000\u4e94\u8001\u5174\u5e08",content:(0,t.jsx)(t.Fragment,{children:"\u4e91\u821e\u9633\u6df1\u6df1\u7684\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u51e0\u5929\u6765\u4f60\u89c1\u5230\u4e00\u4e9b\u4e8b\u60c5\uff0c\u542c\u5230\u4e00\u4e9b\u4e8b\u60c5\u3002\u4f60\u7684\u5988\u5988\u3001 \u9648\u7384\u673a\u3001\u4e0a\u5b98\u5929\u91ce\uff0c\u60f3\u5fc5\u90fd\u5728\u4f60\u9762\u524d\u8bf4\u6211\u3002\u602a\u4e0d\u5f97\u4f60\u8fd9\u6837\u6028\u6068\u7239\u7239\u3002\u201d\u4e91\u7d20\u7d20\u9053\uff1a\u201c\u9648\u7384\u673a \u53ef\u6ca1\u6709\u8bf4\u4f60\u3002\u201d\u4e91\u821e\u9633\u9053\uff1a\u201c\u6211\u77e5\u9053\u4ed6\u4eec\u8bf4\u6211\u4ec0\u4e48\uff0c\u79cd\u79cd\u662f\u975e\uff0c\u6211\u90fd\u4e0d\u60f3\u5206\u8fa9\u3002\u6211\u5f53\u5e74\u66fe\u8c0b \u593a\u4f60\u5916\u7956\u7236\u7684\u5251\u8c31\uff0c\u53c8\u4e00\u76f4\u51b7\u6de1\u4f60\u7684\u5988\u5988\uff0c\u8fd9\u4e9b\u90fd\u662f\u4e8b\u5b9e\uff0c\u4ed6\u4eec\u8bf4\u6211\uff0c\u6211\u4e5f\u4e1d\u6beb\u4e0d\u89c9\u59d4 \u5c48\u3002\u201d \u3000\u3000\u4e91\u7d20\u7d20\u5fc3\u98a4\u624b\u9707\uff0c\u63a9\u9762\u8bf4\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u51b7\u6de1\u5988\u5988\u3002\u542c\u8bf4\u5988\u5988\u5ac1\u4f60\u7684\u65f6\u5019\uff0c\u66fe\u4e3a\u4f60\u5b81 \u613f\u65ad\u4e86\u7236\u5973\u4e4b\u60c5\uff0c\u7ed9\u4f60\u5077\u6765\u5251\u8c31\uff0c\u96be\u9053\u4ed6\u5bf9\u4f60\u8fd8\u4e0d\u591f\u597d\u4e48\uff1f\u201d\u4e91\u821e\u9633\u9053\uff1a\u201c\u90a3\u662f\u6211\u5bf9\u4e0d\u8d77 \u5979\uff0c\u6211\u5a36\u5979\u672c\u6765\u5c31\u662f\u4e3a\u4e86\u8fd9\u672c\u5251\u8c31\uff01\u201d \u3000\u3000\u4e91\u7d20\u7d20\u5c16\u53eb\u4e00\u58f0\uff0c\u9000\u4e86\u4e24\u6b65\uff0c\u5fc3\u4e2d\u4f24\u75db\u4e4b\u6781\uff0c\u60f3\u4e0d\u5230\u7236\u4eb2\u76f4\u8ba4\u4e0d\u8bb3\uff0c\u4ed6\u4eec\u8bf4\u7684\u7adf\u7136\u662f \u771f\u3002\u53ea\u542c\u5f97\u7236\u4eb2\u53c8\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u7d20\u7d20\uff0c\u4f60\u5fc3\u5730\u65e0\u90aa\uff0c\u5bb9\u4e0d\u5f97\u522b\u4eba\u505a\u9519\u534a\u70b9\u3002\u4ec5\u4ec5\u8fd9\u4e9b\uff0c\u4f60\u5c31 \u5bb3\u6015\u4e86\u5417\uff1f\u201d\u4e91\u7d20\u7d20\u9053\uff1a\u201c\u4ec5\u4ec5\u8fd9\u4e9b\uff1f\u4f60\u5341\u51e0\u5e74\u6765\u51b7\u6de1\u5988\u5988\uff0c\u96be\u9053\u8fd9\u8fd8\u662f\u5c0f\u4e8b\u3002\u201d \u3000\u3000\u4e91\u821e\u9633\u51c4\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8fd9\u4e00\u751f\u505a\u9519\u4e86\u8bb8\u591a\u4e8b\u60c5\uff0c\u4ed6\u4eec\u8bf4\u6211\u7684\uff0c\u6709\u4e9b\u662f\u771f\u6709\u4e9b\u662f \u5047\uff0c\u4f46\u5373\u4f7f\u5168\u90e8\u662f\u5b9e\uff0c\u90a3\u4e5f\u7b97\u4e0d\u4e86\u4ec0\u4e48\u3002\u6700\u6700\u4ee4\u6211\u96be\u8fc7\u7684\uff0c\u662f\u6211\u66fe\u505a\u8fc7\u4e00\u4ef6\u6781\u5927\u6781\u5927\u7684\u4e8b \u60c5\uff0c\u5929\u4e0b\u7adf\u6ca1\u6709\u4e00\u4e2a\u4eba\u77e5\u9053\u3002\u8fd9\u5341\u51e0\u5e74\u6765\uff0c\u6211\u4e00\u76f4\u4e3a\u7740\u8fd9\u4ef6\u4e8b\u60c5\u6094\u6068\uff01\u5440\uff0c\u7d20\u7d20\uff0c\u4f60\u53ef\u77e5\u9053 \u6700\u6700\u4ee4\u4eba\u75db\u82e6\u7684\u662f\u4ec0\u4e48\u4e8b\u60c5\uff1f\u90a3\u5c31\u662f\u4f60\u72af\u4e86\u7f6a\u5b7d\u5374\u6ca1\u4eba\u77e5\u9053\uff0c\u6ca1\u6709\u4eba\u8d23\u5907\u4f60\uff0c\u8ba9\u4f60\u81ea\u5df1\u53bb\u53d7 \u826f\u5fc3\u7684\u78e8\u6298\uff0c\u8fd9\u662f\u5929\u4e0b\u6700\u6b8b\u5fcd\u7684\u9177\u5211\uff01\u4f60\u662f\u6211\u75bc\u7231\u7684\u5973\u513f\uff0c\u6211\u5982\u4eca\u8bf4\u7ed9\u4f60\u542c\uff0c\u5b81\u613f\u53d7\u4f60\u8d23 \u5907\uff0c\u53d7\u4f60\u7684\u553e\u5f03\u3002\u201d \u3000\u3000\u8fd9\u771f\u662f\u610f\u60f3\u4e0d\u5230\u7684\u4e8b\u60c5\uff0c\u53f1\u54a4\u98ce\u4e91\uff0c\u4e0d\u53ef\u4e00\u4e16\u7684\u4e91\u821e\u9633\uff0c\u6b64\u9645\u7adf\u662f\u8bf4\u5f97\u5982\u6b64\u53ef\u601c\uff0c\u7adf\u662f \u50cf\u72af\u4eba\u9762\u5bf9\u6cd5\u5b98\u4e00\u6837\uff0c\u8981\u6c42\u4ed6\u5973\u513f\u7684\u8d23\u5907\uff0c\u4ed6\u82cd\u767d\u7684\u8138\u4e0a\u6e10\u6e10\u73b0\u51fa\u4e00\u7247\u7ea2\u6655\uff0c\u663e\u89c1\u4ed6\u7684\u5185\u5fc3 \u50cf\u4e00\u9505\u716e\u6cb8\u4e86\u7684\u5f00\u6c34\uff0c\u5341\u5206\u6fc0\u52a8\uff0c\u53ef\u662f\u4e91\u7d20\u7d20\u5fc3\u60c5\u6bd4\u4ed6\u8fd8\u8981\u6fc0\u52a8\uff0c\u5979\u59cb\u800c\u60ca\u5947\uff0c\u7ee7\u800c\u9a87\u6015\uff0c \u7ec8\u800c\u601c\u60af\uff0c\u5979\u7528\u98a4\u6296\u7684\u7136\u800c\u53c8\u662f\u575a\u5b9a\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u7239\uff0c\u8bf4\u51fa\u6765\u5427\uff0c\u4f60\u505a\u4e86\u5929\u5927\u7684\u9519\u4e8b\uff0c\u7d20 \u7d20\u603b\u662f\u4f60\u7684\u5973\u513f\uff01\u201d \u3000\u3000\u4e91\u821e\u9633\u7d27\u8e59\u7684\u7709\u5c16\u7a0d\u7a0d\u8212\u5c55\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4e8c\u5341\u5e74\u524d\u2026\u2026\u54a6\uff0c\u5f85\u6211\u770b\u770b\u53c8\u662f\u4ec0\u4e48\u8001\u670b\u53cb \u6765\u4e86\uff1f\u201d\u4e91\u7d20\u7d20\u672c\u60f3\u529d\u5979\u7684\u7236\u4eb2\u8bf4\u4e86\u518d\u8d70\uff0c\u4fa7\u8033\u4e00\u542c\uff0c\u5f02\u58f0\u56db\u8d77\uff0c\u521d\u542c\u4e4b\u65f6\uff0c\u5c1a\u8fdc\u5728\u95e8\u5916\uff0c \u7728\u773c\u4e4b\u95f4\uff0c\u5c31\u5230\u4e86\u5ead\u9662\uff0c\u800c\u4e14\u7adf\u4f3c\u6709\u6570\u4eba\u4e4b\u591a\uff01\u4e91\u821e\u9633\u9053\uff1a\u201c\u7d20\u7d20\uff0c\u4f60\u5728\u8fd9\u4e66\u623f\u91cc\u9762\u4e0d\u8981\u51fa \u6765\u3002\u201d\u7d27\u5f20\u7684\u795e\u8272\u4e0d\u4e9a\u4e8e\u6076\u6597\u77f3\u5929\u94ce\u4e4b\u65f6\u3002 \u3000\u3000\u4e91\u7d20\u7d20\u4ece\u7a97\u53e3\u671b\u51fa\u53bb\uff0c\u53ea\u89c1\u9662\u5b50\u91cc\u4e00\u6392\u7ad9\u7740\u4e94\u4e2a\u8001\u8005\uff0c\u4e09\u4e2a\u662f\u9053\u58eb\uff0c\u5176\u4ed6\u4e24\u4e2a\u4fd7\u5bb6\u88c5\u675f \u7684\u4e00\u80a5\u4e00\u7626\uff0c\u5916\u578b\u50cf\u662f\u4e61\u7ec5\u548c\u6559\u5b66\u5148\u751f\u3002\u4e91\u821e\u9633\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6b66\u5f53\u4e94\u8001\uff0c\u4e00\u9f50\u5149\u4e34\uff0c\u771f\u4ee4\u84ec \u7b5a\u751f\u8f89\uff01\u201d \u3000\u3000\u4e91\u7d20\u7d20\u5403\u4e86\u4e00\u60ca\uff0c\u8fd9\u6b66\u5f53\u4e94\u8001\u7684\u540d\u5934\u5979\u66fe\u542c\u7236\u4eb2\u8bf4\u8fc7\u3002\u6b66\u5f53\u6d3e\u662f\u5f53\u65f6\u6b66\u6797\u7684\u201c\u5927\u5b97\u201d\uff0c \u95e8\u5f92\u6700\u591a\uff0c\u6709\u9053\u5bb6\u5f1f\u5b50\u4e5f\u6709\u4fd7\u5bb6\u5f1f\u5b50\u3002\u8fd9\u4e09\u4e2a\u8001\u9053\u58eb\u4fbf\u662f\u6b66\u5f53\u5c71\u9053\u5bb6\u7684\u957f\u8001\uff0c\u4e00\u4e2a\u540d\u5524\u667a \u5706\uff0c\u4e43\u662f\u4e3b\u6301\uff1b\u4e00\u4e2a\u540d\u5524\u667a\u5f18\uff0c\u4e43\u662f\u76d1\u5bfa\uff1b\u4e00\u4e2a\u540d\u5524\u667a\u5e7f\uff0c\u4e43\u662f\u8fbe\u6469\u9662\u7684\u9996\u5ea7\u3002\u90a3\u4e2a\u7c7b\u4f3c\u4e61 \u7ec5\u6a21\u6837\u7684\u8001\u8005\u540d\u5524\u5468\u6850\uff0c\u662f\u6b66\u5f53\u5317\u6d3e\u7684\u540d\u5bbf\uff1b\u90a3\u4e2a\u7c7b\u4f3c\u6559\u5b66\u5148\u751f\u7684\u540d\u5524\u8c37\u949f\uff0c\u662f\u6b66\u5f53\u5357\u6d3e\u7684 \u540d\u5bbf\u3002\u8fd9\u4e24\u4e2a\u4eba\u5929\u5357\u5730\u5317\uff0c\u800c\u4eca\u548c\u6b66\u5f53\u5c71\u7684\u4e09\u4e2a\u8001\u9053\u805a\u96c6\u540c\u6765\uff0c\u663e\u89c1\u6781\u4e0d\u5bfb\u5e38\u3002 \u3000\u3000\u667a\u5706\u957f\u8001\u9996\u5148\u8bf4\u9053\uff1a\u201c\u65e0\u4e8b\u4e0d\u767b\u4e09\u5b9d\u6bbf\uff0c\u4eca\u65e5\u7279\u6765\u95ee\u4f60\u8981\u4eba\u3002\u201d\u4e91\u821e\u9633\u65e9\u5df2\u6599\u5230\u4ed6\u8bf4\u8fd9 \u8bdd\uff0c\u82e5\u5728\u4e00\u4e24\u65e5\u524d\uff0c\u4ed6\u89c1\u4e94\u8001\u9f50\u6765\uff0c\u5206\u660e\u6446\u51fa\u6043\u4f17\u8981\u631f\u7684\u5f62\u52bf\uff0c\u5b9a\u7136\u53d1\u6012\uff0c\u5982\u4eca\u7ecf\u8fc7\u8fd9\u4e00\u573a \u5de8\u53d8\uff0c\u90a3\u4e89\u5f3a\u8981\u80dc\u4e4b\u5fc3\u65e9\u5df2\u51b7\u4e86\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u8981\u4eba\uff0c\u8fd9\u4e2a\u5bb9\u6613\u3002\u8bf7\u8fdb\u91cc\u9762\u5f85\u8336\u3002\u201d\u6b66\u5f53\u4e94 \u8001\u672c\u4ee5\u4e3a\u6709\u4e00\u573a\u4e89\u6267\uff0c\u60f3\u4e0d\u5230\u4e91\u821e\u9633\u4e00\u53e3\u5e94\u5141\u3002\u667a\u5f18\u9053\uff1a\u201c\u4e0a\u5b98\u5929\u91ce\u679c\u7136\u662f\u5728\u8fd9\u513f\u3002\u54fc\uff0c \u54fc\uff0c\u4f60\u5c06\u54b1\u4eec\u7684\u638c\u95e8\u5f1f\u5b50\u600e\u4e48\u6837\u4e86\u3002\u201d\u5468\u6850\u6027\u5b50\u6700\u6025\uff0c\u4e0d\u7b49\u667a\u5f18\u8bf4\u5b8c\uff0c\u5c31\u5927\u58f0\u53eb\u9053\uff1a\u201c\u65e2\u662f \u5bb9\u6613\uff0c\u4f60\u5c31\u8d76\u5feb\u9001\u4ed6\u51fa\u6765\u3002\u8c01\u6709\u529f\u592b\u559d\u4f60\u7684\u8336\uff01\u201d \u3000\u3000\u4e91\u821e\u9633\u9762\u8272\u4e00\u6c89\uff0c\u7ec8\u4e8e\u8fd8\u662f\u5fcd\u7740\u4e0d\u53d1\uff0c\u4ef0\u5929\u6253\u4e86\u4e00\u4e2a\u54c8\u54c8\uff0c\u9053\uff1a\u201c\u4e94\u8001\u65e2\u4e0d\u653e\u5fc3\uff0c\u90a3\u4e48 \u54b1\u4eec\u5c31\u53bb\uff0c\u770b\u770b\u4e91\u67d0\u6709\u5426\u4e8f\u5f85\u4f60\u4eec\u7684\u638c\u95e8\u5f1f\u5b50\uff1f\u201d\u4e91\u7d20\u7d20\u9694\u7a97\u53eb\u9053\uff1a\u201c\u7239\u7239\uff01\u201d\u6b63\u5f85\u8df3\u51fa\uff0c \u4e91\u821e\u9633\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u7d20\u7d20\uff0c\u8fd9\u4e8b\u60c5\u4f60\u4e0d\u8981\u7ba1\u3002\u7239\u7239\u7b54\u5e94\uff0c\u4eca\u65e5\u8981\u4e3a\u4f60\u5927\u53d1\u6148\u60b2\uff0c\u4f60\u653e\u5fc3 \u5427\u3002\u201d\u8bf4\u5230\u6700\u540e\u4e00\u53e5\uff0c\u51e0\u4e2a\u4eba\u65e9\u5df2\u51fa\u4e86\u5927\u95e8\uff0c\u5954\u4e0a\u5c71\u5761\u4e86\uff0c\u90a3\u58f0\u97f3\u662f\u7528\u4e0a\u4e58\u7684\u5185\u529f\u4f20\u8fc7\u6765 \u7684\u3002\u4e91\u7d20\u7d20\u5927\u6025\uff0c\u8d76\u5fd9\u8ffd\u51fa\uff0c\u5979\u8fd8\u672a\u8f6c\u8fc7\u5c71\u5761\uff0c\u4e91\u821e\u9633\u548c\u6b66\u5f53\u4e94\u8001\u5df2\u662f\u5230\u4e86\u90a3\u4e2a\u77f3\u6d1e\u524d\u9762\u3002 \u3000\u3000\u4f46\u89c1\u6d1e\u95e8\u5012\u584c\uff0c\u5730\u4e0a\u5c3d\u662f\u6728\u788e\u5757\uff0c\u4e91\u821e\u9633\u5403\u4e86\u4e00\u60ca\uff0c\u9886\u5934\u5954\u5165\uff0c\u52c3\u7136\u6012\u9053\uff1a\u201c\u4f60\u4eec\u5408\u529b \u5c06\u6211\u7684\u6d1e\u95e8\u6467\u6bc1\uff0c\u8fd8\u6765\u95ee\u6211\u8981\u4eba\uff1f\u201d\u667a\u5f18\u66f4\u6012\uff0c\u559d\u9053\uff1a\u201c\u4f60\u4e5f\u662f\u4e2a\u6210\u540d\u4eba\u7269\uff0c\u600e\u7684\u5982\u6b64\u64a4 \u8d56\uff1f\u201d\u5468\u6850\u9053\uff1a\u201c\u4f60\u628a\u54b1\u4eec\u7684\u638c\u95e8\u5b50\u5f1f\u6536\u5728\u90a3\u513f\uff0c\u662f\u4e0d\u662f\u5c06\u4ed6\u5bb3\u4e86\uff1f\u201d\u8d8a\u8bf4\u8d8a\u6012\uff0c\u970d\u5730\u4e00\u62db \u201c\u4e91\u9501\u82cd\u5c71\u201d\u5c31\u5411\u4e91\u821e\u9633\u7684\u7435\u7436\u9aa8\u6293\u53bb\uff0c\u4e91\u821e\u9633\u4e00\u58f0\u51b7\u7b11\uff0c\u53cd\u624b\u4e00\u5e26\uff0c\u5468\u6850\u89c9\u5f97\u4e24\u814b\u98ce\u751f\uff0c \u672a\u53ca\u62db\u67b6\uff0c\u5df2\u662f\u88ab\u4ed6\u5e26\u51fa\u5c71\u6d1e\u3002\u4e91\u821e\u9633\u5728\u6d1e\u53e3\u4e00\u7ad9\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u8981\u6253\u51fa\u5916\u9762\u6765\u6253\uff0c\u522b\u6bc1\u4e86 \u6211\u7684\u9759\u5ba4\u3002\u201d \u3000\u3000\u667a\u5706\u957f\u8005\u8f83\u6db5\u517b\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u4e0d\u662f\u5e02\u4e95\u4e4b\u5f92\uff0c\u8bf7\u4e24\u4f4d\u6682\u4e14\u52a8\u624b\uff0c\u5f85\u5224\u660e\u66f2\u76f4\u4e4b\u540e\uff0c\u662f \u975e\u81ea\u6709\u516c\u8bba\u3002\u201d\u8fd9\u8bdd\u4f3c\u4e4e\u4e24\u7686\u8d23\u5907\uff0c\u5b9e\u5df2\u662f\u504f\u8892\u4e86\u5468\u6850\u3002\u4e91\u821e\u9633\u4e00\u809a\u5b50\u95f7\u6c14\uff0c\u987e\u7740\u8eab\u4efd\uff0c\u4e0d \u4fbf\u53d1\u4f5c\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u73b0\u6210\u7684\u4e8b\u5b9e\u6446\u5728\u773c\u524d\uff0c\u987b\u4e0d\u6015\u4f60\u4eec\u4eba\u591a\u53e3\u4f17\u3002\u201d \u3000\u3000\u667a\u5706\u957f\u8001\u6700\u540e\u8d70\u51fa\uff0c\u5411\u5730\u4e0a\u4e00\u77a7\uff0c\u8bf4\u9053\uff1a\u201c\u5c45\u58eb\u8bf4\u8fd9\u6d1e\u95e8\u662f\u6211\u4eec\u6bc1\u7684\uff0c\u5c45\u58eb\u56fa\u7136\u662f\u4e00\u4ee3 \u5927\u5e08\uff0c\u54b1\u4eec\u4e94\u4e2a\u8001\u673d\u4e5f\u8fd8\u7565\u64a4\u6b66\u5b66\u76ae\u6bdb\uff0c\u8fd9\u6d1e\u95e8\u4e00\u770b\u5c31\u77e5\u662f\u4e00\u4eba\u638c\u529b\u6240\u6bc1\uff0c\u4e0d\u77e5\u662f\u5c45\u58eb\u6709\u5fc3 \u8bec\u8d56\u8fd8\u662f\u6545\u4f5c\u65e0\u77e5\uff1f\u201d \u3000\u3000\u4e91\u821e\u9633\u5fc3\u4e2d\u4e00\u51db\uff0c\u518d\u7559\u795e\u770b\u90a3\u5730\u4e0a\u7684\u6728\u5934\u788e\u5757\uff0c\u82e5\u662f\u4e94\u4e2a\u4eba\u8054\u638c\u5408\u529b\uff0c\u5219\u6728\u5934\u6240\u53d7\u7684\u538b \u529b\u4ece\u56db\u9762\u800c\u6765\uff0c\u7406\u4e0d\u5e94\u788e\u6210\u4e00\u5757\u5757\u7684\u5f62\u72b6\u3002\u8fd9\u9053\u7406\u4e91\u821e\u9633\u672c\u8be5\u65e9\u5c31\u770b\u51fa\uff0c\u53ea\u56e0\u4ed6\u5fc3\u4e2d\u5148\u5165\u4e3a \u4e3b\uff0c\u517c\u4e4b\u77f3\u5929\u950b\u5df2\u6b7b\uff0c\u4ed6\u6839\u672c\u4e0a\u6ca1\u6709\u60f3\u5230\u4e16\u4e0a\u8fd8\u6709\u50cf\u4ed6\u8fd9\u822c\u529f\u529b\u8db3\u4ee5\u9707\u584c\u6d1e\u95e8\u7684\u4eba\uff0c\u4e00\u65f6\u95f4 \u65e0\u6687\u7ec6\u601d\uff0c\u8bdd\u5df2\u51fa\u53e3\uff0c\u7adf\u88ab\u667a\u5706\u957f\u8001\u62ff\u6765\u5f53\u4f5c\u53cd\u8bc1\u3002 \u3000\u3000\u4e91\u821e\u9633\u88ab\u667a\u5706\u957f\u8001\u95ee\u4f4f\uff0c\u53ea\u5f97\u8bf4\u9053\uff1a\u201c\u662f\u6211\u770b\u8d70\u4e86\u773c\uff0c\u90a3\u4e48\u4e0a\u5b98\u5929\u91ce\u65e9\u5c31\u8dd1\u4e86\u3002\u201d\u667a\u5f18 \u957f\u8001\u563f\u563f\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u6d1e\u95e8\u660e\u660e\u662f\u4f60\u81ea\u5df1\u9707\u584c\u7684\uff0c\u5374\u8d56\u5230\u6211\u4eec\u5934\u4e0a\uff0c\u662f\u4f55\u5c45\u5fc3\uff1f\u201d\u667a\u5706\u957f \u8001\u9053\uff1a\u201c\u4ed6\u5df2\u8ba4\u9519\uff0c\u4e5f\u5c31\u7b97\u4e86\u3002\u54b1\u4eec\u73b0\u5728\u53ea\u662f\u95ee\u4ed6\u8981\u4eba\u3002\u201d\u5468\u6850\u4e5f\u63d2\u53e3\u9053\uff1a\u201c\u4f60\u56da\u7981\u54b1\u4eec\u7684 \u638c\u95e8\u5b50\uff0c\u53c8\u6545\u610f\u81ea\u6bc1\u6d1e\u95e8\uff0c\u54fc\uff0c\u54fc\uff0c\u4f60\u662f\u4e0d\u662f\u610f\u6b32\u79fb\u7978\u6c5f\u4e1c\uff0c\u4f60\u5230\u5e95\u5c06\u54b1\u4eec\u7684\u638c\u95e8\u5f1f\u5b50\u600e\u4e48 \u6837\u4e86\uff1f\u201d\u6b66\u5f53\u4e94\u8001\u4e00\u4eba\u4e00\u53e5\uff0c\u8d8a\u8bf4\u8d8a\u5389\u5bb3\u3002 \u3000\u3000\u4e91\u821e\u9633\u6012\u9053\uff1a\u201c\u6211\u82e5\u8981\u6740\u4e0a\u5b98\u5929\u91ce\uff0c\u4f55\u987b\u5982\u6b64\u4f5c\u4e3a\uff1f\u8bd5\u95ee\u6211\u82e5\u8981\u628a\u4ed6\u4e22\u5230\u5c71\u8c37\u53bb\u5582\u72fc\uff0c \u5bf9\u4f60\u4eec\u77e2\u53e3\u5426\u8ba4\uff0c\u4f60\u4eec\u53c8\u5c06\u5948\u6211\u4f55\uff1f\u201d\u8fd9\u8bdd\u8bf4\u5f97\u9887\u4e3a\u6709\u7406\uff0c\u4e91\u821e\u9633\u786e\u662f\u4e0d\u5fc5\u7528\u5982\u6b64\u7b28\u62d9\u7684\u65b9 \u6cd5\u6765\u81ea\u6bc1\u6d1e\u95e8\uff0c\u8c0b\u6740\u4e00\u4e2a\u665a\u8f88\u3002 \u3000\u3000\u4f46\u4e94\u8001\u5747\u662f\u5982\u6b64\u5fc3\u60f3\uff1a\u9664\u4e86\u4e91\u821e\u9633\u4e4b\u5916\uff0c\u65ad\u6ca1\u6709\u7b2c\u4e8c\u4e2a\u4eba\u6709\u6b64\u529f\u529b\u3002\u4e91\u821e\u9633\u53c8\u9053\uff1a\u201c\u4f60 \u4eec\u4e5f\u4eb2\u773c\u770b\u89c1\u4e86\uff1f\u8fd9\u9759\u5ba4\u4e4b\u4e2d\uff0c\u7559\u6709\u6211\u6f5c\u7814\u6b66\u5b66\u7684\u6bd5\u751f\u5fc3\u8840\u3002\u6211\u628a\u4e0a\u5b98\u5929\u91ce\u5173\u5728\u6b64\u95f4\uff0c\u8fd9\u4e00 \u756a\u82e6\u5fc3\u7528\u610f\uff0c\u4f60\u4eec\u96be\u9053\u8fd8\u770b\u4e0d\u51fa\u6765\uff1f\u201d \u3000\u3000\u667a\u5e7f\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u90fd\u662f\u4f60\u4e00\u4eba\u81ea\u8bf4\u81ea\u8bdd\uff0c\u8c01\u77e5\u9053\u4f60\u600e\u6837\u5bf9\u5f85\u4e0a\u5b98\u5929\u91ce\uff1f\u201d\u8c37\u949f\u4e5f\u9053\uff1a \u201c\u662f\u5440\uff0c\u4f60\u662f\u4e0d\u662f\u5c06\u4ed6\u5173\u5728\u8fd9\u91cc\uff0c\u6211\u4eec\u600e\u751f\u77e5\u9053\uff1f\u7eb5\u8bb8\u4f60\u771f\u7684\u5173\u4ed6\u5728\u6b64\uff0c\u4f60\u81ea\u6bc1\u6d1e\u95e8\uff0c\u66f4\u8bc1 \u5b9e\u4f60\u5bf9\u4ed6\u4e0d\u5229\u3002\u201d\u667a\u5f18\u9053\uff1a\u201c\u9664\u975e\u4f60\u5c06\u4e0a\u5b98\u5929\u91ce\u5373\u627e\u51fa\u6765\uff0c\u5426\u5219\u8c01\u76f8\u4fe1\u4f60\u7684\u9b3c\u8bdd\u3002\u201d \u3000\u3000\u5ffd\u542c\u5230\u4e00\u4e2a\u6e05\u8106\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u4e0a\u5b98\u5929\u91ce\u786e\u66fe\u5173\u5728\u6b64\u95f4\u3002\u8fd9\u6d1e\u95e8\u4e0d\u662f\u6211\u7239\u7239\u6240\u6bc1\u3002\u201d\u8fd9 \u4e24\u53e5\u8bdd\u8bf4\u5f97\u65a9\u9489\u622a\u94c1\uff0c\u5e26\u7740\u4e00\u80a1\u5b9a\u7136\u8981\u4eba\u76f8\u4fe1\u7684\u795e\u6c14\u3002\u539f\u6765\u662f\u4e91\u7d20\u7d20\u8d76\u4e0a\u6765\u4e86\u3002\u667a\u5f18\u9053\uff1a \u201c\u4e91\u5927\u5c0f\u59d0\uff0c\u4f60\u662f\u7ed9\u4ee4\u5c0a\u4f5c\u8bc1\u6765\u4e86\uff1f\u201d\u8bdd\u610f\u4e2d\u5b9e\u5728\u6697\u542b\u8ba5\u8bbd\uff0c\u4e91\u7d20\u7d20\u795e\u6001\u7aef\u5e84\uff0c\u4ecd\u662f\u4e00\u672c\u6b63 \u7ecf\u7684\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u7ed9\u7239\u7239\u4f5c\u8bc1\u3002\u56e0\u4e3a\u6211\u6628\u665a\u4eb2\u81ea\u89c1\u5230\u4e0a\u5b98\u5929\u91ce\uff0c\u662f\u6211\u610f\u6b32\u653e\u4ed6\u9003\uff0c\u53ef \u662f\u4ed6\u4e0d\u80af\u8d70\u3002\u201d\u8f6c\u8fc7\u5934\u5bf9\u4e91\u821e\u9633\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u4e0d\u607c\u6211\u5417\uff1f\u201d \u3000\u3000\u8c37\u949f\u7b11\u9053\uff1a\u201c\u4e91\u5927\u5c0f\u59d0\u6709\u8fd9\u6837\u7684\u597d\u5fc3\uff1f\u201d\u667a\u5f18\u9053\uff1a\u201c\u771f\u662f\u5f3a\u5c06\u624b\u4e0b\u65e0\u5f31\u5175\u4e86\u3002\u201d\u4e24\u4eba\u4e00 \u5439\u4e00\u6253\uff0c\u663e\u7136\u662f\u5f53\u4e91\u7d20\u7d20\u6709\u610f\u504f\u8892\u7236\u4eb2\uff0c\u5bf9\u5979\u7684\u8bf4\u8bdd\u6beb\u4e0d\u76f8\u4fe1\uff0c\u4e91\u821e\u9633\u8fd9\u4e00\u6c14\u975e\u540c\u5c0f\u53ef\uff0c\u4f46 \u89c9\u6b66\u5f53\u4e94\u8001\u4e0d\u4fe1\u4ed6\u7684\u8bdd\u72b9\u81ea\u53ef\u6055\uff0c\u4e0d\u4fe1\u4ed6\u5973\u513f\u7684\u8bdd\uff0c\u90a3\u53ef\u662f\u5bf9\u4ed6\u5927\u5927\u7684\u4fae\u8fb1\uff0c\u53ea\u542c\u5f97\u4ed6\u4e00\u58f0 \u72c2\u5578\uff0c\u7830\u7684\u4e00\u638c\u626b\u53bb\uff0c\u5c06\u4e00\u5757\u5927\u77f3\u6253\u5f97\u88c2\u4e3a\u516b\u5757\uff0c\u77f3\u5c51\u7eb7\u98de\uff01 \u3000\u3000\u6b66\u5f53\u4e94\u8001\u4e00\u9f50\u63a0\u8d77\uff0c\u970e\u773c\u4e4b\u95f4\u6392\u6210\u4e86\u4e00\u4e2a\u5706\u9635\uff0c\u51c6\u5907\u5e94\u654c\u3002\u667a\u5706\u957f\u8001\u9053\uff1a\u201c\u4f60\u5f3a\u8f9e\u593a \u7406\uff0c\u8bf4\u4e0d\u901a\u4e86\uff0c\u5c31\u8001\u7f9e\u6210\u6012\u5417\uff1f\u201d\u4e91\u821e\u9633\u4ef0\u5929\u5927\u7b11\uff0c\u9053\uff1a\u201c\u6211\u8981\u8bf4\u7406\uff0c\u4e5f\u5f97\u770b\u5bf9\u65b9\u662f\u4e0d\u662f\u8bf4 \u7406\u4e4b\u4eba\u3002\u4f60\u8bf4\u6211\u5f3a\u8f9e\u593a\u7406\uff0c\u597d\uff0c\u6211\u5c31\u5f3a\u8f9e\u593a\u7406\uff01\u4e0a\u5b98\u5929\u91ce\u4e43\u662f\u540e\u5b66\u665a\u8f88\uff0c\u5bf9\u6211\u6beb\u65e0\u793c\u8c8c\uff0c\u6df1 \u591c\u64c5\u95ef\u5c71\u95e8\uff0c\u662f\u6211\u5c06\u4ed6\u62ff\u4e86\u6740\u4e86\uff0c\u4f60\u4eec\u53bb\u8bf7\u6b66\u6797\u516c\u65ad\u5427\uff01\u201d \u3000\u3000\u6b66\u5f53\u4e94\u8001\u9762\u9762\u76f8\u89d1\uff0c\u4e0d\u77e5\u4ed6\u8bf4\u7684\u662f\u771f\u662f\u5047\uff0c\u667a\u5706\u957f\u8001\u81ea\u547d\u5fb7\u9ad8\u671b\u91cd\uff0c\u5e73\u751f\u8bb2\u7a76\u7684\u662f\u4ee5\u7406 \u670d\u4eba\uff0c\u4e91\u821e\u9633\u867d\u7136\u81ea\u77e5\u201c\u5f3a\u8f9e\u593a\u7406\u201d\uff0c\u5230\u5e95\u8fd8\u6cbe\u4e86\u4e00\u4e2a\u201c\u7406\u201d\u5b57\uff0c\u5e72\u54b3\u4e86\u4e00\u58f0\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a \u201c\u4e0a\u5b98\u5929\u91ce\u82e5\u662f\u65e0\u7f18\u65e0\u6545\u64c5\u81ea\u95ef\u8fdb\u4f60\u5bb6\uff0c\u90a3\u4e48\u4f60\u62ff\u4ed6\u6740\u4e86\u5250\u4e86\uff0c\u8001\u673d\u51b3\u4e0d\u6562\u4e3a\u4ed6\u51fa\u5934\uff0c\u4f60\u7a83 \u5360\u4e86\u6b66\u5f53\u6d3e\u7684\u5251\u8c31\uff0c\u4ed6\u8eab\u4e3a\u638c\u95e8\u5f1f\u5b50\uff0c\u5411\u4f60\u8ba8\u8fd8\uff0c\u600e\u80fd\u8bf4\u662f\u65e0\u7406\uff1f\u201d \u3000\u3000\u4e91\u821e\u9633\u9762\u8272\u4e00\u53d8\uff0c\u5374\u8f6c\u8fc7\u5934\u6765\u5bf9\u5973\u513f\u8bf4\u9053\uff1a\u201c\u5e74\u9752\u4eba\u8bf4\u7684\u8bdd\u4e5f\u672a\u5fc5\u53ef\u9760\uff0c\u4f60\u4ee5\u540e\u5f97\u5c0f\u5fc3 \u5728\u610f\u624d\u662f\u3002\u6211\u628a\u4e0a\u5b98\u5929\u91ce\u5f53\u4f5c\u4e00\u4e2a\u4eba\u6750\uff0c\u539f\u6765\u4ed6\u4e5f\u4f1a\u9a97\u6211\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u66fe\u5bf9\u4ed6\u8bf4\u8fc7\uff0c\u4ed6\u5949\u4e86\u725f\u4e00\u7c9f\u7684\u9057\u547d\u800c\u6765\uff0c\u8fd9\u5251\u8c31\u4e4b\u4e8b\u4ece\u672a\u5411\u4eba\u63d0\u8fc7\uff1b\u53c8\u8bf4\u8fc7\u4ed6 \u6765\u8d3a\u5170\u5c71\u4e4b\u524d\uff0c\u66fe\u7559\u4e86\u4e00\u5c01\u4fe1\u7ed9\u667a\u5706\u957f\u8001\uff0c\u8981\u7b49\u4e86\u4e00\u5e74\u4e4b\u540e\u4e0d\u95fb\u6d88\u606f\uff0c\u667a\u5706\u957f\u8001\u624d\u884c\u5f00\u62c6\uff0c \u73b0\u5728\u6b66\u5f53\u4e94\u8001\u51e0\u4e4e\u662f\u63a5\u8e35\u800c\u6765\uff0c\u800c\u4e14\u4e00\u6765\u5c31\u63d0\u5251\u8c31\uff0c\u8981\u4e0d\u4e0a\u5b98\u5929\u91ce\u8bf4\u8c0e\uff0c\u90a3\u5c31\u662f\u667a\u5706\u957f\u8001\u672a \u5230\u671f\u5148\u884c\u5077\u62c6\u4e86\u3002\u4e91\u821e\u9633\u4e00\u53e3\u54ac\u5b9a\u4e0a\u5b98\u5929\u91ce\u9a97\u4ed6\uff0c\u5b9e\u662f\u6709\u610f\u8bf4\u7ed9\u5973\u513f\u542c\u7684\uff0c\u542b\u6709\u501f\u4e0a\u5b98\u5929\u91ce \u4e4b\u4e8b\uff0c\u529d\u5979\u4e0d\u8981\u592a\u8fc7\u76f8\u4fe1\u9648\u7384\u673a\uff0c\u4ee5\u514d\u4e0a\u5f53\u7684\u610f\u601d\u3002 \u3000\u3000\u667a\u5706\u957f\u8001\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4e0a\u5b98\u5929\u91ce\u9a97\u4f60\u4ec0\u4e48\uff1f\u201d\u90a3\u5c01\u4fe1\u7684\u786e\u662f\u4ed6\u81ea\u884c\u5f00\u62c6\u7684\uff0c\u539f\u6765\u4ed6 \u65e9\u5df2\u4ece\u522b\u7684\u65b9\u9762\u9690\u9690\u98ce\u95fb\u4e86\u4e91\u821e\u9633\u7a83\u53d6\u725f\u5bb6\u5251\u8c31\u4e4b\u4e8b\uff0c\u4e00\u89c1\u4e0a\u5b98\u5929\u91ce\u7559\u4e0b\u4fe1\u4ef6\uff0c\u4e0d\u522b\u800c\u884c\uff0c \u65e9\u5df2\u6599\u5230\u51e0\u5206\uff0c\u90a3\u5c01\u4fe1\u662f\u4ed6\u77e5\u9053\u4e0a\u5b98\u5929\u91ce\u51fa\u8d70\u7684\u65f6\u5019\u5c31\u7acb\u523b\u5f00\u6298\u7684\u3002 \u3000\u3000\u4e91\u821e\u9633\u9053\uff1a\u201c\u4e0a\u5b98\u5929\u91ce\u9a97\u4e86\u6211\uff0c\u6211\u4e5f\u53ea\u597d\u81ea\u8ba4\u662f\u778e\u4e86\u773c\u775b\uff0c\u4e0d\u987b\u4f60\u6765\u8fc7\u95ee\uff0c\u597d\uff0c\u4f60\u65e2\u662f \u63d0\u5251\u8c31\uff0c\u54b1\u4eec\u5c31\u8bf4\u5251\u8c31\u7684\u4e8b\u60c5\u3002\u6211\u662f\u725f\u5bb6\u7684\u5973\u5a7f\uff0c\u4f60\u662f\u725f\u5bb6\u7684\u4ec0\u4e48\u4eba\uff1f\u6211\u5cb3\u7236\u662f\u524d\u4efb\u7684\u8d35\u6d3e \u638c\u95e8\uff0c\u4f46\u4ed6\u6240\u5f97\u7684\u5251\u8c31\uff0c\u5374\u672a\u5fc5\u5b9a\u8981\u5f52\u8d35\u6d3e\u516c\u6709\u3002\u6b66\u6797\u4e2d\u6ca1\u6709\u8fd9\u4e2a\u89c4\u77e9\uff01\u82e5\u8bf4\u6211\u5cb3\u7236\u66fe\u6709\u9057 \u547d\uff0c\u5251\u8c31\u4e0d\u4f20\u5b50\u5973\u800c\u4f20\u540e\u4efb\u638c\u95e8\u7684\u8bdd\uff0c\u90a3\u5c31\u8bf7\u4f60\u5c06\u6211\u5cb3\u7236\u7684\u9057\u547d\u62ff\u51fa\u6765\uff01\u201d \u3000\u3000\u53c8\u662f\u4e00\u756a\u201c\u5f3a\u8f9e\u593a\u7406\u201d\u667a\u5706\u957f\u8001\u9762\u76ae\u6da8\u5f97\u901a\u7ea2\uff0c\u4e00\u65f6\u4e4b\u95f4\u8fd8\u771f\u65e0\u8bdd\u53ef\u9a73\uff0c\u8c37\u949f\u5ffd\u5730\u5411\u5176 \u4ed6\u56db\u8001\u629b\u4e86\u4e00\u4e2a\u773c\u8272\uff0c\u7ad9\u4e86\u51fa\u6765\uff0c\u9634\u607b\u607b\u7684\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u5cb3\u7236\u7ed9\u4f60\u6bd2\u624b\u5bb3\u6b7b\uff0c\u731d\u7136\u66b4\u6bd9\uff0c\u5c31 \u7b97\u4ed6\u60f3\u5199\u9057\u547d\u4e5f\u5199\u4e0d\u51fa\u6765\uff01\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u6709\u5982\u4e00\u4e2a\u7126\u96f7\uff0c\u76f4\u628a\u4e91\u7d20\u7d20\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u5fc3\u4e2d\u60f3 \u9053\uff1a\u201c\u7239\u7239\u8bf4\u4ed6\u505a\u8fc7\u4e00\u4e2a\u4ee4\u4ed6\u4e00\u751f\u540e\u6094\u7684\u5929\u5927\u9519\u4e8b\uff0c\u83ab\u975e\u5c31\u662f\u8fd9\u6869\uff1f\u4f46\u82e5\u4ed6\u771f\u7684\u6740\u4e86\u5916\u516c\uff0c \u5988\u5988\u53c8\u600e\u80fd\u4e0e\u4ed6\u76f8\u5904\u4e86\u8fd9\u8bb8\u591a\u5e74\u4e86\u3002\u6050\u6015\u4e0d\u5f85\u5982\u4eca\uff0c\u65e9\u5df2\u8be5\u51fa\u8d70\u4e86\uff01\u201d \u3000\u3000\u8fd9\u8c37\u949f\u662f\u725f\u72ec\u9038\u7684\u5927\u5f1f\u5b50\uff0c\u5165\u95e8\u5f88\u65e9\uff0c\u5e74\u7eaa\u6bd4\u725f\u4e00\u7c9f\u5927\u5341\u591a\u5c81\uff0c\u542c\u8bf4\u725f\u72ec\u9038\u901d\u4e16\u7684\u65f6 \u5019\uff0c\u53ea\u6709\u4ed6\u548c\u725f\u4e00\u7c9f\u968f\u4f8d\u5728\u4fa7\uff0c\u4ee5\u4ed6\u7684\u8eab\u4efd\uff0c\u4f3c\u4e4e\u8fd8\u4e0d\u81f3\u4e8e\u8840\u53e3\u55b7\u4eba\u3002\u4e91\u7d20\u7d20\u60f6\u60d1\u6781\u4e86\uff0c\u832b \u7136\u7684\u770b\u7740\u4ed6\u7684\u7236\u4eb2\u3002 \u3000\u3000\u53ea\u89c1\u4e91\u821e\u9633\u9762\u8272\u6c89\u6697\uff0c\u7709\u5b87\u95f4\u9690\u9690\u900f\u7740\u6740\u6c14\uff0c\u84e6\u7136\u4ef0\u5929\u957f\u7b11\uff0c\u5389\u58f0\u8bf4\u9053\uff1a\u201c\u4e91\u67d0\u5728\u4eba\u773c \u4e2d\u65e9\u5df2\u662f\u7f6a\u5927\u6076\u6781\uff0c\u4e5f\u4e0d\u5728\u4e4e\u591a\u6b64\u4e00\u6869\u3002\u54fc\uff0c\u4f17\u53e3\u60a0\u60a0\u80fd\u70c1\u9aa8\uff0c\u542b\u6c99\u5c04\u5f71\u6740\u66fe\u53c2\u3002\u6b66\u5f53\u4e94 \u8001\uff0c\u4f60\u4eec\u4eca\u65e5\u6765\u6b64\uff0c\u58f0\u52bf\u6c79\u6c79\uff0c\u539f\u6765\u7adf\u662f\u95ee\u7f6a\u6765\u7684\uff01\u6211\u4e91\u821e\u9633\u4e00\u53e5\u4e5f\u4e0d\u5206\u8fa8\uff0c\u5212\u51fa\u9053\u6765\uff0c\u9f0e \u956c\u5200\u952f\uff0c\u51b3\u4e0d\u76b1\u7709\uff01\u201d \u3000\u3000\u4e91\u7d20\u7d20\u5fc3\u4e2d\u4e00\u52a8\uff0c\u542c\u5979\u7236\u4eb2\u8fd9\u8bdd\uff0c\u8fd8\u5f15\u7528\u4e86\u201c\u66fe\u53c2\u6740\u4eba\u201d\u7684\u5178\u6545\uff0c\u5206\u660e\u662f\u6307\u8c37\u949f\u6709\u610f\u683d \u8bec\uff0c\u800c\u4e14\u770b\u4ed6\u795e\u60c5\u6fc0\u6124\u5982\u65af\uff0c\u7edd\u4e0d\u662f\u5fc3\u865a\u7406\u4e8f\u7684\u4eba\u6240\u53ef\u5047\u5192\u5f97\u6765\u3002\u4e91\u7d20\u7d20\u7a0d\u7a0d\u8212\u4e86\u53e3\u6c14\uff0c\u4f46 \u4e00\u9897\u5fc3\u4ecd\u662f\u4e03\u4e0a\u516b\u843d\uff0c\u5b9e\u5728\u4e0d\u6562\u65ad\u5b9a\uff1a\u5916\u516c\u7a76\u7adf\u662f\u4e0d\u662f\u7236\u4eb2\u6240\u6740\uff1f \u3000\u3000\u8c37\u949f\u51b7\u7b11\u9053\uff1a\u201c\u7aef\u7684\u662f\uff1a\u5927\u6076\u5927\u5978\u80fd\u5f04\u5047\uff1b\u8bf4\u751a\u4e48\uff1a\u542b\u6c99\u5c04\u5f71\u6740\u66fe\u53c2\uff1f\u6069\u5e08\u6b7b\u72b6\uff0c\u6211\u4eb2 \u773c\u89c1\u6765\uff0c\u6211\u8c37\u949f\u5c82\u662f\u8bf4\u8c0e\u4e4b\u4eba\uff0c\u96be\u9053\u8fd8\u4f1a\u8bec\u6bc1\u4f60\u4e48\uff1f\u201d \u3000\u3000\u4e91\u821e\u9633\u6602\u9996\u5411\u5929\uff0c\u610f\u6b8a\u4e0d\u5c51\uff0c\u4ece\u7259\u7f1d\u4e2d\u900f\u51fa\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u4e0d\u8bf4\u8c0e\uff0c\u53ea\u6709\u4f60\u81ea\u5df1\u77e5 \u9053\u3002\u6211\u8bf4\u8fc7\u51b3\u4e0d\u5206\u8fa9\uff0c\u4f60\u7f57\u5506\u4ec0\u4e48\uff1f\u201d\u84e6\u5730\u5251\u7709\u4e00\u626c\uff0c\u563f\u563f\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u5927\u5978\u5927\u6076\u4e4b\u540d\uff0c\u53cd \u6b63\u5df2\u5750\u5b9e\u4e86\u3002\u597d\uff0c\u6211\u4eca\u65e5\u5c31\u518d\u5e72\u4e00\u6869\uff1b\u5bf9\u4e0d\u4f4f\u4f60\u4eec\u4e94\u8001\uff0c\u6211\u53ef\u8981\u5c06\u4f60\u4eec\u7559\u4e0b\u6765\u5566\uff01\u201d \u3000\u3000\u667a\u5f18\u5927\u6012\uff0c\u66b4\u8d77\u559d\u9053\uff1a\u201c\u4e91\uff0c\u4e91\u821e\u9633\uff0c\u4f60\uff0c\u4f60\u7adf\u6562\u53e3\u51fa\u6b64\u8a00\uff0c\u8981\u5c06\u6211\u4eec\u4e94\u4e2a\u4eba\u4e00\u9f50\u7559 \u4e0b\uff1f\u54fc\u3001\u54fc\uff01\u597d\u5927\u7684\u53e3\u6c14\uff0c\u597d\u5927\u7684\u672c\u9886\uff0c\u6211\u5012\u8981\u770b\u770b\u4eca\u65e5\u662f\u8c01\u57cb\u9aa8\u8352\u5c71\uff1f\u201d\u8981\u77e5\u6b66\u5f53\u4e94\u8001\uff0c \u4e43\u662f\u6b66\u6797\u4e2d\u7684\u6cf0\u5c71\u5317\u6597\uff0c\u5355\u662f\u4e00\u4eba\u5df2\u4e0d\u5bb9\u4eba\u6b3a\u8511\uff0c\u4f55\u51b5\u662f\u4e94\u8001\u9f50\u6765\uff01\u4e91\u821e\u9633\u5374\u5c06\u4ed6\u4eec\u89c6\u540c\u65e0 \u7269\uff0c\u8981\u628a\u4ed6\u4eec\u4e00\u9f50\u6536\u62fe\uff0c\u8fd9\u6559\u6b66\u5f53\u4e94\u8001\u5982\u4f55\u4e0d\u6c14\uff1f\u667a\u5f18\u6027\u5b50\u66b4\u8e81\uff0c\u66f4\u662f\u6fc0\u6124\u4e4b\u6781\uff0c\u8fde\u8bf4\u8bdd\u7684 \u58f0\u97f3\u4e5f\u53d8\u4e86\u3002 \u3000\u3000\u773c\u89c1\u7bad\u5728\u5f26\u4e0a\uff0c\u4e00\u89e6\u5373\u53d1\uff0c\u8c37\u949f\u5ffd\u5730\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u5c06\u6211\u4eec\u7559\u4e0b\uff0c\u90a3\u662f\u6700\u597d\u4e0d\u8fc7\u3002\u6211 \u4eec\u5fdd\u4e3a\u6b66\u5f53\u957f\u8001\uff0c\u5374\u8fd8\u672a\u89c1\u8fc7\u5148\u7956\u6240\u4f20\u7684\u8fbe\u6469\u5251\u6cd5\uff0c\u6b63\u597d\u8d81\u6b64\u673a\u4f1a\u89c1\u8bc6\u89c1\u8bc6\uff01\u5373\u4f7f\u6211\u4eec\u4e94\u4e2a \u8001\u673d\u4e00\u9f50\u6bc1\u5728\u4f60\u7684\u624b\u4e0a\uff0c\u90a3\u4e5f\u662f\u503c\u5f97\u4e4b\u81f3\u3002\u597d\u6559\u5929\u4e0b\u82f1\u96c4\u90fd\u77e5\u9053\u672c\u6d3e\u7684\u795e\u5947\u6b66\u529f\uff01\u201d\u8fd9\u8c37\u949f \u4eba\u79f0\u201c\u9634\u95f4\u79c0\u624d\u201d\uff0c\u9634\u6c89\u591a\u8ba1\uff0c\u8fd9\u8bf4\u8bdd\u5176\u5b9e\u662f\u6324\u5151\u4e91\u821e\u9633\uff0c\u8ba5\u7b11\u4ed6\u5077\u4e86\u6b66\u5f53\u6d3e\u7684\u6b66\u529f\u7528\u6765\u5bf9 \u4ed8\u6b66\u5f53\u6d3e\u7684\u5f1f\u5b50\uff0c\u4e0d\u8bba\u662f\u80dc\u662f\u8d25\uff0c\u603b\u662f\u8138\u4e0a\u65e0\u5149\u3002 \u3000\u3000\u4e91\u821e\u9633\u5251\u7709\u4e00\u626c\uff0c\u84e6\u5730\u53c8\u662f\u4e00\u9635\u72c2\u7b11\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u786c\u8bf4\u8fbe\u6469\u5251\u6cd5\u662f\u4f60\u4eec\u6b66\u5f53\u6d3e \u7684\uff0c\u597d\uff0c\u4e91\u67d0\u4eca\u65e5\u5c31\u4e0d\u7528\u5b9d\u5251\uff0c\u53ea\u51ed\u8fd9\u53cc\u8089\u638c\uff0c\u770b\u80fd\u5426\u5c06\u4f60\u4eec\u62fe\u6387\u4e0b\u6765\uff1f\u201d\u667a\u5f18\u6012\u4e0d\u53ef\u6291\uff0c \u4e0d\u5f85\u4ed6\u8bdd\u8bf4\u5b8c\uff0c\u5df2\u662f\u547c\u7684\u4e00\u638c\u626b\u51fa\uff01 \u3000\u3000\u4f46\u542c\u5f97\u7b11\u58f0\u4e0d\u7edd\uff0c\u9707\u5f97\u6b66\u5f53\u4e94\u8001\u7684\u8033\u9f13\u90fd\u55e1\u55e1\u4f5c\u54cd\uff0c\u638c\u98ce\u4eba\u5f71\u4e4b\u4e2d\uff0c\u53ea\u89c1\u4e91\u821e\u9633\u51fa\u624b\u5982 \u7535\uff0c\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u5c06\u667a\u5f18\u6454\u4e86\u4e00\u4e2a\u7b4b\u6597\uff0c\u5de6\u624b\u4e00\u62ac\uff0c\u4e00\u62db\u201c\u7384\u9e1f\u5212\u6c99\u201d\uff0c\u62c7\u6307\u548c\u98df\u6307\uff0c\u6263 \u6210\u4e00\u4e2a\u7f3a\u53e3\u7684\u73af\u5f62\uff0c\u4e0b\u6309\u7684\u65b9\u4f4d\u6b63\u5f53\u8c37\u949f\u7684\u201c\u91d1\u697c\u201d\u3001\u201c\u7389\u56da\u201d\u4e24\u5904\u201c\u9690\u7a74\u201d\uff0c\u4f55\u4ee5\u53eb\u505a \u201c\u9690\u7a74\u201d\uff1f\u8fd9\u662f\u56e0\u4e3a\u8fd9\u4e24\u5904\u7a74\u9053\u6240\u8f96\u7684\u7ecf\u8109\u4e0d\u5728\u8868\u76ae\uff0c\u800c\u662f\u9690\u85cf\u5728\u4f53\u5185\u9aa8\u9ad3\u4e4b\u95f4\uff0c\u4e00\u822c\u7684\u70b9 \u7a74\u56fe\u8c31\u538b\u6839\u513f\u5c31\u4e0d\u8f7d\u6709\u8fd9\u4e24\u5904\u7a74\u9053\uff0c\u53ea\u6709\u50cf\u4e91\u821e\u9633\u8fd9\u6837\u7684\u5185\u5bb6\u9ad8\u624b\uff0c\u624d\u80fd\u591f\u628a\u771f\u529b\u900f\u8fc7\u6307 \u5c16\uff0c\u5c01\u95ed\u9690\u7a74\uff0c\u8fd9\u4e24\u5904\u9690\u7a74\u4e00\u88ab\u5c01\u95ed\uff0c\u6700\u5c11\u4e5f\u5f97\u843d\u4e2a\u534a\u8eab\u4e0d\u9042\u3002 \u3000\u3000\u201c\u7384\u9e1f\u5212\u6c99\u201d\u4e00\u62db\u4e24\u7528\uff0c\u4e91\u821e\u9633\u7684\u53e6\u5916\u4e09\u4e2a\u624b\u6307\uff0c\u98df\u6307\u4e2d\u6307\u548c\u65e0\u540d\u6307\u5219\u5e76\u62e2\u4e00\u5904\uff0c\u94c1\u6307 \u5982\u5200\uff0c\u6240\u5212\u7684\u65b9\u4f4d\u53c8\u6b63\u5f53\u8c37\u949f\u7684\u5fc3\u53e3\u8981\u5bb3\u3002\u667a\u5f18\u6240\u53d7\u7684\u90a3\u638c\u4e0d\u8fc7\u5916\u4f24\uff0c\u8c37\u949f\u82e5\u7136\u88ab\u8fd9\u4e00\u62db \u201c\u7384\u9e1f\u5212\u6c99\u201d\u4f24\u4e86\uff0c\u4e0d\u6b7b\u4e5f\u5f97\u6b8b\u5e9f\uff0c\u60f3\u662f\u4e91\u821e\u9633\u6068\u6781\u8c37\u949f\uff0c\u662f\u4ee5\u4e0b\u6b64\u6bd2\u624b\u3002 \u3000\u3000\u667a\u5e7f\u548c\u5468\u6850\u4e24\u4eba\u89c1\u72b6\u4e0d\u597d\uff0c\u5927\u53eb\u4e00\u58f0\uff0c\u53cc\u53cc\u62a2\u8fdb\uff0c\u8fd9\u4e24\u4eba\u7684\u529f\u529b\u8fdc\u80dc\u667a\u5f18\uff0c\u53cc\u638c\u5408\u6210\u4e00 \u4e2a\u5706\u5f27\uff0c\u52b2\u98ce\u8d2f\u8033\uff0c\u5288\u529b\u63d2\u8179\uff0c\u4e91\u821e\u9633\u53eb\u9053\uff1a\u201c\u6765\u5f97\u597d\uff01\u201d\u201c\u7384\u9e1f\u5212\u6c99\u201d\u7684\u624b\u6cd5\u500f\u7136\u4e00\u53d8\uff0c \u98df\u6307\u4e2d\u6307\u548c\u65e0\u540d\u6307\u5206\u5f00\u4e00\u5f39\uff0c\u6b63\u4e2d\u667a\u5e7f\u81c2\u5f2f\u7684\u201c\u66f2\u6c60\u7a74\u201d\uff0c\u667a\u5e7f\u767b\u65f6\u534a\u8eab\u9ebb\u6728\uff0c\u201c\u767b\uff0c\u767b\uff0c \u767b\uff01\u201d\u5012\u9000\u4e09\u6b65\uff0c\u4e0e\u6b64\u540c\u65f6\uff0c\u4e91\u821e\u9633\u6a2a\u80b1\u4e00\u649e\uff0c\u53c8\u628a\u5468\u6850\u649e\u5f2f\u4e0b\u4e86\u8170\u3002\u8fd9\u4e24\u62db\u8fc5\u5982\u7535\u5149\u77f3 \u706b\uff0c\u4e91\u821e\u9633\u7684\u8eab\u5f62\u53ea\u662f\u88ab\u4ed6\u4eec\u7a0d\u4e3a\u963b\u4e86\u4e00\u963b\uff0c\u4e00\u4e2a\u8fdb\u6b65\u8fde\u73af\u5954\u96f7\u638c\uff0c\u638c\u950b\u53c8\u6cbe\u5230\u4e86\u8c37\u949f\u7684\u80cc \u5fc3\u3002 \u3000\u3000\u52b2\u529b\u5c06\u53d1\u672a\u53d1\uff0c\u731b\u7136\u95f4\u4e00\u80a1\u6781\u4e3a\u51cc\u5389\u7684\u638c\u98ce\u4ece\u4fa7\u9762\u88ad\u6765\uff0c\u4e91\u821e\u9633\u5fae\u5fae\u4e00\u51db\uff0c\u811a\u8ddf\u4e00\u65cb\uff0c \u786c\u751f\u751f\u7684\u5c06\u653b\u5411\u8c37\u949f\u7684\u638c\u64a4\u4e86\u56de\u6765\uff0c\u4fa7\u8eab\u4e00\u8ba9\uff0c\u53cc\u638c\u4e00\u9f50\u62cd\u51fa\uff0c\u521a\u597d\u63a5\u7740\u4e86\u90a3\u80a1\u638c\u529b\uff0c\u56db\u638c \u76f8\u4ea4\uff0c\u4f46\u542c\u5f97\u58f0\u5982\u90c1\u96f7\uff0c\u767b\u65f6\u80f6\u7740\u3002\u51fa\u624b\u7684\u6b63\u662f\u6b66\u5f53\u4e94\u8001\u4e2d\u529f\u529b\u6700\u9ad8\u7684\u667a\u5706\u957f\u8001\u3002 \u3000\u3000\u8fd9\u76f8\u6301\u4e0d\u4e0b\u7684\u5f62\u52bf\u4e3a\u65f6\u6781\u77ed\uff0c\u4f46\u89c1\u4e91\u821e\u9633\u8eab\u5f62\u4e00\u957f\uff0c\u4e00\u4e2a\u9634\u624b\u9633\u638c\uff0c\u638c\u5fc3\u5f80\u5916\u4e00\u767b\uff0c\u5927 \u559d\u4e00\u58f0\uff1a\u201c\u5012\u4e0b\uff01\u201d\u7a81\u7136\u95f4\u638c\u529b\u6709\u5982\u6d2a\u6ce2\u7a81\u53d1\uff0c\u667a\u5706\u957f\u8001\u7981\u53d7\u4e0d\u8d77\uff0c\u7adf\u7136\u8e09\u8e09\u8dc4\u8dc4\u7684\u5012\u9000\u51e0 \u6b65\uff0c\u8eab\u5f62\u6643\u4e2a\u4e0d\u4f11\uff0c\u7136\u800c\u5374\u4e5f\u6ca1\u6709\u5012\u4e0b\u3002 \u3000\u3000\u4e91\u821e\u9633\u5f97\u610f\u72c2\u7b11\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6599\u4e0d\u5230\u6b66\u5f53\u4e94\u8001\u5982\u6b64\u8113\u5305\uff0c\u667a\u5706\u957f\u8001\u4ea6\u4e0d\u8fc7\u4ec5\u63a5\u6211\u4e09 \u638c\uff01\u201d\u8ba5\u5632\u7684\u8bf4\u8bdd\u8fd8\u672a\u51fa\u53e3\uff0c\u53ea\u89c1\u667a\u5e7f\u667a\u5f18\u8c37\u949f\u5468\u6850\u53c8\u5df2\u7ad9\u597d\u4e86\u65b9\u4f4d\uff0c\u4ece\u56db\u4e2a\u65b9\u5411\u56f4\u62e2\u5408 \u6765\u3002 \u3000\u3000\u4e91\u821e\u9633\u51b7\u7b11\u9053\uff0c\u201c\u9a71\u7fa4\u7f8a\u800c\u6597\u731b\u864e\uff0c\u867d\u591a\u4f55\u7528\uff1f\u201d\u54ea\u6599\u7b11\u58f0\u672a\u5df2\uff0c\u53ea\u89c9\u8eab\u5b50\u5468\u56f4\u90fd\u6709\u4e00 \u80a1\u65e0\u5f62\u6f5c\u529b\uff0c\u4e00\u9f50\u5411\u5f53\u4e2d\u6324\u6765\uff0c\u4e91\u821e\u9633\u5403\u4e86\u4e00\u60ca\uff0c\u4f46\u4ecd\u7136\u5e76\u4e0d\u600e\u4e48\u653e\u5728\u5fc3\u4e0a\uff0c\u968f\u624b\u4e00\u62db\u201c\u516b \u65b9\u98ce\u96e8\u201d\uff0c\u53cc\u638c\u8d77\u843d\u5982\u73af\uff0c\u5c06\u638c\u529b\u4e5f\u5411\u56db\u9762\u516b\u65b9\u53cd\u51fb\u51fa\u53bb\uff0c\u4f46\u542c\u5f97\u638c\u98ce\u9707\u8361\uff0c\u58f0\u5982\u88c2\u5e1b\uff0c\u90a3 \u56db\u4e2a\u4eba\u90fd\u8e0f\u5f00\u4e86\u4e00\u6b65\uff0c\u800c\u4e91\u821e\u9633\u5374\u6643\u4e86\u51e0\u6643\uff0c\u8fd9\u4e00\u62db\u201c\u516b\u65b9\u98ce\u96e8\u201d\u7adf\u88ab\u56db\u4eba\u7684\u8054\u5408\u638c\u529b\u786c\u9707 \u56de\u6765\uff01 \u3000\u3000\u4e91\u821e\u9633\u8fd9\u624d\u771f\u7684\u5927\u60ca\uff0c\u4f46\u5728\u4ed6\u5fc3\u76ee\u4e4b\u4e2d\uff0c\u8fd9\u56db\u4e2a\u4eba\uff0c\u4e0d\u8fc7\u4ec5\u6709\u63a5\u4ed6\u4e00\u638c\u7684\u529f\u529b\uff0c\u5373\u7b97\u8054 \u5408\u8d77\u6765\uff0c\u4e5f\u8fd8\u8fdc\u4e0d\u53ca\u4ed6\uff0c\u54ea\u77e5\u6b66\u5f53\u4e94\u8001\u7684\u638c\u6cd5\u522b\u6709\u4e00\u529f\uff0c\u53ea\u8981\u6709\u56db\u4e2a\u4eba\u8fde\u8d77\u624b\u6765\uff0c\u529f\u529b\u5c31\u9a6c \u4e0a\u589e\u52a0\u4e00\u500d\uff0c\u56db\u4e2a\u4eba\u4fbf\u7b49\u5982\u516b\u4e2a\u4eba\u4e86\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u8fd9\u56db\u4e2a\u4eba\u53ea\u662f\u6643\u4e86\u4e00\u6643\uff0c\u7acb\u5373\u53c8\u8e0f\u6b63\u65b9\u4f4d\uff0c\u5c06\u638c\u529b\u786c\u903c\u8fc7\u6765\uff0c\u4e91\u821e\u9633 \u8fd9\u56de\u4e0d\u6562\u518d\u6389\u4ee5\u8f7b\u5fc3\uff0c\u53cc\u8db3\u7ad9\u7262\uff0c\u4ee5\u201c\u62b1\u5143\u5b88\u4e00\u201d\u7684\u59ff\u52bf\uff0c\u53cc\u638c\u5408\u62b1\uff0c\u5f90\u5f90\u63a8\u51fa\uff0c\u521a\u624d\u4ed6\u90a3 \u4e00\u62db\u201c\u516b\u65b9\u98ce\u96e8\u201d\uff0c\u653b\u52bf\u867d\u7136\u51cc\u5389\uff0c\u638c\u529b\u5374\u662f\u5411\u56db\u9762\u516b\u65b9\u5206\u6563\uff0c\u800c\u4eca\u4ed6\u4ee5\u5b88\u4e3a\u653b\uff0c\u638c\u529b\u51dd \u805a\uff0c\u867d\u7136\u8fd8\u672a\u80fd\u5c06\u6b66\u5f53\u56db\u8001\u9707\u9000\uff0c\u4f46\u6b66\u5f53\u56db\u8001\u5374\u4e5f\u4e0d\u80fd\u518d\u8e0f\u8fdb\u534a\u6b65\uff01 \u3000\u3000\u6b63\u81ea\u76f8\u6301\u4e0d\u4e0b\uff0c\u4f46\u89c1\u667a\u5706\u957f\u8001\u773c\u5192\u6012\u706b\uff0c\u9762\u76d6\u4e4c\u4e91\uff0c\u5df2\u662f\u53cd\u8f6c\u4e86\u8eab\uff0c\u4e00\u6b65\u4e00\u6b65\u7684\u8d70\u6765\uff0c \u4e91\u821e\u9633\u6697\u6697\u53eb\u82e6\uff0c\u5438\u4e86\u53e3\u6c14\uff0c\u53cc\u638c\u52a0\u4e86\u52b2\u529b\uff0c\u667a\u5706\u957f\u8001\u5207\u9f7f\u9053\uff1a\u201c\u4e91\u821e\u9633\uff0c\u4ffa\u8fd9\u51e0\u6839\u8001\u9aa8\u5934 \u4eca\u65e5\u7b97\u4ea4\u7ed9\u4f60\u5566\uff01\u201d\u624b\u81c2\u4e00\u62ac\uff0c\u638c\u5fc3\u7acb\u5373\u5411\u4e91\u821e\u9633\u7684\u5934\u9876\u538b\u4e0b\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u5f62\u52bf\u5927\u53d8\uff0c\u4f46\u542c\u5f97\u638c\u98ce\u8d2f\u8033\uff0c\u9690\u9690\u631f\u6709\u98ce\u96f7\u4e4b\u58f0\uff0c\u667a\u5f18\u667a\u5e7f\u8fd9\u56db\u4e2a\u4eba\u4e5f\u8d81\u7740\u8fd9\u4e00 \u51fb\u4e4b\u5a01\uff0c\u540c\u65f6\u53d1\u52b2\uff0c\u4e91\u821e\u9633\u594b\u529b\u62b5\u6321\uff0c\u80f8\u53e3\u51e0\u4e4e\u7ed9\u638c\u529b\u538b\u5f97\u900f\u4e0d\u8fc7\u6c14\u3002\u867d\u7136\u672a\u81f3\u89c1\u8d25\uff0c\u5374\u5df1 \u7ed9\u6b66\u5f53\u4e94\u8001\u53c8\u8e0f\u8fdb\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u539f\u6765\u6b66\u5f53\u4e94\u8001\u8fd9\u5957\u638c\u6cd5\u540d\u4e3a\u201c\u4e94\u96f7\u5929\u5fc3\u638c\u6cd5\u201d\uff0c\u4e94\u4eba\u5408\u529b\u65bd\u4e3a\uff0c\u7aef\u7684\u6709\u96f7\u9706\u4e07\u94a9\u4e4b\u52bf\u3002 \u4e91\u821e\u9633\u6c89\u7740\u5e94\u4ed8\uff0c\u6b66\u5f53\u4e94\u8001\u4e00\u638c\u63a5\u7740\u4e00\u638c\uff0c\u638c\u638c\u8d8a\u6765\u8d8a\u91cd\uff0c\u5927\u7ea6\u8fc7\u4e86\u5341\u62db\uff0c\u6b66\u5f53\u4e94\u8001\u5df2\u903c\u5230 \u4e86\u4e91\u821e\u9633\u8ddf\u524d\u4e03\u5c3a\u4e4b\u5730\u3002 \u3000\u3000\u4e91\u821e\u9633\u77e5\u9053\u5982\u6b64\u4e0b\u53bb\uff0c\u7ec8\u5fc5\u88ab\u4ed6\u4eec\u56f0\u6b7b\uff0c\u7a81\u7136\u76ee\u95ea\u7cbe\u5149\uff0c\u9aa4\u65bd\u6740\u7740\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u9632\u8eab\u7684 \u5de6\u638c\u500f\u7684\u653b\u51fa\uff0c\u638c\u5288\u6307\u6233\uff0c\u653b\u5411\u529f\u529b\u6700\u5f31\u7684\u667a\u5f18\uff0c\u667a\u5f18\u5403\u4e86\u4e00\u60ca\uff0c\u7a81\u7136\u7ed9\u4ed6\u51b2\u9000\u4e86\u51e0\u6b65\uff0c\u4e91 \u821e\u9633\u5927\u559c\uff0c\u4fbf\u5f85\u7a81\u56f4\u800c\u51fa\uff0c\u5ffd\u89c9\u8111\u540e\u98ce\u751f\uff0c\u667a\u5706\u957f\u8001\u548c\u5468\u6850\u4e24\u53cc\u94c1\u638c\u4e00\u9f50\u88ad\u5230\uff0c\u8981\u77e5\u65d7\u9f13\u76f8 \u5f53\u7684\u9ad8\u624b\u62fc\u6597\uff0c\u62db\u62db\u5fc5\u987b\u5e73\u8861\uff0c\u5904\u5904\u6709\u5907\uff0c\u7136\u540e\u624d\u53ef\u4ee5\u5f85\u654c\u7684\u5931\u8bef\u800c\u5236\u80dc\uff0c\u800c\u4eca\u4e91\u821e\u9633\u5192\u9669 \u62a2\u653b\uff0c\u8eab\u540e\u4fbf\u9732\u4e86\u7834\u7efd\uff0c\u51e0\u4e4e\u7ed9\u667a\u5706\u957f\u8001\u7684\u638c\u98ce\u9707\u5012\uff0c\u5e78\u800c\u4e91\u821e\u9633\u53d8\u62db\u5f97\u5feb\uff0c\u4f46\u53c8\u5df2\u7ed9\u6b66\u5f53 \u4e94\u8001\u6062\u590d\u4e86\u539f\u72b6\u3002 \u3000\u3000\u667a\u5706\u957f\u8001\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u9996\u5c3e\u8fde\u574f\uff0c\u56db\u65b9\u5408\u51fb\uff0c\u65e0\u987b\u8d2a\u8fdb\uff0c\u4fbf\u53ef\u5236\u654c\u6b7b\u547d\u3002\u201d\u5176\u4ed6\u56db\u8001\u5fae \u5fae\u70b9\u9996\uff0c\u5468\u56f4\u7684\u52b2\u9053\u679c\u7136\u6536\u7f29\u4e86\u8bb8\u591a\uff0c\u4f46\u5b88\u52bf\u5374\u662f\u5927\u5927\u589e\u957f\uff0c\u4e91\u821e\u9633\u7684\u653b\u52bf\u4e0d\u8bba\u5411\u54ea\u4e00\u65b9\uff0c \u90fd\u4f3c\u88ab\u4e00\u5835\u94dc\u5899\u6321\u4f4f\uff0c\u8fde\u50cf\u521a\u624d\u7684\u90a3\u6837\u7a81\u88ad\u4e5f\u4e0d\u53ef\u80fd\u4e86\u3002 \u3000\u3000\u518d\u8fc7\u4e86\u534a\u679d\u9999\u65f6\u523b\uff0c\u4f46\u89c1\u4e91\u821e\u9633\u989d\u4e0a\u6c81\u51fa\u4e00\u9897\u9897\u9ec4\u8c46\u822c\u5927\u7684\u6c57\u73e0\uff0c\u5934\u4e0a\u4e5f\u5192\u51fa\u4e86\u70ed\u817e\u817e \u7684\u767d\u6c14\uff0c\u4e91\u7d20\u7d20\u867d\u7136\u9020\u8be3\u4e0d\u6df1\uff0c\u5374\u4e5f\u77e5\u9053\u8fd9\u662f\u5185\u529b\u635f\u8017\u592a\u751a\uff0c\u7cbe\u6c14\u6e10\u6e10\u67af\u7aed\u4e4b\u8c61\uff0c\u4e0d\u7531\u5f97\u5fc3 \u4e2d\u7126\u6025\uff0c\u4f46\u89c1\u6b66\u5f53\u4e94\u8001\u53f1\u54a4\u4e00\u58f0\uff0c\u540c\u65f6\u53d1\u638c\uff0c\u53c8\u8e0f\u8fdb\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u667a\u5706\u957f\u8001\u731b\u53d1\u4e24\u638c\uff0c\u773c\u89c1\u4e91\u821e\u9633\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u6beb\u65e0\u8fd8\u624b\u4e4b\u529b\uff0c\u90a3\u5982\u5c65\u8584\u51b0\u7684\u6212\u60e7\u4e4b\u8272 \u6e10\u6e10\u653e\u677e\uff0c\u8f6c\u4e3a\u50b2\u5cb8\uff0c\u518d\u8fc7\u6570\u62db\uff0c\u6b66\u5f53\u4e94\u8001\u7684\u5305\u56f4\u5708\u8d8a\u7f29\u8d8a\u5c0f\uff0c\u903c\u5230\u4e86\u4e91\u821e\u9633\u8ddf\u524d\u4e09\u5c3a\u4e4b \u5730\uff0c\u667a\u5706\u957f\u8001\u51b7\u7b11\u53d1\u8bdd\uff1a\u201c\u54b1\u4eec\u6b66\u5f53\u6d3e\u7684\u5251\u8c31\u867d\u7136\u88ab\u7a83\uff0c\u4f46\u4f60\u8fd9\u4f4d\u6b66\u529f\u76d6\u4e16\u7684\u4e91\u821e\u9633\u53ef\u4e5f\u4e0d \u80fd\u5c06\u54b1\u4eec\u8fd9\u51e0\u526f\u8001\u9aa8\u7559\u4e0b\uff01\u4f60\u800c\u4eca\u603b\u8be5\u670d\u8f93\u4e86\u5427\uff1f\u563f\u563f\uff0c\u4f60\u867d\u7136\u76db\u6c14\u51cc\u4eba\uff0c\u8981\u60f3\u8d76\u5c3d\u6740\u7edd\uff0c \u54b1\u4eec\u6b66\u5f53\u4e94\u8001\u53ef\u4e0d\u662f\u72c2\u5984\u7684\u7a77\u51f6\u6781\u6076\u4e4b\u5f92\u3002\u53ea\u8981\u4f60\u5fc3\u670d\u53e3\u670d\uff0c\u5411\u54b1\u4eec\u5404\u53e9\u4e09\u4e2a\u54cd\u5934\u8d54\u793c\uff0c\u518d \u628a\u5251\u8c31\u4ea4\u8fd8\uff0c\u90a3\u4e48\uff0c\u4e0a\u5929\u4ea6\u6709\u597d\u751f\u4e4b\u5fb7\uff0c\u54b1\u4eec\u6b66\u5f53\u4e94\u8001\u81ea\u5f53\u9976\u4f60\u5c0f\u547d\u4e00\u6761\u3002\u201d \u3000\u3000\u4e91\u821e\u9633\u542c\u4e86\u8fd9\u8bdd\uff0c\u76ee\u5149\u4e00\u95ea\uff0c\u6740\u6c14\u6697\u85cf\uff0c\u4f46\u89c1\u4ed6\u4e0d\u6012\u53cd\u7b11\uff1a\u201c\u4f60\u8bf4\u6211\u8d76\u5c3d\u6740\u7edd\uff0c\u7a77\u51f6\u6781 \u6076\uff1f\u54c8\uff0c\u90a3\u5012\u662f\u4f60\u63d0\u9192\u6211\u4e86\uff01\u201d\u667a\u5706\u957f\u8001\u88ab\u4ed6\u7684\u8bf4\u8bdd\u5413\u4e86\u4e00\u8df3\uff0c\u8fd8\u771f\u5bb3\u6015\u4ed6\u6709\u4ec0\u4e48\u6740\u624b\uff0c\u628a \u624b\u4e00\u6325\uff0c\u4e94\u8001\u8fd0\u638c\u56f4\u653b\uff0c\u4e0d\u9732\u534a\u70b9\u7a7a\u9699\uff0c\u8981\u6559\u4ed6\u7eb5\u6709\u6740\u624b\uff0c\u4e5f\u65bd\u5c55\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u4e0d\u8fc7\u6570\u62db\uff0c\u6b66\u5f53\u4e94\u8001\u53c8\u8e0f\u8fdb\u4e86\u4e00\u6b65\uff0c\u4f46\u542c\u5f97\u7830\u7684\u4e00\u58f0\uff0c\u4e91\u821e\u9633\u7684\u80a9\u5934\u4e2d\u4e86\u667a\u5e7f\u957f\u8001\u7684\u4e00 \u638c\uff0c\u4e91\u7d20\u7d20\u53eb\u9053\uff1a\u201c\u7239\uff0c\u5916\u516c\u4e0d\u662f\u4f60\u6740\u7684\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u5411\u4ed6\u4eec\u8fa9\u89e3\u3002\u201d\u4e91\u7d20\u7d20\u5176\u5b9e\u4e5f\u4e0d\u77e5\u9053 \u8fd9\u4e8b\u662f\u771f\u662f\u5047\uff0c\u4f46\u4ed6\u4eec\u7236\u5973\u76f8\u5904\u4e86\u5341\u591a\u5e74\uff0c\u5979\u4ece\u7236\u4eb2\u7684\u795e\u60c5\u8bed\u6c14\u548c\u6c14\u5ea6\uff0c\u5df2\u786e\u4fe1\u4e86\u5979\u7236\u4eb2\u6ca1 \u6709\u8c0b\u6740\u5916\u516c\uff0c\u8981\u4e0d\u7136\u51b3\u4e0d\u4f1a\u5982\u6b64\u6124\u6fc0\u3002 \u3000\u3000\u4e91\u821e\u9633\u8138\u4e0a\u9732\u51fa\u4e00\u4e1d\u7b11\u610f\uff0c\u4ecd\u7136\u95ed\u53e3\u4e0d\u51fa\u4e00\u53e5\u3002\u53ea\u542c\u5f97\u53c8\u662f\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u8fd9\u4e00\u56de\u4e43\u662f \u88ab\u667a\u5706\u957f\u8001\u6253\u4e2d\uff0c\u667a\u5706\u7684\u638c\u529b\u81ea\u662f\u6bd4\u667a\u5e7f\u6df1\u539a\u5f97\u591a\uff0c\u8fd9\u4e00\u638c\u6253\u4e0b\uff0c\u4f46\u89c1\u4e91\u821e\u9633\u7684\u80cc\u5fc3\u8863\u88f3\uff0c \u88c2\u6210\u7247\u7247\uff0c\u80cc\u4e0a\u9690\u9690\u73b0\u51fa\u6de1\u7ea2\u7684\u638c\u5370\u3002 \u3000\u3000\u4e91\u7d20\u7d20\u53eb\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u7684\u5251\uff01\u201d\u624b\u6309\u6606\u543e\u5b9d\u5251\uff0c\u4fbf\u60f3\u62d4\u5251\u51fa\u9798\uff0c\u63b7\u7ed9\u7239\u7239\u3002\u4e91\u7d20\u7d20\u867d \u7136\u4e5f\u542c\u5230\u4e86\u4e91\u821e\u9633\u6709\u8a00\u5728\u5148\uff0c\u4f46\u603b\u89c9\u5f97\u7239\u7239\u7ed9\u4ed6\u4eec\u7528\u8bdd\u6fc0\u5f97\u820d\u5251\u4e0d\u7528\uff0c\u800c\u4e14\u662f\u4ee5\u4e00\u654c\u4e94\uff0c\u8fd9 \u8d4c\u6597\u672a\u514d\u592a\u4e0d\u516c\u5e73\u3002 \u3000\u3000\u4e91\u821e\u9633\u50b2\u7136\u7b11\u9053\uff1a\u201c\u7d20\u7d20\uff0c\u4f60\u51e0\u66fe\u89c1\u7239\u7239\u8bf4\u8fc7\u7684\u8bdd\u4e0d\u7b97\u6570\u6765\uff1f\u201d\u5c31\u5728\u8fd9\u8bf4\u8bdd\u7684\u65f6\u95f4\uff0c\u6b66 \u5f53\u4e94\u8001\u653b\u52bf\u731b\u53d1\uff0c\u638c\u98ce\u970d\u970d\uff0c\u4e00\u9f50\u6324\u5411\u4e2d\u5fc3\u3002\u773c\u89c1\u5f97\u4e91\u821e\u9633\u5df2\u5168\u65e0\u9000\u6b65\u4e4b\u5730\uff0c\u5ffd\u542c\u4ed6\u4e00\u58f0\u602a \u5578\uff0c\u593a\u9b44\u60ca\u9b42\uff0c\u4e91\u7d20\u7d20\u521a\u597d\u9762\u5411\u7740\u4ed6\uff0c\u89e6\u7740\u4ed6\u7684\u76ee\u5149\uff0c\u4e0d\u7981\u5413\u5f97\u5168\u8eab\u98a4\u6296\uff0c\u8fd9\u76ee\u5149\u548c\u4ed6\u6628\u665a \u6740\u77f3\u5929\u94ce\u4e4b\u65f6\u7684\u76ee\u5149\uff0c\u5b8c\u5168\u4e00\u6837\u3002 \u3000\u3000\u4e91\u7d20\u7d20\u53eb\u9053\uff1a\u201c\u7239\u7239\uff0c\u4e0d\u53ef\uff01\u201d\u53eb\u58f0\u672a\u6bd5\uff0c\u4f46\u89c1\u4e91\u821e\u9633\u7a81\u7136\u7f29\u8eab\u62b1\u819d\uff0c\u8eab\u5f62\u77ee\u4e86\u534a\u622a\uff0c \u6b66\u5f53\u4e94\u8001\u7684\u94c1\u638c\u5168\u90fd\u6253\u4e86\u4e2a\u7a7a\uff0c\u5c31\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u53ea\u89c1\u4e91\u821e\u9633\u7684\u8eab\u5b50\u65cb\u98ce\u822c\u7684\u6253\u4e86\u4e00\u4e2a\u5708 \u5708\uff0c\u53cc\u624b\u5341\u6307\u9f50\u626c\uff0c\u6b66\u5f53\u4e94\u8001\u7adf\u662f\u83ab\u540d\u5947\u5999\u7684\u5168\u90fd\u671d\u5929\u4ec6\u5012\uff0c\u8fde\u53eb\u4e5f\u672a\u80fd\u53eb\u51fa\u4e00\u58f0\uff0c\u8138\u4e0a\u7684 \u808c\u8089\u75c9\u631b\u53d8\u5f62\uff0c\u663e\u5f97\u5341\u5206\u53ef\u6016\uff01 \u3000\u3000\u4e91\u821e\u9633\u6413\u4e86\u4e00\u4e0b\u624b\u638c\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4e91\u67d0\u4e0d\u624d\uff0c\u6bd5\u7adf\u8fd8\u662f\u5c06\u4e94\u8001\u7684\u5927\u9a7e\u7559\u4e0b\u6765\u4e86\u3002\u201d\u6b66 \u5f53\u4e94\u8001\u5589\u5934\u561f\u561f\u4f5c\u54cd\uff0c\u4f3c\u662f\u7a74\u9053\u5df2\u88ab\u5c01\u95ed\uff0c\u4e00\u53e5\u8bdd\u4e5f\u8bf4\u4e0d\u51fa\u6765\u3002\u4e91\u7d20\u7d20\u770b\u7740\u5bb3\u6015\uff0c\u53eb\u9053\uff1a \u201c\u7239\u7239\uff01\u201d\u4e91\u821e\u9633\u9053\uff1a\u201c\u7239\u7239\u4eca\u65e5\u4e3a\u4f60\u5927\u53d1\u5179\u60b2\uff0c\u8981\u4e0d\u662f\u4f60\u9002\u624d\u90a3\u4e48\u4e00\u53eb\uff0c\u7239\u7239\u65e9\u5df2\u5927\u5f00\u6740 \u6212\u5566\uff01\u201d \u3000\u3000\u539f\u6765\u4e91\u821e\u9633\u5728\u8d3a\u5170\u5c71\u4e2d\u6f5c\u7814\u6b66\u5b66\uff0c\u5728\u5341\u516b\u5e74\u7684\u5c81\u6708\u91cc\uff0c\u4e0d\u4f46\u5b66\u6210\u4e86\u8fbe\u6469\u5251\u6cd5\uff0c\u800c\u4e14\u7ec3\u6210 \u4e86\u6b66\u6797\u7edd\u5b66\u7684\u4e00\u6307\u7985\u529f\uff0c\u51fa\u624b\u4f24\u4eba\uff0c\u91cd\u5219\u6b7b\u4ea1\uff0c\u8f7b\u5219\u6b8b\u5e9f\uff0c\u6b66\u5f53\u4e94\u8001\u6b63\u5728\u5360\u5c3d\u4e0a\u98ce\u7684\u65f6\u5019\uff0c \u88ab\u4ed6\u7a81\u7136\u4ee5\u8fd9\u79cd\u6781\u5389\u5bb3\u7684\u6b66\u529f\u53cd\u51fb\uff0c\u8ddd\u79bb\u53c8\u8fd1\uff0c\u7eb5\u60f3\u95ea\u907f\u4ea6\u6765\u4e0d\u53ca\uff0c\u662f\u4ee5\u4e00\u4e2a\u4e2a\u90fd\u88ab\u5c01\u95ed\u4e86 \u9690\u7a74\u3002 \u3000\u3000\u6b66\u5f53\u4e94\u8001\u5728\u5730\u4e0a\u6323\u624e\u4e86\u597d\u4e00\u4f1a\u513f\uff0c\u867d\u7136\u90fd\u80fd\u591f\u76d8\u819d\u5750\u4e86\u8d77\u6765\uff0c\u53ef\u662f\u597d\u51e0\u6b21\u8fd0\u6c14\u51b2\u5173\uff0c\u80f8 \u53e3\u90fd\u597d\u4f3c\u585e\u7740\u4e00\u6839\u6a2a\u6728\u4f3c\u7684\uff0c\u6c14\u673a\u53d7\u963b\uff0c\u8fde\u547c\u5438\u4e5f\u96be\u4ee5\u7545\u8212\uff0c\u66f4\u4e0d\u8981\u8bf4\u80fd\u81ea\u5df1\u89e3\u7a74\u4e86\u3002\u800c\u4e14 \u4e0d\u8fd0\u771f\u6c14\u8fd8\u53ef\uff0c\u4e00\u8fd0\u771f\u6c14\uff0c\u80f8\u53e3\u5c31\u9690\u9690\u4f5c\u75db\uff0c\u4e94\u8001\u5fc3\u4e2d\u90fd\u81ea\u51c9\u4e86\u534a\u622a\uff0c\u65e9\u77e5\u5982\u6b64\u53d7\u8fb1\uff0c\u8fd8\u4e0d \u5982\u5f53\u521d\u4efb\u7531\u4ed6\u4f7f\u7528\u8fbe\u6469\u5251\u6cd5\uff0c\u7eb5\u7136\u662f\u6b7b\u5728\u4ed6\u5b9d\u5251\u4e4b\u4e0b\uff0c\u5374\u514d\u5f97\u53d7\u7ec8\u8eab\u6b8b\u5e9f\u7684\u82e6\u5211\u3002 \u3000\u3000\u4e91\u821e\u9633\u7684\u773c\u5149\u6328\u6b21\u7684\u4ece\u4e94\u8001\u7684\u8138\u4e0a\u63a0\u8fc7\uff0c\u5ffd\u5730\u51b7\u51b7\u4e00\u7b11\uff0c\u6478\u51fa\u4e86\u4e00\u672c\u5251\u8c31\uff0c\u50b2\u7136\u8bf4\u9053\uff1a \u201c\u4e3a\u4e86\u4e00\u672c\u5251\u8c31\uff0c\u52b3\u52a8\u8d35\u6d3e\u7684\u638c\u95e8\u5f1f\u5b50\u548c\u4e94\u4f4d\u957f\u8001\u90fd\u9a7e\u4e34\u5bd2\u820d\uff0c\u4e91\u67d0\u5b9e\u5728\u8fc7\u610f\u4e0d\u53bb\uff0c\u8d35\u6d3e\u65e2 \u7136\u5982\u6b64\u7a00\u7f55\u8fd9\u672c\u5251\u8c31\uff0c\u4e91\u67d0\u4e0d\u59a8\u518d\u505a\u4e00\u4e2a\u4eba\u60c5\uff0c\u5c06\u5b83\u9001\u7ed9\u4f60\u4eec\uff0c\u4f46\u6b66\u6797\u4e2d\u6709\u4e9b\u89c4\u77e9\uff0c\u53d7\u8c31\u5373 \u5f53\u4f20\u5b97\uff0c\u53d7\u8c31\u4e4b\u4eba\uff0c\u82e5\u975e\u665a\u8f88\u4eb2\u5c5e\u5c31\u662f\u8863\u94b5\u5f1f\u5b50\uff0c\u6211\u4ee5\u725f\u5bb6\u534a\u5b50\u7684\u8eab\u4efd\u5b66\u6210\u4e86\u8fd9\u5957\u5251\u6cd5\uff0c\u672c \u6765\u4e5f\u8be5\u5c06\u725f\u5bb6\u7684\u5251\u6cd5\u4f20\u4e4b\u540e\u4eba\uff0c\u53ef\u60dc\u4f60\u4eec\u4e94\u8001\u7684\u8f88\u4efd\u592a\u9ad8\uff0c\u6211\u53ef\u4e0d\u6562\u59d4\u5c48\u4f60\u4eec\u505a\u6211\u7684\u540e \u8f88\uff01\u201d \u3000\u3000\u4e91\u821e\u9633\u53e3\u53e3\u58f0\u58f0\u5c06\u725f\u72ec\u9038\u4e0e\u6b66\u5f53\u6d3e\u5206\u4e86\u5f00\u6765\uff0c\u53ea\u627f\u8ba4\u662f\u725f\u5bb6\u5251\u6cd5\uff0c\u4e0d\u627f\u8ba4\u662f\u6b66\u5f53\u6d3e\u7684\uff0c \u786e\u662f\u4e00\u6d3e\u201c\u5f3a\u8f9e\u593a\u7406\u201d\uff0c\u4f46\u6b66\u5f53\u4e94\u8001\u90fd\u88ab\u5c01\u95ed\u4e86\u7a74\u9053\uff0c\u53e3\u4e0d\u80fd\u8a00\uff0c\u7eb5\u7136\u5fc3\u4e2d\u6c14\u6124\uff0c\u5374\u662f\u65e0\u80fd \u53cd\u9a73\u3002 \u3000\u3000\u53ea\u542c\u5f97\u4e91\u821e\u9633\u53c8\u51b7\u7b11\u9053\uff1a\u201c\u4eca\u65e5\u4e0d\u662f\u770b\u5728\u6211\u5973\u513f\u7684\u4efd\u4e0a\uff0c\u4f60\u4eec\u6b66\u5f53\u4e94\u8001\u4f11\u60f3\u751f\u8fd8\uff0c\u4e3a\u4e86 \u4e00\u672c\u5251\u8c31\uff0c\u9669\u4e9b\u547d\u4e27\u8352\u5c71\uff0c\u771f\u662f\u4f55\u82e6\uff1f\u5012\u4e0d\u5982\u6211\u5c06\u5b83\u6bc1\u4e86\uff0c\u514d\u5f97\u518d\u6709\u4eba\u6b65\u4f60\u4eec\u7684\u540e\u5c18\uff01\u201d\u53cc \u624b\u5c06\u90a3\u672c\u5251\u8c31\u4e00\u6495\u518d\u6495\uff0c\u6495\u4e3a\u56db\u53e0\uff0c\u5408\u5728\u638c\u4e2d\uff0c\u8f7b\u8f7b\u4e00\u62cd\uff0c\u624b\u638c\u4e00\u644a\uff0c\u4f46\u89c1\u90a3\u672c\u7a00\u4e16\u4e4b\u73cd\u7684 \u8fbe\u6469\u5251\u8c31\uff0c\u788e\u6210\u7247\u7247\uff0c\u968f\u98ce\u98de\u821e\uff0c\u4f11\u8bf4\u6b66\u5f53\u4e94\u8001\u5fc3\u4e2d\u6124\u75db\uff0c\u8fde\u4e91\u7d20\u7d20\u5bf9\u7236\u4eb2\u6b64\u4e3e\u4ea6\u611f\u5230\u5927\u51fa \u610f\u5916\uff0c\u9a87\u5f97\u53eb\u51fa\u58f0\u6765\uff01 \u3000\u3000\u4e91\u821e\u9633\u5374\u662f\u54c8\u54c8\u5927\u7b11\uff0c\u5408\u638c\u8bf4\u9053\uff1a\u201c\u4ece\u4eca\u4e4b\u540e\uff0c\u53ea\u6709\u6211\u4e00\u4eba\u77e5\u9053\u8fbe\u6469\u5251\u6cd5\uff0c\u4f60\u4eec\u82e5\u662f\u5fc3 \u75db\uff0c\u5bb3\u6015\u5931\u4f20\uff0c\u53ef\u53eb\u4f60\u4eec\u7684\u638c\u95e8\u5f1f\u5b50\u4e0a\u5b98\u5929\u91ce\u524d\u6765\uff0c\u62dc\u5728\u6211\u7684\u95e8\u4e0b\uff0c\u6211\u4e0d\u4f46\u4f20\u4ed6\u8fbe\u6469\u5251\u6cd5\uff0c \u8fd8\u8981\u4f20\u4ed6\u4e00\u6307\u7985\u7684\u529f\u592b\u3002\u53ef\u662f\u90a3\u4e48\u4e00\u6765\uff0c\u4ed6\u5c31\u662f\u6211\u7684\u8863\u94b5\u5f1f\u5b50\uff0c\u4f60\u4eec\u8d35\u6d3e\u53ea\u597d\u53e6\u9009\u638c\u95e8\u4eba \u5566\uff01\u201d \u3000\u3000------------- \u3000\u3000\u91cd\u5e86\u96ea\u513f \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u8fd8\u5251\u5947\u60c5\u5f55\u300b\u2014\u2014\u7b2c\u516b\u56de\u3000\u53cc\u96c4\u8fd0\u638c \u6881\u7fbd\u751f\u300a\u8fd8\u5251\u5947\u60c5\u5f55\u300b \u7b2c\u516b\u56de\u3000\u53cc\u96c4\u8fd0\u638c \u3000\u3000\u6b66\u6797\u4e2d\u6539\u6295\u522b\u6d3e\u672c\u6765\u4e0d\u7b97\u4ec0\u4e48\uff0c\u4f46\u90a3\u53ea\u662f\u5bf9\u4e00\u822c\u8eab\u4efd\u7684\u665a\u8f88\u5f1f\u5b50\u800c\u8a00\uff0c\u82e5\u662f\u8981\u6536\u522b\u6d3e\u7684 \u638c\u95e8\u5f1f\u5b50\u4e3a\u5f92\uff0c\u90a3\u5374\u662f\u4ece\u6240\u672a\u6709\u4e4b\u4e8b\u3002\u4e91\u821e\u9633\u8fd9\u756a\u8bf4\u8bdd\uff0c\u4e0d\u557b\u662f\u5bf9\u6b66\u5f53\u4e94\u8001\u7684\u83ab\u5927\u4fae\u8fb1\u3002 \u3000\u3000\u4f46\u89c1\u6b66\u5f53\u4e94\u8001\u5634\u5507\u62bd\u6410\uff0c\u773c\u775b\u4e2d\u597d\u50cf\u5c31\u8981\u55b7\u51fa\u706b\u6765\uff0c\u795e\u60c5\u6bd4\u9002\u624d\u66f4\u53ef\u6015\u4e86\uff0c\u4e91\u7d20\u7d20\u8f6c\u8fc7 \u4e86\u8138\uff0c\u5fcd\u4e0d\u7740\u53c8\u4f4e\u58f0\u53eb\u9053\uff1a\u201c\u7239\u7239\uff01\u201d\u4e91\u821e\u9633\u4e0d\u5f85\u5973\u513f\u518d\u8bf4\uff0c\u4fbf\u4ece\u6000\u4e2d\u638f\u51fa\u4e00\u4e2a\u5c0f\u5c0f\u7684\u94f6 \u74f6\uff0c\u74f6\u4e2d\u6709\u7740\u4e09\u9897\u78a7\u7eff\u8272\u7684\u4e39\u4e38\uff0c\u6b63\u662f\u4e91\u821e\u9633\u4ee5\u524d\u8d39\u4e86\u5f88\u5927\u7684\u60c5\u9762\u8bf7\u6258\uff0c\u624d\u4ece\u5f52\u85cf\u5927\u5e08\u90a3\u513f \u6c42\u6765\u7684\u5c11\u9633\u5c0f\u8fd8\u4e39\uff0c\u672c\u6765\u5171\u6709\u516d\u9897\uff0c\u524d\u51e0\u5929\u4e91\u7d20\u7d20\u4e00\u4e0b\u5b50\u5c31\u7ed9\u4e86\u9648\u7384\u673a\u4e09\u9897\uff0c\u5982\u4eca\u74f6\u4e2d\u53ea\u5269 \u4e0b\u4e09\u9897\u4e86\u3002 \u3000\u3000\u4e91\u821e\u9633\u5c06\u5c0f\u8fd8\u4e39\u503e\u5012\u638c\u5fc3\uff0c\u6307\u7532\u8f7b\u8f7b\u4e00\u5212\uff0c\u5c06\u6bcf\u9897\u4e39\u4e38\u5206\u4e3a\u4e24\u534a\uff0c\u4e09\u9897\u5c0f\u8fd8\u4e39\u4fbf\u5206\u6210\u4e86 \u516d\u7247\uff0c\u4e91\u821e\u9633\u81ea\u5df1\u541e\u4e86\u4e00\u7247\uff0c\u5c06\u5176\u4ed6\u4e94\u7247\u4ea4\u7ed9\u4e86\u5973\u513f\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u6bcf\u4e2a\u8001\u5934\u513f\u7ed9\u4ed6\u4e00\u7247\uff0c \u6211\u4e0b\u624b\u4e0d\u91cd\uff0c\u4e09\u5929\u4e4b\u540e\uff0c\u4fbf\u53ef\u6062\u590d\u539f\u6765\u529f\u529b\u3002\u201d \u3000\u3000\u4e91\u7d20\u7d20\u5148\u5230\u667a\u5706\u957f\u8001\u8ddf\u524d\uff0c\u667a\u5706\u957f\u8001\u80f8\u53e3\u8d77\u4f0f\uff0c\u5589\u5499\u683c\u683c\u4f5c\u54cd\uff0c\u773c\u775b\u4e5f\u77aa\u5f97\u5927\u5927\u7684\uff0c\u770b \u90a3\u795e\u60c5\uff0c\u5b9e\u662f\u4e0d\u613f\u63a5\u53d7\u8fd9\u534a\u7c92\u7075\u4e39\u3002\u8981\u77e5\u6b66\u5f53\u4e94\u8001\u662f\u4f55\u7b49\u8eab\u4efd\uff0c\u4ed6\u4eec\u82e5\u63a5\u53d7\u4e86\u4e91\u821e\u9633\u7684\u6069 \u60e0\uff0c\u6c5f\u6e56\u4e0a\u4f20\u5c06\u51fa\u53bb\uff0c\u4e0d\u4f46\u6b66\u5f53\u6d3e\u5931\u5c3d\u9762\u5b50\uff0c\u4ed6\u4eec\u4e5f\u6c38\u4e0d\u80fd\u518d\u5411\u4e91\u821e\u9633\u5bfb\u4ec7\u4e86\u3002 \u3000\u3000\u4e91\u7d20\u7d20\u5929\u771f\u65e0\u90aa\uff0c\u54ea\u77e5\u9053\u6b66\u6797\u4e2d\u6709\u8fd9\u8bb8\u591a\u907f\u5fcc\uff0c\u53ea\u5f53\u6b66\u5f53\u4e94\u8001\u987e\u7740\u8eab\u4efd\uff0c\u4e0d\u597d\u610f\u601d\uff0c\u5fc3 \u4e2d\u60f3\u9053\uff1a\u201c\u867d\u8bf4\u6211\u7239\u7239\u4e0b\u624b\u4e0d\u91cd\uff0c\u4f46\u82e5\u7136\u65e0\u6b64\u7075\u4e39\u89e3\u6551\uff0c\u7ec8\u987b\u6b8b\u5e9f\uff1b\u51b5\u4e14\u4e94\u8001\u5e74\u8fc8\u4f53\u8870\uff0c\u8bf4 \u4e0d\u5b9a\u56e0\u6b64\u800c\u6b7b\uff0c\u90a3\u5c31\u66f4\u52a0\u91cd\u4e86\u7239\u7239\u7684\u7f6a\u5b7d\u4e86\uff01\u201d\u5982\u6b64\u4e00\u60f3\uff0c\u4e0d\u987e\u667a\u5706\u957f\u544a\u53cd\u5e94\u5982\u4f55\uff0c\u4e3e\u624b\u4e00 \u62ac\uff0c\u8f7b\u8f7b\u4e00\u634f\uff0c\u667a\u5706\u957f\u8001\u7684\u5634\u5df4\u4e0d\u7531\u81ea\u5df1\u7684\u5f20\u5f00\uff0c\u4e91\u7d20\u7d20\u4fbf\u786c\u628a\u90a3\u4e00\u7247\u5c0f\u8fd8\u4e39\u585e\u4e86\u8fdb\u53bb\uff0c\u5c0f \u8fd8\u4e39\u5165\u53e3\u5373\u5316\uff0c\u4e91\u7d20\u7d20\u8fd8\u6015\u4ed6\u4e0d\u80af\u54bd\u4e0b\uff0c\u53c8\u5c06\u4ed6\u7684\u5934\u9885\u6276\u5f97\u5fae\u5fae\u540e\u4ef0\uff0c\u6447\u4e86\u4e24\u6447\uff0c\u6545\u6b64\u4e00 \u6765\uff0c\u667a\u5706\u957f\u8001\u4fbf\u662f\u8981\u5410\u4e5f\u5410\u4e0d\u51fa\u6765\u4e86\u3002\u4e91\u7d20\u7d20\u4f9d\u6cd5\u70ae\u5236\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u6559\u6b66\u5f53\u4e94\u8001\u90fd\u541e\u4e0b\u4e86\u4e00 \u7247\u7075\u4e39\uff0c\u4e91\u821e\u9633\u8fd9\u4e2a\u6069\u60e0\uff0c\u4ed6\u4eec\u662f\u53d7\u5b9a\u4e86\uff01 \u3000\u3000\u4e91\u821e\u9633\u5927\u7b11\u9053\uff1a\u201c\u505a\u5f97\u597d\uff0c\u505a\u5f97\u597d\uff01\u201d\u4f46\u542c\u5f97\u6b66\u5f53\u4e94\u8001\u5404\u81ea\u60a0\u60a0\u7684\u53f9\u53e3\u957f\u6c14\uff0c\u9762\u9762\u76f8 \u89d1\uff0c\u90a3\u795e\u60c5\u7adf\u662f\u5982\u4e27\u8003\u59a3\uff0c\u60b2\u60e8\u4e4b\u6781\uff0c\u4e91\u7d20\u7d20\u9887\u4e3a\u7eb3\u7f55\uff0c\u60f3\u9053\uff1a\u201c\u662f\u4e86\uff0c\u60f3\u5fc5\u4ed6\u4eec\u88ab\u7239\u7239\u6253 \u8d25\uff0c\u6545\u6b64\u7f9e\u6127\u60b2\u4f24\u3002\u201d\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u7239\u7239\uff0c\u4ed6\u4eec\u670d\u4e86\u7075\u4e39\u4e4b\u540e\uff0c\u5e94\u8be5\u9759\u5750\u8fd0\u529f\uff0c\u54b1\u4eec\u56de\u5bb6\u53bb \u5427\uff0c\u514d\u5f97\u5206\u4e86\u4ed6\u4eec\u7684\u5fc3\u795e\u3002\u201d \u3000\u3000\u4e91\u821e\u9633\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u7d20\u7d20\uff0c\u4f60\u5012\u5f88\u4f1a\u4f53\u8d34\u4eba\u3002\u201d\u521a\u60f3\u548c\u5973\u513f\u56de\u5bb6\uff0c\u5ffd\u53c8\u542c\u5f97\u5c71\u540e\u4f20 \u6765\u4e86\u4e00\u9635\u53ee\u53ee\u53ee\u53ee\u7684\u50cf\u94c1\u6756\u89e6\u5730\u4e4b\u58f0\uff0c\u4e91\u821e\u9633\u7b11\u9053\uff1a\u201c\u83ab\u975e\u53c8\u662f\u4e00\u4e2a\u4e0d\u6015\u6b7b\u7684\u6765\u5411\u6211\u7d22\u5251\u8c31 \u4e0d\u6210\uff1f\u201d\u8bdd\u58f0\u672a\u6bd5\uff0c\u90a3\u4e2a\u4eba\u5df2\u4ece\u5c71\u5773\u5904\u8f6c\u4e86\u51fa\u6765\uff0c\u628a\u4e91\u7d20\u7d20\u5413\u4e86\u4e00\u5927\u8df3\uff01\u201d \u3000\u3000\u4f46\u89c1\u90a3\u4eba\u53d1\u5982\u4e71\u8349\uff0c\u9762\u4e0a\u8499\u7740\u4e00\u5757\u9ed1\u5dfe\u76d6\u8fc7\u8033\u540e\uff0c\u53ea\u6709\u4e00\u6761\u534a\u81c2\u818a\uff0c\u5de6\u8fb9\u81ea\u81c2\u5f2f\u4ee5\u4e0b\u7684 \u534a\u6761\u8b6c\u818a\u4f3c\u662f\u88ab\u4eba\u524a\u53bb\uff0c\u5374\u524a\u5f97\u751a\u4e0d\u6574\u9f50\uff0c\u51f8\u51fa\u4e00\u5757\u5c16\u5c16\u7684\u9aa8\u5934\uff0c\u675f\u4ee5\u7ea2\u7f15\uff0c\u5c31\u50cf\u4e00\u67c4\u5305\u7740 \u7684\u5315\u9996\uff0c\u5de6\u817f\u4e5f\u5b8c\u5168\u8ddb\u4e86\uff0c\u811a\u5c16\u6839\u672c\u4e0d\u80fd\u6cbe\u5730\uff0c\u534a\u6761\u817f\u540a\u7740\u79bb\u5730\u4e0a\uff0c\u53ea\u9760\u4e00\u6761\u817f\u548c\u4e00\u6839\u94c1\u62d0 \u652f\u6301\u7740\u8eab\u4f53\uff0c\u8fd9\u4e2a\u5f62\u8c8c\u5df2\u662f\u602a\u7edd\uff0c\u8eab\u4e0a\u7684\u88c5\u675f\u4e5f\u7279\u522b\u4e0d\u540c\uff0c\u91cc\u9762\u7a7f\u7684\u662f\u4e00\u4ef6\u9526\u7f0e\u957f\u888d\uff0c\u8d28\u6599 \u534e\u7f8e\uff0c\u4e0a\u534a\u8eab\u5916\u9762\u5374\u7f69\u7740\u4e00\u4ef6\u84dd\u5e03\u5927\u8902\uff0c\u4e0d\u4f46\u5e72\u5e72\u51c0\u51c0\uff0c\u800c\u4e14\u8272\u6cfd\u5982\u65b0\uff0c\u5374\u6545\u610f\u6253\u4e0a\u4e03\u516b\u5904 \u8865\u4e01\uff0c\u7f1d\u4e0a\u5404\u79cd\u989c\u8272\u7684\u7834\u5e03\uff0c\u4e0d\u4f26\u4e0d\u7c7b\uff0c\u4ee4\u4eba\u4e00\u770b\u5c31\u5fc3\u91cc\u538c\u70e6\u3002 \u3000\u3000\u4e91\u821e\u9633\u6014\u4e86\u4e00\u6014\uff0c\u84e6\u7136\u559d\u9053\uff1a\u201c\u6765\u7684\u53ef\u662f\u81ea\u79f0\u534a\u6b8b\u795e\u4e10\u7684\u72ec\u81c2\u602a\u76d7\u4e48\uff1f\u201d\u4e91\u821e\u9633\u867d\u662f\u9690 \u5c45\u8352\u5c71\uff0c\u6bcf\u51e0\u5e74\u4e0b\u5c71\u4e00\u6b21\uff0c\u6d88\u606f\u5374\u5e76\u4e0d\u95ed\u585e\uff0c\u5927\u7ea6\u4e94\u516d\u5e74\u524d\uff0c\u4ed6\u5c31\u542c\u8bf4\u9655\u5317\u7684\u9ed1\u9053\u4e0a\u51fa\u73b0\u4e86 \u8fd9\u4e48\u4e00\u4e2a\u602a\u6a21\u602a\u6837\u7684\u72ec\u884c\u5927\u76d7\uff0c\u4e13\u52ab\u6210\u540d\u7684\u9556\u5e08\u548c\u5b98\u5e9c\u7684\u8d22\u7269\uff0c\u4ece\u6765\u4e0d\u80af\u4ee5\u771f\u9762\u76ee\u793a\u4eba\uff0c\u81ea \u8d1f\u6781\u9ad8\uff0c\u5927\u7ea6\u56e0\u4e3a\u4ed6\u7684\u56db\u80a2\u6709\u4e00\u534a\u6b8b\u5e9f\uff0c\u6240\u4ee5\u81ea\u79f0\u534a\u6b8b\u795e\u4e10\uff0c\u9ed1\u767d\u4e24\u9053\u4e2d\u4eba\u90fd\u79f0\u4ed6\u4e3a\u72ec\u81c2\u602a \u76d7\u3002\u4e91\u821e\u9633\u4e5f\u66fe\u52a8\u8fc7\u5ff5\u5934\u60f3\u53bb\u4f1a\u4f1a\u8fd9\u4e2a\u602a\u4eba\uff0c\u7ec8\u56e0\u4e0d\u613f\u81ea\u9732\u884c\u85cf\u800c\u6253\u6d88\u4e86\u597d\u5947\u4e4b\u5ff5\u3002 \u3000\u3000\u4e91\u821e\u9633\u4e00\u53e3\u5c06\u4ed6\u7684\u6765\u5386\u559d\u7834\uff0c\u6b66\u5f53\u4e94\u8001\u4e5f\u5403\u4e86\u4e00\u60ca\uff0c\u8fd9\u4e2a\u602a\u4eba\u5374\u53ea\u662f\u201c\u563f\uff0c\u563f\uff01\u201d\u7684\u7b11 \u4e86\u4e24\u58f0\uff0c\u65e2\u4e0d\u627f\u8ba4\uff0c\u4e5f\u4e0d\u5426\u8ba4\uff0c\u4e91\u821e\u9633\u5fcd\u7740\u6c14\u95ee\u9053\uff1a\u201c\u5c0a\u9a7e\u5230\u6b64\uff0c\u610f\u6b32\u4f55\u4e3a\uff1f\u201d\u90a3\u602a\u4eba\u903c\u5c16 \u7740\u55d3\u5b50\u8bf4\u9053\uff1a\u201c\u6211\u662f\u5f3a\u76d7\u7684\u7956\u5b97\uff0c\u6765\u95ee\u4f60\u8fd9\u4e2a\u5c0f\u8d3c\u8981\u5b5d\u656c\u6765\u4e86\u3002\u201d\u4e91\u821e\u9633\u6012\u9053\uff1a\u201c\u751a\u4e48\u5b5d \u656c\uff1f\u201d \u3000\u3000\u90a3\u602a\u4eba\u9634\u607b\u607b\u7684\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u5077\u4e86\u725f\u72ec\u9038\u8001\u513f\u7684\u90a3\u672c\u5251\u8c31\uff0c\u5df2\u7528\u4e86\u5341\u516b\u5e74\u5566\uff0c\u8fd8\u4e0d\u591f \u4e48\uff1f\u5feb\u62ff\u51fa\u6765\u732e\u7ed9\u6211\u3002\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u4e91\u821e\u9633\u4e5f\u4e0d\u7981\u5927\u4e3a\u5403\u60ca\uff0c\u60f3\u4ed6\u7a83\u53d6\u5cb3\u7236\u7684\u5251\u8c31\u4e4b\u4e8b\uff0c\u4f55 \u7b49\u9690\u5bc6\uff0c\u8fd9\u4e2a\u602a\u7269\u5374\u77e5\u9053\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u7aef\u7684\u662f\u4ee4\u4eba\u96be\u4ee5\u601d\u8bae\uff01 \u3000\u3000\u4e91\u821e\u9633\u7a76\u7adf\u662f\u6b66\u5b66\u5927\u5e08\u7684\u8eab\u4efd\uff0c\u60ca\u4ff1\u7edd\u4e0d\u5f62\u4e8e\u795e\u8272\uff0c\u5fae\u5fae\u7684\u6014\u4e86\u4e00\u6014\uff0c\u7acb\u5373\u88c5\u4f5c\u82e5\u65e0\u5176 \u4e8b\u7684\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5c0a\u9a7e\u8fd9\u526f\u8eaf\u4f53\uff0c\u8981\u4e86\u5251\u8c31\u8fd8\u6709\u4f55\u7528\uff1f\u5c0a\u9a7e\u65e2\u81ea\u53f7\u534a\u6b8b\uff0c\u4f3c\u5e94\u6709\u81ea\u77e5\u4e4b\u660e\uff0c \u54c8\u54c8\uff0c\u4f60\u96be\u9053\u8fd8\u60f3\u7528\u5251\u4e48\uff1f\u90a3\u9664\u975e\u662f\u6295\u80ce\u8f6c\u4e16\uff0c\u518d\u5ea6\u4e3a\u4eba\u4e86\uff01\u201d\u8981\u77e5\u8fbe\u6469\u5251\u672f\u4e43\u662f\u6700\u4e0a\u4e58\u7684 \u5251\u672f\uff0c\u590d\u6742\u5965\u5999\uff0c\u65e0\u53ef\u6bd4\u4f26\uff0c\u4f3c\u4ed6\u8fd9\u7b49\u7f3a\u4e86\u534a\u8fb9\u624b\u811a\u7684\u4eba\uff0c\u62a1\u5200\u821e\u5251\uff0c\u53ea\u4f7f\u4e24\u4e09\u4e2a\u6781\u7b80\u5355\u7684 \u62db\u5f0f\uff0c\u6216\u8bb8\u8fd8\u6709\u53ef\u80fd\uff0c\u8981\u7ec3\u8fbe\u6469\u5251\u672f\uff0c\u90a3\u786e\u662f\u9664\u975e\u6295\u80ce\u8f6c\u4e16\u4e86\u3002 \u3000\u3000\u4f46\u4e91\u821e\u9633\u7684\u8bdd\u8bed\u4e5f\u8bf4\u5f97\u5fd2\u523b\u8584\u4e86\u4e9b\uff0c\u4e91\u7d20\u7d20\u867d\u7136\u8ba8\u538c\u8fd9\u4e2a\u602a\u7269\uff0c\u542c\u8d77\u6765\u4e5f\u4e0d\u8212\u670d\uff0c\u5fc3 \u9053\uff1a\u201c\u4ed6\u65ad\u8db3\u6b8b\u5e9f\uff0c\u5df2\u662f\u53ef\u601c\uff0c\u7239\u7239\u554a\uff0c\u4f60\u4f55\u5fc5\u8fd8\u62ff\u4ed6\u6765\u8ba5\u9985\uff1f\u201d\u4e00\u822c\u6b8b\u5e9f\u4e4b\u4eba\uff0c\u672c\u6765\u6700\u5fcc \u4eba\u5632\u4ed6\u6b8b\u5e9f\uff0c\u8fd9\u4e2a\u602a\u4eba\uff0c\u5374\u5e76\u4e0d\u53d1\u6012\uff0c\u9732\u5728\u8499\u9762\u5dfe\u5916\u7684\u4e00\u53cc\u5927\u773c\uff0c\u53ea\u662f\u95ea\u4e86\u4e00\u95ea\uff0c\u6de1\u6de1\u8bf4 \u9053\uff1a\u201c\u6211\u4e0d\u80fd\u7528\u5251\uff0c\u6211\u7684\u5f92\u5f1f\u53ef\u5e76\u6ca1\u6709\u6b8b\u5e9f\uff01\u8fd9\u672c\u5251\u8c31\uff0c\u4ed6\u672c\u6765\u8981\u81ea\u884c\u5411\u4f60\u593a\u53d6\uff0c\u53ea\u662f\u4ed6\u7b49 \u5f97\u4e86\uff0c\u6211\u53ef\u6ca1\u6709\u8010\u5fc3\u7b49\u8fd9\u5341\u5e74\uff0c\u6240\u4ee5\u6211\u5411\u4f60\u7d22\u53d6\u8d3c\u8d43\uff0c\u53ea\u662f\u62ff\u8fc7\u624b\u53bb\u5b5d\u656c\u5f92\u5f1f\u3002\u201d \u3000\u3000\u8fd9\u602a\u4eba\u7684\u8bdd\u8d8a\u8bf4\u8d8a\u602a\uff0c\u8fd8\u6709\u4e00\u6837\u5947\u602a\u7684\u662f\uff1a\u8fd9\u602a\u4eba\u867d\u7136\u5f04\u5c16\u4e86\u55d3\u97f3\uff0c\u4f46\u8bf4\u4e86\u8fd9\u4e48\u4e00\u5927\u5806 \u8bdd\uff0c\u96be\u514d\u9732\u51fa\u672c\u6765\u97f3\u8272\uff0c\u542c\u5728\u4e91\u821e\u9633\u8033\u4e2d\uff0c\u7adf\u662f\u4f3c\u66fe\u76f8\u8bc6\uff0c\u4f46\u641c\u5c3d\u67af\u80a0\uff0c\u5374\u600e\u4e48\u6837\u4e5f\u60f3\u4e0d\u8d77 \u6765\u3002\u4e91\u821e\u9633\u53cc\u773c\u70af\u70af\uff0c\u8e0f\u4e0a\u4e00\u6b65\uff0c\u8fce\u7740\u4ed6\u7684\u76ee\u5149\uff0c\u6717\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u7684\u5f92\u5f1f\u662f\u8c01\uff1f\u201d\u90a3\u602a\u4eba \u9053\uff1a\u201c\u4e0a\u5b98\u5929\u91ce\uff01\u201d \u3000\u3000\u8fd9\u8bdd\u66f4\u662f\u602a\u5230\u79bb\u8c31\uff0c\u4e91\u7d20\u7d20\u56e0\u4e3a\u5bf9\u9648\u7384\u673a\u7684\u5173\u7cfb\uff0c\u5bf9\u4e0a\u5b98\u5929\u91ce\u751a\u6709\u597d\u611f\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a \u201c\u4e0a\u5b98\u5929\u91ce\u8fd9\u7b49\u4eba\u624d\uff0c\u5c82\u80af\u8ddf\u4f60\u8fd9\u602a\u7269\u505a\u5f92\u5f1f\u3002\u201d\u5979\u7d20\u6027\u6e29\u67d4\uff0c\u5fc3\u4e2d\u607c\u6012\uff0c\u672a\u66fe\u9a82\u51fa\uff1b\u667a\u5706 \u957f\u8001\u521a\u521a\u6062\u590d\u7cbe\u795e\uff0c\u5374\u5fcd\u4e0d\u7740\u6c14\u7834\u53e3\u9a82\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff01\u4e0a\u5b98\u5929\u91ce\u662f\u6b66\u5f53\u6d3e\u7684\u638c\u95e8\u5f1f\u5b50\uff0c\u4f60 \u8fd9\u4e11\u516b\u602a\u6562\u52a8\u5ff5\u6536\u4ed6\u4e3a\u5f92\uff1f\u201d \u3000\u3000\u90a3\u602a\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u6211\u867d\u7136\u6b8b\u5e9f\u4e11\u964b\uff0c\u53ef\u6bd4\u4f60\u4eec\u8fd9\u51e0\u4e2a\u5927\u8a00\u6b3a\u4e16\u7684\u8001\u5934\u5b50\u5f3a\u5f97\u591a\uff01\u4e0a\u5b98\u5929 \u91ce\u670d\u670d\u8d34\u8d34\uff0c\u81ea\u613f\u62dc\u6211\u4e3a\u5e08\uff0c\u4f60\u5f53\u662f\u6211\u6ca1\u6709\u5f92\u5f1f\uff0c\u60f3\u62a2\u4f60\u7684\u638c\u95e8\u4eba\u4e48\uff1f\u201d\u76f4\u628a\u667a\u5706\u957f\u8001\u6c14\u5f97 \u773c\u775b\u7ffb\u767d\uff0c\u51e0\u4e4e\u6655\u4e86\u8fc7\u53bb\uff01 \u3000\u3000\u4e91\u821e\u9633\u5fc3\u4e2d\u4e00\u52a8\uff0c\u84e6\u7136\u559d\u9053\uff1a\u201c\u4f60\u6765\u5230\u6b64\u95f4\uff1f\u8fd8\u4e0d\u6562\u4ee5\u672c\u6765\u9762\u76ee\u4e0e\u6545\u4eba\u76f8\u89c1\u4e48\uff1f\u201d\u8eab\u5f62 \u4e00\u6643\uff0c\u733f\u81c2\u8f7b\u8212\uff0c\u75be\u4f3c\u98d8\u98ce\uff0c\u4e00\u624b\u5c31\u5411\u4ed6\u7684\u8499\u5dfe\u6293\u53bb\u3002\u4e91\u821e\u9633\u4f55\u7b49\u6b66\u529f\uff0c\u76f8\u8ddd\u53c8\u4e0d\u8fc7\u4ec5\u6570\u5c3a \u4e4b\u5730\uff0c\u6309\u8bf4\u65e0\u6709\u4e0d\u4e2d\u4e4b\u7406\uff0c\u90a3\u6599\u8fd9\u602a\u7269\u867d\u7136\u6b8b\u5e9f\uff0c\u8eab\u6cd5\u5374\u662f\u53e4\u602a\u4e4b\u6781\uff0c\u53ea\u542c\u5f97\u201c\u53ee\u201d\u7684\u4e00 \u58f0\uff0c\u4ed6\u7684\u94c1\u62d0\u5728\u5730\u4e0a\u4e00\u70b9\uff0c\u5df2\u5411\u540e\u5012\u8dc3\u4e86\u4e24\u4e09\u4e08\u8fdc\uff0c\u4e91\u821e\u9633\u7adf\u662f\u6293\u4e86\u4e2a\u7a7a\uff0c\u8fd9\u4e00\u4e0b\u8fde\u4e91\u7d20\u7d20 \u4e5f\u8be7\u5f97\u53eb\u51fa\u58f0\u6765\u3002 \u3000\u3000\u90a3\u602a\u4eba\u5355\u8db3\u7ad9\u5b9a\u4e4b\u540e\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e91\u821e\u9633\u4f60\u60f3\u89c1\u6211\u7684\u672c\u6765\u9762\u76ee\uff0c\u54c8\uff0c\u6211\u54ea\u91cc\u8fd8\u6709\u672c\u6765 \u9762\u76ee\u89c1\u4f60\uff1f\u4e5f\u7f62\uff0c\u65e2\u60f3\u89c1\u5c31\u7531\u4f60\u89c1\u5427\uff0c\u53ea\u6050\u4e8e\u4f60\u4e0d\u4fbf\uff01\u201d\u4e91\u821e\u9633\uff0c\u4e91\u7d20\u7d20\uff0c\u6b66\u5f53\u4e94\u8001\u5168\u90e8\u76ee \u4e0d\u8f6c\u77ac\u7684\u6ce8\u89c6\u7740\u4ed6\uff0c\u8fd9\u602a\u4eba\u7f13\u7f13\u7684\u5c06\u8499\u9762\u5dfe\u626f\u4e0b\uff0c\u4e91\u821e\u9633\u5fc3\u5934\u6251\u901a\u4e00\u8df3\uff0c\u4e91\u7d20\u7d20\u63a9\u4e86\u773c\u775b\uff0c \u6b66\u5f53\u4e94\u8001\u4e5f\u53ea\u89c9\u4e0d\u5bd2\u800c\u6817\u3002 \u3000\u3000\u8fd9\u624b\u8db3\u6b8b\u5e9f\u7684\u4e5e\u4e10\u76f8\u8c8c\u7684\u5947\u4e11\uff0c\u7b80\u76f4\u51fa\u4e4e\u4efb\u4f55\u4eba\u7684\u610f\u60f3\u4e4b\u5916\uff0c\u4f46\u89c1\u4ed6\u8138\u4e0a\u4f24\u75d5\u904d\u5e03\uff0c\u7eb5 \u6a2a\u4ea4\u9519\uff0c\u5c31\u5982\u5341\u5b57\u8def\u53e3\u7684\u8f66\u8f68\u4e00\u822c\uff01\u800c\u4e14\u5728\u6b66\u5f53\u4e94\u8001\u4e0e\u4e91\u7d20\u7d20\u7684\u773c\u4e2d\uff0c\u8fd9\u526f\u5c0a\u5bb9\u867d\u7136\u53ef\u6016\uff0c \u4ea6\u4e0d\u8fc7\u4ec5\u4ec5\u662f\u4e11\u602a\u800c\u5df2\uff0c\u5728\u4e91\u821e\u9633\u773c\u4e2d\uff0c\u5374\u53e6\u6709\u66f4\u4ee4\u4ed6\u60ca\u5fc3\u52a8\u9b44\u4e4b\u5904\uff0c\u4ed6\u8138\u4e0a\u7684\u4f24\u75d5\u867d\u7136\u7eb5 \u6a2a\u4ea4\u9519\uff0c\u4f46\u4e91\u821e\u9633\u662f\u5f53\u4eca\u5929\u4e0b\u7684\u7b2c\u4e00\u5251\u5ba2\uff0c\u5374\u81ea\u770b\u5f97\u51fa\u6765\u3002\u8fd9\u4e9b\u4f24\u75d5\u4e43\u662f\u9876\u7740\u5251\u5c16\u7684\u4e00\u62d6\u4e4b \u52bf\u5168\u90e8\u5212\u6210\u7684\uff0c\u5c31\u50cf\u8349\u4e66\u540d\u5bb6\u6240\u5199\u7684\u5b57\uff0c\u867d\u7136\u7b14\u5212\u590d\u6742\uff0c\u5374\u662f\u4e00\u7b14\u5230\u5e95\u4e00\u822c\uff0c\u8bd5\u60f3\u4eba\u7684\u8138\u76ae \u672c\u6765\u5c31\u8584\uff0c\u4e00\u5251\u5212\u8fc7\uff0c\u5212\u4e86\u8fd9\u8bb8\u591a\u7684\u4f24\u75d5\uff0c\u65e2\u4e0d\u525c\u51fa\u9aa8\u5934\uff0c\u53c8\u4e0d\u4f24\u53ca\u773c\u775b\u9f3b\u5b50\uff0c\u8fd9\u5c82\u4e0d\u662f\u96be \u4ee5\u601d\u8bae\u4e4b\u4e8b\uff1f\u4e91\u821e\u9633\u81ea\u5fd6\uff1a\u82e5\u7136\u662f\u81ea\u5df1\u51fa\u624b\uff0c\u53ea\u8bb8\u4e00\u5251\u5c31\u8981\u5c06\u4ed6\u4f24\u6210\u8fd9\u4e2a\u6a21\u6837\uff0c\u53ea\u6015\u4e5f\u672a\u5fc5 \u80fd\u591f\uff01\u90a3\u4e48\u5929\u4e0b\u8fd8\u6709\u4f55\u4eba\u6709\u5982\u6b64\u9ad8\u660e\u7684\u5251\u6cd5\uff1f \u3000\u3000\u90a3\u602a\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u600e\u4e48\u6837\uff1f\u4e0d\u8ba4\u8bc6\u6211\u4e86\u5417\uff1f\u201d \u3000\u3000\u4e91\u821e\u9633\u55eb\u5685\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u7389\u9762\u4e10\u4fa0\u6bd5\u51cc\u98ce\uff1f\u201d\u8bf4\u8bdd\u7684\u58f0\u8c03\u4f3c\u4e4e\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u5927\u76f8\u4fe1\u3002 \u3000\u3000\u4e91\u7d20\u7d20\u672c\u6765\u63a9\u7740\u773c\u775b\uff0c\u542c\u4e86\u8fd9\u53e5\u7981\u4e0d\u4f4f\u677e\u5f00\u53cc\u624b\uff0c\u53c8\u5077\u77a7\u4e86\u4e00\u773c\uff0c\u867d\u7136\u4e0d\u82e5\u521d\u89c1\u4e4b\u65f6\u7684 \u60ca\u60b8\uff0c\u4ecd\u7136\u5413\u5f97\u4e0d\u6562\u518d\u77a7\uff0c\u5fc3\u4e2d\u7eb3\u7f55\uff1a\u201c\u8fd9\u4e2a\u5947\u4e11\u7684\u602a\u7269\uff0c\u5374\u6709\u8fd9\u6837\u4fca\u7f8e\u7684\u7ef0\u53f7\uff01\u201d \u3000\u3000\u6bd5\u51cc\u98ce\u5728\u4e8c\u5341\u5e74\u524d\u7684\u786e\u662f\u4e2a\u76f8\u5f53\u82f1\u4fca\u7684\u7537\u5b50\uff0c\u4ed6\u7684\u54e5\u54e5\u4fbf\u662f\u5f20\u58eb\u8bda\u519b\u4e2d\u4eba\u79f0\u201c\u618e\u3001\u9053\u3001 \u4e10\u201d\u4e09\u5947\u58eb\u4e4b\u4e00\u7684\u6bd5\u51cc\u865a\uff0c\uff08\u5176\u4ed6\u4e24\u4eba\uff0c\u201c\u50e7\u201d\u662f\u5f6d\u83b9\u7389\uff0c\u201c\u9053\u201d\u662f\u4e03\u4fee\u9053\u4eba\u3002\u5f6d\u83b9\u7389\u4e0e\u77f3 \u5929\u94ce\u3001\u4e91\u821e\u9633\u53c8\u522b\u79f0\u9f99\u3001\u864e\u3001\u51e4\u4e09\u6770\u3002\uff09\u50e7\u3001\u9053\u3001\u4e10\u3001\u9f99\u3001\u864e\u3001\u51e4\uff0c\u867d\u7136\u5e76\u79f0\uff0c\u4f46\u5f6d\u83b9\u7389\u7684 \u8f88\u4efd\u548c\u5730\u4f4d\u5374\u6bd4\u5176\u4ed6\u4eba\u9ad8\u5f97\u591a\uff0c\u6731\u5143\u748b\u548c\u5f20\u58eb\u8bda\u90fd\u662f\u4ed6\u7684\u5f1f\u5b50\uff0c\u6bd5\u51cc\u98ce\u4ef0\u6155\u4ed6\u7684\u6b66\u529f\uff0c\u5728\u519b \u4e2d\u76f8\u9047\u4e4b\u540e\uff0c\u575a\u8981\u62dc\u4ed6\u4e3a\u5e08\uff0c\u7b97\u662f\u4ed6\u7684\u7b2c\u4e09\u4e2a\u5f1f\u5b50\u3002 \u3000\u3000\u534e\u51cc\u98ce\u7684\u6b66\u529f\u662f\u4ed6\u7684\u54e5\u54e5\u4eb2\u6388\u3002\u95f4\u63a5\u4e5f\u5b66\u5230\u4e86\u5f6d\u548c\u5c1a\u7684\u4e00\u4e9b\u5947\u5999\u5185\u529f\uff0c\u4e3a\u4e86\u5c0a\u656c\u5f6d\u548c \u5c1a\uff0c\u5728\u6c5f\u6e56\u4e0a\u4fbf\u81ea\u79f0\u662f\u5f6d\u83b9\u7389\u7684\u5f1f\u5b50\u3002\u6bd5\u51cc\u98ce\u751f\u6027\u4e0d\u7f81\uff0c\u4e0d\u8010\u519b\u4e2d\u751f\u6d3b\u7684\u62d8\u675f\uff0c\u4fbf\u9690\u8eab\u5728\u4e10 \u5e2e\u4e4b\u4e2d\uff0c\u505a\u4e00\u4e2a\u6e38\u620f\u98ce\u5c18\u7684\u4fa0\u4e10\u3002\u4e91\u821e\u9633\u4e0e\u4ed6\u7684\u4ea4\u60c5\u867d\u7136\u4e0d\u7b97\u6df1\u539a\uff0c\u7531\u4e8e\u4ed6\u54e5\u54e5\u7684\u5173\u7cfb\uff0c\u5f53 \u5e74\u4e5f\u6c93\u4ee5\u5144\u5f1f\u76f8\u79f0\u3002\u5728\u5f20\u58eb\u8bda\u5175\u8d25\u5bcc\u4ea1\u7684\u524d\u4e00\u5e74\u8d77\u4e24\u4eba\u4fbf\u6ca1\u6709\u89c1\u8fc7\u9762\uff0c\u7b97\u8d77\u6765\u5df2\u6709\u5341\u4e5d\u5e74 \u4e86\u3002 \u3000\u3000\u60f3\u4e0d\u5230\u73b0\u5728\u91cd\u9022\uff0c\u6bd5\u51cc\u98ce\u5374\u53d8\u6210\u4e86\u8fd9\u4e2a\u6837\u5b50\uff0c\u4e91\u821e\u9633\u6709\u4e24\u4ef6\u4e8b\u60c5\u611f\u5230\u6781\u4e3a\u5947\u602a\uff0c\u7b2c\u4e00\u4ef6 \u662f\u6bd5\u51cc\u98ce\u7684\u6b66\u529f\u867d\u7136\u8fd8\u4e0d\u7b97\u662f\u9876\u513f\u5c16\u513f\u7684\u89d2\u8272\uff0c\u4f46\u6c5f\u6e56\u4e0a\u80fd\u80dc\u8fc7\u4ed6\u7684\u4eba\u5df2\u662f\u5be5\u5be5\u65e0\u51e0\uff0c\u662f\u8c01 \u80fd\u4ee4\u4ed6\u53d7\u5982\u6b64\u5de8\u521b\uff1f\u5374\u53c8\u5e76\u4e0d\u628a\u4ed6\u6740\u6b7b\uff1f\u7b2c\u4e8c\u4ef6\u662f\uff1a\u4ed6\u867d\u7136\u653e\u8361\u4e0d\u7f81\uff0c\u5f53\u5e74\u5bf9\u81ea\u5df1\u4e5f\u9887\u4e3a\u5c0a \u656c\uff0c\u4f55\u4ee5\u5982\u4eca\u5374\u662f\u5982\u6b64\u4fae\u6162\uff0c\u7adf\u6562\u53eb\u81ea\u5df1\u505a\u201c\u5c0f\u8d3c\u201d\uff0c\u8fd8\u6562\u5411\u81ea\u5df1\u7d22\u5251\u8c31\uff1f\u96be\u9053\u76f8\u8c8c\u53d8\u4e86\uff0c \u6027\u60c5\u4e5f\u8ddf\u7740\u53d8\u4e0d\u6210\uff1f\u6216\u662f\u56e0\u4e3a\u4ed6\u77e5\u9053\u4e86\u81ea\u5df1\u7684\u9690\u79d8\uff0c\u4fbf\u80c6\u6562\u524d\u6765\u8981\u631f\uff1f \u3000\u3000\u601d\u5ff5\u53ca\u6b64\uff0c\u4e91\u821e\u9633\u6697\u751f\u6012\u6c14\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u6211\u5341\u4e5d\u5e74\u6ca1\u6709\u89c1\u9762\uff0c\u4f60\u6765\u89c1\u6211\uff0c\u5c31\u662f\u4e3a\u7684 \u8981\u5251\u8c31\u4e48\uff1f\u201d\u6bd5\u51cc\u98ce\u7528\u66f4\u51b7\u9985\u7684\u58f0\u97f3\u7b54\u9053\uff1a\u201c\u6211\u65b0\u6536\u4e86\u4e00\u4e2a\u597d\u5f92\u5f1f\uff0c\u603b\u5f97\u9001\u4ed6\u4e00\u4ef6\u89c1\u9762\u793c \u7269\uff0c\u8fd9\u5251\u8c31\u672c\u6765\u53c8\u662f\u5e94\u5c5e\u4ed6\u7684\uff0c\u6211\u4e0d\u627e\u4f60\u627e\u8c01\uff1f\u201d \u3000\u3000\u4e91\u821e\u9633\u62cd\u4e86\u4e00\u4e0b\u624b\u638c\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u53ef\u60dc\u4f60\u6765\u8fdf\u4e86\u4e00\u6b65\uff0c\u8fd9\u5251\u8c31\u65e9\u5c31\u6495\u5f97\u7247\u7247\u788e\u4e86\u3002\u4e0a \u5b98\u5929\u91ce\u8981\u5b66\uff0c\u53eb\u4ed6\u524d\u6765\u89c1\u6211\u3002\u201d \u3000\u3000\u6bd5\u51cc\u98ce\u51b7\u7b11\u9053\uff1a\u201c\u4e0a\u5b98\u5929\u91ce\u5c31\u662f\u56e0\u4e3a\u4e0d\u613f\u4ece\u4f60\u7684\u624b\u4e0a\u5b66\u53d6\u6b66\u529f\uff0c\u8fd9\u624d\u62dc\u6211\u4e3a\u5e08\u3002\u597d\uff0c\u5251 \u8c31\u65e2\u6bc1\u4e86\uff0c\u6211\u53ea\u6709\u5411\u4f60\u53e6\u8981\u4e00\u4ef6\u793c\u7269\u9001\u7ed9\u6211\u7684\u5f92\u5f1f\u5566\uff01\u201d\u94c1\u62d0\u84e6\u5730\u4e00\u6491\uff0c\u8eab\u4f3c\u79bb\u5f26\u4e4b\u7bad\uff0c\u4e00 \u4e2a\u8d77\u843d\uff0c\u4fbf\u8d70\u5230\u4e86\u4e91\u7d20\u7d20\u7684\u8ddf\u524d\uff0c\u4f38\u624b\u62a2\u5979\u7684\u5b9d\u5251\uff0c\u4e91\u7d20\u7d20\u5413\u5f97\u82b1\u5bb9\u5931\u8272\uff0c\u53eb\u4e0d\u6210\u58f0\uff0c\u8bf4\u65f6 \u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5c31\u5728\u6bd5\u51cc\u98ce\u7684\u624b\u6307\u582a\u582a\u8981\u6cbe\u5230\u4e91\u7d20\u7d20\u7684\u65f6\u5019\uff0c\u4e91\u821e\u9633\u5df2\u662f\u98de\u8eab\u8dc3\u8d77\uff0c\u5982\u5f71\u968f \u5f62\uff0c\u8ddf\u8e2a\u800c\u5df1\u81f3\uff0c\u4eba\u5c1a\u5728\u534a\u7a7a\uff0c\u4fbf\u4e00\u4e2a\u5288\u7a7a\u638c\u53d1\u51fa\uff0c\u5927\u6012\u559d\u9053\uff1a\u201c\u6bd5\u51cc\u98ce\uff0c\u4f60\u80c6\u6562\u5982\u6b64\u65e0 \u793c\uff01\u201d \u3000\u3000\u4e91\u821e\u9633\u8fd9\u4e00\u638c\u51cc\u7a7a\u4e0b\u51fb\uff0c\u52bf\u9053\u5a01\u731b\u65e0\u4f26\uff0c\u6bd5\u51cc\u98ce\u94c1\u62d0\u70b9\u5730\uff0c\u201c\u7ec6\u80f8\u5de7\u7ffb\u4e91\u201d\u53c8\u5012\u7eb5\u51fa\u4e00 \u4e08\u5f00\u5916\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u628a\u6606\u543e\u5b9d\u5251\u4e5f\u662f\u5077\u6765\u7684\uff0c\u4f60\u662f\u6697\u5077\uff0c\u6211\u662f\u660e\u62a2\uff0c\u5f7c\u6b64\u5f7c\u6b64\uff0c\u6709\u751a \u4e48\u65e0\u793c\u53ef\u8a00\uff01\u201d \u3000\u3000\u4e91\u7d20\u7d20\u60ca\u9b42\u65b9\u5b9a\uff0c\u542c\u4e86\u8fd9\u8bdd\uff0c\u4e0d\u7981\u53c8\u6014\u7740\u4e86\u3002\u8fd9\u602a\u4eba\u7adf\u7136\u77e5\u9053\u5979\u7684\u5251\u540d\uff0c\u8fd8\u8bf4\u8fd9\u628a\u5b9d\u5251 \u662f\u4ed6\u7236\u4eb2\u5077\u6765\u7684\uff01\u4ed6\u84e6\u7136\u60f3\u8d77\u9648\u7384\u673a\u4e0e\u5979\u521d\u89c1\u9762\u4e4b\u65f6\uff0c\u4e5f\u66fe\u95ee\u8fc7\u5979\u8fd9\u628a\u5b9d\u5251\u662f\u4e0d\u662f\u5bb6\u4f20\u4e4b \u7269\uff0c\u83ab\u975e\uff0c\u83ab\u975e\u2026\u2026\u83ab\u975e\u771f\u4e2a\u662f\u5077\u6765\u7684\uff1f\u5979\u5728\u5b69\u63d0\u7684\u65f6\u5019\uff0c\u4fbf\u77e5\u9053\u5bb6\u4e2d\u6709\u8fd9\u628a\u5b9d\u5251\uff0c\u7236\u4eb2\u4e5f \u66fe\u8bf4\u8fc7\uff1a\u8fd9\u628a\u5b9d\u5251\u5c06\u6c38\u8fdc\u662f\u4ed6\u4eec\u4e91\u5bb6\u7684\u4f20\u5bb6\u4e4b\u5b9d\uff01 \u3000\u3000\u968f\u53c8\u60f3\u5230\uff1a\u8fd9\u628a\u5b9d\u5251\u4e43\u662f\u7a00\u4e16\u4e4b\u73cd\uff0c\u82e5\u7136\u771f\u4e2a\u662f\u5077\u6765\u7684\uff0c\u7ecf\u8fc7\u8fd9\u4e48\u6f2b\u957f\u7684\u5c81\u6708\uff0c\u5931\u4e3b\u5c82 \u6709\u4e0d\u6765\u8ffd\u7a76\u4e4b\u7406\uff1f\u9664\u975e\u662f\u7236\u4eb2\u5c06\u4ed6\u6740\u4e86\uff01\u83ab\u975e\u8fd9\u5c31\u662f\u7236\u4eb2\u6240\u8bf4\u7684\u2014\u2014\u6700\u5927\u7684\u7f6a\u5b7d\uff1f\u4f46\u7acb\u5373\u53c8 \u628a\u81ea\u5df1\u8fd9\u60f3\u6cd5\u63a8\u7ffb\uff1a\u80fd\u6709\u8fd9\u628a\u5b9d\u5251\u7684\u4eba\uff0c\u5b9a\u7136\u4e0d\u662f\u5bfb\u5e38\u7684\u4eba\u7269\uff0c\u7236\u4eb2\u82e5\u771f\u7684\u662f\u5e72\u4e0b\u4e86\u76d7\u5251\u6740 \u4eba\u7684\u5927\u7f6a\uff0c\u6b66\u6797\u4e2d\u65e9\u5df2\u6380\u8d77\u4e86\u8f69\u7136\u5927\u6ce2\uff0c\u5251\u4e3b\u7684\u670b\u53cb\u95e8\u4eba\uff0c\u4e5f\u65e9\u5c31\u8be5\u6765\u5174\u5e08\u95ee\u7f6a\uff0c\u4f55\u81f3\u4e8e\u5230 \u4eca\u65e5\u65b9\u624d\u7206\u53d1\uff1f \u3000\u3000\u4e91\u7d20\u7d20\u7684\u6025\u6b32\u91ca\u7591\u4e4b\u5fc3\uff0c\u76d6\u8fc7\u4e86\u5979\u5bf9\u90a3\u4e2a\u602a\u4eba\u7684\u6050\u60e7\uff0c\u5979\u56de\u8fc7\u8eab\uff0c\u770b\u5979\u7684\u7236\u4eb2\u600e\u6837\u5bf9\u4ed8 \u90a3\u4e2a\u602a\u4eba\uff0c\u773c\u5149\u4e00\u77a5\uff0c\u4f46\u89c1\u4ed6\u7236\u4eb2\u7684\u795e\u60c5\u4e5f\u662f\u975e\u5e38\u602a\u5f02\uff0c\u597d\u4f3c\u7a81\u7136\u88ab\u4eba\u70b9\u4e2d\u4e86\u7a74\u9053\u4f3c\u7684\uff0c\u534a \u6761\u817f\u65b9\u8de8\u51fa\u53bb\uff0c\u8981\u8ffd\u51fb\u90a3\u4e2a\u602a\u4eba\uff0c\u5374\u5ffd\u5730\u505c\u4f4f\uff0c\u8138\u76ae\u7ef7\u7d27\uff0c\u773c\u5149\u4e2d\u9690\u9690\u900f\u51fa\u6740\u6c14\uff0c\u4f46\u773c\u73e0\u95ea \u70c1\u4e0d\u5b9a\uff0c\u53c8\u4f3c\u5fc3\u4e2d\u5c1a\u81ea\u8e0c\u8e87\u672a\u51b3\u3002 \u3000\u3000\u84e6\u542c\u5f97\u4e91\u821e\u9633\u559d\u9053\uff1a\u201c\u6bd5\u51cc\u98ce\uff0c\u4f60\u5feb\u70b9\u8d70\uff0c\u518d\u8fdf\u4e00\u523b\uff0c\u6211\u5c31\u7ba1\u4e0d\u4f4f\u81ea\u5df1\u5566\u3002\u201d\u58f0\u97f3\u4f4e\u6c89 \u98a4\u6296\uff0c\u5341\u53ea\u6307\u5934\u4e00\u4f38\u4e00\u5c48\uff0c\u9aa8\u8282\u683c\u683c\u4f5c\u54cd\uff0c\u771f\u4f3c\u5c31\u8981\u52a8\u624b\u6740\u4eba\u7684\u5149\u666f\uff0c\u4e91\u7d20\u7d20\u5927\u4e3a\u60ca\u6050\uff0c\u51b7 \u610f\u76f4\u900f\u5fc3\u5934\uff0c\u770b\u7236\u4eb2\u7684\u795e\u60c5\uff0c\u8fd9\u628a\u5b9d\u5251\u7684\u6765\u5386\u53ea\u6015\u5f53\u771f\u6709\u70b9\u53e4\u602a\uff0c\u8981\u4e0d\u7136\u4ed6\u4e0d\u4f1a\u50cf\u53d7\u4f24\u7684\u91ce \u517d\u4e00\u6837\uff0c\u6562\u60c5\u662f\u90a3\u602a\u4eba\u7684\u8bf4\u8bdd\uff0c\u5c31\u4f3c\u730e\u4eba\u6269\u5f13\u7bad\u4e00\u6837\uff0c\u5c04\u4f24\u4e86\u4ed6\uff01 \u3000\u3000\u90a3\u602a\u4eba\u5374\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e91\u821e\u9633\u4f60\u60f3\u6740\u6211\uff0c\u6211\u82e5\u6015\u88ab\u4f60\u6740\uff0c\u4e5f\u5c31\u4e0d\u4f1a\u6765\u5566\uff1f\u4f60\u81ea\u4ee5\u4e3a\u5f97\u4e86 \u8fbe\u6469\u5251\u8c31\u548c\u6740\u4e86\u77f3\u5929\u94ce\u4e4b\u540e\uff0c\u4fbf\u5f53\u771f\u662f\u6b66\u529f\u5929\u4e0b\u7b2c\u4e00\u4e86\u4e48\uff1f\u6709\u5f6d\u548c\u5c1a\u7684\u5f1f\u5b50\u5728\u6b64\uff0c\u53ea\u6015\u8fd8\u672a \u5230\u4f60\u901e\u80fd\uff01\u201d \u3000\u3000\u4e91\u821e\u9633\u9053\uff1a\u201c\u5f6d\u548c\u5c1a\u82e5\u5728\uff0c\u6211\u8ba9\u4ed6\u4e09\u5206\uff0c\u7eb5\u8bb8\u4f60\u54e5\u54e5\u590d\u751f\uff0c\u6211\u4ea6\u4e0d\u60e7\uff0c\u4f60\u662f\u4ec0\u4e48\u4e1c \u897f\uff1f\u201d\u6012\u706b\u4e00\u53d1\uff0c\u4e0d\u53ef\u6291\u6b62\uff0c\u84e6\u7136\u4e00\u8bb0\u201c\u624b\u6325\u7435\u7436\u201d\uff0c\u638c\u529b\u4e2d\u631f\u7740\u4e00\u6307\u63b8\u7684\u529f\u592b\uff0c\u53d1\u4e86\u51fa \u53bb\uff0c\u6bd5\u51cc\u98ce\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u5251\uff0c\u6211\u4e5f\u4e0d\u7528\u5175\u5668\u3002\u201d\u94c1\u62d0\u4e00\u63b7\uff0c\u63d2\u5728\u5730\u4e0a\uff0c\u624b\u8155\u4e00\u7ffb\uff0c\u7adf\u628a \u638c\u5fc3\u8fce\u4e86\u4e0a\u53bb\uff0c\u6b66\u5f53\u4e94\u8001\u89c1\u8bc6\u8fc7\u4e00\u6307\u7985\u7684\u529f\u592b\uff0c\u4e0d\u80dc\u8be7\u5f02\uff0c\u5fc3\u4e2d\u90fd\u9053\uff1a\u201c\u96be\u9053\u8fd9\u4e2a\u602a\u4eba\u8fd8\u6709 \u4ec0\u4e48\u90aa\u6cd5\uff0c\u7adf\u6562\u786c\u6321\u4e00\u6307\u7985\u529f\uff01\u201d \u3000\u3000\u4f46\u89c1\u4e91\u821e\u9633\u9762\u8272\u5fae\u53d8\uff0c\u5ffd\u5730\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u4f38\u51fa\u7684\u4e2d\u6307\u500f\u5730\u6536\u4e86\u56de\u6765\uff0c\u5316\u6307\u4e3a\u638c\uff0c\u8fce\u4e86 \u4e0a\u53bb\uff0c\u53cc\u638c\u65b9\u76f8\u4ea4\uff0c\u773c\u770b\u53cc\u65b9\u90fd\u662f\u7528\u4e86\u6781\u5f3a\u52b2\u7684\u529b\u9053\uff0c\u4f46\u76f8\u4ea4\u4e4b\u9645\uff0c\u7adf\u662f\u65e0\u58f0\u65e0\u606f\uff0c\u4fbf\u5982\u80f6 \u7740\u4e86\u4e00\u822c\uff0c\u6b66\u5f53\u4e94\u8001\u90fd\u7741\u5927\u773c\u775b\uff0c\u83ab\u540d\u5176\u5999\u3002 \u3000\u3000\u539f\u6765\u6bd5\u51cc\u98ce\u7ec3\u7684\u662f\u4e00\u79cd\u6781\u602a\u5f02\u9634\u67d4\u7684\u638c\u529b\uff0c\u4e91\u821e\u9633\u6307\u5c16\u6240\u89e6\uff0c\u7adf\u4f3c\u8f6f\u7ef5\u7ef5\u7684\u4e00\u5806\u7a00\u70c2\u7684 \u8f6f\u6ce5\uff0c\u975e\u4f46\u6beb\u65e0\u53ef\u4ee5\u7740\u529b\u4e4b\u5904\uff0c\u800c\u4e14\u6bd5\u51cc\u98ce\u7684\u638c\u5fc3\u8fd8\u53d1\u51fa\u4e00\u80a1\u65cb\u8f6c\u7684\u5438\u529b\uff0c\u7adf\u4f3c\u8981\u786c\u628a\u4e91\u821e \u9633\u7684\u6307\u5934\u9677\u4e86\u8fdb\u53bb\u3002\u4e00\u6307\u7985\u529f\u7684\u5389\u5bb3\uff0c\u5728\u4e8e\u80fd\u5c01\u95ed\u654c\u4eba\u7684\u201c\u9690\u7a74\u201d\uff0c\u4e00\u88ab\u5438\u7740\uff0c\u8fd9\u529f\u529b\u5c31\u65e0 \u4ece\u53d1\u6325\uff0c\u4ee5\u6307\u654c\u638c\uff0c\u5f53\u7136\u5403\u4e8f\uff0c\u6240\u4ee5\u8981\u5316\u638c\u5e94\u4ed8\u3002\u6bd5\u51cc\u98ce\u559d\u9053\uff1a\u201c\u53cc\u638c\u9f50\u6765\uff0c\u201d\u4e91\u821e\u9633 \u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u610f\u6b8a\u4e0d\u5c51\uff0c\u4ecd\u662f\u5355\u638c\u8fce\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u8fc7\u4e86\u7247\u523b\uff0c\u4f46\u89c1\u4e91\u821e\u9633\u989d\u4e0a\u5fae\u5fae\u6dcc\u6c57\uff0c\u5ffd\u5730\u559d\u9053\uff1a\u201c\u6211\u9759\u5ba4\u7684\u95e8\u539f\u6765\u679c\u7136\u662f\u4f60\u8fd9\u53ae\u6bc1 \u7684\uff01\u201d\u6bd5\u51cc\u98ce\u7b11\u9053\uff1a\u201c\u6211\u65e9\u8bf4\u8fc7\uff0c\u4f60\u8981\u4e0d\u4fe1\uff0c\u6709\u4ec0\u4e48\u529e\u6cd5\uff1f\u82e5\u975e\u6211\u6467\u6bc1\u4f60\u7684\u6d1e\u95e8\uff0c\u600e\u80fd\u5e26\u8d70 \u4e0a\u5b98\u5929\u91ce\uff1f\u201d \u3000\u3000\u4e91\u7d20\u7d20\u8fd9\u4e00\u6765\u4e0d\u80fd\u4e0d\u76f8\u4fe1\u4e86\uff0c\u4f46\u89c9\u4e0a\u5b98\u5929\u91ce\u4e4b\u80af\u8ddf\u4ed6\u51fa\u8d70\uff0c\u800c\u4e14\u542c\u8fd9\u602a\u4eba\u6240\u8bf4\uff0c\u5c45\u7136\u8fd8 \u80af\u62dc\u4ed6\u4e3a\u5e08\uff0c\u5f53\u771f\u662f\u79bb\u5947\u53e4\u602a\uff0c\u4e0d\u53ef\u601d\u8bae\u4e4b\u4e8b\uff01 \u3000\u3000\u6b66\u5f53\u4e94\u8001\u867d\u7136\u4e0d\u61c2\u5f97\u8fd9\u602a\u4eba\u7684\u638c\u529b\u5947\u5999\uff0c\u4f46\u89c1\u8fd9\u6837\u5b50\uff0c\u4e5f\u77e5\u9053\u5df2\u662f\u4e91\u821e\u9633\u5403\u4e86\u4e8f\uff0c\u5fc3\u4e2d \u90fd\u662f\u53c8\u60ca\u53c8\u559c\uff0c\u4ed6\u4eec\u867d\u7136\u6068\u4e0d\u5f97\u501f\u8fd9\u602a\u4eba\u4e4b\u624b\uff0c\u62a5\u90a3\u4e00\u7bad\u4e4b\u4ec7\uff0c\u4f46\u60f3\u5230\u8fd9\u602a\u4eba\u5c45\u7136\u8981\u903c\u4ed6\u4eec \u7684\u638c\u95e8\u5f1f\u5b50\u4e3a\u5f92\uff0c\u53c8\u90fd\u7981\u4e0d\u4f4f\u5fc3\u4e2d\u4e4b\u6124\uff01 \u3000\u3000\u6309\u8bf4\u4e91\u821e\u9633\u7684\u5185\u529f\u672c\u6765\u80dc\u8fc7\u6bd5\u51cc\u98ce\uff0c\u4f46\u4ed6\u4e0e\u6b66\u5f53\u4e94\u8001\u5df2\u6076\u6597\u4e86\u534a\u5929\uff0c\u88ab\u201c\u4e94\u96f7\u5929\u5fc3\u638c \u6cd5\u201d\u6d88\u8017\u4e86\u4e0d\u5c11\u771f\u529b\uff0c\u8981\u4e0d\u662f\u6709\u90a3\u9897\u5c0f\u8fd8\u4e39\uff0c\u65e9\u5df2\u4e0d\u6613\u652f\u6301\uff0c\u518d\u8fc7\u7247\u523b\uff0c\u4e91\u821e\u9633\u989d\u4e0a\u7684\u6c57\u73e0 \u8d8a\u6ef4\u8d8a\u7c97\uff0c\u6bd5\u51cc\u98ce\u51b7\u7b11\u9053\uff1a\u201c\u53cc\u638c\u9f50\u6765\uff01\u201d\u4e91\u821e\u9633\u672c\u4e0d\u613f\u610f\u7528\u53cc\u638c\u5bf9\u4ed8\u4e00\u4e2a\u6b8b\u5e9f\u7684\u4eba\uff0c\u8f6c\u5ff5 \u4e00\u60f3\uff0c\u81ea\u5df1\u6076\u6597\u8fde\u573a\uff0c\u771f\u529b\u5927\u8017\uff0c\u7528\u53cc\u638c\u4e5f\u4e0d\u7b97\u5360\u4ed6\u4fbf\u5b9c\uff0c\u4e8e\u662f\u5251\u7709\u4e00\u626c\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u90a3 \u53ef\u662f\u4f60\u81ea\u5df1\u8981\u7684\u3002\u201d\u6bd5\u51cc\u98ce\u9053\uff1a\u201c\u5c3d\u7ba1\u4f7f\u6765\uff0c\u867d\u6b7b\u65e0\u6028\uff01\u201d \u3000\u3000\u4e91\u821e\u9633\u53cc\u638c\u4e00\u5408\uff0c\u638c\u529b\u8db3\u53ef\u5f00\u7891\u88c2\u77f3\uff0c\u4e00\u80a1\u6781\u5a01\u731b\u7684\u529b\u9053\u76f4\u903c\u8fc7\u53bb\uff0c\u6bd5\u51cc\u98ce\u5355\u8db3\u7262\u7262\u9489 \u5728\u5730\u4e0a\uff0c\u8eab\u8eaf\u5374\u4f3c\u5c0f\u821f\u9047\u6d6a\u4e00\u6837\uff0c\u524d\u540e\u5de6\u53f3\uff0c\u6447\u6446\u4e0d\u505c\uff0c\u4e91\u7d20\u7d20\u89c9\u5f97\u8fd9\u4e2a\u602a\u4eba\u867d\u662f\u4ee4\u4eba\u618e \u538c\uff0c\u4f46\u534a\u8eab\u6b8b\u5e9f\uff0c\u5374\u4e5f\u53ef\u601c\uff0c\u6b63\u60f3\u53eb\u7239\u7239\u9976\u4ed6\uff0c\u5ffd\u89c1\u7239\u7239\u9762\u8272\u6709\u5f02\uff0c\u4ed4\u7ec6\u4e00\u77a7\uff0c\u989d\u4e0a\u7684\u6c57\u73e0 \u5168\u90fd\u6536\u4e86\uff0c\u4e00\u6761\u6761\u7684\u9752\u7b4b\u5374\u8c41\u9732\u51fa\u6765\u3002 \u3000\u3000\u4e91\u7d20\u7d20\u867d\u7136\u770b\u51fa\u6709\u70b9\u4e0d\u5999\uff0c\u5374\u8fd8\u4e0d\u77e5\u9053\u5979\u7684\u7239\u7239\u5df2\u5230\u4e86\u5371\u9669\u7684\u8fb9\u7f18\uff0c\u90a3\u602a\u4eba\u7684\u638c\u529b\u602a\u5f02 \u4e4b\u6781\uff0c\u4e91\u821e\u9633\u90a3\u4e48\u731b\u7684\u529b\u9053\uff0c\u78b0\u4e0a\u53bb\u4e5f\u5982\u6295\u5165\u6c34\u4e2d\u4e00\u6837\uff0c\u88ab\u6d88\u89e3\u4e8e\u65e0\u5f62\u4e4b\u4e2d\uff0c\u8fd9\u8fd8\u4e0d\u6b62\uff0c\u4ece \u90a3\u602a\u4eba\u7684\u638c\u5fc3\u4e2d\uff0c\u8fd8\u9690\u9690\u900f\u51fa\u4e00\u80a1\u9634\u51b7\u4e4b\u6c14\uff0c\u6c81\u5165\u4e91\u821e\u9633\u7684\u76ae\u80a4\uff0c\u76f4\u653b\u5fc3\u80ba\u3002\u4e91\u821e\u9633\u8fd0\u4e86\u4e00 \u53e3\u771f\u6c14\uff0c\u62a4\u4f4f\u5fc3\u5934\uff0c\u4f46\u4ecd\u4e0d\u80fd\u5b8c\u5168\u6291\u6b62\u90a3\u80a1\u51b7\u6c14\u7684\u4e0a\u5347\u4e4b\u52bf\u3002 \u3000\u3000\u6bd5\u51cc\u98ce\u5f97\u610f\u4e4b\u6781\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u5634\u89d2\u62bd\u6410\uff0c\u76f8\u8c8c\u8d8a\u89c1\u6ce5\u79d8\u9a87\u4eba\uff0c\u4e00\u7b11\u4e4b\u540e\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a \u201c\u82e5\u60f3\u6d3b\u547d\uff0c\u5b9d\u5251\u62ff\u6765\uff01\u201d\u4e91\u7d20\u7d20\u624b\u6367\u6606\u543e\u5b9d\u5251\uff0c\u8d70\u4e0a\u4e24\u6b65\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u7239\u7239\uff0c\u5c31\u7ed9\u4e86\u4ed6 \u5427\uff01\u201d\u4f46\u89c1\u4e91\u821e\u9633\u773c\u5149\u4e00\u77a5\uff0c\u7231\u601c\u4e4b\u4e2d\u542b\u7740\u8d23\u5907\uff0c\u4e0d\u7528\u8bf4\u8bdd\uff0c\u4e91\u7d20\u7d20\u5df2\u77e5\u9053\u4ed6\u7236\u4eb2\u7684\u610f\u601d\uff0c \u5fc3\u4e2d\u867d\u662f\u4e0d\u613f\u7236\u4eb2\u548c\u90a3\u602a\u4eba\u6b7b\u62fc\uff0c\u4e5f\u4e0d\u5f97\u4e0d\u9000\u8fc7\u4e00\u8fb9\u3002 \u3000\u3000\u53ea\u542c\u5f97\u4e91\u821e\u9633\u4e00\u58f0\u4f4e\u53f1\uff0c\u53cc\u638c\u5411\u5185\u4e00\u6536\uff0c\u63a5\u7740\u53c8\u7f13\u7f13\u63a8\u51fa\uff0c\u624b\u80cc\u4e0a\u989d\u89d2\u4e0a\u4e00\u6761\u6761\u7684\u9752\u7b4b \u8d8a\u53d1\u8c41\u9732\uff0c\u90a3\u795e\u6c14\u5c31\u4f3c\u63a8\u633d\u7740\u5343\u4e07\u65a4\u91cd\u7269\u4e00\u822c\u3002\u4e91\u7d20\u7d20\u77e5\u9053\u7236\u4eb2\u5df2\u628a\u5185\u5bb6\u771f\u529b\u5168\u8fd0\u5230\u638c\u5fc3\u4e4b \u4e0a\uff0c\u7aef\u7684\u975e\u540c\u5c0f\u53ef\uff0c\u4f46\u89c1\u6bd5\u51cc\u865a\u8eab\u9a71\u53c8\u6643\u4e86\u51e0\u6643\uff0c\u8138\u4e0a\u90a3\u8be1\u79d8\u7684\u7b11\u5bb9\u500f\u7684\u5b8c\u5168\u6536\u655b\uff0c\u4f46\u5355\u8db3 \u4ecd\u662f\u7262\u7262\u7684\u9489\u5728\u5730\u4e0a\u3002 \u3000\u3000\u8fc7\u4e86\u597d\u4e00\u4f1a\u5b50\uff0c\u53ea\u89c1\u6bd5\u51cc\u98ce\u4e5f\u50cf\u4e91\u821e\u9633\u521a\u624d\u4e00\u6837\uff0c\u6c57\u51fa\u5982\u96e8\uff0c\u800c\u4e91\u821e\u9633\u7684\u8138\u4e0a\u5374\u7b3c\u7f69\u7740 \u4e00\u5c42\u6de1\u6de1\u7684\u7d2b\u6c14\uff0c\u6b66\u5f53\u4e94\u8001\u4ecd\u662f\u76d8\u819d\u5750\u5728\u5730\u4e0a\u8fd0\u529f\uff0c\u4f46\u663e\u7136\u662f\u88ab\u8fd9\u5267\u6597\u5206\u4e86\u5fc3\u795e\uff0c\u4e2a\u4e2a\u4fa7\u76ee \u659c\u7768\uff0c\u9732\u51fa\u60ca\u9a87\u7684\u795e\u8272\u3002 \u3000\u3000\u539f\u6765\u8fd9\u65f6\u4e91\u3001\u6bd5\u4e8c\u4eba\u5df2\u5230\u4e86\u751f\u6b7b\u5f85\u51b3\u7684\u5730\u6b65\uff0c\u4e91\u821e\u9633\u7684\u5185\u5bb6\u5143\u9633\u4e4b\u6c14\u51dd\u805a\u6307\u5c16\uff0c\u4e00\u80a1\u70ed \u529b\u4e5f\u662f\u4ece\u6307\u5c16\u4e0a\u4f20\u4e86\u8fc7\u53bb\uff0c\u4e00\u65b9\u9762\u6297\u62d2\u6bd5\u51cc\u98ce\u638c\u5fc3\u6240\u53d1\u7684\u90a3\u80a1\u9634\u51b7\u4e4b\u6c14\uff0c\u4e00\u65b9\u9762\u51b2\u51fb\u6bd5\u51cc\u98ce \u4f53\u5185\u7684\u4e03\u5904\u9690\u7a74\uff0c\u6bd5\u51cc\u98ce\u7684\u529f\u592b\u867d\u7136\u662f\u5f6d\u548c\u5c1a\u8fd9\u6d3e\u7684\u6b63\u5b97\u7384\u529f\uff0c\u5374\u662f\u5f97\u81ea\u54e5\u54e5\u6bd5\u51cc\u865a\u7684\u95f4\u63a5 \u4f20\u6388\uff0c\u5c1a\u672a\u5f97\u7aa5\u201c\u7384\u529f\u8981\u8bc0\u201d\u7684\u79d8\u5965\uff0c\u6309\u8bf4\u4e0d\u662f\u4e91\u821e\u9633\u4e4b\u654c\uff0c\u4f46\u4e91\u821e\u9633\u6628\u665a\u6076\u6597\u4e86\u77f3\u5929\u94ce\uff0c \u4eca\u671d\u53c8\u548c\u6b66\u5f53\u4e94\u8001\u82e6\u6218\u4e00\u573a\uff0c\u635f\u8017\u8fc7\u751a\uff0c\u6bd4\u5bf9\u4e4b\u4e0b\uff0c\u5374\u662f\u6bd5\u51cc\u98ce\u5360\u4e86\u4e0a\u98ce\u3002 \u3000\u3000\u518d\u8fc7\u4e00\u4f1a\uff0c\u7b3c\u7f69\u5728\u4e91\u821e\u9633\u8138\u4e0a\u7684\u7d2b\u6c14\u8d8a\u6765\u8d8a\u6d53\uff0c\u4e00\u9897\u9897\u9ec4\u8c46\u822c\u5927\u7684\u6c57\u73e0\u53c8\u8ff8\u4e86\u51fa\u6765\uff0c\u773c \u795e\u4e5f\u6e10\u6e10\u663e\u5f97\u6709\u70b9\u5446\u6ede\u4e86\uff0c\u6bd5\u51cc\u98ce\u4e00\u58f0\u602a\u5578\uff0c\u5355\u638c\u5f80\u5916\u7f13\u7f13\u63a8\u51fa\uff0c\u4e91\u821e\u9633\u5408\u53cc\u638c\u4e4b\u529b\uff0c\u7adf\u81ea \u6297\u62d2\u4e0d\u4f4f\uff0c\u624b\u81c2\u6e10\u5411\u540e\u5f2f\uff0c\u5ffd\u5730\u91cc\u6b66\u5f53\u4e94\u8001\u4e2d\u7684\u8c37\u949f\u4e00\u8dc3\u800c\u8d77\uff0c\u5389\u58f0\u53eb\u9053\uff1a\u201c\u539f\u6765\u5bb3\u6b7b\u6211\u6069 \u5e08\u7684\u5374\u662f\u4f60\u8fd9\u4e2a\u602a\u7269\uff01\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u77f3\u7834\u5929\u60ca\uff0c\u4e0d\u4f46\u4e91\u7d20\u7d20\u5982\u5760\u4e94\u91cc\u96fe\u4e2d\uff0c\u4e91\u821e\u9633\u4e5f\u662f\u5927\u51fa\u610f\u5916\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a \u201c\u6bd5\u51cc\u98ce\u4eca\u65e5\u7684\u884c\u4e3a\u602a\u8c2c\uff0c\u4ee5\u524d\u5374\u6709\u4e10\u4fa0\u4e4b\u540d\uff0c\u4ed6\u4e0e\u6211\u7684\u5cb3\u7236\u98ce\u9a6c\u725b\u4e0d\u76f8\u53ca\uff0c\u4f55\u4ee5\u5374\u8981\u5bb3\u6b7b \u6211\u7684\u5cb3\u7236\uff1f\u8c37\u949f\u521a\u624d\u8fd8\u4e00\u53e3\u54ac\u5b9a\u662f\u6211\uff0c\u4f55\u4ee5\u73b0\u5728\u5374\u7a81\u7136\u77e5\u9053\u771f\u51f6\uff1f\u201d \u3000\u3000\u539f\u6765\u725f\u72ec\u9038\u6b7b\u5f97\u5f88\u9690\u79d8\uff0c\u90a3\u65f6\u8c37\u949f\u5c1a\u968f\u4f8d\u5728\u4fa7\uff0c\u4e00\u65e5\u6df1\u591c\uff0c\u4f3c\u95fb\u5f97\u5e08\u7236\u548c\u4eba\u683c\u6597\u4e4b\u58f0\uff0c \u5230\u4ed6\u8d76\u53bb\u770b\u65f6\uff0c\u6765\u4eba\u65e9\u5df2\u8d70\u6389\uff01\u5e08\u7236\u4e5f\u5df2\u4e0d\u80fd\u8a00\u8bed\uff0c\u8138\u4e0a\u7b3c\u7f69\u7740\u4e00\u5c42\u7d2b\u6c14\uff0c\u5c31\u50cf\u4e91\u821e\u9633\u6b64\u523b \u4e00\u822c\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u6b66\u5f53\u4e94\u8001\u4e00\u9f50\u51fa\u624b\uff0c\u56f4\u6210\u4e86\u4e00\u4e2a\u5706\u5708\uff0c\u5341\u53ea\u624b\u638c\u540c\u65f6\u5411\u4e2d\u5fc3\u9f50\u903c\uff0c\u6b66 \u5f53\u4e94\u8001\u867d\u7136\u529f\u529b\u672a\u66fe\u6062\u590d\uff0c\u4f46\u4e94\u96f7\u5929\u5fc3\u638c\u4e43\u662f\u6700\u521a\u731b\u7684\u638c\u6cd5\uff0c\u5341\u638c\u9f50\u63a8\uff0c\u4ecd\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u9690 \u9690\u631f\u6709\u98ce\u96f7\u4e4b\u58f0\u3002 \u3000\u3000\u6bd5\u51cc\u98ce\u4e00\u58f0\u5927\u53eb\uff0c\u5355\u8db3\u5728\u5730\u4e0a\u6253\u4e86\u4e00\u4e2a\u76d8\u65cb\uff0c\u9661\u7136\u95f4\u4e00\u4e2a\u7b4b\u6597\u7ffb\u4e86\u8d77\u6765\uff0c\u4eba\u5728\u534a\u7a7a\uff0c\u4fbf \u662f\u4e00\u4e2a\u201c\u8e6c\u811a\u201d\u6253\u51fa\uff0c\u811a\u8e22\u667a\u5f18\u80c1\u4e0b\u7684\u201c\u767d\u6d77\u7a74\u201d\uff0c\u53f3\u624b\u9634\u638c\u62cd\u5411\u667a\u5e7f\u7684\u592a\u9633\u7a74\uff0c\u5de6\u624b\u867d\u7136 \u6b8b\u5e9f\uff0c\u4e5f\u6d3e\u7528\u573a\u3002 \u3000\u3000\u539f\u6765\u4ed6\u7684\u5de6\u81c2\u867d\u7136\u5728\u81c2\u5f2f\u4e4b\u4e0b\u5df2\u88ab\u524a\u65ad\uff0c\u4f46\u51f8\u51fa\u4e00\u5757\u9aa8\u5934\uff0c\u5305\u4ee5\u7ea2\u7f15\uff0c\u675f\u4ee5\u94c1\u76ae\uff0c\u5374\u50cf \u4e00\u628a\u672a\u51fa\u9798\u7684\u5315\u9996\uff0c\u8fd9\u628a\u201c\u5315\u9996\u201d\u5c31\u63d2\u5411\u667a\u5706\u957f\u8001\u80f8\u53e3\u7684\u201c\u7487\u7391\u7a74\u201d\uff0c\u4eba\u5728\u534a\u7a7a\uff0c\u624b\u8db3\u6b8b \u5e9f\uff0c\u5c45\u7136\u5728\u540c\u4e00\u65f6\u95f4\u8fde\u88ad\u4e09\u5927\u9ad8\u624b\uff0c\u62db\u6570\u7aef\u7684\u662f\u602a\u5f02\u65e0\u4f26\uff0c\u8fd9\u65f6\u6b66\u5f53\u4e94\u8001\u7684\u201c\u4e94\u96f7\u5929\u5fc3\u638c\u201d \u7684\u5a01\u529b\u4ea6\u5df2\u53d1\u51fa\uff0c\u4f46\u89c1\u4eba\u5f71\u98de\u817e\uff0c\u60ca\u98d8\u6025\u5377\uff0c\u4e91\u821e\u9633\u53cc\u81c2\u4e00\u5c48\uff0c\u5de6\u53f3\u5f00\u5f13\uff0c\u6a2a\u80b1\u4e00\u649e\uff0c\u667a\u5f18 \u667a\u5e7f\u4e8c\u4eba\u5fc3\u5934\u4e00\u51db\uff0c\u8fd8\u672a\u5f04\u6e05\u695a\u662f\u4ec0\u4e48\u4e8b\u60c5\uff0c\u5df2\u88ab\u4ed6\u649e\u51fa\u4e00\u4e08\u5f00\u5916\uff0c\u4f46\u89c9\u597d\u4f3c\u4eba\u88ab\u8f7b\u8f7b\u63d0\u8d77 \u53c8\u8f7b\u8f7b\u653e\u4e0b\u4e00\u822c\uff0c\u8eab\u4e0a\u6beb\u65e0\u4f24\u635f\uff0c\u8fd9\u624d\u660e\u767d\u4e91\u821e\u9633\u662f\u7528\u6781\u4e0a\u4e58\u7684\u201c\u5de7\u6253\u201d\u529f\u592b\uff0c\u5c06\u81ea\u5df1\u9001\u51fa \u4e86\u9669\u5883\uff0c\u9003\u5f00\u4e86\u6bd5\u51cc\u98ce\u90a3\u4e24\u8bb0\u6bd2\u8fa3\u65e0\u6bd4\u7684\u6740\u624b\u3002 \u3000\u3000\u4e24\u8fb9\u52a8\u4f5c\u90fd\u8fc5\u4f3c\u7535\u5149\u77f3\u706b\uff0c\u5c31\u5728\u8fd9\u4e00\u5239\u90a3\uff0c\u6bd5\u51cc\u98ce\u5de6\u81c2\u7684\u5c16\u9aa8\u5df2\u63d2\u5230\u4e86\u667a\u5706\u7684\u80f8\u53e3\uff0c\u667a \u5706\u7684\u53cc\u638c\u8fd8\u672a\u6765\u5f97\u53ca\u6536\u56de\uff0c\u4e91\u821e\u9633\u7684\u4e00\u6307\u63b8\u529f\u4ea6\u5df2\u53d1\u51fa\uff0c\u4f46\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u675f\u5728\u6bd5\u51cc\u98ce \u5de6\u81c2\u7684\u94c1\u76ae\u548c\u7ea2\u7f15\u7adf\u88ab\u4e91\u821e\u9633\u4e00\u6307\u5212\u5f00\uff0c\u81c2\u4e0a\u51fa\u73b0\u4e00\u6761\u9f99\u5f62\u7684\u7eb9\u8eab\u82b1\u7eb9\uff0c\u4e91\u821e\u9633\u6014\u4e86\u4e00\u6014\uff0c \u597d\u4f3c\u7a81\u7136\u4e4b\u95f4\u89c1\u5230\u4e86\u4ec0\u4e48\u602a\u5f02\u7684\u4e8b\u7269\uff0c\u795e\u667a\u672a\u6e05\uff0c\u4e00\u80a1\u9634\u51b7\u4e4b\u6c14\u5df2\u76f4\u88ad\u5fc3\u5934\uff0c\u4e91\u821e\u9633\u518d\u4e5f\u652f \u6491\u4e0d\u4f4f\uff0c\u4e00\u7ede\u6454\u5012\uff0c\u4f46\u542c\u5230\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u6bd5\u51cc\u98ce\u90a3\u67af\u7626\u7684\u8eab\u8eaf\u98de\u51fa\u4e09\u4e08\u5f00\u5916\uff0c\u50cf\u65ad\u4e86\u7ebf\u7684 \u98ce\u7b5d\u4e00\u6837\u98d8\u4e0b\u5c71\u5761\uff0c\u201c\u563f\u563f\u201d\u7684\u4e24\u58f0\u51b7\u7b11\uff0c\u5934\u4e5f\u4e0d\u56de\uff0c\u970e\u773c\u4e4b\u95f4\uff0c\u5df2\u8d70\u5f97\u65e0\u8e2a\u65e0\u5f71\u3002 \u3000\u3000\u539f\u6765\u4e91\u821e\u9633\u4e0e\u6b66\u5f53\u4e94\u8001\u90fd\u56e0\u4e3a\u53d7\u4f24\u5728\u5148\uff0c\u51ed\u4e91\u821e\u9633\u7684\u4e00\u6307\u7985\u529f\u6216\u53ea\u51ed\u6b66\u5f53\u4e94\u8001\u7684\u201c\u4e94\u96f7 \u5929\u5fc3\u638c\u201d\u90fd\u4e0d\u8db3\u514b\u5236\u4ed6\uff0c\u4e24\u65b9\u8054\u624b\u5408\u6597\uff0c\u8fd9\u624d\u5c06\u4ed6\u51fb\u5012\uff0c\u6bd5\u51cc\u98ce\u5c31\u662f\u5728\u7740\u4e86\u4e91\u821e\u9633\u4e00\u6307\u540e\uff0c \u518d\u88ab\u4e94\u8001\u7684\u638c\u529b\u9707\u98de\u7684\u3002 \u3000\u3000\u4e91\u7d20\u7d20\u60ca\u9b42\u65b9\u5b9a\uff0c\u53ea\u542c\u667a\u5706\u957f\u8001\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u8d60\u4e39\u4e4b\u6069\u5df2\u62a5\uff0c\u54b1\u4eec\u540e\u4f1a\u6709\u671f\u3002\u201d\u4e91\u821e\u9633 \u76ee\u9001\u4e94\u8001\u4e0b\u5c71\uff0c\u4e0d\u53d1\u4e00\u8a00\uff0c\u8138\u8272\u9634\u6697\uff0c\u597d\u4f3c\u6b63\u5728\u6c89\u601d\u4e00\u4ef6\u7591\u96be\u83ab\u51b3\u7684\u4e8b\u60c5\u3002\u4e91\u7d20\u7d20\u9053\uff1a\u201c\u7239 \u7239\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u4e91\u821e\u9633\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4f60\u5916\u516c\u662f\u8fd9\u602a\u4eba\u6740\u7684\u3002\u201d\u987f\u4e86\u4e00\u987f\u53c8\u9053\uff1a\u201c\u6bd5\u51cc\u98ce\u88ab \u65ad\u81c2\uff0c\u524a\u819d\uff0c\u6bc1\u5bb9\uff0c\u8fd9\u90fd\u662f\u4f60\u5916\u516c\u5e72\u7684\u3002\u201d \u3000\u3000\u4e91\u7d20\u7d20\u6253\u4e86\u4e00\u4e2a\u5bd2\u5664\uff0c\u5979\u867d\u7136\u4ece\u6765\u6ca1\u6709\u89c1\u8fc7\u5916\u516c\uff0c\u4f46\u4ece\u6bcd\u4eb2\u53e3\u4e2d\u6240\u5f97\u7684\u5370\u8c61\uff0c\u5916\u516c\u4e43\u662f \u4e00\u4e2a\u521a\u6bc5\u800c\u53c8\u6148\u7965\u7684\u4e00\u4ee3\u5927\u4fa0\uff0c\u4ed6\u548c\u8fd9\u602a\u4eba\u6709\u4ec0\u4e48\u5927\u4ec7\uff0c\u600e\u7684\u4f1a\u5e72\u4e0b\u8fd9\u7b49\u72e0\u5fcd\u4e4b\u4e8b\uff1f \u3000\u3000\u4e91\u821e\u9633\u7eed\u8c0a\uff1a\u201c\u6bd5\u51cc\u98ce\u53f7\u79f0\u7389\u9762\u4e10\u4fa0\uff0c\u5374\u88ab\u4f60\u5916\u516c\u5f04\u6210\u8fd9\u7b49\u5947\u4e11\u65e0\u6bd4\u7684\u602a\u4eba\uff0c\u5f53\u65f6\u5fc5\u5b9a \u662f\u60b2\u6124\u4e4b\u6781\uff0c\u6240\u4ee5\u624d\u7528\u9634\u6bd2\u7684\u638c\u529b\u62a5\u590d\u3002\u4ed6\u81c2\u4e0a\u7684\u90a3\u6761\u98de\u9f99\u82b1\u7eb9\uff0c\u5c31\u662f\u4f60\u5916\u516c\u7684\u6807\u8bb0\u3002\u5176\u5b9e \u6211\u65e9\u5c31\u770b\u51fa\u6765\u4e86\uff0c\u5929\u4e0b\u9664\u4f60\u7684\u5916\u516c\uff0c\u518d\u6ca1\u6709\u65c1\u4eba\u6709\u90a3\u7b49\u7cbe\u5999\u7684\u5251\u6cd5\u3002\u53ea\u662f\u4ed6\u4e8c\u4eba\u4e00\u5411\u6ca1\u6709\u6765 \u5f80\uff0c\u600e\u7684\u5e73\u7a7a\u5f04\u51fa\u8fd9\u573a\u60e8\u7978\u3002\u771f\u771f\u53eb\u6211\u731c\u60f3\u4e0d\u900f\uff01\u201d \u3000\u3000\u4e91\u7d20\u7d20\u5fc3\u98a4\u624b\u9707\uff0c\u201d\u545b\u5577\u201d\u4e00\u58f0\uff0c\u90a3\u67c4\u6606\u543e\u5b9d\u5251\u8dcc\u843d\u5730\u4e0a\uff0c\u5979\u8fc7\u4e86\u5341\u516b\u5e74\u5e73\u9759\u65e0\u6ce2\u7684\u751f \u6d3b\uff0c\u60f3\u4e0d\u5230\u8fd9\u51e0\u65e5\u6765\u5374\u9047\u5230\u4e86\u4e00\u8fde\u4e32\u602a\u5f02\u7684\u4e8b\u60c5\uff0c\u4e00\u4ef6\u6bd4\u4e00\u4ef6\u4ee4\u4eba\u60ca\u5fc3\u52a8\u9b44\uff01\u6b64\u523b\u5b9d\u5251\u89e6\u5730 \u4e4b\u58f0\u53c8\u4ee4\u5979\u5fc3\u4e2d\u4e00\u8df3\uff0c\u9648\u7384\u673a\u521d\u89c1\u8fd9\u628a\u5251\u65f6\u7684\u60f6\u60d1\u795e\u60c5\uff0c\u90a3\u602a\u4eba\u5947\u7279\u7684\u8a00\u8bed\uff0c\u53c8\u4e00\u6b21\u7684\u5728\u5979 \u5fc3\u5934\u6d6e\u8d77\uff0c\u7136\u800c\u6bd4\u8d77\u5176\u4ed6\u7684\u602a\u5f02\u7684\u4e8b\u60c5\uff0c\u8fd9\u628a\u5b9d\u5251\u4e4b\u8c1c\u5374\u53c8\u4f3c\u4e4e\u5e76\u4e0d\u600e\u4e48\u91cd\u8981\u4e86\u3002 \u3000\u3000\u4f46\u5979\u7239\u7239\u7684\u4e00\u53e5\u8bf4\u8bdd\u53c8\u628a\u5979\u7684\u5fc3\u5f26\u62c9\u5f97\u7ef7\u7d27\u8d77\u6765\uff0c\u5979\u7239\u7239\u6307\u7740\u90a3\u628a\u5b9d\u5251\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u7d20 \u7d20\uff0c\u4f60\u53ef\u8bb0\u5f97\u4eca\u65e9\u6211\u5bf9\u4f60\u6240\u8bf4\u6211\u66fe\u5e72\u8fc7\u4e00\u4ef6\u6bd5\u751f\u96be\u5fd8\u7684\u7f6a\u5b7d\uff1f\u201d\u4e91\u7d20\u7d20\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8bb0 \u5f97\u3002\u201d\u4e91\u821e\u9633\u9053\uff1a\u201c\u8fd9\u4ef6\u7f6a\u5b7d\u5c31\u662f\u56e0\u8fd9\u628a\u5b9d\u5251\u800c\u8d77\uff0c\u55ef\uff0c\u6bd5\u51cc\u98ce\u628a\u6211\u7684\u7f6a\u540d\u8fd8\u662f\u8bf4\u5f97\u592a\u8f7b\uff0c \u4ed6\u8bf4\u6211\u8fd9\u628a\u5b9d\u5251\u4e43\u662f\u5077\u6765\u7684\uff0c\u5176\u5b9e\u6bd4\u5077\u6765\u7684\u8fd8\u53ef\u6015\u5f97\u591a\uff0c\u6211\uff0c\u6211\uff0c\u6211\uff0c\u6211\u6740\u4e86\u8fd9\u628a\u5b9d\u5251\u7684\u4e3b \u4eba\uff0c\u5979\u3001\u5979\u3001\u5979\uff0c\u2026\u2026\u5979\u662f\u6211\u4e00\u751f\u4e2d\u5bf9\u6211\u6700\u597d\u7684\u4eba\uff01\u201d \u3000\u3000\u4e91\u7d20\u7d20\u5c16\u53eb\u4e00\u58f0\uff0c\u4f46\u89c1\u5979\u7236\u4eb2\u7684\u989d\u89d2\u4e0a\u53c8\u6c81\u51fa\u4e86\u6c57\u73e0\uff0c\u6ee1\u8138\u75db\u82e6\u7684\u795e\u8272\uff0c\u7b80\u76f4\u8d85\u8fc7\u4e0e\u90a3 \u602a\u4eba\u640f\u6597\u4e4b\u65f6\uff01\u4e91\u7d20\u7d20\u5fc3\u4e2d\u65e2\u662f\u9a87\u4ff1\uff0c\u53c8\u662f\u601c\u60af\uff0c\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u5c31\u628a\u8fd9\u4ef6\u4e8b\u60c5\u8bf4\u51fa \u6765\u5427\uff0c\u514d\u5f97\u7559\u5728\u5fc3\u4e2d\u6298\u78e8\u81ea\u5df1\u3002\u201d \u3000\u3000\u4e91\u821e\u9633\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\uff0c\u6211\u662f\u8981\u5411\u4f60\u8bf4\u2026\u2026\u201d\u58f0\u97f3\u5636\u54d1\uff0c\u8d8a\u8bf4\u8d8a\u89c1\u5fae\u5f31\uff0c\u4e91\u7d20\u7d20\u638f\u51fa\u4e00 \u5f20\u624b\u7ee2\uff0c\u66ff\u5979\u7239\u7239\u8bd5\u6c57\uff0c\u4f46\u89c9\u90a3\u6c57\u73e0\u51b0\u51b7\uff0c\u89e6\u624b\u751f\u51c9\uff0c\u4e91\u7d20\u7d20\u5fc3\u5934\u9707\u64bc\uff0c\u4e91\u821e\u9633\u53f9\u4e86\u4e00\u53e3\u6c14 \u9053\uff1a\u201c\u8fd9\u6545\u4e8b\u592a\u957f\uff0c\u53ea\u6015\u6211\u8bf4\u4e0d\u5b8c\u4e86\u3002\u201d \u3000\u3000\u4e91\u7d20\u7d20\u9053\uff1a\u201c\u7239\uff0c\u4f60\u6b47\u4e00\u4f1a\u513f\uff0c\u4f60\u9759\u5750\u8fd0\u529f\uff0c\u6211\u66ff\u4f60\u9632\u62a4\u3002\u201d\u4e91\u821e\u9633\u9053\uff1a\u201c\u4e0d\uff0c\u4f60\u66ff\u6211 \u5c06\u4e5d\u5929\u743c\u82b1\u56de\u9633\u9152\u62ff\u6765\uff01\u6211\u95f7\u5728\u5fc3\u4e2d\u4e8c\u5341\u5e74\uff0c\u65e9\u5c31\u60f3\u8bf4\uff0c\u4e0d\u613f\u518d\u7b49\u4e09\u5929\u4e09\u591c\u4e86\u3002\u201d\u4e91\u7d20\u7d20\u542c \u5979\u7239\u7239\u8fd9\u4e48\u4e00\u8bf4\uff0c\u8fd9\u624d\u77e5\u9053\u5979\u7239\u7239\u6240\u53d7\u7684\u4f24\uff0c\u7adf\u6bd4\u5979\u610f\u60f3\u7684\u8fd8\u8981\u4e25\u91cd\uff0c\u867d\u7136\u6709\u90a3\u5c11\u9633\u5c0f\u8fd8 \u4e39\uff0c\u8fd8\u662f\u652f\u6301\u4e0d\u4f4f\uff0c\u82e5\u8981\u9759\u5750\u8fd0\u529f\uff0c\u975e\u5f97\u4e09\u65e5\u4e09\u591c\u4e0d\u80fd\u6062\u590d\uff0c\u6240\u4ee5\u624d\u8981\u501f\u52a9\u4e5d\u5929\u743c\u82b1\u56de\u9633\u9152 \u4e4b\u529b\u3002 \u3000\u3000\u4e91\u7d20\u7d20\u9053\uff1a\u201c\u6211\u53bb\u4e86\uff0c\u4f60\u4e00\u4e2a\u4eba\u5728\u8fd9\u513f\uff0c\u6211\uff0c\u6211\u653e\u5fc3\u4e0d\u4e0b\u3002\u201d\u4e91\u821e\u9633\u9053\uff1a\u201c\u4e0d\u59a8\uff0c\u4f60\u5feb \u53bb\u5feb\u56de\uff0c\u62ff\u5230\u77f3\u5ba4\u4e4b\u4e2d\u7ed9\u6211\u3002\u4e0d\u4f1a\u518d\u6709\u7b2c\u4e8c\u4e2a\u6bd5\u51cc\u98ce\u4e86\u3002\u201d\u4e91\u7d20\u7d20\u53ea\u5f97\u542c\u5979\u7239\u7239\u7684\u5429\u5490\uff0c\u8ddf \u56de\u5bb6\u4e2d\u3002\u4e00\u8def\u4e0a\u5fc3\u5934\u60f6\u60d1\u4e0d\u5df2\uff0c\u4f46\u89c9\u5468\u56f4\u4e4b\u7269\u90fd\u5145\u6ee1\u4e86\u795e\u79d8\uff0c\u8fde\u81ea\u5df1\u7684\u7236\u4eb2\uff0c\u8fde\u8fd9\u628a\u968f\u8eab\u7684 \u5b9d\u5251\uff0c\u90fd\u53d8\u6210\u4e86\u4e00\u4e2a\u4e2a\u4ee4\u4eba\u4e0d\u6562\u731c\u6d4b\u7684\u8c1c\u56e2\u3002 \u3000\u3000\u56de\u5230\u5bb6\u4e2d\uff0c\u4f46\u89c1\u5ead\u9662\u91cc\u6b8b\u679d\u8d25\u53f6\uff0c\u4e00\u73af\u9ec4\u571f\uff0c\u4e00\u7247\u8352\u829c\uff0c\u4e91\u7d20\u7d20\u5ffd\u5730\u60f3\u8d77\u4e86\u9648\u7384\u673a\u6765\uff0c \u521a\u624d\u4e00\u8fde\u4e32\u7a81\u53d1\u7684\u98ce\u6ce2\uff0c\u5148\u662f\u6b66\u5f53\u4e94\u8001\uff0c\u540e\u662f\u90a3\u4e2a\u602a\u4eba\uff0c\u4ee4\u5979\u5fc3\u4e2d\u65e0\u7247\u523b\u95f2\u6687\uff0c\u800c\u4eca\u98ce\u6ce2\u6682 \u606f\uff0c\u7b2c\u4e00\u4e2a\u4eca\u5979\u60f3\u8d77\u7684\u5f53\u7136\u662f\u81ea\u5df1\u66fe\u628a\u5fc3\u8eab\u4ea4\u6258\u7ed9\u4ed6\u7684\u4eba\uff0c\u7136\u800c\u9648\u7384\u673a\u5230\u54ea\u513f\u53bb\u4e86\u5462\uff1f\u4e91\u7d20 \u7d20\u4e00\u8fde\u53eb\u4e86\u51e0\u58f0\uff0c\u7a7a\u5ead\u5bc2\u5bde\uff0c\u53ea\u6709\u81ea\u5df1\u7684\u56de\u58f0\uff0c\u9648\u7384\u673a\u7adf\u7136\u4e0d\u77e5\u5230\u54ea\u513f\u53bb\u4e86\uff01 \u3000\u3000\u9648\u7384\u673a\u5230\u54ea\u91cc\u53bb\u4e86\u5462\uff1f\u4e91\u7d20\u7d20\u7aed\u529b\u9547\u9759\u4e0b\u6765\uff0c\u56de\u601d\u524d\u4e8b\uff0c\u60f3\u8d77\u90a3\u662f\u7239\u7239\u8981\u548c\u5979\u5355\u72ec\u8bf4\u8bdd \u4e4b\u65f6\uff0c\u5979\u8868\u793a\u53eb\u4ed6\u56de\u907f\u7684\uff0c\u96be\u9053\u4ed6\u56e0\u6b64\u607c\u602a\u4e86\u81ea\u5df1\uff1f\u60f3\u8d77\u81ea\u5df1\u4e0e\u9648\u7384\u673a\u867d\u7136\u76f8\u8bc6\u4e4b\u65e5\u65e0\u591a\uff0c \u4f46\u5374\u662f\u5f7c\u6b64\u76f8\u77e5\uff0c\u5fc3\u5fc3\u76f8\u5370\uff0c\u7eb5\u8bb8\u4ed6\u4e0e\u7239\u7239\u6709\u4ec7\uff0c\u4e5f\u65ad\u65ad\u4e0d\u4f1a\u607c\u602a\u81ea\u5df1\u3002\u90a3\u4e48\uff0c\u4ed6\u4e3a\u4ec0\u4e48\u4e0d \u7559\u5728\u5bb6\u4e2d\u7b49\u5979\uff0c\u82e5\u8bf4\u4ed6\u8d2a\u770b\u70ed\u95f9\uff0c\u7239\u7239\u540c\u6b66\u5f53\u4e94\u8001\u5230\u77f3\u5ba4\u4e2d\u53bb\u770b\u4e0a\u5b98\u5929\u91ce\uff0c\u5176\u540e\u53c8\u5728\u5c71\u524d\u6bd4 \u6b66\uff0c\u8fd9\u6837\u96be\u5ea6\u7684\u573a\u9762\uff0c\u53c8\u4e3a\u4ec0\u4e48\u59cb\u7ec8\u4e0d\u89c1\u4ed6\u51fa\u73b0\uff1f \u3000\u3000\u9661\u7136\u95f4\u4e91\u7d20\u7d20\u60f3\u8d77\u4e86\u9648\u7384\u673a\u4e34\u8d70\u4e4b\u65f6\u90a3\u79cd\u5947\u5f02\u9707\u6050\u7684\u773c\u5149\uff0c\u4e00\u8e0f\u5165\u4e66\u623f\u4e4b\u65f6\u90a3\u5fc3\u795e\u5fe1\u5fe1 \u5931\u9b42\u843d\u9b44\u7684\u6a21\u6837\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u9707\u51db\u3002\u81ea\u5df1\u4f4f\u4e86\u5341\u516b\u5e74\u7684\u623f\u5b50\u91cc\uff0c\u8fd9\u65f6\u7a7a\u8361\u8361\u51b7\u6e05\u6e05\u7684\u6709\u5982\u9b3c \u57df\uff0c\u6bcd\u4eb2\u8d70\u4e86\u3002\u7239\u7239\u7559\u5728\u77f3\u5ba4\u4e4b\u4e2d\uff0c\u4ed6\u4f24\u597d\u4e4b\u540e\u672a\u5fc5\u80af\u518d\u56de\u5bb6\u4e2d\u4e86\uff0c\u73b0\u5728\u9648\u7384\u673a\u4e5f\u4e0d\u89c1\u4e86\u3002 \u4e91\u7d20\u7d20\u53ea\u611f\u5230\u4e00\u9635\u9635\u5bc2\u5bde\u4e4b\u611f\u88ad\u6765\uff0c\u4e0e\u81ea\u5df1\u81f3\u4eb2\u81f3\u8fd1\u7684\u4eba\u7adf\u7136\u90fd\u50cf\u8c1c\u4e00\u822c\u7684\u96be\u4ee5\u7d22\u89e3\uff01\u5373\u662f \u8bf4\u9648\u7384\u673a\u5427\uff0c\u867d\u7136\u5979\u89c9\u5f97\u5f7c\u6b64\u5fc3\u7075\u76f8\u901a\uff0c\u4f46\u9648\u7384\u673a\u90a3\u79cd\u5947\u5f02\u7684\u795e\u60c5\uff0c\u5979\u4ecd\u662f\u83ab\u540d\u6240\u4ee5\uff0c\u518d\u8bf4 \u5bf9\u4ed6\u7684\u8eab\u4e16\u6765\u5386\uff0c\u5979\u53c8\u4f55\u5c1d\u660e\u767d\uff1f\u8c1c\uff0c\u8c1c\uff0c\u4e00\u5207\u90fd\u662f\u96be\u89e3\u4e4b\u8c1c\uff01 \u3000\u3000\u4e91\u7d20\u7d20\u53d6\u4e86\u7236\u4eb2\u51fa\u95e8\u4e4b\u65f6\u5e38\u5e38\u7528\u6765\u76db\u9152\u7684\u7ea2\u6f06\u5927\u846b\u82a6\uff0c\u5012\u6ee1\u4e86\u4e00\u846b\u82a6\u7684\u4e5d\u5929\u743c\u82b1\u56de\u9633 \u9152\uff0c\u4e0d\u7531\u81ea\u5df1\u7684\u53c8\u60f3\u8d77\u5979\u4e3a\u9648\u7384\u673a\u7597\u4f24\uff0c\u8bf1\u4ed6\u559d\u9152\uff0c\u6bb7\u52e4\u670d\u4f8d\u4ed6\u7684\u60c5\u666f\uff0c\u5fc3\u5934\u4e00\u9635\u9178\u695a\uff0c\u6025 \u5fd9\u68d2\u8d77\u846b\u82a6\uff0c\u5306\u5306\u79bb\u5f00\u5bb6\u95e8\uff0c\u8d76\u5f80\u77f3\u5ba4\u3002 \u3000\u3000\u5915\u9633\u897f\u843d\uff0c\u77f3\u5ba4\u4e2d\u5149\u7ebf\u9eef\u6de1\uff0c\u4e91\u7d20\u7d20\u53eb\u4e86\u4e00\u58f0\u201c\u7239\u7239\u201d\uff0c\u4e0d\u89c1\u56de\u7b54\uff0c\u5fc3\u4e2d\u53c8\u662f\u4e00\u60ca\uff0c\u76f4 \u5230\u6478\u5165\u77f3\u5ba4\uff0c\u5728\u6700\u540e\u53d1\u73b0\u7236\u4eb2\u9762\u91cc\u800c\u5750\uff0c\u6b63\u5728\u8fd0\u529f\uff0c\u8fd9\u624d\u653e\u4e0b\u4e86\u5fc3\u3002\u4e91\u7d20\u7d20\u63cd\u7740\u846b\u82a6\uff0c\u968f\u4f8d \u5728\u4fa7\uff0c\u8fc7\u4e86\u597d\u4e00\u4f1a\uff0c\u53ea\u89c1\u7236\u4eb2\u7f13\u7f13\u62ac\u8d77\u4e86\u5934\uff0c\u4f38\u624b\u5411\u81ea\u5df1\u4e00\u6307\uff0c\u4e91\u7d20\u7d20\u6025\u5fd9\u5c06\u846b\u82a6\u9012\u8fc7\uff0c\u4e91 \u821e\u9633\u559d\u4e86\u4e00\u53e3\u9152\u540e\uff0c\u5589\u5934\u54af\u54af\u4f5c\u54cd\uff0c\u53c8\u8fc7\u4e86\u4e00\u4f1a\uff0c\u53d1\u51fa\u4f4e\u5fae\u5e26\u7740\u9707\u6296\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u7d20\u7d20\uff0c \u4f60\u5750\u4e0b\u6765\uff0c\u542c\u4f60\u7239\u7239\u7684\u5fcf\u6094\uff01\u201d \u3000\u3000\u4e91\u7d20\u7d20\u4f46\u89c9\u4e0d\u5bd2\u800c\u6817\uff0c\u5979\u6e34\u671b\u77e5\u9053\u7236\u4eb2\u7684\u79d8\u5bc6\u3002\u7236\u4eb2\u5e72\u4e0b\u4ec0\u4e48\u7f6a\u5b7d\u554a\uff0c\u4ee4\u4ed6\u5fc3\u7075\u5982\u6b64\u4e0d \u5b89\uff1f\u4e91\u7d20\u7d20\u6b63\u5728\u7aed\u529b\u9547\u5b9a\u5fc3\u795e\uff0c\u5ffd\u542c\u5230\u8fdc\u8fdc\u7684\u6797\u5b50\u91cc\u9690\u9690\u98d8\u6765\u5c11\u5973\u7684\u6b4c\u58f0\uff1a\u201c\u5929\u4e0a\u7684\u6708\u4eae\u6b63 \u8d76\u592a\u9633\uff0c\u5730\u4e0b\u7684\u59d1\u5a18\u8d76\u60c5\u90ce\u3002\u592a\u9633\u4e1c\u5347\uff0c\u6708\u6bbf\u5ae6\u5ae3\u5a25\u5f92\u4e4f\u60b2\u4f24\u2026\u2026\u201d\u6b4c\u58f0\u65f6\u6b47\u65f6\u4f5c\uff0c\u8fd8\u4f9d\u7a00 \u542c\u5f97\u5728\u6b4c\u58f0\u4e2d\u6742\u7740\u90a3\u5c11\u5973\u547c\u5524\u7740\u201c\u7384\u673a\u201d\u7684\u540d\u5b57\uff01 \u3000\u3000\u8fd9\u662f\u8c01\uff0c\u662f\u8c01\u5bf9\u9648\u7384\u673a\u90a3\u7b49\u6df1\u60c5\uff1f\u662f\u4e0a\u5b98\u5929\u91ce\u6240\u8bf4\u7684\u90a3\u4e2a\u5c11\u5973\u5417\uff1f\u5ffd\u542c\u5f97\u7239\u7239\u6c89\u58f0\u8bf4 \u9053\uff1a\u201c\u7d20\u7d20\uff0c\u4f60\u60f3\u4ec0\u4e48\uff1f\u9760\u8fd1\u4e00\u4e9b\uff0c\u4f60\u542c\u6211\u8bf4\uff0c\u4f60\u5bb3\u6015\u5417\uff1f\u54e6\uff0c\u4f60\u5bb3\u6015\u5440\uff01\u201d\u4e91\u821e\u9633\u5f00\u59cb\u8bf4 \u4ed6\u4e8c\u5341\u5e74\u524d\u6240\u5e72\u7684\u90a3\u6869\u7f6a\u5b7d\u3002\u90a3\u65f6\u5915\u9633\u5df2\u7ecf\u843d\u5c71\uff0c\u77f3\u5ba4\u91cc\u4e00\u7247\u6f06\u9ed1\uff01 \u3000\u3000------------- \u3000\u3000\u91cd\u5e86\u96ea\u513f \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u8fd8\u5251\u5947\u60c5\u5f55\u300b\u2014\u2014\u7b2c\u4e5d\u56de\u3000\u8840\u916c\u77e5\u5df1 \u6881\u7fbd\u751f\u300a\u8fd8\u5251\u5947\u60c5\u5f55\u300b \u7b2c\u4e5d\u56de\u3000\u8840\u916c\u77e5\u5df1 \u3000\u3000\u9648\u7384\u673a\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u4ed6\u4e5f\u6b63\u50cf\u4e91\u7d20\u7d20\u4e00\u6837\uff0c\u5728\u8fd9\u77ed\u77ed\u7684\u51e0\u4e2a\u65f6\u8fb0\uff0c\u63a5\u8fde\u9047\u5230\u4e86\u8bb8\u591a\u610f\u5916 \u4e4b\u4e8b\u3002 \u3000\u3000\u4ed6\u6628\u665a\u591c\u5165\u4e91\u5bb6\uff0c\u62fc\u7740\u8eab\u5192\u5947\u9669\uff0c\u65e0\u975e\u662f\u60f3\u89c1\u4e00\u89c1\u5fc3\u76ee\u4e2d\u4eba\uff0c\u679c\u7136\u5929\u4ece\u4eba\u613f\uff0c\u610f\u4e2d\u4eba\u4e0d \u4f46\u89c1\u4e86\uff0c\u800c\u4e14\u82b3\u5fc3\u76f8\u8bb8\uff0c\u871c\u610f\u7f20\u7ef5\uff0c\u4e0d\u6599\u4e91\u821e\u9633\u5374\u7a81\u7136\u56de\u6765\uff0c\u7236\u5973\u76f8\u9022\uff0c\u9690\u60c5\u5f85\u8bc9\uff0c\u4e91\u7d20\u7d20 \u793a\u610f\u53eb\u4ed6\u56de\u907f\uff0c\u4ee4\u5f97\u4ed6\u5fc3\u4e2d\u751a\u662f\u4e0d\u5b89\uff0c\u601d\u6f6e\u7eb7\u8d77\uff1a\u4e91\u821e\u9633\u613f\u610f\u5c06\u5973\u513f\u7ed9\u4ed6\u5417\uff1f\u81ea\u5df1\u53d7\u4e86\u5e08\u53cb \u91cd\u6258\u8981\u884c\u523a\u4e91\u821e\u9633\uff0c\u7eb5\u8bb8\u4e91\u7d20\u7d20\u5bf9\u81ea\u5df1\u503e\u5fc3\uff0c\u7fc1\u5a7f\u4e4b\u95f4\u53c8\u600e\u80fd\u76f8\u5904\uff1f\u518d\u8bf4\u7236\u5973\u4e4b\u60c5\u7ec8\u7a76\u96be \u5fd8\uff0c\u4e91\u821e\u9633\u53ea\u6709\u8fd9\u4e2a\u5973\u513f\uff0c\u82e5\u7136\u81ea\u5df1\u4e0d\u987e\u4e00\u5207\u5c06\u4e91\u7d20\u7d20\u5e26\u8d70\uff0c\u8fd9\u5c82\u4e0d\u662f\u5c06\u4ed6\u4eec\u7236\u5973\u4e4b\u60c5\u79bb \u95f4\uff0c\u600e\u80fd\u4fdd\u5f97\u4f4f\u4e91\u7d20\u7d20\u4ed6\u65e5\u50cf\u5979\u6bcd\u4eb2\u4e00\u6837\u57cb\u6028\u8d77\u81ea\u5df1\u7684\u4e08\u592b\uff1f \u3000\u3000\u9648\u7384\u673a\u7684\u6027\u683c\u6b63\u597d\u4e0e\u4e0a\u5b98\u5929\u91ce\u76f8\u53cd\uff0c\u4e0a\u5bab\u5929\u91ce\u7231\u6068\u8d8b\u4e8e\u6781\u7aef\uff0c\u53ef\u4ee5\u4e0d\u987e\u4e00\u5207\uff1b\u9648\u7384\u673a\u5219 \u51b7\u9759\u5f97\u591a\uff0c\u6b63\u56e0\u4ed6\u5bf9\u4e91\u7d20\u7d20\u7231\u5f97\u592a\u6df1\uff0c\u6240\u4ee5\u4e5f\u4e3a\u5979\u60f3\u5f97\u5468\u5bc6\uff0c\u60f3\u5230\u4ee4\u5979\u7236\u5973\u751f\u5206\u4e4b\u540e\uff0c\u4e91\u7d20 \u7d20\u8fd9\u4e00\u751f\u662f\u5426\u80fd\u591f\u59cb\u7ec8\u5e78\u798f\u6b22\u6109\uff0c\u5fc3\u4e2d\u6b8a\u65e0\u628a\u63e1\uff0c\u5c24\u5176\u60f3\u5230\u5979\u6bcd\u4eb2\u90a3\u526f\u5e7d\u6028\u7684\u795e\u60c5\uff0c\u66f4\u662f\u4e0d \u7531\u81ea\u5df1\u7684\u6253\u4e86\u4e00\u4e2a\u5bd2\u5664\uff0c\u5fc3\u9053\uff1a\u201c\u82e5\u7136\u7d20\u7d20\u4ed6\u65e5\u6709\u534a\u53e5\u6028\u8a00\uff0c\u6211\u8fd9\u4e00\u751f\u5c31\u6127\u6094\u4e0d\u5c3d\u3002\u201d\u7136\u800c \u82e5\u6559\u4ed6\u5c31\u6b64\u820d\u4e91\u7d20\u7d20\uff0c\u90a3\u66f4\u662f\u4e0d\u80fd\u60f3\u50cf\u4e4b\u4e8b\u3002 \u3000\u3000\u9648\u7384\u673a\u6e34\u671b\u4e91\u7d20\u7d20\u65e9\u70b9\u51fa\u6765\uff0c\u4f46\u4ed6\u4eec\u4e24\u7236\u5973\u7684\u8bdd\u5374\u597d\u50cf\u8c08\u4e4b\u4e0d\u5c3d\uff0c\u5176\u5b9e\u4e5f\u6ca1\u6709\u7b49\u5f97\u591a \u4e45\uff0c\u4f46\u4e00\u5206\u4e00\u523b\uff0c\u5728\u9648\u7384\u673a\u90fd\u611f\u89c9\u5f97\u50cf\u4e00\u6708\u4e00\u5e74\uff0c\u4ed6\u8f7b\u8f7b\u7684\u5f00\u4e86\u89d2\u95e8\uff1b\u8d70\u51fa\u9662\u5b50\uff1b\u5fc3\u4e2d\u60f3 \u9053\uff1a\u201c\u597d\uff0c\u6211\u5c31\u50cf\u4e00\u4e2a\u5f85\u51b3\u7684\u56da\u5f92\uff0c\u7b49\u5f85\u7d20\u7d20\u7684\u5ba3\u5224\u5427\u3002\u201d\u4ed6\u53ea\u9053\u4e91\u821e\u9633\u662f\u548c\u4ed6\u5973\u513f\u8c08\u8bba\u4ed6 \u7684\u5a5a\u4e8b\uff0c\u54ea\u77e5\u4e91\u821e\u9633\u5374\u662f\u5411\u5973\u513f\u4edf\u6094\u4ed6\u5e73\u751f\u7684\u7f6a\u5b7d\u3002 \u3000\u3000\u6b63\u81ea\u7126\u8e81\u4e0d\u5b89\uff0c\u5ffd\u542c\u5f97\u6797\u5b50\u91cc\u9690\u7ea6\u4f20\u6765\u4e00\u58f0\u5c16\u53eb\uff0c\u201c\u8fd9\u662f\u4e0a\u5b98\u5929\u91ce\uff01\u4ed6\u9047\u5230\u4e86\u4ec0\u4e48\u5947 \u9669\uff1f\u201d\u9648\u7384\u673a\u65e0\u6687\u601d\u7d22\uff0c\u4e0a\u5b98\u5929\u91ce\u66fe\u5192\u4e86\u6027\u547d\u4e4b\u9669\u8981\u6765\u6551\u4ed6\uff0c\u4ed6\u542c\u5230\u4e0a\u5b98\u5929\u91ce\u7684\u53eb\u58f0\uff0c\u53c8\u600e \u80fd\u8e0c\u8e87\u4e0d\u53bb\uff1f \u3000\u3000\u4ed6\u8ffd\u5165\u4e86\u5bc6\u6797\u4e4b\u4e2d\u3002\u53ea\u542c\u5f97\u94c1\u6756\u89e6\u5730\u7684\u53ee\u53ee\u4e4b\u58f0\uff0c\u58f0\u97f3\u5c31\u5728\u524d\u9762\uff0c\u7136\u800c\u4efb\u4ed6\u5c55\u5f00\u516b\u6b65\u8d76 \u8749\u7684\u8f7b\u529f\uff0c\u5374\u603b\u662f\u8ffd\u4e4b\u4e0d\u4e0a\uff01\u8fc7\u4e86\u4e00\u4f1a\uff0c\u90a3\u91cc\u53c8\u4f20\u6765\u4e86\u4e00\u58f0\u5c16\u53eb\uff0c\u8fd9\u56de\u542c\u5f97\u66f4\u6e05\u695a\u4e86\uff0c\u7edd\u5bf9 \u662f\u5929\u91ce\u7684\u58f0\u97f3\uff0c\u800c\u4e14\u58f0\u97f3\u4e2d\u5145\u6ee1\u9a87\u60e7\u3002\u5929\u4e0d\u6015\u5730\u4e0d\u6015\u7684\u4e0a\u5b98\u5929\u91ce\uff0c\u5c45\u7136\u4f1a\u53d1\u51fa\u8fd9\u79cd\u9a87\u60e7\u7684\u58f0 \u97f3\uff0c\u771f\u771f\u4ee4\u4eba\u96be\u4ee5\u76f8\u4fe1\uff01\u7136\u800c\u8fd9\u5374\u5b9e\u5b9e\u5728\u5728\u662f\u4e0a\u5bab\u5929\u91ce\u7684\u58f0\u97f3\uff01 \u3000\u3000\u9648\u7384\u673a\u7a0d\u4e3a\u4e00\u6162\uff0c\u90a3\u53ee\u53ee\u4e4b\u58f0\u6e10\u8fdc\u6e10\u9690\uff0c\u662f\u4ec0\u4e48\u65b9\u5411\u4e5f\u5206\u8fa8\u4e0d\u51fa\u4e86\u3002\u5c31\u5728\u8fd9\u4e2a\u65f6\u95f4\uff0c\u6797 \u5b50\u91cc\u4f20\u6765\u5c11\u5973\u7684\u6b4c\u58f0\uff1a\u201c\u5929\u4e0a\u7684\u6708\u4eae\u8d76\u592a\u9633\uff0c\u5730\u4e0b\u7684\u59d1\u5a18\u8d76\u60c5\u90ce......\u201d\u8fd9\u662f\u8427\u97f5\u5170\u7684\u6b4c \u58f0\u3002\u9648\u7384\u673a\u53c8\u5fd9\u5411\u6b4c\u58f0\u76f8\u53cd\u7684\u65b9\u5411\u9003\u8dd1\uff0c\u8dd1\u4e86\u4e00\u4f1a\uff0c\u6b4c\u58f0\u4e5f\u542c\u4e0d\u89c1\u4e86\u3002\u201d\u9648\u7384\u673a\u672c\u6ca1\u7761\uff0c\u8fde \u9047\u5947\u9669\uff0c\u8fd9\u65f6\u75b2\u5026\u4e0d\u582a\uff0c\u6905\u5728\u4e00\u68f5\u6811\u4e0a\u7a0d\u6b47\uff0c\u5ffd\u7136\u542c\u5f97\u79bb\u8eab\u51e0\u4e08\u4e4b\u5916\u6709\u8c08\u8bdd\u7684\u58f0\u97f3\uff01 \u3000\u3000\u53ea\u542c\u5f97\u4e00\u9635\u6781\u5176\u523a\u8033\u7684\u7b11\u58f0\uff0c\u9707\u5f97\u8033\u9f13\u55e1\u55e1\u4f5c\u54cd\uff0c\u7b11\u58f0\u8fc7\u540e\uff0c\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u4e0a\u5b98\u5929\u91ce\uff0c \u4f60\u7ed9\u6211\u8fd9\u8001\u602a\u7269\u5413\u7740\u4e86\u5427\uff1f\u201d\u9648\u7384\u673a\u5728\u5927\u6811\u540e\u9762\u5077\u77a7\u51fa\u53bb\uff0c\u8fd9\u4e00\u77a7\u76f4\u5413\u5f97\u6bdb\u9aa8\u609a\u7136\uff0c\u4f46\u89c1\u4e00 \u4e2a\u76f8\u8c8c\u5947\u4e11\u7684\u602a\u4eba\uff0c\u8138\u4e0a\u4f24\u75d5\u7eb5\u6a2a\u4ea4\u9519\uff0c\u800c\u4e14\u53ea\u6709\u4e00\u6761\u624b\u81c2\uff0c\u5de6\u8db3\u53c8\u8ddb\uff0c\u6b63\u4ee5\u94c1\u6756\u652f\u5730\uff0c\u5411 \u7740\u4e0a\u5b98\u5929\u91ce\u8bf4\u8bdd\u3002 \u3000\u3000\u9648\u7384\u673a\u7528\u4e86\u6700\u5927\u7684\u5b9a\u529b\u624d\u9547\u5f97\u4f4f\u5fc3\u795e\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4e0a\u5b98\u5929\u91ce\u521a\u624d\u9a87\u53eb\u51fa\u58f0\u3002\u4ed6 \u600e\u7684\u843d\u5728\u8fd9\u4e2a\u602a\u7269\u624b\u4e2d\uff1f\u201d\u6b63\u5f85\u638f\u51fa\u6697\u5668\uff0c\u53ea\u542c\u5f97\u4e0a\u5b98\u5929\u91ce\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u8001\u524d\u8f88\u6551\u6211\u51fa\u6765\uff0c \u53ea\u662f\uff0c\u53ea\u662f\u2014\u2014\u201d\u9648\u7384\u673a\u6014\u4e86\u4e00\u6014\uff0c\u6599\u4e0d\u5230\u8fd9\u8001\u602a\u7269\u7adf\u662f\u6551\u4e0a\u5b98\u5929\u91ce\u7684\u6069\u4eba\uff0c\u4f38\u5165\u6697\u5668\u56ca\u4e2d \u7684\u624b\u53c8\u7f29\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8fd9\u8001\u602a\u7269\u6b63\u662f\u6bd5\u51cc\u98ce\uff0c\u4e0a\u5b98\u5929\u91ce\u5728\u77f3\u5ba4\u4e4b\u4e2d\u77a7\u4e0d\u6e05\u4ed6\u7684\u9762\u8c8c\uff0c\u51fa\u4e86\u77f3\u6d1e\u4e4b\u540e\uff0c\u5728\u6668\u5149\u8e51 \u5fae\u4e4b\u4e2d\u9aa4\u7136\u89c1\u7740\u8fd9\u526f\u5947\u4e11\u7684\u989c\u5bb9\uff0c\u786e\u662f\u5fc3\u4e2d\u60ca\u60b8\uff0c\u4f46\u8bf4\u4e5f\u5947\u602a\uff0c\u76f8\u5bf9\u7a0d\u4e45\uff0c\u53cd\u800c\u89c9\u5f97\u5728\u6bd5\u51cc \u98ce\u5947\u602a\u65e0\u6bd4\u7684\u8138\u4e0a\uff0c\u9690\u9690\u9732\u51fa\u4e00\u79cd\u4ee4\u4eba\u611f\u5230\u6e29\u6696\u7684\u6148\u7965\uff0c\u4e0a\u5b98\u5929\u91ce\u53cc\u4eb2\u65e9\u4e27\uff0c\u81ea\u5c0f\u4fbf\u662f\u5b64 \u513f\uff0c\u957f\u5927\u4e4b\u540e\uff0c\u82e6\u604b\u8427\u97f5\u5170\uff0c\u5374\u53c8\u906d\u5979\u51b7\u6de1\uff0c\u4f46\u89c9\u4e00\u751f\u4e4b\u4e2d\uff0c\u4ece\u65e0\u4e00\u4eba\u50cf\u8fd9\u4e2a\u201c\u602a\u7269\u201d\u4e00\u6837 \u7684\u5173\u5fc3\u4ed6\uff0c\u6551\u4e86\u4ed6\u8fd8\u6015\u5413\u574f\u4e86\u4ed6\u3002\u201d \u3000\u3000\u6bd5\u51cc\u98ce\u5fae\u5fae\u4e00\u7b11\uff0c\u8138\u4e0a\u808c\u8089\u7275\u6410\uff0c\u5728\u9648\u7384\u673a\u77a7\u6765\uff0c\u66f4\u663e\u5f97\u72f0\u72de\u53ef\u6016\uff0c\u4e0a\u5b98\u5929\u91ce\u5374\u8fce\u7740\u4ed6 \u7684\u76ee\u5149\uff0c\u5e76\u4e0d\u907f\u5f00\u3002\u6bd5\u51cc\u98ce\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u53ea\u662f\uff0c\u53ea\u662f\u4ec0\u4e48\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u665a\u8f88\u66fe\u5728\u5fc3\u4e2d \u81ea\u8a93\uff0c\u82e5\u975e\u51ed\u7740\u672c\u8eab\u4e4b\u529b\uff0c\u51b3\u4e0d\u51fa\u90a3\u77f3\u6d1e\u3002\u201d\u6bd5\u51cc\u98ce\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u90a3\u4f60\u5012\u662f\u602a\u6211\u6551\u4f60\u51fa \u6765\u4e86\u3002\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u4e0d\u6562\u3002\u4f46\u665a\u8f88\u786e\u662f\u60f3\u5f85\u81ea\u5df1\u7ec3\u6210\u672c\u9886\u4e4b\u540e\uff0c\u624d\u4e0e\u90a3\u59d3\u4e91\u7684\u8001\u5339\u592b\u7b97 \u5e10\uff0c\u62a5\u90a3\u593a\u8c31\u8fb1\u8eab\u4e4b\u4ec7\u3002\u201d \u3000\u3000\u6bd5\u51cc\u98ce\u9053\uff1a\u201c\u5927\u4e08\u592b\u4e0d\u613f\u56e0\u4eba\u6210\u4e8b\uff0c\u4f60\u8fd9\u526f\u786c\u813e\u6c14\u6b63\u5408\u6211\u8fd9\u8001\u602a\u7269\u7684\u5fc3\u610f\u3002\u53ef\u662f\uff0c\u4f60\u6709 \u6ca1\u6709\u60f3\u8fc7\uff0c\u7eb5\u8bb8\u4f60\u5728\u77f3\u5ba4\u4e4b\u4e2d\u7ec3\u6210\u672c\u9886\uff0c\u90a3\u8fd8\u662f\u6cbe\u4e86\u4e91\u821e\u9633\u7684\u6069\u60e0\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u7741\u773c\u8bf4\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u6bd5\u51cc\u98ce\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u7684\u5fc3\u610f\uff0c\u4e91\u821e\u9633\u82e5\u6536\u4f60\u4e3a\u5f92\uff0c\u90a3 \u4f60\u5b9a\u7136\u4e0d\u613f\u3002\u4ed6\u5c06\u4f60\u5173\u5728\u77f3\u5ba4\u4e4b\u4e2d\uff0c\u5899\u58c1\u4e0a\u523b\u6709\u8fbe\u6469\u5251\u8c31\uff0c\u5728\u4f60\u7684\u5fc3\u610f\uff0c\u4ee5\u4e3a\u8fd9\u5251\u8c31\u539f\u662f\u4f60 \u6d3e\u4e4b\u7269\uff0c\u53ea\u8981\u4e0d\u662f\u4e91\u821e\u9633\u4eb2\u6388\uff0c\u90a3\u4f60\u5b66\u4e86\u4e5f\u662f\u5fc3\u5b89\u7406\u5f97\uff0c\u662f\u4e48\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u70b9\u4e86\u70b9\u5934\uff0c\u6bd5\u51cc \u98ce\u9053\uff1a\u201c\u4e91\u821e\u9633\u4e3a\u4ec0\u4e48\u8981\u5c06\u4f60\u5173\u5728\u77f3\u5ba4\u4e4b\u4e2d\uff0c\u90a3\u8fd8\u4e0d\u662f\u6709\u610f\u8981\u6210\u5168\u4f60\uff01\u201d \u3000\u3000\u8fd9\u672c\u6765\u662f\u6781\u6613\u660e\u767d\u7684\u9053\u7406\uff0c\u4f46\u4e0a\u5b98\u5929\u91ce\u7d20\u65e0\u673a\u5fc3\uff0c\u800c\u53c8\u4e00\u610f\u8981\u7ec3\u6210\u672c\u9886\u81ea\u5df1\u590d\u4ec7\uff0c\u4e00\u65f6 \u4e4b\u95f4\uff0c\u7adf\u7136\u6ca1\u6709\u60f3\u8d77\uff0c\u987f\u65f6\u795e\u6cae\u6c14\u4e27\uff0c\u6bd5\u51cc\u98ce\u9053\uff1a\u201c\u4f55\u51b5\u4f60\u8981\u7ec3\u6210\u672c\u9886\uff0c\u6700\u5c11\u4e5f\u5f97\u5341\u5e74\uff0c\u4e91 \u821e\u9633\u82e5\u662f\u65e9\u6b7b\u4e86\u5462\uff0c\u6ca1\u4eba\u9001\u98df\u7269\u7ed9\u4f60\uff0c\u90a3\u4f60\u4e5f\u4e0d\u51fa\u77f3\u6d1e\u5417\uff1f\u4f60\u771f\u50cf\u4e00\u4e2a\u5014\u5f3a\u7684\u5b69\u5b50\uff0c\u4e00\u65f6\u5174 \u8d77\uff0c\u5c31\u4e0d\u518d\u60f3\u53ca\u5176\u4ed6\u3002\u4e0d\u8fc7\u6211\u8fd8\u662f\u559c\u6b22\u4f60\u8fd9\u4e2a\u5014\u5f3a\u7684\u5b69\u5b50\u3002\u4f60\u8981\u4eb2\u81ea\u62a5\u4ec7\uff0c\u90a3\u4e5f\u4e0d\u96be\uff0c\u6211\u7ba1 \u4fdd\u4f60\u4e09\u5e74\u4e4b\u5185\uff0c\u4fbf\u53ef\u7ec3\u6210\u7edd\u6280\uff01\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u4e0d\u80fd\u62dc\u4f60\u4e3a\u5e08\uff01\u201d\u6bd5\u51cc\u98ce\u54c8\u54c8\u7b11 \u9053\uff1a\u201c\u6211\u5c82\u4f1a\u52c9\u5f3a\u4f60\u62dc\u6211\u4e3a\u5e08\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u5f85\u6211\u56de\u5230\u6b66\u5f53\u5c71\u7980\u660e\u672c\u6d3e\u957f\u8001\u4e4b\u540e\uff0c\u4ed6\u65e5\u82e5\u8fd8\u6709\u7f18\u76f8\u9047\uff0c\u90a3\u65f6\u518d\u8bf7\u4f60\u8001 \u6307\u70b9\u6b66\u529f\u3002\u201d\u987b\u77e5\u5728\u6b66\u6797\u4e2d\u7684\u89c4\u77e9\uff0c\u6539\u6295\u660e\u5e08\uff0c\u90a3\u662f\u4e00\u4ef6\u5927\u4e8b\u3002\u4f46\u82e5\u53ea\u662f\u4ee5\u79c1\u4eba\u60c5\u8c0a\uff0c\u4f20\u6388 \u51e0\u624b\u6b66\u529f\uff0c\u5176\u95f4\u5e76\u65e0\u5e08\u5f92\u540d\u4efd\u7684\uff0c\u90a3\u5c31\u4e0d\u7b97\u8fdd\u53cd\u95e8\u89c4\u3002\u4e0d\u8fc7\u4e0a\u5b98\u5929\u91ce\u4e43\u662f\u638c\u95e8\u5f1f\u5b50\uff0c\u6240\u4ee5\u7eb5 \u8bb8\u53ea\u662f\u79c1\u4eba\u4e4b\u95f4\u7684\u5207\u78cb\uff0c\u4e5f\u5f97\u7980\u660e\u957f\u8001\u3002 \u3000\u3000\u6bd5\u51cc\u98ce\u7b11\u9053\uff1a\u201c\u4f60\u8981\u7980\u660e\u957f\u8001\uff0c\u4f55\u5fc5\u8981\u56de\u6b66\u5f53\u5c71\u53bb\uff1f\u8d35\u6d3e\u7684\u4e94\u4e2a\u8001\u5934\u513f\u4e00\u76f4\u5c31\u5728\u4f60\u7684\u8eab \u540e\uff0c\u4f60\u4e0d\u77e5\u9053\u4e48\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u6115\u7136\u56de\u987e\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u4e94\u4f4d\u5e08\u4f2f\u5e08\u53d4\u90fd\u6765\u4e86\u4e48\uff1f\u201d\u6bd5\u51cc\u98ce \u9053\uff1a\u201c\u4f60\u524d\u811a\u4e0b\u5c71\uff0c\u4ed6\u4eec\u540e\u811a\u5c31\u8ddf\u7740\u51fa\u95e8\u3002\u73b0\u5728\u53ea\u6015\u6b63\u5728\u5c71\u524d\u8ddf\u4e91\u821e\u9633\u8981\u4eba\u4e86\u5462\uff0c\u4f60\u8981\u89c1\u4ed6 \u4eec\u4e48\uff1f\u201d\u6bd5\u51cc\u98ce\u6240\u6599\u4e0d\u5dee\uff0c\u8fd9\u65f6\u6b66\u5f53\u4e94\u8001\u6b63\u5728\u4ee5\u201c\u4e94\u96f7\u5929\u5fc3\u638c\u6cd5\u201d\u5408\u6218\u4e91\u821e\u9633\uff0c\u4e0a\u5b98\u5929\u91ce\u4fa7 \u8033\u7ec6\u542c\uff0c\u8fd8\u9690\u7ea6\u53ef\u4ee5\u542c\u5230\u4e94\u96f7\u5929\u5fc3\u638c\u72ec\u5177\u7684\u98ce\u96f7\u4e4b\u58f0\u3002 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u4e00\u7247\u832b\u7136\uff0c\u5341\u5206\u4e0d\u89e3\uff0c\u5583\u5583\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u600e\u77e5\u9053\u6211\u662f\u5230\u8d3a\u5170\u5c71\u6765\u627e\u4e91\u821e\u9633\uff1f\u4e3a \u4ec0\u4e48\u4e0d\u4e0e\u6211\u8bf4\u660e\uff1f\u6697\u6697\u8ddf\u5728\u6211\u7684\u540e\u9762\uff1f\u201d\u8981\u77e5\u4ed6\u53d7\u4e86\u5e08\u7236\u725f\u4e00\u7c9f\u7684\u4e34\u7ec8\u9057\u547d\uff0c\u5411\u4e91\u821e\u9633\u7d22\u56de \u5251\u8c31\uff0c\u8fd9\u4e8b\u60c5\u6781\u4e3a\u9690\u79d8\uff0c\u4ed6\u4ece\u672a\u5411\u4efb\u4f55\u4eba\u9732\u8fc7\u534a\u70b9\u98ce\u58f0\uff0c\u53ea\u5728\u4e0b\u5c71\u4e4b\u524d\u7559\u4e0b\u4e00\u5c01\u5bc6\u4fe1\uff0c\u8bf7\u667a \u5706\u957f\u8001\u5728\u4e00\u5e74\u4e4b\u540e\u624d\u5f00\u62c6\u7684\u3002\u8fd9\u4e5f\u662f\u725f\u4e00\u7c9f\u4e34\u7ec8\u65f6\u7684\u5429\u5490\uff0c\u7528\u610f\u5728\u4e8e\u987e\u5168\u4eb2\u621a\u7684\u60c5\u8c0a\uff0c\u82e5\u7136 \u4e91\u821e\u9633\u5584\u7f62\u5e72\u4f11\uff0c\u4ea4\u56de\u5251\u8c31\uff0c\u90a3\u4e48\u4e0a\u5b98\u5929\u91ce\u5728\u4e00\u5e74\u4e4b\u5185\u5fc5\u5b9a\u80fd\u56de\u5230\u6b66\u5f53\u5c71\uff0c\u90a3\u5c01\u5bc6\u4fe1\u4e5f\u5c31\u53ef \u539f\u5c01\u53d6\u56de\u711a\u6bc1\uff0c\u8fd9\u6837\u4fbf\u8fde\u6b66\u5f53\u4e94\u8001\u4e5f\u4e0d\u77e5\u9053\u6b64\u6bb5\u60c5\u7531\uff0c\u514d\u5f97\u4e0e\u4e91\u821e\u9633\u7559\u4e0b\u82a5\u8482\u3002\u82e5\u7136\u4e00\u5e74\u4e4b \u540e\u4e0d\u56de\uff0c\u90a3\u5c31\u662f\u4e0a\u5b98\u5929\u91ce\u9047\u4e86\u610f\u5916\uff0c\u90a3\u65f6\u667a\u5706\u957f\u8001\u62c6\u9605\u7559\u4e66\uff0c\u81ea\u4f1a\u66ff\u4ed6\u62a5\u4ec7\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4eec\u73b0\u5728\u5c31\u8d76\u6765\uff0c\u4e0d\u7531\u5f97\u4e0a\u5b98\u5929\u91ce\u5fc3\u4e2d\u5927\u4e3a\u7591\u60d1\uff0c\u6bd5\u51cc\u98ce\u53cc\u76ee\u70af\u70af\uff0c\u903c\u89c6\u7740\u4e0a\u5b98\u5929 \u91ce\u9053\uff1a\u201c\u667a\u5706\u957f\u8001\u5bf9\u4f60\u5982\u4f55\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u7231\u62a4\u6211\u6709\u5982\u5b50\u4f84\u3002\u201d\u6bd5\u51cc\u98ce\u51b7\u51b7\u4e00\u7b11\u9053\uff1a \u201c\u53ea\u6015\u662f\u7231\u62a4\u90a3\u672c\u8fbe\u6469\u5251\u8c31\u5427\uff1f\u201d\u968f\u624b\u53d6\u51fa\u4e00\u5c01\u4e66\u4fe1\uff0c\u9053\uff1a\u201c\u4f60\u77a7\u8fd9\u4e2a\uff0c\u667a\u5706\u957f\u8001\u6b63\u8981\u62db\u96c6 \u4ed6\u5728\u5916\u4e91\u6e38\u7684\u516b\u4e2a\u5f97\u610f\u5f1f\u5b50\u56de\u5c71\u5462\u3002\u201d \u3000\u3000\u90a3\u5c01\u4fe1\u662f\u5199\u7ed9\u5176\u4e2d\u4e00\u4e2a\u5f1f\u5b50\u7684\uff0c\u53eb\u4ed6\u5c31\u8fd1\u901a\u77e5\u5176\u4ed6\u4e24\u4eba\uff0c\u8bf4\u660e\u4e0a\u5b98\u5929\u91ce\u5df2\u53bb\u7d22\u5251\u8c31\u4e4b \u4e8b\uff0c\u53eb\u4ed6\u4eec\u6025\u901f\u56de\u5c71\uff0c\u679c\u7136\u662f\u667a\u5706\u957f\u8001\u7684\u7b14\u8ff9\uff0c\u770b\u6765\u9664\u4e86\u8fd9\u5c01\u4fe1\u4e4b\u5916\uff0c\u5b9a\u7136\u8fd8\u6709\u5199\u7ed9\u5176\u4ed6\u5f1f \u5b50\u7684\u76f8\u540c\u7684\u4e66\u4fe1\u3002\u4e0a\u5b98\u5929\u91ce\u6240\u7559\u4e0b\u7684\u90a3\u5c01\u5bc6\u4fe1\uff0c\u65e9\u5df2\u88ab\u667a\u5706\u957f\u8001\u62c6\u9605\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u5446\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u667a\u5706\u5e08\u4f2f\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u8981\u77e5\u4e0a\u5b98\u5929\u91ce\u867d\u5c5e\u665a\u8f88\uff0c\u4f46\u7a76\u662f \u638c\u95e8\u4eba\u7684\u8eab\u4efd\uff0c\u5728\u7ea6\u671f\u4e4b\u524d\u5077\u62c6\u638c\u95e8\u4eba\u7684\u5bc6\u4fe1\uff0c\u90a3\u5c31\u662f\u5bf9\u638c\u95e8\u4eba\u7684\u7f9e\u8fb1\u3002\u6bd5\u51cc\u98ce\u53f9\u4e86\u53e3\u6c14 \u9053\uff1a\u201c\u79c1\u5fc3\u81ea\u7528\uff0c\u8d24\u5982\u6b66\u5f53\u4e94\u8001\u4ea6\u81ea\u4e0d\u514d\uff0c\u5c82\u4e0d\u53ef\u53f9\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u53eb\u9053\uff1a\u201c\u8001\u524d\u8f88\u6b64\u8a00\u4f55 \u6765\uff1f\u201d\u6bd5\u51cc\u98ce\u9053\uff1a\u201c\u4f60\u5f53\u6211\u662f\u4f4e\u6bc1\u4f60\u7684\u5e08\u4f2f\u5e08\u53d4\u4e48\uff1f\u6211\u95ee\u4f60\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\u4f60\u5e08\u7236\u725f\u4e00\u7c9f\u662f\u600e \u4e48\u6b7b\u7684\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u6115\u7136\u8bf4\u9053\uff1a\u201c\u6211\u5e08\u7236\u53ef\u662f\u5bff\u7ec8\u6b63\u5bdd\u7684\u554a\u3002\u201d\u6bd5\u51cc\u98ce\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u5e08\u7236\u662f\u75c5\u6b7b \u7684\uff0c\u4f46\u4ed6\u4e0d\u8fc7\u4e94\u5341\u4e4b\u5e74\uff0c\u4fbf\u6e98\u7136\u65e9\u901d\uff0c\u90a3\u4e0d\u662f\u5f88\u53ef\u60dc\u4e48\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u542c\u4ed6\u8bdd\u4e2d\u6709\u8bdd\uff0c\u6124\u7136\u8bf4 \u9053\uff1a\u201c\u8bf7\u524d\u8f88\u660e\u8a00\uff0c\u6211\u5e08\u7236\u662f\u5426\u6b7b\u5f97\u4e0d\u660e\u4e0d\u767d\uff1f\u201d\u6bd5\u51cc\u98ce\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u662f\uff0c\u4f46\u4fd7\u8bed\u4e91\uff1a\u5fe7\u80fd \u4f24\u4eba\uff0c\u81ea\u4f60\u5e08\u7956\u6b7b\u540e\uff0c\u5341\u591a\u5e74\u6765\u5916\u5fe7\u5f3a\u654c\uff0c\u5185\u53c8\u89c1\u903c\u4e8e\u540c\u95e8\uff0c\u5fe7\u90c1\u4ea4\u714e\uff0c\u65e9\u6b7b\u4ea6\u4e0d\u8db3\u602a \u4e86\u3002\u201d\u4e0a\u5b98\u5929\u91ce\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\u5916\u654c\u5185\u5fe7\uff0c\u8bf7\u8001\u524d\u8f88\u8bf4\u4e2a\u660e\u767d\u3002\u201d \u3000\u3000\u6bd5\u51cc\u98ce\u9053\uff1a\u201c\u5176\u5b9e\u4f60\u5e08\u7236\u6240\u5fe7\u8651\u7684\u5f3a\u654c\uff0c\u65e9\u5df2\u6b7b\u4e86\uff0c\u53ea\u7559\u4e0b\u4e00\u4e2a\u5916\u5b59\uff0c\u4e0d\u8db3\u4e3a\u8651\uff0c\u8fd9\u4e8b \u4ee5\u540e\u518d\u8bf4\u3002\u4f60\u5e08\u7236\u7684\u5fe7\u7126\u81f3\u75c5\uff0c\u636e\u6211\u770b\u6765\uff0c\u5012\u6709\u4e00\u534a\u662f\u4f60\u90a3\u4e94\u4f4d\u5e08\u4f2f\u5e08\u53d4\u903c\u51fa\u6765\u7684\u3002\u201d\u4e0a\u5b98 \u5927\u91ce\u60ca\u6115\u4e4b\u6781\uff0c\u9053\uff1a\u201c\u5e08\u4f2f\u5e08\u53d4\u4e3a\u4f55\u8981\u903c\u6211\u7684\u5e08\u7236\uff1f\u201d \u3000\u3000\u6bd5\u51cc\u98ce\u9053\uff1a\u201c\u4f60\u5e08\u7956\u5f97\u4e86\u8fbe\u6469\u5251\u8c31\uff0c\u5176\u4e8b\u751a\u79d8\uff0c\u4f46\u667a\u5706\u957f\u8001\u5374\u4e0d\u77e5\u4ece\u54ea\u91cc\u5f97\u5230\u4e86\u98ce\u58f0\uff0c \u4ed6\u672c\u610f\u4ee5\u4e3a\u4f60\u5e08\u7956\u5fc5\u7136\u4f1a\u4f20\u7ed9\u4ed6\u7684\uff0c\u8fd9\u5251\u8c31\u7ed9\u4e91\u821e\u9633\u76d7\u8d70\uff0c\u4ed6\u5374\u5e76\u4e0d\u77e5\u9053\uff0c\u4f60\u5e08\u7956\u6b7b\u540e\uff0c\u4ed6 \u53ea\u5f53\u662f\u4f60\u5e08\u7236\u72ec\u5f97\u4f20\u6388\uff0c\u6240\u4ee5\u5c61\u6b21\u524d\u6765\uff0c\u8981\u903c\u4f60\u5e08\u7236\u4ea4\u51fa\u5251\u8c31\uff0c\u516c\u8bf8\u540c\u95e8\uff0c\u4f60\u5e08\u7236\u4e00\u6765\u662f\u788d \u4e8e\u59b9\u5b50\u7684\u60c5\u4efd\uff0c\u4e8c\u6765\u4e5f\u5fcc\u60ee\u4e91\u821e\u9633\uff0c\u4e0d\u4fbf\u628a\u5185\u60c5\u8bf4\u51fa\u6765\uff0c\u4f60\u90a3\u51e0\u4f4d\u5e08\u4f2f\u5e08\u53d4\u6b64\u53bb\u5f7c\u6765\uff0c\u4e0d\u4f46 \u7528\u8bf4\u8bdd\u903c\u4ed6\uff0c\u8fd8\u8981\u8bd5\u4ed6\u6b66\u529f\uff0c\u4f60\u5e08\u7236\u6db5\u517b\u7b97\u597d\u7684\u4e86\u3002\u5982\u679c\u662f\u4f60\uff0c\u6211\u770b\u4f60\u66f4\u53d7\u4e0d\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u4e00\u60f3\uff0c\u81ea\u5165\u5e08\u95e8\uff0c\u679c\u7136\u662f\u6bcf\u5e74\u90fd\u6709\u5e08\u6015\u5e08\u53d4\u8f6e\u6d41\u800c\u6765\uff0c\u800c\u6bcf\u6b21\u53bb\u540e\uff0c\u5e08\u7236\u603b\u662f \u90c1\u90c1\u4e0d\u4e50\u7684\u7ecf\u5e38\u8fbe\u5341\u5929\u534a\u6708\u4e4b\u4e45\uff0c\u4e0d\u7531\u5f97\u5bf9\u6bd5\u51cc\u98ce\u7684\u8bdd\u4fe1\u4e86\u51e0\u5206\u3002 \u3000\u3000\u6bd5\u51cc\u98ce\u53c8\u9053\uff1a\u201c\u667a\u5706\u957f\u8001\u903c\u4ed6\uff0c\u5176\u4e2d\u8fd8\u53e6\u6709\u79c1\u5fc3\u3002\u6b66\u5f53\u4e00\u6d3e\uff0c\u7d20\u6765\u6709\u9053\u5bb6\u4fd7\u5bb6\u4e4b\u5206\uff0c\u5728 \u4f60\u5e08\u7956\u4e4b\u524d\uff0c\u4e00\u5411\u662f\u9053\u5bb6\u5f1f\u5b50\u638c\u95e8\uff0c\u4f60\u5e08\u7956\u6587\u6b66\u517c\u4fee\uff0c\u4ee5\u4fd7\u5bb6\u5f1f\u5b50\u63a5\u4efb\u638c\u95e8\uff0c\u8fd9\u4e9b\u725b\u9f3b\u81ed\u9053 \u58eb\u4e0d\u6562\u95f2\u8bdd\uff0c\u4f20\u5230\u4e86\u4f60\u7684\u5e08\u7236\uff0c\u4ed6\u4eec\u53ef\u5c31\u4e0d\u5927\u4e00\u6837\u4e86\u3002\u6240\u4ee5\u8fd9\u6b21\u667a\u5706\u957f\u8001\u62c6\u4e86\u4f60\u7684\u5bc6\u4fe1\uff0c\u5c31 \u6025\u6025\u8981\u62db\u4ed6\u5728\u5916\u4e91\u6e38\u7684\u516b\u4e2a\u5f1f\u5b50\u56de\u6765\uff0c\u7528\u610f\u5c31\u662f\u5f85\u53d6\u56de\u5251\u8c31\u4e4b\u540e\uff0c\u53eb\u4f60\u548c\u4ed6\u7684\u516b\u4e2a\u5f1f\u5b50\u4e00\u9f50 \u7ec3\u5251\uff0c\u6b66\u5f53\u6700\u91cd\u5251\u6cd5\uff0c\u54c8\u54c8\uff0c\u5f85\u5230\u4ed6\u7684\u5f1f\u5b50\u7ec3\u6210\uff0c\u603b\u6709\u4e00\u4eba\u4f1a\u80dc\u4e8e\u4f60\u3002\u90a3\u65f6\uff0c\u4ed6\u53ef\u5c31\u8981\u4ee5\u957f \u8001\u7684\u8eab\u4efd\u8bf4\u662f\u4f20\u4f4d\u5e94\u8be5\u4f20\u8d24\uff0c\u4f60\u638c\u95e8\u4eba\u7684\u5730\u4f4d\u53ef\u5c31\u8981\u5e9f\u4e86\u54ea\uff01\u4ee5\u540e\u6b66\u5f53\u7684\u638c\u95e8\uff0c\u4e5f\u5c31\u603b\u5f97\u7531 \u9053\u58eb\u6765\u505a\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u5fc3\u5934\u5927\u6124\uff0c\u4f46\u4ecd\u662f\u534a\u4fe1\u534a\u7591\uff0c\u6bd5\u51cc\u98ce\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u6211\u662f\u4ee5\u5c0f\u4eba\u4e4b\u5fc3\u5ea6\u541b\u5b50\u4e4b\u8179 \u4e48\uff1f\u54c8\uff0c\u54c8\uff0c\u4e0d\u7792\u4f60\u8bf4\uff0c\u6211\u4e0e\u8fd9\u5251\u8c31\u4e5f\u6709\u4e00\u6bb5\u6e0a\u6e90\u3002\u4f60\u5e08\u7236\u6b7b\u540e\uff0c\u6211\u6599\u4ed6\u5fc5\u6709\u9057\u547d\uff0c\u662f\u4ee5\u6697 \u6697\u8ddf\u8e2a\u4f60\u5230\u6b66\u5f53\u5c71\u4e0a\uff0c\u6211\u672c\u60f3\u76d7\u4f60\u7559\u4e0b\u7684\u90a3\u5c01\u5bc6\u4fe1\uff0c\u6ca1\u6709\u5230\u624b\uff0c\u5374\u628a\u667a\u5706\u957f\u8001\u9001\u51fa\u53bb\u7684\u4fe1\u76d7 \u4e86\u4e00\u5c01\u3002\u8fd8\u5077\u542c\u4e86\u4ed6\u548c\u56db\u4f4d\u5e08\u5f1f\u7684\u8bf4\u8bdd\u3002\u8fd9\u4e8b\u60c5\u6211\u5df2\u8bf4\u5f97\u4e00\u6e05\u4e8c\u695a\uff0c\u4fe1\u4e0e\u4e0d\u4fe1\uff0c\u90a3\u5c31\u5168\u5728\u4f60 \u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u6700\u6068\u4eba\u4e0d\u591f\u5149\u660e\u78ca\u843d\uff0c\u542c\u4e86\u8fd9\u8bdd\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u6211\u624d\u4e0d\u5e0c\u7f55\u8fd9\u6b66\u5f53\u638c\u95e8\uff01\u201d\u6068 \u6068\u7684\u5c06\u667a\u5706\u957f\u8001\u90a3\u5c01\u4fe1\u6495\u6210\u7247\u7247\u3002\u201d \u3000\u3000\u6bd5\u51cc\u98ce\u9053\uff1a\u201c\u597d\uff0c\u6709\u5fd7\u6c14\uff0c\u90a3\u4e48\uff0c\u90a3\u90e8\u8fbe\u6469\u5251\u8c31\u5462\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u90a3\u5251\u8c31\u867d\u7136\u662f\u6211 \u5e08\u7956\u4e4b\u7269\uff0c\u7a76\u7adf\u5e94\u5c5e\u6b66\u5f53\u6d3e\u6240\u6709\uff0c\u6211\u65e2\u4e0d\u505a\u6b66\u5f53\u6d3e\u7684\u638c\u95e8\uff0c\u8fd9\u5251\u8c31\u4e5f\u4e0d\u5e0c\u7f55\u4ed6\u4e86\uff01\u201d\u6bd5\u51cc\u98ce \u5ffd\u5730\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u90a3\u5251\u8c31\u5176\u5b9e\u4e5f\u4e0d\u662f\u4f60\u5e08\u7956\u7684\uff01\u201d \u3000\u3000\u4e0a\u5bab\u5929\u91ce\u9053\uff1a\u201c\u600e\u4e48\uff1f\u5e08\u7236\u4e34\u7ec8\u4e4b\u9645\uff0c\u5bf9\u6211\u8bf4\u5f97\u660e\u660e\u767d\u767d\uff0c\u90a3\u8fbe\u6469\u5251\u8c31\u4e43\u662f\u5e08\u7956\u5728\u4e00\u4e2a \u77f3\u7a9f\u4e4b\u4e2d\u5bfb\u83b7\u7684\uff0c\u96be\u9053\u4e5f\u662f\u5047\u7684\u5417\uff1f\u201d\u6bd5\u51cc\u98ce\u9053\uff1a\u201c\u6709\u4e00\u534a\u771f\uff0c\u6709\u4e00\u534a\u5047\u3002\u201d\u4e0a\u5b98\u5929\u91ce\u62dc\u5e08 \u4e4b\u65f6\uff0c\u4ed6\u5e08\u7956\u65e9\u5df2\u901d\u4e16\uff0c\u4f46\u4ed6\u542c\u957f\u8001\u6240\u8a00\uff0c\u6df1\u4fe1\u5e08\u7956\u4e43\u662f\u4e00\u4ee3\u5927\u4fa0\uff0c\u5bf9\u4ed6\u666f\u4ef0\u4e4b\u6781\uff0c\u6b64\u8bdd\u8001 \u662f\u51fa\u4e8e\u522b\u4eba\u53e3\u4e2d\uff0c\u4ed6\u5b9a\u7136\u4e0d\u80af\u653e\u8fc7\u3002\u51fa\u5728\u6bd5\u51cc\u98ce\u53e3\u4e2d\uff0c\u53ef\u6559\u4ed6\u6014\u7740\u4e86\u3002 \u3000\u3000\u6bd5\u51cc\u98ce\u770b\u4e86\u4e0a\u5b98\u5929\u91ce\u4e00\u773c\uff0c\u9053\uff1a\u201c\u96be\u9053\u4f60\u4e0d\u80af\u76f8\u4fe1\uff0c\u82e5\u975e\u6211\u66fe\u4eb2\u89c1\uff0c\u6211\u4e5f\u4e0d\u4fe1\u725f\u72ec\u9038\u4f1a \u4e3a\u4e86\u8fd9\u672c\u5251\u8c31\uff0c\u4e0e\u548c\u4ed6\u9f50\u540d\u7684\u4e00\u4f4d\u5f53\u4ee3\u5927\u4fa0\uff0c\u820d\u6b7b\u5fd8\u751f\u7684\u6597\u4e86\u4e00\u5929\u4e00\u591c\u3002\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a \u201c\u8bf7\u9053\u5176\u8be6\u3002\u201d \u3000\u3000\u6bd5\u51cc\u98ce\u6c89\u541f\u534a\u664c\uff0c\u9053\uff1a\u201c\u8fd9\u4e8b\u8fc7\u4e8e\u79bb\u5947\uff0c\u6211\u8001\u5934\u5b50\u6709\u4e2a\u813e\u6c14\uff0c\u975e\u6709\u786e\u5207\u51ed\u8bc1\uff0c\u5c31\u5b81\u53ef\u7559 \u4f4f\u4e0d\u8bf4\u3002\u4e0d\u8fc7\u8981\u627e\u51ed\u8bc1\uff0c\u90a3\u4e5f\u5e76\u4e0d\u5f88\u96be\uff0c\u90a3\u5251\u8c31\u786e\u662f\u5c01\u5728\u4e00\u4e2a\u4e3b\u7a9f\u4e4b\u4e2d\uff0c\u6240\u4ee5\u6211\u8bf4\u4f60\u5e08\u7956\u7684 \u8bdd\uff0c\u4e00\u534a\u662f\u771f\u3002\u4f46\u90a3\u5374\u662f\u53e6\u4e00\u4f4d\u4e16\u5916\u9ad8\u4eba\u4e34\u6b7b\u4e4b\u65f6\uff0c\u90d1\u91cd\u4ed8\u6258\u7ed9\u4e0e\u4f60\u5e08\u7956\u9f50\u540d\u7684\u4e00\u4f4d\u5927\u4fa0 \u7684\u3002\u90a3\u65e5\u6070\u5de7\u4e24\u4eba\u90fd\u5230\u4e86\u90a3\u4e2a\u53e4\u7a9f\uff0c\u4f60\u5e08\u7956\u4e0e\u90a3\u4eba\u6597\u4e86\u4e00\u5929\u4e00\u591c\uff0c\u62a2\u4e86\u5251\u8c31\uff0c\u4f46\u4ed6\u4e5f\u7ed9\u90a3\u4eba \u5b9d\u5251\u6240\u4f24\uff0c\u4f60\u5e08\u7956\u53d1\u4e86\u72e0\uff0c\u5c31\u60f3\u8fde\u4ed6\u90a3\u628a\u5b9d\u5251\u4e5f\u62a2\u4e86\uff0c\u90a3\u4eba\u4e0e\u4f60\u5e08\u7956\u9f50\u540d\uff0c\u867d\u7136\u88ab\u4f60\u5e08\u7956\u7528 \u592a\u6e05\u7384\u529f\u6240\u8d25\uff0c\u5b9d\u5251\u53ef\u5e76\u6ca1\u6709\u7ed9\u4ed6\u62a2\u53bb\u3002\u53ea\u662f\u5251\u4e0a\u7684\u4e24\u4ef6\u7389\u73af\uff0c\u5374\u88ab\u4f60\u5e08\u7956\u626f\u65ad\u4e86\u3002\u73b0\u5728\u8fd9 \u4e24\u4ef6\u7389\u73af\uff0c\u5c31\u5728\u6211\u7684\u624b\u4e2d\uff0c\u90a3\u67c4\u5b9d\u5251\uff0c\u5374\u5728\u4e91\u821e\u9633\u624b\u4e0a\uff0c\u5f85\u6211\u53bb\u627e\u4e91\u821e\u9633\uff0c\u5c06\u90a3\u628a\u5b9d\u5251\u62a2 \u56de\uff0c\u8ba9\u4f60\u770b\u770b\u90a3\u5251\u4e0a\u7684\u722a\u75d5\uff0c\u518d\u5c06\u7389\u73af\u5d4c\u4e0a\uff0c\u4f60\u5c31\u660e\u767d\u4e86\u3002\u597d\u5427\uff0c\u542c\u5f97\u5c71\u4e0a\u7684\u6076\u6597\u4e4b\u58f0\uff0c\u6b66 \u5f53\u4e94\u8001\u5c31\u8981\u843d\u8d25\u4e86\uff0c\u6211\u672c\u6765\u4e0d\u662f\u4e91\u821e\u9633\u7684\u5bf9\u624b\uff0c\u8d81\u6b64\u65f6\u673a\uff0c\u6b63\u597d\u4e0e\u4ed6\u6597\u6597\u3002\u4f60\u4e14\u5728\u6b64\u7b49\u6211\uff0c \u65e5\u843d\u4e4b\u524d\uff0c\u6211\u5c31\u53ef\u56de\u6765\uff0c\u90a3\u65f6\u6211\u518d\u628a\u8be6\u7ec6\u60c5\u7531\uff0c\u4e00\u4e00\u544a\u8bc9\u4e8e\u4f60\u3002\u201d \u3000\u3000\u8fd9\u4e00\u756a\u8bdd\u5728\u4e0a\u5b98\u5929\u91ce\u7684\u5fc3\u4e2d\u8d77\u4e86\u6781\u5927\u7684\u53cd\u5e94\uff0c\u4f46\u89c9\u4e3e\u4e16\u832b\u832b\uff0c\u8981\u627e\u4e00\u4e2a\u5149\u660e\u78ca\u843d\u3001\u820d\u5229 \u53d6\u4e49\u7684\u4eba\u6b8a\u4e3a\u4e0d\u6613\uff0c\u4f46\u4ed6\u8fd9\u756a\u611f\u89e6\uff0c\u82e5\u6bd4\u8d77\u9648\u7384\u673a\u6765\uff0c\u90a3\u5374\u8fd8\u8fdc\u4e0d\u53ca\u9648\u7384\u673a\u5fc3\u7075\u6240\u53d7\u7684\u9707\u61be \u4e4b\u6df1\uff0c\u9648\u7384\u673a\u542c\u4e86\u8fd9\u8bdd\uff0c\u51e0\u4e4e\u9707\u9a87\u6b32\u7edd\uff01\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4e0e\u725f\u72ec\u9038\u540c\u65f6\u5e76\u79f0\u7684\u5f53\u4ee3\u5927\u4fa0\uff0c\u9664\u4e86 \u6211\u7684\u5916\u7956\u7236\u9648\u5b9a\u65b9\u4e4b\u5916\u8fd8\u6709\u8c01\uff1f\u4e91\u7d20\u7d20\u90a3\u628a\u5251\uff0c\u5251\u67c4\u4e4b\u4e0a\u786e\u6709\u6307\u7532\u7684\u6293\u75d5\uff0c\u4ece\u6211\u5916\u7956\u7236\u6240\u9057 \u7559\u4e0b\u6765\u7684\u8bb0\u8f7d\uff0c\u5251\u4e0a\u4e5f\u786e\u662f\u6709\u4e24\u4ef6\u7389\u73af\u4f5c\u4e3a\u9970\u7269\u3002\u8fd9\u628a\u5251\uff0c\u8fd9\u628a\u5251\uff0c\u96be\u9053\u5f53\u771f\u662f\u6211\u5916\u7956\u7236\u4e4b \u7269\uff0c\u5374\u600e\u7684\u5230\u4e86\u4e91\u821e\u9633\u624b\u4e2d\uff1f\u201d \u3000\u3000\u53ea\u542c\u5f97\u4e0a\u5b98\u5929\u91ce\u5ffd\u5730\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\u8001\u524d\u8f88\u7684\u610f\u601d\uff0c\u8001\u524d\u8f88\u5192\u5947\u9669\u8981\u5728\u4e91\u821e\u9633 \u624b\u4e2d\u593a\u8c31\u62a2\u5251\uff0c\u65e0\u975e\u662f\u4e3a\u4e86\u6211\u3002\u6211\u73b0\u5728\u7518\u5fc3\u60c5\u613f\u62dc\u8001\u524d\u8f88\u4e3a\u5e08\uff01\u201d \u3000\u3000\u5c45\u4e8e\u6b66\u6797\u9886\u8896\u7684\u6b66\u5f53\u6d3e\u638c\u95e8\u5f1f\u5b50\uff0c\u7adf\u81ea\u62dc\u8fd9\u602a\u4eba\u4e3a\u5e08\uff0c\u9648\u7384\u673a\u4ea6\u4e0d\u7981\u9a87\u7136\uff0c\u4ed6\u6df1\u6089\u4e0a\u5b98 \u5929\u91ce\u7684\u6027\u60c5\uff0c\u89c1\u4ed6\u4e09\u4e2a\u54cd\u5934\u78d5\u4e0b\uff0c\u8fd9\u4e8b\u60c5\u5df2\u662f\u65e0\u53ef\u633d\u56de\u3002 \u3000\u3000\u6bd5\u51cc\u98ce\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u77e5\u9053\u6211\u662f\u4ec0\u4e48\u4eba\uff0c\u4f60\u62dc\u6211\u4e3a\u5e08\uff0c\u4e0d\u6015\u5c06\u6765\u540e\u6094\u4e48\uff1f\u201d\u4e0a\u5b98\u5929 \u91ce\u9053\uff1a\u201c\u4e0d\u7ba1\u524d\u8f88\u662f\u8c01\uff0c\u5f1f\u5b50\u662f\u8ddf\u5b9a\u5e08\u7236\u7684\u4e86\uff0c\u6b63\u8981\u8bf7\u6559\u5e08\u7236\u6cd5\u53f7\u3002\u201d\u6bd5\u51cc\u98ce\u5927\u7b11\u9053\uff1a\u201c\u4f60 \u8fde\u6211\u7684\u59d3\u540d\u6765\u5386\u90fd\u4e00\u6982\u4e0d\u77e5\uff0c\u5c45\u7136\u5982\u6b64\u4fe1\u8d56\u4e8e\u6211\uff0c\u5fc3\u7518\u60c5\u613f\u62dc\u6211\u4e3a\u5e08\uff0c\u54c8\u54c8\uff0c\u4f60\u4e0d\u4f46\u662f\u6211\u7684 \u597d\u5f92\u513f\uff0c\u7adf\u662f\u6211\u751f\u5e73\u7684\u7b2c\u4e00\u77e5\u5df1\u4e86\uff01\u201d\u9648\u7384\u673a\u5fc3\u9053\uff1a\u201c\u8fd9\u602a\u4eba\u7684\u8bf4\u8bdd\u4e5f\u50cf\u4ed6\u7684\u9762\u8c8c\u4e00\u6837\uff0c\u771f \u662f\u602a\u7edd\u4eba\u5bf0\uff01\u201d \u3000\u3000\u53ea\u542c\u5f97\u6bd5\u51cc\u98ce\u5927\u7b11\u4e4b\u540e\uff0c\u5ffd\u5730\u9762\u8272\u4e00\u7aef\uff0c\u4e00\u5b57\u4e00\u53e5\u7684\u90d1\u91cd\u8bf4\u9053\uff1a\u201c\u6211\u53eb\u6bd5\u51cc\u98ce\uff0c\u4e8c\u5341\u5e74 \u524d\uff0c\u522b\u4eba\u5c0a\u79f0\u6211\u4e3a\u4e10\u4fa0\uff0c\u800c\u4eca\u6211\u53ef\u662f\u98ce\u9ad8\u6708\u9ed1\uff0c\u653e\u706b\u6740\u4eba\u7684\u5927\u76d7\uff01\u505a\u6211\u7684\u5f92\u5f1f\uff0c\u5c31\u8981\u8ddf\u6211\u505a \u5f3a\u76d7\uff0c\u4f60\u5f53\u771f\u4e0d\u540e\u6094\u4e48\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u6014\u4e86\u4e00\u6014\uff0c\u5ffd\u542c\u5f97\u6797\u5b50\u5916\u9690\u9690\u4f20\u6765\u8427\u97f5\u5170\u7684\u6b4c\u58f0\uff1a\u201c\u5929\u4e0a\u7684\u6708\u4eae\u8d76\u592a\u9633\uff0c\u5730\u4e0b \u7684\u59d1\u5a18\u8d76\u60c5\u90ce\u2026\u2026\u201d\u6b4c\u58f0\u95f4\u6b47\u4e4b\u4e2d\uff0c\u5939\u7740\u5979\u5bf9\u201c\u7384\u673a\u201d\u7684\u547c\u5524\uff0c\u4e0a\u5b98\u5929\u91ce\u4f46\u89c9\u4e07\u5ff5\u7686\u7070\uff0c\u5bf9 \u539f\u6765\u7684\u5e08\u95e8\uff0c\u5bf9\u672a\u6765\u7684\u4e8b\u4e1a\uff0c\u5bf9\u6697\u604b\u4e86\u591a\u5e74\u7684\u5fc3\u4e0a\u4eba\u513f\uff0c\u4e00\u5207\u7684\u4e00\u5207\u90fd\u597d\u50cf\u6ce1\u6cab\u4e00\u822c\u7834\u706d \u4e86\u3002\u4f46\u89c1\u6bd5\u51cc\u98ce\u7684\u602a\u8138\u4e0a\u5168\u65e0\u8868\u60c5\uff0c\u6de1\u6de1\u7684\u53c8\u95ee\u4e86\u4e00\u53e5\u9053\uff1a\u201c\u4f60\u5f53\u771f\u4e0d\u540e\u6094\u4e48\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u53eb\u9053\uff1a\u201c\u4e0e\u5176\u505a\u6b3a\u4e16\u76d7\u540d\u7684\u4fa0\u58eb\uff0c\u4e0d\u5982\u505a\u6740\u4eba\u653e\u706b\u7684\u5927\u76d7\uff0c\u5929\u4e0b\u6c79\u6c79\uff0c\u9ed1\u767d\u6df7 \u6dc6\uff0c\u4f46\u6c42\u65e0\u6127\u4e8e\u5fc3\uff0c\u505a\u4e00\u4e2a\u4ee4\u5978\u4eba\u9707\u6151\u7684\u5927\u76d7\u53c8\u6709\u4f55\u4e0d\u597d\uff1f\u201d \u3000\u3000\u6bd5\u51cc\u98ce\u63a5\u58f0\u8bf4\u9053\uff1a\u201c\u5bf9\u6781\uff0c\u5bf9\u6781\uff01\u505a\u5f3a\u76d7\u7684\u75db\u5feb\uff0c\u6700\u5c11\u8981\u6bd4\u505a\u4e00\u4e2a\u5faa\u89c4\u8e48\u77e9\u7684\u638c\u95e8\u4eba\u80dc \u8fc7\u591a\u591a\u3002\u597d\uff0c\u4ece\u4eca\u4e4b\u540e\uff0c\u4f60\u662f\u6211\u7684\u8863\u94b5\u4f20\u4eba\uff0c\u6211\u4e14\u5230\u4e91\u821e\u9633\u90a3\u513f\u628a\u5251\u8c31\u62ff\u6765\uff0c\u4f5c\u4e3a\u7ed9\u4f60\u7684\u89c1 \u9762\u4e4b\u793c\u3002\u201d\u94c1\u6756\u53ee\u53ee\u7684\u89e6\u5730\u4e4b\u58f0\uff0c\u6709\u5982\u9aa4\u96e8\uff0c\u5f53\u771f\u662f\u53bb\u4f3c\u98d8\u98ce\uff0c\u500f\u5ffd\u4e4b\u95f4\uff0c\u4e0d\u89c1\u4e86\u8e2a\u5f71\u3002 \u3000\u3000\u9648\u7384\u673a\u5728\u6811\u540e\u4e00\u8dc3\u800c\u51fa\uff0c\u53eb\u9053\uff1a\u201c\u4e0a\u5b98\u5144\uff0c\u4f60\u60f3\u5ff5\u5f97\u5c0f\u5f1f\u597d\u82e6\uff01\u201d\u6b63\u6b32\u95ee\u4ed6\u8fd9\u4e24\u65e5\u6765\u7684 \u7ecf\u5386\uff0c\u4e0a\u5b98\u5929\u91ce\u5ffd\u5730\u4e00\u77aa\u773c\u775b\uff0c\u5389\u58f0\u65a5\u9053\uff1a\u201c\u8c01\u8981\u4f60\u60f3\u5ff5\uff1f\u522b\u4eba\u5bf9\u4f60\u601d\u5ff5\u7684\u82e6\u5904\uff0c\u4f60\u4e5f\u77e5\u9053 \u4e48\uff1f\u201d \u3000\u3000\u9648\u7384\u673a\u4e00\u6014\uff0c\u53ea\u89c1\u4e0a\u5b98\u5929\u91ce\u4f38\u624b\u4e00\u6307\uff0c\u559d\u9053\uff1a\u201c\u97f5\u5170\u59d0\u59d0\u5524\u4f60\uff0c\u4f60\u542c\u4e0d\u89c1\u4e48\uff01\u201d\u9648\u7384\u673a \u9053\uff1a\u201c\u4e0a\u5b98\u5144\uff0c\u4f60\uff0c\u4f60\uff0c\u4f60\u542c\u6211\u8bf4\u2014\u2014\u3002\u201d\u4e0a\u5b98\u5929\u91ce\u6beb\u4e0d\u7406\u776c\uff0c\u8fde\u73e0\u70ae\u4f3c\u7684\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u4f60 \u82e5\u8fd8\u5bf9\u6211\u6709\u4e00\u70b9\u670b\u53cb\u4e4b\u60c5\uff0c\u5feb\u628a\u97f5\u5170\u627e\u56de\u6765\u89c1\u6211\uff0c\u6211\u8981\u89c1\u4f60\u4eec\u5728\u6211\u9762\u524d\u8ba2\u4e0b\u9e33\u76df\uff0c\u6211\u5fc3\u4e2d\u624d \u80fd\u4e86\u65e0\u7275\u6302\uff01\u201d \u3000\u3000\u9648\u7384\u673a\u9053\uff1a\u201c\u522b\u6837\u4e8b\u60c5\uff0c\u7c89\u8eab\u788e\u9aa8\u4ea6\u6240\u4e0d\u8f9e\uff0c\u552f\u72ec\u8fd9\u4ef6\u4e8b\u60c5\uff01\u5c0f\u5f1f\u4e07\u4e07\u4e0d\u80fd\u9075\u547d\u3002\u201d\u4e0a \u5b98\u5929\u91ce\u5251\u7709\u4e00\u7ad6\uff0c\u970d\u5730\u62d4\u51fa\u62a4\u624b\u53cc\u94a9\uff0c\u559d\u9053\uff1a\u201c\u6211\u5df2\u7acb\u5fd7\u53bb\u505a\u5f3a\u76d7\uff0c\u4f60\u5bf9\u97f5\u5170\u5982\u6b64\u8d1f\u5fc3\uff0c\u8981 \u5417\u5c31\u662f\u6211\u628a\u4f60\u6740\u4e86\uff0c\u65ad\u4e86\u97f5\u5170\u4e4b\u5ff5\uff0c\u514d\u5f97\u5979\u7ec8\u751f\u53d7\u90a3\u76f8\u601d\u4e4b\u82e6\uff0c\u8981\u5417\u5c31\u662f\u4f60\u628a\u6211\u6740\u4e86\uff0c\u514d\u5f97 \u6211\u4e00\u4e16\u4f24\u5fc3\uff01\u201d \u3000\u3000\u970d\u7684\u4e00\u94a9\u523a\u51fa\uff0c\u9648\u7384\u673a\u7adf\u4e0d\u95ea\u907f\uff0c\u53cd\u800c\u8fce\u4e86\u4e0a\u6765\uff0c\u4e0a\u5b98\u5929\u91ce\u559d\u9053\uff1a\u201c\u4f60\u600e\u4e48\u8fd8\u4e0d\u62d4 \u5251\uff1f\u201d\u9648\u7384\u673a\u9053\uff1a\u201c\u4f46\u613f\u4f60\u4e0e\u97f5\u5170\u80fd\u514d\u4f24\u5fc3\uff0c\u5c0f\u5f1f\u5b81\u613f\u6b7b\u5728\u543e\u5144\u94a9\u4e0b\u3002\u201d\u4e0a\u5b98\u5929\u91ce\u6012\u9053\uff1a \u201c\u4f60\uff0c\u4f60\u5b81\u613f\u6b7b\u4e5f\u4e0d\u8981\u97f5\u5170\uff0c\u4f60\u600e\u7684\u5bf9\u5979\u5982\u6b64\u6ca1\u6709\u5fc3\u809d\uff1f\u201d\u9648\u7384\u673a\u9053\uff1a\u201c\u6211\u7684\u5fc3\u65e9\u5df2\u4ea4\u7ed9\u4e86 \u53e6\u5916\u4e00\u4e2a\u4eba\u4e86\uff0c\u4f60\u53eb\u6211\u62ff\u4ec0\u4e48\u6765\u7ed9\u97f5\u5170\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u5fc3\u5934\u4e00\u9707\uff0c\u9053\uff1a\u201c\u539f\u6765\u4f60\u679c\u7136\u662f\u7ed9\u4e91\u821e\u9633\u7684\u5973\u513f\u8ff7\u4e0a\u4e86\uff0c\u54fc\u54fc\uff0c\u7ed9\u4ec7\u4eba\u7684\u5973\u513f \u8ff7\u4e0a\u4e86\uff01\u201d\u9648\u7384\u673a\u52c3\u7136\u6012\u9053\uff1a\u201c\u4f60\u628a\u6211\u7684\u7d20\u7d20\u770b\u4f5c\u4ec0\u4e48\u4eba\u4e86\u5440\uff0c\u4e0a\u5b98\u5929\u91ce\u5440\u4e0a\u5b98\u5929\u91ce\uff0c\u6211\u539f \u6765\u8fd8\u662f\u628a\u4f60\u770b\u9519\u4e86\uff01\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u9648\u7384\u673a\u9053\uff1a\u201c\u6211\u770b\u4f60\u5bf9\u97f5\u5170\u59d0\u59d0\u7684\u4e00\u7247\u75f4 \u60c5\uff0c\u4e00\u7247\u82e6\u5fc3\uff0c\u6211\u53ea\u9053\u4f60\u662f\u4e00\u4e2a\u61c2\u5f97\u7528\u60c5\u7684\u7537\u5b50\uff0c\u539f\u6765\u4f60\u7adf\u4e0d\u89e3\u60c5\u4e3a\u4f55\u7269\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u5583\u5583 \u5730\u8bf4\uff1a\u201c\u60c5\u4e3a\u4f55\u7269\uff1f\u60c5\u4e3a\u4f55\u7269\uff1f\u201d \u3000\u3000\u773c\u5149\u4e00\u77a5\uff0c\u53ea\u89c1\u9648\u7384\u673a\u795e\u5149\u7115\u53d1\uff0c\u5e26\u7740\u4e00\u79cd\u5f02\u6837\u7684\u6fc0\u60c5\u6ed4\u6ed4\u4e0d\u7edd\u7684\u8bf4\u9053\uff1a\u201c\u60c5\u4e3a\u4f55\u7269\uff1f \u90a3\u5c31\u662f\u628a\u5979\u770b\u5f97\u6bd4\u81ea\u5df1\u7684\u751f\u547d\u8fd8\u91cd\u8981\uff0c\u66f4\u4e0d\u8981\u8bf4\u8ba1\u8f83\u4ec0\u4e48\u6210\u8d25\u8363\u8fb1\u4e86\uff01\u90a3\u662f\u4ee5\u5fc3\u6362\u5fc3\uff0c\u5728\u5f62 \u9ab8\u4e0a\u662f\u4e24\u4e2a\u4eba\uff0c\u5176\u5b9e\u662f\u4e00\u4e2a\u4eba\uff01\u4efb\u6559\u5730\u88c2\u5c71\u5d29\uff0c\u98ce\u4e91\u53d8\u8272\uff0c\u8fd9\u631a\u7231\u771f\u60c5\u603b\u4e0d\u80fd\u4e3a\u5916\u7269\u6240 \u79fb\uff01\u201d\u4e0a\u5b98\u5929\u91ce\u4e00\u7247\u8ff7\u832b\uff0c\u5fc3\u4e2d\u8bf4\u9053\uff1a\u201c\u5440\uff0c\u96be\u9053\u6211\u5bf9\u97f5\u5170\u4e0d\u662f\u8fd9\u6837\uff1f\u201d \u3000\u3000\u53ea\u542c\u5f97\u9648\u7384\u673a\u9053\uff1a\u201c\u6211\u6253\u4ece\u89c1\u7d20\u7d20\u7684\u7b2c\u4e00\u773c\u8d77\uff0c\u6211\u5c31\u628a\u81ea\u5df1\u7684\u5fc3\u4ea4\u7ed9\u5979\u4e86\uff01\u6211\u4ece\u6765\u6ca1\u6709 \u89c1\u8fc7\u4e16\u754c\u4e0a\u6709\u8fd9\u6837\u7eaf\u6d01\u65e0\u90aa\u7684\u5c11\u5973\uff0c\u6709\u8fd9\u6837\u80af\u4e3a\u522b\u4eba\u5fd8\u4e86\u81ea\u5df1\u7684\u5c11\u5973\uff0c\u6211\u628a\u5979\u5c0a\u656c\u5f97\u5982\u540c\u5bf9 \u6211\u7684\u6bcd\u4eb2\uff0c\u53ea\u8981\u6211\u5728\u8fd9\u4e16\u4e0a\u6d3b\u7740\u4e00\u5929\uff0c\u6211\u5c31\u4e0d\u8bb8\u522b\u4eba\u5bf9\u5979\u6709\u534a\u53e5\u4eb5\u6e0e\u7684\u8bdd\u3002\u54fc\uff0c\u4f60\u600e\u80fd\u53eb\u6211 \u820d\u4e86\u5979\u53e6\u7231\u522b\u4eba\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u5583\u5583\u8bf4\u9053\uff1a\u201c\u96be\u9053\u5979\u7adf\u7136\u80dc\u4f3c\u97f5\u5170\uff1f\u201d\u9648\u7384\u673a\u7eb5\u58f0\u5927\u7b11\u9053\uff1a\u201c\u597d\u554a\uff0c\u4f60\u603b\u7b97\u61c2 \u5f97\u4e00\u4e9b\u4e86\uff0c\u6bcf\u4e2a\u4eba\u773c\u4e2d\u7684\u60c5\u4eba\u90fd\u662f\u4e16\u4e0a\u6700\u5b8c\u7f8e\u7684\u5973\u795e\uff0c\u6211\u7231\u7d20\u7d20\u5c31\u50cf\u4f60\u7231\u97f5\u5170\u4e00\u6837\uff0c\u4f60\u61c2\u5f97 \u4e86\u5427\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u5446\u4e86\u4e00\u5446\uff0c\u5ffd\u5730\u63b7\u94a9\u4e8e\u5730\uff0c\u4e00\u628a\u62b1\u7740\u9648\u7384\u673a\u75db\u54ed\u8d77\u6765\uff0c\u9648\u7384\u673a\u60f3\u4e0d\u5230\u8fd9\u7c97\u8c6a\u7684 \u6c49\u5b50\u7adf\u54ed\u5f97\u8fd9\u6837\u4f24\u5fc3\uff0c\u7136\u800c\u5728\u7231\u60c5\u4e2d\u7684\u7537\u5b50\u5fc3\u5ff5\u76f8\u901a\uff0c\u8f6c\u77ac\u4e4b\u95f4\uff0c\u4e0d\u5f85\u7ec6\u601d\uff0c\u4ed6\u5df2\u61c2\u5f97\u4e0a\u5b98 \u5929\u91ce\u8fd9\u4e00\u628a\u4f24\u5fc3\u4e4b\u6cea\u662f\u56e0\u4f55\u800c\u81f3\uff0c\u4ed6\u7d27\u63e1\u4e0a\u5b98\u5929\u91ce\u53cc\u624b\uff0c\u50cf\u5bf9\u5f85\u4eb2\u5144\u5f1f\u4e00\u6837\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u5982 \u679c\u7d20\u7d20\u6b22\u559c\u4e86\u7b2c\u4e8c\u4eba\uff0c\u6211\u4e5f\u4f1a\u50cf\u4f60\u8fd9\u6837\u505a\u7684\u3002\u4e0d\u8fc7\uff0c\u7d20\u7d20\u5979\u4e5f\u771f\u5fc3\u7684\u6b22\u559c\u6211\uff0c\u90a3\u5c31\u6ca1\u6709\u4ec0\u4e48 \u529b\u91cf\u53ef\u4ee5\u62c6\u5f00\u4e86\u3002\u5929\u91ce\uff0c\u4f60\u4e0d\u5fc5\u4e3a\u97f5\u5170\u96be\u8fc7\uff0c\u8fd9\u4e16\u754c\u4e0a\u6ca1\u6709\u4eba\u7231\u5979\u66f4\u80dc\u4e8e\u4f60\uff0c\u53e4\u8bed\u6709\u4e91\uff0c\u7cbe \u8bda\u6240\u81f3\uff0c\u91d1\u77f3\u4e3a\u5f00\uff0c\u97f5\u5170\u603b\u6709\u4e00\u5929\u4f1a\u7ed9\u4f60\u611f\u52a8\u7684\uff0c\u5979\u548c\u4f60\u7ed3\u5408\uff0c\u5bf9\u4f60\u4eec\u4e24\u4eba\u90fd\u662f\u7ec8\u751f\u7684\u5e78 \u798f\u3002\u4f60\u4e0d\u5fc5\u52a8\u4ec0\u4e48\u50bb\u5ff5\u5934\u4e86\u3002\u597d\u5144\u5f1f\uff0c\u542c\u6211\u7684\u8bdd\uff0c\u4f60\u81ea\u5df1\u53bb\u627e\u5979\u5427\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u773c\u6cea\u6e10\u6536\uff0c\u4f46\u4ecd\u662f\u4e00\u7247\u8ff7\u832b\uff0c\u5583\u5583\u8bf4\u9053\uff1a\u201c\u5440\uff0c\u4f60\u4e0d\u77e5\u9053\u97f5\u5170\u7684\u5fc3\u610f\uff0c\u5979\u4e00\u7247 \u771f\u60c5\u5411\u7684\u662f\u4f60\u3002\u8fd9\u53eb\u6211\u600e\u4e48\u529e\u5462\uff1f\u5440\uff0c\u6211\u4e0d\u613f\u62c6\u6563\u4f60\u4eec\u7684\u795e\u4ed9\u7737\u5c5e\uff0c\u6211\u53c8\u4e0d\u613f\u53eb\u97f5\u5170\u59d0\u59d0\u4f24 \u5fc3\u3002\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5ffd\u5730\u542c\u5f97\u6709\u4eba\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u4e24\u4e2a\u50bb\u5c0f\u5b50\u54ed\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e24\u4eba\u5413\u4e86\u4e00\u8df3\uff0c\u970d\u5730\u5206\u5f00\uff0c\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u6211\u54ed\u6211\u7684\uff0c\u4e0e\u4f60\u4f55\u5e72\uff01\u201d\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u8fd9 \u4eba\u5927\u7ea6\u4e94\u5341\u5de6\u53f3\u5e74\u7eaa\uff0c\u8eab\u6750\u9b41\u4f1f\uff0c\u9e70\u9f3b\u6df1\u76ee\uff0c\u70af\u70af\u6709\u795e\uff0c\u597d\u50cf\u4ee5\u524d\u66fe\u5728\u4ec0\u4e48\u5730\u65b9\u89c1\u8fc7\u4f3c\u7684\uff0c \u65b9\u81ea\u4e00\u6014\uff0c\u90a3\u4eba\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u6b66\u5f53\u6d3e\u65b0\u4efb\u7684\u638c\u95e8\u4eba\u4e0a\u5b98\u5929\u91ce\uff0c\u5e74\u7eaa\u8f7b\u8f7b\u8363\u4efb\u638c\u95e8\uff0c \u8fd8\u4e0d\u79f0\u5fc3\u5982\u610f\u4e48\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f\u6211\u505a\u4e0d\u505a\u638c\u95e8\uff0c\u4f60\u7ba1\u4e0d\u7740\uff01\u201d\u90a3\u4eba\u7b11\u9053\uff1a\u201c\u54c8\uff0c\u539f\u6765\u662f\u8fd9 \u6837\uff0c\u60f3\u5fc5\u662f\u667a\u5706\u957f\u8001\u8981\u628a\u4f60\u7684\u638c\u95e8\u4f4d\u5b50\u593a\u8fc7\u6765\u7ed9\u4ed6\u7684\u5f92\u5f1f\u3002\u4f60\u4e0d\u5fc5\u5fc3\u70e6\uff0c\u6211\u4e0e\u4f60\u5e08\u7236\u9887\u6709\u4ea4 \u60c5\uff0c\u6211\u7ed9\u4f60\u6491\u8170\u4fbf\u662f\u3002\u53ea\u8981\u4f60\u4e5f\u5e2e\u5fd9\u6211\u4e00\u4ef6\u4e8b\u60c5\u3002\u201d \u3000\u3000\u4e0a\u5bab\u5929\u91ce\u6781\u4e0d\u8010\u70e6\uff0c\u6b63\u5f85\u53d1\u4f5c\uff0c\u53ea\u89c1\u90a3\u4eba\u54c8\u54c8\u4e00\u7b11\uff0c\u6307\u7740\u9648\u7384\u673a\u8bf4\u9053\uff1a\u201c\u4f60\u628a\u8fd9\u5c0f\u5b50\u7684 \u8eab\u4efd\u6765\u5386\u8bf4\u4e0e\u6211\u542c\uff0c\u4ed6\u662f\u4e0d\u662f\u5949\u4e86\u5468\u516c\u5bc6\u4e4b\u547d\u53bb\u627e\u4e91\u821e\u9633\u7684\u90a3\u4e2a\u9648\u7384\u673a\uff1f\u4f60\u53ef\u77e5\u9053\u4ed6\u548c\u4e91\u821e \u9633\u8bf4\u4e86\u4e9b\u4ec0\u4e48\uff1f\u8fd8\u6709\u4e00\u4e2a\u4eba\u53eb\u505a\u77f3\u5929\u94ce\u7684\u662f\u5426\u4e5f\u66fe\u6765\u627e\u8fc7\u4e91\u821e\u9633\uff1f\u6211\u77e5\u9053\u4f60\u5230\u4e91\u821e\u9633\u5bb6\u4e2d\u6c42 \u7d22\u5251\u8c31\uff0c\u8fd9\u4e24\u65e5\u4f60\u5b9a\u7136\u4f4f\u5728\u4e91\u5bb6\uff0c\u89c1\u4e86\u4e9b\u4ec0\u4e48\uff1f\u542c\u4e86\u4e9b\u4ec0\u4e48\uff1f\u5feb\u5feb\u8bf4\u4e0e\u6211\u542c\uff01\u201d \u3000\u3000\u9648\u7384\u673a\u8fd9\u65f6\u65e9\u5df2\u770b\u6e05\u4e86\u6765\u4eba\u7684\u9762\u76ee\uff0c\u51db\u7136\u4e00\u60ca\uff0c\u6b64\u4eba\u975e\u4ed6\uff0c\u6b63\u662f\u90a3\u665a\u66fe\u4e0e\u4e91\u821e\u9633\u4e00\u540c\u56de \u6765\uff0c\u592e\u6c42\u4e91\u821e\u9633\u66ff\u4ed6\u7fe6\u9664\u5f20\u58eb\u8bda\u65e7\u90e8\u7684\u90a3\u4e2a\u9526\u8863\u536b\u603b\u6307\u6325\u7f57\u91d1\u5cf0\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4ed6\u90a3\u665a\u4e0b\u5c71 \u7684\u65f6\u5019\uff0c\u6b63\u5de7\u5c31\u662f\u77f3\u5929\u94ce\u4e0e\u4e03\u4fee\u9053\u4eba\u7b49\u76f8\u7ee7\u4e0a\u5c71\u4e4b\u65f6\uff0c\u60f3\u5fc5\u662f\u4ed6\u53d1\u73b0\u4e86\u77f3\u5929\u94ce\u7684\u8e2a\u8ff9\uff0c\u5f53\u65f6 \u4e0d\u6562\u51fa\u9762\uff0c\u8fc7\u540e\u4e00\u60f3\uff0c\u53c8\u6015\u4ed6\u4e0e\u4e91\u821e\u9633\u6709\u4ec0\u52fe\u7ed3\uff0c\u6240\u4ee5\u6298\u56de\u6765\u63a2\u542c\u6d88\u606f\u3002\u4f46\u6211\u662f\u4e00\u4e2a\u521d\u51fa\u6c5f \u6e56\u7684\u65e0\u540d\u5c0f\u5352\uff0c\u4ed6\u53c8\u600e\u77e5\u9053\u6211\u7684\u540d\u5b57\uff1f\u9648\u7384\u673a\u8fd9\u4e00\u731c\u731c\u5230\u4e86\u4e00\u534a\uff0c\u53ea\u6709\u4e00\u534a\u731c\u4e0d\u5230\u7684\u662f\uff0c\u4ed6 \u81ea\u5df1\u4e5f\u88ab\u5176\u4ed6\u7684\u5927\u5185\u9ad8\u624b\u6697\u6697\u8ddf\u8e2a\u3002 \u3000\u3000\u539f\u6765\u6731\u5143\u748b\u5bf9\u5f20\u58eb\u8bda\u7684\u65e7\u90e8\u6700\u4e3a\u5fcc\u60ee\uff0c\u9664\u4e86\u6d3e\u51fa\u7f57\u91d1\u5cf0\u62db\u964d\u4e91\u821e\u9633\u4e4b\u5916\uff0c\u53e6\u5916\u8fd8\u6d3e\u6709\u4eba \u660e\u67e5\u6697\u63a2\uff0c\u90a3\u5468\u516c\u5bc6\u4e43\u662f\u5f20\u58eb\u8bda\u5728\u6c5f\u5357\u65e7\u90e8\u7684\u9996\u9886\uff0c\u4ed6\u7684\u5bb6\u4e2d\uff0c\u4fbf\u6709\u4e32\u901a\u671d\u5ef7\u7684\u4eba\u5367\u5e95\uff0c\u9648 \u7384\u673a\u5949\u547d\u51fa\u53d1\u4e4b\u540e\uff0c\u8fd9\u98ce\u58f0\u4fbf\u6cc4\u9732\u51fa\u6765\uff0c\u5e78\u800c\u4ed6\u9a91\u7684\u4e43\u662f\u5b9d\u9a6c\uff0c\u53c8\u65e9\u8d70\u4e86\u4e24\u5929\uff0c\u8fd9\u624d\u4e0d\u81f3\u4e8e \u7ed9\u6731\u5143\u748b\u6d3e\u6765\u7684\u53e6\u5916\u4e09\u4e2a\u5927\u5185\u9ad8\u624b\u8ffd\u4e0a\u3002\u7f57\u91d1\u5cf0\u5c31\u662f\u5728\u4e0b\u5c71\u4e4b\u540e\uff0c\u5728\u5c71\u53e3\u78b0\u5230\u90a3\u4e09\u4e2a\u540c\u4f34\uff0c \u5f97\u77e5\u4e86\u9648\u7384\u673a\u7684\u6d88\u606f\u7684\u3002\u6240\u4ee5\u4ed6\u8fd9\u6b21\u56de\u6765\uff0c\u7b2c\u4e00\u4ef6\u662f\u8981\u63a2\u542c\u77f3\u5929\u94ce\u4e0a\u5c71\u4e4b\u4e8b\uff0c\u7b2c\u4e8c\u4ef6\u4fbf\u8981\u6349 \u62ff\u9648\u7384\u673a\u6765\u62f7\u95ee\u3002 \u3000\u3000\u4e0a\u5b98\u5927\u91ce\u6012\u9053\uff1a\u201c\u51ed\u4ec0\u4e48\u6211\u8981\u8bf4\u4e0e\u4f60\u542c\u3002\u201d\u7f57\u91d1\u5cf0\u9053\uff1a\u201c\u597d\u554a\uff0c\u4f60\u8bb0\u4e0d\u8d77\u6211\u662f\u8c01\u4e86 \u4e48\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u8fd9\u65f6\u5df2\u7ecf\u8bb0\u8d77\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u9526\u8863\u536b\u7684\u603b\u6307\u6325\u7f57\u91d1\u5cf0\uff0c\u6211\u5e08\u7236\u8981\u5356\u4f60\u7684 \u60c5\u9762\uff0c\u6211\u53ef\u4e0d\u5fc5\u5356\u4f60\u7684\u60c5\u9762\u3002\u201d \u3000\u3000\u7f57\u91d1\u5cf0\u7b11\u9053\uff1a\u201c\u4f60\u7684\u638c\u95e8\u4f4d\u5b50\u8fd8\u672a\u5750\u7a33\uff0c\u4f60\u4e0d\u60f3\u6211\u7ed9\u4f60\u6491\u8170\u4e48\uff1f\u4f60\u65e2\u77e5\u6211\u7684\u8eab\u4efd\uff0c\u90a3\u4e48 \u4f60\u77e5\u4e0d\u77e5\u9053\u4f60\u8fd9\u4f4d\u670b\u53cb\u7684\u8eab\u4efd\uff1f\u4ed6\u662f\u5f20\u58eb\u8bda\u65e7\u90e8\u7684\u9057\u5b7d\uff0c\u78b0\u5728\u6211\u7684\u624b\u4e0a\uff0c\u600e\u80fd\u653e\u8fc7\uff1f\u4f60\u82e5\u80af \u628a\u6240\u77e5\u5c3d\u544a\u4e8e\u6211\uff0c\u90a3\u672a\u4f60\u4e0d\u4f46\u4e3a\u671d\u5ef7\u7acb\u4e86\u529f\u52b3\uff0c\u638c\u95e8\u7684\u4f4d\u5b50\u4e5f\u6ca1\u4eba\u6562\u52a8\u4f60\u7684\u4e86\uff0c\u4e00\u4e3e\u4e24\u5f97\uff0c \u5bf9\u4f60\u5c82\u4e0d\u662f\u5929\u5927\u7684\u4fbf\u5b9c\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u6012\u4e0d\u53ef\u6291\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u5484\uff0c\u4f60\u8fd9\u53ae\u5feb\u95ed\u9e1f\u53e3\uff01\u6211\u4e0a\u5b98\u5929\u91ce\u5c82\u662f\u5356\u53cb\u6c42\u8363\u4e4b \u4eba\u3002\u201d\u7f57\u91d1\u5cf0\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u5230\u5e95\u662f\u521d\u51fa\u9053\u7684\u96cf\u513f\uff0c\u4e00\u5957\u4fbf\u7ed9\u6211\u5957\u51fa\u6765\u4e86\u3002\u54c8\uff0c\u4f60\u8fd9\u5c0f\u5b50 \u679c\u7136\u4fbf\u662f\u9648\u7384\u673a\uff1f\u201d\u9648\u7384\u673a\u9053\uff1a\u201c\u662f\u6211\u4fbf\u600e\u6837\uff1f\u6709\u8bdd\u5c3d\u7ba1\u95ee\u6211\u3002\u4e0a\u5b98\u5144\uff0c\u4e8b\u60c5\u4e0e\u4f60\u65e0\u5173\uff0c\u8d76 \u5feb\u8d70\u5427\uff01\u201d\u9648\u7384\u673a\u77e5\u9053\u7f57\u91d1\u5cf0\u4e43\u662f\u6731\u5143\u748b\u624b\u4e0b\u7684\u7b2c\u4e00\u9ad8\u624b\uff0c\u4e91\u821e\u9633\u5bf9\u4ed6\u4ea6\u751a\u63a8\u5d07\uff0c\u53ea\u6015\u6b66\u529f \u4e0d\u5728\u4e91\u821e\u9633\u4e4b\u4e0b\u3002\u4ed6\u628a\u4e8b\u60c5\u5305\u63fd\u8fc7\u6765\uff0c\u4e43\u662f\u6709\u610f\u5c06\u4e0a\u5b98\u5929\u91ce\u5f00\u8131\u3002 \u3000\u3000\u5c82\u6599\u7f57\u91d1\u5cf0\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u5c31\u4e0d\u4fe1\u4f60\u8fd9\u5c0f\u5b50\u4f1a\u8bf4\u5b9e\u8bdd\u3002\u4e0a\u5b98\u5929\u91ce\u4f60\u518d\u4e09\u601d\uff0c\u4e3a\u4e86\u4f60 \u7684\u9526\u7ee3\u524d\u7a0b\uff0c\u6211\u4fe1\u4f60\u4e0d\u4f1a\u5bf9\u6211\u8bf4\u5047\u3002\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u53ea\u89c1\u4e0a\u5b98\u5929\u91ce\u5df2\u62fe\u8d77\u5730\u4e0a\u7684\u53cc\u94a9\uff0c\u6717\u58f0\u8bf4 \u9053\uff1a\u201c\u5927\u4e08\u592b\u5c82\u80fd\u53d7\u4eba\u6c61\u8fb1\uff0c\u8fd9\u53ae\u628a\u6211\u5f53\u505a\u5356\u53cb\u6c42\u8363\u7684\u5c0f\u4eba\uff0c\u8fb1\u6211\u592a\u751a\uff0c\u6211\u975e\u4e0e\u4ed6\u62fc\u547d\u4e0d \u53ef\uff01\u7384\u673a\u5144\uff0c\u4f60\u6709\u91cd\u4efb\u5728\u8eab\uff0c\u4f60\u8d70\u4e86\u5427\u3002\u201d \u3000\u3000\u7f57\u91d1\u5cf0\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u591f\u670b\u53cb\uff0c\u591f\u4e49\u6c14\uff01\u4e24\u4e2a\u5c0f\u5b50\u90fd\u4e89\u7740\u8981\u6765\u9001\u6b7b\u3002\u4e0d\u5fc5\u4e89\u554a\uff0c\u4f60\u4eec\u4e24\u4e2a \u90fd\u8d70\u4e0d\u4e86\uff01\u201d\u53cc\u638c\u4e00\u62cd\uff0c\u5de6\u624b\u6293\u9648\u7384\u673a\uff0c\u53f3\u624b\u6293\u4e0a\u5b98\u5929\u91ce\uff0c\u7acb\u5fc3\u8981\u628a\u4ed6\u4eec\u4e24\u4eba\u5168\u90fd\u6349\u4f4f\uff0c\u4e25 \u5211\u62f7\u6253\uff0c\u5bf9\u8bc1\u53e3\u4f9b\u3002 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u53cc\u94a9\u5148\u51fa\uff0c\u4f46\u542c\u5f97\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u7f57\u91d1\u5cf0\u5de6\u638c\u4e00\u62cd\uff0c\u53cc\u94a9\u53cd\u5f39\u56de\u6765\uff0c\u638c\u7f18\u626b\u5230 \u4e86\u4e0a\u5b98\u5929\u91ce\u7684\u80f8\u53e3\uff0c\u4e0d\u6599\u4e0a\u5b98\u5929\u91ce\u52c7\u731b\u4e4b\u6781\uff0c\u4e0d\u9000\u53cd\u8fdb\uff0c\u53cc\u94a9\u4e00\u4e2a\u4ea4\u53c9\uff0c\u526a\u4ed6\u624b\u8155\uff0c\u7f57\u91d1\u5cf0 \u8fd9\u638c\u529b\u82e5\u7136\u7528\u5b9e\uff0c\u4e0a\u5b98\u5929\u91ce\u7684\u80f8\u9aa8\u4fbf\u8981\u7acb\u65f6\u788e\u88c2\u3002\u4f46\u4e0a\u5b98\u5929\u91ce\u8fd9\u4e00\u62fc\u547d\uff0c\u5374\u53cd\u6559\u7f57\u91d1\u5cf0\u8e0c\u8e87 \u4e86\uff0c\u987b\u77e5\u4e0a\u5b98\u5929\u91ce\u5230\u5e95\u8fd8\u662f\u6b66\u5f53\u6d3e\u7684\u638c\u95e8\u4eba\uff0c\u7f57\u91d1\u5cf0\u82e5\u662f\u5c06\u4ed6\u6bd9\u4e86\uff0c\u53ef\u5c31\u8981\u7ed3\u4e0b\u5929\u5927\u7684\u51a4 \u4ec7\uff0c\u4f55\u51b5\u4ed6\u672c\u610f\u53ea\u662f\u60f3\u628a\u4e0a\u5b98\u5929\u91ce\u6349\u4f4f\uff0c\u60f3\u4ece\u4ed6\u7684\u53e3\u4e2d\uff0c\u63a2\u51fa\u9648\u7384\u673a\u7684\u79d8\u5bc6\uff0c\u4f5c\u4e3a\u65c1\u8bc1\u3002\u90a3 \u4e00\u638c\u867d\u662f\u6740\u624b\uff0c\u5176\u5b9e\u4e0d\u8fc7\u662f\u7528\u4f5c\u5a01\u80c1\u800c\u5df2\uff0c\u60f3\u4e0d\u5230\u4e0a\u5b98\u5929\u91ce\u7adf\u4e0d\u754f\u6b7b\uff0c\u7adf\u7136\u8981\u62fc\u4e2a\u4e24\u8d25\u4ff1 \u4f24\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5c31\u5728\u7f57\u91d1\u5cf0\u8fd9\u7a0d\u4e00\u8e0c\u8e87\u4e4b\u9645\uff0c\u9648\u7384\u673a\u5251\u8bc0\u4e00\u9886\uff0c\u907f\u5f00\u4e86\u7f57\u91d1\u5cf0\u7684\u53f3 \u638c\uff0c\u5251\u8d70\u8f7b\u7075\uff0c\u53cd\u523a\u56de\u6765\uff0c\u4f46\u542c\u5f97\u201c\u5580\u5693\u201d\u4e00\u58f0\uff0c\u7f57\u91d1\u5cf0\u7f29\u624b\u4e0d\u8fed\uff0c\u5de6\u8fb9\u7684\u8863\u8896\uff0c\u5df2\u7ed9\u4e0a\u5b98 \u5929\u91ce\u7684\u53cc\u94a9\u526a\u4e86\u4e00\u6bb5\u3002 \u3000\u3000\u7f57\u91d1\u5cf0\u9762\u8272\u94c1\u9752\uff0c\u559d\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8fd9\u4e24\u4e2a\u5c0f\u5b50\u8981\u6b7b\uff0c\u8001\u7237\u504f\u504f\u8981\u53eb\u4f60\u4eec\u6c42\u751f\u4e0d\u5f97\uff0c\u6c42\u6b7b \u4e0d\u80fd\uff01\u201d\u8863\u8896\u4e00\u62c2\uff0c\u5c06\u9648\u7384\u673a\u7684\u9752\u94a2\u5251\u62c2\u5f00\uff0c\u4e09\u6307\u84e6\u7136\u4e00\u4f38\uff0c\u6b3a\u8eab\u76f4\u5165\uff0c\u6765\u6263\u9648\u7384\u673a\u7684\u8109 \u95e8\uff0c\u8fd9\u4e00\u62db\u4f7f\u5f97\u9634\u6bd2\u65e0\u6bd4\uff0c\u4e0a\u5b98\u5929\u91ce\u63f4\u6551\u4e0d\u53ca\uff0c\u53f1\u54a4\u4e00\u58f0\uff0c\u5de6\u624b\u4e00\u626c\uff0c\u91d1\u94a9\u8131\u624b\u98de\u51fa\uff0c\u5316\u4f5c \u4e86\u4e00\u9053\u957f\u8679\uff0c\u5c04\u5411\u7f57\u91d1\u5cf0\u7684\u540e\u5fc3\uff0c\u7f57\u91d1\u5cf0\u5927\u6012\uff0c\u53cd\u624b\u4e00\u63a5\uff0c\u5c06\u91d1\u94a9\u6293\u7740\uff0c\u559d\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u4e0d \u77e5\u6b7b\u6d3b\u7684\u5c0f\u5b50\uff01\u201d\u624b\u8155\u4e00\u6296\uff0c\u53ea\u542c\u5f97\u201c\u5361\u5587\u201d\u4e00\u58f0\uff0c\u90a3\u91d1\u94a9\u7adf\u81ea\u88ab\u4ed6\u7684\u5185\u5bb6\u771f\u529b\u9707\u65ad\uff0c\u53ef\u662f \u5982\u6b64\u4e00\u6765\uff0c\u9648\u7384\u673a\u4e5f\u5df2\u8131\u51fa\u9669\u5883\uff0c\u4f46\u89c1\u7f57\u91d1\u5cf0\u4e5f\u53f1\u54a4\u4e00\u58f0\uff0c\u90a3\u534a\u622a\u65ad\u94a9\uff0c\u631f\u7740\u4e00\u6e9c\u91d1\u5149\uff0c\u5411 \u4e0a\u5b98\u5929\u91ce\u98de\u53bb\u3002\u542c\u8fd9\u6697\u7bad\u5636\u98ce\u4e4b\u58f0\uff0c\u52b2\u9053\u4e4b\u5f3a\uff0c\u7edd\u975e\u4e0a\u5b98\u5929\u91ce\u7684\u529f\u529b\u6240\u53ef\u62b5\u6321\uff01 \u3000\u3000\u9648\u7384\u673a\u98de\u8eab\u63a0\u8d77\uff0c\u4e00\u5251\u5288\u4e0b\uff0c\u53ea\u542c\u5f97\u53ee\u5f53\u4e00\u58f0\uff0c\u706b\u82b1\u56db\u6e85\uff0c\u90a3\u534a\u622a\u91d1\u94a9\uff0c\u867d\u7ed9\u6253\u843d\uff0c\u4f46 \u9648\u7384\u673a\u7684\u9752\u94a2\u5251\u4e0a\u4e5f\u7f3a\u4e86\u4e00\u4e2a\u5f88\u5927\u7684\u7f3a\u53e3\uff0c\u864e\u53e3\u9707\u88c2\u6d41\u8840\uff01\u4e0a\u5b98\u5929\u91ce\u89c1\u4e86\uff0c\u4e5f\u4e0d\u7531\u5f97\u6697\u6697\u5fc3 \u60ca\uff1a\u201c\u82e5\u4e0d\u662f\u9648\u7384\u673a\u8fd9\u4e00\u5251\u683c\u5f00\uff0c\u53ea\u6015\u6211\u5c31\u8981\u7ed9\u81ea\u5df1\u7684\u5175\u5203\u7a7f\u5fc3\u800c\u8fc7\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4e24\u4e2a\u65e2\u662f\u77e5\u5df1\u53c8\u662f\u201c\u51a4\u5bb6\u201d\u7684\u5c0f\u4f19\u5b50\uff0c\u5f7c\u6b64\u611f\u6fc0\uff0c\u4e92\u76f8\u6551\u62a4\uff0c\u5747\u662f\u594b\u4e0d\u987e\u8eab\uff1b\u9648\u7384\u673a \u7684\u5251\u6cd5\u517c\u5404\u5bb6\u4e4b\u957f\uff0c\u4e0a\u5b98\u5929\u91ce\u5de6\u94a9\u53f3\u638c\uff0c\u91d1\u94a9\u9501\u3001\u523a\u3001\u52fe\u3001\u526a\uff0c\u638c\u6cd5\u6c89\u539a\u7ef5\u5bc6\uff0c\u4e5f\u662f\u725f\u72ec\u9038 \u7684\u4e0d\u4f20\u4e4b\u79d8\uff0c\u5a01\u529b\u4e0d\u51cf\u4e8e\u53cc\u94a9\u540c\u4f7f\u4e4b\u65f6\uff0c\u4e24\u4eba\u540c\u5fc3\u5408\u529b\uff0c\u4f46\u89c1\u5251\u6c14\u5982\u8679\uff0c\u94a9\u5149\u80dc\u96ea\uff0c\u53c8\u63a5\u4e86 \u7f57\u91d1\u5cf0\u7684\u5341\u6765\u4e8c\u5341\u62db\u3002 \u3000\u3000\u7f57\u91d1\u5cf0\u4e43\u662f\u5927\u5185\u7684\u7b2c\u4e00\u9ad8\u624b\uff0c\u4e0d\u610f\u7adf\u88ab\u4e24\u4e2a\u540e\u751f\u5c0f\u5b50\uff0c\u63a5\u4e86\u4e8c\u5341\u6765\u62db\uff0c\u5fc3\u5934\u5927\u6012\uff0c\u6740\u673a \u9661\u751f\uff0c\u970e\u7136\u95f4\u638c\u6cd5\u9aa4\u53d8\uff0c\u8fc5\u5982\u75be\u98ce\u9aa4\u96e8\uff0c\u638c\u5288\u6307\u6233\uff0c\u5176\u4e2d\u8fd8\u5939\u6742\u7740\u5200\u5251\u7684\u8def\u6570\uff0c\u7adf\u5728\u94a9\u5149\u5251 \u5f71\u4e4b\u4e2d\uff0c\u7740\u7740\u62a2\u653b\uff0c\u800c\u4e14\u4ed6\u90a3\u638c\u529b\u5df2\u5230\u4e86\u8f7b\u91cd\u968f\u5fc3\u7684\u5730\u6b65\uff0c\u5bf9\u4e0a\u5bab\u5929\u91ce\u8fd8\u7a0d\u7a0d\u7559\u60c5\uff0c\u5bf9\u9648\u7384 \u673a\u5374\u662f\u8fde\u4e0b\u6740\u624b\uff01\u4e0d\u8fc7\u6570\u62db\uff0c\u53ea\u89c1\u4ed6\u5de6\u638c\u4e00\u62db\u201c\u4eba\u9694\u5929\u6cb3\u201d\uff0c\u5c06\u4e0a\u5b98\u5929\u91ce\u62e6\u5728\u5916\u95e8\uff0c\u53f3\u638c\u4e00 \u62db\u201c\u4e94\u4e01\u5f00\u5c71\u201d\uff0c\u4e94\u6307\u6210\u94a9\uff0c\u500f\u7684\u4fbf\u5411\u9648\u7384\u673a\u80a9\u5934\u6293\u4e0b\uff0c\u53ea\u51ed\u8fd9\u4e00\u6293\u5c31\u8981\u6293\u88c2\u9648\u7384\u673a\u7684\u7435\u7436 \u8f6f\u9aa8\uff0c\u5e9f\u6389\u4ed6\u7684\u6b66\u529f\u3002 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u5927\u4e3a\u7740\u6025\uff0c\u6325\u94a9\u6025\u523a\uff0c\u4f46\u89c9\u7f57\u91d1\u5cf0\u7684\u638c\u529b\u91cd\u5982\u5c71\u5cb3\uff0c\u4e0a\u5b98\u5929\u91ce\u72c2\u51b2\u731b\u6253\uff0c\u7adf\u81ea \u8fdb\u4e0d\u4e86\u5206\u6beb\uff0c\u4e0a\u5b98\u5929\u91ce\u6025\u6012\u653b\u5fc3\uff0c\u731b\u5730\u4e00\u58f0\u5927\u559d\uff0c\u4f7f\u5c3d\u5403\u4e73\u4e4b\u529b\uff0c\u5c06\u4ec5\u5269\u4e0b\u7684\u4e00\u67c4\u91d1\u94a9\uff0c\u53c8 \u518d\u8131\u624b\u63b7\u51fa\uff0c\u8fd9\u65f6\u7f57\u91d1\u5cf0\u7684\u4e94\u6307\u521a\u521a\u6cbe\u5230\u9648\u7384\u673a\u7684\u80a9\u5934\uff0c\u731b\u542c\u5f97\u91d1\u94a9\u7834\u7a7a\u4e4b\u58f0\uff0c\u4e5f\u4e0d\u7531\u5f97\u7a0d \u68a2\u79fb\u5f00\uff0c\u8ba9\u8fc7\u4e86\u91d1\u94a9\u7684\u6765\u52bf\uff0c\u9648\u7384\u673a\u8d81\u8fd9\u65f6\u673a\uff0c\u80a9\u5934\u4e00\u6c89\uff0c\u907f\u5f00\u4e86\u4ed6\u7684\u4e00\u6293\uff0c\u4e58\u673a\u4e00\u62db\u201c\u4e3e \u706b\u71ce\u5929\u201d\uff0c\u5251\u950b\u81ea\u4e0b\u53cd\u524a\u800c\u4e0a\u3002 \u3000\u3000\u4f46\u542c\u5f97\u201c\u5494\u5693\u201d\u4e00\u58f0\uff0c\u7f57\u91d1\u5cf0\u6293\u7740\u4e86\u94a9\u67c4\uff0c\u53ea\u4e00\u6296\uff0c\u90a3\u91d1\u94a9\u53c8\u65ad\u4e3a\u4e24\u622a\uff0c\u4f46\u89c1\u4ed6\u5de6\u638c\u5f80 \u5916\u4e00\u51fb\uff0c\u638c\u529b\u4e00\u5410\uff0c\u4e0a\u5b98\u5929\u91ce\u5927\u53eb\u4e00\u58f0\uff0c\u683d\u5012\u5730\u4e0a\uff0c\u7adf\u7136\u6655\u4e86\u8fc7\u53bb\u3002\u9648\u7384\u673a\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f \u53ef\uff0c\u90a3\u4e00\u62db\u201c\u4e3e\u706b\u64a9\u5929\u201d\u8fd8\u672a\u4f7f\u8db3\uff0c\u7f57\u91d1\u5cf0\u53cc\u6307\u4e00\u4f38\uff0c\u5df2\u628a\u4ed6\u7684\u5251\u810a\u94b3\u4f4f\uff0c\u534a\u622a\u91d1\u94a9\u4e00\u4e3e\uff0c \u5c31\u5411\u4ed6\u7684\u80f8\u53e3\u201c\u671f\u95e8\u7a74\u201d\u6233\u4e0b\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u5371\u9669\u4e07\u5206\u4e4b\u9645\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u51b7\u7b11\uff0c\u7adf\u662f\u4e00\u4e2a\u5973\u4eba\u7684\u58f0\u97f3\uff0c\u5a07\u58f0\u65a5\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u6562 \u5728\u6211\u4e91\u5bb6\u7684\u95e8\u524d\u653e\u6063\uff1f\u201d\u5f53\u7684\u4e00\u58f0\uff0c\u4e00\u7c92\u77f3\u5b50\u7a81\u7136\u98de\u6765\uff0c\u5c06\u7f57\u91d1\u5cf0\u90a3\u534a\u622a\u91d1\u94a9\u6253\u5f97\u6b6a\u4e86\u51c6 \u5934\uff0c\u7f57\u91d1\u5cf0\u5927\u5403\u4e00\u60ca\uff0c\u5b9a\u775b\u4e00\u770b\uff0c\u53eb\u9053\uff1a\u201c\u4e91\u5ac2\u5b50\uff0c\u8fd9\u5c0f\u5b50\u53ef\u662f\u60f3\u523a\u6740\u4e91\u5927\u54e5\u7684\u523a\u5ba2\u554a\uff01\u201d \u3000\u3000\u9648\u7384\u673a\u7a81\u7136\u8131\u9669\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u89c1\u6765\u7684\u7adf\u7136\u662f\u4e91\u592b\u4eba\uff0c\u51e0\u4e4e\u7591\u5fc3\u662f\u68a6\u4e2d\uff0c\u4f46\u89c1\u4e91\u592b\u4eba\u67f3\u7709 \u5012\u7ad6\uff0c\u8138\u4e0a\u4ecd\u50cf\u524d\u665a\u90a3\u6837\u7684\u5fe7\u90c1\uff0c\u5374\u591a\u4e86\u51e0\u5206\u6012\u6c14\u3002\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u7ba1\u4ed6\u662f\u8c01\u3002\u5c31\u662f\u4e0d\u51c6 \u4f60\u5728\u6211\u7684\u8ddf\u524d\u4e0b\u624b\uff01\u201d \u3000\u3000\u7f57\u91d1\u5cf0\u6115\u7136\u53d8\u8272\uff0c\u5ffd\u5730\u4ef0\u5929\u7b11\u9053\uff1a\u201c\u6211\u53ea\u9053\u4ed6\u662f\u4e91\u5927\u54e5\u7684\u4ec7\u4eba\uff0c\u5374\u539f\u6765\u5ac2\u5b50\u5bf9\u4ed6\u5982\u6b64\u5e87 \u62a4\uff0c\u90a3\u4e48\uff0c\u8fd9\u5012\u7b97\u662f\u6211\u7f57\u67d0\u4eba\u591a\u4e8b\u4e86\uff01\u201d\u7b11\u58f0\u672a\u7edd\uff0c\u4eba\u5f71\u5df2\u6d88\u901d\u5728\u4e1b\u6797\u8302\u8349\u4e4b\u4e2d\u3002 \u3000\u3000\u4e91\u592b\u4eba\u773c\u73e0\u4e00\u8f6c\uff0c\u4f18\u90c1\u7684\u8138\u8272\u7a0d\u7a0d\u5f00\u6717\uff0c\u9732\u51fa\u4e00\u6735\u6de1\u6de1\u7684\u7b11\u5bb9\uff0c\u597d\u50cf\u5e7d\u8c37\u4e2d\u7efd\u5f00\u7684\u767e \u5408\uff0c\u773c\u5149\u6ce8\u5c04\u5230\u9648\u7384\u673a\u7684\u8eab\u4e0a\uff0c\u900f\u51fa\u4e00\u70b9\u559c\u60a6\u7684\u5149\u8f89\uff0c\u5fae\u7b11\u95ee\u9053\uff1a\u201c\u4f60\u5c31\u662f\u9648\u7384\u673a\u4e48\uff1f\u201d \u3000\u3000\u9648\u7384\u673a\u6b63\u81ea\u5728\u8ff7\u60d8\u4e4b\u4e2d\uff0c\u88ab\u5979\u4e00\u95ee\uff0c\u970d\u7136\u60ca\u9192\uff0c\u7b54\u9053\uff1a\u201c\u6b63\u662f\u3002\u55ef\uff0c\u4e91\u592b\u4eba\uff0c\u4f60\u56de\u6765 \u4e86\uff1f\u201d\u8bdd\u8bf4\u51fa\u53e3\uff0c\u8fd9\u624d\u611f\u5230\u5931\u8a00\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4e91\u592b\u4eba\u5f03\u5bb6\u51fa\u8d70\uff0c\u4e00\u5b9a\u5f88\u662f\u4f24\u5fc3\uff0c\u4f24\u5fc3\u4e4b \u4e8b\uff0c\u6700\u6015\u522b\u4eba\u63d0\u8d77\uff0c\u6211\u8fd9\u8bf4\u8bdd\uff0c\u4e0d\u662f\u9732\u51fa\u4e86\u6211\u77e5\u9053\u5979\u7684\u9690\u60c5\u4e48\uff1f\u201d \u3000\u3000\u4e91\u592b\u4eba\u5374\u4f3c\u4e0d\u4ee5\u4e3a\u610f\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u56de\u6765\u4e86\uff0c\u6211\u662f\u4e3a\u7d20\u7d20\u56de\u6765\u7684\u3002\u89c1\u4e86\u4f60\uff0c\u6211 \u7684\u5fc3\u4e8b\u653e\u4e0b\u4e00\u534a\u4e86\u3002\u201d\u9648\u7384\u673a\u5fc3\u5934\u4e0a\u8df3\uff0c\u53ea\u542c\u5f97\u4e91\u592b\u4eba\u7eed\u9053\uff1a\u201c\u4f60\u548c\u4e0a\u5b98\u5929\u91ce\u6240\u8bf4\u7684\u8bdd\u6211\u90fd \u542c\u89c1\u5566\uff0c\u4f60\u771f\u662f\u8fd9\u6837\u7684\u7231\u7d20\u7d20\u4e48\uff1f\u201d\u9648\u7384\u673a\u9053\uff1a\u201c\u6211\u548c\u7d20\u7d20\u8ba4\u8bc6\u7684\u65e5\u5b50\u867d\u7136\u4e0d\u591a\uff0c\u4f46\u6211\u5df2\u611f \u5230\u5979\u50cf\u6211\u81f3\u4eb2\u81f3\u8fd1\u7684\u4eba\u3002\u6211\u7231\u5979\u8d85\u8fc7\u6211\u81ea\u5df1\uff01\u201d\u4e91\u592b\u4eba\u9053\uff1a\u201c\u7f18\u4efd\u4e8c\u5b57\uff0c\u771f\u662f\u795e\u5947\uff0c\u7d20\u7d20\u5bf9 \u6211\u867d\u7136\u6ca1\u6709\u660e\u8a00\uff0c\u505a\u6bcd\u4eb2\u7684\u4e5f\u603b\u4f1a\u611f\u5230\u5979\u5fc3\u4e2d\u7684\u60c5\u610f\uff0c\u6211\u770b\u5979\u7231\u4f60\u53ea\u6709\u66f4\u6df1\uff0c\u6211\u542c\u8fc7\u5979\u5728\u68a6 \u4e2d\u547c\u5524\u4f60\u7684\u540d\u5b57\uff01\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u4e00\u7f15\u6b4c\u58f0\u4ece\u5c71\u5dc5\u4e0a\u4f20\u4e0b\u6765\uff0c\u58f0\u82e5\u6e38\u4e1d\u8885\u7a7a\uff0c\u9690\u7ea6\u53ef\u8fa8\uff0c\u6b63\u662f\u4e91\u7d20\u7d20\u66fe\u4e3a\u9648\u7384 \u673a\u5f39\u594f\u8fc7\u7684\u90a3\u4e24\u8282\u8bd7\u7ecf\uff0c\u90a3\u611f\u4eba\u80ba\u8151\u7684\u60dc\u522b\u76f8\u5fc6\u7684\u8bd7\u7bc7\u53c8\u4e00\u6b21\u7684\u4ece\u5c71\u5cf0\u4e0a\u98d8\u4e0b\u6765\uff1a\u201c\u768e\u768e\u767d \u9a79\uff0c\u98df\u6211\u573a\u82d7\uff0c\u631a\u4e4b\u7ef4\u4e4b\uff0c\u4ee5\u6c38\u4eca\u671d\u3002\u6240\u8c13\u4f0a\u4eba\uff0c\u4e8e\u7109\u900d\u9065\u3002 \u3000\u3000\u201c\u768e\u768e\u767d\u9a79\uff0c\u5728\u5f7c\u7a7a\u8c37\uff0c\u751f\u520d\u4e00\u675f\uff0c\u5176\u4eba\u5982\u7389\u3002\u6bcb\u91d1\u7389\u5c14\u97f3\uff0c\u800c\u6709\u9057\u5fc3\uff01\u201d\u9648\u7384\u673a\u542c\u5f97 \u5fc3\u795e\u4ff1\u9189\uff0c\u6cea\u73e0\u6ef4\u4e86\u4e0b\u6765\uff0c\u4e5f\u4e0d\u77e5\u662f\u559c\u6781\u800c\u6ce3\uff0c\u8fd8\u662f\u522b\u6709\u611f\u4f24\uff0c\u4f46\u542c\u5f97\u6b4c\u58f0\u98d8\u6563\u6797\u4e2d\uff0c\u8fa8\u4e0d \u51fa\u6b4c\u58f0\u7684\u6765\u5904\u3002 \u3000\u3000\u4e91\u592b\u4eba\u5446\u5446\u7684\u51fa\u4e86\u4e00\u4f1a\u795e\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u7d20\u7d20\u5bf9\u4f60\u7684\u601d\u5ff5\u7adf\u662f\u5982\u6b64\u4e4b\u6df1\uff01\u5979\u5728\u627e\u4f60\uff0c\u53ef \u60dc\u5979\u8d70\u9519\u65b9\u5411\u4e86\uff0c\u542c\u8fd9\u6b4c\u58f0\uff0c\u5979\u8d70\u5230\u4e0e\u54b1\u4eec\u76f8\u53cd\u7684\u65b9\u5411\u53bb\u4e86\u3002\u4e0d\u8fc7\uff0c\u4e5f\u4e0d\u8981\u7d27\uff0c\u5979\u627e\u4f60\u4e0d \u7740\uff0c\u603b\u4f1a\u56de\u8f6c\u5bb6\u4e2d\u3002\u201d\u6b47\u4e86\u4e00\u6b47\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u6211\u672c\u6765\u4e0d\u613f\u518d\u89c1\u821e\u9633\uff0c\u4e3a\u4e86\u7d20\u7d20\u4e3a\u4e86\u4f60\uff0c\u6211 \u5c31\u4e3a\u4f60\u4eec\u518d\u53bb\u89c1\u4ed6\u4e00\u6b21\u3002\u55ef\uff0c\u4f60\u8ddf\u6211\u8d70\u5427\u3002\u201d\u9648\u7384\u673a\u521a\u8e0f\u51fa\u4e00\u6b65\uff0c\u53c8\u7f29\u4e86\u56de\u6765\uff0c\u6447\u6447\u5934\u9053\uff1a \u201c\u6211\u4e0d\u80fd\u8d70\u3002\u201d\u4e91\u592b\u4eba\u968f\u7740\u4ed6\u7684\u76ee\u5149\u6240\u6ce8\uff0c\u4f46\u89c1\u4e0a\u5b98\u5929\u91ce\u4ecd\u8eba\u5728\u5730\u4e0a\uff0c\u6655\u8ff7\u672a\u9192\u3002 \u3000\u3000\u4e91\u592b\u4eba\u9053\uff1a\u201c\u4f60\u820d\u4e0d\u5f97\u79bb\u4ed6\u800c\u53bb\uff1f\u4e0d\u9519\uff0c\u6211\u5c31\u662f\u6b22\u559c\u50cf\u4f60\u8fd9\u6837\u7684\u6027\u60c5\u4e2d\u4eba\uff0c\u6211\u653e\u5fc3\u5c06\u7d20 \u7d20\u4ea4\u6258\u7ed9\u4f60\u4e86\u3002\u4e5f\u597d\uff0c\u6211\u5c31\u72ec\u81ea\u53bb\u89c1\u821e\u9633\uff0c\u4f60\u8fd9\u4f4d\u670b\u53cb\u4e5f\u5f88\u597d\uff0c\u5f85\u4ed6\u9192\u6765\u4e4b\u540e\uff0c\u4f60\u548c\u4ed6\u4e00\u8d77 \u6765\u5427\u3002\u201d\u542c\u5979\u8fd9\u8bdd\uff0c\u8bf4\u5f97\u6781\u662f\u5bfb\u5e38\uff0c\u7adf\u4f3c\u628a\u4e0a\u5b98\u5929\u91ce\u7684\u4f24\u52bf\u5e76\u4e0d\u5f53\u4f5c\u4e00\u56de\u4e8b\u513f\u3002\u9648\u7384\u673a\u5f85\u5979 \u4e00\u8d70\uff0c\u6025\u5fd9\u53bb\u770b\u4e0a\u5b98\u5929\u91ce\uff0c\u5374\u89c1\u4ed6\u53cc\u76ee\u7d27\u95ed\uff0c\u53ea\u6709\u4e00\u70b9\u8f7b\u5fae\u7684\u9f3b\u606f\u3002 \u3000\u3000\u518d\u629a\u8109\u606f\uff0c\u7ec6\u82e5\u6e38\u4e1d\uff0c\u800c\u4e14\u4e00\u957f\u4e00\u77ed\uff0c\u6df7\u4e71\u65e0\u5ea6\uff0c\u51f6\u8c61\u6bd5\u9732\u3002\u9648\u7384\u673a\u653e\u58f0\u54ed\u9053\uff1a\u201c\u4e0a\u5b98 \u5144\uff0c\u662f\u5c0f\u5f1f\u7d2f\u4e86\u4f60\u4e86\uff01\u201d\u62b1\u7740\u4ed6\u7684\u8eaf\u4f53\u4e71\u6447\uff0c\u987f\u8db3\u558a\u9053\uff1a\u201c\u82cd\u5929\u65e0\u773c\uff0c\u591a\u5c11\u574f\u4eba\u4e0d\u6b7b\uff0c\u5374\u504f \u504f\u8981\u593a\u8d70\u6211\u7684\u4e0a\u5b98\u5144\u5f1f\uff01\u201d\u60f3\u8d77\u4e0a\u5b98\u5929\u91ce\u82f1\u5e74\u8c6a\u8fc8\uff0c\u809d\u80c6\u7167\u4eba\uff0c\u54ed\u5f97\u8d8a\u53d1\u4f24\u5fc3\u4e86\u3002 \u3000\u3000\u84e6\u7136\u95f4\u5ffd\u89c1\u4e0a\u5b98\u5929\u91ce\u53cc\u773c\u4e00\u5f20\uff0c\u8df3\u4e86\u8d77\u6765\uff0c\u6012\u58f0\u53eb\u9053\uff1a\u201c\u597d\u5440\uff0c\u7384\u673a\u4f60\u8fd9\u5c0f\u5b50\uff0c\u4e3a\u4ec0\u4e48 \u8981\u5492\u6211\u6b7b\uff1f\u201d\u9648\u7384\u673a\u5413\u4e86\u4e00\u8df3\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u72c2\u559c\u53eb\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u6b7b\uff1f\u4f60\u6ca1\u6709\u6b7b\uff01\u201d\u4e0a\u5b98\u5929\u91ce \u9053\uff1a\u201c\u6211\u5f53\u7136\u6ca1\u6709\u6b7b\uff0c\u4f60\u54ed\u4ec0\u4e48\uff1f\u201d\u9648\u7384\u673a\u7834\u6d95\u4e3a\u7b11\uff0c\u5411\u5929\u957f\u63d6\uff0c\u7b11\u9053\uff1a\u201c\u591a\u8c22\u82cd\u5929\uff0c\u6211\u9519 \u602a\u4f60\u4e86\u3002\u201d \u3000\u3000\u539f\u6765\u7f57\u91d1\u5cf0\u7684\u638c\u529b\u8fd0\u7528\u795e\u5999\uff0c\u63a7\u5236\u968f\u5fc3\uff0c\u4ed6\u6253\u4e0a\u5b98\u5929\u91ce\u90a3\u4e00\u638c\uff0c\u51fa\u624b\u867d\u7136\u51f6\u731b\u65e0\u4f26\uff0c\u5176 \u5b9e\u4ed6\u54ea\u91cc\u6562\u628a\u4e0a\u5b98\u5929\u91ce\u6253\u6b7b\uff0c\u638c\u950b\u4e00\u89e6\u5230\u4e0a\u5b98\u5929\u91ce\u7684\u8eab\u4f53\uff0c\u7acb\u523b\u53d8\u4e3a\u95ed\u7a74\u7684\u624b\u6cd5\uff0c\u638c\u529b\u6536\u56de \u4e86\u516b\u6210\uff0c\u8fd9\u6837\u8f7b\u5fae\u7684\u638c\u529b\uff0c\u4ec5\u4ec5\u53ef\u4ee5\u963b\u6ede\u6c14\u8840\u8fd0\u884c\u4e8e\u4e00\u65f6\uff0c\u5373\u7b97\u65e0\u4eba\u89e3\u6551\uff0c\u4e5f\u53ef\u81ea\u9192\u3002\u9648\u7384 \u673a\u62b1\u7740\u4ed6\u4e71\u6447\uff0c\u6c14\u8840\u4e00\u884c\uff0c\u4ed6\u5f53\u7136\u9192\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u5c0f\u5b50\u547c\u5929\u62a2\u5730\uff0c\u88c5\u795e\u5f04\u9b3c\uff0c\u5e72\u4e9b\u4ec0\u4e48\uff1f\u7f57\u91d1\u5cf0\u90a3\u8001\u8d3c\u5462\uff1f\u201d\u9648\u7384 \u673a\u9053\uff1a\u201c\u7ed9\u6253\u8dd1\u4e86\uff01\u201d\u4e0a\u5b98\u5929\u91ce\u8bf4\u9053\uff1a\u201c\u4f60\u5c45\u7136\u628a\u4ed6\u6253\u8dd1\u4e86\uff1f\u201d\u9648\u7384\u673a\u9053\uff1a\u201c\u4e0d\u662f\u6211\uff0c\u662f\u4e91 \u592b\u4eba\u3002\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u54ea\u4e00\u4e2a\u4e91\u592b\u4eba\uff1f\u201d\u9648\u7384\u673a\u9053\uff1a\u201c\u9664\u4e86\u4e91\u821e\u9633\u7684\u59bb\u5b50\uff0c\u8fd8\u6709\u54ea\u4e00\u4e2a\u4e91 \u592b\u4eba\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u5979\u6765\u6551\u4f60\uff1f\u201d \u3000\u3000\u9648\u7384\u673a\u9053\uff1a\u201c\u55ef\uff0c\u4f60\u4e0d\u5fc5\u591a\u95ee\u4e86\u3002\u54b1\u4eec\u8d76\u5feb\u5230\u4e91\u5bb6\u53bb\u5427\u3002\u201d\u4e0a\u5b98\u5929\u91ce\u53cc\u76ee\u4e00\u7741\uff0c\u9053\uff1a \u201c\u53bb\u505a\u4ec0\u4e48\uff1f\u201d\u9648\u7384\u673a\u9053\uff1a\u201c\u6211\u5411\u4ed6\u8981\u5973\u513f\uff0c\u4f60\u5411\u4ed6\u8981\u5251\u8c31\u3002\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u4ed6\u4f1a\u628a\u5973\u513f \u7ed9\u4f60\u5417\uff1f\u201d\u9648\u7384\u673a\u9053\uff1a\u201c\u4ed6\u5185\u759a\u4e8e\u5fc3\uff0c\u6127\u5bf9\u59bb\u5b50\uff0c\u4e0d\u80fd\u4e0d\u5356\u5979\u7684\u60c5\u9762\u3002\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u4ec0 \u4e48\uff0c\u662f\u4e91\u592b\u4eba\u66ff\u4f60\u6c42\u60c5\u3002\u597d\u5440\uff0c\u4f60\u8fd9\u5c0f\u5b50\u771f\u6709\u672c\u4e8b\uff0c\u5c45\u7136\u5148\u5df4\u7ed3\u4e0a\u672a\u6765\u7684\u5cb3\u6bcd\u4e86\u3002\u201d\u9648\u7384\u673a \u9762\u4e0a\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u4e0a\u5b98\u5144\u4f11\u5f97\u53d6\u7b11\u3002\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u8c01\u548c\u4f60\u53d6\u7b11\uff01\u628a\u60c5\u7531\u544a\u8bc9\u6211\u77e5\uff0c\u4e0d\u8bb8 \u534a\u70b9\u9690\u7792\u3002\u201d \u3000\u3000\u9648\u7384\u673a\u77e5\u9053\u4e0a\u5b98\u5929\u91ce\u7684\u813e\u6c14\uff0c\u82e5\u4e0d\u8bf4\u660e\uff0c\u4f11\u60f3\u4ed6\u8d70\u534a\u6b65\u3002\u53ea\u5f97\u5c06\u4e91\u592b\u4eba\u9002\u624d\u6765\u5230\u7684\u60c5 \u5f62\uff0c\u548c\u5979\u7684\u8bf4\u8bdd\u590d\u8ff0\u4e86\u4e00\u904d\uff0c\u4e0a\u5b98\u5929\u91ce\u542c\u5f97\u5446\u5446\u51fa\u795e\uff0c\u5fc3\u4e2d\u6df7\u4e71\u4e4b\u6781\uff0c\u65e2\u4e3a\u9648\u7384\u673a\u6b22\u559c\uff0c\u53c8 \u4e3a\u8427\u97f5\u5170\u4f24\u5fc3\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u90a3\u4f60\u5c31\u53bb\u5427\u3002\u201d\u9646\u7384\u673a\u9053\uff1a\u201c\u4f60\u5462\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u6211\u73b0\u5728\u5df2\u4e0d\u5e0c\u7f55\u90a3\u672c\u5251\u8c31\uff0c\u518d\u8bf4\u6211\u4e5f\u4e0d\u613f\u6cbe\u53d7\u522b\u4eba\u7684\u6069\u60e0\u3002\u6211\u4e0d\u53bb\uff01\u201d \u8fd9\u4e09\u5b57\u8bf4\u5f97\u65a9\u9489\u622a\u94c1\u3002\u9648\u7384\u673a\u4e0d\u6562\u518d\u529d\uff0c\u6014\u6014\u7684\u770b\u7740\u4ed6\u7684\u53cb\u4eba\uff0c\u4ed6\u7684\u5fc3\u65e9\u5df2\u98de\u5230\u4e86\u7d20\u7d20\u7684\u8eab \u65c1\uff0c\u7136\u800c\u5374\u53c8\u820d\u4e0d\u5f97\u7acb\u5373\u79bb\u5f00\u4e0a\u5b98\u5929\u91ce\u3002\u4e0a\u5b98\u5929\u91ce\u4e5f\u5446\u5446\u7684\u770b\u7740\u4ed6\uff0c\u597d\u50cf\u6709\u8bb8\u591a\u8bdd\u8981\u8bf4\u5374\u4e00 \u53e5\u8bdd\u4e5f\u8bf4\u4e0d\u51fa\u6765\u3002\u8fd9\u65f6\u5df2\u662f\u5929\u8fd1\u9ec4\u660f\uff0c\u5c71\u98ce\u9661\u8d77\uff0c\u4e0a\u5b98\u5929\u91ce\u6c14\u8840\u521a\u521a\u6062\u590d\u8fd0\u884c\uff0c\u6709\u70b9\u5bd2\u610f\uff0c \u5ffd\u5730\u63e1\u7740\u9648\u7384\u673a\u7684\u624b\u95ee\u9053\uff1a\u201c\u4f60\u51b7\u4e48\uff1f\u201d \u3000\u3000\u9648\u7384\u673a\u9053\uff1a\u201c\u4e0d\u51b7\uff0c\u4f60\u51b7\u5417\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u89c9\u4ec0\u4e48\u3002\u55ef\uff0c\u6253\u98ce\u5566\uff0c\u8fd8\u98d8\u4e0b\u4e86 \u96ea\u82b1\uff0c\u54b1\u4eec\u5728\u6797\u5b50\u91cc\u4e5f\u6709\u70b9\u5bd2\u610f\uff0c\u6797\u5b50\u5916\u9762\u60f3\u5fc5\u66f4\u51b7\u4e86\u3002\u97f5\u5170\u59d0\u59d0\u5979\u5b64\u4f36\u4f36\u7684\u4e00\u4e2a\u4eba\u5728\u6797\u5b50 \u5916\u8dd1\u6765\u8dd1\u53bb\uff0c\u4f60\u62c5\u4e0d\u62c5\u5fc3\u5979\u4f1a\u53d7\u51c9\u3002\u201d \u3000\u3000\u9648\u7384\u673a\u5fc3\u4e2d\u4e00\u9178\uff0c\u9053\uff1a\u201c\u4e0a\u5b98\u5144\uff0c\u5144\u5f1f\u6c42\u4f60\u4e00\u4ef6\u4e8b\u60c5\u3002\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u8bf7\u8bf4\u3002\u201d\u9648\u7384 \u673a\u9053\uff1a\u201c\u542c\u6211\u7684\u8bdd\uff0c\u53bb\u627e\u97f5\u5170\u59d0\u59d0\u5427\uff01\u201d\u4e0a\u5b98\u5929\u91ce\u9ed8\u9ed8\u4e0d\u8bed\uff0c\u6447\u4e86\u6447\u5934\uff0c\u8fc7\u4e86\u597d\u4e00\u4f1a\u513f\u624d\u4f4e \u58f0\u8bf4\u9053\uff1a\u201c\u7384\u673a\uff0c\u4f60\u522b\u7ba1\u6211\u3002\u6211\u5df2\u51b3\u610f\u7ee7\u627f\u6bd5\u51cc\u98ce\u5927\u76d7\u7684\u8863\u94b5\uff0c\u4ece\u4eca\u4e4b\u540e\uff0c\u4f60\u505a\u4f60\u7684\u4fa0\u58eb\uff0c \u6211\u505a\u6211\u7684\u5f3a\u76d7\uff0c\u54b1\u4eec\u5f7c\u6b64\u4e24\u4e0d\u76f8\u6d89\u4e86\u3002\u4f60\u8d70\u5427\uff01\u201d \u3000\u3000\u9648\u7384\u673a\u77e5\u4ed6\u4f24\u5fc3\u4e4b\u6781\uff0c\u60f3\u9053\uff1a\u201c\u522b\u4eba\u662f\u5931\u610f\u9003\u7985\uff0c\u4ed6\u5374\u662f\u9690\u8eab\u76d7\u8dc3\u3002\u7167\u4ed6\u7684\u6027\u5b50\uff0c\u4e0d\u77e5 \u4eca\u540e\u8fd8\u8981\u505a\u51fa\u4e9b\u4ec0\u4e48\u4e8b\u60c5\uff1f\u5931\u610f\u9003\u7985\u8fd8\u597d\uff0c\u9690\u8eab\u76d7\u8dc3\uff0c\u628a\u6301\u4e0d\u5b9a\u53ef\u5c31\u8981\u8bef\u5165\u6b67\u9014\u3002\u201d\u5fc3\u4e2d\u4e00 \u6025\uff0c\u8131\u53e3\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u53bb\u627e\u97f5\u5170\uff0c\u6211\u5c31\u4e0d\u53bb\u627e\u7d20\u7d20\uff01\u201d \u3000\u3000\u5ffd\u542c\u5f97\u4e00\u58f0\u51b7\u7b11\uff0c\u6709\u4eba\u8bf4\u9053\uff1a\u201c\u4e0d\u52b3\u76f8\u627e\uff0c\u6211\u6765\u4e86\uff01\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u97f5\u5170\u59d0\u59d0\uff01\u201d\u53ea \u89c1\u8427\u97f5\u5170\u53cc\u76ee\u7ea2\u80bf\uff0c\u8138\u4e0a\u6cea\u75d5\u672a\u62ed\uff0c\u5374\u81ea\u4ef0\u5929\u72c2\u7b11\uff0c\u62db\u624b\u8bf4\u9053\uff1a\u201c\u4e0a\u5b98\u5929\u91ce\uff0c\u4f60\u6765\u5440\uff01\u554a\uff0c \u4f60\u4e3a\u4ec0\u4e48\u4e0d\u6765\uff1f\u4f60\u82e5\u4e0d\u6765\uff0c\u53ef\u5c31\u8981\u8bef\u4e86\u4eba\u5bb6\u7684\u795e\u4ed9\u7737\u5c5e\uff01\u201d\u82e5\u5728\u5e73\u65f6\uff0c\u4e0a\u5b98\u5929\u91ce\u5f97\u5979\u76f8\u62db\uff0c \u5f53\u771f\u662f\u5982\u5949\u7eb6\u97f3\u3002\u7136\u800c\u6b64\u9645\uff0c\u4e0d\u4f46\u9648\u7384\u673a\u660e\u767d\uff0c\u4e0a\u5b98\u5929\u91ce\u4e5f\u542c\u5f97\u51fa\u5979\u4e43\u662f\u5fc3\u4e2d\u6124\u6fc0\u4e4b\u6781\uff0c\u6240 \u4ee5\u624d\u8bf4\u51fa\u6b64\u7b49\u8a00\u8bcd\uff0c\u60f3\u6765\u5979\u5df2\u5230\u4e86\u591a\u65f6\uff0c\u9648\u7384\u673a\u7684\u8bdd\u5979\u90fd\u542c\u8fdb\u53bb\u4e86\u3002 \u3000\u3000\u9648\u7384\u673a\u5446\u82e5\u6728\u9e21\uff0c\u4e0a\u5b98\u5929\u91ce\u5fc3\u5982\u5200\u5272\uff0c\u53eb\u9053\uff1a\u201c\u97f5\u5170\u59d0\u59d0\uff0c\u4f60\uff0c\u4f60\u2014\u2014\uff0c\u4e0d\u77e5\u5982\u4f55\u529d\u6170 \u65b9\u597d\uff0c\u53ea\u542c\u5f97\u8427\u97f5\u5170\u53c8\u662f\u4e00\u9635\u72c2\u7b11\uff0c\u6bd4\u75db\u54ed\u66f4\u53eb\u4eba\u96be\u53d7\u4e07\u500d\uff0c\u8427\u97f5\u5170\u5728\u72c2\u7b11\u58f0\u4e2d\u53c8\u62db\u624b\u8bf4 \u9053\uff1a\u201c\u6765\u5440\uff0c\u4f60\u600e\u4e48\u4e0d\u6765\u3002\u8fde\u4f60\u4e5f\u770b\u4e0d\u4e0a\u6211\u4e86\u5417\uff1f\u201d\u84e6\u7136\u95f4\u7b11\u58f0\u53d8\u4e86\u54ed\u58f0\uff0c\u8427\u97f5\u5170\u53cc\u624b\u63a9\u7740 \u8138\u5b54\uff0c\u8f6c\u8eab\u4fbf\u8dd1\u3002 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u518d\u4e5f\u5fcd\u53d7\u4e0d\u5f80\uff0c\u53eb\u9053\uff1a\u201c\u97f5\u5170\u59d0\u59d0\uff0c\u4f60\u7b49\u7b49\u6211\uff0c\u6211\u6765\u5566\uff01\u201d\u98de\u8eab\u8ffd\u8d76\uff0c\u4e00\u5148\u4e00 \u540e\uff0c\u7a7f\u51fa\u4e1b\u6797\uff0c\u53ea\u5269\u4e0b\u9648\u7384\u673a\u5446\u5446\u53d1\u6123\u3002 \u3000\u3000\u9648\u7384\u673a\u53f9\u4e86\u53e3\u6c14\uff0c\u76ee\u9001\u4ed6\u4eec\u7684\u80cc\u5f71\uff0c\u5fc3\u4e2d\u8bf4\u9053\uff1a\u201c\u6211\u8fd9\u9897\u5fc3\u5df2\u4ea4\u7ed9\u4e86\u7d20\u7d20\uff0c\u5170\u59d0\uff0c\u6211\u8fd9 \u4e00\u751f\u4e5f\u4e0d\u6307\u671b\u4f60\u518d\u539f\u8c05\u6211\u4e86\uff01\u201d\u64ae\u571f\u4e3a\u9999\uff0c\u6697\u6697\u7977\u544a\u82cd\u5929\uff0c\u4fdd\u4f50\u4ed6\u4eec\u826f\u7f18\u65e9\u7f14\uff0c\u4f46\u60f3\u8d77\u8427\u97f5 \u5170\u90a3\u526f\u795e\u60c5\uff0c\u5fc3\u4e2d\u7981\u4e0d\u4f4f\u4e0d\u5bd2\u800c\u6817\uff01\u53ea\u6015\u597d\u4e8b\u591a\u78e8\uff0c\u53ea\u6015\u4ed6\u4eec\u96be\u7ed3\u9e33\u76df\uff0c\u5fc3\u5934\u7684\u7599\u7629\u6c38\u751f\u4e5f \u96be\u78e8\u706d\uff01 \u3000\u3000\u970e\u65f6\u95f4\u601d\u6f6e\u7eb7\u6d8c\uff0c\u4f46\u89c9\u4eba\u4e16\u4e4b\u4e0a\uff0c\u6700\u96be\u89e3\u5f00\u7684\u5c31\u662f\u611f\u60c5\u7684\u845b\u85e4\uff0c\u665a\u971e\u6d88\u892a\uff0c\u6797\u5b50\u91cc\u66f4\u9ed1 \u66f4\u51b7\u4e86\uff0c\u9648\u7384\u673a\u4e00\u7247\u8ff7\u832b\uff0c\u5373\u5c06\u5f97\u5230\u4e91\u7d20\u7d20\u7684\u559c\u60a6\uff0c\u4e5f\u88ab\u51b2\u6de1\u4e86\u8bb8\u591a\u3002\u7136\u800c\u8981\u4e0d\u662f\u60f3\u8d77\u7d20 \u7d20\uff0c\u8981\u4e0d\u662f\u53ef\u4ee5\u4f1a\u89c1\u610f\u4e2d\u4eba\u7684\u5e0c\u671b\u652f\u6301\u7740\u4ed6\uff0c\u4ed6\u5df2\u7ecf\u662f\u65e0\u529b\u518d\u8d70\u4e86\u3002 \u3000\u3000\u9648\u7384\u673a\u8d70\u51fa\u6797\u5b50\uff0c\u671d\u7740\u5c71\u9876\u7684\u4e91\u5bb6\uff0c\u4e00\u6b65\u4e00\u6b65\u7684\u8d70\u4e0a\u53bb\u3002\u5fc3\u4e2d\u4e0d\u4f4f\u7684\u60f3\uff1a\u7d20\u7d20\u73b0\u5728\u505a\u4ec0 \u4e48\uff1f\u662f\u8fd8\u5728\u904d\u5c71\u627e\u6211\u8fd8\u662f\u5df2\u56de\u5230\u5bb6\u91cc\uff1f\u4e91\u592b\u4eba\u5bf9\u5979\u7684\u4e08\u592b\u8bf4\u4e86\u4e9b\u4ec0\u4e48\u8bdd\uff1f\u5979\u89c1\u7740\u5973\u513f\u4e86\u4e48\uff1f \u3000\u3000\u4e91\u821e\u9633\u8fd9\u65f6\u6b63\u72ec\u81ea\u5728\u4e66\u623f\uff0c\u501a\u7a97\u51dd\u671b\u6885\u82b1\uff0c\u7ecf\u8fc7\u4e86\u6628\u665a\u90a3\u4e00\u573a\u5927\u6218\uff0c\u8001\u6885\u6811\u4e0a\uff0c\u53ea\u5269\u4e0b \u7a00\u7a00\u843d\u843d\u7684\u51e0\u6735\u6885\u82b1\u4e86\uff0c\u9662\u5b50\u91cc\u6ee1\u76ee\u82cd\u51c9\uff0c\u5899\u89d2\u90a3\u4e00\u676f\u9ec4\u571f\uff0c\u66f4\u5728\u82cd\u51c9\u4e4b\u4e2d\uff0c\u5e73\u6dfb\u4e86\u51e0\u5206\u660e \u68ee\u7684\u201c\u9b3c\u6c14\u201d\u3002 \u3000\u3000\u9662\u5b50\u91cc\u9759\u5bc2\u5982\u6b7b\uff0c\u4e91\u821e\u9633\u8f7b\u8f7b\u7684\u53f9\u4e86\u53e3\u6c14\uff0c\u5524\u4e86\u4e00\u58f0\uff1a\u201c\u7d20\u7d20\u3002\u201d\u665a\u98ce\u7a7f\u8fdb\u7a97\u6237\uff0c\u6b63\u9001 \u6765\u7d20\u7d20\u90a3\u9690\u7ea6\u53ef\u8fa8\u7684\u6b4c\u58f0\u3002\u7d20\u7d20\u53bb\u627e\u9648\u7384\u673a\u8fd8\u6ca1\u6709\u56de\u6765\u3002 \u3000\u3000\u4e91\u821e\u9633\u7684\u8111\u6d77\u4e2d\uff0c\u91cd\u73b0\u51fa\u521a\u624d\u7684\u4e00\u5e55\u60c5\u666f\uff0c\u4ed6\u4ed7\u7740\u534a\u9897\u5c11\u9633\u5c0f\u8fd8\u4e39\u548c\u90a3\u4e00\u846b\u82a6\u63a0\u82b1\u5929\u9999 \u56de\u9633\u9152\u4e4b\u529b\uff0c\u652f\u6491\u7740\u8eab\u5b50\uff0c\u7ec8\u4e8e\u5728\u77f3\u6d1e\u4e4b\u4e2d\uff0c\u5c06\u81ea\u5df1\u7ec8\u8eab\u62b1\u64bc\u7684\u4e00\u6869\u7f6a\u5b7d\u5411\u5973\u513f\u8bf4\u4e86\uff0c\u201c\u53ef \u601c\u7684\u7d20\u7d20\uff0c\u5979\u4e5f\u8bb8\u4ece\u6765\u60f3\u4e0d\u5230\u7236\u4eb2\u662f\u8fd9\u6837\u72e0\u5fc3\u8d1f\u4e49\u7684\u4e00\u4e2a\u574f\u4eba\u5427\uff1f\u201d\u4e91\u7d20\u7d20\u60ca\u9a87\u3001\u9707\u7c9f\u3001\u4f24 \u5fc3\u800c\u53c8\u5e26\u7740\u601c\u60af\u7684\u795e\u60c5\u5982\u5728\u76ee\u524d\uff0c\u201c\u5440\uff0c\u6211\u771f\u4e0d\u8be5\u544a\u8bc9\u5979\u8fd9\u6837\u53ef\u6015\u7684\u4e8b\u60c5\uff0c\u4ee4\u5979\u7eaf\u6d01\u7684\u5fc3\u6c38 \u8fdc\u8499\u4e0a\u4e00\u5c42\u9634\u5f71\uff0c\u4f46\u6211\u4e0d\u5411\u5979\u4edf\u6094\uff0c\u6211\u5c31\u662f\u6b7b\u4e86\uff0c\u4e5f\u8981\u5e26\u7740\u75db\u82e6\u5230\u575f\u5e55\u91cc\u53bb\uff0c\u6b7b\u4e5f\u4e0d\u80fd\u7791 \u76ee\uff01\u201d \u3000\u3000\u201c\u7d20\u7d20\u6d41\u7740\u6cea\uff0c\u542c\u6211\u8bf4\u8fd9\u6869\u53ef\u6015\u7684\u7f6a\u5b7d\uff0c\u5979\u9759\u9759\u7684\u542c\u7740\uff0c\u4ec0\u4e48\u8bdd\u4e5f\u6ca1\u6709\u8bf4\u3002\u5440\uff0c\u5979\u5728\u60f3 \u4e9b\u4ec0\u4e48\u5462\uff1f\u5728\u6211\u8bf4\u5b8c\u4e4b\u540e\uff0c\u5979\u54fd\u54bd\u8bf4\u9053\uff1a\u2018\u7239\u7239\uff0c\u4f60\u75b2\u5026\u4e86\uff0c\u8fd9\u77f3\u6d1e\u4e2d\u9ed1\u5f97\u53ef\u6015\uff0c\u6211\u6276\u4f60\u56de \u5bb6\u53bb\u6b47\u6b47\u5427\u3002\u2019\u7d20\u7d20\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u8d23\u5907\u6211\uff0c\u53cd\u800c\u8fd9\u6837\u7231\u60dc\u6211\u5462\uff0c\u4f60\u53ef\u77e5\u9053\u6211\u5fc3\u91cc\u6709\u591a\u96be \u53d7\uff01\u201d \u3000\u3000\u7d20\u7d20\u548c\u7236\u4eb2\u56de\u5bb6\u4e4b\u540e\uff0c\u670d\u5f85\u7236\u4eb2\u7761\u4e86\u4fbf\u72ec\u81ea\u51fa\u95e8\uff0c\u4e91\u821e\u9633\u60f3\u4e86\u8d77\u6765\uff0c\u5fc3\u4e2d\u6697\u6697\u597d\u7b11\uff1a \u201c\u5973\u513f\u554a\uff0c\u4f60\u96be\u9053\u5f53\u6211\u4e0d\u77e5\u9053\u4f60\u662f\u53bb\u627e\u8c01\u4e48\uff1f\u6211\u662f\u6545\u610f\u88c5\u7761\uff0c\u8ba9\u4f60\u53bb\u7684\u3002\u201d \u3000\u3000\u665a\u98ce\u5439\u6765\uff0c\u4e91\u821e\u9633\u7a81\u7136\u6253\u4e86\u4e00\u4e2a\u5bd2\u5664\uff0c\u63a5\u7740\u60f3\u9053\uff1a\u201c\u7d20\u7d20\u4f1a\u4e0d\u4f1a\u518d\u56de\u6765\u5462\uff1f\u6211\u4e0d\u914d\u505a\u5979 \u7684\u7236\u4eb2\uff0c\u5979\u9119\u5f03\u6211\uff0c\u6211\u4e5f\u53ea\u80fd\u7518\u53d7\u3002\u53ef\u662f\u5979\u82e5\u4e0d\u518d\u56de\u6765\uff0c\u6211\u4e00\u4e2a\u4eba\uff0c\u6d3b\u7740\u53c8\u6709\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u4e91\u821e\u9633\u597d\u4f3c\u5927\u75c5\u521d\u6108\u7684\u4eba\uff0c\u4f46\u89c9\u6d51\u8eab\u6ca1\u6709\u534a\u70b9\u52b2\u513f\uff0c\u9662\u5b50\u91cc\u9759\u5f97\u4ee4\u4eba\u5bb3\u6015\uff0c\u5ffd\u5730\u91cc\u4e00\u9635 \u5fae\u7ec6\u7684\u811a\u6b65\u58f0\u4f20\u6765\uff0c\u4e91\u821e\u9633\u62ac\u5934\u4e00\u671b\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u5b9d\u73e0\uff0c\u662f\u4f60\uff01\u4f60\u56de\u6765\u4e86\uff01\u201d \u3000\u3000\u4e91\u592b\u4eba\u62c2\u5f00\u6885\u679d\uff0c\u5728\u90a3\u53f6\u9ec4\u571f\u4e4b\u524d\u6c89\u9ed8\u4e86\u534a\u523b\uff0c\u7f13\u7f13\u8d70\u8fdb\u4e66\u623f\uff0c\u4e66\u623f\u91cc\u4e91\u821e\u9633\u5df2\u7eb1\u706f\u70b9 \u8d77\uff0c\u706f\u5149\u4e4b\u4e0b\uff0c\u4f46\u89c1\u4e91\u592b\u4eba\u7684\u8138\u8272\uff0c\u66f4\u662f\u82cd\u767d\u5f97\u4ee4\u4eba\u5bd2\u51db\u3002 \u3000\u3000\u4e91\u592b\u4eba\u907f\u5f00\u4e86\u5979\u4e08\u592b\u7684\u773c\u5149\uff0c\u597d\u50cf\u9762\u5bf9\u7740\u4e00\u4e2a\u964c\u751f\u4eba\u4f3c\u7684\uff0c\u6de1\u6de1\u95ee\u9053\uff1a\u201c\u7d20\u7d20\u5462\uff1f\u201d\u4e91 \u821e\u9633\u9053\uff1a\u201c\u5979\u51fa\u53bb\u4e86\uff0c\u8fd8\u6ca1\u56de\u6765\u3002\u55ef\uff0c\u5b9d\u73e0\uff0c\u6211\u77e5\u9053\u4f60\u5f88\u96be\u8fc7\uff0c\u6211\u6628\u665a\u4e0d\u5e94\u6740\u4e86\u5929\u94ce\u3002\u5440\uff0c \u6211\u8fd9\u4e00\u751f\u505a\u9519\u7684\u4e8b\u5f88\u591a\uff0c\u6211\u4e5f\u4e0d\u6562\u518d\u6c42\u4f60\u7684\u9976\u6055\u4e86\u3002\u201d \u3000\u3000\u4e91\u592b\u4eba\u9053\uff1a\u201c\u8fd9\u4e9b\u4e8b\u73b0\u5728\u8bf4\u4e5f\u8fdf\u4e86\u3002\u821e\u9633\uff0c\u6211\u5e73\u751f\u6ca1\u6709\u5411\u4f60\u6c42\u8fc7\u4e00\u4ef6\u4e8b\u60c5\uff0c\u4eca\u665a\u662f\u6211\u7b2c \u4e00\u6b21\u6c42\u4f60\u4e5f\u662f\u6700\u540e\u4e00\u6b21\u6c42\u4f60\uff0c\u6c42\u4f60\u7b54\u5e94\u4e00\u4ef6\u4e8b\u60c5\u3002\u201d\u4e91\u821e\u9633\u9762\u8272\u5927\u53d8\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053 \u4f60\u8981\u7684\u662f\u4ec0\u4e48\uff1f\u4f60\u662f\u4e0d\u662f\u8981\u628a\u7d20\u7d20\u5e26\u8d70\u3002\u201d \u3000\u3000\u4e91\u592b\u4eba\u9053\uff1a\u201c\u6211\u672c\u6765\u60f3\u628a\u7d20\u7d20\u5e26\u8d70\u7684\uff0c\u73b0\u5728\u60f3\u8fc7\u4e86\uff0c\u7d20\u7d20\u7eb5\u7136\u613f\u610f\u8ddf\u6211\uff0c\u6211\u4e5f\u4e0d\u80fd\u4ee4\u5979 \u5feb\u4e50\u3002\u201d\u4e91\u821e\u9633\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u8ba9\u5979\u7559\u4e0b\u6765\u4e86\u3002\u55ef\uff0c\u5b9d\u73e0\uff0c\u4f60\u4e5f\u7559\u4e0b\u6765\u5427\u3002\u201d\u4e91\u592b\u4eba\u7eed\u9053\uff1a \u201c\u6211\u60f3\u8fc7\u4e86\uff0c\u7d20\u7d20\u8ddf\u4f60\uff0c\u4f60\u4e5f\u4e0d\u80fd\u4ee4\u5979\u5feb\u4e50\u3002\u201d\u4e91\u821e\u9633\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d\u4e91\u592b\u4eba\u9053\uff1a \u201c\u6211\u77e5\u9053\u4f60\u75bc\u7d20\u7d20\u4e0d\u4e9a\u4e8e\u6211\uff0c\u90a3\u4e48\u54b1\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u66ff\u7d20\u7d20\u8bbe\u60f3\uff0c\u8ba9\u5979\u5feb\u4e50\uff1f\u201d\u4e91\u821e\u9633\u9ed8\u7136\u4e0d \u8bed\uff0c\u4e91\u592b\u4eba\u9053\uff1a\u201c\u4f60\u820d\u4e0d\u5f97\u5979\uff0c\u6211\u53c8\u4f55\u5c1d\u820d\u5f97\u5979\uff1f\u4f46\u6211\u601d\u4e4b\u518d\u4e09\uff0c\u5979\u8fd8\u662f\u79bb\u5f00\u54b1\u4eec\u7684\u597d\uff01\u201d \u4e91\u821e\u9633\u54fc\u4e86\u4e00\u58f0\uff0c\u51c4\u7136\u5730\u9053\uff1a\u201c\u6211\u61c2\u5f97\u4f60\u7684\u610f\u601d\u4e86\uff01\u201d \u3000\u3000\u4e91\u592b\u4eba\u9053\uff1a\u201c\u4f60\u61c2\u5f97\u5c31\u597d\uff0c\u8fd9\u4e16\u754c\u53ea\u6709\u4e00\u4e2a\u53ef\u4ee5\u4ee4\u5979\u5feb\u4e50\u7684\u4eba\uff01\u201d\u4e91\u821e\u9633\u53eb\u9053\uff1a\u201c\u9648\u7384 \u673a\uff01\u201d\u4e91\u592b\u4eba\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5c31\u662f\u90a3\u4e2a\u60f3\u523a\u6740\u4f60\u7684\u9752\u5e74\u3002\u201d\u4e91\u821e\u9633\u53c8\u9ed8\u7136\u4e0d\u8bed\uff0c\u4e91\u592b\u4eba\u9053\uff1a \u201c\u6211\u5df2\u5bdf\u770b\u8fc7\u4ed6\u7684\u4e3a\u4eba\uff0c\u4ed6\u5bf9\u670b\u53cb\u5c1a\u81ea\u80af\u820d\u8eab\u5171\u96be\uff0c\u5bf9\u5fc3\u7231\u7684\u4eba\u66f4\u4e0d\u4f1a\u8d1f\u5fc3\u3002\u6211\u5c06\u7d20\u7d20\u4ea4\u6258 \u7ed9\u4ed6\uff0c\u653e\u5fc3\u5f97\u5f88\uff01\u201d\u4e91\u821e\u9633\u53f9\u4e86\u53e3\u6c14\u8bf4\u9053\uff1a\u201c\u6211\u7684\u4e00\u73ed\u65e7\u65e5\u540c\u50da\uff0c\u9f50\u5fc3\u5408\u529b\u6559\u4ed6\uff0c\u5c31\u662f\u671b\u4ed6 \u80fd\u591f\u6740\u6211\uff0c\u8fd9\u51a4\u4ec7\u662f\u65e0\u6cd5\u5316\u89e3\u7684\u4e86\u3002\u201d \u3000\u3000\u4e91\u592b\u4eba\u5e7d\u5e7d\u8bf4\u9053\uff1a\u201c\u4e8c\u5341\u5e74\u524d\uff0c\u4f60\u6c42\u6211\u4e3a\u4f60\u76d7\u7238\u7238\u7684\u5251\u8c31\uff0c\u6211\u7b54\u5e94\u4e86\u3002\u90a3\u65f6\u4f60\u600e\u4e48 \u8bf4\uff1f\u201d\u4e91\u821e\u9633\u9053\uff1a\u201c\u6211\u8bf4\u6211\u613f\u610f\u7b54\u5e94\u4f60\u4e00\u5343\u6869\u4e00\u4e07\u6869\u4e8b\u60c5\uff0c\u4f60\u8981\u4ec0\u4e48\uff0c\u6211\u90fd\u53ef\u4ee5\u4e3a\u4f60\u505a\u5230\u3002 \u5440\uff0c\u8fd9\u4e8c\u5341\u5e74\u6765\uff0c\u6211\u5b9e\u5728\u5f85\u9519\u4f60\u4e86\u3002\u201d\u4e91\u592b\u4eba\u9053\uff1a\u201c\u4e8c\u5341\u5e74\u6765\uff0c\u6211\u6ca1\u6709\u5411\u4f60\u8981\u8fc7\u4e00\u4ef6\u4e1c\u897f\uff0c \u66f4\u6ca1\u6709\u5411\u4f60\u6c42\u8fc7\u4efb\u4f55\u4e8b\u60c5\uff0c\u56e0\u4e3a\u6211\u77e5\u9053\u4f60\u5fc3\u91cc\u6ca1\u6709\u6211\uff01\u201d \u3000\u3000\u4e91\u821e\u9633\u5fc3\u4e2d\u9178\u75db\uff0c\u6b63\u60f3\u8bf4\u8bdd\uff0c\u53ea\u542c\u5f97\u59bb\u5b50\u5df2\u62a2\u7740\u8bf4\u9053\uff1a\u201c\u8fd9\u4e9b\u65e7\u4e8b\u4e5f\u4e0d\u7528\u518d\u63d0\u4e86\u3002\u73b0\u5728 \u6211\u53ea\u6c42\u4f60\u4e00\u4ef6\u4e8b\u60c5\uff0c\u8ba9\u7d20\u7d20\u8ddf\u7384\u673a\u8fdc\u8d70\u9ad8\u98de\uff0c\u6700\u597d\u4ee5\u540e\u6c38\u4e0d\u518d\u89c1\u54b1\u4eec\u7684\u9762\u3002\u201d\u4e91\u821e\u9633\u9053\uff1a \u201c\u4e0d\u9519\u3002\u514d\u5f97\u5979\u8bb0\u8d77\u66fe\u6709\u6211\u8fd9\u6837\u7684\u4e00\u4e2a\u4ee4\u5979\u5fc3\u4f24\u7684\u7236\u4eb2\u3002\u5b9d\u73e0\uff0c\u6211\u7b54\u5141\u4f60\u4e86\uff01\u5176\u5b9e\u6211\u4e5f\u613f\u610f \u5979\u548c\u7384\u673a\u540c\u5728\u4e00\u8d77\uff01\u201d \u3000\u3000\u4e91\u592b\u4eba\u542c\u4e86\u8fd9\u8bdd\uff0c\u8f6c\u8eab\u4fbf\u8d70\u3002\u4e91\u821e\u9633\u9053\uff1a\u201c\u5b9d\u73e0\uff0c\u4f60\u5c31\u4e0d\u518d\u7559\u4e00\u4f1a\u513f\uff0c\u7d20\u7d20\u5979\u5c31\u8981\u56de\u6765 \u4e86\u3002\u201d\u4e91\u592b\u4eba\u9053\uff1a\u201c\u6211\u8fd9\u4e00\u6869\u5fc3\u613f\u5df2\u4e86\uff0c\u53cd\u6b63\u90fd\u8981\u5206\u79bb\uff0c\u4f55\u5fc5\u518d\u89c1\u5979\u4ee4\u5979\u4f24\u5fc3\u3002\u201d\u4e91\u821e\u9633\uff1a \u201c\u4f60\u53bb\u54ea\u513f\u3002\u201d\u4e91\u592b\u4eba\u9053\uff1a\u201c\u4f60\u6740\u4e86\u4eba\uff0c\u6211\u66ff\u4f60\u8fd8\u503a\u3002\u201d\u4e91\u821e\u9633\u5583\u5583\u8bf4\u9053\uff1a\u201c\u5929\u94ce\uff0c\u5929\u94ce\uff0c \u6700\u540e\u8fd8\u662f\u4f60\u8d62\u4e86\uff01\u201d \u3000\u3000\u4e91\u592b\u4eba\u542c\u4e86\u8fd9\u8bdd\uff0c\u53c8\u56de\u8fc7\u5934\u6765\uff0c\u9053\uff1a\u201c\u6211\u628a\u5929\u94ce\u5f53\u505a\u6700\u597d\u7684\u670b\u53cb\uff0c\u5bf9\u4ed6\u53ef\u5e76\u6ca1\u6709\u534a\u70b9\u79c1 \u60c5\u3002\u4f46\u4f60\u53ef\u77e5\u9053\u4ed6\u5bb6\u4e2d\u8fd8\u6709\u5be1\u5987\u5b64\u513f\uff1f\u8fd9\u4e00\u5e45\u753b\u4e5f\u8fd8\u8981\u7ed9\u4ed6\u9001\u53bb\u3002\u514d\u5f97\u4ed6\u6b7b\u4e0d\u7791\u76ee\uff01\u5440\uff0c\u82e5 \u4e0d\u662f\u4e3a\u4e86\u7d20\u7d20\uff0c\u4eca\u665a\u6211\u5c31\u4e0d\u4f1a\u56de\u6765\uff01\u201d\u4e91\u821e\u9633\u6709\u6c14\u65e0\u529b\u7684\u501a\u7740\u623f\u95e8\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u5b9d\u73e0\uff0c\u4f60\u53bb \u5427\uff01\u201d \u3000\u3000\u9662\u5b50\u91cc\u53c8\u5f52\u4e8e\u5bc2\u9759\uff0c\u4e91\u821e\u9633\u653e\u58f0\u541f\u9053\uff1a\u201c\u751f\u6b7b\u5e7d\u51a5\u4e24\u6e3a\u832b\uff0c\u4eba\u95f4\u82df\u6d3b\u66f4\u5fc3\u4f24\uff0c\u6b8b\u6885\u51b7\u6708 \u4e34\u65b0\u5bb6\uff0c\u6cea\u6d12\u897f\u98ce\u603b\u65ad\u80a0\uff01\u201d\u541f\u58f0\u65b9\u6bd5\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u9634\u607b\u607b\u7684\u7b11\u9053\uff1a\u201c\u821e\u9633\u5144\u597d\u8bd7\u5174\u554a\uff01\u201d \u3000\u3000------------- \u3000\u3000\u91cd\u5e86\u96ea\u513f \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u8fd8\u5251\u5947\u60c5\u5f55\u300b\u2014\u2014\u7b2c\u5341\u56de\u3000\u60c5\u4ed8\u6773\u6f20 \u6881\u7fbd\u751f\u300a\u8fd8\u5251\u5947\u60c5\u5f55\u300b \u7b2c\u5341\u56de\u3000\u60c5\u4ed8\u6773\u6f20 \u3000\u3000\u4e91\u821e\u9633\u5e76\u4e0d\u56de\u5934\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u7f57\u5927\u4eba\uff0c\u4e00\u4e2a\u6708\u7684\u671f\u9650\u8fd8\u6ca1\u6709\u5230\u5475\uff01\u201d\u7f57\u91d1\u5cf0\u9053\uff1a\u201c\u542c \u8bf4\u77f3\u5929\u94ce\u4e0a\u5c71\u6765\u4e86\uff0c\u8fd8\u6709\u4e03\u4fee\u8001\u9053\u548c\u84b2\u575a\u7b49\u4eba\u4e5f\u90fd\u6765\u4e86\uff0c\u5c0f\u5f1f\u653e\u5fc3\u4e0d\u4e0b\uff0c\u662f\u4ee5\u56de\u6765\u3002\u201d\u4e91\u821e \u9633\u9053\uff1a\u201c\u591a\u8c22\u4f60\u5173\u5fc3\u4e86\u3002\u201d\u53e3\u4e2d\u867d\u8bf4\u591a\u8c22\uff0c\u795e\u8272\u5374\u4ecd\u662f\u51b7\u6f20\u4e4b\u6781\uff0c\u4e00\u76f4\u501a\u7a97\u800c\u671b\uff0c\u773c\u775b\u4e5f\u6ca1 \u6709\u8f6c\u8fc7\u6765\u3002 \u3000\u3000\u7f57\u91d1\u5cf0\u6253\u4e86\u4e00\u4e2a\u54c8\u54c8\uff0c\u51d1\u8fd1\u7a97\u524d\uff0c\u6307\u7740\u90a3\u4e00\u676f\u9ec4\u571f\u8bf4\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u77f3\u5929\u94ce\u81ea\u8d1f\u82f1\u96c4\u65e0 \u654c\uff0c\u5982\u4eca\u5374\u57cb\u9aa8\u6b64\u95f4\u3002\u821e\u9633\u5144\uff0c\u4ece\u4eca\u4e4b\u540e\uff0c\u518d\u6ca1\u6709\u4eba\u6562\u548c\u4f60\u4e89\u8fd9\u6b66\u529f\u5929\u4e0b\u7b2c\u4e00\u7684\u540d\u5934\u4e86\uff0c\u5f53 \u771f\u662f\u53ef\u559c\u53ef\u8d3a\u54ea\uff01\u201d \u3000\u3000\u4e91\u821e\u9633\u970d\u5730\u56de\u5934\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u7f57\u5927\u4eba\uff0c\u4f60\u522b\u6316\u82e6\u6211\u4e86\uff0c\u884c\u4e48\uff1f\u201d\u7f57\u91d1\u5cf0\u6123\u4e86\u4e00\u6123\uff0c\u8bf4 \u9053\uff1a\u201c\u821e\u9633\u5144\uff0c\u8fd9\u662f\u54ea\u91cc\u8bdd\u6765\uff1f\u54c8\uff0c\u6211\u77e5\u9053\u4e86\uff0c\u821e\u9633\u5144\uff0c\u4f60\u662f\u4e0d\u628a\u6d6e\u540d\u653e\u5728\u5fc3\u4e0a\uff0c\u4f46\u4f60\u8fd9\u6b21 \u672a\u66fe\u4e0b\u5c71\uff0c\u4fbf\u66ff\u7687\u4e0a\u7acb\u4e86\u8fd9\u6869\u5927\u529f\uff0c\u4e5f\u662f\u53ef\u559c\u53ef\u8d3a\u54ea\uff01\u201d\u4e91\u821e\u9633\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u6740\u5929\u94ce\uff0c\u53ef \u5e76\u4e0d\u662f\u4e3a\u4e86\u4f60\u4eec\u3002\u201d\u7f57\u91d1\u5cf0\u53c8\u662f\u4e00\u6123\uff0c\u8138\u4e0a\u5ffd\u5730\u9732\u51fa\u4e00\u4e1d\u5978\u7b11\uff0c\u8038\u8038\u80a9\u5934\uff0c\u4f5c\u51fa\u201c\u5fc3\u7167\u4e0d \u5ba3\u201d\u7684\u6837\u5b50\uff0c\u5e72\u7b11\u8bf4\u9053\uff1a\u201c\u55ef\uff0c\u6211\u521a\u521a\u78b0\u89c1\u5ac2\u5b50\u5306\u5306\u4e0b\u5c71\u3002\u821e\u9633\u5144\uff0c\u4f60\u4eec\u8001\u592b\u8001\u59bb\u4e86\uff0c\u6562\u60c5 \u8fd8\u95f9\u4ec0\u4e48\u5b69\u5b50\u7684\u813e\u6c14\u4e48\uff1f\u201d\u4e91\u821e\u9633\u9762\u8272\u4e00\u53d8\uff0c\u770b\u4f3c\u5c31\u8981\u53d1\u4f5c\uff0c\u5374\u4ecd\u5fcd\u4f4f\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u7f57\u5927 \u4eba\u8fd8\u6709\u4ec0\u4e48\u8bdd\u4e48\uff1f\u201d\u90a3\u53e3\u6c14\u7adf\u662f\u9010\u5ba2\u7684\u610f\u601d\u3002 \u3000\u3000\u7f57\u91d1\u5cf0\u9000\u4e86\u4e00\u6b65\uff0c\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u8c6a\u6770\u80f8\u6000\uff0c\u5bb6\u5ba4\u4e4b\u4e8b\uff0c\u7b97\u5f97\u4e86\u4ec0\u4e48\uff1f\u201d\u4e91\u821e\u9633\u9762\u8272\u66f4 \u662f\u9634\u6c89\u53ef\u6015\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u7f57\u91d1\u5cf0\u9634\u607b\u607b\u7684\u7b11\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\u3002\u55ef\uff0c\u4e0d\u7ba1\u4f60\u4e3a\u4ec0\u4e48\u6740 \u77f3\u5929\u94ce\uff0c\u5c0f\u5f1f\u603b\u662f\u611f\u6fc0\u4e0d\u5c3d\u3002\u4e91\u5144\uff0c\u5c0f\u5f1f\u8c2c\u6258\u77e5\u5df1\uff0c\u6562\u5949\u529d\u543e\u5144\u51e1\u4e8b\u8fd8\u662f\u770b\u5f00\u4e00\u4e9b\u3002\u5c24\u5176\u5185 \u4f24\u672a\u6108\uff0c\u52a8\u6012\u66f4\u6613\u4f24\u8eab\u3002\u5c0f\u5f1f\u8eab\u8fb9\u5e26\u6709\u5927\u5185\u7684\u56fa\u5143\u4e39\uff0c\u5bf9\u543e\u5144\u6216\u8bb8\u6709\u7528\u5904\u3002\u201d \u3000\u3000\u4e91\u821e\u9633\u5fc3\u4e2d\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u53ae\u771f\u597d\u773c\u529b\uff0c\u4e0d\u8fc7\u4ed6\u770b\u4f5c\u662f\u77f3\u5929\u94ce\u7684\u638c\u529b\u6240\u4f24\uff0c\u5219\u770b\u9519 \u4e86\u3002\u201d\u539f\u6765\u4e91\u821e\u9633\u4e43\u662f\u4e2d\u4e86\u6bd5\u51cc\u98ce\u7684\u638c\u5fc3\u7684\u9634\u51b7\u5947\u6bd2\uff0c\u867d\u6709\u5c0f\u8fd8\u4e39\u548c\u4e5d\u5929\u743c\u82b1\u56de\u9633\u9152\uff0c\u771f\u529b \u5374\u8fd8\u672a\u6062\u590d\uff0c\u6b63\u662f\u56e0\u6b64\uff0c\u4ed6\u9002\u624d\u51e0\u6b21\u52a8\u6012\uff0c\u5374\u8fd8\u4e0d\u6562\u5bf9\u7f57\u91d1\u5cf0\u53d1\u4f5c\u3002 \u3000\u3000\u7f57\u91d1\u5cf0\u53d6\u4e86\u4e09\u9897\u6de1\u7ea2\u8272\u7684\u4e39\u4e38\uff0c\u653e\u5728\u638c\u5fc3\uff0c\u4e91\u821e\u9633\u77a5\u4e86\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4e0d\u7528\uff01\u201d\u7f57\u91d1\u5cf0\u7b11 \u9053\uff1a\u201c\u543e\u5144\u529f\u529b\u6df1\u539a\uff0c\u4e0d\u7528\u672c\u6765\u4e5f\u53ef\u4ee5\u590d\u5143\u3002\u4f46\u60f3\u6765\u4e0d\u514d\u8981\u591a\u4e9b\u65f6\u65e5\u9759\u517b\uff0c\u8fd9\u5c82\u4e0d\u803d\u6401\u4e86\u543e \u5144\u7684\u5927\u4e8b\u5417\uff1f\u201d\u4e91\u821e\u9633\u9053\uff1a\u201c\u4ec0\u4e48\u5927\u4e8b\uff1f\u201d\u7f57\u91d1\u5cf0\u9053\uff1a\u201c\u543e\u5144\u4eb2\u53e3\u7b54\u5e94\u5c0f\u5f1f\uff0c\u4e00\u6708\u4e4b \u5185\u2026\u2026\u201d\u4e91\u821e\u9633\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u5929\u5927\u7684\u4e8b\uff0c\u5c0f\u5f1f\u4ece\u6b64\u4e5f\u4e0d\u518d\u7ba1\uff01\u543e\u5144\u8bf7\u56de\uff01\u201d \u3000\u3000\u7f57\u91d1\u5cf0\u9053\uff1a\u201c\u541b\u5b50\u4e00\u8a00\uff0c\u5feb\u9a6c\u4e00\u97ad\u3002\u821e\u9633\u5144\u66fe\u7b54\u5e94\u4e3a\u7687\u4e0a\u51fa\u5c71\uff0c\u4f55\u4ee5\u5982\u4eca\u6094\u7ea6\uff1f\u201d\u4e91\u821e \u9633\u51b7\u7b11\u9053\uff1a\u201c\u6211\u672c\u6765\u5c31\u4e0d\u662f\u541b\u5b50\uff0c\u2026\u2026\u201d\u7f57\u91d1\u5cf0\u6545\u610f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4eba\u751f\u4e0d\u5982\u610f\u4e8b\u5e38\u516b \u4e5d\uff0c\u543e\u5144\u4f55\u5fc5\u5982\u6b64\u4f24\u5fc3\uff01\u201d\u53e3\u6c14\u4e4b\u95f4\uff0c\u900f\u9732\u51fa\u4ed6\u5df2\u5077\u542c\u4e86\u4e91\u592b\u4eba\u7684\u8c08\u8bdd\uff0c\u7adf\u81ea\u6000\u7591\u4e91\u592b\u4eba\u4e0e \u77f3\u5929\u950b\u66fe\u6709\u79c1\u60c5\uff0c\u7adf\u81ea\u51fa\u8bed\u6311\u62e8\u3002\u4e91\u821e\u9633\u52c3\u7684\u5927\u6012\uff0c\u53cc\u773c\u7cbe\u5149\u7535\u5c04\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u7f57\u5927\u4eba\u5f53 \u771f\u662f\u6b3a\u8d1f\u5c0f\u5f1f\u53d7\u4f24\u672a\u6108\u4e48\uff1f\u201d \u3000\u3000\u7f57\u91d1\u5cf0\u6253\u4e86\u4e00\u4e2a\u54c8\u54c8\uff0c\u9053\uff1a\u201c\u5c82\u6562\uff0c\u5c82\u6562\uff01\u821e\u9633\u5144\u4f09\u4fea\u60c5\u6df1\uff0c\u540d\u5c71\u5055\u9690\uff0c\u80dc\u4f3c\u795e\u4ed9\uff0c\u65e2 \u7136\u4e0d\u613f\u518d\u67d3\u4fd7\u5c18\uff0c\u5c0f\u5f1f\u4e5f\u4e0d\u6562\u52c9\u5f3a\u4e86\u3002\u201d\u8a00\u8bed\u4e4b\u95f4\uff0c\u4ecd\u7136\u5b58\u6709\u6311\u62d4\u8ba5\u8bbd\u4e4b\u610f\uff0c\u4f46\u5df2\u7f13\u548c\u4e86\u8bb8 \u591a\u3002 \u3000\u3000\u4e91\u821e\u9633\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u6020\u6162\u6020\u6162\uff0c\u8bf7\u6055\u6211\u4e0d\u9001\u4e86\u3002\u201d\u4e24\u4eba\u672c\u6765\u5982\u7bad\u5728\u5f26\uff0c \u6240\u4ee5\u4e0d\u53d1\uff0c\u5b9e\u662f\u5404\u5b58\u987e\u5fcc\u3002\u7f57\u91d1\u5cf0\uff0c\u867d\u7136\u770b\u51fa\u4e91\u821e\u9633\u5143\u6c14\u5df2\u4f24\uff0c\u4f46\u60f3\u8d77\u4e86\u90a3\u529f\u795e\u5165\u5316\u7684\u5251\u672f \u548c\u6b66\u6797\u7edd\u5b66\u7684\u4e00\u6307\u7985\u529f\uff0c\u5fc3\u4e2d\u4e5f\u81ea\u6709\u4e9b\u754f\u60e7\u3002 \u3000\u3000\u4e91\u821e\u9633\u677e\u4e86\u53e3\u6c14\uff0c\u4ecd\u7136\u501a\u7a97\u773a\u671b\uff0c\u4f5c\u51fa\u6ee1\u4e0d\u5728\u4e4e\u7684\u795e\u6c14\u3002\u4e0d\u6599\u7f57\u91d1\u5cf0\u8d70\u5230\u95e8\u8fb9\uff0c\u5374\u5ffd\u5730 \u56de\u5934\uff0c\u53c8\u9634\u607b\u607b\u7684\u7b11\u9053\uff1a\u201c\u821e\u9633\u5144\u5f53\u771f\u662f\u4ece\u6b64\u4e0d\u518d\u7ba1\u4efb\u4f55\u95f2\u4e8b\u4e86\u4e48\uff1f\u201d\u4e91\u821e\u9633\u9053\uff1a\u201c\u4eba\u4e0d\u60f9 \u6211\uff0c\u6211\u4e0d\u60f9\u4eba\uff01\u201d\u7f57\u91d1\u5cf0\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u6709\u4e00\u4e2a\u59d3\u9648\u540d\u53eb\u7384\u673a\u7684\u5c0f\u5b50\uff0c\u542c\u8bf4\u66fe\u610f\u56fe\u884c\u523a\u8001 \u5144\uff0c\u8fd9\u4e2a\u6211\u4e14\u4e0d\u7ba1\u3002\u4e0d\u8fc7\u6211\u82e5\u51fa\u624b\u64d2\u4ed6\uff0c\u8001\u5144\u4e5f\u4e0d\u4f1a\u7ba1\u5427\uff1f\u201d\u4e91\u821e\u9633\u5fc3\u4e2d\u4e00\u51db\uff0c\u60f3\u4e86\u4e00\u60f3\uff0c \u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u82e5\u7136\u4e0e\u6211\u65e0\u5173\uff0c\u6211\u7ba1\u4ed6\u4f5c\u751a\uff1f\u201d\u7f57\u91d1\u5cf0\u5927\u559c\uff0c\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u5f97\u5144\u4e00\u8bfa\uff0c\u5c0f\u5f1f\u544a \u8f9e\u3002\u201d \u3000\u3000\u4e14\u8bf4\u9648\u7384\u673a\u6ee1\u6000\u5e0c\u671b\uff0c\u6765\u5230\u4e91\u5bb6\uff0c\u5728\u5899\u5916\u4f9d\u7a00\u542c\u5f97\u91cc\u9762\u6709\u4e24\u4e2a\u4eba\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u4f3c\u4e4e\u90fd\u662f \u7537\u5b50\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u5fc3\u9053\uff1a\u201c\u96be\u9053\u8fd9\u4e0d\u662f\u4e91\u592b\u4eba\uff1f\u201d\u7a0d\u7a0d\u8fdf\u7591\uff0c\u4ecd\u7136\u63a8\u95e8\u8fdb\u53bb\uff0c\u8fd9\u65f6\u6070\u5de7\u7f57\u91d1 \u5cf0\u8d70\u51fa\u6765\uff0c\u5728\u9662\u5b50\u91cc\u78b0\u4e2a\u6b63\u7740\uff01 \u3000\u3000\u7f57\u91d1\u5cf0\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u4fa5\u5e78\u5f97\u56de\u6027\u547d\uff0c\u8fd8\u4e0d\u8fdc\u9003\uff0c\u5374\u53c8\u6765\u81ea\u6295\u7f57\u7f51\uff01\u54c8\u54c8\uff0c\u5f53\u771f \u662f\u5929\u7f51\u6062\u6062\uff0c\u758f\u800c\u4e0d\u6f0f\uff01\u201d\u58f0\u51fa\u4eba\u5230\uff0c\u957f\u81c2\u4e00\u4f38\uff0c\u4fbf\u65bd\u5c55\u5c0f\u64d2\u62ff\u624b\u7684\u7f20\u8eab\u64d2\u654c\u6bd2\u62db\uff0c\u5f3a\u626d\u9648 \u7384\u673a\u7684\u624b\u8155\u3002 \u3000\u3000\u7f57\u91d1\u5cf0\u4e5f\u662f\u8f7b\u654c\u592a\u751a\uff0c\u82e5\u7136\u4ed6\u4e0d\u518d\u6253\u8bdd\uff0c\u9aa4\u7136\u51fa\u624b\uff0c\u9648\u7384\u673a\u7edd\u9003\u4e0d\u4e86\u4ed6\u8fd9\u4e00\u6bd2\u62db\uff0c\u8fd9\u65f6 \u6709\u4e86\u9632\u5907\uff0c\u4e00\u4e2a\u76d8\u9f99\u7ed5\u6b65\uff0c\u53f3\u638c\u5212\u4e86\u4e00\u4e2a\u5708\u5f27\uff0c\u5de6\u638c\u81ea\u8098\u7a7f\u51fa\uff0c\u4e5f\u6765\u53cd\u6263\u7f57\u91d1\u5cf0\u7684\u8109\u95e8\uff0c\u8fd9 \u4e00\u62db\u4ee5\u653b\u4e3a\u5b88\uff0c\u7528\u5f97\u6070\u5230\u597d\u5904\uff0c\u7adf\u7136\u628a\u7f57\u91d1\u5cf0\u90a3\u4e00\u6bd2\u62db\u8f7b\u8f7b\u5316\u89e3\u3002 \u3000\u3000\u7f57\u91d1\u5cf0\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u80c6\u5b50\u4e0d\u5c0f\uff0c\u5c45\u7136\u4e0e\u6211\u62a2\u653b\uff01\u201d\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u8fd9\u56de\u624b \u5e95\u5374\u662f\u4e1d\u6beb\u4e0d\u7f13\uff0c\u84e6\u7136\u4e00\u8bb0\u201c\u9634\u9633\u53cc\u649e\u638c\u201d\uff0c\u6539\u6293\u4e3a\u63a8\uff0c\u7528\u4e0a\u4e86\u201c\u5c0f\u5929\u661f\u201d\u7684\u638c\u529b\uff0c\u53cc\u638c\u4e00 \u9f50\u63a8\u51fa\uff0c\u9648\u7384\u673a\u907f\u65e0\u53ef\u907f\uff0c\u529b\u8d2f\u638c\u5fc3\uff0c\u7528\u4e86\u4e00\u62db\u201c\u7ae5\u5b50\u62dc\u89c2\u97f3\u201d\uff0c\u53cc\u638c\u5408\u4ec0\uff0c\u8fd8\u4e86\u4e00\u62db\uff0c\u65b9 \u81ea\u5947\u8be7\u5bf9\u65b9\u7684\u638c\u529b\u4e0d\u5982\u60f3\u50cf\u4e4b\u5f3a\uff0c\u9661\u7136\u95f4\u5ffd\u89c9\u4e24\u80a1\u6f5c\u529b\u5de6\u7275\u53f3\u5f15\uff0c\u767b\u65f6\u8eab\u4e0d\u7531\u5df1\u7684\u4e00\u8fde\u6253\u4e86 \u5341\u51e0\u4e2a\u76d8\u65cb\uff0c\u5140\u81ea\u7a33\u4e0d\u4f4f\u8eab\u5f62\u3002\u539f\u6765\u8fd9\u201c\u5c0f\u5929\u661f\u201d\u638c\u529b\u542b\u6709\u4e00\u80a1\u74e2\u6cbe\u4e4b\u52b2\uff0c\u7f57\u91d1\u5cf0\u610f\u5728\u751f \u64d2\uff0c\u4e0d\u60f3\u4ee5\u521a\u731b\u7684\u638c\u529b\u5c06\u4ed6\u51fb\u6b7b\uff0c\u6545\u6b64\u4e0d\u60dc\u8017\u8d39\u7cbe\u795e\uff0c\u7528\u4e0a\u7edd\u5999\u7684\u5185\u5bb6\u638c\u529b\u3002 \u3000\u3000\u7f57\u91d1\u5cf0\u53c8\u662f\u54c8\u54c8\u5927\u7b11\uff0c\u6b63\u5f85\u9648\u7384\u673a\u81ea\u5df1\u8f6c\u5f97\u5934\u660f\u773c\u82b1\uff0c\u81ea\u884c\u8dcc\u5012\uff0c\u5ffd\u542c\u5f97\u201c\u7830\u201d\u7684\u4e00 \u58f0\uff0c\u4e91\u821e\u9633\u4e00\u62f3\u5c06\u73bb\u7483\u7a97\u683c\u6253\u788e\uff0c\u8dc3\u4e86\u51fa\u6765\uff0c\u7f57\u91d1\u5cf0\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u4e91\u821e\u9633 \u4f60\u8bf4\u8bdd\u4e0d\u7b97\u8bdd\u4e48\uff1f\u201d \u3000\u3000\u4e91\u821e\u9633\u51b7\u7b11\u9053\uff1a\u201c\u6211\u8bf4\u8fc7\u4e0d\u7ba1\u95f2\u4e8b\uff0c\u4f46\u8fd9\u5374\u5e76\u975e\u95f2\u4e8b\u5475\uff01\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u5c31\u662f\u4e00\u4e2a\u5288\u7a7a\u638c \u6253\u6765\u3002 \u3000\u3000\u4e91\u821e\u9633\u4e0e\u7f57\u91d1\u5cf0\u4e43\u662f\u5f53\u4e16\u6570\u4e00\u6570\u4e8c\u7684\u9ad8\u624b\uff0c\u51fa\u624b\u6781\u5feb\uff0c\u5c31\u5728\u4e91\u821e\u9633\u53d1\u51fa\u5288\u7a7a\u638c\u4e4b\u65f6\uff0c\u7f57 \u91d1\u5cf0\u4e5f\u662f\u201c\u563f\u201d\u7684\u4e00\u58f0\u51b7\u7b11\uff0c\u53cd\u624b\u4e00\u62ff\uff0c\u6293\u7740\u4e86\u9648\u7384\u673a\uff0c\u7adf\u7528\u5927\u6454\u7891\u624b\u7684\u624b\u6cd5\u7529\u51fa\uff0c\u6253\u4e86\u4e00 \u4e2a\u54c8\u54c8\uff0c\u7b11\u9053\uff1a\u201c\u597d\u5475\uff0c\u4f60\u5c31\u6253\u5427\uff01\u201d \u3000\u3000\u9664\u7384\u673a\u4f53\u91cd\u6709\u4e00\u767e\u6765\u65a4\uff0c\u88ab\u7f57\u91d1\u5cf0\u7528\u5185\u5bb6\u771f\u529b\u6454\u51fa\uff0c\u5c31\u7b49\u5982\u4e00\u5757\u5de8\u77f3\u822c\u5411\u4e91\u821e\u9633\u8fce\u9762\u800c \u649e\uff0c\u90a3\u51b2\u51fb\u529b\u9053\u4f55\u6b62\u5343\u65a4\uff01\u4e91\u821e\u9633\u662f\u6b66\u5b66\u7684\u5927\u884c\u5bb6\uff0c\u5f53\u7136\u77e5\u9053\u5389\u5bb3\uff0c\u4e5f\u77e5\u9053\u5e94\u4ed8\u8fd9\u6837\u7684\u201c\u72e0 \u62db\u201d\uff0c\u53ea\u6709\u4e24\u4e2a\u529e\u6cd5\uff0c\u4e00\u4e2a\u662f\u4e5f\u4ee5\u5185\u5bb6\u771f\u529b\uff0c\u5c06\u9648\u7384\u673a\u53cd\u51fb\u56de\u53bb\uff0c\u628a\u9648\u7384\u673a\u53d8\u6210\u4e86\u4e24\u4e2a\u4eba\u4e4b \u95f4\u95f4\u63a5\u8f83\u91cf\u5185\u5bb6\u771f\u529b\u7684\u5de5\u5177\u3002\u5982\u6b64\u4e00\u6765\uff0c\u9648\u7384\u673a\u88ab\u4e24\u5927\u9ad8\u624b\u629b\u6765\u63b7\u53bb\uff0c\u81ea\u662f\u5fc5\u6b7b\u65e0\u7591\uff01\u7b2c\u4e8c \u4e2a\u529e\u6cd5\u662f\u7acb\u5373\u907f\u5f00\uff0c\u8ba9\u9648\u7384\u673a\u6454\u5012\u5730\u4e0a\uff0c\u8fd9\u6837\u5e94\u4ed8\uff0c\u9648\u7384\u673a\u4e5f\u662f\u5341\u4e5d\u96be\u6d3b\uff01 \u3000\u3000\u8fd9\u5239\u90a3\u95f4\uff0c\u4e91\u821e\u9633\u5df2\u63a5\u8fde\u8f6c\u4e86\u597d\u51e0\u4e2a\u5ff5\u5934\uff0c\u662f\u4fdd\u5168\u9648\u7384\u673a\u5462\u8fd8\u662f\u4fdd\u5168\u81ea\u5df1\uff1f\u5fc3\u4e2d\u5140\u81ea\u8e0c \u8e87\u4e0d\u5b9a\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u4f46\u89c1\u9648\u7384\u673a\u7684\u8eab\u4f53\uff0c\u5934\u524d\u811a\u540e\uff0c\u5df2\u662f\u75be\u98ce\u800c\u81f3\uff0c\u970e\u7136\u95f4\uff0c\u4e91\u821e\u9633 \u7684\u8111\u6d77\u4e2d\u7a81\u7136\u95ea\u51fa\u59bb\u5b50\u5fe7\u90c1\u54c0\u6073\u7684\u98a7\u5bb9\u548c\u5973\u513f\u5929\u771f\u70c2\u6f2b\u7684\u5f71\u5b50\uff0c\u4e91\u821e\u9633\u54ac\u4e86\u54ac\u7259\uff0c\u771f\u6c14\u4e00 \u63d0\uff0c\u4e00\u638c\u5e73\u4f38\uff0c\u5c06\u9648\u7384\u673a\u63a5\u4e86\u8fc7\u6765\uff0c\u5378\u4e86\u7f57\u91d1\u5cf0\u7684\u5185\u5bb6\u771f\u529b\u3002 \u3000\u3000\u8fd9\u4e00\u7740\u5176\u5b9e\u4e5f\u5c31\u7b49\u5982\u4e91\u821e\u9633\u62fc\u4e86\u672c\u8eab\u7684\u529f\u529b\u786c\u63a5\u7f57\u91d1\u5cf0\u7684\u5927\u6454\u7891\u624b\uff0c\u4f46\u89c9\u80f8\u53e3\u5982\u7ed9\u94c1\u6746 \u731b\u649e\uff0c\u9976\u662f\u4e91\u821e\u9633\u529f\u529b\u6df1\u539a\uff0c\u4e5f\u7981\u4e0d\u4f4f\u8e09\u8e09\u8dc4\u8dc4\u7684\u5012\u9000\u51e0\u6b65\uff0c\u54c7\u54c7\u7684\u4e00\u53e3\u9c9c\u8840\u55b7\u4e86\u51fa\u6765\uff01\u4f4e \u5934\u4e00\u671b\uff0c\u4f46\u89c1\u9648\u7384\u673a\u53cc\u773c\u7d27\u95ed\uff0c\u9762\u5982\u91d1\u7eb8\uff0c\u663e\u7136\u4e5f\u662f\u7ed9\u7f57\u91d1\u5cf0\u7684\u5185\u529b\u9707\u6655\u4e86\u3002 \u3000\u3000\u7f57\u91d1\u5cf0\u8fd9\u4e00\u7740\u539f\u662f\u8bd5\u63a2\u4e91\u821e\u9633\u7684\u5fc3\u610f\uff0c\u89c1\u4ed6\u4e3a\u4e86\u4fdd\u5168\u9648\u7384\u673a\u7adf\u4e0d\u60dc\u81ea\u635f\u529f\u529b\uff0c\u5927\u51fa\u610f \u5916\u3002\u8981\u77e5\u8fd9\u4e24\u4eba\u5f7c\u6b64\u987e\u5fcc\uff0c\u4e00\u65e6\u52a8\u624b\uff0c\u5fc5\u5c06\u662f\u4ee5\u4e0a\u4e58\u7684\u6b66\u529f\u76f8\u62fc\uff0c\u8c01\u4eba\u80fd\u652f\u6301\u8f83\u4e45\uff0c\u4fbf\u53ef\u5360 \u4f18\uff0c\u4e91\u821e\u9633\u5bf9\u4ed8\u7f57\u91d1\u5cf0\u90a3\u4e00\u72e0\u62db\uff0c\u82e5\u7136\u4e0d\u7406\u9648\u7384\u9965\u6b7b\u6d3b\uff0c\u8fd0\u529b\u53cd\u51fb\u4e43\u662f\u4e0a\u7b56\uff0c\u7acb\u5373\u907f\u5f00\u4e43\u662f \u4e2d\u7b56\uff0c\u4f3c\u8fd9\u7b49\u786c\u63a5\u4e43\u662f\u4e0b\u4e0b\u4e4b\u7b56\u3002\u4e24\u4eba\u672a\u66fe\u6b63\u5f0f\u4ea4\u624b\uff0c\u4e91\u821e\u9633\u4fbf\u5df2\u5148\u5904\u4e0b\u98ce\u3002 \u3000\u3000\u7f57\u91d1\u5cf0\u7cbe\u660e\u673a\u8b66\uff0c\u4e00\u6709\u673a\u4f1a\uff0c\u90a3\u80af\u653e\u677e\uff0c\u8d81\u7740\u4e91\u821e\u9633\u5598\u606f\u672a\u5b9a\uff0c\u7acb\u5373\u8ffd\u51fb\uff0c\u201c\u547c\u201d\u7684\u4e00 \u58f0\uff0c\u5410\u6c14\u5f00\u58f0\uff0c\u53c8\u662f\u4e00\u62db\u6781\u521a\u731b\u7684\u5927\u6454\u7891\u624b\uff0c\u4e91\u821e\u9633\u5fae\u4e00\u4fa7\u8eab\uff0c\u5c06\u9648\u7384\u673a\u653e\u4e0b\uff0c\u53cd\u638c\u4e00\u62cd\uff0c \u4ee5\u7edd\u5999\u7684\u5378\u529b\u529f\u592b\uff0c\u5c06\u7f57\u91d1\u5cf0\u7684\u638c\u529b\u5378\u53bb\u4e94\u6210\uff0c\u8eab\u4e0d\u7531\u5df1\u7684\u53c8\u9000\u4e86\u51e0\u6b65\u3002\u7f57\u91d1\u5cf0\u8bd5\u51fa\u4e91\u821e\u9633 \u7684\u5185\u529b\u5df2\u663e\u4e8f\u635f\u4e4b\u8c61\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u8ddf\u7740\u53c8\u662f\u4e00\u638c\uff0c\u638c\u52bf\u95ea\u70c1\u4e0d\u5b9a\uff0c\u4f3c\u662f\u653b\u5411\u4e91\u821e\u9633\uff0c\u5374\u7a81\u7136 \u4e2d\u9014\u6539\u5411\uff0c\u5316\u865a\u4e3a\u5b9e\uff0c\u5411\u9648\u7384\u673a\u51fb\u4e0b\u3002\u8fd9\u4e00\u62db\u4f7f\u5f97\u9634\u72e0\u4e4b\u6781\uff0c\u4f46\u4e91\u821e\u9633\u662f\u4f55\u7b49\u6837\u4eba\uff0c\u89c1\u4ed6\u624b \u8155\u4e00\u7ffb\uff0c\u4fbf\u77e5\u6765\u610f\uff0c\u4e00\u4e2a\u817e\u632a\u6362\u4f4d\uff0c\u5df2\u7ecf\u5728\u9648\u7384\u673a\u7684\u524d\u9762\uff0c\u53cc\u638c\u9f50\u51fa\uff0c\u53c8\u786c\u63a5\u4e86\u7f57\u91d1\u5cf0\u7684\u4e00 \u62db\u3002 \u3000\u3000\u9002\u624d\u4e91\u821e\u9633\u56e0\u4e00\u624b\u62b1\u7740\u9648\u7384\u673a\uff0c\u5355\u638c\u5e94\u654c\uff0c\u6545\u6b64\u5927\u5403\u5176\u4e8f\u3002\u8fd9\u4e00\u4e0b\u53cc\u638c\u5f00\u51fa\uff0c\u5404\u81ea\u7528\u4e86 \u5341\u6210\u771f\u529b\uff0c\u53ea\u542c\u5f97\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u90fd\u88ab\u5bf9\u65b9\u7684\u638c\u529b\u9707\u51fa\u4e00\u4e08\u5f00\u5916\uff0c\u534a\u65a4\u516b\u4e24\uff0c\u65d7\u9f13\u76f8\u5f53\u3002 \u3000\u3000\u7f57\u91d1\u5cf0\u53c8\u60ca\u53c8\u559c\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4e91\u821e\u9633\u679c\u7136\u638c\u4e0b\u65e0\u865a\uff0c\u82e5\u672a\u53d7\u4f24\uff0c\u6211\u65ad\u65ad\u4e0d\u662f\u4ed6\u7684\u5bf9 \u624b\uff0c\u800c\u4eca\u4ed6\u6682\u65f6\u8fd8\u53ef\u4ee5\u4e0e\u6211\u6253\u4e2a\u5e73\u624b\uff0c\u4f46\u770b\u4ed6\u7684\u638c\u529b\uff0c\u540e\u52b2\u4e0d\u7ee7\uff0c\u6211\u53ea\u8981\u6c89\u5f97\u4f4f\u6c14\uff0c\u903c\u4ed6\u786c \u62fc\uff0c\u4ed6\u52bf\u96be\u652f\u6301\u3002\u54c8\u54c8\uff0c\u4ed6\u6740\u4e86\u77f3\u5929\u94ce\uff0c\u6211\u6740\u4e86\u4ed6\uff0c\u4ece\u6b64\u5929\u4e0b\u867d\u5927\uff0c\u65e0\u4eba\u518d\u662f\u6211\u7684\u654c\u624b \u4e86\uff01\u201d \u3000\u3000\u4e91\u821e\u9633\u4e00\u9000\u590d\u4e0a\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u7f57\u91d1\u5cf0\uff0c\u4e8f\u4f60\u4e5f\u7b97\u662f\u6b66\u6797\u4e2d\u7684\u4e00\u53f7\u4eba\u7269\uff0c\u7528\u8fd9\u6837\u72e0\u6bd2\u7684 \u624b\u6bb5\u5bf9\u4ed8\u4e00\u4e2a\u540e\u751f\u665a\u8f88\uff0c\u4f20\u51fa\u53bb\u6015\u4e0d\u6015\u5929\u4e0b\u82f1\u96c4\u7b11\u8bdd\uff1f\u201d\u7f57\u91d1\u5cf0\u51b7\u7b11\u9053\uff1a\u201c\u4e91\u821e\u9633\u4e5f\u8c08\u6c5f\u6e56 \u9053\u4e49\uff0c\u786e\u662f\u5929\u4e0b\u5947\u95fb\u3002\u6211\u8981\u64d2\u8fd9\u5c0f\u5b50\uff0c\u4e8b\u524d\u4e0e\u4f60\u8bf4\u8fc7\uff0c\u4f60\u8bf4\u8fc7\u4e0d\u7ba1\uff0c\u4f55\u4ee5\u5982\u4eca\u53c8\u7ba1\uff1f\u201d\u4e91\u821e \u9633\u9053\uff1a\u201c\u6211\u600e\u4e48\u8bf4\uff0c\u6211\u5fd8\u8bb0\u5566\uff0c\u4f60\u80cc\u7ed9\u6211\u542c\u5427\u3002\u201d\u7f57\u91d1\u5cf0\u6124\u9053\uff1a\u201c\u4f60\u5148\u8bf4\u4ece\u6b64\u4e0d\u7ba1\u4eba\u95f4\u95f2 \u4e8b\uff0c\u8ddf\u7740\u53c8\u90d1\u91cd\u58f0\u660e\uff1a\u2018\u82e5\u7136\u4e0e\u6211\u65e0\u5173\uff0c\u6211\u7ba1\u4ed6\u5219\u751a\uff1f\u2019\u8a00\u72b9\u5728\u8033\uff0c\u5c82\u80fd\u5c31\u5fd8\u8bb0\u4e86\u3002\u201d \u3000\u3000\u4e91\u821e\u9633\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u82e5\u5728\u522b\u5904\u6740\u4eba\u653e\u706b\uff0c\u6211\u61d2\u5f97\u7ba1\u4f60\u3002\u4f60\u5728\u6211\u5bb6\u4e2d\u52a8\u624b\uff0c\u773c\u4e2d \u8fd8\u6709\u6211\u4e91\u821e\u9633\u5417\uff1f\u8fd9\u5c0f\u5b50\u5c31\u7b97\u8be5\u6740\uff0c\u5728\u6211\u5bb6\u4e2d\uff0c\u4e5f\u8f6e\u4e0d\u5230\u4f60\u6765\u6740\u4ed6\u3002\u4e8b\u60c5\u4e0e\u6211\u6709\u5173\uff0c\u6211\u600e\u80fd \u4e0d\u7ba1\uff1f\u201d\u8fd9\u4e00\u756a\u4f9d\u7167\u6c5f\u6e56\u7684\u89c4\u77e9\uff0c\u53ef\u4e5f\u4e0d\u7b97\u5f3a\u8f9e\u593a\u7406\u3002\u7f57\u91d1\u5cf0\u5fcd\u7740\u4e86\u6c14\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u5982\u6b64\u8bf4 \u6765\uff0c\u4f60\u5b9a\u662f\u8981\u5e87\u62a4\u8fd9\u4e2a\u5c0f\u5b50\u4e86\uff1f\u201d\u4e91\u821e\u9633\u53cc\u773c\u4e00\u7ffb\uff0c\u65a9\u9489\u622a\u94c1\u7684\u8bf4\u9053\uff1a\u201c\u5728\u6211\u5bb6\u4e2d\uff0c\u7531\u6211\u4f5c \u4e3b\uff0c\u4f60\u7ba1\u4e0d\u4e86\uff01\u201d \u3000\u3000\u7f57\u91d1\u5cf0\u4e5f\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u662f\u5f20\u8d3c\u9057\u5b7d\uff0c\u6211\u8eab\u4e3a\u7ef5\u8863\u536b\u603b\u6307\u6325\uff0c\u8fd9\u4e8b\u60c5\u6211\u662f\u8981\u7ba1\u5b9a \u7684\u3002\u201d\u4e91\u821e\u9633\u9053\uff1a\u201c\u90a3\u4e5f\u6ca1\u6cd5\uff0c\u6211\u53ea\u6709\u518d\u9886\u6559\u4f60\u7f57\u5927\u4eba\u7684\u9ad8\u62db\uff01\u201d\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4f46\u89c1\u4e24\u4eba \u540c\u65f6\u62a2\u4e0a\uff0c\u7f57\u91d1\u5cf0\u4e00\u638c\u6253\u51fa\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u626b\u65ad\u4e86\u4e00\u679d\u6885\u679d\uff0c\u4e00\u638c\u5288\u7a7a\uff0c\u7acb\u77e5\u4e0d\u5999\uff0c\u4f46\u89c9\u80cc\u540e \u5fae\u98ce\u98d2\u7136\uff0c\u4e91\u821e\u9633\u5df2\u4ece\u4fa7\u88ad\u5230\u3002 \u3000\u3000\u7f57\u91d1\u5cf0\u5927\u559d\u4e00\u58f0\uff0c\u4e00\u8f6c\u8eab\u53c8\u662f\u6781\u521a\u731b\u7684\u4e00\u638c\uff0c\u4e91\u821e\u9633\u8eab\u5f62\u4e00\u6643\uff0c\u7f57\u91d1\u5cf0\u53c8\u662f\u4e00\u638c\u5288\u7a7a\u3002 \u4f46\u89c1\u56db\u9762\u516b\u65b9\u90fd\u662f\u4e91\u821e\u9633\u7684\u5f71\u5b50\uff0c\u638c\u98ce\u4eba\u5f71\uff0c\u4ee4\u4eba\u773c\u82b1\u64a9\u4e71\u3002\u7f57\u91d1\u5cf0\u5fc3\u5934\u4e00\u9707\uff0c\u6697\u81ea\u9a82\u9053\uff1a \u201c\u597d\u72e1\u733e\u7684\u4e91\u821e\u9633\uff0c\u4ed6\u4e0d\u6562\u4e0e\u6211\u786c\u62fc\u638c\u529b\uff0c\u5374\u4e0e\u6211\u7528\u8fd9\u6e38\u6597\u7684\u7ed5\u8eab\u638c\u6cd5\u3002\u201d \u3000\u3000\u4e91\u821e\u9633\u7684\u8f7b\u529f\u5185\u529f\u5251\u6cd5\u638c\u6cd5\u5747\u5df2\u5230\u4e86\u7089\u706b\u7eaf\u9752\u4e4b\u98a0\uff0c\u8fd9\u5957\u201c\u516b\u5366\u6e38\u8eab\u638c\u201d\u65bd\u5c55\u5f00\u6765\uff0c\u907f \u654c\u4e4b\u957f\uff0c\u653b\u654c\u4e4b\u77ed\uff0c\u903c\u5f97\u7f57\u91d1\u5cf0\u4e5f\u8ddf\u7740\u4ed6\u56e2\u56e2\u4e71\u8f6c\uff0c\u6e10\u89c9\u5934\u660f\u773c\u82b1\uff0c\u7f57\u91d1\u5cf0\u6697\u547c\u4e0d\u5999\uff0c\u60f3 \u9053\uff1a\u201c\u5982\u6b64\u4e0b\u53bb\uff0c\u6211\u672a\u7d2f\u6b7b\u4ed6\uff0c\u5148\u7ed9\u4ed6\u7d2f\u6b7b\u6211\u4e86\uff01\u201d\u6697\u81ea\u7559\u795e\uff0c\u53ea\u89c1\u4e91\u821e\u9633\u7684\u773c\u5149\u4e0d\u65f6\u7684\u77a7 \u7740\u90a3\u6655\u5012\u5730\u4e0a\u7684\u9648\u7384\u673a\u3002\u7f57\u91d1\u5cf0\u4e5f\u662f\u6b66\u5b66\u7684\u5927\u884c\u5bb6\uff0c\u89c1\u6b64\u60c5\u72b6\uff0c\u5fc3\u4e2d\u5927\u559c\u3002\u7acb\u523b\u4e5f\u60f3\u51fa\u4e00\u4e2a \u201c\u907f\u654c\u4e4b\u957f\uff0c\u653b\u654c\u4e4b\u77ed\u201d\u7684\u5999\u8ba1\u3002 \u3000\u3000\u9163\u6597\u4e2d\u7f57\u91d1\u5cf0\u4e00\u62db\u201c\u516b\u65b9\u98ce\u96e8\u201d\uff0c\u638c\u529b\u5411\u56db\u9762\u8361\u5f00\uff0c\u5c06\u4e91\u821e\u9633\u903c\u9000\u51e0\u6b65\uff0c\u7a81\u7136\u54c8\u54c8\u4e00 \u7b11\uff0c\u76d8\u819d\u5750\u5728\u5730\u4e0a\uff0c\u9053\uff1a\u201c\u821e\u9633\u5144\uff0c\u5c0f\u5f1f\u6ca1\u6709\u53d7\u4f24\uff0c\u4e5f\u89c9\u7d2f\u4e86\uff0c\u4f60\u4e5f\u6b47\u6b47\u5427\u3002\u201d\u8bdd\u4e2d\u4e4b\u610f\uff0c \u5373\u662f\u4e0d\u613f\u4e58\u5371\u53d6\u80dc\u3002\u4e91\u821e\u9633\u52c3\u7136\u5927\u6012\uff0c\u63c9\u8eab\u6251\u4e0a\uff0c\u638c\u52bf\u8fc5\u6377\u65e0\u4f26\uff0c\u970e\u773c\u4e4b\u95f4\uff0c\u8fde\u653b\u4e86\u5341\u516d\u516b \u62db\u3002\u7f57\u91d1\u5cf0\u51dd\u795e\u5e94\u654c\uff0c\u4ee5\u5206\u7b4b\u9519\u9aa8\u624b\u6cd5\uff0c\u53ea\u5f85\u4e91\u821e\u9633\u4e00\u8fd1\u8eab\uff0c\u4fbf\u7acb\u5373\u53cd\u624b\u64d2\u62ff\uff0c\u4e95\u6742\u4ee5\u6781\u521a \u731b\u7684\u91d1\u521a\u638c\u529b\u3002\u4efb\u51ed\u4e91\u821e\u9633\u7684\u8eab\u5f62\u5982\u4f55\u98d8\u5ffd\uff0c\u638c\u52bf\u5982\u4f55\u53d8\u5e7b\uff0c\u4ed6\u603b\u662f\u4e0d\u4e3a\u6240\u52a8\u3002 \u3000\u3000\u672c\u6765\u9ad8\u624b\u5bf9\u654c\uff0c\u5b9a\u987b\u7740\u7740\u4e89\u5148\uff0c\u4f3c\u7f57\u91d1\u5cf0\u8fd9\u6837\u6253\u6cd5\uff0c\u5148\u628a\u81ea\u5df1\u5c40\u9650\u5728\u9632\u5b88\u7684\u5730\u4f4d\uff0c\u90a3\u5c31 \u662f\u6c38\u65e0\u53d6\u80dc\u7684\u673a\u4f1a\u4e86\u3002\u4f46\u56e0\u4ed6\u770b\u51c6\u4e86\u4e91\u821e\u9633\u4e0d\u613f\u8017\u635f\u771f\u529b\uff0c\u4e0d\u6562\u548c\u4ed6\u786c\u62fc\uff0c\u53ea\u51ed\u7740\u8f7b\u7075\u98d8\u5ffd \u7684\u638c\u6cd5\uff0c\u5374\u662f\u65e0\u6cd5\u653b\u7834\u4ed6\u7684\u9632\u5fa1\u3002 \u3000\u3000\u8f6c\u773c\u4e4b\u95f4\uff0c\u53c8\u62c6\u4e86\u4e09\u4e94\u5341\u62db\u3002\u7f57\u91d1\u5cf0\u7b11\u9053\uff1a\u201c\u821e\u9633\u5144\uff0c\u54b1\u4eec\u5c06\u8fd1\u4e8c\u5341\u5e74\u4e0d\u4ea4\u624b\u4e86\uff0c\u4eca\u65e5 \u96be\u5f97\u543e\u5144\u8d4f\u9762\uff0c\u80af\u4e88\u8d50\u6559\uff0c\u6309\u7406\u8bf4\u5c0f\u5f1f\u5c31\u966a\u4f60\u6253\u4e2a\u4e09\u5929\u4e24\u591c\uff0c\u4e5f\u662f\u5e94\u8be5\u3002\u4f46\u543e\u5144\u4f53\u529b\u5c1a\u672a\u590d \u539f\uff0c\u5e94\u8be5\u4fdd\u91cd\u4e9b\u624d\u597d\u3002\u7d2f\u574f\u4e86\u4f60\uff0c\u5440\uff0c\u6211\u4e0d\u6b32\u6740\u4f2f\u4ec1\uff0c\u4f2f\u4ec1\u7531\u6211\u800c\u6b7b\uff0c\u53eb\u6211\u5982\u4f55\u5fc3\u5b89\u3002\u201d \u3000\u3000\u4e91\u821e\u9633\u770b\u7834\u4e86\u4ed6\u7684\u5fc3\u601d\uff0c\u6c89\u7740\u4e86\u6c14\uff0c\u4e0d\u4e3a\u6240\u6fc0\uff0c\u50ac\u7d27\u638c\u529b\uff0c\u5ffd\u521a\u5ffd\u67d4\uff0c\u5ffd\u865a\u5ffd\u5b9e\uff0c\u518d\u6597 \u4e86\u5341\u4f59\u62db\uff0c\u7f57\u91d1\u5cf0\u53c8\u7b11\u9053\uff1a\u201c\u821e\u9633\u5144\uff0c\u4f60\u6216\u8005\u8fd8\u53ef\u518d\u8017\u51e0\u4e2a\u65f6\u8fb0\uff0c\u8fd9\u4f4d\u5c0f\u54e5\u7ed9\u6211\u7528\u5927\u6454\u7891\u624b \u9707\u4f24\u4e86\u4e94\u810f\u516d\u8151\uff0c\u54c8\uff0c\u4f60\u7eb5\u7136\u6253\u80dc\u4e86\u6211\uff0c\u4e5f\u4fdd\u4e0d\u7740\u4ed6\u7684\u6027\u547d\u4e86\u3002\u201d \u3000\u3000\u4e91\u821e\u9633\u5fc3\u5934\u4e00\u9707\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u82e5\u7136\u6b7b\u4e86\uff0c\u7d20\u7d20\u5c82\u4e0d\u4f24\u5fc3\uff1f\u201d\u865a\u6643\u4e00\u62db\uff0c\u53cd\u8eab\u6b32\u9000\uff0c \u7f57\u91d1\u5cf0\u7a81\u7136\u957f\u8eab\u800c\u8d77\uff0c\u731b\u51fb\u4e00\u638c\uff0c\u638c\u529b\u6709\u5982\u6392\u5c71\u5012\u6d77\uff0c\u88ad\u5411\u4e91\u821e\u9633\u7684\u80cc\u5fc3\uff0c\u4e91\u821e\u9633\u903c\u5f97\u8fd0\u4e86 \u5168\u529b\uff0c\u56de\u8eab\u63a5\u4e00\u638c\uff0c\u4e24\u4eba\u529f\u529b\u6089\u654c\uff0c\u7f57\u91d1\u5cf0\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u821e\u9633\u5144\uff0c\u4f60\u6025\u5f85\u517b\u4f24\uff0c\u6211\u770b\u4f60\u8fd8\u662f \u628a\u8fd9\u5c0f\u5b50\u7559\u7ed9\u6211\u5427\u3002\u6211\u8981\u5c06\u4ed6\u732e\u7ed9\u7687\u4e0a\uff0c\u8fd8\u4e0d\u5fcd\u7acb\u5373\u5c06\u4ed6\u5f04\u6b7b\u7684\u3002\u201d \u3000\u3000\u4e91\u821e\u9633\u4e00\u58f0\u4e0d\u54cd\uff0c\u7a81\u7136\u8eab\u5f62\u4e00\u6643\uff0c\u4f38\u6307\u4e00\u5f39\uff0c\u53ea\u542c\u5f97\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u4e91\u821e\u9633\u7740\u4e86\u4e00\u638c\uff0c \u7f57\u91d1\u5cf0\u4e5f\u7ed9\u4ed6\u6233\u4e86\u4e00\u6307\uff0c\u8fd9\u4e24\u5927\u9ad8\u624b\u5404\u5b58\u987e\u5fcc\uff0c\u4ea4\u6362\u4e86\u8fd9\u4e00\u62db\uff0c\u5728\u4e92\u76f8\u62a2\u653b\u4e4b\u4e2d\u4ecd\u7136\u9632\u7740\u5bf9 \u65b9\u3002\u4e91\u821e\u9633\u4ee5\u4e0a\u4e58\u7684\u5185\u529f\u5378\u53bb\u4e86\u7f57\u91d1\u5cf0\u7684\u4e94\u6210\u52b2\u529b\uff0c\u4f46\u80a9\u5934\u4ecd\u89c9\u5982\u540c\u706b\u70d9\u4e00\u822c\uff1b\u7f57\u91d1\u5cf0\u95ed\u4e86 \u7a74\u9053\uff0c\u4f46\u4e2d\u4e86\u4ed6\u7684\u4e00\u6307\uff0c\u4e5f\u89c9\u5f97\u6c14\u95f7\u4e4b\u6781\u3002\u4e24\u4eba\u90fd\u662f\u5fc3\u4e2d\u9707\u9a87\uff0c\u201c\u8981\u662f\u521a\u624d\u53ea\u987e\u4f24\u5bf9\u65b9\u6027 \u547d\uff0c\u53cc\u65b9\u90fd\u6d3b\u4e0d\u6210\u3002\u201d \u3000\u3000\u7f57\u91d1\u5cf0\u95f7\u58f0\u8bf4\u9053\uff1a\u201c\u597d\u4fca\u7684\u4e00\u6307\u7985\u529f\uff01\u4e91\u5144\uff0c\u6211\u529d\u4f60\u8fd8\u662f\u5c11\u7528\u4e00\u70b9\u771f\u529b\uff0c\u4fdd\u91cd\u8eab\u4f53\u4e3a \u597d\u3002\u201d\u4e00\u6307\u7985\u529f\u6700\u8017\u7cbe\u795e\uff0c\u4e91\u821e\u9633\u518d\u62c6\u6570\u62db\uff0c\u5ffd\u89c9\u5fae\u6709\u51b7\u610f\uff0c\u77e5\u9053\u662f\u6240\u53d7\u7684\u6bd5\u51cc\u98ce\u90a3\u9634\u5bd2\u638c \u529b\u7684\u6bd2\u4f24\u53c8\u53d1\u4f5c\u4e86\u3002\u6309\u8bf4\u8fd9\u65f6\u7f57\u91d1\u5cf0\u53ea\u5b88\u4e0d\u653b\uff0c\u4ed6\u672c\u53ef\u820d\u6389\u9648\u7384\u673a\u800c\u53bb\uff0c\u4f46\u60f3\u8d77\u4e86\u5973\u513f\uff0c\u4ed6 \u53c8\u8e0c\u8e87\u4e0d\u5b9a\u4e86\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u5a07\u65a5\uff0c\u6709\u4eba\u8d70\u4e86\u8fdb\u6765\uff0c\u4e91\u821e\u9633\u4e00\u770b\uff0c\u6765\u7684\u6b63\u662f\u4ed6\u7684\u59bb\u5b50\uff01 \u3000\u3000\u4e91\u821e\u9633\u6291\u4e0d\u4f4f\u5fc3\u5934\u7684\u8df3\u52a8\uff0c\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u5b9d\u73e0\uff0c\u4f60\u56de\u6765\u4e86\u3002\u201d\u4e91\u592b\u4eba\u6b63\u662f\u53d1\u89c9\u7f57\u91d1\u5cf0\u4e0a \u5c71\uff0c\u8fd9\u624d\u8d76\u56de\u5bb6\u7684\u3002\u542c\u4e86\u4e91\u821e\u9633\u90a3\u4e00\u58f0\u51fa\u81ea\u771f\u60c5\u7684\u547c\u5524\uff0c\u5fc3\u5934\u4e00\u9178\uff0c\u60f3\u9053\uff1a\u201c\u5440\uff0c\u4ed6\u539f\u6765\u8fd8 \u60f3\u5ff5\u7740\u6211\u3002\u4ed6\u54ea\u77e5\u9053\u6211\u5e76\u4e0d\u662f\u4e3a\u4ed6\u800c\u56de\u3002\u201d \u3000\u3000\u4e91\u592b\u4eba\u548c\u4e08\u592b\u6362\u4e86\u4e00\u4e0b\u773c\u5149\uff0c\u5374\u4e0d\u548c\u4ed6\u8bf4\u8bdd\uff0c\u4e00\u4f38\u624b\u6298\u4e86\u4e00\u682a\u6885\u679d\uff0c\u5411\u7f57\u91d1\u5cf0\u51b7\u51b7\u65a5 \u9053\uff1a\u201c\u4f60\u6562\u5728\u4e91\u5bb6\u4f24\u4eba\uff1f\u5feb\u7ed9\u6211\u6eda\u51fa\u53bb\u3002\u201d\u6811\u679d\u4e00\u6296\uff0c\u4e00\u62db\u201c\u5212\u7834\u5929\u6cb3\u201d\uff0c\u4f7f\u7684\u7adf\u662f\u8fbe\u6469\u5251 \u6cd5\u7684\u62db\u6570\uff0c\u6296\u624b\u4e4b\u95f4\uff0c\u8fde\u523a\u7f57\u91d1\u5cf0\u80f8\u53e3\u7684\u201c\u7487\u7391\u201d\u201c\u7389\u8861\u201d\u201c\u5929\u9619\u201d\u4e09\u5904\u5927\u7a74\u3002 \u3000\u3000\u7f57\u91d1\u5cf0\u5728\u4e91\u821e\u9633\u5939\u653b\u4e4b\u4e0b\uff0c\u82e5\u7136\u8fd8\u662f\u53ea\u5b88\u4e0d\u653b\uff0c\u90a3\u5c31\u5f53\u771f\u662f\u5750\u4ee5\u5f85\u6bd9\u4e86\u3002\u4e91\u592b\u4eba\u7684\u201c\u6811 \u5251\u201d\u4e00\u5251\u523a\u4e0b\uff0c\u53ea\u89c1\u7f57\u91d1\u5cf0\u5728\u5730\u4e0a\u4e00\u6309\uff0c\u5411\u540e\u8e66\u51fa\u4e08\u4f59\uff0c\u5ffd\u5730\u51b7\u7b11\u9053\uff1a\u201c\u6211\u4ee5\u4e3a\u4f60\u5230\u77f3\u5bb6\u53bb \u4e86\uff0c\u5374\u539f\u6765\u8fd8\u662f\u4e91\u5bb6\u7684\u4eba\uff1f\u54c8\u54c8\uff0c\u4f60\u4eec\u4f09\u4fea\u60c5\u6df1\uff0c\u592b\u59bb\u4e0a\u9635\uff0c\u6211\u8fd9\u56de\u53ef\u771f\u662f\u975e\u8d70\u4e0d\u6210\u4e86\uff01\u201d \u3000\u3000\u4e91\u592b\u4eba\u6811\u5251\u4e00\u6296\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u8fd9\u56de\u4f60\u60f3\u8d70\u4e5f\u8d70\u4e0d\u6210\u5566\u3002\u821e\u9633\uff0c\u4f60\u770b\u770b\u7384\u673a\u53bb\u3002\u6211\u8fd9\u4e00 \u751f\u4ece\u6ca1\u6709\u6740\u8fc7\u4eba\uff0c\u4eca\u5929\u53ef\u8981\u7834\u6212\u4e86\uff01\u201d\u4e91\u592b\u4eba\u5fc3\u5934\u6012\u6781\uff0c\u4f46\u5979\u5e7c\u627f\u95fa\u8bad\uff0c\u867d\u7136\u52a8\u6012\uff0c\u8bf4\u8bdd\u4ecd \u662f\u5e73\u9759\u5982\u5e38\u3002\u53cd\u800c\u662f\u4e91\u821e\u9633\u7ed9\u5413\u4e86\u4e00\u8df3\u3002 \u3000\u3000\u4f46\u89c1\u4e91\u592b\u4eba\u6811\u5251\u8d77\u5904\uff0c\u867d\u7136\u662f\u4e00\u682a\u62c7\u6307\u7c97\u7684\u6811\u679d\uff0c\u7adf\u4e5f\u547c\u547c\u5e26\u98ce\uff0c\u201c\u5212\u7834\u5929\u6cb3\u201d\u201c\u9f99\u95e8 \u6d8c\u6d6a\u201d\u201c\u957f\u8679\u5c04\u65e5\u201d\u201c\u5ba2\u661f\u72af\u6708\u201d\uff0c\u4e00\u8fde\u51e0\u62db\uff0c\u201c\u5251\u5251\u201d\u90fd\u662f\u5241\u5411\u654c\u4eba\u8981\u5bb3\u3002 \u3000\u3000\u7f57\u91d1\u5cf0\u672c\u6765\u5c31\u662f\u60f3\u6fc0\u5f97\u4e91\u592b\u4eba\u52a8\u6c14\uff0c\u597d\u6270\u4e71\u5979\u7684\u5fc3\u795e\u3002\u54ea\u77e5\u5979\u867d\u7136\u52a8\u6c14\uff0c\u5251\u6cd5\u5374\u662f\u4e1d\u6beb \u4e0d\u4e71\uff0c\u4e00\u62db\u7d27\u8fc7\u4e00\u62db\uff0c\u5251\u5251\u4e0d\u79bb\u5df2\u8eab\u5927\u7a74\u3002\u7f57\u91d1\u5cf0\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u725f\u72ec\u9038\u662f\u4e09\u5341\u5e74\u524d\u6b66 \u6797\u516c\u8ba4\u7684\u7b2c\u4e00\u5251\u5ba2\uff0c\u8fd9\u5a46\u5a18\u7684\u5251\u6cd5\uff0c\u7adf\u4f3c\u4e0d\u4e9a\u4e8e\u5979\u7236\u4eb2\u7684\u76db\u5e74\uff01\u201d\u9ad8\u624b\u6bd4\u62fc\uff0c\u5bb9\u4e0d\u5f97\u4e1d\u6beb\u5206 \u5fc3\uff0c\u7f57\u91d1\u5cf0\u8fd9\u65f6\u51dd\u795e\u5bf9\u654c\uff0c\u518d\u4e5f\u65e0\u6687\u8ba5\u8c04\uff0c\u4ee5\u5927\u529b\u91d1\u521a\u638c\u82e6\u6597\u4e91\u592b\u4eba\u7684\u8fbe\u6469\u5251\u6cd5\u3002 \u3000\u3000\u4e91\u592b\u4eba\u7684\u529f\u529b\u900a\u4e8e\u4e08\u592b\uff0c\u8fbe\u6469\u5251\u6cd5\u5728\u5979\u624b\u4e2d\u5c55\u5f00\uff0c\u67d4\u591a\u4e8e\u521a\uff0c\u522b\u5177\u4e00\u683c\uff0c\u4f46\u89c1\u90a3\u682a\u6811\u679d \u88ab\u638c\u529b\u9707\u8361\uff0c\u6709\u5982\u94f6\u86c7\u4e71\u63a3\u6781\u5f97\u8f7b\u7075\u7fd4\u52a8\u4e4b\u5999\uff0c\u4efb\u662f\u7f57\u91d1\u5cf0\u7684\u638c\u52bf\u5982\u4f55\u521a\u731b\uff0c\u5374\u603b\u626b\u4e0d\u65ad\u5979 \u7684\u6811\u679d\u3002 \u3000\u3000\u4e91\u821e\u9633\u60f3\u53bb\u770b\u9648\u7384\u673a\uff0c\u5374\u53c8\u4e0d\u653e\u5fc3\u59bb\u5b50\uff0c\u770b\u4e86\u4e00\u9635\uff0c\u8fd9\u624d\u677e\u4e86\u53e3\u6c14\uff0c\u60f3\u9053\uff1a\u201c\u4e8c\u5341\u5e74 \u6765\uff0c\u6211\u4ece\u4e0d\u5173\u5fc3\u5979\u7684\u6b66\u529f\u8fdb\u5883\uff0c\u539f\u6765\u5979\u7684\u5251\u6cd5\u4e5f\u7cbe\u5999\u5982\u65af\uff0c\u7f57\u91d1\u5cf0\u7684\u529f\u529b\u867d\u7136\u7a0d\u9ad8\uff0c\u4f46\u4e0e\u6211 \u4e45\u6218\u4e4b\u4f59\uff0c\u8c05\u4e0d\u662f\u5979\u7684\u5bf9\u624b\u3002\u201d \u3000\u3000\u5f53\u4e0b\u8dd1\u8fc7\u53bb\u4e0e\u9648\u7384\u673a\u628a\u8109\uff0c\u4f46\u89c9\u8109\u8c61\u6df7\u4e71\uff0c\u5ffd\u800c\u72c2\u8df3\uff0c\u5ffd\u800c\u53c8\u7ec6\u82e5\u6e38\u4e1d\uff0c\u4e91\u821e\u9633\u5fc3\u5934\u4e00 \u6c89\uff0c\u9648\u7384\u673a\u679c\u7136\u662f\u53d7\u4e86\u5f88\u91cd\u7684\u5185\u4f24\u3002\u201c\u82e5\u8fd8\u5269\u4e0b\u4e00\u9897\u5c0f\u8fd8\u4e39\u5c31\u597d\u4e86\uff0c\u53ef\u662f\u8fd9\u65f6\u5374\u5230\u90a3\u91cc\u53bb\u6c42 \u53d6\u5c0f\u8fd8\u4e39\u3002\u201d\u4e91\u821e\u9633\u5fc3\u4e2d\u7740\u6025\uff0c\u9762\u4e0a\u5374\u4e0d\u6562\u9732\u51fa\u4e1d\u6beb\u795e\u8272\uff0c\u6050\u6015\u59bb\u5b50\u5206\u5fc3\u3002\u4f46\u542c\u5f97\u4e91\u592b\u4eba\u626c \u58f0\u53eb\u9053\uff1a\u201c\u4ed6\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u4e91\u821e\u9633\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u6211\u8fd9\u4f1a\u5c31\u7ed9\u4ed6\u63a8\u8840\u8fc7\u5bab\u3002\u201d\u5176\u5b9e\u9648\u7384\u673a\u6240\u53d7\u7684\u5185\u4f24\u90a3\u91cc\u662f\u63a8\u8840\u8fc7 \u5bab\u6240\u80fd\u6551\u6cbb\uff0c\u4e91\u821e\u9633\u5fc3\u4e2d\u6b63\u81ea\u7126\u6025\uff0c\u5ffd\u542c\u5f97\u5c71\u540e\u4f20\u6765\u6e05\u5618\u4e4b\u58f0\uff0c\u542c\u90a3\u58f0\u97f3\u6765\u5904\uff0c\u8fdc\u5728\u6570\u91cc\u4e4b \u5916\uff0c\u5374\u662f\u975e\u5e38\u6e05\u6670\uff0c\u4e00\u58f0\u63a5\u7740\u4e00\u58f0\uff0c\u957f\u58f0\u4f3c\u9e64\u5524\u957f\u7a7a\uff0c\u77ed\u58f0\u4f3c\u864e\u5578\u5e7d\u8c37\uff0c\u663e\u7136\u4e0d\u662f\u4e00\u4eba\u6240 \u53d1\u3002 \u3000\u3000\u7f57\u91d1\u5cf0\u54c8\u54c8\u4e00\u7b11\uff0c\u63a5\u7740\u4e5f\u957f\u5578\u8d77\u6765\uff0c\u4e91\u821e\u9633\u52c3\u7136\u53d8\u8272\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u597d\u5475\uff0c\u7f57\u5927\u4eba\u5c45\u7136 \u62db\u670b\u5f15\u53cb\uff0c\u5149\u4e34\u5bd2\u820d\uff0c\u4e91\u67d0\u5c82\u6562\u4e0d\u8fce\u63a5\u5609\u5bbe\uff1f\u201d\u7a7a\u7136\u4e5f\u53d1\u58f0\u957f\u5578\uff0c\u5578\u58f0\u5982\u6d6a\u6d9b\u62cd\u5cb8\uff0c\u88c2\u77f3\u7a7f \u4e91\uff0c\u628a\u7f57\u91d1\u5cf0\u7684\u5578\u58f0\u5b8c\u5168\u63a9\u76d6\uff0c\u7f57\u91d1\u5cf0\u53ea\u89c9\u8033\u9f13\u7ed9\u9707\u5f97\u55e1\u55e1\u4f5c\u54cd\uff0c\u5fc3\u795e\u7f2d\u7ed5\u5f97\u7eb7\u4e71\u4e0d\u5b81\u3002 \u3000\u3000\u539f\u6765\u7f57\u91d1\u5cf0\u7684\u5578\u58f0\u662f\u7ed9\u4f19\u4f34\u7684\u8baf\u53f7\uff0c\u4e91\u821e\u9633\u7684\u5578\u58f0\u5374\u662f\u4ee5\u6781\u4e0a\u4e58\u7684\u5185\u529f\u74e6\u89e3\u4ed6\u7684\u6218\u610f\uff0c \u500f\u7136\u95f4\uff0c\u8fd9\u51e0\u79cd\u5578\u58f0\u4e00\u9f50\u505c\u6b62\uff0c\u53ea\u6709\u7f57\u91d1\u5cf0\u5c1a\u81ea\u5634\u5507\u5f00\u5408\uff0c\u4f46\u58f0\u97f3\u5636\u54d1\uff0c\u98a4\u6296\u65ad\u7eed\uff0c\u51e0\u4e4e\u5df2 \u662f\u5578\u4e0d\u6210\u58f0\uff01 \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u4e91\u592b\u4eba\u6811\u679d\u4e00\u6296\uff0c\u5728\u7f57\u91d1\u5cf0\u7684\u624b\u8155\u4e0a\u523a\u7a7f\u4e86\u516d\u4e03\u4e2a\u5c0f\u5b54\uff0c\u7f57\u91d1\u5cf0\u5927\u53eb\u4e00\u58f0\uff0c \u8eab\u5b50\u51cc\u7a7a\u98de\u8d77\uff0c\u5411\u7740\u9648\u7384\u673a\u6240\u8eba\u4e4b\u5904\u6251\u6765\uff0c\u4e91\u821e\u9633\u4e0d\u5f85\u4ed6\u811a\u8e0f\u5b9e\u5730\uff0c\u5c31\u662f\u4e00\u4e2a\u5288\u7a7a\u638c\u53d1\u51fa\uff0c \u53ea\u89c1\u7f57\u91d1\u5cf0\u62ac\u8d77\u624b\u81c2\uff0c\u4f3c\u6b32\u62db\u67b6\uff0c\u4f46\u8f6f\u7ef5\u7ef5\u7684\u7adf\u662f\u65e0\u529b\u9ad8\u4e3e\uff0c\u539f\u6765\u4ed6\u624b\u8155\u7684\u4e03\u6761\u7b4b\u8109\uff0c\u5df2\u7ed9 \u4e91\u592b\u4eba\u7684\u201c\u6811\u5251\u201d\u5728\u4e00\u62db\u4e4b\u5185\u90fd\u6311\u65ad\u4e86\uff01\u4e91\u821e\u9633\u8fd9\u4e00\u638c\u6253\u51fa\uff0c\u6709\u5982\u6467\u67af\u62c9\u673d\uff0c\u767b\u65f6\u628a\u7f57\u91d1\u5cf0 \u9707\u5012\u5730\u4e0a\uff0c\u6c14\u7edd\u8eab\u4ea1\uff01 \u3000\u3000\u4e91\u592b\u4eba\u62ed\u6389\u6811\u679d\u4e0a\u7684\u8840\u73e0\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u4f60\u52a9\u6211\u9664\u6b64\u6076\u8d3c\u3002\u201d\u4e91\u821e\u9633\u9053\uff1a\u201c\u8bf4\u5230\u591a \u8c22\uff0c\u4e8c\u5341\u5e74\u6765\uff0c\u6211\u4e0d\u77e5\u8be5\u5411\u4f60\u8bf4\u51e0\u5343\u4e07\u904d\uff01\u201d\u8fd9\u662f\u4ed6\u4eec\u592b\u59bb\u4fe9\u7b2c\u4e00\u6b21\u5408\u529b\u5bf9\u654c\uff0c\u4e5f\u662f\u4e91\u821e\u9633 \u7b2c\u4e00\u6b21\u542c\u5230\u59bb\u5b50\u5411\u4ed6\u9053\u8c22\uff0c\u4f46\u89c9\u5fc3\u4e2d\u65e2\u751c\u53c8\u82e6\uff0c\u60f3\u8d77\u8fd9\u5eff\u5e74\u6765\u5bf9\u5979\u7684\u51b7\u6de1\u65e0\u60c5\uff0c\u8fd9\u7f6a\u5b7d\u5b9e\u4e0d \u5728\u4ed6\u5bf9\u5973\u513f\u4edf\u4fae\u7684\u90a3\u6869\u7f6a\u5b7d\u4e4b\u4e0b\u3002 \u3000\u3000\u4e91\u592b\u4eba\u4e5f\u662f\u7b2c\u4e00\u6b21\u542c\u5230\u4e08\u592b\u7684\u8877\u5fc3\u9053\u6b49\uff0c\u5fcd\u4e0d\u4f4f\u6ef4\u51fa\u4e00\u9897\u6cea\u73e0\uff0c\u5ffd\u542c\u5f97\u4e91\u821e\u9633\u53eb\u9053\uff1a \u201c\u5b9d\u73e0\uff0c\u7559\u795e\uff0c\u6697\u5668\u6765\u4e86\uff01\u201d\u500f\u7136\u95f4\u51e0\u679a\u6697\u5668\u7a7f\u8fc7\u6885\u679d\u6253\u4e86\u8fdb\u6765\uff0c\u4e91\u592b\u4eba\u6811\u679d\u4e00\u62c2\uff0c\u5c06\u4e24\u679a \u94c1\u84ba\u85dc\u62c2\u843d\uff0c\u821e\u9633\u53cc\u6307\u8fde\u5f39\uff0c\u94ee\u94ee\u4e24\u58f0\uff0c\u4e5f\u628a\u4e24\u67c4\u98de\u5200\uff0c\u5f39\u51fa\u5899\u5916\uff0c\u5c31\u5728\u8fd9\u65f6\uff0c\u89d2\u95e8\u7ed9\u4eba\u4e00 \u811a\u8e22\u5f00\uff0c\u8fdb\u6765\u4e86\u4e00\u4e2a\u9752\u888d\u9053\u4eba\uff0c\u4e24\u4e2a\u9ed1\u8863\u6b66\u58eb\uff01 \u3000\u3000\u4e91\u821e\u9633\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u592a\u7384\u9053\u957f\uff0c\u4e45\u8fdd\uff0c\u4e45\u8fdd\uff0c\u606d\u559c\u4f60\u5728\u671d\u5ef7\u5f97\u610f\u4e86\u3002\u53ea\u662f\u505a\u7f57\u91d1\u5cf0\u7684\u526f \u624b\uff0c\u672a\u514d\u59d4\u5c48\u4e9b\u513f\uff01\u201d\u539f\u6765\u8fd9\u592a\u7384\u9053\u957f\u4e43\u662f\u4ee5\u524d\u9648\u53cb\u8c05\u5e10\u4e0b\u7684\u7b2c\u4e00\u9ad8\u624b\uff0c\u5143\u672b\u4e4b\u4e16\uff0c\u7fa4\u96c4\u7eb7 \u8d77\uff0c\u4ee5\u6731\u5143\u748b\u3001\u9648\u53cb\u8c05\u3001\u5f20\u58eb\u8bda\u4e09\u80a1\u52bf\u529b\u6700\u5927\uff0c\u9648\u53cb\u8c05\u5f53\u5e74\u4e3a\u4e86\u62b5\u6297\u6731\u5143\u748b\uff0c\u66fe\u4e0e\u5f20\u58eb\u8bda\u8054 \u76df\uff0c\u6545\u6b64\u4e91\u821e\u9633\u4e0e\u592a\u7384\u9053\u957f\u4e5f\u66fe\u89c1\u8fc7\u6570\u9762\uff0c\u9648\u53cb\u8c05\u8986\u8d25\u4e4b\u540e\u3002\u592a\u7384\u9053\u4eba\u6539\u6295\u6731\u5143\u748b\uff0c\u505a\u5230\u9526 \u8863\u536b\u7684\u603b\u6559\u5934\uff0c\u4f4d\u7f6e\u4ec5\u6b21\u4e8e\u7f57\u91d1\u5cf0\uff0c\u7f57\u91d1\u5cf0\u4e0a\u6b21\u8fdb\u5c71\u6e38\u8bf4\u4e91\u821e\u9633\u4e4b\u65f6\uff0c\u5c31\u66fe\u62ff\u4ed6\u4f5c\u8fc7\u4f8b\u5b50\u3002 \u3000\u3000\u592a\u7384\u9053\u4eba\u65e9\u8fb0\u4ece\u7f57\u91d1\u5cf0\u6240\u53d1\u7684\u5578\u58f0\u4e2d\uff0c\u77e5\u9053\u4ed6\u5728\u8fd9\u91cc\u4e0e\u4eba\u52a8\u624b\uff0c\u4e0d\u6599\u8d76\u5230\u4e4b\u65f6\uff0c\u7f57\u91d1\u5cf0 \u5df2\u662f\u5c38\u6a2a\u5730\u4e0a\uff0c\u592a\u7384\u9053\u4eba\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u5374\u4f6f\u4f5c\u4e0d\u77e5\uff0c\u95ee\u9053\uff1a\u201c\u4e91\u5144\uff0c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u4e91\u821e\u9633\u51b7\u51b7\u7b54\u9053\uff1a\u201c\u7f57\u91d1\u5cf0\u4f24\u4e86\u6211\u7684\u5ba2\u4eba\uff0c\u6211\u6740\u4e86\u4ed6\uff01\u201d\u592a\u7384\u9053\u4eba\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u4e0d\u662f\u9648\u7384\u673a \u4e48\uff1f\u201d\u4e91\u821e\u9633\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u592a\u7384\u9053\u4eba\u9053\uff1a\u201c\u96be\u9053\u7f57\u5927\u4eba\u6ca1\u6709\u5411\u4f60\u8bf4\u660e\uff1a\u8fd9\u5c0f\u5b50\u4e43\u662f\u671d\u5ef7\u6240\u8981\u641c\u6355\u7684\u72af\u4eba\u3002\u201d\u4e91\u821e\u9633 \u9053\uff1a\u201c\u8bf4\u8fc7\u4e86\uff01\u201d\u592a\u7384\u9053\u4eba\u53cc\u7709\u4e00\u7ad6\u3002\u9053\uff1a\u201c\u4e91\u821e\u9633\uff0c\u8fd9\u5c31\u662f\u4f60\u7684\u4e0d\u662f\u4e86\u3002\u4f60\u4e0e\u7f57\u5927\u4eba\u8bf4 \u597d\uff0c\u613f\u52a9\u4ed6\u4e00\u81c2\u4e4b\u529b\uff0c\u5c06\u5f20\u58eb\u8bda\u9057\u5b7d\u65a9\u8349\u9664\u6839\uff0c\u5374\u600e\u4e48\u53cd\u800c\u5305\u5e87\u53db\u515a\uff0c\u5c06\u7f57\u5927\u4eba\u6740\u4e86\uff1f\u201d\u4e91 \u821e\u9633\u9053\uff1a\u201c\u8fd9\u53c8\u6709\u4ec0\u4e48\u4e0d\u662f\u4e86\uff1f\u5012\u8981\u8bf7\u6559\uff1f\u201d\u592a\u7384\u9053\u4eba\u6c14\u9053\uff1a\u201c\u4f60\u662f\u6b66\u6797\u4e2d\u7684\u6cf0\u5c71\u5317\u6597\uff0c\u5c82 \u6709\u8fde\u8fd9\u70b9\u9053\u7406\u4e5f\u4e0d\u61c2\u4e4b\u7406\uff0c\u6b66\u6797\u4e2d\u4eba\u6700\u8bb2\u4fe1\u4e49\uff0c\u50cf\u4f60\u8fd9\u6837\u53cd\u590d\u65e0\u4fe1\uff0c\u8be5\u7b97\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e91\u821e\u9633\u51b7\u7b11\u9053\uff1a\u201c\u592a\u7384\u9053\u5144\uff0c\u6211\u8bb0\u5f97\u4f60\u662f\u9648\u53cb\u8c05\u7684\u5fc3\u8179\u6b7b\u58eb\u3002\u5374\u600e\u7684\u5982\u4eca\u53c8\u505a\u4e86\u6731\u5143\u748b \u7684\u9526\u8863\u536b\u603b\u6559\u5934\uff1f\u4e0d\u77e5\u8fd9\u53c8\u8be5\u7b97\u4ec0\u4e48\uff1f\u201d\u592a\u7384\u9053\u4eba\u6c14\u5f97\u53cc\u773c\u7ffb\u767d\uff0c\u6012\u9053\uff1a\u201c\u539f\u6765\u4f60\u8fd8\u662f\u5fe0\u4e8e \u6545\u4e3b\uff0c\u6545\u610f\u5c06\u7f57\u5927\u4eba\u8bf1\u6740\uff01\u201d \u3000\u3000\u4e91\u821e\u9633\u5927\u7b11\u9053\uff1a\u201c\u96be\u9053\u4e00\u4e2a\u4eba\u603b\u8981\u627e\u4e00\u4e2a\u4e3b\u5b50\u5417\uff1f\u54c8\u54c8\uff0c\u4f60\u731c\u9519\u4e86\u3002\u4f60\u4e00\u5b9a\u8981\u77e5\u9053\u6211\u4e3a \u4f55\u8981\u6740\u7f57\u91d1\u5cf0\u5417\uff1f\u597d\uff0c\u90a3\u4e5f\u4e0d\u59a8\u8bf4\u7ed9\u4f60\u542c\u3002\u4e00\u534a\u662f\u56e0\u4e3a\u4ed6\u4f24\u4e86\u6211\u7684\u5ba2\u4eba\uff0c\u53e6\u4e00\u534a\u561b\uff0c\u6b63\u662f\u4e3a \u4f60\u5475\uff01\u201d\u592a\u7384\u9053\u4eba\u9053\uff1a\u201c\u600e\u4e48\u662f\u4e3a\u4e86\u6211\uff1f\u201d\u4e91\u821e\u9633\u7b11\u9053\uff1a\u201c\u514d\u5f97\u4f60\u59d4\u59d4\u5c48\u5c48\u505a\u7f57\u91d1\u5cf0\u7684\u526f\u624b \u5475\uff01\u201d \u3000\u3000\u592a\u7384\u9053\u4eba\u5927\u6012\u9053\uff1a\u201c\u4e91\u821e\u9633\uff0c\u4f60\u5c45\u7136\u81ea\u6043\u6b66\u529f\uff0c\u51fa\u8a00\u620f\u4fae\uff01\u201d\u4e24\u4eba\u5982\u7bad\u5728\u5f26\uff0c\u5373\u5c06\u52a8 \u624b\uff0c\u5de6\u4fa7\u90a3\u4e2a\u9ed1\u8863\u6b66\u58eb\u5ffd\u7136\u8e0f\u4e0a\u4e00\u6b65\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u4eba\u5404\u6709\u5fd7\uff0c\u4f60\u65e2\u7136\u4e0d\u613f\u6295\u6548\u671f\u5ef7\uff0c\u90a3\u81ea \u662f\u4e0d\u4fbf\u76f8\u5f3a\u3002\u54b1\u4eec\u5c31\u6309\u6c5f\u6e56\u9053\u4e0a\u7684\u89c4\u77e9\u529e\u4e8b\u3002\u8bf7\u4f60\u8d4f\u4e00\u4e2a\u9762\uff0c\u8fd9\u5c0f\u5b50\u8ba9\u6211\u4eec\u5e26\u56de\u3002\u7f57\u91d1\u5cf0\u7684 \u4e8b\uff0c\u54b1\u4eec\u4e0d\u518d\u8ffd\u7a76\u4e86\u3002\u201d\u8fd9\u4e24\u4e2a\u6b66\u58eb\u5fcc\u60ee\u4e91\u821e\u9633\u4e86\u5f97\uff0c\u592a\u7384\u9053\u4eba\u4e00\u60f3\uff0c\u5df1\u65b9\u867d\u6709\u4e09\u4eba\uff0c\u672a\u5fc5 \u80dc\u5f97\u4e86\u4ed6\u4eec\u592b\u5987\uff0c\u5fcd\u6c14\u4e0d\u8a00\u3002 \u3000\u3000\u4e91\u821e\u9633\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u76ef\u4e86\u90a3\u9ed1\u8863\u6b66\u571f\u4e00\u773c\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u6211\u9053\u662f\u8c01\uff0c\u539f\u6765\u662f\u5ce8\u5d4b\u5251\u5ba2 \u9633\u8d85\u8c37\uff01\u597d\u5440\uff0c\u4f60\u4eec\u8981\u5c06\u9648\u7384\u673a\u5e26\u8d70\u4e5f\u5e76\u4e0d\u96be\uff0c\u7559\u4e0b\u4e24\u4e2a\u4eba\u6765\u4e0e\u6211\u4ea4\u6362\uff0c\u4f60\u4eec\u81ea\u5df1\u5546\u8bae\uff0c\u613f \u610f\u7559\u4e0b\u90a3\u4e24\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u8fd9\u9633\u8d85\u8c37\u662f\u5ce8\u5d4b\u6d3e\u4e2d\u6570\u4e00\u6570\u4e8c\u7684\u9ad8\u624b\uff0c\u5e73\u751f\u4e5f\u6781\u81ea\u8d1f\uff0c\u53ea\u56e0\u5bf9\u624b\u662f\u4e91\u821e\u9633\uff0c\u800c\u7f57\u91d1\u5cf0\u4e4b \u6b7b\u5bf9\u4ed6\u4ea6\u662f\u6709\u5229\u65e0\u5bb3\uff0c\u6545\u6b64\u624d\u613f\u4e0e\u4e91\u821e\u9633\u548c\u89e3\uff0c\u54ea\u77e5\u4e91\u821e\u9633\u4e00\u70b9\u4e0d\u7559\u60c5\u9762\uff0c\u518d\u5ea6\u51fa\u8a00\u620f\u5f04\uff0c \u9633\u8d85\u8c37\u4e5f\u6c89\u4e0d\u7740\u6c14\u4e86\uff0c\u84e6\u7136\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u5012\u662f\u4e2a\u597d\u4e3b\u610f\uff01\u6211\u5c31\u7559\u4e0b\u4e24\u4e2a\u4eba\u4e0e\u4f60\u4ea4\u6362\uff0c\u8fd9\u4e24\u4e2a \u4eba\u90fd\u662f\u4f60\u76f8\u8bc6\u7684\u5927\u540d\u9f0e\u9f0e\u7684\u4eba\u7269\uff0c\u6362\u4e00\u4e2a\u65e0\u540d\u5c0f\u5b50\uff0c\u603b\u8be5\u503c\u5f97\u4e86\u5427\uff1f\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u4e91\u821e\u9633\u4e5f \u6014\u4e86\u4e00\u6014\uff0c\u7741\u773c\u770b\u65f6\uff0c\u53ea\u89c1\u9633\u8d85\u8c37\u5ffd\u5730\u89e3\u4e0b\u4e86\u80cc\u4e0a\u7684\u5927\u7ea2\u5305\u88b1\uff0c\u89e3\u5f00\u4e00\u770b\uff0c\u91cc\u9762\u5305\u7684\u7adf\u662f\u4e24 \u9897\u8840\u6dcb\u6dcb\u7684\u4eba\u5934\uff0c\u4e91\u592b\u4eba\u4e0d\u7531\u81ea\u5df1\u7684\u9a87\u53eb\u4e00\u58f0\uff0c\u8fd9\u4e24\u4e2a\u4eba\u7adf\u662f\u4e03\u4fee\u9053\u4eba\u4e0e\u84b2\u575a\uff01 \u3000\u3000\u539f\u6765\u592a\u7384\u9053\u4eba\u548c\u8fd9\u4e24\u4e2a\u9ed1\u8863\u6b66\u58eb\u6b63\u662f\u4e3a\u4e86\u8ffd\u6355\u9648\u7384\u673a\u800c\u6765\u5230\u8d3a\u5170\u5c71\u7684\u4e09\u4e2a\u5927\u5185\u9ad8\u624b\uff0c\u8fd9 \u4e09\u4eba\u5728\u5c71\u4e0b\u78b0\u5230\u4e86\u4e03\u4fee\u9053\u4eba\u4e0e\u84b2\u575a\uff0c\u77e5\u9053\u4ed6\u4eec\u662f\u4ece\u8499\u53e4\u6f5c\u56de\u7684\u5f20\u58eb\u8bda\u65e7\u90e8\uff0c\u4fbf\u5408\u529b\u5c06\u4ed6\u4eec\u6740 \u4e86\u3002\u4e91\u821e\u9633\u89c1\u4e86\u8fd9\u4e24\u9897\u4eba\u5934\uff0c\u4e5f\u81ea\u5fc3\u4e2d\u4e00\u51db\u3002\u8981\u77e5\u4e03\u4fee\u9053\u4eba\u7684\u4e03\u4fee\u5251\u6cd5\u5a01\u9707\u6c5f\u6e56\uff0c\u867d\u8bf4\u84b2\u575a \u90a3\u65e5\u66fe\u53d7\u4e86\u77f3\u5929\u94ce\u7684\u4e00\u638c\u4e4b\u4f24\uff0c\u4f46\u8fd9\u4e09\u4eba\u5c45\u7136\u80fd\u591f\u5c06\u4e03\u4fee\u9053\u4eba\u6740\u6389\uff0c\u5374\u662f\u9887\u51fa\u4e91\u821e\u9633\u7684\u610f\u6599 \u4e4b\u5916\u3002 \u3000\u3000\u9633\u8d85\u8c37\u9053\uff1a\u201c\u600e\u4e48\uff1f\u8fd9\u4ea4\u6613\u6709\u4f60\u7684\u4fbf\u5b9c\uff01\u201d\u4e91\u821e\u9633\u51b7\u7b11\u9053\uff1a\u201c\u5f88\u597d\uff0c\u4e24\u4e2a\u6b7b\u7684\u5f53\u4f5c\u4e00\u4e2a \u6d3b\u7684\uff0c\u8fd8\u6709\u4e00\u4e2a\uff0c\u5c31\u5c06\u4f60\u5145\u6570\u4e86\u5427\uff01\u201d\u84e6\u7136\u95f4\u4e00\u638c\u5288\u51fa\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u592a\u7384\u9053\u4eba\u53f3\u4fa7\u7684 \u90a3\u4e2a\u9ed1\u8863\u6b66\u58eb\u628a\u624b\u4e00\u626c\uff0c\u4e24\u628a\u6885\u82b1\u91d1\u9488\u5206\u5411\u4e91\u821e\u9633\u592b\u59bb\u5c04\u53bb\u3002\u8fd9\u4e2a\u9ed1\u8863\u6b66\u58eb\u540d\u53eb\u6851\u4ee4\u72d0\uff0c\u540d \u5934\u867d\u7136\u8fdc\u8fdc\u4e0d\u53ca\u592a\u7384\u9053\u4eba\u548c\u5ce8\u5d4b\u5251\u9633\u8d85\u8c37\u7684\u54cd\u4eae\uff0c\u5374\u662f\u4e00\u4f4d\u4e13\u4f7f\u9634\u6bd2\u6697\u5668\u7684\u597d\u624b\u3002\u4e03\u4fee\u9053\u4eba \u5c31\u662f\u5148\u4e2d\u4e86\u4ed6\u7684\u6697\u5668\uff0c\u624d\u7ed9\u9633\u8d85\u8c37\u6740\u6389\u7684\u3002 \u3000\u3000\u4f46\u542c\u5f97\u547c\u7684\u4e00\u58f0\uff0c\u5c04\u5411\u4e91\u821e\u9633\u7684\u90a3\u4e00\u628a\u6885\u82b1\u91d1\u9488\uff0c\u5168\u90fd\u53cd\u5c04\u56de\u53bb\uff0c\u5413\u5f97\u6851\u4ee4\u72d0\u6eda\u5012\u5730 \u4e0a\uff0c\u597d\u4e0d\u5bb9\u6613\u624d\u907f\u8fc7\u81ea\u5df1\u6240\u53d1\u7684\u8fd9\u4e00\u628a\u91d1\u9488\u3002\u4e91\u592b\u4eba\u6ca1\u6709\u4e08\u592b\u7684\u529f\u529b\uff0c\u5979\u4e0d\u6562\u7528\u5288\u7a7a\u638c\uff0c\u5374 \u7528\u7edd\u5999\u7684\u8f7b\u8eab\u529f\u592b\uff0c\u63d0\u6c14\u4e00\u7eb5\uff0c\u4e00\u628a\u91d1\u9488\u521a\u597d\u8d34\u7740\u5979\u7684\u5f13\u978b\u5e95\u4e0b\u5c04\u8fc7\u3002\u4e91\u592b\u4eba\u5728\u7a7a\u4e2d\u4e00\u4e2a\u9e5e \u5b50\u7ffb\u8eab\uff0c\u201c\u6811\u5251\u201d\u5237\u7684\u4e00\u58f0\uff0c\u4fbf\u5411\u9633\u8d85\u8c37\u51cc\u7a7a\u523a\u4e0b\u3002 \u3000\u3000\u9633\u8d85\u8c37\u5927\u559d\u4e00\u58f0\uff0c\u4e24\u9897\u4eba\u5934\u8131\u624b\u63b7\u51fa\uff0c\u4e91\u821e\u9633\u95ea\u8eab\u907f\u8fc7\uff0c\u811a\u8e0f\u6d2a\u95e8\uff0c\u5f53\u80f8\u4fbf\u662f\u4e00\u638c\u3002\u592a \u7384\u9053\u4eba\u4e00\u5c55\u62c2\u5c18\uff0c\u642d\u7740\u4e86\u4e91\u592b\u4eba\u7684\u6811\u679d\u3002 \u3000\u3000\u592a\u7384\u9053\u4eba\u7684\u62c2\u5c18\uff0c\u7528\u7684\u662f\u4e00\u80a1\u9634\u67d4\u7684\u52b2\u529b\uff0c\u4e91\u592b\u4eba\u6811\u679d\u4e00\u8361\uff0c\u6ca1\u6709\u6446\u8131\uff1b\u90a3\u8fb9\u53a2\uff0c\u9633\u8d85 \u8c37\u786c\u63a5\u4e86\u4e91\u821e\u9633\u7684\u4e00\u638c\uff0c\u8dc4\u8dc4\u8e09\u8e09\u7684\u5012\u9000\u6570\u6b65\uff0c\u4e91\u821e\u9633\u6362\u4e86\u53e3\u6c14\uff0c\u5012\u8e29\u4e86\u4e03\u661f\u6b65\uff0c\u4e00\u4e2a\u8f6c \u8eab\uff0c\u53cd\u638c\u4e00\u5288\uff0c\u6a2a\u5207\u592a\u7384\u9053\u4eba\u7684\u624b\u8155\u3002 \u3000\u3000\u592a\u7384\u9053\u4eba\u5c06\u62c2\u5c18\u4e00\u626f\uff0c\u610f\u6b32\u628a\u4e91\u592b\u4eba\u626f\u5c06\u8fc7\u6765\uff0c\u6321\u8fd9\u4e00\u638c\uff0c\u5374\u7ed9\u4e91\u592b\u4eba\u8d81\u52bf\u5c06\u6811\u5251\u5411\u524d \u4e00\u63a2\uff0c\u89e3\u5f00\u4e86\u62c2\u5c18\u7684\u67d4\u52b2\uff0c\u6811\u5251\u8131\u4e86\u51fa\u6765\uff0c\u4e00\u6296\u624b\u4fbf\u523a\u592a\u7384\u9053\u4eba\u7684\u53cc\u76ee\uff01 \u3000\u3000\u8fd9\u51e0\u62db\u5feb\u5982\u7535\u5149\u77f3\u706b\uff0c\u592a\u7384\u9053\u4eba\u5012\u8f6c\u62c2\u5c18\uff0c\u67b6\u5f00\u4e86\u4e91\u592b\u4eba\u7684\u6811\u5251\uff0c\u5de6\u638c\u5f80\u5916\u4e00\u767b\uff0c\u548c\u4e91 \u821e\u9633\u5bf9\u4e86\u4e00\u638c\uff0c\u4e91\u821e\u9633\u56e0\u4e3a\u5143\u6c14\u5927\u4f24\uff0c\u8fd9\u4e00\u638c\u4e0d\u6562\u8fd0\u7528\u5185\u5bb6\u771f\u529b\uff0c\u4f46\u592a\u7384\u9053\u4eba\u4e5f\u56e0\u4e3a\u4e24\u9762\u5e94 \u6218\uff0c\u8fd9\u4e00\u638c\u548c\u4e91\u821e\u9633\u521a\u521a\u6253\u6210\u5e73\u624b\u3002 \u3000\u3000\u4e91\u592b\u4eba\u7684\u5251\u6cd5\u8f7b\u7075\u8fc5\u6377\uff0c\u4e00\u5251\u5288\u5f00\uff0c\u7b2c\u4e8c\u5251\u7b2c\u4e09\u5251\u63a5\u8fde\u800c\u81f3\uff0c\u592a\u7384\u9053\u4eba\u672a\u53ca\u5012\u8f6c\u62c2\u5c18\uff0c \u62db\u6570\u65bd\u5c55\u4e0d\u5f00\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u7adf\u7ed9\u5979\u903c\u5f97\u8fde\u8fde\u540e\u9000\u3002\u6851\u4ee4\u72d0\u722c\u4e86\u8d77\u6765\uff0c\u6296\u624b\u53d1\u51fa\u4e24\u652f\u900f\u9aa8\u9489\uff0c \u4e91\u592b\u4eba\u7528\u6811\u679d\u6253\u843d\uff0c\u592a\u7384\u9053\u4eba\u677e\u4e86\u53e3\u6c14\uff0c\u8fd9\u624d\u7ad9\u5f97\u7a33\u6b65\u3002 \u3000\u3000\u5ce8\u5d4b\u5251\u5ba2\u9633\u8d85\u8c37\u4e5f\u662f\u4e2a\u6b66\u5b66\u7684\u5927\u884c\u5bb6\uff0c\u867d\u7136\u8f93\u4e86\u4e00\u638c\uff0c\u5374\u4e5f\u8bd5\u51fa\u4e86\u4e91\u821e\u9633\u7684\u4e2d\u6c14\u4e0d\u8db3\uff0c \u638c\u529b\u5148\u5f3a\u540e\u5f31\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u62d4\u51fa\u4e86\u96cc\u96c4\u53cc\u5251\uff0c\u7acb\u523b\u4e0a\u524d\u52a9\u9635\uff0c\u5de6\u523a\u4e91\u821e\u9633\uff0c\u53f3\u523a\u4e91\u592b\u4eba\uff0c\u8fd9 \u4e24\u5251\u52bf\u6377\u529b\u6c89\uff0c\u786e\u4e5f\u7b97\u5f97\u662f\u4e00\u6d41\u5251\u6cd5\u3002 \u3000\u3000\u4e91\u821e\u9633\u9a88\u6307\u4e00\u5f39\uff0c\u201c\u950b\u201d\u7684\u4e00\u58f0\uff0c\u628a\u9633\u8d85\u8c37\u7684\u5de6\u624b\u5251\u63b8\u5f00\uff0c\u592a\u7384\u9053\u4eba\u4e1a\u5df2\u5012\u8f6c\u62c2\u5c18\uff0c\u4e00 \u62db\u201c\u94f6\u6cb3\u5012\u5377\u201d\uff0c\u5c18\u5c3e\u98d8\u98d8\uff0c\u5343\u4e1d\u4e07\u7f15\uff0c\u5982\u5377\u5982\u4f5b\uff0c\u8fd9\u4f5b\u5c16\u4e43\u662f\u7528\u4e4c\u91d1\u7384\u4e1d\u6240\u7cbe\u70bc\u7684\uff0c\u6bcf\u4e00 \u6761\u5c18\u5c3e\u90fd\u53ef\u4ee5\u9493\u8d77\u51e0\u5341\u65a4\u91cd\u7684\u4e1c\u897f\uff0c\u62c9\u529b\u6781\u5f3a\uff0c\u82e5\u7ed9\u5b83\u5377\u7740\u624b\u8155\uff0c\u8155\u9aa8\u975e\u7acb\u65f6\u788e\u88c2\u4e0d\u53ef\uff0c\u540c \u65f6\u53c8\u53ef\u7528\u4f5c\u62c2\u7a74\uff0c\u88ab\u90a3\u4e00\u4e1b\u5c18\u5c3e\u62c2\u626b\uff0c\u53ef\u8981\u6bd4\u91cd\u624b\u6cd5\u95ed\u7a74\u8fd8\u66f4\u96be\u5f53\uff01 \u3000\u3000\u4e91\u821e\u9633\u903c\u5f97\u518d\u8017\u771f\u529b\uff0c\u4f7f\u51fa\u5288\u7a7a\u638c\u7684\u529f\u592b\uff0c\u592a\u7384\u9053\u4eba\u62c2\u5c18\u4e09\u5377\uff0c\u4e91\u821e\u9633\u4e5f\u63a5\u8fde\u4e09\u638c\uff0c\u638c \u98ce\u547c\u547c\uff0c\u5c18\u5c3e\u98d8\u98d8\uff0c\u6253\u5f97\u4e2a\u96be\u5206\u96be\u89e3\u3002\u62ac\u773c\u4e00\u770b\uff0c\u4f46\u89c1\u59bb\u5b50\u4e5f\u9677\u5165\u4e86\u9633\u8d85\u8c37\u7684\u53cc\u5251\u5708\u4e2d\u3002 \u3000\u3000\u672c\u6765\u53ea\u8bba\u5251\u6cd5\uff0c\u81ea\u662f\u4e91\u592b\u4eba\u7cbe\u5999\u5f97\u591a\uff0c\u8bba\u529f\u529b\uff0c\u5979\u548c\u9633\u8d85\u8c37\u4e5f\u4e0d\u76f8\u4e0a\u4e0b\uff0c\u4f46\u5979\u624b\u4e2d\u62ff\u7684 \u7a76\u7adf\u53ea\u662f\u4e00\u6839\u6811\u679d\uff0c\u800c\u9633\u8d85\u8c37\u5374\u662f\u4e24\u67c4\u950b\u5229\u7684\u957f\u5251\uff0c\u5728\u5175\u5668\u4e0a\uff0c\u4e91\u592b\u4eba\u5148\u5403\u4e86\u5927\u4e8f\uff0c\u5e78\u800c\u4e91 \u592b\u4eba\u4ed7\u7740\u8eab\u6cd5\u8f7b\u7075\uff0c\u201c\u6811\u5251\u201d\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u9633\u8d85\u8c37\u53cc\u5251\u970d\u970d\u5c55\u5f00\uff0c\u6597\u4e86\u4e09\u4e94\u5341\u62db\uff0c \u8001\u662f\u60f3\u524a\u65ad\u5979\u7684\u6811\u679d\uff0c\u5374\u603b\u4e0d\u80fd\u5982\u613f\u3002 \u3000\u3000\u4e91\u821e\u9633\u77e5\u9053\u8fd9\u6837\u6253\u4e0d\u662f\u529e\u6cd5\uff0c\u62fc\u4e86\u5168\u529b\uff0c\u9661\u7684\u4e00\u4e2a\u5288\u7a7a\u638c\u53d1\u51fa\uff0c\u628a\u62c2\u5c18\u9707\u8361\u5f97\u6839\u6839\u5012 \u5377\uff0c\u731b\u7136\u5927\u559d\u4e00\u58f0\uff0c\u811a\u8e0f\u4e2d\u5bab\uff0c\u9a88\u6307\u4fbf\u6233\uff0c\u773c\u89c1\u592a\u7384\u9053\u4eba\u4fbf\u8981\u6bc1\u5728\u4ed6\u7684\u4e00\u6307\u63b8\u529f\u4e4b\u4e0b\uff0c\u5ffd\u7136 \u542c\u5f97\u6697\u5668\u7834\u7a7a\u4e4b\u58f0\uff0c\u6851\u4ee4\u72d0\u7a81\u7136\u53d1\u51fa\u4e86\u4e24\u679a\u900f\u9aa8\u9489\uff0c\u4e91\u821e\u9633\u529b\u900f\u6307\u5c16\uff0c\u7830\u7830\u4e24\u58f0\uff0c\u5f39\u6307\u8fc7 \u53bb\uff0c\u4e24\u679a\u900f\u9aa8\u9489\u65ad\u4e3a\u56db\u6bb5\u3002 \u3000\u3000\u8fd9\u4e00\u6307\u5b9e\u4e43\u4e91\u821e\u9633\u6bd5\u751f\u529f\u529b\u6240\u805a\uff0c\u4e0d\u6599\u4e00\u51fb\u4e0d\u4e2d\uff0c\u592a\u7384\u9053\u4eba\u7684\u4f5b\u5c18\u53c8\u5f53\u5934\u62c2\u5230\uff0c\u4e91\u821e\u9633 \u63a5\u4e86\u4e24\u62db\uff0c\u5ffd\u89c9\u80f8\u4e2d\u6c14\u95f7\uff0c\u51b7\u6c14\u76f4\u523a\u5fc3\u5934\uff0c\u89c6\u529b\u6e10\u611f\u6a21\u7cca\uff0c\u8eab\u5f62\u4e5f\u8d8a\u6765\u8d8a\u8fdf\u6ede\u4e86\u3002\u8981\u77e5\u4e91\u821e \u9633\u7684\u5185\u5916\u529f\u592b\uff0c\u867d\u7136\u90fd\u5df2\u5230\u4e86\u7089\u706b\u7eaf\u9752\u4e4b\u5883\uff0c\u4f46\u7a76\u7adf\u4e0d\u662f\u94c1\u6253\u7684\u8eab\u8eaf\uff0c\u4ed6\u53d7\u4e86\u6bd5\u51cc\u98ce\u5bd2\u9634\u6bd2 \u638c\u6240\u4f24\uff0c\u7ee7\u4e4b\u6076\u6597\u7f57\u91d1\u5cf0\uff0c\u8ddf\u7740\u53c8\u7528\u201c\u9f99\u541f\u864e\u5578\u529f\u201d\u6697\u52a9\u59bb\u5b50\uff0c\u5982\u4eca\u53c8\u63a5\u8fde\u4f7f\u7528\u6700\u8017\u5185\u529b\u7684 \u5288\u7a7a\u638c\u4e0e\u4e00\u6307\u7985\u529f\uff0c\u5df1\u662f\u5c06\u5230\u4e86\u6cb9\u5c3d\u706f\u67af\u7684\u5730\u6b65\u3002 \u3000\u3000\u592a\u7384\u9053\u4eba\u62c2\u5c18\u518d\u5c55\uff0c\u4e91\u821e\u9633\u4e00\u4e2a\u76d8\u9f99\u7ed5\u6b65\uff0c\u84e6\u7136\u53c8\u662f\u9a88\u6307\u4e00\u6233\uff0c\u592a\u7384\u9053\u4eba\u4ee5\u4e3a\u4ed6\u53c8\u53d1\u4e00 \u6307\u7985\u529f\uff0c\u5413\u4e86\u4e00\u8df3\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5c31\u5728\u592a\u7384\u9053\u4eba\u95ea\u8eab\u4e00\u907f\u4e4b\u9645\uff0c\u4e91\u821e\u9633\u5f3a\u632f\u7cbe\u795e\uff0c\u4e00\u4e2a \u201c\u71d5\u5b50\u94bb\u4e91\u201d\uff0c\u51cc\u7a7a\u4e00\u8dc3\uff0c\u907f\u5f00\u4e86\u6851\u4ee4\u72d0\u7684\u4e00\u628a\u94c1\u83e9\u63d0\uff0c\u8eab\u5f62\u75be\u7a7f\u800c\u4e0b\uff0c\u5de6\u638c\u62cd\u51fb\u9633\u8d85\u8c37\u7684 \u80a9\u5934\uff0c\u8fd9\u4e00\u638c\u4f3c\u865a\u4f3c\u5b9e\uff0c\u9633\u8d85\u8c37\u84e6\u89c9\u638c\u98ce\u6251\u9762\uff0c\u56de\u5251\u4e00\u524a\uff0c\u4e91\u821e\u9633\u4e00\u58f0\u957f\u7b11\uff0c\u53f3\u638c\u4e00\u7a7f\uff0c\u5288 \u624b\u593a\u53bb\u9633\u8d85\u8c37\u7684\u4e00\u67c4\u957f\u5251\uff0c\u4e91\u592b\u4eba\u8d81\u52bf\u6811\u679d\u4e00\u98a4\uff0c\u70b9\u4e2d\u4e86\u4ed6\u7684\u864e\u53e3\uff0c\u4ed6\u7684\u53e6\u4e00\u67c4\u957f\u5251\u4e5f\u8131\u624b \u98de\u51fa\uff0c\u88ab\u4e91\u592b\u4eba\u62a2\u5230\u4e86\u624b\u4e2d\u3002 \u3000\u3000\u8fd9\u51e0\u62db\u4e91\u821e\u9633\u4f7f\u5f97\u9669\u6781\uff0c\u539f\u6765\u4ed6\u90a3\u9a88\u6307\u4e00\u6233\uff0c\u53ea\u662f\u865a\u4f3c\u4f5c\u52bf\uff0c\u5e76\u975e\u4e00\u6307\u7985\u529f\u3002\u5f85\u5230\u592a\u7384 \u9053\u4eba\u611f\u89c9\u4e4b\u65f6\uff0c\u4ed6\u4eec\u592b\u59bb\u5df2\u662f\u53cc\u5251\u5728\u624b\uff01 \u3000\u3000\u4e91\u592b\u4eba\u6362\u4e86\u4e00\u4e2a\u5251\u82b1\uff0c\u4e00\u62db\u201c\u7389\u5973\u6295\u68ad\u201d\uff0c\u5251\u950b\u659c\u51fa\uff0c\u9633\u8d85\u8c37\u6b63\u5728\u95ea\u907f\u4e91\u821e\u9633\u7684\u8ffd\u51fb\uff0c \u4e0d\u6599\u4e91\u592b\u4eba\u7684\u5251\u62db\u540e\u53d1\u5148\u81f3\uff0c\u201c\u5237\u201d\u7684\u4e00\u5251\uff0c\u5728\u9633\u8d85\u8c37\u7684\u81c2\u818a\u523a\u4e86\u4e00\u9053\u957f\u957f\u7684\u53e3\u5b50\uff0c\u8eab\u5b50\u524d \u503e\uff0c\u80a9\u5934\u4e0a\u53c8\u7740\u4e86\u4e91\u821e\u9633\u4e00\u5251\u3002\u592a\u7384\u9053\u4eba\u5927\u53eb\u9053\uff1a\u201c\u9633\u8001\u5f1f\uff0c\u518d\u633a\u4e00\u4f1a\uff0c\u4e91\u821e\u9633\u5c31\u4e0d\u884c \u5566\uff01\u201d\u62c2\u5c18\u4e00\u6296\uff0c\u5de6\u4e00\u62db\u201c\u6d41\u661f\u8d76\u6708\u201d\uff0c\u53f3\u4e00\u62db\u201c\u6025\u6d6a\u541e\u821f\u201d\uff0c\u5206\u88ad\u4e91\u821e\u9633\u592b\u59bb\uff0c\u4e91\u821e\u9633\u53cd \u5251\u4e00\u6325\uff0c\u521a\u597d\u4e0e\u59bb\u5b50\u7684\u5251\u52bf\u914d\u5408\uff0c\u53cc\u5251\u53cd\u5f39\uff0c\u4f46\u542c\u5f97\u4e00\u7247\u7e41\u97f3\u5bc6\u54cd\uff0c\u592a\u7384\u9053\u4eba\u7684\u62c2\u5c18\u98de\u6563\uff0c \u4e00\u84ec\u7ec6\u82e5\u67d4\u4e1d\u7684\u5c18\u5c3e\uff0c\u7adf\u7ed9\u5251\u950b\u524a\u65ad\uff0c\u4e71\u8349\u4e00\u822c\u7684\u98d8\u821e\u7a7a\u4e2d\uff01 \u3000\u3000\u592a\u7384\u9053\u4eba\u53eb\u9053\uff1a\u201c\u5e76\u80a9\u5b50\u4e0a\u5475\uff0c\u6697\u9752\u5b50\u5582\u4ed6\u5475\uff0c\u4e91\u821e\u9633\u8fc7\u4e0d\u4e86\u4e00\u65f6\u4e09\u523b\uff01\u201d\u9633\u8d85\u8c37\u62d7\u6298 \u4e86\u4e24\u679d\u7c97\u5982\u624b\u81c2\u7684\u6885\u82b1\u6811\u5e72\uff0c\u4e0a\u6765\u52a9\u6218\uff0c\u6851\u4ee4\u72d0\u89d1\u51c6\u673a\u4f1a\uff0c\u4e00\u6709\u7a7a\u9699\uff0c\u5c31\u7528\u5582\u6bd2\u7684\u6697\u5668\u5077\u88ad \u4e91\u821e\u9633\u3002 \u3000\u3000\u4e91\u821e\u9633\u957f\u5578\u4e00\u58f0\uff0c\u6717\u58f0\u541f\u9053\uff1a\u201c\u767e\u6218\u4f59\u751f\u4f55\u4ff1\u6b7b\uff0c\u770b\u8c01\u5148\u6211\u81f3\u6cc9\u95f4\uff01\u201d\u5251\u62db\u75be\u5c55\uff0c\u8361\u5f00 \u4e86\u7529\u624b\u7bad\u3001\u900f\u9aa8\u9489\u3001\u6bd2\u84ba\u85dc\u8bf8\u822c\u6697\u5668\uff0c\u5237\uff0c\u5237\uff0c\u5237\uff0c\u4e00\u8fde\u4e09\u5251\uff0c\u5168\u662f\u8fdb\u624b\u7684\u62db\u6570\uff0c\u592a\u7384\u9053\u4eba \u8fde\u7eb5\u5e26\u8dc3\uff0c\u53ea\u662f\u907f\u5f00\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u597d\uff0c\u770b\u8c01\u5148\u6211\u5230\u6cc9\u95f4\uff1f\u201d\u4e91\u821e\u9633\u610f\u56fe\u62fc\u547d\u3002\u4ed6\u5374\u907f\u800c\u4e0d \u6218\uff0c\u62c2\u94c1\u5c18\u906e\u62e6\u5f97\u98ce\u96e8\u4e0d\u900f\uff0c\u5b88\u5f97\u975e\u5e38\u4e25\u5bc6\u3002 \u3000\u3000\u9633\u8d85\u8c37\u821e\u52a8\u4e24\u682a\u6811\u5e72\uff0c\u52b2\u529b\u4e0d\u5728\u4e91\u592b\u4eba\u4e4b\u4e0b\uff0c\u5374\u8fdc\u4e0d\u53ca\u4e91\u592b\u4eba\u7684\u8f7b\u7075\u7fd4\u52a8\uff0c\u4e91\u592b\u4eba\u51b7\u7b11 \u9053\uff1a\u201c\u4e1c\u65bd\u6548\u98a6\uff0c\u81ea\u53d6\u5176\u8fb1\uff01\u201d\u9752\u94a2\u5251\u51e0\u8bb0\u75be\u653b\uff0c\u67d4\u4e2d\u5e26\u521a\uff0c\u6709\u5982\u5265\u8327\u62bd\u4e1d\uff0c\u8fde\u7ef5\u4e0d\u65ad\uff0c\u9002 \u624d\u4e91\u592b\u4eba\u7528\u4e00\u6280\u62c7\u6307\u822c\u7c97\u7ec6\u7684\u6885\u679d\uff0c\u5df2\u903c\u5f97\u9633\u8d85\u8c37\u7684\u53cc\u80a1\u5251\u65bd\u5c55\u4e0d\u5f00\uff0c\u800c\u4eca\u4e3b\u5ba2\u6613\u52bf\uff0c\u9633\u8d85 \u8c37\u7528\u4e24\u6839\u7c97\u5982\u624b\u81c2\u7684\u6811\u5e72\uff0c\u5374\u65e0\u6cd5\u5c01\u5f97\u4f4f\u4e91\u592b\u4eba\u7684\u5251\u52bf\uff0c\u4e0d\u6d88\u7247\u523b\uff0c\u53ea\u542c\u5f97\u201c\u5361\u5587\u201d\u4e00\u54cd\uff0c \u9633\u8d85\u8c37\u7684\u4e00\u6839\u6811\u5e72\u5df2\u7ed9\u4e91\u592b\u4eba\u524a\u4e3a\u4e24\u6bb5\u3002 \u3000\u3000\u6765\u5230\u4e91\u5bb6\u7684\u4e09\u4eba\u4e4b\u4e2d\uff0c\u6851\u4ee4\u72d0\u7684\u6b66\u529f\u6700\u5f31\uff0c\u4f46\u4e00\u624b\u6697\u5668\uff0c\u5374\u662f\u6253\u5f97\u53c8\u72e0\u53c8\u51c6\uff0c\u4e91\u821e\u9633\u592b \u59bb\u867d\u7136\u5360\u4e86\u4e0a\u98ce\uff0c\u4f46\u6bcf\u88ab\u6697\u5668\u6240\u963b\uff0c\u8bb8\u591a\u6740\u624b\u795e\u62db\uff0c\u90fd\u672a\u80fd\u5f97\u5fc3\u5e94\u624b\uff0c\u4f24\u4e0d\u4e86\u654c\u4eba\u7684\u6027\u547d\u3002 \u3000\u3000\u6218\u5230\u5206\u9645\uff0c\u4e91\u821e\u9633\u8fd0\u7528\u4e86\u4ec5\u6709\u7684\u7cbe\u529b\uff0c\u7a81\u7136\u4e00\u8bb0\u5288\u7a7a\u638c\u53d1\u51fa\uff0c\u5c06\u592a\u7384\u9053\u4eba\u7684\u62c2\u5c18\u9707\u5f00\uff0c \u4e00\u62db\u201c\u4e58\u9f99\u5f15\u51e4\u201d\uff0c\u5251\u950b\u5728\u592a\u7384\u9053\u4eba\u7684\u80f8\u53e3\u72e0\u72e0\u6233\u4e86\u4e00\u8bb0\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u770b\u8c01\u5148\u6211\u5230\u6cc9\u95f4\uff01\u201d \u592a\u7384\u9053\u4eba\u201c\u54c7\u201d\u7684\u4e00\u53e3\u9c9c\u8840\u55b7\u51fa\uff0c\u4e91\u821e\u9633\u4e00\u5251\u5f97\u624b\uff0c\u6c14\u529b\u5168\u5df2\u6d88\u5931\uff0c\u4e00\u4e2a\u8ddf\u6597\uff0c\u4e00\u53e3\u6c14\u7adf\u662f \u63d0\u4e0d\u4e0a\u6765\uff0c\u80f8\u53e3\u5267\u75db\uff0c\u773c\u524d\u660f\u9ed1\uff01\u201c\u535c\u201d\u7684\u4e00\u58f0\uff0c\u80a9\u5934\u4e0a\u53c8\u7740\u4e86\u4e00\u652f\u51b7\u7bad\uff01 \u3000\u3000\u9633\u8d85\u8c37\u4e00\u89c1\u673a\u4e0d\u53ef\u5931\uff0c\u731b\u7684\u62a1\u8d77\u6811\u5e72\uff0c\u5f53\u6210\u68d2\u4f7f\uff0c\u4e00\u68d2\u5288\u4ed6\u7684\u8111\u888b\uff01\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c \u53ea\u542c\u5f97\u201c\u535c\u901a\u201d\u4e00\u58f0\uff0c\u8840\u82b1\u56db\u6e85\uff0c\u5012\u4e0b\u4e86\u4e00\u4e2a\u4eba\uff01\u8fd9\u4e2a\u4eba\u5374\u5e76\u4e0d\u662f\u4e91\u821e\u9633\u800c\u662f\u9633\u8d85\u8c37\uff0c\u539f\u6765 \u4e91\u592b\u4eba\u7684\u51fa\u624b\u6bd4\u4ed6\u66f4\u5feb\uff0c\u5c31\u5728\u9633\u8d85\u8c37\u7684\u6728\u68d2\u5c06\u843d\u672a\u843d\u4e4b\u9645\uff0c\u4e00\u5251\u524a\u53bb\u4e86\u4ed6\u7684\u534a\u8fb9\u8111\u888b\uff01 \u3000\u3000\u8fd9\u8fd8\u662f\u4e91\u592b\u4eba\u7b2c\u4e00\u6b21\u6740\u4eba\uff0c\u89c1\u90a3\u9633\u8d85\u8c37\u88ab\u524a\u53bb\u4e86\u534a\u8fb9\u8111\u888b\uff0c\u5140\u81ea\u5728\u5730\u4e0b\u6eda\u52a8\uff0c\u9c9c\u8840\u76f4 \u5192\uff0c\u7981\u4e0d\u4f4f\u5fc3\u60ca\u8089\u8df3\uff0c\u624b\u811a\u90fd\u9178\u8f6f\u4e86\u3002\u6599\u4e0d\u5230\u592a\u7384\u9053\u4eba\u867d\u53d7\u91cd\u4f24\uff0c\u8ff9\u8fd8\u672a\u6b7b\uff0c\u6b63\u6240\u8c13\u201c\u87b3\u8782 \u6355\u8749\uff0c\u9ec4\u96c0\u5728\u540e\u3002\u201d\u5c31\u5728\u4e91\u592b\u4eba\u6740\u6389\u9633\u8d85\u8c37\uff0c\u957f\u5251\u8fd8\u672a\u62bd\u56de\uff0c\u5446\u5446\u53d1\u6123\u4e4b\u9645\uff0c\u7a81\u7136\u4e00\u8dc3\u800c \u8d77\uff0c\u62c2\u5c18\u4e00\u5c55\uff0c\u201c\u556a\u201d\u7684\u4e00\u4e0b\uff0c\u6b63\u6b63\u51fb\u4e2d\u4e86\u4e91\u592b\u4eba\u7684\u80cc\u5fc3\u5927\u7a74\u3002\u4e91\u821e\u9633\u542c\u5f97\u54cd\u58f0\uff0c\u7741\u773c\u770b \u65f6\uff0c\u53ea\u89c1\u59bb\u5b50\u5df2\u662f\u6447\u6447\u6b32\u5760\uff0c\u4e91\u821e\u9633\u5927\u6012\uff0c\u4e5f\u4e0d\u77e5\u662f\u54ea\u513f\u6765\u7684\u6c14\u529b\uff0c\u4f38\u6307\u4e00\u6233\uff0c\u6700\u540e\u4e00\u6b21\u4f7f \u51fa\u4e86\u4e00\u6307\u63b8\u529f\uff0c\u4e5f\u6233\u4e2d\u4e86\u592a\u7384\u9053\u4eba\u7684\u80cc\u5fc3\u5927\u7a74\u3002\u592a\u7384\u9053\u4eba\u4e00\u8de4\u683d\u5012\uff0c\u5636\u58f0\u53eb\u9053\uff1a\u201c\u628a\u90a3\u5c0f\u5b50 \u62a2\u8d70\uff0c\u7b97\u4f60\u4e00\u529f\uff01\u201d \u3000\u3000\u4e91\u821e\u9633\u60e8\u7136\u4e00\u7b11\uff0c\u4f46\u89c9\u767e\u9ab8\u6b32\u788e\uff0c\u56db\u80a2\u65e0\u529b\uff0c\u773c\u5149\u4e00\u77a5\uff0c\u4f46\u89c1\u90a3\u6851\u4ee4\u72d0\u5954\u5411\u4e86\u8eba\u5728\u5730\u4e0a \u72b9\u660f\u8ff7\u672a\u9192\u7684\u9648\u7384\u673a\u3002\u4e91\u821e\u9633\u5927\u53eb\u4e00\u58f0\uff0c\u53ea\u89c1\u59bb\u5b50\u5954\u4e0a\u4e24\u6b65\uff0c\u957f\u5251\u8131\u624b\u63b7\u51fa\uff0c\u4f7f\u51fa\u4e86\u8fbe\u6469\u5251 \u6cd5\u4e2d\u6700\u540e\u7684\u4e00\u62db\u201c\u795e\u5251\u7a7f\u4e91\u201d\uff0c\u81ea\u6851\u4ee4\u72d0\u7684\u540e\u5fc3\u7a7f\u5165\uff0c\u524d\u5fc3\u7a7f\u51fa\uff0c\u5c06\u4ed6\u9489\u5728\u5730\u4e0a\u3002\u4e91\u592b\u4eba\u98de \u5251\u51fa\u624b\uff0c\u4ea6\u81ea\u6c14\u5598\u5401\u5401\uff0c\u501a\u5728\u8001\u6885\u6811\u4e0a\uff0c\u5c31\u5982\u5927\u75c5\u521d\u8fc7\u4e00\u822c\u3002\u5176\u5b9e\u6bd4\u5927\u75c5\u4e00\u573a\u8fd8\u66f4\u4e25\u91cd\uff0c\u592a \u7384\u9053\u4eba\u4e34\u6b7b\u90a3\u4e00\u51fb\uff0c\u5b9e\u662f\u6bd5\u751f\u529f\u529b\u4e4b\u6240\u805a\uff0c\u7adf\u628a\u5979\u5341\u4e09\u5904\u7ecf\u8109\u5168\u9707\u4f24\u4e86\u3002 \u3000\u3000\u9662\u5b50\u91cc\u5012\u4e0b\u4e86\u56db\u5177\u5c38\u4f53\uff0c\u4e09\u4e2a\u53d7\u91cd\u4f24\u7684\u4eba\u3002\u53c8\u590d\u5f52\u4e8e\u9759\u5bc2\u3002\u6b47\u4e86\u4e00\u9635\uff0c\u4e91\u821e\u9633\u4f4e\u4f4e\u5524\u4e86 \u4e00\u58f0\u201c\u5b9d\u73e0\u201d\uff0c\u4e91\u592b\u4eba\u4e5f\u4f4e\u4f4e\u5524\u4e86\u4e00\u58f0\u201c\u821e\u9633\u201d\uff0c\u76f8\u4e92\u601c\u60dc\uff0c\u5c31\u50cf\u65b0\u5a5a\u65f6\u5019\u4e00\u822c\uff0c\u4e91\u821e\u9633\u4f4e \u58f0\u8bf4\u9053\uff1a\u201c\u5b9d\u73e0\uff0c\u4f60\u641c\u90a3\u7f57\u91d1\u5cf0\u8eab\u4e0a\u3002\u201d\u4e91\u592b\u4eba\u641c\u51fa\u4e86\u5c16\u731d\u91d1\u5b50\uff0c\u4e00\u4e2a\u7389\u74f6\uff0c\u5c06\u91d1\u5b50\u6254\u6389\uff0c \u628a\u7389\u74f6\u629b\u7ed9\u4e86\u4e08\u592b\uff0c\u4e91\u821e\u9633\u770b\u4e86\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4e0d\u662f\u8fd9\u4e2a\uff0c\u518d\u641c\uff01\u201d\u4e91\u592b\u4eba\u95ed\u4e86\u547c\u5438\uff0c\u5fcd\u7740\u90a3 \u80a1\u8840\u8165\u81ed\u5473\uff0c\u5728\u7f57\u91d1\u5cf0\u91cc\u8863\u7684\u5939\u888b\u91cc\u53c8\u641c\u51fa\u4e00\u4e2a\u9526\u56ca\uff0c\u5012\u51fa\u6765\u4e00\u770b\uff0c\u91cc\u9762\u6709\u4e09\u9897\u6de1\u7ea2\u7684\u4e39 \u4e38\u3002 \u3000\u3000\u4e91\u821e\u9633\u9053\uff1a\u201c\u62ff\u6765\u7ed9\u6211\u3002\u201d\u4e91\u592b\u4eba\u8d70\u5230\u4e86\u4e08\u592b\u8ddf\u524d\uff0c\u4e91\u821e\u9633\u5c06\u4e09\u9897\u4e38\u836f\u95fb\u4e00\u95fb\uff0c\u70b9\u70b9\u5934 \u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u662f\u5927\u5185\u7684\u56fa\u672c\u7075\u4e39\u3002\u201d\u63e1\u7740\u59bb\u5b50\u7684\u624b\uff0c\u5c06\u5979\u7684\u624b\u638c\u6162\u6162\u644a\u5f00\uff0c\u628a\u8fd9\u4e09\u9897\u6de1\u7ea2 \u8272\u7684\u6708\u4e38\u653e\u5728\u5979\u7684\u638c\u5fc3\uff0c\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u5b9d\u73e0\uff0c\u8bf7\u4f60\u628a\u8fd9\u4e09\u9897\u7ea2\u4e38\u670d\u4e0b\u3002\u201d\u4e91\u592b\u4eba\u9053\uff1a\u201c\u4f60 \u5462\u3002\u201d\u4e91\u821e\u9633\u51c4\u7136\u7b11\u9053\uff1a\u201c\u5b9d\u73e0\uff0c\u4f60\u8fd8\u770b\u4e0d\u51fa\u5417\uff1f\u6211\u6240\u53d7\u7684\u662f\u6bd5\u51cc\u98ce\u7684\u9634\u5bd2\u6bd2\u638c\uff0c\u4f53\u5185\u7684\u8840 \u90fd\u5df2\u574f\u4e86\uff0c\u771f\u529b\u53c8\u5df2\u8017\u5c3d\uff0c\u5982\u4eca\u5373\u7b97\u6709\u5c0f\u8fd8\u4e39\u4ea6\u5df2\u65e0\u6d4e\u4e8e\u4e8b\uff0c\u8fd9\u4e09\u9897\u56fa\u672c\u4e39\u53ef\u4ee5\u6cbb\u53d7\u521a\u731b\u529b \u91cf\u7684\u9707\u4f24\uff0c\u5bf9\u4f60\u6709\u7528\uff0c\u5bf9\u6211\u65e0\u7528\u3002\u201d \u3000\u3000\u4e91\u592b\u4eba\u70b9\u70b9\u5934\u9053\uff1a\u201c\u6211\u77e5\u9053\u4e86\u3002\u201d\u81ea\u5df1\u628a\u4e86\u4e00\u4e0b\u8109\u606f\uff0c\u53c8\u770b\u4e86\u4e08\u592b\u4e00\u773c\uff0c\u5fae\u5fae\u7b11\u9053\uff1a \u201c\u6211\u548c\u4f60\u90fd\u662f\u4e00\u6837\uff0c\u8fd8\u53ef\u4ee5\u518d\u6d3b\u4e09\u5929\u3002\u201d\u4e91\u821e\u9633\u9053\uff1a\u201c\u4f60\u670d\u4e0b\u4e86\u8fd9\u4e09\u9897\u4e38\u836f\uff0c\u6700\u5c11\u8fd8\u53ef\u4ee5\u518d \u6d3b\u4e09\u5341\u5e74\uff01\u201d\u4e91\u592b\u4eba\u7b11\u9053\uff1a\u201c\u592a\u957f\u5566\uff01\u55ef\uff0c\u4e09\u5929\u4e4b\u5185\uff0c\u5df2\u7ecf\u53ef\u4ee5\u505a\u8bb8\u591a\u4e8b\u60c5\u4e86\uff01\u201d\u7f13\u7f13\u7684\u8d70 \u5230\u9648\u7384\u673a\u65c1\u8fb9\uff0c\u5c06\u4ed6\u6273\u4e86\u8d77\u6765\uff0c\u5ffd\u5730\u642c\u5f00\u4e86\u9648\u7384\u673a\u7684\u5634\u5df4\uff0c\u5c06\u90a3\u4e09\u9897\u56fa\u672c\u7075\u4e39\uff0c\u90fd\u585e\u4e86\u8fdb \u53bb\u3002 \u3000\u3000\u4e91\u821e\u9633\u5446\u4e86\u4e00\u9635\uff0c\u51c4\u7136\u8bf4\u9053\uff1a\u201c\u5b9d\u73e0\uff0c\u539f\u6765\u4f60\u5bf9\u6211\u60c5\u6df1\u4e49\u539a\uff0c\u7adf\u81f3\u5982\u65af\uff01\u6211\uff0c\u6211\u2026\u2026\u201d \u5fc3\u4e2d\u611f\u52a8\uff0c\u7adf\u81ea\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002\u62ac\u8d77\u5934\u6765\uff0c\u4f46\u89c1\u59bb\u5b50\u4e5f\u6b63\u51dd\u671b\u7740\u4ed6\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u7d20\u7d20\u662f\u4e2a\u597d \u5973\u513f\uff0c\u54b1\u4eec\u5374\u4e0d\u662f\u597d\u7236\u6bcd\uff0c\u4e0d\u77e5\u4f60\u5fc3\u91cc\u5982\u4f55\uff1f\u6211\u5374\u662f\u611f\u5230\u4e8e\u5fc3\u60ed\u6127\uff01\u201d\u4e91\u821e\u9633\u6cea\u6d41\u53cc\u776b\uff0c \u9053\uff1a\u201c\u6211\u6bd4\u4f60\u8fd8\u8981\u60ed\u6127\u4e07\u5206\u3002\u201d \u3000\u3000\u4e91\u592b\u4eba\u6df1\u6df1\u5438\u4e86\u53e3\u6c14\uff0c\u6307\u7740\u8eba\u5728\u5730\u4e0a\u7684\u9648\u7384\u673a\u8bf4\u9053\uff1a\u201c\u7d20\u7d20\u7684\u773c\u5149\u6bd4\u4f60\u6211\u90fd\u5f3a\u5f97\u591a\uff0c\u8fd9 \u5b69\u5b50\u5fc3\u5730\u5584\u826f\uff0c\u8bda\u6734\u4fa0\u4e49\uff0c\u786e\u662f\u4e00\u4e2a\u53ef\u4ee5\u4fe1\u6258\u7684\u4eba\u3002\u6211\u628a\u8fd9\u4e09\u9897\u7075\u4e39\u7ed9\u4ed6\u7eed\u547d\uff0c\u4f60\u8be5\u660e\u767d\u6211 \u7684\u5fc3\u610f\u5427\uff1f\u201d\u4e91\u821e\u9633\u9053\uff1a\u201c\u6211\u660e\u767d\uff0c\u5f85\u4ed6\u82cf\u9192\u4e4b\u65f6\uff0c\u7d20\u7d20\u60f3\u5fc5\u4e5f\u5df2\u56de\u6765\u3002\u6211\u5c31\u5f53\u7740\u4ed6\u4eec\u4e24\u4eba \u7684\u9762\uff0c\u4eb2\u53e3\u7b54\u5141\u4ed6\u4eec\u7684\u5a5a\u4e8b\u3002\u5b9d\u73e0\uff0c\u4f60\u2026\u2026\u201d \u3000\u3000\u4e91\u592b\u4eba\u8138\u4e0a\u63a0\u8fc7\u4e00\u4e1d\u7b11\u610f\uff0c\u4f46\u968f\u5373\u9762\u5bb9\u66f4\u6c89\u90c1\u4e86\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u80fd\u7b49\u7d20\u7d20\u56de\u6765\u4e86\u3002 \u55ef\uff0c\u7d20\u7d20\u53ef\u601c\uff0c\u5929\u94ce\u90a3\u5b69\u5b50\u8fd8\u6ca1\u6210\u4eba\uff0c\u66f4\u662f\u53ef\u601c\u3002\u6211\u672c\u6b32\u5c06\u4ed6\u6276\u517b\u6210\u6750\uff0c\u73b0\u5728\u662f\u4e0d\u80fd\u591f\u4e86\u3002 \u4f46\u90a3\u5e45\u753b\u6211\u66fe\u7b54\u5e94\u7ed9\u5929\u94ce\u9001\u5230\u4ed6\u7684\u5bb6\u4e2d\uff0c\u6211\u5fc5\u987b\u5728\u8fd9\u4e09\u5929\u4e4b\u5185\u8d76\u5230\u4e86\u3002\u201d\u58f0\u97f3\u5e73\u9759\uff0c\u5305\u542b\u7684 \u5374\u662f\u6781\u5176\u590d\u6742\u7684\u611f\u60c5\uff0c\u4e91\u821e\u9633\u4ece\u59bb\u5b50\u5e73\u9759\u7684\u8bdd\u58f0\u4e2d\uff0c\u542c\u51fa\u4e86\u5979\u5fc3\u5f26\u7684\u6fc0\u52a8\u3002 \u3000\u3000\u4e91\u821e\u9633\u6014\u4e86\u4e00\u6014\uff0c\u4ed6\u672c\u4ee5\u4e3a\u59bb\u5b50\u662f\u8981\u966a\u4ed6\u540c\u6b7b\uff0c\u5374\u539f\u6765\u662f\u53e6\u6709\u56e0\u7531\uff0c\u5fc3\u4e2d\u7a0d\u7a0d\u6709\u70b9\u96be \u8fc7\u3002\u4f46\u7acb\u5373\u4ee5\u6709\u8fd9\u6837\u7684\u59bb\u5b50\u800c\u81ea\u8c6a\uff0c\u4ef0\u5929\u957f\u7b11\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6b7b\u751f\u51ed\u4e00\u8bfa\uff0c\u4e0d\u6127\u5973\u4e2d\u8c6a\uff0c\u5b9d \u73e0\uff0c\u4e8c\u5341\u5e74\u6765\u6211\u6ca1\u6709\u597d\u597d\u5f85\u4f60\uff0c\u60f3\u4e0d\u5230\u54b1\u4eec\u6ca1\u6709\u540c\u5e74\u540c\u6708\u540c\u65e5\u751f\uff0c\u5374\u5f97\u4ee5\u540c\u5e74\u540c\u6708\u540c\u65e5\u6b7b\uff0c \u4e91\u67d0\u5c1a\u6709\u4f55\u6c42\uff1f\u5b9d\u73e0\uff0c\u4f60\u8d70\u5427\uff01\u6211\u5bf9\u4e0d\u4f4f\u4f60\u7684\u5730\u65b9\uff0c\u4f46\u613f\u80fd\u591f\u6765\u751f\u8865\u8fc7\uff01\u201d \u3000\u3000\u4e91\u592b\u4eba\u4f4e\u4f4e\u5524\u4e86\u4e00\u58f0\u201c\u821e\u9633\u2026\u2026\u201d\u534a\u664c\u624d\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u6765\u751f\u4e4b\u4e8b\u7a76\u5c5e\u6e3a\u832b\uff0c\u4eca\u751f\u4e4b \u79e6\uff0c\u4f60\u80fd\u542c\u6211\u7684\u9057\u8a00\uff0c\u6211\u5df2\u611f\u5230\u5fc3\u6ee1\u610f\u8db3\u3002\u597d\uff0c\u6211\u8d70\u5566\uff01\u55ef\uff0c\u6211\u62c5\u5fc3\u6211\u4e09\u65e5\u4e4b\u5185\uff0c\u8d76\u4e0d\u5230\u77f3 \u5bb6\uff0c\u6682\u501f\u7384\u673a\u8fd9\u5339\u767d\u9a6c\u4e00\u7528\uff0c\u4ed6\u9192\u6765\u540e\u4f60\u544a\u8bc9\u4ed6\uff0c\u53eb\u4ed6\u548c\u7d20\u7d20\u5230\u77f3\u5bb6\u6765\u6536\u6b93\u6211\u7684\u9057\u9aa8\uff0c\u5e76\u5c06 \u8fd9\u5339\u767d\u9a6c\u53d6\u56de\u3002\u5440\uff0c\u6216\u8005\uff0c\u6216\u8005\u4e0d\u544a\u8bc9\u4ed6\u4eec\u4e5f\u597d\uff0c\u6211\u53eb\u5929\u94ce\u7684\u5b69\u5b50\u5c06\u6765\u628a\u8fd9\u767d\u9a6c\u9001\u8fd8\u3002\u201d \u3000\u3000\u9648\u7384\u673a\u90a3\u5339\u767d\u9a6c\u6b63\u5728\u95e8\u5916\u5403\u8349\uff0c\u4e91\u821e\u9633\u9001\u51fa\u95e8\u5916\uff0c\u53ea\u89c1\u4ed6\u7684\u59bb\u5b50\u8de8\u4e0a\u767d\u9a6c\uff0c\u51c4\u7136\u4e00\u7b11\uff0c \u626c\u97ad\u8bf4\u9053\uff1a\u201c\u5929\u4e0b\u65e0\u4e0d\u6563\u4e4b\u7b75\u5e2d\uff0c\u50cf\u4eca\u65e5\u8fd9\u822c\u7684\u6563\u4e86\uff0c\u4f60\u5fc3\u4e2d\u6709\u6211\uff0c\u6211\u5fc3\u4e2d\u6709\u4f60\uff0c\u5c82\u4e0d\u662f\u6bd4 \u540c\u5e8a\u5f02\u68a6\u8981\u80dc\u8fc7\u591a\u591a\uff01\u201d\u9a6c\u97ad\u5728\u7a7a\u4e2d\u6687\u556a\u4e00\u54cd\uff0c\u865a\u62bd\u4e00\u97ad\uff0c\u90a3\u767d\u9a6c\u653e\u5f00\u56db\u8df7\uff0c\u5728\u66ae\u8272\u82cd\u832b\u4e4b \u4e2d\uff0c\u7edd\u5c18\u800c\u53bb\u3002 \u3000\u3000\u8fd9\u5f53\u771f\u662f\u6b7b\u522b\u751f\u79bb\uff0c\u4e91\u821e\u9633\u76ee\u9001\u4ed6\u7684\u59bb\u5b50\u5954\u4e0b\u5c71\u5761\uff0c\u76f4\u5230\u770b\u4e0d\u89c1\u4e86\uff0c\u8fd9\u624d\u53f9\u4e86\u53e3\u6c14\uff0c\u56de \u8fc7\u5934\u6765\uff0c\u4f46\u89c9\u4e00\u7247\u832b\u7136\uff0c\u4e5f\u4e0d\u77e5\u662f\u60b2\u54c0\u8fd8\u662f\u6b22\u559c\uff1f\u4e8c\u5341\u6765\u6765\uff0c\u4ed6\u548c\u59bb\u5b50\u59cb\u7ec8\u50cf\u964c\u751f\u4eba\u4e00\u6837\uff0c \u4eca\u5929\u624d\u7b2c\u4e00\u6b21\u61c2\u5f97\u4e86\u5979\uff1b\u800c\u5979\u4e5f\u662f\u7b2c\u4e00\u6b21\u5411\u81ea\u5df1\u6253\u5f00\u4e45\u95ed\u7684\u5fc3\u6249\uff0c\u7559\u4e0b\u4e86\u4e0d\u5c3d\u7684\u60c5\u610f\u3002\u4e91\u821e \u9633\u4f46\u89c9\u8fd9\u7f20\u7ef5\u7684\u60c5\u610f\uff0c\u8fdc\u8fdc\u80dc\u4e8e\u65b0\u5a5a\u4e4b\u65f6\u3002 \u3000\u3000\u4e91\u821e\u9633\u624b\u629a\u6885\u679d\uff0c\u5583\u5583\u8bf4\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5979\u4eec\u4e24\u4eba\u7adf\u662f\u5982\u6b64\u76f8\u4f3c\uff01\u90fd\u662f\u4fa0\u9aa8\u5982\u94a2\uff0c\u67d4\u60c5\u4f3c \u6c34\uff01\u5440\uff0c\u6211\u6240\u79cd\u4e0b\u7684\u7f6a\u5b7d\u771f\u662f\u4e07\u6b7b\u4e0d\u8db3\u4ee5\u853d\u5176\u8f9c\uff01\u201d\u665a\u98ce\u7a7f\u6811\uff0c\u6811\u4e0a\u672c\u6765\u5c31\u5df2\u7a00\u758f\u7684\u6885\u82b1\uff0c \u53c8\u843d\u4e0b\u4e86\u5c16\u6003\uff0c\u4e91\u821e\u9633\u5ffd\u5730\u53c8\u60f3\u8d77\u4e86\u4ed6\u7684\u524d\u59bb\uff0c\u4e8c\u5341\u5e74\u6765\uff0c\u4ed6\u51e0\u4e4e\u6bcf\u665a\u90fd\u5728\u6885\u82b1\u6811\u4e0b\u5f98\u5f8a\uff0c \u5728\u6885\u82b1\u4e1b\u4e2d\u770b\u5230\u5979\u7684\u5e7b\u5f71\uff0c\u4eca\u665a\u5979\u53c8\u770b\u5230\u5979\u4e86\uff0c\u4e91\u821e\u9633\u53eb\u4e86\u4e00\u58f0\u201c\u96ea\u6885\uff01\u201d\u6251\u4e0a\u524d\u53bb\uff0c\u98ce\u6447 \u6885\u6811\uff0c\u53f6\u843d\u82b1\u98de\uff0c\u970e\u7136\u95f4\uff0c\u4ed6\u8111\u6d77\u4e2d\u53c8\u6cdb\u51fa\u7b2c\u4e8c\u4e2a\u5e7b\u5f71\uff0c\u662f\u4ed6\u73b0\u5728\u8fd9\u4f4d\u59bb\u5b50\u7684\u5f71\u5b50\uff0c\u5ffd\u7136\u95f4 \u4e24\u4e2a\u5f71\u5b50\u5408\u800c\u4e3a\u4e00\uff0c\u5206\u4e0d\u51fa\u8c01\u662f\u5b9d\u73e0\uff0c\u8c01\u662f\u96ea\u6885\uff0c\u4e91\u821e\u9633\u6251\u4e0b\u4e86\u7247\u7247\u6885\u82b1\uff0c\u4e24\u4e2a\u4eba\u7684\u5f71\u5b50\u90fd \u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u591c\u8272\u6df1\u6c89\uff0c\u5c71\u95f4\u660e\u6708\u5189\u5189\u5347\u8d77\uff0c\u4e91\u821e\u9633\u72ec\u81ea\u5728\u6885\u82b1\u6811\u4e0b\u5f98\u5f8a\uff0c\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\u3002\u76f4\u81f3\u6708 \u4e0a\u6885\u68a2\uff0c\u68ee\u6797\u91cc\u7167\u4f8b\u7684\u4f20\u6765\u4e86\u6bcf\u665a\u7684\u733f\u557c\u864e\u5578\uff0c\u4e91\u821e\u9633\u597d\u4f3c\u5728\u6076\u68a6\u4e4b\u4e2d\u9192\u6765\uff0c\u6708\u5149\u4e0b\u9662\u5b50\u91cc \u7684\u666f\u7269\u66f4\u662f\u51c4\u51c9\uff0c\u4e91\u821e\u9633\u770b\u4e00\u770b\u90a3\u6a2a\u4e03\u7ad6\u516b\u5012\u5728\u5730\u4e0a\u7684\u5c38\u4f53\uff0c\u5fc3\u4e2d\u65e0\u9650\u618e\u538c\uff0c\u60f3\u9053\uff1a\u201c\u6211\u4e0d \u80fd\u8ba9\u8fd9\u4e9b\u80ae\u810f\u7684\u4e1c\u897f\u6cbe\u6c61\u4e86\u6211\u7684\u6885\u82b1\u3002\u201d\u62fe\u8d77\u5730\u4e0a\u7684\u94f6\u74f6\uff0c\u90a3\u662f\u4ece\u7f57\u91d1\u5cf0\u8eab\u4e0a\u641c\u51fa\u6765\u7684\uff0c\u91cc \u9762\u88c5\u7684\u662f\u201c\u5316\u9aa8\u4e39\u201d\uff0c\u90a3\u662f\u6740\u4eba\u4e4b\u540e\uff0c\u6bc1\u5c38\u706d\u8ff9\u7528\uff0c\u4e91\u592b\u4eba\u521a\u624d\u4e0d\u8ba4\u5f97\u8fd9\u79cd\u4e1c\u897f\uff0c\u8fd8\u51e0\u4e4e\u5f53 \u4f5c\u7075\u4e39\u4f7f\u7528\u3002 \u3000\u3000\u4e91\u821e\u9633\u628a\u90a3\u4e9b\u5c38\u4f53\uff0c\u62fd\u51fa\u95e8\u5916\uff0c\u627e\u4e00\u4e2a\u51b7\u50fb\u7684\u5730\u65b9\uff0c\u5c06\u5c38\u4f53\u5316\u6210\u4e86\u4e00\u6ee9\u8113\u8840\uff0c\u5c31\u5730\u57cb \u4e86\u3002\u5ffd\u5730\u5fc3\u4e2d\u6253\u4e86\u4e00\u4e2a\u5bd2\u5664\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u8bda\u7136\u90fd\u662f\u574f\u86cb\uff0c\u4f46\u6211\u53c8\u4f55\u5c1d\u6bd4\u4ed6\u4eec\u597d\u4e86\uff1f\u6211\u618e \u6076\u4ed6\u4eec\uff0c\u5176\u5b9e\u6211\u66f4\u5e94\u8be5\u75db\u6068\u81ea\u5df1\uff01\u201d \u3000\u3000\u4eba\u5230\u5c06\u6b7b\u7684\u65f6\u5019\uff0c\u53ea\u8981\u5c1a\u6709\u77e5\u89c9\uff0c\u603b\u4f1a\u56de\u5fc6\u8d77\u81ea\u5df1\u4e00\u751f\u7684\u884c\u4e8b\uff0c\u4e91\u821e\u9633\u800c\u4eca\u4e5f\u662f\u4e00\u6837\uff0c \u5e73\u751f\u4e8b\u8ff9\uff0c\u5728\u5fc3\u5934\u4e0a\u4e00\u5e55\u5e55\u7684\u7ffb\u8fc7\uff0c\u505a\u8fc7\u597d\u4e8b\uff0c\u4e5f\u505a\u8fc7\u574f\u4e8b\uff0c\u53ea\u89c9\u7f6a\u5b7d\u4e4b\u6df1\uff0c\u8fdc\u975e\u81ea\u5df1\u5076\u7136 \u6240\u505a\u7684\u4e00\u4e9b\u597d\u4e8b\u6240\u80fd\u8865\u8fc7\uff01 \u3000\u3000\u5c71\u98ce\u8d8a\u522e\u8d8a\u5927\uff0c\u4e91\u821e\u9633\u611f\u5230\u9635\u9635\u5bd2\u610f\u88ad\u4eba\uff0c\u731b\u7136\u7684\u60f3\u8d77\u4e86\u9648\u7384\u673a\uff0c\u56de\u5230\u9662\u5b50\u91cc\u5c06\u4ed6\u62b1\u4e86 \u8d77\u6765\uff0c\u4e00\u6478\u8109\u8c61\uff0c\u751a\u662f\u548c\u5e73\uff0c\u53ea\u662f\u4eba\u8fd8\u672a\u9192\uff0c\u6708\u5149\u7167\u5728\u9648\u7384\u673a\u9163\u7761\u7684\u9762\u4e0a\uff0c\u4e91\u821e\u9633\u5fc3\u4e2d\u5ffd\u7136 \u8d77\u4e86\u4e00\u4e2a\u5947\u602a\u7684\u611f\u89c9\uff1a\u8fd9\u5929\u771f\u65e0\u90aa\u7684\u7761\u76f8\uff0c\u5c31\u50cf\u4ed6\u7684\u7d20\u7d20\u4e00\u822c\uff01\u4e91\u821e\u9633\u51dd\u89c6\u4e86\u597d\u4e00\u4f1a\uff0c\u53c8\u597d \u50cf\u8fd9\u76f8\u8c8c\u4f3c\u66fe\u76f8\u8bc6\uff0c\u4e0d\u77e5\u662f\u5728\u4ec0\u4e48\u5730\u65b9\u89c1\u8fc7\u4f3c\u7684\uff1f\u968f\u5373\u54d1\u7136\u5931\u7b11\uff0c\u81ea\u5df1\u9690\u5c45\u8d3a\u5170\u5c71\u7684\u65f6\u5019\uff0c \u53ea\u6015\u8fd9\u9648\u7384\u673a\u8fd8\u5728\u8941\u8913\u4e4b\u4e2d\u3002\u4f46\u4e0d\u77e5\u600e\u7684\uff0c\u4e0d\u7531\u81ea\u5df1\u7684\u5bf9\u8fd9\u5c11\u5e74\u4eba\u8d77\u4e86\u4e00\u79cd\u7231\u60dc\u7684\u611f\u60c5\uff0c\u800c \u8fd9\u611f\u60c5\u53c8\u4f3c\u4e4e\u5e76\u4e0d\u662f\u5b8c\u5168\u4e3a\u4e86\u5973\u513f\u7684\u7f18\u6545\u3002 \u3000\u3000\u4e91\u821e\u9633\u5c06\u9648\u7384\u673a\u62b1\u5165\u4e66\u623f\uff0c\u5c06\u4ed6\u653e\u5728\u5e8a\u4e0a\uff0c\u7ed9\u4ed6\u76d6\u4e0a\u4e86\u88ab\uff0c\u53c8\u653e\u4e0b\u4e86\u5e10\u5b50\uff0c\u5c31\u50cf\u7d20\u7d20\u5c0f \u65f6\u5019\u4ed6\u670d\u4f8d\u5979\u5165\u7761\u4e00\u822c\uff0c\u7136\u540e\u71c3\u4e86\u4e00\u7089\u5b89\u606f\u9999\uff0c\u6253\u5f00\u4e86\u4e00\u6247\u7a97\uff0c\u8ba9\u5e26\u7740\u82b1\u8349\u6c14\u606f\u7684\u591c\u98ce\u5439 \u5165\uff0c\u770b\u51fa\u7a97\u5916\uff0c\u6708\u4eae\u5df2\u5c06\u5230\u5929\u5fc3\u4e86\u5440\uff0c\u7d20\u7d20\u8fd8\u6ca1\u6709\u56de\u6765\uff01 \u3000\u3000------------- \u3000\u3000\u91cd\u5e86\u96ea\u513f \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u8fd8\u5251\u5947\u60c5\u5f55\u300b\u2014\u2014\u7b2c\u5341\u4e00\u56de\u3000\u75f4\u7537\u6028\u5973 \u6881\u7fbd\u751f\u300a\u8fd8\u5251\u5947\u60c5\u5f55\u300b \u7b2c\u5341\u4e00\u56de\u3000\u75f4\u7537\u6028\u5973 \u3000\u3000\u4e91\u7d20\u7d20\u8fd9\u65f6\u8fd8\u5728\u5bfb\u89c5\u9648\u7384\u673a\uff0c\u5979\u54ea\u91cc\u77e5\u9053\u9648\u7384\u673a\u5c31\u5728\u5979\u7684\u4e66\u623f\u4e4b\u4e2d\u9163\u7761\u3002 \u3000\u3000\u5c71\u95f4\u660e\u6708\u5189\u5189\u5347\u8d77\uff0c\u6811\u6797\u91cc\u9664\u4e86\u5979\u7684\u811a\u6b65\u58f0\u5916\uff0c\u5c31\u53ea\u6709\u843d\u53f6\u7684\u58f0\u606f\uff0c\u9759\u5f97\u4ee4\u4eba\u5fc3\u60b8\uff0c\u7136 \u800c\u4e91\u7d20\u7d20\u8fd8\u662f\u5728\u68ee\u6797\u91cc\u8e3d\u8e3d\u72ec\u884c\uff0c\u5076\u800c\u4e5f\u6709\u4e00\u4e24\u58f0\u733f\u557c\u864e\u5578\uff0c\u8fdc\u8fdc\u4f20\u6765\uff0c\u6253\u7834\u4e86\u68ee\u6797\u7684\u5bc2 \u9759\u3002\u591c\u98ce\u5439\u6765\uff0c\u4e91\u7d20\u7d20\u6253\u4e86\u4e00\u4e2a\u5bd2\u5664\uff0c\u5979\u4e0d\u662f\u5bb3\u6015\u8fd9\u68ee\u6797\u7684\u5bc2\u9759\uff0c\u7136\u800c\u5979\u7684\u5185\u5fc3\u5374\u786e\u5b9e\u662f\u5728 \u98a4\u6296\u4e0d\u5b89\uff0c\u90a3\u662f\u56e0\u4e3a\u5979\u7236\u4eb2\u7684\u7f18\u6545\u3002 \u3000\u3000\u5979\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u7236\u4eb2\u66fe\u5e72\u90a3\u6869\u7f6a\u5b7d\uff0c\u90a3\u662f\u7edd\u5bf9\u4e0d\u80fd\u9976\u6055\u7684\u7f6a\u5b7d\uff0c\u7eb5\u8bb8\u4ed6\u7684\u7236\u4eb2\uff01\u7136\u800c\u7236 \u4eb2\u662f\u6000\u7740\u591a\u4e48\u6094\u6068\u7684\u5fc3\u60c5\u5411\u5979\u8bc9\u8bf4\u5475\uff0c\u90a3\u75db\u82e6\u7684\u773c\u5149\uff0c\u90a3\u53d1\u6296\u7684\u58f0\u97f3\uff0c\u7b80\u76f4\u50cf\u662f\u4e00\u4e2a\u4e34\u6b7b\u7684 \u7f6a\u4eba\u7684\u5fcf\u6094\uff0c\u5979\u5fcd\u5fc3\u8fd8\u518d\u53bb\u8d23\u9a82\u4ed6\u5417\uff1f\u5440\uff0c\u5979\u591a\u4e48\u6e34\u671b\u80fd\u89c1\u5230\u9648\u7384\u673a\uff0c\u53ea\u6709\u5728\u9648\u7384\u673a\u7684\u8eab \u65c1\uff0c\u53ea\u6709\u5728\u5979\u6700\u4fe1\u4efb\u7684\u4eba\u7684\u8eab\u65c1\uff0c\u4e5f\u8bb8\u80fd\u7a0d\u7a0d\u51cf\u5c11\u5979\u5fc3\u4e2d\u7684\u5bb3\u6015\u3002 \u3000\u3000\u5ffd\u7136\u542c\u5230\u6797\u5b50\u91cc\u6709\u8ffd\u9010\u7684\u811a\u6b65\u58f0\uff0c\u8fd9\u662f\u9648\u7384\u673a\u5417\uff1f\u4ed6\u548c\u8c01\u5728\u4e00\u8d77\uff1f\u811a\u6b65\u58f0\u6765\u5f97\u66f4\u8fd1\u4e86\uff0c \u53ea\u542c\u5f97\u6709\u4eba\u8fde\u58f0\u53eb\u9053\uff1a\u201c\u97f5\u5170\u59d0\u59d0\uff0c\u97f5\u5170\u59d0\u59d0\uff01\u201d\u58f0\u97f3\u7adf\u7136\u4e5f\u662f\u90a3\u6837\u7684\u98a4\u6296\u4e0d\u5b89\uff0c\u5c31\u50cf\u5979\u7684 \u7236\u4eb2\u5728\u77f3\u6d1e\u4e4b\u4e2d\u547c\u5524\u5979\u7684\u58f0\u97f3\u4e00\u6837\uff0c\u8fd9\u4e2a\u4eba\u4e0d\u662f\u9648\u7384\u673a\uff0c\u4ed6\u662f\u4e0a\u5b98\u5929\u91ce\u3002 \u3000\u3000\u4e91\u7d20\u7d20\u8df3\u4e0a\u4e00\u68f5\u5927\u6811\uff0c\u53ea\u89c1\u4e00\u4e2a\u62ab\u5934\u6563\u53d1\u7684\u5c11\u5973\u5411\u524d\u72c2\u5954\uff0c\u4efb\u51ed\u4e0a\u5b98\u5929\u91ce\u53eb\u5f97\u529b\u7aed\u58f0 \u5636\uff0c\u5979\u603b\u662f\u4e0d\u80af\u6b62\u6b65\u3002\u201c\u5440\uff0c\u539f\u6765\u5979\u5c31\u662f\u8427\u97f5\u5170\uff01\u201d\u4e91\u7d20\u7d20\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u5979\u8fd9\u6837\u4f24 \u5fc3\uff1f\u83ab\u975e\u5979\u5df2\u77e5\u9053\u4e86\u9648\u54e5\u54e5\u548c\u6211\u7684\u4e8b\u60c5\uff1f\u201d\u5728\u7231\u60c5\u4e2d\u7684\u5c11\u5973\u6700\u4e3a\u654f\u611f\uff0c\u4e5f\u6700\u5bb9\u6613\u731c\u5230\u53e6\u4e00\u4e2a \u88ab\u7231\u60c5\u6240\u6298\u78e8\u7684\u5c11\u5973\u7684\u5fc3\u4e8b\u3002 \u3000\u3000\u4e91\u7d20\u7d20\u5ffd\u7136\u5bf9\u8427\u97f5\u4e3b\u601c\u60af\u8d77\u6765\uff0c\u5979\u5bf9\u4efb\u4f55\u559c\u6b22\u9648\u7384\u673a\u7684\u4eba\u90fd\u6709\u597d\u611f\uff0c\u7eb5\u7136\u8fd9\u662f\u4e2a\u60f3\u4ece\u5979 \u624b\u4e2d\u62a2\u8d70\u9648\u7384\u673a\u7684\u4eba\u3002\u4e91\u7d20\u7d20\u6084\u6084\u7684\u8ddf\u5728\u4ed6\u4eec\u7684\u540e\u9762\uff0c\u5979\u7684\u6b66\u529f\u8fdc\u5728\u4e0a\u5b98\u5929\u91ce\u4e0e\u8427\u97f5\u5170\u4e4b \u4e0a\uff0c\u4f11\u8bf4\u8fd9\u4e24\u4e2a\u4eba\u90fd\u662f\u6ee1\u6000\u5fc3\u4e8b\uff0c\u5373\u7b97\u5e73\u65f6\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u80fd\u53d1\u73b0\u3002 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u4f53\u529b\u521a\u521a\u6062\u590d\uff0c\u8ffd\u4e86\u8bb8\u4e45\uff0c\u90fd\u6ca1\u6709\u8ffd\u4e0a\uff0c\u5fc3\u4e2d\u6fc0\u52a8\u4e4b\u6781\uff0c\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u97f5\u5170\u59d0 \u59d0\uff0c\u4f60\u8981\u751f\u8981\u6b7b\u6211\u90fd\u548c\u4f60\u4e00\u9053\u3002\u96be\u9053\u4f60\u5fc3\u76ee\u4e2d\u5c31\u53ea\u6709\u4e00\u4e2a\u9648\u7384\u673a\u4e48\uff1f\u201d\u8427\u97f5\u5170\u500f\u7136\u6b62\u6b65\uff0c\u51b7 \u7b11\u4e00\u58f0\uff0c\u56de\u5934\u8bf4\u9053\uff1a\u201c\u4f60\u613f\u4e0e\u6211\u540c\u751f\u5171\u6b7b\u3002\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u8fd9\u4e48\u591a\u5e74\uff0c\u4f60\u8fd8\u4e0d\u77e5\u9053\u6211\u7684 \u5fc3\uff1f\u201d \u3000\u3000\u8427\u97f5\u5170\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u5012\u662f\u5f88\u542c\u9648\u7384\u673a\u7684\u8bdd\uff01\u9648\u7384\u673a\u6015\u6ca1\u4eba\u8981\u6211\uff0c\u6240\u4ee5\u8981\u4f60\u50cf\u5f71\u5b50\u4e00\u6837\u7684 \u8ddf\u7740\u6211\uff0c\u54fc\uff0c\u4e8f\u4f60\u8fd8\u662f\u7537\u5b50\u6c49\u3001\u5927\u4e08\u592b\u3002\u201d\u4e0a\u5b98\u5929\u91ce\u53eb\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u662f\u4ece\u4f55\u8bf4\u8d77\uff1f\u9648\u7384\u673a\u8981 \u6211\u627e\u4f60\uff0c\u90a3\u662f\u4e00\u7247\u597d\u5fc3\uff01\u201d \u3000\u3000\u8427\u97f5\u5170\u9762\u8272\u4e00\u6c89\uff0c\u82cd\u767d\u7684\u9762\u8272\u5728\u6708\u5149\u4e0b\u66f4\u4ee4\u4eba\u611f\u5230\u51b7\u610f\uff0c\u4e0a\u5b98\u5929\u91ce\u6014\u4e86\u4e00\u6014\uff0c\u8fd9\u65f6\u4ed6\u4e0e \u8427\u97f5\u5170\u76f8\u8ddd\u4e0d\u8fc7\u6570\u6b65\uff0c\u4ed6\u5f20\u5f00\u4e86\u53cc\u624b\uff0c\u5374\u4e0d\u6562\u8dd1\u4e0a\u53bb\u62b1\u5979\u3002\u53ea\u542c\u5f97\u8427\u97f5\u5170\u53c8\u662f\u4e00\u58f0\u51b7\u7b11\uff0c\u8bf4 \u9053\uff1a\u201c\u597d\u5fc3\uff1f\u90a3\u6211\u5c31\u771f\u611f\u8c22\u4e0d\u5c3d\u4e86\u3002\u597d\uff0c\u4e0a\u5b98\u5929\u91ce\uff0c\u4f60\u771f\u7684\u613f\u4e0e\u6211\u540c\u751f\u5171\u6b7b\u4e48\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u4f46\u51ed\u4f60\u7684\u5429\u5490\uff0c\u6c34\u91cc\u706b\u91cc\uff0c\u767e\u6b7b\u4e0d\u8f9e\u3002\u201d\u8427\u97f5\u5170\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u597d\u4e86\uff0c\u90a3 \u4f60\u5c31\u7ed9\u6211\u628a\u9648\u7384\u673a\u6740\u4e86\uff0c\u7136\u540e\u56de\u6765\uff0c\u54b1\u4eec\u5c31\u5728\u8fd9\u60ac\u5ca9\u4e0a\u8df3\u4e0b\u53bb\uff01\u201d\u4e0a\u5b98\u5929\u91ce\u5413\u4e86\u4e00\u8df3\uff0c\u53eb \u9053\uff1a\u201c\u97f5\u5170\u59d0\u59d0\uff0c\u4f60\uff0c\u4f60\uff0c\u4f60\u75af\u5566\uff01\u201d\u7231\u4e0e\u6068\u539f\u662f\u76f8\u9694\u4e00\u7eb8\uff0c\u8427\u97f5\u5170\u8fd9\u79cd\u56e0\u7231\u4e4b\u6781\u800c\u6068\u4e4b \u6df1\uff0c\u6124\u6781\u4e4b\u4e0b\uff0c\u5b81\u613f\u540c\u5f52\u4e8e\u5c3d\u7684\u5fc3\u60c5\uff0c\u4e91\u7d20\u7d20\u53ef\u4ee5\u7406\u89e3\uff0c\u4e0a\u5b98\u5929\u91ce\u5374\u7ed9\u5979\u5413\u7740\u4e86\u3002 \u3000\u3000\u4f46\u542c\u5f97\u8427\u97f5\u5170\u4e00\u58f0\u51b7\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4f60\u5c31\u56de\u53bb\u966a\u4f60\u7684\u597d\u670b\u53cb\u5427\uff0c\u522b\u518d\u7ea0\u7f20\u6211\u4e86\uff01\u201d \u62e2\u8896\u4e00\u62c2\uff0c\u624b\u6307\u5ffd\u5730\u4ece\u8896\u7ba1\u4e4b\u4e2d\u4f38\u4e86\u51fa\u6765\uff0c\u5411\u4e0a\u5b98\u5929\u91ce\u91cd\u91cd\u4e00\u6233\uff0c\u4e0a\u5b98\u5929\u91ce\u9aa4\u4e0d\u53ca\u9632\uff0c\u7ed9\u5979 \u6233\u4e2a\u6b63\u7740\uff0c\u4e00\u8de4\u8dcc\u5012\u3002\u8427\u97f5\u5170\u7eb5\u58f0\u72c2\u7b11\uff0c\u65cb\u98ce\u822c\u4f3c\u7684\u9003\u5165\u4e86\u5bc6\u6797\u4e4b\u4e2d\u3002 \u3000\u3000\u5e78\u4e8f\u8fd9\u4e00\u6307\u5e76\u6ca1\u6709\u70b9\u6b63\u4ed6\u7684\u9ebb\u7a74\uff0c\u4e0a\u5b98\u5929\u91ce\u7a0d\u4e3a\u8fd0\u6c14\u51b2\u5173\uff0c\u7a74\u9053\u4fbf\u89e3\u3002\u4e0a\u5b98\u5929\u91ce\u63c9\u63c9\u5173 \u8282\uff0c\u8212\u5c55\u624b\u8db3\uff0c\u7ad9\u4e86\u8d77\u6765\uff0c\u6797\u6df1\u6811\u5bc6\uff0c\u54ea\u91cc\u8fd8\u627e\u5f97\u7740\u8427\u97f5\u5170\u7684\u5f71\u5b50\u3002 \u3000\u3000\u5929\u8fb9\u98de\u6765\u4e86\u4e00\u7247\u9ed1\u4e91\uff0c\u906e\u4f4f\u4e86\u660e\u6708\uff0c\u68ee\u6797\u9634\u6697\u51c4\u51b7\uff01\u4e0a\u5b98\u5929\u91ce\u51e0\u4e4e\u95f7\u5f97\u900f\u4e0d\u8fc7\u6c14\u6765\uff0c\u4ed6 \u672c\u6765\u662f\u4e2a\u8c6a\u8fc8\u7684\u5c11\u5e74\uff0c\u4eca\u665a\u7b2c\u4e00\u6b21\u611f\u5230\u5fc3\u60c5\u662f\u5f02\u5e38\u7684\u6c89\u91cd\uff0c\u7981\u4e0d\u4f4f\u5728\u9ed1\u4e1b\u6797\u4e2d\u53c8\u5927\u58f0\u53eb\u4e86\u8d77 \u6765\uff1a\u201c\u97f5\u5170\u59d0\u59d0\uff01\u97f5\u5170\u59d0\u59d0\uff01\u201d \u3000\u3000\u5ffd\u542c\u5f97\u6709\u4eba\u65a5\u9053\uff1a\u201c\u8427\u59d1\u5a18\u7684\u540d\u5b57\u662f\u4f60\u53eb\u5f97\u7684\u5417\uff1f\u201d\u4e91\u5f00\u6708\u73b0\uff0c\u53ea\u89c1\u56db\u4e2a\u9ed1\u8863\u6c49\u5b50\uff0c\u5df2 \u56f4\u5728\u56db\u8fb9\u3002\u5de6\u4fa7\u4e00\u4e2a\u9762\u4f3c\u7384\u575b\u7684\u77ee\u80d6\u8001\u5934\u8df3\u4e86\u4e0a\u6765\uff0c\u77aa\u7740\u773c\u775b\uff0c\u90a3\u80a1\u795e\u6c14\uff0c\u5c31\u597d\u50cf\u8981\u628a\u4e0a\u5b98 \u5929\u91ce\u5403\u6389\u4f3c\u7684\u3002 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u6012\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u6211\u53eb\u6211\u7684\u97f5\u5170\u59d0\u59d0\uff0c\u4e0e\u4f60\u4f55\u5e72\uff1f\u201d\u90a3\u77ee\u80d6\u8001\u5934\u8de8\u4e0a\u4e00\u6b65\uff0c\u5411 \u4e0a\u5b98\u5929\u91ce\u4ed4\u7ec6\u7684\u6253\u91cf\u4e86\u4e00\u4f1a\uff0c\u201c\u563f\u201d\u7684\u4e00\u58f0\u5e72\u7b11\uff0c\u9634\u607b\u607b\u7684\u95ee\u9053\uff1a\u201c\u4f60\u662f\u4e0a\u5b98\u5929\u91ce\uff1f\u201d\u4e0a\u5b98 \u5929\u91ce\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u90a3\u8001\u5934\u53c8\u9053\uff1a\u201c\u4f60\u662f\u6b66\u5f53\u6d3e\u7684\u65b0\u4efb\u638c\u95e8\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u8fd9\u4e2a\u638c\u95e8\u4eba\u7684\u4f4d\u5b50 \u8fd8\u672a\u6b63\u5f0f\u63a5\u4efb\uff0c\u9664\u4e86\u6b66\u5f53\u6d3e\u7684\u51e0\u4e2a\u957f\u8001\u4e4b\u5916\uff0c\u5916\u4eba\u6839\u672c\u4e0d\u5f97\u800c\u77e5\uff0c\u4e0a\u5b98\u5929\u91ce\u597d\u751f\u8be7\u5f02\uff0c\u8ba8\u538c \u8fd9\u8001\u5934\u7684\u795e\u6c14\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u662f\u53c8\u600e\u6837\uff1f\u4e0d\u662f\u53c8\u600e\u6837\uff1f\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u90a3\u8001\u5934\u5ffd\u5730\u53cd\u624b\u4e00\u638c\uff0c \u7ed9\u4e0a\u5b98\u5929\u91ce\u6765\u4e86\u4e00\u8bb0\u8033\u5149\uff01 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u6839\u672c\u5c31\u4e0d\u8ba4\u8bc6\u8fd9\u4e2a\u8001\u5934\uff0c\u8fd9\u4e00\u8bb0\u8033\u5149\uff0c\u5927\u51fa\u4e0a\u5b98\u5929\u91ce\u610f\u6599\u4e4b\u5916\uff0c\u9669\u9669\u7ed9\u4ed6\u6253 \u4e2d\uff0c\u867d\u7136\u7ec8\u4e8e\u907f\u5f00\uff0c\u4f46\u638c\u98ce\u8fc7\u5904\uff0c\u5df2\u522e\u5f97\u9762\u4e0a\u706b\u8fa3\u8fa3\u7684\u9690\u9690\u4f5c\u75db\u3002\u4e0a\u5b98\u5929\u91ce\u8fd9\u4e00\u6c14\u975e\u540c\u5c0f \u53ef\uff0c\u201c\u7830\u201d\u7684\u4e00\u62db\u201c\u8de8\u864e\u767b\u5c71\u201d\uff0c\u4e00\u8bb0\u957f\u62f3\u731b\u51fb\uff0c\u5927\u6012\u9a82\u9053\uff1a\u201c\u4e16\u95f4\u7adf\u6709\u4f60\u8fd9\u6837\u51f6\u6a2a\u7684\u8001 \u8d3c\uff0c\u4f60\u5f53\u6211\u4e0a\u5b98\u5929\u91ce\u662f\u597d\u6b3a\u8d1f\u7684\u5417\uff1f\u201d \u3000\u3000\u90a3\u8001\u5934\u7528\u4e86\u4e00\u62db\u201c\u4ea4\u52a0\u5341\u5b57\u624b\u201d\uff0c\u5316\u89e3\u4e86\u4e0a\u5b98\u5929\u91ce\u7684\u957f\u62f3\uff0c\u4ef0\u5929\u5927\u7b11\u9053\uff1a\u201c\u4ed7\u52bf\u6b3a\u4eba\uff0c \u90a3\u662f\u4f60\u6b66\u5f53\u6d3e\u60ef\u505a\u7684\u4e8b\uff01\u725f\u72ec\u9038\u65ad\u5b50\u7edd\u5b59\uff0c\u8fd9\u62a5\u5e94\u53ea\u6709\u8f6e\u5230\u4f60\u7684\u8eab\u4e0a\u4e86\u3002\u4f60\u82e5\u60f3\u6d3b\u547d\uff0c\u4e56\u4e56 \u7684\u7ed9\u6211\u78d5\u4e09\u4e2a\u54cd\u5934\uff0c\u542c\u51ed\u6211\u7684\u5429\u5490\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u5316\u62f3\u4e3a\u638c\uff0c\u547c\u3001\u547c\u3001\u547c\uff01\u8fde\u5288\u4e09\u638c\uff0c\u4ed6\u4e0d\u5584\u8a00\u8f9e\uff0c\u4e00\u8154\u6012\u6c14\uff0c\u5c3d\u90fd\u53d1\u6cc4\u51fa\u6765\uff0c \u638c\u52bf\u6709\u5982\u4e71\u77f3\u5d29\u4e91\uff0c\u60ca\u6d9b\u62cd\u5cb8\uff0c\u52c7\u4e0d\u53ef\u5f53\u3002\u90a3\u9ed1\u8138\u819b\u7684\u77ee\u80d6\u8001\u5934\u7ed9\u4ed6\u903c\u5f97\u8fde\u9000\u4e09\u6b65\uff0c\u897f\u9996\u7684 \u90a3\u4e2a\u9ed1\u8863\u6c49\u5b50\u53eb\u9053\uff1a\u201c\u7987\u5927\u54e5\uff0c\u4f55\u5fc5\u4e0e\u4ed6\u591a\u8bf4\u5e9f\u8bdd\uff1f\u201d\u5317\u8fb9\u7684\u90a3\u4e2a\u9ed1\u8863\u6c49\u5b50\u4e5f\u53eb\u9053\uff1a\u201c\u662f \u5440\uff0c\u7987\u4e8c\u54e5\uff0c\u4f60\u54e5\u513f\u4fe9\u4e00\u638c\u5c06\u4ed6\u6253\u53d1\uff0c\u60f3\u8981\u4ec0\u4e48\u4e1c\u897f\uff0c\u8fd8\u6101\u4e0d\u5230\u624b\u5417\uff1f\u201d\u90a3\u77ee\u80d6\u8001\u5934\u5927\u7b11 \u9053\uff1a\u201c\u6b64\u8bdd\u6709\u7406\u3002\u4e8c\u5f1f\u6765\u5440\uff01\u597d\u5c0f\u5b50\uff0c\u4f60\u4e0d\u8bc6\u62ac\u4e3e\uff0c\u660e\u5e74\u4eca\u65e5\uff0c\u662f\u4f60\u7684\u5468\u5e74\u796d\u4e86\u3002\u201d\u53f3\u4fa7\u7684 \u4e00\u4e2a\u77ee\u80d6\u8001\u5934\u5e94\u58f0\u800c\u51fa\uff0c\u4e00\u5de6\u4e00\u53f3\uff0c\u53cc\u638c\u9f50\u626c\u3002 \u3000\u3000\u6765\u7684\u5171\u662f\u56db\u4eba\uff0c\u4e24\u4e2a\u4e2d\u5e74\u7684\u5927\u6c49\u5728\u6797\u8fb9\u628a\u98ce\uff0c\u8fd9\u4e24\u4e2a\u8001\u5934\u5219\u4e0a\u524d\u5e94\u654c\u3002\u4e0a\u5b98\u5929\u91ce\u8fd9\u65f6\u624d \u770b\u5f97\u5206\u660e\uff0c\u4f46\u89c1\u8fd9\u4e24\u4e2a\u8001\u5934\uff0c\u76f8\u8c8c\u751a\u4e3a\u76f8\u4f3c\uff0c\u90fd\u662f\u4e00\u822c\u77ee\u80d6\uff0c\u4e0d\u8fc7\u4e00\u4e2a\u662f\u9ed1\u8138\u819b\uff0c\u4e00\u4e2a\u662f\u7ea2 \u8138\u819b\uff0c\u4e00\u4e2a\u638c\u5fc3\u9ed1\u58a8\u58a8\u7684\uff0c\u4e00\u4e2a\u638c\u5fc3\u5374\u4f3c\u6d82\u6ee1\u4e86\u6731\u7802\uff0c\u638c\u98ce\u8fc7\u5904\uff0c\u90fd\u6709\u4e00\u80a1\u6251\u9f3b\u7684\u8165\u5473\uff01 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u5403\u4e86\u4e00\u60ca\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u4e24\u4e2a\u8001\u8d3c\u6562\u60c5\u662f\u52fe\u9b42\u53cc\u715e\u4e48\uff1f\u201d\u8fd9\u52fe\u9b42\u53cc\u715e\u4e43\u662f\u4e00\u5bf9 \u5b6a\u751f\u5144\u5f1f\uff0c\u9ed1\u8138\u819b\u7684\u90a3\u4e2a\u662f\u5927\u54e5\uff0c\u540d\u53eb\u7987\u82f1\uff0c\u7ec3\u7684\u662f\u94c1\u7802\u638c\u7684\u529f\u592b\uff0c\u7ea2\u8138\u819b\u7684\u90a3\u4e2a\u662f\u4e8c\u54e5\uff0c \u540d\u53eb\u7987\u9738\uff0c\u7ec3\u7684\u662f\u8d64\u7802\u638c\u7684\u529f\u592b\uff0c\u638c\u5fc3\u90fd\u6709\u5267\u6bd2\uff0c\u82e5\u7ed9\u4ed6\u51fb\u4e2d\uff0c\u5341\u4e8c\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u4fbf\u8981\u6bd2\u53d1 \u8eab\u4ea1\uff0c\u6545\u6b64\u4eba\u79f0\u201c\u52fe\u9b42\u53cc\u715e\u201d\uff01\u8fd9\u4e24\u5144\u5f1f\u90fd\u662f\u5c71\u4e1c\u9ed1\u9053\u4e2d\u54cd\u5f53\u5f53\u7684\u4eba\u7269\u3002 \u3000\u3000\u90a3\u9ed1\u8138\u819b\u7684\u8001\u5934\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u65e2\u7136\u8bc6\u5f97\u52fe\u9b42\u53cc\u715e\u7684\u5927\u540d\uff0c\u8fd8\u4e0d\u4e56\u4e56\u7684\u7ed9\u6211\u4eec\u78d5 \u4e09\u4e2a\u54cd\u5934\u3002\u201d\u90a3\u7ea2\u8138\u77a0\u8001\u5934\u4e5f\u559d\u9053\uff1a\u201c\u4f60\u8fd8\u8981\u52a8\u624b\uff1f\u54fc\uff0c\u54fc\uff01\u5f53\u771f\u662f\u4e0d\u8981\u6027\u547d\u4e86\u4e48\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u5403\u8f6f\u4e0d\u5403\u786c\uff0c\u660e\u77e5\u52fe\u9b42\u53cc\u715e\u5389\u5bb3\uff0c\u5374\u662f\u6beb\u4e0d\u793a\u5f31\uff0c\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u51ed\u4f60\u4eec\u8fd9\u4e24\u53cc \u72d7\u722a\u5b50\uff0c\u4e5f\u4e0d\u89c1\u5f97\u5c31\u80fd\u52fe\u4eba\u7684\u9b42\u3001\u593a\u4eba\u7684\u9b44\uff01\u201d\u53cc\u638c\u4e00\u62db\u201c\u5f2f\u5f13\u5c04\u96d5\u201d\uff0c\u5de6\u638c\u5982\u5f13\uff0c\u6a2a\u626b\u7987 \u82f1\uff0c\u53f3\u6307\u5982\u7bad\uff0c\u731b\u6233\u7987\u9738\u3002 \u3000\u3000\u970e\u65f6\u95f4\u6597\u4e86\u4e2a\u4e09\u4e8c\u5341\u62db\uff0c\u4e0a\u5b98\u5929\u91ce\u7684\u638c\u6cd5\u5df2\u5f97\u6b66\u5f53\u7cbe\u9ad3\uff0c\u98d8\u5ffd\u5982\u98ce\uff0c\u529b\u731b\u52bf\u6377\uff0c\u7987\u82f1\u3001 \u7987\u9738\u5728\u4e00\u65f6\u4e4b\u95f4\uff0c\u8fd8\u771f\u7684\u5948\u4ed6\u4e0d\u5f97\u3002 \u3000\u3000\u7987\u82f1\u201c\u563f\u563f\u201d\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u4e8c\u5f1f\uff0c\u8fd9\u5c0f\u5b50\u4e0d\u77e5\u6b7b\u6d3b\uff0c\u54b1\u4eec\u4e14\u6765\u4e2a\u74ee\u4e2d\u6349\u9cd6\u3002\u201d\u4e24\u4e2a\u5de6\u53f3 \u5206\u5f00\uff0c\u53cc\u81c2\u7b95\u5f20\uff0c\u5c06\u4e0a\u5b98\u5929\u91ce\u62e6\u4f4f\uff0c\u6b65\u6b65\u8fdb\u903c\uff0c\u5708\u5b50\u8d8a\u7f29\u8d8a\u5c0f\uff0c\u8981\u77e5\u8fd9\u4e24\u4eba\u7684\u638c\u5fc3\u90fd\u6709\u5267 \u6bd2\uff0c\u4e0a\u5b98\u5929\u91ce\u53ea\u80fd\u4e58\u9699\u53cd\u51fb\uff0c\u4e0d\u6562\u548c\u4ed6\u786c\u62fc\uff0c\u5708\u5b50\u4e00\u7f29\u5c0f\u4e86\uff0c\u62f3\u811a\u65bd\u5c55\u4e0d\u5f00\uff0c\u90a3\u5c31\u5f53\u771f\u662f\u5371 \u9669\u4e07\u5206\uff01 \u3000\u3000\u773c\u89c1\u90a3\u5708\u5b50\u5df2\u7f29\u5230\u5468\u56f4\u516b\u5c3a\u4e4b\u5185\uff0c\u4e0a\u5b98\u5929\u91ce\u731b\u7684\u5927\u559d\u4e00\u58f0\uff0c\u5316\u638c\u4e3a\u62f3\uff0c\u62f3\u638c\u4ea4\u66ff\uff0c\u76f4\u5982 \u5de8\u65a7\u5f00\u5c71\uff0c\u94c1\u9524\u51ff\u77f3\uff0c\u4f7f\u51fa\u4e86\u6700\u521a\u731b\u7684\u91d1\u94a2\u638c\u4e0e\u7f57\u6c49\u62f3\uff0c\u4e0a\u5b98\u5929\u91ce\u5230\u5e95\u662f\u725f\u4e00\u7c9f\u7684\u8863\u94b5\u4f20 \u4eba\uff0c\u5185\u5916\u529f\u592b\u90fd\u6709\u76f8\u5f53\u6839\u5e95\uff0c\u8fd9\u4e00\u8def\u62f3\u638c\u8f6e\u6362\u7684\u529f\u592b\u53c8\u662f\u725f\u72ec\u9038\u7684\u81ea\u521b\u6b66\u529f\uff0c\u5a01\u529b\u4e4b\u5f3a\uff0c\u5728 \u5404\u5bb6\u5404\u6d3e\u7684\u638c\u6cd5\u4e4b\u4e2d\u8981\u63a8\u7b2c\u4e00\uff01 \u3000\u3000\u7987\u82f1\u7987\u9738\u540c\u58f0\u56b7\u9053\uff1a\u201c\u54ce\u54df\uff0c\u8fd9\u5c0f\u5b50\u62fc\u547d\uff01\u201d\u5404\u4eba\u56de\u638c\u81ea\u4fdd\uff0c\u90fd\u95ea\u5f00\u4e86\u4e00\u6b65\uff0c\u4e0a\u5b98\u5929\u91ce \u7528\u8fd9\u7b49\u521a\u731b\u7684\u6253\u6cd5\uff0c\u7684\u786e\u662f\u6709\u4e86\u4e24\u8d25\u4ff1\u4f24\u7684\u5ff5\u5934\uff0c\u7987\u82f1\u7987\u9738\u7684\u94c1\u6c99\u638c\u6253\u4e2d\u4e86\u4ed6\uff0c\u4ed6\u867d\u7136\u4e0d\u80fd \u6d3b\u547d\uff0c\u4f46\u7987\u82f1\u7987\u9738\u82e5\u7ed9\u4ed6\u7684\u91d1\u521a\u638c\u7f57\u6c49\u62f3\u6253\u4e2d\u4e86\uff0c\u6050\u6015\u4e0d\u6b7b\u4ea6\u5f97\u91cd\u4f24\uff0c\u52fe\u9b42\u53cc\u715e\u4e43\u662f\u6210\u540d\u4eba \u7269\uff0c\u4e14\u53c8\u80dc\u7b97\u5728\u63e1\uff0c\u8fd9\u65f6\u53cd\u8f6e\u5230\u4ed6\u4eec\u4e0d\u6562\u548c\u4e0a\u5b98\u5929\u91ce\u786c\u62fc\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u5f97\u624b\u4e0d\u5bb9\u60c5\uff0c\u54ea\u80af\u8ba9\u52fe\u9b42\u53cc\u715e\u518d\u6210\u5305\u56f4\u4e4b\u52bf\uff0c\u7d22\u6027\u786c\u62fc\u5230\u5e95\u3002\u201c\u4e94\u4e01\u5f00\u5c71\u201d\u3001 \u201c\u649e\u5012\u5929\u67f1\u201d\u3001\u201c\u8e0f\u7834\u8d3a\u5170\u201d\u3001\u201c\u54ea\u53f1\u95f9\u6d77\u201d\u7f57\u6c49\u62f3\u4e0e\u91d1\u521a\u638c\u7684\u62db\u6570\u8f6e\u6362\u4f7f\u7528\uff0c\u4e00\u62db\u6bd4\u4e00\u62db \u7d27\u731b\uff0c\u5c06\u5708\u5b50\u53c8\u6269\u5c55\u81f3\u79bb\u8eab\u4e08\u8bb8\uff0c\u84e6\u7136\u957f\u5578\u4e00\u58f0\uff0c\u817e\u8eab\u98de\u8d77\uff0c\u4e00\u8bb0\u201c\u9e70\u51fb\u957f\u7a7a\u201d\uff0c\u5411\u7987\u9738\u7684 \u5929\u7075\u76d6\u62cd\u4e0b\uff0c\u7987\u9738\u5927\u9a87\uff0c\u903c\u5f97\u8eab\u8eaf\u5367\u5730\uff0c\u51e0\u4e2a\u6253\u6eda\uff0c\u6eda\u4e86\u5f00\u53bb\u3002\u4e0a\u5b98\u5929\u91ce\u8eab\u5f62\u843d\u5730\uff0c\u5df2\u662f\u95ef \u51fa\u4e86\u52fe\u9b42\u53cc\u715e\u638c\u529b\u7b3c\u7f69\u7684\u8303\u56f4\u3002 \u3000\u3000\u731b\u542c\u5f97\u5237\u5237\u97ad\u58f0\uff0c\u6c99\u98de\u77f3\u8d77\uff0c\u4e1c\u9996\u90a3\u4e2a\u9ed1\u8863\u6c49\u4eba\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\u60f3\u9003\u4e48\uff1f\u8fd8\u6709\u4ffa\u901a\u5dde\u5e38 \u5c71\u9f99\u5462\uff01\u201d\u4ed6\u4f7f\u7684\u662f\u4e00\u4e08\u591a\u957f\u7684\u8f83\u7b4b\u866c\u9f99\u97ad\uff0c\u97ad\u4e0a\u8fd8\u6709\u8bb8\u591a\u5012\u523a\uff0c\u4e00\u5c55\u5f00\u6765\uff0c\u98ce\u58f0\u547c\u54cd\uff0c\u767b \u65f6\u5377\u8d77\u4e86\u4e00\u56e2\u97ad\u5f71\uff0c\u65b9\u5706\u6570\u4e08\u4e4b\u5185\uff0c\u90fd\u5728\u4ed6\u957f\u97ad\u5377\u53ca\u7684\u8303\u56f4\uff0c\u4f11\u8bf4\u88ab\u4ed6\u7684\u957f\u97ad\u6253\u4e2d\uff0c\u5c31\u662f\u7ed9 \u4ed6\u97ad\u4e0a\u7684\u523a\u94a9\u94a9\u4e2d\uff0c\u4e5f\u662f\u76ae\u5f00\u8089\u88c2\u4e4b\u707e\u3002\u4e0a\u5b98\u5929\u91ce\u53cc\u624b\u7a7a\u7a7a\uff0c\u5982\u4f55\u80fd\u591f\u62b5\u6321\u3002 \u3000\u3000\u597d\u4e2a\u4e0a\u5b98\u5929\u91ce\uff0c\u4e00\u63d0\u8170\u52b2\uff0c\u51ed\u7a7a\u8df3\u8d77\u4e24\u4e08\u591a\u9ad8\uff0c\u5728\u534a\u7a7a\u4e2d\u4e00\u4e2a\u7b4b\u6597\u5012\u7ffb\uff0c\u5411\u540e\u8dc3\u51fa\u4e09\u4e08 \u5f00\u5916\uff0c\u8eab\u5f62\u672a\u5b9a\uff0c\u53ea\u89c1\u897f\u9762\u90a3\u4e2a\u9ed1\u8863\u6c49\u5b50\u4e5f\u8d76\u4e86\u4e0a\u6765\uff0c\u5251\u628a\u4e00\u7ffb\uff0c\u5237\u3001\u5237\u3001\u5237\u4fbf\u662f\u8fde\u73af\u4e09 \u5251\uff0c\u5251\u5c16\u6240\u6307\uff0c\u6b63\u662f\u4e0a\u5b98\u5929\u91ce\u80f8\u90e8\u4e09\u5904\u5927\u7a74\u7684\u65b9\u4f4d\uff0c\u4e0a\u5b98\u5927\u91ce\u82e5\u662f\u4e0d\u53d8\u8eab\u5f62\uff0c\u90a3\u5c31\u662f\u51d1\u4e0a\u53bb \u7ed9\u4ed6\u523a\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u4e00\u626d\u8eab\u8eaf\uff0c\u4ecd\u4e58\u9699\u9003\u8d70\uff01\u4ed6\u95ea\u5f97\u5df2\u7b97\u5f88\u5feb\uff0c\u54ea\u77e5\u6765\u4eba\u7684\u5251\u62db\u66f4\u5feb\uff0c\u5c31\u5728\u4ed6\u62bd\u773c \u5ba1\u89c6\u5f62\u52bf\u4e4b\u65f6\uff0c\u5237\u3001\u5237\u3001\u5237\u53c8\u662f\u4e00\u8fde\u5251\u8ffd\u8e2a\u800c\u81f3\uff0c\u51b7\u68ee\u68ee\u7684\u5251\u950b\u582a\u582a\u7684\u89e6\u5230\u4e86\u4ed6\u7684\u540e\u5fc3\uff01\u53ea \u542c\u5f97\u90a3\u6c49\u5b50\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u4eca\u65e5\u8981\u60f3\u9003\u8d70\uff0c\u4ffa\u516c\u51b6\u826f\u4e09\u5b57\u5012\u5199\uff01\u201d \u3000\u3000\u8fd9\u5e38\u5c71\u9f99\u548c\u516c\u51b6\u826f\u4e8c\u4eba\u90fd\u662f\u5c71\u4e1c\u5267\u76d7\uff0c\u5728\u9ed1\u9053\u4e0a\u7684\u540d\u5934\u6bd4\u52fe\u9b42\u53cc\u715e\u66f4\u54cd\u3002\u4e0a\u5b98\u5929\u91ce\u53c8\u6012 \u53c8\u60ca\uff0c\u540c\u65f6\u53c8\u662f\u5341\u5206\u8be7\u5f02\uff0c\u4ed6\u4eec\u6b66\u5f53\u6d3e\u5386\u4ee3\u5b9a\u4e0b\u6765\u7684\u89c4\u77e9\u662f\u65e2\u4e0d\u80fd\u505a\u5f3a\u76d7\uff0c\u4e5f\u4e0d\u80fd\u505a\u4fdd\u9556\uff0c \u56e0\u4e4b\u867d\u7136\u4e0e\u9ed1\u9053\u4e0a\u7684\u4eba\u7269\u8bf4\u4e0d\u4e0a\u6709\u4ec0\u4e48\u4ea4\u60c5\uff0c\u4f46\u4e5f\u4ece\u6765\u4e0d\u4f1a\u4e0e\u4ed6\u4eec\u7ed3\u6028\u3002\u4e0a\u5b98\u5929\u91ce\u672c\u4eba\u66f4\u662f \u521d\u8d70\u6c5f\u6e56\uff0c\u4eca\u756a\u8fd8\u662f\u7b2c\u4e00\u6b21\u9047\u5230\u4ed6\u4eec\u3002 \u3000\u3000\u4f55\u4ee5\u8fd9\u4e9b\u7eff\u6797\u4e0a\u7684\u51f6\u795e\u6076\u715e\u5982\u6b64\u65e0\u7406\u53d6\u95f9\uff0c\u751a\u800c\u8981\u53d6\u4ed6\u6027\u547d\uff1f\u4e0a\u5b98\u5929\u91ce\u771f\u771f\u662f\u767e\u601d\u83ab \u89e3\uff0c\u6b64\u65f6\u6b64\u9645\uff0c\u4e5f\u4e0d\u5bb9\u4ed6\u6709\u7a7a\u601d\u7d22\uff0c\u5e38\u5c71\u9f99\u7684\u957f\u97ad\uff0c\u516c\u51b6\u826f\u7684\u77ed\u5251\uff0c\u4e24\u8fb9\u4e00\u903c\uff0c\u628a\u4ed6\u903c\u5f97\u8fde \u8fde\u540e\u9000\uff0c\u7136\u800c\u5374\u5e76\u4e0d\u4f24\u4ed6\uff0c\u53ea\u662f\u628a\u4ed6\u903c\u5f97\u518d\u9000\u56de\u539f\u4f4d\uff0c\u4ecd\u7136\u9677\u5165\u4e86\u52fe\u9b42\u53cc\u715e\u7684\u5305\u56f4\u5708\u5185\uff01 \u3000\u3000\u8fd9\u662f\u9ed1\u9053\u4e0a\u7ed9\u540c\u4f19\u627e\u56de\u9762\u5b50\u7684\u610f\u601d\uff0c\u4e0a\u5b98\u5929\u91ce\u662f\u4ece\u52fe\u9b42\u53cc\u715e\u624b\u4e0b\u9003\u8131\u7684\uff0c\u6240\u4ee5\u5e38\u5c71\u9f99\u548c \u516c\u51b6\u826f\u4ecd\u7136\u628a\u4ed6\u201c\u9001\u201d\u56de\u52fe\u9b42\u53cc\u715e\u7684\u624b\u4e2d\uff0c\u8ba9\u52fe\u9b42\u53cc\u715e\u5904\u7f6e\u3002 \u3000\u3000\u52fe\u9b42\u53cc\u715e\u6253\u4e86\u4e00\u4e2a\u54c8\u54c8\uff0c\u7987\u82f1\u9053\uff1a\u201c\u6211\u8c22\u4e24\u4f4d\u8001\u5f1f\u5566\uff01\u201d\u7987\u9738\u6025\u4e8e\u8981\u62a5\u521a\u624d\u90a3\u4e00\u638c\u4e4b \u8fb1\uff0c\u66f4\u4e0d\u6253\u8bdd\uff0c\u624b\u638c\u4e00\u7ffb\uff0c\u90a3\u901a\u7ea2\u5982\u8840\u7684\u638c\u5fc3\u3002\u5e26\u7740\u4e00\u80a1\u8165\u98ce\uff0c\u7acb\u523b\u4fbf\u5411\u4e0a\u5b98\u5929\u91ce\u7684\u80f8\u819b\u5370 \u4e0b\uff0c\u4e0a\u5b98\u5929\u91ce\u6b63\u81ea\u8f6c\u5f97\u660f\u5934\u660f\u8111\uff0c\u5f85\u542c\u5f97\u638c\u98ce\u98d2\u7136\uff0c\u95ea\u907f\u5df2\u6765\u4e0d\u53ca\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u4e00\u58f0\u6e05\u8106\u7684\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u7987\u53d4\u53d4\u624b\u4e0b\u7559\u60c5\uff01\u201d\u7adf\u7136\u662f\u8427\u97f5\u5170\u7684\u58f0\u97f3\uff0c\u7987\u9738\u4e00\u638c\u5288 \u51fa\uff0c\u53c8\u62bd\u4e86\u56de\u6765\uff0c\u5934\u4e0a\u9752\u7b4b\u6bd5\u9732\uff0c\u4e0a\u5b98\u5929\u91ce\u5446\u4e86\u4e00\u5446\uff0c\u201c\u97f5\u5170\u201d\u4e24\u5b57\u8fd8\u672a\u51fa\u53e3\uff0c\u80c1\u4e0b\u7684\u201c\u5c11 \u5e9c\u7a74\u201d\u5ffd\u7136\u4e00\u9ebb\uff0c\u8dcc\u5012\u5730\u4e0a\uff0c\u539f\u6765\u662f\u7ed9\u516c\u51b6\u826f\u98de\u77f3\u6253\u4e2d\u4e86\u9ebb\u7a74\u3002 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u8eab\u5b50\u4e0d\u80fd\u8f6c\u52a8\uff0c\u773c\u775b\u8fd8\u662f\u770b\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u53ea\u89c1\u7987\u82f1\u7987\u9738\u548c\u5e38\u5c71\u9f99\u516c\u51b6\u826f\u56db\u4eba\u5206 \u6210\u4e24\u6392\uff0c\u7adf\u7136\u5bf9\u8427\u97f5\u5170\u6267\u4f53\u751a\u606d\uff0c\u7531\u7987\u82f1\u9886\u5934\u8bf4\u8bdd\uff0c\u8eac\u8eab\u8a00\u9053\uff1a\u201c\u5949\u8001\u8235\u4e3b\u4e4b\u547d\uff0c\u8bf7\u59d1\u5a18\u56de \u5bb6\u3002\u201d\u8427\u97f5\u5170\u9053\uff1a\u201c\u6211\u4e0d\u56de\u53bb\uff01\u201d\u7987\u82f1\u9053\uff1a\u201c\u8001\u8235\u4e3b\u5f88\u60f3\u5ff5\u59d1\u5a18\uff0c\u4ed6\u5df2\u62e9\u597d\u65e5\u5b50\uff0c\u5c31\u8981\u91d1\u76c6 \u6d17\u624b\uff0c\u5c01\u5251\u95ed\u95e8\u3002\u8bf7\u59d1\u5a18\u56de\u53bb\uff0c\u7ee7\u627f\u4ed6\u7684\u5bb6\u5f53\u3002\u201d \u3000\u3000\u8427\u97f5\u5170\u9053\uff1a\u201c\u6211\u4e0d\u5e0c\u7f55\u201d\u3002\u516c\u51b6\u826f\u773c\u73e0\u4e00\u8f6c\uff0c\u4e0a\u524d\u8bf4\u9053\uff1a\u201c\u8427\u59d1\u5a18\uff0c\u4f60\u7239\u7239\u5e74\u7eaa\u8001\u8fc8\uff0c \u819d\u4e0b\u65e0\u513f\uff0c\u53ea\u4f60\u4e00\u5973\uff0c\u4ed6\u4e00\u751f\u5fc3\u8840\uff1f\u6323\u6765\u4e86\u8fd9\u4efd\u57fa\u4e1a\u3002\u55ef\uff0c\u7eb5\u7b97\u4f60\u4e0d\u5e0c\u7f55\uff0c\u96be\u9053\u4f60\u5c31\u4e0d\u987e\u5ff5 \u4ed6\u8001\u4eba\u5bb6\u5417\uff1f\u201d\u8427\u97f5\u5170\u9ed8\u7136\u4e0d\u8bed\u3002\u5e38\u5c71\u9f99\u9053\uff1a\u201c\u4f60\u7239\u7239\u8bf4\uff0c\u53ea\u8981\u4f60\u80af\u56de\u53bb\uff0c\u4e00\u5207\u542c\u4ece\u4f60\u7684\u610f \u601d\u3002\u91d1\u5bb6\u7684\u4e8b\u518d\u4e5f\u4e0d\u63d0\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u5fc3\u5934\u4e00\u9707\uff0c\u8d77\u521d\u4ed6\u5bf9\u8fd9\u56db\u4e2a\u5c71\u4e1c\u7eff\u6797\u4e2d\u54cd\u5f53\u5f53\u7684\u4eba\u7269\u5bf9\u8427\u97f5\u5170\u6267\u4f53\u4e4b\u606d\uff0c\u751a\u4e3a \u60ca\u8be7\uff0c\u5f85\u542c\u5230\u4e86\u201c\u91d1\u76c6\u6d17\u624b\u201d\u7b49\u7b49\u8bdd\u5934\uff0c\u8fd9\u624d\u604d\u7136\u5927\u609f\uff1a\u201c\u6562\u60c5\u8427\u97f5\u5170\u7684\u7236\u4eb2\u7adf\u7136\u662f\u7eff\u6797\u4e2d \u7684\u4e00\u4e2a\u9886\u8896\u4eba\u7269\uff01\u201d \u3000\u3000\u4ed6\u4e0e\u8427\u97f5\u5170\u5df2\u76f8\u8bc6\u4e09\u5e74\uff0c\u4ece\u6765\u672a\u95ee\u8fc7\u5979\u7684\u6765\u5386\uff0c\u6709\u65f6\u5728\u95f2\u8bdd\u4e4b\u4e2d\uff0c\u8427\u97f5\u5170\u4e5f\u5076\u5c14\u900f\u9732\u51fa \u5979\u662f\u201c\u6b66\u5b66\u4e16\u5bb6\u201d\u3002\u5979\u8fd8\u5e38\u8bf4\uff1a\u201c\u7537\u5b50\u53ef\u4ee5\u6e38\u5b66\u56db\u65b9\uff0c\u5973\u5b50\u4e3a\u4ec0\u4e48\u5c31\u4e0d\u53ef\u4ee5\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u5c31 \u56e0\u4e3a\u5979\u8fd9\u80a1\u8c6a\u8fc8\u7684\u6c14\u6982\uff0c\u6df1\u89c9\u4e0e\u81ea\u5df1\u76f8\u6295\uff0c\u8fd9\u624d\u79c1\u4e0b\u6674\u604b\u5979\u7684\u3002\u53ea\u9053\u5979\u662f\u4e00\u4e2a\u4e0d\u7f81\u7684\u5973\u4fa0\uff0c \u5374\u539f\u6765\u5979\u662f\u5f3a\u76d7\u5934\u5b50\u7684\u5973\u513f\u3002\u7136\u800c\u8fd9\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u4e0a\u5b98\u5929\u91ce\u81ea\u5df1\u5c31\u4e0b\u4e86\u51b3\u5fc3\uff0c\u8981\u7ee7\u627f\u6bd5\u51cc \u98ce\u7684\u8863\u94b5\uff0c\u505a\u4e00\u4e2a\u56db\u6d77\u4e3a\u5bb6\u7684\u4fa0\u76d7\u3002 \u3000\u3000\u4ed6\u5012\u4e0d\u662f\u4e3a\u4e86\u8427\u97f5\u5170\u662f\u5f3a\u76d7\u7684\u5973\u513f\u800c\u8f7b\u89c6\u5979\uff0c\u4f46\u4ed6\u5374\u611f\u5230\u5f02\u6837\u7684\u60b2\u54c0\uff0c\u81ea\u5df1\u5bf9\u5979\u62ab\u809d\u6ca5 \u80c6\uff0c\u5979\u5374\u5c06\u81ea\u5df1\u5f53\u505a\u5916\u4eba\uff0c\u5979\u5f03\u5bb6\u51fa\u8d70\uff0c\u5176\u4e2d\u5b9a\u6709\u7f18\u6545\uff0c\u4f46\u5979\u7684\u54c0\u6101\u82e6\u4e50\uff0c\u5374\u4e0d\u80af\u4e0e\u81ea\u5df1\u540c \u62c5\u3002\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u81ea\u5df1\u5411\u4e91\u821e\u9633\u7d22\u5251\u8c31\u4e4b\u4e8b\uff0c\u4f55\u5c1d\u4e0d\u4e5f\u662f\u7792\u7740\u5979\u3002\u4e5f\u8bb8\u5176\u4e2d\u6709\u751a\u9690\u60c5\uff0c\u5979\u8fd8 \u4e0d\u80fd\u5411\u81ea\u5df1\u5410\u9732\uff1f \u3000\u3000\u4f46\u89c1\u8427\u97f5\u5170\u82e5\u6709\u6240\u601d\uff0c\u5446\u5446\u7684\u51fa\u4e86\u4e00\u4f1a\u5b50\u795e\uff0c\u773c\u5149\u5ffd\u7136\u5411\u4ed6\u77a5\u6765\uff0c\u4e0a\u5b98\u5929\u91ce\u6026\u7136\u5fc3\u8df3\uff0c \u53ea\u542c\u5f97\u8427\u97f5\u5170\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u4e3a\u4ec0\u4e48\u628a\u4ed6\u64d2\u4e86\uff1f\u201d \u3000\u3000\u7987\u82f1\u8eac\u8eab\u7b54\u9053\uff1a\u201c\u56de\u7980\u59d1\u5a18\uff0c\u8fd9\u5c0f\u5b50\u540d\u53eb\u4e0a\u5b98\u5929\u91ce\uff0c\u4e43\u662f\u6b66\u5f53\u6d3e\u7684\u65b0\u4efb\u638c\u95e8\u3002\u201d\u8427\u97f5\u5170 \u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d\u7987\u82f1\u5fc3\u9053\uff1a\u201c\u4f60\u4f55\u53ea\u77e5\u9053\uff1f\u8001\u8235\u4e3b\u82e5\u4e0d\u662f\u542c\u5230\u98ce\u58f0\uff0c\u6015\u4f60\u4e0a\u8fd9\u5c0f\u5b50\u7684\u5f53\uff0c \u54ea\u4f1a\u8fd9\u6837\u7740\u6025\u7684\u6d3e\u6211\u8d76\u6765\u3002\u201d \u3000\u3000\u7987\u82f1\u9634\u607b\u607b\u7684\u5fae\u5fae\u4e00\u7b11\uff0c\u8427\u97f5\u5170\u9053\uff1a\u201c\u662f\u6b66\u5f53\u6d3e\u7684\u638c\u95e8\u4eba\u53c8\u600e\u4e48\u6837\uff1f\u201d\u7987\u82f1\u9053\uff1a\u201c\u4f60\u7239 \u7239\u7eb5\u6a2a\u5927\u6c5f\u5357\u5317\uff0c\u4ece\u672a\u6709\u4eba\u6562\u4e0e\u4ed6\u4f5c\u5bf9\uff0c\u53ea\u662f\u6709\u4e00\u6b21\u83ab\u540d\u5176\u5999\u7684\u6298\u5728\u4e00\u4e2a\u8001\u8d3c\u624b\u4e0a\uff0c\u4ed6\u6bd5\u751f \u8ba4\u4e3a\u5947\u803b\u5927\u8fb1\u3002\u8fd9\u8001\u8d3c\u4fbf\u662f\u6b66\u5f53\u6d3e\u7684\u4e0a\u4e24\u4ee3\u638c\u95e8\u725f\u72ec\u9038\uff01\u201d\u8427\u97f5\u5170\u9053\uff1a\u201c\u8fd9\u4e8b\u60c5\u7239\u7239\u4e5f\u4e0e\u6211 \u8bf4\u8fc7\uff0c\u4f46\u8fd9\u4e0e\u4ed6\u6709\u4f55\u76f8\u5e72\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u8fd9\u624d\u604d\u7136\u5927\u609f\uff0c\u602a\u4e0d\u5f97\u8427\u97f5\u5170\u7b2c\u4e00\u6b21\u542c\u8bf4\u4ed6\u662f\u6b66\u5f53\u95e8\u4e0b\u7684\u65f6\u5019\uff0c\u8138\u4e0a\u4f1a\u73b0\u51fa\u90a3 \u4e48\u5947\u5f02\u7684\u795e\u60c5\u3002 \u3000\u3000\u7987\u82f1\u201c\u563f\u563f\u201d\u7b11\u9053\uff1a\u201c\u600e\u4e48\u8bf4\u4e0d\u76f8\u5e72\uff0c\u4ed6\u662f\u725f\u72ec\u9038\u7684\u5f92\u5b59\uff0c\u6b66\u5f53\u6d3e\u7684\u8863\u94b5\u4f20\u4eba\u5440\uff01\u201d\u8427 \u97f5\u5170\u9053\uff1a\u201c\u4e8b\u9694\u4e09\u5341\u5e74\uff0c\u90a3\u65f6\u4ed6\u8fd8\u6ca1\u6709\u51fa\u4e16\u3002\u6211\u8bf4\u4e0d\u76f8\u5e72\u5c31\u4e0d\u76f8\u5e72\u3002\u201d\u7987\u82f1\u9053\uff1a\u201c\u59d1\u5a18\u8981\u653e \u4eba\uff0c\u8001\u5974\u81ea\u4e0d\u6562\u8fdd\u80cc\uff0c\u53ea\u6015\u8001\u8235\u4e3b\u4ed6\u8bf4\u76f8\u5e72\uff0c\u8d23\u5907\u4e0b\u6765\uff0c\u6211\u53ef\u62c5\u5f53\u4e0d\u8d77\uff01\u201d\u8427\u97f5\u5170\u67f3\u7709\u4e00 \u626c\uff0c\u9053\uff1a\u201c\u4f60\u53ea\u7ba1\u653e\u4eba\uff0c\u6211\u81ea\u4e0e\u7239\u7239\u53bb\u8bf4\uff01\u201d \u3000\u3000\u7987\u82f1\u6b63\u662f\u8981\u5979\u8bf4\u8fd9\u53e5\u8bdd\uff0c\u8981\u77e5\u8427\u97f5\u5170\u7684\u7236\u4eb2\u6d3e\u4eba\u5bfb\u5979\u56de\u5bb6\u3002\u542c\u5230\u98ce\u58f0\uff0c\u77e5\u9053\u5979\u4e0e\u4e0a\u5b98\u5929 \u91ce\u4ea4\u6e38\uff0c\u4e5f\u662f\u5176\u4e2d\u7684\u4e00\u4e2a\u7f18\u6545\u3002 \u3000\u3000\u516c\u51b6\u826f\u9053\uff1a\u201c\u7987\u5927\u54e5\uff0c\u5c0f\u59d0\u65e2\u7136\u613f\u4e0e\u54b1\u4eec\u4e00\u540c\u56de\u53bb\uff0c\u90a3\u4e48\u8fd9\u5c0f\u5b50\u4fbf\u653e\u4e86\u5427\u3002\u53ef\u662f\u90a3\u5251\u8c31 \u5374\u4e00\u5b9a\u5f97\u5411\u4ed6\u7d22\u56de\u3002\u201d\u8427\u97f5\u5170\u8be7\u9053\uff1a\u201c\u4ec0\u4e48\u5251\u8c31\uff1f\u201d\u516c\u51b6\u826f\u4f38\u6307\u4e00\u6233\uff0c\u5df2\u662f\u89e3\u5f00\u4e86\u4e0a\u5b98\u5929\u91ce \u7684\u7a74\u9053\u3002 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u53eb\u9053\uff1a\u201c\u97f5\u5170\u59d0\u59d0\uff0c\u4f60\u56de\u53bb\u54ea\u513f\uff1f\u201d\u521a\u521a\u79fb\u52a8\u811a\u6b65\uff0c\u5374\u88ab\u90a3\u56db\u4e2a\u4eba\u4e00\u9f50\u62e6\u4f4f\u3002 \u3000\u3000\u516c\u51b6\u826f\u9053\uff1a\u201c\u770b\u5728\u8427\u59d1\u5a18\u4efd\u4e0a\uff0c\u9976\u4f60\u4e00\u6761\u5c0f\u547d\uff0c\u8fbe\u6469\u5251\u8c31\uff0c\u8fd8\u4e0d\u62ff\u51fa\u6765\u4e48\uff1f\u201d\u4e0a\u5b98\u5929\u91ce \u9053\uff1a\u201c\u4ec0\u4e48\u8fbe\u6469\u5251\u8c31\uff1f\u201d\u516c\u51b6\u826f\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u6545\u4f5c\u75f4\u5446\uff0c\u60f3\u88c5\u849c\u4e48\uff1f\u4ec0\u4e48\u8fbe\u6469\u5251\u8c31\uff1f \u725f\u72ec\u9038\u4e09\u5341\u5e74\u524d\u5de7\u53d6\u8c6a\u593a\u7684\u90a3\u672c\u5251\u8c31\uff0c\u4e0d\u662f\u8fbe\u6469\u5251\u8c31\u662f\u4ec0\u4e48\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u6211\u4e5f\u6b63\u8981\u5bfb \u8fd9\u672c\u5251\u8c31\uff0c\u62ff\u4ec0\u4e48\u7ed9\u4f60\uff1f\u201d\u516c\u51b6\u826f\u9053\uff1a\u201c\u96be\u9053\u725f\u4e00\u7c9f\u8fd8\u80fd\u5c06\u8fd9\u672c\u5251\u8c31\u5e26\u5230\u68fa\u6750\u91cc\u53bb\uff1f\u4f60\u662f\u6b66 \u5f53\u6d3e\u7684\u638c\u95e8\uff0c\u4e0d\u5728\u4f60\u7684\u624b\u4e2d\u8fd8\u5728\u54ea\u513f\uff1f\u201d\u8427\u97f5\u5170\u542c\u5f97\u83ab\u540d\u5176\u5999\uff0c\u62e6\u7740\u4e86\u516c\u51b6\u826f\u9053\uff1a\u201c\u65e2\u7136\u662f \u4ed6\u4eec\u6b66\u5f53\u6d3e\u7684\u5251\u8c31\uff0c\u54b1\u4eec\u8981\u5b83\u4f5c\u751a\u4e48\uff1f\u8fd9\u5c82\u4e0d\u574f\u4e86\u6211\u7239\u7239\u7684\u540d\u5934\uff1f\u201d \u3000\u3000\u516c\u51b6\u826f\u5fae\u73b0\u8be7\u8272\uff0c\u201c\u566b\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u8427\u59d1\u5a18\uff0c\u4f60\u7239\u7239\u8fd8\u6ca1\u6709\u5411\u4f60\u8bf4\u8fc7\u5417\uff1f\u8fd9\u8fbe\u6469\u5251\u8c31 \u672c\u6765\u5c31\u4e0d\u662f\u725f\u72ec\u9038\u5e94\u5f97\u7684\u4e1c\u897f\uff01\u201d \u3000\u3000\u8427\u97f5\u5170\u9053\uff1a\u201c\u96be\u9053\u8fd9\u5251\u8c31\u7adf\u662f\u6211\u7239\u7239\u7684\uff1f\u6211\u53ef\u4ece\u6ca1\u6709\u542c\u7239\u7239\u8bf4\u8fc7\u4ec0\u4e48\u8fbe\u6469\u5251\u8c31\u3002\u201d \u3000\u3000\u7987\u82f1\u9053\uff1a\u201c\u672c\u6765\u8fd9\u5251\u8c31\u65e2\u4e0d\u662f\u725f\u72ec\u9038\u7684\uff0c\u4e5f\u4e0d\u662f\u4f60\u7239\u7239\u7684\uff0c\u4e0d\u8fc7\u5230\u4e86\u4eca\u5929\uff0c\u4e0e\u8fd9\u5251\u8c31\u6709 \u5173\u7684\u4eba\u90fd\u5df1\u6b7b\u4e86\uff0c\u9664\u4e86\u4f60\u7684\u7239\u7239\u4e4b\u5916\uff0c\u8c01\u4e5f\u4e0d\u914d\u505a\u8fd9\u5251\u8c31\u7684\u4e3b\u4eba\u3002\u201d \u3000\u3000\u4e91\u7d20\u7d20\u4f0f\u5728\u5927\u6811\u4e4b\u4e0a\uff0c\u8fd9\u4e9b\u8bdd\u5168\u90e8\u90fd\u542c\u5165\u8033\u4e2d\uff0c\u4e0d\u89c9\u6697\u6697\u8be7\u5f02\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u5988\u5988\u5f53\u5e74\u52a9 \u6211\u7239\u7239\u5077\u4e86\u5979\u5916\u5bb6\u8fd9\u90e8\u5251\u8c31\uff0c\u4e3a\u4e86\u6b64\u4e8b\uff0c\u6211\u5988\u7ec8\u65e5\u62b1\u61be\uff0c\u6211\u5988\u5988\u7edd\u4e0d\u4f1a\u9a97\u6211\u3002\u8fd9\u5251\u8c31\u600e\u4e48\u4f1a \u4e0d\u662f\u6211\u5916\u516c\u7684\uff1f\u201d\u4e91\u7d20\u7d20\u8be7\u5f02\uff0c\u4e0a\u5b98\u5929\u91ce\u66f4\u662f\u8be7\u5f02\uff0c\u867d\u7136\u6bd5\u51cc\u98ce\u4e5f\u5bf9\u4ed6\u8bf4\u8fc7\uff0c\u8fd9\u5251\u8c31\u4e0d\u8be5\u5c5e \u4e8e\u4ed6\u5e08\u7956\u6240\u6709\uff0c\u4f46\u6bd5\u51cc\u98ce\u5374\u8bf4\uff0c\u8fd9\u521b\u8c31\u4e43\u662f\u4e00\u4e2a\u5f02\u4eba\u9057\u7559\u7ed9\u4e0e\u4ed6\u5e08\u7956\u9f50\u540d\u7684\u4e00\u4f4d\u5f53\u4ee3\u5927\u4fa0 \u7684\u3002\u4e0e\u4ed6\u5e08\u7956\u9f50\u540d\u7684\u5f53\u4ee3\u5927\u4fa0\uff0c\u53ea\u6709\u4e00\u4f4d\u9648\u5b9a\u65b9\uff0c\u5374\u53c8\u600e\u7684\u4f1a\u7f20\u4e0a\u8427\u97f5\u5170\u7684\u7236\u4eb2\uff1f\u8427\u97f5\u5170\u4e5f \u662f\u540c\u6837\u7684\u83ab\u540d\u5176\u5999\uff0c\u7987\u82f1\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u82e5\u4e0d\u660e\u767d\u4e4b\u5904\uff0c\u56de\u53bb\u95ee\u4f60\u7239\u7239\u81ea\u7136\u77e5\u9053\uff0c\u5484\uff0c\u8fd9\u5c0f\u5b50 \u5f97\u4e86\u6027\u547d\u3001\u5251\u8c31\u8fd8\u4e0d\u820d\u5f97\u62ff\u51fa\u6765\u5417\uff1f\u201d\u540e\u9762\u8fd9\u4e24\u53e5\u8bdd\u662f\u5411\u7740\u4e0a\u5b98\u5929\u91ce\u8bf4\u7684\u3002 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u70e6\u8e81\u4e4b\u6781\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\u5251\u8c31\u90fd\u4e0d\u5173\u6211\u7684\u4e8b\u3002\u597d\uff0c\u544a\u8bc9\u4f60\u4eec\uff0c\u8fbe\u6469\u5251\u8c31\u5c31 \u5728\u4e91\u821e\u9633\u624b\u4e0a\uff0c\u4f60\u4eec\u8981\u5c31\u81ea\u5df1\u53bb\u62ff\uff0c\u522b\u518d\u9ebb\u70e6\u6211\u4e86\u3002\u6211\u53ea\u60f3\u548c\u97f5\u5170\u59d0\u59d0\u5b89\u5b89\u9759\u9759\u7684\u8bf4\u51e0\u53e5 \u8bdd\u3002\u201d \u3000\u3000\u5e38\u5c71\u9f99\u9762\u8272\u4e00\u6c89\uff0c\u53eb\u9053\uff1a\u201c\u4e91\u821e\u9633\uff0c\u4ed6\u5728\u54ea\u513f\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u5c31\u5728\u8fd9\u8d3a\u5170\u5c71\u4e0a\u3002\u201d \u516c\u51b6\u826f\u7b11\u9053\uff1a\u201c\u4f60\u62ff\u4e91\u821e\u9633\u6765\u5413\u6211\u4eec\uff1f\u201d\u7987\u82f1\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u8fd9\u5c0f\u5b50\u7684\u8bdd\u5f53\u771f\uff1f\u201d\u8427\u97f5\u5170\u9053\uff1a \u201c\u4ec0\u4e48\u5251\u8c31\u4e0d\u5251\u8c31\u7684\uff0c\u6211\u4e00\u6982\u90fd\u4e0d\u77e5\u60c5\u3002\u4e0d\u8fc7\uff0c\u4e0a\u5b98\u5929\u91ce\u4ece\u6765\u4e0d\u4f1a\u8bf4\u8c0e\uff0c\u8fd9\u5374\u662f\u6211\u77e5\u9053 \u7684\u3002\u201d\u7987\u82f1\u9053\uff1a\u201c\u90a3\u4e48\u5bf9\u4e0d\u8d77\uff0c\u6211\u4eec\u53ef\u8981\u5148\u641c\u4e00\u641c\u4ed6\u4e86\u3002\u201d \u3000\u3000\u8427\u97f5\u5170\u67f3\u7709\u4e00\u7ad6\uff0c\u559d\u9053\uff1a\u201c\u7987\u82f1\uff01\u201d\u7987\u82f1\u6253\u4e86\u4e2a\u63d6\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8bf7\u6055\u8001\u5974\u65e0\u793c\uff0c\u8001\u5974 \u5c82\u6562\u4e0d\u7ed9\u59d1\u5a18\u9762\u5b50\uff0c\u53f5\u5948\u8fd9\u5251\u8c31\u5173\u7cfb\u91cd\u5927\uff0c\u4eca\u65e5\u4e0d\u641c\uff0c\u9519\u5931\u673a\u7f18\uff0c\u8001\u8235\u4e3b\u7684\u602a\u8d23\u8c01\u4e5f\u62c5\u5f53\u4e0d \u8d77\uff0c\u641c\uff01\u201d \u3000\u3000\u8427\u97f5\u5170\u6c14\u5f97\u5982\u82b1\u679d\u4e71\u98a4\uff0c\u8fd9\u56db\u4e2a\u867d\u8bf4\u662f\u4ed6\u7236\u4eb2\u7684\u624b\u4e0b\uff0c\u5230\u5e95\u4efd\u5c5e\u53d4\u4f2f\u4e4b\u8f88\uff0c\u7987\u82f1\u8c26\u79f0 \u201c\u8001\u5974\u201d\uff0c\u5176\u5b9e\u7236\u4eb2\u4e5f\u8981\u7ed9\u4ed6\u51e0\u5206\u9762\u5b50\uff0c\u4ed6\u4eec\u4e0d\u542c\u547d\u4ee4\uff0c\u8427\u97f5\u5170\u53ef\u4e5f\u65e0\u53ef\u5948\u4f55\u3002 \u3000\u3000\u8bf4\u5bf9\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u7987\u9738\u4e00\u4e2a\u7bad\u6b65\u8df3\u4e0a\uff0c\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4e3e\u8d77\u624b\u6765\u3002\u201d\u4e0a\u5b98\u5929\u91ce\u5927\u6012\uff0c \u201c\u556a\u201d\u7684\u5c31\u662f\u4e00\u638c\u6253\u51fa\uff0c\u559d\u9053\uff1a\u201c\u51ed\u4ec0\u4e48\u8981\u7ed9\u4f60\u641c\uff1f\u201d\u7987\u9738\u9669\u9669\u7ed9\u4ed6\u6253\u4e2d\uff0c\u6012\u9053\uff1a\u201c\u55d3\uff0c\u4f60 \u8fd9\u5c0f\u5b50\u5c45\u7136\u8fd8\u6562\u52a8\u7c97\uff01\u4e14\u5148\u628a\u4f60\u9501\u4f4f\u4e86\u518d\u8bf4\uff01\u201d\u7987\u82f1\u7987\u9738\u540c\u65f6\u52a8\u624b\uff0c\u54d7\u5566\u5566\u4e00\u9635\u54cd\u58f0\uff0c\u6296\u51fa \u4e86\u4e24\u6761\u94c1\u94fe\uff0c\u5411\u7740\u4e0a\u5b98\u5929\u91ce\u7684\u8116\u5b50\u4fbf\u5957\uff0c\u516c\u51b6\u826f\u4e00\u67c4\u957f\u5251\u6321\u4f4f\u4e86\u4ed6\u7684\u80cc\u5fc3\uff0c\u5e38\u5c71\u9f99\u957f\u97ad\u970d\u5730 \u4e00\u626b\uff0c\u5377\u4ed6\u53cc\u811a\u3002\u4e0a\u5b98\u5929\u91ce\u7eb5\u7b97\u6b66\u529f\u518d\u9ad8\uff0c\u4e5f\u96be\u4f4e\u6321\u56db\u4e2a\u9ad8\u624b\u56f4\u653b\uff0c\u773c\u89c1\u4ed6\u5c31\u8981\u88ab\u957f\u5251\u5377 \u5012\uff0c\u7987\u82f1\u7987\u9738\u7684\u9501\u94fe\u5c31\u8981\u5957\u4e0a\u4ed6\u7684\u8116\u5b50\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u4e00\u4e2a\u5973\u5b50\u7684\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u4f4f\u624b\u3002\u201d\u58f0\u97f3\u8fdc\u8fdc\u4f20\u6765\uff0c\u751a\u662f\u67d4\u548c\uff0c\u4f46\u5374\u6709\u4e00\u79cd\u9707\u6151\u4eba \u5fc3\u7684\u3001\u9ad8\u8d35\u5c0a\u4e25\u7684\u6c14\u6d3e\u3002\u7987\u82f1\u84e6\u5730\u4e00\u60ca\uff0c\u5176\u4ed6\u4e09\u4e2a\u4eba\u4e5f\u5446\u4e86\u4e00\u5446\uff0c\u4f46\u5168\u90e8\u8fd9\u6837\u60f3\u9053\uff1a\u201c\u7edd\u4e0d \u53ef\u80fd\u662f\u5979\uff01\u600e\u4e48\u4f1a\u662f\u5979\u5440\uff1f\u201d\u5446\u4e86\u4e00\u5446\uff0c\u5ff5\u5934\u8fd8\u6ca1\u6709\u8f6c\u5f97\u8fc7\u6765\uff0c\u957f\u97ad\u3001\u77ed\u5251\u3001\u94c1\u94fe\u4ecd\u7136\u53d1 \u51fa\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u56db\u4e2a\u4eba\u90fd\u89c9\u5f97\u773c\u524d\u4e00\u4eae\uff0c\u4fe8\u5982\u4e00\u9053\u5f69\u8679\u7a81\u7136\u4ece\u5929\u800c\u964d\uff0c\u98de\u5230\u4e86\u5b83\u4eec\u7684 \u9762\u524d\uff0c\u4f46\u89c1\u4e00\u4e2a\u4e2d\u5e74\u7f8e\u5987\uff0c\u6325\u821e\u7740\u4e00\u6761\u7ea2\u7ef8\uff0c\u77eb\u5982\u6e38\u9f99\uff0c\u7fe9\u82e5\u60ca\u9e3f\uff0c\u53ea\u6ef4\u6e9c\u6e9c\u7684\u8f6c\u4e86\u4e2a\u8eab\uff0c \u957f\u97ad\u3001\u77ed\u5251\u3001\u94c1\u94fe\u5168\u90fd\u88ab\u5979\u5377\u51fa\u4e86\u624b\uff01 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u7684\u8be7\u5f02\u4e0d\u5728\u56db\u4eba\u4e4b\u4e0b\uff0c\u8fd9\u58f0\u97f3\u7adf\u7136\u4e0d\u662f\u8427\u97f5\u5170\u7684\uff1f\u4ed6\u521a\u521a\u62ac\u8d77\u4e86\u5934\uff0c\u53ea\u542c\u5f97\u90a3 \u5987\u4eba\u53c8\u9053\uff1a\u201c\u4ed6\u8bf4\u7684\u4e0d\u9519\uff0c\u90a3\u8fbe\u6469\u5251\u8c31\u786e\u5b9e\u662f\u5728\u4e91\u821e\u9633\u624b\u4e2d\u3002\u201d \u3000\u3000\u8fd9\u5239\u90a3\u95f4\u90a3\u56db\u4e2a\u7eff\u6797\u5927\u76d7\u5168\u90fd\u50cf\u6ce5\u5851\u6728\u96d5\u4e00\u6837\uff0c\u52a8\u4e5f\u4e0d\u6562\u4e00\u52a8\uff0c\u597d\u50cf\u89c1\u5230\u4e86\u4ec0\u4e48\u602a\u5f02\u7684 \u7269\u4e8b\uff0c\u8138\u8272\u5982\u571f\uff0c\u9a87\u5f02\u4e4b\u6781\uff0c\u53c8\u4f3c\u5974\u624d\u89c1\u4e86\u4e3b\u5b50\u4e00\u6837\uff0c\u60ca\u5f02\u4e4b\u4e2d\u5e26\u7740\u714c\u6050\u3002\u4f46\u8fd9\u90fd\u662f\u5239\u90a3\u95f4 \u4e4b\u4e8b\uff0c\u7987\u82f1\u60ca\u9b42\u7a0d\u5b9a\uff0c\u201c\u963f\u5440\u201d\u4e00\u58f0\uff0c\u53eb\u4e86\u51fa\u6765\uff0c\u7531\u4ed6\u9886\u5934\uff0c\u56db\u4e2a\u7eff\u6797\u5927\u76d7\u90fd\u5411\u5987\u4eba\u8dea\u5012\uff0c \u4e0d\u7ea6\u800c\u540c\u7684\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u5927\u5c0f\u59d0\uff0c\u662f\u4f60\uff0c\u662f\u4f60\u5440\u2014\u2014\u8fd9\uff0c\u8fd9\u2026\u2026\u201d \u3000\u3000\u90a3\u7f8e\u5987\u4eba\u628a\u624b\u4e00\u6325\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u6211\uff01\u4f60\u4eec\u4e0d\u4fe1\u6211\u7684\u8bdd\u5417\uff1f\u201d\u7987\u82f1\u9053\uff1a\u201c\u8fd9\uff0c\u8fd9\u662f\u600e \u4e48\u56de\u4e8b\uff1f\u4e91\uff0c\u4e91\u2026\u2026\u201d\u90a3\u7f8e\u5987\u4eba\u9053\uff1a\u201c\u4e0d\u8bb8\u518d\u63d0\u8fd9\u4e2a\u540d\u5b57\uff0c\u4e5f\u4e0d\u8bb8\u5bf9\u4efb\u4f55\u4eba\u8bf4\u4f60\u4eec\u66fe\u89c1\u4e86 \u6211\uff01\u201d\u7987\u82f1\u7987\u9738\u7b49\u9762\u9762\u76f8\u89d1\uff0c\u8c01\u90fd\u4e0d\u6562\u518d\u8bf4\u534a\u53e5\u3002 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u8fde\u65e5\u6765\u78b0\u5230\u8bb8\u591a\u602a\u4e8b\uff0c\u5c24\u4ee5\u4eca\u65e5\u4e4b\u4e8b\uff0c\u6700\u4e3a\u5947\u602a\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u5987\u4eba\u662f\u8c01\uff1f \u600e\u7684\u8fd9\u56db\u4e2a\u7eff\u6797\u5927\u76d7\u5bf9\u5979\u5982\u6b64\u5c0a\u656c\u754f\u60e7\uff0c\u5e76\u8fdc\u8fdc\u5728\u5bf9\u97f5\u5170\u59d0\u59d0\u4e4b\u4e0a\uff1f\u4e3a\u4ec0\u4e48\u90fd\u79f0\u547c\u5979\u505a\u5927\u5c0f \u59d0\uff1f\u542c\u8fd9\u79f0\u547c\uff0c\u8fd9\u56db\u4e2a\u5927\u76d7\u4f3c\u662f\u81ea\u5c45\u5974\u4ec6\uff0c\u8fd9\u771f\u5947\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u5fc3\u4e2d\u7684\u7591\u95ee\uff0c\u8427\u97f5\u5170\u5374\u5148\u95ee\u4e86\u51fa\u6765\uff0c\u5979\u6bd4\u4e0a\u5b98\u5929\u91ce\u8fd8\u8981\u60ca\u8be7\uff0c\u5979\u60f3\u6765\u60f3\u53bb\uff0c\u4ece \u6765\u6ca1\u6709\u89c1\u8fc7\u8fd9\u4e2a\u5973\u4eba\uff0c\u4e3a\u4ec0\u4e48\u7236\u4eb2\u7684\u624b\u4e0b\u4f1a\u8fd9\u6837\u7684\u542c\u5979\u8bf4\u8bdd\uff0c\u8427\u97f5\u5170\u8e0f\u4e0a\u4e00\u6b65\uff0c\u95ee\u9053\uff1a\u201c\u8bf7 \u95ee\u5927\u5a18\u8d35\u59d3\uff0c\u548c\u5bb6\u7236\u4ec0\u4e48\u79f0\u547c\uff1f\u201d \u3000\u3000\u90a3\u5987\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u4e00\u6325\u624b\u53eb\u7987\u82f1\u8fd9\u56db\u4e2a\u4eba\u7ad9\u8d77\u6765\uff0c\u95ee\u9053\uff1a\u201c\u90a3\u4f4d\u5c0f\u59d1\u5a18\u662f\u8427\u51a0\u82f1\u7684\u5973\u513f \u5417\uff1f\u201d\u7987\u82f1\u9053\uff1a\u201c\u4e0d\u9519\u3002\u8427\u8235\u4e3b\u63a5\u4efb\u5e2e\u4e3b\u4e5f\u5df2\u7ecf\u6709\u4e8c\u5341\u5e74\u4e86\u3002\u201d\u90a3\u5987\u4eba\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u5c0f\u59d1 \u5a18\uff0c\u6211\u662f\u8c01\u4eba\uff0c\u4f60\u56de\u53bb\u95ee\u4f60\u7239\u7239\u5c31\u77e5\u9053\u4e86\u3002\u4f60\u4eca\u5929\u521d\u6b21\u89c1\u6211\uff0c\u6211\u6ca1\u6709\u4ec0\u4e48\u4e1c\u897f\u7ed9\u4f60\uff0c\u53ea\u80fd\u9001 \u7ed9\u4f60\u51e0\u53e5\u8bdd\uff1a\u4eba\u6709\u60b2\u6b22\u79bb\u5408\uff0c\u6708\u6709\u9634\u6674\u5706\u7f3a\uff0c\u4e16\u4e0a\u672c\u6765\u6ca1\u6709\u5b8c\u7f8e\u65e0\u6687\u7684\u4e1c\u897f\u3002\u6240\u4ee5\u4f60\u82e5\u6709\u4ec0 \u4e48\u4e0d\u5982\u610f\u7684\u4e8b\u60c5\uff0c\u4e5f\u4e0d\u8981\u8001\u662f\u6302\u5728\u5fc3\u4e0a\u3002\u201d \u3000\u3000\u8427\u97f5\u5170\u6014\u4e86\u4e00\u6014\uff0c\u4f46\u89c1\u8fd9\u5987\u4eba\u7684\u773c\u5149\u4ece\u4ed6\u7684\u8138\u4e0a\u63a0\u8fc7\uff0c\u4f3c\u4e4e\u65e9\u5df2\u662f\u4ece\u4ed6\u7d27\u8e69\u7684\u7709\u5c16\uff0c\u770b \u51fa\u4e86\u5979\u7684\u5fc3\u4e8b\u3002\u773c\u5149\u4e2d\u770b\u51fa\u65e0\u9650\u540c\u60c5\uff0c\u8427\u97f5\u5170\u7684\u4f24\u5fc3\u5931\u610f\uff0c\u5df2\u5230\u6781\u70b9\uff0c\u5374\u7ed9\u8fd9\u5987\u4eba\u7684\u51e0\u53e5\u8bdd \u89e3\u5f00\u4e86\u8bb8\u591a\u3002 \u3000\u3000\u8fd8\u6709\u4e00\u4e2a\u4eba\u6697\u6697\u8be7\u5f02\u7684\uff0c\u90a3\u662f\u4e91\u7d20\u7d20\u3002\u5979\u53ea\u77a7\u4e86\u90a3\u5973\u4eba\u4e00\u773c\uff0c\u4fbf\u611f\u5230\u98a4\u7c9f\u4e0d\u5b89\u3002\u201c\u5440\uff0c \u771f\u50cf\u6bcd\u4eb2\uff01\u201d\u4e0d\u662f\u5f62\u8c8c\u4e0a\u50cf\uff0c\u800c\u662f\u90a3\u80a1\u795e\u6c14\u50cf\u6781\u4e86\uff0c\u5fe7\u90c1\u7684\u8138\u8272\uff0c\u5927\u5bb6\u95fa\u79c0\u7684\u4e30\u5ea6\uff0c\u6c89\u9759\u800c \u53c8\u4f3c\u8574\u85cf\u7740\u65e0\u9650\u5fc3\u4e8b\u7684\u773c\u5149\uff0c\u2026\u2026\u4e91\u7d20\u7d20\u7981\u4e0d\u4f4f\u60f3\u8d77\u4e86\u81ea\u5df1\u7684\u6bcd\u4eb2\uff0c\u60f3\u8d77\u4e86\u77f3\u5929\u94ce\u4e4b\u6b7b\u7684\u90a3 \u4e2a\u53ef\u6015\u7684\u665a\u4e0a\u3002\u6bcd\u4eb2\u5230\u54ea\u91cc\u53bb\u4e86\u5462\uff1f\u201d\u53ef\u601c\u7684\u7d20\u7d20\uff0c\u5979\u5374\u4e5f\u4e0d\u77e5\u9053\uff0c\u6bcd\u4eb2\u66fe\u7ecf\u56de\u6765\uff0c\u518d\u6b21\u7684 \u548c\u5979\u7684\u7236\u4eb2\u8bc0\u522b\u3002 \u3000\u3000\u4e91\u7d20\u7d20\u7684\u6c89\u601d\u7a81\u7136\u7ed9\u90a3\u5973\u4eba\u8bf4\u8bdd\u7684\u58f0\u97f3\u6253\u65ad\u4e86\u3002\u5979\u8bf4\u51fa\u4e86\u4e00\u4e2a\u4ee4\u4e91\u7d20\u7d20\u5fc3\u7075\u6fc0\u52a8\u7684\u540d\u5b57 \u2014\u2014\u201c\u9648\u7384\u673a\u201d\u3002\u90a3\u662f\u5979\u5411\u4e0a\u5b98\u5929\u91ce\u8be2\u95ee\u65f6\u8bf4\u51fa\u6765\u7684\u3002 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u4e5f\u6b63\u5728\u601d\u7d22\u4e00\u4e2a\u66fe\u7ecf\u542c\u8fc7\u7684\u540d\u5b57\u2014\u2014\u201c\u8427\u51a0\u82f1\u201d\u84e6\u5730\u8bb0\u4e86\u8d77\u6765\uff0c\u4ed6\u5e08\u7236\u725f\u4e00\u7c9f \u6709\u4e00\u5929\u66fe\u548c\u4ed6\u63d0\u8d77\u8fc7\u8fd9\u4e2a\u540d\u5b57\u3002\u8fd9\u4e2a\u4eba\u4e43\u662f\u5317\u4e94\u7701\u7684\u7eff\u6797\u9886\u8896\uff0c\u5f53\u65f6\u4e0a\u5b98\u5929\u91ce\u4e5f\u5e76\u4e0d\u600e\u4e48\u653e \u5728\u5fc3\u4e0a\uff0c\u5374\u60f3\u4e0d\u5230\u7adf\u662f\u8427\u97f5\u5170\u7684\u7236\u4eb2\u3002 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u7684\u6c89\u601d\u4e5f\u662f\u7ed9\u90a3\u4e2d\u5e74\u7f8e\u5987\u7684\u95ee\u8bdd\u7684\u58f0\u97f3\u6253\u65ad\u7684\u3002\u4e0a\u5b98\u5929\u91ce\u62ac\u8d77\u5934\u6765\uff0c\u4f46\u89c1\u4e00\u53cc \u6ee1\u542b\u7740\u671f\u5f85\u7684\u76ee\u5149\u6b63\u5728\u6ce8\u89c6\u7740\u4ed6\uff0c\u90a3\u5973\u4eba\u95ee\u9053\uff1a\u201c\u4f60\u662f\u4e0a\u5b98\u5929\u91ce\u5417\uff1f\u9648\u7384\u673a\u662f\u4e0d\u662f\u4f60\u7684\u597d\u670b \u53cb\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u8ba4\u8bc6\u4ed6\u5417\uff1f\u201d \u3000\u3000\u90a3\u5973\u4eba\u773c\u775b\u4e00\u4eae\uff0c\u7d27\u8ddf\u7740\u95ee\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u9648\u7384\u673a\u5462\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u4ed6\u521a\u521a\u968f\u4e91\u592b\u4eba \u8d70\u4e86\u3002\u201d\u90a3\u5987\u4eba\u9053\uff1a\u201c\u4ec0\u4e48\u4e91\u592b\u4eba\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u4e91\u821e\u9633\u7684\u59bb\u5b50\u5440\uff01\u8fd9\u8d3a\u5170\u5c71\u4e2d\u54ea\u8fd8\u6709 \u7b2c\u4e8c\u4e2a\u4e91\u592b\u4eba\uff1f\u201d\u500f\u7136\u95f4\u4f46\u89c1\u90a3\u5973\u4eba\u7684\u9762\u8272\u4e00\u6c89\uff0c\u7ffb\u4eae\u7684\u773c\u5149\u7a81\u7136\u9634\u6697\u4e86\uff0c\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u4ec0 \u4e48\uff0c\u7384\u673a\uff0c\u4ed6\uff0c\u4ed6\u5230\u4e91\uff0c\u4e91\u5bb6\u53bb\u4e86\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u83ab\u540d\u6240\u4ee5\uff0c\u90a3\u4e2d\u5e74\u5987\u4eba\u5ffd\u5730\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u8f7b\u8f7b\u5ff5\u9053\uff1a\u201c\u52ab\u540e\u5bd2\u6885\u867d\u672a\u6298\uff0c\u66f4\u80fd \u6d88\u53d7\u51e0\u756a\u98ce\uff1f\u201d\u628a\u624b\u4e00\u6325\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u7987\u82f1\u7987\u9738\uff0c\u4f60\u4eec\u8d70\u5427\uff0c\u8bb0\u4f4f\u6211\u7684\u5429\u5490\uff0c\u4eca\u65e5\u4e4b\u4e8b\uff0c \u5207\u4e0d\u53ef\u4e0e\u4eba\u8a00\u8bf4\u3002\u201d\u8f6c\u8eab\u4fbf\u8d70\uff0c\u8863\u8882\u98ce\u98d8\uff0c\u5e76\u4e0d\u89c1\u5979\u8d77\u6b65\u5954\u9a70\uff0c\u5374\u662f\u970e\u773c\u4e4b\u95f4\uff0c\u5c31\u6ca1\u6709\u8e2a \u8ff9\uff01\u4e0a\u5b98\u5929\u91ce\u5fc3\u5934\u4e00\u9707\uff0c\u8fd9\u6b63\u662f\u4f20\u8bf4\u4e2d\u7684\uff0c\u90a3\u4f4d\u4e0e\u81ea\u5df1\u5e08\u7956\u9f50\u540d\u7684\u4e00\u4ee3\u5927\u4fa0\u9648\u5b9a\u65b9\u7684\u201c\u8f7b\u5f62 \u6362\u5f71\u201d\u7684\u8f7b\u529f\u3002 \u3000\u3000\u4e91\u7d20\u7d20\u4f0f\u5728\u90a3\u5927\u6811\u4e4b\u4e0a\uff0c\u76ee\u9001\u90a3\u5973\u4eba\u80cc\u5f71\u6d88\u5931\u5728\u66ae\u972d\u4e4b\u4e2d\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5ffd\u7136\u8d77\u4e86\u4e00\u4e2a\u5947 \u5f02\u7684\u611f\u89c9\uff0c\u8fd9\u5973\u4eba\u5982\u6b64\u5173\u5fc3\u7384\u673a\uff0c\u83ab\u975e\u5979\u5c31\u662f\u9648\u7384\u673a\u7684\u6bcd\u4eb2\uff1f\u4e91\u7d20\u7d20\u65f6\u5e38\u542c\u9648\u7384\u673a\u63d0\u8d77\u5979\u7684 \u6bcd\u4eb2\uff0c\u5fc3\u4e2d\u65e9\u5df2\u6709\u4e86\u5f62\u8c61\uff0c\u8fd9\u65f6\u8d8a\u60f3\u8d8a\u89c9\u76f8\u4f3c\uff0c\u5fc3\u4e2d\u5145\u6ee1\u559c\u60a6\uff0c\u4f46\u5979\u4e3a\u4ec0\u4e48\u53c8\u4e0d\u8bb8\u7987\u82f1\u63d0\u8d77 \u5979\u7236\u4eb2\u7684\u540d\u5b57\uff0c\u5440\uff0c\u770b\u5979\u7684\u795e\u8272\uff0c\u542c\u5979\u7684\u8bed\u6c14\uff0c\u8fd8\u597d\u50cf\u5f88\u618e\u6068\u5979\u4eec\u4e91\u5bb6\u3002 \u3000\u3000\u4f46\u542c\u5f97\u8427\u97f5\u5170\u5583\u5583\u8bf4\u9053\uff1a\u201c\u5979\u662f\u8c01\uff1f\u201d\u7987\u82f1\u9053\uff1a\u201c\u8427\u59d1\u5a18\uff0c\u4f60\u56de\u5230\u5bb6\u4e2d\uff0c\u81ea\u7136\u4e00\u5207\u660e \u767d\u3002\u201d\u8427\u97f5\u5170\u770b\u4e86\u4e0a\u5b98\u5929\u91ce\u4e00\u773c\uff0c\u6bc5\u7136\u5bf9\u7987\u82f1\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u5c31\u548c\u4f60\u4eec\u56de\u53bb\uff0c\u7ee7\u627f\u6211\u7236\u4eb2\u90a3 \u5200\u5934\u8210\u8840\u7684\u751f\u6daf\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u5927\u53eb\u9053\uff1a\u201c\u97f5\u5170\u59d0\u59d0\uff0c\u4f60\u7b49\u7b49\u6211\uff0c\u8981\u505a\u5f3a\u76d7\uff0c\u54b1\u4eec\u4e00\u540c\u505a\u53bb\u3002\u201d\u5e38\u5c71\u9f99\u51b7\u7b11 \u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u7f57\u5506\u4ec0\u4e48\uff1f\u505a\u5f3a\u76d7\u4f60\u4e5f\u8fd8\u672a\u591f\u683c\u5462\uff01\u201d\u957f\u97ad\u4e00\u5c55\uff0c\u62e6\u4f4f\u4e86\u4e0a\u5b98\u5929\u91ce\u3002\u8427\u97f5\u5170\u7fd8 \u9996\u957f\u5929\uff0c\u51c4\u7136\u7b11\u9053\uff1a\u201c\u5404\u4eba\u81ea\u6709\u5404\u4eba\u7684\u7f18\u4efd\uff0c\u96be\u4ee5\u5f3a\u6c42\u3002\u4eba\u6709\u60b2\u6b22\u79bb\u5408\uff0c\u6708\u6709\u9634\u6674\u5706\u7f3a\u3002 \u55ef\uff0c\u90a3\u4f4d\u5927\u5a18\u7684\u8bdd\u662f\u8bf4\u5f97\u4e0d\u9519\u3002\u4e0a\u5b98\u5929\u91ce\uff0c\u4f60\u7559\u4e0b\u6765\u5427\uff0c\u7b49\u4e0b\u4f60\u89c1\u5230\u9648\u7384\u673a\u4f60\u5411\u6211\u66ff\u4ed6\u795d \u8d3a\uff0c\u795d\u8d3a\u4ed6\u4e0e\u90a3\u4f4d\u4e91\u5c0f\u59d0\u767d\u5934\u5230\u8001\uff0c\u65e0\u8651\u65e0\u5fe7\uff0c\u4e00\u751f\u4e5f\u4e0d\u8981\u6709\u4ec0\u4e48\u4f24\u5fc3\u4e4b\u4e8b\u3002\u201d \u3000\u3000\u8427\u97f5\u5170\u8fd9\u51e0\u53e5\u8bdd\u81ea\u662f\u6709\u611f\u800c\u53d1\uff0c\u4e91\u7d20\u7d20\u4e0e\u4e0a\u5b98\u5929\u91ce\u90fd\u542c\u5f97\u51fa\u6765\uff0c\u5979\u8fd8\u662f\u6df1\u6df1\u7684\u7231\u7740\u9648\u7384 \u673a\u3002\u4e0a\u5b98\u5929\u91ce\u5446\u82e5\u6728\u9e21\uff0c\u4e0d\u65ad\u7684\u5480\u56bc\u90a3\u4e24\u53e5\u8bdd\uff1a\u201c\u5404\u4eba\u81ea\u6709\u5404\u4eba\u7684\u7f18\u4efd\uff0c\u4e0d\u53ef\u5f3a\u6c42\u3002\u201d\u8fd9\u53e5 \u8bdd\u7adf\u4f3c\u4e3a\u4ed6\u800c\u53d1\u3002\u4e00\u62ac\u5934\uff0c\u53ea\u89c1\u6708\u4e0a\u6811\u68a2\uff0c\u8427\u97f5\u5170\u53bb\u5f97\u8fdc\u4e86\u3002 \u3000\u3000\u6708\u5149\u5e26\u7740\u5bd2\u610f\uff0c\u4e0a\u5b98\u5929\u91ce\u611f\u5230\u4e00\u79cd\u524d\u6240\u672a\u6709\u7684\u5bc2\u5bde\uff0c\u4f46\u89c9\u832b\u832b\u4eba\u6d77\uff0c\u7adf\u7136\u96be\u89c5\u4e00\u4e2a\u77e5 \u5df1\uff0c\u8427\u97f5\u5170\u4e5f\u66fe\u5411\u8fc7\u4ed6\u95ee\u6696\u5618\u5bd2\uff0c\u4f46\u8427\u97f5\u5170\u7684\u5fc3\u5e76\u4e0d\u5411\u7740\u4ed6\uff0c\u4e0d\u9519\uff0c\u9648\u7384\u673a\u4e5f\u66fe\u4e3a\u8fc7\u4ed6\u820d\u751f \u62fc\u6b7b\uff0c\u5bf9\u4ed6\u6709\u5982\u5f1f\u5144\uff0c\u4f46\u9648\u7384\u673a\u5374\u53c8\u4f55\u5c1d\u61c2\u5f97\u4ed6\u82e6\u604b\u7684\u5fc3\u60c5\uff1f\u4e0a\u5b98\u5929\u91ce\u60a0\u7136\u6c89\u601d\uff0c\u832b\u7136\u82e5 \u68a6\uff0c\u4ed6\u4ece\u6765\u6ca1\u6709\u8fd9\u4e48\u6df1\u6c89\u7684\u601d\u60f3\u8fc7\uff0c\u4ed6\u4ece\u6765\u6ca1\u6709\u50cf\u4eca\u665a\u7684\u90a3\u4e48\u611f\u5230\u5b64\u5355\uff0c\u7ec6\u7ec6\u60f3\u6765\uff0c\u4f46\u89c9\u5929 \u5730\u4e4b\u5927\uff0c\u4f3c\u4e4e\u53ea\u6709\u90a3\u4e2a\u65b0\u62dc\u7684\u6b8b\u5e9f\u5e08\u7236\uff0c\u624d\u662f\u81ea\u5df1\u7684\u77e5\u5df1\u3002 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u5583\u5583\u8bf4\u9053\uff1a\u201c\u5e08\u7236\uff0c\u5e08\u7236\uff0c\u4f60\u4f55\u5fc5\u8fd8\u4e3a\u6211\u53bb\u593a\u4ec0\u4e48\u5b9d\u5251\uff0c\u6c42\u4ec0\u4e48\u5251\u8c31\uff1f\u54b1\u4eec\u8fd8 \u662f\u5feb\u5feb\u79bb\u5f00\u4e86\u8fd9\u4f24\u5fc3\u4e4b\u5730\u5427\u3002\u201d\u5ffd\u542c\u5f97\u4e00\u4e2a\u82cd\u8001\u7684\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u5929\u91ce\uff0c\u5440\uff0c\u4f60\u5728\u8fd9\u513f\uff0c\u4f60\u8bf4 \u4ec0\u4e48\uff1f\u201d\u62ac\u5934\u4e00\u770b\uff0c\u4f46\u89c1\u81ea\u5df1\u90a3\u4e94\u4f4d\u5e08\u6015\u5e08\u53d4\uff0c\u8054\u8882\u8d70\u5165\u6797\u4e2d\u3002 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u5446\u5446\u53d1\u6123\uff0c\u667a\u5706\u9053\uff1a\u201c\u4f60\u662f\u600e\u4e48\u9003\u8131\u4e91\u821e\u9633\u8fd9\u8001\u8d3c\u7684\u9b54\u638c\u7684\uff1f\u55ef\uff0c\u4f60\u53d7\u4e86\u4f24 \u4e48\uff1f\u201d\u667a\u5706\u957f\u8001\u89c1\u4ed6\u4f3c\u662f\u795e\u667a\u660f\u8ff7\uff0c\u8fd8\u4ee5\u4e3a\u4ed6\u5df2\u88ab\u4e91\u821e\u9633\u7528\u5185\u5bb6\u624b\u6cd5\uff0c\u5206\u7b4b\u9519\u8109\uff0c\u6270\u4e71\u7075 \u53f0\u3002 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u9000\u4e86\u4e00\u6b65\uff0c\u9053\uff1a\u201c\u4ece\u4eca\u4ee5\u540e\uff0c\u6211\u518d\u4e5f\u4e0d\u7406\u90a3\u8fbe\u6469\u5251\u8c31\u7684\u4e8b\u60c5\u4e86\u3002\u4f60\u4eec\u8981\uff0c\u81ea\u5df1 \u95ee\u4e91\u821e\u9633\u8981\u53bb\u3002\u201d\u667a\u5706\u957f\u8001\u6014\u4e86\u4e00\u6014\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u667a\u5f18\u957f\u8001\u9053\uff1a\u201c\u4f60\u7ed9\u4ed6\u628a\u628a\u8109 \u770b\uff0c\u6562\u60c5\u4ed6\u771f\u662f\u53d7\u4f24\u4e86\u3002\u201d\u4e0a\u5b98\u5929\u91ce\u628a\u624b\u4e00\u6454\uff0c\u9053\uff1a\u201c\u8c01\u8bf4\u6211\u53d7\u4e86\u4f24\u3002\u201d\u667a\u5f18\u9053\uff1a\u201c\u597d\uff0c\u90a3 \u4e48\u5251\u8c31\u4e4b\u4e8b\uff0c\u4ee5\u540e\u518d\u63d0\uff0c\u54b1\u4eec\u56de\u6b66\u5f53\u5c71\u53bb\u5427\u3002\u201d\u5176\u4f59\u56db\u8001\uff0c\u8fde\u8fde\u70b9\u9996\uff0c\u5fc3\u4e2d\u90fd\u662f\u8fd9\u6837\u7684\u60f3\uff0c \u4e91\u821e\u9633\u4e0d\u8bb2\u9053\u4e49\uff0c\u6253\u53c8\u6253\u4e0d\u8d62\u4ed6\uff0c\u4e0d\u5982\u5148\u56de\u8f6c\u6b66\u5f53\u5c71\uff0c\u628a\u638c\u95e8\u7684\u4f4d\u5b50\u5b9a\u4e86\uff0c\u7136\u540e\u9080\u96c6\u5404\u6b66\u6797 \u714e\u8f88\uff0c\u518d\u5411\u4e91\u821e\u9633\u5174\u95ee\u7f6a\u4e4b\u5e08\u3002 \u3000\u3000\u54ea\u6599\u4e0a\u5b98\u5929\u91ce\u628a\u5934\u4e00\u62ac\uff0c\u5ffd\u5730\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u56de\u6b66\u5f53\u5c71\uff0c\u6211\u4e5f\u4e0d\u518d\u662f\u6b66\u5f53\u6d3e\u7684\u638c\u95e8\u4eba \u4e86\u3002\u201d \u3000\u3000\u54ea\u6599\u4e0a\u5b98\u5929\u91ce\u628a\u5934\u4e00\u62ac\uff0c\u5ffd\u5730\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u56de\u6b66\u5f53\u5c71\uff0c\u6211\u4e5f\u4e0d\u518d\u662f\u6b66\u5f53\u6d3e\u7684\u638c\u95e8\u4eba \u4e86\u3002\u201d \u3000\u3000------------- \u3000\u3000\u91cd\u5e86\u96ea\u513f \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u8fd8\u5251\u5947\u60c5\u5f55\u300b\u2014\u2014\u7b2c\u5341\u4e8c\u56de\u3000\u4f0f\u864e\u964d\u9f99 \u6881\u7fbd\u751f\u300a\u8fd8\u5251\u5947\u60c5\u5f55\u300b \u7b2c\u5341\u4e8c\u56de\u3000\u4f0f\u864e\u964d\u9f99 \u3000\u3000\u667a\u5706\u957f\u8001\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u5389\u58f0\u65a5\u9053\uff1a\u201c\u4e0a\u5b98\u5929\u91ce\uff0c\u4f60\u75af\u4e86\u5417\uff1f\u653e\u7740\u4e00\u4e2a\u597d\u597d\u7684\u638c\u95e8\u4e3a \u4ec0\u4e48\u4e0d\u505a\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u6211\u4e0d\u505a\u81ea\u7136\u6709\u4eba\u4f1a\u505a\u3002\u201d\u667a\u5706\u957f\u8001\u53cc\u773c\u4e00\u7ffb\uff0c\u7741\u5927\u773c\u775b\u559d\u9053\uff1a \u201c\u4ec0\u4e48\u4eba\u8981\u505a\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u5e08\u4f2f\uff0c\u4f60\u95e8\u4e0b\u7684\u51e0\u4f4d\u5e08\u5144\u5c31\u90fd\u6bd4\u6211\u5f3a\u5f97\u591a\u3002\u201d \u3000\u3000\u667a\u5706\u957f\u8001\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u5411\u4f60\u6311\u62e8\u662f\u975e\u6765\u4e86\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u8fd9\u662f\u6211\u81ea \u7518\u9000\u8ba9\uff0c\u7701\u5f97\u5e08\u4f2f\u4f60\u518d\u8d39\u5fc3\u5b89\u6392\u3002\u55ef\uff0c\u90a3\u51e0\u4f4d\u5e08\u5144\u63a5\u5230\u5e08\u4f2f\u7684\u6cd5\u8c15\uff0c\u60f3\u6765\u4e5f\u8be5\u56de\u5230\u6b66\u5f53\u5c71 \u4e86\uff0c\u8fd8\u8981\u6211\u53bb\u51d1\u70ed\u95f9\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u667a\u5706\u957f\u8001\u672c\u6765\u79c1\u5fc3\u81ea\u7528\uff0c\u60f3\u5b89\u6392\u81ea\u5df1\u7684\u5f1f\u5b50\u62a2\u593a\u8fd9\u638c\u95e8\u7684\u4f4d\u7f6e\uff0c\u5ffd\u88ab\u4e0a\u5b98\u5929\u91ce\u76f4\u8a00\u63ed \u7834\uff0c\u4e0d\u89c9\u8001\u7f9e\u6210\u6012\uff0c\u518d\u5389\u58f0\u65a5\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u638c\u95e8\u7684\u5927\u4f4d\u662f\u79c1\u81ea\u6388\u53d7\u7684\u4e48\uff1f\u4f60\u8981\u8ba9\u7ed9\u4f60\u7684 \u5e08\u5144\uff0c\u4e5f\u8be5\u5148\u968f\u6211\u56de\u5c71\uff0c\u518d\u53ec\u96c6\u540c\u95e8\u516c\u51b3\u3002\u201d\u4e0a\u5b98\u5929\u91ce\u51b7\u7b11\u9053\uff1a\u201c\u4f55\u987b\u8fd9\u6837\u8d39\u4e8b\uff0c\u4ece\u4eca\u4e4b \u540e\uff0c\u6211\u5df2\u4e0d\u518d\u662f\u6b66\u5f53\u6d3e\u7684\u4eba\uff0c\u4f60\u4eec\u4e2d\u7684\u4e8b\u60c5\uff0c\u6211\u4e0d\u518d\u8fc7\u95ee\u3002\u201d \u3000\u3000\u667a\u5706\u957f\u8001\u53c8\u60ca\u53c8\u6c14\uff0c\u5927\u6012\u559d\u9053\uff1a\u201c\u4f60\u6562\u6b3a\u5e08\u706d\u7956\uff0c\u53cd\u51fa\u5e08\u95e8\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u6211\u5bf9\u725f \u6069\u5e08\u7684\u8bad\u8bf2\u4e0d\u6562\u5fd8\uff0c\u4f46\u6b66\u6797\u4e4b\u4e2d\uff0c\u5e08\u7236\u6b7b\u540e\uff0c\u6539\u6295\u522b\u4f4d\u540d\u5e08\uff0c\u4e5f\u5e76\u4e0d\u662f\u6ca1\u6709\u5148\u4f8b\uff01\u201d\u667a\u5706\u957f \u8001\u6012\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u6539\u6295\u4e86\u4ec0\u4e48\u660e\u5e08\u4e86\uff1f\u201d\u5176\u4ed6\u56db\u8001\u4e5f\u90fd\u52a8\u4e86\u6012\u6c14\uff0c\u7eb7\u7eb7\u65a5\u9a82\u3002\u201c\u6b66\u5f53\u6d3e\u662f \u6b66\u6797\u6b63\u5b97\uff0c\u4ece\u53e4\u4ee5\u6765\uff0c\u53ea\u542c\u8bf4\u6539\u90aa\u5f52\u6b63\uff0c\u54ea\u6709\u5f03\u6b63\u5f52\u90aa\uff1f\u201d\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u638c\u95e8\u4eba\u5c82\u6709\u6539\u6295\u522b \u6d3e\u4e4b\u7406\uff1f\u201d\u7eb7\u7eb7\u6270\u6270\uff0c\u559d\u9a82\u4e4b\u58f0\u4e71\u6210\u4e00\u7247\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u53ee\u53ee\u7684\u94c1\u6756\u4e4b\u58f0\u7531\u8fdc\u800c\u8fd1\uff0c\u6765\u5f97\u5feb\u6781\uff0c\u6b66\u5f53\u4e94\u8001\u9762\u8272\u5927\u53d8\uff0c\u4e0d\u7ea6\u800c\u540c\uff0c\u559d\u9a82\u4e4b\u58f0 \u5168\u90e8\u6b62\u606f\u3002\u4f46\u542c\u5f97\u6bd5\u51cc\u98ce\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6b66\u5f53\u6d3e\u7684\u4e94\u4f4d\u8001\u5934\u513f\uff0c\u4ffa\u6bd5\u51cc\u98ce\u53ef\u6ca1\u6709\u9a97\u4f60\u4eec\u5427\uff1f\u8d35 \u6d3e\u7684\u638c\u95e8\u4eba\u5fc3\u7518\u60c5\u613f\u62dc\u6211\u4e3a\u5e08\uff0c\u53ef\u4e0d\u662f\u6211\u8981\u62a2\u4f60\u4eec\u7684\uff01\u54c8\uff0c\u54c8\uff0c\u4e0a\u5b98\u5929\u91ce\uff0c\u4f60\u90fd\u8bf4\u6e05\u695a\u4e86 \u4e48\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u8eac\u8eab\u8bf4\u9053\uff1a\u201c\u65e9\u5df2\u8bf4\u6e05\u695a\u4e86\u3002\u201d \u3000\u3000\u6b66\u5f53\u4e94\u8001\u9762\u9762\u76f8\u89d1\uff0c\u667a\u5706\u957f\u8001\u6124\u7136\u8bf4\u9053\uff1a\u201c\u6bd5\u51cc\u98ce\uff0c\u4f60\u597d\uff01\u6b66\u5f53\u6d3e\u53ef\u4e0d\u662f\u7531\u4efb\u4f55\u4eba\u6765\u6b3a \u8d1f\u7684\uff01\u201d\u6bd5\u51cc\u98ce\u54c8\u54c8\u4e00\u7b11\uff0c\u94c1\u62d0\u5212\u4e86\u4e00\u9053\u5706\u5f27\uff0c\u90a3\u5f20\u6ee1\u8138\u5251\u75d5\u7684\u4e11\u8138\u8d8a\u53d1\u72f0\u72de\u53ef\u6016\uff0c\u51b7\u51b7\u7b11 \u9053\uff1a\u201c\u534e\u67d0\u867d\u53ea\u5269\u4e0b\u534a\u8fb9\u8eab\u5b50\uff0c\u4f55\u5c1d\u60e7\u6015\u8c01\u4e86\uff1f\u597d\u5427\uff0c\u7eb5\u7b97\u4f60\u4eec\u6b66\u5f53\u6d3e\u6cf0\u5c71\u538b\u9876\uff0c\u6211\u6bd5\u67d0\u4e5f \u80fd\u72ec\u81c2\u64ce\u5929\uff01\u201d \u3000\u3000\u6b66\u5f53\u4e94\u8001\u5168\u90fd\u6c14\u5f97\u9762\u8272\u7126\u9ec4\uff0c\u4f46\u4ed6\u4eec\u5728\u6597\u4e91\u821e\u9633\u4e4b\u65f6\uff0c\u90fd\u53d7\u4e86\u4e00\u6307\u795e\u529f\u7684\u95ed\u7a74\u4e4b\u4f24\uff0c\u867d \u7136\u6bcf\u4eba\u670d\u4e0b\u534a\u7c92\u5c0f\u8fd8\u4e39\uff0c\u529f\u529b\u5374\u5c1a\u672a\u6062\u590d\uff0c\u53c8\u66fe\u4eb2\u773c\u89c1\u6bd5\u51cc\u98ce\u90a3\u7b49\u5389\u5bb3\u7684\u638c\u529b\uff0c\u5982\u4f55\u6562\u4e0e\u4ed6 \u786c\u62fc\uff1f\u667a\u5706\u957f\u8001\u6012\u9053\uff1a\u201c\u4eca\u65e5\u7531\u4f60\u5634\u786c\uff0c\u4e09\u6708\u4e4b\u540e\uff0c\u6211\u518d\u9080\u96c6\u6b66\u6797\u540c\u9053\u4e0e\u4f60\u7406\u8bba\u3002\u201d\u6bd5\u51cc\u98ce \u5927\u7b11\u9053\uff1a\u201c\u8c01\u8010\u70e6\u7b49\u4f60\u4e09\u6708\uff0c\u4e09\u5929\u6211\u4e5f\u4e0d\u7b49\uff01\u201d\u667a\u5706\u957f\u8001\u9053\uff1a\u201c\u7b49\u4e0d\u7b49\u90a3\u7531\u4f60\u3002\u6211\u4e0d\u627e\u4f60\u7406 \u8bba\u90a3\u53ef\u5f97\u7531\u6211\u3002\u4efb\u4f60\u8d70\u5230\u5929\u6daf\u6d77\u89d2\uff0c\u6211\u4e5f\u8981\u627e\u4e0a\u4f60\u7684\u95e8\u6765\uff01\u201d \u3000\u3000\u6bd5\u51cc\u98ce\u201c\u5f53\u201d\u7684\u4e00\u58f0\u628a\u94c1\u6756\u63d2\u5165\u5730\u4e2d\uff0c\u76ee\u9001\u6b66\u5f53\u4e94\u8001\u75be\u8d70\u4e0b\u5c71\uff0c\u51b7\u51b7\u7b11\u9053\uff1a\u4e09\u6708\u4e4b\u540e\u8fd8 \u60f3\u627e\u6211\uff1f\u54c8\uff0c\u54c8\uff0c\u90a3\u65f6\u4f60\u4eec\u53bb\u5411\u960e\u7f57\u738b\u8981\u4eba\u53bb\u5427\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u9a87\u9053\uff1a\u201c\u5e08\u7236\uff0c\u6211\u8fd9\u51e0\u4f4d\u5e08\u4f2f\u867d\u7136\u79c1\u5fc3\u81ea\u7528\uff0c\u5374\u975e\u5341\u6076\u4e0d\u8d66\u4e4b\u4eba\uff0c\u82e5\u7136\u4ed6\u4eec\u65e5 \u540e\u5bfb\u4ec7\uff0c\u4e5f\u8bf7\u5e08\u7236\u770b\u5728\u6211\u7684\u4efd\u4e0a\uff0c\u4e0d\u8981\u9083\u4e0b\u6740\u624b\u3002\u201d \u3000\u3000\u6bd5\u51cc\u98ce\u795e\u8272\u6697\u7136\uff0c\u60e8\u7b11\u8bf4\u9053\uff1a\u201c\u8c01\u8bf4\u6211\u8981\u6740\u4ed6\u4eec\u4e86\uff1f\u5440\uff0c\u4f60\u8fd9\u5b69\u5b50\u597d\u4e0d\u61c2\u4e8b\uff0c\u54b1\u4eec\u7f18\u4efd \u5c06\u5c3d\uff0c\u8fd8\u6709\u4ec0\u4e48\u65e5\u540e\u5462\uff1f\u201d\u8fd9\u756a\u8bdd\u53e4\u602a\u4e4b\u6781\uff0c\u4ee4\u5f97\u4e0a\u5b98\u5929\u91ce\u60ca\u7591\u4e0d\u5df2\uff0c\u5fc3\u9053\uff1a\u201c\u90a3\u4e48\u5e08\u7236\u8bf4 \u7684\u5411\u960e\u7f57\u738b\u8981\u4eba\u53c8\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u95ee\u9053\uff1a\u201c\u54b1\u4eec\u5e08\u5f92\u521a\u521a\u9047\u5408\uff0c\u600e\u7684\u5e08\u7236\u4fbf\u8bf4\u7f18\u4efd\u5c06\u5c3d\uff0c\u83ab \u975e\u5f1f\u5b50\u6709\u4ec0\u4e48\u505a\u9519\u4e86\u5417\uff1f\u201d \u3000\u3000\u6bd5\u51cc\u98ce\u6447\u4e86\u6447\u5934\uff0c\u60e8\u7136\u4e00\u7b11\uff0c\u4f46\u89c1\u4ed6\u8111\u95e8\u4e0a\u6ccc\u51fa\u6c57\u73e0\u5934\u9876\u4e0a\u84b8\u53d1\u51fa\u4e00\u5c42\u5c42\u6c24\u6c32\u767d\u6c14\uff0c\u534a \u664c\u8bf4\u9053\uff1a\u201c\u6606\u543e\u5b9d\u5251\u548c\u8fbe\u6469\u5251\u8c31\uff0c\u6211\u90fd\u6ca1\u80fd\u591f\u7ed9\u4f60\u8981\u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u53ea\u9053\u4ed6\u662f\u4e3a\u8fd9\u4e24\u4ef6\u4e8b\u4f24\u5fc3\uff0c\u6025\u5fd9\u8bf4\u9053\uff1a\u201c\u8fd9\u4e9b\u8eab\u5916\u4e4b\u7269\u5f1f\u5b50\u4e5f\u4e0d\u5e0c\u7f55\uff0c\u5e08\u7236\uff0c \u4f60\u4e3a\u6211\u53bb\u6076\u6597\u4e91\u821e\u9633\uff0c\u5f1f\u5b50\u5df2\u662f\u611f\u6069\u4e0d\u5c3d\uff0c\u54b1\u4eec\u8fd8\u662f\u65e9\u65e9\u79bb\u5f00\u8fd9\u662f\u975e\u4e4b\u5730\u5427\u3002\u201d\u4ed6\u54ea\u77e5\u9053\u6bd5 \u51cc\u98ce\u5df2\u88ab\u4e91\u821e\u9633\u7684\u4e00\u6307\u7985\u529f\u4f24\u4e86\u5185\u810f\uff0c\u90a3\u4f24\u52bf\u6bd4\u4e91\u821e\u9633\u6240\u53d7\u7684\u4f24\u8fd8\u8981\u4e25\u91cd\uff0c\u6bd5\u51cc\u98ce\u9002\u624d\u5bf9\u6b66 \u5f53\u4e94\u8001\u5176\u5b9e\u53ea\u662f\u865a\u58f0\u606b\u5413\u800c\u5df2\u3002 \u3000\u3000\u6bd5\u51cc\u98ce\u5374\u6276\u7740\u94c1\u6756\u5750\u4e86\u4e0b\u6765\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4e0d\uff0c\u8fd9\u5251\u8c31\u7684\u6545\u4e8b\uff0c\u4eca\u65e5\u6211\u82e5\u4e0d\u5411\u4f60\u8bf4\uff0c\u4ee5 \u540e\u53ef\u5c31\u6ca1\u6709\u673a\u4f1a\u8bf4\u5566\u3002\u201d\u4e0a\u5b98\u5929\u91ce\u89c1\u6bd5\u51cc\u98ce\u8bf4\u5f97\u6781\u4e3a\u90d1\u91cd\uff0c\u5fc3\u4e2d\u9690\u9690\u611f\u5230\u4e00\u9635\u51f6\u5146\u3002 \u3000\u3000\u6708\u4eae\u6e10\u6e10\u79fb\u8fd1\u5929\u5fc3\uff0c\u6df1\u5c71\u4e2d\u53c8\u4f20\u6765\u4e86\u51e0\u58f0\u864e\u5578\uff0c\u6bd5\u51cc\u98ce\u9053\uff1a\u201c\u8fde\u65e5\u6765\u4f60\u78b0\u5230\u4e0d\u5c11\u5947\u602a\u7684 \u4e8b\u60c5\uff0c\u8fd9\u6837\u7684\u591c\u665a\u4e5f\u786e\u5b9e\u4ee4\u4eba\u6709\u70b9\u5bb3\u6015\uff0c\u602a\u4e0d\u5f97\u4f60\u60f3\u65e9\u65e9\u79bb\u5f00\u6b64\u5c71\u4e86\uff0c\u4e8c\u5341\u591a\u5e74\u524d\uff0c\u6211\u4e5f\u66fe \u7ecf\u5386\u8fc7\u8fd9\u6837\u7684\u4e00\u4e2a\u665a\u4e0a\uff0c\u78b0\u5230\u6bd4\u4eca\u65e5\u66f4\u5947\u602a\u7684\u4e8b\u60c5\u3002 \u3000\u3000\u201c\u90a3\u65f6\u6211\u4e5f\u50cf\u4f60\u4e00\u822c\u5e74\u7eaa\uff0c\u96c4\u5fc3\u52c3\u52c3\uff0c\u60f3\u521b\u51fa\u4e00\u756a\u4e8b\u4e1a\uff0c\u6211\u54e5\u54e5\u6bd5\u51cc\u865a\u5728\u5f20\u58eb\u8bda\u519b\u4e2d\uff0c \u9065\u9886\u5317\u65b9\u4e10\u5e2e\u5e2e\u4e3b\u7684\u540d\u4e49\uff0c\u5e2e\u4e2d\u7684\u4e8b\u60c5\u591a\u7531\u6211\u5954\u8dd1\uff0c\u6211\u751f\u6027\u53c8\u559c\u6b22\u6f2b\u6e38\uff0c\u8db3\u8ff9\u6240\u53ca\uff0c\u904d\u53ca\u5927 \u6c5f\u5357\u5317\uff0c\u76f4\u81f3\u585e\u5916\u8fb9\u8352\u3002 \u3000\u3000\u201c\u6709\u4e00\u65e5\uff0c\u6211\u8ff7\u8def\u5728\u7518\u8083\u7684\u2018\u9ea6\u79ef\u77f3\u2019\u5c71\u4e4b\u4e2d\uff0c\u9ec4\u660f\u65f6\u5206\uff0c\u91ce\u98ce\u9661\u8d77\uff0c\u5ffd\u7136\u542c\u5f97\u811a\u4e0b\u6709 \u90c1\u96f7\u4f3c\u7684\u8f70\u8f70\u4e4b\u58f0\uff0c\u6211\u8fd8\u4ee5\u4e3a\u662f\u5730\u9707\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u5ffd\u7136\u4ece\u5730\u5e95\u91cc\u4f20\u51fa\u6765\u51c4\u5389\u7684\u53eb\u58f0\uff0c\u540c\u65f6\u811a \u4e0b\u7684\u571f\u8d28\u4e5f\u50cf\u6bd4\u5468\u56f4\u7684\u677e\u8f6f\u8bb8\u591a\uff0c\u6211\u8bd5\u7528\u94c1\u62d0\u89e6\u5730\uff0c\u679c\u7136\u88c2\u4e86\u4e00\u4e2a\u6d1e\u53e3\uff0c\u6211\u5c06\u6d1e\u53e3\u7684\u77f3\u5757\u79fb \u5f00\uff0c\u7802\u77f3\u7eb7\u7eb7\u4e0b\u843d\uff0c\u539f\u6765\u662f\u4e00\u4e2a\u4e2d\u7a7a\u7684\u77f3\u7a9f\u3002\u201d \u3000\u3000\u201c\u6211\u5927\u7740\u80c6\u5b50\u7f12\u7ef3\u800c\u4e0b\uff0c\u53ea\u89c1\u91cc\u9762\u9ed1\u5f71\u61a7\u61a7\uff0c\u4e0d\u65f6\u63a0\u8fc7\u5200\u5251\u7684\u95ea\u5149\uff0c\u7adf\u662f\u6709\u4eba\u5728\u91cc\u9762\u53ae \u6740\u3002\u6211\u81ea\u5c0f\u7ec3\u8fc7\u6697\u5668\u7684\u529f\u592b\uff0c\u4f46\u521a\u5728\u660e\u4eae\u4e4b\u5904\u8d70\u5165\u9634\u6697\u7684\u77f3\u7a9f\uff0c\u8fd8\u672a\u770b\u5f97\u5341\u5206\u6e05\u695a\u3002\u51dd\u76ee\u7ec6 \u8fa8\uff0c\u9690\u7ea6\u6709\u4e24\u6761\u5927\u6c49\u6b63\u5728\u5411\u4e00\u4e2a\u8001\u4eba\u56f4\u653b\uff0c\u90a3\u8001\u4eba\u8eba\u5728\u571f\u7095\u4e0a\uff0c\u4f46\u89c1\u90a3\u4e24\u4eba\u5200\u5251\u6765\u5f80\uff0c\u5411\u7095 \u4e0a\u4e71\u780d\uff0c\u90a3\u8001\u4eba\u5374\u4e0d\u53d1\u4e00\u58f0\uff0c\u5012\u662f\u90a3\u4e24\u4e2a\u4eba\u5374\u4e0d\u65f6\u53d1\u51fa\u51c4\u5389\u7684\u53eb\u558a\uff01\u60c5\u5f62\u771f\u662f\u5947\u602a\u6781\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u90a3\u65f6\u5c11\u4e0d\u66f4\u4e8b\uff0c\u4e00\u89c1\u662f\u4e24\u6761\u5927\u6c49\u56f4\u653b\u4e00\u4e2a\u75c5\u5728\u7095\u4e0a\u8d77\u4e0d\u5f97\u8eab\u7684\u8001\u4eba\uff0c\u4fbf\u52a8\u4e86\u62b1\u6253\u4e0d \u5e73\u4e4b\u5fc3\uff0c\u7acb\u5373\u6454\u8d77\u94c1\u62d0\uff0c\u88ad\u51fb\u90a3\u4e24\u6761\u5927\u6c49\uff0c\u5ffd\u542c\u5f97\u90a3\u8001\u4eba\u53eb\u9053\uff1a\u2018\u5c11\u5e74\u4eba\u8d70\u8fdc\u4e00\u4e9b\uff0c\u5f53\u5fc3\u8fde \u4f60\u4e5f\u7eca\u5012\u4e86\u3002\u2019\u4ed6\u5185\u529b\u5145\u6c9b\uff0c\u58f0\u97f3\u4e00\u53d1\uff0c\u9707\u5f97\u56db\u9762\u77f3\u58c1\u90fd\u55e1\u55e1\u4f5c\u54cd\uff0c\u6211\u6014\u4e86\u4e00\u6014\uff0c\u4e0d\u81ea\u89c9\u7684 \u9000\u4e86\u51e0\u6b65\u3002\u8fd9\u65f6\u773c\u775b\u5df2\u6e10\u6e10\u4e60\u60ef\u9ed1\u6697\uff0c\u51dd\u795e\u7ec6\u770b\uff0c\u4f46\u89c1\u90a3\u8001\u4eba\u624b\u6267\u957f\u85e4\uff0c\u53ea\u51ed\u5355\u638c\u5e94\u654c\uff0c\u638c \u5288\u6307\u6233\uff0c\u795e\u5999\u975e\u5e38\uff0c\u90a3\u4e24\u6761\u5927\u6c49\u5c31\u50cf\u8001\u9f20\u88ab\u732b\u620f\u5f04\u4e00\u6837\uff0c\u72fc\u72c8\u4e4b\u6781\uff0c\u597d\u51e0\u6b21\u60f3\u8981\u9003\u8d70\uff0c\u5374\u53c8 \u88ab\u90a3\u957f\u85e4\u62e6\u4f4f\u3002\u201d \u3000\u3000\u201c\u6211\u8fd9\u624d\u770b\u51fa\u90a3\u8001\u4eba\u662f\u8eab\u6000\u7edd\u6280\u7684\u5f02\u4eba\uff0c\u5bf9\u90a3\u4e24\u4e2a\u6c49\u5b50\u4e4b\u88ab\u620f\u5f04\u53c8\u5927\u4e3a\u4e0d\u5fcd\uff0c\u4ee3\u4ed6\u4eec\u6c42 \u60c5\u9053\uff1a\u2018\u4ed6\u4eec\u65e2\u7136\u4f24\u5bb3\u4e0d\u4e86\u4f60\u8001\u4eba\u5bb6\uff0c\u4f60\u5c31\u6253\u53d1\u4ed6\u4eec\u8d70\u4e86\u5427\u3002\u2019\u90a3\u8001\u8005\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u2018\u4e5f \u597d\uff0c\u770b\u5728\u8fd9\u5c0f\u54e5\u7684\u4efd\u4e0a\uff0c\u9976\u4f60\u4eec\u5c11\u53d7\u70b9\u7f6a\u3002\u2019\u6325\u638c\u62cd\u51fa\uff0c\u50fb\u556a\u4e24\u58f0\uff0c\u628a\u90a3\u4e24\u6761\u5927\u6c49\u6253\u6b7b\u4e86\u3002 \u62db\u62db\u624b\u9053\uff1a\u2018\u4f60\u8fc7\u6765\u3002\u2019\u201d \u3000\u3000\u201c\u53ea\u542c\u5f97\u90a3\u8001\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u2018\u4f60\u66ff\u8fd9\u4e24\u4eba\u6c42\u60c5\uff0c\u4f60\u77e5\u9053\u4ed6\u4eec\u662f\u8c01\uff1f\u2019\u6211\u8bf4\u4e0d\u77e5\u9053\u3002\u90a3\u8001 \u4eba\u53c8\u95ee\u9053\uff1a\u2018\u4f60\u662f\u4e0d\u662f\u8981\u8fbe\u6469\u5251\u8c31\u7684\uff1f\u2019\u6211\u8bf4\u6211\u6839\u672c\u5c31\u672a\u542c\u8fc7\u4e16\u4e0a\u6709\u8fd9\u4e2a\u5251\u8c31\uff0c\u90a3\u8001\u4eba\u795e\u8272 \u7a0d\u7a0d\u597d\u8f6c\uff0c\u8bf4\u9053\uff1a\u2018\u8981\u4e0d\u662f\u6211\uff0c\u9002\u624d\u89c1\u4f60\u4e00\u7247\u597d\u5fc3\uff0c\u4f60\u4eca\u65e5\u4e5f\u4f11\u60f3\u51fa\u6b64\u6d1e\u4e86\u3002\u4f60\u770b\uff0c\u4e8c\u5341\u591a \u5e74\u6765\uff0c\u66fe\u7ecf\u5165\u8fc7\u8fd9\u4e2a\u77f3\u6d1e\u7684\u4eba\uff0c\u90fd\u5728\u8fd9\u91cc\u4e86\u3002\u2019\u6211\u987a\u7740\u4ed6\u6240\u6307\u7684\u65b9\u5411\uff0c\u4f46\u89c1\u77f3\u5899\u5e95\u4e0b\uff0c\u6392\u7740 \u4e00\u5217\u7684\u9ab7\u9ac5\u767d\u9aa8\u3002\u201d \u3000\u3000\u201c\u90a3\u8001\u8005\u957f\u957f\u7684\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u2018\u4e0d\u662f\u6211\u5fc3\u72e0\u624b\u8fa3\uff0c\u6211\u82e5\u653e\u4ed6\u4eec\u51fa\u53bb\uff0c\u6c5f\u6e56\u4e0a\u66f4\u4f1a\u6380 \u8d77\u6ed4\u5929\u7684\u98ce\u6d6a\u3002\u4eba\u4e3a\u8d22\u6b7b\uff0c\u9e1f\u4e3a\u98df\u4ea1\uff0c\u5b66\u6b66\u7684\u4eba\uff0c\u5219\u4e3a\u5947\u4e66\u5b9d\u5251\u4e27\u751f\uff0c\u8fd9\u90fd\u662f\u4e3a\u4e86\u4e00\u4e2a\u8d2a \u5b57\uff0c\u4e0d\u8fc7\uff0c\u4f60\u4eca\u65e5\u65e2\u662f\u65e0\u5fc3\u8fdb\u6d1e\uff0c\u6211\u4e5f\u5c31\u7b2c\u4e00\u6b21\u7834\u4f8b\uff0c\u8ba9\u4f60\u51fa\u53bb\u3002\u55ef\uff0c\u5c11\u5e74\u4eba\uff0c\u4f60\u53eb\u4ec0\u4e48\u540d \u5b57\uff1f\u2019\u201d \u3000\u3000\u201c\u6211\u4f9d\u5b9e\u8bf4\u4e86\u3002\u90a3\u8001\u4eba\u53cc\u773c\u4e00\u5f20\uff0c\u95ee\u9053\uff1a\u2018\u6bd5\u6e05\u6cc9\u662f\u4f60\u4ec0\u4e48\u4eba\uff1f\u2019\u6211\u8bf4\uff1a\u2018\u6b63\u662f\u5bb6 \u7236\u3002\u2019\u90a3\u8001\u4eba\u518d\u95ee\uff1a\u2018\u51cc\u865a\u5462\uff1f\u2019\u6211\u8bf4\uff1a\u2018\u4e43\u662f\u5bb6\u5144\u3002\u2019\u90a3\u8001\u8005\u54c8\u54c8\u7b11\u9053\uff1a\u2018\u5982\u6b64\u8bf4\u6765\uff0c\u5012 \u4e0d\u662f\u5916\u4eba\u4e86\u3002\u4f60\u7236\u5144\u53ef\u6709\u8bf4\u8fc7\u6211\u7684\u540d\u5b57\u5417\uff1f\u6211\u53eb\u505a\u6fb9\u53f0\u4e00\u7fbd\u3002\u2019\u201d \u3000\u3000\u201c\u6211\u5927\u5403\u4e00\u60ca\uff0c\u8fd9\u6fb9\u53f0\u4e00\u7fbd\u8bba\u8d77\u8f88\u4efd\u6765\u8fd8\u662f\u6211\u7236\u4eb2\u7684\u957f\u8f88\uff0c\u65e9\u5df2\u5728\u51e0\u5341\u5e74\u524d\u9500\u58f0\u533f\u8ff9\uff0c \u8c01\u77e5\u4ed6\u5c45\u7136\u8fd8\u6d3b\u5728\u6b64\u95f4\u3002\u201d \u3000\u3000\u201c\u6fb9\u53f0\u4e00\u7fbd\u6307\u7740\u90a3\u5217\u9ab7\u9ac5\u81ea\u9aa8\u7f13\u7f13\u8bf4\u9053\uff1a\u2018\u6211\u7b11\u4ed6\u4eec\u4e0d\u80fd\u514d\u9664\u8d2a\u5ff5\uff0c\u4e3a\u4e86\u5251\u8c31\u4ea1\u8eab\uff0c\u5176 \u5b9e\u6211\u4e0e\u4ed6\u4eec\u4e5f\u4e0d\u8fc7\u662f\u4e94\u5341\u6b65\u4e4b\u4e8e\u767e\u6b65\uff0c\u4e3a\u4e86\u8fd9\u90e8\u8fbe\u6469\u5251\u8c31\uff0c\u6211\u81ea\u7edd\u4e8e\u4e16\u4eba\uff0c\u72ec\u81ea\u5fcd\u53d7\u4e86\u5927\u534a \u751f\u7684\u7a7a\u5c71\u5c91\u5bc2\uff0c\u60f3\u8981\u7ec3\u6210\u7edd\u4e16\u7684\u6b66\u529f\uff0c\u800c\u4eca\u6b66\u529f\u867d\u8bf4\u5c0f\u6709\u6210\u5c31\uff0c\u800c\u6211\u5374\u4e5f\u5c06\u4e0d\u4e45\u4eba\u4e16 \u4e86\u3002\u2019\u201d \u3000\u3000\u201c\u6211\u5446\u5446\u7684\u671b\u7740\u4ed6\uff0c\u4f46\u89c1\u4ed6\u8eba\u5728\u5e8a\u4e0a\uff0c\u6ee1\u8138\u75c5\u5bb9\uff0c\u67af\u7626\u5f97\u4ee4\u4eba\u5fc3\u60b8\u3002\u4ed6\u6de1\u6de1\u4e00\u7b11\uff0c\u8bf4 \u9053\uff1a\u2018\u4f60\u770b\u4e0d\u51fa\u6211\u662f\u8d70\u706b\u5165\u9b54\uff0c\u534a\u8eab\u4e0d\u9042\u4e48\uff1f\u8fd9\u662f\u534a\u4e2a\u6708\u524d\u53d1\u751f\u7684\uff0c\u8fd9\u534a\u4e2a\u6708\u6765\uff0c\u6211\u5c31\u53ea\u4ed7 \u7740\u8fd9\u77f3\u7a9f\u4e2d\u7684\u77f3\u949f\u4e73\u82df\u5ef6\u6b8b\u5598\uff01\u2019\u201d \u3000\u3000\u201c\u542c\u4e86\u8fd9\u4e00\u756a\u8bdd\uff0c\u6211\u5f53\u771f\u662f\u77eb\u820c\u96be\u4e0b\uff0c\u534a\u4e2a\u6708\u4e0d\u8fdb\u98df\u7269\uff0c\u5185\u529f\u6df1\u539a\u5982\u65af\uff0c\u666e\u5929\u4e4b\u4e0b\uff0c\u53ea \u6015\u518d\u4e5f\u627e\u4e0d\u51fa\u7b2c\u4e8c\u4e2a\u4eba\u3002\u201d \u3000\u3000\u201c\u6fb9\u53f0\u4e00\u7fbd\u7eed\u9053\uff1a\u2018\u90a3\u672c\u8fbe\u6469\u5251\u8c31\u672c\u6765\u662f\u5c11\u6797\u6d3e\u7684\u59cb\u7956\u8fbe\u6469\u5c0a\u8001\u5728\u5d69\u5c71\u9762\u58c1\u4e00\u5341\u516b\u5e74\u4e4b \u540e\uff0c\u6240\u5999\u609f\u51fa\u6765\u7684\u4e00\u5957\u5251\u6cd5\uff0c\u8981\u7ec3\u6210\u8fd9\u5957\u5251\u6cd5\uff0c\u5f53\u7136\u8fd8\u5f97\u6709\u6781\u4e0a\u4e58\u7684\u6b66\u529f\u6839\u57fa\uff0c\u6240\u4ee5\u5251\u8c31\u6240 \u8f7d\uff0c\u4e0d\u53ea\u5251\u6cd5\uff0c\u8fd8\u6709\u7cbe\u6df1\u535a\u5927\u7684\u6b66\u5b66\u7eb2\u8981\uff0c\u6211\u5728\u8fd9\u77f3\u7a9f\u91cc\u7a77\u7814\u4e86\u51e0\u5341\u5e74\uff0c\u4e5f\u53ea\u6562\u8bf4\u4f46\u7aa5\u85e9 \u7bf1\uff0c\u4e0d\u6562\u4e91\u767b\u5802\u5165\u5ba4\u3002\u2019\u201d \u3000\u3000\u201c\u2018\u5230\u4e86\u5b8b\u4ee3\u672a\u5e74\uff0c\u5c11\u6797\u6b66\u5f53\u5206\u5bb6\uff0c\u8fbe\u6469\u5251\u8c31\u6d41\u5165\u6b66\u5f53\u6d3e\u4e4b\u624b\uff0c\u5143\u5175\u5165\u4fb5\u4e4b\u540e\uff0c\u8fd9\u672c\u5251 \u8c31\u5ffd\u7136\u5931\u6389\uff0c\u6b66\u6797\u82f1\u4fca\uff0c\u7eb7\u7eb7\u5bfb\u627e\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\u5b83\u7684\u4e0b\u843d\u3002\u2019\u201d \u3000\u3000\u201c\u2018\u76f4\u5230\u4e09\u5341\u591a\u5e74\u4e4b\u524d\uff0c\u624d\u7ed9\u6211\u6253\u63a2\u51fa\u4e00\u70b9\u6d88\u606f\uff0c\u539f\u6765\u8fd9\u5251\u8c31\u7adf\u7136\u662f\u5728\u6218\u4e71\u4e4b\u4e2d\uff0c\u88ab\u8499 \u53e4\u7687\u5e1d\u7684\u4e00\u4e2a\u56fd\u5e08\u963f\u56fe\u771f\u593a\u53bb\uff0c\u4fdd\u62a4\u8fd9\u5251\u8c31\u9003\u96be\u7684\u5341\u591a\u540d\u6b66\u5f53\u9053\u58eb\u90fd\u5728\u654c\u519b\u4e4b\u4e2d\u6218\u6b7b\uff0c\u6545\u6b64 \u5916\u95f4\u65e0\u4eba\u5f97\u77e5\u3002\u963f\u56fe\u771f\u770b\u4e0d\u61c2\u8fd9\u672c\u5251\u8c31\uff0c\u4f20\u7ed9\u4e86\u4ed6\u7684\u5f92\u5f1f\u9ebb\u7ffc\u8d5e\uff0c\u9ebb\u7ffc\u8d5e\u77e5\u9053\u8fd9\u662f\u5b9d\u7269\uff0c\u4f46 \u4e5f\u53c2\u900f\u4e0d\u4e86\u5176\u4e2d\u5999\u7406\u3002\u4e8e\u662f\u4ed6\u60f3\u51fa\u4e86\u4e00\u4e2a\u8ba1\u5212\uff0c\u62db\u8bf7\u6c49\u4eba\u4e2d\u7684\u6b66\u5b66\u540d\u5bb6\u7ed9\u4ed6\u53c2\u8be6\uff0c\u6709\u771f\u5b9e\u624d \u5b66\u7684\u540d\u5bb6\u5341\u4e5d\u4e0d\u613f\u4e3a\u9791\u5b50\u6548\u52b3\uff0c\u95f4\u6216\u6709\u4e00\u4e24\u4e2a\u4eba\u8d2a\u56fe\u5bcc\u8d35\u53bb\u4e86\uff0c\u5374\u4e0d\u6599\u56e0\u6b64\u53cd\u62db\u4e86\u6740\u8eab\u4e4b \u7978\u3002\u201d \u3000\u3000\u201c\u2018\u90a3\u9ebb\u7ffc\u8d5e\u72e1\u733e\u5f97\u5f88\uff0c\u6015\u4ed6\u4eec\u5f97\u4e86\u8fd9\u5251\u672f\u4e4b\u79d8\uff0c\u4fbf\u5c06\u8fd9\u672c\u5251\u8c31\u5206\u6210\u4e00\u6bb5\u4e00\u6bb5\u6284\u4e0b\u6765\uff0c \u5206\u7ed9\u4ed6\u4eec\u53bb\u94bb\u7814\uff0c\u53eb\u4ed6\u4eec\u505a\u6ce8\u89e3\u7684\u529f\u592b\uff0c\u5176\u5b9e\u8fd9\u6837\u7cbe\u6df1\u9ad8\u5999\u7684\u8fbe\u6469\u5251\u8c31\uff0c\u54ea\u80fd\u5982\u6b64\u96f6\u541e\u788e \u5272\uff1f\u8fd9\u6837\u641e\u4e86\u597d\u591a\u5e74\uff0c\u9ebb\u7ffc\u8d5e\u867d\u7136\u9886\u609f\u4e86\u4e00\u4e9b\u96f6\u661f\u7684\u8fbe\u6469\u5251\u672f\uff0c\u8ddd\u79bb\u878d\u4f1a\u8d2f\u901a\u8fd8\u8fdc\uff0c\u4ed6\u53c8\u4e0d \u653e\u5fc3\u628a\u5168\u90e8\u5251\u8c31\u4ea4\u7ed9\u4e00\u4e2a\u4eba\u53bb\u4e0e\u4ed6\u5171\u901a\u53c2\u8be6\uff0c\u5230\u4e86\u5b9e\u5728\u518d\u641e\u4e0d\u51fa\u4ec0\u4e48\u9053\u7406\u4e86\uff0c\u800c\u4ed6\u81ea\u5df1\u83b7\u5f97 \u4e00\u9cde\u534a\u722a\uff0c\u4e5f\u81ea\u4ee5\u4e3a\u5929\u4e0b\u65e0\u654c\u4e86\u3002\u4fbf\u5c06\u9080\u8bf7\u6765\u7684\u90a3\u4e9b\u5251\u672f\u540d\u5bb6\u4e00\u4e2a\u4e2a\u5bb3\u6b7b\u3002\u5374\u4e0d\u6599\u5176\u4e2d\u4e00\u4e2a \u4eba\u89c1\u673a\u5f97\u65e9\uff0c\u9003\u4e86\u51fa\u6765\uff0c\u4f46\u5728\u9003\u51fa\u4e4b\u65f6\uff0c\u4e5f\u4e2d\u4e86\u8499\u53e4\u6b66\u571f\u7684\u6bd2\u7bad\u3002\u2019\u201d \u3000\u3000\u201c\u2018\u8fd9\u4e2a\u4eba\u662f\u6211\u7684\u670b\u53cb\uff0c\u4e34\u6b7b\u4e4b\u524d\uff0c\u5bf9\u6211\u8bf4\u51fa\u8fd9\u4ef6\u79d8\u5bc6\uff0c\u6211\u4e00\u6765\u4e0d\u6124\u8fd9\u672c\u5251\u8c31\u6d41\u5165\u9774\u5b50 \u4e4b\u624b\uff0c\u4e8c\u6765\u81ea\u5df1\u4e5f\u60f3\u6210\u4e3a\u6b66\u529f\u5929\u4e0b\u7b2c\u4e00\u7684\u5251\u5b66\u5927\u5e08\uff0c\u4fbf\u5077\u5165\u5143\u5bab\u76d7\u8fd9\u5251\u8c31\uff0c\u4fa5\u5e78\u88ab\u6211\u5f97\u624b\uff0c \u8fde\u6740\u4e86\u5341\u516b\u540d\u8499\u53e4\u6b66\u571f\uff0c\u7ec8\u4e8e\u5c06\u8fd9\u672c\u5251\u8c31\u62ff\u5230\u624b\u4e2d\uff0c\u6211\u4fbf\u9690\u59d3\u57cb\u540d\uff0c\u9003\u533f\u5230\u8fd9\u77f3\u7a9f\u4e4b \u4e2d\u3002\u2019\u201d \u3000\u3000\u201c\u6fb9\u53f0\u4e00\u7fbd\u8bf4\u5230\u8fd9\u91cc\uff0c\u60f3\u8d77\u4ed6\u4e3a\u4e86\u8fd9\u672c\u5251\u8c31\uff0c\u5927\u534a\u751f\u4e0d\u89c1\u5929\u65e5\uff0c\u4e0d\u80dc\u611f\u6168\u3002\u6211\u4fbf\u63d2\u53e3\u8bf4 \u9053\uff1a\u2018\u73b0\u5728\u7fa4\u96c4\u7eb7\u8d77\uff0c\u9a71\u9664\u9791\u864f\u4e0d\u8fc7\u6307\u987e\u95f4\u4e8b\uff0c\u6211\u613f\u5728\u8fd9\u91cc\u670d\u4f8d\u4f60\uff0c\u5f85\u4f60\u590d\u539f\u4e4b\u540e\uff0c\u5c82\u4e0d\u662f \u8fd8\u53ef\u4ee5\u51fa\u53bb\u505a\u4e00\u756a\u4e8b\u4e1a\u3002\u2019\u2018\u6fb9\u53f0\u4e00\u7fbd\u5374\u6e17\u7b11\u9053\uff1a\u2018\u6211\u4e3a\u4e86\u8e81\u8fdb\u8d2a\u529f\uff0c\u82e6\u7ec3\u4e0a\u4e58\u5185\u529f\uff0c\u8fd9\u624d \u8d70\u706b\u5165\u9b54\uff0c\u5df2\u662f\u65e0\u6cd5\u53ef\u4ee5\u6551\u6cbb\u3002\u73b0\u5728\u6211\u4e5f\u4e0d\u77e5\u80fd\u6371\u5230\u51e0\u65f6\uff0c\u53ea\u662f\u6709\u4ef6\u5fc3\u613f\u82e5\u7136\u672a\u4e86\uff0c\u6211\u6b7b\u4e5f \u96be\u4ee5\u7791\u76ee\u3002\u2019\u201d \u3000\u3000\u6bd5\u51cc\u98ce\u7eed\u9053\uff1a\u201c\u6211\u6025\u5fd9\u95ee\u4ed6\u662f\u4ec0\u4e48\u5fc3\u613f\uff1f\u6fb9\u53f0\u4e00\u7fbd\u53f9\u4e86\u53e3\u6c14\u8bf4\u9053\uff1a\u2018\u6211\u8d39\u4e86\u5927\u534a\u751f\u5fc3 \u8840\uff0c\u5bf9\u8fd9\u672c\u5251\u8c31\u603b\u7b97\u53c2\u609f\u4e86\u4e00\u70b9\u9053\u7406\uff0c\u6211\u4e0d\u80fd\u8ba9\u5b83\u968f\u6211\u57cb\u846c\u5728\u8fd9\u77f3\u7a9f\u4e4b\u4e2d\uff0c\u6211\u8981\u5bfb\u89c5\u4e00\u4e2a\u53ef \u4ee5\u4ea4\u6258\u7684\u4eba\u5c06\u5b83\u6d41\u4f20\u540e\u4e16\u3002\u2019\u201d \u3000\u3000\u201c\u6211\u542c\u4e86\u6026\u7136\u5fc3\u52a8\uff0c\u6fb9\u53f0\u4e00\u7fbd\u770b\u4e86\u6211\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u2018\u4f60\u5b85\u5fc3\u4ec1\u539a\uff0c\u81ea\u662f\u53ef\u4ee5\u4fe1\u6258\u7684\u4eba\uff0c \u4f46\u4ee5\u4f60\u73b0\u5728\u7684\u6b66\u529f\uff0c\u53ea\u6709\u8fd9\u672c\u5251\u6f5c\uff0c\u53cd\u800c\u4e3a\u4f60\u62db\u6765\u6740\u8eab\u4e4b\u7978\uff0c\u6211\u4e0d\u80fd\u5c06\u5251\u8c31\u4f20\u7ed9\u4f60\u3002\u2019\u8bf4\u7740 \u53c8\u6307\u6307\u90a3\u4e00\u5217\u9ab7\u9ac5\u767d\u9aa8\u8bf4\u9053\uff1a\u2018\u8fd9\u4e9b\u90fd\u662f\u4e0d\u81ea\u91cf\u529b\u8981\u6765\u76d7\u53d6\u5251\u8c31\u7684\u4eba\uff0c\u5440\uff0c\u5176\u5b9e\u4ee5\u4ed6\u4eec\u8fd9\u70b9 \u5fae\u672a\u7684\u672c\u9886\uff0c\u5f97\u4e86\u4e5f\u6ca1\u6709\u7528\u3002\u2019\u201d \u3000\u3000\u201c\u6211\u542c\u4e86\u5fc3\u4e2d\u4f9d\u7136\uff0c\u4e0d\u6562\u591a\u8bf4\u3002\u53ea\u542c\u5f97\u6fb9\u53f0\u4e00\u7fbd\u5401\u4e86\u53e3\u6c14\uff0c\u518d\u7f13\u7f13\u8bf4\u9053\uff1a\u2018\u6211\u5fc3\u76ee\u4e2d\u53ef \u4ee5\u4ea4\u6258\u8fd9\u672c\u5251\u8c31\u7684\u6709\u4e09\u4e2a\u4eba\uff0c\u5176\u4e2d\u4e00\u4eba\u672a\u5fc5\u80af\u8981\uff0c\u53e6\u4e00\u4eba\u6211\u5374\u53c8\u4e0d\u613f\u4ea4\u7ed9\u4ed6\uff0c\u7b97\u6765\u7b97\u53bb\uff0c\u53ea \u6709\u4ea4\u6258\u7ed9\u9648\u5b9a\u65b9\u9648\u5927\u4fa0\u4e86\u3002\u2019\u201d \u3000\u3000\u201c\u6211\u542c\u4e86\u5947\u602a\uff0c\u95ee\u53e6\u5916\u4e24\u4eba\u662f\u8c01\uff0c\u6fb9\u53f0\u4e00\u7fbd\u9053\uff1a\u2018\u6211\u5fc3\u76ee\u4e2d\u7684\u4e09\u4e2a\u4eba\uff0c\u4e00\u4e2a\u662f\u5f6d\u548c\u5c1a\uff0c \u4e00\u4e2a\u725f\u72ec\u9038\uff0c\u6700\u540e\u624d\u662f\u9648\u5b9a\u65b9\u3002\u5f6d\u548c\u5c1a\u662f\u4e00\u4ee3\u7684\u5927\u5b97\u5e08\uff0c\u6240\u5b66\u7684\u662f\u6b63\u5b97\u6b66\u529f\uff0c\u4ed6\u56fa\u7136\u4e0d\u5e0c\u7f55 \u8fd9\u672c\u5251\u8c31\uff0c\u6211\u4f20\u7ed9\u4ed6\u4e5f\u6050\u4fae\u8fb1\u4e86\u4ed6\uff0c\u8981\u77e5\u4ed6\u6b66\u529f\u5728\u6211\u4e4b\u4e0a\uff0c\u5c82\u80fd\u7ee7\u627f\u505a\u6211\u7684\u8863\u94b5\u4f20\u4eba\uff1f\u2019\u201d \u3000\u3000\u201c\u2018\u7b2c\u4e8c\u4e2a\u662f\u725f\u72ec\u9038\uff0c\u4ed6\u7684\u5251\u6cd5\uff0c\u5929\u4e0b\u7b2c\u4e00\uff0c\u8fd9\u5251\u8c31\u672c\u6765\u53c8\u539f\u662f\u6b66\u5f53\u6d3e\u7684\uff0c\u4ea4\u7ed9\u4ed6\u4e43\u662f \u6700\u9002\u5f53\u4e0d\u8fc7\u7684\u4e86\u3002\u4f46\u6211\u5bf9\u4ed6\u7684\u4eba\u54c1\u5c1a\u6709\u6000\u7591\uff0c\u540c\u65f6\u6211\u6709\u4e2a\u602a\u813e\u6c14\uff0c\u8c01\u8d8a\u60f3\u8981\u7684\uff0c\u6211\u5c31\u504f\u504f\u4e0d \u80af\u7ed9\u4ed6\u2019\u201d \u3000\u3000\u542c\u5230\u8fd9\u91cc\uff0c\u4e0a\u5b98\u5929\u91ce\u8bf4\u9053\uff1a\u201c\u6211\u867d\u7136\u672a\u89c1\u8fc7\u725f\u5e08\u7956\uff0c\u4f46\u4e5f\u542c\u524d\u8f88\u8bf4\u8fc7\u4ed6\u8bb8\u591a\u4fa0\u4e49\u4e8b\u8ff9\uff0c \u8fd9\u6fb9\u53f0\u4e00\u7fbd\u4f55\u4ee5\u5982\u6b64\u8bf4\u4ed6\uff1f\u201d \u3000\u3000\u6bd5\u51cc\u98ce\u9053\uff1a\u201c\u662f\u5440\uff0c\u5f53\u65f6\u6211\u4e5f\u8fd9\u6837\u95ee\u4ed6\u3002\u6fb9\u53f0\u4e00\u7fbd\u6307\u7740\u521a\u624d\u88ab\u4ed6\u51fb\u6bd9\u7684\u4e00\u4e2a\u5927\u6c49\u8bf4\u9053\uff1a \u2018\u4f60\u77a7\uff0c\u8fd9\u4eba\u4fbf\u662f\u725f\u72ec\u9038\u7684\u5927\u5f1f\u5b50\uff0c\u725f\u72ec\u9038\u4e0d\u77e5\u4ece\u54ea\u91cc\u5f97\u4e86\u6d88\u606f\uff0c\u5c45\u7136\u6d3e\u4ed6\u6765\u5411\u6211\u5f3a\u8ba8\uff0c\u6211 \u8bf4\u504f\u504f\u4e0d\u7ed9\u4ed6\uff0c\u5251\u8c31\u867d\u7136\u672c\u6765\u662f\u6b66\u5f53\u6d3e\u7684\uff0c\u4f46\u5df2\u7ecf\u5931\u6389\uff0c\u662f\u6211\u820d\u4e86\u6027\u547d\u593a\u56de\uff0c\u53c8\u8d39\u4e86\u8fd9\u5927\u534a \u751f\u5fc3\u8840\uff0c\u6211\u5c31\u662f\u8fd9\u5251\u8c31\u7684\u4e3b\u4eba\uff0c\u6b66\u5f53\u6d3e\u65e0\u6743\u8fc7\u95ee\u3002\u2019\u201d \u3000\u3000\u8fd9\u771f\u662f\u4e00\u7b14\u7b97\u4e0d\u6e05\u7684\u5e10\uff0c\u8bf4\u8d77\u6765\u90fd\u5404\u6709\u7406\u7531\u3002\u4e0a\u5b98\u5929\u91ce\u5fc3\u9053\uff1a\u201c\u539f\u6765\u5e08\u7956\u662f\u6025\u4e8e\u7ed9\u672c\u6d3e \u5bfb\u56de\u5251\u8c31\uff0c\u4ee5\u81f4\u7ed9\u6fb9\u53f0\u4e00\u7fbd\u770b\u5c0f\u4e86\u3002\u5728\u6211\u770b\u6765\uff0c\u8fd9\u4e5f\u4e0d\u89c1\u5f97\u662f\u4ec0\u4e48\u5927\u4e0d\u662f\u5462\uff1f\u201d \u3000\u3000\u6bd5\u51cc\u98ce\u7eed\u9053\uff1a\u201c\u6fb9\u53f0\u4e00\u7fbd\u7ec6\u8ff0\u4e86\u8fd9\u5251\u8c31\u5f97\u5931\u7684\u7ecf\u8fc7\u540e\uff0c\u4fbf\u8981\u6211\u634e\u4fe1\u7ed9\u9648\u5b9a\u65b9\uff0c\u8981\u9648\u5b9a\u65b9 \u5c3d\u5feb\u6765\u53d6\u8fd9\u672c\u5251\u8c31\u3002\u6211\u542c\u4e86\u4e4b\u540e\u6b23\u7136\u53d7\u547d\uff0c\u4e00\u6765\u662f\u56e0\u4e3a\u6211\u94a6\u656c\u9648\u5927\u4fa0\u7684\u4e3a\u4eba\uff0c\u4e8c\u6765\u5462\uff0c\u6211\u4e5f \u6709\u81ea\u5df1\u7684\u5fc3\u4e8b\u3002\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u5947\u4e11\u65e0\u6bd4\u7684\u8138\u4e0a\uff0c\u5ffd\u7136\u73b0\u51fa\u4e00\u9762\u6655\u7ea2\uff0c\u597d\u50cf\u6709\u70b9\u5ff8\u4fc3\u7684\u6837\u5b50\u3002 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u9887\u4e3a\u5947\u602a\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u6bd5\u51cc\u98ce\u8bf4\u9053\uff1a\u201c\u6211\u5982\u4eca\u53c8\u8001\u53c8\u4e11\uff0c\u5bf9\u4f60\u8bf4\u8bf4\u6211\u5f53\u5e74\u7684\u5fc3 \u4e8b\uff0c\u60f3\u6765\u8fd8\u4e0d\u81f3\u4e8e\u4e3a\u4f60\u803b\u7b11\u3002\u201d \u3000\u3000\u201c\u5f53\u5e74\u725f\u72ec\u9038\u4e0e\u9648\u5b9a\u65b9\u5e76\u80a9\u9f50\u540d\uff0c\u88ab\u6b66\u6797\u82f1\u96c4\u5c0a\u79f0\u4e3a\u5f53\u4e16\u7684\u4e24\u4f4d\u5927\u4fa0\u3002\u65e0\u72ec\u6709\u5076\uff0c\u8fd9\u4e24 \u4f4d\u5927\u4fa0\u90fd\u6709\u4e00\u4e2a\u51fa\u843d\u5f97\u5982\u82b1\u4f3c\u7389\u3001\u6587\u6b66\u53cc\u5168\u7684\u5973\u513f\u3002\u725f\u72ec\u9038\u7684\u5973\u513f\u53eb\u725f\u5b9d\u73e0\uff0c\u9648\u5b9a\u65b9\u7684\u5973\u513f \u53eb\u9648\u96ea\u6885\u3002\u6c5f\u6e56\u4e0a\u7684\u5e74\u5c11\u82f1\u96c4\uff0c\u8c01\u4e0d\u60f3\u505a\u4ed6\u4eec\u4e24\u5bb6\u7684\u4f73\u5a7f\uff1f\u201d \u3000\u3000\u201c\u6211\u90a3\u65f6\u8fd8\u672a\u50cf\u4eca\u65e5\u8fd9\u6837\u7684\u4e11\u964b\uff0c\u5bf9\u9648\u5bb6\u7684\u59d1\u5a18\u4e5f\u6709\u4e00\u4efd\u75f4\u5fc3\u5984\u60f3\uff0c\u5f97\u6b64\u673a\u7f18\uff0c\u6b63\u597d\u53bb \u5df4\u7ed3\u4e00\u4e0b\u9648\u5b9a\u65b9\uff0c\u5e0c\u671b\u80fd\u52a9\u4ed6\u5f97\u4e86\u5251\u8c31\u4e4b\u540e\uff0c\u5c06\u6765\u6258\u4eba\u63d0\u4eb2\uff0c\u5f00\u53e3\u4e5f\u5bb9\u6613\u5f97\u591a\u3002\u201d \u3000\u3000\u201c\u6211\u91c7\u4e86\u8bb8\u591a\u5c71\u679c\uff0c\u8fd8\u730e\u4e86\u4e00\u5934\u91ce\u732a\u7559\u5728\u77f3\u7a9f\u4e4b\u4e2d\u4f5c\u6fb9\u53f0\u4e00\u7fbd\u7684\u98df\u7cae\uff0c\u4fbf\u52ff\u52ff\u544a\u8f9e\uff0c\u8d76 \u5f80\u9648\u5bb6\u3002\u201d \u3000\u3000\u201c\u54ea\u77e5\u9648\u5b9a\u65b9\u5374\u4e0d\u5728\u5bb6\u4e2d\uff0c\u6211\u5411\u4ed6\u7684\u5bb6\u4eba\u95ee\u8baf\uff0c\u8fd9\u624d\u77e5\u9053\u9648\u5bb6\u59d1\u5a18\u5df2\u5728\u4e0a\u6708\u51fa\u5ac1\uff0c\u65b0\u5a5a \u592b\u5a7f\u6b63\u662f\u6211\u54e5\u54e5\u7684\u597d\u53cb\u4e91\u821e\u9633\u3002\u9648\u5b9a\u65b9\u5c31\u662f\u56e0\u4e3a\u9001\u5973\u513f\u51fa\u5ac1\uff0c\u51fa\u95e8\u53bb\u7684\u3002\u201d \u3000\u3000\u201c\u6211\u5f53\u7136\u662f\u975e\u5e38\u5931\u671b\uff0c\u4f46\u8fd8\u662f\u7559\u5728\u9648\u5bb6\u7b49\u9648\u5b9a\u65b9\u56de\u6765\u3002\u9648\u5b9a\u65b9\u56de\u6765\u4e4b\u540e\uff0c\u542c\u5f97\u6b64\u4e8b\uff0c\u771f \u662f\u610f\u5916\u6b22\u559c\uff0c\u5bf9\u6211\u9891\u9882\u5938\u8d5e\uff0c\u8bf4\u6211\u4e0d\u8d2a\u56fe\u5b9d\u7269\uff0c\u662f\u4e2a\u80fd\u591f\u9075\u5b88\u6c5f\u6e56\u4fe1\u4e49\u7684\u4eba\u3002\u7b2c\u4e8c\u65e5\u6211\u4fbf\u548c \u4ed6\u4e00\u9053\u5230\u9ea6\u79ef\u77f3\u5c71\u53bb\u8bbf\u6fb9\u53f0\u4e00\u7fbd\u3002\u201d \u3000\u3000\u201c\u6b66\u6797\u4e2d\u7684\u89c4\u77e9\u6781\u4e25\uff0c\u8fd9\u4e24\u4f4d\u6b66\u6797\u4e2d\u7684\u524d\u8f88\u4f20\u7ecf\u53d7\u8c31\uff0c\u6211\u5f53\u7136\u4e0d\u4fbf\u968f\u4f8d\u5728\u4fa7\uff0c\u56e0\u6b64\u6211\u5c06 \u90a3\u77f3\u7a9f\u6240\u5728\u6307\u70b9\u7ed9\u9648\u5b9a\u65b9\u4e4b\u540e\uff0c\u4fbf\u72ec\u5750\u5c71\u5934\u7b49\u4ed6\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u54ea\u77e5\u6fb9\u53f0\u4e00\u7fbd\u65e9\u5df2\u6b7b\u4e86\uff0c\u725f\u72ec\u9038\u56e0\u4e3a\u5927\u5f1f\u5b50\u5931\u8e2a\uff0c\u4e5f\u6070\u5de7\u5728\u90a3\u4e00\u65e5\u5bfb\u6765\uff0c\u4ed6\u6bd4\u9648\u5b9a\u65b9 \u5148\u5230\u4e00\u6b65\uff0c\u5df2\u5c06\u5251\u8c31\u641c\u5230\uff0c\u6b63\u5728\u5f97\u610f\u5fd8\u5f62\u7684\u9ad8\u58f0\u8bf5\u8d5e\uff0c\u9648\u5b9a\u65b9\u4ea6\u5df2\u8de8\u8fdb\u6d1e\u4e2d\uff0c\u4e24\u4f4d\u5e76\u80a9\u9f50\u540d \u7684\u5927\u4fa0\u4fbf\u5728\u77f3\u7a9f\u4e4b\u5185\u964c\u8def\u76f8\u9022\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e9b\u4e8b\u60c5\u6211\u90fd\u662f\u4ee5\u540e\u77e5\u9053\u7684\u3002\u5f53\u65f6\u4e5f\u4e0d\u77e5\u9053\u4ed6\u4eec\u600e\u6837\u4e89\u8bba\u8d77\u6765\uff0c\u4e24\u4f4d\u88ab\u6b66\u6797\u4e2d\u4eba\u89c6\u4e3a \u6cf0\u5c71\u5317\u6597\u7684\u4eba\u7269\uff0c\u7adf\u7136\u4e3a\u4e86\u8fd9\u672c\u5251\u8c31\uff0c\u820d\u6b7b\u5fd8\u751f\u7684\u5927\u6597\u4e00\u573a\u3002\u201d \u3000\u3000\u201c\u5440\uff0c\u8fd9\u771f\u662f\u767e\u5e74\u96be\u9047\u7684\u4e00\u573a\u6bd4\u6b66\uff0c\u9648\u5b9a\u65b9\u6709\u5bb6\u4f20\u7684\u6606\u543e\u5b9d\u5251\uff0c\u5f00\u9996\u4fbf\u5360\u4e86\u4e0a\u98ce\uff0c\u4e24\u4eba \u4ece\u77f3\u7a9f\u91cc\u9762\u6253\u51fa\u6765\uff0c\u4e00\u76f4\u6253\u4e0a\u5cf0\u5dc5\uff0c\u4f46\u89c1\u5251\u6c14\u5f25\u5929\uff0c\u4e24\u4f4d\u5927\u4fa0\u90fd\u4f7f\u51fa\u4e86\u5e73\u751f\u7edd\u5b66\uff0c\u62db\u62db\u90fd\u662f \u6740\u624b\u3002\u6211\u8eb2\u5728\u5927\u77f3\u4e4b\u540e\uff0c\u770b\u5230\u6c14\u4e5f\u900f\u4e0d\u8fc7\u6765\u3002\u201d \u3000\u3000\u201c\u4e24\u4eba\u81ea\u6e05\u6668\u6253\u5230\u5348\u540e\uff0c\u62fc\u6597\u4f55\u6b62\u5343\u62db\uff0c\u5c06\u8fd1\u592a\u9633\u843d\u5c71\u4e4b\u65f6\uff0c\u9648\u5b9a\u65b9\u4e00\u5251\u5c06\u725f\u72ec\u9038\u7684\u5251 \u524a\u65ad\uff0c\u6211\u81ea\u662f\u76fc\u671b\u9648\u5b9a\u65b9\u5f97\u80dc\uff0c\u5fc3\u4e2d\u6b63\u559c\uff0c\u54ea\u77e5\u725f\u72ec\u9038\u65ad\u5251\u4e4b\u540e\uff0c\u6597\u5f97\u66f4\u52c7\uff0c\u8d8a\u6597\u8d8a\u6709\u7cbe \u795e\uff0c\u7adf\u4f7f\u51fa\u4ed6\u82e6\u7ec3\u6570\u5341\u5e74\u7684\u592a\u6e05\u7384\u529f\u3002\u201d \u3000\u3000\u201c\u8bba\u5230\u5185\u529f\u7684\u4fee\u517b\uff0c\u5f53\u65f6\u662f\u4ee5\u5f6d\u548c\u5c1a\u51a0\u7edd\u6b66\u6797\uff0c\u725f\u72ec\u9038\u5374\u8981\u6bd4\u9648\u5b9a\u65b9\u7a0d\u80dc\u5c11\u8bb8\uff0c\u4e24\u4eba\u53c8 \u4ece\u65e5\u843d\u6597\u5230\u5348\u591c\uff0c\u90fd\u5df2\u7b4b\u75b2\u529b\u7aed\uff0c\u725f\u72ec\u9038\u88ab\u9648\u5b9a\u65b9\u7684\u5b9d\u5251\u4f24\u4e86\u51e0\u5904\uff0c\u9648\u5b9a\u65b9\u4e5f\u7ed9\u725f\u72ec\u9038\u8fde\u5288 \u4e86\u4e24\u638c\u3002\u5ffd\u542c\u5f97\u725f\u72ec\u9038\u5927\u559d\u9053\uff1a\u2018\u4f60\u8fd8\u4e0d\u77e5\u8fdb\u9000\uff0c\u6211\u5c31\u5c06\u4f60\u7684\u5b9d\u5251\u4e5f\u4e00\u5e76\u62a2\u4e86\uff01\u2019\u201d \u3000\u3000\u201c\u9648\u5b9a\u65b9\u5927\u6012\u559d\u9053\uff1a\u2018\u597d\uff0c\u4f60\u82e5\u80fd\u628a\u7684\u6211\u5b9d\u5251\u62a2\u53bb\uff0c\u4ece\u4eca\u65e5\u8d77\u6c5f\u6e56\u4e0a\u5c31\u62b9\u6389\u6211\u9648\u5b9a\u65b9\u8fd9 \u53f7\u4eba\u7269\u3002\u2019\u9648\u5927\u4fa0\u6587\u6b66\u53cc\u5168\uff0c\u5e73\u65e5\u5f85\u4eba\u63a5\u7269\uff0c\u6709\u5982\u6042\u6042\u5112\u8005\uff0c\u8fd9\u65f6\u5374\u7ed9\u725f\u72ec\u9038\u6fc0\u6012\u5f97\u5982\u540c\u75af \u864e\u4e00\u822c\uff0c\u4f7f\u51fa\u7684\u7adf\u662f\u62fc\u4e86\u4e24\u8d25\u4ff1\u4f24\u7684\u6781\u4e4b\u51f6\u6b8b\u7684\u5251\u6cd5\uff01\u201d \u3000\u3000\u201c\u6708\u4eae\u6e10\u6e10\u79fb\u5230\u5929\u5fc3\uff0c\u4e24\u4eba\u5df2\u662f\u4ece\u6e05\u65e9\u6253\u5230\u5348\u591c\uff0c\u84e6\u7136\u95f4\u53ea\u542c\u5f97\u2018\u5237\u2019\u7684\u4e00\u5251\uff0c\u725f\u72ec\u9038 \u7684\u80a9\u5934\u4e0a\u53c8\u4e00\u7247\u6bb7\u7ea2\uff0c\u7136\u800c\u4ed6\u5374\u662f\u54c8\u54c8\u5927\u7b11\uff0c\u53ea\u662f\u9648\u5b9a\u65b9\u8dc4\u8dc4\u8e09\u8e09\u7684\u5012\u9000\u6570\u6b65\uff0c\u9762\u8272\u60e8\u767d\uff0c \u5251\u4e0a\u7684\u4e24\u4ef6\u7389\u73af\u5df2\u7ed9\u725f\u72ec\u9038\u626f\u65ad\u4e86\u3002\u4e24\u4eba\u7684\u795e\u8272\u90fd\u662f\u53ef\u6015\u4e4b\u6781\uff0c\u6211\u7981\u4e0d\u4f4f\u60ca\u53eb\u51fa \u3000\u3000\u201c\u6211\u4ece\u5ca9\u77f3\u540e\u9762\u8d70\u51fa\u6765\u65f6\uff0c\u53ea\u542c\u5f97\u7b11\u58f0\u5728\u5c71\u8c37\u4e4b\u4e2d\u56de\u65cb\u9707\u8361\uff0c\u725f\u72ec\u9038\u5df2\u8d70\u5f97\u65e0\u5f71\u65e0\u8e2a\u3002 \u60f3\u6765\u4ea6\u5df2\u6597\u5f97\u7b4b\u75b2\u529b\u7aed\uff0c\u751f\u6015\u6211\u662f\u9648\u5b9a\u65b9\u6697\u4e2d\u4f0f\u4e0b\u7684\u5e2e\u624b\uff0c\u662f\u4ee5\u8d70\u4e86\u3002\u201d \u3000\u3000\u201c\u9648\u5b9a\u65b9\u9893\u7136\u5750\u5728\u5730\u4e0a\uff0c\u8bf4\u9053\uff1a\u2018\u4eca\u65e5\u5168\u4e8f\u4e86\u4f60\u4e86\u3002\u2019\u539f\u6765\u4ed6\u53d7\u7684\u5185\u4f24\u6bd4\u725f\u72ec\u9038\u66f4\u91cd\uff0c \u4f46\u4ed6\u5f53\u65f6\u5374\u52c9\u5f3a\u652f\u6301\uff0c\u4e0d\u8ba9\u6211\u77e5\u9053\u3002\u6b47\u4e86\u4e00\u4f1a\uff0c\u5c31\u50ac\u6211\u548c\u4ed6\u4e00\u540c\u8d76\u8def\u56de\u5bb6\u3002\u6211\u773c\u4f8d\u4ed6\u56de\u5230\u5bb6 \u4e2d\uff0c\u4ed6\u5fc3\u529b\u4ea4\u75b2\uff0c\u7b2c\u4e8c\u65e5\u4fbf\u75c5\u5012\u4e86\u3002\u201d \u3000\u3000\u201c\u4ed6\u53eb\u5bb6\u4eba\u8bf7\u4e86\u98de\u9f99\u5e2e\u7684\u5e2e\u4e3b\u8427\u51a0\u82f1\u6765\u2026\u2026\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u5931\u58f0\u53eb\u9053\uff1a\u201c\u55ef\uff0c\u8427\u51a0\u82f1\uff1f\u4ed6\u662f\u4e0d\u662f\u6709\u51e0\u540d\u5f97\u529b\u7684\u624b\u4e0b\u53eb\u505a\u7987\u82f1\u3001\u7987\u9738\u3001\u516c\u51b6 \u826f\u548c\u5e38\u5c71\u9f99\uff1f\u201d \u3000\u3000\u6bd5\u51cc\u98ce\u4f3c\u662f\u6709\u70b9\u8be7\u5f02\uff0c\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u6c5f\u6e56\u4e0a\u7684\u4e8b\u60c5\u4f60\u5012\u77e5\u5f97\u4e0d\u5c11\u3002\u4e0d\u8fc7\u90a3\u65f6\u8fd9\u51e0\u4e2a\u4eba \u90fd\u8fd8\u662f\u65e0\u540d\u5c0f\u5352\uff0c\u540e\u6765\u624d\u7ed9\u8427\u51a0\u82f1\u63d0\u62d4\u8d77\u6765\u7684\u3002 \u3000\u3000\u201c\u8427\u51a0\u82f1\u662f\u9648\u5b9a\u65b9\u7684\u8bb0\u540d\u5f1f\u5b50\uff0c\u540e\u6765\u6211\u624d\u77e5\u9053\u9648\u5b9a\u65b9\u5c06\u4ed6\u627e\u6765\u662f\u4e3a\u4e86\u5429\u5490\u540e\u4e8b\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u65e2\u7136\u662f\u5429\u5490\u540e\u4e8b\uff0c\u4ed6\u4e3a\u4f55\u4e0d\u5c06\u5973\u513f\u5973\u5a7f\u627e\u6765\uff1f\u201d \u3000\u3000\u6bd5\u51cc\u98ce\u9053\uff1a\u201c\u4e91\u821e\u9633\u8fdc\u5728\u6c5f\u5357\uff0c\u800c\u4e14\u90a3\u65f6\u6218\u4e8b\u6b63\u7d27\uff0c\u9648\u5b9a\u65b9\u5371\u5728\u65e6\u5915\uff0c\u6765\u4e0d\u53ca\u5c06\u4ed6\u4eec\u53ec \u56de\u4e86\u3002\u201d \u3000\u3000\u201c\u5509\uff0c\u60f3\u4e0d\u5230\u6211\u56e0\u4e3a\u5076\u7136\u78b0\u89c1\u6fb9\u53f0\u4e00\u7fbd\uff0c\u7adf\u88ab\u5377\u5165\u8fd9\u4e2a\u6f29\u6da1\u3002\u201d \u3000\u3000\u201c\u9648\u5b9a\u65b9\u4e34\u6b7b\u7684\u524d\u5915\uff0c\u75c5\u69bb\u4e4b\u524d\u5c31\u53ea\u6709\u6211\u548c\u8427\u51a0\u82f1\u4e24\u4e2a\u4eba\u3002\u9648\u5b9a\u65b9\u5c06\u5251\u8c31\u4e4b\u4e8b\u4e0e\u81f4\u6b7b\u4e4b \u7531\u6e90\u6e90\u672c\u672c\u7684\u5411\u8427\u51a0\u82f1\u8bf4\u4e86\u4e00\u904d\u3002\u6700\u540e\u4fbf\u8981\u6211\u4eec\u9886\u53d7\u4ed6\u7684\u9057\u547d\u3002\u201d \u3000\u3000\u201c\u4ed6\u8bf4\uff1a\u2018\u4f60\u4eec\u4e00\u4e2a\u662f\u6211\u8bb0\u540d\u5f1f\u5b50\uff0c\u4e00\u4e2a\u662f\u59cb\u7ec8\u53c2\u4e0e\u6b64\u4e8b\u7684\u4eba\u3002\u6bd5\u51cc\u98ce\u5e26\u6211\u53bb\u89c1\u6fb9\u53f0\u8001 \u4eba\uff0c\u5413\u8d70\u725f\u72ec\u9038\uff0c\u53c8\u4e00\u8def\u670d\u4f8d\u6211\uff0c\u4f7f\u6211\u4e0d\u81f4\u5012\u6bd9\u9053\u4e0a\uff0c\u6211\u5c24\u5176\u611f\u6fc0\u3002\u2019\u201d \u3000\u3000\u201c\u6211\u6b7b\u4e4b\u540e\uff0c\u4f60\u4eec\u4e8c\u4eba\u8c01\u4eba\u82e5\u7136\u80fd\u591f\u4ece\u725f\u72ec\u9038\u624b\u4e2d\u593a\u56de\u8fbe\u6469\u5251\u8c31\uff0c\u8fd9\u5251\u8c31\u4fbf\u5f52\u4ed6\u6240\u6709\u3002 \u4f60\u4eec\u597d\u597d\u7684\u7ed9\u6211\u529e\u8fd9\u4ef6\u4e8b\u5427\u3002\u6211\u8fd9\u91cc\u5199\u4e86\u4e00\u4efd\u9057\u4e66\uff0c\u628a\u4e8b\u60c5\u539f\u59d4\u90fd\u5199\u5728\u4e0a\u9762\uff0c\u82e5\u7136\u5c06\u6765\u56e0\u8fd9 \u90e8\u5251\u8c31\u4e0e\u6b66\u5f53\u6d3e\u6709\u751a\u98ce\u6ce2\uff0c\u4f60\u4eec\u53ef\u4ee5\u5c06\u6211\u7684\u9057\u4e66\u62ab\u9732\uff0c\u8fd9\u4efd\u9057\u4e66\u6682\u4ea4\u7ed9\u6bd5\u51cc\u98ce\u6267\u638c\u3002\u2019\u8bf4\u5b8c \u4e4b\u540e\u4fbf\u54bd\u6c14\u4e86\uff0c\u53ef\u601c\u4e00\u4ee3\u5927\u4fa0\uff0c\u7adf\u7136\u62b1\u6562\u7ec8\u5929\uff01\u201d \u3000\u3000\u6bd5\u51cc\u98ce\u957f\u957f\u7684\u5618\u4e86\u53e3\u6c14\uff0c\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u9648\u5b9a\u65b9\u6b7b\u540e\uff0c\u6211\u4e0e\u8427\u51a0\u82f1\u5546\u91cf\uff0c\u5927\u5bb6\u90fd\u613f\u610f\u4ee5\u6bd5 \u751f\u4e4b\u529b\uff0c\u4e3a\u9648\u5b9a\u65b9\u593a\u56de\u8fd9\u672c\u5251\u8c31\uff0c\u4f46\u5374\u4e92\u76f8\u8bb8\u8bfa\uff0c\u4e0d\u8bba\u662f\u8c01\u5f97\u4e86\uff0c\u8fd9\u90e8\u5251\u8c31\u90fd\u5949\u8fd8\u7ed9\u9648\u5b9a\u65b9 \u7684\u5973\u513f\uff0c\u51b3\u4e0d\u636e\u4e3a\u5df2\u6709\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u8fd9\u4e3b\u610f\u662f\u5e08\u7236\u4f60\u5148\u63d0\u51fa\u7684\u5427\uff1f\u201d\u6bd5\u51cc\u98ce\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d\u4e0a \u5b98\u5929\u91ce\u5fae\u5fae\u4e00\u7b11\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u770b\u6765\u5e08\u7236\u5bf9\u9648\u5b9a\u65b9\u7684\u5973\u513f\u59cb\u7ec8\u6ca1\u6709\u5fd8\u60c5\u3002\u5979\u5df2\u5ac1\u4e86\u4eba\uff0c\u5e08\u7236 \u5bf9\u5979\u7684\u5fc3\u610f\u5979\u4e5f\u672a\u5fc5\u77e5\u9053\u3002\u5e08\u7236\u5374\u80af\u4e3a\u5979\u53bb\u5411\u5929\u4e0b\u7b2c\u4e00\u5251\u5ba2\u8c0b\u593a\u5251\u8c31\uff0c\u8fd9\u6bb5\u6df1\u60c5\uff0c\u5373\u7b97\u662f\u6211 \u5bf9\u8427\u97f5\u5170\u4e5f\u81ea\u6127\u4e0d\u5982\u3002\u201d \u3000\u3000\u6bd5\u51cc\u98ce\u7eed\u9053\uff1a\u201c\u6211\u4eec\u4e8c\u4eba\u81ea\u95ee\u672c\u4e8b\u4f4e\u5fae\uff0c\u8fdc\u8fdc\u4e0d\u662f\u725f\u72ec\u9038\u7684\u5bf9\u624b\uff0c\u76f8\u7ea6\u4ee5\u5341\u5e74\u4e3a\u671f\uff0c\u82e6 \u7ec3\u6b66\u529f\uff0c\u518d\u627e\u725f\u72ec\u9038\u4e00\u62fc\u3002\u201d \u3000\u3000\u201c\u4f46\u6211\u7b49\u4e0d\u5230\u5341\u5e74\uff0c\u5728\u9648\u5b9a\u65b9\u6b7b\u540e\u7684\u7b2c\u4e94\u5e74\uff0c\u6211\u5c31\u5355\u4eba\u53bb\u627e\u725f\u72ec\u9038\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u8fd9\u5374\u4e3a\u4f55\uff1f\u201d\u534e\u51cc\u98ce\u9053\uff1a\u201c\u90a3\u65f6\u5f20\u58eb\u8bda\u6218\u6b7b\u957f\u6c5f\uff0c\u6211\u7684\u54e5\u54e5\u548c\u5f6d\u548c\u5c1a\u7b49 \u4eba\u90fd\u6218\u6b7b\u4e86\u3002\u5f20\u58eb\u8bda\u7684\u519b\u4e2d\u4e09\u6770\u53ea\u6709\u4e91\u821e\u9633\u9003\u4e86\u51fa\u6765\u3002\u4e91\u821e\u9633\u7684\u59bb\u5b50\uff0c\u4e5f\u5c31\u662f\u9648\u5b9a\u65b9\u7684\u5973\u513f \u9648\u96ea\u6885\u542c\u8bf4\u4e5f\u5728\u957f\u6c5f\u4e4b\u6218\u4e2d\u6b7b\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u542c\u4e86\u8fd9\u6d88\u606f\u81ea\u7136\u5f88\u662f\u4f24\u5fc3\uff0c\u4f46\u53e6\u4e00\u4e2a\u66f4\u4ee4\u6211\u4f24\u5fc3\u7684\u6d88\u606f\u53c8\u4f20\u4e86\u6765\uff0c\u4e91\u821e\u9633\u5728\u7231\u59bb\u6b7b \u540e\u4e0d\u4e45\uff0c\u53c8\u505a\u4e86\u725f\u72ec\u9038\u7684\u4e58\u9f99\u4f73\u5a7f\u4e86\u3002\u201d \u3000\u3000\u201c\u4e91\u821e\u9633\u4e5f\u8bb8\u4e0d\u77e5\u9053\u4ed6\u7684\u5cb3\u7236\u7684\u6b7b\u56e0\uff0c\u6211\u5374\u603b\u66ff\u9648\u96ea\u6885\u89c9\u5f97\u4e0d\u503c\uff0c\u53ef\u601c\u5979\u5c38\u9aa8\u672a\u5bd2\uff0c\u4e08 \u592b\u5c31\u53e6\u5a36\u65b0\u4eba\uff0c\u800c\u4e14\u8fd8\u662f\u9648\u5b9a\u65b9\u4ec7\u4eba\u7684\u5973\u513f\uff01\u4e0d\u77e5\u600e\u7684\uff0c\u81ea\u6b64\u6211\u5c31\u5bf9\u4e91\u821e\u9633\u75db\u6068\u3002\u201d \u3000\u3000\u201c\u6211\u672c\u6765\u4ece\u6211\u54e5\u54e5\u90a3\u91cc\uff0c\u95f4\u63a5\u5b66\u5230\u4e86\u4e00\u70b9\u5f6d\u548c\u5c1a\u7684\u5c11\u9633\u7384\u529f\uff0c\u4e3a\u4e86\u6025\u4e8e\u6c42\u6210\uff0c\u6211\u820d\u5f03\u6b63 \u9014\uff0c\u5374\u82e6\u7ec3\u4e00\u79cd\u72ec\u95e8\u7684\u5947\u529f\uff1a\u5bd2\u9634\u4e03\u715e\u638c\uff0c\u82e5\u7136\u6ee1\u4e86\u5341\u5e74\uff0c\u81ea\u4fe1\u53ef\u4ee5\u5bf9\u4ed8\u4e00\u6d41\u9ad8\u624b\uff0c\u4f46\u6211\u7b49 \u4e0d\u53ca\u4e86\uff0c\u6211\u6015\u725f\u72ec\u9038\u53ef\u80fd\u5c06\u5251\u8c31\u4f20\u7ed9\u5973\u5a7f\uff0c\u6211\u90a3\u65f6\u867d\u7136\u75db\u6068\u4e91\u821e\u9633\uff0c\u4f46\u4e5f\u5374\u8fd8\u4e0d\u60f3\u6740\u6389\u9648\u96ea \u6885\u66fe\u7ecf\u5ac1\u8fc7\u7684\u4e08\u592b\u3002\u201d \u3000\u3000\u201c\u90a3\u4e00\u5e74\u6b63\u5de7\u725f\u72ec\u9038\u505a\u4e94\u5341\u4e00\u5c81\u7684\u5927\u5bff\uff0c\u6211\u6697\u4e2d\u4ee4\u4e10\u5e2e\u5f1f\u5b50\u4ee5\u4e5e\u8ba8\u4e3a\u540d\uff0c\u5c06\u725f\u5bb6\u5bb6\u4e2d\u7684 \u60c5\u51b5\u6253\u63a2\u5f97\u6e05\u6e05\u695a\u695a\u3002\u90a3\u4e00\u65e5\u6211\u4e5f\u6df7\u5728\u8d3a\u5ba2\u91cc\u5934\uff0c\u4e58\u7740\u725f\u72ec\u9038\u62db\u5f85\u5bbe\u5ba2\u7684\u65f6\u5019\uff0c\u6084\u6084\u7684\u6df7\u5165 \u4ed6\u7684\u5367\u623f\u3002\u201d \u3000\u3000\u201c\u6211\u672c\u6765\u60f3\u641c\u5bfb\u5251\u8c31\u7684\uff0c\u54ea\u77e5\u521a\u5bfb\u89c1\u90a3\u4e24\u4ef6\u7389\u73af\u2014\u2014\u5c31\u662f\u725f\u72ec\u9038\u4ece\u9648\u5b9a\u65b9\u5bb6\u4f20\u5b9d\u5251\u4e0a\u626f \u4e0b\u7684\u90a3\u4e24\u4ef6\u7389\u73af\uff0c\u4fbf\u542c\u5927\u95e8\u5916\u6709\u811a\u6b65\u58f0\uff0c\u6211\u614c\u5fd9\u8eb2\u8fdb\u5e8a\u5e95\u3002\u201d \u3000\u3000\u201c\u8fdb\u6765\u7684\u4e0d\u662f\u725f\u72ec\u9038\uff0c\u5374\u662f\u4e91\u821e\u9633\u548c\u4ed6\u7684\u65b0\u5a5a\u592b\u4eba\uff0c\u53ea\u542c\u5f97\u4e91\u821e\u9633\u8bf4\u9053\uff1a\u2018\u4f60\u5feb\u70b9\u641c\u90a3 \u672c\u5251\u8c31\uff0c\u6211\u5728\u5916\u9762\u5047\u5c71\u7b49\u4f60\uff0c\u6709\u751a\u53d8\u5316\uff0c\u6211\u7528\u54b3\u55fd\u4e3a\u53f7\u3002\u2019\u4e91\u821e\u9633\u8eab\u4e0a\u4f69\u6709\u957f\u5251\uff0c\u90a3\u6b63\u662f\u9648 \u5b9a\u65b9\u7684\u5bb6\u4f20\u5b9d\u5251\uff0c\u9648\u5b9a\u65b9\u6b7b\u540e\uff0c\u7279\u522b\u53eb\u8427\u51a0\u82f1\u9001\u53bb\u7ed9\u9648\u96ea\u6885\u7684\u3002\u6211\u89c1\u4e86\u4e0d\u7981\u5927\u8d77\u7591\u5fc3\u3002\u201d \u3000\u3000\u201c\u6211\u8ba4\u5f97\u8fd9\u628a\u5b9d\u5251\uff0c\u725f\u72ec\u9038\u81ea\u7136\u4e5f\u662f\u8ba4\u5f97\uff0c\u90a3\u4e48\u7eb5\u7136\u4ed6\u4e0d\u77e5\u9053\u4e91\u821e\u9633\u66fe\u662f\u9648\u5b9a\u65b9\u7684\u5973 \u5a7f\uff0c\u89c1\u4e86\u8fd9\u628a\u5b9d\u5251\uff0c\u4e5f\u5f53\u6709\u6240\u731c\u7591\uff0c\u4f55\u4ee5\u4ed6\u8fd8\u80af\u628a\u5973\u513f\u8bb8\u914d\u7ed9\u4ed6\uff1f\u201d \u3000\u3000\u201c\u5ffd\u542c\u5f97\u4e00\u58f0\u54b3\u55fd\uff0c\u4e91\u821e\u9633\u5728\u5916\u9762\u8f7b\u58f0\u53eb\u9053\uff1a\u2018\u5b9d\u73e0\uff0c\u5b9d\u73e0\uff01\u2019\u725f\u5b9d\u73e0\u6025\u5fd9\u6574\u7406\u597d\u7ffb\u4e71 \u7684\u4e1c\u897f\uff0c\u53ea\u89c1\u95e8\u5e18\u63ed\u5904\uff0c\u725f\u72ec\u9038\u548c\u4ed6\u7684\u4f84\u513f\u725f\u4e00\u7c9f\u8d70\u4e86\u8fdb\u6765\u3002\u201d \u3000\u3000\u201c\u725f\u72ec\u9038\u89c1\u4e86\u5973\u513f\uff0c\u4f3c\u662f\u9887\u4e3a\u5947\u602a\uff0c\u54a6\u4e86\u4e00\u58f0\u9053\uff1a\u2018\u539f\u6765\u4f60\u5728\u8fd9\u513f\uff1f\u821e\u9633\u5728\u5916\u9762\u627e\u4f60 \u5462\u3002\u2019\u725f\u5b9d\u73e0\u9053\uff1a\u2018\u6211\u6015\u7239\u7239\u7ed9\u5ba2\u4eba\u704c\u9189\u4e86\uff0c\u7279\u6765\u63a2\u671b\u3002\u821e\u9633\u627e\u6211\u505a\u4ec0\u4e48\uff1f\u2019\u725f\u72ec\u9038\u7b11\u9053\uff1a \u2018\u6211\u54ea\u80fd\u8fd9\u6837\u8f7b\u6613\u7684\u4fbf\u7ed9\u4ed6\u4eec\u704c\u9189\u4e86\uff0c\u55ef\uff0c\u821e\u9633\u5c31\u5728\u5916\u9762\uff0c\u95ee\u4ed6\u53bb\u5427\u3002\u2019\u201d \u3000\u3000\u201c\u725f\u5b9d\u73e0\u8d70\u540e\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u53ea\u542c\u5f97\u725f\u72ec\u9038\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u2018\u5973\u4e01\u5916\u5411\uff0c\u8fd9\u8bdd\u5f53\u771f\u4e0d \u5047\u3002\u4e00\u7c9f\uff0c\u4f60\u548c\u821e\u9633\u5728\u4e00\u8d77\u7684\u65f6\u5019\u591a\uff0c\u53ef\u77a7\u51fa\u4ec0\u4e48\u7834\u7efd\u4e48\uff1f\u2019\u201d \u3000\u3000\u201c\u725f\u4e00\u7c9f\u9053\uff1a\u2018\u5012\u6ca1\u6709\u53d1\u89c9\u4ec0\u4e48\u3002\u2019\u725f\u72ec\u9038\u4f38\u638c\u5728\u5899\u4e0a\u8f7b\u8f7b\u4e00\u62cd\uff0c\u5c06\u4e00\u5757\u7816\u5934\u62bd\u4e86\u51fa \u6765\uff0c\u53d6\u51fa\u4e00\u4e2a\u9526\u5323\uff0c\u653e\u5728\u684c\u4e0a\uff0c\u55d4\u7136\u53f9\u9053\uff1a\u2018\u4e3a\u4e86\u8fd9\u90e8\u5251\u8c31\uff0c\u9648\u5b9a\u65b9\u767d\u767d\u9001\u4e86\u4e00\u6761\u6027\u547d\uff0c\u8fd9 \u4e9b\u5e74\u6211\u4e5f\u63d0\u5fc3\u540a\u80c6\u3002\u2019\u201d \u3000\u3000\u201c\u4f60\u662f\u6211\u725f\u5bb6\u552f\u4e00\u7684\u7537\u4e01\uff0c\u8fd9\u90e8\u5251\u8c31\uff0c\u5c06\u6765\u81ea\u7136\u8981\u4f20\u6388\u7ed9\u4f60\uff0c\u8fbe\u6469\u5251\u6cd5\uff0c\u4ece\u4ee4\u4e4b\u540e\uff0c\u8981 \u6539\u79f0\u725f\u5bb6\u5251\u6cd5\u4e86\u3002\u4e00\u7c9f\uff0c\u4f60\u53ef\u77e5\u9053\u6211\u62db\u8d58\u4e91\u821e\u9633\u505a\u5973\u5a7f\u7684\u610f\u601d\u4e48\uff1f\u2019\u201d \u3000\u3000\u201c\u725f\u4e00\u7c9f\u9053\uff1a\u2018\u662f\u5440\uff0c\u6211\u6b63\u8981\u8bf7\u95ee\u53d4\u53d4\u3002\u2019\u725f\u72ec\u9038\u9053\uff1a\u2018\u5c31\u662f\u56e0\u4e3a\u4ed6\u7684\u524d\u59bb\u4e43\u662f\u9648\u5b9a\u65b9 \u7684\u5973\u513f\u3002\u9648\u5b9a\u65b9\u90a3\u5e74\u4e0e\u6211\u4e89\u593a\u8fd9\u90e8\u5251\u8c31\uff0c\u6211\u6599\u4ed6\u5fc5\u6b7b\u5728\u6211\u7684\u592a\u6e05\u795e\u638c\u4e4b\u4e0b\uff0c\u8fd9\u90e8\u5251\u8c31\uff0c\u9664\u4e86 \u9648\u5b9a\u65b9\u4e4b\u5916\uff0c\u6b66\u6797\u4e2d\u65e0\u4eba\u77e5\u9053\u662f\u5728\u6211\u624b\u4e0a\u3002\u53ef\u662f\u9648\u5b9a\u65b9\u8fd8\u6709\u5973\u513f\u5973\u5a7f\uff0c\u9648\u5b9a\u65b9\u4e34\u6b7b\u4e4b\u524d\uff0c\u4f1a \u4e0d\u4f1a\u544a\u8bc9\u4ed6\u4eec\uff0c\u8fd9\u4ef6\u4e8b\u4e00\u76f4\u662f\u6211\u5fc3\u4e2d\u7684\u7591\u95ee\u3002\u201d \u3000\u3000\u201c\u2018\u6211\u672c\u60f3\u628a\u4e91\u821e\u9633\u4e5f\u4e00\u5e76\u6740\u4e86\uff0c\u53ef\u662f\u6211\u8fd9\u4e00\u751f\u4ee5\u4fa0\u4e49\u81ea\u5f85\uff0c\u4ece\u672a\u6740\u9519\u8fc7\u4eba\uff0c\u8feb\u4e0d\u5f97\u5df2 \u6740\u4e86\u9648\u5b9a\u65b9\u5df2\u662f\u4e8e\u5fc3\u4e0d\u5fcd\uff0c\u53c8\u600e\u597d\u56e0\u5fc3\u4e2d\u7684\u731c\u7591\u518d\u53bb\u6740\u4eba\uff1f\u662f\u4ee5\u6211\u7279\u5730\u5c06\u5b9d\u73e0\u5ac1\u7ed9\u821e\u9633\uff0c\u597d \u63a2\u542c\u4ed6\u662f\u5426\u77e5\u9053\u4e2a\u4e2d\u79d8\u5bc6\uff0c\u6709\u4e86\u7fc1\u5a7f\u5173\u7cfb\uff0c\u4e5f\u597d\u4ece\u4e2d\u5316\u89e3\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u4e91\u821e\u9633\u6b64\u4eba\u5b9e\u5728\u9634\u6c89\u5f97\u4ee4\u4eba\u53ef\u6015\uff0c\u51e0\u4e2a\u6708\u6765\u6ca1\u6709\u9732\u8fc7\u534a\u53e3\u98ce\u3002\u6211\u53ea\u6015\u6211\u6b7b\u4e4b\u540e\u65e0 \u4eba\u80fd\u591f\u5236\u4ed6\uff0c\u5b9d\u73e0\u867d\u662f\u6211\u72ec\u751f\u5973\u513f\uff0c\u8fd9\u5251\u8c31\u6211\u5374\u4e0d\u60f3\u4e3a\u5916\u59d3\u6240\u6709\u3002\u662f\u4ee5\uff0c\u6211\u4eca\u665a\u7279\u522b\u5411\u4f60\u8a00 \u660e\uff0c\u4f60\u66ff\u6211\u4ed4\u7ec6\u7559\u5fc3\uff0c\u5bdf\u770b\u4ed6\u4eec\u5c0f\u4e24\u53e3\u5b50\u7684\u52a8\u9759\uff0c\u82e5\u6709\u4ec0\u4e48\u86db\u4e1d\u9a6c\u8ff9\uff0c\u4f60\u5f97\u8d76\u5feb\u544a\u8bc9\u6211\u77e5 \u9053\u3002\u5440\uff01\u4eca\u665a\u4e4b\u4e8b\uff0c\u5c31\u4ee4\u6211\u4e0d\u80fd\u65e0\u7591\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u542c\u5230\u8fd9\u513f\uff0c\u4e0d\u89c9\u6bdb\u9aa8\u609a\u7136\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5e08\u7956\u8d1f\u4e00\u4ee3\u4fa0\u540d\uff0c\u5374\u539f\u6765\u4e5f\u662f\u8fd9\u6837\u9634 \u9669\u5fcc\u523b\u3002\u8fd9\u8fbe\u6469\u5251\u8c31\u5f53\u771f\u662f\u4e0d\u7965\u4e4b\u7269\u3002\u201d\u53ea\u542c\u5f97\u6bd5\u51cc\u98ce\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u51fa\u7684\u8bdd\u521a\u597d\u4e0e\u4e0a\u5b98\u5929\u91ce \u6240\u60f3\u7684\u4e0d\u8c0b\u800c\u5408\u3002 \u3000\u3000\u6bd5\u51cc\u98ce\u53f9\u4e86\u53e3\u6c14\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u8fd9\u90e8\u5251\u8c31\u771f\u662f\u5bb3\u4eba\u4e0d\u6d45\uff0c\u6211\u773c\u89c1\u4e00\u4e2a\u4e2a\u6b66\u5b66\u5927\u5e08\u4e3a\u5b83\u4e27 \u751f\uff0c\u6211\u5f04\u6210\u8fd9\u526f\u4e11\u516b\u602a\u7684\u6a21\u6837\uff0c\u4e5f\u90fd\u662f\u7531\u5b83\u6240\u8d50\u3002\u201d \u3000\u3000\u6bd5\u51cc\u98ce\u7684\u8bf4\u8bdd\u8d8a\u6765\u8d8a\u5f31\uff0c\u58f0\u5c3e\u77ed\u4fc3\u6296\u98a4\uff0c\u90a3\u662f\u6c14\u6563\u795e\u6d6e\u4e4b\u8c61\uff0c\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u5e08\u7236\uff0c\u8fd9 \u4e9b\u4f24\u5fc3\u4e4b\u4e8b\uff0c\u4e0d\u63d0\u4e5f\u7f62\u3002\u201d \u3000\u3000\u4f46\u6bd5\u51cc\u98ce\u4ecd\u662f\u6323\u624e\u7740\u5f80\u4e0b\u7eed\u8bf4\uff1a\u201c\u4e0d\u4e45\uff0c\u725f\u4e00\u7c9f\u4e5f\u544a\u9000\u4e86\uff0c\u623f\u95f4\u91cc\u53ea\u5269\u4e0b\u725f\u72ec\u9038\u4e00\u4e2a \u4eba\uff0c\u90a3\u90e8\u5251\u8c31\u4ecd\u6446\u5728\u684c\u4e0a\u3002\u201d \u3000\u3000\u201c\u725f\u72ec\u9038\u659c\u501a\u5e8a\u4e0a\uff0c\u4e00\u53cc\u817f\u5c31\u5728\u6211\u7684\u9f3b\u5c16\u6643\u6765\u6643\u53bb\uff0c\u6211\u7d27\u5f20\u6781\u4e86\uff0c\u8fd9\u6b63\u662f\u6697\u7b97\u4ed6\u7684\u597d\u65f6 \u673a\uff01\u201d \u3000\u3000\u201c\u4e5f\u8bb8\u662f\u6211\u5728\u65e0\u610f\u4e4b\u4e2d\u53d1\u51fa\u58f0\u606f\uff0c\u5ffd\u542c\u5f97\u725f\u72ec\u9038\u4e00\u58f0\u559d\u9053\uff1a\u2018\u8c01\u5728\u5e8a\u4e0b\uff0c\u5feb\u6eda\u51fa\u6765\uff01\u2019 \u6211\u628a\u771f\u529b\u51dd\u805a\u638c\u5fc3\uff0c\u731b\u7684\u5411\u4ed6\u8db3\u8ddf\u4e00\u6293\uff0c\u6307\u7532\u5212\u7834\u4e86\u4ed6\u8db3\u8ddf\u7684\u6d8c\u6cc9\u7a74\uff0c\u9634\u5bd2\u4e4b\u6c14\uff0c\u5faa\u7740\u7a74\u9053 \u653b\u4e0a\u4ed6\u7684\u5fc3\u5934\u3002\u201d \u3000\u3000\u201c\u725f\u72ec\u9038\u867d\u662f\u4e00\u4ee3\u5927\u5e08\uff0c\u5374\u54ea\u91cc\u77e5\u9053\u6211\u8fd9\u79cd\u72ec\u95e8\u795e\u638c\u7684\u5947\u529f\uff0c\u4ed6\u6b66\u529f\u786e\u662f\u9ad8\u5f3a\u4e4b\u6781\uff0c\u88ab \u6211\u6293\u7740\u7a74\u9053\uff0c\u4f9d\u7136\u80fd\u591f\u8fd0\u529b\uff0c\u4e00\u4e2a\u8e6c\u811a\u5c31\u5c06\u6211\u6491\u5012\u4e86\u3002\u201d \u3000\u3000\u201c\u5f85\u4ed6\u770b\u6e05\u695a\u6211\u662f\u8c01\u65f6\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u2018\u539f\u6765\u662f\u7389\u9762\u4e10\u4fa0\u6bd5\u51cc\u98ce\uff0c\u4f60\u8eba\u5728\u6211\u7684\u5e8a\u4e0b\u505a \u751a\uff1f\u2019\u6211\u8bf4\uff1a\u5251\u8c31\u62ff\u6765\uff0c\u7ed9\u4f60\u89e3\u836f\u3002\u725f\u72ec\u9038\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u2018\u725f\u67d0\u5e73\u751f\u4ece\u4e0d\u6c42\u4eba\u3002\u518d\u8bf4\u4f60 \u8fd9\u70b9\u672c\u9886\uff0c\u7109\u80fd\u4f24\u5f97\u4e86\u6211\uff1f\u2019\u5ffd\u5730\u9762\u8272\u4e00\u53d8\uff0c\u53eb\u9053\uff1a\u2018\u4f60\u662f\u5728\u9ea6\u79ef\u5c71\u4e0a\u7684\u90a3\u4e00\u4e2a\u4eba\uff01\u2019\u60f3\u6765 \u4ed6\u5df2\u542c\u51fa\u6211\u7684\u58f0\u97f3\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u51b7\u4e0d\u9632\u7684\u53c8\u6251\u8fc7\u53bb\uff0c\u820d\u4e86\u6027\u547d\uff0c\u8fde\u5288\u4e09\u638c\uff0c\u725f\u72ec\u9038\u5927\u543c\u4e00\u58f0\uff0c\u4e00\u638c\u524a\u4e0b\uff0c\u5c06\u6211\u7684\u5de6 \u81c2\u9f50\u7740\u81c2\u5f2f\u524a\u65ad\uff0c\u731b\u7684\u62d4\u51fa\u5251\u6765\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u2018\u597d\uff0c\u5148\u7ed9\u4f60\u7559\u70b9\u8bb0\u53f7\u2019\u4f46\u89c9\u5251\u98ce\u98d2\u98d2\uff0c\u523a\u9762 \u751f\u5bd2\uff0c\u6211\u6025\u5fd9\u63a8\u7a97\u8df3\u51fa\uff0c\u725f\u4e00\u7c9f\u95fb\u58f0\u8d76\u6765\uff0c\u5374\u6ca1\u6709\u5c06\u6211\u6349\u4f4f\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u98a4\u58f0\u95ee\u9053\uff1a\u201c\u6211\u5e08\u7956\u5462\uff1f\u201d \u3000\u3000\u6bd5\u51cc\u98ce\u9053\uff1a\u201c\u725f\u72ec\u9038\u60f3\u662f\u8981\u6162\u6162\u5c06\u6211\u6298\u78e8\u7684\uff0c\u53ef\u662f\u4ed6\u88ab\u6211\u7684\u5bd2\u9634\u4e03\u715e\u638c\u6240\u4f24\uff0c\u5df1\u662f\u529b\u4e0d \u4ece\u5fc3\u4e86\u3002\u4ed6\u81ea\u6043\u5185\u529f\u6df1\u539a\uff0c\u4e0d\u8981\u6211\u7684\u89e3\u836f\uff0c\u90a3\u77e5\u9053\u5bd2\u9634\u4e4b\u6c14\u4fb5\u5165\u9aa8\u9ad3\uff0c\u4ed6\u8017\u5c3d\u529f\u529b\uff0c\u4e5f\u4e0d\u80fd \u9a71\u9664\u51c0\u5c3d\uff0c\u4ece\u6b64\u4ed6\u5c31\u5367\u75c5\u5728\u5e8a\uff0c\u7ec8\u4e8e\u5f04\u5f97\u8eab\u4f53\u6e10\u6e10\u8870\u5f31\uff0c\u529f\u529b\u8017\u5c3d\u4e4b\u540e\uff0c\u4e00\u671d\u66b4\u6bd9\u3002\u90a3\u65f6\u4e91 \u821e\u9633\u4e5f\u5df2\u5077\u8d70\u5251\u8c31\uff0c\u79bb\u5f00\u4e91\u5bb6\u4e86\u3002\u201d \u3000\u3000\u201c\u800c\u6211\u5462\uff0c\u5374\u6bd4\u725f\u72ec\u9038\u66f4\u60e8\uff0c\u53d8\u6210\u4e86\u8fd9\u6837\u4e00\u4e2a\u534a\u6b7b\u4e0d\u6d3b\uff0c\u6b8b\u5e9f\u5947\u4e11\u7684\u8001\u53eb\u5316\u3002\u4e00\u5207\u96c4\u5fc3 \u58ee\u5fd7\u3001\u79f0\u5f3a\u4e89\u9738\u4e4b\u5fc3\u4f0f\u864e\u964d\u9f99\u4e4b\u613f\uff0c\u5c3d\u90fd\u4ed8\u8bf8\u4e1c\u6d41\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u542c\u5f97\u4e0d\u5bd2\u800c\u6817\uff0c\u826f\u4e45\u826f\u4e45\uff0c\u6bd5\u51cc\u98ce\u58f0\u5636\u529b\u7aed\uff0c\u65ad\u65ad\u7eed\u7eed\u7684\u8bf4\u9053\uff1a\u201c\u73b0\u5728\u8fd9\u4e2a\u6545 \u4e8b\u4e5f\u5230\u4e86\u6536\u573a\u7684\u65f6\u5019\u4e86\u3002\u4e91\u821e\u9633\u4ed6\u4e2d\u4e86\u6211\u7684\u5bd2\u9634\u4e03\u715e\u638c\uff0c\u6700\u591a\u53ea\u80fd\u6d3b\u4e09\u5929\uff01\u4f60\u8d76\u5feb\u5230\u4e91\u5bb6\u53bb \u5427\uff0c\u628a\u4e91\u821e\u9633\u523b\u5728\u77f3\u5ba4\u7684\u5251\u8c31\u6284\u51fa\u6765\uff0c\u5c06\u90a3\u77f3\u58c1\u8c31\u5f0f\u6bc1\u4e86\uff0c\u4ee5\u540e\u4f60\u5c31\u662f\u8fbe\u6469\u5251\u8c31\u7684\u552f\u4e00\u4f20\u4eba \u4e86\uff01\u5feb\u53bb\uff0c\u5feb\u53bb\uff01\u4f60\u6015\u4ec0\u4e48\uff01\u4e91\u821e\u9633\u7eb5\u6709\u5929\u5927\u795e\u901a\uff0c\u4e5f\u4e0d\u80fd\u5948\u4f55\u4f60\u4e86\uff01\u201d \u3000\u3000------------- \u3000\u3000\u91cd\u5e86\u96ea\u513f \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u8fd8\u5251\u5947\u60c5\u5f55\u300b\u2014\u2014\u7b2c\u5341\u4e09\u56de\u3000\u91cd\u91cd\u51a4\u5b7d \u6881\u7fbd\u751f\u300a\u8fd8\u5251\u5947\u60c5\u5f55\u300b \u7b2c\u5341\u4e09\u56de\u3000\u91cd\u91cd\u51a4\u5b7d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u53eb\u9053\uff1a\u201c\u5c0f\u8981\u518d\u63d0\u8fd9\u90e8\u5251\u8c31\u4e86\uff0c\u8c01\u6cbe\u60f9\u4e0a\u5b83\u90fd\u6ca1\u6709\u597d\u4e0b\u573a\uff0c\u5e08\u7236\uff0c\u54b1\u4eec\u8fd8\u662f\u8d76 \u5feb\u79bb\u5f00\u8fd9\u9b3c\u5730\u65b9\u5427\u3002\u201d \u3000\u3000\u6bd5\u51cc\u98ce\u5634\u5507\u5f00\u5408\uff0c\u4e0a\u5b98\u5929\u91ce\u597d\u4e0d\u5bb9\u6613\u624d\u542c\u51fa\u4ed6\u8bf4\u4ec0\u4e48\u3002\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u98a4\u58f0\u53eb\u9053\uff1a \u201c\u5e08\u7236\uff0c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u4f60\u4e5f\u7ed9\u4e91\u821e\u9633\u7684\u4e00\u6307\u7985\u529f\u4f24\u4e86\u5fc3\u810f\uff0c\u5c31\u8981\u8d70\u4e86\u3002\u201d\u4f46\u89c1\u6bd5\u51cc\u98ce\u70b9\u4e86\u70b9 \u5934\uff0c\u8138\u4e0a\u5ffd\u7136\u9732\u51fa\u4e00\u4e1d\u60e8\u7b11\uff0c\u624b\u6307\u6307\u5411\u4e91\u5bb6\uff0c\u4e0d\u4e45\uff0c\u90a3\u7b11\u5bb9\u4e5f\u4f3c\u51dd\u7ed3\u4e86\u8d77\u6765\uff0c\u4e0a\u5b98\u5929\u91ce\u4e0a\u524d \u4e00\u6478\uff0c\u5e08\u7236\u7684\u6c14\u606f\u65e9\u5df2\u6ca1\u4e86\uff01 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u4f46\u89c9\u547c\u5438\u7a92\u606f\uff0c\u5fc3\u5934\u90c1\u95f7\u4e4b\u6781\uff0c\u60f3\u54ed\u7adf\u7136\u54ed\u4e0d\u51fa\u6765\uff0c\u4ed6\u628a\u4e00\u5806\u5806\u7684\u6811\u53f6\u6ce5\u571f\u6252 \u4e86\u8fc7\u6765\uff0c\u8986\u5728\u6bd5\u51cc\u98ce\u7684\u5c38\u4f53\u4e0a\uff0c\u5ffd\u5730\u5583\u5583\u8bf4\u9053\uff1a\u201c\u9648\u5b9a\u65b9\uff0c\u9648\u5b9a\u65b9\uff01\u201d\u8fd9\u540d\u5b57\u597d\u719f\uff0c\u662f\u8c01\u66fe \u5411\u4ed6\u8bf4\u8fc7\u5462\uff1f \u3000\u3000\u5ffd\u542c\u5f97\u6811\u6797\u4e2d\u4e00\u58f0\u5c16\u53eb\uff0c\u4e00\u6761\u4eba\u5f71\u76f4\u5411\u4e91\u5bb6\u5954\u53bb\u3002\u4e0a\u5b98\u5929\u91ce\u53eb\u9053\uff1a\u201c\u4e91\u7d20\u7d20\uff01\u201d\u4e91\u7d20\u7d20 \u5374\u6ca1\u6709\u56de\u5934\uff0c\u6562\u60c5\u662f\u5979\u628a\u5e08\u7236\u7684\u8bdd\u90fd\u5077\u542c\u53bb\u4e86\uff1f\u5440\uff0c\u5979\u65e2\u7136\u8eb2\u5728\u8fd9\u513f\uff0c\u5374\u4e3a\u4ec0\u4e48\u4e0d\u80af\u51fa\u6765\u4e0e \u6211\u89c1\u9762\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u5fc3\u4e2d\u5ffd\u7136\u4e00\u9635\u6296\u98a4\uff0c\u6025\u6025\u8ffd\u8e2a\u4e91\u7d20\u7d20\u7684\u80cc\u5f71\u2026\u2026 \u3000\u3000\u4e91\u821e\u9633\u7b49\u4e86\u8bb8\u4e45\uff0c\u5973\u513f\u8fd8\u6ca1\u6709\u56de\u6765\uff0c\u4ed6\u628a\u7a97\u95e8\u5168\u90e8\u6253\u5f00\uff0c\u8ba9\u6708\u5149\u548c\u6885\u5f71\u4fb5\u5165\u4e66\u623f\uff0c\u6708\u4eae \u5df2\u5230\u5929\u5fc3\uff0c\u591c\u5df2\u6df1\u4e86\uff0c\u591c\u98ce\u7a7f\u6237\uff0c\u96f6\u843d\u7684\u6885\u82b1\u8fd8\u6709\u6de1\u6de1\u5e7d\u9999\uff0c\u892a\u4e86\u8272\u7684\u8bb0\u5fc6\u4ecd\u7136\u6298\u78e8\u7740\u4ed6\u7684 \u5fc3\u3002 \u3000\u3000\u5f80\u4e8b\u53c8\u4e00\u6b21\u7684\u5728\u5fc3\u4e0a\u7ffb\u817e\uff0c\u751f\u5e73\u79cd\u79cd\u884c\u4e8b\uff0c\u5584\u5584\u6076\u6076\uff0c\u7535\u5149\u77f3\u706b\u822c\u7684\u5728\u5fc3\u5934\u4e00\u4e00\u63a0\u8fc7\uff0c \u4e91\u821e\u9633\u5728\u6c89\u601d\u4e2d\u5ffd\u7136\u88ab\u8f7b\u5fae\u7684\u811a\u6b65\u58f0\u60ca\u9192\u3002 \u3000\u3000\u201c\u7d20\u7d20\u2026\u2026\u54a6\uff0c\u4f60\u2026\u2026\u201d\u8fd9\u4e0d\u662f\u7d20\u7d20\uff0c\u662f\u4e00\u4e2a\u9762\u4e0a\u6709\u4e00\u9053\u4f24\u75d5\uff0c\u77ed\u987b\u5982\u671d\u7684\u4e94\u5341\u6765\u5c81\u7684 \u7c97\u8c6a\u6c49\u5b50\u3002 \u3000\u3000\u4e91\u821e\u9633\u8bb0\u4e86\u8d77\u6765\uff0c\u201c\u4f60\u662f\u98de\u9f99\u5e2e\u7684\u5e2e\u4e3b\u8427\u51a0\u82f1\uff1f\u201d\u90a3\u6c49\u5b50\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4f60\u8bb0\u6027\u4e0d\u9519\uff0c\u4f60 \u548c\u6211\u4eec\u7684\u5927\u5c0f\u59d0\u6210\u5a5a\u4e4b\u65f6\uff0c\u6211\u66fe\u4e3a\u4f60\u4eec\u8dd1\u8fc7\u817f\uff0c\u529e\u8fc7\u559c\u7b75\u3002\u4e0d\u8fc7\uff0c\u4f60\u65e9\u5df2\u662f\u725f\u5bb6\u7684\u59d1\u7239\uff0c\u4e0d \u518d\u662f\u9648\u5bb6\u7684\u59d1\u7239\u4e86\uff0c\u54c8\uff0c\u96be\u4e3a\u4f60\u8fd8\u8bb0\u5f97\u6211\u4eec\uff01\u201d \u3000\u3000\u8fd9\u8bdd\u5982\u5632\u4f3c\u8bbd\uff0c\u4e91\u821e\u9633\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u4ec0\u4e48\uff1f\u201d\u8427\u51a0\u82f1\u9053\uff1a\u201c\u6211\u4e00\u6765\u8981\u5251\u8c31\uff0c\u4e8c\u6765\u8981 \u7d22\u4eba\u3002\u201d \u3000\u3000\u4e91\u821e\u9633\u4ef0\u5929\u5927\u7b11\u9053\uff1a\u201c\u53c8\u662f\u4e00\u4e2a\u8981\u5251\u8c31\u7684\uff01\u54c8\uff0c\u4f60\u4e5f\u914d\u8981\u8fd9\u90e8\u8fbe\u6469\u5251\u8c31\uff1f\u201d\u8427\u51a0\u82f1\u9053\uff1a \u201c\u6211\u4eec\u7684\u5927\u5c0f\u59d0\u82e5\u7136\u4e0d\u6b7b\uff0c\u8fd9\u90e8\u5251\u8c31\u81ea\u5f53\u5c5e\u4f60\u3002\u4f46\u4f60\u73b0\u5728\u5df2\u662f\u725f\u5bb6\u7684\u5973\u5a7f\uff0c\u725f\u5bb6\u7684\u5251\u8c31\u5077\u81ea \u9648\u5bb6\uff0c\u9648\u5b9a\u65b9\u53ea\u6709\u6211\u8fd9\u4e2a\u8bb0\u540d\u5f1f\u5b50\uff0c\u8fd9\u5251\u8c31\u5c82\u80fd\u7559\u5728\u9648\u5b9a\u65b9\u4ec7\u4eba\u5973\u5a7f\u7684\u624b\u4e2d\uff1f\u201d \u3000\u3000\u4e91\u821e\u9633\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u5251\u8c31\u6211\u4e5f\u4e0d\u80fd\u5e26\u5230\u575f\u5893\u91cc\u53bb\uff0c\u53ef\u662f\u600e\u4e48\u8bf4\u4e5f\u8fd8\u8f6e\u4e0d\u5230\u4f60\u3002\u4eba\u5462\uff0c\u4f60 \u8981\u7d22\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u8427\u51a0\u82f1\u9053\uff1a\u201c\u6bd5\u51cc\u98ce\uff01\u201d\u4e91\u821e\u9633\u6253\u4e86\u4e00\u4e2a\u5bd2\u5664\uff0c\u63a5\u7740\u53c8\u662f\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u8427\u51a0\u82f1\u6012\u9053\uff1a\u201c\u4e91\u821e\u9633\u4f60\u7b11\u4ec0\u4e48\uff1f\u201d\u4e91\u821e\u9633\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u6bd5\u51cc\u98ce\u8fd9\u4e2a\u4e56\u50fb\u7684\u602a\u7269\uff0c\u5c45\u7136 \u8fd8\u6709\u4f60\u8fd9\u4e2a\u77e5\u5fc3\u670b\u53cb\u66ff\u4ed6\u6536\u5c38\uff01\u201d\u8427\u51a0\u82f1\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u6bd5\u51cc\u98ce\u6b7b\u4e86\uff1f\u201d\u4e91\u821e\u9633\u6de1\u6de1\u8bf4\u9053\uff1a \u201c\u6bd5\u51cc\u98ce\u88ab\u6211\u7528\u4e00\u6307\u7985\u529f\u95ed\u4e86\u4e03\u5904\u9690\u7a74\uff0c\u6599\u60f3\u4e0d\u80fd\u751f\u51fa\u6b64\u5c71\uff0c\u4f60\u7528\u4e0d\u7740\u82b1\u4e00\u5929\u5de5\u592b\uff0c\u641c\u904d\u8fd9 \u5468\u56f4\u5341\u91cc\u7684\u5c71\u5934\uff0c\u5b9a\u5f53\u53d1\u73b0\u4ed6\u7684\u9ab8\u9aa8\uff01\u201d \u3000\u3000\u8427\u51a0\u82f1\u773c\u775b\u53d1\u9ed1\uff0c\u4f24\u5fc3\u3001\u6124\u6012\uff0c\u5230\u4e86\u6781\u70b9\uff0c\u84e6\u7136\u72c2\u7b11\u9053\uff1a\u201c\u4e91\u821e\u9633\uff0c\u4f60\uff0c\u4f60\u597d\u2026\u2026\u4f60\u597d \u4e0b\u5f97\u8fa3\u624b\u5475\uff01\u6bd5\u5927\u54e5\u5440\u6bd5\u5927\u54e5\uff0c\u60f3\u5f53\u5e74\u4f60\u6211\u4e00\u540c\u9886\u53d7\u6211\u6069\u5e08\u7684\u9057\u547d\uff0c\u8a93\u613f\u7c89\u8eab\u788e\u9aa8\u4e5f\u8981\u8ffd\u8fd8 \u8fd9\u90e8\u8fbe\u6469\u5251\u8c31\uff0c\u4f60\u5f53\u771f\u662f\u541b\u5b50\u4e00\u8bfa\uff0c\u751f\u6b7b\u4e0d\u8c15\uff0c\u4f46\u60f3\u4e0d\u5230\u4f60\u4e0d\u6b7b\u5728\u725f\u8001\u8d3c\u7684\u5251\u4e0b\u5374\u6b7b\u5728\u66fe\u662f \u9648\u5bb6\u4f73\u5a7f\u7684\u4e91\u821e\u9633\u624b\u4e0a\uff01\u6069\u5e08\u5440\u6069\u5e08\uff0c\u6bd5\u5927\u54e5\u5440\u6bd5\u5927\u54e5\uff0c\u4f60\u4eec\u4e8c\u4eba\u5728\u6cc9\u4e0b\u5c82\u80fd\u7791\u76ee\uff1f\u6bd5\u5927\u54e5 \u4f60\u662f\u5916\u4eba\u5374\u5148\u6211\u800c\u6b7b\uff0c\u5c82\u4e0d\u6127\u715e\u6211\u8fd9\u4e2a\u672c\u95e8\u5f1f\u5b50\u4e48\uff1f\u201d \u3000\u3000\u8fd9\u72c2\u7b11\u6709\u5982\u5229\u7bad\uff0c\u542c\u8d77\u6765\u6bd4\u75db\u54ed\u5492\u9a82\u8fd8\u66f4\u96be\u53d7\uff0c\u4e91\u821e\u9633\u8fd9\u624d\u660e\u767d\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6211\u9053\u6bd5 \u51cc\u98ce\u4e0e\u6211\u4e08\u4eba\u98ce\u9a6c\u725b\u7d20\u4e0d\u76f8\u6d89\uff0c\u4f55\u4ee5\u6709\u6b64\u6df1\u4ec7\u5927\u6068\uff0c\u5374\u539f\u6765\u90fd\u662f\u4e3a\u4e86\u8fd9\u90e8\u5251\u8c31\u3002\u201d \u3000\u3000\u4f46\u89c1\u8427\u51a0\u82f1\u72e0\u72e0\u7684\u76ef\u7740\u4ed6\uff0c\u4e91\u821e\u9633\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8427\u51a0\u82f1\u4f60\u5f53\u771f\u8981\u4e0e\u6211\u52a8\u624b\u4e48\uff1f\u201d \u3000\u3000\u8427\u51a0\u82f1\u662f\u8ffd\u8e2a\u5973\u513f\u6765\u7684\uff0c\u539f\u6765\u4ed6\u6d3e\u51fa\u7987\u82f1\u7987\u9738\u7b49\u56db\u4eba\u4e4b\u540e\uff0c\u5ffd\u7136\u6253\u63a2\u5230\u4e00\u4e2a\u6d88\u606f\uff0c\u8bf4\u662f \u4e0a\u5b98\u5929\u91ce\u4e0e\u4e00\u4e2a\u540d\u53eb\u9648\u7384\u673a\u7684\u671d\u5ef7\u53db\u9006\u5e38\u5728\u4e00\u8d77\uff0c\u800c\u81ea\u5df1\u7684\u5973\u513f\u548c\u8fd9\u4e24\u4eba\u90fd\u662f\u670b\u53cb\uff0c\u9648\u7384\u673a \u6b63\u5728\u88ab\u5927\u5185\u9ad8\u624b\u8ffd\u8e2a\u4e4b\u4e2d\u3002 \u3000\u3000\u8427\u51a0\u82f1\u4e00\u6765\u6015\u4e0a\u5b98\u5929\u91ce\u52fe\u5f15\u4e86\u4ed6\u7684\u5973\u513f\uff0c\u4e8c\u6765\u6015\u5728\u5927\u5185\u9ad8\u624b\u8ffd\u8e2a\u4e4b\u4e0b\uff0c\u6b83\u53ca\u6c60\u9c7c\uff0c\u800c\u7987 \u82f1\u7987\u9738\u7b49\u65e0\u529b\u76f8\u62a4\uff0c\u662f\u4ee5\u4e5f\u6025\u6025\u8ffd\u8e2a\u800c\u6765\u3002\u4ed6\u672c\u6765\u4e0d\u77e5\u9053\u4e91\u821e\u9633\u85cf\u5728\u6b64\u5c71\uff0c\u8fdb\u5c71\u4e4b\u540e\uff0c\u5ffd\u7136 \u53d1\u73b0\u6bd5\u51cc\u98ce\u7684\u62d0\u5370\uff0c\u4ed6\u4e0e\u6bd5\u51cc\u98ce\u4e5f\u6709\u5341\u591a\u5e74\u672a\u89c1\u9762\u4e86\uff0c\u6599\u60f3\u4ed6\u5728\u6b64\u5c71\u51fa\u73b0\uff0c\u5fc5\u6709\u539f\u56e0\uff0c\u4fbf\u8ddf \u7740\u62d0\u5370\uff0c\u4e00\u8def\u8ffd\u67e5\uff0c\u67e5\u5230\u4e91\u5bb6\uff0c\u610f\u5916\u7684\u53d1\u89c1\u4e86\u4e91\u821e\u9633\uff0c\u800c\u4e14\u66f4\u610f\u5916\u7684\u542c\u5230\u4e86\u6bd5\u51cc\u98ce\u7684\u5669\u8017\uff01 \u3000\u3000\u8427\u51a0\u82f1\u662f\u9648\u5b9a\u65b9\u4e00\u624b\u63d0\u62d4\u7684\uff0c\u867d\u7136\u9648\u5b9a\u65b9\u53ea\u80af\u6536\u4ed6\u505a\u8bb0\u540d\u5f1f\u5b50\uff0c\u4f46\u4e5f\u4f20\u6388\u4e86\u4ed6\u4e0d\u5c11\u6b66 \u529f\uff0c\u800c\u4e14\u6276\u52a9\u4ed6\u505a\u5230\u4e86\u5317\u4e94\u7701\u7eff\u6797\u7684\u9b41\u9996\u3002\u8427\u51a0\u82f1\u60f3\u8d77\u5e08\u6069\uff0c\u60f3\u8d77\u5f53\u5e74\u7684\u9057\u547d\uff0c\u60f3\u8d77\u6bd5\u51cc\u98ce \u662f\u4e2a\u5916\u4eba\u4e5f\u6177\u6168\u8d74\u4e49\uff0c\u66f4\u4e0d\u5fd8\u4e91\u821e\u9633\u7684\u5fd8\u4e86\u9648\u5bb6\u60c5\u4e49\uff0c\u4e0d\u4f46\u6539\u5a36\u4e86\u725f\u72ec\u9038\u7684\u5973\u513f\uff0c\u800c\u4e14\u8fd8\u6253 \u6b7b\u4e86\u6bd5\u51cc\u98ce\u3002\u987f\u65f6\u95f4\u8840\u8109\u6124\u5f20\uff0c\u628a\u751f\u6b7b\u7f6e\u4e4b\u5ea6\u5916\uff0c\u51b2\u7740\u4e91\u821e\u9633\u53eb\u9053\uff1a\u201c\u6211\u5bf9\u725f\u72ec\u9038\u5c1a\u4e14\u4e0d \u4ff1\uff0c\u6015\u4f60\u4f55\u6765\uff0c\u597d\uff0c\u4f60\u6709\u672c\u4e8b\u5c31\u5c06\u6211\u4e00\u5e76\u6740\u4e86\uff01\u201d\u8f6c\u8fc7\u8eab\u6765\uff0c\u6b63\u597d\u5bf9\u7740\u4e91\u821e\u9633\uff0c\u4ed6\u8138\u4e0a\u7684\u90a3 \u9053\u4f24\u75d5\uff0c\u4e5f\u6b63\u662f\u88ab\u725f\u72ec\u9038\u7684\u5229\u5251\u5212\u4e0b\u6765\u7684\uff01 \u3000\u3000\u4e91\u821e\u9633\u51b7\u51b7\u7684\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u8981\u7ed9\u6bd5\u51cc\u98ce\u62a5\u4ec7\uff0c\u8fd9\u6b63\u662f\u5927\u597d\u7684\u826f\u673a\uff0c\u54c8\uff0c\u4f60\u600e\u4e48 \u8fd8\u4e0d\u4e0b\u624b\u5440\uff1f\u201d \u3000\u3000\u8427\u51a0\u82f1\u5927\u543c\u4e00\u58f0\uff0c\u53cd\u624b\u4e00\u638c\uff0c\u4e00\u62db\u201c\u529b\u5288\u534e\u5c71\u201d\uff0c\u4fbf\u5411\u4e91\u821e\u9633\u9876\u95e8\u62cd\u53bb\uff0c\u4ed6\u81ea\u77e5\u4e0d\u662f\u4e91 \u821e\u9633\u7684\u5bf9\u624b\uff0c\u8fd9\u4e00\u638c\u5b9e\u662f\u8fd0\u4e86\u5168\u8eab\u529f\u529b\uff0c\u62fc\u4e2a\u4e24\u8d25\u4ff1\u4f24\u7684\u6253\u6cd5\u3002\u4f46\u89c1\u4e91\u821e\u9633\u7aef\u5750\u4e0d\u52a8\uff0c\u8138\u4e0a \u7684\u795e\u8272\u975e\u5e38\u602a\u5f02\uff0c\u7adf\u4f3c\u4e1d\u6beb\u4e0d\u60f3\u62db\u67b6\u4f3c\u7684\uff0c\u8427\u51a0\u82f1\u6014\u4e86\u4e00\u6014\uff0c\u773c\u5149\u4e00\u77a5\uff0c\u53ea\u89c1\u4e91\u821e\u9633\u7684\u8138\u4e0a \u7b3c\u7f69\u7740\u4e00\u5c42\u6de1\u6de1\u7684\u7d2b\u6c14\uff0c\u76ee\u5149\u5446\u6ede\uff0c\u73b0\u51fa\u6b7b\u9c7c\u4e00\u822c\u7684\u989c\u8272\u3002 \u3000\u3000\u8427\u51a0\u82f1\u5931\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u4e5f\u4e2d\u4e86\u6bd5\u51cc\u98ce\u7684\u5bd2\u9634\u4e03\u715e\u638c\uff01\u201d \u3000\u3000\u4e91\u821e\u9633\u51b7\u7b11\u9053\uff1a\u201c\u6240\u4ee5\u6211\u8bf4\u8fd9\u662f\u4f60\u767e\u5e74\u96be\u9047\u7684\u826f\u673a\uff0c\u54c8\uff0c\u4f60\u600e\u4e48\u8fd8\u4e0d\u4e0b\u624b\uff1f\u4f60\u6740\u4e86\u6211\uff0c \u51c6\u4fdd\u4f60\u80fd\u9707\u52a8\u6b66\u6797\uff0c\u4ece\u4eca\u4e4b\u540e\uff0c\u4f60\u5c31\u662f\u5929\u4e0b\u7b2c\u4e00\u6761\u597d\u6c49\uff01\u201d \u3000\u3000\u8427\u51a0\u82f1\u7684\u624b\u638c\u5212\u4e86\u534a\u9053\u5706\u5f27\uff0c\u505c\u5728\u4e91\u821e\u9633\u5934\u9876\u4e0a\u7a7a\uff0c\u8fdf\u8fdf\u4e0d\u6562\u51fb\u4e0b\uff0c\u4ed6\u5fc3\u4e2d\u4e5f\u6b63\u81ea\u8e0c\u8e87 \u96be\u51b3\uff0c\u8981\u77e5\u4ed6\u4e5f\u662f\u6c5f\u6e56\u4e0a\u6709\u6570\u7684\u4eba\u7269\uff0c\u600e\u80fd\u6740\u6b7b\u4e00\u4e2a\u6beb\u65e0\u62b5\u6297\u80fd\u529b\u7684\u4eba\uff0c\u4f46\u82e5\u7136\u4e0d\u6740\uff0c\u53ef\u80fd \u5f53\u771f\u662f\u201c\u9519\u8fc7\u826f\u673a\u201d\uff0c\u4e07\u4e00\u4e91\u821e\u9633\u4f11\u517b\u590d\u539f\uff0c\u5929\u4e0b\u65e0\u4eba\u80fd\u5236\uff01 \u3000\u3000\u8fd9\u5239\u90a3\u95f4\uff0c\u8427\u51a0\u82f1\u5fc3\u4e2d\u8f6c\u4e86\u65e0\u6570\u5ff5\u5934\uff0c\u7a81\u5730\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4e91\u821e\u9633\u4f60\u4e0d\u5fc5\u6fc0\u6211\uff0c\u6211\u5c31\u62fc\u7740 \u53d7\u5929\u4e0b\u82f1\u96c4\u803b\u7b11\uff0c\u4eca\u65e5\u4e5f\u5f97\u6740\u4e86\u4f60\u8fd9\u5fd8\u6069\u8d1f\u4e49\u4e4b\u5f92\uff01\u201d \u3000\u3000\u8427\u51a0\u82f1\u8bdd\u51fa\u53e3\uff0c\u624b\u8155\u4e00\u7ffb\uff0c\u638c\u5fc3\u7f13\u7f13\u5411\u4e91\u821e\u9633\u9876\u95e8\u538b\u4e0b\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u5343\u94a7\u4e00\u53d1\u4e4b\u65f6\uff0c\u5ffd\u542c\u5f97\u6709\u8427\u58f0\u7ec6\u7ec6\uff0c\u8fdc\u8fdc\u4f20\u6765\u2026\u2026 \u3000\u3000\u90a3\u8427\u58f0\u97f3\u7ec6\u800c\u9ad8\uff0c\u5148\u662f\u4e00\u7247\u6b22\u60a6\u4e4b\u97f3\uff0c\u4e25\u5982\u706f\u524d\u513f\u5973\uff0c\u6d45\u7b11\u76c8\u76c8\uff0c\u53c8\u5982\u7231\u4fa3\u540c\u884c\uff0c\u5581\u5581 \u79c1\u8bed\uff1b\u53ea\u4e00\u77ac\u95f4\uff0c\u8427\u58f0\u500f\u53d8\uff0c\u6709\u5982\u695a\u5ba2\u60b2\u6b4c\uff0c\u957f\u4ead\u6ce3\u522b\uff0c\u97f3\u8c03\u8d8a\u6765\u8d8a\u82e6\uff0c\u7adf\u7136\u5145\u6ee1\u4e86\u751f\u79bb\u6b7b \u522b\u4e4b\u6068\uff0c\u5f81\u4eba\u6028\u5987\u4e4b\u4f24\u3002 \u3000\u3000\u8fd9\u4e00\u77ac\u95f4\uff0c\u7a7a\u6c14\u597d\u4f3c\u8981\u51b7\u5f97\u51dd\u7ed3\u8d77\u6765\uff0c\u5c91\u5bc2\u5982\u6b7b\u2026\u2026 \u3000\u3000\u4e91\u821e\u9633\u6d51\u8eab\u98a4\u6296\uff0c\u8427\u51a0\u82f1\u9762\u8272\u7070\u767d\uff0c\u8fd9\u4e00\u638c\u54ea\u8fd8\u80fd\u518d\u6253\u4e0b\u53bb\uff01 \u3000\u3000\u9661\u7136\u95f4\uff0c\u8427\u51a0\u82f1\u5c16\u53eb\u4e00\u58f0\uff0c\u8df3\u51fa\u5ead\u9662\u3002\u4e91\u821e\u9633\u4ecd\u7136\u7aef\u5750\u4e66\u623f\u3002\u597d\u50cf\u5931\u6389\u4e86\u751f\u547d\u7684\u77f3\u50cf\uff01 \u3000\u3000\u53ea\u542c\u5f97\u8427\u51a0\u82f1\u5728\u9662\u5b50\u5916\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u5927\uff0c\u5927\u5c0f\u59d0\uff0c\u8fd9\uff0c\u8fd9\u8fd9\u4e0d\u662f\u68a6\u5417\uff1f\u201d \u3000\u3000\u662f\u5440\uff0c\u201c\u8fd9\u4e0d\u662f\u68a6\u5417\uff1f\u201d\u7adf\u7136\u662f\u8fd9\u6837\u719f\u6089\u7684\u8427\u58f0\uff0c\u4e91\u821e\u9633\u597d\u50cf\u91cd\u56de\u4e09\u5341\u591a\u5e74\u4e4b\u524d\uff0c\u90a3\u65f6 \u4ed6\u548c\u9648\u96ea\u6885\u8fd8\u662f\u4e00\u5bf9\u9752\u6885\u7af9\u9a6c\u7684\u4f34\u4fa3\uff0c\u96ea\u6885\u5c31\u7231\u5728\u6885\u82b1\u676f\u91cc\u5439\u8427\uff0c\u4e0d\u8fc7\u90a3\u65f6\u7684\u8427\u58f0\u7edd\u4e0d\u662f\u8fd9 \u6837\u60b2\u82e6\u7684\u60c5\u8c03\uff01 \u3000\u3000\u7136\u800c\u8fd9\u4e0d\u662f\u68a6\uff0c\u53ea\u542c\u5f97\u4e00\u4e2a\u9694\u522b\u5df2\u4e45\u4ee4\u4eba\u5fc3\u5f26\u98a4\u6296\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u6211\u56de\u6765\u4e86\u3002 \u4f60\u6765\u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u201d\u8427\u51a0\u82f1\u9053\uff1a\u201c\u6211\uff0c\u6211\uff0c\u5251\u8c31\uff0c\u6bd5\uff0c\u6bd5\u51cc\u98ce\uff0c\u4ed6\uff0c\u4ed6\u4e0e\u6211\u9886\u53d7\u4e86\u4f60\uff0c\u4f60\u7239 \u7239\u7684\u9057\u547d\uff0c\u8981\u8ffd\u8fd8\u8fd9\u90e8\u8fbe\u6469\u5251\u8c31\uff0c\u4ea4\u7ed9\u4f60\u7684\u3002\u6bd5\uff0c\u6bd5\u51cc\u98ce\u4ed6\u56e0\u6b64\u6b7b\u5566\u3002\u201d\u58f0\u97f3\u98a4\u6218\u65ad\u7eed\uff0c\u663e \u89c1\u4ed6\u5fc3\u4e2d\u7684\u60ca\u6050\u3002\u53ef\u662f\u4e91\u821e\u9633\u6bd4\u4ed6\u8fd8\u8981\u60ca\u6050\u767e\u500d\u3001\u5343\u500d\uff0c\u8fd9\u4e00\u77ac\u95f4\u4ed6\u4f46\u89c9\u4e00\u7247\u832b\u7136\uff0c\u597d\u50cf\u77e5 \u89c9\u4e5f\u5931\u6389\u4e86\uff01 \u3000\u3000\u4e91\u821e\u9633\u5728\u4e00\u751f\u4e4b\u4e2d\u4e0d\u77e5\u7ecf\u5386\u8fc7\u591a\u5c11\u9669\u96be\uff0c\u906d\u9022\u8fc7\u65e0\u6570\u5f3a\u654c\u3002\u4f46\u5374\u4ece\u65e0\u4e00\u523b\u4f3c\u73b0\u5728\u8fd9\u822c\u7684 \u4ee4\u4ed6\u611f\u5230\u81ea\u5df1\u7684\u8f6f\u5f31\uff0c\u4ece\u65e0\u4e00\u4e2a\u4eba\u4f3c\u9662\u5b50\u5916\u8fd9\u4e2a\u5973\u4eba\u4ee4\u4ed6\u611f\u5230\u5fc3\u60b8\u3002\u5440\uff0c\u8fd9\u66fe\u7ecf\u662f\u4ed6\u5fc3\u7231\u8fc7 \u7684\u5973\u4eba\uff0c\u5982\u4eca\u5374\u6bd4\u4ec0\u4e48\u6b66\u5f53\u4e94\u8001\uff0c\u4ec0\u4e48\u6bd5\u51cc\u98ce\u7f57\u91d1\u5cf0\u7b49\u7b49\u5f3a\u654c\uff0c\u8fd8\u66f4\u4ee4\u4ed6\u53ef\u6015\uff01\u4e8c\u5341\u5e74\u6765\uff0c \u4ed6\u6ca1\u6709\u4e00\u65e5\u4e0d\u60f3\u5979\uff0c\u5982\u4eca\u5979\u771f\u4e2a\u6765\u4e86\uff0c\u4ed6\u53c8\u6015\u89c1\u5979\uff01 \u3000\u3000\u8ff7\u832b\u4e2d\u9690\u7ea6\u542c\u5f97\u8427\u51a0\u82f1\u5728\u9662\u5b50\u5916\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u5927\uff0c\u5927\u5c0f\u59d0\uff0c\u4f60\u65e2\u7136\u56de\u6765\u4e86\uff0c\u8fd9\u5251\u8c31\u4e5f\u4e0d \u5fc5\u6211\u8d39\u5fc3\u53bb\u66ff\u4f60\u8ffd\u8ba8\u4e86\u3002\u53ea\u53ef\u60dc\u4f60\u6765\u8fdf\u4e00\u6b65\uff0c\u6bd5\u51cc\u98ce\u5374\u4e3a\u8fd9\u5251\u8c31\u6b7b\u4e86\u3002\u201d \u3000\u3000\u90a3\u5973\u4eba\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u6bd5\u51cc\u98ce\uff1f\u55ef\uff0c\u5c31\u662f\u90a3\u7389\u9762\u4e10\u4fa0\u5417\uff1f\u5440\uff0c\u8fd9\u5251\u8c31\u5bb3\u4e86\u591a\u5c11\u4eba\uff1f\u201d\u53ef\u662f \u5979\u4e3a\u4e86\u53e6\u4e00\u4ef6\u66f4\u9707\u64bc\u5fc3\u7075\u7684\u4e8b\u60c5\u6240\u7f20\u7ed5\uff0c\u5bf9\u6bd5\u51cc\u98ce\u4e4b\u6b7b\uff0c\u5374\u663e\u5f97\u5e76\u4e0d\u600e\u6837\u9707\u9a87\u54c0\u4f24\u4e86\u3002 \u3000\u3000\u8427\u51a0\u82f1\u8f7b\u8f7b\u7684\u53f9\u4e86\u53e3\u6c14\uff0c\u4ed6\u662f\u9690\u7ea6\u77e5\u9053\u6bd5\u51cc\u98ce\u7684\u5fc3\u4e8b\u7684\uff0c\u60f3\u4e0d\u5230\u6bd5\u51cc\u98ce\u751f\u524d\u6240\u75f4\u604b\u7684\u5973 \u4eba\uff0c\u5374\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\u4ed6\u7684\u5fc3\u4e8b\uff0c\u8fde\u4ed6\u7684\u540d\u5b57\u4e5f\u51e0\u4e4e\u60f3\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u90a3\u5973\u4eba\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4f60\u8d70\u5427\u3002\u4f60\u7684\u5973\u513f\u521a\u521a\u548c\u7987\u82f1\u7987\u9738\u4ed6\u4eec\u4e00\u9053\u4e0b\u5c71\u3002\u201d\u8427\u51a0\u82f1\u53eb\u4e86 \u4e00\u58f0\uff0c\u9053\uff1a\u201c\u662f\u4e48\uff1f\u97f5\u5170\u679c\u7136\u4e5f\u5728\u8fd9\u91cc\uff1f\u201d\u8df3\u8fc7\u5899\u5934\uff0c\u6025\u6025\u79bb\u5f00\u4e86\u4e91\u5bb6\u3002 \u3000\u3000\u8427\u51a0\u82f1\u90a3\u6025\u4fc3\u800c\u53c8\u6c89\u91cd\u7684\u811a\u6b65\u58f0\u5c31\u597d\u50cf\u8e0f\u5728\u4e91\u821e\u9633\u7684\u5fc3\u4e0a\uff0c\u9662\u5b50\u5916\u9762\u5c31\u53ea\u5269\u4e0b\u5979\u4e00\u4e2a \u4eba\uff0c\u5440\uff0c\u5979\u6765\u4e86\uff0c\u5979\u8f7b\u8f7b\u7684\u8d70\u8fdb\u4e66\u623f\u6765\u4e86\uff0c\u5979\u624b\u628a\u7389\u8427\uff0c\u767d\u8863\u5982\u96ea\uff0c\u5728\u4e91\u821e\u9633\u7684\u773c\u4e2d\uff0c\u5c31\u50cf \u6614\u65e5\u540c\u5728\u6885\u6797\u4e4b\u4e2d\u6563\u6b65\uff0c\u5979\u521a\u5439\u5b8c\u4e00\u66f2\uff0c\u5c31\u8fd9\u6837\u7684\u6162\u6162\u8d70\u6765\u4e86\u3002\u4e8c\u5341\u5e74\u6b7b\u522b\u751f\u79bb\uff0c\u5979\u7684\u76f8\u8c8c \u4e1d\u6beb\u672a\u6539\uff0c\u53ea\u662f\u795e\u60c5\u5374\u5df2\u5927\u5927\u4e0d\u540c\uff0c\u6614\u65e5\u6b22\u6109\u6d3b\u6cfc\u7684\u5c0f\u59d1\u5a18\uff0c\u800c\u4eca\u7709\u5c16\u4e0a\u5374\u5e26\u7740\u592a\u591a\u7684\u54c0 \u4f24\uff0c\u4ed6\u4e0d\u6562\u770b\u5979\uff0c\u4e0d\u6562\u78b0\u7740\u5979\u7684\u773c\u5149\uff0c\u90a3\u6bd4\u6606\u543e\u5b9d\u5251\u8fd8\u66f4\u950b\u5229\uff0c\u4ee4\u4eba\u611f\u5230\u900f\u4e0d\u8fc7\u6c14\u6765\u7684\u773c \u5149\uff01\u7136\u800c\u5979\u7ec8\u4e8e\u8d70\u8fdb\u6765\u4e86\uff0c\u8d70\u5230\u4e86\u4ed6\u7684\u773c\u524d\u4e86\uff01 \u3000\u3000\u5979\u662f\u8c01\uff1f\u5979\u6b63\u662f\u4e91\u821e\u9633\u7684\u524d\u59bb\u9648\u96ea\u6885\uff01 \u3000\u3000\u8fd9\u662f\u53ef\u80fd\u7684\u5417\uff1f\u4e91\u821e\u9633\u5f53\u5e74\u660e\u660e\u770b\u7740\u5979\u7684\u5c38\u4f53\u88ab\u957f\u6c5f\u7684\u6ce2\u6d9b\u5377\u53bb\uff0c\u7136\u800c\u5979\u73b0\u5728\u7adf\u7136\u6d3b\u7740 \u56de\u6765\u4e86\u3002 \u3000\u3000\u4ed6\u8bf4\u8bdd\u4e86\uff1a\u201c\u821e\u9633\uff0c\u4f60\u597d\uff0c\u4f60\u597d\u554a\u2026\u2026\u201d \u3000\u3000\u4e91\u821e\u9633\u84e6\u7136\u53eb\u9053\uff1a\u201c\u96ea\u6885\uff0c\u4f60\uff0c\u4f60\u2014\u2014\u201d\u4ed6\u8df3\u4e86\u8d77\u6765\uff0c\u7136\u800c\u5374\u53c8\u88ab\u5979\u51b0\u51b7\u7684\u773c\u5149\u963b\u4f4f \u4e86\uff01 \u3000\u3000\u4e24\u4eba\u9ed8\u9ed8\u65e0\u8a00\uff0c\u7231\u4e0e\u6068\u5728\u9648\u96ea\u6885\u7684\u5fc3\u4e2d\u4ea4\u7ec7\uff0c\u8fc7\u4e86\u597d\u534a\u664c\uff0c\u9648\u96ea\u6885\u5e7d\u5e7d\u8bf4\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a \u6211\u5df2\u6b7b\u4e86\uff0c\u53ef\u60dc\u8001\u5929\u4e0d\u4f9d\u4f60\u7684\u613f\u671b\uff0c\u6211\u8fd8\u6ca1\u6709\u6b7b\uff01\u4f60\u5931\u671b\u5427\uff1f\u6211\u77e5\u9053\u4f60\u5982\u4eca\u5df2\u662f\u5929\u4e0b\u7b2c\u4e00\u5251 \u5ba2\uff0c\u4f60\u628a\u6606\u543e\u5b9d\u5251\u62d4\u51fa\u6765\uff0c\u53ef\u4ee5\u628a\u6211\u518d\u6740\u6b7b\u4e86\uff01\u201d \u3000\u3000\u4e91\u821e\u9633\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u96ea\u6885\uff0c\u96ea\u6885\uff01\u4f60\u522b\u518d\u8bf4\u4e86\uff01\u201d \u3000\u3000\u9648\u96ea\u6885\u51b7\u7b11\u9053\uff1a\u201c\u54c8\u54c8\uff0c\u81ea\u8d1f\u662f\u5927\u82f1\u96c4\u3001\u5927\u5251\u5ba2\u7684\u4e91\u821e\u9633\u4e5f\u77e5\u9053\u5bb3\u6015\u4e86\uff1f\u4e8c\u5341\u5e74\u524d\u4f60\u628a \u6211\u63a8\u4e0b\u957f\u6c5f\uff0c\u90a3\u65f6\u4e0d\u89c1\u4f60\u5bb3\u6015\uff0c\u73b0\u5728\u4f60\u53cd\u800c\u5bb3\u6015\u4e86\uff1f\u201d \u3000\u3000\u4e91\u821e\u9633\u9762\u5982\u6b7b\u7070\uff0c\u98a4\u5dcd\u5dcd\u7684\u7ad9\u4e86\u8d77\u6765\uff0c\u5634\u5507\u5f00\u5408\uff0c\u597d\u50cf\u60f3\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u8d39\u4e86\u5f88\u5927\u7684\u529b\u6c14\uff0c \u8fd8\u672a\u8bf4\u5f97\u51fa\u6765\uff0c\u53c8\u88ab\u9648\u96ea\u6885\u6124\u6012\u7684\u58f0\u97f3\u6253\u65ad\u4e86\uff01 \u3000\u3000\u201c\u4f60\u6015\u6211\u8bf4\uff1f\u6211\u504f\u8981\u8bf4\uff01\u4f60\u5f53\u5e74\u628a\u6211\u63a8\u4e0b\u957f\u6c5f\uff0c\u4f60\u77e5\u9053\u6211\u5fc3\u4e2d\u60f3\u7684\u662f\u4ec0\u4e48\uff1f\u90a3\u4e00\u5e74\u4e3b\u516c \u548c\u6731\u5143\u748b\u5728\u957f\u6c5f\u51b3\u6218\uff0c\u4f60\u548c\u6211\u62a2\u4e86\u4e00\u53ea\u5c0f\u821f\uff0c\u5728\u6ce2\u6d9b\u6c79\u6d8c\u3001\u4e71\u7bad\u5982\u8757\u4e4b\u4e0b\u51b2\u4e86\u51fa\u6765\uff0c\u6211\u4e2d\u4e86 \u654c\u4eba\u7684\u6bd2\u7bad\uff0c\u5df2\u662f\u5944\u5944\u4e00\u606f\uff0c\u90a3\u65f6\u6211\u60f3\uff1a\u867d\u7136\u4f60\u5e38\u8bf4\u8981\u4e0e\u6211\u540c\u751f\u5171\u6b7b\uff0c\u6211\u5374\u600e\u5fcd\u8fde\u7d2f\u4e8e\u4f60\uff1f \u773c\u89c1\u4f60\u4e5f\u53d7\u4e86\u4f24\uff0c\u54b1\u4eec\u7684\u5c0f\u8239\u5c31\u5feb\u8981\u7ed9\u654c\u4eba\u7684\u5927\u8239\u8ffd\u4e0a\u4e86\uff0c\u90a3\u65f6\u6211\u5fc3\u4e2d\u5145\u6ee1\u5bf9\u4f60\u7684\u871c\u610f\u67d4 \u60c5\uff0c\u6211\u6562\u5bf9\u8001\u5929\u53d1\u8a93\uff0c\u90a3\u65f6\u6211\u4e4b\u7231\u4f60\uff0c\u786e\u786e\u5b9e\u5b9e\u6bd4\u7231\u6211\u81ea\u5df1\u7684\u751f\u547d\u8fd8\u8981\u591a\u8fc7\u767e\u500d\u5e72\u500d\uff01\u201d \u3000\u3000\u201c\u90a3\u65f6\u6211\u6323\u624e\u7740\u8d70\u51fa\u8239\u5934\uff0c\u6b63\u60f3\u8dc3\u4e0b\u957f\u6c5f\uff0c\u514d\u5f97\u62d6\u7d2f\u4f60\u88ab\u654c\u5175\u4fd8\u864f\uff0c\u4f60\uff0c\u4f60\u5c31\u5728\u8fd9\u4e2a\u65f6 \u5019\u6765\u4e86\uff0c\u4f60\u5728\u6211\u7684\u80cc\u540e\uff0c\u6211\u542c\u5f97\u51fa\u4f60\u6c89\u91cd\u7684\u547c\u5438\uff0c\u6211\u8fd8\u4ee5\u4e3a\u4f60\u731c\u5230\u4e86\u6211\u7684\u5fc3\u601d\uff0c\u8981\u6765\u62e6\u963b\u6211 \u4e86\uff0c\u54ea\u77e5\u9053\u4f60\u7adf\u7136\u5728\u6211\u80cc\u540e\u4f7f\u52b2\u4e00\u63a8\uff0c\u5c06\u6211\u63a8\u4e0b\u957f\u6c5f\uff01\u54c8\u54c8\uff0c\u4e91\u821e\u9633\uff0c\u4f60\u82e5\u662f\u8fdf\u4e00\u4e9b\u52a8\u624b\uff0c \u6211\u5148\u5df2\u8df3\u4e0b\u957f\u6c5f\uff0c\u800c\u4e14\u662f\u6ee1\u6000\u7740\u5bf9\u4f60\u7684\u7231\u610f\u7518\u613f\u53bb\u6b7b\uff0c\u5982\u4eca\u5462\uff0c\u6211\u6ca1\u6709\u6b7b\uff0c\u4f60\u5728\u6211\u7684\u5fc3\u4e2d\u5374 \u65e9\u5df2\u6b7b\u4e86\uff01\u201d \u3000\u3000\u4e91\u821e\u9633\u7684\u9762\u8272\u7531\u9752\u8f6c\u767d\uff0c\u7531\u767d\u8f6c\u7ea2\uff0c\u53c8\u7531\u7ea2\u8f6c\u767d\uff0c\u51e0\u5ea6\u5faa\u73af\uff0c\u7ec8\u4e8e\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u73b0\u5728\u60f3 \u6765\uff0c\u6211\u771f\u5b81\u613f\u5f53\u65f6\u6b7b\u53bb\u3002\u5440\uff0c\u8fd9\u4e8c\u5341\u5e74\u6765\uff0c\u82e6\u4e86\u4f60\u4e86\uff0c\u6211\u4e5f\u4f55\u5c1d\u597d\u53d7\uff0c\u6211\u65e5\u65e5\u591c\u591c\u53d7\u826f\u5fc3\u7684 \u8d23\u96be\uff0c\u53ea\u6015\u6bd4\u88ab\u6253\u4e0b\u5341\u516b\u5c42\u5730\u72f1\u8fd8\u8981\u75db\u82e6\u5f97\u591a\uff0c\u6211\u4e0d\u6562\u6c42\u4f60\u9976\u6055\uff0c\u597d\u5427\uff0c\u4f60\u518d\u72e0\u72e0\u7684\u9a82\u6211\uff0c \u9a82\u6211\u554a\uff01\u201d \u3000\u3000\u9648\u96ea\u6885\u90a3\u51b0\u51b7\u3001\u9119\u5f03\u7136\u800c\u53c8\u4f3c\u5e26\u6709\u4e00\u70b9\u601c\u60af\u7684\u773c\u5149\u5728\u4ed6\u9762\u4e0a\u626b\u8fc7\uff0c\u8fd9\u6b21\u662f\u4e91\u821e\u9633\u54c0\u6c42\u5979 \u9a82\uff0c\u5979\u5374\u6ca1\u6709\u5f00\u53e3\u3002 \u3000\u3000\u53ea\u542c\u5f97\u4e91\u821e\u9633\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u9a82\u6211\uff0c\u6211\u4e5f\u8981\u9a82\u6211\u81ea\u5df1\u3002\u96ea\u6885\uff0c\u4f60\u53ef\u77e5\u9053\u6211\u90a3\u65f6\u5019\u53c8\u662f \u60f3\u4e9b\u4ec0\u4e48\uff1f\u5728\u90a3\u6837\u7684\u5371\u96be\u4e4b\u4e2d\uff0c\u4f60\u662f\u8877\u5fc3\u4e3a\u6211\u6253\u7b97\uff0c\u6211\u5462\uff0c\u6211\u5374\u53ea\u662f\u4e3a\u81ea\u5df1\u6253\u7b97\uff01\u4f60\u90a3\u65f6\u53d7 \u4e86\u91cd\u4f24\uff0c\u6211\u81ea\u5fd6\u6ca1\u6709\u80fd\u529b\u53ef\u4ee5\u62a4\u4f60\u8131\u9669\uff0c\u6211\u4e3a\u81ea\u5df1\u5236\u9020\u7406\u7531\uff0c\u4e0e\u5176\u8ba9\u4f60\u4e3a\u654c\u6240\u4fd8\uff0c\u4e0e\u5176\u8ba9\u4f60 \u591a\u53d7\u75db\u82e6\uff0c\u4e0d\u5982\u8ba9\u957f\u6c5f\u7684\u6ce2\u6d6a\u5c06\u4f60\u7684\u75db\u82e6\u6df9\u57cb\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u7406\u7531\u5176\u5b9e\u53ea\u662f\u81ea\u5df1\u5b89\u6170\u81ea\u5df1\u3002\u90a3\u662f\u5047\u7684\uff0c\u6211\u53e6\u6709\u89c1\u4e0d\u5f97\u4eba\uff0c\u8bf4\u4e0d\u51fa\u53e3\u7684\u7406\u7531\uff0c\u6211 \u662f\u8d2a\u751f\u6015\u6b7b\uff0c\u5728\u5371\u96be\u7684\u65f6\u5019\uff0c\u4e0d\u613f\u5e87\u62a4\u59bb\u5b50\uff0c\u53ea\u60f3\u81ea\u5df1\u9003\u751f\u3002\u6211\u8fd8\u60f3\u8d81\u4f60\u6b7b\u540e\uff0c\u6211\u6709\u673a\u4f1a\u53ef \u4ee5\u6210\u4e3a\u5929\u4e0b\u7b2c\u4e00\u5251\u5ba2\uff01\u5440\uff0c\u6709\u4e9b\u4eba\u8fd8\u4ee5\u4e3a\u6211\u662f\u82f1\u96c4\uff0c\u4ed6\u4eec\u54ea\u91cc\u77e5\u9053\uff0c\u6211\u5fc3\u5730\u7684\u9f8c\u9f8a\u7adf\u5230\u4e86\u5982 \u6b64\u53ef\u6015\u7684\u7530\u5730\uff01\u6211\u628a\u4f60\u63a8\u4e0b\u957f\u6c5f\uff0c\u6211\u5077\u4e86\u4f60\u7684\u5bb6\u4f20\u5b9d\u5251\uff0c\u6211\u5728\u654c\u8239\u7684\u8ffd\u6355\u4e4b\u4e0b\u51b2\u4e86\u51fa\u6765\uff0c\u8863 \u670d\u672a\u5e72\uff0c\u6211\u5c31\u8dd1\u53bb\u627e\u725f\u72ec\u9038\uff0c\u8fd9\u4e00\u5207\u90fd\u662f\u4e3a\u4e86\u81ea\u5df1\u6253\u7b97\uff0c\u4e3a\u4e86\u60f3\u6210\u4e3a\u5929\u4e0b\u7b2c\u4e00\u5251\u5ba2\uff0c\u96ea\u6885 \u5440\uff0c\u4f60\u9a82\u6211\uff0c\u4f60\u9a82\u6211\u554a\uff01\u201d \u3000\u3000\u9648\u96ea\u6885\u7684\u773c\u6cea\u4e00\u9897\u4e00\u9897\u6ef4\u4e86\u51fa\u6765\uff0c\u5979\u60f3\u4e0d\u5230\u4e91\u821e\u9633\u4f1a\u6709\u8fd9\u6837\u771f\u8bda\u7684\u81ea\u767d\uff0c\u5979\u90a3\u5584\u826f\u7684\u5fc3 \u51e0\u4e4e\u5c31\u60f3\u5bbd\u6055\u4ed6\u4e86\uff0c\u7136\u800c\u5979\u8fd8\u662f\u6291\u5236\u7740\u81ea\u5df1\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u8fd9\u4e48\u6837\uff0c\u4f60\u5c31\u6210\u4e86\u725f\u5bb6\u7684\u5973\u5a7f\u3002 \u54c8\uff0c\u6211\u4e5f\u5fd8\u5566\uff0c\u6211\u76f4\u5230\u73b0\u5728\uff0c\u8fd8\u6ca1\u6709\u8bf7\u89c1\u4f60\u7684\u65b0\u592b\u4eba\uff0c\u4f60\u7684\u65b0\u592b\u4eba\u5462\uff1f\u201d\u9648\u96ea\u6885\u4f55\u5c1d\u4e0d\u77e5\u9053 \u4e91\u821e\u9633\u548c\u725f\u5b9d\u73e0\u7ed3\u5a5a\u4e5f\u5c06\u8fd1\u4e8c\u5341\u5e74\uff0c\u4f46\uff0c\u201c\u65b0\u592b\u4eba\u201d\u4e09\u5b57\u8fd8\u662f\u81ea\u81ea\u7136\u7136\u7684\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4e91\u821e\u9633\u82e6\u7b11\u9053\uff1a\u201c\u5979\u5417\uff0c\u5979\u4e5f\u8d70\u4e86\u3002\u4e00\u4e2a\u4e13\u4e3a\u81ea\u5df1\u6253\u7b97\u7684\u4eba\uff0c\u8fdf\u65e9\u4f1a\u88ab\u6240\u6709\u7684\u4eba\u629b\u5f03\uff0c \u4f60\u5f53\u6211\u6b7b\u4e86\uff0c\u5979\u5462\uff0c\u5979\u5927\u6982\u4e5f\u5f53\u6211\u6b7b\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u4ece\u6765\u6ca1\u6709\u5728\u4f60\u7684\u9762\u524d\u5938\u8d5e\u8fc7\u7b2c\u4e8c\u4e2a\u5973\u4eba\uff0c\u7136\u800c\u6211\u5374\u4e0d\u5f97\u4e0d\u8bf4\uff0c\u5b9d\u73e0\u5979\u4e5f\u7684\u786e\u662f\u50cf\u4f60 \u4e00\u6837\uff0c\u662f\u4e2a\u5fc3\u5730\u5584\u826f\u7684\u597d\u5973\u5b50\uff0c\u6211\u7528\u5047\u60c5\u5047\u4e49\u9a97\u4e86\u5979\uff0c\u9a97\u5979\u4e3a\u6211\u5077\u4e86\u725f\u72ec\u9038\u7684\u5251\u8c31\uff0c\u4e8e\u662f\u6211 \u4ece\u7b2c\u4e00\u4e2a\u59bb\u5b50\u7684\u624b\u4e2d\u5f97\u4e86\u4e16\u4e0a\u7b2c\u4e00\u7684\u5b9d\u5251\uff0c\u4ece\u7b2c\u4e8c\u4e2a\u7684\u624b\u4e2d\u5f97\u4e86\u4e16\u4e0a\u65e0\u53cc\u7684\u5251\u8c31\uff0c\u6211\u6210\u4e86\u4e16 \u4e0a\u7b2c\u4e00\u5251\u5ba2\uff0c\u800c\u4e5f\u5c31\u5931\u53bb\u4e86\u4e24\u4e2a\u59bb\u5b50\u7684\u7231\u60c5\uff01\u201d \u3000\u3000\u201c\u55ef\uff0c\u8fd9\u90e8\u5251\u8c31\u8fd8\u6709\u4e00\u4e2a\u66f2\u6298\u7684\u6545\u4e8b\uff0c\u5b83\u672c\u6765\u662f\u4f60\u7684\u7236\u4eb2\u7684\u3002\u96ea\u6885\uff0c\u73b0\u5728\u8fd9\u4e16\u4e0a\u53ea\u6709\u4f60 \u6709\u8d44\u683c\u505a\u8fd9\u5251\u8c31\u7684\u4e3b\u4eba\u4e86\uff01\u201d \u3000\u3000\u9648\u96ea\u6885\u4e00\u58f0\u51b7\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5343\u8f9b\u4e07\u82e6\uff0c\u542b\u51a4\u5fcd\u6068\u4e8c\u5341\u5e74\uff0c\u4eca\u65e5\u5192\u9669\u72af\u96be\uff0c\u5230\u6765\u627e\u4f60\uff0c \u4f60\u4ee5\u4e3a\u6211\u662f\u4e3a\u4e86\u4e00\u90e8\u5251\u8c31\u5417\uff1f\u201d \u3000\u3000\u4e91\u821e\u9633\u6253\u5f00\u4e86\u6240\u6709\u7684\u7a97\u95e8\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u6240\u53d7\u7684\u82e6\u96be\u65e0\u53ef\u8865\u507f\u3002\u8fd9\u4e8c\u5341\u5e74\u6765\uff0c \u6211\u60f3\u5c3d\u529e\u6cd5\u51cf\u8f7b\u6211\u5fc3\u7075\u7684\u91cd\u8d1f\uff0c\u5374\u662f\u4e1d\u6beb\u65e0\u6548\uff0c\u4e0d\u8fc7\uff0c\u4f60\u4e5f\u4e0d\u96be\u60f3\u89c1\u6211\u7684\u5fc3\u60c5\u3002\u201d \u3000\u3000\u201c\u55ef\uff0c\u4f60\u770b\u89c1\u5417\uff0c\u8fd9\u7a97\u5916\u7684\u6885\u82b1\uff0c\u8fd9\u4e66\u623f\u7684\u6446\u8bbe\uff0c\u5168\u90fd\u662f\u7167\u7740\u4ee5\u524d\u7684\u6837\u5b50\uff01\u201d \u3000\u3000\u9648\u96ea\u6885\u4e00\u773c\u671b\u53bb\uff0c\u9662\u5b50\u5916\u5c3d\u662f\u6b8b\u679d\u8d25\u53f6\uff0c\u679d\u5934\u4e0a\u53ea\u6709\u51e0\u6735\u7a00\u7a00\u758f\u758f\u7684\u6885\u82b1\uff0c\u5440\uff0c\u8fd9\u5c82\u4e0d \u6b63\u8c61\u5f81\u5979\u4eca\u591c\u7684\u5fc3\u60c5\uff0c\u7eb5\u7136\u8fd8\u6709\u4e9b\u8bb8\u60c5\u610f\uff0c\u4e5f\u50cf\u90a3\u96f6\u843d\u7684\u6885\u82b1\u4e86\u3002 \u3000\u3000\u4e91\u821e\u9633\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u6211\u6559\u5973\u513f\u5b66\u505a\u4f60\u4ee5\u524d\u559c\u7231\u5403\u7684\u5c0f\u83dc\uff0c\u6211\u6559\u5979\u505a\u4f60\u4ee5\u524d\u6b22\u559c\u7740\u7684\u8863 \u88f3\uff0c\u5979\u4eca\u5e74\u5341\u516b\u5c81\u4e86\uff0c\u6211\u5728\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u4e2d\u5c06\u5979\u6559\u517b\u5f97\u50cf\u4f60\u4e00\u6837\uff0c\u5584\u826f\uff0c\u6b63\u76f4\uff0c\u4ece\u6765\u4e0d\u77e5\u9053\u4eba \u95f4\u6709\u9f8c\u9f8a\u7684\u4e8b\u60c5\uff0c\u56e0\u4e3a\u6211\u8981\u5728\u5979\u8eab\u4e0a\u770b\u51fa\u4f60\u7684\u5f71\u5b50\uff01\u201d \u3000\u3000\u9648\u96ea\u6885\u4f4e\u4f4e\u7684\u53eb\u4e86\u4e00\u58f0\uff0c\u4e91\u821e\u9633\u8fd9\u4e00\u6bb5\u8bdd\u6700\u6700\u6253\u52a8\u4e86\u5979\u7684\u5fc3\uff0c\u5979\u611f\u5230\u51c4\u51c9\u4e5f\u611f\u5230\u6b22\u60a6\uff0c \u6124\u6068\u7684\u5fc3\u60c5\u4e0d\u77e5\u4e0d\u89c9\u7684\u6d88\u6563\u4e86\u4e00\u534a\uff0c\u5979\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u662f\u4e48\uff1f\u4f60\u4e5f\u6709\u4e00\u4e2a\u5973\u513f\uff1f\u201d\u4e91\u821e\u9633\u9053\uff1a \u201c\u55ef\uff0c\u4f60\u7b49\u4e00\u7b49\uff0c\u5979\u5c31\u8981\u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u9648\u96ea\u6885\u5ffd\u5730\u53c8\u611f\u5230\u6781\u5927\u7684\u75db\u82e6\uff0c\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u821e\u9633\uff0c\u4f60\u77e5\u9053\u6211\u4eca\u665a\u4e3a\u4ec0\u4e48\u627e\u4f60\uff1f\u4f60\u77e5\u9053 \u6211\u60f3\u8981\u4ec0\u4e48\uff1f\u6211\u672c\u6765\u53d1\u8a93\u4eca\u751f\u4e0d\u89c1\u4f60\u7684\u4e86\uff0c\u6211\u66f4\u4e0d\u662f\u60f3\u8981\u4ec0\u4e48\u5251\u8c31\uff0c\u6211\u8fdd\u80cc\u4e86\u81ea\u5df1\u7684\u8a93\u613f\u800c \u6765\uff0c\u5b8c\u5168\u662f\u4e3a\u4e86\u6211\u7684\u513f\u5b50\uff0c\u6211\u7684\u513f\u5b50\uff01\u201d \u3000\u3000------------- \u3000\u3000\u91cd\u5e86\u96ea\u513f \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u8fd8\u5251\u5947\u60c5\u5f55\u300b\u2014\u2014\u7b2c\u5341\u56db\u56de\u3000\u5bf8\u5bf8\u52ab\u7070 \u6881\u7fbd\u751f\u300a\u8fd8\u5251\u5947\u60c5\u5f55\u300b \u7b2c\u5341\u56db\u56de\u3000\u5bf8\u5bf8\u52ab\u7070 \u3000\u3000\u4e91\u821e\u9633\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u4f60\u7684\u513f\u5b50\uff1f\u4f60\u662f\u8bf4\u3002\u54b1\u4eec\u6709\u4e86\u4e00\u4e2a\u513f\u5b50\uff1f\u201d\u9648\u96ea\u6885\u70b9\u4e86\u70b9\u5934\u9053\uff1a \u201c\u4f60\u628a\u6211\u63a8\u4e0b\u957f\u6c5f\u4e4b\u65f6\uff0c\u6211\u5df2\u6709\u4e86\u4e24\u4e2a\u6708\u7684\u8eab\u5b55\u3002\u201d\u4e91\u821e\u9633\u5c16\u53eb\u4e00\u58f0\uff0c\u8df3\u4e86\u8d77\u6765\uff0c\u7528\u529b\u6376 \u80f8\uff0c\u6d41\u6cea\u8bf4\u9053\uff1a\u201c\u6211\u771f\u8be5\u6b7b\uff0c\u6211\u771f\u8be5\u6b7b\uff0c\u6211\u9669\u4e9b\u8fde\u81ea\u5df1\u7684\u513f\u5b50\u4e5f\u6740\u5bb3\u4e86\uff01\u201d \u3000\u3000\u9648\u96ea\u6885\u7684\u6012\u706b\u53c8\u71c3\u4e86\u8d77\u6765\uff0c\u51b0\u51b7\u8bf4\u9053\uff1a\u201c\u4ed6\u4e0d\u662f\u4f60\u7684\u513f\u5b50\uff0c\u4ed6\u4e5f\u4ece\u6765\u4e0d\u77e5\u9053\u6709\u4f60\u8fd9\u6837\u7684 \u7236\u4eb2\u3002\u201d\u4e91\u821e\u9633\u4f4e\u5934\u8bf4\u9053\uff1a\u201c\u662f\u554a\uff0c\u6211\u7684\u786e\u6ca1\u6709\u989c\u9762\u505a\u4ed6\u7684\u7236\u4eb2\u3002\u201d \u3000\u3000\u9648\u96ea\u6885\u9053\uff1a\u201c\u8fd9\u4e8c\u5341\u5e74\u6765\uff0c\u662f\u6211\u629a\u517b\u4ed6\u6210\u4eba\uff0c\u662f\u6211\u6559\u4ed6\u6210\u4e3a\u4e00\u4e2a\u6b63\u76f4\u5584\u826f\u7684\u4eba\uff0c\u4ed6\u548c\u4f60 \u6ca1\u6709\u4e1d\u6beb\u5173\u7cfb\uff01\u6211\u544a\u8bc9\u4ed6\uff0c\u4ed6\u7684\u7236\u4eb2\u65e9\u5df2\u6b7b\u4e86\uff01\u201d \u3000\u3000\u4e91\u821e\u9633\u5fc3\u75db\u5982\u7ede\uff0c\u4ed6\u4e0d\u6562\u9762\u5bf9\u9648\u96ea\u6885\u90a3\u6028\u6068\u7684\u773c\u5149\u3002\u6c89\u9ed8\u4e86\u8bb8\u4e45\uff0c\u65b9\u624d\u8bf4\u9053\uff1a\u201c\u96ea\u6885\uff0c \u6211\u61c2\u5f97\u4f60\u7684\u5fc3\u60c5\u3002\u4f60\u4e0d\u60f3\u4ed6\u8ba4\u6211\u8fd9\u6837\u4e00\u4e2a\u7236\u4eb2\uff0c\u6211\u4e5f\u4e0d\u914d\u505a\u4ed6\u7684\u7236\u4eb2\u3002\u6211\u53ea\u6073\u6c42\u4f60\u8bb2\u4e00\u8bb2\u4ed6 \u7684\u4e8b\u60c5\uff0c\u5c06\u6765\u8ba9\u6211\u89c1\u4e00\u89c1\u4ed6\u7684\u9762\u3002\u55ef\uff0c\u54b1\u4eec\u5206\u522b\u4e86\u4e8c\u5341\u5e74\uff0c\u7b97\u6765\u4ed6\u4e5f\u6709\u4e8c\u5341\u5c81\u4e86\uff0c\u8fd9\u4e8c\u5341\u5e74 \u4f60\u4eec\u4fe9\u6bcd\u5b50\u662f\u600e\u4e48\u8fc7\u7684\uff1f\u201d \u3000\u3000\u9648\u96ea\u6885\u6709\u70b9\u8be7\u5f02\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u4e5f\u8bb8\u4ed6\u4eec\u8fd8\u6ca1\u6709\u89c1\u9762\u3002\u773c\u5149\u4e00\u77a5\uff0c\u53ea\u89c1\u4e91\u821e\u9633\u6ee1\u9762\u6cea\u75d5\u7684 \u7acb\u5728\u7a97\u524d\uff0c\u6500\u7740\u4e00\u679d\u6885\u679d\uff0c\u597d\u50cf\u8d39\u4e86\u5f88\u5927\u7684\u6c14\u529b\uff0c\u9760\u7740\u8fd9\u4e00\u679d\u6885\u679d\u652f\u6301\uff0c\u624d\u7ad9\u5f97\u4f4f\u3002\u9648\u96ea\u6885 \u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u8981\u4e0d\u662f\u4ed6\uff0c\u6211\u4e5f\u6d3b\u4e0d\u5230\u5982\u4eca\u4e86\u3002\u6211\u7ed9\u4f60\u63a8\u4e0b\u957f\u6c5f\uff0c\u5c31\u56e0\u4e3a\u6211\u60f3\u5230\u8981\u4fdd\u5168 \u4ed6\uff0c\u6211\u624d\u80fd\u591f\u5e26\u7740\u91cd\u4f24\uff0c\u5728\u98ce\u6d6a\u4e4b\u4e2d\u6323\u624e\u3002\u5c31\u56e0\u4e3a\u6709\u4ed6\u4e0e\u6211\u76f8\u4f9d\u4e3a\u547d\uff0c\u6211\u624d\u80fd\u591f\u6371\u8fc7\u4e86\u8fd9\u4e8c \u5341\u5e74\uff01\u201d \u3000\u3000\u201c\u8fd9\u4e8c\u5341\u5e74\uff0c\u6211\u6559\u4ed6\u8bfb\u4e66\uff0c\u6211\u6559\u4ed6\u5251\u6cd5\u3002\u4ed6\u7684\u4f2f\u4f2f\u53d4\u53d4\uff0c\u4f60\u65e7\u65e5\u7684\u90a3\u73ed\u540c\u50da\u4e5f\u6559\u4ed6\u6b66 \u529f\uff0c\u6211\u9690\u5c45\u4e86\u4e8c\u5341\u5e74\uff0c\u6ca1\u6709\u4eba\u77e5\u9053\u6211\u8fd8\u6d3b\u5728\u4e16\u4e0a\u3002\u201d\u9661\u7136\u95f4\uff0c\u5ffd\u89c1\u4e91\u821e\u9633\u9762\u8272\u5927\u53d8\uff0c\u53eb\u9053\uff1a \u201c\u6211\u65e7\u65e5\u90a3\u73ed\u540c\u50da\u4e5f\u5e2e\u4f60\u6559\u4ed6\u6b66\u529f\uff1f\u201d\u9648\u96ea\u6885\u9053\uff1a\u201c\u4e0d\u9519\u3002\u53ef\u662f\u4ed6\u4eec\u4e0d\u77e5\u9053\u4ed6\u662f\u6211\u7684\u513f\u5b50\uff0c \u66f4\u4e0d\u77e5\u9053\u4ed6\u662f\u4f60\u7684\u513f\u5b50\uff0c\u662f\u56e0\u4e3a\u6211\u8981\u4ed6\u6210\u4e3a\u4e00\u4e2a\u66f4\u6709\u672c\u9886\u7684\u4eba\uff0c\u6211\u53eb\u4ed6\u5e26\u7740\u65e7\u65e5\u4e3b\u516c\u7684\u9057 \u7269\uff0c\u53bb\u627e\u5468\u516c\u5bc6\u7684\u3002\u5468\u516c\u5bc6\u53ea\u5f53\u4ed6\u662f\u540c\u50da\u7684\u5b64\u513f\uff0c\u89c1\u4ed6\u806a\u660e\u80c6\u5927\uff0c\u5341\u5206\u559c\u7231\u4ed6\uff0c\u6240\u4ee5\u5c31\u8bf7\u4e00 \u73ed\u53d4\u4f2f\u6bcf\u4eba\u90fd\u5c3d\u5fc3\u6559\u4ed6\u3002\u5440\uff0c\u73b0\u5728\u6211\u624d\u77e5\u9053\uff0c\u4ed6\u4eec\u4e5f\u662f\u522b\u6709\u7528\u5fc3\u3002\u201d\u5468\u516c\u5bc6\u662f\u5f20\u58eb\u8bda\u5728\u6c5f\u5357 \u65e7\u90e8\u7684\u9996\u9886\uff0c\u5f20\u58eb\u8bda\u8986\u8d25\u4e4b\u540e\uff0c\u4ed6\u4e00\u76f4\u5c31\u5728\u56fe\u8c0b\u518d\u8d77\u3002 \u3000\u3000\u4e91\u821e\u9633\u6d51\u8eab\u98a4\u6296\uff0c\u5636\u58f0\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u7528\u5fc3\uff1f\u201d\u9648\u96ea\u6885\u51b7\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u60f3\u53eb\u4ed6\u523a\u6740\u4f60\uff01\u201d \u4e91\u821e\u9633\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u8981\u4ed6\u6765\u523a\u6740\u6211\u3002\u201d\u9648\u96ea\u6885\u9053\uff1a\u201c\u4ed6\u4eec\u4e0d\u77e5\u9053\u4ed6\u662f\u4f60\u7684\u513f\u5b50\u3002\u4ed6\u4eec\u5374\u77e5 \u9053\u6731\u5143\u748b\u8981\u8bf7\u4f60\u51fa\u5c71\u3002\u201d\u4e91\u821e\u9633\u9053\uff1a\u201c\u5feb\u8bf4\uff0c\u5feb\u8bf4\uff0c\u4ed6\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d\u9648\u96ea\u6885\u9053\uff1a\u201c\u6211\u4e0d\u613f \u4ed6\u59d3\u4e91\uff0c\u6211\u8981\u4ed6\u8ddf\u6211\u7684\u59d3\uff0c\u4ed6\u53eb\u9648\u7384\u673a\uff01\u4ed6\u5230\u8fc7\u4f60\u8fd9\u91cc\u6ca1\u6709\uff1f\u8981\u4e0d\u662f\u4e3a\u4e86\u4ed6\uff0c\u6211\u4eca\u65e5\u51b3\u4e0d\u4f1a \u5230\u8fd9\u8d3a\u5170\u5c71\u4e2d\uff0c\u5440\uff0c\u821e\u9633\uff0c\u4f60\uff0c\u4f60\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u53ea\u542c\u5f97\u201c\u535c\u901a\u201d\u4e00\u58f0\uff0c\u4e91\u821e\u9633\u8dcc\u5012\u5730\u4e0a\uff0c\u9762\u5982\u6b7b\u7070\uff0c\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u5929\u54ea\uff01\u201d \u3000\u3000\u8fd9\u4e00\u5207\u90fd\u660e\u767d\uff0c\u9648\u7384\u673a\u7adf\u662f\u4ed6\u7684\u4eb2\u751f\u513f\u5b50\uff0c\u5374\u53c8\u662f\u4ed6\u5973\u513f\u6700\u503e\u5fc3\u7684\u4eba\uff0c\u8fd9\u7a81\u5982\u5176\u6765\u7684\u4e00 \u51fb\uff0c\u5c06\u4e91\u821e\u9633\u51fb\u5012\u4e86\uff01 \u3000\u3000\u8fd9\u7a81\u5982\u5176\u6765\u7684\u4e00\u51fb\u4e5f\u628a\u9648\u96ea\u6885\u51fb\u5f97\u7729\u6655\u4e86\uff0c\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u4ed6\u600e\u4e48\u9707\u9a87\u6210\u8fd9\u4e2a\u6837 \u5b50\uff1f\u201d\u5979\u65e0\u6687\u601d\u7d22\uff0c\u4e00\u628a\u5c06\u4e91\u821e\u9633\u62d6\u4e86\u8d77\u6765\uff0c\u8fd9\u662f\u4e8c\u5341\u5e74\u6765\u5979\u7b2c\u4e00\u6b21\u63a5\u89e6\u4e08\u592b\u7684\u624b\uff0c\u8fd9\u53ea\u624b \u4e5f\u6b63\u662f\u4e8c\u5341\u5e74\u524d\u5c06\u5979\u63a8\u4e0b\u957f\u6c5f\u7684\u624b\uff0c\u5979\u8981\u5c06\u5979\u7684\u624b\u6536\u56de\u6765\uff0c\u9661\u7136\u95f4\u53d1\u89c9\u4e91\u821e\u9633\u7684\u638c\u5fc3\u51b0\u51b7\uff0c \u4e24\u4eba\u9762\u9762\u76f8\u5bf9\uff0c\u9648\u96ea\u6885\u770b\u51fa\u4e86\u5730\u9762\u4e0a\u7b3c\u7f69\u7740\u90a3\u5c42\u6de1\u6de1\u7684\u7d2b\u6c14\u4e86\u3002 \u3000\u3000\u201c\u4ec0\u4e48\u3001\u4f60\u53d7\u4e86\u91cd\u4f24\uff1f\u4f60\u600e\u4e0d\u65e9\u8bf4\uff01\u201d\u9648\u96ea\u6885\u662f\u4e00\u4ee3\u5927\u4fa0\u4e4b\u5973\uff0c\u5f53\u7136\u4e5f\u770b\u5f97\u51fa\u4ed6\u8fd9\u91cd\u4f24 \u5df2\u662f\u4e0d\u6cbb\u4e4b\u75c7\uff0c\u8fd9\u4e00\u77ac\u95f4\uff0c\u4e00\u5207\u6069\u6028\u90fd\u5df2\u629b\u4e4b\u8111\u540e\uff0c\u4e91\u821e\u9633\u4f46\u89c9\u5979\u7684\u624b\u638c\u8f7b\u8f7b\u7684\u629a\u7740\u81ea\u5df1\uff0c \u5c31\u50cf\u4e8c\u5341\u5e74\u524d\u90a3\u6837\u3002 \u3000\u3000\u7136\u800c\u4e91\u821e\u9633\u7684\u5168\u526f\u5fc3\u601d\u90fd\u5df2\u653e\u5728\u5973\u513f\u8eab\u4e0a\uff0c\u201c\u8981\u662f\u7d20\u7d20\u77e5\u9053\u4e86\u8fd9\u4ef6\u4e8b\u60c5\u2026\u2026\u201d\u4ed6\u4e0d\u6562\u60f3 \u50cf\uff0c\u201c\u5e78\u597d\u7d20\u7d20\u8fd8\u6ca1\u6709\u56de\u6765\u3002\u201d\u4ed6\u6323\u624e\u8d77\u6765\uff0c\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u96ea\u6885\uff0c\u5feb\uff0c\u5feb\uff0c\u4f60\u5feb\u628a\u4ed6\u5e26 \u8d70\uff01\u201d\u9648\u96ea\u6885\u54ea\u91cc\u77e5\u9053\uff0c\u8fd9\u65f6\u5979\u4e08\u592b\u5fc3\u4e0a\u6240\u53d7\u7684\u521b\u4f24\u6bd4\u8eab\u4e0a\u7684\u6240\u53d7\u7684\u4f24\u8fd8\u8981\u91cd\u767e\u500d\u5343\u500d\uff01\u3002 \u3000\u3000\u9648\u96ea\u6885\u6014\u4e86\u4e00\u6014\uff0c\u4f46\u89c1\u4e91\u821e\u9633\u6d51\u8eab\u6218\u7c9f\uff0c\u9648\u96ea\u6885\u968f\u7740\u4ed6\u7684\u773c\u5149\u671b\u53bb\uff0c\u4e66\u623f\u91cc\u7684\u90a3\u5f20\u6e58\u5983 \u5e8a\uff0c\u5e18\u5e10\u5ffd\u7136\u65e0\u98ce\u81ea\u52a8\u3002\u9648\u96ea\u6885\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u7384\u673a\u4ed6\u5728\u8fd9\u513f\uff01\u201d \u3000\u3000\u9648\u7384\u673a\u660f\u8ff7\u4e86\u534a\u5929\uff0c\u8fd9\u65f6\u65b9\u81ea\u60a0\u60a0\u9192\u8f6c\uff0c\u63ed\u5f00\u5e18\u5e10\uff0c\u4e00\u773c\u671b\u53bb\uff0c\u6070\u6070\u89c1\u7740\u4ed6\u7684\u6bcd\u4eb2\u5411\u4ed6 \u8d70\u6765\uff01 \u3000\u3000\u8fd9\u662f\u68a6\u5417\uff1f\u4ed6\u54ac\u54ac\u6307\u5934\uff0c\u8fd9\u4e0d\u662f\u68a6\uff01\u9648\u96ea\u6885\u60b2\u559c\u4ea4\u96c6\uff0c\u53eb\u9053\uff1a\u201c\u7384\u673a\uff0c\u7384\u673a\uff01\u4f60\uff0c\u4f60\u6ca1 \u4e8b\u5417\uff1f\u201d\u9648\u7384\u673a\u9053\uff1a\u201c\u6ca1\u4e8b\u5566\u3001\u6211\u88ab\u7f57\u91d1\u950b\u6253\u4f24\uff0c\u662f\u4ed6\uff0c\u662f\u4ed6\u5c06\u6211\u6551\u4e86\u3002\u201d\u9648\u96ea\u6885\u770b\u4e86\u4e91\u821e \u9633\u4e00\u8ddf\uff0c\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u4e5f\u8fd8\u6709\uff0c\u8fd8\u6709\u2026\u2026\u201d\u5979\u60f3\u8bf4\u7684\u662f\uff1a\u201c\u539f\u6765\u4f60\u4e5f\u8fd8\u6709\u7236\u5b50\u4e4b \u60c5\u3002\u201d\u9661\u7136\u95f4\uff0c\u4f46\u89c1\u4e91\u821e\u9633\u53cc\u773c\u7ffb\u767d\uff0c\u8fde\u8fde\u6447\u624b\uff0c\u5636\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u5feb\u8d70\uff0c\u5feb\u8d70\uff01\u8d70\u5f97\u8fdc\u8fdc \u7684\uff0c\u6c38\u8fdc\u4e0d\u8981\u518d\u8e0f\u8fdb\u8fd9\u8d3a\u5170\u5c71\uff01\u201d \u3000\u3000\u9648\u96ea\u6885\u6124\u7136\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u6211\u4eec\u8d70\uff0c\u4e8c\u5341\u5e74\u6765\uff0c\u6211\u4eec\u6bcd\u5b50\u76f8\u4f9d\u4e3a\u547d\uff0c\u4e5f\u662f\u8fd9\u822c\u8fc7\u4e86\uff0c \u8c01\uff0c\u8c01\u2026\u2026\u201d\u4e91\u821e\u9633\u4f7f\u5c3d\u6c14\u529b\uff0c\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u522b\u518d\u8bf4\u4e86\uff0c\u5feb\u8d70\uff0c\u5feb\u8d70\uff01\u201d\u9648\u96ea\u6885\u5fc3\u5934\u4e00\u9707\uff0c \u4e91\u821e\u9633\u8fd9\u58f0\u97f3\u5145\u6ee1\u9a87\u6015\uff1a\u4ed6\u6015\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u9648\u7384\u673a\u66f4\u662f\u5947\u602a\u6781\u4e86\uff0c\u201c\u4e8c\u5341\u5e74\u6765\u6bcd\u4eb2\u8db3\u4e0d\u51fa\u6237\uff0c\u5979\u600e\u4e48\u4e5f\u8ba4\u5f97\u8fd9\u4e91\u821e\u9633\uff1f\u201d\u4f46\u89c1\u4e91\u821e \u9633\u548c\u6bcd\u4eb2\u7684\u795e\u60c5\u90fd\u5947\u602a\u900f\u9876\u4e86\uff0c\u7a7a\u6c14\u597d\u50cf\u51b7\u5f97\u8981\u51dd\u7ed3\u8d77\u6765\uff0c\u672c\u6765\u662f\u6ee1\u5fc3\u5145\u6ee1\u559c\u60a6\u7684\u9648\u7384\u673a\uff0c \u9661\u7136\u95f4\u4e5f\u81ea\u89c9\u5f97\u4e0d\u5bd2\u800c\u6817\uff01 \u3000\u3000\u9648\u96ea\u6885\u6006\u7136\u8bf4\u9053\uff1a\u201c\u673a\u513f\uff0c\u54b1\u4eec\u8d70\u5427\uff01\u201d\u9648\u7384\u673a\u60f6\u60d1\u6781\u4e86\uff0c\u5ffd\u5730\u6323\u5f00\u4e86\u6bcd\u4eb2\uff0c\u4f4e\u58f0\u8bf4 \u9053\uff1a\u201c\u4e0d\uff0c\u6211\u8fd8\u8981\u7b49\u7d20\u7d20\u56de\u6765\uff01\u5a18\uff0c\u4f60\u4f1a\u559c\u6b22\u7d20\u7d20\u7684\u3002\u201d\u9648\u96ea\u6885\u5fc3\u5934\u4e00\u9707\uff0c\u6b63\u60f3\u95ee\u9053\uff1a\u201c\u8c01 \u662f\u7d20\u7d20\uff1f\u201d\u5374\u89c1\u5979\u7684\u513f\u5b50\u5411\u524d\u8d70\u4e86\u4e24\u6b65\uff0c\u7528\u5145\u6ee1\u671f\u5f85\u4e0e\u54c0\u6c42\u7684\u773c\u5149\u770b\u7740\u4e91\u821e\u9633\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a \u201c\u4f60\u7b54\u5e94\u8ba9\u7d20\u7d20\u8ddf\u6211\u8d70\u7684\u3002\u6211\u8981\u7b49\u5979\u5411\u6765\uff0c\u7b49\u5979\u56de\u6765\uff01\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\u50cf\u7126\u96f7\u4e00\u6837\u6253\u5728\u6bcd\u4eb2\u7684\u5fc3\u4e0a\uff0c\u5979\u5fc3\u795e\u4e0d\u5b9a\uff0c\u53ea\u89c1\u4e91\u821e\u9633\u9762\u5982\u6b7b\u7070\uff0c\u6447\u6447\u6b32\u5760\uff01 \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u9648\u7384\u673a\u5ffd\u5730\u4e00\u58f0\u5c16\u53eb\uff0c\u773c\u5149\u5c04\u5904\uff0c\u8001\u6885\u6811\u4e0b\uff0c\u4eba\u5f69\u7ef0\u7ea6\uff0c\u8863\u8882\u98ce\u98d8\uff0c\u4e91 \u7d20\u7d20\u56de\u6765\u4e86\uff0c\u9648\u7384\u673a\u53eb\u9053\uff1a\u201c\u7d20\u7d20\uff0c\u7d20\u7d20\uff0c\u5a18\u2026\u2026\u201d\u4ed6\u7684\u58f0\u97f3\u7a81\u7136\u4e2d\u65ad\uff0c\u4f46\u89c1\u4e91\u7d20\u7d20\u9762\u8272\u60e8 \u767d\uff0c\u7edd\u5927\u7684\u60ca\u6050\uff0c\u7edd\u5927\u7684\u54c0\u4f24\uff0c\u5728\u8fd9\u773c\u5149\u4e00\u77a5\u4e4b\u4e2d\uff0c\u5c3d\u90fd\u8868\u9732\u51fa\u6765\u3002 \u3000\u3000\u9648\u7384\u673a\u624b\u8db3\u65e0\u63aa\uff0c\u4e00\u7247\u832b\u7136\uff0c\u201c\u7d20\u7d20\u201d\u4e24\u4e2a\u5b57\u8fd8\u672a\u66fe\u518d\u53eb\u51fa\u6765\uff0c\u84e6\u7136\u95f4\u53ea\u542c\u5f97\u4e91\u7d20\u7d20\u4e00 \u58f0\u7edd\u671b\u7684\u51c4\u53eb\uff0c\u63a9\u9762\u4fbf\u8dd1\uff0c\u75db\u54ed\u5931\u58f0\uff01\u9648\u96ea\u6885\u5446\u5446\u53d1\u6123\uff0c\u6d51\u8eab\u65e0\u529b\uff0c\u8fd9\u5239\u90a3\u95f4\uff0c\u5979\u4e5f\u5168\u90fd\u660e \u767d\u4e86\u3002\u53ea\u6709\u9648\u7384\u673a\u8fd8\u662f\u8ff7\u8ff7\u7cca\u7cca\uff0c\u4e0d\u6687\u7ec6\u60f3\uff0c\u4e5f\u4e0d\u6562\u7ec6\u60f3\uff0c\u4ed6\u8ffd\u7740\u4e91\u7d20\u7d20\u7684\u80cc\u5f71\uff0c\u65cb\u98ce\u822c\u7684 \u63a0\u8fc7\u5899\u5934\u53bb\u4e86\u3002\u9648\u96ea\u6885\u60f3\u62c9\u7740\u4ed6\uff0c\u7136\u800c\u53cc\u811a\u7adf\u662f\u4e0d\u80fd\u79fb\u52a8\u4e00\u6b65\uff01 \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4e91\u821e\u9633\u4e5f\u662f\u4e00\u58f0\u7edd\u671b\u7684\u51c4\u53eb\uff0c\u518d\u5ea6\u5012\u5730\uff0c\u5583\u5583\u8bf4\u9053\uff1a\u201c\u90fd\u662f\u6211\u4f5c\u7684\u5b7d\uff0c \u90fd\u662f\u6211\u4f5c\u7684\u5b7d\uff01\u201d\u58f0\u97f3\u8d8a\u6765\u8d8a\u5f31\uff0c\u9648\u96ea\u6885\u8eab\u5fc3\u9ebb\u6728\uff0c\u7528\u529b\u7741\u5f00\u773c\u775b\uff0c\u6323\u624e\u7740\u8d70\u5230\u4ed6\u7684\u8eab\u65c1\uff0c \u5979\u4e0d\u6562\u601d\u60f3\uff0c\u4e5f\u8bf4\u4e0d\u51fa\u534a\u53e5\u8bdd\uff0c\u53ea\u542c\u5f97\u4e91\u821e\u9633\u65ad\u65ad\u7eed\u7eed\u7684\u8bf4\u9053\uff1a\u201c\u8ba9\u4ed6\u4eec\u53bb\u5427\uff01\u53bb\u5427\u2026\u2026\u8bf7 \u4f60\u628a\u8fd9\u51e0\u95f4\u623f\u5b50\u4e00\u628a\u706b\u70e7\u4e86\uff0c\u5c06\u6211\u7684\u9aa8\u7070\u5e26\u56de\u6c5f\u5357\uff0c\u6211\u4e0d\u613f\u57cb\u5728\u8fd9\u4f24\u5fc3\u4e4b\u5730\u3002\u201d\u8bf4\u5230\u540e\u6765\uff0c \u58f0\u97f3\u5df2\u662f\u4e0d\u80fd\u5206\u8fa8\uff0c\u672c\u6765\u4ed6\u8fd8\u53ef\u4ee5\u6709\u4e09\u5929\u6027\u547d\uff0c\u4f46\u5728\u6781\u5ea6\u4f24\u5fc3\u4e4b\u4e0b\uff0c\u5fc3\u810f\u7206\u88c2\uff0c\u8fd9\u4f4d\u8d39\u5c3d\u5fc3 \u529b\u3001\u505a\u6210\u529f\u4e86\u5929\u4e0b\u7b2c\u4e00\u5251\u5ba2\u7684\u4e91\u821e\u9633\uff0c\u7adf\u5c31\u6b64\u4e00\u7791\u4e0d\u89c6\uff01 \u3000\u3000\u4e8c\u5341\u5e74\u751f\u79bb\u6b7b\u522b\uff0c\u4e00\u89c1\u9762\u53c8\u6210\u6c38\u8bc0\uff0c\u9648\u96ea\u6885\u4e5f\u4e0d\u77e5\u662f\u7231\u662f\u6068\uff1f\u662f\u5e7b\u662f\u771f\uff1f\u4e08\u592b\u513f\u5b50\uff0c\u513f \u5b50\u4e08\u592b\u2026\u2026\u4f46\u89c9\u5fc3\u5934\u6df7\u4e71\uff0c\u6b32\u54ed\u65e0\u6cea\uff0c\u6bd4\u4e91\u821e\u9633\u5c06\u5979\u63a8\u4e0b\u957f\u6c5f\u4e4b\u65f6\uff0c\u8fd8\u66f4\u96be\u8fc7\uff0c\u518d\u4e5f\u652f\u6301\u4e0d \u4f4f\uff0c\u4e00\u58f0\u5c16\u53eb\uff0c\u4e5f\u8dcc\u5012\u5728\u4e91\u821e\u9633\u7684\u8eab\u8fb9\u3002 \u3000\u3000\u8d3a\u5170\u5c71\u91cc\u8fd8\u6709\u4e24\u4e2a\u4f24\u5fc3\u7684\u4eba\uff0c\u90a3\u662f\u4e91\u7d20\u7d20\u548c\u9648\u7384\u673a\u3002\u4e91\u7d20\u7d20\u4e5f\u51e0\u4e4e\u652f\u6301\u4e0d\u4f4f\u4e86\uff0c\u4f46\u5979\u8fd8 \u662f\u75be\u98ce\u4e00\u6837\u7684\u72c2\u5954\uff0c\u9003\u907f\u9648\u7384\u673a\u7684\u8ffd\u9010\u3002 \u3000\u3000\u591c\u98ce\u4e2d\u5439\u6765\u9648\u7384\u673a\u60b2\u51c9\u7684\u53eb\u58f0\uff1a\u201c\u7d20\u7d20\uff0c\u4f60\u7b49\u7b49\u6211\u5440\uff01\u7d20\u7d20\uff0c\u4f60\u4e0d\u7406\u6211\uff0c\u4e5f\u8be5\u548c\u6211\u8bf4\u4e00 \u53e5\u8bdd\u5440\uff01\u201d\u7136\u800c\u7d20\u7d20\u4ecd\u662f\u4e0d\u80af\u56de\u5934\uff0c\u4e24\u4eba\u4e4b\u95f4\uff0c\u53ea\u6709\u591c\u98ce\u4f5c\u4ed6\u4eec\u7684\u4f7f\u8005\u3002\u5c06\u9648\u7384\u673a\u547c\u5524\u7684\u58f0 \u97f3\u4f20\u8fc7\u53bb\uff0c\u53c8\u5c06\u4e91\u7d20\u7d20\u6ce3\u6ce3\u7684\u58f0\u97f3\u4f20\u8fc7\u6765\uff01 \u3000\u3000\u9648\u7384\u673a\u8ff7\u60d1\u6781\u4e86\uff0c\u9a87\u6015\u6781\u4e86\uff0c\u4ed6\u5df2\u9690\u9690\u611f\u5230\u4e86\u4e0d\u5e78\u7684\u51f6\u5146\uff0c\u4f46\u4ed6\u5374\u538b\u5236\u4e0d\u4f4f\u81ea\u5df1\uff0c\u5440\uff0c \u4ed6\u7adf\u7136\u8fd8\u8981\u53bb\u63ed\u5f00\u8fd9\u4e2a\u4f24\u5fc3\u7684\u8c1c\u5e95\uff01 \u3000\u3000\u7389\u5b57\u65e0\u5c18\uff0c\u94f6\u6cb3\u6cfb\u5f71\uff0c\u6708\u5149\u5982\u6c34\uff0c\u826f\u591c\u8fe2\u8fe2\u3002\u5f80\u4e8b\u5386\u5386\uff0c\u91cd\u6cdb\u5fc3\u5934\u3002\u9648\u7384\u673a\u60f3\u8d77\u4e86\u90a3\u4e00 \u665a\u4e91\u7d20\u7d20\u5728\u5c71\u9876\u629a\u7434\u9ad8\u6b4c\uff0c\u5f39\u51fa\u4e86\u76f8\u601d\u4e07\u7f15\uff1b\u4eca\u665a\u4e00\u6837\u7684\u6708\u8272\uff0c\u4e00\u6837\u7684\u4eba\u513f\uff0c\u4f46\u5fc3\u60c5\u5df2\u662f\u5b8c \u5168\u4e24\u6837\uff01 \u3000\u3000\u9648\u7384\u673a\u53d1\u529b\u72c2\u8ffd\uff0c\u4e0e\u4e91\u7d20\u7d20\u7684\u8ddd\u79bb\u6e10\u6e10\u7f29\u77ed\u4e86\u3002\u9648\u7384\u673a\u53c8\u53eb\u9053\uff1a\u201c\u7d20\u7d20\uff0c\u4f60\u8bf4\u8fc7\u5728\u8fd9\u4e16 \u4e0a\u53ea\u6709\u6211\u4e00\u4e2a\u4eb2\u4eba\uff0c\u4f60\u8bf4\u8fc7\u4ece\u4eca\u4e4b\u540e\uff0c\u4e0d\u8bba\u6d77\u89d2\u5929\u6daf\uff0c\u4f60\u90fd\u8981\u8ddf\u6211\u5728\u4e00\u8d77\uff01\u55ef\uff0c\u7d20\u7d20\uff0c\u4f60\u600e \u4e48\u5566\uff1f\u201d\u591c\u98ce\u5439\u6765\u7d20\u7d20\u54fd\u54bd\u7684\u58f0\u97f3\uff1a\u201c\u4e0d\u6210\uff0c\u4e0d\uff0c\u4e0d\u6210\u2026\u2026\u7384\u673a\uff0c\u4f60\u4e0d\u77e5\u9053\u2026\u2026\u201d \u3000\u3000\u9648\u7384\u673a\u53eb\u9053\uff1a\u201c\u54b1\u4eec\u8fd8\u6709\u4ec0\u4e48\u4e8b\u60c5\u4e0d\u53ef\u4ee5\u8c08\u7684\uff0c\u7d20\u7d20\uff0c\u4f60\u544a\u8bc9\u2014\u2014\u201d\u53ef\u662f\u7d20\u7d20\u6ca1\u6709\u56de \u7b54\uff0c\u5979\u8d8a\u8dd1\u8d8a\u5feb\uff0c\u50cf\u9e1f\u513f\u4e00\u6837\u7684\u98de\u4e0a\u5ced\u58c1\u60ac\u5ca9\uff0c\u5c31\u5c06\u5230\u8fbe\u5cf0\u5dc5\u4e86\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u6709\u4eba\u5927\u53eb\u9053\uff1a\u201c\u7384\u673a\u5144\uff0c\u7384\u673a\u5144\uff01\u201d\u9648\u7384\u673a\u56de\u5934\u4e00\u671b\uff0c\u5374\u662f\u4e0a\u5b98\u5929\u91ce\uff0c\u5728\u8fd9\u4e00\u77ac \u4e4b\u95f4\uff0c\u9648\u7384\u673a\u811a\u6b65\u7a0d\u505c\uff0c\u4e91\u7d20\u7d20\u53c8\u79bb\u5f00\u4ed6\u5341\u6570\u4e08\u4e86\u3002 \u3000\u3000\u9648\u7384\u673a\u9053\uff1a\u201c\u5929\u91ce\u5144\uff0c\u54b1\u4eec\u6539\u65e5\u518d\u8c08\u3002\u201d\u4f46\u89c1\u4e0a\u5b98\u5929\u91ce\u8dd1\u5f97\u4e0a\u6c14\u4e0d\u63a5\u4e0b\u6c14\uff0c\u5927\u58f0\u53eb\u9053\uff1a \u201c\u90a3\u8fbe\u6469\u5251\u8c31\u662f\u4f60\u7684\uff0c\u90a3\u628a\u6606\u543e\u5b9d\u5251\u4e5f\u662f\u4f60\u7684\uff01\u201d\u9648\u7384\u673a\u5fc3\u5934\u4e00\u9707\uff0c\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u4f46\u4ecd \u662f\u811a\u6b65\u5982\u98de\uff0c\u5e76\u4e0d\u56de\u5934\u8be2\u95ee\u3002\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u5582\uff0c\u5582\uff0c\u4f60\u6162\u4e00\u4e9b\uff0c\u542c\u6211\u8bf4\u2014\u2014\u201d\u9648\u7384\u673a\u7eb5\u8eab \u98de\u8dd1\uff0c\u53ea\u89c1\u4e91\u7d20\u7d20\u5728\u5728\u5c71\u5dc5\u4e0a\u8863\u8882\u98d8\u98d8\uff0c\u6447\u6447\u6643\u6643\u3002 \u3000\u3000\u9648\u7384\u673a\u5927\u53eb\u4e00\u58f0\uff0c\u4f7f\u51fa\u6d51\u8eab\u672c\u4e8b\uff0c\u4e00\u4e2a\u201c\u71d5\u5b50\u94bb\u4e91\u201d\uff0c\u5e73\u7a7a\u63a0\u8d77\u6570\u4e08\uff0c\u98de\u4e0a\u5c71\u5934\uff0c\u4e0a\u5b98 \u5929\u91ce\u8fde\u4ed6\u7684\u80cc\u5f71\u4e5f\u770b\u4e0d\u89c1\u4e86\uff0c\u5140\u662f\u9f13\u8db3\u4e86\u6c14\u5927\u53eb\u9053\uff1a\u201c\u4f60\u7684\u5916\u7956\u7236\u662f\u4e0d\u662f\u53eb\u505a\u9648\u5b9a\u65b9\uff1f\u8fbe\u6469 \u5251\u8c31\u662f\u725f\u72ec\u9038\u62a2\u53bb\u7684\uff0c\u6606\u543e\u5b9d\u5251\u662f\u4e91\u821e\u9633\u7684\u7b2c\u4e00\u4e2a\u59bb\u5b50\u7684\uff0c\u90fd\u5e94\u8be5\u662f\u4f60\u7684\u4e1c\u897f\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u53ea\u662f\u7262\u7262\u7684\u8bb0\u7740\u4ed6\u5e08\u7236\u6bd5\u51cc\u98ce\u6240\u8bf4\u7684\u8bdd\uff0c\u90a3\u5251\u8c31\u548c\u5b9d\u5251\u90fd\u5e94\u8be5\u5f52\u8fd8\u9648\u5b9a\u65b9\u7684\u5973 \u513f\uff0c\u4ed6\u4e0d\u77e5\u9053\u9648\u96ea\u6885\u5c1a\u5728\u4e16\u95f4\uff0c\u4f46\u4ed6\u5374\u8bb0\u8d77\u4e86\u9648\u7384\u673a\u7684\u5916\u7956\u7236\u53eb\u9648\u5b9a\u65b9\uff0c\u8fd9\u4e2a\u76f4\u5fc3\u773c\u513f\u7684\u7c97 \u8c6a\u6c49\u5b50\uff0c\u7adf\u7136\u6ca1\u6709\u60f3\u8d77\u9648\u7384\u673a\u548c\u4e91\u7d20\u7d20\u7684\u7684\u5173\u7cfb\uff0c\u53ea\u4e3a\u4e86\u66ff\u5e08\u7236\u8fd8\u90a3\u5fc3\u613f\uff0c\u4e00\u80a1\u52b2\u513f\u7684\u8dd1\u6765 \u544a\u8bc9\u4ed6\uff01 \u3000\u3000\u201c\u8f70\u201d\u7684\u4e00\u58f0\uff0c\u597d\u50cf\u9752\u5929\u8d77\u4e86\u4e2a\u9739\u96f3\uff0c\u9648\u7384\u673a\u4ec0\u4e48\u90fd\u660e\u767d\u4e86\uff0c\u9661\u7136\u95f4\u5ffd\u89c1\u4e91\u7d20\u7d20\u7389\u624b\u4e00 \u626c\uff0c\u5c06\u90a3\u67c4\u6606\u543e\u5b9d\u5251\u629b\u4e86\u8fc7\u6765\uff0c\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u7384\u673a\uff0c\u7384\u673a\uff0c\u4f60\uff0c\u4f60\uff0c\u4f60\u660e\u767d\u4e86\u4e48\uff1f\u4e0d\u8981\u8fd1 \u6211\uff0c\u4e0d\u8981\u8fd1\u6211\uff01\u201d\u8fd9\u4e00\u77ac\u95f4\u9648\u7384\u673a\u597d\u50cf\u7a81\u7136\u7ed9\u62bd\u6389\u4e86\u9b42\u9b44\uff0c\u8eab\u4e0d\u7531\u5df1\u7684\u4ecd\u7136\u98de\u5954\u800c\u4e0a\uff0c\u4e0d\u77e5 \u662f\u4e91\u7d20\u7d20\u60f3\u907f\u5f00\u4ed6\u8fd8\u662f\u5076\u7136\u5931\u8db3\uff0c\u7a81\u7136\u4e00\u6b65\u8e0f\u7a7a\uff0c\u4ece\u5343\u4e08\u9ad8\u5cf0\u76f4\u8dcc\u4e0b\u53bb\uff01 \u3000\u3000\u5c71\u98ce\u9661\u8d77\uff0c\u5c71\u8c37\u56db\u9762\u90fd\u54cd\u8d77\u4e86\u9648\u7384\u673a\u60e8\u5389\u72c2\u53eb\u7684\u58f0\u97f3\uff0c\u4e0a\u5b98\u5929\u91ce\u4e00\u7247\u832b\u7136\uff0c\u5927\u58f0\u53eb\u9053\uff1a \u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u8c01\u4e5f\u6ca1\u6709\u7b54\u4ed6\uff0c\u6ee1\u5c71\u90fd\u662f\u9648\u7384\u673a\u547c\u5524\u201c\u7d20\u7d20\u201d\u7684\u58f0\u97f3\uff0c\u4ed6\u53d1\u72c2\u822c\u7684\u56db\u5904 \u5bfb\u89c5\uff0c\u5f53\u7136\u4ed6\u518d\u4e5f\u627e\u4e0d\u5230\u4e91\u7d20\u7d20\u4e86\uff01\u800c\u4e0a\u5b98\u5929\u91ce\u5462\uff0c\u4e5f\u4e0d\u77e5\u8be5\u5411\u54ea\u4e2a\u65b9\u5411\u53bb\u627e\u9648\u7384\u673a\uff01 \u3000\u3000\u4f46\u89c1\u5927\u706b\u878d\u878d\uff0c\u5c71\u98ce\u547c\u5578\uff0c\u5728\u9648\u7384\u673a\u7684\u72c2\u53eb\u58f0\u4e2d\uff0c\u4e91\u5bb6\u4e5f\u5df2\u70e7\u6210\u4e86\u4e00\u7247\u74e6\u783e\u3002 \u3000\u6b63\u662f\uff1a \u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u91cd\u91cd\u51a4\u5b7d\u968f\u6d41\u6c34\uff0c \u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u5bf8\u5bf8\u4f24\u5fc3\u4ed8\u52ab\u7070\uff01 \u3000\u3000\uff08\u5168\u4e66\u5b8c\uff09"}),showOnHomepage:!0}]}}]);