"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[71839],{75119:(e,n,s)=>{s.r(n),s.d(n,{default:()=>a});var t=s(74848);const a=[{name:"\u71d5\u5b50\u4f20\u5947",content:(0,t.jsx)(t.Fragment,{children:"\u7b2c \u4e00 \u56de\u3000\u91cd\u56da\u540d\u5077 \u3000\u3000\u6df1\u591c\u2026\u2026 \u3000\u3000\u65e0\u6708\u7684\u6df1\u591c\uff0c\u5e7d\u9759\uff0c\u51b7\u51c4\u3002 \u3000\u3000\u79cb\u5929\u7684\u591c\uff0c\u591a\u52a0\u4e86\u4e00\u4efd\u8427\u7d22\uff0c\u98d2\u98d2\u7684\u98ce\uff0c\u5439\u98d8\u7740\u843d\u53f6\u3002 \u3000\u3000\u9752\u7816\u780c\u6210\u7684\u5899\u58c1\uff0c\u9ad8\u5927\u3001\u575a\u7262\uff0c\u4e00\u76cf\u9ad8\u6311\u7684\u98ce\u706f\uff0c\u6447\u66f3\u5728\u591c\u7a7a\u4e2d\uff0c\u4e00\u5708\u660f\u9ec4\u7684\u706f\u5149\uff0c\u7167 \u7740\u7a7a\u5bc2\u7684\u5ead\u9662\u3002 \u3000\u3000\u91cd\u91cd\u7684\u94c1\u6805\uff0c\u7d27\u95ed\u7684\u6728\u95e8\uff0c\u9501\u4f4f\u6728\u95e8\u7684\u7279\u5927\u53f7\u94c1\u9501\uff0c\u771f\u662f\u6212\u62a4\u68ee\u4e25\uff0c\u98de\u9e1f\u96be\u6e21\u3002 \u3000\u3000\u662f\u7684\uff0c\u8fd9\u91cc\u662f\u5f00\u5c01\u5e9c\u5927\u7262\u4e00\u89d2\uff0c\u56da\u7981\u91cd\u5211\u8981\u72af\u7684\u4e09\u53f7\u7262\u623f\uff0c\u5173\u7684\u90fd\u662f\u62f3\u5927\u81c2\u7c97\u7684\u6c5f\u6e56\u4eba \u7269\u3002 \u3000\u3000\u4e24\u76cf\u7eb1\u706f\u5212\u7834\u591c\u8272\uff0c\u8fc5\u5feb\u7684\u884c\u4e86\u8fc7\u6765\uff0c\u516b\u4e2a\u4f69\u5200\u7684\u6355\u5feb\uff0c\u62a4\u62e5\u7740\u5f00\u5c01\u5e9c\u603b\u6355\u5934\u94c1\u638c\u71d5 \u98de\uff0c\u884c\u8fd1\u4e86\u6805\u95e8\u3002 \u3000\u3000\u94c1\u6805\u542f\u52a8\uff0c\u503c\u591c\u7684\u7262\u5934\uff0c\u8fce\u51fa\u4e86\u6805\u95e8\uff0c\u8eac\u8eab\u4e00\u793c\u3001\u9053\uff1a\u201c\u71d5\u7237\uff0c\u8fd9\u4e48\u665a\u4e86\uff0c\u8fd8\u8981\u63d0\u72af\u4eba \u54ea\uff1f\u201d \u3000\u3000\u71d5\u98de\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4e03\u53f7\uff0c\u6c5f\u5343\u91cc\u3002\u201d \u3000\u3000\u201c\u8fd9\u2026\u2026\u201d\u7262\u5934\u6014\u4e86\u4e00\u6014\u3002 \u3000\u3000\u201c\u600e\u4e48\uff0c\u6709\u95ee\u9898\u2026\u2026\u201d\u71d5\u98de\u76b1\u8d77\u4e86\u7709\u5934\u3002 \u3000\u3000\u201c\u6ca1\u95ee\u9898\uff01\u6ca1\u95ee\uff01\u2026\u2026\u2026\u53ea\u4e0d\u8fc7\uff0c\u6c5f\u8001\u4e0d\u592a\u559c\u6b22\u6709\u4eba\u591c\u91cc\u5435\u4ed6\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u6c5f\u8001\uff0c\u54fc\uff01\u8fd9\u91cc\u662f\u5f00\u5c01\u5e9c\u5927\u7262\uff0c\u4ed6\u662f\u88ab\u5173\u7684\u72af\u4eba\u2026\u2026\u201d \u3000\u3000\u201c\u662f\u662f\u662f\uff0c\u5351\u804c\u73b0\u5728\u5c31\u53bb\u5e26\u4ed6\u51fa\u6765\u2026\u2026\u201d \u3000\u3000\u201c\u6162\u7740\uff01\u201d \u3000\u3000\u7262\u5934\u505c\u4e0b\u4e86\u811a\u6b65\uff0c\u9053\uff1a\u201c\u71d5\u7237\uff0c\u8fd8\u6709\u4ec0\u4e48\u5429\u5490\uff1f\u201d \u3000\u3000\u201c\u6211\u966a\u4f60\u8fdb\u53bb\uff0c\u6c5f\u5343\u91cc\u540d\u52a8\u6b66\u6797\uff0c\u662f\u4e2a\u82f1\u96c4\u4eba\u7269\uff0c\u5bf9\u4ed6\u793c\u9047\u4e00\u4e9b\uff0c\u4e5f\u5728\u60c5\u7406\u4e4b\u4e2d\u2026\u2026\u201d \u3000\u3000\u60f3\u5230\u6c5f\u5343\u91cc\u5728\u6b66\u6797\u4e2d\u7684\u8d6b\u8d6b\u58f0\u5a01\uff0c\u4f7f\u71d5\u98de\u6001\u5ea6\u7acb\u523b\u6539\u53d8\uff0c\u4f55\u51b5\uff0c\u4eca\u591c\u63d0\u5ba1\uff0c\u60c5\u5f62\u5f02\u5e38\uff0c \u4e0d\u50cf\u5f00\u5802\u5ba1\u95ee\u3002 \u3000\u3000\u201c\u662f\u554a\uff01\u71d5\u7237\uff0c\u6c5f\u8001\u7684\u7f6a\u540d\u4e5f\u4e0d\u5927\uff0c\u6700\u591a\u5173\u4e2a\u4e8c\u5e74\u3001\u4e09\u5e74\uff0c\u518d\u8bf4\uff0c\u4ed6\u771f\u8981\u8d70\uff0c\u8fd9\u5f00\u5c01\u5e9c \u7684\u5927\u7262\u4e5f\u672a\u5fc5\u7559\u5f97\u4f4f\u4ed6\u2026\u2026\u201d\u7262\u5934\u7a81\u7136\u4f4f\u53e3\uff0c\u770b\u770b\u71d5\u98de\u7684\u8138\u8272\uff0c\u62c5\u5fc3\u6fc0\u6012\u4e86\u8fd9\u4f4d\u4e2d\u539f\u540d\u6355\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\uff0c\u71d5\u98de\u6ca1\u6709\u751f\u6c14\uff0c\u795e\u60c5\u5f88\u5e73\u9759\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u7262\u5934\uff0c\u8bf4\u4e0b\u53bb\u3002\u201d \u3000\u3000\u201c\u71d5\u7237\u4e0d\u751f\u6c14\uff0c\u5351\u804c\u5c31\u653e\u80c6\u76f4\u8a00\u4e86\u3002\u6c5f\u5343\u91cc\u7684\u670b\u53cb\u5f88\u591a\uff0c\u5927\u6c5f\u5357\u5317\uff0c\u9ed1\u767d\u4e24\u9053\uff0c\u90fd\u6709\u4ed6 \u7684\u77e5\u5df1\u597d\u53cb\uff0c\u4e00\u65e6\u4ed6\u7834\u7262\u800c\u53bb\uff0c\u71d5\u7237\u867d\u7136\u6b66\u529f\u9ad8\u5f3a\uff0c\u4f46\u60f3\u8ffd\u6355\u5230\u4ed6\uff0c\u4e5f\u662f\u4e00\u4ef6\u5f88\u9ebb\u70e6\u7684\u4e8b \u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u53ea\u662f\u9ebb\u70e6\uff0c\u53ea\u6015\u662f\u975e\u5e38\u7684\u56f0\u96be\u2026\u2026\u201d\u71d5\u98de\u5fc3\u5e73\u6c14\u548c\u5730\u9053\uff1a\u201c\u8bba\u6c5f\u6e56\u9053\u4e0a\u7684\u58f0\u671b\uff0c\u6211 \u81ea\u77e5\u96be\u671b\u9879\u80cc\u2026\u2026\u201d \u3000\u3000\u201c\u71d5\u7237\uff0c\u4f60\u662f\u8bf4\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u8bf4\u7684\u90fd\u662f\u5b9e\u8bdd\u3002\u201d \u3000\u3000\u201c\u71d5\u7237\u80fd\u4e86\u89e3\u5351\u804c\u7684\u82e6\u8877\uff0c\u8fd9\u5c31\u597d\u8bf4\u8bdd\u4e86\uff0c\u4e8c\u5341\u4e94\u65a4\u7684\u7279\u53f7\u5927\u67b7\uff0c\u4ed6\u53ea\u8981\u4f38\u4e2a\u61d2\u8170\uff0c\u6253 \u4e2a\u54c8\u6b20\uff0c\u5c31\u5168\u6563\u4e86\uff0c\u62c7\u6307\u7c97\u7684\u811a\u9563\uff0c\u4ed6\u4f38\u4f38\u817f\uff0c\u62ac\u62ac\u811a\uff0c\u5c31\u84ec\u7136\u4e2d\u65ad\uff0c\u8001\u5b9e\u8bf4\uff0c\u4ed6\u968f\u65f6\u90fd\u53ef \u4ee5\u79bb\u5f00\u8fd9\u91cc\uff0c\u534a\u5c3a\u539a\u7684\u6728\u95e8\uff0c\u4e24\u652f\u516b\u5c3a\u7684\u56f4\u5899\uff0c\u62e6\u4e0d\u4f4f\u4ed6\uff0c\u5c31\u662f\u8fd9\u5ea7\u94c1\u6805\u95e8\uff0c\u53ea\u6015\u4e5f\u7981\u4e0d\u8d77 \u4ed6\u7684\u795e\u529b\u4e00\u6325\u2026\u2026\u201d \u3000\u3000\u71d5\u7237\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u90a3\u662f\u600e\u4e48\u7559\u4e0b\u4ed6\u7684\uff1f\u201d \u3000\u3000\u201c\u6c42\u4ed6\u2026\u2026\u2026\u201d\u7262\u5934\u82e6\u7b11\u4e00\u4e0b\uff0c\u8bf4\uff1a\u201c\u6c5f\u8001\u5403\u8f6f\u4e0d\u5403\u786c\uff0c\u6211\u4eec\u8ddf\u4ed6\u8bf4\u7406\uff0c\u4e0d\u8981\u4e3a\u96be\u6211\u4eec \u8fd9\u4e9b\u5c0f\u4eba\u7269\u3002\u201d \u3000\u3000\u201c\u4ed6\u5c31\u7b54\u5e94\u4e86\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u6240\u4ee5\uff0c\u6c5f\u8001\u6ca1\u4e0a\u624b\u94d0\uff0c\u4e5f\u6ca1\u52a0\u811a\u9563\u2026\u2026\u201d\u7262\u5934\u4f4e\u58f0\u9053\uff1a\u201c\u71d5\u7237\uff0c\u4f60\u8981\u62c5\u5f85\u4e00\u4e0b\uff0c \u6211\u4eec\u5df2\u7ecf\u628a\u72af\u4eba\u7559\u5728\u4e86\u7262\u623f\u91cc\uff0c\u81f3\u4e8e\u4ece\u4ec0\u4e48\u65b9\u6cd5\uff0c\u71d5\u7237\u5c31\u4e0d\u8981\u8ffd\u7a76\u4e86\u3002\u201d \u3000\u3000\u71d5\u98de\u7b11\u4e00\u7b11\uff0c\u7b54\u975e\u6240\u95ee\u5730\u9053\uff1a\u201c\u5e26\u6211\u53bb\u770b\u4ed6\u5427\uff01\u201d \u3000\u3000\u6c5f\u5343\u91cc\u88ab\u5173\u5728\u9760\u8fb9\u95f4\u7684\u7262\u623f\u4e2d\uff0c\u4eba\u8eab\u4e0a\u867d\u672a\u52a0\u5211\u5177\uff0c\u53ef\u662f\u623f\u95e8\u5916\u5374\u52a0\u4e86\u4e24\u6761\u94c1\u94fe\u5b50\u62e6 \u7740\uff0c\u4e24\u4e2a\u7262\u5352\uff0c\u7a7f\u7740\u9ed1\u8272\u7684\u8863\u670d\uff0c\u9690\u4f0f\u5728\u4e24\u4fa7\u7684\u6697\u5f71\u4e4b\u4e2d\uff0c\u6000\u4e2d\u5404\u62b1\u4e00\u652f\u8bf8\u845b\u5323\u5f29\u3002 \u3000\u3000\u90a3\u662f\u4e00\u79cd\u8fde\u53d1\u7684\u5f29\u7bad\uff0c\u4e00\u5323\u5341\u53ea\uff0c\u8fd1\u8ddd\u79bb\u5185\uff0c\u662f\u975e\u5e38\u6076\u6bd2\u7684\u6697\u5668\u3002 \u3000\u3000\u770b\u5230\u4e86\u8fd9\u79cd\u5e03\u7f6e\uff0c\u71d5\u98de\u5fcd\u4e0d\u4f4f\u5fae\u5fae\u4e00\u7b11\uff0c\u5fd6\u9053\uff1a\u201c\u7262\u5934\u7981\u5352\uff0c\u679c\u7136\u90fd\u662f\u6210\u4e86\u7cbe\u7684\u8001\u72d0\u72f8 \u4e86\uff0c\u8868\u9762\u4e0a\u8bf4\u5c3d\u597d\u8bdd\uff0c\u9aa8\u5b50\u91cc\u5374\u662f\u522b\u6709\u5b89\u6392\u3002 \u3000\u3000\u7262\u5934\u4e3e\u624b\u4e00\u6325\uff0c\u4e24\u4e2a\u62b1\u7740\u5323\u5f29\u7684\u7981\u5352\uff0c\u6084\u7136\u9000\u4e86\u4e0b\u53bb\uff0c\u8fd9\u662f\u9634\u62db\uff0c\u4e0d\u80fd\u8ba9\u6c5f\u5343\u91cc\u770b\u5230\u3002 \u3000\u3000\u89e3\u4e0b\u94c1\u94fe\uff0c\u6253\u5f00\u95e8\u9501\uff0c\u7262\u5934\u91cd\u91cd\u7684\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6c5f\u8001\uff0c\u71d5\u603b\u6355\u5934\u6765\u770b\u4f60\u4e86\u3002\u201d \u3000\u3000\u4e24\u4e2a\u638c\u706f\u7684\u6355\u5feb\uff0c\u4e3e\u8d77\u4e86\u624b\u4e2d\u7684\u7eb1\u706f\uff0c\u53ea\u89c1\u4e00\u4e2a\u8eab\u7a7f\u84dd\u5e03\u957f\u886b\u7684\u4e94\u65ec\u8001\u8005\uff0c\u4ef0\u5367\u7262\u623f\u4e00 \u89d2\uff0c\u867d\u7136\u6ca1\u6709\u5e8a\u94fa\uff0c\u4f46\u5730\u4e0b\u5374\u94fa\u7740\u5f88\u539a\u7684\u7f8a\u6bdb\u6be1\uff0c\u8eab\u4e0a\u8fd8\u76d6\u7740\u4e00\u6761\u68c9\u88ab\uff0c\u53cc\u76ee\u7d27\u95ed\uff0c\u4f3c\u662f\u7761 \u7684\u6b63\u719f\uff0c\u65c1\u8fb9\u8fd8\u653e\u4e00\u575b\u8001\u9152\u3002 \u3000\u3000\u679c\u7136\u662f\u53d7\u7740\u5f88\u4f18\u6e25\u7684\u793c\u9047\u3002 \u3000\u3000\u7262\u5934\u8e72\u4e0b\u8eab\u5b50\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u6c5f\u8001\uff0c\u54b1\u4eec\u603b\u6355\u6765\u770b\u4f60\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u7b11\u4e00\u7b11\uff0c\u633a\u8eab\u5750\u8d77\uff0c\u9053\uff1a\u201c\u603b\u6355\u5934\u534a\u591c\u91cc\u63d0\u6211\u8001\u4eba\u5bb6\u51fa\u53bb\uff0c\u53ef\u662f\u8981\u62c9\u51fa\u53bb\u780d\u5934 \u4e86\uff1f\u201d \u3000\u3000\u201c\u6c5f\u5144\uff0c\u5728\u4e0b\u71d5\u98de\uff0c\u8eab\u4e0d\u7531\u5df1\uff0c\u6df1\u591c\u6765\u6b64\u60ca\u6270\uff0c\u8bf7\u6c5f\u5144\u591a\u591a\u539f\u8c05\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u4e24\u9053\u76ee\u5149\uff0c\u8f6c\u6ce8\u5230\u71d5\u98de\u8eab\u4e0a\uff0c\u6253\u91cf\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u8bf4\u5427\uff01 \u3000\u3000\u4ec0\u4e48\u4eba\u8981\u89c1\u6211\uff1f\u201c \u3000\u3000\u71d5\u98de\u9053\uff1a\u201c\u5e9c\u53f0\u5927\u4eba\u5728\u5185\u5385\u63a5\u89c1\u2026\u2026\u2026\u201d \u3000\u3000\u6c5f\u5343\u91cc\u4e00\u8dc3\u800c\u8d77\u3002\u9053\uff1a\u201c\u884c\uff0c\u8d70\u5427\uff01\u4ed6\u628a\u6211\u8001\u4eba\u5bb6\u5173\u4e86\u8fdb\u6765\uff0c\u6211\u770b\u4ed6\u600e\u4e48\u6837\u628a\u6211\u653e\u51fa \u53bb\u2026\u2026\u201d\u5927\u6b65\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u71d5\u98de\u4e00\u6a2a\u8eab\uff0c\u62e6\u4f4f\u53bb\u8def\uff0c\u9053\uff1a\u201c\u6c5f\u5144\uff0c\u4e0d\u80fd\u5c31\u8fd9\u6837\u51fa\u53bb\uff01\u201d \u3000\u3000\u201c\u96be\u4e0d\u6210\u8fd8\u8981\u52a0\u4e0a\u624b\u94d0\u811a\u9563\uff1f\u201d \u3000\u3000\u201c\u811a\u70e7\u4e0d\u7528\u4e86\uff0c\u624b\u94d0\u53ea\u6015\u96be\u514d\uff0c\u8fd9\u662f\u671d\u5ef7\u7684\u6cd5\u5ea6\uff0c\u6c5f\u5144\u8981\u62c5\u5f85\u4e00\u4e9b\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u7b11\u9053\uff1a\u201c\u71d5\u98de\uff0c\u6211\u8001\u4eba\u5bb6\u53ef\u662f\u6709\u4ec7\u5fc5\u62a5\uff0c\u4f60\u66ff\u6211\u52a0\u4e0a\u624b\u94d0\uff0c\u90a3\u5c31\u662f\u6b20\u4e86\u6211\u4e00\u7b14 \u503a\uff0c\u518d\u8bf4\uff0c\u5f00\u5c01\u5e9c\u53ea\u6015\u5f88\u96be\u6709\u4e00\u526f\u80fd\u9501\u4f4f\u6211\u7684\u624b\u94d0\u3001\u201d \u3000\u3000\u201c\u5b98\u8eab\u96be\u81ea\u4e3b\u3002\u6c5f\u5144\uff0c\u5f97\u7f6a\u4e86\u3002\u201d \u3000\u3000\u71d5\u98de\u4f38\u624b\u7531\u4e00\u4e2a\u6355\u5feb\u8eab\u4e0a\u53d6\u8fc7\u624b\u94d0\uff0c\u53cc\u624b\u9f50\u51fa\uff0c\u53ed\u5730\u4e00\u58f0\u3002\u9501\u5728\u6c5f\u5e72\u91cc\u7684\u53cc\u8155\uff2c\u3002\u8ba4\u4f4d \u5947\u51c6\uff0c\u52a8\u4f5c\u5229\u843d\u3002 \u3000\u3000\u8f66\u5934\u4eb2\u773c\u770b\u5230\u8fc7\u4e8c\u5341\u4e94\u65a4\u91cd\u7684\u5927\u53f7\u811a\u9563\uff0c\u88ab\u6c5f\u5343\u91cc\u786c\u751f\u751f\u9707\u65ad\uff0c\u8fd9\u526f\u5c0f\u5c0f\u624b\u94d0\uff0c\u5982\u4f55\u80fd \u9501\u5f97\u4f4f\u4ed6\u3002\u4e00\u65e6\u7ffb\u8138\uff0c\u52bf\u5fc5\u4f1a\u5f15\u53d1\u4e00\u573a\u6076\u6218\u3002\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u7acb\u523b\u9000\u51fa\u7262\u623f\uff0c\u6697\u4f5c\u6212\u5907\uff0c\u51c6\u5907\u968f \u65f6\u62db\u547c\u5f13\u7bad\u624b\uff0c\u8d76\u6765\u52a9\u6218\u3002 \u3000\u3000\u5927\u51fa\u610f\u5916\u7684\u662f\u6c5f\u5343\u91cc\u5e76\u672a\u6323\u65ad\u624b\u94d0\uff0c\u671b\u7740\u71d5\u98de\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u71d5\u98de\uff0c\u53ef\u4ee5\u8d70\u4e86\u5427\uff01\u201d \u3000\u3000\u201c\u662f\uff0c\u6c5f\u5144\u8bf7\uff01\u201d \u3000\u3000\u6c5f\u4e0b\u91cc\u6602\u9996\u800c\u884c\uff0c\u71d5\u98de\u7d27\u968f\u8eab\u540e\uff0c\u4e24\u4e2a\u6267\u706f\u6355\u5feb\uff0c\u62a2\u5728\u524d\u9762\u5e26\u8def\uff0c\u516d\u4e2a\u6355\u5feb\u624b\u63e1\u5200\u67c4\uff0c \u4e24\u4fa7\u6212\u62a4\u3002 \u3000\u3000\u5185\u5385\u662f\u77e5\u5e9c\u5927\u4eba\u9000\u5802\u4e4b\u540e\uff0c\u63a5\u89c1\u5ba2\u4eba\u7684\u5730\u65b9\uff0c\u5e03\u7f6e\u5f97\u975e\u5e38\u96c5\u81f4\uff0c\u6b64\u523b\uff0c\u71c3\u8d77\u4e86\u56db\u652f\u513f\u81c2 \u7c97\u7684\u5de8\u70db\uff0c\u7167\u5f97\u6574\u4e2a\u5385\u5802\u4e00\u7247\u901a\u660e\u3002 \u3000\u3000\u4f46\u5750\u5728\u4e3b\u4f4d\u7684\uff0c\u5e76\u4e0d\u662f\u5f00\u5c01\u5e9c\u7684\u77e5\u5e9c\u5927\u4eba\uff0c\u800c\u662f\u4e2a\u7a7f\u7740\u9752\u7f0e\u5b50\u957f\u888d\u7684\u4e2d\u5e74\u4eba\uff0c\u4e00\u8138\u51b7\u8083 \u7684\u795e\u60c5\uff0c\u5fae\u76b1\u7740\u4e24\u9053\u7709\uff0c\u5c39\u77e5\u5e9c\u66f4\u662f\u4e00\u8138\u8bda\u60f6\u8bda\u6050\u4e4b\u8272\uff0c\u5750\u5728\u65c1\u8fb9\uff0c\u8fde\u5927\u6c14\u4e5f\u4e0d\u6562\u5598\u4e00\u53e3\u3002 \u3000\u3000\u71d5\u98de\u8ba4\u8bc6\u90a3\u4e2d\u5e74\u4eba\uff0c\u662f\u6cb3\u5357\u5de1\u629a\u9a6c\u6587\u4e2d\uff0c\u5f00\u5c01\u5e9c\u7684\u9876\u5934\u4e0a\u53f8\uff0c\u7acb\u523b\u5c48\u4e0b\u4e00\u819d\uff0c\u62b1\u62f3\u5782 \u9996\uff0c\u9053\uff1a\u201c\u5f00\u5c01\u5e9c\u603b\u6355\u5934\u71d5\u98de\u53e9\u89c1\u5de1\u629a\u5927\u4eba\u3002\u201d \u3000\u3000\u201c\u8d77\u6765\uff0c\u8d77\u6765\uff0c\u5185\u5802\u4e0d\u7528\u591a\u793c\u3002\u201d \u3000\u3000\u201c\u8c22\u5927\u4eba\u2026\u2026\u201d\u71d5\u98de\u8f6c\u5411\u5c39\u77e5\u5e9c\uff0c\u9053\uff1a\u201c\u56da\u72af\u6c5f\u5343\u91cc\u5e26\u5230\u3002\u201d \u3000\u3000\u5c39\u77e5\u5e9c\u70b9\u70b9\u5934\uff0c\u8f6c\u5411\u9a6c\u6587\u4e2d\u9053\uff1a\u201c\u5927\u4eba\uff0c\u8fd9\u4f4d\u5c31\u662f\u6c5f\u5343\u91cc\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u76ee\u5149\u79fb\u6ce8\u5230\u6c5f\u5343\u91cc\u8eab\u4e0a\uff0c\u6c5f\u5e72\u91cc\u50b2\u7136\u800c\u7acb\uff0c\u770b\u4e5f\u4e0d\u770b\u4ed6\u4e00\u773c\u3002 \u3000\u3000\u201c\u6c5f\u5343\u91cc\uff01\u2026\u2026\u201d\u71d5\u98de\u5389\u58f0\u559d\u9053\uff1a\u201c\u89c1\u4e86\u5de1\u629a\u5927\u4eba\uff0c\u8fd8\u4e0d\u53c2\u62dc\uff0c\u7ed9\u6211\u8dea\u4e0b\u3002\u201d \u3000\u3000\u4e00\u4e0a\u6b65\uff0c\u903c\u8fd1\u6c5f\u5343\u91cc\u7684\u8eab\u4fa7\uff0c\u53f3\u638c\u4e00\u626c\uff0c\u5c31\u8981\u5288\u4e0b\u3002 \u3000\u3000\u201c\u4e0d\u7528\u4e86\uff01\u201d\u9a6c\u5de1\u629a\u53f3\u624b\u8fde\u6325\uff0c\u963b\u6b62\u4e86\u71d5\u98de\uff0c\u9053\uff1a\u201c\u5f00\u4e86\u4ed6\u7684\u624b\u94d0\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u2026\u2026\u201d\u71d5\u98de\u8d70\u8fd1\u6b63\u5f85\u884c\u52a8\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u7a81\u7136\u53cc\u624b\u4e00\u6323\uff0c\u624b\u94d0\u65ad\u88c2\uff0c\u6563\u843d\u4e00\u5730\uff0c\u7b11\u9053\uff1a\u201c\u4e0d\u6562\u52b3\u52a8\u603b\u6355\u5934\u3002\u201d \u3000\u3000\u71d5\u98de\u62e6\u5728\u6c5f\u5343\u91cc\u8eab\u524d\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6c5f\u5343\u91cc\uff0c\u4f60\u6700\u597d\u89c4\u77e9\u70b9\u3002\u201d \u3000\u3000\u9a6c\u5de1\u629a\u9053\uff1a\u201c\u71d5\u6355\u5934\uff49\u4f60\u4e0b\u53bb\uff01\u201d \u3000\u3000\u201c\u8fd9\u2026\u2026\u201d\u71d5\u98de\u76ee\u5149\u8f6c\u6ce8\u5728\u5c39\u77e5\u5e9c\u7684\u8138\u4e0a\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u5374\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5de1\u629a\u5927\u4eba\uff0c\u71d5\u98de\u5fe0\u5fc3\u62a4\u4e3b\uff0c\u4f60\u8981\u4ed6\u79bb\u5f00\u5385\u5802\uff0c\u5c82\u4e0d\u662f\u8981\u4ed6\u4e8f \u8d1f\u804c\u5b88\u3002\u201d \u3000\u3000\u201c\u4e5f\u597d\uff0c\u71d5\u6355\u5934\u5c31\u7559\u4e0b\u6765\u5427\uff01\u201d \u3000\u3000\u201c\u662f\uff01\u201d\u71d5\u98de\u9000\u5230\u4e86\u9a6c\u5de1\u629a\u7684\u8eab\u4fa7\uff0c\u51dd\u795e\u6212\u5907\u3002 \u3000\u3000\u6323\u65ad\u624b\u94d0\u4e0d\u96be\uff0c\u4f46\u50cf\u6c5f\u5343\u91cc\u8fd9\u6837\u6325\u624b\u4e4b\u95f4\uff0c\u94c1\u6253\u7684\u624b\u94d0\u88c2\u65ad\u6570\u622a\u6563\u843d\uff0c\u90a3\u5c31\u975e\u6df1\u539a\u7684\u5185 \u529f\u4e0d\u53ef\u4e86\u3002\u71d5\u98de\u5c31\u81ea\u77e5\u65e0\u6cd5\u529e\u5230\u7684\u3002 \u3000\u3000\u201c\u6c5f\u5148\u751f\u8bf7\u5750\uff01\u201d\u9a6c\u5de1\u629a\u8138\u8272\u51b7\u5f97\u53ef\u4ee5\u522e\u4e0b\u4e00\u5c42\u51b0\u6765\uff0c\u4f46\u8bf4\u8bdd\u5374\u662f\u8f7b\u58f0\u7ec6\u8bed\uff0c\u5341\u5206\u5ba2 \u6c14\u3002 \u3000\u3000\u201c\u5927\u4eba\u4ee5\u975e\u5e38\u4e4b\u793c\uff0c\u4f18\u6e25\u8349\u6c11\uff0c\u4e8b\u51fa\u60c5\u7406\u4e4b\u5916\uff0c\u5fc5\u6709\u975e\u5e38\u4e4b\u4e8b\uff0c\u6709\u8bdd\u5c31\u8bf7\u660e\u8bf4\u5427\uff01\u201d \u3000\u3000\u9a6c\u5de1\u629a\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6c5f\u5148\u751f\u679c\u7136\u89c1\u8bc6\u975e\u51e1\uff0c\u6587\u4e2d\u5c31\u76f4\u8bf4\u4e86\u3002\u201d \u3000\u3000\u5c01\u7586\u88c2\u571f\u7684\u4e00\u54c1\u5927\u5458\uff0c\u9762\u5bf9\u4e00\u4e2a\u56da\u72af\uff0c\u7adf\u7136\u81ea\u5df1\u62a5\u51fa\u540d\u5b57\uff0c\u8fd9\u793c\u8d24\u4e0b\u58eb\u4e5f\u793c\u8d24\u7684\u6709\u4e9b\u8c26 \u5351\u4e86\u3002 \u3000\u3000\u71d5\u98de\u5fc3\u5934\u9707\u52a8\uff0c\u60f3\u4e0d\u51fa\u5802\u5802\u4e00\u7701\u7684\u5de1\u629a\u5927\u4eba\uff0c\u4e3a\u4ec0\u4e48\u5982\u6b64\u793c\u9047\u4e00\u4e2a\u56da\u72af\u3002 \u3000\u3000\u518d\u770b\u5c39\u77e5\u5e9c\u5750\u5728\u4e00\u4fa7\u3002\uff0c\u8138\u8272\u82cd\u767d\uff0c\u770b\u4e0a\u53bb\uff0c\u4ed6\u5012\u50cf\u4e00\u4e2a\u5f85\u51b3\u7684\u56da\u72af\u3002 \u3000\u3000\u9a6c\u5de1\u629a\u4f3c\u662f\u5728\u601d\u7d22\u63aa\u8bcd\uff0c\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u63a5\u9053\uff1a\u201c\u542c\u8bf4\u6c5f\u5148\u751f\u64c5\u957f\u8ffd\u8e2a\u89c5\u8ff9\u4e4b\u672f\uff0c\u5929\u4e0b\u65e0 \u51fa\u5176\u53f3\u3002\u201d \u3000\u3000\u5927\u4eba\u8981\u6c5f\u67d0\u53bb\u627e\u4e00\u4e2a\u4eba\uff1f\u201c \u3000\u3000\u201c\u4e0d\u9519\uff01\u6b64\u4eba\u5173\u7cfb\u975e\u5e38\u91cd\u5927\uff0c\u6c5f\u5148\u751f\u5982\u80af\u51fa\u624b\u76f8\u52a9\uff0c\u6587\u4e2d\u5341\u5206\u611f\u6fc0\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4eba\uff0c\u53ef\u4ee5\u544a\u8bc9\u6211\u5427\uff01\u201d \u3000\u3000\u201c\u5f53\u7136\uff01\u201d\u9a6c\u5de1\u629a\u70b9\u70b9\u5934\u9053\uff1a\u201c\u6211\u77e5\u9053\u6c5f\u5148\u751f\u4e00\u8a00\u4e5d\u9f0e\uff0c\u53ea\u8981\u7b54\u5e94\u4e86\uff0c\u6211\u4f1a\u5c3d\u544a\u8be6 \u60c5\u2026\u2026\u201d \u3000\u3000\u201c\u6162\u6765\uff0c\u6162\u6765\u2026\u2026\u201d\u6c5f\u5e72\u91cc\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5148\u628a\u6c5f\u67d0\u4eba\u7684\u5b98\u53f8\u4e86\u65ad\uff0c\u6211\u624d\u80fd\u8003\u8651\u7b54\u4e0d\u7b54 \u5e94\u3002\u201d \u3000\u3000\u5c39\u77e5\u5e9c\u7a81\u7136\u63a5\u9053\uff1a\u201c\u4f60\u53ea\u8981\u7b54\u5e94\u4e86\u5de1\u629a\u5927\u4eba\uff0c\u5b98\u53f8\u5c31\u4e00\u7b14\u52fe\u6d88\uff0c\u7acb\u523b\u653e\u4f60\u51fa\u72f1\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u770b\u77e5\u5e9c\u5927\u4eba\u662f\u6709\u4e9b\u8bef\u4f1a\u4e86\uff0c\u6211\u8981\u4e86\u65ad\u5b98\u53f8\uff0c\u662f\u8981\u628a\u4e8b\u60c5\u5206\u4e2a \u66f2\u76f4\u2026\u2026\u201d \u3000\u3000\u5c39\u77e5\u5e9c\u53c8\u8981\u63a5\u53e3\uff0c\u5374\u88ab\u9a6c\u5de1\u629a\u4f38\u624b\u62e6\u4f4f\u4e86\uff0c\u5c39\u77e5\u5e9c\u53ea\u597d\u628a\u8981\u5410\u51fa\u53e3\u7684\u8bdd\uff0c\u786c\u751f\u751f\u7684\u541e\u4e86 \u56de\u53bb\u3002 \u3000\u3000\u201c\u5e94\u8be5\u7684\uff0c\u5e94\u8be5\u7684\uff0c\u6c5f\u5148\u751f\u72af\u7684\u662f\u4ec0\u4e48\u7f6a\uff0c\u8981\u5982\u4f55\u7533\u96ea\uff0c\u6587\u4e2d\u53ef\u4ee5\u7acb\u523b\u5224\u5b9a\u3002\u201d \u3000\u3000\u6c5f\u5e72\u91cc\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c39\u5927\u4eba\u4eca\u5e74\u6709\u56db\u5341\u5c81\u4e86\u5427\uff1f\u5374\u8ba8\u4e86\u4e00\u4e2a\u5341\u4e03\u5c81\u7684\u4f8d\u59be\u2026\u2026\u201d \u3000\u3000\u201c\u6709\u8fd9\u79cd\u4e8b\uff0c\u5c39\u77e5\u5e9c\uff0c\u4f60\u81ea\u5df1\u8bf4\u5427\uff01\u201d \u3000\u3000\u201c\u5351\u804c\u662f\u82b1\u94b1\u4e70\u6765\u7684\uff0c\u62d9\u8346\u4f53\u5f31\u591a\u75c5\uff0c\u5a36\u623f\u4f8d\u59be\uff0c\u662f\u4e3a\u4fbf\u4e88\u7167\u6599\u751f\u6d3b\u8d77\u5c45\u3002\u201d \u3000\u3000\u9a6c\u5de1\u629a\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6c5f\u5148\u751f\uff0c\u662f\u4e0d\u662f\u8fd9\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u201c\u5c39\u5927\u4eba\u662f\u82b1\u4e86\u4e00\u767e\u4e24\u94f6\u5b50\u3002\u4e0d\u8fc7\uff0c\u90a3\u4f4d\u59d1\u5a18\u53ef\u4e0d\u662f\u5fc3\u7518\u60c5\u613f\u9a6c\u5de1\u629a\u7709\u6bdb\u4e00\u626c\uff0c\u9053\uff1a\u201d \u90a3\u662f\u6043\u5f3a\u9738\u5360\u6c11\u5973\u4e86\uff1f\u201c \u3000\u3000\u201c\u5927\u4eba\uff01\u2026\u2026\u2026\u201d\u5c39\u77e5\u5e9c\u7a81\u7136\u8dea\u4e86\u4e0b\u53bb\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u5e55\u5bbe\u65e0\u77e5\uff0c\u4e8b\u524d\u5351\u804c\u672a\u660e\u5185\u60c5\uff0c\u4e8b \u540e\u77e5\u6653\uff0c\u7c73\u5df2\u6210\u996d\uff0c\u5351\u804c\u5931\u5bdf\uff0c\u8bf7\u5927\u4eba\u964d\u7f6a\u3002\u201d \u3000\u3000\u56db\u54c1\u9ec4\u5802\uff0c\u5a36\u623f\u4f8d\u59be\uff0c\u53c8\u82b1\u4e86\u94f6\u5b50\uff0c\u5b9e\u5728\u4e0d\u7b97\u662f\u4ec0\u4e48\u5927\u4e8b\uff0c\u5728\u90a3\u4e2a\u65f6\u4ee3\u91cc\uff0c\u4e0d\u8981\u8bf4\u5b98\u5ba6 \u4eba\u5bb6\u5566\uff0c\u5c31\u662f\u6709\u94b1\u7684\u4e61\u7ec5\u3001\u5458\u5916\uff0c\u8ba8\u4e2a\u4e09\u59bb\u56db\u59be\u7684\uff0c\u4e5f\u662f\u53f8\u7a7a\u89c1\u60ef\u7684\u4e8b\uff0c\u9a6c\u5de1\u629a\u4ea6\u597d\u6b64\u9053\uff0c \u5bb6\u91cc\u5c31\u6709\u4e24\u4e2a\u4f8d\u59be\uff0c\u53ea\u4e0d\u8fc7\uff0c\u4eca\u591c\u7684\u539f\u544a\u4e0d\u5bf9\uff0c\u5f04\u5f97\u9a6c\u5de1\u629a\u4e5f\u50f5\u5728\u90a3\u91cc\u4e86\u3002 \u3000\u3000\u4f46\u4ed6\u7a76\u7adf\u662f\u5ba6\u6d77\u5728\u5927\u5458\uff0c\u5386\u7ec3\u4e30\u5bcc\uff0c\u7565\u4e00\u6c89\u601d\uff0c\u5df2\u6709\u8ba1\u8f83\uff0c\u9053\uff1a\u201c\u67e5\u4e8b\u4e0d\u660e\uff0c\u662f\u8be5\u964d \u7f6a\uff0c\u6c5f\u5148\u751f\uff0c\u4f60\u770b\uff0c\u8be5\u529e\u5c39\u77e5\u5e9c\u4ec0\u4e48\u7f6a\u540d\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5927\u4eba\u8a00\u91cd\u4e86\uff0c\u5c39\u77e5\u5e9c\u867d\u7136\u4e0d\u662f\u4e2a\u9752\u5929\u5e9c\u53f0\uff0c\u4e5f\u4e0d\u662f\u4e2a\u8d2a\u8d43\u6789\u6cd5 \u7684\u574f\u5b98\uff0c\u6211\u770b\u5e94\u8be5\u7f5a\u4ed6\u4ff8\u94f6\u534a\u5e74\uff0c\u81f3\u4e8e\u90a3\u4f4d\u59d1\u5a18\u4e48\u2026\u2026\u201d \u3000\u3000\u5c39\u77e5\u5e9c\u63a5\u9053\uff1a\u201c\u6211\u7acb\u523b\u628a\u5979\u9063\u9001\u56de\u5bb6\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u5012\u4e0d\u7528\u4e86\uff0c\u6c5f\u67d0\u5df2\u67e5\u6e05\u695a\u4e86\uff0c\u90a3\u4e2b\u5934\u521d\u65f6\u867d\u4e0d\u60c5\u613f\uff0c\u4f46\u4f60\u5c39\u5927\u4eba\u60dc\u82b1\u6709\u672f\uff0c\u90a3\u4e2b\u5934 \u597d\u50cf\u5df2\u7518\u5fc3\u4e3a\u59be\u4e86\uff0c\u5509\uff01\u7530\u820d\u6751\u592b\uff0c\u5982\u4f55\u80fd\u6bd4\u5f97\u4e0a\u5927\u4eba\u7684\u6f47\u6d12\u6587\u96c5\uff0c\u73b0\u5728\u82e5\u9001\u5979\u56de\u53bb\uff0c\u5979\u5374 \u8fc7\u4e0d\u60ef\u90a3\u79cd\u6751\u5987\u751f\u6d3b\u4e86\u3002\u201d \u3000\u3000\u201c\u8bf4\u7684\u4e5f\u662f\uff0c\u6c5f\u5148\u751f\u5904\u7f6e\u7684\u597d\uff0c\u60c5\u7406\u517c\u987e\uff0c\u7f51\u5f00\u4e00\u9762\uff0c\u6587\u4e2d\u597d\u751f\u4f69\u670d\u3002\u5c39\u3001\u77e5\u5e9c\uff0c\u4f60\u53ef \u613f\u8ba4\u7f5a\u3002\u201d \u3000\u3000\u201c\u5351\u804c\u5148\u57ab\u51fa\u534a\u5e74\u7f5a\u4ff8\uff0c\u8fd9\u7b14\u94b1\u2026\u2026\u201d \u3000\u3000\u9a6c\u5de1\u629a\u63a5\u9053\u3002\u201c\u6c5f\u5148\u751f\u65e9\u5df2\u5b89\u6392\u4e86\u53bb\u5904\u5427\uff1f\u201d \u3000\u3000\u201c\u5c31\u9001\u7ed9\u90a3\u4e2b\u5934\u8fc7\u53bb\u7684\u9752\u6885\u65e7\u53cb\uff0c\u8ba9\u4ed6\u8ba8\u623f\u5ab3\u5987\u5427\uff01\u6b64\u4e8b\u4f20\u626c\u6c11\u95f4\uff0c\u4e0d\u4f46\u53ef\u66ff\u5e9c\u53f0\u5927\u4eba \u6811\u7acb\u5b98\u98ce\uff0c\u4e5f\u7b97\u662f\u4e00\u6869\u7f8e\u8c08\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u597d\uff0c\u6b64\u6848\u5df2\u7ed3\uff0c\u5c39\u77e5\u5e9c\u8bf7\u8d77\u6765\u5427\uff01\u201d \u3000\u3000\u201c\u662f\u662f\u662f\uff0c\u591a\u8c22\u5927\u4eba\u3002\u201d \u3000\u3000\u9a6c\u5de1\u629a\u9053\uff1a\u201c\u5c39\u77e5\u5e9c\uff0c\u6c5f\u5148\u751f\u7684\u7f6a\u540d\u662f\u2026\u2026\u2026\u201d \u3000\u3000\u201c\u8bef\u4f24\u516c\u5dee\uff0c\u800c\u4e14\u90a3\u516c\u5dee\u4f24\u52bf\u5df2\u597d\uff0c\u539f\u544a\u5df2\u65e0\u610f\u518d\u544a\uff0c\u5351\u804c\u5373\u523b\u4e0b\u4ee4\u5f00\u91ca\u3002\u201d \u3000\u3000\u8bef\u4f24\u4e86\u4ec0\u4e48\u516c\u5dee\uff1f\u5c39\u77e5\u5e9c\u6ca1\u6709\u8bf4\uff0c\u6c5f\u5343\u91cc\u4e5f\u4e0d\u63d0\uff0c\u9a6c\u5de1\u629a\u4e5f\u4e0d\u95ee\uff0c\u4f46\u5374\u5fc3\u4e2d\u6709\u6570\uff0c\u5b9a\u7136 \u662f\u66ff\u77e5\u5e9c\u5927\u4eba\u4f5c\u5a92\u7684\u90a3\u4f4d\u5e55\u5bbe\u4e86\u3002 \u3000\u3000\u201c\u6c5f\u5148\u751f\u80af\u5728\u5f00\u5c01\u5e9c\u5927\u7262\u4e4b\u4e2d\uff0c\u9664\u4e86\u4fbf\u4e8e\u67e5\u660e\u5c39\u77e5\u5e9c\u5982\u592b\u4eba\u7684\u4e8b\u60c5\u4e4b\u5916\uff0c\u6050\u6015\u8fd8\u6709\u522b\u7684 \u539f\u56e0\u5427\uff1f\u201d\u9a6c\u5de1\u629a\u5c55\u73b0\u51fa\u7cbe\u660e\u5e72\u7ec3\u3001\u6d1e\u5bdf\u7ec6\u5fae\u7684\u624d\u534e\u6765\u3002 \u3000\u3000\u201c\u5927\u4eba\u9ad8\u660e\u2026\u2026\u201d\u6c5f\u5343\u91cc\u4e0d\u5f97\u4e0d\u4f69\u670d\u4e86\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u770b\u6765\u4f60\u4eec\u505a\u5b98\u7684\u4eba\uff0c\u89c2\u5bdf\u4e8b\u7269 \u4e4b\u80fd\uff0c\u6bd4\u8d77\u6c5f\u6e56\u4eba\u7269\uff0c\u6709\u8fc7\u4e4b\u800c\u65e0\u4e0d\u53ca\u3002\u201d \u3000\u3000\u71d5\u98de\u5374\u542c\u5f97\u5fc3\u795e\u7686\u9707\uff0c\u4fbf\u4e8e\u67e5\u660e\u77e5\u5e9c\u5927\u4eba\u7684\u5bb6\u52a1\u4e8b\uff01\u90a3\u662f\u8bf4\uff0c\u6c5f\u5343\u91cc\u5728\u90a3\u7b49\u9690\u5bc6\u3001\u68ee\u4e25 \u7684\u76d1\u89c6\u4e4b\u4e0b\uff0c\u5374\u80fd\u6765\u53bb\u81ea\u5982\uff0c\u7adf\u672a\u88ab\u4eba\u53d1\u89c9\uff0c\u8fd9\u4eba\u7684\u6b66\u529f\u4e4b\u9ad8\uff0c\u8eab\u6cd5\u4e4b\u5947\uff0c\u5f53\u771f\u662f\u4e0d\u53ef\u601d\u8bae \u4e86\u3002 \u3000\u3000\u201c\u53ef\u5426\u8bf4\u51fa\u6765\u542c\u542c\u5462\uff1f\u201d\u9a6c\u5de1\u629a\u6b65\u6b65\u7d27\u903c\u5730\u95ee\u9053\uff1a\u201c\u4ee5\u6c5f\u5148\u751f\u8eab\u624b\u4e4b\u9ad8\uff0c\u8981\u67e5\u660e\u4e00\u6869\u5c0f \u4e8b\uff0c\u5b9e\u4e5f\u7528\u4e0d\u7740\u5728\u7262\u4e2d\u59d4\u5c48\u81ea\u5df1\uff0c\u6587\u4e2d\u867d\u7136\u60f3\u5230\u4e86\u522b\u6709\u7f18\u6545\uff0c\u4f46\u5374\u77e5\u5176\u7136\uff0c\u4e0d\u77e5\u5176\u6240\u4ee5 \u7136\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u7684\u795e\u60c5\u7a81\u7136\u8f6c\u4e3a\u4e25\u8083\uff0c\u4f4e\u58f0\u8bf4\uff1a\u201c\u71d5\u603b\u6355\u5934\uff0c\u5e03\u5b88\u5728\u5185\u5385\u5916\u9762\u7684\u4eba\uff0c\u53ef\u90fd\u9760\u5f97\u4f4f \u4e48\uff1f\u201d \u3000\u3000\u201c\u539f\u6765\uff0c\u706f\u706b\u8f89\u714c\u5904\uff0c\u770b\u4e0d\u5230\u6709\u4eba\u6212\u5907\uff0c\u4f46\u5385\u5916\u7684\u591c\u6697\u4e4b\u4e2d\uff0c\u5374\u662f\u5b88\u5907\u68ee\u4e25\uff0c\u6709\u4e09\u5341\u591a \u540d\u6355\u5feb\uff0c\u73af\u536b\u5728\u5185\u5385\u56db\u5468\u3002 \u3000\u3000\u201c\u9760\u5f97\u4f4f\uff0c\u90fd\u662f\u6211\u624b\u4e0b\u7684\u6355\u5feb\uff0c\u6211\u81ea\u4fe1\u57cb\u4f0f\u5f97\u5341\u5206\u9690\u5bc6\uff0c\u60f3\u4e0d\u5230\u4ecd\u7136\u7792\u4e0d\u8fc7\u6c5f\u5144\u7684\u8033 \u76ee\u3002\u201d \u3000\u3000\u6c5f\u5e72\u91cc\u9053\uff1a\u201c\u90a3\u5c31\u597d\uff0c\u4e0d\u6562\u76f8\u7792\u4e24\u4f4d\u5927\u4eba\u548c\u71d5\u603b\u6355\u5934\uff0c\u6c5f\u67d0\u4eba\u7518\u613f\u5750\u7262\u6570\u6708\uff0c\u662f\u4e3a\u4e86\u8ffd \u67e5\u4e00\u4e2a\u540d\u6ee1\u6c5f\u6e56\u7684\u795e\u5077\u2026\u201d \u3000\u3000\u201c\u4ec0\u4e48\uff1f\u795e\u5077\u2026\u2026\u201d\u71d5\u98de\u987f\u89c9\u4e00\u9635\u8138\u7ea2\uff0c\u8ffd\u795e\u5077\u8ffd\u5230\u5f00\u5c01\u5e9c\u8859\u95e8\u91cc\uff0c\u8981\u4ed6\u4eec\u8fd9\u4e2a\u5f00\u5c01\u5e9c \u7684\u603b\u6355\u5934\uff0c\u8138\u4e0a\u5982\u4f55\u80fd\u6302\u5f97\u4f4f\u3002 \u3000\u3000\u201c\u8bf4\u8d77\u6765\uff0c\u71d5\u603b\u6355\u5934\u4e5f\u8be5\u77e5\u9053\u2026\u201d\u6c5f\u5343\u91cc\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u542c\u8bf4\u8fc7\u8f6c\u9f99\u624b\u5f20\u4e0d\u7a7a\u5427\uff1f\u201d \u3000\u3000\u201c\u6c5f\u89c1\u662f\u8bf4\u8f6c\u9f99\u624b\u5f20\u4e0d\u7a7a\u85cf\u8eab\u5728\u6211\u7684\u5f00\u5c01\u5e9c\u8859\u91cc\uff1f\u201d \u3000\u3000\u71d5\u98de\u7684\u8138\u4e0a\u4e00\u7247\u6012\u8272\uff0c\u8fde\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u4e5f\u6709\u4e9b\u98a4\u6296\u8d77\u6765\u4e86\u3002 \u3000\u3000\u201c\u662f\u7684\uff0c\u71d5\u8001\u5f1f\uff0c\u5f20\u4e0d\u7a7a\u7684\u5077\u7a83\u4e4b\u6280\uff0c\u5929\u4e0b\u65e0\u53cc\uff0c\u800c\u4e14\u884c\u6b62\u8be1\u5bc6\uff0c\u53c8\u64c5\u957f\u6613\u5bb9\u4e4b\u672f\uff0c\u5211 \u90e8\u7684\u5218\u603b\u6355\u5934\uff0c\u66fe\u7ecf\u82b1\u8d39\u534a\u5e74\u65f6\u5149\uff0c\u5c3d\u51fa\u5211\u90e8\u5e72\u5458\uff0c\u4ecd\u7136\u65e0\u6cd5\u67e5\u51fa\u4ed6\u7684\u884c\u8e2a\uff0c\u5509\uff01\u8fd9\u4e2a\u4eba\u4e13 \u5077\u5929\u4e0b\u540d\u8d35\u7684\u73e0\u5b9d\u53e4\u73a9\uff0c\u4ece\u4e0d\u5931\u624b\uff0c\u5404\u5730\u7684\u5b98\u5e9c\u90fd\u5949\u6709\u683c\u6740\u52ff\u8bba\u7684\u4e0a\u8c15\u3002\u201d \u3000\u3000\u201c\u6211\u770b\u8fc7\u8fd9\u9053\u4e0a\u8c15\u3002\u201d\u5c39\u77e5\u5e9c\u9053\uff1a\u201c\u4ed6\u5077\u5230\u7687\u5bab\u5185\u82d1\u53bb\u4e86\uff0c\u771f\u662f\u7f6a\u8be5\u4e07\u6b7b\uff0c\u6c5f\u5148\u751f\u65e2\u7136 \u53d1\u89c9\u4e86\u4ed6\u7684\u884c\u8e2a\uff0c\u5feb\u4e9b\u628a\u4ed6\u7f09\u62ff\u5f52\u6848\uff0c\u4e5f\u662f\u5927\u529f\u4e00\u4ef6\uff0c\u4e0d\u77e5\u4ed6\u73b0\u5728\u4f55\u5904\uff1f\u201d \u3000\u3000\u201c\u5c31\u5728\u5e9c\u8859\u4e4b\u4e2d\u2026\u2026\u201d \u3000\u3000\u4e00\u4e0b\u5b50\u50cf\u88ab\u706b\u70e7\u5230\u4e86\u5c41\u80a1\uff0c\u5c39\u77e5\u5e9c\u51e0\u4e4e\u662f\u8df3\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u4f60\u662f\u8bf4\uff0c\u4ed6\u5c31\u4f4f\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u70b9\u70b9\u5934\u3002 \u3000\u3000\u201c\u8fd9\u8fd8\u5f97\u4e86\uff0c\u4e0a\u8c15\u7f09\u62ff\u7684\u8981\u72af\uff0c\u7adf\u7136\u8eb2\u5728\u8859\u95e8\u91cc\uff0c\u5feb\uff01\u71d5\u603b\u6355\u5934\uff0c\u53bb\u628a\u4ed6\u6293\u6765\u5f52\u6848\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u201d\u71d5\u98de\u5e94\u4e86\u4e00\u58f0\uff0c\u56de\u987e\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u6c5f\u5144\uff0c\u8bf7\u6307\u70b9\u5144\u5f1f\u4e00\u4e2a\u65b9\u4f4d\u3002\u201d \u3000\u3000\u201c\u6025\u4e5f\u4e0d\u5728\u4e00\u65f6\uff0c\u71d5\u8001\u5f1f\uff0c\u5f20\u4e0d\u7a7a\u884c\u8e2a\u98d8\u5ffd\uff0c\u8b66\u89c9\u4e4b\u5fc3\u5947\u9ad8\uff0c\u7a0d\u6709\u98ce\u5439\u8349\u52a8\uff0c\u4ed6\u5c31\u53ef\u80fd \u95fb\u98ce\u800c\u9003\uff0c\u518d\u60f3\u627e\u4ed6\uff0c\u90a3\u5c31\u5982\u5927\u6d77\u635e\u9488\u4e86\u3002\u8001\u5b9e\u8bf4\uff0c\u5211\u90e8\u4e2d\u591a\u5458\u9ad8\u624b\uff0c\u8ffd\u5230\u4ed6\u51fa\u751f\u7684\u539f\u7c4d\uff0c \u7adf\u7136\u67e5\u4e0d\u51fa\u4e00\u70b9\u7ebf\u7d22\uff0c\u8fde\u5f20\u4e0d\u7a7a\u8fd9\u4e2a\u540d\u5b57\uff0c\u53ef\u80fd\u90fd\u662f\u5047\u7684\uff0c\u771f\u662f\u6625\u98ce\u62c2\u9762\u4e0d\u7559\u75d5\u2026\u2026\u201d \u3000\u3000\u201c\u53ef\u662f\u2026\u2026\u201d\u5c39\u77e5\u5e9c\u6025\u6025\u63a5\u9053\uff1a\u201c\u4e8b\u4e0d\u5b9c\u8fdf\u554a\uff01\u8fdf\u6050\u6709\u53d8\uff0c\u6c5f\u5148\u751f\uff0c\u6211\u770b\u65e9\u70b9\u884c\u52a8\uff0c\u624d \u80fd\u638c\u63e1\u5148\u673a\u2026\u2026\u201d \u3000\u3000\u201c\u6c5f\u5148\u751f\u8bf4\u5f97\u5bf9\uff01\u201d\u9a6c\u5de1\u629a\u8bf4\u8bdd\u4e86\uff1a\u201c\u8c0b\u5b9a\u800c\u540e\u52a8\uff0c\u8981\u4e00\u4e3e\u6210\u529f\u3002\u201d \u3000\u3000\u5c39\u77e5\u5e9c\u4e0d\u6562\u518d\u591a\u8a00\u4e86\uff0c\u4f46\u4ed6\u7126\u8651\u7684\u795e\u8272\uff0c\u5374\u6ea2\u6ee1\u5728\u7709\u76ee\u4e4b\u95f4\u3002 \u3000\u3000\u539f\u6765\uff0c\u5c39\u77e5\u5e9c\u7684\u516c\u9986\uff0c\u5c31\u5728\u5f00\u5c01\u5e9c\u8859\u540e\u8fb9\uff0c\u76f8\u8fde\u4e00\u5904\uff0c\u8f6c\u9f99\u624b\u533f\u9690\u5728\u5e9c\u884c\u4e2d\uff0c\u4e5f\u5c31\u662f\u5728 \u4ed6\u7684\u5367\u69bb\u4e4b\u4fa7\uff0c\u53eb\u4ed6\u5982\u4f55\u4e0d\u6025\u3002 \u3000\u3000\u71d5\u98de\u9053\uff1a\u201c\u5728\u4e0b\u6709\u4e9b\u60f3\u4e0d\u660e\u767d\uff0c\u8f6c\u9f99\u624b\u5f20\u4e0d\u7a7a\u516c\u8eb2\u5728\u4ec0\u4e48\u5730\u65b9\u5462\uff1f\u201d \u3000\u3000\u201c\u76d1\u7262\u91cc\u3002\u201d \u3000\u3000\u201c\u5662\uff01\u2026\u2026\u201d\u5c39\u77e5\u5e9c\u604d\u7136\u5927\u609f\u63a5\u9053\uff1a\u201c\u72af\u4eba\u2026\u2026\u201d \u3000\u3000\u201c\u5f20\u4e0d\u7a7a\u4e0d\u50cf\u6c5f\u67d0\u4eba\u8fd9\u4e48\u80fd\u5403\u82e6\u8010\u52b3\uff0c\u8e72\u5728\u7262\u623f\u91cc\uff0c\u6ecb\u5473\u5e76\u4e0d\u597d\u53d7\u3002\u201d \u3000\u3000\u201c\u6c5f\u5144\u662f\u8bf4\u4ed6\u5bc4\u8eab\u5728\u7981\u5352\u4e4b\u4e2d\u2026\u2026\u201d\u71d5\u98de\u7ec8\u4e8e\u60f3\u51fa\u4e86\u53ef\u80fd\u9690\u533f\u7684\u6240\u5728\u3002 \u3000\u3000\u201c\u6709\u7981\u5352\uff0c\u4e5f\u6709\u7262\u5934\u2026\u2026\u201d\u6c5f\u5343\u91cc\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8f6c\u9f99\u624b\u4e0d\u662f\u4e00\u4e2a\u4eba\u6d3b\u52a8\uff0c\u4ed6\u9886\u5bfc\u4e00\u4e2a \u7ec4\u5408\uff0c\u4e00\u5171\u6709\u591a\u5c11\u4eba\u6211\u8fd8\u6ca1\u6709\u67e5\u51fa\u6765\uff0c\u4f46\u6f5c\u9690\u5728\u76d1\u72f1\u4e2d\u7684\u4eba\uff0c\u81f3\u5c11\u6709\u4e09\u4e2a\u4ee5\u4e0a\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u6240\u7ba1\u7406\u662f\u51e0\u53f7\u7262\u623f\u2026\u2026\u201d\u71d5\u98de\u95ee\u9053\uff1a\u201c\u4eba\u624b\u662f\u4e0d\u662f\u96c6\u4e2d\u5728\u4e00\u5904\uff1f\u201d \u3000\u3000\u6c5f\u5e72\u91cc\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u71d5\u603b\u6355\u5934\u521a\u521a\u8fd8\u89c1\u8fc7\u4ed6\u2026\u2026\u201d \u3000\u3000\u201c\u4e09\u53f7\u7262\u623f\u7684\u5f20\u7262\u5934\u2026\u2026\u201d\u71d5\u98de\u7684\u8138\u8272\u53d8\u4e86\uff0c\u54ac\u7259\u5207\u9f7f\u5730\u9053\uff1a\u201c\u53ef\u6076\uff01\u6211\u53bb\u6293\u4ed6\u5f52 \u6848\u3002\u201d \u3000\u3000\u4e00\u4f38\u624b\uff0c\u62e6\u4f4f\u4e86\u71d5\u98de\uff0c\u6c5f\u5343\u91cc\u4f4e\u7740\u5934\u9053\uff1a\u201c\u6682\u8bf7\u606f\u6012\uff0c\u8f6c\u9f99\u624b\u5077\u7a83\u4e4b\u6280\uff0c\u51a0\u7edd\u5929\u4e0b\uff0c\u62e5 \u6709\u7684\u8d22\u5bcc\uff0c\u4e16\u65e0\u5176\u5339\uff0c\u4ed6\u8981\u627e\u4e2a\u9690\u5bc6\u7684\u5730\u65b9\u8eb2\u8d77\u6765\uff0c\u5e76\u4e0d\u56f0\u96be\uff0c\u4e3a\u4ec0\u4e48\u7518\u613f\u5316\u8eab\u4e00\u4e2a\u7262\u5934\uff0c \u6df7\u5165\u5f00\u5c01\u5e9c\u4e2d\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u662f\u554a\u2026\u2026\u201d\u9a6c\u5de1\u629a\u672c\u6709\u8981\u4e8b\uff0c\u53d7\u9ad8\u4eba\u6307\u70b9\uff0c\u8bf7\u6c42\u6c5f\u5343\u91cc\u633a\u8eab\u76f8\u52a9\uff0c\u4f46\u73b0\u5728\uff0c\u5374\u56e0\u8f6c\u9f99 \u624b\u9690\u533f\u5f00\u5c01\u5e9c\u8857\u4e00\u4e8b\uff0c\u5f15\u8d77\u4e86\u5f3a\u70c8\u7684\u597d\u5947\uff0c\u7f13\u7f13\u63a5\u9053\uff1a\u201c\u5f20\u4e0d\u7a7a\u5b9a\u7136\u662f\u53e6\u6709\u6240\u56fe\uff0c\u624d\u7518\u4e3a\u7262 \u5934\uff0c\u6c5f\u5148\u751f\u53ef\u662f\u5df2\u67e5\u51fa\u4e00\u70b9\u7709\u76ee\uff1f\u201d \u3000\u3000\u4ed6\u867d\u4e45\u5386\u5b98\u573a\u98ce\u4e91\uff0c\u6e21\u8fc7\u591a\u91cd\u8270\u9669\uff0c\u664b\u8eab\u4e8e\u4e00\u54c1\u5927\u540f\u4e4b\u4f4d\uff0c\u81ea\u5177\u6709\u5e38\u4eba\u6240\u96be\u53ca\u5224\u65ad\u4e8b\u7406 \u4e4b\u80fd\uff0c\u4e0d\u8fc7\uff0c\u5bf9\u6c5f\u6e56\u4e8b\u7269\u8be1\u8c32\u7384\u5947\u7684\u53d8\u5316\uff0c\u53c8\u975e\u4ed6\u80fd\u6599\u65ad\uff0c\u4f46\u9971\u8bfb\u7ecf\u4e66\uff0c\u80f8\u85cf\u9526\u7ee3\u7684\u624d\u5b66\uff0c \u5374\u80fd\u4e3e\u4e00\u53cd\u4e09\uff0c\u6c5f\u5e72\u91cc\u63d0\u51fa\u4e00\u4e2a\u7591\u95ee\uff0c\u4ed6\u5c31\u8054\u60f3\u51fa\u4e00\u4e9b\u4e8b\u673a\uff0c\u53ea\u4e0d\u8fc7\u65e0\u6cd5\u731c\u6d4b\u51fa\u5185\u60c5\u53d8\u5316\u7f62 \u4e86\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u53cc\u76ee\u70af\u70af\uff0c\u51dd\u6ce8\u5728\u9a6c\u5de1\u629a\u7684\u8138\u4e0a\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5927\u4eba\u662f\u5426\u4e5f\u60f3\u5230\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u7b11\u9053\uff1a\u201c\u8fd9\u5c31\u4e0d\u597d\u731c\u4e86\uff0c\u4e0b\u5b98\u5bf9\u6c5f\u6e56\u4e2d\u4e8b\uff0c\u4e00\u65e0\u6240\u77e5\uff0c\u4e0d\u8fc7\uff0c\u4ee5\u5e38\u60c5\u63a8\u8bba\uff0c\u4ed6\u9690 \u8eab\u4e8e\u5f00\u5c01\u5e9c\u8857\u4e2d\uff0c\u53ef\u80fd\u548c\u5f00\u5c01\u5e9c\u8859\u95e8\u6709\u5173\uff1f\u201d\u2019\u201c\u5927\u4eba\u867d\u672a\u8bf4\u5f97\u5f88\u6e05\u695a\uff0c\u5927\u4f53\u4e0a\u800c\u8a00\u7b97\u662f\u731c \u5bf9\u4e86\u3002\u201d\u6c5f\u5343\u91cc\u795e\u60c5\u8083\u7136\u9053\uff1a\u201c\u8f6c\u9f99\u624b\u7518\u613f\u59d4\u8eab\u4e8e\u4e09\u53f7\u7262\u623f\u7684\u7262\u5934\u8fd9\u4e2a\u5de5\u4f5c\uff0c\u5b9e\u5728\u662f\u4e3a\uff46\u60f3 \u5bfb\u627e\u4e00\u4ef6\u4e1c\u897f\u3002\u201d \u3000\u3000\u201c\u4e0b\u5b98\u8fd9\u5c31\u4e0d\u660e\u767d\u4e86\u3002\u201d\u5c39\u77e5\u5e9c\u63a5\u9053\uff1a\u201c\u5f20\u7262\u5934\u53eb\u5f20\u4e5d\uff0c\u5728\u5f00\u5c01\u5e9c\u4f5c\u76d1\u623f\u7684\u7262\u5934\uff0c\u5df2\u6709 \u5f88\u591a\u5e74\u4e86\uff0c\u672c\u5e9c\u5230\u4efb\u5df2\u7ecf\u4e09\u5e74\uff0c\u5230\u4efb\u65f6\u70b9\u9a8c\u5404\u623f\u73ed\u5f79\uff0c\u4ed6\u5c31\u5728\u4e09\u53f7\u76d1\u623f\u4efb\u7262\u5934\u7684\u804c\u4f4d\uff0c\u518d\u8bf4 \u4e0b\u5b98\u5e76\u672a\u6536\u85cf\u4ec0\u4e48\u73cd\u8d35\u5b9d\u7269\uff0c\u96be\u9053\u5f20\u4e5d\u5373\u8f6c\u9f99\u624b\u7684\u5316\u8eab\u4e0d\u6210\uff1f\u8fd9\u591a\u5e74\uff0c\u4ed6\u4e00\u76f4\u9690\u8eab\u4e8e\u6b64\u3002\u201d \u3000\u3000\u201c\u5f53\u7136\u4e0d\u662f\uff0c\u5c39\u5927\u4eba\u5982\u679c\u77e5\u9053\u8f6c\u9f99\u624b\u5f20\u4e0d\u7a7a\u7684\u80fd\u8010\uff0c\u5c31\u4e0d\u4f1a\u89c9\u5f97\u5947\u602a\u4e86\u2026\u2026\u201d\u6c5f\u5343\u91cc\u8bf4 \u9053\uff1a\u201c\u8f6c\u9f99\u624b\u4e0d\u662f\u5f20\u4e5d\uff0c\u4e5f\u4e0d\u53ef\u80fd\u9690\u8eab\u5728\u6b64\u5730\u5f88\u591a\u5e74\uff0c\u4f46\u4ed6\u7cbe\u4e8e\u6613\u5bb9\u4e4b\u672f\uff0c\u53c8\u5584\u6a21\u4eff\u5404\u5730\u65b9 \u8a00\uff0c\u4e0d\u7559\u5fc3\u5f88\u96be\u770b\u5f97\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u771f\u7684\u5f20\u4e5d\u5462\uff1f\u201d\u5c39\u77e5\u5e9c\u4e0d\u670d\u6c14\u5730\u95ee\u3002 \u3000\u3000\u201c\u53ea\u6015\u65e9\u5df2\u88ab\u6740\u5bb3\u4e86\uff01\u201d \u3000\u3000\u201c\u6212\u62a4\u4e09\u53f7\u7262\u623f\u7684\u4e00\u5171\u4e03\u4e2a\u7981\u5352\uff0c\u4ed6\u4eec\u5e38\u5e74\u5747\u548c\u5f20\u4e5d\u76f8\u5904\uff0c\u96be\u9053\u4f1a\u770b\u4e0d\u51fa\u7834\u7efd\uff1f\u201d \u3000\u3000\u201c\u53ef\u6015\u7684\u5730\u65b9\u4e5f\u5c31\u5728\u6b64\u4e86\uff0c\u4e03\u4e2a\u7981\u5352\u53ef\u80fd\u88ab\u6740\u8fc7\u534a\uff0c\u4f59\u4e0b\u7684\u4e0d\u662f\u53d7\u5236\u4e0d\u6562\u58f0\u5f20\uff0c\u5c31\u662f\u88ab \u8f6c\u9f99\u624b\u91cd\u91d1\u4e70\u901a\uff0c\u6700\u597d\u7684\u4e00\u9879\u63a8\u8bba\uff0c\u4ed6\u4eec\u5fc3\u4e2d\u6709\u70b9\u6000\u7591\uff0c\u4f46\u56e0\u4e3a\u6ca1\u6709\u8bc1\u636e\uff0c\u4e0d\u4fbf\u968f\u4fbf\u51fa\u9762\u544a \u53d1\u3002\u201d \u3000\u3000\u201c\u8fd9\u7b80\u76f4\u662f\u9a87\u4eba\u542c\u95fb\u4e86\u2026\u2026\u201d\u5c39\u77e5\u5e9c\u76ee\u5149\u8f6c\u6ce8\u5230\u71d5\u98de\u7684\u8eab\u4e0a\uff0c\u9053\uff1a\u201c\u71d5\u603b\u6355\u5934\uff0c\u4f60\u8981\u5c3d \u51fa\u7cbe\u9510\uff0c\u6293\u5230\u8f6c\u9f99\u624b\u62f7\u95ee\u4e2a\u660e\u767d\uff0c\u4e00\u4e0b\u5b50\u6740\u5bb3\u4e86\u6570\u6761\u4eba\u547d\uff0c\u5f53\u771f\u662f\u76ee\u65e0\u738b\u6cd5\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u77e5\u5e9c\u5927\u4eba\uff0c\u8f6c\u9f99\u624b\u4e1c\u897f\u8fd8\u672a\u5230\u624b\uff0c\u53ea\u8981\u54b1\u4eec\u76ee\u4e0b\u7684\u8c08\u8bdd\uff0c\u4e0d\u4f1a \u8d70\u6f0f\u6d88\u606f\uff0c\u4ed6\u5e94\u8be5\u4e0d\u4f1a\u79bb\u5f00\u3002\u201d \u3000\u3000\u5c39\u77e5\u5e9c\u9053\uff1a\u201c\u53ef\u662f\uff0c\u5175\u8d35\u795e\u901f\uff0c\u4e0d\u5b9c\u8fdf\u5ef6\u2026\u2026\u201d \u3000\u3000\u201c\u5c39\u77e5\u5e9c\u2026\u2026\u201d\u9a6c\u5de1\u629a\u63a5\u53e3\u4e86\uff1a\u201c\u6c5f\u5148\u751f\u65e2\u7136\u8bf4\u51fa\u4e86\u5185\u60c5\uff0c\u81ea\u662f\u4e0d\u4f1a\u5750\u89c6\uff0c\u6349\u62ff\u8f6c\u9f99\u624b \u7684\u4e8b\uff0c\u5c31\u542c\u6c5f\u5148\u751f\u7684\u5b89\u6392\u5427\uff01\u201d \u3000\u3000\u201c\u5351\u804c\u53d7\u6559\uff01\u201d\u5c39\u77e5\u5e9c\u606d\u5e94\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u201c\u9a6c\u5927\u4eba\u597d\u91cd\u4e00\u9876\u7684\u5e3d\u5b50\u554a\uff01\u201d\u6c5f\u5343\u91cc\u82e6\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u4e09\u66f4\u65f6\u5206\u52a8\u624b\uff0c\u6c5f\u67d0\u4eba\u6ca1\u6709\u548c\u8f6c \u9f99\u624b\u5bf9\u8fc7\u9635\uff0c\u4e0d\u77e5\u9053\u80fd\u4e0d\u80fd\u5236\u4f4f\u4ed6\u2026\u2026\u201d \u3000\u3000\u5c39\u77e5\u5e9c\u63a5\u9053\uff1a\u201c\u71d5\u603b\u6355\u5934\u4f1a\u5168\u529b\u5e2e\u52a9\u4f60\u3002\u201d \u3000\u3000\u201c\u6c5f\u67d0\u4e5f\u4f1a\u5168\u529b\u4ee5\u8d74\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u4e8c\u66f4\u5de6\u53f3\u4e86\u5427\uff01\u2026\u2026\u201d\u9a6c\u5de1\u629a\u9053\uff1a\u201c\u71d5\u603b\u6355\u5934\u8981\u542c\u6c5f\u5148\u751f\u7684\u8c03\u5ea6\u2026\u2026\u201d \u3000\u3000\u201c\u662f\uff01\u5351\u804c\u4e00\u5207\u9075\u7167\u6c5f\u5148\u751f\u4e4b\u547d\u884c\u4e8b\u3002\u201d \u3000\u3000\u201c\u4e0d\u6562\uff0c\u4e0d\u6562\uff0c\u6c5f\u67d0\u5168\u529b\u548c\u71d5\u603b\u6355\u5934\u914d\u5408\uff0c\u5012\u6709\u4e00\u4ef6\u4e8b\uff0c\u8981\u5148\u884c\u51c6\u5907\u3002\u201d \u3000\u3000\u201c\u6c5f\u5144\u5429\u5490\uff0c\u71d5\u98de\u7acb\u523b\u53bb\u529e\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u5148\u5c31\u5e03\u5b88\u5728\u5927\u5385\u5916\u7684\u6355\u5feb\uff0c\u9009\u4e00\u4e9b\u7cbe\u5e72\u7684\u4eba\u5458\uff0c\u5728\u4e09\u53f7\u7262\u623f\u5916\u9762\u7684\u901a\u8def\u57cb \u4f0f\uff0c\u52a8\u4f5c\u8981\u5c0f\u5fc3\u8c28\u614e\uff0c\u4e0d\u8981\u592a\u63a5\u8fd1\uff0c\u8f6c\u9f99\u624b\u673a\u8b66\u7684\u5f88\uff0c\u6700\u597d\u80fd\u8c03\u96c6\u4e00\u4e9b\u519b\u9a6c\uff0c\u628a\u5f00\u5c01\u5e9c\u5148\u56f4 \u8d77\u6765\uff0c\u8981\u4ed6\u4eec\u6697\u5e26\u706b\u628a\uff0c\u542c\u53f7\u4ee4\u4e00\u8d77\u70b9\u71c3\uff0c\u5f13\u7bad\u624b\u5e03\u5b88\u56db\u5468\uff0c\u9a81\u5200\u624b\u5165\u8859\u52a9\u6218\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u71d5\u603b\u6355\u5934\u8bf7\u4f20\u6211\u4ee4\u6e1d\uff0c\u8981\u6b65\u5175\u603b\u9886\u738b\u82f1\u70b9\u4e94\u767e\u7cbe\u5175\uff0c\u4e00\u767e\u540d\u5f13\u7bad\u624b\uff0c\u56f4\u4f4f\u5f00\u5c01\u5e9c \u884c\uff0c\u6b65\u8981\u8f7b\u5de7\uff0c\u4e0d\u5f97\u53d1\u51fa\u58f0\u606f\uff0c\u60ca\u52a8\u4eba\u755c\uff0c\u8fdd\u8005\u6740\u65e0\u8d66\uff0c\u9650\u4e09\u66f4\u4e4b\u524d\u5e03\u7f6e\u5b8c\u6210\uff0c\u518d\u548c\u738b\u7edf\u9886 \u6765\u6b64\u89c1\u6211\u3002\u201d \u3000\u3000\u201c\u662f\uff0c\u5351\u804c\u7acb\u523b\u4f1a\u529e\u3002\u201d\u71d5\u98de\u8fc5\u5feb\u7684\u95ea\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5de1\u629a\u5927\u4eba\u8fd8\u662f\u4e2a\u7528\u5175\u7684\u80fd\u624b\u554a\uff01\u201d \u3000\u3000\u201c\u7eb8\u4e0a\u8c08\u5175\u7f62\u4e86\uff0c\u5982\u4e0d\u662f\u6c5f\u5148\u751f\u5df2\u7ecf\u6307\u70b9\u51fa\u6765\uff0c\u6587\u4e2d\u5c31\u4e0d\u77e5\u5982\u4f55\u8c03\u5ea6\u4e86\u3002\u201d\u8bed\u58f0\u4e00\u987f\uff0c \u53c8\u9053\uff1a\u201c\u8f6c\u9f99\u624b\u8981\u5728\u5f00\u5c01\u5e9c\u8857\u4e2d\u53d6\u5f97\u4ec0\u4e48\u4e1c\u897f\uff0c\u6c5f\u5148\u751f\u4e5f\u65e9\u5df2\u63a2\u542c\u51fa\u6765\u4e86\u5427\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u548c\u6c5f\u6e56\u4e0a\u7684\u670b\u53cb\u6709\u5173\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u4eec\u662f\u4e0d\u4fbf\u542c\u95fb\u4e86\uff1f\u201d\u9a6c\u5de1\u629a\u7b11\u7b11\u9053\uff1a\u201c\u4e0d\u80fd\u8bf4\u5c31\u4e0d\u7528\u8bf4\u4e86\u3002\u201d \u3000\u3000\u201c\u5012\u4e5f\u4e0d\u662f\u2026\u2026\u201d \u3000\u3000\u6c5f\u5343\u91cc\u987f\u4e86\u987f\uff0c\u7f13\u7f13\u5730\u53c8\u9053\uff1a\u201c\u5927\u4eba\u660e\u5bdf\u79cb\u6beb\uff0c\u60f3\u6765\u81ea\u6709\u9002\u5f53\u5904\u65ad\uff0c\u6c5f\u67d0\u4e0d\u60f3\u9690\u7792\uff0c\u5f00 \u5c01\u5e9c\u8859\u4e09\u53f7\u7262\u623f\u5185\u56da\u7981\u4e86\u4e00\u4f4d\u91cd\u5211\u8981\u72af\uff0c\u5728\u6c5f\u6e56\u4e0a\u662f\u5927\u5927\u6709\u540d\u7684\u4eba\u7269\uff0c\u540d\u53eb\u59ec\u91cd\u5929\uff0c\u5165\u72f1 \u65f6\uff0c\u5df2\u8eab\u53d7\u91cd\u4f24\uff0c\u4e3a\u9632\u4ed6\u9003\u8d70\uff0c\u53c8\u7528\u94c1\u94fe\u9501\u4f4f\u4e86\u7435\u7436\u9aa8\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u4eba\u4e0d\u754f\u4e94\u5211\uff0c\u4e0d\u80af\u8ba4\u7f6a\uff0c\u5df2\u7136\u8fc7\u4e86\u4e94\u5802\uff0c\u4ed6\u6b7b\u4e0d\u5212\u62bc\u3002\u201d \u3000\u3000\u5c39\u77e5\u5e9c\u89e3\u91ca\u9053\uff1a\u201c\u4ee5\u81f4\u4e8e\u62d6\u5ef6\u4e86\u4e24\u5e74\uff0c\u65e0\u6cd5\u5b9a\u8bba\uff0c\u5351\u804c\u5df2\u7533\u62a5\u629a\u5e9c\uff0c\u8bf7\u547d\u5b9a\u593a\u3002\u201d \u3000\u3000\u201c\u55ef\uff01\u201d\u6211\u8bb0\u5f97\u770b\u8fc7\u8fd9\u4e2a\u6848\u5b50\uff0c\u6c5f\u5148\u751f\uff0c\u8fd9\u4e2a\u4eba\u662f\u597d\u4eba\u8fd8\u662f\u574f\u4eba\uff1f\u201c\u8be5\u8d66\u8fd8\u662f\u8be5\u6740 \u5462\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u8fd9\u4e2a\u6c5f\u67d0\u5c31\u4e0d\u4fbf\u8bf4\u4e86\uff0c\u5982\u8bba\u4ed6\u5728\u6c5f\u6e56\u4e0a\u7684\u58f0\u8a89\uff0c\u5012\u662f\u4ecb\u4e8e\u6b63\u90aa\u4e4b\u95f4\u3002\u201d \u3000\u3000\u9a6c\u5de1\u629a\u9053\uff1a\u201c\u8f6c\u9f99\u624b\u4e3a\u4ed6\u800c\u6765\uff0c\u662f\u51c6\u5907\u6551\u4ed6\u51fa\u53bb\u4e86\uff1f\u201d \u3000\u3000\u201c\u8f6c\u9f99\u624b\u8981\u6551\u4ed6\uff0c\u65e9\u5c31\u6551\u8d70\u4e86\uff0c\u5c82\u4f1a\u62d6\u5ef6\u5982\u6b64\u4e4b\u4e45\uff0c\u53ea\u56e0\u4ed6\u8eab\u6000\u4e00\u672c\u6b66\u5b66\u5947\u4e66\uff0c\u8f6c\u9f99\u624b \u610f\u56fe\u8c0b\u53d6\uff0c\u624d\u59d4\u8eab\u7262\u5934\u7684\u5de5\u4f5c\uff0c\u4f46\u59ec\u91cd\u5929\u751f\u5c31\u4e00\u526f\u786c\u9aa8\u5934\uff0c\u4efb\u51ed\u8f6c\u9f99\u624b\u767e\u822c\u7684\u6298\u78e8\uff0c\u5c31\u662f\u4e0d \u80af\u8bf4\u51fa\u85cf\u4e66\u7684\u6240\u5728\u3002\u4f46\u6c5f\u67d0\u8fdb\u5165\u4e86\u7262\u623f\u4e4b\u540e\uff0c\u5012\u662f\u7ed9\u59ec\u91cd\u5929\u4e00\u4e2a\u5598\u606f\u7684\u673a\u4f1a\uff0c\u8f6c\u9f99\u624b\u6709\u6240\u987e \u5fcc\uff0c\u5c31\u5f88\u5c11\u62f7\u6253\u59ec\u91cd\u5929\u4e86\uff0c\u53cd\u800c\u6bcf\u65e5\u5907\u4e86\u9152\u83dc\uff0c\u628a\u59ec\u91cd\u5929\u517b\u5f97\u8eab\u4f53\u5927\u5927\u597d\u8f6c\u3002\u201d \u3000\u3000\u9a6c\u5de1\u629a\u63a5\u9053\uff1a\u201c\u5982\u82e5\u80fd\u627e\u5230\u90a3\u672c\u6b66\u5b66\u5947\u4e66\uff0c\u5c31\u9001\u7ed9\u6c5f\u5148\u751f\uff0c\u4ee5\u4f5c\u916c\u8c22\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\uff0c\u6c5f\u67d0\u5c31\u5148\u8c22\u8fc7\u5927\u4eba\u4e86\u3002\u201d \u3000\u3000\u5c39\u77e5\u5e9c\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4e00\u4e2a\u56da\u7981\u8981\u72af\u7684\u7262\u623f\u4e2d\uff0c\u7adf\u7136\u6709\u7740\u5982\u6b64\u66f2\u6298\u7684\u4e8b \u513f\u2026\u2026\u201d \u3000\u3000\u71d5\u98de\u52a8\u4f5c\u5f88\u5feb\uff0c\u4e0d\u8fc7\u987f\u996d\u5de5\u592b\uff0c\u5c31\u8d76\u4e86\u56de\u6765\uff0c\u5148\u5411\u9a6c\u5de1\u629a\u62a5\u544a\uff0c\u738b\u7edf\u9886\u5df2\u70b9\u52a8\u4eba\u9a6c\uff0c\u9075 \u7167\u5e03\u7f6e\uff0c\u5e03\u7f6e\u5b8c\u6210\uff0c\u7acb\u523b\u6765\u89c1\u7763\u629a\u3002 \u3000\u3000\u7136\u540e\uff0c\u4ed6\u53c8\u8f6c\u5411\u6c5f\u5e72\u91cc\u9053\uff1a\u201c\u6211\u5df2\u5c31\u5e03\u5b88\u5385\u5916\u7684\u6355\u5feb\u4e2d\uff0c\u9009\u4e86\u5341\u4e94\u540d\u8d76\u5f80\u4e09\u53f7\u7262\u623f\u5916\u9762 \u5e03\u7f6e\uff0c\u8f6c\u9f99\u624b\u5982\u6709\u884c\u52a8\uff0c\u4ed6\u4eec\u4f1a\u4f20\u8b66\u8fc7\u6765\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u7b11\u9053\uff1a\u201c\u597d\uff0c\u738b\u7edf\u9886\u4e00\u5230\uff0c\u71d5\u603b\u6355\u5934\u5c31\u9001\u6211\u56de\u4e09\u53f7\u7262\u623f\u3002\u201d \u3000\u3000\u5c39\u77e5\u5e9c\u5947\u9053\uff1a\u201c\u600e\u4e48\uff0c\u6c5f\u5148\u751f\u8fd8\u8981\u56de\u7262\u623f\u53bb\uff0c\u4f60\u662f\u5de1\u629a\u5927\u4eba\u8981\u7684\u4eba\uff0c\u4f60\u6562\u5750\u7262\uff0c\u4e0b\u5b98\u53ef \u4e0d\u6562\u518d\u5173\u4f60\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u7b11\u7b11\u9053\uff1a\u201c\u4e0d\u4f46\u8981\u5165\u7262\u623f\uff0c\u4e5f\u8fd8\u8981\u52a0\u4e0a\u4e00\u526f\u624b\u94d0\uff0c\u80fd\u4e0d\u5927\u52a8\u5e72\u6208\uff0c\u6700\u597d\u4e0d\u8981\uff0c\u4ee5 \u514d\u4f24\u4ea1\u2026\u2026\u201d \u3000\u3000\u6c5f\u5343\u91cc\u8bf4\u51fa\u4e86\u64d2\u8d3c\u7684\u8ba1\u5212\u3002 \u3000\u3000\u71d5\u98de\u4e0d\u4f4f\u5730\u70b9\u5934\u3002 \u3000\u3000\u8ba1\u8bae\u505c\u5f53\uff0c\u738b\u7edf\u9886\u4e5f\u8d76\u5230\u4e86\u5385\u5802\u3002 \u3000\u3000\u738b\u82f1\u662f\u6b66\u4e3e\u4eba\u51fa\u8eab\uff0c\u9b41\u68a7\u9ad8\u5927\uff0c\u6b64\u523b\uff0c\u4ed6\u5168\u8eab\u62ab\u6302\uff0c\u8170\u60ac\u7bad\u888b\uff0c\u624b\u4e2d\u63d0\u4e86\u4e00\u628a\u56db\u5341\u4e94\u65a4 \u7684\u5927\u5173\u5200\uff0c\u4e00\u4e2a\u4f69\u8170\u5200\u7684\u968f\u4ece\uff0c\u80cc\u4e86\u4e00\u628a\u786c\u5f13\u3002 \u3000\u3000\u6b66\u5c06\u81ea\u6709\u6b66\u5c06\u7684\u5a01\u98ce\uff0c\u548c\u6c5f\u6e56\u4eba\u7269\u81ea\u6709\u4e0d\u540c\u3002 \u3000\u3000\u738b\u82f1\u4f4e\u58f0\u5411\u9a6c\u5de1\u629a\u62a5\u544a\u4e86\u5e03\u7f72\u7684\u60c5\u5f62\u3002\u5df2\u662f\u4e09\u66f4\u65f6\u5206\uff0c\u71d5\u98de\u62ff\u8fc7\u4e00\u526f\u624b\u94d0\uff0c\u9053\uff1a\u201c\u6c5f \u5144\uff0c\u8981\u4e0d\u8981\u6697\u85cf\u4e00\u628a\u5175\u5203\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u7b11\u9053\uff1a\u201c\u4e0d\u7528\u4e86\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u5f97\u7f6a\u4e86\uff01\u201d\u71d5\u98de\u66ff\u6c5f\u5343\u91cc\u52a0\u4e0a\u624b\u94d0\uff0c\u8f6c\u8eab\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u4ecd\u662f\u539f\u6765\u63d0\u53d6\u6c5f\u5343\u91cc\u7684\u516b\u4e2a\u6355\u5feb\u968f\u884c\uff0c\u4e24\u4e2a\u6311\u7740\u7eb1\u706f\u524d\u5bfc\u3002 \u3000\u3000\u521a\u884c\u8fd1\u4e09\u53f7\u7262\u623f\u7684\u94c1\u6805\uff0c\u6805\u95e8\u7acb\u523b\u5927\u5f00\uff0c\u5f20\u7262\u5934\u5e26\u7740\u4e24\u4e2a\u7981\u5352\uff0c\u8fce\u4e86\u51fa\u6765\uff0c\u9053\uff1a\u201c\u71d5 \u7237\uff0c\u6c5f\u8001\u6ca1\u6709\u53d7\u5211\u5427\uff01\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u4f60\u770b\uff0c\u6211\u8001\u4eba\u5bb6\u4e0d\u662f\u597d\u597d\u7684\u5417\uff0c\u8fde\u6c57\u6bdb\u4e5f\u6ca1\u5c11\u4e00\u6839\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u597d\uff0c\u90a3\u5c31\u597d\uff0c\u6c5f\u8001\u5e73\u5b89\uff0c\u6211\u4eec\u4e5f\u597d\u8fc7\u4e00\u4e9b\u4e86\u3002\u201d \u3000\u3000\u71d5\u98de\u5374\u51b7\u51b7\u5730\u9053\uff1a\u201c\u5f20\u7262\u5934\uff0c\u53eb\u4ed6\u4eec\u53bb\u62ff\u4e00\u526f\u5927\u53f7\u811a\u9563\u6765\uff0c\u77e5\u5e9c\u5927\u4eba\u4ea4\u4ee3\uff0c\u6b64\u540e\u8981\u4e25\u52a0 \u770b\u7ba1\uff0c\u4e0d\u80fd\u50cf\u5df2\u5f80\u90a3\u6837\u653e\u7eb5\u4ed6\uff01\u201d \u3000\u3000\u5f20\u7262\u5934\u5e94\u4e86\u4e00\u58f0\uff0c\u56de\u5934\u5429\u5490\u8eab\u4fa7\u4e00\u4e2a\u7981\u5352\uff0c\u9053\uff1a\u201c\u8001\u674e\uff0c\u53bb\u62ff\u526f\u5927\u53f7\u7684\u811a\u9563\u6765\uff0c\u71d5\u603b\u6355 \u5934\u7684\u5429\u5490\uff0c\u6c5f\u8001\u4e0d\u4f1a\u602a\u5728\u6211\u4eec\u5934\u4e0a\u7684\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u6211\u8001\u4eba\u5bb6\u5df2\u7ecf\u591f\u59d4\u5c48\u4e86\uff0c\u8fd8\u8981\u518d\u52a0\u811a\u9563\uff0c\u6211\u8fde\u8fd9\u526f\u624b\u94d0\u4e5f\u4e0d\u8981\u6234\u4e86\u3002\u201d\u4e24 \u624b\u4e00\u6323\u624b\u94d0\u65ad\u88c2\u843d\u5730\u3002 \u3000\u3000\u53ea\u89c1\uff0c\u4ed6\u4f38\u624b\u5411\u5f20\u7262\u5934\u6293\u53bb\u3002 \u3000\u3000\u71d5\u98de\u5355\u5200\u51fa\u9798\uff0c\u4e00\u4fa7\u8eab\u5835\u4f4f\u4e86\u5f20\u7262\u5934\u7684\u540e\u9000\u4e4b\u8def\uff0c\u516d\u4e2a\u968f\u884c\u6355\u5feb\uff0c\u6251\u5411\u7559\u5728\u539f\u5730\u7684\u7981 \u5352\uff0c\u4e24\u4e2a\u638c\u706f\u6355\u5feb\uff0c\u9ad8\u4e3e\u7eb1\u706f\uff0c\u540e\u9000\u4e86\u51e0\u6b65\uff0c\u53e3\u4e2d\u5439\u8d77\u5c16\u9510\u7684\u7af9\u54e8\u58f0\u3002 \u3000\u3000\u5f20\u7262\u5934\u4e00\u95ea\uff0c\u907f\u5f00\u4e86\u6c5f\u5343\u91cc\u4e00\u8bb0\u64d2\u62ff\u624b\uff0c\u559d\u9053\uff1a\u201c\u6c5f\u8001\uff0c\u4f60\u600e\u5bf9\u6211\u51fa\u6c14\u554a\uff01\u71d5\u603b\u6355\u5934\u7684 \u5429\u5490\uff0c\u6211\u662f\u4e0d\u5f97\u4e0d\u9075\u4ece\u554a\uff01\u201d \u3000\u3000\u5c31\u8fd9\u4e48\u8bf4\u8bdd\u7684\u5de5\u592b\uff0c\u6c5f\u5343\u91cc\u5df2\u653b\u51fa\u4e86\u5341\u4f59\u62db\u3002 \u3000\u3000\u5f20\u7262\u5934\u8eab\u5982\u9640\u87ba\uff0c\u5c31\u5730\u8f6c\u52a8\uff0c\u7adf\u7136\u8fde\u8fde\u907f\u5f00\u4e86\u6c5f\u5343\u91cc\u5341\u4f59\u62db\u7684\u653b\u52bf\u3002 \u3000\u3000\u71d5\u98de\u770b\u5f97\u5fc3\u5934\u9707\u52a8\uff01 \u3000\u3000\u6c5f\u5343\u91cc\u653b\u52bf\u5982\u91cd\u6ce2\u53e0\u6d6a\uff0c\u5f20\u7262\u5934\u5e94\u4ed8\u4ece\u5bb9\uff0c\u8eab\u4e0d\u79bb\u4e09\u5c3a\u65b9\u5706\u4e4b\u5730\uff0c\u662f\u8f6c\u9f99\u624b\u6613\u5bb9\u5047\u626e\u7684 \u5df2\u65e0\u53ef\u7591\u3002 \u3000\u3000\u4f46\u71d5\u98de\u5e76\u6ca1\u6709\u51fa\u624b\uff0c\u4ed6\u8981\u5168\u795e\u6212\u5907\uff0c\u4ee5\u963b\u622a\u8f6c\u9f99\u624b\u9003\u9038\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u7262\u623f\u56db\u5468\u5df2\u4eae\u8d77\u4e86\u706b\u628a\uff0c\u7167\u5f97\u4e00\u7247\u901a\u660e\uff0c\u57cb\u4f0f\u5728\u7262\u623f\u7684\u6355\u5feb\uff0c\u5df2\u628a\u4efb\u52a1\u4ea4\u7ed9\u4e86\u8fdb \u5165\u5e9c\u8859\u7684\u9a81\u5200\u624b\uff0c\u51b2\u5165\u94c1\u6805\u5185\uff0c\u5728\u71d5\u98de\u793a\u610f\u4e0b\u5e03\u6210\u4e86\u4e00\u4e2a\u5706\u5708\uff0c\u56f4\u4f4f\u4e86\u8f6c\u9f99\u624b\u3002 \u3000\u3000\u90a3\u540d\u7981\u5352\u4e5f\u73b0\u9732\u51fa\u4e86\u771f\u6b63\u7684\u6b66\u529f\uff0c\u7531\u8eab\u4e0a\u53d6\u51fa\u4e86\u4e24\u628a\u624b\u53c9\u5b50\uff0c\u529b\u62b5\u516d\u540d\u6355\u5feb\u7684\u56f4\u653b\uff0c\u7adf \u7136\u6e38\u5203\u6709\u4f59\uff0c\u516d\u4e2a\u4eba\u53cd\u88ab\u4ed6\u903c\u5f97\u56e2\u56e2\u4e71\u8f6c\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u4e00\u9762\u52a0\u7d27\u62a2\u653b\uff0c\u4e00\u9762\u8bf4\u9053\uff1a\u201c\u71d5\u603b\u6355\u5934\uff0c\u5206\u4eba\u624b\u53bb\u56f4\u4f4f\u90a3\u4e2a\u53d6\u811a\u9563\u7684\u7981\u5352\u3002\u201d \u3000\u3000\u71d5\u98de\u5206\u6d3e\u51b2\u5165\u6805\u95e8\u7684\u6355\u5feb\uff0c\u8ffd\u6355\u53e6\u4e00\u4e2a\u7981\u5352\uff0c\u4e00\u9762\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u5f20\u4e0d\u7a7a\uff0c\u4f60\u8d70\u4e0d\u6389\u7684\uff0c \u5927\u6279\u7684\u519b\u9a6c\u5df2\u56f4\u4f4f\u4e86\u7262\u623f\u3002\u201d \u3000\u3000\u5f20\u4e0d\u7a7a\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u71d5\u98de\uff0c\u4f60\u90a3\u70b9\u5fae\u672b\u4e4b\u6280\uff0c\u8fd8\u4e0d\u653e\u5728\u5f20\u5927\u7237\u7684\u5fc3\u4e0a\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u76ef\u6ce8\u6c5f\u5343\u91cc\u7684\u8eab\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u6c5f\u5144\uff0c\u4f60\u4e5f\u662f\u6c5f\u6e56\u4e2d\u4eba\uff0c\u5982\u679c\u548c\u516d\u6247\u95e8\u4e2d\u7684\u4eba\u8054\u5408\u4e00 \u6c14\uff0c\u53ea\u6015\u6709\u4f24\u6c5f\u5144\u5728\u6c5f\u6e56\u4e2d\u7684\u58f0\u671b\u4e86\uff0c\u8fd9\u4e48\u529e\u5427\uff1f\u4f60\u5f00\u4e2a\u4ef7\u7801\u51fa\u6765\uff0c\u6211\u7acb\u523b\u4ed8\u73b0\uff0c\u4e0d\u8981\u4f60\u6c5f \u5144\u52a9\u6211\u9000\u654c\uff0c\u4f60\u53ea\u8981\u8f6c\u5934\u4e00\u8d70\uff0c\u65e5\u540e\u54b1\u4eec\u7b97\u662f\u670b\u53cb\uff0c\u6c5f\u5144\u6709\u4ec0\u4e48\u9700\u8981\uff0c\u5144\u5f1f\u662f\u5168\u529b\u4ee5\u8d74\uff0c\u600e \u4e48\u6837\uff1f\u201d \u3000\u3000\u201c\u8bdd\u662f\u8bf4\u5f97\u5f88\u4e0d\u9519\uff0c\u4f46\u4f60\u8f6c\u9f99\u624b\u4e00\u5411\u53e3\u662f\u5fc3\u975e\uff0c\u53ea\u6015\u54b1\u4eec\u8fd9\u4e00\u4e2a\u51a4\u5bb6\u662f\u7ed3\u5b9a\u4e86\u3002\u201d \u3000\u3000\u4f46\u95fb\u4e00\u58f0\u95f7\u54fc\uff0c\u548c\u7981\u5352\u52a8\u624b\u7684\u516d\u4e2a\u6355\u5feb\u4e2d\uff0c\u6709\u4e00\u4eba\u88ab\u5bf9\u65b9\u523a\u4e2d\u4e86\u524d\u80f8\uff0c\u9c9c\u8840\u6cc9\u6d8c\uff0c\u4f24\u52bf \u6781\u91cd\u3002 \u3000\u3000\u71d5\u98de\u6697\u9053\uff1a\u8f6c\u9f99\u624b\u7684\u4ece\u536b\uff0c\u7adf\u4e5f\u5982\u6b64\u5229\u5bb3\u3002\u4e00\u6325\u624b\uff0c\u7acb\u523b\u6709\u6355\u5feb\u9876\u4e86\u4e0a\u53bb\uff0c\u62ac\u8d70\u4e86\u4f24 \u8005\u3002 \u3000\u3000\u4ed6\u8861\u91cf\u5229\u5bb3\uff0c\u963b\u622a\u8f6c\u9f99\u624b\u6700\u4e3a\u91cd\u8981\uff0c\u4e0d\u80af\u51fa\u624b\u5e2e\u52a9\u6355\u5feb\u62d2\u654c\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u706b\u628a\u8000\u76ee\uff0c\u738b\u82f1\u5e26\u7740\u4e8c\u5341\u56db\u540d\u9a81\u5200\u624b\uff0c\u51b2\u5165\u6805\u95e8\uff0c\u5927\u5200\u4e00\u6325\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u8ba9 \u5f00\uff0c\u8ba9\u5f00\uff0c\u6211\u6765\u4f1a\u4f1a\u6c5f\u6e56\u9ad8\u4eba\u3002\u201d \u3000\u3000\u738b\u82f1\u75be\u75be\u5411\u8f6c\u9f99\u624b\u51b2\u53bb\u3002 \u3000\u3000\u71d5\u98de\u4f4e\u58f0\u9053\uff1a\u201c\u7edf\u9886\uff0c\u53bb\u5bf9\u4ed8\u90a3\u4e00\u4e2a\uff0c\u4ed6\u5df2\u4f24\u4e86\u6211\u4e24\u540d\u6355\u5feb\u3002\u201d \u3000\u3000\u4e0d\u9519\uff0c\u53c8\u6709\u4e00\u540d\u6355\u5feb\u88ab\u624b\u53c9\u5b50\u523a\u4e2d\u53f3\u81c2\u3002 \u3000\u3000\u4f46\u6355\u5feb\u4f17\u591a\uff0c\u4e00\u4eba\u53d7\u4f24\uff0c\u7acb\u523b\u6709\u4eba\u9876\u4e0a\uff0c\u59cb\u7ec8\u4fdd\u6301\u7740\u516d\u4eba\u56f4\u51fb\u5408\u653b\u7684\u5c40\u9762\u3002 \u3000\u3000\u738b\u82f1\u773c\u770b\u53c8\u6709\u6355\u5feb\u9876\u4e0a\uff0c\u8f6c\u773c\u53bb\u77a7\u6c5f\u5343\u91cc\u548c\u8f6c\u9f99\u624b\u7684\u640f\u6597\u3002 \u3000\u3000\u4e24\u4eba\u90fd\u672a\u65bd\u7528\u5175\u5203\uff0c\u7a7a\u624b\u7f20\u6218\uff0c\u4f46\u89c1\u638c\u62cd\u6307\u70b9\uff0c\u811a\u8e22\u8098\u649e\uff0c\u4f60\u6765\u6211\u5f80\uff0c\u5feb\u5982\u95ea\u7535\uff0c\u4e0d\u7531 \u5f97\u770b\u5446\u4e86\u3002 \u3000\u3000\u8fd9\u548c\u4ed6\u51b2\u950b\u9677\u9635\uff0c\u821e\u5200\u653b\u654c\uff0c\u5b8c\u5168\u662f\u4e24\u79cd\u6253\u6cd5\uff0c\u5fc3\u4e2d\u6697\u6697\u5fd6\u9053\uff1a\u201c\u4ed6\u4eec\u6253\u7684\u8fd9\u4e48\u4e00\u4e2a\u5feb \u6cd5\uff0c\u6211\u5982\u4e00\u5200\u5288\u4ed6\u4e0d\u4e2d\uff0c\u88ab\u4ed6\u6b3a\u8fd1\u8eab\u6765\uff0c\u8981\u5982\u4f55\u5e94\u4ed8\uff1f\u201d \u3000\u3000\u5fc3\u4e2d\u6709\u4e86\u4e00\u5c42\u987e\u8651\uff0c\u672a\u518d\u575a\u6301\u51fa\u624b\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u4e8c\u5341\u56db\u540d\u9a81\u5200\u624b\uff0c\u4e5f\u56f4\u6210\u4e86\u4e00\u4e2a\u5708\u5708\uff0c\u6805\u95e8\u5916\u706b\u5149\u70db\u5929\uff0c\u5200\u67aa\u8000\u773c\uff0c\u6570\u767e\u540d\u6b65\u5175 \u5df2\u8fdb\u5165\u5e9c\u8859\uff0c\u628a\u4e09\u53f7\u7262\u623f\u56f4\u4e2a\u6c34\u6cc4\u4e0d\u901a\u3002 \u3000\u3000\u8f6c\u9f99\u624b\u76ee\u7779\u60c5\u666f\uff0c\u6709\u4e9b\u7126\u6025\u4e86\uff0c\u4e00\u4e2a\u5931\u795e\uff0c\u88ab\u6c5f\u5343\u91cc\u4e00\u6307\u70b9\u4e2d\u53f3\u80a9\uff0c\u987f\u89c9\u534a\u8eab\u4e00\u9ebb\uff0c\u8eab \u8eaf\u8f6c\u52a8\u987f\u7f13\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u5982\u4f55\u80af\u653e\u8fc7\u8fd9\u4e2a\u673a\u4f1a\uff0c\u8fde\u51fa\u4e09\u62db\uff0c\u70b9\u4e2d\u4e86\u8f6c\u9f99\u624b\u4e24\u5904\u7a74\u9053\u3002 \u3000\u3000\u773c\u770b\u8f6c\u9f99\u624b\u5df2\u88ab\u64d2\u4f4f\uff0c\u71d5\u98de\u624d\u5927\u559d\u4e00\u58f0\uff0c\u6325\u5200\u653b\u5411\u7981\u5352\uff0c\u4e00\u9635\u5feb\u5200\u6025\u65a9\uff0c\u78d5\u98de\u4e86\u4e00\u67c4\u624b \u53c8\u5b50\uff0c\u98de\u8d77\u4e00\u811a\uff0c\u8e22\u4e2d\u4e86\u654c\u8170\uff0c\u4e24\u4e2a\u6355\u5feb\u51b2\u4e0a\u524d\u53bb\uff0c\u751f\u64d2\u6d3b\u6349\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u957f\u957f\u5401\u53e3\u6c14\u9053\uff1a\u201c\u597d\u5229\u5bb3\u7684\u8f6c\u9f99\u624b\uff0c\u5982\u975e\u6211\u4eec\u58f0\u52bf\u58ee\u5927\uff0c\u4f7f\u4ed6\u5fc3\u60ca\u758f\u795e\uff0c\u518d\u6253\u4e00 \u4e24\u767e\u62db\uff0c\u53ea\u6015\u4e5f\u96be\u5206\u80dc\u8d1f\u3002\u201d \u3000\u3000\u201c\u76db\u540d\u4e4b\u4e0b\u65e0\u865a\u58eb\uff0c\u6c5f\u5144\u679c\u7136\u9ad8\u660e\uff0c\u71d5\u67d0\u4eba\u4eca\u5929\u771f\u662f\u5927\u5f00\u773c\u754c\u4e86\u3002\u201d \u3000\u3000\u738b\u82f1\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6c5f\u6e56\u4eba\u7269\u4ea4\u624b\u548c\u51b2\u950b\u6253\u4ed7\u4e0d\u540c\uff0c\u6211\u4eec\u867d\u7136\u6765\u4e86\uff0c\u5374\u4e00\u70b9\u5fd9\u4e5f\u6ca1\u5e2e \u4e0a\uff0c\u54b1\u4eec\u5c31\u6b64\u522b\u8fc7\u4e86\u3002\u201d\u4e00\u6325\u624b\uff0c\u5e26\u7740\u4eba\u9a6c\u9000\u53bb\u3002 \u3000\u3000\u71d5\u98de\u65e9\u5df2\u53eb\u4eba\u51c6\u5907\u597d\u4e86\u4e1d\u7d22\u725b\u7b4b\uff0c\u628a\u8f6c\u9f99\u624b\u548c\u4ed6\u7684\u4ece\u536b\u6346\u4e2a\u7ed3\u5b9e\uff0c\u6c5f\u5343\u91cc\u624d\u62cd\u5f00\u4e86\u5f20\u4e0d \u7a7a\u7684\u7a74\u9053\u3002 \u3000\u3000\u4f46\u90a3\u53bb\u53d6\u811a\u9563\u7684\u7981\u5352\uff0c\u5374\u51fb\u9000\u4e86\u8ffd\u8d76\u7684\u6355\u5feb\uff0c\u8d81\u5927\u519b\u8fd8\u672a\u5408\u56f4\u4e4b\u524d\uff0c\u9003\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u9a6c\u5de1\u629a\u3001\u5c39\u77e5\u5e9c\u5f97\u5230\u4e86\u62a5\u544a\uff0c\u4eb2\u81ea\u524d\u6765\u544a\u8bc9\u6c5f\u5343\u91cc\uff0c\u4e8c\u5802\u4e0a\u5df2\u6446\u4e86\u9152\u5bb4\uff0c\u8981\u597d\u597d\u7684\u5e86\u8d3a \u4e00\u756a\u3002 \u3000\u3000\u6c5f\u5e72\u91cc\u7684\u5fc3\u4e2d\u660e\u767d\uff0c\u9a6c\u5de1\u629a\u8981\u6c42\u7684\u4e8b\u8fd8\u672a\u8bf4\u51fa\u6765\uff0c\u65e0\u6cd5\u63a8\u8f9e\uff0c\u7b11\u7b11\u9053\uff1a\u201c\u4e24\u4f4d\u5927\u4eba\u90fd\u80af \u901a\u5bb5\u4e0d\u7720\uff0c\u6c5f\u67d0\u81ea\u7136\u5949\u966a\uff0c\u4e0d\u8fc7\uff0c\u71d5\u603b\u6355\u5934\u4e5f\u4e0d\u80fd\u653e\u8fc7\uff0c\u5e94\u8be5\u966a\u6211\u4eec\u559d\u51e0\u676f\u3002\u201d \u3000\u3000\u201c\u90a3\u5f53\u7136\uff0c\u71d5\u98de\uff0c\u4f60\u4e5f\u6765\u5427\u3002\u201d\u9a6c\u5de1\u629a\u7acb\u523b\u4f5c\u4e86\u51b3\u5b9a\u3002 \u3000\u3000\u201c\u662f\uff0c\u5351\u804c\u5b89\u987f\u597d\u4e86\u8fd9\u4e24\u4e2a\u8981\u72af\uff0c\u5c31\u53bb\u4f8d\u9152\u3002\u201d \u5367\u9f99\u751f\u300a\u71d5\u5b50\u4f20\u5947\u300b \u7b2c \u4e8c \u56de\u3000\u5bfb\u67e5\u516c\u4e3b \u3000\u3000\u4e8c\u5802\u4e0a\u679c\u7136\u6446\u597d\u4e86\u4e00\u684c\u4e30\u76db\u7684\u9152\u5e2d\uff0c\u4e24\u4e2a\u4f8d\u9152\u7684\u4e66\u7ae5\u4e5f\u65e9\u5df2\u4f2b\u7acb\u5385\u4e2d\u3002 \u3000\u3000\u9a6c\u5de1\u629a\u8ba9\u6c5f\u5343\u91cc\u5750\u4e0a\u4f4d\uff0c\u6c5f\u5343\u91cc\u6267\u610f\u4e0d\u80af\uff0c\u5750\u4e86\u5ba2\u4f4d\uff0c\u4e09\u4eba\u4e3e\u676f\u4e92\u656c\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u53ea\u7ba1\u559d\u9152\uff0c\u4f3c\u662f\u4e00\u76f4\u8eb2\u907f\u7740\u9a6c\u5de1\u629a\u7684\u95ee\u8bdd\u3002 \u3000\u3000\u9a6c\u6587\u4e2d\u5df2\u77e5\u6c5f\u5343\u91cc\u8981\u7b49\u71d5\u98de\u6765\uff0c\u624d\u8c08\u6b63\u4e8b\uff0c\u8ba4\u4e3a\u6c5f\u5343\u91cc\u6709\u610f\u7ed9\u71d5\u98de\u9762\u5b50\uff0c\u53ea\u597d\u8010\u7740\u6027\u5b50 \u7b49\u5f85\u3002 \u3000\u3000\u8db3\u8db3\u7b49\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u71d5\u98de\u624d\u8d76\u5230\u4e8c\u5802\uff0c\u5411\u9a6c\u5de1\u629a\u3001\u5c39\u77e5\u5e9c\u544a\u4e86\u7f6a\uff0c\u624d\u5165\u5e2d\u843d\u5750\u3002 \u3000\u3000\u6c5f\u5e72\u91cc\u656c\u4e86\u71d5\u98de\u4e00\u676f\u9152\uff0c\u5c39\u77e5\u5e9c\u5df2\u62a2\u5148\u95ee\u71d5\u98de\u5982\u4f55\u5b89\u987f\u4e24\u4e2a\u8981\u72af\u3002 \u3000\u3000\u71d5\u98de\u6b20\u8eab\u7b54\u9053\uff1a\u201c\u5351\u804c\u8c03\u6d3e\u4e86\u5341\u4e8c\u540d\u6355\u5feb\uff0c\u56db\u4eba\u4e00\u73ed\uff0c\u8f6e\u756a\u770b\u5b88\uff0c\u9664\u4e86\u725b\u7b4b\u7d22\u7ef3\u6346\u7ed1\u4e4b \u5916\uff0c\u53c8\u52a0\u4e86\u4e24\u6761\u94c1\u94fe\u5b50\uff0c\u9501\u4e86\u53cc\u817f\uff0c\u544a\u8beb\u4e24\u4eba\uff0c\u5982\u6709\u9003\u72f1\u884c\u52a8\uff0c\u7acb\u523b\u4ee5\u5f13\u7bad\u5bf9\u4ed8\uff0c\u5e76\u65ad\u53bb\u53cc \u817f\u3002\u201d \u3000\u3000\u5c39\u77e5\u5e9c\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u5929\u4e00\u4eae\u5c31\u5907\u6587\u4e0a\u5448\u2026\u2026\u201d\u5ffd\u7136\u60f3\u5230\u5de1\u629a\u5c31\u5728\u773c\u524d\uff0c\u6025\u6025\u6539\u53e3\u9053\uff1a \u201c\u5927\u4eba\u53ef\u6709\u6307\u793a\u3002\u201d \u3000\u3000\u9a6c\u5de1\u629a\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u94a6\u62ff\u7684\u8981\u72af\uff0c\u5c39\u5927\u4eba\u8fd9\u4e00\u529f\u7acb\u7684\u4e0d\u5c0f\uff0c\u6211\u4f1a\u5728\u594f\u647a\u4e0a\u529b\u52a0\u4fdd \u4e3e\uff0c\u4fdf\u5723\u610f\u5b9a\u593a\uff0c\u89e3\u4eac\u5019\u5ba1\uff0c\u8fd8\u662f\u5c31\u5730\u6b63\u6cd5\u3002\u201d \u3000\u3000\u201c\u591a\u8c22\u5927\u4eba\uff01\u591a\u8c22\u5927\u4eba\uff01\u201d \u3000\u3000\u5c39\u77e5\u5e9c\u9ad8\u5174\u5f97\u7709\u773c\u90fd\u5728\u8df3\u52a8\uff0c\u628a\u521a\u521a\u88ab\u7f5a\u4ff8\u534a\u5e74\u7684\u81ed\u4e8b\uff0c\u4e5f\u5fd8\u5230\u4e5d\u9704\u4e91\u5916\u53bb\u4e86\u3002\u5fc3\u4e2d\u4e00 \u76f4\u5728\u76d8\u7b97\u7740\uff0c\u8fd9\u4e00\u529f\u5982\u8ba8\u4e86\u5723\u4e0a\u6b22\u5fc3\uff0c\u8bf4\u4e0d\u5b9a\u4e5f\u53ef\u5f04\u4e2a\u5de1\u629a\u5e72\u5e72\u5462\uff01 \u3000\u3000\u201c\u6c5f\u5148\u751f\uff01\u201d\u9a6c\u5de1\u629a\u7b11\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4eec\u53ef\u4ee5\u8c08\u8c08\u522b\u7684\u4e8b\u4e48\uff1f\u201d \u3000\u3000\u201c\u597d\uff0c\u6c5f\u67d0\u6d17\u8033\u606d\u542c\u3002\u201d \u3000\u3000\u9a6c\u5de1\u629a\u9053\uff1a\u201c\u6c5f\u5148\u751f\u662f\u7b54\u5e94\u4e86\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u5927\u4eba\u7684\u7834\u683c\u793c\u9047\uff0c\u6c5f\u67d0\u662f\u611f\u6234\u4e07\u5206\uff0c\u529b\u80fd\u6240\u53ca\uff0c\u4e0d\u4f1a\u62d2\u7edd\uff0c\u53ea\u4e0d\u8fc7\u6c5f\u67d0\u4e5f \u8981\u63d0\u51fa\u4e00\u4e2a\u6761\u4ef6\uff0c\u8981\u5927\u4eba\u4f5c\u4e3b\u6210\u5168\u3002\u201d \u3000\u3000\u8fd9\u4e00\u4e0b\uff0c\u9a6c\u5de1\u629a\u53ef\u6123\u4f4f\u4e86\uff0c\u60f3\u4e0d\u5230\u6c5f\u5343\u91cc\u8001\u5e74\u6210\u7cbe\uff0c\u5012\u6253\u4e00\u628a\u5b50\uff0c\u5982\u679c\u4ed6\u4e0d\u613f\u5e94\u627f\uff0c\u63d0 \u51fa\u4e86\u4e00\u4e2a\u81ea\u5df1\u529b\u96be\u6240\u53ca\u7684\u53cd\u6761\u4ef6\uff0c\u5c82\u4e0d\u662f\u8bf4\u4e86\u4e5f\u662f\u767d\u8bf4\u3002 \u3000\u3000\u4e00\u65f6\u95f4\uff0c\u72b9\u8c6b\u4e0d\u5b9a\uff0c\u8138\u8272\u4e5f\u6c89\u91cd\u8d77\u6765\u3002 \u3000\u3000\u7b2c\u4e00\u4e2a\u53cd\u5e94\u7684\u662f\u5c39\u77e5\u5e9c\uff0c\u8ddf\u7740\u6536\u655b\u4e86\u7b11\u5bb9\uff0c\u8138\u4e5f\u62c9\u957f\u4e86\u3002 \u3000\u3000\u71d5\u98de\u76b1\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u6c5f\u5144\uff0c\u5de1\u629a\u5927\u4eba\u5df2\u660e\u793a\u7684\u5f88\u6e05\u695a\u4e86\uff0c\u662f\u8981\u4f60\u6c5f\u5144\u4ee3\u4e3a\u627e\u4e00\u4e2a\u4eba\uff0c \u90a3\u662f\u6c5f\u5144\u72ec\u6b65\u6c5f\u6e56\u7684\u672c\u9886\uff0c\u81f3\u5e72\u6c5f\u5144\u7684\u6761\u4ef6\uff0c\u80fd\u4e0d\u80fd\u5148\u8bf4\u51fa\u6765\uff0c\u4e5f\u597d\u8ba9\u5de1\u629a\u5927\u4eba\u659f\u914c\u4e00 \u4e0b\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u71d5\u603b\u6355\u5934\uff0c\u6c5f\u67d0\u4eba\u63d0\u7684\u6761\u4ef6\u548c\u4f60\u71d5\u603b\u6355\u5934\u6709\u5173\u3002\u201d \u3000\u3000\u201c\u548c\u6211\u6709\u5173\uff0c\u90a3\u597d\uff0c\u6211\u5148\u7b54\u5e94\u4f60\uff0c\u5c31\u662f\u8981\u6211\u7684\u4eba\u5934\u4e5f\u7acb\u523b\u5bb0\u7ed9\u4f60\uff01\u201d \u3000\u3000\u201c\u71d5\u98de\uff0c\u522b\u592a\u51b2\u52a8\u2026\u2026\u201d\u9a6c\u5de1\u629a\u7684\u8138\u8272\u89e3\u51bb\u4e86\uff0c\u6761\u4ef6\u653e\u5728\u71d5\u98de\u7684\u8eab\u4e0a\uff0c\u6ca1\u4ec0\u4e48\u5927\u4e0d\u4e86 \u7684\uff0c\u4f46\u8bf4\u8bdd\u7684\u53e3\u6c14\u5374\u5341\u5206\u6148\u548c\uff0c\u9053\uff1a\u201c\u542c\u542c\u6c5f\u5148\u751f\u8bf4\u6cd5\u518d\u4f5c\u51b3\u5b9a\u3002\u201d \u3000\u3000\u8fd9\u5c31\u662f\u4f5c\u5927\u5b98\u80fd\u8010\uff0c\u5fc3\u91cc\u60f3\u7684\u53e3\u91cc\u8bf4\u7684\uff0c\u672a\u5fc5\u5c31\u662f\u4e00\u81f4\u7684\u3002 \u3000\u3000\u201c\u4e0d\uff01\u5351\u804c\u5148\u7b54\u5e94\u4ed6\uff0c\u6c5f\u5144\uff0c\u4f60\u8bf4\u5427\uff0c\u8981\u6211\u71d5\u98de\u7684\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4f60\u7684\u513f\u5b50\uff0c\u5c0f\u71d5\u5b50\uff0c\u4ed6\u4eca\u5e74\u5341\u4e94\u5c81\u4e86\u5427\uff01\u201d \u3000\u3000\u71d5\u98de\u9053\uff1a\u201c\u5c0f\u71d5\u5b50\uff0c\u4ed6\u2026\u2026\u4ed6\u8fd8\u662f\u4e2a\u5b69\u5b50\u554a\uff01\u201d \u3000\u3000\u201c\u5bf9\uff0c\u518d\u665a\u4e24\u5e74\u5c31\u6765\u4e0d\u53ca\u4e86\uff0c\u4f60\u80af\u4e0d\u80af\u7b54\u5e94\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u6c5f\u5144\uff0c\u4f60\u8981\u4ed6\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u662f\u4e0d\u662f\u60f3\u6536\u4f5c\u5f92\u5f1f\u2026\u2026\u201d\u9a6c\u5de1\u629a\u7b11\u4e86\uff0c\u7b11\u5f97\u5f88\u5f00\u5fc3\u9053\uff1a\u201c\u6c5f\u5144\u770b\u4e0a\u4e86\u5c0f\u71d5\u5b50\uff0c\u51c6\u5907\u8981 \u4f20\u6388\u8863\u94b5\uff1f\u201d \u3000\u3000\u201c\u5927\u4eba\uff0c\u8fd9\u4e00\u6b21\u53ea\u731c\u5bf9\u4e86\u4e00\u534a\u3002\u201d\u6c5f\u5343\u91cc\u5fae\u7b11\u7740\u8bf4\uff1a\u201c\u5c0f\u71d5\u5b50\u662f\u6c5f\u67d0\u4eba\u51e0\u5341\u5e74\u89c1\u5230\u6700\u597d \u7684\u8d44\u8d28\u2026\u2026\u201d \u3000\u3000\u201c\u6c5f\u5144\u7684\u610f\u601d\u662f\u2026\u2026\u201d\u71d5\u98de\u60f3\u5230\u6c5f\u6e56\u4e0a\u6709\u5229\u7528\u7ae5\u5fc3\u914d\u836f\u7684\u4f20\u8bf4\uff0c\u5fc3\u60c5\u5927\u4e3a\u7d27\u5f20\u3002 \u3000\u3000\u201c\u6c5f\u67d0\u4eba\u76f4\u8bf4\u4e86\u5427\uff01\u6211\u8fd8\u6ca1\u6709\u6536\u5c0f\u71d5\u5b50\u4f5c\u5f92\u5f1f\u7684\u8d44\u683c\uff0c\u56e0\u4e3a\uff0c\u6211\u8fd9\u70b9\u672c\u9886\u592a\u6709\u9650\u4e86\uff0c\u6536 \u4e86\u4ed6\uff0c\u7b49\u4e8e\u7cdf\u8e4b\u4e86\u4ed6\uff0c\u4f60\u71d5\u603b\u6355\u5934\u90a3\u70b9\u672c\u9886\uff0c\u5145\u5176\u91cf\u548c\u6c5f\u67d0\u4eba\u534a\u65a4\u516b\u4e24\uff0c\u54b1\u4eec\u90fd\u4e0d\u914d\u4f5c\u5c0f\u71d5 \u5b50\u7684\u4f20\u827a\u5e08\u7236\u3002\u201d \u3000\u3000\u201c\u6c5f\u5148\u751f\u7684\u610f\u601d\uff0c\u662f\u8981\u66ff\u5c0f\u71d5\u5b50\u63a8\u8350\u4e00\u4f4d\u540d\u5e08\u4e86\uff1f\u201d \u3000\u3000\u201c\u5bf9\uff01\u8fd9\u624d\u662f\u6c5f\u67d0\u4eba\u7559\u5728\u5f00\u5c01\u5e9c\u6700\u5927\u539f\u56e0\uff0c\u81f3\u4e8e\u8ffd\u8e2a\u8f6c\u9f99\u624b\uff0c\u53ea\u4e0d\u8fc7\u9002\u9022\u5176\u4f1a\uff0c\u71d5\u8001\u5f1f \u4e5f\u8bb8\u8fd8\u4e0d\u77e5\u9053\uff0c\u6211\u548c\u5c0f\u71d5\u5b50\u5df2\u7ecf\u8ba4\u8bc6\u4e03\u516b\u4e2a\u6708\u4e86\uff0c\u4e5f\u4f20\u4e86\u4ed6\u51e0\u624b\u529f\u592b\uff0c\u90a3\u771f\u662f\u53eb\u6211\u9707\u60ca \u554a\uff01\u201d \u3000\u3000\u201c\u6709\u8fd9\u7b49\u4e8b\uff1f\u5c0f\u71d5\u5b50\u4ece\u672a\u5bf9\u6211\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u201c\u662f\u6211\u4e0d\u51c6\u4ed6\u8bf4\u7684\u3002\u201d \u3000\u3000\u201c\u90a3\u6c5f\u5144\u53c8\u9707\u60ca\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u201c\u71d5\u8001\u5f1f\uff0c\u6211\u4e0d\u77e5\u9053\u4f60\u4f20\u4e86\u5c0f\u71d5\u5b50\u591a\u5c11\u6b66\u529f\uff0c\u6211\u6c5f\u67d0\u4f20\u4ed6\u6b66\u529f\u4e0d\u8fc7\u4e94\u4e2a\u591a\u6708\uff0c\u51e0\u4e4e\u5df2\u88ab \u638f\u53bb\u4e86\u538b\u7bb1\u5e95\u7684\u80fd\u8010\uff0c\u4ed6\u80fd\u4e00\u5b66\u5c31\u4f1a\uff0c\u4e3e\u4e00\u53cd\u4e09\uff0c\u6211\u770b\u518d\u6709\u4e09\u4e2a\u6708\u6211\u5c31\u6ca1\u6709\u4ec0\u4e48\u53ef\u6559\u7684\u4e86\uff0c \u71d5\u8001\u5f1f\uff0c\u5c0f\u71d5\u5b50\u662f\u767e\u5e74\u96be\u9047\u7684\u5947\u624d\u5440\uff0c\u53ef\u60dc\u672a\u9047\u4e0a\u771f\u6b63\u7684\u5e08\u7236\uff0c\u6211\u8981\u4ed6\u53bb\u89c1\u4e00\u4f4d\u98ce\u5c18\u5947\u4eba\uff0c \u6211\u60f3\uff0c\u4ed6\u4f1a\u4e3a\u5c0f\u71d5\u5b50\u7684\u624d\u8d28\u800c\u7834\u683c\u6536\u5f55\u95e8\u4e0b\u3002\u201d \u3000\u3000\u201c\u6c5f\u5144\uff0c\u80fd\u4e0d\u80fd\u544a\u8bc9\u6211\uff0c\u90a3\u4f4d\u98ce\u5c18\u5947\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u73b0\u5728\u4e0d\u80fd\u8bf4\uff0c\u5c0f\u71d5\u5b50\u8fd8\u672a\u62dc\u5165\u4ed6\u95e8\u4e0b\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u662f\u597d\u4e8b\u554a\uff01\u6587\u4e2d\u4e5f\u8981\u63d2\u4e00\u817f\uff0c\u5982\u679c\u71d5\u603b\u6355\u5934\u540c\u610f\uff0c\u6211\u60f3\u6536\u5c0f\u71d5\u5b50\u505a\u4e2a\u5e72\u513f\u5b50\uff0c\u600e\u4e48 \u6837\uff1f\u201d \u3000\u3000\u201c\u5927\u4eba\uff0c\u8fd9\u2026\u2026\u8fd9\u662f\u5c0f\u71d5\u5b50\u7684\u9020\u5316\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u5c31\u8fd9\u4e48\u8bf4\u5b9a\u4e86\uff0c\u5c39\u77e5\u5e9c\u8bf7\u770b\u4e2a\u65e5\u5b50\uff0c\u5e26\u5c0f\u71d5\u5b50\u5230\u6211\u5bb6\u53bb\uff0c\u6b63\u5f0f\u62dc\u89c1\u5e72\u7239\u5e72 \u5a18\u2026\u2026\u201d \u3000\u3000\u201c\u5bf9\uff01\u8981\u5927\u6392\u7b75\u5e2d\uff0c\u597d\u597d\u70ed\u95f9\u4e00\u4e0b\u3002\u201d\u5c39\u77e5\u5e9c\u8ba8\u597d\u7684\u8bf4\u3002 \u3000\u3000\u201c\u4e5f\u4e0d\u7528\u592a\u94fa\u5f20\u4e86\uff0c\u5ba2\u4eba\u4e5f\u4e0d\u8981\u591a\uff0c\u5fc3\u8bda\u5c31\u597d\u4e86\uff0c\u71d5\u603b\u6355\u5934\uff0c\u4f60\u8bf4\u5462\uff1f\u201d \u3000\u3000\u201c\u4e00\u5207\u7531\u5de1\u629a\u5927\u4eba\u4f5c\u4e3b\u2026\u2026\u201d \u3000\u3000\u9a6c\u5de1\u629a\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4ee5\u540e\u2026\u2026\u71d5\u603b\u6355\u5934\uff0c\u54b1\u4eec\u5c31\u662f\u5e72\u4eb2\u5bb6\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0b\u5b98\u9ad8\u6500\uff0c\u4e0b\u5b98\u9ad8\u6500\u2026\u2026\u201d \u3000\u3000\u201c\u71d5\u8001\u5f1f\uff0c\u4f60\u8fd8\u6ca1\u6709\u7b54\u5e94\u6211\u5440\uff01\u51c6\u8bb8\u6211\u628a\u5c0f\u71d5\u5b50\u5e26\u8d70\uff0c\u5feb\u53ea\u4e09\u5e74\uff0c\u8fdf\u5219\u4e94\u8f7d\uff0c\u8fd8\u4f60\u4e2a\u7edd \u4e16\u9ad8\u624b\u7684\u5c0f\u71d5\u5b50\u3002\u201d \u3000\u3000\u201c\u884c\uff0c\u6211\u7b54\u5e94\u4e86\uff0c\u4e0d\u8fc7\uff0c\u56de\u53bb\u53ef\u8981\u5bf9\u8001\u5a46\u8d39\u5c3d\u53e3\u820c\u4e86\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\uff0c\u6c5f\u5148\u751f\u2026\u2026\u201d\u9a6c\u5de1\u629a\u6025\u6025\u53eb\u9053\u3002 \u3000\u3000\u201c\u6211\u77e5\u9053\uff0c\u6211\u4f1a\u5148\u529e\u5de1\u629a\u5927\u4eba\u7684\u4e8b\uff0c\u73b0\u5728\uff0c\u6211\u7b54\u5e94\u4e86\uff0c\u5927\u4eba\u8bf7\u8bf4\u660e\u5185\u60c5\u5427\uff1f\u201d \u3000\u3000\u9a6c\u5de1\u629a\u8138\u4e0a\u6cdb\u51fa\u6b22\u6109\u4e4b\u8272\uff0c\u4f46\u4e5f\u53ea\u662f\u4e00\u95ea\u800c\u901d\uff0c\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6c5f\u5148\u751f\uff0c\u8fd9\u4ef6\u4e8b\u4e0d\u4f46\u5173 \u7cfb\u7740\u6587\u4e2d\u7684\u524d\u7a0b\uff0c\u4e5f\u5173\u7cfb\u5c39\u77e5\u5e9c\u3001\u71d5\u603b\u6355\u5934\u7684\u8fdb\u9000\uff0c\u4e25\u91cd\u70b9\u8bf4\uff0c\u53ea\u6015\u8fd8\u7275\u8fde\u7740\u8eab\u5bb6\u6027 \u547d\u2026\u2026\u201d \u3000\u3000\u8fd9\u756a\u8bdd\u592a\u4e25\u91cd\u4e86\uff0c\u5c39\u77e5\u5e9c\u672c\u6765\u7684\u4e00\u8154\u6b22\u559c\uff0c\u7a81\u7136\u95f4\u5982\u51b7\u6c34\u6d47\u5934\uff0c\u8138\u8272\u5927\u53d8\uff0c\u71d5\u98de\u4e5f\u542c\u5f97 \u6123\u4f4f\u4e86\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u8138\u8272\u6c89\u91cd\u5730\u9053\uff1a\u201c\u5927\u4eba\uff0c\u4ec0\u4e48\u4eba\u5982\u6b64\u91cd\u8981\uff1f\u201d \u3000\u3000\u201c\u662f\u554a\uff01\u5927\u4eba\u2026\u2026\u201d\u5c39\u77e5\u8010\u4e0d\u4f4f\u5fc3\u4e2d\u7684\u7591\u60e7\uff0c\u63a5\u53e3\u8bf4\u9053\uff1a\u201c\u96be\u9053\u90a3\u4eba\u662f\u6bd4\u8f6c\u9f99\u624b\u5f20\u4e0d\u7a7a \u8fd8\u91cd\u8981\u7684\u94a6\u72af\uff1f\u201d \u3000\u3000\u201c\u6293\u4e0d\u5230\u94a6\u62ff\u8981\u72af\uff0c\u5211\u90e8\u7684\u8d23\u4efb\u4f1a\u6bd4\u5730\u65b9\u5b98\u5458\u91cd\u5927\u2026\u2026\u201d\u9a6c\u5de1\u629a\u8bed\u58f0\u5e7d\u6c89\u7684\u8bf4\uff1a\u201c\u5927\u4e0d \u4e86\u964d\u65e8\u53f1\u8d23\u4e00\u756a\uff0c\u7edd\u4e0d\u4f1a\u7275\u8fde\u5230\u8eab\u5bb6\u6027\u547d\u201d\u6284\u5bb6\u706d\u95e8\u554a\uff01\u2026\u2026\u201c\u5c39\u77e5\u5e9c\u8138\u8272\u66f4\u89c1\u60e8\u767d\u3002 \u3000\u3000\u201c\u4e0d\u9519\uff0c\u6211\u4e0d\u662f\u5371\u8a00\u8038\u542c\uff0c\u627e\u4e0d\u5230\u8fd9\u4e2a\u4eba\uff0c\u5723\u4e0a\u4e00\u5b9a\u9707\u6012\u2026\u2026\u3000\u3000\u201d\u9a6c\u5de1\u629a\u9053\uff1a\u201c\u6284\u5bb6 \u706d\u95e8\u786e\u6709\u53ef\u80fd\uff0c\u5c31\u7b97\u5723\u4e0a\u5bbd\u539a\uff0c\u4e5f\u4f1a\u529e\u4e00\u4e2a\u7f62\u5b98\u5145\u519b\uff0c\u6c38\u4e0d\u5f55\u7528\u3002\u201d \u3000\u3000\u201c\u6211\u7684\u5988\u5440\uff01\u2026\u2026\u201d\u5c39\u77e5\u5e9c\u5dee\u70b9\u53eb\u51fa\u53e3\u6765\uff0c\u5f3a\u5fcd\u6025\u541e\uff0c\u8fd8\u662f\u628a\u6211\u5b57\u53eb\u4e86\u51fa\u6765\u3002 \u3000\u3000\u201c\u5c39\u77e5\u5e9c\uff0c\u4f60\u60f3\u8bf4\u4ec0\u4e48\uff0c\u5c31\u8bf4\u51fa\u6765\u5427\uff1f\u201d\u9a6c\u5de1\u629a\u671b\u7740\u5c39\u77e5\u5e9c\u3002 \u3000\u3000\u201c\u5351\u804c\u662f\u60f3\u77e5\u9053\u4e00\u4e9b\u8be6\u7ec6\u7684\u5185\u60c5\uff0c\u5927\u4eba\u53ef\u5426\u660e\u793a\u5462\uff1f\u201d \u3000\u3000\u201c\u662f\u8981\u8bf4\u6e05\u695a\u7684\u2026\u2026\u3002\u6c5f\u5148\u751f\u4e0d\u660e\u5185\u60c5\uff0c\u4e5f\u65e0\u6cd5\u7740\u624b\u627e\u4eba\u7684\uff0c\u4e09\u516c\u4e3b\u51e4\u9a7e\u5357\u6e38\uff0c\u5728\u8fd9\u91cc \u5931\u8e2a\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u5728\u4ec0\u4e48\u5e9c\u53bf\u5931\u8e2a\u7684\uff1f\u201d\u5c39\u77e5\u5e9c\u6025\u6025\u8feb\u95ee\u3002 \u3000\u3000\u201c\u5c31\u5728\u8d35\u5e9c\u5730\u9762\u4e0a\uff0c\u5f00\u5c01\u5e9c\u4e1c\u5927\u8857\u7684\u9ad8\u5347\u5ba2\u6808\uff0c\u4e09\u516c\u4e3b\u591c\u5bbf\u90a3\u91cc\uff0c\u5929\u4eae\u540e\uff0c\u4eba\u5df2\u4e0d\u89c1 \u4e86\u3002\u201d \u3000\u3000\u201c\u5351\u804c\u600e\u4e48\u4e00\u70b9\u90fd\u4e0d\u77e5\u9053\u4e09\u516c\u4e3b\u5230\u5f00\u5c01\u7684\u6d88\u606f\u2026\u2026\u201d\u5c39\u77e5\u5e9c\u8bf4\uff1a\u201c\u5982\u679c\u5351\u804c\u5f97\u5230\u8baf\u606f\uff0c \u81ea\u4f1a\u5168\u529b\u4fdd\u62a4\uff0c\u4fdd\u4e0d\u4f4f\u516c\u4e3b\u7684\u5b89\u5168\uff0c\u7518\u613f\u9886\u7f6a\u53d7\u5211\uff0c\u6b7b\u800c\u65e0\u6028\uff0c\u4f46\u5351\u804c\u2026\u2026\u201d \u3000\u3000\u201c\u516c\u4e3b\u662f\u8f7b\u9a91\u5357\u4e0b\uff0c\u6211\u4e5f\u662f\u5728\u516c\u4e3b\u5931\u8e2a\u540e\u624d\u5f97\u5230\u6d88\u606f\u7684\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u672c\u6765\u4e0d\u5e94\u8be5\u602a\u7f6a\u5230\u5730 \u65b9\u5b98\u5458\uff0c\u4f46\u5929\u5a01\u96be\u6d4b\u5440\uff01\u2026\u2026\u201d \u3000\u3000\u201c\u5927\u4eba\u2026\u2026\u201d\u71d5\u98de\u5f00\u53e3\u4e86\uff1a\u201c\u516c\u4e3b\u79bb\u4eac\uff0c\u96be\u9053\u5c31\u6ca1\u6709\u5b98\u5ef7\u7684\u4f8d\u536b\u968f\u884c\u4fdd\u62a4\u4e48\uff1f\u201d \u3000\u3000\u201c\u6709\uff01\u53ea\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u662f\u5728\u4e09\u516c\u4e3b\u5931\u8e2a\u4e4b\u540e\uff0c\u624d\u5230\u5de1\u5e9c\u4e2d\u89c1\u6211\u3002\u201d \u3000\u3000\u201c\u6c5f\u67d0\u6597\u80c6\uff0c\u8bf7\u95ee\u5927\u4eba\uff0c\u4ed6\u4eec\u662f\u51e0\u65f6\u5230\u5de1\u5e9c\u4e2d\u62a5\u6848\uff1f\u201d \u3000\u3000\u201c\u5348\u65f6\u8fc7\u540e\u2026\u2026\u201d\u9a6c\u5de1\u629a\u9053\uff1a\u201c\u65e5\u4e0a\u4e09\u7aff\uff0c\u8fd8\u672a\u89c1\u516c\u4e3b\u8d77\u8eab\uff0c\u4ed6\u4eec\u8fd8\u8ba4\u4e3a\u662f\u4e09\u516c\u4e3b\u65c5\u9014 \u52b3\u987f\uff0c\u7761\u5f97\u9999\u751c\uff0c\u4e0d\u6562\u60ca\u6270\uff0c\u76f4\u5230\u8fd1\u5348\u65f6\u5206\uff0c\u624d\u89c9\u60c5\u5f62\u4e0d\u5bf9\uff0c\u7834\u95e8\u67e5\u770b\uff0c\u4e09\u516c\u4e3b\u82b3\u8e2a\u5df2\u6773\uff0c \u4e0d\u77e5\u53bb\u5411\u4e86\u3002\u201d \u3000\u3000\u201c\u62a4\u9001\u4e09\u516c\u4e3b\u5357\u4e0b\u7684\u6709\u51e0\u4e2a\u5bab\u5ef7\u4f8d\u536b\uff1f\u201d\u6c5f\u5343\u91cc\u795e\u60c5\u51b7\u8083\u5730\u8bf4\uff1a\u201c\u662f\u5426\u6709\u5bab\u5973\u968f\u884c\uff1f\u201d \u3000\u3000\u201c\u4e09\u4e2a\u4f8d\u536b\uff0c\u4e24\u4e2a\u5bab\u5973\uff0c\u4f46\u4e24\u4e2a\u5bab\u5973\u90fd\u88ab\u4eba\u70b9\u4e86\u7a74\u9053\uff0c\u5012\u5367\u5728\u4e09\u516c\u4e3b\u7684\u4f4f\u5bbf\u623f\u5185\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u90a3\u4e24\u4e2a\u5bab\u5973\u73b0\u5728\u4f55\u5904\uff1f\u201d \u3000\u3000\u201c\u5728\u5de1\u5e9c\u4e2d\u3002\u201d \u3000\u3000\u201c\u54ea\u4e09\u4e2a\u4f8d\u536b\u5462\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u4f3c\u662f\u5df2\u88ab\u8fd9\u4ef6\u4e8b\u5413\u5f97\u516d\u795e\u65e0\u4e3b\u4e86\uff0c\u4e00\u76f4\u50ac\u7740\u6587\u4e2d\u60f3\u529e\u6cd5\u627e\u4eba\u3002\u201d \u3000\u3000\u201c\u5927\u4eba\u53c8\u662f\u600e\u4e48\u627e\u4e0a\u6c5f\u67d0\u7684\uff1f\u201d \u3000\u3000\u201c\u8fd9\u5c31\u591a\u4e8f\u674e\u603b\u9556\u5934\u4e86\u3002\u201d \u3000\u3000\u201c\u9ec4\u6cb3\u9556\u5c40\u7684\u674e\u9752\u7af9\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u9519\uff01\u201d\u9a6c\u5de1\u629a\u53f9\u606f\u4e00\u58f0\u9053\uff1a\u201c\u5de1\u629a\u5e9c\u4e0b\uff0c\u672c\u6709\u4e00\u4f4d\u5f88\u7cbe\u660e\u7684\u603b\u6355\u5934\u97e9\u6d9b\uff0c\u548c\u5f00\u5c01\u5e9c \u7684\u71d5\u603b\u6355\u5934\u5408\u4f5c\u826f\u597d\uff0c\u4f7f\u6cb3\u5357\u7701\u5730\u9762\u4e0a\u4e00\u5411\u5e73\u9759\uff0c\u53ef\u60dc\u7684\u662f\u97e9\u6d9b\u5f97\u4e86\u4e00\u79cd\u602a\u75c5\uff0c\u5df2\u5367\u5e8a\u4e09 \u6708\uff0c\u4e5f\u66fe\u4e24\u5ea6\u8bf7\u8f9e\uff0c\u4f46\u6587\u4e2d\u5ff5\u4ed6\u7acb\u529f\u751a\u591a\uff0c\u662f\u4e00\u4f4d\u96be\u5f97\u7684\u5e72\u5458\uff0c\u51c6\u4ed6\u534a\u5e74\u75c5\u5047\uff0c\u534a\u5e74\u5185\u75c5\u60c5 \u5982\u6ca1\u6709\u597d\u8f6c\uff0c\u518d\u4f5c\u8ba1\u8bae\uff0c\u60f3\u4e0d\u5230\u5728\u62b1\u75c5\u671f\u4e2d\uff0c\u7adf\u7136\u51fa\u4e86\u8fd9\u4e48\u4e00\u4ef6\u91cd\u5927\u7684\u6848\u4ef6\u3002\u201d \u3000\u3000\u201c\u5feb\u5200\u97e9\u6d9b\u7adf\u7136\u5367\u75c5\u4e86\u6570\u6708\uff0c\u4f46\u4e0d\u77e5\u4ed6\u8fd1\u65e5\u7684\u75c5\u60c5\u5982\u4f55\u4e86\uff1f\u201d\u6c5f\u5343\u91cc\u611f\u53f9\u5730\u8bf4\u3002 \u3000\u3000\u201c\u75c5\u52bf\u751a\u91cd\uff0c\u4e3a\u4e86\u4e09\u516c\u4e3b\u5931\u8e2a\u4e00\u6848\uff0c\u6587\u4e2d\u4eb2\u5230\u97e9\u5e9c\u4e2d\u63a2\u89c6\uff0c\u987a\u4fbf\u8bf4\u660e\u4e86\u6848\u60c5\uff0c\u4ed6\u63a8\u8350\u6587 \u4e2d\u8bf7\u674e\u603b\u9556\u5934\u51fa\u9a6c\u76f8\u52a9\uff0c\u674e\u9752\u7af9\u679c\u7136\u8033\u76ee\u7075\u901a\uff0c\u65e9\u5df2\u77e5\u9053\u6c5f\u5148\u751f\u5c48\u7559\u5728\u5f00\u5c01\u5e9c\u8859\uff0c\u8981\u6587\u4e2d\u6c42 \u5148\u751f\u76f8\u52a9\u4e00\u81c2\u4e4b\u529b\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u674e\u9752\u7af9\u4e5f\u4e0d\u80fd\u7f6e\u8eab\u4e8b\u5916\uff0c\u4ed6\u5bf9\u8c6b\u3001\u9c81\u9053\u4e0a\u7684\u5f62\u52bf\u4e86\u5982\u6307\u638c\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u5929\u4e00\u4eae\uff0c\u6587\u4e2d\u5c31\u8bf7\u4ed6\u6765\u6b64\u548c\u5148\u751f\u89c1\u9762\uff0c\u4ee5\u8944\u52a9\u5148\u751f\u3002\u201d \u3000\u3000\u201c\u8fd9\u4ef6\u4e8b\uff0c\u4e0d\u7528\u5927\u4eba\u8d39\u5fc3\u4e86\uff0c\u6c5f\u6e56\u4e2d\u7684\u4e8b\u52a1\uff0c\u8981\u7528\u6c5f\u6e56\u4eba\u7684\u529e\u6cd5\u5904\u7406\uff0c\u6211\u81ea\u5df1\u4f1a\u53bb\u627e \u4ed6\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u201c\u6c5f\u5148\u751f\u6709\u4ec0\u4e48\u9700\u8981\uff0c\u53ea\u7ba1\u8bf4\u51fa\u6765\uff0c\u6587\u4e2d\u65e0\u4e0d\u5c3d\u529b\u652f\u5e94\u3002\u201d \u3000\u3000\u201c\u6c5f\u67d0\u60f3\u5148\u548c\u4e09\u4e2a\u4f8d\u536b\u4e24\u4f4d\u5bab\u5973\u8be6\u7ec6\u8c08\u8c08\uff0c\u4e0d\u77e5\u65b9\u4fbf\u5426\uff1f\u201d \u3000\u3000\u201c\u65b9\u4fbf\u5f97\u5f88\uff01\u201d\u9a6c\u5de1\u629a\u8138\u8272\u7a81\u7136\u51b7\u4e86\u4e0b\u6765\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u62a4\u536b\u4e0d\u5468\uff0c\u8bba\u7f6a\u5f53\u6b7b\uff0c\u6587\u4e2d\u5df2\u7ecf \u8bad\u65a5\u8fc7\u4ed6\u4eec\u4e00\u756a\u4e86\uff0c\u6c5f\u5148\u751f\u8981\u95ee\u4ed6\u4eec\uff0c\u6211\u7acb\u523b\u53bb\u627e\u4ed6\u4eec\u6765\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u90a3\u5c31\u6709\u52b3\u5927\u4eba\u4e86\uff0c\u6211\u8981\u8be6\u7ec6\u7684\u95ee\u4e00\u4e0b\u5185\u60c5\uff0c\u770b\u80fd\u5426\u627e\u51fa\u4e00\u70b9\u5934\u7eea\u6765\u3002\u201d \u3000\u3000\u9a6c\u5de1\u629a\u53ec\u5524\u6765\u968f\u884c\u65d7\u724c\u5b98\u5458\uff0c\u7acb\u523b\u53bb\u5e26\u4e09\u540d\u4f8d\u536b\u4e24\u4f4d\u5bab\u5973\u6765\u5f00\u5c01\u5e9c\u5185\u5385\u76f8\u89c1\u3002 \u3000\u3000\u6b64\u65f6\uff0c\u5df2\u8d77\u4e94\u66f4\uff0c\u6b63\u662f\u4eba\u4eec\u597d\u7761\u7684\u65f6\u523b\uff0c\u4f46\u9a6c\u5de1\u629a\u3001\u5c39\u77e5\u5e9c\u7adf\u90fd\u6beb\u65e0\u7761\u610f\uff0c\u751f\u6b7b\u5927\u4e8b\uff0c \u6bd5\u7adf\u662f\u8ba9\u4eba\u7cbe\u795e\u7d27\u5f20\uff0c\u5fc3\u5f26\u9707\u8361\u3002 \u3000\u3000\u4e09\u4e2a\u4f8d\u536b\u7684\u5e74\u9f84\u90fd\u4e0d\u5927\uff0c\u4e8c\u5341\u4e03\u516b\u5c81\u7684\u6837\u5b50\uff0c\u4f46\u6c5f\u5e72\u91cc\u5374\u770b\u51fa\u4ed6\u4eec\u90fd\u662f\u5185\u5916\u517c\u4fee\u7684\u597d \u624b\uff0c\u53ea\u53ef\u60dc\uff0c\u592a\u7f3a\u5c11\u6c5f\u6e56\u5386\u7ec3\u4e86\u3002 \u3000\u3000\u4e24\u4e2a\u5bab\u5973\u4e5f\u76f8\u5f53\u7f8e\u4e3d\uff0c\u867d\u7136\u54ed\u5f97\u53cc\u76ee\u7ea2\u80bf\uff0c\u8138\u8272\u82cd\u767d\uff0c\u4f46\u5374\u63a9\u4e0d\u4f4f\u5929\u751f\u7684\u4e3d\u8d28\u3002 \u3000\u3000\u6df1\u5bab\u5185\u82d1\u4e2d\uff0c\u679c\u7136\u662f\u4eba\u624d\u4f1a\u805a\u4e4b\u5730\u3002 \u3000\u3000\u4e09\u4e2a\u4f8d\u536b\u8bf4\u7684\uff0c\u5927\u81f4\u548c\u9a6c\u5de1\u629a\u8bf4\u7684\u76f8\u540c\uff0c\u4ed6\u4eec\u5165\u591c\u540e\uff0c\u5206\u73ed\u5728\u4e09\u516c\u4e3b\u5367\u623f\u5916\u9762\u5de1\u5b88\uff0c\u76f4 \u5230\u8fd1\u4e94\u66f4\u65f6\u5206\u624d\u677e\u61c8\u4e0b\u6765\uff0c\u56de\u623f\u4f11\u606f\u3002 \u3000\u3000\u6c5f\u5e72\u91cc\u6697\u6697\u5fd6\u9053\uff1a\u201c\u771f\u662f\u5c11\u4e0d\u66f4\u4e8b\uff0c\u6700\u5371\u9669\u7684\u65f6\u523b\u4ed6\u4eec\u7adf\u7136\u90fd\u56de\u623f\u7761\u89c9\u53bb\u4e86\u3002\u4f46\u60f3\u5230\u4f4f \u5728\u5f00\u5c01\u5e9c\u8fd9\u7b49\u4e00\u7701\u9996\u5584\u4e4b\u533a\uff0c\u6ca1\u6709\u6c5f\u6e56\u7ecf\u9a8c\u7684\u4eba\uff0c\u4e5f\u96be\u514d\u4f1a\u51cf\u5c11\u6212\u5fc3\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u8d23\u5907\u4e09\u4e2a\u4f8d\u536b\uff0c\u8fd9\u4e9b\u5bab\u5ef7\u5185\u536b\uff0c\u5e73\u65e5\u8dbe\u9ad8\u6c14\u626c\uff0c\u5c31\u662f\u4e00\u54c1\u5927\u5458\u4ed6\u4eec\u4e5f\u4e0d\u4f1a\u6709\u6240\u754f \u60e7\uff0c\u4f46\u6b64\u523b\u5bf9\u6c5f\u5343\u91cc\u5374\u662f\u606d\u606d\u656c\u656c\u7684\uff0c\u6709\u95ee\u5fc5\u7b54\u3002 \u3000\u3000\u4e24\u4e2a\u5bab\u5973\u66f4\u662f\u6218\u6218\u5162\u5162\u7684\u56de\u8bdd\uff0c\u4f46\u56de\u7b54\u7684\u90fd\u662f\u4e00\u4e9b\u6ca1\u6709\u4ef7\u503c\u7684\u4e1c\u897f\u3002 \u3000\u3000\u5979\u4eec\u548c\u516c\u4e3b\u540c\u4f4f\u5728\u4e00\u95f4\u5206\u5185\u5916\u7684\u5927\u5ba2\u623f\u4e2d\uff0c\u4e09\u516c\u4e3b\u4f4f\u5728\u5185\u95f4\uff0c\u4e24\u4e2a\u5bab\u5973\u5408\u4f4f\u5916\u95f4\uff0c\u5979\u4eec \u4ec0\u4e48\u90fd\u4e0d\u77e5\u9053\uff0c\u7a74\u9053\u88ab\u89e3\uff0c\u9192\u4e86\u8fc7\u6765\uff0c\u8fd9\u624d\u77e5\u9053\u516c\u4e3b\u5931\u8e2a\u4e86\uff0c\u5413\u5f97\u4e09\u9b42\u90fd\u51fa\u4e86\u7a8d\uff0c\u54ed\u5f97\u53cc\u76ee \u7ea2\u80bf\uff0c\u5fc3\u4e2d\u5df2\u5bb3\u6015\u5230\u8336\u996d\u4e0d\u601d\u4e86\u3002 \u3000\u3000\u5c39\u77e5\u5e9c\u542c\u5f97\u4e0d\u4f4f\u6447\u5934\u53f9\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u7b97\u4ec0\u4e48\u56de\u7b54\uff0c\u7b80\u76f4\u662f\u8bf4\u4e86\u7b49\u4e8e\u767d\u8bf4\uff01\u201d \u3000\u3000\u9a6c\u5de1\u629a\u5374\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u548c\u516c\u4e3b\u5408\u4f4f\u4e00\u623f\uff0c\u516c\u4e3b\u5931\u8e2a\u4e86\uff0c\u4f60\u4eec\u5374\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\uff0c \u8fd9\u53ef\u662f\u6284\u5bb6\u706d\u95e8\u5927\u7f6a\uff0c\u672c\u5de1\u629a\u5c31\u662f\u5148\u628a\u4f60\u4eec\u91cd\u8d23\u4e00\u987f\u677f\u5b50\uff0c\u7687\u4e0a\u4e5f\u4e0d\u4f1a\u602a\u7f6a\u4e0b\u6765\u7684\u3002\u201d \u3000\u3000\u4e24\u4e2a\u5bab\u5973\u521a\u521a\u505c\u4e0b\u6765\u7684\u773c\u6cea\uff0c\u53c8\u5f00\u59cb\u5782\u4e0b\u53cc\u816e\uff0c\u54fd\u54bd\u7740\u8bf4\uff1a\u201c\u5a62\u5b50\u8be5\u6b7b\uff0c\u53ef\u662f\u6211\u4eec\u771f\u7684 \u4ec0\u4e48\u90fd\u4e0d\u77e5\u9053\u554a\uff01\u5927\u4eba\u5904\u6b7b\u6211\u4eec\uff0c\u5a62\u5b50\u4ea6\u65e0\u6028\u8a00\uff0c\u53cd\u6b63\u56de\u5230\u5bab\u91cc\u4e5f\u4f1a\u88ab\u6d3b\u6d3b\u6253\u6b7b\u3002\u201d \u3000\u3000\u9a6c\u5de1\u629a\u6ca1\u8f84\u4e86\uff0c\u5413\u5413\u5979\u4eec\u53ef\u4ee5\uff0c\u771f\u8981\u52a8\u5211\u903c\u95ee\u53e3\u4f9b\uff0c\u4ed6\u8fd9\u4f4d\u4e00\u7701\u9996\u957f\u8fd8\u771f\u7684\u4e0d\u6562\uff0c\u6bd5\u7adf \u5979\u4eec\u662f\u7687\u5bab\u4e2d\u7684\u4eba\u3002 \u3000\u3000\u201c\u5927\u4eba\uff01\u201d\u6c5f\u5343\u91cc\u4f4e\u58f0\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\uff0c\u8fd8\u662f\u4ea4\u7ed9\u6c5f\u67d0\u5904\u7f6e\u5427\uff01\u201d \u3000\u3000\u201c\u597d\uff01\u597d\uff01\u90a3\u5c31\u6709\u52b3\u6c5f\u5148\u751f\u4e86\u3002\u201d \u3000\u3000\u201c\u4e24\u4f4d\u59d1\u5a18\u53ef\u6709\u4e00\u4e2a\u79f0\u547c\uff1f\u201d \u3000\u3000\u4e8c\u5973\u70b9\u70b9\u5934\uff0c\u5de6\u9996\u4e00\u4e2a\u9053\uff1a\u201c\u6211\u53eb\u60dc\u6625\u3002\u201d \u3000\u3000\u53f3\u9996\u4e00\u4e2a\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u6211\u53eb\u601c\u82b1\u3002\u201d \u3000\u3000\u201c\u597d\u96c5\u81f4\u7684\u540d\u5b57\u2026\u2026\u201d\u6c5f\u5e72\u91cc\u5fae\u7b11\u7740\u8bf4\u3002 \u3000\u3000\u60dc\u6625\u9053\uff1a\u201c\u540d\u5b57\u662f\u4e09\u516c\u4e3b\u66ff\u6211\u4eec\u53d6\u7684\uff0c\u6211\u4eec\u4e09\u5e74\u524d\u5165\u5bab\uff0c\u5c31\u62e8\u5728\u4e09\u516c\u4e3b\u624b\u4e0b\u542c\u5dee\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u5c3d\u91cf\u4fdd\u6301\u6e29\u548c\uff0c\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u4e09\u516c\u4e3b\u5f88\u559c\u6b22\u4f60\u4eec\u4e24\u4e2a\u4e86\uff0c\u8fd8\u7279\u522b\u66ff\u4f60\u4eec\u53d6\u4e86\u540d \u5b57\uff1f\u201d \u3000\u3000\u601c\u82b1\u9053\uff1a\u201c\u662f\uff0c\u4e09\u516c\u4e3b\u770b\u6211\u4eec\u8fd8\u7b97\u4f36\u4fd0\uff0c\u53c8\u8bfb\u8fc7\u51e0\u5e74\u8bd7\u4e66\uff0c\u5c31\u628a\u6211\u4eec\u6536\u4f5c\u8fd1\u8eab\u4f8d\u5973\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u7b11\u9053\uff1a\u201c\u597d\uff0c\u90a3\u5c31\u8bf7\u4e24\u4f4d\u4ed4\u7ec6\u60f3\u60f3\u770b\uff0c\u6709\u4ec0\u4e48\u80fd\u544a\u8bc9\u6211\u7684\uff0c\u4e5f\u8bb8\u4e00\u8bed\u60ca\u9192\u68a6\u4e2d \u4eba\uff0c\u80fd\u5e2e\u6211\u8ffd\u67e5\u51fa\u4e09\u516c\u4e3b\u7684\u4e0b\u843d\u3002\u201d \u3000\u3000\u60dc\u6625\u9053\uff1a\u201c\u548c\u4e09\u516c\u4e3b\u5931\u8e2a\u65e0\u5173\u7684\u4e8b\uff0c\u4e5f\u8981\u8bf4\u4e48\uff1f\u201d \u3000\u3000\u201c\u5bf9\uff01\u4f60\u6240\u77e5\u9053\u7684\u5c3d\u91cf\u90fd\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u60dc\u6625\u4eba\u5df2\u9547\u9759\u4e0b\u6765\uff0c\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u559c\u8bfb\u8bd7\u8bcd\uff0c\u5e38\u5e38\u5bf9\u6708\u541f\u548f\uff0c\u4e5f\u5f39\u5f97\u4e00\u624b \u597d\u7434\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u53ea\u597d\u70b9\u70b9\u5934\u3002 \u3000\u3000\u4ed6\u867d\u7136\u9605\u5386\u4e30\u5bcc\uff0c\u6d1e\u5bdf\u7ec6\u81f4\uff0c\u4f46\u5bf9\u8bd7\u8bcd\u6b4c\u8d4b\u3001\u7434\u68cb\u4e66\u753b\u7684\u4e8b\uff0c\u5374\u662f\u5168\u65e0\u4e86\u89e3\uff0c\u4e00\u70b9\u513f\u4e5f \u642d\u4e0d\u4e0a\u8c03\u3002 \u3000\u3000\u9a6c\u5de1\u629a\u5374\u63a5\u53e3\u8bf4\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u597d\u8bd7\u8bcd\u3001\u7cbe\u7434\u827a\u4e4b\u5916\uff0c\u8fd8\u4f1a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u8c31\u66f2\u2026\u2026\u201d \u3000\u3000\u201c\u55c5\uff01\u4e09\u516c\u4e3b\u8fd8\u7cbe\u901a\u97f3\u5f8b\uff1f\u201d \u3000\u3000\u201c\u662f\uff0c\u4e09\u516c\u4e3b\u8c31\u66f2\u586b\u8bcd\uff0c\u81ea\u5f39\u81ea\u5531\u3002\u201d\u60dc\u6625\u8bf4\uff1a\u201c\u5531\u5f97\u5a49\u7ea6\u52a8\u4eba\uff0c\u542c\u5f97\u5a62\u5b50\u4eec\u5982\u75f4\u5982 \u9189\u3002\u201d \u3000\u3000\u9a6c\u5de1\u629a\u9053\uff1a\u201c\u4f60\u53ef\u8bb0\u5f97\u4e09\u516c\u4e3b\u8c31\u7684\u66f2\u8bcd\u4e48\uff1f\u201d \u3000\u3000\u201c\u5a62\u5b50\u8bb0\u5f97\u51e0\u53e5\u2026\u2026\u201d \u3000\u3000\u201c\u80fd\u4e0d\u80fd\u80cc\u51fa\u6765\u542c\u542c\uff1f\u201d \u3000\u3000\u60dc\u6625\u70b9\u70b9\u5934\u9053\uff1a\u201c\u5986\u53f0\u8102\u7c89\uff0c\u955c\u91cc\u82b1\u5bb9\uff0c\u76f8\u5bf9\u4ea6\u5b64\u5f71\uff0c\u8f7b\u98a6\u6d45\u7b11\u4e24\u76f8\u540c\u2026\u2026\u201d \u3000\u3000\u201c\u597d\u8bcd\uff0c\u597d\u8bcd\uff0c\u4e09\u516c\u4e3b\u771f\u662f\u4e00\u4f4d\u624d\u5973\u2026\u2026\u201d \u3000\u3000\u6c5f\u5e72\u91cc\u63a5\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u9664\u4e86\u5f39\u7434\u5531\u6b4c\u4e4b\u5916\u5462\uff1f\u201d \u3000\u3000\u201c\u821e\u5251\u2026\u2026\u201d\u601c\u82b1\u63a5\u53e3\u8bf4\u3002 \u3000\u3000\u201c\u821e\u5251\uff1f\u4f60\u662f\u8bf4\u4e09\u516c\u4e3b\u4f1a\u6b66\u529f\u4e86\u2026\u2026\u201d\u6c5f\u5e72\u91cc\u6025\u6025\u8feb\u95ee\u3002 \u3000\u3000\u201c\u597d\u50cf\u662f\u5427\uff01\u201d\u601c\u82b1\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u4ea4\u5f85\u8fc7\u6211\u4eec\uff0c\u4e0d\u8bb8\u6211\u4eec\u8bf4\u51fa\u8fd9\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u5462\uff1f\u201d\u5c39\u77e5\u5e9c\u4e5f\u63a5\u4e86\u4e00\u53e5\u3002 \u3000\u3000\u201c\u4e0d\u77e5\u9053\uff0c\u4e09\u516c\u4e3b\u4ea4\u4ee3\u4e86\uff0c\u6211\u4eec\u5c31\u8bb0\u5728\u5fc3\u4e2d\u2026\u2026\u201d\u601c\u82b1\u8bf4\uff1a\u201c\u4e0d\u6562\u968f\u968f\u4fbf\u4fbf\u7684\u5c31\u8bf4\u51fa \u53bb\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\uff0c\u60c5\u5f62\u4e0d\u540c\u4e86\u3002\u201d\u6c5f\u5343\u91cc\u4f4e\u58f0\u5730\u8bf4\uff1a\u201c\u4e09\u516c\u4e3b\u5931\u8e2a\u4e86\uff0c\u6211\u4eec\u8981\u628a\u5979\u627e\u56de\u6765\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u201d\u601c\u82b1\u3001\u60dc\u6625\u540c\u65f6\u7b54\u5e94\u3002 \u3000\u3000\u201c\u90a3\u5c31\u8981\u8bf4\u51fa\u4f60\u4eec\u6240\u77e5\u9053\u7684\u4e8b\uff0c\u4e3a\u4e86\u6551\u4e09\u516c\u4e3b\uff0c\u6211\u60f3\u65e5\u540e\uff0c\u4e09\u516c\u4e3b\u8981\u662f\u77e5\u9053\u4e86\u4e5f\u4e0d\u4f1a\u8d23 \u602a\u4f60\u4eec\u7684\u3002\u201d \u3000\u3000\u601c\u82b1\u9eef\u7136\u5730\u9053\uff1a\u201c\u53ea\u8981\u80fd\u627e\u5230\u4e09\u516c\u4e3b\uff0c\u5c31\u662f\u8d23\u6253\u5a62\u5b50\u4e00\u987f\uff0c\u5a62\u5b50\u4e5f\u5fc3\u7518\u60c5\u613f\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u90a3\u5c31\u5feb\u8bf4\u5427\uff01\u201d\u6c5f\u5343\u91cc\u6253\u94c1\u8d81\u70ed\u5730\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u7684\u6b66\u529f\uff0c\u662f\u4e0d\u662f\u5f88\u9ad8\u660e\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u5a62\u5b50\u4e0d\u61c2\u6b66\u529f\u3002\u201d\u601c\u82b1\u8bf4\uff1a\u201c\u6211\u53ea\u770b\u5230\u5979\u821e\u4e00\u6b21\u5251\uff0c\u597d\u50cf\u4e00\u5927\u56e2\u767d\u5149\uff0c\u628a \u4eba\u5f71\u90fd\u906e\u4f4f\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u601c\u82b1\u59d1\u5a18\uff0c\u8fd8\u6709\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4e09\u516c\u4e3b\u6709\u4e00\u4e2a\u5bc6\u5ba4\u2026\u2026\u201d \u3000\u3000\u201c\u4ec0\u4e48\u6837\u7684\u5bc6\u5ba4\uff1f\u201d \u3000\u3000\u201c\u4e09\u516c\u4e3b\u4e0d\u51c6\u6211\u4eec\u8fdb\u53bb\uff0c\u8fde\u6253\u626b\u90fd\u662f\u5979\u4eb2\u81ea\u52a8\u624b\u7684\u3002\u201d\u601c\u82b1\u8bf4\uff1a\u201c\u4e0d\u8fc7\uff0c\u4e09\u516c\u4e3b\u6bcf\u5929\u90fd \u5728\u5bc6\u5ba4\u4e2d\u5f85\u4e0a\u51e0\u4e2a\u65f6\u8fb0\uff0c\u6709\u4e00\u6b21\uff0c\u5a62\u5b50\u4e00\u65e9\u53bb\u8bf7\u4e09\u516c\u4e3b\u5403\u65e9\u70b9\uff0c\u770b\u5230\u5979\u5728\u5bc6\u5ba4\u4e2d\u8d70\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u90a3\u662f\u7ec3\u529f\u7684\u5bc6\u5ba4\u4e86\u2026\u2026\u201d\u6c5f\u5343\u91cc\u76ee\u5149\u8f6c\u5230\u4e09\u4e2a\u4f8d\u536b\u8eab\u4e0a\uff0c\u9053\uff1a\u201c\u4e09\u4f4d\u90fd\u662f\u5bab\u4e2d\u4f8d\u536b\uff0c \u53ef\u77e5\u9053\u4e09\u516c\u4e3b\u4f1a\u6b66\u529f\u7684\u4e8b\uff1f\u201d \u3000\u3000\u4e09\u4e2a\u4eba\u4e00\u9f50\u6447\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u77e5\u9053\uff0c\u5728\u5185\u5bab\u4f8d\u536b\u4e2d\uff0c\u4e5f\u672a\u542c\u95fb\u8fd9\u4e2a\u4f20\u8bf4\u3002\u201d \u3000\u3000\u201c\u4e09\u4f4d\u90fd\u662f\u5185\u5916\u517c\u4fee\u7684\u4eba\uff0c\u966a\u516c\u4e3b\u957f\u9014\u5357\u6e38\uff0c\u81f3\u5c11\u5e94\u8be5\u770b\u5f97\u51fa\u4e09\u516c\u4e3b\u662f\u5426\u8eab\u5177\u6b66\u529f\u3002\u201d \u3000\u3000\u7ad9\u5728\u4e0a\u9996\u7684\u4e00\u4e2a\u4f8d\u536b\u9053\uff1a\u201c\u516c\u4e3b\u4e58\u5750\u7bf7\u8f66\uff0c\u6211\u4eec\u9a91\u9a6c\u968f\u62a4\uff0c\u867d\u7136\u65e5\u5915\u76f8\u8fd1\uff0c\u4f46\u771f\u6b63\u89c1\u9762 \u5374\u662f\u4e0d\u591a\uff0c\u4f55\u51b5\uff0c\u516c\u4e3b\u51e4\u4eea\u903c\u4eba\uff0c\u6211\u4eec\u4e5f\u4e0d\u6562\u51dd\u89c6\uff0c\u5c31\u7ea6\u7565\u51e0\u773c\u8bb0\u5fc6\uff0c\u77a7\u4e0d\u51fa\u4e09\u516c\u4e3b\u6709\u4e60\u8fc7 \u6b66\u529f\u7684\u6837\u5b50\u3002\u201d \u3000\u3000\u201c\u601c\u82b1\u59d1\u5a18\uff0c\u4f60\u518d\u4ed4\u7ec6\u7684\u8bf4\u8bf4\uff0c\u4e09\u516c\u4e3b\u821e\u5251\u7684\u60c5\u5f62\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u5a62\u5b50\u867d\u4e0d\u61c2\u6b66\u529f\uff0c\u8bf4\u4e0d\u51fa\u4e09\u516c\u4e3b\u821e\u7684\u662f\u4ec0\u4e48\u5251\u6cd5\uff0c\u4f46\u662f\uff0c\u90a3\u5251\u5149\u98de\u65cb\uff0c\u4e24\u652f\u65b9\u5706 \u5185\u5251\u6c14\u903c\u4eba\uff0c\u5a62\u5b50\u7ad9\u5728\u4e09\u4e08\u5916\u4e00\u4e1b\u82b1\u6811\u4e0b\uff0c\u4e5f\u611f\u89c9\u5230\u5251\u98ce\u98d2\u98d2\uff0c\u6251\u9762\u751f\u5bd2\u3002\u6211\u60f3\u90a3\u5e94\u8be5\u662f\u4e00 \u79cd\u5f88\u5389\u5bb3\u7684\u821e\u5251\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u662f\u7684\uff0c\u90a3\u662f\u76f8\u5f53\u51cc\u5389\u7684\u5251\u6cd5\u4e86\u3002\u201d\u6c5f\u5e72\u91cc\u7684\u76ee\u5149\u53c8\u8f6c\u5230\u4e09\u4e2a\u4f8d\u536b\u7684\u8138\u4e0a\uff0c\u9053\uff1a\u201c\u5982\u6b64 \u5251\u6c14\uff0c\u5e38\u73b0\u5185\u5bab\uff0c\u4e09\u4e2a\u5e94\u8be5\u6709\u4e2a\u8033\u95fb\u624d\u662f\u3002\u201d \u3000\u3000\u4ecd\u662f\u90a3\u4e0a\u9996\u7684\u4f8d\u536b\u5f00\u53e3\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u738b\u91cd\u5c71\uff0c\u51fa\u8eab\u6b66\u5f53\u95e8\u4e0b\uff0c\u7ec3\u5251\u5341\u4f59\u5e74\uff0c\u51fa\u5e08\u4e4b\u540e\uff0c \u672c\u8be5\u884c\u9053\u6c5f\u6e56\uff0c\u4f46\u5374\u88ab\u53d1\u73b0\u4efb\u5185\u5bab\u4f8d\u536b\u7edf\u9886\u5f15\u5165\u5185\u5bab\uff0c\u5c31\u4efb\u73b0\u804c\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4ef6\u4e8b\uff0c\u4ee4\u5e08\u77e5\u9053\u4e48\uff1f\u201d \u3000\u3000\u201c\u77e5\u9053\uff0c\u91cd\u5c71\u7980\u544a\u8fc7\u5e08\u957f\uff0c\u5e08\u957f\u6ca1\u6709\u53cd\u5bf9\uff0c\u53ea\u544a\u8bc9\u91cd\u5c71\uff0c\u4e0d\u51c6\u52a0\u5165\u5382\u536b\uff0c\u4e5f\u4e0d\u80fd\u4ee5\u6b66\u5f53 \u95e8\u4e0b\u70ab\u8000\uff0c\u5185\u5bab\u4f8d\u536b\u4e0d\u5c5e\u4e1c\u5382\u7ba1\u8f96\uff0c\u4e5f\u4e0d\u53d7\u5b98\u65b9\u6307\u6325\uff0c\u7b97\u662f\u7687\u5bb6\u96c7\u8bf7\u7684\u79c1\u4eba\u4f8d\u536b\uff0c\u4ff8\u94f6\u4f18 \u539a\uff0c\u91cd\u5c71\u7b54\u5e94\u738b\u7edf\u9886\u4efb\u804c\u4e09\u5e74\uff0c\u51b3\u4e0d\u5ef6\u7eed\u3002\u4eca\u5e74\u814a\u6708\uff0c\u9650\u671f\u5373\u6ee1\uff0c\u738b\u7edf\u9886\u5df2\u5e94\u5141\u91cd\u5c71\u79bb \u804c\u2026\u2026\uff0c\uff0c\u201d\u5662\uff01\u90a3\u4f4d\u5185\u5bab\u4f8d\u536b\u7edf\u9886\u4e5f\u59d3\u738b\uff1f\u201c\u6c5f\u5343\u91cc\u5fae\u7b11\u7740\u95ee\u3002 \u3000\u3000\u201c\u662f\uff0c\u738b\u7edf\u9886\u4e5f\u662f\u91cd\u5c71\u7684\u4e8c\u53d4\uff0c\u624d\u96be\u5374\u76db\u610f\uff0c\u8fdb\u5165\u5bab\u5ef7\uff0c\u60f3\u4e0d\u5230\u8ddd\u79bb\u804c\u4e0d\u5230\u534a\u5e74\u7684\u65f6 \u5149\uff0c\u7ade\u66ff\u4ed6\u51fa\u4e86\u4e00\u4e2a\u7f6a\u53ca\u5bb6\u65cf\u7684\u5927\u6f0f\u5b50\uff0c\u60f3\u8d77\u6765\uff0c\u771f\u662f\u60b2\u75db\u4e07\u5206\u3002\u201d \u3000\u3000\u201c\u738b\u5c11\u5144\u2026\u2026\u201d\u6c5f\u5343\u91cc\u5b89\u6170\u5730\u9053\uff1a\u201c\u4e8b\u5df2\u81f3\u6b64\uff0c\u6025\u5728\u5584\u540e\uff0c\u4f46\u4e09\u516c\u4e3b\u662f\u5426\u8eab\u5177\u6b66\u529f\uff0c\u5173 \u7cfb\u91cd\u5927\u2026\u2026\u201d \u3000\u3000\u201c\u601c\u82b1\uff0c\u4f60\u8bf4\u4e09\u516c\u4e3b\u5e38\u5e38\u821e\u5251\uff0c\u5251\u6c14\u53ef\u8fdc\u53ca\u4e09\u4e08\uff1f\u201d\u738b\u91cd\u5c71\u53cc\u76ee\u51dd\u6ce8\u601c\u82b1\u3002 \u3000\u3000\u201c\u662f\uff0c\u5a62\u5b50\u7ad9\u7684\u5730\u65b9\uff0c\u8ddd\u79bb\u6050\u6015\u8fd8\u5728\u4e09\u4e08\u4ee5\u5916\uff0c\u4e0d\u8fc7\uff0c\u4e09\u516c\u4e3b\u4e0d\u662f\u5e38\u5e38\u821e\u5251\uff0c\u5a62\u5b50\u5949\u4f8d \u4e09\u516c\u4e3b\u4e09\u5e74\u591a\uff0c\u53ea\u89c1\u8fc7\u90a3\u4e00\u6b21\u3002\u201d \u3000\u3000\u201c\u4e09\u516c\u4e3b\u5728\u4ec0\u4e48\u5730\u65b9\u821e\u5251\uff1f\u201d \u3000\u3000\u201c\u5728\u542c\u8749\u9662\uff0c\u90a3\u662f\u4e00\u4e2a\u9ec4\u660f\u65f6\u523b\uff0c\u665a\u971e\u6ee1\u5929\u2026\u2026\u201d \u3000\u3000\u201c\u542c\u8749\u9662\u662f\u516c\u4e3b\u5c45\u4f4f\u7684\u9662\u843d\uff0c\u975e\u6709\u7d27\u6025\u4e8b\u6545\uff0c\u6211\u4eec\u662f\u4e0d\u80fd\u53bb\u90a3\u5730\u65b9\uff0c\u5bab\u5899\u9ad8\u5927\uff0c\u82b1\u6728\u63a9 \u6620\uff0c\u665a\u971e\u7eda\u70c2\u4e2d\u5251\u6c14\u6eb6\u4e8e\u65e5\u5149\u4e4b\u5185\uff0c\u5f88\u96be\u67e5\u89c9\u3002\u60dc\u6625\uff0c\u4f60\u89c1\u8fc7\u4e09\u516c\u4e3b\u821e\u5251\u4e48\uff1f\u201d\u738b\u91cd\u5c71\u7684\u76ee \u5149\u8f6c\u5230\u60dc\u6625\u7684\u8138\u4e0a\u3002 \u3000\u3000\u201c\u6ca1\u6709\uff0c\u5a62\u5b50\u53ea\u770b\u8fc7\u4e09\u516c\u4e3b\u4e66\u623f\u4e2d\u6302\u7740\u4e00\u652f\u5b9d\u5251\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u4e09\u516c\u4e3b\u662f\u6709\u610f\u9690\u85cf\u4f1a\u6b66\u529f\u7684\u4e8b\u4e86\u3002\u201d\u6c5f\u5343\u91cc\u4f4e\u58f0\u9053\uff1a\u201c\u738b\u5c11\u5144\uff0c\u4ee4\u53d4\u7684\u5927 \u540d\u662f\u2026\u2026\u201d \u3000\u3000\u738b\u91cd\u5c71\u9053\uff1a\u201c\u7687\u5ba4\u5185\u536b\uff0c\u6781\u91cd\u79c1\u5bc6\uff0c\u4f46\u76ee\u4e0b\u60c5\u52bf\u4e0d\u540c\uff0c\u91cd\u5c71\u4e0d\u6562\u9690\u7792\uff0c\u5bb6\u53d4\u5355\u540d\u4e00\u4e2a\u5f64 \u5b57\u3002\u201d \u3000\u3000\u201c\u5475\uff01\u6458\u661f\u624b\u738b\u5f64\uff0c\u6211\u8bf4\u5462\uff01\u6c5f\u6e56\u4e0a\u5df2\u7ecf\u5341\u51e0\u5e74\u542c\u4e0d\u5230\u4ed6\u7684\u8baf\u606f\uff0c\u6211\u8fd8\u4ee5\u4e3a\u4ed6\u8eb2\u8d77\u6765\u4eab \u6e05\u798f\u4e86\uff0c\u60f3\u4e0d\u5230\u7adf\u88ab\u7687\u5bb6\u8058\u4f5c\u4e86\u4f8d\u536b\u7edf\u9886\u3002\u201d \u3000\u3000\u201c\u6c5f\u524d\u8f88\u548c\u5bb6\u53d4\u8ba4\u8bc6\uff1f\u201d \u3000\u3000\u201c\u4e0d\u4f46\u8ba4\u8bc6\uff0c\u4e5f\u7b97\u5f97\u4e0a\u662f\u809d\u80c6\u76f8\u4ea4\u7684\u597d\u670b\u53cb\u3002\u201d\u6c5f\u5343\u91cc\u5401\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u738b\u5c11\u5144\uff0c\u5bf9\u4e09 \u516c\u4e3b\u5931\u8e2a\u4e00\u4e8b\uff0c\u4f5c\u4f55\u6253\u7b97\uff1f\u201d \u3000\u3000\u738b\u7ae5\u5c71\u9053\uff1a\u3002\u2018\u665a\u8f88\u60f3\u4e86\u5f88\u4e45\uff0c\u6b64\u4e8b\u4e0d\u80fd\u518d\u9690\u7792\u4e0b\u53bb\u2026\u2026\u201c \u3000\u3000\u5c39\u77e5\u5e9c\u5927\u611f\u7d27\u5f20\u5730\u9053\uff1a\u201c\u90a3\u738b\u4f8d\u536b\u7684\u610f\u601d\u662f\uff0c\u56de\u4eac\u53bb\u594f\u660e\u5723\u5382\uff1f\u201d \u3000\u3000\u201c\u91cd\u5c71\u4e0d\u6562\u4f5c\u4e3b\uff0c\u51c6\u5907\u6d3e\u4e24\u4f4d\u540c\u6765\u7684\u4f8d\u536b\u56de\u4eac\uff0c\u5411\u7edf\u9886\u7980\u62a5\u8be6\u60c5\uff0c\u662f\u5426\u7980\u660e\u5723\u4e0a\u7531\u7edf\u9886 \u4f5c\u4e3b\uff0c\u91cd\u5c71\u613f\u610f\u7559\u4e0b\u6765\uff0c\u8ffd\u968f\u6c5f\u524d\u8f88\u8ffd\u67e5\u516c\u4e3b\u7684\u4e0b\u843d\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u201d\u5c39\u77e5\u5e9c\u770b\u7740\u9a6c\u5de1\u629a\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4e5f\u8bb8\u6c5f\u5927\u4fa0\u51fa\u624b\u76f8\u52a9\uff0c\u4f1a\u5f88\u5feb\u627e\u5230\u4e09\u516c \u4e3b\u7684\u4e0b\u843d\u3002\u201d \u3000\u3000\u6c5f\u5e72\u91cc\u7b11\u9053\uff1a\u201c\u738b\u7edf\u9886\u5fc3\u601d\u7f1c\u5bc6\uff0c\u8be5\u5982\u4f55\u5904\u7f6e\uff0c\u4ed6\u81ea\u4f1a\u62ff\u634f\u8f7b\u91cd\uff0c\u91cd\u5c71\u80af\u7559\u4e0b\u6765\uff0c\u5bf9\u6211 \u7684\u5e2e\u52a9\u5f88\u5927\u3002\u601c\u82b1\u3001\u60dc\u6625\u6682\u7559\u5728\u5de1\u629a\u5e9c\u4e2d\uff0c\u6c5f\u67d0\u8fd9\u5c31\u548c\u738b\u4f8d\u536b\u3001\u71d5\u603b\u6355\u5934\u540c\u8d74\u4e1c\u5927\u8857\u9ad8\u5347\u5ba2 \u6808\uff0c\u52d8\u67e5\u4e00\u4e0b\uff0c\u95ee\u95ee\u5e97\u4e2d\u4f19\u8ba1\u548c\u8d76\u8f66\u7684\u8f66\u592b\u3002\u201d \u3000\u3000\u201c\u7bf7\u8f66\u662f\u6cbf\u9014\u96c7\u6765\u7684\uff0c\u516c\u4e3b\u8981\u9690\u5bc6\u884c\u8e2a\uff0c\u4e0d\u80af\u7528\u5bab\u4e2d\u7684\u534e\u4e3d\u7bf7\u8f66\uff0c\u81f3\u4e8e\u8f66\u592b\uff0c\u4f3c\u662f\u4e2a\u4ee5 \u8d76\u8f66\u4e3a\u4e1a\u7684\u8001\u5b9e\u4eba\uff0c\u4f46\u91cd\u5c71\u4ecd\u7136\u628a\u4ed6\u64d2\u5f80\u5de1\u5e9c\uff0c\u4ee5\u5907\u67e5\u8be2\u3002\u201d \u3000\u3000\u201c\u6211\u5df2\u7ecf\u628a\u4ed6\u5173\u5165\u76d1\u623f\uff0c\u7740\u4ee4\u4e25\u52a0\u770b\u7ba1\u2026\u2026\u201d\u9a6c\u5de1\u629a\u8bf4\uff1a\u201c\u6c5f\u5148\u751f\u5982\u8981\u95ee\u8bdd\uff0c\u7acb\u523b\u53ef\u4ee5 \u63d0\u5ba1\u3002\u201d \u3000\u3000\u201c\u6211\u60f3\u5148\u53bb\u9ad8\u5347\u5ba2\u6808\u770b\u770b\u73b0\u573a\u2026\u2026\u201d\u7ad9\u8d77\u8eab\u5b50\uff0c\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u201c\u4e14\u6162\u2026\u2026\u201d\u9a6c\u5de1\u629a\u53eb\u4f4f\u4e86\u6c5f\u5343\u91cc\uff0c\u9053\uff1a\u201c\u8ffd\u67e5\u4e09\u516c\u4e3b\u7684\u884c\u8e2a\uff0c\u7531\u6c5f\u5148\u751f\u5168\u6743\u4f5c\u4e3b\uff0c\u71d5 \u603b\u6355\u5934\u5168\u529b\u8944\u52a9\uff0c\u518d\u52a0\u4e0a\u738b\u4f8d\u536b\u8eab\u6000\u5185\u5bab\u4f8d\u536b\u7684\u8170\u724c\uff0c\u5168\u56fd\u90fd\u53ef\u4ee5\u53bb\u5f97\u2026\u2026\u201d\u8bed\u58f0\u4e00\u987f\uff0c\u63a5 \u9053\uff1a\u201c\u62ff\u4e0a\u6765\uff01\u201d \u3000\u3000\u4e00\u4e2a\u4e2d\u519b\u63d0\u7740\u4e00\u4e2a\u5c0f\u76ae\u7bb1\uff0c\u884c\u8fd1\u524d\u6765\u3002 \u3000\u3000\u9a6c\u5de1\u629a\u6253\u5f00\u7bb1\u76d6\uff0c\u9053\uff1a\u201c\u6c5f\u5148\u751f\uff0c\u8fd9\u91cc\u6709\u94f6\u7968\u4e00\u4e07\u4e24\uff0c\u5206\u4e3a\u4e00\u767e\u4e24\u548c\u4e94\u767e\u4e24\u7684\u7968\u9762\uff0c\u91d1 \u53f6\u5b50\u4e09\u767e\u7247\uff0c\u6bcf\u7247\u91cd\u4e00\u4e24\uff0c\u8bf7\u5148\u751f\u6536\u4e0b\uff0c\u4ee5\u5907\u4e0d\u65f6\u4e4b\u9700\u3002\u201d \u3000\u3000\u671b\u7740\u7bb1\u4e2d\u7684\u9ec4\u91d1\u94f6\u7968\uff0c\u6c5f\u5343\u91cc\u6697\u6697\u5fd6\u9053\uff1a\u201c\u597d\u5927\u7684\u624b\u7b14\uff0c\u4e09\u5e74\u6e05\u77e5\u5e9c\uff0c\u5341\u4e07\u96ea\u82b1\u94f6\u3002\u770b \u8d77\u6765\uff0c\u679c\u7136\u4e0d\u9519\uff0c\u505a\u5927\u5b98\u7684\u4eba\u5b9e\u5728\u6709\u94b1\uff0c\u4e0d\u8981\u767d\u4e0d\u8981\u4e86\u2026\u2026\u3002 \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u4f38\u624b\u53d6\u8fc7\u94f6\u7968\u3001\u91d1\u53f6\u5b50\uff0c\u7b11\u9053\uff1a\u201c\u5927\u4eba\u539a\u8d50\u5374\u4e4b\u4e0d\u606d\uff0c\u6c5f\u67d0\u6536\u4e0b\u4e86\uff0c\u591a\u5219\u534a \u6708\uff0c\u5c11\u5219\u4e03\u5929\uff0c\u6c5f\u67d0\u5b9a\u6709\u56de\u97f3\u5949\u544a\u3002\u201d \u3000\u3000\u9a6c\u5de1\u629a\u7b11\u9053\uff1a\u201c\u4ee5\u6c5f\u5148\u751f\u72ec\u6b65\u5929\u4e0b\u7684\u8ffd\u8e2a\u4e4b\u672f\u548c\u4e00\u8eab\u8d85\u7edd\u6b66\u529f\uff0c\u76f8\u4fe1\u5fc5\u7136\u662f\u9a6c\u5230\u6210\u529f\uff0c \u6587\u4e2d\u5728\u5e9c\u4e2d\u606d\u5019\u4f73\u97f3\u3002\u201d \u3000\u3000\u5c39\u77e5\u5e9c\u6025\u6025\u63a5\u9053\uff1a\u201c\u6c5f\u5148\u751f\uff0c\u4e0b\u5b98\u4e5f\u60f3\u5907\u4e9b\u7a0b\u4eea\uff0c\u4ee5\u58ee\u5148\u751f\u884c\u8272\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u7528\u4e86\u3002\u201d\u6c5f\u5343\u91cc\u7b11\u7b11\u9053\uff1a\u201c\u9a6c\u5927\u4eba\u7684\u539a\u8d50\uff0c\u5df2\u591f\u6c5f\u67d0\u4eba\u3001\u738b\u4f8d\u536b\u3001\u71d5\u8001\u5f1f\u4e09\u4eba\u82b1\u4e0a \u4e00\u5e74\u534a\u8f7d\u7684\u3002\u201d \u3000\u3000\u201c\u597d\u597d\u597d\u2026\u2026\u201d\u5c39\u77e5\u5e9c\u9053\uff1a\u201c\u4e0b\u5b98\u5c31\u7b49\u7740\u6c5f\u5148\u751f\u8fce\u63a5\u4e09\u516c\u4e3b\u5f52\u6765\u65f6\uff0c\u518d\u4f5c\u916c\u8c22\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u4e0d\u518d\u7406\u4f1a\u5c39\u77e5\u5e9c\uff0c\u4e3e\u6b65\u5411\u5916\u884c\u53bb\uff0c\u71d5\u98de\u7d27\u968f\u8eab\u540e\uff0c\u738b\u91cd\u5c71\u56de\u5934\u5631\u5490\u4e24\u4e2a\u540c\u6765\u7684\u4f8d \u536b\uff0c\u8981\u4ed6\u4eec\u7acb\u523b\u56de\u4eac\uff0c\u4eab\u62a5\u7edf\u9886\uff0c\u624d\u5feb\u6b65\u8ffd\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u52d8\u67e5\u8fc7\u4e09\u516c\u4e3b\u7684\u5ba2\u623f\uff0c\u738b\u91cd\u5c71\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u6c5f\u524d\u8f88\uff0c\u770b\u51fa\u4e9b\u86db\u4e1d\u9a6c\u8ff9\u6ca1\u6709\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u6325\u6325\u624b\uff0c\u7740\u4ee4\u4e24\u4e2a\u63d0\u7740\u706f\u7b3c\u7684\u5e97\u4f19\u8ba1\u9000\u51fa\u53bb\uff0c\u624d\u56de\u5934\u7b11\u9053\uff1a\u201c\u71d5\u8001\u5f1f\uff0c\u4f60\u770b \u5462\uff1f\u201d \u3000\u3000\u71d5\u98de\u9053\uff1a\u201c\u6211\u770b\uff0c\u6765\u4eba\u662f\u7531\u540e\u9762\u7a97\u6237\u8fdb\u6765\uff0c\u63b3\u63a0\u4e86\u4e09\u516c\u4e3b\u540e\u4ecd\u7531\u540e\u7a97\u79bb\u53bb\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u4eec \u5f88\u4ece\u5bb9\uff0c\u628a\u7a97\u5b50\u5173\u597d\u624d\u5e26\u7740\u4eba\u79bb\u53bb\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u70b9\u70b9\u5934\u9053\uff1a\u201c\u8fd8\u6709\u5462\uff1f\u201d \u3000\u3000\u71d5\u98de\u9053\uff1a\u201c\u597d\u50cf\u7528\u4e86\u8ff7\u9b42\u9999\u4e4b\u7c7b\u7684\u836f\u7269\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u4f59\u5473\u72b9\u5b58\u2026\u2026\u201d\u6c5f\u5343\u91cc\u8bf4\uff1a\u201c\u4e0d\u662f\u4e00\u822c\u7684\u9e21\u9e23\u4e94\u66f4\u8fd8\u9b42\u9999\uff0c\u800c\u662f\u4e00\u79cd\u6781\u4e3a\u5389\u5bb3 \u7684\u8ff7\u9999\uff0c\u53eb\u505a\u2018\u4e00\u6b65\u5012\u2019\uff0c\u836f\u6027\u53d1\u4f5c\u5947\u5feb\uff0c\u95fb\u5230\u9999\u5473\u4eba\u5c31\u660f\u8ff7\uff0c\u5c31\u7b97\u4e00\u8eab\u6b66\u529f\u7684\u9ad8\u624b\uff0c\u5982\u679c \u4e8b\u5148\u4e0d\u95ed\u4f4f\u547c\u5438\uff0c\u4e5f\u4f1a\u7740\u4e86\u9053\u513f\uff0c\u4f46\u4ed6\u4eec\u7528\u836f\u4efd\u91cf\u628a\u63e1\u5f97\u5f88\u597d\uff0c\u60dc\u6625\u3001\u601c\u82b1\u624d\u80fd\u5f88\u5feb\u7684\u6551\u9192 \u8fc7\u6765\u3002\u81f3\u4e8e\u70b9\u4e8c\u5973\u7684\u7a74\u9053\uff0c\u53ea\u4e0d\u8fc7\u63a9\u4eba\u8033\u76ee\u4e4b\u4e3e\uff0c\u6b64\u9999\u5473\u9053\u5f88\u6de1\uff0c\u5e78\u597d\u95e8\u7a97\u90fd\u5173\u5f97\u5f88\u7d27\uff0c\u624d \u4fdd\u7559\u4e00\u70b9\u4f59\u5473\u2026\u2026\u201d \u3000\u3000\u738b\u91cd\u5c71\u63a5\u9053\uff1a\u201c\u4e00\u6b65\u5012\uff01\u665a\u8f88\u5728\u6b66\u5f53\u5c71\u5b66\u827a\u65f6\uff0c\u4f3c\u542c\u51e0\u4f4d\u4e0b\u5c71\u884c\u9053\u7684\u5e08\u5144\u4eec\u63d0\u8fc7\uff0c\u597d\u50cf \u662f\u4e00\u79cd\u5f88\u73cd\u8d35\u7684\u4e1c\u897f\uff0c\u4e0d\u6613\u5f97\u5230\uff0c\u8fd9\u5e94\u8be5\u662f\u4e00\u6761\u5f88\u597d\u7684\u7ebf\u7d22\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u53ef\u4ee5\u786e\u5b9a\u7684\u662f\u4ed6\u4eec\u7528\u4e00\u6b65\u5012\u8ff7\u9b42\u9999\uff0c\u8ff7\u5012\u4e86\u516c\u4e3b\uff0c\u770b\u5e8a\u4e0a\u7684\u6795 \u5934\u503e\u659c\u89d2\u5ea6\uff0c\u4e5f\u8bf4\u660e\u4e86\u4e09\u516c\u4e3b\u786e\u6709\u4e00\u8eab\u4e0d\u9519\u7684\u6b66\u529f\uff0c\u8b66\u89c9\u5947\u9ad8\uff0c\u95fb\u5230\u9999\u5473\uff0c\u4eba\u5df2\u5750\u8d77\uff0c\u53ef\u60dc \u2018\u4e00\u6b65\u5012\u2019\u592a\u8fc7\u9738\u9053\uff0c\u4f46\u5979\u4ecd\u80fd\u5728\u6655\u8ff7\u7684\u4e00\u5239\u90a3\uff0c\u6293\u659c\u4e86\u6795\u5934\u2026\u2026\u201d \u3000\u3000\u201c\u6293\u659c\u6795\u5934\uff0c\u4e3a\u4ec0\u4e48\u5462\uff1f\u5982\u679c\u5979\u6709\u65f6\u95f4\uff0c\u5e94\u8be5\u7559\u4e0b\u4e00\u4e9b\u66f4\u660e\u663e\u7684\u7ebf\u7d22\u624d\u5bf9\u3002\u201d\u738b\u91cd\u5c71\u4f4e \u58f0\u8be2\u95ee\u3002 \u3000\u3000\u201c\u6765\u4e0d\u53ca\u4e86\uff0c\u5185\u529f\u4e0d\u591f\u7cbe\u6df1\u7684\u4eba\uff0c\u6839\u672c\u5c31\u65e0\u6cd5\u6709\u6240\u53cd\u5e94\uff0c\u8fd9\u4e00\u70b9\uff0c\u8db3\u4ee5\u8bc1\u660e\u4e09\u516c\u4e3b\u6709\u7740 \u76f8\u5f53\u6df1\u539a\u7684\u5185\u529f\u3002\u81f3\u4e8e\u6293\u659c\u6795\u5934\uff0c\u53ef\u80fd\u6709\u4e24\u4e2a\u539f\u56e0\u3002\u4e00\u662f\u6795\u5934\u4e0b\u85cf\u6709\u5fc3\u7231\u4e4b\u7269\uff0c\u672c\u80fd\u4ea7\u751f\u4fdd \u62a4\u7684\u53cd\u5e94\uff1b\u7b2c\u4e8c\u79cd\u662f\u6795\u4e0b\u653e\u6709\u5175\u5203\uff0c\u51c6\u5907\u53d6\u51fa\u6740\u654c\u3002\u53ef\u60dc\uff0c\u65f6\u95f4\u592a\u8fc7\u77ed\u4fc3\uff0c\u5979\u4ec0\u4e48\u4e5f\u505a\u4e0d \u5230\u3002\u201d \u3000\u3000\u987f\u4e86\u987f\uff0c\u6c5f\u5343\u91cc\u624d\u53c8\u8bf4\u9053\uff1a\u201c\u81f3\u4e8e\u2018\u4e00\u6b65\u5012\u2019\uff0c\u786e\u5b9e\u662f\u4e0d\u6613\u5f97\u5230\u7684\u4e1c\u897f\uff0c\u4f46\u5e76\u975e\u662f\u4e0d\u80fd \u5f97\u5230\uff0c\u53ea\u8981\u80af\u82b1\u94f6\u5b50\uff0c\u4ecd\u7136\u53ef\u4ee5\u4e70\u5230\uff0c\u6c5f\u6e56\u4e0a\u6301\u6709\u6b64\u7269\u7684\u4eba\u4e0d\u5728\u5c11\u6570\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\uff0c\u662f\u5168\u65e0\u7ebf\u7d22\u4e86\uff1f\u201d\u738b\u91cd\u5c71\u5341\u5206\u61ca\u607c\u3002 \u3000\u3000\u201c\u542c\u8bf4\u2018\u4e00\u6b65\u5012\u2019\u662f\u5deb\u5c71\u795e\u5973\u5cf0\u4e0a\u660e\u6708\u89c2\u7684\u6563\u82b1\u4ed9\u5b50\u914d\u5236\u7684\u72ec\u95e8\u836f\u7269\uff0c\u8981\u4e0d\u8981\u4e0a\u795e\u5973\u5cf0 \u8d70\u4e00\u8d9f\uff1f\u201d\u71d5\u98de\u4e5f\u5c55\u73b0\u4e30\u5bcc\u7684\u89c1\u95fb\u77e5\u8bc6\uff0c\u5f00\u5c01\u5e9c\u603b\u6355\u5934\u7684\u4f4d\u7f6e\uff0c\u7edd\u975e\u6ee5\u7aff\u80fd\u591f\u5145\u6570\u3002 \u3000\u3000\u201c\u53ea\u6015\u662f\u5e0c\u671b\u4e0d\u5927\u2026\u2026\u201d\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u522b\u8bf4\u6563\u82b1\u4ed9\u5b50\u4e0d\u597d\u62db\u60f9\uff0c\u5c31\u7b97\u5979\u80af\u5408\u4f5c\uff0c\u4e5f\u6ca1\u6709 \u529e\u6cd5\u627e\u51fa\u63b3\u8d70\u4e09\u516c\u4e3b\u7684\u4eba\uff0c\u660e\u6708\u89c2\u867d\u7136\u60dc\u552e\u2018\u4e00\u6b65\u5012\u2019\uff0c\u4e5f\u89c4\u5b9a\u975e\u6709\u4e00\u5b9a\u8eab\u5206\u7684\u6c5f\u6e56\u4eba\u7269\uff0c \u4e0d\u80af\u5356\u51fa\u3002\u4f46\u2018\u4e00\u6b65\u5012\u2019\u5728\u6c5f\u6e56\u4e0a\u5df2\u7ecf\u6d41\u4f20\u4e86\u6570\u5341\u5e74\u4e4b\u4e45\uff0c\u6563\u82b1\u4ed9\u5b50\u627f\u7ee7\u5e08\u7236\u8863\u94b5\uff0c\u4e0d\u8fc7\u662f \u4e94\u5e74\u524d\u7684\u4e8b\uff0c\u60f3\u60f3\u770b\uff0c\u660e\u6708\u89c2\u4e0a\u4e00\u4ee3\u7684\u89c2\u4e3b\u5c31\u5f00\u59cb\u51fa\u552e\u2018\u4e00\u6b65\u5012\u2019\u4e86\uff0c\u4f55\u51b5\u8f97\u8f6c\u51fa\u552e\u8c0b\u6c42\u539a \u5229\u7684\u4eba\uff0c\u6c5f\u6e56\u4e0a\u6bd4\u6bd4\u7686\u662f\uff0c\u627e\uff1a\u6563\u82b1\u4ed9\u5b50\u672a\u5fc5\u6709\u76ca\uff0c\u5374\u5192\u4e86\u548c\u5979\u51b2\u7a81\u7684\u5371\u9669\uff01\u201d \u3000\u3000\u201c\u90a3\u2026\u2026\u8be5\u600e\u4e48\u529e\u5462\uff1f\u201d\u738b\u91cd\u5c71\u5fc3\u4e2d\u7126\u6025\u4e4b\u60c5\uff0c\u6ea2\u4e8e\u8a00\u8868\u3002 \u3000\u3000\u201c\u76f4\u63a5\u7684\u8ffd\u4e0b\u53bb\u3002\u4f60\u4eec\u5fd8\u4e86\u6c5f\u67d0\u4eba\u7684\u8ffd\u8e2a\u6280\u672f\uff0c\u5929\u4e0b\u65e0\u4eba\u80fd\u53ca\u3002\u201d \u3000\u3000\u201c\u5bf9\uff0c\u5bf9\uff01\u771f\u662f\u6025\u660f\u4e86\u5934\uff0c\u5fd8\u4e86\u6c5f\u5144\u72ec\u6b65\u6c5f\u6e56\u7684\u795e\u6280\u3002\u5deb\u5c71\u660e\u6708\u89c2\u5f80\u8fd4\u6570\u5343\u91cc\uff0c\u5c31\u7b97\u4e00 \u5207\u987a\u5229\uff0c\u4e5f\u662f\u6765\u4e0d\u53ca\u4e86\u2026\u2026\u201d\u71d5\u98de\u63a5\u9053\uff1a\u201c\u73b0\u5728\uff0c\u8bf7\u6c5f\u5144\u5429\u5490\uff0c\u54b1\u4eec\u7acb\u523b\u884c\u52a8\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u5c31\u89c2\u5bdf\u6240\u5f97\uff0c\u4ed6\u4eec\u8fdb\u5165\u5ba2\u623f\u7684\u81f3\u5c11\u6709\u4e24\u4e2a\u4eba\uff0c\u73b0\u5728\u54b1\u4eec\u5148\u597d\u597d\u4f11\u606f\u4e00\u4e0b\uff0c \u7136\u540e\u9971\u9910\u4e00\u987f\uff0c\u5348\u65f6\u51fa\u53d1\uff0c\u5e26\u4e9b\u5e72\u7cae\uff0c\u4e00\u53e3\u6c14\u8ffd\u4e0b\u53bb\u3002\u201d \u3000\u3000\u201c\u524d\u8f88\uff0c\u5175\u8d35\u795e\u901f\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u73b0\u5728\u5c31\u8d70\u5462\uff1f\u201d\u738b\u91cd\u5c71\u63d0\u51fa\u4e86\u5fc3\u4e2d\u7684\u7591\u95ee\u3002 \u3000\u3000\u201c\u5929\u8272\u8fd8\u672a\u5927\u660e\uff0c\u67e5\u5bdf\u75d5\u8ff9\u4e0d\u6613\u3002\u4f55\u51b5\uff0c\u8ffd\u8e2a\u8e29\u8ff9\uff0c\u8981\u7cbe\u795e\u826f\u597d\u624d\u884c\u2026\u2026\u201d\u6c5f\u5343\u91cc\u8bf4\uff1a \u201c\u8fd8\u662f\u5148\u4f11\u606f\u4e00\u9635\u518d\u8d70\u3002\u201d \u3000\u3000\u738b\u91cd\u5c71\u4e0d\u4fbf\u518d\u8bf4\u4e86\uff0c\u5fc3\u4e2d\u5374\u6697\u6697\u5fd6\u9053\uff1a\u4e09\u516c\u4e3b\u6628\u591c\u5931\u8e2a\uff0c\u5df2\u8fc7\u4e86\u5341\u4e8c\u4e2a\u65f6\u8fb0\u4e4b\u4e45\uff0c\u73b0\u5728 \u662f\u5bf8\u9634\u5fc5\u4e89\uff0c\u4f60\u5374\u8fd8\u8981\u4f11\u606f\u534a\u5929\uff0c\u771f\u662f\u8d3b\u8bef\u620e\u673a\u4e86\u3002 \u3000\u3000\u4f46\u89c1\u6c5f\u5343\u91cc\u76d8\u819d\u5750\u4e0b\uff0c\u8fd0\u6c14\u8c03\u606f\u8d77\u6765\u3002 \u3000\u3000\u71d5\u98de\u4f4e\u58f0\u9053\uff1a\u201c\u738b\u5144\u5f1f\uff0c\u4f11\u606f\u4e00\u4e0b\u5427\uff01\u6c5f\u5144\u6628\u591c\u529b\u6218\u8f6c\u9f99\u624b\u5fd9\u5230\u73b0\u5728\uff0c\u53ea\u6015\u738b\u5144\u4e5f\u4e00\u591c \u672a\u9616\u8fc7\u773c\uff0c\u518d\u8bf4\uff0c\u516c\u4e3b\u5df2\u5931\u8e2a\u4e00\u65e5\u591c\uff0c\u6025\u4e5f\u4e0d\u5728\u4e00\u65f6\uff0c\u517b\u8db3\u7cbe\u795e\uff0c\u4e5f\u597d\u5e94\u4ed8\u7a81\u53d1\u7684\u610f\u5916\u53d8 \u5316\u3002\u201d \u3000\u3000\u738b\u91cd\u5c71\u60f3\u4e00\u60f3\uff0c\u786e\u4e5f\u4e0d\u9519\uff0c\u4f9d\u8a00\u95ed\u76ee\u800c\u5750\u3002 \u3000\u3000\u4e09\u4e2a\u4eba\u90fd\u5f88\u75b2\u7d2f\uff0c\u8fd9\u4e00\u653e\u677e\u5750\u606f\uff0c\u7acb\u65f6\u8fdb\u5165\u4e86\u7269\u6211\u4e24\u5fd8\u4e4b\u5883\u3002 \u3000\u3000\u5f53\u4ed6\u4eec\u4e09\u4eba\u9192\u8fc7\u6765\u65f6\uff0c\u5df2\u7ecf\u662f\u8fd1\u5348\u65f6\u523b\uff0c\u4e24\u4e2a\u591a\u65f6\u8fb0\u7684\u5750\u606f\uff0c\u987f\u4f7f\u4e09\u4eba\u7cbe\u795e\u7115\u53d1\uff0c\u795e\u5145 \u6c14\u8db3\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u5403\u8fc7\u4e1c\u897f\uff0c\u5c31\u53ef\u4ee5\u4e0a\u8def\u4e86\u3002\u201d \u3000\u3000\u71d5\u98de\u53eb\u8fc7\u5e97\u5c0f\u4e8c\uff0c\u5429\u5490\u51c6\u5907\u9152\u83dc\u4e8e\u7cae\uff0c\u6c5f\u5343\u91cc\u5374\u62ff\u51fa\u4e00\u7247\u91d1\u53f6\u5b50\uff0c\u8981\u5e97\u5c0f\u4e8c\u53bb\u5907\u51e0\u5957\u4e0d \u540c\u989c\u8272\u7684\u8863\u670d\u548c\u4e00\u4e9b\u5e94\u7528\u836f\u6750\u3002 \u3000\u3000\u7136\u540e\uff0c\u4ed6\u56de\u5934\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u71d5\u8001\u5f1f\u3001\u91cd\u5c71\uff0c\u4f60\u4eec\u4ed4\u7ec6\u7684\u60f3\u60f3\u770b\uff0c\u8fd8\u8981\u51c6\u5907\u4e9b\u4ec0\u4e48\uff1f\u54b1 \u4eec\u8fd9\u4e00\u53bb\uff0c\u4e0d\u77e5\u8ffd\u8e2a\u591a\u8fdc\uff0c\u884c\u5230\u4f55\u5904\uff1f\u8def\u4e0a\u4e5f\u6709\u53ef\u80fd\u9047\u4e0a\u51f6\u5371\u640f\u6218\uff0c\u5175\u5203\u6697\u5668\u662f\u5426\u9700\u8981\u8865 \u5145\u3002\u201d \u3000\u3000\u738b\u91cd\u5c71\u9053\uff1a\u201c\u665a\u8f88\u7684\u5175\u5203\u6697\u5668\u5168\u90fd\u5e26\u5728\u8eab\u4e0a\u3002\u201d \u3000\u3000\u71d5\u98de\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u662f\u5200\u4e0d\u79bb\u8eab\uff0c\u5012\u662f\u6c5f\u5144\u9700\u8981\u8d2d\u7f6e\u4e00\u4ef6\u5175\u5203\u5462\uff01\u201d \u3000\u3000\u6c5f\u5343\u91cc\u7b11\u9053\uff1a\u201c\u6211\u548c\u71d5\u8001\u5f1f\u4e00\u6837\uff0c\u5175\u5203\u4e00\u76f4\u90fd\u5e26\u5728\u8eab\u4e0a\uff0c\u5f00\u5c01\u5e9c\u7262\u623f\u7981\u5352\uff0c\u90fd\u5f88\u5ba2\u6c14\uff0c \u4e00\u76f4\u90fd\u6ca1\u6709\u4ed4\u7ec6\u7684\u641c\u67e5\u8fc7\u6211\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u6c5f\u5343\u91cc\u628a\u5e26\u5728\u8eab\u4e0a\u7684\u91d1\u53f6\u5b50\u5206\u6210\u4e09\u4efd\uff0c\u7531\u4e09\u4eba\u5206\u5e26\uff0c\u4e09\u767e\u4e24\u9ec4\u91d1\uff0c\u5feb\u5341\u4e5d\u65a4\u7684\u91cd \u91cf\uff0c\u5e26\u5728\u4e00\u4e2a\u4eba\u7684\u8eab\u4e0a\uff0c\u76f8\u5f53\u6c89\u91cd\u3002 \u3000\u3000\u79bb\u5f00\u4e86\u9ad8\u5347\u5ba2\u6808\uff0c\u6c5f\u5343\u91cc\u5e76\u672a\u76f4\u5954\u57ce\u5916\uff0c\u53cd\u800c\u7ed5\u7740\u8857\u9053\u3001\u5df7\u5b50\u8f6c\u52a8\u3002 \u3000\u3000\u4e09\u4eba\u90fd\u7ecf\u8fc7\u4e86\u7b80\u5355\u7684\u6613\u5bb9\uff0c\u6362\u4e0a\u4e86\u7c97\u5e03\u8863\u670d\uff0c\u5b8c\u5168\u53d8\u4e86\u5f62\u8c61\uff0c\u5426\u5219\uff0c\u4ee5\u71d5\u98de\u603b\u6355\u5934\u7684\u8eab \u5206\uff0c\u5f00\u5c01\u5e9c\u65e0\u4eba\u4e0d\u8bc6\uff0c\u8fd9\u4e48\u8f6c\u6765\u8f6c\u53bb\uff0c\u65e9\u5df2\u5f15\u8d77\u8def\u4eba\u7684\u4fa7\u76ee\u4e86\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u4e5f\u4e0d\u662f\u4e71\u8f6c\uff0c\u6bcf\u5230\u4e00\u5904\u8f6c\u5f2f\u7684\u5730\u65b9\uff0c\u4e00\u5b9a\u505c\u4e0b\u6765\u89c2\u5bdf\u5f88\u4e45\uff0c\u800c\u4e14\u4e0d\u505c\u7684\u95fb\u6765\u95fb \u53bb\uff0c\u7136\u540e\u624d\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u738b\u91cd\u5c71\u5fcd\u4e86\u53c8\u5fcd\uff0c\u5b9e\u5728\u5fcd\u4e0d\u4f4f\u4e86\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u524d\u8f88\uff0c\u54b1\u4eec\u8d70\u4e86\u4e00\u4e2a\u591a\u65f6\u8fb0\u4e86\uff0c\u597d\u50cf\u8fd8 \u672a\u8d70\u51fa\u5f00\u5c01\u57ce\uff1f\u201d \u3000\u3000\u201c\u662f\u554a\uff01\u6211\u4e5f\u662f\u89c9\u5f97\u5947\u602a\uff0c\u96be\u9053\u4ed6\u4eec\u771f\u7684\u5c31\u7559\u4e86\u4e0b\u6765\uff0c\u672a\u66fe\u79bb\u5f00\uff1f\u201d \u3000\u3000\u201c\u4ec0\u4e48\uff1f\u2026\u2026\u201d\u71d5\u98de\u53c8\u60ca\u53c8\u559c\u5730\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u8fd8\u7559\u5728\u8fd9\u91cc\uff0c\u90a3\u5177\u662f\u597d\u6781\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u73b0\u5728\uff0c\u8fd8\u4e0d\u80fd\u80af\u5b9a\u2026\u2026\u201d\u6c5f\u5343\u91cc\u4e00\u9762\u56de\u7b54\uff0c\u4e00\u9762\u4e3e\u6b65\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u6761\u5f88\u6574\u9f50\u7684\u5df7\u9053\uff1a\u201c\u4e24\u4fa7\u4eba\u5bb6\u90fd\u662f\u4e00\u8272\u6731\u8272\u5927\u95e8\uff0c\u95e8\u867d\u5927\u5c0f\u4e0d\u540c\uff0c\u4f46\u989c\u8272\u4e00\u81f4\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u5728\u5df7\u5e95\u4e00\u5904\u9ad8\u5927\u7684\u6731\u95e8\u5916\u9762\u505c\u4e0b\uff0c\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u71d5\u8001\u5f1f\uff0c\u8fd9\u662f\u4ec0\u4e48\u5730\u65b9\uff0c\u6211\u5728\u5f00 \u5c01\u5df2\u4f4f\u4e86\u4e03\u516b\u4e2a\u6708\uff0c\u600e\u4e48\u4e00\u76f4\u672a\u5230\u8fc7\u8fd9\u4e2a\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u8fd9\u53eb\u56de\u56de\u5df7\uff0c\u4f4f\u7684\u5168\u662f\u56de\u5b50\uff0c\u4ed6\u4eec\u975e\u5e38\u56e2\u7ed3\uff0c\u4e5f\u662f\u5f00\u5c01\u5e9c\u6cbb\u5b89\u6700\u597d\u7684\u5730\u65b9\uff0c\u4ece\u672a\u53d1\u751f \u8fc7\u62a2\u52ab\u7a83\u76d7\u7684\u6848\u5b50\u3002 \u3000\u3000\u71d5\u98de\u62ac\u5934\u671b\u671b\u90a3\u5ea7\u5927\u5b85\u9662\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u8fd9\u662f\u8d75\u4e8c\u5824\u4f4f\u7684\u5b85\u5b50\uff0c\u4ed6\u662f\u56de\u56de\u5df7\u7684\u5934\u5934\uff0c\u8fd9 \u91cc\u4f4f\u7684\u56de\u5b50\u90fd\u542c\u4ed6\u7684\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u55c5\u4e86\u4e00\u58f0\uff0c\u4e3e\u6b65\u5411\u524d\u884c\u53bb\uff0c\u4e00\u9762\u53c8\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u8fd9\u6761\u56de\u56de\u5df7\u6709\u591a\u5c11\u6237\u4eba\u5bb6\uff1f\u201d \u3000\u3000\u201c\u8fde\u540c\u53f3\u8fb9\u9a6c\u5bb6\u5df7\uff0c\u5927\u6982\u6709\u56db\u767e\u591a\u6237\uff0c\u5f88\u5927\u7684\u4e00\u80a1\u52bf\u529b\u3002\u201d \u3000\u3000\u201c\u8d75\u4e8c\u5824\u7684\u4e3a\u4eba\u5982\u4f55\u5462\uff1f\u201d \u3000\u3000\u201c\u5f88\u8c6a\u723d\uff0c\u4e5f\u5f88\u548c\u6c14\uff0c\u751f\u610f\u505a\u5f97\u5f88\u5927\uff0c\u5f00\u5c01\u5e9c\u5730\u9762\u4e0a\uff0c\u56de\u56de\u4eec\u53d1\u751f\u4e86\u4efb\u4f55\u4e89\u6267\uff0c\u8d75\u4e8c\u5824 \u4e00\u53e5\u8bdd\u5c31\u5168\u90fd\u6446\u5e73\u4e86\uff0c\u6ca1\u6709\u4e00\u4e2a\u56de\u56de\u4f1a\u53cd\u6297\u4ed6\uff0c\u5f53\u7136\uff0c\u4ed6\u4e5f\u4ed7\u4e49\u758f\u8d22\uff0c\u4efb\u4f55\u56de\u5b50\u6709\u56f0\u96be\u627e\u4e0a \u4ed6\uff0c\u4ed6\u51b3\u4e0d\u63a8\u8f9e\u2026\u2026\u201d \u3000\u3000\u987f\u4e86\u987f\uff0c\u71d5\u98de\u63a5\u7740\u56de\u7b54\u8bf4\uff1a\u201c\u5bf9\u5730\u65b9\u516c\u76ca\u4e5f\u5f88\u70ed\u5fc3\uff0c\u590f\u820d\u7c73\u7cae\uff0c\u51ac\u820d\u68c9\u8863\uff0c\u4e09\u5e74\u524d\uff0c\u9ec4 \u6cb3\u5c0f\u6e83\uff0c\u6df9\u4e86\u51e0\u5341\u4e2a\u6751\u5e84\uff0c\u8d75\u4e8c\u5824\u72ec\u81ea\u6350\u4e86\u4e09\u5343\u4e24\u94f6\u5b50\uff0c\u5728\u5f00\u5c01\u662f\u4f4d\u76f8\u5f53\u6709\u4eba\u671b\u7684\u540d\u4eba\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u4ed6\u505a\u7684\u662f\u4ec0\u4e48\u751f\u610f\uff1f\u201d \u3000\u3000\u201c\u542c\u8bf4\u662f\u73e0\u5b9d\u3001\u76ae\u8d27\uff0c\u8be6\u7ec6\u7684\u5c31\u4e0d\u592a\u6e05\u695a\u4e86\uff0c\u4e0d\u8fc7\uff0c\u4e00\u5e74\u4e2d\u5012\u6709\u5927\u534a\u5e74\u4e0d\u5728\u5bb6\u4e2d\u3002\u201d\u71d5 \u98de\u6c89\u541f\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u4e0d\u5bf9\u5417\uff1f\u201d \u3000\u3000\u201c\u5509\uff01\u5982\u82e5\u4e09\u516c\u4e3b\u8fd8\u7559\u5728\u5f00\u5c01\uff0c\u516b\u6210\u5c31\u5728\u8d75\u4e8c\u5824\u7684\u5bb6\u4e2d\uff0c\u81f3\u5c11\uff0c\u90a3\u4e24\u4e2a\u63b3\u6765\u516c\u4e3b\u7684\u4eba\uff0c \u524d\u5929\u591c\u91cc\u5148\u5230\u8fc7\u4e86\u8fd9\u91cc\uff0c\u56e0\u4e3a\u7ebf\u7d22\u81f3\u6b64\u5df2\u65ad\u3002\u201d \u3000\u3000\u201c\u4f1a\u6709\u8fd9\u79cd\u4e8b\u2026\u2026\u201d\u71d5\u98de\u6709\u4e9b\u4e0d\u4fe1\u5730\u8bf4\uff1a\u201c\u8d75\u4e8c\u5824\u5f88\u8bb2\u4e49\u6c14\uff0c\u4ed6\u548c\u9a6c\u5de1\u629a\u662f\u597d\u670b\u53cb\uff0c\u600e \u4f1a\u505a\u51fa\u8fd9\u7b49\u5927\u9006\u4e0d\u9053\u7684\u4e8b\u5462\uff1f\u201d \u3000\u3000\u201c\u71d5\u8001\u5f1f\uff0c\u4f60\u8bf4\u8d75\u4e8c\u5824\u548c\u9a6c\u5de1\u629a\u662f\u597d\u670b\u53cb\uff1f\u201d \u3000\u3000\u201c\u662f\u554a\uff01\u4ed6\u5e38\u5230\u5de1\u629a\u5e9c\u4e2d\u4f5c\u5ba2\u2026\u2026\u201d\u71d5\u98de\u7f13\u7f13\u5730\u9053\uff1a\u201c\u9a6c\u5de1\u629a\u4e5f\u5e38\u7559\u4ed6\u4fbf\u996d\uff0c\u4e00\u4e2a\u5de1\u629a \u5927\u4eba\u548c\u5e73\u6c11\u5982\u6b64\u4ea4\u5f80\uff0c\u7b97\u4e0d\u7b97\u662f\u597d\u670b\u53cb\u5462\uff1f\u201d \u3000\u3000\u201c\u7b97\uff0c\u800c\u4e14\u7b97\u662f\u975e\u5e38\u597d\u7684\u670b\u53cb\uff0c\u4f46\u9a6c\u5de1\u629a\u5e38\u5e38\u7559\u4ed6\u4fbf\u996d\uff0c\u662f\u4e0d\u662f\u4e0d\u592a\u65b9\u4fbf\u5462\uff1f\u201d\u6c5f\u5343\u91cc \u63d0\u51fa\u4e86\u5fc3\u4e2d\u7684\u7591\u95ee\u3002 \u3000\u3000\u71d5\u98de\u7b11\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u95ee\u9898\uff0c\u56e0\u4e3a\u9a6c\u5de1\u629a\u4e5f\u662f\u56de\u56de\u3002\u201d \u3000\u3000\u201c\u539f\u6765\u5982\u6b64\uff01\u201d\u6c5f\u5343\u91cc\u5fc3\u5934\u8df3\u52a8\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u738b\u91cd\u5c71\u63a5\u53e3\u8bf4\u9053\uff1a\u201c\u6c5f\u524d\u8f88\uff0c\u4e0d\u7528\u62c5\u5fc3\u9a6c\u5de1\u629a\uff0c\u53ea\u8981\u627e\u51fa\u4e09\u516c\u4e3b\u7684\u4e0b\u843d\uff0c\u9a6c\u5de1\u629a\u7edd\u4e0d\u6562 \u8892\u62a4\u8d75\u4e8c\u5824\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u662f\u627e\u4e0d\u5230\u5462\uff1f\u201d \u3000\u3000\u201c\u90a3\u4e5f\u6ca1\u6709\u4ec0\u4e48\uff0c\u9a6c\u5de1\u629a\u4e5f\u4e0d\u6562\u8d70\u54b1\u4eec\u4ec0\u4e48\u7f6a\u540d\uff01\u201d \u3000\u3000\u738b\u91cd\u5c71\u7b2c\u4e00\u6b21\u8868\u73b0\u51fa\u4e86\u5185\u5bab\u4f8d\u536b\u7684\u5f3a\u608d\uff0c\u679c\u7136\u662f\u6ca1\u6709\u628a\u4e00\u54c1\u5927\u5458\u7684\u5a01\u52bf\u653e\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u201c\u6211\u4e0d\u62c5\u5fc3\u54b1\u4eec\u4f1a\u72af\u4e86\u4ec0\u4e48\u7f6a\u540d\uff0c\u6211\u62c5\u5fc3\u7684\u662f\u4e00\u51fb\u4e0d\u4e2d\uff0c\u6253\u8349\u60ca\u86c7\uff0c\u4f1a\u63d0\u9ad8\u4ed6\u4eec\u7684\u8b66 \u89c9\uff0c\u4e5f\u8bb8\u4f1a\u903c\u4ed6\u4eec\u4e0b\u6bd2\u624b\u5bb3\u4e86\u4e09\u516c\u4e3b\u7684\u6027\u547d\u3002\u201d\u6c5f\u5e72\u91cc\u795e\u60c5\u6c89\u91cd\u5730\u8bf4\u3002 \u3000\u3000\u201c\u8fd9\u5c31\u4e25\u91cd\u4e86\uff01\u201d\u738b\u91cd\u5c71\u9053\uff1a\u201c\u5982\u679c\u516c\u4e3b\u88ab\u5bb3\uff0c\u4e8c\u53d4\u548c\u6211\u80af\u5b9a\u8981\u88ab\u5904\u6b7b\uff0c\u53ea\u6015\u8fd8\u4f1a\u7275\u8fde \u738b\u5bb6\u6570\u5341\u53e3\u4eba\u7684\u6027\u547d\uff0c\u771f\u662f\u4f34\u541b\u5982\u4f34\u864e\uff0c\u4f5c\u5b98\u8fd9\u6863\u4e8b\u4e00\u70b9\u4e5f\u4e0d\u597d\u73a9\u3002\u201d \u3000\u3000\u201c\u6c5f\u5144\uff0c\u8d75\u4e8c\u5824\u4e00\u5411\u5949\u516c\u5b88\u6cd5\uff0c\u8fd9\u4ef6\u4e8b\u7275\u8fde\u5230\u4ed6\u7684\u8eab\u4e0a\uff0c\u5b9e\u5728\u6709\u4e9b\u4e0d\u5927\u53ef\u80fd\uff0c\u4f46\u6211\u66f4\u76f8 \u4fe1\u6c5f\u5144\u7684\u8ffd\u8e2a\u4e4b\u672f\uff0c\u4f60\u770b\u4f1a\u4e0d\u4f1a\u662f\u53d7\u5230\u4ec0\u4e48\u4eba\u7684\u5229\u7528\uff1f\u201d\u71d5\u98de\u63d0\u51fa\u53e6\u4e00\u4e2a\u770b\u6cd5\u3002 \u3000\u3000\u201c\u4f60\u662f\u8bf4\uff0c\u8d75\u4e8c\u5824\u53d7\u5230\u4e86\u9a6c\u5de1\u629a\u7684\u5229\u7528\uff1f\u201d\u6c5f\u5e72\u91cc\u53cd\u6263\u4e00\u9876\u5927\u5e3d\u5b50\uff0c\u538b\u7684\u71d5\u98de\u5446\u4f4f\u4e86\u3002 \u3000\u3000\u201c\u6211\u2026\u2026\u6211\u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\u2026\u2026\u201d\u71d5\u98de\u7a81\u7136\u95f4\u53d8\u7684\u53e3\u5403\u8d77\u6765\uff1a\u201c\u6211\u662f\u8bf4\u2026\u2026\u8d75\u4e8c\u5824\u2026\u2026\u5e38 \u5e74\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\uff0c\u4f1a\u4e0d\u4f1a\u4ea4\u4e0a\u4e86\u4e00\u4e9b\u574f\u670b\u53cb\uff0c\u63b3\u4e86\u4e09\u516c\u4e3b\uff0c\u6682\u501f\u4f4f\u5728\u8d75\u4e8c\u5824\u7684\u5bb6\u4e2d\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\uff0c\u6211\u4eec\u8981\u771f\u6b63\u7684\u4ea4\u6362\u4e00\u4e0b\u610f\u89c1\u4e86\u2026\u2026\u201d\u6c5f\u5343\u91cc\u7ed5\u51fa\u5df7\u53e3\uff0c\u5728\u4e00\u7247\u7a7a\u65f7\u7684\u8349\u5730\u4e0a\u5750 \u4e86\u4e0b\u6765\u3002 \u3000\u3000\u71d5\u98de\u3001\u738b\u91cd\u5c71\u76f8\u5bf9\u800c\u5750\uff0c\u4e09\u4eba\u6210\u4e00\u4e2a\u54c1\u5b57\u5f62\uff0c\u53ef\u4ee5\u770b\u6e05\u695a\u56db\u5468\u4eba\u7269\u79fb\u52a8\u3002 \u3000\u3000\u201c\u9996\u5148\uff0c\u6211\u4eec\u8981\u786e\u5b9a\uff0c\u4ed6\u4eec\u63b3\u53bb\u4e09\u516c\u4e3b\u7684\u76ee\u7684\u4f55\u5728\uff1f\u8fd9\u4e0d\u662f\u4e00\u4ef6\u666e\u901a\u7684\u63b3\u4eba\u6848\u3002\u91cd\u5c71\uff0c \u4f60\u5148\u8bf4\uff0c\u4e09\u516c\u4e3b\u5728\u4eac\u91cc\u548c\u90a3\u4e9b\u8fd1\u81e3\u516c\u537f\u4eec\u8d70\u5f97\u6700\u8fd1\u3002\u201d \u3000\u3000\u201c\u6ca1\u6709\uff01\u6c5f\u524d\u8f88\uff0c\u4e09\u516c\u4e3b\u7684\u751f\u6d3b\u4e00\u76f4\u5f88\u5355\u7eaf\u3002\u201d\u738b\u91cd\u5c71\u56de\u7b54\u5f97\u5f88\u80af\u5b9a\uff0c\u9053\uff1a\u201c\u4e8c\u53d4\u6d3e\u6211 \u5e26\u4eba\u62a4\u536b\u65f6\uff0c\u628a\u4e09\u516c\u4e3b\u7684\u4e00\u5207\u90fd\u8bf4\u7684\u5f88\u6e05\u695a\uff0c\u5979\u5f88\u5c11\u548c\u5916\u4eba\u4ea4\u5f80\uff0c\u867d\u5f97\u7687\u4e0a\u5ba0\u7231\uff0c\u4f46\u5979\u672a\u6043 \u5ba0\u800c\u9a84\uff0c\u548c\u5144\u957f\u53ca\u59d0\u59d0\u90fd\u5904\u5f97\u5f88\u597d\uff0c\u4f46\u5374\u5f88\u5c11\u5f80\u6765\uff0c\u5979\u6709\u4e00\u5ea7\u72ec\u7acb\u7684\u5ead\u9662\uff0c\u5f88\u5c11\u5230\u522b\u5904\u8d70 \u52a8\uff0c\u662f\u5c5e\u4e8e\u5b64\u72ec\u4e00\u578b\u7684\u4eba\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5982\u679c\u4e0d\u6d89\u5165\u671d\u81e3\u4e92\u6597\uff0c\u4e89\u53d6\u6743\u4f4d\uff0c\u90a3\u5c31\u53ea\u6709\u4e24\u4e2a\u539f\u56e0\u4e86\u3002\u7b2c\u4e00\u662f \u4e09\u516c\u4e3b\u672c\u8eab\u7684\u79d8\u5bc6\uff1b\u7b2c\u4e8c\u662f\u7687\u4e0a\u5ba0\u7231\u5979\uff0c\u6709\u4eba\u8981\u631f\u5979\u4e3a\u8d28\uff0c\u5411\u7687\u4e0a\u8981\u6c42\u4ec0\u4e48\uff1f\u8fd9\u53ef\u80fd\u5c31\u7275\u626f \u5230\u56fd\u4e8b\u7eb7\u4e89\u7684\u5927\u4e8b\u4e86\u3002\u201d \u3000\u3000\u71d5\u98de\u9053\uff1a\u201c\u5148\u8bf4\u7b2c\u4e00\u4ef6\uff0c\u4e09\u516c\u4e3b\u5e7c\u5c45\u6df1\u5bab\uff0c\u7edd\u5c11\u79bb\u5f00\uff0c\u5979\u672c\u8eab\u4f1a\u6709\u4ec0\u4e48\u79d8\u5bc6\u5462\uff1f\u201d \u3000\u3000\u201c\u5509\uff01\u5979\u8eab\u5177\u6b66\u529f\uff0c\u5374\u9c9c\u4e3a\u4eba\u77e5\uff0c\u601c\u82b1\u5076\u4e00\u89c1\u4e4b\uff0c\u53c8\u88ab\u4e25\u5631\u4e0d\u5f97\u5916\u6cc4\uff0c\u53ea\u6b64\u4e00\u4e8b\uff0c\u5df2\u7ecf \u662f\u5145\u6ee1\u8be1\u5f02\uff0c\u4f55\u51b5\uff0c\u5979\u6084\u7136\u79bb\u4eac\uff0c\u8fdc\u884c\u5343\u91cc\uff0c\u5355\u662f\u8fd9\u4efd\u80c6\u6c14\uff0c\u4e5f\u4e0d\u662f\u4e00\u4e2a\u5e7c\u5c45\u6df1\u5bab\u7684\u5c0f\u59d1\u5a18 \u6240\u80fd\u6709\u7684\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u76ee\u5149\u8f6c\u6ce8\u5230\u738b\u91cd\u5c71\u7684\u8138\u4e0a\uff0c\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u8fd9\u4e00\u6b21\uff0c\u4f60\u4eec\u662f\u51c6\u5907\u5357\u4e0b\u4f55\u5904\u7684\uff1f\u201d \u3000\u3000\u738b\u91cd\u5c71\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u8bf4\u8d77\u6765\uff0c\u8fd8\u771f\u662f\u6709\u70b9\u795e\u79d8\uff0c\u4e00\u5207\u884c\u7a0b\u90fd\u7531\u5979\u5728\u8f66\u4e2d\u4f20\u8bdd\u51fa\u6765\uff0c \u6211\u4eec\u53ea\u662f\u8ddf\u7740\u9a6c\u8f66\u4e0a\u8def\uff0c\u5979\u8981\u5230\u54ea\u91cc\uff0c\u6211\u4eec\u662f\u5168\u4e0d\u77e5\u9053\uff0c\u5f53\u65f6\u8fd8\u4e0d\u89c9\u5f97\u600e\u6837\uff0c\u73b0\u5728\u56de\u60f3\u8d77\u6765 \u771f\u662f\u5947\u8be1\u83ab\u6d4b\u4e86\u3002\u201d \u3000\u3000\u201c\u4ee4\u53d4\u4e5f\u4e0d\u77e5\u9053\u4e48\uff1f\u201d \u3000\u3000\u201c\u597d\u50cf\u4e0d\u77e5\u9053\uff0c\u4e8c\u53d4\u53ea\u544a\u8bc9\u6211\u4e09\u516c\u4e3b\u8981\u8f7b\u9a91\u5357\u4e0b\uff0c\u8981\u6211\u968f\u884c\u4fdd \u3000\u3000\u62a4\uff0c\u975e\u5230\u4e07\u4e0d\u5f97\u5df2\uff0c\u4e0d\u5f97\u5411\u5b98\u65b9\u6c42\u63f4\u800c\u66b4\u9732\u8eab\u4efd\u3002\u201c \u3000\u3000\u201c\u8fd9\u4e48\u4e00\u8bf4\uff0c\u8fd8\u771f\u662f\u4e00\u4ef6\u79bb\u5947\u7684\u6848\u5b50\u3002\u201d\u71d5\u98de\u9053\uff1a\u201c\u90a3\u5c31\u5148\u67e5\u4e00\u4e0b\u8d75\u4e8c\u5824\u7684\u5b85\u9662\u518d\u8bf4 \u5427\uff01\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u6211\u539f\u60f3\u8e51\u8e2a\u8e29\u8ff9\uff0c\u8fd9\u4e00\u8ffd\uff0c\u975e\u8981\u6570\u767e\u91cc\u5f88\u96be\u505c\u4e0b\uff0c\u5374\u6ca1\u6599\u5230\uff0c\u6c34\u6d41\u5343\u91cc\uff0c \u6e90\u5934\u8fd1\u5728\u98d3\u5c3a\uff0c\u73b0\u5728\uff0c\u8981\u91cd\u65b0\u5e03\u7f6e\u4e00\u4e0b\u4e86\u3002\u201d \u3000\u3000\u71d5\u98de\u9053\uff1a\u201c\u6c5f\u5144\u5429\u5490\uff01\u201d \u3000\u3000\u201c\u9009\u51e0\u4e2a\u7cbe\u5e72\u6355\u5feb\uff0c\u8981\u4ed6\u4eec\u6539\u626e\u4e00\u4e0b\uff0c\u5e03\u5b88\u5728\u56de\u56de\u5df7\u7684\u56db\u5468\uff0c\u4ee5\u8d75\u4e8c\u5824\u5b85\u9662\u4f5c\u4e3a\u4e2d\u5fc3\uff0c \u4e25\u5bc6\u76d1\u89c6\uff0c\u51fa\u5165\u7684\u4eba\u4e00\u5f8b\u8bb0\u4e0b\uff0c\u968f\u65f6\u4f20\u62a5\u8fc7\u6765\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u6211\u8fd9\u5c31\u53bb\u8c03\u52a8\u4eba\u624b\u3002\u201d \u3000\u3000\u201c\u8981\u7edd\u5bf9\u79d8\u5bc6\uff0c\u8fde\u5f00\u5c01\u5e9c\u4e2d\u4eba\u90fd\u4e0d\u80fd\u6cc4\u6f0f\u3002\u201d\u6c5f\u5343\u91cc\u8bf4\uff1a\u201c\u5175\u5206\u4e24\u8def\uff0c\u518d\u9009\u4e00\u6279\u6355\u5feb\uff0c \u6539\u626e\u6210\u4f60\u3001\u6211\u3001\u91cd\u5c71\u7684\u6837\u5b50\uff0c\u5929\u8272\u4e00\u5165\u591c\uff0c\u9a91\u4e0a\u5feb\u9a6c\uff0c\u51fa\u4e1c\u95e8\u76f4\u5954\u5170\u5c01\uff0c\u7136\u540e\uff0c\u627e\u4e00\u5904\u9690\u5bc6 \u5730\u65b9\uff0c\u6362\u8863\u670d\u3001\u5378\u53bb\u9a6c\u978d\uff0c\u4e00\u628a\u706b\u70e7\u5149\uff0c\u628a\u9a6c\u5339\u4e5f\u5356\u4e86\uff0c\u518d\u626e\u6210\u519c\u6c11\uff0c\u6084\u7136\u56de\u57ce\u3002\u201d \u3000\u3000\u201c\u8fd9\u662f\u7591\u5175\u4e4b\u8ba1\uff0c\u6211\u660e\u767d\uff0c\u4f46\u591c\u51fa\u57ce\u5916\uff0c\u8d70\u4e2a\u5341\u51e0\u4e8c\u5341\u91cc\u5c31\u884c\u4e86\uff0c\u4e3a\u4ec0\u4e48\u8981\u76f4\u5954\u5170\u5c01 \u5462\uff1f\u201d \u3000\u3000\u201c\u4e0d\u884c\uff0c\u81f3\u5c11\u8981\u5954\u9a70\u767e\u91cc\u4e4b\u5916\uff0c\u5728\u5170\u5c01\u8fd8\u8981\u7559\u4e0b\u4e00\u4e9b\u75d5\u8ff9\uff0c\u518d\u4e0a\u9a6c\u8d76\u8def\uff0c\u5c94\u5165\u8352\u9053\uff0c\u4f9d \u8ba1\u711a\u978d\uff0c\u8981\u4ed6\u4eec\u591a\u52a0\u5c0f\u5fc3\uff0c\u5b88\u53e3\u5982\u74f6\uff0c\u4e0d\u53ef\u7559\u4e0b\u75d5\u8ff9\u3002\u201d \u3000\u3000\u71d5\u98de\u9053\uff1a\u201c\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u76d1\u89c6\u56de\u56de\u5df7\u7684\u4eba\uff0c\u6700\u597d\u8981\u751f\u9762\u5b54\uff0c\u8ba4\u8bc6\u4ed6\u7684\u4eba\u8d8a\u5c11\u8d8a\u597d\uff0c\u88c5\u626e\u4e5f\u8981\u7cbe\u7ec6\uff0c \u4e0d\u53ef\u5927\u610f\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u6c5f\u5144\u2026\u2026\u201d\u71d5\u98de\u63d0\u51fa\u4e86\u5fc3\u4e2d\u7684\u7591\u95ee\uff0c\u9053\uff1a\u201c\u770b\u6837\u5b50\uff0c\u4e0d\u4f1a\u7acb\u523b\u641c\u67e5\u8d75\u4e8c\u5824\u7684 \u5bb6\u5b85\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5982\u679c\u641c\u4e0d\u51fa\u4e09\u516c\u4e3b\uff0c\u71d5\u603b\u6355\u5934\u8981\u5982\u4f55\u4ea4\u4ee3\uff1f\u201d \u3000\u3000\u201c\u8bf4\u7684\u4e5f\u662f\uff0c\u8d75\u4e8c\u5824\u8981\u662f\u5411\u9a6c\u5de1\u629a\u8bf4\u4e00\u58f0\uff0c\u71d5\u67d0\u5c31\u5403\u4e0d\u5b8c\u515c\u7740\u8d70\u4e86\u3002\u201d \u3000\u3000\u201c\u6700\u91cd\u8981\u7684\u662f\u4e00\u6b21\u641c\u67e5\u4e0d\u51fa\u6765\uff0c\u4e0b\u4e00\u6b21\u5c31\u4e0d\u597d\u518d\u6765\u4e86\uff0c\u6240\u4ee5\uff0c\u8fd9\u4e00\u6b21\u8981\u7279\u522b\u614e\u91cd\u3002\u201d\u6c5f \u5343\u91cc\u770b\u770b\u5929\u8272\uff0c\u9053\uff1a\u201c\u71d5\u8001\u5f1f\uff0c\u53bb\u51c6\u5907\u5427\uff01 \u3000\u3000\u5343\u4e07\u5c0f\u5fc3\uff0c\u4e0d\u53ef\u9732\u51fa\u98ce\u58f0\uff0c\u5c24\u5176\u4e0d\u80fd\u8ba9\u8859\u95e8\u4e2d\u4eba\u77e5\u9053\u3002 \u3000\u3000\u201c\u6211\u660e\u767d\uff0c\u5f00\u5c01\u5e9c\u51e0\u5341\u4f4d\u6355\u5feb\uff0c\u4e00\u5411\u90fd\u7531\u6211\u638c\u63e1\u3001\u8c03\u6d3e\uff0c\u7528\u4e0d\u7740\u5411\u4eba\u8bf7\u793a\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u54b1\u4eec\u665a\u996d\u65f6\u523b\u5728\u53c8\u4e00\u6751\u89c1\uff0c\u4e0d\u8fc7\uff0c\u4f60\u8981\u6539\u88c5\u4e00\u4e0b\uff0c\u522b\u8ba9\u4eba\u77a7\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u6211\u6d3e\u4eba\u53bb\u5148\u8ba2\u4e00\u4e2a\u96c5\u5ba4\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u884c\u2026\u2026\u201d\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u54b1\u4eec\u5728\u697c\u4e0b\u53f3\u8fb9\u7684\u5927\u5385\u4e2d\u89c1\u3002\u201d \u3000\u3000\u53f3\u8fb9\u5927\u5385\u662f\u4e13\u95e8\u4f9b\u5e94\u8d29\u592b\u8d70\u5352\u5403\u996d\u7684\u5730\u65b9\uff0c\u4e0d\u77e5\u6c5f\u5e72\u91cc\u4e3a\u4ec0\u4e48\u8981\u9009\u62e9\u90a3\u4e2a\u5730\u65b9\uff0c\u4f46\u71d5\u98de \u5e76\u672a\u591a\u95ee\uff0c\u70b9\u70b9\u5934\uff0c\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u76ee\u5149\u51dd\u6ce8\u738b\u91cd\u5c71\u8eab\u4e0a\uff0c\u9053\uff1a\u201c\u91cd\u5c71\uff0c\u4f60\u51fa\u8eab\u6b66\u5f53\uff0c\u4e0d\u77e5\u9053\u5b66\u8fc7\u6f5c\u6728\u672f\u6ca1\u6709\uff1f\u201d \u3000\u3000\u738b\u91cd\u5c71\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u6b66\u5f53\u6d3e\u4e00\u79cd\u6700\u4e3a\u9690\u5bc6\u7684\u6280\u827a\uff0c\u6c5f\u524d\u8f88\u53c8\u600e\u4e48\u77e5\u6653\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u89c1\u8bc6\u8fc7\u90a3\u79cd\u6b66\u529f\uff0c\u786e\u5b9e\u5f88\u96be\u8ba9\u4eba\u53d1\u73b0\u3002\u201d\u6c5f\u5343\u91cc\u5fae\u7b11\u7740\u9053\uff1a\u201c\u5982\u662f\u738b\u8d24\u4f84\u5b66\u8fc7\u8fd9\u95e8 \u6280\u827a\uff0c\u4e5f\u8bb8\u54b1\u4eec\u53ef\u80fd\u4f1a\u5f88\u5feb\u5c31\u627e\u51fa\u4e09\u516c\u4e3b\u4e86\u3002\u201d \u3000\u3000\u201c\u5728\u4e0b\u662f\u5b66\u8fc7\uff0c\u800c\u4e14\u8fd8\u5c0f\u6709\u6210\u5c31\u2026\u2026\u201d \u3000\u3000\u201c\u597d\u6781\u4e86\uff01\u201d\u6c5f\u5343\u91cc\u62ac\u5934\u671b\u7740\u8d75\u4e8c\u5824\u5ead\u9662\u4e2d\u4e00\u68f5\u9ad8\u5927\u7684\u767d\u6768\u6811\uff0c\u9053\uff1a\u201c\u8d24\u4f84\u60f3\u529e\u6cd5\u6f5c\u9690 \u5728\u90a3\u68f5\u5927\u6811\u4e4b\u4e0a\u3002\u201d \u3000\u3000\u201c\u6b64\u523b\u662f\u6df1\u79cb\u5929\u6c14\uff0c\u6211\u8981\u9009\u62e9\u4e00\u4e9b\u548c\u6811\u6728\u989c\u8272\u76f8\u540c\u7684\u8863\u670d\u624d\u884c\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u8d24\u4f84\u5c31\u53bb\u51c6\u5907\u5427\uff01\u4eca\u591c\u56db\u66f4\u5929\uff0c\u54b1\u4eec\u5728\u6b64\u76f8\u89c1\uff0c\u6211\u5e2e\u52a9\u4f60\u6df7\u5165\u8d75\u5b85\uff0c\u73b0\u5728\uff0c\u65f6\u95f4 \u8fd8\u65e9\uff0c\u4f60\u81ea\u53bb\u627e\u4e2a\u5730\u65b9\u4f11\u606f\u4e00\u4e0b\uff0c\u4ee5\u4fbf\u591c\u95f4\u884c\u4e8b\u3002\u201d \u3000\u3000\u738b\u91cd\u5c71\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6c5f\u5144\uff0c\u6f5c\u6728\u672f\u5e76\u4e0d\u80fd\u7edd\u5bf9\u4e0d\u4f1a\u88ab\u4eba\u53d1\u73b0\uff0c\u4f55\u51b5\uff0c\u5728\u4e0b\u7684\u529f\u529b\u4e0d \u591f\uff0c\u6700\u591a\u53ea\u6709\u5341\u4e8c\u4e2a\u65f6\u8fb0\u7684\u652f\u6491\u80fd\u529b\u2026\u2026\u201d \u3000\u3000\u201c\u5982\u80fd\u591a\u4e00\u4e9b\u65f6\u95f4\u66f4\u597d\uff0c\u8d75\u4e8c\u5824\u6562\u52a8\u4e09\u516c\u4e3b\u7684\u8111\u7b4b\uff0c\u5fc5\u7136\u6709\u6240\u4ed7\u6043\uff0c\u4e0d\u80fd\u5c0f\u770b\u4e86\u4ed6\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u51dd\u91cd\u5730\u9053\uff1a\u201c\u5982\u679c\u6211\u63a8\u60f3\u7684\u4e0d\u9519\uff0c\u4ed6\u4eec\u665a\u4f1a\u5c3d\u91cf\u4fdd\u6301\u5e73\u9759\uff0c\u771f\u6b63\u7684\u884c\u52a8\u53ef\u80fd\u6539\u5728 \u767d\u5929\uff0c\u5b9e\u5728\u65e0\u6cd5\u652f\u6301\u4e0b\u53bb\uff0c\u660e\u665a\u4e0a\u5c31\u9000\u51fa\u6765\u5427\uff01\u201d \u3000\u3000\u738b\u91cd\u5c71\u9053\uff1a\u201c\u597d\u5427\uff01\u665a\u8f88\u5168\u529b\u4ee5\u8d74\u5c31\u662f\u3002\u201d \u3000\u3000\u6f5c\u6728\u672f\u5c31\u662f\u628a\u8eab\u5f62\u9690\u5728\u6811\u4e0a\uff0c\u7a7f\u7740\u548c\u6811\u6728\u989c\u8272\u76f8\u540c\u7684\u8863\u670d\uff0c\u4f9d\u9644\u5728\u6811\u8eab\u4e0a\u6216\u662f\u88c5\u4f5c\u6811\u7684 \u4e00\u90e8\u4efd\uff0c\u6280\u827a\u5230\u4e86\u67d0\u4e00\u79cd\u5883\u754c\uff0c\u80fd\u8d34\u5728\u6811\u4e0a\u5f62\u5982\u679d\u5e72\uff0c\u4e03\u516b\u5929\u4e0d\u52a8\u4e0d\u98df\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u8981\u738b\u91cd\u5c71\u81ea\u5df1\u53bb\u51c6\u5907\u8863\u670d\uff0c\u518d\u597d\u597d\u7684\u5750\u606f\u4e00\u9635\uff0c\u5230\u65f6\u95f4\u518d\u6765\u3002 \u3000\u3000\u738b\u91cd\u5c71\u81ea\u53bb\u51c6\u5907\uff0c\u6c5f\u5343\u91cc\u5374\u5229\u7528\u8fd9\u9635\u7a7a\u6863\uff0c\u6f5c\u5165\u4e86\u9ec4\u6cb3\u9556\u5c40\u3002 \u5367\u9f99\u751f\u300a\u71d5\u5b50\u4f20\u5947\u300b \u7b2c \u4e09 \u56de\u3000\u60ca\u4eba\u4e4b\u79d8 \u3000\u3000\u9ec4\u6cb3\u9556\u5c40\u603b\u9556\u5934\u674e\u9752\u7af9\u4f3c\u662f\u65e9\u5df2\u5728\u7b49\u7740\u6c5f\u5343\u91cc\uff0c\u4e00\u4e2a\u4eba\u5750\u5728\u5927\u5385\u5446\u5446\u51fa\u795e\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u64c5\u957f\u6f5c\u8e2a\u9690\u8ff9\u4e4b\u672f\uff0c\u6240\u4ee5\uff0c\u5f88\u5bb9\u6613\u5c31\u907f\u8fc7\u4e86\u9556\u5c40\u4e2d\u4eba\uff0c\u6478\u5165\u4e86\u5927\u5385\u3002 \u3000\u3000\u5bf9\u6c5f\u5343\u91cc\u7684\u9661\u7136\u51fa\u73b0\uff0c\u674e\u9752\u7af9\u5e76\u65e0\u8bb6\u5f02\u4e4b\u8272\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u51e0\u5929\uff0c\u6211\u4e00\u76f4\u5728\u606d\u5019 \u5927\u9a7e\uff0c\u4f60\u8981\u662f\u518d\u4e0d\u6765\uff0c\u6211\u5f97\u53bb\u627e\u4f60\u4e86\u3002\u201d \u3000\u3000\u201c\u7b49\u6211\uff1f\u90a3\u4e00\u5b9a\u662f\u6709\u91cd\u5927\u7684\u4e8b\u60c5\u544a\u8bc9\u6211\u4e86\uff1f\u201d \u3000\u3000\u5148\u66ff\u6c5f\u5343\u91cc\u5012\u4e86\u4e00\u676f\u8336\uff0c\u674e\u9752\u7af9\u9053\uff1a\u201c\u4f60\u5077\u5077\u6478\u6478\u7684\u8fdb\u6765\uff0c\u800c\u4e14\u8fd8\u7ecf\u8fc7\u4e86\u4e00\u756a\u6539\u626e\uff0c\u5fc5 \u7136\u662f\u6709\u5f88\u91cd\u8981\u7684\u4e8b\uff0c\u6211\u7b49\u4f60\uff0c\u5c31\u662f\u8981\u4f60\u6765\u95ee\u6211\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u8d75\u4e8c\u5824\u7684\u8eab\u624b\u5982\u4f55\uff1f\u201d\u6c5f\u5343\u91cc\u63d0\u51fa\u4e86\u7b2c\u4e00\u4e2a\u95ee\u9898\u3002 \u3000\u3000\u201c\u7b2c\u4e00\u6d41\u7684\u9ad8\u624b\uff0c\u5e94\u8be5\u548c\u4f60\u6c5f\u5144\u4e0d\u76f8\u4e0a\u4e0b\uff0c\u4f46\u5374\u662f\u85cf\u800c\u4e0d\u9732\uff0c\u5f00\u5c01\u5e9c\u77e5\u9053\u4ed6\u4f1a\u6b66\u529f\u7684\u4eba \u5e76\u4e0d\u591a\uff0c\u597d\u50cf\u2026\u2026\u201d\u7a81\u7136\u4f4f\u4e86\u53e3\u3002 \u3000\u3000\u201c\u597d\u50cf\u600e\u6837\uff1f\u65e2\u7136\u8bf4\u4e86\uff0c\u96be\u9053\u8fd8\u8981\u7559\u4e2a\u5c3e\u5df4\uff1f\u201d \u3000\u3000\u201c\u6b64\u4e8b\u5173\u7cfb\u91cd\u5927\uff0c\u674e\u67d0\u4e0d\u6562\u4e71\u8bf4\uff0c\u4f55\u51b5\uff0c\u8fd9\u4e2a\u4f20\u8bf4\u4e5f\u672a\u5fc5\u53ef\u9760\uff1f\u201d \u3000\u3000\u201c\u5c3d\u7ba1\u8bf7\u8bf4\uff0c\u6211\u5fc3\u4e2d\u81ea\u6709\u5206\u5bf8\uff0c\u4f60\u5df2\u4e8b\u5148\u8bf4\u660e\u4e86\uff0c\u5c31\u7b97\u65e5\u540e\u5f15\u51fa\u4ec0\u4e48\u9ebb\u70e6\uff0c\u6c5f\u67d0\u4eba\u4fdd\u8bc1 \u4e0d\u4f1a\u7275\u626f\u5230\u4f60\u674e\u603b\u9556\u5934\u3002\u201d \u3000\u3000\u674e\u9752\u7af9\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u9ec4\u6cb3\u9556\u5c40\u4e0b\u4e2a\u6708\u5c31\u76d8\u7ed9\u522b\u4eba\uff0c\u6211\u4e5f\u8981\u9000\u51fa\u6c5f\u6e56\u2026\u2026\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6c5f\u6e56\u4e0a\u98ce\u9669\u5927\u5927\uff0c\u6211\u6df7\u4e86\u4e8c\u5341\u5e74\u3002\u4e0d\u60f3\u518d\u6df7\u4e0b\u53bb\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u6c5f\u6e56\u5b50\u5f1f\u6c5f\u6e56\u8001\uff0c\u4f60\u60f3\u9000\u51fa\u53bb\uff0c\u53ea\u6015\u4e0d\u592a\u5bb9\u6613\u3002\u201d \u3000\u3000\u674e\u9752\u7af9\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u770b\u5230\u8fc7\u4e0d\u5c11\u91d1\u76c6\u6d17\u624b\uff0c\u9000\u51fa\u6c5f\u6e56\u7684\u4eba\u88ab\u4eba\u6740\u5bb3\uff0c\u6240\u4ee5\uff0c\u8fd9\u4e00 \u6b21\u6211\u65e9\u6709\u4e86\u51c6\u5907\uff0c\u674e\u9752\u7af9\u9000\u51fa\u6c5f\u6e56\uff0c\u5c31\u7b49\u4e8e\u6ca1\u6709\u674e\u9752\u7af9\u8fd9\u4e2a\u4eba\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u674e\u5144\u8981\u7ed9\u6c5f\u67d0\u4eba\u505a\u6b21\u4ea4\u6613\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u6211\u8981\u4f60\u7b54\u5e94\uff0c\u54b1\u4eec\u4eca\u65e5\u4e4b\u4f1a\u662f\u6700\u540e\u4e00\u9762\uff0c\u4eca\u540e\u6c38\u4e0d\u518d\u89c1\uff0c\u6211\u76f8\u4fe1\u9664\u4e86\u4f60\u6c5f\u5144\u8e29 \u8ff9\u8ffd\u8e2a\u4e4b\u672f\u5916\uff0c\u5929\u4e0b\u518d\u4e5f\u6ca1\u6709\u7b2c\u4e8c\u4e2a\u4eba\u80fd\u627e\u5230\u6211\u4e86\u3002\u201d \u3000\u3000\u201c\u674e\u5144\uff0c\u4f60\u51c6\u5907\u62ff\u4ec0\u4e48\u6765\u4ea4\u6362\u5462\uff1f\u201d \u3000\u3000\u201c\u5c3d\u5410\u80f8\u4e2d\u4e4b\u79d8\uff0c\u5305\u7ba1\u4f60\u542c\u5f97\u60ca\u5fc3\u52a8\u9b44\uff0c\u8001\u5b9e\u8bf4\uff0c\u5144\u5f1f\u5982\u679c\u6ca1\u6709\u9000\u51fa\u6c5f\u6e56\u7684\u6253\u7b97\uff0c\u5fc5\u7136 \u4f1a\u6709\u6240\u4fdd\u7559\uff0c\u6c5f\u5144\u4e5f\u65e0\u6cd5\u5c3d\u6089\u5185\u60c5\u3002\u201d \u3000\u3000\u539f\u6765\u674e\u9752\u7af9\u6b66\u529f\u5e73\u5e38\uff0c\u4f46\u4ed6\u6709\u4e00\u4e2a\u5f88\u96be\u53ca\u7684\u80fd\u529b\uff0c\u6d88\u606f\u7279\u522b\u7075\u901a\uff0c\u4e2d\u539f\u6570\u7701\u9ed1\u3001\u767d\u4e24\u9053 \u4e2d\u7684\u79d8\u8f9b\uff0c\u4e86\u5982\u6307\u638c\u3002 \u3000\u3000\u5f53\u7136\uff0c\u4ed6\u4e0b\u4e86\u5f88\u591a\u7684\u5de5\u592b\uff0c\u5e03\u7f6e\u4e86\u65e0\u6570\u7684\u6697\u6869\uff0c\u5e73\u5e38\u5c31\u6ce8\u610f\u5404\u79cd\u5f81\u8c61\uff0c\u91cd\u91d1\u6536\u8d2d\u6d88\u606f\uff0c \u9ec4\u6cb3\u9556\u5c40\u8d5a\u7684\u94b1\uff0c\u5927\u90e8\u4efd\u90fd\u82b1\u5728\u8fd9\u65b9\u9762\u3002 \u3000\u3000\u4f46\u4e5f\u51ed\u4ed7\u7740\u8fd9\u4e9b\u7075\u901a\u7684\u6d88\u606f\uff0c\u89e3\u53bb\u4e86\u9ec4\u6cb3\u9556\u5c40\u591a\u6b21\u7684\u5371\u96be\u3002 \u3000\u3000\u201c\u597d\uff0c\u6c5f\u67d0\u7b54\u5e94\uff0c\u4f46\u5e0c\u671b\u674e\u5144\u63d0\u4f9b\u51fa\u7684\u6d88\u606f\u5185\u60c5\uff0c\u786e\u4e5f\u80fd\u591f\u8ba9\u5144\u5f1f\u60ca\u5fc3\u52a8\u9b44\u3002\u201d \u3000\u3000\u674e\u9752\u7af9\u70b9\u70b9\u5934\u9053\uff1a\u201c\u5148\u8bf4\u8d75\u4e8c\u5824\uff0c\u4ed6\u6765\u81ea\u897f\u57df\uff0c\u5728\u5f00\u5c01\u4f4f\u4e86\u4e8c\u5341\u5e74\uff0c\u57f9\u517b\u4e86\u76f8\u5f53\u7684\u4eba \u671b\uff0c\u4f46\u5374\u6ca1\u6709\u4eba\u77e5\u9053\uff0c\u4ed6\u9886\u5bfc\u4e86\u4e00\u4e2a\u6c5f\u6e56\u7ec4\u7ec7\u2026\u2026\u201d \u3000\u3000\u201c\u4ec0\u4e48\u6837\u7684\u7ec4\u7ec7\uff1f\u201d \u3000\u3000\u201c\u4e0d\u50cf\u662f\u4e00\u822c\u6c5f\u6e56\u4e0a\u7684\u7ec4\u5408\uff0c\u548c\u6ce2\u65af\u3001\u897f\u57df\u90fd\u6709\u6765\u5f80\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u4e9b\u5e74\u6765\u5012\u6ca1\u6709\u505a\u8fc7\u4ec0\u4e48 \u574f\u4e8b\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u795e\u60c5\u4e25\u8083\u5730\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8d75\u4e8c\u5824\u548c\u9a6c\u5de1\u629a\u662f\u5f88\u597d\u7684\u670b\u53cb\uff1f\u201d \u3000\u3000\u674e\u9752\u7af9\u6c89\u601d\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u6211\u5f97\u5230\u7684\u6d88\u606f\uff0c\u4ed6\u4eec\u662f\u540c\u95e8\u5e08\u5144\u5f1f\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\uff1f\u2026\u2026\u201d\u6c5f\u5343\u91cc\u786e\u5b9e\u542c\u5f97\u60ca\u5fc3\u52a8\u9b44\u4e86\u3002\u201c \u3000\u3000\u201c\u8fd9\u662f\u957f\u7ebf\u2026\u2026\u201d\u674e\u9752\u7af9\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u653e\u4e8c\u5341\u51e0\u5e74\u7684\u957f\u4e1d\u3002\u201d \u3000\u3000\u201c\u9a6c\u5de1\u629a\u4e0d\u50cf\u662f\u4f1a\u6b66\u529f\u7684\u6837\u5b50\uff01\u201d\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u800c\u4e14\uff0c\u4ed6\u6ee1\u8179\u7ecf\u8bba\uff0c\u662f\u4e2a\u5f88\u6709\u5b66\u95ee\u7684 \u4eba\u3002\u201d \u3000\u3000\u201c\u5982\u82e5\u4e2d\u539f\u9053\u4e0a\u6709\u4e00\u4f4d\u771f\u6b63\u7684\u9ad8\u624b\u2026\u2026\u201d\u674e\u9752\u7af9\u795e\u60c5\u51b7\u8083\u5730\u9053\uff1a\u201c\u9a6c\u5de1\u629a\u5f53\u4e4b\u65e0\u6127 \u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u674e\u5144\u662f\u8bf4\uff0c\u9a6c\u5de1\u629a\u7684\u6b66\u529f\u5df2\u7ecf\u5230\u8fbe\u4e86\u795e\u6c14\u8fd8\u865a\u3001\u4e0d\u7740\u76ae\u76f8\u7684\u5883\u754c\u4e86\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u8bf4\u4e00\u53e5\u4e0d\u6015\u4f60\u6c5f\u5144\u751f\u6c14\u7684\u8bdd\uff0c\u5144\u5f1f\u8fd9\u4e8c\u5341\u51e0\u5e74\u6c5f\u6e56\u751f\u6daf\u4e2d\uff0c\u89c1\u5230\u4e86\u4e0d\u5c11\u9ad8\u624b\uff0c\u9a6c \u5de1\u629a\u662f\u5144\u5f1f\u89c1\u5230\u7684\u7b2c\u4e00\u9ad8\u4eba\u3002\u201d \u3000\u3000\u201c\u8fd9\u2026\u2026\u201d\u6c5f\u5343\u91cc\u771f\u7684\u5446\u4f4f\u4e86\uff1a\u201c\u8fd9\u6d88\u606f\u4e0d\u4f1a\u9519\u5427\uff1f\u201d \u3000\u3000\u674e\u9752\u7af9\u6ca1\u6709\u6b63\u9762\u56de\u7b54\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6c5f\u5144\uff0c\u53ef\u4ee5\u53bb\u770b\u770b\u97e9\u6d9b\uff0c\u4ed6\u4e0d\u662f\u751f\u75c5\uff0c\u800c\u662f\u88ab \u4e00\u79cd\u5947\u602a\u7684\u70b9\u7a74\u672f\u5236\u4f4f\u4e86\u7a74\u9053\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u542c\u5f97\u5fc3\u5934\u5927\u9707\uff0c\u6697\u9053\uff1a\u5e78\u597d\u6ca1\u6709\u5e26\u738b\u91cd\u5c71\u548c\u71d5\u98de\u540c\u6765\uff0c\u5982\u662f\u4e24\u4eba\u540c\u6765\uff0c\u53ea\u6015\u4f1a\u5413 \u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u201c\u674e\u5144\uff0c\u8fd8\u6709\u4ec0\u4e48\u6307\u6559\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u7a81\u7136\u5ba2\u6c14\u8d77\u6765\uff0c\u5bf9\u7740\u674e\u9752\u7af9\u6d41\u9732\u51fa\u65e0\u9650\u7684\u656c\u91cd\uff0c\u4ed6\u6cc4\u6f0f\u51fa\u5982\u6b64\u91cd\u5927\u7684\u79d8\u5bc6\uff0c\u90a3\u662f \u975e\u8d70\u4e0d\u53ef\u4e86\u3002 \u3000\u3000\u674e\u9752\u7af9\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u9a6c\u5de1\u629a\u4e0d\u77e5\u4e0b\u4e86\u591a\u5c11\u5de5\u592b\uff0c\u82b1\u4e86\u591a\u5c11\u5fc3\u8840\uff0c\u624d\u6709\u4eca\u5929\u8fd9\u4efd\u4e00\u54c1 \u5927\u540f\u7684\u5730\u4f4d\uff0c\u5982\u679c\u4e0d\u662f\u903c\u5f97\u592a\u7d27\uff0c\u6211\u76f8\u4fe1\u4ed6\u4e0d\u4f1a\u66b4\u9732\u8eab\u4efd\u2026\u2026\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u5728\u4e0b\u4e0d\u660e\u767d\uff0c\u6211\u4f1a\u8c28\u614e\u4ece\u4e8b\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u80fd\u5e2e\u52a9\u97e9\u6d9b\uff0c\u60f3\u529e\u6cd5\u89e3\u53bb\u4ed6\u7684\u75db\u82e6\uff0c\u751f\u9f99\u6d3b\u864e\u822c\u7684\u4e00\u6761\u6c49\u5b50\uff0c\u73b0\u5728\u53ea\u6709\u8eba\u5728\u5e8a\u4e0a \u7684\u4efd\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u4f1a\u5c3d\u529b\u8bd5\u8bd5\uff0c\u674e\u5144\uff0c\u8fd8\u6709\u4ec0\u4e48\u6307\u70b9\u2026\u2026\u201d \u3000\u3000\u201c\u6c5f\u5144\u8a00\u91cd\u4e86\uff0c\u4e2d\u539f\u6b66\u6797\u9053\u4e0a\uff0c\u4e00\u5207\u90fd\u8fd8\u5e73\u9759\uff0c\u9a6c\u5de1\u629a\u7b97\u662f\u4e00\u4e2a\u597d\u5b98\uff0c\u5728\u6c11\u95f4\u7684\u58f0\u671b\u4e0d \u9519\uff0c\u770b\u6837\u5b50\uff0c\u597d\u50cf\u8fd8\u6709\u66f4\u4e0a\u4e00\u5c42\u697c\u7684\u6253\u7b97\uff0c\u4ec0\u4e48\u76ee\u7684\uff0c\u6211\u5c31\u4e0d\u4fbf\u731c\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u6697\u6697\u5fd6\u9053\uff1a\u770b\u6837\u5b50\uff0c\u4ed6\u8fd8\u4e0d\u77e5\u9053\u4e09\u516c\u4e3b\u88ab\u63b3\u8d70\u7684\u4e8b\uff0c\u4e09\u516c\u4e3b\u8fd9\u8d9f\u79bb\u4eac\u5012\u4e5f\u662f\u76f8\u5f53 \u79d8\u5bc6\u4e86\uff0c\u4f46\u4ecd\u88ab\u8d75\u4e8c\u5824\u77e5\u9053\u4e86\uff0c\u662f\u4e0d\u662f\u9a6c\u5de1\u629a\u901a\u7684\u6d88\u606f\u5462\uff1f \u3000\u3000\u8f9e\u51fa\u4e86\u9ec4\u6cb3\u9556\u5c40\uff0c\u6c5f\u5343\u91cc\u771f\u7684\u832b\u7136\u4e86\uff0c\u8ffd\u89c5\u4eba\u7684\u884c\u8e2a\uff0c\u4ed6\u662f\u5929\u4e0b\u7b2c\u4e00\u9ad8\u624b\uff0c\u4f46\u4ed6\u5374\u65e0\u80fd \u5904\u7406\u773c\u524d\u8fd9\u7b49\u8be1\u5947\u8ff7\u79bb\u7684\u5c40\u52bf\u3002 \u3000\u3000\u5982\u679c\u9a6c\u6587\u4e2d\u548c\u8d75\u4e8c\u5824\u771f\u7684\u662f\u5e08\u5144\u5f1f\uff0c\u8d75\u4e8c\u5824\u63b3\u8d70\u516c\u4e3b\u7684\u4e8b\uff0c\u9a6c\u6587\u4e2d\u5c82\u4f1a\u4e0d\u77e5\uff0c\u4f46\u53c8\u4e3a\u4ec0 \u4e48\u4e0d\u60dc\u91cd\u91d1\u8bf7\u6258\uff0c\u8981\u6211\u6c5f\u67d0\u4eba\u8ffd\u67e5\u4e09\u516c\u4e3b\u7684\u4e0b\u843d\u5462\uff1f \u3000\u3000\u96be\u9053\u4ed6\u4e0d\u76f8\u4fe1\u6211\u6c5f\u5343\u91cc\u7684\u8ffd\u8e2a\u6280\u672f\u51a0\u7edd\u6b66\u6797\uff0c\u6545\u610f\u6765\u8bd5\u4e00\u8bd5\uff1f \u3000\u3000\u6216\u662f\u6545\u4f5c\u59ff\u6001\uff0c\u7559\u4e0b\u5bf9\u4e0a\u67e5\u95ee\u65f6\u7684\u9000\u6b65\uff1f \u3000\u3000\u96be\u9053\u662f\u8d75\u4e8c\u5824\u79c1\u81ea\u884c\u52a8\uff0c\u9a6c\u5de1\u629a\u4e5f\u88ab\u8499\u5728\u9f13\u4e2d\uff1f \u3000\u3000\u4e5f\u8bb8\u662f\u674e\u9752\u7af9\u7684\u6d88\u606f\u9519\u4e86\uff0c\u9a6c\u5de1\u629a\u548c\u8d75\u4e8c\u5824\u53ea\u662f\u666e\u901a\u670b\u53cb\uff1f \u3000\u3000\u6211\u6c5f\u5e72\u91cc\u5e94\u8be5\u600e\u4e48\u505a\u5462\uff1f \u3000\u3000\u628a\u5185\u60c5\u544a\u8bc9\u738b\u91cd\u5c71\uff0c\u8981\u4ed6\u98de\u62a5\u56de\u4eac\uff0c\u8bf7\u65e8\u5b9a\u593a\u3002\u6216\u662f\uff0c\u53ea\u628a\u4e09\u516c\u4e3b\u6551\u51fa\u6765\uff0c\u7136\u540e\u5e26\u7740\u5c0f \u71d5\u5b50\u79bb\u5f00\u8fd9\u91cc\u2026\u2026 \u3000\u3000\u60f3\u5230\u5c0f\u71d5\u5b50\uff0c\u6c5f\u5343\u91cc\u7acb\u523b\u53c8\u591a\u4e86\u4e00\u91cd\u5fc3\u4e8b\uff0c\u5982\u679c\u9a6c\u5de1\u629a\u771f\u662f\u4e00\u4f4d\u7edd\u9876\u9ad8\u4eba\uff0c\u7edd\u4e0d\u4f1a\u653e\u8fc7 \u5c0f\u71d5\u5b50\u90a3\u6837\u7684\u5947\u4f73\u8d44\u8d28\u2026\u2026 \u3000\u3000\u7ecf\u5386\u8fc7\u5927\u98ce\u5927\u6d6a\u7684\u6c5f\u5343\u91cc\uff0c\u7adf\u7136\u88ab\u56f0\u5f97\u6ca1\u6709\u4e3b\u610f\u4e86\uff0c\u4ed6\u4ece\u6ca1\u6709\u9047\u5230\u8fc7\u8fd9\u79cd\u4ea6\u654c\u4ea6\u53cb\u7684\u95ee \u9898\uff1f\u4e00\u9762\u662f\u6c5f\u6e56\u9ad8\u624b\uff0c\u4e00\u9762\u662f\u624b\u63e1\u5927\u6743\u7684\u5b98\u5e9c\u52bf\u529b\uff0c\u4e00\u6b65\u5931\u9519\uff0c\u4e0d\u4f46\u80fd\u5f15\u8d77\u6740\u8eab\u4e4b\u7978\uff0c\u53ef\u80fd \u8fd8\u8981\u682a\u8fde\u6570\u5341\u767e\u6761\u4eba\u547d\u2026\u2026 \u3000\u3000\u5750\u5728\u5927\u76f8\u56fd\u5bfa\u5916\u7684\u4e00\u6761\u77f3\u9636\u4e0a\uff0c\u770b\u7740\u7edc\u7ece\u4e0d\u7edd\u7684\u4eba\u7fa4\uff0c\u6765\u5f80\u5728\u5404\u79cd\u4e0d\u540c\u7684\u644a\u4f4d\u4e0a\uff0c\u5356\u4e1c \u897f\u7684\u543c\u53eb\u58f0\uff0c\u95f9\u9152\u7684\u731c\u62f3\u58f0\uff0c\u5b69\u5b50\u4eec\u7684\u5b09\u7b11\u58f0\uff0c\u6784\u6210\u4e00\u79cd\u6df7\u4e71\u7684\u70ed\u95f9\uff0c\u4f60\u5982\u679c\u662f\u7231\u6e05\u9759\u7684 \u4eba\uff0c\u8fd9\u5730\u65b9\u5f88\u5feb\u4f1a\u628a\u4f60\u95f9\u51fa\u795e\u7ecf\u75c5\u6765\u3002 \u3000\u3000\u4f46\u8fd9\u4e2a\u5730\u65b9\u5374\u662f\u5b89\u5168\u7684\u3002 \u3000\u3000\u662f\u7684\uff0c\u8c01\u4f1a\u60f3\u5230\u540d\u52a8\u6c5f\u6e56\u7684\u6c5f\u5927\u4fa0\uff0c\u7a7f\u7740\u84dd\u5e03\u88e4\u8902\uff0c\u5750\u5728\u76f8\u56fd\u5bfa\u5916\u7684\u77f3\u9636\u4e0a\u770b\u70ed\u95f9\u5462\uff1f \u3000\u3000\u6c5f\u5343\u91cc\u53e6\u5916\u4e00\u4e2a\u7528\u5fc3\u662f\uff0c\u5e0c\u671b\u78b0\u5230\u4e10\u5e2e\u4e2d\u4eba\uff0c\u4ed6\u5df2\u611f\u89c9\u5230\u4e8b\u5173\u91cd\u5927\uff0c\u5df2\u4e0d\u662f\u4ed6\u6c5f\u5343\u91cc\u4e00 \u4e2a\u4eba\u7684\u529b\u91cf\u80fd\u591f\u62f1\u7684\u8d77\u6765\u7684\u3002 \u3000\u3000\u4ed6\u9700\u8981\u5e2e\u624b\uff0c\u7b2c\u4e00\u6d41\u7684\u5e2e\u624b\u3002 \u3000\u3000\u4f55\u51b5\uff0c\u71d5\u98de\u7684\u529b\u91cf\u5df2\u4e0d\u53ef\u6043\uff0c\u9a6c\u5de1\u629a\u968f\u4fbf\u627e\u4e2a\u7406\u7531\uff0c\u5c31\u53ef\u4ee5\u628a\u71d5\u98de\u62bd\u8c03\u5230\u6570\u767e\u91cc\u5916 \u53bb\u2026\u2026 \u3000\u3000\u5947\u602a\uff01\u7ecf\u5e38\u51fa\u6ca1\u5728\u76f8\u56fd\u5bfa\u5916\u7684\u4e10\u5e2e\u5f1f\u5b50\uff0c\u5ffd\u7136\u95f4\u90fd\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u5915\u9633\u5c06\u5c3d\uff0c\u76f8\u56fd\u5bfa\u5916\u591c\u5e02\u5df2\u5f00\uff0c\u767e\u4e1a\u6742\u9648\u7684\u644a\u4f4d\u4e0a\uff0c\u5df2\u5f00\u59cb\u70b9\u4e0a\u706f\u706b\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u7f13\u7f13\u7ad9\u8d77\uff0c\u5df2\u6697\u4f5c\u51b3\u5b9a\uff0c\u5148\u628a\u5185\u60c5\u544a\u8bc9\u71d5\u98de\uff0c\u81f3\u5c11\uff0c\u71d5\u98de\u6bd4\u4ed6\u66f4\u4e86\u89e3\u5b98\u573a\u4e2d\u7684\u60c5 \u5f62\u3002 \u3000\u3000\u71d5\u98de\u65e9\u5df2\u5728\u53c8\u4e00\u6751\u7684\u53f3\u5927\u5385\u4e2d\u7b49\u5019\uff0c\u4ed6\u867d\u7136\u66f4\u8863\u6539\u626e\uff0c\u4e5f\u7b80\u5355\u7684\u53d8\u4e86\u5f62\u8c8c\uff0c\u4f46\u6c5f\u5e72\u91cc\u4ecd \u80fd\u4e00\u773c\u5c31\u77a7\u51fa\u6765\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u8d70\u4e86\u8fc7\u53bb\uff0c\u5728\u71d5\u98de\u7684\u5bf9\u9762\u5750\u4e0b\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u71d5\u8001\u5f1f\uff0c\u4f60\u542c\u7740\uff0c\u4e8b\u60c5\u6709\u4e86\u5f88\u5927\u7684\u53d8 \u5316\uff0c\u54b1\u4eec\u5f97\u597d\u597d\u5546\u91cf\u4e00\u4e0b\u4e86\u3002\u201d \u3000\u3000\u8fd9\u5730\u65b9\u4eba\u58f0\u5435\u6742\uff0c\u867d\u5df2\u662f\u6df1\u79cb\u5929\u6c14\uff0c\u4ecd\u53ef\u95fb\u5230\u6c57\u81ed\u7684\u5473\u9053\uff0c\u6765\u8fd9\u91cc\u5403\u996d\u7684\u4eba\u5927\u90fd\u662f\u53eb\u4e9b \u5c0f\u83dc\u4e4b\u7c7b\u7684\u4e1c\u897f\uff0c\u7136\u800c\u731c\u62f3\u7684\u58f0\u97f3\u5374\u5927\u5f97\u60ca\u4eba\uff0c\u6bd4\u76f8\u56fd\u5bfa\u5916\u7684\u5435\u95f9\u58f0\uff0c\u6beb\u4e0d\u900a\u8272\u3002 \u3000\u3000\u71d5\u98de\u76b1\u76b1\u7709\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u6362\u4e2a\u5730\u65b9\u8c08\u5427\uff01\u201d \u3000\u3000\u6c5f\u5343\u91cc\u5fc3\u4e2d\u4e00\u52a8\uff0c\u9053\uff1a\u201c\u597d\uff01\u5230\u4f60\u5bb6\u91cc\u53bb\uff0c\u4e0d\u8fc7\u884c\u52a8\u8981\u8c28\u614e\uff0c\u522b\u8ba9\u4eba\u53d1\u89c9\u4e86\u3002\u201d \u3000\u3000\u71d5\u98de\u867d\u7136\u6ca1\u6709\u51fa\u8a00\u53cd\u9a73\uff0c\u4f46\u5fc3\u4e2d\u5374\u662f\u5927\u4e0d\u4e3a\u7136\uff0c\u8fd9\u91cc\u662f\u5f00\u5c01\u5e9c\u554a\uff01\u71d5\u98de\u7ba1\u7406\u7684\u5730\u76d8\uff0c\u6c5f \u5343\u91cc\u7684\u5c0f\u5fc3\u672a\u514d\u662f\u592a\u8fc7\u4efd\u4e86\uff0c\u597d\u50cf\u968f\u65f6\u90fd\u53ef\u80fd\u9047\u4e0a\u5371\u9669\u4f3c\u7684\u3002 \u3000\u3000\u591c\u5e55\u4f4e\u5782\uff0c\u5f00\u5c01\u5e9c\u5df2\u70b9\u8d77\u4e07\u5bb6\u706f\u706b\u3002 \u3000\u3000\u4e24\u4eba\u6ca1\u6709\u53eb\u95e8\uff0c\u8d8a\u5899\u800c\u5165\uff0c\u8eab\u5b50\u8fd8\u672a\u505c\u7a33\uff0c\u56db\u70b9\u5bd2\u661f\u5982\u7535\uff0c\u5206\u53d6\u4e24\u4eba\u8981\u7a74\u3002 \u3000\u3000\u71d5\u98de\u5403\u4e86\u4e00\u60ca\uff0c\u6025\u6025\u4ef0\u8eab\u8ba9\u907f\uff0c\u5bd2\u661f\u63a0\u9762\u800c\u8fc7\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u5374\u4e00\u4f38\u53cc\u624b\uff0c\u5404\u63a5\u4f4f\u4e86\u4e00\u679a\u5236\u94b1\uff0c\u5fd6\u9053\uff1a\u597d\u5c0f\u5b50\uff0c\u679c\u7136\u662f\u8fdb\u6b65\u795e\u901f\uff0c\u4f20\u4ed6\u6697\u5668\u4e0d \u8fc7\u624d\u4e00\u4e2a\u6708\u7684\u5de5\u592b\uff0c\u5374\u5df2\u80fd\u8fd0\u7528\u7eaf\u719f\uff0c\u4e00\u624b\u53d1\u51fa\u56db\u679a\u5236\u94b1\u3002 \u3000\u3000\u5fc3\u5ff5\u8f6c\u52a8\u95f4\uff0c\u4eba\u5f71\u4e00\u95ea\uff0c\u5df2\u75be\u6251\u800c\u81f3\uff0c\u8fce\u9762\u4e00\u5251\uff0c\u523a\u5411\u7709\u5fc3\u3002 \u3000\u3000\u201c\u6d41\u661f\u8d76\u6708\u201d\u6b63\u662f\u6c5f\u5343\u91cc\u7684\u7edd\u62db\u4e4b\u4e00\uff0c\u5251\u52bf\u4e4b\u5feb\uff0c\u5df2\u795e\u4f3c\u6c5f\u5343\u91cc\u5341\u4e4b\u4e03\u516b\u4e86\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u53f3\u624b\u4e2d\u5236\u94b1\u75be\u626c\uff0c\u4e00\u9635\u91d1\u94c1\u4ea4\u9e23\uff0c\u5c01\u5f00\u5251\u52bf\uff0c\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u679c\u7136\u662f\u4e00\u65e5\u5343\u91cc\u7684 \u8fdb\u5883\u3002\u201d \u3000\u3000\u201c\u6c5f\u4f2f\u4f2f\u2026\u2026\u201d\u6765\u4eba\u5df2\u542c\u51fa\u662f\u6c5f\u5343\u91cc\u7684\u58f0\u97f3\uff0c\u6536\u5251\u75be\u9000\u3002 \u3000\u3000\u662f\u5c0f\u71d5\u5b50\u3002\u4ed6\u624b\u4e2d\u957f\u5251\u6a2a\u80f8\uff0c\u76ee\u5149\u8f6c\u6ce8\u5728\u71d5\u98de\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u679c\u7136\u662f\u51b7\u9759\u975e\u51e1\uff0c\u6c5f\u5343\u91cc\u4f20\u4e86\u4ed6\u6b66\u529f\uff0c\u4e5f\u4f20\u4e86\u4ed6\u5bf9\u654c\u7684\u7ecf\u9a8c\uff0c\u5df2\u77e5\u662f\u6c5f\u5343\u91cc\u4e86\uff0c\u4f46\u4ecd\u4e0d \u653e\u677e\u5bf9\u53e6\u4e00\u4e2a\u4eba\u7684\u6212\u5907\u3002 \u3000\u3000\u71d5\u98de\u770b\u5446\u4e86\uff0c\u521a\u624d\u513f\u5b50\u9732\u7684\u4e00\u624b\uff0c\u6697\u5668\u3001\u8f7b\u529f\u3001\u5251\u62db\uff0c\u4e09\u6837\u4f3c\u5df2\u4e0d\u5728\u4ed6\u4e4b\u4e0b\u3002 \u3000\u3000\u5ba4\u4e2d\u4f20\u6765\u4e86\u4e00\u4e2a\u5973\u4eba\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u5c0f\u71d5\u5b50\uff0c\u662f\u8c01\u554a\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6123\u4e86\u4e00\u4e0b\uff0c\u4e0d\u77e5\u5982\u4f55\u56de\u7b54\uff0c\u5a18\u6ca1\u89c1\u8fc7\u6c5f\u4f2f\u4f2f\uff0c\u90a3\u4e2a\u4eba\u4ed6\u53c8\u4e0d\u8ba4\u8bc6\u3002 \u3000\u3000\u5e78\u597d\u71d5\u98de\u63a5\u4e0a\u4e86\u53e3\uff0c\u9053\uff1a\u201c\u662f\u6211\uff0c\u6dd1\u8d1e\uff01\u53bb\u53a8\u623f\u5f04\u4e24\u4e2a\u83dc\uff0c\u70eb\u58f6\u9152\uff0c\u9001\u5230\u6211\u4e66\u623f\u53bb\uff0c\u6211 \u4eec\u6709\u8981\u4e8b\u5546\u91cf\u3002\u201d \u3000\u3000\u623f\u4e2d\u5973\u4eba\u5e94\u4e86\u4e00\u58f0\uff0c\u672a\u518d\u591a\u95ee\uff0c\u81ea\u53bb\u51c6\u5907\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u5374\u4e00\u4e0b\u6251\u5230\u71d5\u98de\u6000\u4e2d\uff0c\u9053\uff1a\u201c\u7239\u5440\uff01\u4f60\u600e\u4e48\u8fd9\u8eab\u6253\u626e\uff0c\u71d5\u513f\u90fd\u4e0d\u8ba4\u8bc6\u4e86\u3002\u201d \u3000\u3000\u63a8\u5f00\u5c0f\u71d5\u5b50\uff0c\u71d5\u98de\u9053\uff1a\u201c\u5341\u4e94\u5c81\u4e86\u8fd8\u8981\u7f20\u4eba\uff0c\u5feb\u53bb\u7ed9\u6c5f\u4f2f\u4f2f\u89c1\u793c\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u7a81\u7136\u5f03\u53bb\u957f\u5251\uff0c\u8dea\u4e86\u4e0b\u53bb\uff0c\u606d\u606d\u656c\u656c\u7684\u7ed9\u6c5f\u5343\u91cc\u53e9\u4e86\u4e09\u4e2a\u5934\uff0c\u9053\uff1a\u201c\u6c5f\u4f2f\u4f2f\uff0c\u4f60 \u4e00\u76f4\u4e0d\u80af\u628a\u6211\u6536\u5165\u95e8\u4e0b\uff0c\u73b0\u5728\uff0c\u4f60\u548c\u6211\u7239\u4e5f\u8ba4\u8bc6\u4e86\uff0c\u5f53\u7239\u4e4b\u9762\uff0c\u8bf7\u4f60\u7b54\u5e94\u6536\u71d5\u513f\u5165\u4f60\u95e8\u4e0b\uff0c \u71d5\u513f\u4f1a\u597d\u597d\u5b5d\u987a\u4f60\u7684\u3002\u201d \u3000\u3000\u4ed6\u4eba\u4e0d\u4f46\u806a\u660e\uff0c\u5634\u513f\u66f4\u751c\uff0c\u867d\u662f\u5f88\u5e84\u4e25\u7684\u8981\u6c42\uff0c\u5728\u4ed6\u53e3\u4e2d\u4e5f\u8bf4\u7684\u5145\u6ee1\u7740\u60c5\u610f\uff0c\u5341\u5206\u52a8 \u4eba\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u5374\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6c5f\u4f2f\u4f2f\u4e5f\u60f3\u5f97\u8981\u547d\u554a\uff01\u53ef\u662f\uff0c\u5c0f\u71d5\u5b50\uff0c\u6c5f\u4f2f\u4f2f\u4e0d\u80fd\u5bb3\u4f60\uff0c\u6536\u4e86 \u4f60\u505a\u5f92\u5f1f\u5c31\u7b49\u4e8e\u62bd\u6389\u4e00\u6839\u652f\u6301\u5927\u53a6\u7684\u67f1\u5b50\uff0c\u53bb\u505a\u4e86\u4e8c\u67b6\u6881\uff0c\u4e0d\u4f46\u662f\u5927\u6750\u5c0f\u7528\uff0c\u7b80\u76f4\u662f\u66b4\u6b84\u5929 \u7269\uff0c\u8fd9\u4e09\u4e2a\u5934\u4f60\u7b97\u662f\u767d\u78d5\u4e86\u3002\u201d\u4f38\u51fa\u624b\u633d\u8d77\u4e86\u5c0f\u71d5\u5b50\u3002 \u3000\u3000\u4ed6\u51b0\u96ea\u806a\u660e\uff0c\u5fc3\u4e2d\u660e\u767d\uff0c\u8fd9\u4e9b\u8bdd\u662f\u5f26\u5916\u4e4b\u97f3\uff0c\u662f\u8bf4\u7ed9\u71d5\u98de\u542c\u7684\u3002 \u3000\u3000\u679c\u7136\uff0c\u71d5\u98de\u4e5f\u6709\u4e86\u53cd\u5e94\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u6c5f\u5144\uff0c\u8fd9\u4ef6\u4e8b\u71d5\u67d0\u4e0d\u4f1a\u53d8\u5366\uff0c\u54b1\u4eec\u5230\u4e66\u623f\u53bb\u518d\u4ed4\u7ec6 \u7684\u8c08\u3002\u201d \u3000\u3000\u6c5f\u5e72\u91cc\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5c0f\u71d5\u5b50\uff0c\u5230\u5c4b\u9876\u4e0a\u4f0f\u7740\uff0c\u53d1\u73b0\u6709\u53ef\u7591\u7684\u4eba\uff0c\u7acb\u523b\u62db\u547c\u6211\u4eec\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u5e94\u4e86\u4e00\u58f0\uff0c\u547c\u5730\u4e00\u58f0\u76f4\u62d4\u800c\u8d77\uff0c\u8dc3\u8d77\u4e86\u4e00\u652f\u56db\u4e94\u5c3a\u9ad8\uff0c\u9690\u5165\u4e86\u5c4b\u9876\u591c\u6697\u4e2d\u3002 \u3000\u3000\u201c\u597d\uff01\u597d\uff01\u8fd9\u4e00\u5f0f\u6f5c\u9f99\u5347\u5929\uff0c\u59ff\u52bf\u3001\u529b\u9053\uff0c\u90fd\u7528\u7684\u6070\u5230\u597d\u5904\uff0c\u624d\u80fd\u76f4\u76f4\u7684\u767b\u7a7a\u800c\u8d77\uff0c\u71d5 \u8001\u5f1f\uff0c\u803d\u8bef\u4e86\u4ed6\uff0c\u53ef\u80fd\u662f\u7ec8\u8eab\u5927\u61be\u3002\u201d \u3000\u3000\u201c\u6211\u770b\u7684\u51fa\u6765\uff0c\u4ed6\u7684\u8eab\u624b\u5df2\u7ecf\u8ddf\u6211\u5dee\u4e0d\u591a\u4e86\uff0c\u8fd9\u51e0\u4e2a\u6708\u6765\uff0c\u771f\u662f\u8f9b\u82e6\u4e86\u6c5f\u5144\u3002\u201d \u3000\u3000\u62a2\u524d\u4e00\u6b65\uff0c\u5e26\u7740\u6c5f\u5343\u91cc\u8fdb\u5165\u4e66\u623f\u3002 \u3000\u3000\u71d5\u592b\u4eba\u7684\u624b\u811a\u5f88\u5feb\uff0c\u4e24\u4e2a\u4eba\u521a\u521a\u843d\u5750\uff0c\u5979\u5df2\u6258\u7740\u4e2a\u5927\u6728\u76d8\uff0c\u8d70\u4e86\u8fdb\u6765\uff0c\u56db\u6837\u83dc\u53ca\u4e00\u5927\u58f6 \u9152\u3002 \u3000\u3000\u90a3\u662f\u4e2a\u4e09\u5341\u56db\u4e94\u5c81\u7684\u4e2d\u5e74\u5987\u4eba\uff0c\u54c1\u8c8c\u7aef\u5e84\uff0c\u98ce\u97f5\u6e05\u96c5\u3002 \u3000\u3000\u71d5\u98de\u5fc3\u4e2d\u5fd6\u9053\uff1a\u770b\u60c5\u5f62\uff0c\u5c0f\u71d5\u5b50\u975e\u5f97\u88ab\u6c5f\u5343\u91cc\u5e26\u8d70\u4e0d\u53ef\uff0c\u4f55\u4e0d\u501f\u673a\u4f1a\u5148\u7ed9\u6dd1\u8d1e\u4e00\u70b9\u5fc3\u7406 \u4e0a\u7684\u51c6\u5907\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u71d5\u98de\u8f7b\u58f0\u5730\u9053\uff1a\u201c\u592b\u4eba\uff0c\u8fd9\u4f4d\u662f\u6c5f\u5927\u4fa0\uff0c\u5982\u975e\u4ed6\u4ed7\u4e49\u63f4\u624b\uff0c\u6211\u8fd9\u4e2a\u5f00\u5c01\u5e9c\u7684\u603b\u6355 \u5934\uff0c\u53ea\u6015\u5df2\u7ecf\u88ab\u62bc\u5165\u5927\u7262\u4e86\u3002\u201d \u3000\u3000\u4e00\u9876\u5927\u5e3d\u5b50\u538b\u8fc7\u53bb\uff0c\u679c\u7136\u4f7f\u71d5\u592b\u4eba\u6123\u4f4f\u4e86\uff0c\u8fd9\u624d\u8f6c\u5934\u770b\u770b\u6c5f\u5343\u91cc\uff0c\u9053\uff1a\u201c\u591a\u8c22\u4f2f\u4f2f\u5e2e \u5fd9\u3002\u201d \u3000\u3000\u71d5\u98de\u9053\uff1a\u201c\u6c5f\u5144\u770b\u4e0a\u4e86\u5c0f\u71d5\u5b50\uff0c\u8981\u5e2e\u4ed6\u8350\u5165\u540d\u5e08\u95e8\u4e0b\uff0c\u5de1\u629a\u5927\u4eba\u4e5f\u540c\u610f\uff0c\u800c\u4e14\uff0c\u8fd8\u8981\u628a \u5c0f\u71d5\u5b50\u6536\u4f5c\u4e49\u5b50\u3002\u201d \u3000\u3000\u8bdd\u5982\u6025\u6c34\u4e0b\u6ee9\uff0c\u4e09\u4e24\u53e5\u4e2d\u5374\u5305\u542b\u4e86\u592a\u591a\u7684\u5185\u5bb9\uff0c\u71d5\u592b\u4eba\u4e00\u4e0b\u5b50\u4f3c\u662f\u4e0d\u80fd\u63a5\u53d7\uff0c\u6123\u4e86\u4e00 \u6123\uff0c\u9053\uff1a\u201c\u5b98\u4eba\uff0c\u6211\u4eec\u53ef\u662f\u8bf4\u8fc7\u4e86\uff0c\u5c0f\u71d5\u513f\u8981\u4ee5\u8bfb\u4e66\u4e3a\u91cd\uff0c\u4f55\u51b5\uff0c\u4ed6\u4e66\u4e5f\u8bfb\u7684\u4e0d\u9519\uff0c\u6211\u662f\u5e0c \u671b\u4ed6\u80fd\u6539\u6362\u54b1\u4eec\u71d5\u5bb6\u95e8\u98ce\uff0c\u4ee5\u79d1\u4e3e\u6b65\u5165\u4ed5\u9014\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u9519\u5440\uff01\u6211\u4e5f\u8fd9\u4e48\u60f3\uff0c\u4e0d\u8fc7\uff0c\u5b66\u6b66\u4e5f\u6ca1\u6709\u4ec0\u4e48\u4e0d\u597d\uff0c\u6587\u6b66\u5168\u624d\uff0c\u65e5\u540e\uff0c\u624d\u80fd\u62c5\u5f53\u5927 \u4efb\uff0c\u518d\u8bf4\u5de1\u629a\u5927\u4eba\u90fd\u540c\u610f\u4e86\uff0c\u6211\u4e5f\u4e0d\u4fbf\u53cd\u5bf9\u3002\u201d \u3000\u3000\u8fd9\u756a\u8bdd\u7684\u5185\u5bb9\u4e00\u56e2\u4e71\uff0c\u4f46\u610f\u601d\u5374\u5f88\u660e\u767d\uff0c\u90a3\u662f\u544a\u8bc9\u71d5\u592b\u4eba\uff0c\u5de1\u629a\u5927\u4eba\u90fd\u540c\u610f\u7684\u4e8b\uff0c\u4e0d\u5bb9 \u53cd\u5bf9\u3002 \u3000\u3000\u71d5\u592b\u4eba\u4e00\u5411\u8d24\u6dd1\u6e29\u67d4\uff0c\u4f46\u5bf9\u819d\u524d\u7684\u72ec\u5b50\u5374\u6709\u7740\u5979\u575a\u6301\u7684\u770b\u6cd5\uff0c\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u4f60\u5728\u5f00\u5c01 \u5e9c\u4e2d\u542c\u5dee\uff0c\u60f3\u89c1\u5de1\u629a\u5927\u4eba\u4e00\u9762\u53ea\u6015\u90fd\u4e0d\u5bb9\u6613\uff0c\u4ed6\u662f\u5927\u540f\uff0c\u65e5\u7406\u4e07\u673a\uff0c\u600e\u4f1a\u548c\u4f60\u8c08\u5230\u5c0f\u71d5\u5b50\u7684 \u4e8b\uff1f\u201d \u3000\u3000\u8fd9\u4f4d\u6e29\u987a\u7684\u5c0f\u5987\u4eba\uff0c\u4f3c\u662f\u8bfb\u8fc7\u4e0d\u5c11\u7684\u4e66\uff0c\u5916\u67d4\u5185\u521a\uff0c\u770b\u6837\u5b50\u662f\u4e2a\u5f88\u6709\u4e3b\u89c1\u7684\u4eba\uff0c\u8981\u8bf4\u670d \u5979\u5e26\u8d70\u5c0f\u71d5\u5b50\uff0c\u8fd8\u771f\u7684\u4e0d\u662f\u4e00\u4ef6\u5bb9\u6613\u7684\u4e8b\uff0c\u800c\u4e14\uff0c\u5979\u53e3\u9f7f\u4f36\u4fd0\uff0c\u71d5\u98de\u4e5f\u672a\u5fc5\u80fd\u8bf4\u5f97\u8fc7\u5979\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u4e0d\u613f\u628a\u4e8b\u60c5\u641e\u50f5\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u662f\u5f1f\u59b9\u5427\uff01\u6055\u6211\u6c5f\u67d0\u4eba\u6258\u5927\u4e86\uff0c\u6487\u4e0b\u5c0f\u71d5\u5b50\u7684 \u4e8b\uff0c\u6682\u65f6\u4e0d\u8c08\uff0c\u5f00\u5c01\u5e9c\u6b63\u5728\u6c79\u6d8c\u7740\u4e00\u80a1\u6697\u6d41\uff0c\u95f9\u4e0d\u597d\uff0c\u5c31\u662f\u5343\u4e07\u4eba\u5934\u843d\u5730\u7684\u4e8b\u2026\u2026\u201d \u3000\u3000\u71d5\u592b\u4eba\u5446\u4f4f\u4e86\uff0c\u9759\u9759\u7ad9\u7740\u4e0d\u52a8\uff0c\u4f3c\u662f\u5df2\u4e0d\u51c6\u5907\u79bb\u5f00\u4e86\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u76ee\u5149\u4e00\u63a0\u71d5\u98de\uff0c\u201c\u71d5\u8001\u5f1f\uff0c\u53ef\u5426\u8bf7\u5f1f\u59b9\u4e5f\u5750\u4e0b\u6765\uff0c\u6211\u770b\u5f1f\u59b9\u662f\u5f88\u6709\u89c1\u8bc6\u7684\u4eba\uff0c\u8bf4 \u4e0d\u5b9a\u8fd8\u4f1a\u5e2e\u54b1\u4eec\u62ff\u4e2a\u4e3b\u610f\u5462\uff01\u201d \u3000\u3000\u71d5\u98de\u7684\u5fc3\u4e2d\u5fd6\u9053\uff1a\u4e09\u516c\u4e3b\u5728\u5f00\u5c01\u5931\u8e2a\uff0c\u786e\u5b9e\u662f\u4e00\u4ef6\u5927\u4e8b\uff0c\u4f46\u8fd9\u672a\u5fc5\u5c31\u80fd\u8bf4\u52a8\u5979\u8ba9\u4f60\u5e26\u8d70 \u5c0f\u71d5\u5b50\uff0c\u77e5\u59bb\u83ab\u82e5\u592b\uff0c\u4f60\u8981\u60f3\u8bf4\u670d\u5979\uff0c\u95e8\u90fd\u6ca1\u6709\uff0c\u4e5f\u597d\uff0c\u8ba9\u4f60\u9886\u6559\u4e00\u4e0b\u71d5\u67d0\u4eba\u8001\u5a46\u7684\u5507\u5251\u820c \u5200\u4e86\u3002 \u3000\u3000\u5fc3\u4e2d\u76d8\u7b97\uff0c\u4eba\u5374\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6dd1\u8d1e\uff0c\u5750\u4e0b\u6765\u5427\uff01\u6c5f\u5144\u4e0d\u662f\u5916\u4eba\uff0c\u8fd9\u4e00\u6b21\u5e2e\u4e86\u6211\u5f88\u5927 \u7684\u5fd9\uff0c\u6293\u4f4f\u4e86\u4e00\u4e2a\u94a6\u62ff\u8981\u72af\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u6c5f\u4f2f\u4f2f\u4e0d\u5acc\u5192\u8bfb\uff0c\u8d31\u59be\u5c31\u606d\u656c\u4e0d\u5982\u4ece\u547d\u4e86\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u659f\u4e0a\u4e00\u676f\u9152\uff0c\u53cc\u624b\u6367\u8d77\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u5148\u656c\u6c5f\u4f2f\u4f2f\u62d4\u5200\u52a9\u6211\u592b\u541b\uff0c\u518d\u8c22\u8c22\u4f60\u8fd9\u534a \u5e74\u591a\u6765\uff0c\u6559\u71d5\u513f\u82e6\u7ec3\u6b66\u529f\uff0c\u8d31\u59be\u662f\u4e00\u7247\u8bda\u5fc3\uff0c\u5148\u5e72\u4e3a\u656c\u3002\u201d \u3000\u3000\u4e3e\u676f\u4e00\u996e\u800c\u5c3d\u3002 \u3000\u3000\u679c\u7136\uff0c\u4e0d\u662f\u4e00\u4e2a\u7b80\u5355\u7684\u4eba\u7269\uff0c\u6c5f\u5343\u91cc\u5fc3\u4e2d\u6697\u8d5e\uff0c\u8fd9\u6837\u4e5f\u597d\uff0c\u80fd\u628a\u4f60\u8bf4\u670d\uff0c\u5c0f\u71d5\u5b50\u4e5f\u4f1a\u8d70 \u5f97\u5b89\u5fc3\u3002 \u3000\u3000\u5f53\u4e0b\u4e3e\u676f\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\uff0c\u6211\u4e5f\u5e72\u4e00\u676f\u3002\u201d \u3000\u3000\u71d5\u98de\u4e5f\u5e72\u4e86\u4e00\u676f\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4f60\u65e9\u77e5\u9053\u4e86\uff0c\u4f60\u4eec\u6bcd\u5b50\u4e00\u6761\u5fc3\uff0c\u628a\u6211\u8fd9\u4e2a\u4f5c\u7239\u7684\u8499 \u5728\u9f13\u91cc\u3002\u201d \u3000\u3000\u71d5\u592b\u4eba\u7b11\u9053\uff1a\u201c\u4f60\u6bcf\u5929\u5fd9\u4e0a\u5fd9\u4e0b\u7684\uff0c\u90a3\u91cc\u4f1a\u5173\u6ce8\u5230\u513f\u5b50\u7684\u8eab\u4e0a\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u71d5\u8001\u5f1f\uff0c\u9a6c\u5de1\u629a\u4e0d\u662f\u8d75\u4e8c\u5824\u7684\u670b\u53cb\uff0c\u800c\u662f\u4ed6\u7684\u5e08\u5144\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u2026\u2026\u8fd9\u600e\u4e48\u4f1a\u5462\uff1f\u201d\u71d5\u98de\u771f\u771f\u6b63\u6b63\u7684\u5403\u4e86\u4e00\u60ca\u3002 \u3000\u3000\u201c\u6240\u4ee5\uff0c\u8d75\u4e8c\u5824\u63b3\u8d70\u4e09\u516c\u4e3b\u7684\u4e8b\uff0c\u9a6c\u5927\u4eba\u53ef\u80fd\u65e9\u5df2\u5fc3\u4e2d\u6709\u6570\u3002\u201d \u3000\u3000\u201c\u516c\u4e3b\u5931\u8e2a\uff0c\u90a3\u662f\u8bf4\u7687\u4e0a\u7684\u5973\u513f\u88ab\u4eba\u635e\u8d70\u4e86\uff1f\u201d\u71d5\u592b\u4eba\u7684\u8138\u8272\u5927\u53d8\uff0c\u5fc3\u4e2d\u7684\u60ca\u60b8\u80dc\u8fc7\u71d5 \u98de\u3002 \u3000\u3000\u201c\u4e0d\u9519\uff0c\u4e09\u516c\u4e3b\u7684\u5728\u5f00\u5c01\u88ab\u4eba\u52ab\u8d70\uff0c\u6211\u5949\u547d\u67e5\u529e\u6b64\u6848\uff0c\u5e78\u5f97\u6c5f\u5144\u534f\u52a9\uff0c\u624d\u627e\u51fa\u4e86\u4e00\u70b9\u5934 \u7eea\uff0c\u8981\u4e0d\u7136\uff0c\u8fd8\u771f\u4e0d\u77e5\u9053\u8981\u5982\u4f55\u7740\u624b\u3002\u201d \u3000\u3000\u71d5\u98de\u8bf4\u51fa\u4e86\u4e09\u516c\u4e3b\u5931\u8e2a\u7684\u7ecf\u8fc7\u3002 \u3000\u3000\u201c\u5982\u679c\u627e\u4e0d\u5230\u4e09\u516c\u4e3b\u7684\u4e0b\u843d\uff0c\u4f60\u5c82\u4e0d\u662f\u8981\u53d7\u5230\u5927\u72f1\u682a\u8fde\u2026\u2026\u201d \u3000\u3000\u71d5\u592b\u4eba\u5173\u5fc3\u7684\u770b\u7740\u4e08\u592b\uff0c\u7709\u76ee\u95f4\u4e00\u7247\u5fe7\u82e6\u3002 \u3000\u3000\u201c\u4e0d\u5355\u662f\u6211\u8fd9\u4e2a\u603b\u6355\u5934\uff0c\u5c39\u77e5\u5e9c\u3001\u9a6c\u5de1\u629a\u90fd\u8131\u4e0d\u4e86\u5173\u7cfb\u3002\u201d \u3000\u3000\u201c\u53ef\u6015\u7684\u8fd8\u4e0d\u662f\u8fd9\u4e9b\u2026\u2026\u201d\u6c5f\u5343\u91cc\u4f4e\u58f0\u9053\uff1a\u201c\u9a6c\u5927\u4eba\u5df2\u80f8\u6709\u6210\u7af9\uff0c\u8bf4\u4e0d\u5b9a\u4ed6\u65e9\u5df2\u8bbe\u8ba1\u597d \u727a\u7272\u4f60\u548c\u5c39\u77e5\u5e9c\uff0c\u4ee5\u4fdd\u4f4f\u4ed6\u7684\u804c\u4f4d\u2026\u2026\u201d \u3000\u3000\u71d5\u98de\u957f\u957f\u5401\u4e86\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u542c\u6c5f\u5144\u7684\u53e3\u6c14\uff0c\u597d\u50cf\u662f\u5de1\u629a\u5927\u4eba\u4e5f\u53c2\u52a0\u4e86\u63b3\u52ab\u516c\u4e3b\u7684\u4e8b \u4e86\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u627e\u5230\u6709\u529b\u7684\u8bc1\u636e\u4e4b\u524d\uff0c\u6211\u4e0d\u80fd\u968f\u4fbf\u4e71\u8bf4\uff0c\u4e0d\u8fc7\uff0c\u6709\u51e0\u70b9\u65e0\u6cd5\u89e3\u91ca\u7684\u5730\u65b9\uff0c\u4e0d\u80fd\u4e0d \u8ba9\u4eba\u6000\u7591\uff1f\u201d \u3000\u3000\u71d5\u98de\u9053\uff1a\u201c\u90a3\u51e0\u70b9\uff1f\u201d \u3000\u3000\u201c\u4e09\u516c\u4e3b\u79bb\u4eac\u4e00\u4e8b\u975e\u5e38\u9690\u5bc6\uff0c\u6211\u67e5\u95ee\u7684\u7ed3\u679c\uff0c\u6c5f\u6e56\u4e2d\u65e0\u4eba\u77e5\u6653\uff0c\u5230\u76ee\u524d\u4e3a\u6b62\uff0c\u6c5f\u6e56\u4e0a\u8fd8 \u672a\u4f20\u51fa\u4e09\u516c\u4e3b\u5931\u8e2a\u7684\u4e8b\uff0c\u800c\u8d75\u4e8c\u5824\u600e\u4e48\u4f1a\u77e5\u9053\u5462\uff1f\u201d \u3000\u3000\u201c\u662f\u554a\uff01\u5c39\u77e5\u5e9c\u548c\u6211\u71d5\u67d0\u4eba\u90fd\u4e0d\u77e5\u9053\u4e09\u516c\u4e3b\u5230\u4e86\u5f00\u5c01\uff0c\u8d75\u4e8c\u5824\u53c8\u600e\u4e48\u4f1a\u77e5\u9053\u5462\uff1f\u4e0d\u8fc7\uff0c \u8fd9\u4e5f\u4e0d\u80fd\u8bc1\u660e\u9a6c\u5927\u4eba\u548c\u8d75\u4e8c\u5824\u6709\u52fe\u7ed3\u554a\uff01\u201d \u3000\u3000\u6c5f\u5343\u91cc\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6709\u9053\u7406\u3002\u5982\u679c\u6709\u4eba\u80fd\u77e5\u9053\u516c\u4e3b\u7684\u884c\u8e2a\uff0c\u6cb3\u5357\u4e00\u7701\u4e2d\uff0c\u90a3\u4e2a\u4eba\u4f1a \u6700\u5148\u77e5\u9053\uff1f\u201d \u3000\u3000\u71d5\u98de\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u9a6c\u5de1\u629a\u2026\u2026\u201d \u3000\u3000\u201c\u636e\u8bf4\u88c2\u571f\u5c01\u7586\u7684\u5927\u5458\uff0c\u5728\u4eac\u57ce\u91cc\u9762\u90fd\u6d3e\u6709\u4e13\u4eba\u9a7b\u5b88\uff0c\u67e5\u770b\u653f\u60c5\uff0c\u4ee5\u901a\u6d88\u606f\uff0c\u52fe\u7ed3\u671d \u81e3\uff0c\u4e92\u4e3a\u8868\u91cc\uff0c\u5f7c\u6b64\u63a9\u62a4\uff0c\u4ee5\u4fdd\u7235\u4f4d\uff0c\u71d5\u8001\u5f1f\u542c\u4eba\u8bf4\u8fc7\u5417\uff1f\u201d \u3000\u3000\u71d5\u98de\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u542c\u662f\u542c\u8fc7\uff0c\u53ea\u4e0d\u8fc7\uff0c\u90a3\u662f\u9ad8\u7b49\u673a\u5bc6\uff0c\u53ea\u542c\u4f20\u95fb\uff0c\u672a\u66fe\u76ee\u7779\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6c5f\u6e56\u591a\u51f6\u9669\uff0c\u4f46\u5b98\u573a\u7684\u51f6\u9669\u72e1\u8bc8\u5c24\u8fc7\u6c5f\u6e56\uff0c\u9a6c\u6587\u4e2d\u505a\u4e86\u591a\u4e45\u7684\u6cb3 \u5357\u5de1\u629a\u4e86\uff1f\u201d \u3000\u3000\u201c\u56db\u5e74\u591a\u4e86\u5427\uff01\u53bb\u5e74\u6709\u5185\u8c03\u4eac\u5b98\u7684\u4f20\u8bf4\uff0c\u4e0d\u77e5\u4f55\u6545\uff0c\u53c8\u7559\u4e86\u4e0b\u6765\u3002\u201d \u3000\u3000\u201c\u4f60\u5bf9\u9a6c\u5de1\u629a\u77e5\u9053\u591a\u5c11\uff1f\u201d \u3000\u3000\u201c\u4ed6\u9ad8\u9ad8\u5728\u4e0a\uff0c\u4e00\u5e74\u4e2d\u96be\u5f97\u89c1\u4e00\u4e24\u6b21\u9762\uff0c\u81ea\u7136\u662f\u77e5\u9053\u7684\u4e0d\u591a\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u4f4e\u58f0\u9053\uff1a\u201c\u4ed6\u4e00\u8eab\u6b66\u529f\uff0c\u5df2\u5230\u795e\u6c14\u8fd8\u865a\u3001\u4e0d\u7740\u76ae\u76f8\u4e4b\u5883\u201d\u4ec0\u4e48\uff1f\u2026\u2026\u201c\u71d5\u98de\u60ca\u6115 \u5730\u9053\uff1a\u201d\u9a6c\u5de1\u629a\u4f1a\u6b66\u529f\uff1f\u6211\u4e0d\u76f8\u4fe1\uff01\u201c \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u4f60\u548c\u97e9\u6d9b\u7684\u4ea4\u60c5\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u7b97\u8d77\u6765\uff0c\u4ed6\u662f\u6211\u7684\u4e0a\u53f8\uff0c\u8c08\u4e0d\u4e0a\u809d\u80c6\u76f8\u7167\uff0c\u5374\u4e5f\u5f7c\u6b64\u80fd\u8c08\u5f97\u6765\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u4f60\u53bb\u770b\u770b\u97e9\u603b\u6355\u5934\u3002\u201d\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u6700\u597d\u662f\u63a2\u75c5\u5bc6\u5ba4\uff0c\u8ba9\u4ed6\u6709\u8bf4\u8bdd\u7684\u65b9\u4fbf\uff0c\u97e9\u6d9b \u4e0d\u662f\u5367\u75c5\u5728\u5e8a\uff0c\u800c\u662f\u88ab\u4eba\u4ee5\u7279\u6b8a\u7684\u624b\u6cd5\uff0c\u4f24\u4e86\u7ecf\u7a74\u3002\u201d \u3000\u3000\u71d5\u98de\u5982\u53d7\u96f7\u51fb\u822c\uff0c\u50bb\u4f4f\u4e86\uff0c\u624b\u4e2d\u7684\u7b77\u5b50\u4e5f\u8dcc\u843d\u5730\u9762\uff0c\u826f\u4e45\u540e\uff0c\u624d\u4f4e\u58f0\u5730\u9053\uff1a\u201c\u9a6c\u5de1\u629a\u4e0b \u7684\u624b\u4e48\uff1f\u201d \u3000\u3000\u201c\u53bb\u95ee\u97e9\u6d9b\u5427\uff01\u4ee5\u4ed6\u4e4b\u80fd\uff0c\u6570\u6708\u4e4b\u4e45\uff0c\u4ecd\u65e0\u6cd5\u81ea\u89e3\u7a74\u9053\uff0c\u4e0b\u624b\u7684\u4eba\u6b66\u529f\u4e4b\u9ad8\u3001\u624b\u6cd5\u4e4b \u5947\uff0c\u5728\u5f53\u4eca\u6b66\u6797\u4e4b\u4e2d\uff0c\u7b97\u5f97\u4e0a\u6709\u6570\u9ad8\u4eba\u4e4b\u4e00\u4e86\u3002\u201d \u3000\u3000\u674e\u9752\u7af9\u6ca1\u6709\u544a\u8bc9\u6c5f\u5343\u91cc\uff0c\u4ec0\u4e48\u4eba\u70b9\u4e86\u97e9\u6d9b\u7684\u7a74\u9053\uff0c\u6c5f\u5343\u91cc\u4e5f\u4e0d\u4fbf\u81ea\u4f5c\u4e3b\u5f20\u7684\u8bf4\u51fa\u4e00\u4e2a\u4eba \u6765\u3002 \u3000\u3000\u201c\u6211\u76f8\u4fe1\u6c5f\u5144\uff0c\u4f46\u6682\u65f6\u8fd8\u4e0d\u4fbf\u53bb\u63a2\u671b\u97e9\u603b\u6355\u5934\uff0c\u4ee5\u514d\u9732\u51fa\u98ce\u58f0\uff0c\u7b49\u529e\u5b8c\u4e09\u516c\u4e3b\u7684\u6848\u5b50\u518d \u8bf4\u3002\u201d \u3000\u3000\u201c\u5509\uff01\u6211\u544a\u8bc9\u4f60\u8fd9\u4e9b\u4e8b\uff0c\u5c31\u662f\u8981\u4f60\u5c0f\u5fc3\u5e94\u5bf9\uff0c\u54b1\u4eec\u7684\u8ba1\u5212\u8981\u6539\u4e86\uff0c\u4e00\u5207\u90fd\u65e9\u4f5c\u5b89\u6392\uff0c\u4e5f \u8bb8\u8fd8\u53ef\u4ee5\u6df7\u4e00\u65f6\u2026\u2026\u201d \u3000\u3000\u71d5\u98de\u9053\uff1a\u201c\u6c5f\u5144\uff0c\u6211\u770b\uff0c\u5c0f\u71d5\u5b50\u4f60\u5f97\u5148\u5e26\u8d70\u4e86\uff0c\u9a6c\u5de1\u629a\u8981\u6536\u4ed6\u4f5c\u4e49\u5b50\uff0c\u867d\u662f\u4e00\u53e5\u620f\u8a00\uff0c \u4f46\u4e5f\u4e0d\u80fd\u4e0d\u9632\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4ef6\u4e8b\uff0c\u8981\u5f1f\u59b9\u6765\u51b3\u5b9a\u4e86\u3002\u201d \u3000\u3000\u71d5\u592b\u4eba\u6eda\u4e0b\u4e86\u4e24\u884c\u6cea\uff0c\u9053\uff1a\u201c\u5b98\u573a\u5982\u6b64\u7684\u51f6\u9669\uff0c\u4e0d\u4f5c\u4e5f\u7f62\uff0c\u65e2\u7136\u4f2f\u4f2f\u559c\u6b22\u5c0f\u71d5\u5b50\uff0c\u5c31\u5e26 \u4ed6\u53bb\u5427\uff01\u201d \u3000\u3000\u201c\u5bf9\uff01\u4e09\u516c\u4e3b\u7684\u6848\u5b50\u6709\u4e2a\u5b9a\u8bba\uff0c\u6211\u5c31\u8f9e\u53bb\u8fd9\u4e2a\u804c\u4f4d\uff0c\u56de\u5bb6\u53bb\u4f5c\u4e2a\u7530\u820d\u519c\u592b\uff0c\u8fc7\u51e0\u5e74\u5b89\u9759 \u7684\u65e5\u5b50\u3002\u201d\u71d5\u98de\u76ee\u6ce8\u8d24\u6dd1\u7684\u59bb\u5b50\uff0c\u9053\uff1a\u201c\u771f\u4e5f\u8be5\u597d\u597d\u966a\u966a\u4f60\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u5fc3\u4e2d\u6697\u9053\uff1a\u60f3\u8f9e\u53bb\u8fd9\u4e2a\u804c\u4f4d\u4e00\u8d70\u4e86\u4e4b\uff0c\u53ea\u6015\u4e0d\u4f1a\u6709\u8fd9\u4e48\u4fbf\u5b9c\u7684\u4e8b\u2026\u2026\u3002\u5fc3\u4e2d\u8bdd\u5e76 \u672a\u8bf4\u51fa\u53e3\uff0c\u56e0\u4e3a\u71d5\u592b\u4eba\u4f3c\u662f\u60f3\u5230\u4e86\u5f88\u591a\uff0c\u6cea\u73e0\u513f\u4e00\u9897\u9897\u7684\u6eda\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4ed6\u4e0d\u613f\u518d\u60ca\u5413\u5230\u8fd9\u4f4d\u5584\u826f\u3001\u8d24\u6dd1\u7684\u5c0f\u5987\u4eba\u4e86\u3002 \u3000\u3000\u71d5\u98de\u76b1\u76b1\u7709\uff0c\u9053\uff1a\u201c\u592b\u4eba\uff0c\u4e0d\u8981\u54ed\u4e86\uff0c\u6c5f\u5927\u4fa0\u662f\u6b66\u6797\u9ad8\u4eba\uff0c\u5c0f\u71d5\u5b50\u8ddf\u7740\u4ed6\u6bd4\u8ddf\u54b1\u4eec\u6709\u51fa \u606f\u3002\u201d \u3000\u3000\u201c\u6211\u4e0d\u662f\u62c5\u5fc3\u5c0f\u71d5\u5b50\uff0c\u4ed6\u8ddf\u7740\u6c5f\u4f2f\u4f2f\u6211\u5f88\u653e\u5fc3\uff0c\u6211\u62c5\u5fc3\u7684\u662f\u4f60\u201d\u6211\u2026\u2026\u201c\u71d5\u98de\u6447\u6447\u5934\uff0c \u9053\uff1a\u201d\u6211\u6709\u4ec0\u4e48\u597d\u62c5\u5fc3\u7684\uff1f\u201c \u3000\u3000\u201c\u4e00\u9762\u662f\u91d1\u6280\u7389\u53f6\u7684\u516c\u4e3b\u8eab\u4efd\uff0c\u4e00\u9762\u662f\u88c2\u4e0a\u5c01\u7586\u7684\u65b9\u9762\u5927\u5458\uff0c\u4e5f\u662f\u4f60\u7684\u5934\u9876\u4e0a\u53f8\uff0c\u4f60\u5c31 \u7b97\u67e5\u51fa\u4e86\u4e09\u516c\u4e3b\u7684\u4e0b\u843d\uff0c\u4f60\u53c8\u5c06\u5982\u4f55\uff1f \u3000\u3000\u2026\u2026\u3000\u3000\u201c \u3000\u3000\u71d5\u592b\u4eba\u4e3e\u624b\u62b9\u53bb\u6cea\u75d5\uff0c\u9053\uff1a\u201c\u516c\u4e8b\u516c\u529e\uff0c\u8fd8\u662f\u653e\u624b\u4e0d\u7ba1\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u201d\u71d5\u98de\u60f3\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u7167\u9053\u7406\uff0c\u738b\u5b50\u72af\u6cd5\uff0c\u4e0e\u6c11\u540c\u7f6a\uff0c\u53ef\u662f\uff0c\u9a6c\u5de1\u629a\u7684\u5b98 \u9ad8\u4f4d\u5c0a\uff0c\u627e\u4e0d\u51fa\u660e\u786e\u7684\u8bc1\u636e\u2026\u2026\u201d \u3000\u3000\u201c\u5c31\u7b97\u80fd\u627e\u51fa\u8bc1\u636e\uff0c\u4e5f\u4e0d\u4f1a\u5f88\u660e\u786e\u2026\u2026\u201d\u71d5\u592b\u4eba\u9eef\u7136\u63a5\u9053\uff1a\u201c\u4f60\u6597\u4e0d\u8fc7\u4ed6\u7684\uff0c\u4ed6\u662f\u4e00\u54c1 \u516c\u537f\uff0c\u4f60\u4e0d\u8fc7\u662f\u4e2a\u5f00\u5c01\u5e9c\u8857\u7684\u6355\u5934\u800c\u5df2\uff0c\u600e\u80fd\u6273\u5f97\u5012\u4ed6\u2026\u2026\u201d \u3000\u3000\u6c5f\u5343\u91cc\u6697\u6697\u8d5e\u9053\uff1a\u770b\u4e0d\u51fa\u8fd9\u4e2a\u5a07\u5f31\u7684\u5c0f\u5987\u4eba\uff0c\u7adf\u6709\u5982\u6b64\u89c1\u89e3\uff0c\u52ff\u602a\u71d5\u98de\u63d0\u5230\u592b\u4eba\u5fc3\u4e2d\u4f1a \u6709\u4e9b\u656c\u754f\u4e86\u3002 \u3000\u3000\u201c\u592b\u4eba\u2026\u2026\u201d\u71d5\u98de\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u662f\u798f\u4e0d\u662f\u7978\uff0c\u662f\u7978\u8eb2\u4e0d\u8fc7\uff0c\u5982\u975e\u6c5f\u5144\u70b9\u9192\uff0c\u6211\u8fde\u505a \u68a6\u90fd\u68a6\u4e0d\u5230\u4e09\u516c\u4e3b\u5931\u8e2a\u4e00\u4e8b\u4f1a\u7275\u8fde\u5230\u5de1\u629a\u5927\u4eba\uff0c\u5fc3\u4e2d\u5168\u65e0\u9632\u5907\uff0c\u96be\u514d\u88ab\u4ed6\u7b97\u8ba1\uff0c\u73b0\u5728\u65e2\u7136\u77e5 \u6653\u5185\u60c5\uff0c\u81ea\u4f1a\u6709\u4e9b\u51c6\u5907\uff0c\u592b\u4eba\u8bf7\u6536\u62fe\u4e00\u4e0b\uff0c\u5929\u4e00\u4eae\u5c31\u79bb\u5f00\u8fd9\u91cc\uff0c\u56de\u5f52\u539f\u7c4d\uff0c\u5982\u82e5\u60c5\u52bf\u6709\u53d8\uff0c \u6211\u7acb\u523b\u8d76\u56de\u53bb\u548c\u4f60\u4f1a\u5408\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u5f03\u804c\u6f5c\u9003\uff0c\u5b98\u5bb6\u5c82\u4e0d\u662f\u4f1a\u7ed8\u5f62\u7f09\u62ff\uff0c\u5929\u4e0b\u867d\u5927\uff0c\u6050\u6015\u4e5f\u6ca1\u6709\u6211\u4eec\u5bb9\u8eab\u4e4b\u5730\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u592b\u4eba\u4e0d\u7528\u62c5\u5fc3\uff0c\u6211\u505a\u4e86\u4e8c\u5341\u51e0\u5e74\u7684\u6355\u5934\uff0c\u81ea\u4f1a\u6709\u9003\u8d70\u7684\u529e\u6cd5\u3002\u201d \u3000\u3000\u71d5\u592b\u4eba\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u597d\u5427\uff01\u59be\u8eab\u4e00\u5207\u4ece\u547d\u5c31\u662f\uff0c\u597d\u5728\u5c0f\u71d5\u5b50\u5df2\u88ab\u6c5f\u4f2f\u4f2f\u5e26\u8d70\uff0c\u5c11\u4e86 \u4e00\u4efd\u5fc3\u4e8b\uff0c\u6211\u56de\u5bb6\u7b49\u4f60\u4e00\u5e74\uff0c\u4e00\u5e74\u5185\u6ca1\u6709\u6d88\u606f\uff0c\u6211\u5c31\u2026\u2026\u201d \u3000\u3000\u6c5f\u5343\u91cc\u89c1\u4e24\u4eba\u5728\u7814\u8ba8\u4eca\u540e\u5927\u8ba1\uff0c\u4e0d\u4fbf\u63d2\u53e3\uff0c\u6b64\u523b\u542c\u5f97\u71d5\u592b\u4eba\u53e3\u6c14\u4e0d\u5bf9\uff0c\u6025\u6025\u8bf4\u9053\uff1a\u201c\u4e09 \u516c\u4e3b\u5931\u8e2a\u4e00\u4e8b\uff0c\u662f\u9707\u52a8\u671d\u91ce\u7684\u5927\u6848\u5b50\uff0c\u9a6c\u5de1\u629a\u7edd\u96be\u4e00\u624b\u906e\u5929\uff0c\u5c31\u7b97\u51c6\u5907\u62ff\u5c39\u77e5\u5e9c\u548c\u71d5\u8001\u5f1f\u4f5c \u4ee3\u7f6a\u7f94\u7f8a\uff0c\u4e5f\u65e0\u6cd5\u51cf\u53bb\u672c\u8eab\u7684\u7f6a\u884c\uff0c\u5b98\u65b9\u7684\u8ffd\u67e5\u62ff\u95ee\u5012\u662f\u4e0d\u8db3\u4e3a\u8651\uff0c\u53ef\u5fe7\u7684\u662f\u9a6c\u5de1\u629a\u90a3\u8eab\u4e0d \u4e3a\u4eba\u77e5\u53c8\u5353\u7edd\u4e0d\u51e1\u7684\u6b66\u529f\uff0c\u548c\u4ed6\u90a3\u6df1\u6c89\u7684\u5fc3\u673a\u3001\u8c0b\u7565\uff0c\u65e0\u6cd5\u9884\u6d4b\u51fa\u4ed6\u4e0b\u4e00\u6b65\u7528\u7684\u662f\u4ec0\u4e48\u65b9 \u6cd5\uff1f\u7167\u76ee\u524d\u7684\u60c5\u5f62\u770b\u6765\uff0c\u54b1\u4eec\u8ffd\u7d22\u4e09\u516c\u4e3b\u884c\u8e2a\u7684\u8ba1\u5212\uff0c\u5012\u662f\u8981\u6682\u505c\u4e0b\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u6c5f\u5144\u6709\u4f55\u9ad8\u89c1\uff0c\u71d5\u98de\u6d17\u8033\u606d\u542c\u3002\u201d \u3000\u3000\u201c\u662f\u554a\uff01\u6c5f\u4f2f\u4f2f\u6307\u70b9\u6211\u4eec\u4e00\u6761\u660e\u8def\uff0c\u6211\u4eec\u7ec8\u8eab\u611f\u6fc0\u3002\u201d\u71d5\u592b\u4eba\u76c8\u76c8\u7ad9\u8d77\uff0c\u8dea\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u201c\u8d77\u6765\uff0c\u8d77\u6765\u2026\u2026\u201d \u3000\u3000\u6c5f\u5343\u91cc\u6025\u5f97\u77aa\u5927\u4e86\u773c\u775b\uff0c\u53c8\u4e0d\u4fbf\u4f38\u624b\u53bb\u6276\u3002 \u3000\u3000\u71d5\u592b\u4eba\u76f8\u5f53\u806a\u660e\uff0c\u5f88\u61c2\u5f97\u9002\u53ef\u800c\u6b62\uff0c\u7ad9\u8d77\u8eab\u5b50\uff0c\u64e6\u53bb\u6cea\u75d5\uff0c\u9053\uff1a\u201c\u6211\u53bb\u53a8\u623f\u66ff\u4f60\u4eec\u52a0\u4e24 \u4e2a\u83dc\u3002\u201d \u3000\u3000\u8bf4\u7f62\uff0c\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u6697\u6697\u70b9\u5934\uff0c\u5fd6\u9053\uff1a\u8fd9\u4e2a\u5c0f\u5987\u4eba\u806a\u660e\u5f97\u5f88\uff0c\u660e\u91cc\u4e0b\u53a8\u505a\u83dc\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u662f\u6709\u610f\u907f\u5f00\u597d \u8ba9\u6211\u548c\u71d5\u98de\u80fd\u65e0\u6240\u987e\u5fcc\u7684\u7545\u6240\u6b32\u8a00\u3002 \u3000\u3000\u7b49\u71d5\u592b\u4eba\u9001\u4e0a\u6dfb\u52a0\u7684\u9152\u83dc\u65f6\uff0c\u6c5f\u5e72\u91cc\u548c\u71d5\u98de\u7684\u4ea4\u8c08\u5df2\u8fd1\u5c3e\u58f0\uff0c\u71d5\u592b\u4eba\u542c\u5230\u6c5f\u5343\u91cc\u6700\u540e\u4e00 \u6bb5\u8bdd\u3002 \u3000\u3000\u201c\u9a6c\u5de1\u629a\u53ef\u80fd\u9519\u4f30\u4e86\u738b\u91cd\u5c71\uff0c\u4e5f\u5c11\u7b97\u4e86\u6458\u661f\u624b\u738b\u5f64\u8fd9\u4e2a\u4eba\uff0c\u5982\u679c\u6211\u63a8\u65ad\u4e0d\u9519\uff0c\u5185\u5bab\u7edf\u9886 \u738b\u5f64\u4f1a\u5c3d\u5feb\u8d76\u6765\uff0c\u8fd9\u7b49\u5bab\u95f1\u9690\u5bc6\uff0c\u4e0d\u662f\u4f60\u4eec\u4e13\u4f3a\u8ffd\u6355\u76d7\u532a\u7684\u6355\u5934\u80fd\u591f\u4e86\u89e3\uff0c\u53ea\u8981\u4e09\u516c\u4e3b\u8fd8\u6d3b \u7740\uff0c\u8fd9\u4ef6\u4e8b\u6682\u65f6\u4e0d\u4f1a\u63ed\u5f00\u6765\uff0c\u4f60\u53ea\u8981\u60f3\u6cd5\u5b50\u62d6\u5ef6\u65f6\u95f4\uff0c\u7b49\u738b\u7edf\u9886\u8d76\u5230\u5f00\u5c01\u5e9c\u7531\u4ed6\u63a5\u624b\uff0c\u5230 \u65f6\u2026\u2026\u201d \u3000\u3000\u71d5\u592b\u4eba\u542c\u5f97\u5f88\u7528\u5fc3\uff0c\u4f46\u5979\u5374\u4f4e\u7740\u5934\uff0c\u6ca1\u6709\u63a5\u53e3\u3002 \u3000\u3000\u71d5\u98de\u5374\u8fde\u8fde\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5bf9\uff0c\u5bf9\uff0c\u6c5f\u5144\u679c\u662f\u89c1\u8bc6\u5353\u7edd\uff0c\u738b\u4f8d\u536b\u90a3\u8fb9\u6211\u4e0d\u4fbf\u8bf4\u4ec0\u4e48\uff0c\u8fd8\u8bf7 \u6c5f\u5144\u4ea4\u4ee3\u4ed6\u4e00\u58f0\u3002\u201d \u3000\u3000\u201c\u738b\u91cd\u5c71\u51fa\u8eab\u6b66\u5f53\u95e8\u4e0b\uff0c\u5bf9\u6c5f\u6e56\u4e2d\u7684\u9634\u8bc8\u65e9\u6709\u4e86\u89e3\uff0c\u53ea\u662f\u7ecf\u9a8c\u6b20\u7f3a\u4e00\u4e9b\uff0c\u6211\u4f1a\u5b89\u6392\uff0c\u71d5 \u8001\u5f1f\u53ea\u8981\u548c\u4ed6\u7d27\u5bc6\u5408\u4f5c\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u76ee\u5149\u8f6c\u6ce8\u71d5\u592b\u4eba\u7684\u8138\u4e0a\uff0c\u6c5f\u5343\u91cc\u53c8\u7f13\u7f13\u63a5\u9053\uff1a\u201c\u5f1f\u59b9\uff0c\u6211\u8981\u5e26\u5c0f\u71d5\u5b50\u8d70\u4e86\u3002\u201d \u3000\u3000\u71d5\u592b\u4eba\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u51e0\u65f6\u52a8\u8eab\uff1f\u201d \u3000\u3000\u201c\u5929\u4eae\u5c31\u8d70\uff01\u201d\u6c5f\u5343\u91cc\u8bf4\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u7684\u4e8b\uff0c\u6211\u5df2\u548c\u71d5\u8001\u5f1f\u8c08\u51fa\u4e86\u4e00\u4e2a\u529e\u6cd5\uff0c\u62d6\u5ef6\u65f6 \u95f4\uff0c\u4ee5\u5f85\u63f4\u624b\u3002\u201d \u3000\u3000\u201c\u6709\u6c5f\u4f2f\u4f2f\u66ff\u6211\u4eec\u501f\u7740\u4ee3\u7b79\uff0c\u8d31\u59be\u5c31\u5b89\u5fc3\u591a\u4e86\u3002\u201d \u3000\u3000\u71d5\u98de\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6c5f\u5144\u7684\u8bbe\u8ba1\u5f88\u9ad8\u660e\uff0c\u6211\u76f8\u4fe1\u53ef\u4ee5\u5e94\u4ed8\u4e0b\u6765\uff0c\u73b0\u5728\uff0c\u6211\u53bb\u66ff\u4f60\u96c7\u597d\u8f66 \u5b50\uff0c\u5929\u4e00\u4eae\uff0c\u4f60\u5c31\u5e26\u7740\u5c0f\u71d5\u5b50\u4e00\u8d77\u51fa\u57ce\uff0c\u4e0d\u7528\u7b49\u6211\u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u71d5\u592b\u4eba\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u548c\u6c5f\u4f2f\u4f2f\u5728\u54ea\u91cc\u89c1\u9762\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u6211\u5728\u7409\u7483\u5854\u4e0b\u7b49\u4f60\u4eec\u3002\u201d \u3000\u3000\u71d5\u592b\u4eba\u672a\u518d\u591a\u95ee\uff0c\u5979\u5fc3\u4e2d\u660e\u767d\uff0c\u6c5f\u5343\u91cc\u548c\u71d5\u98de\u4e5f\u4e3a\u5979\u7684\u53bb\u5904\u4f5c\u4e86\u5b89\u6392\uff0c\u4e5f\u8bb8\u4e0d\u518d\u56de\u5f52\u539f \u7c4d\uff0c\u5f53\u7136\uff0c\u6700\u597d\u662f\u8ddf\u7740\u5c0f\u71d5\u5b50\u3002 \u3000\u3000\u6682\u522b\u4e86\u4e08\u592b\uff0c\u80fd\u8ddf\u513f\u5b50\u5728\u4e00\u8d77\uff0c\u4e5f\u662f\u4e00\u79cd\u5b89\u6170\u3002 \u3000\u3000\u53ef\u60dc\u7684\u662f\uff0c\u5979\u7684\u5e0c\u671b\u843d\u7a7a\u4e86\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u5982\u7ea6\u5728\u7409\u7483\u5854\u4e0b\u51fa\u73b0\uff0c\u4f46\u4ed6\u53ea\u5c06\u5c0f\u71d5\u5b50\u5e26\u8d70\uff0c\u71d5\u592b\u4eba\u5e76\u6ca1\u6709\u56de\u5f52\u539f\u7c4d\uff0c\u5374\u5bc4\u5c45\u5728 \u5f00\u5c01\u57ce\u4e1c\u5341\u91cc\u5916\u7684\u5c01\u5bb6\u5821\u4e2d\u3002 \u3000\u3000\u90a3\u662f\u4e00\u5904\u767e\u6237\u4eba\u5bb6\u96c6\u5c45\u7684\u5c0f\u5be8\u5b50\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u9063\u8d70\u4e86\u7bf7\u8f66\uff0c\u4eb2\u81ea\u9001\u71d5\u592b\u4eba\u8fdb\u5165\u4e86\u5c01\u5bb6\u5821\u3002 \u3000\u3000\u5c01\u5bb6\u5821\u767e\u6237\u4eba\u5bb6\u5168\u90fd\u59d3\u5c01\uff0c\u4fdd\u4e3b\u662f\u4e00\u4f4d\u56db\u5341\u5de6\u53f3\u7684\u866c\u9aef\u5927\u6c49\uff0c\u5bf9\u6c5f\u5343\u91cc\u4f3c\u662f\u6781\u4e3a\u7684\u606d \u656c\u3002 \u3000\u3000\u71d5\u592b\u4eba\u88ab\u5b89\u6392\u5728\u4e00\u5ea7\u72ec\u7acb\u7684\u8de8\u9662\u4e2d\uff0c\u8fd8\u6709\u4e00\u4f4d\u4e2d\u5e74\u7684\u8001\u5988\u5b50\u4f3a\u5019\uff0c\u6c5f\u5343\u91cc\u5e76\u6ca1\u6709\u4e3a\u4e24\u4eba \u5f15\u89c1\uff0c\u5c01\u5821\u4e3b\u4e5f\u4e0d\u95ee\u71d5\u592b\u4eba\u7684\u8eab\u4efd\uff0c\u4e00\u5207\u884c\u52a8\uff0c\u4f3c\u4e4e\u662f\u5c3d\u91cf\u4fdd\u6301\u5e73\u6de1\uff0c\u4e0d\u613f\u610f\u591a\u8bf4\u4e00\u4e9b\u8bdd \u513f\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u5b89\u7f6e\u597d\u71d5\u592b\u4eba\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5c06\u5c0f\u71d5\u5b50\u9001\u8fc7\u53bb\u6211\u5c31\u6765\u770b\u4f60\uff0c\u53ea\u7ba1\u5b89\u5fc3\u7684\u4f4f\u4e0b\u6765\uff0c \u71d5\u8001\u5f1f\u77e5\u9053\u8fd9\u4e2a\u5730\u65b9\uff0c\u4e3a\u4e86\u4fdd\u6301\u9690\u5bc6\uff0c\u975e\u5fc5\u8981\uff0c\u4ed6\u662f\u4e0d\u4f1a\u6765\u7684\u3002\u201d\u8bf4\u7740\uff0c\u4ed6\u77a7\u4e86\u71d5\u592b\u4eba\u4e00 \u773c\uff0c\u7136\u540e\uff0c\u5e26\u7740\u5c0f\u71d5\u5b50\u79bb\u5f00\u4e86\u5c01\u5bb6\u5821\u3002 \u3000\u3000\u71d5\u592b\u4eba\u76f4\u6123\u6123\u7684\u76ef\u7740\u5c0f\u71d5\u5b50\uff0c\u76f4\u5230\u4e0d\u89c1\u8e2a\u5f71\u3002 \u5367\u9f99\u751f\u300a\u71d5\u5b50\u4f20\u5947\u300b \u7b2c \u56db \u56de\u3000\u8ffd\u9b42\u4e03\u715e \u3000\u3000\u738b\u91cd\u5c71\u4e5f\u6ca1\u6709\u591c\u63a2\u8d75\u4e8c\u5824\u7684\u5b85\u9662\uff0c\u6c5f\u5343\u91cc\u544a\u8bc9\u4ed6\u4e0d\u5b9c\u6253\u8349\u60ca\u86c7\uff0c\u7b49\u4eac\u91cc\u63f4\u624b\u8d76\u6765\uff0c\u518d\u4f5c \u884c\u52a8\uff0c\u8981\u4ed6\u548c\u71d5\u98de\u5408\u4f5c\uff0c\u770b\u770b\u9a6c\u5de1\u629a\u7684\u53cd\u5e94\uff1f\u4ed6\u8981\u5355\u72ec\u8ffd\u67e5\u4e00\u4e9b\u7ebf\u7d22\uff0c\u4f1a\u5c3d\u5feb\u8d76\u56de\u5f00\u5c01\u3002 \u3000\u3000\u738b\u91cd\u5c71\u5fc3\u4e2d\u867d\u7136\u6709\u5f88\u591a\u7591\u95ee\uff0c\u4f46\u5374\u4e5f\u6ca1\u6709\u591a\u95ee\u3002 \u3000\u3000\u81ea\u4ece\u4e09\u516c\u4e3b\u5931\u8e2a\u4e4b\u540e\uff0c\u738b\u91cd\u5c71\u5df2\u5f00\u59cb\u7528\u8111\u7b4b\u601d\u7d22\u4e8b\u60c5\uff0c\u4ed6\u51fa\u8eab\u6b63\u5927\u95e8\u6237\uff0c\u5185\u5fc3\u4e2d\u9690\u6f5c\u7740 \u4e00\u80a1\u50b2\u6c14\u3002 \u3000\u3000\u4ed6\u5bf9\u4e00\u822c\u6c5f\u6e56\u4eba\u7269\u6709\u4e9b\u8f7b\u89c6\uff0c\u4ed6\u6839\u672c\u5c31\u4e0d\u592a\u76f8\u4fe1\u6c5f\u5343\u91cc\u7684\u8ffd\u8e2a\u4e4b\u672f\uff0c\u4e5f\u4e00\u76f4\u4e0d\u8ba4\u4e3a\u4e09\u516c \u4e3b\u4f1a\u6f5c\u85cf\u5728\u8d75\u4e8c\u5824\u7684\u5bb6\u4e2d\u3002 \u3000\u3000\u7136\u800c\uff0c\u4ed6\u56e0\u4e3a\u60f3\u4e0d\u51fa\u8ffd\u67e5\u4e09\u516c\u4e3b\u884c\u8e2a\u7684\u529e\u6cd5\uff0c\u6240\u4ee5\uff0c\u53ea\u597d\u542c\u4ece\u6c5f\u5343\u91cc\u505a\u7684\u5b89\u6392\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u6d1e\u5bdf\u7ec6\u5fae\uff0c\u65e9\u5df2\u77a7\u51fa\u738b\u91cd\u5c71\u5fc3\u91cc\u9690\u4f0f\u4e00\u80a1\u4e0d\u670d\u4e4b\u6c14\uff0c\u539f\u672c\u60f3\u627e\u51fa\u4e09\u516c\u4e3b\u4e0b\u843d\u4e4b \u540e\uff0c\u8ba9\u4ed6\u4e3b\u52a8\u7684\u5fc3\u670d\u3001\u53e3\u670d\u3002 \u3000\u3000\u4f46\u662f\uff0c\u674e\u9752\u7af9\u603b\u5ad6\u5934\u7684\u4e00\u756a\u8bdd\uff0c\u4f7f\u6574\u4e2a\u7684\u8ba1\u5212\u6539\u53d8\u4e86\uff0c\u6c5f\u5343\u91cc\u5c31\u5229\u7528\u738b\u91cd\u5c71\u90a3\u80a1\u5fc3\u5b58\u7684 \u50b2\u6c14\uff0c\u60f3\u4f7f\u738b\u91cd\u5c71\u81ea\u52a8\u7684\u627f\u62c5\u8d77\u5927\u90e8\u4efd\u7684\u8d23\u4efb\uff0c\u4ee5\u758f\u89e3\u71d5\u98de\u4e4b\u5371\u3002 \u3000\u3000\u679c\u7136\uff0c\u6c5f\u5343\u91cc\u79bb\u53bb\u4e4b\u540e\uff0c\u738b\u91cd\u5c71\u5c31\u8868\u73b0\u51fa\u4e0d\u6ee1\u7684\u795e\u8272\uff0c\u6447\u6447\u5934\uff0c\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u71d5\u603b\u6355 \u5934\uff0c\u4f60\u76f8\u4e0d\u76f8\u4fe1\u4e09\u516c\u4e3b\u88ab\u4eba\u63b3\u85cf\u5728\u8d75\u4e8c\u5824\u7684\u5bb6\u4e2d\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u71d5\u98de\u65e9\u5df2\u548c\u6c5f\u5343\u91cc\u7814\u5546\u51b3\u5b9a\uff0c\u8981\u770b\u738b\u91cd\u5c71\u5982\u4f55\u53cd\u5e94\uff0c\u518d\u4f9d\u8ba1\u5e94\u4ed8\uff0c\u4e8e\u662f\u4ed6\u76ee\u6ce8\u738b\u91cd\u5c71\u8138 \u4e0a\u795e\u8272\u7684\u53d8\u5316\uff0c\u7f13\u7f13\u5730\u53c8\u9053\uff1a\u201c\u6c5f\u6e56\u76db\u4f20\uff0c\u6c5f\u524d\u8f88\u7684\u8ffd\u8e2a\u8e29\u8ff9\u4e4b\u672f\u65e0\u4eba\u80fd\u51fa\u5176\u53f3\uff0c\u9a6c\u5927\u4eba\u53c8 \u793c\u8d24\u4e0b\u58eb\uff0c\u91cd\u91d1\u793c\u8058\uff0c\u4ed6\u8eab\u8d1f\u91cd\u4efb\uff0c\u5728\u4e0b\u4e5f\u53ea\u6709\u4ece\u5176\u6240\u547d\u4e86\u3002\u201d \u3000\u3000\u201c\u6c5f\u6e56\u4f20\u8a00\uff0c\u5c82\u53ef\u5168\u4fe1\u2026\u2026\u201d\u738b\u91cd\u5c71\u6447\u6447\u5934\u9053\uff1a\u201c\u738b\u67d0\u5341\u5206\u7559\u5fc3\u89c2\u5bdf\uff0c\u5b9e\u5728\u770b\u4e0d\u51fa\u4ed6\u7528 \u7684\u662f\u4ec0\u4e48\u65b9\u6cd5\uff0c\u8ffd\u67e5\u5230\u8d75\u5bb6\u8fd9\u5ea7\u5b85\u9662\uff0c\u6b66\u6797\u4e2d\u5404\u5927\u95e8\u6d3e\uff0c\u4e5f\u6709\u8e29\u8e2a\u89c5\u8ff9\u7684\u6280\u5de7\uff0c\u4f46\u8981\u4f9d\u636e\u7ebf \u7d22\uff0c\u62bd\u4e1d\u5265\u8327\uff0c\u624d\u80fd\u6709\u6240\u6536\u83b7\uff0c\u4f46\u90a3\u4f4d\u6c5f\u524d\u8f88\u5374\u6545\u4f5c\u5356\u5f04\uff0c\u627e\u5230\u4e86\u8d75\u5bb6\u5b85\u9662\uff0c\u7ea6\u597d\u4e86\u591c\u63a2\u8d75 \u5b85\uff0c\u5374\u53c8\u53cd\u590d\u81ea\u5706\uff0c\u4e0d\u8ba9\u6211\u6253\u8349\u60ca\u86c7\uff0c\u81ea\u884c\u79bb\u53bb\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u54b1\u4eec\u8be5\u5982\u4f55\u4ea4\u4ee3\uff1f\u5012\u771f\u662f\u8981\u8d39\u4e9b \u601d\u91cf\u4e86\u3002\u201d \u3000\u3000\u71d5\u98de\u5fc3\u4e2d\u6697\u9053\uff1a\u679c\u7136\u59dc\u8fd8\u662f\u8001\u7684\u8fa3\uff0c\u6c5f\u5343\u91cc\u5224\u4e8b\u3001\u8ba4\u4eba\u4e4b\u660e\uff0c\u975e\u6211\u80fd\u53ca\uff0c\u738b\u91cd\u5c71\u7684\u4e0d\u6ee1 \u53cd\u5e94\uff0c\u5012\u662f\u5168\u5728\u4ed6\u7684\u610f\u6599\u4e4b\u4e2d\u4e86\uff1f \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u91cc\u5374\u8bf4\u9053\uff1a\u201c\u738b\u5144\uff0c\u6709\u4f55\u9ad8\u89c1\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u60f3\u2026\u2026\u54b1\u4eec\u5e94\u8be5\u5148\u53bb\u89c1\u89c1\u9a6c\u5de1\u629a\u3002\u201d \u3000\u3000\u201c\u53bb\u89c1\u9a6c\u5927\u4eba\uff1f\u201d \u3000\u3000\u201c\u5bf9\uff01\u6c5f\u5343\u91cc\u8fd9\u4e00\u8d70\uff0c\u4e0d\u77e5\u51e0\u65f6\u624d\u80fd\u56de\u6765\uff0c\u62d6\u5ef6\u65f6\u65e5\u592a\u4e45\uff0c\u53ea\u6015\u4f60\u71d5\u603b\u6355\u5934\u65e0\u6cd5\u5411\u5de1\u629a \u5e9c\u4ea4\u4ee3\uff0c\u738b\u67d0\u4e4b\u610f\u662f\u5148\u5411\u9a6c\u5de1\u629a\u56de\u62a5\u3002\u201d \u3000\u3000\u201c\u628a\u4e8b\u60c5\u5168\u63a8\u5230\u6c5f\u5343\u91cc\u7684\u8eab\u4e0a\uff1f\u2026\u2026\u201d \u3000\u3000\u738b\u91cd\u5c71\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5ef6\u8bef\u4e8b\u673a\u4e4b\u7f6a\uff0c\u6050\u975e\u5c0f\u53ef\uff0c\u71d5\u5144\u53ea\u6015\u4e5f\u625b\u4e0d\u8d77\u6765\uff0c\u6c5f\u5343\u91cc\u662f\u9a6c \u5de1\u629a\u91cd\u91d1\u793c\u8058\u7684\u4eba\uff0c\u800c\u4e14\u662f\u5f85\u7f6a\u7acb\u529f\u7684\u56da\u72af\uff0c\u8fd9\u4e00\u70b9\uff0c\u9a6c\u5de1\u629a\u4e5f\u5f88\u96be\u81ea\u5706\u5176\u8bf4\uff0c\u71d5\u5144\u7ad9\u4f4f\u4e86 \u4e00\u4e2a\u7406\u5b57\uff0c\u5c31\u7b97\u9a6c\u5de1\u629a\u60f3\u79fb\u82b1\u63a5\u6728\u3001\u5ac1\u7978\u4ee3\u7f6a\uff0c\u4e5f\u8981\u4e09\u601d\u800c\u884c\u4e86\u3002\u201d \u3000\u3000\u201c\u6709\u9053\u7406\u2026\u2026\u201d\u71d5\u98de\u5012\u662f\u771f\u7684\u6709\u70b9\u4f69\u670d\u4e86\u3002 \u3000\u3000\u738b\u91cd\u5c71\u6ca1\u6709\u6c5f\u6e56\u7ecf\u9a8c\uff0c\u4f46\u8fd9\u4e24\u5e74\u5185\u5bab\u4f8d\u536b\u7684\u7ecf\u5386\uff0c\u5012\u662f\u5bf9\u5ba6\u6d77\u4e2d\u7684\u9669\u6076\uff0c\u6709\u4e86\u5f88\u6df1\u7684\u8ba4 \u8bc6\uff0c\u71d5\u98de\u70b9\u70b9\u5934\uff0c\u53c8\u9053\uff1a\u201c\u54b1\u4eec\u8fd9\u5c31\u56de\u5f00\u5c01\u5e9c\u53bb\uff0c\u5148\u5411\u77e5\u5e9c\u5927\u4eba\u7980\u62a5\u3002\u201d \u3000\u3000\u5c39\u77e5\u5e9c\u542c\u8bf4\u6c5f\u5343\u91cc\u501f\u6545\u79bb\u53bb\uff0c\u5413\u5f97\u5dee\u4e00\u70b9\u6655\u4e86\u8fc7\u53bb\uff0c\u7acb\u523b\u643a\u71d5\u98de\u548c\u738b\u91cd\u5c71\u8d76\u5f80\u5de1\u629a\u5e9c\uff0c \u664b\u89c1\u4e86\u9a6c\u5de1\u629a\u3002 \u3000\u3000\u71d5\u98de\u662f\u5c3d\u91cf\u9000\u907f\uff0c\u738b\u91cd\u5c71\u633a\u8eab\u800c\u51fa\uff0c\u9053\uff1a\u201c\u6c5f\u5343\u91cc\u81ea\u884c\u8ffd\u7d22\u654c\u8e2a\u800c\u53bb\uff0c\u8bf7\u5de1\u629a\u5927\u4eba\u88c1 \u793a\uff0c\u8be5\u5982\u4f55\u5904\u7f6e\uff1f\u201d \u3000\u3000\u9a6c\u5de1\u629a\u51fa\u5947\u7684\u51b7\u9759\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6c5f\u5343\u91cc\u662f\u6c5f\u6e56\u4e0a\u77e5\u540d\u7684\u4eba\u7269\uff0c\u4e0d\u81f3\u4e8e\u6bc1\u8bfa\u80cc\u4fe1\u3001 \u7f6e\u4e4b\u4e0d\u7406\u5427\uff01\u201d \u3000\u3000\u738b\u91cd\u5c71\u9053\uff1a\u201c\u5927\u4eba\u7684\u610f\u601d\u662f\uff0c\u9759\u5019\u6c5f\u5343\u91cc\u7684\u56de\u97f3\u4e86\uff1f\u201d \u3000\u3000\u201c\u90a3\u5012\u4e0d\u662f\u2026\u2026\u201d\u9a6c\u5de1\u629a\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u884c\u8e2a\u4e0d\u660e\uff0c\u672c\u629a\u5fc3\u6025\u5982\u711a\uff0c\u5723\u4e0a\u4e00\u65e6\u8ffd\u7a76\uff0c\u53ef\u80fd \u662f\u706d\u95e8\u8bdb\u65cf\u7684\u5927\u7f6a\uff0c\u4f46\u8fd9\u79cd\u5168\u65e0\u7ebf\u7d22\u7684\u6848\u60c5\uff0c\u672c\u629a\u867d\u6709\u8ffd\u67e5\u4e4b\u5fc3\uff0c\u5374\u6709\u65e0\u5904\u7740\u624b\u4e4b\u611f\uff0c\u738b\u4f8d \u536b\u4f55\u4ee5\u6559\u6211\uff01\u201d \u3000\u3000\u71d5\u98de\u6697\u6697\u5fd6\u9053\uff1a\u597d\u5229\u5bb3\u7684\u4e00\u8019\u5b50\uff0c\u5982\u975e\u5fc3\u4e2d\u65e9\u5df2\u6709\u6570\uff0c\u771f\u4f1a\u8bef\u8ba4\u4ed6\u662f\u4f4d\u4e0d\u803b\u4e0b\u95ee\u7684\u541b\u5b50 \u5462\uff01 \u3000\u3000\u738b\u91cd\u5c71\u4e00\u633a\u80f8\uff0c\u9053\uff1a\u201c\u5927\u4eba\u5782\u8be2\uff0c\u738b\u67d0\u6597\u80c6\u8fdb\u8a00\u3002\u6c5f\u5343\u91cc\u5355\u4eba\u72ec\u884c\uff0c\u662f\u5426\u80fd\u8ffd\u67e5\u51fa\u4e09\u516c \u4e3b\u7684\u4e0b\u843d\uff0c\u4ee5\u62a5\u5927\u4eba\u793c\u8058\u4e4b\u60c5\uff0c\u6682\u4e0d\u7f6e\u8bba\uff0c\u4e3a\u4eca\u4e4b\u8ba1\uff0c\u5374\u4e0d\u5b9c\u5750\u5f85\u8baf\u606f\uff0c\u6d6a\u8d39\u65f6\u95f4\u2026\u2026\u201d \u3000\u3000\u9a6c\u5de1\u629a\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5bf9\uff0c\u5bf9\uff01\u738b\u4f8d\u536b\u8981\u672c\u629a\u5982\u4f55\u652f\u63f4\uff0c\u53ea\u7ba1\u63d0\u51fa\u6765\uff0c\u53ea\u8981\u672c\u629a\u529b\u6240\u80fd \u53ca\uff0c\u5b9a\u7136\u5168\u529b\u4ee5\u8d74\u3002\u201d \u3000\u3000\u738b\u91cd\u5c71\u5927\u4e3a\u611f\u52a8\uff0c\u5fd6\u9053\uff1a\u4ed6\u4e00\u65b9\u5927\u5458\uff0c\u5982\u6b64\u5e73\u548c\u8c26\u8ba9\uff0c\u679c\u7136\u662f\u96be\u5f97\u4e00\u89c1\u7684\u597d\u5b98\u3002\u5f53\u4e0b\u8bf4 \u9053\uff1a\u201c\u5927\u4eba\u4e5f\u4e0d\u5fc5\u592a\u8fc7\u5fe7\u8651\uff0c\u5728\u4e0b\u6d3e\u4e24\u4f4d\u540c\u50da\u56de\u4eac\u4e4b\u65f6\uff0c\u5df2\u5bc6\u5631\u4ed6\u4eec\u606d\u8bf7\u5185\u5bab\u7edf\u9886\u4eb2\u81ea\u5357 \u4e0b\uff0c\u5feb\u9a6c\u6025\u8d76\uff0c\u65e5\u591c\u517c\u7a0b\uff0c\u5341\u65e5\u4e4b\u5185\uff0c\u5b9a\u53ef\u8d76\u5230\u2014\u2026\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u8bf4\uff0c\u738b\u7edf\u9886\u738b\u5927\u4eba\u8981\u4eb2\u81ea\u8d76\u6765\u2026\u2026\u201d\u9a6c\u5de1\u629a\u6025\u6025\u5730\u95ee\uff1a\u201c\u8981\u6765\u67e5\u529e\u6b64\u6848\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u4e09\u516c\u4e3b\u91d1\u6280\u5de5\u53f6\uff0c\u8eab\u4efd\u9ad8\u8d35\uff0c\u518d\u52a0\u4e0a\u738b\u67d0\u7684\u8bf7\u6c42\uff0c\u6211\u76f8\u4fe1\u738b\u7edf\u9886\u4e00\u5b9a\u4f1a\u4eb2\u81ea\u5357 \u4e0b\u3002\u201d \u3000\u3000\u201c\u8fd9\u2026\u2026\u8fd9\u6211\u5c31\u653e\u5fc3\u4e86\uff0c\u4e0d\u8fc7\uff0c\u8fd8\u8981\u5341\u65e5\u4e4b\u4e45\u738b\u7edf\u9886\u624d\u80fd\u8d76\u5230\uff0c\u4e09\u516c\u4e3b\u4f1a\u4e0d\u4f1a\u6709\u4ec0\u4e48\u5371 \u9669\u5462\uff1f\u201d\u9a6c\u5de1\u629a\u65e0\u9650\u7684\u5fe7\u82e6\u3002 \u3000\u3000\u201c\u8fd9\u5c31\u96be\u8bf4\u4e86\u2026\u2026\u201d\u738b\u91cd\u5c71\u9053\uff1a\u201c\u4f46\u4ed6\u4eec\u63b3\u53bb\u4e09\u516c\u4e3b\uff0c\u4e5f\u8be5\u65e9\u77e5\u5979\u5c0a\u8d35\u7684\u8eab\u4efd\uff0c\u5584\u5f85\u4e09 \u516c\u4e3b\uff0c\u65e5\u540e\uff0c\u8fd8\u6709\u4e2a\u8ba8\u4ef7\u8fd8\u4ef7\u7684\u4f59\u5730\u3002\u201d \u3000\u3000\u9a6c\u5de1\u629a\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6709\u9053\u7406\u2026\u2026\u201d\u76ee\u5149\u7a81\u7136\u8f6c\u5230\u71d5\u98de\u7684\u8138\u4e0a\uff0c\u9053\uff1a\u201c\u71d5\u603b\u6355\u5934\uff0c\u6c5f\u5343 \u91cc\u79bb\u53bb\u4e4b\u65f6\uff0c\u53ef\u6709\u4ec0\u4e48\u4ea4\u4ee3\uff1f\u201d \u3000\u3000\u201c\u6c5f\u5927\u4fa0\u4e34\u53bb\u4e4b\u9645\uff0c\u5f53\u738b\u4f8d\u536b\u4e4b\u9762\u4ea4\u4ee3\u5c5e\u4e0b\uff0c\u4ed6\u72ec\u81ea\u8ffd\u7d22\u654c\u8e2a\uff0c\u8981\u5c5e\u4e0b\u7559\u5728\u5f00\u5c01\u7b49\u4ed6\uff0c \u4ed6\u4f1a\u5c3d\u5feb\u8d76\u56de\u6765\u3002\u201d \u3000\u3000\u9a6c\u5de1\u629a\u9053\uff1a\u201c\u4ed6\u8bf4\u8fc7\u54ea\u4e00\u5929\u56de\u6765\u4e48\uff1f\u201d \u3000\u3000\u71d5\u98de\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u6ca1\u6709\uff01\u201d \u3000\u3000\u201c\u672c\u629a\u6709\u4e9b\u60f3\u4e0d\u901a\u4e86\uff0c\u4ed6\u8981\u8ffd\u7d22\u4e09\u516c\u4e3b\u7684\u8e2a\u8ff9\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u5e26\u4f60\u548c\u738b\u4f8d\u536b\u4e00\u8d77\u53bb\u5462\uff1f\u201d\u9a6c \u5de1\u629a\u795e\u8272\u51b7\u8083\u5730\u9053\uff1a\u201c\u738b\u4f8d\u536b\u662f\u4fdd\u62a4\u516c\u4e3b\u7684\u4eba\uff0c\u4f60\u4e5f\u662f\u5f00\u5c01\u5e9c\u7684\u603b\u6355\u5934\u3002\u201d \u3000\u3000\u201c\u56de\u5927\u4eba\uff0c\u6c5f\u5343\u91cc\u53d7\u547d\u62c5\u8d1f\u8d77\u91cd\u8d23\u5927\u4efb\u2026\u2026\u201d\u71d5\u98de\u80f8\u6709\u6210\u7af9\u7684\u9053\uff1a\u201c\u5351\u804c\u53ea\u6709\u542c\u547d\u884c \u4e8b\uff0c\u4e0d\u6562\u963b\u6270\u4ed6\u7684\u51b3\u5b9a\u3002\u201d \u3000\u3000\u201c\u5509\uff01\u8fd9\u662f\u672c\u629a\u7684\u758f\u5ffd\u4e86\u2026\u2026\u201d\u9a6c\u5de1\u629a\u76ee\u5149\u8f6c\u6ce8\u5728\u738b\u91cd\u5c71\u7684\u8138\u4e0a\uff0c\u9053\uff1a\u201c\u738b\u4f8d\u536b\u5bf9\u6b64\u4e8b \u770b\u6cd5\u5982\u4f55\uff1f\u201d \u3000\u3000\u738b\u91cd\u5c71\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u6c5f\u5343\u91cc\u4e34\u53bb\u4e4b\u9645\uff0c\u8981\u6211\u548c\u71d5\u98de\u5408\u4f5c\uff0c\u770b\u770b\u9a6c\u5927\u4eba\u7684\u53cd\u5e94\uff0c\u8bdd\u4e2d \u542b\u610f\uff0c\u5f26\u5916\u6709\u97f3\uff0c\u53ea\u4e0d\u77e5\u4ed6\u7528\u5fc3\u4f55\u5728\uff1f\u7acb\u523b\u63d0\u9ad8\u8b66\u89c9\uff0c\u9053\uff1a\u201c\u6c5f\u6e56\u4eba\u7269\uff0c\u6709\u4ed6\u4eec\u5904\u4e8b\u7684\u6cd5 \u5219\uff0c\u8fd9\u548c\u5b98\u573a\u4e0d\u540c\uff0c\u6c5f\u5343\u91cc\u4e5f\u8bb8\u4f1a\u53bb\u800c\u590d\u8fd4\uff1f\u4ed6\u53d7\u4e4b\u4e8e\u5de1\u629a\u5927\u4eba\uff0c\u4e8e\u60c5\u4e8e\u7406\u90fd\u8be5\u6709\u6240\u4ea4\u4ee3\u624d \u662f\u2026\u2026\u201d \u3000\u3000\u6697\u4e2d\u7422\u78e8\u4e86\u534a\u5929\u7684\u5c39\u77e5\u5e9c\uff0c\u7a81\u7136\u63a5\u4e86\u53e3\uff0c\u9053\uff1a\u201c\u6c5f\u5343\u91cc\u5b50\u7136\u4e00\u8eab\uff0c\u56db\u6d77\u6f02\u6cca\uff0c\u5c45\u65e0\u5b9a \u5904\uff0c\u53bb\u65e0\u5b9a\u65b9\uff0c\u8ffd\u89c5\u516c\u4e3b\u4e00\u6848\uff0c\u4e0d\u53ef\u518d\u4ef0\u4ed7\u4e8e\u4ed6\u3002\u201d \u3000\u3000\u9a6c\u5de1\u629a\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8d35\u5e9c\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u7948\u76fc\u738b\u7edf\u9886\u65e9\u65e5\u7387\u4eba\u8d76\u5230\uff0c\u62c5\u8d77\u5927\u4efb\uff0c\u6bd5\u7adf\u5185\u5bab\u4f8d\u536b\u624d\u662f\u771f\u6b63\u8d1f\u8d23\u4fdd\u62a4\u516c\u4e3b\u7684\u4eba\u3002\u201d \u5c39\u77e5\u5e9c\u770b\u4e86\u738b\u91cd\u5c71\u4e00\u773c\uff0c\u9053\uff1a\u201c\u738b\u5c11\u5144\u8ba4\u4e3a\u672c\u5e9c\u4e4b\u8a00\uff0c\u662f\u5426\u6709\u7406\uff1f\u201d \u3000\u3000\u738b\u91cd\u5c71\u5fc3\u4e2d\u5fd6\u9053\uff1a\u53ef\u6076\u3002\u8fd8\u672a\u5230\u62ff\u95ee\u8fc7\u5802\u7684\u8fb0\u5149\uff0c\u5df2\u5f00\u59cb\u5378\u8d23\u5ac1\u7978\uff0c\u4f46\u4ed6\u5e74\u8f7b\u6c14\u76db\uff0c\u53c8 \u4ed7\u540e\u53f0\u624e\u5b9e\uff0c\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u738b\u67d0\u81ea\u6709\u4fdd\u62a4\u4e0d\u5468\u7684\u7f6a\u8d23\u2026\u2026\u201d \u3000\u3000\u5c39\u77e5\u5e9c\u9053\uff1a\u201c\u738b\u5c11\u5144\u679c\u7136\u662f\u82f1\u96c4\u6c14\u6168\uff0c\u4e0b\u5b98\u662f\u4e07\u4e07\u4e0d\u53ca\uff0c\u4e07\u4e07\u4e0d\u53ca\u7684\u3002\u201d \u3000\u3000\u8bdd\u4e2d\u6263\u8bdd\uff0c\u5f53\u771f\u662f\u8d8a\u5957\u8d8a\u7262\u3002 \u3000\u3000\u738b\u91cd\u5c71\u4f59\u97f3\u672a\u5c3d\uff0c\u5374\u88ab\u8fd9\u9876\u5927\u5e3d\u5b50\u6263\u5f97\u4e00\u5446\uff0c\u4e00\u65f6\u95f4\u7adf\u7136\u4e5f\u60f3\u4e0d\u51fa\u5982\u4f55\u63aa\u8bcd\u56de\u7b54\u3002 \u3000\u3000\u9a6c\u5de1\u629a\u5374\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u867d\u65e0\u5723\u8c15\u4e0b\u8fbe\u8981\u54b1\u4eec\u6d3e\u4eba\u4fdd\u62a4\uff0c\u4f46\u4e09\u516c\u4e3b\u5728\u6b64\u5730\u5931\u8e2a\uff0c\u672c\u5ea7 \u548c\u5c39\u77e5\u5e9c\u90fd\u6709\u758f\u5ffd\u5931\u5bdf\u4e4b\u8d23\uff0c\u71d5\u603b\u6355\u8d1f\u6709\u7ee5\u9756\u5730\u65b9\u7684\u804c\u53f8\uff0c\u7adf\u5bb9\u6c5f\u6d0b\u5927\u76d7\u7559\u5c45\u5f00\u5c01\uff0c\u5b9e\u5728\u662f \u7f6a\u4e0d\u53ef\u8d66\u2026\u2026\u201d \u3000\u3000\u5c39\u77e5\u5e9c\u9053\uff1a\u201c\u5927\u4eba\u8bf4\u7684\u662f\uff0c\u6765\u4eba\u54ea\uff01\u628a\u71d5\u98de\u62ff\u4e0b\u3002\u201d \u3000\u3000\u4e24\u4e2a\u5de1\u629a\u5e9c\u4e2d\u7684\u6355\u5feb\uff0c\u5e94\u58f0\u800c\u5165\uff0c\u7acb\u523b\u66ff\u71d5\u98de\u52a0\u4e0a\u4e86\u5211\u5177\u3002 \u3000\u3000\u516d\u6708\u5929\u53d8\u5f97\u597d\u5feb\uff0c\u8bf4\u7ffb\u8138\u5c31\u7ffb\u8138\u3002 \u3000\u3000\u71d5\u98de\u5401\u4e00\u53e3\u6c14\uff0c\u6b63\u60f3\u51fa\u8a00\u8fa9\u89e3\uff0c\u9a6c\u5de1\u629a\u5374\u51b7\u51b7\u63a5\u4e86\u4e00\u53e5\uff1a\u201c\u4f46\u613f\u5c0f\u71d5\u5b50\u4e0d\u8981\u53d7\u5230\u682a\u8fde\u624d \u597d\u3002\u201d \u3000\u3000\u71d5\u98de\u5fc3\u5934\u5927\u9707\uff0c\u8eac\u8eab\u8bf4\u9053\uff1a\u201c\u5927\u4eba\u6069\u5178\uff01\u201d\u5782\u4e0b\u5934\u53bb\u3002 \u3000\u3000\u738b\u91cd\u5c71\u4e0d\u77e5\u9053\u5c0f\u71d5\u5b50\u7684\u4e8b\uff0c\u4f46\u76ee\u7779\u5b98\u573a\u4e0a\u7684\u9669\u6076\u53d8\u5316\uff0c\u4ea6\u81ea\u5fc3\u60ca\uff0c\u4ed6\u539f\u60f3\u8bf4\u51fa\u6c5f\u5343\u91cc\u8ffd \u5230\u8d75\u5bb6\u5b85\u9662\u7684\u4e8b\uff0c\u4e5f\u7a81\u7136\u6539\u53d8\u4e86\u4e3b\u610f\u3002 \u3000\u3000\u6b64\u65f6\u6b64\u60c5\uff0c\u597d\u4f3c\u4fdd\u7559\u4e00\u4efd\u9690\u5bc6\uff0c\u4f3c\u5c31\u80fd\u591a\u4e00\u4efd\u4fdd\u969c\uff0c\u8bed\u6c14\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u5e9c\u53f0\u5927\u4eba\u62ff\u4e0b\u71d5 \u603b\u6355\u5934\uff0c\u4e0d\u77e5\u8981\u4f55\u4eba\u5e2e\u52a9\u738b\u67d0\u8ffd\u67e5\u6848\u60c5\u3002\u201d \u3000\u3000\u5c39\u77e5\u5e9c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5982\u82e5\u738b\u4f8d\u536b\u89c9\u5f97\u71d5\u98de\u786e\u6709\u534f\u529e\u6b64\u6848\u7684\u80fd\u529b\uff0c\u672c\u5e9c\u6cd5\u5916\u65bd\u6069\uff0c\u51c6\u71d5 \u98de\u5e26\u7f6a\u7acb\u529f\uff0c\u4e0d\u8fc7\uff0c\u5c31\u6cd5\u8bba\u4e8b\uff0c\u8981\u4e66\u5199\u4e00\u5f20\u4fdd\u72b6\u624d\u884c\u3002\u201d \u3000\u3000\u201c\u4ea4\u4fdd\u529e\u6848\u2026\u2026\u201d\u738b\u91cd\u5c71\u6709\u4e9b\u832b\u7136\u7684\u8bf4\uff1a\u201c\u90a3\u8981\u8c01\u4f5c\u4fdd\u4eba\uff1f\u201d \u3000\u3000\u201c\u534f\u52a9\u4f60\u738b\u4f8d\u536b\u529e\u6848\uff0c\u5f53\u7136\u4f60\u662f\u6700\u597d\u7684\u4fdd\u4eba\u2026\u2026\u201d\u5c39\u77e5\u5e9c\u4f4e\u58f0\u5730\u8bf4\u9053\uff1a\u201c\u60c5\u975e\u5f97\u5df2\uff0c\u738b \u5c11\u5144\u8981\u591a\u591a\u62c5\u5f85\u2026\u2026\u201d \u3000\u3000\u738b\u91cd\u5c71\u6123\u4f4f\u4e86\uff0c\u5199\u4e0b\u8fd9\u5f20\u4fdd\u72b6\uff0c\u4ed6\u5c31\u8981\u62c5\u8d1f\u8d77\u71d5\u98de\u7684\u4e00\u5207\u8d23\u4efb\u3002 \u3000\u3000\u8f6c\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u71d5\u98de\u95ed\u76ee\u800c\u7acb\uff0c\u5b8c\u5168\u77a7\u4e0d\u51fa\u4ed6\u7684\u5fc3\u610f\u3002 \u3000\u3000\u518d\u770b\u9a6c\u5de1\u629a\uff0c\u4ed6\u4e00\u8138\u51b7\u8083\uff0c\u597d\u50cf\u51b0\u96ea\u8986\u76d6\u822c\uff0c\u66f4\u662f\u77a7\u4e0d\u51fa\u5fc3\u4e2d\u4e00\u70b9\u7684\u8baf\u606f\u3002 \u3000\u3000\u738b\u91cd\u5c71\u65e0\u5948\uff0c\u53ea\u597d\u4e00\u54ac\u7259\uff0c\u9053\uff1a\u201c\u597d\uff01\u738b\u67d0\u5c31\u4f5c\u4e2a\u4fdd\u4eba\u5427\uff01\u201d \u3000\u3000\u201c\u738b\u5c11\u5144\u8c6a\u6c14\u4e8e\u4e91\uff0c\u672c\u5e9c\u81ea\u5f53\u6cd5\u5916\u65bd\u4ec1\uff0c\u62ff\u4fdd\u72b6\u6765\u2026\u2026\u201d\u5c39\u77e5\u5e9c\u58f0\u97f3\u53d8\u5f97\u5341\u5206\u67d4\u548c\uff0c \u9053\uff1a\u201c\u8bf7\u738b\u4f8d\u536b\u5728\u4fdd\u72b6\u4e0a\u5212\u62bc\u3002\u201d \u3000\u3000\u4e00\u5207\u90fd\u65e9\u5df2\u51c6\u5907\u59a5\u5f53\uff0c\u7acb\u523b\u6709\u6587\u6848\u5949\u4e0a\u4fdd\u72b6\u3002 \u3000\u3000\u738b\u91cd\u5c71\u8bfb\u8fc7\u4e66\uff0c\u770b\u90a3\u4fdd\u72b6\u4e0a\u5199\u5f97\u5341\u5206\u4e25\u5389\uff0c\u8981\u62c5\u4fdd\u71d5\u98de\u968f\u4f20\u968f\u5230\uff0c\u4f46\u4e8b\u5df2\u81f3\u6b64\uff0c\u65e0\u6cd5\u6539 \u53e3\uff0c\u53ea\u597d\u5728\u4fdd\u72b6\u4e0a\u5212\u62bc\u3002 \u3000\u3000\u5c39\u77e5\u5e9c\u6536\u4e86\u4fdd\u72b6\uff0c\u7b11\u9053\uff1a\u201c\u597d\uff0c\u71d5\u98de\u5c31\u4ea4\u7ed9\u738b\u5c11\u5144\u4e86\u3002\u677e\u4e0b\u5211\u5177\uff01\u201d \u3000\u3000\u71d5\u98de\u8c22\u8fc7\u5c39\u77e5\u5e9c\u548c\u9a6c\u5de1\u629a\uff0c\u7acb\u523b\u9000\u53bb\u3002 \u3000\u3000\u738b\u91cd\u5c71\u7d27\u968f\u71d5\u98de\u8eab\u540e\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u71d5\u5144\uff0c\u6025\u4ec0\u4e48\uff1f\u5144\u5f1f\u8fd8\u6709\u8bdd\u8981\u8bf7\u6559\u8bf7\u6559\u5c39\u77e5\u5e9c\u3002\u201d \u3000\u3000\u201c\u5148\u79bb\u5f00\u8fd9\u91cc\u518d\u8bf4\uff01\u201d\u71d5\u98de\u52a0\u5feb\u811a\u6b65\uff0c\u79bb\u5f00\u5de1\u629a\u8859\u95e8\u3002 \u3000\u3000\u7136\u540e\u4ed6\u76f4\u5954\u4e1c\u5927\u8857\u9ad8\u5347\u5ba2\u6808\uff0c\u8981\u4e86\u4e00\u4e2a\u8de8\u9662\u4e0a\u623f\uff0c\u5429\u5490\u4e86\u5e97\u4f19\u53bb\u51c6\u5907\u9152\u83dc\uff0c\u624d\u5401\u4e86\u4e00\u53e3 \u6c14\uff0c\u9053\uff1a\u201c\u738b\u5144\uff0c\u6709\u8bdd\u53ef\u4ee5\u8bf4\u4e86\u3002\u201d \u3000\u3000\u738b\u91cd\u5c71\u56db\u987e\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4e8b\u60c5\u6709\u4e9b\u5947\u602a\uff0c\u4ed6\u4eec\u597d\u50cf\u65e9\u90fd\u8bbe\u8ba1\u597d\u4e86\uff0c\u7b49\u7740\u6f14\u51fa\u8fd9\u5e55\u4ea4\u4fdd\u529e \u6848\u7684\u620f\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u4e00\u5207\u90fd\u662f\u5b89\u6392\u597d\u7684\u5708\u5957\uff0c\u7b49\u7740\u4f60\u738b\u5144\u8df3\u8fdb\u53bb\u2026\u2026\u201d\u71d5\u98de\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5176 \u5b9e\uff0c\u738b\u5144\u4e0d\u4f5c\u4fdd\u4eba\uff0c\u4ed6\u4eec\u5c31\u767d\u8d39\u5fc3\u673a\u4e86\uff0c\u4e0d\u8fc7\uff0c\u71d5\u98de\u5bf9\u738b\u5144\u4f5c\u4fdd\u7684\u4e49\u6c14\uff0c\u4ecd\u662f\u611f\u6fc0\u4e07\u5206\u3002\u201d \u3000\u3000\u201c\u8fd9\u756a\u8ba1\u8c0b\uff0c\u5168\u662f\u8bbe\u8ba1\u6211\u4e86\uff01\u201d\u738b\u91cd\u5c71\u5fff\u5fff\u4e0d\u5e73\u5730\u9053\uff1a\u201c\u5f53\u65f6\uff0c\u5982\u662f\u5144\u5f1f\u4e0d\u4f5c\u4fdd\u4eba\uff0c\u4ed6 \u4eec\u4e5f\u4f1a\u81ea\u884c\u653e\u4e86\u71d5\u5144\u4e48\uff1f\u201d\u201c\u201d\u90a3\u5012\u4e0d\u4f1a\u2026\u2026\u201c\u71d5\u98de\u9053\uff1a\u201d\u4ed6\u4eec\u4f1a\u628a\u6211\u5173\u8d77\u6765\uff0c\u4e5f\u8bb8\u8fd8\u4f1a\u7ed9 \u6211\u4e00\u70b9\u82e6\u5934\u5403\u5403\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u5c31\u5957\u4e0d\u7262\u738b\u5144\u4e86\uff0c\u738b\u7edf\u9886\u5e26\u4eba\u8d76\u5230\uff0c\u4ed6\u4eec\u4e5f\u62c5\u7740\u5f88\u5927\u7684\u7f6a\u540d\u3002 \u201c \u3000\u3000\u201c\u6211\u662f\u4e00\u5b9a\u6709\u7f6a\uff01\u4f46\u90a3\u4efd\u4fdd\u72b6\u672a\u5fc5\u5c31\u80fd\u589e\u52a0\u6211\u591a\u5c11\u7f6a\u8d23\uff0c\u4fdd\u4ed6\u4eec\u5e73\u5b89\u65e0\u4e8b\uff0c\u738b\u7edf\u9886\u76ee\u5149 \u5982\u7535\uff0c\u773c\u775b\u91cc\u5bb9\u4e0d\u4e0b\u4e00\u9897\u6c99\u5b50\uff0c\u4ed6\u4eec\u53ef\u4ee5\u7b97\u8ba1\u6211\uff0c\u4f46\u738b\u7edf\u9886\u5c31\u4e0d\u4f1a\u7ed9\u4ed6\u4eec\u5957\u4f4f\u4e86\u3002\u201d \u3000\u3000\u5e97\u5c0f\u4e8c\u9001\u4e0a\u4e86\u9152\u83dc\u3002 \u3000\u3000\u71d5\u98de\u5148\u66ff\u738b\u91cd\u5c71\u659f\u4e0a\u9152\uff0c\u6325\u6325\u624b\uff0c\u64b5\u8d70\u4e86\u5e97\u5c0f\u4e8c\uff0c\u7b11\u9053\uff1a\u201c\u6211\u60f3\uff0c\u8fd9\u53ea\u662f\u4ed6\u4eec\u8131\u7f6a\u8bbe\u8ba1 \u7684\u4e00\u90e8\u4efd\uff0c\u738b\u5144\u662f\u7edf\u9886\u7684\u81f3\u4eb2\uff0c\u4ed6\u4eec\u4e0d\u4f1a\u4ee5\u4fdd\u72b6\u8981\u80c1\u7edf\u9886\uff0c\u4f46\u5982\u5723\u4e0a\u9881\u65e8\u67e5\u529e\u8fd9\u4ef6\u6848\u5b50\uff0c\u738b \u5144\u8fd9\u4efd\u4fdd\u72b6\uff0c\u5c31\u4f1a\u5728\u5211\u90e8\u5927\u5802\u4e0a\u53d1\u751f\u4e00\u4e9b\u529b\u91cf\u4e86\uff0c\u81f3\u4e8e\u4ed6\u4eec\u8fd8\u6709\u4e9b\u4ec0\u4e48\u8bbe\u8ba1\u548c\u4fdd\u72b6\u5f7c\u6b64\u547c \u5e94\uff0c\u5728\u4e0b\u8fd8\u60f3\u4e0d\u51fa\u6765\u2026\u2026\u201d \u3000\u3000\u201c\u770b\u6765\u5ba6\u6d77\u4e2d\u7684\u673a\u8bc8\u8be1\u53d8\uff0c\u6bd4\u8d77\u6c5f\u6e56\u51f6\u9669\uff0c\u6709\u8fc7\u4e4b\u800c\u65e0\u4e0d\u53ca\u3002\u201d \u3000\u3000\u738b\u91cd\u5c71\u65e0\u9650\u611f\u6168\u5730\u9053\uff1a\u201c\u4f46\u6700\u4f7f\u738b\u67d0\u96be\u8fc7\u7684\u662f\u6c5f\u5343\u91cc\u8fd9\u4e00\u624b\uff0c\u592a\u4e0d\u591f\u4e49\u6c14\u4e86\uff0c\u627e\u4e0d\u51fa\u4e09 \u516c\u4e3b\u7684\u4e0b\u843d\uff0c\u81f3\u5c11\u4e5f\u5e94\u8be5\u7ed9\u54b1\u4eec\u4e00\u4e2a\u6697\u793a\uff0c\u50cf\u8fd9\u6837\u4e00\u8d70\u4e86\u4e4b\u672a\u514d\u6709\u5931\u539a\u9053\u3002\u201d \u3000\u3000\u71d5\u98de\u5fc3\u4e2d\u5f88\u4e3a\u96be\uff0c\u4f46\u6b64\u523b\u8fd8\u4e0d\u80fd\u8bf4\u660e\u548c\u6c5f\u5e72\u91cc\u7684\u8bbe\u8ba1\uff0c\u53ea\u597d\u652f\u543e\u4ee5\u5bf9\uff0c\u9053\uff1a\u201c\u6c5f\u524d\u8f88\u76db \u540d\u6ee1\u6b66\u6797\uff0c\u5927\u6982\u4e0d\u4f1a\u8fd9\u4e48\u4e00\u8d70\u4e86\u4e4b\uff0c\u77ed\u671f\u5185\u5b9a\u7136\u4f1a\u6709\u4e2a\u4ea4\u4ee3\u2026\u2026\u201d \u3000\u3000\u8bdd\u950b\u4e00\u8f6c\uff0c\u53c8\u9053\uff1a\u201c\u738b\u5144\uff0c\u4f60\u770b\u9a6c\u5de1\u629a\u662f\u4e0d\u662f\u6709\u4ec0\u4e48\u53ef\u7591\u7684\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u6c5f\u5343\u91cc\u4e5f\u63d0\u5230\u8fc7\u9a6c\u5de1\u629a\u2026\u2026\u201d\u738b\u91cd\u5c71\u9053\uff1a\u201c\u4f46\u4ed6\u9664\u4e86\u8138\u8272\u51b0\u5bd2\u4e00\u4e9b\u4e4b\u5916\uff0c\u6ca1\u6709\u4ec0\u4e48\u4e0d \u540c\uff0c\u4e00\u822c\u4f5c\u5927\u5b98\u7684\u90fd\u662f\u90a3\u4e2a\u6837\u5b50\uff0c\u4e0d\u559c\u522b\u4eba\u770b\u900f\u4ed6\u3002\u201d \u3000\u3000\u201c\u6211\u662f\u8bf4\uff0c\u4ed6\u662f\u4e0d\u662f\u7ec3\u8fc7\u6b66\u529f\uff1f\u201d \u3000\u3000\u738b\u91cd\u5c71\u5fae\u5fae\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u9a6c\u5de1\u629a\u4f1a\u6b66\u529f\uff1f\u5728\u4e0b\u5012\u662f\u6ca1\u6709\u77a7\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u4e09\u516c\u4e3b\u4e00\u8eab\u7edd\u6280\uff0c\u738b\u5144\u548c\u5979\u540c\u884c\u5343\u91cc\uff0c\u4e5f\u4e00\u6837\u6ca1\u6709\u77a7\u51fa\u6765\u554a\uff1f\u201d \u3000\u3000\u738b\u91cd\u5c71\u51dd\u8651\u601d\u7d22\u4e86\u7247\u523b\uff0c\u9053\uff1a\u201c\u71d5\u5144\uff0c\u4f60\u662f\u4e0d\u662f\u542c\u5230\u4e86\u4ec0\u4e48\u6d88\u606f\uff0c\u6b64\u65f6\u6b64\u523b\uff0c\u54b1\u4eec\u662f\u540c \u821f\u5171\u6d4e\uff0c\u6700\u597d\u80fd\u5f7c\u6b64\u5766\u7136\u76f8\u5bf9\uff0c\u624d\u80fd\u4e92\u4e0d\u76f8\u7591\uff0c\u8054\u624b\u534f\u529b\u2026\u2026\u201d \u3000\u3000\u71d5\u98de\u7f13\u7f13\u5730\u7ad9\u4e86\u8d77\u6765\uff0c\u98de\u8dc3\u51fa\u5ba4\uff0c\u56db\u4e0b\u6e38\u8d70\u4e86\u4e00\u9635\uff0c\u786e\u5b9a\u56db\u5468\u65e0\u4eba\uff0c\u624d\u91cd\u8fd4\u5ba4\u5185\u9053\uff1a \u201c\u738b\u5144\uff0c\u8fd9\u6d88\u606f\u8fd8\u65e0\u6cd5\u8bc1\u5b9e\uff0c\u9a6c\u5de1\u629a\u662f\u4e00\u4f4d\u6df1\u85cf\u4e0d\u9732\u7684\u9ad8\u624b\u3002\u201d \u3000\u3000\u201c\u6709\u8fd9\u7b49\u4e8b\u2026\u2026\u201d\u738b\u91cd\u5c71\u9053\uff1a\u201c\u71d5\u5144\u5728\u5f00\u5c01\u65f6\u65e5\u751a\u4e45\uff0c\u60f3\u5fc5\u65e9\u5df2\u6536\u96c6\u5230\u8bc1\u636e\u4e86\uff1f\u201d \u3000\u3000\u201c\u9a6c\u5de1\u629a\u523b\u610f\u63a9\u9970\uff0c\u56db\u5e74\u591a\u6765\uff0c\u6ca1\u6709\u4eba\u77e5\u9053\u4ed6\u8eab\u5177\u6b66\u529f\uff0c\u5144\u5f1f\u4e5f\u662f\u8fd1\u65e5\u624d\u542c\u5230\u7684\u4f20\u8a00 \u5462\uff01\u201d \u3000\u3000\u201c\u4f20\u8a00\u662f\u5426\u53ef\u4fe1\u5462\uff1f\u201d \u3000\u3000\u201c\u7edd\u5bf9\u53ef\u4fe1\u2026\u2026\u201d\u71d5\u98de\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u66f4\u53ef\u6015\u7684\u662f\u8d75\u4e8c\u5824\u548c\u9a6c\u5de1\u629a\u7684\u5173\u7cfb\uff01\u4ed6\u4eec\u662f\u827a \u51fa\u540c\u95e8\u7684\u5e08\u5144\u5f1f\uff0c\u4e5f\u540c\u662f\u56de\u56de\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bf4\u51fa\u4e86\u5927\u90e8\u4efd\u7684\u9690\u5bc6\uff0c\u4f46\u5374\u628a\u6c5f\u5343\u91cc\u5e26\u8d70\u5c0f\u71d5\u5b50\u7684\u4e8b\uff0c\u9690\u4e86\u8d77\u6765\u3002 \u3000\u3000\u738b\u91cd\u5c71\u9707\u60ca\u4e86\uff0c\u8138\u4e0a\u7684\u808c\u8089\u5267\u70c8\u8df3\u52a8\uff0c\u4e45\u4e45\u624d\u5e73\u590d\u4e0b\u6765\u3002 \u3000\u3000\u738b\u91cd\u5c71\u957f\u957f\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u6c5f\u524d\u8f88\u627e\u5230\u8d75\u5bb6\u7684\u5b85\u9662\uff0c\u5e76\u975e\u662f\u65e0\u56e0\u800c\u8d77 \u7684\uff1f\u201d \u3000\u3000\u5ffd\u7136\u6539\u79f0\u4e3a\u6c5f\u524d\u8f88\uff0c\u4f3c\u662f\u6062\u590d\u4e86\u5bf9\u6c5f\u5343\u91cc\u7684\u5c0a\u91cd\u3002 \u3000\u3000\u201c\u662f\uff01\u4e09\u516c\u4e3b\u5931\u8e2a\u4e00\u4e8b\uff0c\u5982\u679c\u7275\u626f\u5230\u9a6c\u5927\u4eba\uff0c\u71d5\u67d0\u4eba\u771f\u4e0d\u77e5\u8fd9\u6848\u5b50\u5982\u4f55\u80fd\u529e\u5f97\u4e0b\u53bb\uff1f\u201d \u3000\u3000\u201c\u4e00\u7701\u5de1\u629a\uff0c\u5b98\u4f4d\u5f88\u5927\uff0c\u4f46\u5728\u7687\u4e0a\u7684\u773c\u4e2d\u5c31\u4e0d\u7b97\u4ec0\u4e48\u4e86\u3002\u71d5\u6b7b\u4e0d\u7528\u592a\u62c5\u5fc3\uff0c\u653e\u5f00\u624b\uff0c\u516c \u4e8b\u516c\u529e\uff0c\u6211\u8fd9\u5c31\u53bb\u627e\u4ed6\u4eec\u4e2a\u660e\u767d\uff01\u201d\u738b\u91cd\u5c71\u8bf4\u5e72\u5c31\u5e72\uff0c\u970d\u7136\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u201c\u6162\u6765\uff0c\u6162\u6765\u2026\u2026\u201d\u71d5\u98de\u62e6\u4f4f\u4e86\u738b\u91cd\u5c71\uff0c\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u6025\u4e0d\u5f97\uff0c\u54b1\u4eec\u53ea\u6709\u7ebf\u7d22\uff0c\u6ca1\u6709\u8bc1 \u636e\u3002\u201d \u3000\u3000\u201c\u5728\u4e0b\u592a\u51b2\u52a8\u4e86\uff01\u201d\u738b\u91cd\u5c71\u6062\u590d\u4e86\u51b7\u9759\uff0c\u9053\uff1a\u201c\u4f46\u4e09\u516c\u4e3b\u968f\u65f6\u6709\u6027\u547d\u7684\u5371\u9669\uff0c\u54b1\u4eec\u603b\u4e0d \u80fd\u5750\u4ee5\u5f85\u6bd9\u3002\u201d \u3000\u3000\u201c\u5e94\u8be5\u662f\u5750\u4ee5\u5f85\u63f4\u2026\u2026\u201d\u71d5\u98de\u9053\uff1a\u201c\u738b\u7edf\u9886\u8fd1\u65e5\u4f1a\u5230\uff0c\u6211\u76f8\u4fe1\u6c5f\u5927\u4fa0\u4e5f\u4f1a\u5c3d\u5feb\u8d76\u6765\uff0c\u8fd9 \u662f\u4e00\u573a\u8c6a\u8d4c\uff0c\u6ca1\u6709\u76f8\u5f53\u7684\u5b9e\u529b\u4e0d\u80fd\u8f7b\u4e3e\u5984\u52a8\uff0c\u6211\u4eec\u4e00\u6b65\u5931\u9519\uff0c\u4f1a\u8fde\u7d2f\u5230\u4e09\u516c\u4e3b\u7684\u751f\u6b7b\u3002\u201d \u3000\u3000\u738b\u91cd\u5c71\u7f13\u7f13\u5750\u4e0b\uff0c\u9053\uff1a\u201c\u4e0d\u77e5\u9a6c\u5de1\u629a\u7684\u6b66\u529f\u9ad8\u660e\u5230\u4ec0\u4e48\u7a0b\u5ea6\uff1f\u201d \u3000\u3000\u71d5\u98de\u77e5\u4ed6\u5fc3\u4e2d\u4e0d\u670d\uff0c\u7b11\u7b11\u9053\uff1a\u201c\u771f\u8981\u7275\u6d89\u5230\u9a6c\u5de1\u629a\uff0c\u738b\u5144\u8fd8\u6015\u6ca1\u6709\u548c\u9a6c\u5de1\u629a\u52a8\u624b\u7684\u673a\u4f1a \u4e48\uff1f\u6b64\u523b\uff0c\u4f60\u80a9\u8d1f\u5927\u4efb\uff0c\u4e0d\u53ef\u5b64\u6ce8\u4e00\u63b7\uff0c\u5c0f\u4e0d\u5fcd\u5219\u4e71\u5927\u8c0b\uff0c\u8fd8\u662f\u7b49\u738b\u7edf\u9886\u5230\u4e86\u4e4b\u540e\uff0c\u518d\u4ed8\u8bf8 \u884c\u52a8\u3002\u201d \u3000\u3000\u201c\u71d5\u5144\u8bf4\u7684\u662f\u2026\u2026\u201d\u738b\u91cd\u5c71\u5df2\u5b8c\u5168\u5e73\u9759\u4e0b\u6765\uff0c\u9053\uff1a\u201c\u73b0\u5728\uff0c\u54b1\u4eec\u5e94\u8be5\u505a\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u201c\u8ffd\u67e5\u516c\u4e3b\u7684\u4e0b\u843d\u2026\u2026\u201d \u3000\u3000\u201c\u5bf9\uff01\u54b1\u4eec\u51fa\u57ce\u5317\u4e0a\uff0c\u53bb\u8fce\u63a5\u738b\u7edf\u9886\uff0c\u4e5f\u597d\u65e9\u4e9b\u544a\u8bc9\u4ed6\u4e2a\u4e2d\u5185\u60c5\uff0c\u4f7f\u4ed6\u5fc3\u7406\u4e0a\u6709\u6240\u51c6 \u5907\u3002\u201d \u3000\u3000\u71d5\u98de\u611f\u89c9\u5230\u6709\u70b9\u4e0d\u5bf9\uff0c\u4f46\u5374\u8bf4\u4e0d\u51fa\u6765\u54ea\u91cc\u4e0d\u5bf9\uff0c\u53ea\u597d\u5e94\u9053\uff1a\u201c\u597d\u5427\uff01\u54b1\u4eec\u4eca\u591c\u597d\u597d\u4f11\u606f \u4e00\u4e0b\uff0c\u660e\u5929\u51fa\u57ce\u3002\u201d \u3000\u3000\u4e24\u5339\u9a6c\u5954\u9a70\u5728\u8fbd\u9614\u7684\u8352\u91ce\u4e2d\u3002 \u3000\u3000\u9065\u8fdc\u5373\u671b\u89c1\u9ec4\u6cb3\u6eda\u6eda\uff0c\u6d4a\u6d41\u98de\u6d9b\uff0c\u8fd9\u6761\u631f\u5e26\u7740\u5927\u91cf\u6ce5\u6c99\u7684\u6d2a\u6d41\uff0c\u4e1c\u6d41\u4e86\u6570\u5343\u91cc\uff0c\u4ecd\u7136\u662f \u72c2\u91ce\u96be\u9a6f\u3002 \u3000\u3000\u6bcf\u5f53\u6d2a\u6cdb\u4e4b\u671f\uff0c\u5c31\u6cdb\u6ee5\u6210\u707e\uff0c\u6df9\u6ca1\u519c\u820d\uff0c\u4f7f\u6cb3\u6c34\u6539\u9053\uff0c\u5c31\u7b97\u5e73\u5e38\u65f6\u65e5\uff0c\u4ea6\u4e0d\u6e29\u9a6f\uff0c\u65e0\u98ce \u4e09\u5c3a\u6d6a\uff0c\u6d4a\u6c34\u98de\u6e85\uff0c\u4e0d\u5b9c\u884c\u821f\u3002 \u3000\u3000\u6cb3\u5cb8\u79ef\u6c99\u6570\u91cc\uff0c\u4e00\u65e6\u98ce\u8d77\uff0c\u66f4\u662f\u9ec4\u6c99\u6f2b\u6f2b\uff0c\u5f25\u76ee\u96be\u7741\uff0c\u81f4\u4f7f\u9ec4\u6cb3\u6c34\u707e\uff0c\u6210\u4e86\u4e2d\u56fd\u5386\u53f2\u4e0a \u8fde\u7eed\u5343\u4f59\u5e74\u7684\u8bdd\u9898\uff0c\u5386\u4ee3\u6cbb\u6c34\u80fd\u540f\uff0c\u5747\u65e0\u5b8c\u5584\u4e4b\u7b56\u3002 \u3000\u3000\u738b\u91cd\u5c71\u7eb5\u9a91\u9a70\u9a8b\uff0c\u4e00\u9a6c\u5f53\u5148\uff0c\u76f4\u6251\u5411\u767d\u77f3\u6e21\u3002 \u3000\u3000\u71d5\u98de\u5374\u76ee\u5149\u8f6c\u52a8\uff0c\u56db\u4e0b\u63a2\u89c6\u3002 \u3000\u3000\u6b63\u5348\u65f6\u5206\u3002 \u3000\u3000\u9635\u9635\u79cb\u98ce\uff0c\u5076\u5439\u8d77\u4e00\u7247\u9ec4\u6c99\uff0c\u76ee\u5149\u6240\u53ca\u5904\uff0c\u56db\u5904\u4e0d\u89c1\u4eba\u8e2a\u3002 \u3000\u3000\u9ec4\u6cb3\u6cbf\u5cb8\uff0c\u5168\u662f\u9ec4\u8272\u7684\u6c99\u571f\uff0c\u4f46\u767d\u77f3\u6e21\u5374\u5168\u662f\u4e00\u9897\u9897\u9e3d\u86cb\u5927\u5c0f\u767d\u8272\u7684\u77f3\u5b50\uff0c\u8fd9\u4e00\u5e26\u6ca1\u6709 \u5f25\u76ee\u7684\u571f\u6c99\uff0c\u4e5f\u6210\u4e86\u4e00\u5904\u5357\u5317\u4ea4\u901a\u7684\u6e21\u53e3\u3002 \u3000\u3000\u8fd9\u91cc\u8fd8\u642d\u4e86\u4e00\u6240\u7b80\u964b\u7684\u8349\u68da\uff0c\u4e5f\u7ecf\u5e38\u505c\u6cca\u7740\u4e09\u4e94\u8258\u6e21\u6cb3\u7684\u5c0f\u821f\uff0c\u638c\u8239\u7684\u821f\u5b50\u4f11\u606f\u65f6\uff0c\u5e38 \u805a\u96c6\u5728\u8fd9\u8349\u68da\u4e2d\u804a\u5929\u3002 \u3000\u3000\u8fd9\u4e5f\u662f\u738b\u91cd\u5c71\u548c\u4e09\u516c\u4e3b\u5357\u4e0b\u65f6\u7684\u6e21\u53e3\u3002 \u3000\u3000\u4eca\u5929\u60c5\u5f62\u6709\u70b9\u602a\uff0c\u8349\u68da\u4e2d\u6ca1\u6709\u7b49\u5019\u6e21\u6cb3\u7684\u5ba2\u4eba\uff0c\u4e5f\u4e0d\u89c1\u7b49\u5f85\u751f\u610f\u7684\u821f\u5b50\u3002 \u3000\u3000\u4f46\u6cb3\u53e3\u5904\u5374\u6cca\u7740\u4e00\u8258\u6e21\u8239\u3002 \u3000\u3000\u738b\u91cd\u5c71\u8dc3\u4e0b\u9a6c\u80cc\uff0c\u5411\u6cca\u7740\u7684\u6e21\u8239\u884c\u53bb\u3002 \u3000\u3000\u201c\u7b49\u4e00\u4e0b\u2026\u2026\u201d\u71d5\u98de\u8dc3\u843d\u8eab\u4fa7\uff0c\u9053\uff1a\u201c\u738b\u5144\uff0c\u5e26\u4e0a\u5175\u5203\u3002\u201d \u3000\u3000\u539f\u6765\u738b\u91cd\u5c71\u7684\u957f\u5251\uff0c\u4ecd\u7136\u6302\u5728\u9a6c\u978d\u4e0a\u3002 \u3000\u3000\u738b\u91cd\u5c71\u53d6\u4e0b\u957f\u5251\uff0c\u7b11\u9053\uff1a\u201c\u4eca\u5929\u6e21\u8239\u751f\u610f\u5f88\u5fd9\u2026\u2026\u201d \u3000\u3000\u201c\u5fd9\uff1f\u201d\u71d5\u98de\u4f4e\u58f0\u9053\uff1a\u201c\u4e0d\u662f\u5fd9\uff0c\u662f\u6709\u70b9\u5947\u602a\uff0c\u4f60\u53ef\u66fe\u770b\u4e86\u6e21\u8239\u3002\u201d \u3000\u3000\u738b\u91cd\u5c71\u62ac\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u6d4a\u6d41\u6eda\u6eda\uff0c\u4e0a\u4e0b\u6570\u767e\u4e08\u7684\u6cb3\u4e2d\uff0c\u4e0d\u89c1\u4e00\u8258\u6e21\u6cb3\u7684\u8239\u53ea\u3002 \u3000\u3000\u4f3c\u4e4e\u2026\u2026\u53ea\u6709\u8fd9\u4e00\u8258\u505c\u5728\u6e21\u53e3\u4e0a\u3002 \u3000\u3000\u201c\u679c\u7136\u662f\u6709\u70b9\u5947\u602a\uff01\u201d\u738b\u91cd\u5c71\u7f13\u7f13\u5730\u9053\uff1a\u201c\u6211\u8bb0\u5f97\u8fd9\u8349\u68da\u4e2d\uff0c\u8fd8\u6709\u4e00\u4f4d\u4f9b\u5949\u8336\u6c34\u7684\u8001 \u4eba\uff0c\u4eca\u5929\u4e5f\u4e0d\u89c1\u4e86\uff1f\u201d \u3000\u3000\u201c\u90a3\u8258\u8239\u2026\u2026\u201d\u71d5\u98de\u6307\u6307\u6e21\u53e3\uff0c\u9053\uff1a\u201c\u662f\u552f\u4e00\u7684\u6e21\u8239\uff0c\u60f3\u6e21\u6cb3\u7684\u4eba\uff0c\u53ea\u6709\u8d70\u8fd1\u5b83\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u662f\u8bf4\uff0c\u8239\u4e2d\u6709\u9b3c\u2026\u2026\u201d\u738b\u91cd\u5c71\u5fc3\u5934\u4e00\u9707\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u4f1a\u5b89\u6392\u4ec0\u4e48\u6837\u7684\u57cb\u4f0f\uff1f\u201d \u3000\u3000\u71d5\u98de\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u77e5\u9053\uff0c\u767d\u77f3\u6e21\u662f\u8fd9\u767e\u91cc\u4e4b\u5185\uff0c\u6700\u597d\u7684\u4e00\u4e2a\u6e21\u53e3\uff0c\u98ce\u6c99\u6700\u5c11\uff0c\u6e21\u8239 \u6700\u591a\uff0c\u6c34\u52bf\u4e5f\u8f83\u5e73\u7a33\uff0c\u5e73\u5e38\u65f6\u95f4\uff0c\u5019\u6e21\u7684\u5ba2\u4eba\u7531\u6668\u81f3\u66ae\uff0c\u7edc\u7ece\u4e0d\u7edd\u2026\u2026\u201d \u3000\u3000\u201c\u71d5\u5144\u5c0f\u5c0f\u2026\u2026\u201d \u3000\u3000\u738b\u91cd\u5c71\u559d\u58f0\u4e2d\uff0c\u957f\u5251\u5df2\u7136\u51fa\u9798\uff0c\u8eab\u5b50\u98de\u8d77\uff0c\u8d8a\u8fc7\u4e86\u71d5\u98de\uff0c\u8fce\u5411\u6765\u4eba\u3002\u201c \u3000\u3000\u679c\u7136\uff0c\u90a3\u552f\u4e00\u505c\u6cca\u7684\u6e21\u8239\u4e2d\uff0c\u65e9\u6709\u4f0f\u5175\uff0c\u71d5\u98de\u6307\u660e\u4e86\u6e21\u8239\u6709\u8bc8\uff0c\u4f7f\u4ed6\u4eec\u8ba1\u5212\u6539\u53d8\uff0c\u6084\u7136 \u6f5c\u51fa\u8231\u5916\uff0c\u5077\u88ad\u71d5\u98de\u3002 \u3000\u3000\u5e78\u597d\uff0c\u88ab\u738b\u91cd\u5c71\u53d1\u73b0\uff0c\u53ca\u65f6\u622a\u51fb\u3002 \u3000\u3000\u4e00\u9635\u91d1\u94c1\u4ea4\u9e23\u58f0\u4e2d\uff0c\u4e24\u4eba\u5df2\u5200\u3001\u5251\u89e6\u63a5\uff0c\u4e92\u653b\u4e86\u56db\u5251\u3001\u4e09\u5200\u3002 \u3000\u3000\u738b\u91cd\u5c71\u51fa\u8eab\u6b66\u5f53\u95e8\u4e0b\uff0c\u81ea\u8ba4\u5251\u4e0a\u9020\u8be3\u5f88\u6df1\uff0c\u8fd9\u56db\u5251\u8fde\u73af\u653b\u51fa\uff0c\u5b9a\u53ef\u4f24\u654c\u3002 \u3000\u3000\u90a3\u77e5\u5bf9\u65b9\u4e0d\u4f46\u8fde\u6321\u56db\u5251\uff0c\u800c\u4e14\u8fd8\u56de\u653b\u4e09\u5200\u3002 \u3000\u3000\u71d5\u98de\u4e0d\u7981\u4e00\u6014\uff0c\u6a2a\u5251\u5f53\u80f8\uff0c\u4ed4\u7ec6\u7684\u6253\u91cf\u6765\u4eba\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u8fd9\u65f6\u6b63\u6709\u4e09\u4e2a\u5168\u8eab\u9ed1\u8863\u3001\u624b\u6267\u957f\u5200\u7684\u4eba\uff0c\u5305\u56f4\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u71d5\u98de\u53f7\u79f0\u94c1\u638c\uff0c\u7ec3\u6709\u94c1\u6c99\u638c\u7684\u5de5\u592b\uff0c\u638c\u529b\u80fd\u788e\u77f3\u88c2\u7891\uff0c\u82e5\u975e\u5fc5\u8981\uff0c\u4e0d\u4f1a\u4eae\u51fa\u5175\u5203\u6765\u3002 \u3000\u3000\u73b0\u5728\uff0c\u71d5\u98de\u5374\u611f\u89c9\u5230\u5df2\u662f\u5fc5\u8981\u7684\u65f6\u523b\uff0c\u738b\u91cd\u5c71\u548c\u90a3\u9ed1\u8863\u4eba\u5bf9\u640f\u4e00\u62db\uff0c\u71d5\u98de\u5df2\u770b\u51fa\u4e86\u6765\u4eba \u90fd\u662f\u7cbe\u6311\u7ec6\u9009\u7684\u9ad8\u624b\uff0c\u5175\u5203\u5728\u624b\uff0c\u4e5f\u672a\u5fc5\u80fd\u62b5\u6297\u5f97\u4f4f\uff0c\u53c8\u600e\u6562\u6258\u5927\u3002 \u3000\u3000\u56f4\u4e0a\u6765\u7684\u4e00\u5171\u6709\u56db\u4e2a\u4eba\uff0c\u90fd\u7a7f\u7740\u9ed1\u8272\u7684\u8863\u670d\uff0c\u9ed1\u8272\u7684\u5934\u5957\uff0c\u9664\u4e86\u4e24\u53ea\u773c\u775b\u5916\uff0c\u5168\u8eab\u90fd\u9690 \u6ca1\u5728\u9ed1\u8272\u4e2d\uff0c\u7ed9\u4eba\u4e00\u79cd\u5341\u5206\u8be1\u5f02\u7684\u611f\u89c9\u3002 \u3000\u3000\u201c\u5f2f\u6708\u5200\u2026\u2026\u201d\u71d5\u98de\u770b\u6e05\u695a\u4ed6\u4eec\u624b\u4e2d\u7684\u5175\u5203\uff0c\u5931\u58f0\u800c\u53eb\uff1a\u201c\u8ffd\u9b42\u4e03\u715e\u2026\u2026\u201d \u3000\u3000\u56db\u4e2a\u9ed1\u8863\u4eba\u6ca1\u6709\u56de\u7b54\uff0c\u53ea\u662f\u77aa\u7740\u56db\u53ea\u773c\u775b\uff0c\u76ef\u7740\u4e24\u4eba\uff0c\u811a\u4e0b\u7f13\u7f13\u7684\u79fb\u52a8\u7740\uff0c\u4f3c\u662f\u8981\u5e03\u7f6e \u4e00\u4e2a\u5408\u51fb\u7684\u9635\u52bf\u3002 \u3000\u3000\u201c\u8ffd\u9b42\u4e03\u715e\u662f\u6c5f\u6e56\u4e0a\u8457\u540d\u7684\u6740\u624b\uff0c\u4e00\u5411\u8054\u624b\u884c\u52a8\uff0c\u8fd9\u56de\u600e\u4e48\u53ea\u6709\u56db\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u738b\u91cd\u5c71\u6ca1\u89c1\u8fc7\u8ffd\u9b42\u4e03\u715e\uff0c\u4f46\u5374\u542c\u5e08\u5144\u4eec\u8bf4\u8fc7\u3002 \u3000\u3000\u71d5\u98de\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e00\u5b9a\u5728\u8fd9\u91cc\u2026\u2026\u201d\u76ee\u5149\u8f6c\u52a8\uff0c\u5374\u4e0d\u89c1\u4eba\u8e2a\u3002 \u3000\u3000\u738b\u91cd\u5c71\u5feb\u901f\u7684\u9760\u8fd1\u71d5\u98de\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8ffd\u9b42\u4e03\u715e\u53ef\u662f\u548c\u71d5\u5144\u6709\u8fc7\u8282\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u662f\u6740\u624b\uff0c\u6709\u4eba\u51fa\u94b1\uff0c\u4ed6\u4eec\u5c31\u8ffd\u9b42\u53d6\u547d\u3002\u201d\u71d5\u98de\u82e6\u7b11\u9053\uff1a\u201c\u770b\u6765\u4eca\u65e5\u4e00\u6218\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u7528\u62c5\u5fc3\u2026\u2026\u201d\u738b\u91cd\u5c71\u8c6a\u6c14\u5e72\u4e91\u7684\u8bf4\uff1a\u201c\u5c31\u7b97\u4e03\u715e\u5230\u9f50\uff0c\u4e5f\u672a\u5fc5\u771f\u80fd\u8ffd\u9b42\uff01\u201d \u3000\u3000\u56db\u4e2a\u9ed1\u8863\u4eba\u4e00\u76f4\u4e0d\u80af\u5f00\u53e3\u8bf4\u8bdd\uff0c\u540d\u6c14\u6108\u5927\u7684\u6740\u624b\uff0c\u6108\u662f\u8981\u628a\u81ea\u5df1\u9690\u85cf\u7684\u4e25\u5bc6\uff0c\u4ed6\u4eec\u4e0d\u613f \u8ba9\u4eba\u770b\u5230\u9762\u8c8c\uff0c\u8fde\u58f0\u97f3\u4e5f\u4e0d\u613f\u610f\u8ba9\u4eba\u542c\u5230\u3002 \u3000\u3000\u9664\u975e\u4ed6\u4eec\u62d4\u5200\u51fa\u624b\uff0c\u5426\u5219\u7ad9\u5728\u4f60\u7684\u8eab\u4fa7\uff0c\u4f60\u4e5f\u672a\u5fc5\u77e5\u9053\u4ed6\u4eec\u7684\u8eab\u4efd\u3002 \u3000\u3000\u8ffd\u9b42\u4e03\u715e\u5c31\u662f\u8fd9\u6837\u7684\u4eba\uff0c\u4ed6\u4eec\u884c\u8e2a\u98d8\u5ffd\uff0c\u51fa\u6ca1\u65e0\u5e38\uff0c\u51f6\u540d\u52a8\u6c5f\u6e56\uff0c\u4f46\u5374\u5f88\u5c11\u6709\u4eba\u80fd\u8bf4\u51fa \u4ed6\u4eec\u7684\u6837\u5b50\u3002 \u3000\u3000\u201c\u4ed6\u4eec\u7684\u5200\u4e0a\u6709\u6bd2\uff0c\u53ea\u8981\u7834\u76ae\u89c1\u8840\uff0c\u5c31\u80fd\u7f6e\u4eba\u4e8e\u6b7b\u3002\u201d\u71d5\u98de\u9053\uff1a\u201c\u5c0f\u5fc3\u4ed6\u4eec\u7684\u5175\u5203\uff01\u201d \u3000\u3000\u738b\u91cd\u5c71\u70b9\u70b9\u5934\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u71d5\u5144\uff0c\u4ed6\u4eec\u4e00\u51fa\u624b\uff0c\u4f60\u5c31\u60f3\u6cd5\u5b50\u9000\u51fa\u53bb\u2026\u2026\uff02\u71d5\u98de\u542c\u5f97\u4e00 \u6014\uff0c\u9053\uff1a\u201d\u4e3a\u4ec0\u4e48\uff1f\u201c \u3000\u3000\u738b\u91cd\u5c71\u9053\uff1a\u201c\u672c\u95e8\u6709\u4e00\u79cd\u5251\u6cd5\uff0c\u4e13\u7834\u5408\u51fb\u4e4b\u672f\uff0c\u5982\u82e5\u71d5\u5144\u5728\u4fa7\uff0c\u5144\u5f1f\u53cd\u800c\u6709\u4e9b\u65bd\u5c55\u4e0d\u5f00 \u4e86\u3002\u201d \u3000\u3000\u4f46\u95fb\u5200\u98ce\u7834\u7a7a\uff0c\u56db\u4e2a\u9ed1\u8863\u4eba\u540c\u65f6\u51fa\u624b\uff0c\u50cf\u56db\u652f\u6012\u7bad\u4e00\u6837\uff0c\u75be\u5c04\u800c\u81f3\u3002 \u3000\u3000\u8fd9\u662f\u5f88\u5c11\u89c1\u7684\u6253\u6cd5\uff0c\u8fde\u4eba\u5e26\u5200\u7684\u51b2\u98de\u8fc7\u6765\u3002 \u3000\u3000\u6708\u5149\u4e0b\uff0c\u53ea\u89c1\u56db\u67c4\u5f2f\u6708\u5f62\u7684\u957f\u5200\uff0c\u6cdb\u8d77\u4e86\u84dd\u6c6a\u6c6a\u7684\u5149\u8272\uff0c\u679c\u7136\u662f\u6dec\u8fc7\u6bd2\u7684\u5175\u5203\u3002 \u3000\u3000\u738b\u91cd\u5c71\u594b\u8d77\u795e\u52c7\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u957f\u5251\u98de\u8d77\uff0c\u4eba\u968f\u5251\u8f6c\uff0c\u75be\u5982\u98ce\u8f6e\uff0c\u540c\u65f6\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u71d5 \u5144\u5feb\u8d70\uff01\u201d \u3000\u3000\u5176\u5b9e\uff0c\u71d5\u98de\u5728\u56db\u4e2a\u9ed1\u8863\u4eba\u53d1\u52a8\u4e4b\u65f6\uff0c\u5df2\u7136\u65bd\u5c55\u2019\u201c\u91d1\u9ca4\u5012\u7a7f\u6ce2\u201d\uff0c\u5411\u4e00\u4fa7\u6a2a\u7a9c\u8fc7\u53bb\uff0c\u624b \u4e2d\u7684\u5bbd\u800c\u77ed\u5200\uff0c\u62a4\u4f4f\u4e86\u5168\u8eab\u3002 \u3000\u3000\u56db\u4e2a\u9ed1\u8863\u4eba\u96c6\u4e2d\u529b\u91cf\u653b\u5411\u738b\u91cd\u5c71\uff0c\u672a\u60f3\u5230\u71d5\u98de\u7adf\u672a\u5408\u529b\u8fce\u654c\uff0c\u6240\u4ee5\uff0c\u71d5\u98de\u5f88\u8f7b\u6613\u7684\u9000\u51fa \u4e86\u5408\u56f4\u4e4b\u52bf\u3002 \u3000\u3000\u51dd\u795e\u671b\u53bb\uff0c\u56db\u9053\u9525\u5c16\u822c\u7684\u5200\u98ce\uff0c\u5c01\u9501\u4e86\u56db\u9762\u516b\u65b9\uff0c\u738b\u91cd\u5c71\u9664\u4e86\u786c\u63a5\u4e0b\u8fd9\u4e00\u51fb\u4e4b\u5916\uff0c\u5f88\u96be \u7834\u56f4\u800c\u51fa\u3002 \u3000\u3000\u4e00\u67c4\u5251\uff0c\u5982\u4f55\u80fd\u63a5\u4e0b\u5206\u7531\u56db\u9762\u653b\u6765\u7684\u5f2f\u6708\u5200\u3002 \u3000\u3000\u4f55\u51b5\uff0c\u56db\u628a\u631f\u5e26\u7740\u5f3a\u5927\u52b2\u529b\u5408\u56f4\u800c\u81f3\u7684\u5200\u3002 \u3000\u3000\u71d5\u98de\u5fc3\u4e2d\u6709\u70b9\u540e\u6094\uff0c\u7eb5\u7136\u738b\u91cd\u5c71\u53e3\u6c14\u6258\u5927\uff0c\u4e5f\u4e0d\u8be5\u771f\u7684\u9000\u51fa\u6218\u5708\uff0c\u4e24\u4e2a\u4eba\u5408\u529b\u62d2\u654c\uff0c\u81f3 \u5c11\uff0c\u63a5\u4e0b\u8fd9\u4e00\u51fb\u7684\u673a\u4f1a\u5927\u4e9b\u3002 \u3000\u3000\u4f46\u89c1\u738b\u91cd\u5c71\u957f\u5251\u5982\u4e00\u80a1\u6025\u8d77\u7684\u65cb\u98ce\uff0c\u5c55\u5e03\u7684\u5251\u5149\uff0c\u8fc5\u5feb\u7684\u5316\u6210\u4e00\u7247\u51b7\u832b\uff0c\u5251\u5e55\u628a\u4ed6\u6574\u4e2a \u4eba\u5305\u56f4\u8d77\u6765\uff0c\u76f4\u51b2\u800c\u4e0a\u3002 \u3000\u3000\u4e00\u9635\u91d1\u94c1\u4ea4\u9e23\u58f0\u4e2d\uff0c\u738b\u91cd\u5c71\u7adf\u7136\u7834\u56f4\u800c\u51fa\u3002 \u3000\u3000\u201c\u597d\u5251\u6cd5\uff0c\u597d\u5251\u6cd5\uff0c\u6b66\u5f53\u95e8\u4e0b\u5f1f\u5b50\uff0c\u679c\u7136\u975e\u540c\u51e1\u54cd\u2026\u2026\u201d\u71d5\u98de\u5fc3\u4e2d\u6697\u6697\u8d5e\u9053\uff1a\u201c\u8fd9\u5c31\u522b \u602a\u4ed6\u53e3\u6c14\u6258\u5927\u4e86\u3002\u201d \u3000\u3000\u56db\u4e2a\u9ed1\u8863\u4eba\u4e00\u51fb\u4e0d\u4e2d\uff0c\u800c\u4e14\uff0c\u4e5f\u672a\u80fd\u628a\u4eba\u56f0\u4f4f\uff0c\u4e5f\u4e0d\u7981\u4e3a\u4e4b\u4e00\u6123\uff01 \u3000\u3000\u738b\u91cd\u5c71\u8131\u51fa\u4e86\u56db\u5200\u56f4\u88ad\uff0c\u4f46\u5df2\u611f\u89c9\u90a3\u5408\u51fb\u529b\u91cf\u7684\u5f3a\u5927\uff0c\u8fd9\u201c\u7834\u56f4\u4e00\u5251\u201d\u662f\u6b66\u5f53\u4e0a\u4e00\u4ee3\u516d \u4f4d\u5e08\u957f\u5408\u529b\u7814\u521b\u51fa\u6765\u7684\u7edd\u62db\u4e4b\u4e00\uff0c\u4e13\u7834\u5408\u56f4\u4e4b\u672f\u7684\u6b66\u529f\u3002 \u3000\u3000\u4f46\u662f\uff0c\u738b\u91cd\u5c71\u5728\u65bd\u51fa\u4e00\u5251\u4e4b\u540e\uff0c\u611f\u53d7\u5230\u538b\u529b\u4e4b\u5f3a\u5927\uff0c\u5b8c\u5168\u51fa\u4e4e\u4e8e\u610f\u6599\u4e4b\u5916\uff0c\u56db\u628a\u5f2f\u6708\u5200 \u4e0a\u7684\u5f3a\u5927\u52b2\u529b\uff0c\u51e0\u4e4e\u6324\u7834\u4ed6\u65cb\u8d77\u7684\u62a4\u8eab\u5251\u5e55\u3002 \u3000\u3000\u73b0\u5728\uff0c\u8981\u4ed6\u518d\u8bd5\u4e00\u62db\uff0c\u4ed6\u5df2\u5b8c\u5168\u6ca1\u6709\u7834\u56f4\u800c\u51fa\u7684\u4fe1\u5fc3\u3002 \u3000\u3000\u5bd2\u5929\u996e\u51b0\u6c34\uff0c\u51b7\u6696\u5728\u5fc3\u5934\uff0c\u56db\u4e2a\u9ed1\u8863\u4eba\u7adf\u4e5f\u672a\u518d\u51fa\u624b\u3002 \u3000\u3000\u4ed6\u4eec\u90a3\u88c2\u77f3\u7a7f\u4e91\u7684\u5168\u529b\u4e00\u51fb\uff0c\u5df2\u6d88\u8017\u4e86\u4ed6\u4eec\u5927\u90e8\u4efd\u7684\u529f\u529b\uff0c\u4e00\u51fb\u4e0d\u6210\uff0c\u7adf\u4e5f\u4e0d\u77e5\u5982\u4f55\u624d \u80fd\u6740\u6b7b\u8fd9\u4e2a\u5f3a\u52b2\u7684\u654c\u624b\u3002 \u3000\u3000\u8ffd\u9b42\u4e03\u715e\u6740\u4eba\u7684\u624b\u6bb5\u4e00\u5411\u662f\u8bb2\u6c42\u5feb\u901f\uff0c\u5168\u529b\u4e00\u51fb\uff0c\u81f4\u654c\u4e8e\u6b7b\uff0c\u800c\u56db\u4eba\u5408\u529b\u4e00\u51fb\u672a\u4e2d\uff0c\u4e00 \u65f6\u95f4\uff0c\u65e0\u6cd5\u51dd\u805a\u8d77\u518d\u6b21\u653b\u4e0a\u7684\u5185\u529b\u3002 \u3000\u3000\u53ef\u60dc\uff0c\u56db\u4eba\u90fd\u6234\u7740\u8499\u5934\u906e\u8138\u7684\u5e3d\uff0c\u738b\u91cd\u5c71\u65e0\u6cd5\u770b\u5230\u4ed6\u4eec\u82cd\u767d\u7684\u8138\u8272\uff0c\u56db\u4eba\u6a2a\u5200\u800c\u7acb\uff0c\u6697 \u4e2d\u5374\u5728\u8fd0\u6c14\u8c03\u606f\u3002 \u3000\u3000\u4f46\u5371\u6025\u63a5\u8e35\u800c\u81f3\uff0c\u53e6\u5916\u4e09\u4e2a\u9ed1\u8863\u4eba\u7a81\u7136\u51fa\u73b0\uff0c\u7f13\u7f13\u5411\u73b0\u573a\u903c\u8fd1\u3002 \u3000\u3000\u539f\u6765\uff0c\u4ed6\u4eec\u5c31\u9690\u8eab\u5728\u6570\u4e08\u5916\u7684\u9ec4\u6c99\u4e4b\u4e2d\uff0c\u9ec4\u6c99\u63a9\u76d6\u4e86\u6574\u4e2a\u8eab\u4f53\uff0c\u53ea\u9732\u51fa\u4e00\u53cc\u773c\u775b\uff0c\u89c2\u5bdf \u73b0\u573a\u60c5\u5f62\u3002 \u3000\u3000\u662f\u7684\uff0c\u8ffd\u9b42\u4e03\u715e\u884c\u52a8\u4e00\u4f53\uff0c\u4ece\u4e0d\u5206\u79bb\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u8863\u7740\uff0c\u4f7f\u4ed6\u4eec\u65b9\u4fbf\u4e8e\u9690\u5f62\u6f5c\u8e2a\u3002 \u3000\u3000\u7b2c\u4e00\u6d41\u6740\u624b\u7684\u7279\u8272\uff0c\u5c31\u662f\u884c\u8ff9\u8be1\u5f02\uff0c\u9ad8\u6df1\u83ab\u6d4b\u3002 \u3000\u3000\u71d5\u98de\u53d1\u73b0\u654c\u4eba\u65f6\uff0c\u654c\u4eba\u5df2\u903c\u8fd1\u4e00\u4e08\u5de6\u53f3\u3002 \u3000\u3000\u738b\u91cd\u5c71\u9053\uff1a\u201c\u71d5\u5144\uff0c\u8fc7\u6765\uff01\u54b1\u4eec\u5408\u529b\u62d2\u654c\u3002 \u3000\u3000\u4ed6\u5c3d\u91cf\u4fdd\u6301\u4e86\u7a33\u5b9a\u3001\u5e73\u9759\uff0c\u6697\u4e2d\u8c03\u606f\u4e00\u4e0b\uff0c\u4f46\u4ed6\u65e0\u6cd5\u4f7f\u529f\u529b\u5b8c\u5168\u6062\u590d\uff0c\u53ea\u80fd\u8bf4\u7a0d\u7f13\u8fc7\u4e00 \u53e3\u6c14\u3002 \u3000\u3000\u4f46\u662f\u71d5\u98de\u5df2\u5bf9\u738b\u91cd\u5c71\u4fe1\u5fc3\u5927\u589e\uff0c\u6b66\u5f53\u6b63\u5b97\u5251\u6cd5\uff0c\u5b9e\u975e\u4e00\u822c\u95e8\u6d3e\u53ef\u53ca\u3002 \u3000\u3000\u4e09\u4e2a\u9ed1\u8863\u4eba\u5e76\u672a\u6025\u7740\u51fa\u624b\uff0c\u4ed6\u4eec\u867d\u9690\u8eab\u5728\u9ec4\u6c99\u4e4b\u4e2d\uff0c\u4f46\u4ecd\u7136\u773c\u770b\u5230\u90a3\u4e00\u5251\u7684\u5a01\u529b\uff0c\u4e09\u4e2a \u4eba\u73b0\u8eab\u540e\u548c\u56db\u4eba\u5408\u4e8e\u4e00\u5904\uff0c\u4e03\u715e\u805a\u9f50\u4e86\u3002 \u3000\u3000\u73b0\u5728\uff0c\u8ffd\u9b42\u4e03\u715e\u5206\u6210\u4e86\u4e00\u4e2a\u534a\u5706\u5f62\u7684\u9635\u52bf\uff0c\u628a\u71d5\u98de\u548c\u738b\u91cd\u5c71\u56fe\u5728\u4e2d\u95f4\u3002 \u3000\u3000\u71d5\u98de\u4f4e\u58f0\u9053\uff1a\u201c\u738b\u5144\uff0c\u4e03\u715e\u5408\u624b\uff0c\u53ef\u8981\u5144\u5f1f\u51fa\u624b\u76f8\u52a9\uff1f\u201d \u3000\u3000\u738b\u91cd\u5c71\u82e6\u7b11\u4e00\u4e0b\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u5982\u62a2\u5148\u653b\u51fa\uff0c\u4f60\u80fd\u4e0d\u80fd\u98de\u8eab\u4e0a\u9a6c\uff0c\u9003\u56de\u5e9c\u57ce\uff1f\u201d \u3000\u3000\u71d5\u98de\u770b\u770b\u4e24\u5339\u5750\u9a6c\uff0c\u505c\u5728\u5341\u4e08\u4e4b\u5916\uff0c\u90a3\u662f\u4e24\u5339\u597d\u9a6c\uff0c\u5c1a\u672a\u5f03\u4e3b\u800c\u53bb\uff0c\u4f46\u56e0\u65e0\u4eba\u64cd\u7f30\uff0c\u88ab \u903c\u51fa\u5728\u5341\u4e08\u4e4b\u5916\u3002 \u3000\u3000\u201c\u6050\u6015\u662f\u6765\u4e0d\u53ca\u2026\u2026\u201d\u71d5\u98de\u8bf4\uff1a\u201c\u4f55\u51b5\uff0c\u71d5\u67d0\u4e5f\u4e0d\u80fd\u5f03\u4f60\u800c\u53bb\uff0c\u54b1\u4eec\u5408\u529b\u4e00\u62fc\u5427\uff01\u751f\u6b7b \u7531\u547d\uff0c\u4f55\u60e7\u4e4b\u6709\u3002\u201d \u3000\u3000\u53ea\u542c\u4e00\u58f0\u957f\u7b11\uff0c\u9065\u9065\u4f20\u6765\uff0c\u6d4a\u6d6a\u6eda\u6eda\u4e2d\uff0c\u4e00\u53f6\u5c0f\u821f\uff0c\u5982\u98de\u800c\u6765\u3002 \u3000\u3000\u201c\u7edf\u9886\u5230\u4e86\u2026\u2026\u201d \u3000\u3000\u738b\u91cd\u5c71\u7684\u8138\u4e0a\u6cdb\u73b0\u51fa\u4e00\u62b9\u559c\u8272\uff0c\u5168\u8eab\u7684\u6c14\u529b\u4e5f\u9aa4\u589e\uff0c\u500f\u5730\u626c\u8d77\u957f\u5251\uff0c\u6446\u51fa\u4e86\u6b66\u5f53\u9632\u5b88\u7684 \u5251\u5f0f\u3002 \u3000\u3000\u8ffd\u9b42\u4e03\u715e\u5ffd\u7136\u98de\u8dc3\u800c\u8d77\uff0c\u75be\u653b\u800c\u4e0a\u3002 \u3000\u3000\u738b\u91cd\u5c71\u5251\u5316\u4e00\u7247\u5bd2\u8292\uff0c\u5b8c\u5168\u91c7\u53d6\u4e86\u5b88\u62a4\uff0c\u540c\u65f6\u8bf4\u9053\uff1a\u201c\u71d5\u5144\uff0c\u5168\u529b\u9632\u5b88\u3002\u201d \u3000\u3000\u4e00\u9635\u91d1\u94c1\u4ea4\u9e23\u58f0\u4e2d\uff0c\u738b\u91cd\u5c71\u6321\u4f4f\u4e86\u56db\u5200\uff0c\u71d5\u98de\u4e5f\u5168\u529b\u65bd\u5c55\uff0c\u5c01\u4f4f\u4e86\u8fd1\u8eab\u5200\u5149\u3002 \u3000\u3000\u8ffd\u9b42\u4e03\u715e\u5e76\u672a\u5168\u529b\u653b\u654c\uff0c\u4e00\u51fb\u672a\u4e2d\uff0c\u7acb\u65f6\u98de\u8eab\u800c\u51fa\u3002 \u3000\u3000\u4e00\u6761\u4eba\u5f71\uff0c\u63a0\u7a7a\u800c\u81f3\uff0c\u98de\u843d\u5728\u738b\u91cd\u5c71\u7684\u8eab\u4fa7\u3002 \u3000\u3000\u9ed1\u987b\u98d8\u80f8\uff0c\u53cc\u76ee\u5982\u7535\uff0c\u6b63\u662f\u5185\u5bab\u7edf\u9886\u6458\u661f\u624b\u738b\u5f64\u3002 \u3000\u3000\u201c\u53d4\u53d4\u518d\u665a\u5230\u4e00\u6b65\uff0c\u53ea\u6015\u6211\u8981\u8840\u6e85\u767d\u77f3\u6e21\u4e86\u3002\u201d\u738b\u91cd\u5c71\u529f\u529b\u672a\u590d\uff0c\u867d\u7136\u53c8\u63a5\u4e0b\u654c\u4eba\u4e00 \u51fb\uff0c\u4eba\u5df2\u6709\u865a\u8131\u7684\u611f\u89c9\u4e86\u3002 \u3000\u3000\u201c\u8ffd\u9b42\u4e03\u715e\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d\u71d5\u98de\u9053\uff1a\u201c\u5351\u804c\u5f00\u5c01\u5e9c\u603b\u6355\u5934\u71d5\u98de\uff0c\u53e9\u89c1\u5927\u4eba\u3002\u201d \u5367\u9f99\u751f\u300a\u71d5\u5b50\u4f20\u5947\u300b \u7b2c \u4e94 \u56de\u3000\u4e03\u5de7\u7f57\u6c49 \u3000\u3000\u71d5\u98de\u8981\u4e0b\u8dea\uff0c\u5374\u88ab\u738b\u5f64\u62e6\u4f4f\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u7b97\u6b63\u5f0f\u7684\u5b98\u5458\uff0c\u603b\u6355\u5934\u4e0d\u7528\u591a\u793c\u2026\u2026\u201d\u63a2\u624b\u5165 \u6000\u53d6\u51fa\u4e00\u4e2a\u7389\u74f6\uff0c\u5012\u51fa\u4e00\u7c92\u767d\u8272\u4e39\u4e38\uff0c\u4ea4\u7ed9\u738b\u91cd\u5c71\u9053\uff1a\u201c\u541e\u4e0b\u53bb\uff0c\u4f60\u80fd\u63a5\u4e0b\u8ffd\u9b42\u4e03\u715e\u7684\u4e24\u6b21 \u5408\u51fb\uff0c\u672a\u88ab\u8ffd\u53bb\u9b42\u9b44\uff0c\u76f8\u5f53\u4e0d\u9519\u4e86\u3002\u201d \u3000\u3000\u8fd9\u662f\u8bf4\uff0c\u4ed6\u5df2\u770b\u5230\u4e86\u8ffd\u9b42\u4e03\u715e\u7b2c\u4e00\u6b21\u7684\u653b\u52bf\uff0c\u624d\u6025\u6025\u5fa1\u821f\u98de\u6e21\uff0c\u770b\u4ed6\u76ee\u5149\u4e2d\u7684\u795e\u91c7\uff0c\u5fc5 \u6709\u8fc7\u4eba\u7684\u76ee\u529b\u3002 \u3000\u3000\u201c\u662f\u7684\uff0c\u6c5f\u6e56\u4e0a\u80fd\u63a5\u4e0b\u4ed6\u4eec\u5408\u529b\u4e00\u51fb\u7684\u4eba\u4e0d\u591a\u2026\u2026\u201d \u3000\u3000\u738b\u5f64\u6253\u65ad\u4e86\u71d5\u98de\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u600e\u4e48\u4f1a\u60f9\u4e0a\u8ffd\u9b42\u4e03\u715e\u5462\uff1f\u201d\u4e00\u9762\u6325\u624b\u793a\u610f\uff0c\u8ba9\u738b\u91cd\u5c71\u5750\u4e0b \u6765\u8c03\u606f\u3002 \u3000\u3000\u76f4\u5230\u6b64\u523b\uff0c\u90a3\u5c0f\u821f\u624d\u9760\u8fd1\u5cb8\u8fb9\uff0c\u4e24\u4e2a\u5168\u8eab\u52b2\u88c5\u7684\u4f69\u5200\u4f8d\u536b\u7cfb\u597d\u5c0f\u821f\uff0c\u5feb\u6b65\u884c\u6765\u3002 \u3000\u3000\u90a3\u662f\u4e24\u4e2a\u7cbe\u58ee\u7684\u5e74\u8f7b\u4eba\uff0c\u5bf9\u738b\u5f64\u4f3c\u6781\u656c\u754f\uff0c\u8ddd\u79bb\u4e94\u516d\u6b65\u5c31\u505c\u4e86\u4e0b\u6765\uff0c\u606d\u5b88\u4e00\u4fa7\u3002 \u3000\u3000\u201c\u4ed6\u4eec\u662f\u8457\u540d\u7684\u6740\u624b\uff0c\u4e0d\u4f1a\u65e0\u6545\u6740\u4eba\u2026\u2026\u201d\u71d5\u98de\u8bf4\uff1a\u201c\u4e09\u516c\u4e3b\u5931\u8e2a\u4e00\u6848\uff0c\u53d8\u4e2d\u6709\u53d8\uff0c\u5351 \u804c\u548c\u91cd\u5c71\u5144\u5317\u4e0a\u8fce\u9a7e\uff0c\u5c31\u662f\u60f3\u65e9\u4e00\u4e9b\u5411\u7edf\u9886\u62a5\u544a\u5185\u60c5\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u672c\u5e94\u63a5\u5230\u4e86\u98de\u9e3d\u4f20\u4e66\uff0c\u5c31\u517c\u7a0b\u5357\u4e0b\uff0c\u8fd9\u4ef6\u6848\u5b50\u975e\u5e38\u8be1\u5f02\uff0c\u71d5\u603b\u6355\u5934\u6709\u4ec0\u4e48\u8bdd\uff0c\u53ea \u7ba1\u8bf7\u8bf4\uff0c\u8fd9\u5730\u65b9\u5927\u6982\u4e0d\u4f1a\u6709\u654c\u4eba\u7684\u8033\u76ee\uff01\u201d \u3000\u3000\u9510\u5229\u7684\u76ee\u5149\uff0c\u56db\u4e0b\u641c\u5bfb\u3002 \u3000\u3000\u71d5\u98de\u5fc3\u5fd6\u9053\uff1a\u539f\u6765\u5185\u5bab\u7981\u536b\u7adf\u4e5f\u5728\u6c5f\u6e56\u4e0a\u5e03\u7f6e\u6709\u4f20\u8baf\u7684\u98de\u9e3d\uff0c\u52ff\u602a\u4ed6\u6765\u7684\u5982\u6b64\u4e4b\u5feb\u3002\u5f53 \u4e0b\u628a\u6c5f\u5343\u91cc\u63a2\u5f97\u5185\u60c5\uff0c\u5c3d\u7686\u76f8\u544a\uff0c\u53ea\u9690\u53bb\u4e86\u5c0f\u71d5\u5b50\u6295\u5e08\u5b66\u827a\u548c\u71d5\u592b\u4eba\u9041\u79bb\u5f00\u5c01\u4e24\u4ef6\u4e8b\u60c5\u3002 \u3000\u3000\u201c\u597d\u4e00\u4e2a\u9a6c\u6587\u4e2d\u2026\u2026\u201d\u738b\u5f64\u6c89\u91cd\u5730\u9053\uff1a\u201c\u4e09\u5e74\u524d\u4ed6\u591c\u5165\u7981\u5bab\uff0c\u88ab\u6211\u53d1\u89c9\u60ca\u9000\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u662f\u8bf4\uff0c\u7edf\u9886\u5927\u4eba\u4e09\u5e74\u524d\u5c31\u53d1\u89c9\u9a6c\u5927\u4eba\u2026\u2026\u201d \u3000\u3000\u201c\u5f53\u65f6\u6709\u56db\u7701\u5de1\u629a\u5949\u65e8\u5165\u4eac\u664b\u89c1\uff0c\u9a6c\u6587\u4e2d\u4e3a\u5176\u4e2d\u4e4b\u4e00\uff0c\u90a3\u591c\u6211\u8ffd\u81f3\u793c\u90e8\u5bbe\u9986\u9644\u8fd1\uff0c\u4eba\u8e2a \u6d88\u5931\u3002\u5f53\u65f6\uff0c\u793c\u90e8\u5bbe\u9986\u4e2d\u4f4f\u6709\u56db\u7701\u5de1\u629a\u548c\u968f\u5458\uff0c\u6211\u4e0d\u4fbf\u5165\u9986\u67e5\u95ee\uff0c\u53ea\u597d\u5fc3\u4e2d\u5b58\u7591\uff0c\u4e0b\u4ee4\u52a0\u5f3a \u6212\u5907\u5185\u5bab\uff0c\u5370\u8bc1\u4e86\u4eca\u65e5\u71d5\u603b\u6355\u5934\u4e4b\u8a00\uff0c\u90a3\u591c\u6df7\u5165\u5185\u5bab\u7684\u4eba\uff0c\u5b9a\u662f\u9a6c\u6587\u4e2d\u4e86\u2026\u2026\u201d\u738b\u5f64\u957f\u957f\u5401 \u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u672c\u5ea7\u60f3\u4e0d\u901a\u7684\u662f\uff0c\u9a6c\u6587\u4e2d\u8d35\u4e3a\u4e00\u65b9\u5927\u5458\uff0c\u8eab\u53d7\u7687\u6069\u6df1\u91cd\uff0c\u52ab\u6301\u516c\u4e3b\u7528\u5fc3\u4f55 \u5728\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e00\u70b9\uff0c\u5351\u804c\u4e5f\u662f\u767e\u601d\u83ab\u89e3\uff0c\u4f46\u53d6\u5411\u6240\u6307\uff0c\u4f3c\u65e0\u53ef\u7591\uff0c\u6b20\u7f3a\u7684\u53ea\u662f\u8bc1\u636e\u2026\u2026\u201d \u3000\u3000\u201c\u8ffd\u8e2a\u8e29\u8ff9\u4e4b\u80fd\uff0c\u6c5f\u5343\u91cc\u582a\u79f0\u7b2c\u4e00\uff0c\u8fd9\u8001\u5c0f\u5b50\u4e3a\u5fb7\u4e0d\u5352\uff0c\u600e\u4e48\u4f1a\u4e2d\u9014\u6492\u624b\u800c\u53bb\u3002\u201d \u3000\u3000\u201c\u6c5f\u524d\u8f88\u544a\u8bc9\u5351\u804c\uff0c\u4ed6\u4f1a\u5c3d\u5feb\u56de\u6765\u2026\u2026\u201d \u3000\u3000\u201c\u6c5f\u5343\u91cc\u8bf4\u8fc7\u7684\u8bdd\uff0c\u4e00\u5b9a\u7b97\u6570\uff0c\u53ea\u662f\u65f6\u95f4\u8feb\u4fc3\uff0c\u627e\u4e0d\u51fa\u4e8c\u4e09\u516c\u4e3b\u7684\u4e0b\u843d\uff0c\u6050\u7687\u4e0a\u9707\u6012\uff0c \u5174\u8d77\u5927\u72f1\uff0c\u90a3\u5c31\u682a\u8fde\u592a\u591a\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u4ef6\u4e8b\uff0c\u5723\u4e0a\u5df2\u7ecf\u77e5\u9053\u4e86\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u73b0\u5728\u8fd8\u4e0d\u77e5\u9053\u2026\u2026\u201d\u738b\u5f64\u9053\uff1a\u201c\u4f46\u9690\u7792\u4e0d\u4e86\u591a\u4e45\u7684\uff0c\u7687\u4e0a\u949f\u7231\u4e09\u516c\u4e3b\uff0c\u6d88\u606f\u4e00\u65e6\u66dd \u5149\uff0c\u9f99\u989c\u8272\u53d8\uff0c\u6050\u6015\u662f\u5343\u4e07\u4eba\u5934\u843d\u5730\u7684\u5927\u5c60\u6740\u4e86\u2026\u2026\u3002 \u3000\u3000\u5343\u4e07\u4eba\u5934\u843d\u5730\uff0c\u4ed6\u71d5\u98de\u5fc5\u7136\u662f\u5176\u4e2d\u4e4b\u4e00\u3002 \u3000\u3000\u201c\u6700\u597d\u7684\u529e\u6cd5\u662f\uff0c\u6d88\u606f\u672a\u6f0f\u4e4b\u524d\uff0c\u5148\u628a\u4e09\u516c\u4e3b\u6551\u51fa\u6765\u2026\u2026\u201d\u738b\u5f64\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u53ef\u60dc \u6c5f\u5343\u91cc\u4e0d\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u738b\u91cd\u5c71\u5ffd\u7136\u633a\u8eab\u7ad9\u8d77\uff0c\u9053\uff1a\u201c\u4e8c\u53d4\uff0c\u6c5f\u5343\u91cc\u7684\u8ffd\u8e2a\u4e4b\u672f\uff0c\u771f\u7684\u90a3\u4e48\u795e\u5947\u5417\uff1f\u201d \u3000\u3000\u201c\u600e\u4e48\uff0c\u4f60\u4e0d\u76f8\u4fe1\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u6b66\u5f53\u95e8\u4e0b\u4e5f\u6709\u8ffd\u8e2a\u8e29\u8ff9\u4e4b\u6cd5\uff0c\u5c0f\u4f84\u7559\u5fc3\u89c2\u5bdf\uff0c\u5b9e\u5728\u770b\u4e0d\u51fa\u6c5f\u524d\u8f88\u6709\u4ec0\u4e48\u7279\u522b\u7684\u5730 \u65b9\u3002\u201d \u3000\u3000\u738b\u5f64\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u603b\u53ef\u4ee5\u770b\u51fa\u6765\uff0c\u4ed6\u548c\u4f60\u7684\u8ffd\u8e2a\u672f\u6709\u4e9b\u4e0d\u540c\u7684\u5730\u65b9\u5427\uff1f\u201d \u3000\u3000\u201c\u5c0f\u4f84\u53d1\u73b0\u4ed6\u4e0d\u540c\u4e4b\u5904\u662f\uff0c\u4e0d\u505c\u5728\u8fd0\u7528\u9f3b\u5b50\u2026\u2026\u201d\u738b\u91cd\u5c71\u8bf4\uff1a\u201c\u597d\u50cf\u662f\u5728\u5229\u7528\u55c5\u89c9\u5206\u8fa8 \u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u90a3\u5c31\u5bf9\u4e86\uff0c\u6c5f\u5343\u91cc\u7684\u9f3b\u5b50\u80fd\u5206\u8fa8\u51fa\u4e94\u91cc\u5185\u4e0d\u540c\u7684\u82b1\u9999\u6c14\u5473\u2026\u2026\u201d\u738b\u5f64\u5fae\u7b11\u7740\u9053\uff1a\u201c\u4ed6 \u88ab\u4eba\u5c0a\u79f0\u4e3a\u5929\u72d7\uff0c\u53ea\u4e0d\u8fc7\uff0c\u8fd9\u5916\u53f7\u6709\u4e9b\u4e0d\u96c5\uff0c\u6240\u4ee5\uff0c\u6ca1\u6709\u4eba\u5f53\u9762\u53eb\u4ed6\u3002\u201d \u3000\u3000\u201c\u539f\u6765\u5982\u6b64\uff0c\u90a3\u5c31\u52ff\u602a\u88ab\u79f0\u4e3a\u5929\u4e0b\u7b2c\u4e00\u7684\u8ffd\u8e2a\u9ad8\u624b\u4e86\u3002\u201d\u738b\u91cd\u5c71\u65e0\u9650\u656c\u6155\u7684\u8bf4\u3002 \u3000\u3000\u738b\u5f64\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u662f\u4e00\u4f4d\u6e38\u620f\u98ce\u5c18\u7684\u5947\u4eba\uff0c\u89c2\u5bdf\u4e8b\u7269\u548c\u5224\u65ad\u654c\u60c5\u7684\u7cbe \u5bc6\uff0c\u4e5f\u975e\u5e38\u4eba\u80fd\u53ca\uff0c\u4e0d\u53ef\u7a0d\u5b58\u8f7b\u89c6\u4e4b\u5fc3\u3002\u201d \u3000\u3000\u201c\u662f\uff0c\u5c0f\u4f84\u77e5\u9519\u4e86\uff01\u201d \u3000\u3000\u738b\u5f64\u7684\u76ee\u5149\u8f6c\u6ce8\u5230\u71d5\u98de\u7684\u8138\u4e0a\uff0c\u9053\uff1a\u201c\u71d5\u603b\u6355\u5934\uff0c\u9a6c\u6587\u4e2d\u5728\u5f00\u5c01\u7684\u5730\u9762\u4e0a\uff0c\u9664\u4e86\u8d75\u4e8c\u5824 \u8fd9\u80a1\u529b\u91cf\u4e4b\u5916\uff0c\u8fd8\u6709\u4ec0\u4e48\u52bf\u529b\uff1f\u201d \u3000\u3000\u201c\u9a6c\u5de1\u629a\u7edf\u5fa1\u6709\u672f\uff0c\u6b65\u3001\u9a91\u4e24\u519b\u90fd\u5bf9\u4ed6\u552f\u547d\u662f\u4ece\uff0c\u4e09\u5343\u6b65\u5175\uff0c\u4e24\u5343\u7cbe\u9a91\uff0c\u90fd\u662f\u7cbe\u9510\u4e4b \u5e08\uff0c\u662f\uff3f\u80a1\u5f88\u53ef\u6015\u7684\u529b\u91cf\uff0c\u4ed6\u4eec\u64c5\u5c04\u3001\u9a81\u52c7\uff0c\u4e0d\u6613\u5bf9\u4ed8\u3002\u201d \u3000\u3000\u738b\u5f64\u6c89\u541f\u4e00\u9635\uff0c\u9053\uff1a\u201c\u5982\u679c\u6211\u8868\u660e\u4e86\u8eab\u4efd\uff0c\u6b65\u3001\u9a91\u4e24\u519b\u7684\u7edf\u9886\u4f1a\u4e0d\u4f1a\u542c\u547d\u4e8e\u6211\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\uff0c\u5351\u804c\u5c31\u4e0d\u6562\u4e71\u731c\u4e86\u3002\u201d\u71d5\u98de\u8bf4\uff1a\u201c\u5e73\u5e38\u5f88\u5c11\u548c\u6b65\u3001\u9a91\u4e24\u519b\u4ea4\u5f80\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c31\u6709\u4e9b\u9ebb\u70e6\u4e86\uff0c\u8fd9\u6b21\u79bb\u5bab\u4e0d\u4fbf\u8bf7\u65e8\uff0c\u53ea\u6709\u5185\u5bab\u7981\u536b\u7edf\u9886\u7684\u8170\u724c\uff0c\u82e5\u6b65\u3001\u9a91\u4e24\u519b\u7edf\u9886 \u4e0d\u4e70\u8fd9\u4e2a\u5e10\u2026\u2026\u201d \u3000\u3000\u201c\u96be\u9053\u4ed6\u4eec\u6562\u8bb2\u9006\u7687\u547d\u2026\u2026\u201d\u738b\u91cd\u5c71\u5927\u4e0d\u670d\u6c14\u5730\u9053\uff1a\u201c\u6211\u770b\u671d\u4e2d\u7684\u4e00\u54c1\u516c\u4faf\uff0c\u4e5f\u8981\u7ed9\u53d4 \u53d4\u51e0\u5206\u9762\u5b50\u3002\u201d \u3000\u3000\u201c\u90a3\u4e0d\u540c\uff0c\u4ed6\u4eec\u77e5\u9053\u6211\u7684\u8eab\u4efd\u2026\u2026\u201d \u3000\u3000\u201c\u9a6c\u5de1\u629a\u4e5f\u77e5\u9053\u554a\uff01\u201d \u3000\u3000\u201c\u4f46\u6cb3\u5357\u7701\u6b65\u3001\u9a91\u4e24\u519b\u7684\u7edf\u9886\uff0c\u5374\u672a\u5fc5\u77e5\u9053\uff0c\u9a6c\u6587\u4e2d\u8981\u662f\u771f\u7ffb\u4e86\u8138\uff0c\u4e0d\u627f\u8ba4\u6211\u4eec\u7684\u8eab \u4efd\uff0c\u90a3\u5c31\u96be\u5e94\u4ed8\u4e86\u3002\u518d\u8bf4\uff0c\u6211\u4eec\u8fd9\u7981\u5bab\u4f8d\u536b\uff0c\u672a\u5949\u5723\u65e8\uff0c\u4e5f\u4e0d\u80fd\u516c\u7136\u5bf9\u4ed8\u4e00\u65b9\u5927\u5458\u3002\u8fd9\u4ef6\u4e8b \u6709\u4e9b\u68d8\u624b\u2026\u2026\u201d \u3000\u3000\u201c\u5927\u4eba\u2026\u2026\u201d\u71d5\u98de\u4f3c\u662f\u60f3\u8d77\u4e86\u4e00\u4ef6\u5341\u5206\u91cd\u8981\u7684\u4e8b\uff0c\u9053\uff1a\u201c\u5351\u804c\u5fc3\u4e2d\u6709\u51e0\u53e5\u8bdd\uff0c\u4e0d\u77e5\u9053\u8be5 \u4e0d\u8be5\u8bf4\uff1f\u201d \u3000\u3000\u201c\u5c3d\u7ba1\u8bf7\u8bf4\uff0c\u738b\u67d0\u6d17\u8033\u606d\u542c\u3002\u201d \u3000\u3000\u4ed6\u653e\u4e0b\u5185\u5bab\u7edf\u9886\u7684\u8eab\u4efd\uff0c\u6539\u4ee5\u6c5f\u6e56\u4eba\u7684\u53e3\u6c14\uff0c\u610f\u601d\u4e0a\u5df2\u628a\u71d5\u98de\u5f53\u670b\u53cb\u770b\u5f85\uff0c\u8ba9\u71d5\u98de\u7545\u6240 \u6b32\u8a00\u3002 \u3000\u3000\u201c\u9a6c\u5de1\u629a\u53e6\u517b\u4e86\u4e94\u767e\u7cbe\u9a91\uff0c\u597d\u50cf\u90fd\u662f\u56de\u56de\u2026\u3002\u3002\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\uff1f\u53e6\u517b\u4e86\u4e94\u767e\u7cbe\u9a91\u2026\u2026\u201d\u738b\u5f64\u6709\u4e9b\u5403\u60ca\u4e86\u3002 \u3000\u3000\u201c\u662f\uff01\u90a3\u4e94\u767e\u7cbe\u9a91\u4e0d\u5c5e\u9a91\u519b\u7edf\u9886\u7ba1\u8f96\uff0c\u53e6\u5916\u9a7b\u624e\u5728\u4e00\u5904\u5730\u65b9\u2026\u2026\u201d\u71d5\u98de\u9053\uff1a\u201c\u8fd9\u4e9b\u4e8b\u672c \u548c\u5351\u804c\u7684\u804c\u8d23\u65e0\u5173\uff0c\u672a\u4e88\u4ee5\u8ffd\u67e5\uff0c\u73b0\u5728\u60f3\u6765\uff0c\u89c9\u5f97\u6709\u4e9b\u53ef\u7591\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4ef6\u4e8b\u592a\u91cd\u8981\u4e86\u2026\u2026\u201d\u738b\u5f64\u9053\uff1a\u201c\u5236\u5916\u7ec3\u519b\uff0c\u663e\u7136\u51fa\u4e8e\u79c1\u610f\uff0c\u8131\u79bb\u56fd\u5bb6\u7684\u5efa\u5236\uff0c\u662f\u8c13 \u4e00\u652f\u79c1\u5175\uff0c\u4f46\u4f60\u600e\u4e48\u80af\u5b9a\u4ed6\u4eec\u90fd\u662f\u56de\u56de\u5462\uff1f\u201d \u3000\u3000\u201c\u5351\u804c\u6ce8\u610f\u5230\u4ed6\u4eec\u7684\u91c7\u8d2d\uff0c\u5168\u4ee5\u725b\u3001\u7f8a\u8089\u4e3a\u4e3b\uff0c\u5f53\u65f6\u53ea\u89c9\u5f97\u4ed6\u4eec\u5403\u5f97\u7279\u522b\u597d\u4e00\u4e9b\u800c\u5df2\uff0c \u6b64\u4e8b\u867d\u7136\u65e0\u6cd5\u8bf4\u51fa\u5177\u4f53\u8bc1\u660e\uff0c\u4f46\u4e0d\u96be\u4e00\u67e5\u5373\u77e5\u3002\u201d \u3000\u3000\u201c\u5927\u6982\u4e0d\u4f1a\u9519\u4e86\uff0c\u770b\u8d77\u6765\uff0c\u8bad\u7ec3\u7684\u4e0d\u662f\u4e94\u767e\u7cbe\u5175\uff0c\u800c\u662f\u4e94\u767e\u540d\u527d\u608d\u7684\u52c7\u58eb\u3001\u9ad8\u624b\uff0c\u52ff\u602a \u4ed6\u4e0d\u80af\u79bb\u5f00\u6cb3\u5357\u7701\u5de1\u629a\u7684\u804c\u4f4d\uff0c\u575a\u6301\u4e0d\u8981\u540f\u90e8\u5c1a\u4e66\u7684\u5927\u4f4d\u3002 \u3000\u3000\u201c\u8fd9\u4e2a\u4eba\u771f\u7684\u662f\u8981\u9020\u53cd\u4e86\u3002\u201d\u738b\u91cd\u5c71\u9053\uff1a\u201c\u79c1\u4e0b\u7ec3\u5175\uff0c\u6d6a\u8d39\u516c\u5e26\u2026\u2026\u201d\u5927\u660e\u671d\u6b63\u503c\u627f\u4e4e \u65f6\u671f\uff0c\u9020\u53cd\u4ed6\u8fd8\u4e0d\u6562\u2026\u2026\u201d\u738b\u5f64\u6c89\u541f\u4e00\u9635\uff0c\u9053\uff1a\u201c\u4f46\u4ed6\u4eec\u80c6\u6562\u63b3\u63a0\u4e09\u516c\u4e3b\uff0c\u4f3c\u4e4e\u662f\u53e6\u6709\u4e00\u756a \u5927\u80c6\u8ba1\u5212\uff0c\u4e0d\u81f4\u662f\u5355\u7eaf\u7684\u63b3\u4eba\u52d2\u7d22\u4e86\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u5982\u6b64\uff0c\u54b1\u4eec\u4e0d\u7528\u5230\u5f00\u5c01\u5e9c\u4e86\u2026\u2026\u201d\u738b\u91cd\u5c71\u9053\uff1a\u201c\u9000\u56de\u6cb3\u5317\uff0c\u8c03\u52a8\u5927\u519b\uff0c\u518d\u6e21\u9ec4 \u6cb3\u2026\u2026\u201d \u3000\u3000\u201c\u8f7b\u542f\u5927\u6218\uff0c\u4e0d\u77e5\u8981\u9020\u6210\u591a\u5c11\u7684\u65e0\u8f9c\u4f24\u4ea1\uff0c\u975e\u5230\u4e07\u4e0d\u5f97\u5df2\uff0c\u4e0d\u5b9c\u52a8\u7528\uff0c\u518d\u8bf4\uff0c\u4e09\u516c\u4e3b\u8fd8 \u5728\u4ed6\u4eec\u624b\u4e2d\uff0c\u6551\u4eba\u8981\u7d27\u3002\u201d\u738b\u5f64\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9a6c\u6587\u4e2d\u4e5f\u4e0d\u4f1a\u7ed9\u54b1\u4eec\u56de\u5934\u7684\u673a\u4f1a\u4e86\uff0c\u4ed6\u96c7 \u7528\u4e86\u8ffd\u9b42\u4e03\u715e\uff0c\u663e\u89c1\u5fc3\u4e2d\u987e\u5fcc\u4ecd\u5927\uff0c\u770b\u60c5\u5f62\u5012\u4e0d\u5982\u5c06\u8ba1\u5c31\u8ba1\uff0c\u548c\u9a6c\u6587\u4e2d\u6597\u6597\u5fc3\u673a\u4e86\u3002\u201d \u3000\u3000\u201c\u4e8c\u53d4\u7684\u610f\u601d\u662f\u3002\u3002\uff0c\u2026\u201d \u3000\u3000\u201c\u54b1\u4eec\u56de\u5f00\u5c01\u5e9c\u53bb\u2026\u2026\u201d\u738b\u5f64\u8bf4\u51fa\u4e86\u4e00\u756a\u8ba1\u5212\uff0c\u8981\u71d5\u98de\u5c3d\u91cf\u628a\u4ed6\u7684\u8eab\u4efd\u5ba3\u626c\u51fa\u53bb\uff0c\u6700\u91cd \u8981\u7684\u662f\u8ba9\u6b65\u3001\u9a91\u4e8c\u519b\u7684\u7edf\u9886\u77e5\u9053\u3002 \u3000\u3000\u71d5\u98de\u70b9\u70b9\u5934\uff0c\u548c\u738b\u91cd\u5c71\u98de\u8eab\u4e0a\u9a6c\u800c\u53bb\u3002 \u3000\u3000\u738b\u5f64\u5631\u5490\u4e86\u4e24\u4e2a\u968f\u6765\u7684\u4e24\u540d\u7cbe\u5e72\u624b\u4e0b\u4e00\u756a\uff0c\u8981\u4ed6\u4eec\u4f3a\u673a\u628a\u6d88\u606f\u4f20\u51fa\u53bb\uff0c\u624d\u8fdc\u8e51\u71d5\u98de\u7b49\u8eab \u540e\u800c\u53bb\u3002 \u3000\u3000\u71d5\u98de\u548c\u738b\u91cd\u5c71\u5148\u56de\u5230\u5f00\u5c01\u5e9c\u884c\uff0c\u664b\u89c1\u4e86\u5c39\u77e5\u5e9c\uff0c\u544a\u8bc9\u4ed6\u662f\u5185\u5bab\u4f8d\u536b\u7edf\u9886\uff0c\u5df2\u4eb2\u7387\u5927\u6279\u7684 \u4f8d\u536b\u8d76\u5230\uff0c\u5f53\u7136\uff0c\u71d5\u98de\u4e5f\u4ea4\u5f85\u5c5e\u4e0b\u7684\u6355\u5feb\uff0c\u5c3d\u91cf\u628a\u6d88\u606f\u4f20\u5165\u6b65\u3001\u9a91\u519b\u4e2d\u3002 \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u2019\u5f00\u5c01\u5e9c\u4e0a\u4e0a\u4e0b\u4e0b\u5168\u77e5\u9053\uff0c\u5185\u5bab\u4f8d\u536b\u7edf\u9886\u5df2\u5230\u4e86\u5f00\u5c01\u7684\u4e8b\u3002 \u3000\u3000\u5c39\u77e5\u5e9c\u7b2c\u4e00\u4e2a\u53cd\u5e94\uff0c\u5c31\u662f\u664b\u8c12\u5de1\u629a\u5927\u4eba\u2026\u2026\u3002 \u3000\u3000\u738b\u91cd\u5c71\u3001\u71d5\u98de\u968f\u4ece\u5c39\u77e5\u5e9c\u8fdb\u5165\u4e86\u629a\u5e9c\u4e8c\u5802\uff0c\u5c39\u77e5\u5e9c\u53ea\u8bf4\u4e86\u4e00\u53e5\uff1a\u201c\u738b\u7edf\u9886\u5df2\u5230\u5f00 \u5c01\u2026\u2026\u201d\u95e8\u623f\u4f20\u6765\u4e86\u738b\u5f64\u6c42\u89c1\u7684\u8bdd\u3002 \u3000\u3000\u9a6c\u5de1\u629a\u7565\u7565\u6c89\u601d\uff0c\u9053\uff1a\u201c\u8bf7\u5165\u4e66\u623f\u5f85\u8336\u2026\u2026\u201d\u56de\u5934\u4e00\u987e\u77e5\u5e9c\uff0c\u63a5\u9053\uff1a\u201c\u8d70\uff01\u54b1\u4eec\u53bb\u89c1\u89c1 \u738b\u7edf\u9886\u3002\u201d \u3000\u3000\u71d5\u98de\u8981\u544a\u9000\uff0c\u9a6c\u5de1\u629a\u5374\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u71d5\u603b\u6355\u5934\u5df2\u89c1\u8fc7\u4e86\u738b\u7edf\u9886\uff0c\u518d\u89c1\u4e00\u6b21\u4f55\u59a8\uff1f\u738b \u4f8d\u536b\u6765\u81ea\u5185\u5bab\uff0c\u81ea\u662f\u975e\u89c1\u4e0d\u53ef\u4e86\uff0c\u4e00\u8d77\u53bb\u5427\uff01\u201d \u3000\u3000\u8d77\u8eab\u5148\u884c\uff0c\u6b65\u5165\u4e66\u623f\u3002 \u3000\u3000\u738b\u5f64\u65e9\u5df2\u5728\u5ea7\uff0c\u4e24\u4e2a\u4f69\u5200\u4f8d\u536b\u4e5f\u8ddf\u7740\u8fdb\u6765\u4e86\uff0c\u5206\u7acb\u8eab\u540e\u3002 \u3000\u3000\u9a6c\u6587\u4e2d\u8fd8\u672a\u5f00\u53e3\uff0c\u738b\u7edf\u9886\u5df2\u5927\u58f0\u56b7\u9053\uff1a\u201c\u9a6c\u5927\u4eba\uff0c\u4f60\u8fd9\u5730\u9762\u4e0a\u4e0d\u6e05\u9759\u554a\uff01\u5144\u5f1f\u4e00\u6e21\u6cb3\u5c31 \u78b0\u4e0a\u4e86\u8ffd\u9b42\u4e03\u715e\u2026\u2026\u201d \u3000\u3000\u8fd9\u548c\u738b\u5f64\u5e73\u65f6\u4e3a\u4eba\u7684\u6c89\u7740\uff0c\u5927\u4e0d\u76f8\u540c\uff0c\u9a6c\u6587\u4e2d\u5fae\u5fae\u4e00\u6014\uff0c\u9053\uff1a\u201c\u8ffd\u9b42\u4e03\u715e\u2026\u2026\u201d\u76ee\u5149\u8f6c \u5230\u71d5\u98de\u7684\u8138\u4e0a\uff0c\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u6837\u7684\u571f\u532a\uff0c\u6562\u62e6\u52ab\u7edf\u9886\u5927\u4eba\uff1f\u201d \u3000\u3000\u4ed6\u88c5\u5f97\u6709\u6a21\u6709\u6837\u7684\uff0c\u771f\u662f\u5531\u4f5c\u4ff1\u4f73\uff0c\u71d5\u98de\u4e5f\u4e00\u8eac\u8eab\uff0c\u6b63\u5bb9\u7b54\u9053\uff1a\u201c\u662f\u4e03\u4e2a\u6709\u540d\u7684\u6740\u624b\uff0c \u5982\u975e\u7edf\u9886\u53ca\u65f6\u8d76\u5230\uff0c\u5351\u804c\u548c\u738b\u4f8d\u536b\u6050\u5df2\u906d\u6bd2\u624b\u4e86\uff01\u201d \u3000\u3000\u201c\u8fd9\u7b49\u6740\u624b\uff0c\u51fa\u6ca1\u4e8e\u5f00\u5c01\u5730\u9762\u4e0a\uff0c\u4f60\u8fd9\u4e2a\u5f00\u5c01\u603b\u6355\u5934\u662f\u600e\u4e48\u5e72\u7684\uff1f\u2026\u2026\u201d\u76ee\u5149\u8f6c\u5230\u5f00\u5c01 \u5e9c\u5c39\u77e5\u5e9c\u7684\u8138\u4e0a\uff0c\u9053\uff1a\u201c\u5148\u662f\u4e09\u516c\u4e3b\u884c\u8e2a\u4e0d\u660e\uff0c\u53c8\u6709\u6740\u624b\u51fa\u73b0\u9ec4\u6cb3\u6e21\u53e3\uff0c\u4f60\u56de\u8859\u53bb\u542c\u53c2 \u5427\uff01\u2026\u2026\u201d \u3000\u3000\u5c39\u77e5\u5e9c\u6218\u6218\u5162\u5162\u7684\u5e94\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5351\u804c\u5c0a\u547d\u2026\u2026\u201d\u76ee\u5149\u8f6c\u6ce8\u5230\u71d5\u98de\u7684\u8eab\u4e0a\uff0c\u9053\uff1a\u201c\u71d5 \u98de\uff0c\u672c\u5e9c\u4e00\u5411\u5bf9\u4f60\u4fe1\u4efb\uff0c\u60f3\u4e0d\u5230\uff0c\u7ec8\u662f\u88ab\u4f60\u62d6\u7d2f\uff0c\u6765\u4eba\u54ea\uff0c\u7ed9\u6211\u62ff\u4e0b\uff0c\u91cd\u67b7\u9501\u7ed1\uff0c\u62bc\u56de\u5f00\u5c01 \u5e9c\u7262\u3002\u201d \u3000\u3000\u7acb\u523b\u6709\u4ece\u536b\u5e94\u58f0\u800c\u5165\uff0c\u94d0\u4e86\u71d5\u98de\uff0c\u5e26\u51fa\u4e66\u623f\uff0c\u8fd4\u56de\u77e5\u5e9c\u8859\u95e8\u3002 \u3000\u3000\u738b\u91cd\u5c71\u60f3\u963b\u6b62\uff0c\u4f46\u89c1\u738b\u5f64\u89c6\u82e5\u65e0\u7779\uff0c\u53ea\u597d\u6291\u5236\u4f4f\u6fc0\u52a8\u7684\u5fc3\u60c5\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u4e66\u623f\u4e2d\uff0c\u53ea\u6709\u9a6c\u6587\u4e2d\u4e00\u4eba\uff0c\u738b\u5f64\u5982\u6709\u64d2\u62ff\u4ed6\u7684\u7528\u5fc3\uff0c\u5e94\u8be5\u662f\u6700\u597d\u7684\u673a\u4f1a\u3002 \u3000\u3000\u4f46\u738b\u5f64\u5374\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\uff01\u5730\u65b9\u5b98\uff0c\u4e0d\u80fd\u7ee5\u9756\u5730\u65b9\uff0c\u4efb\u7531\u76d7\u532a\u51fa\u6ca1\uff0c\u5e94\u8be5\u4e25\u529e\uff0c\u6587 \u4e2d\u5144\u679c\u7136\u662f\u6cbb\u4e8b\u6709\u65b9\u3002\u201d \u3000\u3000\u201c\u5144\u5f1f\u662f\u6709\u82e6\u96be\u8a00\u54ea\u2026\u2026\u201d\u9a6c\u6587\u4e2d\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5723\u4e0a\u5341\u5206\u9707\u6012\u5427\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4ef6\u4e8b\uff0c\u8fd8\u88ab\u5144\u5f1f\u538b\u7740\uff0c\u4f46\u65f6\u95f4\u4e0d\u80fd\u592a\u4e45\uff0c\u53ea\u5f1f\u517c\u7a0b\u8d76\u6765\uff0c\u5c31\u662f\u60f3\u548c\u9a6c\u5144\u7814\u5546\u4e2a\u5e94\u5bf9 \u4e4b\u6cd5\u2026\u2026\u201d\u738b\u5f64\u8bf4\uff1a\u201c\u6700\u597d\u662f\u5148\u628a\u4e09\u516c\u4e3b\u6551\u51fa\u6765\uff0c\u90a3\u5c31\u5e73\u5b89\u65e0\u4e8b\u4e86\u3002\u201d \u3000\u3000\u201c\u6587\u4e2d\u66fe\u91cd\u91d1\u793c\u8058\u6c5f\u5343\u91cc\uff0c\u8ffd\u67e5\u4e09\u516c\u4e3b\u7684\u884c\u8e2a\uff0c\u6599\u4e0d\u5230\u6c5f\u5343\u91cc\u4e2d\u9014\u79bb\u53bb\uff0c\u4ed6\u5e26\u8d70\u4e86\u4e07\u4e24 \u91cd\u91d1\uff0c\u4e09\u516c\u4e3b\u4ecd\u662f\u674f\u5982\u9ec4\u9e64\uff0c\u6587\u4e2d\u5b9e\u4e0d\u77e5\u5982\u4f55\u4e0b\u624b\u4e86\uff0c\u738b\u5144\u4f55\u4ee5\u6559\u6211\uff1f\u201d \u3000\u3000\u738b\u5f64\u6697\u6697\u5401\u4e00\u53e3\u6c14\uff0c\u5fd6\u9053\uff1a\u5982\u975e\u5148\u5f97\u71d5\u98de\u62a5\u544a\uff0c\u771f\u8981\u88ab\u4ed6\u5f04\u5f97\u6655\u5934\u8f6c\u5411\u4e86\uff0c\u8fd9\u4e2a\u4eba\u5b9e\u4e0d \u7b80\u5355\u3002 \u3000\u3000\u4f46\u662f\uff0c\u738b\u5f64\u5374\u6545\u610f\u76b1\u8d77\u7709\u5934\uff0c\u9053\uff1a\u201c\u8fd9\u2026\u2026\u8fd9\u5c31\u6709\u4e9b\u9ebb\u70e6\u4e86\uff0c\u6587\u4e2d\u5144\uff0c\u4e09\u516c\u4e3b\u6700\u53d7\u5f53\u4eca \u5ba0\u7231\uff0c\u51fa\u4e86\u4e8b\uff0c\u4e0d\u4f46\u5144\u5f1f\u8fd9\u4e2a\u5185\u5bab\u4f8d\u536b\u7edf\u9886\u62c5\u5f85\u4e0d\u8d77\uff0c\u53ea\u6015\u9a6c\u5144\u8fd9\u4e2a\u5de1\u629a\u7684\u804c\u4f4d\u2026\u2026\u201d \u3000\u3000\u201c\u4e22\u5b98\u7684\u4e8b\uff0c\u81ea\u5728\u9884\u6599\u4e4b\u4e2d\uff0c\u6015\u53ea\u6015\u8981\u7978\u8fde\u6ee1\u95e8\u2026\u2026\u201d\u9a6c\u6587\u4e2d\u9053\uff1a\u201c\u82e5\u662f\u7f6a\u53ca\u4e5d\u65cf\uff0c\u6211 \u5c82\u4e0d\u6210\u4e86\u9a6c\u5bb6\u7684\u7f6a\u4eba\uff1f\u60f3\u6765\u771f\u662f\u60f6\u6050\u5f97\u5f88\u3002\u201d \u3000\u3000\u738b\u5f64\u5fd6\u9053\uff1a\u4ed6\u53ea\u8c08\u60f6\u6050\u5374\u4e0d\u63d0\u529e\u6cd5\uff0c\u5fc3\u673a\u4e4b\u6df1\uff0c\u679c\u7136\u53ef\u6015\u3002\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u6587\u4e2d\u5144\uff0c\u73b0\u5728 \u662f\u6551\u4eba\u8981\u7d27\uff0c\u53ef\u5426\u63d0\u4f9b\u4e00\u4e9b\u8ffd\u67e5\u7684\u7ebf\u7d22\uff1f \u3000\u3000\u201c\u71d5\u98de\u5df2\u89c1\u8fc7\u4e86\u7edf\u9886\uff0c\u96be\u9053\u4ed6\u6ca1\u6709\u63d0\u4f9b\u7ebf\u7d22\u4e48\uff1f\u201d \u3000\u3000\u201c\u5509\uff01\u4ed6\u5982\u662f\u63d0\u4f9b\u4e86\u7ebf\u7d22\uff0c\u6211\u600e\u4e48\u4f1a\u6765\u6b64\u6253\u6270\u9a6c\u5144\u2026\u2026\u201d\u738b\u5f64\u4f4e\u58f0\u8bf4\uff1a\u201c\u6c5f\u5343\u91cc\u8ffd\u8e2a\u4e4b \u80fd\uff0c\u5929\u4e0b\u7b2c\u4e00\uff0c\u96be\u9053\u6ca1\u6709\u544a\u8bc9\u9a6c\u5144\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u63d0\u8d77\u6b64\u4eba\uff0c\u5144\u5f1f\u5c31\u6709\u4e9b\u6124\u6012\u96be\u5e73\uff0c\u6c5f\u6e56\u4e2d\u4eba\uff0c\u4e0d\u80fd\u7ed9\u4eba\u4fe1\u4efb\uff0c\u9a97\u94b1\u9a97\u4eba\uff0c\u53ef\u6076\u81f3\u6781\uff0c \u518d\u72af\u6211\u624b\uff0c\u7acb\u6740\u65e0\u8d66\u3002\u201d \u3000\u3000\u9a6c\u5de1\u629a\u7684\u8138\u4e0a\uff0c\u6cdb\u8d77\u4e86\u51b7\u5389\u7684\u6740\u673a\u3002 \u3000\u3000\u738b\u5f64\u773c\u770b\u6cb9\u7530\u96be\u6d78\uff0c\u518d\u8c08\u4e0b\u53bb\uff0c\u4ea6\u65e0\u6536\u83b7\uff0c\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u5144\u5f1f\u544a\u8f9e\u4e86\uff01\u201d \u3000\u3000\u201c\u7edf\u9886\u610f\u6b32\u4f55\u5f80\uff1f\u201d \u3000\u3000\u201c\u9a6c\u5927\u4eba\u65e0\u6cd5\u63d0\u4f9b\u52a9\u529b\uff0c\u5144\u5f1f\u53ea\u597d\u81ea\u529b\u8ffd\u67e5\u4e86\u3002\u201d \u3000\u3000\u201c\u975e\u4e0d\u6b32\u4e5f\uff0c\u5b9e\u4e0d\u80fd\u4e5f\uff0c\u6587\u4e2d\u5c31\u5728\u8857\u4e2d\u606d\u5019\u6d88\u606f\u3002\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u597d\uff0c\u627e\u51fa\u7ebf\u7d22\uff0c\u8fd8\u671b\u5de1\u629a\u5927\u4eba\u80fd\u4e88\u534f\u52a9\uff0c\u7f09\u62ff\u51f6\u624b\u3002\u201d \u3000\u3000\u201c\u90a3\u5f53\u7136\uff0c\u672c\u629a\u804c\u8d23\u6240\u5728\uff0c\u81ea\u662f\u4e0d\u5bb9\u63a8\u8f9e\u3002\u201d \u3000\u3000\u79bb\u5f00\u4e86\u5de1\u629a\u5e9c\uff0c\u738b\u91cd\u5c71\u5fff\u5fff\u7684\u8bf4\uff1a\u201c\u4ed6\u597d\u50cf\u5b8c\u5168\u7f6e\u5e72\u4e8b\u5916\u7684\u6837\u5b50\u2026\u2026\u201d \u3000\u3000\u201c\u73b0\u5728\uff0c\u54b1\u4eec\u8981\u627e\u4e00\u5904\u53ef\u4ee5\u5b89\u8eab\u7684\u5730\u65b9\uff0c\u6700\u597d\u662f\u6613\u5b88\u96be\u653b\u7684\u6240\u5728\u3002\u201d \u3000\u3000\u738b\u91cd\u5c71\u9053\uff1a\u201c\u600e\u4e48\uff1f\u9a6c\u5de1\u629a\u2026\u2026\u201d \u3000\u3000\u201c\u4ed6\u5df2\u7ecf\u6446\u51fa\u4e86\u7ffb\u8138\u7684\u67b6\u5f0f\uff0c\u6700\u597d\u7684\u529e\u6cd5\uff0c\u662f\u628a\u54b1\u4eec\u5b8c\u5168\u6740\u6b7b\u2026\u2026\u201d\u738b\u5f64\u9053\uff1a\u201c\u6211\u5343\u601d \u4e07\u60f3\uff0c\u7adf\u672a\u60f3\u5230\u4ed6\u5982\u6b64\u9634\u72e0\uff01\u201d \u3000\u3000\u738b\u91cd\u5c71\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u71d5\u98de\u4e5f\u88ab\u4ed6\u4eec\u62bc\u56de\u5f00\u5c01\u5e9c\u7262\u4e2d\u4e86\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u53c8\u63a5\u9053\uff1a\u201c\u65e2\u7136\u8981\u7ffb\u8138\u4e86\uff0c\u521a\u624d\u4f55\u4e0d\u5148\u628a\u9a6c\u5de1\u629a\u62ff\u4e0b\u518d\u8bf4\uff1f\u201d \u3000\u3000\u201c\u62ff\u4e0b\u4ed6\uff0c\u5c82\u4e0d\u662f\u903c\u4ed6\u4eec\u6740\u5bb3\u4e09\u516c\u4e3b\u2026\u2026\u201d\u738b\u5f64\u8bf4\uff1a\u201c\u6d88\u606f\u4e00\u65e6\u6cc4\u6f0f\uff0c\u9a6c\u6587\u4e2d\u5fc3\u4e2d\u518d\u65e0 \u987e\u5fcc\uff0c\u5fc5\u5c06\u503e\u5c3d\u5168\u529b\u5bf9\u4ed8\u54b1\u4eec\u2026\u2026\u201d \u3000\u3000\u201c\u4ed6\u5df2\u7ecf\u62c9\u4e0b\u8138\u4e86\uff0c\u96be\u9053\u8fd8\u4f1a\u624b\u4e0b\u7559\u60c5\u4e0d\u6210\uff1f\u201d\u738b\u91cd\u5c71\u4e0d\u592a\u670d\u6c14\u5730\u8bf4\uff1a\u201c\u4ed6\u793a\u610f\u5c39\u77e5\u5e9c \u62ff\u4e0b\u71d5\u98de\uff0c\u5df2\u7ecf\u662f\u8868\u660e\u4e86\u8981\u5207\u65ad\u6211\u4eec\u7684\u652f\u63f4\u529b\u91cf\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff01\u201d\u738b\u5f64\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u62ff\u4e0b\u71d5\u98de\u662f\u4e00\u7740\u9ad8\u62db\uff0c\u65ad\u4e86\u6211\u4eec\u7684\u8054\u7cfb\uff0c\u5c01\u95ed\u4e86\u6211\u4eec\u8033 \u76ee\uff0c\u5728\u5f00\u5c01\u6211\u4eec\u4eba\u5730\u751f\u758f\uff0c\u4e5f\u6ca1\u6cd5\u9003\u8fc7\u4ed6\u4eec\u6697\u4e2d\u7684\u76d1\u89c6\uff0c\u4f46\u6ca1\u6709\u5f53\u9762\u6495\u7834\u8138\uff0c\u4ed6\u5fc3\u4e2d\u591a\u5c11\u8fd8 \u6709\u70b9\u987e\u5fcc\uff0c\u4e0d\u81f3\u4e8e\u52a8\u7528\u5b98\u5175\uff0c\u660e\u76ee\u5f20\u80c6\u7684\u5bf9\u4ed8\u6211\u4eec\u3002\u201d \u3000\u3000\u738b\u91cd\u5c71\u770b\u770b\u5929\u8272\uff0c\u5df2\u662f\u201c\u5915\u9633\u65e0\u9650\u597d\uff0c\u53ea\u662f\u8fd1\u9ec4\u660f\u201d\u7684\u65f6\u523b\uff0c\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4eca\u665a \u4e0a\uff0c\u4ed6\u4eec\u4f1a\u4e0d\u4f1a\u5c31\u4e0b\u624b\u5462\uff1f\u201d \u3000\u3000\u201c\u4f1a\uff0c\u800c\u4e14\u662f\u5168\u529b\u65bd\u4e3a\u2026\u2026\u201d\u738b\u5f64\u8bf4\uff1a\u201c\u6240\u4ee5\uff0c\u6211\u4eec\u65e9\u4e9b\u627e\u4e00\u5904\u843d\u811a\u7684\u5730\u65b9\uff0c\u4f11\u606f\u4e00 \u4e0b\uff0c\u6700\u8fdf\u4e09\u66f4\uff0c\u4ed6\u4eec\u5c31\u4f1a\u5927\u4e3e\u6765\u72af\u4e86\u3002\u201d \u3000\u3000\u4e00\u76f4\u9759\u9759\u7684\u8ffd\u968f\u738b\u5f64\u8eab\u540e\u7684\u4e24\u4e2a\u4f8d\u536b\uff0c\u5de6\u4fa7\u4e00\u4e2a\u7a81\u7136\u5f00\u4e86\u53e3\uff0c\u9053\uff1a\u201c\u7edf\u9886\uff0c\u6211\u4eec\u8fde\u591c\u79bb \u5f00\uff0c\u6e21\u6cb3\u5317\u4e0a\uff0c\u53ec\u96c6\u4e86\u4eba\u624b\u518d\u6765\u6349\u62ff\u53db\u9006\u3002\u201d \u3000\u3000\u201c\u8d70\u4e0d\u4e86\u7684\uff0c\u54b1\u4eec\u79bb\u6b64\u5317\u4e0a\uff0c\u6b63\u662f\u9a6c\u6587\u4e2d\u6240\u671f\u671b\u7684\u4e8b\uff0c\u4ed6\u4eec\u5728\u9014\u4e2d\u57cb\u4f0f\u91cd\u5175\uff0c\u5168\u529b\u622a \u6740\uff0c\u8352\u91ce\u65e0\u4eba\uff0c\u6b63\u597d\u7ed9\u4ed6\u4eec\u5927\u529b\u56f4\u653b\u7684\u673a\u4f1a\u2026\u2026\u201d\u738b\u5f64\u53cc\u76ee\u4e2d\u795e\u5149\u95ea\u52a8\uff0c\u56db\u4e0b\u63a2\u89c6\u4e00\u9635\uff0c\u82e6 \u7b11\u9053\uff1a\u201c\u73b0\u5728\uff0c\u4ed6\u4eec\u81f3\u5c11\u6709\u516d\u4e2a\u4eba\u5728\u76ef\u7740\u6211\u4eec\u3002\u201d \u3000\u3000\u4e24\u4e2a\u968f\u884c\u4f8d\u536b\u662f\u738b\u5f64\u6311\u9009\u7684\u968f\u8eab\u62a4\u4ece\uff0c\u6b66\u529f\u9ad8\u5f3a\uff0c\u5e74\u7eaa\u4e0d\u5927\uff0c\u4e00\u4e2a\u53eb\u8d75\u4fdd\u4e00\u4e2a\u53eb\u9648\u5b8f\uff0c \u5df2\u8ffd\u968f\u738b\u5f64\u4e24\u5e74\u4e4b\u4e45\uff0c\u5f97\u738b\u5f64\u6307\u70b9\uff0c\u5df2\u6df1\u5f97\u5bab\u5ef7\u5200\u6cd5\u7684\u7cbe\u8981\u3002 \u3000\u3000\u539f\u6765\uff0c\u5bab\u5ef7\u4e2d\u53e6\u6709\u4e00\u5957\u5251\u672f\u3001\u5200\u6cd5\uff0c\u662f\u6536\u96c6\u5929\u4e0b\u5251\u62db\u3001\u5200\u6cd5\u4e4b\u957f\uff0c\u7ec4\u5408\u800c\u6210\u7684\u4e00\u5957\u6b66 \u529f\uff0c\u90fd\u662f\u4ee5\u76f4\u63a5\u6740\u4f24\u654c\u4eba\u4e3a\u4e3b\uff0c\u662f\u4e00\u79cd\u975e\u5e38\u5b9e\u7528\u7684\u6b66\u529f\u3002 \u3000\u3000\u8d75\u4fdd\u3001\u9648\u5b8f\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u9f50\u58f0\u8bf4\u9053\uff1a\u201c\u65e2\u5df2\u5982\u6b64\uff0c\u4f55\u4e0d\u64d2\u8d3c\u64d2\u738b\uff0c\u51b2\u5165\u629a\u5e9c\uff0c\u653e\u624b\u4e00 \u6218\uff0c\u5982\u80fd\u5236\u4f4f\u9a6c\u5de1\u629a\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u884c\uff01\u201d\u738b\u5f64\u9053\uff1a\u201c\u90a3\u662f\u7ed9\u9a6c\u6587\u4e2d\u52a8\u7528\u5b98\u5175\u7684\u501f\u53e3\uff0c\u4f55\u51b5\u2026\u2026\u201d\u4f55\u51b5\u600e\u6837\uff0c\u4ed6\u6ca1\u8bf4\u4e0b \u53bb\u7adf\u4e5f\u65e0\u4eba\u6ce8\u610f\u8ffd\u95ee\u4e0b\u53bb\u3002 \u3000\u3000\u738b\u91cd\u5c71\u76b1\u8d77\u4e86\u7709\u5934\uff0c\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u5c0f\u4f84\u8fd8\u672a\u719f\u6089\u5f00\u5c01\u5e9c\u7684\u5f62\u52bf\uff0c\u60f3\u4e0d\u51fa\u4ec0\u4e48\u5730\u65b9\u6613\u5b88\u96be \u653b\u2026\u2026\u201d \u3000\u3000\u738b\u5f64\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8bf4\u4e0d\u5f97\uff0c\u53ea\u597d\u53bb\u9ebb\u70e6\u4e00\u6b21\u8001\u548c\u5c1a\u4e86\u2026\u2026\u201d \u3000\u3000\u76f8\u56fd\u5bfa\u7684\u4e3b\u6301\u5fc3\u5370\u5927\u5e08\u5c31\u662f\u4e2a\u8001\u548c\u5c1a\uff0c\u8bf4\u4e0d\u51fa\u4ed6\u6709\u591a\u5927\u7684\u5e74\u7eaa\uff0c\u53cc\u7709\u96ea\u767d\uff0c\u8138\u4e0a\u5e03\u6ee1\u4e86 \u76b1\u7eb9\u3002 \u3000\u3000\u4ed6\u5728\u7985\u623f\u4e2d\u63a5\u89c1\u4e86\u738b\u5f64\u4e00\u884c\u4eba\uff0c\u4f46\u5374\u6ca1\u6709\u6b22\u8fce\u4f73\u5bbe\u7684\u5fae\u7b11\uff0c\u795e\u60c5\u672a\u7136\u3001\u51b7\u6f20\u3002 \u3000\u3000\u201c\u6211\u4eec\u6765\u6b64\u501f\u4f4f\u51e0\u65e5\u2026\u2026\u201d\u4e3e\u624b\u4e92\u51fb\u4e00\u638c\uff0c\u4e00\u4e2a\u5c0f\u6c99\u5f25\u5e94\u58f0\u800c\u5165\u3002 \u3000\u3000\u201c\u5e26\u4ed6\u4eec\u5230\u540e\u9662\u97ec\u5149\u517b\u6666\u5802\u2026\u2026\u201d\u5fc3\u5370\u5927\u5e08\u8bf4\uff1a\u201c\u518d\u51c6\u5907\u4e00\u5e2d\u7d20\u658b\u9001\u53bb\u3002\u201d \u3000\u3000\u5c0f\u6c99\u5f25\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u97ec\u5149\u517b\u6666\u5802\u548c\u4e00\u5e2d\u7d20\u658b\uff1f\u201d \u3000\u3000\u5fc3\u5370\u5927\u5e08\u5408\u638c\u5f53\u80f8\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5b7d\u56e0\u6076\u679c\uff0c\u963f\u5f25\u9640\u4f5b\uff01\u201d \u3000\u3000\u5c0f\u6c99\u5f25\u82e5\u6709\u6240\u609f\uff0c\u5408\u5341\u8bf4\u9053\uff1a\u201c\u5c0f\u50e7\u4e3a\u8bf8\u4f4d\u65bd\u4e3b\u5e26\u8def\u3002\u201d \u3000\u3000\u8f6c\u8eab\uff0c\u5f53\u5148\u800c\u884c\u3002 \u3000\u3000\u738b\u5f64\u7b49\u56db\u4eba\u9c7c\u8d2f\u76f8\u968f\uff0c\u8fdb\u4eba\u4e86\u4e00\u5ea7\u5706\u6708\u95e8\u7684\u8de8\u9662\u4e2d\u3002 \u3000\u3000\u4e24\u68f5\u9ad8\u5927\u7684\u767d\u6768\u6811\uff0c\u904d\u5730\u53ca\u819d\u7684\u9752\u8349\uff0c\u4f7f\u8fd9\u5ea7\u8de8\u9662\u770b\u8d77\u6765\u6709\u70b9\u9634\u68ee\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u5ea7\u4e09\u5408\u9662\u843d\uff0c\u4e1c\u897f\u4e24\u53a2\u6728\u95e8\u7d27\u95ed\uff0c\u5411\u5357\u7684\u5927\u5385\u5374\u662f\u95e8\u6237\u5927\u95e8\u3002 \u3000\u3000\u76f8\u56fd\u5bfa\u5728\u5f00\u5c01\u5e9c\u662f\u4e2a\u6742\u800d\u4e91\u96c6\u3001\u767e\u4e1a\u6742\u9648\u7684\u5730\u65b9\uff0c\u4eba\u53e3\u6d3b\u52a8\u529b\u5341\u5206\u5f3a\u5927\uff0c\u975e\u5e38\u7684\u70ed\u95f9\uff0c \u4f46\u8fd9\u5ea7\u8de8\u9662\u4e2d\u7684\u5e7d\u5bc2\u548c\u5bfa\u5916\u7684\u70ed\u95f9\u7eb7\u6518\uff0c\u6210\u4e86\u4e24\u4e2a\u5b8c\u5168\u4e0d\u540c\u7684\u4e16\u754c\u3002 \u3000\u3000\u5c0f\u6c99\u5f25\u5e26\u51e0\u4eba\u76f4\u5165\u6b63\u5385\uff0c\u70b9\u71c3\u8d77\u684c\u4e0a\u7684\u706b\u70db\u3002 \u3000\u3000\u8fd9\u662f\u4e09\u95f4\u76f8\u8fde\u7684\u5927\u5385\uff0c\u4e2d\u95f4\u6709\u4e00\u5f20\u5927\u65b9\u684c\uff0c\u516b\u5f20\u5206\u6446\u56db\u9762\u7684\u592a\u5e08\u6905\u5916\uff0c\u518d\u65e0\u9648\u8bbe\u3002 \u3000\u3000\u201c\u56db\u4f4d\u65bd\u4e3b\u8bf7\u5750\uff0c\u5c0f\u50e7\u53bb\u5429\u5490\u4ed6\u4eec\u5949\u8336\u6446\u5e2d\u3002\u201d \u3000\u3000\u56db\u4e2a\u4eba\u90fd\u6709\u7740\u9965\u997f\u7684\u611f\u89c9\uff0c\u6240\u4ee5\uff0c\u6ca1\u6709\u4eba\u63a8\u8f9e\u5ba2\u6c14\uff0c\u4f55\u51b5\uff0c\u4eca\u591c\u53ef\u80fd\u6709\u4e00\u573a\u751f\u6b7b\u4e4b\u640f\uff0c \u6025\u9700\u8981\u9971\u9910\u4e00\u987f\uff0c\u517b\u606f\u6218\u529b\u3002 \u3000\u3000\u76ee\u7779\u5c0f\u6c99\u5f25\u79bb\u53bb\u4e4b\u540e\uff0c\u738b\u5f64\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4eca\u591c\u4e4b\u6218\uff0c\u654c\u4eba\u653b\u52bf\u5fc5\u6781\u731b\u70c8\uff0c\u975e\u81f3\u6027\u547d\u4ea4 \u5173\uff0c\u4e0d\u5fc5\u5168\u529b\u53cd\u51fb\uff0c\u5200\u6cd5\u3001\u5251\u62db\u5c3d\u7ba1\u8f9b\u8fa3\uff0c\u4f46\u5374\u8981\u4fdd\u7559\u4f53\u80fd\uff0c\u62d6\u957f\u65f6\u95f4\uff0c\u6211\u76f8\u4fe1\u4ed6\u4eec\u5728\u65e5\u521d \u4e4b\u524d\uff0c\u5b9a\u4f1a\u64a4\u8d70\uff0c\u8fd9\u4e00\u6218\uff0c\u6211\u4eec\u4ee5\u4fdd\u547d\u3001\u9632\u5b88\u4e3a\u4e3b\u2026\u2026\u201d \u3000\u3000\u8bf4\u7740\uff0c\u76ee\u5149\u8f6c\u6ce8\u5230\u738b\u91cd\u5c71\u8138\u4e0a\uff0c\u53c8\u9053\uff1a\u201c\u6b66\u5f53\u5251\u6cd5\u4ee5\u7ef5\u67d4\u89c1\u957f\uff0c\u4ee5\u4f60\u7684\u6210\u5c31\u8db3\u53ef\u62b5\u5fa1\u8ffd \u9b42\u4e03\u715e\u7684\u56f4\u653b\u5728\u767e\u62db\u4ee5\u4e0a\uff0c\u4f46\u4f60\u5374\u5728\u51fa\u624b\u4e00\u51fb\u4e2d\uff0c\u5168\u529b\u65bd\u4e3a\uff0c\u4ee5\u9634\u67d4\u4e4b\u52b2\uff0c\u65bd\u5c55\u9633\u521a\u53cd\u51fb\uff0c \u81f3\u5185\u529b\u8fd8\u672a\u5f00\u59cb\u5faa\u8f6c\uff0c\u5df2\u7136\u65e0\u4ee5\u4e3a\u7ee7\uff0c\u51e0\u81f3\u4e3a\u654c\u6240\u4e58\u2026\u2026\u201d \u3000\u3000\u201c\u662f\uff01\u5c0f\u4f84\u4e5f\u60f3\u901a\u4e86\u8fd9\u4e2a\u9053\u7406\u4e86\u3002\u201d \u3000\u3000\u738b\u5f64\u70b9\u70b9\u5934\uff0c\u76ee\u5149\u4e00\u63a0\u8d75\u4fdd\u3001\u9648\u5b8f\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u4e5f\u8981\u8bb0\u7740\uff0c\u5bab\u5ef7\u5200\u6cd5\uff0c\u672c\u662f\u4ee5\u4f24\u4eba\u4e3a\u4e3b \u7684\u5b9e\u7528\u6b66\u529f\uff0c\u4ee5\u8be1\u53d8\u80dc\u654c\uff0c\u5c3d\u91cf\u907f\u514d\u548c\u4eba\u786c\u62da\u5185\u529b\uff0c\u9700\u77e5\uff0c\u5185\u529b\u8017\u5c3d\uff0c\u518d\u7cbe\u5947\u7684\u5200\u6cd5\u4e5f\u96be\u89c1 \u5a01\u529b\uff0c\u654c\u4f17\u6211\u5be1\uff0c\u8981\u5c3d\u91cf\u5229\u7528\u4efb\u4f55\u5bf9\u6211\u6709\u5229\u7684\u5f62\u52bf\uff0c\u4e00\u52a8\u4e0a\u624b\uff0c\u4ed6\u4eec\u5c31\u4e0d\u4f1a\u8ba9\u6211\u4eec\u6709\u5598\u606f\u7684 \u673a\u4f1a\u3002\u201d \u3000\u3000\u8d75\u4fdd\u3001\u9648\u5b8f\uff0c\u606d\u8c28\u53d7\u6559\u3002 \u3000\u3000\u738b\u91cd\u5c71\u5fc3\u4e2d\u6709\u5f88\u591a\u7591\u70b9\uff0c\u6b63\u60f3\u5f00\u53e3\u8be2\u95ee\uff0c\u5c0f\u6c99\u5f25\u5df2\u5e26\u7740\u4e09\u4e2a\u5927\u548c\u5c1a\uff0c\u9001\u4e0a\u4e86\u665a\u9910\u3002 \u3000\u3000\u7cbe\u81f4\u7684\u7d20\u658b\uff0c\u6446\u6ee1\u4e86\u4e00\u5ea7\u65b9\u684c\uff0c\u52ff\u602a\u8981\u52a8\u7528\u4e09\u4e2a\u5927\u548c\u5c1a\u4e00\u8d77\u9001\u6765\u4e86\u3002 \u3000\u3000\u5c0f\u6c99\u5f25\u5bf9\u738b\u5f64\u8eac\u8eab\u5408\u638c\uff0c\u9053\uff1a\u201c\u8bf8\u4f4d\u65bd\u4e3b\u8bf7\u8fdb\u665a\u9910\uff0c\u5bb6\u5e08\u4e0d\u5949\u966a\u4e86\u3002\u201d\u4e5f\u4e0d\u5f85\u738b\u5f64\u8bf4 \u8bdd\uff0c\u8f6c\u8eab\u5c31\u8d70\u4e86\u3002 \u3000\u3000\u738b\u5f64\u5f53\u5148\u4e3e\u7b77\uff0c\u9053\uff1a\u201c\u5927\u5bb6\u5403\u5427\uff01\u201d \u3000\u3000\u201c\u8001\u548c\u5c1a\u51b7\u6f20\u5f97\u4e0d\u8fd1\u4eba\u60c5\u2026\u2026\u201d\u738b\u91cd\u5c71\u8bf4\uff1a\u201c\u4f46\u8fd9\u4e00\u684c\u7cbe\u81f4\u7684\u658b\u83dc\uff0c\u53c8\u50cf\u662f\u63a5\u5f85\u4e0a \u5bbe\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u8fd9\u662f\u76f8\u56fd\u5bfa\u4e2d\u6700\u597d\u7684\u5e2d\uff0c\u8bf8\u4f4d\u90fd\u662f\u4e3b\u6301\u65b9\u4e08\u5fc3\u76ee\u4e2d\u7684\u8d35\u5bbe\u3002\u201d \u3000\u3000\u4e0d\u77e5\u4f55\u65f6\uff0c\u4e00\u4e2a\u8eab\u7740\u53ca\u819d\u7684\u7070\u886b\u3001\u8db3\u7740\u589e\u5c65\u7684\u5e74\u8f7b\u548c\u5c1a\uff0c\u5df2\u7ad9\u7acb\u5728\u5385\u95e8\u53e3\u5904\u3002 \u3000\u3000\u706f\u5149\u4e0b\uff0c\u738b\u91cd\u5c71\u770b\u90a3\u5c0f\u548c\u5c1a\u9762\u5982\u9505\u5e95\uff0c\u9ed1\u7684\u900f\u4eae\uff0c\u53cc\u76ee\u4e2d\u795e\u91c7\u7167\u4eba\uff0c\u53ea\u662f\u90a3\u88ad\u7070\u8863\uff0c\u957f \u886b\u4e0d\u50cf\u957f\u886b\uff0c\u8888\u88df\u53c8\u4e0d\u50cf\u8888\u88df\uff0c\u770b\u4e0a\u53bb\u6709\u4e9b\u6ed1\u7a3d\u3002 \u3000\u3000\u201c\u5c0f\u5e08\u7236\u8bf7\u8fdb\u6765\u5750\u2026\u2026\u201d\u738b\u5f64\u9605\u5386\u4e30\u5bcc\uff0c\u4e00\u773c\u5df2\u77a7\u51fa\u8fd9\u5c0f\u548c\u5c1a\u4e0d\u662f\u7b49\u95f2\u4eba\u7269\uff0c\u7ad9\u8d77\u8eab\u5b50 \u65bd\u4e86\u4e00\u793c\u3002 \u3000\u3000\u5c0f\u548c\u5c1a\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c0f\u50e7\u547d\u91cc\u72af\u715e\uff0c\u4e0d\u65e9\u4e0d\u665a\u7684\u8d76\u5230\u4e86\u76f8\u56fd\u5bfa\uff0c\u5fc3\u5370\u5e08\u53d4\u6b63\u5728\u9063\u8d70 \u5bfa\u4e2d\u50e7\u4fa3\uff0c\u65e0\u6687\u5949\u966a\uff0c\u53ea\u597d\u6d3e\u6211\u5c0f\u548c\u5c1a\u5148\u6765\u62db\u547c\u5ba2\u4eba\u2026\u2026\u201d \u3000\u3000\u201c\u738b\u5f64\u611f\u6fc0\u4e07\u5206\uff0c\u8001\u548c\u5c1a\u7684\u6069\u5fb7\uff0c\u65e5\u540e\u5fc5\u5f53\u62a5\u7b54\u3002\u201d \u3000\u3000\u53cc\u65b9\u9762\u5fc3\u7167\u4e0d\u5ba3\uff0c\u8001\u548c\u5c1a\u9063\u8d70\u90e8\u5206\u50e7\u4f17\uff0c\u4f3c\u662f\u5df2\u51b3\u5fc3\u6dcc\u8fd9\u6b21\u6df7\u6c34\u4e86\u3002 \u3000\u3000\u539f\u6765\uff0c\u76f8\u56fd\u5bfa\u4e2d\uff0c\u6709\u7edd\u5927\u90e8\u5206\u7684\u50e7\u4fa3\u672a\u4e60\u6b66\u529f\u3002 \u3000\u3000\u9ed1\u8138\u548c\u5c1a\u6b65\u5165\u5385\u5802\uff0c\u9053\uff1a\u201c\u8bf7\u5750\uff01\u8bf7\u5750\uff01\u201d\u81ea\u5df1\u5374\u5148\u5728\u4e3b\u4f4d\u5750\u4e0b\u3002 \u3000\u3000\u8d75\u4fdd\u5fc3\u4e2d\u5fd6\u9053\uff1a\u8001\u548c\u5c1a\u9762\u51b7\u5fc3\u70ed\uff0c\u5012\u4e5f\u7f62\u4e86\uff0c\u8fd9\u5c0f\u548c\u5c1a\u7adf\u4e5f\u5982\u6b64\u653e\u8086\uff0c\u7edf\u9886\u8fd8\u672a\u843d\u5750\uff0c \u4ed6\u5012\u5148\u5750\u4e0b\u4e86\u3002 \u3000\u3000\u5185\u5bab\u4f8d\u536b\u5230\u5904\u53d7\u4eba\u5949\u627f\uff0c\u6b64\u756a\u8eab\u5904\u9006\u5883\uff0c\u987f\u89c9\u5904\u5904\u53d7\u6c14\u3002 \u3000\u3000\u738b\u5f64\u5374\u4f3c\u5168\u4e0d\u653e\u5728\u5fc3\u4e0a\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c0f\u5e08\u7236\u7684\u6cd5\u53f7\u53ef\u5426\u89c1\u793a\uff1f\u201d \u3000\u3000\u201c\u5c0f\u548c\u5c1a\u91ce\u60ef\u4e86\uff0c\u8fde\u6cd5\u53f7\u90fd\u5eb8\u4fd7\u5f97\u4e0d\u5e26\u7075\u6c14\uff0c\u7edf\u9886\u65e2\u7136\u95ee\u4e86\uff0c\u5c0f\u548c\u5c1a\u4e5f\u4e0d\u4fbf\u4e0d\u8bf4\uff0c\u5c0f\u548c \u5c1a\u6cd5\u540d\u4e03\u5de7\u2026\u2026\u201d \u3000\u3000\u201c\u9ed1\u7f57\u6c49\u4e03\u5de7\u50e7\uff0c\u738b\u67d0\u65e9\u8be5\u60f3\u5230\u7684\u3002\u201d\u738b\u5f64\u5fe7\u82e6\u7684\u795e\u8272\u4e2d\u7efd\u5f00\u4e86\u4e00\u62b9\u7b11\u5bb9\uff0c\u8bf4\uff1a\u201c\u5f53\u771f \u662f\u5931\u656c\u4e86\u3002\u201d \u3000\u3000\u5750\u5728\u4e00\u4fa7\u7684\u738b\u91cd\u5c71\uff0c\u7a81\u7136\u50cf\u88ab\u706b\u70e7\u7740\u4e86\u4e00\u6837\uff0c\u4e00\u4e0b\u5b50\u8df3\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u9ed1\u7f57\u6c49\u4e03\u5de7\u50e7\uff0c \u5e78\u4f1a\u5440\uff01\u5e78\u4f1a\uff01\u5c0f\u5f1f\u738b\u91cd\u5c71\uff0c\u51fa\u8eab\u6b66\u5f53\u95e8\u4e0b\u2026\u2026\u201d\u53e3\u4e2d\u8bf4\u7740\u8bdd\uff0c\u53cc\u624b\u62b1\u4e3e\uff0c\u8fde\u8fde\u6253\u8eac\uff0c\u9053\uff1a \u201c\u4e45\u95fb\u5927\u540d\u2026\u2026\u201d \u3000\u3000\u201c\u597d\u4e86\uff0c\u4f60\u8bf7\u5750\u5427\uff0c\u54b1\u4eec\u4e0d\u6765\u8fd9\u4e00\u5957\uff0c\u4ec0\u4e48\u4e45\u95fb\u5927\u540d\u3001\u5982\u96f7\u8d2f\u8033\uff0c\u4f60\u4eec\u6b66\u5f53\u6d3e\u5012\u6709\u5c0f\u548c \u5c1a\u4e00\u4e2a\u597d\u670b\u53cb\uff0c\u540d\u53eb\u767d\u7fbd\uff0c\u5c0f\u548c\u5c1a\u4e5f\u4e0d\u77e5\u9053\u8fd9\u767d\u7fbd\u662f\u540d\u5b57\u8fd8\u662f\u6cd5\u53f7\uff0c\u4f46\u5374\u4eba\u5982\u5176\u540d\uff0c\u767d\u7684\u50cf \u4e2a\u5927\u59d1\u5a18\u2026\u2026\u201d \u3000\u3000\u201c\u767d\u7fbd\u5e08\u5144\u662f\u6cd5\u53f7\uff0c\u4f60\u7f57\u6c49\u50e7\u7684\u5927\u540d\u53ca\u4e8b\u8ff9\uff0c\u90fd\u662f\u767d\u7fbd\u5e08\u5144\u544a\u8bc9\u6211\u7684\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u7ed3\u4e86\uff0c\u5750\u4e0b\u8bf4\u8bdd\u5427\uff01\u201d \u3000\u3000\u201c\u5bf9\u5bf9\u5bf9\uff0c\u767d\u7fbd\u5e08\u5144\u8bf4\u8fc7\uff0c\u7f57\u6c49\u50e7\u4e00\u5411\u4e0d\u62d8\u4efd\u793c\u3002\u201d \u3000\u3000\u4e03\u5de7\u50e7\u4e00\u76b1\u7709\uff0c\u9053\uff1a\u201c\u738b\u7edf\u9886\uff0c\u8001\u548c\u5c1a\u544a\u8bc9\u6211\u5c0f\u548c\u5c1a\u8bf4\uff0c\u4f60\u4eec\u662f\u8dd1\u5230\u76f8\u56fd\u5bfa\u6765\u907f\u96be\u7684\uff0c \u5bb3\u5f97\u6211\u90a3\u8001\u548c\u5c1a\u5e08\u53d4\uff0c\u628a\u76f8\u56fd\u5bfa\u4f1a\u8d5a\u94b1\u7684\u548c\u5c1a\uff0c\u5168\u90fd\u9020\u5f80\u5bfa\u5916\u907f\u96be\u53bb\u4e86\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u53eb\u8d5a\u94b1\u548c\u5c1a\uff1f\u201d\u738b\u91cd\u5c71\u4e0d\u89e3\u7684\u95ee\u3002 \u3000\u3000\u4e03\u5de7\u501f\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u6bd4\u767d\u8138\u9053\u58eb\u5dee\u8fdc\u4e86\uff0c\u571f\u8c46\u4e00\u4e2a\uff0c\u4ec0\u4e48\u90fd\u4e0d\u61c2\u554a\uff01\u201d \u3000\u3000\u201c\u662f\u554a\uff01\u5c0f\u5f1f\u4e00\u51fa\u5e08\uff0c\u5c31\u88ab\u4e8c\u53d4\u63ea\u5230\u7687\u5bab\u4f5c\u4f8d\u536b\u4e86\uff0c\u4ece\u672a\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\u8fc7\uff0c\u8fd9\u8981\u4f60\u9ed1\u7f57 \u6c49\u6307\u70b9\u6307\u70b9\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\u5427\uff01\u5c0f\u548c\u5c1a\u7684\u6bdb\u75c5\u5c31\u662f\u597d\u4e3a\u4eba\u5e08\uff0c\u4f60\u542c\u7740\uff0c\u8d5a\u94b1\u548c\u5c1a\u5c31\u662f\u4f1a\u5ff5\u7ecf\u7684\u548c\u5c1a\uff0c\u5f00\u5c01\u5e9c\u5730 \u65b9\u5927\u3001\u6cd5\u4e8b\u591a\uff0c\u76f8\u56fd\u5bfa\u53c8\u6709\u540d\u6c14\uff0c\u4e00\u5e74\u5230\u5934\u6cd5\u4e8b\u505a\u4e0d\u5b8c\uff0c\u5e2e\u6211\u5e08\u53d4\u8001\u548c\u5c1a\u635e\u4e86\u4e0d\u5c11\u94f6\u5b50\u3002\u201d \u3000\u3000\u201c\u6211\u61c2\u4e86\uff01\u201d\u738b\u91cd\u5c71\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u6574\u5929\u5fd9\u7740\u5ff5\u7ecf\uff0c\u6ca1\u65f6\u95f4\u5b66\u529f\u592b\u3002\u201d \u3000\u3000\u201c\u884c\uff01\u4f60\u5c0f\u5b50\u4e00\u62e8\u5c31\u8f6c\uff0c\u6211\u770b\u5f00\u6b64\u4e8b\u4e86\uff0c\u4f60\u4e5f\u522b\u5e72\u4ec0\u4e48\u5185\u5bab\u4f8d\u536b\u4e86\uff0c\u54b1\u4eec\u627e\u5230\u767d\u8138\u5c0f\u9053 \u571f\u4f5c\u4f34\uff0c\u6211\u5e26\u7740\u4f60\u4eec\u95ef\u6c5f\u6e56\u53bb\uff0c\u671d\u6e21\u7a77\u5c71\u6076\u6c34\uff0c\u591c\u5bbf\u674f\u82b1\u6c5f\u5357\uff0c\u4e24\u8896\u6e05\u98ce\uff0c\u4e00\u80a9\u660e\u6708\uff0c\u76ee\u7779 \u5343\u5947\u767e\u602a\uff0c\u597d\u73a9\u5f97\u5f88\u54ea\u2026\u2026\u201d \u3000\u3000\u738b\u91cd\u5c71\u60a0\u7136\u795e\u5f80\u5730\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u6709\u4e9b\u5fc3\u733f\u610f\u9a6c\u4e86\u3002\u201d \u3000\u3000\u738b\u5f64\u957f\u957f\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u597d\uff01\u8fd9\u4ef6\u6848\u5b50\u4e00\u4e86\uff0c\u6211\u5c31\u51c6\u4f60\u79bb\u5f00\u3002\u201d \u3000\u3000\u201c\u591a\u8c22\u4e8c\u53d4\u6210\u5168\uff01\u201d \u3000\u3000\u4e03\u5de7\u50e7\u7b11\u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u73a9\u662f\u597d\u73a9\uff0c\u5148\u5f97\u4fdd\u4f4f\u8001\u547d\u624d\u884c\uff0c\u738b\u7edf\u9886\uff0c\u5c0f\u548c\u5c1a\u5e2e\u5fd9\u5e2e\u5230\u5e95\uff0c \u66ff\u4f60\u627e\u4e2a\u5e2e\u624b\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u8001\u548c\u5c1a\u4e3b\u6301\u4e86\u76f8\u56fd\u5bfa\uff0c\u597d\u50cf\u6ca1\u6709\u6536\u8fc7\u4f20\u6388\u6b66\u529f\u7684\u5f1f\u5b50\u2026\u2026\uff02\u201d\u76f8\u56fd\u5bfa\u6709\u4e00\u767e \u591a\u540d\u548c\u5c1a\uff0c\u5b66\u8fc7\u6b66\u529f\u7684\u53ea\u6709\u5341\u4e2a\u4eba\uff0c\u800c\u4e14\u5168\u662f\u4e09\u811a\u732b\u7684\u628a\u5f0f\uff0c\u5de1\u591c\u503c\u66f4\u3001\u6293\u6293\u5c0f\u5077\u8fd8\u53ef\u4ee5\u51d1 \u5408\uff0c\u8981\u4ed6\u4eec\u62ff\u5bb6\u4f19\u548c\u9ad8\u624b\u8fc7\u62db\uff0c\u90a3\u5c31\u662f\u53eb\u4ed6\u4eec\u53bb\u9001\u6b7b\u4e86\u2026\u2026\u201d \u3000\u3000\u4e03\u5de7\u50e7\u795e\u79d8\u7684\u7b11\u4e86\u7b11\uff0c\u53c8\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u5c0f\u548c\u5c1a\u5374\u77e5\u9053\u76f8\u56fd\u5bfa\u4e2d\u8fd8\u6709\u4e00\u4f4d\u9ad8\u624b\u2026\u2026\u201d\u7a81\u7136 \u4f4f\u53e3\u4e0d\u8a00\u3002 \u3000\u3000\u738b\u5f64\u6025\u6025\u95ee\u9053\uff1a\u201c\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u4e03\u5de7\u501f\u6293\u6293\u5149\u8111\u888b\uff0c\u9053\uff1a\u201c\u5c0f\u548c\u5c1a\u867d\u7136\u53e3\u6ca1\u906e\u62e6\uff0c\u6ca1\u5927\u6ca1\u5c0f\u7684\u80e1\u8bcc\uff0c\u4f46\u6211\u90a3\u8001\u548c\u5c1a\u5e08\u53d4 \u771f\u7684\u751f\u6c14\u8d77\u6765\uff0c\u6211\u8fd8\u662f\u6709\u4e9b\u5bb3\u6015\u7684\u3002\u201d \u3000\u3000\u738b\u5f64\u7b49\u56db\u4eba\uff0c\u5fcd\u4e0d\u4f4f\u5fae\u5fae\u4e00\u7b11\u3002 \u3000\u3000\u4e03\u5de7\u50e7\u63a5\u7740\u53c8\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u5fc3\u5370\u5e08\u53d4\u7684\u79d8\u5bc6\uff0c\u77e5\u9053\u7684\u4eba\u5e76\u4e0d\u591a\uff0c\u4e0d\u8fc7\uff0c\u90a3\u4e2a\u4eba\u7684\u6b66\u529f\u5374 \u76f8\u5f53\u9ad8\u660e\u3002\u201d \u3000\u3000\u738b\u91cd\u5c71\u9053\uff1a\u201c\u6bd4\u4f60\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u4e0d\u5728\u5c0f\u548c\u5c1a\u4e4b\u4e0b\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u5012\u771f\u662f\u4e00\u4e2a\u597d\u5e2e\u624b\u2026\u2026\u201d\u738b\u5f64\u9053\uff1a\u201c\u4f60\u5c31\u5c3d\u7ba1\u8bf4\u5427\uff01\u8981\u662f\u8001\u548c\u5c1a\u602a\u7f6a\u4e0b\u6765\uff0c\u4e00\u5207\u7531 \u6211\u6765\u627f\u62c5\u3002\u201d \u3000\u3000\u201c\u4e00\u53e5\u8bdd\uff01\u90a3\u4e2a\u4eba\u5c31\u5173\u5728\u4e1c\u53a2\u623f\u91cc\uff0c\u4f60\u53ea\u8981\u653e\u4ed6\u51fa\u6765\u5c31\u884c\u4e86\uff0c\u5c0f\u548c\u5c1a\u4fdd\u8bc1\u80fd\u8bf4\u670d\u4ed6\u5e2e\u6211 \u4eec\u62d2\u6321\u5f3a\u654c\u3002\u201d \u3000\u3000\u738b\u5f64\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd6\u9053\uff1b\u9ed1\u7f57\u6c49\u4e03\u5de7\u50e7\u662f\u51fa\u4e86\u540d\u7684\u5201\u94bb\u53e4\u602a\uff0c\u90a3\u4eba\u65e2\u6709\u5f88\u9ad8\u7684\u6b66\u529f\uff0c\u4ec0\u4e48 \u5211\u5177\u624d\u80fd\u9501\u5f97\u4f4f\u4ed6\uff1f\u522b\u8981\u662f\u5fc3\u5370\u5927\u5e08\u7684\u751f\u6b7b\u5927\u654c\uff0c\u6211\u53bb\u628a\u4ed6\u653e\u4e86\uff0c\u90a3\u5c82\u4e0d\u662f\u66ff\u8001\u548c\u5c1a\u7559\u4e0b\u540e \u60a3\uff0c\u90a3\u5c31\u5f97\u4e0d\u507f\u5931\u4e86\u3002 \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u91cc\u5374\u95ee\u9053\uff1a\u201c\u4ed6\u5e26\u7684\u662f\u4ec0\u4e48\u5211\u5177\uff1f\u662f\u5426\u53d7\u4f24\u4e86\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u5211\u5177\u2026\u2026\u201d\u4e03\u5de7\u50e7\u9053\uff1a\u201c\u9501\u4f4f\u4ed6\u7684\u662f\u4e00\u628a\u5fc3\u9501\uff0c\u4f60\u53ea\u8981\u5e2e\u4ed6\u6253\u5f00\u5fc3\u9501\uff0c\u4ed6\u5c31\u53ef\u4ee5 \u79bb\u5f00\u4e1c\u53a2\u623f\u4e86\u3002\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u8fd9\u5fc3\u9501\u8981\u5982\u4f55\u624d\u80fd\u6253\u5f00\uff1f\u201d \u3000\u3000\u201c\u5bb9\u6613\u5f97\u5f88\uff0c\u4f60\u63a8\u5f00\u6728\u95e8\u2026\u2026\u201d\u4e03\u5de7\u50e7\u653e\u4f4e\u4e86\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u544a\u8bc9\u4ed6\uff1a\u5357\u6d77\u9f99\u9041\uff0c\u5317\u51e4\u5b64 \u98de\uff0c\u4ed6\u5c31\u4f1a\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u4ee5\u738b\u5f64\u9605\u5386\u7684\u4e30\u5bcc\uff0c\u6c89\u601d\u4e86\u826f\u4e45\uff0c\u4e5f\u60f3\u4e0d\u901a\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u76b1\u76b1\u7709\uff0c\u9053\uff1a\u201c\u5c0f\u548c\u5c1a\uff0c\u80fd \u4e0d\u80fd\u8bf4\u6e05\u695a\uff0c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u4e03\u5de7\u50e7\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u8001\u5b9e\u8bf4\uff0c\u6211\u4e5f\u4e0d\u5927\u6e05\u695a\uff0c\u4f60\u4e0d\u80af\u53bb\uff0c\u90a3\u5c31\u7b97\u4e86\uff0c\u5c0f\u548c\u5c1a\u5df2\u7ecf\u997f \u5f97\u524d\u5fc3\u8d34\u540e\u5fc3\u4e86\uff0c\u54b1\u4eec\u5403\u996d\u5427\uff01\u201d \u3000\u3000\u738b\u91cd\u5c71\u9053\uff1a\u201c\u9ed1\u7f57\u6c49\uff0c\u6211\u53bb\u884c\u4e0d\u884c\uff1f\u201d \u3000\u3000\u201c\u597d\u554a\uff01\u4f60\u5c0f\u5b50\u4e0d\u6015\u65e5\u540e\u9ebb\u70e6\uff0c\u90a3\u5c31\u53bb\u5427\uff01\u4e0d\u8fc7\uff0c\u8bdd\u53ef\u53c8\u8bf4\u56de\u6765\u4e86\uff0c\u65e5\u540e\u7684\u9ebb\u70e6\u53ef\u80fd\u6709 \u89e3\u51b3\u7684\u6cd5\u5b50\uff0c\u603b\u6bd4\u4eca\u665a\u4e0a\u5c31\u4e22\u4e86\u8001\u547d\u597d\u4e9b\u3002\u201d\u4e03\u5de7\u50e7\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u4eba\u65e0\u8fdc\u8651\uff0c\u5fc5\u6709\u8fd1\u5fe7\u3002\u4e0d \u662f\u4e48\uff1f\u201d \u3000\u3000\u201c\u8bf4\u5f97\u5bf9\uff0c\u4ee5\u540e\u7684\u4e8b\uff0c\u4ee5\u540e\u518d\u8bf4\uff0c\u6211\u53bb\u53eb\u4ed6\u51fa\u6765\u3002\u201d\u738b\u91cd\u5c71\u7ad9\u8d77\u4e86\u8eab\u5b50\uff0c\u76f4\u5954\u4e1c\u53a2\u3002 \u3000\u3000\u738b\u5f64\u60f3\u963b\u6b62\uff0c\u4f46\u5374\u53c8\u5f3a\u81ea\u5fcd\u4e0b\u3002 \u3000\u3000\u4e03\u5de7\u50e7\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u8001\u72d0\u72f8\u96be\u6597\uff0c\u8fd8\u662f\u5e74\u8f7b\u7684\u670b\u53cb\u5bb9\u6613\u4e0a\u5f53\u3002\u201d \u3000\u3000\u738b\u5f64\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4e03\u5de7\u50e7\u679c\u7136\u662f\u8be1\u8ba1\u591a\u7aef\uff0c\u6211\u8fd9\u5934\u8001\u72d0\u72f8\u4e5f\u88ab\u4f60\u800d\u5f97\u6655\u5934\u8f6c\u5411\u4e86\uff0c\u7a76 \u7adf\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u201c\u4f5b\u65e5\uff1a\u4e0d\u53ef\u8bf4\uff0c\u4e0d\u53ef\u8bf4\uff0c\u56e0\u4e3a\u4ed6\u4eec\u5df2\u7ecf\u8fc7\u6765\u4e86\u3002\u201d \u3000\u3000\u679c\u7136\uff0c\u738b\u91cd\u5c71\u5e26\u7740\u4e00\u4e2a\u4e09\u5341\u5de6\u53f3\u7684\u9752\u8863\u4eba\uff0c\u7f13\u6b65\u5165\u5385\u3002 \u3000\u3000\u4e03\u5de7\u50e7\u9053\u3002\u201c\u4e24\u4f4d\u5feb\u8bf7\u5165\u5e2d\uff0c\u5c0f\u548c\u5c1a\u5df2\u997f\u5f97\u6ca1\u6709\u6c14\u529b\u8bf4\u8bdd\u4e86\u3002\u201d \u3000\u3000\u5f53\u5148\u4e3e\u7b77\u5927\u5403\u8d77\u6765\u3002 \u3000\u3000\u738b\u5f64\u6697\u9053\uff1a\u5c0f\u548c\u5c1a\u8bf4\u7684\u4e5f\u5bf9\uff0c\u4ee5\u540e\u7684\u4e8b\uff0c\u4ee5\u540e\u518d\u8bf4\uff0c\u5148\u586b\u9971\u809a\u5b50\u3001\u6e21\u8fc7\u4eca\u591c\u4e4b\u5371\uff0c\u518d\u4f5c \u8ba1\u8f83\u3002\u4e5f\u8ddf\u7740\u5927\u5403\u8d77\u6765\u4e86\u3002 \u3000\u3000\u5305\u62ec\u90a3\u9752\u8863\u4eba\u5728\u5185\uff0c\u4f3c\u662f\u90fd\u5f88\u997f\u4e86\uff0c\u6ca1\u6709\u4eba\u518d\u5f00\u53e3\u8bf4\u8bdd\uff0c\u4e00\u7247\u4e3e\u7b77\u8fdb\u9910\u4e4b\u58f0\u3002 \u3000\u3000\u4e03\u5de7\u50e7\u5148\u4e3e\u7b77\uff0c\u4e5f\u5148\u5403\u9971\uff0c\u4e00\u62b9\u5634\uff0c\u671b\u7740\u90a3\u9752\u8863\u4eba\u9053\uff1a\u201c\u4f60\u4ec0\u4e48\u4e5f\u4e0d\u8981\u95ee\uff0c\u5148\u5e2e\u5fd9\u6253\u4e00 \u67b6\uff0c\u6253\u5b8c\u67b6\u518d\u95ee\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u90a3\u9752\u8863\u4eba\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u90fd\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4e00\u4e0b\uff0c\u4e03\u5de7\u50e7\u53ef\u4e5f\u6123\u4f4f\u4e86\uff01 \u3000\u3000\u4f46\u4ed6\u7acb\u523b\u53c8\u660e\u767d\u4e86\uff0c\u90a3\u4e24\u53e5\u6697\u8bed\u53ef\u80fd\u5df2\u4ea4\u4ee3\u4e86\u8bb8\u591a\u4e8b\uff0c\u4e8e\u662f\uff0c\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u90a3\u66f4 \u597d\u4e86\uff0c\u4f60\u4e5f\u8be5\u5c3d\u5fc3\u5c3d\u529b\u5e2e\u4eba\u5bb6\u6253\u8fd9\u4e00\u67b6\u4e86\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u76ee\u5149\u6253\u91cf\u7740\u738b\u5f64\u3001\u8d75\u4fdd\u3001\u9648\u5b8f\u4e00\u9635\uff0c\u5ffd\u7136\u79bb\u5f00\u5750\u4f4d\uff0c\u76d8\u819d\u5e2d\u5730\u800c\u5750\uff0c\u95ed\u76ee\u8c03\u606f\u8d77 \u6765\uff0c\u4f3c\u662f\u8981\u4e89\u53d6\u65f6\u95f4\uff0c\u517b\u7cbe\u84c4\u9510\uff0c\u51c6\u5907\u5168\u529b\u5e2e\u5fd9\u3002 \u3000\u3000\u738b\u91cd\u5c71\u4f4e\u58f0\u9053\uff1a\u201c\u5c0f\u548c\u5c1a\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u201c\u73b0\u5728\u65f6\u95f4\u5b9d\u8d35\uff0c\u5feb\u4e9b\u6253\u5750\u8c03\u606f\uff0c\u8001\u548c\u5c1a\u6253\u63a2\u5230\u7684\u6d88\u606f\u662f\uff0c\u654c\u52bf\u5f3a\u5927\uff0c\u4eca\u591c\u8fd9\u4e00\u6218\uff0c\u4e0d \u6b7b\u4e5f\u5f97\u8131\u5c42\u76ae\u2026\u2026\u201d \u3000\u3000\u53ea\u542c\u4e00\u58f0\u4f5b\u53f7\u4f20\u6765\uff0c\u9053\uff1a\u201c\u76f8\u56fd\u5bfa\u5df2\u88ab\u4eba\u6697\u4e2d\u5305\u56f4\u4e86\u3002\u201d \u3000\u3000\u5fc3\u5370\u5927\u5e08\u624b\u4e2d\u63d0\u7740\u7985\u6756\u3001\u6212\u5200\uff0c\u884c\u5165\u5385\u5802\u3002\u53cc\u76ee\u76ef\u5728\u90a3\u9752\u886b\u4eba\u8138\u4e0a\uff0c\u77a7\u4e86\u4e00\u9635\uff0c\u6447\u6447 \u5934\uff0c\u81ea\u884c\u843d\u5750\u8fdb\u9910\u3002 \u3000\u3000\u6562\u60c5\uff0c\u8fd9\u4f4d\u8001\u548c\u5c1a\u8fd8\u672a\u5403\u996d\u3002 \u3000\u3000\u4e03\u5de7\u50e7\u4f4e\u58f0\u5bf9\u738b\u91cd\u5c71\u9053\uff1a\u201c\u4e0d\u5f97\u4e86\uff0c\u5fc3\u5370\u5e08\u53d4\u5341\u51e0\u5e74\u6ca1\u6709\u7528\u8fc7\u7684\u5bb6\u4f19\uff0c\u90fd\u53d6\u51fa\u6765\u4e86\uff0c\u4eca \u665a\u7684\u573a\u9762\u5b9a\u7136\u76f8\u5f53\u70ed\u95f9\uff0c\u4f60\u8138\u4e0a\u9690\u73b0\u5026\u5bb9\uff0c\u5feb\u5750\u4e0b\u8c03\u606f\u4e00\u4e0b\u3002\u201d \u3000\u3000\u8fd9\u5c0f\u548c\u5c1a\u53e3\u820c\u4f3c\u5200\uff0c\u4f46\u5fc3\u7ec6\u5982\u53d1\uff0c\u5bf9\u670b\u53cb\u5173\u6000\u5f97\u5f88\u3002 \u3000\u3000\u738b\u5f64\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8001\u548c\u5c1a\uff01\u62d6\u4f60\u4e0b\u6c34\u771f\u662f\u4e0d\u597d\u610f\u601d\uff0c\u82e5\u9003\u8131\u8fc7\u8fd9\u4e00\u52ab\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u5fc3\u5370\u5927\u5e08\u767d\u7709\u8038\u52a8\uff0c\u63a5\u9053\uff1a\u201c\u51fa\u5bb6\u4eba\u4ea4\u53cb\u4e0d\u614e\uff0c\u53ea\u597d\u8ba4\u4e86\uff0c\u820d\u5f03\u8fd9\u5177\u81ed\u76ae\u56ca\uff0c\u65e9\u5f97\u89e3 \u8131\uff0c\u4f46\u76f8\u56fd\u5bfa\u5374\u4e0d\u80fd\u6bc1\u5728\u6211\u7684\u624b\u91cc\u2026\u2026\u201d \u3000\u3000\u201c\u653e\u5fc3\uff0c\u53ea\u8981\u6211\u6709\u4e09\u5bf8\u6c14\u5728\uff0c\u4fdd\u8bc1\u628a\u4f60\u8fd9\u5ea7\u76f8\u56fd\u5bfa\u4fee\u5f97\u91d1\u78a7\u8f89\u714c\u3002\u201d\u738b\u5f64\u8bf4\uff1a\u201c\u4f46\u81ea\u6211 \u5165\u5bfa\u4ee5\u6765\uff0c\u4f60\u4e00\u76f4\u90fd\u62c9\u7740\u8138\uff0c\u4e00\u526f\u5148\u77e5\u5148\u89c9\u7684\u6837\u5b50\uff0c\u4f60\u53ef\u77e5\u9053\u6211\u662f\u8ddf\u8c01\u5bf9\u4e0a\u4e86\uff1f\u201d \u3000\u3000\u201c\u9a6c\u5de1\u629a\u3001\u8d75\u4e8c\u5824\uff0c\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u201c\u4f60\u2026\u2026\u4f60\u2026\u2026\u597d\u554a\uff01\u4f60\u4e0d\u662f\u65e9\u5df2\u4e0d\u95ee\u6c5f\u6e56\u662f\u975e\u4e86\uff0c\u600e\u4e48\u6d88\u606f\u8fd8\u5982\u6b64\u7075\u901a\uff1f\u201d \u3000\u3000\u201c\u6cb3\u5357\u603b\u6355\u5934\u97e9\u6d9b\u591c\u5165\u76f8\u56fd\u5bfa\u4e2d\u6c42\u6551\uff0c\u6211\u548c\u5c1a\u867d\u6709\u70b9\u8eab\u4ef7\uff0c\u4f46\u5bcc\u4e0d\u8ddf\u5b98\u6597\uff0c\u53ea\u597d\u4ed4\u7ec6\u67e5 \u770b\u4e86\u4ed6\u7684\u4f24\u52bf\uff0c\u7adf\u662f\u88ab\u2018\u516d\u9634\u7edd\u8109\u624b\u2019\u6240\u4f24\uff0c\u8fd9\u79cd\u6b66\u529f\u6e90\u51fa\u897f\u57df\u9b54\u6559\uff0c\u600e\u4f1a\u51fa\u73b0\u4e2d\u539f\uff1f\u4ed6\u544a \u8bc9\u8001\u548c\u5c1a\uff0c\u662f\u9a6c\u5de1\u629a\u4e0b\u7684\u624b\uff0c\u786e\u5b9e\u8ba9\u6211\u5403\u4e86\u4e00\u60ca\uff0c\u5802\u5802\u4e00\u7701\u5de1\u629a\u4f1a\u662f\u9b54\u6559\u5f1f\u5b50\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u4ed6\u52ab\u8d70\u4e09\u516c\u4e3b\u7684\u4e8b\uff0c\u66f4\u662f\u65e9\u6709\u9884\u8c0b\u4e86\uff1f\u201d \u3000\u3000\u6b64\u4e8b\u679c\u7136\u5c1a\u672a\u4f20\u5165\u6c5f\u6e56\uff0c\u8fde\u4e03\u5de7\u50e7\u4e5f\u4e0d\u77e5\u9053\uff0c\u542c\u5f97\u5706\u7741\u4e86\u4e00\u5bf9\u5927\u773c\u775b\u3002 \u3000\u3000\u201c\u5927\u6982\u4e0d\u4f1a\u9519\u4e86\uff0c\u53ea\u662f\u8fd8\u672a\u627e\u5230\u660e\u786e\u7684\u8bc1\u636e\uff0c\u97e9\u6d9b\u7684\u4f24\u52bf\u5982\u4f55\u4e86\uff1f\u5982\u80fd\u5f97\u5230\u4ed6\u76f8\u52a9\uff0c\u81f3 \u5c11\u53ef\u4ee5\u963b\u6b62\u9a6c\u3001\u6b65\u4e24\u519b\u4e0d\u4e3a\u9a6c\u6587\u4e2d\u6240\u7528\u3002\u201d \u3000\u3000\u201c\u8001\u548c\u5c1a\u65e0\u80fd\u7597\u6cbb\u97e9\u6d9b\u7684\u4f24\u52bf\uff0c\u4f46\u5374\u628a\u4e24\u7c92\u5c11\u6797\u5bfa\u7684\u5c0f\u8fd8\u4e39\u9001\u7ed9\u4ed6\uff0c\u97e9\u6d9b\u548c\u8001\u8896\u76f8\u7ea6\uff0c \u5f7c\u6b64\u5b88\u5bc6\uff0c\u7b49\u5f85\u673a\u4f1a\u3002\u5179\u4e8b\u4f53\u5927\uff0c\u8001\u548c\u5c1a\u627e\u4e0d\u5230\u8bc1\u636e\uff0c\u4e5f\u4e0d\u4fbf\u8d38\u7136\u53bb\u544a\u8bc9\u522b\u4eba\uff0c\u53ea\u597d\u4f20\u8a00\uff0c \u8981\u5c0f\u548c\u5c1a\u6765\u6b64\u542c\u547d\uff0c\u8c01\u77e5\u4ed6\u5c45\u65e0\u5b9a\u6240\uff0c\u4e00\u62d6\u56db\u4e2a\u591a\u6708\u2026\u2026\u201d \u3000\u3000\u4e03\u5de7\u50e7\u9053\uff1a\u201c\u6765\u7684\u65e9\uff0c\u4e0d\u5982\u6765\u5f97\u5de7\u554a\uff01\u4eca\u5929\u521a\u5230\uff0c\u5c31\u8d76\u4e0a\u4e86\u4eca\u665a\u7684\u8fde\u53f0\u597d\u620f\u3002\u201d \u3000\u3000\u201c\u4f60\u8ba4\u4e3a\u597d\u73a9\u554a\uff01\u9a6c\u5de1\u629a\u65e9\u5df2\u5728\u5f00\u5c01\u5e9c\u5730\u9762\u4e0a\u96c6\u7ed3\u4e86\u5f88\u5e9e\u5927\u7684\u5b9e\u529b\uff0c\u4f55\u51b5\uff0c\u9b54\u6559\u4e2d\u6b66\u529f \u602a\u5f02\u5f97\u6709\u4e9b\u8ff9\u8fd1\u90aa\u672f\uff0c\u6781\u96be\u5bf9\u4ed8\uff0c\u8001\u8896\u867d\u7136\u6709\u4e86\u4e9b\u51c6\u5907\uff0c\u4f46\u4e0d\u77e5\u662f\u5426\u6709\u6548\uff1f\u4eca\u591c\u4e00\u6218\uff0c\u53ea\u597d \u5c3d\u4eba\u4e8b\u800c\u542c\u5929\u547d\u4e86\u3002\u201d \u3000\u3000\u738b\u5f64\u672c\u60f3\u63d0\u51fa\u8ffd\u9b42\u4e03\u715e\uff0c\u548c\u9a6c\u5de1\u629a\u501f\u7ec3\u5175\u4e4b\u540d\uff0c\u53ef\u80fd\u5728\u8bad\u7ec3\u9b54\u6559\u5f1f\u5b50\uff0c\u4f46\u60f3\u5230\u8bf4\u51fa\u6765\u5f92 \u4e71\u4eba\u610f\uff0c\u53ea\u597d\u5fcd\u4e0b\u4e0d\u8bf4\u3002 \u3000\u3000\u5f53\u7136\uff0c\u5bf9\u4eca\u591c\u4e00\u6218\u7684\u5371\u6076\u53ef\u6015\uff0c\u53c8\u589e\u52a0\u4e86\u51e0\u5206\u5fe7\u8651\u3002\u5f53\u4e0b\u4e00\u6539\u8bdd\u9898\uff0c\u9053\uff1a\u201c\u6211\u5df2\u4e00\u65e5\u591c \u672a\u5f97\u4f11\u606f\u2026\u2026\u201d\u738b\u5f64\u8bf4\uff1a\u201c\u5148\u8c03\u606f\u4e00\u4e0b\uff0c\u6062\u590d\u4f53\u80fd\uff0c\u624d\u597d\u5168\u529b\u8fce\u654c\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u4f60\u4eec\u90fd\u5229\u7528\u8fd9\u6bb5\u65f6\u5149\uff0c\u5750\u606f\u4e00\u4e0b\uff0c\u8001\u548c\u5c1a\u66ff\u4f60\u4eec\u62a4\u6cd5\u3002\u201d \u3000\u3000\u4e03\u5de7\u50e7\u8d76\u4e86\u4e00\u5929\u7684\u8def\uff0c\u4e5f\u6709\u4e9b\u5026\u610f\uff0c\u542c\u8bf4\u6765\u7684\u662f\u9b54\u6559\u4e2d\u4eba\uff0c\u54ea\u91cc\u8fd8\u6562\u5927\u610f\uff0c\u7acb\u523b\u76d8\u5750\u8c03 \u606f\u8d77\u6765\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u5728\u738b\u5f64\u7684\u6697\u793a\u4e4b\u4e0b\uff0c\u8d75\u4fdd\u3001\u9648\u5b8f\u90fd\u5728\u76d8\u5750\u8c03\u606f\uff0c\u4f7f\u771f\u6c14\u5728\u7ecf\u7a74\u4e2d\u6d41\u52a8\u3002 \u3000\u3000\u5fc3\u5370\u5927\u5e08\u63a9\u4e0a\u4e86\u5385\u95e8\uff0c\u7184\u53bb\u70db\u706b\uff0c\u9762\u95e8\u800c\u5750\uff0c\u8eab\u524d\u653e\u7740\u7985\u6756\u3001\u6212\u5200\u3002 \u3000\u3000\u7985\u6756\u662f\u6c89\u91cd\u7684\u5175\u5203\uff0c\u4f7f\u7528\u8005\u5927\u90fd\u662f\u4e00\u6756\u968f\u8eab\uff0c\u8fd9\u8001\u548c\u5c1a\u5374\u591a\u4e86\u4e00\u628a\u6212\u5200\uff0c\u60f3\u6765\uff0c\u5fc5\u662f\u6709 \u7279\u6b8a\u7684\u9020\u8be3\u3002 \u3000\u3000\u5927\u5385\u4e0a\u867d\u7136\u5750\u4e86\u4e03\u4e2a\u4eba\uff0c\u4f46\u5374\u9759\u5f97\u542c\u4e0d\u5230\u534a\u70b9\u58f0\u97f3\u3002 \u3000\u3000\u9662\u4e2d\u7684\u767d\u6768\u6811\uff0c\u5728\u79cb\u98ce\u4fb5\u88ad\u4e0b\uff0c\u843d\u53f6\u98d8\u98de\uff0c\u53d1\u51fa\u8f7b\u5fae\u7684\u6c99\u6c99\u4e4b\u58f0\uff1a\u51ac\u590f\u8f6e\u8f6c\uff0c\u7269\u6362\u661f \u79fb\uff0c\u767d\u6768\u6811\u4e5f\u8131\u843d\u53bb\u9ec4\u53f6\uff0c\u51dd\u805a\u8d77\u751f\u547d\u529b\uff0c\u4ee5\u6297\u62d2\u8feb\u8fd1\u7684\u971c\u96ea\u4e25\u5bd2\uff0c\u4fbf\u4e8e\u6765\u6625\u518d\u5410\u65b0\u7eff\u3002 \u3000\u3000\u5927\u81ea\u7136\u4e2d\u6f5c\u4f0f\u4e86\u51b7\u9177\u7684\u8003\u9a8c\uff0c\u4e5f\u542b\u8574\u4e86\u8f6c\u5316\u7684\u65b0\u673a\u2026\u2026\u3002 \u3000\u3000\u5929\u4ea4\u4e8c\u66f4\uff0c\u76f8\u56fd\u5bfa\u5916\u7684\u591c\u5e02\u5435\u6742\u58f0\uff0c\u4f20\u5165\u4e86\u5e7d\u9759\u7684\u5bfa\u4e2d\u3002 \u3000\u3000\u4eca\u591c\u7684\u76f8\u56fd\u5bfa\uff0c\u50e7\u4fa3\u5927\u90fd\u79bb\u53bb\uff0c\u5341\u5ba4\u4e5d\u7a7a\u2026\u2026\u3002 \u3000\u3000\u5fae\u95ed\u7740\u53cc\u76ee\u7684\u5fc3\u5370\u5927\u5e08\uff0c\u7a81\u7136\u7741\u5f00\u4e86\u773c\u775b\uff0c\u591c\u6697\u4e2d\uff0c\u53ef\u89c1\u4e24\u9053\u66b4\u5c04\u800c\u51fa\u7684\u795e\u5149\u3002 \u3000\u3000\u738b\u5f64\u3002\u4e03\u5de7\u50e7\u3001\u738b\u91cd\u5c71\u7b49\uff0c\u4e5f\u76f8\u7ee7\u7531\u5750\u606f\u4e2d\u6e05\u9192\u8fc7\u6765\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u6742\u4e71\u7684\u6b65\u5c65\u58f0\uff0c\u6e05\u6670\u7684\u4f20\u5165\u5385\u4e2d\u3002 \u3000\u3000\u6765\u4eba\u7684\u6d3b\u52a8\u660e\u663e\u800c\u4e14\u5927\u80c6\uff0c\u5fc3\u4e2d\u4f3c\u65e0\u987e\u5fcc\uff0c\u5e76\u4e14\u5341\u5206\u7684\u7b03\u5b9a\uff0c\u4ed6\u4eec\u5c31\u85cf\u5728\u97ec\u5149\u517b\u6666\u5802\u7684 \u5927\u5385\u4e2d\u3002 \u5367\u9f99\u751f\u300a\u71d5\u5b50\u4f20\u5947\u300b \u7b2c \u516d \u56de\u3000\u8eab\u9677\u91cd\u56f4 \u3000\u3000\u679c\u7136\uff0c\u4e00\u4e2a\u5a01\u91cd\u7684\u58f0\u97f3\u4f20\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u201c\u76f8\u56fd\u5bfa\u662f\u5929\u4e0b\u540d\u5239\uff0c\u5927\u5e08\u4e5f\u662f\u8df3\u51fa\u4e09\u754c\u5916\uff0c\u4e0d\u5728\u4e94\u884c\u4e2d\u7684\u6709\u9053\u9ad8\u50e7\uff0c\u6211\u5e0c\u671b\u5927\u5e08\u4e0d\u8981 \u5377\u5165\u8fd9\u79cd\u4fd7\u98ce\u4e8b\u52a1\u4e4b\u4e2d\uff0c\u5f71\u54cd\u5230\u5927\u5e08\u6570\u5341\u5e74\u6e05\u4fee\u7684\u58f0\u8a89\u3002\u201d \u3000\u3000\u738b\u5f64\u4f4e\u58f0\u9053\uff1a\u201c\u542c\u5f97\u51fa\u4ed6\u662f\u4ec0\u4e48\u4eba\u5417\uff1f\uff1f \u3000\u3000\u201c\u542c\u4e0d\u51fa\u6765\uff0c\u8001\u548c\u5c1a\u5df2\u7ecf\u5341\u5e74\u6ca1\u6709\u79bb\u5f00\u8fc7\u76f8\u56fd\u5bfa\u4e86\u3002\u201d\u5fc3\u5370\u5927\u5e08\u6447\u5934\u9053\uff1a\u201c\u5982\u4e0d\u662f\u4ed6\u5076 \u7136\u5165\u5bfa\uff0c\u8001\u548c\u5c1a\u5b8c\u5168\u4e0d\u4e86\u89e3\u5bfa\u5916\u7684\u4efb\u4f55\u4e8b\u60c5\u3002\u201d\u8001\u548c\u5c1a\u5fc3\u601d\u7f1c\u5bc6\uff0c\u672a\u8bf4\u51fa\u97e9\u6d9b\u7684\u540d\u5b57\u3002 \u3000\u3000\u201c\u6211\u77e5\u9053\u4ed6\u2026\u2026\u201d\u4e03\u5de7\u50e7\u9053\uff1a\u201c\u4ed6\u53eb\u9c7c\u5316\u9f99\uff0c\u662f\u6d1e\u5ead\u6e56\u7684\u603b\u5be8\u4e3b\u3002\u201d \u3000\u3000\u201c\u6d1e\u5ead\u5341\u516b\u5be8\u7684\u603b\u5be8\u4e3b\u4e0d\u662f\u94b1\u8001\u7237\u5b50\u94b1\u541b\u5c71\u4e48\uff1f\u600e\u4e48\u6362\u4e86\u4e2a\u59d3\u9c7c\u7684\u2026\u2026\u201d\u738b\u5f64\u9053\uff1a\u201c\u4e09 \u5e74\u524d\uff0c\u6211\u8fd8\u89c1\u8fc7\u4ed6\u3002\u201d \u3000\u3000\u201c\u94b1\u541b\u5c71\u5728\u4e00\u573a\u516c\u5f00\u7684\u51b3\u6597\u4e2d\uff0c\u88ab\u59d3\u9c7c\u7684\u6740\u4e86\u2026\u2026\u201d\u4e03\u5de7\u50e7\u9053\uff1a\u201c\u59d3\u9c7c\u7684\u5c31\u7ee7\u4efb\u4e86\u603b\u5be8 \u4e3b\u7684\u4f4d\u7f6e\u3002\u201d \u3000\u3000\u738b\u5f64\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8fc7\u53bb\u597d\u50cf\u6ca1\u6709\u542c\u8bf4\u8fc7\u8fd9\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\u554a\uff01\u9c7c\u5316\u9f99\u7a81\u7136\u51fa\u73b0\u4e86\u5728\u6c5f\u6e56\u4e0a\uff0c\u6740\u6b7b\u4e86\u94b1\u541b\u5c71\uff0c\u4e00\u5915\u6210\u540d\u3001\u201d\u4e03\u5de7\u50e7\u9053\uff1a\u201c\u6ca1 \u6709\u4eba\u77e5\u9053\u4ed6\u7684\u6765\u5386\u51fa\u8eab\uff1f\u201d \u3000\u3000\u53ea\u542c\u9c7c\u5316\u9f99\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u6211\u4eec\u5df2\u77e5\u5927\u5e08\u5c31\u5728\u5385\u4e2d\uff0c\u4f55\u6545\u7f6e\u4e4b\u4e0d\u7406\uff0c\u518d\u4e0d\u4f5c\u58f0\uff0c\u83ab\u602a\u5728 \u4e0b\u653e\u706b\u4e86\u3002\u201d \u3000\u3000\u738b\u5f64\u970d\u7136\u7ad9\u8d77\uff0c\u5fc3\u5370\u5927\u5e08\u5374\u8fdf\u5230\u4e00\u4fa7\uff0c\u663e\u7136\u6709\u8ba9\u8def\u4e4b\u610f\uff0c\u5927\u6982\u8001\u548c\u5c1a\u771f\u7684\u62c5\u5fc3\u5bf9\u65b9\u653e\u8d77 \u706b\u6765\u3002 \u3000\u3000\u6253\u5f00\u5385\u95e8\uff0c\u738b\u5f64\u7f13\u6b65\u800c\u51fa\u3002 \u3000\u3000\u8d75\u4fdd\u3001\u9648\u5b8f\u5206\u968f\u4e24\u4fa7\u3002 \u3000\u3000\u4e03\u5de7\u50e7\u4e00\u62c9\u738b\u91cd\u5c71\uff0c\u7d27\u968f\u7740\u884c\u51fa\u5927\u5385\uff0c\u7ad9\u5728\u738b\u5f64\u8eab\u540e\u3002 \u3000\u3000\u6b65\u51fa\u5385\u95e8\uff0c\u738b\u5f64\u7acb\u523b\u4e3a\u4e4b\u4e00\u6014\uff01 \u3000\u3000\u53ea\u89c1\u957f\u8349\u53ca\u819d\u7684\u5ead\u9662\u4e2d\uff0c\u5df2\u7ecf\u4e95\u7136\u6709\u5e8f\u7684\u7ad9\u4e86\u51e0\u5341\u4e2a\u4eba\u4e86\u3002 \u3000\u3000\u5f53\u5148\u4e00\u4eba\u8eab\u6750\u9ad8\u5927\uff0c\u624b\u4e2d\u6267\u7740\u4e00\u628a\u957f\u903e\u4e94\u5c3a\u7684\u65a9\u9a6c\u5200\uff0c\u539a\u80cc\u5bbd\u9762\uff0c\u81f3\u5c11\u6709\u4e09\u5341\u65a4\u4ee5\u4e0a\u7684 \u91cd\u91cf\uff0c\u5355\u770b\u5175\u5203\u7684\u6c14\u52bf\uff0c\u5c31\u8db3\u4ee5\u5bd2\u4eba\u4e4b\u80c6\u3002 \u3000\u3000\u201c\u9601\u4e0b\u662f\u2026\u2026\u201d \u3000\u3000\u738b\u5f64\u76ee\u5149\u8f6c\u52a8\uff0c\u53d1\u89c9\u9664\u4e86\u5ead\u9662\u4e2d\u7684\u654c\u4eba\u4e4b\u5916\uff0c\u4e24\u53a2\u5c4b\u9762\u4e0a\u4e5f\u5e03\u5b88\u6709\u4eba\uff0c\u4e00\u8272\u7684\u9ed1\u8272\u52b2 \u88c5\uff0c\u624b\u6267\u957f\u5200\uff0c\u6697\u4e2d\u4f30\u7b97\u4e00\u4e0b\uff0c\u5355\u662f\u76ee\u529b\u53ef\u89c1\u7684\uff0c\u5e94\u8be5\u5728\u4e94\u5341\u4eba\u4ee5\u4e0a\uff0c\u679c\u7136\u662f\u5927\u4e3e\u6765\u72af\u3002 \u3000\u3000\u201c\u4f60\u5c31\u662f\u6458\u661f\u624b\u2026\u2026\u201d\u8eab\u6750\u9ad8\u5927\u7684\u4eba\u6253\u65ad\u4e86\u738b\u5f64\u7684\u8bdd\u3002 \u3000\u3000\u201c\u4e0d\u9519\uff0c\u533a\u533a\u6b63\u662f\u738b\u5f64\uff0c\u9601\u4e0b\u7684\u5927\u540d\uff0c\u600e\u4e48\u79f0\u547c\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u662f\u8c01\u4e0d\u91cd\u8981\u4e86\uff01\u201d\u4e00\u626c\u624b\u4e2d\u7684\u65a9\u9a6c\u5200\uff0c\u9053\uff1a\u201c\u56e0\u4e3a\uff0c\u4eca\u591c\u6211\u4eec\u4e24\u4e2a\u4eba\u4e2d\uff0c\u53ea\u6709\u4e00\u4e2a \u4eba\u80fd\u6d3b\u7740\u51fa\u53bb\u3002\u201d \u3000\u3000\u738b\u5f64\u5fc3\u4e2d\u5fd6\u9053\uff1a\u8001\u548c\u5c1a\u8bf4\u7684\u4e0d\u9519\uff0c\u4eca\u591c\u80fd\u5426\u8131\u51fa\u56f4\u56f0\uff0c\u7559\u4e0b\u6027\u547d\uff0c\u53ea\u6709\u5c3d\u4eba\u4e8b\uff0c\u542c\u5929\u547d \u4e86\u3002\u770b\u6837\u5b50\uff0c\u4ed6\u4eec\u8bbe\u8ba1\u7684\u5f88\u7cbe\u5bc6\uff0c\u52a8\u5458\u4eba\u6570\u4e4b\u4f17\uff0c\u5b9e\u529b\u4e4b\u5f3a\uff0c\u6050\u975e\u6211\u4eec\u51e0\u4eba\u4e4b\u529b\u80fd\u591f\u5e94\u4ed8\u7684 \u4e86\u3002 \u3000\u3000\u201c\u9c7c\u5316\u9f99\uff0c\u6d1e\u5ead\u5341\u516b\u5be8\u7684\u603b\u5be8\u4e3b\uff0c\u8eab\u4efd\u4f55\u7b49\u5d07\u9ad8\u2026\u2026\u201d\u4e03\u5de7\u50e7\u51b7\u51b7\u5730\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u6562 \u8bf4\u51fa\u6765\u5462\uff1f\u201d \u3000\u3000\u201c\u4f60\u662f\u76f8\u56fd\u5bfa\u7684\u5c0f\u548c\u5c1a\u2026\u2026\u201d\u9c7c\u5316\u9f99\u76ee\u5149\u51dd\u6ce8\u5230\u4e03\u5de7\u50e7\u8eab\u4e0a\uff0c\u7b11\u9053\uff1a\u201c\u51fa\u5bb6\u4eba\u516d\u6839\u6e05 \u9759\uff0c\u522b\u7ba1\u8fd9\u79cd\u6c5f\u6e56\u4e0a\u6740\u6740\u780d\u780d\u7684\u4e8b\uff0c\u5feb\u8bf7\u79bb\u5f00\u5427\uff01\u201d \u3000\u3000\u201c\u4ed6\u4e0d\u662f\u76f8\u56fd\u5bfa\u7684\u548c\u5c1a\uff0c\u662f\u6c5f\u6e56\u4e0a\u6709\u540d\u7684\u9ed1\u7f57\u6c49\u3002\u201d\u7ad9\u5728\u9c7c\u5316\u9f99\u8eab\u4fa7\u7684\u4e00\u4e2a\u84dd\u8863\u6587\u58eb\uff0c \u4f4e\u58f0\u7684\u8bf4\u51fa\u4e86\u4e03\u5de7\u50e7\u7684\u8eab\u4efd\u3002 \u3000\u3000\u201c\u5931\u656c\u4e86\uff0c\u539f\u6765\u662f\u5927\u540d\u9f0e\u9f0e\u7684\u9ed1\u7f57\u6c49\u2026\u2026\u201d\u9c7c\u5316\u9f99\u5fae\u7b11\u9053\uff1a\u201c\u7f57\u6c49\u4e5f\u7f62\uff0c\u91d1\u521a\u4e5f\u597d\uff0c\u4eca \u591c\u7684\u4e8b\u548c\u6c5f\u6e56\u4e0a\u7684\u6069\u6028\u65e0\u5173\uff0c\u5c0f\u5e08\u7236\u4f55\u82e6\u6dcc\u5165\u6df7\u6c34\u5462\uff1f\u201d \u3000\u3000\u4e03\u5de7\u50e7\u7b11\u9053\uff1a\u201c\u600e\u4e48\uff0c\u662f\u529d\u6211\u5c0f\u548c\u5c1a\u79bb\u5f00\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u201c\u4e88\u4eba\u65b9\u4fbf\uff0c\u81ea\u5df1\u65b9\u4fbf\uff0c\u4f5b\u95e8\u65e0\u55d4\u5ff5\uff0c\u5c0f\u5e08\u7236\u5982\u613f\u79bb\u5f00\u8fd9\u662f\u975e\u4e4b\u5730\uff0c\u5728\u4e0b\u4fdd\u8bc1\uff0c\u54b1\u4eec\u4ee5 \u793c\u9001\u884c\u3002\u201d \u3000\u3000\u9c7c\u5316\u9f99\u770b\u4e0a\u53bb\u9ad8\u5927\u7c97\u8c6a\uff0c\u4f46\u4ed6\u4fee\u517b\u4e4b\u597d\uff0c\u5904\u4e8b\u4e4b\u51b7\u9759\u3001\u5706\u719f\uff0c\u5b9e\u662f\u4e00\u4f4d\u6781\u5177\u667a\u8c0b\u7684\u4eba \u7269\u3002 \u3000\u3000\u738b\u5f64\u8fd8\u771f\u7684\u6709\u70b9\u62c5\u5fc3\u4e03\u5de7\u501f\u88ab\u5bf9\u65b9\u8bf4\u52a8\uff0c\u4f46\u53c8\u4e0d\u4fbf\u51fa\u8a00\u963b\u6b62\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u4e03\u5de7\u50e7\u548c\u4ed6\u738b\u5f64\u5168\u65e0\u534a\u70b9\u5173\u8fde\uff0c\u5c0f\u548c\u5c1a\u6765\u6b64\u52a9\u4e00\u81c2\u4e4b\u529b\uff0c\u5b8c\u5168\u662f\u770b\u5728\u8001\u548c\u5c1a\u7684 \u4efd\u4e0a\uff0c\u89c1\u673a\u9000\u8d70\uff0c\u4e5f\u6ca1\u4ec0\u4e48\u4e0d\u5bf9\uff01 \u3000\u3000\u201c\u5c31\u8ba9\u6211\u5c0f\u548c\u5c1a\u8fd9\u6837\u8d70\u4e48\uff1f\u201d \u3000\u3000\u90a3\u84dd\u8863\u4eba\u51b7\u51b7\u5730\u63a5\u9053\uff1a\u201c\u89c1\u597d\u5c31\u6536\u5427\uff0c\u9ed1\u7f57\u6c49\uff01\u4eca\u665a\u8fd9\u4e2a\u5c40\u9762\uff0c\u4f60\u5e94\u8be5\u770b\u5f97\u5f88\u6e05\u695a\uff0c\u73b0 \u5728\u4f60\u770b\u5230\u7684\u4eba\u53ea\u4e0d\u8fc7\u662f\u5341\u5206\u4e4b\u4e8c\u4e09\u2026\u2026\u201d \u3000\u3000\u4e03\u5de7\u50e7\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u600e\u4e48\uff1f\u8981\u5413\u552c\u6211\u554a\uff01\u5c0f\u548c\u5c1a\u6211\u662f\u5403\u7c73\u3001\u5403\u9762\u957f\u5927\u7684\uff0c\u53ef\u4e0d\u662f\u88ab\u4eba \u5413\u5927\u7684\u3002\u201d \u3000\u3000\u9c7c\u5316\u9f99\u4e00\u6325\u624b\uff0c\u963b\u6b62\u84dd\u8863\u4eba\u518d\u63a5\u53e3\uff0c\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u5c0f\u5e08\u7236\uff0c\u4f60\u6709\u4ec0\u4e48\u6761\u4ef6\uff1f\u53ea\u7ba1\u8bf7\u8bf4 \u51fa\u6765\u5427\uff01\u201d \u3000\u3000\u60c5\u52bf\u9010\u6e10\u660e\u663e\uff0c\u4eca\u591c\u4e00\u6218\u7684\u9886\u5bfc\u4eba\u7269\u5e94\u8be5\u662f\u9c7c\u5316\u9f99\u4e86\uff0c\u81f3\u5c11\uff0c\u7b2c\u4e00\u6ce2\u7684\u653b\u52bf\uff0c\u7531\u4ed6\u6307 \u6325\u3001\u7387\u9886\u3002 \u3000\u3000\u4e03\u5de7\u50e7\u5fae\u5fae\u4e00\u6014\uff0c\u5fd6\u9053\uff1a\u6c5f\u6e56\u4f20\u8a00\uff0c\u9c7c\u5316\u9f99\u4e00\u5411\u813e\u6c14\u66b4\u8e81\uff0c\u600e\u4f1a\u6709\u5982\u6b64\u597d\u7684\u8010\u6027\uff0c\u4e2a\u4e2d \u96be\u9053\u6709\u4ec0\u4e48\u9634\u8c0b\u4e0d\u6210\uff1f\u8001\u548c\u5c1a\u548c\u738b\u7edf\u9886\u7684\u8ba1\u5212\uff0c\u5e76\u65e0\u64a4\u79bb\u6b64\u5730\u7684\u6253\u7b97\uff0c\u51c6\u5907\u662f\u6b7b\u5b88\u6b64\u5730\uff0c\u5168 \u529b\u6297\u654c\uff0c\u7b49\u5f85\u63f4\u5175\uff0c\u4e5f\u8bb8\u62d6\u5ef6\u65f6\u95f4\u5bf9\u4ed6\u4eec\u6709\u5229\uff0c\u4f46\u665a\u4e00\u523b\u52a8\u624b\uff0c\u5bf9\u6211\u65b9\u53c8\u4f55\u5c1d\u4e0d\u662f\u6709\u5229\u7684 \u5462\uff1f \u3000\u3000\u9ed1\u7f57\u6c49\u5fc3\u4e2d\u660e\u767d\uff0c\u8fd9\u97ec\u5149\u517b\u6666\u5802\u770b\u8d77\u6765\u6700\u4e0d\u8d77\u773c\uff0c\u5176\u5b9e\u6574\u4e2a\u7684\u5efa\u7b51\u5168\u90fd\u662f\u9ed1\u7070\u8272\u5c71\u77f3\u780c \u6210\uff0c\u575a\u56fa\u65e0\u6bd4\uff0c\u5229\u7528\u5f62\u52bf\uff0c\u4ee5\u5be1\u5fa1\u4f17\uff0c\u662f\u6700\u7406\u60f3\u7684\u5730\u65b9\uff0c\u5ba4\u4e2d\u53c8\u65e0\u53ef\u71c3\u4e4b\u7269\uff0c\u5bf9\u4e8e\u653e\u706b\u653b\u4e0a \u5e76\u4e0d\u4f1a\u600e\u4e48\u6837\u3002 \u3000\u3000\u4f46\u95fb\u9c7c\u5316\u9f99\u7b11\u9053\uff1a\u201c\u5c0f\u5e08\u7236\uff0c\u53ef\u4ee5\u63d0\u51fa\u4f60\u7684\u6761\u4ef6\u4e86\uff1f\u201d \u3000\u3000\u4e03\u5de7\u50e7\u9053\uff1a\u201c\u6211\u8bf4\u51fa\u6765\uff0c\u4f60\u4e00\u5b9a\u80fd\u529e\u5230\u4e48\uff1f\u201d \u3000\u3000\u201c\u4e0d\u4e00\u5b9a\u2026\u2026\u201d\u9c7c\u5316\u9f99\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u53ea\u8981\u4e0d\u592a\u82db\u523b\uff0c\u4e00\u5b9a\u6709\u5546\u91cf\u7684\u4f59\u5730\u3002\u201d \u3000\u3000\u7a81\u7136\uff0c\u738b\u5f64\u63a5\u4e86\u4e00\u53e5\uff1a\u201c\u9c7c\u603b\u5be8\u4e3b\u597d\u5927\u7684\u53e3\u6c14\uff0c\u4eca\u665a\u4e0a\u7684\u4e8b\uff0c\u4f60\u5f53\u771f\u80fd\u4f5c\u5f97\u4e86\u4e3b\uff1f\u201d \u3000\u3000\u9c7c\u5316\u9f99\u5bf9\u4e03\u5de7\u50e7\u5341\u5206\u5ba2\u6c14\uff0c\u4f46\u5bf9\u738b\u5f64\u5374\u4e0d\u5047\u8bcd\u8272\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u738b\u5f64\uff0c\u4f60\u6700\u597d\u7559\u70b9\u7cbe \u795e\uff0c\u76d8\u7b97\u4e00\u4e0b\u4f60\u81ea\u5df1\u7684\u8eab\u540e\u4e8b\uff0c\u8fd9\u91cc\u6ca1\u6709\u4f60\u8bf4\u8bdd\u7684\u4efd\u2026\u2026\uff02\u8d75\u4fdd\u6012\u9053\uff1a\u201d\u6d1e\u5ead\u6c34\u5bc7\uff0c\u843d\u8349\u6e56 \u532a\uff0c\u597d\u5927\u7684\u80c6\u5b50\u2026\u2026\u201c \u3000\u3000\u738b\u5f64\u4e5f\u8868\u73b0\u51fa\u65e0\u6bd4\u5fcd\u8010\u7684\u5de5\u592b\uff0c\u62e6\u4f4f\u4e86\u8d75\u4fdd\uff0c\u7b11\u9053\uff1a\u201c\u9c7c\u603b\u5be8\u4e3b\u4ece\u8fe2\u8fe2\u5343\u91cc\u4e4b\u5916\uff0c\u8d76\u6765 \u5f00\u5c01\uff0c\u5fc5\u6709\u76ee\u7684\uff0c\u522b\u4eba\u80fd\u7ed9\u4e88\u9c7c\u603b\u5be8\u4e3b\u7684\uff0c\u738b\u67d0\u76f8\u4fe1\u4ea6\u53ef\u529e\u5230\uff0c\u4e5f\u8bb8\uff0c\u522b\u4eba\u4e0d\u80fd\u7ed9\u4e88\u9c7c\u603b\u5be8 \u4e3b\u7684\uff0c\u738b\u67d0\u6216\u53ef\u4ee3\u6c42\u5723\u4e0a\u6069\u8d50\u3002\u201d \u3000\u3000\u8fd9\u662f\u5f88\u8bf1\u60d1\u7684\u8bdd\uff0c\u4e5f\u8868\u660e\u4e86\u81ea\u5df1\u7684\u8eab\u4efd\u3002 \u3000\u3000\u53cc\u65b9\u90fd\u5c55\u5f00\u4e86\u6311\u62e8\u2014\u2014\u5fc3\u6218\u3002 \u3000\u3000\u54ea\u77e5\u9c7c\u5316\u9f99\u7adf\u4e0d\u518d\u7406\u4f1a\u738b\u5f64\uff0c\u5374\u671b\u7740\u4e03\u5de7\u50e7\u9053\uff1a\u201c\u5c0f\u5e08\u7236\uff0c\u5e26\u7740\u5fc3\u5370\u5927\u5e08\u4e00\u8d77\u8d70\u5427\uff01\u9c7c \u67d0\u4eba\u613f\u610f\u5949\u8d60\u767d\u94f6\u53c2\u4e07\u4e24\uff0c\u4e24\u4f4d\u627e\u4e00\u5904\u540d\u5239\u9a7b\u9521\uff0c\u53c2\u609f\u5927\u4e58\u4f5b\u6cd5\uff0c\u4e0d\u96be\u6210\u4e00\u4ee3\u5723\u50e7\uff0c\u4f55\u82e6\u7559 \u604b\u8fd9\u662f\u975e\u5c60\u573a\uff0c\u6cbe\u67d3\u8840\u8165\u3002\u201d \u3000\u3000\u4e03\u5de7\u50e7\u5fc3\u5934\u9707\u52a8\u4e86\u4e00\u4e0b\uff0c\u5fd6\u9053\uff1a\u8fd9\u7c97\u58ee\u5927\u6c49\u5982\u6b64\u7684\u7ec6\u817b\u6709\u81f4\uff0c\u4e0d\u50cf\u51fa\u8eab\u8349\u83bd\uff0c\u5012\u662f\u548c\u6211 \u5c0f\u548c\u5c1a\u4e00\u6837\uff0c\u5916\u7c97\u5185\u79c0\uff0c\u4e0d\u53ef\u8f7b\u89c6\uff0c\u4e0d\u59a8\u548c\u4ed6\u6597\u6597\u5fc3\u673a\u4e86\u3002 \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u5408\u638c\u5f53\u80f8\uff0c\u9053\uff1a\u201c\u963f\u5f25\u9640\u4f5b\uff01\u9c7c\u65bd\u4e3b\u8bed\u5ff5\u7985\u673a\uff0c\u5c0f\u50e7\u5982\u68a6\u521d\u9192\uff0c\u539a\u8d50\u5374\u4e4b\u4e0d \u606d\uff0c\u5c0f\u50e7\u8fd9\u91cc\u62dc\u8c22\u4e86\u3002\u201d \u3000\u3000\u6df1\u6df1\u8eac\u4e86\u4e00\u793c\u3002 \u3000\u3000\u738b\u91cd\u5c71\u6014\u4e86\u4e00\u6014\uff0c\u5fd6\u9053\uff1a\u770b\u6837\u5b50\u5c0f\u548c\u5c1a\u7adf\u7136\u8ba4\u771f\u4e86\u3002\u4e0d\u7981\u5fc3\u4e2d\u5927\u6025\uff0c\u543c\u9053\uff1a\u201c\u5582\uff01\u9ed1\u7f57 \u6c49\uff0c\u4f60\u8fd9\u7b97\u4ec0\u4e48\u561b\uff1f\u201d \u3000\u3000\u201c\u4eba\u5404\u6709\u5fd7\uff0c\u5c82\u53ef\u52c9\u5f3a\uff0c\u9c7c\u65bd\u4e3b\u8bed\u610f\u4e2d\u80af\uff0c\u6709\u5982\u66ae\u9f13\u3001\u6668\u949f\uff0c\u5c0f\u50e7\u5df2\u8df3\u51fa\u7ea2\u5c18\uff0c\u5c82\u53ef\u518d \u6cbe\u6740\u5b7d\u3002\u201d \u3000\u3000\u201c\u54fc\uff01\u4e09\u4e07\u4e24\u94f6\u5b50\uff0c\u4e70\u53bb\u4e86\u4f60\u4e00\u9897\u4f5b\u5fc3\uff0c\u90a3\u6709\u4f55\u96be\u2026\u2026\u201d\u738b\u91cd\u5c71\u9053\uff1a\u201c\u54b1\u4eec\u518d\u591a\u51fa\u4e00\u70b9 \u5c31\u662f\u3002\u201d \u3000\u3000\u9c7c\u5316\u9f99\u4f38\u624b\u7531\u6000\u4e2d\u53d6\u51fa\u4e00\u53e0\u94f6\u7968\uff0c\u7b11\u9053\uff1a\u201c\u5c0f\u5e08\u7236\uff0c\u62ff\u53bb\u5427\uff01 \u3000\u3000\u94f6\u4e24\u53ea\u591a\u4e0d\u5c11\uff0c\u8db3\u591f\u4f60\u76d6\u4e0a\u4e00\u5ea7\u7985\u9662\uff0c\u4e00\u751f\u4eab\u7528\u4e0d\u5c3d\u4e86\u3002\u201c \u3000\u3000\u4e03\u5de7\u50e7\u5927\u6b65\u884c\u53bb\uff0c\u63a5\u8fc7\u94f6\u7968\uff0c\u7adf\u7136\u5f88\u4ed4\u7ec6\u7684\u67e5\u770b\u4e86\u4e00\u756a\uff0c\u9053\uff1a\u201c\u9c7c\u65bd\u4e3b\uff0c\u4f60\u591a\u4ed8\u4e86\u516b\u5343 \u4e24\u94f6\u5b50\u3002\u201d \u3000\u3000\u9c7c\u5316\u9f99\u4e00\u6325\u624b\uff0c\u9053\uff1a\u201c\u76f8\u9022\u5c31\u662f\u6709\u7f18\uff0c\u591a\u4e00\u4e9b\u4f55\u59a8\u3002\u201d \u3000\u3000\u201c\u963f\u5f25\u9640\u4f5b\uff01\u5c0f\u50e7\u62dc\u522b\u3002\u201d\u8f6c\u8eab\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u9c7c\u5316\u9f99\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5c0f\u5e08\u7236\uff01\u5fc3\u5370\u5927\u5e08\u5462\uff1f\u4f55\u4e0d\u7ed3\u4f34\u540c\u884c\u3002\u201d \u3000\u3000\u201c\u5c0f\u548c\u5c1a\u662f\u5c0f\u548c\u5c1a\uff0c\u8001\u548c\u5c1a\u662f\u8001\u548c\u5c1a\uff0c\u5c0f\u548c\u5c1a\u5982\u4f55\u80fd\u7ba1\u5f97\u4e86\u8001\u548c\u5c1a\uff0c\u544a\u8f9e\u4e86\u3002\u201d\u5927\u6b65\u5411 \u5916\u884c\u53bb\u3002 \u3000\u3000\u4e24\u4e2a\u9752\u8863\u5927\u6c49\u7a81\u7136\u4e00\u6a2a\u8eab\uff0c\u963b\u6321\u4e86\u4e03\u5de7\u50e7\u7684\u53bb\u8def\u3002 \u3000\u3000\u201c\u95ea\u5f00\uff01\u201d\u9c7c\u5316\u9f99\u51b7\u51b7\u7684\u559d\u9053\uff1a\u201c\u8fdd\u4ee4\u8005\u6b7b\uff01\u201d\u58f0\u97f3\u5f88\u5927\uff0c\u5168\u573a\u4e2d\u4eba\u4eba\u90fd\u542c\u5f97\u6e05\u6e05\u695a \u695a\u3002 \u3000\u3000\u4e24\u4e2a\u9752\u8863\u4eba\u5982\u65af\u54cd\u5e94\uff0c\u9000\u56de\u539f\u4f4d\uff0c\u8ba9\u5f00\u4e86\u53bb\u8def\u3002 \u3000\u3000\u4e03\u5de7\u50e7\u5927\u6b65\u884c\u51fa\u97ec\u5149\u517b\u6666\u5802\u7684\u5706\u6708\u9662\u95e8\u3002 \u3000\u3000\u738b\u91cd\u5c71\u5927\u53eb\u9053\uff1a\u201c\u5931\u671b\u554a\uff01\u5931\u671b\u554a\uff01\u89c1\u9762\u4e0d\u5982\u95fb\u540d\u77e3\uff0c\u9ed1\u7f57\u6c49\u7adf\u7136\u662f\u5982\u6b64\u5730\u8d2a\u8d22\u2026\u2026\u201d \u3000\u3000\u738b\u5f64\u559d\u9053\uff1a\u201c\u95ed\u5634\uff01\u4eba\u5404\u6709\u5fd7\uff0c\u7531\u4ed6\u53bb\u5427\uff01\u201d \u3000\u3000\u8d75\u4fdd\u3001\u9648\u5b8f\u867d\u672a\u8bf4\u8bdd\uff0c\u4f46\u76ee\u4e2d\u7684\u6124\u6012\u3001\u6b67\u89c6\uff0c\u5c24\u8fc7\u738b\u91cd\u5c71\u3002 \u3000\u3000\u9c7c\u5316\u9f99\u795e\u8272\u5e73\u9759\u7684\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u5fc3\u5370\u5927\u5e08\uff0c\u9c7c\u67d0\u4e0d\u613f\u60ca\u6270\u4f5b\u9a7e\uff0c\u4e00\u76f4\u5217\u961f\u606d\u5019\uff0c\u656c\u671b\u5927 \u5e08\u4f53\u5ff5\u9c7c\u67d0\u4e00\u7247\u8654\u656c\u4e4b\u5fc3\uff0c\u65e9\u79bb\u6b64\u5730\uff0c\u9c7c\u67d0\u8bed\u51fa\u81f3\u8bda\uff0c\u8fd8\u671b\u5927\u5e08\u4fef\u5bdf\u3002\u201d \u3000\u3000\u738b\u5f64\u9707\u52a8\u4e86\uff0c\u9c7c\u5316\u9f99\u65e9\u5df2\u5b8c\u6210\u4e86\u5305\u56f4\uff0c\u5374\u8fdf\u8fdf\u6ca1\u6709\u52a8\u624b\uff0c\u96be\u9053\u771f\u662f\u4e3a\u5fc3\u5370\u5927\u5e08\u548c\u4e03\u5de7\u50e7 \u4e0d\u6210\uff1f\u4ed6\u548c\u4f5b\u95e8\u4e2d\u6709\u4f55\u6e0a\u6e90\uff0c\u5982\u6b64\u656c\u91cd\u4f5b\u95e8\u5b50\u5f1f\u3002 \u3000\u3000\u4ee5\u738b\u5f64\u7684\u4e30\u5bcc\u9605\u5386\uff0c\u7adf\u7136\u77a7\u4e0d\u51fa\u9c7c\u5316\u9f99\u6709\u77eb\u9970\u4e4b\u8272\u3002 \u3000\u3000\u5fc3\u5370\u5927\u5e08\u6ca1\u6709\u56de\u7b54\uff0c\u4e5f\u6ca1\u6709\u8d70\u51fa\u5385\u95e8\u3002 \u3000\u3000\u90a3\u84dd\u8863\u6587\u58eb\u62ac\u5934\u671b\u671b\u5929\u8272\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u603b\u5be8\u4e3b\uff0c\u5df2\u8fd1\u4e09\u66f4\uff0c\u54b1\u4eec\u5df2\u665a\u4e86\u4e00\u523b\u65f6\u5149\u3002\u201d \u3000\u3000\u9c7c\u5316\u9f99\u53f9\u53e3\u6c14\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u5927\u5e08\uff0c\u8bf7\u6055\u9c7c\u67d0\u653e\u8086\u4e86\uff01\u201d \u3000\u3000\u7a81\u7136\uff0c\u4ed6\u98de\u8eab\u800c\u8d77\uff0c\u51b2\u5411\u5385\u95e8\u3002 \u3000\u3000\u8d75\u4fdd\u957f\u5200\u4e00\u6325\uff0c\u4e00\u5f0f\u201c\u96c1\u843d\u5e73\u6c99\u201d\uff0c\u6a2a\u91cc\u65a9\u51fa\u3002 \u3000\u3000\u9c7c\u5316\u9f99\u53f3\u624b\u65a9\u9a6c\u5200\u5411\u524d\u4e00\u63a8\uff0c\u8fce\u4e86\u4e0a\u53bb\uff0c\u90a3\u4e48\u6c89\u91cd\u7684\u5200\uff0c\u4ed6\u53ea\u7528\u4e00\u624b\u6325\u51fa\uff0c\u8f7b\u6377\u7075\u5de7\uff0c \u5feb\u5982\u95ea\u7535\uff0c\u8d75\u4fdd\u60f3\u907f\u5f00\u5200\u52bf\uff0c\u5df2\u662f\u6765\u4e0d\u53ca\u4e86\u3002 \u3000\u3000\u4f46\u95fb\u4e00\u58f0\u91d1\u94c1\u4ea4\u9e23\uff0c\u53cc\u81c2\u987f\u611f\u4e00\u9ebb\uff0c\u624b\u4e2d\u957f\u5200\u4e5f\u88ab\u9707\u5f97\u53cd\u98de\u56de\u53bb\uff0c\u51e0\u4e4e\u8131\u624b\uff0c\u4f46\u9c7c\u5316\u9f99 \u7684\u65a9\u9a6c\u5200\u5374\u987a\u6c34\u63a8\u821f\u822c\uff0c\u75be\u65a9\u800c\u4e0b\u3002 \u3000\u3000\u8fd9\u4e00\u5200\u5e76\u4e0d\u8be1\u5f02\uff0c\u53ea\u662f\u638c\u63e1\u4e86\u5200\u52bf\u53d8\u5316\uff0c\u800c\u81f4\u4f7f\u4e3a\u81f4\u547d\u7684\u4e00\u51fb\u3002 \u3000\u3000\u8d75\u4fdd\u5728\u4e00\u5239\u95f4\uff0c\u8fde\u60f3\u4e86\u4e09\u62db\u5200\u6cd5\uff0c\u53ea\u662f\u6536\u5200\u4e0d\u53ca\uff0c\u65e0\u6cd5\u5e94\u53d8\u3002 \u3000\u3000\u738b\u91cd\u5c71\u7684\u957f\u5251\u201c\u5929\u5916\u6765\u4e91\u201d\uff0c\u4e00\u7f15\u5251\u98ce\u75be\u523a\u800c\u81f3\uff0c\u70b9\u5411\u9c7c\u5316\u9f99\u7684\u53f3\u8155\u3002 \u3000\u3000\u8fd9\u5c31\u903c\u5f97\u9c7c\u5316\u9f99\u4e0d\u5f97\u4e0d\u6c89\u8155\u53d8\u62db\u3002 \u3000\u3000\u8d75\u4fdd\u6b7b\u91cc\u9003\u751f\uff0c\u9000\u4e86\u4e09\u6b65\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u7edf\u9886\uff0c\u59d3\u9c7c\u7684\u5200\u52bf\u6c89\u91cd\uff0c\u4e0d\u53ef\u786c\u63a5\u3002\u201d \u3000\u3000\u201c\u9000\u5165\u5927\u5385\u4e2d\uff0c\u5b88\u4f4f\u5927\u95e8\u53f3\u4fa7\u2026\u2026\u201d\u738b\u5f64\u4e00\u9762\u7b54\u8bdd\uff0c\u4e00\u9762\u53d6\u51fa\u4e86\u4e00\u526f\u9e7f\u76ae\u62a4\u8155\u3002 \u3000\u3000\u8fd9\u62a4\u8155\u5bbd\u7ea6\u4e09\u5bf8\uff0c\u5916\u9762\u662f\u7cbe\u94a2\u6253\u6210\u7684\u957f\u6761\u5f62\u94a2\u7247\uff0c\u6263\u5728\u53cc\u8155\u4e0a\uff0c\u53ef\u4ee5\u5c01\u67b6\u654c\u4eba\u5175\u5203\uff0c\u800c \u4e14\u5185\u85cf\u673a\u7c27\u3001\u94f6\u9488\uff0c\u662f\u4e00\u79cd\u6781\u5177\u5de7\u601d\u7684\u5389\u5bb3\u6697\u5668\u3002 \u3000\u3000\u8fd9\u662f\u738b\u5f64\u5c31\u4efb\u4e86\u5185\u5bab\u4f8d\u536b\u7edf\u9886\u4e4b\u540e\uff0c\u6df1\u611f\u4fdd\u62a4\u7687\u5e1d\u5b89\u5371\u7684\u8d23\u4efb\u91cd\u5927\uff0c\u6709\u522b\u4e8e\u6c5f\u6e56\u4e0a\u76db\u540d \u4e4b\u4e89\uff0c\u8058\u8bf7\u4e86\u5de7\u624b\u5de5\u5320\uff0c\u8bbe\u8ba1\u4e86\u8fd9\u4e48\u4e00\u526f\u62a4\u8155\uff0c\u4ee5\u5907\u4e0d\u65f6\u4e4b\u9700\u3002 \u3000\u3000\u4eca\u591c\uff0c\u60c5\u5f62\u7279\u6b8a\uff0c\u4e3a\u738b\u5f64\u751f\u5e73\u672a\u66fe\u9047\u5230\u8fc7\u7684\u5f3a\u654c\u9669\u5883\uff0c\u51c6\u5907\u5c3d\u51fa\u516b\u5b9d\u5168\u529b\u4e00\u62da\u4e86\u3002 \u3000\u3000\u738b\u91cd\u5c71\u5251\u8d70\u8f7b\u7075\uff0c\u4ee5\u5de7\u5236\u529b\u8fde\u653b\u4e86\u4e09\u5251\uff0c\u624d\u7b97\u628a\u9c7c\u5316\u9f99\u8feb\u8fdb\u4e4b\u52bf\u963b\u6b62\uff0c\u4e5f\u907f\u5f00\u4e86\u9c7c\u5316\u9f99 \u542b\u8574\u7740\u5f3a\u5927\u529b\u9053\u7684\u65a9\u9a6c\u5200\u3002 \u3000\u3000\u4f46\u4e5f\u6fc0\u6012\u4e86\u9c7c\u5316\u9f99\uff0c\u7a81\u7136\u5927\u559d\u4e00\u58f0\uff0c\u65a9\u9a6c\u5200\u6a2a\u626b\u5343\u519b\uff0c\u62a2\u51fa\u4e86\u4e00\u9053\u957f\u8679\u822c\u7684\u5200\u5149\uff0c\u5e73\u65a9 \u8fc7\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u5200\u5a01\u52bf\u5947\u5927\uff0c\u4e0d\u4f46\u903c\u5f97\u738b\u91cd\u5c71\u9000\u56de\u5385\u5185\uff0c\u4e5f\u628a\u738b\u5f64\u903c\u5165\u5385\u95e8\uff0c\u51e0\u4e2a\u7531\u4e24\u4fa7\u51b2\u4e0a\u653b\u51fb \u738b\u5f64\u7684\u9752\u8863\u5927\u6c49\uff0c\u4e5f\u88ab\u903c\u5f97\u9000\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5929\u4e0a\u65e0\u6708\u3002 \u3000\u3000\u591c\u8272\u8ff7\u8499\u3002 \u3000\u3000\u5927\u5385\u5185\u4e00\u7247\u9ed1\u6697\u3002 \u3000\u3000\u738b\u5f64\u4f4e\u58f0\u9053\uff1a\u201c\u5927\u5e08\uff0c\u9c7c\u5316\u9f99\u8c6a\u52c7\u65e0\u5339\uff0c\u5200\u52bf\u6c89\u91cd\u2026\u2026\u201d \u3000\u3000\u201c\u6240\u4ee5\uff0c\u6211\u624d\u6697\u4e2d\u4f20\u8a00\uff0c\u628a\u4f60\u4eec\u53ec\u56de\u5385\u4e2d\uff0c\u636e\u9669\u9632\u5b88\uff0c\u5148\u632b\u4e00\u4e0b\u654c\u4eba\u7684\u653b\u52bf\u518d\u8bf4\u3002\u201d \u3000\u3000\u738b\u5f64\u539f\u672c\u60f3\u8bf4\u660e\uff0c\u8981\u5148\u6740\u4e86\u9c7c\u5316\u9f99\uff0c\u4f46\u8bdd\u88ab\u5fc3\u5370\u5927\u5e08\u63a5\u4e86\u4e0b\u53bb\uff0c\u4e5f\u5c31\u5fcd\u4e0b\u4e0d\u8bf4\u3002 \u3000\u3000\u521a\u52a8\u624b\u5c31\u51fb\u6740\u9c7c\u5316\u9f99\uff0c\u4e5f\u8bb8\u53ef\u4ee5\u4e00\u632b\u654c\u52bf\uff0c\u4f46\u4ea6\u5c06\u5f15\u6765\u9a6c\u6587\u4e2d\u66f4\u5f3a\u5927\u3001\u66f4\u4e3a\u6076\u6bd2\u7684\u653b \u52bf\u3002 \u3000\u3000\u9700\u77e5\uff0c\u4ed6\u8fd9\u8bbe\u8ba1\u662f\u501f\u7528\u5bf9\u65b9\u5175\u5203\uff0c\u51fb\u4e2d\u5728\u62a4\u8155\u4e0a\u94a2\u7247\u4e4b\u529b\uff0c\u9707\u52a8\u4e86\u5f3a\u529b\u7684\u673a\u7c27\u53d1\u51fa\u6697 \u5668\uff0c\u76f8\u8ddd\u4e0d\u8fc7\u5728\u5c3a\u8bb8\u4e4b\u95f4\uff0c\u4e00\u65e6\u51fa\u624b\uff0c\u654c\u4eba\u7edd\u65e0\u907f\u5f00\u7684\u673a\u4f1a\uff0c\u5b9e\u5728\u662f\u4f24\u4eba\u7684\u5229\u5668\u3002 \u3000\u3000\u5f53\u7136\uff0c\u8fd9\u4e5f\u662f\u738b\u5f64\u4ece\u672a\u7528\u7684\u514b\u654c\u4e4b\u79d8\u3002 \u3000\u3000\u9c7c\u5316\u9f99\u52c7\u51a0\u4e09\u519b\uff0c\u4ed7\u51ed\u5200\u91cd\u529b\u731b\uff0c\u4e00\u76f4\u5f53\u5148\u62a2\u653b\uff0c\u6b64\u523b\uff0c\u5200\u5149\u62a4\u9762\uff0c\u76f4\u5411\u5385\u5185\u51b2\u6765\u3002 \u3000\u3000\u7a81\u89c9\uff0c\u91d1\u51e4\u6251\u9762\uff0c\u4e00\u80a1\u5f3a\u70c8\u81f3\u6781\u7684\u52b2\u9053\uff0c\u76f4\u538b\u4e0b\u6765\u3002 \u3000\u3000\u9c7c\u5316\u9f99\u4e3e\u5200\u4e00\u5c01\uff0c\u8fce\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u4f46\u95fb\u4e00\u58f0\u91d1\u94c1\u5927\u9707\uff0c\u9c7c\u5316\u9f99\u987f\u611f\u5200\u52bf\u53d7\u963b\uff0c\u88ab\u4e00\u4ef6\u7c97\u91cd\u7684\u5175\u5203\u5c01\u4f4f\uff0c\u7d27\u8ddf\u7740\u4e00\u9053\u5bd2\u5149\u6a2a \u65a9\u800c\u81f3\u3002 \u3000\u3000\u8fd9\u5c31\u4e0d\u5f97\u4e0d\u901a\u5f97\u9c7c\u5316\u9f99\u5411\u540e\u75be\u9000\uff0c\u51b2\u5165\u5385\u95e8\u7684\u9ad8\u5927\u8eab\u8eaf\uff0c\u53c8\u9000\u51fa\u4e86\u95e8\u5916\u3002 \u3000\u3000\u662f\u7684\uff01\u5fc3\u5370\u5927\u5e08\u51fa\u624b\u4e86\uff0c\u7985\u6756\u5c01\u5f00\u65a9\u9a6c\u5200\uff0c\u6212\u5200\u903c\u9000\u4e86\u9c7c\u5316\u9f99\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u90a3\u84dd\u8863\u6587\u58eb\u4f4e\u58f0\u5730\u8bf4\u9053\uff1a\u201c\u603b\u5be8\u4e3b\uff0c\u654c\u4eba\u9690\u4e8e\u5ba4\u5185\uff0c\u591c\u8272\u5e7d\u6697\uff0c\u654c\u60c5\u4e0d\u660e\uff0c\u603b\u5be8 \u4e3b\u8eab\u62c5\u5927\u4efb\uff0c\u4e0d\u53ef\u6d89\u9669\u3002\u201d \u3000\u3000\u9c7c\u5316\u9f99\u51b7\u54fc\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u71c3\u8d77\u706b\u628a\uff0c\u51b2\u8fdb\u53bb\uff01\u201d \u3000\u3000\u201c\u8bf7\u603b\u5be8\u4e3b\u4e00\u65c1\u63a0\u9635\uff0c\u5bdf\u660e\u654c\u60c5\u4e4b\u540e\uff0c\u518d\u51fa\u624b\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u9c7c\u5316\u9f99\u70b9\u70b9\u5934\uff0c\u9000\u540e\u516d\u5c3a\u3002 \u3000\u3000\u84dd\u8863\u6587\u58eb\u7acb\u523b\u4e3e\u624b\u4e00\u6325\uff0c\u9053\uff1a\u201c\u71c3\u8d77\u706b\u7403\uff0c\u6295\u5165\u5385\u4e2d\uff0c\u7b2c\u4e00\u7ebf\u5168\u529b\u62a2\u653b\u3002\u201d \u3000\u3000\u679c\u7136\u662f\u65e9\u6709\u4e86\u653b\u52bf\u7684\u8bbe\u8ba1\u3002 \u3000\u3000\u4f46\u89c1\u706b\u5149\u95ea\u52a8\uff0c\u4eae\u8d77\u4e86\u5341\u56e2\u706b\u7403\u3002 \u3000\u3000\u6562\u60c5\uff0c\u4e0d\u662f\u706b\u628a\uff0c\u800c\u662f\u4ee5\u677e\u6cb9\u3001\u68c9\u82b1\u5236\u6210\u7684\u5706\u5f62\u706b\u7403\u3002 \u3000\u3000\u738b\u5f64\u4f4e\u58f0\u9053\uff1a\u201c\u5c3d\u91cf\u5bf9\u6321\u706b\u7403\uff0c\u522b\u8ba9\u5b83\u6295\u5165\u5385\u4e2d\u3002\u201d\u6697\u63d0\u771f\u6c14\uff0c\u8fd0\u96c6\u638c\u529b\uff0c\u84c4\u52bf\u4ee5\u5f85\u3002 \u3000\u3000\u540c\u65f6\uff0c\u501f\u706b\u5149\u67e5\u770b\u4e86\u654c\u4eba\u7684\u5f62\u6001\u3002 \u3000\u3000\u738b\u5f64\u76ee\u5149\u9510\u5229\uff0c\u53d1\u89c9\u6563\u5e03\u5728\u5ead\u9662\u4e2d\u7684\u4eba\uff0c\u90fd\u7a7f\u7740\u6df1\u84dd\u8272\u548c\u9752\u8272\u7684\u52b2\u88c5\uff0c\u4f3c\u662f\u7531\u4e24\u80a1\u529b\u91cf \u5408\u4e8e\u4e00\u5904\uff0c\u4f7f\u7528\u7684\u5175\u5203\u4e5f\u5404\u4e0d\u76f8\u540c\uff0c\u6709\u5200\u3001\u6709\u5251\u3001\u6709\u5224\u5b98\u7b14\u3001\u94fe\u5b50\u67aa\u7b49\u3002 \u3000\u3000\u4f46\u7ad9\u5728\u5c4b\u9762\u4e0a\u7684\u4eba\u5374\u5168\u7a7f\u7740\u9ed1\u670d\uff0c\u624b\u4e2d\u5175\u5203\u4e5f\u4e00\u6837\uff0c\u662f\u5e26\u6709\u62a4\u624b\u94a2\u5708\u7684\u957f\u5200\u3002 \u3000\u3000\u8fd9\u539f\u662f\u4e2d\u539f\u5f88\u5c11\u89c1\u7684\u957f\u5200\uff0c\u6267\u5200\u4eba\u7684\u53f3\u624b\u5168\u5728\u4e00\u4e2a\u5706\u5f62\u7684\u62a4\u624b\u5708\u5185\uff0c\u4fdd\u62a4\u4e86\u63e1\u5200\u7684\u53f3\u624b \u548c\u4e09\u5bf8\u624b\u8155\uff0c\u4e0d\u4e3a\u654c\u4eba\u5175\u5203\u6240\u4f24\u3002 \u3000\u3000\u90a3\u4e00\u76f4\u9759\u5750\u672a\u52a8\u7684\u9752\u8863\u4eba\uff0c\u7a81\u7136\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5200\u6709\u62a4\u8155\uff0c\u5185\u85cf\u5315\u9996\uff0c\u53f7\u79f0\u5b50\u6bcd\u5200\uff0c\u662f \u897f\u65b9\u9b54\u6559\u7684\u6807\u51c6\u5175\u5203\uff0c\u60f3\u4e0d\u5230\u4ed6\u4eec\u5df2\u4fb5\u5165\u4e2d\u539f\uff0c\u5f00\u5c01\u5e9c\u4e2d\u7adf\u6709\u5982\u6b64\u591a\u7684\u9b54\u6559\u5f1f\u5b50\u3002\u201d \u3000\u3000\u539f\u6765\uff0c\u90a3\u5c4b\u9762\u4e0a\u53c8\u6709\u4e86\u5f88\u591a\u9ed1\u8863\u4eba\u51fa\u73b0\uff0c\u4e1c\u3001\u897f\u4e24\u53a2\u52a0\u8d77\u6765\uff0c\u8db3\u8db3\u6709\u4e94\u5341\u4eba\u4e4b\u591a\u3002 \u3000\u3000\u738b\u5f64\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8fd8\u672a\u6765\u5f97\u53ca\u95ee\u8bdd\uff0c\u654c\u4eba\u653b\u52bf\u5df2\u7ecf\u5c55\u5f00\uff0c\u5341\u679a\u706b\u7403\u6d41\u661f\u822c\u6295\u5411\u5385\u4e2d\uff0c\u56db\u4e2a \u84dd\u8863\u4eba\u7d27\u968f\u706b\u7403\u51b2\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u738b\u5f64\u53cc\u638c\u9f50\u6325\uff0c\u6253\u51fa\u4e86\u4e00\u80a1\u5f3a\u70c8\u7684\u638c\u98ce\uff0c\u4e24\u4e2a\u5f53\u5148\u98de\u5165\u5385\u95e8\u7684\u706b\u7403\uff0c\u88ab\u4ed6\u9707\u51fb\u7684\u53cd\u5f39\u51fa \u53bb\u3002 \u3000\u3000\u738b\u91cd\u5c71\u3001\u8d75\u4fdd\u3001\u9648\u5b8f\uff0c\u5200\u3001\u5251\u5e76\u51fa\uff0c\u5de6\u3001\u53f3\u65bd\u88ad\uff0c\u5404\u6321\u4f4f\u4e00\u679a\u706b\u7403\u3002 \u3000\u3000\u4f46\uff0c\u4ecd\u6709\u4e09\u679a\u706b\u7403\u98de\u5165\u4e86\u5385\u4e2d\u3002 \u3000\u3000\u8fd9\u4e9b\u706b\u7403\u71c3\u70e7\u529b\u5f88\u5f3a\uff0c\u4e0d\u6613\u6251\u706d\uff0c\u5982\u88ab\u649e\u788e\uff0c\u66f4\u662f\u706b\u82b1\u56db\u6e85\uff0c\u6ee1\u5ba4\u706b\u7130\uff0c\u4e0d\u4f46\u5ba4\u4e2d\u60c5\u52bf \u4e00\u76ee\u4e86\u7136\uff0c\u800c\u4e14\u53d8\u6210\u4e86\u654c\u6697\u6211\u660e\uff0c\u5931\u53bb\u4e86\u5229\u7528\u591c\u6697\u4ee5\u5c11\u5fa1\u4f17\u7684\u5c4f\u969c\u3002 \u3000\u3000\u4f46\u89c1\u90a3\u76d8\u819d\u800c\u606f\u7684\u9752\u8863\u4eba\uff0c\u7a81\u7136\u98de\u8eab\u800c\u8d77\uff0c\u8fce\u5411\u706b\u7403\uff0c\u5de6\u3001\u53f3\u53cc\u624b\u5404\u63a5\u4e00\u4e2a\uff0c\u53cd\u63b7\u51fa \u5385\uff0c\u540c\u65f6\u53f3\u811a\u62ac\u8d77\uff0c\u8e22\u51fa\u4e86\u53e6\u4e00\u679a\u706b\u7403\u3002 \u3000\u3000\u4ed6\u7528\u7684\u5168\u662f\u9634\u67d4\u4e4b\u529b\uff0c\u718a\u718a\u9ad8\u70e7\u7684\u706b\u7403\uff0c\u7adf\u7136\u5168\u65e0\u635f\u4f24\u7684\u98de\u5411\u654c\u4eba\u7fa4\u4e2d\u3002 \u3000\u3000\u56db\u4e2a\u75be\u51b2\u800c\u81f3\u7684\u84dd\u8863\u4eba\uff0c\u5148\u88ab\u706b\u7403\u4e00\u6321\uff0c\u4e0d\u5f97\u4e0d\u505c\u4e0b\u8eab\u5b50\u8ba9\u907f\u706b\u7403\u3002 \u3000\u3000\u4f46\u8fd9\u5df2\u7ed9\u4e86\u738b\u5f64\u5236\u654c\u5148\u673a\u7684\u673a\u4f1a\uff0c\u4eba\u5f71\u75be\u63a0\u800c\u81f3\uff0c\u53cc\u624b\u9f50\u51fa\uff0c\u6263\u4f4f\u4e86\u4e24\u4e2a\u84dd\u8863\u4eba\u7684\u8155 \u7a74\uff0c\u731b\u7136\u4e00\u7529\uff0c\u6d3b\u751f\u751f\u7684\u628a\u4e24\u4e2a\u84dd\u8863\u4eba\u629b\u51fa\u4e86\u4e00\u4e08\u591a\u8fdc\u3002 \u3000\u3000\u201c\u597d\uff01\u6458\u661f\u624b\u540d\u4e0d\u865a\u4f20\u2026\u2026\u201d\u84dd\u8863\u6587\u4e0a\u7a81\u7136\u6b3a\u8eab\u800c\u4e0a\uff0c\u624b\u4e2d\u4e00\u628a\u647a\u6247\uff0c\u547c\u5730\u5c55\u5f00\uff0c\u7adf\u7136 \u5f53\u4f5c\u5355\u5200\u65bd\u7528\uff0c\u5237\u5237\u5237\u8fde\u5288\u4e09\u6247\u3002 \u3000\u3000\u738b\u5f64\u4ee5\u64d2\u62ff\u624b\u548c\u638c\u6cd5\u4eab\u8a89\u6b66\u6797\uff0c\u9020\u8be3\u6781\u6df1\uff0c\u62a4\u8155\u4e0a\u7684\u8fde\u7ed3\u94a2\u7247\u53c8\u53ef\u4ee5\u5c01\u6321\u654c\u4eba\u5175\u5203\uff0c\u4f7f \u5f97\u70b9\u7a74\u622a\u8109\u624b\u6cd5\uff0c\u66f4\u80fd\u53d1\u6325\u51fa\u5236\u654c\u7684\u53d8\u5316\u3002 \u3000\u3000\u90a3\u84dd\u8863\u4eba\u624b\u4e2d\u7684\u647a\u6247\uff0c\u867d\u7136\u653b\u52bf\u51cc\u5389\u3001\u6781\u5c3d\u53d8\u5316\u4e4b\u5999\uff0c\u4f46\u738b\u5f64\u5728\u52a8\u624b\u4e94\u62db\u4e4b\u5185\uff0c\u5df2\u627e\u51fa \u4e86\u5bf9\u65b9\u7684\u7834\u7efd\uff0c\u5236\u670d\u5bf9\u65b9\u6613\u5982\u53cd\u638c\uff0c\u4f46\u738b\u5f64\u5374\u4fdd\u7559\u4e86\u80dc\u5229\uff0c\u6218\u4e86\u4e2a\u4e0d\u80dc\u4e0d\u8d25\u4e4b\u5c40\u3002 \u3000\u3000\u4ed6\u9690\u85cf\u4e86\u5b9e\u529b\uff0c\u4f46\u8868\u9762\u4e0a\u5374\u548c\u90a3\u84dd\u8863\u4eba\u6253\u6597\u7684\u5341\u5206\u6fc0\u70c8\uff0c\u4eba\u5f71\u4ea4\u9519\uff0c\u628a\u5385\u95e8\u5b8c\u5168\u6321\u4f4f \u4e86\u3002 \u3000\u3000\u8fd9\u5c31\u4f7f\u5f97\u5ead\u9662\u4e2d\u7684\u4eba\uff0c\u65e0\u6cd5\u628a\u706b\u7403\u6295\u5165\u5385\u4e2d\uff0c\u4e00\u76f4\u4fdd\u6301\u4e86\u5385\u4e2d\u7684\u591c\u6697\u3001\u9690\u5bc6\uff0c\u4f46\u5374\u628a\u5ead \u9662\u4e2d\u7684\u654c\u60c5\u5f62\u52bf\uff0c\u770b\u5f97\u5341\u5206\u6e05\u695a\u3002 \u3000\u3000\u654c\u4eba\u5206\u6210\u4e86\u4e09\u884c\u7eb5\u961f\uff0c\u5df2\u5206\u7ad9\u4e86\u4e09\u4e2a\u65b9\u4f4d\uff0c\u4ee5\u8863\u670d\u989c\u8272\u533a\u5206\uff0c\u5404\u6210\u4e00\u961f\u3002 \u3000\u3000\u8fd9\u4e9b\u6c5f\u6e56\u4e2d\u7684\u8349\u83bd\u4eba\u7269\uff0c\u5728\u624b\u52bf\u6325\u5f15\u4e4b\u4e0b\uff0c\u80fd\u591f\u8fc5\u5feb\u7684\u6392\u6210\u4e86\u961f\u5f62\u3001\u9635\u52bf\uff0c\u770b\u5f97\u738b\u5f64\u5927 \u5927\u7684\u5403\u60ca\u3002 \u3000\u3000\u4ed6\u6697\u6697\u5fd6\u9053\uff1a\u8fd9\u4e0d\u662f\u6c5f\u6e56\u4e2d\u4eba\uff0c\u800c\u662f\u8bad\u7ec3\u6709\u7d20\u7684\u8d85\u7ea7\u519b\u961f\u4e86\uff0c\u770b\u6765\uff0c\u4ed6\u4eec\u5373\u5c06\u5c55\u5f00\u7684\u653b \u52bf\u548c\u6c5f\u6e56\u4e2d\u7684\u56f4\u6740\u4e0d\u540c\u2026\u2026\u3002 \u3000\u3000\u4f46\u95fb\u9c7c\u5316\u9f99\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u9648\u5e73\uff0c\u9000\u4e0b\u6765\u5427\uff01\u5982\u82e5\u738b\u5f64\u8981\u65bd\u5c55\u6bd2\u624b\uff0c\u4f60\u65e9\u5c31\u6b7b\u4f24\u5728\u4eba\u5bb6\u7684 \u638c\u4e0b\u4e86\u3002\u201d \u3000\u3000\u679c\u7136\uff0c\u9c7c\u5316\u9f99\u65e9\u5df2\u77a7\u51fa\u4e86\u738b\u5f64\u6545\u610f\u624b\u4e0b\u7559\u60c5\u3002 \u3000\u3000\u9648\u5e73\u647a\u6247\u75be\u6325\uff0c\u653b\u51fa\u4e86\u4e09\u62db\uff0c\u903c\u5f97\u738b\u5f64\u653b\u52bf\u4e00\u7f13\uff0c\u95ea\u9000\u4e94\u5c3a\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u59d3\u738b\u7684\uff0c\u544a \u8bc9\u6211\uff0c\u4f60\u662f\u4e0d\u662f\u624b\u4e0b\u7559\u60c5\u4e86\uff1f\u201d \u3000\u3000\u738b\u5f64\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u5fc3\u4e2d\u660e\u767d\uff0c\u6211\u4eec\u79cb\u8272\u5e73\u5206\uff0c\u8981\u8bf4\u738b\u67d0\u5360\u5148\uff0c\u533a\u533a\u53ef\u4e0d\u6562\u79f0 \u7f8e\u3002\u201d \u3000\u3000\u9648\u5e73\u51dd\u795e\u601d\u7d22\uff0c\u826f\u4e45\u4e0d\u8a00\u3002 \u3000\u3000\u539f\u6765\uff0c\u738b\u5f64\u60f3\u62d6\u5ef6\u65f6\u95f4\uff0c\u6240\u4ee5\u548c\u9648\u5e73\u52a8\u624b\u65f6\uff0c\u8868\u73b0\u51fa\u5168\u529b\u8fce\u654c\u7684\u6253\u6cd5\uff0c\u7ed9\u9648\u5e73\u7684\u611f\u89c9 \u662f\uff0c\u4e24\u4eba\u52bf\u5747\u529b\u654c\u3001\u80dc\u8d1f\u96be\u5206\u3002 \u3000\u3000\u9c7c\u5316\u9f99\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9648\u5144\uff0c\u4e0d\u76f8\u4fe1\u5144\u5f1f\u7684\u8bdd\uff0c\u4ea6\u65e0\u6240\u8c13\uff0c\u4e0d\u8fc7\uff0c\u9c7c\u67d0\u4eba\u8981\u9648\u5144\u9000\u4e0b \u6765\u2026\u2026\u201d \u3000\u3000\u201c\u7406\u5f53\u4ece\u547d\uff01\u201d \u3000\u3000\u9648\u5e73\u75be\u75be\u7684\u5411\u540e\u9000\u4e86\u4e94\u5927\u6b65\u3002 \u3000\u3000\u9c7c\u5316\u9f99\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u5fc3\u5370\u5927\u5e08\uff01\u9c7c\u67d0\u4eba\u518d\u8fdb\u4e00\u8a00\uff0c\u8fd9\u662f\u4e00\u573a\u62da\u547d\u4e4b\u6218\uff0c\u4e0d\u6740\u738b\u5f64\uff0c\u8a93\u4e0d \u7f62\u4f11\uff0c\u5927\u5e08\u5982\u524d\u9000\u51fa\u662f\u975e\u4e4b\u4e89\uff0c\u73b0\u5728\u8fd8\u6765\u5f97\u53ca\u3002 \u3000\u3000\u4e00\u76f4\u4e0d\u80af\u5f00\u53e3\u7684\u5fc3\u5370\u5927\u5e08\uff0c\u7a81\u7136\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u9c7c\u65bd\u4e3b\u7684\u597d\u610f\u8001\u8896\u5fc3\u9886\u4e86\uff0c\u5728\u52ab\u8005\u96be \u9003\uff0c\u65bd\u4e3b\u8bf7\u653e\u624b\u65bd\u4e3a\u5427\uff01\u201d \u3000\u3000\u201c\u597d\uff0c\u5316\u9f99\u5df2\u5c3d\u5fc3\u529b\uff0c\u5927\u5e08\u81ea\u5df1\u73cd\u91cd\u4e86\uff01\u201d\u53f3\u624b\u4e00\u6325\u6307\u5411\u5385\u95e8\u3002 \u3000\u3000\u90a3\u8d64\u624b\u63a5\u4e0b\u706b\u7403\u3001\u4e0d\u8651\u70eb\u4f24\u7684\u9752\u8863\u4eba\uff0c\u7a81\u7136\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5c0f\u548c\u5c1a\u8981\u4eba\u8f6c\u544a\u6211\uff1a\u5357\u6d77\u9f99 \u9041\uff0c\u5317\u51e4\u5b64\u98de\u3002\u8fd9\u4ef6\u4e8b\u662f\u771f\u662f\u5047\uff1f\u201d \u3000\u3000\u5fc3\u5370\u5927\u5e08\u4f4e\u5ba3\u4e00\u58f0\u4f5b\u53f7\uff0c\u9053\uff1a\u201c\u8001\u8872\u5df2\u5341\u5e74\u6ca1\u6709\u79bb\u5f00\u8fc7\u76f8\u56fd\u5bfa\u4e00\u6b65\uff0c\u77e5\u9053\u7684\uff0c\u90fd\u662f\u5341\u5e74 \u4ee5\u524d\u7684\u65e7\u4e8b\u2026\u2026\u201d \u3000\u3000\u201c\u597d\uff01\u6211\u518d\u8bf7\u6559\u4e00\u4e8b\uff0c\u90a3\u59d3\u9c7c\u7684\u4e00\u76f4\u529d\u4f60\u548c\u9ed1\u7f57\u6c49\u79bb\u5f00\u8fd9\u91cc\uff0c\u5bf9\u4f60\u4eec\u8fd9\u4e9b\u7a7a\u95e8\u4e2d\u4eba\uff0c\u4f3c \u6709\u4e00\u79cd\u504f\u7231\uff0c\u662f\u4f55\u9053\u7406\uff1f\u201d \u3000\u3000\u5fc3\u5370\u5927\u5e08\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u5fc3\u6709\u6240\u7cfb\uff0c\u5f62\u4e4b\u4e8e\u5916\uff0c\u4e2a\u4e2d\u5fc5\u6709\u539f\u56e0\uff0c\u4f46\u4ec0\u4e48\u539f\u56e0\uff1f\u5c31\u975e\u8001 \u8872\u6240\u80fd\u660e\u767d\u7684\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u4ed6\u4eec\u8fd9\u756a\u653b\u52bf\u4e00\u65e6\u5c55\u5f00\uff0c\u5fc5\u662f\u51f6\u9669\u7edd\u4f26\uff0c\u8001\u8872\u8fd1\u5341\u4f59\u5e74\u7985\u5750\u4e0b \u6765\uff0c\u5df2\u7a0d\u5177\u89c2\u5bdf\u6c14\u673a\u4e4b\u80fd\uff0c\u8fd9\u4e00\u5e2e\u4eba\u90fd\u662f\u6b7b\u58eb\u2026\u2026\u201d \u3000\u3000\u9752\u8863\u4eba\u62ac\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u4e09\u884c\u654c\u4eba\uff0c\u7f13\u6b65\u5411\u524d\u884c\u6765\uff0c\u9ed1\u8863\u4eba\u5c45\u4e2d\uff0c\u84dd\u8863\u3001\u6df1\u9752\u5206\u636e\u4e24\u4fa7\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u653b\u52bf\u4e0d\u5feb\uff0c\u4f46\u6b65\u5c65\u7a33\u5b9a\uff0c\u624b\u4e2d\u7684\u5175\u5203\u95ea\u70c1\u7740\u5bd2\u5149\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u9c7c\u5316\u9f99\u5df2\u4e0b\u4ee4\u5f03\u7f6e\u706b\u7403\uff0c\u5374\u71c3\u8d77\u4e86\u56db\u652f\u677e\u6cb9\u706b\u628a\uff0c\u7167\u5f97\u5ead\u9662\u4e2d\u4e00\u7247\u901a\u660e\u3002 \u3000\u3000\u738b\u91cd\u5c71\u4f4e\u58f0\u9053\uff1a\u201c\u9648\u5144\u3001\u8d75\u5144\uff0c\u8bf7\u5b88\u62a4\u5de6\u7ffc\u3002\u201d\u5f53\u5148\u884c\u4e0a\u4e00\u6b65\uff0c\u5b88\u5728\u738b\u5f64\u7684\u53f3\u4fa7\u3002 \u3000\u3000\u56db\u4e2a\u4eba\u5835\u4f4f\u4e86\u5927\u5385\u95e8\u53e3\u3002 \u3000\u3000\u9752\u886b\u4eba\u51b7\u8bf4\u9053\uff1a\u201c\u4e09\u4f4d\uff0c\u8fd9\u4e0d\u662f\u5e2e\u5fd9\uff0c\u800c\u662f\u81ea\u4e71\u9635\u6b65\u3002\u201d \u3000\u3000\u738b\u91cd\u5c71\u76ee\u5149\u4e00\u8f6c\uff0c\u679c\u7136\u4e0d\u9519\uff0c\u56db\u4e2a\u4eba\u5e76\u6392\u4e00\u7ad9\uff0c\u786e\u6709\u70b9\u65bd\u5c55\u4e0d\u5f00\u3002 \u3000\u3000\u4f46\u95fb\u5de5\u5f64\u5927\u58f0\u559d\u9053\uff1a\u201c\u9000\u56de\u53bb\uff01\u201d\u4eba\u5374\u98de\u8eab\u800c\u8d77\uff0c\u8fce\u5411\u7b2c\u4e00\u6ce2\u653b\u52bf\u3002 \u3000\u3000\u4f46\u95fb\u4e00\u9635\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\uff0c\u738b\u5f64\u4ee5\u62a4\u8155\u94a2\u7247\u63a5\u4e0b\u4e86\u4e09\u67c4\u5200\u5203\uff0c\u8fde\u4e32\u98de\u95ea\u7684\u706b\u661f\u4e2d\uff0c\u4e09\u4e2a\u653b \u6765\u7684\u654c\u4eba\uff0c\u7a81\u7136\u4e00\u4e2a\u5012\u7ffb\uff0c\u9000\u4e86\u56de\u53bb\u3002 \u3000\u3000\u4f46\u7b2c\u4e8c\u6ce2\u7684\u653b\u52bf\uff0c\u8fde\u7ef5\u800c\u81f3\uff0c\u653b\u51fa\u4e00\u5200\uff0c\u7acb\u523b\u9000\u4e0b\u3002 \u3000\u3000\u90a3\u662f\u5168\u529b\u6325\u51fa\u7684\u4e00\u5200\uff0c\u4e09\u4e2a\u4eba\u624b\u4e2d\u7684\u5175\u5203\u867d\u7136\u4e0d\u540c\uff0c\u4f46\u7ed3\u5408\u5374\u5341\u5206\u7d27\u5bc6\uff0c\u4ee5\u4e2d\u95f4\u9ed1\u8863\u4eba \u7684\u653b\u52bf\u4e3a\u4e3b\uff0c\u5de6\u3001\u53f3\u84dd\u8863\u53ca\u9752\u8863\u4eba\u4e3a\u8f85\uff0c\u4e09\u4ef6\u5175\u5203\u6784\u6210\u7684\u653b\u51fb\uff0c\u9664\u4e86\u786c\u63a5\u4e4b\u5916\u5c31\u53ea\u6709\u5411\u540e\u9000 \u907f\uff0c\u8ba9\u51fa\u5385\u95e8\u3002 \u3000\u3000\u738b\u5f64\u4e00\u8fde\u63a5\u4e0b\u4e86\u5341\u6ce2\u653b\u52bf\uff0c\u624b\u81c2\u5df2\u88ab\u9707\u5f97\u5fae\u5fae\u9178\u9ebb\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u79cd\u6fc0\u70c8\u7684\u5f3a\u653b\uff0c\u6bcf\u4eba\u4e00\u62db\uff0c\u4e0d\u604b\u6218\u3001\u4e0d\u901e\u5f3a\uff0c\u662f\u4e00\u79cd\u4e45\u7ecf\u8bad\u7ec3\u7684\u9ad8\u624b\u3002 \u3000\u3000\u738b\u91cd\u5c71\u957f\u5251\u4e00\u6446\uff0c\u51c6\u5907\u63a5\u66ff\u738b\u5f64\u4e0b\u6765\uff0c\u8ba9\u4ed6\u4f11\u606f\u4e00\u4e0b\uff0c\u5374\u88ab\u9752\u8863\u4eba\u7ed9\u62e6\u4f4f\uff1a\u201c\u6b66\u5f53\u5251\u6cd5 \u4ee5\u8f7b\u7075\u4e3a\u4e3b\uff0c\u4e0d\u9002\u5b9c\u8fd9\u79cd\u6253\u6cd5\u2026\u2026\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u4e00\u4f38\u624b\uff0c\u53d6\u8fc7\u5fc3\u5370\u5927\u5e08\u7684\u6212\u5200\uff0c\u53c8\u9053\uff1a\u201c\u8001\u548c\u5c1a\uff0c\u6212\u5200\u501f\u7528\u4e00\u4e0b\u5982\u4f55\uff1f\u201d \u3000\u3000\u53e3\u4e2d\u867d\u7136\u8bf4\u7684\u5ba2\u6c14\uff0c\u4f46\u53d6\u5200\u7684\u6c14\u52bf\uff0c\u5374\u662f\u6709\u70b9\u9738\u738b\u786c\u4e0a\u5f13\u7684\u5473\u9053\u3002 \u3000\u3000\u738b\u91cd\u5c71\u5fc3\u4e2d\u5fd6\u9053\uff1a\u8fd9\u9752\u8863\u4eba\u5e74\u7eaa\u4e0d\u5927\uff0c\u4f3c\u662f\u5c0f\u548c\u5c1a\u7684\u670b\u53cb\uff0c\u4f46\u5bf9\u8001\u548c\u5c1a\u5374\u4e5f\u5e73\u8f88\u8bba\u4ea4\uff0c \u4e0d\u77e5\u662f\u4ec0\u4e48\u8eab\u4efd\u7684\u4eba\u7269\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u90a3\u9752\u8863\u4eba\u5df2\u66ff\u6362\u4e0b\u738b\u5f64\uff0c\u6212\u5200\u6325\u52a8\uff0c\u6321\u4f4f\u4e86\u4e09\u884c\u7eb5\u961f\u7684\u653b\u52bf\u3002 \u3000\u3000\u8fd9\u9752\u8863\u4eba\u72ec\u5750\u5e7d\u5ba4\uff0c\u5982\u8001\u50e7\u5165\u5b9a\u4e00\u822c\uff0c\u9ed1\u7f57\u6c49\u8bf4\u4ed6\u88ab\u5fc3\u9501\u6240\u62d8\uff0c\u4e0d\u77e5\u662f\u4ec0\u4e48\u6837\u7684\u5fc3\u9501\uff1f \u800c\u4e24\u53e5\u8bdd\u5374\u53c8\u80fd\u4f7f\u5fc3\u9501\u81ea\u5f00\uff0c\u884c\u51fa\u5ba4\u5916\uff0c\u5e2e\u5fd9\u62d2\u6321\u5f3a\u654c\uff0c\u9ed1\u7f57\u6c49\u8bf4\u4ed6\u6b66\u529f\u9ad8\u5f3a\uff0c\u4e0d\u77e5\u9ad8\u5f3a\u5230 \u4ec0\u4e48\u7a0b\u5ea6\uff1f \u3000\u3000\u53ea\u89c1\u4ed6\u6212\u5200\u7ffb\u98de\uff0c\u5e7b\u5316\u4f5c\u4e00\u7247\u5200\u5149\uff0c\u5b8c\u5168\u628a\u5385\u95e8\u5c01\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8033\u9645\u95f4\u8fde\u7ef5\u4e0d\u7edd\u7684\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\uff0c\u5168\u88ab\u5c01\u6321\u5728\u5200\u5149\u4e4b\u5916\u3002 \u3000\u3000\u8fd9\u5200\u6cd5\u7684\u7ef5\u5bc6\u795e\u5947\uff0c\u8fde\u738b\u5f64\u4e5f\u770b\u5f97\u6026\u7136\u5fc3\u52a8\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5927\u5e08\uff0c\u8fd9\u5e74\u8f7b\u4eba\u7684\u5200\u6cd5\u5f88\u9ad8 \u660e\u3002\u201d \u3000\u3000\u201c\u662f\uff0c\u98ce\u96f7\u5341\u516b\u5200\uff0c\u4ee5\u5a01\u731b\u89c1\u79f0\u6b66\u6797\uff0c\u5982\u8fd0\u8f6c\u5230\u81f3\u9ad8\u7684\u5883\u754c\uff0c\u5200\u6cd5\u80fd\u5982\u6cd5\u8f6e\u8f6c\u52a8\uff0c\u5200\u5200 \u76f8\u751f\uff0c\u6cfc\u6c34\u96be\u5165\uff0c\u4f46\u53c8\u4e0d\u7528\u592a\u8017\u5185\u529b\u2026\u2026\u201d\u5fc3\u5370\u5927\u5e08\u8bf4\uff1a\u201c\u662f\u5200\u6cd5\u4e2d\u7684\u6781\u54c1\u4e0a\u5b66\uff0c\u5bf9\u4ed8\u8fd9\u7b49 \u7fa4\u653b\uff0c\u662f\u6700\u6709\u6548\u7684\u4e00\u79cd\u5200\u6cd5\u4e86\u3002\u201d \u3000\u3000\u201c\u98ce\u96f7\u5341\u516b\u5200\uff01\u201d\u738b\u5f64\u6709\u4e9b\u5403\u60ca\u5730\u9053\uff1a\u201c\u4ed6\u662f\u98ce\u96f7\u5200\u859b\u8001\u4eba\u7684\u5f1f\u5b50\uff1f\u201d \u3000\u3000\u201c\u662f\u5b59\u5b50\uff0c\u5200\u516c\u5b50\u859b\u767e\u80dc\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c31\u96be\u602a\u4e86\uff01\u201d\u738b\u5f64\u8bf4\u9053\uff1a\u201c\u4e45\u95fb\u98ce\u96f7\u5200\u6cd5\uff0c\u4e3a\u767e\u5e74\u6765\u5200\u6cd5\u4e2d\u7b2c\u4e00\u5947\u5b66\uff0c\u4eca\u65e5\u679c\u7136\u662f \u5927\u5f00\u773c\u754c\u4e86\u3002\u201d \u3000\u3000\u90a3\u7ed5\u98de\u4e8e\u5385\u95e8\u4e4b\u5916\u7684\u5200\u6cd5\uff0c\u6108\u6765\u6108\u89c1\u6025\u5feb\uff0c\u4e00\u523b\u5de5\u592b\u4e4b\u540e\uff0c\u5df2\u7ecf\u4e0d\u89c1\u4eba\u8ff9\uff0c\u53ea\u89c1\u4e00\u7247\u5200 \u5149\u8f6c\u52a8\u3002 \u3000\u3000\u7a81\u7136\uff0c\u4e00\u58f0\u96f7\u9707\u7684\u5927\u559d\u4f20\u4e86\u8fc7\u6765\uff1a\u201c\u4f4f\u624b\uff01\u201d \u3000\u3000\u8f6e\u8f6c\u4e0d\u606f\u7684\u653b\u52bf\uff0c\u7a81\u7136\u95f4\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u859b\u767e\u80dc\u4e5f\u6536\u4f4f\u4e86\u6212\u5200\u3002 \u3000\u3000\u9c7c\u5316\u9f99\u5389\u58f0\u9053\uff1a\u201c\u98ce\u96f7\u5200\u859b\u8001\u4eba\u7684\u95e8\u4e0b\uff0c\u51e0\u65f6\u4e5f\u548c\u59d3\u738b\u7684\u642d\u4e0a\u4e86\uff1f\u2026\u2026\u201d \u3000\u3000\u859b\u767e\u80dc\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u53ea\u662f\u53d7\u4eba\u4f20\u8baf\u4e4b\u6069\uff0c\u804a\u4ee5\u56de\u62a5\uff0c\u4f60\u4eec\u4e4b\u95f4\u7684\u6069\u6069\u6028\u6028\uff0c\u5374\u662f\u548c \u6211\u65e0\u5173\u3002\u201d \u3000\u3000\u9c7c\u5316\u9f99\u9053\uff1a\u201c\u9601\u4e0b\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u859b\u767e\u80dc\u3002\u201d \u3000\u3000\u201c\u5200\u516c\u5b50\u859b\u767e\u80dc\uff0c\u9c7c\u67d0\u4eba\u4e45\u4ef0\u4e86\uff01\u201d\u9c7c\u5316\u9f99\u6325\u6325\u624b\uff0c\u9053\uff1a\u201c\u4f60\u6321\u62d2\u4f4f\u6211\u4eec\u4e5d\u5341\u6b21\u7684\u653b \u52bf\uff0c\u8fd9\u4f20\u8baf\u7684\u60c5\u8c0a\uff0c\u4e5f\u8be5\u62a5\u7b54\u5b8c\u4e86\u5427\uff01\u201d \u3000\u3000\u201c\u8fd8\u6ca1\u6709\u2026\u2026\u201d\u859b\u767e\u80dc\u9053\uff1a\u201c\u6211\u7b54\u5e94\u5e2e\u5fd9\u4e00\u591c\uff0c\u5929\u4e00\u4eae\u6211\u5c31\u79bb\u5f00\uff0c\u90a3\u65f6\uff0c\u4f60\u4eec\u4e4b\u95f4\u7684\u6b7b \u6d3b\uff0c\u5c31\u548c\u6211\u5b8c\u5168\u65e0\u5173\u7cfb\u4e86\u3002\u201d \u3000\u3000\u9c7c\u5316\u9f99\u9053\uff1a\u201c\u53ea\u53ef\u60dc\uff0c\u6211\u4eec\u6ca1\u6709\u591a\u4f59\u7684\u65f6\u95f4\u7ed9\u4f60\u859b\u516c\u5b50\u8fd9\u4efd\u9762\u5b50\u2026\u2026\uff02\u201d\u90a3\u5c31\u6ca1\u6709\u529e\u6cd5 \u4e86\u3002\u201c\u859b\u767e\u80dc\u9053\uff1a\u201d\u5728\u4e0b\u8bf4\u8fc7\u7684\u8bdd\uff0c\u4e00\u5411\u4e0d\u4f1a\u66f4\u6539\u3002\u201c \u3000\u3000\u9c7c\u5316\u9f99\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u53ea\u597d\u5f97\u7f6a\u4e86\u3002\u201d\u7a81\u7136\u4e00\u4e0a\u6b65\uff0c\u65a9\u9a6c\u5200\u8fce\u5934\u5288\u4e0b\u3002 \u3000\u3000\u859b\u767e\u80dc\u4e3e\u5200\u4e00\u5c01\uff0c\u53cc\u5200\u76f8\u89e6\uff0c\u53ea\u54cd\u8d77\u4e00\u58f0\u8f7b\u5fae\u7684\u8106\u9e23\uff0c\u4e24\u67c4\u5200\u7adf\u7136\u7c98\u5728\u4e00\u8d77\u3002 \u3000\u3000\u539f\u6765\uff0c\u9c7c\u5316\u9f99\u6697\u8fd0\u5185\u529f\uff0c\u96c6\u805a\u5200\u950b\u4e4b\u4e0a\uff0c\u4f7f\u5200\u4e0a\u529b\u9053\u91cd\u903e\u5343\u65a4\uff0c\u7f13\u7f13\u5411\u4e0b\u8feb\u538b\u3002 \u3000\u3000\u859b\u767e\u80dc\u611f\u53d7\u5230\u90a3\u6c89\u91cd\u7684\u5200\u52bf\uff0c\u4f46\u5df2\u65e0\u6cd5\u95ea\u907f\uff0c\u53ea\u597d\u8fd0\u52b2\u6297\u62d2\u3002 \u3000\u3000\u8fd9\u5c31\u5f62\u6210\u4e86\u4e00\u573a\u6bd4\u62da\u5185\u529b\u7684\u51b3\u6597\uff0c\u4efb\u4f55\u5de7\u5999\u7684\u53d8\u5316\u90fd\u65e0\u6cd5\u65bd\u5c55\u51fa\u6765\u3002 \u3000\u3000\u9c7c\u5316\u9f99\u5929\u751f\u795e\u529b\uff0c\u518d\u52a0\u4e0a\u6df1\u539a\u7684\u5185\u529f\uff0c\u5200\u516c\u5b50\u820d\u957f\u53d6\u77ed\uff0c\u7acb\u9677\u5371\u5883\uff0c\u53ea\u89c1\u624b\u4e2d\u6212\u5200\uff0c\u7f13 \u7f13\u5411\u4e0b\u6c89\u843d\uff0c\u4e00\u4ef6\u9752\u886b\u4e5f\u5168\u88ab\u6c57\u6c34\u6e7f\u900f\u3002 \u3000\u3000\u4e00\u773c\u4e4b\u95f4\u53ef\u4ee5\u77a7\u51fa\u6765\uff0c\u4ed6\u662f\u771f\u7684\u9677\u5165\u4e86\u5371\u6025\u4e4b\u4e2d\uff0c\u5982\u82e5\u65e0\u6cd5\u6446\u8131\u8fd9\u8981\u547d\u7684\u4e00\u51fb\uff0c\u5373\u5c06\u662f \u4e00\u4e2a\u8f7b\u5219\u53d7\u4f24\u3001\u91cd\u5219\u6b92\u547d\u7684\u7ed3\u679c\u3002 \u3000\u3000\u738b\u5f64\u6b63\u60f3\u51fa\u624b\uff0c\u6321\u5f00\u9c7c\u5316\u9f99\u6c89\u91cd\u7684\u5200\u52bf\uff0c\u5ffd\u89c1\u4e00\u6839\u7985\u6756\uff0c\u6a2a\u91cc\u98de\u6765\uff0c\u6572\u5728\u65a9\u9a6c\u5200\u4e0a\u3002 \u3000\u3000\u90a3\u4e00\u6572\u4e4b\u529b\uff0c\u5341\u5206\u5f3a\u5927\u3002 \u3000\u3000\u9c7c\u5316\u9f99\u624b\u4e2d\u7684\u65a9\u9a6c\u5200\uff0c\u7adf\u7136\u504f\u5f00\u4e86\u4e24\u5c3a\uff0c\u859b\u767e\u80dc\u8d81\u52bf\u6536\u5200\u800c\u9000\u3002 \u3000\u3000\u4e0d\u7528\u56de\u5934\u770b\uff0c\u9c7c\u5316\u9f99\u5df2\u77e5\u9053\u5fc3\u5370\u5927\u5e08\u51fa\u624b\u4e86\uff0c\u90a3\u6c89\u91cd\u7684\u5175\u5203\uff0c\u5f3a\u5927\u7684\u52b2\u9053\uff0c\u5385\u4e2d\u4e09\u4eba\uff0c \u53ea\u6709\u5fc3\u5370\u5927\u5e08\u80fd\u591f\u529e\u5230\u3002 \u3000\u3000\u201c\u9c7c\u67d0\u5bf9\u4f5b\u95e8\u4e2d\u4eba\uff0c\u6709\u4e00\u4efd\u7279\u522b\u8bda\u656c\uff0c\u90a3\u662f\u6e90\u81ea\u4e00\u6bb5\u5f80\u4e8b\uff0c\u8bf7\u5927\u5e08\u4e0d\u8981\u7834\u574f\u4e86\u7559\u5728\u6211\u5fc3 \u4e2d\u7684\u8bda\u656c\u3002\u201d \u3000\u3000\u201c\u9c7c\u65bd\u4e3b\u662f\u5426\u80fd\u6682\u65f6\u9000\u51fa\u6b64\u5730\uff0c\u4e94\u66f4\u4e4b\u540e\u518d\u6765\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u65f6\uff0c\u5927\u5e08\u5c31\u4e0d\u518d\u5e72\u4e0e\u6b64\u4e8b\u4e86\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u9c7c\u65bd\u4e3b\u5927\u6982\u4e5f\u770b\u51fa\u4eca\u591c\u7684\u5f62\u52bf\u4e86\uff0c\u5982\u82e5\u738b\u65bd\u4e3b\u4e0d\u6c42\u6709\u529f\uff0c\u5200\u516c\u5b50\u5168\u529b\u914d\u5408\uff0c\u518d\u52a0 \u4e0a\u8001\u8896\u8fd9\u652f\u7985\u6756\uff0c\u51ed\u9669\u56fa\u5b88\uff0c\u65bd\u4e3b\u8981\u60f3\u653b\u5165\u8fd9\u5ea7\u5927\u5385\uff0c\u53ea\u6015\u8981\u8d39\u4e0a\u4e00\u756a\u624b\u811a\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff01\u9002\u624d\u4e00\u6756\uff0c\u5df2\u89c1\u529f\u529b\uff0c\u4f46\u4e94\u66f4\u4e4b\u540e\u5462\uff1f\u201d \u3000\u3000\u201c\u65bd\u4e3b\u53ef\u4ee5\u8fdb\u5165\u8fd9\u5ea7\u5927\u5385\u4e2d\u4e86\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u4e0d\u518d\u62b5\u6297\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u60f3\u662f\u4e0d\u4f1a\u4e86\uff0c\u81f3\u5c11\uff0c\u8001\u8896\u548c\u5200\u516c\u5b50\u4e0d\u4f1a\u518d\u51fa\u624b\u6297\u62d2\u4e86\u3002\u201d \u3000\u3000\u201c\u5927\u5e08\u671b\u91cd\u4e00\u65b9\uff0c\u9c7c\u67d0\u4eba\u76f8\u4fe1\u5f97\u8fc7\uff0c\u4e0d\u8fc7\uff0c\u738b\u5f64\u53ef\u80fd\u8fd8\u6709\u4e9b\u4e0d\u670d\uff0c\u9c7c\u67d0\u53ea\u60f3\u8ba9\u738b\u5f64\u89c1\u8bc6 \u4e00\u4e0b\uff0c\u4ec0\u4e48\u53eb\u4e0d\u754f\u6b7b\u4ea1\u7684\u52c7\u58eb\uff0c\u4e5f\u8bb8\u66f4\u80fd\u4f7f\u5927\u5e08\u5316\u89e3\u52ab\u96be\u3001\u5fc3\u613f\u5f97\u507f\u3002\u201d \u3000\u3000\u56de\u8eab\u884c\u51fa\u4e86\u5341\u6b65\uff0c\u9053\uff1a\u201c\u5fc5\u6740\uff01\u201d \u3000\u3000\u4e00\u4e2a\u9ed1\u8863\u4eba\uff0c\u5e94\u58f0\u51b2\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u8d75\u4fdd\u770b\u6765\u4eba\u53ea\u6709\u4e00\u4e2a\uff0c\u6a2a\u5200\u8fce\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u4e5f\u4e0d\u7b54\u8bdd\uff0c\u626c\u624b\u4e00\u5200\uff0c\u523a\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8d75\u4fdd\u8eab\u8eaf\u5fae\u95ea\uff0c\u6a2a\u91cc\u4e00\u5200\u65a9\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u5200\u653b\u6240\u5fc5\u6551\uff0c\u90a3\u9ed1\u8863\u4eba\u81f3\u5c11\u5e94\u8be5\u5148\u5c01\u4f4f\u5bf9\u65b9\u5200\u52bf\uff0c\u518d\u62bd\u5200\u653b\u654c\u3002 \u3000\u3000\u54ea\u77e5\u9ed1\u8863\u4eba\u7406\u4e5f\u4e0d\u7406\uff0c\u4e5f\u4e0d\u9000\uff0c\u731b\u529b\u5411\u524d\u4e00\u51b2\u3002 \u3000\u3000\u8fd9\u662f\u4e07\u4e07\u6599\u4e0d\u5230\u7684\u4e8b\u60c5\uff0c\u9ed1\u8863\u4eba\u4e0d\u8981\u547d\u4e86\u3002 \u3000\u3000\u8d75\u4fdd\u94a2\u5200\u4e00\u95ea\uff0c\u4eba\u5934\u98de\u8d77\uff0c\u4e00\u9897\u8111\u888b\u98de\u51fa\u4e00\u4e08\u591a\u8fdc\u3002 \u3000\u3000\u4f46\u90a3\u9ed1\u8863\u4eba\u7684\u4e00\u5200\uff0c\u4e5f\u624e\u5728\u8d75\u4fdd\u7684\u5de6\u80a9\u4e0a\uff0c\u6d1e\u7a7f\u800c\u8fc7\u3002 \u3000\u3000\u4e00\u547d\u6362\u4e00\u5200\uff0c\u4e00\u6b7b\u4e00\u4f24\u2026\u2026 \u3000\u3000\u9c7c\u5316\u9f99\u4e00\u6325\u624b\uff0c\u4eba\u624b\u9000\u51fa\u4e86\u5341\u6b65\uff0c\u4f46\u4ecd\u628a\u5927\u5385\u56e2\u56e2\u56f4\u4f4f\u3002 \u3000\u3000\u738b\u5f64\u7b49\u770b\u5446\u4e86\u3002 \u3000\u3000\u738b\u91cd\u5c71\u6697\u6697\u5fd6\u9053\uff1a\u8fd9\u662f\u4ec0\u4e48\u6b66\u529f\uff1f\u5b8c\u5168\u4e0d\u8ba1\u672c\u8eab\u751f\u6b7b\uff0c\u53ea\u6c42\u4f24\u654c\u3002 \u3000\u3000\u5bab\u5ef7\u5200\u6cd5\uff0c\u8d35\u5728\u51cc\u5389\uff0c\u5200\u5200\u53d6\u4eba\u8981\u5bb3\uff0c\u4ee5\u62a2\u5148\u673a\uff0c\u4f46\u5982\u78b0\u4e0a\u4e86\u8fd9\u79cd\u820d\u547d\u7684\u6b7b\u58eb\uff0c\u4e0d\u8bba\u5982 \u4f55\u51f6\u5389\u7684\u5200\u6cd5\uff0c\u4e5f\u96be\u6536\u514b\u654c\u5236\u80dc\u7684\u6548\u679c\u3002 \u3000\u3000\u9648\u5b8f\u6276\u4e0b\u8d75\u4fdd\uff0c\u62d4\u51fa\u80a9\u4e0a\u94a2\u5200\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8d75\u5144\uff0c\u4f24\u7684\u5982\u4f55\uff1f\u201d \u3000\u3000\u8d75\u4fdd\u75bc\u5f97\u4e00\u5934\u5927\u6c57\uff0c\u4f46\u4ecd\u54ac\u7259\u82e6\u5fcd\uff0c\u9053\uff1a\u201c\u4f24\u75bc\u53ef\u4ee5\u5fcd\u53d7\uff0c\u4f46\u4e0d\u77e5\u8fd9\u6761\u5de6\u81c2\u662f\u5426\u5df2\u5e9f \u4e86\uff1f\u201d \u3000\u3000\u9648\u5b8f\u53d6\u51fa\u91d1\u521b\u836f\uff0c\u66ff\u8d75\u4fdd\u5305\u624e\u4f24\u52bf\uff0c\u9053\uff1a\u201c\u4f24\u7684\u867d\u7136\u4e0d\u8f7b\uff0c\u4f46\u8fd8\u4e0d\u81f4\u6b8b\u5e9f\u3002\u201d \u3000\u3000\u738b\u91cd\u5c71\u60f3\u4e86\u534a\u5929\uff0c\u4e5f\u60f3\u4e0d\u51fa\u4e00\u62db\u5251\u6cd5\uff0c\u80fd\u591f\u81f4\u654c\u4e8e\u6b7b\uff0c\u800c\u53c8\u4e0d\u4f1a\u7559\u4e0b\u7a7a\u9699\uff0c\u4f24\u5728\u5bf9\u65b9\u5200 \u4e0b\u3002\u5fcd\u4e0d\u4f4f\u4f4e\u58f0\u9053\uff1a\u201c\u5200\u516c\u5b50\u662f\u5200\u6cd5\u884c\u5bb6\uff0c\u80fd\u5426\u6709\u4e24\u5168\u4e4b\u7b56\uff0c\u65e2\u4f24\u654c\u53c8\u53ef\u81ea\u4fdd\u3002\u201d \u3000\u3000\u201c\u6211\u80fd\u6740\u4e86\u4ed6\u800c\u81ea\u5df1\u4e0d\u4f1a\u53d7\u4f24\uff0c\u4f46\u8fd9\u79cd\u4eba\uff0c\u4e00\u6b21\u6211\u53ea\u80fd\u5bf9\u4ed8\u4e24\u4e2a\uff0c\u5982\u679c\u4ed6\u6709\u4e09\u4e2a\u4eba\u653b\u4e0a \u6765\uff0c\u800c\u4e14\uff0c\u90fd\u662f\u4e0d\u8981\u547d\u7684\u6b7b\u58eb\uff0c\u5728\u4e0b\u4e5f\u65e0\u6cd5\u9003\u8fc7\u4e00\u573a\u52ab\u96be\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\u6765\u2026\u2026\u201d\u738b\u5f64\u9053\uff1a\u201c\u8fd9\u662f\u65e0\u6cd5\u7834\u89e3\u7684\u5200\u6cd5\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u77e5\u9053\uff01\u4f46\u672c\u516c\u5b50\u5c31\u65e0\u6cd5\u7834\u89e3\u4e09\u4e2a\u4e0d\u8981\u547d\u7684\u5200\u5ba2\u5408\u529b\u7684\u653b\u52bf\u3002\u201d \u3000\u3000\u201c\u6211\u770b\uff0c\u6211\u4e5f\u4e0d\u80fd\u2026\u2026\u201d\u738b\u5f64\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u56f4\u5728\u56db\u5468\u7684\u654c\u4eba\uff0c\u4e0d\u4e0b\u767e\u540d\u4e4b\u591a\uff0c\u5982 \u679c\uff0c\u4ed6\u4eec\u4eba\u4eba\u5982\u6b64\u7684\u52c7\u731b\uff0c\u6211\u4eec\u5927\u6982\u53ea\u80fd\u652f\u6301\u4e00\u523b\u5de5\u592b\u3002\u201d \u3000\u3000\u5fc3\u5370\u5927\u5e08\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u4eca\u591c\u4e00\u6218\uff0c\u65e0\u6cd5\u786c\u6324\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u2026\u2026\u201d\u738b\u5f64\u9053\uff1a\u201c\u7834\u56f4\u800c\u51fa\u7684\u673a\u4f1a\uff0c\u66f4\u4e3a\u6e3a\u5c0f\u4e86\u3002\u201d \u3000\u3000\u201c\u6709\u9c7c\u5316\u9f99\u8fd9\u6837\u7684\u654c\u4eba\u5728\u573a\uff0c\u6211\u4eec\u80dc\u7b97\u4e0d\u5927\u3002\u201d \u3000\u3000\u9002\u624d\u4e24\u4eba\u4e00\u62db\u786c\u62fc\uff0c\u5df2\u4f7f\u5f97\u5200\u516c\u5b50\u7684\u50b2\u6c14\u5c3d\u6d88\u3002 \u3000\u3000\u5fc3\u5370\u5927\u5e08\u9053\uff1a\u201c\u54b1\u4eec\u53ea\u5b88\u5385\u95e8\uff0c\u53ea\u8981\u914d\u5408\u5f97\u5b9c\uff0c\u6491\u5230\u5929\u4eae\uff0c\u5e76\u975e\u592a\u96be\uff0c\u95ee\u9898\u662f\u5929\u4eae\u4e4b \u540e\uff0c\u662f\u5426\u4e00\u5b9a\u53ef\u4ee5\u89e3\u56f4\u5462\uff1f\u201d \u3000\u3000\u201c\u5bf9\uff01\u201d\u859b\u767e\u80dc\u9053\uff1a\u201c\u5929\u4e00\u4eae\uff0c\u6211\u5c31\u79bb\u5f00\u2026\u2026\u201d\u76ee\u6ce8\u738b\u91cd\u5c71\u53c8\u9053\uff1a\u201c\u5929\u4eae\u4e4b\u524d\uff0c\u5728\u4e0b\u4e0e \u8bf8\u4f4d\u751f\u6b7b\u4e0e\u5171\uff0c\u5929\u4e00\u4eae\uff0c\u54b1\u4eec\u5c31\u6069\u4e49\u4e24\u6e05\u3002\u201d \u3000\u3000\u201c\u7167\u8ba1\u5212\u7684\u65f6\u95f4\uff0c\u4ed6\u4eec\u5e94\u8be5\u5728\u5929\u4eae\u4e4b\u540e\uff0c\u8d76\u5230\u5f00\u5c01\u2026\u2026\u201d\u738b\u5f64\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u96be\u514d\u6709\u4e9b\u6f0f \u5931\uff0c\u6240\u4ee5\uff0c\u5e94\u8be5\u4ee5\u5348\u65f6\u8ba1\u7b97\u3002\u201d \u3000\u3000\u201c\u90a3\u662f\u8bf4\u5348\u65f6\u4e4b\u524d\uff0c\u4f60\u4eec\u7684\u63f4\u5175\u4e00\u5b9a\u4f1a\u8d76\u5230\u76f8\u56fd\u5bfa\u4e86\uff1f\u201d \u3000\u3000\u201c\u8fd9\u8981\u4e00\u5207\u987a\u5229\u624d\u884c\u3002\u201d\u738b\u5f64\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u4e8b\u5b9e\u4e0a\uff0c\u6211\u65e0\u6cd5\u8bc1\u5b9e\uff0c\u4ed6\u4eec\u662f\u5426\u5df2\u7ecf\u6536 \u5230\u4e86\u6211\u4eec\u7684\u6c42\u63f4\u4fe1\u53f7\u3002\u201d \u3000\u3000\u5fc3\u5370\u5927\u5e08\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u4f60\u7684\u529e\u6cd5\u662f\u884c\u4e0d\u901a\u4e86\u3002\u201d \u3000\u3000\u201c\u5bf9\uff0c\u4f60\u8001\u548c\u5c1a\u5b9a\u6709\u9ad8\u660e\u4e4b\u6cd5\uff0c\u624d\u628a\u6211\u4eec\u5f15\u6765\u6b64\u5730\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u4f60\u662f\u5403\u5b9a\u8001\u6cb9\u4e86\u3002\u201d \u3000\u3000\u738b\u5f64\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5728\u573a\u7684\u4eba\uff0c\u77e5\u9053\u4f60\u8fc7\u53bb\u5728\u6c5f\u6e56\u4e0a\u4e4b\u5229\u5bb3\u624b\u6bb5\u7684\u4eba\uff0c\u53ea\u6709\u6211\u738b\u67d0\u4e00 \u4e2a\uff0c\u8c01\u4e0d\u77e5\u4f60\u4ece\u4e0d\u505a\u5192\u9669\u4e4b\u4e8b\u2026\u2026\u201d \u3000\u3000\u201c\u54fc\uff01\u90a3\u5df2\u7ecf\u662f\u51e0\u5341\u5e74\u524d\u7684\u4e8b\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u4f46\u672c\u6027\u96be\u79fb\uff0c\u6211\u60f3\u4f60\u8001\u548c\u5c1a\u8fd9\u4efd\u7a33\u5065\u7684\u6027\u683c\uff0c\u4ecd\u7136\u4e0d\u4f1a\u6539\u53d8\u3002\u201d \u3000\u3000\u201c\u5982\u679c\uff0c\u4ed6\u4eec\u628a\u706b\u7403\u6d78\u6cb9\uff0c\u52a0\u4e0a\u5e72\u67f4\uff0c\u706b\u70e7\u8fd9\u5ea7\u5927\u5385\uff0c\u8bf8\u4f4d\u6709\u4f55\u826f\u7b56\uff1f\u201d \u3000\u3000\u201c\u820d\u547d\u7a81\u56f4\u4e00\u9014\u3002\u201d\u5200\u516c\u5b50\u9053\uff1a\u201c\u4f46\u8981\u706b\u52bf\u70e7\u5f00\u4e4b\u524d\u624d\u6709\u5e0c\u671b\u3002\u201d \u3000\u3000\u201c\u4f60\u8ba4\u4e3a\u6709\u51e0\u6210\u9003\u8131\u7684\u673a\u4f1a\uff1f\u201d \u3000\u3000\u201c\u859b\u67d0\u6709\u4e03\u6210\u7684\u628a\u63e1\uff0c\u522b\u4eba\u6211\u5c31\u4e0d\u6e05\u695a\u4e86\u3002\u201d \u3000\u3000\u201c\u8001\u548c\u5c1a\uff01\u201d\u738b\u5f64\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u7528\u5356\u5173\u5b50\u4e86\uff1f\u5982\u4f55\u79bb\u5f00\u6b64\u5730\uff0c\u8bf7\u660e\u8bf4\u51fa\u6765\u5427\uff01\u201d \u3000\u3000\u5fc3\u5370\u5927\u5e08\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u5982\u80fd\u6fc0\u8d77\u4ed6\u4eec\u653e\u706b\uff0c\u8001\u8896\u5c31\u6709\u79bb\u5f00\u8fd9\u91cc\u7684\u529e\u6cd5\u4e86\u3002\u201d \u3000\u3000\u5200\u516c\u5b50\u9053\uff1a\u201c\u5f00\u73a9\u7b11\u554a\uff01\u8fd9\u5ea7\u77f3\u5ba4\u4e0d\u8fc7\u6570\u5341\u5c3a\u65b9\u5706\u5927\u5c0f\uff0c\u706b\u52bf\u4e00\u8d77\uff0c\u56db\u5468\u5374\u88ab\u5c01\u4f4f\uff0c\u5982 \u4f55\u8d70\u6cd5\uff1f\u96be\u9053\u5927\u5e08\u5df2\u6709\u706b\u9041\u672c\u9886\u3002\u201d \u3000\u3000\u201c\u706b\u52bf\u71c3\u8d77\uff0c\u4e5f\u53ef\u4ee5\u63a9\u853d\u4ed6\u4eec\u7684\u8033\u76ee\u2026\u2026\u201d \u3000\u3000\u7a81\u7136\uff0c\u95ed\u4e0a\u4e86\u53cc\u76ee\uff0c\u4e0d\u518d\u591a\u8a00\u3002 \u3000\u3000\u738b\u5f64\u6c89\u541f\u4e00\u9635\uff0c\u7a81\u7136\u4e5f\u6709\u6240\u609f\u3002 \u3000\u3000\u53ea\u542c\u4ed6\u5927\u58f0\u9053\uff1a\u201c\u5fd7\u4e0d\u540c\u4e0d\u76f8\u4e3a\u8c0b\uff0c\u4f60\u8001\u548c\u5c1a\u8fd9\u529e\u6cd5\uff0c\u54b1\u4eec\u4e0d\u80fd\u63a5\u53d7\uff0c\u738b\u67d0\u4eba\u5c31\u4e0d\u4fe1\uff0c \u5c31\u90a3\u4e48\u4e00\u4e24\u767e\u4eba\u771f\u80fd\u628a\u6211\u56f0\u4f4f\u3002\u201d \u3000\u3000\u7a81\u7136\uff0c\u4f0f\u8eab\u6361\u8d77\u4e00\u628a\u94a2\u5200\uff0c\u5411\u524d\u51b2\u53bb\u3002 \u5367\u9f99\u751f\u300a\u71d5\u5b50\u4f20\u5947\u300b \u7b2c \u4e03 \u56de\u3000\u9669\u6076\u9634\u8c0b \u3000\u3000\u9c7c\u5316\u9f99\u4ece\u5c0f\u5728\u4e00\u5ea7\u5bfa\u9662\u4e2d\u957f\u5927\uff0c\u5341\u4e8c\u5c81\u624d\u88ab\u5e08\u7236\u5e26\u8d70\u5b66\u827a\uff0c\u6240\u4ee5\uff0c\u5bf9\u548c\u5c1a\u6709\u4e00\u4efd\u7279\u522b\u7684 \u5c0a\u91cd\uff0c\u4f46\u7b54\u5e94\u4e86\u5fc3\u5370\u5927\u5e08\u4e4b\u540e\uff0c\u6df1\u89c9\u4e0d\u5bf9\uff0c\u5374\u4e5f\u4e0d\u4fbf\u53cd\u6094\u3002 \u3000\u3000\u6b64\u65f6\uff0c\u5ffd\u89c1\u738b\u5f64\u5411\u524d\u51b2\u6765\uff0c\u6b63\u5408\u5fc3\u610f\uff0c\u7acb\u523b\u6325\u5200\u8fce\u4e0a\u3002 \u3000\u3000\u738b\u5f64\u53f3\u624b\u6267\u5200\uff0c\u5bf9\u6321\u4e24\u4e2a\u51b2\u4e0a\u6765\u7684\u9ed1\u8863\u4eba\uff0c\u5de6\u624b\u75be\u62ac\uff0c\u8fce\u5411\u65a9\u9a6c\u5200\u3002 \u3000\u3000\u9c7c\u5316\u9f99\u5200\u52bf\u6c89\u91cd\uff0c\u738b\u5f64\u63a5\u4e0b\u4e00\u5200\uff0c\u6574\u4e2a\u5de6\u81c2\u90fd\u9ebb\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4f46\u90a3\u5f3a\u70c8\u7684\u649e\u51fb\uff0c\u4e5f\u9707\u52a8\u4e86\u673a\u7c27\uff0c\u4e24\u679a\u94f6\u9488\u75be\u5c04\u800c\u51fa\u3002 \u3000\u3000\u4efb\u662f\u9c7c\u5316\u9f99\u6b66\u529f\u9ad8\u5f3a\uff0c\u4f46\u5728\u8fd9\u7b49\u8fd1\u8ddd\u79bb\u5185\uff0c\u53c8\u5168\u65e0\u9632\u5907\u4e4b\u4e0b\uff0c\u4e24\u679a\u94f6\u9488\u4e00\u4e2d\u524d\u80f8\uff0c\u4e00\u4e2d \u53f3\u80a9\u3002 \u3000\u3000\u529b\u9053\u5f3a\u52b2\uff0c\u6df1\u5165\u7b4b\u9aa8\u3002 \u3000\u3000\u9c7c\u5316\u9f99\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u597d\u6b79\u6bd2\u7684\u6697\u5668\u3002\u201d \u3000\u3000\u738b\u5f64\u5927\u559d\u4e00\u58f0\uff0c\u4e00\u5200\u5288\u4e0b\uff0c\u9707\u843d\u4e86\u9c7c\u5316\u9f99\u624b\u4e2d\u7684\u65a9\u9a6c\u5200\u3002 \u3000\u3000\u624b\u6267\u647a\u6247\u7684\u84dd\u886b\u4eba\uff0c\u6025\u6025\u51b2\u4e0a\uff0c\u6276\u4f4f\u4e86\u9c7c\u5316\u9f99\uff0c\u9000\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4e24\u4e2a\u9ed1\u8863\u4eba\u5374\u820d\u6b7b\u5fd8\u751f\u7684\u51b2\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u738b\u5f64\u5df2\u5b58\u5fc3\u8981\u8bd5\u8bd5\u8fd9\u4e9b\u9ed1\u8863\u6b7b\u58eb\u7684\u5a01\u529b\uff0c\u4e00\u5200\u5feb\u65a9\uff0c\u8fce\u9762\u800c\u4e0b\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u8eab\u5b50\u4e00\u5f13\uff0c\u786c\u5411\u524d\u51b2\u4e86\u8fc7\u6765\uff0c\u624b\u4e2d\u5355\u5200\uff0c\u76f4\u6413\u5c0f\u8179\u3002 \u3000\u3000\u679c\u7136\u662f\u4e0d\u8981\u547d\u7684\u6253\u6cd5\u3002 \u3000\u3000\u738b\u5f64\u5de6\u811a\u98de\u8d77\uff0c\u51c6\u786e\u65e0\u6bd4\u7684\u8e22\u4e2d\u4e86\u9ed1\u8863\u4eba\u7684\u524d\u989d\uff0c\u8eab\u5b50\u4e5f\u540c\u65f6\u6a2a\u79fb\u534a\u6b65\u3002 \u3000\u3000\u5200\u950b\u63a0\u8fc7\u8eab\u4fa7\uff0c\u5212\u7834\u4e86\u8863\u886b\u3002 \u3000\u3000\u4f46\u90a3\u9ed1\u8863\u4eba\u88ab\u738b\u5f64\u4e00\u811a\u8e22\u98de\u8d77\u6765\uff0c\u5f53\u573a\u6bd9\u547d\u3002 \u3000\u3000\u7b2c\u4e8c\u4e2a\u9ed1\u8863\u4eba\u5df2\u76f4\u76f4\u7684\u51b2\u4e86\u4e0a\u6765\uff0c\u4e00\u5200\u76f4\u523a\u524d\u80f8\u3002 \u3000\u3000\u738b\u5f64\u53f3\u624b\u4e00\u7ffb\uff0c\u5200\u52bf\u4e0a\u64a9\u3002 \u3000\u3000\u60f3\u4e0d\u5230\u90a3\u9ed1\u8863\u4eba\u7adf\u7136\u4e0d\u80af\u8ba9\u907f\uff0c\u4e00\u6761\u53f3\u81c2\u88ab\u9f50\u8098\u5207\u65ad\u3002 \u3000\u3000\u4f46\u9ed1\u8863\u4eba\u7684\u5200\u52bf\u4e0d\u53d8\uff0c\u5e26\u7740\u4e00\u622a\u65ad\u81c2\uff0c\u5212\u8fc7\u738b\u5f64\u53f3\u808b\uff0c\u5272\u51fa\u4e00\u6761\u4e09\u5bf8\u591a\u957f\u7684\u53e3\u5b50\uff0c\u8840\u6d41 \u5982\u6ce8\u3002 \u3000\u3000\u8fd9\u7b49\u51f6\u72e0\u7684\u6253\u6cd5\uff0c\u4f7f\u738b\u5f64\u4e5f\u6709\u4e9b\u60ca\u6015\u4e86\uff0c\u5982\u679c\u518d\u6709\u4e24\u4e2a\u9ed1\u8863\u4eba\u653b\u4e0a\u6765\uff0c\u738b\u5f64\u81ea\u77e5\u65e0\u6cd5\u5e94 \u4ed8\u4e86\u3002 \u3000\u3000\u5c31\u5728\u738b\u5f64\u4e00\u6014\u795e\u95f4\uff0c\u90a3\u65ad\u53bb\u4e00\u81c2\u7684\u9ed1\u8863\u4eba\uff0c\u5ffd\u7136\u4e00\u5934\u649e\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u53cc\u65b9\u8fd1\u5728\u98d3\u5c3a\uff0c\u738b\u5f64\u8ba9\u907f\u5df2\u662f\u4e0d\u53ca\uff0c\u88ab\u90a3\u9ed1\u8863\u4eba\u4e00\u5934\u649e\u5728\u524d\u80f8\u4e0a\uff0c\u4eba\u4e5f\u88ab\u649e\u5f97\u5012\u98de\u800c \u9000\u3002 \u3000\u3000\u738b\u5f64\u5185\u529f\u7cbe\u6e5b\uff0c\u501f\u673a\u9000\u56de\u5385\u4e2d\u3002 \u3000\u3000\u4e24\u6247\u6c89\u91cd\u7684\u6728\u95e8\uff0c\u7a81\u7136\u63a9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u9c7c\u5316\u9f99\u53d7\u4f24\u4e4b\u540e\uff0c\u90a3\u84dd\u886b\u4eba\u4f3c\u5df2\u63a5\u4e0b\u4e86\u5927\u6743\uff0c\u9ad8\u58f0\u9053\uff1a\u201c\u5728\u4e0b\u4e0d\u4f3c\u9c7c\u603b\u5be8\u4e3b\u4ec1\u6148\uff0c\u5982\u82e5 \u8bf8\u4f4d\u4e0d\u80af\u9f50\u5200\u5c31\u7f1a\uff0c\u5728\u4e0b\u53ef\u8981\u4ee5\u706b\u653b\u4e86\u3002\u201d \u3000\u3000\u738b\u5f64\u4e00\u9762\u6577\u836f\uff0c\u4e00\u9762\u8bf4\u9053\uff1a\u201c\u90a3\u4e9b\u9ed1\u8863\u6b7b\u58eb\u679c\u7136\u5229\u5bb3\uff0c\u4e0d\u77e5\u4ed6\u4eec\u662f\u5982\u4f55\u8bad\u7ec3\u51fa\u6765\u7684\uff1f\u8001 \u548c\u5c1a\uff0c\u738b\u67d0\u5e78\u672a\u8fb1\u547d\uff0c\u4ed6\u4eec\u679c\u7136\u8981\u7528\u706b\u653b\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u8bf8\u4f4d\uff01\u8fd8\u662f\u5c0f\u5fc3\u4e00\u4e9b\uff0c\u4e5f\u8bb8\uff0c\u4ed6\u4eec\u5148\u8981\u8bd5\u653b\u4e00\u9635\uff0c\u518d\u7528\u706b\u653b\u3002\u201d \u3000\u3000\u738b\u91cd\u5c71\u3001\u5200\u516c\u5b50\u90fd\u88ab\u90a3\u9ed1\u8863\u6b66\u58eb\u4e0d\u8981\u547d\u7684\u6253\u6cd5\u7ed9\u9707\u4f4f\u4e86\uff0c\u90a3\u4e0d\u662f\u640f\u6740\u62fc\u547d\uff0c\u800c\u662f\u62ff\u6027\u547d \u4f5c\u5168\u529b\u7684\u4e00\u51fb\uff0c\u53ea\u8981\u4f24\u5230\u654c\u4eba\uff0c\u4ed6\u4eec\u5c31\u6b7b\u800c\u65e0\u61be\u4e86\u3002 \u3000\u3000\u6c5f\u6e56\u4e0a\u4ece\u6ca1\u6709\u8fd9\u6837\u7684\u6253\u6cd5\uff0c\u4e2d\u539f\u6b66\u6797\u4e2d\u4e5f\u6ca1\u6709\u8fd9\u6837\u7684\u6b66\u529f\u3002 \u3000\u3000\u4f46\u89c1\u706b\u5149\u95ea\u52a8\uff0c\u6d78\u6cb9\u7684\u706b\u7403\u7eb7\u7eb7\u5411\u5927\u5385\u6295\u6765\u3002 \u3000\u3000\u540c\u65f6\uff0c\u524d\u540e\u56db\u4e2a\u7a97\u5b50\u4e5f\u540c\u65f6\u51fb\u7834\uff0c\u706b\u7403\u6295\u5165\u4e86\u5927\u5385\u3002 \u3000\u3000\u5fc3\u5370\u5927\u5e08\u7985\u6756\u6325\u52a8\uff0c\u6311\u8d77\u706b\u7403\uff0c\u6295\u63b7\u7a97\u5916\u3002 \u3000\u3000\u738b\u91cd\u5c71\u3001\u9752\u8863\u4eba\u7528\u957f\u5251\u5c01\u95ed\u7a97\u53e3\u3002 \u3000\u3000\u539f\u6765\uff0c\u90a3\u677e\u6cb9\u6d78\u8fc7\u7684\u706b\u7403\uff0c\u71c3\u70e7\u4e4b\u529b\uff0c\u5341\u5206\u5f3a\u70c8\uff0c\u4e0d\u6613\u6251\u7184\uff0c\u5904\u7f6e\u4e0d\u5f53\uff0c\u5c31\u4f1a\u6563\u843d\u4e00 \u5730\uff0c\u5230\u5904\u662f\u706b\u3002 \u3000\u3000\u738b\u5f64\u5305\u597d\u4f24\u52bf\uff0c\u5411\u5916\u671b\u4e86\u4e00\u9635\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u8001\u548c\u5c1a\uff0c\u4ed6\u4eec\u5df2\u7ecf\u5728\u5ba4\u5916\u67b6\u8d77\u4e86\u6728\u67f4\uff0c\u5927 \u706b\u5df2\u8d77\uff0c\u5c31\u7b97\u662f\u5927\u7f57\u91d1\u4ed9\u4e5f\u662f\u65e0\u6cd5\u9003\u8fc7\u8fd9\u573a\u52ab\u96be\u4e86\u3002\u201d \u3000\u3000\u5fc3\u5370\u5927\u5e08\u9053\uff1a\u201c\u8001\u8896\u8fd9\u65b9\u5916\u4e4b\u4eba\uff0c\u88ab\u4f60\u62d6\u4e0b\u6c34\u6765\uff0c\u6211\u90fd\u4e0d\u62c5\u5fc3\u81ea\u5df1\u7684\u751f\u6b7b\uff0c\u4f60\u8fd8\u6709\u4ec0\u4e48 \u597d\u62c5\u5fc3\u7684\uff1f\u201d \u3000\u3000\u201c\u4e0d\u884c\u2026\u2026\u201d\u859b\u767e\u80dc\u51b7\u51b7\u5730\u9053\uff1a\u201c\u4e24\u4f4d\u53ef\u4ee5\u6b7b\uff0c\u5728\u4e0b\u4e07\u4e07\u4e0d\u80fd\u6b7b\uff0c\u6211\u8fd8\u6709\u5fc3\u613f\u672a\u507f\uff0c\u8fd9 \u6b21\u53ea\u597d\u53d8\u5366\u4e86\uff0c\u5982\u82e5\u4e24\u4f4d\u4eca\u591c\u80fd\u5f97\u4e0d\u6b7b\uff0c\u6211\u6b20\u4e24\u4f4d\u7684\u65f6\u95f4\u4e0d\u4f1a\u8d85\u8fc7\u4e09\u4e2a\u65f6\u8fb0\uff0c\u65e5\u540e\u52a0\u500d\u5949\u8fd8 \u5c31\u662f\u3002\u544a\u8f9e\u4e86\uff01\u201d \u3000\u3000\u5fc3\u5370\u5927\u5e08\u7985\u679d\u4e00\u6a2a\uff0c\u6321\u4f4f\u4e86\u53bb\u8def\uff0c\u9053\uff1a\u201c\u4f60\u7b97\u8fc7\u6ca1\u6709\uff1f\u51b2\u51fa\u53bb\uff0c\u751f\u79bb\u6b64\u5730\u7684\u673a\u4f1a\u6709\u591a \u5927\uff1f\u201d \u3000\u3000\u201c\u4e00\u534a\u4e00\u534a\u2026\u2026\u201d\u859b\u767e\u80dc\u9053\uff1a\u201c\u5728\u4e0b\u5982\u4e0d\u604b\u6218\uff0c\u9003\u751f\u7684\u673a\u4f1a\u5f88\u5927\uff0c\u4f55\u51b5\uff0c\u9c7c\u5316\u9f99\u53c8\u53d7\u4e86 \u4f24\uff0c\u65e0\u80fd\u518d\u6218\u3002\u201d \u3000\u3000\u201c\u90a3\u4e9b\u6b7b\u58eb\u2026\u2026\u201d\u5fc3\u5370\u5927\u5e08\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u4f46\u53d7\u8fc7\u4e25\u683c\u7684\u8bad\u7ec3\uff0c\u800c\u4e14\uff0c\u4f3c\u662f\u53d7\u7740\u4e00\u79cd\u795e \u79d8\u7684\u529b\u91cf\u6240\u63a7\u5236\uff0c\u5728\u672a\u5f97\u8bc1\u660e\u4e4b\u524d\uff0c\u6211\u4e0d\u6562\u65ad\u8a00\uff0c\u4ed6\u4eec\u662f\u88ab\u836f\u7269\u8fd8\u662f\u5176\u4ed6\u7684\u65b9\u6cd5\u6240\u5f71\u54cd\uff0c\u4f7f \u4ed6\u4eec\u4e00\u76f4\u4fdd\u6301\u4e86\u4e00\u79cd\u9ad8\u6602\u7684\u4ea2\u594b\uff0c\u6240\u4ee5\u80fd\u89c6\u6b7b\u5982\u5f52\u3002\u201d \u3000\u3000\u5200\u516c\u5b50\u5fae\u5fae\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u98ce\u96f7\u5200\u6cd5\u867d\u7136\u51a0\u7edd\u4e00\u65f6\uff0c\u4f46\u5728\u7cbe\u795e\u4ea2\u594b\u3001\u5168\u65e0\u754f\u60e7\u7684\u610f\u8bc6\u4e0b\uff0c\u5200\u6cd5\u7684\u5a01\u529b\u4f1a\u53d7\u5230\u5f88\u5927 \u7684\u635f\u5bb3\u3002\u201d\u5fc3\u5370\u5927\u5e08\u9053\uff1a\u201c\u5c31\u7b97\u4f60\u5200\u5200\u53d6\u80dc\uff0c\u53c8\u80fd\u6740\u5f97\u51e0\u4e2a\uff1f\u4ed6\u4eec\u4f1a\u627e\u51fa\u7834\u7efd\uff0c\u628a\u4f60\u5e72\u6389 \u7684\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u5f53\u4eca\u6b66\u6797\u4e4b\u4e16\uff0c\u6ca1\u6709\u4eba\u80fd\u591f\u5bf9\u4ed8\u4ed6\u4eec\u4e86\uff1f\u201d\u5200\u516c\u5b50\u867d\u7136\u53e3\u4e2d\u4e0d\u670d\uff0c\u4f46\u5df2\u4e0d \u518d\u575a\u6301\u79bb\u5f00\u5385\u5802\u7834\u56f4\u800c\u51fa\u7684\u8ba1\u5212\u4e86\u3002 \u3000\u3000\u201c\u90a3\u4e5f\u4e0d\u662f\uff0c\u5c11\u6797\u5bfa\u7684\u7f57\u6c49\u9635\u81f3\u5c11\u53ef\u4ee5\u6297\u62d2\u4f4f\u4ed6\u4eec\u653b\u52bf\uff0c\u4e94\u884c\u5251\u9635\u4e5f\u53ef\u4ee5\u548c\u4ed6\u4eec\u653e\u624b\u4e00 \u640f\uff0c\u81f3\u4e8e\u4e00\u4e2a\u4eba\uff0c\u6b66\u529f\u518d\u9ad8\uff0c\u53ea\u6015\u4e5f\u65e0\u6cd5\u6297\u62d2\u3002\u201d\u5fc3\u5370\u5927\u5e08\u65e0\u9650\u9eef\u7136\u5730\u9053\uff1a\u201c\u56e0\u4e3a\uff0c\u4ed6\u4eec\u4e0d \u6015\u6b7b\u4ea1\u3002\u201d \u3000\u3000\u4f46\u89c1\u706b\u5149\u5927\u8d77\uff0c\u5806\u5728\u5385\u5916\u7684\u5e72\u67f4\uff0c\u5df2\u88ab\u5f15\u71c3\u3002 \u3000\u3000\u5927\u5385\u5185\u5916\uff0c\u5df2\u88ab\u706b\u52bf\u9694\u7edd\u3002 \u3000\u3000\u5fc3\u5370\u5927\u5e08\u4f4e\u58f0\u9053\uff1a\u201c\u8ddf\u6211\u6765\uff01\u201d \u3000\u3000\u4ed6\u4eec\u8f6c\u5954\u5927\u5385\u4e00\u89d2\uff0c\u7adf\u7136\u5728\u58c1\u89d2\u5904\uff0c\u63ed\u5f00\u4e00\u5757\u65b9\u77f3\u3002 \u3000\u3000\u5728\u8fd9\u4e48\u4e00\u5ea7\u7981\u95ed\u72af\u8fc7\u50e7\u4fa3\u7684\u5730\u65b9\uff0c\u7adf\u7136\u6709\u4e00\u6761\u5730\u4e0b\u5bc6\u9053\uff0c\u771f\u662f\u5b8c\u5168\u51fa\u4eba\u610f\u5916\u7684\u8bbe\u65bd\uff0c\u592a \u7edd\u4e86\uff01\u201c \u3000\u3000\u5fc3\u5370\u5927\u5e08\u8981\u738b\u5f64\u7b49\u9c7c\u8d2f\u800c\u4e0b\uff0c\u81ea\u5df1\u7559\u5728\u6700\u540e\uff0c\u653e\u4e0b\u77f3\u677f\u3002 \u3000\u3000\u8fd9\u6761\u5bc6\u9053\u901a\u5230\u76f8\u56fd\u5bfa\u5916\u4e00\u5904\u5e72\u4e86\u7684\u67af\u4e95\u5185\uff0c\u4f46\u738b\u5f64\u8e0f\u51fa\u6d1e\u53e3\u65f6\uff0c\u7a81\u7136\u95fb\u5230\u4e86\u4e00\u80a1\u5f02\u5473\uff0c \u7acb\u523b\u6655\u8ff7\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u9192\u6765\u65f6\uff0c\u53d1\u89c9\u8eba\u5728\u4e00\u95f4\u96c5\u5ba4\u4e4b\u5185\u3002 \u3000\u3000\u4f46\u6700\u4f7f\u738b\u5f64\u60ca\u8bb6\u7684\u662f\uff0c\u5ba4\u4e2d\u4e09\u5f20\u5e76\u6392\u800c\u653e\u7684\u6728\u6905\u4e0a\uff0c\u5206\u5750\u7740\u71d5\u98de\u3001\u738b\u91cd\u5c71\u3001\u9a6c\u5de1\u629a\u4e09 \u4eba\u3002 \u3000\u3000\u4e09\u4eba\u5750\u5728\u4e00\u5904\uff0c\u5b9e\u662f\u4e00\u4ef6\u9707\u9a87\u4eba\u5fc3\u7684\u5927\u4e8b\uff0c\u4ee5\u738b\u5f64\u8fd9\u6837\u7a33\u6c89\u7684\u4eba\uff0c\u90fd\u4e3a\u4e4b\u5927\u5403\u4e00\u60ca\uff0c \u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u4ed6\u7a81\u7136\u633a\u8eab\u5750\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4eba\u662f\u5750\u8d77\u6765\u4e86\uff0c\u4f46\u7528\u529b\u592a\u5927\uff0c\u7adf\u7136\u63a7\u5236\u4e0d\u4f4f\uff0c\u8eab\u4e0d\u7531\u5df1\u7684\u51b2\u4e0b\u4e86\u6728\u5e8a\uff0c\u8dcc\u6454\u5728\u5730\u4e0a\u3002 \u3000\u3000\u738b\u5f64\u6014\u4f4f\u4e86\uff01 \u3000\u3000\u4ed6\u7f13\u7f13\u7684\u7ad9\u8d77\uff0c\u6697\u4e2d\u63d0\u805a\u771f\u6c14\uff0c\u53ea\u89c9\u6c14\u8840\u6ede\u6b62\uff0c\u7ecf\u8109\u9178\u75bc\uff0c\u5fc3\u77e5\u6b66\u529f\u5df2\u906d\u4eba\u5e9f\u53bb\uff0c\u9eef\u7136 \u4e00\u53f9\uff0c\u9053\uff1a\u201c\u9a6c\u6587\u4e2d\uff0c\u4f60\u597d\u72e0\u7684\u624b\u6bb5\uff0c\u5fc3\u5370\u5927\u5e08\u5462\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u90fd\u6d3b\u5f97\u5f88\u597d\uff0c\u53ea\u4e0d\u8fc7\u90fd\u548c\u738b\u7edf\u9886\u4e00\u6837\uff0c\u6682\u65f6\u5931\u53bb\u4e86\u6b66\u529f\u3002\u201d \u3000\u3000\u738b\u5f64\u76ee\u5149\u4e00\u63a0\u738b\u91cd\u5c71\u548c\u71d5\u98de\uff0c\u8fd8\u672a\u6765\u5f97\u53ca\u5f00\u53e3\uff0c\u4e24\u4eba\u5df2\u62a2\u5148\u9053\uff1a\u201c\u6211\u4eec\u548c\u7edf\u9886\u4e00\u6837\uff0c\u65e9 \u5df2\u65e0\u53cd\u6297\u4e4b\u80fd\u4e86\u3002\u201d \u3000\u3000\u201c\u9a6c\u5de1\u629a\uff0c\u4f60\u53ef\u4ee5\u52a8\u624b\u4e86\uff0c\u4e0d\u8fc7\uff0c\u6211\u76f8\u4fe1\u5185\u5bab\u7edf\u9886\u4e00\u5b9a\u4f1a\u627e\u4e0a\u4f60\u7684\u3002\u201d \u3000\u3000\u201c\u6211\u5982\u8981\u6740\u4f60\uff0c\u90a3\u5c31\u7528\u4e0d\u7740\u89c1\u4f60\u4e86\u3002\u201d\u9a6c\u5de1\u629a\u7b11\u9053\uff1a\u201c\u518d\u5949\u544a\u4e00\u4ef6\u4e8b\uff0c\u4f60\u7684\u90e8\u5c5e\u5df2\u7ecf\u5230 \u4e86\u5f00\u5c01\uff0c\u4e0d\u8fc7\uff0c\u6700\u597d\u662f\u4ed6\u4eec\u4e0d\u8981\u53d1\u89c9\u4ec0\u4e48\u7ebf\u7d22\u3002\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u5f00\u5c01\u5e9c\u867d\u7136\u805a\u96c6\u4e86\u4e00\u4e9b\u6c5f\u6e56\u597d\u624b\uff0c\u4f46\u4ed6\u4eec\u7edd\u5bf9\u65e0\u6cd5\u548c\u671d\u5ef7\u7684\u5927\u519b\u5bf9\u6297\u3002\u201d \u3000\u3000\u201c\u662f\u554a\uff01\u4e3a\u4e86\u9632\u5fae\u675c\u6e10\u2026\u2026\u201d\u9a6c\u5de1\u629a\u9053\uff1a\u201c\u5982\u82e5\u4ed6\u4eec\u53d1\u89c9\u4e86\u4ec0\u4e48\u4e0d\u5229\u4e8e\u672c\u629a\u7684\u7ebf\u7d22\uff0c\u90a3 \u5c31\u53ea\u597d\u628a\u4ed6\u4eec\u5168\u90fd\u7559\u4e0b\u6765\u4e86\u3002\u201d \u3000\u3000\u738b\u5f64\u6012\u9053\uff1a\u201c\u4f60\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u4e0d\u4f1a\u518d\u5fc3\u5b58\u4ec1\u6148\uff0c\u6211\u4f1a\u628a\u4ed6\u4eec\u5168\u90e8\u5904\u6b7b\uff0c\u5f53\u7136\uff0c\u4e5f\u5305\u62ec\u4f60\u738b\u7edf\u9886\u548c\u71d5\u603b\u6355\u5934\u5728\u5185 \u5462\uff01\u201d \u3000\u3000\u201c\u4f46\u6d88\u606f\u5df2\u6cc4\uff0c\u4f60\u5982\u60f3\u4e00\u624b\u906e\u5929\uff0c\u53ea\u6015\u662f\u5f88\u96be\u5982\u613f\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\uff0c\u6211\u4f5c\u4e86\u6700\u574f\u7684\u6253\u7b97\uff0c\u5927\u519b\u538b\u5883\u4e4b\u524d\uff0c\u6211\u4f1a\u79bb\u5f00\u8fd9\u91cc\u3002\u201d \u3000\u3000\u9a6c\u5de1\u629a\u957f\u957f\u53f9\u606f\u4e00\u58f0\uff0c\u6539\u53d8\u4e86\u8bdd\u9898\uff0c\u9053\uff1a\u201c\u6211\u6765\u89c1\u4f60\uff0c\u662f\u60f3\u544a\u8bc9\u4f60\u6700\u60f3\u77e5\u9053\u7684\u4e8b\uff0c\u7136 \u540e\uff0c\u8bf7\u4f60\u4f5c\u4e2a\u51b3\u5b9a\uff0c\u662f\u654c\u662f\u53cb\uff1f\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\u5462\uff1f\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u6211\u4eec\u8981\u63b3\u63a0\u4e86\u4e09\u516c\u4e3b\u2026\u2026\u201d \u3000\u3000\u201c\u4e09\u516c\u4e3b\u662f\u4e0d\u662f\u8fd8\u6d3b\u7740\uff1f\u2026\u2026\u201d\u738b\u5f64\u6025\u6025\u5730\u95ee\u3002 \u3000\u3000\u9a6c\u5de1\u629a\u7b11\u7b11\u9053\uff1a\u201c\u6211\u660e\u767d\uff0c\u4e09\u516c\u4e3b\u5982\u679c\u5df2\u7ecf\u6b7b\u4e86\uff0c\u54b1\u4eec\u4e5f\u6ca1\u6709\u4ec0\u4e48\u597d\u8c08\u7684\u4e86\uff0c\u6211\u80fd\u63a5\u89c1 \u738b\u5144\uff0c\u8fd9\u5c31\u8bf4\u660e\u4e86\u4e09\u516c\u4e3b\u6d3b\u5f97\u5f88\u597d\u3002\u201d \u3000\u3000\u201c\u4e09\u516c\u4e3b\u8fd8\u6d3b\u7740\u4e5f\u8bb8\u8fd8\u6709\u70b9\u597d\u8c08\uff0c\u9a6c\u5de1\u629a\u5c31\u8bf7\u8bf4\u5427\uff0c\u738b\u5f64\u6d17\u8033\u606d\u542c\u3002\u201d \u3000\u3000\u9a6c\u5de1\u629a\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u624d\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u5306\u5306\u7684\u79bb\u5f00\u4eac\u57ce\uff0c\u5c31\u662f\u6211\u4eec\u8bbe\u8ba1\u597d\u7684\u5708 \u5957\u2026\u2026\u201d \u3000\u3000\u201c\u80fd\u628a\u4e09\u516c\u4e3b\u5f15\u8bf1\u5f97\u5306\u5306\u4e0a\u8def\uff0c\u79bb\u5f00\u4eac\u57ce\uff0c\u5c31\u662f\u4e00\u4ef6\u975e\u540c\u5c0f\u53ef\u7684\u4e8b\uff0c\u5fc5\u9700\u8981\u6709\u6781\u5927\u7684\u667a \u6167\u624d\u80fd\u529e\u5230\uff0c\u738b\u67d0\u5c31\u60f3\u4e0d\u51fa\u8fd9\u79cd\u529e\u6cd5\u3002\u201d \u3000\u3000\u201c\u4e8b\u5b9e\u4e0a\uff0c\u4e0d\u662f\u5f88\u96be\uff0c\u738b\u5144\u56e0\u4e3a\u4e0d\u4e86\u89e3\u4e09\u516c\u4e3b\uff0c\u6240\u4ee5\u611f\u89c9\u5230\u65e0\u5904\u4e0b\u624b\uff0c\u6211\u4eec\u4e86\u89e3\u5979\uff0c\u77e5 \u9053\u5979\u6700\u5173\u5fc3\u7684\u4eba\u548c\u4e8b\uff0c\u53ea\u8981\u7565\u65bd\u5c0f\u8ba1\uff0c\u5c31\u4f7f\u5979\u614c\u6025\u7684\u4e0a\u8def\u4e86\u2026\u2026\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u5fae\u5fae\u5730\u7b11\u4e86\u7b11\uff0c\u9a6c\u5de1\u629a\u63a5\u7740\u53c8\u8bf4\uff1a\u201c\u4e09\u516c\u4e3b\u6b66\u529f\u7684\u7cbe\u6e5b\u3001\u9ad8\u660e\uff0c\u53ea\u6015\u4f60\u738b\u7edf \u9886\u4e5f\u65e0\u6cd5\u671b\u5176\u9879\u80cc\uff0c\u6240\u4ee5\uff0c\u6211\u4eec\u4e0d\u5f97\u4e0d\u7528\u70b9\u624b\u6bb5\u2026\u2026\uff02\u201d\u65e2\u662f\u5982\u6b64\uff0c\u4e3a\u4ec0\u4e48\u53c8\u8981\u91cd\u91d1\u793c\u8058\u6c5f \u5343\u91cc\uff0c\u8ffd\u67e5\u884c\u8e2a\u5462\uff1f\u201c \u3000\u3000\u201c\u8fd9\u7684\u786e\u662f\u4e00\u9879\u5931\u7b56\uff0c\u6c5f\u5343\u91cc\u7684\u8ffd\u8e2a\u4e4b\u80fd\uff0c\u5927\u5927\u7684\u51fa\u4e4e\u6211\u7684\u610f\u6599\u4e4b\u5916\u2026\u2026\uff02\u738b\u91cd\u5c71\u7a81\u7136 \u63a5\u9053\uff1a\u201d\u8fd9\u4e48\u8bf4\u6765\uff0c\u4e09\u516c\u4e3b\u771f\u7684\u5728\u8d75\u4e8c\u5824\u5e9c\u4e2d\u4e86\uff1f\u201c \u3000\u3000\u201c\u4e0d\u9519\uff0c\u4f60\u53ef\u80fd\u4e0d\u592a\u76f8\u4fe1\u5427\uff01\u201d\u9a6c\u5de1\u629a\u7b11\u9053\uff1a\u201c\u6c5f\u5343\u91cc\u5982\u4e0d\u662f\u60f3\u8ba9\u4f60\u4eec\u5fc3\u670d\u53e3\u670d\uff0c\u800c\u7a81 \u7136\u51b2\u5165\u5e9c\u4e2d\u6551\u4eba\uff0c\u672c\u629a\u8fd8\u771f\u6709\u4e9b\u63aa\u624b\u4e0d\u53ca\u5462\uff1f\u8bf4\u8d77\u6765\uff0c\u8fd8\u662f\u4f60\u738b\u4f8d\u536b\u5e2e\u4e86\u5927\u5fd9\u3002\u201d \u3000\u3000\u738b\u91cd\u5c71\u6697\u9053\u4e24\u58f0\u60ed\u6127\uff0c\u5782\u4e0b\u4e86\u5934\u3002 \u3000\u3000\u9a6c\u5de1\u629a\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u73b0\u5728\u6211\u4eec\u624b\u4e2d\uff0c\u548c\u4f60\u4eec\u4e00\u6837\uff0c\u5b8c\u5168\u4e27\u5931\u4e86\u6297\u62d2\u7684\u80fd\u529b\uff0c\u6211\u8981\u6740\u5979\uff0c \u53ea\u4e0d\u8fc7\u662f\u4e3e\u624b\u4e4b\u52b3\u3002\u201d \u3000\u3000\u201c\u8bf4\u6b63\u9898\u5427\uff01\u4f60\u4e0d\u6740\u4e09\u516c\u4e3b\uff0c\u771f\u6b63\u7684\u76ee\u7684\u4f55\u5728\uff1f\u201d \u3000\u3000\u201c\u59d3\u6731\u7684\u5df2\u7ecf\u5f97\u5230\u4e86\u5929\u4e0b\uff0c\u897f\u5317\u9633\u5173\u4e4b\u5916\uff0c\u9ec4\u6c99\u4e07\u91cc\uff0c\u90a3\u91cc\u4e0d\u9002\u4e2d\u539f\u519b\u58eb\u7684\u751f\u6d3b\uff0c\u571f\u5730 \u8d2b\u810a\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u9a7b\u624e\u5927\u519b\uff0c\u8fde\u5e74\u5f81\u6218\uff0c\u53cc\u65b9\u90fd\u6709\u7740\u5f88\u5927\u7684\u4f24\u4ea1\uff0c\u8499\u53e4\u52c7\u58eb\u867d\u7136\u64c5\u4e8e\u9a91\u6218\uff0c \u4f46\u65cf\u4eba\u4eba\u53e3\u7a00\u5c11\uff0c\u8fd9\u573a\u6218\u4e89\u518d\u6253\u4e0b\u53bb\uff0c\u5bf9\u53cc\u65b9\u90fd\u65e0\u597d\u5904\u3002\u201d\u9a6c\u6587\u4e2d\u9053\uff1a\u201c\u5982\u82e5\u53cc\u65b9\u7f62\u6218\u606f \u5175\uff0c\u671d\u5ef7\u64a4\u56de\u5c6f\u4f4f\u5728\u9633\u5173\u7684\u5927\u519b\uff0c\u8499\u53e4\u53ef\u6c57\u613f\u4e3a\u5c5e\u56fd\uff0c\u6c38\u4e0d\u518d\u4fb5\u72af\u5929\u671d\u3002\u201d \u3000\u3000\u201c\u4e8b\u5173\u8fb9\u9632\u5927\u8ba1\uff0c\u7687\u4e0a\u672a\u5fc5\u4f1a\u7b54\u5e94\u64a4\u519b\u3002\u201d \u3000\u3000\u201c\u7687\u4e0a\u4e0d\u653e\u5fc3\uff0c\u53ef\u4ee5\u5c6f\u5175\u5728\u5609\u5cea\u5173\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u662f\u8bf4\uff0c\u8981\u7687\u4e0a\u8ba9\u51fa\u4e00\u5927\u7247\u7684\u571f\u5730\u7ed9\u4f60\u4eec\u4e86\uff0c\u6c42\u548c\u7684\u662f\u8499\u53e4\u53ef\u6c57\uff0c\u53ef\u4e0d\u662f\u5927\u660e\u671d\u7684\u7687 \u5e1d\u3002\u201d \u3000\u3000\u201c\u738b\u5144\uff0c\u90a3\u7247\u571f\u5730\u867d\u7136\u5e7f\u5927\uff0c\u4f46\u5bf9\u4f60\u4eec\u6c49\u4eba\u662f\u6ca1\u6709\u7528\u5904\u7684\uff0c\u53ea\u9002\u5408\u7ef4\u543e\u5c14\u65cf\u7684\u6e38\u7267\u751f \u6d3b\uff0c\u4e2d\u539f\u9526\u7ee3\u6c5f\u5c71\uff0c\u56fe\u5e45\u8fbd\u9614\uff0c\u5230\u5904\u7269\u4ea7\u4e30\u9976\uff0c\u5f3a\u5360\u4f4f\u90a3\u7247\u6c99\u6f20\uff0c\u6d6a\u8d39\u4e86\u4e09\u5341\u4e07\u5927\u519b\u2026\u2026\u201d \u9a6c\u6587\u4e2d\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u8fd9\u4e48\u529e\u5427\uff01\u90a3\u7247\u7586\u571f\u4ecd\u4e3a\u5929\u671d\u7687\u4e0a\u6240\u6709\uff0c\u6211\u4eec\u6bcf\u5e74\u5949\u4e0a\u725b\u3001\u7f8a\u4e07 \u5934\uff0c\u6218\u9a6c\u4e24\u5343\u5339\uff0c\u7b97\u4f5c\u79df\u7528\u4e4b\u8d44\uff0c\u9a6c\u67d0\u5728\u5929\u671d\u4f5c\u5b98\u8fd1\u4e8c\u5341\u5e74\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u6211\u4e5f\u6709\u4e00\u4e9b\u5411\u5f80\u5929\u671d \u7684\u793c\u4e49\u4e4b\u5e2e\u3002\u201d \u3000\u3000\u738b\u5f64\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u53ea\u662f\u4e00\u4e2a\u5185\u5bab\u4f8d\u536b\u7edf\u9886\uff0c\u8c08\u8fd9\u7b49\u519b\u56fd\u5927\u4e8b\uff0c\u672a\u514d\u8fc7\u4efd\u592a\u591a\u592a\u591a \u4e86\u5427\uff1f\u201d \u3000\u3000\u201c\u5f53\u4eca\u7687\u4e0a\uff0c\u82f1\u660e\u679c\u65ad\uff0c\u6027\u60c5\u521a\u70c8\uff0c\u671d\u4e2d\u5927\u81e3\uff0c\u656c\u754f\u5982\u864e\uff0c\u6ca1\u6709\u4eba\u6562\u548c\u4ed6\u8c08\u8fd9\u4e9b\u4e8b\u60c5\uff0c \u738b\u5144\u8eab\u5c45\u5185\u5bab\uff0c\u65e5\u5915\u548c\u7687\u4e0a\u76f8\u89c1\uff0c\u5c31\u5728\u4e0b\u6240\u77e5\uff0c\u4ed6\u5bf9\u738b\u5144\u975e\u5e38\u4fe1\u8d56\u501a\u91cd\uff0c\u662f\u65b9\u4fbf\u548c\u4ed6\u8bf4\u8bdd\u7684 \u4eba\u9009\u3002\u201d \u3000\u3000\u201c\u5929\u5a01\u96be\u6d4b\u554a\uff01\u2026\u2026\u201d\u738b\u5f64\u9053\uff1a\u201c\u4e00\u65e6\u89e6\u6012\u5723\u4e0a\uff0c\u90a3\u53ef\u662f\u6ee1\u65cf\u8bdb\u7edd\u7684\u5927\u7f6a\u5440\uff01\u201d \u3000\u3000\u201c\u6240\u4ee5\uff0c\u6211\u4eec\u63b3\u6765\u4e86\u4e09\u516c\u4e3b\uff0c\u7687\u4e0a\u6700\u559c\u6b22\u7684\u5973\u513f\uff0c\u738b\u5144\u5c45\u4e2d\u4f20\u8bdd\uff0c\u603b\u4e0d\u81f3\u4e8e\u89e6\u6012\u5929\u5a01 \u5427\uff1f\u201d \u3000\u3000\u738b\u5f64\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u662f\u4e0d\u662f\u628a\u4e09\u516c\u4e3b\u4ea4\u7531\u5728\u4e0b\u5e26\u56de\u4eac\u57ce\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e00\u70b9\uff0c\u738b\u5144\u4f1a\u5931\u671b\u7684\u3002\u201d\u9a6c\u6587\u4e2d\u51b7\u51b7\u5730\u9053\uff1a\u201c\u6628\u591c\u4e00\u6218\u5440\uff0c\u738b\u5144\u5927\u6982\u4e5f\u770b\u5230\u4e86\u6211\u4eec \u7684\u5b9e\u529b\u4e86\u3002\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u8bf4\u5230\u6b64\u5904\uff0c\u6211\u5012\u8981\u8bf7\u6559\u4e00\u4e8b\uff0c\u4f60\u9a6c\u5144\u7684\u8eab\u4efd\u662f\u8499\u53e4\u53ef\u6c57\u624b\u4e0b\u7684\u91cd\u81e3\u5462\uff1f\u8fd8\u662f \u897f\u65b9\u9b54\u6559\u4e2d\u4eba\uff1f\u201d \u3000\u3000\u201c\u5e94\u8bf4\u4e24\u4e2a\u90fd\u662f\uff0c\u6211\u51fa\u8eab\u5c0f\u897f\u5929\u96f7\u97f3\u5bfa\u2026\u2026\u201d\u9a6c\u6587\u4e2d\u9053\uff1a\u201c\u4f46\u5f53\u4eca\u7684\u8499\u53e4\u53ef\u6c57\u53c8\u662f\u6211\u7684 \u65cf\u5144\uff0c\u4ed6\u4e5f\u662f\u96f7\u97f3\u5bfa\u7684\u4e3b\u6301\uff0c\u4e5f\u5c31\u662f\u6211\u7684\u5e08\u7236\uff0c\u4ea4\u5f80\u5f88\u6df1\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u73b0\u4eca\u7684\u8499\u53e4\u53ef\u6c57\u4e5f\u662f\u9b54\u6559\u4e2d\u5f1f\u5b50\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u3002\u81ea\u4ece\u592a\u7956\u94c1\u6728\u771f\u548c\u4e18\u5904\u673a\u9053\u957f\u7ed3\u8bc6\uff0c\u62dc\u4e3a\u56fd\u5e08\u4ee5\u6765\uff0c\u96f7\u97f3\u5bfa\u5c31\u672a\u518d\u4ecb\u5165\u8499\u53e4\u56fd \u4e8b\uff0c\u5728\u4e0b\u6b64\u756a\u4e1c\u6765\uff0c\u53ea\u662f\u60f3\u6d88\u9664\u6b64\u573a\u7ef5\u5ef6\u4e0d\u65ad\u7684\u6740\u622e\u3002\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u987a\u4fbf\u628a\u9b54\u6559\u7684\u52bf\u529b\uff0c\u518d\u5e26\u4eba\u4e2d\u539f\u6b66\u6797\u4e2d\u6765\u2026\u2026\u201d \u3000\u3000\u201c\u5b8c\u5168\u6ca1\u6709\u8fd9\u4e2a\u6253\u7b97\u2026\u2026\u201d \u3000\u3000\u201c\u4f46\u4f60\u5047\u516c\u6d4e\u79c1\uff0c\u5728\u5f00\u5c01\u5e9c\u79d8\u5bc6\u7684\u8bad\u7ec3\u4e86\u4e00\u6279\u6b7b\u58eb\u2026\u2026\u201d\u738b\u5f64\u9053\uff1a\u201c\u96be\u9053\u8fd9\u4e0d\u662f\u94c1\u7684\u8bc1 \u636e\u3002\u201d \u3000\u3000\u9a6c\u5de1\u629a\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u5982\u679c\u770b\u7684\u4ed4\u7ec6\uff0c\u5c31\u4f1a\u53d1\u89c9\uff0c\u4ed6\u4eec\u90fd\u662f\u8499\u53e4\u7684\u9752\u5e74\uff0c\u90a3\u662f\u6211\u4f5c\u6700 \u574f\u6253\u7b97\u7684\u8d44\u672c\uff0c\u5f53\u7136\uff0c\u6211\u4e5f\u7f57\u81f4\u4e86\u4e00\u6279\u4e2d\u539f\u6b66\u6797\u540c\u9053\uff0c\u4f46\u4e3a\u6570\u4e0d\u591a\uff0c\u539f\u56e0\u662f\u6709\u4e9b\u5730\u65b9\uff0c\u6765\u81ea \u5927\u6f20\u7684\u56de\u65cf\u5b50\u5f1f\u4e0d\u5b9c\u6e17\u5165\u5230\u5404\u4e2a\u5730\u65b9\uff0c\u6211\u9700\u8981\u7075\u654f\u7684\u8033\u76ee\uff0c\u5f53\u4eca\u7684\u56fd\u541b\uff0c\u79c9\u8d4b\u5f3a\u608d\uff0c\u6211\u53d1\u89c9 \u4e86\u8fd9\u4ef6\u4e8b\u4e4b\u540e\uff0c\u51c6\u5907\u4e0b\u624b\u628a\u4ed6\u523a\u6b7b\uff0c\u4f7f\u65b0\u541b\u5c31\u4f4d\uff0c\u53ea\u8981\u6709\u4e00\u4e2a\u7231\u597d\u548c\u5e73\u7684\u65b0\u541b\uff0c\u8fd9\u573a\u7ef5\u7ef5\u4e0d \u65ad\u7684\u6740\u622e\uff0c\u5c31\u53ef\u4ee5\u4e2d\u6b62\u4e0b\u6765\u4e86\uff0c\u4f46\u610f\u5916\u7684\u662f\u6211\u4eec\u53d1\u89c9\u4e86\u4e09\u516c\u4e3b\u2026\u2026\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u963b\u6b62\u4e86\u4f60\u4eec\u6697\u6740\u7684\u884c\u52a8\uff1f\u201d \u3000\u3000\u201c\u738b\u5144\u7edf\u9886\u5185\u5bab\u4f8d\u536b\uff0c\u7f57\u81f4\u4e86\u4e0d\u5c11\u4eba\u624d\u2026\u2026\u201d\u9a6c\u6587\u4e2d\u9053\uff1a\u201c\u4f46\u5c0f\u897f\u5929\u96f7\u97f3\u5bfa\u6d3e\u4e86\u5341\u540d\u6700 \u597d\u7684\u9b54\u6559\u9ad8\u624b\u7ed9\u6211\uff0c\u8bf4\u53e5\u4e0d\u6015\u4f60\u738b\u5144\u751f\u6c14\u7684\u8bdd\uff0c\u5c31\u51ed\u4f60\u90a3\u4e9b\u4f8d\u536b\uff0c\u662f\u65e0\u6cd5\u963b\u6321\u6211\u4eec\u7684\uff0c\u4f46\u4e09 \u516c\u4e3b\u5374\u6210\u4e86\u5185\u5bab\u7684\u5c4f\u969c\u3002\u201d \u3000\u3000\u201c\u4e09\u516c\u4e3b\u6709\u7740\u5f88\u9ad8\u660e\u7684\u6b66\u529f\uff0c\u4f60\u9a6c\u5de1\u629a\u53c8\u5982\u4f55\u77e5\u6653\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u4f60\u5fc3\u4e2d\u4e0d\u670d\u6c14\u2026\u2026\u201d\u9a6c\u6587\u4e2d\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u4eec\u4e5f\u6ca1\u6709\u60f3\u5230\uff0c\u76f4\u5230\u6211\u4eec\u4e09\u6b21\u5165\u5bab\u884c \u523a\u2026\u2026\u201d \u3000\u3000\u201c\u5728\u4e0b\u53d1\u73b0\u8fc7\u4e00\u6b21\uff0c\u8ffd\u5230\u793c\u90e8\u7684\u9a7f\u9986\u4e4b\u4e2d\uff0c\u90a3\u4e00\u6b21\u662f\u4f60\u9a6c\u5144\u5427\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u4f60\u53d1\u73b0\u4e86\u4e00\u6b21\uff0c\u4f46\u4e09\u516c\u4e3b\u5374\u51fb\u9000\u4e86\u6211\u4eec\u4e09\u6b21\u7684\u591c\u88ad\uff0c\u6740\u6b7b\u4e86\u6211\u4e09\u4f4d\u5e08\u5f1f\uff0c\u53e6\u5916 \u53c8\u4f24\u4e86\u6211\u4eec\u516d\u4e2a\u4eba\u2026\u2026\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u795e\u60c5\u51b7\u8083\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u8bf7\u738b\u5144\u6ce8\u610f\u7684\u662f\uff0c\u6211\u4eec\u6bcf\u6b21\u884c\u52a8\u90fd\u662f\u4e09\u4eba\u4e00\u7ec4\uff0c\u4e09\u6b21\u591c \u88ad\u53bb\u4e86\u4e5d\u4e2a\u4eba\uff0c\u4e09\u6b7b\u516d\u4f24\uff0c\u7ec8\u4e8e\u4f7f\u6211\u4eec\u53d1\u89c9\u4e86\u90a3\u79cd\u5251\u6cd5\uff0c\u6b63\u662f\u56db\u5341\u5e74\u524d\uff0c\u628a\u897f\u65b9\u9b54\u6559\u9010\u79bb\u4e2d \u539f\u7684\u5251\u6cd5\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u8bf4\u4e09\u516c\u4e3b\u2026\u2026\u201d\u738b\u5f64\u9707\u60ca\u83ab\u540d\u5730\u9053\uff1a\u201c\u7528\u7684\u662f\u2018\u56de\u8f6e\u5251\u2019\u6cd5\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\u90a3\u662f\u4ec0\u4e48\u5251\u6cd5\uff0c\u4f46\u5251\u51fa\u5fc5\u4e2d\uff0c\u4e0d\u6b7b\u4ea6\u4f24\uff0c\u8fd9\u5c31\u903c\u4f7f\u6211\u6539\u53d8\u65b9\u6cd5\uff0c\u6211\u82b1\u4e86\u4e09\u5e74 \u7684\u5de5\u592b\uff0c\u624d\u67e5\u51fa\u4e09\u516c\u4e3b\u6bcf\u5e74\u90fd\u5e26\u4e00\u4efd\u91cd\u793c\u5230\u6d1e\u5ead\u6e56\u53bb\uff0c\u5728\u4e00\u8258\u753b\u80aa\u4e0a\u548c\u4e00\u4f4d\u4e2d\u5e74\u5987\u4eba\u89c1\u9762\uff0c \u4e8e\u662f\uff0c\u6211\u4eec\u5b89\u6392\u4e86\u4e00\u4e2a\u8bf1\u9975\uff0c\u4e09\u516c\u4e3b\u679c\u7136\u4e0a\u5f53\uff0c\u5728\u5f00\u5c01\u5e9c\u7528\u836f\u7269\u628a\u5979\u5236\u670d\uff0c\u5f53\u7136\uff0c\u6211\u4eec\u82b1\u4e86 \u5f88\u591a\u7684\u65f6\u95f4\u548c\u91d1\u94b1\uff0c\u624d\u67e5\u51fa\u4e86\u8fd9\u4e2a\u79d8\u5bc6\u2026\u2026\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u957f\u957f\u5730\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u53c8\u9053\uff1a\u201c\u8be6\u60c5\u7ec6\u8282\u65e0\u5173\u91cd\u8981\uff0c\u91cd\u8981\u7684\u662f\u7687\u4e0a\u5931\u53bb\u4e09\u516c\u4e3b\u8fd9\u4e2a \u4fdd\u62a4\u7684\u529b\u91cf\uff0c\u5fc5\u7136\u4f1a\u5fc3\u4e2d\u7126\u8651\uff0c\u4f60\u8f6c\u8fbe\u6211\u4eec\u7684\u8981\u6c42\u65f6\uff0c\u4ed6\u4f1a\u8ba4\u771f\u7684\u8003\u8651\u8003\u8651\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u4e09\u516c \u4e3b\u4ecd\u5728\u6211\u4eec\u624b\u4e2d\u4f5c\u4e3a\u4eba\u8d28\uff0c\u5bf9\u738b\u5144\u7684\u5b89\u5168\u4e5f\u662f\u4e00\u5927\u4fdd\u969c\u3002\u201d \u3000\u3000\u201c\u5982\u82e5\u7687\u4e0a\u95ee\u8d77\u4e09\u516c\u4e3b\u5462\uff1f\u201d \u3000\u3000\u201c\u544a\u8bc9\u4ed6\uff0c\u628a\u5e38\u5927\u5c06\u519b\u7684\u5175\u9a6c\uff0c\u64a4\u5165\u5609\u5cea\u5173\u4e4b\u540e\uff0c\u53cc\u65b9\u4e0d\u80fd\u518d\u6709\u51b2\u7a81\uff0c\u6211\u4eec\u4f1a\u9075\u5b88\u8bfa \u8a00\uff0c\u5e74\u5e74\u732e\u4e0a\u725b\u3001\u7f8a\u3001\u9a6c\u5339\uff0c\u4e8c\u5e74\u540e\uff0c\u6211\u4eec\u4f1a\u628a\u4e09\u516c\u4e3b\u9001\u5165\u4eac\u57ce\u3002\u201d \u3000\u3000\u738b\u5f64\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u6211\u5982\u4e0d\u7b54\u5e94\u5462\uff1f\u9a6c\u5927\u4eba\u662f\u4e0d\u662f\u8981\u6740\u4e86\u6211\u4eec\u3002\u201d \u3000\u3000\u201c\u90a3\u5012\u4e0d\u4f1a\u2026\u2026\u201d\u9a6c\u6587\u4e2d\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9a6c\u67d0\u5728\u4f60\u4eec\u4e2d\u539f\u56fd\u571f\u4e0a\uff0c\u4f5c\u4e86\u5341\u51e0\u5e74\u7684\u5b98 \u4e86\uff0c\u5df2\u6df1\u901a\u4e3a\u5b98\u4e4b\u9053\uff0c\u4e2a\u4e2d\u9634\u9669\u673a\u8bc8\uff0c\u5f53\u771f\u662f\u66f2\u6298\u6709\u81f4\u2026\u2026\u201d \u3000\u3000\u4e00\u76f4\u6ca1\u6709\u5f00\u53e3\u7684\u71d5\u98de\uff0c\u7a81\u7136\u5f00\u4e86\u53e3\uff0c\u9053\uff1a\u201c\u5927\u4eba\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u8981\u5b89\u6392\u4e00\u573a\u51a4\u72f1\uff0c\u738b\u7edf\u9886\u3001\u71d5\u603b\u6355\u5934\uff0c\u90fd\u662f\u8fd9\u4ef6\u51a4\u72f1\u7684\u8981\u4eba\uff0c\u4f60\u4eec\u51fa\u5356\u4e86\u4e09\u516c\u4e3b\uff0c \u6240\u4ee5\u5979\u624d\u4f1a\u8eab\u9677\u532a\u7a9f\uff0c\u8bc1\u636e\u660e\u786e\uff0c\u4f60\u4eec\u53c8\u6709\u53e3\u96be\u8a00\u2026\u2026\u201d \u3000\u3000\u201c\u9a6c\u6587\u4e2d\u2026\u2026\u201d\u738b\u5f64\u6025\u6025\u9053\uff1a\u201c\u6709\u53e3\u96be\u8a00\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u201c\u4f60\u4eec\u4f1a\u53d8\u6210\u54d1\u5df4\u2026\u2026\u201d\u9a6c\u6587\u4e2d\u9053\uff1a\u201c\u4f60\u4eec\u65e0\u6cd5\u4e89\u8fa9\uff0c\u4e5f\u65e0\u6cd5\u6307\u51fa\u90a3\u4e9b\u8bc1\u636e\u53ef\u7591\u3002\u4f55 \u51b5\uff0c\u5c0f\u897f\u5929\u96f7\u97f3\u5bfa\u4e2d\u7684\u8ff7\u9b42\u5927\u6cd5\uff0c\u4e5f\u4f1a\u4f7f\u4f60\u4eec\u5728\u4e0d\u81ea\u4e3b\u7684\u60c5\u51b5\u4e0b\u62db\u4f9b\u3002\u201d \u3000\u3000\u201c\u8fd9\u662f\u706d\u65cf\u7684\u5927\u7f6a\u554a\uff01\u2026\u2026\u201d\u71d5\u98de\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u4e0d\u80fd\u8fd9\u4e48\u505a\uff0c\u4f60\u53ef\u4ee5\u6740\u4e86\u6211\u4eec\u706d\u53e3\uff0c \u4e0d\u80fd\u7275\u8fde\u65e0\u8f9c\u7684\u5987\u5b7a\u2026\u2026\u201d \u3000\u3000\u201c\u8bf4\u8d77\u5987\u5b7a\uff0c\u6211\u5012\u60f3\u8d77\u6765\u4e86\u2026\u2026\u201d\u9a6c\u6587\u4e2d\u95ee\u9053\uff1a\u201c\u5c0f\u71d5\u5b50\u548c\u4ed6\u5a18\uff0c\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u71d5\u98de\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u770b\u6837\u5b50\u4ed6\u5f88\u5728\u610f\u6b64\u4e8b\uff0c\u6211\u4e0d\u80fd\u8ba9\u4ed6\u627e\u51fa\u7834\u7efd\u3002\u5f53\u4e0b\u53f9\u606f\u4e00\u58f0\u9053\uff1a \u201c\u4ed6\u4eec\u88ab\u6c5f\u5343\u91cc\u5e26\u8d70\u4e86\u3002\u201d \u3000\u3000\u201c\u6c5f\u5343\u91cc\u8fd9\u53ea\u8001\u72d0\u72f8\u2026\u2026\u201d\u9a6c\u6587\u4e2d\u6447\u6447\u5934\u9053\uff1a\u201c\u679c\u7136\u662f\u6ed1\u6e9c\u5f97\u5f88\uff0c\u5e0c\u671b\u4ed6\u522b\u518d\u56de\u5230\u5f00\u5c01 \u5e9c\u6765\u2026\u2026\u201d \u3000\u3000\u4ed6\u6ca1\u6709\u8bf4\u51fa\u6c5f\u5343\u91cc\u518d\u56de\u5230\u5f00\u5c01\u5e9c\uff0c\u4ed6\u4f1a\u5982\u4f55\u5bf9\u4ed8\uff0c\u4f46\u4ed6\u76ee\u5149\u4e2d\u6d41\u9732\u51fa\u7684\u51f6\u72e0\u4e4b\u6c14\uff0c\u663e\u7136 \u5bf9\u6c5f\u5343\u91cc\u654c\u89c6\u6781\u6df1\u3002 \u3000\u3000\u9a6c\u6587\u4e2d\u76ee\u5149\u51dd\u6ce8\u5728\u738b\u5f64\u8138\u4e0a\uff0c\u770b\u4e86\u597d\u4e00\u9635\uff0c\u9053\uff1a\u201c\u5fc3\u5370\u5927\u5e08\u3001\u9ed1\u7f57\u6c49\u3001\u859b\u767e\u80dc\u7b49\uff0c\u4e5f\u90fd \u88ab\u56da\u62bc\u8d77\u6765\uff0c\u4f60\u5fc5\u987b\u5728\u4e24\u4e2a\u65f6\u8fb0\u5185\u6709\u6240\u51b3\u5b9a\uff0c\u73b0\u5728\uff0c\u6211\u7559\u4e0b\u71d5\u98de\u548c\u738b\u91cd\u5c71\u548c\u4f60\u5546\u91cf\uff0c\u4ed6\u4eec\u90fd \u662f\u8eab\u4efb\u516c\u804c\u7684\u4eba\uff0c\u5f53\u7136\u77e5\u9053\u5229\u5bb3\u7684\uff0c\u4e24\u4e2a\u65f6\u8fb0\u4e4b\u540e\uff0c\u6211\u6765\u542c\u4f60\u7684\u56de\u8bdd\u3002\u201d \u3000\u3000\u7ad9\u8d77\u8eab\u5b50\uff0c\u51fa\u5ba4\u800c\u53bb\u3002 \u3000\u3000\u738b\u5f64\u6ca1\u6709\u52a8\uff0c\u4ed6\u660e\u767d\u81ea\u5df1\u76ee\u524d\u8eab\u4f53\u7684\u72b6\u51b5\uff0c\u65e0\u6cd5\u548c\u4e00\u4e2a\u5f3a\u58ee\u7684\u519b\u5352\u5bf9\u6297\uff0c\u6709\u6240\u884c\u52a8\uff0c\u4e5f \u4e0d\u8fc7\u662f\u81ea\u53d6\u5176\u8fb1\u7f62\u4e86\u3002 \u3000\u3000\u4e24\u6247\u539a\u91cd\u7684\u6728\u95e8\uff0c\u7f13\u7f13\u7684\u5173\u4e0a\uff0c\u4f46\u7a97\u5916\u9633\u5149\u8000\u76ee\uff0c\u663e\u7136\uff0c\u8fd9\u53ea\u662f\u4e00\u4e2a\u666e\u901a\u7684\u623f\u820d\uff0c\u53ea\u4e0d \u8fc7\u5efa\u7b51\u5f97\u7a0d\u4e3a\u575a\u56fa\u4e00\u4e9b\u3002 \u3000\u3000\u201c\u71d5\u5144\uff0c\u80fd\u770b\u51fa\u8fd9\u662f\u4ec0\u4e48\u5730\u65b9\u4e48\uff1f\u201d\u738b\u5f64\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u54b1\u4eec\u79bb\u5f00\u6b64\u5730\u4e4b\u540e\uff0c\u6709\u591a\u5c11\u9003\u8d70 \u7684\u673a\u4f1a\uff1f\u201d \u3000\u3000\u71d5\u98de\u76ee\u5149\u8f6c\u52a8\uff0c\u56db\u987e\u4e86\u4e00\u773c\uff0c\u9053\uff1a\u201c\u597d\u50cf\u5728\u5f00\u5c01\u5e9c\u884c\u5185\uff0c\u8fd9\u5730\u65b9\u597d\u50cf\u662f\u5c39\u77e5\u5e9c\u7684\u516c\u9986\uff0c \u4e0d\u8fc7\uff0c\u4e0d\u51fa\u53bb\u770b\u4e00\u4e0b\uff0c\u65e0\u6cd5\u80af\u5b9a\u3002\u201d \u3000\u3000\u738b\u5f64\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u9a6c\u6587\u4e2d\u662f\u4e2a\u975e\u5e38\u8c28\u614e\u7684\u4eba\uff0c\u5982\u82e5\u6ca1\u6709\u628a\u63e1\uff0c\u6700\u597d\u4e0d\u8981\u5192\u9669\u3002\u201d \u3000\u3000\u71d5\u98de\u8d77\u8eab\u884c\u8fd1\u7a97\u53e3\uff0c\u63a8\u5f00\u7a97\u5b50\uff0c\u5934\u8fd8\u672a\u4f38\u51fa\u53bb\uff0c\u4e00\u628a\u660e\u4eae\u4eae\u7684\u94a2\u5200\uff0c\u5df2\u7136\u51fa\u73b0\u7a97\u5916\uff0c\u4e00 \u4e2a\u5168\u8eab\u9ed1\u8863\u7684\u6b66\u58eb\uff0c\u540c\u65f6\u73b0\u8eab\u3002 \u3000\u3000\u5200\u5149\u5728\u7a97\u5916\uff0c\u95ea\u52a8\u51fa\u4e00\u7247\u5bd2\u8292\uff0c\u628a\u6574\u6247\u7684\u7a97\u5b50\u90fd\u5c01\u4e86\u8d77\u6765\u3002 \u3000\u3000\u71d5\u98de\u5173\u4e0a\u4e86\u7a97\u95e8\uff0c\u90a3\u98de\u65cb\u7684\u5200\u5149\u624d\u6d88\u5931\u4e0d\u89c1\u3002 \u3000\u3000\u738b\u5f64\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u6211\u4eec\u5b8c\u5168\u6ca1\u6709\u9003\u8d70\u7684\u673a\u4f1a\uff01\u201d \u3000\u3000\u201c\u7edf\u9886\uff01\u73b0\u5728\u6211nl\u8be5\u4f5c\u4f55\u6253\u7b97\u5462\uff1f\u201d\u71d5\u98de\u9053\uff1a\u201c\u6211\u4eec\u5fc5\u987b\u5728\u4e24\u4e2a\u65f6\u8fb0\u5185\uff0c\u4f5c\u4e00\u4e2a\u51b3\u5b9a \u554a\uff01\u201d \u3000\u3000\u201c\u9a6c\u6587\u4e2d\u8bf4\u7684\u6b63\u662f\uff0c\u4e09\u5341\u4e07\u5927\u519b\u5e38\u9a7b\u5927\u6f20\uff0c\u5c55\u5f00\u4e86\u957f\u5e74\u640f\u6740\u3001\u5c60\u622e\uff0c\u5e76\u6ca1\u6709\u6d88\u706d\u4e86\u8499\u53e4 \u4eba\u7684\u62b5\u6297\uff0c\u53cd\u800c\u4fc3\u4f7f\u5317\u7586\u3001\u897f\u57df\u7684\u591a\u652f\u56de\u65cf\u8054\u6210\u4e00\u6c14\uff0c\u8499\u4eba\u897f\u79fb\uff0c\u501f\u5927\u6208\u58c1\u6c99\u6f20\u7684\u5929\u9669\uff0c\u4ee5 \u963b\u6b62\u5929\u671d\u5927\u519b\uff0c\u8fd9\u573a\u6218\u4e89\u4e0d\u5b9c\u518d\u6253\u4e0b\u53bb\u2026\u2026\u201d \u3000\u3000\u201c\u53d4\u53d4\u662f\u51c6\u5907\u4ee3\u4ed6\u4f20\u8bdd\u4e86\uff1f\u201d \u3000\u3000\u201c\u5bf9\uff01\u201d\u738b\u5f64\u9053\uff1a\u201c\u4ed6\u8bf4\u7684\u6709\u7406\uff0c\u95ee\u9898\u662f\u4ed6\u7528\u7684\u624b\u6bb5\u9519\u4e86\uff0c\u8fd9\u662f\u5a01\u80c1\uff0c\u7687\u4e0a\u795e\u5a01\u6b63\u9686\uff0c \u6211\u6015\u4ed6\u4e0d\u4f1a\u63a5\u53d7\u3002\u201d \u3000\u3000\u71d5\u98de\u9053\uff1a\u201c\u9a6c\u6587\u4e2d\u5fc3\u8ba1\u6df1\u6c89\uff0c\u6211\u60f3\u4ed6\u5fc5\u7136\u6709\u591a\u91cd\u7684\u5b89\u6392\uff0c\u71d5\u67d0\u4eba\u62c5\u5fc3\u7684\u662f\u5185\u5bab\u4e2d\u7684\u9632\u536b \u529b\u91cf\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u5012\u4e0d\u7528\u62c5\u5fc3\uff0c\u5185\u5b98\u4f8d\u536b\u52a0\u4e0a\u9526\u8863\u536b\uff0c\u6709\u4e00\u5343\u591a\u4eba\uff0c\u518d\u52a0\u5382\u536b\u5bc6\u63a2\uff0c\u4e94\u57ce\u5175\u9a6c\u53f8\u8f96\u4e0b \u7684\u4e24\u4e07\u7cbe\u5175\uff0c\u5c31\u7b97\u9a6c\u6587\u4e2d\u628a\u5f00\u5c01\u4e2d\u8bad\u7ec3\u7684\u4e94\u767e\u540d\u6b7b\u58eb\uff0c\u5168\u5e26\u5165\u5317\u4eac\uff0c\u4e5f\u96be\u5bf9\u7687\u4e0a\u6784\u6210\u5a01 \u80c1\u3002\u201d\u738b\u5f64\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u6015\u7684\u662f\u4ed6\u4eec\u6697\u4e2d\u884c\u4e8b\uff0c\u90a3\u5c31\u6709\u70b9\u9632\u4e0d\u80dc\u9632\u4e86\u3002\u201d \u3000\u3000\u201c\u4e09\u516c\u4e3b\u8eab\u5177\u7edd\u4e16\u6b66\u529f\u7684\u4e8b\uff0c\u4e8c\u53d4\u662f\u5426\u77e5\u9053\uff1f\u201d\u738b\u91cd\u5c71\u63d0\u51fa\u4e86\u5fc3\u4e2d\u4e4b\u7591\u3002 \u3000\u3000\u201c\u6211\u77e5\u9053\u4e09\u516c\u4e3b\u4f1a\u6b66\u529f\uff0c\u7687\u4e0a\u4e5f\u5e38\u548c\u4e09\u516c\u4e3b\u5728\u5fa1\u4e66\u623f\u4e2d\u5bc6\u8c08\u56fd\u4e8b\uff0c\u4f46\u9ad8\u660e\u5230\u5982\u6b64\u5883\u754c\uff0c \u5012\u662f\u51fa\u4e4e\u6211\u610f\u6599\u4e4b\u5916\u3002\u201d \u3000\u3000\u201c\u4e8c\u53d4\u7684\u610f\u601d\u662f\u2026\u2026\u201d\u738b\u91cd\u5c71\u95ee\u9053\uff1a\u201c\u9a6c\u6587\u4e2d\u5e76\u672a\u5938\u5f20\u3002\u201d \u3000\u3000\u201c\u5e94\u8be5\u4e0d\u4f1a\u2026\u2026\u201d\u738b\u5f64\u9053\uff1a\u201c\u867d\u7136\u6211\u6ca1\u6709\u548c\u9a6c\u6587\u4e2d\u52a8\u8fc7\u624b\uff0c\u4f46\u5c0f\u897f\u5929\u96f7\u97f3\u5bfa\u7684\u6b66\u529f\uff0c\u602a \u5f02\u51cc\u5389\uff0c\u5947\u5e7b\u83ab\u6d4b\uff0c\u56db\u5341\u5e74\u524d\uff0c\u51e0\u4e4e\u7edf\u6cbb\u4e86\u4e2d\u539f\u6b66\u6797\uff0c\u9a6c\u6587\u4e2d\u4e00\u6d3e\u6587\u96c5\uff0c\u5168\u65e0\u4f1a\u6b66\u529f\u7684\u6837 \u5b50\uff0c\u663e\u7136\u5df2\u5230\u6c14\u795e\u8fd8\u865a\u7684\u5883\u754c\uff0c\u4ee5\u5fc3\u5370\u5927\u5e08\u7cbe\u6df1\u7684\u5185\u529f\uff0c\u5374\u65e0\u6cd5\u89e3\u5f00\u4ed6\u622a\u8109\u624b\u6cd5\uff0c\u4f7f\u97e9\u6d9b\u5367 \u75c5\u5728\u5e8a\uff0c\u5f62\u540c\u5e9f\u4eba\u2026\u2026\u201d \u3000\u3000\u201c\u7edf\u9886\u4e5f\u77e5\u9053\u4e86\u97e9\u603b\u6355\u5934\u7684\u4e8b\uff1f\u201d\u71d5\u98de\u9053\uff1a\u201c\u771f\u662f\u60f3\u4e0d\u5230\u5440\uff0c\u4ed6\u5f53\u4e86\u56db\u5e74\u6cb3\u5357\u5de1\u629a\uff0c\u6211 \u4eec\u7adf\u4e0d\u77e5\u4ed6\u662f\u6b66\u6797\u9ad8\u624b\uff1f\u201d \u3000\u3000\u201c\u6240\u4ee5\uff0c\u4f60\u4eec\u624d\u80fd\u5b89\u7136\u65e0\u6059\uff0c\u97e9\u6d9b\u5f88\u53ef\u80fd\u5c31\u662f\u53d1\u73b0\u9a6c\u6587\u4e2d\u79d8\u5bc6\uff0c\u624d\u843d\u5f97\u5367\u5e8a\u4e0d\u8d77\u7684\u4e0b \u573a\u3002\u201d \u3000\u3000\u201c\u6709\u4e00\u70b9\uff0c\u5c0f\u4f84\u60f3\u4e0d\u901a\u2026\u2026\u201d\u738b\u91cd\u5c71\u56fd\u6ce8\u738b\u5f64\uff0c\u95ee\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u4e00\u8eab\u7edd\u4e16\u6b66\u529f\uff0c\u4e3a\u4ec0\u4e48 \u4ed6\u7684\u8d34\u8eab\u5bab\u5973\u60dc\u6625\u3001\u601c\u82b1\uff0c\u7adf\u7136\u662f\u5b8c\u5168\u4e0d\u4f1a\u6b66\u529f\uff1f\u7261\u4e39\u867d\u597d\uff0c\u8fd8\u8981\u7eff\u53f6\u966a\u886c\uff0c\u5982\u679c\u60dc\u6625\u3001\u601c \u82b1\u90fd\u4f1a\u6b66\u529f\uff0c\u4e5f\u8bb8\u4e09\u516c\u4e3b\u5c31\u4e0d\u4f1a\u90a3\u4e48\u5bb9\u6613\u88ab\u8ff7\u5012\u4e86\uff1f\u201d \u3000\u3000\u201c\u90a3\u4e5f\u4e0d\u6210\uff0c\u4ed6\u4eec\u7528\u7684\u8ff7\u836f\u592a\u5229\u5bb3\uff0c\u95fb\u5f97\u5c11\u8bb8\uff0c\u7acb\u523b\u6655\u5012\uff0c\u6240\u4ee5\u5fc3\u5370\u5927\u5e08\u867d\u7136\u5b89\u6392\u4e86\u9003 \u8d70\u7684\u79d8\u9053\uff0c\u6697\u6d3e\u9ed1\u7f57\u6c49\u9884\u4f5c\u63a5\u8fce\uff0c\u6211\u4eec\u8fde\u4e00\u62db\u90fd\u672a\u53d1\u51fa\uff0c\u5c31\u6655\u4e86\u8fc7\u53bb\uff0c\u6839\u672c\u4e0d\u77e5\u9053\u662f\u600e\u4e48\u88ab \u751f\u64d2\u7684\uff1f\u201d \u3000\u3000\u201c\u5509\uff01\u90a3\u662f\u2018\u4e00\u6b65\u5012\u2019\uff0c\u636e\u8bf4\u662f\u5deb\u5c71\u795e\u5973\u5cf0\u660e\u6708\u89c2\u6563\u82b1\u4ed9\u5b50\u914d\u5236\u7684\u72ec\u95e8\u8ff7\u9999\uff0c\u4e09\u516c\u4e3b\u4e5f \u662f\u4e2d\u8fd9\u79cd\u8ff7\u9999\u88ab\u64d2\u3002\u201d \u3000\u3000\u71d5\u98de\u8bf4\u51fa\u201c\u4e00\u6b65\u5012\u201d\u7684\u6765\u5386\u3002 \u3000\u3000\u201c\u73b0\u5728\uff0c\u4e8b\u60c5\u5df2\u7ecf\u5927\u81f4\u660e\u6717\u4e86\uff0c\u6700\u5927\u7684\u95ee\u9898\u662f\u4e09\u516c\u4e3b\u2026\u2026\u201d\u738b\u5f64\u9053\uff1a\u201c\u5982\u82e5\u9a6c\u6587\u4e2d\u80af\u4ea4 \u51fa\u4e09\u516c\u4e3b\uff0c\u4e8b\u60c5\u5c31\u591a\u51e0\u5206\u6210\u529f\u7684\u5e0c\u671b\u3002\u201d \u3000\u3000\u4f46\u95fb\u672f\u95e8\u5440\u7136\u800c\u5f00\uff0c\u56db\u4e2a\u5065\u7f8e\u7684\u5973\u5a62\u6367\u7740\u9152\u98df\uff0c\u7f13\u7f13\u7684\u884c\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u4f46\u4f7f\u738b\u5f64\u60ca\u8bb6\u7684\u662f\uff0c\u60dc\u6625\u3001\u601c\u82b1\u7adf\u7136\u968f\u5728\u56db\u5a62\u8eab\u540e\u800c\u5165\u3002 \u3000\u3000\u4e8c\u5a62\u8863\u5c65\u6574\u6d01\uff0c\u5bb9\u5149\u7115\u53d1\uff0c\u4f3c\u662f\u5b8c\u5168\u6ca1\u53d7\u59d4\u5c48\u3002 \u3000\u3000\u738b\u91cd\u5c71\u9053\uff1a\u201c\u4e24\u4f4d\u59d1\u5a18\u597d\u554a\uff01\u201d \u3000\u3000\u4f46\u4ed6\u5fc3\u4e2d\u5374\u6697\u6697\u5fd6\u9053\uff1a\u96be\u9053\u8fd9\u4e24\u4e2a\u4e2b\u5934\u4e5f\u65e9\u5df2\u88ab\u4eba\u4e70\u901a\uff0c\u662f\u9a6c\u6587\u4e2d\u5e03\u7f6e\u5728\u4e09\u516c\u4e3b\u8eab\u4fa7\u7684 \u4e00\u6b65\u68cb\u5b50\uff1f \u3000\u3000\u201c\u738b\u4f8d\u536b\u597d\uff01\u201d\u60dc\u6625\u5fae\u7b11\u5730\u8bf4\u9053\uff1a\u201c\u9a6c\u5de1\u629a\u544a\u8bc9\u6211\u4eec\uff0c\u4e09\u4f4d\u90fd\u53d7\u4e86\u5185\u4f24\uff0c\u6211\u4eec\u59d0\u59b9\u8981\u6c42 \u9a6c\u5927\u4eba\uff0c\u51c6\u6211\u4eec\u6765\u63a2\u671b\u4e09\u4f4d\u4e00\u6b21\u2026\u2026\u201d \u3000\u3000\u601c\u82b1\u9053\uff1a\u201c\u6211\u4eec\u5c31\u8981\u79bb\u5f00\u8fd9\u91cc\u4e86\uff0c\u4e5f\u662f\u6765\u5411\u7edf\u9886\u548c\u4f8d\u536b\u8f9e\u884c\u3002\u201d \u3000\u3000\u201c\u79bb\u5f00\u8fd9\u91cc\uff1f\u2026\u2026\u201d\u738b\u5f64\u5fc3\u5934\u4e00\u9707\uff0c\u9053\uff1a\u201c\u5230\u54ea\u91cc\u53bb\u5462\uff1f\u201d \u3000\u3000\u201c\u81ea\u7136\u662f\u8ddf\u968f\u516c\u4e3b\u4e86\u3002\u201d\u601c\u82b1\u9053\uff1a\u201c\u516c\u4e3b\u8981\u5230\u54ea\u91cc\uff0c\u6211\u4eec\u5c31\u8ddf\u5230\u54ea\u91cc\u3002\u201d \u3000\u3000\u201c\u4e8c\u4f4d\u59d1\u5a18\u89c1\u8fc7\u4e09\u516c\u4e3b\u4e86\uff1f\u201d\u738b\u5f64\u8ffd\u95ee\u4e86\u4e00\u53e5\uff1a\u201c\u4e09\u516c\u4e3b\u7684\u8eab\u4f53\u597d\u5427\uff1f\u201d \u3000\u3000\u201c\u4e09\u516c\u4e3b\u8eab\u4f53\u5f88\u597d\uff0c\u5bb9\u8272\u4f9d\u65e7\uff0c\u53ea\u662f\u770b\u4e0a\u53bb\u6709\u4e00\u4e9b\u61d2\u61d2\u7684\u3002\u201d \u3000\u3000\u201c\u61d2\u61d2\u7684\u2026\u2026\u201d\u738b\u5f64\u6709\u4e9b\u4e0d\u592a\u660e\u767d\uff0c\u9053\uff1a\u201c\u662f\u4e0d\u662f\u7cbe\u795e\u4e0d\u592a\u597d\uff1f\u201d \u3000\u3000\u201c\u4e5f\u4e0d\u662f\u5566\uff01\u201d\u60dc\u6625\u9053\uff1a\u201c\u6211\u60f3\u5c0f\u5a62\u4e3e\u4e2a\u4f8b\u5b50\uff0c\u738b\u7edf\u9886\u5c31\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u201c\u54e6\uff01\u8fd9\u8981\u8ba8\u6559\u4e86\uff01\u201d \u3000\u3000\u201c\u61d2\u61d2\u7684\u610f\u601d\uff0c\u597d\u50cf\u4e00\u4e2a\u521a\u521a\u7761\u9192\u8fc7\u6765\uff0c\u72b9\u5e26\u4e09\u5206\u60fa\u677e\u7761\u773c\u201d\u539f\u6765\u5982\u6b64\uff01\u201c\u738b\u5f64\u9053\uff1a\u201d \u662f\u5c31\u662f\u8bf4\uff0c\u4e09\u516c\u4e3b\u8fd8\u6ca1\u6709\u5341\u5206\u6e05\u9192\u3002\u201c \u3000\u3000\u60dc\u6625\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u795e\u5fd7\u5f88\u6e05\u9192\uff0c\u8bf4\u8bdd\u4e5f\u5f88\u6e05\u695a\u3002\u201d \u3000\u3000\u201c\u90a3\u2026\u2026\u4e09\u516c\u4e3b\u544a\u8bc9\u4f60\u4eec\u4e9b\u4ec0\u4e48\u8bdd\uff1f\u201d \u3000\u3000\u201c\u4eca\u5929\u665a\u4e0a\u79bb\u5f00\u6b64\u5730\u2026\u2026\u201d\u60dc\u6625\u9053\uff1a\u201c\u8981\u6211\u51c6\u5907\u4e00\u4e0b\u3002\u201d \u3000\u3000\u201c\u662f\u662f\u662f\u2026\u2026\u201d\u738b\u5f64\u5c3d\u91cf\u4f7f\u81ea\u5df1\u7684\u8bed\u6c14\u5e73\u9759\uff0c\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u53ef\u66fe\u5429\u5490\u51c6\u5907\u4e9b\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u201c\u8863\u670d\u884c\u56ca\u2026\u2026\u201d\u60dc\u6625\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u7684\u8863\u7269\u4fdd\u7ba1\u7684\u5f88\u5b8c\u6574\uff0c\u6240\u4ee5\u7528\u4e0d\u7740\u82b1\u65f6\u95f4\u53bb\u6574\u7406 \u5b83\uff0c\u6211\u4eec\u6709\u5f88\u591a\u7684\u65f6\u95f4\u7559\u5728\u8fd9\u91cc\u966a\u966a\u4f60\u4eec\u3002\u201d \u3000\u3000\u738b\u91cd\u5c71\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u8fd9\u4e2b\u5934\u4f3c\u4e4e\u662f\u6709\u4e9b\u53d8\u4e86\uff0c\u53d8\u5f97\u80c6\u5927\u800c\u4e14\u5f88\u4f1a\u8bf4\u8bdd\uff0c\u548c\u524d\u51e0\u5929\u90a3 \u79cd\u67d4\u5f31\u65e0\u52a9\u3001\u695a\u695a\u53ef\u601c\u7684\u6837\u5b50\uff0c\u5b8c\u5168\u4e0d\u540c\u2026\u2026\u3002 \u3000\u3000\u662f\u4e0d\u662f\u56e0\u4e3a\u89c1\u5230\u4e86\u4e09\u516c\u4e3b\uff0c\u5fc3\u4e2d\u7684\u5fe7\u8651\u5c3d\u53bb\uff0c\u6062\u590d\u4e86\u4e00\u4e2a\u5c11\u5973\u6d3b\u6cfc\u7684\u4e2a\u6027\uff0c\u738b\u91cd\u5c71\u5374\u4e0d \u6562\u65ad\u8a00\u3002 \u3000\u3000\u8fd9\u4e9b\u65f6\u65e5\u4e2d\uff0c\u8fde\u8fde\u6709\u610f\u5916\u4e4b\u53d8\uff0c\u4f7f\u738b\u91cd\u5c71\u611f\u89c9\u5230\uff0c\u5728\u6b66\u5f53\u5b66\u5251\u516b\u5e74\uff0c\u53ea\u4e0d\u8fc7\u662f\u521a\u521a\u6b65\u5165 \u5251\u672f\u4e4b\u95e8\uff0c\u8fd9\u4e00\u6b21\u79bb\u4eac\u6b65\u5165\u6c5f\u6e56\uff0c\u624d\u660e\u767d\u6c5f\u6e56\u4e0a\u7684\u9669\u6076\u3001\u8be1\u8bc8\uff0c\u95e8\u8def\u4e4b\u5e7f\uff0c\u53d8\u5316\u4e4b\u5947\uff0c\u5b8c\u5168 \u4e0d\u662f\u81ea\u5df1\u80fd\u591f\u5e94\u4ed8\u2026\u2026 \u3000\u3000\u4ed6\u5fc3\u4e2d\u7684\u4e00\u4e9b\u9a84\u72c2\u4e4b\u6c14\uff0c\u4e00\u626b\u800c\u5c3d\uff0c\u751a\u81f3\u53d8\u5f97\u5bf9\u81ea\u5df1\u5931\u53bb\u4fe1\u5fc3\u3002 \u3000\u3000\u5c3d\u7ba1\u4ed6\u5fc3\u4e2d\u6709\u5f88\u591a\u7591\u95ee\uff0c\u4f46\u5374\u5fcd\u4e86\u4e0b\u6765\uff0c\u6ca1\u6709\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u738b\u5f64\u5374\u7b11\u7b11\uff0c\u9053\uff1a\u201c\u60dc\u6625\u59d1\u5a18\uff0c\u4f60\u770b\u6211\u4eec\u80fd\u4e0d\u80fd\u89c1\u89c1\u4e09\u516c\u4e3b\uff1f\u201d \u3000\u3000\u201c\u73b0\u5728\u4e48\uff1f\u201d \u3000\u3000\u201c\u662f\u554a\uff01\u201d\u738b\u5f64\u9053\uff1a\u201c\u5728\u4e0b\u4e5f\u8be5\u5411\u4e09\u516c\u4e3b\u8bf7\u793a\u4e00\u4e0b\u4eca\u540e\u7684\u884c\u52a8\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u6211\u662f\u4e0d\u80fd\u4f5c\u4e3b\u7684\u3002\u201d\u60dc\u6625\u9053\uff1a\u201c\u8981\u5148\u5411\u4e09\u516c\u4e3b\u8bf7\u793a\u4e4b\u540e\uff0c\u624d\u80fd\u51b3\u5b9a\u3002\u201d \u3000\u3000\u201c\u662f\u4e0d\u662f\u8981\u5148\u5411\u9a6c\u5927\u4eba\u8bf7\u793a\u4e00\u4e0b\u5462\uff1f\u201d\u71d5\u98de\u9053\uff1a\u201c\u5305\u62ec\u4e09\u516c\u4e3b\u5728\u5185\uff0c\u76ee\u524d\u4f3c\u4e4e\u90fd\u5728\u9a6c\u5927 \u4eba\u7684\u7ba1\u5236\u4e0b\uff1f\u201d \u3000\u3000\u4ed6\u8bf4\u5f97\u975e\u5e38\u660e\u663e\uff0c\u5e0c\u671b\u80fd\u4ee5\u76f4\u63a5\u7684\u8bd5\u63a2\uff0c\u6d4b\u51fa\u60dc\u6625\u7684\u53cd\u5e94\u3002 \u3000\u3000\u60dc\u6625\u7a81\u7136\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u71d5\u603b\u6355\u5934\uff0c\u4f60\u7a76\u7adf\u60f3\u8bf4\u4ec0\u4e48\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u660e\u660e\u767d\u767d\u7684\u8bf4\u51fa\u6765 \u5462\uff1f\u201d \u3000\u3000\u738b\u5f64\u82e6\u7b11\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u597d\uff01\u54b1\u4eec\u5c31\u6253\u5f00\u5929\u7a97\u8bf4\u4eae\u8bdd\u5427\u3002\u4e09\u516c\u4e3b\u5927\u6982\u8fd8\u4e0d\u80fd\u81ea\u7531\u884c\u52a8\uff0c \u81f3\u4e8e\u6211\u4eec\u4e09\u4e2a\uff0c\u4e5f\u90fd\u88ab\u5236\u4e86\u7ecf\u7a74\uff0c\u8001\u5b9e\u8bf4\uff0c\u5b8c\u5168\u6ca1\u6709\u6297\u62d2\u7684\u80fd\u529b\uff0c\u6211\u4eec\u662f\u88ab\u56f0\u5728\u8fd9\u91cc\u7684\u3002\u201d \u3000\u3000\u601c\u82b1\u9053\uff1a\u201c\u4f60\u4eec\u2026\u2026\u4f60\u4eec\u2026\u2026\u770b\u4e0d\u51fa\u6709\u53d7\u4f24\u7684\u6837\u5b50\u554a\uff01\u201d \u3000\u3000\u201c\u738b\u7edf\u9886\u8bf4\u5f97\u5f88\u6e05\u695a\u4e86\uff0c\u4ed6\u4eec\u662f\u88ab\u4eba\u4f24\u4e86\u8109\u7a74\uff0c\u4e5f\u5c31\u662f\u53d7\u4e86\u5185\u4f24\u2026\u2026\u201d\u60dc\u6625\u9053\uff1a\u201c\u5916\u9762 \u81ea\u7136\u662f\u77a7\u4e0d\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u60dc\u6625\uff0c\u6211\u5e0c\u671b\u660e\u767d\u771f\u76f8\u3002\u201d\u738b\u5f64\u9053\uff1a\u201c\u4f60\u2026\u2026\u7a76\u7adf\u662f\u4e2a\u4ec0\u4e48\u8eab\u4efd\uff1f\u201d \u3000\u3000\u201c\u6211\u662f\u4e00\u4e2a\u5bab\u5973\u2026\u2026\u201d\u60dc\u6625\u795e\u60c5\u51b7\u5389\u5730\u9053\uff1a\u201c\u4e5f\u662f\u4e09\u516c\u4e3b\u7684\u4f8d\u5a62\uff0c\u53ea\u4e0d\u8fc7\uff0c\u6211\u6765\u7684\u5730\u65b9 \u4e0d\u592a\u9002\u5408\u800c\u5df2\u3002\u201d \u3000\u3000\u201c\u59d1\u5a18\u6765\u81ea\u4f55\u5904\uff1f\u53ef\u5426\u8bf4\u51fa\u6765\uff0c\u4ee5\u5e7f\u738b\u5f64\u7684\u89c1\u95fb\u5462\uff1f\u201d \u3000\u3000\u201c\u53ef\u4ee5\uff0c\u6211\u5982\u4e0d\u51c6\u5907\u6cc4\u6f0f\u8eab\u4efd\uff0c\u4e5f\u4e0d\u4f1a\u544a\u8bc9\u4f60\u4eec\u8fd9\u4e48\u591a\u4e86\u3002\u201d\u60dc\u6625\u9053\uff1a\u201c\u5c0f\u897f\u5929\u96f7\u97f3 \u5bfa\uff0c\u738b\u7edf\u9886\u5e94\u8be5\u660e\u767d\u4e86\u5427\uff01\u201d \u5367\u9f99\u751f\u300a\u71d5\u5b50\u4f20\u5947\u300b \u7b2c \u516b \u56de\u3000\u5c34\u5c2c\u4f7f\u8005 \u3000\u3000\u679c\u7136\uff0c\u5927\u51fa\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u738b\u5f64\u3001\u738b\u91cd\u5c71\u3001\u71d5\u98de\u4e09\u4eba\uff0c\u5168\u542c\u5f97\u6014\u4f4f\u4e86\uff01 \u3000\u3000\u4e09\u516c\u4e3b\u7684\u5fc3\u8179\u5973\u5a62\u4e4b\u4e00\uff0c\u7adf\u662f\u9b54\u6559\u4e2d\u6d3e\u4eba\u7687\u5bab\u7684\u5978\u7ec6\uff1f \u3000\u3000\u7687\u5bab\u5185\u82d1\u4e4b\u4e2d\uff0c\u7a76\u7adf\u8fd8\u6709\u591a\u5c11\u8fd9\u6837\u7684\u5978\u7ec6\u5462\uff1f \u3000\u3000\u601c\u82b1\u5462\uff1f\u662f\u4e0d\u662f\u4e5f\u662f\u9b54\u6559\u4e2d\u4eba\uff1f \u3000\u3000\u62ac\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u601c\u82b1\u5446\u5446\u7684\u7ad9\u5728\u90a3\u91cc\uff0c\u671b\u7740\u60dc\u6625\uff0c\u8138\u4e0a\u662f\u4e00\u7247\u832b\u832b\u7684\u795e\u60c5\uff0c\u4f3c\u662f\u542c\u5230\u4e86 \u5929\u4e0b\u6700\u5947\u602a\u7684\u4e00\u4ef6\u4e8b\u60c5\uff0c\u6709\u4e9b\u4e0d\u77e5\u6240\u63aa\uff1f \u3000\u3000\u201c\u60dc\u6625\uff0c\u4f60\u771f\u7684\u662f\u4ed6\u4eec\u6d3e\u6765\u7684\u4eba\u4e48\uff1f\u201d\u601c\u82b1\u9053\uff1a\u201c\u6211\u600e\u4e48\u4e00\u70b9\u4e5f\u770b\u4e0d\u51fa\u6765\u5462\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\uff01\u6211\u4e00\u76f4\u628a\u81ea\u5df1\u9690\u85cf\u5f97\u5f88\u597d\uff0c\u4ee5\u4e09\u516c\u4e3b\u7684\u7cbe\u660e\uff0c\u5c31\u6ca1\u6709\u53d1\u89c9\u3002\u201d\u60dc\u6625\u5fae\u7b11\u5982\u82b1\u5730 \u8bf4\u9053\uff1a\u201c\u6211\u5341\u4e09\u5c81\u88ab\u9009\u4e2d\u6d3e\u4eba\u7687\u5bab\uff0c\u4e00\u4e0b\u5b50\u4fbf\u6f5c\u4f0f\u4e86\u4e94\u5e74\u2026\u2026\u201d \u3000\u3000\u201c\u5509\uff01\u60dc\u6625\uff0c\u4f60\u8fd8\u53ef\u4ee5\u6f5c\u4f0f\u4e0b\u53bb\u7684\uff0c\u4e3a\u4ec0\u4e48\u8981\u81ea\u66dd\u8eab\u4efd\u5462\uff1f\u4e09\u516c\u4e3b\u6ca1\u6709\u6000\u7591\u4f60\uff0c\u6211\u4e5f\u6ca1 \u53d1\u89c9\u4ec0\u4e48\uff1f\u4f60\u2026\u2026\u4f60\u53ef\u4ee5\u6c38\u8fdc\u628a\u81ea\u5df1\u9690\u85cf\u8d77\u6765\u2026\u3002\u3002\uff0d\u201d \u3000\u3000\u201c\u6700\u540e\uff0c\u603b\u6709\u4e00\u5929\uff0c\u6211\u8981\u73b0\u9732\u51fa\u672c\u6765\u7684\u9762\u76ee\uff0c\u5bf9\u4e48\uff1f\u201d \u3000\u3000\u201c\u4e0d\uff01\u201d\u601c\u82b1\u56fa\u6267\u5730\u9053\uff1a\u201c\u4eba\u751f\u53ea\u4e0d\u8fc7\u77ed\u77ed\u6570\u5341\u5e74\u8fb0\u5149\uff0c\u4f60\u4e00\u76f4\u88c5\u4e0b\u53bb\uff0c\u76f4\u5230\u6b7b\u53bb\uff0c\u53c8 \u6709\u8c01\u4f1a\u77e5\u9053\u4f60\u662f\u4e2a\u6f5c\u5728\u7687\u5bab\u7684\u5978\u7ec6\u5462\uff1f\u201d \u3000\u3000\u201c\u601c\u82b1\uff0c\u6211\u77e5\u9053\u4f60\u5bf9\u6211\u4e00\u76f4\u5f88\u597d\uff0c\u6211\u4e5f\u5f88\u62b1\u6b49\u6b3a\u9a97\u4e86\u4f60\uff0c\u4e0d\u8fc7\uff0c\u53ea\u8981\u6211\u6709\u9009\u62e9\u7684\u4f59\u5730\uff0c \u6211\u7edd\u4e0d\u4f1a\u4f24\u5bb3\u4f60\u2026\u2026\u201d \u3000\u3000\u60dc\u6625\u4e0d\u7981\u9eef\u7136\u8d77\u6765\uff0c\u53c8\u9053\uff1a\u201c\u8fd9\u4e9b\u5e74\u6765\uff0c\u6211\u4eec\u76f8\u4e92\u7231\u60dc\uff0c\u60c5\u540c\u59d0\u59b9\uff0c\u6211\u771f\u7684\u4e0d\u613f\u610f\u4f24\u5bb3 \u5230\u4f60\uff0c\u53ef\u662f\uff0c\u6211\u4e0d\u80fd\u6297\u62d2\u5e08\u957f\u7684\u53ec\u5524\uff0c\u4e5f\u4e0d\u80fd\u5fd8\u53bb\u672c\u6765\u2026\u2026\u201d \u3000\u3000\u601c\u82b1\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u8fd9\u4e9b\u90fd\u662f\u65e0\u53ef\u5948\u4f55\u7684\u4e8b\u4e86\uff0c\u4e0d\u80fd\u602a\u4f60\u7684\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u5c31\u597d\uff0c\u601c\u82b1\uff0c\u4f60\u7eaf\u6d01\u7684\u50cf\u4e00\u5757\u767d\u7389\uff0c\u6211\u771f\u7684\u5f88\u5e0c\u671b\u6c38\u8fdc\u548c\u4f60\u76f8\u5904\u5728\u4e00\u8d77\u2026\u2026\u201d \u3000\u3000\u201c\u53ef\u80fd\u4e48\uff1f\u60dc\u6625\uff0c\u4f60\u4f1a\u4e0d\u4f1a\u6740\u4e86\u4e09\u516c\u4e3b\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u4f1a\uff0c\u53ea\u8981\u4e09\u516c\u4e3b\u80af\u548c\u6211\u4eec\u5408\u4f5c\uff0c\u5979\u4f1a\u53d7\u5230\u6700\u597d\u7684\u7167\u987e\uff0c\u800c\u4e14\uff0c\u4f60\u548c\u6211\u4e5f\u4f1a\u50cf\u8fc7\u53bb\u4e00 \u6837\uff0c\u4f3a\u5019\u5979\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\uff0c\u4e09\u516c\u4e3b\u7684\u813e\u6c14\uff0c\u4f1a\u5fcd\u53d7\u8fd9\u4e9b\u5c48\u8fb1\u4e48\uff1f\u201d\u601c\u82b1\u6d41\u7740\u6cea\u73e0\u513f\u95ee\u9053\uff1a\u201c\u5979\u5982\u679c\u53d1\u8d77\u4e86 \u516c\u4e3b\u7684\u813e\u6c14\uff0c\u4f60\u4eec\u8fd8\u80fd\u591f\u5bb9\u5fcd\u5979\u4e48\uff1f\u201d \u3000\u3000\u738b\u5f64\u3001\u738b\u91cd\u5c71\u3001\u71d5\u98de\u90fd\u542c\u5f97\u5168\u795e\u8d2f\u6ce8\uff0c\u6682\u65f6\u5fd8\u5374\u4e86\u5904\u5883\u7684\u51f6\u9669\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u4e24\u4e2a\u4eba\u8c08\u7684\u4e8b\u60c5\uff0c\u4e5f\u90fd\u662f\u4ed6\u4eec\u5fc3\u4e2d\u6700\u5173\u5fc3\u7684\u4e8b\u60c5\u3002 \u3000\u3000\u201c\u6211\u4e5f\u4e00\u76f4\u5f88\u62c5\u5fc3\u8fd9\u4ef6\u4e8b\u60c5\u3002\u201d\u60dc\u6625\u8bf4\uff1a\u201c\u4f46\u4e09\u516c\u4e3b\u7684\u806a\u6167\u548c\u8fdc\u89c1\uff0c\u5374\u5927\u5927\u7684\u51fa\u4e86\u6211\u4eec \u7684\u610f\u6599\u4e4b\u5916\uff0c\u5979\u5e73\u9759\u5982\u5e38\uff0c\u4e00\u70b9\u4e5f\u4e0d\u89c1\u6fc0\u52a8\uff0c\u6240\u4ee5\u73b0\u5728\u4e0d\u7528\u62c5\u5fc3\u4e09\u516c\u4e3b\u4e86\uff0c\u6211\u76f8\u4fe1\u5979\u4f1a\u5408\u4f5c \u7684\u3002\u201d \u3000\u3000\u601c\u82b1\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u76ee\u5149\u626b\u63a0\u738b\u5f64\u7b49\u4e09\u4eba\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u5462\uff1f\u201d \u3000\u3000\u201c\u738b\u7edf\u9886\u662f\u6211\u4eec\u7684\u4f20\u8bdd\u4f7f\u8005\u2026\u2026\u201d\u60dc\u6625\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u81f3\u4e8e\u71d5\u603b\u6355\u5934\u548c\u738b\u4f8d\u536b\u7684\u4e0b \u573a\uff0c\u5c31\u8981\u9760\u4ed6\u4eec\u81ea\u5df1\u5e94\u4ed8\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u60dc\u6625\u59d0\uff0c\u6211\u542c\u61c2\u4f60\u8bf4\u7684\u610f\u601d\uff0c\u4ed6\u4eec\u81ea\u5df1\u8981\u5982\u4f55\u5e94\u4ed8\u5462\uff1f\u201d \u3000\u3000\u60dc\u6625\u9053\uff1a\u201c\u597d\u5427\uff01\u770b\u5728\u59b9\u59b9\u7684\u4efd\u4e0a\uff0c\u6211\u5c31\u6307\u70b9\u4ed6\u4eec\u4e00\u4e0b\uff0c\u80fd\u9886\u609f\u591a\u5c11\u5c31\u5f97\u770b\u4ed6\u4eec\u7684\u609f\u6027 \u4e86\u3002\u201d \u3000\u3000\u51b7\u5389\u7684\u76ee\u5149\u7f13\u7f13\u7531\u71d5\u98de\u3001\u738b\u91cd\u5c71\u7684\u8138\u4e0a\u626b\u8fc7\uff0c\u53c8\u9053\uff1a\u201c\u4eba\u8d35\u81ea\u77e5\uff0c\u5728\u4ec0\u4e48\u6837\u7684\u73af\u5883\u4e0b\u80fd \u591f\u4fdd\u62a4\u81ea\u5df1\uff1f\u6700\u597d\u80fd\u60f3\u6e05\u695a\u81ea\u5df1\u6709\u591a\u5c11\u4efd\u91cf\uff0c\u8fc7\u72b9\u4e0d\u53ca\uff0c\u90fd\u975e\u81ea\u4fdd\u4e4b\u9053\u3002\u201d \u3000\u3000\u201c\u60dc\u6625\u59d1\u5a18\uff01\u201d\u738b\u5f64\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u80fd\u4e0d\u80fd\u89c1\u89c1\u4e09\u516c\u4e3b\uff1f\u201d \u3000\u3000\u201c\u738b\u7edf\u9886\uff0c\u4f60\u4f1a\u89c1\u5230\u7684\uff0c\u4e0d\u8fc7\uff0c\u4e0d\u662f\u73b0\u5728\u2026\u2026\u201d\u60dc\u6625\u5fae\u7b11\u9053\uff1a\u201c\u4eca\u665a\uff0c\u6211\u4eec\u5c31\u52a8\u8eab\u79bb \u5f00\uff0c\u9a6c\u6587\u4e2d\u4f1a\u5728\u8fd9\u91cc\u7b49\u4f60\u7684\u56de\u97f3\uff0c\u4f60\u5df2\u7ecf\u4e00\u8d25\u6d82\u5730\uff0c\u5f88\u96be\u6709\u633d\u56de\u5927\u5c40\u7684\u673a\u4f1a\uff0c\u6211\u5e0c\u671b\u4f60\u80fd\u8bc6 \u65f6\u52a1\uff0c\u6700\u597d\u628a\u4f60\u53ec\u96c6\u5230\u5f00\u5c01\u6765\u7684\u90a3\u6279\u624b\u4e0b\uff0c\u5148\u884c\u9063\u79bb\u8fd9\u91cc\uff0c\u4ed6\u4eec\u7559\u4e0b\u6765\u4e0d\u4f1a\u6709\u4efb\u4f55\u4f5c\u7528 \u7684\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\uff01\u201d\u738b\u5f64\u9053\uff1a\u201c\u6211\u4f1a\u5e26\u4ed6\u4eec\u79bb\u5f00\u8fd9\u91cc\uff0c\u4e0d\u8fc7\uff0c\u6211\u5e0c\u671b\u548c\u9a6c\u6587\u4e2d\u8bf4\u51e0\u53e5\u8bdd\u3002\u201d \u3000\u3000\u60dc\u6625\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u601c\u82b1\uff0c\u54b1\u4eec\u8d70\uff01\u4f60\u5df2\u7ecf\u5c3d\u5230\u4e86\u5fc3\u610f\uff0c\u5269\u4e0b\u7684\u7531\u4ed6\u4eec\u81ea\u5df1\u53bb\u9009\u62e9\u5427\uff0c \u8d70\u5427\uff01\u201d \u3000\u3000\u671b\u7740\u4e8c\u5973\u8fdc\u53bb\u7684\u80cc\u5f71\uff0c\u738b\u91cd\u5c71\u957f\u957f\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u6709\u7740\u4e00\u79cd\u5947\u602a\u7684\u611f\u89c9\uff0c\u548c\u60dc\u6625\u8c08 \u6761\u4ef6\uff0c\u4f3c\u662f\u6bd4\u9a6c\u6587\u4e2d\u66f4\u52a0\u56f0\u96be\uff1f\u201d \u3000\u3000\u201c\u738b\u4f8d\u536b\u5938\u5956\u4e86\uff01\u201d\u9a6c\u6587\u4e2d\u7f13\u6b65\u884c\u5165\u5ba4\u4e2d\uff0c\u7b11\u9053\uff1a\u201c\u5973\u5b69\u5b50\u89c1\u8bc6\u6d45\u8584\uff0c\u987e\u8651\u4e0d\u5468\uff0c\u96be\u514d \u4f1a\u7387\u6027\u800c\u4e3a\u3002\u201d \u3000\u3000\u4f46\u4f7f\u71d5\u98de\u548c\u738b\u91cd\u5c71\u9707\u60ca\u7684\u662f\uff0c\u9a6c\u6587\u4e2d\u7684\u8eab\u540e\uff0c\u7adf\u7136\u7d27\u968f\u7740\u8f6c\u9f99\u624b\u5f20\u4e0d\u7a7a\u3002 \u3000\u3000\u71d5\u98de\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u5f20\u4e0d\u7a7a\u2026\u2026\u201d \u3000\u3000\u201c\u771f\u662f\u98ce\u6c34\u8f6e\u6d41\u8f6c\u554a\uff01\u4e09\u5e74\u6cb3\u4e1c\u8f6c\u6cb3\u897f\uff0c\u71d5\u603b\u6355\u662f\u540d\u52a8\u4e2d\u539f\u7684\u7f09\u6355\u9ad8\u624b\uff0c\u73b0\u5728\u597d\u50cf \u662f\u2026\u2026\u201d\u5f20\u4e0d\u7a7a\u5fae\u7b11\u9053\uff1a\u201c\u9636\u4e0b\u56da\u4e86\uff01\u201d \u3000\u3000\u71d5\u98de\u53f9\u606f\u4e00\u58f0\uff0c\u5782\u9996\u4e0d\u8bed\u3002 \u3000\u3000\u9a6c\u6587\u4e2d\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u71d5\u98de\uff0c\u4e0d\u8981\u96be\u8fc7\uff0c\u60f3\u60f3\u90a3\u5927\u6f20\u4e4b\u4e0a\uff0c\u9ec4\u6c99\u4e4b\u4e0b\uff0c\u57cb\u4e86\u591a\u5c11\u8499\u53e4 \u5c11\u5e74\uff0c\u591a\u5c11\u6c49\u5bb6\u513f\u90ce\uff0c\u8fd9\u4e9b\u4eba\u4e5f\u90fd\u6709\u7236\u6bcd\u59bb\u513f\uff0c\u9a6c\u67d0\u4eba\u6b63\u5c3d\u6211\u624d\u667a\u80fd\u529b\uff0c\u5728\u633d\u6551\u8fd9\u573a\u6d69\u52ab\uff0c \u4e0d\u8ba9\u5b83\u7ee7\u7eed\u4e0b\u53bb\uff0c\u7528\u4e00\u70b9\u5316\u654c\u4e3a\u53cb\u7684\u624b\u6bb5\uff0c\u53c8\u7b97\u5f97\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u71d5\u98de\u9053\uff1a\u201c\u59ec\u91cd\u5929\u5462\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4e5f\u6d3b\u5f97\u5f88\u597d\u2026\u2026\u201d\u9a6c\u6587\u4e2d\u7b11\u9053\uff1a\u201c\u4ed6\u7b97\u5f97\u4e0a\u662f\u94c1\u94ee\u94ee\u7684\u6c49\u5b50\uff0c\u71ac\u8fc7\u4e86\u4e94\u5211\u5374\u8fc7\u4e0d\u4e86 \u7f8e\u4eba\u5173\uff0c\u8fd9\u4e5f\u96be\u602a\u554a\uff01\u82f1\u96c4\u7f8e\u4eba\u4e00\u5411\u662f\u5343\u53e4\u4f73\u8bdd\u5440\uff01\u201d \u3000\u3000\u201c\u4f60\u662f\u8bf4\uff0c\u59ec\u91cd\u5929\u4e5f\u6295\u5165\u9b54\u6559\u2026\u2026\u201d \u3000\u3000\u201c\u738b\u89c1\u4e5f\u77e5\u9053\u59ec\u91cd\u5929\u8fd9\u4e2a\u4eba\uff0c\u770b\u6765\uff0c\u4ed6\u771f\u662f\u4e2a\u5f88\u6709\u540d\u6c14\u7684\u4eba\u7269\u4e86\u3002\u201d\u9a6c\u6587\u4e2d\u9053\uff1a\u201c\u4ed6\u6ca1 \u6709\u6295\u5165\u9b54\u6559\uff0c\u53ea\u662f\u613f\u4e3a\u5144\u5f1f\u6548\u529b\u5c31\u662f\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u8f6c\u5230\u71d5\u98de\u7684\u8eab\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u6c5f\u5343\u91cc\u5f88\u60f3\u5230\u624b\u7684\u90a3\u672c\u6b66\u5b66\u5947\u4e66\uff0c\u59ec\u91cd\u5929\u4e5f\u4ea4\u51fa\u6765\u4e86\uff0c \u90a3\u662f\u4e00\u672c\u8bb0\u8f7d\u4e2d\u539f\u6b66\u6797\u4e2d\u591a\u79cd\u7edd\u6280\u79d8\u672c\uff0c\u679c\u7136\u662f\u73cd\u8d35\u5f97\u5f88\uff0c\u53ea\u53ef\u60dc\u6c5f\u5343\u91cc\u8fd9\u5934\u8001\u72d0\u72f8\u7684\u5e0c\u671b \u8981\u6210\u6ce1\u5f71\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7684\u662f\u5f88\u5177\u4f53\uff0c\u4f46\u5374\u59cb\u7ec8\u6ca1\u6709\u8bf4\u51fa\u90a3\u672c\u4e66\u7684\u540d\u5b57\u3002 \u3000\u3000\u738b\u5f64\u548c\u71d5\u98de\u90fd\u672a\u518d\u95ee\uff0c\u4ed6\u4eec\u5fc3\u4e2d\u660e\u767d\uff0c\u9a6c\u6587\u4e2d\u5982\u4e0d\u613f\u8bf4\uff0c\u95ee\u4e86\u53cd\u800c\u81ea\u53d6\u5176\u8fb1\u3002 \u3000\u3000\u679c\u7136\uff0c\u9a6c\u6587\u4e2d\u8f6c\u8fc7\u4e86\u8bdd\u9898\uff0c\u9053\uff1a\u201c\u738b\u5144\uff0c\u4f60\u770b\u8981\u591a\u5c11\u65f6\u95f4\uff0c\u624d\u53ef\u4ee5\u56de\u6211\u7684\u6d88\u606f\u5462\uff1f\u201d \u3000\u3000\u201c\u5feb\u9a6c\u517c\u7a0b\uff0c\u6765\u5f80\u4e5f\u9700\u5341\u5929\u4ee5\u4e0a\u7684\u5de5\u592b\uff0c\u6700\u597d\u6709\u4e00\u4e2a\u6708\u7684\u65f6\u95f4\u7ed9\u6211\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u884c\uff01\u201d\u9a6c\u6587\u4e2d\u51b7\u51b7\u5730\u9053\uff1a\u201c\u6700\u591a\u4e8c\u5341\u5929\u3002\u201d \u3000\u3000\u738b\u5f64\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u53ef\u662f\uff0c\u6211\u7684\u8eab\u4f53\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u4f1a\u89e3\u5f00\u4f60\u53d7\u5236\u7684\u7ecf\u8109\uff0c\u4f60\u53ef\u4ee5\u6062\u590d\u4e94\u6210\u7684\u6b66\u529f\uff0c\u8db3\u53ef\u4ee5\u8ba9\u4f60\u7eb5\u9a6c\u98de\u884c\u4e86\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\uff0c\u65f6\u9650\u7d27\u8feb\uff0c\u6211\u5e0c\u671b\u65e9\u4e9b\u52a8\u8eab\u3002\u201d \u3000\u3000\u201c\u660e\u5929\u4e00\u65e9\u8d77\u7a0b\u2026\u2026\u201d\u9a6c\u6587\u4e2d\u8bf4\uff1a\u201c\u4e0d\u8fc7\uff0c\u6709\u4e9b\u4e8b\u6211\u8981\u5148\u8bf4\u660e\u767d\uff0c\u4e8c\u5341\u5929\u5185\u4f60\u5982\u4e0d\u8d76\u56de \u6765\uff0c\u6211\u5c31\u5148\u6740\u4e86\u738b\u91cd\u5c71\u548c\u4f60\u7684\u4e24\u4e2a\u4f8d\u536b\u8d75\u4fdd\u548c\u9648\u5b8f\uff0c\u63a5\u7740\u662f\u9ed1\u7f57\u6c49\u3001\u859b\u767e\u80dc\u3001\u5fc3\u5370\u5927\u5e08\u3001\u71d5 \u603b\u6355\u3002\u201d \u3000\u3000\u201c\u6211\u4e00\u5b9a\u5982\u7ea6\u8d76\u56de\uff0c\u4f46\u6211\u8981\u4f60\u9a6c\u5927\u4eba\u4e00\u53e5\u8bdd\uff0c\u4ed6\u4eec\u8fd9\u4e9b\u4eba\u8981\u6beb\u53d1\u65e0\u4f24\u2026\u2026\u201d \u3000\u3000\u201c\u4e00\u8a00\u4e3a\u5b9a\uff01\u201d\u9a6c\u6587\u4e2d\u9053\uff1a\u201c\u5982\u679c\u738b\u5144\u80fd\u5e26\u6765\u7687\u4e0a\u7684\u627f\u8bfa\uff0c\u8fd9\u4e9b\u4eba\u4e5f\u90fd\u662f\u6211\u9a6c\u6587\u4e2d\u7684\u8d35 \u5bbe\uff0c\u6211\u7b54\u5e94\u4f60\uff0c\u8fd9\u4e8c\u5341\u5929\u5185\u6211\u4e0d\u4f1a\u5bf9\u4ed6\u4eec\u6709\u6240\u6162\u5f85\uff0c\u4e5f\u4e0d\u4f1a\u7528\u672f\u6cd5\u5f15\u8bf1\u4ed6\u4eec\u3002\u201d \u3000\u3000\u5fae\u5fae\u4e00\u7b11\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u4f60\u5982\u679c\u4e8c\u5341\u5929\u5185\u8d76\u4e0d\u56de\u6765\uff0c\u53ea\u6015\u738b\u5144\u4e5f\u96be\u518d\u770b\u5230\u7b2c\u4e8c\u5929\u7684\u65e5\u51fa \u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4e00\u70b9\uff0c\u738b\u67d0\u4eba\u65e9\u60f3\u5230\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u518d\u5949\u529d\u738b\u5144\u4e00\u53e5\u8bdd\uff0c\u5728\u4f60\u51fa\u53d1\u4e4b\u524d\uff0c\u6211\u4eec\u662f\u672f\u3001\u6bd2\u5e76\u7528\uff0c\u6700\u597d\u4e0d\u8981\u5984\u751f\u89e3\u6bd2\u4e4b \u5fc3\uff0c\u90a3\u53ef\u80fd\u4fc3\u4f7f\u6f5c\u6bd2\u65e9\u53d1\u3002\u201d \u3000\u3000\u201c\u591a\u8c22\u6307\u6559\u4e86\uff01\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u4e00\u6325\u624b\uff0c\u9053\uff1a\u201c\u4e09\u4f4d\u597d\u597d\u5403\u4e00\u987f\u996d\uff0c\u4ed4\u7ec6\u7684\u804a\u804a\uff0c\u660e\u5929\u6211\u6709\u4e8b\uff0c\u4e0d\u6765\u7ed9\u738b\u5144\u9001\u884c \u4e86\u3002\u201d \u3000\u3000\u8f6c\u8eab\u5927\u6b65\u800c\u53bb\u3002 \u3000\u3000\u201c\u5389\u5bb3\u5440\u5389\u5bb3\uff0c\u6574\u4e2a\u5f00\u5c01\u5e9c\u6446\u6ee1\u4e86\u5404\u79cd\u5708\u5957\uff0c\u53ea\u8981\u8d70\u4e00\u6b65\uff0c\u90fd\u4f1a\u8de8\u5165\u5708\u5957\u4e2d\u3002\u201d\u71d5\u98de\u61ca \u607c\u4e2d\uff0c\u53c8\u4e0d\u5f97\u4e0d\u6d41\u9732\u51fa\u5341\u5206\u656c\u4f69\u7684\u795e\u60c5\uff0c\u8bf4\uff1a\u201c\u9a6c\u6587\u4e2d\u7684\u8bbe\u8ba1\u3001\u624d\u667a\u3001\u884c\u52a8\u3001\u5e03\u7f6e\uff0c\u5f53\u771f\u662f \u7ef5\u5bc6\u65e0\u6687\uff0c\u70b9\u6ef4\u4e0d\u6f0f\uff0c\u8fde\u59ec\u91cd\u5929\u3001\u8f6c\u9f99\u624b\uff0c\u4ed6\u90fd\u80fd\u6536\u4e3a\u5df1\u7528\u3002\u201d \u3000\u3000\u738b\u91cd\u5c71\u7a81\u7136\u54c8\u54c8\u5927\u7b11\u4e00\u9635\uff0c\u9053\uff1a\u201c\u8fd9\u4e48\u770b\u8d77\u6765\uff0c\u6c5f\u5343\u91cc\u4e0d\u4f46\u5bfb\u4eba\u3001\u8ffd\u8e2a\u4e4b\u672f\u5929\u4e0b\u65e0\u53cc\uff0c \u5224\u4e8b\u9690\u8eab\u4e4b\u80fd\u4ea6\u662f\u795e\u9b3c\u83ab\u6d4b\u4e86\uff0c\u9a6c\u5de1\u629a\u5728\u5f00\u5c01\u5e9c\u5e03\u4e0b\u4e86\u91cd\u91cd\u7f57\u7f51\uff0c\u5c31\u662f\u7f51\u4e0d\u4f4f\u6c5f\u5343\u91cc\u3002\u201d \u3000\u3000\u71d5\u98de\u5fc3\u4e2d\u5fd6\u9053\uff1a\u8bf4\u7684\u4e5f\u662f\uff0c\u4ed6\u4e0d\u4f46\u7a81\u7834\u4e86\u9a6c\u5de1\u629a\u91cd\u91cd\u7684\u7f57\u7f51\uff0c\u800c\u4e14\u7adf\u628a\u5c0f\u71d5\u5b50\u548c\u4ed6\u5a18\u4e5f \u5e26\u51fa\u4e86\u5f00\u5c01\uff0c\u8fd9\u4efd\u80fd\u8010\u771f\u662f\u4e0d\u53ef\u601d\u8bae\u4e86\u3002 \u3000\u3000\u5927\u5c40\u5df2\u660e\uff0c\u5fc3\u4e2d\u53cd\u800c\u5c11\u53bb\u4e86\u90a3\u4efd\u5bc6\u4e91\u4e0d\u96e8\u7684\u5fe7\u95f7\uff0c\u9762\u5bf9\u7740\u4e30\u76db\u9152\u83dc\uff0c\u71d5\u98de\u7b49\u6682\u653e\u6101\u6000\uff0c \u5f00\u6000\u7545\u996e\u8d77\u6765\u4e86\u3002 \u3000\u3000\u738b\u5f64\u5fc3\u4e2d\u76d8\u7b97\u4e86\u5343\u767e\u904d\uff0c\u600e\u4e48\u7b97\uff0c\u4e5f\u65e0\u6cd5\u5bf9\u738b\u91cd\u5c71\u548c\u71d5\u98de\u63d0\u4f9b\u51fa\u5e2e\u52a9\u7684\u80fd\u529b\uff0c\u53ea\u597d\u8981\u4e24 \u4eba\u968f\u673a\u5e94\u53d8\uff0c\u4fdd\u5b58\u6027\u547d\u8981\u7d27\uff0c\u8fd9\u7b49\u56fd\u5bb6\u5927\u4e8b\u5df2\u8d85\u51fa\u4e86\u4ed6\u4eec\u7684\u804c\u53f8\u8303\u56f4\u3002 \u3000\u3000\u71d5\u98de\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5927\u4eba\uff0c\u4e0d\u7528\u4e3a\u6211\u7b49\u591a\u8d39\u5fc3\u601d\u4e86\uff0c\u4e00\u5207\u4e8b\u52a1\uff0c\u9a6c\u6587\u4e2d\u4f3c\u662f\u90fd\u5df2\u5b89\u6392\u597d \u4e86\uff0c\u5012\u662f\u5927\u4eba\u8981\u5982\u4f55\u7a81\u7834\u4ed6\u7684\u8bbe\u8ba1\uff0c\u624d\u80fd\u653e\u624b\u4e00\u640f\u3002\u201d \u3000\u3000\u201c\u76ee\u524d\u7684\u5c40\u52bf\u53d8\u5316\uff0c\u5df2\u5b8c\u5168\u8d85\u51fa\u4e86\u6211\u7684\u63a7\u5236\u3002\u201d\u738b\u5f64\u653e\u4f4e\u4e86\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u9a6c\u6587\u4e2d\u7684\u8bbe\u8ba1 \u4e0d\u9519\uff0c\u5bf9\u7687\u4e0a\u7684\u6027\u683c\u4e5f\u6709\u7740\u5145\u5206\u7684\u4e86\u89e3\uff0c\u6211\u76f8\u4fe1\u7687\u4e0a\u53ef\u80fd\u63a5\u53d7\u4ed6\u4e5e\u548c\u7684\u8981\u6c42\uff0c\u4f7f\u6211\u62c5\u5fc3\u7684 \u662f\uff0c\u9a6c\u6587\u4e2d\u662f\u5426\u4f1a\u771f\u5982\u6240\u8bf4\uff0c\u5230\u65f6\u5019\u5c65\u884c\u8bfa\u8a00\uff0c\u628a\u5fc3\u5370\u5927\u5e08\u3001\u859b\u767e\u80dc\u7b49\u5168\u6570\u653e\u51fa\uff0c\u800c\u4e0d\u4ee5\u672f \u6cd5\u63a7\u5236\u3002\u201d \u3000\u3000\u738b\u91cd\u5c71\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4e8c\u53d4\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u8ba4\u4e3a\u9a6c\u6587\u4e2d\u4e0d\u4f1a\u5b8c\u5168\u76f8\u4fe1\u7687\u4e0a\u7684\u627f\u8bfa\uff0c\u6240\u4ee5\u4ed6\u4f1a\u5728\u4e2d\u539f\u6b66\u6797\u9053\u4e0a\uff0c\u6709\u6240\u5e03\u7f6e\u3002\u201d\u738b \u5f64\u82e6\u7b11\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u4ed6\u4f1a\u7559\u4e0b\u4e00\u7740\uff0c\u8ba9\u54b1\u4eec\u5fc3\u4e2d\u987e\u8651\uff0c\u4e0d\u80fd\u653e\u624b\u641c\u67e5\u4ed6\u5e03\u7559\u4e0b\u7684\u8033\u76ee\u3001\u5978 \u7ec6\u3002\u201d \u3000\u3000\u201c\u5bf9\uff0c\u50cf\u60dc\u6625\u8fd9\u6837\u7684\u5bab\u5973\uff0c\u4e5f\u8bb8\u8fd8\u6709\u5f88\u591a\u2026\u2026\u201d\u738b\u91cd\u5c71\u63d0\u51fa\u4e86\u4ed6\u7684\u770b\u6cd5\u3002 \u3000\u3000\u201c\u60dc\u6625\u6545\u610f\u66b4\u9732\u51fa\u8eab\u4efd\uff0c\u662f\u4e00\u79cd\u6050\u5413\u548c\u8b66\u544a\u3002\u201d\u738b\u5f64\u9053\uff1a\u201c\u6df1\u5bab\u5185\u82d1\u4e4b\u4e2d\uff0c\u90fd\u85cf\u6709\u9b54\u6559 \u9ad8\u624b\uff0c\u8bf4\u660e\u4e86\u4ed6\u4eec\u65e0\u6240\u4e0d\u5728\u3002\u201d \u3000\u3000\u71d5\u98de\u9053\uff1a\u201c\u4ee5\u738b\u7edf\u9886\u7684\u770b\u6cd5\uff0c\u6df1\u5bab\u5185\u82d1\u4e4b\u4e2d\u662f\u4e0d\u662f\u8fd8\u6709\u4ed6\u4eec\u7684\u4eba\uff1f\u201d \u3000\u3000\u201c\u6709\uff01\u201d\u738b\u5f64\u9053\uff1a\u201c\u4e00\u822c\u4eba\u4e0d\u6562\u6d89\u53ca\u8fd9\u7b49\u5927\u9006\u4e0d\u9053\u7684\u6b3a\u541b\u4e4b\u7f6a\uff0c\u90a3\u8981\u682a\u8fde\u4e5d\u65cf\uff0c\u4f46\u9a6c\u6587 \u4e2d\u4e0d\u6015\u8fd9\u4e9b\uff0c\u6240\u4ee5\u4ed6\u53ef\u4ee5\u4e0d\u62e9\u624b\u6bb5\u7684\u628a\u5979\u4eec\u9001\u5165\u7981\u5bab\uff0c\u53e6\u4e00\u4e2a\u53ef\u80fd\u662f\u4ed6\u4eec\u5c31\u5728\u5bab\u4e2d\u5438\u6536\u5f1f \u5b50\uff0c\u4f20\u6388\u9b54\u6559\u7684\u672f\u6cd5\uff0c\u9002\u624d\u542c\u60dc\u6625\u7684\u53e3\u6c14\uff0c\u5728\u96f7\u97f3\u5bfa\u4e2d\u7684\u8eab\u4efd\u4f3c\u662f\u4e0d\u4f4e\uff0c\u4f46\u5979\u7684\u5e74\u7eaa\u5374\u662f\u5f88 \u8f7b\uff0c\u90a3\u8bf4\u660e\u4e86\uff0c\u5979\u662f\u5343\u4e07\u4eba\u4e2d\u9009\u4e00\u4e2a\u7684\u4f18\u79c0\u624d\u5973\uff0c\u4e5f\u662f\u96f7\u97f3\u5bfa\u7684\u6770\u51fa\u5f1f\u5b50\u3002\u201d \u3000\u3000\u201c\u8bf4\u7684\u662f\u554a\uff01\u5341\u4e09\u5c81\u8fdb\u5165\u7687\u5bab\u2026\u2026\u201d\u71d5\u98de\u9053\uff1a\u201c\u80fd\u628a\u8eab\u4efd\u9690\u85cf\u5f97\u5982\u6b64\u5468\u5bc6\uff0c\u800c\u4e14\u7531\u5343\u767e \u540d\u5bab\u5973\u4e2d\u8131\u9896\u800c\u51fa\uff0c\u6210\u4e3a\u4e09\u516c\u4e3b\u7684\u8d34\u8eab\u5973\u5a62\uff0c\u8fd9\u4efd\u624d\u6167\u3001\u8010\u529b\uff0c\u6210\u5e74\u4eba\u4e5f\u672a\u5fc5\u80fd\u529e\u5230\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u4e09\u516c\u4e3b\u771f\u5982\u4ed6\u4eec\u5f62\u5bb9\u7684\u90a3\u7b49\u7edd\u4e16\u9ad8\u624b\uff0c\u7adf\u7136\u672a\u53d1\u89c9\u957f\u968f\u8eab\u4fa7\u7684\u5973\u5a62\u4e5f\u8eab\u8d1f\u4e0a\u4e58\u6b66 \u529f\uff0c\u90a3\u624d\u53eb\u771f\u7684\u53ef\u6015\u3002\u201d \u3000\u3000\u738b\u91cd\u5c71\u9053\uff1a\u201c\u5bf9\uff01\u4ee5\u601c\u82b1\u548c\u5979\u7684\u4eb2\u5bc6\u7a0b\u5ea6\uff0c\u90fd\u6ca1\u6709\u53d1\u89c9\u53ef\u7591\uff0c\u53ef\u89c1\u5979\u7684\u5c0f\u5fc3\u8c28\u614e\uff0c\u771f\u7684 \u662f\u5df2\u5230\u4e86\u98ce\u96e8\u4e0d\u900f\u7684\u5883\u754c\u3002\u201d \u3000\u3000\u738b\u5f64\u56db\u987e\u65e0\u4eba\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u91cd\u5c71\uff0c\u4e8c\u53d4\u5f88\u5bf9\u4e0d\u8d77\u4f60\uff0c\u60f3\u4e0d\u5230\u7adf\u628a\u4f60\u62d6\u5165\u4e86\u8fd9\u4e48\u4e00\u4e2a\u9669\u5883 \u4e4b\u4e2d\u3002\u201d \u3000\u3000\u201c\u6c5f\u6e56\u4f55\u5904\u4e0d\u51f6\u9669\u2026\u2026\u201d\u738b\u91cd\u5c71\u9053\uff1a\u201c\u4e8c\u53d4\u8bf7\u653e\u5fc3\uff0c\u5c0f\u4f84\u7edd\u4e0d\u4f1a\u7ed9\u738b\u5bb6\u4e22\u4eba\uff0c\u5fc5\u8981\u65f6\u6211 \u4f1a\u2026\u2026\u201d \u3000\u3000\u201c\u4e00\u5b9a\u8981\u7b49\u6211\u56de\u6765\u2026\u2026\u201d\u738b\u5f64\u9053\uff1a\u201c\u9a6c\u6587\u4e2d\u5982\u679c\u98df\u8a00\u800c\u80a5\uff0c\u6211\u4e0d\u4f1a\u4efb\u4ed6\u79bb\u5f00\u4e2d\u539f\uff0c\u6211\u8981 \u7b56\u52a8\u671d\u5ef7\u5168\u529b\u51fa\u51fb\uff0c\u4e5f\u53d1\u52a8\u6b66\u6797\u4e2d\u5404\u5927\u95e8\u6d3e\u9063\u6d3e\u9ad8\u624b\uff0c\u968f\u519b\u5f81\u6218\uff0c\u8e0f\u5e73\u96f7\u97f3\u5bfa\uff0c\u6b7c\u706d\u8499\u53e4\u5168 \u65cf\u3002\u201d \u3000\u3000\u201c\u592a\u72e0\u4e86\u5427\uff01\u2026\u2026\u201d\u9c7c\u5316\u9f99\u7f13\u6b65\u884c\u4e86\u8fdb\u6765\uff0c\u9053\uff1a\u201c\u522b\u8bf4\u8fd9\u4ef6\u4e8b\u4e0d\u5bb9\u6613\u505a\u5230\uff0c\u5c31\u7b97\u4f60\u771f\u80fd \u7b56\u52a8\u4e3e\u56fd\u5927\u519b\uff0c\u5c3d\u51fa\u8fb9\u585e\uff0c\u4f60\u4e5f\u8981\u60f3\u60f3\u770b\uff0c\u8981\u727a\u7272\u591a\u5c11\u6c49\u5bb6\u513f\u90ce\u7684\u6027\u547d\u624d\u80fd\u529e\u5230\uff0c\u585e\u5916\u98ce\u6c99 \u53d8\u5316\u83ab\u6d4b\uff0c\u4e0d\u6bd4\u4e2d\u539f\uff0c\u8499\u53e4\u53ef\u6c57\u5df2\u548c\u7ef4\u543e\u5c14\u65cf\u7ed3\u4e3a\u5144\u5f1f\u4e4b\u5e2e\uff0c\u8fde\u7ed3\u897f\u57df\u5341\u4f59\u56fd\uff0c\u7ec4\u6210\u8054\u76df\uff0c \u5929\u671d\u56fd\u52bf\u867d\u503c\u76db\u65f6\uff0c\u4f46\u8fd9\u4e00\u4ed7\u4e5f\u4f1a\u6253\u5f97\u7b4b\u75b2\u529b\u7aed\uff0c\u8d25\u56fa\u53ef\u60b2\uff0c\u80dc\u4ea6\u60e8\u80dc\u3002\u738b\u7edf\u9886\u4ee5\u4e3a\u81ea\u67d0\u4eba \u7684\u770b\u6cd5\u5982\u4f55\uff1f\u201d \u3000\u3000\u9c7c\u5316\u9f99\u4f24\u52bf\u5df2\u6108\uff0c\u6c14\u5ea6\u6062\u5b8f\uff0c\u4f3c\u662f\u6839\u672c\u5df2\u5fd8\u53bb\u4e86\u738b\u5f64\u6253\u4ed6\u4e00\u8bb0\u98de\u9488\u7684\u4e8b\u3002 \u3000\u3000\u201c\u9a6c\u6587\u4e2d\u5904\u5fc3\u79ef\u8651\uff0c\u5b89\u6392\u4e86\u91cd\u91cd\u9677\u9631\uff0c\u54b1\u4eec\u88ab\u64d2\u53d7\u5236\u90a3\u4e5f\u5c31\u8ba4\u4e86\uff0c\u4f46\u671d\u5ef7\u6709\u6cd5\u3001\u6c5f\u6e56\u6709 \u9053\uff0c\u4e09\u4e24\u4eba\u7684\u751f\u6b7b\u662f\u65e0\u6cd5\u6539\u53d8\u5927\u5c40\u7684\uff0c\u4f46\u4e8b\u5728\u4eba\u4e3a\uff0c\u4eba\u80fd\u5f71\u54cd\u5230\u5927\u5c40\u7684\u53d8\u5316\u3002\u201d \u3000\u3000\u738b\u5f64\u795e\u60c5\u4e25\u8083\uff0c\u51b7\u51b7\u5730\u53c8\u9053\uff1a\u201c\u6211\u8d5e\u6210\u9a6c\u6587\u4e2d\u7684\u60f3\u6cd5\uff0c\u4e5f\u613f\u5168\u529b\u4fc3\u6210\u4e24\u56fd\u7f62\u606f\u4e89\u7aef\uff0c\u4f46 \u6211\u5e0c\u671b\u9a6c\u6587\u4e2d\u4e5f\u8981\u4fe1\u5b88\u627f\u8bfa\uff0c\u4e0d\u80fd\u4f24\u5bb3\u4e09\u516c\u4e3b\u548c\u6211\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u8fd9\u4e2a\u8bf7\u738b\u5144\u653e\u4e00\u767e\u4e2a\u5fc3\uff0c\u4f60\u672a\u56de\u6765\u4e4b\u524d\uff0c\u5144\u5f1f\u62c5\u4fdd\u4f60\u7684\u670b\u53cb\u4e0d\u4f1a\u53d7\u5230\u4efb\u4f55\u7684\u4f24 \u5bb3\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u597d\uff0c\u738b\u67d0\u4e5f\u4f1a\u5982\u671f\u8d76\u56de\u3002 \u3000\u3000\u738b\u5f64\u4e0d\u77e5\u9053\u9a6c\u6587\u4e2d\u7528\u4ec0\u4e48\u65b9\u6cd5\uff0c\u89e3\u9664\u4ed6\u8eab\u4e0a\u7684\u7981\u5236\uff0c\u9192\u6765\u65f6\uff0c\u611f\u89c9\u5230\u6b66\u529f\u5df2\u7ecf\u6062\u590d\u4e86\u4e0d \u5c11\u3002 \u3000\u3000\u7559\u5728\u623f\u4e2d\u7684\u53ea\u6709\u4e00\u4e2a\u9c7c\u5316\u9f99\uff0c\u800c\u4e14\uff0c\u4f4f\u7684\u5730\u65b9\u4e5f\u5b8c\u5168\u4e0d\u540c\u3002 \u3000\u3000\u8fd9\u662f\u5f00\u5c01\u5e9c\u4e00\u5bb6\u5ba2\u6808\u3002 \u3000\u3000\u9c7c\u5316\u9f99\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9a6c\u5339\u3001\u8f66\u8f86\u90fd\u5df2\u7ecf\u51c6\u5907\u597d\u4e86\uff0c\u738b\u5144\uff0c\u51e0\u65f6\u4e0a\u8def\u4efb\u51ed\u81ea\u4e3b\uff0c\u5144\u5f1f\u4e0d \u6253\u6270\uff0c\u5c31\u6b64\u544a\u9000\u3002\u201d \u3000\u3000\u62f1\u62f1\u624b\uff0c\u9000\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4e00\u4e2a\u5e97\u5c0f\u4e8c\u6258\u7740\u4e00\u4e2a\u6728\u76d8\u5b50\uff0c\u7f13\u7f13\u884c\u4e86\u8fdb\u6765\uff0c\u6728\u76d8\u4e0a\u653e\u7740\u56db\u76d8\u83dc\u4e00\u58f6\u9152\uff0c\u8fd8\u653e\u4e86\u4e24\u53cc\u676f \u7b77\u3002 \u3000\u3000\u738b\u5f64\u4e00\u76b1\u7709\uff0c\u9053\uff1a\u201c\u6211\u6ca1\u53eb\u9152\u83dc\uff0c\u4e5f\u5403\u4e0d\u4e0b\uff0c\u8981\u4ed6\u4eec\u4e3a\u6211\u5907\u4e00\u8f86\u5feb\u8f66\uff0c\u4e24\u5339\u968f\u884c\u5065\u9a6c\uff0c \u6211\u8981\u7acb\u523b\u4e0a\u8def\u3002\u201d \u3000\u3000\u5e97\u5c0f\u4e8c\u4f3c\u662f\u6839\u672c\u6ca1\u6709\u542c\u5230\u738b\u5f64\u7684\u8bdd\uff0c\u53ea\u7ba1\u6446\u597d\u4ed6\u7684\u9152\u83dc\uff0c\u81ea\u8a00\u81ea\u8bed\u5730\u9053\uff1a\u201c\u8fd9\u91cc\u5f88\u5b89 \u9759\uff0c\u4e5f\u53ef\u4ee5\u653e\u5fc3\u8bf4\u8bdd\uff0c\u4e0a\u4e86\u9a6c\u8f66\u5c31\u4e0d\u80fd\u7545\u6240\u6b32\u8a00\u4e86\uff0c\u9152\u4e2d\u65e0\u6bd2\uff0c\u83b1\u91cc\u65e0\u836f\uff0c\u53ef\u4ee5\u653e\u5fc3\u7684\u5403\u559d \u4e00\u987f\u3002\u201d \u3000\u3000\u738b\u5f64\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4f60\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u6c5f\u5343\u91cc\uff01\u201d \u3000\u3000\u5e97\u5c0f\u4e8c\u62ac\u5934\u4e00\u7b11\uff0c\u53c8\u9053\uff1a\u201c\u4e24\u5929\u524d\u6211\u5c31\u5230\u4e86\u5f00\u5c01\uff0c\u4e00\u5207\u4e8b\u60c5\u7684\u7ecf\u8fc7\uff0c\u6211\u90fd\u5df2\u7ecf\u6709\u4e2a\u5927\u6982 \u7684\u4e86\u89e3\uff0c\u76ee\u4e0b\u7684\u5f00\u5c01\u5e9c\u6ee1\u5730\u90fd\u662f\u9a6c\u5de1\u629a\u7684\u5bc6\u63a2\u3001\u8033\u76ee\uff0c\u884c\u8ff9\u7a0d\u6709\u53ef\u7591\u4fbf\u5bf8\u6b65\u96be\u884c\u4e86\uff0c\u6211\u4e00\u8def \u66f4\u6362\u8eab\u4efd\uff0c\u8fd9\u624d\u6df7\u5165\u57ce\u4e2d\uff0c\u4f46\u8fdb\u4e0d\u4e86\u5de1\u629a\u5e9c\u548c\u5f00\u5c01\u5e9c\u8859\uff0c\u76f4\u7b49\u5230\u73b0\u5728\u624d\u6709\u673a\u4f1a\u89c1\u5230\u4f60\uff0c\u771f\u662f \u592a\u597d\u4e86\u3002\u201d \u3000\u3000\u738b\u5f64\u53cc\u76ee\u76ef\u5728\u5e97\u5c0f\u4e8c\u7684\u8138\u4e0a\uff0c\u770b\u4e86\u53c8\u770b\uff0c\u9053\uff1a\u201c\u4f60\u771f\u662f\u5e72\u91cc\u5144\u4e48\uff1f\u201d \u3000\u3000\u201c\u5982\u5047\u5305\u6362\uff01\u201d\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u9a6c\u6587\u4e2d\u5728\u5de1\u629a\u5e9c\u7684\u6355\u5feb\u4e2d\uff0c\u5b89\u6392\u4e86\u5f88\u591a\u4eb2\u4fe1\uff0c\u8fd9\u4e9b\u4eba\u591a\u5e74 \u6765\u90fd\u5728\u5f00\u5c01\u5730\u9762\u4e0a\u6d3b\u52a8\uff0c\u773c\u76ae\u5b50\u5bbd\u5f97\u5f88\uff0c\u8ba4\u4eba\u7684\u80fd\u529b\u5f88\u5f3a\uff0c\u5144\u5f1f\u5f88\u53d7\u9a6c\u5de1\u629a\u7684\u62ac\u4e3e\uff0c\u7528\u4e39\u9752 \u753b\u4e86\u51e0\u5341\u5f20\u5144\u5f1f\u7684\u5f62\u8c8c\uff0c\u4ea4\u7ed9\u4ed6\u4eec\u5230\u5904\u67e5\u8bbf\uff0c\u6240\u4ee5\u6211\u4e0d\u5f97\u4e0d\u628a\u81ea\u5df1\u9690\u85cf\u5f97\u6df1\u4e00\u70b9\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u9a6c\u6587\u4e2d\u5bf9\u4f60\u6068\u4e4b\u5165\u9aa8\uff01\u201d\u738b\u5f64\u5df2\u4ece\u53e3\u97f3\u4e2d\u542c\u51fa\u6765\uff0c\u786e\u662f\u591a\u5e74\u597d\u53cb\u6c5f\u5343\u91cc\uff0c\u5fc3\u4e2d \u4e00\u4e50\uff0c\u9053\uff1a\u201c\u4ed6\u6068\u4f60\u6ed1\u6e9c\u5f97\u5f88\uff0c\u9003\u8fc7\u4ed6\u91cd\u91cd\u7f57\u7f51\uff0c\u4e5f\u6068\u4f60\u7684\u8ffd\u8e2a\u4e4b\u80fd\uff0c\u627e\u51fa\u4e86\u4e09\u516c\u4e3b\u7684\u4e0b \u843d\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\uff0c\u4ed6\u5fc5\u987b\u6293\u5230\u6211\u624d\u4f1a\u7518\u5fc3\u3002\u201d\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u73b0\u5728\uff0c\u4f60\u51c6\u5907\u5982\u4f55\u884c\u52a8\uff1f\u201d \u3000\u3000\u738b\u5f64\u7b80\u7565\u8bf4\u660e\u7ecf\u8fc7\uff0c\u9053\uff1a\u201c\u5c31\u76ee\u4e0b\u60c5\u5f62\u800c\u8a00\uff0c\u6211\u53ea\u6709\u56de\u4eac\u91cc\u9762\u590d\u7687\u4e0a\uff0c\u8bf7\u65e8\u5b9a\u593a\u4e00\u9014 \u4e86\u3002\u201d \u3000\u3000\u201c\u4f46\u613f\u9a6c\u6587\u4e2d\u5fc3\u53e3\u5982\u4e00\uff0c\u786e\u5b9e\u662f\u4e3a\u4e86\u7f62\u606f\u4e24\u56fd\u7684\u5e72\u6208\u3002\u201d \u3000\u3000\u201c\u600e\u4e48\uff1f\u4f60\u662f\u8bf4\u9a6c\u6587\u4e2d\u8fd8\u6709\u9634\u8c0b\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u4eba\u7684\u5fc3\u673a\u592a\u6df1\u4e86\uff01\u201d\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u6211\u89c1\u8fc7\u5f88\u591a\u624d\u6c14\u7eb5\u6a2a\u3002 \u3000\u3000\u8c0b\u7565\u5353\u7edd\u7684\u9ad8\u4eba\uff0c\u4f46\u5374\u4ece\u672a\u89c1\u8fc7\u50cf\u9a6c\u6587\u4e2d\u8fd9\u6837\u64c5\u4e8e\u4f2a\u88c5\u3001\u8868\u91cc\u5b8c\u5168\u4e0d\u540c\u7684\u4eba\uff0c\u4f60\u5f88\u96be\u6d4b \u900f\u4ed6\u5fc3\u4e2d\u7a76\u7adf\u5728\u60f3\u4e9b\u4ec0\u4e48\uff1f\u5f53\u7136\uff0c\u4e5f\u65e0\u6cd5\u5224\u65ad\u4ed6\u7684\u76ee\u7684\u4f55\u5728\uff1f\u201c \u3000\u3000\u201c\u6c5f\u5144\uff0c\u8bf7\u8bf4\u660e\u767d\uff0c\u4f60\u7a76\u7adf\u6000\u7591\u9a6c\u6587\u4e2d\u6709\u4ec0\u4e48\u9634\u8c0b\uff1f\u201d \u3000\u3000\u201c\u6211\u6000\u7591\u4ed6\u65e9\u5df2\u5728\u4e2d\u539f\u6b66\u6797\u9053\u4e0a\u6697\u4e2d\u57f9\u690d\u52bf\u529b\u2026\u2026\u201d\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u5148\u884c\u63a7\u5236\u4e86\u6c5f\u6e56\u4eba \u7269\uff0c\u624d\u653e\u5fc3\u5236\u670d\u5404\u5730\u5927\u540f\uff0c\u8ba9\u4ed6\u4eec\u542c\u547d\u884c\u4e8b\uff0c\u518d\u6d3e\u9ad8\u624b\u6e17\u5165\u4eac\u757f\u3002\u201d \u3000\u3000\u201c\u90a3\u8fd8\u5f97\u4e86\uff0c\u5c82\u4e0d\u662f\u5168\u56fd\u7684\u519b\u653f\u90fd\u88ab\u4ed6\u63a7\u5236\u4e86\uff1f\u201d \u3000\u3000\u201c\u6b63\u662f\u5982\u6b64\uff01\u201d\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u9a6c\u6587\u4e2d\u7684\u806a\u660e\u662f\uff0c\u4ed6\u80fd\u770b\u6e05\u5c40\u52bf\uff0c\u4ee5\u76ee\u4e0b\u5927\u660e\u671d\u5f3a\u76db\u7684\u56fd \u52bf\uff0c\u4e3e\u4e16\u65e0\u53ef\u5339\u654c\uff0c\u4f46\u4ed6\u4eec\u5148\u4ece\u4e2d\u539f\u6b66\u6797\u540c\u9053\u4e0b\u624b\uff0c\u4e00\u5207\u5728\u6697\u4e2d\u8fdb\u884c\uff0c\u9c7c\u5316\u9f99\u593a\u4e0b\u6d1e\u5ead\u4e00\u5341 \u516b\u6c34\u5be8\u7684\u603b\u5be8\u4e3b\uff0c\u5c31\u662f\u660e\u663e\u7684\u9634\u8c0b\u4e4b\u4e00\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u6b64\u4e8b\u5173\u7cfb\u91cd\u5927\uff0c\u5343\u91cc\u5144\u8981\u4ed4\u7ec6\u7684\u67e5\u660e\u624d\u884c\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u5401\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u53ea\u662f\u5728\u4e0b\u7684\u731c\u6d4b\u4e4b\u8a00\uff0c\u6211\u76f8\u4fe1\u867d\u4e0d\u4e2d\u4ea6\u4e0d\u8fdc\u77e3\uff0c\u738b\u5144\u4e0d\u59a8\u518d \u7a0d\u4f5c\u6c42\u8bc1\uff0c\u518d\u4f5c\u5e94\u53d8\u7684\u51c6\u5907\u3002\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u597d\uff01\u6211\u8fd9\u5c31\u52a8\u8eab\u8d76\u56de\u4eac\u4e2d\uff0c\u8001\u5b9e\u8bf4\uff0c\u4f1a\u6709\u4e00\u4e2a\u4ec0\u4e48\u6837\u7684\u7ed3\u679c\uff0c\u5144\u5f1f\u5fc3\u4e2d\u5168\u65e0 \u628a\u63e1\u3002\u201d \u3000\u3000\u201c\u96be\u9053\u7687\u4e0a\u4f1a\u4e0d\u987e\u4e09\u516c\u4e3b\u7684\u5b89\u5371\u4e48\uff1f\u201d \u3000\u3000\u201c\u5929\u5a01\u96be\u6d4b\u554a\uff01\u5b98\u573a\u4e0a\u7684\u9634\u9669\u3001\u5be1\u60c5\uff0c\u6bd4\u4e4b\u6c5f\u6e56\uff0c\u6709\u8fc7\u4e4b\u800c\u65e0\u4e0d\u53ca\u3002\u201d\u738b\u5f64\u5401\u4e86\u4e00\u53e3 \u6c14\uff0c\u9053\uff1a\u201c\u4e0d\u7ba1\u5982\u4f55\uff0c\u7687\u4e0a\u4e00\u5b9a\u4f1a\u6709\u4e00\u4e2a\u5904\u7f6e\u7684\u3002\u201d \u3000\u3000\u201c\u4f1a\u4e0d\u4f1a\u5728\u9707\u6012\u4e4b\u4e0b\uff0c\u4e00\u9053\u5723\u65e8\uff0c\u8c03\u52a8\u4e86\u5175\u9a6c\uff0c\u653b\u6253\u5f00\u5c01\u5462\uff1f \u3000\u3000\u2026\u2026\u201c\u6c5f\u5343\u91cc\u9053\uff1a\u201d\u90a3\u5c82\u4e0d\u662f\u6fc0\u6012\u9a6c\u6587\u4e2d\uff0c\u800c\u6740\u4e86\u5fc3\u5370\u3001\u738b\u91cd\u5c71\u7b49\u2026\u2026\u201c \u3000\u3000\u201c\u6211\u6700\u6015\u7684\u5c31\u662f\u8fd9\u4ef6\u4e8b\u4e86\uff0c\u5982\u82e5\u95f9\u51fa\u8fd9\u6837\u4e00\u4e2a\u7ed3\u679c\u6765\uff0c\u5144\u5f1f\u771f\u4e0d\u77e5\u8be5\u5982\u4f55\u81ea\u5904\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u65e0\u8bba\u5982\u4f55\uff0c\u4f60\u8981\u5c3d\u529b\u8bf4\u670d\u7687\u4e0a\uff0c\u8fd8\u6709\u4e09\u516c\u4e3b\u7684\u751f\u6b7b\u4f5c\u6ce8\uff0c\u6211\u76f8\u4fe1\u7687\u4e0a\u4e5f\u4f1a \u7528\u4e9b\u5fc3\u601d\u60f3\u60f3\u7684\u3002\u201d \u3000\u3000\u201c\u6211\u4f1a\u5c3d\u5168\u529b\u4e00\u8bd5\uff01\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u597d\uff01\u6211\u5728\u5f00\u5c01\u7b49\u4f60\uff0c\u5982\u82e5\u6709\u673a\u4f1a\u6211\u4f1a\u7ea6\u51e0\u4e2a\u5e2e\u624b\u6765\uff0c\u4e0d\u77e5\u4e09\u5144\u51e0\u65f6\u53ef\u4ee5\u56de \u6765\uff1f\u201d \u3000\u3000\u201c\u9a6c\u6587\u4e2d\u9650\u5236\u6211\u4e8c\u5341\u5929\u5185\u8d76\u56de\u5f00\u5c01\u3002\u201d \u3000\u3000\u201c\u6700\u597d\u4f60\u80fd\u5728\u9650\u671f\u4e4b\u524d\u5148\u5230\u5f00\u5c01\uff0c\u6211\u4eec\u5148\u89c1\u4e00\u6b21\u9762\u3002\u201d\u6c5f\u5e72\u91cc\u4f4e\u58f0\u5730\u9053\uff1a\u201c\u6b64\u523b\u6ce2\u8c32\u4e91 \u8be1\uff0c\u4e8b\u60c5\u3001\u5c40\u52bf\u7684\u53d8\u5316\uff0c\u5e76\u975e\u5e38\u60c5\u6240\u53ef\u4ee5\u9884\u6599\u7684\u3002\u201d \u3000\u3000\u738b\u5f64\u5fc3\u4e2d\u4e00\u52a8\uff0c\u9053\uff1a\u201c\u96be\u9053\u6c5f\u5144\u6709\u4ec0\u4e48\u65b0\u7684\u53d1\u73b0\uff1f\u201d \u3000\u3000\u201c\u9a6c\u6587\u4e2d\u5750\u9547\u5f00\u5c01\uff0c\u4f3c\u662f\u4e3b\u6301\u5927\u8ba1\u4e4b\u4eba\u2026\u2026\u201d\u6c5f\u5343\u91cc\u795e\u60c5\u8083\u7136\u7684\u8bf4\uff1a\u201c\u4f46\u5374\u672a\u5fc5\u662f\u771f\u6b63 \u80fd\u591f\u4f5c\u4e3b\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u6c5f\u5144\u7684\u610f\u601d\u662f\u8bf4\uff0c\u8fd9\u5f00\u5c01\u7684\u5730\u9762\u4e0a\uff0c\u8fd8\u6709\u8eab\u4efd\u9ad8\u8fc7\u9a6c\u6587\u4e2d\u7684\u4eba\u4e48\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u70b9\u70b9\u5934\u3002 \u3000\u3000\u201c\u4f1a\u662f\u4ec0\u4e48\u4eba\u5462\uff1f\u96be\u9053\u96f7\u97f3\u5bfa\u4e2d\u8fd8\u6709\u4e2a\u8eab\u4efd\u66f4\u9ad8\u7684\u4eba\u5728\u6b64\uff01\u201d\u738b\u5f64\u7a81\u7136\u60f3\u5230\u4e86\u60dc\u6625\u7684\u8eab \u4efd\uff0c\u7acb\u523b\u6539\u53e3\u8bf4\u9053\uff1a\u201c\u60dc\u6625\u662f\u9b54\u6559\u4e2d\u4eba\uff0c\u6f5c\u4f0f\u7687\u5bab\u4e94\u5e74\u4e4b\u4e45\uff0c\u5165\u9009\u4e3a\u4e09\u516c\u4e3b\u7684\u4f8d\u5973\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u5c31\u662f\u4e86\uff0c\u80fd\u6d3e\u4eba\u6f5c\u5165\u7687\u5bab\u4e2d\uff0c\u663e\u7136\u9b54\u6559\u7edd\u975e\u53ea\u6709\u4e00\u6761\u8def\u7ebf\u7684\u5b89\u6392\uff0c\u738b\u89c1\u4e0d\u8981\u5206\u5fc3\uff0c \u5148\u56de\u4eac\u529e\u4e8b\uff0c\u5144\u5f1f\u4f1a\u67e5\u6e05\u695a\u5f00\u5c01\u5730\u9762\u7684\u5f62\u52bf\uff0c\u518d\u89c1\u9762\u65f6\uff0c\u5b9a\u7136\u5411\u738b\u5144\u6709\u4e2a\u4ea4\u4ee3\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u4ed6\u4eec\u7cbe\u660e\u5f97\u5f88\uff0c\u6c5f\u5144\u8981\u591a\u591a\u5c0f\u5fc3\u4e86\u3002\u201d \u3000\u3000\u201c\u660e\u67aa\u660e\u5200\u6211\u4e5f\u8bb8\u4e0d\u662f\u9a6c\u6587\u4e2d\u7684\u654c\u624b\uff0c\u4f46\u5982\u8bb2\u6f5c\u8e2a\u9690\u8ff9\u4e4b\u672f\uff0c\u5144\u5f1f\u81ea\u4fe1\u53ef\u4ee5\u5728\u5f00\u5c01\u5e9c\u6765 \u53bb\u81ea\u5982\uff0c\u5012\u662f\u738b\u5144\u8981\u5b89\u6392\u4e00\u4e0b\uff0c\u5982\u4f55\u5148\u4e24\u5929\u6f5c\u5165\u5f00\u5c01\u800c\u4e0d\u4e3a\u4eba\u53d1\u89c9\u3002\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u6211\u4f1a\u7528\u5fc3\u5b89\u6392\uff0c\u4e0d\u8bba\u4e8b\u60c5\u5982\u4f55\uff0c\u6211\u518d\u6765\u5f00\u5c01\u65f6\uff0c\u4e00\u5b9a\u4f1a\u5c3d\u51fa\u7cbe\u9510\uff0c\u4f46\u4e0d\u77e5\u5982 \u4f55\u548c\u6c5f\u5144\u89c1\u9762\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u738b\u5144\u77e5\u9053\u9ec4\u6cb3\u5927\u9556\u5c40\u5427\uff1f\u201d \u3000\u3000\u738b\u5f64\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u77e5\u9053\uff0c\u636e\u8bf4\u603b\u9556\u5934\u674e\u9752\u7af9\uff0c\u662f\u4e2d\u539f\u6b66\u6797\u9053\u4e0a\uff0c\u8033\u76ee\u6700\u4e3a\u7075\u654f\u7684\u4eba \u7269\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\uff0c\u4ed6\u5df2\u7ecf\u89e3\u6563\u4e86\u7f25\u5c40\u2026\u2026\u201d \u3000\u3000\u6c5f\u5343\u91cc\u53f9\u4e86\u53e3\u6c14\uff0c\u53c8\u9053\uff1a\u201c\u4ed6\u606f\u9690\u5c71\u6797\uff0c\u79bb\u5f00\u4e86\u5f00\u5c01\uff0c\u4e0d\u8fc7\uff0c\u90a3\u5ea7\u7f25\u5c40\u7684\u623f\u820d\u5e76\u6ca1\u6709\u51fa \u552e\uff0c\u6210\u4e86\u4e00\u5ea7\u7a7a\u5b85\uff0c\u7531\u4eca\u5929\u7b97\u8d77\uff0c\u7b2c\u5341\u516b\u5929\u5348\u591c\u4e09\u66f4\u65f6\u5206\uff0c\u54b1\u4eec\u4ee5\u767d\u5e03\u4f69\u80f8\u4e3a\u8bb0\uff0c\u5728\u90a3\u91cc\u89c1 \u9762\u3002\u201d \u3000\u3000\u738b\u5f64\u95ee\u660e\u8be6\u60c5\uff0c\u9053\uff1a\u201c\u4e00\u8a00\u4e3a\u5b9a\uff0c\u6c5f\u5144\u4fdd\u91cd\uff01\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u5144\u5f1f\u606d\u5019\u4e24\u591c\uff0c\u8fc7\u65f6\u5c31\u53d6\u6d88\u8fd9\u4e2a\u7ea6\u4f1a\uff0c\u544a\u8f9e\u4e86\uff01\u201d \u3000\u3000\u6258\u8d77\u4e86\u6728\u76d8\uff0c\u9000\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u738b\u5f64\u559d\u4e86\u4e24\u676f\u9152\uff0c\u624d\u7f13\u6b65\u884c\u51fa\u5ba2\u6808\u3002 \u3000\u3000\u4ed6\u76f8\u4fe1\u9a6c\u6587\u4e2d\u548c\u9c7c\u5316\u9f99\u5fc5\u7136\u6709\u5f88\u7cbe\u5bc6\u7684\u5b89\u6392\uff0c\u7528\u4e0d\u7740\u81ea\u5df1\u82b1\u5fc3\u601d\u53bb\u627e\u3002 \u3000\u3000\u679c\u7136\uff0c\u4ed6\u4e00\u8d70\u51fa\u5ba2\u6808\uff0c\u7acb\u523b\u5c31\u6709\u4e00\u4e2a\u8f66\u592b\u6a21\u6837\u7684\u4eba\uff0c\u8fce\u4e86\u4e0a\u6765\uff0c\u9053\uff1a\u201c\u738b\u7237\uff01\u5750\u8f66\u8fd8\u662f \u9a91\u9a6c\uff1f\u201d \u3000\u3000\u4e00\u53e3\u8bf4\u51fa\u4e86\u59d3\u6c0f\uff0c\u663e\u7136\u662f\u9a6c\u6587\u4e2d\u5b89\u6392\u597d\u7684\u4eba\u624b\uff0c\u5fae\u4e00\u9894\u9996\uff0c\u9053\uff1a\u201c\u5148\u5750\u8f66\uff0c\u6e21\u8fc7\u9ec4\u6cb3\u4e4b \u540e\uff0c\u518d\u5feb\u9a6c\u517c\u7a0b\u8d76\u8def\uff0c\u4f60\u4eec\u51c6\u5907\u597d\u4e86\u4e48\uff1f\u201d \u3000\u3000\u201c\u597d\u4e86\uff0c\u56db\u5957\u5065\u9a6c\u5feb\u8f66\uff0c\u53e6\u5916\u6709\u4e24\u5339\u957f\u7a0b\u5065\u9a6c\u5907\u7528\u3002\u201d \u3000\u3000\u738b\u5f64\u5fc3\u4e2d\u4e00\u52a8\uff0c\u9053\uff1a\u201c\u6211\u4e00\u8def\u4e58\u8f66\uff0c\u8d76\u5230\u4eac\u4e2d\u5927\u7ea6\u8981\u591a\u5c11\u65f6\u95f4\u3002\u201d \u3000\u3000\u201c\u6cbf\u9014\u66f4\u6362\u9a6c\u5339\uff0c\u65e5\u591c\u4e0d\u505c\u7684\u8d76\u8def\uff0c\u884c\u7a0b\u4e2d\u6ca1\u6709\u610f\u5916\uff0c\u4e94\u5929\u4e94\u591c\u53ef\u5230\uff0c\u5bbd\u88d5\u4e00\u70b9\u7b97\uff0c\u7b2c \u516d\u5929\u4e2d\u5348\u4e00\u5b9a\u53ef\u4ee5\u8d76\u5230\u4eac\u57ce\u4e4b\u5185\u3002\u201d \u3000\u3000\u738b\u5f64\u5fc3\u4e2d\u5fd6\u9053\uff1a\u8fd9\u4e9b\u7275\u9a6c\u8d76\u8f66\u7684\u4eba\uff0c\u60f3\u6765\u90fd\u662f\u9a6c\u6587\u4e2d\u7684\u5fc3\u8179\u624b\u4e0b\uff0c\u65e2\u7136\u662f\u5728\u91cd\u91cd\u76d1\u89c6\u4e4b \u4e0b\uff0c\u4f55\u4e0d\u8eba\u5728\u8f66\u4e2d\u597d\u597d\u4f11\u606f\u4e00\u4e0b\uff0c\u4e5f\u53ef\u514d\u53bb\u9a6c\u6587\u4e2d\u5fc3\u5185\u4e4b\u7591\u3002 \u3000\u3000\u5f53\u4e0b\u4fbf\u8bf4\u9053\uff1a\u201c\u5982\u662f\u5750\u8f66\u80fd\u4f9d\u65f6\u9650\u62b5\u8fbe\u4eac\u57ce\uff0c\u6211\u5c31\u4e00\u8def\u5750\u8f66\u597d\u4e86\u3002\u201d \u3000\u3000\u201c\u738b\u7237\u653e\u5fc3\uff0c\u5c0f\u7684\u7edd\u4e0d\u8bef\u4e8b\u3002\u201d \u3000\u3000\u738b\u5f64\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u9a6c\u5de1\u629a\u9650\u4ee4\u6211\u4e8c\u5341\u5929\u5982\u671f\u8d76\u56de\u5f00\u5c01\uff0c\u8def\u4e0a\u8981\u662f\u6709\u4e86\u803d\u6401\uff0c\u53ea\u6015\u8bf8\u4f4d \u4e5f\u8981\u53d7\u5230\u7275\u8fde\u4e86\u3002\u201d \u3000\u3000\u90a3\u8f66\u592b\u626e\u88c5\u7684\u5927\u6c49\uff0c\u5fae\u5fae\u4e00\u6014\uff0c\u9053\uff1a\u201c\u738b\u7237\u5b89\u5fc3\uff0c\u8def\u4e0a\u5982\u6709\u53d8\u5316\uff0c\u6211\u4eec\u4f1a\u7acb\u523b\u8f6c\u544a\u738b\u7237 \u88c1\u793a\u3002\u201d \u3000\u3000\u738b\u5f64\u51b7\u51b7\u5730\u9053\uff1a\u201c\u5f53\u7136\uff0c\u4e5f\u8981\u7acb\u523b\u7980\u62a5\u9a6c\u5de1\u629a\uff0c\u5426\u5219\uff0c\u5ef6\u8bef\u4e86\u4e8b\u673a\uff0c\u5982\u4f55\u5f97\u4e86\u3002\u201d \u3000\u3000\u5728\u738b\u5f64\u76ee\u5149\u903c\u89c6\u4e4b\u4e0b\uff0c\u90a3\u5927\u6c49\u53ea\u597d\u70b9\u70b9\u5934\u627f\u8ba4\u4e0b\u6765\u3002 \u3000\u3000\u201c\u8fd9\u6211\u5c31\u653e\u5fc3\u4e86\u2026\u2026\u201d\u738b\u5f64\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53ef\u4ee5\u4e0a\u8def\u4e86\uff0c\u5982\u80fd\u5728\u8f66\u4e2d\u5907\u4e9b\u7f8e\u9152\u3001\u4f73\u80b4\uff0c \u5728\u4e0b\u8fde\u996d\u4e5f\u4e0d\u7528\u4e0b\u8f66\u5403\u4e86\u3002\u201d \u3000\u3000\u201c\u771f\u662f\u5de7\u5f97\u5f88\uff0c\u90a3\u7bf7\u8f66\u4e0d\u4f46\u53ef\u5750\u53ef\u5367\uff0c\u800c\u4e14\u8fd8\u5e26\u4e86\u5f88\u591a\u597d\u9152\uff0c\u738b\u7237\u60f3\u559d\u51e0\u676f\uff0c\u90a3\u771f\u5f97\u5176 \u6240\u5728\u4e86\u2026\u2026\u201d \u3000\u3000\u8f66\u592b\u88c5\u626e\u7684\u5927\u6c49\u8bf4\u5b8c\u8bdd\uff0c\u4e3e\u624b\u4e00\u62db\uff0c\u4e00\u8f86\u56db\u9a6c\u7275\u5f15\u7684\u7bf7\u8f66\uff0c\u8fc5\u5feb\u7684\u9a70\u4e86\u8fc7\u6765\uff0c\u65e9\u5df2\u6709\u4e00 \u4e2a\u5168\u8eab\u9ed1\u8863\u3001\u624b\u6267\u957f\u97ad\u7684\u8f66\u592b\u9ad8\u5750\u5728\u8f66\u95e8\u4e4b\u524d\u3002 \u3000\u3000\u90a3\u7bf7\u8f66\u505c\u5728\u4e00\u4e2a\u5df7\u5b50\u91cc\uff0c\u738b\u5f64\u51fa\u4e86\u5ba2\u6808\uff0c\u66fe\u7ecf\u56db\u4e0b\u77a7\u4e86\u4e00\u9635\uff0c\u7adf\u672a\u77a7\u5230\u3002 \u5367\u9f99\u751f\u300a\u71d5\u5b50\u4f20\u5947\u300b \u7b2c \u4e5d \u56de\u3000\u84c4\u52bf\u5f85\u53d1 \u3000\u3000\u53ea\u89c1\u8f66\u5e18\u4f4e\u5782\uff0c\u5e18\u5b50\u524d\u6709\u4e24\u4e2a\u94fa\u6709\u8f6f\u57ab\u7684\u5750\u4f4d\uff0c\u5f88\u660e\u663e\u7684\u6709\u4e24\u4e2a\u8f66\u592b\u540c\u5750\u9a7e\u8f66\u3002 \u3000\u3000\u8fce\u63a5\u738b\u5f64\u7684\u5927\u6c49\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u738b\u7237\uff0c\u5c0f\u7684\u53eb\u9a6c\u4e09\uff0c\u8f66\u4e0a\u90a3\u4f4d\u53eb\u9a6c\u4e94\uff0c\u4f60\u9700\u8981\u4ec0\u4e48\u62db\u547c\u4e00 \u58f0\u5c31\u662f\u3002\u201d \u3000\u3000\u4f38\u624b\u642d\u8d77\u5782\u5e18\uff0c\u63a5\u9053\uff1a\u201c\u8bf7\u4e0a\u8f66\u5427\uff01\u201d \u3000\u3000\u201c\u55ef\uff01\u9a6c\u4e09\u3001\u9a6c\u4e94\uff0c\u4e24\u4f4d\u90fd\u662f\u9a6c\u5927\u4eba\u7684\u4eb2\u968f\u4e86\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u201d\u9a6c\u4e94\u9053\uff1a\u201c\u5de1\u629a\u5927\u4eba\u5429\u5490\uff0c\u738b\u7237\u662f\u8d35\u5bbe\uff0c\u4e0d\u80fd\u6162\u5f85\uff0c\u6240\u4ee5\uff0c\u8981\u5c0f\u7684\u4eec\u968f\u884c\u7167 \u987e\u3002\u201d \u3000\u3000\u738b\u5f64\u767b\u4e0a\u7bf7\u8f66\uff0c\u76ee\u5149\u8f6c\u52a8\uff0c\u53d1\u73b0\u5b9e\u5728\u662f\u4e00\u4e2a\u5f88\u8212\u670d\u7684\u5730\u65b9\uff0c\u8f66\u8eab\u5bbd\u5927\uff0c\u957f\u5ea6\u4e5f\u591f\uff0c\u5916\u9762 \u4e0d\u600e\u4e48\u8d77\u773c\uff0c\u4f46\u5185\u90e8\u5374\u6781\u5c3d\u8c6a\u534e\u3002 \u3000\u3000\u8f66\u5185\u7684\u7f8a\u6bdb\u5730\u6be1\u4e0a\uff0c\u6709\u4e00\u5f20\u53ef\u4ee5\u7761\u89c9\u7684\u8f6f\u69bb\uff0c\u4e00\u5f20\u5c0f\u5de7\u7684\u6c34\u51e0\u6a2a\u5728\u8f6f\u69bb\u4e4b\u524d\uff0c\u7ea2\u7eeb\u9992\u906e \u7740\u7684\u6728\u67b6\u4e0a\uff0c\u6446\u4e86\u5341\u51e0\u74f6\u9152\u3002 \u3000\u3000\u7bf7\u8f66\u7f13\u7f13\u542f\u52a8\uff0c\u8d8a\u8d70\u8d8a\u5feb\uff0c\u4f46\u738b\u5f64\u5374\u6ca1\u6709\u98a0\u52a8\u7684\u611f\u89c9\u3002 \u3000\u3000\u6562\u60c5\u8fd9\u7bf7\u8f66\u7684\u627f\u8f74\u662f\u7279\u522b\u5236\u9020\u7684\uff0c\u88c5\u4e0a\u4e86\u5f3a\u529b\u7684\u5f39\u7c27\uff0c\u518d\u52a0\u4e0a\u4e24\u5bf8\u539a\u7684\u5730\u6be1\uff0c\u8f66\u4e0a\u7684\u4eba \u5df2\u6ca1\u6709\u5750\u8f66\u7684\u611f\u89c9\u3002 \u3000\u3000\u738b\u5f64\u6697\u4e2d\u5f88\u7559\u5fc3\u7684\u6253\u91cf\u8fc7\u9a6c\u4e09\u3001\u9a6c\u4e94\uff0c\u53d1\u89c9\u4ed6\u4eec\u773c\u795e\u51cc\u5389\uff0c\u7cbe\u6c14\u5145\u6c9b\uff0c\u5206\u660e\u662f\u5185\u5916\u517c\u4fee \u7684\u9ad8\u624b\uff0c\u968f\u884c\u7167\u987e\u4e5f\u662f\u76d1\u89c6\uff0c\u5f53\u7136\u4e5f\u6709\u4fdd\u62a4\u7684\u610f\u601d\u3002 \u3000\u3000\u9a6c\u6587\u4e2d\u89e3\u53bb\u738b\u5f64\u8eab\u4e0a\u7684\u7981\u5236\uff0c\u4f7f\u4ed6\u6062\u590d\u4e86\u51e0\u6210\u529f\u529b\uff0c\u4f46\u4ed6\u4e00\u76f4\u6ca1\u6709\u673a\u4f1a\u6d4b\u8bd5\uff0c\u6b64\u523b\u76d8\u5750 \u8c03\u606f\uff0c\u8fd0\u6c14\u884c\u52a8\uff0c\u7acb\u65f6\u611f\u89c9\u4e39\u7530\u5904\u6709\u6ede\u6b62\u7684\u611f\u89c9\uff0c\u5185\u529b\u65e0\u6cd5\u7545\u884c\u767e\u8109\u3002 \u3000\u3000\u738b\u5f64\u4e0d\u7981\u9eef\u7136\u4e00\u53f9\uff0c\u5fd6\u9053\uff1a\u73b0\u5728\uff0c\u4e0d\u8bba\u662f\u9a6c\u4e09\u8fd8\u662f\u9a6c\u4e94\uff0c\u4efb\u4f55\u4e00\u4e2a\u4eba\u90fd\u53ef\u4ee5\u8f7b\u6613\u7684\u5236\u670d \u6211\u4e86\u3002 \u3000\u3000\u5929\u8272\u6697\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u7bf7\u8f66\u4e2d\u66f4\u662f\u4e00\u7247\u6f06\u9ed1\u3002 \u3000\u3000\u8033\u9645\u95f4\u6c34\u58f0\u6ed4\u6ed4\uff0c\u7bf7\u8f66\u4f3c\u662f\u9a70\uff3f\u4e0a\u4e86\u4e00\u8258\u5927\u8239\u3002 \u3000\u3000\u7389\u5f64\u770b\u4e5f\u61d2\u5f97\u770b\u4e86\uff0c\u5fc3\u4e2d\u60f3\u7684\u662f\u5982\u4f55\u89e3\u9664\u8eab\u4e0a\u7684\u7981\u5236\u3002 \u3000\u3000\u4ed6\u7ecf\u9a8c\u4e30\u5bcc\uff0c\u5df2\u611f\u89c9\u5230\u4e0d\u662f\u88ab\u4e00\u822c\u622a\u8109\u5236\u7a74\u624b\u6cd5\u6240\u4f24\uff0c\u80fd\u5426\u89e3\u9664\u8eab\u4e0a\u7981\u5236\uff0c\u5b8c\u5168\u6ca1\u6709\u628a \u63e1\u3002 \u3000\u3000\u8fd9\u5a01\u80c1\u592a\u5927\u4e86\uff0c\u8868\u9762\u4e0a\u770b\u6765\u4ed6\u6beb\u53d1\u65e0\u4f24\uff0c\u4f46\u4e8b\u5b9e\u4e0a\uff0c\u5341\u6210\u5185\u529b\u5927\u6982\u4e5f\u53ea\u80fd\u7528\u51fa\u4e09\u6210\u800c \u5df2\u3002 \u3000\u3000\u8fd9\u4e5f\u4f7f\u738b\u5f64\u85cf\u8d77\u4e86\u5728\u9014\u4e2d\u548c\u5bab\u536b\u8054\u7edc\u7684\u6253\u7b97\uff0c\u5168\u526f\u7cbe\u795e\u7528\u5728\u89e3\u9664\u7981\u5236\u65b9\u9762\u3002 \u3000\u3000\u5f88\u53ef\u60dc\uff0c\u738b\u5f64\u4e94\u65e5\u591c\u5750\u5728\u8f66\u4e2d\uff0c\u9664\u4e86\u4fbf\u6eba\u79bb\u5f00\u8f66\u4e2d\u7247\u523b\u4e4b\u5916\uff0c\u98df\u3001\u5bbf\u90fd\u672a\u66fe\u79bb\u5f00\u8fc7\u8f66\uff0c \u4f46\u5374\u672a\u89e3\u5f00\u8eab\u4e0a\u7981\u5236\u3002 \u3000\u3000\u9a6c\u4e09\u3001\u9a6c\u4e94\u9664\u4e86\u5e94\u4f20\u5949\u4f8d\u4e4b\u5916\uff0c\u4ece\u4e0d\u591a\u8bdd\uff0c\u4e00\u76f4\u5230\u8f66\u5165\u4eac\u57ce\uff0c\u9a6c\u4e09\u624d\u9694\u7740\u7a97\u5e18\u9053\uff1a\u201c\u5df2 \u5165\u4eac\u57ce\uff0c\u8bf7\u738b\u7237\u88c1\u793a\u3002\u201d \u3000\u3000\u201c\u8d70\u4e86\u51e0\u5929\u2026\u2026\u201d \u3000\u3000\u201c\u4e94\u5929\u4e94\u591c\uff0c\u9014\u4e2d\u4e09\u6b21\u6613\u9a6c\uff0c\u4e94\u6b21\u6253\u5c16\uff0c\u8fd9\u65f6\u662f\u7b2c\u516d\u5929\u4e2d\u5348\u65f6\u5206\u3002\u201d\u9a6c\u4e09\u9053\uff1a\u201c\u5e78\u672a\u8fb1 \u738b\u7237\u4e4b\u547d\u3002\u201d \u3000\u3000\u201c\u5f88\u597d\uff0c\u9a6c\u6587\u4e2d\u8fd9\u8f86\u5750\u8f66\u8212\u9002\u6781\u4e86\uff0c\u4e0d\u77e5\u4e24\u4f4d\u662f\u5426\u4f1a\u5728\u4eac\u4e2d\u7b49\u6211\uff1f\u201d \u3000\u3000\u201c\u738b\u7237\u7684\u610f\u601d\u662f\u2026\u2026\u201d\u9a6c\u4e09\u4f4e\u58f0\u95ee\u3002 \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u5357\u4e0b\u4e4b\u65f6\uff0c\u6211\u60f3\u4ecd\u5750\u6b64\u8f66\uff0c\u4e0d\u77e5\u4e24\u4f4d\u610f\u4e0b\u5982\u4f55\uff1f\u201d \u3000\u3000\u9a6c\u4e09\u9053\uff1a\u201c\u5357\u4e0b\u884c\u7a0b\uff0c\u6700\u591a\u516d\u5929\uff0c\u738b\u7237\u5728\u4eac\u5e08\u6709\u516b\u65e5\u505c\u7559\uff0c\u54b1\u4eec\u5728\u4e1c\u57ce\u95e8\u5916\u5929\u95e8\u5ba2\u6808\u5019 \u9a7e\uff0c\u7531\u4eca\u65e5\u7b97\u8d77\uff0c\u7b2c\u516b\u5929\u638c\u706f\u65f6\u5206\uff0c\u4ecd\u4e0d\u5f97\u738b\u7237\u6d88\u606f\uff0c\u6211\u4eec\u5c31\u81ea\u884c\u8d77\u7a0b\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u4e00\u8a00\u4e3a\u5b9a\uff01\u201d\u738b\u5f64\u6380\u5f00\u8f66\u5e18\uff0c\u4e0b\u8f66\u800c\u53bb\u3002 \u3000\u3000\u4f46\u738b\u5f64\u5fc3\u4e2d\u5374\u6697\u6697\u5fd6\u9053\uff1a\u5929\u95e8\u5ba2\u6808\uff0c\u5927\u6982\u662f\u9a6c\u6587\u4e2d\u5728\u4eac\u4e2d\u8054\u7edc\u7684\u6240\u5728\u4e86\uff0c\u5982\u6709\u5f81\u527f\u884c \u52a8\uff0c\u5fc5\u5f97\u5148\u7834\u53bb\u4ed6\u5728\u4eac\u4e2d\u7684\u8033\u76ee\u3002 \u3000\u3000\u7687\u4e0a\u5728\u5fa1\u4e66\u623f\u53ec\u89c1\u738b\u5f64\uff0c\u542c\u5b8c\u4e86\u738b\u5f64\u7684\u594f\u8ff0\u4e4b\u540e\uff0c\u9887\u6709\u96be\u8272\uff0c\u4e00\u65f6\u4e5f\u771f\u65e0\u6cd5\u62ff\u51fa\u4e2a\u4e3b\u610f \u6765\u3002 \u3000\u3000\u7687\u4e0a\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u624d\u9053\uff1a\u201c\u6b64\u4e8b\u4e0d\u5b9c\u5f20\u626c\uff0c\u6715\u89c9\u5f97\u8fd8\u662f\u4ee5\u4f60\u4eec\u6c5f\u6e56\u4e0a\u7684\u65b9\u6cd5\u6765\u5904\u7f6e\u6700 \u597d\uff0c\u4e0d\u8fc7\u6211\u9881\u4e00\u9053\u5bc6\u65e8\u7ed9\u4f60\uff0c\u5fc5\u8981\u65f6\u53ef\u4ee5\u8c03\u52a8\u5404\u5730\u519b\u9a6c\u63f4\u52a9\uff0c\u64a4\u9000\u897f\u5317\u5927\u519b\uff0c\u5bb9\u6715\u63d0\u4ea4\u9601\u8bae \u540e\u518d\u9881\u65e8\u64a4\u519b\uff0c\u4f46\u4e09\u516c\u4e3b\u5fc5\u9700\u4fdd\u5168\uff0c\u4e0d\u5f97\u6709\u6beb\u53d1\u4e4b\u4f24\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u201d\u738b\u5f64\u9053\uff1a\u201c\u9a6c\u6587\u4e2d\u51fa\u8eab\u96f7\u97f3\u5bfa\uff0c\u90a3\u91cc\u4e3a\u897f\u65b9\u9b54\u6559\u4e4b\u5bab\uff0c\u6b66\u529f\u5947\u8be1\uff0c\u4f3c\u901a\u90aa\u672f\uff0c \u52bf\u529b\u975e\u5e38\u5e9e\u5927\uff0c\u5c31\u7b97\u5c3d\u51fa\u5185\u5bab\u4f8d\u536b\uff0c\u4e5f\u672a\u5fc5\u80fd\u591f\u548c\u4ed6\u4eec\u9889\u9883\u3002 \u3000\u3000\u7687\u4e0a\u76b1\u76b1\u7709\uff0c\u9053\uff1a\u201c\u90a3\u662f\u8bf4\uff0c\u4e00\u5b9a\u8981\u6d3e\u5927\u519b\u56f4\u653b\u5f00\u5c01\u4e86\uff1f\u201d \u3000\u3000\u738b\u5f64\u7b11\u7b11\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u7528\u4e86\uff0c\u4f8d\u81e3\u4e4b\u610f\uff0c\u8bf7\u7687\u4e0a\u9881\u9053\u624b\u6e1d\uff0c\u7740\u5382\u536b\u6682\u7531\u4f8d\u81e3\u8c03\u52a8\uff0c\u4ee5\u4e3a \u52a9\u529b\u2026\u2026\u201d \u3000\u3000\u201c\u597d\uff0c\u8fd9\u4ef6\u4e8b\u5bb9\u6613\uff0c\u6211\u7acb\u523b\u4e0b\u8bcf\uff0c\u8fd8\u6709\u4ec0\u4e48\u4e8b\u4e48\uff1f\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u7687\u4e0a\u5982\u80fd\u8d50\u81e3\u4e00\u9053\u5bc6\u65e8\uff0c\u5f81\u53ec\u6c5f\u6e56\u4e0a\u5404\u5927\u95e8\u6d3e\u4e2d\u4eba\uff0c\u52a9\u81e3\u9504\u9664\u9b54\u6559\u5e76\u6388\u81e3\u4ee5 \u5168\u6743\u5904\u7f6e\u9a6c\u5927\u4eba\u4e00\u6848\uff0c\u4e0d\u53d7\u5211\u3001\u5175\u4e24\u90e8\u7684\u5e72\u4e88\uff0c\u81e3\u5f53\u5168\u529b\u4ee5\u8d74\uff0c\u4ee5\u6700\u5c0f\u7684\u727a\u7272\u65e9\u7ed3\u6b64\u6848\uff0c\u518d \u8bf7\u7687\u4e0a\u653e\u81e3\u5f52\u7c4d\u3002\u201d \u3000\u3000\u201c\u5f52\u7c4d\u7684\u4e8b\uff0c\u4ee5\u540e\u518d\u8bf4\u3002\u201d\u7687\u4e0a\u6325\u6325\u624b\uff0c\u9053\uff1a\u201c\u5176\u4ed6\u7684\u4e8b\uff0c\u6715\u90fd\u51c6\u594f\u3002\u201d \u3000\u3000\u5723\u6069\u5df2\u6781\u6d69\u8361\uff0c\u738b\u5f64\u4e0d\u6562\u518d\u591a\u8bf7\u65e8\u3002 \u3000\u3000\u82b1\u4e86\u4e09\u5929\u7684\u65f6\u95f4\uff0c\u738b\u5f64\u7531\u5185\u5bab\u4e2d\u4f8d\u536b\u9009\u51fa\u4e86\u5341\u4e2a\u4eba\uff0c\u968f\u540c\u5357\u4e0b\uff0c\u4f46\u5374\u4ece\u5382\u536b\u4e2d\u8c03\u96c6\u4e86\u4e09 \u5341\u540d\u597d\u624b\uff0c\u8981\u4ed6\u4eec\u5148\u884c\u5357\u4e0b\u5f85\u547d\uff0c\u91cd\u65b0\u8bbe\u8ba1\u597d\u5bab\u4e2d\u7684\u9632\u536b\uff0c\u8fd9\u624d\u6362\u4e0a\u4fbf\u88c5\uff0c\u8d76\u5f80\u5929\u95e8\u5ba2\u6808 \u53bb\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u5bb6\u5e76\u4e0d\u8457\u540d\u7684\u5ba2\u6808\uff0c\u77d7\u7acb\u5728\u4e1c\u5173\u5916\u7684\u8352\u90ca\u8fb9\u7f18\u3002 \u3000\u3000\u4e5f\u5c31\u662f\u8bf4\uff0c\u8fc7\u4e86\u5929\u95e8\u5ba2\u6808\u5c31\u662f\u4e00\u5927\u7247\u7684\u7530\u56ed\uff0c\u5728\u5ba2\u6808\u7684\u540e\u9762\uff0c\u6709\u4e00\u7247\u6742\u6811\u6797\u548c\u4e07\u682a\u4fee\u7bc1 \u6742\u751f\u5728\u4e00\u8d77\u3002 \u3000\u3000\u770b\u8fc7\u4e86\u5468\u56f4\u7684\u5f62\u52bf\uff0c\u738b\u5f64\u5fc3\u4e2d\u5403\u60ca\uff0c\u5982\u82e5\u80fd\u5145\u5206\u5229\u7528\u90a3\u7247\u6811\u6797\u3001\u7af9\u5b50\u4ea4\u96c6\u800c\u6210\u7684\u6742\u6797\uff0c \u9690\u85cf\u4e2a\u5343\u628a\u4eba\u4e5f\u4e0d\u4f1a\u9732\u51fa\u75d5\u8ff9\u3002 \u3000\u3000\u679c\u7136\uff0c\u9a6c\u6587\u4e2d\u662f\u4e00\u4ee3\u9738\u4e3b\u4e4b\u624d\u3002 \u3000\u3000\u738b\u5f64\u6b65\u5165\u4e86\u5929\u95e8\u5ba2\u6808\uff0c\u610f\u5916\u7684\u662f\uff0c\u8fd9\u91cc\u7684\u751f\u610f\u5e76\u4e0d\u662f\u60f3\u50cf\u4e2d\u90a3\u4e48\u4e2a\u574f\u6cd5\uff0c\u5927\u5385\u5185\u3001\u9910\u5ba4 \u4e2d\u7adf\u7136\u5750\u6ee1\u4e86\u4eba\u3002 \u3000\u3000\u4f46\u5374\u6709\u4e00\u80a1\u5f3a\u70c8\u7684\u7f8a\u8089\u81bb\u5473\uff0c\u738b\u5f64\u7acb\u523b\u660e\u767d\uff0c\u8fd9\u91cc\u662f\u585e\u5916\u3001\u5927\u6f20\u6765\u4eba\u7684\u96c6\u4e2d\u6240\u5728\uff0c\u4e5f\u662f \u56de\u56de\u7684\u805a\u4f1a\u6240\u5728\u3002 \u3000\u3000\u738b\u5f64\u8fd8\u672a\u5f00\u53e3\uff0c\u9a6c\u4e09\u5df2\u8fce\u4e86\u4e0a\u6765\uff0c\u9053\uff1a\u201c\u738b\u7237\uff0c\u8d70\uff01\u4e8c\u697c\u5750\uff0c\u90a3\u91cc\u6709\u96c5\u5ba4\u3002\u201d \u3000\u3000\u6562\u60c5\u8fd9\u5ea7\u5929\u95e8\u5ba2\u6808\u7684\u524d\u534a\u90e8\uff0c\u662f\u4e00\u7816\u5230\u9876\u7684\u4e09\u697c\u5efa\u7b51\uff0c\u540e\u534a\u90e8\u5e73\u623f\u63a5\u8fde\uff0c\u9662\u843d\u5206\u660e\uff0c\u76f4 \u5230\u6811\u6797\u8fb9\u7f18\uff0c\u624d\u5efa\u8d77\u4e86\u4e00\u9053\u56f4\u5899\u3002 \u3000\u3000\u4e8c\u697c\u679c\u7136\u662f\u96c5\u5ea7\uff0c\u800c\u4e14\u623f\u95f4\u8bbe\u5907\u975e\u5e38\u8c6a\u534e\uff0c\u4f46\u66f4\u8ba9\u738b\u5f64\u5403\u60ca\u7684\u662f\uff0c\u63a5\u5f85\u5ba2\u4eba\u7684\u7adf\u7136\u662f\u5a07 \u8273\u52a8\u4eba\u7684\u7f8e\u4e3d\u5c11\u5973\u3002 \u3000\u3000\u5979\u4eec\u7a7f\u7740\u7684\u670d\u88c5\uff0c\u8bbe\u8ba1\u5f97\u5f88\u7279\u522b\uff0c\u4f3c\u662f\u6709\u610f\u628a\u5c11\u5973\u7684\u7f8e\u597d\u90e8\u4f4d\uff0c\u5938\u5f20\u7684\u66b4\u9732\u51fa\u6765\uff0c\u770b\u4e0a \u53bb\uff0c\u8fd9\u4e9b\u5973\u5b69\u5b50\u90fd\u5f88\u6027\u611f\u3001\u5145\u6ee1\u7740\u8bf1\u60d1\u3002 \u3000\u3000\u9a6c\u4e09\u70b9\u4e86\u51e0\u9053\u83dc\uff0c\u738b\u5f64\u867d\u7136\u89c1\u591a\u8bc6\u5e7f\uff0c\u4f46\u5374\u8fde\u90a3\u4e9b\u83dc\u540d\u5b57\u4e5f\u672a\u66fe\u542c\u8fc7\u3002\u201c \u3000\u3000\u83dc\u9001\u4e0a\u684c\u6765\uff0c\u738b\u5f64\u624d\u53d1\u89c9\u90fd\u662f\u725b\u3001\u7f8a\u8eab\u4e0a\u7684\u4e1c\u897f\uff0c\u4e0d\u8fc7\uff0c\u5473\u9053\u5947\u7279\uff0c\u4ece\u672a\u5403\u8fc7\uff0c\u611f\u89c9\u86ee \u4e0d\u9519\u7684\u3002 \u3000\u3000\u9a6c\u4e09\u656c\u8fc7\u4e24\u6b21\u9152\u540e\uff0c\u624d\u7b11\u7b11\u9053\uff1a\u201c\u8fd9\u4e9b\u53e3\u5473\uff0c\u738b\u7237\u662f\u5426\u5403\u8fc7\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\uff0c\u5473\u9053\u5947\u7279\uff0c\u4f46\u975e\u5e38\u53ef\u53e3\u3002\u201d \u3000\u3000\u201c\u8c22\u5566\uff01\u201d\u9a6c\u4e09\u9053\uff1a\u201c\u8fd9\u53ea\u4e0d\u8fc7\u662f\u666e\u901a\u7684\u725b\u3001\u7f8a\u8089\uff0c\u4f46\u56e0\u70f9\u8c03\u624b\u6cd5\u4e0d\u540c\uff0c\u5473\u9053\u624d\u4f1a\u5947 \u7279\uff0c\u8fd9\u662f\u9a6c\u5de1\u629a\u521b\u9020\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u201c\u60f3\u4e0d\u5230\uff0c\u9a6c\u6587\u4e2d\u5bf9\u5403\u7684\u8fd9\u4e00\u884c\uff0c\u7adf\u6709\u5982\u6b64\u7cbe\u6df1\u7684\u7814\u7a76\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u9a6c\u5927\u4eba\u5f88\u656c\u91cd\u738b\u7237\u7684\u4e3a\u4eba\uff0c\u5c0f\u7684\u4e5f\u5e0c\u671b\u80fd\u548c\u738b\u7237\u6210\u4e3a\u670b\u53cb\uff0c\u6240\u4ee5\uff0c\u6211\u4eec\u5e0c\u671b\u738b\u7237 \u5e26\u6765\u7684\u662f\u597d\u6d88\u606f\u3002\u201d\u9a6c\u4e09\u9053\uff1a\u201c\u5f53\u7136\uff0c\u6211\u4eec\u4e5f\u77e5\u9053\u738b\u7237\u4f5c\u4e0d\u4e86\u4e3b\uff0c\u8981\u5f53\u4eca\u5723\u4e0a\u88c1\u51b3\uff0c\u4e0d\u7ba1\u5982 \u4f55\uff0c\u6211\u4eec\u90fd\u627f\u738b\u7237\u7684\u60c5\u3002\u201d \u3000\u3000\u201c\u6211\u63d0\u524d\u8d76\u6765\u8fd9\u91cc\uff0c\u6d88\u606f\u5f53\u7136\u662f\u4e0d\u4f1a\u592a\u574f\uff0c\u5929\u5a01\u96be\u72af\uff0c\u6211\u738b\u5f64\u53ef\u662f\u5192\u7740\u6284\u5bb6\u706d\u65cf\u7684\u5927\u7f6a \u529b\u4e89\u5230\u7684\u3002\u201d \u3000\u3000\u201c\u771f\u662f\u8f9b\u82e6\u4e86\u738b\u7237\uff0c\u4eca\u4e2a\u5c0f\u7684\u8981\u597d\u597d\u62db\u5f85\u738b\u7237\u4e00\u6b21\uff0c\u5230\u5f00\u5c01\uff0c\u518d\u7531\u9a6c\u5927\u4eba\u6b63\u5f0f\u81f4\u8c22\u3002\u201d \u3000\u3000\u9a6c\u4e09\u8bf4\u7740\u8bdd\uff0c\u4eba\u4e5f\u7ad9\u4e86\u8d77\u6765\uff0c\u6df1\u6df1\u4e00\u4e2a\u957f\u63d6\u3002 \u3000\u3000\u201c\u6162\u6765\u2026\u2026\u6162\u6765\u2026\u2026\u201d\u738b\u5f64\u8bf4\uff1a\u201c\u4e8b\u60c5\u867d\u7136\u5df2\u5341\u4e4b\u516b\u4e5d\u4e86\uff0c\u4f46\u8fd8\u987b\u7b49\u51e0\u5929\u624d\u6709\u6b63\u5f0f\u6d88 \u606f\u3002\u201d \u3000\u3000\u9a6c\u4e09\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u8fd9\u8bdd\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u201c\u6210\u7956\u5148\u7687\u8ba2\u4e0b\u7684\u89c4\u77e9\uff0c\u519b\u56fd\u5927\u4e8b\u8981\u9601\u8bae\u901a\u8fc7\u624d\u884c\uff0c\u7687\u4e0a\u662f\u7b54\u5e94\u64a4\u519b\u4e86\uff0c\u4f46\u4ea4\u7ed9\u9601\u8bae\u7684 \u7ed3\u679c\u5374\u8fd8\u6ca1\u6709\u6d88\u606f\u3002\u201d \u3000\u3000\u201c\u8fd9\u2026\u2026\u201d\u9a6c\u4e09\u7126\u6025\u5730\u9053\uff1a\u201c\u4f1a\u4e0d\u4f1a\u6709\u4ec0\u4e48\u53d8\u5316\u5462\uff1f\u201d \u3000\u3000\u201c\u5e94\u8be5\u662f\u4e0d\u4f1a\uff0c\u7687\u4e0a\u4ea4\u8bae\u7684\u4e8b\uff0c\u4e00\u5411\u90fd\u662f\u7167\u6848\u901a\u8fc7\u3002\u201d\u738b\u5f64\u9053\uff1a\u201c\u5c31\u6211\u6240\u77e5\uff0c\u9601\u8bae\u4e2d\u4ece \u6ca1\u6709\u63a8\u7ffb\u8fc7\u7687\u4e0a\u610f\u601d\u7684\u51b3\u5b9a\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u597d\uff0c\u90a3\u5c31\u597d\uff0c\u738b\u7237\u8f9b\u82e6\u4e86\u51e0\u5929\uff0c\u4eca\u665a\u5c31\u7559\u5728\u5929\u95e8\u5ba2\u6808\u4e00\u5bb5\u3002\u201d\u9a6c\u4e09\u8be1\u79d8\u7684\u7b11\u4e86 \u7b11\uff0c\u9053\uff1a\u201c\u5305\u4f60\u8eab\u5fc3\u8212\u7545\uff0c\u75b2\u52b3\u5c3d\u6d88\u3002\u201d \u3000\u3000\u738b\u5f64\u5fc3\u4e2d\u5fd6\u9053\uff1a\u53cd\u6b63\u6211\u5df2\u7ecf\u8eab\u53d7\u5185\u4f24\uff0c\u5f62\u540c\u5e9f\u4eba\uff0c\u518d\u4e2d\u4e00\u6b21\u6697\u7b97\u4e5f\u662f\u65e0\u6240\u8c13\u4e86\uff0c\u4f55\u4e0d\u7559 \u4e0b\u6765\u89c1\u8bc6\u89c1\u8bc6\uff01 \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff01\u53ea\u6015\u7559\u4e0b\u4f1a\u6253\u6270\u4e24\u4f4d\u3002\u201d \u3000\u3000\u201c\u4e0d\u7528\u5ba2\u6c14\uff0c\u738b\u7237\u51c6\u5907\u51e0\u65f6\u5357\u4e0b\uff1f\u201d \u3000\u3000\u201c\u5feb\u5219\u4e09\u5929\uff0c\u8981\u662f\u6162\u8d77\u6765\uff0c\u6050\u6015\u8981\u5341\u5929\u5de6\u53f3\u4e86\u3002\u201d \u3000\u3000\u201c\u90a3\u5c82\u4e0d\u662f\u803d\u8bef\u4e86\u4f60\u548c\u9a6c\u5927\u4eba\u7684\u7ea6\u4f1a\uff1f\u201d \u3000\u3000\u201c\u8fd9\u624d\u662f\u738b\u67d0\u6765\u627e\u4f60\u7684\u91cd\u70b9\u2026\u2026\u201d\u738b\u5f64\u9053\uff1a\u201c\u5982\u8981\u77e5\u9053\u9601\u8bae\u7ed3\u679c\uff0c\u5fc5\u987b\u8981\u7b49\u4e0b\u53bb\uff0c\u4e0d \u8fc7\uff0c\u6211\u4e5f\u53ef\u4ee5\u5148\u8d70\uff0c\u5230\u5f00\u5c01\u518d\u7b49\u9601\u8bae\u7684\u7ed3\u679c\uff0c\u9a6c\u6587\u4e2d\u548c\u6211\u7ea6\u5b9a\u4e86\u4e09\u4ef6\u4e8b\uff0c\u4e24\u4ef6\u90fd\u6709\u4e86\u80af\u5b9a\u7684 \u7ed3\u679c\uff0c\u603b\u7b97\u6709\u4e2a\u4ea4\u4ee3\u4e86\u3002\u201d \u3000\u3000\u201c\u738b\u7237\uff0c\u6211\u770b\u8fd8\u662f\u7b49\u5230\u9601\u8bae\u6709\u4e86\u7ed3\u679c\u518d\u5357\u4e0b\uff0c\u53cd\u6b63\uff0c\u6025\u4e5f\u4e0d\u5728\u4e4e\u591a\u4e2a\u5341\u5929\u516b\u5929\u3002\u201d \u3000\u3000\u201c\u6211\u5728\u4e4e\u5f97\u5f88\u2026\u2026\u201d\u738b\u5f64\u9053\uff1a\u201c\u9a6c\u6587\u4e2d\u5728\u6211\u8eab\u4e0a\u4e0b\u4e86\u7981\u5236\uff0c\u7ea6\u5b9a\u4e8c\u5341\u5929\u8981\u738b\u67d0\u8d76\u56de\u5f00 \u5c01\uff0c\u5982\u662f\u62d6\u5ef6\u4e86\u65f6\u95f4\uff0c\u5c82\u4e0d\u662f\u8981\u4e86\u6211\u7684\u8001\u547d\uff1f\u201d \u3000\u3000\u9a6c\u4e09\u9053\uff1a\u201c\u8bf4\u7684\u4e5f\u662f\uff0c\u5982\u679c\u4e00\u5b9a\u8981\u5ef6\u957f\u65f6\u95f4\uff0c\u770b\u5c0f\u7684\u80fd\u4e0d\u80fd\u60f3\u5230\u529e\u6cd5\uff1f\u201d \u3000\u3000\u201c\u8fd9\u662f\u751f\u6b7b\u76f8\u5173\u7684\u5927\u4e8b\uff0c\u4e0d\u80fd\u78b0\u8fd0\u6c14\u2026\u2026\u201d \u3000\u3000\u201c\u6709\u7406\u2026\u2026\u201d\u9a6c\u4e09\u9053\uff1a\u201c\u660e\u5929\u5348\u65f6\u4e4b\u524d\uff0c\u6211\u4e00\u5b9a\u7ed9\u738b\u7237\u4e00\u4e2a\u80af\u5b9a\u7684\u7b54\u590d\u3002\u201d \u3000\u3000\u201c\u597d\u5427\uff01\u90a3\u6211\u5c31\u7b49\u5230\u8fc7\u5348\u4e4b\u540e\u518d\u8d70\uff01\u201d \u3000\u3000\u4f46\u4ed6\u5fc3\u4e2d\u5374\u6697\u6697\u7b11\u9053\uff1a\u4efb\u4f60\u9a6c\u4e09\u5978\u4f3c\u9b3c\uff0c\u4e5f\u8981\u88ab\u6211\u7275\u7740\u9f3b\u5b50\u8d70\u4e00\u56de\u3002 \u3000\u3000\u738b\u5f64\u53d7\u5230\u4e86\u5e1d\u738b\u822c\u7684\u62db\u5f85\u3002 \u3000\u3000\u5728\u4e09\u697c\u4e0a\u4e00\u95f4\u8c6a\u534e\u7684\u623f\u95f4\u4e2d\uff0c\u6709\u4e00\u5ea7\u53ef\u5bb9\u516b\u4e2a\u4eba\u540c\u65f6\u6c90\u6d74\u7684\u5927\u6d74\u6c60\uff0c\u56db\u4e2a\u5065\u7f8e\u7684\u5c11\u5973\u8131 \u5149\u4e86\u8863\u670d\uff0c\u66ff\u738b\u5f64\u6d17\u6fa1\u3002 \u3000\u3000\u53ea\u89c1\u5979\u4eec\u4e3a\u738b\u5f64\u6413\u6413\u80cc\u3001\u6309\u6309\u6469\u3001\u51b2\u51b2\u6c34\uff0c\u522b\u8bf4\u5979\u4eec\u7684\u52a8\u4f5c\u6781\u5176\u6311\u9017\uff0c\u5355\u662f\u56db\u5177\u7a7f\u68ad\u5f80 \u6765\u7684\u7f8e\u4e3d\u80f4\u4f53\uff0c\u5c31\u8ba9\u4f60\u5fc3\u733f\u610f\u9a6c\uff0c\u96be\u4ee5\u81ea\u7981\u3002 \u3000\u3000\u738b\u5f64\u653e\u8361\u5730\u5728\u4eab\u53d7\u7740\uff0c\u628a\u56db\u4f4d\u5c11\u5973\u5168\u7559\u4e86\u4e0b\u6765\uff0c\u4e00\u591c\u7684\u7f20\u7ef5\uff0c\u771f\u662f\u4eab\u5c3d\u4e86\u4eba\u95f4\u6700\u7f8e\u597d\u7684 \u6b22\u4e50\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u738b\u5f64\u4ed6\u5e76\u4e0d\u597d\u8272\uff0c\u4ed6\u8fd9\u4e48\u653e\u7eb5\u81ea\u5df1\uff0c\u662f\u8981\u9a6c\u4e09\u8bef\u8ba4\u4ed6\u662f\u4e2a\u9152\u3001\u8272\u4e4b\u5f92\uff0c\u800c\u4e0d\u518d \u591a\u5fc3\u9632\u8303\u3002 \u3000\u3000\u679c\u7136\uff0c\u7b2c\u4e8c\u5929\u4e2d\u5348\u65f6\u5206\uff0c\u9a6c\u4e09\u5c31\u6709\u4e86\u5f88\u5927\u7684\u53cd\u5e94\u3002 \u3000\u3000\u4ed6\u628a\u996d\u83dc\u5f00\u5230\u738b\u5f64\u7684\u5367\u623f\u4e2d\u5403\uff0c\u56db\u4e2a\u966a\u4f8d\u7684\u5c11\u5973\u4e5f\u672a\u79bb\u5f00\uff0c\u4e0d\u505c\u7684\u4e3a\u738b\u5f64\u6376\u6376\u80cc\u3001\u634f\u634f \u817f\uff0c\u62db\u5f85\u5f97\u70ed\u70c8\u53c8\u4eb2\u5207\uff0c\u7b80\u76f4\u53eb\u4eba\u5fd8\u53bb\u4e86\u4e16\u95f4\u8fd8\u6709\u4e0d\u5feb\u4e50\u7684\u4e8b\u60c5\u3002 \u3000\u3000\u76f4\u5230\u9a6c\u4e09\u63d0\u4e86\u4e00\u4e2a\u76ae\u7bb1\u5b50\u8fdb\u6765\uff0c\u624d\u66ff\u738b\u5f64\u9063\u8d70\u4e86\u56db\u4f4d\u59d1\u5a18\u3002 \u3000\u3000\u5173\u597d\u623f\u95e8\uff0c\u9a6c\u4e09\u6253\u5f00\u4e86\u76ae\u7bb1\u7684\u76d6\u5b50\uff0c\u738b\u5f64\u9aa4\u89c9\u773c\u524d\u4e00\u4eae\uff01 \u3000\u3000\u9ec4\u6f84\u6f84\u7684\u91d1\u952d\u8db3\u8db3\u6709\u534a\u7bb1\uff0c\u81f3\u5c11\u6709\u4e09\u5343\u4e24\u5de6\u53f3\uff0c\u518d\u52a0\u4e0a\u5341\u9897\u9e3d\u86cb\u5927\u5c0f\u7684\u660e\u73e0\uff0c\u91d1\u8272\u73e0 \u5149\uff0c\u770b\u5f97\u4eba\u773c\u82b1\u7f18\u4e71\u3002 \u3000\u3000\u662f\u7684\uff0c\u597d\u8272\u7684\u4eba\u5fc5\u5b9a\u4e5f\u7231\u8d22\uff0c\u738b\u5f64\u7559\u4e0b\u4e86\u56db\u5973\u966a\u5bbf\uff0c\u5e94\u8be5\u662f\u975e\u5e38\u597d\u8272\u7684\u4eba\uff0c\u6240\u4ee5\uff0c\u9a6c\u4e09 \u53c8\u732e\u4e86\u9ec4\u91d1\u3001\u660e\u73e0\u3002 \u3000\u3000\u738b\u5f64\u5fc3\u4e2d\u51b7\u51b7\u7b11\u9053\uff1a\u51fa\u624b\u679c\u7136\u5927\u65b9\uff0c\u4e0d\u8981\u767d\u4e0d\u8981\u3002\u4e8e\u662f\u4fbf\u88c5\u51fa\u4e00\u526f\u8d2a\u5a6a\u7684\u795e\u60c5\uff0c\u671b\u7740\u9ec4 \u91d1\u3001\u660e\u73e0\u51fa\u795e\u3002 \u3000\u3000\u201c\u738b\u7237\uff01\u201d\u9a6c\u4e09\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5728\u5317\u4e0a\u4e4b\u524d\uff0c\u9a6c\u5927\u4eba\u4ea4\u7ed9\u5c0f\u7684\u8fd9\u4e00\u7bb1\u9ec4\u91d1\u3001\u660e\u73e0\uff0c\u8981\u5728 \u738b\u7237\u6700\u9700\u8981\u7684\u65f6\u5019\uff0c\u7531\u5c0f\u7684\u9001\u4e0a\u3002\u73b0\u5728\u9601\u8bae\u8981\u8ba8\u8bba\u5723\u6279\u4ea4\u4e0b\u7684\u64a4\u519b\u4e00\u6848\uff0c\u9a6c\u4e09\u89c9\u5f97\u73b0\u5728\u5c31\u662f \u738b\u7237\u6700\u9700\u8981\u7684\u65f6\u5019\uff0c\u4ee5\u738b\u7237\u7684\u8eab\u4efd\u3001\u5730\u4f4d\uff0c\u518d\u9001\u70b9\u793c\u7269\uff0c\u8981\u4ed6\u4eec\u5468\u65cb\u4e00\u4e0b\uff0c\u8fd8\u4e0d\u662f\u624b\u5230\u64d2 \u6765\u2026\u2026\u201d \u3000\u3000\u201c\u5509\uff01\u8fd9\u662f\u4e00\u4ef6\u597d\u4e8b\uff0c\u4e24\u56fd\u606f\u4e89\uff0c\u8001\u767e\u59d3\u624d\u80fd\u5b89\u5c45\u4e50\u4e1a\u2026\u2026\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u7687\u4e0a\u90fd\u540c\u610f\u4e86\uff0c\u4ed6\u4eec\u5e94\u8be5\u4e00\u81f4\u8d5e\u6210\u624d\u5bf9\uff0c\u8fd8\u9700\u8981\u9001\u793c\u6d3b\u52a8\u4e48\uff1f\u201d \u3000\u3000\u201c\u53cd\u6b63\u4e5f\u4e0d\u7528\u82b1\u738b\u7237\u4e00\u4e2a\u94dc\u677f\uff0c\u6709\u4eba\u51fa\u94b1\uff0c\u4f60\u4f55\u4e50\u800c\u4e0d\u4e3a\u5462\uff1f\u201d \u3000\u3000\u9a6c\u4e09\u9053\uff1a\u201c\u53ea\u8981\u4ed6\u4eec\u6536\u4e86\u793c\u7269\uff0c\u5c31\u7b97\u4f60\u738b\u7237\u642d\u4e0a\u4e86\u4e00\u4efd\u4ea4\u60c5\uff0c\u65e5\u540e\u7684\u7528\u5904\u5f88\u591a\u3002\u201d \u3000\u3000\u201c\u8bf4\u5f97\u4e5f\u662f\u554a\uff01\u2026\u2026\u201d\u738b\u5f64\u5408\u4e0a\u4e86\u7bb1\u76d6\uff0c\u9053\uff1a\u201c\u793c\u7269\u6211\u6536\u4e0b\u4e86\uff0c\u6211\u770b\u600e\u4e48\u4e2a\u5206\u914d\u6cd5\u624d\u9002 \u5408\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u5c31\u7531\u738b\u7237\u5168\u6743\u4f5c\u4e3b\u4e86\u2026\u2026\u201d\u9a6c\u4e09\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u54ea\u91cc\u8be5\u9001\uff0c\u54ea\u91cc\u4e0d\u80fd\u9001\uff0c\u5168\u7531\u738b \u7237\u914c\u5904\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\uff0c\u6211\u738b\u67d0\u4eba\u6700\u5173\u5fc3\u7684\u662f\u6211\u8fd9\u6761\u8001\u547d\uff0c\u7f8e\u5973\u76f8\u4ece\uff0c\u91d1\u94f6\u6210\u5806\uff0c\u8981\u662f\u6ca1\u6709\u547d\u53bb\u4eab\u53d7\uff0c \u90a3\u624d\u771f\u7684\u9057\u61be\u5462\uff01\u201d \u3000\u3000\u201c\u8fd9\u4e00\u70b9\uff0c\u738b\u7237\u8bf7\u653e\u5fc3\uff0c\u9a6c\u4e09\u5df2\u6c42\u5f97\u4e00\u679a\u836f\u7269\uff0c\u8bf7\u738b\u7237\u670d\u4e0b\uff0c\u81f3\u5c11\uff0c\u53ef\u4ee5\u5ef6\u957f\u534a\u4e2a\u6708\u7684 \u65f6\u95f4\uff0c\u660e\u5929\uff0c\u6211\u5c31\u5dee\u4eba\u53bb\u901a\u62a5\u9a6c\u5927\u4eba\u4e00\u58f0\uff0c\u8bf4\u660e\u5185\u60c5\u2026\u2026\u201d \u3000\u3000\u201c\u53e3\u6c14\u8981\u6d3b\u4e00\u70b9\uff0c\u7167\u6211\u7684\u63a8\u65ad\uff0c\u4e09\u5929\u4e4b\u5185\u9601\u8bae\u5341\u4e4b\u516b\u4e5d\u53ef\u4ee5\u6279\u51c6\uff0c\u8bf4\u4e0d\u5b9a\u54b1\u4eec\u4e09\u4e24\u5929\u5185 \u5c31\u53ef\u4ee5\u4e0a\u8def\u4e86\u3002\u201d \u3000\u3000\u201c\u90a3\u662f\u6700\u597d\u4e0d\u8fc7\uff0c\u4f46\u6709\u5907\u65e0\u60a3\uff0c\u738b\u7237\u670d\u4e0b\u8fd9\u9897\u836f\u7269\uff0c\u4e5f\u597d\u5b89\u5fc3\u4e00\u4e9b\u3002\u201d\u9a6c\u4e09\u5fae\u7b11\u7740\uff0c\u4ece \u6000\u4e2d\u53d6\u51fa\u4e00\u9897\u7528\u767d\u8721\u5c01\u7740\u7684\u836f\u4e38\uff0c\u51c6\u5907\u634f\u788e\u3002 \u3000\u3000\u738b\u5f64\u4f38\u624b\u6b62\u4f4f\uff0c\u7b11\u9053\uff1a\u201c\u6162\u7740\uff0c\u6162\u7740\uff0c\u4e0d\u7528\u592a\u6025\uff0c\u836f\u6548\u53ea\u6709\u534a\u4e2a\u6708\uff0c\u738b\u67d0\u8fd8\u662f\u665a\u51e0\u65e5\u670d \u7528\u8f83\u597d\u3002\u201d \u3000\u3000\u5bf9\u5bf9\u5bf9\u2026\u2026\u201c\u9a6c\u4e09\u628a\u8721\u4e38\u4ea4\u7ed9\u4e86\u738b\u5f64\uff0c\u9053\uff1a\u201d\u670d\u7528\u65f6\uff0c\u634f\u5f00\u8721\u4e38\u7528\u9152\u6216\u5f00\u6c34\u51b2\u670d\u90fd\u53ef\uff0c \u5343\u4e07\u4e0d\u53ef\u4ee5\u8336\u548c\u670d\u3002\u201c \u3000\u3000\u738b\u5f64\u6536\u8d77\u836f\u4e38\uff0c\u63d0\u8d77\u4e86\u76ae\u7bb1\uff0c\u9053\uff1a\u201c\u8fd9\u4e00\u8d9f\uff0c\u771f\u662f\u6536\u83b7\u4e30\u5bcc\uff0c\u53ea\u53ef\u60dc\uff0c\u4e0d\u4fbf\u5e26\u7740\u56db\u4f4d\u59d1 \u5a18\u540c\u884c\u3002\u201d \u3000\u3000\u201c\u5979\u4eec\u4f1a\u5728\u8fd9\u91cc\u606d\u5019\u738b\u7237\u2026\u2026\u201d\u9a6c\u4e09\u9053\uff1a\u201c\u5f53\u7136\uff0c\u5357\u4e0b\u65f6\uff0c\u5148\u53ef\u4ee5\u9009\u4e24\u4e2a\u5728\u8f66\u4e2d\u966a\u4f8d\uff0c \u4ee5\u89e3\u65c5\u9014\u5bc2\u5bde\u3002\u201d \u3000\u3000\u738b\u5f64\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u597d\u6781\u4e86\uff0c\u771f\u5e0c\u671b\u9601\u8bae\u660e\u5929\u5c31\u80fd\u901a\u8fc7\uff0c\u54b1\u4eec\u660e\u5929\u665a\u4e0a\u5c31\u53ef\u4ee5\u8fde\u591c\u4e0a\u8def \u4e86\u3002\u201d \u3000\u3000\u9a6c\u4e09\u7b11\u7b11\u9053\uff1a\u201c\u6211\u8fd9\u513f\u968f\u65f6\u606d\u5019\u4f73\u97f3\uff0c\u53ea\u8981\u738b\u7237\u51b3\u5b9a\u51e0\u65f6\u8d70\uff0c\u54b1\u4eec\u5c31\u7acb\u523b\u4e0a\u8def\u3002\u201d \u3000\u3000\u79bb\u5f00\u4e86\u5929\u95e8\u5ba2\u6808\uff0c\u738b\u5f64\u76f4\u8fd4\u5185\u5bab\uff0c\u6536\u597d\u9ec4\u91d1\u3001\u73e0\u5b9d\uff0c\u8f6c\u5230\u592a\u533b\u9662\uff0c\u62ff\u51fa\u4e86\u8721\u4e38\uff0c\u8981\u592a\u533b \u4eec\u67e5\u67e5\u770b\u8fd9\u836f\u4e38\u662f\u7528\u4ec0\u4e48\u836f\u7269\u5236\u6210\u7684\uff1f \u3000\u3000\u56db\u4f4d\u592a\u533b\u634f\u788e\u4e86\u8721\u5c01\uff0c\u95fb\u6765\u95fb\u53bb\uff0c\u53c8\u522e\u4e0b\u4e86\u4e00\u4e9b\u7c89\u672b\u54c1\u5c1d\u4e00\u4e0b\uff0c\u4f46\u4e5f\u53ea\u80fd\u8bf4\u51fa\u51e0\u79cd\u836f\u7269 \u7684\u540d\u5b57\uff0c\u90fd\u662f\u4e00\u4e9b\u6e29\u548c\u6027\u7684\u836f\u7269\u3002 \u3000\u3000\u738b\u5f64\u53c8\u522e\u4e0b\u4e86\u4e00\u7247\u836f\u7c89\uff0c\u9053\uff1a\u201c\u8fd9\u4e9b\u7559\u7ed9\u8bf8\u4f4d\u6162\u6162\u7814\u7a76\u5427\uff01\u4e0d\u8fc7\uff0c\u8fd9\u4ef6\u4e8b\u4e0d\u80fd\u5f20\u626c\uff0c\u5426 \u5219\uff0c\u53ea\u6015\u4f1a\u4e3a\u8bf8\u4f4d\u5e26\u6765\u5f88\u5927\u7684\u9ebb\u70e6\u3002\u201d \u3000\u3000\u51e0\u4f4d\u5927\u533b\u8fde\u8fde\u79f0\u662f\uff0c\u738b\u5f64\u624d\u5e26\u7740\u836f\u7269\u79bb\u53bb\uff0c\u56de\u5230\u4e86\u4f4f\u5904\uff0c\u5012\u676f\u9152\u541e\u4e0b\u4e86\u836f\u4e38\uff0c\u76d8\u5750\u8c03\u606f \u8d77\u6765\u3002 \u3000\u3000\u738b\u5f64\u611f\u89c9\u5230\u4e39\u7530\u5904\u6ede\u6b62\u7684\u5730\u65b9\uff0c\u679c\u7136\u5728\u836f\u529b\u4e0b\u6709\u6240\u5f00\u5c55\uff0c\u4f46\u4ecd\u7136\u65e0\u6cd5\u5b8c\u5168\u590d\u5143\uff0c\u4e0d\u7981\u6697 \u6697\u4e00\u53f9\uff01 \u3000\u3000\u7687\u4e0a\u4ea4\u8bae\u7684\u4e8b\uff0c\u5f53\u5929\u5c31\u5f97\u9601\u8bae\u901a\u8fc7\uff0c\u738b\u5f64\u4e5f\u5f88\u5feb\u7684\u5c06\u516c\u6587\u5f04\u5230\u624b\u4e0a\uff0c\u800c\u4e14\u65e9\u5df2\u63e3\u5728\u6000\u4e2d \u4e86\u3002 \u3000\u3000\u5f53\u4ed6\u53bb\u89c1\u9a6c\u4e09\u65f6\uff0c\u65e9\u5df2\u5c06\u516c\u6587\u5e26\u7740\uff0c\u4e3a\u4e86\u8981\u62d6\u5ef6\u65f6\u95f4\uff0c\u5e0c\u671b\u903c\u51fa\u89e3\u53bb\u8eab\u4e0a\u7981\u5236\u7684\u65b9\u6cd5\uff0c \u4f46\u4ecd\u4e0d\u80fd\u5b8c\u5168\u89e3\u9664\uff0c\u5374\u662f\u5927\u51fa\u4e86\u738b\u5f64\u7684\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u8fd9\u5c31\u903c\u5f97\u4ed6\u4e0d\u5f97\u4e0d\u6539\u53d8\u8ba1\u5212\uff0c\u9009\u4e86\u4e00\u4e2a\u7cbe\u660e\u7684\u4f8d\u536b\u7f57\u6052\uff0c\u5148\u884c\u5357\u4e0b\uff0c\u8981\u4ed6\u4ee3\u8d74\u6c5f\u5343\u91cc\u7684 \u7ea6\u4f1a\u3002 \u3000\u3000\u62d6\u8fc7\u4e86\u7b2c\u516b\u5929\uff0c\u738b\u5f64\u624d\u5306\u5306\u8d76\u5230\u5929\u95e8\u5ba2\u6808\u3002 \u3000\u3000\u9a6c\u4e09\u4f3c\u662f\u5df2\u7b49\u5f97\u5341\u5206\u7126\u6025\uff0c\u89c1\u5230\u4e86\u738b\u5f64\uff0c\u7acb\u523b\u8fce\u4e86\u4e0a\u53bb\uff0c\u9053\uff1a\u201c\u738b\u7237\uff0c\u600e\u4e48\u6837\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e00\u5207\u987a\u5229\uff0c\u54b1\u4eec\u53ef\u4ee5\u5357\u4e0b\u4e86\u3002\u201d \u3000\u3000\u9a6c\u4e09\u7b11\u7b11\u9053\uff1a\u201c\u5927\u5c40\u65e2\u5b9a\uff0c\u6025\u4e5f\u4e0d\u5728\u4e00\u65f6\uff0c\u54b1\u4eec\u660e\u5929\u4e00\u65e9\u4e0a\u8def\u5427\uff01\u201d \u3000\u3000\u738b\u5f64\u5fc3\u4e2d\u6697\u9053\uff1a\u5982\u679c\u9a6c\u6587\u4e2d\u9065\u63a7\u6b64\u5730\uff0c\u5fc5\u662f\u4f9d\u4ed7\u98de\u9e3d\u4f20\u4e66\u4e4b\u7c7b\u7684\u529e\u6cd5\uff0c\u4f46\u4ed6\u4eec\u529e\u4e8b\u7684\u901f \u5ea6\u4e4b\u5feb\uff0c\u4f3c\u4e4e\u662f\u8fd9\u91cc\u53e6\u6709\u51b3\u5b9a\u5927\u8ba1\u7684\u4eba\u7269\uff0c\u96be\u9053\u8fd9\u9a6c\u4e09\u5c31\u662f\u8eab\u8d1f\u51b3\u7b56\u5927\u8ba1\u7684\u4eba\u7269\u4e0d\u6210\uff1f \u3000\u3000\u4e00\u5ff5\u53ca\u6b64\uff0c\u4e0d\u7981\u5fc3\u5934\u4e00\u9707\uff0c\u5fd6\u9053\uff1a\u83ab\u8981\u5927\u610f\u683d\u5728\u4ed6\u7684\u624b\u91cc\u624d\u662f\u3002 \u3000\u3000\u4ed6\u6697\u6697\u5730\u63d0\u9ad8\u8b66\u89c9\uff0c\u53e3\u4e2d\u5927\u7b11\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u4e5f\u597d\uff0c\u8fd9\u91cc\u8f6f\u7389\u6e29\u9999\uff0c\u9189\u4eba\u5982\u9152\uff0c\u591a\u7559\u4e00 \u5929\uff0c\u56fa\u6240\u613f\u4e5f\uff0c\u4e0d\u4fbf\u8bf7\u5c14\u2026\u2026\u201d \u3000\u3000\u9a6c\u4e09\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\uff01\u597d\uff01\u738b\u7237\u8bf7\u597d\u597d\u4eab\u53d7\u8fd9\u5343\u91d1\u6625\u5bb5\uff0c\u6211\u51fa\u53bb\u6574\u7406\u4e00\u4e0b\u7bf7\u8f66\uff0c\u9a6c \u4e0a\u5c31\u6765\u3002\u201d \u3000\u3000\u738b\u5f64\u53d1\u89c9\u4e86\u9a6c\u4e09\u4e34\u53bb\u4e4b\u9645\uff0c\u76ee\u5149\u4e2d\u6d41\u9732\u51fa\u4e0d\u5c51\u7684\u795e\u8272\uff0c\u5fc3\u4e2d\u5374\u5927\u611f\u5feb\u6170\uff0c\u9a6c\u4e09\u771f\u7684\u628a\u4ed6 \u770b\u6210\u4e86\u4e00\u4e2a\u7231\u8d22\u7684\u4eba\u7269\u4e86\u3002 \u3000\u3000\u4f46\u9a6c\u4e09\u7684\u5229\u5bb3\uff0c\u5374\u5728\u4ed6\u5e03\u7f6e\u7684\u5468\u5bc6\u4e0a\uff0c\u56e0\u4e3a\uff0c\u6709\u4e24\u4e2a\u966a\u4f8d\u8fc7\u738b\u5f64\u7684\u5c11\u5973\uff0c\u7adf\u7136\u65e9\u5df2\u5728\u8f66 \u4e0a\u7b49\u5019\u4e86\u3002 \u3000\u3000\u738b\u5f64\u88c5\u4e86\u4e24\u6b21\u7684\u8272\u72fc\uff0c\u5012\u662f\u4e0d\u4fbf\u62c9\u4e0b\u8138\u6765\u62d2\u7edd\uff0c\u8fd9\u5c31\u82e6\u4e86\u738b\u5f64\uff0c\u4e0d\u4f46\u65e0\u6cd5\u8ddf\u6cbf\u9014\u8ffd\u8e2a\u7684 \u4f8d\u536b\u8054\u7edc\uff0c\u8fde\u4e00\u4e2a\u4eba\u9759\u5750\u6c89\u601d\u7684\u673a\u4f1a\uff0c\u90fd\u88ab\u4e8c\u5973\u95f9\u5f97\u65e0\u6cd5\u5b89\u9759\u4e0b\u6765\u3002\u9664\u4e86\u548c\u4e8c\u5973\u5b09\u95f9\u4e4b\u5916\uff0c \u53ea\u6709\u95ed\u76ee\u5047\u5bd0\u3002 \u3000\u3000\u4e00\u8def\u6025\u8f66\u98de\u9a70\uff0c\u65e5\u591c\u517c\u7a0b\u3002 \u3000\u3000\u5f53\u8f66\u62b5\u5f00\u5c01\u65f6\uff0c\u7adf\u7136\u6bd4\u5317\u4e0a\u8fd8\u5feb\u4e86\u534a\u5929\u7684\u8def\u7a0b\u3002 \u3000\u3000\u7bf7\u8f66\u76f4\u9a70\u5165\u4e00\u5ea7\u5927\u5b85\u9662\u5185\uff0c\u624d\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u9a6c\u4e09\u6253\u5f00\u4e86\u8f66\u5e18\u5b50\uff0c\u9a6c\u6587\u4e2d\u5df2\u8fce\u4e86\u4e0a\u6765\uff0c\u7b11\u9053\uff1a\u201c\u738b\u5144\uff0c\u8f9b\u82e6\u4e86\uff01\u201d \u3000\u3000\u201c\u8d39\u4e86\u4e00\u756a\u5468\u6298\uff0c\u5e78\u672a\u8fb1\u547d\uff01\u201d\u738b\u5f64\u6b65\u4e0b\u4e86\u7bf7\u8f66\u3002 \u3000\u3000\u4ed6\u770b\u4e86\u770b\u56db\u5468\uff0c\u53c8\u9053\uff1a\u201c\u4e00\u5207\u90fd\u5982\u9a6c\u5c3c\u6240\u6599\uff0c\u7687\u4e0a\u5b8c\u5168\u7167\u51c6\uff0c\u552f\u4e00\u7684\u8981\u6c42\u662f\u8981\u6211\u89c1\u89c1\u4e09 \u516c\u4e3b\uff0c\u6d3e\u9063\u968f\u540c\u5357\u4e0b\u7684\u4f8d\u536b\uff0c\u56de\u5bab\u9762\u5723\uff0c\u544a\u8bc9\u4ed6\u4e09\u516c\u4e3b\u5e73\u5b89\u7684\u6d88\u606f\uff0c\u5f53\u7136\uff0c\u4e09\u516c\u4e3b\u5982\u80fd\u5199\u5c01 \u4fe1\u5e26\u56de\u53bb\uff0c\u4ee5\u6170\u5723\u5fc3\uff0c\u90a3\u5c31\u66f4\u597d\u4e86\u3002\u201d \u3000\u3000\u201c\u7236\u5973\u60c5\u6df1\uff0c\u5723\u4e0a\u4e5f\u662f\u4e0d\u80fd\u514d\u4fd7\uff0c\u8fd9\u4ef6\u4e8b\u597d\u5546\u91cf\u2026\u2026\u201d\u9a6c\u6587\u4e2d\u9053\uff1a\u201c\u5144\u5f1f\u4e00\u5b9a\u60f3\u529e\u6cd5\u8981 \u4e09\u5144\u5982\u613f\u3002\u201d \u3000\u3000\u201c\u600e\u4e48\uff1f\u542c\u8d77\u6765\uff0c\u8fd9\u4e48\u4e00\u4ef6\u7b80\u5355\u7684\u4e8b\uff0c\u8fd8\u5f97\u8d39\u4e00\u756a\u5de5\u592b\u4e48\uff1f\u201d \u3000\u3000\u201c\u4e0d\u7792\u738b\u5144\uff0c\u4e09\u516c\u4e3b\u5df2\u4e0d\u5728\u5f00\u5c01\uff0c\u738b\u5144\u4e00\u5b9a\u8981\u89c1\uff0c\u53ea\u6709\u4e24\u4e2a\u529e\u6cd5\uff1a\u4e00\u662f\u63a5\u5979\u56de\u6765\uff1b\u4e00\u4e2a \u662f\u54b1\u4eec\u79fb\u6a3d\u5c31\u6559\u3002\u201d \u3000\u3000\u201c\u6211\u770b\uff0c\u53ea\u6709\u54b1\u4eec\u53bb\u5c31\u6559\u4e86\uff0c\u5979\u662f\u91d1\u679d\u7389\u53f6\u7684\u8eab\u4ef7\uff0c\u603b\u4e0d\u80fd\u8981\u5979\u8f66\u9a6c\u52b3\u7d2f\u7684\u6765\u770b\u6211\u4eec \u5440\uff01\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u7684\u968f\u548c\u3001\u6e29\u67d4\uff0c\u53ea\u6015\u738b\u5144\u8fde\u505a\u68a6\u4e5f\u90fd\u6ca1\u6709\u60f3\u5230\u2026\u2026\u201d \u3000\u3000\u738b\u5f64\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u9a6c\u5144\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5144\u5f1f\u7684\u610f\u601d\u662f\u8bf4\uff0c\u5c31\u7b97\u8981\u4e09\u516c\u4e3b\u56de\u5230\u5f00\u5c01\uff0c\u5979\u4e5f\u4e0d\u4f1a\u751f\u6c14\uff0c\u4f55 \u51b5\u4f60\u4e09\u5144\u6570\u65e5\u7684\u5954\u6ce2\u52b3\u82e6\uff0c\u4e5f\u8be5\u597d\u597d\u7684\u4f11\u606f\u4e00\u4e0b\uff0c\u4eca\u591c\u5144\u5f1f\u4fdd\u8bc1\u8ba9\u4f60\u548c\u4e09\u516c\u4e3b\u89c1\u9762\u3002\u201d \u3000\u3000\u738b\u5f64\u672a\u518d\u591a\u8a00\uff0c\u4ed6\u5fc3\u4e2d\u660e\u767d\uff0c\u8fd9\u65f6\u8868\u73b0\u5f97\u592a\u8fc7\u6fc0\u70c8\uff0c\u4f1a\u628a\u8fd9\u4e9b\u65e5\u5b50\u6765\u82e6\u5fc3\u626e\u88c5\u7684\u5f62\u8c61\uff0c \u4ed8\u8bf8\u4e8e\u6d41\u6c34\uff0c\u4f46\u5982\u8868\u73b0\u5f97\u592a\u6e29\u548c\uff0c\u53c8\u4f1a\u5f15\u8d77\u9a6c\u6587\u4e2d\u7684\u7591\u5fc3\u3002 \u3000\u3000\u4ed6\u5728\u6c89\u601d\u4e2d\uff0c\u5df2\u6b65\u5165\u4e86\u4e00\u5ea7\u5385\u5802\u3002 \u3000\u3000\u8fd9\u91cc\u4e0d\u662f\u5de1\u629a\u629a\u8859\uff0c\u4e5f\u4e0d\u662f\u4e0a\u6b21\u56da\u5c45\u4e4b\u5904\uff0c\u4f3c\u662f\u4e00\u5ea7\u6df1\u5927\u7684\u5b85\u9662\uff0c\u9a6c\u8f66\u76f4\u9a70\u800c\u5165\uff0c\u738b\u5f64 \u7adf\u7136\u65e0\u6cd5\u770b\u51fa\u8fd9\u662f\u4e00\u5904\u4ec0\u4e48\u6837\u7684\u5730\u65b9\uff1f \u3000\u3000\u9a6c\u6587\u4e2d\u8ba9\u738b\u5f64\u5728\u4f4d\u5750\u4e0b\uff0c\u6325\u6325\u624b\uff0c\u9000\u53bb\u4e86\u5385\u4e2d\u7684\u4ec6\u5a62\uff0c\u9053\uff1a\u201c\u7687\u4e0a\u7b54\u5e94\u4e86\u64a4\u519b\uff0c\u5168\u662f\u738b \u5144\u7684\u529f\u52b3\uff0c\u5144\u5f1f\u4ee3\u8868\u8499\u53e4\u53ef\u6c57\uff0c\u5411\u738b\u5144\u81f4\u6700\u9ad8\u7684\u8c22\u610f\u3002\u201d \u3000\u3000\u738b\u5f64\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u8981\u6c42\u7684\u4e8b\uff0c\u738b\u67d0\u4eba\u5168\u90fd\u529e\u597d\u4e86\uff0c\u4f46\u6211\u8981\u6c42\u7684\u4e8b\uff0c\u4e5f\u5e0c\u671b\u9a6c\u5144 \u5151\u73b0\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u7b11\u7b11\u9053\uff1a\u201c\u90a3\u5f53\u7136\u4e86\uff01\u738b\u91cd\u5c71\u3001\u71d5\u98de\u3001\u8d75\u4fdd\u3001\u9648\u5b8f\uff0c\u6beb\u53d1\u65e0\u4f24\uff0c\u738b\u5144\u6b47\u4e00\u4f1a\u513f\u5c31 \u53ef\u4ee5\u548c\u4ed6\u4eec\u89c1\u9762\u2026\u2026\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u6211\u5df2\u6d3e\u98de\u9a6c\u5feb\u8f66\u8bf7\u4e09\u516c\u4e3b\u6765\u6b64\uff0c\u6700\u8fdf\u4e8c\u66f4\u53ef\u5230\uff0c\u4eca\u591c\u4e09\u66f4\u4e4b \u524d\uff0c\u738b\u5144\u4e5f\u53ef\u664b\u89c1\u4e09\u516c\u4e3b\u7684\u51e4\u9a7e\uff0c\u8981\u5979\u4fee\u4e66\u3001\u4e0a\u8868\uff0c\u751a\u81f3\u5e26\u4e0a\u4e00\u4ef6\u968f\u8eab\u7269\u54c1\u7ed9\u7687\u4e0a\uff0c\u90fd\u7531\u4e09 \u5144\u8bf7\u793a\u516c\u4e3b\u4f5c\u4e3b\u3002\u5927\u519b\u64a4\u5165\u897f\u5317\u4e09\u5173\u4e4b\u5185\uff0c\u4e24\u5e74\u540e\uff0c\u6211\u4eec\u4e00\u5b9a\u9001\u8fd8\u516c\u4e3b\u3002\u201d \u3000\u3000\u738b\u5f64\u6c89\u541f\u4e00\u9635\uff0c\u9053\uff1a\u201c\u7687\u4e0a\u4e5f\u65e0\u610f\u6253\u8fd9\u79cd\u4e0d\u80dc\u4e0d\u8d25\u7684\u4ed7\u4e86\uff0c\u64a4\u5175\u5df2\u5b9a\uff0c\u4e0d\u4f1a\u66f4\u6539\uff0c\u4f46\u5144 \u5f1f\u8eab\u4e0a\u7684\u5185\u4f24\u4ee5\u53ca\u738b\u91cd\u5c71\u3001\u71d5\u98de\u7b49\uff0c\u662f\u5426\u90fd\u80fd\u6062\u590d\u5982\u5e38\u5462\uff1f\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u7b11\u7b11\u9053\uff1a\u201c\u5f53\u7136\u53ef\u4ee5\uff0c\u660e\u5929\u5144\u5f1f\u5c31\u8bf7\u51e0\u4f4d\u9ad8\u624b\uff0c\u66ff\u738b\u5144\u7b49\u6cbb\u7597\uff0c\u4e09\u5929\u5185\uff0c\u4fdd\u8bc1\u5eb7 \u590d\u5982\u65e7\u3002\u201d \u3000\u3000\u738b\u5f64\u5fc3\u4e2d\u5fd6\u9053\uff1a\u597d\u5bb6\u4f19\uff0c\u770b\u6837\u5b50\uff0c\u81f3\u5c11\u8fd8\u8981\u5728\u4ed6\u4eec\u7684\u76d1\u89c6\u4e4b\u4e0b\u56db\u4e94\u5929\uff0c\u624d\u6709\u5e0c\u671b\u79bb\u5f00\u8fd9 \u91cc\u3002 \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u9a6c\u5144\uff0c\u73b0\u5728\u5144\u5f1f\u8981\u95ee\u4e00\u4ef6\u6211\u6700\u5173\u5fc3\u7684\u4e8b\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u9053\uff1a\u201c\u6211\u6d17\u8033\u606d\u542c\uff01\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u5fc3\u5370\u5927\u5e08\u3001\u859b\u767e\u80dc\u3001\u9ed1\u7f57\u6c49\u4ed6\u4eec\u2026\u2026\u201d \u3000\u3000\u201c\u90fd\u5f88\u597d\u2026\u2026\u201d\u9a6c\u6587\u4e2d\u9053\uff1a\u201c\u859b\u767e\u80dc\u8bf4\uff0c\u4ed6\u5df2\u7ecf\u8fd8\u5b8c\u4f60\u4e09\u5144\u4eba\u60c5\uff0c\u6211\u4eec\u4e5f\u5c31\u653e\u4ed6\u8d70 \u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u597d\uff01\u8001\u548c\u5c1a\u548c\u5c0f\u548c\u5c1a\u5462\uff1f\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u9053\uff1a\u201c\u4e24\u4e2a\u4eba\u90fd\u5728\u76f8\u56fd\u5bfa\u4e2d\u3002\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u5c0f\u548c\u5c1a\u4e0d\u662f\u62ff\u4e86\u9c7c\u5316\u9f99\u4e09\u4e07\u516b\u5343\u4e24\u94f6\u5b50\uff0c\u8dd1\u4e86\u4e48\uff1f\u201d \u3000\u3000\u201c\u738b\u5144\u7684\u670b\u53cb\u4e4b\u4e2d\uff0c\u5927\u6982\u8981\u5c5e\u5c0f\u548c\u5c1a\u6700\u9b3c\u4e86\u3002\u201d\u9a6c\u6587\u4e2d\u7b11\u9053\uff1a\u201c\u5982\u82e5\u653e\u4ed6\u79bb\u53bb\uff0c\u5f00\u5c01\u5e9c \u54ea\u8fd8\u4f1a\u8fd9\u4e48\u5e73\u9759\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u4e5f\u90fd\u53d7\u4e86\u4f24\u5427\uff1f\u201d\u738b\u5f64\u4f4e\u58f0\u5730\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u4e0e\u5fc3\u5370\u5927\u5e08\u4ed6\u4eec\u65e0\u5173\uff0c\u662f\u6211\u628a\u4ed6\u4eec\u62d6 \u4e0b\u4e86\u6c34\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u2026\u2026\u201d\u9a6c\u6587\u4e2d\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u6211\u4eec\u5e76\u6ca1\u6709\u5bf9\u4e24\u4eba\u65bd\u4ee5\u62a5\u590d\uff0c\u53ea\u662f\u6682\u65f6\u4f7f\u4ed6\u4eec\u5931\u53bb \u6b66\u529f\uff0c\u4ee5\u514d\u4ece\u4e2d\u6363\u86cb\uff0c\u7834\u574f\u4e8b\u60c5\u3002\u5f53\u7136\uff0c\u73b0\u5728\u5144\u5f1f\u548c\u738b\u5144\u90fd\u8c08\u59a5\u4e86\uff0c\u4e5f\u53ef\u4ee5\u89e3\u53bb\u4ed6\u8eab\u4e0a\u7684\u7981 \u5236\uff0c\u8ba9\u4ed6\u4eec\u6062\u590d\u81ea\u7531\u4e86\u3002\u201d \u3000\u3000\u738b\u5f64\u70b9\u70b9\u5934\uff0c\u7531\u6000\u4e2d\u53d6\u51fa\u4e00\u4efd\u9601\u8bae\u64a4\u519b\u7684\u516c\u6587\uff0c\u4ea4\u7ed9\u9a6c\u6587\u4e2d\u9053\uff1a\u201c\u8fd9\u662f\u9601\u8bae\u7684\u7687\u4e0a\u516c \u6587\uff0c\u9a6c\u5c3c\u53ef\u4ee5\u76f8\u4fe1\u4e86\u5427\uff01\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u5f88\u4ed4\u7ec6\u7684\u770b\u4e86\u4e00\u904d\uff0c\u7b11\u9053\uff1a\u201c\u738b\u5144\uff0c\u4f60\u4e0d\u4f46\u6551\u4e86\u65e0\u6570\u8499\u53e4\u9752\u5e74\u548c\u6c49\u4eba\u5b50\u5f1f\u7684\u6027 \u547d\uff0c\u4e5f\u662f\u6211\u9a6c\u6587\u4e2d\u7684\u597d\u670b\u53cb\u4e86\uff0c\u5144\u5f1f\u8be5\u529e\u7684\u4e8b\u7acb\u523b\u53bb\u51c6\u5907\uff0c\u6211\u76f8\u4fe1\u660e\u5e74\u6625\u672b\uff0c\u5723\u4e0a\u5c31\u53ef\u4ee5\u6536 \u5230\u8d21\u54c1\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u4e09\u516c\u4e3b\u53ea\u8981\u6709\u5e73\u5b89\u8baf\u606f\u4f20\u56de\u4eac\u57ce\u2026\u2026\u201d\u738b\u5f64\u9053\uff1a\u201c\u8fd9\u573a\u591a\u5e74\u7eb7\u6270\u8fb9\u5883\u4e4b\u6218\uff0c\u5c31\u53ef \u4ee5\u5e73\u606f\u4e86\u3002\u201d \u3000\u3000\u201c\u89c1\u7740\u4e09\u516c\u4e3b\u65f6\uff0c\u8fd8\u8bf7\u738b\u5144\u7f8e\u8a00\u4e00\u4e8c\u3002\u201d\u9a6c\u6587\u4e2d\u7b11\u7b11\u9053\uff1a\u201c\u4f60\u597d\u597d\u4f11\u606f\u4e00\u4e0b\uff0c\u5144\u5f1f\u4e0d\u6253 \u6270\u4e86\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u8d77\u8eab\u544a\u9000\u3002 \u3000\u3000\u738b\u5f64\u672c\u60f3\u95ee\u95ee\u4ed6\u89e3\u53bb\u8eab\u4e2d\u7981\u5236\u7684\u4e8b\uff0c\u4f46\u5374\u5f3a\u81ea\u5fcd\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u5df2\u5145\u5206\u4e86\u89e3\u76ee\u524d\u7684\u5904\u5883\uff0c\u9a6c\u6587\u4e2d\u7684\u6b66\u529f\u5df2\u5f88\u53ef\u6015\uff0c\u4f55\u51b5\u8fd8\u6709\u8f6c\u9f99\u624b\u53ca\u9c7c\u5316\u9f99\u7387\u9886\u7684\u9ad8 \u624b\uff0c\u90fd\u8fd8\u5728\u8fd9\u91cc\uff0c\u8fde\u59ec\u91cd\u5929\u90a3\u7b49\u5728\u6c5f\u6e56\u4e0a\u5927\u5927\u6709\u540d\u7684\u4eba\uff0c\u4e5f\u90fd\u5f52\u670d\u4e86\u9a6c\u6587\u4e2d\uff0c\u5c31\u7b97\u5357\u4e0b\u7684\u5341 \u540d\u5185\u5bab\u4f8d\u536b\uff0c\u5168\u90fd\u5728\u6b64\uff0c\u4e5f\u65e0\u6cd5\u548c\u5bf9\u65b9\u5f3a\u5927\u7684\u5b9e\u529b\u76f8\u6297\u62d2\u3002 \u3000\u3000\u9a6c\u6587\u4e2d\u679c\u7136\u662f\u4e2a\u8a00\u800c\u6709\u4fe1\u7684\u4eba\u3002 \u3000\u3000\u665a\u996d\u65f6\u523b\uff0c\u7adf\u7136\u628a\u738b\u91cd\u5c71\u3001\u71d5\u98de\u3001\u8d75\u4fdd\u3001\u9648\u5b8f\u5168\u90fd\u53ec\u6765\u548c\u738b\u5f64\u540c\u684c\u8fdb\u9910\uff0c\u800c\u4e14\uff0c\u9a6c\u6587\u4e2d \u548c\u51e0\u4eba\u62db\u547c\u4e00\u4e0b\uff0c\u5c31\u5148\u884c\u79bb\u53bb\u4e86\uff0c\u9001\u4e0a\u4e86\u9152\u83dc\u4e4b\u540e\uff0c\u5927\u5385\u4e2d\u7684\u7f8e\u5a62\u4e5f\u5168\u90fd\u64a4\u8d70\u4e86\uff0c\u53ea\u5269\u4e0b\u738b \u5f64\u7b49\u4e94\u4eba\uff0c\u663e\u7136\uff0c\u662f\u8981\u4e94\u4eba\u5728\u6781\u7aef\u81ea\u7531\u4e2d\u4ea4\u8c08\u3002 \u3000\u3000\u738b\u91cd\u5c71\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u770b\u6765\u4e8c\u53d4\u5df2\u7ecf\u529e\u597d\u4e86\u9a6c\u5de1\u629a\u8981\u6c42\u7684\u4e8b\u60c5\u4e86\u3002\u201d \u3000\u3000\u738b\u5f64\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u7687\u4e0a\u5df2\u7b54\u5141\u64a4\u519b\uff0c\u4eca\u665a\u4e0a\uff0c\u54b1\u4eec\u4e5f\u53ef\u4ee5\u89c1\u5230\u4e09\u516c\u4e3b\u4e86\u3002\u201d \u3000\u3000\u71d5\u98de\u9053\uff1a\u201c\u5927\u4eba\uff0c\u5351\u804c\u8eab\u53d7\u7684\u7981\u5236\uff0c\u5c1a\u672a\u89e3\u9664\u3002\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u6211\u4e5f\u4e00\u6837\uff0c\u4e0d\u8fc7\uff0c\u9a6c\u5de1\u629a\u5df2\u7b54\u5e94\u4e09\u5929\u4e4b\u5185\uff0c\u89e3\u53bb\u54b1\u4eec\u8eab\u4e0a\u7684\u7981\u5236\u3002\u201d \u3000\u3000\u201c\u4e8c\u53d4\u2026\u2026\u201d\u738b\u91cd\u5c71\u9053\uff1a\u201c\u5c0f\u4f84\u8ba4\u4e3a\u54b1\u4eec\u4e0d\u662f\u88ab\u836f\u7269\u6240\u6bd2\u71d5\u98de\u9053\uff1a\u201d\u738b\u5144\u7684\u610f\u601d\u662f\u2026\u2026 \u201c \u3000\u3000\u201c\u6211\u5e26\u6709\u6b66\u5f53\u6d3e\u4e2d\u7684\u767e\u8349\u89e3\u6bd2\u4e39\uff0c\u5982\u662f\u836f\u7269\u6240\u5236\uff0c\u6bd2\u6027\u65e9\u8be5\u89e3\u53bb\uff0c\u672c\u95e8\u89e3\u6bd2\u4e39\u53ef\u89e3\u767e \u6bd2\uff0c\u4f46\u6211\u8fde\u670d\u4e24\u7c92\u5168\u65e0\u6548\u679c\u3002\u201d \u3000\u3000\u71d5\u98de\u9053\uff1a\u201c\u4e5f\u8bb8\u662f\u836f\u4e0d\u5bf9\u75c7\uff01\u201d \u3000\u3000\u201c\u5c31\u7b97\u662f\u836f\u4e0d\u5bf9\u75c7\u2026\u2026\u201d\u738b\u91cd\u5c71\u9053\uff1a\u201c\u4f46\u6211\u670d\u4e0b\u4e4b\u540e\uff0c\u4e5f\u8be5\u6709\u6240\u53cd\u5e94\u624d\u5bf9\uff0c\u7adf\u7136\u662f\u5b8c\u5168 \u6ca1\u6709\u611f\u89c9\u3002\u201d \u3000\u3000\u738b\u5f64\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u628a\u4ed6\u4eec\u9001\u6211\u7684\u4e00\u7c92\u4e39\u4e38\uff0c\u4ea4\u7ed9\u592a\u533b\u9662\u4e2d\u7684\u592a\u533b\u68c0\u67e5\uff0c\u4e5f\u662f\u5168\u65e0\u6240 \u83b7\uff0c\u6240\u4ee5\uff0c\u6211\u4e5f\u6709\u70b9\u6000\u7591\uff0c\u54b1\u4eec\u53ef\u80fd\u662f\u88ab\u4e00\u79cd\u602a\u5f02\u7684\u6b66\u529f\u6240\u4f24\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u5c0f\u4f84\u4e5f\u6709\u8fd9\u79cd\u611f\u89c9\uff0c\u771f\u6c14\u65e0\u6cd5\u805a\u96c6\u4e39\u7530\uff0c\u90a3\u91cc\u4f3c\u6709\u4e00\u80a1\u5947\u5f02\u7684\u529b\u91cf\u963b\u9694\u3002\u201d \u3000\u3000\u71d5\u98de\u3001\u8d75\u4fdd\u3001\u9648\u5b8f\u9f50\u9f50\u70b9\u5934\u3002 \u3000\u3000\u738b\u5f64\u7565\u4e00\u6c89\u541f\uff0c\u9053\uff1a\u201c\u7b49\u4eca\u591c\u89c1\u8fc7\u4e86\u4e09\u516c\u4e3b\u4e4b\u540e\uff0c\u6211\u4f1a\u5bf9\u9a6c\u5de1\u629a\u63d0\u51fa\u8fd9\u4ef6\u4e8b\u60c5\u3002\u201d \u3000\u3000\u738b\u5f64\u7b49\u4eba\u521a\u521a\u7528\u8fc7\u996d\uff0c\u9a6c\u6587\u4e2d\u5c31\u8fdb\u4e86\u5927\u5385\uff0c\u544a\u8bc9\u738b\u91cd\u5c71\u3001\u71d5\u98de\u3001\u8d75\u4fdd\u3001\u9648\u5b8f\uff0c\u5df2\u5e2e\u56db\u4eba \u6574\u7406\u597d\u5367\u5ba4\uff0c\u4eca\u591c\u5c31\u53ef\u4ee5\u8fc1\u8fc7\u6765\u548c\u738b\u5f64\u4f4f\u5728\u4e00\u5904\u4e86\u3002 \u3000\u3000\u8fd9\u5012\u5927\u51fa\u51e0\u4eba\u610f\u6599\u4e4b\u5916\uff0c\u4f3c\u662f\u654c\u610f\u5168\u6d88\u3002 \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u5144\u5f1f\u8ba4\u4e3a\u6700\u91cd\u8981\u7684\u4e24\u4ef6\u4e8b\uff0c\u4e00\u662f\u5feb\u70b9\u628a\u4e09\u516c\u4e3b\u7684\u8baf\u606f\u4f20\u4eba\u4eac\u4e2d\uff0c\u4ee5\u6170\u5929\u5fc3\uff1b \u4e8c\u662f\u6211\u7b49\u8eab\u53d7\u7981\u5236\uff0c\u80fd\u65e9\u4e9b\u89e3\u53bb\u3002\u201d \u3000\u3000\u201c\u4e09\u516c\u4e3b\u5df2\u8fdb\u5165\u5f00\u5c01\u57ce\uff0c\u5927\u6982\u5c31\u8981\u5230\u4e86\uff0c\u5144\u5f1f\u60f3\u8bf7\u6559\u738b\u5144\uff0c\u51c6\u5907\u5728\u90a3\u91cc\u548c\u516c\u4e3b\u89c1\u9762\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u7b11\u7b11\u9053\uff1a\u201c\u660e\u65e5\u5348\u65f6\u4e4b\u524d\uff0c\u8bf8\u4f4d\u8eab\u53d7\u7684\u7981\u5236\uff0c\u4e00\u5b9a\u53ef\u4ee5\u89e3\u53bb\uff0c\u4fdd\u8bc1\u662f\u5b8c\u5168\u6062\u590d\uff0c \u6beb\u53d1\u65e0\u635f\u3002\u201d \u3000\u3000\u738b\u5f64\u7684\u5fc3\u4e2d\u5fd6\u9053\uff1a\u4ed6\u4f3c\u662f\u5c31\u5728\u65c1\u8fb9\u7b49\u7740\uff0c\u6211\u4eec\u521a\u5403\u5b8c\u996d\uff0c\u4ed6\u5c31\u8fdb\u6765\uff0c\u9a6c\u6587\u4e2d\u679c\u662f\u80fd\u5c48\u80fd \u4f38\u7684\u4eba\u7269\uff0c\u975e\u540c\u5c0f\u53ef\u3002 \u3000\u3000\u5fc3\u4e2d\u5fd6\u601d\uff0c\u53e3\u4e2d\u5374\u8bf4\u9053\uff1a\u201c\u5c31\u5728\u6b64\u5730\u62dc\u89c1\u516c\u4e3b\uff0c\u4e0d\u77e5\u662f\u5426\u9002\u5f53\uff1f\u201d \u3000\u3000\u201c\u4e09\u516c\u4e3b\u4e5f\u662f\u8fd9\u4e2a\u610f\u601d\u2026\u2026\u201d\u9a6c\u6587\u4e2d\u7b11\u9053\uff1a\u201c\u5979\u5e0c\u671b\u4e00\u4e0b\u8f66\u5c31\u53ef\u4ee5\u548c\u4e09\u5144\u89c1\u9762\uff0c\u4f46\u5144\u5f1f \u6050\u4e09\u5144\u522b\u6709\u9ad8\u89c1\uff0c\u7279\u522b\u6765\u95ee\u4e00\u58f0\u3002\u201d \u3000\u3000\u738b\u5f64\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u91d1\u6280\u5de5\u53f6\u7684\u516c\u4e3b\uff0c\u7adf\u77e5\u6653\u901a\u6743\u8fbe\u53d8\uff0c\u4e5f\u771f\u7684\u59d4\u5c48\u5979\u4e86\u3002\u201d \u3000\u3000\u201c\u5982\u82e5\u738b\u5144\u8ba4\u4e3a\u4e09\u516c\u4e3b\u53ea\u6653\u5f97\u901a\u6743\u8fbe\u53d8\uff0c\u90a3\u6765\u514d\u592a\u5c0f\u770b\u4e09\u516c\u4e3b\u4e86\u3002\u201d\u9a6c\u6587\u4e2d\u7b11\u9053\uff1a\u201c\u4eca \u591c\uff0c\u738b\u5144\u89c1\u8fc7\u4e09\u516c\u4e3b\u4e4b\u540e\uff0c\u4fdd\u8bc1\u662f\u53e6\u6709\u4e00\u756a\u89c2\u611f\u3002\u201d \u3000\u3000\u738b\u5f64\u5fae\u5fae\u4e00\u6014\uff0c\u9053\uff1a\u201c\u9a6c\u5c3c\u5bf9\u4e09\u516c\u4e3b\u7684\u4e3a\u4eba\uff0c\u4f3c\u662f\u77e5\u6653\u5f97\u5f88\u6e05\u695a\u554a\uff01\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u7b49\u738b\u89c1\u89c1\u8fc7\u4e09\u516c\u4e3b\u4e4b\u540e\uff0c\u54b1\u4eec\u518d\u4f5c\u8be6\u8c08\uff0c\u5144\u5f1f\u8fd9\u5c31\u53bb \u8fce\u63a5\u4e09\u516c\u4e3b\u51e4\u9a7e\u3002\u201d \u3000\u3000\u201c\u4e0d\u6562\u6709\u52b3\uff01\u201d\u4e00\u4e2a\u5a07\u751c\u7684\u58f0\u97f3\uff0c\u4f20\u5165\u8033\u9645\uff0c\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u5df2\u5230\u5385\u5916\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u7684\u6b63\u662f\u60dc\u6625\u3002 \u3000\u3000\u5979\u6539\u53d8\u4e86\u8fc7\u53bb\u5a07\u5f31\u5bab\u5973\u7684\u88c5\u675f\uff0c\u6362\u4e86\u4e00\u5957\u7384\u8eab\u52b2\u88c5\uff0c\u8170\u4e2d\u7cfb\u4e86\u4e00\u652f\u725b\u89d2\u4e3a\u67c4\u7684\u5f2f\u6708\u5c0f \u5200\u3002 \u3000\u3000\u8bf4\u662f\u5c0f\u5200\uff0c\u5176\u5b9e\u4e5f\u6709\u4e00\u5c3a\u591a\u957f\uff0c\u53ea\u662f\u6bd4\u8d77\u4e00\u822c\u80e1\u4eba\u7528\u7684\u5f2f\u6708\u5200\uff0c\u5c0f\u4e86\u4e0d\u6b62\u4e00\u534a\u3002 \u3000\u3000\u5979\u6574\u4e2a\u4eba\u5c31\u50cf\u6eb6\u5728\u591c\u8272\u4e2d\u4e00\u822c\uff0c\u4e0d\u5f00\u53e3\uff0c\u5f88\u96be\u53d1\u89c9\u5979\u505c\u8eab\u7684\u4f4d\u7f6e\uff0c\u4e00\u5f00\u53e3\uff0c\u7adf\u7136\u53d1\u89c9\u5979 \u5c31\u7ad9\u5728\u5927\u5385\u5916\u9762\u3002 \u3000\u3000\u738b\u5f64\u3001\u9a6c\u6587\u4e2d\u7b49\u9f50\u9f50\u8fce\u5411\u95e8\u5916\uff0c\u4e09\u516c\u4e3b\u5df2\u5728\u601c\u82b1\u6276\u6301\u4e0b\uff0c\u7f13\u6b65\u800c\u5165\u3002 \u3000\u3000\u867d\u7136\uff0c\u4e09\u516c\u4e3b\u73b0\u5728\u662f\u843d\u96be\u7684\u4eba\uff0c\u4f46\u5979\u7684\u516c\u4e3b\u8eab\u4efd\u72b9\u5728\uff0c\u738b\u5f64\u4e00\u64a9\u957f\u886b\uff0c\u8dea\u4e86\u4e0b\u53bb\uff0c\u7d27\u63a5 \u7740\u738b\u91cd\u5c71\u3001\u8d75\u4fdd\u3001\u9648\u5b8f\u3001\u71d5\u98de\uff0c\u76f8\u7ee7\u8dea\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u9a6c\u6587\u4e2d\u56db\u987e\u4e86\u4e00\u773c\uff0c\u65e0\u53ef\u5948\u4f55\uff0c\u4e5f\u53ea\u597d\u8dea\u4e0b\u8fce\u9a7e\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u53f3\u624b\u8f7b\u6325\uff0c\u9053\uff1a\u201c\u8bf8\u4f4d\u8bf7\u8d77\uff0c\u54b1\u4eec\u5230\u5385\u4e2d\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u60dc\u6625\u8eab\u5b50\u4e00\u95ea\uff0c\u4eba\u5df2\u5230\u4e86\u4e09\u516c\u4e3b\u5de6\u4fa7\uff0c\u548c\u601c\u82b1\u4e00\u5de6\u4e00\u53f3\u6276\u6301\u7740\u4e09\u516c\u4e3b\u6b65\u5165\u5385\u95e8\u3002 \u3000\u3000\u4e09\u5f64\u4e5f\u8d77\u8eab\u884c\u5165\u4e86\u5385\u4e2d\u3002 \u3000\u3000\u738b\u91cd\u5c71\u6697\u6697\u5bdf\u770b\u4e86\u60dc\u6625\u7684\u8eab\u6cd5\uff0c\u53ea\u89c9\u5f97\u5979\u7684\u8eab\u6cd5\u5feb\u5982\u95ea\u7535\uff0c\u8f7b\u529f\u4e4b\u4f73\uff0c\u81ea\u5df1\u4e07\u4e07\u96be\u4ee5\u654c \u5bf9\u3002 \u3000\u3000\u706f\u5149\u4e0b\uff0c\u53ea\u89c1\u4e09\u516c\u4e3b\u539f\u672c\u6731\u6da6\u7389\u5706\u7684\u8138\u8272\uff0c\u5fae\u89c1\u82cd\u767d\uff0c\u4f46\u795e\u60c5\u5e73\u9759\uff0c\u5634\u89d2\u7adf\u6302\u4e00\u4efd\u9690\u73b0 \u7684\u5fae\u7b11\u3002 \u3000\u3000\u5979\u4eba\u8fd8\u672a\u843d\u5750\uff0c\u5c31\u62a2\u5148\u8bf4\u9053\uff1a\u201c\u8bf8\u4f4d\u4e0d\u8981\u518d\u770b\u91cd\u6211\u8fd9\u4e2a\u516c\u4e3b\u7684\u8eab\u4efd\uff0c\u738b\u7edf\u9886\u4e5f\u8bf7\u629b\u53bb\u5185 \u5bab\u7edf\u9886\u7684\u8eab\u4efd\uff0c\u5927\u5bb6\u90fd\u4ee5\u6c5f\u6e56\u4eba\u8eab\u4efd\u76f8\u89c1\uff0c\u8bf4\u8bdd\u4e4b\u95f4\u65e0\u6240\u987e\u5fcc\uff0c\u4e5f\u8bb8\u80fd\u6709\u6240\u7ed3\u8bba\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u54b1\u4eec\u9075\u4ece\u793a\u8c15\uff0c\u53ea\u662f\u592a\u8fc7\u59d4\u5c48\u2026\u2026\u201d \u3000\u3000\u6447\u5934\u6253\u65ad\u4e86\u738b\u5f64\u7684\u8bdd\uff0c\u4e09\u516c\u4e3b\u8bf4\u9053\uff1a\u201c\u53c8\u6765\u4e86\uff0c\u6211\u5df2\u7ecf\u8bf4\u8fc7\uff0c\u5927\u5bb6\u90fd\u628a\u8eab\u4efd\u6487\u5f00\uff0c\u4f60\u4eec \u628a\u6211\u5f53\u6210\u662f\u6b66\u5f53\u6216\u5ce8\u5d4b\u95e8\u4e0b\u5973\u5f1f\u5b50\u770b\u5f85\uff0c\u5f7c\u6b64\u5c31\u8f7b\u677e\u591a\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\u3015\u90a3\u5c31\u7531\u6211\u5148\u8bf4\u2026\u2026\u201d\u738b\u5f64\u9053\uff1a\u201c\u6211\u5df2\u548c\u9a6c\u6587\u4e2d\u8c08\u59a5\u4e86\uff0c\u7687\u4e0a\u5df2\u5141\u64a4\u519b\u5165\u5173\uff0c\u8499\u53e4 \u4eba\u5e74\u5e74\u8fdb\u8d21\uff0c\u8fb9\u754c\u6682\u606f\u6218\u706b\uff0c\u53ea\u662f\u4e09\u516c\u4e3b\u5374\u8981\u5728\u4e24\u5e74\u540e\uff0c\u624d\u80fd\u56de\u4eac\u9762\u5723\u3002\u201d \u3000\u3000\u201c\u5f88\u597d\u7684\u7ed3\u679c\u2026\u2026\u201d\u4e09\u516c\u4e3b\u9053\uff1a\u201c\u7687\u4e0a\u53ef\u8981\u6211\u505a\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5979\u4e0d\u79f0\u7236\u7687\uff0c\u76f4\u4e4e\u7687\u4e0a\uff0c\u679c\u7136\u662f\u51c6\u5907\u629b\u5f00\u516c\u4e3b\u7684\u8eab\u4efd\u4e86\u3002 \u3000\u3000\u201c\u7687\u4e0a\u8981\u4e09\u516c\u4e3b\u5e73\u5b89\u65e0\u4e8b\u2026\u2026\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u5374\u62a2\u7740\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u738b\u5144\u5c3d\u7ba1\u653e\u5fc3\uff0c\u4e24\u5e74\u540e\u516c\u4e3b\u56de\u4eac\uff0c\u9a6c\u67d0\u4eba\u62c5\u4fdd\u4e09\u516c\u4e3b\u6beb\u53d1\u4e0d \u635f\u3002\u201d \u3000\u3000\u738b\u91cd\u5c71\u9053\uff1a\u201c\u5728\u4e0b\u8bf7\u6559\u9a6c\u5927\u4eba\uff0c\u5982\u662f\u4e24\u5e74\u4e4b\u540e\uff0c\u8499\u53e4\u4eba\u4e0d\u5b88\u7ea6\u5b9a\u5462\uff1f\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u7b11\u9053\uff1a\u201c\u738b\u5c11\u5144\u591a\u8651\u4e86\uff0c\u8499\u53e4\u4e0d\u5b88\u7ea6\u5b9a\uff0c\u5929\u671d\u5c31\u5e08\u51fa\u6709\u540d\u4e86\u3002\u201d \u3000\u3000\u4e09\u516c\u4e3b\u7b11\u9053\uff1a\u201c\u8fd9\u4e00\u70b9\uff0c\u5012\u4e0d\u7528\u62c5\u5fc3\uff0c\u4ed6\u4eec\u5982\u548c\u5927\u660e\u671d\u7ea6\u597d\u4e86\u4e24\u5e74\u653e\u4eba\uff0c\u7edd\u4e0d\u4f1a\u5931\u7ea6\u80cc \u4fe1\u7684\u3002\u201d \u3000\u3000\u738b\u5f64\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u7687\u4e0a\u5e0c\u671b\u786e\u77e5\u4e09\u516c\u4e3b\u5b89\u7136\u65e0\u6059\uff0c\u624d\u80fd\u653e\u5fc3\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u6211\u5199\u5c01\u4fe1\uff0c\u4f60\u5e26\u56de\u4eac\u53bb\u3002\u201d \u3000\u3000\u201c\u591a\u8c22\u516c\u4e3b\uff01\u201d\u738b\u5f64\u76ee\u5149\u8f6c\u52a8\uff0c\u4e00\u63a0\u60dc\u6625\u3001\u9a6c\u6587\u4e2d\uff0c\u9053\uff1a\u201c\u738b\u67d0\u6709\u4e00\u4e2a\u4e0d\u60c5\u4e4b\u6c42\u2026\u2026\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u7b11\u9053\uff1a\u201c\u8bf7\u8bf4\u3002\u201d \u3000\u3000\u738b\u5f64\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8bf7\u9a6c\u5144\u548c\u60dc\u6625\u59d1\u5a18\u6682\u65f6\u9000\u51fa\u6b64\u5730\uff0c\u4ee5\u4fbf\u5728\u4e0b\u548c\u4e09\u516c\u4e3b\u79c1\u4e0b\u4ea4\u8c08\u6570 \u8bed\u3002\u201d \u3000\u3000\u201c\u5e94\u8be5\u7684\uff01\u201d\u9a6c\u6587\u4e2d\u9053\uff1a\u201c\u60dc\u6625\uff0c\u54b1\u4eec\u51fa\u53bb\u8d70\u8d70\u5427\uff01\u201d \u3000\u3000\u60dc\u6625\u867d\u672a\u53cd\u9a73\u9a6c\u6587\u4e2d\uff0c\u5374\u51b7\u51b7\u7684\u770b\u4e86\u738b\u5f64\u4e00\u773c\uff0c\u9053\uff1a\u201c\u8fd9\u91cc\u4e0d\u662f\u7981\u5bab\u5185\u82d1\uff0c\u53ea\u8bb2\u5b9e\u529b\u4e0d \u8bba\u8eab\u4efd\uff0c\u4e09\u516c\u4e3b\u7684\u8eab\u4f53\u4e0d\u592a\u597d\uff0c\u8bf8\u4f4d\u6709\u8bdd\u5e0c\u671b\u5c3d\u5feb\u7684\u8bf4\u5b8c\u3002\u201d \u3000\u3000\u7f13\u7f13\u4e3e\u6b65\uff0c\u968f\u5728\u9a6c\u6587\u4e2d\u8eab\u540e\u800c\u53bb\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u5fcd\u4e0b\u4e86\u8fd9\u53e3\u6c14\uff01 \u3000\u3000\u738b\u5f64\u4e5f\u541e\u4e0b\u4e86\u8fd9\u53e3\u6c14\uff01 \u3000\u3000\u4f46\u738b\u91cd\u5c71\u3001\u8d75\u4fdd\u3001\u9648\u5b8f\uff0c\u8138\u4e0a\u90fd\u6d6e\u73b0\u51fa\u6124\u6124\u4e0d\u5e73\u4e4b\u8272\uff0c\u53ea\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u7684\u6b66\u529f\u672a\u590d\uff0c\u65e0\u80fd \u53d1\u4f5c\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u4f4e\u58f0\u9053\uff1a\u201c\u601c\u82b1\uff0c\u53bb\u95e8\u53e3\u770b\u770b\uff01\u201d \u3000\u3000\u201c\u662f\uff01\u201d\u601c\u82b1\u5e94\u4e86\u4e00\u58f0\uff0c\u6b65\u51fa\u4e86\u5927\u5385\u3002 \u3000\u3000\u738b\u5f64\u76ee\u5149\u4e00\u63a0\u8d75\u4fdd\u3001\u9648\u5b8f\u9053\uff1a\u201c\u53bb\uff01\u5e2e\u52a9\u601c\u82b1\u59d1\u5a18\u4e00\u628a\uff0c\u76d1\u89c6\u56db\u5468\u3002\u201d\u8d75\u4fdd\u3001\u9648\u5b8f\u4e8c\u4eba \u9886\u547d\u800c\u53bb\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u4f4e\u58f0\u9053\uff1a\u201c\u601c\u82b1\u5df2\u4e3a\u60dc\u6625\u63a7\u5236\uff0c\u542c\u5230\u4ec0\u4e48\uff0c\u5979\u90fd\u4f1a\u544a\u8bc9\u60dc\u6625\u3002\u201d \u3000\u3000\u201c\u4e09\u516c\u4e3b\u7684\u5904\u5883\u5f88\u51c4\u51c9\u4e86\uff1f\u201d\u738b\u5f64\u4f4e\u58f0\u9053\uff1a\u201c\u4f8d\u81e3\u51c6\u5907\u62e6\u622a\u4e09\u516c\u4e3b\u7684\u51e4\u9a7e\uff0c\u4e0d\u77e5\u516c\u4e3b\u610f \u4e0b\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u5982\u662f\u6ca1\u6709\u628a\u63e1\uff0c\u6700\u597d\u4e0d\u8981\u83bd\u649e\uff01\u201d\u4e09\u516c\u4e3b\u9053\uff1a\u201c\u6211\u77e5\u9053\u4ed6\u4eec\u7684\u7528\u5fc3\uff0c\u8981\u628a\u6211\u9001\u5165\u9b54\u6559 \u7684\u91cd\u5730\uff0c\u5c3d\u4e24\u5e74\u4e4b\u529b\uff0c\u628a\u6211\u6539\u9020\u6210\u4e00\u4e2a\u9b54\u6559\u5f1f\u5b50\uff0c\u90a3\u65f6\uff0c\u518d\u9001\u6211\u56de\u4eac\uff0c\u6df1\u5bab\u5185\u82d1\u4e2d\u5c31\u6709\u4e86\u4ed6 \u4eec\u7684\u652f\u63f4\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u5927\u53ef\u6015\u4e86\uff01\u201d\u738b\u5f64\u9053\uff1a\u201c\u7edd\u4e0d\u80fd\u8ba9\u4ed6\u4eec\u628a\u4e09\u516c\u4e3b\u8fd0\u51fa\u9633\u5173\u3002\u201d \u3000\u3000\u201c\u65e0\u6cd5\u963b\u6b62\u4e86\u3002\u201d\u4e09\u516c\u4e3b\u8bf4\uff1a\u201c\u9664\u975e\uff0c\u4f60\u80fd\u4e00\u4e3e\u95f4\u5c3d\u51fa\u6b66\u6797\u7cbe\u82f1\u6765\u963b\u622a\u4ed6\u4eec\uff0c\u518d\u8bf4\uff0c\u6211 \u4e5f\u5e0c\u671b\u53bb\u89c1\u8bc6\u4e00\u4e0b\u9b54\u6559\u7684\u795e\u5f02\u90aa\u672f\uff0c\u4f46\u6709\u4e00\u4ef6\u4e8b\u4f7f\u6211\u62c5\u5fc3\u3002\u201d \u3000\u3000\u201c\u8bf7\u516c\u4e3b\u5429\u5490\uff01\u201d \u3000\u3000\u4e09\u516c\u4e3b\u76ee\u6ce8\u738b\u5f64\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u8bad\u7ec3\u4e00\u4e2a\u4eba\u7684\u9b54\u6027\uff0c\u8981\u7528\u4e9b\u4ec0\u4e48\u65b9\u6cd5\uff0c\u6211\u6015\u2026\u2026\u6211 \u6015\u2026\u2026\u4ed6\u4eec\u4f1a\u73b7\u6c61\u4e86\u6211\u7684\u8eab\u4f53\u2026\u2026\u201d \u3000\u3000\u5979\u867d\u7136\u5c3d\u91cf\u4fdd\u6301\u81ea\u5df1\u7684\u5e73\u9759\uff0c\u4f46\u8bf4\u5b8c\u4e86\u4e00\u756a\u8bdd\uff0c\u4ecd\u7136\u6cdb\u8d77\u4e86\u4e00\u8138\u7f9e\u7ea2\u3002 \u3000\u3000\u738b\u5f64\u3001\u738b\u91cd\u5c71\u3001\u71d5\u98de\u5168\u542c\u5f97\u5446\u4f4f\u4e86\uff0c\u8fd9\u662f\u4e2a\u65e0\u6cd5\u7b54\u590d\u7684\u95ee\u9898\uff0c\u4e09\u4e2a\u4eba\u9762\u9762\u76f8\u89d1\u3002 \u3000\u3000\u5e78\u597d\uff0c\u4e09\u516c\u4e3b\u6ca1\u8981\u4ed6\u4eec\u7b54\u590d\u7684\u610f\u601d\uff0c\u63a5\u7740\u53c8\u8bf4\uff1a\u201c\u6211\u4e2a\u4eba\u6e05\u767d\u53d7\u6c61\u5012\u65e0\u5173\u8981\u7d27\uff0c\u4f46\u6211\u8fd9 \u516c\u4e3b\u7684\u8eab\u4efd\u53d7\u8fb1\uff0c\u90a3\u5c31\u6709\u4e9b\u6127\u5bf9\u7236\u7687\u4e86\uff0c\u6240\u4ee5\u6211\u8981\u5148\u5bf9\u4f60\u4eec\u8bf4\u660e\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u6211\u4f1a\u5c3d\u529b\u4fdd\u62a4\u81ea \u5df1\uff0c\u4e00\u65e6\u6f5c\u5728\u9b54\u6027\u5f15\u53d1\u5fc5\u4f7f\u6211\u6709\u6240\u53d8\u5316\uff0c\u4f60\u4eec\u53d1\u89c9\u4e86\uff0c\u5c31\u4e0d\u8981\u518d\u6709\u987e\u5fcc\uff0c\u60f3\u529e\u6cd5\u6740\u6b7b \u6211\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u8fd9\u4e2a\u2026\u2026\u201d\u738b\u5f64\u5927\u611f\u72b9\u8c6b\uff0c\u9053\uff1a\u201c\u8fd9\u4e9b\u4e8b\uff0c\u662f\u5426\u8981\u594f\u660e\u5723\u4e0a\u5462\uff1f\u201d \u3000\u3000\u201c\u8981\uff01\u6211\u7684\u4fe1\u4e0a\u4e0d\u4fbf\u5199\u660e\uff0c\u4f60\u627e\u4e2a\u673a\u4f1a\u548c\u7687\u4e0a\u5bc6\u8c08\u4e00\u4e0b\uff0c\u6b64\u4e8b\u53ea\u80fd\u5fc3\u7167\u4e0d\u5ba3\uff0c\u4e0d\u80fd\u5f20\u626c \u51fa\u53bb\uff0c\u544a\u8bc9\u7236\u7687\uff0c\u6211\u6ca1\u6709\u5b89\u5168\u56de\u6765\u4e4b\u524d\uff0c\u522b\u60e6\u8bb0\u6211\uff0c\u5c31\u5f53\u6211\u65e9\u5df2\u6b7b\u53bb\u5c31\u662f\u3002\u201d \u3000\u3000\u7a81\u7136\uff0c\u64a9\u8d77\u4e86\u5de6\u8896\uff0c\u9732\u51fa\u4e86\u4e00\u622a\u7389\u6da6\u6731\u5706\u7684\u7c89\u81c2\u3002 \u3000\u3000\u8fd9\u4e00\u7740\u7a81\u5982\u5176\u6765\uff0c\u628a\u738b\u5f64\u7b49\u4eba\u5413\u4e86\u4e00\u5927\u8df3\uff0c\u5927\u5bb6\u90fd\u522b\u8fc7\u5934\u53bb\uff0c\u4e0d\u6562\u591a\u770b\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u770b\u554a\uff01\u8fd9\u662f\u6211\u70b9\u7684\u5b88\u5bab\u7802\u2026\u2026\u201d \u3000\u3000\u4e09\u4e2a\u4eba\u8f6c\u5934\u770b\u53bb\uff0c\u679c\u7136\uff0c\u5728\u4e09\u516c\u4e3b\u767d\u808c\u4f3c\u7389\u7684\u7c89\u81c2\u4e0a\uff0c\u8fd1\u814b\u5904\uff0c\u6709\u4e00\u9897\u7eff\u8c46\u5927\u5c0f\u7684\u7ea2 \u70b9\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u653e\u4e0b\u8863\u8896\uff0c\u53c8\u9053\uff1a\u201c\u6b64\u5fc3\u53ef\u9274\u65e5\u6708\uff0c\u4f60\u4eec\u8bb0\u4e0b\u90a3\u70b9\u5b88\u5bab\u7802\u7684\u4f4d\u7f6e\uff0c\u4e00\u65e6\u6211\u56de\u4e2d \u539f\uff0c\u4f60\u4eec\u8981\u60f3\u6cd5\u5b50\u67e5\u67e5\u770b\u2026\u2026\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u8fd9\u70b9\u662f\u5426\u4e5f\u8981\u544a\u8bc9\u7687\u4e0a\u5462\uff1f\u201d \u3000\u3000\u201c\u90a3\u5c31\u4e0d\u7528\u4e86\u2026\u2026\u201d\u4e09\u516c\u4e3b\u9053\uff1a\u201c\u4f60\u8981\u628a\u8bdd\u8bf4\u5f97\u59d4\u5a49\u4e00\u70b9\uff0c\u522b\u8ba9\u7687\u4e0a\u542c\u4e86\u96be\u8fc7\u3002\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u4f8d\u81e3\u8bb0\u4e0b\u4e86\u3002\u201d \u3000\u3000\u4f46\u95fb\u60dc\u6625\u7684\u58f0\u97f3\uff0c\u4f20\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u8bf8\u4f4d\u7684\u4f53\u5df2\u79c1\u8bed\u8bf4\u5b8c\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5979\u6545\u610f\u85d0\u89c6\u7fa4\u81e3\uff0c\u663e\u7136\u662f\u6709\u610f\u7684\u6c61\u8fb1\u3002 \u3000\u3000\u738b\u5f64\u5fcd\u4e0d\u4f4f\u4e86\uff0c\u53cc\u7709\u4e00\u626c\uff0c\u60f3\u8981\u53d1\u4f5c\uff0c\u4f46\u5374\u88ab\u4e09\u516c\u4e3b\u7ed9\u538b\u4e86\u4e0b\u6765\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u9b54 \u6027\uff0c\u4e0d\u80fd\u6fc0\u6012\u5979\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u2026\u2026\u201d\u738b\u5f64\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u591a\u627f\u6307\u6559\uff01\u201d \u3000\u3000\u8bdd\u843d\uff0c\u63a5\u7740\u53c8\u63d0\u9ad8\u4e86\u58f0\u97f3\uff0c\u8bf4\u9053\uff1a\u201c\u60dc\u6625\u59d1\u5a18\uff0c\u8bf7\u5165\u5385\u4e2d\u6765\u5427\uff01 \u3000\u3000\u738b\u67d0\u4eba\u8fd8\u6709\u4e8b\u8bf7\u6559\uff01\u201c \u3000\u3000\u4f46\u89c1\u4eba\u5f71\u4e00\u95ea\uff0c\u60dc\u6625\u5df2\u4fcf\u751f\u751f\u7684\u7ad9\u5728\u4e09\u516c\u4e3b\u7684\u8eab\u4fa7\u3002 \u3000\u3000\u738b\u5f64\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u597d\u5feb\u7684\u8eab\u6cd5\uff1f\u201d \u3000\u3000\u60dc\u6625\u5ae3\u7136\u4e00\u7b11\uff0c\u5a07\u5a9a\u6a2a\u751f\u7684\u8bf4\u9053\uff1a\u201c\u738b\u7edf\u9886\u662f\u7531\u8877\u7684\u8d5e\u7f8e\u5462\uff1f \u3000\u3000\u8fd8\u662f\u968f\u53e3\u5e94\u916c\u4e00\u4e0b\uff1f\u201c \u3000\u3000\u5979\u7b11\u5f97\u9b41\u529b\u56db\u5c04\uff0c\u548c\u8fc7\u53bb\u90a3\u4efd\u7aef\u5e84\u5a34\u96c5\u5b8c\u5168\u4e0d\u540c\u3002 \u3000\u3000\u201c\u7531\u8877\u7684\u8d5e\u7f8e\uff01\u201d\u738b\u5f64\u9053\uff1a\u201c\u60dc\u6625\u59d1\u5a18\u5982\u6b64\u8eab\u624b\uff0c\u5728\u5bab\u4e2d\u4f4f\u4e86\u6570\u5e74\u4e4b\u4e45\uff0c\u738b\u67d0\u4eba\u7adf\u672a\u53d1 \u89c9\uff0c\u771f\u662f\u778e\u4e86\u8fd9\u53cc\u773c\u775b\u3002\u201d \u3000\u3000\u201c\u522b\u8fd9\u4e48\u81ea\u8d23\uff0c\u516c\u4e3b\u6b66\u529f\u5f3a\u4f60\u5341\u500d\uff0c\u5979\u4e5f\u6ca1\u6709\u53d1\u89c9\u554a\uff01\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u601c\u82b1\u3001\u8d75\u4fdd\u3001\u9648\u5b8f\u3001\u9a6c\u6587\u4e2d\uff0c\u9c7c\u8d2f\u8fdb\u5165\u4e86\u5927\u5385\u3002 \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u60dc\u6625\u59d1\u5a18\uff0c\u738b\u67d0\u6073\u6258\u4e00\u4e8b\uff0c\u597d\u597d\u7684\u7167\u987e\u516c\u4e3b\uff0c\u65e5\u540e\uff0c\u5fc5\u6709\u62a5\u507f\u3002 \u3000\u3000\u201c\u653e\u5fc3\u4e86\uff01\u201d\u60dc\u6625\u9053\uff1a\u201c\u516c\u4e3b\u806a\u660e\u7edd\u4f26\uff0c\u65e9\u5df2\u6709\u4e86\u6253\u7b97\uff0c\u6211\u60dc\u6625\u80fd\u7167\u987e\u7684\u5730\u65b9\uff0c\u5fc5\u5c06\u5168 \u529b\u4ee5\u8d74\u3002\u201d \u3000\u3000\u738b\u5f64\u4e00\u62b1\u62f3\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u8fd9\u91cc\u5148\u8bb2\u4e86\uff01\u201d \u3000\u3000\u60dc\u6625\u7b11\u9053\uff1a\u201c\u4e0d\u7528\u5ba2\u6c14\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u9a6c\u6587\u4e2d\u5df2\u547d\u4eba\u9001\u4e0a\u6587\u623f\u56db\u5b9d\uff0c\u4e09\u516c\u4e3b\u5306\u5306\u5199\u4e86\u4e00\u5c01\u51fd\u4ef6\uff0c\u4ea4\u7ed9\u738b\u5f64\uff0c\u7ad9\u8d77\u8eab\u5b50\uff0c \u9053\uff1a\u201c\u6211\u8d70\u4e86\uff0c\u89c1\u5230\u7687\u4e0a\uff0c\u4ee3\u6211\u81f4\u610f\u3002\u201d \u3000\u3000\u5728\u60dc\u6625\u3001\u601c\u82b1\u5de6\u53f3\u6276\u6301\u4e0b\uff0c\u7f13\u6b65\u79bb\u53bb\u3002 \u3000\u3000\u671b\u7740\u4e09\u516c\u4e3b\u6d88\u5931\u5728\u591c\u8272\u4e2d\u7684\u80cc\u5f71\uff0c\u738b\u5f64\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u770b\u5979\u5a07\u5f31\u6a21\u6837\uff0c\u5f88\u96be\u53eb\u4eba\u76f8 \u4fe1\uff0c\u5979\u4f1a\u662f\u4e00\u4f4d\u8eab\u8d1f\u4e0a\u4e58\u5251\u672f\u7684\u4eba\uff1f\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u7b11\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u5c31\u53eb\u771f\u4eba\u4e0d\u9732\u76f8\u554a\uff01\u201d \u3000\u3000\u201c\u8bf4\u7684\u4e5f\u662f\uff01\u2026\u2026\u201d\u738b\u5f64\u5e7d\u5e7d\u5730\u9053\uff1a\u201c\u60dc\u6625\u5728\u5bab\u4e2d\u4e94\u5e74\u4e4b\u4e45\uff0c\u6211\u5c31\u6ca1\u6709\u77a7\u51fa\u5979\u6709\u4e00\u8eab\u9ad8 \u660e\u7684\u6b66\u529f\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u73b0\u5728\uff0c\u8bf8\u4f4d\u662f\u5426\u8981\u65e9\u4e9b\u5b89\u6b47\uff0c\u660e\u65e5\u6b63\u5348\u65f6\u5206\uff0c\u89e3\u53bb\u8bf8\u4f4d\u8eab\u4e0a\u7684 \u7981\u5236\uff0c\u8bf8\u4f4d\u5fc3\u60c5\u6109\u5feb\uff0c\u5728\u6b64\u75db\u5feb\u7684\u73a9\u4e0a\u51e0\u5929\uff0c\u518d\u56de\u4eac\u53bb\u590d\u547d\uff01\u201d \u3000\u3000\u4ed6\u53cc\u76ee\u51dd\u6ce8\u5728\u738b\u5f64\u7684\u8138\u4e0a\uff0c\u8138\u4e0a\u6cdb\u8d77\u4e86\u4e00\u62b9\u8be1\u5f02\u7684\u7b11\u5bb9\uff0c\u4f3c\u662f\u5728\u8868\u8fbe\u4ec0\u4e48\uff1f\u53c8\u4f3c\u662f\u5e26\u7740 \u4e00\u4efd\u8ba5\u5632\u4e4b\u610f\u3002 \u3000\u3000\u738b\u5f64\u5fc3\u4e2d\u660e\u767d\uff0c\u5fc5\u662f\u9a6c\u4e09\u544a\u8bc9\u4ed6\u4eac\u4e2d\u53d1\u751f\u7684\u4e8b\u60c5\uff0c\u5fc3\u4e2d\u53c8\u662f\u5b89\u6170\u53c8\u662f\u96be\u8fc7\u3002 \u3000\u3000\u96be\u8fc7\u7684\u662f\u534a\u4e16\u82f1\u540d\uff0c\u7adf\u7136\u6bc1\u5728\u6570\u65e5\u4e4b\u95f4\uff0c\u8fd9\u4ef6\u4e8b\u4e00\u65e6\u4f20\u626c\u6c5f\u6e56\uff0c\u771f\u662f\u65e0\u989c\u518d\u89c1\u6c5f\u4e1c\u7236 \u8001\u3002 \u3000\u3000\u5b89\u6170\u7684\u662f\uff0c\u8fd9\u756a\u88c5\u4f5c\uff0c\u7adf\u7136\u7792\u8fc7\u4e86\u9a6c\u6587\u4e2d\u8fd9\u5934\u8001\u72d0\u72f8\uff0c\u4e5f\u8db3\u4ee5\u81ea\u8c6a\u4e86\uff0c\u4e5f\u8bb8\u5c31\u662f\u8fd9\u6837\u624d \u677e\u61c8\u4e86\u9a6c\u6587\u4e2d\u7684\u6212\u5907\u4e4b\u5fc3\u3002\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u4eba\u5374\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5bf9\uff01\u660e\u5929\u89e3\u53bb\u4e86\u8eab\u4e2d\u7684\u7981\u5236\uff0c \u53ef\u4ee5\u653e\u5f00\u80f8\u6000\uff0c\u597d\u597d\u7684\u73a9\u5b83\u51e0\u5929\u4e86\u3002\u201d \u3000\u3000\u201c\u5305\u7ba1\u8ba9\u738b\u5144\u8d81\u5fc3\u5982\u610f\uff0c\u4e50\u4e0d\u601d\u8700\u2026\u2026\u201d\u9a6c\u6587\u4e2d\u795e\u79d8\u4e00\u7b11\uff0c\u8d77\u8eab\u79bb\u53bb\u3002 \u3000\u3000\u738b\u5f64\u56de\u5230\u4e86\u5367\u5ba4\u4e2d\uff0c\u63a9\u4e0a\u4e86\u623f\u95e8\uff0c\u7184\u53bb\u4e86\u706b\u70db\uff0c\u89e3\u8863\u767b\u69bb\uff0c\u4f3c\u662f\u5f88\u5feb\u7684\u5c31\u7761\u719f\u4e86\u3002 \u3000\u3000\u5176\u5b9e\uff0c\u4ed6\u7684\u5fc3\u4e2d\u6b63\u5728\u7422\u78e8\u7740\u660e\u5929\u7684\u4e8b\u60c5\uff0c\u4e0d\u7ba1\u662f\u7528\u836f\u7269\u3001\u8fd8\u662f\u7528\u624b\u6cd5\u89e3\u7a74\uff0c\u9b54\u6559\u4e2d\u4eba\u7edd \u5bf9\u4e0d\u4f1a\u8ba9\u4eba\u77a7\u5230\uff0c\u4e5f\u5c31\u662f\u8bf4\u5fc5\u987b\u8fd8\u8981\u660f\u8ff7\u8fc7\u53bb\u4e00\u6b21\u3002 \u3000\u3000\u738b\u5f64\u62c5\u5fc3\u7684\u662f\u8eab\u4e0a\u4ecd\u5e26\u7740\u4e00\u9053\u5bc6\u65e8\uff0c\u90a3\u662f\u8c03\u52a8\u5404\u5904\u5b98\u5175\u3001\u6355\u5feb\u7684\u5723\u8c15\uff0c\u6655\u8ff7\u4e4b\u540e\uff0c\u5fc5\u906d \u641c\u8eab\uff0c\u4e00\u4f46\u88ab\u641c\u51fa\u8fd9\u9053\u5723\u8c15\uff0c\u4e0d\u4f46\u524d\u529f\u5c3d\u5f03\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u4f1a\u6fc0\u8d77\u9a6c\u6587\u4e2d\u7684\u6740\u673a\u2026\u2026\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u8fd9\u9053\u5723\u8c15\uff0c\u5fc5\u987b\u8981\u85cf\u597d\u3002 \u3000\u3000\u738b\u5f64\u7422\u78e8\u4e86\u4e00\u4e2a\u66f4\u6b21\uff0c\u60f3\u4e0d\u51fa\u4e00\u4e2a\u7edd\u5bf9\u5b89\u5168\u7684\u5730\u65b9\u3002 \u3000\u3000\u60c5\u52bf\u903c\u4eba\uff0c\u738b\u5f64\u53ea\u597d\u8d4c\u4e00\u4e0b\u4e86\uff0c\u628a\u5723\u8c15\u85cf\u5728\u5e8a\u94fa\u4e0b\u9762\u3002 \u3000\u3000\u4ed6\u76f8\u4fe1\u9a6c\u6587\u4e2d\u5f88\u53ef\u80fd\u6d3e\u4eba\u5728\u6697\u4e2d\u76d1\u89c6\uff0c\u5fc5\u987b\u501f\u591c\u8272\u63a9\u62a4\u624d\u80fd\u884c\u52a8\u3002 \u3000\u3000\u738b\u5f64\u8eab\u4e0a\uff0c\u672c\u6765\u6709\u53e6\u4e00\u9053\u5bc6\u65e8\uff0c\u90a3\u662f\u8bf7\u5404\u5927\u95e8\u6d3e\u6d3e\u9ad8\u624b\u52a9\u62f3\u7684\u5723\u8c15\uff0c\u4f46\u5df2\u79d8\u5bc6\u7684\u4ea4\u7ed9\u7f57 \u6052\uff0c\u8981\u4ed6\u8f6c\u4ea4\u6c5f\u5343\u91cc\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\u5f00\u5c01\u5e9c\u7684\u53d8\u5316\uff0c\u4f7f\u738b\u5f64\u5bf9\u64c5\u957f\u6f5c\u8e2a\u9690\u5f62\u7684\u6c5f\u5343\u91cc\uff0c\u7531\u8877\u7684\u751f\u51fa\u656c\u670d\u4e4b\u610f\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\uff0c\u9a6c\u6587\u4e2d\u4e00\u65e9\u5c31\u8d76\u6765\uff0c\u966a\u51e0\u4eba\u5171\u8fdb\u65e9\u9910\uff0c\u7136\u540e\u5c31\u9001\u51e0\u4eba\u56de\u5404\u81ea\u7684\u5367\u623f\uff0c\u7b49\u5019\u5927\u592b \u7597\u6cbb\u4f24\u52bf\u3002 \u3000\u3000\u5404\u56de\u5404\u7684\u5367\u5ba4\uff0c\u4f7f\u5f97\u738b\u5f64\u6697\u6697\u53eb\u82e6\uff0c\u8fd9\u4e00\u62db\u5b89\u6392\u5f97\u5f88\u9ad8\u660e\uff0c\u4f46\u4e8b\u5df2\u81f3\u6b64\uff0c\u4e5f\u53ea\u6709\u59d4\u8bf8\u5929 \u547d\u4e86\u3002 \u3000\u3000\u679c\u7136\uff0c\u738b\u5f64\u7b49\u518d\u559d\u4e86\u4e00\u676f\u8336\u4e4b\u540e\uff0c\u6655\u8ff7\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8fd9\u65b9\u6cd5\u5f88\u81ea\u7136\uff0c\u6ca1\u6709\u4eba\u4f1a\u60f3\u5230\u5728\u8336\u4e2d\u653e\u4e0b\u836f\u7269\uff0c\u4f46\u51e0\u4eba\u5fc3\u4e2d\u4ea6\u65e9\u5df2\u6709\u51c6\u5907\uff0c\u7597\u6cbb\u4f24\u52bf\u65f6 \u53ef\u80fd\u4f1a\u518d\u88ab\u6655\u8ff7\u8fc7\u53bb\u3002 \u3000\u3000\u5f53\u4ed6\u4eec\u9192\u6765\u65f6\uff0c\u5df2\u662f\u665a\u971e\u6ee1\u5929\uff0c\u5c06\u8fd1\u665a\u996d\u7684\u65f6\u523b\u4e86\u3002 \u3000\u3000\u9a6c\u6587\u4e2d\u65e9\u5df2\u5728\u738b\u5f64\u623f\u4e2d\u5750\u5019\uff0c\u738b\u5f64\u4e00\u7741\u5f00\u53cc\u773c\uff0c\u9a6c\u6587\u4e2d\u5df2\u8fde\u8fde\u81f4\u6b49\uff0c\u9053\uff1a\u201c\u5bf9\u4e0d\u4f4f\u4e86\uff0c \u738b\u5144\uff0c\u6765\u81ea\u897f\u57df\u7684\u5927\u592b\u975e\u8981\u628a\u4eba\u8ff7\u8fc7\u53bb\uff0c\u624d\u80af\u4e0b\u624b\u7597\u6cbb\uff0c\u6240\u4ee5\u5144\u5f1f\u53ea\u597d\u9075\u529e\u4e86\u3002\u201d \u3000\u3000\u738b\u5f64\u89c2\u5bdf\u9a6c\u6587\u4e2d\u7684\u8138\u8272\uff0c\u4f3c\u662f\u4e0d\u50cf\u53d1\u89c9\u5723\u8c15\u7684\u6837\u5b50\uff0c\u5fc3\u4e2d\u5b89\u6170\u4e0d\u5c11\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9 \u600e\u4e48\u80fd\u602a\u9a6c\u5144\uff0c\u73b0\u5728\uff0c\u5144\u5f1f\u5750\u606f\u4e00\u4e0b\uff0c\u8bd5\u8bd5\u770b\u6bd2\u6027\u662f\u5426\u5168\u89e3\u4e86\uff1f\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u9053\uff1a\u201c\u53ef\u8981\u5144\u5f1f\u52a9\u4e00\u81c2\u4e4b\u529b\u3002\u201d \u3000\u3000\u201c\u90a3\u5012\u4e0d\u7528\u4e86\uff0c\u5144\u5f1f\u4ee5\u672c\u8eab\u7684\u529f\u529b\u76f8\u8bd5\uff0c\u624d\u80fd\u660e\u786e\u77e5\u6653\u3002\u201d\u738b\u5f64\u62d2\u7edd\u4e86\u9a6c\u6587\u4e2d\u7684\u597d\u610f\u3002 \u3000\u3000\u9a6c\u6587\u4e2d\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u597d\uff01\u90a3\u9a6c\u67d0\u5c31\u544a\u9000\u4e86\uff0c\u4e00\u4e2a\u65f6\u8fb0\u4e4b\u540e\uff0c\u518d\u6765\u8bf7\u738b\u5144\u5171\u8fdb\u665a\u9910\uff0c\u5f85 \u4f1a\u89c1\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u9000\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u738b\u5f64\u7acb\u523b\u76d8\u819d\u6253\u5750\uff0c\u9ed8\u8fd0\u771f\u6c14\uff0c\u5168\u8eab\u6d41\u8f6c\uff0c\u7adf\u662f\u6c14\u8d70\u767e\u9ab8\uff0c\u56de\u805a\u4e39\u7530\uff0c\u679c\u7136\uff0c\u4f24\u52bf\u75ca \u6108\uff0c\u5b8c\u5168\u6062\u590d\u4e86\u3002 \u3000\u3000\u518d\u67e5\u884c\u56ca\u4e2d\u7684\u7cbe\u94a2\u62a4\u8155\uff0c\u4e5f\u662f\u5b8c\u597d\u5982\u521d\uff0c\u8fde\u91cc\u9762\u85cf\u7684\u94a2\u9488\uff0c\u4e5f\u672a\u52a8\u8fc7\u3002 \u3000\u3000\u518d\u4f38\u624b\u5165\u5e8a\u4e0b\u4e00\u6478\uff0c\u5723\u8c15\u4ecd\u5728\uff0c\u7acb\u523b\u85cf\u5165\u6000\u4e2d\uff0c\u6234\u4e0a\u62a4\u8155\uff0c\u91cd\u65b0\u5750\u606f\u8d77\u6765\u3002 \u3000\u3000\u884c\u529f\u4e00\u5468\u5929\uff0c\u7cbe\u795e\u5927\u632f\uff0c\u8fd9\u65f6\uff0c\u85cf\u5728\u5fc3\u4e2d\u6570\u5341\u5929\u7684\u754f\u60e7\u3001\u60ca\u7591\uff0c\u4e00\u626b\u800c\u7a7a\u3002 \u3000\u3000\u9a6c\u6587\u4e2d\u8868\u73b0\u51fa\u4e86\u7edd\u5bf9\u6b49\u759a\u7684\u5fc3\u610f\uff0c\u53c8\u4eb2\u81ea\u8d76\u6765\u8bf7\u738b\u5f64\u5230\u996d\u5385\u7528\u9910\u3002 \u3000\u3000\u7247\u523b\u4e4b\u540e\uff0c\u71d5\u98de\u3001\u738b\u91cd\u5c71\u3001\u8d75\u4fdd\u3001\u9648\u5b8f\u4e5f\u76f8\u7ee7\u5165\u5385\u3002 \u3000\u3000\u4e0d\u5f85\u738b\u5f64\u5f00\u53e3\uff0c\u9a6c\u6587\u4e2d\u5df2\u62a2\u5148\u8bf4\u9053\uff1a\u201c\u56db\u4f4d\u4f24\u52bf\u662f\u5426\u5df2\u5168\u597d\u4e86\uff1f\u201d \u3000\u3000\u56db\u4eba\u9f50\u58f0\u5e94\u9053\uff1a\u201c\u5b8c\u5168\u590d\u5143\uff01\u201d \u3000\u3000\u201c\u597d\uff01\u597d\uff01\u201d\u9a6c\u6587\u4e2d\u56fd\u6ce8\u738b\u5f64\uff0c\u9053\uff1a\u201c\u8fd9\u6837\u5144\u5f1f\u4e5f\u7b97\u5bf9\u738b\u5144\u6709\u4e2a\u4ea4\u4ee3\u4e86\u3002\u201d \u3000\u3000\u76ee\u5149\u4e00\u63a0\u71d5\u98de\uff0c\u9053\uff1a\u201c\u71d5\u603b\u6355\u5934\uff0c\u76ee\u4e0b\u4e00\u5207\u90fd\u96e8\u8fc7\u5929\u6674\uff0c\u53ef\u5426\u5230\u6211\u5de1\u629a\u8859\u95e8\u5c31\u4efb\u603b\u6355\u5934 \u7684\u804c\u4f4d\uff0c\u5f53\u7136\uff0c\u5982\u82e5\u71d5\u603b\u6355\u5934\u5e0c\u671b\u7559\u5728\u5f00\u5c01\u5e9c\uff0c\u6211\u4e5f\u4e0d\u4fbf\u52c9\u5f3a\u4e86\u3002\u201d \u3000\u3000\u71d5\u98de\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u738b\u5f64\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9a6c\u5144\uff0c\u5144\u5f1f\u60f3\u628a\u71d5\u98de\u5e26\u5165\u4eac\u57ce\uff0c\u8865\u4e00\u540d\u5bab\u536b\u7684\u7f3a\uff0c\u4e0d\u77e5\u9a6c\u5144\u53ef\u613f\u653e \u4eba\uff1f\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u738b\u5144\u8981\u4eba\uff0c\u5144\u5f1f\u4e0d\u653e\u6210\u5417\uff1f\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u6c5f\u6e56\u9053\u4e0a\uff0c\u9053\u4e49\u4e3a\u5148\uff0c\u5144\u5f1f\u996d\u540e\uff0c\u60f3\u53bb\u76f8\u56fd\u5bfa\u4e2d\u77a7\u77a7\u5fc3\u5370\u5927\u5e08\uff0c\u4e0d\u77e5\u662f\u5426\u65b9 \u4fbf\uff1f\u201d \u3000\u3000\u201c\u65b9\u4fbf\u5f97\u5f88\u2026\u2026\u201d\u9a6c\u6587\u4e2d\u9053\uff1a\u201c\u5403\u8fc7\u996d\uff0c\u6211\u6d3e\u8f66\u9001\u4f60\u4eec\u8fc7\u53bb\u3002\u201d \u3000\u3000\u201c\u4e0d\u7528\u4e86\uff01\u2026\u2026\u201d\u738b\u5f64\u9053\uff1a\u201c\u6709\u71d5\u98de\u5e26\u8def\uff0c\u6211\u4eec\u6b65\u884c\u901b\u53bb\uff0c\u4e5f\u501f\u673a\u6d3b\u52a8\u4e00\u4e0b\u7b4b\u9aa8\u3002\u201d \u3000\u3000\u76f8\u56fd\u5bfa\u4e2d\uff0c\u4e00\u5207\u5982\u65e7\uff0c\u5fc3\u5370\u5927\u5e08\u548c\u4e03\u5de7\u50e7\u65e9\u5df2\u5728\u6392\u623f\u7b49\u5019\u3002 \u3000\u3000\u738b\u91cd\u5c71\u4e00\u89c1\u4e03\u5de7\u50e7\uff0c\u5fc3\u5934\u706b\u8d77\uff0c\u5fcd\u4e0d\u4f4f\u543c\u9053\uff1a\u201c\u5c0f\u548c\u5c1a\uff0c\u4f60\u2026\u2026\u201d \u3000\u3000\u201c\u522b\u53eb\uff0c\u5c0f\u548c\u5c1a\u53d7\u7684\u7f6a\uff0c\u8d85\u8fc7\u4f60\u4eec\u5341\u500d\u2026\u2026\u201d \u3000\u3000\u201c\u600e\u4e48\u8bf4\u5462\uff1f\u201d\u738b\u91cd\u5c71\u9053\uff1a\u201c\u9c7c\u5316\u9f99\u653e\u4e86\u4f60\uff0c\u96be\u9053\u4f1a\u51fa\u5c14\u53cd\u5c14\uff1f\u201d \u3000\u3000\u4e03\u5de7\u50e7\u9053\uff1a\u201c\u9c7c\u5316\u9f99\u6ca1\u6709\u6d3e\u4eba\u8ffd\u6211\uff0c\u4f46\u8d75\u4e8c\u5824\u4e0d\u4e70\u8fd9\u4e2a\u5e10\uff0c\u5c0f\u548c\u5c1a\u88ab\u4ed6\u4eec\u56e2\u56e2\u56f4\u4f4f\uff0c\u82e6 \u6218\u4e86\u5927\u534a\u591c\uff0c\u6700\u540e\u8fd8\u662f\u88ab\u64d2\u3002\u201d \u3000\u3000\u201c\u597d\u554a\uff01\u8fd9\u5c31\u53eb\u4eba\u7b97\u4e0d\u5982\u5929\u7b97\uff0c\u54c8\u54c8\u54c8\u2026\u2026\u8fd9\u4e5f\u662f\u4f60\u629b\u5f03\u670b\u53cb\u7684\u62a5\u5e94\uff01\u201d \u3000\u3000\u201c\u60e8\u60e8\u60e8\uff01\u2026\u2026\u8fd9\u4e00\u4e0b\uff0c\u5c0f\u548c\u5c1a\u8df3\u5230\u9ec4\u6cb3\u4e5f\u6d17\u4e0d\u6e05\u4e86\u3002\u201d \u3000\u3000\u4e03\u5de7\u589e\u5927\u6982\u662f\u771f\u6015\u5de5\u91cd\u5c71\u8bef\u4f1a\u96be\u91ca\uff0c\u76ee\u6ce8\u5fc3\u5370\u6c42\u6551\u3002 \u3000\u3000\u5fc3\u5370\u5927\u5e08\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u738b\u65bd\u4e3b\u771f\u7684\u8bef\u4f1a\u5c0f\u548c\u5c1a\u4e86\uff0c\u4ed6\u77a7\u51fa\u60c5\u52bf\u4e0d\u5bf9\uff0c\u51c6\u5907\u53bb\u5c11\u6797\u642c \u6551\u5175\uff0c\u5c31\u7b97\u8d76\u4e0d\u4e0a\u89e3\u6551\u90a3\u591c\u4e4b\u5371\uff0c\u4f46\u5c11\u6797\u7fa4\u50e7\u8d76\u6765\uff0c\u81f3\u5c11\u53ef\u9547\u4f4f\u9a6c\u6587\u4e2d\u4e0d\u6562\u4e0b\u6bd2\u624b\u53d6\u6211\u4eec\u7684 \u6027\u547d\uff01\u201d \u3000\u3000\u201c\u539f\u6765\u5982\u6b64\uff01\u5c0f\u548c\u5c1a\uff0c\u5bf9\u4e0d\u4f4f\u4e86\uff01\u201d\u738b\u91cd\u5c71\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u4f60\u628a\u5200\u516c\u5b50\u859b\u767e\u80dc\u62d6\u5165\u6f29\u6da1\uff0c \u4e00\u58f0\u4e0d\u8bb2\u5c31\u8dd1\u4e86\uff0c\u672a\u514d\u4e0d\u591f\u610f\u601d\u5427\uff01\u201d \u3000\u3000\u201c\u62d6\u5200\u516c\u5b50\u859b\u767e\u80dc\u7684\u662f\u4f60\u738b\u91cd\u5c71\uff0c\u5c0f\u548c\u5c1a\u53ea\u4e0d\u8fc7\u544a\u8bc9\u4f60\u4e00\u6bb5\u6c5f\u6e56\u79d8\u8f9b\u7f62\u4e86\uff01\u201d\u4e03\u5de7\u50e7\u5fae \u7b11\u9053\uff1a\u201c\u518d\u8bf4\uff0c\u98ce\u96f7\u5200\u859b\u8001\u4eba\u53ef\u662f\u6709\u540d\u7684\u5927\u9a6c\u8702\u7a9d\uff0c\u9a6c\u6587\u4e2d\u9664\u975e\u51c6\u5907\u653e\u624b\u5927\u5e72\uff0c\u4e0d\u4f1a\u968f\u4fbf\u53bb \u62db\u60f9\u859b\u8001\u4eba\u7684\uff0c\u6240\u4ee5\uff0c\u4ed6\u4eec\u5148\u653e\u8d70\u4e86\u5200\u516c\u5b50\u3002\u201d \u3000\u3000\u738b\u91cd\u5c71\u9053\uff1a\u201c\u5c0f\u548c\u5c1a\uff0c\u4f60\u7b54\u5e94\u5e26\u6211\u9053\u6e38\u56db\u6d77\u7684\u8bfa\u8a00\uff0c\u8fd8\u7b97\u4e0d\u7b97\u6570\uff1f\u201d \u3000\u3000\u201c\u7b97\uff01\u95ee\u9898\u662f\uff0c\u4f60\u80fd\u79bb\u5f00\u5185\u5bab\u4f8d\u536b\u4e48\uff1f\u201d \u3000\u3000\u738b\u91cd\u5c71\u53e3\u6ce8\u738b\u5f64\uff0c\u738b\u5f64\u5fae\u5fae\u9894\u9996\u9053\uff1a\u201c\u4f60\u4eec\u5927\u3001\u5c0f\u4e24\u4e2a\u548c\u5c1a\uff0c\u7981\u5236\u662f\u5426\u5df2\u5b8c\u5168\u89e3\u9664\uff1f\u201d \u3000\u3000\u5fc3\u5370\u5927\u5e08\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u89e3\u9664\u4e86\uff0c\u4e0d\u8fc7\uff0c\u53c8\u88ab\u4ed6\u4eec\u7528\u836f\u8ff7\u6655\u4e86\u4e00\u6b21\u3002\u53d7\u4ed6\u4eec\u6446\u5e03\u4e86\u51e0\u4e2a \u65f6\u8fb0\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u51dd\u6ce8\u738b\u5f64\uff0c\u53c8\u9053\uff1a\u201c\u9a6c\u6587\u4e2d\u5fc3\u610f\u6539\u53d8\uff0c\u4e5f\u662f\u548c\u4f60\u6709\u5173\u4e86\uff1f\u201d \u3000\u3000\u201c\u662f\u554a\uff01\u5723\u4e0a\u7b54\u5e94\u4e86\u4ed6\u4eec\u5168\u90e8\u7684\u8bf7\u6c42\uff0c\u4ed6\u8fd8\u6709\u4ec0\u4e48\u4e0d\u6ee1\u610f\u7684\uff1f\u201d \u3000\u3000\u201c\u2018\u963f\u5f25\u9640\u4f5b\uff01\u201d \u3000\u3000\u4f46\u95fb\u4e00\u58f0\u4f5b\u53f7\uff0c\u4e00\u4e2a\u7a7f\u7740\u8888\u88df\u7684\u548c\u5c1a\uff0c\u76f4\u5411\u7985\u623f\u4e2d\u884c\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u6240\u6709\u4eba\u7684\u76ee\u5149\u90fd\u6295\u6ce8\u5728\u90a3\u548c\u5c1a\u7684\u8eab\u4e0a\uff0c\u4f46\u56e0\u4e3b\u6301\u5fc3\u5370\u5927\u5e08\u5728\u573a\uff0c\u5927\u5bb6\u90fd\u4e0d\u4fbf\u5f00\u53e3\uff0c\u5fc3\u4e2d \u5374\u611f\u89c9\u5230\u8fd9\u4e2a\u548c\u5c1a\u6a2a\u51b2\u76f4\u95ef\u8fdb\u6765\uff0c\u6709\u70b9\u5927\u5931\u793c\u6570\u3002 \u3000\u3000\u5fc3\u5370\u5927\u5e08\u76b1\u76b1\u7709\uff0c\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u559d\uff01\u5fc3\u5370\u5927\u5e08\u4e5f\u4e0d\u8ba4\u8bc6\uff0c\u8fd9\u548c\u5c1a\u5206\u660e\u662f\u5916\u6765\u7684\u4e86\u3002 \u3000\u3000\u4e03\u5de7\u50e7\u4f4e\u58f0\u559d\u9053\uff1a\u201c\u56f4\u4e0a\u4ed6\uff01\u201d \u3000\u3000\u571f\u91cd\u5c71\u3001\u8d75\u4fdd\u3001\u9648\u5b8f\u3001\u71d5\u98de\u540c\u65f6\u884c\u52a8\uff0c\u628a\u548c\u5c1a\u56f4\u4e86\u8d77\u6765\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u50e7\u4eba\u5408\u638c\u62dc\u9053\uff1a\u201c\u5c0f\u50e7\u6c5f\u5343\u91cc\u89c1\u8fc7\u5927\u5e08\u7236\u3001\u5c0f\u5e08\u7236\u548c\u8bf8\u4f4d\u65bd\u4e3b\u3001\u201d \u3000\u3000\u4e3e\u624b\u5728\u8138\u4e0a\u4e00\u6309\uff0c\u5411\u540e\u4e00\u63a8\uff0c\u548c\u5c1a\u7684\u5934\u4e0d\u89c1\u4e86\uff0c\u9732\u51fa\u4e86\u672c\u6765\u7684\u9762\u76ee\uff0c\u6b63\u662f\u6c5f\u5343\u91cc\u3002\u201c \u3000\u3000\u201c\u6c5f\u5144\uff01\u2026\u2026\u201d\u738b\u5f64\u5927\u4e3a\u632f\u594b\uff0c\u9053\uff1a\u201c\u9ad8\u660e\u554a\uff01\u9ad8\u660e\uff01\u8fd9\u4efd\u6613\u5bb9\u3001\u9690\u5f62\u4e4b\u672f\uff0c\u5927\u6982\u5929\u4e0b \u65e0\u4eba\u80fd\u51fa\u5176\u53f3\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u8131\u4e0b\u8888\u88df\uff0c\u7b11\u9053\uff1a\u201c\u4eca\u65e5\u5348\u540e\uff0c\u76f8\u56fd\u5bfa\u5404\u5904\u76d1\u89c6\u7684\u4eba\u624b\uff0c\u5ffd\u7136\u64a4\u8d70\uff0c\u521a\u624d\uff0c\u53c8\u89c1 \u4f60\u4eec\u51e0\u4e2a\u4eba\u8fdb\u5165\u4e86\u5bfa\u4e2d\uff0c\u6c5f\u67d0\u4eba\u89c9\u5f97\u5947\u602a\uff0c\u53ea\u597d\u8d76\u7d27\u8fc7\u6765\u77a7\u77a7\uff01\u201d \u3000\u3000\u4e03\u5de7\u50e7\u70b9\u8d77\u7985\u623f\u4e2d\u7684\u4e00\u76cf\u6cb9\u706f\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u5230\u5f00\u5c01\u591a\u4e45\u4e86\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u4e00\u4e2a\u6708\u4e86\u5427\uff01\u201d \u3000\u3000\u201c\u4ec0\u4e48\uff1f\u2026\u2026\u201d\u4e03\u5de7\u501f\u9053\uff1a\u201c\u5f00\u5c01\u6212\u5907\u68ee\u4e25\uff0c\u81f3\u5c11\u6709\u4e09\u56db\u767e\u4eba\u5728\u67e5\u627e\u5978\u7ec6\uff0c\u5176\u4e2d\u4e0d\u4e4f\u6b66 \u6797\u9ad8\u624b\uff0c\u4f60\u662f\u600e\u4e48\u8eb2\u8fc7\u7684\u3002\u201d \u3000\u3000\u738b\u5f64\u7b11\u9053\uff1a\u201c\u5c0f\u548c\u5c1a\uff0c\u4ed6\u521a\u624d\u8fde\u6211\u548c\u5fc3\u5370\u5927\u5e08\u90fd\u7792\u4f4f\u4e86\uff0c\u4f55\u51b5\u662f\u654c\u4eba\uff1f\u201d \u3000\u3000\u201c\u4f69\u670d\uff01\u4f69\u670d\uff01\u201d\u4e03\u5de7\u50e7\u9053\uff1a\u201c\u6c5f\u524d\u8f88\uff0c\u5c0f\u548c\u5c1a\u60f3\u8ddf\u4f60\u5b66\u5b66\u8fd9\u79cd\u6f5c\u9690\u8e2a\u8ff9\u4e4b\u672f\uff0c\u4e0d\u77e5\u662f \u5426\u53ef\u4ee5\uff1f\u201d \u3000\u3000\u201c\u4f60\u4eca\u5e74\u51e0\u5c81\u4e86\uff1f\u201d \u3000\u3000\u201c\u5c0f\u548c\u5c1a\u4e8c\u5341\u6709\u56db\u3002\u201d \u3000\u3000\u201c\u884c\uff01\u4e0d\u8fc7\u2026\u2026\u201d\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u6709\u6761\u4ef6\u3002\u201d \u3000\u3000\u201c\u8bf4\u8bf4\u770b\u5427\uff01\u53ea\u8981\u4e0d\u662f\u8981\u5c0f\u548c\u5c1a\u7684\u547d\uff0c\u6211\u90fd\u7b54\u5e94\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c31\u884c\u4e86\uff0c\u5c06\u6765\uff0c\u4f60\u5e2e\u6211\u529e\u4ef6\u4e8b\u60c5\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\u554a\uff01\u4e00\u8a00\u4e3a\u5b9a\uff0c\u6c5f\u524d\u8f88\u53ef\u4e0d\u80fd\u9a97\u6211\u8fd9\u8df3\u51fa\u4e09\u754c\u5916\u3001\u4e0d\u5728\u4e94\u884c\u4e2d\u7684\u51fa\u5bb6\u4eba\uff1f\u201d \u3000\u3000\u201c\u5c11\u6765\u8fd9\u4e00\u5957\u4e86\uff0c\u5c0f\u548c\u5c1a\uff0c\u6211\u6c5f\u67d0\u4eba\u8fd9\u70b9\u672c\u9886\uff0c\u653e\u773c\u5929\u4e0b\uff0c\u4f60\u7b97\u662f\u6700\u5408\u9002\u7684\u8863\u94b5\u4f20\u4eba\uff0c \u53ef\u60dc\u4f60\u51fa\u4e86\u5bb6\uff0c\u5426\u5219\u6211\u4e00\u5b9a\u8981\u628a\u4f60\u6536\u5165\u95e8\u4e0b\u3002\u201d \u3000\u3000\u5fc3\u5370\u5927\u5e08\u9053\uff1a\u201c\u5c0f\u548c\u5c1a\u5df2\u7ecf\u88ab\u5c11\u6797\u5bfa\u64b5\u51fa\u6765\u4e86\uff0c\u9999\u706b\u7f18\u4efd\u867d\u7136\u672a\u65ad\uff0c\u4f46\u5df2\u4e0d\u7b97\u5c11\u6797\u5f1f \u5b50\uff0c\u4f60\u5e26\u7740\u4ed6\u884c\u8d70\u6c5f\u6e56\uff0c\u5e76\u65e0\u4e0d\u53ef\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u70b9\u70b9\u5934\uff0c\u76ee\u5149\u8f6c\u5230\u738b\u5f64\u8eab\u4e0a\uff0c\u9053\uff1a\u201c\u4f60\u7684\u4e8b\uff0c\u662f\u4e0d\u662f\u8981\u548c\u6211\u5355\u72ec\u8c08\u8c08\uff1f\u201d \u3000\u3000\u201c\u6211\u770b\uff0c\u7528\u4e0d\u7740\u4e86\u3002\u201d\u738b\u5f64\u9053\uff1a\u201c\u5728\u573a\u7684\u4eba\u90fd\u662f\u77e5\u5df1\uff0c\u53ef\u4ee5\u660e\u8bf4\u5427\uff01\u201d \u3000\u3000\u201c\u884c\uff01\u2026\u2026\u201d\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u7f57\u6052\u7684\u4e1c\u897f\uff0c\u6211\u6536\u5230\u4e86\uff0c\u4f8d\u3001\u5382\u4e24\u536b\uff0c\u5df2\u7ecf\u6709\u56db\u5341\u516b\u4e2a\u4eba\u5230 \u4e86\u5f00\u5c01\uff0c\u6211\u8981\u7f57\u6052\u628a\u4ed6\u4eec\u5b89\u6392\u5728\u5341\u91cc\u5916\u7684\u519c\u820d\u4e2d\uff0c\u4f60\u73b0\u5728\u51c6\u5907\u4f5c\u4f55\u6253\u7b97\u5462\uff1f\u201d \u3000\u3000\u201c\u56db\u5341\u516b\u4e2a\u5382\u536b\u5b9e\u529b\u592a\u5f31\u4e86\u2026\u2026\u201d\u738b\u5f64\u9053\uff1a\u201c\u9a6c\u6587\u4e2d\u624b\u4e0b\u81f3\u5c11\u6709\u4e00\u5343\u4e2a\u9ad8\u660e\u7684\u5b50\u5f1f\u5175\uff0c \u4eba\u4eba\u90fd\u4f1a\u51e0\u624b\uff0c\u800c\u4e14\uff0c\u608d\u4e0d\u754f\u6b7b\u2026\u2026\u201d \u3000\u3000\u4e03\u5de7\u50e7\u9053\uff1a\u201c\u9664\u4e86\u5c11\u6797\u5bfa\u7684\u7f57\u6c49\u9635\uff0c\u6b66\u5f53\u6d3e\u7684\u4e94\u884c\u5251\u9635\u4e4b\u5916\uff0c\u5927\u6982\u5929\u4e0b\u5f88\u96be\u6709\u963b\u6321\u4ed6\u4eec \u90a3\u79cd\u751f\u6b7b\u4e0d\u987e\u3001\u5168\u529b\u4e00\u51fb\u7684\u8054\u653b\u9635\u52bf\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u6211\u6c5f\u67d0\u4eba\u8fd9\u4e2a\u8eab\u4efd\u662f\u5426\u9002\u5408\u4f20\u51fa\u8fd9\u9053\u79d8\u8c15\uff0c\u6240\u4ee5\u2026\u2026\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u73b0\u5728\u662f\u4e00\u52a8\u4e0d\u5982\u4e00\u9759\uff0c\u4e09\u516c\u4e3b\u5df2\u51b3\u5b9a\u4eb2\u8d74\u9b54\u5bab\u4e00\u63a2\uff0c\u54b1\u4eec\u4e0d\u4fbf\u963b\u62e6\uff0c\u90a3\u5c31\u4e0d \u7528\u6025\u5728\u4e00\u65f6\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u9a6c\u6587\u4e2d\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd8\u662f\u5e72\u4ed6\u7684\u6cb3\u5357\u5de1\u629a\u5427\uff01\u2026\u2026\u201d\u738b\u5f64\u9053\uff1a\u201c\u4ed6\u7684\u5b9e\u529b\u592a\u5f3a\u5927\u4e86\uff0c\u8981\u805a\u96c6\u4e00\u80a1\u53ef\u4ee5\u548c\u4ed6 \u6297\u8861\u7684\u5b9e\u529b\uff0c\u5927\u6982\u4e0d\u662f\u4e09\u4e24\u4e2a\u6708\u53ef\u4ee5\u5b8c\u6210\u7684\u4e8b\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u8fd9\u6837\u4e5f\u597d\uff0c\u5c0f\u71d5\u5b50\u4e5f\u53ef\u4ee5\u5b89\u5fc3\u7684\u7ec3\u6b66\u529f\u4e86\u3002\u201d \u3000\u3000\u201c\u6c5f\u5144\uff0c\u4ed6\u5728\u90a3\u91cc\u2026\u2026\u201d\u63d0\u8d77\u513f\u5b50\uff0c\u71d5\u98de\u5fcd\u4e0d\u4f4f\u8ffd\u95ee\u4e86\u4e00\u53e5\u3002 \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u6211\u65e0\u6cd5\u56de\u7b54\uff0c\u90a3\u5730\u65b9\u4e5f\u6ca1\u6709\u540d\u5b57\uff0c\u4f55\u51b5\uff0c\u6211\u7b54\u5e94\u8fc7\u4ed6\uff0c\u7edd\u4e0d\u8bf4\u51fa\u4ed6\u9690\u5c45\u7684\u5730 \u65b9\u3002\u201d\u6c5f\u5343\u91cc\u7b11\u7b11\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u4e09\u4e94\u5e74\u540e\uff0c\u4f60\u4f1a\u770b\u5230\u4e00\u53ea\u5f3a\u58ee\u7684\u5c0f\u71d5\u5b50\uff0c\u98de\u7fd4\u4e8e\u4e07\u91cc\u78a7\u7a7a\u4e4b \u4e2d\uff0c\u6240\u5230\u4e4b\u5904\uff0c\u6625\u98ce\u548c\u7166\uff0c\u6768\u67f3\u98d8\u98de\uff0c\u4ed6\u5c06\u5e26\u7ed9\u6b66\u6797\u4e2d\u4e00\u7247\u6625\u82b1\u76db\u5f00\u4e4b\u666f\u3002\u201d \u3000\u3000\u201c\u662f\u662f\u662f\u2026\u2026\u201d\u71d5\u98de\u9053\uff1a\u201c\u6c5f\u5144\uff0c\u4f60\u5c31\u5f53\u6211\u5728\u8bf4\u68a6\u8bdd\uff0c\u6211\u4ec0\u4e48\u90fd\u6ca1\u95ee\u8fc7\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u53e3\u6c14\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u738b\u5f64\uff0c\u73b0\u5728\u6211\u628a\u4e1c\u897f\u8fd8\u7ed9\u4f60\uff0c\u65e2\u7136\uff0c\u6b64\u95f4\u4e8b\u5df2\u4e86\uff0c\u6211\u4e5f\u8981\u79bb\u5f00 \u4e86\u3002\u201d \u3000\u3000\u4e03\u5de7\u50e7\u9053\uff1a\u201c\u6211\u4e5f\u8be5\u8ddf\u7740\u524d\u8f88\u8d70\u5427\uff01\u201d \u3000\u3000\u6c5f\u5343\u91cc\u70b9\u70b9\u5934\u3002 \u3000\u3000\u738b\u91cd\u5c71\u7a81\u7136\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5c0f\u548c\u5c1a\uff0c\u6211\u597d\u60f3\u8ddf\u4f60\u4eec\u4e00\u5757\u513f\u53bb\u554a\uff01\u201d \u3000\u3000\u738b\u5f64\u7b11\u9053\uff1a\u201c\u90a3\u5c31\u53bb\u5427\uff0c\u71d5\u98de\u521a\u597d\u63a5\u4f60\u7559\u4e0b\u7684\u4f4d\u7f6e\u3002\u201d \u3000\u3000\u738b\u91cd\u5c71\u7a81\u7136\u4e00\u6b65\u4e00\u6b65\uff0c\u8dea\u5728\u6c5f\u5e72\u91cc\u7684\u9762\u524d\uff0c\u9053\uff1a\u201c\u6c5f\u524d\u8f88\uff0c\u6211\u77e5\u9053\u6211\u505a\u4e86\u5f88\u591a\u9519\u4e8b\uff0c\u4e0d \u8fc7\uff0c\u6211\u73b0\u5728\u5df2\u7ecf\u5168\u77e5\u9053\u81ea\u5df1\u7684\u9519\u8bef\u4e86\uff0c\u6211\u5c11\u4e0d\u66f4\u4e8b\uff0c\u5e0c\u671b\u4f60\u770b\u5728\u4e8c\u53d4\u7684\u4efd\u4e0a\uff0c\u5e26\u7740\u6211\u4e00\u8d77 \u8d70\u2026\u2026\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u8d77\u6765\uff01\u8d77\u6765\uff01\u4f60\u662f\u6b66\u5f53\u95e8\u4e0b\uff0c\u8001\u9053\u58eb\u8981\u662f\u4e0d\u7b54\u5e94\u2026\u2026\u201d \u3000\u3000\u201c\u5305\u5728\u6211\u8eab\u4e0a\uff01\u201d\u738b\u5f64\u9053\uff1a\u201c\u6211\u4f1a\u53bb\u89c1\u6b66\u5f53\u638c\u95e8\u7684\uff0c\u7ed9\u4ed6\u4eec\u4e00\u4e2a\u4ea4\u4ee3\u3002\u201d \u3000\u3000\u201c\u884c\uff01\u6709\u4f60\u51fa\u5934\uff0c\u5927\u6982\u53ef\u4ee5\u6446\u5f97\u5e73\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u7531\u6000\u4e2d\u53d6\u51fa\u4e00\u7269\uff0c\u606d\u606d\u656c\u656c\u7684\u4ea4\u7ed9\u4e86\u738b\u5f64\u3002 \u3000\u3000\u738b\u5f64\u4e5f\u606d\u606d\u656c\u656c\u7684\u63a5\u8fc7\uff0c\u85cf\u5165\u6000\u4e2d\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u6211\u8d70\u4e86\uff0c\u9a6c\u6587\u4e2d\u53ef\u4ee5\u653e\u8fc7\u4f60\u4eec\uff0c\u4f46\u7edd\u4e0d\u4f1a\u653e\u8fc7\u6211\u3002\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u6c5f\u5144\uff0c\u4e00\u5e74\u540e\uff0c\u8bf7\u5230\u4eac\u57ce\u53bb\u4e00\u8d9f\uff0c\u6211\u5728\u90a3\u91cc\u7b49\u4f60\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u70b9\u70b9\u5934\uff0c\u5e26\u7740\u4e03\u5de7\u50e7\u548c\u738b\u91cd\u5c71\uff0c\u79bb\u5f00\u4e86\u76f8\u56fd\u5bfa\u3002 \u3000\u3000\u5fc3\u5370\u5927\u5e08\u76ee\u7779\u4e09\u4eba\u8fdc\u53bb\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u97e9\u6d9b\u662f\u4e00\u628a\u597d\u624b\uff0c\u9a6c\u6587\u4e2d\u4e5f\u89e3\u53bb\u4e86\u4ed6\u7684\u7981\u5236\uff0c\u770b\u8d77 \u6765\uff0c\u4ed6\u4eec\u771f\u7684\u53ea\u6709\u4e00\u4e2a\u76ee\u7684\u800c\u65e0\u4f24\u4eba\u4e4b\u5fc3\uff0c\u97e9\u6d9b\u5df2\u8f9e\u53bb\u4e86\u603b\u6355\u5934\u7684\u804c\u4f4d\uff0c\u6211\u60f3\uff0c\u4f60\u53ef\u4ee5\u5e26\u4ed6 \u8d70\u3002\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u53ef\u4ee5\uff0c\u4f46\u662f\u4f60\u5462\uff1f\u628a\u76f8\u56fd\u5bfa\u65b9\u4e08\u4ea4\u4e86\u5427\uff0c\u9a6c\u6587\u4e2d\u7684\u529b\u91cf\u4f1a\u8d8a\u6765\u8d8a\u5927\uff0c\u6211\u770b\uff0c \u8fd9\u5f00\u5c01\u5e9c\u4e5f\u4e0d\u662f\u4f60\u4e45\u5c45\u4e4b\u5730\u4e86\uff0c\u5230\u4eac\u91cc\u53bb\uff0c\u4e0d\u60f3\u5403\u516c\u4e8b\u996d\uff0c\u6211\u66ff\u4f60\u627e\u4e2a\u5bfa\u9662\uff0c\u9759\u5fc3\u4fee\u4f60\u7684\u4f5b \u95e8\u5927\u9053\u3002\u201d \u3000\u3000\u201c\u5509\uff01\u8001\u548c\u5c1a\u51e0\u5341\u5e74\u7684\u7985\u95e8\u9759\u4fee\uff0c\u5168\u88ab\u4f60\u6405\u4e71\u4e86\uff0c\u5230\u4eac\u57ce\uff0c\u6211\u4e5f\u65e0\u6cd5\u505a\u4e2a\u4f5b\u95e8\u5f1f\u5b50\u4e86\uff0c \u76f8\u56fd\u5bfa\u65b9\u4e08\u6211\u65e9\u5df2\u5b89\u6392\u597d\u4e86\u4eba\u9009\uff0c\u6211\u662f\u8981\u8d70\uff0c\u4f46\u4e0d\u4f1a\u8ddf\u4f60\u53bb\u7684\uff0c\u6211\u8981\u4e91\u6e38\u5929\u4e0b\uff0c\u4fee\u4fee\u82e6\u884c \u6212\u3002\u201d \u3000\u3000\u201c\u8001\u548c\u5c1a\uff0c\u8fd9\u662f\u66b4\u98ce\u96e8\u524d\u4e00\u573a\u5e73\u9759\uff0c\u80fd\u5e73\u9759\u591a\u4e45\u6211\u4e5f\u4e0d\u77e5\u9053\uff0c\u4e5f\u8bb8\u4e09\u4e24\u5e74\uff0c\u4e5f\u8bb8\u4e09\u4e94\u4e2a \u6708\uff0c\u5c31\u4f1a\u95f9\u51fa\u6765\u4e86\uff0c\u4f60\u4e00\u4e2a\u4eba\u2026\u2026\u201d\u738b\u5f64\u65e0\u9650\u62b1\u759a\u5730\u9053\uff1a\u201c\u6211\u5e0c\u671b\u4f60\u53bb\u4eac\u57ce\uff0c\u5927\u5bb6\u4e5f\u6709\u4e2a\u7167 \u5e94\u3002\u201d \u3000\u3000\u5fc3\u5370\u5927\u5e08\u611f\u6168\u5730\u9053\uff1a\u201c\u6211\u548c\u5c1a\u5927\u6982\u8fd8\u53ef\u4ee5\u81ea\u4fdd\uff0c\u518d\u8bf4\uff0c\u6211\u60f9\u4e0d\u8d77\u9a6c\u6587\u4e2d\u548c\u9b54\u6559\uff0c\u8eb2\u603b\u53ef \u4ee5\u5427\uff01\u4e0d\u8981\u52c9\u5f3a\u6211\uff0c\u4f60\u4eec\u8d70\u5427\uff01\u4e09\u5929\u540e\uff0c\u8001\u548c\u5c1a\u5c31\u79bb\u5f00\u8fd9\u91cc\u3002\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u597d\u5427\uff01\u8981\u6211\u4f5c\u4ec0\u4e48\uff1f\u901a\u77e5\u4e00\u58f0\u5c31\u662f\u3002\u201d \u3000\u3000\u8bdd\u843d\u3002\u5e26\u7740\u71d5\u98de\u3001\u8d75\u4fdd\u3001\u9648\u5b8f\u3002\u79bb\u5f00\u4e86\u76f8\u56fd\u5bfa\u3002 \u3000\u3000\u4e09\u5929\u540e\u3002 \u3000\u3000\u738b\u5f64\u5e26\u7740\u97e9\u6d9b\u3001\u71d5\u98de\u548c\u56db\u5341\u516b\u540d\u5bab\u3001\u5382\u4f8d\u536b\uff0c\u79bb\u5f00\u4e86\u5f00\u5c01\u2026\u2026\u3002 \u3000\u3000\u5982\u4f55\u6551\u56de\u4e09\u516c\u4e3b\uff1f\u548c\u9a6c\u6587\u4e2d\u7b49\u518d\u5ea6\u4ea4\u950b\uff0c\u4e2d\u539f\u6b66\u6797\u548c\u5b98\u65b9\u7684\u4f8d\u536b\u529b\u91cf\u7ed3\u5408\uff0c\u5171\u540c\u5bf9\u4ed8\u9b54 \u6559\u3002 \u5367\u9f99\u751f\u300a\u71d5\u5b50\u4f20\u5947\u300b \u7b2c \u5341 \u56de\u3000\u4e1c\u5c71\u518d\u8d77 \u3000\u3000\u8fd9\u662f\u4e2a\u975e\u5e38\u5947\u602a\u7684\u5bb4\u4f1a\uff0c\u88ab\u8bf7\u7684\u5ba2\u4eba\uff0c\u4e0d\u77e5\u9053\u4e3b\u4eba\u662f\u8c01\uff1f \u3000\u3000\u800c\u4e14\u5ba2\u4eba\u4e5f\u6545\u4f5c\u795e\u79d8\uff0c\u6bcf\u4e2a\u4eba\u90fd\u7a7f\u7740\u5bbd\u5927\u9ed1\u888d\uff0c\u8138\u4e0a\u4e5f\u8499\u4e86\u4e00\u5757\u9ed1\u8272\u7684\u9762\u7eb1\u3002 \u3000\u3000\u4f46\u5bb4\u5ba2\u7684\u5730\u70b9\uff0c\u5374\u662f\u5145\u6ee1\u8bd7\u60c5\u753b\u610f\u7684\u6d1e\u5ead\u80aa\u3002 \u3000\u3000\u6d1e\u5ead\u80aa\u662f\u4e00\u5904\u6709\u540d\u7684\u9152\u80aa\uff0c\u4f9b\u5e94\u65f6\u9c9c\u3001\u6c34\u4ea7\uff0c\u9152\u80aa\u4e0a\u7684\u5927\u5e08\u7236\uff0c\u624b\u827a\u7cbe\u81f4\uff0c\u8fdc\u8fd1\u9a70\u540d\u3002 \u3000\u3000\u88ab\u8bf7\u7684\u5ba2\u4eba\u53ea\u77e5\u9053\u81ea\u5df1\u7684\u53f7\u7801\u548c\u7ea6\u5b9a\u7684\u65f6\u95f4\u53ca\u5730\u70b9\u3002 \u3000\u3000\u795e\u79d8\u7684\u662f\u4e3b\u4eba\u8bf7\u7684\u662f\u5403\u5bb5\u591c\u3002 \u3000\u3000\u5ba2\u4eba\u4e0d\u591a\uff0c\u53ea\u6709\u4e94\u4e2a\u3002 \u3000\u3000\u4e94\u4e2a\u4eba\u90fd\u4f9d\u7167\u7740\u7ea6\u5b9a\u7684\u65f6\u95f4\uff0c\u8d76\u5230\u4e86\u4e0d\u540c\u7684\u5730\u70b9\u3002 \u3000\u3000\u90a3\u662f\u6d1e\u5ead\u6e56\u7554\uff0c\u4e00\u4e2a\u50fb\u9759\u7684\u5730\u65b9\uff0c\u4e0d\u8fdc\u7684\u6c34\u57df\u4e2d\u505c\u7740\u4e00\u8258\u5c0f\u821f\u3002 \u3000\u3000\u516b\u6708\u521d\u4e00\u7684\u591c\u8272\uff0c\u4e00\u7247\u9edd\u6697\uff0c\u4e8c\u66f4\u65f6\u5206\uff0c\u5df2\u662f\u96be\u89c1\u5230\u4e00\u4e08\u5916\u7684\u666f\u7269\u3002 \u3000\u3000\u5bbd\u5927\u7684\u9ed1\u888d\u3002\u7f69\u5934\u7684\u9ed1\u7eb1\uff0c\u6df1\u6df1\u7684\u591c\u8272\uff0c\u4f7f\u8fd9\u6b21\u5bb4\u5ba2\u7684\u7ea6\u4f1a\u66f4\u663e\u5f97\u795e\u79d8\u3001\u8be1\u5f02\uff0c\u800c\u53c8\u5e26 \u70b9\u9634\u68ee\u7684\u5473\u9053\u3002 \u3000\u3000\u5c0f\u821f\u672a\u70b9\u706f\u706b\uff0c\u4f46\u5374\u4f20\u8fc7\u6765\u4e00\u4e2a\u6e05\u51b7\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u8d35\u5ba2\u662f\u2026\u2026\uff1f\u201d \u3000\u3000\u201c\u4e00\u53f7\u3002\u201d\u8d76\u5230\u5cb8\u8fb9\u7684\u4e00\u4e2a\u9ed1\u888d\u4eba\uff0c\u538b\u4f4e\u7684\u56de\u7b54\u3002 \u3000\u3000\u5c0f\u821f\u8fc5\u5feb\u7684\u6447\u9a70\u8fc7\u6765\uff0c\u8239\u4e0a\u53c8\u54cd\u8d77\u90a3\u6e05\u51b7\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u8bf7\u4e0a\u8239\u3002\u201d \u3000\u3000\u9ed1\u888d\u4eba\u8eab\u5b50\u98d8\u8d77\uff0c\u8dc3\u843d\u8239\u4e0a\u3002 \u3000\u3000\u770b\u4ed6\u4e0a\u8239\u7684\u59ff\u52bf\uff0c\u817f\u4e0d\u5c48\u819d\uff0c\u811a\u672a\u8d70\u52a8\uff0c\u53ea\u662f\u4e00\u63d0\u6c14\uff0c\u4eba\u5c31\u8f7b\u98d8\u98d8\u7684\u843d\u5728\u8239\u4e0a\uff0c\u5206\u660e\u662f \u6709\u7740\u6781\u6df1\u7684\u6b66\u529f\u9020\u8be3\u3002 \u3000\u3000\u8239\u4e0a\u4eba\u4e5f\u7a7f\u7740\u9ed1\u8272\u7684\u8863\u670d\uff0c\u9001\u4e0a\u6765\u4e00\u4e2a\u725b\u5934\u9762\u5177\uff0c\u9053\uff1a\u201c\u6234\u4e0a\u5b83\uff0c\u8fd9\u5c31\u662f\u4f60\u7684\u4ee3\u53f7\u3002\u201d \u3000\u3000\u9ed1\u888d\u4eba\u7531\u8499\u9762\u9ed1\u7eb1\u4e2d\u900f\u51fa\u4e24\u9053\u51b7\u7535\u4e00\u822c\u7684\u76ee\u5149\uff0c\u51dd\u6ce8\u5728\u90a3\u4e2a\u725b\u5934\u9762\u5177\u4e0a\uff0c\u4f3c\u662f\u53d7\u5230\u4e86\u6781 \u5927\u7684\u5c48\u8fb1\uff0c\u4e0d\u80af\u4f38\u624b\u53bb\u63a5\u3002 \u3000\u3000\u201c\u4e00\u53f7\uff0c\u522b\u5c0f\u770b\u8fd9\u4e2a\u725b\u5934\u9762\u5177\uff0c\u662f\u4eca\u665a\u9996\u5ea7\u8d35\u5bbe\uff0c\u6234\u4e0a\u5427\uff01\u4f60\u4f1a\u5f97\u5230\u4f60\u60f3\u8981\u7684\u4e1c\u897f\uff0c\u800c \u4e14\u2026\u2026\u201d \u3000\u3000\u800c\u4e14\u600e\u4e48\u6837\uff0c\u90a3\u9ed1\u8863\u4eba\u672a\u8bf4\u4e0b\u53bb\uff0c\u56e0\u4e3a\uff0c\u4f60\u4f1a\u5f97\u5230\u4f60\u60f3\u8981\u7684\u4e1c\u897f\uff0c\u5df2\u5177\u6709\u4e86\u5f3a\u5927\u7684\u8bf4\u670d \u529b\uff0c\u9ed1\u888d\u4eba\u63a5\u8fc7\u9762\u5177\uff0c\u6234\u5728\u5934\u4e0a\u3002 \u3000\u3000\u5c0f\u821f\u7acb\u523b\u8fc5\u901f\u7684\u5411\u6e56\u4e2d\u9a70\u53bb\u3002 \u3000\u3000\u6d1e\u5ead\u822b\u4e00\u5411\u5728\u6d1e\u5ead\u6e56\u5cb8\u8fb9\u505c\u9760\uff0c\u4eca\u665a\u5374\u7f06\u8d77\u951a\uff0c\u505c\u5728\u4e00\u5904\u8fbd\u9614\u7684\u6c34\u57df\u4e2d\u3002 \u3000\u3000\u5c0f\u821f\u9760\u8fd1\u6d1e\u5ead\u80aa\uff0c\u821f\u4e2d\u7684\u4eba\u7acb\u523b\u88ab\u4e24\u4e2a\u5c11\u5973\u63a5\u4eba\u8231\u4e2d\u3002 \u3000\u3000\u8231\u4e2d\u5012\u662f\u706f\u706b\u901a\u660e\uff0c\u53ea\u662f\u7a97\u3001\u95e8\u90fd\u88ab\u5f88\u539a\u7684\u9ed1\u8272\u5e37\u5e55\u906e\u4f4f\uff0c\u5916\u9762\u65e0\u6cd5\u770b\u5230\u3002 \u3000\u3000\u4f46\u95fb\u5feb\u821f\u5212\u6ce2\u4e4b\u58f0\uff0c\u56db\u8258\u5c0f\u821f\uff0c\u98de\u9a70\u800c\u6765\uff0c\u9760\u8fd1\u4e86\u6d1e\u5ead\u822b\u3002 \u3000\u3000\u8fce\u5ba2\u7684\u5c11\u5973\u5e26\u8fdb\u6765\u56db\u4e2a\u7a7f\u7740\u5bbd\u5927\u9ed1\u888d\u7684\u4eba\u3002 \u3000\u3000\u725b\u5934\u4eba\u6700\u5173\u5fc3\u7684\u4e00\u4ef6\u4e8b\uff0c\u662f\u770b\u770b\u6765\u4eba\uff0c\u662f\u5426\u4e5f\u6234\u7740\u9762\u5177\u3002 \u3000\u3000\u9762\u5177\u7684\u53cc\u76ee\u5904\uff0c\u662f\u4e24\u4e2a\u7a7a\u6d1e\uff0c\u900f\u8fc7\u9ed1\u7eb1\uff0c\u53ef\u4ee5\u770b\u5f97\u5341\u5206\u6e05\u695a\u3002 \u3000\u3000\u4e0d\u9519\uff0c\u56db\u4e2a\u4eba\u548c\u4ed6\u4e00\u6837\uff0c\u90fd\u6234\u6709\u9762\u5177\uff0c\u5206\u522b\u662f\u9a6c\u3001\u7f8a\u3001\u9a74\u3001\u732a\u3002 \u3000\u3000\u725b\u5934\u4eba\u5fc3\u6697\u5fd6\u9053\uff1a\u770b\u6837\u5b50\uff0c\u6211\u771f\u662f\u4eca\u591c\u7684\u9996\u5ea7\u5ba2\u4eba\u3002 \u3000\u3000\u8231\u4e2d\u706f\u706b\u901a\u660e\uff0c\u4e94\u4eba\u76f8\u4e92\u89c2\u770b\uff0c\u4f46\u5374\u65e0\u6cd5\u770b\u51fa\u4ed6\u4eec\u5fc3\u4e2d\u662f\u4f55\u611f\u53d7\uff1f\u56e0\u4e3a\u9762\u5177\u52a0\u4e0a\u8499\u9762\u9ed1 \u7eb1\uff0c\u65e0\u8bba\u5982\u4f55\u51cc\u5389\u7684\u76ee\u5149\uff0c\u4e5f\u65e0\u6cd5\u770b\u5230\u4ed6\u4eec\u8138\u4e0a\u7684\u795e\u8272\u3001\u8868\u60c5\u3002 \u3000\u3000\u201c\u6b22\u8fce\uff01\u6b22\u8fce\uff01\u4e94\u4f4d\u90fd\u662f\u5f88\u5b88\u4fe1\u7684\u4eba\uff0c\u5728\u4e0b\u975e\u5e38\u611f\u6fc0\uff0c\u73b0\u5728\u8bf7\u5404\u6309\u987a\u4f4d\u5750\u4e0b\u5427\uff01\u201d \u3000\u3000\u539f\u6765\uff0c\u8231\u4e2d\u65e9\u5df2\u6446\u597d\u4e86\u516d\u5f20\u592a\u5e08\u6905\uff0c\u9ec4\u7f0e\u5b50\u5750\u57ab\uff0c\u914d\u7740\u56db\u58c1\u9ec4\u7eeb\u5e54\u58c1\uff0c\u770b\u6765\u662f\u5341\u5206\u9ad8 \u8d35\u3002 \u3000\u3000\u56e0\u4e3a\u6574\u4e2a\u5927\u8231\uff0c\u5e73\u5e38\u6446\u6709\u4e94\u516d\u5341\u5f20\u684c\u5b50\uff0c\u73b0\u5728\u5374\u5f88\u7a7a\u65f7\uff0c\u9664\u4e86\u516d\u5f20\u592a\u5e08\u6905\u5916\uff0c\u518d\u65e0\u6446 \u8bbe\u3002 \u3000\u3000\u6ca1\u6709\u5403\u996d\u7684\u684c\u5b50\u3002 \u3000\u3000\u539f\u6765\uff0c\u4e94\u4eba\u63a5\u5230\u7684\u901a\u77e5\u662f\uff0c\u4eca\u591c\u4ee5\u6700\u540d\u8d35\u7684\u4f73\u80b4\u62db\u5f85\uff0c\u5c71\u73cd\u3001\u6d77\u5473\u3001\u8364\u7d20\u7686\u5907\uff0c\u5e0c\u671b\u8bf8 \u4f4d\u54c1\u5c1d\u3002\u6240\u4ee5\uff0c\u6765\u7684\u4eba\uff0c\u90fd\u672a\u5403\u996d\uff0c\u6b64\u523b\u8179\u4e2d\u9887\u6709\u9965\u997f\u4e4b\u611f\uff0c\u4e0d\u89c1\u9910\u684c\u98df\u5177\uff0c\u500d\u589e\u997f\u610f\u3002 \u3000\u3000\u8f6c\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u90a3\u8bf4\u8bdd\u7684\u4e3b\u4eba\uff0c\u5012\u662f\u672a\u5e26\u9762\u5177\uff0c\u7709\u773c\u3001\u53cc\u8033\uff0c\u90fd\u770b\u5f97\u5341\u5206\u6e05\u695a\u3002 \u3000\u3000\u4f46\u770b\u4e86\u7b49\u4e8e\u6ca1\u770b\u3002 \u3000\u3000\u56e0\u4e3a\u4eba\u4e16\u95f4\u4e0d\u53ef\u80fd\u6709\u90a3\u4e48\u767d\u7684\u4eba\u3002 \u3000\u3000\u539f\u6765\u90a3\u4e3b\u4eba\uff0c\u7a7f\u4e86\u4e00\u4ef6\u5982\u96ea\u767d\u888d\uff0c\u767d\u8272\u7684\u978b\u5b50\uff0c\u5934\u4e0a\u8fd8\u6709\u4e00\u6761\u767d\u8272\u5e26\u5b50\uff0c\u624e\u7740\u5934\u53d1\uff0c\u5168 \u8eab\u4e0a\u4e0b\uff0c\u9664\u4e86\u5934\u53d1\u3001\u7709\u6bdb\u662f\u9ed1\u8272\u5916\uff0c\u4e00\u7247\u7eaf\u767d\uff0c\u4e00\u5f20\u8138\u767d\u5f97\u548c\u888d\u5b50\u4e00\u6837\uff0c\u4f3c\u662f\u90a3\u5f20\u8138\u5c31\u662f\u5236 \u888d\u7684\u767d\u5e03\u526a\u4e0b\u6765\u505a\u7684\u4e00\u6837\uff0c\u53eb\u4eba\u5206\u4e0d\u6e05\u76ae\u80a4\u548c\u8863\u670d\u3002 \u3000\u3000\u767d\u8863\u4eba\u51b7\u7136\u4e00\u7b11\uff0c\u9732\u51fa\u4e00\u53e3\u5c16\u5229\u7684\u767d\u7259\u3002\u9053\uff1a\u201c\u4e3a\u4e86\u4fbf\u4e8e\u6b64\u540e\u79f0\u547c\uff0c\u53c8\u80fd\u4e0d\u6cc4\u6f0f\u8bf8\u4f4d\u7684 \u8eab\u4efd\uff0c\u4e94\u4f4d\u5934\u4e0a\u6234\u7684\u9762\u5177\uff0c\u4e5f\u5c31\u662f\u8bf8\u4f4d\u4ee3\u53f7\uff0c\u725b\u5934\u662f\u725b\u5148\u751f\uff0c\u9a6c\u5934\u662f\u9a6c\u5148\u751f\uff0c\u7f8a\u3001\u732a\u3001\u9a74\uff0c \u5c31\u662f\u6768\u3001\u6731\u3001\u5415\u3001\u4e09\u4f4d\u5148\u751f\uff0c\u8bf7\u8bf8\u4f4d\u7262\u7262\u8bb0\u4e0b\uff0c\u518d\u6b21\u4f1a\u9762\u65f6\uff0c\u8bf7\u8bf8\u4f4d\u81ea\u5df1\u5148\u5c06\u9762\u5177\u6234\u4e0a\uff0c\u4ee5 \u63a9\u53bb\u672c\u6765\u9762\u76ee\u3002\u201d \u3000\u3000\u201c\u54b1\u4eec\u5df2\u5fcd\u53d7\u4e86\u6781\u5927\u7684\u5c48\u8fb1\u2026\u2026\u201d\u725b\u5148\u751f\u8bf4\uff1a\u201c\u4e0d\u77e5\u9053\u4f60\u7b54\u5e94\u6211\u7684\u4e8b\uff0c\u662f\u5426\u80fd\u5373\u523b\u5151 \u73b0\uff1f\u201d \u3000\u3000\u4ed6\u5927\u6982\u662f\u6015\u4ece\u58f0\u97f3\u4e2d\uff0c\u6cc4\u9732\u51fa\u8eab\u4efd\uff0c\u6545\u610f\u6539\u7528\u5047\u97f3\u8bf4\u8bdd\uff0c\u58f0\u97f3\u7531\u725b\u5934\u4e2d\u4f20\u4e86\u51fa\u6765\uff0c\u542c\u5f97 \u4eba\u6c57\u6bdb\u76f4\u7ad6\u3002 \u3000\u3000\u201c\u5f53\u7136\uff0c\u7acb\u523b\u5151\u73b0\u2026\u2026\u201d\u767d\u8863\u4eba\u4e3e\u624b\u4e92\u51fb\u4e09\u638c\uff0c\u4e94\u4e2a\u8eab\u7740\u9ec4\u8863\u7684\u5a07\u7f8e\u5c11\u5973\uff0c\u5404\u6367\u4e00\u4e2a\u9ec4 \u7f0e\u5b50\u5e54\u906e\u7684\u6728\u5323\u884c\u4e86\u51fa\u6765\uff0c\u5206\u522b\u4ea4\u7ed9\u5de5\u4eba\u3002 \u3000\u3000\u4e94\u4e2a\u4eba\u90fd\u8feb\u4e0d\u53ca\u5f85\u7684\u6253\u5f00\u5323\u76d6\uff0c\u4f46\u53c8\u4f3c\u6015\u522b\u4eba\u770b\u5230\uff0c\u7528\u8eab\u5b50\u906e\u7740\uff0c\u77a7\u770b\u4e86\u4e00\u9635\uff0c\u4f3c\u662f\u90fd \u5f88\u6ee1\u610f\uff0c\u5408\u4e0a\u5323\u76d6\u3002 \u3000\u3000\u767d\u8863\u4eba\u7b11\u4e00\u7b11\uff1a\u201c\u4e1c\u897f\u4e0d\u9519\u5427\uff01\u201d \u3000\u3000\u4e94\u4e2a\u4eba\u6ca1\u6709\u7b54\u8bdd\uff0c\u4f46\u5374\u7528\u70b9\u5934\u8868\u793a\u4e86\u6ee1\u610f\u3002 \u3000\u3000\u4ed6\u4eec\u6781\u5c3d\u5c0f\u5fc3\uff0c\u63a9\u9970\u81ea\u5df1\u7684\u8eab\u4efd\uff0c\u80fd\u4e0d\u8bf4\u8bdd\uff0c\u5c31\u5c3d\u91cf\u4e0d\u5f00\u53e3\u3002 \u3000\u3000\u767d\u8863\u4eba\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u5f88\u5b88\u4fe1\u8bfa\uff0c\u5e0c\u671b\u8bf8\u4f4d\u4e5f\u4e0d\u8981\u5fd8\u6389\u4eca\u65e5\u4e4b\u4f1a\uff0c\u5929\u4e0b\u81f3\u7f8e\u7684\u4f73\u80b4\uff0c \u65e9\u5df2\u5907\u9f50\uff0c\u5305\u62ec\u5f88\u591a\u90fd\u662f\u8bf8\u4f4d\u6ca1\u6709\u5403\u8fc7\u7684\u4e1c\u897f\u2026\u2026\u201d \u3000\u3000\u4e94\u4e2a\u4eba\u9f50\u9f50\u6447\u5934\uff0c\u4f3c\u8981\u544a\u8f9e\uff0c\u4e0d\u51c6\u5907\u5403\u4e1c\u897f\u4e86\u3002 \u3000\u3000\u767d\u8863\u4eba\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u82b1\u4e86\u4e00\u5e74\u7684\u5de5\u592b\uff0c\u624d\u51c6\u5907\u8fd9\u4e9b\u7f8e\u5473\uff0c\u5c82\u53ef\u4e0d\u5403\uff0c\u90a3\u5c06\u662f\u8bf8\u4f4d \u4e00\u751f\u5927\u61be\u4e8b\uff0c\u4e1c\u897f\u5df2\u4ea4\u5230\u8bf8\u4f4d\u624b\u4e0a\uff0c\u4f55\u5fc5\u6025\u5728\u4e00\u65f6\u5462\uff1f\u4e3a\u4e86\u65b9\u4fbf\u8bf8\u4f4d\u7528\u9910\uff0c\u6211\u4e5f\u6709\u51c6\u5907\uff0c\u73b0 \u5728\u8bf8\u4f4d\u770b\u573a\u8fce\u5bbe\u6b4c\u821e\uff0c\u5373\u523b\u5206\u5934\u8fdb\u9910\u3002\u201d \u3000\u3000\u4e94\u4e2a\u4eba\u6447\u5934\u62d2\u7edd\u3002 \u3000\u3000\u4f46\u767d\u8863\u4eba\u5374\u4e0d\u7406\u4f1a\uff0c\u4e3e\u624b\u6325\u52a8\uff0c\u7acb\u523b\u54cd\u8d77\u4e86\u52a8\u4eba\u7684\u97f3\u4e50\u3002 \u3000\u3000\u4e94\u4e2a\u8eab\u7740\u7ea2\u7eb1\u5916\u8863\u7684\u5c11\u5973\uff0c\u5df2\u968f\u7740\u97f3\u4e50\u51fa\u73b0\uff0c\u4f18\u7f8e\u7684\u821e\u59ff\uff0c\u4e00\u4e0b\u5b50\u5c31\u5438\u5f15\u4f4f\u4e86\u4e94\u4eba\u773c \u5149\u3002 \u3000\u3000\u4f46\u95fb\u4e50\u58f0\u4e00\u53d8\uff0c\u4e94\u4e2a\u5c11\u5973\u7684\u821e\u6b65\u5ffd\u89c1\u6025\u5feb\u3002 \u3000\u3000\u90a3\u662f\u65e0\u6bd4\u8bf1\u60d1\u7684\u821e\u6b65\uff0c\u7389\u817f\u98de\u626c\uff0c\u67f3\u8170\u6b3e\u6446\u3002 \u3000\u3000\u4e0d\u77e5\u4f55\u65f6\uff0c\u4e94\u4e2a\u5c11\u5973\u8eab\u4e0a\u7684\u7ea2\u7eb1\u5916\u8863\uff0c\u5df2\u7ecf\u9000\u53bb\uff0c\u5168\u8eab\u51e0\u4e4e\u5168\u88f8\uff0c\u53ea\u6709\u4e00\u7247\u624b\u638c\u5927\u5c0f\u7684 \u83b2\u82b1\uff0c\u63a9\u906e\u4f4f\u5c0f\u8179\u4ee5\u4e0b\u3002 \u3000\u3000\u5239\u65f6\u95f4\uff0c\u4e73\u9001\u81c2\u6d6a\uff0c\u968f\u7740\u626c\u52a8\u7684\u7c89\u7ea2\u83b2\u82b1\uff0c\u4f7f\u4eba\u76ee\u6ce2\u4e94\u8272\uff0c\u5982\u996e\u9187\u9152\u3002 \u3000\u3000\u4e94\u4e2a\u4eba\u90fd\u6709\u6df1\u539a\u7684\u5b9a\u529b\uff0c\u4f46\u7adf\u7136\u628a\u6301\u4e0d\u4f4f\uff0c\u4e0d\u81ea\u4e3b\u7684\u5168\u795e\u51dd\u6ce8\u90a3\u52a8\u4eba\u7684\u8273\u821e\u3002 \u3000\u3000\u767d\u8863\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u5fd6\u9053\uff1a\u770b\u6765\uff0c\u4ed6\u4eec\u5df2\u7ecf\u9677\u5165\u4e86\u8ff7\u9b42\u9635\u4e2d\uff0c\u5b9a\u529b\u6d88\u9000\uff0c\u65e0\u6cd5\u81ea\u4e3b\u4e86\u3002\u7acb \u523b\u53c8\u4f5c\u51fa\u4e86\u4e00\u4e2a\u624b\u52bf\u3002 \u3000\u3000\u4f46\u95fb\u4e50\u58f0\u4e00\u53d8\uff0c\u4e94\u4e2a\u5c11\u5973\u5206\u5411\u4e94\u4eba\u5954\u53bb\u3002 \u3000\u3000\u5979\u4eec\u52a8\u4f5c\u8fc5\u5feb\uff0c\u5954\u5230\u4e94\u4eba\u8eab\u4fa7\uff0c\u5df2\u7136\u88ab\u4e0a\u4e86\u7ea2\u7eb1\u5916\u8863\u3002 \u3000\u3000\u7ea2\u7eb1\u98d8\u52a8\uff0c\u7f8a\u8102\u822c\u7684\u808c\u80a4\uff0c\u73b2\u73d1\u7684\u5a07\u8eaf\uff0c\u82e5\u9690\u82e5\u73b0\uff0c\u4e94\u4e2a\u9ed1\u888d\u4eba\u65e9\u5df2\u5f15\u8d77\u7684\u6b32\u671b\u60c5\u7130\uff0c \u6b64\u523b\u66f4\u89c9\u5f3a\u70c8\u3002 \u3000\u3000\u4f46\u95fb\u5a07\u5a9a\u7684\u58f0\u97f3\uff0c\u4f20\u5165\u8033\u9645\uff0c\u8fce\u4e86\u53bb\uff1a\u201c\u6211\u4eec\u5403\u996d\u53bb\u3002\u201d \u3000\u3000\u4e94\u4e2a\u5c11\u5973\u540c\u65f6\u4f38\u51fa\u7ea4\u5de7\u7684\u7389\u624b\uff0c\u5404\u62c9\u4e00\u4e2a\uff0c\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u8231\u95e8\u5927\u5f00\uff0c\u4e94\u8258\u4e2d\u578b\u753b\u822b\uff0c\u65e9\u5df2\u6cca\u5728\u6d1e\u5ead\u80aa\u65c1\uff0c\u6b65\u5165\u8231\u4e2d\u3002 \u3000\u3000\u8231\u5185\u65e9\u5df2\u6446\u597d\u4f73\u80b4\u7f8e\u9152\uff0c\u800c\u4e14\u522b\u65e0\u4e00\u4eba\u3002 \u3000\u3000\u4e94\u4e2a\u9ed1\u888d\u4eba\u8fdb\u5165\u8231\u4e2d\u4e4b\u540e\uff0c\u4e94\u8258\u753b\u822b\uff0c\u5206\u522b\u9a70\u52a8\uff0c\u5404\u5954\u4e00\u65b9\uff0c\u7247\u523b\u4e4b\u540e\uff0c\u5df2\u7136\u4e92\u4e0d\u76f8 \u89c1\u3002 \u3000\u3000\u6e56\u9762\u4e0a\u4e00\u7247\u5e7d\u6697\uff0c\u8231\u4e2d\u5374\u706b\u70db\u878d\u878d\uff0c\u666f\u7269\u6e05\u660e\u3002 \u3000\u3000\u5f53\u7136\uff0c\u90a3\u7ea2\u7eb1\u7f8e\u5973\u4f9d\u504e\u8eab\u4fa7\uff0c\u4e5f\u770b\u5f97\u66f4\u52a0\u6e05\u695a\u4e86\u3002 \u3000\u3000\u90a3\u662f\u6781\u5c11\u89c1\u7684\u7edd\u4e16\u7f8e\u5973\uff0c\u7709\u76ee\u5982\u753b\uff0c\u5a07\u6001\u6a2a\u751f\u3002 \u3000\u3000\u53ea\u89c1\u5979\u83b2\u6b65\u8f7b\u79fb\uff0c\u884c\u8fd1\u7a97\u53e3\uff0c\u62c9\u4e0a\u7a97\u5e18\uff0c\u987f\u65f6\u5185\u5916\u9694\u7edd\uff0c\u624d\u7f13\u7f13\u7684\u9664\u4e0b\u7ea2\u7eb1\u5916\u8863\uff0c\u884c\u8fd1 \u725b\u5148\u751f\u7684\u8eab\u4fa7\uff0c\u5a07\u58f0\u7b11\u9053\uff1a\u201c\u8fd9\u8258\u753b\u822b\u4e4b\u4e0a\uff0c\u53ea\u6709\u4f60\u6211\u4e24\u4eba\uff0c\u4e0d\u7528\u5e26\u8fd9\u725b\u5934\u9762\u5177\u4e86\u3002\u201d \u3000\u3000\u4e00\u4f38\u624b\u53d6\u4e0b\u9762\u5177\uff0c\u7d27\u63a5\u7740\u53bb\u63ed\u725b\u5148\u751f\u7684\u8499\u9762\u9ed1\u7eb1\u3002 \u3000\u3000\u201c\u6447\u6a79\u7684\u4eba\u5462\uff1f\u201d\u725b\u5148\u751f\u53f3\u624b\u75be\u51fa\uff0c\u6263\u4f4f\u4e86\u5c11\u5973\u7684\u5de6\u8155\u3002 \u3000\u3000\u4ed6\u867d\u65e9\u5df2\u5fc3\u52a8\u795e\u6447\uff0c\u4f46\u8fd8\u6015\u6cc4\u6f0f\u8eab\u4efd\u3002 \u3000\u3000\u5c11\u5973\u5ae3\u7136\u4e00\u7b11\uff0c\u90a3\u4e24\u4e2a\u6447\u6a79\u7684\u4eba\uff0c\u90fd\u5728\u8239\u5c3e\uff0c\u90a3\u5904\u548c\u524d\u9762\u9694\u7edd\uff0c\u4ed6\u4eec\u770b\u4e0d\u5230\u4ec0\u4e48\uff0c\u4e5f\u542c \u4e0d\u5230\u4ec0\u4e48\u58f0\u97f3\u3002 \u3000\u3000\u53f3\u624b\u4e00\u6325\uff0c\u6293\u4e0b\u4e86\u725b\u5148\u751f\u7684\u8499\u9762\u9ed1\u7eb1\uff0c\u4e5f\u62c9\u4e0b\u4e86\u4ed6\u5305\u5934\u9ed1\u5dfe\u3002 \u3000\u3000\u4e00\u4e0b\u5b50\u9732\u51fa\u4e86\u672c\u6765\u7684\u9762\u76ee\u3002 \u3000\u3000\u4e56\u4e56\uff0c\u7adf\u7136\u662f\u4e00\u4e2a\u5149\u5934\u548c\u5c1a\u3002 \u3000\u3000\u8fd9\u548c\u5c1a\u65b9\u9762\u5927\u8033\uff0c\u6148\u7709\u5584\u76ee\uff0c\u5982\u82e5\u8eab\u62ab\u8888\u88df\uff0c\u770b\u4e0a\u53bb\u5b9a\u7136\u662f\u4e00\u4f4d\u6709\u9053\u7684\u9ad8\u50e7\u3002 \u3000\u3000\u53ea\u662f\u4ed6\u7a7f\u7740\u9ed1\u8272\u7684\u5bbd\u5927\u68c9\u888d\uff0c\u8138\u4e0a\u7ea2\u6655\u4f3c\u706b\uff0c\u770b\u4e0a\u53bb\u5c31\u6709\u70b9\u8be1\u5f02\u4e86\u3002 \u3000\u3000\u90a3\u5c11\u5973\u683c\u683c\u4e00\u7b11\uff0c\u5012\u4e86\u4e24\u676f\u9152\uff0c\u9053\uff1a\u201c\u5927\u5e08\uff0c\u5e72\u676f\u3002\u201d\u5979\u53cc\u624b\u5404\u7aef\u7740\u4e00\u676f\u9152\uff0c\u4e00\u676f\u7ed9\u548c \u5c1a\uff0c\u4e00\u676f\u81ea\u5df1\u559d\uff0c\u53cc\u624b\u5e76\u7528\uff0c\u4e24\u4eba\u4e5f\u540c\u65f6\u559d\u4e0b\u3002 \u3000\u3000\u201c\u5403\u83dc\u5427\u2026\u2026\u201d\u5c11\u5973\u62ff\u8d77\u7b77\u5b50\uff0c\u4e00\u4e0b\u5750\u5165\u548c\u5c1a\u6000\u91cc\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u684c\u4e0a\u7684\u5341\u76d8\u4f73\u80b4\uff0c\u786e\u8d39 \u5f88\u591a\u65f6\u95f4\uff0c\u624d\u627e\u9f50\u6750\u6599\uff0c\u5f53\u4e16\u738b\u516c\uff0c\u4e5f\u672a\u5fc5\u5403\u8fc7\uff0c\u4e0d\u5403\u5b9e\u5728\u53ef\u501f\u3002\u201d \u3000\u3000\u5979\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u7b77\u5b50\u5374\u4e0d\u505c\u7684\u4e3e\u52a8\uff0c\u5939\u8d77\u83dc\u80b4\uff0c\u653e\u5165\u548c\u5c1a\u53e3\u4e2d\u3002 \u3000\u3000\u5927\u548c\u5c1a\u4f3c\u662f\u5df2\u5931\u53bb\u63a7\u5236\u81ea\u5df1\u7684\u80fd\u529b\uff0c\u4efb\u7531\u90a3\u5c11\u5973\u6446\u5e03\u3001\u6298\u817e\u3002 \u3000\u3000\u4ed6\u4e00\u751f\u8339\u7d20\uff0c\u54ea\u91cc\u5403\u8fc7\u8fd9\u79cd\u5c71\u73cd\u3001\u6d77\u5473\uff0c\u53ea\u89c9\u9999\u900f\u80ba\u8151\uff0c\u5473\u5165\u809d\u80a0\uff0c\u5176\u9999\u5ae9\u9c9c\u7f8e\uff0c\u662f\u60f3 \u4e5f\u60f3\u4e0d\u5230\u7684\u611f\u89c9\u3002 \u3000\u3000\u9152\u80fd\u4e71\u6027\uff0c\u4f55\u51b5\u5927\u548c\u5c1a\u65e9\u5df2\u88ab\u90a3\u573a\u9999\u8273\u4e4b\u821e\uff0c\u6fc0\u8d77\u4e86\u538b\u5236\u6570\u5341\u5e74\u7684\u60c5\u7130\uff0c\u4e24\u676f\u9152\u4e0b\u809a\uff0c \u66f4\u89c9\u6b32\u5ff5\u9ad8\u6da8\u3002 \u3000\u3000\u9152\u4e2d\u6709\u9b3c\uff0c\u65e9\u5df2\u4e0b\u4e86\u52a9\u957f\u6fc0\u60c5\u7684\u836f\u7269\uff0c\u773c\u524d\u7f8e\u5973\u5982\u82b1\uff0c\u8863\u5c65\u5c3d\u53bb\uff0c\u7edd\u7f8e\u7684\u80f4\u4f53\uff0c\u5728\u6000\u4e2d \u5a49\u8f6c\u626d\u52a8\uff0c\u8179\u4e2d\u836f\u529b\u53d1\u4f5c\uff0c\u5fc3\u4f3c\u706b\u70e7\u5c31\u7b97\u5927\u548c\u5c1a\u4fee\u70bc\u7cbe\u6df1\uff0c\u4e5f\u5fcd\u53d7\u4e0d\u4f4f\u8fd9\u5185\u5916\u4ea4\u8feb\u7684\u538b\u529b\uff0c \u987f\u89c9\u70ed\u8840\u6cb8\u817e\uff0c\u53cc\u624b\u5728\u90a3\u5c11\u5973\u8eab\u4e0a\u4e71\u6478\u8d77\u6765\u3002 \u3000\u3000\u5c11\u5973\u5a9a\u773c\u5982\u4e1d\uff0c\u5a07\u7b11\u8fde\u58f0\u9053\uff1a\u201c\u4f60\u597d\u574f\u54e6\uff01\u5927\u5e08\u7236\u5c31\u4e0d\u6015\u6570\u5341\u5e74\u7684\u6e05\u4fee\u3001\u7ae5\u8eab\uff0c\u6bc1\u5728\u5c0f \u59b9\u6211\u7684\u8eab\u4e0a\u4e48\uff1f\u201d \u3000\u3000\u5979\u7b11\u5f97\u5996\u8273\uff0c\u4e00\u53cc\u67d4\u82e5\u65e0\u9aa8\u7684\u7389\u624b\uff0c\u6367\u4f4f\u4e86\u5927\u548c\u5c1a\u7684\u53cc\u988a\uff0c\u5a9a\u76ee\u6df1\u6ce8\uff0c\u4f3c\u662f\u5f15\u8d77\u4ed6\u7684\u711a \u8eab\u6b32\u706b\u3002 \u3000\u3000\u5927\u548c\u5c1a\u4e00\u70b9\u7075\u6167\u4e0d\u6627\uff0c\u5c3d\u7ba1\u5168\u8eab\u5982\u9677\u706b\u7a9f\uff0c\u4f46\u5374\u505c\u4e0b\u4e86\u53cc\u624b\u5c0f\u5996\u5973\u5403\u4e86\u4e00\u60ca\uff0c\u6697\u9053\uff1a\u8fd9 \u548c\u5c1a\u8fd8\u771f\u6709\u4e00\u70b9\u9053\u884c\uff0c\u83ab\u8ba9\u4ed6\u60ac\u5d16\u52d2\u9a6c\uff0c\u5c3d\u5f03\u524d\u529f\u3002\u7acb\u523b\u9001\u4e0a\u4e86\u6e7f\u6da6\u7684\u6a31\u5507\u2026\u2026 \u3000\u3000\u8fd9\u79cd\u6311\u9017\uff0c\u5982\u706b\u52a0\u6cb9\uff0c\u5927\u548c\u5c1a\u90a3\u4e00\u70b9\u6ca1\u6627\u7684\u7075\u6167\uff0c\u7acb\u523b\u88ab\u88ab\u518d\u5ea6\u9ad8\u6da8\u7684\u6b32\u5ff5\u63a9\u6ca1\uff0c\u53e3\u4e2d \u543c\u4e86\u4e00\u58f0\uff0c\u53cd\u624b\u62b1\u7d27\u4e86\u5c11\u5973\u3002 \u3000\u3000\u5c11\u5973\u5401\u4e00\u53e3\u6c14\uff0c\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5927\u5e08\u7236\uff0c\u8fde\u996d\u4e5f\u5403\u4e0d\u4e0b\u4e86\u662f\u4e48\uff1f\u201d \u3000\u3000\u548c\u5c1a\u53e3\u4e2d\u53d1\u51fa\u6df1\u6c89\u7684\u58f0\u97f3\uff0c\u5706\u7741\u53cc\u76ee\uff0c\u54ea\u91cc\u8fd8\u50cf\u6709\u9053\u9ad8\u50e7\u7684\u6837\u5b50\uff0c\u7b80\u76f4\u662f\u4e00\u5934\u6b32\u706b\u9ad8\u6da8 \u7684\u8272\u72fc\u3002 \u3000\u3000\u5c0f\u5996\u5973\u6ee1\u8db3\u7684\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u770b\u6765\uff0c\u8981\u5c0f\u59b9\u6148\u60b2\u6148\u60b2\u4f60\u4e86\uff0c\u4e0d\u8fc7\u3002\u4f60\u53ef\u8981\u8bb0\u7740\uff0c\u4e0d\u80fd\u59cb \u4e71\u7ec8\u5f03\u2026\u2026\u201d \u3000\u3000\u5927\u548c\u5c1a\u4e0d\u4f4f\u7684\u70b9\u5934\uff0c\u5c31\u50cf\u70c8\u9633\u4e0b\u7684\u884c\u4eba\uff0c\u6325\u6c57\u5982\u96e8\uff0c\u6025\u9700\u90a3\u4e00\u53e3\u6e05\u51c9\u7684\u51b7\u6c34\u3002 \u3000\u3000\u5c11\u5973\u7275\u7740\u5927\u548c\u5c1a\uff0c\u63a8\u5f00\u53e6\u4e00\u6247\u8231\u95e8\uff0c\u6b65\u5165\u5176\u4e2d\uff0c\u90a3\u91cc\u6709\u4e00\u5f20\u5927\u5e8a\uff0c\u9526\u5e10\u7ee3\u88ab\uff0c\u5e03\u7f6e\u7684\u5982 \u540c\u65b0\u623f\u3002 \u3000\u3000\u5927\u548c\u5c1a\u4e00\u62ac\u624b\uff0c\u628a\u5c11\u5973\u6295\u5165\u5e8a\u4e2d\uff0c\u548c\u8eab\u6251\u4e86\u4e0a\u53bb\u2026\u2026 \u3000\u3000\u4f46\u95fb\u5c0f\u5996\u5973\u53e3\u4e2d\u53d1\u51fa\u5a07\u55f2\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u5927\u5e08\u7236\uff0c\u6211\u53eb\u76c8\u76c8\uff0c\u8bb0\u7740\u554a\uff01\u4f60\u7b54\u5e94\u8fc7\u6211\u4e0d\u80fd \u59cb\u4e71\u7ec8\u5f03\u554a\u2026\u2026\u201d \u3000\u3000\u538b\u5236\u4e86\u6570\u5341\u5e74\u7684\u60c5\u6b32\u66b4\u53d1\u4e86\uff0c\u662f\u90a3\u4e48\u75af\u72c2\u2026\u2026 \u3000\u3000\u4ed6\u5c1d\u8bd5\u5230\u4ece\u672a\u6709\u7684\u5feb\u4e50\uff0c\u8680\u9aa8\u6d88\u9b42\uff0c\u5982\u767b\u4ed9\u754c\u2026\u2026 \u3000\u3000\u4f46\u4e5f\u6bc1\u4e86\u4ed6\u6570\u5341\u5e74\u7684\u6e05\u4fee\u4e4b\u8eab\uff0c\u4e00\u591c\u4e4b\u95f4\u8fde\u7834\u4e86\u8364\u8272\u4e8c\u6212\u3002 \u3000\u3000\u8fd9\u5c31\u662f\u6b32\u6d77\uff0c\u4e00\u65e6\u9677\u8eab\u5176\u4e2d\uff0c\u4efb\u4f60\u91d1\u521a\u7f57\u6c49\uff0c\u4e5f\u5c06\u6c89\u6ca6\u5e94\u52ab\uff0c\u53d7\u5c3d\u78e8\u96be\u3002 \u3000\u3000\u5927\u548c\u5c1a\u5728\u753b\u822b\u4e2d\u4f4f\u4e86\u4e09\u5929\uff0c\u4e09\u5929\u65f6\u95f4\uff0c\u5b8c\u5168\u751f\u6d3b\u5728\u9152\u8272\u4e4b\u4e2d\u3002 \u3000\u3000\u9152\u662f\u597d\u9152\uff0c\u83dc\u662f\u9646\u6d77\u5947\u73cd\uff0c\u8272\u662f\u5e74\u8f7b\u7f8e\u4e3d\u7684\u5c11\u5973\u2026\u2026 \u3000\u3000\u7b2c\u4e8c\u5929\uff0c\u5927\u548c\u5c1a\u5c31\u5b8c\u5168\u6e05\u9192\u8fc7\u6765\uff0c\u4ed6\u75db\u6094\u72af\u4e0b\u4e86\u6212\u5f8b\uff0c\u4f46\u53c8\u65e0\u6cd5\u6297\u62d2\u76c8\u76c8\u59d1\u5a18\u7684\u8bf1\u60d1\uff0c \u8fd9\u5c31\u53ea\u597d\u88c5\u4f5c\u8fd8\u672a\u5b8c\u5168\u6e05\u9192\u7684\u6837\u5b50\uff0c\u5750\u4eab\u53e3\u8179\u4e4b\u6b32\u548c\u65e0\u9650\u7684\u6e29\u67d4\u3002 \u3000\u3000\u5176\u5b9e\uff0c\u7b2c\u4e8c\u5929\u7684\u9152\u83dc\u4e2d\uff0c\u5c31\u5df2\u7ecf\u6ca1\u6709\u6fc0\u60c5\u7684\u836f\u7269\u3002 \u3000\u3000\u5927\u548c\u5c1a\u4fdd\u6301\u5b8c\u5168\u7684\u6e05\u9192\uff0c\u8ba9\u4ed6\u5fc3\u7518\u60c5\u613f\u7684\u6ca6\u5165\u6b32\u6d77\uff0c\u4ee5\u5229\u65e5\u540e\u7684\u63a7\u5236\u3002 \u3000\u3000\u5927\u548c\u5c1a\u88c5\u4f5c\u7684\u795e\u60c5\uff0c\u76c8\u76c8\u5b8c\u5168\u660e\u767d\uff0c\u4f46\u5e76\u672a\u63ed\u7a7f\u3002 \u3000\u3000\u800c\u4e14\uff0c\u7b2c\u4e8c\u5929\u7684\u76c8\u76c8\u59d1\u5a18\uff0c\u4e5f\u6709\u4e86\u5f88\u5927\u7684\u6539\u53d8\uff0c\u5b8c\u5168\u6ca1\u6709\u4e86\u5996\u8273\u7684\u5473\u9053\uff0c\u4ee3\u4e4b\u800c\u8d77\uff0c\u50cf \u4e00\u53ea\u4f9d\u4eba\u5c0f\u9e1f\uff0c\u6709\u7740\u65e0\u9650\u7684\u6e29\u67d4\u3002 \u3000\u3000\u5927\u548c\u5c1a\u88ab\u5996\u8273\u8ff7\u60d1\uff0c\u4e5f\u88ab\u8fd9\u6e29\u67d4\u5f81\u670d\u3002 \u3000\u3000\u4e09\u5929\u5f88\u5feb\u7684\u8fc7\u53bb\uff0c\u76c8\u76c8\u9001\u548c\u5c1a\u767b\u5cb8\uff0c\u5927\u548c\u5c1a\u56de\u5934\u76ef\u5728\u76c8\u76c8\u7684\u8138\u8272\u4e0a\uff0c\u53cc\u76ee\u662f\u4e00\u7247\u4f9d\u4f9d\u60dc \u522b\u7684\u6837\u5b50\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u5f00\u53e3\uff0c\u4f46\u795e\u8272\u5374\u6d41\u9732\u51fa\u671f\u5f85\u540e\u4f1a\u7684\u7ea6\u5b9a\u3002 \u3000\u3000\u76c8\u76c8\u59d1\u5a18\u4e5f\u662f\u4e00\u7247\u4f9d\u604b\u7684\u795e\u8272\uff0c\u9053\uff1a\u201c\u8bb0\u7740\u7b54\u5e94\u8fc7\u6211\u7684\u8bdd\u2026\u2026\u201d \u3000\u3000\u5927\u548c\u5c1a\u70b9\u70b9\u5934\u3002 \u3000\u3000\u76c8\u76c8\u4e00\u95ed\u53cc\u76ee\uff0c\u6324\u4e0b\u6765\u4e24\u6ef4\u6cea\u6c34\uff0c\u9053\uff1a\u201c\u4e09\u65e5\u6069\u7231\uff0c\u5982\u9c7c\u5f97\u6c34\uff0c\u6211\u4f1a\u60f3\u5ff5\u4f60\u7684\uff01\u5927\u5e08 \u7236\uff01\u4e00\u5e74\u4e4b\u5185\uff0c\u4e0d\u8bba\u60c5\u5f62\u5982\u4f55\u56f0\u96be\uff0c\u6211\u90fd\u8981\u60f3\u529e\u6cd5\u518d\u89c1\u4f60\u2026\u2026\u201d \u3000\u3000\u4e5f\u8bb8\u662f\u5927\u548c\u5c1a\u5c31\u5728\u7b49\u8fd9\u53e5\u8bdd\uff0c\u671f\u5f85\u7684\u540e\u4f1a\u4e4b\u7ea6\uff0c\u70b9\u70b9\u5934\uff0c\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u8fd9\u662f\u4ef6\u60ca\u5929\u52a8\u5730\u7684\u5927\u4e8b\uff0c\u4f46\u5374\u6ca1\u6709\u4f20\u5165\u6c5f\u6e56\uff0c\u56e0\u4e3a\u77e5\u9053\u7684\u4eba\u4e0d\u591a\uff0c\u9664\u4e86\u5f53\u4e8b\u4eba\u5fc3\u4e2d\u7559\u4e0b \u4e86\u4e00\u7f15\u75db\u6094\u548c\u6000\u5ff5\u4e4b\u5916\uff0c\u6c5f\u6e56\u4e2d\u4e00\u5207\u5982\u5e38\u3002 \u3000\u3000\uff38\uff38\uff38\uff38\uff38\uff38\uff38\uff38\uff38\uff38\uff38\uff38\uff38\uff38\uff38\uff38\uff38\uff38\uff38\uff38\uff38\uff38\uff38\uff38\uff38\uff38\uff38\u660e\u6708\u5728\u5929\uff0c\u6e05\u8f89\u6d41\u7167\uff0c\u4f46 \u65e5\u7406\u4e07\u673a\u7684\u7687\u5e1d\uff0c\u8fd8\u6ca1\u6709\u4f11\u606f\u3002 \u3000\u3000\u4ed6\u8d35\u4e3a\u5929\u5b50\uff0c\u62e5\u6709\u56db\u6d77\uff0c\u4f46\u5374\u4f3c\u4e5f\u6709\u7740\u5f88\u5927\u7684\u70e6\u607c\uff0c\u9759\u9759\u7684\u5750\u7740\uff0c\u671b\u7740\u5c4b\u9876\u51fa\u795e\u3002 \u3000\u3000\u4e24\u4e2a\u5f53\u503c\u7684\u592a\u76d1\uff0c\u76f8\u4e92\u671b\u4e86\u4e00\u773c\uff0c\u5de6\u9996\u4e00\u4eba\u8eac\u8eab\u8bf4\u9053\uff1a\u201c\u7687\u4e0a\uff0c\u591c\u6df1\u4e86\uff0c\u9f99\u4f53\u60ac\u4e07\u6c11\u5b89 \u5371\uff0c\u8bf7\u591a\u4fdd\u91cd\u3002\u201d \u3000\u3000\u7687\u4e0a\u55ef\u4e86\u4e00\u58f0\uff0c\u7b54\u975e\u6240\u95ee\uff0c\u9053\uff1a\u201c\u4f20\u738b\u7edf\u9886\u6765\u89c1\u6211\u3002\u201d \u3000\u3000\u4e24\u4e2a\u592a\u76d1\u6014\u4e86\u4e00\u6014\uff0c\u4e0d\u6562\u8fdd\u65e8\uff0c\u6025\u6025\u5411\u5916\u5954\u53bb\u3002 \u3000\u3000\u7247\u523b\u4e4b\u540e\u3002 \u3000\u3000\u6458\u661f\u624b\u738b\u5f64\u4e00\u8eab\u58a8\u8272\u52b2\u88c5\uff0c\u5306\u5306\u884c\u5165\u4e86\u5fa1\u4e66\u623f\u4e2d\u3002 \u3000\u3000\u738b\u5f64\u53e9\u89c1\u7687\u4e0a\uff0c\u7687\u4e0a\u5374\u6446\u6446\u624b\uff0c\u547d\u623f\u4e2d\u7684\u592a\u76d1\u9000\u51fa\u4e66\u623f\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u537f\u5bb6\u8bf7\u8d77\u3002\u201d \u3000\u3000\u738b\u5f64\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u7687\u4e0a\u6709\u4f55\u793a\u8c15\uff1f\u201d \u3000\u3000\u201c\u5509\uff01\u7a97\u5916\u6708\u8272\u5982\u753b\uff0c\u4e0d\u77e5\u4eca\u5915\u4f55\u5915\uff1f\u201d \u3000\u3000\u201c\u516b\u6708\u5341\u56db\u2026\u2026\u201d\u738b\u5f64\u9053\uff1a\u201c\u4e5d\u6708\u5341\u4e94\uff0c\u5c31\u662f\u4e09\u516c\u4e3b\u91cd\u56de\u5929\u671d\u7684\u65e5\u5b50\u3002\u201d \u3000\u3000\u7687\u4e0a\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4e2a\u6708\u7684\u65f6\u95f4\uff0c\u600e\u4e48\u6ca1\u6709\u4e00\u70b9\u6d88\u606f\u5462\uff1f\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u4f8d\u81e3\u7acb\u523b\u5e26\u4eba\u5357\u4e0b\u67e5\u95ee\uff0c\u5982\u82e5\u4ed6\u4eec\u6562\u80cc\u5f03\u7ea6\u5b9a\uff0c\u4f8d\u81e3\u5c06\u53ec\u96c6\u4e2d\u539f\u6b66\u6797\u9ad8\u624b\uff0c \u76f4\u6363\u9b54\u7a9f\u2026\u2026\u201d \u3000\u3000\u201c\u5200\u5175\u65e2\u606f\uff0c\u6715\u5df2\u4e0d\u613f\u518d\u8c03\u5927\u519b\u897f\u5f81\uff0c\u4f46\u4e5f\u4e0d\u80fd\u8ba9\u4ed6\u4eec\u8f7b\u85d0\u4e0a\u56fd\uff0c\u80cc\u5f03\u76df\u7ea6\u3002\u6715\u8d50\u4f60\u7389 \u5370\u4e00\u65b9\uff0c\u5bc6\u65e8\u4e24\u9053\uff0c\u5929\u4e0b\u5404\u5e9c\u3001\u5dde\u7684\u6355\u5feb\uff0c\u7687\u5bab\u4f8d\u536b\u3001\u4e1c\u5382\u9ad8\u624b\uff0c\u4efb\u4f60\u8c03\u52a8\uff0c\u6c5f\u5357\u6c5f\u5317\u6b66\u6797 \u95e8\u6d3e\uff0c\u4e5f\u7531\u4f60\u5f81\u9009\u4eba\u624b\u2026\u2026\u201d \u3000\u3000\u987f\u4e86\u4e00\u987f\u3002 \u3000\u3000\u7687\u5e1d\u53c8\u9053\uff1a\u201c\u6715\u53ea\u5e0c\u671b\u4e09\u516c\u4e3b\u65e9\u56de\u4eac\u5e08\uff0c\u4ee5\u6170\u6715\u601d\u5ff5\u4e4b\u60c5\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u4f8d\u81e3\u7acb\u523b\u884c\u52a8\u2026\u2026\u201d \u3000\u3000\u201c\u738b\u537f\u5bb6\u2026\u2026\u201d \u3000\u3000\u201c\u738b\u5f64\u5728\u3002\u201d \u3000\u3000\u7687\u4e0a\u63a5\u9053\uff1a\u201c\u6715\u5df2\u8c03\u63f4\u5e93\u94f6\u4e94\u5341\u4e07\u4e24\uff0c\u7531\u4f60\u53d6\u7528\uff0c\u4e0d\u8db3\u4e4b\u6570\uff0c\u53ef\u7531\u6715\u8d50\u4f60\u7684\u7389\u5370\u2014\u2014\u5411 \u5404\u5730\u5dde\u5e9c\u5f81\u7528\uff0c\u6715\u77e5\u9053\uff0c\u6c5f\u6e56\u4e2d\u4eba\u82b1\u8d39\u751a\u5927\u3002\u201d \u3000\u3000\u201c\u7687\u4e0a\u539a\u6069\uff0c\u4f8d\u81e3\u544a\u9000\u3002\u201d \u3000\u3000\u738b\u5f64\u56de\u5230\u4e86\u4f4f\u5904\uff0c\u7acb\u523b\u62db\u6765\u97e9\u6d9b\u3001\u71d5\u98de\uff0c\u5429\u5490\u97e9\u6d9b\u4e25\u5b88\u7687\u5bab\uff0c\u81ea\u5df1\u5e26\u4e86\u71d5\u98de\u3001\u8d75\u4fdd\u3001\u9648 \u5b8f\uff0c\u56db\u9a91\u5feb\u9a6c\uff0c\u8fde\u591c\u5357\u4e0b\uff0c\u76f4\u5954\u5f00\u5c01\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u8c03\u52a8\u9526\u8863\u536b\u548c\u4e1c\u5382\u7684\u9ad8\u624b\uff0c\u65f6\u9650\u672a\u5230\uff0c\u4ed6\u4e0d\u60f3\u628a\u4e8b\u60c5\u95f9\u7684\u592a\u590d\u6742\uff0c\u4f55\u51b5\uff0c\u8fd9\u4e9b\u9ad8 \u624b\uff0c\u4e5f\u672a\u5fc5\u80fd\u5bf9\u4ed8\u9b54\u6559\u4e2d\u4eba\u2026\u2026 \u3000\u3000\u89e3\u94c3\u8fd8\u9700\u7cfb\u94c3\u4eba\uff0c\u4ed6\u51b3\u5b9a\u5148\u627e\u9a6c\u6587\u4e2d\u9a6c\u5de1\u629a\u3002 \u3000\u3000\u5feb\u9a6c\u75be\u8fdb\uff0c\u65e5\u591c\u517c\u7a0b\uff0c\u738b\u5f64\u8fdb\u5165\u5de1\u629a\u5e9c\u65f6\uff0c\u53ea\u6709\u8d75\u4fdd\u3001\u9648\u5b8f\u76f8\u968f\u3002 \u3000\u3000\u539f\u6765\uff0c\u71d5\u98de\u5949\u4e86\u738b\u5f64\u5bc6\u4ee4\uff0c\u627e\u5929\u4e0b\u7b2c\u4e00\u8ffd\u8e2a\u9ad8\u624b\u6c5f\u5343\u91cc\u53bb\u4e86\u3002 \u3000\u3000\u9a6c\u6587\u4e2d\u4f3c\u662f\u5df2\u7ecf\u5f97\u5230\u5feb\u62a5\uff0c\u738b\u7edf\u9886\u521a\u8fdb\u5e9c\u95e8\uff0c\u9a6c\u5de1\u629a\u5df2\u4eb2\u81ea\u8fce\u4e86\u51fa\u6765\uff0c\u9053\uff1a\u201c\u8d35\u5ba2\uff0c\u8d35 \u5ba2\uff0c\u8bf7\u5165\u4e66\u623f\u5f85\u8336\u3002\u201d\u4e00\u9762\u62b1\u62f3\u4f5c\u63d6\uff0c\u4e00\u526f\u6bd5\u606d\u6bd5\u656c\u7684\u795e\u60c5\u3002 \u3000\u3000\u4e24\u4eba\u65e9\u5df2\u76f8\u8bc6\uff0c\u800c\u4e14\uff0c\u8fd8\u4ea4\u8fc7\u624b\uff0c\u90a3\u4e00\u6218\u867d\u7136\u95f9\u7684\u60ca\u5929\u52a8\u5730\uff0c\u4f46\u9a6c\u6587\u4e2d\u4ecd\u4fdd\u4e0b\u6cb3\u5357\u5de1\u629a \u7684\u4f4d\u7f6e\uff0c\u738b\u5f64\u5e2e\u4e86\u5f88\u5927\u7684\u5fd9\uff0c\u9a6c\u6587\u4e2d\u5fc3\u4e2d\u7684\u611f\u6fc0\uff0c\u5012\u975e\u865a\u4f2a\u505a\u4f5c\u3002 \u3000\u3000\u4e66\u623f\u4e2d\u65e9\u5df2\u6446\u597d\u4e86\u9999\u8317\u8336\u70b9\uff0c\u9a6c\u6587\u4e2d\u4e00\u6325\u624b\uff0c\u4e24\u4e2a\u4f8d\u5019\u5728\u4fa7\u7684\u4e66\u7ae5\uff0c\u8eac\u8eab\u9000\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u738b\u5f64\u56de\u987e\u4e86\u4e24\u4e2a\u4ece\u536b\u4e00\u773c\uff0c\u8d75\u4fdd\u3001\u9648\u5b8f\u4e5f\u8ddf\u7740\u9000\u51fa\u4e66\u623f\u3002 \u3000\u3000\u738b\u5f64\u4e3e\u8d77\u8336\u676f\u559d\u4e86\u4e00\u53e3\uff0c\u672a\u6765\u5f97\u53ca\u5f00\u53e3\uff0c\u9a6c\u5de1\u629a\u5df2\u62a2\u5148\u9053\uff1a\u201c\u738b\u5144\uff0c\u5feb\u4e24\u5e74\u4e0d\u89c1\u4e86\uff0c\u5bf9 \u738b\u5144\u7684\u8bda\u4fe1\u3001\u4e49\u6c14\uff0c\u5144\u5f1f\u662f\u94ed\u611f\u5728\u5fc3\u3002\u201d \u3000\u3000\u201c\u5509\uff01\u738b\u67d0\u4fe1\u5b88\u627f\u8bfa\uff0c\u4e5f\u671b\u9a6c\u5144\u4e00\u6837\u4ee5\u8bda\u4fe1\u4e3a\u4e0a\uff0c\u4eca\u4e2a\u662f\u516b\u6708\u4e09\u5341\uff0c\u4e0b\u4e2a\u6708\u5e94\u8be5\u662f\u4e09\u516c \u4e3b\u56de\u671d\u7684\u65e5\u5b50\uff0c\u9a6c\u5144\u6ca1\u6709\u5fd8\u5427\uff01\u201d \u3000\u3000\u201c\u8fd9\u7b49\u5927\u4e8b\uff0c\u5982\u4f55\u80fd\u591f\u5fd8\u5f97\uff0c\u5144\u5f1f\u5728\u4e24\u4e2a\u6708\u524d\uff0c\u5df2\u9063\u6d3e\u8d75\u4e8c\u5824\u548c\u9a6c\u4e09\u8fdc\u884c\u897f\u57df\uff0c\u50ac\u4ed6\u4eec \u9001\u56de\u4e09\u516c\u4e3b\u2026\u2026\u201d \u3000\u3000\u987f\u4e86\u4e00\u987f\u3002 \u3000\u3000\u9a6c\u6587\u4e2d\u53c8\u8bf4\uff1a\u201c\u6211\u60f3\uff0c\u4ed6\u4eec\u4e0d\u81f4\u53d8\u5366\uff0c\u552f\u4e00\u7684\u9ebb\u70e6\uff0c\u53ef\u80fd\u662f\u7b97\u6cd5\u4e0a\u7684\u5dee\u5f02\u3002\u201d \u3000\u3000\u738b\u5f64\u4efb\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u8fd9\u8bdd\u600e\u4e48\u4e2a\u8bf4\u6cd5\uff1f\u201d \u3000\u3000\u201c\u738b\u5144\u7684\u7b97\u6cd5\u662f\u516c\u4e3b\u88ab\u63b3\u90a3\u5929\u7b97\u8d77\uff0c\u4e8c\u5e74\u4e4b\u671f\u662f\u4e5d\u6708\u5341\u4e94\uff0c\u5e94\u8be5\u628a\u4eba\u9001\u5165\u4e2d\u539f\u2026\u2026\u201d \u3000\u3000\u987f\u4e86\u4e00\u987f\u3002 \u3000\u3000\u9a6c\u5de1\u629a\u53c8\u8bf4\uff1a\u201c\u5144\u5f1f\u62c5\u5fc3\u7684\u662f\uff0c\u4ed6\u4eec\u7684\u7b97\u6cd5\uff0c\u516c\u4e3b\u8fdb\u5165\u897f\u57df\u540e\uff0c\u4f4f\u8db3\u4e86\u4e24\u5e74\uff0c\u624d\u7b97\u671f \u6ee1\uff0c\u7136\u540e\u518d\u9001\u4e09\u516c\u4e3b\u56de\u5f52\u5929\u671d\u3002\u201d \u3000\u3000\u738b\u5f64\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8fd9\u8981\u76f8\u5dee\u591a\u5c11\u65f6\u95f4\uff1f\u201d \u3000\u3000\u201c\u5e94\u8be5\u6709\u4e09\u4e2a\u6708\u7684\u5dee\u5f02\u2026\u2026\u201d\u9a6c\u6587\u4e2d\u82e6\u7b11\u4e00\u4e0b\u3002 \u3000\u3000\u63a5\u7740\u53c8\u9053\uff1a\u201c\u8fd9\u4e00\u70b9\u5144\u5f1f\u4e5f\u65e9\u60f3\u5230\u4e86\uff0c\u6240\u4ee5\uff0c\u624d\u6d3e\u8d75\u4e8c\u5824\u548c\u9a6c\u4e09\u517c\u7a0b\u8d76\u5f80\u897f\u57df\uff0c\u4ee5\u4ed6\u4eec \u65e5\u591c\u517c\u7a0b\u7684\u884c\u901f\u8ba1\u7b97\uff0c\u5341\u5929\u4e4b\u5185\uff0c\u4e5f\u8be5\u6709\u6d88\u606f\u4f20\u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7684\u5012\u4e5f\u5728\u60c5\u7406\u4e4b\u4e2d\uff0c\u738b\u5f64\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u7687\u4e0a\u601d\u5ff5\u4e09\u516c\u4e3b\u4e4b\u5fc3\u751a\u5207\uff0c\u4ed6\u5fcd\u53d7\u4e86\u8fd1 \u4e24\u5e74\u7684\u601d\u5ff5\uff0c\u624d\u53ec\u6211\u95ee\u8d77\u6b64\u4e8b\uff0c\u5e0c\u671b\u8fd9\u4e09\u4e2a\u6708\u7684\u5dee\u522b\u7b97\u6cd5\uff0c\u4e0d\u8981\u5f15\u8d77\u7687\u4e0a\u7684\u8bef\u4f1a\u624d\u597d\u3002\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\uff0c\u6709\u5f88\u5927\u7684\u5a01\u80c1\u6027\uff0c\u7687\u4e0a\u8bef\u4f1a\u4e86\uff0c\u90a3\u53ef\u80fd\u53c8\u5c06\u5f15\u8d77\u5927\u519b\u897f\u5f81\u7684\u5371\u9669\u3002 \u3000\u3000\u9a6c\u6587\u4e2d\u6025\u5f97\u4e00\u8138\u7126\u8651\uff0c\u9053\uff1a\u201c\u4e09\u5144\uff0c\u8fd9\u8981\u4f60\u7f8e\u8a00\u4e00\u4e8c\u4e86\uff0c\u89e6\u6012\u7687\u4e0a\uff0c\u975e\u540c\u5c0f\u53ef\uff0c\u54b1\u4eec\u4e24 \u5e74\u524d\u7684\u4e00\u756a\u5fc3\u8840\uff0c\u5c82\u4e0d\u662f\u767d\u8d39\u4e86\u3002\u201d \u3000\u3000\u738b\u5f64\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8fd9\u4e24\u5e74\u6765\uff0c\u98ce\u8c03\u96e8\u987a\uff0c\u56fd\u6cf0\u6c11\u5b89\uff0c\u5929\u671d\u94f6\u4e24\u5145\u8db3\uff0c\u5175\u620e\u518d\u8d77\uff0c\u53ea\u6015 \u4e0d\u662f\u4f60\u6211\u5144\u5f1f\u7684\u529b\u91cf\uff0c\u80fd\u591f\u7f62\u606f\u5e72\u6208\u4e86\u201d\u662f\u662f\u662f\u2026\u2026\u201c\u9a6c\u6587\u4e2d\u9053\uff1a\u201d\u5e72\u4e07\u4e0d\u53ef\u518d\u8d77\u5f81\u4f10\uff0c\u738b \u5144\u52a1\u8bf7\u5c3d\u529b\u65a1\u65cb\uff0c\u597d\u5728\u5341\u5929\u4e4b\u5185\uff0c\u5b9a\u6709\u786e\u5b9e\u7684\u56de\u97f3\uff0c\u4e09\u5144\u4ea6\u53ef\u4e0a\u8986\u5723\u610f\u4e86\u3002\u201c \u3000\u3000\u201c\u5144\u5f1f\u5c3d\u529b\u800c\u4e3a\uff0c\u7687\u4e0a\u5723\u660e\uff0c\u8fd8\u53ef\u591a\u5fcd\u4e09\u4e2a\u6708\u7684\u601d\u5ff5\u4e4b\u82e6\uff0c\u4f46\u662f\u62c5\u5fc3\u7684\u662f\u2026\u2026\u201d\u738b\u5f64\u6101 \u9501\u53cc\u7709\u9053\uff1a\u201c\u5ef6\u957f\u7684\u4e09\u6708\u671f\u6ee1\uff0c\u4e09\u516c\u4e3b\u4ecd\u7136\u672a\u56de\u5929\u671d\uff0c\u5144\u5f1f\u5982\u4f55\u80fd\u62c5\u5f53\u5f97\u8d77\u3002\u201d \u3000\u3000\u201c\u738b\u5144\u8bf4\u5f97\u6709\u7406\uff0c\u597d\u5728\u53ea\u6709\u5341\u5929\uff0c\u53ea\u8981\u8d75\u4e8c\u5824\u4f20\u56de\u6765\u786e\u5b9a\u6d88\u606f\uff0c\u4e09\u5144\u518d\u4e0a\u590d\u5723\u8c15\u4e5f\u4e0d \u8fdf\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u6d88\u606f\u4e0d\u597d\u5462\uff1f\u201d\u738b\u5f64\u7d27\u76ef\u7740\u95ee\u5382\u4e00\u53e5\u3002 \u3000\u3000\u9a6c\u6587\u4e2d\u8138\u4e0a\u7684\u795e\u60c5\u8083\u7136\u7684\u9053\uff1a\u201c\u771f\u6709\u53d8\u5316\uff0c\u6587\u4e2d\u5b9a\u7136\u4f1a\u5411\u738b\u5144\u4ea4\u4ee3\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u6211\u5c31\u627f\u62c5\u4e09\u4e2a\u6708\u7684\u65f6\u95f4\u5dee\u5f02\uff0c\u4f46\u5c4a\u65f6\u6709\u53d8\uff0c\u9a6c\u5144\u51c6\u5907\u5982\u4f55\u5411\u738b\u67d0\u4ea4\u4ee3\uff1f\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u82e6\u6da9\u7684\u9053\uff1a\u201c\u6211\u8ddf\u4f60\u540c\u8d74\u897f\u57df\uff0c\u5f53\u7136\u8981\u9009\u51fa\u7cbe\u9510\u76f8\u4ece\uff0c\u4ed6\u5982\u679c\u53d8\u8138\u52a8\u624b\uff0c\u5144\u5f1f\u53ea \u80fd\u4fdd\u8bc1\uff0c\u6211\u4e00\u5b9a\u6218\u6b7b\u5728\u738b\u5144\u4e4b\u524d\u3002\u201d \u3000\u3000\u201c\u4e00\u8a00\u4e3a\u5b9a\u2026\u2026\u201d\u738b\u5f64\u9053\uff1a\u201c\u5144\u5f1f\u5c31\u5728\u5f00\u5c01\u606d\u5019\u5341\u65e5\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u5401\u4e00\u53e3\u6c14\uff0c\u7b11\u9053\uff1a\u201c\u738b\u5144\uff0c\u5144\u5f1f\u5df2\u4ea4\u5f85\u53a8\u4e0b\uff0c\u51c6\u5907\u4e86\u4e09\u5144\u559c\u6b22\u7684\u51e0\u9053\u5c0f\u83dc\u2026\u2026\u201d \u3000\u3000\u201c\u597d\uff01\u738b\u67d0\u53e8\u6270\u3002\u201d \u3000\u3000\u738b\u5f64\u8d70\u4e00\u8d9f\u76f8\u56fd\u5bfa\uff0c\u4f46\u89c1\u666f\u7269\u4f9d\u65e7\uff0c\u4eba\u4e8b\u5168\u975e\uff0c\u539f\u4e3b\u8bd7\u5fc3\u5370\u5927\u5e08\u679c\u7136\u5df2\u4e8e\u524d\u5e74\u8f9e\u53bb\u65b9\u6587 \u4e4b\u804c\uff0c\u4e00\u6756\u968f\u8eab\u5929\u6daf\u98d8\u96f6\uff0c\u4e0d\u77e5\u6240\u7ec8\u3002 \u3000\u3000\u65b0\u4efb\u65b9\u4e08\u5bd2\u6708\u7985\u5e08\uff0c\u662f\u4e00\u4e2a\u56db\u5341\u51fa\u5934\uff0c\u7cbe\u795e\u5065\u65fa\u7684\u4e2d\u5e74\u548c\u5c1a\uff0c\u5bfa\u4e2d\u4e5f\u6765\u4e86\u4e00\u6279\u65b0\u7684\u50e7 \u4fa3\uff0c\u90fd\u662f\u4e09\u5341\u5de6\u53f3\u7684\u5065\u58ee\u50e7\u4eba\u3002 \u3000\u3000\u738b\u5f64\u5fc3\u4e2d\u660e\u767d\uff0c\u90a3\u662f\u5c11\u6797\u5bfa\u4e2d\u6d3e\u6765\u7684\u7cbe\u9510\u4e4b\u5e08\uff0c\u5bd2\u6708\u7985\u5e08\u4eb2\u81ea\u63a5\u89c1\u4e86\u738b\u5f64\uff0c\u4f46\u53cc\u65b9\u5e76\u672a \u591a\u8c08\uff0c\u738b\u5f64\u732e\u4e0a\u4e86\u5343\u4e24\u94f6\u5b50\u7684\u9999\u6cb9\u94b1\uff0c\u7acb\u523b\u8f9e\u51fa\u3002 \u3000\u3000\u4ed6\u4e0d\u8ba4\u8bc6\u5bd2\u6708\uff0c\u5bd2\u6708\u4e5f\u4e0d\u77e5\u9053\u738b\u5f64\u7684\u8eab\u4efd\uff0c\u4ed6\u4eb2\u81ea\u8fce\u89c1\u738b\u5f64\uff0c\u662f\u56e0\u4e3a\u738b\u5f64\u51fa\u624b\u592a\u5927\u65b9\uff0c \u5165\u5bfa\u968f\u559c\uff0c\u65bd\u820d\u51fa\u5343\u4e24\u7eb9\u94f6\uff0c\u662f\u5341\u5206\u5c11\u89c1\u7684\u8c6a\u5ba2\u3002 \u3000\u3000\u738b\u5f64\u884c\u52a8\u8c28\u614e\uff0c\u62c5\u5fc3\u9a6c\u6587\u4e2d\u5728\u5bfa\u5185\u6d3e\u6709\u6697\u63a2\uff0c\u5306\u5306\u6765\u53bb\uff0c\u8fde\u676f\u8336\u4e5f\u672a\u559d\u3002 \u3000\u3000\u4f46\u51fa\u4e86\u76f8\u56fd\u5bfa\uff0c\u738b\u5f64\u7acb\u523b\u53d1\u73b0\u4e86\u71d5\u98de\u7559\u4e0b\u7684\u6697\u8bb0\u3002 \u3000\u3000\u90a3\u662f\u4ed6\u548c\u71d5\u98de\u7684\u7ea6\u5b9a\uff0c\u8fde\u8d75\u4fdd\u3001\u9648\u5b8f\u4e5f\u4e0d\u660e\u5185\u60c5\u3002 \u3000\u3000\u738b\u5f64\u6084\u7136\u7559\u4e0b\u56de\u5e94\u7684\u8bb0\u53f7\uff0c\u5e26\u7740\u8d75\u4fdd\u3001\u9648\u5b8f\u5728\u5bfa\u5916\u901b\u4e86\u4e00\u9635\uff0c\u8fd4\u56de\u5de1\u629a\u5e9c\u4e2d\u3002 \u3000\u3000\u4ed6\u8fd9\u6b21\u5357\u4e0b\u5f00\u5c01\uff0c\u5c31\u7559\u5bbf\u5de1\u629a\u5e9c\u7684\u5ba2\u623f\u4e4b\u4e2d\u3002 \u3000\u3000\u4e00\u5219\u662f\u9a6c\u5de1\u629a\u8bda\u610f\u76f8\u7559\uff1b\u4e8c\u5219\u662f\u738b\u5f64\u4e5f\u5e0c\u671b\u4e86\u89e3\u4e00\u4e0b\u5de1\u629a\u5e9c\u4e2d\u7684\u60c5\u5f62\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u6b66\u6797\u4e2d\u58f0\u540d\u55a7\u8d6b\u7684\u59ec\u91cd\u5929\uff0c\u4e0d\u4f46\u4ea4\u51fa\u4e86\u4e00\u672c\u968f\u8eab\u643a\u5e26\u7684\u6b66\u6797\u5947\u4e66\uff0c\u800c\u4e14\uff0c\u4eba\u4e5f\u88ab \u9a6c\u6587\u4e2d\u6536\u7f57\u624b\u4e0b\u3002\u540d\u52a8\u6c5f\u6e56\u7684\u8f6c\u9f99\u624b\u5f20\u4e0d\u7a7a\uff0c\u4e5f\u88ab\u9a6c\u6587\u4e2d\u6240\u7f57\u81f4\u3002\u8fd9\u4e9b\u4eba\u90fd\u662f\u6c5f\u6e56\u4e0a\u53eb\u4eba\u5934 \u75bc\u7684\u4eba\u7269\uff0c\u4e0d\u77e5\u9a6c\u6587\u4e2d\u5982\u4f55\u5b89\u6392\u4ed6\u4eec\uff0c\u4e24\u5e74\u591a\u6765\uff0c\u4ece\u672a\u5728\u6c5f\u6e56\u4e2d\u51fa\u73b0\u8fc7\u3002 \u3000\u3000\u738b\u5f64\u60f3\u5728\u6697\u4e2d\u5bdf\u770b\u4e00\u4e0b\uff0c\u8fd9\u4e9b\u4eba\u662f\u5426\u90fd\u7559\u5728\u5de1\u629a\u5e9c\u4e2d\u3002 \u3000\u3000\u4f46\u7ecf\u8fc7\u4e86\u4e94\u5929\u7684\u6697\u4e2d\u89c2\u5bdf\uff0c\u8fd9\u4e9b\u4eba\u4ecd\u662f\u884c\u8e2a\u4e0d\u89c1\u3002 \u3000\u3000\u738b\u5f64\u5f3a\u81ea\u5fcd\u4e0b\uff0c\u6ca1\u6709\u63d0\u8fc7\u4e24\u4eba\uff0c\u4e5f\u6ca1\u6709\u95ee\u8d77\u9a6c\u6587\u4e2d\uff0c\u8bad\u7ec3\u7684\u4e94\u767e\u540d\u4f8d\u536b\u9ad8\u624b\uff0c\u5b89\u7f6e\u5728\u4ec0 \u4e48\u5730\u65b9\uff1f \u3000\u3000\u9a6c\u6587\u4e2d\u6df1\u85cf\u4e0d\u9732\uff0c\u8fde\u4e00\u6279\u624b\u4e0b\u4e5f\u90fd\u9690\u85cf\u7684\u5f62\u8ff9\u4e0d\u660e\u3002 \u3000\u3000\u4f46\u6b64\u523b\uff0c\u4e09\u516c\u4e3b\u56de\u6765\u7684\u4e8b\u60c5\uff0c\u6700\u4e3a\u91cd\u8981\uff0c\u5176\u4ed6\u7684\uff0c\u90fd\u53ea\u597d\u6162\u6162\u518d\u8bf4\u3002 \u3000\u3000\u7136\u800c\u3002 \u3000\u3000\u738b\u5f64\u4e5f\u4e0d\u662f\u7b80\u5355\u4eba\u7269\uff0c\u5728\u8fd9\u4e24\u5e74\u4e2d\u6697\u91cc\u6d3e\u51fa\u4e86\u5185\u5bab\u4f8d\u536b\uff0c\u5728\u6c5f\u6e56\u4e0a\u79d8\u5bc6\u67e5\u8bbf\uff0c\u7adf\u7136\u627e\u4e0d \u5230\u9b54\u6559\u4eba\u7269\u7684\u6d3b\u52a8\u8ff9\u8c61\u3002 \u3000\u3000\u4e24\u5e74\u6765\uff0c\u6c5f\u6e56\u4e0a\u51fa\u5947\u7684\u5e73\u9759\u3002 \u3000\u3000\u738b\u5f64\u5f88\u5e0c\u671b\u65e9\u4e9b\u89c1\u5230\u6c5f\u5343\u91cc\u3002 \u3000\u3000\u4ed6\u77e5\u9053\uff0c\u6c5f\u5343\u91cc\u4e0d\u4f46\u6781\u64c5\u8ffd\u8e2a\u8e29\u8ff9\u4e4b\u672f\uff0c\u800c\u4e14\uff0c\u719f\u77e5\u6c5f\u6e56\u4e2d\u5404\u79cd\u9690\u5bc6\uff0c\u6d88\u606f\u4e4b\u7075\u901a\uff0c\u4e16 \u65e0\u5176\u53f3\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u5ff5\u5934\u8f6c\u52a8\uff0c\u8f97\u8f6c\u5e8a\u7b2c\uff0c\u7adf\u96be\u6210\u7720\uff0c\u76f4\u5230\u5929\u8272\u5927\u4eae\uff0c\u624d\u6c89\u6c89\u7761\u719f\u8fc7\u53bb\uff0c\u9192\u6765\u65f6\uff0c\u5df2 \u7ecf\u662f\u65e5\u4e0a\u4e09\u7aff\uff0c\u9a6c\u6587\u4e2d\u7531\u9648\u5b8f\u3001\u8d75\u4fdd \u3000\u3000\u966a\u7740\u65e9\u5df2\u5728\u5385\u4e2d\u7b49\u5019\u3002 \u3000\u3000\u738b\u5f64\u5306\u5306\u76e5\u6d17\u4e00\u4e0b\uff0c\u6025\u6b65\u51fa\u5385\uff0c\u53f1\u8d23\u8d75\u4fdd\u3001\u9648\u5b8f\uff0c\u600e\u4e0d\u65e9\u4e9b\u4f20\u62a5\u3002 \u3000\u3000\u201c\u4e0d\u80fd\u602a\u4ed6\u4eec\u2026\u2026\u201d\u9a6c\u6587\u4e2d\u6025\u6025\u63a5\u9053\uff1a\u201c\u662f\u6211\u4e0d\u8981\u4ed6\u4eec\u60ca\u9192\u738b\u5144\u7684\u597d\u68a6\u3002\u201d \u3000\u3000\u738b\u5f64\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8bf4\u8d77\u6765\uff0c\u771f\u662f\u60ed\u6127\uff0c\u5144\u5f1f\u6628\u591c\u60f3\u8d77\u4e86\u4e09\u516c\u4e3b\u7684\u4e8b\uff0c\u7adf\u662f\u96be\u4e8e\u6210 \u7720\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u2026\u2026\u201d\u9a6c\u6587\u4e2d\u8bf4\uff1a\u201c\u5144\u5f1f\u4e5f\u6709\u4e24\u4e09\u591c\u7761\u4e0d\u5b89\u5b81\u4e86\uff0c\u6240\u4ee5\uff0c\u4e00\u65e9\u5f97\u5230\u4e86\u8baf\u606f\uff0c\u5fcd \u4e0d\u4f4f\u5c31\u6765\u89c1\u738b\u5144\u4e86\u3002\u201d \u3000\u3000\u201c\u662f\u4e0d\u662f\u8d75\u4e8c\u5824\u56de\u6765\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u6700\u8fdf\u660e\u5929\u665a\u4e0a\u624d\u5230\u2026\u2026\u201d\u9a6c\u6587\u4e2d\u9053\uff1a\u201c\u5e78\u597d\u6709\u8baf\u606f\u4f20\u6765\uff0c\u5144\u5f1f\u4e00\u76f4\u5728\u62c5\u5fc3\uff0c\u5341\u65e5\u9650\u671f \u5c06\u5c4a\uff0c\u6ca1\u6709\u6d88\u606f\uff0c\u5982\u4f55\u5411\u738b\u5144\u4ea4\u5f85\u3002\u201d \u3000\u3000\u201c\u4e09\u516c\u4e3b\u662f\u5426\u548c\u8d75\u4e8c\u5824\u4e00\u540c\u56de\u6765\u2026\u2026\u201d\u738b\u5f64\u6025\u6025\u95ee\u9053\u3002 \u3000\u3000\u201c\u597d\u50cf\u662f\u4e09\u516c\u4e3b\u7684\u51e4\u9a7e\u5df2\u8d77\u8eab\u4e1c\u8fdb\uff0c\u8be6\u7ec6\u7684\u60c5\u5f62\uff0c\u5927\u6982\u8981\u8d75\u4e8c\u5824\u56de\u6765\u540e\uff0c\u624d\u4f1a\u6e05 \u695a\u2026\u2026\u201d \u3000\u3000\u201c\u597d\uff01\u597d\uff01\u597d\u6781\u4e86\uff0c\u5144\u5f1f\u4eca\u5929\u653e\u5f00\u5fc3\u60c5\u5230\u5f00\u5c01\u9644\u8fd1\u8d70\u8d70\uff0c\u660e\u5929\u8d77\uff0c\u6253\u8d77\u7cbe\u795e\u8fce\u63a5\u4e09\u516c\u4e3b \u7684\u51e4\u9a7e\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u5fae\u5fae\u4e00\u6014\uff0c\u7b11\u9053\uff1a\u201c\u738b\u5144\uff0c\u53ef\u8981\u5144\u5f1f\u5949\u966a\u3002\u201d \u3000\u3000\u201c\u4e0d\u7528\uff0c\u4e0d\u7528\uff0c\u4f60\u662f\u4e00\u7701\u4e4b\u957f\uff0c\u4e00\u51fa\u5e9c\u8859\uff0c\u5230\u5904\u9707\u52a8\uff0c\u6211\u53ea\u60f3\u4fbf\u88c5\u8f7b\u9a91\uff0c\u770b\u770b\u5f00\u5c01\u5e9c\u540d \u80dc\u53e4\u8ff9\uff0c\u6709\u8d75\u4fdd\u3001\u9648\u5b8f\u4e24\u4e2a\u8ddf\u7740\u5c31\u591f\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u738b\u5144\u8fd9\u4e48\u8bf4\uff0c\u5144\u5f1f\u5c31\u4e0d\u5949\u966a\u4e86\uff0c\u65e9\u9910\u5df2\u5907\uff0c\u5144\u5f1f\u966a\u738b\u5144\u4e00\u8d77\u5403\u5427\uff01\u201d \u3000\u3000\u51fa\u4e86\u5f00\u5c01\u5de1\u629a\u8859\u95e8\uff0c\u738b\u5f64\u7acb\u523b\u5429\u5490\u8d75\u4fdd\u3001\u9648\u5b8f\uff0c\u7559\u5fc3\u56db\u5468\u666f\u7269\uff0c\u6ce8\u610f\u662f\u5426\u6709\u4eba\u8ddf\u8e2a\u3002 \u3000\u3000\u4e24\u4eba\u542c\u5f97\u5fae\u5fae\u4e00\u5446\uff0c\u6697\u53eb\u4e86\u4e24\u58f0\u60ed\u6127\uff0c\u738b\u5f64\u548c\u9a6c\u6587\u4e2d\u79f0\u5144\u9053\u5f1f\uff0c\u770b\u4e0a\u53bb\u76f8\u4ea4\u751a\u6df1\uff0c\u4f46\u9aa8 \u5b50\u91cc\u7adf\u4ecd\u7136\u662f\u5f7c\u6b64\u63d0\u9632\uff0c\u5404\u6709\u5e03\u5c40\u3002 \u3000\u3000\u8d75\u4fdd\u3001\u9648\u5b8f\u4e8c\u4eba\u7acb\u523b\u632f\u8d77\u7cbe\u795e\uff0c\u8fc5\u5feb\u7684\u95ea\u5230\u4e24\u4fa7\uff0c\u4e0d\u518d\u7d27\u968f\u738b\u5f64\u8eab\u540e\uff0c\u8fdc\u8fdc\u76f8\u968f\uff0c\u76ee\u5149 \u5374\u5206\u522b\u626b\u89c6\u53ef\u7591\u7684\u4eba\u4eba\u7269\u7269\u3002 \u3000\u3000\u5185\u5bab\u4e2d\u7684\u4f8d\u536b\uff0c\u90fd\u53d7\u8fc7\u76ef\u68a2\u548c\u9632\u4eba\u8ffd\u8e2a\u7684\u8bad\u7ec3\uff0c\u6240\u4ee5\uff0c\u8d75\u4fdd\u3001\u9648\u5b8f\u884c\u52a8\u8d77\u6765\uff0c\u5012\u4e5f\u9a7e\u8f7b \u5c31\u719f\u3002 \u3000\u3000\u4e24\u4eba\u8fd9\u4e00\u7559\u5fc3\u89c2\u5bdf\uff0c\u679c\u7136\u53d1\u89c9\u4e86\u4e00\u4e2a\u53ef\u7591\u7684\u4eba\u7269\uff0c\u6697\u6697\u8ffd\u8e2a\u800c\u6765\u3002 \u3000\u3000\u9648\u5b8f\u51b7\u7b11\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u770b\u6765\u7edf\u9886\u5c31\u662f\u7edf\u9886\uff0c\u6bd4\u6211\u4eec\u9ad8\u660e\u591a\u4e86\uff0c\u7adf\u7136\u9884\u77e5\u9a6c\u5de1\u629a\u6d3e\u4e86\u76ef \u68a2\u7684\u4eba\u3002\u201d \u3000\u3000\u8d75\u4fdd\u6253\u51fa\u624b\u52bf\uff0c\u8981\u9648\u5b8f\u53bb\u544a\u8bc9\u7edf\u9886\uff0c\u5df2\u53d1\u89c9\u4e86\u6709\u4eba\u76ef\u68a2\uff0c\u4e00\u9762\u7ffb\u8eab\u8fce\u5411\u6765\u4eba\uff0c\u6765\u63a9\u62a4\u9648 \u5b8f\u7684\u884c\u52a8\u3002 \u3000\u3000\u4f46\u89c1\u6765\u4eba\u52a0\u5feb\u4e86\u811a\u6b65\uff0c\u76f4\u884c\u8fc7\u6765\u3002 \u3000\u3000\u8d75\u4fdd\u6697\u4e2d\u9a82\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u6697\u91cc\u4e0d\u884c\uff0c\u51c6\u5907\u660e\u5e72\u4e86\uff0c\u51dd\u795e\u6212\u5907\u3002\u201d \u3000\u3000\u4f46\u95fb\u4e00\u4e2a\u4f4e\u5fae\u7684\u58f0\u97f3\uff0c\u4f20\u5165\u8033\u9645\uff0c\u9053\uff1a\u201c\u8d75\u8001\u5f1f\uff0c\u6211\u662f\u71d5\u98de\uff0c\u8bf7\u544a\u8bc9\u5934\u513f\uff0c\u4e2d\u5348\u65f6\u5206\uff0c \u53c8\u4e00\u6751\u4e09\u53f7\u5ba2\u623f\u4e2d\u89c1\uff0c\u8bf7\u4f60\u4eec\u5403\u9ec4\u6cb3\u9cd7\u9c7c\u3002\u201d \u3000\u3000\u8d75\u4fdd\u5fae\u5fae\u4e00\u6014\uff0c\u71d5\u98de\u5df2\u8f6c\u8eab\u7ed5\u5165\u53e6\u4e00\u6761\u8857\u9053\u3002 \u3000\u3000\u53c8\u4e00\u6751\uff0c\u662f\u5f00\u5c01\u5730\u9762\u4e0a\u6700\u6709\u540d\u7684\u5927\u996d\u5e84\uff0c\u7ea2\u70e7\u9ec4\u6cb3\u9cd7\u9c7c\uff0c\u8fdc\u8fd1\u9a70\u540d\u3002 \u3000\u3000\u4e09\u53f7\u5ba2\u623f\uff0c\u662f\u4e00\u5ea7\u5c0f\u5de7\u7684\u8de8\u9662\uff0c\u6574\u5ea7\u7684\u9662\u843d\u4e2d\uff1b\u53ea\u6709\u4e00\u95f4\u5bb4\u5ba2\u7684\u623f\u95f4\uff0c\u6709\u5f88\u597d\u7684\u79c1\u5bc6 \u6027\uff0c\u4f46\u4e5f\u662f\u53c8\u4e00\u6751\u4e2d\u6700\u8d35\u7684\u4e00\u5904\u5bb4\u5ba2\u5385\u5802\u3002 \u3000\u3000\u738b\u5f64\u5728\u57ce\u4e2d\u7ed5\u4e86\u4e00\u4e2a\u5927\u5708\u5b50\uff0c\u8fd1\u4e2d\u5348\u65f6\uff0c\u624d\u8e0f\u5165\u53c8\u4e00\u6751\u4e09\u53f7\u5ba2\u623f\u3002 \u3000\u3000\u5385\u5802\u4e2d\u7684\u7279\u53f7\u5927\u5706\u684c\u4e0a\uff0c\u65e9\u5df2\u5750\u4e86\u4e00\u4f4d\u987b\u53d1\u82cd\u7136\u7684\u8001\u8005\u3002 \u3000\u3000\u738b\u5f64\u51dd\u6ce8\u8001\u8005\uff0c\u7adf\u662f\u770b\u4e0d\u51fa\u4e00\u70b9\u7834\u7efd\uff0c\u5982\u662f\u5728\u8def\u4e0a\u76f8\u9047\uff0c\u7edd\u5bf9\u770b\u4e0d\u51fa\u4ed6\u7684\u8eab\u4efd\uff0c\u5fcd\u4e0d\u4f4f \u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6c5f\u5144\u7684\u6613\u5ba2\u624b\u6cd5\uff0c\u679c\u7136\u662f\u6c5f\u6e56\u4e00\u7edd\u2026\u2026\u201d\u2019\u4f46\u95fb\u90a3\u8001\u8005\u4f4e\u58f0\u7b11\u9053\uff1a\u201c\u4e8c \u53d4\uff0c\u6211\u662f\u738b\u91cd\u5c71\uff0c\u6c5f\u524d\u8f88\u8bf4\u6211\u6700\u6613\u9732\u51fa\u9a6c\u811a\uff0c\u6240\u4ee5\uff0c\u628a\u6211\u5316\u88c5\u6210\u4e00\u4e2a\u8001\u4eba\u3002\u201d \u3000\u3000\u738b\u5f64\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u6765\u4e86\uff0c\u9ed1\u7f57\u6c49\u5462\uff1f\u201d \u3000\u3000\u201c\u5c0f\u548c\u5c1a\u5728\u8fd9\u91cc\u2026\u2026\u201d\u4eba\u5f71\u95ea\u52a8\uff0c\u5c4b\u9876\u6a2a\u6881\u4e0a\u98de\u843d\u4e0b\u4e03\u5de7\u50e7\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u4e24\u5e74\u8ddf\u7740\u6c5f\u524d \u8f88\uff0c\u786e\u662f\u5b66\u4e86\u4e0d\u5c11\u4e1c\u897f\u3002\u201d \u3000\u3000\u738b\u5f64\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6c5f\u5343\u91cc\u5462\uff1f\u201d \u3000\u3000\u201c\u5144\u5f1f\u5728\u6b64\u2026\u2026\u201d\u4e00\u4e2a\u5f62\u5982\u996d\u5e84\u4f19\u8ba1\u7684\u7070\u8863\u4eba\uff0c\u5feb\u6b65\u884c\u5165\u5ba4\u4e2d\uff0c\u7b11\u9053\uff1a\u201c\u9a6c\u6587\u4e2d\u89c6\u6211\u5982 \u8089\u4e2d\u4e4b\u9489\uff0c\u5fc5\u6b32\u6740\u4e4b\u800c\u540e\u5feb\uff0c\u6240\u4ee5\u5144\u5f1f\u7684\u884c\u52a8\uff0c\u4e0d\u5f97\u4e0d\u5c0f\u5fc3\u4e00\u4e9b\u3002\u201d \u3000\u3000\u5c31\u662f\u8fd9\u4e00\u9635\u8bf4\u8bdd\u7684\u5de5\u592b\uff0c\u4ed6\u5df2\u7ecf\u628a\u4e00\u4ef6\u7070\u5e03\u5927\u8902\u7ed9\u7ffb\u8f6c\u8fc7\u6765\uff0c\u7adf\u6210\u4e00\u4ef6\u84dd\u7f0e\u5b50\u56e2\u82b1\u9a6c \u8902\u3002 \u3000\u3000\u597d\u5bb6\u4f19\uff0c\u8863\u670d\u7ffb\u8f6c\u8fc7\u6765\uff0c\u4eba\u7684\u6c14\u52bf\uff0c\u4e5f\u8ddf\u7740\u5927\u53d8\uff0c\u5f62\u4f3c\u5e97\u4f19\u8ba1\u7684\u4eba\uff0c\u53d8\u6210\u4e00\u4e2a\u8863\u7740\u9ad8\u8d35 \u7684\u5bcc\u5bb6\u7fc1\u3002 \u3000\u3000\u738b\u5f64\u4e0d\u80fd\u4e0d\u4f69\u670d\u4e86\uff0c\u4e00\u4ef6\u8863\u670d\uff0c\u5728\u6c5f\u5343\u91cc\u7684\u73a9\u5f04\u4e2d\uff0c\u7adf\u6709\u7740\u5982\u6b64\u5927\u7684\u4f5c\u7528\u3002 \u3000\u3000\u5f53\u7136\uff0c\u8fd9\u4ef6\u8863\u670d\uff0c\u4e5f\u7ecf\u8fc7\u4e86\u7ec6\u5fc3\u8bbe\u8ba1\uff0c\u4e00\u9762\u662f\u7070\u8272\u7c97\u5e03\uff0c\u4e00\u9762\u662f\u540d\u8d35\u7684\u56e2\u82b1\u7f0e\u5b50\u3002 \u3000\u3000\u738b\u5f64\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6c5f\u5144\u8fd9\u4efd\u795e\u51fa\u9b3c\u6ca1\u7684\u672c\u9886\uff0c\u5c31\u7b97\u9a6c\u6587\u4e2d\u5c3d\u51fa\u7cbe\u9510\u9ad8\u624b\uff0c\u4e5f\u662f\u627e\u4e0d\u51fa \u4e00\u70b9\u75d5\u8ff9\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9a6c\u6587\u4e2d\u662f\u4e2a\u5341\u5206\u9ad8\u660e\u7684\u4eba\uff0c\u4f46\u770b\u4ed6\u7528\u4eba\u7684\u624b\u6cd5\u800c\u8a00\uff0c\u5c31\u8db3\u4ee5\u53eb \u4eba\u5fc3\u670d\u4e86\u3002\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u6c5f\u5144\u662f\u6307\u2026\u2026\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u5de1\u629a\u5e9c\u7684\u603b\u6355\u5934\uff0c\u81ea\u97e9\u6d9b\u968f\u4f60\u5317\u4e0a\u4e4b\u540e\uff0c\u7adf\u7531\u8f6c\u9f99\u624b\u5f20\u4e0d\u7a7a\u53d6\u800c\u4ee3\u4e4b\uff0c\u4ed6 \u662f\u8d3c\u4e2d\u4e4b\u8d3c\uff0c\u7531\u4ed6\u4e3b\u6301\uff0c\u6349\u62ff\u5bb5\u5c0f\uff0c\u771f\u662f\u624b\u5230\u64d2\u6765\uff0c\u522b\u5904\u6548\u7528\u5982\u4f55\u4e0d\u53bb\u8bf4\u5b83\uff0c\u6c57\u5c01\u5e9c\u5730\u9762 \u4e0a\uff0c\u5374\u5df2\u7ecf\u5bb5\u5c0f\u7edd\u8ff9\uff0c\u5509\uff01\u591c\u4e0d\u95ed\u6237\uff0c\u8def\u4e0d\u62fe\u9057\u7684\u5fb7\u653f\uff0c\u91cd\u73b0\u4eca\u65e5\u5f00\u5c01\uff0c\u9a6c\u6587\u4e2d\u7684\u653f\u7ee9\uff0c\u5df2 \u7ecf\u5e7f\u4e3a\u4f20\u8bf5\uff0c\u5fb7\u88ab\u8f96\u533a\u4e86\u3002\u201d \u3000\u3000\u201c\u9a6c\u6587\u4e2d\u5177\u6709\u7684\u624d\u6167\u80fd\u529b\uff0c\u5c31\u4eca\u65e5\u5c01\u7586\u5927\u540f\u800c\u8a00\uff0c\u662f\u6781\u4e3a\u7279\u6b8a\u7684\u4e00\u4e2a\u2026\u2026\u201d\u738b\u5f64\u8bf4\uff1a \u201c\u4ed6\u5982\u60f3\u4e3a\u5b98\uff0c\u4f1a\u662f\u597d\u5b98\uff0c\u62dc\u9601\u5165\u76f8\uff0c\u4e5f\u4f1a\u628a\u519b\u56fd\u5927\u4e8b\uff0c\u6cbb\u7406\u5f97\u6709\u6761\u4e0d\u7d0a\uff0c\u53ea\u53ef\u60dc\uff0c\u4ed6\u51fa\u8eab \u897f\u57df\u9b54\u6559\uff0c\u4e0d\u4f1a\u4e00\u5fc3\u4e3a\u56fd\u4e86\u2026\u2026\uff02\u987f\u4e86\u4e00\u987f\u3002 \u3000\u3000\u738b\u5f64\u63a5\u7740\u9053\uff1a\u201c\u6c5f\u5144\uff0c\u8fd9\u4e24\u5e74\u6765\uff0c\u6c5f\u6e56\u4e4b\u4e0a\uff0c\u53ef\u6709\u9b54\u6559\u4e2d\u4eba\u6d3b\u52a8\uff1f\u201d \u3000\u3000\u201c\u6211\u5e26\u7740\u738b\u91cd\u5c71\u548c\u9ed1\u7f57\u6c49\uff0c\u7531\u6c5f\u5357\u5165\u5ddd\uff0c\u5728\u9633\u5173\u505c\u4e86\u534a\u6708\u4e4b\u4e45\uff0c\u7ed5\u8fc7\u7518\u5b81\u8fb9\u533a\uff0c\u5165\u4eac\u5e08 \u5728\u5173\u5916\u505c\u7559\u4e24\u4e2a\u6708\uff0c\u5165\u5173\u5357\u4e0b\u2026\u2026\u201d \u3000\u3000\u738b\u5f64\u6253\u65ad\u4e86\u6c5f\u5343\u91cc\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u7ecf\u8fc7\u4eac\u5e08\u4e86\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u90a3\u662f\u4e5d\u4e2a\u6708\u524d\u7684\u4e8b\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u4e0d\u89c1\u6211\u4e00\u9762\u2026\u2026\u201d\u738b\u5f64\u6709\u4e9b\u6012\u610f\u7684\u8bf4\u9053\uff1a\u201c\u6c5f\u5144\uff0c\u5168\u4e0d\u662f\u628a\u6211\u5f53\u4e2a\u670b\u53cb\u770b \u4e86\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u6211\u5728\u89c2\u5bdf\u6c5f\u6e56\u4e2d\u7684\u5f62\u52bf\uff0c\u884c\u52a8\u4ee5\u9690\u5bc6\u4e3a\u4e3b\uff0c\u5982\u679c\u5728\u4eac\u5e08\u4e2d\u62dc\u4f1a\u4f60\u738b\u7edf\u9886\uff0c \u90a3\u5c82\u4e0d\u662f\u628a\u6211\u4eec\u7684\u884c\u52a8\uff0c\u662d\u544a\u5929\u4e0b\u4e48\uff1f\u201d \u3000\u3000\u738b\u5f64\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u5b98\u8eab\u4e0d\u81ea\u7531\uff0c\u8fd9\u4e5f\u7f62\u4e86\uff0c\u4e0d\u77e5\u4f60\u4eec\u53d1\u89c9\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e00\u8d9f\u8fe2\u8fe2\u4e07\u91cc\u7684\u884c\u7a0b\uff0c\u7ed5\u8fc7\u4e86\u5927\u534a\u4e2a\u4e2d\u56fd\u7586\u571f\uff0c\u7adf\u7136\u6ca1\u6709\u53d1\u89c9\u4ec0\u4e48\u53ef\u7591\u7684\u4e8b\u2026\u2026\u201d \u3000\u3000\u987f\u4e86\u4e00\u987f\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u53c8\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u6211\u5fc3\u4e2d\u4e00\u76f4\u6709\u4e9b\u6000\u7591\uff1f\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u71d5\u98de\u5927\u6b65\u884c\u4e86\u8fc7\u6765\uff0c\u8d75\u4fdd\u3001\u9648\u5b8f\u3002\u7d27\u968f\u8eab\u540e\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5927\u5bb6\u8bf7\u5750\uff0c\u5c0f\u548c\u5c1a\u5429\u5490\u4ed6\u4eec\u4e0a\u83dc\u3002\u201d \u3000\u3000\u738b\u5f64\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u6000\u7591\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u9b54\u6559\u4e2d\u4eba\uff0c\u5e94\u8be5\u6709\u6240\u884c\u52a8\u7684\uff0c\u4e3a\u4ec0\u4e48\u7adf\u7136\u6c89\u5bc2\u65e0\u58f0\u2026\u2026\u201d\u6c5f\u5343\u91cc\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e5f \u597d\uff0c\u5e0c\u671b\u4ed6\u4eec\u80fd\u591a\u7b49\u4e00\u5e74\u3002\u201d \u3000\u3000\u71d5\u98de\u63a5\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u90a3\u65f6\u5c0f\u71d5\u5b50\u5c31\u5341\u516b\u5c81\u4e86\uff0c\u5165\u5e08\u95e8\u4e09\u5e74\uff0c\u4e5f\u53ef\u4ee5\u51fa\u5e08\u4e86\u3002\u201d \u3000\u3000\u71d5\u98de\u5fc3\u4e2d\u5927\u559c\uff0c\u5fcd\u4e2a\u4e00\u5e74\uff0c\u5c31\u53ef\u4ee5\u770b\u5230\u513f\u5b50\u4e86\u3002 \u3000\u3000\u71d5\u592b\u4eba\u4e5f\u65e9\u968f\u71d5\u98de\u5165\u4eac\u57ce\uff0c\u8fd9\u4e24\u5e74\u6765\uff0c\u71d5\u98de\u592b\u5987\u6700\u591a\u7684\u8bdd\u9898\uff0c\u5c31\u662f\u5c0f\u71d5\u5b50\uff0c\u5c24\u5176\u71d5\u592b \u4eba\uff0c\u601d\u513f\u5fc3\u5207\uff0c\u6bcf\u5929\u90fd\u54ed\u5b83\u4e00\u573a\uff0c\u4e00\u76f4\u5728\u6697\u4e2d\u62b1\u6028\uff0c\u4e0d\u8be5\u628a\u513f\u5b50\u4ea4\u7ed9\u6c5f\u5343\u91cc\u5e26\u8d70\u3002 \u3000\u3000\u4f46\u9664\u4e86\u5728\u5bb6\u91cc\u4e4b\u5916\uff0c\u71d5\u98de\u7edd\u53e3\u4e0d\u8c08\u5c0f\u71d5\u5b50\u7684\u4e8b\uff0c\u8fde\u738b\u5f64\u4e5f\u975e\u5e38\u4f69\u670d\u71d5\u98de\u90a3\u80a1\u5fcd\u60c5\u8010\u6027\u7684 \u7a33\u5065\u7cbe\u795e\u3002 \u3000\u3000\u201c\u4e09\u5e74\uff0c\u4e09\u5e74\u7684\u65f6\u95f4\uff0c\u4e0d\u7b97\u957f\uff0c\u5c0f\u71d5\u5b50\u53c8\u80fd\u5b66\u5230\u4ec0\u4e48\u4e1c\u897f\u5462\uff1f\u201d \u3000\u3000\u738b\u5f64\u8bf4\u51fa\u4e86\u5fc3\u4e2d\u7684\u60f3\u6cd5\u3002 \u3000\u3000\u8fd9\u4e5f\u662f\u71d5\u98de\u5fc3\u4e2d\u7684\u7591\u8651\uff0c\u6068\u4e0d\u5f97\u7acb\u523b\u770b\u5230\u4ed6\uff0c\u4f46\u4e5f\u5e0c\u671b\u513f\u5b50\u6709\u771f\u6b63\u7684\u6210\u5c31\uff0c\u6210\u4e3a\u6b66\u6797\u4e2d \u7b2c\u4e00\u6d41\u7684\u9ad8\u624b\u3002 \u3000\u3000\u201c\u5e08\u7236\u4e0d\u540c\uff0c\u6559\u6cd5\u4e0d\u540c\u2026\u2026\u201d\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u5c0f\u71d5\u5b50\u5929\u751f\u6709\u6700\u597d\u7684\u7ec3\u6b66\u7980\u8d4b\uff0c\u4e5f\u9047\u4e0a\u4e86\u5929 \u4e0b\u6700\u597d\u7684\u5e08\u7236\uff0c\u518d\u8bf4\uff0c\u5c0f\u71d5\u5b50\u62dc\u5165\u5e08\u95e8\u65f6\uff0c\u5df2\u7ecf\u6709\u4e86\u76f8\u5f53\u7684\u6b66\u529f\u57fa\u7840\u2026\u2026\u201d \u3000\u3000\u201c\u5c0f\u71d5\u5b50\u4e5f\u8bb8\u806a\u6167\u7edd\u4e16\uff0c\u4e09\u5e74\u4e2d\u53ef\u80fd\u5b66\u4f1a\u4e86\u5f88\u591a\u5947\u5965\u7684\u62db\u672f\uff0c\u4f46\u5185\u529f\u7684\u8fdb\u5883\uff0c\u5374\u8981\u8f83\u957f \u7684\u65f6\u95f4\u624d\u884c\u554a\uff01\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u6211\u8bf4\u4ed6\u662f\u6700\u597d\u7684\u5e08\u7236\uff0c\u5305\u62ec\u4e86\u4ed6\u5177\u6709\u7684\u7cbe\u5947\u7684\u533b\u5b66\u77e5\u8bc6\uff0c\u738b\u5144\uff0c\u76f8\u4fe1\u5144\u5f1f \u7684\u8bdd\uff0c\u5c0f\u71d5\u5b50\u7684\u5e08\u7236\u662f\u4e00\u4f4d\u5b66\u7a76\u5929\u4eba\u7684\u9ad8\u4eba\uff0c\u4ed6\u7684\u624d\u6167\u80fd\u8010\uff0c\u4e0d\u662f\u6211\u4eec\u80fd\u60f3\u5f97\u5230\u7684\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u8f6c\u6ce8\u5230\u71d5\u98de\u7684\u8138\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u71d5\u8001\u5f1f\uff0c\u5c0f\u71d5\u5b50\u7684\u5e08\u7236\uff0c\u66ff\u4ed6\u53d6\u4e86\u4e00\u4e2a\u540d\u5b57\uff0c\u53eb\u4f5c\u71d5 \u6625\u98ce\u3002\u201d \u3000\u3000\u201c\u8fd9\u540d\u5b57\u5f88\u597d\uff0c\u71d5\u6625\u98ce\u2026\u2026\u71d5\u6625\u98ce\u2026\u2026\u201d\u71d5\u98de\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u6625\u98ce\u71d5\u5b50\u98de\uff0c\u71d5\u5b50\u5e26\u6765\u6625 \u98ce\u3002\u201d \u3000\u3000\u201c\u5c31\u662f\u8fd9\u4e2a\u610f\u601d\u2026\u2026\u201d\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u4ed6\u5e08\u7236\u6015\u4ed6\u6740\u5b7d\u592a\u91cd\uff0c\u53d6\u540d\u6625\u98ce\uff0c\u5e0c\u671b\u4ed6\u80fd\u5982\u6625\u98ce \u8fc7\u5883\uff0c\u4f7f\u767e\u82b1\u76db\u653e\u3002\u201d \u3000\u3000\u738b\u5f64\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6c5f\u5144\uff0c\u6211\u8f6c\u5f2f\u62b9\u89d2\uff0c\u82b1\u5b50\u534a\u5929\u5de5\u592b\uff0c\u4f60\u5374\u662f\u53e3\u98ce\u5947\u7d27\uff0c\u8bf4\u4e0d\u5f97\u6211 \u5c31\u5355\u5200\u76f4\u5165\u4e86\uff0c\u90a3\u4eba\u7a76\u7adf\u662f\u8c01\u554a\uff1f\u201d \u3000\u3000\u201c\u4e09\u5144\uff0c\u6211\u7b54\u5e94\u8fc7\u4ed6\uff0c\u4e0d\u6cc4\u6f0f\u4ed6\u7684\u540d\u5b57\uff0c\u4ee5\u540e\uff0c\u4e5f\u8bb8\u5c0f\u71d5\u5b50\u4f1a\u544a\u8bc9\u4f60\uff01\u201d \u3000\u3000\u738b\u5f64\u7565\u4e00\u6c89\u541f\uff0c\u9053\uff1a\u201c\u4e0d\u8bf4\u4e5f\u7f62\uff0c\u5144\u5f1f\u53e6\u6709\u8981\u4e8b\uff0c\u5411\u6c5f\u5144\u8bf7\u6559\uff1f\u201d \u3000\u3000\u201c\u9152\u83dc\u4e0a\u6765\u4e86\u2026\u2026\u201d\u6c5f\u5343\u91cc\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u5403\u8fc7\u518d\u8c08\u5427\uff01\u201d \u3000\u3000\u5e97\u4f19\u8ba1\u9001\u4e0a\u9152\u83dc\uff0c\u51e0\u4eba\u5306\u5306\u7528\u8fc7\uff0c\u6c5f\u5343\u91cc\u6325\u9000\u5e97\u4f19\u8ba1\uff0c\u9053\uff1a\u201c\u53ef\u662f\u6709\u5173\u4e09\u516c\u4e3b\u7684\u4e8b\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\u2026\u2026\u201d \u3000\u3000\u738b\u5f64\u53c8\u9053\uff1a\u201c\u9a6c\u6587\u4e2d\u4e24\u4e2a\u591a\u6708\u524d\uff0c\u5df2\u6d3e\u8d75\u4e8c\u5824\u8d76\u5f80\u897f\u57df\uff0c\u4eca\u590d\u5230\u8baf\u606f\uff0c\u4e09\u516c\u4e3b\u5df2\u8d77\u8eab\u4e1c \u8fdb\uff0c\u4e0d\u8fc7\u8be6\u7ec6\u60c5\u5f62\uff0c\u8981\u660e\u665a\u4e4b\u524d\uff0c\u8d75\u4e8c\u5824\u8d76\u56de\u5f00\u5c01\uff0c\u624d\u80fd\u4e86\u89e3\u2026\u2026\u201d\u4ed4\u7ec6\u8bf4\u51fa\u4e86\u4e8b\u60c5\u7ecf\u8fc7\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u5982\u679c\u4e8b\u60c5\u771f\u4f1a\u8fd9\u4e48\u987a\u5229\uff0c\u90a3\u771f\u662f\u5929\u4e0b\u82cd\u751f\u4e4b\u5e78\uff0c\u4e5f\u8bb8\uff0c\u9a6c\u6587 \u4e2d\u4f5c\u5b98\u592a\u4e45\u4e86\uff0c\u6df1\u53d7\u4e2d\u539f\u5112\u5b66\u9676\u51b6\uff0c\u5177\u6709\u4e2d\u539f\u6c49\u4eba\u65e0\u4fe1\u4e0d\u7acb\u7684\u7cbe\u795e\u3002\u201d \u3000\u3000\u201c\u770b\u4ed6\u8bf4\u8bdd\u795e\u60c5\uff0c\u4e0d\u4f3c\u4f5c\u4f2a\uff0c\u73b0\u5728\uff0c\u738b\u67d0\u6700\u62c5\u5fc3\u7684\u662f\u4e24\u4ef6\u4e8b\u4e86\uff01\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u90a3\u4e24\u4ef6\u4e8b\uff1f\u201d \u3000\u3000\u201c\u4e00\u662f\u9b54\u6559\u80cc\u4fe1\uff0c\u4e0d\u80af\u4ea4\u8fd8\u4e09\u516c\u4e3b\u2026\u2026\u201d\u738b\u5f64\u8bf4\uff1a\u201c\u4e8c\u662f\uff0c\u4e09\u516c\u4e3b\u53d7\u5230\u4e86\u5f88\u5927\u7684\u4f24\u5bb3\uff0c \u6b64\u4e8b\u53c8\u8be5\u5982\u4f55\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u7b2c\u4e8c\u4e2a\u53ef\u80fd\u8f83\u5927\uff0c\u4e09\u516c\u4e3b\u4e0d\u4f46\u53d7\u5230\u4e86\u5f88\u5927\u7684\u4f24\u5bb3\uff0c\u800c\u4e14\uff0c\u4e5f \u611f\u67d3\u4e86\u9b54\u6027\uff0c\u653e\u5165\u7687\u5bab\uff0c\u5bf9\u7687\u4e0a\u4e5f\u6784\u6210\u4e86\u5f88\u5927\u7684\u5a01\u80c1\u3002\u201d \u3000\u3000\u738b\u5f64\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4e24\u5e74\u524d\uff0c\u4e09\u516c\u4e3b\u884c\u5f80\u897f\u57df\u65f6\uff0c\u66fe\u7ecf\u8c06\u8c06\u5631\u5490\u5728\u4e0b\uff0c\u5982\u82e5\u53d1\u89c9\u5979\u611f\u53d7 \u9b54\u6027\uff0c\u5fd8\u5374\u672c\u6765\u65f6\uff0c\u8981\u6211\u7acb\u523b\u51fa\u624b\u6740\u4e86\u5979\u2026\u2026\u201d\u4f3c\u662f\u8a00\u672a\u5c3d\u610f\uff0c\u5374\u7a81\u7136\u4f4f\u53e3\u4e0d\u8a00\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u5df2\u611f\u89c9\u5230\u738b\u5f64\u7684\u8bdd\u672a\u8bf4\u5b8c\uff0c\u4e0d\u8fc7\uff0c\u5374\u6ca1\u6709\u5f00\u53e3\u8ffd\u95ee\uff0c\u6c89\u601d\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u4f60\u80fd\u4e0b \u5f97\u4e86\u624b\u5417\uff1f\u201d \u3000\u3000\u201c\u4e0d\u80fd\uff01\u201d\u738b\u5f64\u8bf4\uff1a\u201c\u6211\u6740\u4e86\u4e09\u516c\u4e3b\uff0c\u5e72\u72af\u9f99\u989c\uff0c\u53ea\u6015\u8981\u7978\u8bdb\u4e5d\u65cf\uff0c\u6240\u4ee5\uff0c\u5144\u5f1f\u6b63\u4e3a\u8fd9 \u4e24\u4ef6\u4e8b\u53d1\u6101\uff0c\u8bf7\u6c5f\u5144\u6307\u70b9\u6307\u70b9\u3002\u201d \u3000\u3000\u201c\u6211\u60f3\uff0c\u4f60\u5df2\u7ecf\u6709\u4e86\u6253\u7b97\uff0c\u806a\u660e\u83ab\u8fc7\u5e1d\u738b\uff0c\u53ea\u6015\u4e5f\u65e9\u6709\u5723\u8c15\u9881\u4e0b\u4e86\u3002\u201d \u3000\u3000\u201c\u5509\uff01\u770b\u6765\uff0c\u4ec0\u4e48\u4e8b\u90fd\u65e0\u6cd5\u7792\u8fc7\u6c5f\u5144\u2026\u2026\u201d \u3000\u3000\u987f\u4e86\u4e00\u987f\u3002 \u3000\u3000\u738b\u5f64\u53c8\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u7687\u4e0a\u6388\u4e8e\u6211\u8c03\u52a8\u5404\u5730\u6355\u5feb\u7684\u5927\u6743\uff0c\u4e5f\u53ef\u4ee5\u7ea6\u8bf7\u6b66\u6797\u4e2d\u6240\u6709\u7684\u5e2e\u6d3e\u9063 \u4eba\u52a9\u62f3\uff0c\u4e5f\u6388\u4e0b\u4e86\u4e94\u5341\u4e07\u4e24\u94f6\u5b50\u7684\u8d39\u7528\uff0c\u4e0d\u8db3\u4e4b\u6570\uff0c\u8fd8\u53ef\u4ee5\u5411\u5730\u65b9\u5b98\u5e9c\u652f\u7528\uff0c\u5982\u662f\u9b54\u6559\u8fdd\u53cd \u4e86\u7ea6\u5b9a\uff0c\u90a3\u662f\u903c\u6211\u8c03\u52a8\u4e3e\u56fd\u7684\u5211\u6355\u9ad8\u624b\uff0c\u9080\u96c6\u6b66\u6797\u540c\u9053\uff0c\u548c\u5c0f\u897f\u5929\u96f7\u97f3\u5bfa\uff0c\u62fc\u4e2a\u751f\u6b7b\u5b58\u4ea1 \u4e86\uff0c\u5f53\u7136\uff0c\u9a6c\u6587\u4e2d\u4e5f\u5fc5\u987b\u5728\u654c\u53cb\u4e4b\u95f4\uff0c\u4f5c\u4e2a\u6289\u62e9\u2026\u2026\u201d \u3000\u3000\u6c5f\u5343\u91cc\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\u5389\u5bb3\u7684\u7687\u5e1d\uff0c\u7d27\u63a5\u7740\u5c06\u662f\u8c03\u96c6\u5927\u519b\uff0c\u8fdc\u5f81\u897f\u57df\uff0c\u6211\u770b\u9b54\u6559\u8fdd\u7ea6 \u6263\u4eba\u7684\u673a\u4f1a\u4e0d\u5927\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u4ed6\u4eec\u5df2\u8bf1\u53d1\u4e09\u516c\u4e3b\u7684\u9b54\u6027\uff0c\u5374\u5982\u7ea6\u628a\u5979\u9001\u56de\u5929\u671d\uff0c\u738b\u67d0\u8be5\u5982\u4f55\u5e94\u4ed8\u5462\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u6682\u5fcd\u4e00\u65f6\uff0c\u5168\u529b\u6212\u5907\uff0c\u4fdd\u62a4\u7687\u5e1d\u7684\u5b89\u5168\uff0c\u5f53\u7136\uff0c\u8981\u628a\u90e8\u5206\u5185\u60c5\uff0c\u59d4\u5a49\u7684\u594f \u660e\u7687\u4e0a\u3002\u201d \u3000\u3000\u201c\u4ee5\u540e\u5462\uff1f\u201d\u738b\u5f64\u9053\uff1a\u201c\u603b\u4e0d\u80fd\u4e00\u76f4\u8fd9\u6837\u7684\u6491\u4e0b\u53bb\u3002\u201d \u3000\u3000\u201c\u628a\u9a6c\u6587\u4e2d\u62d6\u4e0b\u6c34\uff0c\u8981\u4ed6\u4fdd\u4e09\u516c\u4e3b\u7684\u5b89\u5168\uff0c\u5f53\u7136\uff0c\u4e5f\u8d1f\u8d23\u76d1\u89c6\u5979\u7684\u884c\u52a8\u2026\u2026\u201d \u3000\u3000\u201c\u597d\u529e\u6cd5\u2026\u2026\u201d\u738b\u5f64\u7b11\u9053\uff1a\u201c\u6211\u60f3\u4e86\u597d\u51e0\u5929\uff0c\u5c31\u6ca1\u6709\u60f3\u5230\u5229\u7528\u9a6c\u6587\u4e2d\u8fd9\u80a1\u529b\u91cf\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u7b11\u9053\uff1a\u201c\u8fd9\u90fd\u662f\u4e00\u65f6\u7684\u6551\u6025\u529e\u6cd5\uff0c\u4f60\u7b2c\u4e8c\u6b65\u884c\u52a8\u662f\u5229\u7528\u5723\u8c15\uff0c\u8981\u6c5f\u6e56\u4e0a\u51e0\u4e2a\u5927\u95e8 \u6d3e\uff0c\u5404\u6d3e\u51fa\u82e5\u5e72\u6b66\u529f\u9ad8\u5f3a\u7684\u4eba\uff0c\u96c6\u4e2d\u5728\u4eac\u5e08\u6212\u5907\uff0c\u8fd9\u6837\u7684\u5b89\u6392\uff0c\u7ef4\u6301\u4e2a\u4e00\u5e74\u7684\u5b89\u9759\uff0c\u5927\u6982\u5e94 \u8be5\u53ef\u4ee5\u4e86\u3002\u201d \u3000\u3000\u201c\u90a3\u4e00\u5e74\u4e4b\u540e\u5462\uff1f\u201d\u738b\u5f64\u9053\uff1a\u201c\u529e\u6cd5\u867d\u597d\uff0c\u5374\u4e5f\u65e0\u6cd5\u62d6\u5ef6\u592a\u4e45\u3002\u201d \u3000\u3000\u201c\u90a3\u65f6\u5019\uff0c\u5c0f\u71d5\u5b50\u51fa\u5e08\u4e86\uff0c\u4ed6\u82e5\u662f\u5185\u5bab\u4f8d\u536b\uff0c\u5f53\u7136\u4e0d\u80fd\u5750\u89c6\u2026\u2026    \u201d\u6c5f\u5343\u91cc\u7b11\u9053\uff1a \u201c\u8ba9\u5c0f\u71d5\u5b50\u5bf9\u4ed8\u4e09\u516c\u4e3b\uff01\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u201d\u738b\u5f64\u795e\u60c5\u72b9\u8c6b\u7684\u8bf4\uff1a\u201c\u5c0f\u71d5\u5b50\u4e00\u4e2a\u4eba\u5c31\u80fd\u5bf9\u4ed8\u4e09\u516c\u4e3b\u4e48\uff1f\u201d \u3000\u3000\u201c\u4e5f\u8bb8\u4ed6\u53ef\u4ee5\u5bf9\u4ed8\u9b54\u6559\uff0c\u5f53\u7136\uff0c\u90a3\u4e0d\u662f\u4ed6\u4e00\u4e2a\u4eba\u7684\u529b\u91cf\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u5c06\u662f\u6700\u4e3b\u8981\u7684\u529b \u91cf\u2026\u2026\u201d \u3000\u3000\u6c5f\u5343\u91cc\u53c8\u9053\uff1a\u201c\u4f60\u4eec\u5927\u6982\u90fd\u5df2\u7ecf\u77e5\u9053\uff0c\u4e09\u516c\u4e3b\u6709\u4e00\u8eab\u7cbe\u7edd\u7684\u6b66\u529f\uff0c\u5c24\u5176\u662f\u5251\u672f\u4e0a\u7684\u9020 \u8be3\uff0c\u975e\u5e38\u7cbe\u6e5b\u3002\u201d \u3000\u3000\u738b\u5f64\u5446\u4e86\u4e00\u5446\uff0c\u63a5\u9053\uff1a\u201c\u6c5f\u5144\u5bf9\u4e09\u516c\u4e3b\u7684\u4e8b\uff0c\u662f\u5426\u6709\u66f4\u591a\u4e00\u4e9b\u7684\u4e86\u89e3\uff1f\u201d \u3000\u3000\u201c\u662f\u2026\u2026\u201d\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u6211\u8d39\u4e86\u4e0d\u5c11\u7684\u5de5\u592b\u3002\u6253\u542c\u51fa\u5979\u7684\u5e08\u7236\u662f\u8c01\u3002\u201d \u3000\u3000\u201c\u662f\u8c01\uff1f\u201d\u738b\u5f64\u6025\u6025\u7684\u8ffd\u95ee\u3002 \u3000\u3000\u201c\u4e00\u9053\u98de\u8679\u82df\u5f57\u6708\u2026\u2026\u201d \u3000\u3000\u6c5f\u5343\u91cc\u63a5\u7740\u9053\uff1a\u201c\u4e09\u5341\u5e74\u524d\uff0c\u5979\u5df2\u6709\u6b66\u6797\u4e2d\u7b2c\u4e00\u5feb\u5251\u4e4b\u79f0\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c31\u96be\u602a\u4e86\u2026\u2026\u201d\u738b\u5f64\u4e00\u8138\u60ca\u8bb6\u7684\u8bf4\u9053\uff1a\u201c\u4f46\u4e09\u516c\u4e3b\u5e38\u5c45\u6df1\u5bab\uff0c\u600e\u4e48\u62dc\u5728\u82df\u5973\u4fa0\u7684\u95e8 \u4e0b\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd9\u65b9\u9762\u4e48\uff1f\u6c5f\u67d0\u4eba\u8fd8\u6ca1\u6709\u6253\u542c\u6e05\u695a\u3002\u201d \u3000\u3000\u201c\u4e0e\u541b\u4e00\u5e2d\u8bdd\uff0c\u80dc\u8bfb\u5341\u5e74\u4e66\u3002\u6c5f\u5144\uff0c\u6b66\u6797\u4e2d\u6ca1\u6709\u4f60\u4e0d\u77e5\u9053\u7684\u4e8b\u201d\u60c5\u2026\u2026\u201c\u738b\u5f64\u5927\u4e3a\u6298\u670d \u7684\u8bf4\uff1a\u201d\u8fd9\u4e9b\u6b66\u6797\u79d8\u8f9b\uff0c\u4f60\u662f\u600e\u4e48\u63a2\u542c\u5230\u7684\uff1f\u201c \u3000\u3000\u201c\u6c5f\u67d0\u4eba\u7684\u670b\u53cb\u591a\uff0c\u800c\u4e14\u6211\u8fd9\u5927\u534a\u751f\u7684\u5c81\u6708\uff0c\u90fd\u5728\u6c5f\u6e56\u4e2d\u8d70\u52a8\uff0c\u6240\u95fb\u3001\u6240\u89c1\uff0c\u81ea\u975e\u5e38\u4eba \u6240\u80fd\u53ca\u4e86\uff0c\u4f60\u65e2\u5f88\u4f69\u670d\u6211\uff0c\u6c5f\u67d0\u5c31\u7d22\u6027\u518d\u5356\u5f04\u4e00\u6b21\uff0c\u738b\u5144\u53ef\u77e5\u9053\u59ec\u91cd\u5929\u4ea4\u7ed9\u9a6c\u6587\u4e2d\u7684\u4e00\u672c\u6b66 \u6797\u5947\u4e66\uff0c\u662f\u4ec0\u4e48\u5417\uff1f\u201d \u3000\u3000\u201c\u4e0d\u77e5\u9053\uff01\u201d\u738b\u5f64\u9053\uff1a\u201c\u6c5f\u5144\uff0c\u4e0d\u7528\u518d\u5356\u5173\u5b50\u4e86\uff0c\u6211\u8fd9\u91cc\u65e9\u5df2\u7ecf\u4e94\u4f53\u6295\u5730\u4e86\u3002\u201d \u3000\u3000\u201c\u5927\u7f57\u5341\u4e09\u5200\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u63a5\u7740\u9053\uff1a\u201c\u98ce\u96f7\u5200\u859b\u8001\u4eba\uff0c\u7a77\u6570\u5341\u5e74\u7cbe\u529b\uff0c\u82e6\u82e6\u5bfb\u89c5\u4e0d\u5f97\u7684\u5200\u4e2d\u7edd\u5b66\uff0c\u672a\u6599\uff0c\u7adf \u7136\u843d\u5230\u4e86\u59ec\u91cd\u5929\u7684\u624b\u4e2d\uff0c\u53ef\u60dc\u4ed6\u9152\u9189\u88ab\u64d2\uff0c\u4e0b\u5165\u5927\u7262\uff0c\u8f6c\u9f99\u624b\u5f20\u4e0d\u7a7a\uff0c\u5316\u8eab\u72f1\u5352\u7262\u5934\uff0c\u8d39\u4e86 \u4e5d\u725b\u4e8c\u864e\u4e4b\u529b\uff0c\u6ca1\u6709\u5230\u624b\uff0c\u5374\u88ab\u9a6c\u6587\u4e2d\u8f7b\u8f7b\u6613\u6613\u7684\u53d6\u5230\u624b\u4e2d\uff0c\u770b\u6765\uff0c\u5b98\u573a\u4e2d\u4eba\u7684\u667a\u8c0b\uff0c\u7edd\u975e \u6c5f\u6e56\u4e2d\u4eba\u80fd\u591f\u53ca\u5f97\u7684\u3002\u201d \u3000\u3000\u738b\u5f64\u7b80\u76f4\u542c\u5446\u4e86\uff0c\u6123\u4e86\u534a\u5929\uff0c\u624d\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u5f97\u4e86\u554a\uff01\u9a6c\u6587\u4e2d\u51fa\u8eab\u96f7\u97f3 \u5bfa\uff0c\u6b66\u529f\u7384\u5947\uff0c\u5df2\u662f\u9ad8\u4e0d\u53ef\u6d4b\uff0c\u5b66\u4f1a\u5927\u7f57\u5341\u4e09\u5200\uff0c\u5929\u4e0b\u90a3\u8fd8\u6709\u4eba\u662f\u4ed6\u654c\u624b\uff1f\u201d \u3000\u3000\u201c\u6240\u4ee5\u554a\uff01\u4f60\u771f\u8981\u5e26\u4eba\u897f\u5f81\uff0c\u5fc5\u987b\u8981\u6263\u7d27\u9a6c\u6587\u4e2d\uff0c\u81f3\u5c11\u4e5f\u8981\u4ed6\u4fdd\u6301\u4e2d\u7acb\u2026\u2026\u201d\u6c5f\u5343\u91cc\u7b11 \u4e00\u7b11\uff0c\u9053\uff1a\u201c\u81f3\u4e8e\u5927\u7f57\u5341\u4e09\u5200\uff0c\u867d\u7136\u9738\u9053\u65e0\u6bd4\uff0c\u4f46\u4e5f\u975e\u65e0\u4eba\u53ef\u654c\uff0c\u4e09\u516c\u4e3b\u7684\u5251\u6cd5\uff0c\u5e94\u8be5\u80fd\u548c \u4ed6\u4e00\u62da\u9ad8\u4e0b\u2026\u2026\u201d \u3000\u3000\u201c\u53ef\u662f\uff0c\u4e09\u516c\u4e3b\u662f\u5426\u5df2\u88ab\u8bf1\u52a8\u9b54\u6027\u2026\u2026\u201d\u738b\u5f64\u65e0\u9650\u5fe7\u8651\u7684\u9053\uff1a\u201c\u8fd8\u5c1a\u672a\u53ef\u77e5\u554a\uff01\u600e\u80fd\u501a \u4e3a\u81c2\u52a9\uff1f\u201d \u3000\u3000\u201c\u90a3\u5c31\u53ea\u597d\u5bc4\u671b\u71d5\u6625\u98ce\u4e86\u3002\u201d \u3000\u3000\u201c\u5c0f\u71d5\u5b50\uff0c\u4ed6\u53ea\u662f\u4e00\u4e2a\u5341\u516b\u5c81\u7684\u5b69\u5b50\u554a\u2026\u2026\u201d\u738b\u5f64\u9053\uff1a\u201c\u518d\u8bf4\uff0c\u4ed6\u53ea\u4ece\u5e08\u4e09\u5e74\uff0c\u80fd\u6709\u591a \u5927\u6210\u5c31\uff1f\u201d \u3000\u3000\u201c\u6211\u60f3\u4e09\u516c\u4e3b\u8ffd\u968f\u4e00\u9053\u98de\u8679\u82df\u6167\u6708\uff0c\u4e5f\u4e0d\u4f1a\u6709\u592a\u4e45\u7684\u65f6\u95f4\uff0c\u4e5f\u8bb8\u8fd8\u4e0d\u5230\u4e09\u5e74\u2026\u2026\u201d\u6c5f\u5343 \u91cc\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u738b\u5144\uff0c\u4e8b\u5df2\u81f3\u6b64\uff0c\u6025\u4ea6\u65e0\u7528\uff0c\u773c\u524d\u6700\u91cd\u8981\u7684\u4e00\u4ef6\u4e8b\uff0c\u662f\u5957\u7d27\u9a6c\u6587\u4e2d\uff0c\u4ed6\u867d \u7136\u51fa\u8eab\u96f7\u97f3\u5bfa\uff0c\u4f46\u6211\u770b\u548c\u8499\u53e4\u53ef\u6c57\uff0c\u4ea6\u6709\u7740\u6df1\u539a\u7684\u5173\u7cfb\uff0c\u7528\u5927\u519b\u5f81\u4f10\u7684\u4e00\u62db\uff0c\u5bf9\u4ed6\u538b\u529b\u5f88 \u5927\uff0c\u4e0d\u8fc7\uff0c\u4e0d\u80fd\u628a\u5f13\u5f26\u62c9\u5f97\u592a\u7d27\uff0c\u4ee5\u514d\u8feb\u4f7f\u4ed6\u751f\u51fa\u60c5\u6025\u4e00\u62fc\u7684\u5ff5\u5934\uff0c\u52a8\u4e4b\u4ee5\u60c5\uff0c\u8bc9\u4e4b\u4ee5\u7406\uff0c \u518d\u52a0\u4e0a\u4e3e\u56fd\u5927\u519b\u897f\u5f81\u7684\u5a01\u80c1\uff0c\u4e2a\u4e2d\u4e4b\u5999\uff0c\u5b58\u4e4e\u4e00\u5fc3\uff0c\u738b\u5144\uff0c\u597d\u81ea\u4e3a\u4e4b\uff0c\u6c5f\u67d0\u8981\u5148\u8d70\u4e00\u6b65 \u4e86\u3002\u201d \u3000\u3000\u738b\u5f64\u6025\u9053\uff1a\u201c\u4f60\u600e\u4e48\u80fd\u8d70\u2026\u2026\u201d \u3000\u3000\u201c\u4e00\u65e6\u9a6c\u6587\u4e2d\u4fa6\u77e5\u4e86\u6211\u4eec\u7684\u884c\u8e2a\uff0c\u5fc5\u5c06\u503e\u5168\u529b\u8ffd\u6740\u6211\uff0c\u90a3\u65f6\uff0c\u53cd\u4f7f\u738b\u5144\u4e3a\u96be\u4e86\uff0c\u4f55\u51b5\uff0c \u6211\u8fd8\u8981\u53bb\u6b66\u5f53\u5c71\u627e\u767d\u8138\u5c0f\u9053\u58eb\uff0c\u987a\u4fbf\u89c2\u5bdf\u4e00\u4e0b\u6c5f\u6e56\u4e0a\u7684\u60c5\u52bf\uff0c\u6211\u4e00\u76f4\u4e0d\u592a\u76f8\u4fe1\u9b54\u6559\u4e2d\u4eba\u4f1a\u6309 \u5175\u4e0d\u52a8\uff0c\u8001\u5b9e\u8bf4\uff0c\u738b\u5144\uff0c\u8fd9\u662f\u5728\u5e2e\u4f60\u94fa\u8def\u3002\u201d \u3000\u3000\u738b\u5f64\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u597d\u5427\uff0c\u5e0c\u671b\u4f60\u80fd\u65e9\u4e9b\u56de\u6765\uff0c\u6709\u4e9b\u4e8b\uff0c\u8fd8\u8981\u6c5f\u5144\u5e2e\u6211\u62ff\u4e2a\u4e3b\u610f\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u5fae\u5fae\u4e00\u7b11\uff0c\u8d77\u8eab\u800c\u53bb\u3002 \u3000\u3000\u738b\u91cd\u5c71\u3001\u9ed1\u7f57\u6c49\uff0c\u5bf9\u738b\u5f64\u62b1\u62f3\u4e00\u793c\u3002\u8ffd\u968f\u6c5f\u5343\u91cc\u79bb\u53bb\u3002 \u3000\u3000\u201c\u8fd9\u624d\u771f\u6b63\u662f\u884c\u8d70\u6c5f\u6e56\u2026\u2026\u201d\u671b\u7740\u6c5f\u5343\u91cc\u5df2\u8fdc\u53bb\u7684\u80cc\u5f71\uff0c\u738b\u5f64\u65e0\u9650\u611f\u6168\u7684\u8bf4\uff1a\u201c\u52ff\u602a\u91cd \u5c71\u8981\u8f9e\u53bb\u4f8d\u536b\uff0c\u8ddf\u7740\u4ed6\u6e38\u884c\u5929\u4e0b\uff0c\u4e00\u5e74\u4e4b\u95f4\uff0c\u5c31\u8d70\u4e86\u534a\u58c1\u5c71\u6cb3\u3002\u201d \u3000\u3000\u71d5\u98de\u4f4e\u58f0\u9053\uff1a\u201c\u7edf\u9886\uff0c\u6211\u662f\u8ffd\u968f\u8eab\u4fa7\uff0c\u8fd8\u662f\u7559\u5728\u5916\u9762\u63a5\u5e94\uff1f\u201d \u3000\u3000\u201c\u4f60\u4e0d\u7528\u8ddf\u6211\u8fdb\u5de1\u629a\u5e9c\u4e86\uff0c\u60f3\u6cd5\u5b50\u4fa6\u5bdf\u4e00\u4e0b\uff0c\u9a6c\u6587\u4e2d\u628a\u59ec\u91cd\u5929\u5b89\u7f6e\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u6709\u4e8b\u60c5 \u7531\u6211\u4eec\u7ea6\u5b9a\u6697\u8bb0\u8054\u7edc\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u5c5e\u4e0b\u4e5f\u5148\u8d70\u4e00\u6b65\u4e86\uff01\u201d \u3000\u3000\u71d5\u98de\u5feb\u6b65\u79bb\u53bb\uff0c\u738b\u5f64\u4e5f\u5e26\u7740\u8d75\u4fdd\u3001\u9648\u5b8f\uff0c\u56de\u8f6c\u5de1\u629a\u5e9c\u4e2d\u3002 \u5367\u9f99\u751f\u300a\u71d5\u5b50\u4f20\u5947\u300b \u7b2c\u5341\u4e00\u56de\u3000\u53c8\u89c1\u6740\u673a \u3000\u3000\u8d75\u4e8c\u5824\u4e2a\u5b50\u4e0d\u9ad8\uff0c\u5706\u8138\u5927\u8179\uff0c\u770b\u4e0a\u53bb\u4e00\u56e2\u548c\u6c14\uff0c\u8fd8\u771f\u6709\u4e00\u70b9\u5927\u5546\u4eba\u7684\u6c14\u6d3e\u3002 \u3000\u3000\u4f46\u4ed6\u7709\u5b87\u4e4b\u95f4\uff0c\u9690\u9690\u900f\u51fa\u98ce\u5c18\u4e4b\u8272\uff0c\u663e\u7136\u662f\u7ecf\u8fc7\u4e86\u957f\u9014\u8dcb\u6d89\uff0c\u75b2\u7d2f\u672a\u6d88\u3002 \u3000\u3000\u9a6c\u6587\u4e2d\u66ff\u738b\u5f64\u5f15\u89c1\u4e4b\u540e\uff0c\u8d75\u4e8c\u5824\u7acb\u523b\u62b1\u62f3\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u738b\u5927\u4eba\u4e49\u4f38\u63f4\u624b\uff0c\u963b\u6b62\u4e86\u5927\u519b\u6740 \u4f10\uff0c\u8d75\u67d0\u548c\u65cf\u4eba\u90fd\u5f88\u611f\u6fc0\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u7528\u5ba2\u6c14\u2026\u2026\u201d\u738b\u5f64\u7b11\u9053\uff1a\u201c\u6587\u4e2d\u5144\u65a1\u65cb\u6709\u65b9\uff0c\u4f7f\u4e00\u573a\u8fde\u7ef5\u591a\u5e74\u7684\u6218\u4e89\uff0c\u505c\u6b62\u4e0b\u6765\uff0c \u4e5f\u662f\u82cd\u751f\u4e4b\u798f\u4e86\u3002\u201d \u3000\u3000\u8d75\u4e8c\u5824\u9053\uff1a\u201c\u662f\u662f\u662f\uff0c\u9a6c\u5927\u4eba\u5bf9\u6211\u4eec\u7684\u7167\u987e\uff0c\u672c\u65cf\u4e2d\u4eba\u4ea6\u5f88\u611f\u6fc0\u3002\u201d \u3000\u3000\u738b\u5f64\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u542c\u4ed6\u53e3\u6c14\uff0c\u9a6c\u6587\u4e2d\u4e0d\u50cf\u8499\u53e4\u65cf\u4eba\uff0c\u96be\u9053\u6c5f\u5343\u91cc\u7684\u63a8\u60f3\u9519\u4e86\u3002 \u3000\u3000\u4f46\u95fb\u9a6c\u6587\u4e2d\u8bf4\u9053\uff1a\u201c\u4e8c\u5824\uff0c\u738b\u5927\u4eba\u6700\u5173\u5fc3\u662f\u4e09\u516c\u4e3b\u7684\u6d88\u606f\uff0c\u5148\u628a\u5185\u60c5\u8bf4\u51fa\u6765\u2026\u2026\u201d \u3000\u3000\u8d75\u4e8c\u5824\u7b11\u4e00\u7b11\uff0c\u63a5\u7740\u9053\uff1a\u201c\u738b\u5927\u4eba\uff0c\u4e8c\u5824\u5e78\u672a\u8fb1\u547d\uff0c\u4e09\u516c\u4e3b\u7684\u51e4\u9a7e\uff0c\u5df2\u7ecf\u52a8\u8eab\u4e86\u3002\u201d \u3000\u3000\u201c\u611f\u8c22\uff0c\u611f\u8c22\u2026\u2026\u201d\u738b\u5f64\u9762\u9732\u6b22\u5bb9\uff0c\u9053\uff1a\u201c\u4e0d\u77e5\u51e0\u65f6\u53ef\u5230\u3002\u201d \u3000\u3000\u8d75\u4e8c\u5824\u9053\uff1a\u201c\u7b97\u884c\u7a0b\uff0c\u5927\u6982\u4e0d\u4f1a\u8fc7\u4e5d\u6708\u4e2d\u65ec\uff0c\u4e0d\u8fc7\uff0c\u738b\u5927\u4eba\uff0c\u4e09\u4e48\u4e3b\u4e0d\u5230\u5f00\u5c01\uff0c\u4ed6\u4eec\u76f4 \u8d74\u5317\u4eac\u3002\u201d \u3000\u3000\u738b\u5f64\u5fae\u5fae\u4e00\u6014\uff0c\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u5728\u4e0b\u8981\u5c3d\u5feb\u8d76\u56de\u4eac\u57ce\u4e86\u3002\u201d \u3000\u3000\u201c\u738b\u5144\u5982\u679c\u8d1f\u8d23\u4fdd\u62a4\u4e09\u516c\u4e3b\u7684\u5b89\u5371\uff0c\u6050\u6015\u8981\u56de\u4eac\u57ce\u4e00\u8d9f\u4e86\u3002\u201d\u9a6c\u6587\u4e2d\u63d0\u51fa\u4e86\u81ea\u5df1\u7684\u770b \u6cd5\u3002 \u3000\u3000\u8d75\u4e8c\u5824\u9053\uff1a\u201c\u738b\u5927\u4eba\u8981\u56de\u4eac\u63a5\u9a7e\uff0c\u53ea\u6015\u8981\u8d76\u5feb\u4e00\u6b65\u624d\u884c\u3002\u201d \u3000\u3000\u201c\u5bf9\u2026\u2026\u201d\u738b\u5f64\u63a5\u9053\uff1a\u201c\u660e\u5929\uff0c\u6211\u5c31\u52a8\u8eab\u56de\u4eac\u3002\u201d \u3000\u3000\u738b\u5f64\u4f4f\u7684\u5ba2\u623f\uff0c\u662f\u9a6c\u6587\u4e2d\u8981\u4e0b\u4eba\u7cbe\u5fc3\u5e03\u7f6e\u7684\uff0c\u4e00\u69bb\u4e00\u6905\uff0c\u90fd\u7ecf\u7cbe\u9009\uff0c\u4f46\u8fd9\u6837\u8212\u670d\u7684\u5730 \u65b9\uff0c\u738b\u5f64\u5374\u662f\u7761\u4e0d\u5b89\u7a33\uff0c\u4ed6\u5728\u4ed4\u7ec6\u7684\u63a8\u6572\uff0c\u5982\u4f55\u624d\u80fd\u628a\u4fdd\u62a4\u3001\u76d1\u89c6\u4e09\u516c\u4e3b\u7684\u8d23\u4efb\uff0c\u5957\u5230\u9a6c\u6587 \u4e2d\u7684\u5934\u4e0a\u3002 \u3000\u3000\u4ed6\u662f\u6cb3\u5357\u5de1\u629a\uff0c\u5f53\u7136\u4e0d\u80fd\u540c\u8d74\u4eac\u4e2d\uff0c\u76d1\u62a4\u4e09\u516c\u4e3b\uff0c\u4f46\u8981\u4ed6\u6d3e\u51fa\u9ad8\u624b\uff0c\u62c5\u8d1f\u8d77\u5927\u90e8\u5206\u7684\u8d23 \u4efb\uff0c\u5374\u4e5f\u4e0d\u662f\u4e00\u4ef6\u7b80\u5355\u7684\u4e8b\u3002 \u3000\u3000\u738b\u5f64\u53ef\u4ee5\u6307\u660e\u70b9\u5c06\uff0c\u4f46\u738b\u5f64\u5374\u4e0d\u77e5\u9053\u9a6c\u6587\u4e2d\u624b\u4e0b\uff0c\u8c01\u662f\u771f\u6b63\u7684\u9ad8\u624b\u3002\u4ed6\u77e5\u9053\u7684\u51e0\u4e2a\u4eba\uff0c \u8f6c\u9f99\u624b\u5f20\u4e0d\u7a7a\uff0c\u662f\u5929\u4e0b\u7b2c\u4e00\u7684\u7a83\u8d3c\uff0c\u81ea\u662f\u4e0d\u80fd\u51fa\u5165\u7687\u5bab\uff1b\u59ec\u91cd\u5929\u662f\u4e2a\u9ad8\u624b\uff0c\u4f46\u738b\u5f64\u5fc3\u4e2d\u660e \u767d\uff0c\u81ea\u5df1\u7684\u5a01\u671b\uff0c\u4e0d\u8db3\u4ee5\u4f7f\u59ec\u91cd\u5929\u5fc3\u751f\u656c\u670d\uff0c\u4f55\u51b5\uff0c\u9a6c\u6587\u4e2d\u8fd8\u53ef\u4ee5\u63a8\u8131\uff0c\u56e0\u4e3a\u59ec\u91cd\u5929\u6295\u5165\u9a6c \u6587\u4e2d\u7684\u624b\u4e0b\uff0c\u4ecd\u662f\u4e00\u4ef6\u6ca1\u6cd5\u8bc1\u5b9e\u7684\u4e8b\u60c5\uff0c\u5728\u76ee\u524d\u7684\u60c5\u5f62\u4e0b\uff0c\u8fd8\u4e0d\u5b9c\u5f53\u573a\u63ed\u7a7f\uff0c\u8d75\u4e8c\u5824\u662f\u4e2a\u56de \u56de\uff0c\u5df2\u77e5\u9053\u662f\u8499\u53e4\u65cf\u4eba\uff0c\u5f53\u7136\u4e0d\u80fd\u5e26\u4ed6\u5165\u4eac\u2026\u2026 \u3000\u3000\u738b\u5f64\u60f3\u7834\u4e86\u8111\u888b\uff0c\u4e5f\u60f3\u4e0d\u51fa\u8981\u4ec0\u4e48\u4eba\u624d\u6700\u6070\u5f53\u3002 \u3000\u3000\u660e\u5929\uff0c\u4ed6\u5c31\u8981\u52a8\u8eab\u56de\u4eac\uff0c\u53ea\u6709\u65e9\u9910\u65f6\u4e00\u4e2a\u673a\u4f1a\u4e86\u3002 \u3000\u3000\u8fd9\u4f3c\u662f\u4e00\u4ef6\u5f88\u5c0f\u7684\u4e8b\u60c5\uff0c\u738b\u5f64\u53ea\u8981\u63d0\u51fa\u6765\uff0c\u9a6c\u6587\u4e2d\u4e00\u5b9a\u4f1a\u7b54\u5e94\uff0c\u95ee\u9898\u662f\u9a6c\u6587\u4e2d\u4f1a\u4e0d\u4f1a\u6d3e \u51fa\u771f\u6b63\u7684\u9ad8\u624b\uff0c\u751a\u81f3\u6d3e\u51fa\u9b54\u6559\u4e2d\u4eba\uff0c\u90a3\u5c31\u5f04\u5de7\u6210\u62d9\u4e86\u3002 \u3000\u3000\u76f4\u60f3\u5230\u5929\u8272\u5fae\u660e\uff0c\u738b\u5f64\u4ecd\u7136\u662f\u60f3\u4e0d\u51fa\u4e00\u4e2a\u5b8c\u7f8e\u7684\u529e\u6cd5\u3002 \u3000\u3000\u65e9\u9910\u5f88\u4e30\u76db\uff0c\u800c\u4e14\uff0c\u9a6c\u6587\u4e2d\u4e5f\u8bf7\u4e86\u8d75\u4e8c\u5824\uff0c\u90a3\u662f\u51c6\u5907\u738b\u5f64\u968f\u65f6\u63d0\u51fa\u95ee\u9898\u65f6\uff0c\u8d75\u4e8c\u5824\u53ef\u4ee5 \u5f53\u573a\u7b54\u590d\u3002 \u3000\u3000\u8fd9\u4e5f\u662f\u4e00\u4ef6\u5c0f\u4e8b\uff0c\u4f46\u53ef\u89c1\u9a6c\u6587\u4e2d\u662f\u4e00\u4e2a\u601d\u8651\u5468\u5bc6\u7684\u4eba\uff0c\u7b80\u76f4\u662f\u70b9\u6ef4\u4e0d\u6f0f\u3002 \u3000\u3000\u679c\u7136\uff0c\u738b\u5f64\u63d0\u51fa\u4e86\u7b2c\u4e00\u4e2a\u95ee\u9898\uff0c\u9053\uff1a\u201c\u4e0d\u77e5\u5bab\u5973\u60dc\u6625\uff0c\u662f\u5426\u4ecd\u8ffd\u968f\u5728\u4e09\u516c\u4e3b\u7684\u8eab\u4fa7\uff1f\u201d \u3000\u3000\u60dc\u6625\u662f\u9b54\u6559\u7684\u5f1f\u5b50\uff0c\u6df7\u5165\u7687\u5bab\u88ab\u4e09\u516c\u4e3b\u9009\u4e3a\u8fd1\u8eab\u7684\u4f8d\u5a62\u3002 \u3000\u3000\u201c\u597d\u50cf\u6ca1\u6709\u2026\u2026\u201d\u8d75\u4e8c\u5824\u8bf4\uff1a\u201c\u5c31\u5728\u4e0b\u6240\u77e5\uff0c\u4e09\u516c\u4e3b\u53ea\u5e26\u8d70\u4e86\u4e00\u540d\u4f8d\u5973\u601c\u82b1\u3002\u540c\u5f52\u4e2d \u539f\u3002\u201d \u3000\u3000\u201c\u55c5\uff01\u201d\u738b\u5f64\u9053\uff1a\u201c\u8d75\u5144\u53ef\u89c1\u8fc7\u4e09\u516c\u4e3b\uff1f\u201d \u3000\u3000\u201c\u89c1\u8fc7\uff0c\u5728\u4e0b\u8fd8\u8499\u4e09\u516c\u4e3b\u8d50\u4e0b\u4e00\u684c\u9152\u5e2d\u3002\u201d \u3000\u3000\u8d75\u4e8c\u5824\u53c8\u63a5\u7740\u9053\uff1a\u201c\u738b\u5927\u4eba\u56de\u4eac\u4e4b\u540e\uff0c\u5f53\u4f1a\u660e\u767d\u4e09\u516c\u4e3b\u5728\u897f\u57df\u53d7\u5230\u4e86\u6781\u4e3a\u5c0a\u8d35\u7684\u4f18\u5f85\uff0c \u751f\u6d3b\u4e0a\u7684\u4eab\u53d7\uff0c\u4e0d\u4f1a\u6bd4\u7687\u5bab\u5185\u9662\u5dee\u591a\u5c11\u3002\u201d \u3000\u3000\u738b\u5f64\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u8fd9\u4e24\u5e74\u4e2d\uff0c\u90fd\u4f4f\u5728\u5c0f\u897f\u5929\u96f7\u97f3\u5bfa\u4e2d\u4e48\uff1f\u201d \u3000\u3000\u201c\u5c0f\u897f\u5929\u96f7\u97f3\u5bfa\uff0c\u662f\u4e2a\u5145\u6ee1\u7740\u795e\u79d8\u4f20\u8bf4\u7684\u5730\u65b9\uff01\u201d \u3000\u3000\u987f\u4e86\u4e00\u987f\u3002 \u3000\u3000\u8d75\u4e8c\u5824\u53c8\u9053\uff1a\u201c\u8d75\u67d0\u5728\u897f\u57df\u957f\u5927\uff0c\u8db3\u8ff9\u904d\u53ca\u5929\u5c71\u3001\u6ce2\u65af\uff0c\u8499\u53e4\u5730\u65b9\u7684\u90e8\u843d\u533a\u57df\uff0c\u66f4\u662f\u5927 \u90fd\u5230\u8fc7\uff0c\u4f46\u6211\u6ca1\u6709\u53bb\u8fc7\u96f7\u97f3\u5bfa\uff0c\u8001\u5b9e\u8bf4\uff0c\u6211\u6839\u672c\u4e0d\u77e5\u5b83\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u96f7\u97f3\u5bfa\u4f20\u8bf5\u4e86\u51e0\u767e\u5e74\u2026\u2026\u201d\u738b\u5f64\u8bf4\uff1a\u201c\u4e2d\u539f\u6b66\u6797\u540c\u9053\uff0c\u6709\u5f88\u591a\u4eba\u542c\u8fc7\u4e86\u8fd9\u4e2a\u5730\u65b9\uff0c\u4e0d \u4f1a\u662f\u51ed\u7a7a\u634f\u9020\u51fa\u8fd9\u4e48\u4e00\u4e2a\u6240\u5728\u5427\uff1f\u201d \u3000\u3000\u201c\u738b\u5927\u4eba\u2026\u2026\u201d\u8d75\u4e8c\u5824\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u5728\u5f88\u5c0f\u7684\u65f6\u5019\uff0c\u5c31\u542c\u8fc7\u8fd9\u4e48\u4e00\u4e2a\u5730\u65b9\uff0c\u53ef\u4e5f\u53ea \u662f\u542c\u5230\u4f20\u8bf4\uff0c\u6211\u4ece\u672a\u5230\u8fc7\u5c0f\u897f\u5929\u96f7\u97f3\u5b88\u8fd9\u4e2a\u5730\u65b9\u3002\u201d \u3000\u3000\u201c\u8fd9\u2026\u2026\u201d\u738b\u5f64\u4e00\u8138\u5947\u5f02\u7684\u795e\u60c5\uff0c\u9053\uff1a\u201c\u5f53\u771f\u662f\u6709\u4e9b\u4e0d\u53ef\u601d\u8bae\u4e86\uff0c\u897f\u57df\u9b54\u6559\uff0c\u5e94\u8be5\u662f\u5343 \u771f\u4e07\u786e\u7684\u5b58\u5728\u5427\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u8d75\u4e8c\u5824\u63a5\u7740\u53c8\u9053\uff1a\u201c\u4e2d\u539f\u6b66\u6797\u4f20\u8bf5\u7684\u9b54\u6559\uff0c\u539f\u662f\u897f\u57df\u5587\u561b\u6559\u4e2d\u7684\u4e00\u652f\uff0c\u6b64\u6559\u7531\u5929\u7afa\u4f20 \u5165\uff0c\u672c\u4ee5\u745c\u4f3d\u672f\u4fee\u70bc\u4e3a\u4e3b\uff0c\u4f46\u5b83\u540c\u65f6\u5e26\u6765\u4e86\u4e09\u672c\u6559\u4e2d\u79d8\u7b08\uff0c\u90a3\u4e0a\u9762\u8bb0\u8f7d\u4e86\u5f88\u591a\u7684\u7b26\u5f55\u3001\u5492 \u8bed\uff0c\u4e5f\u8bb0\u8f7d\u4e86\u5929\u7afa\u7684\u533b\u672f\u548c\u7528\u6bd2\u7684\u65b9\u6cd5\uff0c\u6700\u91cd\u8981\u7684\u662f\uff0c\u8fd8\u8bb0\u8f7d\u4e86\u4e00\u4e9b\u7279\u5f02\u7684\u6b66\u529f\uff0c\u90a3\u662f\u7531\u745c \u4f3d\u672f\u4e2d\u53c2\u609f\u51fa\u6765\u7684\u6b66\u529f\uff0c\u662f\u4eba\u4f53\u80fd\u7684\u6781\u9650\u8fb9\u7f18\u53d1\u6325\u51fa\u7684\u6253\u51fb\u529b\u91cf\uff0c\u4e0d\u89e3\u6b64\u4e2d\u9053\u7406\u7684\u4eba\uff0c\u5c31\u6709 \u4e9b\u532a\u5937\u6240\u601d\u4e86\u3002\u201d \u3000\u3000\u201c\u8d75\u5144\u60f3\u5fc5\u662f\u7ec3\u8fc7\u8fd9\u4e9b\u6b66\u529f\u4e86\uff1f\u201d\u738b\u5f64\u63d0\u51fa\u4e86\u5fc3\u4e2d\u7684\u7591\u95ee\u3002 \u3000\u3000\u201c\u662f\u3002\u8fd9\u4e9b\u6b66\u529f\u5728\u897f\u57df\u6d41\u4f20\u2026\u2026\u201d\u8d75\u4e8c\u5824\u9053\uff1a\u201c\u5f88\u591a\u7684\u8499\u53e4\u65cf\u4eba\u7684\u5b50\u5f1f\u4eec\uff0c\u90fd\u5b66\u8fc7\u4e86\u8fd9 \u4e9b\u6b66\u529f\u3002\u201d \u3000\u3000\u738b\u5f64\u70b9\u70b9\u5934\uff0c\u76ee\u6ce8\u9a6c\u6587\u4e2d\uff0c\u9053\uff1a\u201c\u9a6c\u5144\u5bf9\u8fd9\u65b9\u9762\u7684\u77e5\u8bc6\uff0c\u5341\u5206\u6e0a\u535a\uff0c\u53ef\u5426\u63ed\u793a\u4e00\u4e8c\uff0c\u4ee5 \u5f00\u5144\u5f1f\u7684\u8305\u585e\u3002\u201d \u3000\u3000\u4ed6\u51fa\u8eab\u9b54\u6559\u7684\u5f1f\u5b50\u7684\u8eab\u4efd\uff0c\u867d\u672a\u4eb2\u53e3\u627f\u8ba4\uff0c\u4f46\u4ed6\u548c\u9b54\u6559\u5173\u7cfb\uff0c\u884c\u52a8\u4e0a\u5219\u5448\u73b0\u51fa\u5341\u5206\u6df1 \u539a\uff0c\u5df2\u662f\u65e0\u6cd5\u5426\u8ba4\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u9a6c\u6587\u4e2d\u6ca1\u6709\u5426\u8ba4\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u738b\u5144\u60f3\u77e5\u9053\u9b54\u6559\u4e8b\u60c5\uff0c\u65e9\u8be5\u95ee\u5144\u5f1f\u7684\uff0c\u5982\u8bba \u548c\u9b54\u6559\u5173\u7cfb\uff0c\u9a6c\u6587\u4e2d\u6bd4\u8d75\u4e8c\u5824\uff0c\u53ef\u662f\u5bc6\u5207\u591a\u4e86\uff0c\u6211\u9996\u5148\u89e3\u7b54\u738b\u5144\u5fc3\u4e2d\u7684\u6700\u5927\u7591\u95ee\uff0c\u897f\u57df\u786e\u5b58 \u5728\u7740\u9b54\u6559\uff0c\u4e5f\u786e\u6709\u4e00\u5ea7\u96f7\u97f3\u5bfa\uff0c\u96f7\u97f3\u5bfa\u5728\u5c0f\u897f\u5929\u4e2d\uff0c\u4f46\u5c0f\u897f\u5929\u5728\u54ea\u91cc\u5c31\u975e\u4e00\u822c\u4eba\u6240\u77e5\u9053\u7684 \u4e86\u3002\u201d \u3000\u3000\u5927\u6982\uff0c\u8d75\u4e8c\u5824\u4e5f\u662f\u521d\u6b21\u542c\u5230\u8fd9\u4e9b\u9690\u5bc6\uff0c\u77aa\u7740\u4e00\u5bf9\u5927\u773c\u775b\uff0c\u671b\u7740\u9a6c\u6587\u4e2d\uff0c\u8138\u4e0a\u7684\u671f\u76fc\u795e\u60c5 \u5c24\u8fc7\u738b\u5f64\u3002 \u3000\u3000\u201c\u9a6c\u5c3c\u65e2\u53bb\u8fc7\u5c0f\u897f\u5929\u4e86\uff0c\u90a3\u7a76\u7adf\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u5730\u65b9\uff0c\u662f\u4e00\u5ea7\u57ce\u9547\u2026\u2026\u201d\u738b\u5f64\u9053\uff1a\u201c\u8fd8\u662f \u53ea\u6709\u4e00\u5ea7\u96f7\u97f3\u5bfa\uff1f\u201d \u3000\u3000\u4e5f\u8bb8\uff0c\u65e5\u540e\uff0c\u514d\u4e0d\u4e86\u548c\u9b54\u6559\u4e00\u51b3\u6b7b\u6218\uff0c\u5982\u80fd\u6709\u5148\u63a2\u5f97\u4e00\u4e9b\u6d88\u606f\u7684\u673a\u4f1a\uff0c\u738b\u5f64\u7edd\u4e0d\u653e\u8fc7\u3002 \u3000\u3000\u201c\u6211\u53bb\u8fc7\u5c0f\u897f\u5929\u7684\u96f7\u97f3\u5bfa\uff0c\u90a3\u53ea\u662f\u9752\u77f3\u780c\u6210\u7684\u5bfa\u9662\uff0c\u800c\u4e14\u89c4\u6a21\u4e5f\u4e0d\u5927\u2026\u2026\u201d\u9a6c\u6587\u4e2d\u8bf4\uff1a \u201c\u5168\u5bfa\u7684\u4eba\u6570\uff0c\u5305\u62ec\u65b9\u4e08\u3001\u957f\u8001\u548c\u6742\u5f79\u5728\u5185\uff0c\u4e0d\u8d85\u8fc7\u4e00\u767e\u4eba\uff0c\u4e0d\u8fc7\uff0c\u96f7\u97f3\u5bfa\u5374\u6709\u4e00\u4e2a\u4e16\u65e0\u5176 \u5339\u7684\u7279\u8272\u3002\u201d \u3000\u3000\u2018\u4ec0\u4e48\u7279\u8272\uff1f\u201c\u738b\u5f64\u9053\uff1a\u201d\u662f\u6b66\u529f\uff1f\u8fd8\u662f\u5947\u672f\uff1f\u201c \u3000\u3000\u201c\u662f\u98ce\u666f\u3002\u201d\u9a6c\u6587\u4e2d\u7b11\u9053\uff1a\u201c\u90a3\u662f\u4e00\u5904\u7fa4\u5c71\u73af\u7ed5\u7684\u76c6\u5730\uff0c\u56db\u5468\u6709\u5e38\u5e74\u4e0d\u5316\u7684\u79ef\u96ea\u73af\u56f4\uff0c \u4f46\u90a3\u7247\u76c6\u5730\u5374\u662f\u56db\u5b63\u5982\u6625\uff0c\u904d\u5f00\u7740\u4e94\u8272\u5947\u82b1\uff0c\u6ee1\u751f\u7740\u5982\u8335\u9752\u8349\uff0c\u90a3\u91cc\u6709\u6587\u7fe0\u9e1f\u3001\u5de8\u96d5\u3001\u9e7f \u7fa4\u3001\u4e5f\u6709\u731b\u72ee\u3001\u864e\u8c79\u51fa\u6ca1\uff0c\u5947\u602a\u7684\u662f\u8fd9\u4e9b\u98df\u8089\u7684\u731b\u517d\uff0c\u597d\u6597\u7684\u51f6\u79bd\uff0c\u5728\u90a3\u7247\u6d1e\u5929\u798f\u5730\u4e2d\uff0c\u7adf \u7136\u90fd\u6d88\u53bb\u51f6\u7130\uff0c\u548c\u7766\u76f8\u5904\uff0c\u771f\u6709\u4e00\u4e9b\u6781\u4e50\u4e16\u754c\uff0c\u897f\u65b9\u51c0\u571f\u7684\u611f\u89c9\u3002\u201d \u3000\u3000\u201c\u90a3\u91cc\u5e94\u8be5\u4fee\u70bc\u51fa\u6551\u4e16\u7684\u89c2\u97f3\uff0c\u89e3\u96be\u7684\u7f57\u6c49\u624d\u662f\u2026\u2026\u201d\u738b\u5f64\u9053\uff1a\u201c\u600e\u4f1a\u6559\u51fa\u4e86\u6df7\u4e16\u7684\u9b54 \u738b\u3001\u5996\u5973\u6076\u715e\uff1f\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u7b11\u9053\uff1a\u201c\u738b\u5144\uff0c\u90a3\u672c\u662f\u4e00\u5757\u771f\u6b63\u7eaf\u51c0\u7684\u5730\u65b9\uff0c\u9690\u5c45\u4e86\u6570\u767e\u4f4d\u7537\u5973\u6c11\u4f17\uff0c\u4ed6\u4eec\u4e60\u7ec3 \u6b66\u529f\uff0c\u6559\u5316\u9e1f\u517d\uff0c\u6280\u827a\u9650\u4e8e\u4e00\u9685\uff0c\u672f\u6cd5\u4e0d\u51fa\u7981\u5730\u3002\u5f62\u6210\u4eca\u5929\u9b54\u6559\u7684\u5f62\u52bf\uff0c\u738b\u5144\uff0c\u90a3\u662f\u62dc\u4f60\u4eec \u6c49\u4eba\u4e4b\u8d50\u4e86\u201d\u8fd9\u8bdd\u600e\u4e48\u8bf4\u5462\uff1f\u201c\u738b\u5f64\u591a\u5e74\u5728\u6c5f\u6e56\u8d70\u52a8\uff0c\u5bf9\u9b54\u6559\u5728\u4e2d\u539f\u6b66\u6797\u9053\u4e0a\u60f9\u7684\u9ebb\u70e6\uff0c\u77e5 \u4e4b\u751a\u8be6\uff0c\u4f46\u4ed6\u5bf9\u9b54\u6559\u7684\u6839\u6e90\u5185\u60c5\uff0c\u5374\u662f\u5168\u4e0d\u4e86\u89e3\u3002 \u3000\u3000\u5c31\u662f\u6d88\u606f\u7075\u901a\u5982\u6c5f\u5343\u91cc\u8005\uff0c\u4e5f\u672a\u5fc5\u77e5\u9053\u9b54\u6559\u7684\u6765\u9f99\u53bb\u8109\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u6c5f\u5343\u91cc\u6ca1\u6709\u8bf4\u8fc7\u3002 \u3000\u3000\u201c\u90a3\u662f\u5f88\u4e45\u8fdc\u4ee5\u524d\u7684\u4e8b\u4e86\uff0c\u5927\u6982\u4e24\u767e\u591a\u5e74\u524d\u5427\u2026\u2026\u201d\u9a6c\u6587\u4e2d\u9053\uff1a\u201c\u6709\u4e00\u6279\u6c49\u4eba\uff0c\u4e3a\u5927\u98ce \u6c99\u6240\u56f0\uff0c\u6655\u5012\u5728\u5927\u6c99\u6f20\u4e2d\uff0c\u4f46\u5374\u88ab\u5c0f\u897f\u5929\u4e2d\u7684\u91c7\u7269\u4f7f\u8005\u9047\u4e0a\uff0c\u628a\u4ed6\u4eec\u6551\u4eba\u96f7\u97f3\u5bfa\u4e2d\uff0c\u90a3\u4e00\u6279 \u6c49\u4eba\u4e00\u884c\u6709\u5341\u51e0\u4e2a\u4eba\uff0c\u4f46\u88ab\u6551\u6d3b\u7684\u53ea\u6709\u4e24\u4e2a\u4eba\uff0c\u4ed6\u4eec\u53d1\u89c9\u90a3\u91cc\u666f\u7269\u4f18\u7f8e\uff0c\u517b\u597d\u8eab\u4f53\u540e\uff0c\u7adf\u7136 \u4e0d\u80af\u79bb\u5f00\uff0c\u82e6\u6c42\u4e3b\u6301\uff0c\u8981\u4ed6\u4eec\u7559\u4e0b\u6765\uff0c\u7518\u4e3a\u6742\u5f79\uff0c\u5f53\u65f6\u7684\u4e3b\u6301\u65b9\u4e08\uff0c\u5fc3\u5934\u4e00\u8f6f\uff0c\u5c31\u7b54\u5e94\u4e86\u4e0b \u6765\u3002\u201d \u3000\u3000\u201c\u9a6c\u5144\u7684\u610f\u601d\u662f\u8bf4\uff0c\u4ed6\u4eec\u7559\u5728\u90a3\u91cc\u4e4b\u540e\uff0c\u5f71\u54cd\u4e86\u9b54\u6559\u7684\u53d1\u5c55\uff1f\u201d \u3000\u3000\u738b\u5f64\u4f4e\u58f0\u63d0\u51fa\u5fc3\u4e2d\u7684\u7591\u95ee\u3002 \u3000\u3000\u201c\u5927\u5927\u7684\u5f71\u54cd\u2026\u2026\u201d \u3000\u3000\u987f\u4e86\u4e00\u987f\u3002 \u3000\u3000\u9a6c\u6587\u4e2d\u53c8\u9053\uff1a\u201c\u4e24\u4e2a\u6c49\u4eba\u672c\u6765\u5c31\u662f\u6b66\u6797\u4e2d\u4eba\uff0c\u5df2\u7ecf\u6709\u4e86\u5f88\u597d\u7684\u6b66\u529f\u57fa\u7840\uff0c\u7559\u5728\u5c0f\u897f\u5929 \u540e\uff0c\u53c8\u5f00\u59cb\u4e60\u7ec3\u96f7\u97f3\u5bfa\u7684\u6b66\u529f\uff0c\u4e24\u4eba\u517c\u6709\u4e2d\u897f\u6b66\u6280\u4e4b\u957f\uff0c\u8fdb\u5883\u5f88\u5feb\uff0c\u5728\u4e00\u6b21\u664b\u7ea7\u957f\u8001\u7684\u6bd4\u6b66 \u5927\u4f1a\u4e2d\uff0c\u4e24\u4eba\u90fd\u664b\u5347\u4e3a\u957f\u8001\u7684\u8eab\u4efd\uff0c\u4ece\u6b64\uff0c\u5f97\u4ee5\u53c2\u4e0e\u5bfa\u4e2d\u7684\u51b3\u7b56\u5927\u4e8b\uff0c\u4e5f\u770b\u5230\u4e86\u4e09\u672c\u5bc6\u7ecf\u3002 \u4e24\u4eba\u523b\u82e6\u81ea\u52b1\uff0c\u82e6\u4fee\u5929\u7afa\u6587\u5b57\uff0c\u82b1\u8d39\u4e86\u8fd1\u5341\u5e74\u7684\u5de5\u592b\uff0c\u5b8c\u5168\u5f04\u901a\u4e86\u5929\u7afa\u6587\u610f\uff0c\u4e13\u7814\u4e09\u672c\u5bc6 \u7ecf\uff0c\u6b65\u5165\u5927\u4e58\u5883\u754c\uff0c\u5176\u4e2d\u4e00\u4eba\u7adf\u6210\u4e86\u5c0f\u897f\u5929\u4e2d\u7b2c\u4e00\u9ad8\u624b\uff0c\u7ec8\u4e8e\u5f97\u5230\u4e86\u4e3b\u6301\u7684\u4f4d\u7f6e\uff0c\u5c0f\u897f\u5929\u7684 \u4e3b\u6301\u8eab\u4efd\uff0c\u5728\u90a3\u7247\u571f\u5730\u4e0a\uff0c\u6709\u7740\u7edd\u5bf9\u7684\u6743\u5a01\uff0c\u53d7\u5230\u6240\u6709\u7537\u5973\u7684\u656c\u91cd\uff0c\u90a3\u65f6\uff0c\u4ed6\u5df2\u662f\u82b1\u7532\u4e4b \u5e74\uff0c\u4f46\u5374\u770b\u4e0a\u4e86\u4e00\u4f4d\u5341\u516b\u5c81\u7684\u5c11\u5973\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u201d\u738b\u5f64\u9053\uff1a\u201c\u96be\u9053\u4ed6\u4f1a\u8feb\u5a5a\u4e0d\u6210\u2026\u2026\u201d \u3000\u3000\u201c\u7528\u4e0d\u7740\u8feb\u5a5a\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u53c8\u63a5\u7740\u9053\uff1a\u201c\u4ed6\u4ee5\u4e3b\u6301\u7684\u8eab\u4efd\uff0c\u53ea\u8981\u8bf4\u51fa\u5fc3\u4e2d\u6240\u601d\uff0c\u7acb\u523b\u5f97\u507f\u6240\u613f\uff0c\u90a3\u91cc\u4e3b\u6301\u7684 \u6743\u52bf\uff0c\u6bd4\u4e2d\u539f\u7684\u7687\u5e1d\u6709\u8fc7\u4e4b\u800c\u65e0\u4e0d\u53ca\u3002\u201d \u3000\u3000\u738b\u5f64\u53f9\u606f\u4e00\u58f0\uff0c\u6b32\u8a00\u53c8\u6b62\u3002 \u3000\u3000\u9a6c\u6587\u4e2d\u9053\uff1a\u201c\u5982\u8bba\u667a\u7565\u624b\u8155\uff0c\u5fcd\u8fb1\u8d1f\u91cd\uff0c\u6cbb\u4e8b\u65b9\u6cd5\uff0c\u897f\u57df\u5404\u65cf\u4eba\uff0c\u4e07\u4e07\u96be\u53ca\u5f97\u6c49\u4eba\uff0c\u4ed6 \u4eec\u6269\u5efa\u96f7\u97f3\u5bfa\uff0c\u5728\u90a3\u91cc\u7acb\u4e0b\u4e86\u5404\u79cd\u89c4\u8303\u3001\u6212\u5f8b\uff0c\u628a\u4e00\u7247\u751f\u6d3b\u6563\u6f2b\u7684\u4e16\u5916\u6843\u6e90\uff0c\u5efa\u7acb\u6210\u4e00\u4e2a\u575a \u56fa\u7684\u56e2\u4f53\uff0c\u8fd9\u5c31\u662f\u4e2d\u539f\u6b66\u6797\u6240\u6307\u7684\u9b54\u6559\u8d77\u6e90\u3002\u201d \u3000\u3000\u201c\u4ee5\u540e\u5462\uff1f\u201d\u738b\u5f64\u542c\u5f97\u795e\u9a70\u5728\u6570\u767e\u5e74\u524d\u7684\u5f80\u4e8b\u4e2d\u3002 \u3000\u3000\u201c\u4ee5\u540e\u5404\u4ee3\u52a0\u5f3a\u4fee\u8ba2\u5404\u79cd\u89c4\u6212\u2026\u2026\u201d\u9a6c\u6587\u4e2d\u8bf4\uff1a\u201c\u5404\u79cd\u6b66\u529f\uff0c\u4e5f\u8d35\u4ee5\u7cbe\u4e13\uff0c\u6210\u4e86\u73b0\u5728\u7684 \u7ec4\u5408\u3002\u201d \u3000\u3000\u201c\u90a3\u7247\u4e50\u571f\uff0c\u7a76\u7adf\u5728\u4ec0\u4e48\u5730\u65b9\u5462\uff1f\u201d\u738b\u5f64\u5e0c\u671b\u77e5\u9053\u9b54\u6559\u7684\u5de2\u7a74\u3002 \u3000\u3000\u201c\u9a6c\u67d0\u53bb\u8fc7\u5c0f\u897f\u5929\u96f7\u97f3\u5bfa\uff0c\u4f46\u6211\u65e0\u6cd5\u786e\u5b9a\u7684\u8bf4\u51fa\u5b83\u7684\u5730\u7406\u4f4d\u7f6e\uff0c\u9664\u4e86\u4e3b\u6301\u3001\u957f\u8001\u548c\u5341\u516b \u884c\u8005\u5916\uff0c\u6ca1\u6709\u4eba\u80fd\u627e\u5230\u90a3\u4e2a\u5730\u65b9\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\uff0c\u9a6c\u5144\u662f\u600e\u4e48\u53bb\u7684\u5462\uff1f\u201d \u3000\u3000\u201c\u88ab\u63a5\u8fdb\u53bb\u7684\uff01\u201d \u3000\u3000\u201c\u5c31\u7b97\u88ab\u8499\u4e0a\u4e86\u53cc\u773c\u2026\u2026\u201d\u738b\u5f64\u8bf4\uff1a\u201c\u4ee5\u9a6c\u5144\u7684\u624d\u667a\uff0c\u4e5f\u53ef\u4ee5\u6307\u51fa\u4e2a\u5927\u6982\u7684\u65b9\u4f4d\u3002\u201d \u3000\u3000\u201c\u9519\u4e86\uff0c\u738b\u5144\uff0c\u8fdb\u5165\u5c0f\u897f\u5929\u7684\u4eba\uff0c\u5148\u670d\u7528\u4e00\u79cd\u836f\u7269\uff0c\u5b8c\u5168\u6655\u8ff7\u8fc7\u53bb\uff0c\u5c31\u50cf\u66ff\u4f60\u738b\u5144\u7597\u4f24 \u4e00\u6837\uff0c\u6709\u5982\u4e00\u573a\u68a6\u822c\uff0c\u9192\u8fc7\u6765\uff0c\u5df2\u7ecf\u5728\u96f7\u97f3\u5bfa\u4e2d\u3002\u201d \u3000\u3000\u738b\u5f64\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u90a3\u771f\u662f\u4e00\u5757\u5929\u5916\u7684\u4e50\u571f\uff0c\u6ca1\u6709\u4eba\u80fd\u591f\u627e\u5f97\u5230\u7684\u4e16\u5916\u6843\u6e90\uff0c\u4ed6\u4eec\u53ef\u4ee5\u6765 \u53bb\u81ea\u5982\uff0c\u6211\u4eec\u5374\u65e0\u6cd5\u627e\u5230\u90a3\u4e2a\u5730\u65b9\u3002\u201d \u3000\u3000\u201c\u738b\u5144\uff0c\u6211\u8bf4\u7684\u662f\u5b9e\u8bdd\u2026\u2026\u201d\u9a6c\u6587\u4e2d\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4efb\u4f55\u5feb\u4e50\u7684\u5730\u65b9\uff0c\u6709\u4e00\u4e2a\u6700\u91cd\u8981 \u7684\u6761\u4ef6\uff0c\u90a3\u91cc\u5fc5\u7136\u662f\u65e0\u62d8\u65e0\u675f\u7684\uff0c\u4f46\u73b0\u5728\u7684\u5c0f\u897f\u5929\uff0c\u5374\u6709\u4e86\u4e25\u5211\u5cfb\u6cd5\u7684\u7ea6\u675f\uff0c\u5211\u6cd5\u4e4b\u6b8b\u9177\uff0c \u7b80\u76f4\u4ee4\u4eba\u60f3\u5230\u5c31\u6015\uff0c\u5f53\u7136\uff0c\u4ed6\u4eec\u4e5f\u4ed7\u6043\u8fd9\u79cd\u4e25\u5389\u7684\u6212\u5f8b\uff0c\u7ef4\u6301\u4e86\u95e8\u4e0b\u5f1f\u5b50\u7684\u5fe0\u8bda\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u4e00\u63a0\u8d75\u4e8c\u5824\u548c\u738b\u5f64\u3002 \u3000\u3000\u63a5\u7740\u53c8\u9053\uff1a\u201c\u6211\u5728\u96f7\u97f3\u5bfa\u4e2d\uff0c\u770b\u5230\u90a3\u6bb5\u7531\u6c49\u4eba\u4e3b\u6301\u7684\u8bb0\u8f7d\uff0c\u7a81\u7136\u89c9\u5f97\u6c49\u4eba\u662f\u6700\u806a\u660e\u7684\u79cd \u65cf\uff0c\u7531\u90a3\u5929\u5f00\u59cb\uff0c\u6211\u5168\u529b\u7814\u7a76\u6c49\u4eba\u7684\u6587\u5316\u3001\u7ecf\u53f2\uff0c\u8001\u5b9e\u8bf4\uff0c\u56e0\u6b64\u803d\u8bef\u4e86\u6211\u5f88\u591a\u6b66\u529f\u4e0a\u7684\u6210 \u5c31\uff0c\u5728\u5c0f\u897f\u5929\u96f7\u97f3\u5bfa\u4e2d\u4f4f\u4e86\u4e03\u5e74\uff0c\u6211\u624d\u79bb\u5f00\u4e86\u90a3\u91cc\uff0c\u4f46\u4ed6\u4eec\u4ecd\u7136\u8feb\u6211\u670d\u4e0b\u4e86\u836f\u7269\uff0c\u624d\u9001\u6211\u79bb \u5f00\uff0c\u738b\u6606\uff0c\u6211\u552f\u4e00\u80fd\u591f\u63d0\u4f9b\u7ebf\u7d22\u7684\uff0c\u5c31\u662f\u6211\u8fdb\u5165\u5c0f\u897f\u5929\u65f6\uff0c\u662f\u5728\u4e00\u8258\u8239\u4e0a\u670d\u4e0b\u8ff7\u9b42\u836f\u7269\uff0c\u79bb \u5f00\u5c0f\u897f\u5929\u65f6\u9192\u8fc7\u6765\uff0c\u4e5f\u5728\u4e00\u8258\u8239\u4e0a\uff0c\u90a3\u662f\u4e00\u4e2a\u5927\u6e56\uff0c\u5982\u82e5\u738b\u5144\u8981\u627e\u90a3\u5ea7\u5927\u6e56\uff0c\u5144\u5f1f\u53ef\u4ee5\u5e26 \u8def\u3002\u201d \u3000\u3000\u201c\u6211\u76f8\u4fe1\u9a6c\u5144\u7684\u8bdd\u2026\u2026\u201d \u3000\u3000\u987f\u4e86\u4e00\u987f\u3002 \u3000\u3000\u738b\u5f64\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u738b\u67d0\u65e0\u610f\u63a2\u7a76\u5c0f\u897f\u5929\u7684\u795e\u6c81\uff0c\u4e5f\u65e0\u610f\u548c\u9b54\u6559\u4e3a\u654c\uff0c\u7167\u9a6c\u5144\u7684\u8bf4\u6cd5\uff0c\u4ed6 \u4eec\u4e5f\u53ea\u7b97\u4e00\u4e2a\u6b66\u6797\u4e2d\u7684\u5b97\u6d3e\uff0c\u8fd9\u548c\u5c11\u6797\u3002 \u3000\u3000\u6b66\u5f53\u4f3c\u4e4e\u6ca1\u6709\u4ec0\u4e48\u4e0d\u540c\uff1f\u4ed6\u4eec\u5982\u679c\u4fb5\u5165\u4e2d\u539f\uff0c\u5176\u5bf9\u624b\uff0c\u4e5f\u53ea\u662f\u6b66\u6797\u4eba\u7269\uff0c\u4ee5\u5144\u5f1f\u7684\u8eab\u4efd \u800c\u8a00\uff0c\u4f3c\u5df2\u8131\u51fa\u6b66\u6797\u9053\u4e0a\uff0c\u6211\u6700\u5173\u5fc3\u7684\u4e8b\uff0c\u662f\u4e09\u516c\u4e3b\u7684\u5e73\u5b89\uff0c\u7687\u4e0a\u7684\u5eb7\u6cf0\uff0c\u6240\u4ee5\uff0c\u738b\u67d0\u6709\u4e00 \u4e2a\u4e0d\u60c5\u4e4b\u6c42\uff0c\u5e0c\u671b\u9a6c\u5144\u80fd\u52a9\u4e00\u81c2\u4e4b\u529b\uff1f\u201c \u3000\u3000\u9a6c\u6587\u4e2d\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u8bf7\u8bf4\u5427\uff0c\u6587\u4e2d\u529b\u80fd\u6240\u53ca\u7684\u5730\u65b9\uff0c\u7edd\u4e0d\u63a8\u8f9e\u3002\u201d \u3000\u3000\u201c\u8bf7\u9a6c\u5144\u6d3e\u4e09\u4e24\u4f4d\u5fe0\u8bda\u53ef\u9760\uff0c\u6b66\u529f\u9ad8\u5f3a\u7684\u4eba\uff0c\u8ddf\u738b\u67d0\u4e00\u8d77\u56de\u4eac\uff0c\u4fdd\u62a4\u4e09\u516c\u4e3b\uff0c\u4e00\u5e74\u4e3a \u671f\uff0c\u4e00\u5e74\u5185\u4e09\u516c\u4e3b\u6ca1\u6709\u4ec0\u4e48\u53d8\u5316\uff0c\u4ed6\u4eec\u5c31\u53ef\u4ee5\u56de\u6765\u4e86\uff0c\u4f46\u738b\u67d0\u916c\u62a5\u9a6c\u5144\u7684\uff0c\u662f\u4fdd\u4f4f\u4f60\u8fd9\u4e2a\u5de1 \u629a\u7684\u4f4d\u7f6e\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u201d\u9a6c\u6587\u4e2d\u7b54\u5e94\u7684\u975e\u5e38\u723d\u5feb\uff1a\u201c\u8fd9\u4e9b\u5e74\u6765\uff0c\u6211\u6df1\u53d7\u6c49\u4eba\u7684\u6587\u5316\u5f71\u54cd\uff0c\u65e9\u5df2\u65e0\u897f\u5f52\u7684 \u6253\u7b97\uff0c\u4e09\u5144\u5982\u4fdd\u4f4f\u6211\u8fd9\u4f4d\u5de1\u629a\u7684\u4f4d\u7f6e\uff0c\u501f\u91cd\u8fd9\u4e2a\u5b98\u4f4d\uff0c\u53ef\u4ee5\u8bad\u517b\u4e00\u6279\u4eba\u624b\uff0c\u4f5c\u4e3a\u81ea\u4fdd\u7684\u672c \u94b1\u3002\u201d \u3000\u3000\u738b\u5f64\u867d\u7136\u660e\u767d\u9a6c\u6587\u4e2d\u6240\u8c13\u81ea\u4fdd\u542b\u610f\uff0c\u4f46\u4ecd\u7136\u5fcd\u4e0d\u4f4f\u95ee\u4e86\u4e00\u53e5\uff0c\u9053\uff1a\u201c\u9a6c\u5144\u6240\u8c13\u7684\u81ea\u4fdd\uff0c \u53ef\u5426\u8bf4\u5f97\u660e\u767d\u4e00\u4e9b\uff1f\u201d \u3000\u3000\u201c\u5176\u5b9e\uff0c\u738b\u5144\u65e9\u5df2\u4e86\u89e3\u4e8e\u80f8\uff0c\u4f46\u8981\u6211\u660e\u6717\u7684\u627f\u8bfa\u4e00\u53e5\uff0c\u4e5f\u65e0\u4e0d\u53ef\uff0c\u6211\u62e5\u6709\u4e00\u6279\u5f3a\u5927\u7684\u529b \u91cf\uff0c\u624d\u80fd\u62d2\u7edd\u96f7\u97f3\u5bfa\u5bf9\u6211\u7684\u53ec\u5524\uff0c\u96f7\u97f3\u5bfa\u51fa\u8eab\u7684\u4eba\uff0c\u6c38\u8fdc\u90fd\u4e0d\u80fd\u62d2\u7edd\u4ed6\u4eec\u4ee4\u8c15\u2026\u2026\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u795e\u60c5\u8083\u7136\u7684\u63a5\u9053\uff1a\u201c\u5f53\u7136\uff0c\u5982\u679c\u7687\u4e0a\u903c\u6211\u592a\u7d27\uff0c\u8fd9\u80a1\u529b\u91cf\u4e5f\u662f\u4fdd\u6211\u9000\u56de\u897f\u57df\u7684\u672c \u94b1\u3002\u201d \u3000\u3000\u201c\u591a\u8c22\u9a6c\u5144\u5766\u8bda\u76f4\u8a00\uff0c\u738b\u67d0\u4e5f\u4f1a\u5c3d\u6211\u6240\u80fd\u4fdd\u4f4f\u9a6c\u5144\u7684\u804c\u4f4d\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u7ad9\u8d77\u8eab\u5b50\uff0c\u62b1\u62f3\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u7ed9\u6211\u4e94\u5e74\u65f6\u95f4\uff0c\u4e94\u5e74\u540e\u6211\u4f1a\u8f9e\u5b98\u5f52\u9690\u3002\u201d \u3000\u3000\u201c\u4ee5\u9a6c\u5144\u7684\u6cbb\u4e8b\u624d\u80fd\uff0c\u8f9e\u5b98\u5f52\u9690\uff0c\u4e0d\u4f46\u662f\u671d\u5ead\u7684\u635f\u5931\uff0c\u4e5f\u662f\u4e07\u6c11\u82cd\u751f\u7684\u4e0d\u5e78\u2026\u2026\u201d\u2019 \u201c\u8a00\u91cd\u4e86\uff0c\u8a00\u91cd\u4e86\uff01\u201d\u9a6c\u6587\u4e2d\u7b11\u9053\uff1a\u201c\u738b\u5144\u53ef\u5426\u803d\u6401\u4e00\u5929\u518d\u8d70\uff01\u201d \u3000\u3000\u201c\u5982\u679c\u4eca\u5929\u8d70\u4e0d\u6210\uff0c\u53ea\u597d\u518d\u62d6\u4e00\u5929\u3002\u201d \u3000\u3000\u201c\u6211\u770b\u4e09\u5144\u5f52\u5fc3\u4f3c\u7bad\uff0c\u5012\u662f\u4e0d\u6562\u591a\u7559\u4e86\uff0c\u6211\u7acb\u523b\u5c31\u53bb\u9009\u62d4\u4eba\u624b\uff0c\u4e5f\u8bb8\u53ef\u4ee5\u8ba9\u4e09\u5144\u5348\u540e\u52a8 \u8eab\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u9009\u6d3e\u6765\u7684\u4eba\uff0c\u90fd\u662f\u4e09\u5341\u5de6\u53f3\uff0c\u540d\u4e0d\u89c1\u7ecf\u4f20\u7684\u4eba\uff0c\u4ee5\u738b\u5f64\u7684\u7ecf\u9a8c\u4e4b\u4e30\uff0c\u4e5f\u77a7\u4e0d\u51fa\u4ed6 \u4eec\u662f\u6c49\u4eba\u6216\u662f\u8499\u53e4\u5b50\u5f1f\u3002 \u3000\u3000\u4f46\u738b\u5f64\u770b\u5f97\u51fa\u4ed6\u4eec\u6709\u9ad8\u660e\u7684\u6b66\u529f\uff0c\u53cc\u76ee\u5f00\u5408\u4e4b\u95f4\uff0c\u795e\u5149\u5982\u7535\u3002 \u3000\u3000\u4ed6\u4eec\u90fd\u6709\u4e00\u4e2a\u6c49\u4eba\u7684\u540d\u5b57\uff0c\u8981\u4e00\u5207\u542c\u4ece\u738b\u7edf\u9886\u7684\u4ee4\u6e1d\uff0c\u4e0d\u5f97\u64c5\u81ea\u4f5c\u4e3b\u884c\u52a8\u3002 \u3000\u3000\u738b\u5f64\u5e26\u7740\u4e09\u4eba\u52a0\u4e0a\u8d75\u4fdd\u3001\u9648\u5b8f\uff0c\u516d\u4e2a\u4eba\u5348\u540e\u767b\u7a0b\uff0c\u51fa\u4e86\u5f00\u5c01\u5e9c\uff0c\u71d5\u98de\u65e9\u5df2\u57ce\u5916\u7b49\u5019\uff0c\u540c \u65f6\uff0c\u4e5f\u5e26\u6765\u4e86\u4e00\u4e2a\u60ca\u5947\u7684\u6d88\u606f\u3002 \u3000\u3000\u6709\u5927\u6279\u6b66\u6797\u4eba\u7269\uff0c\u6e21\u8fc7\u9ec4\u6cb3\u5317\u4e0a\u3002 \u3000\u3000\u738b\u5f64\u76b1\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u8eab\u4efd\u5462\uff1f\u201d \u3000\u3000\u201c\u975e\u5e38\u590d\u6742\u2026\u2026\u201d\u71d5\u98de\u770b\u4e86\u9a6c\u6c0f\u4e09\u82f1\u4e00\u773c\uff0c\u89c1\u738b\u5f64\u6ca1\u6709\u53cd\u5e94\uff0c\u624d\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u4f3c\u662f \u90fd\u6709\u610f\u9690\u5bc6\u8eab\u4efd\uff0c\u7ecf\u8fc7\u4e86\u4e00\u756a\u6539\u88c5\u6613\u5bb9\uff0c\u8fd9\u5c31\u5f15\u8d77\u4e86\u6211\u5f88\u5927\u7684\u597d\u5947\uff0c\u6697\u4e2d\u8ffd\u8e2a\u67e5\u770b\uff0c\u624d\u53d1\u89c9 \u4e8b\u6001\u5f02\u5e38\u2026\u2026\u201d \u3000\u3000\u201c\u53ef\u662f\u67e5\u51fa\u4e86\u4ed6\u4eec\u7684\u7528\u5fc3\u76ee\u7684\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\uff0c\u53ea\u662f\u53d1\u89c9\u4e86\u4ed6\u4eec\u5176\u4e2d\u4e00\u90e8\u5206\u4eba\u7684\u8eab\u4efd\u3002\u201d\u71d5\u98de\u9053\uff1a\u201c\u6709\u548c\u5c1a\uff0c\u4e5f\u6709\u9053\u58eb\uff0c\u4f46\u4ed6 \u4eec\u6362\u4e86\u5e73\u5e38\u4eba\u7684\u8863\u670d\uff0c\u4f3c\u662f\u5c3d\u91cf\u5728\u63a9\u9970\u8eab\u4efd\u3002\u201d \u3000\u3000\u738b\u5f64\u5fc3\u5934\u9707\u52a8\u4e86\uff0c\u4e00\u52d2\u9a6c\u7f30\uff0c\u505c\u4e86\u4e0b\u6765\uff0c\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u4ed6\u4eec\u662f\u5c11\u6797\u548c\u6b66\u5f53\u95e8\u4e0b\u5f1f\u5b50\uff1f\u201d \u3000\u3000\u201c\u9664\u4e86\u5c11\u6797\u3001\u6b66\u5f53\u95e8\u4e0b\u7684\u5f1f\u5b50\u4e4b\u5916\u2026\u2026\u2026\u201d\u71d5\u98de\u8bf4\uff1a\u201c\u5c5e\u4e0b\u5c31\u60f3\u4e0d\u901a\uff0c\u8fd8\u6709\u4ec0\u4e48\u8eab\u5177\u6b66 \u529f\u7684\u65b9\u5916\u4e4b\u4eba\u5728\u9ec4\u6cb3\u897f\u5cb8\u8d70\u52a8\u4e86\uff0c\u5728\u8fd9\u91cc\u7ecf\u5e38\u6709\u4ed6\u4eec\u51fa\u73b0\u7684\u884c\u8e2a\uff0c\u5b9e\u5728\u7528\u4e0d\u7740\u63a9\u9970\u8eab\u4efd\u7684 \u5440\uff1f\u201d \u3000\u3000\u738b\u5f64\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u65f6\u5019\u7684\u4e8b\uff1f\u201d \u3000\u3000\u201c\u6628\u5929\u4e0a\u5348\u53d1\u73b0\u4e86\u7b2c\u4e00\u6279\u4eba\u2026\u2026\u201d\u71d5\u98de\u4e00\u9762\u601d\u7d22\uff0c\u4e00\u9762\u8bf4\u9053\uff1a\u201c\u5230\u4eca\u5929\u5348\u65f6\u4e3a\u6b62\uff0c\u4e00\u5171 \u53d1\u73b0\u4e86\u4e94\u6279\u53ef\u7591\u7684\u4eba\u7269\uff0c\u9664\u4e86\u548c\u5c1a\u3001\u9053\u58eb\u4e4b\u5916\uff0c\u8fd8\u6709\u5f88\u591a\u6b66\u6797\u4eba\u7269\uff0c\u552f\u4e00\u76f8\u540c\u4e4b\u5904\uff0c\u4ed6\u4eec\u90fd \u4f3c\u6709\u610f\u63a9\u9970\u81ea\u5df1\u7684\u8eab\u4efd\uff0c\u800c\u4e14\uff0c\u5168\u90e8\u5317\u4e0a\u2026\u2026\u201d \u3000\u3000\u201c\u9664\u4e86\u5c11\u6797\u3001\u6b66\u5f53\u4e4b\u5916\uff0c\u8fd8\u6709\u4e9b\u4ec0\u4e48\u4eba\u7269\u5462\uff1f\u201d\u738b\u5f64\u6c89\u91cd\u7684\u95ee\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u5426\u6df7\u5728\u4e00 \u8d77\uff0c\u6216\u662f\u5404\u8d70\u5404\u7684\uff1f\u4e94\u6279\u4eba\u52a0\u8d77\u6765\uff0c\u6709\u591a\u5c11\u4eba\uff1f\u201d \u3000\u3000\u201c\u5c5e\u4e0b\u4e0d\u80fd\u80af\u5b9a\u4ed6\u4eec\u7684\u8eab\u4efd\uff0c\u4e0d\u8fc7\uff0c\u53d1\u89c9\u4e86\u4e00\u6279\u4eba\u5e26\u7740\u56db\u5c3a\u4ee5\u4e0a\u7684\u957f\u5200\uff0c\u867d\u7136\u5916\u9762\u7528\u9752 \u5e03\u5305\u7740\uff0c\u4f46\u4e00\u773c\u4e4b\u95f4\uff0c\u4ecd\u53ef\u4ee5\u770b\u5f97\u51fa\u6765\uff0c\u597d\u50cf\u662f\u795e\u5200\u5802\u7684\u4eba\u2026\u2026\u201d\u71d5\u98de\u8bf4\uff1a\u201c\u4e5f\u8bb8\u662f\u6b66\u6797\u4e2d \u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u6545\uff1f \u3000\u3000\u201c\u6c5f\u5357\u795e\u5200\u5802\uff0c\u662f\u6700\u8fd1\u4e09\u5341\u5e74\u53d1\u5c55\u6700\u5feb\u7684\u4e00\u652f\u6b66\u6797\u5b97\u6d3e\uff0c\u4e0d\u9519\uff0c\u4ed6\u4eec\u7528\u7684\u5200\uff0c\u957f\u6709\u56db\u5c3a \u516b\u5bf8\uff0c\u975e\u5e38\u7279\u6b8a\uff0c\u53ef\u662f\u4e00\u5411\u90fd\u5728\u6c5f\u5357\u6d3b\u52a8\uff0c\u8db3\u8ff9\u4e0d\u6e21\u957f\u6c5f\uff0c\u600e\u4e48\u51fa\u73b0\u5728\u5f00\u5c01\u5462\uff1f\u2026\u2026\u201d\u738b\u5f64 \u7684\u76ee\u5149\u51dd\u6ce8\u5728\u71d5\u98de\u7684\u8138\u4e0a\u3002 \u3000\u3000\u201c\u4e94\u6279\u4eba\uff0c\u5927\u7ea6\u6709\u56db\u4e94\u5341\u4e2a\u2026\u2026\u201d\u71d5\u98de\u9053\uff1a\u201c\u8fd9\u4e9b\u662f\u9053\u7ecf\u5f00\u5c01\u5e9c\u7684\u4eba\uff0c\u5176\u4ed6\u8def\u4e0a\u662f\u5426\u8fd8 \u6709\uff0c\u6216\u8005\u5c5e\u4e0b\u6ca1\u6709\u53d1\u73b0\u7684\uff0c\u90a3\u5c31\u65e0\u6cd5\u6570\u8ba1\u4e86\uff1f\u201d \u3000\u3000\u201c\u8fd9\u5df2\u7ecf\u662f\u5f88\u5927\u89c4\u6a21\u7684\u884c\u52a8\u4e86\uff0c\u96be\u9053\u90af\u90f8\u9053\u4e0a\uff0c\u771f\u7684\u53d1\u751f\u4e86\u4ec0\u4e48\u53d8\u6545\u2026\u2026\u201d\u738b\u5f64\u6c89\u541f\u4e86 \u4e00\u9635\uff0c\u9053\uff1a\u201c\u4e0d\u5bf9\u5440\uff01\u8fd9\u4e00\u8def\u90fd\u6709\u4e1c\u5382\u548c\u9526\u8863\u536b\u4eba\u5458\u9a7b\u5b88\uff0c\u6709\u4e8b\u60c5\uff0c\u54b1\u4eec\u5e94\u8be5\u65e9\u5df2\u5f97\u5230\u6d88\u606f \u624d\u5bf9\uff1f\u201d \u3000\u3000\u201c\u539f\u6765\u7edf\u9886\u4e0d\u77e5\u9053\u8fd9\u4ef6\u4e8b\u60c5\u2026\u2026\u201d\u71d5\u98de\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u5e73\u5e38\u4e0d\u76f8\u5f80\u6765\uff0c\u600e\u4e48\u4f1a\u7a81\u7136\u5728\u4e00\u8d77 \u51fa\u73b0\uff1f\u8fd9\u4e2d\u95f4\u4ee3\u8868\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u738b\u5f64\u56de\u987e\u4e86\u9a6c\u6c0f\u4e09\u82f1\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4e09\u4f4d\u542c\u8bf4\u8fc7\u8fd9\u4ef6\u4e8b\u4e48\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\uff01\u201d\u9a6c\u82f1\u4e3a\u4e09\u4eba\u4e4b\u9996\uff0c\u4e00\u5207\u4e8b\u60c5\u90fd\u7531\u4ed6\u51fa\u9762\u56de\u7b54\uff0c\u9053\uff1a\u201c\u6211\u4eec\u7edd\u5927\u90e8\u4efd\u7684\u65f6\u95f4\uff0c \u90fd\u662f\u5728\u4e60\u7ec3\u6b66\u529f\uff0c\u5bf9\u6c5f\u6e56\u4e0a\u7684\u4e8b\u52a1\uff0c\u5168\u4e0d\u4e86\u89e3\u3002\u201d \u3000\u3000\u738b\u5f64\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u7684\u6b66\u529f\uff0c\u53ef\u90fd\u662f\u9a6c\u5de1\u629a\u4eb2\u81ea\u4f20\u6388\u7684\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\uff01\u201d\u9a6c\u82f1\u9053\uff1a\u201c\u4e3b\u4eba\u5076\u800c\u4f20\u6388\u4e00\u4e9b\uff0c\u5341\u51e0\u5e74\u6765\uff0c\u4e3b\u4eba\u53ea\u6307\u70b9\u4e86\u6211\u4eec\u51e0\u6b21\u3002\u201d \u3000\u3000\u738b\u5f64\u5fc3\u5934\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u201c\u5341\u51e0\u5e74\u6765\uff0c\u9a6c\u6587\u4e2d\u53ea\u6307\u70b9\u4e86\u4ed6\u4eec\u51e0\u6b21\uff0c\u90a3\u662f\u8bf4\u8fd9\u4e09\u4eba\u81f3\u5c11\u7ec3\u4e86 \u5341\u51e0\u5e74\u4ee5\u4e0a\u7684\u6b66\u529f\u4e86\uff0c\u4ed6\u4eec\u6ca1\u6709\u5916\u52a1\uff0c\u96c6\u4e2d\u7cbe\u795e\u7ec3\u6b66\uff0c\u8fd9\u4e9b\u5e74\u7684\u6210\u5c31\u5b9a\u6781\u53ef\u89c2\uff0c\u4f46\u4e0d\u77e5\u4ed6\u4eec \u6388\u4e1a\u5e08\u7236\u53c8\u662f\u4f55\u7b49\u4eba\u7269\uff1f\u201d \u3000\u3000\u201c\u6587\u4e2d\u5144\u516c\u52a1\u7e41\u91cd\uff0c\u65e0\u6687\u6307\u70b9\u4f60\u4eec\u7684\u6b66\u529f\u2026\u2026\u201d\u738b\u5f64\u95ee\u9053\uff1a\u201c\u90a3\u662f\u4ec0\u4e48\u4eba\u4f20\u6388\u4f60\u4eec\u7684\u6280 \u827a\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u5b66\u5f97\u5341\u5206\u7e41\u6742\u2026\u2026\u201d\u9a6c\u82f1\u8bf4\uff1a\u201c\u8d75\u5e08\u7236\u662f\u6700\u5e38\u4f20\u6388\u6211\u4eec\u6b66\u529f\u7684\u4eba\uff0c\u53e6\u5916\u8fd8\u6709\u4e3b\u4eba \u9080\u8bf7\u7684\u9ad8\u4eba\uff0c\u4ed6\u4eec\u4e0d\u8bf4\u59d3\u540d\uff0c\u5728\u90a3\u7559\u4e0a\u4e09\u4e24\u4e2a\u6708\uff0c\u77ed\u7684\u53ea\u80af\u7559\u4e2a\u5341\u5929\u516b\u5929\u7684\uff0c\u6307\u70b9\u4e86\u6211\u4eec\u4e00 \u4e9b\u6b66\u529f\uff0c\u5c31\u79bb\u5f00\u4e86\u90a3\u91cc\u3002\u6211\u4eec\u7b97\u4e0d\u4e0a\u662f\u4ed6\u4eec\u7684\u5f1f\u5b50\uff0c\u4e5f\u6ca1\u6709\u5e08\u5f92\u7684\u60c5\u4efd\uff0c\u4ed6\u4eec\u4f20\u6388\u6211\u4eec\u7684\u6b66 \u529f\uff0c\u5927\u90e8\u5206\u662f\u4e3b\u4eba\u91cd\u91d1\u793c\u8058\u6765\u7684\uff0c\u4e5f\u6709\u5c11\u6570\uff0c\u662f\u4e3b\u4eba\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u201c\u4f60\u4eec\u6709\u51e0\u767e\u4eba\uff0c\u662f\u9a6c\u5de1\u629a\u624b\u4e0b\u4e00\u80a1\u5f3a\u5927\u7684\u4fdd\u5883\u5b89\u6c11\u529b\u91cf\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u2026\u2026\u201d\u9a6c\u82f1\u9053\uff1a\u201c\u5927\u4eba\u8bf4\u7684\u662f\u4e3b\u4eba\u624b\u4e0b\u7684\u94c1\u9a91\u519b\uff0c\u6211\u4eec\u53ea\u6709\u5341\u4e2a\u4eba\uff0c\u7b97\u662f\u4e3b\u4eba\u7684 \u8fd1\u536b\u5427\uff1f\u201d \u3000\u3000\u4ed6\u4e0d\u80af\u547c\u53eb\u9a6c\u5de1\u629a\u804c\u8854\uff0c\u6216\u662f\u9a6c\u5927\u4eba\uff0c\u4e00\u76f4\u4ee5\u4e3b\u4eba\u79f0\u547c\uff0c\u5f7c\u6b64\u7684\u60c5\u4e49\uff0c\u663e\u7136\u5df2\u8d85\u8fc7\u5b98\u65b9 \u7684\u5c5e\u4ece\u5173\u7cfb\uff0c\u8fd9\u4e9b\u4eba\u662f\u9a6c\u6587\u4e2d\u624b\u4e0b\u7684\u6b7b\u58eb\uff0c\u4e5f\u662f\u771f\u6b63\u7684\u8fd1\u8eab\u4ece\u536b\u3002 \u3000\u3000\u738b\u5f64\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u4f60\u4eec\u4ece\u672a\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\u8fc7\uff0c\u4e5f\u6ca1\u6709\u548c\u4eba\u52a8\u624b\u7684\u7ecf\u9a8c \u4e86\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\uff0c\u8bba\u5b9e\u6218\u7ecf\u9a8c\uff0c\u6211\u4eec\u5dee\u4e86\u4e00\u4e9b\uff0c\u4e0d\u8fc7\uff0c\u5927\u4eba\u53ef\u4ee5\u653e\u5fc3\uff0c\u6211\u4eec\u5b66\u7684\u6280\u827a\u535a\u6742\uff0c\u7528\u4e8e \u5bf9\u654c\uff0c\u5e94\u8be5\u6709\u5f3a\u5927\u7684\u6548\u7528\u2026\u2026\u201d \u3000\u3000\u987f\u4e86\u4e00\u987f\u3002 \u3000\u3000\u9a6c\u82f1\u795e\u60c5\u8083\u7136\u7684\u53c8\u9053\uff1a\u201c\u4e3b\u4eba\u5df2\u7ecf\u4ea4\u5f85\u4e86\u6211\u4eec\uff0c\u738b\u5927\u4eba\u4ea4\u5f85\u7684\u4e8b\u60c5\uff0c\u4e00\u5b9a\u8981\u5168\u529b\u4ee5\u8d74\uff0c \u6b7b\u800c\u540e\u5df2\uff0c\u6211\u4eec\u5931\u8d25\u4e86\uff0c\u6211\u4eec\u4f1a\u732e\u51fa\u81ea\u5df1\u7684\u6027\u547d\u3002\u201d \u3000\u3000\u597d\u5bb6\u4f19\uff0c\u771f\u6b63\u7684\u6b7b\u58eb\uff0c\u9a6c\u6587\u4e2d\u5429\u5490\u4e00\u53e5\u8bdd\uff0c\u4ed6\u4eec\u5c31\u8d74\u6c64\u8e48\u706b\u3002 \u3000\u3000\u89c6\u6b7b\u5982\u5f52\u3002 \u3000\u3000\u201c\u9a6c\u82f1\uff0c\u6c5f\u6e56\u4e0a\u80fd\u4eba\u5f88\u591a\u2026\u2026\u201d\u738b\u5f64\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u5e0c\u671b\u4f60\u4eec\u4ee5\u5b8c\u6210\u4efb\u52a1\u4e3a\u4e3b\uff0c\u6211\u4e0d\u5e0c \u671b\u4f60\u4eec\u6218\u6b7b\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u6211\u4eec\u4f1a\u5c3d\u5168\u529b\u3002\u201d\u9a6c\u82f1\u606d\u656c\u7684\u56de\u7b54\u3002 \u3000\u3000\u738b\u5f64\u77e5\u9053\u65e0\u6cd5\u5728\u51e0\u4eba\u53e3\u4e2d\u95ee\u51fa\u4ec0\u4e48\u4e86\uff1f\u76ee\u4e0b\uff0c\u80fd\u548c\u4ed6\u8c08\u8bba\u3001\u63a8\u65ad\u654c\u60c5\u7684\uff0c\u53ea\u6709\u4e00\u4e2a\u71d5\u98de \u4e86\u3002 \u3000\u3000\u201c\u8fd9\u4e9b\u4eba\uff0c\u603b\u4e0d\u4f1a\u51b2\u7740\u6211\u4eec\u6765\u7684\u5427\uff1f\u201d\u738b\u5f64\u671b\u7740\u71d5\u98de\u95ee\u3002 \u3000\u3000\u201c\u5b8c\u5168\u65e0\u6cd5\u9884\u6d4b\uff0c\u6211\u5df2\u7ecf\u6253\u542c\u8fc7\u4e86\uff0c\u90af\u90f8\u9053\u4e0a\uff0c\u5e76\u65e0\u4ec0\u4e48\u5927\u4e8b\uff1f \u3000\u3000\u2026\u2026    \u201c\u71d5\u98de\u9053\uff1a\u201d\u8fd9\u4e9b\u5206\u5c45\u5728\u5929\u5357\u5730\u5317\u7684\u4eba\uff0c\u50e7\u3001\u9053\u3001\u4fd7\uff0c\u53c8\u8eab\u4efd\u4e0d\u540c\uff0c\u5374\u80fd\u8d70\u5728 \u4e00\u5904\uff0c\u5f7c\u6b64\u4e0d\u76f8\u654c\u89c6\u3002\u201c \u3000\u3000\u738b\u5f64\u5ffd\u7136\u611f\u5230\u4e00\u80a1\u5f3a\u5927\u7684\u538b\u529b\uff0c\u6d8c\u4e0a\u5fc3\u5934\uff0c\u51dd\u76ee\u6c89\u601d\uff0c\u826f\u4e45\u4e0d\u8bed\u3002 \u3000\u3000\u4ed6\u4e45\u8d70\u6c5f\u6e56\uff0c\u7ecf\u9a8c\u4e30\u5bcc\uff0c\u4f46\u5bf9\u8fd9\u4ef6\u4e8b\uff0c\u5374\u662f\u60f3\u4e0d\u51fa\u4e00\u70b9\u53ef\u4ee5\u6355\u6349\u86db\u4e1d\u9a6c\u8ff9\u3002 \u3000\u3000\u201c\u7edf\u9886\u2026\u2026\u6709\u5907\u65e0\u60a3\u5440\u2026\u2026\u201d\u9648\u5b8f\u63d0\u51fa\u4e86\u610f\u89c1\uff0c\u9053\uff1a\u201c\u8981\u4e0d\u8981\u901a\u77e5\u9a7b\u5730\u5382\u536b\uff0c\u8d76\u6765\u63a5\u5e94 \u554a\uff1f\u201d \u3000\u3000\u201c\u6211\u60f3\uff0c\u4ed6\u4eec\u4e0d\u4f1a\u662f\u5bf9\u4ed8\u6211\u4eec\u7684\u2026\u2026\u201d\u738b\u5f64\u8bf4\uff1a\u201c\u6c5f\u6e56\u4e2d\u6709\u4e00\u9879\u4e0d\u6210\u4e08\u7684\u7ea6\u5b9a\uff0c\u975e\u751f\u6b7b \u4ea4\u5173\uff0c\u4e0d\u548c\u5b98\u65b9\u4e2d\u4eba\u52a8\u624b\u3002\u201d \u3000\u3000\u201c\u7edf\u9886\u2026\u2026\u201d\u71d5\u98de\u8bf4\uff1a\u201c\u90af\u90f8\u9053\u4e0a\u65e0\u4e8b\uff0c\u8fd9\u4e9b\u4eba\u96c6\u4e2d\u4e8e\u6b64\uff0c\u76ee\u7684\u4f55\u5728\u5462\uff1f\u201d \u3000\u3000\u201c\u4f60\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u8d5e\u6210\u9648\u8001\u5f1f\u7684\u610f\u89c1\uff0c\u6709\u5907\u65e0\u60a3\u2026\u2026\u201d\u71d5\u98de\u8bf4\uff1a\u201c\u4e0d\u8fc7\uff0c\u9a7b\u5730\u5382\u536b\uff0c\u4eba\u6570\u6709\u9650\uff0c\u9664\u975e \u7531\u4eac\u4e2d\u8c03\u96c6\u5927\u6279\u4eba\u624b\u8d76\u6765\uff0c\u4f46\u65f6\u95f4\u4e0a\uff0c\u5df2\u6765\u4e0d\u53ca\u4e86\uff0c\u8fdc\u6c34\u96be\u6551\u8fd1\u706b\u2026\u2026\u201d\u76ee\u5149\u4e00\u63a0\u9a6c\u6c0f\u4e09 \u82f1\uff0c\u63a5\u9053\uff1a\u201c\u9a6c\u5de1\u629a\u5b9e\u529b\u96c4\u539a\uff0c\u7edf\u9886\uff0c\u4f55\u4e0d\u5411\u9a6c\u5927\u4eba\u501f\u5175\u6c42\u63f4\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\uff0c\u6211\u5b9e\u5728\u60f3\u4e0d\u901a\uff0c\u4ed6\u4eec\u548c\u5b98\u65b9\u4f5c\u5bf9\u7684\u7406\u7531\uff1f\u201d\u738b\u5f64\u8bf4\uff1a\u201c\u8fd9\u4ef6\u4e8b\u4e0d\u8981\u518d\u8c08\u8d77 \u4e86\u3002\u201d \u3000\u3000\u4ed6\u8eab\u6000\u5bc6\u65e8\uff0c\u53ef\u4ee5\u76f4\u63a5\u8981\u6c42\u5404\u5927\u95e8\u6d3e\u9063\u9ad8\u624b\u76f8\u52a9\uff0c\u7687\u5e1d\u7684\u5723\u65e8\uff0c\u867d\u6b66\u6797\u4e2d\u4eba\uff0c\u4ea6\u4e0d\u6562\u6297 \u62d2\uff0c\u90a3\u662f\u8bdb\u65cf\u706d\u95e8\u5927\u9006\u3002 \u3000\u3000\u71d5\u98de\u4e0d\u6562\u518d\u591a\u8bf4\u8bdd\uff0c\u8d75\u4fdd\u3001\u9648\u5b8f\uff0c\u66f4\u662f\u4e0d\u6562\u518d\u8a00\uff0c\u4e03\u9a91\u5065\u9a6c\uff0c\u653e\u7f30\u75be\u9a70\u3002 \u3000\u3000\u6e21\u8fc7\u9ec4\u6cb3\uff0c\u662f\u4e00\u6761\u5bbd\u9614\u7684\u5927\u9053\uff0c\u4f46\u738b\u5f64\u5374\u53d1\u89c9\u60c5\u52bf\u4e0d\u5bf9\u4e86\u3002 \u3000\u3000\u8fd9\u6761\u8def\uff0c\u4e3a\u5317\u4e0a\u7684\u5b98\u9053\uff0c\u5e73\u65e5\u91cc\u5546\u65c5\u8f7d\u9014\uff0c\u7edc\u7ece\u4e0d\u7edd\uff0c\u4eca\u5929\uff0c\u5374\u662f\u4e00\u8def\u5e7d\u9759\uff0c\u4e0d\u89c1\u4eba \u5f71\u3002 \u3000\u3000\u71d5\u98de\u76ee\u5149\u8f6c\u52a8\uff0c\u56db\u4e0b\u77a7\u7740\uff0c\u8138\u4e0a\u662f\u4e00\u7247\u7591\u8651\u4e0d\u5b89\u4e4b\u8272\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u4e0d\u6562\u8bf4\u8bdd\u3002 \u3000\u3000\u201c\u71d5\u98de\uff0c\u8fd9\u6761\u8def\u6709\u4e9b\u5947\u602a\uff1f\u2026\u2026\u201d\u738b\u5f64\u5fcd\u4e0d\u4f4f\u4e86\uff0c\u63d0\u51fa\u5fc3\u4e2d\u7684\u7591\u95ee\u3002 \u3000\u3000\u201c\u662f\u7684\uff01\u8fd9\u662f\u6761\u9633\u5173\u5927\u9053\u3002\u4e00\u5411\u8f66\u6c34\u9a6c\u9f99\uff0c\u884c\u65c5\u4f17\u591a\uff0c\u73b0\u5728\uff0c\u6b63\u662f\u8d76\u8def\u7684\u65f6\u523b\uff0c\u5374\u4e0d\u89c1 \u884c\u4eba\uff0c\u5c5e\u4e0b\u4ea6\u662f\u89c9\u5f97\u5927\u60bb\u5e38\u60c5\uff1f\u201d\u71d5\u98de\u56de\u7b54\u7684\u5f88\u5c0f\u5fc3\u3002 \u3000\u3000\u738b\u5f64\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u6761\u5b98\u9053\u5f88\u8352\u51c9\uff1f\u201d \u3000\u3000\u539f\u6765\uff0c\u8fd9\u662f\u7247\u9ec4\u6c99\u5e73\u539f\u3001\u4e0d\u9002\u5c45\u4f4f\uff0c\u9664\u4e86\u8def\u8fb9\u7684\u8336\u68da\u4e4b\u5916\uff0c\u4e0d\u89c1\u623f\u820d\u3001\u6751\u843d\u3002 \u3000\u3000\u8fd9\u6761\u8def\uff0c\u738b\u5f64\u4e5f\u8d70\u8fc7\u591a\u6b21\uff0c\u53ea\u662f\u5f80\u5e38\u7684\u884c\u5ba2\u4f17\u591a\uff0c\u4e00\u8def\u90fd\u662f\u7b11\u8c08\u4e4b\u58f0\uff0c\u6b64\u523b\u4e00\u7247\u5e7d\u9759\uff0c \u5c31\u611f\u89c9\u5230\u4e00\u7247\u8352\u51c9\u4e86\u3002 \u3000\u3000\u602a\u4e86\uff0c\u8def\u8fb9\u7684\u8336\u68da\uff0c\u4eca\u5929\u4e5f\u4e0d\u505a\u751f\u610f\u4e86\uff0c\u90fd\u5173\u4e0a\u4e86\u5e97\u95e8\u3002 \u3000\u3000\u201c\u96be\u9053\uff0c\u771f\u6709\u4eba\u8981\u5bf9\u4ed8\u6211\u4eec\u2026\u2026\u201d\u738b\u5f64\u5fc3\u4e2d\u5fd6\u601d\uff0c\u53e3\u4e2d\u5374\u95f4\u9053\uff1a\u201c\u524d\u9762\u591a\u8fdc\uff0c\u624d\u6709\u6751 \u9547\uff1f\u201d \u3000\u3000\u201c\u5927\u7ea6\u4e8c\u5341\u91cc\u5427\uff0c\u5c31\u662f\u5173\u5bb6\u5be8\u4e86\uff0c\u90a3\u662f\u4e00\u5904\u5f88\u5927\u7684\u5e02\u9547\uff0c\u5b98\u9053\u7a7f\u8857\u800c\u8fc7\uff0c\u5e02\u9762\u76f8\u5f53\u7e41 \u534e\u3001\u201d\u71d5\u98de\u9053\uff1a\u201c\u5feb\u9a6c\u6025\u8d76\uff0c\u7247\u523b\u53ef\u8fbe\u2026\u2026\u201d \u3000\u3000\u201c\u53ea\u6015\u662f\u8d70\u4e0d\u4e86\u5566\uff01\u201d\u738b\u5f64\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5f53\u771f\u662f\u88ab\u4f60\u4e0d\u5e78\u8a00\u4e2d\uff0c\u6c5f\u6e56\u9053\u4e0a\u7684\u670b\u53cb \u4eec\uff0c\u5403\u4e86\u864e\u5fc3\u8c79\u80c6\uff0c\u8fde\u5b98\u65b9\u4e2d\u4eba\uff0c\u4e5f\u6562\u622a\u6740\u4e86\u3002\u201d \u3000\u3000\u8bed\u6c14\u4e2d\uff0c\u9690\u5e26\u6012\u610f\u3002 \u3000\u3000\u71d5\u98de\u62ac\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u5341\u4e08\u5916\u7684\u5927\u9053\u4e0a\uff0c\u5e76\u6392\u7ad9\u7740\u4e03\u4e2a\u5168\u8eab\u9ed1\u8863\u7684\u5927\u6c49\uff0c\u5934\u8138\u4e5f\u90fd\u7528\u9ed1\u5e03 \u5305\u8d77\uff0c\u663e\u7136\u662f\u4e0d\u613f\u9732\u51fa\u771f\u9762\u76ee\uff0c\u66b4\u9732\u8eab\u4efd\u3002 \u3000\u3000\u4e03\u5bf9\u4e03\uff0c\u5bf9\u65b9\u4f3c\u662f\u65e9\u6709\u4e86\u5f88\u5468\u5bc6\u7684\u90e8\u7f72\u3002 \u3000\u3000\u201c\u7edf\u9886\uff0c\u4ed6\u4eec\u4e0d\u53ea\u4e03\u4e2a\u4eba\uff0c\u90a3\u91cc\u6709\u4e00\u6761\u6c99\u6c9f\uff0c\u53ef\u9690\u85cf\u767e\u4f4d\u4ee5\u4e0a\u7684\u4eba\u9a6c\u2026\u2026\u201d\u71d5\u98de\u66fe\u4efb\u5f00 \u5c01\u5e9c\u7684\u603b\u6355\u5934\uff0c\u719f\u6089\u9644\u8fd1\u5f62\u52bf\u3002 \u3000\u3000\u738b\u5f64\u4e00\u6536\u7f30\u7ef3\uff0c\u4f46\u5750\u9a91\u5e76\u672a\u505c\u4e0b\uff0c\u4ecd\u7136\u7f13\u7f13\u5411\u524d\u884c\u53bb\uff0c\u76ee\u5149\u5374\u5728\u4e03\u4e2a\u9ed1\u8863\u4eba\u7684\u8eab\u4e0a\u6253 \u8f6c\u3002 \u3000\u3000\u201c\u662f\u795e\u5200\u5802\u7684\u4eba\u2026\u2026\u201d\u738b\u5f64\u770b\u5230\u4e86\u4ed6\u4eec\u7279\u5f02\u7684\u957f\u5200\u3002 \u3000\u3000\u71d5\u98de\u9053\uff1a\u201c\u8fd9\u6279\u7fa4\u96c6\u4e8e\u6b64\u7684\u6c5f\u6e56\u540c\u9053\uff0c\u679c\u7136\u662f\u5bf9\u4ed8\u6211\u4eec\u7684\uff0c\u4ed6\u4eec\u4eba\u6570\u4f17\u591a\u2026\u2026\u201d \u3000\u3000\u201c\u597d\uff01\u4f60\u5feb\u9a6c\u56de\u7a0b\uff0c\u544a\u8bc9\u9a6c\u5de1\u629a\uff0c\u8981\u4ed6\u6d3e\u4eba\u8d76\u6765\u652f\u63f4\u3002\u201d \u3000\u3000\u738b\u5f64\u4f5c\u4e86\u51b3\u5b9a\u3002 \u3000\u3000\u201c\u5c5e\u4e0b\u60f3\u7559\u4e0b\u6765\u2026\u2026\u201d\u71d5\u98de\u9053\uff1a\u201c\u9a6c\u6c0f\u4e09\u82f1\u51fa\u8eab\u5de1\u629a\u7684\u95e8\u4e0b\uff0c\u4eba\u5934\u719f\uff0c\u7531\u4ed6\u4eec\u56de\u57ce\u6c42 \u63f4\uff0c\u6bd4\u5c5e\u4e0b\u9002\u5f53\u591a\u4e86\uff1f\u201d \u3000\u3000\u9a6c\u82f1\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u56de\u4e0d\u53bb\u4e86\uff0c\u54b1\u4eec\u5df2\u88ab\u524d\u540e\u5939\u653b\u3002\u201d \u3000\u3000\u738b\u5f64\u56de\u5934\u4e00\u770b\uff0c\u4e0d\u9519\uff0c\u679c\u7136\u6709\u4e00\u6279\u5168\u8eab\u9ed1\u8863\uff0c\u9ed1\u5e03\u5305\u5934\u7684\u4eba\uff0c\u6b63\u5feb\u6b65\u5954\u884c\u800c\u6765\u3002 \u3000\u3000\u8fd9\u4e00\u6279\u4eba\u624b\u4f17\u591a\uff0c\u81f3\u5c11\u6709\u4e8c\u5341\u4e2a\u4ee5\u4e0a\uff0c\u800c\u4e14\u5175\u5203\u4e0d\u540c\uff0c\u6709\u5200\u6709\u5251\uff0c\u4e5f\u6709\u7c97\u91cd\u7684\u94c1\u68cd\u3002 \u3000\u3000\u4ee5\u738b\u5f64\u6c5f\u6e56\u9605\u5386\u4e4b\u5e7f\uff0c\u4e00\u773c\u4e4b\u95f4\uff0c\u5df2\u5206\u8fa8\u51fa\u6765\uff0c\u90a3\u662f\u7531\u4f5b\u95e8\u7985\u6756\u53d8\u5316\u800c\u6765\u3002 \u3000\u3000\u8fd9\u662f\u5904\u5fc3\u79ef\u8651\u7684\u4f0f\u6740\uff0c\u8fde\u4f7f\u7528\u7684\u66ff\u4ee3\u5175\u5203\u65e9\u5c31\u51c6\u5907\u597d\u4e86\uff0c\u8fd9\u4e2a\u51c6\u5907\u5de5\u4f5c\uff0c\u81f3\u5c11\u6709\u4e8c\u4e09\u4e2a \u6708\u4ee5\u4e0a\u7684\u65f6\u95f4\uff0c\u624d\u80fd\u6709\u5982\u6b64\u4e25\u5bc6\u7684\u6210\u6548\u3002 \u3000\u3000\u6458\u661f\u624b\u738b\u5f64\u672a\u5e94\u8058\u5185\u5bab\u7edf\u9886\u4e4b\u524d\uff0c\u884c\u8d70\u6c5f\u6e56\uff0c\u7ed3\u4ea4\u4e86\u5f88\u591a\u7684\u670b\u53cb\uff0c\u5c11\u6797\u3001\u6b66\u5f53\u4e24\u5927\u95e8\u6d3e \u7684\u638c\u95e8\u4eba\uff0c\u90fd\u548c\u4ed6\u5e38\u76f8\u5f80\u6765\u3002 \u3000\u3000\u738b\u5f64\u5c31\u4efb\u4e86\u5185\u5bab\u7edf\u9886\u4e4b\u540e\uff0c\u6743\u52bf\u751a\u5927\uff0c\u4e5f\u5e2e\u4e86\u5404\u5927\u95e8\u6d3e\u4e0d\u5c11\u7684\u5fd9\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u4ed6\u8ba4\u4e3a\u6b66\u6797\u5404\u5927\u95e8\u6d3e\uff0c\u90fd\u548c\u4ed6\u6709\u7740\u76f8\u5f53\u7684\u4ea4\u60c5\uff0c\u4f55\u51b5\uff0c\u5982\u4eca\u8eab\u6000\u5bc6\u65e8\uff0c\u6709\u8c03\u52a8\u5404 \u5927\u95e8\u6d3e\u4eba\u624b\u7684\u6743\u529b\u3002 \u3000\u3000\u4ed6\u611f\u89c9\u5230\u5904\u5883\u7684\u51f6\u9669\uff0c\u4f46\u5fc3\u4e2d\u53c8\u6709\u6043\u65e0\u6050\uff0c\u884c\u8fd1\u9ed1\u8863\u4eba\u4e09\u4e08\u5de6\u53f3\u5904\uff0c\u7ffb\u8eab\u4e0b\u9a6c\u3002 \u3000\u3000\u71d5\u98de\u3001\u9648\u5b8f\u7b49\uff0c\u4e5f\u968f\u7740\u4e0b\u9a6c\uff0c\u5206\u968f\u8eab\u540e\u3001\u4e24\u4fa7\u3002 \u3000\u3000\u53cc\u65b9\u7acb\u523b\u5f62\u6210\u4e86\u5bf9\u6043\u7684\u5c40\u52bf\u3002 \u3000\u3000\u4e03\u4e2a\u8499\u7740\u5934\u8138\u7684\u9ed1\u8863\u4eba\uff0c\u53f3\u624b\u62ac\u52a8\uff0c\u63e1\u4f4f\u4e86\u957f\u5200\u628a\u67c4\u3002 \u3000\u3000\u738b\u5f64\u5df2\u6697\u4e2d\u6234\u4e0a\u4e86\u7cbe\u5de7\u7684\u62a4\u8155\uff0c\u4e5f\u88c5\u597d\u4e86\u7ec6\u5c0f\u7684\u94a2\u9488\uff0c\u795e\u60c5\u9547\u5b9a\u7684\u6325\u6325\u624b\uff0c\u9053\uff1a\u201c\u5728\u4e0b \u738b\u5f64\uff0c\u8bf8\u4f4d\u662f\u90a3\u4e00\u9053\u4e0a\u7684\u670b\u53cb\uff0c\u62e6\u4f4f\u53bb\u8def\uff0c\u76ee\u7684\u4f55\u5728\uff1f\u201d \u3000\u3000\u4e03\u4e2a\u8499\u9762\u7684\u9ed1\u8863\u4eba\uff0c\u5c45\u4e2d\u7684\u4e00\u4f4d\uff0c\u4f3c\u662f\u9886\u961f\uff0c\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u5982\u82e5\u54b1\u4eec\u80af\u901a\u540d\u62a5\u59d3\uff0c\u4e5f\u7528 \u4e0d\u7740\u7528\u9ed1\u5e03\u5305\u4f4f\u5934\u8138\u4e86\uff0c\u9601\u4e0b\u7528\u4e0d\u7740\u591a\u8d39\u5507\u820c\uff0c\u4e0d\u60f3\u675f\u624b\u5c31\u64d2\uff0c\u90a3\u53ea\u6709\u653e\u624b\u4e00\u6218\u4e86\u3002\u201d \u3000\u3000\u738b\u5f64\u4f4e\u58f0\u9053\uff1a\u201c\u8d75\u4fdd\u653e\u8d77\u70df\u82b1\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u91cc\u4e48\uff1f\u2026\u2026\u201d\u8d75\u4fdd\u9053\uff1a\u201c\u53ea\u6015\u2026\u2026\u201d \u3000\u3000\u201c\u5feb\u4e9b\u65bd\u653e\uff0c\u52a8\u4e0a\u624b\uff0c\u4f60\u5c31\u6ca1\u6709\u673a\u4f1a\u653e\u4e86\u3002\u201d \u3000\u3000\u8d75\u4fdd\u672c\u60f3\u8bf4\u5927\u767d\u5929\uff0c\u53c8\u672a\u9884\u7ea6\uff0c\u653e\u51fa\u53bb\uff0c\u53ea\u6015\u4e5f\u6ca1\u6709\u6548\u7528\uff0c\u4f46\u88ab\u738b\u5f64\u4e00\u559d\uff0c\u4e0d\u6562\u518d\u8bf4\uff0c \u4f38\u624b\u5165\u6000\uff0c\u53d6\u51fa\u4e24\u679a\u4e03\u5f69\u70df\u82b1\uff0c\u6295\u5165\u7a7a\u4e2d\u3002 \u5367\u9f99\u751f\u300a\u71d5\u5b50\u4f20\u5947\u300b \u7b2c\u5341\u4e8c\u56de\u3000\u90af\u90f8\u9053\u4e0a \u3000\u3000\u5c31\u5728\u8d75\u4fdd\u53d6\u51fa\u70df\u82b1\u65f6\uff0c\u4e03\u4e2a\u9ed1\u8863\u4eba\u5df2\u53d1\u52a8\u4e86\u51f6\u5389\u7684\u653b\u52bf\uff0c\u4e03\u67c4\u56db\u5c3a\u516b\u5bf8\u7684\u957f\u5200\uff0c\u5939\u7740\u51cc \u5389\u7684\u5200\u98ce\uff0c\u5206\u5411\u4e03\u4eba\u653b\u53bb\u3002 \u3000\u3000\u738b\u5f64\u53cc\u8155\u6325\u52a8\uff0c\u6321\u5f00\u4e24\u67c4\u957f\u5200\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u4f4f\u624b\uff0c\u8981\u52a8\u624b\u4e5f\u7b49\u738b\u67d0\u8bf4\u5b8c\u51e0\u53e5\u8bdd\u518d\u52a8\u624b \u4e0d\u8fdf\uff1f\u201d \u3000\u3000\u4ed6\u6234\u7684\u62a4\u8155\uff0c\u7531\u8863\u8896\u63a9\u906e\uff0c\u6c5f\u6e56\u4e2d\u77e5\u9053\u8fd9\u4e2a\u79d8\u5bc6\u7684\u4eba\uff0c\u66f4\u662f\u6781\u5c11\uff0c\u770b\u4ed6\u6325\u8155\u4e4b\u95f4\uff0c\u63a5\u4e0b \u4e24\u67c4\u539a\u91cd\u5341\u4f59\u65a4\u3001\u950b\u5229\u7684\u957f\u5200\uff0c\u771f\u88ab\u5413\u4e86\u4e00\u8df3\u3002 \u3000\u3000\u5148\u58f0\u593a\u4eba\uff0c\u518d\u52a0\u4e0a\u90a3\u9635\u5927\u559d\u4e4b\u58f0\uff0c\u4e03\u4e2a\u9ed1\u8863\u4eba\uff0c\u90fd\u4e0d\u81ea\u89c9\u7684\u505c\u4e0b\u4e86\u624b\u3002 \u3000\u3000\u738b\u5f64\u76ee\u5149\u70af\u70af\u7684\u626b\u4e86\u4e03\u4eba\u4e00\u773c\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u622a\u6740\u5b98\u5458\u662f\u706d\u95e8\u5927\u7f6a\uff0c\u52a8\u5230\u5723\u65e8\uff0c\u66f4\u662f\u5c06 \u7978\u8fde\u4e5d\u65cf\uff0c\u795e\u5200\u5802\u8fd9\u51e0\u5e74\u5728\u6c5f\u6e56\u7a9c\u7684\u5f88\u5feb\uff0c\u4f46\u4f60\u4eec\u81ea\u4fe1\u80fd\u6297\u62d2\u6570\u4e07\u5927\u519b\u7684\u56f4\u653b\u4e48\uff1f\u201d \u3000\u3000\u4e03\u4e2a\u9ed1\u8863\u4eba\u663e\u7136\u662f\u542c\u5f97\u5341\u5206\u5165\u795e\uff0c\u7ad9\u7740\u672a\u52a8\u3002 \u3000\u3000\u201c\u4f60\u4eec\u867d\u7136\u8499\u7740\u9762\u76ee\uff0c\u4e5f\u8bb8\u8fd8\u5b58\u4e0b\u4e86\u5fc5\u6b7b\u4e4b\u5fc3\uff0c\u4f46\u662f\u52ab\u5b98\u5f62\u540c\u9020\u53cd\u3002\u548c\u6c5f\u6e56\u4e0a\u79c1\u4eba\u7684\u4ec7 \u6740\u4e0d\u540c\uff0c\u6740\u4eba\u507f\u547d\uff0c\u9020\u53cd\u5374\u8fde\u7d2f\u4e86\u4eb2\u670b\u597d\u53cb\uff0c\u4f60\u4e00\u4eba\u72af\u7f6a\uff0c\u4f1a\u4f7f\u4e0a\u767e\u4eba\u53d7\u4f60\u7275\u626f\uff0c\u8bf8\u4f4d\u53ef\u4ee5 \u4ed4\u7ec6\u7684\u60f3\u60f3\u8fd9\u65f6\uff0c\u7531\u6765\u8def\u6d8c\u73b0\u7684\u9ed1\u8863\u4eba\uff0c\u5df2\u7136\u903c\u8fd1\uff0c\u5f62\u6210\u4e86\u5939\u51fb\u4e4b\u52bf\u3002 \u3000\u3000\u62e6\u4f4f\u53bb\u8def\u7684\u4e03\u4e2a\u9ed1\u8863\u4eba\uff0c\u4f3c\u662f\u5df2\u88ab\u738b\u5f64\u5413\u4f4f\uff0c\u7ad9\u7740\u4e0d\u52a8\u3002 \u3000\u3000\u9ed1\u5e03\u5305\u5934\uff0c\u65e0\u6cd5\u770b\u5230\u4ed6\u4eec\u8138\u4e0a\u7684\u795e\u60c5\u53d8\u5316\uff0c\u4f46\u4ed6\u4eec\u8083\u7acb\u4e0d\u52a8\uff0c\u663e\u662f\u5df2\u5fc3\u751f\u754f\u60e7\u3002 \u3000\u3000\u201c\u738b\u67d0\u4eba\u4e5f\u662f\u6c5f\u6e56\u51fa\u8eab\uff0c\u8eab\u5165\u516c\u95e8\u4e4b\u540e\uff0c\u4e00\u76f4\u6ca1\u6709\u5fd8\u672c\uff0c\u4ece\u4e0d\u5201\u96be\u6c5f\u6e56\u4e0a\u7684\u670b\u53cb\uff0c\u800c\u4e14 \u8fd8\u5e2e\u4e86\u4e0d\u5c11\u7684\u5fd9\uff0c\u5c11\u6797\u3001\u6b66\u5f53\u3001\u534e\u5c71\u3001\u4e10\u5e2e\uff0c\u90fd\u53d7\u8fc7\u738b\u67d0\u4eba\u7684\u7167\u987e\uff0c\u5728\u6211\u80fd\u529b\u4e4b\u5185\uff0c\u51e1\u662f\u627e \u4e0a\u6211\u7684\u6c5f\u6e56\u670b\u53cb\uff0c\u6211\u4ece\u672a\u8ba9\u4ed6\u4eec\u5931\u671b\u8fc7\uff0c\u5404\u4f4d\u4e5f\u8bb8\u6ca1\u6709\u53d7\u5230\u8fc7\u6211\u738b\u67d0\u4eba\u7684\u7167\u5e94\uff0c\u4e0d\u8fc7\uff0c\u5728\u4e0b \u4e5f\u6ca1\u6709\u5f00\u7f6a\u8fc7\u8bf8\u4f4d\u5427\uff01\u795e\u5200\u5802\u96be\u9053\u548c\u738b\u67d0\u7edf\u7387\u7684\u5927\u5185\u4f8d\u536b\u4eec\u7ed3\u6709\u6069\u6028\uff1f\u201d \u3000\u3000\u540e\u9762\u4e00\u53e5\u8bdd\uff0c\u660e\u6717\u7684\u4eae\u51fa\u4e86\u8eab\u4efd\u3002 \u3000\u3000\u201c\u6211\u4eec\u4e0d\u662f\u795e\u5200\u5802\u7684\u4eba\u2026\u2026\u201d\u5c45\u4e2d\u9ed1\u8863\u4eba\u8bf4\uff1a\u201c\u548c\u4f60\u738b\u5927\u4eba\u66f4\u8c08\u4e0d\u4e0a\u4ec0\u4e48\u6069\u6028\uff0c\u6211\u4eec\u662f \u5949\u547d\u884c\u4e8b\uff0c\u5f97\u7f6a\u4e4b\u5904\uff0c\u8fd8\u671b\u738b\u5927\u4eba\u591a\u591a\u539f\u8c05\uff1f\u201d \u3000\u3000\u71d5\u98de\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u56db\u5c3a\u516b\u5bf8\u7684\u65a9\u9a6c\u5200\uff0c\u662f\u795e\u5200\u5802\u7684\u6807\u5e1c\uff0c\u9664\u4e86\u795e\u5200\u5802\u4e2d\u5f1f\u5b50\u5916\uff0c\u4ece \u65e0\u5916\u4eba\u7528\u8fc7\uff0c\u9601\u4e0b\u65e2\u7136\u6562\u51fa\u624b\u62e6\u52ab\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u6562\u627f\u8ba4\u5462\uff1f\u201d \u3000\u3000\u53ea\u542c\u9634\u68ee\u68ee\u7684\u58f0\u97f3\uff0c\u7531\u8eab\u540e\u4f20\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u4e0d\u8981\u7d27\uff0c\u4e00\u4e2a\u4eba\u5982\u662f\u6b7b\u4e86\uff0c\u4e0d\u7ba1\u4ed6\u77e5\u9053\u591a \u5c11\u79d8\u5bc6\uff0c\u4e5f\u662f\u65e0\u6cd5\u8bf4\u51fa\u53bb\u4e86\u3002\u201d \u3000\u3000\u58f0\u97f3\u7531\u8eab\u540e\u4f20\u6765\uff0c\u71d5\u98de\u8f6c\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u4e8c\u5341\u51e0\u4e2a\u9ed1\u5dfe\u8499\u9762\u7684\u9ed1\u8863\u4eba\uff0c\u5e03\u6210\u4e86\u4e00\u4e2a\u5f2f\u6708\u5f62 \u7684\u9635\u52bf\uff0c\u628a\u4e09\u9762\u5168\u5835\u4e86\u8d77\u6765\u3002 \u3000\u3000\u6b63\u9762\u7531\u4e03\u4e2a\u9ed1\u8863\u4eba\u62e6\u4f4f\u4e86\u53bb\u8def\uff0c\u662f\u56db\u9762\u56f4\u5835\u7684\u6253\u6cd5\u3002 \u3000\u3000\u8fd9\u79cd\u9635\u52bf\uff0c\u5728\u6c5f\u6e56\u4e0a\u7684\u6709\u4e2a\u540d\u5802\uff0c\u53eb\u505a\u4e0d\u7559\u6d3b\u53e3\u3002 \u3000\u3000\u5f53\u7136\uff0c\u8fd9\u4e2a\u67b6\u52bf\u6446\u51fa\u6765\uff0c\u90a3\u5c31\u662f\u903c\u7740\u53d7\u5305\u56f4\u7684\u4eba\u6324\u547d\u3002 \u3000\u3000\u8fd9\u65b9\u5f0f\u5bf9\u56f4\u5835\u7684\u4eba\u5e76\u975e\u6709\u5229\uff0c\u4f20\u51fa\u8fd9\u4e2a\u8baf\u606f\uff0c\u552f\u4e00\u7684\u4f5c\u7528\u662f\u8868\u793a\u51fa\u5149\u660e\u78ca\u843d\u3002 \u3000\u3000\u4f46\u738b\u5f64\u660e\u767d\uff0c\u90a3\u7fa4\u4eba\u4e2d\uff0c\u53ef\u80fd\u6709\u4e00\u4e24\u4e2a\u4ed6\u8ba4\u8bc6\uff0c\u65e2\u662f\u8fde\u5934\u8138\u90fd\u7528\u9ed1\u5e03\u5305\u8d77\u6765\u7684\u5077\u88ad\uff0c\u5b9e \u5728\u7528\u4e0d\u7740\u518d\u4f20\u51fa\u8fd9\u4e2a\u8baf\u606f\u4e86\u3002 \u3000\u3000\u201c\u8fd9\u662f\u4e00\u79cd\u8b66\u544a\uff0c\u4e5f\u662f\u4e00\u79cd\u5173\u6000\u3002 \u3000\u3000\u80fd\u6307\u6325\u5077\u88ad\u7684\u4eba\uff0c\u6446\u51fa\u65b9\u9635\uff0c\u663e\u7136\u8eab\u4efd\u4e0d\u4f4e\uff0c\u624d\u80fd\u4f7f\u522b\u4eba\u542c\u547d\u884c\u4e8b\uff0c\u800c\u4e14\uff0c\u4eba\u4e5f\u5c31\u5728\u4eba \u7fa4\u4e4b\u4e2d\u3002 \u3000\u3000\u738b\u5f64\u51dd\u805a\u76ee\u5149\uff0c\u4f3c\u662f\u900f\u8fc7\u5bf9\u9762\u7684\u8499\u9762\u9ed1\u5dfe\uff0c\u770b\u6e05\u695a\u4ed6\u4eec\u7684\u8eab\u4efd\u3002 \u3000\u3000\u53ef\u60dc\uff0c\u4ed6\u6ca1\u6709\u8fd9\u4efd\u80fd\u8010\u3002 \u3000\u3000\u4f46\u4ed6\u662f\u4e45\u5386\u5927\u654c\u7684\u8001\u6c5f\u6e56\u4e86\uff0c\u4e86\u89e3\u5230\u4e86\u76ee\u524d\u5f62\u52bf\u4e4b\u540e\uff0c\u5b8c\u5168\u51b7\u9759\u4e86\u4e0b\u6765\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a \u201c\u6211\u4eec\u4e0d\u80fd\u7a81\u56f4\u4e86\uff0c\u5b9e\u529b\u4e00\u5206\uff0c\u66f4\u5bb9\u906d\u5230\u640f\u6740\uff0c\u773c\u4e0b\u552f\u4e00\u7684\u529e\u6cd5\u662f\u5408\u5728\u4e00\u5904\uff0c\u8fb9\u6218\u8fb9\u8d70\uff0c\u4e0d \u8fc7\uff0c\u8fd9\u4e2a\u673a\u4f1a\u4e5f\u4e0d\u5927\uff0c\u4ee5\u73b0\u5728\u7684\u654c\u4eba\u800c\u8a00\uff0c\u5df2\u8fd1\u4e09\u5341\u4e2a\uff0c\u8fd8\u4e0d\u77e5\u6709\u591a\u5c11\u4eba\u5206\u5e03\u5728\u56db\u5468\u7b49 \u5f85\u3002\u201d \u3000\u3000\u738b\u5f64\u5fc3\u4e2d\u975e\u5e38\u6e05\u695a\uff0c\u4ee5\u81ea\u5df1\u533a\u533a\u4e03\u4eba\u4e4b\u529b\uff0c\u7edd\u5bf9\u65e0\u6cd5\u652f\u6301\u592a\u4e45\uff0c\u4f55\u51b5\uff0c\u6b64\u5730\u4e00\u7247\u5e73\u539f\uff0c \u65e0\u9669\u53ef\u5b88\u3002 \u3000\u3000\u76ee\u7779\u968f\u884c\u516d\u4eba\u4e2a\u4e2a\u51dd\u795e\u6212\u5907\u7684\u4e25\u8083\u8138\u8272\uff0c\u738b\u5f64\u6709\u7740\u5fc3\u5982\u5200\u7ede\u7684\u75db\u82e6\uff0c\u81ea\u5df1\u592a\u6258\u5927\u4e86\uff0c\u5ffd \u7565\u4e86\u71d5\u98de\u8bf4\u51fa\u7684\u8b66\u8baf\uff0c\u81f4\u4f7f\u4e03\u4e2a\u4eba\uff0c\u90fd\u9677\u5165\u4e86\u4e07\u52ab\u4e0d\u590d\u4e4b\u4e2d\u3002 \u3000\u3000\u8fd9\u662f\u5fc5\u6b7b\u7684\u5c40\u9762\uff0c\u800c\u4e14\uff0c\u738b\u5f64\u7684\u4f30\u7b97\u4e2d\uff0c\u4ed6\u4eec\u6491\u4e0d\u5230\u638c\u706f\u65f6\u5206\u3002 \u3000\u3000\u4f46\u95fb\u9a6c\u6770\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8001\u5927\uff0c\u8981\u4e0d\u8981\u8bd5\u8bd5\u4f60\u5e26\u7684\u5341\u91cc\u4fe1\u9999\uff1f\u201d \u3000\u3000\u9a6c\u82f1\u9053\uff1a\u201c\u54b1\u4eec\u79bb\u5f00\u5e9c\u57ce\uff0c\u5df2\u5230\u4e86\u56db\u5341\u91cc\u5916\uff0c\u5341\u91cc\u4fe1\u9999\u8fd8\u4f1a\u6709\u4ec0\u4e48\u7528\uff1f\u201d \u3000\u3000\u9a6c\u6770\u9053\uff1a\u201c\u770b\u60c5\u5f62\uff0c\u5bf9\u65b9\u6765\u7684\u90fd\u662f\u9ad8\u624b\uff0c\u6b64\u5730\u53c8\u65e0\u9669\u53ef\u5b88\uff0c\u52a8\u4e0a\u624b\uff0c\u5c31\u662f\u640f\u547d\u4e4b\u6218\uff0c\u4e0d \u7ba1\u54b1\u4eec\u6740\u6b7b\u591a\u5c11\u654c\u4eba\uff0c\u54b1\u4eec\u7edd\u5bf9\u652f\u6491\u4e0d\u8fc7\u4e24\u4e2a\u65f6\u8fb0\uff0c\u4e24\u4e2a\u65f6\u8fb0\u7684\u5168\u529b\u62da\u6597\uff0c\u6ca1\u6709\u5598\u606f\u4e00\u4e0b\u7684 \u673a\u4f1a\uff0c\u4e0d\u7528\u654c\u4eba\u6740\u6211\u4eec\uff0c\u7d2f\u4e5f\u8be5\u7d2f\u6b7b\u4e86\u3002\u201d \u3000\u3000\u4ed6\u4eec\u662f\u5168\u65e0\u6c5f\u6e56\u9605\u5386\u7684\u4eba\uff0c\u4f46\u5bf9\u4e8b\u60c5\u7684\u770b\u6cd5\u548c\u4f30\u7b97\uff0c\u7adf\u6709\u81ea\u5df1\u7684\u4e3b\u89c1\uff0c\u4f30\u7b97\u7684\u7ed3\u679c\uff0c\u7adf \u548c\u738b\u5f64\u7684\u770b\u6cd5\u5b8c\u5168\u76f8\u540c\u3002 \u3000\u3000\u201c\u5927\u54e5\u2026\u2026\u201d\u9a6c\u8c6a\u8bf4\uff1a\u201c\u6211\u8d5e\u6210\u4e8c\u54e5\u7684\u8bf4\u6cd5\uff0c\u65e2\u7136\u54b1\u4eec\u6b7b\u5b9a\u4e86\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u8bd5\u8bd5\u90a3\u5341\u91cc\u4fe1 \u9999\u5462\uff1f\u81f3\u5c11\uff0c\u6211\u4eec\u628a\u5b83\u70e7\u6389\u4e86\uff0c\u4e0d\u4f1a\u843d\u5165\u654c\u4eba\u4e4b\u624b\u3002\u201d \u3000\u3000\u201c\u8bf4\u7684\u5bf9\u2026\u2026\u201d\u9a6c\u82f1\u63a2\u624b\u5165\u6000\uff0c\u6478\u51fa\u4e00\u4e2a\u5bf8\u8bb8\u957f\u77ed\uff0c\u62c7\u6307\u7c97\u7ec6\u7684\u7ea2\u8272\u4e4b\u7269\u3002 \u3000\u3000\u738b\u5f64\u65e9\u5df2\u542c\u5230\u4e09\u4eba\u5728\u8ba8\u8bba\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u4f46\u4ed6\u4e00\u76f4\u6ca1\u6709\u63d2\u53e3\u3002 \u3000\u3000\u9a6c\u6587\u4e2d\u7684\u7cbe\u5e72\u3001\u795e\u79d8\uff0c\u738b\u5f64\u5fc3\u4e2d\u660e\u767d\uff0c\u8fd9\u4e09\u4eba\u8eab\u4e0a\u5e26\u4e9b\u53e4\u53e4\u602a\u602a\u7684\u4e1c\u897f\uff0c\u81ea\u662f\u4e0d\u8db3\u4e3a \u5947\u3002 \u3000\u3000\u9a6c\u82f1\u65e9\u5df2\u6709\u4e86\u5f88\u5b8c\u7f8e\u7684\u51c6\u5907\uff0c\u7acb\u523b\u6643\u71c3\u706b\u71a0\u5b50\uff0c\u70b9\u8d77\u4fe1\u9999\u3002 \u3000\u3000\u5df2\u7ecf\u903c\u8fd1\u8eab\u4fa7\u7684\u9ed1\u8863\u4eba\uff0c\u770b\u9a6c\u82f1\u7528\u706b\u71c3\u8d77\u4e86\u706b\u70ae\u4f3c\u7684\u4e1c\u897f\uff0c\u4e0d\u77e5\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u5fc3\u4e2d\u90fd\u6709 \u70b9\u754f\u60e7\uff0c\u6709\u4e9b\u8d94\u8d84\u4e0d\u524d\u3002 \u3000\u3000\u4f46\u89c1\u4e00\u7f15\u9752\u70df\uff0c\u51b2\u5929\u800c\u8d77\uff0c\u76f4\u4e0a\u4e91\u9704\u3002 \u3000\u3000\u8fd9\u4fe1\u9999\u6bd4\u8d77\u8d75\u4fdd\u7684\u4e03\u5f69\u70df\u82b1\uff0c\u867d\u7136\u5c11\u4e86\u4e00\u4e9b\u5f69\u82b1\u706b\u7130\uff0c\u4f46\u70df\u6c14\u5374\u6d53\u8fc7\u5f88\u591a\u3002 \u3000\u3000\u71d5\u98de\u7a81\u7136\u5927\u58f0\u559d\u9053\uff1a\u201c\u95ed\u4f4f\u547c\u5438\u2026\u2026\u201d \u3000\u3000\u5175\u4e0d\u538c\u8bc8\uff0c\u4ed6\u8fd9\u4e48\u5927\u58f0\u4e00\u543c\uff0c\u4f7f\u5f97\u5305\u56f4\u56db\u5468\u7684\u9ed1\u8863\u4eba\uff0c\u5927\u90fd\u95ed\u4f4f\u4e86\u547c\u5438\uff0c\u4eba\u4e5f\u5411\u540e\u9000\u4e86 \u4e00\u5927\u6b65\u3002 \u3000\u3000\u738b\u5f64\u5fc3\u4e2d\u5fd6\u9053\uff1a\u8001\u6c5f\u6e56\u679c\u6709\u6025\u667a\uff0c\u81f3\u5c11\uff0c\u53ef\u4ee5\u5ef6\u7f13\u4e00\u4e0b\u4ed6\u4eec\u7684\u653b\u52bf\u3002 \u3000\u3000\u73b0\u5728\u7684\u5904\u5883\uff0c\u5b9e\u5df2\u5230\u7a77\u9014\u672b\u8def\uff0c\u552f\u4e00\u529e\u6cd5\uff0c\u5c31\u662f\u62d6\u5ef6\u654c\u4eba\u7684\u653b\u52bf\u3002 \u3000\u3000\u4f46\u738b\u5f64\u7684\u4e24\u9053\u773c\u795e\uff0c\u5374\u4e00\u76f4\u76ef\u4f4f\u90a3\u4e0a\u5347\u98d8\u6563\u7684\u70df\u6c14\u3002 \u3000\u3000\u8fd9\u5341\u91cc\u4fe1\u9999\uff0c\u4e0d\u7ba1\u5176\u9999\u5473\u5982\u4f55\u6d53\u70c8\uff0c\u738b\u5f64\u5fc3\u4e2d\u660e\u767d\u7edd\u5bf9\u65e0\u6cd5\u4f20\u5165\u5f00\u5c01\u57ce\u4e2d\u3002 \u3000\u3000\u4f46\u738b\u5f64\u53c8\u76f8\u4fe1\u9a6c\u6587\u4e2d\u7684\u8fd9\u4fe1\u9999\u7684\u4f5c\u7528\uff0c\u53e6\u6709\u5999\u7740\u3002 \u3000\u3000\u51fa\u8eab\u9b54\u6559\u7684\u9a6c\u6587\u4e2d\uff0c\u662f\u4e0d\u53ef\u8f7b\u4fae\u7684\u4eba\u3002 \u3000\u3000\u679c\u7136\uff0c\u738b\u5f64\u53d1\u73b0\u4e86\u4e00\u4e2a\u5947\u602a\u7684\u73b0\u8c61\uff0c\u4e00\u53ea\u5728\u9ad8\u7a7a\u98de\u7fd4\u7684\u9ed1\u9e70\uff0c\u7a81\u7136\u6298\u8f6c\u4e86\u8eab\u5b50\uff0c\u8fc5\u5982\u6d41 \u661f\u4e00\u822c\uff0c\u5411\u4e1c\u5357\u98de\u53bb\u3002 \u3000\u3000\u90a3\u65b9\u4f4d\uff0c\u6b63\u662f\u5f00\u5c01\u5e9c\u57ce\u3002 \u3000\u3000\u738b\u5f64\u5e0c\u671b\u90a3\u98de\u9e70\u662f\u4f20\u8fbe\u4fe1\u9999\u7684\u8baf\u606f\uff0c\u4f46\u4ed6\u5374\u4e0d\u6562\u80af\u5b9a\uff0c\u8fd9\u5728\u738b\u5f64\u7684\u6c5f\u6e56\u7ecf\u5386\u4e2d\uff0c\u662f\u4ece\u672a \u9047\u8fc7\u7684\u4e8b\u60c5\u3002 \u3000\u3000\u4f46\u90a3\u98de\u9e70\u5374\u5e26\u53bb\u4e86\u4e00\u7ebf\u7684\u5e0c\u671b\u3002 \u3000\u3000\u738b\u5f64\u8fc5\u5feb\u7684\u96c6\u4e2d\u4e86\u5fc3\u795e\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8bf8\u4f4d\uff0c\u54b1\u4eec\u4ee5\u65b9\u9635\u5bf9\u654c\uff0c\u9a6c\u82f1\u3001\u9a6c\u6770\uff0c\u5206\u62d2\u4e1c\u3001 \u5317\u65b9\u5411\u4e4b\u654c\uff0c\u8d75\u4fdd\u3001\u9648\u5b8f\uff0c\u5206\u62d2\u5357\u3001\u897f\u65b9\u5411\u4e4b\u654c\uff0c\u9a6c\u8c6a\u3001\u71d5\u98de\uff0c\u5c45\u4e2d\u7b56\u5e94\u56db\u9762\uff0c\u65b9\u9635\u4e0d\u53ef\u8d85 \u8fc7\u4e00\u4e08\u65b9\u5706\u3002\u201d \u3000\u3000\u8fd9\u662f\u738b\u5f64\u60f3\u5230\u7684\u5fa1\u654c\u4e4b\u7b56\uff0c\u4ee5\u5be1\u8fce\u4f17\uff0c\u6700\u597d\u628a\u6218\u573a\u9650\u4e8e\u65b9\u4e08\u4e4b\u5730\uff0c\u6108\u5c0f\u6108\u597d\uff0c\u4f7f\u4f17\u591a\u7684 \u654c\u4eba\u65e0\u6cd5\u5168\u6570\u51fa\u624b\u3002 \u3000\u3000\u65b9\u9635\u7acb\u523b\u6446\u6210\uff0c\u738b\u5f64\u53c8\u53d1\u51fa\u7b2c\u4e8c\u9053\u547d\u4ee4\uff0c\u9053\uff1a\u201c\u4e0d\u53ef\u8d2a\u529f\uff0c\u4ee5\u5b88\u4e3a\u4e3b\uff0c\u4e0d\u5e78\u53d7\u4f24\u7684\u7acb\u523b \u9000\u4e0b\uff0c\u7531\u63a5\u8fce\u7684\u4eba\u9012\u8865\uff0c\u65b9\u9635\u4ee5\u5b88\u4e3a\u4e3b\uff0c\u4f46\u63a5\u8fce\u4eba\u7684\u6551\u63f4\uff0c\u5374\u8981\u8fc5\u5feb\u3001\u51cc\u5389\uff0c\u4ee5\u8865\u4e0d \u8db3\u2026\u2026\u201d \u3000\u3000\u738b\u5f64\u65e0\u6cd5\u4e86\u89e3\uff0c\u8fd9\u6279\u5c5e\u4e0b\uff0c\u662f\u5426\u5df2\u5b8c\u5168\u4e86\u89e3\u4ed6\u7684\u7528\u5fc3\uff0c\u4f46\u4ed6\u5df2\u6ca1\u6709\u673a\u4f1a\u518d\u89e3\u8bf4\u4e86\u3002 \u3000\u3000\u56f4\u5e03\u5728\u56db\u5468\u7684\u9ed1\u8863\u4eba\uff0c\u5df2\u5c55\u5f00\u653b\u52bf\u3002 \u3000\u3000\u738b\u5f64\u51b7\u773c\u89c2\u5bdf\uff0c\u5206\u7531\u56db\u9762\u653b\u4e0a\u7684\u9ed1\u8863\u4eba\uff0c\u6709\u7740\u6781\u660e\u663e\u7684\u5206\u522b\u3002 \u3000\u3000\u4e1c\u9762\u7684\u653b\u52bf\uff0c\u5927\u90fd\u662f\u624b\u6267\u94c1\u68cd\u7684\u9ed1\u8863\u4eba\uff0c\u5927\u5f00\u5927\u9616\uff0c\u6325\u68cd\u5982\u98ce\uff0c\u5b8c\u5168\u662f\u786c\u6253\u786c\u653b\u7684\u6253 \u6cd5\u3002 \u3000\u3000\u4ee5\u738b\u5f64\u4e30\u5bcc\u7684\u6c5f\u6e56\u7ecf\u9a8c\u800c\u8a00\uff0c\u5df2\u9690\u9690\u611f\u89c9\u5230\uff0c\u9887\u4f3c\u5c11\u6797\u50e7\u4fa3\u7684\u6253\u6cd5\u3002 \u3000\u3000\u96be\u9053\u5c11\u6797\u771f\u7684\u4e5f\u53c2\u52a0\u4e86\u8fd9\u6b21\u56f4\u653b\uff1f\u4ee5\u76ee\u524d\u5c11\u6797\u638c\u95e8\u65b9\u6587\u548c\u738b\u67d0\u7684\u4ea4\u60c5\u800c\u8a00\uff0c\u8fd9\u662f\u4e0d\u53ef\u80fd \u7684\u4e8b\u554a\uff01 \u3000\u3000\u8fd9\u65f6\uff0c\u770b\u51fa\u4e86\u9a6c\u82f1\u7684\u771f\u5de5\u592b\u4e86\uff0c\u4ed6\u7528\u7684\u662f\u4e00\u67c4\u5bbd\u9762\u539a\u80cc\u5200\uff0c\u53ea\u6709\u4e24\u5c3a\u591a\u957f\uff0c\u4f46\u7075\u6d3b\u591a \u53d8\uff0c\u7adf\u628a\u4e09\u652f\u731b\u5389\u7684\u94c1\u68cd\u63a5\u4f4f\u3002 \u3000\u3000\u5f53\u7136\uff0c\u9a6c\u8c6a\u6bcf\u5728\u5371\u6025\u4e4b\u4e2d\uff0c\u5e38\u80fd\u53ca\u65f6\u65bd\u63f4\uff0c\u4f7f\u6218\u5c40\u4fdd\u6301\u4e86\u6682\u5206\u79cb\u8272\u7684\u5c40\u9762\u3002 \u3000\u3000\u9a6c\u6770\u5e94\u4ed8\u5317\u9762\u7684\u653b\u52bf\uff0c\u4e00\u8272\u4f7f\u5251\u7684\u9ed1\u8863\u4eba\u3002 \u3000\u3000\u4f7f\u738b\u5f64\u60ca\u8bb6\u7684\u662f\uff0c\u90a3\u5251\u6cd5\uff0c\u5927\u90e8\u5206\u662f\u6b66\u5f53\u5251\u6cd5\u3002 \u3000\u3000\u771f\u7684\uff0c\u6b66\u5f53\u95e8\u4e0b\u5f1f\u5b50\uff0c\u4e5f\u53c2\u4e0e\u4e86\u8fd9\u6b21\u56f4\u653b\u3002 \u3000\u3000\u738b\u5f64\u6123\u4f4f\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u4ed6\u548c\u6b66\u5f53\u73b0\u4ee3\u638c\u95e8\u4eba\u7684\u4ea4\u60c5\uff0c\u6df1\u539a\u5c24\u8fc7\u5c11\u6797\u3002 \u3000\u3000\u9a6c\u6770\u4e00\u628a\u5bbd\u9762\u5200\uff0c\u72ec\u62d2\u4e09\u652f\u957f\u5251\u7684\u653b\u52bf\uff0c\u7adf\u4e5f\u4fdd\u6301\u4e0d\u80dc\u4e0d\u8d25\u4e4b\u5c40\uff0c\u9a6c\u8c6a\u5c45\u95f4\u7684\u63a5\u5e94\u3001\u53cd \u51fb\uff0c\u4e5f\u7ed9\u4e86\u4e24\u4eba\u4e0d\u5c11\u7684\u52a9\u529b\u3002 \u3000\u3000\u9a6c\u6c0f\u4e09\u82f1\u7684\u7cbe\u5947\u5200\u6cd5\uff0c\u4f7f\u738b\u5f64\u65e2\u9707\u60ca\u53c8\u4f69\u670d\u3002 \u3000\u3000\u8fd9\u7b49\u7f29\u5c0f\u9632\u5fa1\u7684\u62d2\u654c\u65b9\u9635\uff0c\u4f7f\u4f17\u591a\u7684\u6765\u653b\u9ad8\u624b\uff0c\u5927\u90e8\u5206\u65e0\u7528\u6b66\u4e4b\u5730\u3002 \u3000\u3000\u4e00\u65b9\u6b63\u9762\uff0c\u80fd\u7ef4\u6301\u4e09\u4e2a\u4eba\u653b\u654c\uff0c\u5df2\u7ecf\u662f\u6700\u5927\u7684\u5bb9\u91cf\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u662f\u9ad8\u624b\u640f\u6740\uff0c\u5404\u6709\u5947\u62db\u653b\u654c\uff0c\u65e0\u6cd5\u4e00\u62e5\u800c\u4e0a\uff0c\u6324\u4f5c\u4e00\u56e2\u3002 \u3000\u3000\u738b\u5f64\u8fd9\u5ea7\u62d2\u654c\u65b9\u9635\uff0c\u786e\u662f\u4ee5\u5be1\u51fb\u4f17\u7684\u9ad8\u62db\uff0c\u4e03\u4e2a\u4eba\u53ea\u9700\u627f\u53d7\u5341\u4e8c\u4e2a\u4eba\u7684\u653b\u51fb\u3002 \u3000\u3000\u4f46\u5206\u62d2\u5357\u3001\u897f\u4e24\u9762\u653b\u52bf\u7684\u8d75\u4fdd\u3001\u9648\u5b8f\uff0c\u5374\u662f\u627f\u53d7\u4e0d\u4e86\u5bf9\u65b9\u7684\u5f3a\u731b\u653b\u51fb\u3002 \u3000\u3000\u897f\u65b9\u4ee5\u4e09\u4e2a\u795e\u5200\u5802\u7684\u957f\u5200\u653b\u52bf\u4e3a\u4e3b\uff0c\u5200\u5200\u51f6\u731b\u7edd\u4f26\uff0c\u8d75\u4fdd\u62db\u67b6\u6781\u611f\u5403\u529b\uff0c\u8feb\u5f97\u71d5\u98de\u5168\u529b \u76f8\u52a9\u3002 \u3000\u3000\u5b88\u5728\u5357\u9762\u7684\u9648\u5b8f\uff0c\u5374\u88ab\u4e09\u4e2a\u65bd\u7528\u4e0d\u540c\u5175\u5203\u7684\u9ed1\u8863\u4eba\u5408\u529b\u62a2\u653b\uff0c\u5f62\u52bf\u5371\u9669\u4e07\u5206\uff0c\u56e0\u4e3a\u71d5\u98de \u5df2\u65e0\u6cd5\u62bd\u8eab\u52a9\u62f3\u3002 \u3000\u3000\u8fd9\u5c31\u903c\u5f97\u738b\u5f64\u4e0d\u5f97\u4e0d\u51fa\u624b\u6551\u63f4\uff0c\u624d\u7b97\u628a\u60c5\u5f62\u7a33\u4f4f\u3002 \u3000\u3000\u738b\u5f64\u867d\u7136\u6709\u62a4\u8155\u4e2d\u7684\u98de\u8ba1\u53ef\u7528\uff0c\u4f46\u4e5f\u5e76\u672a\u65bd\u653e\uff0c\u6b64\u60c5\u6b64\u666f\u4e4b\u4e0b\uff0c\u5982\u82e5\u9020\u6210\u4e86\u654c\u4eba\u7684\u4f24 \u4ea1\uff0c\u5fc5\u5c06\u5f15\u8d77\u9ed1\u8863\u4eba\u7684\u5f3a\u70c8\u590d\u4ec7\u4e4b\u5fc3\uff0c\u90a3\u5bf9\u738b\u5f64\u800c\u8a00\uff0c\u5c40\u52bf\u5c31\u5927\u5927\u7684\u4e0d\u5229\u4e86\u3002 \u3000\u3000\u8fd9\u4e9b\u56f4\u6740\u7684\u9ed1\u8863\u4eba\uff0c\u786e\u7136\u7686\u9ad8\u624b\uff0c\u6bcf\u4e2a\u4eba\u90fd\u6709\u72ec\u5f53\u4e00\u9762\u7684\u5b9e\u529b\uff0c\u4f46\u4ed6\u4eec\u5408\u640f\u4e4b\u672f\uff0c\u5374\u914d \u5408\u7684\u4e0d\u662f\u5f88\u597d\u3002 \u3000\u3000\u738b\u5f64\u4ed4\u7ec6\u89c2\u5bdf\u5357\u9762\u4e3b\u653b\u7684\u9ed1\u8863\u4eba\uff0c\u5175\u5203\u867d\u7136\u6df7\u6742\u4e0d\u4e00\uff0c\u4f46\u5374\u6709\u4eba\u65bd\u5c55\u51fa\u4e10\u5e2e\u7684\u6253\u72d7\u68d2 \u6cd5\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\uff0c\u7ed9\u4e88\u738b\u5f64\u7684\u9707\u9a87\uff0c\u7b80\u76f4\u65e0\u6cd5\u5f62\u5bb9\u4e86\u3002 \u3000\u3000\u96be\u9053\u4e10\u5e2e\u4e5f\u53c2\u4e0e\u8fd9\u6b21\u56f4\u6740\u7684\u8ba1\u5212\u3002 \u3000\u3000\u5168\u6c5f\u6e56\u4e2d\u7684\u6b66\u6797\u540c\u9053\uff0c\u4f3c\u662f\u90fd\u8d77\u800c\u548c\u4ed6\u4f5c\u5bf9\u4e86\uff0c\u5f53\u771f\u662f\u4e0d\u53ef\u601d\u8bae\u7684\u4e8b\uff1f \u3000\u3000\u738b\u5f64\u5fc3\u5934\u751f\u51fa\u4e86\u5bd2\u610f\uff0c\u4e3a\u4ec0\u4e48\u4f1a\u6709\u8fd9\u6837\u7684\u5c40\u9762\u2026\u2026 \u3000\u3000\u9a6c\u6c0f\u4e09\u82f1\u7684\u6b66\u529f\uff0c\u8be1\u5f02\u591a\u53d8\uff0c\u800c\u4e14\u8010\u529b\u5f88\u5f3a\uff0c\u5bf9\u65b9\u5df2\u8fde\u7eed\u66f4\u6362\u4e86\u4e09\u6b21\u4eba\u624b\uff0c\u9a6c\u6c0f\u4e09\u82f1\uff0c \u4ecd\u7136\u80fd\u652f\u6301\u5f97\u4f4f\u3002 \u3000\u3000\u8fd9\u5c31\u662f\u738b\u5f64\u7b49\u6700\u5403\u4e8f\u7684\u5730\u65b9\u4e86\uff0c\u7f29\u5c0f\u65b9\u9635\uff0c\u867d\u7136\u53ef\u9632\u6b62\u654c\u4eba\u4e00\u62e5\u800c\u4e0a\uff0c\u518d\u884c\u5206\u5272\u56f4\u653b\uff0c \u4f46\u654c\u4eba\u4f17\u591a\uff0c\u53ef\u4ee5\u4e0d\u505c\u66ff\u6362\u4eba\u624b\uff0c\u4fdd \u3000\u3000\u6301\u7740\u5145\u6c9b\u7684\u4f53\u529b\uff0c\u548c\u6301\u7eed\u7740\u5f3a\u731b\u653b\u52bf\u3002 \u3000\u3000\u4f46\u738b\u5f64\u7b49\u4e03\u4e2a\u4eba\uff0c\u5374\u65e0\u6cd5\u62bd\u6362\u4f11\u606f\u3002 \u3000\u3000\u738b\u5f64\u529f\u529b\u6df1\u539a\uff0c\u8fd8\u53ef\u652f\u6301\uff0c\u4f46\u8d75\u4fdd\u3001\u9648\u5b8f\uff0c\u5df2\u7ecf\u7d2f\u5f97\u5927\u6c57\u6dcb\u6f13\u4e86\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u4e24\u4e2a\u4eba\u4ecd\u7136\u54ac\u7259\u82e6\u6491\u3002 \u3000\u3000\u738b\u5f64\u6697\u6697\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u71d5\u98de\uff0c\u52a0\u628a\u52b2\uff0c\u63a5\u4e0b\u653b\u52bf\uff0c\u8ba9\u8d75\u4fdd \u3000\u3000\u5598\u53e3\u6c14\u3002\u201c \u3000\u3000\u71d5\u98de\u5e94\u4e86\u4e00\u58f0\uff0c\u5200\u5149\u5c55\u5e03\uff0c\u63a5\u4e0b\u4e86\u4e09\u628a\u65a9\u9a6c\u5200\u3002 \u3000\u3000\u8d75\u4fdd\u5df2\u7d2f\u5f97\u6c14\u5598\u5982\u725b\uff0c\u4e5f\u4e0d\u5ba2\u6c14\uff0c\u9000\u540e\u4e24\u6b65\uff0c\u8fd0\u6c14\u8c03\u606f\u3002 \u3000\u3000\u738b\u5f64\u4e00\u4f38\u624b\uff0c\u53d6\u8fc7\u8d75\u4fdd\u624b\u4e2d\u5355\u5200\uff0c\u6c89\u58f0\u559d\u9053\uff1a\u201c\u9648\u5b8f\uff0c\u9000\u4e0b\u53bb\uff0c\u5c3d\u5feb\u8c03\u606f\u4e00\u4e0b\uff0c\u51c6\u5907\u63a5 \u624b\u3002\u201d \u3000\u3000\u9648\u5b8f\u5e94\u58f0\u9000\u4e0b\u3002 \u3000\u3000\u738b\u5f64\u5200\u52bf\u5c55\u5f00\uff0c\u6709\u5982\u8f66\u8f6e\u98de\u8f6c\uff0c\u4e0d\u4f46\u963b\u6b62\u4e86\u5f53\u9762\u7684\u653b\u52bf\uff0c\u800c\u4e14\uff0c\u8fd8\u7167\u5e94\u5230\u4e86\u71d5\u98de\u4fa7\u7ffc\u3002 \u3000\u3000\u8fd9\u5c31\u662f\u7ed9\u4e86\u71d5\u98de\u5f88\u5927\u52a9\u529b\uff0c\u71d5\u98de\u7cbe\u795e\u4e00\u632f\uff0c\u5168\u529b\u53cd\u51fb\uff0c\u628a\u51f9\u9677\u4e00\u89d2\u65b9\u9635\uff0c\u91cd\u65b0\u7684\u6491\u4e86\u8d77 \u6765\u3002 \u3000\u3000\u4f46\u95fb\u4e00\u7f15\u7ec6\u5fae\u7684\u58f0\u97f3\uff0c\u4f20\u5165\u8033\u9645\uff0c\u9053\uff1a\u201c\u8001\u738b\uff0c\u771f\u4e8f\u4f60\u60f3\u51fa\u4e86\u8fd9\u4e48\u4e00\u4e2a\u62d2\u654c\u7684\u65b9\u6cd5\uff0c\u6491 \u4e86\u8fd9\u4e48\u957f\u7684\u65f6\u95f4\uff0c\u4e0d\u8fc7\uff0c\u4f60\u6491\u4e0d\u4e0b\u53bb\u7684\uff0c\u8001\u5b9e\u8bf4\uff0c\u76ee\u4e0b\u7684\u73b0\u573a\u4e4b\u4e2d\uff0c\u6709\u4f60\u4e0d\u5c11\u7684\u8001\u670b\u53cb\uff0c\u4ed6 \u4eec\u548c\u6211\u4e00\u6837\uff0c\u90fd\u5f88\u60f3\u5e2e\u4f60\u7684\u5fd9\uff0c\u4f46\u4ed6\u4eec\u6ca1\u6709\u529e\u6cd5\uff0c\u6211\u4eec\u5df2\u5c3d\u91cf\u62d6\u5ef6\u65f6\u95f4\uff0c\u7ed9\u4f60\u6c42\u63f4\u7684\u673a \u4f1a\u2026\u2026\u201d \u3000\u3000\u738b\u5f64\u542c\u5f97\u5b57\u5b57\u5165\u8033\uff0c\u4e5f\u77e5\u9053\u90a3\u662f\u5343\u91cc\u4f20\u97f3\u7684\u4e0a\u4e58\u529f\u592b\uff0c\u9664\u4ed6\u4e4b\u5916\uff0c\u522b\u4eba\u65e0\u6cd5\u542c\u5230\u3002 \u3000\u3000\u4e00\u9762\u62d2\u654c\uff0c\u4e00\u9762\u8fd0\u8db3\u56fd\u529b\u5411\u56db\u4e0b\u641c\u5bfb\uff0c\u5e0c\u671b\u80fd\u770b\u51fa\u4e00\u4e1d\u75d5\u8ff9\u3002 \u3000\u3000\u53ef\u662f\uff0c\u6240\u6709\u7684\u4eba\uff0c\u90fd\u7a7f\u7740\u4e00\u6837\u7684\u9ed1\u8272\u8863\u670d\uff0c\u4e00\u6837\u9ed1\u5dfe\u8499\u9762\uff0c\u5b9e\u5728\u65e0\u6cd5\u77a7\u51fa\u4e00\u70b9\u8ff9\u8c61\u3002 \u3000\u3000\u90a3\u7ec6\u5fae\u7684\u58f0\u97f3\uff0c\u91cd\u53c8\u54cd\u8d77\uff0c\u9053\uff1a\u201c\u8001\u738b\uff0c\u522b\u8ba4\u4e3a\u4f60\u8fd9\u70b9\u529b\u91cf\uff0c\u80fd\u6297\u62d2\u5982\u6b64\u4f17\u591a\u7684\u9ad8\u624b\u56f4 \u653b\uff0c\u4e8b\u5b9e\u4e0a\u662f\u4f60\u51e0\u4e2a\u8001\u670b\u53cb\u5728\u6697\u4e2d\u8c03\u5ea6\uff0c\u4ed6\u4eec\u4e0d\u80fd\u5382\u4ee4\u8981\u522b\u4eba\u624b\u4e0b\u7559\u60c5\uff0c\u4f46\u5374\u628a\u6b66\u529f\u6700\u5f3a\u7684 \u4eba\u7559\u4f4f\u672a\u5e94\u3002\u4e0d\u8fc7\uff0c\u65f6\u95f4\u6709\u9650\uff0c\u4ed6\u4eec\u65e0\u6cd5\u62d6\u5ef6\u592a\u4e45\uff0c\u5929\u8272\u5165\u591c\u4e4b\u540e\uff0c\u8fd8\u4f1a\u6709\u4eba\u8d76\u6765\uff0c\u5c31\u8c01\u90fd \u65e0\u6cd5\u5e2e\u4e0a\u4f60\u7684\u5fd9\u4e86\uff0c\u4eca\u65e5\u4e4b\u5c40\uff0c\u975e\u5e38\u5371\u9669\uff0c\u6c5f\u6e56\u9053\u4e0a\uff0c\u53ea\u6015\u662f\u65e0\u4eba\u63d2\u624b\u5e2e\u52a9\u4f60\u4e86\u3002\u552f\u4e00\u7684\u529e \u6cd5\u662f\uff0c\u5929\u8272\u4e00\u5165\u591c\uff0c\u4f60\u4e2a\u4eba\u8bbe\u6cd5\u7a81\u56f4\u800c\u53bb\uff0c\u5f80\u5357\u65b9\u9003\u8d70\uff0c\u4e5f\u8bb8\u8fd8\u6709\u4e00\u7ebf\u751f\u673a\uff0c\u81f3\u4e8e\u4f60\u5e26\u6765\u8fd9 \u4e00\u6279\u5c5e\u4e0b\uff0c\u53ea\u6015\u662f\u65e0\u6cd5\u751f\u79bb\u6b64\u5730\u4e86\uff0c\u8bb0\u7740\uff0c\u5929\u8272\u4e00\u9ed1\uff0c\u5c31\u5168\u529b\u7a81\u56f4\uff0c\u5982\u7b49\u7ea2\u706f\u51fa\u73b0\u3001\u63a5\u8fd1\uff0c \u53ea\u6709\u5168\u519b\u8986\u4ea1\u4e00\u9014\u2014\u2014\u4e00\u201d \u3000\u3000\u8bf4\u81f3\u6b64\u5904\uff0c\u58f0\u97f3\u4e2d\u65ad\uff0c\u6d88\u5931\u4e0d\u95fb\u3002 \u3000\u3000\u5bf9\u65b9\u8bdd\u5df2\u7ecf\u8bf4\u7684\u5f88\u6e05\u695a\u4e86\uff0c\u738b\u5f64\u611f\u89c9\u5230\u90a3\u58f0\u97f3\u6709\u70b9\u8033\u719f\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u5374\u65e0\u6cd5\u60f3\u8d77\u5bf9\u65b9\u662f \u8c01\u3002 \u3000\u3000\u4f46\u8fd9\u5df2\u7ecf\u65e0\u5173\u7d27\u8981\u4e86\uff0c\u5c31\u7b97\u77e5\u9053\u5bf9\u65b9\u7684\u8eab\u4efd\uff0c\u4e5f\u4e00\u6837\u963b\u6b62\u4e0d\u4e86\u8fd9\u6b21\u8bbe\u8ba1\u7684\u56f4\u6740\u3002 \u3000\u3000\u4f7f\u738b\u5f64\u4e0d\u7518\u7684\u662f\uff0c\u4ed6\u4e00\u76f4\u60f3\u4e0d\u660e\u767d\uff0c\u4e3a\u4ec0\u4e48\u4f1a\u6709\u8fd9\u6b21\u6c5f\u6e56\u4eba\u7269\u5927\u7ed3\u5408\u7684\u56f4\u6740\u3002 \u3000\u3000\u539f\u672c\u4ed7\u4ee5\u5965\u63f4\u7684\u5404\u5927\u95e8\u6d3e\uff0c\u60f3\u4e0d\u5230\u7adf\u7136\u90fd\u6210\u4e86\u56f4\u6740\u81ea\u5df1\u7684\u654c\u4eba\u3002 \u3000\u3000\u9b54\u6559\uff0c\u96be\u9053\u8fd9\u6b21\u56f4\u6740\uff0c\u4f1a\u548c\u9b54\u6559\u6709\u5173\u2026\u2026 \u3000\u3000\u4f46\u600e\u4e48\u53ef\u80fd\u5462\uff1f\u8fd9\u4e48\u591a\u6c5f\u6e56\u95e8\u6d3e\uff0c\u5927\u90fd\u6709\u7740\u5149\u8363\u7684\u4f20\u7edf\uff0c\u548c\u68ee\u4e25\u7684\u6559\u89c4\uff0c\u4e0d\u53ef\u80fd\u4e00\u4e0b\u5b50 \u5168\u4e3a\u9b54\u6559\u6240\u7528\uff0c\u4f55\u51b5\uff0c\u8fd1\u4e24\u5e74\u6765\uff0c\u4e5f\u4e00\u76f4\u6ca1\u6709\u4f20\u51fa\u9b54\u6559\u6d3b\u52a8\u7684\u6d88\u606f\u3002 \u3000\u3000\u738b\u5f64\u56f0\u60d1\u6781\u4e86\u3002 \u3000\u3000\u4f46\u5de6\u81c2\u4e00\u75bc\uff0c\u4e00\u62b9\u5200\u5149\uff0c\u7531\u5de6\u81c2\u5212\u8fc7\uff0c\u5e26\u8d77\u4e00\u6e9c\u9c9c\u8840\u3002 \u3000\u3000\u539f\u6765\uff0c\u4ed6\u60f3\u4e8b\u60c5\uff0c\u60f3\u7684\u5165\u795e\uff0c\u624b\u4e2d\u5200\u52bf\u4e00\u7f13\uff0c\u5373\u88ab\u654c\u4eba\u6240\u4f24\u3002 \u3000\u3000\u5e78\u597d\uff0c\u90a3\u4f24\u52bf\u4e0d\u91cd\uff0c\u53ea\u662f\u4f24\u53ca\u76ae\u8089\u3002 \u3000\u3000\u4f46\u8fd9\u5374\u8ba9\u738b\u5f64\u7531\u6c89\u8ff7\u7684\u82e6\u601d\u4e2d\u6e05\u9192\u4e86\u8fc7\u6765\uff0c\u7cbe\u795e\u4e00\u632f\uff0c\u5168\u529b\u8fd8\u51fb\u3002 \u3000\u3000\u539f\u6765\uff0c\u71d5\u98de\u5728\u738b\u5f64\u5fc3\u795e\u4e0d\u5c5e\u4e4b\u65f6\uff0c\u5168\u529b\u51fa\u5200\uff0c\u53cd\u66ff\u738b\u5f64\u63a5\u4e0b\u4e0d\u5c11\u653b\u52bf\uff0c\u5341\u51e0\u62db\u4e0b\u6765\uff0c\u5df2 \u7ecf\u6c14\u5598\u4e0d\u606f\uff0c\u5185\u529b\u96be\u53ca\uff0c\u5e78\u597d\u738b\u5f64\u53ca\u65f6\u6e05\u9192\uff0c\u63a5\u4e0b\u4e86\u5927\u90e8\u5206\u7684\u653b\u52bf\uff0c\u51cf\u53bb\u4e86\u71d5\u98de\u5927\u90e8\u5206\u538b \u529b\u3002 \u3000\u3000\u552f\u4e00\u4f7f\u738b\u5f64\u5fc3\u4e2d\u7a0d\u89c9\u5b89\u6170\u7684\u662f\uff0c\u9a6c\u6c0f\u4e09\u82f1\u529f\u529b\u4e4b\u6df1\uff0c\u5b8c\u5168\u51fa\u4e86\u738b\u5f64\u7684\u610f\u5916\uff0c\u4ed6\u4eec\u4e09\u4eba\uff0c \u5206\u62d2\u4e1c\u3001\u5317\u4e24\u9762\u654c\u4eba\uff0c\u4ecd\u7136\u53ef\u4ee5\u5e94\u4ed8\u3002 \u3000\u3000\u8d75\u4fdd\u3001\u9648\u5b8f\uff0c\u7ecf\u8fc7\u4e86\u4e00\u4f1a\u513f\u7684\u8c03\u606f\uff0c\u4eba\u5df2\u6062\u590d\u4e0d\u5c11\uff0c\u9648\u5b8f\u4e00\u6325\u5355\u5200\uff0c\u5e2e\u52a9\u71d5\u98de\u62d2\u654c\uff0c\u4f46 \u8d75\u4fdd\u624b\u4e2d\u6ca1\u6709\u5175\u5203\uff0c\u4e0d\u77e5\u5982\u4f55\u662f\u597d\uff0c\u4e00\u65f6\u95f4\u6123\u5728\u4e86\u90a3\u91cc\u3002 \u3000\u3000\u6b64\u65f6\u843d\u65e5\u53cd\u7167\uff0c\u665a\u971e\u4f3c\u706b\uff0c\u5df2\u662f\u5915\u9633\u65e0\u9650\u597d\uff0c\u53ea\u662f\u8fd1\u9ec4\u660f\u7684\u65f6\u523b\uff0c\u738b\u5f64\u5fc3\u4e2d\u5927\u611f\u7126\u8651\uff0c \u591c\u5e55\u8f6c\u773c\u5373\u4e34\uff0c\u4ee5\u90a3\u4eba\u7684\u4f20\u97f3\u8b66\u544a\uff0c\u8fd9\u4e9b\u9ed1\u8863\u4eba\u5373\u5c06\u4f5c\u5168\u529b\u7684\u6251\u51fb\u3002 \u3000\u3000\u4f46\u738b\u5f64\u4ecd\u65e0\u6cd5\u51b3\u5b9a\uff0c\u662f\u5426\u8981\u629b\u5f03\u71d5\u98de\u7b49\u7834\u56f4\u800c\u53bb\u3002 \u3000\u3000\u4ed6\u53cd\u590d\u7684\u76d8\u7b97\u8fc7\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u81ea\u5df1\u80fd\u7a81\u56f4\u800c\u53bb\uff0c\u65e5\u540e\u81ea\u7136\u4f1a\u6709\u7740\u66ff\u4ed6\u4eec\u62a5\u4ec7\u7684\u673a\u4f1a\uff0c\u4f46\u90a3 \u5c06\u4f7f\u4ed6\u6c38\u8fdc\u843d\u4e0b\u4e2a\u4e0d\u4ec1\u4e0d\u4e49\u7684\u540d\u58f0\u4f46\u5982\u548c\u51e0\u4eba\u540c\u65f6\u6218\u6b7b\u6b64\u5730\uff0c\u4e0d\u4f46\u6ca1\u6709\u673a\u4f1a\u4e86\u89e3\u4eca\u65e5\u8fd9\u573a\u56f4 \u6740\u7684\u7ecf\u8fc7\uff0c\u800c\u4e14\u4e00\u6b7b\u65e0\u5bf9\u8bc1\uff0c\u522b\u4eba\u5982\u4f55\u53bb\u8bc4\u8bba\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u90a3\u5c31\u65e0\u6cd5\u63a8\u65ad\uff0c\u5f53\u771f\u662f\u6b7b\u4e5f\u96be\uff0c\u4e0d \u6b7b\u4e5f\u96be\uff1f \u3000\u3000\u6700\u91cd\u8981\u7684\u662f\u738b\u5f64\u8eab\u6000\u6570\u9053\u5bc6\u65e8\uff0c\u7687\u4e0a\u5723\u65e8\uff0c\u5982\u4f55\u80fd\u843d\u5165\u4ed6\u4eba\u4e4b\u624b\uff1f \u3000\u3000\u7a81\u7136\u95f4\uff0c\u54cd\u8d77\u4e00\u9635\u53f7\u89d2\u4e4b\u58f0\uff0c\u5212\u7834\u5e7f\u9614\u7684\u539f\u91ce\uff0c\u4e5f\u6df7\u5165\u5175\u5203\u7684\u4ea4\u51fb\u4e4b\u58f0\uff0c\u4f20\u5165\u8033\u9645\u3002 \u3000\u3000\u9a6c\u8c6a\u5927\u58f0\u559d\u9053\uff1a\u201c\u5927\u4eba\uff0c\u63f4\u519b\u6765\u4e86\u3002\u201d \u3000\u3000\u9ec4\u6c99\u5e73\u539f\u4e0a\u8361\u8d77\u4e86\u6f2b\u5929\u7684\u5c18\u571f\uff0c\u6570\u5343\u5339\u5feb\u9a6c\uff0c\u5939\u8d77\u4e86\u6eda\u6eda\u70df\u5c18\u800c\u6765\u3002 \u3000\u3000\u5c45\u4e2d\u4e00\u9a91\uff0c\u7adf\u662f\u6cb3\u5357\u5de1\u629a\u9a6c\u6587\u4e2d\u3002 \u3000\u3000\u6b64\u65f6\uff0c\u4ed6\u6539\u7a7f\u4e86\u4e00\u8eab\u6df1\u84dd\u8272\u7684\u75be\u670d\u52b2\u88c5\uff0c\u624b\u6267\u4e00\u67c4\u4e5d\u5c3a\u516d\u5bf8\u7684\u5927\u5173\u5200\u3002 \u3000\u3000\u4e03\u4e2a\u8eab\u7740\u52b2\u88c5\u7684\u5e74\u8f7b\u4eba\uff0c\u7d27\u968f\u5728\u9a6c\u6587\u4e2d\u7684\u8eab\u540e\u3002 \u3000\u3000\u5de6\u9996\u4e00\u4eba\u7adf\u662f\u8f6c\u9f99\u624b\u5f20\u4e0d\u7a7a\uff0c\u5de6\u624b\u6267\u7740\u4e00\u67c4\u94c1\u5c3a\uff0c\u53f3\u624b\u63e1\u7740\u4e00\u652f\u957f\u5251\uff0c\u8eab\u540e\u7d27\u968f\u7740\u4e8c\u5341 \u4f59\u540d\u624b\u6267\u5175\u5203\u7684\u5065\u9a91\uff0c\u4f3c\u662f\u5de1\u629a\u5e9c\u4e2d\u7684\u6355\u5feb\u7cbe\u82f1\u3002 \u3000\u3000\u53f3\u9996\u662f\u4e00\u4e2a\u767d\u53d1\u767d\u987b\u7684\u8001\u8005\uff0c\u624b\u6267\u957f\u5200\uff0c\u6bd4\u4e00\u822c\u7684\u5355\u5200\u7a84\u4e86\u4e00\u4e9b\uff0c\u4f46\u957f\u5ea6\u8db3\u6709\u4e94\u5c3a\u4ee5 \u4e0a\u3002 \u3000\u3000\u738b\u5f64\u53cc\u76ee\u4e00\u4eae\uff0c\u5fd6\u9053\uff1a\u59ec\u91cd\u5929\uff0c\u679c\u7136\u5df2\u4e3a\u9a6c\u6587\u4e2d\u6240\u6536\u7528\u4e86\u3002 \u3000\u3000\u4f46\u95fb\u9a6c\u6587\u4e2d\u7684\u58f0\u97f3\u9065\u6447\u4f20\u6765\uff0c\u9053\uff1a\u201c\u738b\u5927\u4eba\uff0c\u518d\u82e6\u6491\u4e00\u4e0b\uff0c\u6587\u4e2d\u5df2\u4eb2\u7387\u63f4\u624b\u8d76\u5230\uff0c\u4e94\u767e \u540d\u94c1\u9a91\u519b\u5df2\u5728\u6e21\u6cb3\uff0c\u4e24\u5343\u540d\u6b65\u9a91\u5927\u519b\uff0c\u4e5f\u5c06\u968f\u540e\u8d76\u5230\uff0c\u8fd9\u4e9b\u76d7\u532a\u7adf\u6562\u62e6\u52ab\u5185\u5bab\u4f8d\u536b\uff0c\u4e2a\u4e2a\u90fd \u662f\u6b7b\u7f6a\uff0c\u683c\u6740\u52ff\u8bba\u201d \u3000\u3000\u524d\u9762\u51e0\u53e5\u8bdd\u8bf4\u7ed9\u738b\u5f64\u542c\uff0c\u540e\u9762\u51e0\u53e5\u8bdd\uff0c\u5374\u662f\u4e0b\u4ee4\u51b2\u6740\u3002 \u3000\u3000\u59ec\u91cd\u5929\u9996\u5148\u98de\u8eab\u800c\u8d77\uff0c\u4eba\u79bb\u9a6c\u978d\uff0c\u6709\u5982\u4e00\u53ea\u5212\u7a7a\u800c\u6765\u7684\u5927\u9e1f\uff0c\u7adf\u7136\u4e00\u98de\u516d\u4e03\u4e08\u8fdc\uff0c\u624b\u4e2d \u957f\u5200\u5df2\u75be\u65a9\u800c\u4e0b\u3002 \u3000\u3000\u4e24\u4e2a\u4f7f\u5251\u7684\u9ed1\u8863\u4eba\u8fce\u4e0a\u5200\u52bf\uff0c\u4f46\u5374\u88ab\u957f\u5200\u9707\u5f00\u3002 \u3000\u3000\u59ec\u91cd\u5929\u65e8\u5728\u6551\u4eba\uff0c\u53cc\u8db3\u4e00\u843d\u5b9e\u5730\uff0c\u7acb\u523b\u53c8\u7a9c\u5347\u800c\u8d77\uff0c\u76f4\u5411\u53d7\u56f0\u7684\u738b\u5f64\u51b2\u53bb\u3002 \u3000\u3000\u4f46\u59ec\u91cd\u5929\u7b2c\u4e8c\u6b21\u60f3\u501f\u529b\u518d\u8d77\u65f6\uff0c\u5df2\u6ca1\u6709\u90a3\u4e48\u5e78\u8fd0\u4e86\uff0c\u7acb\u523b\u88ab\u56db\u4e2a\u9ed1\u8863\u4eba\u5305\u56f4\u4e86\u8d77\u6765\uff0c\u94c1 \u68cd\u6709\u5982\u6cf0\u5c71\u538b\u9876\u4e00\u822c\uff0c\u4e3e\u843d\u4e0b\u6765\u3002 \u3000\u3000\u59ec\u91cd\u5929\u6325\u5200\u53cd\u51fb\uff0c\u548c\u56db\u4e2a\u9ed1\u8863\u4eba\u6218\u5728\u4e00\u5904\u3002 \u3000\u3000\u9a6c\u6587\u4e2d\u62a2\u5200\u7eb5\u9a6c\uff0c\u51b2\u6740\u8fc7\u6765\uff0c\u5927\u5200\u6325\u51fa\u4e00\u7247\u5bd2\u8292\uff0c\u9510\u4e0d\u53ef\u5f53\u5b8c\u5168\u662f\u4e00\u526f\u51b2\u950b\u9677\u9635\u7684\u6740 \u5f0f\uff0c\u5200\u5149\u8fc7\u5904\uff0c\u62e6\u622a\u7684\u5175\u5203\uff0c\u7eb7\u7eb7\u88ab\u9707\u8361\u5f00\u53bb\u3002 \u3000\u3000\u60f3\u4e0d\u5230\u4ed6\u9a6c\u4e0a\u7684\u529f\u592b\uff0c\u7adf\u4e5f\u662f\u5982\u6b64\u7684\u7cbe\u6e5b\u3001\u9ad8\u660e\u3002 \u3000\u3000\u4e03\u4e2a\u4ece\u536b\u5374\u98de\u843d\u9a6c\u978d\uff0c\u968f\u62a4\u5728\u9a6c\u524d\u9a6c\u540e\uff0c\u5411\u654c\u4eba\u51b2\u6740\uff0c\u5200\u62db\u8be1\u5f02\uff0c\u52c7\u4e0d\u53ef\u6321\u3002 \u3000\u3000\u5f20\u4e0d\u7a7a\u7387\u9886\u7684\u6355\u5feb\u7cbe\u9510\uff0c\u4e5f\u548c\u9ed1\u8863\u4eba\u63a5\u4e0a\u4e86\u624b\uff0c\u4f46\u65e0\u6cd5\u548c\u59ec\u91cd\u5929\u3001\u9a6c\u6587\u4e2d\u76f8\u63d0\u5e76\u8bba\uff0c\u88ab \u5341\u4f59\u4e2a\u9ed1\u8863\u4eba\u62e6\u4f4f\u6076\u6597\uff0c\u65e0\u6cd5\u8d8a\u96f7\u6c60\u4e00\u6b65\u3002 \u3000\u3000\u71d5\u98de\u7684\u5224\u65ad\u4e0d\u9519\uff0c\u4e00\u4fa7\u58d5\u6c9f\u7a81\u7136\u53c8\u98de\u8dc3\u51fa\u6570\u5341\u540d\u9ed1\u8863\u4eba\uff0c\u98de\u8eab\u9a70\u63f4\u3002 \u3000\u3000\u773c\u770b\u9a6c\u6587\u4e2d\u7387\u9886\u7684\u63f4\u519b\uff0c\u4e5f\u5c06\u9677\u5165\u5305\u56f4\u4e4b\u4e2d\uff0c\u7a81\u7136\u54cd\u8d77\u4e86\u4e00\u9635\u5c16\u5389\u7af9\u54e8\u4e4b\u58f0\u3002 \u3000\u3000\u58f0\u97f3\u5c16\u9510\u9ad8\u4ea2\uff0c\u54cd\u5f7b\u5168\u573a\u3002 \u3000\u3000\u56f4\u51fb\u3001\u62e6\u622a\u7684\u9ed1\u8863\u4eba\uff0c\u7a81\u7136\u820d\u53bb\u654c\u624b\uff0c\u7eb7\u7eb7\u5411\u540e\u9000\u53bb\uff0c\u4e2a\u4e2a\u8eab\u624b\u77eb\u5065\uff0c\u8f7b\u529f\u4e86\u5f97\uff0c\u7247\u523b \u4e4b\u95f4\uff0c\u5df2\u8d70\u5f97\u4e0d\u89c1\u8e2a\u8ff9\u3002 \u3000\u3000\u9a6c\u6587\u4e2d\u6ca1\u6709\u8ffd\u8d76\u3002 \u3000\u3000\u9002\u624d\u63a5\u624b\u4e00\u6218\uff0c\u9a6c\u6587\u4e2d\u5fc3\u4e2d\u660e\u767d\uff0c\u8ffd\u4e0a\u53bb\uff0c\u4e5f\u672a\u5fc5\u8ba8\u5f97\u4e86\u597d\uff0c\u4ed6\u4eec\u662f\u5949\u547d\u64a4\u8d70\uff0c\u5e76\u975e\u662f \u6e83\u8d25\u4e0d\u652f\uff0c\u5c31\u53cc\u65b9\u5b9e\u529b\u800c\u8bba\uff0c\u9ed1\u8863\u4eba\u7684\u529b\u91cf\uff0c\u5927\u8fc7\u5f88\u591a\u3002 \u3000\u3000\u738b\u5f64\u4e00\u62f1\u624b\u9053\uff1a\u201c\u6709\u52b3\u9a6c\u5144\u4eb2\u81ea\u9a70\u63f4\uff0c\u5144\u5f1f\u611f\u6fc0\u5f97\u5f88\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u770b\u738b\u5f64\u5de6\u81c2\u4e0a\u8840\u8ff9\u6591\u6591\uff0c\u5176\u4ed6\u7684\u4eba\uff0c\u867d\u7136\u4e2a\u4e2a\u6c57\u900f\u8863\u88e4\uff0c\u4f46\u5e76\u65e0\u4e00\u4eba\u53d7\u4f24\u3002 \u3000\u3000\u201c\u738b\u89c1\u53d7\u4f24\u4e86\u2026\u2026\u201d\u9a6c\u6587\u4e2d\u9053\uff1a\u201c\u5982\u6b64\u5e9e\u5927\u7684\u9635\u5bb9\uff0c\u6211\u4e5f\u662f\u521d\u6b21\u9047\u4e0a\uff0c\u4e0d\u77e5\u90a3\u4e00\u95e8\u6d3e \u4e2d\uff0c\u6709\u5982\u6b64\u5f3a\u5927\u7684\u5b9e\u529b\u3002\u201d \u3000\u3000\u90a3\u767d\u53d1\u767d\u9aef\u8001\u4eba\u63a5\u9053\uff1a\u201c\u548c\u8001\u592b\u52a8\u624b\u7684\u56db\u4e2a\u9ed1\u8863\u4eba\uff0c\u6709\u70b9\u5947\u602a\uff0c\u4ed6\u4eec\u624b\u4e2d\u7684\u94c1\u68cd\uff0c\u65bd\u7684 \u662f\u5341\u516b\u62db\u7f57\u6c49\u6756\u6cd5\uff0c\u50cf\u662f\u5c11\u6797\u5bfa\u4e2d\u7684\u4eba\uff0c\u96be\u9053\u662f\u5c11\u6797\u6d3e\u7684\u4fd7\u5bb6\u5f1f\u5b50\uff1f\u201d \u3000\u3000\u201c\u59ec\u5144\u2026\u2026\u201d\u9a6c\u6587\u4e2d\u62db\u547c\u90a3\u767d\u9aef\u8001\u4eba\u9053\uff1a\u201c\u6211\u66ff\u4f60\u5f15\u89c1\u4e00\u4f4d\u670b\u53cb\uff0c\u73b0\u4efb\u5185\u5bab\u7edf\u9886\u738b\u5f64\u738b \u5927\u4eba\u3002\u201d \u3000\u3000\u738b\u5f64\u4e00\u62b1\u62f3\u9053\uff1a\u201c\u59ec\u5144\uff0c\u54b1\u4eec\u5f88\u4e45\u4e0d\u89c1\u4e86\u3002\u201d \u3000\u3000\u539f\u6765\u4e24\u4eba\u4e8c\u5341\u5e74\u524d\u5df2\u89c1\u8fc7\u9762\u4e86\u3002 \u3000\u3000\u59ec\u91cd\u5929\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u73b0\u5728\u662f\u7687\u5e1d\u9762\u524d\u7684\u5927\u7ea2\u4eba\uff0c\u4ee5\u540e\uff0c\u8fd8\u8981\u591a\u7167\u987e\u4e00\u4e0b\u8001\u670b\u53cb \u54e6\uff01\u201d \u3000\u3000\u201c\u59ec\u5144\u8a00\u91cd\u4e86\uff0c\u738b\u67d0\u529b\u80fd\u6240\u53ca\uff0c\u7edd\u4e0d\u6709\u5f00\u7f6a\u59ec\u5144\u4e4b\u5904\u3002\u201d \u3000\u3000\u5f20\u4e0d\u7a7a\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u5f20\u67d0\u5df2\u6539\u90aa\u5f52\u6b63\uff0c\u738b\u67d0\u53ef\u4e0d\u80fd\u627e\u6211\u7b97\u8001\u5e10\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u6562\uff0c\u4e0d\u6562\u2026\u2026\u201d\u738b\u5f64\u8bf4\uff1a\u201c\u5f00\u5c01\u5e9c\u8def\u4e0d\u62fe\u9057\uff0c\u5168\u662f\u5f20\u5144\u4e4b\u529f\u3002\u201d \u3000\u3000\u201c\u5927\u8d3c\u6349\u5c0f\u8d3c\uff0c\u81ea\u7136\u662f\u624b\u5230\u64d2\u6765\u2026\u2026\u201d\u5f20\u4e0d\u7a7a\u9053\uff1a\u201c\u4e0d\u5bf9\uff0c\u5144\u5f1f\u73b0\u5728\u53ef\u662f\u6cb3\u5357\u7701\u7684\u603b\u6355 \u5934\uff0c\u5b98\u5df2\u8d70\u9a6c\u4e0a\u4efb\uff0c\u4f46\u5982\u5211\u90e8\u4e0d\u51c6\u9a6c\u5927\u4eba\u7684\u4fdd\u8350\uff0c\u90a3\u5c31\u88ab\u62c9\u4e0b\u9a6c\u6765\uff0c\u4e0d\u77e5\u738b\u5144\u53ef\u5426\u5e2e\u5144\u5f1f\u4e00 \u628a\uff0c\u597d\u6b79\u4e5f\u8ba9\u6211\u8fc7\u8fc7\u8fd9\u603b\u6355\u5934\u763e\u5934\u3002\u201d \u3000\u3000\u201c\u5f20\u5144\u653e\u5fc3\uff0c\u8fd9\u4ef6\u4e8b\u5305\u5728\u5144\u5f1f\u8eab\u4e0a\u3002\u201d \u3000\u3000\u201c\u591a\u8c22\u738b\u6606\u2026\u2026\u201d\u5f20\u4e0d\u7a7a\u7adf\u7136\u606d\u606d\u656c\u656c\u7684\u4f5c\u4e86\u4e00\u4e2a\u957f\u63d6\u3002 \u3000\u3000\u738b\u5f64\u51dd\u76ee\u4e1c\u671b\uff0c\u4e0d\u505c\u63a2\u770b\u3002 \u3000\u3000\u9a6c\u6587\u4e2d\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u738b\u5144\u770b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u9a6c\u5144\u8c03\u52a8\u7684\u94c1\u9a91\u519b\u2026\u2026\u201d \u3000\u3000\u201c\u5144\u5f1f\u5f97\u98de\u9e70\u4f20\u56de\u4fe1\u9999\uff0c\u77e5\u9053\u738b\u5144\u9047\u4e0a\u4e86\u53d8\u6545\uff0c\u7acb\u523b\u5e26\u59ec\u5144\u548c\u5f20\u603b\u6355\u5934\u8d76\u6765\uff0c\u5374\u672a\u6599\u5230 \u654c\u4eba\u7684\u9635\u4ed7\uff0c\u7adf\u662f\u5982\u6b64\u5e9e\u5927\uff0c\u4e0d\u5f97\u51fa\u8a00\u606b\u5413\u4e00\u4e0b\uff0c\u5175\u4e0d\u538c\u8bc8\uff0c\u738b\u89c1\u4e0d\u4f1a\u602a\u5144\u5f1f\u5427\uff01\u201d \u3000\u3000\u201c\u539f\u6765\u5982\u6b64\u2026\u2026\u201d\u738b\u5f64\u9053\uff1a\u201c\u5144\u5f1f\u5fc3\u4e2d\u4e00\u76f4\u4e0d\u5b89\uff0c\u9a6c\u5144\u5982\u6b64\u52b3\u5e08\u52a8\u4f17\uff0c\u8c03\u52a8\u5927\u519b\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4e9b\u4eba\u662f\u90a3\u4e00\u8def\u7684\u4eba\u9a6c\uff1f\u2026\u2026\u201d\u9a6c\u6587\u4e2d\u9053\uff1a\u201c\u738b\u89c1\u5fc3\u4e2d\u662f\u5426\u6709\u4e2a\u5e95\u5b50\uff1f\u201d \u3000\u3000\u201c\u5f53\u4eca\u6c5f\u6e56\u4e4b\u4e0a\uff0c\u80fd\u51fa\u52a8\u5982\u6b64\u5f3a\u5927\u7684\u4eba\u624b\uff0c\u5e94\u8be5\u4e0d\u591a\uff0c\u5144\u5f1f\u4e00\u65f6\u95f4\uff0c\u4e5f\u60f3\u4e0d\u51fa\u4ed6\u4eec\u7684\u6765 \u8def\uff1f\u201d\u738b\u5f64\u9690\u85cf\u4e86\u5fc3\u4e2d\u7684\u79d8\u5bc6\u3002 \u3000\u3000\u59ec\u91cd\u5929\u9053\uff1a\u201c\u548c\u8001\u592b\u52a8\u624b\u7684\u4eba\uff0c\u80af\u5b9a\u5728\u5c11\u6797\u5bfa\u7684\u50e7\u4fa3\u4e4b\u4e2d\uff0c\u5341\u516b\u7f57\u6c49\u6756\u6cd5\uff0c\u662f\u5bfa\u4e2d\u50e7\u4fa3 \u72ec\u95e8\u4e4b\u5b66\uff0c\u8fde\u5c11\u6797\u4fd7\u5bb6\u5f1f\u5b50\uff0c\u4e5f\u4e0d\u80fd\u5b66\u4e60\u8fd9\u79cd\u6756\u6cd5\uff01\u201d \u3000\u3000\u201c\u5c11\u6797\u5bfa\u548c\u738b\u5927\u4eba\u6709\u7740\u5f88\u6df1\u539a\u7684\u4ea4\u60c5\u2026\u2026\u201d\u9a6c\u6587\u4e2d\u9053\uff1a\u201c\u600e\u4e48\u6d3e\u9063\u5bfa\u4e2d\u9ad8\u624b\u62e6\u52ab\u738b\u5927\u4eba \u5462\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\uff0c\u59ec\u67d0\u4eba\u5c31\u65e0\u6cd5\u89e3\u91ca\u4e86\uff0c\u4e0d\u8fc7\uff0c\u90a3\u4eba\u51fa\u8eab\u5c11\u6797\u5bfa\uff0c\u7edd\u4e0d\u4f1a\u9519\u3002\u201d \u3000\u3000\u5f20\u4e0d\u7a7a\u9053\uff1a\u201c\u548c\u5f20\u67d0\u52a8\u624b\u7684\u4e00\u4e2a\uff0c\u7adf\u7136\u65bd\u5c55\u6b66\u5f53\u6d3e\u7684\u5251\u6cd5\uff0c\u771f\u662f\u602a\u4e8b\u5e74\u5e74\u6709\uff0c\u6ca1\u6709\u4eca\u5e74 \u591a\uff0c\u6b66\u5f53\u6d3e\u4e5f\u548c\u738b\u5927\u4eba\u662f\u4ea4\u60c5\u5f88\u6df1\u7684\u670b\u53cb\u554a\uff01\u201d \u3000\u3000\u201c\u8fd9\u4e2d\u95f4\u6709\u5f88\u591a\u53ef\u7591\u5730\u65b9\u2026\u2026\u201d\u738b\u5f64\u8bf4\uff1a\u201c\u5728\u6ca1\u6709\u660e\u786e\u7684\u8bc1\u636e\u4e4b\u524d\uff0c\u6211\u4e5f\u4e0d\u6562\u4e0b\u65ad \u8a00\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u9053\uff1a\u201c\u4ee5\u738b\u5927\u4eba\u9605\u5386\u4e4b\u591a\uff0c\u7ecf\u9a8c\u4e4b\u535a\uff0c\u96be\u9053\u77a7\u4e0d\u51fa\u4e00\u70b9\u86db\u4e1d\u9a6c\u8ff9\u4e48\uff1f\u201d \u3000\u3000\u738b\u5f64\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6697\u9053\uff1a\u201c\u5982\u82e5\u8fd9\u756a\u56f4\u6740\uff0c\u662f\u897f\u65b9\u9b54\u6559\u7b56\u52a8\uff0c\u4e5f\u8bb8\u9a6c\u6587\u4e2d\u77e5\u9053\u4e00\u70b9\u5185\u60c5\uff0c \u4f55\u4e0d\u6d4b\u8bd5\u4e00\u4e0b\uff1f\u201d \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u59ec\u8001\u8bf4\u7684\u4e0d\u9519\uff0c\u56f4\u88ad\u738b\u67d0\u7684\u4eba\u4e2d\uff0c\u6709\u4eba\u65bd\u5c55\u6b63\u7edf\u7684\u5c11\u6797\u6b66\u6797\uff0c \u5f20\u603b\u6355\u5934\u4e5f\u6ca1\u6709\u8bf4\u9519\uff0c\u6709\u4eba\u65bd\u5c55\u6b66\u5f53\u5251\u6cd5\uff0c\u4e5f\u6709\u6c5f\u5357\u795e\u5200\u5802\u7684\u5200\u6cd5\uff0c\u548c\u4ed6\u4eec\u7279\u5f02\u7684\u957f\u5200\uff0c\u8fd8 \u6709\u4e10\u5e2e\u7684\u6253\u72d7\u68d2\u6cd5\uff0c\u8fd9\u4e48\u4e00\u60f3\uff0c\u4f3c\u4e4e\u662f\u6574\u4e2a\u6b66\u6797\u540c\u9053\uff0c\u7ed3\u5408\u4e86\u8d77\u6765\uff0c\u5c3d\u51fa\u7cbe\u9510\uff0c\u62e6\u6740\u6211\u4eec \u4e86\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u5e94\u8be5\u662f\u4e0d\u592a\u53ef\u80fd\u7684\u4e8b\u554a\uff01\u201d \u3000\u3000\u738b\u5f64\u6697\u4e2d\u7559\u5fc3\u9a6c\u6587\u4e2d\u7684\u53cd\u5e94\uff0c\u679c\u7136\uff0c\u9a6c\u5de1\u629a\u4e00\u4e0b\u5b50\u76b1\u8d77\u4e86\u7709\u5934\uff0c\u6c89\u601d\u4e0d\u8bed\u3002 \u3000\u3000\u7531\u4ed6\u7684\u795e\u8272\u4e2d\uff0c\u738b\u5f64\u5f88\u5feb\u7684\u5f97\u5230\u4e86\u4e00\u4e2a\u7ed3\u8bba\uff0c\u9a6c\u6587\u4e2d\u786e\u5b9e\u4e0d\u77e5\u9053\u8fd9\u4ef6\u4e8b\u60c5\u3002 \u3000\u3000\u8fd9\u6837\u7684\u4e00\u526f\u8868\u60c5\uff0c\u4f7f\u738b\u5f64\u4e5f\u6709\u4e9b\u628a\u63e1\u4e0d\u5b9a\u4e86\uff0c\u4e24\u5e74\u591a\u6765\uff0c\u6c5f\u6e56\u4e0a\u4ece\u672a\u542c\u5230\u8fc7\u6709\u9b54\u6559\u6d3b\u52a8 \u7684\u6d88\u606f\uff0c\u5c31\u7b97\u9b54\u9ad8\u4e00\u4e08\uff0c\u4e5f\u65e0\u6cd5\u540c\u65f6\u652f\u914d\u5c11\u6797\u3001\u6b66\u5f53\u3001\u4e10\u5e2e\u3001\u795e\u5200\u5802\u7b49\u8fd9\u6837\u7684\u5b9e\u529b\u5f3a\u5927\u95e8 \u6d3e\uff0c\u9063\u51fa\u9ad8\u624b\u4e3a\u4ed6\u4eec\u6240\u7528\u3002 \u3000\u3000\u4f46\u95fb\u9a6c\u6587\u4e2d\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\u738b\u5927\u4eba\u5fc3\u4e2d\u6240\u7591\uff0c\u4e0d\u8fc7\uff0c\u6587\u4e2d\u786e\u6ca1\u6709\u4e00\u70b9\u6d88\u606f\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u4ed4\u7ec6\u60f3\u8fc7\u4e86\uff0c\u8fd9\u4ef6\u4e8b\u4e0d\u592a\u53ef\u80fd\u2026\u2026\u201d\u738b\u5f64\u8bf4\uff1a\u201c\u5c31\u7b97\u4ed6\u4eec\u795e\u901a\u5e7f\u5927\u5427\uff01\u4e5f\u4e0d\u53ef\u80fd\u4e00 \u4e0b\u5b50\u80fd\u52a8\u5458\u5230\u5982\u6b64\u4f17\u591a\u7684\u5927\u95e8\u6d3e\uff0c\u4e3a\u5176\u6240\u7528\u3002\u201d \u3000\u3000\u201c\u738b\u5144\u80fd\u6d88\u9664\u5bf9\u6b64\u4e4b\u7591\uff0c\u6587\u4e2d\u5c31\u5b89\u5fc3\u591a\u4e86\uff0c\u73b0\u5728\uff0c\u738b\u5144\u4f5c\u4f55\u6253\u7b97\u5462\uff1f\u662f\u7ee7\u7eed\u5317\u4e0a\uff0c\u8fd8\u662f \u6682\u56de\u5f00\u5c01\uff0c\u7b49\u5144\u5f1f\u8c03\u6d3e\u5927\u519b\u62a4\u9001\u3002\u201d \u3000\u3000\u201c\u7ee7\u7eed\u5317\u4e0a\uff0c\u65e9\u56de\u4eac\u4e2d\uff0c\u5144\u5f1f\u4e5f\u53ef\u5b89\u5fc3\u4e00\u4e9b\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u6587\u4e2d\u5c31\u518d\u9001\u738b\u5144\u4e00\u7a0b\u2026\u2026\u201d\u56de\u5934\u5bf9\u5f20\u4e0d\u7a7a\u9053\uff1a\u201c\u4f60\u56de\u53bb\u5e9c\u8859\u5427\uff0c\u987a\u4fbf\u901a\u77e5\u8d75\u4e8c\u5824 \u4e00\u58f0\uff0c\u8981\u4ed6\u4eb2\u7387\u4e8c\u767e\u540d\u94c1\u9a91\u519b\u4e13\u7a0b\u8d76\u6765\u3002\u201d \u3000\u3000\u5f20\u4e0d\u7a7a\u5e94\u4e86\u4e00\u58f0\uff0c\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u738b\u5f64\u7b11\u9053\uff1a\u201c\u9a6c\u5c3c\u662f\u5426\u8ba4\u4e3a\u4ed6\u4eec\u8fd8\u4f1a\u518d\u5ea6\u62e6\u52ab\u5417\uff1f\u201d \u3000\u3000\u201c\u5982\u679c\u4ed6\u4eec\u6ca1\u6709\u6563\u53bb\uff0c\u53c8\u53d1\u73b0\u738b\u5144\u4e00\u884c\u4eba\u6570\u4e0d\u591a\uff0c\u53ef\u80fd\u4f1a\u518d\u6b21\u4e0b\u624b\u2026\u2026\u201d\u9a6c\u6587\u4e2d\u9053\uff1a \u201c\u8fd9\u53eb\u6709\u5907\u65e0\u60a3\u554a\uff01\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u8868\u73b0\u6781\u7aef\u7684\u53cb\u597d\u3001\u4e49\u6c14\uff0c\u4e00\u76f4\u628a\u738b\u5f64\u9001\u51fa\u6cb3\u5357\u7701\u5883\uff0c\u8def\u9014\u8d85\u8fc7\u4e09\u767e\u91cc\uff0c\u624d\u544a\u8f9e\u56de \u7a0b\u3002 \u5367\u9f99\u751f\u300a\u71d5\u5b50\u4f20\u5947\u300b \u7b2c\u5341\u4e09\u56de\u3000\u70df\u7b3c\u96fe\u7f69 \u3000\u3000\u4e00\u8def\u4ea4\u8c08\uff0c\u4f7f\u5f97\u738b\u5f64\u5bf9\u9a6c\u6587\u4e2d\u6709\u4e86\u66f4\u591a\u7684\u8ba4\u8bc6\uff0c\u4ed6\u4e0d\u4f46\u719f\u8bfb\u4e86\u4e2d\u56fd\u7684\u7ecf\u53f2\uff0c\u8fde\u8bd7\u3001\u8bcd\u4e5f \u662f\u9020\u8be3\u5f88\u6df1\uff0c\u662f\u771f\u6709\u6c38\u7559\u4e2d\u4e0a\u7684\u7528\u5fc3\u3002 \u3000\u3000\u7ecf\u8fc7\u4e86\u4e00\u6b21\u5927\u56f4\u6740\u4e4b\u540e\uff0c\u738b\u5f64\u7684\u5fc3\u91cc\u6709\u4e86\u5f88\u5927\u7684\u6539\u53d8\uff0c\u56de\u4eac\u4e4b\u540e\uff0c\u51b3\u5fc3\u52a0\u91cd\u5185\u5bab\u4f8d\u536b\u529b \u91cf\uff0c\u4e00\u65b9\u9762\u6269\u5145\u4eba\u6570\uff0c\u589e\u52a0\u5b9e\u529b\uff1b\u4e00\u65b9\u9762\u8981\u9009\u5de5\u827a\u540d\u5320\uff0c\u5236\u9020\u950b\u5229\u7684\u6697\u5668\uff0c\u4f7f\u5185\u5bab\u7684\u653b\u51fb\u3001 \u9632\u5b88\u529b\u91cf\uff0c\u90fd\u8981\u5927\u5927\u7684\u589e\u5f3a\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u6c5f\u6e56\u4e4b\u4e0a\uff0c\u4f3c\u4e4e\u662f\u5df2\u7ecf\u6ca1\u6709\u4e00\u4e2a\u53ef\u4ee5\u76f8\u4fe1\u7684\u670b\u53cb\u4e86\uff0c\u5c11\u6797\u3001\u6b66\u5f53\u638c\u95e8\u4eba\u90fd\u662f\u4ed6\u7684 \u670b\u53cb\uff0c\u4f46\u8fd9\u4e24\u5927\u95e8\u6d3e\u5374\u6d3e\u4e86\u9ad8\u624b\u52ab\u6740\u4ed6\u3002 \u3000\u3000\u4f46\u4ed6\u4ecd\u662f\u65e0\u6cd5\u7406\u89e3\u5f00\u8fd9\u4e9b\u56f4\u6740\u7684\u539f\u56e0\uff0c\u4ec0\u4e48\u4eba\u6709\u90a3\u6837\u7684\u80fd\u529b\uff0c\u628a\u8fd9\u4e9b\u5f3a\u5927\u7684\u95e8\u6d3e\uff0c\u7ed3\u5408 \u5728\u4e00\u8d77\u3002\u770b\u6765\uff0c\u8fd9\u4ef6\u4e8b\u53ea\u6709\u65e5\u540e\u8bf7\u6c5f\u5343\u91cc\u89e3\u7b54\u4e86\u3002 \u3000\u3000\u738b\u5f64\u8d76\u56de\u4eac\u57ce\uff0c\u7b2c\u4e00\u4ef6\u5de5\u4f5c\uff0c\u8c03\u6d3e\u4e86\u5341\u540d\u5fc3\u8179\u4f8d\u536b\uff0c\u4f1a\u5408\u9a6c\u6c0f\u4e09\u82f1\uff0c\u5e03\u7f6e\u5728\u4e09\u516c\u4e3b\u5bdd\u5bab \u56db\u5468\uff0c\u540d\u4e49\u662f\u4fdd\u62a4\uff0c\u5b9e\u662f\u76d1\u89c6\u3002 \u3000\u3000\u7136\u540e\uff0c\u624d\u664b\u89c1\u7687\u4e0a\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u5982\u671f\u5f52\u6765\uff0c\u7687\u4e0a\u975e\u5e38\u9ad8\u5174\uff0c\u738b\u5f64\u53c8\u9690\u533f\u53bb\u8eab\u906d\u56f4\u6740\u7684\u4e8b\uff0c\u53ea\u63d0\u51fa\u6269\u5145\u5185\u5bab\u4f8d\u536b\u7684 \u4e8b\uff0c\u7687\u4e0a\u7acb\u523b\u7167\u51c6\u3002 \u3000\u3000\u4f11\u606f\u4e86\u4e00\u591c\uff0c\u517b\u8db3\u4e86\u7cbe\u795e\uff0c\u7b2c\u4e8c\u5929\u738b\u5f64\u624d\u53bb\u664b\u89c1\u4e09\u516c\u4e3b\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u7acb\u523b\u5728\u4e66\u623f\u4f20\u89c1\uff0c\u800c\u4e14\u6452\u9000\u5bab\u5973\uff0c\u7b11\u9053\uff1a\u201c\u738b\u7edf\u9886\uff0c\u6211\u77e5\u9053\u4f60\u6709\u5f88\u591a\u8bdd\u8981\u95ee\u6211\uff0c \u653e\u80c6\u7684\u8bf4\u5427\u3002\u201d \u3000\u3000\u5982\u6b64\u5f00\u95e8\u89c1\u5c71\u7684\u5766\u7136\uff0c\u53cd\u4f7f\u738b\u5f64\u4e3a\u4e4b\u4e00\u5446\uff0c\u6c89\u541f\u4e86\u4e00\u9635\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u8fdb\u5165\u8fc7\u96f7\u97f3\u5bfa \u4e48\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\uff01\u201d\u4e09\u516c\u4e3b\u9053\uff1a\u201c\u4ed6\u4eec\u5b89\u6392\u6211\u5728\u4e00\u5904\u5c71\u8c37\u4e2d\u7684\u4e00\u5ea7\u5927\u5ead\u9662\u4e2d\uff0c\u6d3e\u4e86\u5f88\u591a\u4eba\u76d1\u89c6\u6211 \u7684\u884c\u52a8\uff0c\u4e5f\u4f20\u4e86\u6211\u5f88\u591a\u6b66\u529f\u3002\u2019\u201d \u3000\u3000\u201c\u53ef\u662f\u9b54\u6559\u4e2d\u7684\u6b66\u529f\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u90a3\u4e9b\u6b66\u529f\u602a\u5f02\u3001\u8be1\u5947\uff0c\u548c\u4e2d\u539f\u6b66\u529f\u6709\u5f88\u5927\u7684\u4e0d\u540c\u3002\u201d \u3000\u3000\u201c\u9664\u6b64\u4e4b\u5916\u5462\uff1f\u201d\u738b\u5f64\u751a\u611f\u4e3a\u96be\u7684\u8bf4\uff1a\u201c\u4ed6\u4eec\u662f\u5426\u4f24\u5bb3\u5230\u4e09\u516c\u4e3b\uff1f\u201d \u3000\u3000\u4e09\u516c\u4e3b\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u53ea\u662f\u4e0d\u51c6\u6211\u79bb\u5f00\u90a3\u5ea7\u5ead\u9662\u3002\u201d \u3000\u3000\u201c\u4e09\u516c\u4e3b\u662f\u4e00\u9053\u98de\u8679\u82df\u5973\u4fa0\u5f1f\u5b50\uff0c\u82df\u5973\u4fa0\u88ab\u8a89\u4e3a\u6c5f\u6e56\u4e2d\u7b2c\u4e00\u5feb\u5251\uff0c\u5982\u82e5\u4e09\u516c\u4e3b\u672a\u906d\u7981 \u5236\uff0c\u5b9a\u7136\u548c\u4ed6\u4eec\u6709\u8fc7\u51b2\u7a81\u4e86\u2026\u2026\u201d\u738b\u5f64\u8bd5\u63a2\u7740\u95ee\u9053\u3002 \u3000\u3000\u201c\u53ef\u60dc\u7684\u662f\u4ed6\u4eec\u5728\u6211\u8eab\u4e0a\u4e0b\u4e86\u7981\u5236\u3002\u201d \u3000\u3000\u201c\u662f\u7528\u6bd2\u4e86\uff1f\u201d \u3000\u3000\u201c\u5927\u6982\u662f\u5427\u2026\u2026\u201d\u4e09\u516c\u4e3b\u9053\uff1a\u201c\u6211\u6b66\u529f\u867d\u672a\u5931\u53bb\uff0c\u4f46\u5185\u529b\u4e00\u76f4\u65e0\u6cd5\u63d0\u805a\uff0c\u6240\u4ee5\uff0c\u53ea\u597d\u542c \u4ed6\u4eec\u5b89\u6392\u4e86\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u4f20\u4f60\u9b54\u529f\uff0c\u5374\u53c8\u5728\u4e09\u516c\u4e3b\u8eab\u4e0a\u4e0b\u6bd2\uff0c\u90a3\u8981\u5982\u4f55\u4e60\u7ec3\u529f\u592b\u5462\uff1f\u201d \u3000\u3000\u201c\u542c\u8d77\u6765\uff0c\u786e\u5b9e\u662f\u4e00\u4ef6\u5f88\u77db\u76fe\u7684\u4e8b\uff0c\u4f46\u4ed6\u4eec\u786e\u5b9e\u8fd9\u6837\u505a\uff0c\u4ed6\u4eec\u4f20\u6211\u7684\u4ee5\u53e3\u8bc0\u53d8\u5316\u4e3a\u4e3b\uff0c \u4e5f\u53ef\u4ee5\u53bb\u7ec3\u4e60\uff0c\u53ea\u4e0d\u8fc7\uff0c\u771f\u529b\u65e0\u6cd5\u63d0\u805a\uff0c\u65bd\u5c55\u7684\u62f3\u3001\u638c\uff0c\u90fd\u662f\u82b1\u62f3\u3001\u7ee3\u817f\uff0c\u65e0\u6cd5\u5bf9\u4eba\u6784\u6210\u4f24 \u5bb3\u7f62\u4e86\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u5462\uff1f\u201d\u738b\u5f64\u4f4e\u58f0\u95ee\uff1a\u201c\u4e09\u516c\u4e3b\u662f\u5426\u5df2\u6062\u590d\u4e86\u771f\u529b\uff1f\u201d \u3000\u3000\u201c\u79bb\u5f00\u65f6\uff0c\u4ed6\u4eec\u53c8\u52a8\u4e86\u4e00\u79cd\u624b\u811a\u2026\u2026\u201d\u4e09\u516c\u4e3b\u8bf4\uff1a\u201c\u53ea\u53ef\u60dc\uff0c\u4ed6\u4eec\u662f\u5148\u8ba9\u6211\u660f\u8ff7\u8fc7\u53bb\uff0c \u9192\u6765\u65f6\uff0c\u611f\u89c9\u5230\u529f\u529b\u6b63\u5728\u6062\u590d\uff0c\u6240\u4ee5\uff0c\u6211\u4e00\u76f4\u65e0\u6cd5\u4e86\u89e3\uff0c\u4ed6\u4eec\u7528\u7684\u4ec0\u4e48\u65b9\u6cd5\uff0c\u4f7f\u6211\u7684\u529f\u529b\u9010 \u6e10\u6062\u590d\u3002\u201d \u3000\u3000\u738b\u5f64\u51b7\u773c\u89c2\u5bdf\uff0c\u4e09\u516c\u4e3b\u76ee\u5149\u6e05\u660e\uff0c\u8bf4\u8bdd\u6d41\u5229\uff0c\u5b8c\u5168\u6ca1\u6709\u53ef\u7591\u4e4b\u5904\uff0c\u5fc3\u4e2d\u6697\u6697\u5fd6\u9053\uff1a\u7687\u4e0a \u662f\u5979\u7684\u7236\u4eb2\uff0c\u53c8\u5bf9\u5979\u5ba0\u7231\u5f02\u5e38\uff0c\u5982\u82e5\u4e0d\u662f\u53d7\u5230\u592a\u6df1\u7684\u9b54\u672f\u63a7\u5236\uff0c\u5e94\u8be5\u8fd8\u8bb0\u5f97\u6df1\u539a\u7684\u7236\u5973\u4e4b \u60c5\u3002 \u3000\u3000\u7687\u4e0a\u5bf9\u738b\u5f64\u7ed9\u4e88\u4e86\u5145\u5206\u7684\u4fe1\u4efb\uff0c\u4f46\u4e5f\u6362\u5f97\u4e86\u738b\u5f64\u7684\u5fe0\u8bda\u3002 \u3000\u3000\u201c\u516c\u4e3b\u2026\u2026\u201d\u738b\u5f64\u8bf4\u9053\uff1a\u201c\u662f\u5426\u8fd8\u8bb0\u5f97\u548c\u5c5e\u4e0b\u7ea6\u5b9a\u7684\u4e00\u4ef6\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u4e09\u516c\u4e3b\u9053\uff1a\u201c\u7ea6\u5b9a\u7684\u4e8b\u60c5\uff0c\u6211\u5012\u4e00\u65f6\u95f4\u8bb0\u4e0d\u8d77\u6765\u4e86\uff0c\u4f60\u4e0d\u7528\u541e\u541e\u5410\u5410\uff0c\u5c31\u76f4\u63a5\u7684\u8bf4\u51fa\u6765 \u5427\uff01\u201d \u3000\u3000\u201c\u662f\u2026\u2026\u662f\u4e24\u5e74\u524d\u5728\u5f00\u5c01\u5e9c\u4e2d\u2026\u2026\u201d\u738b\u5f64\u6709\u4e9b\u82e6\u6da9\u7684\u8bf4\uff1a\u201c\u516c\u4e3b\u88ab\u62bc\u8d74\u897f\u57df\u4e4b\u524d\uff0c\u8981\u5c5e \u4e0b\u770b\u7684\u2026\u2026\u2014\u2014\u2026\u2026\u4e00\u4e2a\u2026\u2026\u201d \u3000\u3000\u201c\u4e00\u4e2a\u4ec0\u4e48\u5440\u2026\u2026\u201d\u4e09\u516c\u4e3b\u8bf4\uff1a\u201c\u4f60\u8bf4\u6e05\u695a\uff1f\u201d \u3000\u3000\u201c\u662f\u4e00\u4e2a\u7ea2\u8272\u7684\u5c0f\u70b9\u2026\u2026\u201d\u738b\u5f64\u9f13\u8db3\u4e86\u52c7\u6c14\uff0c\u8bf4\uff1a\u201c\u516c\u4e3b\u8fd8\u8bf4\uff0c\u5982\u662f\u90a3\u4e2a\u5c0f\u70b9\u4e0d\u5728\u4e86\uff0c \u5c31\u8981\u5c5e\u4e0b\u60f3\u529e\u6cd5\u6740\u6b7b\u516c\u4e3b\u2026\u2026\u201d \u3000\u3000\u201c\u4e00\u4e2a\u7ea2\u8272\u5c0f\u70b9\u2026\u2026\u201d\u4e09\u516c\u4e3b\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u82e5\u6709\u6240\u609f\u7684\uff0c\u7a81\u7136\u62c9\u8d77\u8863\u8896\uff0c\u9732\u51fa\u4e86\u6574\u6761\u7684 \u7389\u81c2\uff0c\u4e00\u4e0b\u5b50\u4f38\u5230\u4e86\u738b\u5f64\u7684\u9762\u524d\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u7684\uff0c\u662f\u4e0d\u662f\u8fd9\u4e2a\u554a\uff01\u201d \u3000\u3000\u201c\u5b88\u5bab\u7802\u2026\u2026\u201d\u738b\u5f64\u770b\u5230\u4e86\u90a3\u4e00\u70b9\u8273\u7ea2\uff0c\u4f46\u4e5f\u770b\u5230\u4e86\u516c\u4e3b\u6574\u6761\u624b\u81c2\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u5df2\u662f\u98ce\u59ff\u5ae3\u7136\u7684\u5c11\u5973\uff0c\u5979\u7684\u624b\u81c2\u5f88\u7f8e\uff0c\u7389\u6da6\u73e0\u5706\u3002 \u3000\u3000\u4f46\u5374\u770b\u5f97\u738b\u5f64\u5dee\u4e00\u70b9\u6655\u4e86\u8fc7\u53bb\uff0c\u5982\u82e5\u73b0\u5728\u6709\u4e00\u4e2a\u5bab\u5973\u8fdb\u6765\uff0c\u770b\u5230\u4e86\u8fd9\u4ef6\u4e8b\u4f20\u626c\u51fa\u53bb\uff0c\u88ab \u7687\u5e1d\u77e5\u9053\uff0c\u738b\u5f64\u53ea\u6709\u4e00\u6761\u8def\u597d\u8d70\uff0c\u90a3\u5c31\u662f\u6b7b\u3002 \u3000\u3000\u5982\u82e5\u516c\u4e3b\u628a\u8fd9\u4ef6\u4e8b\u594f\u660e\u7687\u4e0a\uff0c\u53ea\u8981\u7a0d\u52a0\u6cb9\u6dfb\u918b\u4e00\u756a\uff0c\u738b\u5f64\u5c31\u662f\u6ee1\u95e8\u6284\u65a9\u7684\u5927\u7f6a\u3002 \u3000\u3000\u516c\u4e3b\u7684\u5149\u6ed1\u7389\u81c2\u554a\uff01\u5982\u4f55\u80fd\u8ba9\u4eba\u767d\u767d\u7684\u770b\u5230\u3002 \u3000\u3000\u201c\u738b\u5f64\uff0c\u4f60\u770b\u6e05\u695a\u4e86\u5417\uff1f\u662f\u4e0d\u662f\u8bf4\u7684\u8fd9\u4e2a\u2026\u2026\u201d\u4e09\u516c\u4e3b\u8bf4\uff1a\u201c\u7ea2\u7ea2\u7684\u4e00\u5360\u2026\u2026\u201d \u3000\u3000\u201c\u662f\u662f\u662f\u2026\u2026\u201d\u738b\u5f64\u770b\u90a3\u70b9\u5b88\u5bab\u7802\u540e\uff0c\u65e9\u5df2\u95ed\u4e0a\u4e86\u773c\u775b\uff0c\u90a3\u91cc\u8fd8\u6562\u591a\u770b\u4e00\u773c\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u7f13\u7f13\u653e\u4e0b\u4e86\u8863\u8896\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u4f60\u662f\u4e00\u7247\u597d\u610f\uff0c\u738b\u5f64\uff0c\u6211\u4e0d\u4f1a\u602a\u4f60\u7684\uff0c\u4e5f \u4e0d\u4f1a\u544a\u8bc9\u4efb\u4f55\u4eba\u3002\u201d \u3000\u3000\u201c\u591a\u8c22\u516c\u4e3b\u2026\u2026\u201d\u738b\u5f64\u7adf\u7136\u606d\u606d\u656c\u656c\u7684\u53e9\u4e0a\u4e86\u4e00\u4e2a\u5934\uff0c\u8868\u793a\u4e86\u7531\u8877\u7684\u8c22\u610f\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u5fae\u7b11\u5982\u82b1\u7684\u9053\uff1a\u201c\u73b0\u5728\uff0c\u738b\u7edf\u9886\u8fd8\u6709\u4ec0\u4e48\u6000\u7591\u4e48\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\uff01\u201d\u738b\u5f64\u7ad9\u8d77\u8eab\u4e86\uff0c\u9053\uff1a\u201c\u5c5e\u4e0b\u544a\u9000\u3002\u201d \u3000\u3000\u4e09\u516c\u4e3b\u4f4e\u58f0\u8bf4\uff1a\u201c\u738b\u7edf\u9886\uff0c\u6ca1\u6709\u4e8b\uff0c\u5e0c\u671b\u4f60\u5e38\u6765\u2018\u542c\u8749\u9662\u2019\u63a2\u671b\u6211\uff0c\u8fd9\u7247\u5e7f\u5927\u7684\u7687\u5bab\u4e4b \u4e2d\uff0c\u80fd\u548c\u6211\u8bf4\u4e0a\u51e0\u53e5\u8bdd\u7684\u4eba\u5374\u4e0d\u591a\u3002\u201d \u3000\u3000\u201c\u662f\u662f\u662f\u2026\u2026\u201d\u738b\u5f64\u611f\u89c9\u80cc\u810a\u4e0a\u5347\u8d77\u4e86\u4e00\u80a1\u5bd2\u610f\uff0c\u9053\uff1a\u201c\u5c5e\u4e0b\u5c0a\u547d\u3002\u201d \u3000\u3000\u8f6c\u8eab\u75be\u9000\u800c\u53bb\u3002 \u3000\u3000\u671b\u7740\u738b\u5f64\u8fdc\u53bb\u80cc\u5f71\uff0c\u4e09\u516c\u4e3b\u5634\u89d2\u95f4\uff0c\u6cdb\u8d77\u4e86\u4e00\u80a1\u51b7\u51b7\u7684\u7b11\u610f\u3002 \u3000\u3000\u5982\u82e5\u738b\u5f64\u56de\u5934\u770b\u4e00\u4e0b\uff0c\u4ee5\u738b\u5f64\u7684\u4e30\u5bcc\u9605\u5386\uff0c\u4e00\u5b9a\u53ef\u4ee5\u53d1\u73b0\u90a3\u51b7\u51b7\u7684\u7b11\u610f\u4e2d\uff0c\u9690\u542b\u7740\u4e00\u80a1 \u6740\u673a\u3002 \u3000\u3000\u53ef\u60dc\uff0c\u738b\u5f64\u6ca1\u6709\u56de\u5934\u3002 \u3000\u3000\u548c\u4e09\u516c\u4e3b\u8fd9\u6b21\u7684\u4f1a\u9762\uff0c\u4f7f\u738b\u5f64\u5ffd\u7136\u751f\u51fa\u4e86\u5f88\u5927\u7684\u656c\u754f\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u6ca1\u6709\u53ef\u7591\u7684\u5730\u65b9\uff0c\u4f46\u738b\u5f64\u5374\u6709\u4e9b\u4e0d\u6562\u518d\u89c1\u4e09\u516c\u4e3b\u4e86\u3002 \u3000\u3000\u7b2c\u4e09\u5929\uff0c\u8fd1\u5348\u65f6\uff0c\u738b\u91cd\u5c71\u5728\u71d5\u98de\u7684\u5f15\u5bfc\u4e0b\u8fdb\u5165\u4e86\u7687\u5bab\u3002 \u3000\u3000\u201c\u6765\u7684\u597d\uff0c\u91cd\u5c71\uff0c\u6c5f\u5144\u4e5f\u6765\u4e86\u5427\uff01\u71d5\u98de\uff0c\u5feb\u53bb\u8bf7\u6c5f\u5144\u8fdb\u5bab\u4e00\u53d9\u3002\u201d \u3000\u3000\u738b\u5f64\u5fc3\u4e2d\u5927\u611f\u9ad8\u5174\u7740\u3002 \u3000\u3000\u201c\u6162\u7740\u2026\u2026\u201d\u738b\u91cd\u5c71\u963b\u6b62\u4e86\u71d5\u98de\u7684\u884c\u52a8\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4e8c\u53d4\uff0c\u6c5f\u5927\u4fa0\u8bf4\uff0c\u4ed6\u5230\u4eac\u4e2d\u7684\u4e8b\uff0c \u8d8a\u5c11\u4eba\u77e5\u9053\u8d8a\u597d\uff0c\u4ed6\u8981\u4e8c\u53d4\u4fbf\u88c5\u51fa\u5bab\uff0c\u5728\u57ce\u5916\u767d\u4e91\u5bfa\u4e2d\u89c1\u9762\uff0c\u4e8c\u53d4\u5982\u8981\u5e26\u4eba\u540c\u53bb\uff0c\u4e5f\u53ea\u80fd\u5e26 \u71d5\u5144\u540c\u884c\u738b\u5f64\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201d\u6211\u77e5\u9053\u4e86\uff01\u201c\u738b\u91cd\u5c71\u7acb\u523b\u544a\u8f9e\u9000\u51fa\u3002 \u3000\u3000\u9001\u8d70\u4e86\u738b\u91cd\u5c71\uff0c\u71d5\u98de\u91cd\u56de\u5230\u738b\u5f64\u7684\u7edf\u9886\u5ba4\u4e2d\uff0c\u9053\uff1a\u201c\u5927\u4eba\uff0c\u4e0b\u5348\u4e4b\u884c\uff0c\u5c5e\u4e0b\u662f\u5426\u8981 \u53bb\uff1f\u201d \u3000\u3000\u201c\u8981\uff01\u201d\u738b\u5f64\u8bf4\uff1a\u201c\u6c5f\u5343\u91cc\u5982\u6b64\u5b89\u6392\uff0c\u5fc5\u6709\u4f5c\u7528\uff0c\u4f60\u5148\u79bb\u5f00\uff0c\u54b1\u4eec\u5728\u767d\u4e91\u5bfa\u4e2d\u89c1 \u9762\u2026\u2026\u201d\u653e\u4f4e\u4e86\u58f0\u97f3\uff0c\u5631\u5490\u71d5\u98de\u6570\u8bed\u3002 \u3000\u3000\u71d5\u98de\u70b9\u70b9\u5934\uff0c\u6084\u7136\u800c\u53bb\u3002 \u3000\u3000\u7ecf\u8fc7\u4e86\u90a3\u6b21\u65e0\u7aef\u800c\u6765\u7684\u5927\u56f4\u6740\u4e4b\u540e\uff0c\u738b\u5f64\u63d0\u9ad8\u4e86\u8b66\u89c9\uff0c\u5f53\u771f\u662f\u676f\u5f13\u86c7\u5f71\uff0c\u5bf9\u4efb\u4f55\u4e8b\u60c5\u90fd \u5b58\u4e09\u5206\u6000\u7591\u3002 \u3000\u3000\u5bf9\u6c5f\u5343\u91cc\u7684\u7a81\u7136\u6765\u4eac\uff0c\u738b\u5f64\u5fc3\u4e2d\u5b9e\u6709\u7740\u65e0\u6bd4\u7684\u6b22\u6b23\uff0c\u4ed6\u5fc3\u4e2d\u6709\u592a\u591a\u7684\u7591\u95ee\uff0c\u4e5f\u8bb8\u6c5f\u5343\u91cc \u80fd\u4e3a\u4ed6\u4f5c\u4e00\u4e2a\u660e\u786e\u7684\u89e3\u91ca\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u6c5f\u6e56\u4e2d\u4efb\u4f55\u79d8\u5bc6\uff0c\u90fd\u65e0\u6cd5\u7792\u8fc7\u6c5f\u5343\u91cc\u3002 \u3000\u3000\u4f46\u6df1\u4e00\u5c42\u60f3\uff0c\u6c5f\u5e72\u91cc\u7a81\u7136\u5165\u4eac\uff0c\u548c\u4ed6\u9884\u5b9a\u7684\u884c\u7a0b\u4e0d\u5408\uff0c\u8fd9\u5e94\u8be5\u662f\u4e13\u7a0b\u8d76\u6765\uff0c\u5982\u975e\u6709\u7279\u522b \u4e8b\u6545\uff0c\u6c5f\u5343\u91cc\u4e0d\u4f1a\u6539\u53d8\u8ba1\u5212\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u738b\u5f64\u6068\u4e0d\u5f97\u7acb\u523b\u89c1\u5230\u6c5f\u5343\u91cc\u3002 \u3000\u3000\u4f46\u4ed6\u4ecd\u7136\u6309\u637a\u7740\u5fc3\u5934\u7684\u7126\u8651\uff0c\u4f9d\u4f8b\u5de1\u89c6\u4e86\u7687\u5bab\u4e00\u904d\uff0c\u624d\u66f4\u6362\u8863\u670d\uff0c\u6e9c\u51fa\u5bab\u53bb\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u7126\u6025\uff0c\u76f4\u5954\u57ce\u5916\u767d\u4e91\u5bfa\uff0c\u4e00\u76f4\u6b65\u5165\u5927\u6bbf\uff0c\u7247\u523b\u4e4b\u540e\uff0c\u71d5\u98de\u4e00\u8eab\u9752\u5e03\u8863\u670d\uff0c\u884c\u540c\u519c \u592b\u7684\u8ddf\u5165\u5927\u6bbf\uff0c\u9053\uff1a\u201c\u5c5e\u4e0b\u770b\u7684\u5f88\u4ed4\u7ec6\uff0c\u672a\u89c1\u8ddf\u8e2a\u7684\u4eba\u3002\u201d \u3000\u3000\u539f\u6765\uff0c\u71d5\u98de\u5949\u547d\u5148\u5728\u767d\u4e91\u5bfa\u5916\u57cb\u4f0f\uff0c\u770b\u770b\u662f\u5426\u6709\u4eba\u8ddf\u8e2a\u738b\u5f64\u800c\u6765\u3002 \u3000\u3000\u4e00\u4e2a\u5c0f\u6c99\u5f25\u7531\u5927\u6bbf\u540e\u9762\u8f6c\u4e86\u51fa\u6765\uff0c\u884c\u8fd1\u4e09\u5f64\u65f6\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u7531\u540e\u95e8\u51fa\u53bb\uff0c\u95e8\u5916\u662f\u4e00\u7247 \u6d53\u5bc6\u7684\u7af9\u6797\uff0c\u738b\u91cd\u5c71\u4f1a\u5e26\u4f60\u4eec\u53bb\u2026\u2026\u201d \u3000\u3000\u6562\u60c5\uff0c\u8fd9\u4eba\u662f\u9ed1\u7f57\u6c49\uff0c\u672c\u95e8\u672c\u884c\uff0c\u81ea\u7136\u662f\u626e\u88c5\u7684\u5341\u5206\u4f20\u795e\u4e86\u3002 \u3000\u3000\u201c\u8fd9\u5c31\u662f\u6709\u4e9b\u6545\u4f5c\u795e\u79d8\u4e86\uff01\u201d \u3000\u3000\u201c\u4f60\u4eec\u88ab\u4eba\u8ddf\u8e2a\u4e86\u2026\u2026\u201d\u9ed1\u7f57\u6c49\u4f4e\u58f0\u9053\uff1a\u201c\u90a3\u662f\u7b2c\u4e00\u6d41\u7684\u8ddf\u8e2a\u9ad8\u624b\uff0c\u907f\u5f00\u71d5\u98de\u7684\u76d1\u89c6\uff0c \u5982\u82e5\u8fd0\u6c14\u597d\uff0c\u5c0f\u548c\u5c1a\u53ef\u80fd\u6d3b\u6349\u4f4f\u4ed6\uff0c\u8ba9\u4f60\u738b\u5927\u4eba\u5f00\u5f00\u773c\u754c\uff0c\u4e5f\u8bb8\uff0c\u8fd8\u662f\u4f60\u4eec\u719f\u4eba\u5462\uff1f\u201d \u3000\u3000\u738b\u5f64\u6709\u5982\u88ab\u4eba\u5728\u524d\u80f8\u4e0a\u6253\u4e86\u4e00\u62f3\u822c\uff0c\u51e0\u4e4e\u8981\u5410\u51fa\u4e00\u53e3\u8840\u6765\uff0c\u4ed6\u81ea\u89c9\u90e8\u7f72\u7684\u5f88\u4e25\u5bc6\u4e86\uff0c\u4f46 \u5374\u4e00\u65e0\u53d1\u73b0\uff0c\u6697\u6697\u53f9\u606f\u4e00\u58f0\uff0c\u5feb\u6b65\u5411\u540e\u884c\u53bb\u3002 \u3000\u3000\u71d5\u98de\u7d27\u968f\u5728\u738b\u5f64\u8eab\u540e\u3002 \u3000\u3000\u767d\u4e91\u5bfa\u540e\u95e8\u5916\uff0c\u679c\u7136\u662f\u4e00\u7247\u6d53\u5bc6\u7684\u7af9\u6797\uff0c\u738b\u5f64\u7acb\u523b\u51b2\u5165\u4e86\u7af9\u6797\u4e4b\u4e2d\u3002 \u3000\u3000\u201c\u8fd9\u91cc\u2026\u2026\u201d\u738b\u91cd\u5c71\u679c\u7136\u65e9\u5df2\u5728\u6797\u4e2d\u7b49\u5019\uff0c\u5e26\u7740\u738b\u5f64\u3001\u71d5\u98de\u5728\u6797\u4e2d\u7a7f\u884c\u4e86\u4e00\u9635\uff0c\u7adf\u7136\u5230 \u4e86\u4e00\u5ea7\u8305\u820d\u524d\u9762\u3002 \u3000\u3000\u90a3\u8305\u820d\u5b8c\u5168\u9690\u85cf\u5728\u6d53\u5bc6\u7af9\u6797\u4e4b\u4e2d\uff0c\u5c31\u7b97\u884c\u5230\u8fd1\u524d\uff0c\u4e5f\u662f\u65e0\u6cd5\u77a7\u5230\uff0c\u9664\u975e\uff0c\u4f60\u65e9\u5df2\u77e5\u9053\u8fd9 \u91cc\u6709\u4e00\u5ea7\u8305\u820d\u3002 \u3000\u3000\u738b\u91cd\u5c71\u63a8\u5f00\u5ba4\u95e8\uff0c\u8ba9\u738b\u5f64\u3001\u71d5\u98de\u8fdb\u5165\u5ba4\u4e2d\uff0c\u81ea\u5df1\u5e76\u672a\u8ddf\u8fdb\uff0c\u60f3\u662f\u7559\u5728\u5916\u9762\u8b66\u6212\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u65e9\u5df2\u5750\u5728\u5ba4\u4e2d\u7b49\u5019\u3002 \u3000\u3000\u71d5\u98de\u4e0a\u524d\u62dc\u89c1\uff0c\u5374\u88ab\u6c5f\u5343\u91cc\u963b\u6b62\uff0c\u9053\uff1a\u201c\u738b\u5144\uff0c\u5144\u5f1f\u5f15\u4f60\u5230\u6b64\u5730\u6765\uff0c\u4e0d\u662f\u6545\u4f5c\u795e\u79d8\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\uff01\u201d\u738b\u5f64\u8bf4\uff1a\u201c\u53ef\u662f\u6709\u4eba\u6df7\u5165\u4e86\u6211\u7edf\u7387\u7684\u5927\u5185\u4f8d\u536b\u4e4b\u4e2d\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u5927\u5185\u4f8d\u536b\u4e2d\uff0c\u5df2\u7ecf\u6709\u4e86\u4ed6\u4eec\u4e0d\u5c11\u7684\u4eba\uff0c\u53ea\u4e0d\u8fc7\uff0c\u4e0d\u662f\u4ed6\u4eec\u6d3e\u4eba\u6df7\u5165\u7684\u2026\u2026\u201d\u6c5f \u5e72\u91cc\u8bf4\uff1a\u201c\u4ed6\u4eec\u5c31\u5728\u4f60\u7684\u5927\u5185\u4f8d\u536b\u4e2d\u9009\u62e9\u5438\u6536\uff0c\u53d8\u4e3a\u5df1\u7528\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u201d\u738b\u5f64\u7684\u9876\u95e8\u4e0a\u6c81\u51fa\u4e86\u6c57\u6c34\uff0c\u9053\uff1a\u201c\u4e0d\u592a\u53ef\u80fd\u554a\uff01 \u3000\u3000\u2026\u2026\u3000\u3000\u3000\u3000\u201c\u201d\u662f\u5343\u771f\u4e07\u786e\u7684\u4e8b\u2026\u2026\u201c\u6c5f\u5343\u91cc\u8bf4\uff1a\u201d\u6211\u8fd8\u60f3\u4e0d\u51fa\u6765\uff0c\u4ed6\u4eec\u7528\u4ec0\u4e48\u529e \u6cd5\uff0c\u628a\u4ed6\u4eec\u5438\u6536\u8fc7\u53bb\uff0c\u6211\u76f8\u4fe1\uff0c\u90a3\u4e9b\u4f8d\u536b\uff0c\u90fd\u662f\u4f60\u7cbe\u5fc3\u6311\u9009\u7684\u4eba\u2026\u2026\u201c \u3000\u3000\u201c\u662f\uff01\u4e0d\u4f46\u4eba\u54c1\u3001\u51fa\u8eab\uff0c\u90fd\u7ecf\u8fc7\u67e5\u8bc1\u2026\u2026\u201d\u738b\u5f64\u8bf4\uff1a\u201c\u8fde\u5bb6\u4e16\u4ea4\u5f80\uff0c\u4e5f\u90fd\u8c03\u67e5\u5f97\u5f88\u6e05 \u695a\u2026\u2026\u8fd9\u2026\u2026\u201d \u3000\u3000\u201c\u738b\u5144\uff0c\u4e0d\u7528\u96be\u8fc7\uff0c\u9053\u9ad8\u4e00\u5c3a\uff0c\u9b54\u9ad8\u4e00\u4e08\uff0c\u654c\u6697\u6211\u660e\uff0c\u5148\u5929\u4e0a\u5c31\u5403\u4e86\u5927\u4e8f\u2026\u2026\u201d\u6c5f\u5343\u91cc \u7b11\u9053\uff1a\u201c\u5982\u82e5\u6c5f\u67d0\u4eba\u5b89\u6392\u7684\u4e0d\u9519\uff0c\u9ed1\u7f57\u6c49\u5e94\u8be5\u4f1a\u6d3b\u6349\u4ed6\u4eec\u4e00\u4e2a\u6765\uff0c\u8ba9\u4f60\u95ee\u51fa\u4ed6\u4eec\u88ab\u4eba\u5438\u6536\u7684 \u5185\u60c5\u2026\u2026\u201d \u3000\u3000\u201c\u5509\u2026\u2026\u201d\u738b\u5f64\u65e0\u9650\u611f\u6168\u7684\u8bf4\u9053\uff1a\u201c\u6c5f\u6e56\u662f\u8d8a\u8d70\u8d8a\u6015\uff0c\u670b\u53cb\u662f\u6108\u6765\u6108\u5c11\uff0c\u771f\u4ee4\u4eba\u5bd2\u5fc3\u5f97 \u5f88\u554a\uff01\u201d \u3000\u3000\u6c5f\u5343\u91cc\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u738b\u5144\uff0c\u4f60\u4e00\u5411\u8c6a\u6c14\u5e72\u4e91\uff0c\u600e\u4f1a\u5ffd\u7136\u95f4\u53d8\u5f97\u5982\u6b64\u6d88\u6781\u4e86\uff1f\u201d \u3000\u3000\u201c\u6c5f\u5144\uff0c\u60f3\u5fc5\u65e9\u5df2\u77e5\u6653\u5144\u5f1f\u79bb\u5f00\u5c01\u65f6\uff0c\u9047\u4e0a\u4e86\u4e00\u6b21\u5927\u89c4\u6a21\u7684\u56f4\u6740\uff1f\u201d \u3000\u3000\u201c\u4f60\u6743\u9ad8\u4f4d\u91cd\uff0c\u81ea\u7136\u662f\u654c\u4eba\u4f17\u591a\uff0c\u4f46\u4f60\u6beb\u53d1\u65e0\u635f\uff0c\u8db3\u89c1\u9ad8\u660e\u4e86\u3002\u201d \u3000\u3000\u542c\u53e3\u6c14\uff0c\u6c5f\u5343\u91cc\u663e\u7136\u662f\u4e0d\u77e5\u9053\u8fd9\u6b21\u5927\u56f4\u6740\u7684\u4e8b\u4ef6\u3002 \u3000\u3000\u201c\u770b\u6765\uff0c\u6c5f\u6e56\u4e0a\u4e5f\u6709\u4f60\u6c5f\u5144\u4e0d\u77e5\u9053\u7684\u4e8b\u60c5\u2026\u2026\u201d\u738b\u5f64\u8bf4\uff1a\u201c\u5144\u5f1f\u8fd9\u6ee1\u8179\u72d0\u7591\uff0c\u53ea\u6015\u4e5f\u5f88 \u96be\u627e\u5230\u7b54\u6848\u4e86\u3002 \u3000\u3000\u201c\u542c\u8d77\u6765\uff0c\u597d\u50cf\u662f\u4e00\u4ef6\u5927\u4e8b\uff0c\u53ef\u5426\u4ed4\u7ec6\u7684\u8bf4\u51fa\u6765\uff0c\u5144\u5f1f\u4e5f\u53ef\u63d0\u4f9b\u6211\u4e00\u5f97\u4e4b\u611a\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4ef6\u4e8b\u7ed9\u4eba\u7684\u9707\u60ca\uff0c\u7edd\u4e0d\u5728\u53d1\u73b0\u5185\u5bab\u4f8d\u536b\u4e2d\u6f5c\u4f0f\u6709\u654c\u4eba\u5978\u7ec6\u4e4b\u4e0b\u2026\u2026\u201d\u738b\u5f64\u8bf4\uff1a\u201c\u5bf9 \u6211\u7684\u5927\u56f4\u6740\u4e2d\uff0c\u7adf\u7136\u53d1\u73b0\u4e86\u5c11\u6797\u3001\u6b66\u5f53\u3001\u4e10\u5e2e\u4e2d\u4eba\u2026\u2026\u201d \u3000\u3000\u201c\u662f\u771f\u7684\u4e48\uff1f\u2026\u2026\u201d\u8fd9\u4e00\u4e0b\uff0c\u6c5f\u5343\u91cc\u53d7\u5230\u7684\u9707\u60ca\uff0c\u5c24\u8fc7\u738b\u5f64\u3002 \u3000\u3000\u201c\u738b\u5144\u4e0d\u4f1a\u770b\u9519\u5427\u2026\u2026\u201d\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u4e5f\u8bb8\u662f\u6709\u4eba\u5192\u5145\u4ed6\u4eec\u5462\uff1f\u201d \u3000\u3000\u201c\u6839\u672c\u5c31\u770b\u4e0d\u5230\u2026\u2026\u201d\u738b\u5f64\u8bf4\uff1a\u201c\u4ed6\u4eec\u8eab\u7740\u9ed1\u8863\uff0c\u8138\u4e0a\u4e5f\u7528\u9ed1\u5dfe\u8499\u4e86\u8d77\u6765\uff0c\u5728\u4e0b\u770b\u51fa\u4ed6 \u4eec\u7684\u8eab\u4efd\uff0c\u662f\u7531\u6b66\u529f\u4e0a\u5bdf\u89c9\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u4ed4\u7ec6\u7684\u8bf4\u7ed9\u6211\u542c\u542c\uff1f\u201d \u3000\u3000\u201c\u5c31\u662f\u6c5f\u5144\u4e0d\u95ee\uff0c\u5144\u5f1f\u4e5f\u8981\u8bf4\u4e2a\u660e\u767d\u3002\u76ee\u4e0b\uff0c\u738b\u67d0\u4eba\u5df2\u89c9\u5f97\u6574\u4e2a\u6b66\u6797\u540c\u9053\uff0c\u90fd\u5728\u548c\u6211\u4e3a \u654c\uff0c\u76f8\u8bc6\u6ee1\u5929\u4e0b\uff0c\u7adf\u7136\u6ca1\u6709\u4e00\u4e2a\u53ef\u4ee5\u4fe1\u4efb\u7684\u670b\u53cb\uff0c\u53ea\u6709\u4f60\u6c5f\u5144\u662f\u552f\u4e00\u7684\u4f8b\u5916\u3002\u201d \u3000\u3000\u201c\u4e0d\u4f1a\u90a3\u4e48\u51c4\u51c9\u5427\uff01\u6162\u6162\u7684\u8bf4\u51fa\u7ecf\u8fc7\uff0c\u6211\u8981\u4f60\u8bf4\u7684\u4ed4\u7ec6\uff0c\u4e0d\u80fd\u6f0f\u5931\u4efb\u4f55\u5c0f\u8282\u3002\u201d \u3000\u3000\u738b\u5f64\u679c\u7136\u5f88\u4ed4\u7ec6\u7684\u8bf4\u660e\u4e86\u4e8b\u60c5\u7ecf\u8fc7\u3002 \u3000\u3000\u201c\u8fd9\u4e48\u5e9e\u5927\u7684\u4eba\u529b\uff0c\u4e0d\u662f\u4efb\u4f55\u4e00\u4e2a\u957f\u8001\u3001\u62a4\u6cd5\u6d3e\u7684\u4eba\u7269\u6240\u80fd\u8c03\u52a8\uff0c\u6811\u7531\u6839\u4e0a\u8d77\uff0c\u5173\u952e\u4eba \u7269\uff0c\u6050\u6015\u5728\u638c\u95e8\u4eba\u7684\u8eab\u4e0a\u4e86\u2026\u2026\u201d\u6c5f\u5343\u91cc\u8bf4\uff1a\u201c\u4e5f\u53ea\u6709\u638c\u95e8\u4eba\uff0c\u624d\u6709\u6743\u8c03\u52a8\u51fa\u5982\u6b64\u4f17\u591a\u7684\u95e8 \u4e0b\u3001\u9ad8\u624b\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e00\u70b9\uff0c\u6211\u4e5f\u60f3\u8fc7\u2026\u2026\u201d\u738b\u5f64\u8bf4\uff1a\u201c\u8ba9\u6211\u4f24\u5fc3\u7684\u4e5f\u5728\u6b64\u5904\u4e86\uff0c\u5c11\u6797\u3001\u6b66\u5f53\uff0c\u4e24\u6d3e\u638c \u95e8\uff0c\u548c\u738b\u67d0\u662f\u5f88\u597d\u7684\u670b\u53cb\uff0c\u6211\u4e5f\u5e2e\u8fc7\u4ed6\u4eec\u5f88\u5927\u7684\u5fd9\uff0c\u81f3\u4e8e\u4e10\u5e2e\u7684\u9ec4\u5e2e\u4e3b\u548c\u6211\u738b\u5f64\u7684\u4ea4\u60c5\uff0c\u6c5f \u5144\u5f88\u6e05\u695a\u4e86\uff0c\u60c5\u540c\u5144\u5f1f\u3002 \u3000\u3000\u738b\u67d0\u4eba\u5e2e\u4e10\u5e2e\u7684\u5fd9\uff0c\u8bf4\u4e0d\u4e0a\u5929\u9ad8\u5730\u539a\uff0c\u4e5f\u7b97\u5f97\u662f\u65bd\u6069\u6df1\u91cd\u4e86\uff0c\u53ef\u662f\uff0c\u4ed6\u4eec\u5374\u8054\u624b\u5bf9\u4ed8\u6211 \u738b\u67d0\u4eba\uff01\u4e0d\u4f46\u6ca1\u6709\u4e86\u670b\u53cb\u7684\u4ea4\u60c5\uff0c\u7b80\u76f4\u662f\u6709\u4e9b\u5fd8\u6069\u8d1f\u4e49\uff0c\u4e3a\u6b64\u4e8b\uff0c\u6211\u771f\u6709\u4e9b\u75db\u65ad\u809d\u80a0\u4e86\u3002\u201c \u3000\u3000\u6c5f\u5343\u91cc\u6025\u9053\uff1a\u201c\u738b\u5144\uff0c\u4f60\u91c7\u53d6\u4e86\u4ec0\u4e48\u884c\u52a8\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u8fd8\u6ca1\u6709\uff0c\u6709\u4e9b\u5173\u8282\uff0c\u6211\u8fd8\u60f3\u4e0d\u660e\u767d\u2026\u2026\u201d\u738b\u5f64\u8bf4\uff1a\u201c\u5929\u4e0b\u6b66\u6797\u540c\u9053\uff0c\u65e2\u7136\u90fd\u4e0d\u8bb2\u4e49\u6c14 \u4e86\uff0c\u90a3\u4e5f\u4e0d\u80fd\u602a\u6211\u738b\u67d0\u4eba\u5fc3\u72e0\u624b\u8fa3\u4e86\uff0c\u7b49\u6211\u627e\u51fa\u4e00\u70b9\u8bc1\u636e\uff0c\u6211\u5c31\u4f7f\u4ed6\u4eec\u95e8\u6d88\u6d3e\u6563\uff0c\u6c38\u96be\u5728\u6c5f \u6e56\u4e0a\u7acb\u8db3\u3002\u201d \u3000\u3000\u201c\u8fd8\u597d\uff0c\u4f60\u5f88\u614e\u91cd\u2026\u2026\u201d\u6c5f\u5343\u91cc\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u786e\u7136\u53ef\u6068\uff0c\u4e0d\u8fc7\uff0c\u4e2a\u4e2d\u7684 \u539f\u56e0\uff0c\u53ef\u80fd\u4e0d\u5982\u4f60\u60f3\u7684\u5355\u7eaf\uff0c\u5982\u82e5\u6c5f\u6e56\u4e0a\u5404\u5927\u95e8\u6d3e\uff0c\u90fd\u88ab\u4f60\u7528\u5b98\u65b9\u52bf\u529b\uff0c\u5937\u4e3a\u5e73\u5730\uff0c\u7559\u4e0b\u8fd9 \u4e00\u7247\u5927\u7a7a\u9699\uff0c\u4ec0\u4e48\u4eba\u4f1a\u628a\u5b83\u586b\u8865\u8d77\u6765\u5462\uff1f\u201d \u3000\u3000\u738b\u5f64\u6c89\u601d\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u9b54\u6559\uff01\u201d \u3000\u3000\u201c\u5bf9\uff0c\u9b54\u6559\u4e2d\u4eba\uff0c\u5c31\u53ef\u4ee5\u4e0d\u8d39\u5439\u7070\u4e4b\u529b\uff0c\u8fdb\u5165\u4e2d\u539f\u6c5f\u6e56\u9053\u4e0a\uff0c\u6536\u7f57\u5404\u5927\u95e8\u6d3e\u7684\u6b8b\u5175\u8d25 \u5c06\uff0c\u7ec4\u6210\u4e00\u80a1\u65e0\u6bd4\u5f3a\u5927\u7684\u52bf\u529b\uff0c\u90a3\u65f6\u4e2d\u539f\u6b66\u6797\uff0c\u5c3d\u5165\u9b54\u6559\u7684\u638c\u63e1\u4e86\u3002\u201d \u3000\u3000\u201c\u591a\u627f\u6c5f\u5144\u6307\u6559\uff0c\u738b\u67d0\u4eba\u51e0\u4e4e\u9020\u6210\u5927\u9519\u3002\u201d \u3000\u3000\u987f\u4e86\u4e00\u987f\u3002 \u3000\u3000\u63a5\u7740\u53c8\u9053\uff1a\u201c\u4f46\u4ed6\u4eec\u5df2\u4e3a\u9b54\u6559\u6240\u7528\uff0c\u7559\u4e0b\u4ed6\u4eec\uff0c\u4e5f\u4e0d\u8fc7\u9661\u589e\u654c\u4eba\u7684\u5b9e\u529b\u7f62\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u54b1\u4eec\u53ea\u80fd\u63a8\u65ad\u662f\u9b54\u6559\u6240\u7b56\u52a8\u8fd9\u6b21\u5927\u56f4\u6740\uff0c\u4f46\u8fd8\u662f\u6ca1\u6709\u8bc1\u636e\uff0c\u4e5f\u60f3\u4e0d\u901a\u4ed6\u4eec \u5982\u4f55\u80fd\u7b56\u52a8\u5230\u5404\u5927\u95e8\u6d3e\u7684\u638c\u95e8\u4eba\uff0c\u542c\u4ece\u4ed6\u4eec\u7684\u6307\u4ee4\uff0c\u6d3e\u51fa\u90a3\u4e48\u591a\u9ad8\u624b\uff0c\u53d1\u52a8\u8fd9\u6b21\u56f4\u6740\uff0c\u8fd9\u4e2d \u95f4\uff0c\u6700\u91cd\u8981\u662f\u4e8b\u4ef6\u8fc7\u7a0b\uff0c\u600e\u4e48\u4f1a\u9020\u6210\u8fd9\u6837\u4e00\u4e2a\u5c40\u9762\uff1f\u201d \u3000\u3000\u201c\u662f\u554a\uff01\u738b\u67d0\u60f3\u7834\u4e86\u8111\u888b\uff0c\u4e5f\u60f3\u4e0d\u51fa\u539f\u56e0\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u5509\uff01\u738b\u5144\uff0c\u8fd9\u662f\u65e0\u6cd5\u60f3\u901a\u7684\u4e8b\uff0c\u5fc5\u987b\u8981\u7740\u624b\u53bb\u8ffd\u67e5\uff0c\u5982\u6b64\u5927\u89c4\u6a21\u7684\u884c\u52a8\uff0c\u67e5\u8d77\u6765\u5e76\u4e0d \u592a\u96be\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c31\u91cd\u6258\u6c5f\u5144\u4e86\u2026\u2026\u201d\u738b\u5f64\u9053\uff1a\u201c\u6211\u5012\u5e0c\u671b\u80fd\u5c3d\u65e9\u67e5\u660e\u8fd9\u4ef6\u4e8b\u60c5\u3002\u201d \u3000\u3000\u201c\u770b\u6765\uff0c\u76ee\u4e0b\u4e09\u4ef6\u4e8b\u60c5\uff0c\u4ef6\u4ef6\u90fd\u5f88\u91cd\u8981\uff0c\u4e00\u4ef6\u4e5f\u8f7b\u5ffd\u4e0d\u5f97\u201d\u4e09\u4ef6\u4e8b\u60c5\u2026\u2026\u201c\u738b\u5f64\u9053\uff1a\u201d \u90a3\u4e09\u4ef6\u4e8b\uff1f\u6211\u60f3\u6765\u60f3\u53bb\uff0c\u53ea\u6709\u4e24\u4ef6\u3002\u201c\u201d\u7b2c\u4e00\uff0c\u5148\u8981\u67e5\u660e\u5185\u5bab\u4f8d\u536b\u4e2d\uff0c\u6709\u591a\u5c11\u5df2\u4e3a\u9b54\u6559\u6240 \u7528\uff0c\u4ed6\u4eec\u5982\u4f55\u7684\u88ab\u4eba\u5438\u6536\u2026\u2026\u201c\u6c5f\u5343\u91cc\u9053\uff1a\u201d\u5f53\u7136\uff0c\u6700\u91cd\u8981\u7684\u662f\u4eca\u540e\u8981\u5982\u4f55\u9632\u6b62\uff01\u201c \u3000\u3000\u738b\u5f64\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5bf9\uff01\u4e8b\u5173\u7687\u4e0a\u7684\u5b89\u5371\uff0c\u7cfb\u5929\u4e0b\u7684\u52a8\u4e71\uff0c\u4e0d\u80fd\u7a0d\u5b58\u5927\u610f\u4e4b\u5fc3\u3002\u201d \u3000\u3000\u201c\u7b2c\u4e8c\u4ef6\u2026\u2026\u201d\u6c5f\u5343\u91cc\u8bf4\uff1a\u201c\u8981\u5c3d\u5feb\u67e5\u51fa\u6765\uff0c\u4ed6\u4eec\u5982\u4f55\u80fd\u7b56\u52a8\u5404\u6d3e\u7684\u9996\u8111\u4eba\u7269\uff0c\u542c\u547d\u884c \u4e8b\uff0c\u6b64\u4e8b\u5173\u7cfb\u7740\u6574\u4e2a\u6b66\u6797\u7684\u5b58\u4ea1\u5ef6\u7eed\uff0c\u4e5f\u4e0d\u80fd\u6389\u4ee5\u8f7b\u5fc3\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e24\u4ef6\u90fd\u662f\u5927\u4e8b\uff0c\u53ef\u662f\u7b2c\u4e09\u4ef6\u53c8\u662f\u4ec0\u4e48\u4e8b\u5462\uff1f\u201d\u738b\u5f64\u9053\uff1a\u201c\u5728\u4e0b\u5c31\u60f3\u4e0d\u901af\uff1f\u201d \u3000\u3000\u201c\u8fd9\u624d\u662f\u771f\u6b63\u7684\u5927\u4e8b\uff0c\u53ea\u6709\u4ed6\u624d\u80fd\u5e2e\u52a9\u4f60\u2026\u2026\u201d\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u8fd8\u8981\u4f60\u5927\u529b\u8d5e\u52a9\u624d \u6210\u3002\u4ed6\u627f\u53d7\u4e86\u4f60\u8fd9\u4efd\u6069\u6cfd\uff0c\u5bf9\u4f60\u662f\u6709\u767e\u5229\u800c\u65e0\u4e00\u5bb3\u3002\u201d \u3000\u3000\u201c\u8bf4\u5427\uff0c\u53ea\u8981\u6211\u529b\u6240\u80fd\u53ca\uff0c\u7edd\u5bf9\u5168\u529b\u4ee5\u8d74\u3002\u201d \u3000\u3000\u201c\u6b64\u7269\uff0c\u53ea\u6709\u5927\u5185\u6709\uff0c\u4e0d\u8fc7\uff0c\u4f60\u867d\u662f\u5185\u5bab\u7edf\u7387\u8eab\u4efd\uff0c\u4e5f\u672a\u5fc5\u4e00\u5b9a\u80fd\u591f\u5230\u624b\uff0c\u5f53\u7136\uff0c\u6211\u4e0d \u4f1a\u903c\u8feb\u4f60\uff0c\u9876\u591a\u662f\u62d6\u5ef6\u4e2a\u4e00\u5e74\u7684\u65f6\u95f4\uff0c\u4f46\u5c31\u76ee\u4e0b\u60c5\u52bf\u6765\u770b\uff0c\u8fd9\u4e00\u5e74\u591a\u707e\u591a\u96be\uff0c\u53ea\u6015\u4e0d\u6613\u6e21\u8fc7 \u4e86\u3002\u201d \u3000\u3000\u71d5\u98de\u65e0\u7aef\u7aef\u7684\u5fc3\u5934\u4e00\u8df3\u3002 \u3000\u3000\u201c\u8bf4\u554a\uff01\u6c5f\u5144\uff0c\u6211\u5df2\u7ecf\u7b54\u5e94\u4f60\u4e86\uff0c\u9664\u975e\u662f\u6211\u529e\u4e0d\u5230\uff0c\u90a3\u5c31\u6ca1\u6709\u6cd5\u5b50\u4e86\u3002\u201d \u3000\u3000\u201c\u7687\u5bab\u4e4b\u5185\uff0c\u6709\u4e00\u682a\u5343\u5e74\u53c2\u738b\u2026\u2026\u201d\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u4f60\u80fd\u591f\u6c42\u5230\u624b\u5417\uff1f\u201d \u3000\u3000\u201c\u5343\u5e74\u53c2\u738b\uff1f\u201d\u738b\u5f64\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u53ea\u6709\u4e00\u682a\u4e48\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\uff0c\u53c2\u738b\u53ea\u6709\u4e00\u682a\uff0c\u4e0d\u8fc7\uff0c\u5343\u5e74\u8001\u53c2\u5012\u6709\u4e0d\u5c11\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u96be\u4e86\uff0c\u5982\u662f\u6709\u4e2a\u4e09\u4e94\u682a\uff0c\u5728\u4e0b\u76f8\u4fe1\u53ef\u4ee5\u6c42\u5f97\u4e00\u682a\uff0c\u53ea\u6709\u4e00\u682a\u2026\u2026\u201d\u738b\u5f64\u9053\uff1a\u201c\u5728 \u4e0b\u5f00\u53e3\u4e5f\u65e0\u6cd5\u5f00\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u5982\u679c\u53ea\u662f\u533b\u75c5\u7597\u4f24\uff0c\u8001\u5b9e\u8bf4\u7528\u4e0d\u5230\u5343\u5e74\u53c2\u738b\uff0c\u51e0\u767e\u5e74\u7684\u91ce\u5c71\u53c2\uff0c\u4e5f\u662f\u4e00\u6837 \u7684\u6548\u7528\uff0c\u5f53\u7136\uff0c\u53ea\u6709\u4e00\u682a\u7684\u4e1c\u897f\uff0c\u7687\u4e0a\u4e5f\u4e0d\u4f1a\u8f7b\u6613\u7b54\u5141\u9001\u4eba\uff0c\u6240\u4ee5\u8fd9\u5c31\u8981\u7528\u4e9b\u5fc3\u673a\u4e86\u3002\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u6c5f\u5144\uff0c\u4f60\u7684\u610f\u601d\u662f\u5077\u4e86\uff1f\u201d \u3000\u3000\u201c\u79fb\u82b1\u63a5\u6728\u2026\u2026\u201d\u6c5f\u5343\u91cc\u8bf4\uff1a\u201c\u6709\u5343\u5e74\u53c2\u738b\u4e4b\u52a9\uff0c\u53ef\u4f7f\u5c0f\u71d5\u5b50\u63d0\u524d\u4e00\u5e74\u51fa\u5e08\uff0c\u53c2\u738b\u5230\u624b \u4e00\u4e2a\u6708\uff0c\u5c0f\u71d5\u5b50\u5c31\u53ef\u4ee5\u6765\u4eac\u4e2d\u52a9\u4f60\u4e86\uff01\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u79fb\u82b1\u63a5\u6728\u2026\u2026\u201d\u738b\u5f64\u8bf4\uff1a\u201c\u79fb\u82b1\u63a5\u6728\u5fc5\u5f97\u5148\u6709\u82b1\u624d\u6210\uff0c\u82b1\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u82b1\u5c31\u5728\u6728\u7684\u65c1\u8fb9\uff0c\u90a3\u682a\u53c2\u738b\u548c\u4e94\u767e\u5e74\u4ee5\u4e0a\u91ce\u53c2\uff0c\u76f8\u5dee\u4e0d\u591a\uff0c\u4f60\u53ea\u8981\u628a\u5b83\u4eec\u6362\u4e2a\u4f4d\u7f6e\u5c31 \u884c\u4e86\u3002\u201d \u3000\u3000\u987f\u4e86\u4e00\u987f\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u63a5\u7740\u9053\uff1a\u201c\u8fd9\u4e5f\u53ef\u80fd\u662f\u6740\u5934\u7684\u5927\u7f6a\uff0c\u6240\u4ee5\uff0c\u6211\u4e0d\u613f\u5f3a\u8feb\u4f60\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u8bf4\uff0c\u53ef\u80fd\u8ba9\u5c0f\u71d5\u5b50\u63d0\u524d\u4e00\u5e74\u51fa\u5e08\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u201c\u5c0f\u71d5\u5b50\u771f\u80fd\u5e2e\u52a9\u6211\u5316\u89e3\u53bb\u5404\u79cd\u5927\u96be\uff0c\u4e5f\u4fdd\u8bc1\u53ef\u4ee5\u4f7f\u7687\u4e0a\u5b89\u5168\uff1f\u201d \u3000\u3000\u201c\u5bf9\uff01\u201d\u6c5f\u5e72\u91cc\u9053\uff1a\u201c\u6211\u53ef\u4ee5\u62ff\u4eba\u5934\u62c5\u4fdd\u3002\u201d \u3000\u3000\u738b\u5f64\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u6c5f\u5144\uff0c\u8fd9\u4ef6\u4e8b\u8ba9\u6211\u8003\u8651\u4e00\u4e0b\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u53ef\u4ee5\uff0c\u6211\u8bf4\u8fc7\uff0c\u4e0d\u4f1a\u5f3a\u8feb\u4f60\uff0c\u4f60\u6162\u6162\u7684\u60f3\u5427\uff01\u201d \u3000\u3000\u4f46\u95fb\u6728\u95e8\u5440\u7136\u800c\u5f00\uff0c\u9ed1\u7f57\u6c49\u4e03\u5de7\u50e7\uff0c\u624b\u63d0\u4e00\u4e2a\u9752\u886b\u9752\u88e4\u7684\u5e74\u8f7b\u4eba\u884c\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u201c\u7830\u201d\u7136\u4e00\u58f0\uff0c\u6454\u5728\u4e86\u5730\u4e0a\u3002 \u3000\u3000\u71d5\u98de\u770b\u4e86\u90a3\u5e74\u8f7b\u4eba\u4e00\u773c\uff0c\u5931\u58f0\u800c\u53eb\u3002 \u3000\u3000\u738b\u5f64\u6ca1\u6709\u53eb\u51fa\u58f0\uff0c\u4f46\u4ed6\u8138\u8272\u94c1\u9752\uff0c\u5168\u8eab\u5728\u5fae\u5fae\u7684\u6296\u52a8\u3002 \u3000\u3000\u4e0d\u9519\uff0c\u8fd9\u5e74\u8f7b\u4eba\u6b63\u662f\u5927\u5185\u4f8d\u536b\uff0c\u800c\u65e6\u662f\u975e\u5e38\u80fd\u5e72\u7684\u4f8d\u536b\uff0c\u738b\u5f64\u8ba4\u8bc6\uff0c\u71d5\u98de\u4e5f\u8ba4\u8bc6\u8fd9\u4e2a \u4eba\u3002 \u3000\u3000\u201c\u8c2d\u4fe1\u2026\u2026\u201d\u738b\u5f64\u867d\u7136\u5c3d\u529b\u5728\u63a7\u5236\u81ea\u5df1\uff0c\u4f46\u58f0\u97f3\u4ecd\u7136\u6709\u70b9\u53d1\u6296\u3002 \u3000\u3000\u4e03\u5de7\u50e7\u9053\uff1a\u201c\u6211\u70b9\u4e86\u4ed6\u7684\u6655\u7a74\u2026\u2026\u201d\u53f3\u624b\u6325\u52a8\uff0c\u89e3\u5f00\u5e74\u8f7b\u4eba\u7684\u6655\u7a74\uff0c\u4f46\u5374\u53c8\u987a\u624b\u70b9\u4e86\u4ed6 \u7684\u56db\u80a2\u4e0a\u7a74\u9053\u3002 \u3000\u3000\u5e74\u8f7b\u4eba\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u62ac\u8138\u770b\u5230\u4e86\u738b\u5f64\uff0c\u633a\u8eab\u6b32\u8d77\uff0c\u4f46\u4ed6\u53cc\u817f\u3001\u53cc\u81c2\u7a74\u9053\u90fd\u88ab\u70b9\u5236\uff0c\u7adf \u7136\u672a\u5750\u8d77\u6765\u3002 \u3000\u3000\u201c\u8c2d\u4fe1\uff0c\u4f60\u597d\u5927\u80c6\u5b50\u554a\uff01\u201d\u738b\u5f64\u51b7\u8bf4\u9053\uff1a\u201c\u7adf\u6562\u8ffd\u8e2a\u672c\u5ea7\uff1f\u201d \u3000\u3000\u201c\u7edf\u9886\uff01\u201d\u8c2d\u4fe1\u8bf4\uff1a\u201c\u5728\u4e0b\u662f\u6697\u4e2d\u968f\u884c\uff0c\u4fdd\u62a4\u7edf\u9886\uff0c\u6b63\u662f\u4e00\u7247\u5fe0\u8bda\uff0c\u8fd8\u671b\u5927\u4eba\u660e\u5bdf\u3002\u201d \u3000\u3000\u201c\u5509\uff01\u201d\u738b\u5f64\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u6b7b\u5230\u4e34\u5934\uff0c\u8fd8\u8981\u5f3a\u8bcd\u72e1\u8fa9\uff0c\u544a\u8bc9\u6211\uff0c\u7a76\u7adf\u662f\u600e\u4e48\u56de \u4e8b\uff1f\u4ec0\u4e48\u4eba\u547d\u4ee4\u4f60\u6697\u4e2d\u76d1\u89c6\u6211\u7684\u884c\u52a8\uff1f\u201d \u3000\u3000\u201c\u7687\u4e0a\uff01\u201d\u8c2d\u4fe1\u8bf4\uff1a\u201c\u4e0d\u76f8\u4fe1\uff0c\u4f60\u5c31\u53bb\u95ee\u95ee\u5427\uff1f\u201d \u3000\u3000\u201c\u7687\u4e0a\uff01\u201d\u738b\u5f64\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u53ef\u662f\u771f\u7684\uff1f\u201d \u3000\u3000\u201c\u662f\u554a\uff01\u4f60\u8ba4\u4e3a\u7687\u4e0a\u8fd8\u5f88\u4fe1\u4efb\u4f60\u4e48\uff1f\u201d\u8c2d\u4fe1\u8bf4\uff1a\u201c\u65e9\u5df2\u7ecf\u5bf9\u4f60\u6000\u7591\u4e86\uff0c\u6240\u4ee5\uff0c\u8981\u6211\u6697\u4e2d \u76d1\u89c6\u4f60\uff0c\u627e\u51fa\u6bdb\u75c5\uff0c\u4f9d\u6cd5\u5904\u6b7b\u3002\u201d \u3000\u3000\u8bf4\u5b8c\uff0c\u54c8\u54c8\u5927\u7b11\u8d77\u6765\u3002 \u3000\u3000\u738b\u5f64\u832b\u7136\u4e86\uff0c\u663e\u7136\uff0c\u4ed6\u65e0\u6cd5\u5206\u8fa8\u51fa\u8c2d\u4fe1\u8bf4\u7684\u8bdd\u662f\u771f\u662f\u5047\u3002 \u3000\u3000\u81ea\u4ece\u5927\u56f4\u6740\u4e4b\u540e\uff0c\u738b\u5f64\u5bf9\u4efb\u4f55\u4eba\u90fd\u5931\u53bb\u4e86\u4fe1\u5fc3\uff0c\u4f55\u51b5\uff0c\u4f34\u541b\u5982\u4f34\u864e\uff0c\u7687\u4e0a\u662f\u6700\u96be\u76f8\u4fe1\u7684 \u4eba\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u738b\u5144\uff0c\u4ed6\u5728\u80e1\u8bf4\uff1f\u201d \u3000\u3000\u738b\u5f64\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8c2d\u4fe1\uff0c\u6211\u8bb0\u5f97\u4f60\u662f\u5211\u90e8\u5c1a\u4e66\u63a8\u8350\u7684\u4eba\uff0c\u597d\u50cf\u662f\u51fa\u8eab\u534e\u5c71\u6d3e\u5427\uff01\u4e5f \u8bb8\uff0c\u4f60\u8ba4\u4e3a\u6211\u4e0d\u6562\u53bb\u95ee\u7687\u4e0a\uff0c\u4f46\u4f60\u9519\u4e86\uff0c\u4e0d\u8fc7\uff0c\u4f60\u53ef\u77e5\u9053\uff0c\u6211\u95ee\u8fc7\u7687\u4e0a\u7684\u540e\u679c\u4e48\uff1f\u201d \u3000\u3000\u8c2d\u4fe1\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5927\u4e0d\u4e86\u4e00\u6761\u547d\u5427\uff01\u603b\u4e0d\u80fd\u8ba9\u6211\u6b7b\u4e24\u6b21\uff1f\u201d \u3000\u3000\u201c\u5bf9\uff01\u4f60\u53ea\u4f1a\u6b7b\u4e00\u6b21\u2026\u2026\u201d\u738b\u5f64\u8bf4\uff1a\u201c\u4e0d\u8fc7\uff0c\u63a8\u8350\u4f60\u5165\u5bab\u7684\u5211\u90e8\u5c1a\u4e66\uff0c\u4f1a\u7b2c\u4e00\u4e2a\u53d7\u5230\u682a \u8fde\uff0c\u4f60\u7684\u7236\u6bcd\u5144\u59b9\u4e00\u5bb6\u4eba\uff0c\u4e5f\u90fd\u5c06\u53d7\u5230\u8bdb\u7edd\uff0c\u80cc\u53db\u7687\u4e0a\uff0c\u662f\u6ee1\u95e8\u8bdb\u7edd\u7684\u5927\u7f6a\uff0c\u73b0\u5728\uff0c\u6211\u5c31\u5e26 \u4f60\u56de\u5b83\u9762\u5723\u3002\u201d \u3000\u3000\u201c\u6162\u6765\uff0c\u6162\u6765\u2026\u2026\u201d\u8c2d\u4fe1\u9053\uff1a\u201c\u4e00\u4eba\u505a\u4e8b\u4e00\u4eba\u5f53\uff0c\u4e3a\u4ec0\u4e48\u8981\u8bdb\u8fde\u5230\u6211\u7684\u5bb6\u4eba\u5462\uff1f\u201d \u3000\u3000\u201c\u56fd\u6cd5\u5982\u6b64\uff01\u201d\u738b\u5f64\u9053\uff1a\u201c\u8c01\u4e5f\u5e2e\u4e0d\u4e0a\u5fd9\u3002\u201d \u3000\u3000\u8c2d\u4fe1\u9053\uff1a\u201c\u5982\u82e5\u6211\u8bb2\u4e86\u5b9e\u8bdd\uff0c\u80fd\u4e0d\u80fd\u9976\u8fc7\u6211\u7684\u5bb6\u4eba\u5462\uff1f\u201d \u3000\u3000\u201c\u90a3\u53ea\u6709\u4e00\u4e2a\u529e\u6cd5\uff01\u201d\u738b\u5f64\u8bf4\uff1a\u201c\u8fde\u4f60\u7684\u6b7b\u7f6a\u4e5f\u514d\u4e86\uff0c\u672c\u7edf\u9886\u529e\u4f60\u4e2a\u758f\u5ffd\u804c\u5b88\uff0c\u9010\u51fa\u5927 \u5185\uff0c\u6c38\u4e0d\u5f55\u7528\uff0c\u4e0d\u8fc7\uff0c\u6211\u8981\u5e9f\u4e86\u4f60\u7684\u6b66\u529f\u3002\u201d \u3000\u3000\u8c2d\u4fe1\u9053\uff1a\u201c\u771f\u7684\u4e48\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u771f\u7684\uff01\u201d\u738b\u5f64\u8bf4\u9053\uff1a\u201c\u672c\u7edf\u9886\u4e00\u8bfa\u5343\u91d1\uff0c\u5929\u4e0b\u7686\u77e5\uff0c\u5f53\u7136\uff0c\u4f60\u5982\u679c\u4f9b\u8bcd\u660e\u6717\uff0c\u5f97 \u7834\u5927\u9006\uff0c\u6216\u53ef\u5c06\u529f\u6298\u7f6a\uff0c\u7559\u5728\u5bab\u5ef7\uff01\u201d \u3000\u3000\u201c\u6211\u4e0d\u8981\u7559\u5728\u5bab\u4e2d\uff0c\u53ea\u6c42\u4e0d\u8981\u8fde\u7d2f\u6211\u7684\u5bb6\u4eba\u5c31\u662f\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u8c2d\u4fe1\u9053\uff1a\u201c\u6211\u8bf4\u4e86\u4e5f\u662f\u767d\u8bf4\uff0c\u4f60\u7edd\u5bf9\u4e0d\u6562\u8ffd\u67e5\u4e0b\u53bb\u3002\u201d \u3000\u3000\u738b\u5f64\u6012\u9053\uff1a\u201c\u4f60\u8bf4\u8bf4\u770b\uff0c\u6211\u5012\u4e0d\u76f8\u4fe1\uff0c\u6211\u6ca1\u6709\u8ffd\u67e5\u4e0b\u53bb\u7684\u80c6\u91cf\u3002\u201d \u3000\u3000\u201c\u4e09\u516c\u4e3b\uff01\u201d\u8c2d\u4fe1\u9053\uff1a\u201c\u4f60\u771f\u6562\u8ffd\u67e5\u4e0b\u53bb\u4e48\uff1f\u201d \u3000\u3000\u738b\u5f64\u5446\u4f4f\u4e86\u3002 \u3000\u3000\u8c2d\u4fe1\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u53ec\u89c1\u6211\uff0c\u8981\u6211\u542c\u5979\u7684\u4ee4\u8c15\uff0c\u6697\u4e2d\u8ffd\u67e5\u4f60\u7684\u884c\u8e2a\uff0c\u5f53\u7136\uff0c\u4e0d\u53ea \u6211\u4e00\u4e2a\u6709\u8fd9\u79cd\u6b8a\u8363\uff0c\u6211\u76f8\u4fe1\u8fd8\u6709\u522b\u7684\u4f8d\u536b\u3002\u201d \u3000\u3000\u201c\u4e09\u516c\u4e3b\u7684\u76ee\u7684\u4f55\u5728\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\uff0c\u6211\u5c31\u4e0d\u77e5\u9053\u4e86\uff0c\u5979\u662f\u516c\u4e3b\u7684\u8eab\u4efd\uff0c\u4f60\u53ea\u662f\u4e2a\u5927\u5185\u7edf\u9886\uff0c\u4e24\u76f8\u6bd4\u8f83\uff0c\u5f53\u7136\uff0c\u5979\u8981 \u6bd4\u4f60\u7edf\u9886\u7684\u540e\u53f0\u624e\u5b9e\uff0c\u53ea\u662f\u6211\u4e00\u4e0b\u5b50\u8bf4\u9519\u4e86\u8bdd\uff0c\u88ab\u4f60\u6293\u5230\u628a\u67c4\uff0c\u6211\u5982\u6b7b\u4e0d\u8ba4\u5e10\uff0c\u4e00\u53e3\u54ac\u5b9a\u662f \u6697\u4e2d\u4fdd\u62a4\u4f60\uff0c\u6211\u76f8\u4fe1\u4e09\u516c\u4e3b\u5fc5\u80fd\u6551\u6211\u3002\u201d \u3000\u3000\u201c\u4f60\u9519\u4e86\uff0c\u4e09\u516c\u4e3b\u4e0d\u4f1a\u6551\u4f60\uff0c\u5979\u4f1a\u8bbe\u6cd5\u6740\u4f60\u706d\u53e3\u3002\u201d \u3000\u3000\u201c\u4e0d\u4f1a\u5427\uff01\u5979\u8bf4\u8fc7\uff0c\u53ea\u8981\u6211\u4e0d\u54ac\u5979\u51fa\u6765\uff0c\u5979\u4e00\u5b9a\u4f1a\u60f3\u529e\u6cd5\u89e3\u6551\u6211\u7684\u3002\u201d \u3000\u3000\u8fd9\u8c2d\u4fe1\u663e\u7136\u5f88\u5ae9\uff0c\u5168\u65e0\u6c5f\u6e56\u7ecf\u9a8c\uff0c\u60f3\u6cd5\u4e5f\u4e00\u6d3e\u5929\u771f\u3002 \u3000\u3000\u738b\u5f64\u56de\u987e\u71d5\u98de\u4e00\u773c\uff0c\u9053\uff1a\u201c\u70b9\u4ed6\u7684\u6655\u7a74\u3002\u201d \u3000\u3000\u738b\u5f64\u5401\u53e3\u6c14\uff0c\u53c8\u9053\uff1a\u201c\u6c5f\u5144\uff0c\u6211\u60f3\u6e05\u695a\u4e86\uff0c\u4f60\u90a3\u79fb\u82b1\u63a5\u6728\u7684\u65b9\u6cd5\u5f88\u597d\uff0c\u6211\u5728\u4e09\u5929\u4e4b\u5185\uff0c \u529e\u59a5\u8fd9\u4ef6\u4e8b\u60c5\u3002\u201d \u3000\u3000\u201c\u6709\u4e9b\u4e8b\u65e0\u6cd5\u516c\u4e8b\u516c\u529e\u3002\u201d\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u7687\u5bab\u4e22\u4e86\u4e00\u682a\u53c2\u738b\uff0c\u4f46\u5374\u4fdd\u4f4f\u9526\u7ee3\u6cb3\u5c71\uff0c\u7687\u5e1d \u77e5\u9053\u4e86\uff0c\u4e5f\u5e94\u8be5\u611f\u6fc0\u4f60\u738b\u5144\u624d\u662f\u3002\u201d \u3000\u3000\u201c\u5404\u4eba\u7684\u7b97\u6cd5\u4e0d\u540c\uff0c\u7687\u5e1d\u5982\u6709\u6211\u738b\u67d0\u4eba\u8fd9\u4e9b\u906d\u9047\uff0c\u6211\u76f8\u4fe1\u4ed6\u4f1a\u540c\u610f\uff0c\u53ef\u60dc\uff0c\u4ed6\u5750\u5728\u91d1\u6bbf \u7684\u9f99\u6905\u4e4b\u4e0a\uff0c\u600e\u4f1a\u60f3\u5230\u8fd9\u79cd\u4e8b\u60c5\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\uff0c\u6709\u4e9b\u4e8b\u4e5f\u53ea\u597d\u9ed8\u9ed8\u7684\u53bb\u505a\u4e86\u3002\u201d\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u53ea\u8981\u4ef0\u4e0d\u6127\u5929\uff0c\u5e9c\u4e0d\u4f5c\u5730\uff0c\u662f\u975e \u4e4b\u95f4\uff0c\u4e5f\u5c31\u65e0\u6cd5\u5206\u5f97\u592a\u6e05\u695a\u4e86\u3002\u201d \u3000\u3000\u738b\u5f64\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u6211\u5148\u544a\u9000\u4e86\uff0c\u4e09\u5929\u4e4b\u5185\uff0c\u4e0d\u7ba1\u4e8b\u60c5\u5982\u4f55\uff0c\u6211\u90fd\u6765\u89c1\u4f60\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u77a7\u77a7\u8c2d\u4fe1\uff0c\u9053\uff1a\u201c\u4f60\u51c6\u5907\u5982\u4f55\u5904\u7f6e\u8fd9\u5c0f\u5b50\uff1f\u201d \u3000\u3000\u201c\u6740\u4e86\u4ed6\uff0c\u4e0d\u8fc7\uff0c\u6211\u8981\u60f3\u4e2a\u529e\u6cd5\uff0c\u9690\u533f\u8d77\u4ed6\u7684\u7f6a\u540d\uff0c\u8fd9\u4ef6\u4e8b\u4e0d\u5b9c\u5f20\u626c\u3002\u201d \u3000\u3000\u201c\u5728\u4e0b\u5012\u6709\u4e00\u4e2a\u4e3b\u610f\uff0c\u4e0d\u77e5\u738b\u5144\u8ba4\u4e3a\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u60f3\u6765\u5fc5\u7136\u9ad8\u660e\uff01\u201d\u738b\u5f64\u8bf4\uff1a\u201c\u5728\u4e0b\u6d17\u8033\u606d\u542c\u3002\u201d \u3000\u3000\u201c\u70b9\u4ed6\u7684\u54d1\u7a74\u3002\u201d \u3000\u3000\u987f\u4e86\u4e00\u987f\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u53c8\u9053\uff1a\u201c\u5e26\u56de\u5bab\u4e2d\uff0c\u6682\u65f6\u5173\u8d77\u6765\uff0c\u6211\u76f8\u4fe1\uff0c\u5fc5\u6709\u4eba\u4f1a\u6740\u4ed6\u706d\u53e3\uff0c\u738b\u5144\u8bbe\u6cd5\u9690\u85cf\u8d77 \u6765\uff0c\u770b\u770b\u4ec0\u4e48\u4eba\u4e0b\u624b\u706d\u53e3\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u4f5c\u4e3a\u8bf1\u9975\uff0c\u591a\u6293\u51e0\u6761\u9c7c\u3002\u201d\u738b\u5f64\u9ad8\u5174\u7684\u8bf4\uff1a\u201c\u8fd8\u662f\u6c5f\u5144\u9ad8\u660e\u3002\u201d \u3000\u3000\u54ea\u77e5\uff0c\u6c5f\u5343\u91cc\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u9519\u4e86\uff0c\u53ea\u8981\u770b\u51fa\u4ed6\u7684\u8eab\u4efd\u5c31\u884c\uff0c\u4e0d\u8981\u6293\u4ed6\uff0c\u4e5f\u4e0d\u8981\u5f20\u626c\uff0c\u8fd9 \u65f6\u523b\uff0c\u8fd8\u4e0d\u662f\u4f60\u4eec\u548c\u4eba\u6b63\u9762\u51b2\u7a81\u7684\u65f6\u5019\uff0c\u4e00\u5207\u7b49\u5c0f\u71d5\u5b50\u6765\u4e86\u518d\u8bf4\u3002\u201d \u3000\u3000\u738b\u5f64\u60f3\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u5bf9\uff0c\u5c31\u7167\u6c5f\u5144\u7684\u5429\u5490\u53bb\u529e\u3002\u201d \u3000\u3000\u201c\u8fd9\u4ef6\u4e8b\u53ea\u6709\u4f60\u548c\u71d5\u98de\u77e5\u9053\u2026\u2026\u201d \u3000\u3000\u505c\u4e86\u4e00\u4f1a\u3002 \u3000\u3000\u6c5f\u5e72\u91cc\u53c8\u63a5\u7740\u9053\uff1a\u201c\u4f60\u73b0\u5728\u7684\u5904\u5883\uff0c\u5f53\u771f\u662f\u56db\u9762\u695a\u6b4c\uff0c\u4e00\u5207\u90fd\u8981\u591a\u591a\u5c0f\u5fc3\u554a\uff01\u201d \u3000\u3000\u738b\u5f64\u70b9\u70b9\u5934\uff0c\u544a\u8f9e\u800c\u53bb\u3002 \u3000\u3000\u8c2d\u4fe1\u88ab\u7981\u95ed\u5728\u5927\u5185\u4e00\u5ea7\u5e7d\u5ba4\u4e2d\uff0c\u5f53\u5929\u5c31\u88ab\u4eba\u6740\u4ee5\u706d\u53e3\u3002 \u3000\u3000\u738b\u5f64\u9690\u5728\u6697\u4e2d\u89c2\u5bdf\uff0c\u770b\u5f97\u4ed6\u51fa\u4e86\u4e00\u8eab\u51b7\u6c57\uff0c\u56e0\u4e3a\uff0c\u4e0b\u624b\u7684\u4eba\uff0c\u5c31\u662f\u5949\u547d\u770b\u5b88\u8c2d\u4fe1\u7684\u4f8d\u536b \u4e4b\u4e00\u91d1\u5929\u7965\u3002 \u3000\u3000\u91d1\u5929\u7965\u4e5f\u662f\u738b\u5f64\u5668\u91cd\u4f8d\u536b\u4e4b\u4e00\uff0c\u6b63\u51c6\u5907\u63d0\u5347\u4e3a\u4f8d\u536b\u9886\u73ed\u7684\u4eba\u3002 \u3000\u3000\u8fd9\u53d8\u6545\uff0c\u5b8c\u5168\u51fb\u6e83\u4e86\u738b\u5f64\u7684\u5fc3\u7406\u9632\u7ebf\uff0c\u4f7f\u4ed6\u611f\u89c9\u5230\u5185\u5fe7\u5916\u60a3\uff0c\u5df2\u7ecf\u8feb\u5728\u7709\u776b\u3002 \u3000\u3000\u7136\u800c\u3002 \u3000\u3000\u552f\u4e00\u80fd\u6551\u4ed6\u7684\uff0c\u4f3c\u662f\u53ea\u6709\u6c5f\u5343\u91cc\u53e3\u4e2d\u7684\u5c0f\u71d5\u5b50\u4e86\u3002 \u3000\u3000\u738b\u5f64\u6ca1\u6709\u5927\u529b\u8ffd\u7a76\u8c2d\u4fe1\u7684\u6b7b\u56e0\uff0c\u53ea\u628a\u4e09\u4e2a\u5949\u547d\u76d1\u5b88\u7684\u4f8d\u536b\u53f1\u8d23\u4e86\u4e00\u987f\u3002 \u3000\u3000\u5f53\u7136\uff0c\u8fd9\u4e5f\u662f\u6700\u8f7b\u7684\u5904\u5206\u3002 \u3000\u3000\u4f46\u738b\u5f64\u5df2\u4e0b\u5b9a\u51b3\u5fc3\u76d7\u53d6\u53c2\u5de5\u4e86\u3002 \u3000\u3000\u4ed6\u662f\u7687\u4e0a\u9762\u524d\u7684\u5927\u7ea2\u4eba\uff0c\u7ba1\u7406\u4ed3\u5e93\u7684\u592a\u76d1\uff0c\u5bf9\u4ed6\u4e5f\u793c\u8ba9\u4e09\u5206\u3002 \u3000\u3000\u4ed6\u501f\u53e3\u67e5\u9a8c\u5e93\u4e2d\u5b58\u5b9d\uff0c\u8f7b\u6613\u628a\u53c2\u738b\u76d7\u4e86\u51fa\u6765\uff0c\u4f46\u5374\u4f9d\u7167\u6c5f\u5343\u91cc\u7684\u5429\u5490\uff0c\u7528\u79fb\u82b1\u63a5\u6728\u4e4b \u8ba1\uff0c\u628a\u4e00\u682a\u6570\u767e\u5e74\u7684\u91ce\u5c71\u53c2\uff0c\u653e\u7f6e\u5728\u53c2\u738b\u7684\u4f4d\u7f6e\u4e0a\u3002 \u3000\u3000\u8fd9\u4e9b\u540d\u8d35\u4eba\u53c2\uff0c\u5927\u81f4\u76f8\u4eff\uff0c\u4e0d\u4ed4\u7ec6\u770b\uff0c\u4e5f\u77a7\u4e0d\u51fa\u4ec0\u4e48\u5dee\u5f02\uff0c\u738b\u5f64\u624b\u6cd5\u5feb\u901f\uff0c\u90a3\u592a\u76d1\u53c8\u672a \u6ce8\u610f\uff0c\u679c\u7136\u5168\u672a\u53d1\u89c9\u3002 \u3000\u3000\u7b2c\u4e09\u5929\u4e2d\u5348\u65f6\u5206\uff0c\u738b\u5f64\u53c8\u8d76\u5230\u7af9\u6797\u8305\u820d\u4e4b\u4e2d\uff0c\u89c1\u5230\u6c5f\u5343\u91cc\uff0c\u4ea4\u51fa\u53c2\u738b\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u4ed4\u7ec6\u7684\u770b\u4e86\u4e00\u773c\uff0c\u7b11\u9053\uff1a\u201c\u6709\u6b64\u53c2\u738b\uff0c\u5c0f\u71d5\u5b50\u53ef\u4ee5\u63d0\u524d\u4e00\u5e74\u51fa\u5e08\uff0c\u738b\u5144\uff0c\u4e00\u4e2a\u6708 \u5185\uff0c\u6211\u5e26\u5c0f\u71d5\u5b50\u6765\u89c1\u4f60\uff0c\u4f60\u8c28\u614e\u5c0f\u5fc3\uff0c\u8fd9\u4e00\u4e2a\u6708\u5185\uff0c\u5343\u4e07\u4e0d\u8981\u51fa\u6765\u3002\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u597d\uff01\u6211\u606d\u5019\u6c5f\u5144\u7684\u5927\u9a7e\u4e86\u3002\u201d \u5367\u9f99\u751f\u300a\u71d5\u5b50\u4f20\u5947\u300b \u7b2c\u5341\u56db\u56de\u3000\u96cf\u71d5\u5c55\u7fc5 \u3000\u3000\u8fd9\u4e00\u4e2a\u6708\u65f6\u95f4\u4e2d\uff0c\u738b\u5f64\u6218\u6218\u5162\u5162\uff0c\u6781\u5c3d\u5c0f\u5fc3\u8c28\u614e\uff0c\u6700\u5927\u7684\u91cd\u70b9\uff0c\u6446\u5728\u7687\u5e1d\u7684\u5b89\u5371\u4e4b\u4e0a\u3002 \u3000\u3000\u4ed6\u9003\u907f\u4e09\u516c\u4e3b\uff0c\u5c3d\u91cf\u907f\u4e0d\u89c1\u9762\u3002 \u3000\u3000\u53e6\u4e00\u4e2a\u7d27\u5f20\u4e0d\u5728\u738b\u5f64\u4e4b\u4e0b\u7684\u4eba\u662f\u71d5\u98de\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u628a\u5c0f\u71d5\u5b50\u8bf4\u7684\u592a\u795e\u4e86\uff0c\u4f46\u4e00\u4e2a\u5341\u51e0\u5c81\u7684\u5c0f\u5b69\uff0c\u600e\u4f1a\u6709\u5982\u6b64\u80fd\u8010\u5462\uff1f\u5bab\u5ef7\u4e2d\u7684\u6597 \u4e89\uff0c\u4e0d\u4f46\u6b66\u529f\u91cd\u8981\uff0c\u800c\u4e14\u667a\u6167\u4e5f\u6781\u91cd\u8981\uff0c\u5c0f\u71d5\u5b50\u90a3\u70b9\u5e74\u7eaa\uff0c\u5c31\u7b97\u6b66\u529f\u5b66\u597d\u4e86\uff0c\u4f46\u667a\u6167\u5462\uff1f\u518d \u806a\u660e\u7684\u4eba\uff0c\u5168\u65e0\u6c5f\u6e56\u5386\u7ec3\uff0c\u53c8\u80fd\u6709\u591a\u5927\u7684\u6210\u5c31\u5462\uff1f \u3000\u3000\u4f46\u60f3\u5230\u7236\u5b50\u76f8\u89c1\u5728\u5373\uff0c\u53c8\u5fcd\u4e0d\u4f4f\u90a3\u6ee1\u5fc3\u7684\u6b22\u559c\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u679c\u7136\u662f\u8a00\u800c\u6709\u4fe1\uff0c\u4e00\u4e2a\u6708\u9650\u671f\u5185\uff0c\u679c\u7136\u5e26\u7740\u82f1\u633a\u4fca\u96c5\u7684\u5e74\u8f7b\u4eba\uff0c\u91cd\u4e34\u4eac\u5e08\u3002 \u3000\u3000\u4f46\u8fd9\u4e00\u6b21\uff0c\u4ed6\u4eec\u4f1a\u9762\u7684\u5730\u65b9\uff0c\u4e0d\u662f\u90a3\u7af9\u6797\u4e2d\u7684\u8305\u820d\uff0c\u800c\u662f\u5728\u4e00\u5bb6\u6709\u540d\u7684\u5927\u5ba2\u6808\u5185\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u53ea\u6709\u5341\u4e03\u5c81\u591a\u4e00\u70b9\uff0c\u4f46\u5374\u957f\u7684\u9ad8\u5927\u82f1\u4fca\uff0c\u671b\u4e4b\u82e5\u4e8c\u5341\u8bb8\u4eba\u3002 \u3000\u3000\u5343\u5e74\u53c2\u738b\u7684\u795e\u6548\uff0c\u4f7f\u4ed6\u7684\u8eab\u8eaf\u3001\u667a\u6167\uff0c\u90fd\u63d0\u65e9\u6210\u719f\u4e86\u4e0d\u5c11\u3002 \u3000\u3000\u738b\u5f64\u89c1\u5230\u5c0f\u71d5\u5b50\u65f6\uff0c\u5b9e\u5728\u4e0d\u76f8\u4fe1\u4ed6\u53ea\u662f\u5341\u4e03\u5c81\u7684\u5c0f\u5b69\u5b50\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u8868\u73b0\u5f97\u6210\u719f\u6c89\u7740\uff0c\u66f4\u662f\u5927\u51fa\u4e86\u71d5\u98de\u7684\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u71d5\u98de\u6ca1\u6709\u540c\u6765\uff0c\u7167\u6c5f\u5343\u91cc\u8bbe\u8ba1\uff0c\u8981\u4ed6\u4eec\u7236\u5b50\u6700\u597d\u5148\u522b\u76f8\u8bc6\uff0c\u4ee5\u65b9\u4fbf\u5c0f\u71d5\u5b50\u7684\u5de5\u4f5c\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u7b2c\u4e00\u4e2a\u4efb\u52a1\uff0c\u662f\u5bf9\u4ed8\u4e09\u516c\u4e3b\uff0c\u6d88\u9664\u5b98\u4e2d\u7684\u5185\u5978\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u65e9\u5df2\u628a\u5bab\u4e2d\u5f62\u52bf\uff0c\u548c\u738b\u5f64\u7684\u5904\u5883\u8bf4\u7684\u5f88\u6e05\u695a\uff0c\u6240\u4ee5\uff0c\u5c0f\u71d5\u5b50\u4e5f\u672a\u53d1\u95ee\uff0c\u4f46\u738b\u5f64\u4ecd \u7136\u628a\u5bab\u4e2d\u60c5\u5f62\u5bf9\u5c0f\u71d5\u5b50\u5927\u7ea6\u518d\u8bf4\u4e86\u4e00\u904d\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u71d5\u6625\u98ce\uff0c\u63d0\u51fa\u4e86\u7b2c\u4e00\u4e2a\u95ee\u9898\uff0c\u9053\uff1a\u201c\u5982\u679c\u9b54\u6559\u4e2d\u4eba\uff0c\u5728\u5bab\u4e2d\u53d1\u5c55\u5b9e\u529b\uff0c\u5e94\u8be5\u5982\u4f55 \u5904\u7f6e\uff1f\u5c24\u5176\u662f\u516c\u4e3b\uff01\u201d \u3000\u3000\u8fd9\u4e00\u95ee\uff0c\u8fd8\u771f\u628a\u738b\u5f64\u95ee\u5012\u4e86\uff0c\u6c89\u541f\u4e86\u826f\u4e45\uff0c\u9053\uff1a\u201c\u6700\u597d\u662f\u80fd\u628a\u5979\u4e89\u53d6\u8fc7\u6765\uff0c\u5316\u53bb\u5979\u7684\u9b54 \u6027\u3002\u201d \u3000\u3000\u8fd9\u662f\u5012\u6253\u4e00\u9489\u8019\u7684\u96be\u9898\uff0c\u4f46\u71d5\u6625\u98ce\u5374\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53ef\u4ee5\u662f\u53ef\u4ee5\uff0c\u4f46\u8981\u4ee5\u9b54\u5236\u9b54\uff0c\u8fd9\u624b \u6bb5\u65b9\u9762\uff0c\u53ef\u6709\u4ec0\u4e48\u9650\u5236\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u9650\u5236\u3002\u201d \u3000\u3000\u987f\u4e86\u4e00\u987f\u3002 \u3000\u3000\u738b\u5f64\u53c8\u9053\uff1a\u201c\u53ea\u8981\u80fd\u4f7f\u5979\u9664\u53bb\u9b54\u6027\u5c31\u597d\uff0c\u5979\u662f\u4e00\u9053\u98de\u8679\u82df\u6167\u6708\u82df\u5973\u4fa0\u7684\u5f1f\u5b50\uff0c\u4e00\u8eab\u5251\u672f \u7cbe\u6e5b\u65e0\u5339\uff0c\u4e89\u53d6\u8fc7\u6765\uff0c\u5bf9\u6211\u4eec\u4e5f\u662f\u4e00\u5927\u52a9\u529b\u3002\u201d \u3000\u3000\u71d5\u6625\u98ce\u9053\uff1a\u201c\u5927\u4eba\uff0c\u53ef\u662f\u4f60\u8bf4\u7684\uff0c\u624b\u6bb5\u4e0d\u9650\u5236\u6211\uff0c\u5230\u65f6\u5019\uff0c\u53ef\u4e0d\u80fd\u8d23\u65a5\u6211\u5440\uff1f\u201d \u3000\u3000\u738b\u5f64\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4f60\u51c6\u7528\u4ec0\u4e48\u624b\u6bb5\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\u3002\u201d\u71d5\u6625\u98ce\u8bf4\uff1a\u201c\u53cd\u6b63\u662f\u4ee5\u9b54\u5236\u9b54\uff0c\u6211\u53ea\u80fd\u4fdd\u8bc1\u4f7f\u5979\u4fef\u9996\u5f52\u9644\u6211\u4eec\uff0c\u53ef\u4e0d\u80fd \u4fdd\u8bc1\u7528\u4ec0\u4e48\u624b\u6bb5\u3002\u201d \u3000\u3000\u738b\u5f64\u76b1\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u6c5f\u5144\uff0c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u5fc3\u4e2d\u6709\u4ec0\u4e48\u987e\u8651\uff0c\u5c3d\u91cf\u7684\u8bf4\u51fa\u6765\u5427\uff1f\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u90a3\u662f\u6df1\u5bab\u5185\u82d1\uff0c\u53ef\u4e0d\u80fd\u4e0d\u62e9\u624b\u6bb5\u7684\u65bd\u4e3a\uff0c\u4e09\u516c\u4e3b\u8fd8\u662f\u9ec4\u82b1\u5927\u95fa\u5973\u554a\uff01\u201d \u3000\u3000\u71d5\u6625\u98ce\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u7edf\u9886\u5927\u4eba\uff0c\u6cbe\u6709\u9b54\u6027\u7684\u4eba\uff0c\u7edd\u5bf9\u4e0d\u662f\u9ec4\u82b1\u95fa\u5973\uff0c\u6211\u5e08\u7236\u544a\u8bc9\u6211\uff0c \u7edd\u5bf9\u4e0d\u4f1a\u6709\u9519\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u201d\u738b\u5f64\u8bf4\uff1a\u201c\u6211\u770b\u5230\u4e09\u516c\u4e3b\u81c2\u4e0a\u7684\u5b88\u5bab\u7802\uff0c\u9c9c\u8273\u5982\u521d\u3002\u201d \u3000\u3000\u201c\u662f\u771f\u7684\uff1f\u201d\u71d5\u6625\u98ce\u5e74\u7eaa\u4e0d\u5927\uff0c\u77e5\u9053\u7684\u8fd8\u771f\u662f\u4e0d\u5c11\u3002 \u3000\u3000\u201c\u5f53\u7136\u662f\u771f\u7684\uff01\u201d\u738b\u5f64\u8bf4\uff1a\u201c\u6211\u4eb2\u773c\u6240\u89c1\uff0c\u90a3\u8fd8\u5047\u5f97\u4e86\u4e48\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u738b\u5144\uff0c\u8fd9\u65b9\u9762\uff0c\u6211\u4eec\u90fd\u662f\u5916\u884c\u3002\u5c0f\u71d5\u5b50\uff0c\u90a3\u5b88\u5bab\u7802\u80fd\u4e0d\u80fd\u9020\u5047\u5462\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u80fd\u3002\u201d\u71d5\u6625\u98ce\u8bf4\uff1a\u201c\u800c\u4e14\u5bb9\u6613\u5f97\u5f88\uff0c\u53ea\u8981\u7528\u6731\u7802\u5728\u81c2\u4e0a\u70b9\u4e00\u4e0b\u5c31\u884c\uff0c\u4e0d\u8fc7\uff0c\u4e5f\u5f88 \u597d\u9274\u522b\uff0c\u82e5\u7528\u624b\u4e00\u64e6\uff0c\u5047\u7684\u7acb\u523b\u88ab\u62b9\u6389\uff0c\u771f\u7684\u5219\u66f4\u89c1\u9c9c\u8273\u3002\u201d \u3000\u3000\u201c\u4f60\u8fd9\u70b9\u5e74\u7eaa\uff0c\u600e\u4e48\u4f1a\u77e5\u9053\u7684\u5982\u6b64\u591a\u5462\uff1f\u201d\u738b\u5f64\u95ee\u9053\u3002 \u3000\u3000\u201c\u964d\u9b54\u554a\uff01\u5e08\u7236\u544a\u8bc9\u6211\u5f88\u591a\u9b54\u6559\u7684\u6280\u827a\uff0c\u5176\u4e2d\u4e4b\u4e00\u5c31\u662f\u9a97\u2026\u2026\u201d \u3000\u3000\u71d5\u6625\u98ce\u9053\uff1a\u201c\u5927\u4eba\u548c\u6c5f\u4f2f\u7236\u90fd\u662f\u6b63\u4eba\u541b\u5b50\uff0c\u5f88\u5bb9\u6613\u5c31\u88ab\u4eba\u9a97\u8fc7\u53bb\uff0c\u53ef\u662f\u8981\u9a97\u6211\u5c0f\u71d5\u5b50\uff0c \u90a3\u5c31\u4e0d\u592a\u5bb9\u6613\u4e86\u3002\u201d \u3000\u3000\u738b\u5f64\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u53c8\u600e\u4e48\u80fd\u770b\u5230\u4e09\u516c\u4e3b\u81c2\u4e0a\u7684\u5b88\u5bab\u7802\u5462\uff1f \u3000\u3000\u8863\u8896\u906e\u63a9\uff0c\u5979\u5982\u679c\u5b58\u5fc3\u4e0d\u8ba9\u4f60\u770b\uff0c\u96be\u9053\u4f60\u8981\u5f3a\u770b\u4e0d\u6210\uff1f\u201c \u3000\u3000\u201c\u8fd9\u5c31\u4e0d\u7528\u5927\u4eba\u62c5\u5fc3\u4e86\u3002\u201d\u71d5\u6625\u98ce\u9053\uff1a\u201c\u6211\u4f1a\u60f3\u529e\u6cd5\u770b\u5230\u7684\u3002\u201d \u3000\u3000\u738b\u5f64\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u957f\u6c5f\u540e\u6d6a\u63a8\u524d\u6d6a\u554a\uff01\u738b\u67d0\u611f\u89c9\u5341\u5206\u56f0\u96be\u7684\u4e8b\uff0c\u4f60\u7adf\u7136\u4f1a\u6709\u529e\u6cd5 \u4e86\uff1f\u201d \u3000\u3000\u201c\u90fd\u662f\u56e0\u4e3a\u4f60\u4eec\u592a\u5c0a\u91cd\u4e09\u516c\u4e3b\u4e86\uff01\u201d\u71d5\u6625\u98ce\u9053\uff1a\u201c\u5fc3\u6709\u6240\u7591\uff0c\u4e5f\u4e0d\u6562\u6c42\u8bc1\u3002\u201d \u3000\u3000\u201c\u96be\u9053\u4f60\u4e0d\u5c0a\u91cd\uff0c\u5979\u53ef\u662f\u91d1\u679d\u7389\u53f6\u554a\uff01\u7687\u5e1d\u7684\u5973\u513f\u3002\u201d\u738b\u5f64\u8bf4\uff1a\u201c\u975e\u6bd4\u767e\u59d3\u5bb6\u7684\u59d1\u5a18 \u554a\uff01\u201d \u3000\u3000\u201c\u6211\u656c\u91cd\u4e09\u516c\u4e3b\u3002\u201d\u71d5\u6625\u98ce\u9053\uff1a\u201c\u4f46\u6211\u5fc5\u987b\u8981\u4e86\u89e3\u5979\u662f\u5426\u5df2\u6ca6\u5165\u9b54\u9053\uff0c\u5982\u679c\u5979\u5df2\u4e3a\u9b54\u5bab \u6240\u7528\uff0c\u5c31\u662f\u9b54\u5973\uff0c\u5fc5\u987b\u8981\u7528\u5bf9\u4ed8\u9b54\u5973\u7684\u624b\u6bb5\u5bf9\u4ed8\u5979\uff0c\u5426\u5219\uff0c\u6211\u4e5f\u4e0d\u7528\u8fdb\u5bab\u4e86\u3002\u201d \u3000\u3000\u738b\u5f64\u60f3\u4e86\u4e00\u4e0b\uff0c\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8bf4\u7684\u6709\u7406\uff0c\u5c31\u51c6\u4f60\u4fbf\u5b9c\u884c\u4e8b\u5427\uff01 \u3000\u3000\u4e0d\u8fc7\uff0c\u4e0d\u80fd\u5f04\u51fa\u5927\u7ee7\u6f0f\u6765\u3002\u201c\u201d\u5927\u4eba\u653e\u5fc3\uff0c\u5929\u5927\u7684\u4e8b\uff0c\u4e5f\u7531\u5c0f\u71d5\u5b50\u4e00\u8eab\u72ec\u6491\uff0c\u7edd\u4e0d\u8fde\u7d2f \u5927\u4eba\u3002\u201c \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u8bf4\u4ec0\u4e48\u8fde\u7d2f\u4e0d\u8fde\u7d2f\uff1f\u6211\u8bf7\u4f60\u6765\uff0c\u5c31\u662f\u4e0d\u6015\u4f60\u8fde\u7d2f\u6211\u4e86\uff0c\u4e07\u4e8b\u4e00\u5b9a\u8981\u5360\u4f4f\u4e00\u4e2a \u7406\u5b57\u3002\u201d \u3000\u3000\u201c\u662fS\u5c0f\u71d5\u5b50\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u77ed\u77ed\u4e00\u756a\u4ea4\u8c08\uff0c\u738b\u5f64\u4e5f\u7a81\u7136\u559c\u6b22\u8d77\u5c0f\u71d5\u5b50\u6765\uff0c\u8fd9\u4e2a\u5927\u5b69\u5b50\u6761\u7406\u5206\u660e\uff0c\u5374\u5e26\u70b9\u90aa\u6c14\uff0c\u679c\u7136 \u548c\u4e00\u822c\u6b66\u6797\u4eba\u7269\u4e0d\u540c\u3002 \u3000\u3000\u201c\u6211\u5e26\u4f60\u5165\u5bab\uff0c\u8865\u4e00\u4e2a\u4f8d\u536b\u7684\u7f3a\u3002\u201d\u738b\u5f64\u8bf4\uff1a\u201c\u5c31\u6d3e\u4f60\u76d1\u89c6\u4e09\u516c\u4e3b\u7684\u884c\u52a8\u3002\u201d \u3000\u3000\u71d5\u6625\u98ce\u70b9\u70b9\u5934\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u5c0f\u71d5\u5b50\uff0c\u8bb0\u4f4f\u4e00\u4ef6\u4e8b\uff0c\u6682\u65f6\u522b\u548c\u7236\u4eb2\u76f8\u8ba4\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d\u71d5\u6625\u98ce\u8bf4\uff1a\u201c\u4e0d\u8fc7\uff0c\u7236\u4eb2\u90a3\u8fb9\uff0c\u8fd8\u8981\u7edf\u9886\u5927\u4eba\u4ea4\u4ee3\u4e00\u58f0\uff0c\u4ed6\u8981\u53eb\u513f\u5b50\uff0c\u6211 \u603b\u4e0d\u6562\u4e0d\u7b54\u5e94\u554a\uff01\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\uff0c\u4ea4\u7ed9\u6211\u529e\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u5c0f\u548c\u5c1a\u3001\u738b\u91cd\u5c71\u8fd8\u5728\u57ce\u5916\u7b49\u6211\uff0c\u6211\u7acb\u523b\u5357\u4e0b\uff0c\u4e09\u4e2a\u6708\u540e\u518d\u56de\u6765\uff0c\u4e5f\u8bb8\u80fd\u628a \u4f60\u9053\u4eba\u56f4\u6740\u7684\u8bc1\u636e\u63ed\u5f00\u6765\u3002\u201d \u3000\u3000\u201c\u6211\u4e0d\u7559\u4f60\uff0c\u6c5f\u5144\uff0c\u6069\u5927\u4e0d\u8a00\u8c22\uff0c\u529e\u597d\u4e86\u8fd9\u6863\u4e8b\u60c5\uff0c\u6211\u5c31\u8f9e\u53bb\u7edf\u9886\u4e4b\u804c\uff0c\u966a\u6c5f\u5144\u5728\u6c5f\u6e56 \u4e0a\u8d70\u52a8\u51e0\u5e74\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u5bcc\u8d35\u4e2d\u4eba\u3002\u201d\u6c5f\u5343\u91cc\u8bf4\uff1a\u201c\u8fd9\u4ef6\u4e8b\uff0c\u53ea\u6015\u4e0d\u592a\u5bb9\u6613\uff0c\u4e0d\u8fc7\uff0c\u6211\u4eec\u5148\u8bb2\u597d\u4e00\u4e2a\u6761 \u4ef6\uff0c\u89e3\u51b3\u4e86\u4e09\u516c\u4e3b\u7684\u9ebb\u70e6\uff0c\u8981\u653e\u5c0f\u71d5\u5b50\u79bb\u5f00\u5bab\u5ef7\u3002\u201d \u3000\u3000\u201c\u4e0d\u653e\u6210\u5417\uff1f\u201d\u738b\u5f64\u8bf4\uff1a\u201c\u6c5f\u6e56\u4e0a\u8fd8\u6709\u5f88\u591a\u4e8b\uff0c\u8981\u5c0f\u71d5\u5b50\u53bb\u5904\u7406\uff0c\u5bf9\u4e48\uff1f\u201d \u3000\u3000\u201c\u597d\uff01\u5144\u5f1f\u544a\u8f9e\u3002\u201d\u6c5f\u5343\u91cc\u62b1\u62f3\u4e00\u63d6\uff0c\u8d77\u8eab\u5c31\u8d70\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u88ab\u5e26\u5165\u5bab\u4e2d\uff0c\u8865\u4e86\u4e00\u4e2a\u4f8d\u536b\u7684\u7f3a\u3002 \u3000\u3000\u71d5\u98de\u662f\u53c8\u559c\u6b22\u53c8\u96be\u8fc7\uff0c\u7236\u5b50\u76f8\u89c1\uff0c\u867d\u975e\u5f62\u540c\u964c\u8def\uff0c\u4f46\u4e5f\u53ea\u80fd\u70b9\u70b9\u5934\uff0c\u6253\u4e2a\u62db\u547c\u3002 \u3000\u3000\u4f46\u6bcf\u65e5\u603b\u53ef\u89c1\u5230\uff0c\u4e5f\u7b97\u7a0d\u6170\u601d\u5ff5\u4e86\u3002 \u3000\u3000\u71d5\u6625\u98ce\u5165\u5bab\u540e\u7b2c\u56db\u5929\uff0c\u6b63\u5728\u542c\u7985\u9662\u5916\u9762\u6e9c\u8fbe\uff0c\u5bab\u5973\u601c\u82b1\uff0c\u7a81\u7136\u51fa\u73b0\uff0c\u7f13\u6b65\u884c\u4e86\u8fc7\u6765\uff0c\u5230 \u4e86\u5c0f\u71d5\u5b50\u7684\u8ddf\u524d\u4e00\u7ad9\uff0c\u9053\uff1a\u201c\u5582\uff0c\u4f60\u8ba4\u8bc6\u6211\u4e48\uff1f\u201d \u3000\u3000\u71d5\u6625\u98ce\u6447\u6447\u5934\u3002 \u3000\u3000\u201c\u6211\u53eb\u601c\u82b1\uff0c\u662f\u4e09\u516c\u4e3b\u7684\u8d34\u8eab\u5bab\u5973\uff0c\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\u554a\uff1f\u201d \u3000\u3000\u201c\u65b0\u6765\u7684\u4f8d\u536b\u71d5\u6625\u98ce\u201d\u65b0\u6765\u7684\uff0c\u52ff\u602a\u4e0d\u8ba4\u8bc6\u6211\u4e86\u3002\u201c\u601c\u82b1\u8bf4\uff1a\u201d \u3000\u3000\u4f60\u5728\u8fd9\u91cc\u8f6c\u6765\u8f6c\u53bb\uff0c\u5e72\u4ec0\u4e48\u5440\uff1f\u201c \u3000\u3000\u201c\u4fdd\u62a4\u4e09\u516c\u4e3b\u554a\uff01\u201d \u3000\u3000\u201c\u54fc\uff01\u662f\u4fdd\u62a4\uff0c\u8fd8\u662f\u76d1\u89c6\uff1f\u54ea\u6709\u50cf\u4f60\u8fd9\u6837\u660e\u76ee\u5f20\u80c6\u7684\uff0c\u8fd9\u4e48\u4e2a\u8d70\u6cd5\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u662f\u60f3\u77a7\u77a7\u4e09\u516c\u4e3b\u554a\uff01\u201d \u3000\u3000\u201c\u71d5\u6625\u98ce\uff0c\u4f60\u597d\u5927\u7684\u80c6\u5b50\uff0c\u4e09\u516c\u4e3b\u8eab\u4efd\u4f55\u7b49\u9ad8\u8d35\uff0c\u5c82\u662f\u4f60\u80fd\u77a7\u5f97\u7684\uff1f\u201d \u3000\u3000\u201c\u5509\uff01\u6ca1\u6709\u529e\u6cd5\u554a\uff01\u6211\u4e0d\u8ba4\u8bc6\u4e09\u516c\u4e3b\uff0c\u4e00\u65e6\u53d1\u751f\u4ec0\u4e48\u4e8b\u6545\uff0c\u6211\u4f1a\u628a\u4f60\u5f53\u4f5c\u4e09\u516c\u4e3b\u6765\u4fdd \u62a4\u2026\u2026\u201d \u3000\u3000\u201c\u4f1a\u5417\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u96be\u8bf4\u554a\uff01\u4f60\u957f\u5f97\u90a3\u4e48\u6f02\u4eae\uff0c\u770b\u8d77\u6765\u5f88\u50cf\u516c\u4e3b\u554a\uff01\u201d \u3000\u3000\u601c\u82b1\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u662f\u4e0d\u662f\u5728\u8ba8\u597d\u6211\u2026\u2026\u201d \u3000\u3000\u201c\u5c31\u7b97\u662f\u5427\uff01\u4f46\u6211\u8fd8\u662f\u8981\u770b\u770b\u4e09\u516c\u4e3b\u624d\u884c\uff01\u201d \u3000\u3000\u601c\u82b1\u9053\uff1a\u201c\u53ef\u4ee5\uff0c\u4e0d\u8fc7\uff0c\u8981\u5148\u8fc7\u4e86\u6211\u8fd9\u4e00\u5173\u624d\u884c\u3002\u201d \u3000\u3000\u201c\u600e\u4e48\u8fc7\u5462\uff1f\u201d\u71d5\u6625\u98ce\u9053\uff1a\u201c\u6211\u60f3\u4e00\u5b9a\u4e0d\u662f\u4ef6\u5f88\u5bb9\u6613\u7684\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u201c\u4e00\u70b9\u4e5f\u4e0d\u96be\uff0c\u5148\u544a\u8bc9\u6211\uff0c\u4f60\u4eca\u5e74\u51e0\u5c81\u4e86\uff1f\u201d\u601c\u82b1\u8bf4\uff1a\u201c\u4ec0\u4e48\u95e8\u6d3e\u51fa\u8eab\uff0c\u4ec0\u4e48\u4eba\u63a8\u8350\u4f60 \u5165\u5bab\u7684\uff1f\u201d \u3000\u3000\u201c\u5341\u4e5d\u5c81\u2026\u2026\u201d\u71d5\u6625\u98ce\u591a\u8bb2\u4e86\u4e24\u5c81\uff0c\u4ed6\u4f53\u578b\u9ad8\u5927\uff0c\u591a\u8bb2\u4e24\u5c81\uff0c\u66f4\u4e3a\u9002\u5408\u3002 \u3000\u3000\u201c\u548c\u6211\u540c\u5e74\u3002\u201d\u601c\u82b1\u7b11\u9053\uff1a\u201c\u770b\u4f60\u4e00\u8138\u7a1a\u6c14\uff0c\u53ef\u662f\u4e00\u51fa\u5e08\u95e8\uff0c\u5c31\u5165\u7687\u5bab\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u6211\u662f\u795e\u5200\u5802\u51fa\u8eab\u7684\u5f1f\u5b50\u3002\u201d\u71d5\u6625\u98ce\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u5bb6\u7236\u4e0d\u5e0c\u671b\u6211\u5728\u6c5f\u6e56\u4e0a\u6df7\uff0c\u5c31\u6258 \u4eba\u4ecb\u7ecd\u6211\u5165\u5bab\uff0c\u662f\u6cb3\u5357\u5de1\u629a\u9a6c\u5927\u4eba\u4fdd\u6211\u7684\uff0c\u8fd8\u7ecf\u8fc7\u4e86\u4e00\u756a\u6d4b\u8bd5\uff0c\u624d\u5f97\u5230\u8fd9\u4e2a\u5dee\u4e8b\u3002\u201d \u3000\u3000\u201c\u9a6c\u5de1\u629a\u2026\u2026\u201d\u601c\u82b1\u7b11\u9053\uff1a\u201c\u4ed6\u662f\u5c01\u7586\u5927\u540f\uff0c\u53c8\u548c\u738b\u7edf\u9886\u662f\u597d\u670b\u53cb\uff0c\u7531\u4ed6\u63a8\u8350\uff0c\u81ea\u7136\u662f \u7acb\u53ef\u5f55\u7528\u3002\u201d \u3000\u3000\u201c\u4e0d\u662f\u554a\uff01\u201d\u71d5\u6625\u98ce\u8bf4\uff1a\u201c\u6211\u662f\u51ed\u672c\u9886\u8003\u4e0a\u7684\uff0c\u738b\u7edf\u9886\u94c1\u9762\u65e0\u79c1\uff0c\u4e00\u70b9\u4e5f\u4e0d\u9a6c\u864e\u2026\u2026\u201d \u7a81\u7136\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u5947\u602a\u5440\uff01\u601c\u82b1\u59d0\uff0c\u4f60\u6df1\u5c45\u7981\u5bab\uff0c\u600e\u4e48\u4f1a\u77e5\u9053\u9a6c\u5de1\u629a\uff1f\u201d \u3000\u3000\u201c\u6211\u662f\u6b63\u6708\u521d\u4e94\u751f\uff0c\u53eb\u6211\u59d0\u59d0\uff0c\u5927\u6982\u4e0d\u4f1a\u8ba9\u4f60\u5403\u4e8f\uff0c\u81f3\u4e8e\u6211\u77e5\u9053\u9a6c\u5de1\u629a\u7684\u4e8b\uff0c\u4f55\u8db3\u4e3a \u5947\uff0c\u544a\u8bc9\u4f60\uff0c\u5bab\u5ef7\u4e2d\u4e8b\u52a1\u7eb7\u6df7\uff0c\u6ca1\u6709\u4e00\u70b9\u5bdf\u989c\u89c2\u8272\u7684\u672c\u9886\uff0c\u5f88\u96be\u751f\u5b58\u4e0b\u53bb\u2026\u2026\u201d\u601c\u82b1\u7b11\u9053\uff1a \u201c\u59d0\u59d0\u6211\u77e5\u9053\u7684\u4e8b\u60c5\u591a\u4e86\uff0c\u4ee5\u540e\uff0c\u6211\u4f1a\u6559\u4f60\uff0c\u4f60\u5f97\u542c\u6211\u7684\u8bdd\u624d\u884c\u3002\u201d \u3000\u3000\u201c\u542c\u5c31\u542c\u561b\uff01\u4e0d\u8fc7\uff0c\u4f60\u53ef\u4e0d\u80fd\u5bb3\u6211\uff1f\u201d\u71d5\u6625\u98ce\u8bf4\uff1a\u201c\u542c\u8bf4\u738b\u7edf\u9886\u5c5e\u4e0b\u5f88\u4e25\uff0c\u7a0d\u6709\u9519\u5931\uff0c \u5c31\u4e0b\u4ee4\u4e25\u529e\uff1f\u201d \u3000\u3000\u4ed6\u82f1\u4fca\u4e2d\u5e26\u7740\u4e00\u8138\u7a1a\u6c14\uff0c\u795e\u60c5\u98d8\u9038\u3001\u8f7b\u677e\uff0c\u6709\u80a1\u4ee4\u4eba\u5fc3\u52a8\u7684\u90aa\u6c14\uff0c\u601c\u82b1\u53ea\u89c9\u5fc3\u5934\u8df3\u52a8\uff0c \u53cc\u76ee\u4e2d\u9690\u73b0\u51fa\u65e0\u9650\u67d4\u60c5\u3002 \u3000\u3000\u201c\u6240\u4ee5\uff0c\u4f60\u8981\u627e\u4e2a\u9760\u5c71\u2026\u2026\u201d\u601c\u82b1\u9053\uff1a\u201c\u624d\u80fd\u5728\u7687\u5bab\u4e2d\u51fa\u4eba\u5934\u5730\u3002\u201d \u3000\u3000\u201c\u6211\u521a\u521a\u5165\u5bab\uff0c\u4eba\u5730\u751f\u758f\uff0c\u54ea\u91cc\u6709\u9760\u5c71\u597d\u627e\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u53ef\u4ee5\u5e2e\u5fd9\u4f60\u5440\uff01\u201d\u601c\u82b1\u9053\uff1a\u201c\u4ee5\u540e\u4e0d\u8981\u5fd8\u4e86\u601c\u82b1\u59d0\u59d0\u5c31\u662f\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u8fd9\u4e2a\u4eba\u6700\u77e5\u611f\u6069\u56fe\u62a5\u4e86\u2026\u2026\u201d \u3000\u3000\u601c\u82b1\u4f4e\u58f0\u9053\uff1a\u201c\u4eca\u665a\u4e0d\u503c\u591c\uff0c\u5c31\u5230\u8fd9\u91cc\u627e\u6211\uff0c\u5c0f\u5fc3\u4e00\u70b9\uff0c\u4e0d\u8981\u88ab\u522b\u4eba\u53d1\u89c9\u3002\u201d \u3000\u3000\u71d5\u6625\u98ce\u70b9\u70b9\u5934\u3002 \u3000\u3000\u601c\u82b1\u5feb\u6b65\u800c\u53bb\u3002 \u3000\u3000\u671b\u7740\u601c\u82b1\u8fdc\u53bb\u7684\u80cc\u5f71\uff0c\u71d5\u6625\u98ce\u5fc3\u4e2d\u6697\u9053\uff1a\u96be\u9053\u8fd9\u4e48\u7b80\u5355\u5c31\u642d\u4e0a\u4e86\uff1f \u3000\u3000\u5e0c\u671b\u738b\u7edf\u9886\u63a8\u8350\u6211\u5165\u5bab\u7684\u8ba1\u5212\u5f88\u5468\u5bc6\uff0c\u4e0d\u8981\u9732\u51fa\u7834\u7efd\uff0c\u5979\u90fd\u65e2\u7136\u95ee\u4e86\uff0c\u53ef\u80fd\u4f1a\u5411\u9a6c\u5de1\u629a \u8c03\u67e5\u8fd9\u4ef6\u4e8b\uff1f \u3000\u3000\u71d5\u98de\u867d\u7136\u6781\u529b\u907f\u514d\u548c\u71d5\u6625\u98ce\u89c1\u9762\uff0c\u4f46\u5374\u65e0\u6cd5\u6309\u4e0b\u90a3\u80a1\u601d\u5ff5\u4e4b\u60c5\uff0c\u5fcd\u4e0d\u4f4f\u4fbf\u5411\u542c\u7985\u9662\u884c\u4e86 \u8fc7\u6765\u3002 \u3000\u3000\u4ed6\u73b0\u5728\u662f\u5185\u5bab\u5de1\u67e5\u8eab\u4efd\uff0c\u4e0d\u7528\u503c\u73ed\u5b88\u536b\uff0c\u53ea\u8d1f\u8d23\u67e5\u770b\u5404\u5730\u7684\u503c\u73ed\u60c5\u5f62\u3002 \u3000\u3000\u8fdc\u8fdc\u77a7\u89c1\u4e86\u7236\u4eb2\u884c\u6765\uff0c\u5c0f\u71d5\u5b50\u5927\u5927\u7684\u5403\u4e86\u4e00\u60ca\uff0c\u4ed6\u521d\u5165\u7687\u5bab\uff0c\u5982\u82e5\u71d5\u98de\u8868\u73b0\u51fa\u7236\u5b50\u60c5 \u4efd\uff0c\u5fc5\u5c06\u4f1a\u4f7f\u4e09\u516c\u4e3b\u5fc3\u4e2d\u6000\u7591\u3002 \u3000\u3000\u4ed6\u76f8\u4fe1\uff0c\u5fc5\u5b9a\u6709\u4eba\u5728\u6697\u4e2d\u76d1\u89c6\u7740\u4ed6\u3002\u5fc3\u4e2d\u4e00\u6025\uff0c\u6697\u8fd0\u529f\u529b\uff0c\u65bd\u5c55\u4f20\u97f3\u5165\u5bc6\u4e4b\u672f\uff0c\u9053\uff1a \u201c\u7239\uff0c\u4ed6\u4eec\u5df2\u7ecf\u627e\u4e0a\u6211\u4e86\uff0c\u6211\u5f53\u503c\u4e0d\u7ecf\u5fc3\uff0c\u4f60\u53ef\u4ee5\u4e25\u5389\u7684\u53f1\u8d23\u6211\u4e00\u987f\uff0c\u6700\u597d\u80fd\u6253\u6211\u4e24\u62f3\u3002\u201d \u3000\u3000\u58f0\u97f3\u94bb\u5165\u4e86\u71d5\u98de\u7684\u8033\u6735\uff0c\u786e\u8ba9\u71d5\u98de\u5927\u5927\u7684\u5403\u4e86\u4e00\u60ca\uff0c\u56e0\u4e3a\uff0c\u53cc\u65b9\u8fd8\u6709\u4e24\u4e09\u4e08\u4ee5\u4e0a\u7684\u8ddd \u79bb\uff0c\u5355\u662f\u8fd9\u4f20\u97f3\u4e4b\u672f\uff0c\u71d5\u98de\u5fc3\u4e2d\u660e\u767d\uff0c\u8fd9\u4e00\u8f88\u5b50\u662f\u65e0\u6cd5\u7ec3\u5230\u8fd9\u79cd\u5883\u754c\uff0c\u4ee5\u738b\u5f64\u548c\u6c5f\u5e72\u91cc\u4e4b \u80fd\uff0c\u53ea\u6015\u4e5f\u6ca1\u6709\u8fd9\u7b49\u6210\u5c31\u3002 \u3000\u3000\u5c3d\u7ba1\u71d5\u98de\u4e3a\u513f\u5b50\u5982\u6b64\u7684\u6210\u5c31\u611f\u5230\u559c\u60a6\uff0c\u4f46\u4ed6\u662f\u4e2a\u8001\u516c\u4e8b\u4e86\uff0c\u81ea\u7136\u660e\u767d\u5c0f\u71d5\u5b50\u7684\u82e6\u5fc3\u6240 \u5728\uff0c\u7acb\u523b\u62c9\u4e0b\u8138\uff0c\u4e00\u526f\u516d\u4eb2\u4e0d\u8ba4\u7684\u795e\u8272\u3002 \u3000\u3000\u71d5\u6625\u98ce\u5012\u4e5f\u914d\u5408\u5f97\u5f88\u597d\uff0c\u4f3c\u662f\u6839\u672c\u4e0d\u77e5\u5de1\u67e5\u5230\u6765\uff0c\u4e00\u526f\u540a\u513f\u90ce\u5f53\u7684\u6837\u5b50\uff0c\u671b\u7740\u5929\u4e0a\u4e00\u7247 \u6d6e\u52a8\u7684\u767d\u4e91\u51fa\u795e\u3002 \u3000\u3000\u71d5\u98de\u4e00\u76b1\u7709\uff0c\u653e\u8f7b\u4e86\u811a\u6b65\uff0c\u884c\u5230\u4e86\u5c0f\u71d5\u5b50\u7684\u8eab\u540e\uff0c\u7a81\u7136\u8bf4\u9053\uff1a\u201c\u4f60\u5728\u770b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u71d5\u6625\u98ce\u5403\u4e86\u4e00\u60ca\uff0c\u56de\u5934\u770b\u5230\u71d5\u98de\uff0c\u7acb\u65f6\u8083\u7136\u7ad9\u597d\uff0c\u8eac\u8eab\u884c\u4e86\u4e00\u793c\uff0c\u9053\uff1a\u201c\u6211\u5728 \u770b\u2026\u2026\uff0c\uff0c\u201d\u770b\u5929\u4e0a\u90a3\u4e00\u7247\u53d8\u5e7b\u7684\u4e91\u5f69\uff0c\u662f\u4e48\uff1f\u201c\u71d5\u98de\u795e\u60c5\u51b7\u5389\u5730\u9053\uff1a\u201d\u8fd9\u662f\u4e09\u516c\u4e3b\u4f4f\u7684\u6240 \u5728\uff0c\u8981\u4f60\u5b88\u536b\u8b66\u6212\uff0c\u4f60\u7adf\u7136\u6f20\u4e0d\u5173\u5fc3\u2026\u2026\u201c\u7a81\u7136\u626c\u624b\u4e00\u4e2a\u8033\u5149\u63b4\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u71d5\u6625\u98ce\u6ca1\u6709\u95ea\u907f\uff0c\u8fd9\u4e00\u638c\u5012\u662f\u6253\u5f97\u7ed3\u7ed3\u5b9e\u5b9e\uff0c\u5c0f\u71d5\u5b50\u4e5f\u6ca1\u6709\u8fd0\u529f\u6297\u62d2\uff0c\u8138\u4e0a\u7acb\u523b\u6cdb\u8d77\u4e86 \u4e94\u6761\u7ea2\u8272\u7684\u6307\u75d5\u3002 \u3000\u3000\u5c3d\u7ba1\u71d5\u98de\u75db\u5728\u5fc3\u4e2d\uff0c\u53e3\u4e2d\u4ecd\u7136\u662f\u51b7\u51b7\u53f1\u9053\uff1a\u201c\u59d1\u5ff5\u4f60\u65b0\u6765\u521d\u72af\uff0c\u7a0d\u793a\u8584\u60e9\uff0c\u5982\u82e5\u518d\u88ab\u6211 \u53d1\u73b0\u758f\u5ffd\u804c\u5b88\uff0c\u5b9a\u5f53\u91cd\u8d23\u4e0d\u8d37\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u8fde\u58f0\u5e94\u662f\uff0c\u71d5\u98de\u624d\u5e26\u7740\u6012\u5bb9\u79bb\u53bb\u3002 \u3000\u3000\u679c\u7136\uff0c\u8fd9\u4e00\u8033\u5149\u6253\u5f97\u5c0f\u71d5\u5b50\u4e0d\u6562\u518d\u7a0d\u6709\u758f\u5ffd\uff0c\u5168\u795e\u8d2f\u6ce8\uff0c\u8b66\u6212\u56db\u5468\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u71d5\u6625\u98ce\u4e5f\u8fd0\u96c6\u4e86\u5168\u8eab\u529f\u529b\uff0c\u5c55\u5f00\u5929\u89c6\u5730\u542c\u4e4b\u672f\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u4e94\u4e08\u5185\u98d8\u4e0b\u4e00\u7247\u843d\u53f6\uff0c\u4e5f\u7792\u4e0d\u8fc7\u4ed6\u8033\u76ee\u3002 \u3000\u3000\u4ed6\u542c\u5230\u4e86\u4e00\u79cd\u6781\u5fae\u7684\u54cd\u58f0\uff0c\u90a3\u662f\u4e0a\u4e58\u8f7b\u529f\uff0c\u8e0f\u843d\u5728\u679d\u53f6\u4e0a\u7684\u5fae\u5f31\u58f0\u97f3\u3002 \u3000\u3000\u4f46\u5c0f\u71d5\u5b50\u5374\u88c5\u4f5c\u4e0d\u77e5\uff0c\u4ed6\u5fc3\u4e2d\u65e9\u5df2\u6709\u4e86\u8ba1\u5212\uff0c\u4e0d\u80fd\u8ba9\u4eba\u53d1\u89c9\u4ed6\u8eab\u6000\u4e0a\u4e58\u6b66\u529f\u2026\u2026 \u3000\u3000\u4ed6\u8981\u626e\u732a\u5403\u8001\u864e\uff01 \u3000\u3000\u5c0f\u71d5\u5b50\u4e5f\u611f\u89c9\u5230\u6709\u4e00\u5bf9\u51cc\u5389\u7684\u773c\u795e\uff0c\u5728\u6697\u4e2d\u7aa5\u89c6\u7740\u4ed6\uff0c\u7adf\u4e5f\u80fd\u5fcd\u4f4f\u6ca1\u6709\u8f6c\u5934\uff0c\u88c5\u4f5c\u4e0d \u77e5\u3002 \u3000\u3000\u8fd9\u662f\u6781\u5927\u7684\u5fcd\u8010\u529f\u592b\uff0c\u4e0d\u662f\u4ed6\u8fd9\u79cd\u5e74\u9f84\u7684\u4eba\u6240\u80fd\u505a\u5230\u7684\u3002 \u3000\u3000\u4f46\u5c0f\u71d5\u5b50\u529e\u5230\u4e86\uff0c\u4e0d\u77e5\u9053\u6c5f\u5343\u91cc\u7ed9\u4ed6\u627e\u4e86\u4e00\u4e2a\u4ec0\u4e48\u6837\u7684\u5e08\u7236\uff0c\u5728\u77ed\u77ed\u7684\u4e24\u5e74\u591a\uff0c\u4e0d\u4f46\u9020 \u5c31\u4e86\u4ed6\u4e00\u8eab\u6b66\u529f\uff0c\u4e5f\u628a\u4ed6\u8bad\u7ec3\u6210\u4e00\u4e2a\u7cbe\u5e72\u7684\u6c5f\u6e56\u4eba\u3002 \u3000\u3000\u8db3\u8db3\u8fc7\u4e86\u4e00\u76cf\u70ed\u8336\u7684\u5de5\u592b\uff0c\u5c0f\u71d5\u5b50\u624d\u611f\u89c9\u5230\u90a3\u4e00\u5bf9\u76ee\u5149\u6d88\u5931\uff0c\u4eba\u4e5f\u79bb\u53bb\u3002 \u3000\u3000\u4ed6\u867d\u7136\u6781\u529b\u5728\u5fcd\u8010\u7740\uff0c\u4f46\u4e00\u76f4\u88ab\u4eba\u6697\u770b\u7740\uff0c\u5fc3\u4e2d\u5341\u5206\u4e0d\u5b89\u3002 \u3000\u3000\u957f\u957f\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u5c0f\u71d5\u5b50\u4f38\u52a8\u4e00\u4e0b\u53cc\u81c2\uff0c\u770b\u770b\u5929\u8272\uff0c\u4e5f\u8be5\u662f\u6362\u73ed\u7684\u8fb0\u5149\u4e86\u3002 \u3000\u3000\u5185\u5bab\u4f8d\u536b\u7684\u73ed\u65f6\u5f88\u51c6\uff0c\u5c0f\u71d5\u5b50\u60f3\u5230\u6362\u73ed\uff0c\u4e00\u4e2a\u4e8c\u5341\u56db\u4e94\u5c81\u7684\u5e74\u8f7b\u4eba\uff0c\u5df2\u5feb\u6b65\u884c\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u503c\u73ed\u4f8d\u536b\u90fd\u7a7f\u7740\u540c\u6837\u7684\u8863\u670d\uff0c\u8863\u9886\u4e0a\u4f69\u7740\u91d1\u82b1\uff0c\u5206\u522b\u54c1\u7ea7\u3002 \u3000\u3000\u770b\u6765\u4eba\u4f69\u4e86\u4e09\u6735\u91d1\u82b1\uff0c\u90a3\u662f\u4e09\u7b49\u4f8d\u536b\u7684\u8eab\u4efd\u4e86\uff0c\u5c0f\u71d5\u5b50\u662f\u4e00\u6735\u82b1\u7684\u4e94\u7b49\u54c1\u7ea7\uff0c\u7acb\u523b\u4e00\u62b1 \u62f3\uff0c\u9053\uff1a\u201c\u6211\u662f\u65b0\u6765\u7684\u71d5\u6625\u98ce\u3002\u201d \u3000\u3000\u6765\u4eba\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u91d1\u5929\u7965\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u539f\u6765\u662f\u91d1\u5144\uff0c\u4ee5\u540e\u8fd8\u671b\u591a\u591a\u6307\u6559\u3002\u201d \u3000\u3000\u91d1\u5929\u7965\u9762\u65e0\u8868\u60c5\u7684\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u53bb\u5427\uff0c\u4f60\u53ef\u4ee5\u56de\u53bb\u4f11\u606f\u4e86\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u738b\u7edf\u9886\u8bf4\u4ed6\u5df2\u88ab\u4e09\u516c\u4e3b\u4e70\u8fc7\u53bb\uff0c\u679c\u7136\u662f\u6c14\u52bf\u5f88\u5927\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u4eba\u5374\u8f6c\u8eab\u79bb\u53bb\u3002 \u3000\u3000\u201c\u56de\u6765\u2026\u2026\u201d\u91d1\u5929\u7965\u9053\uff1a\u201c\u4f60\u8138\u4e0a\u6709\u4f24\u75d5\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u201d\u71d5\u6625\u98ce\u56de\u8fc7\u8eab\u4e88\uff0c\u5e94\u9053\uff1a\u201c\u88ab\u5de1\u67e5\u6253\u7684\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u758f\u5ffd\u804c\u5b88\uff01\u201d \u3000\u3000\u201c\u5662\uff01\u201d\u91d1\u5929\u7965\u7b11\u9053\uff1a\u201c\u4f60\u662f\u7b2c\u4e00\u6b21\u503c\u73ed\u5427\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u201d \u3000\u3000\u201c\u7b2c\u4e00\u6b21\u503c\u73ed\u5c31\u88ab\u6293\u4f4f\u628a\u67c4\uff0c\u6253\u7684\u9f3b\u9752\u8138\u80bf\uff0c\u5012\u662f\u5c11\u89c1\u7684\u4e8b\u554a\uff01\u201d \u3000\u3000\u91d1\u5929\u7965\u7b11\u9053\uff1a\u201c\u5185\u5bab\u4f8d\u536b\u8fd9\u4e2a\u5dee\u4e8b\u4e0d\u662f\u90a3\u4e48\u597d\u5e72\u7684\u5427\uff01\u201d \u3000\u3000\u659c\u7768\u7740\u5c0f\u71d5\u5b50\u95ee\u8bdd\uff0c\u8bed\u6c14\u4e2d\u5e26\u7740\u8bbd\u523a\u3002 \u3000\u3000\u71d5\u6625\u98ce\u53ea\u89c9\u4e00\u9635\u6012\u706b\uff0c\u7531\u5fc3\u4e2d\u5192\u8d77\uff0c\u6b63\u8981\u53cd\u5507\u76f8\u8ba5\uff0c\u5ffd\u7136\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4ed8\u9053\uff1a\u6211\u8fd9\u6b21\u8fdb\u5bab \u4efb\u91cd\u9053\u8fdc\uff0c\u82e5\u548c\u4ed6\u51b2\u7a81\u8d77\u6765\uff0c\u95f9\u51fa\u4e8b\u60c5\uff0c\u5c82\u4e0d\u662f\u574f\u4e86\u5927\u4e8b\uff0c\u5c0f\u4e0d\u5fcd\u5219\u4e71\u5927\u8c0b\uff0c\u8fd9\u4ef6\u4e8b\u8bb0\u5728\u5e10 \u672c\u4e0a\uff0c\u5148\u8ba9\u4f60\u6446\u6446\u5a01\u98ce\u5427\uff01 \u3000\u3000\u541e\u4e0b\u4e00\u8154\u6012\u706b\uff0c\u5c0f\u71d5\u5b50\u88c5\u51fa\u4e86\u6ee1\u8138\u59d4\u5c48\uff0c\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u6211\u53ea\u662f\u62ac\u8d77\u5934\u6b23\u8d4f\u4e00\u4e0b\u5929\u4e0a\u6d41\u52a8 \u7684\u4e91\u5f69\uff0c\u521a\u597d\u5c31\u88ab\u770b\u5230\u4e86\uff0c\u5509\uff01\u91d1\u5144\u8bf4\u5f97\u4e0d\u9519\uff0c\u5185\u5bab\u4f8d\u536b\u8fd9\u53e3\u996d\u4e0d\u597d\u5403\u554a\uff01\u5728\u4e0b\u5bb6\u4e2d\u8584\u6709\u8d44 \u4ea7\uff0c\u6211\u770b\u8fd8\u662f\u56de\u53bb\u5403\u81ea\u5df1\u7684\u7b97\u4e86\u3002\u201d \u3000\u3000\u201c\u600e\u4e48\uff1f\u8fd9\u4e00\u70b9\u5c0f\u5c0f\u7684\u632b\u6298\uff0c\u4f60\u5c31\u7070\u5fc3\u4e86\u3002\u201d\u91d1\u5929\u7965\u9053\uff1a\u201c\u90a3\u8fd8\u80fd\u6210\u4ec0\u4e48\u5927\u4e8b\uff1f\u201d \u3000\u3000\u201c\u53ea\u4e0d\u8fc7\u662f\u62ac\u5934\u770b\u770b\u5929\u4e0a\u7684\u4e00\u7247\u4e91\uff0c\u5c31\u88ab\u6253\u4e86\u4e00\u4e2a\u8033\u5149\uff0c\u8981\u662f\u72af\u4e86\u7a0d\u4e3a\u5927\u4e00\u70b9\u7684\u9519\u5931\uff0c \u90a3\u8fd8\u5f97\u4e86\uff01\u201d\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u5c82\u4e0d\u662f\u8981\u62c9\u51fa\u53bb\u780d\u4e86\u8111\u888b\u3002\u201d \u3000\u3000\u201c\u780d\u8111\u888b\u5012\u662f\u4e0d\u4f1a\u2026\u2026\u201d\u91d1\u5929\u7965\u9053\uff1a\u201c\u8fd9\u91cc\u6700\u91cd\u7684\u5904\u5206\u662f\u5272\u804c\u9010\u9000\uff0c\u6c38\u4e0d\u5f55\u7528\uff0c\u5f53\u7136\uff0c \u4f60\u5982\u662f\u72af\u4e86\u738b\u6cd5\uff0c\u90a3\u5c31\u4e0d\u540c\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u5509\uff01\u771f\u662f\u4e0d\u7ecf\u4e00\u4e8b\uff0c\u4e0d\u957f\u4e00\u667a\uff0c\u5728\u4e0b\u5e74\u8f7b\u8bc6\u6d45\uff0c\u4ee5\u540e\uff0c\u8fd8\u8981\u91d1\u5144\u591a\u591a\u6307\u6559\u3002\u201d \u3000\u3000\u201c\u4f60\u80fd\u4ea4\u4e0a\u6211\u91d1\u67d0\u4eba\u8fd9\u4e2a\u670b\u53cb\uff0c\u90a3\u4f60\u5c31\u627e\u5bf9\u4eba\u4e86\u3002\u201d \u3000\u3000\u201c\u54e6\uff01\u91d1\u5144\u80af\u4ea4\u6211\u8fd9\u4e2a\u670b\u53cb\u5417\uff1f\u201d \u3000\u3000\u201c\u672c\u6765\uff0c\u6211\u4e0d\u592a\u559c\u6b22\u548c\u4eba\u5f80\uff0c\u4e0d\u8fc7\uff0c\u4f60\u8fd9\u4e2a\u4eba\u5f88\u8c26\u865a\uff0c\u6211\u5c31\u4ea4\u4f60\u8fd9\u4e2a\u670b\u53cb\u5427\uff01\u201d\u91d1\u5929\u7965 \u8bf4\uff1a\u201c\u660e\u5929\uff0c\u4f60\u51e0\u65f6\u5f53\u503c\uff1f\u201d \u3000\u3000\u71d5\u6625\u98ce\u9053\uff1a\u201c\u5927\u6982\u662f\u5348\u65f6\u8fc7\u540e\u5427\uff01\u201d \u3000\u3000\u201c\u597d\uff01\u660e\u5929\u665a\u4e0a\u6211\u4eec\u4e00\u8d77\u51fa\u53bb\u5403\u665a\u996d\uff0c\u6211\u5e26\u4f60\u901b\u901b\u5317\u4eac\u57ce\u3002\u201d \u3000\u3000\u201c\u597d\u6781\u4e86\uff0c\u5c0f\u5f1f\u4eba\u5730\u751f\u758f\uff0c\u4ea4\u4e0a\u4f60\u91d1\u5144\u8fd9\u4e2a\u670b\u53cb\uff0c\u771f\u662f\u5c0f\u5f1f\u4e4b\u5e78\u4e86\u3002\u201d\u71d5\u6625\u98ce\u9053\uff1a\u201c\u6211 \u8fd9\u91cc\u5148\u884c\u8c22\u8c22\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u7528\u5ba2\u6c14\uff0c\u4f60\u53bb\u4f11\u606f\u5427\uff01\u201d\u91d1\u5929\u7965\u5e74\u7eaa\u4e0d\u5927\uff0c\u4f46\u5374\u4e00\u526f\u501a\u8001\u5356\u8001\u7684\u6837\u5b50\uff0c\u5b8c\u5168\u628a\u71d5\u6625 \u98ce\u770b\u4f5c\u4e86\u665a\u751f\u540e\u8fdb\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u4e5f\u4f1a\u5356\u4e56\uff0c\u7adf\u7136\u4e00\u62b1\u62f3\uff0c\u624d\u8f6c\u8eab\u884c\u53bb\u3002 \u3000\u3000\u8d70\u4e86\u4e24\u4e09\u6b65\uff0c\u505c\u4e0b\u6765\u56de\u8fc7\u5934\uff0c\u9053\uff1a\u201c\u91d1\u5144\uff0c\u6211\u8981\u5230\u90a3\u91cc\u627e\u4f60\uff1f\u201d \u3000\u3000\u201c\u5230\u65f6\u5019\uff0c\u6211\u4f1a\u53bb\u627e\u4f60\u2026\u2026\u201d\u4ed6\u8bdd\u4f3c\u662f\u672a\u8bf4\u5b8c\uff0c\u4f46\u5374\u7a81\u7136\u4f4f\u53e3\u4e0d\u8a00\u3002 \u3000\u3000\u201c\u5c0f\u71d5\u5b50\u5fc3\u4e2d\u660e\u767d\uff0c\u6b64\u5730\u4e0d\u662f\u8bb2\u8bdd\u6240\u5728\uff0c\u8f6c\u8eab\u4fbf\u79bb\u53bb\u3002\u4e00\u8def\u4e0a\u7559\u5fc3\u5404\u5904\u5f62\u52bf\uff0c\u7262\u7262\u7684\u8bb0 \u5728\u5fc3\u91cc\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u56de\u5230\u4e86\u4e0b\u69bb\u4e4b\u5904\uff0c\u738b\u5f64\u7adf\u7136\u65e9\u5df2\u5728\u90a3\u91cc\u7b49\u5019\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u627e\u65f6\u95f4\u5230\u6211\u7684\u4e66\u623f \u53bb\u3002\u201d\u7f13\u6b65\u884c\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u71d5\u6625\u98ce\u76ee\u5149\u8f6c\u52a8\uff0c\u6beb\u65e0\u4eba\u8e2a\uff0c\u5fc3\u4e2d\u5374\u611f\u5947\u602a\uff0c\u5fd6\u9053\uff1a\u738b\u7edf\u9886\u4e5f\u662f\u8001\u6c5f\u6e56\u4e86\uff0c\u600e\u4e48\u8fd8\u8fd9\u822c \u5927\u610f\uff0c\u8fd9\u4e9b\u4f8d\u536b\u4e4b\u4e2d\uff0c\u4e0d\u77e5\u6709\u591a\u5c11\u88ab\u4eba\u6536\u4e70\u53bb\u4e86\uff0c\u82e5\u88ab\u4ed6\u4eec\u77a7\u89c1\uff0c\u5c82\u4e0d\u662f\u5927\u6709\u59a8\u788d\u3002 \u3000\u3000\u5c3d\u7ba1\u5fc3\u4e2d\u4e0d\u6ee1\uff0c\u4ecd\u7136\u662f\u66f4\u6362\u4e86\u8863\u670d\uff0c\u7f13\u6b65\u884c\u5411\u738b\u5f64\u7684\u4e66\u623f\u3002 \u3000\u3000\u8d75\u4fdd\u3001\u9648\u5b8f\u5b88\u5728\u4e66\u623f\u95e8\u5916\uff0c\u4f46\u5374\u6ca1\u6709\u963b\u6b62\u71d5\u6625\u98ce\u3002 \u3000\u3000\u663e\u7136\uff0c\u4ed6\u4eec\u65e9\u5df2\u5949\u5230\u4e86\u4ee4\u8c15\u3002 \u3000\u3000\u738b\u5f64\u65e9\u5df2\u5728\u4e66\u623f\u5750\u5019\uff0c\u4eb2\u624b\u5949\u4e0a\u4e86\u4e00\u76c5\u9999\u8317\uff0c\u9053\uff1a\u201c\u5c0f\u71d5\u5b50\uff0c\u8fd9\u91cc\u53ef\u4ee5\u653e\u5fc3\u7684\u8bf4\u8bdd\uff0c\u4ee4 \u5c0a\u544a\u8bc9\u6211\u5df2\u7ecf\u6709\u4eba\u627e\u4e0a\u4f60\u4e86\uff0c\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u4e00\u4e2a\u5bab\u5973\uff0c\u81ea\u79f0\u601c\u82b1\u2026\u2026\u201d\u71d5\u6625\u98ce\u9053\uff1a\u201c\u5979\u8981\u6211\u627e\u4e2a\u9760\u5c71\uff0c\u624d\u80fd\u5728\u5bab\u4e2d\u51fa\u4eba\u5934 \u5730\u2026\u2026\u201d \u3000\u3000\u738b\u5f64\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u601c\u82b1\u2026\u2026\u201d \u3000\u3000\u201c\u662f\u554a\uff01\u5979\u8fd9\u4e48\u5bf9\u6211\u8bf4\u7684\u3002\u201d \u3000\u3000\u201c\u5509\uff01\u8fd9\u771f\u662f\u53ef\u6015\u6781\u4e86\uff01\u201d\u738b\u5f64\u9053\uff1a\u201c\u601c\u82b1\u4eb2\u53e3\u7b54\u5e94\u6211\u6697\u4e2d\u76d1\u89c6\u4e09\u516c\u4e3b\u7684\u884c\u52a8\uff0c\u4f46\u770b\u8d77 \u6765\uff0c\u5979\u597d\u50cf\u662f\u6761\u4e24\u5934\u86c7\uff01\u201d \u3000\u3000\u201c\u601c\u82b1\u53ef\u662f\u7edf\u9886\u6d3e\u5728\u4e09\u516c\u4e3b\u8eab\u8fb9\u7684\u4eba\uff1f\u201d \u3000\u3000\u201c\u4e0d\u80fd\u7b97\u662f\u6211\u6d3e\u7684\uff0c\u5979\u4e00\u76f4\u662f\u4e09\u516c\u4e3b\u8eab\u4fa7\u7684\u5bab\u5973\u3002\u201d\u738b\u5f64\u8bf4\uff1a\u201c\u4e09\u516c\u4e3b\u8eab\u4fa7\u539f\u6709\u4e24\u4e2a\u8d34 \u8eab\u7684\u5973\u5a62\uff0c\u53e6\u4e00\u4e2a\u53eb\u60dc\u6625\uff0c\u7adf\u7136\u662f\u9b54\u6559\u4e2d\u4eba\uff0c\u6df7\u5165\u7687\u5bab\uff0c\u8eab\u4efd\u88ab\u63ed\u7a7f\u4e4b\u540e\uff0c\u5c31\u672a\u968f\u516c\u4e3b\u56de \u6765\u3002\u601c\u82b1\u662f\u4e2a\u5bab\u5973\uff0c\u6211\u5df2\u7ecf\u7b54\u5e94\u5979\uff0c\u4e24\u5e74\u540e\uff0c\u60f3\u529e\u6cd5\u9063\u5979\u51fa\u5bab\uff0c\u5ac1\u4eba\u751f\u5b50\uff0c\u4e5f\u5141\u4e86\u5979\u4e94\u5343\u4e24 \u94f6\u5b50\u7684\u916c\u8c22\uff0c\u96be\u9053\uff0c\u6211\u53c8\u88ab\u8fd9\u4e2a\u5c0f\u4e2b\u5934\u800d\u4e86\u4e0d\u6210\uff1f\u201d \u3000\u3000\u201c\u6211\u770b\u5c31\u662f\u4e86\u3002\u201d\u5c0f\u71d5\u5b50\u8bf4\uff1a\u201c\u5979\u65e5\u5915\u8ffd\u968f\u4e09\u516c\u4e3b\u7684\u8eab\u4fa7\uff0c\u5982\u4f55\u4f1a\u4e3a\u4f60\u6536\u7528\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u8fd9\u7687\u5bab\u4e4b\u5185\uff0c\u6211\u5df2\u7ecf\u6ca1\u6709\u53ef\u4fe1\u4efb\u7684\u4eba\uff0c\u53ef\u7528\u4e4b\u5175\u4e86\uff1f\u201d \u3000\u3000\u201c\u90a3\u5012\u4e0d\u662f\uff0c\u4f60\u53ef\u4ee5\u76f8\u4fe1\u6211\u7684\u7236\u4eb2\uff0c\u4ed6\u8d64\u5fc3\u5fe0\u80c6\uff0c\u7edd\u4e0d\u4f1a\u80cc\u53db\u7edf\u9886\uff0c\u4e5f\u53ef\u4ee5\u76f8\u4fe1\u6211\u5c0f\u71d5 \u5b50\uff0c\u6211\u4e0d\u4f1a\u8ba9\u4f60\u5931\u671b\uff0c\u4f55\u51b5\uff0c\u6211\u6b20\u4e86\u7edf\u9886\u4e00\u4efd\u60c5\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\uff0c\u4e5f\u53ea\u6709\u9760\u4f60\u4e86\uff0c\u4f60\u8fd9\u4e00\u62db\u68cb\u4e0d\u80fd\u53cd\u8d25\u4e3a\u80dc\uff0c\u8fd9\u4e00\u5c40\u5c31\u8f93\u5b9a\u4e86\u3002\u201d\u738b\u5f64\u82e6\u7b11\u4e00 \u4e0b\uff0c\u9053\uff1a\u201c\u6211\u771f\u7684\u60f3\u4e0d\u660e\u767d\uff0c\u4e09\u516c\u4e3b\u600e\u4e48\u5fc3\u6000\u4e8c\u5fd7\uff0c\u5f53\u4eca\u7687\u4e0a\u662f\u5979\u7684\u7236\u4eb2\u554a\uff0c\u5979\u7adf\u7136\u7518\u4e3a\u9b54 \u6559\u6548\u547d\uff0c\u548c\u7236\u7687\u4f5c\u5bf9\u3002\u201d \u3000\u3000\u71d5\u6625\u98ce\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u518d\u544a\u8bc9\u7edf\u9886\u4e00\u4ef6\u4e8b\u60c5\uff0c\u4e09\u516c\u4e3b\u4f4f\u7684\u542c\u7985\u9662\u4e2d\u5382\u6f5c\u6709\u7740\u7edd\u4e16\u9ad8 \u624b\uff0c\u4e00\u76f4\u5728\u6697\u4e2d\u7aa5\u89c6\u7740\u6211\u3002\u201d \u3000\u3000\u201c\u90a3\u53ef\u80fd\u662f\u4e09\u516c\u4e3b\u4e86\uff0c\u5979\u662f\u4e00\u9053\u98de\u8679\u82df\u6167\u6708\u7684\u95e8\u4e0b\u5f1f\u5b50\uff0c\u5251\u6280\u7cbe\u6e5b\uff0c\u8fd9\u4e24\u5e74\u4f4f\u5728\u897f\u57df\u9b54 \u5bab\u4e2d\uff0c\u53c8\u4e0d\u77e5\u5b66\u4f1a\u4e86\u4ec0\u4e48\u5947\u5f02\u7684\u6b66\u529f\uff1f\u653e\u773c\u5185\u5bab\u4e2d\u4eba\uff0c\u53ea\u6015\u5f88\u96be\u6709\u4eba\u80fd\u5bf9\u4ed8\u5979\u7684\u3002\u201d \u3000\u3000\u201c\u7edf\u9886\uff0c\u5c0f\u71d5\u5b50\u7684\u60f3\u6cd5\uff0c\u90a3\u6697\u4e2d\u76d1\u89c6\u6211\u7684\u4eba\uff0c\u672a\u5fc5\u4f1a\u662f\u4e09\u516c\u4e3b\uff1f\u201d \u3000\u3000\u201c\u4f60\u662f\u8bf4\uff0c\u8fd8\u4f1a\u6709\u522b\u7684\u9ad8\u624b\uff1f\u201d\u738b\u5f64\u8bf4\uff1a\u201c\u90a3\u771f\u662f\u5185\u5fe7\u5916\u60a3\uff0c\u5904\u5883\u9669\u6076\u4e86\u3002\u201d \u3000\u3000\u201c\u4e09\u516c\u4e3b\u4e0d\u4f1a\u5192\u8fd9\u4e2a\u9669\uff0c\u5927\u767d\u5929\u9690\u85cf\u5728\u82b1\u6811\u4e1b\u4e2d\uff0c\u6240\u4ee5\uff0c\u6211\u60f3\u662f\u53e6\u6709\u5176\u4eba\u3002\u201d \u3000\u3000\u201c\u770b\u6765\uff0c\u8fd9\u4ef6\u4e8b\u65e0\u6cd5\u518d\u9690\u533f\u4e0b\u53bb\u4e86\uff0c\u6211\u8981\u7acb\u523b\u594f\u660e\u7687\u4e0a\uff0c\u8bf7\u65e8\u5b9a\u593a\u3002\u201d\u738b\u5f64\u8bf4\uff1a\u201c\u4e00\u65e6 \u4e09\u516c\u4e3b\u4e2d\u9b54\u592a\u6df1\uff0c\u52a8\u4e86\u6740\u673a\uff0c\u7687\u4e0a\u5c31\u968f\u65f6\u6709\u6027\u547d\u4e4b\u5371\uff0c\u6b64\u4e8b\u975e\u540c\u5c0f\u53ef\uff0c\u65e0\u6cd5\u518d\u62d6\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u201c\u95f4\u4e0d\u758f\u4eb2\uff0c\u5979\u662f\u516c\u4e3b\u554a\uff01\u7687\u4e0a\u7684\u5973\u513f\u554a\uff01\u7edf\u9886\u63d0\u4e0d\u51fa\u660e\u786e\u7684\u8bc1\u636e\uff0c\u8981\u5982\u4f55\u5f00\u53e3\u542f\u594f \u5462\uff1f\u518d\u8bf4\uff0c\u7687\u4e0a\u80af\u76f8\u4fe1\u4e48\uff1f\u201d\u5c0f\u71d5\u5b50\u8bf4\uff1a\u201c\u6b64\u4e8b\u8fd8\u8bf7\u7edf\u9886\u4e09\u601d\uff01\u201d \u3000\u3000\u738b\u5f64\u6014\u4f4f\u4e86\uff01 \u3000\u3000\u7b2c\u4e00\uff0c\u4ed6\u60f3\u4e0d\u5230\u71d5\u6625\u98ce\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u7adf\u7136\u601d\u8651\u5f97\u5982\u6b64\u5468\u5bc6\u3002 \u3000\u3000\u7b2c\u4e8c\uff0c\u4e09\u516c\u4e3b\u771f\u6b63\u7684\u5c45\u5fc3\u4f55\u5728\uff1f\u96be\u9053\u5979\u8981\u63a7\u5236\u7687\u4e0a\uff0c\u631f\u5929\u5b50\u4ee5\u4ee4\u8bf8\u4faf\u3002 \u3000\u3000\u201c\u8fd9\u6837\u5427\uff01\u7ed9\u6211\u4e09\u5929\u7684\u65f6\u95f4\uff0c\u770b\u770b\u80fd\u4e0d\u80fd\u8bf4\u670d\u4e09\u516c\u4e3b\u2026\u2026\u201d \u3000\u3000\u201c\u53ea\u6015\u4e0d\u592a\u5bb9\u6613\u554a\uff01\u4e09\u516c\u4e3b\u4eba\u6027\u5927\u53d8\uff0c\u5c82\u662f\u8a00\u8bed\u80fd\u591f\u529d\u670d\u7684\uff1f\u201d \u3000\u3000\u2018\u54ea\u5c31\u627e\u673a\u4f1a\u5236\u670d\uff0c\u5e9f\u4e86\u5979\u4e00\u8eab\u6b66\u529f\u2026\u2026\u201c\u5c0f\u71d5\u5b50\u8bf4\uff1a\u201d\u4e09\u516c\u4e3b\u6210\u4e86\u4e00\u822c\u7684\u5973\u513f\u4e4b\u8eab\uff0c \u5c31\u4e0d\u4f1a\u518d\u751f\u5984\u5ff5\uff0c\u52a8\u7687\u4e0a\u7684\u8111\u7b4b\u4e86\u3002\u201c\u201d\u8fd9\u4e2a\u2026\u2026\u201c\u738b\u5f64\u5927\u5927\u7684\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201d\u8fd9\u662f\u4ee5\u4e0b \u72af\u4e0a\u2026\u2026\u201c \u3000\u3000\u201c\u7edf\u9886\u653e\u5fc3\uff0c\u5979\u4e0d\u4f1a\u544a\u8bc9\u7687\u4e0a\u7684\uff0c\u56e0\u4e3a\uff0c\u5979\u6ca1\u6709\u529e\u6cd5\u4e3a\u81ea\u5df1\u8fa9\u767d\u3002\u201d \u3000\u3000\u738b\u5f64\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u4f60\u771f\u80fd\u5236\u670d\u4e09\u516c\u4e3b\u4e48\uff1f\u5979\u662f\u8377\u6167\u6708\u7684\u5f1f\u5b50\u554a\uff01\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u4e48\uff1f\u7edf\u9886\u76f8\u4fe1\u6211\u597d\u4e86\uff0c\u6211\u4e0d\u4f1a\u62ff\u81ea\u5df1\u7684\u8fd9\u6761\u547d\u53bb\u5f00\u73a9\u7b11\u2026 \u3000\u3000\u2026\u3000\u3000\u201c\u71d5\u6625\u98ce\u9053\uff1a\u201d\u518d\u8bf4\uff0c\u771f\u7684\u5e9f\u4e86\u5979\u7684\u6b66\u529f\uff0c\u5bf9\u5979\u6709\u76ca\u65e0\u5bb3\uff0c\u5979\u4e0d\u4f1a\u518d\u6709\u91ce\u5fc3\uff0c\u53ef \u4ee5\u5b89\u5b89\u7a33\u7a33\u7684\u505a\u5979\u7684\u516c\u4e3b\u4e86\u3002\u201c \u3000\u3000\u770b\u4ed6\u8bf4\u7684\u8f7b\u677e\uff0c\u6beb\u65e0\u987e\u5fcc\uff0c\u738b\u5f64\u5374\u542c\u51fa\u4e86\u4e00\u8eab\u51b7\u6c57\u3002 \u3000\u3000\u4f46\u4ed6\u6ca1\u6709\u575a\u51b3\u7684\u963b\u6b62\u5c0f\u71d5\u5b50\uff0c\u56e0\u4e3a\uff0c\u4ed6\u60f3\u4e0d\u51fa\u4e00\u4e2a\u66f4\u597d\u7684\u529e\u6cd5\uff0c\u53ea\u597d\u957f\u957f\u53f9\u606f\u4e00\u58f0\uff0c \u9053\uff1a\u201c\u5c0f\u71d5\u5b50\uff0c\u6ca1\u6709\u628a\u63e1\uff0c\u7edd\u4e0d\u53ef\u8f7b\u6613\u5c1d\u8bd5\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\uff0c\u8fd8\u6709\u4e00\u4ef6\u4e8b\u544a\u8bc9\u7edf\u9886\uff0c\u91d1\u5929\u7965\u7ea6\u6211\u660e\u5929\u53bb\u5bab\u5916\u901b\u901b\uff0c\u5171\u8fdb\u665a\u9910\u3002\u201d \u3000\u3000\u201c\u8981\u5c0f\u5fc3\u554a\uff01\u91d1\u5929\u7965\u5df2\u88ab\u4eba\u6536\u7528\uff0c\u6211\u4eb2\u773c\u770b\u5230\u4ed6\u6740\u4e86\u8c2d\u4fe1\uff0c\u4ed6\u4eec\u662f\u4e09\u56db\u5e74\u7684\u670b\u53cb\u4e86\u3002\u201d \u738b\u5f64\u8bf4\uff1a\u201c\u8fd9\u5c0f\u5b50\u5fc3\u72e0\u624b\u8fa3\uff0c\u6050\u6015\u6697\u65bd\u7b97\u8ba1\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c31\u8981\u7edf\u9886\u5e2e\u5fd9\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u4f60\u8bf4\u5427\uff01\u5c0f\u71d5\u5b50\uff0c\u80fd\u591f\u529e\u5230\u7684\uff0c\u6211\u4e00\u5b9a\u5168\u529b\u652f\u6301\u3002\u201d \u3000\u3000\u201c\u522b\u5e38\u5e38\u627e\u6211\u8bf4\u8bdd\u2026\u2026\u201d\u5c0f\u71d5\u5b50\u8bf4\uff1a\u201c\u4ed6\u4eec\u90fd\u6ca1\u6709\u6000\u7591\u5230\u6211\uff0c\u5c31\u4e0d\u4f1a\u5bf9\u6211\u4e0b\u624b\u3002\u201d \u3000\u3000\u738b\u5f64\u70b9\u70b9\u5934\u3002 \u3000\u3000\u201c\u6211\u4e0d\u6015\u4ed6\u4eec\u6697\u65bd\u7b97\u8ba1\uff0c\u4f46\u6211\u6015\u4ed6\u4eec\u5bf9\u6211\u52a8\u7591\u4e4b\u540e\uff0c\u5c31\u4e0d\u518d\u7406\u6211\uff0c\u90a3\u65f6\u5019\uff0c\u6211\u5c31\u65e0\u6cd5\u6df7 \u5165\u4ed6\u4eec\u4e4b\u4e2d\uff0c\u5957\u53d6\u9690\u5bc6\u2026\u2026\u201d\u71d5\u6625\u98ce\u9053\uff1a\u201c\u53ea\u6015\u4e5f\u65e0\u6cd5\u63a5\u8fd1\u4e09\u516c\u4e3b\uff0c\u90a3\u5c31\u53ea\u6709\u4e00\u6761\u8def\u8d70\uff0c\u786c \u95f4\u542c\u7985\u9662\u4e86\uff01\u201d \u3000\u3000\u201c\u4f7f\u4e0d\u5f97\uff01\u4f7f\u4e0d\u5f97\uff01\u201d\u738b\u5f64\u6025\u6025\u5730\u9053\uff1a\u201c\u90a3\u65f6\uff0c\u5185\u5bab\u4f8d\u536b\u90fd\u5c06\u548c\u4f60\u4e3a\u654c\uff0c\u8fde\u6211\u4e5f\u65e0\u6cd5\u963b \u6b62\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\uff0c\u6700\u597d\u522b\u8981\u6211\u9732\u51fa\u7834\u7efd\u2026\u2026\u201d\u5c0f\u71d5\u5b50\u7b11\u9053\uff1a\u201c\u7edf\u9886\u6700\u597d\u4e5f\u8f6c\u544a\u6211\u7239\u4e00\u58f0\uff0c\u7236\u5b50\u60c5 \u6df1\uff0c\u6211\u4e00\u6837\u60f3\u5ff5\u7740\u4ed6\uff0c\u4e00\u65e6\u78b0\u9762\uff0c\u5374\u96be\u514d\u6d41\u9732\u51fa\u5929\u6027\u81f3\u60c5\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\uff0c\u6211\u4f1a\u544a\u8bc9\u4ee4\u5c0a\u3002\u201d\u738b\u5f64\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u5c0f\u71d5\u5b50\uff0c\u4f60\u603b\u4e0d\u80fd\u8ba9\u97f3\u8baf\u5b8c\u5168\u9694\u7edd\uff0c\u5f7c \u6b64\u65e0\u6cd5\u652f\u63f4\u3001\u914d\u5408\u3002\u201d \u3000\u3000\u201c\u5b58\u91cd\u5927\u7684\u4e8b\u60c5\uff0c\u6211\u4f1a\u627e\u65f6\u95f4\u6765\u89c1\u7edf\u9886\uff0c\u8fd9\u51e0\u65e5\uff0c\u7edf\u9886\u6700\u597d\u7559\u5bbf\u5728\u4e66\u623f\u4e4b\u4e2d\u3002\u201d \u3000\u3000\u738b\u5f64\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8fd9\u91cc\u5f88\u6e05\u9759\uff0c\u6211\u5c31\u7559\u5bbf\u5728\u6b64\uff0c\u968f\u65f6\u7b49\u5019\u4f60\u7684\u6d88\u606f\u3002\u201d \u3000\u3000\u201c\u8fd9\u6837\uff0c\u4e5f\u53ef\u4ee5\u7ed9\u4ed6\u4eec\u4e2a\u83ab\u6d4b\u9ad8\u6df1\u7684\u611f\u89c9\u2026\u2026\u201d\u7ad9\u8d77\u8eab\u5b50\uff0c\u544a\u8f9e\u800c\u53bb\u3002 \u3000\u3000\u77a7\u7740\u5c0f\u71d5\u5b50\u7684\u80cc\u5f71\uff0c\u738b\u5f64\u5446\u4f4f\u4e86\uff01 \u3000\u3000\u8fd9\u4e2a\u5c0f\u5bb6\u4f19\u5b9e\u5728\u6709\u4e9b\u80c6\u5927\u5305\u5929\uff0c\u6284\u5bb6\u706d\u65cf\u7684\u5927\u7978\u5728\u4ed6\u8bf4\u6765\uff0c\u4e5f\u662f\u8f7b\u8f7b\u677e\u677e\u7684\u4e8b\uff0c\u4efb\u4ed6\u653e \u624b\u53bb\u505a\uff0c\u771f\u4e0d\u77e5\u4f1a\u95f9\u51fa\u4ec0\u4e48\u60ca\u5929\u52a8\u5730\u7684\u5927\u4e8b\u6765\u3002 \u3000\u3000\u4f46\u738b\u5f64\u5c31\u662f\u60f3\u4e0d\u51fa\u4e00\u4e2a\u529e\u6cd5\uff0c\u6765\u5316\u89e3\u53bb\u8fd9\u573a\u8feb\u5728\u7709\u776b\u7684\u5371\u673a\uff0c\u89e3\u5f00\u8fd9\u4e2a\u6b7b\u7ed3\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u771f\u7684\u5f88\u8f7b\u677e\uff0c\u4ed6\u7f13\u6b65\u4e71\u901b\uff0c\u5230\u5904\u5bdf\u770b\uff0c\u6697\u4e2d\u5374\u628a\u5404\u79cd\u5f62\u52bf\uff0c\u9ed8\u8bb0\u4e8e\u5fc3\u3002 \u3000\u3000\u5bab\u5ef7\u5e7f\u5927\uff0c\u6bbf\u9601\u91cd\u91cd\uff0c\u5230\u5904\u662f\u5bab\u5973\u3001\u592a\u76d1\uff0c\u5c0f\u71d5\u5b50\u8868\u9762\u4e0a\u5927\u800c\u5316\u4e4b\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u5374\u662f\u6781\u4e3a \u5c0f\u5fc3\uff0c\u5374\u4e5f\u88ab\u4ed6\u907f\u5f00\u4e86\u51e0\u9053\u592a\u76d1\u9a7b\u5b88\u7684\u5173\u53e3\u3002 \u5367\u9f99\u751f\u300a\u71d5\u5b50\u4f20\u5947\u300b \u7b2c\u5341\u4e94\u56de\u3000\u5bab\u4e2d\u9664\u5978 \u3000\u3000\u4e8c\u66f4\u65f6\u5206\uff0c\u5c0f\u71d5\u5b50\u6362\u7a7f\u4e86\u4e00\u8eab\u7384\u8272\u52b2\u88c5\uff0c\u6e9c\u5165\u4e86\u542c\u7985\u9662\u4e2d\u3002 \u3000\u3000\u4ed6\u8f7b\u529f\u7edd\u4f73\uff0c\u501f\u82b1\u6811\u63a9\u906e\uff0c\u907f\u5f00\u4e86\u4e09\u9053\u9632\u5b88\u3002 \u3000\u3000\u542c\u8749\u9662\u5728\u5e7f\u9614\u7684\u7687\u5bab\u4e2d\uff0c\u72ec\u6210\u4e00\u4e2a\u9662\u843d\uff0c\u9662\u5916\u5374\u5e03\u5b88\u4e86\u91cd\u91cd\u4f8d\u536b\u3002 \u3000\u3000\u5f53\u7136\uff0c\u8fd9\u662f\u738b\u5f64\u6709\u610f\u7684\u5b89\u6392\uff0c\u770b\u4e0a\u53bb\uff0c\u4f3c\u662f\u4fdd\u62a4\u4e09\u516c\u4e3b\uff0c\u4f46\u9aa8\u5b50\u91cc\u5374\u662f\u76d1\u89c6\u542c\u8749\u9662\u4e2d\u7684 \u884c\u52a8\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u8fdb\u4e86\u542c\u8749\u9662\uff0c\u4e0d\u518d\u63a9\u853d\u8eab\u5f62\uff0c\u5927\u6447\u5927\u6446\u7684\u884c\u51fa\u82b1\u4e1b\uff0c\u8f7b\u8f7b\u7684\u54b3\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u58f0\u97f3\u867d\u7136\u4e0d\u5927\uff0c\u4f46\u5728\u5e7d\u9759\u7684\u542c\u8749\u9662\u4e2d\uff0c\u5374\u4f20\u51fa\u4e86\u6570\u4e08\u8fdc\u8fd1\u3002 \u3000\u3000\u679c\u7136\uff0c\u4e00\u6761\u4eba\u5f71\uff0c\u7a81\u7136\u5728\u82b1\u4e1b\u4e2d\u51fa\u73b0\uff0c\u76f4\u76f4\u7684\u884c\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4ed6\u8d70\u7684\u5f88\u6162\uff0c\u4e3e\u6b62\u4ece\u5bb9\uff0c\u76f4\u884c\u81f3\u71d5\u6625\u98ce\u8eab\u524d\u56db\u4e94\u5c3a\u5904\uff0c\u624d\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u51dd\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u6765\u4eba\u4e00\u8138\u76b1\u7eb9\uff0c\u6ee1\u5934\u4e71\u53d1\uff0c\u7a7f\u4e00\u4ef6\u9ed1\u8272\u5927\u8902\uff0c\u624b\u4e2d\u63d0\u7740\u4e00\u6839\u62d0 \u6756\uff0c\u4e00\u773c\u4e4b\u4e0b\uff0c\u7adf\u7136\u65e0\u6cd5\u8fa8\u51fa\u662f\u7537\u662f\u5973\u3002 \u3000\u3000\u201c\u6211\u627e\u601c\u82b1\u59d0\u59d0\uff0c\u662f\u5979\u7ea6\u6211\u6765\u7684\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u70b9\u70b9\u5934\uff0c\u4e3e\u624b\u793a\u610f\uff0c\u8981\u5c0f\u71d5\u5b50\u8ddf\u7740\u4ed6\u8d70\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u827a\u9ad8\u80c6\u5927\uff0c\u4e5f\u4e0d\u591a\u95ee\uff0c\u968f\u5728\u9ed1\u8863\u4eba\u7684\u8eab\u540e\uff0c\u884c\u5165\u4e86\u4e00\u95f4\u5e7d\u6697\u7684\u623f\u95f4\u4e2d\u3002 \u3000\u3000\u201c\u601c\u82b1\u59d0\u59d0\u5462\uff1f\u2026\u2026\u201d \u3000\u3000\u9ed1\u8863\u4eba\u6ca1\u6709\u56de\u7b54\uff0c\u8f6c\u8eab\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u201c\u6211\u5728\u8fd9\u91cc\u3002\u201d\u5c4b\u89d2\u5e7d\u6697\u5904\uff0c\u884c\u51fa\u4e86\u601c\u82b1\u59d1\u5a18\u3002 \u3000\u3000\u5c4b\u4e2d\u5f88\u9ed1\uff0c\u96be\u89c1\u5230\u4e24\u5c3a\u5916\u7684\u666f\u7269\uff0c\u4f46\u5c0f\u71d5\u5b50\u76ee\u529b\u8fc7\u4eba\uff0c\u7a0d\u4f5c\u8c03\u6574\uff0c\u5df2\u9002\u5c4b\u4e2d\u7684\u9ed1\u6697\u3002 \u3000\u3000\u601c\u82b1\u83b2\u6b65\u8f7b\u76c8\uff0c\u4e00\u76f4\u649e\u5165\u4e86\u5c0f\u71d5\u5b50\u7684\u6000\u4e2d\uff0c\u624d\u505c\u4e86\u4e0b\u6765\uff0c\u9053\uff1a\u201c\u4f60\u600e\u4e48\u8fdb\u6765\u7684\uff1f\u201d \u3000\u3000\u5410\u6c14\u5982\u5170\uff0c\u6a31\u6843\u5c0f\u53e3\u5c31\u8981\u8d34\u4e0a\u4e86\u5c0f\u71d5\u5b50\u7684\u4e0b\u989a\u3002 \u3000\u3000\u201c\u59d0\u59d0\u8981\u6211\u6765\uff0c\u6211\u5c31\u6765\u4e86\u3002\u201d \u3000\u3000\u7b54\u590d\u7684\u5e72\u8106\uff0c\u542c\u5f97\u53eb\u4eba\u7a9d\u5fc3\uff0c\u5c31\u662f\u6ca1\u6709\u8bf4\u600e\u4e48\u8fdb\u6765\u7684\u3002 \u3000\u3000\u601c\u82b1\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6ca1\u6709\u4eba\u963b\u62e6\u4f60\u4e48\uff1f\u201d \u3000\u3000\u201c\u6709\u5440\uff01\u201d\u71d5\u6625\u98ce\u9053\uff1a\u201c\u6211\u544a\u8bc9\u4ed6\u4eec\u5949\u547d\u5de1\u67e5\uff0c\u8d70\u5230\u56f4\u5899\u8fb9\uff0c\u501f\u82b1\u6728\u63a9\u906e\uff0c\u4fbf\u8d8a\u5899\u800c \u5165\u3002\u201d \u3000\u3000\u201c\u55c5\uff01\u77a7\u4e0d\u51fa\u4f60\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u8fd8\u4f1a\u968f\u673a\u5e94\u53d8\u554a\uff01\u201d\u601c\u82b1\u9053\uff1a\u201c\u6709\u6ca1\u6709\u4eba\u77a7\u5230\u4f60\u7ffb\u8d8a\u56f4\u5899 \u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\u2026\u2026\u201d\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u77a7\u5230\u4e86\u6253\u4ec0\u4e48\u7d27\uff0c\u5de1\u591c\u67e5\u54e8\u662f\u516c\u4e8b\u554a\uff01\u201d \u3000\u3000\u601c\u82b1\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u4f60\u662f\u804b\u5b50\u4e0d\u6015\u96f7\uff0c\u662f\u771f\u7684\u8ff7\u7cca\u554a\uff01\u201d \u3000\u3000\u201c\u5509\uff01\u83b2\u82b1\u59d0\u59d0\u662f\u8ddf\u6211\u5f00\u73a9\u7b11\u4e86\uff0c\u6ca1\u6709\u4e8b\uff0c\u6211\u5c31\u8d70\u4e86\u3002\u201d \u3000\u3000\u8bf4\u8d70\u5c31\u8d70\uff0c\u4e3e\u6b65\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u601c\u82b1\u7adf\u7136\u6ca1\u6709\u963b\u6b62\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u884c\u5165\u82b1\u4e1b\uff0c\u98de\u8dc3\u56f4\u5899\u800c\u53bb\u3002 \u3000\u3000\u201c\u4ed6\u7684\u6b66\u529f\u4e0d\u9519\u2026\u2026\u201d\u6697\u5f71\u4e2d\u884c\u51fa\u4e86\u4e09\u516c\u4e3b\uff0c\u9053\uff1a\u201c\u4eba\u4e5f\u83ab\u6d4b\u9ad8\u6df1\uff01\u201d \u3000\u3000\u201c\u53ea\u8981\u5f00\u5c01\u5e9c\u6d88\u606f\u4f20\u5230\uff0c\u5c31\u53ef\u4ee5\u77e5\u6653\u4ed6\u7684\u6765\u5386\u4e86\u3002\u201d\u601c\u82b1\u9053\uff1a\u201c\u770b\u4ed6\u90a3\u80a1\u50bb\u547c\u547c\u7684\u6837 \u5b50\uff0c\u5012\u662f\u4e0d\u50cf\u88c5\u4f5c\u3002\u201d \u3000\u3000\u201c\u738b\u5f64\u5df2\u5bf9\u6211\u52a8\u4e86\u6000\u7591\uff0c\u6bcf\u591c\u521d\u66f4\u4e4b\u540e\uff0c\u5c31\u52a0\u6d3e\u4e86\u5341\u51e0\u540d\u4f8d\u536b\uff0c\u5e03\u5b88\u56db\u5468\uff0c\u8fd9\u5c0f\u5b50\u4e0d\u53ef \u80fd\u907f\u5f97\u5f00\u90a3\u4e9b\u8033\u76ee\u3002\u201d\u4e09\u516c\u4e3b\u9053\uff1a\u201c\u9664\u975e\uff0c\u4ed6\u7684\u8f7b\u529f\u5df2\u5230\u4e86\u8e0f\u96ea\u65e0\u75d5\u3001\u98de\u884c\u7edd\u8ff9\u7684\u5883\u754c\uff1f\u201d \u3000\u3000\u201c\u4e0d\u50cf\u554a\uff01\u2026\u2026\u201d\u601c\u82b1\u9053\uff1a\u201c\u770b\u4ed6\u7ffb\u8d8a\u56f4\u5899\u7684\u8eab\u6cd5\uff0c\u53ea\u80fd\u7b97\u7b2c\u4e8c\u6d41\u8eab\u624b\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u662f\u738b\u5f64\u4e0b\u7684\u89e3\u4e86\uff0c\u54fc\u54fc\uff01\u660e\u5929\u8981\u91d1\u5929\u7965\u5e26\u4ed6\u53bb\u60dc\u6625\u90a3\u91cc\uff0c\u6211\u8981\u597d\u597d\u7684\u67e5\u4ed6\u4e00 \u67e5\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u5c0f\u5a62\u4f1a\u4f20\u8bdd\u7ed9\u91d1\u5929\u7965\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u4e00\u53e3\u6c14\u5954\u56de\u4f4f\u5904\uff0c\u5012\u5934\u4fbf\u7761\uff0c\u5fc3\u4e2d\u5374\u6697\u81ea\u76d8\u7b97\u9053\uff1a\u201c\u601c\u82b1\u5206\u660e\u8eab\u6000\u6b66\u529f\uff0c\u7edf\u9886\u600e \u4f1a\u8bf4\u5979\u662f\u4e00\u4e2a\u666e\u901a\u7684\u5bab\u5973\u3002 \u3000\u3000\u90a3\u4e3a\u6211\u5e26\u8def\u7684\u9ed1\u8863\u4eba\u7a76\u7adf\u662f\u7537\u662f\u5973\uff0c\u90a3\u9690\u5728\u6697\u4e2d\uff0c\u5077\u542c\u6211\u548c\u601c\u82b1\u8bf4\u8bdd\u7684\u53c8\u662f\u4ec0\u4e48\u4eba\u5462\uff1f \u662f\u4e0d\u662f\u4e09\u516c\u4e3b\uff1f \u3000\u3000\u4eca\u665a\u4e0a\u5927\u5927\u610f\u4e86\uff0c\u51e0\u4e4e\u9732\u51fa\u4e86\u9a6c\u811a\uff0c\u601c\u82b1\u8ba9\u6211\u79bb\u5f00\uff0c\u4e0d\u52a0\u4ee5\u963b\u6b62\uff0c\u60f3\u662f\u5fc3\u4e2d\u5bf9\u6211\u8d77\u7591 \u4e86\uff1f\u201d \u3000\u3000\u60f3\u4e86\u4e00\u9635\uff0c\u7acb\u523b\u6697\u4e2d\u8fd0\u6c14\uff0c\u5e73\u606f\u6742\u5ff5\uff0c\u6c89\u6c89\u7684\u7761\u53bb\u4e86\u3002 \u3000\u3000\u5c11\u5e74\u4e0d\u77e5\u6101\u6ecb\u5473\uff0c\u5c3d\u7ba1\u5c0f\u71d5\u5b50\u5fc3\u4e2d\u6709\u65e0\u6570\u7684\u7591\u95ee\uff0c\u4f46\u4e00\u629b\u5f00\uff0c\u7acb\u523b\u4fbf\u5165\u68a6\u4e86\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\uff0c\u5c0f\u71d5\u5b50\u503c\u73ed\u5f52\u6765\uff0c\u91d1\u5929\u7965\u5c31\u6765\u9020\u8bbf\uff0c\u50ac\u4fc3\u5c0f\u71d5\u4e86\u6362\u4e0a\u8863\u670d\uff0c\u4e00\u8d77\u79bb\u5f00\u4e86\u5bab\u5ef7\u3002 \u3000\u3000\u4ed6\u4eec\u6709\u4f8d\u536b\u8170\u724c\uff0c\u51fa\u5165\u7981\u5bab\u4e0d\u53d7\u963b\u62e6\u3002 \u3000\u3000\u71d5\u6625\u98ce\u5fc3\u4e2d\u5fd6\u9053\uff1a\u91d1\u5929\u7965\u4f3c\u662f\u4e00\u76f4\u5728\u76d1\u89c6\u7740\u6211\uff0c\u624d\u80fd\u8fd9\u4e48\u5feb\u7684\u53ca\u65f6\u800c\u81f3\uff0c\u4eca\u5929\u8fd9\u4e00\u5173\u7edd \u4e0d\u597d\u8fc7\uff0c\u5fc5\u6709\u4e00\u4e9b\u7279\u6b8a\u7684\u5b89\u6392\uff0c\u8981\u5c0f\u5fc3\u5e94\u4ed8\u624d\u884c\u3002 \u3000\u3000\u91d1\u5929\u7965\u53ea\u7ba1\u5e26\u5934\u8d76\u8def\uff0c\u5c0f\u71d5\u5b50\u88c5\u8ff7\u7cca\u4e5f\u4e0d\u591a\u95ee\uff0c\u884c\u5165\u4e86\u4e00\u6761\u6e05\u5e7d\u5df7\u5b50\u91cc\uff0c\u91d1\u5929\u7965\u624d\u7b11 \u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4eca\u5929\u65f6\u95f4\u4e0d\u591a\uff0c\u6211\u5148\u5e26\u4f60\u53bb\u54c1\u5c1d\u4e00\u4e0b\u4eac\u4e2d\u6700\u597d\u7684\u9152\u83dc\uff0c\u5982\u4f55\uff1f\u201d \u3000\u3000\u71d5\u6625\u98ce\u76ee\u5149\u8f6c\u52a8\uff0c\u53d1\u89c9\u8fd9\u6761\u767d\u77f3\u94fa\u6210\u7684\u5df7\u5b50\u91cc\uff0c\u4e24\u4fa7\u90fd\u662f\u9ad8\u5927\u7684\u7816\u623f\uff0c\u7ea2\u6f06\u5927\u95e8\uff0c\u4f3c\u662f \u4e00\u5904\u9ad8\u8d35\u7684\u4f4f\u5b85\u533a\u57df\uff0c\u4e0d\u7981\u5947\u9053\uff1a\u201c\u5927\u83dc\u9986\u4e0d\u5f00\u8bbe\u5728\u5927\u8857\u4e0a\uff0c\u4f1a\u5728\u8fd9\u4e48\u6837\u7684\u4e00\u6761\u5df7\u5b50\u91cc\uff1f\u201d \u3000\u3000\u201c\u66f2\u5f84\u901a\u5e7d\u554a\uff01\u8001\u5f1f\uff0c\u771f\u6b63\u7684\u4f73\u80b4\u7f8e\u5473\uff0c\u53ea\u4f9b\u884c\u5bb6\u98df\u7528\uff0c\u7b49\u4e00\u4e0b\u4f60\u5c31\u4f1a\u559d\u5230\u6700\u597d\u7684\u9152\uff0c \u5403\u5230\u4ece\u672a\u5403\u8fc7\u7684\u7f8e\u5473\u2026\u2026\u201d\u91d1\u5929\u7965\u795e\u79d8\u7684\u7b11\u7b11\u9053\uff1a\u201c\u5f53\u7136\uff0c\u4e5f\u80fd\u770b\u5230\u7edd\u4e16\u59ff\u8272\u7684\u7f8e\u5973\u3002\u201d \u3000\u3000\u201c\u6211\u542c\u4eba\u8bf4\u8fc7\uff0c\u5929\u4e0b\u7f8e\u5973\uff0c\u5c3d\u96c6\u7687\u5bab\uff0c\u90a3\u4e9b\u5bab\u5a25\u91c7\u5973\u4eec\uff0c\u679c\u7136\u90fd\u957f\u5f97\u5982\u4ed9\u5973\u4e00\u822c\uff0c\u96be\u9053 \u8fd8\u6709\u6bd4\u5bab\u4e2d\u7684\u5973\u4eba\u66f4\u6f02\u4eae\u7684\u5730\u65b9\u4e0d\u6210\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u4f4e\u58f0\u9053\uff1a\u201c\u90a3\u662f\u4e2a\u4ec0\u4e48\u6240\u5728\u5462\uff1f\u201d \u3000\u3000\u91d1\u5929\u7965\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u4e0d\u9519\uff0c\u540e\u5bab\u7c89\u9edb\uff0c\u90fd\u662f\u7531\u6c11\u95f4\u7f8e\u5973\u9009\u51fa\u6765\u7684\uff0c\u7f8e\u5219\u7f8e\uff0c\u5374\u5c11\u4e86\u5973\u4eba \u90a3\u4efd\u98ce\u60c5\uff0c\u8fd9\u4e2a\u5730\u65b9\u7684\u5973\u5b50\uff0c\u4e0d\u4f46\u8c8c\u7f8e\u5982\u82b1\uff0c\u800c\u4e14\u98ce\u60c5\u4e07\u79cd\uff0c\u90a3\u624d\u662f\u53eb\u7537\u4eba\u7740\u8ff7\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u201c\u55c5\uff01\u6709\u8fd9\u7b49\u5730\u65b9\uff0c\u90a3\u771f\u5f97\u53bb\u5f00\u5f00\u773c\u754c\u4e86\u3002\u201d\u5c0f\u71d5\u5b50\u6d41\u9732\u51fa\u5411\u5f80\u7684\u795e\u8272\u3002 \u3000\u3000\u5916\u9762\u770b\u53bb\uff0c\u8fd9\u53ea\u662f\u4e00\u5ea7\u666e\u901a\u7684\u5b85\u9662\uff0c\u4f46\u8fdb\u4e86\u5927\u95e8\uff0c\u7acb\u523b\u6709\u4e24\u4e2a\u5934\u68b3\u53cc\u8fab\u7684\u9752\u8863\u5973\u5b50\u8fce\u4e86 \u4e0a\u6765\u3002 \u3000\u3000\u5979\u4eec\u4e0d\u662f\u5f88\u6f02\u4eae\uff0c\u4f46\u90a3\u80a1\u8fce\u4eba\u7684\u5a9a\u7b11\uff0c\u5374\u5341\u5206\u52a8\u4eba\uff0c\u7ec6\u788e\u83b2\u6b65\uff0c\u5954\u884c\u5982\u98de\uff0c\u6446\u52a8\u7684\u67f3 \u8170\uff0c\u7a81\u663e\u51fa\u4e86\u70af\u5a1c\u591a\u59ff\u7279\u8272\u3002 \u3000\u3000\u663e\u7136\uff0c\u5979\u4eec\u662f\u7ecf\u8fc7\u4e86\u4e00\u756a\u4e25\u683c\u7684\u8bad\u7ec3\u3002 \u3000\u3000\u201c\u8fd9\u53ea\u662f\u4e24\u4e2a\u8fce\u5ba2\u7684\u4e2b\u5934\uff0c\u8001\u5f1f\uff0c\u600e\u4e48\u6837\uff1f\u201d\u91d1\u5929\u7965\u9053\uff1a\u201c\u662f\u4e0d\u662f\u4e0e\u4f17\u4e0d\u540c\u554a\uff01\u201d \u3000\u3000\u201c\u5bf9\uff01\u5bab\u5973\u4eec\u7f3a\u5c11\u4e86\u5979\u4eec\u90a3\u4efd\u8fce\u4eba\u7b11\u8138\u548c\u5a07\u5a9a\u795e\u6001\u2026\u2026\u201d\u71d5\u6625\u98ce\u9053\uff1a\u201c\u770b\u6837\u5b50\uff0c\u8fd9\u4e00\u9910 \u6050\u6015\u8981\u82b1\u4e0d\u5c11\u7684\u94f6\u5b50\u5427\uff01\u201d \u3000\u3000\u201c\u94f6\u5b50\u7684\u4e8b\u4e0d\u7528\u4f60\u8001\u5f1f\u8d39\u5fc3\uff0c\u7531\u5927\u54e5\u6211\u8bf7\u5ba2\u3002\u201d\u91d1\u5929\u7965\u9053\uff1a\u201c\u4f60\u8001\u5f1f\u53ea\u7ba1\u597d\u597d\u7684\u5403\u559d\u3001 \u73a9\u4e50\uff0c\u5c3d\u60c5\u4eab\u53d7\u3002\u201d \u3000\u3000\u53e3\u6c14\u5927\u53d8\uff0c\u548c\u6628\u5929\u90a3\u79cd\u76ee\u4e2d\u65e0\u4eba\u3001\u9ad8\u9ad8\u5728\u4e0a\u7684\u53e3\u6c14\u548c\u6c14\u52bf\uff0c\u5b8c\u5168\u4e0d\u540c\u3002 \u3000\u3000\u8c08\u8bdd\u4e4b\u95f4\uff0c\u5df2\u8fd1\u5385\u5802\uff0c\u4e24\u4e2a\u8fce\u5ba2\u7684\u5c11\u5973\u5206\u5217\u5927\u95e8\u4e24\u4fa7\uff0c\u9f50\u58f0\u8bf4\u9053\uff1a\u201c\u91d1\u7237\u548c\u8d35\u5ba2\u8bf7\uff01\u201d \u3000\u3000\u770b\u6765\uff0c\u91d1\u5929\u7965\u4f3c\u662f\u8fd9\u91cc\u7684\u5e38\u5ba2\u3002 \u3000\u3000\u91d1\u5929\u7965\u5927\u6b65\u800c\u5165\uff0c\u71d5\u6625\u98ce\u7d27\u968f\u8eab\u540e\u3002 \u3000\u3000\u5927\u5385\u4e2d\u65e9\u5df2\u7ad9\u4e86\u4e2a\u5343\u5a07\u767e\u5a9a\u7684\u4fcf\u4f73\u4eba\u3002 \u3000\u3000\u662f\u60dc\u6625\uff0c\u53ef\u60dc\u71d5\u6625\u98ce\u6ca1\u6709\u89c1\u8fc7\uff0c\u8fd9\u4f4d\u51fa\u8eab\u9b54\u6559\uff0c\u6df7\u5165\u5185\u5bab\u7684\u5c0f\u9b54\u5973\uff0c\u7ecf\u8fc7\u4e86\u4e00\u756a\u4ed4\u7ec6\u7684 \u88c5\u626e\uff0c\u679c\u7136\u662f\u98ce\u59ff\u7ef0\u7ea6\u3001\u5a07\u5a9a\u64a9\u4eba\u3002 \u3000\u3000\u53ea\u89c1\u5979\u8f7b\u79fb\u83b2\u6b65\uff0c\u8eac\u8eab\u4e00\u798f\uff0c\u9053\uff1a\u201c\u91d1\u7237\uff0c\u591a\u65e5\u4e0d\u89c1\u4e86\uff0c\u98ce\u91c7\u66f4\u89c1\u6e05\u96c5\u2026\u2026\u3002 \u3000\u3000\u4e00\u6307\u71d5\u6625\u98ce\uff0c\u91d1\u5929\u7965\u4f4e\u58f0\u5730\u8bf4\u9053\uff1a\u201c\u71d5\u8001\u5f1f\u662f\u4eca\u5929\u7684\u4e3b\u5ba2\uff0c\u59d1\u5a18\u8981\u597d\u597d\u7684\u62db\u5f85\u3002\u201d \u3000\u3000\u6084\u4f73\u4eba\u8f6c\u5411\u71d5\u6625\u98ce\uff0c\u5148\u9001\u4e0a\u4e00\u4e2a\u5a07\u751c\u7684\u5a9a\u7b11\uff0c\u9053\uff1a\u201c\u91d1\u7237\u7684\u8d35\u5bbe\u600e\u6562\u6020\u6162\uff0c\u71d5\u516c\u5b50\uff0c \u6765\uff01\u5c0f\u59b9\u4e3a\u516c\u5b50\u5e26\u8def\u3002\u201d \u3000\u3000\u5979\u80c6\u5927\u7684\u7adf\u7136\u4f38\u51fa\u4e86\u7ea4\u5de7\u7684\u7389\u624b\uff0c\u4e00\u628a\u6293\u4f4f\u4e86\u5c0f\u71d5\u5b50\u7684\u5de6\u8155\uff0c\u5fae\u7b11\u7740\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u71d5\u6625\u98ce\u611f\u89c9\u5230\u90a3\u67d4\u6ed1\u7684\u7389\u624b\u4e2d\uff0c\u900f\u51fa\u4e86\u9635\u9635\u7684\u70ed\u529b\uff0c\u653b\u5165\u808c\u80a4\uff0c\u6563\u5411\u5168\u8eab\uff0c\u7acb\u523b\u5f15\u8d77\u4e86 \u4e00\u9635\u83ab\u540d\u7684\u5174\u594b\uff0c\u4e0d\u7981\u5403\u4e86\u4e00\u60ca\uff0c\u5fd6\u9053\uff1a\u4e00\u89c1\u9762\u5c31\u4e0b\u624b\u554a\uff01\u8d76\u5fd9\u4e00\u63d0\u771f\u6c14\uff0c\u62a4\u4f4f\u8981\u7a74\u3001\u7ecf \u8109\uff0c\u5e73\u606f\u4e0b\u90a3\u80a1\u5174\u594b\u7684\u60c5\u7eea\uff0c\u4f46\u5374\u9002\u53ef\u800c\u6b62\uff0c\u4e0d\u8ba9\u5bf9\u65b9\u611f\u89c9\u5230\u4ed6\u5728\u8fd0\u529f\u6297\u62d2\u3002 \u3000\u3000\u4fcf\u4f73\u4eba\u62d6\u7740\u71d5\u6625\u98ce\uff0c\u4e00\u53e3\u6c14\u5954\u5165\u4e86\u5927\u5385\u540e\u4e00\u5ea7\u96c5\u5ba4\u4e4b\u4e2d\uff0c\u624d\u56de\u9996\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u71d5\u516c\u5b50\uff0c \u4f60\u2026\u2026\u201d \u3000\u3000\u7a81\u7136\u4e00\u5446\uff0c\u4f4f\u53e3\u4e0d\u8a00\u3002 \u3000\u3000\u539f\u6765\u5c0f\u71d5\u5b50\u9762\u8272\u5e73\u548c\uff0c\u795e\u60c5\u660e\u6717\uff0c\u4e00\u70b9\u4e5f\u6ca1\u6709\u53d7\u5230\u611f\u67d3\u3001\u5f71\u54cd\u3002 \u3000\u3000\u201c\u6211\u5f88\u597d\u2026\u2026\u201d\u71d5\u6625\u98ce\u8f7b\u8f7b\u6323\u8131\u4e86\u5de6\u8155\uff0c\u9053\uff1a\u201c\u8fd9\u91cc\u5f88\u96c5\u81f4\uff0c\u6b63\u662f\u559d\u9152\u8c08\u5fc3\u7684\u597d\u5730\u65b9 \u554a\uff01\u201d \u3000\u3000\u201c\u9152\u83dc\u53ef\u53e3\uff0c\u4f73\u4eba\u5982\u753b\u2026\u2026\u201d\u91d1\u5929\u7965\u6b65\u5165\u96c5\u5ba4\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u5317\u4eac\u57ce\u6700\u8ff7\u4eba\u7684\u4e00\u5904\u597d\u5730 \u65b9\u3002\u201d \u3000\u3000\u60dc\u6625\u6062\u590d\u4e86\u5e38\u6001\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53ea\u6015\u7559\u4e0d\u4f4f\u71d5\u516c\u5b50\u8fd9\u6837\u7684\u9ad8\u4eba\uff1f\u201d \u3000\u3000\u628a\u8d35\u5ba2\u6539\u79f0\u9ad8\u4eba\uff0c\u5df2\u7136\u8868\u8fbe\u4e86\u5fc3\u4e2d\u7684\u60ca\u6115\u3001\u7591\u8651\u3002 \u3000\u3000\u201c\u597d\u773c\u5149\u2026\u2026\u201d\u91d1\u5929\u7965\u9053\uff1a\u201c\u71d5\u8001\u5f1f\u662f\u7edf\u9886\u5927\u4eba\u4eb2\u81ea\u6311\u9009\u7684\u65b0\u4efb\u4f8d\u536b\uff0c\u4e0d\u662f\u9ad8\u4eba\u5c82\u80fd\u5165 \u9009\u3002\u201d \u3000\u3000\u4e24\u4eba\u5bf9\u7b54\u4e4b\u95f4\uff0c\u8bdd\u91cc\u6709\u8bdd\uff0c\u4ee5\u4f5c\u547c\u5e94\uff0c\u91d1\u5929\u7965\u7b49\u4e8e\u628a\u71d5\u6625\u98ce\u7684\u6765\u5386\u4f5c\u4e86\u4e2a\u7b80\u660e\u7684\u4ecb\u7ecd\u3002 \u3000\u3000\u4f46\u8fd9\u4e5f\u4f7f\u5c0f\u71d5\u5b50\u63d0\u9ad8\u4e86\u8b66\u89c9\uff0c\u91d1\u5929\u7965\u72e1\u8bc8\u591a\u53d8\uff0c\u662f\u4e2a\u4e0d\u6613\u5bf9\u4ed8\u7684\u654c\u4eba\uff0c\u5bf9\u6b64\u4eba\u8981\u591a\u591a\u7559 \u5fc3\u624d\u884c\u3002 \u3000\u3000\u201c\u91d1\u5144\u5938\u5956\u4e86\uff0c\u5c0f\u5f1f\u65e5\u540e\u501f\u91cd\u91d1\u5144\u4e4b\u5904\u6b63\u591a\u5462\uff01\u201d\u71d5\u6625\u98ce\u5ba2\u6c14\u5730\u8bf4\u3002 \u3000\u3000\u201c\u5bf9\uff01\u54b1\u4eec\u8981\u597d\u597d\u7684\u4ea4\u4e00\u4ea4\u2026\u2026\u201d\u91d1\u5929\u7965\u5927\u7b11\u9053\uff1a\u201c\u6625\u59d1\u5a18\uff0c\u6446\u4e00\u5e2d\u4e0a\u597d\u7684\u9152\u83dc\u6765\uff0c\u6211 \u8981\u548c\u71d5\u8001\u5f1f\u559d\u4e2a\u75db\u5feb\u3002\u201d \u3000\u3000\u201c\u5144\u5f1f\u5168\u529b\u5949\u966a\uff01\u201d\u71d5\u6625\u98ce\u867d\u7136\u660e\u77e5\u90a3\u662f\u4e00\u4e2a\u9677\u4e95\uff0c\u5374\u4e5f\u4e0d\u7518\u793a\u5f31\u3002 \u3000\u3000\u60dc\u6625\u56de\u5934\u5429\u5490\u5973\u5a62\u4e00\u58f0\uff0c\u8f6c\u671b\u7740\u71d5\u6625\u98ce\uff0c\u9053\uff1a\u201c\u71d5\u516c\u5b50\u6210\u5bb6\u4e86\u5427\uff1f\u201d \u3000\u3000\u201c\u5f88\u60ed\u6127\uff0c\u71d5\u67d0\u4eba\u751f\u6027\u55dc\u6b66\uff0c\u8fd9\u4e9b\u5e74\u90fd\u628a\u65f6\u95f4\u82b1\u5728\u4e60\u7ec3\u6b66\u529f\u4e4b\u4e0a\uff0c\u4e00\u51fa\u5e08\u95e8\uff0c\u5c31\u88ab\u9a6c\u5de1 \u629a\u63a8\u8350\u5165\u5bab\u3002\u201d\u71d5\u6625\u98ce\u9053\uff1a\u201c\u8fd9\u6210\u5bb6\u5a36\u59bb\u7684\u4e8b\uff0c\u53ea\u597d\u62d6\u5ef6\u4e00\u4e9b\u65f6\u95f4\u4e86\u3002\u201d \u3000\u3000\u201c\u9a6c\u5de1\u629a\uff01\u54ea\u4e2a\u9a6c\u5de1\u629a\uff1f\u201d \u3000\u3000\u201c\u6cb3\u5357\u5de1\u629a\u9a6c\u6587\u4e2d\u3002\u201d \u3000\u3000\u201c\u597d\u5b98\u54ea\uff01\u542c\u8bf4\u4ed6\u6cbb\u7406\u6cb3\u5357\uff0c\u653f\u7b80\u5211\u6e05\uff0c\u5f00\u5c01\u5e9c\u5df2\u662f\u591c\u4e0d\u95ed\u6237\uff0c\u8def\u4e0d\u62fe\u9057\u2026\u2026\u201d\u60dc\u6625 \u9053\uff1a\u201c\u8fd9\u4e9b\u4f20\u95fb\u4e0d\u77e5\u662f\u771f\u662f\u5047\uff1f\u201d \u3000\u3000\u201c\u662f\u771f\u7684\uff01\u201d \u3000\u3000\u9152\u83dc\u4e0a\u7684\u5f88\u5feb\uff0c\u5c31\u8fd9\u4e48\u8c08\u51e0\u53e5\u8bdd\u7684\u5de5\u592b\uff0c\u4f73\u80b4\u5df2\u5ddd\u6d41\u4e0d\u606f\u7684\u9001\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u4f3c\u4e4e\u662f\u65e9\u5df2\u6709\u4e86\u51c6\u5907\u3002 \u3000\u3000\u9152\u5e2d\u95f4\uff0c\u60dc\u6625\u65bd\u5c55\u51fa\u5a07\u5a9a\u7684\u653b\u52bf\uff0c\u4e0d\u505c\u7684\u529d\u71d5\u6625\u98ce\u559d\u9152\uff0c\u5c0f\u71d5\u5b50\u8c6a\u6c14\u5e72\u4e91\uff0c\u6765\u8005\u4e0d\u62d2\uff0c \u9152\u5230\u676f\u5e72\u3002 \u3000\u3000\u91d1\u5929\u7965\u9152\u91cf\u4ea6\u8c6a\uff0c\u4e0d\u505c\u7684\u548c\u71d5\u6625\u98ce\u62fc\u9152\u5e72\u676f\u3002 \u3000\u3000\u5c31\u8fd9\u6837\uff0c\u83dc\u8fd8\u6ca1\u6709\u5b8c\u5168\u4e0a\u5b8c\uff0c\u71d5\u6625\u98ce\u5df2\u9189\u7684\u4eba\u4e8b\u4e0d\u7701\uff0c\u4e00\u8ddf\u5934\u683d\u5012\u5728\u5730\u4e0a\u3002 \u3000\u3000\u91d1\u5929\u7965\u8fde\u63a8\u6570\u6b21\uff0c\u71d5\u6625\u98ce\u604d\u5982\u4e0d\u89c9\u3002 \u3000\u3000\u60dc\u6625\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9152\u91cc\u5df2\u653e\u4e86\u4e09\u65e5\u9189\uff0c\u6ca1\u6709\u4e09\u5929\u4e09\u591c\u7684\u5de5\u592b\uff0c\u7edd\u4e0d\u4f1a\u9192\u8fc7\u6765\u7684\u3002\u201d \u3000\u3000\u91d1\u5929\u7965\u9053\uff1a\u201c\u4eca\u591c\u4e09\u66f4\u4e4b\u524d\uff0c\u4e00\u5b9a\u8981\u5e26\u4ed6\u56de\u53bb\uff0c\u5426\u5219\uff0c\u5fc5\u4f1a\u5f15\u8d77\u738b\u5f64\u7684\u8ffd\u67e5\uff0c\u90a3\u5c31\u4e0d\u597d \u5e94\u4ed8\u4e86\u3002\u201d \u3000\u3000\u201c\u53ea\u7ba1\u653e\u5fc3\uff0c\u4e09\u65e5\u9189\u867d\u7136\u9738\u9053\uff0c\u4f46\u89e3\u836f\u795e\u6548\u65e0\u6bd4\uff0c\u836f\u5230\u9152\u9192\uff0c\u7b49\u4e09\u516c\u4e3b\u82b3\u9a7e\u5230\u6765\uff0c\u662f\u6740 \u662f\u653e\u5c31\u7531\u5979\u51b3\u5b9a\u4e86\u3002\u201d \u3000\u3000\u201c\u4e09\u516c\u4e3b\u4e5f\u8981\u6765\u554a\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u601c\u82b1\u4f20\u6765\u8baf\u606f\u8bf4\uff0c\u8fd9\u5c0f\u5b50\u4f3c\u5fe0\u539a\u53c8\u4f3c\u5978\u8bc8\uff0c\u8fde\u4e09\u516c\u4e3b\u4e5f\u77a7\u4e0d\u51fa\u4e00\u70b9\u7709\u76ee\uff0c\u6240\u4ee5 \u8981\u4eb2\u81ea\u68c0\u67e5\u5ba1\u95ee\u3002\u201d\u60dc\u6625\u9053\uff1a\u201c\u4f60\u80fd\u5e26\u4ed6\u6765\u6b64\uff0c\u4e5f\u7b97\u5927\u529f\u4e00\u4ef6\u5462\uff01\u201d \u3000\u3000\u91d1\u5929\u7965\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u554a\uff01\u6628\u5929\u6211\u8fd8\u4e0d\u5c51\u548c\u4ed6\u591a\u8c08\uff0c\u4eca\u65e9\u5374\u5f97\u5230\u4ee4\u8c15\uff0c\u8981\u6211\u5e26 \u4ed6\u5230\u6b64\uff0c\u800c\u4e14\uff0c\u4e09\u516c\u4e3b\u4e5f\u8981\u4eb2\u81ea\u8d76\u6765\uff0c\u5f53\u771f\u662f\u4e00\u6869\u5927\u5927\u7684\u610f\u5916\u4e8b\u4ef6\uff0c\u8fd9\u5c0f\u5b50\u5341\u5206\u5e74\u8f7b\uff0c\u96be\u9053 \u771f\u6709\u4ec0\u4e48\u8fc7\u4eba\u7684\u672c\u9886\u4e0d\u6210\uff1f\u201d \u3000\u3000\u201c\u91d1\u5929\u7965\uff0c\u6211\u5982\u4e0d\u4e0b\u4e09\u65e5\u9189\uff0c\u4f60\u548c\u4ed6\u786c\u884c\u62fc\u9152\uff0c\u662f\u4e0d\u662f\u80fd\u62fc\u5f97\u8fc7\u4ed6\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u5f88\u96be\u8bf4\u4e86\uff01\u201d\u91d1\u5929\u7965\u770b\u4e86\u60dc\u6625\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4e5f\u8bb8\u5728\u4e0b\u4e0d\u662f\u654c\u624b\uff1f\u201d \u3000\u3000\u201c\u4f60\u6839\u672c\u5c31\u559d\u4e0d\u8fc7\u4ed6\u2026\u2026\u201d\u60dc\u6625\u7ad9\u8d77\u8eab\u4e8e\uff0c\u9053\uff1a\u201c\u8d70\uff01\u628a\u4ed6\u67b6\u4eba\u5211\u5ba4\uff0c\u5148\u4e0a\u5211\u5177\uff0c\u4e09\u516c \u4e3b\u968f\u65f6\u5c31\u4f1a\u8d76\u6765\u3002\u201d \u3000\u3000\u91d1\u5929\u7965\u6b63\u60f3\u53eb\u4eba\uff0c\u60dc\u6625\u5df2\u4f0f\u8eab\u62b1\u8d77\u4e86\u71d5\u6625\u98ce\uff0c\u5f53\u5148\u884c\u53bb\u3002 \u3000\u3000\u770b\u4e0a\u53bb\u7626\u7626\u5f31\u5f31\u7684\u60dc\u6625\uff0c\u62b1\u8d77\u71d5\u6625\u98ce\u90a3\u4e48\u4fee\u4f1f\u7684\u8eaf\u4f53\uff0c\u7adf\u4f3c\u6beb\u4e0d\u5403\u529b\uff0c\u6b65\u5c65\u5982\u98de\u3002 \u3000\u3000\u71d5\u6625\u98ce\u6e05\u9192\u8fc7\u6765\uff0c\u53d1\u89c9\u5168\u8eab\u8863\u670d\u90fd\u88ab\u8131\u53bb\uff0c\u53ea\u7559\u4e86\u4e00\u6761\u77ed\u88e4\uff0c\u53cc\u624b\u53cc\u8db3\u90fd\u88ab\u7c97\u903e\u62c7\u6307\u5927 \u7684\u94c1\u5708\uff0c\u6263\u5728\u7279\u5236\u7684\u94c1\u67b6\u4e0a\u3002 \u3000\u3000\u4e00\u5927\u95f4\u9752\u77f3\u780c\u6210\u7684\u5c4b\uff0c\u56db\u5468\u4e0d\u89c1\u5929\u65e5\uff0c\u5ba4\u4e2d\u71c3\u7740\u4e24\u652f\u7ad6\u8d77\u7684\u706b\u628a\uff0c\u7167\u5f97\u6ee1\u5ba4\u901a\u660e\u3002 \u3000\u3000\u4e00\u4e2a\u6c34\u7eff\u8863\u88d9\u7684\u5c11\u5973\uff0c\u7aef\u5750\u5728\u4e00\u5f20\u592a\u5e08\u6905\u4e0a\uff0c\u4e24\u4fa7\u7ad9\u7740\u60dc\u6625\u548c\u601c\u82b1\u3002 \u3000\u3000\u7eff\u8863\u5c11\u5973\u8138\u4e0a\u6234\u4e86\u4e00\u526f\u9752\u94dc\u9762\u5177\uff0c\u4f3c\u662f\u4e0d\u613f\u8ba9\u5c0f\u71d5\u5b50\u77a7\u5230\u672c\u6765\u9762\u76ee\u3002 \u3000\u3000\u201c\u601c\u82b1\u59d0\u59d0\uff0c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\u554a\uff1f\u201d \u3000\u3000\u201c\u71d5\u8001\u5f1f\uff0c\u597d\u6c49\u4e0d\u5403\u773c\u524d\u4e8f\uff0c\u8bc6\u65f6\u52a1\u8005\u4e3a\u4fca\u6770\uff0c\u5b9e\u8bdd\u5b9e\u8bf4\uff0c\u53ef\u4ee5\u5c11\u5403\u4e00\u70b9\u82e6\u5934\u3002\u201d \u3000\u3000\u8f6c\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u91d1\u5929\u7965\u7ad9\u5728\u5de6\u4fa7\uff0c\u624b\u4e2d\u63d0\u7740\u4e00\u6761\u76ae\u97ad\u5b50\u3002 \u3000\u3000\u201c\u91d1\u5144\uff0c\u4f60\u600e\u4e48\u53d8\u6210\u884c\u5211\u624b\u4e86\uff1f\u201d\u71d5\u6625\u98ce\u7b11\u9053\uff1a\u201c\u4f60\u662f\u4e09\u7b49\u4f8d\u536b\u7684\u8eab\u4efd\u554a\uff01\u201d \u3000\u3000\u771f\u662f\u4e0d\u77e5\u5929\u9ad8\u5730\u539a\u7684\u6123\u5c0f\u5b50\uff0c\u4ec0\u4e48\u8fb0\u5149\u4e86\uff0c\u4ed6\u8fd8\u80fd\u7b11\u5f97\u51fa\u6765\uff1f \u3000\u3000\u201c\u71d5\u6625\u98ce\u2026\u2026\u201d\u7aef\u5750\u5728\u592a\u5e08\u6905\u4e0a\u7684\u7eff\u8863\u5c11\u5973\u5f00\u4e86\u53e3\uff0c\u9053\uff1a\u201c\u8001\u5b9e\u56de\u7b54\u6211\u7684\u95ee\u8bdd\uff0c\u514d\u5f97\u76ae \u8089\u53d7\u82e6\uff0c\u6211\u77e5\u9053\u4f60\u7ec3\u4e86\u4e00\u8eab\u597d\u529f\u592b\uff0c\u4e5f\u8bb8\u4e0d\u5728\u4e4e\u97ad\u5b50\u7684\u62bd\u6253\uff0c\u4f46\u5206\u7b4b\u9519\u9aa8\u5374\u53ef\u4ee5\u4f7f\u4e00\u6761\u94c1\u94ee \u94ee\u7684\u6c49\u5b50\uff0c\u53d8\u5f97\u8f6f\u5f31\u6c42\u9976\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u662f\u4e09\u516c\u4e3b\u4e86\uff01\u201d\u71d5\u6625\u98ce\u7b11\u9053\uff1a\u201c\u89c1\u4f60\u4e00\u9762\u53ef\u771f\u662f\u4e0d\u5bb9\u6613\u554a\uff0c\u5f04\u5f97\u6211\u5982\u6b64\u72fc\u72c8\u4e0d\u582a \u554a\uff1f\u201d \u3000\u3000\u201c\u4f4f\u53e3\uff01\u201d\u60dc\u6625\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u771f\u662f\u4e0d\u89c1\u68fa\u6750\u4e0d\u6389\u6cea\uff0c\u91d1\u5929\u7965\uff0c\u5148\u62bd\u4ed6\u4e8c\u5341\u97ad\u5b50\u3002\u201d \u3000\u3000\u5750\u7740\u7684\u7eff\u8863\u5c11\u5973\u6ca1\u6709\u963b\u6b62\uff0c\u91d1\u5929\u7965\u626c\u8d77\u4e86\u624b\u4e2d\u7684\u76ae\u97ad\u3002 \u3000\u3000\u201c\u5c0f\u71d5\u5b50\u2014\u2026\u3002\u201d\u601c\u82b1\u5f00\u4e86\u53e3\uff0c\u9053\uff1a\u201c\u4f60\u71ac\u4e0d\u8fc7\u7684\uff0c\u5f04\u5f97\u6b7b\u53bb\u6d3b\u6765\uff0c\u518d\u62db\u51fa\u6765\uff0c\u5c82\u4e0d \u662f\u2026\u2026\u201d \u3000\u3000\u201c\u601c\u82b1\u59d0\u59d0\u2026\u2026\u201d\u5c0f\u71d5\u5b50\u795e\u60c5\u8f7b\u677e\uff0c\u4e00\u53e3\u4e00\u4e2a\u601c\u82b1\u59d0\u59d0\u53eb\u5f97\u53c8\u54cd\u53c8\u751c\u3002 \u3000\u3000\u6b63\u662f\u8f6f\u786c\u517c\u65bd\uff0c\u5404\u5c55\u6240\u957f\u3002 \u3000\u3000\u201c\u5509\uff01\u597d\u5427\uff0c\u4f60\u8981\u6211\u8bf4\u4ec0\u4e48\u5462\uff1f\u201d\u5c0f\u71d5\u5b50\u7eaf\u7eaf\u7684\u8bed\u6c14\uff0c\u60f9\u4eba\u601c\u60dc\u3002 \u3000\u3000\u201c\u95ee\u4f60\u4ec0\u4e48\uff0c\u4f60\u5c31\u56de\u7b54\u4ec0\u4e48\uff1f\u201d\u7eff\u8863\u5c11\u5973\u9053\uff1a\u201c\u6211\u4e0d\u60f3\u4f24\u5bb3\u4f60\uff0c\u4e0d\u8981\u903c\u6211\u3002\u201d \u3000\u3000\u9752\u94dc\u9762\u5177\u4e4b\u540e\uff0c\u900f\u51fa\u4e86\u4e24\u9053\u76ee\u5149\uff0c\u626b\u63a0\u8fc7\u5c0f\u71d5\u5b50\u4fee\u4f1f\u5065\u58ee\u7684\u8eab\u8eaf\u3002 \u3000\u3000\u201c\u597d\uff0c\u77e5\u9053\u7684\u6211\u90fd\u8bf4\uff0c\u4e0d\u8fc7\uff0c\u6211\u4e5f\u6709\u6761\u4ef6\u2026\u2026\u201d \u3000\u3000\u201c\u6761\u4ef6\uff1f\u2026\u2026\u201d\u7eff\u8863\u5c11\u5973\u6709\u4e9b\u8bb6\u7136\uff1a\u201c\u73b0\u5728\uff0c\u4f60\u8fd8\u6709\u6761\u4ef6\uff1f\u201d \u3000\u3000\u201c\u662f\u554a\uff01\u6761\u4ef6\u5f88\u7b80\u5355\uff0c\u4e0d\u8fc7\u662f\u4e3e\u624b\u4e4b\u52b3\u800c\u5df2\u3002\u201d\u5c0f\u71d5\u5b50\u7684\u8bed\u6c14\u5341\u5206\u5e73\u9759\uff0c\u4f3c\u662f\u4e00\u70b9\u4e5f\u6ca1 \u6709\u60ca\u6015\u7684\u611f\u89c9\u3002 \u3000\u3000\u201c\u597d\uff0c\u4f60\u8bf4\u51fa\u6765\u7ed9\u6211\u542c\u542c\uff01\u201d\u7eff\u8863\u5c11\u5973\u5927\u611f\u597d\u5947\u3002 \u3000\u3000\u201c\u53d6\u4e0b\u4f60\u7684\u9752\u94dc\u9762\u5177\u3002\u201d\u71d5\u6625\u98ce\u9053\uff1a\u201c\u6211\u53ea\u8981\u770b\u4f60\u4e00\u773c\u3002\u201d \u3000\u3000\u201c\u653e\u8086\uff01\u201d\u91d1\u5929\u7965\u53f3\u624b\u6325\u52a8\uff0c\u5939\u7740\u5578\u98ce\u7684\u76ae\u97ad\uff0c\u72e0\u72e0\u7684\u62bd\u5728\u5c0f\u71d5\u5b50\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u5065\u58ee\u7684\u8eaf\u4f53\u4e0a\uff0c\u7acb\u523b\u6cdb\u8d77\u4e86\u4e00\u9053\u7ea2\u8272\u7684\u97ad\u75d5\u3002 \u3000\u3000\u201c\u91d1\u5144\uff0c\u54b1\u4eec\u521a\u521a\u4ea4\u4e86\u670b\u53cb\uff0c\u4f60\u600e\u4e48\u5982\u6b64\u5fc3\u72e0\u624b\u8fa3\uff0c\u6253\u5f97\u8fd9\u4e48\u7528\u529b\u2026\u2026\u201d\u5c0f\u71d5\u5b50\u53f9\u53e3 \u6c14\uff0c\u9053\uff1a\u201c\u771f\u662f\u9152\u8089\u670b\u53cb\uff0c\u4e0d\u8db3\u4ea4\u5f80\uff0c\u6211\u8981\u548c\u4f60\u5212\u5730\u7edd\u4ea4\u3002\u201d \u3000\u3000\u91d1\u5929\u7965\u9053\uff1a\u201c\u597d\uff0c\u90a3\u6211\u5c31\u518d\u62bd\u4f60\u51e0\u97ad\u5b50\uff01\u201d \u3000\u3000\u53f3\u624b\u75be\u6325\uff0c\u8fde\u62bd\u4e09\u97ad\u5b50\u3002 \u3000\u3000\u201c\u601c\u82b1\u59d0\u59d0\uff0c\u6253\u5f97\u6211\u597d\u75bc\u554a\uff01\u2026\u2026\u201d\u5c0f\u71d5\u5b50\u7a76\u7adf\u662f\u4e2a\u5b69\u5b50\uff0c\u7a1a\u6c14\u5c1a\u672a\u5168\u8131\uff0c\u6328\u4e86\u51e0\u97ad \u5b50\uff0c\u59d0\u59d0\u5c31\u53eb\u5f97\u66f4\u4eb2\u70ed\u4e86\u3002 \u3000\u3000\u601c\u82b1\u88ab\u53eb\u5f97\u5fc3\u795e\u5927\u4e71\uff0c\u8fd8\u771f\u6709\u70b9\u5fc3\u75bc\u8d77\u6765\uff0c\u4f46\u516c\u4e3b\u5728\u4fa7\uff0c\u60dc\u6625\u53c8\u864e\u89c6\u7708\u7708\uff0c\u7adf\u7136\u4e0d\u6562\u5f00 \u53e3\u4e3a\u5c0f\u71d5\u5b50\u6c42\u60c5\u3002 \u3000\u3000\u5e78\u597d\u7eff\u8863\u5c11\u5973\u4e00\u6325\u624b\uff0c\u963b\u6b62\u4e86\u91d1\u5929\u7965\u518d\u6253\u4e0b\u53bb\uff0c\u9053\uff1a\u201c\u5c0f\u71d5\u5b50\uff0c\u4f60\u770b\u6211\u4e00\u773c\u771f\u7684\u5c31\u80af\u56de \u7b54\u6211\u95ee\u7684\u95ee\u9898\uff1f\u201d \u3000\u3000\u201c\u662f\u554a\uff01\u201d\u71d5\u6625\u98ce\u7b11\u9053\uff1a\u201c\u6211\u8bf4\u8fc7\u7684\u8bdd\uff0c\u5f53\u7136\u7b97\u6570\u3002\u201d \u3000\u3000\u201c\u597d\u5427\uff01\u201d\u7eff\u8863\u5c11\u5973\u7f13\u7f13\u7684\u62ff\u5f00\u4e86\u9752\u94dc\u9762\u5177\u3002 \u3000\u3000\u201c\u679c\u7136\u662f\u4e00\u4f4d\u7edd\u4ee3\u4e3d\u4eba\u2026\u2026\u201d\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u52ff\u602a\u4f60\u4e0d\u613f\u522b\u4eba\u770b\u5230\u4f60\u3002\u201d \u3000\u3000\u201c\u4f60\u5df2\u7ecf\u770b\u8fc7\u4e86\u2026\u2026\u201d\u7eff\u8863\u5c11\u5973\u9053\uff1a\u201c\u73b0\u5728\u53ef\u4ee5\u56de\u7b54\u6211\u7684\u95ee\u9898\u4e86\u5427\uff01\u201d \u3000\u3000\u201c\u884c\uff01\u518d\u544a\u8bc9\u6211\u4e00\u4ef6\u4e8b\uff0c\u4f60\u662f\u4e0d\u662f\u4e09\u516c\u4e3b\uff1f\u201d \u3000\u3000\u7eff\u8863\u5c11\u5973\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u662f\u516c\u4e3b\u53c8\u600e\u6837\uff1f\u4e0d\u662f\u53c8\u5982\u4f55\uff1f\u4f60\u5f88\u91cd\u89c6\u6211\u7684\u8eab\u4efd\u4e48\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u4e86\uff0c\u4e09\u516c\u4e3b\u91d1\u679d\u7389\u53f6\uff0c\u6211\u8981\u5c3d\u6211\u4e4b\u80fd\u4fdd\u62a4\u5979\u2026\u2026\u201d\u71d5\u6625\u98ce\u9053\uff1a\u201c\u4f60\u5982\u4e0d\u662f\u4e09\u516c \u4e3b\uff0c\u90a3\u5c31\u7528\u4e0d\u7740\u6211\u8d39\u5fc3\u4e86\u3002\u201d \u3000\u3000\u7eff\u8863\u5c11\u5973\u7b11\u9053\uff1a\u201c\u73b0\u5728\u8fd9\u4e2a\u5c40\u9762\uff0c\u4f60\u8fde\u81ea\u5df1\u7684\u6027\u547d\u90fd\u65e0\u6cd5\u4fdd\u62a4\u4e86\uff0c\u8fd8\u8981\u4fdd\u62a4\u6211\u4e48\uff1f\u201d \u3000\u3000\u201c\u597d\u6781\u4e86\uff0c\u8fd9\u4e48\u8bf4\uff0c\u4f60\u5c31\u662f\u4e09\u516c\u4e3b\u4e86\uff1f\u201d \u3000\u3000\u201c\u662f\uff0c\u5c0f\u71d5\u5b50\uff0c\u73b0\u5728\u8be5\u6211\u95ee\u4f60\u4e86\u3002\u201d \u3000\u3000\u71d5\u6625\u98ce\u7a81\u7136\u5927\u559d\u4e00\u58f0\uff0c\u58f0\u5982\u9f99\u541f\uff0c\u9707\u5f97\u4eba\u8033\u819c\u4f5c\u54cd\uff0c\u58f0\u97f3\u5e73\u606f\uff0c\u5c0f\u71d5\u5b50\u5df2\u5b8c\u5168\u6062\u590d\u4e86\u81ea \u7531\uff0c\u9501\u4f4f\u4ed6\u53cc\u624b\u53cc\u811a\u7684\u94c1\u73af\uff0c\u7adf\u7136\u88c2\u6210\u4e86\u4e24\u534a\u3002 \u3000\u3000\u201c\u601c\u82b1\u59d0\u59d0\uff0c\u628a\u8863\u670d\u62ff\u7ed9\u6211\u5427\uff0c\u8fd9\u6837\u8d64\u8eab\u9732\u4f53\uff0c\u5982\u4f55\u80fd\u62dc\u89c1\u4e09\u516c\u4e3b\u5462\uff1f\u201d \u3000\u3000\u8fd9\u7a81\u5982\u5176\u6765\u7684\u53d8\u5316\uff0c\u5168\u573a\u7684\u4eba\u5168\u90fd\u6014\u4f4f\u4e86\uff0c\u671b\u7740\u754f\u7f29\u5728\u4e00\u4fa7\u7684\u5c0f\u71d5\u5b50\uff0c\u5446\u5446\u51fa\u795e\u3002 \u3000\u3000\u60dc\u6625\u9053\uff1a\u201c\u6211\u62ff\u8863\u670d\u7ed9\u4f60\u2026\u2026\u201d\u679c\u7136\u62ff\u8d77\u8863\u670d\uff0c\u884c\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8ddd\u79bb\u8fd8\u6709\u4e09\u5c3a\uff0c\u60dc\u6625\u5df2\u5c06\u8863\u670d\u629b\u4e86\u8fc7\u53bb\uff0c\u4eba\u4e5f\u7d27\u968f\u7740\u6b3a\u8eab\u800c\u4e0a\uff0c\u653b\u51fa\u4e00\u638c\uff0c\u70b9\u51fa\u4e86\u4e00 \u6307\u3002 \u3000\u3000\u5728\u8863\u670d\u63a9\u906e\u4e4b\u4e0b\uff0c\u8fd9\u4e00\u638c\u4e00\u6307\uff0c\u770b\u4e0a\u53bb\u7279\u522b\u7684\u8be1\u5f02\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u6025\u6025\u53eb\u9053\uff1a\u201c\u8ba9\u6211\u628a\u8863\u670d\u7a7f\u4e0a\u518d\u6253\u561b\uff01\u201d\u53f3\u624b\u6325\u51fa\u4e86\u4e00\u638c\uff0c\u6b63\u8fce\u4e0a\u4e86\u60dc\u6625\u7684\u638c \u52bf\uff0c\u53cc\u638c\u63a5\u5b9e\u3002 \u3000\u3000\u4e00\u80a1\u5f3a\u5927\u7684\u529b\u9053\uff0c\u7531\u5c0f\u71d5\u5b50\u638c\u4e0a\u6d8c\u51fa\uff0c\u60dc\u6625\u88ab\u9707\u5f97\u8fde\u8fde\u9000\u4e86\u4e09\u6b65\uff0c\u8fde\u70b9\u51fa\u7684\u4e00\u6307\u4e5f\u65e0\u6cd5 \u51fb\u4e2d\u5c0f\u71d5\u5b50\u3002 \u3000\u3000\u60dc\u6625\u672c\u6b32\u518d\u51b2\u4e0a\u53bb\uff0c\u65bd\u5c55\u7b2c\u4e8c\u6ce2\u7684\u653b\u52bf\uff0c\u4f46\u4e00\u65f6\u4e4b\u95f4\uff0c\u7adf\u7136\u65e0\u6cd5\u63d0\u805a\u771f\u6c14\uff0c\u4e0d\u7981\u5927\u5403\u4e00 \u60ca\uff0c\u5fd6\u9053\uff1a\u8fd9\u662f\u4ec0\u4e48\u529f\u592b\uff0c\u600e\u4f1a\u5982\u6b64\u795e\u5947\uff1f \u3000\u3000\u7eff\u8863\u5c11\u5973\u5750\u7740\u672a\u52a8\uff0c\u91d1\u5929\u7965\u65e9\u5df2\u88ab\u9707\u4f4f\u4e86\uff0c\u60dc\u6625\u6682\u5931\u6218\u529b\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u5f88\u5feb\u7684\u5c31\u7a7f\u597d\u4e86\u8863\u670d\u3002 \u3000\u3000\u7eff\u8863\u5c11\u5973\u7f13\u7f13\u7ad9\u8d77\uff0c\u62bd\u51fa\u601c\u82b1\u80cc\u4e0a\u7684\u5b9d\u5251\uff0c\u9053\uff1a\u201c\u5c0f\u71d5\u5b50\uff0c\u4f60\u679c\u7136\u662f\u4e00\u4f4d\u975e\u5e38\u4eba\u7269\uff01\u201d \u3000\u3000\u201c\u516c\u4e3b\u522b\u5fd9\uff0c\u5728\u4e0b\u7cfb\u597d\u8170\u5e26\u5c31\u53bb\u62dc\u89c1\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7684\u4e00\u8138\u8ba4\u771f\uff0c\u53eb\u4eba\u96be\u5206\u771f\u5047\uff1f \u3000\u3000\u201c\u6211\u8be5\u5728\u4ed6\u9152\u9189\u672a\u9192\u65f6\uff0c\u5148\u6740\u4e86\u4ed6\u2026\u2026\u201d\u60dc\u6625\u6068\u58f0\u5730\u8bf4\u3002 \u3000\u3000\u201c\u60dc\u6625\uff0c\u4f60\u8ba4\u4e3a\u4e09\u65e5\u9189\u771f\u7684\u8ff7\u5012\u6211\u4e86\u4e48\uff1f\u2026\u2026\u2026\u201d\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u6211\u4e0d\u8fc7\u60f3\u89c1\u89c1\u4e09\u516c\u4e3b\u7f62 \u4e86\u3002\u201d \u3000\u3000\u201c\u80e1\u8bf4\uff01\u4e09\u65e5\u9189\u867d\u4e0d\u662f\u836f\u7269\uff0c\u4f46\u5374\u9738\u9053\u65e0\u6bd4\uff0c\u6ca1\u6709\u4eba\u559d\u4e0b\u5b83\u800c\u4e0d\u9189\u5012\u7684\u3002\u201d \u3000\u3000\u201c\u662f\u554a\uff01\u5982\u679c\u6ca1\u6709\u559d\u4e0b\u53bb\u5462\uff1f\u201d \u3000\u3000\u201c\u4e0d\u53ef\u80fd\u554a\uff01\u201d\u60dc\u6625\u9053\uff1a\u201c\u6211\u770b\u7740\u4f60\u559d\u4e0b\u53bb\u7684\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u597d\u5427\uff0c\u6211\u4eec\u7528\u4e0d\u7740\u62ac\u6760\uff0c\u4f60\u544a\u8bc9\u91d1\u5929\u7965\u8bf4\u4e09\u516c\u4e3b\u8981\u6765\u8fd9\u91cc\u2026\u2026\u201d \u3000\u3000\u60dc\u6625\u6014\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u771f\u7684\u6ca1\u6709\u9189\u5012\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u5fae\u5fae\u4e00\u7b11\uff0c\u4e0d\u518d\u7406\u4f1a\u60dc\u6625\uff0c\u5374\u884c\u5411\u7eff\u8863\u5c11\u5973\uff0c\u9053\uff1a\u201c\u71d5\u6625\u98ce\u62dc\u89c1\u516c\u4e3b\u3002\u201d \u3000\u3000\u606d\u606d\u656c\u656c\u7684\u884c\u4e86\u4e00\u63d6\uff0c\u8dea\u62dc\u4e0b\u53bb\u3002 \u3000\u3000\u2019\u4e09\u516c\u4e3b\u4e5f\u6123\u4f4f\u4e86\uff0c\u8fd9\u4e2a\u5c0f\u71d5\u5b50\u4f5c\u4e8b\uff0c\u5904\u5904\u51fa\u4eba\u610f\u6599\u4e4b\u5916\uff0c\u624b\u4e2d\u63d0\u7740\u5b9d\u5251\uff0c\u7adf\u7136\u65e0\u6cd5\u523a \u51fa\u3002 \u3000\u3000\u71d5\u6625\u98ce\u62dc\u7f62\u8d77\u8eab\uff0c\u7b11\u9053\uff1a\u201c\u516c\u4e3b\uff0c\u60dc\u6625\u662f\u897f\u65b9\u9b54\u6559\u7684\u5f1f\u5b50\uff0c\u8981\u4e0d\u8981\u628a\u5979\u9664\u53bb\uff1f\u201d \u3000\u3000\u201c\u516c\u4e3b\uff0c\u8fd9\u4e2a\u4eba\u4e0d\u80fd\u7559\u4e0b\uff0c\u54b1\u4eec\u5408\u529b\u6740\u4e86\u4ed6\u3002\u201d\u60dc\u6625\u8bf4\u3002 \u3000\u3000\u7eff\u8863\u5c11\u5973\u5e94\u58f0\u51fa\u624b\uff0c\u4e00\u5251\u523a\u5411\u71d5\u6625\u98ce\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u7a81\u7136\u4e00\u4e2a\u8f6c\u8eab\uff0c\u907f\u5f00\u4e86\u7eff\u8863\u5c11\u5973\u7684\u5251\u52bf\uff0c\u4eba\u5374\u6b3a\u5230\u60dc\u6625\u7684\u8eab\u4fa7\uff0c\u5288\u51fa\u4e86\u4e00\u638c\u3002 \u3000\u3000\u60dc\u6625\u5a07\u53f1\u4e00\u58f0\uff0c\u53f3\u624b\u70b9\u51fa\uff0c\u6307\u5411\u71d5\u6625\u98ce\u7684\u8109\u7a74\u3002 \u3000\u3000\u4e0d\u6599\uff0c\u5c0f\u71d5\u5b50\u53f3\u624b\u4e00\u8f6c\uff0c\u7a81\u7136\u5316\u4e3a\u64d2\u62ff\uff0c\u4e00\u628a\u6263\u4f4f\u4e86\u60dc\u6625\u7684\u53f3\u8155\u8109\u7a74\u3002 \u3000\u3000\u8fd9\u771f\u662f\u68cb\u5dee\u4e00\u62db\uff0c\u6ee1\u76d8\u7686\u8f93\u3002\u5c0f\u71d5\u5b50\u6b66\u529f\u4e4b\u9ad8\uff0c\u53d8\u5316\u4e4b\u5947\uff0c\u5c31\u50cf\u662f\u60dc\u6625\u628a\u53f3\u8155\u9001\u5165\u4e86\u5c0f \u71d5\u5b50\u7684\u624b\u4e2d\u4e00\u822c\u3002 \u3000\u3000\u201c\u60dc\u6625\uff0c\u4f60\u867d\u7136\u51fa\u8eab\u9b54\u6559\uff0c\u4f46\u6211\u8fd8\u662f\u4e0d\u60f3\u7528\u6b8b\u9177\u7684\u624b\u6cd5\u60e9\u6cbb\u4f60\uff0c\u4e0d\u8fc7\uff0c\u4f60\u8981\u89e3\u53bb\u4e09\u516c\u4e3b \u8eab\u4e0a\u7684\u7981\u5236\uff0c\u6062\u590d\u5979\u81ea\u7531\u4e4b\u8eab\u3002\u201d \u3000\u3000\u201c\u6211\u4e0d\u80fd\u2026\u2026\u201d\u60dc\u6625\u9053\uff1a\u201c\u6211\u4e5f\u6ca1\u6709\u8fd9\u4e2a\u6cd5\u529b\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u539f\u672c\u53ea\u662f\u4e2a\u63a8\u60f3\uff0c\u4e09\u516c\u4e3b\u53ef\u80fd\u53d7\u5230\u4e86\u4ec0\u4e48\u7981\u5236\uff0c\u60f3\u4e0d\u5230\u51fa\u8a00\u4e00\u552c\uff0c\u7adf\u7136\u662f\u6b6a\u6253\u6b63 \u7740\uff0c\u4e00\u4e0b\u5b50\u5c31\u627e\u51fa\u4e86\u75c5\u6e90\u3002 \u3000\u3000\u201c\u597d\uff01\u2026\u2026\u201d\u71d5\u6625\u98ce\u9053\uff1a\u201c\u90a3\u4f60\u8bf4\uff0c\u8c01\u80fd\u89e3\u53bb\u4e09\u516c\u4e3b\u8eab\u4e0a\u7684\u7981\u5236\uff1f\u201d \u3000\u3000\u4f46\u95fb\u6025\u4fc3\u7684\u6b65\u5c65\u58f0\u4f20\u4e86\u8fc7\u6765\uff0c\u5173\u7740\u7684\u6728\u95e8\u4e5f\u88ab\u649e\u4e86\u5f00\u6765\uff0c\u738b\u5f64\u5e26\u7740\u71d5\u98de\u3001\u9648\u5b8f\u3001\u8d75\u4fdd\uff0c \u9c7c\u8d2f\u800c\u5165\u3002 \u3000\u3000\u91d1\u5929\u7965\u4e00\u770b\u51fa\u8def\u88ab\u963b\uff0c\u65e0\u6cd5\u9003\u5f97\u51fa\u53bb\u4e86\uff0c\u7acb\u523b\u79fb\u52a8\u8eab\u8eaf\uff0c\u8eb2\u5230\u4e09\u516c\u4e3b\u7684\u8eab\u540e\u53bb\u3002 \u3000\u3000\u201c\u597d\u554a\uff01\u60dc\u6625\uff0c\u4f60\u7adf\u7136\u65e9\u6f5c\u5165\u4eac\u57ce\u4e2d\u4e86\u2026\u2026\u201d\u738b\u5f64\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u5165\u5bab\u4e2d\u4f8d\u5019\u516c\u4e3b \u5462\uff1f\u201d \u3000\u3000\u201c\u516c\u4e3b\u88ab\u4ed6\u4eec\u4e0b\u4e86\u7981\u5236\uff0c\u4e0d\u5f97\u4e0d\u542c\u4ed6\u4eec\u6446\u5e03\uff0c\u8fd9\u4e2a\u5c0f\u5996\u5973\u624d\u662f\u4e3b\u6301\u5927\u5c40\u7684\u4eba\u7269\u3002\u201d\u71d5\u6625 \u98ce\u8bf4\u5b8c\u8bdd\uff0c\u51fa\u624b\u8fde\u70b9\u4e86\u60dc\u6625\u4e09\u5904\u8981\u7a74\uff0c\u4ea4\u7ed9\u4e86\u9648\u5b8f\u3002 \u3000\u3000\u71d5\u98de\u773c\u770b\u7231\u513f\u4e00\u51fa\u9a6c\uff0c\u5c31\u76f4\u6363\u654c\u4eba\u7684\u5de2\u7a74\uff0c\u627e\u51fa\u4e86\u60dc\u6625\uff0c\u800c\u4e14\u4e5f\u672a\u548c\u4e09\u516c\u4e3b\u9020\u6210\u706b\u62fc\u7684 \u5c40\u9762\uff0c\u5fc3\u91cc\u90a3\u4efd\u9ad8\u5174\uff0c\u7b80\u76f4\u662f\u65e0\u6cd5\u5f62\u5bb9\uff0c\u53ea\u662f\u60c5\u51b5\u8fd8\u672a\u5b8c\u5168\u660e\u6717\uff0c\u4e5f\u4e0d\u6562\u7236\u5b50\u76f8\u8bc6\u3002 \u3000\u3000\u201c\u5c0f\u71d5\u5b50\uff0c\u771f\u662f\u5947\u8ff9\u554a\uff01\u6211\u4eec\u56f0\u6270\u4e86\u51e0\u4e2a\u6708\uff0c\u675f\u624b\u65e0\u7b56\uff0c\u6478\u4e0d\u7740\u5934\u8111\u7684\u95ee\u9898\uff0c\u4f60\u4e24\u4e09\u5929 \u5c31\u628a\u5b83\u89e3\u51b3\u4e86\uff0c\u771f\u662f\u957f\u6c5f\u540e\u6d6a\u63a8\u524d\u6d6a\uff0c\u4e00\u4ee3\u65b0\u4eba\u80dc\u65e7\u4eba\u3002\u201d\u738b\u5f64\u65e0\u9650\u7684\u611f\u6168\u3002 \u3000\u3000\u201c\u7edf\u9886\uff0c\u771f\u6b63\u9ebb\u70e6\u7684\u4e8b\uff0c\u662f\u8981\u5982\u4f55\u66ff\u4e09\u516c\u4e3b\u89e3\u53bb\u7981\u5236\uff1f\u6211\u76f8\u4fe1\u60dc\u6625\u4e00\u5b9a\u77e5\u9053\u5185\u60c5\u2026\u2026\u201d \u3000\u3000\u71d5\u6625\u98ce\u9053\uff1a\u201c\u53ea\u662f\u5c0f\u5996\u5973\u4e0d\u80af\u62db\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u5148\u9001\u516c\u4e3b\u56de\u5bab\u53bb\uff0c\u518d\u5904\u7f6e\u60dc\u6625\u8fd9\u4e2a\u4e2b\u5934\u3002\u201d\u738b\u5f64\u9053\uff1a\u201c\u6211\u4e0d\u4fe1\u9b54\u6559\u4e2d\u4eba\u4e2a\u4e2a\u90fd\u662f\u94c1\u6253 \u7684\u4eba\uff0c\u5c31\u7b97\u662f\u5427\uff0c\u6211\u4e5f\u8981\u4ee5\u7259\u8fd8\u7259\uff0c\u8981\u5979\u5316\u6210\u7ed5\u6307\u67d4\u3002\u201d \u3000\u3000\u201c\u91d1\u5929\u7965\u8fd9\u4e2a\u4eba\u9634\u72e0\u6bd2\u8fa3\uff0c\u53e3\u662f\u5fc3\u975e\uff0c\u7edd\u5bf9\u4e0d\u80fd\u7559\u4e0b\u6765\uff0c\u6211\u7684\u4e8b\u5df2\u7ecf\u529e\u5b8c\uff0c\u544a\u8f9e\u4e86\u3002\u201d \u3000\u3000\u8f6c\u8eab\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u71d5\u98de\u5fc3\u5934\u5927\u6025\uff0c\u5dee\u4e00\u70b9\u628a\u513f\u5b50\u558a\u51fa\u53e3\u6765\u3002 \u3000\u3000\u5e78\u597d\u6709\u4e2a\u4eba\u6bd4\u4ed6\u66f4\u6025\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5c0f\u71d5\u5b50\uff0c\u4f60\u4e0d\u80fd\u8d70\uff01\u201d \u3000\u3000\u662f\u4e09\u516c\u4e3b\uff0c\u53eb\u58f0\u7684\u614c\u6025\uff0c\u663e\u7136\u662f\u5df2\u65e0\u6cd5\u63a7\u5236\u5fc3\u4e2d\u7684\u7126\u8651\u3002 \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u4e8b\u90fd\u529e\u59a5\u4e86\uff0c\u6211\u8981\u56de\u5bb6\u53bb\uff0c\u770b\u770b\u6211\u5988\u2026\u2026\uff02\u201d\u770b\u4f60\u5988 \u5988\u7684\u4e8b\uff0c\u53ea\u6015\u8981\u518d\u7b49\u7b49\u4e86\u3002\u201c\u4e09\u516c\u4e3b\u9053\uff1a\u201d\u4f60\u8ba4\u4e3a\u4f60\u5df2\u7ecf\u529e\u59a5\u6240\u6709\u7684\u4e8b\u4e86\u4e48\uff1f\u544a\u8bc9\u4f60\uff0c\u8fd9\u91cc \u7684\u4eba\u90fd\u662f\u9b54\u6559\u5f1f\u5b50\uff0c\u7531\u4e2b\u5934\u5230\u770b\u95e8\u7684\u8001\u82cd\u5934\uff0c\u5168\u90fd\u662f\u9ad8\u624b\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u5947\u9053\uff1a\u201c\u7edf\u9886\u8fdb\u6765\u65f6\uff0c\u662f\u5426\u9047\u8fc7\u62b5\u6297\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\uff0c\u800c\u4e14\u8fd8\u6709\u4eba\u6307\u70b9\u6211\u4eec\u7684\u6765\u8def\u3002\u201d\u738b\u5f64\u9053\uff1a\u201c\u5426\u5219\uff0c\u4e5f\u4e0d\u4f1a\u4e00\u4e0b\u5b50\u4fbf\u627e\u5230\u8fd9\u91cc\u6765 \u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\uff0c\u662f\u548c\u5c1a\u54e5\u54e5\u2026\u2026\u201d\u71d5\u6625\u98ce\u63d0\u9ad8\u4e86\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u51fa\u6765\u5427\uff0c\u9a97\u4e0d\u8fc7\u6211\u7684\uff01\u201d \u3000\u3000\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u771f\u662f\u8d8a\u6765\u8d8a\u9b3c\u4e86\u2026\u2026\u201d\u9ed1\u7f57\u6c49\u4e03\u5de7\u501f\u548c\u738b\u91cd\u5c71\uff0c\u8fd8\u6709\u4e00\u4e2a\u4f69\u7740\u957f\u5251\u7684\u9752\u5e74 \u9053\u4eba\uff0c\u9c7c\u8d2f\u884c\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u201c\u767d\u7fbd\u9053\u957f\u2026\u2026\u201d\u71d5\u6625\u98ce\u7b11\u9053\uff1a\u201c\u9ed1\u548c\u5c1a\u7ec8\u4e8e\u628a\u4f60\u627e\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u6211\u53eb\u767d\u7fbd\uff0c\u4f60\u662f\u5c0f\u71d5\u5b50\u4e86\uff1f\u201d \u3000\u3000\u201c\u5c0f\u71d5\u5b50\uff0c\u5c0f\u548c\u5c1a\u7684\u8bdd\u5df2\u7ecf\u5151\u73b0\u4e86\uff0c\u4f60\u7b54\u5e94\u6211\u7684\u4e8b\u53ef\u4e0d\u80fd\u9ec4\u725b\uff0c\u6b3a\u9a97\u51fa\u5bb6\u4eba\u53ef\u8981\u6253\u5165\u5341 \u516b\u5c42\u5730\u72f1\u7684\u3002\u201d \u3000\u3000\u201c\u6211\u5c31\u662f\u60f3\u9a97\u4f60\u4e00\u6b21\u73a9\u73a9\uff0c\u53ea\u6015\u4e5f\u4e0d\u6210\u529f\u2026\u2026\u201d\u71d5\u6625\u98ce\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u4f60\u5f97\u5148\u544a\u8bc9\u6211\uff0c\u4e0a \u9762\u7684\u4eba\u5462\uff1f\u201d \u3000\u3000\u201c\u90fd\u88ab\u5c0f\u548c\u5c1a\u7ed9\u653e\u5012\u4e86\u2026\u2026\u201d\u767d\u7fbd\u9053\uff1a\u201c\u65e0\u4e00\u6f0f\u7f51\u3002\u201d \u3000\u3000\u201c\u5c0f\u548c\u5c1a\u53ef\u6ca1\u6709\u8fd9\u4e48\u5927\u7684\u672c\u9886\uff0c\u662f\u767d\u8138\u5c0f\u9053\u58eb\u7ed9\u6211\u7684\u2026\u2026\u201d \u3000\u3000\u76ee\u7779\u767d\u7fbd\u8fde\u8fde\u793a\u610f\uff0c\u9ed1\u7f57\u6c49\u4f4f\u53e3\u4e0d\u8a00\u4e86\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u9053\uff1a\u201c\u5c0f\u71d5\u5b50\uff0c\u8fd9\u90fd\u662f\u4f60\u7684\u670b\u53cb\uff1f\u201d \u3000\u3000\u201c\u662f\u554a\uff0c\u5f88\u597d\u73a9\u5427\uff01\u548c\u5c1a\u3001\u9053\u58eb\u5168\u90fd\u6709\u4e86\u3002\u201d \u3000\u3000\u4e09\u516c\u4e3b\u9053\uff1a\u201c\u80fd\u4e00\u4e3e\u95f4\u5236\u4f4f\u4e86\u8fd9\u91cc\u6240\u6709\u7684\u9b54\u6559\u5f1f\u5b50\uff0c\u8db3\u89c1\u9ad8\u660e\u554a\uff01\u4ed6\u4eec\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u91ce\u548c\u5c1a\u548c\u91ce\u9053\u58eb\u2026\u2026\u201d\u4e03\u5de7\u50e7\u9053\uff1a\u201c\u6211\u4eec\u90fd\u662f\u88ab\u9010\u51fa\u5e08\u95e8\u7684\u4eba\uff0c\u4e0d\u7528\u8bf4\u51fa\u8eab\u4efd\u6765\u5386 \u4e86\u3002\u5bf9\u4ed8\u4ed6\u4eec\u7684\u624b\u6bb5\uff0c\u4e5f\u4e0d\u600e\u4e48\u5149\u660e\uff0c\u4e0d\u8bf4\u4e5f\u7f62\u4e86\uff01\u201d \u3000\u3000\u201c\u600e\u4e48\uff1f\u2026\u2026\u201d\u738b\u5f64\u5947\u9053\uff1a\u201c\u767d\u7fbd\u4e5f\u88ab\u9010\u51fa\u4e86\u6b66\u5f53\u6d3e\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6cd5\u5b50\u554a\uff01\u5c0f\u9053\u58eb\u4ea4\u53cb\u4e0d\u614e\uff0c\u5c0f\u548c\u5c1a\u95f9\u5f97\u5927\u5bfa\u4e0d\u6536\u3001\u5c0f\u5e99\u4e0d\u7559\uff0c\u5c0f\u9053\u58eb\u4e5f\u88ab\u4ed6\u62d6\u5f97\u6709 \u89c2\u5f52\u4e0d\u5f97\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\u6781\u4e86\u2026\u2026\u201d\u71d5\u6625\u98ce\u7b11\u9053\uff1a\u201c\u6c5f\u53d4\u53d4\u544a\u8bc9\u6211\uff0c\u4f60\u4eec\u90fd\u4e0d\u662f\u51fa\u5bb6\u4eba\u7684\u6750\u6599\uff0c\u65e9\u665a\u90fd\u8981\u88ab \u64b5\u51fa\u5e99\u95e8\uff0c\u679c\u7136\u662f\u8a00\u4e2d\u4e86\u3002\u201d \u3000\u3000\u738b\u5f64\u5fc3\u4e2d\u660e\u767d\uff0c\u8fd9\u662f\u6c5f\u5343\u91cc\u7684\u5b89\u6392\uff0c\u4e2a\u4e2d\u5fc5\u6709\u6df1\u610f\uff0c\u4f46\u4e09\u5c0f\u5ab3\u7b11\u81ea\u5982\uff0c\u5168\u6ca1\u628a\u4e09\u516c\u4e3b\u653e \u5728\u5fc3\u4e0a\uff0c\u6210\u4f55\u4f53\u7edf\uff0c\u91cd\u91cd\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5c0f\u71d5\u5b50\uff0c\u5148\u542c\u4e09\u516c\u4e3b\u628a\u8bdd\u8bf4\u5b8c\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u4e00\u6574\u8138\u8272\uff0c\u5bf9\u4e09\u516c\u4e3b\u4e00\u62b1\u62f3\uff0c\u9053\uff1a\u201c\u4e09\u516c\u4e3b\uff0c\u5c0f\u548c\u5c1a\u3001\u5c0f\u9053\u58eb\u5168\u90fd\u5230\u9f50\u4e86\uff0c\u4e09\u516c \u4e3b\u8bf7\u5429\u5490\u5427\uff1f\u201d \u3000\u3000\u4e09\u516c\u4e3b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u542c\u8749\u9662\u4e2d\u8fd8\u6709\u4e24\u4f4d\u9b54\u6559\u957f\u8001\u6f5c\u4f0f\uff0c\u63a7\u5236\u7740\u6211\u548c\u601c\u82b1\u7684\u4e3e\u52a8\uff0c\u6240 \u4ee5\u6211\u4e0d\u80fd\u4e0d\u542c\u4ed6\u4eec\u7684\u6307\u4ee4\uff0c\u56e0\u4e3a\uff0c\u4ed6\u4eec\u5a01\u80c1\u6211\uff0c\u53ea\u8981\u6211\u7a0d\u6709\u53cd\u6297\uff0c\u7acb\u523b\u53d6\u6211\u7236\u738b\u4e4b\u547d\u3002\u201d \u3000\u3000\u201c\u53ef\u6076\uff01\u201d\u738b\u5f64\u9053\uff1a\u201c\u4f46\u4e09\u516c\u4e3b\u662f\u4e00\u9053\u98de\u8679\u82df\u6167\u6708\u5973\u4fa0\u5f1f\u5b50\uff0c\u96be\u9053\u6ca1\u6709\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u8eab\u53d7\u7981\u5236\uff0c\u4ed6\u4eec\u968f\u65f6\u53ef\u4ee5\u8ba9\u6211\u5931\u53bb\u6297\u62d2\u7684\u80fd\u529b\uff0c\u6211\u867d\u6b66\u529f\u672a\u5931\uff0c\u5374\u53ea\u80fd\u4e3a\u4ed6\u4eec\u6548\u547d \u51fa\u529b\uff0c\u5374\u4e0d\u80fd\u548c\u4ed6\u4eec\u5bf9\u624b\u4e3a\u654c\u3002\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u90a3\u662f\u4e00\u79cd\u4ec0\u4e48\u7981\u5236\uff0c\u4f1a\u6709\u6b64\u7b49\u795e\u5947\u4f5c\u7528\uff1f\u201d \u3000\u3000\u201c\u597d\u50cf\u662f\u866b\u6bd2\u2026\u2026\u201d\u4e09\u516c\u4e3b\u9053\uff1a\u201c\u4f46\u6211\u4e0d\u80fd\u80af\u5b9a\u2026\u2026\u201d \u3000\u3000\u201c\u4e09\u516c\u4e3b\u2026\u2026\u201d\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u7981\u5236\u5982\u4f55\u624d\u4f1a\u53d1\u4f5c\u5462\uff1f\u201d \u3000\u3000\u201c\u53ea\u8981\u4ed6\u5439\u51fa\u4e00\u58f0\u5c16\u5389\u7684\u7af9\u54e8\u58f0\uff0c\u6211\u5c31\u7acb\u523b\u8179\u75bc\u5982\u7ede\uff0c\u624b\u811a\u65e0\u529b\u2026\u2026\u201d\u4e09\u516c\u4e3b\u9eef\u7136\u8bf4 \u9053\uff1a\u201c\u6211\u5982\u80fd\u6709\u987f\u996d\u5de5\u592b\u7684\u65f6\u95f4\uff0c\u65e9\u5c31\u548c\u4ed6\u4eec\u653e\u624b\u4e00\u6218\u4e86\uff01\u6211\u51e0\u5ea6\u60f3\u81ea\u7edd\u6b7b\u53bb\uff0c\u4f46\u5374\u4e3a\u601c\u82b1 \u529d\u963b\uff0c\u6211\u53c8\u62c5\u5fc3\u4ed6\u4eec\u5728\u6211\u6b7b\u53bb\u4e4b\u540e\uff0c\u627e\u4e00\u4e2a\u9b54\u5973\u4ee3\u66ff\u6211\uff0c\u90a3\u5c31\u66f4\u4e3a\u53ef\u6015\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u9b54\u6559\u624b\u6cd5\u679c\u7136\u53ef\u6015\u2026\u2026\u201d\u738b\u5f64\u9053\uff1a\u201c\u5c0f\u71d5\u5b50\uff0c\u6709\u6ca1\u6709\u89e3\u7981\u4e4b\u6cd5\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\uff01\u201d\u71d5\u6625\u98ce\u9053\uff1a\u201c\u627e\u4e0d\u51fa\u7981\u5236\u7684\u539f\u56e0\uff0c\u6211\u4e5f\u65e0\u6cd5\u4e0b\u624b\u3002\u201d \u3000\u3000\u738b\u5f64\u76ee\u5149\u8f6c\u6ce8\u5230\u91d1\u5929\u7965\u7684\u8eab\u4e0a\uff0c\u9053\uff1a\u201c\u4f60\u8981\u653e\u624b\u4e00\u6218\u5462\uff1f\u8fd8\u662f\u675f\u624b\u5c31\u7f1a\uff1f\u201d \u3000\u3000\u201c\u4e09\u516c\u4e3b\uff0c\u6211\u662f\u53d7\u547d\u53d8\u8282\u964d\u654c\u2026\u2026\u201d\u91d1\u5929\u7965\u9053\uff1a\u201c\u516c\u4e3b\u7b54\u5e94\u8fc7\u4fdd\u969c\u6211\u7684\u8eab\u5bb6\u6027\u547d\u7684\u3002\u201d \u3000\u3000\u201c\u738b\u7edf\u9886\uff0c\u653e\u4ed6\u53bb\u5427\uff01\u6211\u786e\u5b9e\u7b54\u5e94\u8fc7\u4ed6\u4eec\u2026\u2026\u201d\u4e09\u516c\u4e3b\u9053\uff1a\u201c\u6211\u867d\u662f\u8eab\u4e0d\u7531\u5df1\uff0c\u4f46\u4ed6\u4eec \u5e76\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u91d1\u5929\u7965\uff0c\u4e09\u516c\u4e3b\u65e2\u5df2\u627f\u8bfa\uff0c\u738b\u67d0\u4e5f\u4e0d\u4fbf\u6df1\u7a76\u4e86\uff0c\u73b0\u5728\uff0c\u4f60\u6709\u4e24\u6761\u8def\u8d70\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u4e24\u6761\u8def\uff1f\u201d \u3000\u3000\u201c\u4e00\u662f\u5e9f\u4e86\u4f60\u7684\u6b66\u529f\uff0c\u9010\u51fa\u5bab\u5ef7\uff1b\u4e8c\u662f\u7559\u4efb\u539f\u804c\uff0c\u5e26\u7f6a\u7acb\u529f\u3002 \u3000\u3000\u5509\uff01\u6211\u770b\u5230\u4e86\u4f60\u6740\u8c2d\u4fe1\u706d\u53e3\uff0c\u8fd9\u5904\u7f5a\u8be5\u662f\u6700\u8f7b\u7684\u4e86\u3002\u201d \u3000\u3000\u91d1\u5929\u7965\u9053\uff1a\u201c\u5c5e\u4e0b\u613f\u5e26\u7f6a\u7acb\u529f\uff0c\u7c89\u8eab\u788e\u9aa8\uff0c\u5728\u6240\u4e0d\u60dc\uff01\u201d \u3000\u3000\u201c\u597d\uff0c\u73b0\u5728\u6700\u5927\u7684\u96be\u9898\uff0c\u662f\u5982\u4f55\u624d\u80fd\u89e3\u53bb\u4e09\u516c\u4e3b\u8eab\u4e0a\u7684\u7981\u5236\u4e86\u2026\u2026\u201d\u738b\u5f64\u76ee\u5149\u8f6c\u6ce8\u5230\u60dc \u6625\u7684\u8eab\u4e0a\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u51fa\u89e3\u53bb\u4e09\u516c\u4e3b\u8eab\u4e0a\u7981\u5236\u4e4b\u58f0\uff0c\u6211\u5c31\u7acb\u523b\u653e\u4e86\u4f60\uff01\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u60dc\u6625\u5df2\u88ab\u5c0f\u71d5\u5b50\u70b9\u4e86\u7a74\u9053\uff0c\u6263\u5728\u4e00\u5f20\u94c1\u67b6\u5b50\u4e0a\u3002 \u3000\u3000\u7247\u523b\u95f4\uff0c\u4e3b\u3001\u5ba2\u6613\u52bf\uff0c\u53d8\u5316\u4e4b\u5feb\uff0c\u76ee\u4e0d\u6687\u63a5\u3002 \u3000\u3000\u201c\u5c31\u6211\u6240\u77e5\uff0c\u4e09\u516c\u4e3b\u7684\u89e3\u836f\u5e76\u672a\u5e26\u5165\u4e2d\u539f\u2026\u2026\u201d\u60dc\u6625\u9053\uff1a\u201c\u866b\u6bd2\u53d1\u4f5c\u65f6\u867d\u7136\u75db\u82e6\uff0c\u4f46\u4e0d \u4f1a\u81f4\u547d\uff0c\u516c\u4e3b\u53ea\u8981\u80af\u542c\u8bdd\uff0c\u7acb\u523b\u5c31\u53ef\u4ee5\u6b62\u75db\u6062\u590d\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u8bf4\uff0c\u89e3\u836f\u653e\u5728\u897f\u57df\u9b54\u5bab\u4e2d\u4e86\uff1f\u201d\u738b\u5f64\u9053\uff1a\u201c\u60dc\u6625\uff0c\u90a3\u5c31\u5bf9\u4f60\u4e0d\u600e\u4e48\u597d\u4e86\u2026\u2026\u201d \u3000\u3000\u4e03\u5de7\u50e7\u7a81\u7136\u63a5\u9053\uff1a\u201c\u5c0f\u548c\u5c1a\u548c\u5c0f\u9053\u58eb\u5230\u5916\u9762\u8b66\u6212\uff0c\u987a\u4fbf\u628a\u51e0\u4e2a\u9b54\u5d3d\u5b50\u5904\u7f6e\u4e00\u4e0b\u3002\u201d\u4e00\u62c9 \u767d\u7fbd\uff0c\u98de\u8dc3\u800c\u51fa\u3002 \u3000\u3000\u201c\u4e09\u516c\u4e3b\u548c\u601c\u82b1\u59d1\u5a18\u8bf7\u56de\u907f\u4e00\u4e0b\uff0c\u6211\u8981\u52a8\u5211\u903c\u4f9b\u4e86\u3002\u201d\u738b\u5f64\u795e\u60c5\u5982\u94c1\u7684\u8bf4\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u9b54\u6559\u91cc\u6709\u4e00\u79cd\u8840\u9041\u8eab\u6cd5\uff0c\u80fd\u501f\u672c\u8eab\u4e00\u53e3\u9c9c\u8840\uff0c\u8131\u8eab\u800c\u53bb\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\uff01\u201d\u5c0f\u71d5\u5b50\u8bf4\uff1a\u201c\u90a3\u662f\u4e00\u79cd\u8f6f\u9aa8\u795e\u529f\uff0c\u501f\u4e00\u53e3\u9c9c\u8840\uff0c\u547d\u540d\u9aa8\u9abc\u6536\u7f29\uff0c\u8131\u51fa\u7ef3\u7d22 \u9563\u94d0\u3002\u201d \u3000\u3000\u60dc\u6625\u51b7\u51b7\u5730\u9053\uff1a\u201c\u5c0f\u71d5\u5b50\uff0c\u4f60\u7a76\u7adf\u662f\u8c01\uff1f\u8fd8\u77e5\u9053\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4f60\u4eec\u9b54\u6559\u4e2d\u5f88\u591a\u82b1\u6837\uff0c\u5927\u90fd\u65e0\u6cd5\u7792\u5f97\u8fc7\u6211\uff0c\u6240\u4ee5\uff0c\u4f60\u6700\u597d\u8001\u5b9e\u4e00\u70b9\u2026\u2026\u201d\u5c0f\u71d5\u5b50\u9053\uff1a \u201c\u6211\u4e5f\u77e5\u9053\u4f60\u4eec\u9b54\u6559\u53ef\u4ee5\u5fcd\u53d7\u884c\u5211\u4e4b\u82e6\uff0c\u53ef\u60dc\uff0c\u6709\u6211\u5c0f\u71d5\u5b50\u5728\u6b64\uff0c\u4f60\u8fd9\u4e9b\u90aa\u672f\u5c31\u7528\u4e0d\u4e0a \u4e86\u3002\u201d \u3000\u3000\u60dc\u6625\u8138\u8272\u4e00\u53d8\uff0c\u4f46\u5374\u95ed\u53e3\u4e0d\u8a00\u3002 \u3000\u3000\u738b\u5f64\u4e00\u6446\u5934\uff0c\u9648\u5b8f\u3001\u8d75\u4fdd\u7acb\u523b\u5206\u5de6\u53f3\u884c\u4e86\u8fc7\u53bb\uff0c\u9648\u5b8f\u53f3\u624b\u6258\u8d77\u4e86\u60dc\u6625\u4e0b\u989a\uff0c\u624b\u6307\u52a0\u529b\uff0c \u634f\u5f00\u4e86\u60dc\u6625\u7684\u7259\u5173\uff0c\u4e00\u56e2\u7ee2\u5e03\uff0c\u585e\u5165\u4e86\u60dc\u6625\u53e3\u4e2d\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u90fd\u662f\u884c\u5211\u8001\u624b\uff0c\u9996\u5148\u9632\u6b62\u7684\u662f\u72af\u4eba\u54ac\u820c\u81ea\u5c3d\u3002 \u3000\u3000\u738b\u5f64\u6c89\u58f0\u9053\uff1a\u201c\u60dc\u6625\uff0c\u4f60\u6ca1\u6709\u6c42\u6b7b\u7684\u673a\u4f1a\u4e86\uff0c\u613f\u610f\u62db\u8ba4\u65f6\uff0c\u5c31\u70b9\u70b9\u5934\u2026\u2026\u201d\u8f6c\u5411\u601c\u82b1 \u9053\uff1a\u201c\u601c\u82b1\u59d1\u5a18\uff0c\u8bf7\u4e09\u516c\u4e3b\u56de\u907f\u3002\u201d \u3000\u3000\u601c\u82b1\u5e94\u4e86\u4e00\u58f0\uff0c\u6276\u7740\u4e09\u516c\u4e3b\u8f6c\u5165\u5927\u5385\u4e00\u89d2\u3002 \u3000\u3000\u201c\u91d1\u5929\u7965\uff0c\u7531\u4f60\u884c\u5211\uff01\u201d\u738b\u5f64\u9053\uff1a\u201c\u5341\u6307\u8fde\u5fc3\uff0c\u5148\u7531\u624b\u4e0a\u52a8\u8d77\u5427\uff01\u201d \u3000\u3000\u90a3\u65f6\u4ee3\uff0c\u541b\u6743\u7b2c\u4e00\uff0c\u4e3a\u7687\u5e1d\u7684\u5e73\u5b89\uff0c\u53ef\u4ee5\u9519\u6740\u4e00\u767e\u5374\u4e0d\u4f1a\u653e\u8fc7\u4e00\u4e2a\u5acc\u72af\uff0c\u5185\u5bab\u4f8d\u536b\u662f\u771f \u6b63\u4fdd\u62a4\u7687\u4e0a\u5b89\u5168\u7684\u4eba\uff0c\u7ecf\u624b\u7684\u6848\u4ef6\u4e5f\u5927\u90fd\u548c\u8c0b\u53cd\u3001\u53db\u9006\u6709\u5173\uff0c\u4e25\u8ffd\u6839\u5e95\uff0c\u6e05\u9664\u53db\u515a\uff0c\u4e3a\u5185\u5bab \u4f8d\u536b\u7684\u8d23\u4efb\u4e4b\u4e00\uff0c\u6240\u4ee5\uff0c\u4ed6\u4eec\u53d1\u5c55\u51fa\u4e86\u4e0d\u5c11\u5947\u82db\u7684\u4e25\u5211\uff0c\u738b\u5f64\u638c\u7406\u4e86\u5185\u5bab\u7edf\u9886\u4e4b\u540e\uff0c\u867d\u7136\u529b \u6c42\u6539\u8fdb\uff0c\u4f46\u767e\u5e74\u4f20\u7edf\u4f59\u6bd2\u4ecd\u70c8\u3002 \u3000\u3000\u9b54\u6559\u5f1f\u5b50\u96be\u7f20\uff0c\u4e09\u516c\u4e3b\u8eab\u4e0a\u7981\u5236\u53c8\u5fc5\u987b\u89e3\u9664\uff0c\u6240\u4ee5\uff0c\u738b\u5f64\u4e5f\u5c31\u53ea\u597d\u653e\u4efb\u4ed6\u4eec\u65bd\u5c55\u4e25\u5389\u7684 \u884c\u5211\u624b\u6bb5\u4e86\u3002 \u3000\u3000\u91d1\u5929\u7965\u884c\u8fd1\u60dc\u6625\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u60dc\u6625\u59d1\u5a18\uff0c\u5bf9\u4e0d\u4f4f\u4e86\uff0c\u6211\u662f\u4e0a\u547d\u96be\u8fdd\uff0c\u6307\u5211\u5148\u5254\u6307\u7532\uff0c \u90a3\u79cd\u75db\u82e6\uff0c\u8d85\u8fc7\u4e86\u4eba\u5fcd\u53d7\u7684\u6781\u9650\uff0c\u59d1\u5a18\u5982\u80af\u5408\u4f5c\uff0c\u8fd9\u79cd\u75db\u82e6\u5c31\u53ef\u514d\u53bb\u4e86\uff0c\u4f55\u51b5\uff0c\u5982\u6b64\u7f8e\u4e3d\u7684 \u7ea4\u7ea4\u7389\u6307\uff0c\u53d7\u5230\u4f24\u5bb3\u4e5f\u662f\u53ef\u60dc\u7684\uff01\u201d \u3000\u3000\u60dc\u6625\u65e0\u6cd5\u56de\u7b54\uff0c\u53ea\u7528\u4e24\u53ea\u773c\u775b\u770b\u7740\u91d1\u5929\u7965\uff0c\u8138\u4e0a\u662f\u4e00\u79cd\u81ea\u601c\u81ea\u60dc\u7684\u795e\u60c5\uff0c\u771f\u662f\u54c0\u75db\u6b32 \u7edd\uff0c\u76ee\u5149\u4e2d\u66f4\u662f\u4e00\u7247\u51c4\u6726\u7684\u6e29\u67d4\uff0c\u52a8\u4eba\u81f3\u6781\u3002 \u3000\u3000\u91d1\u5929\u7965\u770b\u5f97\u75f4\u4e86\uff0c\u63e1\u7740\u60dc\u6625\u5a07\u5ae9\u7684\u7389\u6307\uff0c\u54ea\u91cc\u8fd8\u5fcd\u5fc3\u4e0b\u624b\u3002 \u3000\u3000\u5176\u5b9e\uff0c\u4f55\u53ea\u662f\u91d1\u5929\u7965\uff0c\u9648\u5b8f\u3001\u8d75\u4fdd\u4e5f\u90fd\u662f\u770b\u5f97\u4e2a\u4e2a\u795e\u8272\u9eef\u7136\uff0c\u76ee\u8574\u6cea\u5149\u3002 \u3000\u3000\u5c31\u662f\u738b\u5f64\uff0c\u4e5f\u89c9\u5fc3\u5934\u60e8\u7136\uff0c\u5fd8\u8bb0\u4e86\u559d\u4ee4\u91d1\u5929\u7965\u52a8\u624b\u884c\u5211\u3002 \u3000\u3000\u5e78\u597d\uff0c\u5c0f\u71d5\u5b50\u70b9\u4e86\u60dc\u6625\u7684\u54d1\u7a74\uff0c\u5982\u82e5\u8fd9\u79cd\u52a8\u4eba\u7684\u51c4\u7edd\u795e\u60c5\uff0c\u518d\u914d\u4e0a\u5a07\u751c\u7684\u58f0\u97f3\uff0c\u8fd9\u4e9b\u4eba \u53ea\u6015\u65e9\u5df2\u6d51\u7136\u5fd8\u6211\uff0c\u89e3\u5176\u7f1a\u675f\uff0c\u653e\u5979\u79bb\u53bb\u4e86\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u6697\u6697\u53f9\u606f\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u9b54\u6559\u5f02\u672f\uff0c\u679c\u5177\u5a01\u529b\uff0c\u8fd9\u4e9b\u4eba\u7eb5\u7136\u6b66\u529f\u9ad8\u8fc7\u9b54\u6559\u5f1f\u5b50\uff0c\u4f46 \u4e5f\u65e0\u6cd5\u548c\u4ed6\u4eec\u5bf9\u6297\u4e8e\u6c5f\u6e56\u4e4b\u4e0a\u3002 \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u5374\u4f4e\u58f0\u559d\u9053\uff1a\u201c\u91d1\u5929\u7965\uff0c\u8499\u4f4f\u5979\u7684\u773c\u775b\uff01\u201d \u3000\u3000\u58f0\u97f3\u4e0d\u5927\uff0c\u4f46\u5374\u5982\u5f53\u5934\u68d2\u559d\uff0c\u91d1\u5929\u7965\u5fc3\u5934\u4e00\u9707\uff0c\u4eba\u4e5f\u6e05\u9192\u8fc7\u6765\uff0c\u6495\u4e0b\u60dc\u6625\u8eab\u4e0a\u4e00\u7247\u8863 \u895f\uff0c\u8499\u4e0a\u4e86\u60dc\u6625\u7684\u773c\u775b\u3002 \u3000\u3000\u201c\u5229\u5bb3\u554a\uff0c\u5229\u5bb3\uff01\u201d\u738b\u5f64\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5c0f\u71d5\u5b50\uff0c\u672c\u5ea7\u4e0d\u60f3\u65bd\u5211\u6c42\u4f9b\uff0c\u4f60\u53ef\u6709\u529e\u6cd5\u8ba9\u60dc \u6625\u8bf4\u51fa\u5b9e\u8bdd\uff1f\u201d \u3000\u3000\u201c\u6211\u8bd5\u8bd5\u770b\u5427\uff01\u201d\u5c0f\u71d5\u5b50\u53f3\u624b\u75be\u51fa\uff0c\u70b9\u4e86\u60dc\u6625\u8eab\u4e0a\u4e09\u5904\u8981\u7a74\u3002 \u3000\u3000\u4f46\u89c1\u60dc\u6625\u8eab\u8eaf\u98a4\u6296\uff0c\u7c89\u988a\u4e0a\u5f00\u59cb\u6eda\u843d\u6c57\u73e0\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u77e5\u9053\u5c0f\u71d5\u5b50\u7528\u7684\u662f\u4ec0\u4e48\u624b\u6cd5\uff0c\u4f46\u5374\u90fd\u770b\u5f97\u51fa\u60dc\u6625\u6b63\u5fcd\u53d7\u7740\u5de8\u5927\u7684\u75db\u82e6\u3002 \u3000\u3000\u5979\u53cc\u773c\u88ab\u8499\u4f4f\uff0c\u53e3\u4e2d\u53c8\u585e\u4e86\u7ee2\u5e15\uff0c\u771f\u662f\u76ee\u4e0d\u80fd\u89c6\uff0c\u53e3\u4e0d\u80fd\u8a00\uff0c\u53ea\u89c1\u8138\u4e0a\u7684\u6c57\u73e0\u513f\u8d8a\u6765\u8d8a \u591a\uff0c\u6eda\u6eda\u800c\u4e0b\u3002 \u3000\u3000\u5ffd\u7136\uff0c\u60dc\u6625\u4e0d\u4f4f\u5730\u70b9\u5934\uff0c\u663e\u7136\u5df2\u5fcd\u4e0d\u4e86\u8fd9\u79cd\u75db\u82e6\u3002 \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u89e3\u4e0b\u5979\u773c\u7f69\uff0c\u9664\u53bb\u53e3\u4e2d\u7684\u7ee2\u5e15\u3002\u201d \u3000\u3000\u91d1\u5929\u7965\u5e94\u58f0\u51fa\u624b\uff0c\u60dc\u6625\u4e00\u5f00\u53e3\u5c31\u53eb\u9053\uff1a\u201c\u5feb\u4e9b\u6b62\u4f4f\u6211\u7684\u884c\u8840\u56de\u805a\uff0c\u6211\u53d7\u4e0d\u4e86\u2026\u2026\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u4f38\u624b\u89e3\u5f00\u60dc\u6625\u4e09\u5904\u7a74\u9053\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u60dc\u6625\uff0c\u4f60\u771f\u7684\u5f88\u75db\u82e6\u4e48\uff1f\u201d \u3000\u3000\u60dc\u6625\u957f\u957f\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u76ee\u5149\u4e2d\u53c8\u6068\u53c8\u6015\u7684\u51dd\u6ce8\u7740\u5c0f\u71d5\u5b50\uff0c\u9053\uff1a\u201c\u4f60\u89c9\u5f97\u597d\u73a9\u54ea\uff0c\u518d\u6709\u4e00 \u523b\u5de5\u592b\uff0c\u6211\u5c31\u795e\u706d\u3001\u9aa8\u9500\uff0c\u4e0d\u6210\u4eba\u5f62\u4e86\u3002\u201d \u3000\u3000\u201c\u771f\u62b1\u6b49\uff0c\u60dc\u6625\uff0c\u6211\u4e0d\u77e5\u9053\u8fd9\u624b\u6cd5\u5982\u6b64\u6076\u6bd2\uff01\u201d\u5c0f\u71d5\u5b50\u8138\u4e0a\u6cdb\u51fa\u65e0\u9650\u6b49\u610f\u3002 \u3000\u3000\u201c\u60dc\u6625\uff0c\u4f60\u8fd8\u4fdd\u6301\u5982\u82b1\u5bb9\u8c8c\uff0c\u5982\u679c\u662f\u6211\u7684\u4eba\u51fa\u624b\uff0c\u53ea\u6015\u65e9\u5df2\u6574\u5f97\u4f60\u6307\u65ad\u80a2\u6b8b\uff0c\u8840\u8089\u6a2a\u98de \u4e86\u3002\u201d\u738b\u5f64\u795e\u60c5\u51b7\u51b7\u5730\u9053\uff1a\u201c\u73b0\u5728\uff0c\u4f60\u5e94\u8be5\u77e5\u9053\uff0c\u6211\u4eec\u7edd\u65e0\u601c\u9999\u60dc\u7389\u4e4b\u5fc3\uff0c\u8be5\u8bf4\u7684\uff0c\u53ef\u4ee5\u8bf4 \u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u4e09\u516c\u4e3b\u4e2d\u7684\u866b\u6bd2\uff0c\u90a3\u4e0d\u662f\u9b54\u6559\u4e2d\u7528\u7684\u624b\u6cd5\u2026\u2026\u201d\u60dc\u6625\u8bf4\uff1a\u201c\u866b\u6bd2\u6765\u81ea\u82d7\u7586\uff0c\u6211\u4e5f\u65e0\u6cd5 \u77e5\u9053\u5979\u4e2d\u7684\u662f\u4ec0\u4e48\u866b\uff0c\u9664\u4e86\u627e\u5230\u4e0b\u866b\u7684\u4eba\uff0c\u5426\u5219\u6ca1\u4eba\u80fd\u89e3\u5f97\u3002\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u9b54\u6559\u4e2d\u4eba\u51e0\u65f6\u548c\u82d7\u7586\u4e07\u866b\u95e8\u7684\u4eba\u52fe\u642d\u4e0a\u4e86\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u5e2e\u52a9\u4e07\u866b\u95e8\u6e21\u8fc7\u4e00\u6b21\u52ab\u96be\uff0c\u53cc\u65b9\u5c31\u5efa\u7acb\u4e86\u5408\u4f5c\u7684\u5173\u7cfb\u3002\u201d \u3000\u3000\u60dc\u6625\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u7684\u5b9a\u529b\u5f88\u5f3a\uff0c\u6297\u62d2\u4e86\u9b54\u6559\u7684\u8ff7\u9b42\u5927\u6cd5\uff0c\u4f46\u5979\u65e0\u6cd5\u6297\u62d2\u86ca\u672f\uff0c\u6240\u4ee5\u5979\u4ecd \u7136\u88ab\u9b54\u6559\u63a7\u5236\u3002\u201d \u3000\u3000\u201c\u60dc\u6625\uff0c\u9b54\u6559\u5982\u6b64\u5bf9\u5f85\u4e09\u516c\u4e3b\uff0c\u4e00\u65e6\u63ed\u9732\u2026\u2026\u201d\u738b\u5f64\u9053\uff1a\u201c\u96be\u9053\u4e0d\u62c5\u5fc3\u7687\u4e0a\u6495\u6bc1\u548c\u7ea6\uff0c \u518d\u8c03\u6d3e\u5927\u519b\u897f\u5f81\u4e48\uff1f\u201d \u3000\u3000\u201c\u8fd9\u662f\u6211\u4eec\u552f\u4e00\u7684\u987e\u8651\uff01\u201d\u60dc\u6625\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u8fd9\u4e24\u4e09\u5e74\u7684\u53d8\u5316\u5f88\u5927\uff0c\u5404\u5730\u85e9\u738b\u5df2\u80cc\u5f03\u4e86 \u6731\u5143\u748b\u7684\u9057\u8bad\uff0c\u6697\u4e2d\u62db\u5175\u4e70\u9a6c\uff0c\u62e5\u5175\u81ea\u91cd\uff0c\u5bf9\u7687\u4e0a\u7684\u5723\u65e8\u5df2\u7136\u9633\u5949\u9634\u8fdd\uff0c\u6570\u5341\u4e07\u897f\u5f81\u7684\u5927 \u519b\uff0c\u65e9\u5df2\u5206\u6563\u5404\u5904\uff0c\u4f60\u738b\u7edf\u9886\u5fc3\u4e2d\u660e\u767d\uff0c\u518d\u60f3\u53ec\u96c6\u4e24\u5e74\u524d\u90a3\u6837\u4e00\u6279\u80fd\u5f81\u5584\u6218\u7684\u5927\u519b\uff0c\u5df2\u4e0d\u5bb9 \u6613\u3002\u4f55\u51b5\uff0c\u4eb2\u738b\u5272\u636e\uff0c\u5185\u5371\u91cd\u91cd\uff0c\u7687\u4e0a\u4e5f\u660e\u767d\uff0c\u5b89\u5185\u5b9e\u5df2\u91cd\u8fc7\u5f81\u4f10\u5916\u4fae\uff0c\u4f55\u51b5\uff0c\u8499\u53e4\u53ef\u6c57\u5982 \u7ea6\u8fdb\u8d21\uff0c\u7687\u4e0a\u4e0d\u4f1a\u4e3a\u4e00\u4e2a\u4e09\u516c\u4e3b\u518d\u5174\u5927\u6218\u7684\uff0c\u4e00\u65e6\u4eac\u757f\u738b\u5ba4\u5f62\u6210\u5e93\u865a\u5175\u7a7a\uff0c\u4ed6\u8fd9\u4e2a\u7687\u5e1d\u7684\u4f4d \u7f6e\uff0c\u968f\u65f6\u90fd\u53ef\u80fd\u88ab\u522b\u4eba\u53d6\u800c\u4ee3\u4e4b\u3002\u201d \u3000\u3000\u738b\u5f64\u9ed8\u7136\u4e0d\u8bed\u3002 \u3000\u3000\u56e0\u4e3a\u60dc\u6625\u6240\u8bf4\u7684\u662f\u5b9e\u8bdd\uff0c\u7687\u5e1d\u624b\u4e0b\u638c\u63e1\u4e86\u4e8c\u5341\u4e07\u5de6\u53f3\u7684\u4eb2\u5175\uff0c\u662f\u9547\u538b\u5404\u5730\u843d\u738b\u7684\u672c\u94b1\uff0c \u5982\u679c\u8c03\u51fa\u53bb\u8fdc\u5f81\u897f\u57df\uff0c\u90a3\u5c31\u6210\u4e86\u7a7a\u58f3\u5b50\uff0c\u968f\u65f6\u53ef\u80fd\u88ab\u51e0\u4e2a\u91ce\u5fc3\u52c3\u52c3\u3001\u62e5\u6709\u5b9e\u529b\u7684\u85e9\u738b\u53d6\u4ee3 \u4e86\u3002 \u3000\u3000\u8fd9\u662f\u7687\u5e1d\u5fc3\u4e2d\u7684\u6700\u5927\u5fe7\u82e6\uff0c\u8fde\u671d\u4e2d\u5927\u81e3\u77e5\u9053\u7684\u90fd\u4e0d\u592a\u591a\uff0c\u4e0d\u77e5\u9b54\u6559\u600e\u4f1a\u5982\u6b64\u6e05\u695a\uff1f \u3000\u3000\u4f46\u95fb\u60dc\u6625\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\u7684\u90fd\u5df2\u8bf4\u51fa\u6765\uff0c\u82e5\u4f60\u4eec\u8fd8\u4e0d\u613f\u653e\u8fc7\u6211\uff0c\u6211\u53ea\u6c42\u4e00\u4ef6 \u4e8b\uff0c\u4f60\u4eec\u6740\u4e86\u6211\uff0c\u6211\u7edd\u4e0d\u65bd\u672f\u53cd\u6297\uff01\u201d \u3000\u3000\u738b\u5f64\u542c\u5f97\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u8981\u6740\u5979\u65f6\uff0c\u5979\u8fd8\u80fd\u65bd\u672f\u53cd\u6297\uff0c\u8fd9\u5f53\u771f\u662f\u5996\u672f\u4e86\uff0c\u5b9e\u5728\u4e0d\u53ef\u601d \u8bae\uff01 \u3000\u3000\u91d1\u5929\u7965\u4f4e\u58f0\u9053\uff1a\u201c\u7edf\u9886\uff0c\u5982\u4f55\u5904\u7f6e\u8fd9\u4e2a\u5996\u5973\uff1f\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u5e9f\u4e86\u5979\u7684\u6b66\u529f\uff0c\u653e\u5979\u53bb\u5427\uff01\u201d \u3000\u3000\u9648\u5b8f\u3001\u8d75\u4fdd\u63a2\u624b\u5165\u6000\uff0c\u62bd\u51fa\u4e86\u4e24\u652f\u5315\u9996\uff0c\u884c\u8fd1\u60dc\u6625\u3002 \u3000\u3000\u539f\u6765\u5185\u5bab\u4f8d\u536b\u5e9f\u4eba\u6b66\u529f\u7684\u624b\u6cd5\uff0c\u662f\u6311\u65ad\u4eba\u7684\u56db\u80a2\u4e3b\u8109\uff0c\u4f7f\u53cc\u624b\u3001\u53cc\u811a\u90fd\u65e0\u6cd5\u65bd\u5c55\uff0c\u6574\u4e2a \u4eba\u53d8\u6210\u4e86\u6b8b\u5e9f\u3002 \u3000\u3000\u201c\u6162\u7740\u2026\u2026\u201d\u5c0f\u71d5\u5b50\u62e6\u4f4f\u4e86\u9648\u5b8f\u3001\u8d75\u4fdd\u3002 \u3000\u3000\u738b\u5f64\u4e00\u76b1\u7709\u5934\uff0c\u6b32\u8a00\u53c8\u6b62\u3002 \u3000\u3000\u71d5\u98de\u77a7\u51fa\u4e86\u738b\u5f64\u5fc3\u4e2d\u7684\u4e0d\u5feb\uff0c\u518d\u4e5f\u5fcd\u4e0d\u4f4f\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u5c0f\u71d5\u5b50\uff0c\u4f60\u8981\u5e72\u4ec0\u4e48\uff1f\u8fd9\u8be5\u662f\u6700 \u5bbd\u5927\u7684\u5904\u7f6e\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u8fd8\u6709\u4e8b\u8981\u8bf7\u6559\u60dc\u6625\u59d1\u5a18\u3002\u201d\u5c0f\u71d5\u5b50\u795e\u60c5\u8f7b\u677e\u5730\u9053\uff1a\u201c\u544a\u8bc9\u6211\uff0c\u60dc\u6625\u59d1\u5a18\uff0c\u4e24\u4e2a\u6f5c\u4f0f \u5728\u5bab\u4e2d\u7684\u9b54\u6559\u957f\u8001\uff0c\u662f\u5426\u6709\u4e2a\u662f\u4e07\u866b\u95e8\u4e2d\u4eba\uff1f\u201d \u3000\u3000\u60dc\u6625\u4e45\u5c45\u5185\u5bab\uff0c\u5bf9\u5185\u5bab\u4f8d\u536b\u5e9f\u4eba\u6b66\u529f\u7684\u624b\u6cd5\uff0c\u81ea\u7136\u662f\u5f88\u6e05\u695a\uff0c\u4f46\u5979\u5bf9\u6b64\u4f3c\u662f\u5e76\u65e0\u60ca\u60e7\uff0c \u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u53ea\u77e5\u9053\u6709\u4e24\u4f4d\u957f\u8001\u6f5c\u5165\u5185\u5bab\uff0c\u76d1\u89c6\u4e09\u516c\u4e3b\uff0c\u4f46\u4ed6\u4eec\u662f\u8c01\u6211\u5e76\u4e0d\u6e05\u695a\uff1f\u9b54 \u6559\u4e2d\u89c4\u6212\u68ee\u4e25\uff0c\u957f\u8001\u7684\u8eab\u4efd\u5f88\u9ad8\uff0c\u6211\u53ea\u662f\u4e2a\u4e09\u4ee3\u5f1f\u5b50\uff0c\u6240\u77e5\u6709\u9650\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u70b9\u70b9\u5934\uff0c\u53e3\u9f7f\u542f\u52a8\u4e00\u9635\uff0c\u5411\u540e\u9000\u53bb\u3002 \u3000\u3000\u4ed6\u65bd\u5c55\u4f20\u97f3\u4e4b\u672f\uff0c\u522b\u4eba\u53ea\u80fd\u77a7\u5230\u4ed6\u53e3\u9f7f\u542f\u52a8\uff0c\u5374\u542c\u4e0d\u5230\u4ed6\u8bf4\u4ec0\u4e48\uff1f \u3000\u3000\u4f46\u95fb\u60dc\u6625\u6025\u6025\u8bf4\u9053\uff1a\u201c\u5c0f\u71d5\u5b50\uff0c\u4f60\u8fc7\u6765\uff01\u201d \u3000\u3000\u71d5\u6625\u98ce\u4eba\u5df2\u5411\u540e\u9000\u53bb\uff0c\u95fb\u8a00\u53c8\u5411\u524d\u884c\u4e86\u51e0\u6b65\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u6709\u4ec0\u4e48\u5429\u5490\uff1f\u201d \u3000\u3000\u60dc\u6625\u4ee5\u76ee\u793a\u610f\uff0c\u5c0f\u71d5\u5b50\u9644\u8033\u4e0a\u53bb\u3002 \u3000\u3000\u4f3c\u4e4e\u662f\u5c0f\u71d5\u5b50\u5f88\u6ee1\u610f\u60dc\u6625\u7684\u56de\u7b54\uff0c\u70b9\u70b9\u5934\uff0c\u53c8\u5411\u540e\u9000\u51fa\u4e94\u6b65\u3002 \u3000\u3000\u71d5\u98de\u9053\uff1a\u201c\u5c0f\u5996\u5973\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u5feb\u544a\u8bc9\u7edf\u9886\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u9762\u73b0\u96be\u8272\u3002 \u3000\u3000\u738b\u5f64\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u7528\u4e86\uff0c\u53ea\u662f\u5185\u5bab\u4e2d\u8fd8\u6709\u9b54\u6559\u4e2d\u4eba\uff0c\u5012\u662f\u4e0d\u4fbf\u653e\u4f60\u79bb\u53bb\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\uff01\u201d\u5c0f\u71d5\u5b50\u8bf4\uff1a\u201c\u6211\u4f1a\u62a4\u9001\u4e09\u516c\u4e3b\u56de\u5185\u5bab\u53bb\uff0c\u53ea\u4e0d\u8fc7\u8bf7\u7edf\u9886\u5141\u51c6\uff0c\u6211\u8981\u548c\u4e09\u516c \u4e3b\u5148\u884c\u4e00\u6b65\u3002\u201d \u3000\u3000\u738b\u5f64\u5fae\u5fae\u4e00\u6014\uff0c\u9053\uff1a\u201c\u73b0\u5728\u5c31\u8d70\u4e48\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u201d \u3000\u3000\u201c\u4e0d\u884c\uff0c\u5b98\u8eab\u4e0d\u81ea\u7531\u2026\u2026\u201d\u71d5\u98de\u9053\uff1a\u201c\u4f60\u73b0\u5728\u662f\u4f8d\u536b\u8eab\u4efd\uff0c\u4e00\u5207\u90fd\u8981\u542c\u547d\u884c\u4e8b\u3002\u201d \u3000\u3000\u738b\u5f64\u6325\u6325\u624b\uff0c\u963b\u6b62\u4e86\u71d5\u98de\uff0c\u7b11\u9053\uff1a\u201c\u6240\u8bf7\u7167\u51c6\uff0c\u4e0d\u8fc7\uff0c\u4e09\u516c\u4e3b\u90a3\u8fb9\u8981\u4f60\u81ea\u5df1\u8bf7\u547d\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u76f8\u4fe1\u4e09\u516c\u4e3b\u4e0d\u4f1a\u62d2\u7edd\uff01\u201d\u5c0f\u71d5\u5b50\u6ee1\u8138\u81ea\u4fe1\u5730\u8bf4\uff1a\u201c\u8fd8\u8981\u7edf\u9886\u51c6\u8bb8\uff0c\u6211\u8981\u5e26\u5c0f\u9053\u58eb\u3001 \u5c0f\u548c\u5c1a\u3001\u738b\u91cd\u5c71\u4e00\u8d77\u5165\u5bab\u3002\u201d \u3000\u3000\u201c\u5c0f\u71d5\u5b50\uff0c\u4f60\u5728\u80e1\u95f9\u554a\uff01\u201d\u71d5\u98de\u9053\uff1a\u201c\u738b\u91cd\u5c71\u662f\u5bab\u536b\u51fa\u8eab\uff0c\u53ef\u4ee5\u5165\u5bab\uff0c\u4f46\u5c0f\u548c\u5c1a\u548c\u5c0f\u9053 \u58eb\u90fd\u662f\u51fa\u5bb6\u4eba\uff0c\u5982\u4f55\u80fd\u5165\u7981\u5bab\uff1f\u201d \u3000\u3000\u201c\u6211\u76f8\u4fe1\u5c0f\u71d5\u5b50\u5df2\u7ecf\u80f8\u6709\u6210\u7af9\uff0c\u9648\u5b8f\u3001\u8d75\u4fdd\uff0c\u628a\u4f60\u4eec\u7684\u8170\u724c\u4ea4\u7ed9\u5c0f\u71d5\u5b50\u3002\u201d\u738b\u5f64\u8bf4\uff1a \u201c\u4e0d\u8fc7\uff0c\u8981\u4ed6\u4eec\u59d4\u5c48\u4e00\u4e0b\uff0c\u6362\u4ef6\u8863\u670d\uff0c\u548c\u5c1a\u3001\u9053\u58eb\u8fdb\u4e86\u5185\u5bab\uff0c\u53ea\u6015\u8981\u5413\u574f\u4e86\u5bab\u5973\u3001\u592a\u76d1\u3002\u201d \u3000\u3000\u201c\u591a\u8c22\u7edf\u9886\uff0c\u4e09\u516c\u4e3b\u90a3\u91cc\u6211\u4f1a\u8bf4\u660e\u3002\u201d\u5c0f\u71d5\u5b50\u63a5\u8fc7\u8170\u724c\uff0c\u770b\u71d5\u98de\u4e00\u8138\u51b7\u8083\uff0c\u4e0d\u7981\u4e00\u4f38\u820c \u5934\uff0c\u8f6c\u5411\u4e09\u516c\u4e3b\u884c\u53bb\u3002 \u3000\u3000\u71d5\u98de\u4e00\u5f20\u8138\u7ef7\u5f97\u96be\u770b\uff0c\u4f46\u5fc3\u4e2d\u90a3\u4efd\u9ad8\u5174\u7b80\u76f4\u662f\u65e0\u6cd5\u5f62\u5bb9\uff0c\u773c\u770b\u7edf\u9886\u5927\u4eba\u5bf9\u5c0f\u71d5\u5b50\u8a00\u542c\u8ba1 \u4ece\uff0c\u65e0\u6240\u4e0d\u51c6\uff0c\u6bd4\u8d77\u4ed6\u8fd9\u4e2a\u505a\u7239\u7684\u53ef\u8981\u795e\u6c14\u591a\u4e86\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u5c0f\u71d5\u5b50\u7528\u7684\u662f\u4ec0\u4e48\u65b9\u6cd5\uff0c\u4e09\u516c\u4e3b\u548c\u601c\u82b1\u7adf\u7136\u8ddf\u7740\u4ed6\u4e00\u8d77\u79bb\u53bb\u3002 \u3000\u3000\u738b\u5f64\u53cc\u76ee\u76ef\u4f4f\u5728\u60dc\u6625\u8138\u4e0a\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\uff0c\u6211\u8be5\u600e\u4e48\u5904\u7f6e\u4f60\uff1f\u201d \u3000\u3000\u201c\u7edf\u9886\u5927\u4eba\u770b\u7740\u529e\u5427\uff0c\u5e9f\u4e86\u6211\uff0c\u5173\u8d77\u6211\u90fd\u884c\uff0c\u53ea\u8981\u5927\u4eba\u80fd\u4ea4\u4ee3\u8fc7\u53bb\uff0c\u5c0f\u5973\u5b50\u7518\u613f\u53d7 \u7f5a\u3002\u201d \u3000\u3000\u738b\u5f64\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u4eb2\u81ea\u89e3\u53bb\u60dc\u6625\u8eab\u4e0a\u7684\u5211\u5177\uff0c\u9053\uff1a\u201c\u4f60\u8d70\u5427\uff01 \u3000\u3000\u6b64\u540e\u662f\u53cb\u662f\u654c\uff0c\u4f60\u81ea\u5df1\u51b3\u5b9a\u5427\uff01\u201d \u3000\u3000\u8fd9\u4e2a\u4e3e\u52a8\uff0c\u5927\u5927\u7684\u51fa\u4e86\u573a\u4e2d\u4eba\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u60dc\u6625\u6d3b\u52a8\u4e00\u4e0b\u624b\u811a\uff0c\u7a81\u7136\u51fa\u624b\uff0c\u70b9\u4e86\u91d1\u5929\u7965\u7684\u7a74\u9053\uff0c\u9053\uff1a\u201c\u591a\u8c22\u7edf\u9886\u5927\u4eba\uff0c\u60dc\u6625\u6295\u6843\u62a5 \u674e\uff0c\u51e1\u662f\u88ab\u9b54\u6559\u5438\u6536\u7684\u4eba\u90fd\u65e0\u6cd5\u6062\u590d\u5e38\u6027\uff0c\u5c31\u7b97\u4ed6\u4eec\u771f\u7684\u5df2\u5b58\u5fc3\u6539\u8fc7\u5411\u5584\uff0c\u4f46\u4e5f\u65e0\u6cd5\u63a7\u5236\u81ea \u5df1\uff0c\u5728\u9b54\u97f3\u547c\u53eb\u4e4b\u4e0b\uff0c\u8eab\u4e0d\u7531\u5df1\uff0c\u6211\u548c\u5c0f\u71d5\u5b50\u4e0d\u6562\u6b63\u5e38\u4ea4\u8c08\uff0c\u5c31\u662f\u6015\u79d8\u5bc6\u5916\u6cc4\u3002\u201d \u3000\u3000\u738b\u5f64\u6123\u4f4f\u4e86\uff0c\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u51e1\u662f\u5df2\u88ab\u9b54\u6559\u5438\u6536\u7684\u4eba\uff0c\u90fd\u8981\u7acb\u523b\u5904\u6b7b \u4e86\uff1f\u201d \u3000\u3000\u201c\u90a3\u5012\u4e0d\u7528\uff01\u201d\u60dc\u6625\u8bf4\uff1a\u201c\u4ed6\u4eec\u53ea\u662f\u88ab\u4e00\u79cd\u9b54\u6559\u7684\u5f02\u672f\u63a7\u5236\uff0c\u542c\u4e0d\u5230\u9b54\u97f3\u53ec\u5524\u5c31\u4e0d\u4f1a\u53d1 \u4f5c\uff0c\u4f46\u4e3a\u4e86\u5b89\u5168\u4e9b\uff0c\u6700\u597d\u6682\u65f6\u628a\u4ed6\u4eec\u56da\u7981\u8d77\u6765\u3002\uff1a\u738b\u5f64\u9053\uff1a\u201d\u7ec8\u8eab\u5165\u72f1\uff0c\u5012\u4e0d\u5982\u6740\u4e86\u4ed6\u4eec\u597d \u4e9b\u3002\u201d \u3000\u3000\u201c\u7528\u4e0d\u7740\u7ec8\u8eab\u76d1\u7981\uff0c\u4ed6\u4eec\u53ea\u662f\u795e\u5fd7\u53d7\u5230\u63a7\u5236\uff0c\u6709\u4e2a\u4e00\u5e74\u534a\u8f7d\uff0c\u4e0d\u95fb\u9b54\u97f3\u53ec\u5524\uff0c\u795e\u5fd7\u5b8c\u5168 \u6062\u590d\u5c31\u53ef\u4ee5\u8131\u79bb\u63a7\u5236\u4e86\u3002\u201d \u3000\u3000\u201c\u9b54\u6559\u5f02\u672f\uff0c\u5982\u6b64\u7684\u53ef\u6016\u2026\u2026\u201d\u738b\u5f64\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u771f\u53eb\u4eba\u9632\u4e0d\u80dc\u9632\u4e86\uff01\u201d \u3000\u3000\u201c\u5927\u4eba\uff01\u201d\u60dc\u6625\u9053\uff1a\u201c\u6211\u4e0d\u80fd\u5e2e\u4f60\uff0c\u4f46\u6211\u4f1a\u5c3d\u5feb\u79bb\u5f00\u4eac\u57ce\uff0c\u4ee5\u540e\uff0c\u8bb8\u8fd8\u6709\u89c1\u9762\u7684\u673a \u4f1a\u3002\u201d\u8f6c\u8eab\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u201c\u60dc\u6625\uff01\u201d\u71d5\u98de\u7a81\u7136\u8bf4\u9053\uff1a\u201c\u80fd\u4e0d\u80fd\u544a\u8bc9\u6211\u4eec\uff0c\u5185\u5bab\u4f8d\u536b\u4e2d\u6709\u591a\u5c11\u4eba\u88ab\u9b54\u6559\u5438\u6536\u8fc7 \u53bb\uff1f\u201d \u3000\u3000\u201c\u4e09\u516c\u4e3b\u7684\u6392\u62d2\u529b\u91cf\u5f88\u5927\uff0c\u5e76\u672a\u653e\u624b\u65bd\u4e3a\uff0c\u6240\u4ee5\u5438\u6536\u7684\u4eba\u4e0d\u662f\u5f88\u591a\uff0c\u4e00\u5171\u6709\u4e03\u4e2a\u4eba\u5427\uff01 \u4f46\u6211\u89c1\u8fc7\u7684\u53ea\u6709\u4e09\u4e2a\uff0c\u91d1\u5929\u7965\u3001\u8c2d\u4fe1\uff0c\u8fd8\u6709\u4e00\u4e2a\u53eb\u79e6\u80dc\u7684\uff0c\u5982\u82e5\u5c0f\u71d5\u5b50\u80fd\u591f\u89e3\u53bb\u4e09\u516c\u4e3b\u8eab\u4e0a \u7684\u7981\u5236\uff0c\u6211\u76f8\u4fe1\u4e09\u516c\u4e3b\u4f1a\u544a\u8bc9\u4f60\uff0c\u5927\u4eba\uff01\u6211\u8fd8\u662f\u9b54\u6559\u5f1f\u5b50\uff0c\u4e0d\u80fd\u8bf4\u5f97\u592a\u591a\u3002\u201d\u60dc\u6625\u8eac\u8eab\u4e00 \u793c\uff0c\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u5f53\u5979\u5c06\u8d70\u8fd1\u95e8\u53e3\u65f6\uff0c\u7a81\u7136\u53c8\u8f6c\u4e86\u56de\u6765\uff0c\u9053\uff1a\u201c\u5c0f\u71d5\u5b50\u662f\u4f60\u552f\u4e00\u7684\u5e0c\u671b\uff0c\u4e5f\u8bb8\u4ed6\u548c\u4e09\u516c\u4e3b \u8054\u624b\uff0c\u80fd\u6d88\u9664\u4e2d\u539f\u6b66\u6797\u7684\u4e00\u6b21\u5927\u6d69\u52ab\u3002\u201d \u3000\u3000\u4f59\u97f3\u672a\u7edd\uff0c\u4eba\u8e2a\u5df2\u674f\u3002 \u3000\u3000\u738b\u5f64\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u90fd\u662f\u6700\u597d\u7684\u4eba\uff0c\u79e6\u80dc\u51fa\u8eab\u534e\u5c71\u6d3e\uff0c\u5251\u672f\u9020\u8be3\u5f88\u9ad8\u2026\u2026\u201d \u3000\u3000\u71d5\u98de\u9053\uff1a\u201c\u5927\u4eba\uff0c\u5148\u52a8\u624b\u5236\u4f4f\u4ed6\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u544a\u8bc9\u97e9\u6d9b\uff0c\u7cbe\u9009\u4e8c\u5341\u540d\u4f8d\u536b\uff0c\u6697\u4e2d\u76d1\u63a7\uff0c\u8981\u628a\u4f59\u4e0b\u7684\u56db\u4e2a\u4eba\u5c3d\u5feb\u7684\u627e\u51fa\u6765\uff0c \u8d70\uff01\u54b1\u4eec\u4e5f\u56de\u53bb\u5427\u3002\u201d \u3000\u3000\u9648\u5b8f\u9053\uff1a\u201c\u91d1\u5929\u7965\u5462\uff1f\u201d \u3000\u3000\u201c\u5bc4\u62bc\u5728\u5211\u90e8\u5927\u7262\uff01\u201d\u738b\u5f64\u4e0b\u4ee4\u540e\uff0c\u7acb\u523b\u8d76\u56de\u5185\u5bab\u3002 \u5367\u9f99\u751f\u300a\u71d5\u5b50\u4f20\u5947\u300b \u7b2c\u5341\u516d\u56de\u3000\u65bd\u6551\u516c\u4e3b \u3000\u3000\u9ed1\u7f57\u6c49\u548c\u767d\u7fbd\u9053\u957f\u90fd\u6362\u4e86\u4f8d\u536b\u7684\u8863\u7740\uff0c\u548c\u738b\u91cd\u5c71\u3001\u5c0f\u71d5\u5b50\u56db\u4e2a\u4eba\u62a4\u536b\u7740\u4e09\u516c\u4e3b\uff0c\u8fdb\u5165\u4e86 \u542c\u8749\u9662\u5185\u3002 \u3000\u3000\u4e00\u8def\u4e0a\uff0c\u5c0f\u71d5\u5b50\u5df2\u548c\u4e09\u516c\u4e3b\u5546\u91cf\u597d\u5bf9\u4ed8\u4e8c\u4e2a\u9b54\u6559\u957f\u8001\u7684\u8ba1\u5212\uff0c\u7ea6\u5b9a\u597d\u4e86\u52a8\u624b\u7684\u6697\u53f7\uff0c\u4e09 \u516c\u4e3b\u6697\u53f7\u4e00\u51fa\uff0c\u5c31\u6765\u4e2a\u5148\u53d1\u5236\u4eba\u3002 \u3000\u3000\u542c\u8749\u9662\u5e76\u4e0d\u592a\u5927\uff0c\u4f46\u4e09\u516c\u4e3b\u8fdb\u4e86\u542c\u8749\u9662\u540e\uff0c\u7adf\u7136\u5df2\u4e0d\u89c1\u4e24\u4e2a\u9b54\u6559\u957f\u8001\u3002 \u3000\u3000\u516b\u4e2a\u7167\u987e\u4e09\u516c\u4e3b\u751f\u6d3b\u7684\u5bab\u5973\uff0c\u96c6\u4e2d\u4e8e\u82b1\u8f69\u4e4b\u5185\uff0c\u867d\u7136\u90fd\u8fd8\u6d3b\u7740\uff0c\u4f46\u4e00\u4e2a\u4e2a\u9762\u76ee\u5446\u677f\uff0c\u795e \u60c5\u6728\u7136\uff0c\u4e00\u770b\u5373\u77e5\u53d7\u4e00\u79cd\u5f02\u672f\u7684\u4f24\u5bb3\u3002 \u3000\u3000\u63a7\u5236\u3002 \u3000\u3000\u5979\u4eec\u5206\u5750\u5728\u82b1\u8f69\u7684\u6728\u6905\u4e0a\uff0c\u6709\u5982\u6ce5\u5851\u7684\u5f62\u50cf\u4e00\u822c\u3002 \u3000\u3000\u201c\u597d\u50cf\uff0c\u5df2\u6709\u8baf\u606f\u4f20\u5165\u4e86\u542c\u8749\u9662\u2026\u2026\u201d\u738b\u91cd\u5c71\u8bf4\uff1a\u201c\u4e24\u4e2a\u9b54\u6559\u957f\u8001\u5df2\u7136\u79bb\u53bb\u2026\u2026\u201d \u3000\u3000\u201c\u4ed6\u4eec\u8fd8\u7559\u5728\u8fd9\u91cc\uff0c\u6ca1\u6709\u79bb\u5f00\u2026\u2026\u201d\u9ed1\u7f57\u6c49\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u8bf7\u60f3\u4e00\u4e0b\uff0c\u4ed6\u4eec\u53ef\u80fd\u85cf\u5728\u4ec0\u4e48 \u5730\u65b9\uff1f\u201d \u3000\u3000\u4e09\u516c\u4e3b\u76ee\u5149\u8f6c\u52a8\uff0c\u56db\u4e0b\u63a2\u89c6\u4e00\u9635\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5c0f\u71d5\u5b50\uff0c\u542c\u5230\u4e00\u79cd\u5c16\u5578\u7684\u54e8\u97f3\uff0c\u5c31\u5c3d\u5feb\u70b9 \u6211\u7a74\u9053\uff0c\u5343\u4e07\u8bb0\u7262\uff01\u201d \u3000\u3000\u71d5\u6625\u98ce\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u516c\u4e3b\uff0c\u8fd9\u4e9b\u5bab\u5973\u4f1a\u4e0d\u4f1a\u6b66\u529f\uff1f\u201d \u3000\u3000\u6240\u6709\u7684\u4eba\u7684\u76ee\u5149\u90fd\u56db\u4e0b\u63a2\u89c6\uff0c\u4ee5\u627e\u51fa\u4e24\u4e2a\u9b54\u6559\u957f\u8001\u7684\u85cf\u8eab\u4e4b\u5904\uff1f \u3000\u3000\u53ea\u6709\u5c0f\u71d5\u5b50\u4e24\u9053\u773c\u795e\uff0c\u4e0d\u505c\u5730\u5728\u516b\u4e2a\u5bab\u5973\u8eab\u4e0a\u6253\u8f6c\u3002 \u3000\u3000\u5979\u4eec\u90fd\u662f\u5e72\u4e2d\u9009\u4e00\u7684\u7f8e\u5973\uff0c\u4e2a\u4e2a\u90fd\u5177\u6709\u76f8\u5f53\u7684\u59ff\u8272\uff0c\u5927\u6982\u662f\u540c\u4e00\u6279\u9009\u5165\u5bab\u4e2d\u7684\u7f8e\u5973\uff0c\u5e74 \u7eaa\u60f3\u4eff\uff0c\u90fd\u662f\u4e8c\u5341\u5de6\u53f3\u7684\u5e74\u9f84\u3002 \u3000\u3000\u4e8c\u5341\u5c81\u5bf9\u4e00\u4e2a\u5bab\u5973\u800c\u8a00\uff0c\u5df2\u7b97\u662f\u5f88\u8d44\u6df1\u4e86\uff0c\u5728\u5bab\u4e2d\u81f3\u5c11\u6709\u4e09\u5e74\u4ee5\u4e0a\u7684\u65f6\u95f4\u3002 \u3000\u3000\u201c\u5b66\u8fc7\u4e00\u4e9b\u7c97\u6d45\u7684\u6b66\u529f\u2026\u2026\u201d\u4e09\u516c\u4e3b\u8bf4\uff1a\u201c\u90a3\u53ea\u662f\u4e3a\u4e86\u5f3a\u8eab\u4e4b\u7528\uff0c\u7ec3\u4e9b\u6c14\u529b\uff0c\u4e5f\u65b9\u4fbf\u5979 \u4eec\u7684\u5de5\u4f5c\u3002\u201d \u3000\u3000\u201c\u5979\u4eec\u7684\u8eab\u624b\u6bd4\u601c\u82b1\u59d0\u59d0\u5982\u4f55\uff1f\u201d\u5c0f\u71d5\u5b50\u7a81\u7136\u8f6c\u8fc7\u8bdd\u9898\uff0c\u53cc\u76ee\u70af\u70af\u7684\u6295\u6ce8\u5728\u601c\u82b1\u8eab\u4e0a\u3002 \u3000\u3000\u201c\u601c\u82b1\u5230\u897f\u57df\u4e4b\u540e\u624d\u4e60\u7ec3\u6b66\u529f\uff0c\u5927\u90fd\u662f\u6211\u4eb2\u81ea\u4f20\u6388\u7684\uff0c\u5f53\u7136\uff0c\u4e5f\u8ddf\u60dc\u6625\u7ec3\u8fc7\u4e00\u4e9b\u9b54\u6559\u8eab \u6cd5\u3002\u201d\u4e09\u516c\u4e3b\u4ee3\u66ff\u601c\u82b1\u4f5c\u4e86\u56de\u7b54\u3002 \u3000\u3000\u201c\u539f\u6765\u5982\u6b64\uff01\u201d\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u601c\u82b1\u59d0\u59d0\uff0c\u4e0d\u8981\u89c1\u602a\uff0c\u4ed6\u4eec\u544a\u8bc9\u6211\u4f60\u5b8c\u5168\u4e0d\u4f1a\u6b66\u529f\uff0c\u4f46\u5c31 \u5c0f\u5f1f\u770b\uff0c\u4f60\u4e00\u8eab\u6b66\u529f\u4e0d\u5728\u4e00\u822c\u7684\u4f8d\u536b\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u201c\u516c\u4e3b\u4f20\u4e86\u6211\u6b63\u5b97\u5fc3\u6cd5\uff0c\u6211\u65e5\u591c\u82e6\u7ec3\uff0c\u624d\u4fdd\u4f4f\u4e86\u7075\u667a\u4e0d\u6627\uff0c\u6ca1\u6709\u6ca6\u5165\u9b54\u9053\u3002\u201d\u601c\u82b1\u9053\uff1a \u201c\u5f53\u7136\uff0c\u4e09\u516c\u4e3b\u5168\u529b\u7ef4\u62a4\u3001\u6297\u4e89\uff0c\u624d\u662f\u6211\u672a\u8fdb\u5165\u9b54\u6559\u7684\u6700\u5927\u539f\u56e0\u3002\u201d \u3000\u3000\u201c\u601c\u82b1\u59d0\u59d0\uff0c\u4f60\u4ed4\u7ec6\u7684\u770b\u770b\u8fd9\u4e9b\u5bab\u5973\uff0c\u5979\u4eec\u662f\u4e0d\u662f\u672c\u4eba\u2026\u2026\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u8981\u4ed4\u7ec6\u554a\uff01\u83ab\u8981\u88ab\u5979\u4eec\u7684\u6613\u5bb9\u672f\u7ed9\u9a97\u4e86\u3002\u201d \u3000\u3000\u601c\u82b1\u5fae\u5fae\u4e00\u6014\uff0c\u5f00\u59cb\u68c0\u67e5\u516b\u4e2a\u5bab\u5973\u3002 \u3000\u3000\u5979\u770b\u5f97\u5f88\u4ed4\u7ec6\uff0c\u5168\u8eab\u90fd\u641c\u67e5\u5f97\u5f88\u6e05\u695a\u3002 \u3000\u3000\u201c\u786e\u662f\u5979\u4eec\u672c\u4eba\u2026\u2026\u201d\u601c\u82b1\u8bf4\uff1a\u201c\u4e5f\u90fd\u8fd8\u597d\u597d\u7684\u6d3b\u7740\uff0c\u5947\u602a\u7684\u662f\uff0c\u5979\u4eec\u8eab\u4e0a\u4f3c\u662f\u6563\u53d1\u51fa \u4e00\u80a1\u602a\u5f02\u6c14\u606f\u2026\u2026\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6025\u6025\u8bf4\u9053\uff1a\u201c\u662f\u4e0d\u662f\u4e00\u80a1\u6de1\u6de1\u8165\u5473\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff01\u201d\u601c\u82b1\u9053\uff1a\u201c\u5979\u4eec\u7684\u5185\u8179\u4e4b\u4e2d\uff0c\u597d\u50cf\u8fd8\u6709\u4e00\u80a1\u6c14\u5728\u6d41\u52a8\u3002\u201d \u3000\u3000\u201c\u5feb\uff0c\u70b9\u5979\u4eec\u7684\u6655\u7a74\u2026\u2026\u201d\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u8ba9\u5979\u4eec\u719f\u7761\u8fc7\u53bb\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u601c\u82b1\u6709\u4e9b\u4e0d\u89e3\u3002 \u3000\u3000\u5c31\u662f\u8fd9\u4e00\u523b\u7684\u803d\u8bef\uff0c\u4e00\u7f15\u67d4\u7ec6\u7684\u7b1b\u58f0\uff0c\u4f20\u5165\u4e86\u8033\u9645\u3002 \u3000\u3000\u516b\u540d\u5446\u5750\u7684\u5bab\u5973\uff0c\u7a81\u7136\u7741\u5f00\u4e86\u53cc\u76ee\uff0c\u624b\u811a\u6325\u52a8\uff0c\u970d\u7136\u7ad9\u8d77\u3002 \u3000\u3000\u53d8\u5316\u8fde\u7eed\u53d1\u751f\uff0c\u5c0f\u71d5\u5b50\u4e5f\u4e0d\u7981\u5fae\u5fae\u4e00\u6014\uff0c\u4e0d\u77e5\u8be5\u5148\u5bf9\u4ed8\u8fd9\u516b\u4e2a\u5bab\u5973\uff0c\u6216\u662f\u5148\u6251\u6740\u5439\u7b1b\u7684 \u4eba\uff1f \u3000\u3000\u5c04\u4eba\u5148\u5c04\u9a6c\uff0c\u64d2\u8d3c\u5148\u64d2\u738b\u3002\u5c0f\u71d5\u5b50\u98de\u8eab\u800c\u8d77\uff0c\u6251\u5411\u4e00\u7247\u82b1\u6811\u4e1b\u53bb\uff0c\u51c6\u5907\u5148\u6293\u4f4f\u5439\u7b1b\u7684\u4eba \u518d\u8bf4\u3002 \u3000\u3000\u90a3\u91cc\u662f\u7b1b\u58f0\u4f20\u6765\u7684\u6240\u5728\u3002 \u3000\u3000\u5c31\u5728\u5c0f\u71d5\u5b50\u98de\u8eab\u800c\u8d77\u7684\u540c\u65f6\uff0c\u4e00\u9635\u5c16\u5389\u7684\u54e8\u58f0\uff0c\u7a81\u7136\u54cd\u8d77\uff0c\u4f20\u5165\u4e86\u8033\u9645\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u7684\u8138\u8272\u4e00\u53d8\uff0c\u7acb\u523b\u76d8\u819d\u5750\u4e0b\u3002 \u3000\u3000\u5c0f\u9053\u58eb\u4e00\u4e2a\u60ac\u7a7a\u7b4b\u6597\uff0c\u7ffb\u5165\u82b1\u8f69\uff0c\u957f\u5251\u51fa\u9798\u5b88\u5728\u4e09\u516c\u4e3b\u7684\u8eab\u4fa7\u3002 \u3000\u3000\u601c\u82b1\u542c\u5429\u5490\u51fa\u624b\u70b9\u7a74\uff0c\u4f46\u53ea\u70b9\u4e2d\u4e24\u4e2a\uff0c\u5176\u4f59\u7684\u516d\u4e2a\u5bab\u5973\u5df2\u5f00\u59cb\u884c\u52a8\uff0c\u7c89\u62f3\u8fde\u7ef5\uff0c\u51fb\u5411\u601c \u82b1\u3002 \u3000\u3000\u8fd9\u4e9b\u5bab\u5973\u672c\u53ea\u662f\u7ec3\u8fc7\u4e00\u4e9b\u5f3a\u8eab\u7684\u672c\u9886\uff0c\u4f46\u6b64\u523b\u4e00\u51fa\u624b\uff0c\u5374\u662f\u653b\u52bf\u731b\u70c8\uff0c\u5c24\u5176\u662f\u51fb\u51fa\u7684\u62f3 \u52bf\u3001\u529b\u91cf\u5341\u5206\u60ca\u4eba\u3002 \u3000\u3000\u5f80\u65e5\u91cc\uff0c\u601c\u82b1\u8981\u5bf9\u4ed8\u8fd9\u516d\u4e2a\u5bab\u5973\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e3e\u624b\u4e4b\u52b3\uff0c\u4f46\u6b64\u523b\uff0c\u5374\u88ab\u516d\u4e2a\u5bab\u5973\u903c\u5f97\u5168\u529b \u62db\u67b6\u3002 \u3000\u3000\u5979\u4eec\u7684\u8138\u4e0a\u6c81\u51fa\u6c57\u6c34\uff0c\u4f3c\u662f\u751f\u547d\u4e2d\u7684\u6f5c\u529b\uff0c\u6b63\u88ab\u4e00\u79cd\u529b\u91cf\u6fc0\u53d1\u51fa\u6765\u3002 \u3000\u3000\u738b\u91cd\u5c71\u3001\u9ed1\u7f57\u6c49\u7d27\u968f\u7740\u8dc3\u5165\u82b1\u8f69\uff0c\u6253\u91cf\u4e86\u8f69\u4e2d\u60c5\u52bf\u4e00\u773c\uff0c\u9ed1\u7f57\u6c49\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5c0f\u738b\uff0c\u5e2e \u52a9\u601c\u82b1\uff0c\u5148\u628a\u51e0\u4e2a\u5bab\u5973\u653e\u5012\uff0c\u6211\u53bb\u63a5\u5e94\u5c0f\u71d5\u5b50\u3002\u201d \u3000\u3000\u5c31\u5728\u9ed1\u7f57\u6c49\u98de\u8eab\u79bb\u53bb\u65f6\uff0c\u4e09\u516c\u4e3b\u4e5f\u540c\u65f6\u7ad9\u4e86\u8d77\u6765\uff0c\u7f13\u7f13\u62bd\u51fa\u957f\u5251\u3002 \u3000\u3000\u201c\u5c0f\u5fc3\u516c\u4e3b\u2026\u2026\u201d\u601c\u82b1\u65e9\u5df2\u6599\u77e5\u4e09\u516c\u4e3b\u5fc5\u6709\u53cd\u5e94\uff0c\u4e00\u76f4\u5728\u6697\u4e2d\u7559\u5fc3\u7740\uff0c\u89c1\u5979\u5b9d\u5251\u51fa\u9798\uff0c \u7acb\u65f6\u547c\u53eb\u51fa\u58f0\u3002 \u3000\u3000\u5c0f\u9053\u58eb\u767d\u7fbd\u662f\u6b66\u5f53\u6d3e\u540e\u8d77\u4e4b\u79c0\u4e2d\u7b2c\u4e00\u9ad8\u624b\uff0c\u8eab\u5b50\u4e00\u8f6c\uff0c\u9762\u5bf9\u4e09\u516c\u4e3b\uff0c\u4f46\u4e09\u516c\u4e3b\u7684\u5b9d\u5251\u5df2 \u523a\u4e86\u8fc7\u6765\uff0c\u5c0f\u9053\u58eb\u957f\u5251\u65e9\u5df2\u5728\u624b\uff0c\u626c\u624b\u67b6\u4f4f\u5251\u52bf\uff0c\u9053\uff1a\u201c\u516c\u4e3b\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u4e09\u516c\u4e3b\u5251\u5982\u95ea\u7535\uff0c\u5c0f\u9053\u58eb\u4e00\u53e5\u8bdd\u8fd8\u672a\u8bf4\u5b8c\uff0c\u4e09\u516c\u4e3b\u5df2\u653b\u51fa\u4e86\u4e03\u5251\uff0c\u628a\u767d\u7fbd\u8feb\u9000\u4e86\u4e24\u6b65\u3002 \u3000\u3000\u8fd9\u5c31\u6fc0\u8d77\u4e86\u5c0f\u9053\u58eb\u7684\u597d\u80dc\u4e4b\u5fc3\uff0c\u5fd6\u9053\uff1a\u4e00\u9053\u98de\u8679\u82df\u6167\u6708\u88ab\u8a89\u79f0\u4e2d\u539f\u6b66\u6797\u4e2d\u7b2c\u4e00\u5973\u5251\u5ba2\uff0c \u4f60\u662f\u5979\u95e8\u4e0b\u5f1f\u5b50\uff0c\u4f46\u4f60\u8fd9\u516c\u4e3b\u8eab\u4efd\u4f7f\u4f60\u9ad8\u9ad8\u5728\u4e0a\uff0c\u60f3\u548c\u4f60\u8fc7\u624b\u51e0\u62db\uff0c\u5b9e\u975e\u6613\u4e8b\uff0c\u96be\u5f97\u4eca\u65e5\u6709 \u6b64\u673a\u4f1a\uff0c\u5012\u8981\u9886\u6559\u4e00\u4e0b\u4e86\u3002 \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u624b\u4e2d\u957f\u5251\u52a0\u7d27\u53cd\u51fb\u3002 \u3000\u3000\u5239\u65f6\u95f4\uff0c\u5bd2\u8292\u98de\u65cb\uff0c\u53ea\u89c1\u4e24\u9053\u767d\u5149\u98de\u8f6c\u4ea4\u51fb\uff0c\u51db\u70c8\u7684\u5251\u6c14\u4f7f\u5f97\u601c\u82b1\u8863\u8882\u98d8\u52a8\uff0c\u808c\u80a4\u751f \u5bd2\uff0c\u5fcd\u4e0d\u4f4f\u5927\u58f0\u53eb\u9053\uff1a\u201c\u516c\u4e3b\uff0c\u4f60\u4e0d\u80fd\u4f24\u4e86\u9053\u957f\u554a\uff0c\u5c0f\u9053\u957f\uff0c\u516c\u4e3b\u662f\u5fc3\u795e\u53d7\u5236\uff0c\u8eab\u4e0d\u7531\u5df1\uff0c \u4f60\u8981\u624b\u4e0b\u7559\u60c5\u3002\u201d \u3000\u3000\u6fc0\u70c8\u7684\u640f\u6218\uff0c\u62db\u62db\u90fd\u53ef\u80fd\u5236\u4eba\u4e8e\u6b7b\uff0c\u5251\u5251\u90fd\u53ef\u80fd\u51fb\u4e2d\u8981\u5bb3\uff0c\u5c31\u7b97\u6709\u4eba\u60f3\u624b\u4e0b\u7559\u60c5\uff0c\u4e5f\u662f \u6709\u6240\u4e0d\u80fd\u3002 \u3000\u3000\u5c0f\u9053\u58eb\u76ee\u524d\u7684\u5904\u5883\uff0c\u5c31\u662f\u5982\u6b64\uff0c\u4ed6\u5df2\u5c3d\u5c55\u6240\u5b66\uff0c\u4f46\u4e09\u516c\u4e3b\u5251\u6cd5\u7684\u51cc\u5389\uff0c\u5927\u51fa\u4ed6\u610f\u6599\u4e4b \u5916\uff0c\u4e0d\u7981\u6c14\u52bf\u4e00\u632b\uff0c\u6697\u9053\uff1a\u76db\u540d\u4e4b\u4e0b\u65e0\u865a\u58eb\uff0c\u4e00\u9053\u98de\u8679\u7684\u5a01\u540d\u679c\u7136\u4e0d\u5c0f\u3002 \u3000\u3000\u4f46\u89c9\u5de6\u81c2\u4e00\u51c9\uff0c\u9c9c\u8840\u900f\u8863\u800c\u51fa\u3002 \u3000\u3000\u539f\u6765\uff0c\u5c0f\u9053\u957f\u5de6\u81c2\u4e2d\u4e86\u4e00\u5251\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u662f\u767d\u7fbd\u51fa\u8eab\u4ee5\u6765\uff0c\u7b2c\u4e00\u6b21\u53d7\u5230\u7684\u4f24\u5bb3\uff0c\u4f46\u4e5f\u6fc0\u8d77\u4e86\u4ed6\u7684\u62fc\u547d\u4e4b\u5fc3\uff0c\u957f\u5578\u4e00\u58f0\uff0c\u7edd \u62db\u75be\u51fa\uff0c\u4e00\u8fde\u4e09\u5251\uff0c\u5012\u662f\u628a\u4e09\u516c\u4e3b\u7684\u5251\u5c01\u4f4f\u4e86\u3002 \u3000\u3000\u4f46\u4e09\u5251\u4e4b\u540e\uff0c\u7acb\u523b\u53c8\u4e3a\u4e09\u516c\u4e3b\u7684\u5251\u52bf\u7f69\u4f4f\u3002 \u3000\u3000\u6280\u5dee\u4e00\u7740\uff0c\u675f\u624b\u7f1a\u811a\uff0c\u4e09\u516c\u4e3b\u5251\u6280\u7cbe\u7edd\uff0c\u5c0f\u9053\u58eb\u867d\u5df2\u5f97\u6b66\u5f53\u5251\u6cd5\u771f\u4f20\uff0c\u4f46\u5374\u65e0\u6cd5\u963b\u6b62\u4e09 \u516c\u4e3b\u51cc\u5389\u7684\u5251\u52bf\u3002 \u3000\u3000\u9c9c\u8840\u67d3\u7ea2\u4e86\u767d\u7fbd\u9053\u888d\uff0c\u770b\u4e0a\u53bb\u602a\u53ef\u6015\u7684\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u5c0f\u9053\u58eb\u7684\u4f53\u529b\u4ea6\u6b63\u5728\u5feb\u901f\u7684\u6d88\u9000\u4e2d\uff0c\u5931\u8840\u8fc7\u591a\uff0c\u5df2\u4f7f\u5c0f\u9053\u58eb\u7684\u4f53\u529b\u4e0d\u7ee7\u3002 \u3000\u3000\u5e78\u597d\uff0c\u738b\u91cd\u5c71\u5df2\u5236\u670d\u4e86\u516d\u4e2a\u5bab\u5973\uff0c\u56de\u5934\u770b\u5c0f\u9053\u58eb\u8eab\u9677\u5371\u5883\uff0c\u968f\u65f6\u6709\u53ef\u80fd\u88ab\u4e09\u516c\u4e3b\u4e00\u5251\u523a \u6b7b\uff0c\u5fc3\u4e2d\u5927\u6025\uff0c\u6012\u559d\u4e00\u58f0\uff0c\u6325\u5251\u800c\u4e0a\u3002 \u3000\u3000\u4e24\u4eba\u82e6\u6218\u4e09\u516c\u4e3b\uff0c\u4e5f\u53ea\u662f\u6682\u65f6\u628a\u5c40\u52bf\u7a33\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u56e0\u4e3a\u4e09\u516c\u4e3b\u7684\u7cbe\u7edd\u5251\u62db\uff0c\u83ab\u6d4b\u7684\u53d8\u5316\uff0c\u7acb\u523b\u628a\u4e24\u4eba\u5708\u5165\u4e86\u5251\u5149\u4e4b\u4e2d\u3002 \u3000\u3000\u4e24\u4eba\u5408\u624b\uff0c\u4ecd\u975e\u654c\u624b\u3002 \u3000\u3000\u601c\u82b1\u671b\u7740\u6fc0\u70c8\u7684\u62fc\u6740\uff0c\u5c3d\u7ba1\u5fc3\u6025\u5982\u711a\uff0c\u4f46\u5374\u65e0\u6cd5\u5e2e\u5fd9\uff0c\u4e09\u652f\u5251\u5212\u51fa\u7684\u68ee\u5bd2\u5251\u6c14\uff0c\u6ce2\u53ca\u5230 \u4e94\u516d\u5c3a\u5916\uff0c\u6839\u672c\u65e0\u6cd5\u63a5\u8fd1\u3002 \u3000\u3000\u773c\u770b\u5230\u5c0f\u9053\u58eb\u3001\u738b\u91cd\u5c71\u5df2\u9669\u8c61\u73af\u751f\uff0c\u770b\u6837\u5b50\uff0c\u5728\u5341\u62db\u5185\u4e24\u4eba\u90fd\u6709\u53ef\u80fd\u8840\u6e85\u5251\u4e0b\u3002 \u3000\u3000\u4f46\u601c\u82b1\u7684\u558a\u53eb\u58f0\uff0c\u5374\u62db\u6765\u4e86\u9ed1\u7f57\u6c49\u3002 \u3000\u3000\u76ee\u7779\u5f62\u52bf\uff0c\u9ed1\u7f57\u6c49\u4e0d\u7981\u4e00\u5446\uff0c\u6765\u4e0d\u53ca\u518d\u95ee\u601c\u82b1\uff0c\u7acb\u523b\u52a0\u5165\u6218\u5708\u3002 \u3000\u3000\u5c0f\u548c\u5c1a\u7528\u7684\u5175\u5203\u4e0d\u662f\u6212\u5200\uff0c\u4e5f\u4e0d\u662f\u7985\u6756\uff0c\u800c\u662f\u4e03\u4e2a\u8854\u63a5\u7684\u91d1\u73af\uff0c\u540d\u5b57\u53eb\u4f5c\u4e03\u5de7\u73af\u3002 \u3000\u3000\u91d1\u73af\u7528\u6d3b\u53e3\u8854\u63a5\uff0c\u53ef\u4ee5\u5728\u640f\u6597\u4e2d\uff0c\u677e\u5f00\u6d3b\u53e3\uff0c\u98de\u51fa\u4f24\u4eba\uff0c\u53ef\u4ee5\u62c6\u5f00\u65bd\u5c55\uff0c\u4e5f\u53ef\u5408\u5728\u4e00\u5904 \u5f53\u8f6f\u97ad\u5bf9\u654c\u3002 \u3000\u3000\u90a3\u662f\u4e00\u79cd\u6781\u5177\u53d8\u5316\u4e4b\u5999\u7684\u5175\u5203\u3002 \u3000\u3000\u5c0f\u548c\u5c1a\u91d1\u73af\u821e\u52a8\uff0c\u63a5\u4e0b\u4e86\u4e09\u516c\u4e3b\u7684\u5251\u52bf\uff0c\u4e00\u9762\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u5c0f\u9053\u58eb\uff0c\u5feb\u4e9b\u4e0b\u53bb\uff0c\u5305\u624e\u4e00 \u4e0b\u4f24\u52bf\u3002\u201d \u3000\u3000\u5c0f\u9053\u58eb\u5012\u662f\u4e0d\u518d\u901e\u5f3a\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u516c\u4e3b\u5251\u62db\u53d8\u5316\u83ab\u6d4b\uff0c\u4f60\u8981\u5c0f\u5fc3\u70b9\u3002\u201d\u75be\u5feb\u9000\u4e86\u4e0b \u53bb\u3002 \u3000\u3000\u9ed1\u7f57\u6c49\u91d1\u73af\u62a1\u5f00\uff0c\u5e26\u8d77\u5f3a\u52b2\u7684\u98ce\uff0c\u5b8c\u5168\u662f\u4e00\u526f\u5f3a\u653b\u786c\u6253\u7684\u67b6\u52bf\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u5c0f\u548c\u5c1a\u4e5f\u6697\u5b58\u4e86\u4e00\u8bd5\u516c\u4e3b\u5251\u62db\u7684\u7528\u5fc3\u3002 \u3000\u3000\u4e00\u9053\u98de\u8679\u82df\u6167\u6708\u5728\u6b66\u6797\u4e2d\u7684\u58f0\u8a89\uff0c\u4e00\u5ea6\u51cc\u9a7e\u4e8e\u5c11\u6797\u3001\u6b66\u5f53\u4e4b\u4e0a\uff0c\u8fd9\u5c31\u4f7f\u5c0f\u548c\u5c1a\u5fc3\u4e2d\u6709\u4e9b \u4e0d\u670d\u3002 \u3000\u3000\u8fd9\u79cd\u6f5c\u9690\u7684\u610f\u8bc6\uff0c\u5982\u82e5\u6ca1\u6709\u673a\u4f1a\uff0c\u90a3\u5c31\u4e00\u751f\u4e0d\u4f1a\u8868\u793a\u51fa\u6765\uff0c\u56e0\u4e3a\uff0c\u9ed1\u7f57\u6c49\u4e0d\u53ef\u80fd\u53bb\u627e\u82df \u6167\u6708\uff0c\u8981\u6c42\u5370\u8bc1\u6b66\u529f\uff0c\u4f55\u51b5\uff0c\u5c0f\u548c\u5c1a\u8fd9\u4e2a\u8eab\u4efd\u4e5f\u4e0d\u591f\u5411\u82df\u6167\u6708\u6311\u6218\u7684\u8d44\u683c\u3002 \u3000\u3000\u4f46\u4eca\u65e5\uff0c\u9047\u4e0a\u4e09\u516c\u4e3b\u8fd9\u4e2a\u8eab\u4efd\uff0c\u5c0f\u548c\u5c1a\u6f5c\u5728\u7684\u610f\u8bc6\u53d1\u4f5c\u4e86\uff0c\u501f\u673a\u4f1a\u9886\u6559\u4e00\u4e0b\u5251\u62db\u7684\u5fc3 \u613f\uff0c\u7acb\u523b\u840c\u751f\u51fa\u6765\uff0c\u800c\u4e14\u5341\u5206\u5f3a\u70c8\uff0c\u65e0\u6cd5\u904f\u6b62\uff0c\u4e00\u51fa\u624b\u5c31\u4eae\u51fa\u4e86\u770b\u5bb6\u7684\u672c\u9886\u3002 \u3000\u3000\u8fd9\u4e03\u4e2a\u91d1\u73af\u5404\u91cd\u4e8c\u65a4\uff0c\u4e03\u73af\u5408\u8ba1\u518d\u52a0\u4e0a\u8fde\u63a5\u94a2\u6263\uff0c\u8db3\u8db3\u6709\u5341\u516d\u65a4\u91cd\uff0c\u867d\u7136\u7b97\u4e0d\u4e0a\u91cd\u5175 \u5203\uff0c\u4f46\u6bd4\u4e00\u822c\u7684\u5200\u5251\u5374\u662f\u91cd\u4e86\u8bb8\u591a\uff0c\u5c0f\u548c\u5c1a\u4ee5\u5168\u529b\u65bd\u5c55\uff0c\u5012\u4e5f\u6709\u6a2a\u626b\u5343\u519b\u7684\u6c14\u52bf\uff0c\u4e00\u9762\u4f4e\u58f0 \u8bf4\u9053\uff1a\u201c\u5c0f\u738b\uff0c\u4e0b\u53bb\u7167\u987e\u767d\u8138\u9053\u58eb\u7684\u4f24\uff0c\u4e09\u516c\u4e3b\u4ea4\u7ed9\u6211\u5c0f\u548c\u5c1a\u4e86\u3002\u201d \u3000\u3000\u738b\u91cd\u5c71\u65e9\u5df2\u7528\u5c3d\u4e86\u7cbe\u5947\u7684\u5251\u62db\uff0c\u5185\u5fc3\u4e2d\u4ea6\u65e9\u5df2\u627f\u8ba4\u4e0d\u662f\u4e09\u516c\u4e3b\u7684\u654c\u624b\uff0c\u4f55\u51b5\uff0c\u5251\u672f\u9ad8\u8fc7 \u4ed6\u751a\u591a\u7684\u5e08\u5144\uff0c\u5df2\u4f24\u5728\u5bf9\u65b9\u7684\u5251\u4e0b\uff0c\u4e0d\u9000\u4e0b\u53bb\u4e5f\u5e2e\u4e0d\u4e86\u5c0f\u548c\u5c1a\u591a\u5927\u7684\u5fd9\uff0c\u5f53\u4e0b\u4e00\u6536\u5251\u52bf\uff0c\u9000 \u51fa\u4e86\u6218\u5708\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u601c\u82b1\u6b63\u5728\u5e2e\u5c0f\u9053\u58eb\u5305\u624e\u4f24\u52bf\uff0c\u5251\u4f24\u5e76\u4e0d\u592a\u91cd\uff0c\u53ea\u662f\u4f24\u540e\u4ecd\u7136\u529b\u6218\u4e0d\u9000\uff0c\u5931\u8840\u8fc7 \u591a\uff0c\u4f7f\u5c0f\u9053\u58eb\u7684\u5143\u6c14\u5927\u4f24\u3002 \u3000\u3000\u539f\u672c\u6709\u4e9b\u82cd\u767d\u7684\u8138\u8272\uff0c\u66f4\u89c1\u82cd\u767d\u4e86\u3002 \u3000\u3000\u9ed1\u7f57\u6c49\u5927\u5c55\u795e\u5a01\uff0c\u91d1\u73af\u5728\u5185\u529b\u8d2f\u6ce8\u4e0b\uff0c\u6296\u5f97\u7b14\u76f4\uff0c\u786c\u5bf9\u4e09\u516c\u4e3b\u7684\u5251\u52bf\u3002 \u3000\u3000\u73af\u5251\u76f8\u51fb\uff0c\u706b\u661f\u8ff8\u5c04\uff0c\u4e09\u516c\u4e3b\u624b\u4e2d\u4e4b\u5251\u5927\u90fd\u88ab\u9707\u8361\u5f00\u53bb\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u795e\u60c5\u4f3c\u662f\u6709\u70b9\u75f4\u5446\uff0c\u7adf\u7136\u8fde\u756a\u4ee5\u957f\u5251\u5c01\u63a5\u9ed1\u7f57\u6c49\u7684\u91d1\u73af\u3002 \u3000\u3000\u76f4\u5403\u8fc7\u4e09\u6b21\u5927\u4e8f\u4e4b\u540e\uff0c\u624d\u6539\u53d8\u6253\u6cd5\u3002 \u3000\u3000\u4f46\u5251\u52bf\u4e00\u53d8\u4e4b\u540e\uff0c\u5c0f\u548c\u5c1a\u7acb\u523b\u88ab\u8feb\u843d\u4e0b\u98ce\u3002 \u3000\u3000\u5c0f\u548c\u5c1a\u4e45\u8d70\u6c5f\u6e56\uff0c\u8bba\u89c1\u8bc6\u4e4b\u5e7f\uff0c\u5b9e\u6218\u7ecf\u9a8c\u4e4b\u4e30\uff0c\u90a3\u53ef\u662f\u4e09\u516c\u4e3b\u96be\u53ca\u4e07\u4e00\uff0c\u89c1\u98ce\u8f6c\u8235\uff0c\u7acb \u523b\u6539\u91c7\u5b88\u52bf\uff0c\u540c\u65f6\uff0c\u5fc3\u4e2d\u4e5f\u660e\u767d\u4e86\u4e00\u4ef6\u4e8b\uff0c\u82df\u6167\u6708\u5b9e\u81f3\u540d\u5f52\uff0c\u4e09\u516c\u4e3b\u5251\u6cd5\u7684\u7cbe\u7edd\uff0c\u975e\u4ed6\u80fd \u53ca\uff0c\u5982\u82e5\u4e0d\u662f\u5979\u5fc3\u795e\u53d7\u5236\uff0c\u6362\u4f5c\u5e73\u5e38\u65f6\u523b\uff0c\u4ed6\u6839\u672c\u65e0\u6cd5\u63a5\u4e0b\u5bf9\u65b9\u5341\u5251\u3002 \u3000\u3000\u660e\u767d\u4e86\u654c\u6211\u5f62\u52bf\uff0c\u5c0f\u548c\u5c1a\u7684\u6253\u6cd5\u662f\u4f46\u6c42\u65e0\u8fc7\uff0c\u4e0d\u6c42\u6709\u529f\uff0c\u628a\u95e8\u6237\u5b88\u5f97\u5341\u5206\u4e25\u7d27\u3002 \u3000\u3000\u6700\u91cd\u8981\u7684\u662f\u4e09\u516c\u4e3b\u6253\u6cd5\u6709\u4e9b\u5446\u677f\uff0c\u5f88\u591a\u7684\u7cbe\u5947\u5251\u62db\uff0c\u53d8\u5316\u4e0d\u6d3b\uff0c\u624d\u4f7f\u5f97\u5c0f\u548c\u5c1a\u6682\u65f6\u4fdd\u4f4f \u4e86\u4e00\u4e2a\u52c9\u53ef\u4e0d\u8d25\u7684\u5c40\u9762\u3002 \u3000\u3000\u6b64\u523b\uff0c\u5c0f\u548c\u5c1a\u5fc3\u4e2d\u6700\u5927\u7684\u5e0c\u671b\uff0c\u662f\u5c0f\u71d5\u5b50\u5feb\u4e9b\u56de\u6765\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u5c0f\u71d5\u5b50\u5f97\u5929\u72ec\u539a\uff0c\u4e3a\u53f7\u79f0\u5929\u4e0b\u7b2c\u4e00\u5947\u4eba\u5782\u9752\uff0c\u7834\u4f8b\u6536\u5217\u95e8\u5899\uff0c\u4f20\u6388\u7edd\u6280\uff0c\u738b\u5f64\u4e00 \u679d\u5185\u5bab\u6536\u85cf\u7684\u5343\u5e74\u53c2\u738b\uff0c\u5e2e\u52a9\u4ed6\u7a81\u7834\u4e86\u65f6\u95f4\u9650\u5236\uff0c\u4e24\u5e74\u591a\u7684\u65f6\u95f4\uff0c\u6b65\u5165\u4e86\u4e0a\u4e58\u5883\u754c\u3002 \u3000\u3000\u4f46\u5c0f\u71d5\u5b50\u7684\u6280\u827a\u7a76\u7adf\u5230\u4e86\u4ec0\u4e48\u7a0b\u5ea6\uff0c\u5c0f\u548c\u5c1a\u5e76\u4e0d\u6e05\u695a\uff0c\u800c\u4e14\uff0c\u4e5f\u5f88\u60f3\u89c1\u8bc6\u4e00\u756a\u3002 \u3000\u3000\u4eca\u65e5\uff0c\u5e94\u8be5\u662f\u6700\u597d\u673a\u4f1a\u3002 \u3000\u3000\u5c0f\u9053\u58eb\u53d7\u4e86\u4f24\uff0c\u81ea\u5df1\u4e5f\u65e9\u5728\u5fc3\u4e2d\u8ba4\u8f93\uff0c\u5982\u679c\uff0c\u5c0f\u71d5\u5b50\u80fd\u80dc\u5f97\u8fc7\u4e09\u516c\u4e3b\uff0c\u4eca\u540e\uff0c\u4e3a\u4ed6\u630e\u5200 \u6548\u547d\u4e5f\u5c31\u5fc3\u7518\u60c5\u613f\u4e86\u3002 \u3000\u3000\u5c31\u5728\u5c0f\u548c\u5c1a\u5fc3\u795e\u4e0d\u5c5e\uff0c\u7565\u4e00\u95ea\u795e\u65f6\uff0c\u4e09\u516c\u4e3b\u7684\u5251\u7adf\u5982\u6bd2\u86c7\u4e00\u822c\uff0c\u7a7f\u8fc7\u4e03\u5de7\u73af\uff0c\u523a\u5411\u54bd\u5589 \u8981\u5bb3\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5c0f\u548c\u5c1a\u5df2\u5931\u53bb\u5c01\u67b6\u7684\u5148\u673a\uff0c\u552f\u4e00\u7684\u529e\u6cd5\u53ea\u6709\u95ea\u907f\u3002 \u3000\u3000\u4ed6\u7ecf\u9a8c\u4e30\u5bcc\uff0c\u4e00\u5438\u6c14\uff0c\u7acb\u523b\u5411\u540e\u9000\u51fa\u4e86\u4e09\u5c3a\u3002 \u3000\u3000\u4f46\u957f\u5251\u7adf\u5982\u5f71\u968f\u5f62\u822c\uff0c\u75be\u51b2\u800c\u8fdb\u3002 \u3000\u3000\u5c0f\u548c\u5c1a\u6697\u53eb\u4e00\u58f0\u7f62\u4e86\uff0c\u60f3\u4e0d\u5230\u6211\u548c\u5c1a\u7adf\u4f1a\u6bd9\u547d\u4e8e\u6b64\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u4ed6\u5df2\u5b8c\u5168\u65e0\u6cd5\u6446\u8131\u90a3\u523a\u5411\u54bd\u5589\u7684\u4e00\u5251\u3002 \u3000\u3000\u738b\u91cd\u5c71\u770b\u5230\u4e86\u5c0f\u548c\u5c1a\u7684\u5371\u673a\uff0c\u5c0f\u9053\u58eb\u4e5f\u770b\u5230\u4e86\uff0c\u53ea\u4e0d\u8fc7\uff0c\u4e24\u4e2a\u4eba\u5374\u65e0\u6cd5\u63f4\u6551\uff0c\u53d8\u5316\u592a\u5feb \u4e86\uff0c\u5feb\u5f97\u4ee4\u4eba\u63aa\u624b\u4e0d\u53ca\u3002 \u3000\u3000\u4f46\u5c0f\u71d5\u5b50\u6b64\u65f6\u98de\u8fdb\u6765\u4e86\uff0c\u5feb\u5f97\u6bd4\u771f\u7684\u71d5\u5b50\u6284\u6c34\u8fd8\u5feb\uff0c\u5feb\u5f97\u5982\u4e00\u9635\u75be\u98ce\u3002 \u3000\u3000\u5c31\u5728\u5251\u5c16\u8ddd\u79bb\u5c0f\u548c\u5c1a\u54bd\u5589\u5bf8\u8bb8\u65f6\uff0c\u7a81\u7136\u505c\u4f4f\u4e86\u3002 \u3000\u3000\u539f\u6765\uff0c\u5c0f\u71d5\u5b50\u4e00\u53ea\u5f3a\u800c\u6709\u52b2\u7684\u53f3\u624b\uff0c\u6293\u4f4f\u4e86\u4e09\u516c\u4e3b\u7684\u53f3\u8155\uff0c\u786c\u751f\u751f\u7684\u5c06\u5411\u524d\u63a8\u8fdb\u7684\u5251 \u52bf\uff0c\u7ed9\u7a33\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5c0f\u548c\u5c1a\u4e00\u4fa7\u5934\uff0c\u7ed5\u8fc7\u5251\u52bf\uff0c\u62cd\u62cd\u5149\u8111\u888b\uff0c\u9053\uff1a\u201c\u5c0f\u71d5\u5b50\uff0c\u4f60\u518d\u665a\u6765\u90a3\u4e48\u4e00\u4e1d\u4e1d\u7684\u65f6\u95f4\uff0c \u5c0f\u548c\u5c1a\u5c31\u8fdb\u4e86\u9b3c\u95e8\u5173\u3002\u201d \u3000\u3000\u201c\u5b9e\u5728\u5f88\u5371\u9669\uff01\u201d\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u4f60\u600e\u4f1a\u5982\u6b64\u5927\u610f\uff0c\u8fd9\u4e00\u5251\u80fd\u67b6\u4e0d\u80fd\u95ea\uff01\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u738b\u91cd\u5c71\u6709\u4e9b\u6115\u7136\u3002 \u3000\u3000\u201c\u8fd9\u662f\u98de\u8679\u5251\u6cd5\u4e2d\u7684\u4e00\u8bb0\u7edd\u62db\uff0c\u540d\u53eb\u2018\u7a7f\u4e91\u5c04\u76ee\u2019\u2026\u2026\u201d\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u662f\u76f4\u8fdb\u5251\u62db\uff0c\u53ea \u8981\u88ab\u5b83\u4e58\u9699\u800c\u5165\uff0c\u5c31\u662f\u53d6\u547d\u7684\u4e00\u5251\uff0c\u4e0d\u8fc7\uff0c\u5012\u4e5f\u662f\u6709\u4e9b\u5947\u602a\u2026\u2026\u201d \u3000\u3000\u201c\u5947\u602a\u4ec0\u4e48\uff1f\u201d\u5c0f\u548c\u5c1a\u9053\uff1a\u201c\u5c0f\u548c\u5c1a\u547d\u5927\u624d\u649e\u5f97\u5929\u9f13\u54cd\uff0c\u4f60\u624d\u4e0d\u65e9\u4e0d\u665a\u7684\u53ca\u65f6\u800c\u81f3\u3002\u201d \u3000\u3000\u201c\u6211\u662f\u8bf4\uff0c\u4e09\u516c\u4e3b\u8fd9\u4e00\u5251\uff0c\u5e94\u8be5\u4f1a\u66f4\u5feb\u4e00\u4e9b\u7684\uff0c\u4f46\u7adf\u7136\u88ab\u6211\u53ca\u65f6\u963b\u6b62\u4e86\u2026\u2026\u201d\u5c0f\u71d5\u5b50 \u9053\uff1a\u201c\u662f\u4e0d\u662f\u516c\u4e3b\u6709\u610f\u624b\u4e0b\u7559\u60c5\u2026\u2026\u201d \u3000\u3000\u56de\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u4e09\u516c\u4e3b\u9762\u8272\u94c1\u9752\uff0c\u53cc\u7709\u7d27\u9501\uff0c\u4e00\u8138\u90fd\u662f\u75db\u82e6\u4e4b\u8272\u3002 \u3000\u3000\u539f\u6765\uff0c\u5c0f\u71d5\u5b50\u7528\u529b\u5f88\u5927\uff0c\u4f7f\u4e09\u516c\u4e3b\u5168\u8eab\u90fd\u65e0\u6cd5\u6323\u52a8\u3002 \u3000\u3000\u201c\u516c\u4e3b\u6bd2\u6027\u53d1\u4f5c\uff0c\u5fc3\u795e\u53d7\u5236\uff0c\u6240\u4ee5\u5251\u6cd5\u81ea\u662f\u4e0d\u50cf\u5e73\u5e38\u7075\u6d3b\u3002\u201d\u601c\u82b1\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5979 \u4ee5\u81ea\u5df1\u6df1\u539a\u7684\u5185\u529f\u53cd\u6297\u3001\u6323\u624e\uff0c\u4f46\u6700\u540e\uff0c\u603b\u662f\u6297\u62d2\u4e0d\u4f4f\uff0c\u53d7\u5c3d\u4e86\u5404\u79cd\u6298\u78e8\u3001\u75db\u82e6\u2026\u2026\u201d \u3000\u3000\u53ea\u89c1\u4e09\u516c\u4e3b\u6a31\u53e3\u4e00\u5f20\uff0c\u4e00\u9053\u8840\u7bad\u75be\u55b7\u800c\u51fa\uff0c\u6b63\u597d\u5c04\u5728\u5c0f\u71d5\u5b50\u7684\u8138\u4e0a\u3002 \u3000\u3000\u4e00\u5f20\u82f1\u4fca\u7684\u9762\u5b54\uff0c\u7acb\u523b\u8840\u82b1\u6ee1\u5e03\u3002 \u3000\u3000\u4f46\u4e09\u516c\u4e3b\u7684\u6323\u624e\uff0c\u5374\u66f4\u4e3a\u6fc0\u70c8\u4e86\uff0c\u5168\u8eab\u98a4\u6296\uff0c\u53f3\u624b\u6325\u7529\uff0c\u5e0c\u671b\u6323\u8131\u5c0f\u71d5\u5b50\u7684\u4e94\u6307\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u5de6\u624b\u75be\u51fa\uff0c\u8fde\u70b9\u4e09\u516c\u4e3b\u4e09\u5904\u7a74\u9053\uff0c\u624d\u4e00\u62b9\u8138\u4e0a\u7684\u8840\uff0c\u9053\uff1a\u201c\u5e26\u8def\uff0c\u628a\u516c\u4e3b\u9001\u5165\u5367 \u623f\uff0c\u770b\u770b\u6709\u6ca1\u6709\u65b9\u6cd5\u6551\u5979\u3002\u201d \u3000\u3000\u601c\u82b1\u56de\u8eab\u5e26\u8def\uff0c\u5c0f\u71d5\u5b50\u5374\u4e00\u628a\u62b1\u8d77\u4e86\u4e09\u516c\u4e3b\uff0c\u968f\u540e\u8ddf\u8fdb\uff0c\u4e00\u9762\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u5c0f\u548c\u5c1a\u3001\u5c0f \u9053\u58eb\uff0c\u4f60\u4eec\u90fd\u8981\u6765\u5440\uff01\u201d \u3000\u3000\u5c0f\u548c\u5c1a\u3001\u5c0f\u9053\u58eb\u3001\u738b\u91cd\u5c71\u4e09\u4eba\u4e92\u770b\u4e86\u4e00\u773c\uff0c\u4e3e\u6b65\u884c\u53bb\u3002 \u3000\u3000\u738b\u91cd\u5c71\u4e00\u9762\u8d70\uff0c\u4e00\u9762\u60f3\u9053\uff1a\u5979\u662f\u516c\u4e3b\u8eab\u4efd\uff0c\u4f60\u8fd9\u6837\u62b1\u7740\u5979\uff0c\u50cf\u4ec0\u4e48\u8bdd\uff1f \u3000\u3000\u516c\u4e3b\u7684\u5367\u5ba4\u5e76\u4e0d\u8c6a\u534e\uff0c\u4f46\u5374\u975e\u5e38\u96c5\u81f4\uff0c\u6697\u9999\u6d6e\u52a8\uff0c\u9189\u4eba\u5982\u9152\u3002 \u3000\u3000\u601c\u82b1\u6380\u5f00\u4e86\u7f57\u5e10\uff0c\u5c0f\u71d5\u5b50\u628a\u4e09\u516c\u4e3b\u653e\u5728\u5e8a\u4e0a\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u5462\uff1f\u201d \u3000\u3000\u201c\u90fd\u6765\u4e86\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u91cc\u662f\u516c\u4e3b\u95fa\u623f\uff0c\u4e0d\u51c6\u7537\u4eba\u6d89\u8db3\uff0c\u4e0d\u80fd\u8ba9\u4ed6\u4eec\u90fd\u8fdb\u6765\u3002\u201d\u601c\u82b1\u8bf4\u3002 \u3000\u3000\u201c\u597d\u5427\uff01\u90a3\u5c31\u8ba9\u4ed6\u4eec\u90fd\u5b88\u5728\u8fd9\u95fa\u623f\u4e4b\u5916\uff0c\u66ff\u6211\u62a4\u6cd5\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u6211\u53bb\u544a\u8bc9\u4ed6\u4eec\uff0c\u5509\uff01\u4f46\u6700\u8981\u7d27\u7684\u662f\u5148\u6551\u516c\u4e3b\uff0c\u6211\u53bb\u592a\u533b\u9662\uff0c\u8981\u4ed6\u4eec\u6d3e\u5927\u533b\u6765\u3002\u201d \u3000\u3000\u201c\u601c\u82b1\u59d0\u59d0\uff0c\u592a\u533b\u80fd\u6cbb\u516c\u4e3b\u7684\u75c5\u4e48\uff1f\u201d \u3000\u3000\u201c\u53ef\u662f\uff0c\u603b\u6bd4\u6211\u4eec\u5f3a\u4e00\u4e9b\u5427\uff01\u201d\u601c\u82b1\u9053\uff1a\u201c\u516c\u4e3b\u88ab\u4f60\u70b9\u4e86\u7a74\u9053\uff0c\u4e0d\u80fd\u6323\u52a8\u3001\u558a\u53eb\uff0c\u4f46\u6211 \u770b\u5f97\u51fa\uff0c\u5979\u4e00\u5b9a\u5f88\u75db\u82e6\u3002\u201d \u3000\u3000\u201c\u544a\u8bc9\u6211\uff0c\u516c\u4e3b\u7684\u4f24\u52bf\u53d1\u4f5c\u4e4b\u540e\uff0c\u53ef\u90fd\u662f\u8fd9\u4e2a\u6837\u5b50\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\uff01\u201d\u601c\u82b1\u9053\uff1a\u201c\u5979\u6bcf\u6b21\u6253\u5750\u6297\u62d2\uff0c\u4f46\u7ec8\u662f\u5c48\u670d\u5728\u8001\u5996\u5a46\u7684\u624b\u4e0b\uff0c\u4f46\u8fd9\u4e00\u6b21\uff0c\u4f3c\u662f \u6700\u5389\u5bb3\u4e86\uff0c\u5410\u51fa\u4e86\u90a3\u4e48\u4e00\u5927\u53e3\u7684\u8840\u3002\u201d \u3000\u3000\u201c\u8001\u5996\u5a46\uff1f\u4e24\u4e2a\u9b54\u6559\u957f\u8001\u90fd\u662f\u5973\u7684\u3002\u201d \u3000\u3000\u201c\u4e00\u7537\u4e00\u5973\uff0c\u5176\u5b9e\uff0c\u4e24\u4e2a\u4eba\u957f\u76f8\u602a\u5f02\uff0c\u7537\u5973\u90fd\u4e00\u6837\u96be\u770b\uff0c\u53ea\u4e0d\u8fc7\u5973\u7684\u90a3\u4e2a\u5341\u5206\u51f6\u608d\uff0c\u6709 \u4e00\u6b21\u516c\u4e3b\u548c\u5979\u4e89\u8bba\uff0c\u5979\u7adf\u7136\u51fa\u624b\u63b4\u4e86\u516c\u4e3b\u4e00\u4e2a\u8033\u5149\u3002\u201d \u3000\u3000\u201c\u627e\u4e0d\u5230\u4ed6\u4eec\u85cf\u8eab\u4e4b\u5904\uff0c\u4e5f\u5c31\u6293\u4e0d\u5230\u4ed6\u4eec\u6765\u66ff\u516c\u4e3b\u7597\u6bd2\uff0c\u4f46\u6211\u4eec\u4e5f\u65e0\u6cd5\u518d\u7b49\u4e0b\u53bb\u4e86\uff01\u201d \u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u73b0\u5728\uff0c\u53ea\u597d\u5148\u66ff\u516c\u4e3b\u7597\u4f24\u8981\u7d27\u2026\u2026\u201d\u4f38\u624b\u8131\u53bb\u516c\u4e3b\u7684\u8863\u670d\u3002 \u3000\u3000\u601c\u82b1\u5403\u4e86\u4e00\u60ca\uff0c\u4f38\u624b\u62e6\u4f4f\u4e86\u71d5\u6625\u98ce\uff0c\u9053\uff1a\u201c\u5c0f\u71d5\u5b50\uff0c\u4f60\u8981\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u7597\u4f24\u554a\uff01\u6211\u8981\u5148\u627e\u51fa\u5979\u4f24\u5728\u4f55\u5904\uff0c\u6211\u624d\u80fd\u4e0b\u624b\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\uff0c\u5c0f\u71d5\u5b50\uff01\u4f60\u77e5\u9053\u4e48\uff1f\u5979\u662f\u516c\u4e3b\u554a\uff0c\u91d1\u679d\u7389\u53f6\uff0c\u4f60\u8131\u53bb\u5979\u8eab\u4e0a\u7684\u8863\u670d\uff0c\u90a3\u600e\u4e48\u5f97 \u4e86\uff1f\u201d \u3000\u3000\u201c\u8981\u662f\u4e0d\u8131\u53bb\u5979\u8eab\u4e0a\u7684\u8863\u670d\uff0c\u4fbf\u627e\u4e0d\u5230\u5979\u4f24\u5728\u4f55\u5904\uff0c\u8981\u6211\u5982\u4f55\u5e2e\u5979\u6cbb\u7597\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u201d\u601c\u82b1\u6c89\u541f\u4e00\u9635\uff0c\u9053\uff1a\u201c\u5c0f\u71d5\u5b50\uff0c\u4f60\u77e5\u9053\u592a\u533b\u600e\u4e48\u66ff\u7687\u540e\u3001\u5983\u5b50\u770b\u75c5\u5417\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6447\u6447\u5934\u3002 \u3000\u3000\u601c\u82b1\u8bf4\uff1a\u201c\u738b\u540e\u3001\u5983\u5b50\u4eec\u8eab\u5728\u7f57\u5e10\u4e4b\u5185\uff0c\u53ea\u4f38\u51fa\u4e00\u53ea\u53f3\u624b\uff0c\u7531\u5927\u533b\u628a\u8109\uff0c\u592a\u533b\u53ea\u770b\u5230\u4e00 \u53ea\u624b\u8155\uff0c\u5c31\u53ef\u4ee5\u4e0b\u836f\u4e86\uff0c\u4f60\u53ef\u4ee5\u5bdf\u770b\u516c\u4e3b\u7684\u5934\u3001\u624b\uff0c\u96be\u9053\u4f60\u8fd8\u4e0d\u80fd\u4e0b\u836f\u4e48\uff1f\u201d \u3000\u3000\u201c\u5979\u4e0d\u662f\u5bb3\u75c5\uff0c\u6211\u4e5f\u4e0d\u4f1a\u628a\u8109\uff0c\u6211\u627e\u5230\u5979\u7684\u4f24\u5904\uff0c\u4ed4\u7ec6\u5bdf\u770b\u4e4b\u540e\uff0c\u80fd\u4e0d\u80fd\u7597\u6cbb\u8fd8\u4e0d\u4e00 \u5b9a\uff0c\u4f46\u5982\u80fd\u628a\u4f24\u52bf\u7a33\u4f4f\uff0c\u6211\u518d\u53bb\u6293\u90a3\u8001\u5996\u5a46\u51fa\u6765\uff0c\u66ff\u5979\u533b\u6cbb\u3002\u201d\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u8fd9\u6709\u4ec0\u4e48\u4e0d\u5bf9 \u7684\u5462\uff1f\u201d \u3000\u3000\u601c\u82b1\u9053\uff1a\u201c\u5c0f\u71d5\u5b50\uff0c\u5982\u679c\u662f\u6211\uff0c\u968f\u4f60\u9ad8\u5174\uff0c\u600e\u4e48\u6446\u5e03\u6211\u90fd\u53ef\u4ee5\uff0c\u53ef\u662f\u516c\u4e3b\u4e0d\u884c\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u53c8\u6ca1\u6709\u53d7\u5bb3\uff0c\u53d7\u5bb3\u7684\u662f\u516c\u4e3b\u554a\uff01\u4f60\u8bf4\u8131\u5979\u7684\u8863\u670d\u91cd\u8981\uff1f\u8fd8\u662f\u5979\u7684\u6027\u547d\u91cd\u8981\uff1f\u201d \u3000\u3000\u201c\u6211\u2026\u2026\u6211\u4e0d\u77e5\u9053\uff0c\u4e0d\u8fc7\uff0c\u6211\u4e0d\u80fd\u4f5c\u4e3b\uff0c\u8fd9\u6837\u5427\u2026\u2026\u201d\u601c\u82b1\u8bf4\uff1a\u201c\u4f60\u89e3\u5f00\u5979\u7684\u54d1\u7a74\uff0c\u95ee \u95ee\u516c\u4e3b\u7684\u610f\u601d\uff0c\u597d\u5417\uff1f\u201d \u3000\u3000\u201c\u5bf9\u5440\uff01\u6211\u600e\u4e48\u6ca1\u6709\u60f3\u5230\u5462\uff1f\u201d \u3000\u3000\u62cd\u6d3b\u4e86\u4e09\u516c\u4e3b\u7684\u54d1\u7a74\uff0c\u5c0f\u71d5\u5b50\u7406\u76f4\u6c14\u58ee\u7684\u95ee\u9053\uff1a\u201c\u4e09\u516c\u4e3b\uff0c\u6211\u8981\u627e\u51fa\u4f60\u7684\u4f24\u5728\u54ea\u91cc\uff0c\u5fc5 \u987b\u8131\u53bb\u4f60\u7684\u8863\u670d\uff0c\u53ef\u4ee5\u5417\uff1f\u201d \u3000\u3000\u8fd9\u79cd\u5355\u5200\u76f4\u5165\u7684\u95ee\u6cd5\uff0c\u542c\u5f97\u601c\u82b1\u76f4\u76b1\u7709\u5934\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u7adf\u4e5f\u88ab\u95ee\u50bb\u4e86\uff0c\u671b\u7740\u5c0f\u71d5\u5b50\u4e0d\u77e5\u5982\u4f55\u5f00\u53e3\uff1f \u3000\u3000\u5176\u5b9e\uff0c\u5979\u88ab\u5c0f\u71d5\u5b50\u70b9\u4e2d\u7a74\u9053\u4e4b\u540e\uff0c\u90a3\u5e72\u6270\u5979\u7684\u9b54\u97f3\u4fbf\u6d88\u5931\u4e86\uff0c\u4eba\u4e5f\u6062\u590d\u4e86\u6e05\u9192\uff0c\u5979\u77e5\u9053 \u88ab\u5c0f\u71d5\u5b50\u62b1\u5165\u5367\u5ba4\uff0c\u4f46\u5c0f\u71d5\u5b50\u660e\u8bf4\uff0c\u8981\u8131\u5979\u8863\u670d\u9a8c\u4f24\uff0c\u987f\u89c9\u65e0\u6cd5\u542f\u9f7f\u3002 \u3000\u3000\u4f46\u5979\u7a76\u7adf\u662f\u516c\u4e3b\u4e4b\u624d\uff0c\u6c89\u601d\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u601c\u82b1\uff0c\u4f60\u51fa\u53bb\uff0c\u5b88\u5728\u95e8\u53e3\uff0c\u4efb\u4f55\u4eba\u90fd\u4e0d\u8bb8\u8fdb \u6765\u3002\u201d \u3000\u3000\u95fb\u5f26\u6b4c\u800c\u77e5\u96c5\u610f\uff0c\u601c\u82b1\u5df2\u660e\u767d\u4e86\u4e09\u516c\u4e3b\u7684\u5fc3\u610f\uff0c\u5e94\u4e86\u4e00\u58f0\uff0c\u9000\u51fa\u623f\u53bb\u3002 \u3000\u3000\u201c\u89e3\u5f00\u6211\u7684\u7a74\u9053\uff0c\u6211\u5df2\u5b8c\u5168\u6e05\u9192\u2026\u2026\u201d\u4e09\u516c\u4e3b\u9053\uff1a\u201c\u53ef\u4ee5\u63a7\u5236\u81ea\u5df1\u4e86\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u7565\u4e00\u6c89\u541f\uff0c\u4f9d\u8a00\u89e3\u53bb\u4e09\u516c\u4e3b\u7684\u7a74\u9053\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u633a\u8eab\u5750\u8d77\uff0c\u9053\uff1a\u201c\u5c0f\u71d5\u5b50\uff0c\u5bdf\u770b\u4f24\u52bf\uff0c\u4e00\u5b9a\u8981\u8131\u53bb\u8863\u670d\u4e48\uff1f\u201d \u3000\u3000\u201c\u662f\u554a\uff01\u6211\u53c8\u6ca1\u6709\u5b66\u8fc7\u628a\u8109\u770b\u75c5\u7684\u6280\u672f\uff0c\u53ea\u6709\u7528\u773c\u775b\u770b\u4e86\uff0c\u770b\u8fc7\u4e86\u4f24\u52bf\u624d\u80fd\u60f3\u529e\u6cd5 \u554a\uff01\u201d \u3000\u3000\u201c\u5c0f\u71d5\u5b50\uff0c\u4f60\u53ef\u77e5\u9053\uff0c\u4f60\u770b\u8fc7\u4e86\u6211\u7684\u8eab\u4f53\u2026\u2026\u201d\u4e09\u516c\u4e3b\u9053\uff1a\u201c\u6211\u4ee5\u540e\u2026\u2026\u600e\u4e48\u518d\u5ac1\u4eba \u5462\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u5c31\u4e0d\u660e\u767d\u4e86\uff0c\u4f60\u4f24\u52bf\u53d1\u4f5c\u65f6\u90a3\u4e48\u75db\u82e6\uff0c\u522b\u4eba\u5982\u4f55\u5bf9\u4ed8\u4f60\uff0c\u4f60\u4e5f \u65e0\u6cd5\u53cd\u6297\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u65e9\u533b\u597d\u5b83\u5462\uff1f\u6b7b\u4ea1\u548c\u5ac1\u4eba\u6bd4\u8d77\u6765\uff0c\u90a3\u4e2a\u91cd\u8981\u5462\uff1f\u201d \u3000\u3000\u201c\u4f60\u597d\u6d51\u554a\uff01\u5c0f\u71d5\u5b50\u2026\u2026\u201d\u4e09\u516c\u4e3b\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u4f60\u65e2\u7136\u89c9\u5f97\u90a3\u4e0d\u91cd\u8981\uff0c\u6211\u5c31\u8131\u7ed9\u4f60 \u770b\u5427\uff01\u201d \u3000\u3000\u201c\u6211\u5c31\u77e5\u9053\uff0c\u516c\u4e3b\u6bd4\u601c\u82b1\u59d0\u59d0\u806a\u660e\u3002\u201d\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u544a\u8bc9\u6211\uff0c\u4f24\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u5728\u5c0f\u8179\u5730\u65b9\uff0c\u6bcf\u4e00\u6b21\u53d1\u4f5c\uff0c\u90fd\u7531\u90a3\u91cc\u5f00\u59cb\u2026\u2026\u201d\u4e09\u516c\u4e3b\u7f13\u7f13\u5bbd\u8863\u89e3\u5e26\uff0c\u771f\u7684\u8131\u4e0b\u4e86\u8863 \u670d\u3002 \u3000\u3000\u671b\u7740\u4e09\u516c\u4e3b\u8131\u4e0b\u8863\u886b\u7684\u7c89\u767d\u808c\u80a4\uff0c\u5c0f\u71d5\u5b50\u7a81\u7136\u50cf\u88ab\u9488\u523a\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u770b\u7740\u5982\u6b64\u4e00\u4e2a\u7f8e\u5973\u8131\u53bb\u8863\u886b\uff0c\u5c0f\u71d5\u5b50\u8fd8\u662f\u6709\u751f\u4ee5\u6765\u7684\u7b2c\u4e00\u6b21\uff0c\u770b\u4e86\u4e00\u9635\uff0c\u95ed\u4e0a\u4e86\u773c\u775b\uff0c \u4e0d\u6562\u518d\u770b\u3002 \u3000\u3000\u8fd9\u4e00\u9635\u89c6\u89c9\u7684\u523a\u6fc0\uff0c\u5c0f\u71d5\u5b50\u4f3c\u662f\u7a81\u7136\u957f\u5927\u4e86\u4e0d\u5c11\u3002 \u3000\u3000\u201c\u5c0f\u71d5\u5b50\uff0c\u4f60\u770b\u8fd9\u6837\u591f\u4e0d\u591f\u2026\u2026\u201d\u4e09\u516c\u4e3b\u7684\u58f0\u97f3\uff0c\u4f20\u5165\u4e86\u5c0f\u71d5\u5b50\u7684\u8033\u9645\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u7741\u5f00\u773c\u775b\u770b\u53bb\uff0c\u53ea\u89c1\u4e09\u516c\u4e3b\u5374\u5df2\u95ed\u4e0a\u773c\u775b\u3002 \u3000\u3000\u4f46\u5979\u7684\u4e0a\u8863\u5df2\u8131\uff0c\u53ea\u6709\u4e00\u4ef6\u7ea2\u809a\u515c\uff0c\u906e\u7740\u4e00\u5bf9\u7389\u4e73\u3002 \u3000\u3000\u7ea2\u809a\u515c\u7684\u4e0b\u9762\u5df2\u7ffb\u6298\u4e0a\u53bb\uff0c\u9732\u4e00\u4e2a\u6d51\u5706\u7684\u5c0f\u8179\uff0c\u5c0f\u8179\u4ee5\u4e0b\u7eff\u88d9\u63a9\u906e\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u5438\u4e00\u53e3\u6c14\uff0c\u5e73\u9759\u4e86\u4e00\u4e0b\u5fc3\u60c5\uff0c\u9053\uff1a\u201c\u516c\u4e3b\uff0c\u5728\u4e0b\u95ee\u4ec0\u4e48\uff0c\u5e0c\u671b\u516c\u4e3b\u80fd\u636e\u5b9e\u56de\u7b54 \u6211\u3002\u201d \u3000\u3000\u201c\u4f60\u95ee\u5427\uff0c\u6211\u77e5\u9053\u7684\uff0c\u5168\u90fd\u544a\u8bc9\u4f60\u2026\u2026\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u4f38\u51fa\u624b\u6309\u5728\u4e09\u516c\u4e3b\u7684\u5c0f\u8179\u4e0a\uff0c\u9053\uff1a\u201c\u6bcf\u6b21\u4f24\u52bf\u53d1\u4f5c\uff0c\u7686\u7531\u8fd9\u513f\u5f00\u59cb\uff0c\u5bf9\u4e48\uff1f\u201d \u3000\u3000\u201c\u5bf9\uff0c\u597d\u50cf\u90a3\u91cc\u9762\u86f0\u4f0f\u7740\u4e00\u4e2a\u6d3b\u7684\u6bd2\u7269\uff0c\u53ea\u662f\u65e0\u6cd5\u628a\u5b83\u53d6\u51fa\u6765\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6697\u4e2d\u8fd0\u6c14\uff0c\u6309\u5728\u4e09\u516c\u4e3b\u7684\u5c0f\u8179\u4e4b\u4e0a\u7684\u53f3\u4fa7\uff0c\u987f\u7136\u6709\u4e00\u80a1\u70ed\u529b\u76f4\u900f\u51fa\u6765\uff0c\u9053\uff1a\u201c\u6211 \u7528\u672c\u8eab\u7eaf\u9633\u4e4b\u706b\u8bd5\u8bd5\uff0c\u4f46\u4e09\u516c\u4e3b\u8981\u8fd0\u6c14\u914d\u5408\uff0c\u514d\u5f97\u53d7\u4f24\u3002\u201d \u3000\u3000\u201c\u597d\u5427\uff01\u6211\u5168\u529b\u4ee5\u8d74\uff0c\u4e0d\u8fc7\uff0c\u6211\u5982\u62b5\u53d7\u4e0d\u4f4f\u5462\uff1f\u201d \u3000\u3000\u201c\u90a3\u5c31\u544a\u8bc9\u6211\uff0c\u4f46\u6211\u76f8\u4fe1\u82df\u8001\u524d\u8f88\u95e8\u4e0b\u5f1f\u5b50\uff0c\u5185\u529f\u9020\u8be3\u5fc5\u5df2\u767b\u5802\u5165\u5ba4\u3002\u201d\u5c0f\u71d5\u5b50\u9053\uff1a \u201c\u5982\u82e5\u80fd\u591f\u4e00\u53e3\u6c14\u903c\u51fa\u4f60\u5185\u8179\u4e4b\u6bd2\uff0c\u5c31\u53ef\u4ee5\u653e\u624b\u548c\u9b54\u6559\u4e2d\u4eba\u4e00\u6218\u4e86\u3002\u201d \u3000\u3000\u51e0\u53e5\u8bdd\uff0c\u6fc0\u8d77\u4e86\u4e09\u516c\u4e3b\u7684\u8c6a\u6c14\uff0c\u9053\uff1a\u201c\u5c0f\u71d5\u5b50\uff0c\u6211\u4f24\u52bf\u5982\u80fd\u533b\u597d\uff0c\u6211\u8981\u8ddf\u4f60\u95ef\u8361\u6c5f\u6e56\uff0c \u627e\u9b54\u6559\u4e2d\u4eba\u62a5\u4ec7\uff01\u201d \u3000\u3000\u201c\u597d\u554a\uff01\u201d\u5c0f\u71d5\u5b50\u7565\u4e00\u6c89\u541f\uff0c\u53c8\u6447\u6447\u5934\u9053\uff1a\u201c\u4e0d\u884c\uff0c\u4e0d\u884c\u2026\u2026\u201d \u3000\u3000\u4e09\u516c\u4e3b\u5927\u611f\u5931\u671b\uff0c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u561b\uff1f\u201d \u3000\u3000\u201c\u4f60\u662f\u516c\u4e3b\u7684\u8eab\u4efd\uff0c\u5230\u90a3\u91cc\u90fd\u6709\u5b98\u5e9c\u4eba\u8fce\u63a5\uff0c\u6211\u4eec\u5982\u4f55\u80fd\u548c\u4f60\u8d70\u5728\u4e00\u8d77\uff1f\u201d \u3000\u3000\u201c\u4e0d\u4f1a\u7684\u2026\u2026\u201d\u4e09\u516c\u4e3b\u9053\uff1a\u201c\u6211\u548c\u4f60\u4eec\u884c\u8d70\u6c5f\u6e56\u65f6\uff0c\u6211\u53ea\u662f\u4e00\u4e2a\u6c5f\u6e56\u5973\u5b50\uff0c\u51e1\u662f\u4f60\u4eec\u7684 \u89c4\u77e9\uff0c\u6211\u4f1a\u5168\u529b\u9075\u5b88\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c31\u597d\u5546\u91cf\u4e86\uff0c\u7b49\u6211\u8ddf\u5c0f\u548c\u5c1a\u3001\u5c0f\u9053\u58eb\u8bf4\u597d\u4e86\uff0c\u518d\u7b54\u5e94\u4f60\uff0c\u73b0\u5728\uff0c\u6211\u4eec\u5f00\u59cb\u7597\u4f24\u3002\u201d \u3000\u3000\u4e00\u80a1\u70ed\u6c14\uff0c\u7531\u5c0f\u71d5\u5b50\u7684\u638c\u5fc3\u4e2d\u900f\u5c04\u800c\u51fa\uff0c\u653b\u5165\u4e09\u516c\u4e3b\u7684\u5c0f\u8179\u4e4b\u5185\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u4e5f\u9ed8\u8fd0\u771f\u6c14\uff0c\u548c\u90a3\u80a1\u70ed\u529b\u878d\u5408\uff0c\u5faa\u884c\u4e8e\u7ecf\u8109\u4e4b\u4e2d\u3002 \u3000\u3000\u679c\u7136\uff0c\u71d5\u6625\u98ce\u611f\u53d7\u5230\u4e09\u516c\u4e3b\u5185\u8179\u4e2d\u6709\u4e00\u7269\u4f3c\u5728\u8822\u8822\u6b32\u52a8\uff0c\u7acb\u65f6\u52a0\u7d27\u65bd\u4e3a\u3002 \u3000\u3000\u5343\u5e74\u53c2\u5de5\u8865\u52a9\u4ed6\u6570\u5341\u5e74\u4fee\u4e3a\u7684\u529f\u529b\uff0c\u5728\u795e\u7269\u548c\u826f\u5e08\u76f8\u8f85\u4e4b\u4e0b\uff0c\u5c0f\u71d5\u5b50\u529f\u529b\u5341\u5206\u6df1\u539a\uff0c\u6574 \u4e2a\u53f3\u638c\u5f00\u59cb\u6cdb\u6210\u7ea2\u8272\u3002 \u3000\u3000\u4f46\u4e09\u516c\u4e3b\u5374\u5df2\u627f\u53d7\u4e0d\u4e86\uff0c\u5168\u8eab\u9999\u6c57\u6dcb\u6f13\uff0c\u5a07\u5598\u5401\u5401\uff0c\u53ea\u4e0d\u8fc7\uff0c\u8fd8\u5728\u54ac\u7259\u82e6\u6491\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u4e5f\u611f\u89c9\u5230\u4e09\u516c\u4e3b\u5728\u5f3a\u5236\u5fcd\u53d7\uff0c\u53ea\u662f\u6c42\u529f\u5fc3\u5207\uff0c\u5e0c\u671b\u4ee5\u672c\u8eab\u7684\u7eaf\u9633\u771f\u529b\uff0c\u914d\u5408\u4e09\u516c \u4e3b\u7684\u5185\u529f\uff0c\u70bc\u6bc1\u8179\u4e2d\u6bd2\u7269\uff0c\u4e00\u9762\u9f13\u52b1\u4e09\u516c\u4e3b\u5168\u529b\u8fce\u5408\uff0c\u4e00\u9762\u52a0\u7d27\u65bd\u4e3a\u3002 \u3000\u3000\u53ea\u53ef\u60dc\uff0c\u4e09\u516c\u4e3b\u5df2\u65e0\u6cd5\u518d\u5fcd\u53d7\u4e0b\u53bb\uff0c\u53d1\u51fa\u4e86\u4e00\u58f0\u5c16\u53eb\u3002 \u3000\u3000\u201c\u4f4f\u624b\uff01\u201d\u4e00\u4e2a\u51b7\u5389\u7684\u58f0\u97f3\u4f20\u5165\u8033\u4e2d\uff0c\u58f0\u97f3\u4e0d\u5927\uff0c\u4f46\u5c0f\u71d5\u5b50\u5374\u542c\u5f97\u5fc3\u5934\u4e00\u9707\u3002 \u3000\u3000\u56de\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u4e00\u4e2a\u5e03\u8863\u8346\u9497\u7684\u4e2d\u5e74\u7f8e\u5987\uff0c\u6ee1\u8138\u51b7\u8083\u7684\u7ad9\u5728\u623f\u4e2d\u3002 \u3000\u3000\u5979\u6765\u7684\u65e0\u58f0\u65e0\u606f\uff0c\u5c0f\u71d5\u5b50\u7adf\u4e0d\u77e5\u5979\u4f55\u65f6\u8fdb\u5165\u4e86\u623f\u4e2d\u3002 \u3000\u3000\u4f46\u6700\u4f7f\u5c0f\u71d5\u5b50\u60ca\u8bb6\u7684\u662f\uff0c\u5c0f\u548c\u5c1a\u3001\u5c0f\u9053\u58eb\u3001\u738b\u91cd\u5c71\u90fd\u5b88\u5728\u5ba4\u5916\uff0c\u600e\u4f1a\u65e0\u58f0\u65e0\u606f\u7684\u653e\u8fdb\u8fd9 \u4e48\u4e00\u4e2a\u5987\u4eba\u3002 \u3000\u3000\u4f55\u51b5\uff0c\u601c\u82b1\u8fd8\u5b88\u5728\u623f\u95e8\u53e3\u5904\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u4f3c\u662f\u5df2\u6655\u4e86\u8fc7\u53bb\uff0c\u7d27\u95ed\u7740\u53cc\u76ee\u3002 \u3000\u3000\u201c\u4f60\u8fd9\u6837\u4f1a\u4f7f\u5979\u7ecf\u8109\u53d7\u4f24\uff0c\u6b66\u529f\u5168\u5931\u2026\u2026\u201d\u4e2d\u5e74\u7f8e\u5987\u671b\u7740\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u5e74\u8f7b\u4eba\uff0c\u5982\u6b64\u5192 \u5931\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u662f\u8c01\uff1f\u201d\u5c0f\u71d5\u5b50\u5438\u4e00\u53e3\u6c14\uff0c\u8dc3\u4e0b\u6728\u69bb\uff0c\u6446\u51fa\u4e00\u526f\u8fce\u654c\u7684\u67b6\u52bf\uff0c\u9053\uff1a\u201c\u4f60\u600e\u4e48\u8fdb\u6765 \u7684\uff1f\u201d \u3000\u3000\u201c\u4f60\u8fd9\u70b9\u5e74\u7eaa\uff0c\u7adf\u7136\u6709\u5982\u6b64\u529f\u529b\uff0c\u5f53\u4eca\u4e4b\u4e16\uff0c\u9664\u4e86\u8001\u778e\u5b50\u4e4b\u5916\uff0c\u8c01\u4e5f\u6559\u4e0d\u51fa\u8fd9\u6837\u7684\u5f92\u5f1f \u4e86\uff0c\u8bf4\uff01\u4f60\u7684\u5e08\u7236\u662f\u4e0d\u662f\u5929\u96f7\u8001\u4eba\u2026\u2026\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4f60\u8ba4\u8bc6\u6211\u5e08\u7236\uff1f\u201d \u3000\u3000\u4e2d\u5e74\u5987\u4eba\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e5f\u8bb8\u4f60\u662f\u4e00\u7247\u597d\u610f\uff0c\u4f46\u4f60\u4eec\u8fd9\u7b49\u7597\u4f24\u7684\u65b9\u6cd5\uff0c\u53ef\u66fe\u60f3\u5230\u5982\u4f55 \u5584\u540e\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u5979\u4f24\u52bf\u53d1\u4f5c\u65f6\uff0c\u5341\u5206\u75bc\u75db\uff0c\u516d\u4eb2\u4e0d\u8ba4\uff0c\u4e0d\u4f46\u4f1a\u6740\u81ea\u5df1\u4eba\uff0c\u4e5f\u4f1a\u53d8\u5f97\u75f4\u5446\uff0c \u957f\u4e45\u4e0b\u53bb\uff0c\u6574\u4e2a\u4eba\u5c31\u4f1a\u5e9f\u4e86\u3002\u201d \u3000\u3000\u4e2d\u5e74\u5987\u4eba\u53c8\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u4ee5\u540e\u518d\u8bf4\u5427\uff0c\u4f60\u5148\u51fa\u53bb\uff0c\u8ba9\u6211\u770b\u770b\u5979\u7684\u4f24\u52bf\u3002\u201d \u3000\u3000\u201c\u4e0d\u884c\uff0c\u4f60\u5982\u662f\u9b54\u6559\u4e2d\u4eba\uff0c\u4f1a\u5bb3\u4e86\u4e09\u516c\u4e3b\u2026\u2026\u201d\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u6211\u4e0d\u80fd\u8ba9\u4e09\u516c\u4e3b\u518d\u53d7\u4f24 \u5bb3\u3002\u201d \u3000\u3000\u4e2d\u5e74\u7f8e\u5987\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8bf4\u7684\u6709\u7406\uff0c\u8001\u8eab\u82df\u6167\u6708\uff0c\u4f60\u542c\u8fc7\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u82df\u8001\u524d\u8f88\u2026\u2026\u201d\u5c0f\u71d5\u5b50\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u665a\u8f88\u542c\u5e08\u7236\u63d0\u8fc7\u4f60\u8001\u4eba\u5bb6\u3002\u201d \u3000\u3000\u201c\u54fc\uff01\u8001\u778e\u5b50\uff0c\u5982\u4f55\u7f16\u6392\u6211\uff1f\u201d \u3000\u3000\u201c\u6211\u5e08\u7236\u8bf4\uff0c\u4ec0\u4e48\u4eba\u6211\u90fd\u53ef\u4ee5\u62db\u60f9\uff0c\u5c31\u662f\u4e0d\u80fd\u60f9\u4f60\u751f\u6c14\u3002\u201d \u3000\u3000\u201c\u53ea\u8bf4\u4e86\u8fd9\u4e9b\u4e48\uff1f\u201d \u3000\u3000\u201c\u8fd8\u6709\u554a\uff01\u2026\u2026\u201d\u5c0f\u71d5\u5b50\u6025\u6025\u5730\u8bf4\u9053\uff1a\u201c\u5e08\u7236\u8981\u6211\u89c1\u5230\u4f60\u4e4b\u540e\uff0c\u8981\u5927\u793c\u53c2\u62dc\uff0c\u5bf9\u4e86\uff0c\u6211 \u8981\u7ed9\u4f60\u53e9\u5934\u3002\u201d \u3000\u3000\u8bf4\u53e9\u5934\u5c31\u53e9\uff0c\u771f\u7684\u62dc\u4f0f\u4e8e\u5730\uff0c\u78d5\u4e86\u4e00\u4e2a\u5934\u3002 \u3000\u3000\u4e2d\u5e74\u7f8e\u5987\u4f3c\u662f\u88ab\u5c0f\u71d5\u5b50\u95f9\u5f97\u706b\u6c14\u5168\u6d88\uff0c\u6325\u6325\u624b\u9053\uff1a\u201c\u51fa\u53bb\u5427\uff01\u201d \u3000\u3000\u201c\u662f\uff01\u201d\u5c0f\u71d5\u5b50\u53c8\u606d\u606d\u656c\u656c\u7684\u62b1\u62f3\u4e00\u793c\uff0c\u624d\u9000\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u76ee\u7779\u5c0f\u71d5\u5b50\u79bb\u53bb\u4e4b\u540e\uff0c\u82df\u6167\u6708\u624d\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e2b\u5934\uff0c\u8d77\u6765\u4e86\uff0c\u88c5\u4ec0\u4e48\uff0c\u96be\u9053\u4f60\u771f\u80fd \u9a97\u8fc7\u5e08\u7236\u8fd9\u4e00\u5bf9\u773c\u775b\u4e0d\u6210\uff1f\u201d \u3000\u3000\u4e09\u516c\u4e3b\u633a\u8eab\uff0c\u8dc3\u4e0b\u6728\u69bb\uff0c\u62dc\u4f0f\u4e8e\u5730\uff0c\u9053\uff1a\u201c\u5f1f\u5b50\u53e9\u89c1\u5e08\u7236\uff01\u201d \u3000\u3000\u201c\u5509\uff01\u8d77\u6765\uff0c\u8d77\u6765\uff0c\u544a\u8bc9\u6211\uff0c\u90a3\u5c0f\u5b50\u5e2e\u4f60\u7597\u4f24\u65f6\uff0c\u53ef\u66fe\u8f7b\u8584\u8fc7\u4f60\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\uff01\u201d\u4e09\u516c\u4e3b\u9053\uff1a\u201c\u53ea\u662f\u5f92\u513f\u2026\u2026\u201d \u3000\u3000\u82df\u6167\u6708\u76ee\u5149\u5728\u4e09\u516c\u4e3b\u7389\u81c2\u4e0a\u63a0\u8fc7\uff0c\u795e\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u4e2b\u5934\uff0c\u4f60\u7684\u5b88\u5bab\u7802\u2026\u2026\u201d \u3000\u3000\u4e09\u516c\u4e3b\u7a81\u7136\u545c\u545c\u54bd\u54bd\u7684\u54ed\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8fd9\u4e00\u54ed\uff0c\u4f3c\u662f\u628a\u5fc3\u4e2d\u7684\u59d4\u5c48\u3001\u75db\u82e6\uff0c\u5168\u54ed\u4e86\u51fa\u6765\uff0c\u76f4\u54ed\u4e2a\u54c0\u54c0\u6b32\u7edd\u3002 \u3000\u3000\u82df\u6167\u6708\u4e5f\u4e0d\u529d\u6b62\uff0c\u8ba9\u4e09\u516c\u4e3b\u75db\u54ed\u4e86\u4e00\u9635\u540e\uff0c\u624d\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u90a3\u5c0f\u5b50\u65e2\u7136\u5360\u4e86\u4f60\u7684\u8eab\u5b50\uff0c \u90a3\u5c31\u5ac1\u7ed9\u4ed6\u5427\uff01\u7531\u4e3a\u5e08\u4f5c\u4e3b\uff0c\u8001\u778e\u5b50\u6562\u4e0d\u7b54\u5e94\uff0c\u6211\u5c31\u6740\u4e86\u90a3\u5c0f\u5b50\u3002\u201d \u3000\u3000\u201c\u4e0d\u662f\u4ed6\u2026\u2026\u201d\u4e09\u516c\u4e3b\u7a7f\u597d\u4e86\u8863\u670d\uff0c\u9053\uff1a\u201c\u5f92\u513f\u662f\u5728\u897f\u57df\u5931\u8eab\u2026\u2026\u201d \u3000\u3000\u201c\u9b54\u6559\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u5f92\u513f\u63a5\u53d7\u4e86\u9b54\u6559\u56db\u5341\u4e5d\u65e5\u7684\u8003\u9a8c\uff0c\u5ea6\u8fc7\u8003\u9a8c\u4e4b\u5173\uff0c\u5c31\u4e0d\u518d\u903c\u6211\u8fdb\u5165\u9b54\u6559\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u7684\u786e\u662f\u4e00\u4ef6\u5f88\u4e0d\u5bb9\u6613\u7684\u4e8b\uff0c\u4f60\u5ea6\u8fc7\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u5f1f\u5b50\u65e0\u80fd\uff0c\u652f\u6491\u4e86\u4e09\u5341\u65e5\u540e\uff0c\u65e0\u6cd5\u518d\u6491\u4e0b\u53bb\u4e86\uff0c\u773c\u770b\u5c31\u5c06\u6ca6\u4e3a\u9b54\u6559\u5f1f\u5b50\uff0c\u5374\u6709\u4e00\u4e2a\u4eba \u613f\u52a9\u5f1f\u5b50\u6e21\u8fc7\u9b54\u96be\u3002\u201d\u4e09\u516c\u4e3b\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u4ed6\u8981\u4ee5\u5f1f\u5b50\u6e05\u767d\u7684\u8eab\u8eaf\uff0c\u4f5c\u4e3a\u62a5\u7b54\u3002\u201d \u3000\u3000\u201c\u4f60\u7b54\u5e94\u4e86\u4ed6\u2026\u2026\u201d\u82df\u6167\u6708\u8138\u8272\u5e73\u9759\uff0c\u770b\u4e0d\u51fa\u5fc3\u4e2d\u60f3\u4e9b\u4ec0\u4e48\uff1f \u3000\u3000\u201c\u5f1f\u5b50\u8861\u91cf\u5229\u5bb3\u3001\u5f97\u5931\u4e4b\u540e\uff0c\u7b54\u5e94\u4e86\u4ed6\uff0c\u56e0\u4e3a\u6ca6\u5165\u9b54\u6559\u4e5f\u65e0\u6cd5\u4fdd\u6301\u6e05\u767d\u8eab\u8eaf\uff0c\u5f1f\u5b50\u6ee1\u8179 \u60b2\u5fff\uff0c\u53ea\u671b\u7559\u4e0b\u6027\u547d\u3001\u65e5\u540e\u597d\u62a5\u6b64\u4ec7\uff01\u201d \u3000\u3000\u82df\u6167\u6708\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u90a3\u4e2a\u4eba\u5462\uff1f\u201d \u3000\u3000\u201c\u4ecd\u5728\u897f\u57df\u9b54\u5bab\u4e4b\u4e2d\u2026\u2026\u201d\u4e09\u516c\u4e3b\u8bf4\u9053\uff1a\u201c\u4ed6\u672c\u662f\u9b54\u6559\u5f1f\u5b50\u3002\u201d \u3000\u3000\u201c\u5ea6\u8fc7\u4e86\u8fd9\u6b21\u96be\u5173\u4e4b\u540e\uff0c\u4ed6\u4eec\u5c31\u653e\u4f60\u91cd\u56de\u5929\u671d\uff1f\u201d \u3000\u3000\u201c\u90a3\u9b54\u6559\u7684\u638c\u6559\u5012\u662f\u8bf4\u8bdd\u7b97\u8bdd\uff0c\u7ecf\u8fc7\u4e86\u4e00\u573a\u8003\u9a8c\u4e4b\u540e\uff0c\u5c31\u672a\u518d\u8feb\u6211\u5165\u6559\uff0c\u5f1f\u5b50\u5728\u897f\u57df\u7559 \u4f4f\u4e86\u4e24\u5e74\uff0c\u624d\u88ab\u9001\u56de\u4eac\u57ce\u3002\u201d \u3000\u3000\u201c\u5176\u4ed6\u7684\u6211\u90fd\u77e5\u9053\u4e86\uff0c\u4f60\u662f\u53bb\u63a2\u671b\u4e3a\u5e08\u65f6\uff0c\u88ab\u4ed6\u4eec\u63b3\u52ab\u53bb\u7684\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\uff0c\u5f1f\u5b50\u77e5\u9053\u5e08\u7236\u5373\u5c06\u95ed\u5173\uff0c\u5e0c\u671b\u5728\u95ed\u5173\u4e4b\u524d\u80fd\u548c\u5e08\u7236\u5c0f\u805a\u6570\u65e5\uff0c\u6545\u800c\u5357\u4e0b\u63a2\u671b\uff0c \u4e0d\u6599\u5f1f\u5b50\u65e0\u80fd\uff0c\u7adf\u88ab\u4eba\u63b3\u53bb\u3002\u201d \u3000\u3000\u201c\u4e0d\u7528\u518d\u8bf4\u4e0b\u53bb\u4e86\uff0c\u6211\u51fa\u5173\u4e4b\u540e\uff0c\u5c31\u6765\u770b\u4f60\uff0c\u4e00\u8def\u884c\u6765\uff0c\u6c5f\u6e56\u4f9d\u65e7\uff0c\u53ea\u662f\u611f\u89c9\u5230\u6709\u4e9b\u4e0d \u5bf9\uff0c\u4f46\u4e00\u65f6\u4e4b\u95f4\u53c8\u8bf4\u4e0d\u51fa\u6765\uff0c\u5509\uff01\u8fd9\u4e00\u6b21\u6211\u5012\u8981\u62bd\u51fa\u4e00\u4e9b\u65f6\u95f4\uff0c\u5bdf\u770b\u4e00\u4e0b\u6c5f\u6e56\u4e0a\u6709\u4e86\u4ec0\u4e48\u53d8 \u8fc1\u3002\u201d \u3000\u3000\u5979\u6ca1\u6709\u8d23\u5907\u4e09\u516c\u4e3b\uff0c\u663e\u7136\u5bf9\u4e09\u516c\u4e3b\u7684\u51b3\u5b9a\u5e76\u4e0d\u53cd\u5bf9\uff0c\u4f46\u5979\u7edd\u53e3\u4e0d\u63d0\u4e3a\u4e09\u516c\u4e3b\u62a5\u4ec7\u7684\u4e8b\uff0c \u5374\u8ba9\u4e09\u516c\u4e3b\u5927\u611f\u5947\u602a\u3002 \u3000\u3000\u4f9d\u7740\u82df\u6167\u6708\u7684\u813e\u6c14\uff0c\u4e00\u5b9a\u4f1a\u6740\u5165\u9b54\u5bab\uff0c\u4e3a\u8fd9\u4e2a\u91d1\u679d\u7389\u53f6\u7684\u5f92\u5f1f\u51fa\u4e00\u53e3\u6c14\u3002 \u3000\u3000\u4f46\u5979\u7adf\u7136\u4e00\u53e5\u8bdd\u4e5f\u4e0d\u63d0\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u884c\u5230\u5ba4\u5916\uff0c\u53ea\u89c1\u5c0f\u9053\u58eb\u3001\u5c0f\u548c\u5c1a\u3001\u738b\u91cd\u5c71\u4e09\u4e2a\u4eba\uff0c\u805a\u5728\u4e00\u5757\u513f\u4f4e\u58f0\u4ea4\u8c08\u3002 \u3000\u3000\u770b\u5230\u5c0f\u71d5\u5b50\u884c\u8fc7\u6765\uff0c\u4e09\u4e2a\u4eba\u624d\u9762\u9732\u559c\u8272\uff0c\u8fce\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u5c0f\u548c\u5c1a\u7b11\u9053\uff1a\u201c\u5c0f\u71d5\u5b50\uff0c\u89c1\u5230\u4e86\u82df\u5973\u4fa0\u4e48\uff1f\u201d \u3000\u3000\u201c\u89c1\u5230\u4e86\u3002\u201d \u3000\u3000\u201c\u542c\u8bf4\u82df\u6167\u6708\u7684\u813e\u6c14\u5f88\u574f\uff0c\u662f\u4e0d\u662f\u5403\u4e86\u4e00\u987f\u82e6\u5934\uff1f\u201d\u5c0f\u9053\u58eb\u5927\u4e3a\u5173\u5fc3\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u4f60\u4eec\u5982\u679c\u80af\u4f20\u4e2a\u8b66\u793a\u7ed9\u6211\uff0c\u5c31\u4e0d\u4f1a\u5f04\u5f97\u90a3\u4e48\u96be\u770b\u2026\u2026\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u54ed\u58f0\u7531\u623f\u4e2d\u4f20\u51fa\uff0c\u5c0f\u71d5\u5b50\u5927\u611f\u4e0d\u5b89\uff0c\u53c8\u9053\uff1a\u201c\u6211\u867d\u6ca1\u6709\u6328\u9a82\uff0c\u53ea\u6015\u4e09\u516c\u4e3b\u8981\u53d7\u4e00 \u987f\u75db\u8d23\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u66ff\u516c\u4e3b\u7597\u4f24\u662f\u4ef6\u597d\u4e8b\u554a\uff01\u201d\u738b\u91cd\u5c71\u9053\uff1a\u201c\u82df\u6167\u6708\u600e\u4f1a\u8d23\u9a82\u516c\u4e3b\u5462\uff1f\u201d \u3000\u3000\u201c\u597d\u4e8b\u662f\u597d\u4e8b\uff0c\u53ea\u662f\u2026\u2026\u53ea\u662f\u6709\u70b9\u4e0d\u592a\u96c5\u89c2\u3002\u201d \u3000\u3000\u201c\u600e\u4e48\u56de\u4e8b\u5462\uff1f\u201d\u738b\u91cd\u5c71\u6253\u7834\u7802\u9505\u95ee\u5230\u5e95\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u8bf4\u51fa\u4e86\u7597\u4f24\u7684\u7ecf\u8fc7\u3002 \u3000\u3000\u5c0f\u548c\u5c1a\u4f4e\u58f0\u9053\uff1a\u201c\u6211\u7684\u5988\u5440\uff01\u771f\u662f\u6211\u4f5b\u6709\u7075\uff0c\u82df\u6167\u6708\u6ca1\u6709\u4e00\u5251\u6740\u4e86\u4f60\uff01\u201d \u3000\u3000\u201c\u8fd9\u4ee5\u540e\uff0c\u8981\u4e09\u516c\u4e3b\u5982\u4f55\u505a\u4eba\u54ea\uff1f\u201d\u738b\u91cd\u5c71\u662f\u5bab\u536b\u51fa\u8eab\uff0c\u4e5f\u77e5\u9053\u516c\u4e3b\u8eab\u4efd\u5c0a\u8d35\u3002 \u3000\u3000\u201c\u6211\u770b\uff0c\u8fd9\u4ef6\u4e8b\u7edd\u4e0d\u80fd\u6cc4\u9732\u51fa\u53bb\uff01\u201d\u5c0f\u9053\u58eb\u4e00\u526f\u8db3\u667a\u591a\u8c0b\u7684\u6837\u5b50\uff0c\u9053\uff1a\u201c\u6211\u4eec\u8981\u5b88\u53e3\u5982 \u74f6\uff0c\u5c0f\u71d5\u5b50\uff0c\u4f60\u66f4\u8981\u7d27\u8bb0\u5728\u5fc3\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u8ba9\u5b83\u95f7\u5728\u5fc3\u91cc\uff0c\u8fde\u4f60\u7239\u4e5f\u4e0d\u80fd\u544a\u8bc9\u4ed6\u3002\u201d \u3000\u3000\u201c\u597d\u5427\uff0c\u672c\u6765\u6211\u89c9\u5f97\u8fd9\u4ef6\u4e8b\u6ca1\u6709\u4ec0\u4e48\u4e25\u91cd\uff0c\u4e5f\u4e0d\u89c9\u5f97\u6709\u4ec0\u4e48\u4e0d\u5bf9\uff1f\u2026\u2026\u201d\u5c0f\u71d5\u5b50\u9053\uff1a \u201c\u4f60\u4eec\u8fd9\u4e48\u8bf4\u4e86\uff0c\u6211\u8bb0\u5728\u5fc3\u91cc\u5c31\u662f\u3002\u201d \u3000\u3000\u201c\u8fd8\u4e0d\u4e25\u91cd\u554a\uff01\u201d\u738b\u91cd\u5c71\u9053\uff1a\u201c\u8fd9\u6709\u5173\u4e00\u4e2a\u5973\u4eba\u7684\u540d\u8282\uff0c\u4f55\u51b5\uff0c\u5979\u8fd8\u662f\u4e00\u4f4d\u516c\u4e3b\u3002\u201d \u3000\u3000\u201c\u597d\u4e86\uff0c\u6211\u4e0d\u8bf4\u5c31\u662f\uff0c\u4e0d\u8fc7\uff0c\u5979\u60f3\u548c\u54b1\u4eec\u4e00\u8d77\u95ef\u8361\u6c5f\u6e56\uff0c\u4f60\u4eec\u540c\u610f\u4e0d\u540c\u610f\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u4e0d\u5927\u597d\u5427\u2026\u2026\u201d\u5c0f\u548c\u5c1a\u8bf4\uff1a\u201c\u5979\u662f\u516c\u4e3b\u554a\uff01\u54b1\u4eec\u56db\u4e2a\u4eba\u4e00\u5929\u5230\u665a\u4f8d\u5019\u7740\u5979\uff0c \u90a3\u53ef\u662f\u6d3b\u53d7\u7f6a\u554a\uff01\u201d \u3000\u3000\u201c\u4e0d\u4f1a\u7684\uff0c\u5979\u544a\u8bc9\u6211\uff0c\u5979\u548c\u54b1\u4eec\u4e00\u6837\uff0c\u54b1\u4eec\u542c\u6c5f\u53d4\u53d4\u7684\uff0c\u5979\u4e5f\u8ddf\u54b1\u4eec\u4e00\u6837\uff0c\u542c\u547d\u884c\u4e8b\uff0c \u7528\u4e0d\u7740\u54b1\u4eec\u7167\u987e\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4ef6\u4e8b\uff0c\u6050\u6015\u4e09\u516c\u4e3b\u4e5f\u4f5c\u4e0d\u4e86\u4e3b\u2026\u2026\u201d\u5c0f\u9053\u58eb\u9053\uff1a\u201c\u82df\u6167\u6708\u65e2\u7136\u6765\u4e86\uff0c\u4e00\u5207\u90fd\u5f97\u542c\u5979 \u7684\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u201d\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u5979\u8981\u8ddf\u6211\u4eec\u5230\u6c5f\u6e56\u4e0a\u884c\u8d70\uff0c\u5c31\u5f97\u7167\u6211\u4eec\u7684\u89c4\u77e9\uff0c\u8981\u4e0d\u7136\u5c31\u4e0d\u5e26\u5979 \u53bb\u3002\u201d \u3000\u3000\u738b\u91cd\u5c71\u6447\u6447\u5934\uff0c\u6b32\u8a00\u53c8\u6b62\uff0c\u5fc3\u4e2d\u5374\u6697\u6697\u5fd6\u9053\uff1a\u5c0f\u71d5\u5b50\u4e00\u7247\u7eaf\u6d01\uff0c\u4e0d\u77e5\u5929\u9ad8\u5730\u539a\uff0c\u5979\u662f\u516c \u4e3b\u554a\uff0c\u600e\u4e48\u80fd\u8981\u5979\u6309\u6211\u4eec\u7684\u89c4\u77e9\u884c\u4e8b\uff0c\u6211\u4eec\u90fd\u542c\u5979\u7684\u624d\u5bf9\u5440\uff01 \u3000\u3000\u5c0f\u548c\u5c1a\u7a81\u7136\u626d\u8f6c\u8bdd\u9898\uff0c\u9053\uff1a\u201c\u4e24\u4e2a\u9b54\u6559\u7684\u4eba\u4e0d\u4f1a\u5c31\u8fd9\u6837\u6d88\u5931\u5728\u542c\u8749\u9662\u4e2d\uff0c\u6211\u4eec\u8981\u60f3\u4e2a\u529e \u6cd5\uff0c\u627e\u51fa\u6765\u624d\u662f\u3002\u201d \u3000\u3000\u201c\u4f1a\u4e0d\u4f1a\u5df2\u7ecf\u79bb\u5f00\u4e86\uff1f\u201d\u738b\u91cd\u5c71\u9053\uff1a\u201c\u5c0f\u71d5\u5b50\u5df2\u641c\u5bdf\u8fc7\u8fd9\u91cc\u7684\u82b1\u6811\u5047\u5c71\u3002\u201d \u3000\u3000\u201c\u5e94\u8be5\u4e0d\u4f1a\uff01\u201d\u5c0f\u548c\u5c1a\u8bf4\uff1a\u201c\u6211\u521a\u521a\u5077\u77a7\u4e86\u4e00\u4e0b\uff0c\u6574\u5ea7\u7684\u542c\u8749\u9662\u90fd\u5df2\u88ab\u5185\u5bab\u4f8d\u536b\u91cd\u91cd\u7684 \u5305\u56f4\u8d77\u6765\uff0c\u4eba\u6570\u4e4b\u591a\uff0c\u53ea\u6015\u6709\u4e24\u767e\u4eba\u4ee5\u4e0a\uff0c\u738b\u5f64\u4eb2\u81ea\u6307\u6325\u5750\u9547\uff0c\u522b\u8bf4\u662f\u4eba\u4e86\uff0c\u5c31\u662f\u4e00\u53ea\u98de\u9e1f \u4e5f\u4e0d\u5bb9\u6613\u95ef\u8fc7\u53bb\u3002\u201d \u3000\u3000\u201c\u82df\u6167\u6708\u53ef\u4ee5\u8fdb\u6765\uff0c\u9b54\u6559\u4e2d\u4eba\u5c31\u6709\u51fa\u53bb\u7684\u673a\u4f1a\u3002\u201d\u738b\u91cd\u5c71\u63d0\u51fa\u8d28\u7591\u3002 \u3000\u3000\u201c\u90a3\u4e0d\u540c\uff0c\u4f60\u53d4\u53d4\u8ba4\u8bc6\u82df\u6167\u6708\uff0c\u4e5f\u77e5\u9053\u5979\u662f\u4e09\u516c\u4e3b\u7684\u5e08\u7236\u2026\u2026\u201d\u5c0f\u548c\u5c1a\u9053\uff1a\u201c\u81ea\u7136\u53ef\u4ee5 \u653e\u5979\u8fdb\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e9b\u4e8b\uff0c\u7528\u4e0d\u7740\u6211\u4eec\u70e6\u5fc3\u4e86\uff0c\u82df\u6167\u6708\u5230\u4e86\uff0c\u54b1\u4eec\u53ea\u8981\u542c\u547d\u529e\u4e8b\u5c31\u6210\u4e86\u3002\u201d\u5c0f\u9053\u58eb\u4f5c\u4e86 \u7ed3\u8bba\u3002 \u3000\u3000\u8c08\u8bdd\u4e4b\u95f4\uff0c\u601c\u82b1\u5df2\u884c\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u516c\u4e3b\u4e3b\u8bf7\u56db\u4f4d\u5230\u5385\u4e2d\u8bae\u4e8b\u3002\u201d \u3000\u3000\u56db\u4eba\u5165\u5385\u540e\u4e0d\u4e45\uff0c\u82df\u6167\u6708\u5df2\u5e26\u7740\u4e09\u516c\u4e3b\u884c\u5165\u5385\u4e2d\u3002 \u3000\u3000\u5bf9\u8fd9\u4f4d\u8a89\u6ee1\u6c5f\u6e56\u7684\u5973\u4fa0\uff0c\u5c0f\u548c\u5c1a\u7b49\u90fd\u6709\u7740\u6781\u9ad8\u7684\u656c\u6155\uff0c\u9f50\u9f50\u8d77\u8eab\uff0c\u884c\u793c\u62dc\u89c1\u3002 \u3000\u3000\u82df\u6167\u6708\u6325\u6325\u624b\uff0c\u793a\u610f\u51e0\u4eba\u5750\u4e0b\uff0c\u5750\u9053\uff1a\u201c\u5c0f\u5f92\u7684\u4e8b\u627f\u51e0\u4f4d\u63f4\u624b\u76f8\u52a9\uff0c\u8001\u8eab\u5341\u5206\u611f\u6fc0\uff0c\u4f46 \u76ee\u4e0b\u6700\u91cd\u8981\u7684\u4e8b\uff0c\u662f\u8bbe\u6cd5\u89e3\u53bb\u4e09\u516c\u4e3b\u7684\u866b\u6bd2\uff0c\u5c31\u8001\u8eab\u6240\u77e5\uff0c\u516c\u4e3b\u4e2d\u7684\u662f\u6d3b\u866b\uff0c\u90a3\u662f\u4e07\u866b\u95e8\u4e2d \u6700\u9ad8\u7684\u866b\u672f\u4e4b\u4e00\uff0c\u5e78\u597d\u4e0b\u866b\u4eba\u8fd8\u5728\u8fd9\u91cc\uff0c\u8fd9\u79cd\u866b\u672f\u867d\u7136\u5229\u5bb3\uff0c\u4f46\u4e5f\u597d\u533b\u6cbb\uff0c\u53ea\u8981\u4e0b\u866b\u4eba\u65bd \u672f\uff0c\u5f15\u51fa\u6d3b\u866b\uff0c\u4eba\u5c31\u7acb\u523b\u75ca\u6108\uff0c\u6240\u4ee5\uff0c\u6211\u4eec\u8981\u751f\u64d2\u6b64\u4eba\u3002\u201d \u3000\u3000\u201c\u662f\uff0c\u6211\u4eec\u542c\u5019\u5973\u4fa0\u5dee\u9063\uff01\u201d\u56db\u4eba\u9f50\u58f0\u56de\u7b54\u3002"}),showOnHomepage:!0},{name:"\u7b2c\u5341\u4e03\u56de\u3000\u964d\u9b54\u9a71\u866b",content:(0,t.jsx)(t.Fragment,{children:"\u4e00\u9053\u98de\u8679\u82df\u6167\u6708\u5f00\u59cb\u5206\u6d3e\u804c\u52a1\u4e86\u3002 \u3000\u3000\u201c\u5c0f\u71d5\u5b50\uff0c\u4f60\u548c\u8001\u8eab\u8d1f\u8d23\u64d2\u62ff\u4e0b\u866b\u7684\u4eba\u3002\u201d \u3000\u3000\u82df\u6167\u6708\u76ee\u5149\u4e00\u63a0\u9ed1\u7f57\u6c49\u548c\u767d\u7fbd\u9053\u957f\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4eba\u62c5\u4efb\u5b88\u536b\u7684\u8d23\u4efb\uff0c\u4ee5\u963b\u6b62\u4ed6\u4eec\u7684 \u53bb\u8def\uff0c\u52a1\u6c42\u4e00\u51fb\u6210\u529f\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u665a\u8f88\u5df2\u641c\u67e5\u8fc7\u542c\u8749\u9662\u2026\u2026\u201d\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u627e\u4e0d\u5230\u4ed6\u4eec\u7684\u85cf\u8eab\u4e4b\u5904\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u8eb2\u5728\u6211\u7ec3\u529f\u7684\u5bc6\u5ba4\u4e2d\uff0c\u90a3\u91cc\u94c1\u95e8\u77f3\u58c1\uff0c\u6df1\u5904\u5730\u4e0b\u2026\u2026\u201d \u3000\u3000\u4e09\u516c\u4e3b\u9053\uff1a\u201c\u8981\u653b\u5165\u5bc6\u5ba4\uff0c\u53ea\u6015\u8981\u82b1\u8d39\u4e00\u6bb5\u65f6\u95f4\u624d\u884c\u3002\u201d \u3000\u3000\u201c\u529b\u654c\u4e0d\u5982\u667a\u53d6\uff01\u201d\u9ed1\u7f57\u6c49\u9053\uff1a\u201c\u4f46\u91cd\u8981\u7684\u662f\u5148\u8981\u4e86\u89e3\u4ed6\u4eec\u662f\u5426\u6015\u6b7b\uff1f\u201d \u3000\u3000\u201c\u9b54\u6559\u4e2d\u4eba\u987d\u56fa\u608d\u5f3a\uff0c\u4e0d\u5230\u7edd\u5730\uff0c\u4ed6\u4eec\u4e0d\u4f1a\u81ea\u520e\u3002\u201d\u4e09\u516c\u4e3b\u8bf4\u51fa\u4e86\u5979\u7684\u770b\u6cd5\u3002 \u3000\u3000\u82df\u6167\u6708\u9053\uff1a\u201c\u4f60\u662f\u9ed1\u7f57\u6c49\u4e03\u5de7\u50e7\u5427\uff1f\u201d \u3000\u3000\u201c\u6b63\u662f\u5c0f\u50e7\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u6c5f\u6e56\u4e0a\u4f20\u8bf4\u4f60\u667a\u8ba1\u767e\u51fa\uff0c\u8bf4\u8bf4\u770b\uff0c\u4f60\u6709\u4ec0\u4e48\u529e\u6cd5\uff1f\u201d \u3000\u3000\u201c\u5c0f\u548c\u5c1a\u7684\u770b\u6cd5\uff0c\u5e94\u8be5\u91c7\u7528\u6c34\u3001\u706b\u653b\u52bf\u6700\u4e3a\u6709\u6548\u2026\u2026\u201d \u3000\u3000\u201c\u597d\u529e\u6cd5\uff01\u201d\u82df\u6167\u6708\u9053\uff1a\u201c\u8001\u8eab\u5230\u6b64\u7684\u6d88\u606f\u7edd\u4e0d\u80fd\u6cc4\u9732\uff0c\u4ee5\u4fbf\u51fa\u5176\u4e0d\u610f\uff0c\u4e00\u51fb\u6210\u529f\u3002\u201d \u3000\u3000\u9ed1\u7f57\u6c49\u8bf4\u51fa\u4e86\u4e00\u756a\u8ba1\u5212\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u7ec3\u529f\u7684\u5bc6\u5ba4\uff0c\u5728\u4e00\u5ea7\u53a2\u623f\u4e4b\u4e0b\uff0c\u5c0f\u548c\u5c1a\u7684\u51b3\u5b9a\u662f\u5148\u7528\u70df\u718f\u518d\u706b\u653b\uff0c\u4e8c\u5341\u62c5\u5e72\u67f4\u5728 \u5bc6\u5ba4\u53e3\u5904\u71c3\u8d77\uff0c\u52a0\u4e0a\u4e00\u4e9b\u6850\u6cb9\uff0c\u706b\u52bf\u71c3\u5f97\u5f88\u65fa\uff0c\u5f3a\u70c8\u7684\u6d53\u70df\u62b5\u9699\u800c\u5165\u3002 \u3000\u3000\u4e24\u4e2a\u9b54\u6559\u957f\u8001\u679c\u7136\u662f\u9690\u85cf\u5728\u5bc6\u5ba4\u4e4b\u4e2d\u3002 \u3000\u3000\u4ed6\u4eec\u5e76\u6ca1\u6709\u628a\u8fd9\u4e9b\u4f8d\u536b\u653e\u5728\u5fc3\u4e0a\uff0c\u53ea\u662f\u8ba1\u5212\u5f15\u53d1\u4e09\u516c\u4e3b\u7684\u866b\u6bd2\uff0c\u8ba9\u4e09\u516c\u4e3b\u5c55\u5f00\u4e00\u573a\u5c60 \u6740\u3002 \u3000\u3000\u4ed6\u4eec\u77e5\u9053\u4e09\u516c\u4e3b\u7684\u5251\u6cd5\u7cbe\u7edd\uff0c\u4e5f\u5c31\u4e50\u5f97\u8896\u624b\u770b\u70ed\u95f9\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u7b2c\u4e8c\u6b65\u8ba1\u5212\u662f\uff0c\u53ec\u96c6\u6c47\u805a\u5728\u4eac\u4e2d\u7684\u9b54\u6559\u5f1f\u5b50\uff0c\u8981\u4ed6\u4eec\u4e24\u5929\u540e\u96c6\u9f50\u4eba\u624b\uff0c\u51c6\u5907\u5927\u95f9\u7687 \u5bab\uff0c\u6700\u597d\u4e00\u4e3e\u628a\u7687\u5e1d\u8001\u513f\u6293\u4f4f\uff0c\u8fd9\u5c31\u517b\u7cbe\u84c4\u9510\uff0c\u8eb2\u5728\u5730\u4e0b\u5bc6\u5ba4\u4e2d\u6253\u5750\u8c03\u606f\u3002 \u3000\u3000\u8fd9\u4e00\u6bb5\u65f6\u65e5\u5728\u5bab\u4e2d\u86f0\u4f0f\uff0c\u4ed6\u4eec\u5bf9\u5bab\u4e2d\u7684\u5b9e\u529b\u5df2\u6709\u4e86\u5927\u6982\u7684\u4e86\u89e3\uff0c\u53ec\u96c6\u4e86\u5206\u914d\u5728\u4eac\u4e2d\u7684\u9b54 \u6559\u5f1f\u5b50\uff0c\u529b\u91cf\u8db3\u53ef\u4e00\u62fc\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u5374\u672a\u6599\u5230\u5c0f\u71d5\u5b50\u548c\u9ed1\u7f57\u6c49\u8fd9\u80a1\u7a81\u7136\u51fa\u73b0\u7684\u529b\u91cf\u3002 \u3000\u3000\u4ed6\u4eec\u5fc3\u76ee\u4e2d\u552f\u4e00\u7684\u52b2\u654c\u662f\u6458\u661f\u624b\u738b\u5f64\u3002 \u3000\u3000\u6d53\u70df\u52a0\u4e0a\u6251\u9f3b\u6b32\u5455\u7684\u6850\u6cb9\u6c14\u5473\uff0c\u4e24\u4e2a\u6b66\u529f\u7cbe\u6df1\u7684\u9b54\u6559\u957f\u8001\u4e5f\u4e00\u6837\u65e0\u6cd5\u5fcd\u53d7\u3002 \u3000\u3000\u4e00\u5207\u90fd\u5982\u9ed1\u7f57\u6c49\u9884\u6599\uff0c\u4e00\u4e2a\u65f6\u8fb0\u4e4b\u540e\uff0c\u4ed6\u4eec\u6253\u5f00\u5bc6\u5ba4\u7684\u94c1\u95e8\u51b2\u4e86\u51fa\u6765\uff0c\u6311\u5f00\u4e00\u90e8\u5206\u71c3\u70e7 \u7684\u5e72\u67f4\uff0c\u5728\u6d53\u70df\u4e2d\u98de\u6251\u800c\u51fa\u3002 \u3000\u3000\u4e45\u95fb\u9b54\u6559\u6280\u827a\uff0c\u5c0f\u548c\u5c1a\u5b58\u7740\u4e00\u8bd5\u4e4b\u5fc3\uff0c\u89c1\u4e24\u4e2a\u9ed1\u8863\u4eba\u7531\u6d53\u70df\u4e2d\u98de\u5c04\u800c\u51fa\uff0c\u8fce\u9762\u5c31\u662f\u4e00 \u638c\u3002 \u3000\u3000\u9ed1\u7f57\u6c49\u662f\u8bda\u5fc3\u8bd5\u8bd5\uff0c\u53cc\u638c\u7adf\u7136\u63a5\u5b9e\u3002 \u3000\u3000\u84ec\u7136\u8f7b\u9707\u4e2d\uff0c\u9ed1\u7f57\u6c49\u8dc3\u8d77\u7684\u8eab\u5b50\uff0c\u88ab\u9707\u9000\u4e94\u516d\u5c3a\u8fdc\uff0c\u843d\u7740\u5b9e\u5730\u3002 \u3000\u3000\u4f46\u90a3\u9ed1\u8863\u4eba\u53d7\u5230\u638c\u529b\u4e00\u963b\uff0c\u4e5f\u5411\u4e0b\u843d\u53bb\u3002 \u3000\u3000\u90a3\u6b63\u662f\u70c8\u706b\u71c3\u70e7\u7684\u5730\u65b9\uff0c\u8863\u670d\u867d\u4e3a\u706b\u52bf\u71c3\u7740\uff0c\u4f46\u4eba\u5374\u6ca1\u6709\u53d7\u4f24\uff0c\u7b2c\u4e8c\u5ea6\u98de\u8eab\u8dc3\u843d\u5728\u5ead\u9662 \u5916\u9762\uff0c\u5c31\u5730\u4e00\u6eda\uff0c\u7184\u53bb\u4e86\u706b\u52bf\u3002 \u3000\u3000\u8fd9\u7b49\u653e\u706b\u903c\u654c\u7684\u5927\u9635\u4ed7\uff0c\u738b\u5f64\u4e5f\u4e0d\u6562\u81ea\u4f5c\u51b3\u5b9a\uff0c\u9762\u62a5\u7687\u5e1d\uff0c\u8bf7\u65e8\u88c1\u51b3\u3002 \u3000\u3000\u7687\u5e1d\u8981\u738b\u5f64\u65e9\u4f5c\u51c6\u5907\uff0c\u4e0d\u8981\u4f7f\u706b\u52bf\u5931\u63a7\uff0c\u4e00\u5207\u90fd\u7167\u738b\u5f64\u7684\u610f\u601d\u53bb\u529e\u3002 \u3000\u3000\u4f46\u738b\u5f64\u77a7\u5f97\u51fa\u7687\u5e1d\u5fc3\u4e2d\u7684\u4e0d\u5feb\uff0c\u5bf9\u9b54\u6559\u4e2d\u4eba\u6f5c\u5165\u7687\u5bab\u7684\u4e8b\uff0c\u6709\u4e9b\u4e0d\u6ee1\u3002 \u3000\u3000\u7687\u5e1d\u6ca1\u6709\u8d23\u53f1\u738b\u5f64\uff0c\u4f46\u4e5f\u6ca1\u6709\u7ec6\u95ee\u8be6\u60c5\uff0c\u66f4\u6ca1\u6709\u7247\u8a00\u4e00\u8bed\u8d50\u95ee\u4e09\u516c\u4e3b\uff0c\u663e\u7136\u662f\u5bf9\u4e09\u516c\u4e3b \u5df2\u4e0d\u6ee1\u610f\u3002 \u3000\u3000\u7231\u5973\u867d\u7136\u91cd\u8981\uff0c\u4f46\u628a\u9b54\u6559\u4e2d\u4eba\u9690\u533f\u5bab\u4e2d\uff0c\u5f71\u54cd\u5230\u7687\u5e1d\u7684\u5b89\u5168\uff0c\u66f4\u662f\u8d50\u6b7b\u7684\u5927\u7f6a\uff0c\u80fd\u4e0d\u8ffd \u95ee\uff0c\u5df2\u7ecf\u662f\u7687\u6069\u6d69\u8361\u4e86\u3002 \u3000\u3000\u4e24\u4e2a\u9ed1\u8863\u4eba\u843d\u5165\u5ead\u9662\uff0c\u82df\u6167\u6708\u3001\u5c0f\u71d5\u5b50\u8fce\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u56db\u5468\u6570\u5341\u4e2a\u4f8d\u536b\uff0c\u867d\u672a\u903c\u8fd1\uff0c\u4f46\u5374\u91c7\u53d6\u4e86\u5408\u56f4\u4e4b\u52bf\u3002 \u3000\u3000\u9ed1\u7f57\u6c49\u3001\u5c0f\u9053\u58eb\u3001\u738b\u91cd\u5c71\u6321\u5728\u5bc6\u5ba4\u524d\u9762\uff0c\u5df2\u6709\u5341\u51e0\u540d\u4f8d\u536b\u5165\u5ba4\u653e\u706b\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u6253\u91cf\u654c\u4eba\uff0c\u53d1\u89c9\u601c\u82b1\u8bf4\u7684\u4e0d\u9519\uff0c\u4e24\u4e2a\u4eba\u867d\u662f\u4e00\u7537\u4e00\u5973\uff0c\u4f46\u5374\u4e00\u822c\u7684\u9ad8\u5927\uff0c\u7537\u7684\u8d64 \u624b\u7a7a\u62f3\uff0c\u5973\u7684\u624b\u4e2d\u5374\u63d0\u7740\u4e00\u679d\u9e20\u5934\u6756\u3002 \u3000\u3000\u82df\u6167\u6708\u6253\u91cf\u4e24\u4eba\u4e00\u773c\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u54ea\u4e00\u4f4d\u662f\u4e07\u866b\u95e8\u4e2d\u4eba\uff1f\u201d \u3000\u3000\u201c\u8001\u8eab\u51fa\u8eab\u4e07\u866b\u95e8\uff0c\u4f46\u73b0\u5728\u5df2\u8eab\u4e3a\u9b54\u6559\u957f\u8001\uff01\u201d\u9ed1\u8863\u8001\u5987\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u770b\u4eca\u591c\u8fd9\u4e2a\u9635 \u4ed7\uff0c\u4f60\u4eec\u662f\u51c6\u5907\u52a8\u624b\u4e86\uff1f\u201d\u76ee\u5149\u8f6c\u52a8\uff0c\u56db\u4e0b\u6253\u91cf\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5929\u8272\u5df2\u6697\uff0c\u591c\u5e55\u5c06\u5782\u3002 \u3000\u3000\u4f46\u738b\u5f64\u65e9\u5df2\u6709\u5907\uff0c\u4f4e\u559d\u4e00\u58f0\uff1a\u201c\u4eae\u706f\uff01\u201d \u3000\u3000\u5239\u90a3\u95f4\uff0c\u706b\u5149\u95ea\u8d77\uff0c\u4eae\u8d77\u4e86\u5341\u51e0\u652f\u706b\u628a\uff0c\u8fd8\u6709\u4e94\u76cf\u5b54\u660e\u706f\uff0c\u5206\u7531\u4e0d\u540c\u7684\u65b9\u4f4d\u7167\u5c04\u8fdb\u6765\u3002 \u3000\u3000\u6574\u5ea7\u7684\u542c\u8749\u9662\uff0c\u90fd\u5728\u706b\u5149\u7684\u7167\u8000\u4e0b\u3002 \u3000\u3000\u82df\u6167\u6708\u770b\u56db\u5468\u9632\u5b88\u7684\u5e03\u7f6e\uff0c\u4f30\u7b97\u654c\u4eba\u5df2\u5b8c\u5168\u5931\u53bb\u4e86\u9ed1\u591c\u9041\u9003\u7684\u4f18\u52bf\uff0c\u624d\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u542c\u8bf4\u4e07\u866b\u95e8\u4e2d\uff0c\u80fd\u591f\u65bd\u4e0b\u6d3b\u866b\u7684\u53ea\u6709\u5c11\u6570\u51e0\u4eba\uff0c\u60f3\u6765\uff0c\u4f60\u5728\u4e07\u866b\u95e8\u4e2d\u7684\u8eab\u4efd\uff0c\u5b9a\u7136\u662f\u5f88\u9ad8 \u4e86\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u4f60\u53ef\u662f\u60f3\u8bd5\u8bd5\u8001\u8eab\u65bd\u4e0b\u6d3b\u866b\u7684\u624b\u6bb5\u4e48\uff1f\u201d \u3000\u3000\u201c\u53ef\u4ee5\u3002\u4e0d\u8fc7\uff0c\u4f60\u5148\u56de\u7b54\u6211\u51e0\u95ee\u9898\uff1f\u201d \u3000\u3000\u201c\u90a3\u8981\u770b\u8001\u8eab\u662f\u5426\u9ad8\u5174\u56de\u7b54\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u8981\u6295\u5165\u9b54\u6559\uff1f\u201d \u3000\u3000\u9ed1\u8863\u5987\u4eba\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u95ee\u5f97\u592a\u591a\u4e86\uff01\u201d \u3000\u3000\u7a81\u7136\u4e00\u626c\u53f3\u624b\uff0c\u4e24\u70b9\u7eff\u8292\u75be\u98de\u800c\u81f3\u3002 \u3000\u3000\u9762\u5bf9\u7740\u4f1a\u65bd\u653e\u6d3b\u866b\u7684\u4eba\uff0c\u4e00\u9053\u98de\u8679\u82df\u6167\u6708\u4e5f\u4e0d\u6562\u5fc3\u5b58\u5927\u610f\uff0c\u7acb\u523b\u62d4\u5251\u51fb\u51fa\u3002 \u3000\u3000\u5251\u5165\u624b\uff0c\u5df2\u98de\u8d77\u4e00\u9053\u5bd2\u8292\uff0c\u7728\u773c\u95f4\uff0c\u7adf\u5728\u8eab\u524d\u5e03\u6210\u4e86\u4e00\u7247\u5bd2\u7f51\u5251\u5e55\u3002 \u3000\u3000\u5251\u52bf\u4e4b\u5feb\uff0c\u771f\u53eb\u4eba\u76ee\u4e0d\u6687\u63a5\u3002 \u3000\u3000\u90a3\u4e24\u70b9\u7eff\u8292\uff0c\u7adf\u88ab\u5251\u52bf\u5288\u65a9\u6210\u4e00\u7247\u7247\u788e\u5c51\u843d\u4e0b\u3002 \u3000\u3000\u82df\u6167\u6708\u611f\u89c9\u5230\u7740\u5251\u5904\uff0c\u6beb\u65e0\u529b\u9053\uff0c\u843d\u5730\u7684\u788e\u5c51\uff0c\u5fae\u5e26\u8840\u8165\u3002 \u3000\u3000\u4f3c\u662f\u4e00\u79cd\u6d3b\u7684\u5c0f\u866b\u3002 \u3000\u3000\u53ef\u60dc\uff0c\u5df2\u88ab\u65a9\u788e\uff0c\u770b\u4e0d\u51fa\u90a3\u662f\u4ec0\u4e48\u6837\u5b50\u7684\u5c0f\u866b\u3002 \u3000\u3000\u9ed1\u8863\u8001\u5987\u4eba\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5979\u4ece\u672a\u89c1\u8fc7\u8fd9\u4e48\u5feb\u7684\u5251\u62db\uff0c\u4e0d\u7981\u5fc3\u5934\u4e00\u5bd2\u3002 \u3000\u3000\u82df\u6167\u6708\u6ca1\u6709\u56de\u7b54\uff0c\u4e5f\u4e0d\u5bb9\u5979\u518d\u65bd\u88ad\u51fb\uff0c\u957f\u5251\u4e00\u6446\uff0c\u653b\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5251\u5982\u6492\u5e03\u6e14\u7f51\uff0c\u5316\u6210\u4e00\u7247\u5149\u5e55\u7f69\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u4e5f\u540c\u65f6\u51fa\u624b\uff0c\u653b\u5411\u53e6\u4e00\u4e2a\u9ed1\u8863\u4eba\u3002 \u3000\u3000\u4ed6\u8bda\u5fc3\u8981\u5728\u82df\u6167\u6708\u9762\u524d\uff0c\u65bd\u5c55\u4e00\u4e0b\u672c\u9886\uff0c\u770b\u6765\u4eba\u672a\u5e26\u5175\u5203\uff0c\u7adf\u7136\u4e5f\u4e0d\u4eae\u5251\uff0c\u98de\u8dc3\u6251\u51fb\uff0c \u653b\u51fa\u4e00\u638c\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u51b7\u54fc\u4e00\u58f0\uff0c\u4e3e\u638c\u8fce\u51fb\u3002 \u3000\u3000\u53cc\u638c\u63a5\u5b9e\uff0c\u9ed1\u8863\u4eba\u7a81\u7136\u53d1\u51fa\u4e86\u4e00\u58f0\u60e8\u53eb\u3002 \u3000\u3000\u539f\u6765\uff0c\u5c0f\u71d5\u5b50\u4e00\u51fa\u624b\u5c31\u7528\u51fa\u5929\u96f7\u795e\u638c\uff0c\u4e00\u80a1\u5f3a\u5927\u7684\u638c\u529b\u4e2d\u5e26\u7740\u731b\u70c8\u7684\u70ed\u529b\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u5e72\u601d\u4e07\u60f3\uff0c\u4e5f\u672a\u6599\u5230\u4e00\u4e2a\u5e74\u7eaa\u8f7b\u8f7b\u7684\u5c0f\u4f8d\u536b\uff0c\u7adf\u7136\u8eab\u6000\u7edd\u6280\uff0c\u638c\u52bf\u76f8\u89e6\uff0c\u7acb\u65f6\u88ab \u9707\u65ad\u4e86\u53f3\u8155\u3002\u53f3\u638c\u7684\u808c\u80a4\u4e5f\u53d8\u6210\u9ed1\u8272\uff0c\u4f3c\u88ab\u706b\u711a\u3002 \u3000\u3000\u82df\u6167\u6708\u4e5f\u5236\u4f4f\u4e86\u90a3\u9ed1\u8863\u5987\u4eba\uff0c\u4e00\u5251\u523a\u7a7f\u4e86\u5bf9\u65b9\u5de6\u80a9\uff0c\u540c\u65f6\u70b9\u4e86\u4e24\u5904\u7a74\u9053\u3002 \u3000\u3000\u4e24\u4e2a\u9ed1\u8863\u4eba\u7684\u6b66\u529f\u4e0d\u662f\u5f88\u5dee\uff0c\u53ea\u662f\u4ed6\u4eec\u9047\u4e0a\u4e86\u4e2d\u539f\u4e24\u4f4d\u6700\u9ad8\u7684\u9ad8\u624b\uff0c\u53ea\u4e00\u4e2a\u7167\u9762\uff0c\u53cc\u53cc \u53d7\u4f24\u88ab\u64d2\u3002 \u3000\u3000\u738b\u5f64\u5e26\u8d70\u4e86\u90a3\u9ed1\u8863\u7537\u5b50\uff0c\u82df\u6167\u6708\u5374\u628a\u90a3\u9ed1\u8863\u5987\u4eba\u5e26\u5165\u4e00\u5ea7\u96c5\u5ba4\u4e4b\u4e2d\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u8eab\u4e0a\u6709\u516d\u5904\u7a74\u9053\u88ab\u70b9\uff0c\u867d\u7136\u8eab\u6000\u5f02\u672f\uff0c\u5374\u662f\u65e0\u6cd5\u65bd\u5c55\u3002 \u3000\u3000\u82df\u6167\u6708\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u89e3\u53bb\u4e09\u516c\u4e3b\u8eab\u4e2d\u7684\u866b\u6bd2\uff0c\u6211\u5c31\u653e\u4f60\u4e00\u6761\u751f\u8def\uff0c\u89e3\u5f00\u4f60\u7684\u7a74\u9053\u4efb\u4f60 \u79bb\u53bb\uff0c\u4e0d\u80af\u7b54\u5e94\uff0c\u6211\u5c31\u4e00\u5251\u4e00\u5251\u65a9\u4f60\uff0c\u65a9\u5230\u4f60\u7b54\u5e94\u4e3a\u6b62\u3002\u201d \u3000\u3000\u9ed1\u8863\u5987\u4eba\u9053\uff1a\u201c\u8001\u8eab\u6b7b\u4e86\uff0c\u5c31\u518d\u4e5f\u6ca1\u6709\u4eba\u80fd\u89e3\u53bb\u4e09\u516c\u4e3b\u7684\u866b\u6bd2\u4e86\u3002\u201d \u3000\u3000\u82df\u6167\u6708\u957f\u5251\u4e00\u6325\uff0c\u65a9\u53bb\u4e86\u9ed1\u8863\u5987\u4eba\u53f3\u624b\u4e00\u6839\u5c0f\u6307\uff0c\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u6211\u53ea\u8981\u82b1\u4e9b\u5de5\u592b\u5185 \u529b\uff0c\u4ecd\u80fd\u89e3\u53bb\u4e09\u516c\u4e3b\u8eab\u4e2d\u7684\u866b\u6bd2\u3002\u201d \u3000\u3000\u9ed1\u8863\u5987\u4eba\u60f3\u4e0d\u5230\u82df\u6167\u6708\u4e0b\u624b\u5982\u6b64\u72e0\u6bd2\uff0c\u4e00\u53e5\u8bdd\u5c31\u65a9\u4e86\u4e00\u4e2a\u624b\u6307\uff0c\u4e0d\u7981\u4e00\u5446\u3002 \u3000\u3000\u82df\u6167\u6708\u53c8\u9053\uff1a\u201c\u4f60\u7b54\u4e0d\u7b54\u5e94\uff1f\u201d \u3000\u3000\u5251\u5149\u6d12\u843d\uff0c\u53c8\u65ad\u4e00\u6307\u3002 \u3000\u3000\u9ed1\u8863\u5987\u4eba\u5401\u4e00\u53e3\u6c14\uff0c\u8fd8\u6765\u4e0d\u53ca\u7b54\u8bdd\uff0c\u82df\u6167\u6708\u5df2\u51b7\u51b7\u5730\u518d\u9053\uff1a\u201c\u8fd9\u4e00\u6b21\uff0c\u6211\u8981\u523a\u778e\u4f60\u4e00\u53ea \u773c\u775b\u3002\u201d \u3000\u3000\u9ed1\u8863\u5987\u4eba\u5403\u4e86\u4e00\u60ca\uff0c\u6025\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d \u3000\u3000\u201c\u7b54\u5e94\u4e86\uff0c\u662f\u4e48\uff1f\u201d\u82df\u6167\u6708\u51b7\u5389\u7684\u5251\u8292\uff0c\u5df2\u6307\u5411\u8863\u5987\u4eba\u7684\u5de6\u773c\u3002 \u3000\u3000\u201c\u597d\u3002\u6211\u6cbb\u597d\u5979\u7684\u866b\u6bd2\uff0c\u4f60\u771f\u7684\u4f1a\u653e\u4e86\u6211\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u7b54\u5e94\u4e86\u4f60\uff0c\u5c31\u4e0d\u4f1a\u98df\u8a00\uff0c\u4f46\u4f60\u5982\u60f3\u501f\u6545\u8981\u4ec0\u4e48\u82b1\u6837\uff0c\u90a3\u5c31\u6709\u4f60\u7684\u82e6\u5934\u5403\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u5148\u628a\u4e09\u516c\u4e3b\u5e73\u653e\u5728\u4e00\u5f20\u6728\u69bb\u4e4b\u4e0a\uff0c\u518d\u51c6\u5907\u597d\u4e00\u74f6\u4e0a\u597d\u7684\u70c8\u9152\u2026\u2026\u201d\u9ed1\u8863\u5987\u4eba\u76ee\u5149 \u4e00\u63a0\u82df\u6167\u6708\uff0c\u9053\uff1a\u201c\u8fd8\u8981\u4e00\u53ea\u80a5\u58ee\u7684\u516c\u9e21\u3002\u201d \u3000\u3000\u201c\u8fd8\u6709\u4e48\uff1f\u201d \u3000\u3000\u201c\u591f\u4e86\uff0c\u5c31\u662f\u8fd9\u4e24\u6837\u4e1c\u897f\uff0c\u4f46\u884c\u6cd5\u5f15\u866b\u4e4b\u65f6\uff0c\u4e00\u5b9a\u8981\u89e3\u5f00\u6211\u7684\u7a74\u9053\u3002\u201d \u3000\u3000\u201c\u53ef\u4ee5\uff0c\u6211\u5e0c\u671b\u4f60\u4e0d\u8981\u641e\u9b3c\uff0c\u6211\u51fa\u5251\u5f88\u5feb\uff0c\u5728\u6211\u5168\u795e\u6212\u5907\u4e4b\u4e0b\uff0c\u4f60\u8fde\u5bfb\u6b7b\u7684\u673a\u4f1a\u4e5f\u6ca1 \u6709\u3002\u201d \u3000\u3000\u201c\u6211\u5df2\u9886\u6559\u8fc7\u4e86\uff0c\u53ea\u5e0c\u671b\u4f60\u7b54\u5e94\u7684\u4e8b\uff0c\u4e0d\u8981\u53d8\u5366\uff01\u201d \u3000\u3000\u4e09\u516c\u4e3b\u88ab\u5b89\u6392\u5728\u4e00\u95f4\u96c5\u5ba4\u4e2d\uff0c\u8eba\u5728\u4e00\u5f20\u6728\u69bb\u4e0a\u3002 \u3000\u3000\u4e3a\u4e86\u4f7f\u5979\u5b89\u9759\uff0c\u82df\u6167\u6708\u70b9\u4e86\u5979\u7684\u7761\u7a74\u3002 \u3000\u3000\u9ed1\u8863\u5987\u4eba\u628a\u70c8\u727a\u5012\u5728\u4e00\u4e2a\u74f7\u7897\u4e2d\uff0c\u71c3\u4e86\u8d77\u6765\uff0c\u7acb\u65f6\u6cdb\u8d77\u4e00\u7247\u84dd\u8272\u7684\u706b\u5149\uff0c\u7136\u540e\uff0c\u7531\u8eab\u4e0a \u53d6\u51fa\u4e00\u4e2a\u5c0f\u74f6\u5b50\uff0c\u5012\u51fa\u4e86\u4e00\u4e9b\u91d1\u8272\u7684\u7c89\u672b\uff0c\u6295\u5165\u4e86\u706b\u5149\u4e4b\u4e2d\u3002 \u3000\u3000\u4e00\u80a1\u602a\u5f02\u7684\u9999\u5473\uff0c\u6df7\u5165\u4e86\u9152\u5473\u4e4b\u4e2d\uff0c\u5e03\u6ee1\u5168\u5ba4\u3002 \u3000\u3000\u82df\u6167\u6708\u63d0\u5251\u7ad9\u5728\u90a3\u9ed1\u8863\u5987\u4eba\u8eab\u4fa7\uff0c\u4e25\u5bc6\u7684\u76d1\u89c6\u7740\u5979\u7684\u4e00\u4e3e\u4e00\u52a8\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u3001\u9ed1\u7f57\u6c49\u3001\u738b\u91cd\u5c71\u3001\u601c\u82b1\u5206\u7ad9\u96c5\u5ba4\u56db\u5468\u3002 \u3000\u3000\u9ed1\u8863\u5987\u4eba\u53cc\u624b\u5728\u4e09\u516c\u4e3b\u5c0f\u8179\u5904\u4e0d\u505c\u7684\u63c9\uff0c\u53e3\u4e2d\u5ff5\u5ff5\u6709\u8bcd\u3002 \u3000\u3000\u5927\u7ea6\u4e00\u523b\u5de5\u592b\u4e4b\u4e45\uff0c\u9ed1\u8863\u5987\u4eba\u7a81\u7136\u7ad9\u8d77\u8eab\u5b50\uff0c\u6293\u8d77\u516c\u9e21\u548c\u684c\u4e0a\u7684\u5c0f\u5200\uff0c\u53f3\u624b\u4e00\u6325\uff0c\u65a9\u65ad \u4e86\u9e21\u5934\uff0c\u4e00\u80a1\u9c9c\u8840\u75be\u55b7\u800c\u51fa\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u7684\u9f3b\u5b54\u4e4b\u4e2d\uff0c\u4f3c\u662f\u98de\u51fa\u4e00\u70b9\u7eff\u8292\uff0c\u6025\u6025\u5c04\u5165\u4e86\u9e21\u8eab\u4e4b\u4e2d\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u770b\u6e05\u695a\u90a3\u662f\u4e00\u4e2a\u4ec0\u4e48\u4e1c\u897f\uff0c\u4f46\u5374\u90fd\u770b\u5f97\u5fc3\u751f\u5bd2\u610f\u3002 \u3000\u3000\u9ed1\u8863\u5987\u4eba\u7acb\u65f6\u628a\u9e21\u8eab\u6295\u5165\u4e86\u71c3\u70e7\u7684\u70c8\u9152\u4e4b\u4e2d\uff0c\u9053\uff1a\u201c\u597d\u4e86\u3002\u201d \u3000\u3000\u82df\u6167\u6708\u9053\uff1a\u201c\u90a3\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d \u3000\u3000\u9ed1\u8863\u5987\u4eba\u9053\uff1a\u201c\u8001\u8eab\u517b\u7684\u6d3b\u866b\uff0c\u5b83\u5df2\u4e60\u60ef\u4e8e\u5bc4\u8eab\u5728\u4e09\u516c\u4e3b\u7684\u8eab\u4e0a\uff0c\u5982\u4e0d\u628a\u5b83\u718f\u9189\u70e7\u6b7b\uff0c \u6211\u6015\u5b83\u4f1a\u518d\u627e\u4e0a\u4e09\u516c\u4e3b\uff0c\u6240\u4ee5\uff0c\u53ea\u597d\u727a\u7272\u5b83\u4e86\u3002\u201d \u3000\u3000\u82df\u6167\u6708\u9053\uff1a\u201c\u662f\u4e00\u79cd\u4ec0\u4e48\u6837\u5b50\u7684\u52a8\u7269\uff0c\u80fd\u4e0d\u80fd\u8ba9\u6211\u4eec\u5f00\u5f00\u773c\u754c\uff1f\u201d \u3000\u3000\u201c\u4e0d\u884c\uff0c\u8fd9\u662f\u8001\u8eab\u7684\u79d8\u5bc6\uff0c\u81f3\u5c11\u4eca\u5929\u4e0d\u884c\uff0c\u4e0b\u4e00\u6b21\u6211\u4eec\u5982\u679c\u8fd8\u6709\u89c1\u9762\u7684\u673a\u4f1a\uff0c\u4e5f\u8bb8\u8001\u8eab \u4f1a\u8ba9\u8bf8\u4f4d\u77a7\u77a7\u3002\u201d \u3000\u3000\u82df\u6167\u6708\u9053\uff1a\u201c\u6211\u7b54\u5e94\u653e\u4e86\u4f60\uff0c\u7edd\u4e0d\u6539\u53d8\uff0c\u4e0d\u8fc7\uff0c\u8981\u7b49\u5230\u4e09\u516c\u4e3b\u5b8c\u5168\u6062\u590d\u4e4b\u540e\uff0c\u4f60\u624d\u80fd \u8d70\u3002\u201d \u3000\u3000\u9ed1\u8863\u5987\u4eba\u9053\uff1a\u201c\u5979\u73b0\u5728\u5df2\u7ecf\u6062\u590d\u4e86\uff0c\u4f60\u89e3\u5f00\u5979\u7684\u7a74\u9053\uff0c\u7acb\u523b\u53ef\u4ee5\u8bc1\u660e\u3002\u201d \u3000\u3000\u82df\u6167\u6708\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u89e3\u5f00\u4e86\u4e09\u516c\u4e3b\u7684\u7a74\u9053\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u633a\u8eab\u5750\u8d77\uff0c\u9053\uff1a\u201c\u5e08\u7236\uff0c\u5c31\u662f\u5979\uff0c\u5728\u6211\u8eab\u4e0a\u4e0b\u866b\uff01\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u2026\u2026\u201d\u82df\u6167\u6708\u9053\uff1a\u201c\u5979\u8bf4\u4f60\u866b\u6bd2\u5df2\u89e3\uff0c\u8bd5\u8bd5\u770b\uff0c\u662f\u771f\u662f\u5047\uff1f\u201d \u3000\u3000\u4e09\u516c\u4e3b\u8fd0\u6c14\u76f8\u8bd5\uff0c\u679c\u7136\u866b\u6bd2\u5df2\u89e3\uff0c\u7acb\u523b\u7cbe\u795e\u4e00\u632f\uff0c\u968f\u624b\u6293\u8d77\u4e86\u957f\u5251\uff0c\u6307\u7740\u9ed1\u8863\u5987\u4eba\u9053\uff1a \u201c\u6211\u88ab\u4f60\u6298\u78e8\u4e86\u4e00\u5e74\u591a\uff0c\u5185\u5fc3\u79ef\u5b58\u4e86\u65e0\u9650\u7684\u6028\u6068\uff0c\u6765\u5427\uff01\u6211\u4eec\u516c\u516c\u5e73\u5e73\u7684\u653e\u624b\u4e00\u6218\uff0c\u751f\u6b7b\u65e0 \u6028\uff01\u201d \u3000\u3000\u9ed1\u8863\u5987\u4eba\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e09\u516c\u4e3b\uff0c\u4e0d\u8981\u903c\u6211\uff0c\u51ed\u7740\u771f\u5b9e\u7684\u6b66\u529f\uff0c\u4e5f\u8bb8\u6211\u975e\u4f60\u4e4b\u654c\uff0c\u4f46 \u8001\u8eab\u8fd8\u6709\u81ea\u6551\u4e4b\u9053\uff0c\u903c\u6211\u62fc\u4e0a\u8001\u547d\uff0c\u4e09\u516c\u4e3b\u672a\u5fc5\u4e00\u5b9a\u80fd\u8ba8\u5f97\u597d\u53bb\uff01\u201d \u3000\u3000\u201c\u4e91\u513f\uff01\u2026\u2026\u201d\u82df\u6167\u6708\u62c9\u4e00\u628a\u4e09\u516c\u4e3b\uff0c\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u7b54\u5e94\u5979\uff0c\u53ea\u8981\u4e0d\u518d\u6697\u52a8\u624b\u811a\uff0c\u5c31\u653e \u5979\u79bb\u5f00\uff0c\u4f60\u4e0d\u4f1a\u8ba9\u4e3a\u5e08\u5931\u4fe1\u4e8e\u4eba\u5427\uff01\u201d \u3000\u3000\u201c\u5f1f\u5b50\u4e0d\u6562\uff01\u201d\u4e09\u516c\u4e3b\u6b20\u8eab\u8bf4\u9053\uff1a\u201c\u90a3\u8001\u5996\u5987\u4ee5\u866b\u6bd2\u5236\u6211\uff0c\u4e00\u5e74\u591a\u6765\uff0c\u628a\u5f1f\u5b50\u6298\u817e\u60e8 \u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u4e5f\u4e0d\u80fd\u602a\u6211\uff0c\u54b1\u4eec\u5f7c\u6b64\u654c\u5bf9\u76f8\u5904\uff0c\u81ea\u7136\u662f\u5404\u4e3a\u5176\u4e3b\u4e86\u201d\u4f60\u53ef\u4ee5\u8d70\u4e86\uff01\u201c\u82df\u6167\u6708\u51b7\u51b7 \u7684\u6253\u65ad\u4e86\u9ed1\u8863\u5987\u4eba\u7684\u8bdd\uff0c\u9053\uff1a\u201d\u5e0c\u671b\u4f60\u5c3d\u5feb\u79bb\u5f00\u4eac\u5e08\uff0c\u4e0b\u6b21\u518d\u9047\u4e0a\uff0c\u6211\u4e5f\u4e0d\u4f1a\u653e\u8fc7\u4f60\u7684\u3002\u201c \u3000\u3000\u9ed1\u8863\u5987\u4eba\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5f88\u597d\uff0c\u82df\u5973\u4fa0\u540d\u4e0d\u865a\u4f20\uff0c\u679c\u7136\u662f\u8a00\u800c\u6709\u4fe1\uff0c\u8001\u8eab\u8fd9\u5c31\u79bb\u5f00 \u4e86\u2026\u2026\u201d \u3000\u3000\u9ed1\u7f57\u6c49\u4e00\u6a2a\u8eab\u62e6\u4f4f\u4e86\u9ed1\u8863\u5987\u4eba\uff0c\u9053\uff1a\u201c\u4e0d\u597d\u610f\u601d\u554a\uff0c\u5c0f\u548c\u5c1a\u4e0d\u662f\u627e\u9ebb\u70e6\uff0c\u53ea\u662f\u60f3\u5411\u4f60\u8001 \u4eba\u5bb6\u8bf7\u6559\u4e24\u4ef6\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u9ed1\u8863\u5987\u4eba\u7cbe\u901a\u6c49\u8bed\uff0c\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u597d\u5427\uff01\u4f60\u8fd9\u5c0f\u548c\u5c1a\u633a\u6709\u793c\u8c8c\uff0c\u4f60\u95ee\u5427\uff0c\u4e0d\u8fc7\uff0c\u4e0d\u80fd \u8bf4\u7684\uff0c\u8001\u8eab\u53ef\u4e0d\u4e00\u5b9a\u4f1a\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u201c\u4e09\u516c\u4e3b\u8eab\u4e2d\u7684\u866b\u6bd2\uff0c\u65e5\u540e\u4f1a\u4e0d\u4f1a\u518d\u53d1\uff1f\u201d \u3000\u3000\u8fd9\u786e\u5b9e\u662f\u4e00\u4ef6\u4ee4\u4eba\u5173\u5fc3\u7684\u4e8b\uff0c\u53ea\u4e0d\u8fc7\uff0c\u5c0f\u548c\u5c1a\u5374\u6700\u5148\u60f3\u5230\u3002 \u3000\u3000\u201c\u4e0d\u4f1a\uff01\u201d\u9ed1\u8863\u5987\u4eba\u9053\uff1a\u201c\u5979\u4e2d\u7684\u662f\u6d3b\u866b\uff0c\u867d\u7136\u8001\u8eab\u968f\u65f6\u53ef\u8981\u5b83\u53d1\u4f5c\uff0c\u4f46\u53ea\u8981\u9664\u53bb\u8eab\u4e2d \u866b\u6bcd\uff0c\u5c31\u5b8c\u5168\u6446\u8131\u5b83\u7684\u6bd2\u5bb3\u4e86\u3002\u201d \u3000\u3000\u9ed1\u7f57\u6c49\u70b9\u70b9\u5934\uff0c\u7b11\u9053\uff1a\u201c\u597d\uff01\u4f60\u8001\u4eba\u5bb6\u867d\u975e\u4e2d\u539f\u4eba\u6c0f\uff0c\u4f46\u5012\u4e5f\u6709\u4e2d\u539f\u82f1\u96c4\u7684\u8c6a\u60c5\u5766\u7136\uff0c \u4f69\u670d\uff0c\u4f69\u670d\u3002\u201d \u3000\u3000\u4e00\u9876\u9ad8\u5e3d\u5b50\u6263\u8fc7\u53bb\uff0c\u90a3\u9ed1\u8863\u5987\u4eba\u8138\u4e0a\u7adf\u4e5f\u6cdb\u8d77\u4e86\u7b11\u5bb9\u3002 \u3000\u3000\u6253\u94c1\u8d81\u70ed\uff0c\u5c0f\u548c\u5c1a\u63a5\u7740\u95ee\u9053\uff1a\u201c\u7b2c\u4e8c\u4ef6\u4e8b\u95ee\u7684\u6709\u70b9\u8fc7\u4efd\uff0c\u4e0d\u8fc7\u4f60\u5982\u80af\u8bf4\u51fa\u6765\u3002\u53ef\u4ee5\u4f7f\u6211 \u4eec\u5927\u5e7f\u89c1\u95fb\uff0c\u90a3\u53ef\u662f\u4e00\u4ef6\u5927\u5927\u7684\u8363\u8a89\u554a\uff01\u201d \u3000\u3000\u201c\u8001\u8eab\u80fd\u8bf4\u7684\u81ea\u7136\u4f1a\u8bf4\uff0c\u4e0d\u80fd\u8bf4\u7684\uff0c\u8001\u8eab\u4e5f\u4e0d\u4f1a\u88ab\u4f60\u9a97\u51fa\u53e3\u6765\uff01\u201d \u3000\u3000\u201c\u82d7\u7586\u866b\u6bd2\u5927\u90fd\u662f\u653e\u5728\u98df\u7269\u8336\u6c34\u4e4b\u4e2d\uff0c\u8ba9\u4eba\u670d\u7528\uff0c\u90a3\u5e94\u8be5\u662f\u4e00\u79cd\u7c89\u672b\u4e4b\u7c7b\u7684\u836f\u7269\uff0c\u4f60\u8001 \u4eba\u5bb6\u8fd9\u6d3b\u866b\u662f\u600e\u4e48\u8ba9\u4eba\u541e\u4e0b\u53bb\u7684\uff1f\u201d \u3000\u3000\u9ed1\u8863\u5987\u4eba\u8138\u4e0a\u6cdb\u8d77\u4e86\u4e00\u80a1\u9887\u4e3a\u81ea\u8d1f\u7684\u795e\u8272\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u82d7\u7586\u7684\u4e07\u866b\u95e8\u4e2d\u786e\u6709\u5f88\u591a\u4e0b\u6bd2 \u7684\u9ad8\u624b\uff0c\u4e0b\u866b\u7684\u65b9\u6cd5\u4e5f\u53eb\u4eba\u9632\u4e0d\u80dc\u9632\uff0c\u771f\u6b63\u80fd\u5f79\u65bd\u6d3b\u866b\u7684\u4eba\u5374\u662f\u5c48\u6307\u53ef\u6570\u4e86\uff0c\u8fde\u8001\u8eab\u7b97\u4e0a\u4e5f \u4e0d\u8fc7\u4e09\u4e94\u4eba\u800c\u5df2\u3002\u201d \u3000\u3000\u201c\u540c\u6837\u4e0b\u866b\uff0c\u4e00\u822c\u7684\u53d6\u4eba\u6027\u547d\u3002\u96be\u9053\u8fd8\u6709\u6b7b\u866b\u3001\u6d3b\u866b\u4e4b\u5206\u4e48\uff1f\u201d \u3000\u3000\u9ed1\u7f57\u6c49\u88c5\u51fa\u6ee1\u8138\u4e0d\u89e3\u5730\u95ee\u3002 \u3000\u3000\u201c\u5927\u5927\u7684\u4e0d\u540c\u4e86\uff01\u201d\u9ed1\u8863\u5987\u4eba\u9053\uff1a\u201c\u6240\u8c13\u6b7b\u866b\u5c31\u662f\u4e00\u822c\u7684\u4e0b\u866b\u65b9\u6cd5\uff0c\u628a\u866b\u6bd2\u4e0b\u5165\u9152\u98df\u4e4b \u4e2d\uff0c\u8ba9\u4eba\u670d\u7528\uff0c\u7136\u540e\u5b9a\u65f6\u5b9a\u671f\u53d1\u4f5c\uff0c\u4f46\u8001\u8eab\u8fd9\u6d3b\u866b\u5c31\u5927\u5927\u4e0d\u540c\u4e86\u3002\u201d \u3000\u3000\u201c\u5c0f\u548c\u5c1a\u773c\u754c\u4e0d\u5bbd\uff0c\u53ef\u4e5f\u542c\u8bf4\u8fc7\u4e0b\u866b\u4e4b\u9053\uff0c\u975e\u541e\u5165\u8179\u4e2d\u65e0\u6cd5\u4f24\u4eba\u3002\u201d \u3000\u3000\u201c\u76ae\u76f8\u4e4b\u8bba\uff0c\u5b64\u964b\u5be1\u95fb\uff01\u201d\u9ed1\u8863\u5987\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u80fd\u73a9\u6d3b\u866b\u7684\u4eba\uff0c\u65e9\u5df2\u7ecf\u72af\u4e0d\u7740\u628a\u866b\u6bd2 \u7f6e\u5165\u9152\u4e2d\u4e86\uff0c\u8001\u8eab\u53ef\u4ee5\u5f79\u65bd\u6d3b\u866b\u5728\u767e\u4e08\u4e4b\u5185\u4fb5\u5165\u4eba\u8eab\u3002\u201d \u3000\u3000\u5ba4\u4e2d\u4eba\u90fd\u542c\u5f97\u5fc3\u5934\u5927\u9707\uff0c\u7531\u5fc3\u5e95\u5192\u51fa\u6765\u4e00\u80a1\u5bd2\u610f\u3002 \u3000\u3000\u9ed1\u7f57\u6c49\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8001\u524d\u8f88\u7684\u610f\u601d\u662f\u8bf4\uff0c\u4f60\u5728\u767e\u4e08\u4e4b\u5185\u968f\u65f6\u53ef\u4ee5\u653e\u866b\u4f24\u4eba\u4e86\uff1f\u201d \u3000\u3000\u9ed1\u8863\u5987\u4eba\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c0f\u548c\u5c1a\uff0c\u4f60\u53ef\u662f\u6000\u7591\u8001\u8eab\u4f1a\u6697\u7b97\u8bf8\u4f4d\u4e48\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\uff0c\u6211\u60f3\u4e0d\u4f1a\u5427\uff01\u2026\u2026\u201d\u9ed1\u7f57\u6c49\u9053\uff1a\u201c\u6211\u4eec\u90fd\u5728\u5168\u795e\u6212\u5907\u7740\uff0c\u5982\u6709\u6d3b\u866b\u4fb5\u88ad\uff0c\u81f3\u5c11 \u6211\u4eec\u6709\u70b9\u611f\u89c9\u624d\u5bf9\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff01\u201d\u9ed1\u8863\u5987\u4eba\u76ee\u5149\u6295\u6ce8\u5728\u82df\u6167\u6708\u7684\u8eab\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u5982\u82e5\u4e00\u4e2a\u4eba\u7684\u4fee\u517b\uff0c\u80fd\u5230\u4e86\u8fd9 \u4f4d\u5973\u4fa0\u7684\u5883\u754c\uff0c\u5929\u4e0b\u6700\u5229\u5bb3\u7684\u6d3b\u866b\u4e5f\u65e0\u6cd5\u4fb5\u72af\u5230\u5979\u4e86\u3002\u201d \u3000\u3000\u201c\u662f\u6781\uff01\u662f\u6781\uff01\u201d\u9ed1\u7f57\u6c49\u9053\uff1a\u201c\u8fd9\u4f4d\u5973\u4fa0\u662f\u6211\u4eec\u4e2d\u539f\u6b66\u6797\u4e2d\u9876\u5c16\u7684\u9ad8\u624b\uff0c\u653e\u773c\u6c5f\u6e56\u4e5f\u6ca1 \u51e0\u4e2a\u4eba\u80fd\u5728\u5979\u5251\u4e0b\u9003\u751f\uff0c\u8001\u524d\u8f88\uff0c\u53ef\u7b97\u662f\u7b2c\u4e00\u4e2a\u5728\u5979\u5251\u4e0b\u7559\u547d\u7684\u4eba\u4e86\u3002\u201d \u3000\u3000\u4ee5\u82df\u6167\u6708\u7684\u4fee\u517b\uff0c\u4e5f\u4e0d\u7981\u8138\u4e0a\u6cdb\u73b0\u51fa\u4e00\u62b9\u7b11\u610f\u3002 \u3000\u3000\u9ad8\u5e3d\u5b50\u7684\u5a01\u529b\uff0c\u5f53\u771f\u662f\u65e0\u8fdc\u65e0\u754c\uff0c\u5929\u4e0b\u80fd\u6709\u51e0\u4eba\u53ef\u4ee5\u9003\u907f\uff1f \u3000\u3000\u201c\u8fd9\u5c31\u96be\u602a\u4e86\uff01\u201d\u9ed1\u8863\u5987\u4eba\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u5973\u4fa0\u5251\u7f51\u7684\u7ef5\u5bc6\uff0c\u8fde\u8001\u8eab\u7684\u98de\u866b\u4e5f\u65e0\u6cd5\u4fb5\u5165 \u4e86\u3002\u201d \u3000\u3000\u82df\u6167\u6708\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u53ef\u4ee5\u8d70\u4e86\uff0c\u8bb0\u7740\uff01\u8d70\u7684\u8d8a\u8fdc\u8d8a\u597d\u3002\u201d \u3000\u3000\u201c\u662f\u662f\u662f\u2026\u2026\u8001\u8eab\u544a\u9000\uff01\u201d\u9ed1\u8863\u5987\u4eba\u8f6c\u8eab\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u201c\u8001\u524d\u8f88\uff01\u5c0f\u548c\u5c1a\u9001\u4f60\u4e00\u7a0b\uff0c\u542c\u8749\u9662\u5916\u8b66\u536b\u68ee\u4e25\uff0c\u8001\u524d\u8f88\u7eb5\u7136\u4e0d\u653e\u5728\u5fc3\u4e0a\uff0c\u4f46\u5982\u51b2\u6740\u51fa \u53bb\uff0c\u6050\u6015\u8981\u8d39\u4e00\u756a\u624b\u811a\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u597d\uff01\u201d\u9ed1\u8863\u5987\u4eba\u9053\uff1a\u201c\u5c0f\u548c\u5c1a\uff0c\u8001\u8eab\u4e0d\u4f1a\u767d\u767d\u53d7\u4f60\u4e00\u756a\u7167\u987e\uff0c\u65e5\u540e\uff0c\u5fc5\u6709\u62a5\u507f \u7684\u3002\u201d \u3000\u3000\u201c\u4ee5\u540e\u7684\u4e8b\uff0c\u4ee5\u540e\u518d\u8bf4\u5427\uff01\u8001\u524d\u8f88\u8bf7\u2026\u2026\u201d \u3000\u3000\u4e24\u4eba\u4e00\u524d\u4e00\u540e\u7684\u884c\u53bb\u3002 \u3000\u3000\u76ee\u7779\u4e24\u4eba\u79bb\u53bb\u4e4b\u540e\uff0c\u5c0f\u71d5\u5b50\u7a81\u7136\u957f\u957f\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5c0f\u9053\u58eb\uff0c\u5c0f\u548c\u5c1a\u600e\u4e48\u4f1a\u53d8\u5f97\u8fd9 \u4e48\u6709\u793c\u4e86\uff1f\u201d \u3000\u3000\u201c\u6211\u770b\u4ed6\u662f\u522b\u6709\u7528\u5fc3\u3002\u201d\u767d\u7fbd\u4f4e\u58f0\u5730\u9053\uff1a\u201c\u5c0f\u548c\u5c1a\u4e34\u53bb\u65f6\u7ed9\u6211\u626e\u4e86\u4e00\u4e2a\u9b3c\u8138\uff0c\u6211\u5f97\u8d76\u5feb \u53bb\u63a5\u5e94\u4ed6\u4e00\u4e0b\u3002\u201d \u3000\u3000\u4e00\u95ea\u8eab\uff0c\u75be\u5982\u98d8\u98ce\u800c\u53bb\u3002 \u3000\u3000\u201c\u65e9\u5c31\u542c\u8bf4\u9ed1\u50e7\u3001\u767d\u9053\u4eba\u5c0f\u9b3c\u5927\uff0c\u679c\u7136\u662f\u540d\u4e0d\u865a\u4f20\u3002\u201d\u82df\u6167\u6708\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u5c0f\u71d5\u5b50\uff0c\u4f60 \u548c\u4ed6\u4eec\u957f\u4e45\u76f8\u5904\u4e0b\u53bb\uff0c\u5f53\u5fc3\u88ab\u4ed6\u4eec\u5e26\u574f\u4e86\u3002\u201d \u3000\u3000\u201c\u5e08\u7236\u8bf4\uff0c\u6211\u672c\u6765\u5c31\u4e0d\u662f\u4e00\u4e2a\u597d\u5b69\u5b50\u2026\u2026\u201d\u5c0f\u71d5\u5b50\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u9b54\u6559\u4e1c\u8fdb\uff0c\u6574\u4e2a\u6c5f \u6e56\u90fd\u7b3c\u7f69\u5728\u4e00\u79cd\u90aa\u6076\u7684\u8be1\u79d8\u4e4b\u4e2d\uff0c\u5fc5\u9700\u8981\u64cd\u5200\u4e00\u5272\uff0c\u4f46\u5fc5\u9700\u8981\u6709\u9739\u96f3\u624b\u6bb5\u7684\u4eba\uff0c\u5fc3\u5730\u592a\u597d\u7684 \u4eba\u662f\u4e0d\u80fd\u64cd\u5200\u3002\u201d \u3000\u3000\u201c\u60f3\u4e0d\u5230\u8001\u778e\u5b50\u4f1a\u8fd9\u6837\u6559\u80b2\u4ed6\u7684\u5f1f\u5b50\u2026\u2026\u201d\u82df\u6167\u6708\u5e7d\u5e7d\u4e00\u53f9\uff0c\u9053\uff1a\u201c\u4ed6\u548c\u6211\u8ba2\u4e0b\u4e86\u4e00\u573a \u8d4c\u7ea6\uff0c\u770b\u6765\uff0c\u4ed6\u5df2\u7ecf\u80dc\u5238\u5728\u63e1\u4e86\uff0c\u4f60\u89c1\u7740\u4ed6\u65f6\uff0c\u544a\u8bc9\u4ed6\uff0c\u8fd9\u573a\u8d4c\u7ea6\u6211\u8ba4\u8f93\u4e86\uff0c\u5230\u65f6\u5019\uff0c\u6211\u4f1a \u767b\u95e8\u62dc\u89c1\uff0c\u4efb\u4ed6\u5904\u7f6e\u3002\u201d \u3000\u3000\u201c\u53ea\u6015\u8001\u524d\u8f88\u518d\u96be\u89c1\u5230\u4ed6\u8001\u4eba\u5bb6\u4e86\u2026\u2026\u201d \u3000\u3000\u82df\u6167\u6708\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u4ee5\u4ed6\u6df1\u539a\u7684\u529f\u529b\uff0c\u6d3b\u4e0a\u4e2a\u767e\u591a\u5c81\u5e76\u4e0d\u662f\u4ef6\u96be\u4e8b\uff0c\u96be\u9053 \u4ed6\u2026\u2026\u201d \u3000\u3000\u201c\u5bb6\u5e08\u628a\u4e00\u8eab\u529f\u529b\uff0c\u5df2\u8f6c\u5ac1\u5230\u665a\u8f88\u7684\u8eab\u4e0a\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u662f\u8bf4\uff0c\u4f60\u5bb3\u6b7b\u4e86\u5e08\u7236\uff0c\u8001\u778e\u5b50\u600e\u4e48\u4f1a\u6536\u4e86\u4f60\u8fd9\u4e48\u4e2a\u5f92\u5f1f\uff1f\u201d \u3000\u3000\u82df\u6167\u6708\u76ee\u4e2d\u795e\u5149\u95ea\u52a8\uff0c\u7adf\u7136\u9690\u9690\u73b0\u51fa\u6740\u673a\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u53ea\u770b\u5f97\u5fc3\u795e\u9707\u52a8\uff01 \u3000\u3000\u5979\u77e5\u9053\u5e08\u7236\u7684\u6027\u683c\uff0c\u8fd9\u662f\u771f\u6b63\u7684\u751f\u6c14\u4e86\uff0c\u51fa\u624b\u4e00\u51fb\uff0c\u5fc5\u5c06\u662f\u8ffd\u9b42\u593a\u547d\uff0c\u5979\u624b\u4e2d\u6709\u5251\uff0c\u5251 \u4e0a\u5bd2\u8292\u98a4\u52a8\uff0c\u4f3c\u5df2\u65e0\u6cd5\u514b\u5236\u4f4f\u5185\u5fc3\u7684\u6124\u6012\uff0c\u5927\u5927\u6709\u7acb\u523b\u51fa\u624b\uff0c\u624d\u80fd\u629a\u5e73\u5fc3\u4e2d\u90a3\u4efd\u6124\u6012\u3001\u4e0d\u5b89 \u4e4b\u610f\u3002 \u3000\u3000\u201c\u8001\u524d\u8f88\u8bef\u4f1a\u4e86\u2026\u2026\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u65e0\u9650\u59d4\u5c48\uff0c\u6025\u6025\u5730\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u5f1f\u5b50\u867d\u7136\u662f\u611a\u6627\uff0c\u4f46\u4e5f\u4e0d\u6562\u5982\u6b64\u5927\u9006\u4e0d\u9053\uff0c\u4f46 \u662f\uff0c\u5bb6\u5e08\u8bf4\u4ed6\u5929\u5e74\u65e9\u5c3d\uff0c\u5df2\u7ecf\u591a\u6d3b\u4e86\u597d\u51e0\u5e74\uff0c\u5c31\u662f\u5728\u7b49\u7740\u665a\u8f88\u5165\u95e8\uff0c\u4ed6\u6258\u8bf7\u6c5f\u53d4\u53d4\u4ee3\u4ed6\u7269\u8272 \u4e00\u4e2a\u2026\u2026\u201d \u3000\u3000\u201c\u54ea\u4e00\u4e2a\u6c5f\u53d4\u53d4\uff1f\u4ed6\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d\u82df\u6167\u6708\u6253\u65ad\u5c0f\u71d5\u5b50\u7684\u8bdd\uff0c\u6025\u6025\u95ee\u9053\u3002 \u3000\u3000\u201c\u6c5f\u5343\u91cc\uff0c\u6c5f\u53d4\u53d4\u3002\u201d \u3000\u3000\u201c\u662f\u4ed6\uff01\u201d \u3000\u3000\u201c\u4ed6\u662f\u5e08\u7236\u8fd1\u5e74\u4e2d\uff0c\u552f\u4e00\u4ea4\u5f80\u7684\u597d\u53cb\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\uff0c\u4f60\u5c31\u63a5\u53d7\u4e86\u4ed6\u8f6c\u5ac1\u7ed9\u4f60\u7684\u4e00\u5207\u529f\u529b\u2026\u2026\u201d\u82df\u6167\u6708\u6fc0\u52a8\u5730\u9053\uff1a\u201c\u8ba9\u4ed6\u6cb9\u5c3d\u706f\u67af\uff0c \u65e9\u65e5\u7edd\u547d\u3002\u201d \u3000\u3000\u201c\u665a\u8f88\u4e0d\u6562\uff01\u201d \u3000\u3000\u201c\u8bf4\uff01\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u4f60\u628a\u4e8b\u60c5\u8bb2\u6e05\u695a\u4e00\u70b9\u2026\u2026\u201d\u82df\u6167\u6708\u4f59\u6012\u4ecd\u5b58\uff0c\u6740\u673a\u4ecd\u65e7\u662f\u672a\u606f \u53bb\u5206\u6beb\u3002 \u3000\u3000\u201c\u5e08\u7236\u70b9\u4e86\u665a\u8f88\u7684\u7a74\u9053\uff0c\u4ee5\u704c\u9876\u5927\u6cd5\uff0c\u4f20\u71c3\u85aa\u706b\uff0c\u665a\u8f88\u5fc3\u4e2d\u867d\u662f\u60b2\u75db\u6b32\u7edd\uff0c\u4f46\u5374\u65e0\u80fd\u6297 \u62d2\u2026\u2026\u201d \u3000\u3000\u201c\u54fc\uff0c\u53ef\u6076\uff01\u4ed6\u8981\u6b7b\u4e5f\u8be5\u7b49\u6211\u53bb\u8ba4\u8f93\u4e4b\u540e\u624d\u6b7b\u554a\u2026\u2026\u201d \u3000\u3000\u201c\u5bb6\u5e08\u4f20\u85aa\u4e4b\u540e\uff0c\u544a\u8bc9\u665a\u8f88\u8bf4\uff0c\u8981\u6211\u4ee3\u4ed6\u5b8c\u6210\u4e09\u4e2a\u5fc3\u613f\uff0c\u7136\u540e\uff0c\u5c31\u81ea\u95ed\u77f3\u95e8\uff0c\u4e0d\u518d\u548c\u5f1f \u5b50\u76f8\u89c1\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u8bf4\uff0c\u8001\u778e\u5b50\u8fd8\u6d3b\u7740\uff1f\u201d \u3000\u3000\u201c\u665a\u8f88\u4e0d\u77e5\u9053\uff0c\u90a3\u5ea7\u77f3\u6d1e\u6df1\u5728\u5c71\u8179\u4e2d\uff0c\u90a3\u6247\u77f3\u95e8\u662f\u552f\u4e00\u901a\u6c14\u7684\u5730\u65b9\uff0c\u5173\u95ed\u4e4b\u540e\uff0c\u4e0e\u4e16\u9694 \u7edd\uff0c\u4eba\u5728\u90a3\u79cd\u73af\u5883\u4e2d\uff0c\u90a3\u91cc\u8fd8\u80fd\u6d3b\u5f97\u4e0b\u53bb\uff01\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u4e0d\u5c06\u77f3\u95e8\u6253\u5f00\u6765\u77a7\u4e00\u77a7\uff0c\u8001\u778e\u5b50\u5185\u529f\u7cbe\u6df1\uff0c\u5df2\u5feb\u6210\u4e3a\u91d1\u521a\u4e0d\u574f\u4e4b\u8eab\uff0c\u5e94\u8be5\u8fd8\u6709 \u6d3b\u547d\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u201c\u4ed6\u5631\u5490\u5f1f\u5b50\uff0c\u6700\u5feb\u4e5f\u8981\u4e09\u5e74\u4e4b\u540e\u518d\u542f\u5f00\u77f3\u95e8\uff0c\u5426\u5219\uff0c\u90a3\u5c31\u662f\u80cc\u5f03\u9057\u547d\uff0c\u5bb3\u4ed6\u6ca6\u5165\u9b54 \u9053\u2026\u2026\u201d \u3000\u3000\u201c\u6709\u8fd9\u6837\u4e25\u91cd\u4e48\uff1f\u201d \u3000\u3000\u201c\u662f\uff0c\u665a\u8f88\u53e5\u53e5\u771f\u5b9e\uff0c\u7edd\u65e0\u4e00\u8a00\u4e00\u5b87\u865a\u5047\u3002\u201d \u3000\u3000\u201c\u4ed6\u8981\u4f60\u4ee3\u4ed6\u5b8c\u6210\u4ec0\u4e48\u5fc3\u613f\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u542b\u7740\u6cea\u6c34\uff0c\u4f24\u5fc3\u5730\u8bf4\u7740\u2014\u2014\u4e00\u3001\u5c3d\u529b\u9010\u51fa\u9b54\u6559\uff0c\u4ee5\u89e3\u6b66\u6797\u4e4b\u5371\u3002 \u3000\u3000\u4e8c\u3001\u4e0d\u5f97\u51fa\u4efb\u671d\u5ef7\uff0c\u4e3a\u5b98\u7267\u6c11\u3002 \u3000\u3000\u4e09\u3001\u8981\u542c\u6c5f\u53d4\u53d4\u7684\u547d\u4ee4\u884c\u4e8b\uff0c\u4e0d\u5f97\u8fdd\u547d\u6297\u62d2\u3002 \u3000\u3000\u8bf4\u7740\uff0c\u5c0f\u71d5\u5b50\u62ac\u773c\u671b\u7740\u8461\u6167\u6708\uff0c\u53c8\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u6c5f\u53d4\u53d4\u8981\u6211\u548c\u9ed1\u50e7\u3001\u767d\u9053\u8d70\u5728\u4e00\u8d77\uff0c\u665a \u8f88\u662f\u4e0d\u80fd\u4e0d\u542c\u4e86\uff0c\u4f55\u51b5\uff0c\u4ed6\u4eec\u6c5f\u6e56\u7ecf\u9a8c\u4e30\u5bcc\uff0c\u65e5\u540e\uff0c\u665a\u8f88\u8fd8\u8981\u501f\u91cd\u4ed6\u4eec\u4e24\u4eba\u7684\u5e2e\u52a9\uff0c\u5b8c\u6210\u5fc3 \u613f\u5462\uff01\u201d \u3000\u3000\u201c\u53ea\u6709\u8fd9\u4e9b\u4e48\uff1f\u2026\u2026\u201d\u82df\u6167\u6708\u671b\u4e86\u5c0f\u71d5\u5b50\u4e00\u773c\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u4ed6\u8fd8\u6709\u4ec0\u4e48\u60ca\u4eba\u7684\u5631\u5490\u6ca1 \u6709\uff1f\u201d \u3000\u3000\u201c\u6709\uff01\u201d\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u4f46\uff0c\u665a\u8f88\u4e0d\u6562\u8bf4\u3002\u201d \u3000\u3000\u82df\u6167\u6708\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u4e0d\u6562\u8bf4\u7684\uff0c\u5929\u96f7\u8001\u4eba\u7684\u8863\u94b5\u4f20\u4eba\uff0c\u600e\u4e48\u4f1a\u5982\u6b64\u80c6\u5c0f\u6015\u4e8b \u5462\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u7b11\u4e86\u7b11\uff0c\u8f7b\u58f0\u8bf4\u9053\uff1a\u201c\u665a\u8f88\u5929\u4e0d\u6015\u3001\u5730\u4e0d\u6015\uff0c\u53ea\u6015\u5728\u8a00\u8bed\u4e0a\u5f00\u7f6a\u4e86\u8001\u524d\u8f88\uff0c\u56e0\u4e3a \u5e08\u7236\u66fe\u7ecf\u8bf4\u8fc7\uff0c\u5929\u4e0b\u6240\u6709\u7684\u4eba\u6211\u90fd\u53ef\u4ee5\u5f00\u7f6a\uff0c\u4f46\u662f\uff0c\u7edd\u5bf9\u4e0d\u53ef\u4ee5\u5f00\u7f6a\u4e86\u4f60\u8001\u4eba\u5bb6\u2014\u2014\u82df\u5973 \u4fa0\u3002\u201d \u3000\u3000\u201c\u8001\u778e\u5b50\u771f\u7684\u8fd9\u4e48\u8bf4\u8fc7\u2026\u2026\u201d \u3000\u3000\u201c\u662f\uff0c\u5e08\u7236\u8bf4\uff0c\u4ed6\u6700\u6015\u7684\u4eba\u5c31\u662f\u8001\u524d\u8f88\u2026\u2026\u201d \u3000\u3000\u201c\u4ed6\u6015\u6211\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u4f46\u6700\u656c\u91cd\u7684\u4eba\u4e5f\u662f\u8001\u524d\u8f88\uff0c\u5e08\u7236\u5982\u6b64\uff0c\u6211\u8fd9\u505a\u5f92\u5f1f\u7684\u600e\u6562\u4e0d\u6015\u3002\u201d\u5c0f\u71d5\u5b50\u8bed\u6c14\u771f\u631a\uff0c \u8138\u4e0a\u6cdb\u8d77\u4e86\u7b11\u5bb9\u3002 \u3000\u3000\u4f46\u662f\uff0c\u82df\u6167\u6708\u5374\u7a81\u7136\u6d41\u4e0b\u4e86\u4e24\u884c\u6cea\u6c34\uff0c\u9053\uff1a\u201c\u6211\u8bef\u4f1a\u4ed6\u4e8c\u5341\u4e2a\u5e74\u5934\u4e86\uff0c\u6211\u4e00\u76f4\u8ba4\u4e3a\u4ed6\u8f7b \u89c6\u6211\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u4e0d\u4e0d\u2026\u2026\u201d\u5c0f\u71d5\u5b50\u6025\u6025\u63a5\u9053\uff1a\u201c\u5e08\u7236\u63d0\u8d77\u8001\u524d\u8f88\u65f6\uff0c\u4e00\u8138\u8bda\u656c\u4e4b\u8272\uff0c\u5c31\u597d\u50cf\u662f\u62dc\u795e \u6c42\u4f5b\u4e00\u6837\u3002\u201d \u3000\u3000\u201c\u8001\u778e\u5b50\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u544a\u8bc9\u6211\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u591a\u6d3b\u51e0\u5e74\uff0c\u8fd9\u4e24\u5e74\u6211\u95ed\u5173\u9759\u601d\uff0c\u4e5f\u628a\u5f88\u591a\u4e8b\u60c5 \u60f3\u901a\u4e86\u3002\u201d\u82df\u6167\u6708\u9053\uff1a\u201c\u53ef\u662f\uff0c\u4f60\u7adf\u7136\u7b49\u4e0d\u53ca\u6211\u53bb\u89c1\u4f60\uff0c\u5c31\u8fd9\u6837\u6b7b\u4e86\u3002\u201d \u3000\u3000\u201c\u8001\u524d\u8f88\uff01\u201d\u5c0f\u71d5\u5b50\u9eef\u7136\u63a5\u9053\uff1a\u201c\u5e08\u7236\u7559\u4e86\u4e00\u5c01\u4fe1\u7ed9\u4f60\u3002\u201d \u3000\u3000\u201c\u600e\u4e0d\u65e9\u8bf4\uff0c\u5728\u54ea\u91cc\uff0c\u5feb\u62ff\u7ed9\u6211\u2026\u2026\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u7559\u5728\u77f3\u5ba4\u91cc\uff0c\u4ecd\u7136\u63e3\u5728\u5e08\u7236\u7684\u8863\u888b\u91cc\u3002\u5e08\u7236\u8bf4\uff0c\u665a\u8f88\u5982\u82e5\u6709\u7f18\u80fd\u89c1\u5230\u8001\u524d \u8f88\uff0c\u5c31\u628a\u8fd9\u4ef6\u4e8b\u5949\u544a\u524d\u8f88\uff0c\u5982\u662f\u65e0\u7f18\u76f8\u89c1\uff0c\u5c31\u8981\u665a\u8f88\u542f\u5f00\u77f3\u5ba4\u4e4b\u540e\uff0c\u628a\u5b83\u70e7\u4e86\uff0c\u800c\u4e14\u4e0d\u5f97\u62c6 \u9605\u3002\u201d \u3000\u3000\u82df\u6167\u6708\u62b9\u53bb\u8138\u4e0a\u7684\u6cea\u75d5\uff0c\u6781\u5c3d\u8f7b\u58f0\u5730\u8bf4\u9053\uff1a\u201c\u5c0f\u71d5\u5b50\uff01\u6709\u4ec0\u4e48\u8981\u6211\u5e2e\u5fd9\u4f60\u7684\u5730\u65b9\uff0c\u53ea\u7ba1 \u63d0\u51fa\u6765\u5427\uff01\u201d \u3000\u3000\u201c\u5982\u6709\u82df\u5973\u4fa0\u51fa\u9762\u4e3b\u6301\u5927\u5c40\uff0c\u4e2d\u539f\u6b66\u6797\u540c\u9053\u5c31\u6709\u6551\u4e86\u3002\u201d \u3000\u3000\u968f\u7740\u8bdd\u58f0\uff0c\u6c5f\u5343\u91cc\u5927\u6b65\u8d70\u4e86\u8fdb\u6765\uff0c\u9ed1\u50e7\u3001\u767d\u9053\u548c\u738b\u5f64\u9c7c\u8d2f\u76f8\u968f\u8eab\u540e\uff0c\u4e5f\u4e00\u4e00\u7684\u8d70\u4e86\u8fdb \u6765\u3002 \u5367\u9f99\u751f\u300a\u71d5\u5b50\u4f20\u5947\u300b \u7b2c\u5341\u516b\u56de\u3000\u5c11\u6797\u63ed\u79d8 \u3000\u3000\u201c\u6c5f\u53d4\u53d4\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6b22\u53eb\u4e86\u4e00\u58f0\uff0c\u5411\u6c5f\u5343\u91cc\u6251\u4e86\u8fc7\u53bb\uff0c\u4e00\u526f\u5982\u89c1\u4eb2\u4eba\u822c\u7684\u5feb\u4e50\u3002 \u3000\u3000\u201c\u4f60\u5c31\u662f\u6c5f\u5343\u91cc\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u662f\uff0c\u6c5f\u67d0\u6709\u5e78\uff0c\u62dc\u8bc6\u5973\u4fa0\u3002\u201d \u3000\u3000\u201c\u4e0d\u7528\u5ba2\u6c14\u4e86\uff0c\u5c0f\u71d5\u5b50\u5df2\u7ecf\u544a\u8bc9\u4e86\u6211\u4f60\u7684\u4e8b\u60c5\uff0c\u8001\u778e\u5b50\u8fd9\u4e9b\u5e74\u6765\u591a\u4e8f\u4f60\u7167\u987e\u4ed6\u7684\u8d77\u5c45\u751f \u6d3b\u3002\u201d \u3000\u3000\u201c\u4ed6\u5982\u80fd\u542c\u5230\u8fd9\u4e00\u53e5\u8bdd\u2026\u2026\u201d\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u5fc5\u7136\u4f1a\u96c0\u8dc3\u800c\u8d77\uff0c\u8bf4\u4e0d\u5b9a\u8fde\u5931\u660e\u7684\u53cc\u76ee\uff0c\u4e5f \u4f1a\u91cd\u89c1\u5149\u660e\u5462\uff1f\u201d \u3000\u3000\u201c\u6c5f\u5144\uff0c\u8fd9\u592a\u7384\u4e86\u5427\uff01\u201d\u738b\u5f64\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u82df\u5973\u4fa0\u662f\u4f55\u7b49\u8eab\u4efd\uff1f \u3000\u3000\u4f60\u5c82\u53ef\u4fe1\u53e3\u5f00\u6cb3\uff1f\u201c \u3000\u3000\u201c\u662f\u771f\u7684\u2026\u2026\u201d\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u4ed6\u672c\u5c31\u6709\u81ea\u533b\u53cc\u76ee\u7684\u80fd\u529b\uff0c\u4f7f\u5b83\u91cd\u89c1\u5149\u660e\uff0c\u4f46\u662f\u4ed6\u5374\u4e0d\u80af \u590d\u660e\u3002\u201d \u3000\u3000\u4e09\u516c\u4e3b\u7a81\u7136\u63a5\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\uff0c\u4ed6\u8ba4\u4e3a\u8fd9\u4e2a\u4e16\u754c\u4e0a\uff0c\u5df2\u7ecf\u6ca1\u6709\u8ba9\u4ed6\u7231\u770b\u7684\u4eba\u3001\u7269\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u4e09\u516c\u4e3b\u6b63\u60f3\u51fa\u8a00\u8fa9\u89e3\uff0c\u4f46\u89c1\u5e08\u7236\u5782\u9996\u542b\u6cea\uff0c\u7acb\u523b\u4f4f\u53e3\u3002 \u3000\u3000\u201c\u4ed6\u8eab\u4e2d\u9b54\u6559\u4e03\u715e\u6bd2\u9488\uff0c\u867d\u4ed7\u51ed\u7740\u6df1\u539a\u5185\u529b\uff0c\u53ef\u4ee5\u538b\u5236\u5947\u6bd2\uff0c\u4e0d\u8ba9\u5b83\u53d1\u4f5c\u51fa\u6765\u3002\u201d\u6c5f\u5343 \u91cc\u9053\uff1a\u201c\u4f46\u4ed6\u5b9e\u5728\u6d3b\u5f97\u5f88\u8f9b\u82e6\uff0c\u6bcf\u5929\u8981\u8017\u8d39\u4e24\u4e2a\u65f6\u8fb0\u7684\u65f6\u5149\u548c\u6bd2\u6027\u6297\u62d2\uff0c\u8fd9\u4e5f\u662f\u4ed6\u81ea\u6c42\u901f\u6b7b \u7684\u539f\u56e0\uff0c\u6211\u628a\u5c0f\u71d5\u5b50\u5f15\u5165\u4ed6\u7684\u95e8\u4e0b\uff0c\u5b8c\u4e86\u4ed6\u6700\u540e\u7684\u5fc3\u613f\uff0c\u4e3a\u4f7f\u5c0f\u71d5\u5b50\u6709\u80fd\u529b\u640f\u6740\u5f3a\u654c\uff0c\u624d\u8f6c \u5ac1\u5185\u529f\u3002\u81ea\u95ed\u77f3\u5ba4\uff0c\u4ee5\u6c42\u89e3\u8131\u2026\u2026\u201d \u3000\u3000\u201c\u6c5f\u5343\u91cc\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u591a\u7b49\u4e24\u5e74\u518d\u628a\u5c0f\u71d5\u5b50\u5f15\u5165\u4ed6\u7684\u95e8\u4e0b\u3002\u201d \u3000\u3000\u201c\u82df\u5973\u4fa0\uff0c\u6211\u5df2\u7ecf\u7b49\u4e86\u4e94\u5e74\uff0c\u5b9e\u4e5f\u4e0d\u5fcd\u518d\u89c1\u4ed6\u6bd2\u53d1\u65f6\u7684\u75db\u82e6\u2026\u2026\u201d\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u4f55\u51b5\uff0c \u9b54\u6559\u4e1c\u8fdb\uff0c\u6c5f\u6e56\u4e0a\u5927\u52ab\u5df2\u6210\uff0c\u518d\u665a\u4e86\u4e5f\u6765\u4e0d\u53ca\u4e86\u3002\u201d \u3000\u3000\u201c\u5929\u554a\uff01\u96be\u9053\u8fd9\u5c31\u662f\u5b9a\u6570\uff0c\u6211\u4e3a\u4ec0\u4e48\u60f3\u4e86\u4e8c\u5341\u5e74\u624d\u60f3\u901a\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u4e8c\u5341\u5e74\u5927\u68a6\u521d\u9192\uff0c\u4eba \u9192\u6765\u6068\u4e8b\u5df2\u94f8\uff0c\u5f53\u4eca\u4e4b\u4e16\uff0c\u53ea\u6709\u6211\u80fd\u52a9\u4ed6\u89e3\u53bb\u4e03\u715e\u6bd2\u9488\uff0c\u53ef\u662f\uff0c\u6211\u5374\u8bef\u4e86\u4ed6\u3002\u201d \u3000\u3000\u201c\u662f\u5b9a\u6570\u3002\u6c5f\u67d0\u4eba\u662f\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u5728\u6b64\u5730\u80fd\u9047\u4e0a\u82df\u5973\u4fa0\uff0c\u800c\u4e14\uff0c\u53c8\u662f\u5982\u6b64\u4e00\u4e2a\u6e05\u6717\u660e\u6f88 \u7684\u5c40\u9762\uff0c\u6068\u53ea\u6068\u6211\u6c5f\u67d0\u4eba\u80c6\u5c0f\u5982\u9f20\uff0c\u5fc3\u754f\u82df\u5973\u4fa0\u7684\u5feb\u5251\u593a\u9b42\uff0c\u7adf\u7136\u4e0d\u6562\u5bfb\u4e0a\u95e8\u53bb\uff0c\u62fc\u7740\u4e00\u547d \u4ee3\u4e49\u5144\u6c42\u544a\u5973\u4fa0\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u662f\u4ed6\u7684\u5144\u5f1f\uff1f\u2026\u2026\u201d\u82df\u6167\u6708\u8bb6\u5f02\u7684\u671b\u7740\u6c5f\u5343\u91cc\uff0c\u9053\uff1a\u201c\u4ed6\u662f\u771f\u7684\u8ba4\u4e86\u4f60\uff0c\u8fd8\u662f\u53e3\u5934 \u4e0a\u968f\u4fbf\u6536\u3002\u201d \u3000\u3000\u201c\u662f\u771f\u7684\u2026\u2026\u201d\u6c5f\u5343\u91cc\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u6709\u4e00\u6b21\uff0c\u4ed6\u81ea\u884c\u5bfb\u6b7b\uff0c\u6c5f\u67d0\u4eba\u6597\u80c6\u548c\u4ed6\u4e89\u8bba\uff0c\u8bf4\u4ed6 \u754f\u82e6\u6c42\u6b7b\uff0c\u81ea\u79c1\u53ef\u803b\uff0c\u800c\u4e14\uff0c\u8a00\u800c\u65e0\u4fe1\uff0c\u51c6\u5907\u628a\u6b66\u6797\u7edd\u6280\u57cb\u5165\u6cc9\u4e0b\u3002\u90a3\u6b21\u4e89\u8bba\u4e4b\u540e\uff0c\u4ed6\u624d\u8ba4 \u6211\u4f5c\u4e3a\u5144\u5f1f\uff0c\u4f46\u5374\u8981\u6211\u4e00\u5e74\u4e4b\u5185\uff0c\u5e2e\u4ed6\u627e\u5230\u8863\u94b5\u4f20\u4eba\u3002\u4ed6\u6d3b\u5f97\u592a\u8f9b\u82e6\u4e86\uff0c\u65e5\u65e5\u5982\u5229\u5203\u523a\u5fc3\uff0c \u56e0\u6b64\uff0c\u6c5f\u67d0\u4eba\u627e\u5230\u4e86\u5c0f\u71d5\u5b50\uff0c\u5b69\u5b50\u867d\u7136\u624d\u6167\u7edd\u4f26\uff0c\u4f46\u5374\u65e0\u6cd5\u4f7f\u529f\u529b\u901f\u6210\uff0c\u6240\u4ee5\uff0c\u501f\u738b\u7edf\u9886\u4e4b \u529b\uff0c\u53d6\u5230\u4e86\u5343\u5e74\u53c2\u738b\uff0c\u518d\u52a0\u4e0a\u4e49\u5144\u8f6c\u5ac1\u85aa\u706b\uff0c\u624d\u9020\u5c31\u5c0f\u71d5\u5b50\u8fd9\u4e2a\u4eba\u624d\u3002\u201d \u3000\u3000\u82df\u6167\u6708\u9053\uff1a\u201c\u542c\u8bf4\u4ed6\u7559\u4e86\u4e00\u5c01\u4fe1\u7ed9\u6211\uff0c\u4ed6\u53cc\u76ee\u5df2\u778e\uff0c\u4ec0\u4e48\u4eba\u5e2e\u4ed6\u4ee3\u7b14\u5199\u7684\uff1f\u201d \u3000\u3000\u201c\u4e49\u5144\u81ea\u5df1\u5199\u7684\uff0c\u4ed6\u76ee\u76f2\u5fc3\u4e0d\u76f2\uff0c\u611f\u5e94\u80fd\u529b\u4e4b\u5f3a\uff0c\u53ef\u8fa8\u8bc6\u5341\u4e08\u843d\u53f6\u7684\u58f0\u97f3\u2026\u2026\u201d\u6c5f\u5343\u91cc \u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e09\u5e74\u540e\u5f00\u542f\u77f3\u5ba4\u4e4b\u95e8\uff0c\u82df\u5973\u4fa0\u5c31\u53ef\u4ee5\u89c1\u5230\u4ed6\u4e00\u624b\u94c1\u94a9\u94f6\u5212\u7684\u597d\u4e66\u6cd5\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\u5427\uff01\u5230\u65f6\u5019\uff0c\u6211\u4eec\u4e00\u8d77\u53bb\u5f00\u77f3\u95e8\u2026\u2026\u201d \u3000\u3000\u201c\u4e49\u5144\u5982\u662f\u9634\u7075\u6709\u77e5\uff0c\u5b9a\u4f1a\u542b\u7b11\u4e5d\u6cc9\u7684\u3002\u201d\u6c5f\u5343\u91cc\u8bdd\u9898\u4e00\u8f6c\uff0c\u9053\uff1a\u201c\u5f53\u4e16\u7684\u5404\u5927\u95e8\u6d3e\u90fd \u5df2\u88ab\u9b54\u6559\u63a7\u5236\uff0c\u82df\u5973\u4fa0\u65e2\u5df2\u91cd\u5165\u6c5f\u6e56\uff0c\u5c31\u8be5\u66ff\u6b66\u6797\u4e3b\u6301\u6b63\u4e49\uff01\u201d \u3000\u3000\u201c\u4e0d\uff0c\u5c0f\u71d5\u5b50\u5982\u4eca\u5df2\u5f97\u4ed6\u771f\u4f20\uff0c\u6280\u827a\u7edd\u4e0d\u4f1a\u8f93\u6211\uff0c\u4f55\u51b5\uff0c\u626b\u8361\u9b54\u6559\u662f\u4ed6\u7684\u9057\u8a00\uff0c\u5c0f\u71d5\u5b50 \u5c82\u53ef\u8fdd\u80cc\u9057\u547d\uff0c\u6211\u4f1a\u5168\u529b\u76f8\u52a9\u3002\u4e91\u513f\uff0c\u6536\u62fe\u4e00\u4e0b\uff0c\u8ddf\u4e3a\u5e08\u7684\u8d70\uff01\u201d \u3000\u3000\u201c\u597d\uff0c\u5f1f\u5b50\u4e5f\u6b63\u6709\u4e8b\u7980\u660e\u5e08\u7236\u3002\u201d\u4e09\u516c\u4e3b\u9053\uff1a\u201c\u5f1f\u5b50\u60f3\u629b\u53bb\u516c\u4e3b\u7684\u5934\u8854\uff0c\u548c\u5e08\u7236\u4e00\u8d77\u95ef \u8361\u6c5f\u6e56\uff0c\u5e2e\u52a9\u5c0f\u71d5\u5b50\u626b\u8361\u9b54\u6559\u3002\u201d \u3000\u3000\u201c\u4f60\u65e9\u5df2\u662f\u6c5f\u6e56\u4eba\u4e86\uff0c\u518d\u5165\u6c5f\u6e56\u5e2e\u52a9\u5c0f\u71d5\u5b50\u6211\u4e0d\u53cd\u5bf9\uff0c\u800c\u4e14\uff0c\u4e3a\u5e08\u7684\u4e5f\u6b63\u6709\u6b64\u610f\uff0c\u6211\u5e26 \u4f60\u79bb\u5f00\u6b64\u5730\uff0c\u5c31\u662f\u8981\u628a\u4e3a\u5e08\u8fd1\u5e74\u4e2d\u4f53\u4f1a\u5f97\u5230\u7684\u5251\u827a\u7edd\u5b66\uff0c\u4f20\u6388\u7ed9\u4f60\uff0c\u6211\u4e0d\u80fd\u8ba9\u8001\u778e\u5b50\u4e13\u7f8e\u4e8e \u524d\u3002\u4f46\u4f60\u629b\u53bb\u516c\u4e3b\u7684\u5934\u8854\uff0c\u4e3a\u5e08\u7684\u5374\u4e0d\u8d5e\u540c\uff0c\u4f60\u751f\u4e3a\u91d1\u679d\u7389\u53f6\uff0c\u5c82\u53ef\u81ea\u7518\u5f03\u4e4b\u3002\u201d \u3000\u3000\u201c\u751f\u5728\u5e1d\u738b\u5bb6\uff0c\u6709\u4ec0\u4e48\u597d\uff1f\u6df1\u5bab\u5e7d\u5c45\uff0c\u6709\u5982\u72af\u4eba\u4f55\u5f02\uff0c\u7236\u738b\u3001\u6bcd\u540e\u5404\u6709\u4e13\u5ba0\uff0c\u4e00\u5e74\u4e2d\u4e5f \u96be\u5f97\u89c1\u51e0\u6b21\u9762\uff0c\u89c1\u9762\u5982\u89c1\u5b98\uff0c\u5148\u8981\u884c\u4e0a\u5bab\u5ef7\u4e2d\u70e6\u7410\u7684\u793c\u4eea\uff0c\u4ed6\u4eec\u771f\u6b63\u5173\u5fc3\u7684\u662f\u56fd\u4e8b\u6c5f\u5c71\u548c\u662f \u5426\u8fd8\u53d7\u5230\u7687\u4e0a\u7684\u6069\u5ba0\u2026\u2026\u201d\u4e09\u516c\u4e3b\u65e0\u9650\u9eef\u7136\u5730\u8bf4\uff0c\u201c\u6211\u662f\u5f88\u53d7\u7236\u738b\u5ba0\u7231\u7684\u5973\u513f\uff0c\u8fd8\u53ef\u4ee5\u6043\u5ba0 \u4e3a\u81ea\u5df1\u4e89\u5230\u51e0\u5206\u81ea\u7531\uff0c\u4e00\u822c\u7684\u6240\u8c13\u91d1\u679d\u7389\u53f6\u65e9\u5c31\u88ab\u7236\u738b\u89c6\u4e3a\u793c\u54c1\uff0c\u914d\u5ac1\u7ed9\u4ed6\u7684\u5ba0\u81e3\u7231\u5c06\uff0c\u4e0d \u7ba1\u5973\u513f\u7684\u611f\u53d7\u5982\u4f55\uff1f\u6216\u662f\u5f53\u4f5c\u5956\u8d50\uff0c\u8d4f\u7ed9\u4e86\u529f\u81e3\uff0c\u6211\u4e3a\u4e86\u7236\u738b\u7684\u6c5f\u5c71\u8fdc\u8d74\u897f\u57df\uff0c\u53d7\u5c3d\u4e86\u8bf8\u822c \u7684\u6298\u78e8\uff0c\u4f46\u56de\u5230\u4eac\u5e08\u4e4b\u540e\uff0c\u5374\u6ca1\u6709\u53d7\u5230\u4efb\u4f55\u7684\u6170\u95ee\u5956\u52b1\uff0c\u53ea\u6015\u53cd\u800c\u8ba4\u4e3a\u6211\u662f\u7978\u56fd\u4e4b\u6e90\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u4f1a\u5427\uff01\u6211\u77e5\u9053\u7687\u4e0a\u5f88\u559c\u7231\u4f60\u2026\u2026\u201d\u82df\u6167\u6708\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u4f20\u4f60\u6b66\u827a\uff0c\u4ed6\u8fd8\u7279\u5730\u7684\u63a5\u89c1 \u8fc7\u6211\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u4ed6\u6ca1\u6709\u903c\u6211\u5ac1\u4eba\uff0c\u5c31\u662f\u5e0c\u671b\u6211\u7559\u5728\u5bab\u4e2d\uff0c\u80fd\u4fdd\u62a4\u7687\u5bab\u4e2d\u7684\u5b89\u5168\u3002\u201d\u4e09\u516c\u4e3b\u8bf4\uff1a \u201c\u4f46\u897f\u57df\u5f52\u6765\uff0c\u6211\u5df2\u611f\u89c9\u5230\u7236\u738b\u5bf9\u6211\u5df2\u5728\u6162\u6162\u7684\u8f6c\u53d8\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4e2a\uff0c\u8981\u4e0d\u8981\u6211\u548c\u7687\u4e0a\u8c08\u8c08\uff1f\u201d\u82df\u6167\u6708\u9053\uff1a\u201c\u6211\u4eec\u867d\u53ea\u89c1\u8fc7\u4e00\u9762\uff0c\u5012\u4e5f\u9887\u8c08\u5f97\u6765\uff0c\u518d \u8bf4\u4ed6\u4e5f\u662f\u4eba\uff0c\u6ca1\u6709\u4ec0\u4e48\u597d\u6015\u7684\uff0c\u8c08\u7684\u597d\uff0c\u4f60\u5c31\u7559\u7740\u8fd9\u4e2a\u516c\u4e3b\u7684\u5934\u8854\uff0c\u8c08\u4e0d\u597d\uff0c\u4e3a\u5e08\u5c31\u5e26\u7740\u4f60 \u79bb\u5f00\uff0c\u770b\u770b\u8c01\u80fd\u5948\u6211\u4f55\uff1f\u201d \u3000\u3000\u738b\u5f64\u5403\u4e86\u4e00\u60ca\uff0c\u5fd6\u9053\uff1a\u5979\u662f\u6c5f\u6e56\u5947\u4eba\uff0c\u5251\u827a\u7cbe\u7edd\uff0c\u5bf9\u671d\u5ef7\u4e2d\u7684\u6cd5\u5ea6\u3001\u793c\u4eea\u53ef\u672a\u5fc5\u653e\u5728\u5fc3 \u4e0a\uff0c\u5979\u82e5\u771f\u8981\u53bb\u89c1\u7687\u4e0a\uff0c\u8fd9\u5185\u5bab\u4e2d\u867d\u7136\u4e91\u96c6\u4e86\u8fd1\u4e24\u767e\u540d\u4f8d\u536b\uff0c\u53c8\u6709\u8c01\u80fd\u62e6\u5f97\u4f4f\u5979\uff1f \u3000\u3000\u60f3\u7740\uff0c\u4ed6\u5f53\u4e0b\u4fbf\u7b11\u9053\uff1a\u201c\u5982\u679c\u4e09\u516c\u4e3b\u613f\u610f\u79bb\u5f00\u5185\u5bab\uff0c\u82df\u5973\u4fa0\u53c8\u4e0d\u53cd\u5bf9\u65f6\uff0c\u90a3\u5012\u662f\u4e2a\u5f88\u597d \u7684\u4e3b\u610f\u4e86\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u82df\u6167\u6708\u5947\u9053\uff1a\u201c\u5979\u662f\u516c\u4e3b\uff0c\u7687\u4e0a\u7684\u7231\u5973\uff0c\u5979\u4e3a\u4ec0\u4e48\u8981\u9003\u5165\u6c5f\u6e56\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\uff0c\u5979\u5e26\u6765\u4e86\u897f\u57df\u7684\u9b54\u6559\u5f1f\u5b50\uff0c\u6f5c\u4f0f\u5728\u5185\u5bab\u4e4b\u4e2d\uff0c\u7687\u4e0a\u65e9\u77e5\u9053\u4e86\u3002\u201d\u738b\u5f64\u9053\uff1a\u201c\u4e8b \u5b9e\u4e0a\uff0c\u4e09\u516c\u4e3b\u5df2\u72af\u4e86\u85cf\u533f\u654c\u4eba\u3001\u6b3a\u9a97\u7687\u4e0a\u7684\u5927\u7f6a\uff0c\u7687\u4e0a\u662f\u4e00\u56fd\u4e4b\u541b\uff0c\u600e\u80fd\u5bb9\u4e0b\u8fd1\u8eab\u81e3\u5b50\u5982\u6b64 \u5192\u72af\u9f99\u5a01\uff0c\u4f46\u7b49\u6b64\u95f4\u4e8b\u4e86\uff0c\u5f88\u53ef\u80fd\u4e0b\u4ee4\u5904\u7f6e\u4e09\u516c\u4e3b\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u600e\u4e48\u80fd\u591f\u602a\u5979\uff0c\u5979\u4e00\u8eab\u6d89\u9669\uff0c\u72ec\u627f\u82e6\u96be\uff0c\u4e5f\u662f\u4e3a\u5979\u7236\u7687\u7684\u6c5f\u5c71\uff0c\u5f53\u7687\u5e1d\u5c31\u53ef\u4ee5\u4e0d\u8bb2 \u9053\u7406\u3001\u4e0d\u5206\u9ed1\u767d\u4e86\u4e48\uff1f\u201d \u3000\u3000\u738b\u5f64\u867d\u7136\u5fc3\u4e2d\u7126\u6025\uff0c\u4f46\u4ed6\u5fc3\u4e2d\u660e\u767d\uff0c\u4e0d\u80fd\u628a\u4e8b\u60c5\u5f04\u50f5\uff0c\u4e00\u5df4\u6fc0\u8d77\u4e86\u82df\u6167\u6708\u7684\u6012\u706b\uff0c\u8ba9\u5979 \u5931\u53bb\u81ea\u5236\uff0c\u90a3\u5c31\u5927\u5927\u7684\u9ebb\u70e6\u4e86\u3002 \u3000\u3000\u201c\u4f34\u541b\u5982\u4f34\u864e\u554a\uff01\u201d\u738b\u5f64\u9053\uff1a\u201c\u76ee\u4e0b\u7687\u4e0a\u6b63\u906d\u53d7\u5185\u5fe7\u5916\u60a3\u7684\u56f0\u6270\uff0c\u8001\u524d\u8f88\u5982\u80af\u5e26\u516c\u4e3b\u79bb \u5f00\u7687\u5bab\uff0c\u65e2\u53ef\u4fdd\u5168\u516c\u4e3b\uff0c\u4e5f\u7ed9\u4e86\u7687\u5e1d\u9762\u5b50\uff0c\u4f55\u4e50\u800c\u4e0d\u4e3a\u5462\uff1f\u201d \u3000\u3000\u82df\u6167\u6708\u4f3c\u662f\u4e2a\u76f8\u5f53\u56fa\u6267\u7684\u4eba\uff0c\u8fd8\u5f85\u51fa\u8a00\u53cd\u9a73\uff0c\u5ffd\u89c1\u4e09\u516c\u4e3b\u53e3\u9f7f\u542f\u52a8\uff0c\u4e00\u8bf4\u4e86\u6570\u8a00\u3002 \u3000\u3000\u5979\u7528\u7684\u662f\u4f20\u97f3\u4e4b\u672f\uff0c\u522b\u4eba\u53ea\u89c1\u5979\u53e3\u9f7f\u52a8\uff0c\u5374\u4e0d\u77e5\u5979\u8bf4\u4e9b\u4ec0\u4e48\uff1f \u3000\u3000\u4f46\u82df\u6167\u6708\u7684\u8138\u8272\u5374\u7f13\u548c\u4e0b\u6765\u4e86\uff0c\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u597d\u5427\uff01\u4f60\u5c31\u5199\u4e00\u4efd\u8f9e\u738b\u8868\u7ae0\uff0c\u4ea4\u7ed9\u738b\u5f64 \u8f6c\u5448\u7687\u4e0a\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u738b\u67d0\u4eba\u7406\u5f53\u6548\u52b3\u3002\u201d \u3000\u3000\u82df\u6167\u6708\u76ee\u5149\u8f6c\u6ce8\u5230\u6c5f\u5343\u91cc\u7684\u8eab\u4e0a\uff0c\u9053\uff1a\u201c\u6c5f\u6e56\u4e0a\u7684\u60c5\u52bf\uff0c\u662f\u5426\u5df2\u7ecf\u5230\u4e86\u53d1\u53d1\u53ef\u5371\u7684\u65f6\u523b \u4e86\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u5404\u5927\u95e8\u6d3e\u53ca\u6c5f\u6e56\u4e0a\u7684\u5e2e\u4f1a\uff0c\u5927\u90e8\u5206\u90fd\u53d7\u5230\u4e86\u6765\u81ea\u5916\u529b\u7684\u63a7\u5236\uff0c\u5fc5\u9700\u8981\u7acb\u523b\u52a8\u624b\uff0c \u5206\u522b\u89e3\u51b3\uff0c\u5148\u884c\u627e\u51fa\u51e0\u4e2a\u91cd\u5927\u95e8\u6d3e\u88ab\u63a7\u5236\u7684\u539f\u56e0\u3002\u201d\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u7136\u540e\uff0c\u4ee5\u8c0b\u5bf9\u7b56\uff0c\u5982\u82e5\u7b49 \u5230\u4ed6\u4eec\u529b\u91cf\u6c47\u96c6\u3001\u5408\u6d41\u4e4b\u540e\uff0c\u90a3\u5c31\u4e0d\u6613\u6297\u62d2\u4e86\u3002\u201d \u3000\u3000\u201c\u80fd\u4e0d\u80fd\u7ed9\u6211\u5341\u5929\u65f6\u95f4\u2026\u2026\u201d\u82df\u6167\u6708\u9053\uff1a\u201c\u5341\u65e5\u540e\uff0c\u6211\u518d\u548c\u4f60\u4eec\u89c1\u9762\uff0c\u5171\u540c\u5e94\u4ed8\u5371\u5c40\uff0c \u4ee5\u6e21\u52ab\u96be\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u5e26\u7740\u4e09\u516c\u4e3b\u548c\u601c\u82b1\u98de\u8eab\u800c\u53bb\u3002 \u3000\u3000\u201c\u5509\uff01\u8fd9\u4f4d\u82df\u5973\u4fa0\u628a\u671d\u5ef7\u4e2d\u7684\u4e8b\u7269\uff0c\u4e5f\u5f53\u4f5c\u6c5f\u6e56\u4e2d\u4e8b\u6765\u5904\u7406\u3002\u201d \u3000\u3000\u738b\u5f64\u957f\u957f\u5401\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5f53\u771f\u7684\u662f\u53ef\u6015\u6781\u4e86\uff0c\u6c5f\u5144\uff0c\u6700\u597d\u522b\u518d\u8ba9\u5979\u5165\u7687\u5bab\u4e86\uff01\u201d \u3000\u3000\u201c\u6211\u60f3\uff0c\u4e09\u516c\u4e3b\u4f1a\u5a49\u8f6c\u7684\u544a\u8bc9\u5979\u4e2a\u4e2d\u7684\u5229\u5bb3\u2026\u2026\u201d\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u5979\u4e3a\u4e86\u4e00\u53e5\u6c14\u8bdd\uff0c\u62d2\u7edd \u518d\u548c\u5979\u5fc3\u4e2d\u60c5\u90ce\u76f8\u89c1\uff0c\u800c\u4e14\u4e00\u628a\u6c99\u77f3\u6253\u778e\u4e86\u60c5\u90ce\u7684\u773c\u775b\uff0c\u8fd9\u4e00\u6c14\u6c14\u4e86\u4e8c\u5341\u5e74\uff0c\u7ec8\u4e8e\u94f8\u6210\u4e00\u751f \u6068\u4e8b\u3002\u201d \u3000\u3000\u201c\u6c5f\u53d4\u53d4\uff0c\u5979\u5c31\u662f\u5e08\u7236\u7684\u7ea2\u989c\u77e5\u5df1\u4e48\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u4ed6\u4eec\u662f\u5f53\u4ee3\u6c5f\u6e56\u4e0a\u6700\u6709\u6210\u5c31\u7684\u4e00\u5bf9\u60c5\u4fa3\uff0c\u5374\u56e0\u4e00\u53e5\u6c14\u8bdd\uff0c\u7ffb\u8138\u5206\u624b\uff0c\u524d\u5341\u5e74\u4f60 \u5e08\u7236\u8fd8\u4e00\u76f4\u8ffd\u89c5\u5979\u7684\u884c\u8e2a\uff0c\u5e0c\u671b\u80fd\u633d\u56de\u82b3\u5fc3\uff0c\u5374\u4e0d\u6599\u82df\u5973\u4fa0\u94c1\u4e86\u5fc3\uff0c\u5c31\u662f\u4e0d\u80af\u518d\u7eed\u524d\u7f18\uff0c\u4ee5 \u540e\u7684\u4e8b\u4f60\u90fd\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u5e08\u7236\u8eab\u4e2d\u4e03\u715e\u6bd2\u9488\uff0c\u662f\u5728\u88ab\u82df\u5973\u4fa0\u6253\u778e\u4e4b\u524d\uff1f\u8fd8\u662f\u6253\u778e\u4e4b\u540e\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u5f53\u7136\u662f\u5728\u6253\u778e\u4e4b\u540e\uff0c\u4f60\u5e08\u7236\u5982\u975e\u53cc\u76ee\u778e\u53bb\uff0c\u9b54\u6559\u4e2d\u4eba\u5982\u4f55\u80fd\u4f24\u5f97\u4e86\u4ed6\u4e00\u6839 \u6c57\u6bdb\u5462\uff1f\u201d \u3000\u3000\u201c\u6c5f\u53d4\u53d4\u2026\u2026\u201d\u5c0f\u71d5\u5b50\u60f3\u4e86\u60f3\uff0c\u9053\uff1a\u201c\u6211\u5e08\u7236\u662f\u4f55\u7b49\u8eab\u624b\uff0c\u600e\u4e48\u4f1a\u907f\u4e0d\u5f00\u82df\u5973\u4fa0\u7684\u4e00\u628a \u98de\u6c99\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u60f3\uff0c\u4ed6\u662f\u6545\u610f\u53d7\u4f24\u7684\uff0c\u5e0c\u671b\u80fd\u4e00\u6d88\u5979\u5fc3\u4e2d\u4e4b\u6c14\uff0c\u4f46\u82df\u5973\u4fa0\u4ecd\u662f\u4e0d\u80af\u56de\u5fc3\u8f6c\u610f\uff0c\u4f60\u5e08 \u7236\u767d\u778e\u4e86\u4e00\u53cc\u773c\u775b\uff0c\u4e5f\u7ed9\u4e86\u9b54\u6559\u4e2d\u4eba\u6697\u7b97\u4ed6\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u201c\u8fd9\u5973\u4eba\u597d\u6bd2\u597d\u6bd2\u2026\u2026\u201d\u5c0f\u71d5\u5b50\u7a81\u7136\u5192\u51fa\u4e86\u4e00\u53e5\u8bdd\uff0c\u5374\u542c\u5f97\u6c5f\u5343\u91cc\u51fa\u4e86\u4e00\u8eab\u51b7\u6c57\uff0c\u6025\u6025 \u8bf4\u9053\uff1a\u201c\u5c0f\u71d5\u5b50\uff0c\u4e0a\u4e00\u4ee3\u7684\u79c1\u4eba\u60c5\u4ec7\uff0c\u548c\u4f60\u65e0\u5173\uff0c\u4e0d\u53ef\u4efb\u610f\u6279\u8bc4\uff0c\u53e3\u51fa\u72c2\u8a00\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u4e0d\u6015\u5979\uff01\u201d\u5c0f\u71d5\u5b50\u8bf4\u9053\uff1a\u201c\u5979\u90a3\u6837\u6298\u817e\u5e08\u7236\uff0c\u6211\u4e0d\u627e\u5979\u7b97\u5e10\uff0c\u5df2\u7ecf\u662f\u5929\u9ad8\u5730\u539a\u4e86 \u5462\uff01\u201d \u3000\u3000\u201c\u5c0f\u71d5\u5b50\uff01\u2026\u2026\u201d\u6c5f\u5343\u91cc\u6709\u4e9b\u706b\u4e86\uff0c\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u4f60\u5e08\u7236\u5fc3\u76ee\u4e2d\u6700\u656c\u7231\u7684\u7684\u4eba\uff0c\u4f60\u4e5f \u6562\u4f24\u5bb3\u5979\u4e48\uff1f\u201d \u3000\u3000\u201c\u5c0f\u4f84\u4e0d\u6562\u201d \u3000\u3000\u6c5f\u5343\u91cc\u53d1\u4e86\u813e\u6c14\uff0c\u71d5\u6625\u98ce\u8fd8\u771f\u6709\u70b9\u5bb3\u6015\u3002 \u3000\u3000\u201c\u82df\u6167\u6708\u5c31\u662f\u4f60\u5e08\u7236\u5fc3\u4e2d\u6700\u656c\u7231\u7684\u4eba\uff0c\u6709\u4e00\u5929\uff0c\u4f60\u542f\u5f00\u77f3\u95e8\uff0c\u53d6\u51fa\u9057\u4e66\uff0c\u800c\u82df\u5973\u4fa0\u56e0\u4f60 \u7684\u4e0d\u656c\uff0c\u6ca1\u6709\u5230\u573a\uff0c\u4f60\u8981\u5982\u4f55\u5411\u4f60\u5e08\u7236\u4ea4\u4ee3\u3002\u201d \u3000\u3000\u201c\u5c0f\u71d5\u5b50\u660e\u767d\u4e86\uff0c\u4ee5\u540e\u518d\u4e5f\u4e0d\u6562\u653e\u8086\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5e72\u91cc\u53f9\u606f\u4e00\u58f0\uff0c\u8f6c\u5411\u738b\u5f64\u9053\uff1a\u201c\u8fd9\u4e00\u6b21\u662f\u5411\u4f60\u6c42\u52a9\u7684\uff0c\u5e0c\u671b\u4f60\u80fd\u8c03\u52a8\u4e00\u6279\u4eba\u624b\uff0c\u652f\u63f4 \u6211\u4eec\u3002\u201d \u3000\u3000\u201c\u884c\uff01\u738b\u67d0\u4eba\u80fd\u529e\u5230\u7684\u7edd\u4e0d\u63a8\u8f9e\uff0c\u5982\u662f\u4eac\u57ce\u4e2d\u5e73\u9759\u4e0b\u6765\uff0c\u5144\u5f1f\u51c6\u5907\u548c\u6c5f\u5144\u540c\u5165\u6c5f\u6e56\u4e00\u884c \u5462\uff01\u201d \u3000\u3000\u6c5f\u5e72\u91cc\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u662f\u6700\u597d\u4e0d\u8fc7\uff0c\u6709\u4f60\u540c\u884c\uff0c\u53ef\u4ee5\u968f\u65f6\u8c03\u52a8\u5b98\u5175\u52a9\u5a01\uff0c\u90a3\u5c31\u5e2e\u5fd9\u592a \u5927\u4e86\u3002\u5f53\u7136\uff0c\u6700\u610f\u5916\u7684\u662f\u5f97\u5230\u8461\u6167\u6708\u548c\u4e09\u516c\u4e3b\u8fd9\u80a1\u5f3a\u5927\u7684\u52a9\u529b\uff0c\u4e0d\u8fc7\uff0c\u738b\u5144\uff0c\u4e09\u516c\u4e3b\u6295\u5165\u6c5f \u6e56\uff0c\u7687\u4e0a\u4f1a\u540c\u610f\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u60f3\u4e0d\u4f1a\u8ffd\u7a76\uff0c\u636e\u5144\u5f1f\u8fd1\u65e5\u89c2\u5bdf\uff0c\u7687\u4e0a\u5bf9\u4e09\u516c\u4e3b\u7684\u5ba0\u7231\u65e5\u6e10\u6d88\u9000\uff0cM\u5bf9\u5979\u5f15\u5165\u9b54\u6559\u9ad8 \u624b\u6df7\u5165\u7687\u5bab\u4e00\u4e8b\uff0c\u66f4\u4e3a\u4e0d\u6ee1\uff0c\u8ba9\u5979\u957f\u5e74\u7559\u5728\u6df1\u5bab\u4e4b\u4e2d\uff0c\u6050\u6015\u6709\u5bb3\u65e0\u76ca\uff0c\u4f55\u51b5\uff0c\u7687\u4e0a\u77e5\u9053\u5979\u4e3a \u9b54\u6559\u6240\u4e58\uff0c\u65e9\u5df2\u4e0d\u518d\u4fe1\u4efb\u5979\u4e86\u3002\u201d \u3000\u3000\u201c\u5509\uff01\u751f\u5728\u5e1d\u738b\u4e4b\u5bb6\uff0c\u771f\u5012\u672a\u5fc5\u662f\u798f\u554a\uff01\u201d\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u4f46\u4e09\u516c\u4e3b\u6295\u5165\u6c5f\u6e56\u4e4b\u4e2d\uff0c\u6b64\u540e \u53c8\u4f55\u53bb\u4f55\u4ece\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd9\u6050\u6015\u8981\u4f60\u6c5f\u5144\u5927\u529b\u6210\u5168\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\uff1f\u2026\u2026\u201d\u6c5f\u5343\u91cc\u8ba2\u5f02\u5730\u9053\uff1a\u201c\u6211\u54ea\u6709\u8fd9\u7b49\u80fd\u529b\u3002\u201d \u3000\u3000\u738b\u5f64\u4f4e\u58f0\u5730\u548c\u6c5f\u5343\u91cc\u4ea4\u8c08\u4e86\u4e00\u9635\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u56de\u987e\u4e86\u5c0f\u71d5\u5b50\u4e00\u773c\uff0c\u9053\uff1a\u201c\u6211\u5c3d\u529b\u800c\u4e3a\u5427\uff01\u4e0d\u8fc7\uff0c\u8fd9\u4ef6\u4e8b\u8981\u82df\u6167\u6708\u540c\u610f\u624d\u6709\u5e0c\u671b \u7684\u3002\u201d \u3000\u3000\u201c\u6c5f\u5144\u80af\u5168\u529b\u652f\u63f4\u5c31\u884c\uff0c\u5176\u4ed6\u7684\uff0c\u7531\u4ed6\u4eec\u81ea\u7136\u53d1\u5c55\u5427\uff01\u201d \u3000\u3000\u201c\u6211\u660e\u767d\u4e86\u3002\u201d\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u627e\u4e00\u4e2a\u6e05\u9759\u7684\u5730\u65b9\uff0c\u7ed9\u6211\u4eec\u4f4f\u4e0b\uff0c\u7ed9\u5c0f\u71d5\u5b50\u4e24\u5929\u5047\u671f\uff0c\u8ba9 \u4ed6\u7236\u4e8e\u3001\u6bcd\u5b50\u56e2\u805a\u4e24\u65e5\uff0c\u7136\u540e\u6211\u8981\u5229\u7528\u4f59\u4e0b\u516b\u5929\u7684\u65f6\u95f4\uff0c\u8ba9\u4ed6\u4eec\u7ec3\u4e60\u4e00\u4e9b\u6b66\u529f\uff0c\u82df\u5973\u4fa0\u4e00\u56de \u6765\uff0c\u6211\u4eec\u7acb\u523b\u52a8\u8eab\u3002\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u6c5f\u5144\u51c6\u5907\u5148\u5230\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u5d69\u5c71\u5c11\u6797\u5bfa\uff0c\u5982\u679c\u987a\u5229\uff0c\u5c31\u8f6c\u5f80\u6b66\u5f53\u5c71\u4e00\u884c\uff0c\u4f9d\u5e8f\u662f\u795e\u5200\u5802\u3001\u4e10\u5e2e\u603b\u8235\uff0c\u8fd9\u56db\u4e2a\u5730\u65b9 \u80fd\u5b89\u5b9a\u4e0b\u6765\uff0c\u9b54\u6559\u60f3\u5728\u4e2d\u539f\u7acb\u8db3\uff0c\u5c31\u4e0d\u592a\u53ef\u80fd\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u738b\u67d0\u4e5f\u53bb\u51c6\u5907\u4e00\u4e0b\uff0c\u6211\u5c3d\u5168\u529b\u534f\u52a9\u3002\u201d \u3000\u3000\u82df\u6167\u6708\u5f88\u5b88\u7ea6\u5b9a\uff0c\u7b2c\u5341\u5929\u7684\u65e5\u843d\u65f6\u5206\uff0c\u679c\u7136\u5e26\u7740\u4e09\u516c\u4e3b\u8d76\u4e86\u56de\u6765\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u4e5f\u5229\u7528\u8fd9\u51e0\u65e5\u7684\u65f6\u95f4\uff0c\u8981\u5c0f\u71d5\u5b50\u5c3d\u53ef\u80fd\u628a\u4e00\u4e9b\u6b66\u529f\u4f20\u7ed9\u9ed1\u50e7\u3001\u767d\u9053\u3001\u738b\u91cd\u5c71\u4e09 \u4eba\u3002 \u3000\u3000\u4ed6\u4eec\u5e74\u7eaa\u867d\u90fd\u4e0d\u5927\uff0c\u4f46\u662f\uff0c\u5374\u662f\u6b64\u884c\u7684\u6218\u6597\u4e3b\u529b\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u548c\u7236\u6bcd\u56e2\u805a\u4e86\u4e24\u65e5\uff0c\u5929\u4f26\u4e4b\u4e50\u3002\u8ff0\u8bf4\u4e0d\u5c3d\uff0c\u4f59\u4e0b\u7684\u516b\u5929\uff0c\u5c0f\u71d5\u5b50\u90fd\u5728\u4f20\u6388\u4e03\u5de7\u50e7 \u7b49\u4e09\u4eba\u6b66\u529f\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u8981\u6c42\u4e25\u683c\uff0c\u4e0d\u5047\u8bcd\u8272\uff0c\u8fd8\u771f\u628a\u9ed1\u50e7\u3001\u767d\u9053\u3001\u738b\u91cd\u5c71\u6298\u817e\u7684\u5e2d\u4e0d\u6696\u6687\uff0c\u8fde\u89c9\u90fd\u4e0d\u6562 \u7761\uff0c\u5077\u5077\u82e6\u7ec3\u3002\u56e0\u4e3a\uff0c\u5c0f\u71d5\u5b50\u53e3\u820c\u5982\u5200\uff0c\u9a82\u8d77\u4eba\u6765\u4e00\u70b9\u4e5f\u4e0d\u7559\u9762\u5b50\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u65f6\u65e5\u867d\u77ed\uff0c\u4f46\u4e09\u4e2a\u4eba\u5374\u4e5f\u5b66\u5230\u4e86\u4e0d\u5c11\u7684\u4e1c\u897f\u3002 \u3000\u3000\u738b\u5f64\u534f\u8c03\u4e86\u4e94\u57ce\u5175\u9a6c\u53f8\uff0c\u8c03\u4e86\u4e00\u4e07\u540d\u7cbe\u9510\u7684\u6b65\u5175\uff0c\u8fdb\u5165\u4eac\u57ce\uff0c\u4f4f\u624e\u5728\u7687\u5bab\u56db\u5468\uff0c\u4fdd\u62a4\u7687 \u5bab\uff0c\u5e03\u7f6e\u4e4b\u4e25\uff0c\u5df2\u5230\u4e86\u98de\u9e1f\u96be\u6e21\u4e4b\u5883\u3002 \u3000\u3000\u4ed6\u786e\u5b9e\u5728\u5168\u529b\u5e2e\u5fd9\uff0c\u7531\u5382\u3001\u536b\u4e2d\u9009\u4e86\u56db\u5341\u540d\u7cbe\u9510\u9ad8\u624b\u4e4b\u5916\uff0c\u7531\u5185\u5bab\u4f8d\u536b\u4e2d\u4e5f\u9009\u4e86\u56db\u5341\u540d \u9ad8\u624b\u968f\u884c\uff0c\u53e6\u6709\u71d5\u98de\u3001\u97e9\u6d9b\u3001\u8d75\u4fdd\u3001\u9648\u5b8f\u3001\u91d1\u5929\u7965\u968f\u884c\uff0c\u52a0\u4e0a\u4e86\u6c5f\u5343\u91cc\u7b49\u4e94\u4eba\uff0c\u4e09\u516c\u4e3b\u3001\u601c \u82b1\u4e8c\u5973\uff0c\u8fd1\u767e\u9a91\u5065\u9a6c\u51fa\u4e86\u4eac\u57ce\uff0c\u76f4\u5954\u5d69\u5c71\u5c11\u6797\u5bfa\u4e2d\u3002 \u3000\u3000\u82df\u6167\u6708\u5374\u662f\u4e00\u4eba\u72ec\u8d70\u3002 \u3000\u3000\u6700\u5999\u7684\u662f\uff0c\u738b\u5f64\u8bf7\u4e86\u4e00\u9053\u5723\u65e8\uff0c\u6307\u6d3e\u9a6c\u6587\u4e2d\u5168\u529b\u534f\u52a9\u738b\u5f64\uff0c\u6d3e\u6b65\u9a91\u7cbe\u5175\u516b\u767e\u540d\uff0c\u5230\u5d69\u5c71 \u5c11\u6797\u5bfa\u5916\u542c\u7528\u3002 \u3000\u3000\u738b\u5f64\u7b49\u4e00\u884c\u4eba\u8d76\u5230\u5c11\u6797\u5bfa\u5916\uff0c\u9a6c\u6587\u4e2d\u7684\u516b\u767e\u7cbe\u5175\uff0c\u65e9\u5df2\u628a\u5c11\u6797\u5bfa\u56e2\u56e2\u56f4\u4f4f\u3002 \u3000\u3000\u9a6c\u6587\u4e2d\u4eb2\u81ea\u7387\u519b\uff0c\u8d75\u4e8c\u5824\u3001\u9c7c\u5316\u9f99\u3001\u59ec\u91cd\u5929\u3001\u5f20\u4e0d\u7a7a\u56db\u5927\u9ad8\u624b\uff0c\u7adf\u90fd\u968f\u9a6c\u6587\u4e2d\u540c\u6765\u5c11 \u6797\uff0c\u4e5f\u7b97\u5f97\u4e0a\u662f\u7cbe\u9510\u5c3d\u51fa\u4e86\u3002 \u3000\u3000\u8fce\u4e0a\u738b\u5f64\uff0c\u9a6c\u6587\u4e2d\u5fae\u7b11\u9053\uff1a\u201c\u63a5\u5230\u5723\u65e8\uff0c\u6211\u5c31\u60f3\u5230\u53ef\u80fd\u662f\u4f60\u738b\u5144\u4ece\u4e2d\u5b89\u6392\uff0c\u770b\u6765\u662f\u6ca1\u6709 \u731c\u9519\uff0c\u8fd9\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u201c\u4e0a\u4e00\u6b21\uff0c\u9a6c\u5144\u6d3e\u4eba\u9001\u738b\u67d0\u56de\u4eac\uff0c\u5728\u5f00\u5c01\u57ce\u5916\u9047\u4e0a\u4e86\u4f17\u591a\u7684\u9ad8\u624b\u62e6\u622a\uff0c\u90a3\u4e3a\u9996\u7684\u4eba\u5c31\u662f \u5c11\u6797\u7684\u9ad8\u50e7\u3002\u201d \u3000\u3000\u201c\u771f\u7684\u5982\u6b64\u7b80\u5355\u4e48\uff1f\u201d \u3000\u3000\u738b\u5f64\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9a6c\u5144\uff0c\u4f55\u5fc5\u6df1\u7a76\uff0c\u6709\u4e9b\u4e8b\uff0c\u5fc3\u4e2d\u660e\u767d\u5c31\u597d\u3002\u201d \u3000\u3000\u201c\u4f60\u8981\u5982\u4f55\u653b\u6253\u5c11\u6797\u5bfa\u5462\uff1f\u2026\u2026\u201d\u9a6c\u6587\u4e2d\u9053\uff1a\u201c\u5144\u5f1f\u5175\u9a6c\u6628\u65e5\u8d76\u5230\uff0c\u7acb\u523b\u5206\u5f00\u56f4 \u4f4f\u2026\u2026\u201d \u3000\u3000\u201c\u5c11\u6797\u5bfa\u4e2d\u4eba\u53ef\u6709\u53cd\u5e94\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\uff01\u201d\u9a6c\u6587\u4e2d\u9053\uff1a\u201c\u4ed6\u4eec\u4f5c\u606f\u5982\u5e38\uff0c\u4f3c\u662f\u6ca1\u6709\u53d1\u751f\u4e8b\u60c5\u4e00\u6837\uff0c\u8fd9\u4efd\u6c89\u7740\uff0c\u5012\u662f\u4ee4\u4eba \u656c\u4f69\uff01\u201d \u3000\u3000\u76ee\u5149\u8f6c\u6ce8\u5230\u6c5f\u5343\u91cc\u7684\u8138\u4e0a\uff0c\u9053\uff1a\u201c\u600e\u4e48\uff1f\u6c5f\u5927\u4fa0\u4e5f\u6295\u8eab\u5165\u5185\u5bab\u4f8d\u536b\u4e2d\u4e86\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u6c5f\u67d0\u4f9d\u7136\u6545\u6211\uff0c\u4e1d\u6beb\u6ca1\u6709\u6539\u53d8\uff0c\u6c5f\u67d0\u8fd8\u6b20\u5927\u4eba\u4e00\u7b14\u9ec4\u91d1\uff0c\u6b64\u95f4\u4e8b\u4e86\uff0c\u5c31\u8bbe\u6cd5\u5949 \u8fd8\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u6709\u5e10\u7b97\u660e\uff0c\u5e0c\u671b\u6c5f\u5927\u4fa0\u5bf9\u6211\u6709\u4e2a\u4ea4\u4ee3\uff01\u201d \u3000\u3000\u201c\u4e24\u4f4d\u6069\u6028\uff0c\u5168\u51fa\u8bef\u4f1a\uff0c\u627e\u4e2a\u65f6\u95f4\uff0c\u738b\u67d0\u4eba\u4eba\u4f5c\u4e1c\uff0c\u66ff\u4e24\u4f4d\u8bf4\u5408\u8bf4\u5408\u2026\u2026\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u7531\u738b\u5144\u51fa\u9762\uff0c\u4ec0\u4e48\u4e8b\u90fd\u597d\u8bf4\u4e86\u3002\u73b0\u5728\uff0c\u5148\u529e\u6b63\u4e8b\u8981\u7d27\uff0c\u738b\u5144\u662f \u6b64\u884c\u7684\u9f99\u5934\uff0c\u60f3\u5fc5\u65e9\u5df2\u80f8\u6709\u6210\u7af9\u4e86\u3002\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u5148\u793c\u540e\u5175\uff0c\u9a6c\u5927\u4eba\u548c\u6c5f\u5144\u5404\u9009\u4e24\u4eba\uff0c\u968f\u884c\u5165\u5bfa\uff0c\u5148\u548c\u5c11\u6797\u5bfa\u4e2d\u65b9\u4e08\u8c08\u8c08\uff0c\u518d \u4f5c\u51b3\u5b9a\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u9009\u4e86\u9c7c\u5316\u9f99\u3001\u59ec\u91cd\u5929\u540c\u884c\uff0c\u6c5f\u5343\u91cc\u9009\u4e86\u9ed1\u50e7\u3001\u767d\u9053\u540c\u884c\uff0c\u738b\u5f64\u9009\u7684\u662f\u5c0f\u71d5\u5b50\u548c\u4e09 \u516c\u4e3b\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u4e09\u516c\u4e3b\u5df2\u6539\u7740\u7537\u88c5\uff0c\u9752\u886b\u4f69\u5251\uff0c\u8138\u4e0a\u4e5f\u7ecf\u8fc7\u4e86\u4e00\u756a\u6539\u626e\uff0c\u9a6c\u6587\u4e2d\u7adf\u7136\u4e5f\u6ca1\u6709\u8ba4\u51fa \u6765\u3002 \u3000\u3000\u9ed1\u7f57\u6c49\u4e03\u5de7\u50e7\u51fa\u8eab\u5c11\u6797\u5bfa\uff0c\u867d\u5df2\u88ab\u9010\u51fa\u95e8\u5899\uff0c\u4f46\u9999\u706b\u4e4b\u7f18\u4ecd\u5728\uff0c\u6b64\u756a\u56de\u5bfa\uff0c\u5374\u662f\u4ee5\u95ee\u7f6a \u7684\u8eab\u4efd\u91cd\u5165\u5c11\u6797\uff0c\u6487\u5f00\u4e86\u6b66\u6797\u6b63\u4e49\uff0c\u90a3\u5c31\u662f\u4ee5\u4e0b\u72af\u4e0a\u7684\u4e3e\u52a8\u3002 \u3000\u3000\u5c0f\u548c\u5c1a\u867d\u7136\u662f\u4e2a\u5f88\u6d12\u8131\u7684\u4eba\uff0c\u4f46\u5fc3\u4e2d\u4ecd\u7136\u6709\u4e9b\u5fd0\u5fd1\u4e0d\u5b89\uff0c\u5c31\u8fde\u8d70\u8d77\u8def\u6765\uff0c\u4e5f\u6709\u7740\u4e00\u80a1\u522b \u626d\u7684\u611f\u89c9\u3002 \u3000\u3000\u7528\u4e0d\u7740\u8bf7\u4eba\u901a\u62a5\uff0c\u4ed6\u4eec\u4e00\u884c\u4e5d\u4e2a\u4eba\u521a\u5165\u5bfa\u95e8\uff0c\u7acb\u523b\u6709\u4e5d\u4e2a\u5927\u548c\u5c1a\u8fce\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u4e5d\u4e2a\u4eba\u8fce\u63a5\u4e5d\u4e2a\u8bbf\u5ba2\uff0c\u90a3\u662f\u8bf4\u65e9\u5df2\u6709\u4e86\u51c6\u5907\uff0c\u5fc5\u8981\u65f6\uff0c\u963b\u62e6\u5165\u5bfa\u7684\u610f\u601d\u3002 \u3000\u3000\u9ed1\u7f57\u6c49\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5c45\u4e2d\u7684\u8001\u5e08\u7236\u662f\u8fbe\u6469\u9662\u7684\u4e3b\u6301\u5927\u89c9\u957f\u8001\uff0c\u5de6\u53f3\u4e24\u4f4d\u8001\u50e7\u4e5f\u90fd\u662f\u957f\u8001 \u8eab\u4efd\uff0c\u5c11\u6797\u5bfa\u6709\u5341\u4e8c\u4f4d\u957f\u8001\uff0c\u4e00\u5341\u5b50\u5c31\u51fa\u52a8\u4e86\u4e09\u4f4d\u3002\u201d \u3000\u3000\u201c\u5c0f\u548c\u5c1a\u2026\u2026\u201d\u5c0f\u71d5\u5b50\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u53e6\u5916\u516d\u4e2a\u548c\u5c1a\u53c8\u662f\u4ec0\u4e48\u8eab\u4efd\u5462\uff1f\u201d \u3000\u3000\u201c\u8fbe\u6469\u9662\u7684\u4e0a\u5ea7\u9ad8\u50e7\u2026\u2026\u201d\u9ed1\u7f57\u6c49\u9053\uff1a\u201c\u4ed6\u4eec\u867d\u7136\u4e0d\u662f\u957f\u8001\u8eab\u4efd\uff0c\u4f46\u6b63\u503c\u4e2d\u5e74\uff0c\u4f53\u529b\u65fa \u76db\uff0c\u6280\u827a\u6210\u719f\uff0c\u767b\u4e0a\u8fbe\u6469\u9662\u4e0a\u5ea7\u50e7\u4f4d\uff0c\u5c31\u662f\u4e13\u53f8\u8ffd\u6355\u9003\u5f92\u3001\u5fa1\u6297\u5f3a\u654c\u7684\u8d23\u4efb\uff0c\u4ed6\u4eec\u624d\u662f\u5c11\u6797 \u5bfa\u4e2d\u771f\u6b63\u7684\u4e3b\u529b\u3002\u201d \u3000\u3000\u201c\u8fd9\u6837\u7684\u4e0a\u5ea7\u9ad8\u50e7\u4e00\u5171\u6709\u591a\u5c11\u4eba\uff1f\u201d\u5c0f\u71d5\u5b50\u63d0\u51fa\u95ee\u9898\u3002 \u3000\u3000\u201c\u6ca1\u6709\u4e00\u5b9a\u7684\u540d\u989d\uff0c\u6240\u4ee5\u4e0d\u7528\u52c9\u5f3a\u51d1\u6570\uff0c\u4e0d\u5230\u4e00\u5b9a\u6b66\u529f\u6c34\u51c6\uff0c\u65e0\u6cd5\u5347\u767b\u4e0a\u5ea7\u3002\u201d\u9ed1\u7f57\u6c49 \u9053\uff1a\u201c\u8fd9\u4e00\u4ee3\u5c11\u6797\u5bfa\u9ad8\u624b\u8f88\u51fa\uff0c\u5c31\u5c0f\u548c\u5c1a\u6240\u77e5\uff0c\u8fbe\u6469\u9662\u4e0a\u5ea7\u50e7\u4f17\uff0c\u5df2\u6709\u4e09\u5341\u591a\u4eba\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u4e00\u65e6\u52a8\u624b\uff0c\u8fd9\u4e00\u6218\u5fc5\u7136\u662f\u60e8\u70c8\u7edd\u4f26\u4e86\uff1f\u201d\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u4ed6\u4eec\u90fd\u662f\u4f60\u7684\u5e08 \u5144\u3001\u5e08\u53d4\uff0c\u4f60\u4e0d\u80fd\u548c\u4ed6\u4eec\u52a8\u624b\u5427\uff1f\u201d \u3000\u3000\u201c\u4f46\u613f\u6211\u4f5b\u6709\u7075\uff0c\u6700\u597d\u53cc\u65b9\u4e0d\u8981\u52a8\u624b\uff0c\u53ea\u5728\u53e3\u5934\u4e0a\u4f5c\u4e00\u756a\u4e89\u8bba\u2026\u2026\u3000\u3000\u201d\u9ed1\u7f57\u6c49\u82e6\u7b11 \u9053\uff1a\u201c\u90a3\u624d\u662f\u4e0a\u4e0a\u5927\u5409\u5462\uff01\u201d \u3000\u3000\u8c08\u8bdd\u95f4\uff0c\u53cc\u65b9\u5df2\u5f7c\u6b64\u884c\u8fd1\u5230\u4e09\u5c3a\u5de6\u53f3\uff0c\u540c\u65f6\uff0c\u4e5f\u505c\u4e0b\u4e86\u811a\u6b65\u3002 \u3000\u3000\u5c45\u4e2d\u4e00\u50e7\uff0c\u5355\u638c\u7acb\u80f8\uff0c\u4f4e\u5ba3\u4e86\u4e00\u58f0\u4f5b\u53f7\uff0c\u9053\uff1a\u201c\u8001\u8872\u5927\u89c9\uff0c\u89c1\u8fc7\u5404\u4f4d\u65bd\u4e3b\u3002\u201d \u3000\u3000\u738b\u5f64\u4e00\u62b1\u62f3\uff0c\u9053\uff1a\u201c\u5927\u5e08\u53ef\u8fd8\u8bb0\u5f97\u6614\u5e74\u7684\u65e7\u53cb\u738b\u5f64\u4e48\uff1f\u201d \u3000\u3000\u201c\u8bb0\u5f97\u5012\u662f\u8bb0\u5f97\uff0c\u4e0d\u8fc7\uff0c\u542c\u8bf4\u738b\u65bd\u4e3b\u5df2\u51fa\u4efb\u5185\u5bab\u4e2d\u7edf\u9886\u4e4b\u804c\uff0c\u6743\u503e\u4e00\u65f6\u2026\u2026\u201d\u5927\u89c9\u5927\u5e08 \u9053\uff1a\u201c\u4eca\u65e5\u5175\u56f4\u5c11\u6797\u5bfa\uff0c\u4e0d\u77e5\u6709\u4f55\u89c1\u6559\uff1f\u201d \u3000\u3000\u6c5f\u5e72\u91cc\u9053\uff1a\u201c\u8349\u6c11\u6c5f\u5343\u91cc\uff0c\u5927\u5e08\u5982\u80af\u65b9\u4fbf\uff0c\u8bf7\u4e0a\u590d\u8d35\u5bfa\u638c\u95e8\u4e00\u58f0\uff0c\u5c31\u8bf4\u6cb3\u5357\u5de1\u629a\u9a6c\u5927 \u4eba\u3001\u5185\u5bab\u7edf\u9886\u738b\u5927\u4eba\u548c\u5c71\u91ce\u8349\u6c11\u6c5f\u5343\u91cc\u6c42\u89c1\u4e00\u9762\uff0c\u6709\u4e8b\u76f8\u5546\u3002\u201d \u3000\u3000\u201c\u8bf8\u4f4d\u631f\u91cd\u5175\u800c\u6765\uff0c\u5206\u660e\u662f\u903c\u4eba\u5c31\u8303\uff0c\u8fd9\u2018\u6c42\u89c1\u2019\u4e8c\u5b57\u5c31\u7528\u5f97\u592a\u5ba2\u6c14\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u7ba1\u5927\u5e08\u7684\u60f3\u6cd5\u5982\u4f55\uff0c\u4f46\u5c40\u52bf\u5df2\u7ecf\u975e\u5e38\u660e\u663e\uff0c\u548c\u3001\u6218\u4e4b\u5c40\u6050\u5df2\u975e\u5927\u5e08\u80fd\u4f5c\u51b3\u5b9a\u4e86\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u9053\uff1a\u201c\u901a\u62a5\u8d35\u5bfa\u638c\u95e8\uff0c\u7531\u4ed6\u4f5c\u4e2a\u51b3\u5b9a\uff0c\u5927\u5e08\u5c31\u7528\u4e0d\u7740\u62c5\u4ed8\u592a\u591a\u7684\u8d23\u4efb\u4e86\u3002\u201d \u3000\u3000\u5927\u5e08\u6c89\u541f\u4e00\u9635\uff0c\u9053\uff1a\u201c\u8bf4\u7684\u6709\u7406\uff0c\u8bf8\u4f4d\u8bf7\u5728\u6b64\u7a0d\u5019\uff0c\u8d2b\u50e7\u4eb2\u5f80\u901a\u62a5\uff0c\u4e0d\u8fc7\uff0c\u8d2b\u50e7\u672a\u56de\u6765 \u524d\uff0c\u8fd8\u671b\u8bf8\u4f4d\u5c0a\u91cd\u5c11\u6797\u5bfa\u89c4\uff0c\u4e0d\u5f97\u5f3a\u884c\u5165\u5bfa\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u6211\u4eec\u606d\u5019\u5927\u5e08\u2026\u2026\u201d\u738b\u5f64\u9053\uff1a\u201c\u8d35\u5bfa\u65b9\u4e08\u548c\u738b\u67d0\u8c0a\u5c5e\u8001\u53cb\uff0c\u5f7c\u6b64\u4e5f\u66fe\u60a3\u96be\u76f8\u6276\uff0c \u4ec0\u4e48\u4e8b\u90fd\u597d\u5546\u91cf\uff0c\u8bf7\u5927\u5e08\u8f6c\u544a\u738b\u67d0\u4eba\u7684\u4e00\u7247\u8bda\u5fc3\u3002\u201d \u3000\u3000\u5927\u89c9\u70b9\u70b9\u5934\uff0c\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u8db3\u8db3\u7b49\u5019\u4e86\u534a\u4e2a\u65f6\u8fb0\u4e4b\u4e45\uff0c\u8fd8\u4e0d\u89c1\u5927\u89c9\u5f52\u6765\uff0c\u5c0f\u71d5\u5b50\u5df2\u5fcd\u8010\u4e0d\u4f4f\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8001\u548c\u5c1a\u662f \u8bda\u5fc3\u8981\u6211\u4eec\u4e86\uff0c\u5c0f\u9053\u58eb\uff0c\u54b1\u4eec\u5148\u51b2\u8fc7\u53bb\uff01\u201d \u3000\u3000\u201c\u4e0d\u8981\uff01\u201d\u9ed1\u7f57\u6c49\u9053\uff1a\u201c\u5927\u4eba\u90fd\u80fd\u5fcd\u8010\u4e0b\u53bb\uff0c\u6211\u4eec\u591a\u7b49\u4e00\u4f1a\u513f\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\u5462\uff1f\u4f60\u51b2\u8fc7 \u53bb\uff0c\u4e00\u52a8\u4e0a\u624b\uff0c\u90a3\u5c31\u6709\u7406\u8bf4\u4e0d\u6e05\u4e86\uff0c\u4f60\u770b\u3002 \u3000\u3000\u5927\u89c9\u957f\u8001\u56de\u6765\u4e86\u2026\u2026\u201c \u3000\u3000\u62ac\u5934\u770b\u53bb\uff0c\u679c\u89c1\u5927\u89c9\u5feb\u6b65\u884c\u6765\uff0c\u9053\uff1a\u201c\u672c\u5bfa\u65b9\u4e08\u613f\u610f\u63a5\u89c1\u738b\u65bd\u4e3b\u2026\u2026\u201d \u3000\u3000\u201c\u6c5f\u67d0\u4eba\u53ef\u5426\u540c\u5f80\u5462\uff1f\u201d \u3000\u3000\u201c\u8001\u8872\u6597\u80c6\u4f5c\u4e3b\uff0c\u589e\u52a0\u4e00\u4f4d\uff0c\u4e0d\u80fd\u518d\u591a\u4e86\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u9053\uff1a\u201c\u6211\u4eec\u5728\u90a3\u91cc\u7b49\u5019\u6d88\u606f\u5462\uff1f\u201d \u3000\u3000\u201c\u8bf7\u5165\u8fce\u5ba2\u7cbe\u820d\uff0c\u7531\u8001\u8872\u5949\u966a\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u7a81\u7136\u8d70\u4e0a\u6b65\uff0c\u9053\uff1a\u201c\u5927\u5e08\uff0c\u52a0\u4e24\u4e2a\u5c0f\u5b69\u5b50\u6ca1\u6709\u5173\u7cfb\u5427\uff1b\u6211\u4eec\u662f\u738b\u5927\u4eba\u7684\u957f \u968f\u2026\u2026\u201d \u3000\u3000\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u4eba\u5374\u7275\u4f4f\u4e09\u516c\u4e3b\u7684\u624b\uff0c\u786c\u628a\u4e09\u516c\u4e3b\u62c9\u524d\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u7eaf\u6d01\uff0c\u5168\u65e0\u7537\u5973\u4e4b\u522b\uff0c\u4f55\u51b5\uff0c\u4e09\u516c\u4e3b\u73b0\u5728\u8eab\u7740\u7537\u88c5\uff0c\u201c\u8fd9\u4e2a\u2026\u2026\u201d\u5927\u89c9\u4e24\u9053\u76ee\u5149 \u5728\u5c0f\u71d5\u5b50\u8138\u4e0a\u6253\u91cf\u4e86\u4e00\u9635\uff0c\u786e\u5b9a\u53ea\u662f\u4e24\u4e2a\u5f62\u5982\u4e66\u7ae5\u7684\u5c0f\u5b69\u5b50\uff0c\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u597d\u5427\uff01\u4f46\u4f60\u4eec \u53ea\u80fd\u5b88\u5728\u7985\u623f\u95e8\u5916\uff0c\u4e0d\u5f97\u5165\u5185\u3002\u201d \u3000\u3000\u201c\u884c\uff01\u4e00\u5207\u7167\u5927\u5e08\u5429\u5490\u5c31\u662f\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u3001\u4e09\u516c\u4e3b\u83b7\u5141\u968f\u884c\uff0c\u5374\u4f7f\u5f97\u6c5f\u5e72\u91cc\u3001\u738b\u5f64\u80c6\u6c14\u5927\u58ee\u3002 \u3000\u3000\u5927\u89c9\u62db\u62db\u624b\uff0c\u4e00\u4e2a\u5c0f\u6c99\u5f25\u884c\u4e86\u8fc7\u6765\uff0c\u5e26\u7740\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u884c\u5165\u4e00\u5ea7\u9ec4\u5899\u56f4\u7ed5\u7684\u9759\u9662\u4e4b\u4e2d\uff0c \u6cbf\u9014\u4e0a\u50e7\u4fa3\u904d\u5e03\uff0c\u663e\u7136\u5c11\u6797\u5bfa\u5df2\u4f5c\u4e86\u8fce\u654c\u7684\u51c6\u5907\u3002 \u3000\u3000\u7eff\u7af9\u73af\u62b1\u7740\u4e00\u5ea7\u5f88\u5927\u7684\u7985\u623f\uff0c\u95e8\u53e3\u5904\uff0c\u5206\u7ad9\u7740\u56db\u4e2a\u4e2d\u5e74\u50e7\u4fa3\uff0c\u62e6\u963b\u4f4f\u51e0\u4eba\u53bb\u8def\uff0c\u76f4\u5f85\u5c0f \u6c99\u5f25\u5165\u5185\u7980\u62a5\u4e4b\u540e\uff0c\u624d\u5e26\u738b\u5f64\u3001\u6c5f\u5343\u91cc\u8fdb\u5165\u4e86\u7985\u623f\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u672c\u60f3\u6765\u4e00\u4e2a\u6df7\u6c34\u6478\u9c7c\uff0c\u4f46\u5374\u88ab\u5b88\u95e8\u7684\u50e7\u4fa3\u62e6\u4f4f\u3002 \u3000\u3000\u7985\u5ba4\u4e2d\u6a80\u9999\u8885\u8885\uff0c\u6563\u53d1\u51fa\u4e00\u80a1\u6c81\u4eba\u5fc3\u813e\u7684\u6e05\u9999\u3002 \u3000\u3000\u5c11\u6797\u5bfa\u638c\u95e8\u65b9\u4e08\u5927\u667a\u7985\u5e08\uff0c\u8eab\u62ab\u4e00\u4ef6\u5927\u7ea2\u8272\u7684\u8888\u88df\uff0c\u76d8\u819d\u5750\u5728\u4e00\u5f20\u7985\u5e8a\u4e4b\u4e0a\uff0c\u5e8a\u524d\u6446\u4e86 \u56db\u5f20\u592a\u5e08\u6905\u3002 \u3000\u3000\u8fd9\u4e00\u5207\uff0c\u4f3c\u662f\u65e9\u7ecf\u5e03\u7f6e\u3002 \u3000\u3000\u5927\u667a\u6325\u6325\u624b\uff0c\u793a\u610f\u5c0f\u6c99\u5f25\u9000\u51fa\u53bb\uff0c\u800c\u4e14\uff0c\u4e5f\u793a\u610f\u4ed6\u5e26\u4e0a\u623f\u95e8\u3002 \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u5927\u5e08\uff0c\u6545\u53cb\u65e7\u8bc6\uff0c\u600e\u4e48\u53d8\u5f97\u5982\u6b64\u751f\u758f\u8d77\u6765\u4e86\uff1f\u201d \u3000\u3000\u5927\u667a\u9053\uff1a\u201c\u5929\u6709\u4e0d\u6d4b\u98ce\u4e91\uff0c\u4eba\u95f4\u6709\u65e0\u5e38\u7684\u53d8\u5316\uff0c\u54b1\u4eec\u662f\u654c\u662f\u53cb\uff0c\u771f\u53eb\u4eba\u96be\u4ee5\u5206\u8fa8\u5440\uff01\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u738b\u67d0\u5c31\u542c\u4e0d\u61c2\u4e86\u2026\u2026\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u5927\u5e08\uff0c\u8fc7\u53bb\u7684\u4ea4\u60c5\u4e0d\u8c08\uff0c\u6c5f\u67d0\u4eba\u8981\u8bf7\u6559\u7684\u662f\uff0c\u5f00\u5c01\u90ca\u5916\u62e6\u622a\u738b\u7edf\u9886\u7684\u5c11\u6797 \u548c\u5c1a\uff0c\u662f\u4e0d\u662f\u5927\u5e08\u6d3e\u51fa\u7684\u4eba\uff1f\u201d \u3000\u3000\u5927\u667a\u7985\u5e08\u70b9\u70b9\u5934\u3002 \u3000\u3000\u5f53\u9762\u627f\u8ba4\uff0c\u5012\u662f\u51fa\u4e4e\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u7684\u610f\u5916\uff0c\u5728\u4ed6\u4e24\u4eba\u7684\u63a8\u65ad\u4e4b\u4e2d\uff0c\u4ed6\u4f1a\u4e00\u53e3\u5426\u8ba4\uff0c\u7edd\u4e0d \u8ba4\u5e10\uff0c\u90a3\u5c31\u6709\u5f97\u626f\u4e86\u3002 \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u5462\uff1f\u738b\u67d0\u4eba\u5bf9\u5f85\u5c11\u6797\u5bfa\u6709\u529f\u65e0\u8fc7\uff0c\u6709\u6069\u65e0\u6028\uff0c\u4f55\u51b5\uff0c\u5927\u5e08\u8fd8\u662f\u6211\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u201c\u738b\u5927\u4eba\uff0c\u4f60\u9664\u4e86\u53d1\u89c9\u5c11\u6797\u5bfa\u4e2d\u7684\u50e7\u4eba\u4e4b\u5916\uff0c\u8fd8\u53d1\u89c9\u4e86\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u5f88\u91cd\u8981\u4e48\uff1f\u201d\u738b\u5f64\u9053\uff1a\u201c\u7eb5\u6709\u5176\u4ed6\u95e8\u6d3e\u4e2d\u4eba\uff0c\u53c8\u548c\u5c11\u6797\u5bfa\u4f55\u5173\uff1f\u201d \u3000\u3000\u201c\u97ed\u5e38\u91cd\u8981\uff0c\u4f60\u5fc5\u987b\u636e\u5b9e\u8bf4\u51fa\u6765\uff0c\u8fd9\u4ef6\u4e8b\u5173\u7cfb\u592a\u5927\u4e86\u3002\u201d\u5927\u667a\u7985\u5e08\u7684\u795e\u60c5\u4e2d\u5145\u6ee1\u671f\u5f85\u3002 \u3000\u3000\u4f3c\u4e4e\u738b\u5f64\u7684\u56de\u7b54\uff0c\u80fd\u5f71\u54cd\u5230\u4ed6\u4e00\u4e2a\u5f88\u91cd\u8981\u7684\u51b3\u5b9a\u3002 \u3000\u3000\u201c\u53d1\u89c9\u4e86\u5f88\u591a\u4eba\uff0c\u9664\u4e86\u8d35\u5bfa\u4e4b\u5916\uff0c\u8fd8\u6709\u6b66\u5f53\u3001\u4e10\u5e2e\u53ca\u65b0\u8fd1\u5d1b\u8d77\u6c5f\u6e56\u7684\u795e\u5200\u5802\u2026\u2026\u201d \u3000\u3000\u5927\u667a\u7985\u5e08\u76ee\u4e2d\u795e\u5149\u95ea\u52a8\uff0c\u6253\u65ad\u4e86\u738b\u5f64\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u4e0d\u5bf9\uff0c\u5e94\u8be5\u6709\u4e94\u5927\u95e8\u6d3e\u624d\u5bf9\uff0c\u725b\u3001 \u9a6c\u3001\u732a\u3001\u7f8a\u3001\u9a74\uff0c\u4e94\u4e2a\u4eba\u5171\u4e94\u5927\u95e8\u6d3e\u2026\u2026\u3000\u3000\u201d \u3000\u3000\u8bdd\u58f0\u987f\u4e86\u987f\uff0c\u53c8\u9053\uff1a\u201c\u65e2\u662f\u6709\u5982\u6b64\u4f17\u591a\u7684\u95e8\u6d3e\u88ad\u51fb\u4f60\u738b\u5927\u4eba\uff0c\u4e0d\u77e5\u738b\u5927\u4eba\u4e3a\u4ec0\u4e48\u5148\u627e\u4e0a \u5c11\u6797\u5bfa\u6765\uff0c\u53ef\u662f\u89c9\u5f97\u5c11\u6797\u5bfa\u6700\u597d\u6b3a\u4fae\u4e48\uff1f\u201d \u3000\u3000\u201c\u5509\uff01\u6211\u4eec\u591a\u5e74\u4ea4\u5f80\uff0c\u60f3\u4e0d\u5230\u5927\u5e08\u8fd8\u4f1a\u6709\u5982\u6b64\u7684\u8bef\u4f1a\u2026\u2026\u201d\u738b\u5f64\u9053\uff1a\u201c\u6211\u5148\u6765\u5c11\u6797\uff0c\u662f \u56e0\u4e3a\u738b\u67d0\u81ea\u4fe1\u548c\u5927\u5e08\u4ea4\u60c5\u6df1\u539a\uff0c\u5fc5\u53ef\u5f97\u5230\u5927\u5e08\u7684\u5e2e\u52a9\u3002\u725b\u3001\u9a6c\u3001\u7f8a\u3001\u732a\u3001\u9a74\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u201c\u4e0d\u8bba\u4f60\u5fc3\u91cc\u771f\u6b63\u7684\u60f3\u6cd5\u5982\u4f55\uff0c\u8fd9\u756a\u8bdd\uff0c\u5012\u9887\u4f7f\u8001\u8872\u5b89\u6170\uff0c\u738b\u5927\u4eba\u65e2\u7136\u8ba4\u5b9a\u548c\u8001\u8896\u4ea4\u60c5 \u6df1\u539a\uff0c\u8001\u8872\u5012\u60f3\u8bf7\u95ee\u4e00\u53e5\u2026\u2026\u201d\u5927\u667a\u65b9\u4e08\u7b54\u975e\u6240\u95ee\u7684\u8bf4\u3002 \u3000\u3000\u201c\u5927\u5e08\u8bf7\u95ee\uff0c\u738b\u67d0\u77e5\u65e0\u4e0d\u8a00\u3002\u201d \u3000\u3000\u201c\u8981\u5b9e\u8bdd\u5b9e\u8bf4\uff0c\u738b\u5927\u4eba\uff0c\u9700\u77e5\u4f60\u73b0\u5728\u5982\u6709\u4e00\u53e5\u865a\u8a00\u2026\u2026\u201d\u5927\u667a\u65b9\u4e08\u795e\u8272\u8083\u7136\u5730\u8bf4\uff1a\u201c\u53ef \u80fd\u803d\u8bef\u4e86\u5927\u5c40\u3002\u201d \u3000\u3000\u201c\u662f\uff0c\u738b\u67d0\u5b9e\u8bdd\u5b9e\u8bf4\u3002\u201d \u3000\u3000\u201c\u738b\u5927\u4eba\u662f\u60f3\u62a5\u590d\u62e6\u51fb\u4f60\u7684\u4ec7\u6068\u5462\uff1f\u8fd8\u662f\u60f3\u6f84\u6e05\u6c5f\u6e56\u5927\u5c40\uff0c\u4e3a\u6b66\u6797\u540c\u9053\u5c3d\u4efd\u5fc3\u529b\uff1f\u201d \u3000\u3000\u201c\u738b\u67d0\u4eba\u5bf9\u53d7\u5230\u88ad\u51fb\u4e4b\u4e8b\uff0c\u65e9\u5df2\u4e0d\u653e\u5fc3\u4e0a\uff0c\u4f46\u613f\u80fd\u4e3a\u6b66\u6797\u540c\u9053\u5c3d\u4efd\u5fc3\u529b\uff0c\u738b\u67d0\u4eba\u6b7b\u800c\u65e0 \u61be\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u6709\u4f60\u8fd9\u53e5\u8bdd\uff0c\u8001\u8872\u5c31\u5148\u5165\u5730\u72f1\u2026\u2026\u201d\u5927\u667a\u65b9\u4e08\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u4e00\u5931\u8db3\u6210\u5343\u53e4\u6068\uff0c\u8001 \u8872\u6570\u5341\u5e74\u6e05\u4fee\u5374\u88ab\u6bc1\u4e8e\u4e00\u671d\uff0c\u6c5f\u65bd\u4e3b\u53ef\u5426\u6682\u9000\u5ba4\u5916\uff0c\u8001\u8872\u8981\u548c\u738b\u5927\u4eba\u5355\u72ec\u5bc6\u8c08\u4e00\u756a\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u7ad9\u8d77\u8eab\u5b50\uff0c\u4e00\u62b1\u62f3\uff0c\u9053\uff1a\u201c\u6c5f\u67d0\u544a\u9000\u3002\u201d\u8f6c\u8eab\u884c\u51fa\u7985\u623f\u3002 \u3000\u3000\u738b\u5f64\u542c\u5b8c\u4e86\u5927\u667a\u65b9\u4e08\u4e00\u756a\u8bf4\u8bdd\uff0c\u6574\u4e2a\u4eba\u53d8\u5f97\u5446\u4f4f\u4e86\uff0c\u826f\u4e45\uff0c\u624d\u8d77\u8eab\u4e00\u4e2a\u957f\u63d6\uff0c\u9053\uff1a\u201c\u5927 \u5e08\uff0c\u836f\u7269\u8ff7\u9b42\u5c82\u662f\u4eba\u529b\u80fd\u6297\u62d2\u7684\uff0c\u5927\u5e08\u5343\u4e07\u4e0d\u53ef\u81ea\u6c42\u89e3\u8131\uff0c\u6b64\u4e8b\u5173\u7cfb\u7740\u6574\u4e2a\u4e2d\u539f\u6b66\u6797\u5b58\u4ea1\u7edd \u7eed\uff0c\u5927\u5e08\u4e00\u5b9a\u8981\u5fcd\u8fb1\u8d1f\u91cd\uff0c\u63ed\u7a7f\u9634\u8c0b\uff0c\u738b\u67d0\u4eba\u5168\u529b\u4fdd\u536b\u4ed6\u4eec\uff0c\u4e0d\u8ba9\u8fd9\u4ef6\u4e8b\u66dd\u4f20\u4e8e\u6c5f\u6e56\u4e4b \u4e0a\u3002\u201d \u3000\u3000\u201c\u8001\u8872\u65e2\u7136\u8bf4\u51fa\u6765\u4e86\uff0c\u65e9\u5df2\u4e0d\u8ba1\u6bc1\u8a89\uff0c\u53ea\u662f\u5f80\u4e8b\u53ef\u6016\uff0c\u8001\u8896\u62c5\u5fc3\u4f1a\u91cd\u8e48\u8986\u8f99\uff0c\u6211\u4eec\u7684\u884c \u52a8\uff0c\u4eba\u624b\u4e0d\u80fd\u4f17\u591a\uff0c\u591a\u5219\u6d88\u606f\u5fc5\u6cc4\uff0c\u5c11\u53c8\u6050\u5165\u4ed6\u4eec\u57cb\u4f0f\uff0c\u90a3\u5c31\u5f97\u4e0d\u507f\u5931\u4e86\uff0c\u8fd9\u4ef6\u4e8b\u9887\u96be\u4e24 \u5168\u2026\u2026\u201d \u3000\u3000\u738b\u5f64\u6c89\u541f\u4e00\u9635\uff0c\u9053\uff1a\u201c\u738b\u67d0\u56f4\u56f0\u5c11\u6797\u5bfa\u7684\u6d88\u606f\u6050\u5df2\u4f20\u5165\u6c5f\u6e56\uff0c\u5982\u65e0\u540e\u7eed\u884c\u52a8\uff0c\u6050\u5c06\u5f15\u4eba \u731c\u7591\u2026\u2026\u201d \u3000\u3000\u201c\u738b\u5927\u4eba\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u54b1\u4eec\u53cc\u65b9\u4e0d\u514d\u5047\u610f\u4e00\u6218\uff0c\u6253\u6597\u867d\u53ef\u771f\u5b9e\uff0c\u4f46\u8981\u907f\u514d\u4f24\u4eba\uff0c\u5927\u5e08\u5728\u5bfa\u4e2d\u5b89\u6392\uff0c\u738b\u67d0\u4e5f\u53bb \u5bfa\u5916\u5e03\u7f6e\uff0c\u7136\u540e\uff0c\u5927\u5e08\u548c\u738b\u67d0\u5404\u9009\u4e03\u540d\u7cbe\u9510\u9ad8\u624b\uff0c\u501f\u591c\u8272\u63a9\u62a4\u6f5c\u884c\uff0c\u8d76\u5f80\u8d74\u7ea6\u4e4b\u5904\uff0c\u63ed\u5f00\u771f \u8c61\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u5341\u4f59\u4eba\u7684\u884c\u52a8\uff0c\u4e5f\u96be\u7792\u4eba\u8033\u76ee\u2026\u2026\u201d\u5927\u667a\u65b9\u4e08\u8bf4\uff1a\u201c\u6b64\u4e3e\u53ea\u80fd\u6210\u529f\uff0c\u4e0d\u8bb8\u5931 \u8d25\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u5927\u5e08\u653e\u5fc3\uff0c\u6c5f\u5343\u91cc\u7684\u89c5\u8e2a\u9690\u8ff9\u4e4b\u672f\uff0c\u5929\u4e0b\u65e0\u53cc\uff0c\u54b1\u4eec\u53ef\u4ee5\u5206\u6279\u800c\u884c\u3002\u201d \u3000\u3000\u201c\u4e00\u8a00\u4e3a\u5b9a\uff0c\u4eca\u591c\u4e09\u66f4\u4e0a\u8def\u3002\u201d\u5927\u667a\u65b9\u4e08\u8bf4\uff1a\u201c\u738b\u5927\u4eba\u8bf7\u53bb\u5e03\u7f6e\uff0c\u8981\u614e\u9009\u4eba\u624b\uff0c\u52a1\u6c42\u529f \u529b\u5353\u7edd\uff0c\u4ee5\u4e00\u5f53\u5341\u7684\u9ad8\u624b\uff0c\u65b9\u53ef\u80dc\u4efb\u3002\u201d \u3000\u3000\u201c\u738b\u67d0\u77e5\u9053\uff0c\u5927\u5e08\u653e\u5fc3\u3002\u201d \u3000\u3000\u738b\u5f64\u628a\u4e8b\u60c5\u544a\u8bc9\u4e86\u6c5f\u5343\u91cc\u3002 \u3000\u3000\u8fd9\u4f4d\u6c5f\u6e56\u5947\u4eba\u4e5f\u542c\u5f97\u5fc3\u60ca\u4e0d\u5df2\uff0c\u5f53\u7136\uff0c\u738b\u5f64\u4e5f\u9690\u85cf\u4e86\u4e00\u4e9b\u9690\u5bc6\uff0c\u5c3d\u91cf\u4fdd\u62a4\u5927\u667a\u65b9\u4e08\u7684\u58f0 \u8a89\u4e0d\u53d7\u4f24\u5bb3\u3002 \u3000\u3000\u8fd9\u4ef6\u4e8b\uff0c\u53ea\u6709\u544a\u8bc9\u9a6c\u6587\u4e2d\uff0c\u56e0\u4e3a\u8981\u9a6c\u6587\u4e2d\u6b63\u5f0f\u548c\u9b54\u6559\u4e2d\u4eba\u706b\u62da\uff0c\u4e5f\u8bb8\u4e0d\u662f\u5584\u7b56\uff0c\u6240\u4ee5\uff0c \u9a6c\u6587\u4e2d\u8d1f\u8d23\u653b\u6253\u5c11\u6797\u5bfa\uff0c\u4ed6\u7387\u9886\u4e86\u672c\u90e8\u4eba\u9a6c\u548c\u738b\u5f64\u7559\u4e0b\u7684\u5927\u90e8\u5206\u4f8d\u536b\u548c\u5382\u536b\u3002 \u3000\u3000\u738b\u5f64\u9009\u4e86\u516d\u4e2a\u4eba\uff0c\u662f\u6c5f\u5343\u91cc\u3001\u5c0f\u71d5\u5b50\u3001\u9ed1\u50e7\u3001\u767d\u9053\u3001\u738b\u91cd\u5c71\u3001\u4e09\u516c\u4e3b\u52a0\u4e0a\u4ed6\u81ea\u5df1\uff0c\u6b63\u597d \u662f\u4e03\u4e2a\u4eba\u3002 \u3000\u3000\u5927\u667a\u65b9\u4e08\u5341\u5206\u8c28\u614e\uff0c\u53ea\u9009\u4e86\u5927\u65b9\u3001\u5927\u6b63\u4e24\u4f4d\u957f\u8001\u968f\u884c\uff0c\u8fde\u4ed6\u53ea\u6709\u4e09\u4e2a\u4eba\u3002 \u3000\u3000\u738b\u5f64\u5fc3\u4e2d\u660e\u767d\uff0c\u5927\u667a\u65b9\u4e08\u4e0d\u613f\u968f\u884c\u50e7\u4fa3\u8fc7\u591a\uff0c\u867d\u662f\u4e3a\u4e86\u5bb9\u6613\u9690\u5bc6\u884c\u8e2a\uff0c\u4f46\u6700\u91cd\u8981\u7684\u8fd8\u662f \u6015\u8fd9\u4ef6\u9690\u5bc6\u6cc4\u6f0f\u51fa\u53bb\uff0c\u4e00\u4e2a\u4eba\u5728\u80fd\u591f\u4fdd\u62a4\u81ea\u5df1\u7684\u65f6\u5019\uff0c\u5c31\u5c3d\u529b\u4fdd\u62a4\u5df2\u5f97\u5230\u7684\u6743\u52bf\uff0c\u5927\u667a\u65b9\u4e08 \u4ea6\u4e0d\u4f8b\u5916\u3002 \u3000\u3000\u4f46\u738b\u5f64\u4e5f\u660e\u767d\uff0c\u5927\u65b9\u3001\u5927\u6b63\u4e24\u4f4d\u957f\u8001\uff0c\u5728\u5c11\u6797\u5bfa\u5341\u4e8c\u957f\u8001\u4e2d\uff0c\u662f\u6b66\u529f\u6700\u9ad8\u7684\u4e24\u4f4d\u3002 \u3000\u3000\u539f\u6765\uff0c\u4e24\u4f4d\u65e0\u610f\u4e8e\u6743\u52bf\uff0c\u4e5f\u4e0d\u613f\u51fa\u4efb\u5404\u9662\u4e3b\u6301\uff0c\u628a\u5168\u90e8\u7cbe\u795e\u90fd\u6295\u6ce8\u5728\u7cbe\u7814\u6b66\u529f\u4e4b\u4e0a\uff0c\u5c11 \u6797\u5bfa\u4e2d\u4e03\u5341\u4e8c\u79cd\u7edd\u6280\uff0c\u4e24\u4eba\u5404\u517c\u4e60\u4e03\u79cd\u4ee5\u4e0a\uff0c\u662f\u5c11\u6797\u5bfa\u4e2d\u6280\u827a\u6700\u5e7f\u535a\u7684\u9ad8\u50e7\u3002 \u3000\u3000\u9ed1\u7f57\u6c49\u81ea\u544a\u594b\u52c7\uff0c\u626e\u4f5c\u4e86\u5927\u667a\u548c\u5c1a\u7684\u968f\u884c\u6c99\u5f25\u3002 \u3000\u3000\u56db\u4e2a\u548c\u5c1a\u7ed3\u4f34\u800c\u884c\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u548c\u738b\u5f64\u7b49\u516d\u4eba\u5374\u6613\u5bb9\u6539\u626e\uff0c\u8fdc\u8fdc\u76f8\u968f\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\u7684\u9080\u7ea6\uff0c\u4ecd\u7136\u5145\u6ee1\u7740\u795e\u79d8\uff0c\u5927\u667a\u65b9\u4e08\u8d76\u5230\u4e86\u7ea6\u5b9a\u7684\u5730\u70b9\uff0c\u8944\u9633\u9686\u4e2d\u5927\u5ba2\u6808\u65f6\uff0c\u7acb \u523b\u88ab\u8bf7\u4e0a\u4e86\u4e00\u8f86\u505c\u5728\u5df7\u53e3\u7684\u9a6c\u8f66\u4e0a\uff0c\u5927\u65b9\u3001\u5927\u6b63\u4e24\u4f4d\u9ad8\u50e7\u4e0d\u51c6\u968f\u884c\uff0c\u4e24\u4f4d\u8001\u548c\u5c1a\u6b66\u529f\u867d\u9ad8\uff0c \u6c5f\u6e56\u4e0a\u7684\u9605\u5386\u5374\u662f\u4e0d\u591a\uff0c\u53ea\u597d\u4e56\u4e56\u7684\u5728\u5ba2\u6808\u4e2d\u7b49\u5019\uff0c\u4f46\u9ed1\u7f57\u6c49\u5374\u636e\u7406\u529b\u4e89\uff0c\u8981\u968f\u884c\u7167\u987e\u5e08 \u7236\uff0c\u603b\u7b97\u4e5f\u88ab\u9001\u4e0a\u4e86\u9a6c\u8f66\u3002 \u5367\u9f99\u751f\u300a\u71d5\u5b50\u4f20\u5947\u300b \u7b2c\u5341\u4e5d\u56de\u3000\u9999\u8273\u5bb4\u4f1a \u3000\u3000\u8fd9\u8f86\u7bf7\u8f66\uff0c\u56db\u5468\u5bc6\u5c01\uff0c\u65e0\u6cd5\u770b\u5230\u5916\u9762\u7684\u666f\u7269\uff0c\u66f4\u4f55\u51b5\uff0c\u4e24\u4eba\u53c8\u88ab\u4e00\u6761\u9ed1\u8272\u7684\u773c\u7f69\uff0c\u8499\u4e0a \u4e86\u773c\u775b\u3002 \u3000\u3000\u8f66\u5185\uff0c\u8fd8\u6709\u4eba\u540c\u5750\u76d1\u89c6\u3002 \u3000\u3000\u5c0f\u548c\u5c1a\u53ea\u89c9\u65c1\u4fa7\u6709\u4eba\uff0c\u5374\u4e0d\u77e5\u662f\u4ec0\u4e48\u6837\u7684\u4eba\u7269\uff0c\u4f46\u4ed6\u4e3a\u4eba\u7cbe\u7075\uff0c\u9690\u9690\u95fb\u5230\u4e86\u6de1\u6de1\u7684\u8102\u7c89 \u9999\u6c14\uff0c\u5728\u8f66\u4e2d\u76d1\u89c6\u7684\u4f3c\u662f\u4e00\u4e2a\u5973\u4eba\u3002 \u3000\u3000\u7bf7\u8f66\u5728\u5feb\u901f\u7684\u98de\u9a70\u4e2d\u3002 \u3000\u3000\u7136\u800c\uff0c\u5c0f\u548c\u5c1a\u5374\u9690\u9690\u7ea6\u7ea6\u5730\u542c\u5230\u5927\u667a\u65b9\u4e08\u4e00\u79cd\u5f02\u5e38\u7684\u5598\u606f\u58f0\uff0c\u548c\u4e00\u79cd\u5973\u5b50\u7684\u5a07\u5598\u58f0\uff0c\u6df7 \u5728\u4e00\u8d77\u3002 \u3000\u3000\u9ed1\u7f57\u6c49\u4ed7\u51ed\u7740\u4e30\u5bcc\u7684\u6c5f\u6e56\u7ecf\u9a8c\uff0c\u4fa7\u52a8\u8eab\u4f53\uff0c\u5728\u4e24\u79cd\u58f0\u97f3\u8f83\u4e3a\u5f3a\u70c8\u65f6\uff0c\u6697\u8fd0\u529f\u529b\uff0c\u8f7b\u8f7b\u62e8 \u52a8\u4e86\u4e00\u4e0b\u9ed1\u8272\u7684\u773c\u7f69\u3002 \u3000\u3000\u4e56\u4e56\uff0c\u4e00\u526f\u60ca\u5fc3\u52a8\u9b44\u7684\u753b\u9762\uff0c\u7acb\u523b\u5448\u73b0\u5728\u773c\u524d\u3002 \u3000\u3000\u4e00\u4e2a\u957f\u53d1\u62ab\u5782\u7684\u5c11\u5973\uff0c\u628a\u5a07\u8eaf\u5b8c\u5168\u504e\u5728\u5927\u667a\u65b9\u4e08\u7684\u6000\u4e2d\uff0c\u6a31\u53e3\u7d27\u543b\u5728\u8001\u548c\u5c1a\u7684\u5634\u4e0a\uff0c\u4e01 \u9999\u820c\u8f6c\uff0c\u4e0d\u505c\u5730\u5438\u5141\u3001\u6311\u9017\uff0c\u4e00\u53cc\u7389\u624b\u4e5f\u5728\u8001\u548c\u5c1a\u7684\u8eab\u4e0a\u8f6c\u6765\u6478\u53bb\u3002 \u3000\u3000\u5927\u667a\u65b9\u4e08\u4f3c\u662f\u6781\u529b\u5730\u5fcd\u8010\u7740\uff0c\u4e0d\u6562\u58f0\u5f20\u3002 \u3000\u3000\u4f46\u5c0f\u548c\u5c1a\u5374\u770b\u51fa\u4e86\u4ed6\u5fcd\u8010\u5f97\u975e\u5e38\u8f9b\u82e6\uff0c\u4e0d\u7981\u6697\u6697\u53f9\u606f\u9053\uff1a\u5927\u667a\u65b9\u4e08\u4e00\u751f\u6e05\u4fee\uff0c\u5f88\u5c11\u5728\u6c5f \u6e56\u4e0a\u8d70\u52a8\uff0c\u5982\u4f55\u80fd\u627f\u53d7\u5982\u6b64\u6311\u9017\uff0c\u9b54\u6559\u4e2d\u4eba\u7684\u624b\u6bb5\u679c\u7136\u662f\u4e0b\u6d41\u53ef\u6016\u3002 \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u6697\u4e2d\u62c9\u6b63\u4e86\u773c\u7f69\uff0c\u8f7b\u8f7b\u54b3\u55fd\u4e00\u58f0\uff0c\u7a81\u7136\u4e00\u4f38\u817f\uff0c\u6b63\u597d\u8e22\u4e2d\u4e86\u90a3\u5c11\u5973\u7684\u53cc\u817f\u4e2d \u95f4\u3002 \u3000\u3000\u8fd9\u4e00\u811a\u8e22\u5f97\u4e0d\u8f7b\u4e5f\u4e0d\u91cd\uff0c\u6070\u5230\u597d\u5904\uff0c\u5c11\u5973\u611f\u89c9\u5230\u6709\u70b9\u75bc\u75db\uff0c\u4e5f\u6709\u7740\u88ab\u6311\u9017\u7684\u611f\u89c9\u3002 \u3000\u3000\u5979\u6b63\u5728\u65bd\u5c55\u6d51\u8eab\u7684\u89e3\u6570\uff0c\u8feb\u4f7f\u8001\u548c\u5c1a\u5c48\u670d\uff0c\u8fd9\u4e00\u6765\uff0c\u53ea\u597d\u505c\u4e86\u4e0b\u6765\uff0c\u4f4e\u58f0\u9a82\u9053\uff1a\u201c\u5c0f\u548c \u5c1a\uff0c\u778e\u4e86\u72d7\u773c\u4e0d\u6210\uff0c\u4f38\u817f\u4e5f\u8be5\u77a7\u77a7\u5730\u65b9\u554a\uff01\u201d \u3000\u3000\u201c\u6211\u770b\u4e0d\u89c1\u54ea\uff01\u53cc\u773c\u4e00\u7247\u4e4c\u9ed1\uff0c\u54a6\uff0c\u5947\u602a\u4e86\uff0c\u600e\u4e48\u4f1a\u6709\u4e2a\u5973\u4eba\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u6709\u4ec0\u4e48\u597d\u5947\u602a\u7684\uff0c\u4f60\u5988\uff0c\u4f60\u59b9\u59b9\uff0c\u4e0d\u90fd\u4e5f\u662f\u5973\u4eba\u4e48\uff1f\u201d \u3000\u3000\u9ed1\u7f57\u6c49\u5fc3\u4e2d\u5fd6\u9053\uff1a\u597d\u5201\u86ee\u7684\u4e2b\u5934\uff0c\u53ea\u542c\u5230\u4f60\u8fd9\u51e0\u53e5\u8bdd\u5c31\u77e5\u4e0d\u662f\u597d\u76f8\u4e0e\u7684\uff0c\u53ea\u53ef\u60dc\u65b9\u4e08\u5c11 \u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\uff0c\u4e0d\u77e5\u80fd\u4e0d\u80fd\u8fa8\u522b\u51fa\u8fd9\u5973\u4eba\u7684\u5201\u6076\u2026\u2026\u3002 \u3000\u3000\u8ddf\u7740\u5728\u9a6c\u8f66\u540e\u9762\u7684\u6c5f\u5343\u91cc\uff0c\u4e5f\u4e3a\u8fd9\u5201\u6076\u7684\u8bbe\u8ba1\uff0c\u6697\u6697\u5f00\u9a82\u3002 \u3000\u3000\u539f\u6765\uff0c\u7bf7\u8f66\u5feb\u901f\u5730\u5954\u9a70\uff0c\u4e00\u76f4\u5728\u8944\u9633\u57ce\u4e2d\u6253\u8f6c\uff0c\u8db3\u8db3\u8dd1\u4e86\u4e00\u4e2a\u591a\u65f6\u8fb0\uff0c\u8dd1\u5f97\u56fd\u5339\u62c9\u8f66\u7684 \u5065\u9a6c\u5168\u8eab\u662f\u6c57\uff0c\u7bf7\u8f66\u624d\u9a70\u5165\u4e86\u4e00\u5ea7\u5927\u5b85\u9662\u4e2d\uff0c\u800c\u4e14\uff0c\u7acb\u523b\u5173\u4e0a\u4e86\u5927\u95e8\u3002 \u3000\u3000\u9ad8\u5899\u6df1\u9662\uff0c\u5927\u95e8\u9ad8\u5927\u7684\u50cf\u57ce\u95e8\u4e00\u6837\uff0c\u95e8\u4e00\u5173\u4e0a\uff0c\u7acb\u523b\u5185\u5916\u9694\u7edd\u3002 \u3000\u3000\u7bf7\u8f66\u505c\u4e0b\uff0c\u5927\u667a\u65b9\u4e08\u548c\u9ed1\u7f57\u6c49\u624d\u88ab\u53d6\u4e0b\u773c\u7f69\uff0c\u63a5\u4e0b\u7bf7\u8f66\u3002 \u3000\u3000\u505c\u8eab\u5904\uff0c\u662f\u4e00\u5ea7\u5f62\u5982\u4ed3\u5e93\u7684\u5927\u5385\u623f\u4e2d\uff0c\u4ecd\u7136\u65e0\u6cd5\u770b\u5230\u5916\u9762\u666f\u7269\uff0c\u4e5f\u65e0\u6cd5\u786e\u5b9a\u8fd9\u662f\u4e2a\u4ec0\u4e48 \u6240\u5728\uff1f\u90a3\u957f\u53d1\u5c11\u5973\u4e0d\u77e5\u4f55\u65f6\u5df2\u53bb\uff0c\u5e26\u8def\u7684\u662f\u4e00\u4e2a\u5168\u8eab\u9ed1\u8863\u7684\u5e74\u8f7b\u4eba\uff0c\u4f46\u4e00\u5f20\u8138\u5374\u767d\u5f97\u53ef\u6015\u3002 \u3000\u3000\u81ea\u4ece\u9ed1\u7f57\u6c49\u8ffd\u968f\u6c5f\u5343\u91cc\u4e4b\u540e\uff0c\u53c8\u589e\u591a\u4e86\u4e0d\u5c11\u7684\u9605\u5386\uff0c\u4e00\u773c\u4e4b\u4e0b\uff0c\u7acb\u523b\u770b\u51fa\u90a3\u662f\u4e00\u5f20\u5047 \u8138\uff0c\u5728\u8138\u4e0a\u6d82\u4e86\u4e00\u5c42\u767d\u77f3\u818f\u3002 \u3000\u3000\u884c\u81f3\u5927\u5385\u4e00\u89d2\uff0c\u7adf\u7136\u6298\u5411\u5730\u4e0b\u884c\u53bb\u3002 \u3000\u3000\u6574\u4e2a\u7684\u884c\u52a8\uff0c\u5168\u90fd\u5728\u8fd9\u5ea7\u5927\u5385\u4e2d\uff0c\u81ea\u7136\u65e0\u6cd5\u548c\u6c5f\u5343\u91cc\u7b49\u53d6\u5f97\u4efb\u4f55\u7684\u8054\u7edc\u4e86\u3002 \u3000\u3000\u5730\u7a96\u5165\u53e3\u5904\uff0c\u7ad9\u7740\u4e00\u4e2a\u8eab\u7740\u767d\u8863\u7684\u5c11\u5973\uff0c\u624b\u4e2d\u62ff\u7740\u4e00\u4e2a\u725b\u5934\u5934\u5957\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u5927\u5e08\uff0c\u8bf7 \u66f4\u6362\u8863\u670d\u3001\u6234\u4e0a\u9762\u5177\u3002\u201d \u3000\u3000\u5927\u667a\u65b9\u4e08\u4f3c\u662f\u5df2\u4e60\u60ef\u8fd9\u79cd\u6298\u817e\uff0c\u7a7f\u4e0a\u4e86\u4e00\u4ef6\u5bbd\u5927\u7684\u9ed1\u888d\uff0c\u6234\u4e0a\u4e86\u725b\u5934\u9762\u5177\uff0c\u8ddf\u5728\u90a3\u9ed1\u8863 \u4eba\u8eab\u540e\u800c\u53bb\u3002 \u3000\u3000\u9ed1\u7f57\u6c49\u770b\u90a3\u5c11\u5973\u4e0d\u7406\u4f1a\u4ed6\uff0c\u4e5f\u5c31\u88c5\u8ff7\u7cca\u7684\u8ddf\u7740\u884c\u53bb\u3002 \u3000\u3000\u4f46\u5374\u88ab\u767d\u8863\u5c11\u5973\u4f38\u624b\u62e6\u4f4f\uff0c\u9053\uff1a\u201c\u4f60\u53e6\u6709\u53bb\u5904\uff0c\u8ddf\u6211\u6765\u5427\uff01\u201d \u3000\u3000\u201c\u4e0d\u884c\uff0c\u6211\u8981\u8ddf\u7740\u5e08\u7236\u7167\u987e\u4ed6\u2026\u2026\u201d \u3000\u3000\u201c\u6709\u6bd4\u4f60\u597d\u4e0a\u51e0\u767e\u500d\u7684\u4eba\u7167\u987e\uff0c\u7528\u4e0d\u7740\u4f60\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\uff01\u6211\u4e00\u5b9a\u8981\u8ddf\u7740\u5e08\u7236\u2026\u2026\u201d\u9ed1\u7f57\u6c49\u65e0\u6cd5\u4e86\u89e3\u5927\u667a\u65b9\u5927\u7684\u5fc3\u6001\u5982\u4f55\uff1f\u56e0\u4e3a\uff0c\u4ed6\u6234\u4e0a\u725b \u5934\u9762\u5177\u4e4b\u540e\uff0c\u5c31\u672a\u518d\u56de\u5934\u671b\u8fc7\u9ed1\u7f57\u6c49\u4e00\u773c\uff0c\u6cbf\u7740\u5bbd\u655e\u7684\u77f3\u7ea7\uff0c\u5411\u4e0b\u884c\u53bb\u3002 \u3000\u3000\u201c\u4f60\u53bb\u4e86\u4e4b\u540e\u53ea\u4f1a\u5927\u715e\u98ce\u666f\u7684\u2026\u2026\u201d\u767d\u8863\u5c11\u5973\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u611f\u89c9\u5230\u4e48\uff1f\u4f60\u5e08\u7236\u5e76\u4e0d\u6b22\u8fce \u4f60\u8ddf\u4e0b\u53bb\u3002\u201d \u3000\u3000\u5927\u667a\u65b9\u4e08\u5df2\u7ecf\u8f6c\u8fc7\u4e00\u4e2a\u5f2f\uff0c\u8eab\u5f71\u6d88\u5931\uff0c\u8fd9\u5ea7\u901a\u5f80\u5730\u4e0b\u7684\u77f3\u7ea7\uff0c\u4f3c\u662f\u66f2\u8f6c\u6709\u81f4\uff0c\u5982\u6b64\u7684\u5efa \u7b51\uff0c\u90a3\u4e48\u5730\u4e0b\u5ba4\u7684\u89c4\u6a21\u4e00\u5b9a\u4e0d\u4f1a\u5c0f\u4e86\u3002 \u3000\u3000\u9ed1\u7f57\u6c49\u5fc3\u4e2d\u5fd6\u9053\uff1a\u8fd9\u662f\u4ec0\u4e48\u9b3c\u5730\u65b9\uff0c\u6709\u6b64\u96c4\u4f1f\u7684\u5730\u4e0b\u5efa\u7b51\uff0c\u8ba1\u7b97\u7bf7\u8f66\u7684\u901f\u5ea6\u884c\u7a0b\uff0c\u8fd9\u5730 \u65b9\u8ddd\u79bb\u8944\u9633\uff0c\u81f3\u5c11\u4e5f\u6709\u4e09\u56db\u5341\u91cc\u4e86\u3002 \u3000\u3000\u4f46\u89c9\u4e00\u53ea\u6ed1\u6e9c\u6e9c\u3001\u8f6f\u7ef5\u7ef5\u7684\u7389\u624b\uff0c\u63e1\u4f4f\u4e86\u624b\u8155\uff0c\u9053\uff1a\u201c\u5c0f\u548c\u5c1a\uff0c\u8ddf\u6211\u8d70\u5427\uff01\u4f60\u5e08\u7236\u4e0d\u4f1a \u6709\u4e8b\u7684\uff0c\u4ed6\u53c8\u4e0d\u662f\u7b2c\u4e00\u6b21\u53c2\u52a0\u8fd9\u79cd\u5927\u4f1a\uff0c\u8ddf\u7740\u5927\u59d0\u59d0\uff0c\u6211\u8981\u4f60\u4e5f\u4eab\u53d7\u4e00\u4e0b\u4f60\u4ece\u672a\u7ecf\u5386\u8fc7\u7684\u4eba \u751f\uff0c\u4fdd\u8bc1\u4f60\u518d\u89c1\u7740\u5927\u59d0\u59d0\u65f6\uff0c\u8fde\u5988\u90fd\u4f1a\u558a\u53eb\u51fa\u53e3\u3002\u201d \u3000\u3000\u9ed1\u7f57\u6c49\u5fc3\u4e2d\u5fd6\u9053\uff1a\u6211\u51fa\u624b\u5236\u670d\u4e86\u8fd9\u4e2b\u5934\uff0c\u4e00\u4e2a\u4eba\u8ddf\u8fdb\u53bb\u4e5f\u96be\u6709\u4f5c\u7528\uff0c\u5fc5\u987b\u548c\u5c0f\u71d5\u5b50\u4ed6\u4eec \u8054\u7edc\u4e0a\u624d\u884c\u2026\u2026 \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u6253\u6d88\u4e86\u5f3a\u884c\u8fdb\u5165\u7684\u51b3\u5b9a\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u8981\u5e26\u6211\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u4ed6\u6700\u5927\u7684\u5fc3\u613f\uff0c\u5c31\u662f\u79bb\u5f00\u8fd9\u5ea7\u5385\u5802\uff0c\u80fd\u89c1\u5230\u9633\u5149\u5927\u5730\uff0c\u8fd9\u6837\u624d\u80fd\u60f3\u529e\u6cd5\u548c\u5c0f\u71d5\u5b50\u4eec\u53d6\u5f97 \u8054\u7edc\u3002 \u3000\u3000\u201c\u5c31\u5728\u8fd9\u5ea7\u5927\u5385\u5802\u4e2d\uff0c\u53ea\u4e0d\u8fc7\uff0c\u548c\u4f60\u5e08\u7236\u7684\u5730\u65b9\u4e0d\u540c\u7f62\u4e86\uff0c\u8d70\u5427\uff01\u6625\u5bb5\u4e00\u523b\u503c\u5343\u91d1\u2014 \u2026\u3002\u201d\u767d\u8863\u5c11\u5973\u7684\u5a07\u8eaf\u7adf\u7136\u6251\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u522b\u518d\u6d6a\u8d39\u65f6\u95f4\u4e86\uff1b\u201d \u3000\u3000\u5979\u5c55\u5f00\u4e86\u5b8c\u5168\u7684\u6311\u9017\uff0c\u6a31\u5507\u3001\u5a07\u8eaf\u5168\u9001\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u9ed1\u7f57\u6c49\u6697\u9053\uff1a\u8fd9\u4e9b\u5c0f\u5996\u5973\u53ef\u80fd\u90fd\u7ec3\u8fc7\u5996\u672f\u3001\u5a9a\u529f\uff0c\u83ab\u8981\u7740\u4e86\u5979\u7684\u9053\u513f\u3002\u53f3\u624b\u6697\u62ac\uff0c\u4e00\u4e0b \u5b50\u70b9\u4e2d\u4e86\u90a3\u5c11\u5973\u7684\u6655\u7a74\u3002 \u3000\u3000\u4ed6\u52a8\u4f5c\u8001\u7ec3\uff0c\u6276\u4f4f\u767d\u8863\u5c11\u5973\u8eab\u5b50\uff0c\u672a\u8ba9\u5979\u5012\u6454\u4e0b\u53bb\uff0c\u79fb\u653e\u5230\u4e00\u58c1\u89d2\u5904\uff0c\u770b\u4e00\u4fa7\u8fd8\u6709\u5f88\u591a \u4ef6\u9ed1\u8272\u957f\u888d\uff0c\u968f\u624b\u53d6\u8fc7\u4e00\u4ef6\uff0c\u63a9\u53bb\u5c11\u5973\u8eab\u4e0a\u7684\u767d\u8863\u3002 \u3000\u3000\u9ed1\u7f57\u6c49\u89c9\u5f97\u59a5\u5f53\u4e86\uff0c\u8fd9\u624d\u98de\u8eab\u800c\u8d77\uff0c\u63a8\u5f00\u4e00\u6247\u7a97\u5b50\uff0c\u98de\u8dc3\u800c\u53bb\u3002 \u3000\u3000\u4ed6\u7ecf\u9a8c\u4e30\u5bcc\uff0c\u4e00\u843d\u5730\u7acb\u523b\u9690\u8d77\u8eab\u5b50\uff0c\u518d\u6162\u6162\u6253\u91cf\u56db\u5468\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5df2\u662f\u65e5\u843d\u897f\u5c71\u7684\u65f6\u5206\u3002 \u3000\u3000\u5929\u8272\uff0c\u6162\u6162\u7684\u660f\u6697\u4e0b\u6765\u3002 \u3000\u3000\u5c0f\u548c\u5c1a\u6b63\u60f3\u53d1\u51fa\u5578\u58f0\uff0c\u4ee5\u53ec\u5524\u540c\u4f34\uff0c\u8eab\u9645\u7a81\u7136\u54cd\u8d77\u4e86\u4e00\u9635\u9e1f\u9e23\u4e4b\u58f0\u3002 \u3000\u3000\u8fd9\u6b63\u662f\u548c\u6c5f\u5343\u91cc\u7b49\u8054\u7edc\u7684\u65b9\u6cd5\u4e4b\u4e00\u3002 \u3000\u3000\u5c0f\u548c\u5c1a\u5fc3\u4e2d\u5927\u559c\uff0c\u95ea\u8eab\u800c\u51fa\uff0c\u4e3e\u53cc\u624b\u56db\u4e0b\u62db\u52a8\u3002 \u3000\u3000\u4f46\u89c1\u4eba\u5f71\u7ffb\u98de\uff0c\u5c0f\u71d5\u5b50\u3001\u4e09\u516c\u4e3b\uff0c\u5148\u540e\u98de\u843d\u8eab\u4fa7\uff0c\u7d27\u63a5\u7740\u6c5f\u5343\u91cc\u3001\u738b\u5f64\u3001\u4e5f\u2014\u4e00\u98de\u8eab\u800c \u4e0b\u3002 \u3000\u3000\u539f\u6765\uff0c\u4ed6\u4eec\u8ffd\u5165\u4e86\u5ead\u9662\uff0c\u5374\u627e\u4e0d\u5230\u7bf7\u8f66\u4f55\u5728\uff1f\u201c\u5c0f\u9053\u58eb\u548c\u738b\u91cd\u5c71\u5462\uff1f\u201d\u5c0f\u548c\u5c1a\u4e0d\u89c1\u4e24 \u4eba\uff0c\u5927\u4e3a\u6302\u5ff5\u3002 \u3000\u3000\u201c\u4ed6\u53bb\u8bf7\u5927\u65b9\u3001\u5927\u6b63\u4e24\u4f4d\u957f\u8001\u2026\u2026\u201d \u3000\u3000\u5c0f\u548c\u5c1a\u622a\u4f4f\u4e86\u738b\u5f64\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u8fd9\u91cc\u8ddd\u8944\u9633\u5c11\u8bf4\u4e5f\u6709\u4e09\u5341\u91cc\u4ee5\u4e0a\uff0c\u6765\u53bb\u4e00\u8d9f\uff0c\u53ea\u6015\u8981\u597d \u4e00\u9635\u5b50\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\uff01\u7bf7\u8f66\u4e00\u76f4\u672a\u51fa\u8944\u9633\u57ce\u2026\u2026\u201d\u6c5f\u5e72\u91cc\u9053\uff1a\u201c\u5927\u667a\u65b9\u4e08\u5462\uff1f\u201d \u3000\u3000\u201c\u8fdb\u5165\u5730\u4e0b\u5ba4\u4e2d\u4e86\uff0c\u6211\u4e3a\u51e0\u4f4d\u5e26\u8def\u2026\u2026\u201d\u5c0f\u548c\u5c1a\u4e00\u9762\u8fdb\u5165\u5385\u623f\uff0c\u4e00\u9762\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u8fd9\u91cc \u6ca1\u6709\u5b88\u671b\u7684\u4eba\u4e48\uff1f\u201d \u3000\u3000\u201c\u90fd\u88ab\u5c0f\u71d5\u5b50\u7ed9\u653e\u5012\u4e86\u2026\u2026\u201d\u4e09\u516c\u4e3b\u9053\uff1a\u201c\u770b\u8d77\u6765\uff0c\u4ed6\u4eec\u5728\u8fd9\u91cc\u7684\u4eba\u624b\u4e0d\u591a\uff0c\u6211\u4eec\u5e94\u4ed8 \u5f97\u4e86\uff0c\u4e0d\u7528\u7b49\u4e24\u4f4d\u957f\u8005\u8d76\u6765\u4e86\u3002\u201d \u3000\u3000\u9ed1\u7f57\u6c49\u4e00\u9762\u8d70\uff0c\u4e00\u9762\u4f4e\u58f0\u7684\u8ff0\u8bf4\u7740\u7ecf\u8fc7\uff0c\u5728\u7ecf\u8fc7\u5165\u53e3\u5904\u65f6\uff0c\u968f\u624b\u53d6\u8fc7\u4e86\u4e00\u4ef6\u9ed1\u888d\u7a7f\u5728\u8eab \u4e0a\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u4e5f\u8ddf\u7740\u5b66\u6837\uff0c\u5927\u5bb6\u90fd\u53d6\u4e86\u4e00\u4ef6\u9ed1\u888d\u7a7f\u4e0a\uff0c\u53ea\u53ef\u60dc\u5df2\u6ca1\u6709\u9762\u5177\u53ef\u6234\u3002 \u3000\u3000\u4e24\u4e2a\u6298\u8f6c\uff0c\u624d\u8fdb\u5165\u5730\u4e0b\u5927\u5385\u3002 \u3000\u3000\u4f46\u89c1\u706f\u706b\u8f89\u714c\uff0c\u7167\u5f97\u5927\u5385\u4e2d\u4e00\u7247\u660e\u4eae\u3002 \u3000\u3000\u5927\u5385\u56db\u5468\u5206\u7ad9\u7740\u4e00\u4e9b\u8138\u8272\u5947\u767d\u7684\u9ed1\u8863\u4eba\uff0c\u4f46\u8b66\u6212\u5374\u4e00\u70b9\u4e5f\u4e0d\u201c\u5bc6\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u7b49\u4e00\u884c\u4eba\u9c7c\u8d2f\u800c\u5165\uff0c\u90a3\u4e9b\u9ed1\u8863\u4eba\u5e76\u6ca1\u6709\u8b66\u89c9\u3002 \u3000\u3000\u4e5f\u8bb8\u5927\u5bb6\u90fd\u7a7f\u7740\u9ed1\u8272\u7684\u957f\u888d\uff0c\u5728\u611f\u89c9\u4e0a\u662f\u81ea\u5df1\u4eba\u4e86\u3002 \u3000\u3000\u8fd9\u5ea7\u5730\u4e0b\u655e\u5385\u867d\u7136\u5f88\u5927\uff0c\u4f46\u5ba2\u4eba\u5374\u4e0d\u591a\uff0c\u53ea\u89c1\u4e94\u4e2a\u5206\u5e26\u7740\u725b\u3001\u9a6c\u3001\u7f8a\u3001\u732a\u3001\u9a74\u4e94\u79cd\u5934\u5957 \u9762\u5177\u7684\u4eba\uff0c\u5206\u5750\u5728\u4e94\u5f20\u6728\u684c\u524d\u9762\uff0c\u6bcf\u4eba\u7684\u8eab\u4fa7\u8fd8\u5750\u7740\u4e00\u4f4d\u7edd\u7f8e\u7684\u5c11\u5973\u3002 \u3000\u3000\u9ed1\u7f57\u6c49\u4f4e\u58f0\u9053\uff1a\u201c\u5c0f\u71d5\u5b50\uff0c\u90a3\u6234\u7740\u725b\u5934\u9762\u5177\u7684\u4eba\uff0c\u5c31\u662f\u5c11\u6797\u65b9\u4e08\u3002\u201d \u3000\u3000\u738b\u5f64\u5fc3\u4e2d\u4e5f\u9661\u7136\u660e\u767d\uff0c\u5927\u667a\u7985\u5e08\u53e3\u4e2d\u7684\u725b\u3001\u9a6c\u3001\u7f8a\u3001\u732a\u3001\u9a74\u662f\u600e\u4e48\u4e00\u56de\u4e8b\u4e86\u3002 \u3000\u3000\u8fd9\u4e94\u79cd\u9762\u5177\u4ee3\u8868\u4e86\u4e94\u4e2a\u95e8\u6d3e\uff0c\u6b66\u5f53\u3001\u4e10\u5e2e\u3001\u795e\u5200\u5802\u548c\u5c11\u6797\u4e4b\u5916\uff0c\u53e6\u4e00\u4e2a\u4eba\u4ee3\u8868\u4e86\u90a3\u4e2a\u95e8 \u6d3e\uff0c\u738b\u5f64\u5374\u65e0\u6cd5\u60f3\u5f97\u8d77\u6765\u2026\u2026 \u3000\u3000\u8fd9\u65f6\uff0c\u4e00\u4e2a\u8eab\u6750\u9ad8\u5927\u3001\u8eab\u7740\u9526\u8863\u7684\u4eba\uff0c\u7f13\u7f13\u5730\u884c\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4f46\u542c\u4ed6\u9ad8\u58f0\u5730\u8bf4\u9053\uff1a\u201c\u6c5f\u6e56\u60c5\u52bf\uff0c\u5df2\u7136\u5927\u6709\u53d8\u52a8\uff0c\u8bf8\u4f4d\u4e8e\u6b64\u4e00\u5bb5\u4e4b\u6b22\u540e\uff0c\u5c31\u8981\u7acb\u523b\u5404\u56de \u5730\u76d8\uff0c\u96c6\u4e2d\u95e8\u4e0b\u6700\u597d\u7684\u4eba\u624b\uff0c\u6bcf\u4f4d\u81f3\u5c11\u8981\u5e26\u4e00\u767e\u540d\u9ad8\u624b\uff0c\u4e24\u4e2a\u6708\u540e\uff0c\u5728\u5317\u4eac\u96c6\u4e2d\uff0c\u8bf8\u4f4d\u8981\u4eb2 \u81ea\u9886\u961f\u51fa\u9a6c\uff0c\u8bf8\u4f4d\u7684\u5fc3\u7231\u59ec\u59be\u4e5f\u4f1a\u5728\u5317\u4eac\u7b49\u5019\uff0c\u8bf8\u4f4d\u56de\u53bb\u65f6\uff0c\u5404\u8d50\u9526\u56ca\u4e00\u4e2a\uff0c\u91cc\u9762\u6709\u8be6\u7ec6\u7684 \u884c\u52a8\u8bf4\u660e\u3002\u8bf8\u4f4d\u8bf7\u5c3d\u4e00\u676f\u7f8e\u9152\uff0c\u5404\u81ea\u6b22\u5a31\u53bb\u5427\uff01\u201d \u3000\u3000\u4f46\u89c1\u4e94\u4eba\u8eab\u4fa7\u7684\u7f8e\u5973\uff0c\u53d6\u8fc7\u9152\u676f\uff0c\u659f\u6ee1\u4e86\u78a7\u7eff\u989c\u8272\u7684\u7f8e\u9152\uff0c\u5de6\u624b\u8f7b\u6380\u4e94\u4eba\u5934\u7f69\u9762\u5177\uff0c\u53f3 \u624b\u6267\u676f\u9001\u5411\u5404\u4eba\u7684\u5507\u524d\u3002 \u3000\u3000\u201c\u6162\u7740\u2026\u2026\u201d \u3000\u3000\u8bf4\u8bdd\u7684\u4eba\u662f\u5927\u667a\u7985\u5e08\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u7ad9\u8d77\u8eab\u5b50\uff0c\u53d6\u4e0b\u725b\u5934\u9762\u7f69\uff0c\u9732\u51fa\u4e86\u672c\u76f8\uff0c\u9053\uff1a\u201c\u8bf8\u4f4d\uff0c\u8fd9\u676f\u9152\u4e0d\u80fd\u559d\uff0c\u4e00\u559d\u4e0b \u53bb\uff0c\u5c31\u9677\u5165\u4e86\u4e07\u52ab\u4e0d\u590d\u4e4b\u5883\u2026\u2026\u201d \u3000\u3000\u9526\u8863\u4eba\u5389\u58f0\u559d\u9053\uff1a\u201c\u76c8\u76c8\uff0c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u76c8\u76c8\u53f3\u624b\u5f03\u53bb\u9152\u676f\uff0c\u70b9\u5411\u5927\u667a\u524d\u80f8\u3002 \u3000\u3000\u5927\u667a\u6325\u638c\u4e00\u6321\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5c0f\u5996\u5973\uff0c\u8001\u8896\u5df2\u7ecf\u63ed\u53d1\u4e86\u4f60\u4eec\u7684\u9634\u8c0b\u2026\u2026\u201d \u3000\u3000\u76c8\u76c8\u9053\uff1a\u201c\u96be\u9053\uff0c\u6211\u4eec\u8fc7\u53bb\u7684\u6069\u6069\u7231\u7231\u3001\u60c5\u610f\u7ef5\u7ef5\uff0c\u4f60\u90fd\u80fd\u5fd8\u4e86\u4e48\uff1f\u201d \u3000\u3000\u53e3\u4e2d\u8bf4\u5f97\u751c\u871c\u871c\uff0c\u4eba\u5374\u6251\u8eab\u800c\u4e0a\uff0c\u638c\u6307\u5e76\u51fa\uff0c\u653b\u5411\u5927\u548c\u5c1a\u7684\u8981\u5bb3\u3002 \u3000\u3000\u8fd9\u771f\u662f\u53e3\u662f\u5fc3\u975e\u7684\u4e3e\u52a8\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u89c1\u72b6\uff0c\u6025\u6025\u5730\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u4e00\u8d77\u4e0a\uff0c\u4e00\u4eba\u4e00\u4e2a\uff0c\u5148\u5c06\u4ed6\u4eec\u6551\u4e0b\u6765\u8131\u4e86\u9669\uff0c\u518d\u8bf4\u522b \u7684\u3002\u201d \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u53e6\u5916\u56db\u5973\u5df2\u628a\u624b\u4e2d\u4e4b\u9152\uff0c\u4fbf\u5411\u51e0\u4eba\u704c\u53bb\u3002 \u3000\u3000\u9a6c\u9762\u4eba\u548c\u7f8a\u9762\u4eba\u51fa\u624b\u5c01\u6321\uff0c\u95ea\u907f\u5f00\u53bb\uff0c\u4f46\u90a3\u732a\u9762\u4eba\u548c\u9a74\u9762\u4eba\uff0c\u7a0d\u4e00\u8fdf\u7591\uff0c\u5df2\u88ab\u8eab\u4fa7\u4e8c\u5973 \u628a\u836f\u9152\u704c\u5165\u4e86\u53e3\u4e2d\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5c0f\u71d5\u5b50\u7b49\u4e5f\u8131\u53bb\u9ed1\u888d\uff0c\u66b4\u8d77\u53d1\u52a8\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u6251\u5411\u732a\u9762\u4eba\uff0c\u4e09\u516c\u4e3b\u6251\u5411\u9a74\u9762\u4eba\uff0c\u4e24\u4eba\u52a8\u4f5c\u5947\u5feb\uff0c\u5251\u5982\u95ea\u7535\uff0c\u4e24\u4e2a\u5728\u704c\u836f\u9152\u7684\u5c11 \u5973\u5446\u4e86\u4e00\u5446\uff0c\u5df2\u88ab\u90a3\u75be\u5982\u95ea\u7535\u7684\u5251\u5149\uff0c\u65a9\u6bd9\u5251\u4e0b\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u968f\u540e\u8dc3\u5230\uff0c\u51fa\u624b\u5982\u98ce\uff0c\u70b9\u4e86\u732a\u9762\u4eba\u548c\u9a74\u9762\u4eba\u7684\u7a74\u9053\u3002 \u3000\u3000\u4ed6\u89c1\u95fb\u867d\u7136\u5e7f\u535a\uff0c\u4f46\u4e5f\u65e0\u6cd5\u9884\u6d4b\u4e24\u4eba\u5728\u559d\u4e0b\u836f\u9152\u4e4b\u540e\uff0c\u4f1a\u6709\u4ec0\u4e48\u53cd\u5e94\uff1f\u5148\u70b9\u4e86\u4e24\u4eba\u7a74 \u9053\uff0c\u4ee5\u514d\u4e24\u4eba\u65e0\u6cd5\u63a7\u5236\uff0c\u5927\u51fa\u4e11\u6001\u3002 \u3000\u3000\u738b\u5f64\u6251\u5411\u90a3\u9526\u888d\u4eba\u3002 \u3000\u3000\u6b64\u4eba\u4e43\u662f\u8fd9\u6b21\u805a\u4f1a\u7684\u9996\u8111\u4eba\u7269\uff0c\u6b66\u529f\u6700\u5f3a\uff0c\u738b\u5f64\u4eba\u8fd8\u672a\u5230\uff0c\u4ed6\u7684\u5200\u5df2\u7ecf\u51fa\u9798\u4e86\uff0c\u800c\u4e14\u8fce \u9762\u65a9\u6765\u3002 \u3000\u3000\u738b\u5f64\u7528\u8155\u4e0a\u94a2\u73af\u6325\u624b\u4e00\u6321\uff0c\u91d1\u94c1\u4ea4\u9e23\u4e2d\u9707\u52a8\u4e86\u673a\u7c27\uff0c\u73af\u4e2d\u7684\u94a2\u9488\u98de\u51fa\uff0c\u5c04\u5411\u90a3\u9526\u888d\u4eba\u7684 \u7709\u5fc3\uff0c\u738b\u5f64\u518d\u5f53\u80f8\u4e00\u638c\uff0c\u7acb\u523b\u4e86\u5e10\u3002 \u3000\u3000\u8fd9\u4e9b\u4e8b\u5199\u6765\u5927\u8d39\u6beb\u58a8\uff0c\u5176\u5b9e\uff0c\u8fde\u7eed\u53d1\u751f\uff0c\u76f4\u5230\u9526\u888d\u4eba\u88ab\u738b\u5f64\u51fb\u6bd9\uff0c\u56db\u5468\u7684\u9ed1\u8863\u4eba\u624d\u75be\u6251 \u9762\u4e0a\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u3001\u4e09\u516c\u4e3b\u5206\u522b\u8fce\u4e86\u4e0a\u53bb\uff0c\u5251\u5982\u96f7\u9706\u7535\u63a3\uff0c\u5bd2\u5149\u4e00\u95ea\uff0c\u5fc5\u6709\u4eba\u4ea1\u547d\u5251\u4e0b\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u6025\u9053\uff1a\u201c\u7559\u4e0b\u6d3b\u53e3\u3002\u201d \u3000\u3000\u53ef\u60dc\u5df2\u53eb\u665a\u4e86\u4e00\u6b65\uff0c\u516d\u4e2a\u9ed1\u8863\u4eba\u5df2\u6709\u4e94\u4e2a\u6b7b\u4e8e\u5251\u4e0b\uff0c\u53ea\u4f59\u4e0b\u4e00\u4eba\uff0c\u88ab\u5c0f\u71d5\u5b50\u4e00\u4e0b\u70b9\u4e2d\u4e86 \u7a74\u9053\u3002 \u3000\u3000\u4e00\u573a\u9999\u8273\u7684\u5bb4\u4f1a\uff0c\u7247\u523b\u95f4\uff0c\u8840\u8089\u6a2a\u98de\uff0c\u6b7b\u4f24\u4e00\u7247\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u5947\u602a\u554a\uff01\u9b54\u6559\u4e2d\u4eba\u600e\u4f1a\u5982\u6b64\u7684\u4e0d\u582a\u4e00\u51fb\u3002\u201d \u3000\u3000\u5176\u5b9e\uff0c\u5927\u5385\u4e2d\u8fd8\u6709\u4eba\u5728\u640f\u6597\uff0c\u662f\u5927\u667a\u7985\u5e08\u548c\u76c8\u76c8\u3001\u9a6c\u9762\u4eba\u53ca\u7f8a\u9762\u4eba\u548c\u4e24\u4e2a\u966a\u4ed6\u4eec\u7684\u7f8e \u5973\uff0c\u516d\u4e2a\u4eba\u5206\u6210\u4e09\u5bf9\u53ae\u6740\uff0c\u6253\u5f97\u6fc0\u70c8\u7edd\u4f26\u3002 \u3000\u3000\u53cc\u65b9\u90fd\u6ca1\u6709\u4eae\u51fa\u5175\u5203\uff0c\u4f46\u638c\u6307\u7684\u653b\u88ad\u6240\u5728\uff0c\u4e5f\u90fd\u662f\u8db3\u4ee5\u81f4\u547d\u7684\u5927\u7a74\u3002 \u3000\u3000\u738b\u5f64\u884c\u8fd1\u4e86\u6c5f\u5343\u91cc\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u9b54\u6559\u4e2d\u7684\u6b66\u529f\uff0c\u4e0d\u5728\u4e2d\u539f\u5404\u5927\u95e8\u6d3e\u7684\u6b66\u529f\u4e4b\u4e0b\uff0c\u8fd9\u4e09 \u4eba\u5728\u4e2d\u539f\u7684\u6b66\u6797\u4e2d\uff0c\u90fd\u7b97\u662f\u7b2c\u4e00\u6d41\u7684\u9ad8\u624b\uff0c\u4f46\u4ed6\u4eec\u5df2\u640f\u4e94\u5341\u4f59\u4e2a\u7167\u9762\uff0c\u4ecd\u7136\u65e0\u6cd5\u5236\u670d\u4e09\u5973 \u5b50\u2026\u2026\u201d \u3000\u3000\u201c\u4f46\u5c0f\u71d5\u5b50\u3001\u4e09\u516c\u4e3b\u4ed6\u4eec\u5251\u51fa\u53d6\u547d\uff0c\u738b\u5144\u4e5f\u662f\u4e00\u51fb\u514b\u654c\u2026\u2026\u201d \u3000\u3000\u6c5f\u5343\u91cc\u63d0\u51fa\u4e86\u7591\u95ee\u3002 \u3000\u3000\u201c\u5144\u5f1f\u662f\u4ed7\u51ed\u8fd9\u4e2a\u2026\u2026\u201d\u738b\u5f64\u4eae\u4e86\u4e00\u4e0b\u8155\u4e0a\u7684\u94a2\u73af\uff0c\u9053\uff1a\u201c\u6211\u8fd9\u662f\u9738\u9053\u5f97\u5f88\uff0c\u5728\u5168\u65e0\u9632 \u5907\u4e4b\u4e0b\uff0c\u5c31\u7b97\u7edd\u4e16\u9ad8\u624b\u4e5f\u96be\u907f\u5f00\uff0c\u81f3\u4e8e\u5c0f\u71d5\u5b50\u548c\u4e09\u516c\u4e3b\u7684\u5251\u6cd5\uff0c\u5df2\u5230\u4e86\u65e0\u62db\u80dc\u6709\u62db\u7684\u5883\u754c\uff0c \u5251\u968f\u5fc3\u52a8\uff0c\u51fa\u5fc5\u4f24\u654c\uff0c\u4e00\u9053\u98de\u8679\u82df\u6167\u6708\u5750\u5173\u4e4b\u540e\uff0c\u624d\u609f\u51fa\u4e86\u8fd9\u79cd\u5251\u6cd5\uff0c\u4f20\u7ed9\u4e86\u4e09\u516c\u4e3b\u3002\u201d \u3000\u3000\u6c5f\u5e72\u91cc\u9053\uff1a\u201c\u54b1\u4eec\u770b\u770b\u90a3\u4e9b\u4eba\uff0c\u4e3a\u4ec0\u4e48\u628a\u8138\u4e0a\u6d82\u4e86\u4e00\u5c42\u539a\u539a\u7684\u77f3\u818f\uff0c\u8fd9\u4e09\u4f4d\u638c\u95e8\u4eba\u5df2\u4e89 \u5230\u5148\u673a\uff0c\u5f88\u5feb\u5c31\u53ef\u4ee5\u5236\u670d\u654c\u4eba\u4e86\u3002\u201d \u3000\u3000\u5f04\u5f00\u4e86\u9ed1\u8863\u4eba\u8138\u4e0a\u7684\u77f3\u818f\uff0c\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u4e0d\u7981\u5927\u5403\u4e00\u60ca\uff01 \u3000\u3000\u4ed6\u4eec\u4e0d\u662f\u4e3a\u4e86\u6613\u5bb9\u3001\u63a9\u8fd9\u672c\u6765\u7684\u9762\u76ee\uff0c\u800c\u662f\uff0c\u4ed6\u4eec\u7684\u8138\u4e0a\u7684\u808c\u8089\uff0c\u5927\u90e8\u5206\u90fd\u5df2\u6e83\u70c2\u6389 \u4e86\uff0c\u662f\u4ee5\u7528\u77f3\u818f\u6577\u51fa\u4e00\u5f20\u8138\u6765\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u6572\u5f00\u4e86\u6240\u6709\u7684\u5047\u8138\uff0c\u5f20\u5f20\u5982\u6b64\uff0c\u770b\u6765\u751a\u662f\u53ef\u6016\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u4e0d\u7981\u9eef\u7136\u4e00\u53f9\uff0c\u9053\uff1a\u201c\u6c5f\u67d0\u4eba\u603b\u7b97\u89c1\u8bc6\u5230\u9b54\u6559\u4e2d\u4eba\u7684\u624b\u6bb5\u4e86\uff0c\u8fd9\u6837\u7684\u6bc1\u5bb9\u63a7\u5236\u624b \u6cd5\uff0c\u5c31\u662f\u653e\u4e86\u4ed6\u4eec\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u6562\u79bb\u5f00\u8fd9\u91cc\u3002\u201d \u3000\u3000\u53ea\u542c\u90a3\u552f\u4e00\u8fd8\u6d3b\u7740\u7684\u9ed1\u8863\u4eba\uff0c\u8bf4\u9053\uff1a\u201c\u8fd8\u4e0d\u6b62\u5982\u6b64\uff0c\u8138\u4e0a\u7684\u808c\u8089\u6bcf\u65e5\u75bc\u75d2\u4ea4\u4f5c\uff0c\u5982\u65e0\u4ed6 \u4eec\u7684\u836f\u7269\u6b62\u75bc\u6d88\u75d2\uff0c\u8c01\u4e5f\u65e0\u6cd5\u5fcd\u8010\u8fc7\u4e00\u4e2a\u65f6\u8fb0\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u671b\u7740\u90a3\u4e2a\u9ed1\u8863\u4eba\uff0c\u6447\u6447\u5934\u9053\uff1a\u201c\u9601\u4e0b\u80fd\u4e0d\u80fd\u544a\u8bc9\u6211\u4eec\uff0c\u4f60\u7684\u51fa\u8eab\u6765\u5386\uff1f\u8981\u4e0d\u8981\u6211 \u4eec\u9001\u4f60\u56de\u53bb\uff1f\u201d \u3000\u3000\u201c\u6211\u5e94\u8be5\u56de\u53bb\u7684\uff0c\u6211\u5bb6\u6709\u59bb\u513f\uff0c\u4f46\u6211\u80fd\u8fd9\u6837\u56de\u53bb\u4e48\uff1f\u81f3\u4e8e\u6211\u7684\u59d3\u540d\u3001\u51fa\u8eab\uff0c\u90a3\u5c31\u4e0d\u7528\u95ee \u4e86\uff0c\u4f60\u552f\u4e00\u80fd\u5e2e\u52a9\u6211\u7684\u5730\u65b9\uff0c\u662f\u8ba9\u6211\u6b7b\u5f97\u8212\u670d\u4e00\u70b9\u2026\u2026\u201d \u3000\u3000\u6c5f\u5343\u91cc\u70b9\u70b9\u5934\uff0c\u51fa\u624b\u4e00\u6307\uff0c\u70b9\u4e86\u4ed6\u7684\u6b7b\u7a74\u3002 \u3000\u3000\u5927\u5385\u4e2d\u7684\u640f\u6597\u4ea6\u544a\u7ed3\u675f\uff0c\u4e09\u4f4d\u638c\u95e8\u4eba\u529f\u529b\u6df1\u539a\uff0c\u638c\u6bd9\u5f3a\u654c\u3002 \u3000\u3000\u4ed6\u4eec\u6ca1\u7559\u6d3b\u53e3\uff0c\u8fd9\u5973\u4eba\u7ed9\u4e86\u4ed6\u4eec\u5f88\u9ad8\u7684\u4eab\u53d7\uff0c\u4f7f\u4ed6\u4eec\u6ca6\u5165\u9b54\u9053\uff0c\u4e5f\u638c\u63e1\u4e86\u4ed6\u4eec\u7684\u79d8\u5bc6\uff0c \u6740\u4eba\u706d\u53e3\u81ea\u7136\u662f\u6700\u597d\u7684\u4fdd\u5bc6\u65b9\u6cd5\u3002 \u3000\u3000\u9a6c\u9762\u4eba\u3001\u7f8a\u9762\u4eba\u90fd\u6ca1\u6709\u53d6\u4e0b\u9762\u5177\uff0c\u4f46\u4ed6\u4eec\u6295\u6ce8\u5411\u5927\u667a\u7985\u5e08\u7684\u76ee\u5149\uff0c\u5374\u6d41\u9732\u51fa\u65e0\u6bd4\u7684\u4f69\u670d \u4e4b\u8272\u3002 \u3000\u3000\u4ed6\u4eec\u8d70\u5230\u4e86\u5927\u667a\u8eab\u524d\uff0c\u7f13\u7f13\u5730\u63ed\u5f00\u4e86\u5934\u7f69\u9762\u5177\uff0c\u5927\u667a\u5fae\u5fae\u4e00\u70b9\u5934\uff0c\u4ed6\u4eec\u4fbf\u7acb\u523b\u653e\u4e0b\uff0c\u5306 \u5306\u79bb\u53bb\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u90fd\u662f\u4e2d\u539f\u5404\u5927\u95e8\u6d3e\u4e2d\u7684\u9996\u8111\u4eba\u7269\uff0c\u4e5f\u53ef\u4ee5\u60f3\u5f97\u5230\u4ed6\u4eec\u662f\u8c01\uff0c\u4f46\u771f\u6b63\u89c1\u5230\u4ed6\u4eec \u7684\uff0c\u53ea\u6709\u5927\u667a\u65b9\u4e08\u4e00\u4eba\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u90a3\u4e24\u4f4d\u4e5f\u4e00\u4e95\u52b3\u8bf7\u5927\u5e08\u5904\u7f6e\u4e86\uff0c\u4ed6\u4eec\u5df2\u559d\u4e0b\u836f\u9152\uff0c\u5728\u4e0b\u70b9\u4e86\u4ed6\u4eec\u6655\u7a74\uff0c\u89e3 \u836f\u53ef\u80fd\u5c31\u5728\u90a3\u9526\u8863\u4eba\u7684\u8eab\u4e0a\u2026\u2026\u201d \u3000\u3000\u201c\u8001\u8896\u77e5\u9053\uff0c\u6211\u80fd\u8fa8\u8bc6\u51fa\u6765\uff0c\u4e24\u4f4d\u98ce\u8303\u4ed6\u4eec\u90fd\u5f88\u611f\u6fc0\uff0c\u6b64\u540e\uff0c\u6211\u76f8\u4fe1\u4e24\u4f4d\u4f1a\u662f\u5404\u5927\u95e8\u6d3e \u4e2d\u6700\u656c\u91cd\u7684\u4eba\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u738b\u5144\uff0c\u54b1\u4eec\u53ef\u4ee5\u8d70\u4e86\u3002\u201d \u3000\u3000\u4e3e\u6b65\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u738b\u5f64\u6325\u6325\u624b\uff0c\u5148\u8ba9\u5c0f\u71d5\u5b50\u3001\u4e09\u516c\u4e3b\u8ddf\u5728\u6c5f\u5343\u91cc\u8eab\u540e\u884c\u53bb\uff0c\u81ea\u5df1\u624d\u7f13\u6b65\u800c\u51fa\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a \u201c\u6211\u4eec\u5728\u5927\u95e8\u5916\u9762\u606d\u5019\u3002\u201d \u3000\u3000\u4ed6\u4e3a\u4eba\u6301\u91cd\uff0c\u552f\u6050\u5c0f\u71d5\u5b50\u548c\u4e09\u516c\u4e3b\u4e24\u4eba\u597d\u5947\u5fc3\u91cd\uff0c\u5077\u5077\u53d6\u4e0b\u732a\u9762\u5177\u548c\u9a74\u9762\u5177\uff0c\u7aa5\u5176\u771f\u9762 \u76ee\uff0c\u90a3\u5c31\u5927\u715e\u98ce\u666f\u4e86\u3002 \u3000\u3000\u5c0f\u9053\u58eb\u3001\u738b\u91cd\u5c71\u5e26\u7740\u5927\u6b63\u3001\u5927\u65b9\u4e24\u4f4d\u5c11\u6797\u957f\u8001\u8d76\u6765\u7684\u65f6\u5019\uff0c\u5927\u667a\u65b9\u6587\u4e5f\u884c\u4e86\u51fa\u6765\uff0c\u8eab\u540e \u8ddf\u7740\u9ed1\u7f57\u6c49\u3002 \u3000\u3000\u5927\u65b9\u3001\u5927\u6b63\u4e8c\u4eba\u5408\u638c\u4e00\u793c\uff0c\u9053\uff1a\u201c\u65b9\u4e08\u5b89\u597d\uff1f\u201d \u3000\u3000\u201c\u8fd8\u597d\uff0c\u6b64\u95f4\u4e8b\u5df2\u4e86\uff0c\u4e24\u4f4d\u8bf7\u5c3d\u5feb\u8d76\u56de\u5c11\u6797\u5bfa\u53bb\uff0c\u4e5f\u514d\u5f97\u53cc\u65b9\u957f\u65e5\u5bf9\u5cd9\u9020\u6210\u4f24\u4ea1\uff0c\u4f7f\u5927 \u6218\u5f04\u5047\u6210\u771f\u4e86\u3002\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u4e24\u4f4d\u5927\u5e08\u7b49\u6211\u4e00\u4e0b\uff0c\u54b1\u4eec\u7ed3\u4f34\u540c\u884c\u3002\u201d \u3000\u3000\u5927\u667a\u65b9\u4e08\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6709\u738b\u5927\u4eba\u540c\u884c\uff0c\u53cc\u65b9\u5c31\u53ef\u4ee5\u7acb\u523b\u6536\u5175\u4e86\uff0c\u9b54\u6559\u4fb5\u5165\u4e2d\u539f\u7684 \u4e8b\uff0c\u5927\u4eba\u53ef\u4ee5\u653e\u5fc3\uff0c\u5404\u5927\u95e8\u6d3e\u4f1a\u5c3d\u51fa\u7cbe\u9510\uff0c\u5168\u9762\u641c\u6740\uff0c\u4e00\u5b9a\u4f1a\u628a\u4ed6\u4eec\u9010\u51fa\u4e2d\u539f\uff0c\u6b64\u540e\uff0c\u4efb\u4f55 \u98ce\u5439\u8349\u52a8\uff0c\u5404\u5927\u95e8\u6d3e\u53ea\u8981\u5f97\u5230\u6d88\u606f\uff0c\u7acb\u523b\u4f1a\u6d3e\u4eba\u901a\u77e5\u4f60\u3002\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u70e6\u8bf7\u5927\u5e08\uff0c\u4ee3\u6211\u8c22\u8c22\u4ed6\u4eec\u4e86\u3002\u201d \u3000\u3000\u201c\u4e00\u5b9a\u8f6c\u8fbe\uff0c\u6211\u90a3\u56db\u4f4d\u670b\u53cb\uff0c\u4e5f\u8981\u8001\u8896\u5411\u4f60\u8868\u8fbe\u6700\u5d07\u9ad8\u7684\u656c\u610f\u3002\u201d \u3000\u3000\u738b\u5f64\u6325\u4e86\u6325\u624b\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4f1a\u594f\u660e\u5723\u4e0a\uff0c\u4f60\u4eec\u53ef\u4ee5\u653e\u5fc3\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\uff0c\u522b\u65e0\u987e \u5fcc\u4e86\u3002\u201d \u3000\u3000\u4e00\u8bed\u53cc\u5173\uff0c\u76ee\u5149\u5374\u76ef\u6ce8\u5728\u4e09\u516c\u4e3b\u7684\u8138\u4e0a\uff0c\u4fbf\u544a\u8f9e\u4e86\u3002 \u3000\u3000\u76ee\u7779\u738b\u5f64\u53bb\u8fdc\uff0c\u5927\u667a\u65b9\u4e08\u7a81\u7136\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6c5f\u65bd\u4e3b\uff0c\u4ed6\u4eec\u6258\u6211\u4f20\u4e2a\u53e3\u4fe1\uff0c\u65e5\u540e\uff0c\u6c5f\u65bd \u4e3b\u5982\u6709\u4ec0\u4e48\u9700\u8981\u7684\u8bdd\uff0c\u53ea\u8981\u4e00\u4e2a\u901a\u77e5\uff0c\u5c11\u6797\u6d3e\u3001\u6b66\u5f53\u6d3e\u3001\u4e10\u5e2e\u3001\u795e\u5200\u5802\u3001\u534e\u5c71\u6d3e\uff0c\u90fd\u5c06\u5168\u529b \u652f\u6301\u3002\u201d \u3000\u3000\u201c\u591a\u8c22\u5927\u5e08\u4e86\uff01\u201d \u3000\u3000\u6c5f\u5343\u91cc\u4e00\u62b1\u62f3\uff0c\u5fae\u5fae\u5730\u7b11\u4e86\u7b11\uff0c\u5e26\u7740\u5c0f\u71d5\u5b50\u3001\u9ed1\u7f57\u6c49\u3001\u767d\u7fbd\u3001\u738b\u91cd\u5c71\u3001\u4e09\u516c\u4e3b\uff0c\u5927\u6b65\u800c \u53bb\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u671b\u4e86\u6c5f\u5343\u91cc\u4e00\u773c\uff0c\u6b32\u8a00\u53c8\u6b62\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u89c1\u72b6\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u516c\u4e3b\u6709\u4ec0\u4e48\u8bdd\uff0c\u8bf7\u5c3d\u7ba1\u8bf4\uff01\u201d \u3000\u3000\u4e09\u516c\u4e3b\u602f\u751f\u751f\u7684\u8bf4\u9053\uff1a\u201c\u6c5f\u524d\u8f88\uff0c\u5e08\u7236\u8981\u6211\u8ddf\u4f60\u4eec\u5728\u6c5f\u6e56\u4e0a\u5386\u7ec3\u4e00\u6bb5\u65f6\u95f4\uff0c\u5979\u8bf4\uff0c\u4e09\u5e74 \u540e\u518d\u548c\u6211\u89c1\u9762\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u4e09\u5e74\u540e\u5f00\u542f\u77f3\u5ba4\u4e4b\u95e8\u2026\u2026\u201d \u3000\u3000\u5ffd\u7136\uff0c\u4ed6\u7684\u8bdd\u505c\u4f4f\u4e86\u3002 \u3000\u3000\u5927\u5bb6\u751a\u611f\u8be7\u5f02\uff0c\u4e0d\u7ea6\u800c\u540c\u7684\u671b\u5411\u6c5f\u5343\u91cc\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u5374\u7b11\u4e86\u7b11\uff0c\u53c8\u9053\uff1a\u201c\u4f46\u662f\uff0c\u6c5f\u6e56\u4e0a\u53ef\u4e0d\u6bd4\u5728\u6df1\u5bab\u5185\u82d1\uff0c\u4f60\u4ee5\u540e\uff0c\u53ef\u4e0d\u80fd\u6446\u51fa\u516c\u4e3b \u7684\u8eab\u4efd\u6765\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\uff01\u6211\u77e5\u9053\uff01\u201d\u4e09\u516c\u4e3b\u6025\u6025\u5730\u8bf4\u3002 \u3000\u3000\u7247\u523b\uff0c\u4e09\u516c\u4e3b\u53c8\u8bf4\u9053\uff1a\u201c\u6211\u53eb\u6731\u4e91\uff0c\u4e00\u4e2a\u5e73\u5e38\u4eba\u5bb6\u7684\u5973\u5b69\u5b50\uff0c\u6211\u4f1a\u5e2e\u5c0f\u71d5\u5b50\u4ed6\u4eec\u70e7\u83dc\u3001 \u6d17\u8863\u2026\u2026\uff0c\u4ee5\u524d\u7684\u4e09\u516c\u4e3b\u5df2\u7ecf\u6b7b\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u597d\uff01\u597d\uff01\u2026\u2026\u201d\u6c5f\u5343\u91cc\u8bf4\u7740\u5927\u7b11\u8d77\u6765\uff0c\u9053\uff1a\u201c\u5c0f\u4e2b\u5934\uff0c\u6c5f\u53d4\u53d4\u4f1a\u5c3d\u5168\u529b\u6765\u5e2e\u52a9 \u4f60\u2026\u2026\u201d \u3000\u3000\u201c\u591a\u8c22\u6c5f\u53d4\u53d4\uff01\u201d \u3000\u3000\u9ed1\u7f57\u6c49\u5927\u7b11\u9053\uff1a\u201c\u6731\u4e91\uff0c\u4e0d\u7528\u6362\u7a7f\u5973\u88c5\u4e86\uff0c\u548c\u6211\u4eec\u4e00\u8d77\u9080\u6e38\u5929\u4e0b\u5427\uff0c\u6c5f\u53d4\u53d4\u6b63\u51c6\u5907\u5e26\u6211 \u4eec\u5230\u897f\u57df\u53bb\u73a9\u73a9\u5462\uff01\u201d \u3000\u3000\u201c\u597d\u554a\uff01\u90a3\u91cc\u6211\u4f4f\u8fc7\u4e24\u5e74\uff0c\u6211\u505a\u5e26\u8def\u7684\u8bc6\u9014\u8001\u9a6c\u3002\u201d \u3000\u3000\u201c\u897f\u57df\u8981\u53bb\uff0c\u4f46\u8981\u5728\u4e09\u5e74\u4ee5\u540e\u2026\u2026\u201d\u6c5f\u5e72\u91cc\u9053\uff1a\u201c\u73b0\u5728\uff0c\u6211\u5148\u5e26\u4f60\u4eec\u53bb\u6e38\u6e38\u5357\u8352\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u7a81\u7136\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u60f3\u53bb\u5357\u8352\u4f46\u53c8\u60f3\u5ff5\u6211\u5a18\uff0c\u6211\u8be5\u600e\u4e48\u529e\u5462\uff1f\u201d \u3000\u3000\u201c\u90a3\u5c31\u5148\u53bb\u770b\u770b\u4f60\u5a18\u5427\uff0c\u6211\u8fd8\u8bb0\u5f97\u5979\u70e7\u7684\u4e00\u624b\u597d\u83dc\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u5927\u7b11\u7740\uff0c\u5411\u524d\u884c\u53bb\u3002 \u5367\u9f99\u751f\u300a\u71d5\u5b50\u4f20\u5947\u300b \u7b2c\u4e8c\u5341\u56de\u3000\u5fae\u6ce2\u53c8\u8d77 \u3000\u3000\u7531\u4eac\u5e08\u5357\u4e0b\u5c11\u6797\u7684\u539f\u73ed\u4eba\u9a6c\uff0c\u4ecd\u7136\u56de\u8f6c\u4eac\u5e08\u3002 \u3000\u3000\u5230\u8fbe\u4eac\u5e08\uff0c\u738b\u5f64\u3001\u97e9\u6d9b\u3001\u71d5\u98de\u7b49\u5927\u5185\u9ad8\u624b\u7acb\u523b\u8fdb\u5b98\u5411\u7687\u4e0a\u590d\u547d\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u6731\u4e91\u4e5f\u8d81\u8fd9\u673a\u4f1a\u8f6c\u56de\u5bab\u4e2d\uff0c\u5e76\u8a00\u660e\u4e94\u65e5\u540e\u5411\u6c5f\u5343\u91cc\u62a5\u5230\u3002\u5c0f\u71d5\u5b50\u5219\u56de\u5bb6\u4e0e\u6bcd\u4eb2\u6b22 \u805a\u51e0\u5929\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u5e26\u7740\u9ed1\u50e7\u3001\u767d\u7fbd\u4ee5\u53ca\u738b\u91cd\u5c71\u7b49\u4eba\u4f4f\u5728\u5929\u95e8\u5ba2\u6808\uff0c\u4ee5\u4fbf\u7b49\u4e09\u516c\u4e3b\u548c\u5c0f\u71d5\u5b50\u524d\u6765\u4f1a\u9f50 \u540e\uff0c\u518d\u4e00\u8d77\u95ef\u8361\u6c5f\u6e56\u3002 \u3000\u3000\u5306\u5306\u4e94\u65e5\u5df2\u8fc7\uff0c\u5c0f\u71d5\u5b50\u5982\u671f\u6765\u5411\u6c5f\u5343\u91cc\u62a5\u5230\u3002 \u3000\u3000\u504f\u504f\u4e09\u516c\u4e3b\u5374\u903e\u671f\u672a\u6765\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u4e09\u5929\uff0c\u8fd8\u662f\u4e0d\u89c1\u4e09\u516c\u4e3b\u8e2a\u5f71\uff0c\u6c5f\u5343\u91cc\u4e0d\u7531\u7740\u6025\u8d77\u6765\u3002 \u3000\u3000\u9ed1\u7f57\u6c49\u4e03\u5de7\u50e7\u9053\uff1a\u201c\u6c5f\u5927\u53d4\uff0c\u522b\u7b49\u4e86\u5427\uff01\u54b1\u4eec\u8fd8\u662f\u4e0a\u8def\u8981\u7d27\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u6447\u6447\u5934\u9053\uff1a\u201c\u4e0d\u6210\uff01\u4e09\u516c\u4e3b\u867d\u7136\u5df2\u8bf4\u8fc7\u548c\u54b1\u4eec\u4e0d\u5206\u5c0a\u5351\uff0c\u4f46\u5979\u7ec8\u7a76\u662f\u91d1\u679d\u7389\u53f6\uff0c \u4e0d\u6765\u5fc5\u6709\u539f\u56e0\uff0c\u5979\u5c31\u662f\u6539\u53d8\u4e86\u4e3b\u610f\uff0c\u4e5f\u5fc5\u5b9a\u4f1a\u7ed9\u54b1\u4eec\u4e00\u70b9\u6d88\u606f\uff0c\u82e5\u4e0d\u7b49\u5979\u5c31\u8d70\uff0c\u603b\u662f\u8bf4\u4e0d\u8fc7 \u53bb\u3002\u201d \u3000\u3000\u201c\u6b63\u56e0\u4e3a\u5979\u662f\u91d1\u6280\u5de5\u53f6\uff0c\u6240\u4ee5\u624d\u4e0d\u53ef\u80fd\u548c\u54b1\u4eec\u4e00\u8d77\u5403\u82e6\uff0c\u5979\u8bf4\u8981\u8ddf\u54b1\u4eec\u4e00\u8d77\u95ef\u8361\u6c5f\u6e56\uff0c \u4f9d\u6211\u770b\u90a3\u4e0d\u8fc7\u662f\u968f\u4fbf\u8bf4\u8bf4\u800c\u5df2\u3002\u201d \u3000\u3000\u201c\u8bdd\u4e0d\u80fd\u8fd9\u4e48\u8bf4\u3002\u6211\u770b\u5f97\u51fa\u4e09\u516c\u4e3b\u867d\u7136\u5e74\u7eaa\u5f88\u8f7b\uff0c\u4f46\u5374\u4e0d\u50cf\u8a00\u800c\u65e0\u4fe1\u4e4b\u4eba\uff0c\u82e5\u8bf4\u5979\u4e0d\u80fd \u5403\u82e6\uff0c\u4f60\u5c0f\u5b50\u8fd9\u8bdd\u5c31\u5927\u9519\u7279\u9519\u4e86\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5979\u5728\u897f\u57df\u53d7\u8fc7\u4e24\u5e74\u6298\u78e8\uff0c\u4ec0\u4e48\u82e6\u5934\u6ca1\u5403\u8fc7\uff0c\u4f60\u53ef\u66fe\u53d7\u8fc7\u5979\u90a3\u6837\u7684\u82e6\uff1f\u201d \u3000\u3000\u9ed1\u7f57\u6c49\u65e0\u8a00\u4ee5\u5bf9\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u53ea\u89c1\u738b\u5f64\u5306\u5306\u800c\u6765\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u5fd9\u9053\uff1a\u201c\u738b\u8001\u5f1f\u6765\u5f97\u6b63\u597d\uff0c\u4e09\u516c\u4e3b\u662f\u5426\u4e0d\u51c6\u5907\u51fa\u5bab\u4e86\uff1f\u201d \u3000\u3000\u738b\u5f64\u795e\u8272\u95f4\u663e\u5f97\u65e2\u5fe7\u90c1\u53c8\u7d27\u5f20\uff0c\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u5144\u5f1f\u6b64\u6765\uff0c\u6b63\u662f\u8981\u544a\u8bc9\u6c5f\u5144\u8fd9\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u201c\u83ab\u975e\u5bab\u4e2d\u53d1\u751f\u4e86\u53d8\u6545\uff1f\u201d\u6c5f\u5343\u91cc\u6025\u6025\u53d1\u95ee\u3002 \u3000\u3000\u201c\u5bab\u4e2d\u5012\u6ca1\u53d1\u751f\u4ec0\u4e48\u53d8\u6545\uff0c\u53d8\u6545\u662f\u53d1\u751f\u5728\u4e09\u516c\u4e3b\u4e00\u4eba\u8eab\u4e0a\u3002\u201d \u3000\u3000\u201c\u5230\u5e95\u4ec0\u4e48\u4e8b\uff1f\u201d\u6c5f\u5343\u91cc\u6709\u4e9b\u8feb\u4e0d\u53ca\u5f85\u3002 \u3000\u3000\u201c\u4e09\u516c\u4e3b\u4f53\u5185\u7684\u866b\u6bd2\u53c8\u53d1\u4f5c\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u554a\u4e86\u58f0\u9053\uff1a\u201c\u542c\u8bf4\u4e09\u516c\u4e3b\u8eab\u4e0a\u7684\u866b\u6bd2\u662f\u6d3b\u866b\uff0c\u4e0a\u6b21\u4e0d\u662f\u5df2\u7ecf\u53d6\u51fa\u4e86\u5417\uff1f\uff0d\u201d\u53ef\u662f \u73b0\u5728\u53c8\u53d1\u4f5c\u4e86\u3002\u201c \u3000\u3000\u201c\u4e0a\u6b21\u66ff\u4e09\u516c\u4e3b\u53d6\u866b\u7684\uff0c\u662f\u4e2a\u8001\u5996\u5a46\uff0c\u90a3\u8001\u5996\u5a46\u5462\uff1f\u201d \u3000\u3000\u201c\u65e9\u5c31\u4e0d\u77e5\u54ea\u91cc\u53bb\u4e86\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u8981\u653e\u5979\u8d70\uff1f\u201d \u3000\u3000\u201c\u662f\u4e09\u516c\u4e3b\u7b54\u5e94\u5979\u7684\u3002\u201d \u3000\u3000\u201c\u5f53\u521d\u4e0b\u866b\u7684\u4e5f\u662f\u5979\u5417\uff1f\u201d \u3000\u3000\u201c\u5979\u81ea\u79f0\u662f\u4e0b\u866b\u4eba\uff0c\u4f46\u8fd9\u8bdd\u662f\u5426\u771f\u5b9e\uff0c\u90a3\u5c31\u5f88\u96be\u8bf4\u4e86\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4e09\u516c\u4e3b\u88ab\u4e0b\u866b\u65f6\uff0c\u5df2\u5148\u88ab\u70b9\u4e86\u7a74\u9053\uff0c\u5979\u81ea\u5df1\u5e76\u6ca1\u770b\u5230\u4e0b\u866b\u7684\u7a76\u7adf\u662f\u8c01\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u4f4e\u5934\u6c89\u601d\u4e86\u7247\u523b\uff0c\u518d\u95ee\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u76ee\u524d\u7684\u60c5\u5f62\u5982\u4f55\uff1f\u201d \u3000\u3000\u738b\u5f64\u7d27\u76b1\u7740\u53cc\u7709\u9053\uff1a\u201c\u5144\u5f1f\u4eca\u5929\u4e00\u65e9\u5373\u8499\u4e09\u516c\u4e3b\u53ec\u89c1\uff0c\u770b\u5979\u7684\u6c14\u8272\u5e76\u6ca1\u4ec0\u4e48\u4e24\u6837\uff0c\u7cbe\u795e \u4e5f\u65e0\u4efb\u4f55\u5f02\u72b6\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u5982\u6b64\uff0c\u600e\u77e5\u866b\u6bd2\u53c8\u5df2\u53d1\u4f5c\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u662f\u542c\u4e09\u516c\u4e3b\u8bf4\u7684\u3002\u201d \u3000\u3000\u201c\u5979\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u201c\u636e\u4e09\u516c\u4e3b\u8bf4\uff0c\u90a3\u6d3b\u866b\u662f\u65bd\u653e\u5728\u5c0f\u8179\u90e8\u4f4d\uff0c\u6bcf\u5929\u5341\u4e8c\u4e2a\u65f6\u8fb0\u5185\u4f1a\u968f\u8840\u6db2\u5faa\u73af\u800c\u79fb\u52a8\u4f4d \u7f6e\uff0c\u597d\u5728\u79fb\u52a8\u8303\u56f4\u5e76\u4e0d\u5f88\u5927\uff0c\u4ee5\u5c0f\u8179\u4e3a\u4e2d\u5fc3\u70b9\uff0c\u5927\u7ea6\u53ea\u80fd\u4e0a\u4e0b\u5de6\u53f3\u79fb\u52a8\u4e94\u5bf8\u5de6\u53f3\u3002\u4e0a\u6b21\u7531\u90a3 \u8001\u5996\u5a46\u65bd\u884c\u672f\u6cd5\u540e\uff0c\u5728\u611f\u89c9\u4e0a\u7684\u786e\u50cf\u5df2\u88ab\u53d6\u51fa\uff0c\u5c82\u77e5\u7531\u5c11\u6797\u8fd4\u56de\u7684\u7b2c\u4e8c\u5929\uff0c\u5c0f\u8179\u5185\u4fbf\u53c8\u6709\u4e86 \u53d8\u5316\u3002\u201d \u3000\u3000\u201c\u6709\u4e86\u4ec0\u4e48\u6837\u7684\u53d8\u5316\uff1f\u201d \u3000\u3000\u201c\u636e\u8bf4\u5728\u611f\u89c9\u4e0a\uff0c\u5c0f\u8179\u5185\u4f3c\u4e4e\u53c8\u6709\u4e00\u79cd\u4e1c\u897f\u5728\u6162\u6162\u6d3b\u52a8\u3002\u201d \u3000\u3000\u201c\u90a3\u4e1c\u897f\u81ea\u7136\u5c31\u662f\u6d3b\u866b\u4e86\uff0c\u662f\u5426\u548c\u4ee5\u524d\u7684\u4e00\u6837\u5927\u5c0f\uff1f\u201d \u3000\u3000\u201c\u4ece\u524d\u90a3\u6d3b\u866b\u8db3\u6709\u9e3d\u86cb\u5927\u5c0f\uff0c\u73b0\u5728\u7684\u5927\u7ea6\u6709\u9ec4\u8c46\u90a3\u4e48\u5927\u3002\u201d \u3000\u3000\u201c\u90a3\u662f\u8bf4\u5f88\u5c0f\u4e86\uff1f\u201d \u3000\u3000\u201c\u73b0\u5728\u867d\u7136\u5f88\u5c0f\uff0c\u4f46\u5b83\u4f1a\u6162\u6162\u957f\u5927\u3002\u201d \u3000\u3000\u201c\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u4e5f\u662f\u4e09\u516c\u4e3b\u8bf4\u7684\uff0c\u5979\u5728\u897f\u57df\u6700\u521d\u88ab\u4e0b\u866b\u65f6\uff0c\u4e5f\u662f\u8fd9\u4e48\u5927\uff0c\u4e24\u5e74\u540e\u4fbf\u957f\u5f97\u8db3\u6709\u9e3d\u86cb\u5927 \u5c0f\uff0c\u5728\u611f\u89c9\u4e0a\u8fd9\u6b21\u5c0f\u8179\u5185\u7684\u866b\u548c\u4e0a\u6b21\u7684\u5b8c\u5168\u4e00\u6837\u3002\u201d \u3000\u3000\u201c\u5c31\u56e0\u4e3a\u8fd9\u539f\u56e0\uff0c\u4e09\u516c\u4e3b\u624d\u65e0\u6cd5\u51fa\u5bab\uff1f\u201d\u3002\u201c\u4e0d\u9519\uff0c\u4e09\u516c\u4e3b\u5bf9\u8fd9\u4e8b\u611f\u5230\u5f88\u6b49\u610f\uff0c\u7279\u522b\u4ea4 \u5f85\u5144\u5f1f\u6765\u544a\u77e5\u6c5f\u5144\uff0c\u8981\u6c5f\u5144\u4e0d\u5fc5\u518d\u7b49\u5979\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9677\u5165\u6c89\u601d\uff0c\u795e\u8272\u4e00\u7247\u9eef\u7136\uff0c\u8bb8\u4e45\u624d\u957f\u957f\u5401\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u56e0\u6b64\u800c\u4e0d\u51fa\u4eac\u662f\u5c0f \u4e8b\uff0c\u4f46\u5979\u8eab\u4e0a\u7684\u866b\u6bd2\u5374\u4e0d\u80fd\u4e0d\u6cbb\u3002\u201d \u3000\u3000\u738b\u5f64\u82e6\u7b11\u9053\uff1a\u201c\u5144\u5f1f\u4f55\u5c1d\u4e0d\u4e3a\u8fd9\u4e8b\u53d1\u6101\uff0c\u89e3\u94c3\u8fd8\u987b\u7cfb\u94c3\u4eba\uff0c\u6b64\u523b\u6700\u91cd\u8981\u7684\u4fbf\u662f\u627e\u5230\u90a3\u8001 \u5996\u5a46\uff0c\u53ef\u662f\u90a3\u8001\u5996\u5a46\u65e9\u5df2\u4e0d\u77e5\u53bb\u5411\u4e86\uff0c\u53c8\u5230\u54ea\u91cc\u53bb\u627e\u5462\uff1f\u201d \u3000\u3000\u6c5f\u5e72\u91cc\u671b\u4e86\u5c0f\u71d5\u5b50\u4e00\u773c\u9053\uff1a\u201c\u4f60\u4e0a\u6b21\u4e0d\u662f\u4e5f\u66ff\u4e09\u516c\u4e3b\u6cbb\u8fc7\u4e48\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u8138\u4e0a\u4e00\u70ed\u9053\uff1a\u201c\u5c0f\u4f84\u6b63\u5728\u4e3a\u4e09\u516c\u4e3b\u7597\u4f24\u65f6\uff0c\u5bab\u4e2d\u4fbf\u5df2\u6349\u4f4f\u4e86\u90a3\u8001\u5996\u5a46\uff0c\u6240\u4ee5\u4e0a\u6b21 \u4e3a\u4e09\u516c\u4e3b\u53d6\u866b\uff0c\u53ef\u8bf4\u5b8c\u5168\u662f\u51fa\u4e8e\u8001\u5996\u5a46\u4e00\u4eba\u4e4b\u624b\u3002\u201d \u3000\u3000\u201c\u5c0f\u71d5\u5b50\uff0c\u4e0a\u6b21\u5982\u679c\u63d0\u4e0d\u5230\u90a3\u8001\u5996\u5a46\uff0c\u4f60\u6709\u628a\u63e1\u80fd\u4e3a\u4e09\u516c\u4e3b\u6cbb\u597d\u866b\u6bd2\u5417\uff1f\u201d \u3000\u3000\u201c\u5c0f\u4f84\u8fde\u866b\u662f\u4ec0\u4e48\u6837\u5b50\u90fd\u6ca1\u770b\u5230\uff0c\u54ea\u91cc\u4f1a\u6709\u628a\u63e1\u3002\u201d \u3000\u3000\u201c\u597d\u5c0f\u5b50\uff0c\u4e09\u516c\u4e3b\u662f\u91d1\u679d\u7389\u53f6\u4e4b\u8eaf\uff0c\u4f60\u65e2\u7136\u4e00\u70b9\u628a\u63e1\u90fd\u6ca1\u6709\uff0c\u7adf\u6562\u968f\u4fbf\u4e71\u6765\u3002\u542c\u8bf4\u4e09\u516c \u4e3b\u8fde\u8863\u670d\u90fd\u8131\u4e86\uff0c\u4f60\u8fd9\u5c0f\u5b50\u80c6\u5b50\u4e5f\u672a\u514d\u592a\u5927\u4e86\uff0c\u82e5\u7687\u4e0a\u964d\u7f6a\u4e0b\u6765\uff0c\u53ea\u6015\u5c31\u6709\u706d\u95e8\u4e4b\u7978\u3002\u201d \u3000\u3000\u8c01\u77e5\u5c0f\u71d5\u5b50\u5374\u7406\u76f4\u6c14\u58ee\u7684\u9053\uff1a\u201c\u6c5f\u53d4\u53d4\uff0c\u5f53\u521d\u4f60\u597d\u50cf\u4e5f\u66fe\u9f13\u52b1\u8fc7\u6211\uff0c\u73b0\u5728\u600e\u4e48\u53c8\u8bf4\u51fa\u8fd9 \u79cd\u8bdd\u6765\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u987f\u4e86\u987f\u9053\uff1a\u201c\u6211\u9f13\u52b1\u8fc7\u4f60\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6c5f\u53d4\u53d4\u771f\u662f\u8d35\u4eba\u591a\u5fd8\u4e8b\u3002\u60a8\u8bf4\u5c0f\u4f84\u5df2\u5f97\u4e86\u5e08\u7236\u2018\u5929\u96f7\u795e\u638c\u2019\u7684\u771f\u4f20\uff0c\u4ee5\u5929\u96f7\u795e\u638c\u7684\u5185 \u529b\uff0c\u5fc5\u53ef\u5c06\u4e09\u516c\u4e3b\u4f53\u5185\u7684\u866b\u6bd2\u903c\u51fa\u3002\u5c0f\u4f84\u56e0\u4e3a\u53d7\u4e86\u6c5f\u53d4\u53d4\u7684\u9f13\u52b1\uff0c\u624d\u6562\u6597\u80c6\u4e00\u8bd5\uff0c\u4e0d\u7136\u5c0f\u4f84 \u54ea\u91cc\u6709\u8fd9\u79cd\u80c6\u5b50\u3002\u201d \u3000\u3000\u53ea\u542c\u738b\u5f64\u63d2\u8a00\u9053\uff1a\u201c\u6c5f\u5144\uff0c\u5929\u96f7\u795e\u638c\u771f\u6709\u8fd9\u79cd\u529f\u6548\u5417\uff1f\u201d \u3000\u3000\u6c5f\u5e72\u91cc\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u6c5f\u67d0\u7684\u4e49\u5144\u5929\u96f7\u8001\u4eba\uff0c\u5728\u5f53\u4eca\u6b66\u6797\u662f\u516c\u8ba4\u7684\u5929\u4e0b\u6b66\u529f\u7b2c\u4e00\uff0c\u4ed6\u7684 \u5929\u96f7\u795e\u638c\u638c\u529b\u4e4b\u96c4\u6d51\uff0c\u65e0\u4eba\u53ef\u53ca\uff0c\u4ee5\u638c\u529b\u9a71\u9664\u4f53\u5185\u5267\u6bd2\uff0c\u4e0d\u4e4f\u5148\u4f8b\u3002\u5c0f\u71d5\u5b50\u5df2\u5f97\u5176\u5e08\u771f\u4f20\uff0c \u6c5f\u67d0\u5f53\u65f6\u8981\u5c0f\u71d5\u5b50\u4e00\u8bd5\uff0c\u53ea\u662f\u60c5\u6025\u95f4\u4f5c\u51fa\u7684\u51b3\u5b9a\uff0c\u5f53\u7136\u4e5f\u4e1d\u6beb\u6ca1\u6709\u628a\u63e1\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u5982\u6b64\uff0c\u4f55\u4e0d\u518d\u8981\u5c0f\u71d5\u5b50\u8fdb\u5bab\u4e00\u8bd5\uff1f\u201d \u3000\u3000\u6c5f\u5e72\u91cc\u5927\u611f\u72b9\u8c6b\u7684\u671b\u5411\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u4f60\u662f\u5426\u6709\u52c7\u6c14\u518d\u8fdb\u5bab\u4e00\u8bd5\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u8038\u4e86\u8038\u80a9\u9053\uff1a\u201c\u5e08\u7236\u4ea4\u5f85\u7684\u4e09\u4ef6\u4e8b\uff0c\u5176\u4e2d\u6709\u4e00\u4ef6\u5c31\u662f\u51e1\u4e8b\u90fd\u8981\u542c\u4ece\u4f60\u8001\u4eba\u5bb6\u7684\u5429 \u5490\uff0c\u53ea\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u53ea\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u60a8\u521a\u624d\u8bf4\u8fc7\uff0c\u5982\u679c\u4e71\u6765\u800c\u6cbb\u4e0d\u597d\u4e09\u516c\u4e3b\u7684\u75c5\uff0c\u5f88\u53ef\u80fd\u5c31\u6709\u706d\u95e8\u4e4b\u7978\uff0c\u8fd9\u4e8b\u5c0f\u4f84\u4e00\u70b9\u628a \u63e1\u4e5f\u6ca1\u6709\uff0c\u800c\u542c\u60a8\u8001\u4eba\u5bb6\u7684\u8bed\u6c14\uff0c\u4e5f\u6ca1\u6709\u628a\u63e1\uff0c\u5c0f\u4f84\u81ea\u5df1\u88ab\u6740\u5934\u6ca1\u5173\u7cfb\uff0c\u82e5\u8fde\u7d2f\u4e86\u6211\u7239\u548c\u6211 \u5a18\u2026\u2026\u201d \u3000\u3000\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u5012\u6293\u6211\u8bed\u75c5\u2026\u2026\u201d \u3000\u3000\u6c5f\u5343\u91cc\u5634\u91cc\u867d\u8fd9\u4e48\u8bf4\uff0c\u56e0\u4e3a\u5179\u4e8b\u4f53\u5927\uff0c\u7167\u6837\u4e5f\u4e0d\u6562\u9083\u4e0b\u51b3\u5b9a\u3002 \u3000\u3000\u738b\u5f64\u5fd9\u9053\uff1a\u201c\u6c5f\u5144\u4e0d\u5fc5\u4e3a\u96be\uff0c\u5c31\u8ba9\u5c0f\u71d5\u5b50\u53bb\u8bd5\u8bd5\u5427\uff01\u4f9d\u5144\u5f1f\u6240\u6599\uff0c\u5c31\u7b97\u6cbb\u4e0d\u597d\u4e09\u516c\u4e3b\u7684 \u75c5\uff0c\u5c0f\u71d5\u5b50\u4e5f\u7edd\u5bf9\u4e0d\u4f1a\u6709\u7978\u4e8b\u4e34\u8eab\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u738b\u8001\u5f1f\u6041\u4ec0\u4e48\u80fd\u63d0\u51fa\u8fd9\u9879\u4fdd\u8bc1\uff1f\u201d \u3000\u3000\u201c\u5144\u5f1f\u4e0d\u76f8\u4fe1\u6c5f\u5144\u770b\u4e0d\u51fa\uff0c\u4e0a\u6b21\u51fa\u5bab\u5230\u5c11\u6797\uff0c\u518d\u7531\u5c11\u6797\u8fd4\u56de\u4eac\u5e08\uff0c\u4e09\u516c\u4e3b\u65e9\u5df2\u5bf9\u5c0f\u71d5\u5b50 \u6697\u751f\u60c5\u612b\uff0c\u5c24\u5176\u4e00\u9053\u98de\u8679\u82df\u5973\u4fa0\u66fe\u8868\u793a\u8fc7\uff0c\u6709\u610f\u5c06\u4ed6\u4eec\u4e24\u4eba\u914d\u6210\u4e00\u5bf9\u2026\u2026\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u542c\u5230\u8fd9\u91cc\uff0c\u7acb\u5373\u7ea2\u7740\u8138\u79bb\u5f00\u623f\u95f4\u3002\u5176\u5b9e\u4ed6\u5fc3\u91cc\u4e5f\u65e9\u6709\u6570\uff0c\u4e09\u516c\u4e3b\u786e\u5b9e\u5bf9\u4ed6\u6709\u60c5\uff0c \u81f3\u4e8e\u4ed6\u81ea\u5df1\u4e5f\u65e9\u5bf9\u4e09\u516c\u4e3b\u5fc3\u751f\u7231\u6155\uff0c\u53ea\u56e0\u81ea\u5df1\u662f\u4e2a\u5e73\u6c11\uff0c\u8eab\u4efd\u5730\u4f4d\u76f8\u5dee\u60ac\u6b8a\uff0c\u4e0d\u6562\u8868\u793a\u800c \u5df2\u3002 \u3000\u3000\u738b\u5f64\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u66f4\u4f55\u51b5\u4e09\u516c\u4e3b\u7684\u8eab\u4f53\u5df2\u7ecf\u8ba9\u4ed6\u770b\u5230\u4e86\uff0c\u7eb5\u7136\u4ed6\u6cbb\u4e0d\u597d\u4e09\u516c\u4e3b\u7684\u75c5\uff0c\u4e09 \u516c\u4e3b\u4e5f\u4e0d\u53ef\u80fd\u602a\u7f6a\uff0c\u800c\u4e14\u8bf4\u4e0d\u5b9a\u8fd8\u4f1a\u5fc3\u5b58\u611f\u6fc0\u5462\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u6c89\u541f\u7740\u9053\uff1a\u201c\u8fd9\u8bdd\u4e5f\u6709\u9053\u7406\u3002\u53ef\u662f\u82e5\u4e07\u4e00\u88ab\u7687\u4e0a\u77e5\u9053\uff0c\u90a3\u5c31\u4e0d\u662f\u4e09\u516c\u4e3b\u6240\u80fd\u4f5c\u4e3b \u7684\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u6c5f\u5144\u653e\u5fc3\uff0c\u8fd9\u4e8b\u82e5\u4e0d\u7980\u62a5\u7687\u4e0a\uff0c\u7687\u4e0a\u6839\u672c\u4e0d\u53ef\u80fd\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u5018\u82e5\u6709\u4eba\u5077\u5077\u7980\u62a5\u7687\u4e0a\u5462\uff1f\u201d \u3000\u3000\u201c\u9664\u4e09\u516c\u4e3b\u672c\u4eba\u5916\uff0c\u8fd9\u4e8b\u53ea\u80fd\u8ba9\u601c\u82b1\u4e00\u4eba\u77e5\u9053\uff0c\u601c\u82b1\u662f\u4e09\u516c\u4e3b\u7684\u8d34\u8eab\u4f8d\u5a62\uff0c\u5979\u600e\u53ef\u80fd\u5077 \u5077\u7980\u62a5\u7687\u4e0a\u5462\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u7ec8\u4e8e\u70b9\u70b9\u5934\u9053\uff1a\u201c\u597d\uff0c\u5c31\u8fd9\u4e48\u51b3\u5b9a\uff0c\u8ba9\u5c0f\u71d5\u5b50\u8fdb\u5bab\u4e00\u8d9f\u3002\u201d \u3000\u3000\u5c31\u5728\u5f53\u65e5\u508d\u665a\uff0c\u738b\u5f64\u5e26\u7740\u6c5f\u5343\u91cc\u548c\u5c0f\u71d5\u5b50\u8fdb\u5165\u540e\u5bab\u3002 \u3000\u3000\u4e0d\u6d88\u8bf4\uff0c\u6c5f\u5343\u91cc\u548c\u5c0f\u71d5\u5b50\u90fd\u6362\u4e86\u4e00\u8eab\u5927\u5185\u4f8d\u536b\u670d\u88c5\uff0c\u800c\u4e14\u5e26\u4e86\u8170\u724c\u3002 \u3000\u3000\u81f3\u4e8e\u9ed1\u7f57\u6c49\u4e03\u5de7\u50e7\u3001\u5c0f\u9053\u58eb\u767d\u7fbd\u4ee5\u53ca\u738b\u91cd\u5c71\uff0c\u4ecd\u7559\u5728\u5929\u95e8\u5ba2\u6808\u5f85\u547d\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u548c\u5c0f\u71d5\u5b50\u88ab\u62db\u5f85\u5728\u738b\u5f64\u7684\u4f8d\u536b\u7edf\u9886\u4f4f\u5904\u3002 \u3000\u3000\u71d5\u98de\u5f97\u77e5\u513f\u5b50\u5165\u5bab\uff0c\u8fde\u5fd9\u8d76\u6765\u4e0e\u5c0f\u71d5\u5b50\u76f8\u4f1a\u3002 \u3000\u3000\u5bf9\u4e8e\u513f\u5b50\u6b64\u6765\uff0c\u71d5\u98de\u662f\u53c8\u9ad8\u5174\u53c8\u60f6\u6050\u3002 \u3000\u3000\u9ad8\u5174\u7684\u662f\u82e5\u513f\u5b50\u771f\u80fd\u4e3a\u4e09\u516c\u4e3b\u6cbb\u597d\u866b\u6bd2\uff0c\u8fd9\u4e00\u7b14\u5927\u529f\u52b3\u5c06\u4f1a\u8ba9\u4ed6\u5168\u5bb6\u6709\u4eab\u4e0d\u5c3d\u7684\u8363\u534e\u5bcc \u8d35\uff0c\u5982\u679c\u513f\u5b50\u8fdb\u4e00\u6b65\u4f5c\u4e86\u9a78\u9a6c\uff0c\u90a3\u5c31\u8d8a\u53d1\u5149\u5b97\u8000\u7956\u4e86\u3002 \u3000\u3000\u81f3\u4e8e\u60f6\u6050\u65b9\u9762\uff0c\u81ea\u7136\u4e5f\u662f\u62c5\u5fc3\u4e07\u4e00\u6cbb\u4e0d\u597d\u516c\u4e3b\u7684\u75c5\u800c\u906d\u5230\u706d\u95e8\u5927\u7978\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u8fd9\u4e8b\u65e2\u5df2\u7531\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u4f5c\u4e86\u51b3\u5b9a\uff0c\u4ed6\u5e76\u4e0d\u60f3\u8868\u793a\u610f\u89c1\uff0c\u597d\u5728\u8fd9\u4e8b\u5e94\u8be5\u662f\u559c\u591a\u4e8e \u5fe7\uff0c\u5373\u4f7f\u78b0\u78b0\u8fd0\u6c14\u4e5f\u5341\u5206\u503c\u5f97\u3002 \u3000\u3000\u738b\u5f64\u5148\u5230\u201c\u542c\u8749\u9662\u201d\u53bb\u89c1\u4e09\u516c\u4e3b\uff0c\u8bf4\u660e\u4e0a\u60c5\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u4e00\u53e3\u7b54\u5e94\u4e0b\u6765\u3002 \u3000\u3000\u738b\u5f64\u518d\u56de\u5230\u4f4f\u5904\uff0c\u53ea\u89c1\u6c5f\u5343\u91cc\u3001\u71d5\u98de\u3001\u5c0f\u71d5\u5b50\u6b63\u5728\u4e00\u9762\u95f2\u8c08\uff0c\u4e00\u9762\u7b49\u5019\u6d88\u606f\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u62a2\u7740\u95ee\u9053\uff1a\u201c\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u201c\u4e09\u516c\u4e3b\u5b8c\u5168\u540c\u610f\u3002\u201d\u738b\u5f64\u9053\uff1a\u201c\u73b0\u5728\u5929\u8272\u5df2\u665a\uff0c\u6b63\u662f\u65f6\u5019\uff0c\u54b1\u4eec\u8d70\u5427\uff01\u201d \u3000\u3000\u201c\u5c0f\u71d5\u5b50\u4e00\u4eba\u968f\u4f60\u53bb\u5c31\u6210\u4e86\uff0c\u96be\u9053\u6211\u548c\u71d5\u8001\u5f1f\u4e5f\u8981\u53bb\uff1f\u201d \u3000\u3000\u201c\u6c5f\u5144\u662f\u6709\u540d\u7684\u4e07\u4e8b\u901a\uff0c\u5f85\u4f1a\u513f\u82e5\u6709\u7591\u96be\u4e4b\u5904\uff0c\u5fc5\u987b\u5411\u4f60\u8bf7\u6559\uff0c\u81f3\u4e8e\u71d5\u98de\u8001\u5f1f\uff0c\u4e0d\u59a8\u5728 \u542c\u8749\u9662\u95e8\u5916\u8d1f\u8d23\u8b66\u6212\u3002\u201d \u3000\u3000\u4e8e\u662f\u4e00\u884c\u56db\u4eba\uff0c\u5728\u738b\u5f64\u7684\u5e26\u9886\u4e0b\uff0c\u4e00\u540c\u8fdb\u5165\u542c\u8749\u9662\u3002 \u3000\u3000\u5728\u9662\u95e8\u5916\uff0c\u71d5\u98de\u9996\u5148\u7559\u4e0b\uff0c\u6c5f\u5343\u91cc\u5219\u5728\u5929\u4e95\u7559\u4e0b\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u7531\u738b\u5f64\u76f4\u63a5\u5e26\u5165\u4e09\u516c\u4e3b\u5bdd\u5bab\u3002 \u3000\u3000\u601c\u82b1\u8fde\u5fd9\u5377\u5e18\u8fce\u63a5\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u6c14\u8272\u5f88\u597d\uff0c\u6839\u672c\u4e0d\u50cf\u6709\u75c5\u7684\u6837\u5b50\u3002 \u3000\u3000\u5176\u5b9e\u8fd9\u662f\u610f\u6599\u4e2d\u4e8b\uff0c\u4e09\u516c\u4e3b\u76ee\u524d\u4f53\u5185\u7684\u6d3b\u866b\u53ea\u6709\u9ec4\u8c46\u5927\u5c0f\uff0c\u8d77\u4e0d\u4e86\u591a\u5927\u4f5c\u7528\uff0c\u6240\u62c5\u5fc3\u7684 \u53ea\u662f\u65e5\u540e\u6e10\u957f\u6e10\u5927\u800c\u5df2\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u867d\u7136\u4e0a\u6b21\u5728\u5f80\u8fd4\u5c11\u6797\u9014\u4e2d\uff0c\u5df2\u548c\u4e09\u516c\u4e3b\u591a\u65e5\u671d\u5915\u76f8\u5904\uff0c\u4f46\u5c0a\u5351\u4e4b\u793c\u5374\u4e0d\u80fd\u5e9f\uff0c\u8de8 \u8fdb\u95e8\u69db\u540e\uff0c\u7acb\u5373\u8eac\u8eab\u6df1\u65bd\u4e00\u793c\u9053\uff1a\u201c\u71d5\u6625\u98ce\u53c2\u89c1\u4e09\u516c\u4e3b\uff01\u201d \u3000\u3000\u4e09\u516c\u4e3b\u5750\u5728\u5e8a\u524d\uff0c\u542b\u60c5\u8109\u8109\u770b\u4e86\u5c0f\u71d5\u5b50\u4e00\u773c\uff0c\u4e00\u8fb9\u6b20\u4e86\u6b20\u8eab\u9053\uff1a\u201c\u5f7c\u6b64\u76f8\u8bc6\u591a\u65e5\uff0c\u71d5\u5c11 \u4fa0\u7528\u4e0d\u7740\u62d8\u793c\uff0c\u5feb\u5feb\u8bf7\u5750\uff0c\u738b\u7edf\u9886\u4e5f\u8bf7\u5750\u3002\u201d\u738b\u5f64\u548c\u5c0f\u71d5\u5b50\u7565\u4e00\u8c26\u900a\uff0c\u4f9d\u8a00\u5728\u4e00\u65c1\u5750\u4e0b\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u518d\u671b\u5411\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u6211\u4f53\u5185\u866b\u6bd2\u590d\u53d1\u7684\u4e8b\uff0c\u738b\u7edf\u9886\u5df2\u5bf9\u71d5\u5c11\u4fa0\u8bf4\u8fc7\u4e86\u5427\uff1f\u201d \u3000\u3000\u201c\u738b\u7edf\u9886\u5df2\u5bf9\u8349\u6c11\u8bf4\u5f97\u5f88\u8be6\u7ec6\u3002\u201d\u5c0f\u71d5\u5b50\u795e\u6001\u62d8\u8c28\u3002 \u3000\u3000\u201c\u5728\u6211\u9762\u524d\uff0c\u7528\u4e0d\u7740\u81ea\u79f0\u8349\u6c11\uff0c\u7b49\u6211\u866b\u6bd2\u6cbb\u597d\u4ee5\u540e\uff0c\u8fd8\u8981\u548c\u5927\u5bb6\u4e00\u8d77\u95ef\u8361\u6c5f\u6e56\uff0c\u90a3\u65f6\u5f7c \u6b64\u8eab\u4efd\u5b8c\u5168\u76f8\u540c\uff0c\u82e5\u5927\u5bb6\u8fd8\u628a\u6211\u770b\u6210\u662f\u91d1\u679d\u7389\u53f6\uff0c\u90a3\u5c31\u4e0d\u597d\u76f8\u5904\u4e86\u3002\u201d \u3000\u3000\uff0f.\u71d5\u5b50\u548c\u738b\u5f64\u90fd\u6ca1\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u9ed8\u4e86\u4e00\u9ed8\uff0c\u518d\u9053\uff1a\u201c\u71d5\u5c11\u4fa0\uff0c\u4f60\u6709\u628a\u63e1\u80fd\u66ff\u6211\u6cbb\u597d\u4f53\u5185\u7684\u866b\u6bd2\u5417\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u5fc3\u795e\u4e00\u7d27\uff0c\u8fde\u5fd9\u8eac\u8eab\u9053\uff1a\u201c\u8fd9\u65b9\u9762\u6211\u600e\u80fd\u8c08\u5f97\u5230\u6709\u628a\u63e1\uff0c\u672c\u6765\u6211\u662f\u4e0d\u6562\u524d\u6765\uff0c\u4f46 \u7981\u4e0d\u4f4f\u6c5f\u53d4\u53d4\u548c\u738b\u7edf\u9886\u7684\u5429\u5490\uff0c\u53ea\u597d\u524d\u6765\u4e00\u8bd5\u3002\u201d \u3000\u3000\u4e09\u516c\u4e3b\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u8bd5\u8bd5\u5c31\u8bd5\u8bd5\u5427\uff01\u6211\u76f8\u4fe1\u4f60\u4f1a\u6709\u529e\u6cd5\u7684\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u5fc3\u5934\u4e00\u9707\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u4e3a\u4ec0\u4e48\u4f1a\u6709\u8fd9\u79cd\u60f3\u6cd5\uff1f\u201d \u3000\u3000\u4e09\u516c\u4e3b\u9053\uff1a\u201c\u4f60\u662f\u5929\u96f7\u8001\u4eba\u7684\u552f\u4e00\u4f20\u4eba\uff0c\u5bb6\u5e08\u8bf4\u8fc7\u5929\u96f7\u8001\u4eba\u662f\u5f53\u4eca\u4e4b\u4e16\u7684\u7b2c\u4e00\u9ad8\u4eba\uff0c\u4ee5 \u4ed6\u7684\u638c\u529b\u6240\u53d1\u51fa\u7684\u795e\u529f\uff0c\u53ef\u8bf4\u65e0\u6240\u4e0d\u80fd\u3002 \u3000\u3000\u6211\u76f8\u4fe1\u5bb6\u5e08\u7684\u8bdd\uff0c\u5f53\u7136\u4e5f\u5c31\u76f8\u4fe1\u4f60\u4e86\u3002\u201c \u3000\u3000\u7531\u4e8e\u4e09\u516c\u4e3b\u7684\u8fd9\u51e0\u53e5\u8bdd\uff0c\u53cd\u800c\u4f7f\u5f97\u5c0f\u71d5\u5b50\u5750\u7acb\u4e0d\u5b89\uff0c\u65e9\u77e5\u5982\u6b64\uff0c\u4ed6\u7edd\u4e0d\u8fdb\u5bab\u6765\u505a\u8fd9\u79cd\u6beb \u65e0\u628a\u63e1\u7684\u4e8b\u3002 \u3000\u3000\u5982\u4eca\uff0c\u4ed6\u7b49\u4e8e\u5df2\u9a91\u864e\u96be\u4e0b\u3002 \u3000\u3000\u738b\u5f64\u600e\u4f1a\u770b\u4e0d\u51fa\u5c0f\u71d5\u5b50\u7684\u4e3a\u96be\uff0c\u5fd9\u8eac\u8eab\u9053\uff1a\u201c\u7980\u4e09\u516c\u4e3b\uff0c\u71d5\u5c11\u4fa0\u7684\u786e\u65e0\u7edd\u5bf9\u628a\u63e1\uff0c\u8fd9\u65b9 \u9762\u4e09\u516c\u4e3b\u5fc5\u987b\u8981\u4e8b\u5148\u5fc3\u91cc\u6709\u6570\uff0c\u5426\u5219\u2026\u2026\u201d \u3000\u3000\u4e09\u516c\u4e3b\u6446\u6446\u624b\u9053\uff1a\u201c\u7528\u4e0d\u7740\u89e3\u91ca\u4e86\uff0c\u6211\u81ea\u7136\u660e\u767d\u3002\u5373\u4f7f\u534e\u9640\u518d\u4e16\u3001\u6241\u9e4a\u590d\u751f\uff0c\u4e5f\u4e0d\u6562\u4fdd \u8bc1\u4e00\u5b9a\u4f1a\u533b\u597d\u75c5\u4eba\u7684\u75c5\u3002\u6211\u65e2\u7136\u7b54\u5e94\u8981\u4ed6\u8fdb\u5bab\uff0c\u5c31\u51b3\u5b9a\u8ba9\u4ed6\u8bd5\u8bd5\u770b\uff0c\u81f3\u4e8e\u662f\u5426\u6709\u6548\uff1f\u90a3\u5c31\u542c \u5929\u7531\u547d\u5427\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u7ec8\u4e8e\u5fc3\u60c5\u8f7b\u677e\u4e0b\u6765\u3002 \u3000\u3000\u738b\u5f64\u5fd9\u8d77\u8eab\u9053\uff1a\u201c\u90a3\u4e48\u5351\u804c\u544a\u9000\uff01\u201d \u3000\u3000\u738b\u5f64\u8d70\u540e\uff0c\u5367\u623f\u5185\u53ea\u5269\u4e0b\u4e09\u516c\u4e3b\u3001\u601c\u82b1\u548c\u5c0f\u71d5\u5b50\u4e09\u4eba\u3002 \u3000\u3000\u601c\u82b1\u95ee\u9053\uff1a\u201c\u71d5\u5c11\u4fa0\u51c6\u5907\u600e\u4e48\u4e3a\u4e09\u516c\u4e3b\u7597\u6cbb\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u5728\u4e0b\u53ea\u80fd\u4ee5\u5929\u96f7\u638c\u529b\u8bd5\u7740\u8eaf\u9664\u4e09\u516c\u4e3b\u4f53\u5185\u866b\u6bd2\uff0c\u5e76\u65e0\u5176\u4ed6\u529e\u6cd5\u3002\u201d \u3000\u3000\u201c\u90a3\u662f\u548c\u4e0a\u6b21\u4e00\u6837\u7f57\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u601c\u82b1\u671b\u671b\u4e09\u516c\u4e3b\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u5c31\u8bf7\u5728\u5e8a\u4e0a\u8eba\u4e0b\u5427\uff01\u201d \u3000\u3000\u4e09\u516c\u4e3b\u5f88\u542c\u8bdd\uff0c\u968f\u5373\u5728\u7ee3\u69bb\u4e0a\u8eba\u4e86\u4e0b\u6765\uff0c\u5e76\u95ed\u4e0a\u773c\u775b\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u7ad9\u8d77\u8eab\u6765\uff0c\u8d70\u8fd1\u69bb\u524d\u3002 \u3000\u3000\u6b64\u523b\uff0c\u4ed6\u5185\u5fc3\u96be\u514d\u53c8\u5f00\u59cb\u7d27\u5f20\u3002 \u3000\u3000\u601c\u82b1\u8f7b\u54b3\u4e86\u58f0\u9053\uff1a\u201c\u662f\u5426\u4ecd\u8981\u4e09\u516c\u4e3b\u8131\u4e0b\u8863\u670d\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u70b9\u70b9\u5934\u9053\uff1a\u201c\u5728\u4e0b\u4e0d\u4f1a\u628a\u8109\uff0c\u5fc5\u987b\u76ee\u89c6\uff0c\u800c\u4e14\u5fc5\u987b\u89e6\u6478\u5230\u60a3\u5904\uff0c\u624d\u80fd\u786e\u5b9a\u866b\u6bd2\u4f4d \u7f6e\uff0c\u53e6\u5916\u2026\u2026\u201d \u3000\u3000\u201c\u53e6\u5916\u8fd8\u6709\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5f85\u4f1a\u513f\u4ee5\u638c\u529b\u8eaf\u866b\u65f6\uff0c\u4e5f\u5fc5\u987b\u624b\u638c\u4e0e\u808c\u80a4\u76f8\u63a5\uff0c\u771f\u6c14\u624d\u80fd\u900f\u5165\u4e09\u516c\u4e3b\u4f53\u5185\u3002\u201d \u3000\u3000\u601c\u82b1\u7565\u4e00\u72b9\u8c6b\uff0c\u5f2f\u4e0b\u8eab\u6765\u95ee\u9053\uff1a\u201c\u71d5\u5c11\u4fa0\u7684\u8bdd\uff0c\u4e09\u516c\u4e3b\u90fd\u542c\u5230\u4e86\u5427\uff1f\u201d \u3000\u3000\u4e09\u516c\u4e3b\u5e76\u672a\u7741\u773c\uff0c\u70b9\u70b9\u5934\u9053\uff1a\u201c\u65e2\u7136\u8981\u6cbb\u75c5\uff0c\u5df2\u65e0\u6cd5\u987e\u8651\u5f97\u592a\u591a\uff0c\u4f55\u51b5\u4e0a\u6b21\u4ed6\u5df2\u8fd9\u6837\u4e3a \u6211\u6cbb\u8fc7\u3002\u201d \u3000\u3000\u601c\u82b1\u76f4\u8d77\u8eab\u5b50\uff0c\u56de\u8fc7\u5934\u6765\u9053\uff1a\u201c\u8fd9\u5c31\u5f00\u59cb\u5417\uff1f\u201d \u3000\u3000\u201c\u6ca1\u4ec0\u4e48\u53ef\u51c6\u5907\u7684\uff0c\u5c31\u8bf7\u59d1\u5a18\u4e3a\u4e09\u516c\u4e3b\u892a\u53bb\u80f8\u8863\u3002\u201d \u3000\u3000\u601c\u82b1\u5306\u5306\u4e3a\u4e09\u516c\u4e3b\u892a\u4e0b\u7f57\u88d9\uff0c\u518d\u5c06\u4e0a\u8863\u548c\u809a\u515c\u5411\u4e0a\u64a9\u8d77\uff0c\u9732\u51fa\u51dd\u8102\u822c\u96ea\u767d\u7c89\u5ae9\u3001\u808c\u8d28\u6676 \u83b9\u7684\u5c0f\u8179\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u4fef\u4e0b\u8eab\u53bb\uff0c\u7f13\u7f13\u63a2\u51fa\u53f3\u624b\u3002 \u3000\u3000\u4ed6\u7684\u624b\u6709\u4e9b\u53d1\u6296\uff0c\u4f46\u795e\u60c5\u5374\u53c8\u5f02\u5e38\u4e25\u8083\u3002 \u3000\u3000\u5728\u8fd9\u79cd\u60c5\u5f62\u4e0b\uff0c\u5f53\u5c0f\u71d5\u5b50\u90a3\u53ea\u624b\u6309\u5728\u4e09\u516c\u4e3b\u7684\u5c0f\u8179\u4e4b\u4e0a\uff0c\u53cd\u800c\u6ca1\u6709\u4ec0\u4e48\u611f\u89c9\u3002 \u3000\u3000\u4ed6\u53ea\u611f\u5230\u8fd9\u662f\u6700\u795e\u5723\u7684\u4e00\u523b\uff0c\u5185\u5fc3\u5e76\u4e0d\u5b58\u4e1d\u6beb\u6742\u5ff5\u3002 \u3000\u3000\u53ef\u80fd\u662f\u4e09\u516c\u4e3b\u5c0f\u8179\u5185\u7684\u6d3b\u866b\u4f53\u79ef\u592a\u5c0f\uff0c\u4ec5\u6041\u89e6\u6478\uff0c\u6839\u672c\u65e0\u6cd5\u5bdf\u89c9\u7a76\u7adf\u5728\u4f55\u5904\uff1f\u601c\u82b1\u4e00\u8fb9 \u5168\u795e\u8d2f\u6ce8\u5728\u5c0f\u71d5\u5b50\u624b\u4e0a\uff0c\u4e00\u8fb9\u95ee\u9053\uff1a\u201c\u6478\u5230\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u53cc\u7709\u7d27\u9501\uff0c\u6447\u6447\u5934\u9053\uff1a\u201c\u611f\u89c9\u4e0d\u51fa\u6765\u3002\u201d \u3000\u3000\u5ffd\u542c\u4e09\u516c\u4e3b\u9053\uff1a\u201c\u5c31\u5728\u4f60\u638c\u5fc3\u7684\u4f4d\u7f6e\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u5fd9\u9053\uff1a\u201c\u4f1a\u4e0d\u4f1a\u79fb\u52a8\uff1f\u201d \u3000\u3000\u201c\u867d\u7136\u4f1a\u79fb\u52a8\uff0c\u4f46\u79fb\u52a8\u5f97\u5f88\u6162\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u5982\u6b64\uff0c\u6211\u73b0\u5728\u5c31\u8981\u53d1\u51fa\u638c\u529b\u4e86\uff0c\u8981\u4e0d\u8981\u4e3a\u4e09\u516c\u4e3b\u70b9\u4e86\u9ebb\u7a74\uff1f\u2019\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u8981\u70b9\u9ebb\u7a74\uff1f\u201d \u3000\u3000\u201c\u5929\u96f7\u638c\u529b\u53d1\u51fa\u540e\uff0c\u7099\u70ed\u96be\u5f53\uff0c\u6211\u62c5\u5fc3\u4e09\u516c\u4e3b\u53d7\u4e0d\u4e86\u3002\u201d \u3000\u3000\u201c\u7b97\u4e0d\u4e86\u4ec0\u4e48\uff01\u6211\u5728\u897f\u57df\u4e24\u5e74\uff0c\u4ec0\u4e48\u82e6\u90fd\u5403\u8fc7\uff0c\u4f60\u53ea\u7ba1\u8fd0\u638c\u5427\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6df1\u6df1\u5438\u4e00\u53e3\u4e39\u7530\u771f\u6c14\uff0c\u53f3\u638c\u7d27\u8d34\u4e09\u516c\u4e3b\u5c0f\u8179\uff0c\u521a\u8981\u8fd0\u51fa\u638c\u529b\uff0c\u5ffd\u542c\u5916\u9762\u811a\u6b65\u58f0 \u54cd\uff01\u63a5\u7740\u662f\u738b\u5f64\u7684\u58f0\u97f3\u9053\uff1a\u201c\u5c0f\u71d5\u5b50\u7528\u4e0d\u7740\u518d\u66ff\u4e09\u516c\u4e3b\u7597\u6bd2\uff0c\u6211\u5df2\u628a\u8001\u5996\u5a46\u6349\u6765\u4e86\u3002\u201d \u3000\u3000\u8fd9\u6d88\u606f\u4e0d\u4f46\u4ee4\u4eba\u60ca\u559c\uff0c\u4e5f\u5927\u5927\u51fa\u4eba\u610f\u5916\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u8fde\u5fd9\u7531\u5e8a\u4e0a\u5750\u8d77\uff0c\u5306\u5306\u7cfb\u597d\u8863\u88d9\uff0c\u4e0b\u5e8a\u518d\u5750\u56de\u7ee3\u58a9\uff0c\u4e00\u9762\u5429\u5490\u601c\u82b1\u9053\uff1a\u201c\u53eb\u738b\u7edf \u9886\u8fdb\u6765\uff01\u201d \u3000\u3000\u601c\u82b1\u7acb\u5373\u5411\u5916\u9ad8\u58f0\u9053\uff1a\u201c\u738b\u7edf\u9886\u8bf7\u8fdb\uff01\u201d \u3000\u3000\u738b\u5f64\u5f88\u5feb\u4fbf\u62bc\u7740\u4e00\u4e2a\u9762\u76ee\u4e11\u6076\u7684\u9ed1\u8863\u8001\u5987\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u8fd9\u9ed1\u8863\u8001\u5987\u679c\u7136\u5c31\u662f\u88ab\u9b54\u6559\u6d3e\u6765\u5bab\u4e2d\u7684\u957f\u8001\u4e4b\u4e00\u7684\u8001\u5996\u5a46\u3002 \u3000\u3000\u738b\u5f64\u4e00\u811a\u8e22\u4e0a\u8001\u5996\u5a46\u817f\u5f2f\uff0c\u559d\u9053\uff1a\u201c\u8dea\u4e0b\uff01\u201d \u3000\u3000\u4e00\u5411\u5014\u5f3a\u65e0\u6bd4\u7684\u8001\u5996\u5a46\uff0c\u6b64\u523b\u5012\u662f\u6e29\u9a6f\u5f97\u5f88\uff0c\u4e00\u58f0\u4e0d\u54cd\u7684\u4fbf\u5728\u4e09\u516c\u4e3b\u9762\u524d\u8dea\u4e0b\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u4e00\u5411\u53d7\u5236\u4e8e\u8001\u5996\u5a46\uff0c\u5982\u4eca\u4e00\u89c1\u5bf9\u65b9\u8dea\u5728\u9762\u524d\uff0c\u53cd\u800c\u6709\u4e9b\u4e0d\u81ea\u7136\uff0c\u5fd9\u95ee\u738b\u5f64\u9053\uff1a \u201c\u662f\u600e\u4e48\u628a\u5979\u6349\u5230\u7684\uff1f\u96be\u9053\u5979\u8fd8\u6f5c\u4f0f\u5728\u5bab\u5ef7\u4e2d\uff1f\u201d \u3000\u3000\u201c\u662f\u4e94\u57ce\u5175\u9a6c\u53f8\u7684\u4eba\u5728\u897f\u5c71\u6349\u5230\u7684\uff0c\u521a\u624d\u624d\u89e3\u6765\u5bab\u4e2d\u3002\u201d \u3000\u3000\u201c\u4f60\u5bf9\u5979\u95ee\u8fc7\u8bdd\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u8fd8\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u4e0d\u5148\u95ee\u53e3\u4f9b\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u66ff\u4e09\u516c\u4e3b\u9a71\u866b\u8981\u7d27\u3002\u201d \u3000\u3000\u201c\u6211\u7684\u4e8b\u53ef\u4ee5\u7f13\u4e00\u7f13\uff0c\u73b0\u5728\u5c31\u4ea4\u7531\u4f60\u5148\u95ee\u95ee\u5979\u7684\u53e3\u4f9b\uff0c\u5c31\u5728\u8fd9\u91cc\u95ee\u3002\u201d \u3000\u3000\u738b\u5f64\u5e94\u4e86\u4e00\u58f0\u201c\u662f\u201d\uff0c\u63a5\u7740\u559d\u9053\uff1a\u201c\u628a\u8138\u8f6c\u8fc7\u6765\uff01\u201d \u3000\u3000\u8001\u5996\u5a46\u4e00\u58f0\u4e0d\u54cd\u7684\u8f6c\u8eab\u8dea\u5411\u738b\u5f64\u3002 \u3000\u3000\u738b\u5f64\u6c89\u58f0\u95ee\u9053\uff1a\u201c\u4e0a\u6b21\u5df2\u7ecf\u653e\u4f60\u8d70\u4e86\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u8981\u8eb2\u5728\u897f\u5c71\uff0c\u662f\u4e0d\u662f\u8fd8\u8981\u4f3a\u673a\u8fdb\u5bab\uff0c \u7ee7\u7eed\u4f5c\u795f\uff1f\u201d \u3000\u3000\u8001\u5996\u5a46\u82e6\u7740\u8138\uff0c\u8fde\u5fd9\u6447\u5934\u9053\uff1a\u201c\u738b\u7edf\u9886\u522b\u7591\u5fc3\uff0c\u8001\u8eab\u7edd\u6ca1\u6709\u8fd9\u610f\u601d\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u6ca1\u6709\u8fd9\u610f\u601d\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u9a6c\u4e0a\u8fd4\u56de\u897f\u57df\uff1f\u201d \u3000\u3000\u201c\u8001\u8eab\u4e0d\u6562\u518d\u56de\u53bb\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u6562\u56de\u53bb\uff1f\u8fd9\u8bdd\u4ec0\u4e48\u610f\u601d\uff1f\u4f60\u4e0d\u662f\u9b54\u6559\u7684\u957f\u8001\u5417\uff1f\u201d \u3000\u3000\u201c\u957f\u8001\u6709\u4ec0\u4e48\u7528\uff0c\u56e0\u4e3a\u6211\u505a\u4e86\u4e00\u4ef6\u80cc\u53db\u4ed6\u4eec\u7684\u4e8b\u3002\u201d \u3000\u3000\u201c\u54ea\u4ef6\u4e8b\uff1f\u201d \u3000\u3000\u201c\u5c31\u662f\u4e0a\u6b21\u4e3a\u4e09\u516c\u4e3b\u9a71\u866b\u7684\u4e8b\u3002\u201d \u3000\u3000\u201c\u4f60\u518d\u8bf4\u660e\u767d\u4e9b\uff01\u201d \u3000\u3000\u201c\u8001\u8eab\u7531\u897f\u57df\u6765\u4e2d\u539f\u65f6\uff0c\u66fe\u53d7\u8fc7\u6559\u4e3b\u6cd5\u8c15\uff0c\u4e07\u4e00\u5728\u5bab\u4e2d\u88ab\u6355\uff0c\u53ea\u6709\u4e00\u6b7b\uff0c\u7edd\u4e0d\u53ef\u4e3a\u4e09\u516c \u4e3b\u9a71\u866b\u3002\u8001\u8eab\u56e0\u5e76\u672a\u9075\u7167\u6cd5\u8c15\u884c\u4e8b\uff0c\u6240\u4ee5\u624d\u4e0d\u6562\u56de\u53bb\uff0c\u8fd9\u4e9b\u5929\u6765\uff0c\u4e00\u76f4\u5728\u897f\u5c71\u85cf\u533f\u3002\u201d \u3000\u3000\u201c\u4eac\u5e08\u79bb\u897f\u57df\u8fe2\u8fe2\u4e07\u91cc\uff0c\u4f60\u4e0d\u56de\u53bb\u5c3d\u7ba1\u4e0d\u56de\u53bb\uff0c\u4f55\u5fc5\u85cf\u533f\uff1f\u201d \u3000\u3000\u201c\u4f60\u738b\u7edf\u9886\u54ea\u91cc\u77e5\u9053\uff0c\u76ee\u524d\u5bab\u4e2d\u4ecd\u6709\u897f\u57df\u9b54\u6559\u6d3e\u6765\u7684\u4eba\u6f5c\u4f0f\uff0c\u53ea\u662f\u4e0d\u6562\u516c\u5f00\u6d3b\u52a8\u800c\u5df2\uff0c \u5373\u4f7f\u4eac\u57ce\u8857\u4e0a\uff0c\u4e5f\u6709\u4ed6\u4eec\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u4f60\u82e5\u56de\u53bb\uff0c\u5c06\u4f1a\u53d7\u5230\u4f55\u79cd\u5904\u5206\uff1f\u201d \u3000\u3000\u201c\u9664\u4e86\u88ab\u5904\u6b7b\uff0c\u6ca1\u7b2c\u4e8c\u6761\u8def\u3002\u201d \u3000\u3000\u8fd9\u65f6\u4e09\u516c\u4e3b\u8bf4\u4e86\u8bdd\uff1a\u201c\u4f60\u867d\u7136\u4e3a\u6211\u9a71\u8fc7\u866b\uff0c\u4f46\u6cbb\u7597\u5f97\u5e76\u4e0d\u5f7b\u5e95\uff0c\u4f60\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u8001\u5996\u5a46\u70b9\u70b9\u5934\u9053\uff1a\u201c\u8001\u8eab\u660e\u767d\uff0c\u4e09\u516c\u4e3b\u5c0f\u8179\u4e4b\u5185\u76ee\u524d\u53c8\u6709\u4e86\u9ec4\u8c46\u5927\u5c0f\u7684\u6d3b\u866b\uff0c\u5bf9\u4e0d \u5bf9\uff1f\u201d \u3000\u3000\u738b\u5f64\u559d\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u8001\u5996\u5a46\uff0c\u4f60\u65e2\u7136\u77e5\u9053\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u66ff\u4e09\u516c\u4e3b\u5f7b\u5e95\u6cbb\u597d\uff1f\u201d \u3000\u3000\u8001\u5996\u5a46\u53cc\u988a\u62bd\u6410\u4e86\u51e0\u4e0b\u9053\uff1a\u201c\u8001\u8eab\u662f\u90a3\u4e48\u60f3\uff0c\u4f46\u5374\u6ca1\u6709\u90a3\u79cd\u80fd\u8010\u3002\u201d \u3000\u3000\u738b\u5f64\u6014\u4e86\u4e00\u6014\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u4e09\u516c\u4e3b\u4f53\u5185\u7684\u6d3b\u866b\u5f53\u521d\u662f\u4f60\u4e0b\u7684\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u66ff\u4e09\u516c\u4e3b\u5f7b \u5e95\u53d6\u51fa\u6765\uff1f\u201d \u3000\u3000\u8001\u5996\u5a46\u55eb\u5685\u7740\u9053\uff1a\u201c\u4e0d\u662f\u8001\u8eab\u4e0b\u7684\uff0c\u4e0d\u8fc7\u8001\u8eab\u5f53\u65f6\u66fe\u5728\u4e00\u65c1\u89c1\u4e60\u3002\u201d \u3000\u3000\u738b\u5f64\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u7a76\u7adf\u662f\u8c01\u4e0b\u7684\uff1f\u201d \u3000\u3000\u201c\u662f\u8001\u8eab\u7684\u5e08\u7236\u3002\u201d \u3000\u3000\u201c\u4f60\u8fd9\u5996\u5a46\u4eca\u5e74\u591a\u5927\u5e74\u7eaa\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e03\u5341\u6574\u5bff\u3002\u201d \u3000\u3000\u738b\u5f64\u5403\u4e86\u4e00\u60ca\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u5e08\u7236\u5c82\u4e0d\u5df2\u662f\u4e0a\u767e\u5e74\u7eaa\u7684\u8001\u5deb\u5a46\u4e86\uff1f\u201d \u3000\u3000\u8001\u5996\u5a46\u731b\u6447\u5176\u5934\u9053\uff1a\u201c\u4f60\u731c\u9519\u4e86\uff0c\u8001\u8eab\u7684\u5e08\u7236\u624d\u4e0d\u8fc7\u4e8c\u5341\u5de6\u53f3\uff0c\u5927\u6982\u548c\u4e09\u516c\u4e3b\u7684\u5e74\u7eaa\u5dee \u4e0d\u591a\u5427\u3002\u201d \u3000\u3000\u738b\u5f64\u8d8a\u53d1\u5403\u60ca\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u8001\u5996\u5a46\u6162\u6761\u65af\u7406\u7684\u9053\uff1a\u201c\u4e00\u70b9\u4e5f\u7528\u4e0d\u7740\u5947\u602a\uff0c\u53e4\u4eba\u8bf4\u5f97\u597d\uff0c\u2018\u6709\u4e09\u5c81\u4e4b\u7fc1\uff0c\u6709\u767e\u5c81\u4e4b \u7ae5\u2019\u3002\u5979\u7684\u5e74\u7eaa\u867d\u7136\u53ea\u80fd\u4f5c\u8001\u8eab\u7684\u5b59\u5973\uff0c\u4f46\u4e00\u8eab\u672c\u9886\u5374\u6bd4\u8001\u8eab\u9ad8\u51fa\u592a\u591a\u3002\u201d \u3000\u3000\u201c\u96be\u9053\u4f60\u5728\u62dc\u4ed6\u4e3a\u5e08\u4e4b\u524d\uff0c\u5c31\u4e0d\u66fe\u4e60\u8fc7\u827a\uff1f\u201d \u3000\u3000\u201c\u81ea\u7136\u4e60\u8fc7\u827a\uff0c\u5979\u7b97\u662f\u8001\u8eab\u7b2c\u4e8c\u4e2a\u5e08\u7236\uff0c\u800c\u4e14\u8001\u8eab\u5411\u5979\u5b66\u7684\u91cd\u70b9\u53ea\u5728\u866b\u672f\u3002\u201d \u3000\u3000\u201c\u4f60\u73b0\u5728\u8bf4\u8bf4\uff0c\u4e0a\u6b21\u4e3a\u4ec0\u4e48\u6ca1\u628a\u4e09\u516c\u4e3b\u4f53\u5185\u7684\u866b\u6bd2\u5f7b\u5e95\u6cbb\u597d\uff1f\u201d \u3000\u3000\u201c\u8001\u8eab\u521a\u624d\u4e0d\u662f\u8bf4\u8fc7\u5417\uff1f\u662f\u5fc3\u6709\u4f59\u800c\u529b\u4e0d\u8db3\u3002\u201d \u3000\u3000\u8001\u5996\u5a46\u987f\u4e86\u4e00\u987f\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u5176\u5b9e\u8001\u8eab\u4e0a\u6b21\u5df2\u5c3d\u4e86\u6700\u5927\u7684\u52aa\u529b\uff0c\u5316\u89e3\u4e86\u9e3d\u86cb\u5927\u5c0f\u7684\u6d3b \u866b\u3002\u201d \u3000\u3000\u738b\u5f64\u53cc\u76ee\u7d27\u77aa\u7740\u8001\u5996\u5a46\u9053\uff1a\u201c\u53ef\u662f\u4e09\u516c\u4e3b\u8eab\u4e0a\u76ee\u524d\u53c8\u6709\u4e00\u4e2a\u65b0\u7684\u6d3b\u866b\u4e86\u3002\u201d \u3000\u3000\u201c\u8001\u8eab\u77e5\u9053\uff0c\u90a3\u662f\u866b\u79cd\uff0c\u4f1a\u6162\u6162\u957f\u5927\u3002\u201d \u3000\u3000\u201c\u4f60\u4e0a\u6b21\u4e3a\u4ec0\u4e48\u4e0d\u66ff\u4e09\u516c\u4e3b\u628a\u866b\u4e00\u8d77\u53d6\u51fa\uff1f\u201d \u3000\u3000\u201c\u8001\u8eab\u8bf4\u8fc7\uff0c\u6ca1\u6709\u8fd9\u79cd\u80fd\u8010\uff0c\u90a3\u866b\u79cd\u4e5f\u53eb\u866b\u6839\uff0c\u552f\u6709\u4eb2\u624b\u4e0b\u866b\u7684\u4eba\uff0c\u624d\u6709\u529e\u6cd5\u6d88\u9664\u6389 \u5b83\u3002\u201d\u2019\u738b\u5f64\u671b\u4e86\u5c0f\u71d5\u5b50\u4e00\u773c\u9053\uff1a\u201c\u4e2d\u539f\u6b66\u6797\u7b2c\u4e00\u9ad8\u624b\u5929\u96f7\u8001\u4eba\u7684\u5929\u96f7\u795e\u638c\uff0c\u4f60\u4e00\u5b9a\u542c\u8bf4\u8fc7 \u5427\uff1f\u201d \u3000\u3000\u8001\u5996\u5a46\u70b9\u70b9\u5934\u9053\uff1a\u201c\u8001\u8eab\u5f53\u7136\u542c\u8bf4\u8fc7\uff0c\u4f60\u4e3a\u4ec0\u4e48\u5ffd\u7136\u63d0\u8d77\u5929\u96f7\u8001\u4eba\uff1f\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u5982\u679c\u4ee5\u5929\u96f7\u8001\u4eba\u7684\u5929\u96f7\u795e\u638c\uff0c\u5c06\u795e\u638c\u5185\u529b\u900f\u5165\u4e09\u516c\u4e3b\u4f53\u5185\uff0c\u662f\u5426\u80fd\u5c06\u90a3\u866b\u79cd \u6d88\u9664\u5316\u89e3\uff1f\u201d \u3000\u3000\u8001\u5996\u5a46\u60f3\u4e86\u60f3\u9053\uff1a\u201c\u8fd9\u4e2a\u8001\u8eab\u4e0d\u77e5\u9053\u3002\u4e0d\u8fc7\u636e\u8001\u8eab\u5224\u65ad\uff0c\u90a3\u4e5f\u8bb8\u6839\u672c\u662f\u4e0d\u53ef\u80fd\u7684\u4e8b\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4e09\u516c\u4e3b\u4f53\u5185\u7684\u866b\u79cd\u6297\u6027\u548c\u8010\u6027\u7279\u5f3a\uff0c\u5373\u4f7f\u4e22\u5230\u706b\u91cc\u4e5f\u65e0\u6cd5\u70e7\u6b7b\u5b83\uff0c\u5929\u96f7\u638c\u529b\u900f\u5165 \u4f53\u5185\uff0c\u4e0d\u8fc7\u662f\u4e00\u79cd\u9644\u6709\u7f61\u6c14\u7684\u70ed\u529b\uff0c\u603b\u4e0d\u80fd\u80dc\u8fc7\u70c8\u706b\u5427\uff1f\u90a3\u6837\u505a\uff0c\u53ea\u6015\u53d7\u5bb3\u7684\u53cd\u800c\u662f\u4e09\u516c \u4e3b\u3002\u201d \u3000\u3000\u8001\u5996\u5a46\u7684\u5206\u6790\uff0c\u4e0d\u80fd\u8bf4\u6beb\u65e0\u9053\u7406\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u5c0f\u71d5\u5b50\u4ee5\u5929\u96f7\u638c\u529b\u4e3a\u4e09\u516c\u4e3b\u9a71\u866b\u7684\u4e8b\uff0c\u52bf\u5fc5\u505c\u6b62\u8fdb\u884c\u3002 \u3000\u3000\u5ba4\u5185\u5f00\u59cb\u6c89\u5bc2\uff0c\u4f3c\u4e4e\u8c01\u4e5f\u60f3\u4e0d\u51fa\u8be5\u518d\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u76f4\u5230\u4e00\u76cf\u8336\u5de5\u592b\u4e4b\u540e\uff0c\u4e09\u516c\u4e3b\u624d\u95ee\u8001\u5996\u5a46\u9053\uff1a\u201c\u4f60\u90a3\u5e08\u7236\u76ee\u524d\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u5e0c\u671b\u4f60\u80fd\u544a \u8bc9\u6211\uff01\u201d \u3000\u3000\u8001\u5996\u5a46\u4e0d\u5047\u601d\u7d22\u7684\u9053\uff1a\u201c\u8001\u8eab\u867d\u7136\u548c\u5979\u4e45\u672a\u8054\u7edc\uff0c\u4f46\u9884\u6599\u4e2d\u5979\u4e00\u5b9a\u8fd8\u5728\u4e2d\u539f\u3002\u201d \u3000\u3000\u201c\u4e2d\u539f\u592a\u5927\u4e86\uff0c\u600e\u6837\u624d\u80fd\u627e\u5230\u5979\uff1f\u201d \u3000\u3000\u201c\u60f3\u627e\u5230\u5979\u5e76\u4e0d\u96be\uff0c\u56e0\u4e3a\u5979\u548c\u6cb3\u5357\u9a6c\u5de1\u629a\u4e00\u76f4\u4fdd\u6301\u7740\u5bc6\u5207\u8054\u7cfb\uff0c\u5341\u6709\u516b\u4e5d\u5c31\u5728\u9a6c\u5de1\u629a\u7684 \u629a\u53f0\u8859\u7f72\u4e2d\u3002\u201d \u3000\u3000\u201c\u4f60\u6562\u8fd9\u6837\u65ad\u5b9a\uff1f\u201d \u3000\u3000\u201c\u8001\u8eab\u53ea\u80fd\u8bf4\u5341\u6709\u516b\u4e5d\u3002\u201d \u3000\u3000\u4e09\u516c\u4e3b\u5411\u738b\u5f64\u5429\u5490\u9053\uff1a\u201c\u6682\u65f6\u628a\u5979\u62bc\u56de\u53bb\uff0c\u4e00\u5b9a\u8981\u4e25\u5bc6\u770b\u5b88\u3002\u201d \u3000\u3000\u738b\u5f64\u5e94\u4e86\u4e00\u58f0\u201c\u662f\u201d\u3002 \u3000\u3000\u738b\u516c\u4e3b\u53c8\u9053\uff1a\u201c\u542c\u8bf4\u6c5f\u5343\u91cc\u5927\u4fa0\u4e5f\u8fdb\u5bab\u4e86\uff0c\u5f85\u4f1a\u513f\u4f60\u548c\u4ed6\u4e00\u8d77\u518d\u6765\u89c1\u6211\uff0c\u71d5\u5c11\u4fa0\u4e5f\u4e00\u8d77 \u6765\u3002\u201d \u3000\u3000\u738b\u5f64\u4eb2\u81ea\u628a\u8001\u5996\u5a46\u62bc\u8fdb\u4f4f\u5904\u9644\u8fd1\u4e00\u95f4\u7a7a\u5c4b\uff0c\u4e0d\u4f46\u4e0a\u4e86\u7ed1\uff0c\u800c\u4e14\u70b9\u4e86\u7a74\uff0c\u518d\u4ea4\u5f85\u4e24\u540d\u5fc3\u8179 \u624b\u4e0b\u8f6e\u6d41\u770b\u5b88\u3002 \u3000\u3000\u7a0d\u4e8b\u4f11\u606f\u4e4b\u540e\uff0c\u4fbf\u548c\u6c5f\u5343\u91cc\u3001\u5c0f\u71d5\u5b50\u4e00\u8d77\u518d\u6765\u5230\u542c\u8749\u9662\u3002 \u3000\u3000\u6709\u5173\u8001\u5996\u5a46\u88ab\u64d2\u540e\u7684\u7ecf\u8fc7\uff0c\u738b\u5f64\u5df2\u5168\u544a\u8bc9\u4e86\u6c5f\u5343\u91cc\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u5fc3\u91cc\u6709\u6570\uff0c\u4e09\u516c\u4e3b\u8981\u4ed6\u664b\u89c1\uff0c\u4e0d\u5916\u662f\u542c\u53d6\u4ed6\u7684\u610f\u89c1\uff0c\u4ee5\u4fbf\u96c6\u601d\u5e7f\u76ca\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u5df2\u5728\u5ba2\u5385\u7b49\u5019\uff0c\u8eab\u65c1\u4ec5\u6709\u601c\u82b1\u4e00\u4eba\u966a\u4f8d\u3002 \u3000\u3000\u4e00\u89c1\u738b\u5f64\u7b49\u4eba\u8fdb\u6765\uff0c\u4e09\u516c\u4e3b\u8fde\u5fd9\u8d77\u8eab\u76f8\u8fce\uff0c\u4e00\u8fb9\u7b11\u5bb9\u53ef\u63ac\u7684\u9053\uff1a\u201c\u90fd\u662f\u76f8\u5904\u5df2\u4e45\u7684\u81ea\u5df1 \u4eba\uff0c\u4e0d\u5fc5\u518d\u89c1\u793c\u4e86\uff0c\u5927\u5bb6\u5feb\u5feb\u8bf7\u5750\u3002\u201d \u3000\u3000\u843d\u5750\u540e\uff0c\u601c\u82b1\u5fd9\u7740\u7aef\u4e0a\u8336\u6765\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u4ee5\u4e3b\u4eba\u8eab\u4efd\u9996\u5148\u9053\uff1a\u201c\u6211\u51b3\u5b9a\u518d\u5230\u5f00\u5c01\u4e00\u8d9f\uff0c\u76ee\u7684\u662f\u8981\u627e\u5230\u8001\u5996\u5a46\u7684\u5e08\u7236\uff0c\u4ee5\u4fbf \u5f7b\u5e95\u6cbb\u7597\u8eab\u4e0a\u866b\u6bd2\uff0c\u9664\u738b\u7edf\u9886\u968f\u884c\u4e4b\u5916\uff0c\u4e5f\u5e0c\u671b\u6c5f\u53d4\u53d4\u548c\u71d5\u5c11\u4fa0\u4ecd\u76f8\u52a9\u4e00\u81c2\u4e4b\u529b\uff0c\u4e0d\u77e5\u4e8c\u4f4d \u662f\u5426\u80af\u7b54\u5e94\u6211\u8fd9\u9879\u8981\u6c42\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u7528\u4e0d\u7740\u8fd9\u4e48\u5ba2\u6c14\uff0c\u8fd9\u662f\u8349\u6c11\u4eec\u4e49\u4e0d\u5bb9\u8f9e\u7684\u4e8b\uff0c\u53ea\u662f\u6709\u51e0\u4ef6\u4e8b\u4e09\u516c\u4e3b \u5fc5\u987b\u591a\u52a0\u8003\u8651\u3002\u201d \u3000\u3000\u4e09\u516c\u4e3b\u7d27\u76ef\u7740\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u90a3\u5c31\u7531\u6c5f\u53d4\u53d4\u76f4\u63a5\u8bf4\u51fa\u6765\u5427\uff01\u6211\u8bf7\u6c5f\u53d4\u53d4\u6765\uff0c\u4e0d\u5916\u662f\u542c\u53d6\u4f60 \u7684\u9ad8\u89c1\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u9ad8\u89c1\u4e0d\u6562\u5f53\uff0c\u6211\u662f\u60f3\u5230\u8fd9\u6b21\u4e0a\u5f00\u5c01\uff0c\u4e07\u4e00\u9a6c\u5de1\u629a\u53d8\u4e86\u8138\uff0c\u628a\u4e09\u516c\u4e3b\u548c\u738b\u7edf \u9886\u4ee5\u53ca\u5e26\u53bb\u7684\u5927\u5185\u9ad8\u624b\u5168\u6570\u6263\u62bc\u8d77\u6765\uff0c\u7136\u540e\u8fdb\u4e00\u6b65\u5411\u7687\u4e0a\u8981\u631f\uff0c\u90a3\u65f6\u8be5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u4e09\u516c\u4e3b\u76b1\u8d77\u9edb\u7709\u9053\uff1a\u201c\u6709\u8fd9\u79cd\u53ef\u80fd\u5417\uff1f\u201d \u3000\u3000\u201c\u8349\u6c11\u7684\u610f\u601d\uff0c\u662f\u4e3a\u9632\u4e07\u4e00\uff0c\u4e09\u516c\u4e3b\u5fc5\u987b\u5148\u6709\u5fc3\u7406\u51c6\u5907\uff0c\u800c\u4e14\u8fd9\u79cd\u53ef\u80fd\u6027\u5f88\u5927\u3002\u201d \u3000\u3000\u201c\u6c5f\u53d4\u53d4\u6839\u636e\u4ec0\u4e48\u4f1a\u6709\u8fd9\u79cd\u60f3\u6cd5\uff1f\u201d \u3000\u3000\u201c\u4e8b\u5b9e\u6446\u5728\u9762\u524d\uff0c\u8fd9\u4e3e\u52a8\u4e0e\u9020\u53cd\u4f55\u5f02\uff1f\u82e5\u8bba\u56fd\u6cd5\uff0c\u5c31\u8be5\u8bdb\u706d\u4e5d\u65cf\uff0c\u4ed6\u4f55\u5c1d\u4e0d\u77e5\u9053\uff0c\u8fd9\u662f \u7687\u4e0a\u4e3a\u4e86\u987e\u5168\u5927\u5c40\uff0c\u624d\u6682\u65f6\u5bb9\u5fcd\u4e0b\u6765\uff0c\u5176\u5b9e\u9a6c\u6587\u4e2d\u8fd9\u4e24\u5e74\u6765\uff0c\u4e00\u76f4\u662f\u5904\u5728\u63d0\u5fc3\u540a\u62c5\u7684\u72b6\u51b5 \u4e2d\uff0c\u4ed6\u81ea\u5df1\u5fc3\u91cc\u6709\u6570\uff0c\u4ed6\u90a3\u6cb3\u5357\u5de1\u629a\u6839\u672c\u5df2\u7ecf\u4e0d\u53ef\u80fd\u7a33\u505a\u4e0b\u53bb\uff0c\u968f\u65f6\u968f\u5730\u90fd\u6709\u5927\u7978\u4e34\u5934\u7684\u4e8b \u53d1\u751f\u3002\u201d \u3000\u3000\u4e09\u516c\u4e3b\u70b9\u70b9\u5934\u9053\uff1a\u201c\u8fd9\u8bdd\u6709\u9053\u7406\u3002\u53ef\u662f\u4ed6\u82e5\u518d\u635e\u4e86\u6211\uff0c\u5927\u7978\u4e34\u5934\u7684\u4e8b\uff0c\u5c82\u4e0d\u53cd\u800c\u8981\u52a0\u5feb \u5230\u6765\uff1f\u201d \u3000\u3000\u201c\u4ed6\u8fd9\u6837\u505a\uff0c\u867d\u7136\u7b49\u4e8e\u662f\u5b64\u6ce8\u4e00\u63b7\uff0c\u4f46\u662f\u81f3\u5c11\u624b\u4e2d\u63e1\u6709\u4e86\u4eba\u8d28\uff0c\u7167\u6837\u53ef\u4ee5\u518d\u5411\u7687\u4e0a\u63d0\u51fa \u6761\u4ef6\u3002\u201d \u3000\u3000\u4e09\u516c\u4e3b\u4f3c\u4e4e\u5df2\u5b8c\u5168\u540c\u610f\u4e86\u6c5f\u5343\u91cc\u7684\u8bdd\uff0c\u89c6\u7ebf\u8f6c\u5411\u738b\u5f64\uff0c\u5374\u6ca1\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u738b\u5f64\u8eac\u8eab\u9053\uff1a\u201c\u5176\u5b9e\u6c5f\u5144\u8fd9\u79cd\u987e\u8651\uff0c\u5351\u804c\u4e5f\u65e9\u5df2\u60f3\u5230\u3002\u201d \u3000\u3000\u201c\u738b\u7edf\u9886\u65e2\u7136\u60f3\u5230\u4e86\uff0c\u662f\u5426\u5df2\u6709\u5e94\u5bf9\u4e4b\u7b56\uff1f\u201d \u3000\u3000\u201c\u9664\u4e86\u591a\u5e26\u9ad8\u624b\u4e4b\u5916\uff0c\u548c\u4e0a\u6b21\u4e00\u6837\uff0c\u518d\u8bf7\u7687\u4e0a\u9881\u4e00\u9053\u8c03\u52a8\u5c11\u6797\u3001\u6b66\u5f53\u3001\u4e10\u5e2e\u7684\u5bc6\u65e8\uff0c\u5230 \u8fbe\u6cb3\u5357\u540e\uff0c\u5148\u4e0e\u5c11\u6797\u3001\u6b66\u5f53\u3001\u4e10\u5e2e\u4e09\u5904\u53d6\u5f97\u8054\u7cfb\uff0c\u7136\u540e\u518d\u89c1\u9a6c\u5de1\u629a\u3002\u4e07\u4e00\u53d1\u751f\u53d8\u6545\uff0c\u4ee5\u5c11 \u6797\u3001\u6b66\u5f53\u3001\u4e10\u5e2e\u4e09\u65b9\u9762\u7684\u9ad8\u624b\u8054\u5408\u8d77\u6765\uff0c\u8db3\u53ef\u5bf9\u4ed8\u5f97\u4e86\u9a6c\u6587\u4e2d\u624b\u4e0b\u7684\u9a6c\u6b65\u7cbe\u5175\u4e86\uff0c\u53ea\u662f\u2026\u2026 \u53ea\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u53ea\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4e09\u516c\u4e3b\u548c\u5351\u804c\u843d\u5165\u9a6c\u6587\u4e2d\u624b\u4e2d\u540e\uff0c\u5409\u51f6\u5c31\u5f88\u96be\u9884\u6599\u4e86\u3002\u201d \u3000\u3000\u4e09\u516c\u4e3b\u6b63\u8272\u9053\uff1a\u201c\u90a3\u4e5f\u7b97\u4e0d\u4e86\u4ec0\u4e48\uff0c\u5982\u679c\u80fd\u4e3a\u6211\u7236\u738b\u7684\u6c5f\u5c71\u793e\u7a37\u9664\u53bb\u9a6c\u6587\u4e2d\u8fd9\u4e00\u5fc3\u8179\u5927 \u60a3\uff0c\u6211\u5c31\u7b97\u820d\u53bb\u8fd9\u4e00\u6761\u547d\u4e5f\u503c\u5f97\u3002\u53ea\u662f\u738b\u7edf\u9886\u4f60\u548c\u5176\u4ed6\u7684\u5927\u5185\u5f1f\u5144\u4eec\u56e0\u6211\u800c\u727a\u7272\uff0c\u5c06\u4f7f\u6211\u4e8e \u5fc3\u96be\u5b89\u3002\u201d \u3000\u3000\u738b\u5f64\u76f4\u4e86\u76f4\u8170\uff0c\u4e00\u633a\u80f8\u9053\uff1a\u201c\u5351\u804c\u4eec\u4e3a\u4e86\u4fdd\u5168\u5927\u660e\u6c5f\u5c71\uff0c\u4e3a\u56fd\u6350\u8eaf\uff0c\u6839\u672c\u5c31\u662f\u5e94\u8be5\u7684\uff0c \u8fde\u4e09\u516c\u4e3b\u4ee5\u91d1\u679d\u7389\u53f6\u4e4b\u8eab\uff0c\u90fd\u80fd\u4e0d\u4e3a\u751f\u6b7b\u8ba1\u8f83\uff0c\u5351\u804c\u4eec\u7684\u4e00\u6761\u547d\u53c8\u7b97\u5f97\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u6b64\u53bb\u6cb3\u5357\uff0c\u738b\u8001\u5f1f\u522b\u5fd8\u4e86\u628a\u90a3\u8001\u5996\u5a46\u4e00\u8d77\u5e26\u53bb\u3002\u201d \u3000\u3000\u738b\u5f64\u6709\u4e9b\u4e0d\u89e3\uff0c\u95ee\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u8981\u5e26\u7740\u5979\uff1f\u201d \u3000\u3000\u201c\u738b\u8001\u5f1f\u53ef\u8ba4\u8bc6\u5979\u5e08\u7236\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u4e0d\u8ba4\u8bc6\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c31\u5bf9\u4e86\u3002\u82e5\u9a6c\u5de1\u629a\u4ea4\u51fa\u7684\u4eba\u4e0d\u662f\u5979\u5e08\u7236\uff0c\u54b1\u4eec\u53c8\u5982\u4f55\u8fa8\u8ba4\uff0c\u4e3a\u4e09\u516c\u4e3b\u6d88\u9664\u4f53\u5185\u7684\u866b \u662f\u4ef6\u5927\u4e8b\uff0c\u600e\u53ef\u9a6c\u864e\uff0c\u6240\u4ee5\u5fc5\u987b\u5e26\u7740\u5979\u624d\u884c\u3002\u201d \u3000\u3000\u738b\u5f64\u900f\u7740\u72b9\u8c6b\u795e\u60c5\u9053\uff1a\u201c\u6c5f\u5144\u7684\u8bdd\u56fa\u7136\u6709\u7406\uff0c\u4f46\u5144\u5f1f\u5374\u4e0d\u80fd\u4e0d\u53e6\u6709\u987e\u8651\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u4e0d\u89e3\u9053\uff1a\u201c\u738b\u8001\u5f1f\u6709\u4ec0\u4e48\u987e\u8651\uff1f\u201d \u3000\u3000\u201c\u8001\u5996\u5a46\u662f\u653e\u866b\u9ad8\u624b\uff0c\u54b1\u4eec\u82e5\u5e26\u7740\u5979\uff0c\u4e00\u8def\u4e0a\u5fc5\u987b\u671d\u5915\u5171\u540c\u884c\u52a8\uff0c\u4e07\u4e00\u5979\u5bf9\u54b1\u4eec\u8fd9\u4e9b\u4eba \u52a8\u4e86\u624b\u811a\uff0c\u540e\u679c\u5c82\u4e0d\u5c31\u592a\u4e25\u91cd\u4e86\u5417\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u5fcd\u4e0d\u4f4f\u7b11\u9053\uff1a\u201c\u738b\u8001\u5f1f\u987e\u8651\u5f97\u672a\u514d\u592a\u8fc7\u4efd\u4e86\uff0c\u53ea\u8981\u54b1\u4eec\u4e8b\u5148\u786e\u5b9e\u505a\u597d\u68c0\u67e5\u548c\u9632\u8303 \u5de5\u4f5c\uff0c\u8001\u5996\u5a46\u7eb5\u7136\u662f\u5929\u4e0b\u653e\u866b\u7b2c\u4e00\u9ad8\u624b\uff0c\u4e5f\u662f\u65e0\u7528\u6b66\u4e4b\u5730\u3002\u201d \u3000\u3000\u738b\u5f64\u76b1\u4e86\u76b1\u7709\u9053\uff1a\u201c\u5144\u5f1f\u8fd8\u662f\u542c\u4e0d\u61c2\u6c5f\u89c1\u8fd9\u8bdd\u7684\u610f\u601d\uff1f\u201d \u3000\u3000\u201c\u6240\u8c13\u653e\u866b\u4e0b\u6bd2\uff0c\u5fc5\u5b9a\u8981\u6709\u866b\u53ef\u653e\uff0c\u6709\u6bd2\u53ef\u4e0b\uff0c\u5982\u679c\u65e0\u866b\u65e0\u6bd2\uff0c\u5979\u672c\u9886\u518d\u5927\uff0c\u4e5f\u4e0d\u53ef\u80fd \u6041\u7a7a\u53d8\u9020\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u6c5f\u5144\u7684\u610f\u601d\uff0c\u662f\u8981\u5148\u4e3a\u8001\u5996\u5a46\u641c\u8eab\uff1f\u201d \u3000\u3000\u201c\u5c82\u6b62\u641c\u8eab\uff0c\u5fc5\u987b\u5f7b\u5e95\u6e05\u67e5\uff0c\u5c06\u8001\u5996\u5a46\u8131\u5149\u8863\u670d\uff0c\u5728\u6d74\u7f38\u91cc\u81f3\u5c11\u6ce1\u5979\u4e00\u5929\u4e00\u591c\uff0c\u7136\u540e\u518d \u628a\u5979\u8131\u4e0b\u7684\u8863\u670d\u6d88\u6bc1\uff0c\u4e3a\u5979\u6362\u4e0a\u4e00\u5957\u95f2\u8863\u670d\u3002\u5982\u6b64\u4e00\u6765\uff0c\u8001\u5996\u5a46\u8fd8\u51ed\u4ec0\u4e48\u518d\u653e\u866b\u4e0b\u6bd2\uff1f\u201d \u3000\u3000\u4e09\u516c\u4e3b\u70b9\u70b9\u5934\u9053\uff1a\u201c\u6c5f\u53d4\u53d4\u8bf4\u5f97\u5bf9\uff0c\u8fd9\u4ef6\u4e8b\u6211\u4f1a\u4ea4\u5f85\u601c\u82b1\u7167\u529e\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u4e0d\u59a8\u518d\u52a0\u6d3e\u4e24\u4f4d\u5bab\u5973\u534f\u52a9\uff0c\u6700\u4e3b\u8981\u7684\u662f\u5f7b\u5e95\u68c0\u67e5\u5979\u5168\u8eab\u3002\u201d \u3000\u3000\u4e09\u516c\u4e3b\u7728\u7728\u7738\u5b50\u9053\uff1a\u201c\u8131\u4e86\u5979\u7684\u8863\u670d\uff0c\u8eab\u4e0a\u4fbf\u4e00\u76ee\u4e86\u7136\uff0c\u8fd8\u6709\u4ec0\u4e48\u53ef\u68c0\u67e5\u7684\u5462\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u5e72\u54b3\u4e00\u58f0\u9053\uff1a\u201c\u636e\u6211\u6240\u77e5\uff0c\u866b\u6bd2\u4f53\u79ef\u5c0f\uff0c\u5f88\u5bb9\u6613\u85cf\u5728\u8eab\u4e0a\u7684\u9690\u79c1\u4e4b\u5904\uff0c\u8fde\u5934\u53d1\u3001 \u6307\u7532\u4ee5\u53ca\u811a\u8dbe\u6216\u809a\u8110\u3001\u814b\u4e0b\u90fd\u53ef\u85cf\u653e\uff0c\u8fd9\u4e9b\u5730\u65b9\u5fc5\u987b\u8be6\u7ec6\u641c\u67e5\u3002\u201d \u3000\u3000\u201c\u82e5\u5c0f\u5f97\u8089\u773c\u65e0\u6cd5\u770b\u89c1\uff0c\u53c8\u600e\u4e48\u641c\u67e5\u5462\uff1f\u201d \u3000\u3000\u201c\u6709\u529e\u6cd5\uff0c\u800c\u4e14\u529e\u6cd5\u5f88\u7b80\u5355\uff0c\u628a\u827e\u8349\u716e\u6210\u6c64\uff0c\u52a0\u4e0a\u849c\u6c41\u3001\u96c4\u9ec4\u3001\u96c4\u9e21\u8840\uff0c\u8fd9\u4e9b\u90fd\u662f\u866b\u6bd2 \u7684\u514b\u661f\uff0c\u53ea\u8981\u628a\u8fd9\u4e9b\u4e1c\u897f\u653e\u8fdb\u6d74\u7f38\uff0c\u8ba9\u8001\u5996\u5a46\u6d78\u6ce1\u4e0a\u4e00\u5929\u4e00\u591c\uff0c\u5979\u5373\u4f7f\u8eab\u4e0a\u9690\u79c1\u4e4b\u5904\u85cf\u6709\u866b \u6bd2\uff0c\u4e5f\u5fc5\u4f1a\u5168\u6570\u6b7b\u5149\u3002 \u3000\u3000\u201c\u597d\uff01\u5c31\u7167\u6c5f\u53d4\u53d4\u7684\u5429\u5490\u53bb\u529e\u3002\u201d \u3000\u3000\u738b\u5f64\u95ee\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u51c6\u5907\u4ec0\u4e48\u65f6\u5019\u542f\u7a0b\uff1f\u201d \u3000\u3000\u4e09\u516c\u4e3b\u9053\uff1a\u201c\u5f53\u7136\u662f\u8d8a\u5feb\u8d8a\u597d\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u5351\u804c\u56de\u5934\u5c31\u5f00\u59cb\u51c6\u5907\uff0c\u516c\u4e3b\u51fa\u5bab\uff0c\u5351\u804c\u968f\u884c\u662f\u4ef6\u5927\u4e8b\uff0c\u5351\u804c\u8fd8\u8981\u594f\u660e\u7687\u4e0a\u624d\u6210\uff0c \u4e09\u516c\u4e3b\u662f\u5426\u4e5f\u8be5\u89c1\u89c1\u7687\u4e0a\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u5f53\u7136\u4f1a\u5148\u53bb\u8c12\u89c1\u7236\u738b\u7684\uff0c\u6211\u4eec\u662f\u7236\u5973\uff0c\u89c1\u9762\u6bd4\u8d77\u4f60\u4eec\u8981\u65b9\u4fbf\u4e9b\u3002\u201d \u3000\u3000\u4e8b\u60c5\u8fdb\u884c\u5f97\u5f88\u987a\u5229\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u51fa\u5bab\u7684\u4e8b\uff0c\u7687\u4e0a\u5b8c\u5168\u540c\u610f\uff0c\u5e76\u9881\u4e0b\u4e00\u9053\u5bc6\u65e8\uff0c\u7531\u738b\u5f64\u5e26\u5728\u8eab\u8fb9\uff0c\u53ef\u968f\u65f6\u5f81\u8c03\u5c11 \u6797\u3001\u6b66\u5f53\u3001\u4e10\u5e2e\u65b9\u9762\u6d3e\u51fa\u9ad8\u624b\u5e94\u53d8\u3002 \u3000\u3000\u4e09\u65e5\u540e\uff0c\u4e09\u516c\u4e3b\u4e00\u884c\u4eba\u4f17\u4fbf\u51fa\u5bab\u517c\u7a0b\u5357\u4e0b\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u8fd9\u6b21\u51fa\u4eac\uff0c\u968f\u884c\u4eba\u5458\u5728\u5927\u5185\u65b9\u9762\u53ea\u6709\u738b\u5f64\u3001\u97e9\u6d9b\u3001\u71d5\u98de\u3001\u8d75\u4fdd\u3001\u9648\u5b8f\u4ee5\u53ca\u968f\u4f8d\u4e09 \u516c\u4e3b\u7684\u5bab\u5973\u601c\u82b1\u3002 \u3000\u3000\u53e6\u5916\u4fbf\u662f\u4ee5\u6c5f\u5343\u91cc\u4e3a\u9996\u968f\u884c\u7684\u9ed1\u589e\u3001\u767d\u7fbd\u3001\u738b\u91cd\u5c71\u4ee5\u53ca\u5c0f\u71d5\u5b50\u71d5\u6625\u98ce\u3002 \u3000\u3000\u5f53\u7136\uff0c\u6700\u5c11\u4e0d\u4e86\u7684\u8fd8\u6709\u4e00\u4e2a\u5e74\u7eaa\u6700\u8001\u7684\u8001\u5996\u5a46\u3002 \u3000\u3000\u4e00\u884c\u4eba\u4f17\u4e3a\u514d\u4e8e\u62db\u6447\uff0c\u90fd\u6362\u4e86\u4fbf\u88c5\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u548c\u601c\u82b1\u4e58\u5750\u9a6c\u8f66\uff0c\u5176\u4f59\u7684\u4eba\u6709\u7684\u4e58\u9a6c\uff0c\u6709\u7684\u6b65\u884c\uff1b\u8001\u5996\u5a46\u867d\u7136\u5df2\u4e03\u5341\u9ad8\u9f84\uff0c\u4e5f\u662f \u4e24\u6761\u817f\u8d70\u8def\u5728\u884c\u5217\u4e4b\u4e2d\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u4e00\u884c\uff0c\u5e76\u672a\u76f4\u63a5\u8fdb\u5165\u5f00\u5c01\uff0c\u800c\u662f\u5148\u884c\u62b5\u8fbe\u5d69\u5c71\u5c11\u6797\u5bfa\uff0c\u6682\u4f4f\u5c11\u6797\u5bfa\u4e2d\uff0c\u9664\u4e86\u548c\u5c11 \u6797\u638c\u95e8\u4eba\u5927\u667a\u7985\u5e08\u7ea6\u5b9a\u5e94\u53d8\u884c\u52a8\u5916\uff0c\u5e76\u4ea4\u5f85\u5927\u667a\u7985\u5e08\u6d3e\u51fa\u5f1f\u5b50\u548c\u6b66\u5f53\u4ee5\u53ca\u4e10\u5e2e\u65b9\u9762\u53d6\u5f97\u8054 \u7cfb\uff0c\u76f4\u7b49\u6b66\u5f53\u548c\u4e10\u5e2e\u65b9\u9762\u6709\u4e86\u56de\u5e94\uff0c\u624d\u539f\u73ed\u4eba\u9a6c\u5f80\u5f00\u5c01\u884c\u53bb\u3002 \u3000\u3000\u738b\u5f64\u5305\u4e0b\u4e86\u5f00\u5c01\u6700\u5927\u7684\u4e00\u5bb6\u5ba2\u6808\u2014\u2014\u4e1c\u90fd\u8001\u6808\u7684\u6574\u5e62\u540e\u9662\uff0c\u6240\u6709\u7684\u4eba\u5168\u4f4f\u5728\u540e\u9662\u91cc\uff0c\u8fde \u7528\u9910\u4e5f\u5728\u540e\u9662\u3002 \u3000\u3000\u5982\u6b64\u4e0d\u5916\u662f\u4e0d\u4e0e\u5176\u4ed6\u5ba2\u4eba\u63a5\u89e6\u3002 \u3000\u3000\u5ba2\u6808\u7684\u638c\u67dc\u4ee5\u53ca\u5e97\u4f19\uff0c\u6839\u672c\u5f04\u4e0d\u6e05\u540e\u9662\u4f4f\u7684\u5ba2\u4eba\u4eec\u662f\u4f55\u8eab\u4efd\uff1f\u4e0d\u8fc7\u4ed6\u4eec\u5fc3\u91cc\u6709\u6570\uff0c\u8fd9\u4e9b \u4eba\u7edd\u975e\u7b80\u5355\u4eba\u7269\u3002 \u3000\u3000\u665a\u9910\u65f6\uff0c\u738b\u5f64\u4ea4\u5f85\u6240\u6709\u7684\u4eba\u4eca\u665a\u8c01\u90fd\u4e0d\u51c6\u5916\u51fa\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u4e0d\u77e5\u738b\u8001\u5f1f\u4e3a\u4ec0\u4e48\u8981\u5982\u6b64\u89c4\u5b9a\uff1f\u201d \u3000\u3000\u201c\u5144\u5f1f\u7684\u610f\u601d\u4e0d\u5916\u662f\u8981\u770b\u770b\u9a6c\u6587\u4e2d\u7684\u53cd\u5e94\uff0c\u54b1\u4eec\u6765\u5230\u5f00\u5c01\uff0c\u5e76\u672a\u901a\u77e5\u5de1\u629a\u8859\u95e8\uff0c\u8ba9\u4ed6\u6682 \u65f6\u4e0d\u77e5\u9053\u6700\u597d\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u7b11\u9053\uff1a\u201c\u9a6c\u6587\u4e2d\u6d88\u606f\u6700\u7075\uff0c\u54b1\u4eec\u6b64\u6765\uff0c\u6839\u672c\u7792\u4e0d\u8fc7\u4ed6\uff0c\u5982\u679c\u6211\u9884\u6599\u4e0d\u5dee\uff0c\u5f85\u4f1a\u513f \u4ed6\u4e00\u5b9a\u4f1a\u4eb2\u81ea\u5230\u5ba2\u6808\u6765\u8c12\u89c1\u4e09\u516c\u4e3b\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u4ed6\u6765\u4e86\uff0c\u6c5f\u5144\u662f\u5426\u8981\u548c\u4ed6\u89c1\u89c1\uff1f\u201d \u3000\u3000\u201c\u4ed6\u548c\u6c5f\u67d0\u4e24\u5e74\u524d\u4fbf\u6709\u5fc3\u7ed3\uff0c\u6682\u65f6\u8fd8\u662f\u56de\u907f\u4e00\u4e0b\u7684\u597d\u3002\u201d \u3000\u3000\u201c\u4e5f\u597d\uff0c\u5144\u5f1f\u4f1a\u60f3\u529e\u6cd5\u5e94\u4ed8\u4ed6\u3002\u201d \u3000\u3000\u665a\u996d\u540e\uff0c\u4e09\u516c\u4e3b\u548c\u601c\u82b1\u968f\u5373\u56de\u623f\u4f11\u606f\u3002 \u3000\u3000\u5176\u4ed6\u7684\u4eba\uff0c\u56e0\u738b\u5f64\u89c4\u5b9a\u4e0d\u5f97\u5916\u51fa\uff0c\u4e5f\u591a\u534a\u5404\u81ea\u56de\u623f\u3002 \u3000\u3000\u5269\u4e0b\u5c0f\u71d5\u5b50\u3001\u8d75\u4fdd\u3001\u9648\u5b8f\u4e09\u4eba\uff0c\u5219\u5728\u540e\u9662\u5929\u4e95\u4e2d\u804a\u5929\u3002 \u3000\u3000\u8fd9\u4e09\u4eba\uff0c\u5c0f\u71d5\u5b50\u53ea\u6709\u5341\u4e03\u5c81\uff0c\u8d75\u4fdd\u548c\u9648\u5b8f\u4e5f\u4e0d\u8fc7\u4e8c\u5341\u51fa\u5934\u3002 \u3000\u3000\u4e09\u4eba\u56e0\u5e74\u9f84\u76f8\u5dee\u65e0\u51e0\uff0c\u81ea\u7136\u5f88\u8c08\u5f97\u6765\u3002\u4ed6\u4eec\u5fc3\u60f3\uff0c\u56de\u623f\u4e5f\u7761\u4e0d\u7740\uff0c\u5728\u5929\u4e95\u91cc\u8c08\u8c08\u5929\uff0c\u4e5f \u53ef\u6253\u53d1\u65e0\u804a\u3002 \u3000\u3000\u8fd9\u65f6\u4e00\u540d\u6808\u4f19\u63d0\u7740\u4e00\u53ea\u5927\u8336\u58f6\u524d\u6765\u9001\u8336\uff0c\u628a\u8336\u58f6\u653e\u5230\u5ba2\u5385\u684c\u4e0a\uff0c\u6b63\u8d70\u56de\u5929\u4e95\u65f6\uff0c\u9648\u5b8f\u4fbf \u62e6\u4f4f\u9053\uff1a\u201c\u4f19\u8ba1\u6162\u8d70\uff01\u201d \u3000\u3000\u90a3\u4f19\u8ba1\u505c\u4e0b\u811a\u6b65\u9053\uff1a\u201c\u5ba2\u5b98\u6709\u4ec0\u4e48\u5429\u5490\uff1f\u201d \u3000\u3000\u201c\u5429\u5490\u6ca1\u6709\u3002\u4f60\u5982\u679c\u6ca1\u6709\u6025\u4e8b\u8981\u529e\uff0c\u6211\u60f3\u8ddf\u4f60\u804a\u804a\u3002\u201d \u3000\u3000\u201c\u5929\u90fd\u9ed1\u4e86\uff0c\u54ea\u91cc\u8fd8\u6709\u4ec0\u4e48\u6025\u4e8b\uff0c\u4f60\u4eec\u51e0\u4f4d\u5ba2\u5b98\u597d\u50cf\u662f\u4ece\u4eac\u57ce\u6765\u7684\u5427\uff1f\u201d \u3000\u3000\u9648\u5b8f\u5fc3\u5934\u4e00\u52a8\u9053\uff1a\u201c\u4f60\u4ece\u54ea\u4e00\u70b9\u770b\u51fa\u6211\u4eec\u662f\u4ece\u4eac\u57ce\u6765\u7684\uff1f\u201d \u3000\u3000\u90a3\u4f19\u8ba1\u7b11\u9053\uff1a\u201c\u5e72\u6211\u4eec\u8fd9\u4e00\u884c\u7684\uff0c\u5ba2\u4eba\u770b\u5f97\u592a\u591a\u4e86\uff0c\u4e00\u642d\u773c\u5c31\u731c\u5f97\u516b\u4e5d\u4e0d\u79bb\u5341\uff0c\u5404\u4f4d\u4e0d \u4f46\u662f\u4eac\u57ce\u6765\u7684\uff0c\u800c\u4e14\u6709\u51e0\u4f4d\u53ef\u80fd\u8fd8\u662f\u5927\u8859\u95e8\u51fa\u8eab\u3002\u201d \u3000\u3000\u9648\u5b8f\u6446\u6446\u624b\u9053\uff1a\u201c\u4e0d\u53ef\u4e71\u8bb2\u3002\u6211\u4eec\u53ea\u662f\u884c\u8def\u5ba2\u5546\uff0c\u6839\u672c\u548c\u516d\u6247\u95e8\u626f\u4e0d\u4e0a\u5173\u7cfb\u3002\u201d \u3000\u3000\u90a3\u4f19\u8ba1\u987f\u4e86\u987f\u9053\uff1a\u201c\u90a3\u4f4d\u5c0f\u59d0\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u4f60\u95ee\u90a3\u4f4d\u5c0f\u59d0\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u597d\u50cf\u662f\u4f4d\u6781\u6709\u8eab\u4efd\u7684\u8d35\u4eba\uff01\u201d \u3000\u3000\u201c\u90a3\u662f\u6211\u4eec\u4e1c\u5bb6\u7684\u5343\u91d1\u3002\u4e1c\u5bb6\u5f88\u6709\u94b1\uff0c\u6211\u4eec\u5c0f\u59d0\u5f53\u7136\u4e5f\u5c31\u5f88\u6709\u8eab\u4efd\u4e86\u3002\u4f19\u8ba1\uff0c\u73b0\u5728\u662f\u6211 \u95ee\u4f60\uff0c\u4e0d\u662f\u4f60\u95ee\u6211\u3002\u201d \u3000\u3000\u201c\u5ba2\u5b98\u60f3\u95ee\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u662f\u7b2c\u4e00\u6b21\u5230\u8d35\u5b9d\u5730\u5f00\u5c01\u6765\uff0c\u5f00\u5c01\u662f\u4e2a\u5927\u5730\u65b9\uff0c\u597d\u73a9\u7684\u5730\u65b9\u4e00\u5b9a\u5f88\u591a\uff0c\u4f60\u80fd\u4e0d\u80fd\u544a \u8bc9\u6211\u4eec\u54ea\u4e9b\u5730\u65b9\u597d\u73a9\uff1f\u201d \u3000\u3000\u90a3\u4f19\u8ba1\u4e24\u773c\u7728\u4e86\u51e0\u7728\uff0c\u53c8\u95ee\u9053\uff1a\u201c\u4e0d\u77e5\u5ba2\u5b98\u4eec\u8981\u5728\u5f00\u5c01\u505c\u7559\u591a\u4e45\uff1f\u201d \u3000\u3000\u9648\u5b8f\u4e0d\u60a6\u9053\uff1a\u201c\u4f60\u600e\u4e48\u53c8\u95ee\u8d77\u6211\u6765\uff1f\u201d \u3000\u3000\u90a3\u4f19\u8ba1\u966a\u7740\u7b11\u8138\u9053\uff1a\u201c\u5c0f\u7684\u5fc5\u987b\u95ee\u4e86\u540e\u624d\u80fd\u56de\u7b54\u95ee\u9898\u3002\u201d \u3000\u3000\u9648\u5b8f\u987f\u4e86\u987f\u9053\uff1a\u201c\u4e0d\u4e00\u5b9a\uff0c\u4e5f\u8bb8\u4f1a\u505c\u7559\u4e09\u4e94\u5929\u5427\u3002\u201d \u3000\u3000\u201c\u597d\u3002\u73b0\u5728\u5c0f\u7684\u8981\u544a\u8bc9\u5404\u4f4d\u5ba2\u5b98\u4e00\u4ef6\u4e8b\uff0c\u6709\u4e2a\u5730\u65b9\u662f\u4f60\u4eec\u5e74\u8f7b\u7684\u5ba2\u5b98\u6700\u60f3\u53bb\u7684\uff0c\u4f46\u662f\u73b0 \u5728\u5374\u5343\u4e07\u522b\u53bb\uff01\u201d \u3000\u3000\u201c\u4f60\u8bf4\u7684\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u5ba2\u5b98\u53ef\u542c\u8bf4\u8fc7\u5f00\u5c01\u6709\u95f4\u2018\u8fce\u6625\u9601\u2019\u5417\uff1f\u201d \u3000\u3000\u201c\u542c\u8bf4\u8fc7\uff0c\u662f\u8d35\u5b9d\u5730\u6700\u5927\u7684\u4e00\u5bb6\u5993\u9662\uff0c\u53f7\u79f0\u7f8e\u5973\u5982\u4e91\uff0c\u6ca1\u9519\u5427\uff1f\u201d \u3000\u3000\u201c\u5ba2\u5b98\u4ee5\u524d\u662f\u5426\u53bb\u8fc7\uff1f\u201d \u3000\u3000\u201c\u6211\u521a\u624d\u5df2\u8bf4\u8fc7\u662f\u7b2c\u4e00\u6b21\u5230\u5f00\u5c01\u6765\uff0c\u2018\u8fce\u6625\u9601\u2019\u53ea\u662f\u8033\u95fb\u800c\u5df2\uff0c\u600e\u4f1a\u53bb\u8fc7\uff01\u201d \u3000\u3000\u201c\u90a3\u662f\u4e0d\u662f\u4f60\u4eec\u5e74\u8f7b\u5ba2\u5b98\u6700\u559c\u6b22\u53bb\u7684\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u4f19\u8ba1\uff0c\u4f60\u600e\u4e48\u8001\u559c\u6b22\u53d1\u95ee\uff1f\u4f9d\u6211\u770b\u8001\u5bb6\u4f19\u5230\u90a3\u79cd\u5730\u65b9\u53bb\u7684\u4e5f\u4e0d\u5728\u5c11\u6570\u3002\u201d \u3000\u3000\u201c\u597d\u5566\uff01\u5c0f\u7684\u73b0\u5728\u5c31\u63d0\u51fa\u8b66\u544a\uff0c\u4e0d\u8bba\u5e74\u8f7b\u7684\u5e74\u8001\u7684\uff0c\u6700\u597d\u522b\u5230\u2018\u8fce\u6625\u9601\u2019\u53bb\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u53bb\u4e86\u4ee5\u540e\uff0c\u5341\u6709\u516b\u4e5d\u4f1a\u4e2d\u5956\u3002\u201d \u3000\u3000\u201c\u4e2d\u5956\uff1f\u90a3\u4e0d\u662f\u597d\u4e8b\u5417\uff1f\u201d \u3000\u3000\u201c\u597d\u4e8b\uff1f\u8fd9\u79cd\u597d\u4e8b\u7b49\u4e8e\u5012\u4e86\u516b\u8f88\u5b50\u7684\u9709\uff01\u201d \u3000\u3000\u8d75\u4fdd\u63d2\u53e3\u9053\uff1a\u201c\u4f19\u8ba1\uff0c\u4f60\u660e\u660e\u767d\u767d\u8bf4\u51fa\u6765\uff0c\u4e0d\u5c31\u7ed3\u4e86\uff0c\u7528\u4e0d\u7740\u518d\u8ddf\u6211\u4eec\u6253\u54d1\u8c1c\u3002\u201d \u3000\u3000\u90a3\u4f19\u8ba1\u6574\u4e86\u6574\u8138\u8272\uff0c\u5374\u53c8\u95ee\u9053\uff1a\u201c\u597d\uff0c\u5c0f\u7684\u5c31\u5b9e\u8bdd\u5b9e\u8bf4\uff0c\u7edd\u4e0d\u518d\u515c\u5708\u5b50\uff0c\u4e0d\u8fc7\u5fc5\u987b\u5148\u95ee \u5ba2\u5b98\u4e00\u4ef6\u4e8b\uff0c\u4f60\u4eec\u77e5\u4e0d\u77e5\u9053\u866b\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d \u3000\u3000\u8d75\u4fdd\u5fc3\u5934\u4e00\u9707\uff0c\u54a6\u4e86\u58f0\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u5ffd\u7136\u95ee\u8d77\u8fd9\u4e2a\uff1f\u201d \u3000\u3000\u201c\u5ba2\u5b98\u5fc5\u987b\u77e5\u9053\u866b\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u5c0f\u7684\u624d\u80fd\u7ee7\u7eed\u8bb2\u4e0b\u53bb\uff0c\u4e0d\u7136\u8bb2\u4e86\u4e5f\u662f\u767d\u8bb2\u3002\u201d \u3000\u3000\u201c\u6211\u867d\u7136\u6ca1\u89c1\u8fc7\u866b\uff0c\u4f46\u5374\u542c\u8bf4\u90a3\u4e1c\u897f\u5f88\u6bd2\uff0c\u800c\u4e14\u65e0\u5b54\u4e0d\u5165\uff0c\u8c01\u78b0\u5230\u5b83\u8c01\u5c31\u5012\u9709\uff0c\u4e25\u91cd\u7684 \u66f4\u53ef\u4ee5\u6740\u4eba\u3002\u201d \u3000\u3000\u201c\u5ba2\u5b98\u80fd\u77e5\u9053\u8fd9\u4e9b\u5c31\u597d\uff0c\u73b0\u5728\u8fce\u6625\u9601\u2019\u91cc\u6b63\u6709\u866b\u6bd2\u6d41\u884c\uff0c\u6240\u4ee5\u5c0f\u7684\u624d\u63d0\u9192\u4e09\u4f4d\u5343\u4e07\u522b\u5230 \u90a3\u91cc\u53bb\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u542c\u8c01\u8bf4\u7684\uff1f\u201d \u3000\u3000\u201c\u8fd9\u51e0\u5929\u4e0d\u5c11\u5ba2\u4eba\u90fd\u5728\u8c08\u8bba\u8fd9\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u8bf4\u662f\u5f00\u5c01\u57ce\u5185\u6709\u4e0d\u5c11\u4eba\u4e2d\u4e86\u866b\u6bd2\uff0c\u6709\u7684\u60c5\u51b5\u8fd8\u5341\u5206\u4e25\u91cd\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e0e\u2018\u8fce\u6625\u9601\u2019\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\u5462\uff1f\u201d \u3000\u3000\u201c\u5173\u7cfb\u53ef\u5927\u5566\uff01\u636e\u8bf4\u8fd9\u4e9b\u8eab\u4e2d\u866b\u6bd2\u7684\u4eba\u90fd\u662f\u5230\u2018\u8fce\u6625\u9601\u2019\u5ad6\u8fc7\u59d1\u5a18\u7684\u3002\u201d \u3000\u3000\u201c\u6709\u8fd9\u79cd\u4e8b\uff1f\u90a3\u662f\u8bf4\u2018\u8fce\u6625\u9601\u2019\u7684\u59d1\u5a18\u5728\u5ad6\u5ba2\u8eab\u4e0a\u4e0b\u866b\u7f57\uff1f\u201d \u3000\u3000\u201c\u6b63\u662f\u5982\u6b64\u3002\u201d \u3000\u3000\u201c\u6211\u60f3\u2018\u8fce\u6625\u9601\u2019\u7684\u59d1\u5a18\u4e0d\u53ef\u80fd\u6bcf\u4e2a\u90fd\u4f1a\u653e\u866b\uff0c\u662f\u5426\u67e5\u51fa\u653e\u866b\u7684\u59d1\u5a18\u662f\u54ea\u4e00\u4e2a\u5462\uff1f\u201d \u3000\u3000\u201c\u82e5\u80fd\u67e5\u51fa\u6765\u5c31\u597d\u529e\u4e86\u3002\u201d \u3000\u3000\u201c\u90a3\u4e9b\u8eab\u4e2d\u866b\u6bd2\u7684\u5ad6\u5ba2\uff0c\u5ad6\u8fc7\u54ea\u4e2a\u59d1\u5a18\uff0c\u4ed6\u5fc3\u91cc\u603b\u8be5\u6709\u6570\u3002\u201d \u3000\u3000\u201c\u82e5\u5fc3\u91cc\u6709\u6570\u5c31\u597d\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u96be\u9053\u4ed6\u4eec\u8fde\u5ad6\u8fc7\u8c01\u90fd\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u8fd9\u4e9b\u5ad6\u5ba2\u90fd\u662f\u51fa\u4e86\u540d\u7684\u5927\u5ad6\u5ba2\uff0c\u53ea\u8981\u957f\u5f97\u597d\u770b\u7684\u59d1\u5a18\uff0c\u4ed6\u4eec\u90fd\u8981\u70b9\u7740\u540d\u5ad6\uff0c\u6709\u7684 \u4e00\u5929\u5c31\u80fd\u5ad6\u4e0a\u597d\u51e0\u4e2a\uff0c\u5230\u5e95\u54ea\u4e2a\u59d1\u5a18\u4e0b\u7684\u866b\uff0c\u4ed6\u4eec\u53c8\u600e\u80fd\u77e5\u9053\u5462\uff1f\u201d \u3000\u3000\u90a3\u4f19\u8ba1\u6b63\u8bf4\u5230\u8fd9\u91cc\uff0c\u5ffd\u89c1\u5ba2\u6808\u7684\u4e00\u540d\u7ba1\u4e8b\u5306\u5306\u5fd9\u5fd9\u8dd1\u4e86\u8fdb\u6765\uff0c\u9053\uff1a\u201c\u5c0f\u4e09\u5b50\uff0c\u5feb\u5230\u524d\u9762 \u6765\uff0c\u6709\u5927\u4eba\u7269\u5230\u54b1\u4eec\u8fd9\u91cc\u6765\u4e86\uff0c\u638c\u67dc\u7684\u6b63\u5728\u95e8\u53e3\u606d\u8fce\u3002\u201d \u3000\u3000\u90a3\u53eb\u5c0f\u4e09\u5b50\u7684\u4f19\u8ba1\u6025\u6025\u95ee\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u6837\u7684\u5927\u4eba\u7269\u6765\u4e86\uff1f\u201d \u3000\u3000\u201c\u54b1\u4eec\u6cb3\u752b\u6700\u5927\u7684\uff0c\u629a\u53f0\u5927\u4eba\u6765\u4e86\uff0c\u591f\u5927\u5427\uff1f\u201d \u3000\u3000\u5c0f\u4e09\u5b50\u54ea\u91cc\u8fd8\u80fd\u518d\u8bf4\u4ec0\u4e48\uff1f\u987f\u65f6\u5168\u8eab\u9aa8\u5934\u4e0d\u8db3\u56db\u4e24\uff0c\u968f\u7740\u90a3\u7ba1\u4e8b\u7684\u5954\u5411\u524d\u5934\u53bb\u3002 \u3000\u3000\u9648\u5b8f\u548c\u8d75\u4fdd\u4e5f\u7acb\u523b\u5411\u738b\u5f64\u901a\u62a5\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u5219\u5fd9\u7740\u8fdb\u623f\u8f6c\u544a\u6c5f\u5343\u91cc\u3002 \u3000\u3000\u5f53\u4e0b\uff0c\u7531\u738b\u5f64\u5e26\u7740\u9648\u5b8f\u548c\u8d75\u4fdd\u5728\u540e\u9662\u5ba2\u5385\u7b49\u5019\u3002\u81f3\u4e8e\u5176\u4f59\u7684\u4eba\uff0c\u51b3\u5b9a\u6682\u65f6\u4e0d\u548c\u9a6c\u6587\u4e2d\u89c1 \u9762\u3002 \u3000\u3000\u4e0d\u4e00\u4f1a\u513f\uff0c\u9a6c\u6587\u4e2d\u4fbf\u6765\u5230\u540e\u9662\u3002 \u3000\u3000\u968f\u540c\u9a6c\u6587\u4e2d\u524d\u6765\u7684\uff0c\u9664\u4e86\u73b0\u4efb\u603b\u6355\u5934\u7684\u8f6c\u9f99\u624b\u5f20\u4e0d\u7a7a\u5916\uff0c\u53e6\u6709\u51e0\u540d\u5e26\u5200\u968f\u4ece\u3002 \u3000\u3000\u738b\u5f64\u5e26\u7740\u9648\u5b8f\u3001\u8d75\u4fdd\u8fce\u51fa\u5ba2\u5385\u95e8\u5916\u3002 \u3000\u3000\u9a6c\u6587\u4e2d\u8001\u8fdc\u5c31\u9ad8\u62f1\u53cc\u624b\u9053\uff1a\u201c\u738b\u5927\u4eba\u9a7e\u4e34\u5f00\u5c01\uff0c\u4e3a\u4f55\u4e8b\u5148\u4e0d\u5411\u4e0b\u5b98\u6253\u58f0\u62db\u547c\uff0c\u6709\u5931\u8fdc \u8fce\uff0c\u8fd8\u8bf7\u5f53\u9762\u6055\u7f6a\uff01\u201d \u3000\u3000\u738b\u5f64\u4e5f\u62b1\u62f3\u4e00\u793c\u9053\uff1a\u201c\u738b\u67d0\u508d\u665a\u624d\u5230\uff0c\u6b63\u51c6\u5907\u660e\u65e5\u4e00\u65e9\u5230\u629a\u7f72\u62dc\u5019\uff0c\u4e0d\u60f3\u9a6c\u5927\u4eba\u7adf\u5148\u6765 \u4e86\uff0c\u5b9e\u5728\u4e0d\u6562\u5f53\uff0c\u9a6c\u5927\u4eba\u6d88\u606f\u771f\u662f\u7075\u901a\u3002\u201d \u3000\u3000\u8fdb\u5165\u5ba2\u5385\uff0c\u5206\u5bbe\u4e3b\u5750\u5b9a\u3002 \u3000\u3000\u5176\u4f59\u7684\u4eba\u5219\u4f8d\u7acb\u5728\u4e24\u4eba\u8eab\u540e\u3002 \u3000\u3000\u9648\u5b8f\u4e3a\u4e24\u4eba\u732e\u4e0a\u8336\u3002 \u3000\u3000\u9a6c\u6587\u4e2d\u9053\uff1a\u201c\u738b\u5927\u4eba\u521a\u56de\u4eac\u4e0d\u4e45\uff0c\u4fbf\u518d\u6765\u5f00\u5c01\uff0c\u4e00\u5b9a\u53c8\u6709\u91cd\u8981\u5927\u4e8b\u5427\uff1f\u201d \u3000\u3000\u738b\u5f64\u7b11\u9053\uff1a\u201c\u7b97\u4e0d\u5f97\u4ec0\u4e48\u5927\u4e8b\uff0c\u738b\u67d0\u867d\u8eab\u4e3a\u5927\u5185\u5f85\u536b\u7edf\u9886\uff0c\u4f46\u6700\u8fd1\u8fd9\u4e24\u5e74\u6765\u5374\u7ecf\u5e38\u5728\u5916 \u5954\u6ce2\uff0c\u5c24\u5176\u5bf9\u9a6c\u5927\u4eba\u591a\u6240\u6253\u6270\uff0c\u5185\u5fc3\u5b9e\u5728\u6709\u4e9b\u8fc7\u610f\u4e0d\u53bb\u3002\u201d \u3000\u3000\u201c\u738b\u5927\u4eba\u8fd9\u6837\u8bf4\u8bdd\u5c31\u592a\u89c1\u5916\u4e86\uff0c\u738b\u5927\u4eba\u51fa\u4eac\uff0c\u5fc5\u662f\u5949\u4e86\u5723\u4e0a\u65e8\u610f\uff0c\u4e0b\u5b98\u662f\u5730\u65b9\u5b98\u540f\uff0c\u738b \u5927\u4eba\u5e38\u6765\u5f00\u5c01\uff0c\u662f\u770b\u5f97\u8d77\u4e0b\u5b98\uff0c\u4f55\u8a00\u6253\u6270\u4e8c\u5b57\uff1f\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u8bf4\u5230\u8fd9\u91cc\uff0c\u89c6\u7ebf\u56db\u4e0b\u626b\u89c6\u4e86\u4e00\u904d\uff0c\u63a5\u9053\uff1a\u201c\u542c\u8bf4\u4e09\u516c\u4e3b\u7684\u51e4\u9a7e\u4e5f\u5230\u4e86\uff0c\u4e3a\u4ec0\u4e48\u4e0d \u51fa\u6765\u5bb9\u4e0b\u5b98\u62dc\u89c1\uff1f\u201d \u3000\u3000\u738b\u5f64\u6697\u6697\u9707\u60ca\u4e8e\u9a6c\u6587\u4e2d\u6d88\u606f\u7684\u7075\u901a\uff01 \u3000\u3000\u4ed6\u5fc3\u60f3\u5bf9\u65b9\u65e2\u7136\u5df2\u77e5\uff0c\u5f53\u7136\u4e0d\u4fbf\u9690\u7792\uff0c\u5fd9\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u56e0\u957f\u9014\u8dcb\u6d89\uff0c\u8eab\u4f53\u7a0d\u6709\u4e0d\u9002\uff0c\u5df2 \u7136\u56de\u623f\u4f11\u606f\u4e86\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u663e\u51fa\u4e0d\u5b89\u795e\u8272\u9053\uff1a\u201c\u8981\u4e0d\u8981\u627e\u540d\u5927\u592b\u4e3a\u4e09\u516c\u4e3b\u770b\u770b\uff1f\u5f00\u5c01\u57ce\u5185\u6709\u597d\u51e0\u4f4d\u540d\u533b\uff0c\u4e0b \u5b98\u73b0\u5728\u5c31\u6d3e\u5f20\u603b\u6355\u5934\u53bb\u8bf7\u5927\u592b\u6765\uff01\u201d \u3000\u3000\u738b\u5f64\u6025\u6025\u4e00\u6446\u624b\u9053\uff1a\u201c\u9a6c\u5927\u4eba\u7528\u4e0d\u7740\u8d39\u5fc3\uff0c\u4e09\u516c\u4e3b\u53ea\u662f\u6709\u70b9\u7d2f\u800c\u5df2\uff0c\u4f11\u606f\u4e00\u665a\u4fbf\u4f1a\u597d \u7684\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u5982\u6b64\uff0c\u4e0b\u5b98\u7406\u5e94\u8fdb\u5185\u62dc\u89c1\u3002\u201d \u3000\u3000\u201c\u4e0d\u5fc5\u4e86\uff0c\u9a6c\u5927\u4eba\u7684\u4e00\u756a\u5fc3\u610f\uff0c\u738b\u67d0\u660e\u65e5\u81ea\u5f53\u4ee3\u4e3a\u8f6c\u8fbe\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u672c\u6765\u5df2\u7ad9\u8d77\u8eab\u5b50\uff0c\u53ea\u597d\u53c8\u5750\u4e0b\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u7684\u51e4\u9a7e\uff0c\u51c6\u5907\u5728\u5f00\u5c01\u505c\u7559\u591a\u4e45\uff1f\u201d \u3000\u3000\u738b\u5f64\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u73b0\u5728\u8fd8\u5f88\u96be\u8bf4\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u968f\u5373\u7ad9\u8d77\u8eab\u6765\u9053\uff1a\u201c\u738b\u5927\u4eba\u8def\u9014\u8f9b\u82e6\uff0c\u4e5f\u8be5\u4f11\u606f\u4e86\uff0c\u4e0b\u5b98\u5148\u884c\u544a\u8f9e\uff0c\u660e\u65e5\u518d\u6765\u62dc \u8c12\u4e09\u516c\u4e3b\u3002\u201d \u3000\u3000\u201c\u9a6c\u5927\u4eba\u4e0d\u5fc5\u5ba2\u6c14\uff0c\u660e\u5929\u8fd8\u662f\u738b\u67d0\u5230\u629a\u7f72\u62dc\u6664\u9a6c\u5927\u4eba\u7684\u597d\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u8d70\u540e\uff0c\u4e09\u516c\u4e3b\u548c\u6c5f\u5343\u91cc\u7acb\u523b\u8fdb\u4eba\u5ba2\u5385\u3002 \u3000\u3000\u4ed6\u4eec\u5bf9\u9a6c\u6587\u4e2d\u548c\u738b\u5f64\u7684\u5bf9\u8c08\u7ecf\u8fc7\uff0c\u5df2\u5168\u77e5\u9053\u3002 \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u9a6c\u6587\u4e2d\u660e\u5929\u5fc5\u5b9a\u8fd8\u4f1a\u518d\u6765\uff0c\u4e09\u516c\u4e3b\u662f\u5426\u8981\u548c\u4ed6\u89c1\u9762\uff1f\u201d \u3000\u3000\u201c\u660e\u5929\u4f60\u5230\u629a\u8859\u53bb\u62dc\u4f1a\u4ed6\u4e00\u4e0b\uff0c\u8981\u4ed6\u4e0d\u5fc5\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u662f\u541b\u81e3\u4e4b\u793c\uff0c\u9a6c\u6587\u4e2d\u65e2\u7136\u5df2\u7ecf\u77e5\u9053\u4e09\u516c\u4e3b\u6765\u5230\u5f00\u5c01\uff0c\u5728\u793c\u6570\u4e0a\uff0c\u4ed6\u662f\u4e0d\u80fd\u4e0d\u6765\u8c12\u89c1 \u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u4e0d\u59a8\u4f20\u8bdd\u7ed9\u4ed6\uff0c\u6211\u6682\u65f6\u4e0d\u63a5\u89c1\u5730\u65b9\u4e0a\u7684\u4efb\u4f55\u5b98\u5458\u3002\u201d \u3000\u3000\u201c\u53ea\u662f\u4e09\u516c\u4e3b\u6b64\u6765\u76ee\u7684\uff0c\u5fc5\u987b\u5bf9\u4ed6\u660e\u8bb2\uff0c\u5982\u6b64\u624d\u80fd\u8ba9\u4ed6\u4ea4\u51fa\u4eba\u6765\u3002\u201d \u3000\u3000\u201c\u6700\u597d\u4e0d\u8981\u6b63\u9762\u63d0\u51fa\u8fd9\u95ee\u9898\uff0c\u6539\u4ee5\u65c1\u6572\u4fa7\u51fb\u7684\u65b9\u5f0f\u8fdb\u884c\uff0c\u5426\u5219\u9a6c\u6587\u4e2d\u82e5\u4e0d\u627f\u8ba4\u8859\u7f72\u91cc\u6709 \u8fd9\u4e48\u4e2a\u4eba\uff0c\u8c01\u4e5f\u62ff\u4ed6\u6ca1\u529e\u53bb\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u9a6c\u6587\u4e2d\u771f\u7684\u77e2\u53e3\u5426\u8ba4\u5462\uff1f\u201d \u3000\u3000\u201c\u5230\u65f6\u5019\u4f60\u4e0d\u59a8\u548c\u6c5f\u53d4\u53d4\u5546\u8bae\u8be5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u201c\u4e5f\u597d\uff0c\u4e09\u516c\u4e3b\u5c31\u8bf7\u56de\u623f\u4f11\u606f\u5427\uff0c\u5351\u804c\u73b0\u5c31\u548c\u6c5f\u5144\u5546\u8bae\u4e00\u4e0b\u3002\u201d \u3000\u3000\u4e09\u516c\u4e3b\u968f\u5373\u56de\u623f\u800c\u53bb\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u5148\u628a\u90a3\u8001\u5996\u5a46\u62bc\u8fdb\u6765\u518d\u8bf4\u3002\u201d \u3000\u3000\u8001\u5996\u5a46\u8fd9\u4e00\u8def\u4e0a\u4e00\u76f4\u53d7\u7740\u76d1\u89c6\uff0c\u4f4f\u8fdb\u4e86\u5ba2\u6808\uff0c\u884c\u52a8\u4e5f\u5e76\u4e0d\u81ea\u7531\u3002\u4e0d\u591a\u65f6\uff0c\u4fbf\u88ab\u9648\u5b8f\u62bc\u4e86 \u8fdb\u6765\u3002 \u3000\u3000\u738b\u5f64\u5f88\u5ba2\u6c14\uff0c\u7ed9\u4e86\u5979\u4e00\u4e2a\u5ea7\u4f4d\u3002 \u3000\u3000\u8001\u5996\u5a46\u4e00\u5750\u4e0b\u5c31\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u628a\u8001\u8eab\u62bc\u51fa\u6765\uff0c\u6709\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u738b\u5f64\u89c1\u8001\u5996\u5a46\u4f3c\u4e4e\u8981\u501a\u8001\u5356\u8001\u7684\u62ff\u8d77\u52bf\u6765\uff0c\u5f53\u7136\u4e0d\u80fd\u4e0d\u538b\u538b\u5979\u8fd9\u80a1\u6c14\u7130\u3002 \u3000\u3000\u4ed6\u7acb\u5373\u4e24\u773c\u4e00\u77aa\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u81ed\u5a46\u5a18\uff0c\u5728\u6c5f\u5927\u4fa0\u548c\u672c\u7edf\u9886\u9762\u524d\u7adf\u6562\u7528\u8fd9\u79cd\u53e3\u6c14\u8bf4\u8bdd\uff0c\u7b80 \u76f4\u5c82\u6709\u6b64\u7406\uff01\u201d \u3000\u3000\u5c82\u77e5\u8001\u5996\u5a46\u7684\u6c14\u7130\u5e76\u6ca1\u88ab\u538b\u4e0b\u53bb\uff0c\u53cd\u800c\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u8981\u8001\u8eab\u600e\u6837\u8bf4\u8bdd\uff1f\u96be\u9053\u8fd8\u8981\u8dea \u5728\u5730\u4e0a\u628a\u4f60\u5f53\u7687\u4e0a\u4e0d\u6210\uff1f\u201d \u3000\u3000\u738b\u5f64\u54ea\u91cc\u5fcd\u5f97\u4e0b\u8fd9\u53e3\u6c14\uff1f\u4ed6\u5927\u559d\u9053\uff1a\u201c\u9648\u5b8f\u638c\u5634\uff01\u201d \u3000\u3000\u9648\u5b8f\u65e9\u5bf9\u8001\u5996\u5a46\u770b\u4e0d\u987a\u773c\uff0c\u6b63\u597d\u85c9\u8fd9\u673a\u4f1a\u8fc7\u8fc7\u624b\u763e\uff0c\u95fb\u8a00\u4e4b\u540e\uff0c\u5f53\u5373\u5de6\u53f3\u5f00\u5f13\uff0c\u5728\u8001\u5996 \u5a46\u4e24\u8fb9\u9762\u988a\u4e0a\u63b4\u4e86\u4e2a\u75db\u5feb\u3002 \u3000\u3000\u8001\u5996\u5a46\u672c\u662f\u5750\u5728\u6905\u5b50\u4e0a\uff0c\u7ecf\u8fc7\u4e00\u9635\u75db\u6bb4\uff0c\u65e9\u5df2\u88ab\u6253\u5f97\u762b\u5750\u5728\u5730\u4e0a\u3002 \u3000\u3000\u539f\u6765\u8001\u5996\u5a46\u89c1\u5df2\u6765\u5230\u5f00\u5c01\uff0c\u6709\u4e86\u9a6c\u6587\u4e2d\u8fd9\u4f4d\u540e\u53f0\u9760\u5c71\uff0c\u6240\u4ee5\u80c6\u5b50\u624d\u5927\u8d77\u6765\u3002\u6b64\u523b\u89c1\u738b\u5f64 \u5e76\u4e0d\u5403\u8fd9\u4e00\u5957\uff0c\u53ea\u6709\u518d\u8f6f\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5979\u8db4\u5728\u5730\u4e0a\u731b\u5598\u4e86\u4e00\u9635\uff0c\u624d\u9f87\u7259\u54a7\u5634\u7684\u9053\uff1a\u201c\u738b\u7edf\u9886\uff0c\u4f60\u4f55\u82e6\u62ff\u6211\u8fd9\u8001\u5a46\u5b50\u51fa\u6c14\u5462\uff1f\u6211 \u5df2\u7ecf\u662f\u4e03\u5341\u5c81\u7684\u4eba\u4e86\uff0c\u4f60\u4eec\u603b\u8be5\u656c\u8001\u5c0a\u8d24\u624d\u5bf9\u3002\u201d \u3000\u3000\u738b\u5f64\u9a82\u9053\uff1a\u201c\u4f60\u9664\u4e86\u6ee1\u8eab\u5996\u6c14\u866b\u6bd2\uff0c\u8d24\u5728\u54ea\u91cc\uff1f\u5982\u679c\u8fde\u4f60\u8fd9\u79cd\u5996\u5a46\u90fd\u80fd\u6cbe\u4e0a\u4e00\u4e2a\u8d24\u5b57\uff0c \u5929\u4e0b\u5c31\u4e0d\u53ef\u80fd\u518d\u6709\u574f\u4eba\uff01\u201d \u3000\u3000\u5ffd\u542c\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u738b\u8001\u5f1f\u73b0\u5728\u5c31\u628a\u5979\u62bc\u56de\u53bb\u3002\u201d \u3000\u3000\u738b\u5f64\u5927\u611f\u4e0d\u89e3\u9053\uff1a\u201c\u521a\u62bc\u51fa\u6765\uff0c\u4ec0\u4e48\u4e8b\u90fd\u6ca1\u4ea4\u5f85\uff0c\u4e3a\u4ec0\u4e48\u53c8\u8981\u5979\u56de\u53bb\uff1f\u201d \u3000\u3000\u201c\u6c5f\u67d0\u60f3\u8d77\u4e00\u4ef6\u4e8b\uff0c\u6682\u65f6\u5df2\u7ecf\u7528\u4e0d\u7740\u5979\u4e86\u3002\u201d \u3000\u3000\u738b\u5f64\u53ea\u597d\u53c8\u547d\u9648\u5b8f\u628a\u8001\u5996\u5a46\u9001\u56de\u5ba4\u5185\u3002 \u3000\u3000\u8001\u5996\u5a46\u9664\u4e86\u6df1\u901a\u866b\u672f\u4e4b\u5916\uff0c\u6b66\u529f\u53ea\u80fd\u79f0\u5f97\u4e0a\u662f\u4e00\u822c\u9ad8\u624b\u800c\u5df2\uff0c\u56e0\u4e4b\u9664\u4e86\u5fcd\u6c14\u541e\u58f0\uff0c\u5e76\u4e0d \u6562\u53cd\u6297\u3002 \u3000\u3000\u738b\u5f64\u8fd9\u624d\u53c8\u95ee\u9053\uff1a\u201c\u6c5f\u5144\uff0c\u4eba\u662f\u4f60\u5429\u5490\u62bc\u51fa\u6765\u7684\uff0c\u534a\u53e5\u8bdd\u90fd\u6ca1\u95ee\uff0c\u53c8\u5429\u5490\u62bc\u56de\u53bb\uff0c\u7a76\u7adf \u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u671b\u4e86\u7ad9\u5728\u4e00\u65c1\u7684\u5c0f\u71d5\u5b50\u548c\u8d75\u4fdd\u4e00\u773c\uff0c\u9053\uff1a\u201c\u521a\u624d\u5ba2\u6808\u91cc\u7684\u4f19\u8ba1\u548c\u4ed6\u4eec\u51e0\u4e2a\u5728\u5929\u4e95 \u91cc\u8bb2\u7684\u8bdd\uff0c\u738b\u8001\u5f1f\u5e94\u8be5\u542c\u5230\u4e86\u5427\uff1f\u201d \u3000\u3000\u738b\u5f64\u9894\u9996\u9053\uff1a\u201c\u4ed6\u4eec\u5728\u5929\u4e95\u91cc\u8bb2\u8bdd\uff0c\u5144\u5f1f\u5f53\u65f6\u6b63\u5728\u7a97\u524d\uff0c\u600e\u4f1a\u542c\u4e0d\u5230\uff01\u201d \u3000\u3000\u201c\u6c5f\u67d0\u6b63\u56e0\u4e3a\u60f3\u5230\u8fd9\u4ef6\u4e8b\uff0c\u6240\u4ee5\u624d\u8981\u4f60\u6682\u65f6\u628a\u8001\u5996\u5973\u62bc\u56de\u53bb\u3002\u201d \u3000\u3000\u201c\u6c5f\u5144\u7684\u610f\u601d\u2026\u2026\u201d \u3000\u3000\u201c\u6682\u65f6\u522b\u60ca\u52a8\u9a6c\u5de1\u629a\uff0c\u660e\u5929\u5c31\u7531\u8d75\u4fdd\u548c\u9648\u5b8f\u626e\u4f5c\u5ad6\u5ba2\uff0c\u5230\u2018\u8fce\u6625\u9601\u2019\u4e00\u8d9f\uff0c\u8bbe\u6cd5\u67e5\u51fa\u653e \u866b\u4e4b\u4eba\u3002\u201d \u3000\u3000\u201c\u6c5f\u5144\u76f8\u4fe1\u4e0b\u866b\u7684\u4e00\u5b9a\u662f\u2018\u8fce\u6625\u9601\u2019\u7684\u59d1\u5a18\uff1f\u201d \u3000\u3000\u201c\u5e94\u8be5\u662f\u516b\u4e5d\u4e0d\u79bb\u5341\uff0c\u5982\u679c\u80fd\u67e5\u51fa\u4e0b\u866b\u7684\u59d1\u5a18\uff0c\u4e8b\u60c5\u5c31\u7531\u54b1\u4eec\u81ea\u5df1\u89e3\u51b3\u3002\u201d \u3000\u3000\u53ea\u542c\u8d75\u4fdd\u9053\uff1a\u201c\u6700\u597d\u4e5f\u80fd\u8ba9\u71d5\u8001\u5f1f\u548c\u5c5e\u4e0b\u4eec\u4e00\u8d77\u53bb\u3002\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u4e24\u4e2a\u4eba\u8fd8\u4e0d\u591f\u5417\uff1f\u4e3a\u4ec0\u4e48\u8fd8\u8981\u52a0\u4e0a\u5c0f\u71d5\u5b50\uff1f\u201d \u3000\u3000\u201c\u71d5\u8001\u5f1f\u7fe9\u7fe9\u5e74\u5c11\uff0c\u4e00\u8868\u4eba\u624d\uff0c\u8fd9\u6837\u7684\u5c0f\u4f19\u5b50\u6700\u53d7\u59d1\u5a18\u4eec\u6b22\u8fce\u4e86\uff0c\u8bf4\u8d77\u8bdd\u6765\u81ea\u7136\u4e5f\u6bd4\u8f83 \u65b9\u4fbf\u4e9b\u3002\u201d \u3000\u3000\u201c\u4f60\u548c\u9648\u5b8f\u5e74\u7eaa\u4e5f\u4e0d\u5927\uff0c\u8bba\u4eba\u624d\u4e5f\u662f\u4e0a\u4e0a\u4e4b\u9009\uff0c\u6211\u60f3\u59d1\u5a18\u4eec\u7167\u6837\u4e5f\u4f1a\u559c\u6b22\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u6bd4\u8d77\u71d5\u8001\u5f1f\u6765\uff0c\u603b\u662f\u5dee\u4e86\u4e9b\u5427\uff0c\u53cd\u6b63\u71d5\u8001\u5f1f\u4e5f\u95f2\u7740\u6ca1\u4e8b\uff0c\u8ddf\u7740\u6211\u4eec\u53bb\u901b\u901b\uff0c\u53c8\u6709 \u4ec0\u4e48\u4e0d\u53ef\uff1f\u201d \u3000\u3000\u738b\u5f64\u76ee\u6ce8\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u6c5f\u5144\uff0c\u5c0f\u71d5\u5b50\u662f\u4f60\u7684\u4eba\u3002\u662f\u5426\u4e5f\u8981\u53bb\uff0c\u5c31\u7531\u4f60\u51b3\u5b9a\u5427\uff01\u201d \u3000\u3000\u6c5f\u5343\u91cc\u8f6c\u8fc7\u5934\u6765\u9053\uff1a\u201c\u5c0f\u71d5\u5b50\uff0c\u4f60\u5c31\u8ddf\u4ed6\u4eec\u53bb\u4e00\u8d9f\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u8138\u4e0a\u4e00\u70ed\uff0c\u4f4e\u4e0b\u5934\u9053\uff1a\u201c\u90a3\u79cd\u5730\u65b9\uff0c\u4f84\u513f\u600e\u4e48\u53bb\u5f97\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u7b11\u9053\uff1a\u201c\u53bb\u53bb\u53c8\u4f55\u59a8\uff1f\u4e5f\u4e0d\u662f\u8ba9\u4f60\u6765\u771f\u7684\uff0c\u4f60\u4e00\u5fc3\u4e00\u610f\u8ddf\u7740\u6211\u95ef\u8361\u6c5f\u6e56\u2026\u2026\u201d \u3000\u3000\u201c\u53ef\u662f\u6211\u4e0d\u60f3\u8ddf\u7740\u4f60\u8001\u4eba\u5bb6\u95ef\u8361\u90a3\u79cd\u5730\u65b9\uff0c\u2018\u8fce\u6625\u9601\u2019\u4e5f\u7b97\u6c5f\u6e56\u5417\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u77aa\u773c\u9053\uff1a\u201c\u8c01\u8bf4\u4e0d\u662f\u6c5f\u6e56\uff01\u90a3\u6b63\u662f\u6c5f\u6e56\u7684\u4e00\u90e8\u5206\uff0c\u96be\u9053\u4e00\u5b9a\u957f\u6c5f\u624d\u7b97\u6c5f\uff1f\u6d1e\u5ead\u6e56 \u624d\u7b97\u6e56\u5417\uff1f\u66f4\u4f55\u51b5\u8fd9\u4e8b\u662f\u4e3a\u4e86\u4e09\u516c\u4e3b\uff0c\u54b1\u4eec\u7531\u4eac\u57ce\u6765\u5230\u5f00\u5c01\uff0c\u4e3a\u7684\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u7ec8\u4e8e\u6ca1\u7406\u7531\u53ef\u8bb2\uff0c\u987f\u4e86\u987f\u9053\uff1a\u201c\u53ef\u662f\u6211\u7239\u540c\u610f\u5417\uff1f\u522b\u8ba9\u4ed6\u8001\u4eba\u5bb6\u8ba4\u4e3a\u6211\u8fd9\u4e2a\u4f5c\u513f \u5b50\u7684\u53d8\u574f\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5e72\u91cc\u5475\u5475\u7b11\u9053\uff1a\u201c\u6211\u81ea\u7136\u4f1a\u5bf9\u4f60\u7239\u8bb2\uff0c\u5982\u679c\u4f60\u80fd\u56e0\u800c\u7acb\u4e0b\u4e00\u529f\uff0c\u4ed6\u9ad8\u5174\u8fd8\u6765\u4e0d\u53ca \u5462\uff01\u201d \u3000\u3000\u6c5f\u5343\u91cc\u5411\u5916\u671b\u4e86\u671b\u5929\u8272\uff0c\u53c8\u9053\uff1a\u201c\u4eca\u665a\u6ca1\u4e8b\u4e86\uff0c\u5927\u5bb6\u5c31\u56de\u623f\u4f11\u606f\u5427\uff01\u201d \u5367\u9f99\u751f\u300a\u71d5\u5b50\u4f20\u5947\u300b \u7b2c\u4e8c\u5341\u4e00\u56de\u3000\u5982\u6b64\u5ad6\u5ba2 \u3000\u3000\u7fcc\u65e5\u4e00\u65e9\u2014\u2014\u738b\u5f64\u8fd8\u6ca1\u6765\u5f97\u53ca\u53bb\u62dc\u4f1a\u9a6c\u6587\u4e2d\uff0c\u9a6c\u6587\u4e2d\u4fbf\u7387\u540c\u5f00\u5c01\u5e9c\u77e5\u5e9c\u5c39\u5b88\u4e49\uff0c\u53c8\u6765\u5230 \u4e86\u5ba2\u6808\uff0c\u51c6\u5907\u62dc\u89c1\u4e09\u516c\u4e3b\u3002 \u3000\u3000\u8fd9\u6b21\u4ecd\u662f\u7531\u738b\u5f64\u63a5\u5f85\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u5219\u6258\u75c5\u672a\u51fa\u3002 \u3000\u3000\u81f3\u4e8e\u6c5f\u5343\u91cc\uff0c\u7531\u4e8e\u4e0d\u65b9\u4fbf\u548c\u9a6c\u6587\u4e2d\u89c1\u9762\uff0c\u81ea\u7136\u4e5f\u672a\u9732\u9762\u3002 \u3000\u3000\u4e0d\u8fc7\u6c5f\u5343\u91cc\u5fc3\u91cc\u6709\u6570\uff0c\u9a6c\u6587\u4e2d\u53ef\u80fd\u5df2\u77e5\u4ed6\u968f\u540c\u4e09\u516c\u4e3b\u6765\u5230\u5f00\u5c01\uff0c\u53ea\u662f\u4e5f\u4e0d\u65b9\u4fbf\u5411\u738b\u5f64\u8be6 \u95ee\u800c\u5df2\u3002 \u3000\u3000\u9a6c\u6587\u4e2d\u66fe\u4e00\u518d\u5411\u738b\u5f64\u8868\u793a\uff0c\u5e0c\u671b\u4e09\u516c\u4e3b\u80fd\u7387\u9886\u968f\u4ece\u4eba\u5458\uff0c\u8fdb\u9a7b\u629a\u7f72\u884c\u9986\uff0c\u5982\u6b64\u4e5f\u597d\u65b9\u4fbf \u63a5\u5f85\u3002 \u3000\u3000\u4f46\u88ab\u738b\u5f64\u5a49\u8a00\u8c22\u5374\u3002 \u3000\u3000\u5f53\u65e5\u5348\u540e\uff0c\u5f00\u5c01\u57ce\u5185\u6700\u5927\u7684\u4e00\u5bb6\u5993\u9662\u201c\u8fce\u6625\u9601\u201d\uff0c\u6765\u4e86\u4e09\u4f4d\u5e74\u8f7b\u8d35\u5ba2\u3002 \u3000\u3000\u4e0d\u6d88\u8bf4\uff0c\u8fd9\u4e09\u4eba\u6b63\u662f\u8d75\u4fdd\u3001\u9648\u5b8f\u4e0e\u5c0f\u71d5\u5b50\u3002 \u3000\u3000\u8fd9\u4e09\u4eba\u672c\u6765\u5c31\u4eea\u8868\u4e0d\u4fd7\uff0c\u518d\u52a0\u4e0a\u8863\u5c65\u5149\u9c9c\uff0c\u5728\u201c\u8fce\u6625\u9601\u201d\u6765\u8bf4\uff0c\u6beb\u65e0\u7591\u95ee\u662f\u8d35\u5ba2\u4e34\u95e8\u3002 \u3000\u3000\u201c\u8fce\u6625\u9601\u201d\u4e0d\u6127\u662f\u5f00\u5c01\u57ce\u5185\u6700\u5927\u7684\u79e6\u697c\u695a\u9986\uff0c\u4ec5\u770b\u8fdb\u5165\u5927\u95e8\u540e\u90a3\u8bbe\u6709\u5047\u5c71\u6c34\u69ad\u3001\u4ead\u53f0\u56de \u5eca\u7684\u5bbd\u655e\u5ead\u9662\uff0c\u5c31\u6709\u7740\u65e0\u6bd4\u7684\u6c14\u6d3e\u3002 \u3000\u3000\u8fd9\u91cc\u5171\u6709\u767e\u6765\u4f4d\u59d1\u5a18\uff0c\u6bcf\u4f4d\u59d1\u5a18\u90fd\u662f\u7ecf\u8fc7\u7cbe\u6311\u7ec6\u9009\u7684\uff0c\u5f62\u5bb9\u4e3a\u201c\u7f8e\u5973\u5982\u4e91\u201d\u7edd\u4e0d\u4e3a\u8fc7\uff0c \u53ef\u60dc\u90a3\u65f6\u5019\u7684\u5973\u4eba\u90fd\u662f\u4e0d\u9732\u817f\u7684\uff0c\u5426\u5219\u5fc5\u5b9a\u8fd8\u53ef\u52a0\u4e0a\u4e00\u53e5\u201c\u7c89\u817f\u5982\u6797\u201d\u7684\u5f62\u5bb9\u8bcd\u3002 \u3000\u3000\u5728\u5c0f\u71d5\u5b50\u7b49\u4eba\u7684\u60f3\u50cf\u4e2d\uff0c\u6b64\u523b\u7684\u201c\u8fce\u6625\u9601\u201d\u4e00\u5b9a\u662f\u95e8\u53ef\u7f57\u96c0\uff0c\u751f\u610f\u6e05\u6de1\u3002\u5c82\u77e5\u5927\u8c2c\u4e0d \u7136\uff0c\u5bfb\u82b3\u5ba2\u4eec\u6d8c\u8fdb\u95e8\u7684\uff0c\u7167\u6837\u8fd8\u662f\u4eba\u6f6e\u6c79\u6d8c\uff0c\u7edc\u7ece\u4e0d\u7edd\u3002 \u3000\u3000\u751f\u610f\u597d\u5f97\u5f88\u3002 \u3000\u3000\u539f\u6765\u5ad6\u5ba2\u4eec\u6709\u4eba\u8eab\u4e2d\u866b\u6bd2\u7684\u4e8b\uff0c\u591a\u6570\u4eba\u5e76\u4e0d\u77e5\u60c5\u3002 \u3000\u3000\u6709\u7684\u867d\u7136\u542c\u5230\u4f20\u95fb\uff0c\u4e5f\u5e76\u4e0d\u76f8\u4fe1\u6bdb\u75c5\u662f\u51fa\u5728\u201c\u8fce\u6625\u9601\u201d\u7684\u59d1\u5a18\u8eab\u4e0a\uff0c\u66f4\u4f55\u51b5\u81f3\u5c11\u534a\u6570\u4ee5 \u4e0a\u5230\u8fd9\u513f\u6765\u7684\uff0c\u76ee\u7684\u53ea\u662f\u8ba9\u773c\u775b\u8fc7\u8fc7\u4e8e\u763e\uff0c\u5e76\u4e0d\u4e00\u5b9a\u8981\u201c\u771f\u5200\u771f\u67aa\u201d\u4e0a\u9635\uff0c\u539f\u56e0\u662f\u6625\u98ce\u4e00\u5ea6 \u7684\u4ee3\u4ef7\u6240\u8d39\u4e0d\u8d27\uff0c\u4e0d\u662f\u4e00\u822c\u4eba\u8d1f\u62c5\u5f97\u8d77\u7684\u3002 \u3000\u3000\u4e24\u540d\u9f9f\u5974\u6b63\u7ad9\u5728\u5927\u95e8\u8fdb\u53e3\u5904\uff0c\u4ed6\u4eec\u773c\u5c16\uff0c\u4e00\u89c1\u6709\u4e09\u4f4d\u8d35\u5ba2\u4e0a\u95e8\uff0c\u4fbf\u6709\u4e00\u540d\u53eb\u5c24\u4e09\u7684\u54c8\u7740 \u8170\uff0c\u5168\u8eab\u9aa8\u5934\u4e0d\u5230\u56db\u4e24\u91cd\u7684\u8fce\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u5c24\u4e09\u4ee5\u4e5d\u5341\u5ea6\u7684\u97a0\u8eac\uff0c\u772f\u773c\u54a7\u5634\u7684\u7b11\u8fce\u9053\uff1a\u201c\u4e09\u4f4d\u5927\u7237\u8bf7\u5230\u82b1\u5385\u5750\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u671b\u671b\u9648\u5b8f\u548c\u8d75\u4fdd\uff0c\u5e76\u672a\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u9648\u5b8f\u548c\u8d75\u4fdd\u5728\u8fd9\u65b9\u9762\u662f\u8001\u624b\u3002 \u3000\u3000\u5728\u4eac\u57ce\u65f6\uff0c\u4ed6\u4eec\u66fe\u7ecf\u662f\u516b\u5927\u80e1\u540c\u7684\u5e38\u5ba2\uff0c\u4e24\u4eba\u70b9\u4e86\u70b9\u5934\uff0c\u4fbf\u5728\u5c24\u4e09\u7684\u966a\u4f8d\u4e0b\u7531\u6c34\u69ad\u4e00\u4fa7 \u767b\u4e0a\u4e86\u82b1\u5385\u3002 \u3000\u3000\u6240\u8c13\u82b1\u5385\uff0c\u82e5\u62ff\u73b0\u5728\u7684\u540d\u8bcd\u6765\u8bb2\uff0c\u5c31\u662f\u8d35\u5bbe\u63a5\u5f85\u5ba4\u3002 \u3000\u3000\u91cc\u9762\u5206\u6210\u82e5\u5e72\u5c0f\u623f\u95f4\uff0c\u6bcf\u4e2a\u5c0f\u623f\u95f4\u90fd\u5e03\u7f6e\u5f97\u5341\u5206\u9ad8\u96c5\u3002\u51e1\u662f\u6709\u8eab\u4efd\u5730\u4f4d\u7684\u5ba2\u4eba\u524d\u6765\uff0c\u591a \u534a\u5148\u8bf7\u5230\u8fd9\u91cc\uff0c\u7136\u540e\u518d\u53ec\u5524\u59d1\u5a18\u3002\u5ba2\u4eba\u4e2d\u610f\u4e86\u4ee5\u540e\uff0c\u59d1\u5a18\u5c31\u628a\u5ba2\u4eba\u9886\u5230\u81ea\u5df1\u623f\u95f4\u3002\u5f53\u7136\uff0c\u76f8 \u719f\u7684\u5ba2\u4eba\u5c31\u7528\u4e0d\u7740\u8fd9\u4e00\u5957\u4e86\u3002 \u3000\u3000\u8fdb\u5165\u5c0f\u623f\u95f4\uff0c\u5c0f\u71d5\u5b50\u4e09\u4eba\u5750\u4e0b\u540e\uff0c\u5c24\u4e09\u5fd9\u7740\u5012\u8336\uff0c\u4e00\u8fb9\u54a7\u5634\u95ee\u9053\uff1a\u201c\u4e09\u4f4d\u5927\u7237\u597d\u50cf\u662f\u521d \u5230\u5f00\u5c01\u6765\u7684\u5427\uff1f\u201d \u3000\u3000\u9648\u5b8f\u9053\uff1a\u201c\u4f60\u7684\u773c\u529b\u4e0d\u5dee\uff0c\u4e0d\u8fc7\u53ea\u8bf4\u5bf9\u4e86\u4e00\u534a\u3002\u201d \u3000\u3000\u5c24\u4e09\u6014\u4e86\u6014\u9053\uff1a\u201c\u5927\u7237\u8fd9\u8bdd\u600e\u4e48\u8bb2\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u53ea\u80fd\u8bf4\u662f\u521d\u5230\u2018\u8fce\u6625\u9601\u2019\u6765\uff0c\u81f3\u4e8e\u5f00\u5c01\uff0c\u5374\u5df2\u6765\u8fc7\u591a\u6b21\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u662f\u8fd9\u6837\uff0c\u4e09\u4f4d\u5927\u7237\u4ece\u524d\u90a3\u51e0\u6b21\u5230\u5f00\u5c01\u6765\uff0c\u7b80\u76f4\u662f\u767d\u6765\u4e86\u3002\u201d \u3000\u3000\u9648\u5b8f\u6545\u610f\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u73b0\u5728\u8be5\u6211\u95ee\u4f60\u8fd9\u8bdd\u600e\u4e48\u8bb2\u4e86\uff1f\u201d \u3000\u3000\u5c24\u4e09\u5f20\u7740\u86e4\u87c6\u5634\u9053\uff1a\u201c\u5c0f\u7684\u662f\u8bf4\u82e5\u6765\u5f00\u5c01\uff0c\u8c01\u90fd\u4e0d\u80fd\u4e0d\u5149\u987e\u2018\u8fce\u6625\u9601\u2019\u3002\u201d \u3000\u3000\u201c\u6709\u8fd9\u79cd\u4e8b\uff1f\u2018\u8fce\u6625\u9601\u2019\u5c45\u7136\u662f\u4e0d\u6765\u4e0d\u53ef\uff0c\u5982\u679c\u4e0d\u6765\uff0c\u4f1a\u6709\u4ec0\u4e48\u5bb3\u5904\uff1f\u201d \u3000\u3000\u201c\u81f3\u5c11\u4f1a\u8ba9\u60a8\u5fc3\u60c5\u4e0d\u6109\u5feb\uff0c\u5c31\u50cf\u809a\u5b50\u997f\u4e86\u6ca1\u5403\u996d\u4e00\u6837\u3002\u201d \u3000\u3000\u201c\u6709\u8fd9\u4e48\u4e25\u91cd\u5417\uff1f\u201d \u3000\u3000\u201c\u5c0f\u7684\u7edd\u4e0d\u6562\u9a97\u4e09\u4f4d\u5927\u7237\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u602a\u4e86\uff01\u201d \u3000\u3000\u201c\u5927\u7237\u6709\u4ec0\u4e48\u5947\u602a\u7684\uff1f\u201d \u3000\u3000\u201c\u6211\u4ece\u6765\u6ca1\u5230\u8fc7\u8fd9\u79cd\u5730\u65b9\uff0c\u53ef\u662f\u809a\u5b50\u5e76\u4e0d\u89c9\u5f97\u997f\uff0c\u5982\u679c\u809a\u5b50\u997f\u4e86\uff0c\u4e5f\u662f\u8981\u5230\u996d\u9986\u53bb\u89e3\uff0c \u83ab\u975e\u4f60\u4eec\u8fd9\u91cc\u4e5f\u5f00\u996d\u8ba9\u5403\u4e0d\u6210\uff1f\u201d \u3000\u3000\u5c24\u4e09\u5e72\u54b3\u4e86\u4e24\u58f0\u9053\uff1a\u201c\u5982\u679c\u4e09\u4f4d\u5927\u7237\u8981\u5403\u996d\uff0c\u5c0f\u7684\u53ef\u4ee5\u9a6c\u4e0a\u5230\u5916\u9762\u53eb\u3002\u201d \u3000\u3000\u201c\u4e0d\u5fc5\u4e86\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u7528\u4e9b\u70b9\u5fc3\u5427\uff01\u9694\u58c1\u5c31\u662f\u70b9\u5fc3\u623f\uff0c\u5c0f\u7684\u73b0\u5728\u5c31\u53bb\u62ff\u3002\u201d \u3000\u3000\u201c\u4e5f\u4e0d\u5fc5\u4e86\u3002\u201d \u3000\u3000\u5c24\u4e09\u8fd9\u624d\u7a0d\u5fae\u5b89\u9759\u4e0b\u6765\uff0c\u642d\u8baa\u7740\u95ee\u9053\uff1a\u201c\u4e09\u4f4d\u65e2\u7136\u662f\u7b2c\u4e00\u6b21\u5230\u8fd9\u91cc\u6765\uff0c\u4e00\u5b9a\u8fd8\u4e0d\u4f1a\u6709\u76f8 \u719f\u7684\u59d1\u5a18\uff0c\u5c0f\u7684\u73b0\u5728\u5c31\u53bb\u66ff\u5927\u7237\u4eec\u53eb\u4e09\u4f4d\u59d1\u5a18\uff0c\u4e0d\u77e5\u4e09\u4f4d\u5927\u7237\u559c\u6b22\u4ec0\u4e48\u6837\u7684\u59d1\u5a18\uff1f\u201d \u3000\u3000\u9648\u5b8f\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u4f60\u5148\u8bf4\u8bf4\u8fd9\u91cc\u90fd\u6709\u4ec0\u4e48\u6837\u7684\u59d1\u5a18\uff1f\u201d \u3000\u3000\u201c\u6709\u9ad8\u7684\u3001\u77ee\u7684\u3001\u80d6\u7684\u3001\u7626\u7684\u3001\u4e0d\u9ad8\u4e0d\u77ee\u7684\u3001\u4e0d\u80d6\u4e0d\u7626\u7684\uff0c\u5168\u770b\u5ba2\u4eba\u7684\u7231\u597d\u3002\u603b\u800c\u8a00 \u4e4b\uff0c\u7edf\u800c\u8a00\u4e4b\uff0c\u603b\u7edf\u800c\u8a00\u4e4b\uff0c\u4e0d\u7ba1\u662f\u4ec0\u4e48\u4f53\u5f62\uff0c\u6ca1\u4e00\u4e2a\u4e0d\u6f02\u4eae\u7684\u3002\u201d \u3000\u3000\u201c\u6709\u6ca1\u6709\u804b\u7684\u3001\u778e\u7684\u3001\u584c\u9f3b\u7684\u3001\u5154\u5507\u7684\u3001\u7f3a\u624b\u65ad\u817f\u8fd9\u4e00\u7c7b\u7684\u59d1\u5a18\u5462\uff1f\u201d \u3000\u3000\u5c24\u4e09\u542c\u5f97\u6709\u4e9b\u54ed\u7b11\u4e0d\u5f97\uff0c\u9f87\u7740\u51e0\u4e4e\u8981\u6ef4\u51fa\u6cb9\u6765\u7684\u9ec4\u677f\u7259\u9053\uff1a\u201c\u60a8\u8001\u5f00\u73a9\u7b11\u4e86\uff0c\u5c31\u662f\u6253\u7740 \u706f\u7b3c\u627e\uff0c\u5728\u6211\u4eec\u2018\u8fce\u6625\u9601\u2019\u4e5f\u627e\u4e0d\u51fa\u8fd9\u79cd\u4eba\u6765\u3002\u201d \u3000\u3000\u9648\u5b8f\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u5982\u679c\u6211\u4eec\u559c\u6b22\u7684\u5c31\u662f\u8fd9\u79cd\u59d1\u5a18\u5462\uff1f\u201d \u3000\u3000\u201c\u5927\u7237\u8d8a\u53d1\u53d6\u7b11\u4e86\uff0c\u4e16\u754c\u4e0a\u6050\u6015\u8fd8\u6ca1\u6709\u8fd9\u79cd\u4eba\u5427\uff1f\u201d \u3000\u3000\u201c\u90a3\u662f\u4f60\u5c11\u89c1\u591a\u602a\uff0c\u6211\u4eec\u4e09\u4e2a\u5c31\u662f\u8fd9\u79cd\u4eba\u3002\u201d \u3000\u3000\u5c24\u4e09\u5446\u5728\u5f53\u5730\uff0c\u4e0d\u77e5\u8be5\u518d\u8bf4\u4ec0\u4e48\u624d\u597d\u3002 \u3000\u3000\u9648\u5b8f\u559d\u4e86\u53e3\u8336\u9053\uff1a\u201c\u65e2\u7136\u4f60\u4eec\u8fd9\u91cc\u627e\u4e0d\u5230\u6211\u521a\u624d\u6240\u8bf4\u7684\u90a3\u79cd\u59d1\u5a18\uff0c\u6211\u4eec\u4e5f\u53ea\u597d\u5c06\u5c31\u70b9 \u4e86\uff0c\u73b0\u5728\u4f60\u628a\u6240\u6709\u7684\u59d1\u5a18\u90fd\u53ec\u96c6\u6765\uff0c\u8ba9\u6211\u4eec\u81ea\u5df1\u6311\u9009\u4e00\u4e0b\uff0c\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u5c24\u4e09\u82e6\u7b11\u9053\uff1a\u201c\u5927\u7237\uff0c\u60a8\u8fd9\u4e0d\u662f\u8ba9\u5c0f\u7684\u4e3a\u96be\u5417\uff1f\u201d \u3000\u3000\u201c\u4f60\u6709\u4ec0\u4e48\u96be\u5904\uff1f\u201d\u9648\u5b8f\u6545\u610f\u95ee\u9053\uff1a\u201c\u4f60\u505a\u7684\u4e0d\u5c31\u662f\u8fd9\u79cd\u4e8b\u5417\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u8fd9\u91cc\u7684\u59d1\u5a18\u6709\u4e00\u767e\u591a\u4f4d\uff0c\u800c\u4e14\u73b0\u5728\u623f\u95f4\u91cc\u591a\u534a\u5df2\u6709\u5ba2\u4eba\uff0c\u600e\u53ef\u80fd\u628a\u6240\u6709\u7684\u4eba\u90fd\u53ec \u96c6\u6765\uff1f\u201d \u3000\u3000\u201c\u90a3\u5c31\u628a\u6ca1\u6709\u5ba2\u4eba\u7684\u59d1\u5a18\u90fd\u53ec\u96c6\u6765\u3002\u201d \u3000\u3000\u201c\u6ca1\u5ba2\u4eba\u7684\u59d1\u5a18\uff0c\u6050\u6015\u6709\u51e0\u5341\u4e2a\uff0c\u82e5\u7edf\u7edf\u53eb\u6765\uff0c\u8fd9\u91cc\u600e\u80fd\u5bb9\u7eb3\u5f97\u4e0b\uff1f\u201d \u3000\u3000\u201c\u90a3\u5c31\u5206\u6279\u524d\u6765\uff01\u201d \u3000\u3000\u201c\u8fd9\u2026\u2026\u201d \u3000\u3000\u9648\u5b8f\u8fde\u5fd9\u4ece\u6000\u91cc\u638f\u51fa\u4e00\u952d\u94f6\u5b50\uff0c\u653e\u5728\u8336\u51e0\u4e0a\u9053\uff1a\u201c\u8fd9\u662f\u4e00\u70b9\u5c0f\u610f\u601d\uff0c\u4e0d\u7ba1\u53ec\u96c6\u6765\u7684\u59d1\u5a18 \u6211\u4eec\u4e2d\u4e0d\u4e2d\u610f\uff0c\u94f6\u5b50\u90fd\u662f\u4f60\u7684\u3002\u201d \u3000\u3000\u8fd9\u952d\u94f6\u5b50\u8db3\u591f\u5c24\u4e09\u4e24\u4e2a\u6708\u7684\u85aa\u6c34\uff0c\u5c24\u4e09\u5c82\u80fd\u4e0d\u770b\u7740\u773c\u7ea2\uff0c\u867d\u7136\u9648\u5b8f\u8981\u6c42\u5f97\u8fc7\u4efd\uff0c\u770b\u5728\u94f6 \u5b50\u4efd\u4e0a\uff0c\u4ed6\u4e5f\u975e\u7167\u529e\u4e0d\u53ef\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u4ed6\u5fd9\u4e0d\u8fed\u7684\u63e3\u8d77\u94f6\u5b50\uff0c\u54c8\u7740\u8170\u9053\uff1a\u201c\u5c0f\u7684\u73b0\u5c31\u53bb\u53eb\u59d1\u5a18\u6765\uff0c\u4e0d\u8fc7\u59d1\u5a18\u5206\u6279\u524d\u6765\uff0c \u65f6\u95f4\u4e5f\u8bb8\u4f1a\u803d\u8bef\u4e45\u4e00\u70b9\uff0c\u5927\u7237\u5343\u4e07\u8981\u8010\u4f4f\u6027\u5b50\u7b49\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7740\uff0c\u4fbf\u5954\u51fa\u5c0f\u623f\u95f4\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u5927\u4e3a\u4e0d\u89e3\u7684\u95ee\u9053\uff1a\u201c\u9648\u5927\u54e5\uff0c\u4f60\u8fd9\u4e48\u505a\u662f\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9648\u5b8f\u50cf\u80f8\u6709\u6210\u7af9\u822c\u518d\u559d\u4e86\u4e00\u53e3\u8336\u9053\uff1a\u201c\u5f53\u7136\u662f\u8981\u627e\u51fa\u4f1a\u4e0b\u866b\u7684\u59d1\u5a18\u6765\u3002\u201d \u3000\u3000\u201c\u4f60\u51ed\u4ec0\u4e48\u80fd\u770b\u5f97\u51fa\u6765\uff1f\u201d \u3000\u3000\u201c\u51e1\u662f\u64c5\u4e8e\u866b\u672f\u7684\uff0c\u591a\u534a\u51fa\u81ea\u4e07\u866b\u95e8\uff0c\u4e07\u866b\u95e8\u51fa\u8eab\u7684\u591a\u534a\u5e26\u4e00\u4e9b\u5996\u6c14\uff0c\u53ea\u8981\u7559\u5fc3\u89c2\u5bdf\uff0c \u5c31\u4e0d\u96be\u770b\u51fa\u86db\u4e1d\u9a6c\u8ff9\u6765\u3002\u201d \u3000\u3000\u201c\u9648\u5927\u54e5\u76f8\u4fe1\u4f60\u6709\u8fd9\u79cd\u773c\u529b\u5417\uff1f\u201d \u3000\u3000\u201c\u8fd9\u662f\u5f53\u7136\u7684\u4e8b\uff0c\u8c01\u90fd\u6709\u8fd9\u79cd\u773c\u529b\uff0c\u5f85\u4f1a\u513f\u59d1\u5a18\u5230\u4e86\uff0c\u4e24\u4f4d\u4e5f\u8981\u5e2e\u7740\u4ed4\u7ec6\u770b\u3002\u6709\u53e5\u8bdd \u8bf4\uff0c\u773c\u775b\u8981\u76ef\u5230\u8089\u91cc\u53bb\uff0c\u51e1\u662f\u5fc3\u672f\u4e0d\u6b63\u7684\uff0c\u53cd\u5e94\u5fc5\u5b9a\u4e0e\u4f17\u4e0d\u540c\uff0c\u4e00\u773c\u5c31\u80fd\u770b\u51fa\u7834\u7efd\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u548c\u8d75\u4fdd\u4e24\u4eba\uff0c\u53ea\u6709\u59d1\u5984\u542c\u4e4b\u3002 \u3000\u3000\u4e0d\u4e00\u4f1a\u513f\uff0c\u5c24\u4e09\u4fbf\u5e26\u7740\u4e09\u540d\u59d1\u5a18\u6380\u5e18\u8d70\u8fdb\u5c0f\u623f\u95f4\u3002 \u3000\u3000\u7528\u4e0d\u7740\u9f99\u4e09\u5429\u5490\uff0c\u9648\u5b8f\u3001\u8d75\u4fdd\u3001\u5c0f\u71d5\u5b50\u4e09\u5bf9\u773c\u775b\u7acb\u523b\u4e00\u77ac\u4e0d\u77ac\u7684\u76f4\u76ef\u5728\u4e09\u540d\u59d1\u5a18\u8138\u4e0a\u3001 \u8eab\u4e0a\u3002 \u3000\u3000\u548c\u9648\u5b8f\u8bf4\u7684\u5dee\u4e0d\u591a\uff0c\u4e09\u5bf9\u773c\u775b\u516d\u6761\u89c6\u7ebf\u7adf\u771f\u50cf\u8981\u76ef\u5230\u59d1\u5a18\u4eec\u7684\u8089\u91cc\u53bb\u3002 \u3000\u3000\u8fd9\u60c5\u5f62\u8fde\u5c24\u4e09\u90fd\u770b\u5f97\u6709\u4e9b\u53d1\u5446\uff0c\u4ed6\u770b\u8fc7\u7684\u5bfb\u82b3\u5ba2\u6210\u5343\u4e0a\u4e07\uff0c\u5374\u4ece\u672a\u89c1\u5230\u5ba2\u4eba\u7adf\u201c\u8272\u201d\u5230 \u8fd9\u79cd\u7a0b\u5ea6\u3002 \u3000\u3000\u4e0d\u6d88\u8bf4\uff0c\u59d1\u5a18\u4eec\u4e5f\u90fd\u88ab\u770b\u5f97\u4f4e\u4e0b\u4e86\u5934\u3002 \u3000\u3000\u5979\u4eec\u5fc3\u91cc\u96be\u514d\u5728\u60f3\uff1a\u8fd9\u4e09\u4f4d\u5ba2\u4eba\u4e0d\u4f46\u5e74\u8f7b\uff0c\u800c\u4e14\u53c8\u90fd\u4e00\u8868\u4eba\u624d\uff0c\u4e3a\u4ec0\u4e48\u770b\u5230\u5973\u4eba\u4f1a\u8fd9\u79cd \u6837\u5b50\uff1f\u4ee5\u4ed6\u4eec\u7684\u6761\u4ef6\uff0c\u59d1\u5a18\u4eec\u53ea\u6015\u8fde\u5012\u8d34\u90fd\u5e72\uff0c\u4f55\u5fc5\u5982\u6b64\uff1f\u8db3\u8db3\u534a\u76cf\u8336\u7684\u5de5\u592b\u8fc7\u53bb\uff0c\u9648\u5b8f\u624d \u6536\u56de\u89c6\u7ebf\uff0c\u6325\u6325\u624b\u9053\uff1a\u201c\u8981\u5979\u4eec\u90fd\u56de\u53bb\uff01\u201d \u3000\u3000\u4e09\u540d\u59d1\u5a18\u8d70\u540e\uff0c\u5c24\u4e09\u6253\u4e86\u4e00\u8eac\u9053\uff1a\u201c\u4e09\u4f4d\u5927\u7237\u770b\u4e2d\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u9648\u5b8f\u6447\u5934\u9053\uff1a\u201c\u6ca1\u4e00\u4e2a\u4e2d\u610f\u7684\u3002\u201d \u3000\u3000\u5c24\u4e09\u6014\u4e86\u6014\u9053\uff1a\u201c\u4e09\u4f4d\u5927\u7237\u521a\u624d\u770b\u5f97\u90a3\u4e48\u4ed4\u7ec6\uff0c\u5c0f\u7684\u8fd8\u4ee5\u4e3a\u662f\u201d\u4f60\u4ee5\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4ee5\u4e3a\u662f\u4e00\u89c1\u949f\u60c5\u5462\uff01\u4e0d\u7136\u4f55\u5fc5\u770b\u90a3\u4e48\u4e45\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u9ad8\u5174\u770b\uff0c\u4f60\u7ba1\u5f97\u7740\u5417\uff1f\u201d \u3000\u3000\u201c\u65e2\u7136\u770b\u5f97\u9ad8\u5174\uff0c\u90a3\u4e48\u5f53\u662f\u4e2d\u610f\u624d\u5bf9\uff1f\u201d \u3000\u3000\u201c\u53ef\u60dc\u6709\u70b9\u6bdb\u75c5\u3002\u201d \u3000\u3000\u201c\u6bdb\u75c5\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u5728\u6211\u4eec\u773c\u775b\u91cc\uff0c\u6ca1\u5fc5\u8981\u544a\u8bc9\u4f60\uff0c\u5feb\u53bb\u518d\u62db\u547c\u522b\u7684\u59d1\u5a18\u6765\uff0c\u90a3\u952d\u94f6\u5b50\u4e0d\u662f\u767d\u7ed9\u4f60\u7684\u3002\u201d \u3000\u3000\u5c24\u4e09\u54c8\u7740\u8170\u5012\u9000\u51fa\u53bb\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u95ee\u9053\uff1a\u201c\u9648\u5927\u54e5\uff0c\u8fd9\u6837\u770b\u7a76\u7adf\u8981\u770b\u591a\u4e45\uff1f\u201d \u3000\u3000\u9648\u5b8f\u9053\uff1a\u201c\u73b0\u5728\u5929\u8fd8\u4e0d\u9ed1\uff0c\u5c31\u662f\u770b\u5230\u534a\u591c\uff0c\u4e5f\u8981\u770b\u4e0b\u53bb\uff0c\u4e0d\u7136\u54b1\u4eec\u5c31\u767d\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u5c0f\u5f1f\u770b\u59d1\u5a18\uff0c\u4e00\u70b9\u8981\u9886\u4e5f\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u591a\u770b\u8138\uff0c\u8eab\u4e0a\u53ea\u8981\u7784\u4e00\u7784\u5c31\u6210\u4e86\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u5979\u4eec\u8138\u4e0a\u4e5f\u6ca1\u6807\u8bb0\u3002\u201d \u3000\u3000\u201c\u6211\u4e0d\u662f\u8bf4\u8fc7\u5417\uff1f\u51e1\u662f\u4f1a\u4e0b\u866b\u7684\uff0c\u5341\u6709\u516b\u4e5d\u662f\u4e07\u866b\u95e8\u51fa\u8eab\uff0c\u4e07\u866b\u95e8\u5728\u82d7\u7586\uff0c\u82d7\u7586\u7684\u4eba\u548c \u54b1\u4eec\u4e2d\u539f\u4eba\u8138\u5f62\u548c\u8868\u60c5\u603b\u662f\u6709\u4e9b\u4e0d\u4e00\u6837\uff0c\u773c\u795e\u4e5f\u6709\u6781\u5927\u7684\u5dee\u522b\uff0c\u4f60\u53ea\u8981\u5728\u8fd9\u65b9\u9762\u591a\u7559\u610f\u5c31\u6210 \u4e86\u3002\u201d \u3000\u3000\u8bf4\u8bdd\uff1b\u53f8\uff0c\u5c24\u4e09\u53c8\u5e26\u8fdb\u4e09\u4f4d\u59d1\u5a18\u6765\u3002 \u3000\u3000\u524d\u540e\u4e24\u6b21\u8fd9\u516d\u4f4d\u59d1\u5a18\u867d\u975e\u8c8c\u7f8e\u5982\u82b1\uff0c\u81f3\u5c11\u90fd\u662f\u4e2d\u7b49\u4ee5\u4e0a\u59ff\u8272\u3002 \u3000\u3000\u7531\u6b64\u63a8\u65ad\uff0c\u201c\u8fce\u6625\u9601\u201d\u7684\u5c0a\u5c0a\u71d5\u71d5\u7684\u786e\u79f0\u5f97\u4e0a\u590f\u5929\u7684\u68c9\u88ab\u2014\u2014\u4e0d\u662f\u76d6\u7684\u3002 \u3000\u3000\u8fd9\u6b21\u9648\u5b8f\u7b49\u4e09\u4eba\uff0c\u4ecd\u662f\u8001\u6837\u5b50\uff0c\u8001\u8868\u60c5\u3002 \u3000\u3000\u4e0d\u8fc7\u8fd9\u4e09\u4f4d\u59d1\u5a18\u548c\u521a\u521a\u7684\u4e09\u4f4d\u59d1\u5a18\u5df2\u6709\u6240\u4e0d\u540c\uff0c\u5979\u4eec\u867d\u7136\u5e74\u7eaa\u4e0d\u5927\uff0c\u5374\u5df2\u79f0\u5f97\u4e0a\u662f\u201c\u8001 \u4e4c\u201d\u4e86\uff0c\u4e0d\u4f46\u4e0d\u5728\u4e4e\u522b\u4eba\u770b\uff0c\u53cd\u800c\u7d22\u6027\u548c\u9648\u5b8f\u7b49\u4e09\u4eba\u5c55\u5f00\u5bf9\u770b\u7684\u67b6\u52bf\uff0c\u7167\u6837\u4e5f\u662f\u770b\u5f97\u76ee\u4e0d\u8f6c \u775b\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u9648\u5b8f\u7b49\u4e09\u4eba\u987f\u65f6\u88ab\u770b\u5f97\u8868\u73b0\u51fa\u7518\u62dc\u4e0b\u98ce\u6a21\u6837\u3002 \u3000\u3000\u9648\u5b8f\u5fc3\u91cc\u4e00\u6025\uff0c\u5fd9\u6446\u624b\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u56de\u53bb\uff01\u201d \u3000\u3000\u504f\u504f\u4e09\u4f4d\u59d1\u5a18\u90fd\u4e0d\u80af\u8d70\u3002 \u3000\u3000\u5176\u4e2d\u4e00\u540d\u53eb\u5c0f\u6843\u7684\u59d1\u5a18\uff0c\u629b\u4e2a\u773c\u6ce2\u9053\uff1a\u201c\u8fd9\u4f4d\u5927\u7237\uff0c\u4f60\u4eec\u628a\u6211\u4eec\u770b\u4e86\u534a\u5929\uff0c\u8fde\u6211\u4eec\u8eab\u4e0a \u6709\u51e0\u6839\u6c57\u6bdb\u90fd\u4e00\u5b9a\u770b\u51fa\u6765\u4e86\uff0c\u6211\u4eec\u54ea\u4e00\u70b9\u4e0d\u597d\uff1f\u4e3a\u4ec0\u4e48\u8981\u6211\u4eec\u56de\u53bb\uff1f\u201d \u3000\u3000\u8fd9\u4e09\u540d\u59d1\u5a18\u90fd\u5f88\u591f\u98ce\u9a9a\uff0c\u5c0f\u6843\u4f3c\u4e4e\u662f\u6700\u98ce\u9a9a\u7684\u4e00\u4e2a\u3002 \u3000\u3000\u9648\u5b8f\u5e72\u54b3\u4e86\u4e24\u58f0\u9053\uff1a\u201c\u542c\u8bf4\u4f60\u4eec\u8fd9\u91cc\u6709\u767e\u591a\u4f4d\u59d1\u5a18\uff0c\u6211\u4eec\u5fc5\u987b\u5168\u90e8\u770b\u5b8c\u624d\u80fd\u51b3\u5b9a\u8981\u54ea\u4e09 \u4e2a\u3002\u201d \u3000\u3000\u201c\u5927\u7237\uff0c\u4f60\u8bf4\u8fd9\u79cd\u8bdd\u50cf\u8bdd\u5417\uff1f\u7b80\u76f4\u8981\u8ba9\u4eba\u7b11\u6389\u5927\u7259\uff01\u201d \u3000\u3000\u201c\u6211\u8bf4\u7684\u54ea\u91cc\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u4e00\u767e\u591a\u4e2a\u59d1\u5a18\uff0c\u5927\u90e8\u5206\u5c4b\u91cc\u90fd\u6709\u5ba2\u4eba\uff0c\u4f60\u60f3\u5168\u90e8\u770b\u5b8c\uff0c\u53ea\u6015\u4e00\u8f88\u5b50\u4e5f\u529e\u4e0d\u5230\u3002\u201d \u3000\u3000\u201c\u6211\u53ef\u4ee5\u5168\u90e8\u5305\u4e0b\u3002\u201d \u3000\u3000\u5c0f\u6843\u5ffd\u7136\u63a2\u51fa\u7c89\u5ae9\u7684\u624b\u81c2\u9053\uff1a\u201c\u62ff\u6765\uff01\u201d \u3000\u3000\u9648\u5b8f\u6014\u4e86\u6014\u9053\uff1a\u201c\u62ff\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5f53\u7136\u662f\u62ff\u94f6\u5b50\uff0c\u6211\u4eec\u59d1\u5a18\u63a5\u5f85\u4e00\u6b21\u5ba2\u662f\u4e09\u4e24\u94f6\u5b50\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u6211\u8fd8\u6ca1\u8ba9\u4f60\u63a5\u5f85\u3002\u201d \u3000\u3000\u201c\u6211\u662f\u8bf4\u4f60\u53ea\u8981\u62ff\u51fa\u4e94\u767e\u4e24\u94f6\u5b50\u6765\uff0c\u6211\u5c31\u8d1f\u8d23\u628a\u6240\u6709\u7684\u59d1\u5a18\u96c6\u5408\u8d77\u6765\u7ed9\u4f60\u770b\uff0c\u800c\u4e14\u5fc5\u987b \u660e\u5929\u6765\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u5fc5\u987b\u660e\u5929\u6765\uff1f\u201d \u3000\u3000\u201c\u6709\u5ba2\u4eba\u7684\u59d1\u5a18\uff0c\u603b\u4e0d\u80fd\u628a\u5ba2\u4eba\u8d76\u8d70\u5427\uff01\u201d \u3000\u3000\u201c\u90a3\u6211\u5c31\u660e\u5929\u518d\u5e26\u94f6\u5b50\u6765\u3002\u201d \u3000\u3000\u5c0f\u6843\u6487\u4e86\u64a4\u5634\u9053\uff1a\u201c\u6211\u770b\u5927\u7237\u8fd8\u662f\u7701\u7701\u5427\uff01\u6709\u4e86\u90a3\u4e94\u767e\u4e24\u94f6\u5b50\uff0c\u6211\u5c0f\u6843\u60c5\u613f\u966a\u4f60\u4e09\u4e2a \u6708\uff0c\u5982\u679c\u4f60\u89c9\u5f97\u5bf9\u6211\u5f88\u6ee1\u610f\uff0c\u966a\u4f60\u534a\u5e74\u4e5f\u6210\uff0c\u53e6\u5916\u4e09\u4e2a\u6708\u4e0d\u7b97\u94b1\uff0c\u7531\u6211\u5012\u8d34\u3002\u201d \u3000\u3000\u9648\u5b8f\u8fd8\u771f\u662f\u96be\u4ee5\u5e94\u4ed8\u5c0f\u6843\u8fd9\u79cd\u5973\u4eba\uff0c\u987f\u4e86\u987f\u9053\uff1a\u201c\u8001\u5b50\u6709\u4e86\u94b1\uff0c\u4f55\u5fc5\u4e00\u5b9a\u627e\u4f60\uff1f\u201d \u3000\u3000\u5c0f\u6843\u5a9a\u773c\u4e00\u98de\uff1a\u201c\u5927\u7237\uff0c\u597d\u5403\u7684\u6843\u5b50\u5fc5\u987b\u54ac\u4e0a\u4e00\u53e3\u624d\u77e5\u9053\uff0c\u53ea\u8981\u4f60\u8ddf\u6211\u4e0a\u4e86\u5e8a\uff0c\u4fdd\u8bc1\u4f60 \u4e0d\u60f3\u518d\u4e0b\u5e8a\uff0c\u4f60\u4e0d\u59a8\u6253\u542c\u6253\u542c\uff0c\u6211\u5c0f\u6843\u5728\u2018\u8fce\u6625\u9601\u2019\u53ef\u4e0d\u662f\u76d6\u7684\uff0c\u5ba2\u4eba\u53ea\u8981\u7167\u987e\u6211\u4e00\u6b21\uff0c\u5c31 \u4e00\u5b9a\u662f\u6211\u6c38\u8fdc\u7684\u8001\u6237\u5934\u3002\u201d \u3000\u3000\u9648\u5b8f\u76b1\u7709\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u4f60\u4e3a\u4ec0\u4e48\u73b0\u5728\u8fd8\u6ca1\u5ba2\u4eba\u4e0a\u95e8\uff1f\u201d \u3000\u3000\u201c\u6211\u7684\u5ba2\u4eba\u521a\u521a\u624d\u8d70\uff0c\u51b2\u7740\u5c24\u4e09\u7684\u9762\u5b50\uff0c\u5974\u5bb6\u624d\u6765\u7ed9\u4f60\u770b\uff0c\u4e0d\u4fe1\u4f60\u5c31\u5230\u6211\u623f\u95e8\u53e3\u770b\u770b\uff0c \u4e00\u5b9a\u53c8\u6709\u5ba2\u4eba\u5728\u7b49\u4e86\u3002\u201d \u3000\u3000\u201c\u90a3\u4f60\u5c31\u5fd9\u4f60\u7684\u53bb\u5427\uff01\u5927\u7237\u6211\u4e0d\u60f3\u803d\u8bef\u4f60\u7684\u751f\u610f\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u4eba\u5bb6\u5df2\u7ecf\u770b\u4e0a\u4e86\u4f60\u3002\u201d \u3000\u3000\u201c\u6211\u6709\u4ec0\u4e48\u597d\u770b\u7684\uff1f\u201d \u3000\u3000\u201c\u4f60\u8fd9\u4f4d\u5927\u7237\u65e2\u9ad8\u5927\u53c8\u7ed3\u5b9e\uff0c\u4e00\u8868\u4eba\u624d\uff0c\u552f\u6709\u548c\u6211\u5728\u4e00\u8d77\uff0c\u624d\u80fd\u914d\u6210\u771f\u6b63\u7684\u4e00\u5bf9\u3002\u201d \u3000\u3000\u5c0f\u6843\u8bf4\u5230\u8fd9\u91cc\uff0c\u731b\u7136\u4e0a\u524d\u4e00\u62c9\u9648\u5b8f\uff0c\u9053\uff1a\u201c\u5927\u7237\uff0c\u8ddf\u6211\u8d70\u5427\uff01\u201d \u3000\u3000\u4e00\u8fb9\u53c8\u56de\u5934\u9053\uff1a\u201c\u674f\u82b1\u3001\u6d77\u68e0\uff01\u53e6\u5916\u4e24\u4f4d\u5927\u7237\u4e5f\u4e0d\u9519\uff0c\u4f60\u4eec\u8fd8\u7ad9\u5728\u90a3\u91cc\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c31\u5728\u9648\u5b8f\u3001\u8d75\u4fdd\u548c\u5c0f\u71d5\u5b50\u79bb\u5f00\u4e1c\u90e8\u5ba2\u6808\u524d\u5f80\u201c\u8fce\u6625\u9601\u201d\u540e\uff0c\u4e00\u540d\u8eab\u7740\u4fbf\u88c5\u7684\u5f00\u5c01\u5e9c\u6355\u5feb \u5077\u5077\u7684\u8fdb\u5165\u4e86\u4e1c\u90fd\u5ba2\u6808\u3002 \u3000\u3000\u8fd9\u540d\u6355\u5feb\u5e74\u5728\u56db\u5341\u5de6\u53f3\uff0c\u540d\u53eb\u5f20\u4ec1\uff0c\u5728\u5f00\u5c01\u5e9c\u5145\u4efb\u6355\u5feb\u5df2\u6709\u4e03\u516b\u5e74\uff0c\u662f\u66fe\u4efb\u5f00\u5c01\u5e9c\u603b\u6355 \u5934\u71d5\u98de\u7684\u8001\u90e8\u4e0b\u3002 \u3000\u3000\u4ed6\u6b64\u6765\u6b63\u662f\u8981\u627e\u8001\u957f\u5b98\u71d5\u98de\u7684\u3002 \u3000\u3000\u6b63\u597d\u8fd9\u65f6\u71d5\u98de\u5728\u540e\u9662\u5ba2\u5385\uff0c\u548c\u738b\u5f64\u3001\u6c5f\u5343\u91cc\u7b49\u4eba\u559d\u8336\u804a\u5929\u3002 \u3000\u3000\u5f20\u4ec1\u4e00\u95ef\u8fdb\u5ba2\u5385\u4fbf\u5411\u71d5\u98de\u53cc\u819d\u8dea\u5012\uff0c\u800c\u4e14\u8fd8\u6ee1\u9762\u6cea\u6d41\u3002 \u3000\u3000\u71d5\u98de\u5b9a\u4e86\u5b9a\u795e\uff0c\u624d\u5403\u60ca\u7684\u53eb\u9053\uff1a\u201c\u5f20\u4ec1\uff0c\u600e\u4e48\u662f\u4f60\uff1f\u4f60\u600e\u4e48\u4f1a\u8fd9\u4e2a\u6837\u5b50\uff1f\u5feb\u5feb\u8d77\u6765\uff0c\u6709 \u8bdd\u597d\u597d\u8bb2\uff01\u201d \u3000\u3000\u63a5\u7740\u53c8\u6307\u6307\u738b\u3001\u6c5f\u4e24\u4eba\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u5927\u5185\u738b\u7edf\u9886\uff0c\u90a3\u4f4d\u662f\u6c5f\u5927\u4fa0\uff0c\u5148\u89c1\u8fc7\u4ed6\u4eec\u4e24\u4f4d\u3002\u201d \u3000\u3000\u5f20\u4ec1\u867d\u4e0d\u8ba4\u8bc6\u738b\u5f64\uff0c\u5374\u89c1\u8fc7\u6c5f\u5343\u91cc\u3002 \u3000\u3000\u56e0\u4e3a\u6c5f\u5343\u91cc\u4e24\u5e74\u524d\u66fe\u88ab\u5173\u5728\u5f00\u5c01\u5e9c\u5927\u7262\u534a\u5e74\u591a\uff0c\u4e8e\u662f\u8fde\u5fd9\u518d\u5411\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u4e8c\u4eba\u53e9\u5934\uff0c \u7136\u540e\u8d77\u8eab\u7ad9\u5728\u4e00\u65c1\u3002 \u3000\u3000\u71d5\u98de\u671b\u7740\u5f20\u4ec1\u9053\uff1a\u201c\u770b\u4f60\u8fd9\u6837\u5b50\uff0c\u5fc5\u5b9a\u6709\u8981\u7d27\u7684\u4e8b\u6765\u627e\u6211\uff0c\u6709\u4e8b\u73b0\u5728\u5c31\u8bf7\u660e\u8bb2\u5427\uff01\u201d \u3000\u3000\u5f20\u4ec1\u671b\u671b\u738b\u5f64\uff0c\u518d\u671b\u671b\u6c5f\u5343\u91cc\uff0c\u5374\u6b32\u8a00\u53c8\u6b62\u3002 \u3000\u3000\u71d5\u98de\u5fd9\u9053\uff1a\u201c\u6709\u8bdd\u53ea\u7ba1\u8bf4\uff0c\u738b\u5927\u4eba\u662f\u6211\u7684\u957f\u5b98\uff0c\u6c5f\u5927\u4fa0\u662f\u6211\u7684\u597d\u53cb\uff0c\u90fd\u662f\u81ea\u5df1\u4eba\uff0c\u4e0d\u7ba1 \u4ec0\u4e48\u4e8b\uff0c\u8ba9\u4ed6\u4eec\u77e5\u9053\u548c\u8ba9\u6211\u77e5\u9053\u6ca1\u4ec0\u4e48\u4e24\u6837\u3002\u201d \u3000\u3000\u5f20\u4ec1\u8fd9\u624d\u987f\u4e86\u987f\u9053\uff1a\u201c\u4e8b\u60c5\u662f\u603b\u5ea7\u5728\u5f00\u5c01\u5e9c\u4efb\u804c\u65f6\u4fbf\u5df2\u53d1\u751f\u4e86\uff0c\u5f53\u65f6\u77e5\u5e9c\u5927\u4eba\u4ee5\u4e00\u767e\u4e24 \u94f6\u5b50\uff0c\u8ba8\u4e86\u4e00\u540d\u4f8d\u59be\u2026\u2026\u201d \u3000\u3000\u71d5\u98de\u70b9\u70b9\u5934\u9053\uff1a\u201c\u8fd9\u4e8b\u6211\u5f53\u7136\u77e5\u9053\uff0c\u4e3a\u4e86\u8fd9\u4e8b\uff0c\u8fde\u6c5f\u5927\u4fa0\u90fd\u6124\u6124\u4e0d\u5e73\uff0c\u800c\u4e14\u6c5f\u5927\u4fa0\u4e5f\u6b63 \u662f\u4e3a\u4e86\u8fd9\u4e8b\u4f24\u4e86\u4eba\u624d\u5750\u7262\u7684\u3002\u5f20\u4ec1\uff0c\u4f60\u4e3a\u4ec0\u4e48\u5ffd\u7136\u63d0\u8d77\u8fd9\u4e8b\u6765\uff1f\u201d \u3000\u3000\u5f20\u4ec1\u671b\u4e86\u6c5f\u5343\u91cc\u4e00\u773c\u9053\uff1a\u201c\u542c\u8bf4\u6c5f\u5927\u4fa0\u51fa\u72f1\u540e\uff0c\u8fd8\u7279\u522b\u5728\u5de1\u629a\u5927\u4eba\u9762\u524d\u63d0\u8d77\u8fd9\u4e8b\uff0c\u5f53\u65f6 \u77e5\u5e9c\u5927\u4eba\u4e5f\u5728\u5ea7\uff0c\u4e0d\u77e5\u662f\u5426\u6709\u8fd9\u56de\u4e8b\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u5f53\u65f6\u5de1\u629a\u5927\u4eba\u8981\u628a\u77e5\u5e9c\u5927\u4eba\u7f5a\u4ff8\u534a\u5e74\uff0c\u4e14\u628a\u8fd9\u94b1\u9001\u7ed9\u7537\u65b9\uff0c\u8981\u90a3\u5e74\u8f7b\u4eba\u6b7b\u4e86\u8fd9 \u6761\u5fc3\uff0c\u53e6\u8ba8\u4e00\u623f\u5ab3\u5987\uff0c\u81f3\u4e8e\u77e5\u5e9c\u5927\u4eba\u540e\u6765\u662f\u5426\u66fe\u8fd9\u4e48\u505a\uff1f\u56e0\u4e3a\u6211\u5df2\u79bb\u5f00\u5f00\u5c01\uff0c\u6240\u4ee5\u5c31\u4e0d\u5f97\u800c \u77e5\u4e86\u3002\u201d \u3000\u3000\u201c\u77e5\u5e9c\u5927\u4eba\u6839\u672c\u6ca1\u8fd9\u4e48\u505a\uff0c\u5de1\u629a\u5927\u4eba\u4e5f\u6ca1\u518d\u8ffd\u7a76\u3002\u201d \u3000\u3000\u201c\u5176\u5b9e\u5f53\u65f6\u6211\u4e5f\u77e5\u9053\uff0c\u5de1\u629a\u5927\u4eba\u4e0d\u8fc7\u6545\u610f\u5728\u6c5f\u5927\u4fa0\u9762\u524d\u8bf4\u8bf4\u800c\u5df2\uff0c\u5b98\u573a\u91cc\u7684\u4e8b\u672c\u6765\u5c31\u662f \u5982\u6b64\uff0c\u4f60\u53ea\u4e0d\u8fc7\u662f\u4e00\u540d\u5c0f\u5c0f\u6355\u5feb\uff0c\u7eb5\u7136\u5185\u5fc3\u4e0d\u5e73\uff0c\u4e5f\u7528\u4e0d\u7740\u6765\u8ddf\u6211\u8bb2\u3002\u201d \u3000\u3000\u5c82\u77e5\u5f20\u4ec1\u970e\u65f6\u53c8\u6d41\u4e0b\u4e86\u773c\u6cea\uff0c\u4f3c\u6709\u6ee1\u8179\u51a4\u6124\uff0c\u62ac\u8896\u62ed\u7740\u6cea\u6c34\uff0c\u9053\uff1a\u201c\u603b\u5ea7\uff0c\u60a8\u53ef\u77e5\u9053\u548c \u77e5\u5e9c\u5927\u4eba\u90a3\u4f8d\u59be\u8981\u597d\u7684\u5e74\u8f7b\u4eba\u662f\u8c01\u5417\uff1f\u201d \u3000\u3000\u71d5\u98de\u6447\u5934\u9053\uff1a\u201c\u6211\u53ea\u542c\u8bf4\u90a3\u5e74\u8f7b\u4eba\u59d3\u6797\uff0c\u8be6\u7ec6\u6765\u5389\u5e76\u4e0d\u6e05\u695a\u3002\u201d \u3000\u3000\u201c\u90a3\u5e74\u8f7b\u4eba\u53eb\u6797\u5bb6\u58f0\uff0c\u4ed6\u2026\u2026\u201d \u3000\u3000\u201c\u4ed6\u600e\u4e48\u4e86\uff1f\u201d \u3000\u3000\u201c\u4ed6\u662f\u5c5e\u4e0b\u7684\u4eb2\u5916\u7525\uff01\u201d \u3000\u3000\u71d5\u98de\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u539f\u6765\u662f\u8fd9\u6837\uff01\u4f60\u4ee5\u524d\u4e3a\u4ec0\u4e48\u4e0d\u8bf4\uff1f\u201d \u3000\u3000\u5f20\u4ec1\u54fd\u54bd\u7740\u9053\uff1a\u201c\u4ee5\u524d\u5c5e\u4e0b\u4e0d\u6562\u8bf4\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u4e3a\u4ec0\u4e48\u6562\u8bb2\uff1f\u201d \u3000\u3000\u201c\u73b0\u5728\u5c5e\u4e0b\u662f\u4e0d\u5f97\u4e0d\u8bb2\uff0c\u800c\u4e14\u603b\u5ea7\u5df2\u79bb\u5f00\u5f00\u5c01\u5e9c\uff0c\u8be5\u662f\u77e5\u5e9c\u5927\u4eba\u7684\u4e0a\u7ea7\u4e86\u3002\u201d \u3000\u3000\u71d5\u98de\u53ea\u5f97\u5b89\u6170\u7740\u9053\uff1a\u201c\u5f20\u4ec1\uff0c\u6211\u73b0\u5728\u867d\u7136\u662f\u5927\u5185\u7684\u4eba\uff0c\u4f46\u5728\u4f53\u5236\u4e0a\u6765\u8bb2\uff0c\u5730\u65b9\u5b98\u540f\u662f\u5c42 \u5c42\u8282\u5236\uff0c\u77e5\u53bf\u5f52\u77e5\u53bf\u7ba1\uff0c\u77e5\u5e9c\u5f52\u5de1\u629a\u7ba1\uff0c\u5de1\u629a\u4e0a\u9762\u8fd8\u6709\u603b\u7763\uff0c\u5927\u5185\u7684\u4eba\u6839\u672c\u65e0\u6cd5\u771f\u63a5\u7ba1\u5230\u77e5 \u5e9c\u8859\u95e8\u7684\u4e8b\uff0c\u4f60\u6c42\u6211\u53c8\u6709\u4f55\u7528\uff1f\u518d\u8bf4\u8fd9\u79cd\u4e8b\u5728\u5b98\u573a\u4e0a\u5df2\u662f\u53f8\u7a7a\u89c1\u60ef\uff0c\u4f60\u4e00\u540d\u5c0f\u5c0f\u6355\u5feb\uff0c\u5fc3\u91cc \u4e0d\u5e73\u53ea\u80fd\u85cf\u5728\u5fc3\u91cc\uff0c\u5982\u679c\u60f3\u7ba1\uff0c\u6839\u672c\u4e0d\u662f\u4f60\u7684\u529b\u91cf\u6240\u80fd\u529e\u5f97\u5230\u7684\u3002\u201d \u3000\u3000\u7a81\u89c1\u5f20\u4ec1\u201c\u6251\u901a\u201d\u4e00\u58f0\uff0c\u53c8\u8dea\u4e86\u4e0b\u53bb\uff0c\u53e9\u5934\u5982\u6363\u849c\u822c\u9053\uff1a\u201c\u603b\u5ea7\uff0c\u5c5e\u4e0b\u8fd8\u6ca1\u8bb2\u660e\u767d\uff0c\u5982 \u679c\u4e8b\u60c5\u4e0d\u7d27\u6025\uff0c\u5c5e\u4e0b\u53c8\u600e\u6562\u6597\u80c6\u6765\u6c42\u60a8\uff1f\u201d \u3000\u3000\u71d5\u98de\u5fc3\u5934\u4e00\u9707\uff0c\u8f6c\u52a8\u7740\u4e24\u773c\u9053\uff1a\u201c\u83ab\u975e\u4f60\u90a3\u5916\u7525\u6797\u5bb6\u58f0\u51fa\u4e86\u4e8b\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u77e5\u5e9c\u5927\u4eba\u5df2\u7ecf\u628a\u4ed6\u5b9a\u4e86\u6b7b\u7f6a\uff0c\u5c31\u5728\u8fd9\u51e0\u5929\u4fbf\u8981\u884c\u5211\u3002\u201d \u3000\u3000\u71d5\u98de\u4e24\u773c\u5927\u7741\u9053\uff1a\u201c\u6709\u8fd9\u79cd\u4e8b\uff1f\u4f60\u628a\u539f\u56e0\u8bf4\u660e\u767d\uff01\u201d \u3000\u3000\u201c\u603b\u5ea7\uff0c\u77e5\u5e9c\u5927\u4eba\u90a3\u4f8d\u59be\u53eb\u5b59\u5c0f\u51e4\uff0c\u60a8\u662f\u77e5\u9053\u7684\uff0c\u5979\u548c\u5c5e\u4e0b\u90a3\u5916\u7525\u6797\u5bb6\u58f0\uff0c\u4e0d\u4f46\u662f\u9752\u6885 \u7af9\u9a6c\u7684\u60c5\u4eba\uff0c\u800c\u4e14\u5df2\u7ecf\u8ba2\u8fc7\u5a5a\uff0c\u5c31\u5728\u79bb\u6210\u4eb2\u8fd8\u4e0d\u5230\u4e09\u4e2a\u6708\u65f6\uff0c\u786c\u751f\u751f\u7684\u88ab\u77e5\u5e9c\u5927\u4eba\u7ed9\u62c6\u6563 \u4e86\u3002\u201d \u3000\u3000\u201c\u518d\u8bf4\u4e0b\u53bb\uff01\u201d \u3000\u3000\u201c\u5c5e\u4e0b\u90a3\u5916\u7525\u662f\u4e2a\u75f4\u60c5\u7684\u5e74\u8f7b\u4eba\uff0c\u5bf9\u8fd9\u4e8b\u5f53\u7136\u4e0d\u80af\u6b7b\u5fc3\uff0c\u5c31\u5728\u534a\u5e74\u524d\u4e00\u4e2a\u591c\u665a\uff0c\u7adf\u7136\u5077 \u5077\u6f5c\u5165\u540e\u8859\uff0c\u548c\u5b59\u5c0f\u51e4\u5e7d\u4f1a\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u89c1\u5230\u9762\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u82e5\u80fd\u89c1\u5230\u9762\uff0c\u6b7b\u4e86\u4e5f\u7b97\u662f\u4e0d\u51a4\uff0c\u5c5e\u4e0b\u90a3\u5916\u7525\u521a\u4e00\u6478\u8fdb\u540e\u8859\uff0c\u4fbf\u88ab\u5b88\u536b\u5728\u540e\u8859\u7684\u5f1f\u5144\u6349 \u4f4f\u4e86\u3002\u201d \u3000\u3000\u201c\u6349\u4f4f\u4e86\u4fbf\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u201c\u77e5\u5e9c\u5927\u4eba\u4eb2\u81ea\u50fb\u5ba4\u5ba1\u95ee\uff0c\u786c\u662f\u95ee\u4e86\u4ed6\u4e2a\u79c1\u901a\u76d7\u532a\u3001\u56fe\u8c0b\u4e0d\u8f68\u7684\u7f6a\u540d\uff0c\u5728\u5c48\u6253\u6210\u62db\u4e4b \u4e0b\uff0c\u5b9a\u4e86\u6b7b\u7f6a\uff0c\u542c\u8bf4\u5c31\u5728\u8fd9\u4e00\u4e24\u5929\u4fbf\u8981\u5904\u51b3\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e8b\u5b59\u5c0f\u51e4\u77e5\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u662f\u5426\u6709\u4eba\u544a\u8bc9\u5979\uff0c\u5c5e\u4e0b\u4e0d\u6e05\u695a\u3002\u201d \u3000\u3000\u201c\u4f60\u8be5\u77e5\u9053\u5b59\u5c0f\u51e4\u5bf9\u4ed6\u662f\u5426\u4f9d\uff1b\u65e5\u6709\u60c5\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u9752\u6885\u7af9\u9a6c\u957f\u5927\uff0c\u6839\u672c\u4e0d\u53ef\u80fd\u4e00\u4e0b\u5b50\u53d8\u5fc3\uff0c\u8fd9\u662f\u8c01\u90fd\u60f3\u50cf\u5f97\u5230\u7684\u3002\u201d \u3000\u3000\u201c\u5b59\u5c0f\u51e4\u76ee\u524d\u5728\u77e5\u5e9c\u5927\u4eba\u9762\u524d\u662f\u5426\u5f88\u5f97\u5ba0\uff1f\u201d \u3000\u3000\u201c\u5979\u5e74\u8f7b\u6f02\u4eae\uff0c\u77e5\u5e9c\u5927\u4eba\u5f53\u7136\u4f1a\u5f88\u559c\u6b22\u5979\u3002\u201d \u3000\u3000\u71d5\u98de\u4e0d\u518d\u95ee\u4ec0\u4e48\uff0c\u53ea\u662f\u4f4e\u4e0b\u5934\u53bb\uff0c\u4e45\u4e45\u4e0d\u53d1\u4e00\u8bedc\u5f20\u4ec1\u53c8\u53e9\u8d77\u5934\u6765\uff0c\u4e00\u9762\u54c0\u6c42\u7740\u9053\uff1a \u201c\u603b\u5ea7\uff0c\u60a8\u4e00\u5b9a\u8981\u8bbe\u6cd5\u6551\u6551\u5c5e\u4e0b\u90a3\u5916\u7525\uff0c\u60a8\u73b0\u5728\u662f\u7687\u4e0a\u8ddf\u524d\u7684\u4eba\uff0c\u53ea\u8981\u7ad9\u51fa\u6765\u8bf4\u4e00\u53e5\u8bdd\uff0c\u77e5 \u5e9c\u5927\u4eba\u5c31\u4e0d\u6562\u4e0d\u4e70\u5e10\u3002\u201d \u3000\u3000\u63a5\u7740\u53c8\u8f6c\u5411\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u53e9\u5934\u3002 \u3000\u3000\u71d5\u98de\u53ea\u80fd\u671b\u7740\u738b\u5f64\u548c\u6c5f\u5343\u91cc\uff0c\u73b0\u51fa\u6073\u6c42\u7684\u773c\u5149\uff0c\u5374\u65e0\u6cd5\u5bf9\u5f20\u4ec1\u4f5c\u4efb\u4f55\u627f\u8bfa\u3002 \u3000\u3000\u53ea\u542c\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u8981\u4ed6\u56de\u53bb\uff0c\u4e0d\u7ba1\u5982\u4f55\uff0c\u54b1\u4eec\u5e94\u8be5\u66ff\u4ed6\u60f3\u60f3\u529e\u6cd5\u3002\u201d \u3000\u3000\u5f20\u4ec1\u8f6c\u5411\u6c5f\u5e72\u91cc\uff0c\u518d\u5ea6\u53e9\u5934\u9053\uff1a\u201c\u6c5f\u5927\u4fa0\uff0c\u60a8\u771f\u7684\u6709\u529e\u6cd5\u6551\u51fa\u6211\u90a3\u5916\u7525\u5417\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u957f\u957f\u5401\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u6c5f\u67d0\u53ea\u80fd\u5c3d\u529b\u800c\u4e3a\uff0c\u4e0d\u8fc7\u6c5f\u67d0\u65e2\u7136\u8bdd\u5df2\u51fa\u53e3\uff0c\u4f60\u5c31\u5927\u53ef\u653e\u4e0b \u5fc3\u6765\uff0c\u6211\u603b\u4f1a\u7ed9\u4f60\u4e00\u4e2a\u4ea4\u5f85\u3002\u201d \u3000\u3000\u5f20\u4ec1\u6709\u5982\u559c\u4ece\u5929\u964d\uff0c\u5fd9\u9053\uff1a\u201c\u90a3\u592a\u597d\uff0c\u5982\u679c\u6c5f\u5927\u4fa0\u80fd\u6551\u51fa\u6211\u90a3\u5916\u7525\uff0c\u6211\u548c\u6211\u90a3\u5916\u7525\u5c31\u662f \u4e0b\u8f88\u5b50\u4e5f\u4e0d\u4f1a\u5fd8\u8bb0\u60a8\u8001\u4eba\u5bb6\u7684\u5927\u6069\u5927\u5fb7\u3002\u201d \u3000\u3000\u201c\u4e0d\u5fc5\u8bf4\u90a3\u4e48\u591a\uff0c\u5feb\u5feb\u8d77\u6765\u56de\u8859\u53bb\uff0c\u82e5\u4f60\u6765\u8fc7\u8fd9\u91cc\u7684\u4e8b\u88ab\u77e5\u5e9c\u8859\u95e8\u77e5\u9053\uff0c\u53cd\u800c\u4e0d\u597d\u529e \u4e86\u3002\u201d \u3000\u3000\u201c\u6c5f\u5927\u4fa0\u52a1\u5fc5\u8d8a\u5feb\u8d8a\u597d\uff0c\u6211\u90a3\u5916\u7525\u5c31\u5728\u8fd9\u51e0\u5929\u4fbf\u8981\u884c\u5211\uff0c\u82e5\u65f6\u95f4\u665a\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\uff0c\u7528\u4e0d\u7740\u4f60\u53ee\u549b\uff0c\u5feb\u4e9b\u56de\u53bb\u8981\u7d27\u3002\u201d \u3000\u3000\u5f20\u4ec1\u8d70\u540e\uff0c\u738b\u5f64\u7d27\u8e59\u7740\u7709\u5934\u9053\uff1a\u201c\u6c5f\u5144\uff0c\u8fd9\u662f\u4ef6\u5f88\u9ebb\u70e6\u7684\u4e8b\uff0c\u53ea\u6015\u4e0d\u597d\u5904\u7406\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u53cd\u95ee\u9053\uff1a\u201c\u738b\u8001\u5f1f\u8ba4\u4e3a\u9ebb\u70e6\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u71d5\u8001\u5f1f\u521a\u624d\u8bf4\u5f97\u5f88\u5bf9\uff0c\u800c\u4e14\u6c5f\u5144\u4e5f\u77e5\u9053\uff0c\u5927\u5185\u7684\u4eba\u53ea\u80fd\u529e\u7687\u5bab\u7684\u79c1\u4e8b\uff0c\u82e5\u5e72 \u6d89\u5230\u5730\u65b9\u5b98\u540f\u7684\u653f\u4e8b\uff0c\u4fbf\u662f\u8d8a\u6743\uff1b\u5018\u82e5\u5c39\u77e5\u5e9c\u5012\u544a\u4e00\u72b6\uff0c\u5144\u5f1f\u867d\u7136\u8eab\u4e3a\u5927\u5185\u4f8d\u536b\u7edf\u9886\uff0c\u4e5f\u4f1a \u5403\u4e0d\u4e86\u515c\u7740\u8d70\u7684\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u65e2\u7136\u6c5f\u67d0\u7b54\u5e94\u4e86\u5f20\u4ec1\uff0c\u4e8b\u60c5\u5f53\u7136\u8981\u7531\u6211\u4e00\u4eba\u627f\u62c5\uff0c\u7edd\u4e0d\u7275\u626f\u5230\u4f60 \u4eec\u5927\u5185\u7684\u4eba\uff0c\u738b\u8001\u5f1f\u5c3d\u7ba1\u4e0d\u5fc5\u8ffd\u95ee\u3002\u201d \u3000\u3000\u738b\u5f64\u88ab\u8bf4\u5f97\u8138\u4e0a\u53d1\u70ed\uff0c\u987f\u4e86\u987f\u9053\uff1a\u201c\u6c5f\u5144\u6253\u7b97\u600e\u4e48\u529e\u8fd9\u4ef6\u4e8b\uff1f\u662f\u5426\u53ef\u4ee5\u5148\u544a\u8bc9\u5144\u5f1f\uff0c\u5176 \u5b9e\u5144\u5f1f\u53c8\u4f55\u5c1d\u4e0d\u60f3\u5e2e\u5f20\u4ec1\uff0c\u53ea\u662f\u788d\u4e8e\u671d\u5ef7\u4f53\u5236\uff0c\u4e0d\u4fbf\u51fa\u9762\u800c\u5df2\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u6c5f\u67d0\u5c31\u544a\u8bc9\u738b\u8001\u5f1f\uff0c\u7531\u4e8e\u4e8b\u60c5\u5df2\u7ecf\u592a\u7d27\u8feb\uff0c\u9664\u4e86\u52ab\u72f1\uff0c\u6ca1\u6709\u7b2c\u4e8c\u4e2a\u529e\u6cd5\u3002\u201d \u3000\u3000\u201c\u5bf9\uff0c\u8fd9\u7684\u786e\u662f\u76ee\u524d\u552f\u4e00\u7684\u529e\u6cd5\uff0c\u53ea\u662f\u82e5\u56e0\u52ab\u72f1\u800c\u4f24\u4eba\uff0c\u4f3c\u4e4e\u4e0d\u59a5\u3002\u201d \u3000\u3000\u201c\u4f60\u653e\u5fc3\uff0c\u6c5f\u67d0\u7edd\u4e0d\u4f1a\u4f24\u4ed6\u4e00\u5175\u4e00\u5352\u3002\u201d \u3000\u3000\u201c\u6c5f\u5144\u662f\u5426\u8981\u4eb2\u81ea\u51fa\u9a6c\uff1f\u201d \u3000\u3000\u201c\u6c5f\u67d0\u66fe\u5728\u5f00\u5c01\u5e9c\u5927\u7262\u8e72\u8fc7\u534a\u5e74\uff0c\u82e5\u4eb2\u81ea\u51fa\u9a6c\uff0c\u8f7b\u8f66\u719f\u8def\uff0c\u5f53\u7136\u662f\u4e07\u65e0\u4e00\u5931\uff0c\u4e0d\u8fc7\u8fd9\u6b21 \u6c5f\u67d0\u51b3\u5b9a\u8ba9\u8d24\u3002\u201d \u3000\u3000\u201c\u6c5f\u5144\u51c6\u5907\u6d3e\u8c01\u53bb\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u671b\u4e86\u71d5\u98de\u4e00\u773c\uff0c\u7f13\u7f13\u7684\u9053\uff1a\u201c\u71d5\u8001\u5f1f\uff0c\u6c5f\u67d0\u51b3\u5b9a\u4ea4\u4ee3\u5c0f\u71d5\u5b50\u529e\u8fd9\u4ef6\u4e8b\uff0c\u4f60\u8be5\u4e0d\u4f1a \u53cd\u5bf9\u5427\uff1f\u201d \u3000\u3000\u71d5\u98de\u987f\u4e86\u987f\u9053\uff1a\u201c\u4ed6\u529e\u5f97\u4e86\u5417\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u7b11\u9053\uff1a\u201c\u4f60\u522b\u5c0f\u770b\u4e86\u4f60\u90a3\u513f\u5b50\uff0c\u6bd4\u8d77\u4ed6\u6765\uff0c\u4f60\u8fd8\u5dee\u5f97\u8fdc\uff1b\u5373\u4ee5\u6c5f\u67d0\u548c\u4ed6\u6bd4\uff0c\u4e0d\u8bba \u6b66\u529f\u673a\u667a\uff0c\u4e5f\u672a\u5fc5\u80dc\u5f97\u8fc7\u4ed6\u3002\u201d \u3000\u3000\u71d5\u98de\u9732\u51fa\u4e3a\u96be\u4e4b\u8272\u9053\uff1a\u201c\u53ef\u662f\u52ab\u72f1\u7684\u4e8b\uff0c\u603b\u662f\u4e0d\u6b63\u5f53\uff0c\u5144\u5f1f\u8fd8\u771f\u4e0d\u65b9\u4fbf\u5f53\u9762\u4ea4\u4ee3\u4ed6\u8fd9\u4ef6 \u4e8b\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u5475\u5475\u7b11\u9053\uff1a\u201c\u5c31\u7531\u6211\u4ea4\u4ee3\u4ed6\u5427\uff01\u4f60\u5df2\u628a\u4ed6\u4ea4\u7ed9\u6211\u4e86\uff0c\u81ea\u7136\u8be5\u7531\u6211\u8c03\u6559\u4ed6\uff0c\u52ab\u72f1\u867d \u7136\u4e0d\u662f\u4ef6\u597d\u4e8b\uff0c\u4f46\u8fd9\u6b21\u7684\u52ab\u72f1\uff0c\u51fa\u53d1\u70b9\u5374\u662f\u6551\u4eba\uff0c\u53cd\u800c\u53ef\u4ee5\u8bf4\u662f\u529f\u5fb7\u65e0\u91cf\u3002\u201d \u3000\u3000\u6b63\u8c08\u8bba\u95f4\uff0c\u9648\u5b8f\u3001\u8d75\u4fdd\u3001\u5c0f\u71d5\u5b50\u5168\u56de\u6765\u4e86\u3002 \u3000\u3000\u539f\u6765\u9648\u5b8f\u4e09\u4eba\u597d\u4e0d\u5bb9\u6613\u624d\u6446\u8131\u5c0f\u6843\u7b49\u4eba\u7684\u7ea0\u7f20\uff0c\u54ea\u91cc\u8fd8\u6562\u518d\u53ec\u5524\u59d1\u5a18\uff0c\u53ea\u770b\u4e86\u516d\u4e2a\uff0c\u4fbf \u88ab\u8feb\u72fc\u72c8\u800c\u56de\uff0c\u800c\u4e14\u767d\u8d54\u4e86\u4e00\u952d\u94f6\u5b50\u3002 \u3000\u3000\u5728\u738b\u5f64\u7684\u8ffd\u95ee\u4e0b\uff0c\u9648\u5b8f\u53ea\u597d\u628a\u7ecf\u8fc7\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u738b\u5f64\u5fcd\u4e0d\u4f4f\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u4e2a\u50bb\u74dc\u86cb\uff0c\u59d1\u5a18\u4f1a\u4e0d\u4f1a\u4e0b\u866b\uff0c\u5c31\u51ed\u4f60\u80fd\u770b\u5f97\u51fa\u6765\u5417\uff1f\u201d \u3000\u3000\u9648\u5b8f\u7ea2\u7740\u8138\u9053\uff1a\u201c\u5c5e\u4e0b\u51fa\u53d1\u65f6\uff0c\u4f60\u8001\u4eba\u5bb6\u5e76\u4e0d\u66fe\u9762\u6388\u673a\u5b9c\uff0c\u5c5e\u4e0b\u54ea\u91cc\u6709\u8fc7\u8fd9\u79cd\u7ecf\u9a8c\uff0c\u53ea \u597d\u60f3\u5230\u600e\u4e48\u505a\u5c31\u600e\u4e48\u505a\u4e86\u3002\u201d \u3000\u3000\u201c\u82e5\u4e8b\u60c5\u5fc5\u987b\u6709\u7ecf\u9a8c\u624d\u80fd\u529e\uff0c\u90a3\u5c31\u4ec0\u4e48\u4e8b\u90fd\u4e0d\u80fd\u529e\u4e86\uff0c\u4f60\u4eec\u56de\u53bb\u4f11\u606f\u5427\uff01\u4ee5\u540e\u518d\u60f3\u529e \u6cd5\u3002\u201d \u3000\u3000\u9648\u5b8f\u548c\u8d75\u4fdd\u56de\u623f\u540e\uff0c\u6c5f\u5343\u91cc\u62db\u547c\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u5230\u6211\u623f\u91cc\u6765\uff0c\u6211\u6709\u4e8b\u4ea4\u4ee3\u4f60\u3002\u201d \u3000\u3000\u5c31\u5728\u5f53\u665a\u4e8c\u66f4\u8fc7\u540e\uff0c\u4e24\u6761\u52b2\u670d\u6253\u626e\u7684\u4eba\u5f71\uff0c\u6f5c\u6765\u5f00\u5c01\u5e9c\u5927\u7262\u5916\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u6b63\u662f\u6c5f\u5e72\u91cc\u548c\u5c0f\u71d5\u5b50\u3002 \u3000\u3000\u6b64\u523b\uff0c\u4ed6\u4eec\u4e0d\u4f46\u6362\u4e86\u8863\u670d\uff0c\u800c\u4e14\u4e5f\u6613\u4e86\u5bb9\u3002 \u3000\u3000\u672c\u6765\uff0c\u6c5f\u5343\u91cc\u51c6\u5907\u8ba9\u5c0f\u71d5\u5b50\u4e00\u4eba\u5b8c\u6210\u4efb\u52a1\uff0c\u4f46\u53c8\u89c9\u5f97\u5c0f\u71d5\u5b50\u867d\u7136\u673a\u667a\u3001\u6b66\u529f\u9ad8\u4eba\u4e00\u7b49\uff0c \u603b\u5acc\u7ecf\u9a8c\u4e0d\u8db3\uff0c\u4e07\u4e00\u51fa\u4e86\u5dee\u9519\uff0c\u4e8b\u60c5\u5fc5\u5b9a\u65e0\u6cd5\u633d\u6551\uff0c\u56e0\u4e4b\u624d\u4e00\u8d77\u524d\u6765\u3002 \u3000\u3000\u4ed6\u5f88\u540e\u6094\u65e5\u95f4\u6ca1\u5411\u5f20\u4ec1\u95ee\u6e05\u695a\u6797\u5bb6\u58f0\u56da\u5728\u51e0\u53f7\u7262\u623f\uff0c\u597d\u5728\u5f00\u5c01\u5e9c\u53ea\u6709\u8fd9\u4e00\u5ea7\u5927\u7262\uff0c\u591a\u8d39 \u4e9b\u5de5\u592b\uff0c\u603b\u662f\u53ef\u4ee5\u627e\u5230\u7684\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u548c\u5c0f\u71d5\u5b50\u5148\u9690\u4f0f\u5728\u5927\u7262\u5bf9\u9762\u5c4b\u810a\u4e0a\uff0c\u8fdc\u8fdc\u671b\u89c1\u6b63\u6709\u4e00\u540d\u4f69\u5200\u7684\u7262\u5352\u5728\u5927\u7262\u95e8\u53e3\u8d1f \u8d23\u8b66\u536b\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u5411\u524d\u540e\u5de6\u53f3\u4ed4\u7ec6\u641c\u89c6\u4e86\u4e00\u904d\uff0c\u89c1\u9644\u8fd1\u5e76\u65e0\u5176\u4ed6\u4eba\uff0c\u7acb\u5373\u4f4e\u58f0\u9053\uff1a\u201c\u5c0f\u71d5\u5b50\uff0c\u4f60\u5148 \u8fc7\u53bb\u70b9\u4e86\u90a3\u7262\u5352\u7a74\u9053\uff0c\u8981\u4e00\u4e3e\u6210\u529f\uff0c\u5426\u5219\u90a3\u7262\u5352\u4e00\u51fa\u58f0\u558a\u53eb\uff0c\u4e8b\u60c5\u5c31\u7cdf\u4e86\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u70b9\u4e86\u70b9\u5934\uff0c\u5f85\u90a3\u7262\u5352\u8f6c\u5934\u671b\u5411\u522b\u5904\u65f6\uff0c\u539f\u5730\u4e00\u5f0f\u201c\u9526\u9ca4\u7a7f\u6ce2\u201d\uff0c\u8eab\u5f62\u75be\u5982\u79bb\u5f26\u6025 \u77e2\u822c\uff0c\u65e0\u58f0\u65e0\u606f\uff0c\u4e94\u516d\u4e08\u7684\u8ddd\u79bb\u970e\u65f6\u4fbf\u5df2\u51cc\u7a7a\u63a0\u81f3\u90a3\u7262\u5352\u80cc\u540e\u3002 \u3000\u3000\u90a3\u7262\u5352\u4e5f\u9887\u4e3a\u673a\u8b66\uff0c\u5fae\u95fb\u8eab\u540e\u6709\u8863\u8882\u98d8\u98ce\u4e4b\u58f0\uff0c\u6025\u6025\u56de\u8fc7\u5934\u6765\u3002 \u3000\u3000\u53ef\u60dc\u4ed6\u8fd8\u662f\u6162\u4e86\u4e00\u6b65\uff0c\u5c1a\u672a\u770b\u6e05\u6765\u7684\u662f\u4ec0\u4e48\u4eba\uff0c\u4fbf\u88ab\u5c0f\u71d5\u5b50\u4e00\u6307\u70b9\u660f\u8fc7\u53bb\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u53c8\u662f\u4e00\u6761\u4eba\u5f71\u7535\u5c04\u800c\u81f3\uff0c\u6c5f\u5343\u91cc\u5df2\u968f\u540e\u6765\u5230\u3002 \u3000\u3000\u201c\u5feb\u628a\u4ed6\u62d6\u5230\u6697\u5904\uff0c\u8131\u4e0b\u4ed6\u7684\u8863\u670d\u6362\u4e0a\uff0c\u8fd8\u6709\u522b\u5fd8\u4e86\u627e\u5230\u4ed6\u8eab\u4e0a\u7684\u8170\u724c\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u52a8\u4f5c\u5947\u5feb\uff0c\u4e0d\u6d88\u7247\u523b\uff0c\u4fbf\u5df2\u6362\u4e86\u7262\u5352\u8863\u670d\u6765\u5230\u6c5f\u5343\u91cc\u9762\u524d\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u518d\u4f4e\u58f0\u9053\uff1a\u201c\u73b0\u5728\u5c31\u8fdb\u53bb\u3002\u201d \u3000\u3000\u201c\u6c5f\u53d4\u53d4\u4f60\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u5728\u5916\u9762\u8d1f\u8d23\u63a5\u5e94\uff0c\u4e0d\u4f1a\u518d\u6709\u4eba\u5230\u5927\u7262\u53bb\uff0c\u4f60\u53ea\u8981\u628a\u91cc\u9762\u7684\u4e8b\u529e\u597d\u5c31\u6210\u4e86\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u5bf9\u5927\u7262\u5185\u7684\u60c5\u5f62\uff0c\u5df2\u5411\u6c5f\u5343\u91cc\u95ee\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u867d\u7136\u4ece\u672a\u6765\u8fc7\uff0c\u5374\u4e5f\u7b49\u4e8e\u8f7b\u8f66\u719f\u8def \u4e86\u3002 \u3000\u3000\u5f53\u4ed6\u521a\u8fdb\u5165\u5927\u7262\u91cc\u9762\u8f6c\u89d2\u5904\uff0c\u4fbf\u542c\u6709\u4eba\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u5f53\u7136\u65e9\u6709\u5fc3\u7406\u51c6\u5907\uff0c\u5fd9\u9053\uff1a\u201c\u662f\u6211\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u9ad8\u5c0f\u660e\u3002\u201d \u3000\u3000\u5927\u7262\u5185\u70b9\u7740\u597d\u51e0\u76cf\u706f\u3002 \u3000\u3000\u95ee\u8bdd\u4eba\u662f\u8d1f\u8d23\u5927\u7262\u5185\u90e8\u5de1\u903b\u7684\u7262\u5352\uff0c\u8bf4\u8bdd\u95f4\u5df2\u6765\u5230\u5c0f\u71d5\u5b50\u9762\u524d\uff0c\u4e0d\u89c9\u54a6\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u6211 \u600e\u4e48\u4e0d\u8ba4\u8bc6\u4f60\uff1f\u8fde\u4f60\u8fd9\u540d\u5b57\u4e5f\u6ca1\u542c\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6c14\u5b9a\u795e\u95f2\u7684\u9053\uff1a\u201c\u6211\u662f\u5728\u5185\u8859\u670d\u52e4\u7684\uff0c\u662f\u5927\u4eba\u8eab\u8fb9\u7684\u4eba\u3002\u201d \u3000\u3000\u8bf4\u7740\u3002\u628a\u8170\u724c\u5411\u90a3\u7262\u5352\u4eae\u4e86\u4eae\u3002 \u3000\u3000\u90a3\u7262\u5352\u7728\u7740\u4e24\u773c\u9053\uff1a\u201c\u8001\u5f1f\u65e2\u5728\u5185\u884c\u670d\u52e4\uff0c\u4e3a\u4ec0\u4e48\u6df1\u66f4\u534a\u591c\u95ef\u5230\u5927\u7262\u91cc\u6765\uff1f\u201d \u3000\u3000\u201c\u5c0f\u5f1f\u662f\u5949\u5927\u4eba\u4e4b\u547d\u6765\u529e\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u201c\u529e\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u8001\u5927\u54e5\u53ef\u77e5\u9053\u6709\u4f4d\u53eb\u6797\u5bb6\u58f0\u7684\u91cd\u56da\u5417\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u77e5\u9053\uff0c\u90a3\u5c0f\u5b50\u79c1\u901a\u76d7\u5bc7\u3001\u56fe\u8c0b\u4e0d\u8f68\uff0c\u5c31\u5728\u8fd9\u51e0\u5929\u4fbf\u8981\u5904\u51b3\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u5c0f\u5f1f\u5c31\u544a\u8bc9\u4f60\u3002\u884c\u5211\u7684\u65f6\u95f4\u5c31\u5728\u660e\u5929\u5348\u65f6\u4e09\u523b\u3002\u201d \u3000\u3000\u201c\u679c\u7136\u597d\u5feb\uff0c\u8001\u5f1f\uff01\u4f60\u63d0\u8d77\u90a3\u5c0f\u5b50\u505a\u4ec0\u4e48\uff1f\u884c\u5211\u7684\u4e8b\u81ea\u6709\u523d\u5b50\u624b\u6267\u884c\uff0c\u65e2\u4e0d\u662f\u4f60\uff0c\u4e5f\u4e0d \u662f\u6211\u3002\u201d \u3000\u3000\u201c\u5f53\u7136\u4e0d\u662f\u8ba9\u4f60\u8001\u5927\u54e5\u884c\u5211\uff0c\u5c0f\u5f1f\u662f\u5949\u4e86\u5927\u4eba\u4e4b\u547d\uff0c\u6765\u770b\u770b\u59d3\u6797\u7684\u90a3\u5c0f\u5b50\u3002\u201d \u3000\u3000\u201c\u660e\u5929\u5c31\u8981\u884c\u5211\u4e86\uff0c\u8fd8\u6709\u4ec0\u4e48\u53ef\u770b\u7684\uff1f\u201d \u3000\u3000\u201c\u5927\u4eba\u8981\u5c0f\u5f1f\u95ee\u4ed6\u51e0\u53e5\u8bdd\uff0c\u8fd9\u4e8b\u5f88\u91cd\u8981\uff0c\u5982\u679c\u4e0d\u73b0\u5728\u5f04\u6e05\u695a\uff0c\u5c06\u6765\u5c31\u6b7b\u65e0\u5bf9\u8bc1\u4e86\u3002\u201d \u3000\u3000\u90a3\u7262\u5352\u542c\u51fa\u8fd9\u4e8b\u597d\u50cf\u5f88\u91cd\u8981\uff0c\u54ea\u6562\u6020\u6162\uff0c\u5fd9\u5728\u524d\u5e26\u8def\u9053\uff1a\u201c\u8001\u5f1f\u968f\u6211\u6765\uff0c\u90a3\u5c0f\u5b50\u56da\u5728\u4e03 \u53f7\u7262\u623f\u3002\u201d \u3000\u3000\u6765\u5230\u4e03\u53f7\u7262\u623f\u524d\uff0c\u90a3\u7262\u5352\u638f\u51fa\u94a5\u5319\uff0c\u6253\u5f00\u94c1\u6805\u95e8\u9053\uff1a\u201c\u5c31\u5728\u91cc\u9762\uff0c\u53ef\u80fd\u5df2\u7ecf\u7761\u7740\u4e86\uff0c\u8001 \u5f1f\u8bf7\u81ea\u5df1\u8fdb\u53bb\u95ee\u2026\u2026\u201d \u3000\u3000\u90a3\u7262\u5352\u7684\u8bdd\u8fd8\u6ca1\u8bf4\u5b8c\uff0c\u4fbf\u89c9\u7a74\u773c\u4e00\u9ebb\uff0c\u5012\u4e0b\u5730\u53bb\u3002 \u3000\u3000\u6797\u5bb6\u58f0\u672c\u6765\u5df2\u7ecf\u7761\u7740\uff0c\u6b64\u523b\u88ab\u6253\u5f00\u94c1\u6805\u95e8\u7684\u58f0\u97f3\u60ca\u9192\uff0c\u4ed6\u5173\u5728\u7262\u91cc\u5df2\u597d\u51e0\u4e2a\u6708\uff0c\u5bf9\u8fd9\u79cd \u4e8b\u5df2\u7ecf\u662f\u89c1\u602a\u4e0d\u602a\u4e86\uff0c\u56e0\u4e4b\u4ecd\u7136\u88c5\u7761\u3002 \u3000\u3000\u5355\u95f4\u7262\u623f\u91cc\u5149\u7ebf\u90fd\u8f83\u6697\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u5411\u4eba\u5f71\u5904\u671b\u4e86\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u5c31\u662f\u6797\u5bb6\u58f0\u5417\uff1f\u201d \u3000\u3000\u6797\u5bb6\u58f0\u4e0d\u80fd\u518d\u88c5\u7761\u4e86\uff0c\u53ea\u5f97\u5e94\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u5c31\u662f\u2026\u2026\u201d \u3000\u3000\u4ed6\u7684\u201c\u662f\u201d\u5b57\u521a\u51fa\u53e3\uff0c\u5c0f\u71d5\u5b50\u4e5f\u4e00\u6307\u70b9\u4e86\u4ed6\u7684\u7a74\u9053\u3002 \u3000\u3000\u8fd9\u662f\u56e0\u4e3a\u4e8b\u60c5\u5fc5\u987b\u529e\u5f97\u8d8a\u5feb\u8d8a\u597d\uff0c\u53ea\u8981\u5f04\u6e05\u5bf9\u8c61\u6ca1\u9519\uff0c\u522b\u7684\u8bdd\u6ca1\u6709\u5fc5\u8981\u73b0\u5728\u8bf4\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u62b1\u8d77\u6797\u5bb6\u58f0\uff0c\u4fbf\u51fa\u4e86\u5927\u7262\u3002 \u3000\u3000\u6765\u5230\u5927\u7262\u95e8\u53e3\uff0c\u6c5f\u5343\u91cc\u7acb\u523b\u7531\u6697\u4e2d\u73b0\u8eab\u800c\u51fa\u3002 \u3000\u3000\u201c\u597d\u5c0f\u5b50\uff0c\u4e8b\u60c5\u679c\u7136\u529e\u5f97\u4e0d\u9519\u3002\u201d \u3000\u3000\u201c\u6c5f\u53d4\u53d4\uff0c\u8fd9\u4e2a\u4eba\u600e\u4e48\u5904\u7f6e\uff1f\u201d \u3000\u3000\u201c\u660e\u5929\u77e5\u5e9c\u8859\u95e8\u5fc5\u5b9a\u5f20\u8d34\u699c\u6587\u5c55\u5f00\u7f09\u6355\uff0c\u53ea\u6709\u628a\u4ed6\u5e26\u5230\u5ba2\u6808\u518d\u8bf4\u4e86\u3002\u201d \u5367\u9f99\u751f\u300a\u71d5\u5b50\u4f20\u5947\u300b \u7b2c\u4e8c\u5341\u4e8c\u56de\u3000\u667a\u5ba1\u77e5\u5e9c \u3000\u3000\u65f6\u5df2\u4e09\u66f4\uff0c\u8857\u4e0a\u65e9\u65e0\u884c\u4eba\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u80cc\u7740\u6797\u5bb6\u58f0\uff0c\u5728\u6c5f\u5343\u91cc\u7684\u63a9\u62a4\u4e0b\uff0c\u5f88\u5feb\u4fbf\u6765\u5230\u4e1c\u90fd\u8001\u6808\u7684\u540e\u9662\u56f4\u5899\u5916\u3002 \u3000\u3000\u4e3a\u4e86\u4e0d\u60ca\u52a8\u5e97\u5bb6\uff0c\u4ed6\u4eec\u5fc5\u987b\u8d8a\u5899\u800c\u5165\u3002 \u3000\u3000\u6b63\u597d\u5c0f\u71d5\u5b50\u623f\u5185\u6709\u4e2a\u7a7a\u5e8a\u4f4d\uff0c\u4fbf\u8ba9\u6797\u5bb6\u58f0\u548c\u4ed6\u4f4f\u5728\u540c\u4e00\u623f\u95f4\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u76f4\u7761\u5230\u5929\u4eae\uff0c\u624d\u89e3\u5f00\u6797\u5bb6\u58f0\u7684\u7a74\u9053\u3002 \u3000\u3000\u6797\u5bb6\u58f0\u60a0\u60a0\u9192\u6765\uff0c\u4e00\u89c1\u81ea\u5df1\u5df2\u4e0d\u5728\u5f00\u5c01\u5e9c\u7262\u623f\uff0c\u53c8\u60f3\u8d77\u6628\u665a\u6709\u4eba\u95ef\u5165\u7262\u623f\u7684\u4e8b\uff0c\u7acb\u523b\u5750 \u8d77\u8eab\u6765\uff0c\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u600e\u4e48\u5230\u8fd9\u91cc\u6765\u4e86\uff1f\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u8fd9\u65f6\u65e9\u5df2\u7a7f\u597d\u8863\u670d\u4e0b\u4e86\u5e8a\uff0c\u5f53\u5373\u628a\u6551\u4ed6\u7684\u7ecf\u8fc7\u53d9\u8ff0\u4e86\u4e00\u904d\u3002 \u3000\u3000\u5f53\u6797\u5bb6\u58f0\u5f97\u77e5\u81ea\u5df1\u5df2\u88ab\u6551\u51fa\uff0c\u6025\u6025\u4e0b\u5e8a\u8dea\u5012\u5728\u5c0f\u71d5\u5b50\u8eab\u524d\uff0c\u9053\uff1a\u201c\u539f\u6765\u5c11\u4fa0\u662f\u6211\u7684\u6551\u547d \u6069\u4eba\uff0c\u5927\u6069\u5927\u5fb7\uff0c\u771f\u4e0d\u77e5\u8ba9\u6211\u600e\u6837\u62a5\u7b54\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u522b\u8bf4\u8fd9\u4e9b\u4e86\uff0c\u5feb\u8d77\u6765\uff0c\u4f60\u4e00\u5b9a\u5f88\u997f\u4e86\uff0c\u5f85\u4f1a\u513f\u6211\u5c31\u7ed9\u4f60\u9001\u996d\u6765\u3002\u201d \u3000\u3000\u6b63\u8bf4\u8bdd\u95f4\uff0c\u6c5f\u5343\u91cc\u63a8\u95e8\u800c\u5165\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u4e00\u8fdb\u95e8\u5c31\u95ee\u9053\uff1a\u201c\u4e8b\u60c5\u7ecf\u8fc7\uff0c\u5168\u544a\u8bc9\u4ed6\u4e86\u5427\uff1f\u201d \u3000\u3000\u201c\u5bf9\u4ed6\u8bb2\u8fc7\u4e86\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u8bf4\u7740\uff0c\u53c8\u5bf9\u6797\u5bb6\u58f0\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u6211\u6c5f\u53d4\u53d4\uff0c\u6628\u665a\u6c5f\u53d4\u53d4\u4e5f\u5230\u8fc7\u5f00\u5c01\u5e9c\u5927\u7262\uff0c\u6211\u662f \u5949\u4e86\u6c5f\u53d4\u53d4\u7684\u547d\u4ee4\u624d\u53bb\u6551\u4f60\u7684\uff0c\u6c5f\u53d4\u53d4\u624d\u662f\u771f\u6b63\u7684\u6551\u547d\u6069\u4eba\u3002\u201d \u3000\u3000\u6797\u5bb6\u58f0\u521a\u521a\u7ad9\u8d77\u6765\uff0c\u7acb\u523b\u53c8\u8dea\u5012\u5728\u6c5f\u5343\u91cc\u8eab\u524d\uff0c\u8fde\u8fde\u7684\u78d5\u7740\u5934\u9053\uff1a\u201c\u591a\u8c22\u6c5f\u5927\u4fa0\u6551\u547d\u4e4b \u6069\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u6446\u6446\u624b\u9053\uff1a\u201c\u8be5\u8c22\u7684\u662f\u4f60\u8205\u8205\u5f20\u4ec1\uff0c\u5982\u679c\u6628\u5929\u4e0b\u5348\u4e0d\u662f\u4ed6\u6765\u8bf4\u51fa\u8fd9\u4ef6\u4e8b\uff0c\u4f60\u6050\u6015 \u5df2\u7ecf\u6ca1\u51e0\u5929\u597d\u6d3b\u4e86\u3002\u201d \u3000\u3000\u201c\u5c0f\u7684\u77e5\u9053\uff0c\u542c\u8bf4\u5c0f\u7684\u8fd9\u51e0\u5929\u5c31\u8981\u95ee\u65a9\u3002\u201d \u3000\u3000\u201c\u4f60\u73b0\u5728\u6709\u4ec0\u4e48\u6253\u7b97\uff1f\u201d \u3000\u3000\u201c\u5c0f\u7684\u53ea\u60f3\u5feb\u5feb\u56de\u5bb6\u770b\u770b\u7236\u6bcd\u548c\u5bb6\u91cc\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u597d\u5c0f\u5b50\uff0c\u60f3\u7684\u5012\u4e0d\u9519\uff0c\u4f60\u82e5\u73b0\u5728\u56de\u53bb\uff0c\u9a6c\u4e0a\u5c31\u4f1a\u518d\u88ab\u6293\u8fdb\u5927\u7262\uff0c\u800c\u4e14\u5f88\u53ef\u80fd\u5c31\u5730\u6b63 \u6cd5\uff0c\u5230\u90a3\u65f6\u8001\u592b\u548c\u5c0f\u71d5\u5b50\u8c01\u90fd\u65e0\u6cd5\u518d\u6551\u4f60\u4e86\u3002\u201d \u3000\u3000\u201c\u90a3\u2026\u2026\u5c0f\u7684\u8be5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u201c\u53ea\u6709\u6682\u65f6\u4f4f\u5728\u8fd9\u91cc\uff0c\u4e0d\u80fd\u79bb\u5f00\u623f\u95f4\u4e00\u6b65\u3002\u201d \u3000\u3000\u201c\u5018\u82e5\u77e5\u5e9c\u8859\u95e8\u6d3e\u4eba\u6765\u641c\u67e5\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd9\u91cc\u4f4f\u7740\u4e00\u4f4d\u5927\u5185\u4f8d\u536b\u7edf\u9886\uff0c\u522b\u8bf4\u5c39\u77e5\u5e9c\u4e0d\u6562\u641c\u67e5\uff0c\u5c31\u662f\u9a6c\u5de1\u629a\u4e5f\u4e0d\u6562\u4e71\u95ef\u4e00\u6b65\u3002\u201d \u3000\u3000\u6797\u5bb6\u58f0\u771f\u6ca1\u60f3\u5230\u5ba2\u6808\u91cc\u4f4f\u7684\u8fd9\u4e9b\u4eba\uff0c\u6765\u5934\u7adf\u662f\u5982\u6b64\u4e4b\u5927\uff0c\u81ea\u5df1\u4e5f\u5c31\u5b8c\u5168\u653e\u4e0b\u5fc3\u6765\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u62c5\u5fc3\u6797\u5bb6\u58f0\u809a\u5b50\u997f\uff0c\u8fde\u5fd9\u51fa\u53bb\u5429\u5490\u5ba2\u6808\u4f19\u8ba1\u9001\u4efd\u4e00\u4eba\u7684\u65e9\u9910\u6765\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u53c8\u5230\u8857\u4e0a\u4e70\u4e86\u4e24\u5957\u8863\u670d\u4ea4\u7ed9\u6797\u5bb6\u58f0\u6362\u6d17\u7a7f\u7528\u3002 \u3000\u3000\u8fd9\u65f6\u5019\uff0c\u81ea\u738b\u5f64\u4ee5\u4e0b\uff0c\u6240\u6709\u7684\u4eba\u90fd\u5df2\u77e5\u9053\u6797\u5bb6\u58f0\u5df2\u88ab\u6551\u6765\u5ba2\u6808\u3002 \u3000\u3000\u6797\u5bb6\u58f0\u8bfb\u8fc7\u4e0d\u5c11\u4e66\uff0c\u4eba\u4e5f\u957f\u5f97\u5341\u5206\u6e05\u79c0\uff0c\u800c\u4e14\u5f6c\u5f6c\u6709\u793c\u3002\u56e0\u4e4b\uff0c\u6240\u6709\u7684\u4eba\u90fd\u5bf9\u4ed6\u5f88\u597d\uff0c \u738b\u5f64\u8fd8\u7279\u522b\u4ea4\u4ee3\u4ed6\u4e00\u4e9b\u6709\u5173\u6587\u6848\u65b9\u9762\u7684\u4e8b\u3002 \u3000\u3000\u5348\u540e\uff0c\u6c5f\u5e72\u91cc\u628a\u5c0f\u71d5\u5b50\u53eb\u5230\u623f\u95f4\u3002 \u3000\u3000\u201c\u5c0f\u71d5\u5b50\uff0c\u4f60\u5728\u8857\u4e0a\u6709\u6ca1\u6709\u53d1\u73b0\u753b\u7740\u56fe\u5f62\u7f09\u6355\u6797\u5bb6\u58f0\u5f52\u6848\u7684\u699c\u6587\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6447\u5934\u9053\uff1a\u201c\u6211\u5728\u8857\u4e0a\u8d70\u4e86\u4e0d\u5c11\u5730\u65b9\uff0c\u5e76\u6ca1\u53d1\u73b0\u8d34\u6709\u8fd9\u79cd\u699c\u6587\u3002\u201d \u3000\u3000\u201c\u6709\u6ca1\u6709\u542c\u5230\u6709\u4eba\u8bae\u8bba\u6628\u665a\u52ab\u7262\u7684\u4e8b\uff1f\u201d \u3000\u3000\u201c\u4e5f\u6ca1\u542c\u5230\u3002\u201d \u3000\u3000\u6c5f\u5e72\u91cc\u6c89\u541f\u4e86\u4e00\u9635\u9053\uff1a\u201c\u8fd9\u4e5f\u96be\u602a\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u832b\u7136\u9053\uff1a\u201c\u96be\u602a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u88ab\u52ab\u7684\u6b7b\u56da\u662f\u5c39\u77e5\u5e9c\u4f8d\u59be\u7684\u672a\u5a5a\u592b\uff0c\u5c39\u77e5\u5e9c\u4e3a\u4e86\u540d\u58f0\u548c\u524d\u7a0b\uff0c\u660e\u91cc\u5f53\u7136\u4e0d\u6562\u5f20\u626c\uff0c\u4f46 \u6697\u5730\u91cc\u5374\u5fc5\u5b9a\u8d8a\u53d1\u52a0\u7d27\u8ffd\u67e5\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\uff0c\u6797\u5bb6\u58f0\u4e0d\u4f46\u6c38\u8fdc\u4e0d\u6562\u56de\u5bb6\uff0c\u4e5f\u6c38\u8fdc\u4e0d\u6562\u5728\u5916\u516c\u5f00\u9732\u9762\u4e86\u3002\u201d \u3000\u3000\u201c\u7684\u786e\u5982\u6b64\uff0c\u53ea\u8981\u5c39\u77e5\u5e9c\u5728\u4efb\u4e00\u5929\uff0c\u90a3\u5c0f\u5b50\u5c31\u5fc5\u987b\u8eb2\u85cf\u8d77\u6765\u3002\u201d \u3000\u3000\u201c\u90a3\u600e\u4e48\u529e\uff1f\u54b1\u4eec\u4e0d\u53ef\u80fd\u5728\u5f00\u5c01\u592a\u4e45\uff0c\u81ea\u7136\u4e5f\u4e0d\u53ef\u80fd\u6c38\u8fdc\u4fdd\u62a4\u4ed6\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u663e\u51fa\u4e3a\u96be\u4e4b\u8272\u3002 \u3000\u3000\u4ed6\u6c89\u601d\u4e86\u534a\u664c\uff0c\u5ffd\u7136\u53cc\u76ee\u795e\u5149\u4e00\u95ea\u9053\uff1a\u201c\u5c0f\u71d5\u5b50\uff0c\u4f60\u518d\u53bb\u505a\u4e00\u4ef6\u4e8b\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u54e6\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u60a8\u8001\u4eba\u5bb6\u53c8\u8981\u6211\u505a\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u4f60\u548c\u4ed6\u90fd\u662f\u5e74\u8f7b\u4eba\uff0c\u6bd4\u8f83\u8c08\u5f97\u6765\uff0c\u4e0d\u59a8\u53bb\u95ee\u95ee\u4ed6\u3002\u201d \u3000\u3000\u201c\u95ee\u4ed6\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u95ee\u4ed6\u5bf9\u4ed6\u7684\u672a\u5a5a\u59bb\u5b59\u5c0f\u51e4\u662f\u5426\u5df2\u7ecf\u6b7b\u4e86\u5fc3\u3002\u201d \u3000\u3000\u201c\u8fd9\u65b9\u9762\u7528\u4e0d\u7740\u95ee\u4e86\uff0c\u4ed6\u82e5\u662f\u6b7b\u4e86\u5fc3\uff0c\u5c31\u4e0d\u81f3\u4e8e\u5192\u7740\u751f\u547d\u5371\u9669\uff0c\u64c5\u95ef\u77e5\u5e9c\u540e\u8859\u53bb\u770b\u5b59\u5c0f \u51e4\u4e86\u3002\u201d \u3000\u3000\u201c\u8bf4\u7684\u4e5f\u662f\uff0c\u4e0d\u8fc7\u5173\u952e\u4e0d\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u201c\u6c5f\u53d4\u53d4\u6307\u7684\u5173\u952e\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5b59\u5c0f\u51e4\u662f\u5426\u5bf9\u4ed6\u8fd8\u6709\u60c5\uff1f\u201d \u3000\u3000\u201c\u5b59\u5c0f\u51e4\u88ab\u5c39\u77e5\u5e9c\u5df2\u7ecf\u9738\u5360\u4e24\u5e74\u591a\u4e86\uff0c\u73b0\u5728\u662f\u5426\u8fd8\u5bf9\u6797\u5bb6\u58f0\u65e7\u60c5\u4e0d\u5fd8\uff0c\u53ea\u6015\u6797\u5bb6\u58f0\u4e5f\u65e0 \u6cd5\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u4ed6\u4e00\u5b9a\u5fc3\u91cc\u6709\u6570\uff0c\u4f60\u53ea\u7ba1\u53bb\u8bd5\u63a2\u8bd5\u63a2\u4ed6\uff0c\u56de\u5934\u518d\u628a\u60c5\u5f62\u544a\u8bc9\u6211\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u56de\u5230\u81ea\u5df1\u623f\u95f4\u3002 \u3000\u3000\u53ea\u89c1\u6797\u5bb6\u58f0\u6b63\u5728\u5ba4\u5185\u95f2\u5f97\u65e0\u804a\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u5728\u5e8a\u524d\u6905\u4e0a\u5750\u4e0b\uff0c\u7b11\u9053\uff1a\u201c\u6797\u5144\uff0c\u770b\u6837\u5b50\u4f60\u4e00\u5b9a\u5f88\u5bc2\u5bde\u5427\uff1f\u201d \u3000\u3000\u201c\u5509\uff01\u65e2\u4e0d\u80fd\u56de\u5bb6\uff0c\u53c8\u4e0d\u80fd\u5230\u5916\u9762\u8d70\u52a8\u8d70\u52a8\uff0c\u600e\u4e48\u4e0d\u5bc2\u5bde\u5462\uff1f\u201d \u3000\u3000\u201c\u53ea\u6015\u8fd8\u4e0d\u6b62\u8fd9\u4e9b\u5427\uff1f\u201d \u3000\u3000\u6797\u5bb6\u58f0\u542c\u51fa\u5bf9\u65b9\u8bdd\u4e2d\u6709\u8bdd\uff0c\u4e0d\u89c9\u4e00\u6014\u9053\uff1a\u201c\u4e0d\u77e5\u71d5\u5c11\u4fa0\u6307\u7684\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6545\u610f\u60f3\u4e86\u60f3\u9053\uff1a\u201c\u6797\u5144\u8fd9\u6b21\u88ab\u62bc\u4eba\u5f00\u5c01\u5e9c\u5927\u7262\uff0c\u8d77\u56e0\u53c8\u662f\u4e3a\u7684\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u6797\u5bb6\u58f0\u7acb\u523b\u6028\u6124\u4ea4\u52a0\uff0c\u4f4e\u4e0b\u5934\u9053\uff1a\u201c\u71d5\u5c11\u5feb\u4e0d\u662f\u65e9\u5df2\u7ecf\u77e5\u9053\u4e86\u5417\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u70b9\u70b9\u5934\u9053\uff1a\u201c\u6211\u662f\u8bf4\u4f60\u8fd9\u4e48\u505a\u662f\u5426\u503c\u5f97\uff1f\u201d \u3000\u3000\u6797\u5bb6\u58f0\u7728\u52a8\u7740\u4e24\u773c\u9053\uff1a\u201c\u7537\u5973\u4e4b\u95f4\uff0c\u4e0d\u8fc7\u662f\u4e3a\u4e86\u4e00\u4e2a\u2018\u60c5\u2019\u5b57\uff0c\u4eba\u975e\u8349\u6728\uff0c\u5b70\u80fd\u65e0\u60c5\uff1f \u6211\u548c\u5979\u9752\u6885\u7af9\u9a6c\u4e00\u8d77\u957f\u5927\uff0c\u5982\u4eca\u4ed6\u88ab\u522b\u4eba\u9738\u5360\u4e86\u53bb\uff0c\u6211\u5f53\u7136\u4e0d\u80fd\u5fd8\u60c5\u4e8e\u5979\u3002\u201d \u3000\u3000\u201c\u6211\u662f\u8bf4\uff0c\u7537\u5973\u53cc\u65b9\u5fc5\u987b\u90fd\u6709\u60c5\u624d\u6210\u3002\u201d \u3000\u3000\u201c\u71d5\u5c11\u4fa0\uff0c\u6211\u53ef\u4ee5\u5bf9\u5929\u53d1\u8a93\uff0c\u7eb5\u7136\u6d77\u67af\u77f3\u70c2\uff0c\u5979\u5bf9\u6211\u4e5f\u7edd\u4e0d\u4f1a\u53d8\u5fc3\uff1b\u5982\u679c\u5979\u5df2\u53d8\u4e86\u5fc3\uff0c \u6211\u53c8\u4f55\u82e6\u5192\u7740\u90a3\u4e48\u5927\u7684\u5371\u9669\u53bb\u770b\u5979\u5462\uff1f\u201d \u3000\u3000\u201c\u53ef\u662f\u4f60\u548c\u5979\u5df2\u7ecf\u5206\u624b\u4e24\u5e74\u591a\uff0c\u53c8\u600e\u77e5\u5979\u4e0d\u4f1a\u53d8\u5fc3\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u5f53\u521d\u66fe\u53d1\u8fc7\u8a93\uff0c\u6211\u7edd\u5bf9\u76f8\u4fe1\u5979\u4e00\u5b9a\u8fd8\u60f3\u5ff5\u7740\u6211\u3002\u201d \u3000\u3000\u201c\u8fd9\u53ea\u662f\u4f60\u60f3\u5f53\u7136\u7684\u4e8b\uff0c\u4e8b\u9694\u4e24\u5e74\u591a\uff0c\u4eba\u5fc3\u4e5f\u7edd\u4e0d\u662f\u6c38\u4e45\u4e0d\u53d8\u7684\uff0c\u8c01\u80fd\u4fdd\u8bc1\u5979\u5bf9\u4f60\u4f1a\u6c38 \u8fdc\u4e0d\u53d8\u5fc3\uff1f\u201d \u3000\u3000\u201c\u71d5\u5c11\u4fa0\u65e2\u7136\u8fd9\u4e48\u8bf4\uff0c\u6211\u8fd8\u6709\u4ec0\u4e48\u597d\u8bb2\u7684\u5462\uff01\u201d \u3000\u3000\u2018\u54e6\u8bb2\u8fd9\u8bdd\uff0c\u4e5f\u662f\u4ee5\u5e38\u7406\u63a8\u65ad\uff0c\u5e76\u975e\u968f\u4fbf\u4e71\u8bb2\u7684\u3002\u201d \u3000\u3000\u201c\u71d5\u5c11\u4fa0\u6839\u636e\u4ec0\u4e48\u5e38\u7406\uff1f\u201d \u3000\u3000\u201c\u5b59\u5c0f\u51e4\u76ee\u524d\u867d\u7136\u53ea\u662f\u4e00\u540d\u4f8d\u59be\uff0c\u4f46\u5374\u6709\u4eab\u4e0d\u5c3d\u7684\u8363\u534e\u5bcc\u8d35\uff0c\u5e9c\u4e0a\u7684\u5bb6\u5ead\u73af\u5883\u80fd\u548c\u4e00\u540d \u77e5\u5e9c\u5927\u4eba\u76f8\u6bd4\u5417\uff1f\u201d \u3000\u3000\u6797\u5bb6\u58f0\u4e0d\u89c9\u8138\u8272\u5927\u53d8\uff0c\u4ed6\u7b80\u76f4\u4e0d\u6562\u76f8\u4fe1\u5c0f\u71d5\u5b50\u4f1a\u8bf4\u51fa\u8fd9\u6837\u7684\u8bdd\u6765\uff0c\u4f46\u5bf9\u65b9\u662f\u81ea\u5df1\u7684\u6551\u547d \u6069\u4eba\uff0c\u4ed6\u53c8\u4e0d\u4fbf\u51fa\u8a00\u9876\u649e\uff0c\u53ea\u597d\u80c0\u7ea2\u7740\u8138\u4f4e\u4e0b\u5934\u4e0d\u518d\u5f00\u53e3\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u4e5f\u7528\u4e0d\u7740\u518d\u8bf4\u4ec0\u4e48\uff0c\u968f\u5373\u6765\u5230\u6c5f\u5343\u91cc\u623f\u95f4\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u6b63\u5728\u7b49\u56de\u97f3\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u600e\u4e48\u6837\uff1f\u5feb\u544a\u8bc9\u6211\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u628a\u548c\u6797\u5bb6\u58f0\u4ea4\u8c08\u7ecf\u8fc7\u8bf4\u4e86\u4e00\u904d\u3002 \u3000\u3000\u63a5\u7740\uff0c\u53c8\u63d0\u51fa\u81ea\u5df1\u7684\u770b\u6cd5\u9053\uff1a\u201c\u5b59\u5c0f\u51e4\u4e00\u5b9a\u4e0d\u4f1a\u53d8\u5fc3\uff0c\u83ab\u975e\u6c5f\u53d4\u53d4\u4e5f\u60f3\u628a\u5979\u4e00\u8d77\u6551\u51fa\u6765 \u5417\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u4f3c\u4e4e\u5df2\u6709\u51b3\u5b9a\uff0c\u62cd\u62cd\u5c0f\u71d5\u5b50\u80a9\u8180\u9053\uff1a\u201c\u8fd9\u6b21\u8be5\u4f60\u5355\u72ec\u51fa\u9a6c\u4e86\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u6211\u5f04\u4e0d\u6e05\u5b59\u5c0f\u51e4\u4f4f\u5728\u540e\u8859\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u6211\u5df2\u95ee\u8fc7\u4f60\u7239\uff0c\u5f53\u7136\u4f1a\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u201c\u662f\u5426\u4e5f\u8981\u628a\u5979\u6551\u5230\u8fd9\u91cc\u6765\uff1f\u201d \u3000\u3000\u201c\u4e0d\u6210\u3002\u4f60\u53ea\u8981\u628a\u6211\u7684\u8bdd\u4ea4\u4ee3\u5979\u5c31\u6210\u4e86\uff0c\u4e0d\u8fc7\u5fc5\u987b\u5148\u8bd5\u63a2\u5979\u662f\u5426\u53d8\u5fc3\uff0c\u5982\u679c\u5df2\u7ecf\u53d8\u5fc3\uff0c \u6211\u7684\u529e\u6cd5\u5c31\u4e0d\u7075\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u6c5f\u53d4\u53d4\u5c31\u544a\u8bc9\u6211\uff0c\u60a8\u7684\u529e\u6cd5\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u968f\u5373\u4f4e\u58f0\u5411\u5c0f\u71d5\u5b50\u4ea4\u4ee3\u4e86\u4e00\u9635\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u8fb9\u542c\u8fb9\u70b9\u5934\u9053\uff1a\u201c\u679c\u7136\u597d\u529e\u6cd5\uff0c\u5982\u679c\u53ea\u628a\u5979\u6551\u51fa\u6765\uff0c\u9ebb\u70e6\u4e8b\u8fd8\u5728\u540e\u9762\uff0c\u8fd9\u4e48\u4e00 \u6765\uff0c\u624d\u7b97\u628a\u95ee\u9898\u771f\u6b63\u5f7b\u5e95\u89e3\u51b3\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u90d1\u91cd\u5176\u4e8b\u7684\u9053\uff1a\u201c\u8fdb\u53bb\u540e\u5fc5\u987b\u4fdd\u6301\u673a\u8b66\uff0c\u82e5\u5c39\u77e5\u5e9c\u6b63\u5728\u5979\u623f\u4e2d\uff0c\u90a3\u5c31\u8d76\u7d27\u56de\u6765\uff0c \u660e\u5929\u665a\u4e0a\u518d\u53bb\uff0c\u5343\u4e07\u4e0d\u80fd\u60ca\u52a8\u4efb\u4f55\u4eba\u3002\u201d \u3000\u3000\u5927\u7ea6\u4e09\u66f4\uff0c\u5c0f\u71d5\u5b50\u5df2\u6f5c\u4f0f\u5728\u77e5\u5e9c\u540e\u8859\u7684\u5c4b\u9876\u4e0a\u3002 \u3000\u3000\u51ed\u4ed6\u7684\u8f7b\u529f\uff0c\u679c\u7136\u5e76\u672a\u88ab\u4efb\u4f55\u4eba\u53d1\u73b0\u3002 \u3000\u3000\u672c\u6765\uff0c\u540e\u8857\u7b49\u4e8e\u5185\u9662\uff0c\u5fc5\u987b\u901a\u8fc7\u91cd\u91cd\u8b66\u6212\u548c\u5de1\u903b\uff0c\u624d\u80fd\u6765\u5230\u8fd9\u91cc\uff0c\u800c\u8fd9\u91cc\u53cd\u800c\u662f\u8b66\u6212\u6700 \u758f\u5ffd\u7684\u5730\u65b9\u3002 \u3000\u3000\u4e09\u66f4\uff0c\u6b63\u662f\u591c\u6df1\u4eba\u9759\u65f6\u523b\u3002 \u3000\u3000\u7531\u5c4b\u9876\u51e0\u4e4e\u53ef\u4ee5\u4fef\u89c6\u6574\u5ea7\u5185\u8859\u3002 \u3000\u3000\u9664\u4e86\u4e00\u4e24\u5904\u623f\u95f4\u5c1a\u6709\u706f\u706b\u5916\uff0c\u5176\u4f59\u5168\u662f\u6f06\u9ed1\u4e00\u7247\u3002 \u3000\u3000\u5929\u4e95\u5185\u4e5f\u4e0d\u89c1\u4eba\u5f71\u3002 \u3000\u3000\u4e0d\u96be\u60f3\u89c1\uff0c\u540e\u8859\u4f3c\u4e4e\u5e76\u65e0\u4eba\u5de1\u903b\u3002 \u3000\u3000\u8fd9\u5bf9\u5c0f\u71d5\u5b50\u7684\u884c\u52a8\uff0c\u5b9e\u5728\u662f\u4e00\u5927\u65b9\u4fbf\u3002 \u3000\u3000\u4ed6\u5df2\u77e5\u5b59\u5c0f\u51e4\u7684\u4f4f\u5904\uff0c\u90a3\u662f\u540e\u8859\u897f\u4fa7\u7684\u4e00\u5904\u8de8\u9662\u3002 \u3000\u3000\u8fd9\u5904\u8de8\u9662\u662f\u72ec\u95e8\u72ec\u6237\uff0c\u5168\u9662\u5e76\u65e0\u706f\u5149\u5c04\u51fa\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u5728\u5c4b\u9876\u89c2\u5bdf\u4e86\u5f88\u4e45\uff0c\u624d\u8dc3\u4e0b\u8de8\u9662\u5929\u4e95\uff0c\u811a\u4e0b\u5e76\u672a\u53d1\u51fa\u534a\u70b9\u58f0\u606f\u3002 \u3000\u3000\u5b59\u5c0f\u51e4\u65e2\u7136\u72ec\u62e5\u4e00\u5904\u8de8\u9662\uff0c\u4e0d\u6d88\u8bf4\u662f\u4f4f\u5728\u6b63\u623f\u3002\u5728\u6b63\u5e38\u72b6\u51b5\u4e0b\uff0c\u81f3\u5c11\u5e94\u8be5\u6709\u4e24\u540d\u4e2b\u73af\u4e5f \u4f4f\u5728\u8fd9\u91cc\u3002 \u3000\u3000\u8de8\u9662\u5185\u65e2\u65e0\u706f\u5149\u53c8\u5bc2\u9759\u65e0\u58f0\uff0c\u6beb\u65e0\u7591\u95ee\u4eba\u90fd\u5df2\u7ecf\u7761\u4e86\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u6b64\u523b\u6700\u62c5\u5fc3\u7684\u662f\u5c39\u77e5\u5e9c\u5bbf\u5728\u8fd9\u91cc\u3002 \u3000\u3000\u90a3\u5c31\u7b49\u4e8e\u4eca\u665a\u767d\u8dd1\u4e00\u8d9f\u4e86\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u8fd9\u79cd\u53ef\u80fd\u5e76\u4e0d\u5f88\u5927\u3002 \u3000\u3000\u56e0\u4e3a\u636e\u4ed6\u6240\u77e5\uff0c\u5c39\u77e5\u5e9c\u6709\u4e24\u4f4d\u592b\u4eba\uff0c\u4f8d\u59be\u4e5f\u4e0d\u6b62\u4e00\u4e2a\uff0c\u5b59\u5c0f\u51e4\u7eb5\u7136\u5f97\u5ba0\uff0c\u5c39\u77e5\u5e9c\u4e5f\u4e0d\u4e00 \u5b9a\u591c\u591c\u524d\u6765\u3002 \u3000\u3000\u5c31\u5728\u5c0f\u71d5\u5b50\u521a\u521a\u8dc3\u4e0b\u5929\u4e95\uff0c\u5ffd\u7136\u53a2\u623f\u5185\u4eae\u8d77\u4e86\u706f\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u6599\u60f3\u4eae\u706f\u4e0e\u81ea\u5df1\u65e0\u5173\uff0c\u7acb\u5373\u98d8\u8eab\u6765\u5230\u7a97\u5916\uff0c\u7531\u7a97\u9699\u6084\u6084\u5411\u91cc\u671b\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u5c4b\u5185\u4e00\u5f20\u5927\u5e8a\uff0c\u4e00\u540d\u5c11\u5973\u62e5\u88ab\u800c\u5367\uff0c\u53e6\u4e00\u540d\u5c11\u5973\u5219\u62ab\u8863\u7ad9\u5728\u5e8a\u524d\u3002 \u3000\u3000\u60f3\u6765\u8fd9\u540d\u62ab\u8863\u4e0b\u5e8a\u7684\u5c11\u5973\u6b63\u662f\u8d77\u6765\u70b9\u706f\u7684\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u4e0d\u96be\u6599\u5230\uff0c\u8fd9\u4e24\u540d\u5c11\u5973\u5fc5\u662f\u8d1f\u8d23\u670d\u4f8d\u5b59\u5c0f\u51e4\u7684\u4e2b\u73af\u3002 \u3000\u3000\u53ea\u542c\u5e8a\u4e0a\u7684\u5c11\u5973\u9053\uff1a\u201c\u5c0f\u7ea2\uff0c\u7761\u5f97\u597d\u597d\u7684\uff0c\u53c8\u8d77\u6765\u505a\u4ec0\u4e48\uff1f\u800c\u4e14\u53c8\u70b9\u4e0a\u706f\u3002\u201d \u3000\u3000\u90a3\u53eb\u5c0f\u7ea2\u7684\u5c11\u5973\u9053\uff1a\u201c\u4eba\u5bb6\u54ea\u91cc\u6709\u4f60\u8212\u670d\uff0c\u8fd8\u8981\u505a\u4e8b\u5462\uff1f\u201d \u3000\u3000\u5e8a\u4e0a\u7684\u5c11\u5973\u9053\uff1a\u201c\u4ec0\u4e48\u8981\u7d27\u7684\u4e8b\u8981\u534a\u591c\u8d77\u6765\u505a\uff1f\u201d \u3000\u3000\u201c\u96be\u9053\u54b1\u4eec\u51e4\u59e8\u8fd9\u51e0\u5929\u4e0d\u8212\u670d\uff0c\u4f60\u4e5f\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u6211\u5f53\u7136\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u77e5\u9053\u5c31\u597d\uff0c\u51e4\u59e8\u767d\u5929\u4ea4\u4ee3\u6211\uff0c\u4eca\u665a\u4e09\u66f4\u53eb\u5979\u5403\u836f\u3002\u6211\u534a\u591c\u8d77\u6765\u5c31\u662f\u8981\u8fc7\u53bb\u670d\u4f8d\u5979\u5403 \u836f\u3002\u201d \u3000\u3000\u201c\u96be\u602a\u51e4\u59e8\u5bf9\u4f60\u597d\uff0c\u4ec0\u4e48\u4e8b\u90fd\u4ea4\u4ee3\u4f60\u505a\u3002\u201d \u3000\u3000\u201c\u4f60\u82e5\u60f3\u4e89\u529f\uff0c\u5c31\u7531\u4f60\u8fc7\u53bb\u5427\uff01\u522b\u5f97\u4e86\u4fbf\u5b9c\u8fd8\u5356\u4e56\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u51e4\u59e8\u4ea4\u4ee3\u7684\u662f\u4f60\uff0c\u6211\u591f\u4ec0\u4e48\u8d44\u683c\u53bb\uff1f\u201d \u3000\u3000\u201c\u90a3\u5c31\u522b\u8bf4\u51c9\u5feb\u8bdd\uff0c\u7761\u4f60\u7684\u5927\u5934\u515a\u5427\uff01\u6211\u7a7f\u597d\u8863\u670d\u5c31\u8fc7\u53bb\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u542c\u5230\u8fd9\u91cc\uff0c\u4e0d\u89c9\u6697\u559c\uff01\u201d \u3000\u3000\u56e0\u4e3a\uff0c\u7531\u5c0f\u7ea2\u7684\u8bdd\u4e2d\u5df2\u77e5\u5c39\u77e5\u5e9c\u4e0d\u5728\u8fd9\u91cc\u3002 \u3000\u3000\u4ed6\u4e0d\u80fd\u8ba9\u5c0f\u7ea2\u51fa\u623f\u540e\u53d1\u73b0\u81ea\u5df1\uff0c\u8fde\u5fd9\u53c8\u8dc3\u8eab\u4e0a\u4e86\u5c4b\u9876\u3002 \u3000\u3000\u4e0d\u5927\u4e00\u4f1a\uff0c\u5c0f\u7ea2\u51fa\u6765\u4e86\uff0c\u76f4\u5954\u6b63\u5c4b\u3002 \u3000\u3000\u7531\u4e8e\u5c0f\u7ea2\u5e76\u672a\u53eb\u95e8\uff0c\u53ef\u77e5\u6b63\u5c4b\u7684\u623f\u95e8\u672a\u5173\u3002 \u3000\u3000\u6b63\u5c4b\u5f88\u5feb\u5c31\u4eae\u8d77\u706f\uff0c\u9690\u9690\u53ef\u95fb\u5c0f\u7ea2\u548c\u53e6\u4e00\u4e2a\u5973\u4eba\u8bf4\u8bdd\u7684\u58f0\u97f3\u3002 \u3000\u3000\u53e6\u4e00\u4e2a\u5973\u4eba\u81ea\u7136\u5c31\u662f\u5b59\u5c0f\u51e4\u4e86\u3002 \u3000\u3000\u76cf\u8336\u5de5\u592b\uff0c\u6b63\u5c4b\u7684\u706f\u7184\u4e86\uff0c\u5c0f\u7ea2\u4e5f\u56de\u5230\u4e86\u5979\u4f4f\u7684\u53a2\u623f\u3002 \u3000\u3000\u63a5\u7740\uff0c\u53a2\u623f\u4e5f\u7184\u4e86\u706f\u3002 \u3000\u3000\u672c\u6765\uff0c\u5c0f\u71d5\u5b50\u60f3\u7acb\u523b\u8fdb\u5165\u5b59\u5c0f\u51e4\u623f\u95f4\uff0c\u4f46\u9884\u6599\u4e2d\u5b59\u5c0f\u51e4\u5728\u5f04\u4e0d\u6e05\u81ea\u5df1\u662f\u8c01\u524d\uff0c\u5fc5\u5b9a\u4f1a\u51fa \u58f0\u558a\u53eb\uff0c\u90a3\u65f6\u52bf\u5fc5\u60ca\u52a8\u53a2\u623f\u4e2d\u7684\u4e2b\u73af\u3002 \u3000\u3000\u65e0\u5948\u4e4b\u4e0b\uff0c\u4ed6\u4e0d\u80fd\u4e0d\u4f7f\u7528\u968f\u8eab\u5e26\u6765\u7684\u201c\u4e94\u9f13\u8fd4\u9b42\u9999\u3002\u201d \u3000\u3000\u8fd9\u662f\u6c5f\u5343\u91cc\u4ea4\u7ed9\u4ed6\u7684\uff0c\u8981\u4ed6\u5fc5\u8981\u65f6\u4f7f\u7528\u3002 \u3000\u3000\u597d\u5728\u8fd9\u79cd\u8ff7\u9b42\u9999\u5e76\u4e0d\u4f24\u4eba\uff0c\u95fb\u5230\u540e\u53ea\u662f\u6682\u65f6\u660f\u8ff7\uff0c\u4e24\u4e2a\u65f6\u8fb0\u540e\u4fbf\u4f1a\u81ea\u52a8\u9192\u6765\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u4ed6\u518d\u8dc3\u843d\u5929\u4e95\uff0c\u6765\u5230\u53a2\u623f\u7a97\u5916\uff0c\u71c3\u4e0a\u8ff7\u9999\uff0c\u7531\u7a97\u9699\u4e2d\u5c06\u70df\u96fe\u9001\u8fdb\u5ba4\u4e2d\uff0c\u5c06\u4e24\u540d\u4e2b \u73af\u718f\u660f\u540e\uff0c\u518d\u63a8\u95e8\u8fdb\u5165\u5b59\u5c0f\u51e4\u623f\u95f4\u3002 \u3000\u3000\u8fd9\u6b21\u5979\u5e76\u672a\u8e51\u624b\u8e51\u811a\uff0c\u6709\u610f\u8ba9\u5b59\u5c0f\u51e4\u542c\u5230\u58f0\u97f3\u3002 \u3000\u3000\u5b59\u5c0f\u51e4\u542c\u5230\u6709\u4eba\u8fdb\u6765\uff0c\u4e0d\u7531\u95ee\u9053\uff1a\u201c\u662f\u5c0f\u7ea2\u5417\uff1f\u600e\u4e48\u53c8\u56de\u6765\u4e86\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u5e94\u9053\uff1a\u201c\u5728\u4e0b\u4e0d\u662f\u5c0f\u7ea2\uff0c\u5b59\u59d1\u5a18\uff01\u7528\u4e0d\u7740\u6015\u3002\u201d \u3000\u3000\u5b59\u5c0f\u51e4\u5927\u5403\u4e00\u60ca\uff0c\u6025\u6025\u7531\u5e8a\u4e0a\u5750\u8d77\uff0c\u5c16\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u5e76\u4e0d\u60ca\u614c\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u5b59\u5c0f\u51e4\u867d\u7136\u60ca\u53eb\u51fa\u58f0\uff0c\u4f46\u662f\u4e0d\u53ef\u80fd\u6709\u4eba\u542c\u5230\u3002 \u3000\u3000\u4ed6\u6765\u5230\u5e8a\u524d\uff0c\u8bed\u6c14\u5e73\u548c\u7684\u9053\uff1a\u201c\u5b59\u59d1\u5a18\uff0c\u5728\u4e0b\u662f\u6797\u5bb6\u58f0\u7684\u670b\u53cb\uff0c\u6709\u8981\u7d27\u7684\u4e8b\u6765\u89c1\u4f60\uff0c\u7edd \u65e0\u534a\u70b9\u6076\u610f\uff0c\u4f60\u53ea\u7ba1\u70b9\u4e0a\u706f\u3002\u201d \u3000\u3000\u542c\u4e86\u8fd9\u51e0\u53e5\u8bdd\uff0c\u5b59\u5c0f\u51e4\u603b\u7b97\u653e\u4e0b\u5fc3\u6765\uff0c\u8fde\u5fd9\u62ab\u8863\u8d77\u8eab\u5c06\u706f\u71c3\u8d77\u3002 \u3000\u3000\u5f53\u5979\u4e4d\u89c1\u5e8a\u524d\u7ad9\u7684\u662f\u4e2a\u4eea\u8868\u4e0d\u51e1\u7684\u5c11\u5e74\uff0c\u6014\u4e86\u534a\u664c\u624d\u95ee\u9053\uff1a\u201c\u4f60\u8bf4\u662f\u5bb6\u58f0\u7684\u670b\u53cb\uff0c\u5bb6\u58f0 \u4ec0\u4e48\u65f6\u5019\u6709\u4f60\u8fd9\u4f4d\u670b\u53cb\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u521a\u8ba4\u8bc6\u624d\u4e0d\u8fc7\u4e00\u5929\u591a\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c31\u4e0d\u5bf9\u4e86\uff01\u201d \u3000\u3000\u201c\u54ea\u91cc\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u201c\u4ed6\u2026\u201d \u3000\u3000\u201c\u4ed6\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u201c\u4ed6\u5df2\u88ab\u5173\u5728\u5927\u7262\u597d\u51e0\u4e2a\u6708\u2026\u2026\u201d \u3000\u3000\u201c\u539f\u6765\u8fd9\u4e8b\u4f60\u5df2\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u6709\u4eba\u5077\u5077\u544a\u8bc9\u6211\u7684\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u6628\u665a\u6709\u4eba\u628a\u4ed6\u52ab\u8d70\uff0c\u4f60\u662f\u5426\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u4e5f\u6709\u4eba\u5077\u5077\u544a\u8bc9\u8fc7\u6211\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u5982\u6b64\uff0c\u5728\u4e0b\u548c\u4ed6\u521a\u8ba4\u8bc6\u5c31\u4e0d\u8db3\u4e3a\u5947\u4e86\uff0c\u6551\u8d70\u4ed6\u7684\u5c31\u662f\u5728\u4e0b\u3002\u201d \u3000\u3000\u5b59\u5c0f\u51e4\u4e00\u542c\u9762\u524d\u8fd9\u5e74\u8f7b\u4eba\u7adf\u7136\u662f\u6797\u5bb6\u58f0\u7684\u6551\u547d\u6069\u4eba\uff0c\u8fde\u5fd9\u8d70\u4e0b\u5e8a\u6765\u4fbf\u8981\u62dc\u8c22\uff0c\u795e\u60c5\u95f4\u663e \u51fa\u65e0\u6bd4\u7684\u6fc0\u52a8\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u53cd\u800c\u6709\u4e9b\u624b\u8db3\u65e0\u63aa\uff0c\u6025\u6025\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\u5feb\u522b\u8fd9\u6837\uff0c\u6797\u5bb6\u58f0\u662f\u5c48\u6253\u6210\u62db\uff0c\u542b\u51a4\u83ab \u540d\uff0c\u5728\u4e0b\u65e2\u7136\u77e5\u9053\u4e86\u8fd9\u4ef6\u4e8b\uff0c\u600e\u80fd\u89c1\u6b7b\u4e0d\u6551\uff01\u201d \u3000\u3000\u5982\u4eca\u5df2\u7ecf\u7528\u4e0d\u7740\u518d\u8bd5\u63a2\u5b59\u5c0f\u51e4\u4e86\u3002\u3001\u7531\u521a\u624d\u7684\u4e3e\u52a8\uff0c\u663e\u7136\u5979\u5bf9\u6797\u5bb6\u58f0\u4e1d\u6beb\u4e0d\u66fe\u53d8\u5fc3\u3002 \u3000\u3000\u5b59\u5c0f\u51e4\u7f04\u9ed8\u4e86\u7247\u523b\uff0c\u624d\u53c8\u95ee\u9053\uff1a\u201c\u5bb6\u58f0\u76ee\u524d\u4eba\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u5728\u4e0b\u6682\u65f6\u53ea\u80fd\u628a\u4ed6\u85cf\u533f\u5728\u5ba2\u6808\u91cc\u3002\u201d \u3000\u3000\u201c\u5ba2\u6808\u91cc\u90fd\u6709\u4e9b\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u59d1\u5a18\u5fc5\u5b9a\u542c\u8bf4\u8fc7\u5927\u5185\u4f8d\u536b\u7edf\u9886\u738b\u5927\u4eba\u5df2\u7ecf\u6765\u5230\u5f00\u5c01\uff0c\u5728\u4e0b\u5c31\u548c\u4ed6\u4f4f\u5728\u4e00\u8d77\u3002\u201d \u3000\u3000\u201c\u539f\u6765\u5c11\u4f7f\u662f\u4eac\u57ce\u7687\u5bab\u7684\u4eba\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u98ce\u8d77\u521d\u672c\u6765\u8fd8\u6709\u4e9b\u62c5\u5fc3\uff0c\u4e00\u542c\u6709\u5927\u5185\u4f8d\u536b\u7edf\u9886\u738b\u5927\u4eba\u4f5c\u4e3b\uff0c\u987f\u65f6\u50cf\u670d\u4e0b\u4e86\u4e00\u9897\u5b9a\u5fc3 \u4e38\u3002 \u3000\u3000\u201c\u59d1\u5a18\u522b\u95ee\u5728\u4e0b\u662f\u4e0d\u662f\u5927\u5185\u7684\u4eba\uff0c\u603b\u4e4b\u662f\u548c\u738b\u7edf\u9886\u7531\u4eac\u57ce\u4e00\u8d77\u6765\u7684\uff0c\u73b0\u5728\u5728\u4e0b\u60f3\u95ee\u95ee\u59d1 \u5a18\uff0c\u662f\u5426\u60f3\u548c\u6797\u5bb6\u58f0\u91cd\u805a\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u51e4\u7728\u52a8\u7740\u7738\u5b50\u9053\uff1a\u201c\u8fd9\u662f\u6211\u4e24\u5e74\u591a\u6765\u4e00\u76f4\u7684\u5fc3\u613f\uff0c\u4f46\u53c8\u600e\u80fd\u529e\u5f97\u5230\u5462\uff1f\u5c24\u5176\u76ee\u524d\u98ce \u58f0\u6700\u7d27\uff0c\u82e5\u6211\u548c\u4ed6\u5077\u5077\u89c1\u4e0a\u4e00\u9762\uff0c\u4e07\u4e00\u5c0f\u71d5\u5b50\u6447\u5934\u9053\uff1a\u201d\u6211\u662f\u8bf4\u8ba9\u4f60\u4eec\u4ece\u6b64\u540e\u6c38\u8fdc\u5728\u4e00\u8d77\uff0c \u5e76\u975e\u53ea\u662f\u548c\u4ed6\u5077\u5077\u89c1\u4e0a\u4e00\u9762\u3002\u201d \u3000\u3000\u5b59\u5c0f\u51e4\u663e\u51fa\u4e00\u8138\u56f0\u60d1\u4e4b\u8272\u9053\uff1a\u201c\u600e\u4e48\u53ef\u80fd\u5462\uff1f\u9664\u975e\u5f00\u5c01\u5e9c\u4e0d\u518d\u901a\u7f09\u4ed6\uff0c\u540c\u65f6\u6211\u4e5f\u5fc5\u987b\u6062 \u590d\u81ea\u7531\u4e4b\u8eab\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u5e73\u9759\u7684\u8bf4\u9053\uff1a\u201c\u4f60\u53ea\u8981\u6709\u80c6\u91cf\u7167\u7740\u6211\u7684\u529e\u6cd5\u505a\uff0c\u6240\u6709\u7684\u56f0\u96be\u95ee\u9898\u90fd\u53ef\u4ee5\u83b7\u5f97\u89e3 \u51b3\u3002\u201d \u3000\u3000\u201c\u5c11\u4fa0\u6709\u4ec0\u4e48\u529e\u6cd5\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u7acb\u5373\u628a\u6c5f\u5343\u91cc\u8bbe\u8ba1\u7684\u4e00\u5957\u529e\u6cd5\u4e00\u4e94\u4e00\u5341\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5b59\u5c0f\u51e4\u542c\u5f97\u867d\u7136\u4e0d\u4f4f\u70b9\u5934\uff0c\u5374\u4f3c\u4e4e\u53c8\u6709\u4e9b\u72b9\u8c6b\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u4e0d\u96be\u4e86\u89e3\u5bf9\u65b9\u6b64\u523b\u7684\u5fc3\u60c5\u3002 \u3000\u3000\u8fd9\u662f\u4ef6\u5fc5\u987b\u9f13\u8d77\u52c7\u6c14\u624d\u80fd\u5b8c\u6210\u7684\u4e8b\u3002 \u3000\u3000\u5b59\u5c0f\u51e4\u662f\u4e2a\u5f31\u5973\u5b50\uff0c\u867d\u7136\u4f5c\u4e86\u77e5\u5e9c\u7684\u4f8d\u59be\uff0c\u5e76\u6ca1\u89c1\u8fc7\u5927\u573a\u9762\uff0c\u8981\u5979\u629b\u5934\u9732\u9762\u53bb\u505a\u4e00\u4ef6\u4ece \u524d\u8fde\u60f3\u90fd\u6ca1\u60f3\u8fc7\u7684\u4e8b\uff0c\u7684\u786e\u4f1a\u6709\u4e9b\u80c6\u602f\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u7d27\u76ef\u7740\u5b59\u5c0f\u51e4\u7684\u795e\u8272\u9053\uff1a\u201c\u8fd9\u662f\u6709\u5173\u4f60\u7ec8\u751f\u5e78\u798f\u7684\u4e8b\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u4f60\u8981\u62ff\u51fa\u52c7 \u6c14\u3002\u5f53\u7136\uff0c\u8fd9\u9700\u8981\u4f60\u81ea\u5df1\u51b3\u5b9a\uff0c\u522b\u4eba\u65e0\u6cd5\u52c9\u5f3a\uff0c\u4f60\u5c31\u597d\u597d\u8003\u8651\u8003\u8651\u5427\uff01\u4e0d\u8fc7\u6211\u5fc5\u987b\u63d0\u9192\u4f60\uff0c \u673a\u4f1a\u53ea\u6709\u8fd9\u4e00\u4e2a\uff0c\u80af\u4e0d\u80af\u628a\u63e1\u8fd9\u673a\u4f1a\u5168\u5728\u4f60\uff0c\u6211\u8d70\u4e86\u3002\u201d \u3000\u3000\u5b59\u5c0f\u51e4\u5fd9\u9053\uff1a\u201c\u5c11\u4fa0\u6162\u8d70\uff01\u201d \u3000\u3000\u201c\u83ab\u975e\u4f60\u5df2\u7ecf\u51b3\u5b9a\u7167\u8ba1\u884c\u4e8b\uff1f\u201d \u3000\u3000\u201c\u6211\u662f\u8bf4\u53a2\u623f\u91cc\u8fd8\u6709\u4e24\u4e2a\u4e2b\u73af\uff0c\u4e07\u4e00\u5c11\u4fa0\u4eca\u665a\u524d\u6765\u7684\u4e8b\u88ab\u5979\u4eec\u77e5\u9053\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u653e\u5fc3\uff0c\u6211\u5df2\u7528\u8ff7\u9b42\u9999\u628a\u5979\u4eec\u8ff7\u5012\uff0c\u8981\u5230\u5929\u4eae\u624d\u80fd\u9192\u6765\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u6211\u51b3\u5b9a\u7167\u7740\u5c11\u4fa0\u7684\u5429\u5490\u505a\uff0c\u5c11\u4fa0\u53ef\u4ee5\u8d70\u4e86\u3002\u201d \u3000\u3000\u5c31\u5728\u5c0f\u71d5\u5b50\u548c\u5b59\u5c0f\u51e4\u89c1\u9762\u540e\u7684\u4e0a\u5348\uff0c\u6cb3\u5357\u5de1\u629a\u9a6c\u6587\u4e2d\u53c8\u6765\u5ba2\u6808\u62dc\u4f1a\u738b\u5f64\u3002 \u3000\u3000\u8fd9\u662f\u738b\u5f64\u7279\u522b\u76f8\u9080\u7684\uff0c\u540d\u4e49\u4e0a\u662f\u4ed6\u81ea\u79f0\u7531\u5bab\u5185\u5e26\u6765\u51e0\u575b\u897f\u57df\u8fdb\u8d21\u7684\u597d\u9152\uff0c\u7531\u4e8e\u9a6c\u6587\u4e2d\u662f \u897f\u57df\u6765\u7684\uff0c\u7279\u5730\u8bf7\u4ed6\u6765\u54c1\u5c1d\u4e00\u4e0b\u5bb6\u4e61\u7684\u7f8e\u9152\u4f73\u917f\u3002 \u3000\u3000\u5927\u5185\u7edf\u9886\u76f8\u9080\uff0c\u9a6c\u6587\u4e2d\u81ea\u7136\u4e0d\u80fd\u4e0d\u6765\u3002 \u3000\u3000\u9a6c\u6587\u4e2d\u968f\u5e26\u7684\u4eba\uff0c\u9664\u6cb3\u5357\u603b\u6355\u5934\u5f20\u4e0d\u7a7a\u5916\uff0c\u53e6\u6709\u4e00\u4f4d\u6587\u6848\u5e08\u7237\u3002 \u3000\u3000\u4e3b\u4eba\u65b9\u9762\uff0c\u9664\u738b\u5f64\u4e4b\u5916\uff0c\u53ea\u6709\u97e9\u6d9b\u548c\u71d5\u98de\u4f5c\u966a\u3002 \u3000\u3000\u996d\u83dc\u662f\u7531\u5916\u9762\u9152\u697c\u53eb\u6765\u7684\u3002 \u3000\u3000\u9a6c\u6587\u4e2d\u5230\u8fbe\u4e0d\u4e45\uff0c\u996d\u83dc\u4fbf\u5df2\u6446\u4e0a\u3002 \u3000\u3000\u81f3\u4e8e\u5e26\u6765\u7684\u9152\uff0c\u65e9\u5df2\u7531\u71d5\u98de\u642c\u6765\uff0c\u6446\u5728\u4e00\u65c1\u3002 \u3000\u3000\u5f53\u9152\u575b\u6253\u5f00\u6ce5\u5c01\uff0c\u679c\u7136\u9152\u6c14\u6ee1\u5ba4\u9187\u9999\u3002 \u3000\u3000\u5ba2\u6808\u91cc\u7279\u6d3e\u4e24\u540d\u4f19\u8ba1\uff0c\u8d1f\u8d23\u7167\u6599\u4e3b\u5ba2\u53cc\u65b9\u996e\u5bb4\u3002 \u3000\u3000\u9152\u8fc7\u4e09\u5de1\uff0c\u5ffd\u89c1\u5ba2\u6808\u7684\u5e10\u623f\u5148\u751f\u5306\u5306\u5fd9\u5fd9\u5954\u8fdb\u6765\u5411\u9a6c\u6587\u4e2d\u884c\u4e86\u4e2a\u5927\u793c\u9053\uff1a\u201c\u542f\u7980\u5927\u4eba\uff0c \u6709\u4f4d\u5987\u4eba\u6c42\u89c1\u5927\u4eba\uff01\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u76b1\u4e86\u76b1\u7709\u9053\uff1a\u201c\u5987\u4eba\u6c42\u89c1\uff1f\u53ef\u95ee\u8fc7\u5979\u7684\u8eab\u4efd\u6765\u5386\uff1f\u201d \u3000\u3000\u5e10\u623f\u5148\u751f\u8eac\u8eab\u9053\uff1a\u201c\u5e74\u7eaa\u5f88\u8f7b\uff0c\u636e\u5979\u8bf4\u6709\u8981\u7d27\u7684\u4e8b\u60c5\u8981\u6c42\u89c1\u5927\u4eba\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u5373\u4f7f\u8981\u6253\u5b98\u53f8\uff0c\u4e5f\u8981\u5230\u629a\u7f72\u4e0a\u544a\u624d\u6210\uff0c\u672c\u629a\u5e94\u738b\u5927\u4eba\u4e4b \u9080\u6765\u6b64\u996e\u5bb4\uff0c\u90a3\u5987\u4eba\u662f\u600e\u4e48\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e8b\u8349\u6c11\u5c31\u4e0d\u6e05\u695a\u4e86\u3002\u201d \u3000\u3000\u201c\u544a\u8bc9\u5979\uff0c\u672c\u629a\u514d\u89c1\uff0c\u5982\u679c\u6709\u51a4\u6789\uff0c\u53bf\u6709\u77e5\u53bf\u3001\u5e9c\u6709\u77e5\u5e9c\uff0c\u5c82\u53ef\u8d8a\u8859\u544a\u72b6\uff01\u201d \u3000\u3000\u90a3\u5e10\u623f\u5148\u751f\u521a\u8981\u65bd\u793c\u800c\u9000\uff0c\u5374\u542c\u738b\u5f64\u9053\uff1a\u201c\u9a6c\u5927\u4eba\uff0c\u65e2\u7136\u90a3\u6c11\u5987\u5f97\u77e5\u9a6c\u5927\u4eba\u5728\u5ba2\u6808\uff0c\u53c8 \u80c6\u6562\u524d\u6765\u6c42\u89c1\uff0c\u60f3\u5fc5\u6709\u91cd\u5927\u51a4\u60c5\uff0c\u5f53\u5e74\u5305\u9f99\u56fe\u7ecf\u5e38\u6709\u4eba\u5411\u4ed6\u62e6\u8f7f\u558a\u51a4\uff0c\u800c\u4e14\u53e4\u6765\u8fde\u5fa1\u524d\u544a\u72b6 \u4e5f\u65f6\u6709\u6240\u95fb\uff0c\u9a6c\u5927\u4eba\u662f\u6709\u540d\u7684\u6e05\u5b98\uff0c\u81ea\u5f53\u660e\u955c\u9ad8\u60ac\u624d\u5bf9\u3002\u201d \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u9a6c\u6587\u4e2d\u7b49\u4e8e\u975e\u63a5\u89c1\u90a3\u6c11\u5987\u4e0d\u53ef\u4e86\u3002 \u3000\u3000\u4e0d\u8fc7\u4ed6\u8fd8\u662f\u987f\u4e86\u987f\u9053\uff1a\u201c\u4e0b\u5b98\u63a5\u89c1\u90a3\u6c11\u5987\uff0c\u81ea\u662f\u7406\u6240\u5f53\u7136\uff0c\u6015\u7684\u662f\u56e0\u800c\u6253\u6270\u4e86\u738b\u5927\u4eba\u7684 \u9152\u5174\u3002\u201d \u3000\u3000\u738b\u5f64\u6447\u5934\u9053\uff1a\u201c\u54b1\u4eec\u90fd\u662f\u671d\u5ef7\u547d\u5b98\uff0c\u7406\u5e94\u4ee5\u9ece\u6c11\u767e\u59d3\u4e3a\u91cd\uff0c\u9152\u5bb4\u4e8b\u5c0f\uff0c\u6c11\u60c5\u4e8b\u5927\uff0c\u5c31\u7b97 \u803d\u8bef\u4e86\u8fd9\u987f\u9152\u5e2d\uff0c\u738b\u67d0\u518d\u884c\u9080\u5bb4\u53c8\u6709\u4f55\u59a8\uff01\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u8fde\u8fde\u9894\u9996\u79f0\u662f\uff01 \u3000\u3000\u4ed6\u5373\u5429\u5490\u5e10\u623f\u5148\u751f\u9053\uff1a\u201c\u628a\u90a3\u6c11\u5987\u5e26\u5230\u8fd9\u91cc\u6765\uff01\u201d \u3000\u3000\u4e00\u4f1a\u513f\uff0c\u5e10\u623f\u5148\u751f\u4fbf\u5e26\u7740\u4e00\u540d\u770b\u6765\u9887\u4e3a\u7aef\u5e84\u79c0\u4e3d\u7684\u5c11\u5987\u6765\u5230\u5ba2\u5385\u95e8\u53e3\u3002 \u3000\u3000\u5e10\u623f\u5148\u751f\u9053\uff1a\u201c\u5ba2\u5b98\u9996\u5e2d\uff0c\u90a3\u4f4d\u4fbf\u662f\u629a\u53f0\u5927\u4eba\uff0c\u4f60\u81ea\u5df1\u8fdb\u53bb\u5427\uff01\u201d \u3000\u3000\u90a3\u5c11\u5987\u795e\u8272\u751a\u4e3a\u7d27\u5f20\uff0c\u4f4e\u5782\u7740\u7c89\u9888\uff0c\u602f\u602f\u7684\u8de8\u8fdb\u95e8\u69db\uff0c\u7acb\u5373\u5012\u8eab\u76c8\u76c8\u8dea\u4e0b\uff0c\u8fde\u78d5\u4e86\u4e09\u4e2a \u5934\u9053\uff1a\u201c\u5c0f\u5973\u5b50\u53e9\u89c1\u629a\u53f0\u5927\u4eba\uff01\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f\u6709\u4ec0\u4e48\u4e8b\u6765\u89c1\u672c\u629a\uff1f\u201d \u3000\u3000\u5f53\u7740\u738b\u5f64\u7684\u9762\uff0c\u9a6c\u6587\u4e2d\u5fc5\u987b\u88c5\u51fa\u6001\u5ea6\u548c\u853c\u3001\u8bed\u6c14\u4eb2\u5207\u7684\u6837\u5b50\uff0c\u82e5\u6362\u6210\u662f\u5728\u5de1\u629a\u5927\u884c\uff0c\u6709 \u4eba\u51fb\u9f13\u9e23\u51a4\uff0c\u4e0d\u62d6\u4e0b\u53bb\u5148\u6253\u56db\u5341\u5927\u677f\u624d\u602a\u3002 \u3000\u3000\u90a3\u5c11\u5987\u987f\u4e86\u987f\u9053\uff1a\u201c\u5c0f\u5973\u5b50\u53eb\u5b59\u5c0f\u51e4\uff0c\u662f\u5f00\u5c01\u5e9c\u5c39\u77e5\u5e9c\u7684\u5185\u7737\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u5927\u611f\u610f\u5916\uff0c\u554a\u4e86\u58f0\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u4f60\u662f\u5c39\u77e5\u5e9c\u7684\u5185\u7737\uff01\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u71d5\u98de\u5728\u4e00\u65c1\uff0c\u5fd9\u62a2\u7740\u9053\uff1a\u201c\u7980\u9a6c\u5927\u4eba\uff0c\u5979\u7684\u786e\u662f\u5f00\u5c01\u5e9c\u77e5\u5e9c\u5927\u4eba\u7684\u5185\u7737\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u671b\u4e86\u71d5\u98de\u4e00\u773c\u9053\uff1a\u201c\u4e24\u5e74\u524d\uff0c\u4f60\u662f\u5f00\u5c01\u5e9c\u7684\u603b\u6355\u5934\uff0c\u96be\u602a\u4f1a\u8ba4\u8bc6\u5979\uff0c\u4f60\u518d\u8bf4\u660e\u767d \u4e9b\uff01\u201d \u3000\u3000\u71d5\u98de\u9053\uff1a\u201c\u5979\u662f\u5c39\u5927\u4eba\u7684\u4f8d\u59be\u3002\u201d \u3000\u3000\u53ea\u542c\u738b\u5f64\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u738b\u67d0\u4e5f\u60f3\u8d77\u6765\u4e86\u2026\u2026\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u89c6\u7ebf\u8f6c\u5411\u738b\u5f64\u9053\uff1a\u201c\u738b\u5927\u4eba\u60f3\u8d77\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u8fd9\u4e8b\u6c5f\u5343\u91cc\u5927\u4fa0\u5bf9\u738b\u67d0\u8bf4\u8fc7\uff0c\u4e24\u5e74\u534a\u524d\u5c39\u77e5\u5e9c\u4ee5\u4e00\u767e\u4e24\u94f6\u5b50\u4e70\u4e86\u4e00\u540d\u4f8d\u59be\uff0c \u4e00\u5b9a\u5c31\u662f\u5979\u4e86\u3002\u201d \u3000\u3000\u8dea\u5728\u5730\u4e0a\u7684\u5b59\u5c0f\u51e4\u8fde\u5fd9\u70b9\u5934\u9053\uff1a\u201c\u6b63\u662f\u5c0f\u5973\u5b50\u3002\u201d \u3000\u3000\u539f\u6765\u5b59\u5c0f\u51e4\u524d\u6765\u5411\u9a6c\u6587\u4e2d\u544a\u72b6\u7684\u8fd9\u4e00\u5207\uff0c\u6b63\u662f\u6c5f\u5343\u91cc\u7684\u5b89\u6392\u3002 \u3000\u3000\u56e0\u4e3a\u5f53\u7740\u738b\u5f64\u7684\u9762\uff0c\u9a6c\u6587\u4e2d\u5bf9\u5c39\u77e5\u5e9c\u5df2\u65e0\u6cd5\u5b98\u5b98\u76f8\u62a4\uff0c\u975e\u516c\u4e8b\u516c\u529e\u4e0d\u53ef\uff0c\u53ea\u8981\u544a\u5012\u4e86\u5c39 \u77e5\u5e9c\uff0c\u5b59\u5c0f\u51e4\u548c\u6797\u5bb6\u58f0\u81ea\u7136\u4e5f\u5c31\u53ef\u4ee5\u5206\u800c\u590d\u5408\u4e86\u3002 \u3000\u3000\u9a6c\u6587\u4e2d\u4f55\u7b49\u7cbe\u660e\uff0c\u5f53\u7136\u5fc3\u91cc\u4e5f\u6709\u4e86\u6570\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u5fc5\u987b\u88c5\u51fa\u5168\u4e0d\u77e5\u60c5\u7684\u6a21\u6837\uff0c\u6478\u7740\u5634\u5df4\u6c89\u541f\u4e86\u534a\u664c\uff0c\u9053\uff1a\u201c\u4f60\u5230\u8fd9\u91cc\u6765\u89c1\u6211\uff0c\u7a76\u7adf \u662f\u4e3a\u4e86\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u51e4\u53c8\u987f\u4e86\u987f\u9053\uff1a\u201c\u5c0f\u5973\u5b50\u8981\u544a\u5c39\u77e5\u5e9c\uff0c\u6c42\u5927\u4eba\u4f5c\u4e3b\u3002\u201d \u3000\u3000\u201c\u4f60\u544a\u4ed6\u4ec0\u4e48\uff1f\u4ed6\u54ea\u4e00\u70b9\u5bf9\u4e0d\u4f4f\u4f60\uff1f\u201d \u3000\u3000\u201c\u5c0f\u5973\u5b50\u5f53\u521d\u662f\u88ab\u4ed6\u5f3a\u884c\u9738\u5360\u6765\u7684\uff0c\u8fd9\u8fd8\u4e0d\u7b97\u2026\u2026\u201d \u3000\u3000\u201c\u83ab\u975e\u8fd8\u6709\u522b\u7684\uff1f\u201d \u3000\u3000\u201c\u5c0f\u5973\u5b50\u5f53\u521d\u5df2\u8eab\u6709\u6240\u5c5e\uff0c\u548c\u4e00\u6797\u59d3\u7537\u5b50\u884c\u8fc7\u6587\u5b9a\u4e4b\u793c\uff0c\u5c39\u77e5\u5e9c\u4e0d\u4f46\u5f3a\u5360\u4e86\u5c0f\u5973\u5b50\uff0c\u800c \u4e14\u2026\u2026\u201d \u3000\u3000\u201c\u800c\u4e14\u4ec0\u4e48\uff1f\u4e0d\u5fc5\u5bb3\u6015\uff0c\u5f53\u7740\u6211\u7684\u9762\uff0c\u53c8\u6709\u4eac\u57ce\u738b\u5927\u4eba\u5728\u5ea7\uff0c\u522b\u8bf4\u544a\u7684\u53ea\u662f\u4e00\u540d\u5c0f\u5c0f\u56db \u54c1\u77e5\u5e9c\uff0c\u5c31\u662f\u544a\u6211\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u597d\u987e\u8651\u7684\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u4e3a\u4e86\u8ba8\u597d\u738b\u5f64\uff0c\u8bf4\u5b8c\u8bdd\u8fd8\u6545\u610f\u53c8\u6307\u4e86\u6307\u738b\u5f64\u9053\uff1a\u201c\u8fd9\u4f4d\u5c31\u662f\u4eac\u57ce\u7684\u738b\u5927\u4eba\uff0c\u53e6\u4e00 \u4f4d\u662f\u71d5\u4f8d\u536b\uff0c\u66fe\u4f5c\u8fc7\u5f00\u5c01\u5e9c\u7684\u6355\u5934\uff0c\u4f60\u8be5\u65e9\u5c31\u8ba4\u8bc6\u7684\u3002\u201d \u3000\u3000\u5b59\u5c0f\u51e4\u70b9\u70b9\u5934\u9053\uff1a\u201c\u5c0f\u5973\u5b50\u8ba4\u8bc6\u71d5\u6355\u5934\u3002\u201d \u3000\u3000\u201c\u90a3\u8fd8\u6709\u4ec0\u4e48\u53ef\u6015\u7684\uff1f\u5feb\u8bf4\uff01\u201d \u3000\u3000\u201c\u5c39\u77e5\u5e9c\u53c8\u628a\u5c0f\u5973\u5b50\u7684\u672a\u5a5a\u592b\u5a7f\u5173\u8fdb\u5927\u7262\uff0c\u800c\u4e14\u5b9a\u4e86\u6b7b\u7f6a\uff0c\u8fd9\u51e0\u5929\u5c31\u8981\u95ee\u65a9\u2026\u2026\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u4e00\u5bf9\u773c\u73e0\u63a5\u8fde\u8f6c\u4e86\u51e0\u8f6c\uff0c\u9053\uff1a\u201c\u6709\u8fd9\u79cd\u4e8b\uff0c\u4ed6\u628a\u4f60\u90a3\u672a\u5a5a\u592b\u5a7f\u5b9a\u6210\u6b7b\u7f6a\uff0c\u7f6a\u540d\u662f \u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u51e4\u542b\u7740\u6cea\u6c34\u9053\uff1a\u201c\u7f6a\u540d\u662f\u79c1\u901a\u76d7\u532a\u3001\u56fe\u8c0b\u4e0d\u8f68\u3002\u201d \u3000\u3000\u201c\u4f60\u90a3\u672a\u5a5a\u592b\u5a7f\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u591a\u5927\u5e74\u7eaa\uff1f\u201d \u3000\u3000\u201c\u4ed6\u53eb\u6797\u5bb6\u58f0\uff0c\u4eca\u5e74\u4e8c\u5341\u5c81\u3002\u201d \u3000\u3000\u201c\u4f60\u5462\uff1f\u201d \u3000\u3000\u201c\u5c0f\u5973\u5b50\u5341\u4e5d\u5c81\uff0c\u88ab\u5c39\u77e5\u5e9c\u5f3a\u884c\u9738\u5360\u65f6\u624d\u5341\u4e03\u5c81\u3002\u201d \u3000\u3000\u201c\u6797\u5bb6\u58f0\u5e73\u65e5\u505a\u4f55\u751f\u7406\uff1f\u201d \u3000\u3000\u201c\u4ed6\u624d\u4e8c\u5341\u5c81\uff0c\u9664\u4e86\u8bfb\u4e66\uff0c\u4fbf\u662f\u5e2e\u5fd9\u5bb6\u4e2d\u505a\u4e9b\u7530\u91cc\u7684\u6d3b\u8ba1\uff0c\u5927\u4eba\uff01\u5c39\u77e5\u5e9c\u8bf4\u4ed6\u79c1\u901a\u76d7 \u532a\u3001\u56fe\u8c0b\u4e0d\u8f68\uff0c\u4e16\u4e0a\u53ef\u6709\u8fd9\u79cd\u4e8b\u5417\uff1f\u201d \u3000\u3000\u201c\u4f60\u73b0\u5728\u60f3\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u201c\u5c0f\u5973\u5b50\u53ea\u6c42\u5927\u4eba\u5f00\u6069\uff0c\u4e3a\u672a\u5a5a\u592b\u5a7f\u7533\u51a4\uff0c\u6073\u6c42\u5927\u4eba\u4f5c\u4e3b\u8fd8\u4ed6\u6e05\u767d\u3002\u53e6\u5916\uff0c\u66f4\u5e0c\u671b\u65e9\u65e5 \u548c\u5c39\u77e5\u5e9c\u8131\u79bb\u8fd9\u6bb5\u5b7d\u7f18\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u5c39\u5b88\u4e49\uff0c\u7adf\u6562\u505a\u51fa\u8fd9\u79cd\u4e8b\u6765\uff0c\u7b80\u76f4\u5927\u65e0\u6cd5\u65e0\u5929\u4e86\u3002\u201d \u3000\u3000\u63a5\u7740\u8f6c\u5934\u5411\u5b59\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u672c\u629a\u56de\u5230\u629a\u7f72\u4e4b\u540e\uff0c\u9a6c\u4e0a\u4f20\u558a\u5c39\u77e5\u5e9c\u67e5\u529e\uff0c\u4e00\u5b9a\u4f1a\u5bf9 \u4f60\u6709\u4e2a\u4ea4\u4ee3\u3002\u81f3\u4e8e\u4f60\uff0c\u5c31\u5148\u56de\u53bb\u7b49\u6d88\u606f\u5427\uff01\u201d \u3000\u3000\u53ea\u89c1\u738b\u5f64\u6447\u5934\u4e00\u7b11\u9053\uff1a\u201c\u9a6c\u5927\u4eba\uff0c\u8fd9\u6837\u7684\u5904\u7f6e\uff0c\u53ea\u6015\u6709\u4e9b\u4e0d\u59a5\u5427\uff01\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u53cc\u988a\u62bd\u6410\u4e86\u51e0\u4e0b\u9053\uff1a\u201c\u738b\u5927\u4eba\u8ba4\u4e3a\u54ea\u91cc\u4e0d\u59a5\uff1f\u201d \u3000\u3000\u201c\u8fd9\u540d\u5973\u4e8e\u65e2\u7136\u662f\u524d\u6765\u544a\u5c39\u77e5\u5e9c\u7684\uff0c\u600e\u53ef\u8ba9\u5979\u518d\u56de\u53bb\uff1f\u4e07\u4e00\u5c39\u77e5\u5e9c\u5bf9\u5979\u5148\u4e0b\u4e86\u624b\uff0c\u5c82\u975e \u53c8\u6789\u9001\u4e00\u6761\u4eba\u547d\u5417\uff1f\u201d \u3000\u3000\u201c\u53ea\u6015\u5c39\u77e5\u5e9c\u8fd8\u4e0d\u6562\u5427\uff1f\u201d \u3000\u3000\u201c\u4f60\u521a\u624d\u5df2\u7ecf\u8bf4\u4ed6\u65e0\u6cd5\u65e0\u5929\uff0c\u65e2\u7136\u65e0\u6cd5\u65e0\u5929\uff0c\u4ed6\u8fd8\u6709\u4ec0\u4e48\u4e8b\u505a\u4e0d\u51fa\u6765\uff1f\u201d \u3000\u3000\u201c\u4f9d\u738b\u5927\u7684\u610f\u601d\u5462\uff1f\u201d \u3000\u3000\u201c\u5c31\u628a\u5979\u7559\u5728\u5ba2\u6808\uff0c\u7531\u738b\u67d0\u8d1f\u8d23\u5979\u7684\u5b89\u5168\uff0c\u9a6c\u5927\u4eba\u8be5\u653e\u5fc3\u4e86\u5427\uff01\u201d \u3000\u3000\u201c\u8fd9\u6837\u4e5f\u597d\uff0c\u4e0b\u5b98\u73b0\u5728\u5c31\u56de\u629a\u7f72\u4f20\u558a\u5c39\u77e5\u5e9c\u53bb\u3002\u201d \u3000\u3000\u201c\u4e0d\u5fc5\uff01\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u4e00\u6014\u9053\uff1a\u201c\u83ab\u975e\u738b\u5927\u4eba\u662f\u8981\u4e0b\u5b98\u4e0d\u5fc5\u7ba1\u8fd9\u4ef6\u6848\u5b50\uff1f\u201d \u3000\u3000\u738b\u5f64\u51b7\u7b11\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u738b\u67d0\u7684\u610f\u601d\u662f\u5e0c\u671b\u9a6c\u5927\u4eba\u73b0\u5728\u5c31\u6d3e\u4eba\u628a\u5c39\u77e5\u5e9c\u53eb\u5230\u8fd9\u91cc\u6765\uff0c \u4e5f\u597d\u5c31\u8fd9\u4ef6\u6848\u5b50\u901f\u5ba1\u901f\u7ed3\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u82e6\u7b11\u9053\uff1a\u201c\u8fd9\u6837\u505a\u53ea\u6015\u2026\u2026\u201d \u3000\u3000\u201c\u9a6c\u5927\u4eba\u53ea\u6015\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u8fd9\u91cc\u662f\u4e09\u516c\u4e3b\u51e4\u9a7e\u9a7b\u6654\u4e4b\u6240\uff0c\u53c8\u5f53\u7740\u738b\u5927\u4eba\u7684\u9762\uff0c\u628a\u5c39\u77e5\u5e9c\u53eb\u6765\u8fd9\u91cc\u5ba1\u95ee\uff0c\u603b\u662f\u6709\u4e9b \u4e0d\u65b9\u4fbf\u7684\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u5bf9\u9a6c\u5927\u4eba\u5b9e\u8bdd\u5b9e\u8bf4\u5427\uff01\u4e09\u516c\u4e3b\u8fd9\u6b21\u51fa\u4eac\uff0c\u6b63\u662f\u5949\u4e86\u7687\u4e0a\u65e8\u610f\uff0c\u5230\u5404\u5730\u8bbf\u5bdf\u6c11\u9690\u3001 \u6574\u996c\u5b98\u98ce\u3002 \u3000\u3000\u8fd9\u4ef6\u4e8b\u738b\u67d0\u65e2\u7136\u5df2\u7ecf\u77e5\u9053\u4e86\uff0c\u5c31\u5fc5\u987b\u8f6c\u62a5\u4e09\u516c\u4e3b\uff0c\u56de\u4eac\u4e4b\u540e\uff0c\u518d\u5411\u7687\u4e0a\u542f\u594f\u3002\u9a6c\u5927\u4eba\u82e5 \u80fd\u628a\u8fd9\u4ef6\u6848\u5b50\u901f\u5ba1\u901f\u7ed3\uff0c\u53c8\u6709\u738b\u67d0\u5728\u65c1\u4f5c\u8bc1\uff0c\u5bf9\u4f60\u7684\u524d\u7a0b\u3002k\u6b63\u662f\u5927\u6709\u5e2e\u52a9\u7684\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\uff0c\u542c\u5728\u9a6c\u6587\u4e2d\u8033\u6735\u91cc\uff0c\u7b80\u76f4\u6709\u5982\u5723\u65e8\u4e00\u822c\uff0c\u6839\u672c\u4e0d\u80fd\u8fdd\u6297\uff0c\u8fde\u5fd9\u4ea4\u4ee3\u603b\u6355\u5934\u5f20 \u4e0d\u7a7a\u53bb\u4f20\u5524\u5c39\u77e5\u5e9c\u524d\u6765\u3002 \u3000\u3000\u53e6\u4e00\u65b9\u9762\uff0c\u518d\u547d\u5b59\u5c0f\u51e4\u6682\u65f6\u5230\u623f\u95f4\u91cc\u56de\u907f\u3002 \u3000\u3000\u5b59\u5c0f\u51e4\u8fdb\u5165\u7684\u623f\u95f4\uff0c\u6b63\u662f\u5c0f\u71d5\u5b50\u7684\u4f4f\u5904\u3002 \u3000\u3000\u4e0d\u6d88\u8bf4\uff0c\u6797\u5bb6\u58f0\u4e5f\u4f4f\u5728\u8fd9\u91cc\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5c0f\u71d5\u5b50\u548c\u6797\u5bb6\u58f0\u90fd\u5728\u623f\u4e2d\u3002 \u3000\u3000\u6797\u5bb6\u58f0\u5df2\u542c\u5c0f\u71d5\u5b50\u8bf4\u8fc7\u6628\u591c\u63a2\u8bbf\u5b59\u5c0f\u51e4\u4ee5\u53ca\u4eca\u5929\u5c06\u524d\u6765\u544a\u72b6\u7684\u4e8b\uff0c\u6b64\u523b\u4e00\u89c1\u5b59\u5c0f\u51e4\u8fdb \u6765\uff0c\u7981\u4e0d\u4f4f\u4e0a\u524d\u4e00\u4e0b\u5b50\u5c31\u628a\u5b59\u5c0f\u51e4\u7d27\u7d27\u62e5\u4f4f\u3002 \u3000\u3000\u5b59\u5c0f\u51e4\u66f4\u662f\u60ca\u559c\u4ea4\u96c6\u3002 \u3000\u3000\u5f53\u7740\u5c0f\u71d5\u5b50\u7684\u9762\uff0c\u4e24\u4eba\u4ec5\u76f8\u62e5\u4e86\u7247\u523b\uff0c\u4fbf\u5206\u5f00\u624b\u6765\uff0c\u5f7c\u6b64\u6fc0\u52a8\u5f97\u51dd\u76ee\u76f8\u5411\uff0c\u5374\u662f\u8bf4\u4e0d\u51fa \u8bdd\u6765\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u4f60\u4eec\u8981\u8bf4\u8bdd\uff0c\u5fc5\u987b\u4f4e\u58f0\u4e9b\uff0c\u514d\u5f97\u88ab\u5ba2\u5385\u91cc\u7684\u9a6c\u5de1\u629a\u542c\u5230\u3002\u201d \u3000\u3000\u4e24\u4eba\u4f9d\u8a00\u90fd\u5c3d\u91cf\u4e0d\u5f00\u53e3\u3002 \u3000\u3000\u8bb8\u4e45\uff0c\u6797\u5bb6\u58f0\u624d\u5c0f\u58f0\u95ee\u9053\uff1a\u201c\u521a\u624d\u5de1\u629a\u5927\u4eba\u66fe\u8bf4\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u51e4\u4e5f\u4f4e\u58f0\u56de\u7b54\uff1a\u201c\u73b0\u5728\u53bb\u4f20\u5c39\u77e5\u5e9c\u4e86\uff0c\u5c39\u77e5\u5e9c\u9a6c\u4e0a\u5c31\u5230\u3002\u201d \u3000\u3000\u201c\u5de1\u629a\u5927\u4eba\u4f1a\u600e\u4e48\u529e\u4ed6\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u51e4\u671b\u671b\u5c0f\u71d5\u5b50\uff0c\u5374\u4e0d\u77e5\u8be5\u600e\u4e48\u56de\u7b54\uff1f\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u4f60\u4eec\u653e\u5fc3\uff0c\u6211\u6c5f\u53d4\u53d4\u8fd9\u5957\u529e\u6cd5\u662f \u903c\u4f7f\u9a6c\u5de1\u629a\u975e\u4e25\u529e\u5c39\u77e5\u5e9c\u4e0d\u53ef\uff0c\u5426\u5219\u5fc5\u5b9a\u5f71\u54cd\u4ed6\u7684\u524d\u7a0b\u3002\u5728\u8fd9\u79cd\u60c5\u5f62\u4e0b\uff0c\u9a6c\u5de1\u629a\u4e3a\u6c42\u81ea\u4fdd\uff0c \u6839\u672c\u4e0d\u6562\u5b98\u5b98\u76f8\u62a4\u3002\u201d \u3000\u3000\u5b59\u5c0f\u51e4\u611f\u6fc0\u7684\u9053\uff1a\u201c\u6c5f\u5927\u4fa0\u5bf9\u6211\u4eec\u592a\u597d\u4e86\uff0c\u6211\u4eec\u771f\u4e0d\u77e5\u8be5\u5982\u4f55\u62a5\u7b54\u4ed6\u8001\u4eba\u5bb6\uff0c\u80fd\u4e0d\u80fd\u8ba9 \u6211\u73b0\u5728\u5c31\u89c1\u89c1\u4ed6\uff1f\u201d \u3000\u3000\u201c\u4ed6\u8001\u4eba\u5bb6\u5c31\u5728\u9694\u58c1\uff0c\u4f60\u60f3\u62dc\u89c1\u4ed6\uff0c\u673a\u4f1a\u591a\u5f97\u5f88\uff0c\u4e0d\u5fc5\u73b0\u5728\u3002\u201d \u3000\u3000\u201c\u4ed6\u8001\u4eba\u5bb6\u662f\u4e00\u4ee3\u5927\u4fa0\uff0c\u53c8\u662f\u738b\u5927\u4eba\u7684\u597d\u53cb\uff0c\u738b\u5927\u4eba\u8bf7\u5ba2\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u89c1\u4ed6\u8001\u4eba\u5bb6\u5728\u5ea7 \u5462\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4e0d\u65b9\u4fbf\u548c\u9a6c\u5de1\u629a\u89c1\u9762\uff0c\u5176\u5b9e\u8fd9\u4ef6\u4e8b\u5b8c\u5168\u662f\u4ed6\u8001\u4eba\u5bb6\u5728\u5e55\u540e\u4e3b\u6301\u7684\uff0c\u738b\u5927\u4eba\u4e5f\u662f\u542c\u4ed6 \u7684\u5429\u5490\u884c\u4e8b\uff0c\u96be\u9053\u4f60\u4eec\u8fd8\u60f3\u4e0d\u5230\u5417\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6599\u60f3\u6797\u5bb6\u58f0\u548c\u5b59\u5c0f\u51e4\u5fc5\u5b9a\u6709\u8bf4\u4e0d\u5b8c\u7684\u77e5\u5fc3\u8bdd\uff0c\u5fd9\u7ad9\u8d77\u8eab\u9053\uff1a\u201c\u6211\u8fd8\u6709\u70b9\u4e8b\u8981\u51fa\u53bb \u4e00\u4e0b\uff0c\u4f60\u4eec\u597d\u597d\u8c08\u8c08\u5427\uff01\u5e72\u4e07\u58f0\u97f3\u8981\u5c0f\uff0c\u4e0d\u80fd\u8ba9\u5916\u9762\u7684\u4eba\u542c\u5230\u3002\u201d \u3000\u3000\u5f20\u4e0d\u7a7a\u5949\u547d\u5230\u5f00\u5c01\u5e9c\u4f20\u558a\u5c39\u77e5\u5e9c\u53bb\u4e86\u3002 \u3000\u3000\u5ba2\u5385\u91cc\u7684\u9152\u5bb4\u7ee7\u7eed\u8fdb\u884c\u3002 \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u738b\u67d0\u6700\u8fd1\u8fd9\u4e24\u4e09\u5e74\uff0c\u66fe\u6765\u8fc7\u5f00\u5c01\u591a\u6b21\uff0c\u65e9\u5c31\u542c\u8bf4\u5c39\u77e5\u5e9c\u5b98\u58f0\u4e0d\u4f73\uff0c\u73b0\u5728\u770b \u6765\uff0c\u679c\u7136\u4f20\u8a00\u4e0d\u865a\u3002\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\uff0c\u7b49\u4e8e\u6709\u610f\u635f\u9a6c\u6587\u4e2d\u7684\u9762\u5b50\u3002 \u3000\u3000\u9a6c\u6587\u4e2d\u80c0\u7ea2\u7740\u8138\uff0c\u5e72\u54b3\u4e86\u51e0\u58f0\u9053\uff1a\u201c\u4e0b\u5b98\u60ed\u6127\uff0c\u5bf9\u8fd9\u4e8b\u4e00\u76f4\u8499\u5728\u9f13\u91cc\u3002\u201d \u3000\u3000\u201c\u9a6c\u5927\u4eba\u7528\u4e0d\u7740\u81ea\u8d23\uff0c\u4f60\u662f\u4e00\u7701\u4e4b\u957f\uff0c\u7ba1\u7684\u4e8b\u592a\u591a\u4e86\uff0c\u4e8b\u5b9e\u4e0a\u4e5f\u4e0d\u53ef\u80fd\u4ec0\u4e48\u4e8b\u90fd\u80fd\u4e86\u5982 \u6307\u638c\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u603b\u7b97\u677e\u4e86\u4e00\u53e3\u6c14\uff0c\u987f\u4e86\u987f\u9053\uff1a\u201c\u738b\u5927\u4eba\u7684\u610f\u601d\uff0c\u8be5\u628a\u5c39\u77e5\u5e9c\u600e\u6837\u53d1\u843d\uff1f\u201d \u3000\u3000\u738b\u5f64\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u9a6c\u5927\u4eba\u7684\u4e8b\uff0c\u738b\u67d0\u662f\u5927\u5185\u7684\u4eba\uff0c\u4e0d\u4fbf\u591a\u8a00\uff0c\u4e0d\u8fc7\u738b\u67d0\u5fc5\u987b\u628a\u4e8b\u60c5\u7ecf\u8fc7 \u636e\u5b9e\u8f6c\u62a5\u4e09\u516c\u4e3b\uff0c\u81f3\u4e8eZ\u516c\u4e3b\u662f\u5426\u8f6c\u594f\u7687\u4e0a\uff0c\u90a3\u8be5\u662f\u7531\u4e09\u516c\u4e3b\u51b3\u5b9a\u4e86\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u966a\u7b11\u9053\uff1a\u201c\u4e0b\u5b98\u5f88\u5e0c\u671b\u738b\u5927\u4eba\u80fd\u63d0\u4f9b\u4e00\u70b9\u610f\u89c1\u3002\u201d \u3000\u3000\u201c\u5927\u5185\u7684\u4eba\uff0c\u5c82\u53ef\u5e72\u9884\u5730\u65b9\u653f\u4e8b\uff1f\u5f85\u4f1a\u513f\u5c39\u77e5\u5e9c\u5230\u8fbe\u540e\uff0c\u738b\u67d0\u4f3c\u4e4e\u4ee5\u907f\u5e2d\u4e3a\u4f73\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u5fd9\u9053\uff1a\u201c\u738b\u5927\u4eba\u4e00\u5b9a\u8981\u5728\u8fd9\u91cc\u624d\u6210\u3002\u201d \u3000\u3000\u738b\u5f64\u6545\u4f5c\u4e0d\u89e3\u9053\uff1a\u201c\u4e3a\u4f55\u975e\u8981\u738b\u67d0\u5728\u573a\u4e0d\u53ef\uff1f\u201d \u3000\u3000\u201c\u95ee\u5b98\u53f8\u5e94\u5728\u7f72\u8859\uff0c\u5982\u4eca\u628a\u5c39\u77e5\u5e9c\u4f20\u6765\u5ba2\u6808\u95ee\u8bdd\uff0c\u4f3c\u4e4e\u6709\u8fdd\u4f53\u5236\uff0c\u552f\u6709\u738b\u5927\u4eba\u5728\u5ea7\uff0c\u624d \u80fd\u8ba9\u4eba\u77e5\u9053\u4e0b\u5b98\u628a\u5c39\u77e5\u5e9c\u4f20\u6765\u5ba2\u6808\u662f\u53e6\u6709\u539f\u56e0\u3002\u201d \u3000\u3000\u8fd9\u662f\u9a6c\u6587\u4e2d\u7684\u771f\u5fc3\u8bdd\uff0c\u8fd9\u51e0\u5e74\u6765\uff0c\u4ed6\u66fe\u6536\u53d7\u8fc7\u5c39\u77e5\u5e9c\u4e0d\u5c11\u91cd\u793c\uff0c\u4e24\u4eba\u4e4b\u95f4\u9887\u6709\u79c1\u4ea4\uff0c\u552f \u6709\u5f53\u7740\u738b\u5f64\u7684\u9762\u529e\u4eba\uff0c\u624d\u80fd\u8ba9\u5c39\u77e5\u5e9c\u77e5\u9053\u4ed6\u662f\u8feb\u4e0d\u5f97\u5df2\uff0c\u5426\u5219\u4ed6\u53c8\u5982\u4f55\u5411\u5176\u4ed6\u7684\u5fc3\u8179\u624b\u4e0b\u4ea4 \u4ee3\uff1f\u65e2\u7136\u9a6c\u6587\u4e2d\u575a\u9080\u738b\u5f64\u5728\u573a\uff0c\u738b\u5f64\u5f53\u7136\u4e5f\u4e0d\u518d\u63a8\u8f9e\u3002 \u3000\u3000\u8fd9\u987f\u9152\u996d\u5403\u5f97\u5e76\u4e0d\u5c3d\u5174\uff0c\u5c24\u5176\u9a6c\u6587\u4e2d\uff0c\u66f4\u662f\u607c\u5728\u5fc3\u91cc\u3002 \u3000\u3000\u6563\u5e2d\u540e\uff0c\u738b\u5f64\u7acb\u523b\u5429\u5490\u628a\u7897\u76d8\u676f\u7b77\u64a4\u8d70\u3002 \u3000\u3000\u7136\u540e\uff0c\u9a6c\u6587\u4e2d\u548c\u738b\u5f64\u5c45\u4e2d\u5e76\u80a9\u800c\u5750\uff0c\u97e9\u6d9b\u548c\u71d5\u98de\u4f8d\u7acb\u5728\u738b\u5f64\u8eab\u540e\uff0c\u629a\u8857\u7684\u6587\u6848\u5e08\u7237\u5219\u7ad9 \u5728\u9a6c\u6587\u4e2d\u8eab\u540e\u3002 \u3000\u3000\u9a6c\u6587\u4e2d\u53c8\u5429\u5490\u5ba2\u6808\u638c\u67dc\u9001\u6765\u6587\u623f\u56db\u5b9d\uff0c\u4ee5\u4fbf\u5f85\u4f1a\u513f\u95ee\u8bdd\u65f6\u7531\u6587\u6848\u5e08\u7237\u505a\u6210\u7b14\u5f55\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u76cf\u8336\u5de5\u592b\uff0c\u5f20\u4e0d\u7a7a\u5df2\u966a\u540c\u5f00\u5c01\u5e9c\u77e5\u5e9c\u5c39\u5b88\u4e49\u5230\u6765\u3002 \u3000\u3000\u5c39\u77e5\u5e9c\u5230\u73b0\u5728\u8fd8\u4e0d\u77e5\u9053\u7a76\u7adf\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\uff0c\u56e0\u4e3a\u5f20\u4e0d\u7a7a\u53ea\u544a\u8bc9\u4ed6\u5de1\u629a\u5927\u4eba\u5728\u5ba2\u6808\u91cc\u6709 \u8bf7\u3002 \u3000\u3000\u4ed6\u4ece\u524d\u5df2\u89c1\u8fc7\u738b\u5f64\uff0c\u5f53\u4ed6\u8fdb\u5f97\u95e8\u6765\uff0c\u89c1\u738b\u5f64\u4e5f\u5728\u5ea7\uff0c\u5185\u5fc3\u987f\u65f6\u6709\u4e9b\u4e03\u4e0a\u516b\u4e0b\u3002 \u3000\u3000\u597d\u5728\u4ed6\u4e8e\u5b98\u573a\u65e5\u4e45\uff0c\u5185\u5fc3\u867d\u7136\u7d27\u5f20\uff0c\u8868\u9762\u4e0a\u8868\u73b0\u5f97\u4ecd\u4e0d\u5931\u6c89\u7a33\uff0c\u5148\u5411\u9a6c\u6587\u4e2d\u8eac\u8eab\u4e00\u793c \u9053\uff1a\u201c\u5351\u804c\u53c2\u89c1\u5927\u4eba\uff01\u201d \u3000\u3000\u63a5\u7740\u518d\u5411\u738b\u5f64\u65bd\u793c\u9053\uff1a\u201c\u539f\u6765\u738b\u5927\u4eba\u4e5f\u5728\u8fd9\u91cc\uff0c\u4e0b\u5b98\u62dc\u89c1\uff01\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u4e0d\u5fc5\u62d8\u793c\uff0c\u4f60\u8bf7\u5750\u4e0b\uff01\u201d \u3000\u3000\u5c39\u77e5\u5e9c\u606d\u606d\u656c\u656c\u7684\u5728\u4e00\u65c1\u5750\u4e0b\u3002 \u3000\u3000\u9a6c\u6587\u4e2d\u8f7b\u54b3\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u672c\u629a\u8981\u4f60\u6765\uff0c\u4e0d\u4e3a\u522b\u7684\uff0c\u542c\u8bf4\u8d35\u5e9c\u5927\u8f66\u5173\u4e86\u4e00\u540d\u5373\u5c06\u5904\u51b3\u7684\u6b7b \u56de\u6797\u5bb6\u58f0\uff0c\u6709\u8fd9\u56de\u4e8b\u5417\uff1f\u201d \u3000\u3000\u5c39\u77e5\u5e9c\u987f\u65f6\u795e\u8272\u4e00\u53d8\uff0c\u4f4e\u4e0b\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u53ea\u662f\u8fd9\u540d\u6b7b\u56de\u5df2\u5728\u524d\u591c\u88ab\u4eba\u52ab\u8d70\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u54e6\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6709\u8fd9\u79cd\u4e8b\uff1f\u662f\u5426\u5df2\u901a\u7f09\u5f52\u6848\uff1f\u6b7b\u56de\u88ab\u52ab\uff0c\u975e\u540c\u5c0f\u53ef\uff0c\u662f\u5426\u5df2\u62a5 \u5230\u629a\u7f72\uff1f\u201d \u3000\u3000\u201c\u5351\u804c\u5df2\u6d3e\u51fa\u6355\u5feb\u548c\u5b98\u5175\u56db\u5904\u67e5\u7f09\uff0c\u81f3\u4eca\u5c1a\u672a\u67e5\u51fa\u6b7b\u56da\u7a76\u4e3a\u4f55\u4eba\u6240\u52ab\uff1f\u9003\u5f80\u4f55\u65b9\uff1f\u81f3\u4e8e \u62a5\u5f80\u629a\u7f72\u5448\u6587\uff0c\u81f3\u4eca\u5c1a\u672a\u5448\u51fa\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u8f6c\u8fc7\u5934\u6765\u9053\uff1a\u201c\u738b\u5927\u4eba\uff0c\u539f\u6765\u6b7b\u56da\u5df2\u88ab\u4eba\u52ab\u8d70\uff0c\u521a\u624d\u544a\u72b6\u7684\u5973\u5b50\u5e76\u672a\u63d0\u8d77\uff01\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u8fd9\u79cd\u4e8b\u5979\u5982\u4f55\u80fd\u77e5\u9053\uff0c\u5176\u5b9e\u73b0\u5728\u6b7b\u56de\u88ab\u52ab\u7684\u4e8b\u5df2\u4e0d\u91cd\u8981\uff0c\u9a6c\u5927\u4eba\u5e94\u8be5\u5148\u67e5\u660e \u90a3\u6b7b\u56db\u5b9a\u7f6a\u7684\u539f\u56e0\uff1f\u5982\u679c\u4ed6\u662f\u88ab\u8bec\u5b9a\u7f6a\u540d\uff0c\u88ab\u4eba\u52ab\u8d70\u53cd\u800c\u662f\u4e00\u4ef6\u597d\u4e8b\uff0c\u5426\u5219\u5c82\u4e0d\u6789\u9001\u4e00\u6761\u4eba \u547d\uff1f\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u70b9\u70b9\u5934\uff0c\u8f6c\u95ee\u5c39\u77e5\u5e9c\u9053\uff1a\u201c\u90a3\u4e2a\u53eb\u6797\u5bb6\u58f0\u7684\u4e3a\u4f55\u88ab\u5b9a\u4e3a\u6b7b\u7f6a\uff1f\u4f60\u5148\u5728\u738b\u5927\u4eba\u548c\u672c \u629a\u9762\u524d\u8bf4\u660e\u767d\uff01\u201d \u3000\u3000\u6b64\u523b\u5c39\u77e5\u5e9c\u65e9\u5df2\u4e0d\u6562\u518d\u5750\uff0c\u81ea\u52a8\u8d77\u8eab\u606d\u7acb\u5782\u624b\u9053\uff1a\u201c\u8fd9\u4eba\u79c1\u901a\u76d7\u532a\uff0c\u56fe\u8c0b\u4e0d\u8f68\uff0c\u6309\u5f8b\u5e94 \u5f53\u5b9a\u4e3a\u6b7b\u5211\u3002\u201d \u3000\u3000\u201c\u53ef\u6709\u8bc1\u636e\uff1f\u201d \u3000\u3000\u5c39\u77e5\u5e9c\u6253\u4e86\u4e2a\u54c6\u55e6\u9053\uff1a\u201c\u5351\u804c\u65ad\u6848\uff0c\u8bc1\u636e\u786e\u51ff\uff0c\u5211\u6848\u8584\u5185\u90fd\u6709\u8be6\u7ec6\u8bb0\u8f7d\uff0c\u800c\u4e14\u6709\u90a3\u6b7b\u56da \u7684\u4eb2\u7b14\u62db\u4f9b\u5212\u62bc\uff0c\u5982\u679c\u5927\u4eba\u8981\u67e5\u660e\u8be6\u7ec6\u6848\u60c5\uff0c\u5351\u804c\u56de\u8859\u540e\u5c31\u4eb2\u81ea\u5c06\u5211\u6848\u6587\u728a\u9001\u5230\u629a\u7f72\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u51b7\u7b11\u9053\uff1a\u201c\u4e0d\u5fc5\u4e86\uff0c\u5c39\u77e5\u5e9c\uff01\u73b0\u5728\u5df2\u7ecf\u6709\u4eba\u628a\u4f60\u544a\u4e0b\u6765\u4e86\uff01\u201d \u3000\u3000\u5c39\u77e5\u5e9c\u8138\u8272\u53c8\u662f\u4e00\u53d8\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u4eba\u8981\u544a\u5351\u804c\uff1f\u201d \u3000\u3000\u201c\u544a\u4f60\u7684\u4eba\uff0c\u4e5f\u8bb8\u662f\u4f60\u4e07\u4e07\u60f3\u4e0d\u5230\u7684\uff0c\u8fde\u672c\u629a\u4e5f\u5927\u611f\u610f\u5916\u3002\u201d \u3000\u3000\u201c\u5927\u4eba\u6700\u597d\u80fd\u8bf4\u51fa\u662f\u8c01\uff1f\u5982\u679c\u80fd\u628a\u8fd9\u4eba\u627e\u6765\uff0c\u90a3\u5c31\u66f4\u597d\u4e86\u3002\u201d \u3000\u3000\u201c\u672c\u629a\u53ea\u6015\u4f60\u89c1\u4e86\u8fd9\u4eba\uff0c\u4f1a\u65e0\u5730\u81ea\u5bb9\u5427\uff01\u201d \u3000\u3000\u201c\u5927\u4eba\u81ea\u7ba1\u628a\u8fd9\u4eba\u53eb\u6765\uff0c\u5351\u804c\u81ea\u4fe1\u4e3a\u5b98\u6e05\u6b63\uff0c\u7edd\u4e0d\u6015\u88ab\u4eba\u8beco\u2019\u2019rpo\u201d\u597d\uff0c\u4f60\u65e2\u7136\u6267 \u610f\u8981\u89c1\u8fd9\u4eba\uff0c\u672c\u629a\u4e5f\u53ea\u6709\u628a\u5979\u558a\u6765\u4e86\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u8bf4\u7740\uff0c\u671b\u4e86\u738b\u5f64\u8eab\u540e\u7684\u71d5\u98de\u4e00\u773c\uff1a\u201c\u5c31\u8bf7\u71d5\u4f8d\u536b\u628a\u544a\u72b6\u4eba\u5e26\u5230\u8fd9\u91cc\u6765\uff01\u201d \u3000\u3000\u71d5\u98de\u5e94\u4e86\u4e00\u58f0\uff0c\u5f88\u5feb\u4fbf\u628a\u5b59\u5c0f\u51e4\u5e26\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5b59\u5c0f\u51e4\u4f3c\u4e4e\u4e0d\u6562\u770b\u5c39\u77e5\u5e9c\uff0c\u51fa\u6765\u540e\u4f4e\u7740\u5934\u4fbf\u8dea\u5728\u9a6c\u6587\u4e2d\u9762\u524d\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0c\u5c39\u77e5\u5e9c\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u775b\uff0c\u5fc3\u7231\u7684\u5ba0\u59be\u5c45\u7136\u5728\u62c6\u53f0\u5927\u4eba\u9762\u524d\u628a\u81ea\u5df1\u544a \u4e86\u4e00\u72b6\uff0c\u8fd9\u662f\u4ed6\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u7684\u4e8b\u3002 \u3000\u3000\u4ed6\u6014\u4e86\u534a\u664c\uff0c\u624d\u5446\u5446\u7684\u671b\u7740\u5b59\u5c0f\u51e4\u9053\uff1a\u201c\u771f\u7684\u662f\u4f60\u5728\u629a\u53f0\u5927\u4eba\u9762\u524d\u628a\u6211\u544a\u4e0b\u4e86\u5417\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u51e4\u4ecd\u4e0d\u6562\u770b\u5c39\u77e5\u5e9c\uff0c\u4f4e\u7740\u5934\u9053\uff1a\u201c\u662f\u7684\uff0c\u8001\u7237\u89c9\u5f97\u5f88\u610f\u5916\u5427\uff01\u201d \u3000\u3000\u5c39\u77e5\u5e9c\u5f20\u53e3\u7ed3\u820c\u4e86\u4e00\u9635\u624d\u9053\uff1a\u201c\u5c0f\u51e4\uff0c\u6211\u54ea\u4e00\u70b9\u5bf9\u4e0d\u4f4f\u4f60\uff0c\u4f60\u7adf\u505a\u51fa\u8fd9\u79cd\u4e8b\u6765\uff01\u201d \u3000\u3000\u5b59\u5c0f\u51e4\u5e7d\u5e7d\u7684\u9053\uff1a\u201c\u8001\u7237\u7684\u786e\u5f85\u6211\u5f88\u597d\uff0c\u4f46\u4e3a\u4e86\u8ba8\u56de\u516c\u9053\uff0c\u6211\u5374\u4e0d\u80fd\u4e0d\u544a\u4f60\uff0c\u56e0\u4e3a\u4f60\u7684 \u6240\u4f5c\u6240\u4e3a\u592a\u6ca1\u4eba\u6027\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u7adf\u6562\u62ff\u2018\u6ca1\u4eba\u6027\u2019\u8fd9\u4e09\u4e2a\u5b57\u6765\u9a82\u6211\uff0c\u6211\u505a\u9519\u4e86\u4ec0\u4e48\u4e8b\uff1f\u4f60\u8bf4\uff01\u201d \u3000\u3000\u201c\u4f60\u505a\u7684\u574f\u4e8b\u592a\u591a\u4e86\uff0c\u4f60\u660e\u77e5\u6211\u662f\u5df2\u7ecf\u548c\u4eba\u8ba2\u8fc7\u5a5a\u7684\u4eba\uff0c\u5374\u504f\u8981\u7528\u6743\u52bf\u628a\u6211\u5f3a\u9738\u4f5c\u59be\uff0c \u5c31\u51ed\u8fd9\u4ef6\u4e8b\uff0c\u4f60\u7684\u5929\u826f\u4f55\u5728\uff1f\u201d \u3000\u3000\u201c\u5c82\u6709\u6b64\u7406\uff0c\u6211\u662f\u7528\u4e00\u767e\u4e24\u94f6\u5b50\u628a\u4f60\u8ba8\u8fc7\u6765\u7684\uff0c\u4f60\u7684\u7236\u6bcd\u5b8c\u5168\u540c\u610f\uff0c\u600e\u4e48\u8bf4\u662f\u6211\u5f3a\u9738 \u4f60\uff1f\u201d \u3000\u3000\u201c\u4f60\u7528\u90a3\u79cd\u5351\u9119\u624b\u6bb5\u80c1\u8feb\u6211\u7239\u5a18\uff0c\u6211\u7239\u5a18\u6562\u4e0d\u540c\u610f\u5417\uff1f\u4f60\u4e0d\u59a8\u73b0\u5728\u6d3e\u4eba\u628a\u4ed6\u4eec\u4e24\u4f4d\u8001\u4eba \u5bb6\u627e\u6765\uff0c\u770b\u4ed6\u4eec\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u201c\u5c0f\u51e4\uff0c\u4f60\u2026\u2026\u4f60\u592a\u6ca1\u826f\u5fc3\u4e86\uff01\u201d \u3000\u3000\u201c\u7a76\u7adf\u662f\u8c01\u6ca1\u826f\u5fc3\uff0c\u5f53\u7740\u629a\u53f0\u5927\u4eba\u548c\u4eac\u57ce\u738b\u5927\u4eba\u9762\u524d\uff0c\u5f88\u5feb\u5c31\u4f1a\u5f04\u6e05\u695a\uff0c\u73b0\u5728\u8fd9\u91cc\u662f\u6709 \u738b\u6cd5\u7684\u6240\u5728\uff0c\u6211\u5df2\u7528\u4e0d\u7740\u518d\u6015\u4f60\u4e86\uff0c\u4f60\u4e5f\u522b\u518d\u62ff\u8001\u7237\u7684\u8eab\u4efd\u671f\u538b\u6211\u8fd9\u5f31\u5973\u5b50\u3002\u201d \u3000\u3000\u201c\u597d\u554a\uff01\u4f60\u73b0\u5728\u5df2\u7ecf\u6b3a\u8d1f\u5230\u6211\u5934\u4e0a\u6765\u4e86\uff0c\u8fd8\u8bf4\u81ea\u5df1\u662f\u5f31\u5973\u5b50\uff01 \u3000\u3000\u5c0f\u51e4\uff0c\u4f60\u5728\u629a\u53f0\u5927\u4eba\u9762\u524d\u544a\u5b98\uff0c\u544a\u7684\u53c8\u662f\u81ea\u5df1\u7684\u4e08\u592b\uff0c\u8be5\u662f\u4e2a\u4ec0\u4e48\u7f6a\u540d\uff0c\u5e94\u8be5\u5fc3\u91cc\u6709\u6570 \u624d\u5bf9\u3002\u201d \u3000\u3000\u5b59\u5c0f\u51e4\u679c\u7136\u5413\u7684\u534a\u664c\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u9a6c\u6587\u4e2d\u4e3a\u8ba8\u597d\u738b\u5f64\uff0c\u53ea\u597d\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u5c39\u77e5\u5e9c\uff0c\u5f53\u7740\u672c\u629a\u7684\u9762\u8fd8\u6562\u6b3a\u8d1f\u5979\uff01\u73b0\u5728\u662f\u672c \u629a\u5728\u95ee\u8bdd\uff0c\u4e00\u5207\u53ea\u6709\u672c\u629a\u5904\u65ad\uff0c\u4f60\u6ca1\u8d44\u683c\u8bad\u65a5\u5979\u3002\u201d \u3000\u3000\u5c39\u77e5\u5e9c\u4f4e\u4e0b\u4e86\u5934\u3002 \u3000\u3000\u9a6c\u6587\u4e2d\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u5c39\u77e5\u5e9c\uff0c\u4f60\u53ef\u77e5\u9053\u90a3\u4e2a\u53eb\u6797\u5bb6\u58f0\u7684\u662f\u5b59\u5c0f\u51e4\u7684\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5c39\u77e5\u5e9c\u731b\u6447\u5934\u9053\uff1a\u201c\u5351\u804c\u53ea\u77e5\u4ed6\u662f\u4e2a\u79c1\u901a\u76d7\u532a\u3001\u56fe\u8c0b\u4e0d\u8f68\u7684\u8981\u72af\uff0c\u96be\u9053\u4ed6\u548c\u5351\u804c\u7684\u8d31\u59be \u4e5f\u6709\u5173\u7cfb\u4e0d\u6210\uff1f\u679c\u771f\u5982\u6b64\uff0c\u8fd9\u8d31\u4eba\u8bec\u544a\u5351\u804c\uff0c\u90a3\u5c31\u96be\u602a\u4e86\u3002\u201d \u3000\u3000\u201c\u672c\u629a\u4e0d\u76f8\u4fe1\u4f60\u4e0d\u77e5\u9053\u5b59\u5c0f\u51e4\u548c\u90a3\u9003\u72af\u6797\u5bb6\u58f0\u7684\u5173\u7cfb\uff01\u201d \u3000\u3000\u201c\u4ed6\u4eec\u7a76\u7adf\u662f\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d \u3000\u3000\u201c\u4f60\u65e2\u7136\u4e0d\u80af\u627f\u8ba4\uff0c\u672c\u629a\u4e5f\u53ea\u6709\u4ee3\u8bf4\u4e86\uff0c\u4ed6\u4eec\u662f\u4e00\u5bf9\u672a\u5a5a\u592b\u5987\u3002 \u3000\u3000\u5c39\u77e5\u5e9c\uff0c\u4f60\u662f\u4e2a\u806a\u660e\u4eba\uff0c\u4e3a\u4ec0\u4e48\u8981\u505a\u7cca\u6d82\u4e8b\uff1f\u7eb3\u59bb\u8ba8\u59be\u7b97\u4e0d\u4e86\u4ec0\u4e48\uff0c\u4f46\u5f53\u521d\u5fc5\u987b\u6253\u542c\u6e05 \u695a\uff0c\u5bf9\u5417\uff1f\u201d \u3000\u3000\u201c\u5351\u804c\u5f53\u521d\u5b9e\u5728\u4e0d\u6e05\u695a\u8fd9\u4ef6\u4e8b\uff0c\u5426\u5219\u2026\u2026\u201d \u3000\u3000\u201c\u7528\u4e0d\u7740\u5426\u5219\u4e0d\u5426\u5219\u4e86\uff01\u8fd8\u6709\uff0c\u4f60\u628a\u6797\u5bb6\u58f0\u5b9a\u6210\u6b7b\u7f6a\uff0c\u7eb5\u7136\u5ba1\u7406\u516c\u6b63\uff0c\u5728\u5916\u4eba\u770b\u6765\uff0c\u5374 \u96be\u514d\u6709\u79c1\u5fc3\u4e4b\u5acc\u3002\u5c39\u77e5\u5e9c\uff0c\u8fd9\u53c8\u662f\u4f60\u6709\u6b20\u806a\u660e\u4e4b\u5904\u4e86\uff0c\u4f60\u5728\u5916\u670d\u5b98\u5341\u51e0\u5e74\uff0c\u770b\u6765\u662f\u8d8a\u5e72\u8d8a\u56de \u53bb\u4e86\u3002\u201d \u3000\u3000\u5c39\u77e5\u5e9c\u81ea\u7136\u542c\u5f97\u51fa\uff0c\u9a6c\u6587\u4e2d\u7684\u8fd9\u756a\u8bdd\u662f\u6709\u610f\u5bf9\u4ed6\u8892\u62a4\u3002 \u3000\u3000\u4ed6\u8eac\u8eab\u5782\u9996\u9053\uff1a\u201c\u5351\u804c\u5931\u5bdf\uff0c\u9669\u4e9b\u94f8\u6210\u5927\u9519\u3002\u597d\u5728\u6797\u5bb6\u58f0\u6b7b\u7f6a\u5c1a\u672a\u884c\u5211\uff0c\u5f85\u901a\u7f09\u5f52\u6848 \u540e\uff0c\u5351\u804c\u81ea\u5f53\u914c\u60c5\u4e3a\u4ed6\u51cf\u8f7b\u7f6a\u540d\u3002\u201d \u3000\u3000\u5750\u5728\u4e00\u65c1\u7684\u738b\u5f64\u3002\u542c\u5230\u8fd9\u91cc\uff0c\u5b9e\u5728\u65e0\u6cd5\u518d\u9690\u5fcd\u4e0b\u53bb\uff0c\u542c\u9a6c\u6587\u4e2d\u7684\u8bed\u6c14\u3002\u5206\u660e\u662f\u6709\u610f\u5f00\u8131 \u5c39\u77e5\u5e9c\uff0c\u6765\u4e2a\u5927\u4e8b\u5316\u5c0f\uff0c\u5c0f\u4e8b\u5316\u65e0\uff1b\u82e5\u4efb\u7531\u9a6c\u6587\u4e2d\u5982\u6b64\u5ba1\u7406\u4e0b\u53bb\uff0c\u81ea\u5df1\u52bf\u5c06\u5bf9\u6c5f\u5343\u91cc\u65e0\u6cd5\u4ea4 \u4ee3\u3002 \u3000\u3000\u5c24\u5176\uff0c\u5982\u679c\u5c39\u77e5\u5e9c\u4ecd\u7a33\u5750\u5f00\u5c01\uff0c\u6797\u5bb6\u58f0\u548c\u5b59\u5c0f\u51e4\u52bf\u5fc5\u4ecd\u88ab\u62c6\u6563\uff0c\u800c\u4e14\u5c39\u77e5\u5e9c\u4e5f\u7edd\u653e\u4e0d\u8fc7 \u6797\u5bb6\u58f0\u3002 \u3000\u3000\u738b\u5f64\u5728\u60c5\u6025\u4e4b\u4e0b\uff0c\u51b3\u5b9a\u4e0d\u987e\u4e00\u5207\u7684\u4f7f\u51fa\u6740\u624b\u94dc\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u4e0d\u52a8\u58f0\u8272\u7684\u8f6c\u5934\u671b\u5411\u9a6c\u6587\u4e2d\u9053\uff1a\u201c\u9a6c\u5927\u4eba\uff0c\u5c39\u77e5\u5e9c\u628a\u6797\u5bb6\u58f0\u5b9a\u4e3a\u6b7b\u7f6a\u7684\u8fd9\u4e00\u6848\uff0c \u5fc5\u987b\u5c31\u5730\u5ba1\u7406\uff0c\u82e5\u6797\u5bb6\u58f0\u679c\u771f\u79c1\u901a\u76d7\u532a\u3001\u56fe\u8c0b\u4e0d\u8f68\uff0c\u90a3\u662f\u6b7b\u6709\u4f59\u8f9c\uff1b\u5426\u5219\uff0c\u5c31\u662f\u5c39\u77e5\u5e9c\u84c4\u610f \u9677\u5165\u4eba\u7f6a\uff0c\u6309\u5f8b\u5fc5\u987b\u9769\u804c\u67e5\u529e\uff0c\u4ee5\u6b63\u56fd\u6cd5\u3002\u201d \u3000\u3000\u738b\u5f64\u7684\u8fd9\u51e0\u53e5\u8bdd\uff0c\u53ea\u542c\u5f97\u5c39\u77e5\u5e9c\u51b7\u6c57\u76f4\u5192\u3002 \u3000\u3000\u4ed6\u5b9e\u5728\u60f3\u4e0d\u900f\uff0c\u8eab\u4e3a\u5927\u5185\u4f8d\u536b\u7edf\u9886\u7684\u738b\u5f64\uff0c\u4e3a\u4f55\u7adf\u4f1a\u8ddf\u81ea\u5df1\u8fc7\u4e0d\u53bb\uff1f\u83ab\u975e\u4ed6\u56e0\u6765\u5230\u5f00 \u5c01\uff0c\u81ea\u5df1\u4e0d\u66fe\u524d\u6765\u5ba2\u6808\u62dc\u89c1\uff1f\u8fd8\u662f\u4e3a\u4e86\u81ea\u5df1\u4e0d\u66fe\u732e\u4e0a\u539a\u793c\uff1f \u5367\u9f99\u751f\u300a\u71d5\u5b50\u4f20\u5947\u300b \u7b2c\u4e8c\u5341\u4e09\u56de\u3000\u610f\u5916\u53d1\u73b0 \u3000\u3000\u9a6c\u6587\u4e2d\u5e72\u54b3\u4e86\u4e24\u58f0\u9053\uff1a\u201c\u8fd9\u4e8b\u4e0b\u5b98\u5f53\u7136\u4f1a\u5ba1\u7406\u6e05\u695a\uff0c\u5c39\u77e5\u5e9c\u5148\u524d\u8bf4\u8fc7\u4e86\u6709\u5173\u5f53\u65e5\u95ee\u6848\u7684 \u7ecf\u8fc7\uff0c\u5728\u5211\u6848\u7b14\u5f55\u4e0a\u90fd\u6709\u8be6\u7ec6\u8bb0\u8f7d\uff0c\u7b49\u4e0b\u5b98\u56de\u5230\u629a\u7f72\u540e\uff0c\u7b2c\u4e00\u4ef6\u4e8b\u4fbf\u662f\u5148\u5ba1\u9605\u5f00\u5c01\u5e9c\u9001\u6765\u7684 \u5211\u6848\u7b14\u5f55\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u987f\u4e86\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u5f53\u7136\uff0c\u6700\u597d\u7684\u529e\u6cd5\u662f\u4e24\u4eba\u5f53\u5802\u5bf9\u8d28\uff0c\u53ef\u60dc\u7684\u662f\u90a3\u540d\u5acc\u72af\u6797\u5bb6 \u58f0\u5df2\u88ab\u4eba\u52ab\u8d70\uff0c\u5b9e\u5728\u662f\u4ef6\u61be\u4e8b\u3002\u201d \u3000\u3000\u738b\u5f64\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u5982\u679c\u738b\u67d0\u73b0\u5728\u80fd\u8bbe\u6cd5\u5c06\u5acc\u72af\u6797\u5bb6\u58f0\u627e\u5230\uff0c\u9a6c\u5927\u4eba\u5fc5\u5b9a\u5c31\u53ef\u5f53\u573a\u5ba1\u7406 \u7ed3\u6848\u4e86\uff1f\u201d \u3000\u3000\u201c\u90a3\u662f\u81ea\u7136\uff0c\u504f\u504f\u6b64\u523b\u8c01\u4e5f\u65e0\u6cd5\u5c06\u5acc\u72af\u6797\u5bb6\u58f0\u627e\u56de\u6765\uff01\u201d \u3000\u3000\u201c\u5982\u679c\u738b\u67d0\u6709\u529e\u6cd5\u627e\u5230\u5462\uff1f\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u4e0d\u89c9\u4e00\u5446\u9053\uff1a\u201c\u738b\u5927\u4eba\u8fd9\u8bdd\u2026\u2026\u201d \u3000\u3000\u738b\u5f64\u6574\u4e86\u6574\u8138\u8272\u9053\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u90a3\u4e2a\u53eb\u6797\u5bb6\u58f0\u7684\u5e74\u8f7b\u4eba\uff0c\u6b64\u523b\u6b63\u5728\u738b\u67d0\u8fd9\u91cc\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u5927\u611f\u610f\u5916\u4e4b\u4e0b\uff0c\u5185\u5fc3\u95ea\u7535\u822c\u6253\u7740\u8f6c\uff0c\u6697\u5fd6\uff1a\u597d\u4e00\u4e2a\u738b\u5f64\uff0c\u8eab\u4e3a\u5927\u5185\u4f8d\u536b\u7edf\u9886\uff0c\u5c45 \u7136\u505a\u51fa\u52ab\u72f1\u7684\u52fe\u5f53\uff0c\u6709\u4e86\u8fd9\u4e2a\u628a\u67c4\uff0c\u6211\u8fd8\u6015\u4ed6\u4ec0\u4e48\uff1f\u82e5\u6211\u628a\u8fd9\u4e8b\u544a\u5230\u671d\u5ef7\uff0c\u5012\u9709\u7684\u53cd\u800c\u662f\u4ed6 \u4e86\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u4e0d\u4fbf\u9a6c\u4e0a\u7ffb\u8138\u53cd\u95ee\uff0c\u53ea\u80fd\u5e26\u4e9b\u5927\u60d1\u4e0d\u89e3\u7684\u9053\uff1a\u201c\u5acc\u72af\u6797\u5bb6\u58f0\u600e\u4f1a\u843d\u5230\u738b\u5927\u4eba\u624b\u91cc \u5462\uff1f\u201d \u3000\u3000\u738b\u5f64\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u662f\u6709\u4eba\u628a\u4ed6\u9001\u5230\u738b\u67d0\u624b\u4e0a\u7684\u3002\u201d \u3000\u3000\u201c\u96be\u9053\u662f\u52ab\u72f1\u7684\u4eba\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\uff0c\u738b\u5927\u4eba\u5fc5\u5b9a\u5df2\u628a\u52ab\u72f1\u7684\u4eba\u6349\u5230\u4e86\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u52ab\u72f1\u8005\u6309\u5f8b\u5f53\u5904\u6b7b\u5211\uff0c\u5982\u6b64\u91cd\u72af\uff0c\u738b\u5927\u4eba\u4e3a\u4f55\u5c06\u4ed6\u4eec\u653e\u8fc7\uff1f\u201d \u3000\u3000\u201c\u52ab\u72f1\u7684\u4eba\u5c06\u6797\u5bb6\u58f0\u9001\u5230\u5ba2\u6808\u95e8\u53e3\uff0c\u8981\u4ed6\u8fdb\u6765\u5411\u738b\u67d0\u9e23\u51a4\uff0c\u5f53\u738b\u67d0\u89c1\u5230\u6797\u5bb6\u58f0\u7684\u65f6\u5019\uff0c \u52ab\u72f1\u8005\u65e9\u5df2\u4e0d\u77e5\u53bb\u5411\u3002\u9a6c\u5927\u4eba\uff0c\u4f60\u73b0\u5728\u8be5\u4ee5\u5ba1\u7406\u6848\u60c5\u4e3a\u91cd\uff0c\u4e3a\u4ec0\u4e48\u5374\u628a\u5fc3\u601d\u653e\u5728\u52ab\u72f1\u8005\u8eab \u4e0a\uff1f\u201d \u3000\u3000\u201c\u738b\u5927\u4eba\u8fd9\u8bdd\uff0c\u4e0b\u5b98\u6709\u4e9b\u4e0d\u61c2\uff1f\u201d \u3000\u3000\u201c\u9a6c\u5927\u4eba\uff0c\u4f60\u4e00\u5b9a\u542c\u8bf4\u8fc7\u2018\u5b98\u903c\u6c11\u53cd\u2019\u8fd9\u53e5\u4fd7\u8bdd\u5427\uff1f\u201d \u3000\u3000\u201c\u738b\u5927\u4eba\u4e3a\u4f55\u8981\u95ee\u8d77\u8fd9\u4e2a\u6765\uff1f\u201d \u3000\u3000\u201c\u9a6c\u5927\u4eba\u5f85\u4f1a\u513f\u53ea\u7ba1\u5ba1\u7406\u6848\u60c5\uff0c\u82e5\u6797\u5bb6\u58f0\u786e\u8ba4\u5728\u5c39\u77e5\u5e9c\u4e3a\u4ed6\u5b9a\u4e0b\u7684\u7f6a\u540d\uff0c\u738b\u67d0\u4e00\u5b9a\u8d1f\u8d23 \u628a\u52ab\u72f1\u8005\u7f09\u6355\u5f52\u6848\uff0c\u9001\u5230\u8d35\u8859\u53d1\u843d\u3002\u5426\u5219\uff0c\u5982\u679c\u662f\u5c39\u77e5\u5e9c\u84c4\u610f\u9677\u6797\u5bb6\u58f0\u5165\u7f6a\uff0c\u5c48\u6253\u6210\u62db\uff0c\u6709 \u4eba\u628a\u6797\u5bb6\u58f0\u4ece\u72f1\u4e2d\u6551\u51fa\uff0c\u6b63\u662f\u505a\u4e86\u4e00\u4ef6\u597d\u4e8b\uff0c\u53c8\u6709\u4f55\u4e0d\u5bf9\u5462\uff1f\u6240\u8c13\u2018\u5b98\u903c\u6c11\u53cd\u2019\uff0c\u5c82\u4e0d\u663e\u800c \u6613\u89c1\u5417\uff1f\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u7acb\u523b\u88ab\u9a73\u5f97\u54d1\u53e3\u65e0\u8a00\uff0c\u4ed6\u539f\u4ee5\u4e3a\u6709\u4e86\u628a\u67c4\uff0c\u73b0\u53c8\u53d8\u6210\u65e0\u67c4\u53ef\u628a\u4e86\u3002 \u3000\u3000\u738b\u5f64\u7acb\u523b\u5429\u5490\u8eab\u540e\u7684\u71d5\u98de\u9053\uff1a\u201c\u628a\u90a3\u53eb\u6797\u5bb6\u58f0\u7684\u5e74\u8f7b\u4eba\u5e26\u5230\u8fd9\u91cc\u6765\uff0c\u8981\u4ed6\u4e0d\u5fc5\u5bb3\u6015\uff0c\u4e00 \u5207\u5168\u7531\u9a6c\u5927\u4eba\u548c\u6211\u66ff\u4ed6\u505a\u4e3b\u3002\u201d \u3000\u3000\u5728\u8bdd\u4e2d\u5e26\u4e0a\u4e86\u9a6c\u6587\u4e2d\uff0c\u7b49\u4e8e\u5df2\u660e\u544a\u9a6c\u6587\u4e2d\uff0c\u5fc5\u987b\u79c9\u516c\u5ba1\u7406\u3002 \u3000\u3000\u5728\u8fd9\u79cd\u60c5\u5f62\u4e0b\uff0c\u9a6c\u6587\u4e2d\u6839\u672c\u5df2\u65e0\u518d\u504f\u8892\u5c39\u77e5\u5e9c\u7684\u4f59\u5730\u3002 \u3000\u3000\u4ed6\u662f\u806a\u660e\u4eba\uff0c\u4e3a\u4e2a\u4eba\u524d\u7a0b\u8ba1\uff0c\u727a\u7272\u4e00\u4e2a\u5c39\u77e5\u5e9c\uff0c\u53c8\u7b97\u5f97\u4e86\u4ec0\u4e48\uff1f\u5927\u7ea6\u76cf\u8336\u5de5\u592b\u4e4b\u540e\uff0c\u71d5 \u98de\u4fbf\u5e26\u7740\u6797\u5bb6\u58f0\u8fdb\u5165\u5ba2\u5385\u3002 \u3000\u3000\u672c\u6765\uff0c\u6797\u5bb6\u58f0\u7531\u623f\u95f4\u5230\u5ba2\u5385\uff0c\u7acb\u523b\u5c31\u53ef\u524d\u6765\uff0c\u803d\u8bef\u4e86\u8fd9\u4e48\u4e45\u7684\u65f6\u95f4\u4e0d\u5916\u662f\u71d5\u98de\u4ea4\u4ee3\u4e86\u4ed6 \u4e00\u4e9b\u89c1\u4e86\u9a6c\u6587\u4e2d\u8be5\u5982\u4f55\u5e94\u5bf9\u7684\u8bdd\uff0c\u540c\u65f6\u4e5f\u8981\u4ed6\u4e0d\u5fc5\u5bb3\u6015\u3002 \u3000\u3000\u6797\u5bb6\u58f0\u4e00\u8fdb\u95e8\uff0c\u672c\u6765\u5148\u8981\u5411\u738b\u5f64\u884c\u793c\uff0c\u738b\u5f64\u5374\u5148\u884c\u6307\u7740\u9a6c\u6587\u4e2d\u9053\uff1a\u201c\u8fd9\u4f4d\u5c31\u662f\u629a\u53f0\u9a6c\u5927 \u4eba\uff0c\u5feb\u5feb\u5148\u89c1\u8fc7\u9a6c\u5927\u4eba\uff01\u201d \u3000\u3000\u6797\u5bb6\u58f0\u7acb\u5373\u53cc\u819d\u8dea\u5012\uff0c\u9053\uff1a\u201c\u5c0f\u6c11\u6797\u5bb6\u58f0\u7ed9\u9a6c\u5927\u4eba\u78d5\u5934\uff01\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u7acb\u523b\u4e5f\u4e00\u6307\u738b\u5f64\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u4eac\u57ce\u6765\u7684\u738b\u5927\u4eba\uff0c\u4f60\u8be5\u5148\u7ed9\u738b\u5927\u4eba\u78d5\u5934\u3002\u201d \u3000\u3000\u6797\u5bb6\u58f0\u53c8\u8fde\u5fd9\u8f6c\u5411\u738b\u5f64\u53e9\u5934\u3002 \u3000\u3000\u738b\u5f64\u7b11\u9053\uff1a\u201c\u9a6c\u5927\u4eba\uff0c\u4ed6\u65e9\u5df2\u5c31\u89c1\u8fc7\u738b\u67d0\u4e86\uff0c\u7528\u4e0d\u7740\u518d\u6765\u4ecb\u7ecd\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u4e8e\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u738b\u5927\u4eba\u662f\u5949\u7687\u4e0a\u4e4b\u547d\u6765\u5230\u5f00\u5c01\uff0c\u7406\u5e94\u5982\u6b64\uff0c\u4e0b\u5b98\u672c\u6765\u4e0d\u8be5\u5148 \u884c\u53d7\u793c\u3002\u201d \u3000\u3000\u201c\u9a6c\u5927\u4eba\u8bf4\u54ea\u91cc\u8bdd\uff0c\u73b0\u5728\u95ee\u6848\u7684\u662f\u4f60\uff0c\u738b\u67d0\u8fde\u966a\u5ba1\u7684\u8d44\u683c\u90fd\u6ca1\u6709\uff0c\u6700\u591a\u4e0d\u8fc7\u5728\u4e00\u65c1\u53c2\u89c2 \u800c\u5df2\u3002\u201d \u3000\u3000\u201c\u82e5\u738b\u5927\u4eba\u8fd9\u4e48\u8bf4\u8bdd\uff0c\u90a3\u5c31\u592a\u89c1\u5916\u4e86\uff0c\u4f60\u662f\u94a6\u5dee\u4e13\u4f7f\uff0c\u4e0b\u5b98\u7406\u5e94\u542c\u547d\u88c1\u593a\u3002\u201d \u3000\u3000\u201c\u9a6c\u5927\u4eba\u7528\u4e0d\u7740\u5ba2\u6c14\uff0c\u65e2\u7136\u4e24\u9020\u5230\u9f50\uff0c\u5c31\u8be5\u9a6c\u4e0a\u5ba1\u7406\u624d\u662f\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u65e2\u7136\u5df2\u51b3\u5b9a\u4e0d\u518d\u4fdd\u5168\u5c39\u77e5\u5e9c\uff0c\u5df2\u7528\u4e0d\u7740\u518d\u987e\u8651\u4ec0\u4e48\uff0c\u5fc3\u60c5\u53cd\u800c\u8f7b\u677e\u5f88\u591a\u3002 \u3000\u3000\u5f53\u4e0b\u3002\u51b7\u7b11\u4e86\u51e0\u58f0\u9053\uff1a\u201c\u5c39\u77e5\u5e9c\uff0c\u672c\u629a\u5728\u6ca1\u89c1\u6797\u5bb6\u58f0\u4e4b\u524d\uff0c\u603b\u4ee5\u4e3a\u4f60\u5ba1\u7406\u8fd9\u4ef6\u6848\u5b50\u4e0d\u81f4 \u6709\u9519\uff0c\u521a\u624d\u5dee\u4e00\u70b9\u56e0\u800c\u5f97\u7f6a\u4e86\u738b\u5927\u4eba\uff0c\u73b0\u5728\u624d\u8bc1\u660e\u4f60\u679c\u7136\u662f\u79c1\u5fc3\u4f5c\u795f\uff0c\u8bec\u4eba\u4eba\u7f6a\uff0c\u7adf\u7136\u4ee5\u5c48 \u6253\u6210\u62db\u65b9\u5f0f\uff0c\u628a\u8fd9\u5e74\u8f7b\u4eba\u5224\u6210\u6b7b\u5211\uff0c\u4f60\u7684\u5929\u7406\u826f\u5fc3\u4f55\u5728\uff1f\u201d \u3000\u3000\u5c39\u77e5\u5e9c\u8fde\u6253\u51e0\u4e2a\u54c6\u55e6\uff0c\u4ed6\u4e07\u4e07\u6ca1\u60f3\u5230\u8fd9\u4f4d\u9876\u5934\u4e0a\u53f8\u5bf9\u81ea\u5df1\u7684\u6001\u5ea6\u4f1a\u53d8\u5316\u5f97\u5982\u6b64\u4e4b\u5feb\uff0c\u5413 \u5f97\u4ed6\u8fde\u5fd9\u4f0f\u8eab\u8dea\u5730\u3002 \u3000\u3000\u4ed6\u98a4\u7740\u58f0\u97f3\u9053\uff1a\u201c\u5351\u804c\u7a76\u7adf\u9519\u5728\u54ea\u91cc\uff1f\u8bf7\u5927\u4eba\u660e\u793a\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u4e24\u773c\u5706\u7741\uff0c\u62ac\u624b\u4e00\u62cd\u684c\u5b50\u9053\uff1a\u201c\u8fd9\u5e74\u8f7b\u4eba\u624d\u4e0d\u8fc7\u4e8c\u5341\u5de6\u53f3\uff0c\u4e00\u6d3e\u79c0\u6c14\uff0c\u6839\u672c\u4e0d\u53ef \u80fd\u662f\u4f5c\u5978\u72af\u79d1\u4e4b\u4eba\uff0c\u4f60\u8bf4\u4ed6\u79c1\u901a\u76d7\u532a\uff0c\u4ed6\u79c1\u901a\u7684\u662f\u90a3\u4e9b\u76d7\u532a\uff1f\u4ed6\u66fe\u505a\u8fc7\u4ec0\u4e48\u56fe\u8c0b\u4e0d\u8f68\u7684\u4e8b\uff1f \u5c39\u77e5\u5e9c\uff0c\u5b9a\u7f6a\u5fc5\u987b\u8981\u6709\u8bc1\u636e\uff0c\u65e0\u51ed\u65e0\u636e\uff0c\u5c31\u662f\u9677\u5165\u4eba\u7f6a\uff0c\u4f60\u8fd9\u6c11\u4e4b\u7236\u6bcd\uff0c\u5c31\u662f\u8fd9\u79cd\u505a\u5b98\u7684 \u5417\uff1f\u201d \u3000\u3000\u5c39\u77e5\u5e9c\u55eb\u5685\u7740\u9053\uff1a\u201c\u5f53\u65e5\u95ee\u6848\u7b14\u5f55\u4e0a\u90fd\u6709\u8be6\u7ec6\u8bb0\u8f7d\uff0c\u5927\u4eba\u770b\u8fc7\u540e\u81ea\u7136\u660e\u767d\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u53c8\u4e00\u62cd\u684c\u5b50\u9053\uff1a\u201c\u80e1\u8bf4\uff01\u6b32\u52a0\u4e4b\u7f6a\uff0c\u4f55\u60a3\u65e0\u8bcd\uff1f\u4f60\u4ee5\u4e3a\u90a3\u4e9b\u7b14\u5f55\u5c31\u80fd\u9a97\u8fc7\u672c\u629a \u5417\uff1f\u4f60\u662f\u5426\u80fd\u628a\u90a3\u4e9b\u76d7\u532a\u6349\u6765\u5bf9\u8bc1\uff1f\u662f\u5426\u80fd\u4e3e\u51fa\u8fd9\u5e74\u8f7b\u4eba\u56fe\u8c0b\u4e0d\u8f68\u7684\u5177\u4f53\u4e8b\u5b9e\uff1f\u201d \u3000\u3000\u201c\u5351\u804c\u2026\u2026\u201d \u3000\u3000\u201c\u7528\u4e0d\u7740\u518d\u5355\u804c\u4e86\uff0c\u5f53\u672c\u629a\u9769\u4e86\u4f60\u7684\u804c\u4e4b\u540e\uff0c\u4f60\u6839\u672c\u5c31\u65e0\u804c\u53ef\u5351\uff01\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u4e3a\u8868\u793a\u65ad\u6848\u5e76\u4e0d\u6b66\u65ad\uff0c\u76ee\u5149\u8f6c\u5411\u8dea\u5728\u5730\u4e0a\u7684\u6797\u5bb6\u58f0\u9053\uff1a\u201c\u4f60\u4eca\u5e74\u591a\u5927\u5e74\u7eaa\uff1f\u5728\u672a \u5165\u72f1\u524d\u5728\u5bb6\u505a\u4e9b\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u6797\u5bb6\u58f0\u5f53\u7740\u5c39\u77e5\u5e9c\u7684\u9762\uff0c\u672c\u6765\u662f\u80c6\u602f\uff0c\u6b64\u523b\u89c1\u629a\u53f0\u5927\u4eba\u6012\u65a5\u5c39\u77e5\u5e9c\uff0c\u81ea\u7136\u4e5f\u5c31\u4e0d\u518d\u5bb3\u6015 \u4e86\u3002 \u3000\u3000\u4ed6\u7acb\u5373\u5927\u58f0\u5e94\u9053\uff1a\u201c\u5c0f\u6c11\u4eca\u5e74\u4e8c\u5341\u5c81\uff0c\u5728\u5bb6\u91cc\u8015\u8bfb\u4e3a\u751f\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u70b9\u70b9\u5934\uff0c\u518d\u95ee\u9053\uff1a\u201c\u4f60\u53ef\u66fe\u51fa\u8fc7\u8fdc\u95e8\uff1f\u201d \u3000\u3000\u6797\u5bb6\u58f0\u9053\uff1a\u201c\u5c0f\u6c11\u5bb6\u4f4f\u5f00\u5c01\u4e1c\u90ca\u6797\u5bb6\u5e84\uff0c\u8fde\u8fdb\u5f00\u5c01\u57ce\u4e5f\u4e0d\u8fc7\u662f\u4e00\u4e24\u6b21\u800c\u5df2\uff0c\u5176\u4f59\u65f6\u95f4\u4ece \u6ca1\u79bb\u5f00\u6797\u5bb6\u5e84\u4e00\u6b65\u3002\u201d \u3000\u3000\u201c\u4f60\u7684\u7236\u6bcd\u662f\u5426\u5065\u5728\uff1f\u5bb6\u91cc\u8fd8\u6709\u4e9b\u4ec0\u4e48\u4eba\uff1f\u90fd\u505a\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u5c0f\u6c11\u5bb6\u4e2d\u53ea\u6709\u7236\u6bcd\u53cc\u4eb2\uff0c\u5c0f\u6c11\u662f\u72ec\u5b50\uff0c\u5c0f\u6c11\u7684\u7236\u4eb2\u4e00\u751f\u52a1\u519c\uff0c\u4e5f\u4ece\u672a\u51fa\u8fc7\u8fdc\u95e8\u3002\u201d \u3000\u3000\u201c\u5c39\u77e5\u5e9c\u8bf4\u4f60\u79c1\u901a\u76d7\u532a\u3001\u56fe\u8c0b\u4e0d\u8f68\uff0c\u4f60\u4e3a\u4ec0\u4e48\u62db\u8ba4\uff1f\u201d \u3000\u3000\u201c\u5c0f\u6c11\u53d7\u4e0d\u8fc7\uff0c\u53ea\u6709\u62db\u8ba4\u4e86\u3002\u201d \u3000\u3000\u201c\u5f53\u65e5\u90a3\u95ee\u6848\u7b14\u5f55\u4e0a\u8bb0\u8f7d\u7684\uff0c\u4f60\u90fd\u4ed4\u7ec6\u770b\u8fc7\u5417\uff1f\u201d \u3000\u3000\u201c\u5c0f\u6c11\u5f53\u65f6\u5df2\u88ab\u6253\u5f97\u6b7b\u53bb\u6d3b\u6765\uff0c\u5c39\u5927\u4eba\u53ea\u53eb\u5c0f\u6c11\u5212\u62bc\uff0c\u5e76\u4e0d\u51c6\u5c0f\u6c11\u89c2\u770b\u7b14\u5f55\uff0c\u800c \u4e14\u2026\u2026\u201d \u3000\u3000\u201c\u800c\u4e14\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5c0f\u6c11\u4ece\u672a\u8fdb\u8fc7\u8859\u95e8\uff0c\u66f4\u4ece\u672a\u6253\u8fc7\u5b98\u53f8\uff0c\u6839\u672c\u4e0d\u77e5\u9053\u95ee\u6848\u8fd8\u8981\u7b14\u5f55\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u8f6c\u8fc7\u5934\u6765\uff0c\u518d\u671b\u5411\u5c39\u77e5\u5e9c\uff0c\u9053\uff1a\u201c\u5c39\u77e5\u5e9c\uff01\u4f60\u4ed6\u8bfb\u8bd7\u4e66\uff0c\u5fc5\u5b9a\u77e5\u9053\u2018\u7231\u6c11\u5982\u5b50\u2019 \u8fd9\u53e5\u8bdd\u5427\uff1f\u201d \u3000\u3000\u5c39\u77e5\u5e9c\u80c6\u98a4\u7684\u53e9\u5934\u9053\uff1a\u201c\u5351\u804c\u4e3a\u5b98\u591a\u5e74\uff0c\u8fd9\u53e5\u8bdd\u5c82\u80fd\u4e0d\u77e5\uff1f\u201d \u3000\u3000\u201c\u4f60\u7684\u6240\u4f5c\u6240\u4e3a\uff0c\u80fd\u7b97\u7231\u6c11\u5982\u5b50\u5417\uff1f\u201d \u3000\u3000\u201c\u5351\u804c\u2026\u2026\u201d \u3000\u3000\u201c\u521a\u624d\u6797\u5bb6\u58f0\u8bf4\u8fc7\uff0c\u4ed6\u5bb6\u91cc\u53ea\u6709\u7236\u5b50\u6bcd\u5b50\u4e09\u4eba\uff0c\u5168\u5bb6\u52a1\u519c\u4e3a\u751f\uff0c\u4f60\u4e3a\u4e86\u4e00\u5df1\u4e4b\u79c1\uff0c\u5c45\u7136 \u8fde\u4eba\u5bb6\u7684\u72ec\u5b50\u4e5f\u8981\u5165\u7f6a\u9677\u4e3a\u6b7b\u5211\uff0c\u679c\u771f\u8fd9\u5e74\u8f7b\u4eba\u884c\u5211\u4e4b\u540e\uff0c\u5c82\u4e0d\u65ad\u9001\u4e86\u4ed6\u4eec\u6797\u5bb6\u7684\u540e\u4ee3\u9999 \u70df\uff1f\u4ed6\u7684\u7236\u6bcd\u4e0b\u534a\u8f88\u5b50\u53c8\u4f9d\u9760\u4f55\u4eba\uff1f\u4f60\u6789\u6740\u4e00\u4eba\uff0c\u7b49\u4e8e\u6789\u6740\u4ed6\u7684\u5168\u5bb6\u3002\u5c39\u77e5\u5e9c\uff0c\u6211\u4eec\u4e3a\u5b98\u7684 \u4eba\u662f\u8981\u4e0a\u62a5\u671d\u5ef7\u3001\u9020\u798f\u9ece\u6c11\uff0c\u800c\u4f60\u5374\u662f\u53cd\u5176\u9053\u800c\u884c\uff0c\u7a76\u7adf\u5929\u7406\u4f55\u5728\uff1f\u826f\u5fc3\u4f55\u5728\uff1f\u201d \u3000\u3000\u5c39\u77e5\u5e9c\u6b64\u523b\u65e9\u5df2\u5168\u8eab\u762b\u8f6f\u3002 \u3000\u3000\u4ed6\u4f0f\u5728\u5730\u4e0a\uff0c\u8bed\u4e0d\u6210\u8c03\u7684\u9053\uff1a\u201c\u5351\u804c\u2026\u2026\u77e5\u2026\u2026\u7f6a\u2026\u2026\u4e86\uff0c\u597d\u5728\u2026\u2026\u4ed6\u5df2\u88ab\u4eba\u2026\u2026\u6551 \u51fa\u2026\u2026\u5c1a\u672a\u94f8\u6210\u2026\u2026\u6210\u5927\u9519\uff0c\u5351\u804c\u2026\u2026\u4ece\u4eca\u540e\u2026\u2026\u4e00\u5b9a\u4f1a\u2026\u2026\u4f1a\u9075\u7167\u5927\u4eba\u2026\u2026\u5429\u5490\u2026\u2026\u4e0a\u62a5 \u671d\u5ef7\u2026\u2026\u9020\u798f\u767e\u59d3\u2026\u2026\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u51b7\u7b11\u9053\uff1a\u201c\u56fd\u6cd5\u68ee\u4e25\uff0c\u5929\u7406\u662d\u5f70\uff0c\u90a3\u91cc\u8fd8\u6709\u4f60\u7684\u2018\u4eca\u540e\u2019\uff0c\u4f60\u5df2\u7528\u4e0d\u7740\u518d\u56de\u5f00\u5c01 \u5e9c\u4e86\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u8f6c\u5934\u5429\u5490\u603b\u6355\u5934\u5f20\u4e0d\u7a7a\u9053\uff1a\u201c\u628a\u4ed6\u5e26\u5230\u629a\u7f72\uff0c\u6682\u884c\u9769\u804c\u542c\u53c2\u3002\u201d \u3000\u3000\u5f20\u4e0d\u7a7a\u4e0a\u524d\u4e00\u628a\u62c9\u8d77\u5c39\u77e5\u5e9c\u9053\uff1a\u201c\u5c39\u5927\u4eba\uff0c\u8bf7\u968f\u5f20\u67d0\u8d70\u5427\uff01\u201d \u3000\u3000\u5c39\u77e5\u5e9c\u54ea\u91cc\u8fd8\u80fd\u518d\u8bf4\u4ec0\u4e48\uff1f\u88ab\u5f20\u4e0d\u7a7a\u67b6\u8d77\u540e\uff0c\u811a\u4e0d\u70b9\u5730\u7684\u5c31\u51fa\u4e86\u5ba2\u5385\u3002 \u3000\u3000\u9a6c\u6587\u4e2d\u518d\u671b\u5411\u6797\u5bb6\u58f0\u9053\uff1a\u201c\u4f60\u548c\u5b59\u5c0f\u51e4\u90fd\u53ef\u4ee5\u56de\u5bb6\u4e86\uff0c\u5c06\u6765\u6210\u5a5a\u4e4b\u65e5\uff0c\u522b\u5fd8\u4e86\u8bf7\u672c\u629a\u559d \u4e00\u676f\uff01\u201d \u3000\u3000\u6797\u5bb6\u58f0\u5148\u5411\u9a6c\u6587\u4e2d\u53e9\u8c22\uff0c\u7136\u540e\u518d\u5411\u738b\u5f64\u53e9\u8c22\u3002 \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u4f60\u4eec\u4eca\u665a\u6700\u597d\u6682\u4f4f\u5ba2\u6808\uff0c\u660e\u65e5\u518d\u5404\u81ea\u56de\u5bb6\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u56de\u5934\u4ea4\u4ee3\u6587\u6848\u5e08\u7237\u9053\uff1a\u201c\u56de\u5934\u6d3e\u4eba\u9001\u4e00\u767e\u4e24\u94f6\u5b50\u5230\u5ba2\u6808\u6765\uff0c\u9664\u4e86\u4e3a\u4ed6\u4eec\u538b\u60ca\uff0c\u4e5f \u7b97\u672c\u629a\u5bf9\u4ed6\u4eec\u7684\u4e00\u70b9\u5fc3\u610f\u3002\u201d \u3000\u3000\u90a3\u6587\u6848\u5e08\u7237\u5e94\u4e86\u58f0\u201c\u662f\u201d\u3002 \u3000\u3000\u9a6c\u6587\u4e2d\u518d\u8f6c\u5934\u9053\uff1a\u201c\u738b\u5927\u4eba\uff0c\u4e0b\u5b98\u5bf9\u672c\u6848\u5982\u6b64\u5ba1\u5224\uff0c\u662f\u5426\u5f97\u5f53\uff1f\u8fd8\u8bf7\u591a\u8d50\u6307\u6b63\u3002\u201d \u3000\u3000\u738b\u5f64\u62f1\u624b\u4e00\u7b11\u9053\uff1a\u201c\u9a6c\u5927\u4eba\u679c\u7136\u9ad8\u660e\uff0c\u738b\u67d0\u4f69\u670d\uff01\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u957f\u957f\u53f9\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u4e0b\u5b98\u4ece\u524d\u4e00\u76f4\u8ba4\u4e3a\u5c39\u77e5\u5e9c\u662f\u4f4d\u4e0d\u9519\u7684\u5b98\uff0c\u60f3\u4e0d\u5230\u4ed6\u7adf\u505a\u51fa\u8fd9 \u7b49\u4f24\u5929\u5bb3\u7406\u7684\u4e8b\u6765\uff0c\u771f\u662f\u4eba\u5fc3\u96be\u6d4b\u5440\uff01\u201d \u3000\u3000\u201c\u8fd9\u5c31\u53eb\u505a\u77e5\u4eba\u77e5\u9762\u4e0d\u77e5\u5fc3\uff0c\u9a6c\u5927\u4eba\u4ee5\u4e3a\u5bf9\u5417\uff1f\u201d \u3000\u3000\u201c\u5bf9\u6781\u4e86\uff01\u738b\u5927\u4eba\u8fd8\u5c11\u8bf4\u4e86\u4e00\u53e5\uff0c\u524d\u9762\u7684\u4e00\u53e5\u8be5\u662f\u753b\u864e\u96be\u753b\u9aa8\u3002\u201d \u3000\u3000\u63a5\u7740\u7ad9\u8d77\u8eab\u6765\u9053\uff1a\u201c\u6253\u6270\u738b\u5927\u4eba\u8fd9\u4e48\u4e45\uff0c\u4e0b\u5b98\u73b0\u8be5\u544a\u8f9e\u4e86\uff01\u201d \u3000\u3000\u738b\u5f64\u8d77\u8eab\u76f8\u9001\u9053\uff1a\u201c\u8fc7\u51e0\u5929\u738b\u67d0\u518d\u5230\u629a\u7f72\u62dc\u5019\uff01\u201d \u3000\u3000\u201c\u4e0d\u6562\u5f53\uff0c\u8bf7\u738b\u5927\u4eba\u65e0\u8bba\u5982\u4f55\u8981\u8f6c\u7980\u4e09\u516c\u4e3b\uff0c\u8ba9\u4e0b\u5b98\u5f97\u4ee5\u80fd\u664b\u89c1\u4e09\u516c\u4e3b\u4e00\u6b21\uff0c\u5426\u5219\u4e0b\u5b98 \u5c31\u592a\u5931\u793c\u4e86\uff01\u201d \u3000\u3000\u5f53\u665a\uff0c\u9a6c\u6587\u4e2d\u679c\u7136\u6d3e\u4eba\u9001\u4e86\u4e00\u767e\u4e24\u94f6\u5b50\u6765\u7ed9\u6797\u5bb6\u58f0\u3002 \u3000\u3000\u738b\u5f64\u7ecf\u8fc7\u548c\u6c5f\u5343\u91cc\u7684\u5546\u8bae\u7ed3\u679c\uff0c\u51b3\u5b9a\u8ba9\u6797\u5bb6\u58f0\u7b2c\u4e8c\u5929\u5c31\u56de\u5bb6\u53bb\uff0c\u4ee5\u4fbf\u548c\u4ed6\u7684\u7236\u6bcd\u65e9\u65e5\u56e2 \u805a\u3002 \u3000\u3000\u81f3\u4e8e\u5b59\u5c0f\u51e4\uff0c\u7531\u4e8e\u8fd8\u9700\u56de\u5f00\u5c01\u5e9c\u8859\u53d6\u56de\u90a3\u8fb9\u7684\u9996\u9970\u548c\u8863\u7269\uff0c\u800c\u4e14\u8eab\u4efd\u95ee\u9898\u5c1a\u672a\u5f7b\u5e95\u89e3 \u51b3\uff0c\u8fd8\u662f\u6682\u65f6\u7559\u5728\u5ba2\u6808\u6709\u4eba\u4fdd\u62a4\u4e3a\u4f73\u3002 \u3000\u3000\u73b0\u5728\uff0c\u5c1a\u987b\u7ee7\u7eed\u8fdb\u884c\u7684\u4e8b\uff0c\u6700\u4e3b\u8981\u7684\u4fbf\u662f\u67e5\u660e\u8fce\u6625\u9601\u662f\u5426\u771f\u6709\u59d1\u5a18\u5bf9\u5ba2\u4eba\u4e0b\u866b\uff1f\u8fd9\u4e8b\u5173 \u7cfb\u7740\u6b64\u6765\u5f00\u5c01\u4efb\u52a1\uff0c\u7edd\u4e0d\u80fd\u8f7b\u6613\u653e\u8fc7\u7ebf\u7d22\u3002 \u3000\u3000\u4e3a\u4e86\u8fd9\u4e8b\uff0c\u4e0a\u6b21\u9648\u5b8f\u3001\u8d75\u4fdd\u3001\u5c0f\u71d5\u5b50\u662f\u5f92\u52b3\u800c\u8fd4\uff0c\u800c\u4e14\u95f9\u51fa\u4e0d\u5c11\u7b11\u8bdd\uff0c\u8fd9\u6b21\u5f53\u7136\u8981\u53e6\u60f3 \u529e\u6cd5\u3002 \u3000\u3000\u4e0b\u5348\uff0c\u738b\u5f64\u4fbf\u627e\u6c5f\u5343\u91cc\u7814\u7a76\u4e8b\u60c5\u8be5\u5982\u4f55\u8fdb\u884c\u201c \u3000\u3000\u6c5f\u5343\u91cc\u60f3\u4e86\u60f3\u9053\uff1a\u201c\u4f55\u4e0d\u8ba9\u8001\u5996\u5a46\u5230\u2018\u8fce\u6625\u9601\u2019\u4e00\u8d9f\uff1f\u201d \u3000\u3000\u738b\u5f64\u5927\u611f\u610f\u5916\u7684\u9053\uff1a\u201c\u90a3\u600e\u4e48\u53ef\u4ee5\uff0c\u4e00\u4e2a\u8001\u5973\u4eba\uff0c\u600e\u80fd\u5230\u90a3\u79cd\u5730\u65b9\u53bb\uff1f\u201d \u3000\u3000\u201c\u6c5f\u67d0\u4e0d\u59a8\u4eb2\u81ea\u52a8\u624b\uff0c\u628a\u5979\u6613\u5bb9\u6210\u4e00\u4e2a\u7537\u7684\uff0c\u800c\u4e14\u4fdd\u8bc1\u5916\u4eba\u7edd\u5bf9\u770b\u4e0d\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u6c5f\u5144\u7684\u6613\u5bb9\u672f\uff0c\u5144\u5f1f\u7edd\u5bf9\u4fe1\u5f97\u8fc7\u3002\u53ef\u662f\uff0c\u628a\u5979\u53d8\u6210\u7537\u7684\uff0c\u5230\u8fce\u6625\u9601\u53bb\u505a\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u201c\u5230\u90a3\u79cd\u5730\u65b9\u53bb\u7684\uff0c\u81ea\u7136\u662f\u5bfb\u82b3\u5ba2\u3002\u201d \u3000\u3000\u201c\u6709\u90a3\u4e48\u8001\u7684\u5ad6\u5ba2\u5417\uff1f\u201d \u3000\u3000\u201c\u4f60\u8001\u5f1f\u771f\u662f\u5c11\u89c1\u591a\u602a\uff0c\u522b\u8bf4\u662f\u4e03\u5341\u5c81\u7684\u7537\u4eba\u3002\u5373\u4f7f\u516b\u3001\u4e5d\u5341\u5c81\u7684\u7537\u4eba\uff0c\u53ea\u8981\u6709\u90a3\u79cd\u75db \u5934\uff0c\u7167\u6837\u4e0d\u4e4f\u6709\u5230\u90a3\u79cd\u5730\u65b9\u53bb\u7684\uff0c\u6839\u672c\u4e0d\u503c\u5f97\u5927\u60ca\u5c0f\u602a\uff0c\u4f55\u51b5\u6211\u4f1a\u8ba9\u5979\u770b\u8d77\u6765\u5e74\u8f7b\u4e00\u70b9\u3002\u201d \u3000\u3000\u201c\u5979\u53bb\u5c31\u6709\u529e\u6cd5\u67e5\u51fa\u4e0b\u866b\u7684\u59d1\u5a18\u662f\u8c01\u5417\uff1f\u201d \u3000\u3000\u201c\u5b9e\u5bf9\u8001\u5f1f\u8bf4\u5427\uff01\u4e07\u866b\u95e8\u7684\u4eba\uff0c\u8eab\u4e0a\u90fd\u6709\u4e00\u5b9a\u7684\u6807\u8bb0\uff0c\u800c\u5df2\u6709\u80a1\u7279\u6b8a\u6c14\u5473\uff0c\u8fd9\u79cd\u6807\u8bb0\u548c \u6c14\u5473\uff0c\u552f\u6709\u540c\u662f\u4e07\u866b\u95e8\u7684\u4eba\u624d\u80fd\u8fa8\u522b\u548c\u89c9\u5bdf\u51fa\u6765\uff0c\u8fd9\u4e5f\u5c31\u662f\u6211\u5e0c\u671b\u6d3e\u8001\u5996\u5a46\u53bb\u8fce\u6625\u9601\u7684\u539f \u56e0\u3002\u201d \u3000\u3000\u738b\u5f64\u70b9\u4e86\u70b9\u5934\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u5144\u5f1f\u7b97\u957f\u4e86\u4e00\u4e9b\u89c1\u95fb\uff0c\u53ea\u662f\u5144\u5f1f\u62c5\u5fc3\u90a3\u8001\u5996\u5a46\u4e0d\u80af\u5408 \u4f5c\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u7b11\u9053\uff1a\u201c\u53ea\u8981\u5bf9\u5979\u5f00\u51fa\u5979\u6240\u5e0c\u671b\u7684\u6761\u4ef6\uff0c\u5979\u5c31\u6ca1\u6709\u4e0d\u5408\u4f5c\u7684\u9053\u7406\u3002\u201d \u3000\u3000\u201c\u662f\u5426\u8fd8\u8981\u6d3e\u4eba\u76d1\u89c6\u5979\u7684\u884c\u52a8\uff1f\u201d \u3000\u3000\u201c\u8fd9\u662f\u5fc5\u7136\u7684\uff0c\u5426\u5219\u5979\u5c82\u4e0d\u8981\u8d81\u673a\u9003\u8131\u3002\u201d \u3000\u3000\u201c\u6c5f\u5144\u8ba4\u4e3a\u6d3e\u8c01\u53bb\u6bd4\u8f83\u9002\u5408\uff1f\u201d \u3000\u3000\u201c\u6709\u5c0f\u71d5\u5b50\u8ddf\u5979\u53bb\uff0c\u7edd\u5bf9\u51fa\u4e0d\u4e86\u5dee\u9519\u3002\u201d \u3000\u3000\u201c\u5c31\u8fd9\u4e48\u51b3\u5b9a\uff0c\u8ba9\u5c0f\u71d5\u5b50\u966a\u5979\u4eca\u665a\u5c31\u53bb\u3002\u201d \u3000\u3000\u5c31\u5728\u5f53\u5929\u508d\u665a\uff0c\u4e00\u8001\u4e00\u5c11\u79bb\u5f00\u5ba2\u6808\uff0c\u5f80\u8fce\u6625\u9601\u800c\u6765\u3002 \u3000\u3000\u8001\u5996\u5a46\u7531\u4e8e\u672c\u6765\u5c31\u8eab\u6750\u9ad8\u5927\uff0c\u6613\u5bb9\u6210\u7537\u7684\uff0c\u4e0d\u77e5\u9053\u5979\u6765\u8def\u7684\u4eba\uff0c\u4efb\u8c01\u4e5f\u770b\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u6b64\u523b\uff0c\u5979\u770b\u8d77\u6765\u5927\u7ea6\u5728\u516d\u5341\u5de6\u53f3\uff0c\u4eba\u9ad8\u9a6c\u5927\uff0c\u8863\u5c65\u4e5f\u662f\u5d2d\u65b0\u7684\uff0c\u4f46\u90a3\u526f\u5c0a\u5bb9\u5374\u5b9e\u5728\u8ba9\u4eba \u96be\u4ee5\u9886\u6559\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u7684\u6613\u5bb9\u672f\u867d\u7136\u9ad8\u660e\uff0c\u4f46\u5374\u4e0d\u80fd\u628a\u4eba\u7531\u4e11\u53d8\u7f8e\uff0c\u4ee5\u8001\u5996\u5a46\u7684\u5fb7\u6027\uff0c\u7a7f\u8d77\u9f99\u888d\u6765\u4e5f\u7edd \u4e0d\u53ef\u80fd\u50cf\u7687\u5e1d\u3002 \u3000\u3000\u5979\u548c\u5c0f\u71d5\u5b50\uff0c\u4e00\u8001\u4e00\u5c11\uff0c\u4e00\u4e11\u4e00\u7f8e\uff0c\u8d70\u5728\u4e00\u8d77\u5f62\u6210\u4e86\u5f3a\u70c8\u7684\u5bf9\u6bd4\u3002 \u3000\u3000\u8001\u5996\u5a46\u6b64\u523b\u4e0d\u4f46\u6a21\u6837\u50cf\u7537\u4eba\uff0c\u8fde\u8d70\u8def\u4e5f\u50cf\u3002 \u3000\u3000\u5979\u8fb9\u8d70\u8fb9\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u5f85\u4f1a\u513f\u5230\u4e86\u90a3\u91cc\uff0c\u54b1\u4eec\u8be5\u600e\u6837\u79f0\u547c\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6545\u610f\u95ee\u9053\uff1a\u201c\u4f60\u8bf4\u8be5\u600e\u6837\u79f0\u547c\u5462\uff1f\u201d \u3000\u3000\u8001\u5996\u5a46\u5927\u6a21\u5927\u6837\u7684\u9053\uff1a\u201c\u8bba\u5e74\u7eaa\uff0c\u5e94\u8be5\u6211\u662f\u7237\u7237\uff0c\u4f60\u662f\u5b59\u5b50\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u4e24\u773c\u4e00\u77aa\u9053\uff1a\u201c\u2018\u4ec0\u4e48\uff1f\u4f60\u60f3\u5360\u6211\u7684\u4fbf\u5b9c\uff1f\u201d \u3000\u3000\u201c\u672c\u6765\u5c31\u662f\u5982\u6b64\uff0c\u8001\u8eab\u6839\u672c\u4e0d\u6253\u7b97\u5bf9\u4f60\u7684\u4fbf\u5b9c\uff0c\u96be\u9053\u51ed\u8001\u8eab\u7684\u5e74\u7eaa\uff0c\u8fd8\u517b\u4e0d\u51fa\u4f60\u8fd9\u6837\u7684 \u5b59\u5b50\uff1f\u201d \u3000\u3000\u201c\u6709\u4e0d\u5c11\u5e74\u7eaa\u5927\u7684\u53cd\u800c\u8f88\u5206\u4f4e\uff0c\u8b6c\u5982\u5728\u4e0b\u5bb6\u4e61\u6709\u4e2a\u540c\u5b97\u7684\u5b59\u5b50\uff0c\u5e74\u7eaa\u6b63\u597d\u4e03\u5341\u5c81\uff0c\u548c\u4f60 \u4e00\u6837\u5927\uff0c\u8fd9\u53c8\u8be5\u600e\u4e48\u8bb2\uff1f\u201d \u3000\u3000\u201c\u597d\u554a\uff01\u4f60\u662f\u732a\u516b\u6212\u5012\u6253\u4e00\u8019\uff0c\u53cd\u800c\u60f3\u8ba8\u8001\u8eab\u7684\u4fbf\u5b9c\u4e86\uff01\u201d \u3000\u3000\u201c\u54b1\u4eec\u8c01\u90fd\u522b\u60f3\u8ba8\u8c01\u7684\u4fbf\u5b9c\uff0c\u5982\u679c\u771f\u662f\u4e00\u5bf9\u7956\u5b59\uff0c\u54ea\u6709\u6211\u8fd9\u4e2a\u505a\u7237\u7237\u7684\u5e26\u7740\u5b59\u5b50\u53bb\u5ad6\u5993 \u7684\u9053\u7406\uff0c\u6240\u4ee5\u54b1\u4eec\u4e24\u4e2a\u53ea\u80fd\u662f\u670b\u53cb\u7684\u5173\u7cfb\u3002\u201d \u3000\u3000\u201c\u90a3\u8be5\u600e\u4e48\u79f0\u547c\u5462\uff1f\u201d \u3000\u3000\u201c\u4f60\u597d\u50cf\u59d3\u53f2\uff0c\u6211\u5c31\u53eb\u4f60\u53f2\u8001\u5427\uff01\u4f60\u4e0d\u59a8\u53eb\u6211\u4e00\u58f0\u5c0f\u5144\u5f1f\u3002\u201d \u3000\u3000\u201c\u5f88\u597d\u3002\u201d \u3000\u3000\u201c\u53f2\u8001\uff0c\u95f2\u8bdd\u5c11\u8bf4\uff0c\u8ddf\u6211\u8d70\u5427\uff01\u201d \u3000\u3000\u201c\u4f60\u597d\u50cf\u8def\u5f84\u5f88\u719f\uff0c\u662f\u5426\u5df2\u7ecf\u53bb\u8fc7\uff1f\u201d \u3000\u3000\u201c\u53bb\u8fc7\u4fbf\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u201c\u4f60\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u521a\u6765\u5f00\u5c01\uff0c\u5c31\u5f80\u90a3\u79cd\u5730\u65b9\u8dd1\uff0c\u8bc1\u660e\u4f60\u65e9\u5df2\u7ecf\u5b66\u574f\u4e86\u3002\u201d \u3000\u3000\u201c\u518d\u574f\u4e5f\u6ca1\u6709\u4f60\u574f\u3002\u201d \u3000\u3000\u201c\u8001\u8eab\u54ea\u91cc\u574f\uff1f\u201d \u3000\u3000\u201c\u4f60\u4e0b\u866b\u653e\u6bd2\uff0c\u4e00\u8eab\u5996\u6c14\uff0c\u8fd8\u80fd\u8bf4\u4e0d\u574f\u5417\uff1f\u201d \u3000\u3000\u201c\u53ef\u662f\u8001\u8eab\u73b0\u5df2\u6539\u90aa\u5f52\u6b63\u4e86\uff0c\u81f3\u5c11\u6211\u8fd8\u4e0d\u66fe\u505a\u8fc7\u5ad6\u5993\u7684\u4e8b\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u5fcd\u4e0d\u4f4f\u7b11\u51fa\u58f0\u6765\u9053\uff1a\u201c\u4f60\u62ff\u4ec0\u4e48\u53bb\u5ad6\u5993\uff1f\u5f53\u5f53\u5993\u5973\u8fd8\u5dee\u4e0d\u591a\u3002\u201d \u3000\u3000\u8001\u5996\u5a46\u72e0\u72e0\u634f\u4e86\u5c0f\u71d5\u5b50\u4e00\u628a\uff0c\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u7adf\u6562\u62ff\u8fd9\u79cd\u8bdd\u6765\u6d88\u9063\u8001\u8eab\uff0c\u5c0f\u5fc3\u56de\u53bb\u6211 \u544a\u8bc9\u6c5f\u5927\u4fa0\uff0c\u8fd8\u6709\u4f60\u7239\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u7b11\u9053\uff1a\u201c\u6211\u7239\u548c\u6c5f\u53d4\u53d4\u867d\u7136\u4f1a\u7ba1\u6211\uff0c\u4f46\u5374\u5fc5\u5b9a\u4f1a\u770b\u6211\u6d88\u9063\u7684\u5bf9\u8c61\u662f\u8c01\u800c\u5b9a\u3002\u201d \u3000\u3000\u8001\u5996\u5a46\u7728\u7740\u4e00\u5bf9\u9f20\u5149\u95ea\u95ea\u7684\u773c\u775b\u9053\uff1a\u201c\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u201c\u610f\u601d\u5f88\u7b80\u5355\uff0c\u6211\u5982\u679c\u5bf9\u5e74\u8f7b\u6f02\u4eae\u7684\u5973\u5b69\u8bf4\u521a\u624d\u90a3\u79cd\u8bdd\uff0c\u5f53\u7136\u4e0d\u5e94\u8be5\uff0c\u4f46\u5bf9\u4f60\u8fd9\u6837\u8bf4 \u8bf4\uff0c\u5c31\u6ca1\u6709\u4ec0\u4e48\u4e0d\u53ef\u7684\u4e86\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4f60\u8be5\u6492\u6ce1\u5c3f\u7167\u7167\uff0c\u5c31\u51ed\u4f60\u8fd9\u526f\u5fb7\u6027\uff0c\u591f\u8d44\u683c\u5230\u2018\u8fce\u6625\u9601\u2019\u505a\u751f\u610f\u5417\uff1f\u201d \u3000\u3000\u201c\u51ed\u4ec0\u4e48\u4e0d\u80fd\uff1f\u201d \u3000\u3000\u201c\u53ea\u6015\u4e09\u5e74\u4e5f\u4e0d\u4f1a\u6709\u5ba2\u4eba\u4e0a\u95e8\u3002\u201d \u3000\u3000\u201c\u8001\u8eab\u73b0\u5728\u8001\u4e86\uff0c\u5f53\u7136\u4e0d\u5bb9\u6613\u62db\u5230\u5ba2\u4eba\uff0c\u4f46\u82e5\u5012\u9000\u4e94\u5341\u5e74\uff0c\u90a3\u5c31\u4e0d\u4e00\u6837\u4e86\u3002\u201d \u3000\u3000\u201c\u6709\u4ec0\u4e48\u4e0d\u4e00\u6837\uff1f\u4f60\u5c31\u662f\u5012\u9000\u4e00\u767e\u5e74\uff0c\u6050\u6015\u4e5f\u4e0d\u6210\uff01\u201d \u3000\u3000\u8001\u5996\u5a46\u53d1\u4e86\u5996\u55d4\uff0c\u543c\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u597d\u50cf\u628a\u8001\u8eab\u770b\u6241\u4e86\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u7b11\u9053\uff1a\u201c\u4f60\u65e2\u4e0d\u662f\u4e4c\u9f9f\uff0c\u4e5f\u4e0d\u662f\u738b\u516b\uff0c\u6211\u600e\u4f1a\u628a\u4f60\u770b\u6241\uff1f\u6211\u662f\u8bf4\uff0c\u50cf\u4f60\u8fd9\u526f\u5c0a \u5bb9\uff0c\u5c31\u662f\u5341\u516b\u5c81\u4e5f\u5fc5\u5b9a\u548c\u732a\u516b\u6212\u7684\u59b9\u59b9\u5dee\u4e0d\u591a\u3002\u201d \u3000\u3000\u8001\u5996\u5a46\u6c14\u5f97\u5927\u53eb\u9053\uff1a\u201c\u597d\u54c7\uff01\u4f60\u8fd9\u5c0f\u5b50\u5b9e\u5728\u5927\u53ef\u6076\u4e86\uff0c\u4e13\u95e8\u5728\u8001\u8eab\u9762\u524d\u8bf4\u96be\u542c\u7684\uff0c\u5b9e\u5bf9 \u4f60\u8bf4\u5427\uff01\u8001\u8eab\u5728\u5341\u516b\u5c81\u7684\u65f6\u5019\u662f\u51fa\u4e86\u540d\u7684\u4e00\u679d\u82b1\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u70b9\u70b9\u5934\u9053\uff1a\u201c\u90a3\u53ef\u80fd\u662f\u9ebb\u82b1\u5427\uff01\u201d \u3000\u3000\u201c\u5c0f\u5b50\uff0c\u8001\u8eab\u5341\u516b\u5c81\u7684\u65f6\u5019\uff0c\u4f60\u89c1\u8fc7\u5417\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u6ca1\u89c1\u8fc7\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u6ca1\u89c1\u8fc7\uff0c\u4f60\u600e\u77e5\u90a3\u65f6\u8001\u8eab\u4e0d\u662f\u8c8c\u7f8e\u5982\u82b1\uff1f\u201d \u3000\u3000\u201c\u8fd9\u662f\u60f3\u5f53\u7136\u7684\u4e8b\uff0c\u8fde\u4e09\u5c81\u7684\u5c0f\u5b69\u5b50\u90fd\u53ef\u4ee5\u60f3\u5f97\u5230\u7684\u3002\u201d \u3000\u3000\u8001\u5996\u5a46\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u8001\u8eab\u61d2\u5f97\u8ddf\u4f60\u8bf4\u5e9f\u8bdd\uff0c\u544a\u8bc9\u4f60\uff0c\u5982\u679c\u4f60\u771f\u6709\u773c\u798f\u770b\u8fc7\u8001\u8eab\u5341\u516b\u5c81\u65f6 \u7684\u6a21\u6837\uff0c\u53ea\u6015\u4f60\u5c31\u4e0d\u60f3\u518d\u770b\u7b2c\u4e8c\u4e2a\u5973\u4eba\u4e86\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u70b9\u70b9\u5934\u9053\uff1a\u201c\u8fd9\u4e5f\u662f\u60f3\u5f53\u7136\u7684\u4e8b\uff0c\u56e0\u4e3a\u4e0d\u53ef\u80fd\u6709\u7b2c\u4e8c\u4e2a\u5973\u4eba\u6bd4\u4f60\u53f2\u8001\u66f4\u96be\u770b\u3002\u201d \u3000\u3000\u8001\u5996\u5a46\u731b\u8dfa\u811a\u9053\uff1a\u201c\u54b1\u4eec\u522b\u8c08\u8fd9\u4e9b\u4e86\uff0c\u5927\u6982\u5feb\u5230\u4e86\u5427\uff01\u73b0\u5728\u8be5\u8c08\u70b9\u6b63\u7ecf\u7684\u624d\u5bf9\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6709\u4e00\u642d\u6ca1\u4e00\u642d\u7684\u9053\uff1a\u201c\u8bf4\u7684\u4e5f\u662f\uff0c\u4e0d\u77e5\u4f60\u90a3\u6b63\u7ecf\u7684\uff0c\u6307\u7684\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u8b6c\u5982\u5230\u4e86\u90a3\u91cc\uff0c\u8be5\u505a\u4e9b\u4ec0\u4e48\uff1f\u8001\u8eab\u5bf9\u90a3\u79cd\u5730\u65b9\u7684\u89c4\u77e9\uff0c\u4e00\u70b9\u4e0d\u61c2\u3002\u201d \u3000\u3000\u201c\u4f60\u5982\u679c\u60f3\u5ad6\uff0c\u53ea\u8981\u6709\u94f6\u5b50\u5c31\u4e00\u5b9a\u6210\u3002\u201d \u3000\u3000\u201c\u53bb\u4f60\u7684\uff0c\u4f60\u521a\u624d\u8bf4\u8fc7\uff0c\u8001\u8eab\u6211\u62ff\u4ec0\u4e48\u5ad6\uff1f\u88ab\u4eba\u5bb6\u5ad6\u8fd8\u5dee\u4e0d\u591a\u3002\u201d \u3000\u3000\u201c\u867d\u7136\u4e0d\u80fd\u5ad6\uff0c\u5374\u5fc5\u987b\u505a\u51fa\u5ad6\u5ba2\u7684\u6837\u5b50\uff0c\u5426\u5219\u600e\u80fd\u548c\u59d1\u5a18\u63a5\u8fd1\uff0c\u65e0\u6cd5\u548c\u59d1\u5a18\u63a5\u8fd1\uff0c\u90a3\u5c31 \u7b49\u4e8e\u767d\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u5230\u4e86\u4ee5\u540e\uff0c\u8001\u8eab\u4e00\u5207\u542c\u4f60\u7684\uff0c\u4f60\u53ef\u8981\u591a\u591a\u6307\u6559\u3002\u201d \u3000\u3000\u201c\u53f2\u8001\u653e\u5fc3\uff0c\u6211\u6765\u7684\u76ee\u7684\u5c31\u662f\u8d1f\u8d23\u534f\u52a9\u4f60\u7684\u3002\u201d \u3000\u3000\u201c\u4e5f\u662f\u76d1\u89c6\u6211\u884c\u52a8\u7684\uff0c\u5bf9\u5417\uff1f\u201d \u3000\u3000\u201c\u5bf9\u4e0d\u5bf9\uff0c\u4f60\u5fc3\u91cc\u6709\u6570\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u95f4\uff0c\u5df2\u5230\u8fbe\u201c\u8fce\u6625\u9601\u201d\u95e8\u53e3\u3002 \u3000\u3000\u8001\u5996\u5a46\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u90a3\u4e9b\u5973\u4eba\uff0c\u4f4f\u7684\u5730\u65b9\u7adf\u662f\u8fd9\u4e48\u6709\u6c14\u6d3e\uff01\u201d \u3000\u3000\u201c\u53f2\u8001\uff0c\u522b\u8bf4\u5f97\u90a3\u4e48\u5927\u58f0\uff0c\u9a6c\u4e0a\u5c31\u6709\u4eba\u6765\u8fce\u63a5\u54b1\u4eec\u4e86\u3002\u201d \u3000\u3000\u679c\u7136\uff0c\u4e00\u8bed\u672a\u6bd5\uff0c\u90a3\u53eb\u5c24\u4e09\u7684\u9f9f\u5974\u7acb\u523b\u8fce\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u5c24\u4e09\u4e00\u8fc7\u6765\uff0c\u5c31\u7b11\u53e3\u76c8\u76c8\u7684\u51b2\u7740\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u8fd9\u4f4d\u516c\u5b50\u4e0d\u662f\u4e0a\u6b21\u6765\u8fc7\u7684\u90a3\u4f4d\u5417\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u9894\u9996\u9053\uff1a\u201c\u597d\u773c\u529b\uff01\u597d\u8bb0\u6027\uff01\u201d \u3000\u3000\u201c\u53e6\u5916\u90a3\u4e24\u4f4d\u5462\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u6ca1\u6765\u3002\u201d \u3000\u3000\u5c24\u4e09\u4e00\u773c\u518d\u671b\u5411\u8001\u5996\u5a46\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u8c01\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u53f2\u8001\uff0c\u8fd9\u6b21\u662f\u6211\u548c\u4ed6\u4e24\u4e2a\u4eba\u6765\u7684\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u5148\u8bf7\u5230\u82b1\u5385\u5427\uff01\u201d \u3000\u3000\u767b\u697c\u8fdb\u5165\u4e00\u4e2a\u5c0f\u623f\u95f4\uff0c\u5c24\u4e09\u5148\u8bf7\u4e24\u4eba\u5750\u4e0b\uff0c\u518d\u732e\u4e0a\u8336\u6765\u3002 \u3000\u3000\u5374\u89c1\u5c24\u4e09\u5411\u5c0f\u71d5\u5b50\u62db\u62db\u624b\u9053\uff1a\u201c\u516c\u5b50\u8bf7\u5230\u5916\u9762\u6765\u4e00\u4e0b\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u5f04\u4e0d\u6e05\u4ec0\u4e48\u4e8b\uff0c\u53ea\u597d\u968f\u7740\u5c24\u4e09\u6765\u5230\u5c0f\u623f\u95f4\u95e8\u5916\u3002 \u3000\u3000\u5c24\u4e09\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u8fd9\u4f4d\u53f2\u8001\u662f\u6765\u505a\u4ec0\u4e48\u7684\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u5f53\u7136\u662f\u6765\u627e\u59d1\u5a18\u7684\u3002\u201d \u3000\u3000\u5c24\u4e09\u4f38\u4e86\u4e00\u4e0b\u820c\u5934\u9053\uff1a\u201c\u8fd9\u4f4d\u8001\u4eba\u5bb6\u600e\u4e48\u957f\u5f97\u8fd9\u4e48\u96be\u770b\uff1f\u201d \u3000\u3000\u201c\u4f60\u7ba1\u4eba\u5bb6\u96be\u770b\u4e0d\u96be\u770b\uff0c\u96be\u9053\u4f60\u4eec\u8fd8\u89c4\u5b9a\u8fc7\u96be\u770b\u7684\u5ba2\u4eba\u4e0d\u51c6\u8fdb\u6765\uff1f\u201d \u3000\u3000\u201c\u5c0f\u7684\u610f\u601d\u662f\u6050\u6015\u4ed6\u8001\u4eba\u5bb6\u627e\u4e0d\u5230\u59d1\u5a18\u3002\u201d \u3000\u3000\u201c\u5c82\u6709\u6b64\u7406\uff0c\u6709\u94b1\u8d70\u904d\u5929\u4e0b\uff0c\u53f2\u8001\u4eba\u683c\u9ad8\u5c1a\uff0c\u4ece\u4e0d\u767d\u5403\u767d\u5ad6\uff0c\u4e3a\u4ec0\u4e48\u627e\u4e0d\u5230\u59d1\u5a18\uff1f\u201d \u3000\u3000\u201c\u8bdd\u4e0d\u80fd\u8fd9\u4e48\u8bf4\uff0c\u6211\u4eec\u8fd9\u91cc\u7684\u59d1\u5a18\u4e00\u4e2a\u4e2a\u773c\u5149\u90fd\u5f88\u9ad8\uff0c\u5982\u679c\u5bf9\u5ba2\u4eba\u770b\u4e0d\u4e0a\u773c\uff0c\u518d\u591a\u7684\u94f6 \u5b50\u5979\u4eec\u4e5f\u4e0d\u80af\u63a5\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u53f2\u8001\u6709\u624b\u6709\u817f\uff0c\u4e03\u5b54\u4e0d\u7f3a\uff0c\u4f60\u51ed\u4ec0\u4e48\u8bf4\u4ed6\u4e0d\u597d\u770b\uff1f\u201d \u3000\u3000\u5c24\u4e09\u966a\u7740\u5c0f\u5fc3\u9053\uff1a\u201c\u5f53\u7136\u5566\uff01\u8c01\u4e5f\u4e0d\u80fd\u8bf4\u53f2\u8001\u4e0d\u50cf\u4eba\uff0c\u4f46\u4eba\u8ddf\u4eba\u5374\u7edd\u4e0d\u4e00\u6837\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u4e00\u6446\u624b\u9053\uff1a\u201c\u522b\u8bf4\u5e9f\u8bdd\u4e86\uff0c\u5feb\u5feb\u53bb\u53eb\u51e0\u4f4d\u59d1\u5a18\u6765\u7ed9\u4ed6\u8001\u4eba\u5bb6\u770b\uff0c\u4f60\u8bf4\u59d1\u5a18\u4eec\u4e0d\u4e2d \u610f\u4ed6\uff0c\u4f9d\u6211\u770b\u4ed6\u8001\u4eba\u5bb6\u8fd8\u4e0d\u4e00\u5b9a\u5c31\u4e2d\u610f\u4f60\u4eec\u7684\u59d1\u5a18\u5462\uff01\u5b9e\u5bf9\u4f60\u8bf4\u5427\uff0c\u4ed6\u8001\u4eba\u5bb6\u773c\u5149\u9ad8\u5f97 \u5f88\u3002\u201d \u3000\u3000\u5c24\u4e09\u6b63\u5728\u4e0d\u77e5\u5982\u4f55\u5e94\u4ed8\uff0c\u5ffd\u542c\u8001\u5996\u5a46\u5728\u91cc\u9762\u5927\u58f0\u9053\uff1a\u201c\u4f60\u4eec\u5728\u5916\u9762\u524d\u5495\u4ec0\u4e48\uff1f\u6709\u8bdd\u5230\u91cc \u9762\u8bb2\uff0c\u8fd8\u6015\u8001\u592b\u542c\u5230\u4e0d\u6210\uff01\u201d \u3000\u3000\u5c24\u4e09\u8fde\u5fd9\u518d\u8fdb\u5165\u5c0f\u623f\u95f4\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u4e5f\u8ddf\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u201c\u8001\u5996\u5a46\u6c89\u4e0b\u8138\u8272\u9053\uff1a\u201d\u4f60\u662f\u8d1f\u8d23\u62db\u5f85\u5ba2\u4eba\u7684\u5417\uff1f\u8001\u592b\u5728\u91cc\u9762\uff0c\u4e3a\u4ec0\u4e48\u8981\u5230\u5916\u9762\u62db\u5f85\uff1f \u201c \u3000\u3000\u201c\u5c0f\u7684\u73b0\u5728\u4e0d\u662f\u56de\u6765\u4e86\u5417\uff1f\u8001\u7237\u5b50\u5230\u8fd9\u91cc\u6765\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u5f53\u7136\u662f\u627e\u59d1\u5a18\uff0c\u82e5\u4e0d\u627e\u59d1\u5a18\uff0c\u8001\u592b\u6765\u505a\u4ec0\u4e48\uff1f\u95ee\u7684\u7b80\u76f4\u662f\u5e9f\u8bdd\uff01\u201d \u3000\u3000\u5c24\u4e09\u65e9\u88ab\u8001\u5996\u5a46\u7684\u6076\u5f62\u6076\u72b6\u5413\u574f\uff0c\u90a3\u91cc\u8fd8\u6562\u518d\u8bb2\u4ec0\u4e48\uff0c\u53ea\u80fd\u7ad9\u5728\u4e00\u65c1\u53d1\u6296\u3002 \u3000\u3000\u8001\u5996\u5a46\u9f3b\u5b54\u91cc\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u521a\u624d\u5728\u5916\u9762\u8bb2\u7684\u8bdd\uff0c\u6839\u672c\u6ca1\u7792\u8fc7\u8001\u592b\u7684\u8033\u6735\uff0c\u4f60 \u8bf4\u8001\u592b\u957f\u5f97\u96be\u770b\uff0c\u5c31\u8be5\u56de\u5bb6\u770b\u770b\uff0c\u51ed\u4f60\u8fd9\u526f\u5fb7\u6027\uff0c\u4f60\u8001\u5b50\u4e5f\u4e0d\u53ef\u80fd\u6bd4\u8001\u592b\u597d\u5230\u54ea\u91cc\u53bb\uff01\u201d \u3000\u3000\u5c24\u4e09\u9f87\u7259\u54a7\u5634\u7684\u6253\u63d6\u9053\uff1a\u201c\u5c0f\u7684\u600e\u6562\u968f\u4fbf\u6279\u8bc4\u8001\u7237\u5b50\uff0c\u4f60\u8001\u4eba\u5bb6\u5343\u4e07\u522b\u8bef\u4f1a\u3002\u201d \u3000\u3000\u8001\u5996\u5a46\u6c89\u58f0\u9053\uff1a\u201c\u90a3\u5c31\u5feb\u628a\u59d1\u5a18\u53eb\u6765\uff0c\u8d8a\u591a\u8d8a\u597d\uff0c\u8001\u592b\u522b\u7684\u6ca1\u6709\uff0c\u5c31\u662f\u6709\u94b1\uff1b\u5982\u679c\u770b\u4e2d \u4e86\u610f\uff0c\u52a0\u500d\u6709\u8d4f\uff0c\u8fde\u4f60\u8fd9\u5c0f\u5b50\u4e5f\u6709\u597d\u5904\u3002\u201d \u3000\u3000\u5c24\u4e09\u8fb9\u54c6\u55e6\u8fb9\u54c8\u7740\u8170\u9053\uff1a\u201c\u8001\u7237\u5b50\u522b\u751f\u6c14\uff0c\u5c0f\u7684\u73b0\u5728\u5c31\u53bb\u8bd5\u8bd5\u3002\u201d \u3000\u3000\u8001\u5996\u5a46\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u8bd5\u8bd5\uff01\u5982\u679c\u53eb\u4e0d\u6765\u59d1\u5a18\uff0c\u8001\u5b50\u5c31\u6253\u65ad\u4f60\u7684\u72d7\u817f\uff01\u201d \u3000\u3000\u5c24\u4e09\u4e00\u6e9c\u70df\u8dd1\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u8001\u5996\u5a46\u671b\u4e86\u5c0f\u71d5\u5b50\u4e00\u773c\u9053\uff1a\u201c\u7b80\u76f4\u5c82\u6709\u6b64\u7406\uff0c\u771f\u4e0d\u77e5\u8fd9\u5c0f\u5b50\u7684\u773c\u775b\u957f\u5230\u54ea\u91cc\u53bb\u4e86\uff0c\u8fde\u8c01 \u597d\u770b\u8c01\u96be\u770b\u90fd\u5206\u8fa8\u4e0d\u51fa\u6765\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u7b11\u9053\uff1a\u201c\u8fd9\u5c31\u8bc1\u660e\u6211\u5728\u8def\u4e0a\u8bf4\u7684\u8bdd\u6ca1\u9519\uff0c\u53f2\u8001\u8fd8\u662f\u5fcd\u8010\u4e00\u70b9\u7684\u597d\u3002\u201d \u3000\u3000\u201c\u8001\u8eab\u5b9e\u5728\u54bd\u4e0d\u4e0b\u8fd9\u53e3\u6c14\uff01\u201d \u3000\u3000\u201c\u4e0d\u54bd\u6c14\u6700\u597d\uff0c\u82e5\u4f60\u54bd\u4e86\u6c14\uff0c\u90a3\u5c31\u4e0d\u53ef\u80fd\u518d\u6765\u8fd9\u91cc\u8000\u6b66\u626c\u5a01\u4e86\u3002\u201d \u3000\u3000\u201c\u8bf4\u7684\u4e5f\u662f\uff0c\u6765\u5230\u8fd9\u79cd\u5730\u65b9\u53d1\u813e\u6c14\uff0c\u4e5f\u8bb8\u662f\u81ea\u8ba8\u6ca1\u8da3\uff0c\u5f85\u4f1a\u513f\u6211\u8fd8\u662f\u5c3d\u91cf\u5fcd\u8010\u4e9b\u597d\u3002\u201d \u3000\u3000\u8db3\u8db3\u76cf\u8336\u5de5\u592b\u4e4b\u540e\uff0c\u53ea\u542c\u95e8\u5916\u4f20\u6765\u811a\u6b65\u58f0\uff01 \u3000\u3000\u63a5\u7740\u662f\u5c24\u4e09\u7684\u58f0\u97f3\u9053\uff1a\u201c\u90a3\u4f4d\u5927\u7237\u5c31\u5728\u91cc\u9762\uff0c\u4f60\u4eec\u81ea\u5df1\u8fdb\u53bb\u5427\uff01\u201d \u3000\u3000\u95e8\u5e18\u4e00\u6380\uff0c\u63a5\u7740\u8d70\u8fdb\u6765\u4e09\u4f4d\u82b1\u679d\u62db\u5c55\u7684\u59d1\u5a18\u3002 \u3000\u3000\u4e0a\u6b21\u88ab\u9648\u5b8f\u62db\u8fc7\u7684\u5c0f\u6843\uff0c\u4e5f\u8d6b\u7136\u5728\u5185\u3002 \u3000\u3000\u5c0f\u6843\u4e00\u8fdb\u95e8\u5c31\u8f6c\u7740\u4e00\u5bf9\u6ef4\u6e9c\u6e9c\u7684\u7738\u5b50\uff0c\u76f4\u76ef\u5411\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u8fd9\u4f4d\u5927\u7237\uff0c\u4f60\u53c8\u6765\u4e86\uff0c\u4e0a\u6b21 \u6765\u8fc7\u7684\u90a3\u4e24\u4f4d\u600e\u4e48\u4e0d\u89c1\u4e86\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u4ed6\u4eec\u6ca1\u6765\u3002\u201d \u3000\u3000\u201c\u90a3\u4e24\u4e2a\u597d\u50cf\u662f\u6545\u610f\u6363\u86cb\u7684\uff0c\u53ea\u8bf4\u4e0d\u73a9\uff0c\u4f60\u4e00\u4e2a\u4eba\u6765\u6700\u597d\uff0c\u5feb\u5230\u6211\u623f\u95f4\u53bb\u5427\uff01\u201d \u3000\u3000\u8bf4\u7740\uff0c\u8d70\u8fc7\u6765\u4fbf\u62c9\u7740\u5c0f\u71d5\u5b50\u7684\u624b\uff0c\u5927\u6709\u5f3a\u62d6\u786c\u62c9\u4e4b\u610f\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u7ea2\u7740\u8138\u62bd\u56de\u624b\u9053\uff1a\u201c\u4f60\u4eec\u628a\u773c\u775b\u653e\u4eae\u4e9b\uff0c\u627e\u59d1\u5a18\u7684\u4e0d\u662f\u6211\u3002\u201d \u3000\u3000\u5c0f\u6843\u5446\u4e86\u4e00\u5446\u9053\uff1a\u201c\u4e0d\u662f\u4f60\u662f\u8c01\uff1f\u8fd9\u5c4b\u91cc\u9664\u4e86\u4f60\u8fd8\u6709\u54ea\u4e2a\uff1f\u201d \u3000\u3000\u201c\u90a3\u4e48\u5927\u7684\u4e00\u4e2a\u4eba\u5750\u5728\u6905\u5b50\u4e0a\uff0c\u96be\u9053\u4f60\u4eec\u4f1a\u770b\u4e0d\u89c1\uff1f\u201d \u3000\u3000\u5c0f\u6843\u89c6\u7ebf\u8f6c\u5411\u8001\u5996\u5a46\uff0c\u5403\u4e86\u4e00\u60ca\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u662f\u4ed6\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u70b9\u70b9\u5934\u9053\uff1a\u201c\u5f53\u7136\u662f\u4ed6\uff0c\u7b80\u76f4\u662f\u591a\u6b64\u4e00\u95ee\u561b\uff01\u201d \u3000\u3000\u5c0f\u6843\u4e24\u773c\u53d1\u76f4\u9053\uff1a\u201c\u539f\u6765\u8fd9\u4f4d\u8001\u7237\u5b50\u4e5f\u662f\u627e\u59d1\u5a18\u6765\u7684\u3002\u201d \u3000\u3000\u201c\u4ed6\u82e5\u4e0d\u627e\u59d1\u5a18\uff0c\u5230\u4f60\u4eec\u8fd9\u91cc\u6765\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c0f\u6843\u5410\u4e86\u4e00\u4e0b\u820c\u5934\uff0c\u56de\u5934\u671b\u671b\u53e6\u4e24\u540d\u59d1\u5a18\uff0c\u63a5\u7740\u6025\u6025\u5411\u540e\u9000\u4e86\u4e24\u6b65\u3002 \u3000\u3000\u53e6\u4e24\u4f4d\u59d1\u5a18\u4e5f\u90fd\u4e24\u773c\u970e\u970e\u7684\u76f4\u5411\u540e\u9000\u3002 \u3000\u3000\u8001\u5996\u5a46\u53cc\u775b\u4e00\u9635\u6ef4\u6e9c\u6e9c\u7684\u6025\u8f6c\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u60f3\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c0f\u6843\u548c\u53e6\u4e24\u4f4d\u59d1\u5a18\u6839\u672c\u4e0d\u6562\u518d\u770b\u8001\u5996\u5a46\uff0c\u800c\u4e14\u8c01\u4e5f\u4e0d\u5f00\u53e3\u8bf4\u8bdd\u3002 \u3000\u3000\u8001\u5996\u5a46\u671b\u5411\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u524d\u9762\u90a3\u4e2a\u521a\u624d\u8fd8\u8bb2\u8fc7\u8bdd\u7684\u597d\u50cf\u8ddf\u4f60\u5f88\u719f\uff0c\u5979\u53eb\u4ec0\u4e48\u540d \u5b57\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u5979\u53eb\u5c0f\u6843\u3002\u201d \u3000\u3000\u8001\u5996\u5a46\u7acb\u5373\u5927\u58f0\u9053\uff1a\u201c\u5c0f\u6843\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u8bf4\u8bdd\u4e86\uff1f\u201d \u3000\u3000\u5c0f\u6843\u5168\u8eab\u4e00\u6296\u9053\uff1a\u201c\u4f60\u53eb\u4eba\u5bb6\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u8001\u592b\u4eca\u5929\u662f\u4f60\u4eec\u7684\u5ba2\u4eba\uff0c\u4f60\u4eec\u89c1\u4e86\u5ba2\u4eba\uff0c\u4e3a\u4ec0\u4e48\u8868\u73b0\u51fa\u90a3\u79cd\u6837\u5b50\uff1f\u201d \u3000\u3000\u201c\u8001\u7237\u5b50\uff0c\u50cf\u60a8\u8fd9\u79cd\u5e74\u7eaa\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u5728\u5bb6\u91cc\u62b1\u62b1\u5b59\u5b50\uff0c\u5374\u8dd1\u5230\u8fd9\u79cd\u5730\u65b9\u6765\uff1f\u201d \u3000\u3000\u201c\u8001\u592b\u9ad8\u5174\u6765\uff0c\u4eca\u665a\u4e0d\u60f3\u62b1\u5b59\u5b50\uff0c\u8981\u6539\u53d8\u4e00\u4e0b\uff0c\u62b1\u62b1\u59d1\u5a18\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u4eca\u665a\u90fd\u6709\u4eba\u62b1\u4e86\uff0c\u7528\u4e0d\u7740\u4f60\u8001\u4eba\u5bb6\u62b1\u3002\u201d \u3000\u3000\u201c\u8001\u592b\u6709\u94b1\uff0c\u53ea\u8981\u8c01\u8ba9\u8001\u592b\u62b1\uff0c\u5c31\u52a0\u500d\u7ed9\u94b1\u3002\u201d \u3000\u3000\u201c\u4f60\u51c6\u5907\u7ed9\u591a\u5c11\uff1f\u201d \u3000\u3000\u201c\u8001\u592b\u60c5\u613f\u52a0\u4e24\u500d\uff01\u201d \u3000\u3000\u201c\u8001\u7237\u5b50\u8fd8\u662f\u7701\u7701\u7684\u597d\uff0c\u7559\u7740\u94b1\u505a\u68fa\u6750\u8981\u7d27\u3002\u201d \u3000\u3000\u5c0f\u6843\u8bf4\u7740\uff0c\u56de\u5934\u4e00\u6380\u95e8\u5e18\u5c31\u8dd1\u3002 \u3000\u3000\u53e6\u4e24\u540d\u59d1\u5a18\u4e5f\u7acb\u5373\u6e9c\u4e4b\u5927\u5409\u3002 \u3000\u3000\u8001\u5996\u5a46\u53ea\u6c14\u5f97\u53cc\u988a\u53d1\u9752\uff0c\u731b\u4e00\u8dfa\u811a\u9053\uff1a\u201c\u4eba\u5bb6\u8bf4\u6709\u94b1\u80fd\u4f7f\u9b3c\u63a8\u78e8\uff0c\u8001\u8eab\u6765\u65f6\uff0c\u738b\u7edf\u9886\u66fe \u8d4f\u4e86\u4e00\u767e\u4e24\u94f6\u5b50\uff0c\u8eab\u4e0a\u4e0d\u80fd\u8bf4\u6ca1\u94b1\uff0c\u600e\u4e48\u8fde\u4eba\u90fd\u4e0d\u80af\u63a8\u78e8\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u8fd9\u5c31\u8bc1\u660e\u6211\u521a\u624d\u8bf4\u7684\u90a3\u4e9b\u8bdd\u5b8c\u5168\u6b63\u786e\u3002\u201d \u3000\u3000\u201c\u5148\u524d\u90a3\u4e2a\u62db\u547c\u5ba2\u4eba\u7684\u762a\u4e09\u53eb\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u53eb\u5c24\u4e09\u3002\u201d \u3000\u3000\u201c\u5c31\u9ebb\u70e6\u5c0f\u5144\u5f1f\u518d\u628a\u5c24\u4e09\u627e\u6765\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u4f9d\u8a00\u51fa\u623f\uff0c\u5728\u82b1\u5385\u8d70\u4e86\u4e00\u5708\uff0c\u6839\u672c\u4e0d\u89c1\u5c24\u4e09\u7684\u5f71\u5b50\u3002 \u3000\u3000\u4ed6\u53ea\u597d\u56de\u6765\u9053\uff1a\u201c\u627e\u4e0d\u5230\u3002\u201d \u3000\u3000\u8001\u5996\u5a46\u7ec8\u4e8e\u4e0d\u518d\u901e\u5f3a\uff0c\u4e24\u624b\u4e00\u644a\uff0c\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u5e72\u62ff\u4e86\u738b\u5927\u4eba\u4e00\u767e\u4e24\u94f6\u5b50\uff0c\u5374\u4ec0\u4e48\u4e8b \u90fd\u529e\u4e0d\u6210\uff0c\u83ab\u975e\u6211\u771f\u5df2\u53d8\u6210\u8001\u5e9f\u7269\u4e86\u5417\uff1f\u201d \u3000\u3000\u201c\u4f60\u6839\u672c\u7528\u4e0d\u7740\u628a\u59d1\u5a18\u53eb\u5230\u8fd9\u91cc\u6765\uff01\u201d \u3000\u3000\u201c\u4e0d\u53eb\u59d1\u5a18\u6765\uff0c\u600e\u4e48\u770b\uff1f\u201d \u3000\u3000\u201c\u8981\u770b\u59d1\u5a18\u53ef\u4ee5\u5230\u5916\u9762\u81ea\u5df1\u770b\uff0c\u4f60\u628a\u59d1\u5a18\u53eb\u5230\u8fd9\u91cc\uff0c\u4e07\u4e00\u771f\u6709\u59d1\u5a18\u613f\u610f\u63a5\u5f85\u4f60\uff0c\u8fdb\u5165\u623f \u95f4\u4ee5\u540e\uff0c\u4f60\u5c82\u4e0d\u73b0\u4e86\u539f\u5f62\uff1f\u201d \u3000\u3000\u201c\u6709\u9053\u7406\uff0c\u8fd8\u662f\u4f60\u806a\u660e\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u65e9\u8bf4\u5462\uff1f\u54b1\u4eec\u73b0\u5728\u5c31\u4e0b\u697c\uff0c\u53ef\u662f\u4f60\u5fc5\u987b\u7d27\u9760\u7740\u6211\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u73b0\u5728\u5df2\u660e\u767d\uff0c\u81ea\u5df1\u7684\u786e\u4e0d\u53d7\u6b22\u8fce\uff0c\u5982\u679c\u5355\u72ec\u8d70\uff0c\u59d1\u5a18\u4eec\u5fc5\u5b9a\u8eb2\u5f97\u8fdc\u8fdc\u7684\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u522b\u4eba\u5acc\u4f60\uff0c\u5728\u4e0b\u7edd\u4e0d\u5acc\u4f60\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u4e0b\u697c\u5427\uff01\u201d \u3000\u3000\u4e0b\u4e86\u697c\uff0c\u5c0f\u71d5\u5b50\u4f9d\u7167\u8001\u5996\u5a46\u7684\u8981\u6c42\uff0c\u4e24\u4eba\u5e76\u80a9\u800c\u884c\u3002 \u3000\u3000\u8fd9\u65f6\u6b63\u662f\u5bfb\u82b3\u5ba2\u6700\u591a\u7684\u65f6\u5019\uff0c\u51e1\u662f\u6ca1\u6709\u5ba2\u4eba\u7684\u59d1\u5a18\u591a\u534a\u90fd\u7ad9\u5728\u95e8\u53e3\uff0c\u7b11\u8138\u8fce\u4eba\u3002 \u3000\u3000\u5f53\u59d1\u5a18\u4eec\u53d1\u73b0\u82f1\u633a\u79c0\u62d4\u3001\u98ce\u5ea6\u7fe9\u7fe9\u7684\u5c0f\u71d5\u5b50\u65f6\uff0c\u65e0\u4e0d\u60f3\u4e0a\u524d\u642d\u8baa\uff0c\u4f46\u518d\u770b\u5230\u8fd9\u5e74\u8f7b\u4eba\u8eab \u65c1\u7684\u8001\u5996\u5a46\uff0c\u5374\u53c8\u8c01\u90fd\u4e0d\u5f97\u4e0d\u9000\u907f\u4e09\u820d\u3002 \u3000\u3000\u597d\u5728\u59d1\u5a18\u4eec\u867d\u4e0d\u6562\u8fd1\u524d\uff0c\u5728\u8001\u5996\u5a46\u6765\u8bf4\u5e76\u65e0\u5173\u7d27\u8981\uff0c\u5979\u53ea\u8981\u80fd\u770b\u6e05\u695a\u5c31\u597d\u4e86\u3002 \u3000\u3000\u51e0\u4e4e\u628a\u6240\u6709\u59d1\u5a18\u7684\u623f\u95e8\u53e3\u90fd\u8d70\u904d\u4e86\uff0c\u81f3\u5c11\u4e5f\u770b\u4e86\u4e09\u56db\u5341\u540d\u59d1\u5a18\uff0c\u8001\u5996\u5a46\u53ea\u662f\u805a\u7cbe\u4f1a\u795e\u7684 \u770b\uff0c\u5e76\u672a\u505a\u4efb\u4f55\u8868\u793a\u3002 \u3000\u3000\u5c31\u5728\u8d70\u5230\u95e8\u524d\u6709\u68f5\u6842\u6811\u7684\u53a2\u623f\u95e8\u53e3\u65f6\uff0c\u8001\u5996\u5a46\u5ffd\u7136\u795e\u8272\u4e00\u53d8\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\u5feb\u5230\u4e00 \u65c1\u8eb2\u8eb2\uff0c\u5f85\u4f1a\u513f\u8001\u8eab\u518d\u53bb\u627e\u4f60\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6599\u5b9a\u8001\u5996\u5a46\u5fc5\u5b9a\u5df2\u53d1\u73b0\u60c5\u51b5\uff0c\u4e0d\u4fbf\u591a\u95ee\uff0c\u6025\u6025\u8eb2\u5230\u4e0d\u8fdc\u5904\u7684\u5047\u5c71\u65c1\u3002 \u3000\u3000\u4ed6\u5fc5\u987b\u8ba9\u8001\u5996\u5a46\u4e0d\u79bb\u5f00\u81ea\u5df1\u7684\u89c6\u7ebf\uff0c\u5426\u5219\u82e5\u88ab\u5979\u6e9c\u6389\uff0c\u5c82\u4e0d\u7cdf\u7cd5\uff01 \u3000\u3000\u53ea\u89c1\u8001\u5996\u5a46\u8fd9\u65f6\u4e5f\u9690\u8eab\u5230\u6842\u6811\u4e4b\u540e\uff0c\u5077\u5077\u7684\u76f4\u5411\u53a2\u623f\u95e8\u5185\u671b\u53bb\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u518d\u671b\u5411\u53a2\u623f\u95e8\u5185\uff0c\u7531\u4e8e\u89d2\u5ea6\u4e0d\u5bf9\uff0c\u6839\u672c\u770b\u4e0d\u5230\u4ec0\u4e48\uff1f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u624d\u89c1\u8001\u5996\u5a46 \u6ee1\u9762\u7d27\u5f20\u4e4b\u8272\u6765\u5230\u5047\u5c71\u65c1\uff0c\u62cd\u62cd\u5c0f\u71d5\u5b50\u80a9\u8180\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u771f\u662f\u8e0f\u7834\u94c1\u978b\u65e0\u89c5\u5904\uff0c\u5f97\u6765\u5168\u4e0d\u8d39 \u5de5\u592b\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u8feb\u4e0d\u53ca\u5f85\u7684\u95ee\u9053\uff1a\u201c\u7a76\u7adf\u53d1\u73b0\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u91cc\u9762\u90a3\u4f4d\u59d1\u5a18\u6b63\u662f\u8001\u8eab\u7684\u5e08\u7236\u3002\u201d \u3000\u3000\u201c\u771f\u7684\uff1f\u201d \u3000\u3000\u201c\u6211\u770b\u4e86\u5f88\u4e45\uff0c\u4e00\u70b9\u6ca1\u9519\uff0c\u6b63\u662f\u5979\uff01\u201d \u3000\u3000\u201c\u5979\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u201c\u6842\u98d8\u9999\uff01\u201d \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u8fdb\u53bb\u89c1\u5979\uff1f\u201d \u3000\u3000\u201c\u8001\u8eab\u8fd8\u8ba4\u4e3a\u4f60\u5f88\u7075\u5149\uff0c\u539f\u6765\u7adf\u662f\u4e2a\u5c0f\u7b28\u86cb\u3002\u6211\u82e5\u548c\u5979\u89c1\u4e86\u9762\uff0c\u4e09\u516c\u4e3b\u7684\u4e8b\u5c31\u529e\u4e0d\u6210 \u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u8ba4\u4e3a\u8be5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u201c\u4f60\u4e0d\u59a8\u8fdb\u53bb\u548c\u5979\u642d\u8baa\u4e00\u756a\uff0c\u770b\u60c5\u5f62\u518d\u91c7\u53d6\u884c\u52a8\u3002\u201d \u3000\u3000\u201c\u4f60\u8ba4\u4e3a\u6211\u8be5\u91c7\u53d6\u4ec0\u4e48\u884c\u52a8\uff1f\u201d \u3000\u3000\u201c\u6700\u597d\u80fd\u628a\u5979\u8bf1\u5230\u5ba2\u6808\uff0c\u8981\u987a\u5176\u81ea\u7136\uff0c\u5207\u4e0d\u53ef\u7528\u5f3a\u8feb\u7684\uff0c\u5426\u5219\u95f9\u50f5\u8d77\u6765\uff0c\u4e8b\u60c5\u5c31\u4e0d\u597d\u6536 \u62fe\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u6211\u660e\u767d\uff0c\u5728\u5993\u9662\u91cc\u95f9\u4e8b\uff0c\u7684\u786e\u662f\u4ef6\u4e22\u4eba\u7684\u4e8b\uff0c\u5979\u7684\u6b66\u529f\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u8001\u8eab\u53ea\u662f\u5411\u5979\u5b66\u8fc7\u866b\u672f\uff0c\u5e76\u6ca1\u6709\u8ddf\u5979\u5b66\u6b66\u529f\uff0c\u4e0d\u8fc7\u51ed\u60f3\u50cf\uff0c\u5979\u8eab\u624b\u4e00\u5b9a\u6709\u4e24\u4e0b\u5b50\uff0c\u4f46 \u5979\u6b66\u529f\u518d\u9ad8\uff0c\u4e5f\u4e0d\u53ef\u80fd\u9ad8\u8fc7\u4f60\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u6211\u73b0\u5728\u5c31\u8fdb\u53bb\u548c\u5979\u89c1\u89c1\u3002\u201d \u3000\u3000\u8001\u5996\u5a46\u4e00\u628a\u62c9\u4f4f\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u522b\u6025\uff0c\u8001\u8eab\u8fd8\u6709\u8bdd\u4ea4\u4ee3\uff01\u201d \u3000\u3000\u201c\u8fd8\u8981\u4ea4\u4ee3\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u8001\u8eab\u90a3\u5e08\u7236\u5f88\u7f8e\uff0c\u79f0\u5f97\u4e0a\u662f\u98ce\u60c5\u4e07\u79cd\uff0c\u4f60\u5343\u4e07\u4e0d\u80fd\u88ab\u5979\u8ff7\u4f4f\uff0c\u5fc5\u987b\u628a\u6301\u51b7\u9759\u3002\u201d \u3000\u3000\u201c\u4e0d\u51b7\u9759\u4fbf\u600e\u6837\uff1f\u201d \u3000\u3000\u201c\u82e5\u548c\u5979\u4e0a\u4e86\u5e8a\uff0c\u4f60\u4f53\u5185\u4e00\u5b9a\u4e5f\u4f1a\u88ab\u5979\u4e0b\u866b\u3002\u201d \u3000\u3000\u8001\u5996\u5a46\u8bdd\u521a\u8bf4\u5b8c\uff0c\u7a81\u89c1\u5c0f\u71d5\u5b50\u4e00\u7f15\u6307\u98ce\u75be\u88ad\u800c\u6765\u3002 \u3000\u3000\u4ee5\u5c0f\u71d5\u5b50\u7684\u5feb\u6377\u624b\u6cd5\uff0c\u5979\u6839\u672c\u65e0\u6cd5\u907f\u5f00\uff0c\u987f\u89c9\u5168\u8eab\u4e00\u9ebb\uff0c\u52a8\u5f39\u4e0d\u5f97\u3002 \u3000\u3000\u201c\u597d\u5c0f\u5b50\uff0c\u4e3a\u4ec0\u4e48\u53cd\u800c\u8981\u5bf9\u8001\u8eab\u4e0b\u624b\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6b49\u7136\u4e00\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u4e0d\u5f97\u5df1\u7684\u4e8b\uff0c\u6211\u65e0\u6cd5\u5b8c\u5168\u76f8\u4fe1\u4f60\uff0c\u4e07\u4e00\u4f60\u8d81\u673a\u9003\u8dd1\u4e86\uff0c\u6211\u5982 \u4f55\u5411\u738b\u5927\u4eba\u548c\u6c5f\u53d4\u53d4\u4ea4\u4ee3\uff1f\u201d \u3000\u3000\u201c\u597d\u554a\uff01\u8001\u8eab\u4e00\u5fc3\u4e00\u610f\u6539\u90aa\u5f52\u6b63\uff0c\u4f60\u8fd8\u4e0d\u76f8\u4fe1\u6211\uff01\u201d \u3000\u3000\u201c\u4f60\u8bf7\u5728\u5047\u5c71\u4e0b\u5750\u4f1a\u513f\u4f11\u606f\u4f11\u606f\u5427\uff01\u6211\u5f88\u5feb\u5c31\u4f1a\u6765\u66ff\u4f60\u89e3\u5f00\u7a74\u9053\u3002\u201d \u3000\u3000\u201c\u4f60\u597d\u53ef\u6076\uff01\u201d \u3000\u3000\u201c\u968f\u4f60\u600e\u4e48\u8bb2\u90fd\u65e0\u6240\u8c13\uff0c\u6211\u73b0\u5728\u5c31\u8fdb\u53bb\u4e86\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u5148\u6574\u4e86\u6574\u8863\u51a0\uff0c\u968f\u5373\u786c\u8d77\u5934\u76ae\uff0c\u5927\u6447\u5927\u6446\u7684\u8d70\u8fdb\u95e8\u5916\u6709\u68f5\u6842\u6811\u7684\u53a2\u623f\u3002 \u5367\u9f99\u751f\u300a\u71d5\u5b50\u4f20\u5947\u300b \u7b2c\u4e8c\u5341\u56db\u56de\u3000\u72d0\u72f8\u8131\u9003 \u3000\u3000\u8fd9\u53a2\u623f\u5171\u662f\u91cc\u5916\u4e24\u95f4\uff0c\u5916\u9762\u662f\u5ba2\u5385\uff0c\u91cc\u9762\u662f\u5367\u5ba4\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u8fdb\u5165\u540e\uff0c\u5ba2\u5385\u91cc\u5e76\u65e0\u4eba\u5f71\u3002 \u3000\u3000\u6beb\u65e0\u7591\u95ee\uff0c\u6842\u98d8\u9999\u5fc5\u5728\u91cc\u9762\u3002 \u3000\u3000\u770b\u60c5\u5f62\u5367\u623f\u91cc\u53ef\u80fd\u4e0d\u4f1a\u6709\u5ba2\u4eba\uff0c\u5426\u5219\u5916\u95e8\u5fc5\u5b9a\u5173\u8d77\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u4e0d\u80fd\u592a\u62d8\u8c28\uff0c\u5fc5\u987b\u88c5\u51fa\u8001\u4e8e\u6b64\u9053\u7684\u6a21\u6837\uff0c\u7d22\u6027\u5c31\u5728\u5ba2\u5385\u91cc\u5750\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5f88\u5feb\u4fbf\u542c\u5367\u5ba4\u5185\u4f20\u51fa\u5a07\u6ef4\u6ef4\u800c\u53c8\u67d4\u9761\u9761\u7684\u58f0\u97f3\u9053\uff1a\u201c\u5ba2\u5385\u91cc\u662f\u54ea\u4e00\u4f4d\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u53ea\u597d\u5e94\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u662f\u6211\uff01\u201d \u3000\u3000\u201c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u51fa\u6765\u4e00\u770b\u4fbf\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u201c\u90a3\u4e00\u5b9a\u662f\u8001\u5ba2\u4eba\u4e86\uff0c\u600e\u4e48\u58f0\u97f3\u5374\u964c\u751f\u5f97\u5f88\uff0c\u662f\u4e0d\u662f\u6211\u7684\u8033\u6735\u6709\u6bdb\u75c5\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\uff03\u672a\u518d\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u90a3\u5a07\u6ef4\u6ef4\u67d4\u9761\u9761\u7684\u58f0\u97f3\u53c8\u9053\uff1a\u201c\u8010\u5fc3\u7684\u7b49\u6211\u4e00\u4e0b\uff0c\u58f6\u91cc\u7684\u8336\u662f\u521a\u6ce1\u7684\uff0c\u4f60\u81ea\u5df1\u5012\u6765\u559d \u5427\uff01\u201d\u5c0f\u71d5\u5b50\u7981\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4f60\u5728\u91cc\u9762\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u91cc\u9762\u7684\u5973\u4eba\u9053\uff1a\u201c\u522b\u95ee\u90a3\u4e48\u591a\uff0c\u6211\u5f88\u5feb\u5c31\u4f1a\u51fa\u6765\u62db\u5f85\u4f60\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6697\u5fd6\uff1a\u83ab\u975e\u8fd9\u5973\u4eba\u6b63\u5728\u91cc\u9762\u5f04\u866b\uff1f\u4ed6\u771f\u60f3\u51b2\u8fdb\u53bb\u67e5\u770b\u4e00\u4e0b\u7a76\u7adf\uff0c\u4f46\u53c8\u89c9\u5f97\u8fd9\u4e48\u505a \u5f88\u53ef\u80fd\u4f1a\u56e0\u5c0f\u5931\u5927\uff0c\u82e5\u8bef\u4e86\u5927\u4e8b\uff0c\u5c82\u4e0d\u5f04\u5de7\u6210\u62d9\uff01 \u3000\u3000\u770b\u6765\u53ea\u6709\u8010\u5fc3\u7684\u7b49\u5f85\u4e86\u3002 \u3000\u3000\u8db3\u8db3\u8fc7\u4e86\u76cf\u8336\u5de5\u592b\uff0c\u8fd8\u662f\u4e0d\u89c1\u91cc\u9762\u7684\u59d1\u5a18\u51fa\u6765\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u7ec8\u4e8e\u8010\u4e0d\u4f4f\u518d\u95ee\u9053\uff1a\u201c\u4f60\u600e\u4e48\u8fd8\u4e0d\u51fa\u6765\uff1f\u201d \u3000\u3000\u91cc\u9762\u5e94\u9053\uff1a\u201c\u9a6c\u4e0a\u5c31\u597d\u3002\u65e2\u7136\u662f\u8001\u5ba2\u4eba\uff0c\u60a8\u5c31\u591a\u7b49\u4e00\u4f1a\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\uff01\u201d \u3000\u3000\u8bf4\u7740\uff0c\u4eba\u5df2\u5411\u5916\u8d70\u6765\u3002 \u3000\u3000\u53ea\u542c\u5979\u8fb9\u8d70\u8fb9\u9053\uff1a\u201c\u4eba\u5bb6\u5230\u73b0\u5728\u8fd8\u6ca1\u542c\u51fa\u60a8\u662f\u8c01\uff1f\u53ea\u597d\u51fa\u6765\u4eb2\u773c\u770b\u770b\u4e86\u3002\u201d \u3000\u3000\u63a5\u7740\uff0c\u5367\u623f\u95e8\u5e18\u4e00\u6380\uff0c\u626d\u8170\u6446\u81c0\u7684\u8d70\u51fa\u4e00\u4e2a\u5343\u5a07\u767e\u5a9a\u7684\u4fcf\u4f73\u4eba\u6765\u3002 \u3000\u3000\u8fd9\u5973\u4eba\u770b\u6765\u679c\u7136\u53ea\u6709\u4e8c\u5341\u5de6\u53f3\u5e74\u7eaa\uff0c\u4e00\u8eab\u7ec6\u76ae\u5ae9\u8089\uff0c\u660e\u7738\u5584\u7750\uff0c\u987e\u76fc\u751f\u59ff\uff0c\u9690\u7ea6\u53ef\u89c1\u773c \u795e\u4e2d\u4f3c\u6709\u4e00\u80a1\u5996\u6c14\u3002 \u3000\u3000\u504f\u504f\u8fd9\u79cd\u201c\u5996\u6c14\u201d\u662f\u4e00\u822c\u7537\u4eba\uff0c\u5c24\u5176\u662f\u8fd9\u79cd\u5730\u65b9\u6765\u7684\u5bfb\u82b3\u5ba2\u6240\u559c\u6b22\u7684\u3002 \u3000\u3000\u53ea\u542c\u8fd9\u5973\u4eba\u4e00\u51fa\u95e8\u4fbf\u554a\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u539f\u6765\u662f\u4f4d\u65b0\u5230\u7684\u516c\u5b50\uff0c\u96be\u602a\u4eba\u5bb6\u4ece\u58f0\u97f3\u91cc\u4e2d\u672a\u542c\u51fa \u662f\u8c01\uff1f\u516c\u5b50\u4e3a\u4ec0\u4e48\u4e0d\u5148\u5230\u82b1\u5385\uff0c\u5374\u76f4\u63a5\u5230\u8fd9\u91cc\u6765\u4e86\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6781\u529b\u88c5\u51fa\u662f\u6b22\u573a\u4e2d\u8001\u624b\u7684\u6a21\u6837\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u5df2\u7ecf\u5230\u8fc7\u82b1\u5385\uff0c\u662f\u90a3\u4f4d\u53eb\u5c24\u4e09\u7684\u4ecb\u7ecd \u6211\u5230\u8fd9\u91cc\u6765\u7684\u3002\u201d \u3000\u3000\u59d1\u5a18\u7728\u7740\u4e00\u5bf9\u6c34\u6c6a\u6c6a\u7684\u5a9a\u773c\u9053\uff1a\u201c\u5c24\u4e09\u90fd\u5bf9\u516c\u5b50\u8bf4\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4ed6\u8bf4\u4f60\u662f\u8fd9\u91cc\u6700\u7f8e\u7684\u59d1\u5a18\uff0c\u4e5f\u662f\u6700\u4f1a\u62db\u5f85\u5ba2\u4eba\u7684\u59d1\u5a18\u3002\u201d \u3000\u3000\u201c\u90a3\u60a8\u5c31\u8be5\u8981\u5c24\u4e09\u5148\u628a\u6211\u53eb\u5230\u82b1\u5385\u91cc\u770b\u770b\u624d\u5bf9\u3002\u201d \u3000\u3000\u201c\u6211\u76f8\u4fe1\u5c24\u4e09\u8bf4\u7684\u4e0d\u4f1a\u662f\u5047\u8bdd\uff0c\u540c\u65f6\u5c24\u4e09\u8bf4\u59d1\u5a18\u7684\u5ba2\u4eba\u5f88\u591a\uff0c\u4e0d\u4e00\u5b9a\u80fd\u53eb\u5f97\u5230\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u4f60\u5c31\u81ea\u52a8\u6765\u4e86\uff01\u201d \u3000\u3000\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u60a8\u770b\u6211\u548c\u5c24\u4e09\u8bf4\u7684\u662f\u4e0d\u662f\u4e00\u6837\uff1f\u201d \u3000\u3000\u201c\u6bd4\u5c24\u4e09\u8bf4\u7684\u66f4\u80dc\u5341\u5206\u3002\u201d \u3000\u3000\u201c\u76f4\u7684\uff1f\u201d \u3000\u3000\u201c\u5728\u4e0b\u4f55\u5fc5\u6545\u610f\u8ba8\u597d\u59d1\u5a18\uff01\u201d \u3000\u3000\u59d1\u5a18\u7528\u90a3\u52fe\u9b42\u6444\u9b44\u7684\u7738\u5b50\u659c\u7768\u4e86\u5c0f\u71d5\u5b50\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u4f4d\u516c\u5b50\u4e5f\u662f\u6211\u63a5\u8fc7\u7684\u5ba2\u4eba\u4e2d\u6700 \u82f1\u4fca\u6f47\u6d12\u7684\u4e00\u4f4d\uff0c\u54b1\u4eec\u8be5\u662f\u5929\u914d\u4e00\u5bf9\u3001\u5730\u8bbe\u4e00\u53cc\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6ee1\u8eab\u4e0d\u81ea\u5728\u7684\u8038\u4e86\u8038\u80a9\u9053\uff1a\u201c\u6211\u662f\u4e16\u4e0a\u6700\u4e11\u964b\u7684\u7537\u4eba\u3002\u201d \u3000\u3000\u59d1\u5a18\u54af\u54af\u7b11\u9053\uff1a\u201c\u968f\u4fbf\u4f60\u600e\u4e48\u8bf4\uff0c\u53ea\u8981\u6211\u559c\u6b22\u5c31\u597d\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u626d\u52a8\u7740\u8170\u80a2\uff0c\u8d70\u5230\u95e8\u53e3\uff0c\u628a\u5916\u9762\u7684\u95e8\u5173\u597d\u4e0a\u4e86\u95e9\u3002 \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u8981\u5173\u95e8\uff1f\u201d \u3000\u3000\u201c\u5947\u602a\u4e86\uff1f\u8fde\u8fd9\u4e2a\u4e5f\u8981\u95ee\u5417\uff1f\u5173\u95e8\u4fbf\u8868\u793a\u91cc\u9762\u5df2\u7ecf\u6709\u4e86\u5ba2\u4eba\uff0c\u522b\u7684\u5ba2\u4eba\u5c31\u4e0d\u53ef\u4ee5\u518d\u5f80\u91cc \u95ef\u4e86\u3002\u201d \u3000\u3000\u201c\u54b1\u4eec\u5750\u5728\u5ba2\u5385\u91cc\uff0c\u522b\u4eba\u770b\u5f97\u5230\uff0c\u7167\u6837\u4e5f\u4e0d\u53ef\u80fd\u518d\u8fdb\u6765\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u662f\u5728\u5ba2\u5385\u91cc\uff0c\u5f85\u4f1a\u513f\u514d\u4e0d\u4e86\u5c31\u8981\u8fdb\u5367\u5ba4\uff0c\u96be\u9053\u4e5f\u4e0d\u6015\u522b\u4eba\u95ef\u8fdb\u6765\u5417\uff1f\u201d \u3000\u3000\u201c\u65b9\u624d\u59d1\u5a18\u5728\u5367\u5ba4\uff0c\u4e3a\u4ec0\u4e48\u6ca1\u5173\u95e8\u5462\uff1f\u201d \u3000\u3000\u201c\u90a3\u662f\u56e0\u4e3a\u53ea\u6211\u4e00\u4e2a\u4eba\u5728\u5367\u5ba4\uff0c\u5982\u679c\u521a\u624d\u5367\u5ba4\u91cc\u4e5f\u6709\u5ba2\u4eba\uff0c\u53c8\u600e\u53ef\u80fd\u4e0d\u5173\u95e8\uff1f\u201d \u3000\u3000\u59d1\u5a18\u8bf4\u7740\uff0c\u6765\u5230\u5c0f\u71d5\u5b50\u8ddf\u524d\uff0c\u731b\u7136\u8f6c\u8fc7\u8eab\uff0c\u7adf\u4e00\u4e0b\u5b50\u5750\u5728\u5c0f\u71d5\u4e8e\u5927\u817f\u4e0a\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u54ea\u91cc\u6709\u8fc7\u8fd9\u79cd\u7ecf\u9a8c\uff1f\u4f55\u51b5\u6295\u6000\u9001\u62b1\u7684\u53c8\u662f\u8fd9\u822c\u8f6f\u7389\u6e29\u9999\u7684\u5973\u4eba\uff0c\u987f\u65f6\u4f53\u5185\u6709\u5982\u901a \u8fc7\u4e86\u7535\u6d41\u4e00\u822c\uff0c\u5927\u6709\u96be\u4ee5\u81ea\u5236\u4e4b\u6982\u3002 \u3000\u3000\u597d\u5728\u4ed6\u7acb\u523b\u60f3\u5230\u5bf9\u65b9\u662f\u5426\u4f1a\u8d81\u8fd9\u673a\u4f1a\u5bf9\u81ea\u5df1\u4e0b\u866b\uff0c\u6709\u4e86\u8fd9\u4e00\u8b66\u60d5\uff0c\u7ec8\u4e8e\u53c8\u6062\u590d\u4e86\u9547\u5b9a\u3002 \u3000\u3000\u59d1\u5a18\u6545\u610f\u628a\u5c41\u80a1\u5750\u5728\u5c0f\u71d5\u5b50\u5927\u817f\u4e0a\u626d\u78e8\u4e86\u51e0\u4e0b\uff0c\u624d\u5403\u5403\u7b11\u9053\uff1a\u201c\u516c\u5b50\uff0c\u770b\u6837\u5b50\u4f60\u4e00\u5b9a\u5f88 \u5c11\u63a5\u8fd1\u8fc7\u5973\u4eba\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u5168\u8eab\u4e0d\u81ea\u5728\u7684\u8f7b\u54b3\u4e86\u58f0\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u5462\uff1f\u201d \u3000\u3000\u201c\u5982\u679c\u4f60\u7ecf\u5e38\u63a5\u8fd1\u5973\u4eba\uff0c\u73b0\u5728\u4e00\u5b9a\u4f1a\u4f38\u5f00\u53cc\u81c2\u628a\u6211\u62b1\u4f4f\uff0c\u800c\u73b0\u5728\u4f60\u5374\u52a8\u4e5f\u4e0d\u52a8\uff0c\u8ba9\u4eba\u5bb6 \u4e00\u70b9\u5b89\u5168\u611f\u4e5f\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u5c31\u56e0\u4e3a\u6211\u4e0d\u52a8\uff0c\u6240\u4ee5\u6211\u76f8\u4fe1\u4f60\u7edd\u5bf9\u4e0d\u4f1a\u4ece\u6211\u8eab\u4e0a\u6454\u4e0b\u6765\uff0c\u8fd9\u624d\u662f\u771f\u6b63\u7684\u8ba9\u4f60\u6709\u5b89\u5168 \u611f\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u4e0d\u662f\u4e0d\u559c\u6b22\u6211\uff1f\u201d \u3000\u3000\u201c\u600e\u4e48\u4f1a\u5462\uff1f\u201d \u3000\u3000\u59d1\u5a18\u7acb\u523b\u626d\u8fc7\u8eab\u6765\uff0c\u50cf\u86c7\u4e00\u822c\u5c06\u5c0f\u71d5\u5b50\u7d27\u7d27\u6402\u4f4f\uff0c\u63a5\u7740\u5c06\u706b\u7ea2\u7684\u6a31\u5507\u51d1\u4e0a\u4e86\u5c0f\u71d5\u5b50\u7684 \u5634\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u5fc5\u987b\u8d8a\u53d1\u63d0\u9ad8\u8b66\u89c9\uff0c\u4ed6\u6000\u7591\u5bf9\u65b9\u5f88\u53ef\u80fd\u4f1a\u501f\u53e3\u8154\u4f20\u9012\u866b\u6bd2\uff0c\u4e00\u9762\u6025\u6025\u8f6c\u5934\uff0c\u4e00\u9762 \u7528\u529b\u628a\u5bf9\u65b9\u63a8\u5f00\u3002 \u3000\u3000\u59d1\u5a18\u53ea\u597d\u5750\u5230\u53e6\u4e00\u5f20\u6905\u5b50\u4e0a\uff0c\u5403\u60ca\u7684\u9053\uff1a\u201c\u770b\u516c\u5b50\u50cf\u4e2a\u6587\u5f31\u4e66\u751f\uff0c\u600e\u4e48\u4f1a\u6709\u8fd9\u4e48\u5927\u7684\u529b \u6c14\uff1f\u201d \u3000\u3000\u201c\u522b\u8bf4\u8fd9\u4e9b\u4e86\uff0c\u54b1\u4eec\u8c08\u70b9\u522b\u7684\u597d\u5417\uff1f\u201d \u3000\u3000\u59d1\u5a18\u4fa7\u8eab\u5012\u4e86\u4e00\u676f\u8336\uff0c\u9012\u7ed9\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u5148\u559d\u53e3\u8336\u6da6\u6da6\u5589\u5499\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u63a5\u8fc7\u6765\uff0c\u518d\u653e\u56de\u8336\u51e0\u3002 \u3000\u3000\u4ed6\u73b0\u5728\u5fc5\u987b\u5904\u5904\u5c0f\u5fc3\uff0c\u51e0\u4e4e\u5df2\u5230\u4e86\u7591\u795e\u7591\u9b3c\u7684\u7a0b\u5ea6\uff0c\u552f\u6050\u88ab\u5bf9\u65b9\u5728\u6697\u4e2d\u52a8\u4e86\u624b\u811a\u3002 \u3000\u3000\u201c\u8fd9\u662f\u6700\u597d\u7684\u8336\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u559d\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u6e34\u3002\u201d \u3000\u3000\u201c\u771f\u4e0d\u6e34\u8fd8\u662f\u5047\u4e0d\u6e34\uff1f\u201d \u3000\u3000\u201c\u6211\u6709\u5fc5\u8981\u9a97\u59d1\u5a18\u5417\uff1f\u201d \u3000\u3000\u201c\u597d\uff0c\u90a3\u5c31\u5f85\u4f1a\u513f\u518d\u559d\u5427\uff01\u201d \u3000\u3000\u201c\u8fd8\u6ca1\u8bf7\u95ee\u59d1\u5a18\u8d35\u59d3\u82b3\u540d\uff1f\u201d \u3000\u3000\u201c\u5ba2\u4eba\u5230\u8fd9\u91cc\u6765\uff0c\u53ea\u77e5\u540d\u5b57\u5c31\u597d\uff0c\u4ece\u6ca1\u6709\u4eba\u95ee\u59d3\u7684\u3002\u201d \u3000\u3000\u201c\u6211\u5c31\u8bf7\u95ee\u82b3\u540d\uff01\u201d \u3000\u3000\u201c\u6211\u53eb\u6842\u82b1\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6697\u5fd6\u9053\uff1a\u8fd9\u4e48\u770b\u6765\uff0c\u8001\u5996\u5a46\u8bf4\u7684\u5927\u6982\u4e0d\u4f1a\u6709\u5047\uff0c\u8001\u5996\u5a46\u8bf4\u5979\u53eb\u6842\u98d8\u9999\uff0c\u5979\u81ea\u5df1\u8bf4 \u53eb\u6842\u82b1\uff0c\u4e0d\u53ef\u80fd\u662f\u5de7\u5408\u5427\uff1f\u201c\u4e0d\u8fc7\u4ed6\u8fd8\u662f\u642d\u8baa\u7740\u95ee\u9053\uff1a\u201d\u6842\u82b1\u8fd9\u540d\u5b57\u5f88\u96c5\uff0c\u662f\u5426\u59d1\u5a18\u6765\u5230\u8fd9 \u91cc\u4ee5\u540e\u624d\u53d6\u7684\u82b1\u540d\u5462\uff1f\u201c \u3000\u3000\u6842\u82b1\u970e\u4e86\u970e\u7738\u5b50\uff0c\u4e0d\u7b54\u53cd\u95ee\u9053\uff1a\u201c\u516c\u5b50\u4e3a\u4ec0\u4e48\u8981\u95ee\u8fd9\u4e2a\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u987a\u53e3\u9053\uff1a\u201c\u6211\u89c1\u4f60\u95e8\u524d\u6709\u68f5\u6842\u6811\uff0c\u6240\u4ee5\u624d\u6709\u4e86\u8fd9\u79cd\u8054\u60f3\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u968f\u4f60\u731c\u60f3\u5427\uff0c\u53cd\u6b63\u4f60\u77e5\u9053\u6211\u53eb\u6842\u82b1\u5c31\u6210\u4e86\u3002\u201d \u3000\u3000\u201c\u59d1\u5a18\u6765\u8fd9\u91cc\u591a\u4e45\u4e86\uff1f\u201d \u3000\u3000\u201c\u65e5\u5b50\u4e0d\u957f\u4e5f\u4e0d\u77ed\u3002\u201d \u3000\u3000\u201c\u90a3\u662f\u591a\u4e45\uff1f\u201d \u3000\u3000\u201c\u516c\u5b50\u4f55\u5fc5\u8001\u95ee\u8fd9\u4e9b\u4e0e\u4f60\u4e0d\u76f8\u5e72\u7684\u4e8b\u60c5\uff0c\u4f60\u4e5f\u4e0d\u53ef\u80fd\u5a36\u6211\u4e3a\u59bb\uff0c\u7528\u4e0d\u7740\u8c03\u67e5\u5f97\u8fd9\u4e48\u6e05\u695a \u554a\uff01\u201d \u3000\u3000\u201c\u96be\u9053\u54b1\u4eec\u4e0d\u53ef\u80fd\u505a\u4e2a\u670b\u53cb\uff1f\u201d \u3000\u3000\u201c\u505a\u4e2a\u670b\u53cb\u4fbf\u600e\u6837\uff1f\u201d \u3000\u3000\u201c\u6211\u4f1a\u5e38\u5e38\u6765\u6367\u4f60\u7684\u573a\u3002\u201d \u3000\u3000\u201c\u90a3\u6211\u592a\u597d\u4e86\uff0c\u73b0\u5728\u5c31\u5230\u5367\u5ba4\u53bb\u5427\uff01\u201d \u3000\u3000\u6842\u82b1\u7acb\u523b\u7ad9\u8d77\u8eab\u6765\uff0c\u62c9\u7740\u5c0f\u71d5\u5b50\u7684\u624b\u9053\uff1a\u201c\u968f\u6211\u8fdb\u53bb\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u5e76\u672a\u8d77\u8eab\uff0c\u95ee\u9053\uff1a\u201c\u5c31\u5728\u8fd9\u91cc\u8c08\u8c08\uff0c\u4e0d\u662f\u5f88\u597d\u5417\uff1f\u201d \u3000\u3000\u201c\u54b1\u4eec\u5728\u5e8a\u4e0a\u8c08\uff0c\u4e0d\u662f\u6bd4\u8fd9\u91cc\u66f4\u8212\u670d\u5417\uff1f\u201d \u3000\u3000\u201c\u5e8a\u4e0a\u8c08\u6709\u4ec0\u4e48\u597d\uff1f\u201d \u3000\u3000\u201c\u5728\u5e8a\u4e0a\u53ef\u4ee5\u8eba\u7740\u8c08\uff0c\u8eba\u7740\u8c08\u5f53\u7136\u5f88\u8212\u670d\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u672c\u6765\u5df2\u4e0b\u5b9a\u51b3\u5fc3\uff0c\u7edd\u4e0d\u80fd\u8fdb\u5bf9\u65b9\u7684\u5367\u623f\u3002 \u3000\u3000\u4f46\u662f\uff0c\u6b64\u523b\u4ed6\u5df2\u6539\u53d8\u4e86\u4e3b\u610f\uff0c\u4ed6\u60f3\u552f\u6709\u5230\u91cc\u9762\u4ed4\u7ec6\u5730\u89c2\u5bdf\u89c2\u5bdf\uff0c\u624d\u80fd\u8fdb\u4e00\u6b65\u5730\u627e\u51fa\u86db\u4e1d \u9a6c\u8ff9\u3002 \u3000\u3000\u6b63\u6240\u8c13\u4e0d\u5165\u864e\u7a74\uff0c\u7109\u5f97\u864e\u5b50\uff1f\u60f3\u5230\u8fd9\u91cc\uff0c\u7acb\u5373\u8d77\u8eab\u968f\u7740\u5bf9\u65b9\u8fdb\u5165\u5367\u5ba4\u3002 \u3000\u3000\u5367\u623f\u5185\u5e03\u7f6e\u5f97\u522b\u6709\u65d6\u65ce\u98ce\u5149\u3002 \u3000\u3000\u9760\u5185\u58c1\u662f\u5e8a\u69bb\uff0c\u5e8a\u69bb\u4e0a\u7ee3\u8925\u9526\u88ab\uff0c\u4e0a\u65b9\u7f57\u5e10\u9ad8\u60ac\uff0c\u4e0d\u8bba\u94fa\u7684\u76d6\u7684\u3001\u906e\u7684\u63a9\u7684\uff0c\u5168\u662f\u706b\u822c \u7684\u5927\u7ea2\u989c\u8272\u3002 \u3000\u3000\u7d27\u9760\u7740\u5e8a\u69bb\u662f\u68b3\u5986\u53f0\uff0c\u5986\u53f0\u4e0a\u6446\u653e\u7740\u6570\u4e0d\u6e05\u7684\u74f6\u74f6\u7f50\u7f50\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u53ea\u611f\u5ba4\u5185\u9999\u6c14\u88ad\u4eba\uff0c\u4f46\u5374\u5f04\u4e0d\u6e05\u8fd9\u9999\u6c14\u7a76\u7adf\u662f\u4ece\u54ea\u91cc\u6765\u7684\u3002 \u3000\u3000\u4ed6\u76ee\u4e0d\u8f6c\u775b\u7684\u6ce8\u89c6\u7740\u68b3\u5986\u53f0\u4e0a\u90a3\u4e9b\u74f6\u7f50\uff0c\u5fc3\u60f3\u8bf4\u4e0d\u5b9a\u5176\u4e2d\u88c5\u6709\u866b\u7269\uff0c\u800c\u8fd9\u80a1\u6251\u9f3b\u6c81\u5fc3\u7684 \u9999\u6c14\uff0c\u5f88\u53ef\u80fd\u4e5f\u4e0e\u866b\u7269\u6709\u5173\u3002 \u3000\u3000\u6842\u82b1\u8f7b\u8f7b\u62cd\u4e86\u62cd\u5c0f\u71d5\u5b50\u80a9\u8180\u9053\uff1a\u201c\u4f60\u8001\u76ef\u7740\u68b3\u5986\u53f0\u770b\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u5b9a\u4e86\u5b9a\u795e\u9053\uff1a\u201c\u4f60\u4eec\u5973\u4eba\u7528\u7684\u4e1c\u897f\u597d\u50cf\u771f\u4e0d\u5c11\uff0c\u6211\u4ece\u524d\u4e00\u6837\u90fd\u6ca1\u89c1\u8fc7\u3002\u201d \u3000\u3000\u201c\u8981\u770b\u5f85\u4f1a\u513f\u518d\u770b\uff0c\u73b0\u5728\u4e0a\u5e8a\u8981\u7d27\u3002\u201d \u3000\u3000\u6842\u82b1\u8fb9\u8bf4\u8fb9\u4e00\u628a\u4fbf\u628a\u5c0f\u71d5\u5b50\u63a8\u5012\u5e8a\u4e0a\u3002 \u3000\u3000\u6842\u82b1\u7684\u8fd9\u4e00\u63a8\uff0c\u8d8a\u53d1\u53ef\u4ee5\u8bc1\u660e\u8001\u5996\u5a46\u5e76\u6ca1\u9a97\u4ed6\u3002 \u3000\u3000\u56e0\u4e3a\u82e5\u6842\u82b1\u53ea\u662f\u4e00\u540d\u666e\u901a\u7684\u5f31\u5973\u5b50\uff0c\u7edd\u4e0d\u53ef\u80fd\u5c06\u8eab\u5177\u4e0a\u4e58\u6b66\u529f\u7684\u5c0f\u71d5\u5b50\u63a8\u5012\u5728\u5e8a\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u8fc5\u901f\u5750\u8d77\u8eab\u6765\uff0c\u50cf\u60f3\u8d77\u4e00\u4ef6\u91cd\u5927\u4e8b\u822c\u9053\uff1a\u201c\u7cdf\u7cd5\uff01\u6211\u5fc5\u987b\u9a6c\u4e0a\u51fa\u53bb\u529e\u4e00\u4ef6 \u4e8b\u2026\u2026\u201d \u3000\u3000\u6842\u82b1\u8bb6\u7136\u95ee\u9053\uff1a\u201c\u4f60\u8981\u51fa\u53bb\u529e\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u4e00\u4ef6\u975e\u5e38\u91cd\u8981\u7684\u5927\u4e8b\uff0c\u6211\u7adf\u7136\u628a\u5b83\u5fd8\u4e86\uff01\u201d \u3000\u3000\u201c\u5230\u5e95\u662f\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u4e0e\u4f60\u65e0\u5173\uff0c\u5b9e\u5728\u6ca1\u5fc5\u8981\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u201c\u529e\u5b8c\u4e8b\u662f\u5426\u8fd8\u8981\u56de\u6765\uff1f\u201d \u3000\u3000\u201c\u6709\u4f60\u8fd9\u4e48\u597d\u7684\u59d1\u5a18\u966a\u4f34\uff0c\u6211\u5f53\u7136\u8981\u56de\u6765\uff0c\u66f4\u4f55\u51b5\u6211\u5df2\u8981\u4f60\u62db\u5f85\u4e86\u8fd9\u4e48\u4e45\uff0c\u4e5f\u4e0d\u597d\u610f\u601d \u4e0d\u56de\u6765\u3002\u201d \u3000\u3000\u201c\u4f60\u7684\u4e8b\u60c5\u8981\u529e\u591a\u4e45\uff1f\u201d \u3000\u3000\u201c\u90a3\u8981\u770b\u8fdb\u884c\u5f97\u662f\u5426\u987a\u5229\uff0c\u4e0d\u8fc7\u6211\u53ef\u4ee5\u4fdd\u8bc1\uff0c\u5728\u4e09\u66f4\u524d\u4e00\u5b9a\u53ef\u4ee5\u56de\u6765\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u6211\u7b49\u4f60\u3002\u201d \u3000\u3000\u201c\u4f60\u8fd9\u91cc\u591c\u6e21\u8d44\u662f\u591a\u5c11\uff1f\u201d \u3000\u3000\u201c\u6211\u548c\u522b\u7684\u59d1\u5a18\u4e0d\u4e00\u6837\uff0c\u522b\u7684\u59d1\u5a18\u662f\u4e09\u4e24\uff0c\u6211\u516b\u4e24\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u5e72\u8106\u8bf4\u662f\u534a\u65a4\u4e86\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u6bd4\u522b\u4eba\u4ef7\u7801\u9ad8\u5462\uff1f\u201d \u3000\u3000\u201c\u4f60\u4e0d\u662f\u8bf4\u6211\u5f88\u7f8e\u5417\uff1f\u5982\u679c\u5ba2\u4eba\u4e0d\u80af\u51fa\u8fd9\u79cd\u4ef7\u94b1\uff0c\u90a3\u5c31\u522b\u5230\u6211\u8fd9\u91cc\u6765\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u638f\u51fa\u4e00\u952d\u5341\u4e24\u91cd\u7684\u94f6\u5b50\uff0c\u653e\u5728\u68b3\u5986\u53f0\u4e0a\u9053\uff1a\u201c\u8fd9\u952d\u94f6\u5b50\u4f60\u6536\u7740\uff0c\u591a\u51fa\u6765\u7684\u8ba9\u4f60\u4e70 \u82b1\u6234\u3002\u201d \u3000\u3000\u6842\u82b1\u6536\u8d77\u94f6\u5b50\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u8981\u6765\u3002\u201d \u3000\u3000\u201c\u5f53\u7136\u8981\u6765\uff0c\u53ea\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u53ea\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4e07\u4e00\u6211\u8d76\u4e0d\u56de\u6765\uff0c\u94f6\u5b50\u4e5f\u662f\u4f60\u7684\uff0c\u53ea\u8981\u4f60\u4eca\u665a\u4e0d\u63a5\u522b\u7684\u5ba2\u4eba\u5c31\u6210\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u65e2\u7136\u6536\u4e86\u4f60\u7684\u94f6\u5b50\uff0c\u5f53\u7136\u4e0d\u4f1a\u518d\u63a5\u522b\u7684\u5ba2\u4eba\uff0c\u8fd9\u8fd8\u7528\u4ea4\u4ee3\u5417\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u8d77\u8eab\u4fbf\u5f80\u5916\u8d70\u3002 \u3000\u3000\u6842\u82b1\u8ddf\u5728\u540e\u9762\u9053\uff1a\u201c\u6211\u9001\u9001\u4f60\u3002\u201d \u3000\u3000\u201c\u4e0d\u5fc5\u9001\u4e86\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6765\u5230\u5047\u5c71\u65c1\uff0c\u8fd8\u597d\uff01\u8001\u5996\u5a46\u4ecd\u7136\u762b\u5750\u5728\u90a3\u91cc\u3002 \u3000\u3000\u4ed6\u63a2\u624b\u4e3a\u8001\u5996\u5a46\u89e3\u5f00\u9ebb\u7a74\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u56de\u5ba2\u6808\u53bb\u5427\uff01\u201d \u3000\u3000\u8001\u5996\u5a46\u5168\u8eab\u9ebb\u6728\u4e45\u4e86\uff0c\u4f38\u4e86\u4e2a\u61d2\u8170\uff0c\u53c8\u6d3b\u52a8\u6d3b\u52a8\u51e0\u4e0b\uff0c\u624d\u7ad9\u8d77\u8eab\u6765\u9053\uff1a\u201c\u4f60\u770b\u5230\u4e86\u4e9b\u4ec0 \u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u76f8\u4fe1\u4f60\u4e0d\u4f1a\u9a97\u6211\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u8981\u56de\u53bb\uff1f\u201d \u3000\u3000\u6211\u82e5\u5f3a\u884c\u5c06\u5979\u5e26\u8d70\uff0c\u4f17\u76ee\u777d\u777d\u4e4b\u4e0b\u5f53\u7136\u4e0d\u6210\uff0c\u6240\u4ee5\u5fc5\u987b\u5148\u56de\u53bb\u5411\u6c5f\u53d4\u53d4\u548c\u738b\u5927\u4eba\u8bf7\u793a\u4e00 \u4e0b\uff0c\u4e8b\u60c5\u8be5\u600e\u6837\u5904\u7f6e\uff0c\u7531\u4ed6\u4eec\u51b3\u5b9a\u3002\u201c \u3000\u3000\u201c\u4e5f\u597d\uff0c\u90a3\u5c31\u8d70\u5427\uff01\u201d \u3000\u3000\u56de\u5230\u5ba2\u6808\uff0c\u624d\u4e00\u66f4\u5929\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u548c\u738b\u5f64\u90fd\u5728\u5ba2\u6808\u7b49\u5019\u6d88\u606f\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u5f53\u5373\u628a\u7ecf\u8fc7\u53d9\u8ff0\u4e86\u4e00\u904d\u3002 \u3000\u3000\u738b\u5f64\u559c\u4e0d\u81ea\u80dc\u7684\u9053\uff1a\u201c\u597d\u6781\u4e86\uff01\u8fd9\u4e00\u6765\u54b1\u4eec\u5c31\u7528\u4e0d\u7740\u518d\u548c\u9a6c\u6587\u4e2d\u6253\u4ea4\u9053\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u5374\u7d27\u8e59\u7740\u53cc\u7709\u9053\uff1a\u201c\u8fd9\u4e8b\u6c5f\u67d0\u603b\u662f\u6709\u4e9b\u4e0d\u89e3\uff1f\u201d \u3000\u3000\u201c\u83ab\u975e\u6c5f\u5144\u6000\u7591\u90a3\u53eb\u6842\u82b1\u7684\u59d1\u5a18\u4e0d\u662f\u8001\u5996\u5a46\u7684\u5e08\u7236\uff1f\u201d \u3000\u3000\u201c\u5982\u679c\u5979\u771f\u662f\u8001\u5996\u5a46\u7684\u5e08\u7236\uff0c\u90a3\u4e48\u8fd9\u5973\u4eba\u4e0d\u8bba\u5728\u897f\u57df\u9b54\u6559\u6216\u5728\u4e07\u866b\u95e8\uff0c\u8eab\u4efd\u5730\u4f4d\u4e00\u5b9a\u90fd \u5f88\u9ad8\uff0c\u4e3a\u4ec0\u4e48\u5374\u8981\u5728\u9752\u697c\u91cc\u9b3c\u6df7\uff1f\u201d \u3000\u3000\u201c\u6c5f\u5144\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u6000\u7591\u8fd9\u53ef\u80fd\u662f\u9a6c\u6587\u4e2d\u7684\u6709\u610f\u5b89\u6392\uff0c\u4f46\u9a6c\u6587\u4e2d\u8eab\u4e3a\u6cb3\u5357\u5de1\u629a\uff0c\u629a\u7f72\u53c8\u662f\u5728\u5f00\u5c01\uff0c\u4ed6\u628a \u8fd9\u79cd\u5973\u4eba\u5b89\u6392\u5728\u9752\u697c\u6563\u64ad\u866b\u6bd2\uff0c\u53c8\u662f\u4ec0\u4e48\u7528\u5fc3\u5462\uff1f\u201d \u3000\u3000\u738b\u5f64\u4e0d\u89c9\u4e5f\u76b1\u8d77\u7709\u5934\u9053\uff1a\u201c\u82e5\u975e\u6c5f\u5144\u63d0\u8d77\uff0c\u5144\u5f1f\u8fd8\u771f\u6ca1\u60f3\u5230\u8fd9\u65b9\u9762\u53bb\uff0c\u9a6c\u6587\u4e2d\u7a76\u7adf\u7528\u5fc3 \u4f55\u5728\uff0c\u7684\u786e\u4ee4\u4eba\u8d39\u89e3\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u8fd9\u4e9b\u4e8b\u6682\u65f6\u7528\u4e0d\u7740\u591a\u60f3\uff0c\u5982\u4f55\u80fd\u8ba9\u90a3\u4f4d\u53eb\u6842\u82b1\u7684\u59d1\u5a18\u66ff\u4e09\u516c\u4e3b\u7597 \u597d\u866b\u6bd2\uff0c\u8fd9\u624d\u662f\u6700\u91cd\u8981\u7684\u3002\u201d \u3000\u3000\u201c\u6c5f\u5144\u4ee5\u4e3a\u8be5\u7528\u4ec0\u4e48\u529e\u6cd5\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u76ee\u6ce8\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u4f60\u82e5\u80fd\u628a\u90a3\u59d1\u5a18\u8bf1\u5230\u5ba2\u6808\u6765\uff0c\u4fbf\u7b97\u5927\u529f\u544a\u6210\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u6211\u4e5f\u60f3\u5230\u8be5\u8fd9\u4e48\u505a\uff0c\u4f46\u53c8\u62c5\u5fc3\u64cd\u4e4b\u8fc7\u6025\uff0c\u4f1a\u5f04\u5de7\u6210\u62d9\uff1b\u4e00\u65e6\u628a\u4e8b\u60c5\u5f04\u7838\uff0c \u53cd\u800c\u4e0d\u597d\u6536\u62fe\uff0c\u597d\u5728\u8fd9\u4e8b\u4e5f\u7528\u4e0d\u7740\u592a\u6025\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4e5f\u6709\u9053\u7406\uff0c\u4eca\u5929\u592a\u665a\u4e86\uff0c\u4e8b\u60c5\u5c31\u660e\u5929\u8fdb\u884c\u5427\uff01\u201d \u3000\u3000\u738b\u5f64\u95ee\u9053\uff1a\u201c\u6c5f\u5144\u51c6\u5907\u660e\u5929\u5982\u4f55\u8fdb\u884c\uff1f\u201d \u3000\u3000\u201c\u51e1\u5c5e\u7ea2\u59d1\u5a18\uff0c\u5e94\u53ec\u5916\u51fa\u966a\u5ba2\u662f\u5e38\u4e8b\uff0c\u660e\u5929\u54b1\u4eec\u6446\u4e00\u684c\u9152\u5e2d\uff0c\u628a\u5979\u53ec\u51fa\u6765\uff0c\u95ee\u9898\u4e0d\u5c31\u89e3 \u51b3\u4e86\u5417\uff1f\u201d \u3000\u3000\u201c\u53ea\u662f\u82e5\u76f4\u63a5\u628a\u5979\u53ec\u5230\u54b1\u4eec\u8fd9\u91cc\uff0c\u6050\u6015\u4f20\u51fa\u53bb\u4e0d\u592a\u597d\u542c\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u628a\u9152\u5e2d\u6446\u5728\u53e6\u5916\u7684\u5730\u65b9\uff0c\u6563\u5e2d\u540e\u518d\u8bbe\u6cd5\u628a\u5979\u5e26\u6765\u3002\u201d \u3000\u3000\u201c\u5979\u82e5\u6b7b\u4e0d\u627f\u8ba4\u7cbe\u64c5\u866b\u672f\u5462\uff1f\u201d \u3000\u3000\u201c\u5230\u65f6\u5019\u6c5f\u67d0\u81ea\u6709\u529e\u6cd5\uff0c\u66f4\u4f55\u51b5\u6709\u8001\u5996\u5a46\u4f5c\u8bc1\uff0c\u5979\u4e0d\u627f\u8ba4\u4e5f\u4e0d\u6210\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u63a5\u7740\u53c8\u5bf9\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u4f60\u5c0f\u5b50\u8f9b\u82e6\u4e86\uff0c\u4eca\u665a\u8bbe\u4e8b\u4e86\uff0c\u5c31\u56de\u623f\u7761\u5427\uff01\u201d \u3000\u3000\u6b21\u65e5\uff0c\u738b\u5f64\u6d3e\u4eba\u5728\u4e00\u5bb6\u9152\u697c\u8ba2\u4e86\u4e00\u684c\u9152\u5e2d\u3002 \u3000\u3000\u4e3a\u4e86\u4e0d\u60f9\u4eba\u8033\u76ee\uff0c\u8d74\u5bb4\u7684\u7531\u97e9\u6d9b\u3001\u9648\u5b8f\u3001\u8d75\u4fdd\u4ee5\u53ca\u738b\u91cd\u5c71\u7b49\u4eba\u53c2\u52a0\uff0c\u738b\u5f64\u548c\u6c5f\u5e72\u91cc\u90fd\u4e0d \u51c6\u5907\u9732\u9762\u3002 \u3000\u3000\u81f3\u4e8e\u5c0f\u71d5\u5b50\uff0c\u5219\u8d1f\u8d23\u5230\u201c\u8fce\u6625\u9601\u201d\u53eb\u59d1\u5a18\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u5df2\u4ea4\u4ee3\u8fc7\u5c0f\u71d5\u5b50\uff0c\u59d1\u5a18\u5e94\u53ec\u5916\u51fa\u966a\u5ba2\uff0c\u5fc5\u987b\u5148\u5411\u8001\u9e28\u6253\u62db\u547c\uff0c\u53ea\u8981\u94f6\u5b50\u51fa\u5f97\u591f\uff0c \u8001\u9e28\u6ca1\u6709\u4e0d\u540c\u610f\u7684\u3002 \u3000\u3000\u81f3\u4e8e\u6842\u82b1\u65b9\u9762\uff0c\u5c0f\u71d5\u5b50\u81ea\u4fe1\u6709\u7edd\u5bf9\u7684\u628a\u63e1\u3002 \u3000\u3000\u51fa\u95e8\u524d\uff0c\u5c0f\u71d5\u5b50\u7279\u5730\u53c8\u6362\u4e86\u4e00\u8eab\u5d2d\u65b0\u8863\u670d\u3002\u4ed6\u672c\u6765\u5c31\u4eea\u8868\u51fa\u4f17\uff0c\u8fd9\u4e00\u6765\u8d75\u53d1\u50cf\u4e2a\u8d35\u516c\u5b50 \u4e86\u3002 \u3000\u3000\u53e6\u5916\uff0c\u5e76\u5728\u8857\u4e0a\u96c7\u4e86\u4e00\u8f86\u8c6a\u534e\u9a6c\u8f66\uff0c\u505c\u5728\u201c\u8fce\u6625\u9601\u201d\u95e8\u53e3\u3002 \u3000\u3000\u8fd9\u4e5f\u662f\u6c5f\u5343\u91cc\u5429\u5490\u4ed6\u8981\u5982\u6b64\u505a\u7684\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u552f\u6709\u5982\u6b64\uff0c\u624d\u80fd\u663e\u51fa\u8eab\u4ef7\u4e0d\u51e1\uff1b\u5982\u679c\u5e26\u7740\u59d1\u5a18\uff0c\u4e00\u8def\u8d70\u5230\u9152\u697c\uff0c\u53ea\u6015\u8fde\u59d1\u5a18\u4e5f\u6ca1 \u9762\u5b50\u3002 \u3000\u3000\u73b0\u5728\u662f\u8fd1\u5348\u65f6\u5206\uff0c\u201c\u8fce\u6625\u9601\u201d\u5185\u5ba2\u4eba\u591a\u672a\u4e0a\u95e8\uff0c\u8fc7\u591c\u7684\u5ba2\u4eba\u4e5f\u90fd\u672a\u53bb\uff0c\u81ea\u7136\u663e\u5f97\u51b7\u51b7\u6e05 \u6e05\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u56e0\u4e3a\u5df2\u6709\u628a\u63e1\u6842\u82b1\u65b9\u9762\u7edd\u5bf9\u8bf7\u5f97\u52a8\uff0c\u4fbf\u51b3\u5b9a\u76f4\u63a5\u5230\u82b1\u5385\u53bb\u62db\u547c\u9f9f\u5974\u627e\u8001\u9e28\u5b50\u6765 \u89c1\u3002 \u3000\u3000\u82e5\u5148\u5230\u6842\u82b1\u90a3\u91cc\uff0c\u6842\u82b1\u4e00\u7ea0\u7f20\uff0c\u53cd\u800c\u8981\u803d\u8bef\u4e0d\u5c11\u65f6\u95f4\uff0c\u4e0d\u4f46\u803d\u8bef\u4e86\u9152\u5e2d\u65f6\u95f4\uff0c\u8fde\u90a3\u9a6c\u8f66 \u592b\u4e5f\u8981\u7b49\u5f97\u4e0d\u8010\u70e6\u3002 \u3000\u3000\u5f53\u4ed6\u767b\u4e0a\u82b1\u5385\u4e0d\u4e45\uff0c\u5c24\u4e09\u4fbf\u5feb\u6b65\u8ddf\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u4e00\u642d\u773c\uff0c\u4fbf\u770b\u51fa\u5c24\u4e09\u795e\u8272\u6709\u5f02\uff0c\u53ea\u662f\u5374\u53c8\u4e0d\u4fbf\u8be2\u95ee\u3002 \u3000\u3000\u5c24\u4e09\u8bf7\u5c0f\u71d5\u5b50\u5750\u4e0b\uff0c\u5012\u4e86\u676f\u8336\u9012\u8fc7\u6765\u9053\uff1a\u201c\u516c\u5b50\u8fd9\u4e48\u65e9\u5c31\u6765\uff0c\u4e00\u5b9a\u6709\u4ec0\u4e48\u8981\u7d27\u7684\u4e8b\u60c5 \u5427\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u628a\u4f60\u4eec\u8001\u677f\u5a18\u8bf7\u6765\uff01\u201d \u3000\u3000\u5c24\u4e09\u970e\u4e86\u970e\u773c\u9053\uff1a\u201c\u516c\u5b50\u6709\u4ec0\u4e48\u4e8b\u8981\u627e\u6211\u4eec\u8001\u677f\u5a18\uff1f\u201d \u3000\u3000\u201c\u5979\u6765\u4e86\uff0c\u6211\u81ea\u7136\u4f1a\u5bf9\u5979\u8bb2\u3002\u201d \u3000\u3000\u201c\u5f88\u62b1\u6b49\uff0c\u8001\u677f\u5a18\u73b0\u5728\u5fd9\u5f97\u56e2\u56e2\u8f6c\uff0c\u53ea\u6015\u62bd\u4e0d\u51fa\u65f6\u95f4\u6765\u89c1\u5ba2\u4eba\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u6839\u672c\u6ca1\u5ba2\u4eba\uff0c\u5979\u5fd9\u4e9b\u4ec0\u4e48\uff1f\u6211\u6709\u8981\u7d27\u7684\u4e8b\u5fc5\u987b\u8981\u5979\u9a6c\u4e0a\u6765\u89c1\u3002\u201d \u3000\u3000\u201c\u516c\u5b50\u5230\u5e95\u627e\u5979\u6709\u4ec0\u4e48\u4e8b\uff1f\u5148\u544a\u8bc9\u5c0f\u7684\u4e5f\u662f\u4e00\u6837\u3002\u201d \u3000\u3000\u201c\u6211\u51c6\u5907\u627e\u4f4d\u59d1\u5a18\u5916\u51fa\u966a\u9152\uff0c\u94f6\u5b50\u53ef\u4ee5\u52a0\u500d\u3002\u201d \u3000\u3000\u201c\u59d1\u5a18\u51fa\u5916\u5e94\u53ec\u662f\u5e38\u4e8b\uff0c\u6839\u672c\u4e0d\u5fc5\u7ecf\u8fc7\u8001\u677f\u5a18\uff0c\u7531\u5c0f\u7684\u53bb\u901a\u77e5\u59d1\u5a18\u5c31\u6210\u4e86\uff0c\u516c\u5b50\u53eb\u7684\u662f \u54ea\u4e00\u4f4d\uff1f\u201d \u3000\u3000\u201c\u6211\u51c6\u5907\u53eb\u6842\u82b1\u59d1\u5a18\u53bb\u3002\u201d \u3000\u3000\u5c24\u4e09\u4e0d\u89c9\u795e\u8272\u4e00\u53d8\uff0c\u987f\u4e86\u987f\u9053\uff1a\u201c\u516c\u5b50\u53ef\u4e0d\u53ef\u4ee5\u6362\u4e00\u4f4d\u59d1\u5a18\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u8bed\u6c14\u575a\u5b9a\u7684\u9053\uff1a\u201c\u6211\u8981\u7684\u5c31\u662f\u5979\uff0c\u600e\u4e48\u53ef\u4ee5\u6362\u5462\uff1f\u201d \u3000\u3000\u201c\u6842\u82b1\u5979\u2026\u2026\u201d \u3000\u3000\u201c\u5979\u600e\u4e48\u4e86\uff1f\u662f\u5426\u8fd8\u6ca1\u8d77\u5e8a\uff1f\u201d \u3000\u3000\u201c\u5979\u2026\u2026\u5979\u75c5\u4e86\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u987f\u65f6\u50cf\u5f53\u5934\u88ab\u6572\u4e86\u4e00\u68d2\uff0c\u54a6\u4e86\u58f0\u9053\uff1a\u201c\u96be\u9053\u5c31\u4e0d\u80fd\u5e26\u75c5\u5e94\u53ec\u5417\uff1f\u6211\u60f3\u5979\u5c31\u662f\u6709 \u75c5\uff0c\u4e5f\u4e0d\u4f1a\u5f88\u91cd\u3002\u201d \u3000\u3000\u5c24\u4e09\u54a7\u54a7\u5634\u9053\uff1a\u201c\u516c\u5b50\u600e\u77e5\u5979\u4e0d\u4f1a\u75c5\u5f97\u5f88\u91cd\uff1f\u201d \u3000\u3000\u201c\u5b9e\u5bf9\u4f60\u8bf4\u5427\uff01\u6628\u5929\u6211\u548c\u90a3\u4f4d\u8001\u5144\u79bb\u5f00\u8fd9\u91cc\u540e\uff0c\u66fe\u5230\u8fc7\u5979\u7684\u623f\u5b50\uff0c\u90a3\u65f6\u5979\u8fd8\u597d\u597d\u7684\uff0c\u600e \u53ef\u80fd\u8fd9\u4e48\u5feb\u5c31\u5f97\u4e86\u91cd\u75c5\u5462\uff1f\u201d \u3000\u3000\u201c\u90a3\u662f\u8bf4\u516c\u5b50\u548c\u5979\u6628\u665a\u624d\u8ba4\u8bc6\u7684\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u521a\u8ba4\u8bc6\uff0c\u4f55\u5fc5\u4e00\u5b9a\u8981\u5979\u5e94\u53ec\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u770b\u4e0a\u4e86\u5979\u3002\u201d \u3000\u3000\u5c24\u4e09\u663e\u51fa\u4e00\u526f\u65e0\u5948\u6a21\u6837\uff0c\u5374\u4e0d\u77e5\u8be5\u518d\u8bf4\u4ec0\u4e48\u597d\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u7ad9\u8d77\u8eab\u9053\uff1a\u201c\u4f60\u5982\u679c\u62c5\u5fc3\u53eb\u4e0d\u52a8\u5979\uff0c\u90a3\u5c31\u7531\u6211\u81ea\u5df1\u53bb\u8ddf\u5979\u8bf4\u597d\u4e86\uff01\u201d \u3000\u3000\u5c24\u4e09\u6025\u6025\u4e00\u628a\u62c9\u4f4f\u9053\uff1a\u201c\u516c\u5b50\u4e0d\u5fc5\u770b\u4e86\uff0c\u6842\u82b1\u5979\u2026\u2026\u5979\u4e0d\u5728\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u554a\u4e86\u58f0\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u5979\u4e0d\u5728\uff1f\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u5c24\u4e09\u987f\u4e86\u987f\u9053\uff1a\u201c\u88ab\u2026\u2026\u88ab\u5ba2\u4eba\u53ec\u51fa\u53bb\u4e86\uff01\u201d \u3000\u3000\u201c\u88ab\u53ec\u5230\u54ea\u91cc\u53bb\uff1f\u90a3\u5ba2\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u5c0f\u7684\u53ea\u77e5\u5979\u5df2\u5e94\u53ec\u5916\u51fa\uff0c\u5e76\u4e0d\u6e05\u695a\u88ab\u53ec\u5230\u54ea\u91cc\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6709\u5982\u88ab\u6d47\u4e86\u4e00\u76c6\u51b7\u6c34\uff0c\u4e8b\u60c5\u600e\u4f1a\u8fd9\u4e48\u5de7\u5462\uff1f\u201c\u867d\u7136\uff0c\u6842\u82b1\u5e94\u53ec\u540e\u4ecd\u4f1a\u56de\u6765\uff0c\u4f46\u81ea \u5df1\u4e00\u65b9\uff0c\u5374\u767d\u767d\u6d6a\u8d39\u4e86\u4e00\u684c\u9152\u98df\uff0c\u800c\u4e14\u53c8\u5f92\u52b3\u4e0d\u5c11\u4eba\u5728\u9152\u697c\u7a7a\u7b49\uff0c\u8fde\u9a6c\u8f66\u4e5f\u767d\u53eb\u4e86\u3002 \u3000\u3000\u4ed6\u4e0d\u60f3\u518d\u5411\u5c24\u4e09\u8ffd\u95ee\uff0c\u7acb\u5373\u4e0b\u697c\u5954\u5411\u6842\u82b1\u4f4f\u7684\u90a3\u95f4\u53a2\u623f\u3002 \u3000\u3000\u95e8\u662f\u865a\u63a9\u7740\uff0c\u8fdb\u53bb\u4e4b\u540e\uff0c\u5ba2\u5385\u548c\u5367\u623f\u679c\u7136\u4e0d\u89c1\u6842\u82b1\u7684\u5f71\u5b50\u3002 \u3000\u3000\u5f53\u5c0f\u71d5\u5b50\u5954\u51fa\u95e8\u5916\uff0c\u53ea\u89c1\u4e00\u540d\u59d1\u5a18\u6b63\u7ad9\u5728\u4e34\u8fd1\u53a2\u623f\u7684\u53e6\u4e00\u95f4\u623f\u95e8\u5916\uff0c\u800c\u4e14\u7559\u610f\u7684\u6ce8\u89c6\u7740 \u4ed6\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u6765\u5230\u90a3\u540d\u59d1\u5a18\u8ddf\u524d\uff0c\u642d\u8baa\u7740\u9053\uff1a\u201c\u59d1\u5a18\u77e5\u4e0d\u77e5\u9053\u6842\u82b1\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u90a3\u59d1\u5a18\u4f4e\u58f0\u53cd\u95ee\u9053\uff1a\u201c\u516c\u5b50\u627e\u6842\u82b1\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6709\u4e8b\u627e\u5979\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u5979\u7684\u719f\u5ba2\uff1f\u201d \u3000\u3000\u201c\u6628\u665a\u624d\u8ba4\u8bc6\u7684\u3002\u201d \u3000\u3000\u201c\u516c\u5b50\u6628\u665a\u662f\u5426\u5bbf\u5728\u5979\u623f\u91cc\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\uff0c\u6211\u5929\u521a\u9ed1\u5c31\u8d70\u4e86\uff0c\u83ab\u975e\u5979\u6628\u665a\u623f\u91cc\u6709\u5ba2\u4eba\uff1f\u201d \u3000\u3000\u90a3\u59d1\u5a18\u5e76\u672a\u56de\u7b54\uff0c\u5374\u8f6c\u8eab\u5411\u81ea\u5df1\u623f\u91cc\u8d70\u53bb\uff0c\u7136\u540e\u53c8\u56de\u5934\u62db\u62db\u624b\u9053\uff1a\u201c\u4f60\u6765\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u56e0\u6709\u6c42\u4e8e\u4eba\uff0c\u53ea\u597d\u8ddf\u4e86\u8fc7\u53bb\uff0c\u4e00\u9762\u95ee\u9053\uff1a\u201c\u6765\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u90a3\u59d1\u5a18\u76f4\u628a\u5c0f\u71d5\u5b50\u5f15\u8fdb\u623f\u95f4\uff0c\u624d\u629b\u7740\u5a9a\u773c\u9053\uff1a\u201c\u4f60\u82e5\u80af\u7167\u987e\u6211\u4e00\u6b21\uff0c\u6211\u5c31\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u201c\u591a\u5c11\u94f6\u5b50\uff1f\u201d \u3000\u3000\u201c\u5e73\u5e38\u662f\u4e8c\u4e24\uff0c\u8fc7\u591c\u4e94\u4e24\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u638f\u51fa\u4e00\u952d\u4e94\u4e24\u91cd\u7684\u94f6\u5b50\uff0c\u4e00\u58f0\u4e0d\u54cd\u7684\u4ea4\u7ed9\u4e86\u90a3\u59d1\u5a18\u3002 \u3000\u3000\u90a3\u59d1\u5a18\u7709\u5f00\u773c\u7b11\u7684\u9053\uff1a\u201c\u539f\u6765\u516c\u5b50\u8981\u5728\u8fd9\u91cc\u8fc7\u591c\uff0c\u73b0\u5728\u624d\u662f\u4e0a\u5348\uff0c\u53cd\u6b63\u6ca1\u6709\u5ba2\u4eba\u6765\uff0c\u6211 \u60c5\u613f\u966a\u4f60\u4e00\u4e0b\uff0c\u4e0d\u5fc5\u518d\u591a\u7b97\u94f6\u5b50\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u62c9\u8d77\u5c0f\u71d5\u5b50\u4fbf\u8981\u5f80\u5367\u5ba4\u8d70\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u5e76\u672a\u79fb\u52a8\u811a\u6b65\uff0c\u53cd\u800c\u5728\u6905\u4e0a\u5750\u4e86\u4e0b\u6765\uff0c\u9053\uff1a\u201c\u4f60\u53ea\u8981\u544a\u8bc9\u6211\u6842\u82b1\u5230\u54ea\u91cc\u53bb\u4e86\uff0c\u90a3 \u4e94\u4e24\u94f6\u5b50\u7b97\u767d\u9001\u4f60\u7684\u3002\u201d \u3000\u3000\u90a3\u59d1\u5a18\u6014\u6014\u7684\u95ee\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u95ee\u6842\u82b1\u54ea\u513f\u53bb\u4e86\uff1f\u201d \u3000\u3000\u201c\u6211\u6709\u8981\u7d27\u7684\u4e8b\u627e\u5979\u3002\u201d \u3000\u3000\u90a3\u59d1\u5a18\u6765\u5230\u95e8\u53e3\u5411\u5916\u671b\u4e86\u671b\uff0c\u7136\u540e\u5173\u4e0a\u95e8\uff0c\u56de\u5230\u5c0f\u71d5\u5b50\u8eab\u524d\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u6842\u82b1\u53ef\u80fd\u8dd1 \u4e86\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u5fc3\u5934\u731b\u7136\u4e00\u9707\u9053\uff1a\u201c\u8dd1\u4e86\uff1f\u5979\u4e3a\u4ec0\u4e48\u8dd1\u4e86\uff1f\u201d \u3000\u3000\u201c\u73b0\u5728\u6b63\u5728\u67e5\uff0c\u5982\u679c\u4e0d\u662f\u8dd1\u4e86\uff0c\u5c31\u662f\u88ab\u4eba\u63b3\u8d70\u4e86\uff0c\u518d\u4e0d\u5c31\u662f\u51fa\u4e86\u522b\u7684\u610f\u5916\u3002\u201d \u3000\u3000\u201c\u662f\u8c01\u5728\u67e5\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u662f\u8001\u677f\u5a18\u5728\u67e5\u3002\u201d \u3000\u3000\u201c\u4e8b\u60c5\u7ecf\u8fc7\u662f\u600e\u6837\u7684\uff0c\u5feb\u8bf4\uff01\u201d \u3000\u3000\u201c\u4eca\u5929\u4e00\u65e9\uff0c\u8001\u677f\u5a18\u6709\u4e8b\u627e\u5979\uff0c\u63a8\u95e8\u8fdb\u53bb\uff0c\u7adf\u7136\u770b\u4e0d\u5230\u4eba\uff0c\u800c\u4e14\u91cc\u5916\u7684\u4e24\u9053\u95e8\u90fd\u6ca1\u6709 \u5173\u3002\u201d \u3000\u3000\u201c\u53ef\u67e5\u51fa\u5979\u662f\u4ec0\u4e48\u65f6\u5019\u8d70\u7684\uff1f\u201d \u3000\u3000\u201c\u636e\u5218\u5927\u53d4\u8bf4\uff0c\u4e09\u66f4\u5929\u7ecf\u8fc7\u5979\u95e8\u53e3\uff0c\u4fbf\u89c1\u5916\u9762\u7684\u623f\u95e8\u672a\u5173\uff0c\u90a3\u65f6\u6df1\u66f4\u534a\u591c\uff0c\u5218\u5927\u53d4\u4e5f\u4e0d \u65b9\u4fbf\u8fdb\u53bb\u5bdf\u770b\u3002\u7167\u8fd9\u4e48\u770b\u6765\uff0c\u6842\u82b1\u5fc5\u662f\u6628\u665a\u4e09\u66f4\u4ee5\u524d\u8d70\u7684\u3002\u201d \u3000\u3000\u201c\u5218\u5927\u53d4\u662f\u505a\u4ec0\u4e48\u7684\uff1f\u201d \u3000\u3000\u201c\u662f\u8001\u677f\u5a18\u96c7\u6765\u8d1f\u8d23\u5de1\u591c\u7684\uff0c\u6211\u4eec\u8fd9\u91cc\u4e00\u767e\u591a\u4f4d\u59d1\u5a18\uff0c\u591c\u91cc\u591a\u534a\u6709\u5ba2\u4eba\uff0c\u5b89\u5168\u662f\u5f88\u91cd\u8981 \u7684\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u5df2\u53ef\u786e\u5b9a\u6842\u82b1\u662f\u8dd1\u4e86\u5417\uff1f\u201d \u3000\u3000\u201c\u5df2\u7ecf\u5feb\u5230\u4e2d\u5348\u4e86\uff0c\u8fd8\u4e0d\u89c1\u5979\uff0c\u5f53\u7136\u53ef\u4ee5\u786e\u5b9a\u662f\u51fa\u4e86\u4e8b\u60c5\u4e86\uff0c\u800c\u4e14\u59d1\u5a18\u8fd8\u4e0d\u6b62\u5c11\u4e86\u5979\u4e00 \u4e2a\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\uff1f\u8fd8\u6709\u522b\u7684\u59d1\u5a18\u4e5f\u4e0d\u89c1\u4e86\uff1f\u201d \u3000\u3000\u201c\u9664\u4e86\u6842\u82b1\uff0c\u53e6\u4e24\u4f4d\u59d1\u5a18\uff0c\u4e00\u4f4d\u53eb\u77f3\u69b4\uff0c\u4e00\u4f4d\u53eb\u51e4\u4ed9\u3002\u201d \u3000\u3000\u201c\u5979\u4eec\u4e5f\u662f\u6628\u665a\u4e0d\u89c1\u7684\uff1f\u201d \u3000\u3000\u201c\u867d\u7136\u8fd8\u6ca1\u67e5\u51fa\u5979\u4eec\u7684\u9003\u8d70\u65f6\u95f4\uff0c\u4f46\u5374\u90fd\u662f\u6628\u665a\u5931\u8e2a\u7684\u3002\u201d \u3000\u3000\u201c\u6842\u82b1\u548c\u77f3\u69b4\u3001\u51e4\u4ed9\u662f\u5426\u5f88\u719f\uff1f\u201d \u3000\u3000\u201c\u5c82\u6b62\u5f88\u719f\uff0c\u5979\u4eec\u6839\u672c\u5c31\u662f\u4e00\u8d77\u8fdb\u6765\u7684\u3002\u201d \u3000\u3000\u201c\u5979\u4eec\u5230\u8fd9\u91cc\u591a\u4e45\u4e86\uff1f\u201d \u3000\u3000\u201c\u8be6\u7ec6\u65f6\u95f4\u6211\u5df2\u8bb0\u4e0d\u6e05\uff0c\u4e0d\u8fc7\u4e0d\u4f1a\u8d85\u8fc7\u4e09\u4e2a\u6708\u3002\u201d \u3000\u3000\u201c\u5979\u4eec\u5f53\u521d\u662f\u600e\u4e48\u8fdb\u6765\u7684\u5462\uff1f\u662f\u5426\u6709\u4eba\u4ecb\u7ecd\uff1f\u4ecb\u7ecd\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u90a3\u8be5\u95ee\u8001\u677f\u5a18\u4e86\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u4e00\u6014\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u90a3\u59d1\u5a18\u9053\uff1a\u201c\u5728\u51e0\u4e2a\u6708\u524d\uff0c\u6709\u4e00\u5929\u8001\u677f\u5a18\u5728\u8857\u4e0a\u65e0\u610f\u4e2d\u78b0\u5230\u5979\u4eec\u4e09\u4e2a\u3002\u201d \u3000\u3000\u201c\u96be\u9053\u8001\u677f\u5a18\u7adf\u7136\u903c\u826f\u4e3a\u5a3c\uff1f\u201d \u3000\u3000\u201c\u8001\u677f\u5a18\u54ea\u6709\u8fd9\u79cd\u80fd\u8010\uff0c\u5f53\u65f6\u5979\u4eec\u4e09\u4e2a\u6b63\u6d41\u843d\u8857\u5934\uff0c\u65e0\u5bb6\u53ef\u5f52\uff0c\u7ecf\u8be2\u95ee\u4e4b\u4e0b\uff0c\u624d\u77e5\u5979\u4eec \u662f\u56e0\u5bb6\u4e2d\u906d\u53d8\uff0c\u6295\u4eb2\u4e0d\u9047\uff0c\u5728\u76d8\u8d39\u82b1\u5c3d\u4e4b\u4e0b\uff0c\u5c31\u53ea\u597d\u6d41\u843d\u8857\u5934\u3002\u8001\u677f\u5a18\u89c1\u5979\u4eec\u4e0d\u4f46\u5e74\u8f7b\uff0c\u800c \u4e14\u957f\u5f97\u6f02\u4eae\uff0c\u4fbf\u529d\u5979\u4eec\u5230\u8fd9\u91cc\u6765\uff0c\u5979\u4eec\u5c31\u7b54\u5e94\u4e86\u3002\u201d \u3000\u3000\u201c\u539f\u6765\u5982\u6b64\u3002\u5979\u4eec\u53ef\u4e0d\u53ef\u80fd\u662f\u56e0\u4e3a\u5df2\u627e\u5230\u4eb2\u621a\uff0c\u6240\u4ee5\u624d\u8981\u9003\u8d70\uff1f\u201d \u3000\u3000\u201c\u5979\u4eec\u5e76\u975e\u8001\u677f\u5a18\u62ff\u94f6\u5b50\u4e70\u6765\u7684\uff0c\u8981\u8d70\u53ea\u7ba1\u5411\u8001\u677f\u5a18\u8bf4\u660e\uff0c\u4f55\u5fc5\u5077\u5077\u7684\u6e9c\u6389\u5462\uff1f\u201d \u3000\u3000\u201c\u4e5f\u8bb8\u5979\u4eec\u662f\u62c5\u5fc3\u8001\u677f\u5a18\u4e0d\u80af\u7b54\u5e94\u5427\uff01\u201d \u3000\u3000\u201c\u4f60\u8bf4\u7684\u4e5f\u6709\u9053\u7406\uff0c\u4e0d\u8fc7\u5979\u4eec\u5df2\u4e0d\u53ef\u80fd\u518d\u56de\u6765\uff0c\u90a3\u5374\u662f\u4e00\u5b9a\u7684\u3002\u201d\u2018\u201c\u5979\u4eec\u4e09\u4eba\uff0c\u5e73\u65e5 \u548c\u522b\u7684\u59d1\u5a18\u76f8\u5904\u7684\u60c5\u5f62\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u5927\u5bb6\u76f8\u5904\u90fd\u5f88\u597d\u3002\u201d \u3000\u3000\u201c\u5979\u4eec\u8d70\u65f6\uff0c\u662f\u5426\u5e26\u8d70\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d \u3000\u3000\u201c\u8c01\u77e5\u9053\u5462\uff1f\u5e26\u8d70\u7684\u4e5f\u662f\u81ea\u5df1\u7684\u4e1c\u897f\uff0c\u6839\u672c\u67e5\u4e0d\u51fa\u6765\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u7ad9\u8d77\u8eab\u9053\uff1a\u201c\u6211\u8be5\u8d70\u4e86\uff01\u201d \u3000\u3000\u90a3\u59d1\u5a18\u6014\u6014\u7684\u671b\u7740\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u53ea\u95ee\u8fd9\u4e48\u51e0\u53e5\u8bdd\u5c31\u8d70\u4e86\uff0c\u665a\u4e0a\u662f\u5426\u8fd8\u8981\u6765\u8fc7\u591c\uff1f\u201d \u3000\u3000\u201c\u4e0d\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u8fd9\u516c\u5b50\u597d\u5927\u65b9\uff0c\u7adf\u7136\u767d\u767d\u6487\u4e0b\u4e94\u4e24\u94f6\u5b50\u3002\u201d \u3000\u3000\u201c\u4ee5\u540e\u6709\u673a\u4f1a\u6211\u4e00\u5b9a\u4f1a\u518d\u6765\uff0c\u8fd8\u6ca1\u95ee\u59d1\u5a18\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u201c\u6211\u53eb\u5c0f\u7af9\u3002\u201d \u3000\u3000\u201c\u4e0b\u6b21\u89c1\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u7528\u4e0d\u7740\u518d\u56de\u82b1\u5385\u548c\u5c24\u4e09\u89c1\u9762\uff0c\u4e5f\u7528\u4e0d\u7740\u518d\u627e\u8001\u9e28\u67e5\u8be2\uff0c\u6beb\u65e0\u7591\u95ee\uff0c\u6842\u82b1\u662f\u4e0d\u53ef\u80fd \u518d\u56de\u6765\u4e86\u3002 \u3000\u3000\u6842\u82b1\u9003\u8d70\uff0c\u4e0d\u6d88\u8bf4\u662f\u548c\u5c0f\u71d5\u5b50\u6709\u5173\u3002 \u3000\u3000\u4f46\u5c0f\u71d5\u5b50\u5374\u5b9e\u5728\u601d\u89e3\u4e0d\u900f\uff0c\u81ea\u5df1\u7a76\u7adf\u4ec0\u4e48\u5730\u65b9\u5728\u6842\u82b1\u9762\u524d\u9732\u51fa\u7834\u7efd\uff1f\u4ed6\u5148\u5230\u95e8\u53e3\u9063\u8d70\u4e86 \u9a6c\u8f66\u3002 \u3000\u3000\u518d\u8d76\u5230\u90a3\u5bb6\u9152\u697c\uff0c\u5411\u97e9\u6d9b\u7b49\u4eba\u8bf4\u660e\u4e0a\u60c5\u3002 \u3000\u3000\u65e2\u7136\u53eb\u4e0d\u6765\u59d1\u5a18\uff0c\u9152\u98df\u5df2\u7ecf\u8ba2\u4e86\uff0c\u97e9\u6d9b\u7b49\u4eba\u53ea\u597d\u81ea\u5df1\u7528\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\uff0c\u5c0f\u71d5\u5b50\u53ef\u8bf4\u662f\u4e58\u5174\u800c\u53bb\uff0c\u8d25\u5174\u800c\u5f52\u3002 \u3000\u3000\u4ed6\u7b80\u76f4\u65e0\u989c\u56de\u5ba2\u6808\u518d\u89c1\u6c5f\u5343\u91cc\u548c\u738b\u5f64\u3002 \u3000\u3000\u4f46\u60ed\u6127\u5f52\u60ed\u6127\uff0c\u56de\u53bb\u8fd8\u662f\u8981\u56de\u53bb\u3002 \u3000\u3000\u56de\u5230\u5ba2\u6808\uff0c\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u53c8\u5728\u5ba2\u5385\u3002 \u3000\u3000\u4ed6\u4eec\u662f\u7528\u8fc7\u4e86\u5348\u9910\uff0c\u5728\u7b49\u5019\u6d88\u606f\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u8fdb\u5165\u5ba2\u5385\uff0c\u89c1\u8fc7\u793c\uff0c\u8fd8\u6ca1\u6765\u5f97\u53ca\u5f00\u53e3\uff0c\u6c5f\u5343\u91cc\u4fbf\u95ee\u9053\uff1a\u201c\u600e\u4e48\u5c31\u4f60\u4e00\u4e2a\u4eba\u56de\u6765\uff0c \u90a3\u4f4d\u59d1\u5a18\u5462\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u5927\u6709\u65e0\u5730\u81ea\u5bb9\u7684\u611f\u89c9\uff0c\u7acb\u5373\u628a\u6842\u82b1\u5df2\u7ecf\u9003\u8d70\u7684\u4e8b\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u5e76\u672a\u8d23\u5907\u5c0f\u71d5\u5b50\uff0c\u53ea\u662f\u671b\u4e86\u738b\u5f64\u4e00\u773c\u9053\uff1a\u201c\u73b0\u5728\u5df2\u53ef\u8bc1\u5b9e\u90a3\u540d\u53eb\u6842\u82b1\u7684\u59d1\u5a18\uff0c\u5b9a \u662f\u8001\u5996\u5a46\u7684\u5e08\u7236\u4e86\uff01\u201d \u3000\u3000\u738b\u5f64\u7d27\u76b1\u53cc\u7709\u9053\uff1a\u201c\u53ef\u662f\u4eba\u5df2\u7ecf\u8dd1\u4e86\uff0c\u54b1\u4eec\u5fc5\u987b\u8bbe\u6cd5\u9a6c\u4e0a\u902e\u5230\u5979\u3002\u201d \u3000\u3000\u201c\u738b\u8001\u5f1f\u53ef\u6709\u529e\u6cd5\uff1f\u201d \u3000\u3000\u201c\u901a\u77e5\u9a6c\u6587\u4e2d\uff0c\u52a0\u7d27\u5728\u57ce\u5185\u641c\u67e5\uff0c\u540c\u65f6\u57ce\u5916\u767e\u91cc\u4e4b\u5185\u4e5f\u8981\u9a6c\u6587\u4e2d\u6d3e\u51fa\u5b98\u5175\u641c\u6355\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u7b11\u9053\uff1a\u201c\u9003\u8d70\u7684\u4e09\u540d\u59d1\u5a18\uff0c\u6839\u672c\u5c31\u662f\u542c\u547d\u4e8e\u9a6c\u6587\u4e2d\u7684\uff0c\u738b\u8001\u5f1f\u73b0\u5728\u8981\u4ed6\u901a\u7f09\u641c \u6355\uff0c\u5c82\u4e0d\u662f\u6210\u4e86\u7b11\u8bdd\uff1f\u201d \u3000\u3000\u738b\u5f64\u82e6\u7b11\u4e86\u4e00\u4e0b\u9053\uff1a\u201c\u4f9d\u6c5f\u5144\u4e4b\u89c1\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e8b\u7edd\u5bf9\u4e0d\u80fd\u60ca\u52a8\u9a6c\u6587\u4e2d\uff0c\u5fc5\u987b\u54b1\u4eec\u81ea\u5df1\u60f3\u529e\u6cd5\u3002\u201d \u3000\u3000\u201c\u6c5f\u5144\u6700\u64c5\u8ffd\u8e2a\u89c5\u8ff9\u4e4b\u672f\uff0c\u662f\u5426\u5c31\u8be5\u9a6c\u4e0a\u884c\u52a8\uff1f\u201d \u3000\u3000\u201c\u636e\u6c5f\u67d0\u5224\u65ad\uff0c\u8fd9\u4e09\u540d\u5973\u5b50\u5f88\u53ef\u80fd\u662f\u88ab\u9a6c\u6587\u4e2d\u85cf\u533f\u8d77\u6765\uff0c\u679c\u771f\u5982\u6b64\u3001\u6c5f\u67d0\u56e0\u884c\u52a8\u53d7\u5230\u9650 \u5236\uff0c\u4e8b\u60c5\u5c31\u5f88\u96be\u8fdb\u884c\u4e86\u3002\u201d \u3000\u3000\u201c\u7684\u786e\u6709\u6b64\u53ef\u80fd\u3002\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5ba2\u6808\u91cc\u7684\u4e00\u540d\u4f19\u8ba1\u5306\u5306\u8fdb\u5165\u5ba2\u5385\uff0c\u9053\uff1a\u201c\u5916\u9762\u6709\u4f4d\u51fa\u5bb6\u4eba\uff0c\u81ea\u79f0\u662f\u6b66\u5f53\u6765 \u7684\uff0c\u8981\u89c1\u738b\u5927\u4eba\u548c\u6c5f\u5927\u4fa0\uff01\u201d \u3000\u3000\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u5f53\u521d\u4f4f\u8fdb\u8fd9\u5bb6\u5ba2\u6808\u65f6\uff0c\u672c\u6765\u4e0d\u60f3\u6cc4\u9732\u8eab\u4efd\uff0c\u4f46\u56e0\u5de1\u629a\u5927\u4eba\u66fe\u6765\u62dc\u8bbf\u8fc7\u4e24 \u6b21\uff0c\u56e0\u4e4b\u5e97\u5bb6\u6b64\u523b\u65e9\u5df2\u77e5\u9053\u8fd9\u91cc\u4f4f\u7684\u662f\u8c01\uff1f\u738b\u5f64\u8fde\u5fd9\u4ea4\u4ee3\u4f19\u8ba1\u9053\uff1a\u201c\u8bf7\u4ed6\u8fdb\u6765\u3002\u201d \u3000\u3000\u4e0d\u4e00\u4f1a\u513f\uff0c\u679c\u7136\u4e00\u540d\u8eab\u7a7f\u7070\u8272\u9053\u888d\u7684\u4e2d\u5e74\u9053\u58eb\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u8fd9\u540d\u4e2d\u5e74\u9053\u58eb\u663e\u5f97\u98ce\u5c18\u4ec6\u4ec6\uff0c\u800c\u4e14\u795e\u8272\u95f4\u60f6\u6025\u7d27\u5f20\uff0c\u4e00\u8fdb\u5ba2\u5385\uff0c\u6253\u4e2a\u7a3d\u9996\u9053\uff1a\u201c\u8d2b\u9053\u62dc \u89c1\u738b\u5927\u4eba\u548c\u6c5f\u65bd\u4e3b\u3002\u201d \u3000\u3000\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u5404\u81ea\u8d77\u8eab\u8fd8\u4e86\u4e00\u793c\u3002 \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u5feb\u5feb\u8bf7\u5750\uff0c\u542c\u8bf4\u9053\u957f\u662f\u4ece\u6b66\u5f53\u6765\u7684\uff0c\u4e0d\u77e5\u6709\u4f55\u89c1\u6559\uff1f\u201d \u3000\u3000\u4e2d\u5e74\u9053\u58eb\u987f\u4e86\u987f\u9053\uff1a\u201c\u8d2b\u9053\u9752\u6728\uff0c\u662f\u5949\u638c\u95e8\u5e08\u5c0a\u4e4b\u547d\uff0c\u548c\u5341\u4e5d\u540d\u5e08\u5144\u5f1f\u524d\u6765\u542c\u547d\u738b\u5927\u4eba \u8c03\u9063\u7684\u3002\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\uff0c\u5bf9\u738b\u5f64\u6765\u8bf4\u5e76\u4e0d\u610f\u5916\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u4ed6\u5728\u6765\u5f00\u5c01\u4e4b\u524d\uff0c\u66fe\u5148\u5230\u5c11\u6797\uff0c\u51fa\u793a\u8fc7\u5723\u4e0a\u5bc6\u65e8\uff0c\u8981\u5c11\u6797\u548c\u6b66\u5f53\u65b9\u9762\u5fc5\u8981\u65f6\u6d3e\u9063 \u9ad8\u624b\u5e94\u53d8\u3002 \u3000\u3000\u81f3\u4e8e\u6b66\u5f53\u65b9\u9762\uff0c\u5219\u662f\u7531\u5c11\u6797\u638c\u95e8\u5927\u667a\u7985\u5e08\u6d3e\u51fa\u5f1f\u5b50\u8d1f\u8d23\u8054\u7edc\u7684\u3002 \u3000\u3000\u8fd9\u5bf9\u738b\u5f64\u6765\u8bf4\u3002\u8be5\u662f\u4e00\u4ef6\u503c\u5f97\u5174\u594b\u7684\u4e8b\u3002\u56e0\u4e3a\u6709\u4e86\u6b66\u5f53\u65b9\u9762\u7684\u5341\u4f59\u540d\u9ad8\u624b\u652f\u63f4\uff0c\u5bf9\u8ffd\u8e2a \u6842\u82b1\u7b49\u4e09\u4eba\u7684\u4e0b\u843d\u5fc5\u5b9a\u5927\u6709\u5e2e\u52a9\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u53e6\u4e00\u65b9\u9762\u4ed6\u53c8\u4e0d\u5e0c\u671b\u6b66\u5f53\u65b9\u9762\u7684\u4eba\u516c\u5f00\u8fdb\u5165\u5f00\u5c01\uff0c\u56e0\u4e3a\u5982\u6b64\u4e00\u6765\uff0c\u5f88\u5bb9\u6613\u5f15\u8d77\u9a6c \u6587\u4e2d\u7684\u7591\u5fc3\u3002 \u3000\u3000\u5f53\u4e0b\uff0c\u738b\u5f64\u95ee\u9053\uff1a\u201c\u8d35\u6d3e\u7684\u5341\u51e0\u4f4d\u9053\u957f\uff0c\u76ee\u524d\u4eba\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u9752\u6728\u987f\u4e86\u987f\u9053\uff1a\u201c\u5c31\u5728\u5f00\u5c01\u57ce\u897f\u5927\u7ea6\u56db\u4e94\u5341\u91cc\u8def\uff0c\u79bb\u57ce\u9547\u4e0d\u8fdc\u7684\u4e00\u5ea7\u65e0\u4eba\u7684\u9053\u89c2\u91cc \u9762\u3002\u201d \u3000\u3000\u201c\u9053\u957f\u524d\u6765\uff0c\u83ab\u975e\u53ea\u662f\u8981\u4e0e\u738b\u67d0\u53d6\u5f97\u8054\u7ff1\u201d \u3000\u3000\u9752\u6728\u5ffd\u7136\u6d41\u4e0b\u773c\u6cea\u9053\uff1a\u201c\u8d2b\u9053\u82e5\u53ea\u662f\u4e3a\u4e86\u548c\u738b\u5927\u4eba\u53d6\u5f97\u8054\u7cfb\uff0c\u5c31\u7528\u4e0d\u7740\u8fd9\u4e48\u60f6\u6025\u5306\u5fd9 \u4e86\uff01\u201d \u3000\u3000\u738b\u5f64\u4e0d\u7531\u5403\u60ca\u9053\uff1a\u201c\u83ab\u975e\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u6545\uff1f\u201d \u3000\u3000\u9752\u6728\u7acb\u5373\u5c06\u9053\u888d\u4e0b\u6446\u64a9\u8d77\uff0c\u518d\u62ac\u8d77\u4e00\u53ea\u624b\u81c2\u9053\uff1a\u201c\u738b\u5927\u4eba\u8bf7\u770b\uff01\u201d \u3000\u3000\u738b\u5f64\u3001\u6c5f\u5343\u91cc\u3001\u5c0f\u71d5\u5b50\u9f50\u9f50\u5411\u9752\u6728\u9053\u888d\u4e0b\u6446\u548c\u814b\u4e0b\u671b\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u9752\u6728\u8863\u670d\u4e0a\u7684\u8fd9\u4e24\u5904\u67d3\u4e86\u4e0d\u5c11\u8840\u8ff9\uff0c\u663e\u7136\u662f\u4e0e\u4eba\u6253\u6597\u53d7\u4f24\u7684\u3002 \u3000\u3000\u738b\u5f64\u6115\u7136\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u662f\u8c01\u4f24\u4e86\u9053\u957f\uff1f\u201d \u3000\u3000\u9752\u6728\u518d\u5ea6\u6d41\u4e0b\u773c\u6cea\uff0c\u53f9\u9053\uff1a\u201c\u8d2b\u9053\u53ea\u662f\u53d7\u4e86\u70b9\u8f7b\u4f24\uff0c\u6839\u672c\u7b97\u4e0d\u4e86\u4ec0\u4e48\uff0c\u53ef\u601c\u548c\u8d2b\u9053\u540c\u6765 \u7684\u5341\u51e0\u4f4d\u5e08\u5144\u5f1f\uff0c\u5df2\u7ecf\u6709\u4e24\u4e09\u4f4d\u4f24\u91cd\u4e0d\u6cbb\uff0c\u53e6\u6709\u4e09\u56db\u4f4d\u8eab\u53d7\u91cd\u4f24\u7684\u3002\u201d \u3000\u3000\u738b\u5f64\u554a\u4e86\u58f0\u9053\uff1a\u201c\u7a76\u7adf\u600e\u4e48\u56de\u4e8b\uff1f\u9053\u957f\u5feb\u8bf4\uff01\u201d \u3000\u3000\u9752\u6728\u60b2\u4f24\u7684\u9053\uff1a\u201c\u738b\u5927\u4eba\u548c\u6c5f\u65bd\u4e3b\u4e5f\u8bb8\u4e0d\u4f1a\u76f8\u4fe1\u5427\uff01\u8d2b\u50e7\u5e08\u5144\u5f1f\u5341\u51e0\u4eba\u7adf\u8d25\u5728\u4e09\u540d\u5e74\u8f7b \u5973\u5b50\u624b\u4e0a\u3002\u201d \u3000\u3000\u738b\u5f64\u3001\u6c5f\u5343\u91cc\u3001\u5c0f\u71d5\u5b50\u51e0\u4e4e\u540c\u65f6\u5fc3\u5934\u4e00\u9707\uff0c\u6beb\u65e0\u7591\u95ee\uff0c\u8fd9\u4e09\u540d\u5e74\u8f7b\u5973\u5b50\u5c31\u662f\u6842\u82b1\u3001\u77f3 \u69b4\u3001\u51e4\u4ed9\u3002 \u3000\u3000\u738b\u5f64\u6025\u6025\u95ee\u9053\uff1a\u201c\u4e8b\u60c5\u662f\u4ec0\u4e48\u65f6\u5019\u53d1\u751f\u7684\uff1f\u201d \u3000\u3000\u201c\u5c31\u5728\u4eca\u5929\u9ece\u660e\u524d\u540e\u3002\u201d \u3000\u3000\u8ba1\u7b97\u65f6\u95f4\uff0c\u8d8a\u53d1\u53ef\u4ee5\u8bc1\u660e\u738b\u5f64\u7b49\u4eba\u7684\u770b\u6cd5\u6ca1\u9519\u3002\u56e0\u4e3a\u6842\u82b1\u4e09\u4eba\u662f\u6628\u665a\u4e09\u66f4\u524d\u79bb\u5f00\u201c\u8fce\u6625 \u9601\u201d\u7684\uff0c\u5728\u884c\u7a0b\u4e0a\u6b63\u597d\u53ef\u4ee5\u8d70\u5230\u51fa\u4e8b\u5730\u70b9\u3002 \u3000\u3000\u738b\u5f64\u8feb\u4e0d\u53ca\u5f85\u7684\u518d\u95ee\u9053\uff1a\u201c\u90a3\u4e09\u540d\u5e74\u8f7b\u5973\u5b50\u957f\u76f8\u5982\u4f55\uff1f\u201d \u3000\u3000\u9752\u6728\u7b54\u9053\uff1a\u201c\u770b\u6765\u90fd\u5728\u4e8c\u5341\u5c81\u5de6\u53f3\uff0c\u957f\u76f8\u8fd8\u4e0d\u9519\u3002\u201d \u3000\u3000\u201c\u53cc\u65b9\u662f\u600e\u4e48\u6253\u8d77\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u5f53\u65f6\u8d2b\u9053\u5e08\u5144\u5f1f\u5341\u51e0\u4eba\u521a\u521a\u8d77\u5e8a\uff0c\u5979\u4eec\u4e00\u4e0b\u5b50\u5c31\u51b2\u8fdb\u9053\u89c2\u91cc\u6765\uff0c\u534a\u53e5\u8bdd\u4e0d\u95ee\uff0c\u51b7\u4e0d\u9632 \u5c31\u52a8\u8d77\u624b\u6765\u3002\u201d \u3000\u3000\u201c\u539f\u6765\u9053\u957f\u4eec\u662f\u63aa\u624b\u4e0d\u53ca\u624d\u5403\u4e86\u5927\u4e8f\uff1f\u201d \u3000\u3000\u201c\u56fa\u7136\u6709\u4e9b\u63aa\u624b\u4e0d\u53ca\uff0c\u4f46\u4e0d\u662f\u4e3b\u8981\u539f\u56e0\u3002\u201d \u3000\u3000\u201c\u4e3b\u8981\u539f\u56e0\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u662f\u90a3\u4e09\u4e2a\u5e74\u8f7b\u5973\u4eba\u7684\u8eab\u624b\u5b9e\u5728\u592a\u9ad8\u4e86\uff0c\u5c31\u7b97\u8d2b\u9053\u4eec\u5df2\u6709\u4e86\u5fc3\u7406\u51c6\u5907\uff0c\u4e5f\u662f\u62fc\u6218\u4e0d\u8fc7\u5979 \u4eec\u3002\u201d \u3000\u3000\u201c\u4e8b\u540e\u5462\uff1f\u201d \u3000\u3000\u201c\u6240\u5e78\u5979\u4eec\u5e76\u672a\u8d76\u5c3d\u6740\u7edd\uff0c\u53ea\u8bf4\u4eca\u665a\u5979\u4eec\u8981\u8fdb\u9a7b\u90a3\u5ea7\u9053\u89c2\uff0c\u9650\u6211\u4eec\u5373\u523b\u8fd4\u56de\u6b66\u5f53\uff0c\u800c\u4e14 \u5bf9\u8fd9\u4ef6\u4e8b\u4e0d\u5f97\u58f0\u5f20\u3002\u201d \u3000\u3000\u201c\u9053\u957f\u662f\u600e\u6837\u56de\u7b54\u5979\u4eec\u7684\uff1f\u201d \u3000\u3000\u201c\u8d2b\u9053\u4eec\u8868\u9762\u4e0a\u600e\u6562\u4e0d\u4ece\uff0c\u7b49\u5979\u4eec\u79bb\u5f00\u540e\uff0c\u624d\u51b3\u5b9a\u6765\u5411\u4e3b\u5927\u4eba\u548c\u6c5f\u65bd\u4e3b\u6c42\u6551\u3002\u201d \u3000\u3000\u201c\u6b64\u523b\u8d35\u6d3e\u65b9\u9762\u7684\u4eba\u90fd\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u88ab\u6740\u7684\u4e24\u4f4d\u5e08\u5144\u5f1f\u5c31\u5728\u9053\u89c2\u540e\u7684\u5c71\u5761\u4e0a\u8349\u8349\u63a9\u57cb\uff0c\u5176\u4f59\u7684\u6682\u907f\u5728\u6570\u91cc\u5916\u7684\u4e00\u5904\u6c11\u5b85\uff0c \u53ea\u7b49\u738b\u5927\u4eba\u548c\u6c5f\u65bd\u4e3b\u524d\u53bb\u3002\u201d \u3000\u3000\u201c\u9053\u957f\u4e00\u8def\u8d76\u6765\uff0c\u53ef\u80fd\u8fd8\u672a\u7528\u996d\u5427\uff1f\u201d \u3000\u3000\u201c\u8d2b\u9053\u54ea\u91cc\u8fd8\u6765\u5f97\u53ca\u5403\u996d\uff01\u201d \u3000\u3000\u738b\u5f64\u4ea4\u4ee3\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u628a\u8fd9\u4f4d\u9053\u957f\u5e26\u5230\u9910\u5802\u53bb\uff0c\u8981\u5ba2\u6808\u91cc\u51c6\u5907\u4e00\u4efd\u7d20\u9910\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u7acb\u5373\u5e26\u7740\u9752\u6728\u79bb\u5f00\u5ba2\u5385\u3002 \u3000\u3000\u738b\u5f64\u8f6c\u8fc7\u5934\u6765\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u4e09\u540d\u5996\u5973\u6b66\u529f\u7adf\u662f\u5982\u6b64\u4e4b\u9ad8\uff01\u201d \u3000\u3000\u6c5f\u5343\u91cc\u559d\u4e86\u53e3\u8336\uff0c\u9053\uff1a\u201c\u4e0d\u8fc7\u8fd9\u4e00\u6765\u603b\u7b97\u627e\u5230\u5979\u4eec\u7684\u4e0b\u843d\u4e86\uff0c\u738b\u8001\u5f1f\u662f\u5426\u51c6\u5907\u9a6c\u4e0a\u8c03\u52a8 \u4eba\u9a6c\uff1f\u201d \u3000\u3000\u738b\u5f64\u4f3c\u5df2\u6709\u4e86\u51b3\u5b9a\uff0c\u9053\uff1a\u201c\u7b49\u9752\u6728\u9053\u957f\u7528\u8fc7\u9910\u540e\uff0c\u54b1\u4eec\u7684\u4eba\u4e5f\u90fd\u5df2\u56de\u6765\u4e86\uff0c\u90a3\u65f6\u5c31\u9a6c\u4e0a \u51fa\u53d1\uff0c\u90a3\u4e09\u540d\u5996\u5973\u6b66\u529f\u867d\u9ad8\uff0c\u4f46\u4ee5\u54b1\u4eec\u7684\u8eab\u624b\uff0c\u5fc5\u53ef\u624b\u5230\u64d2\u6765\u3002\u201d \u3000\u3000\u201c\u662f\u5426\u4e5f\u8981\u8bf7\u4e09\u516c\u4e3b\u524d\u53bb\uff1f\u201d \u3000\u3000\u201c\u6211\u770b\u4e0d\u5fc5\u52b3\u52a8\u4e09\u516c\u4e3b\u4e86\uff0c\u4e0d\u8fc7\u54b1\u4eec\u7684\u884c\u52a8\u5fc5\u987b\u5411\u4e09\u516c\u4e3b\u7980\u660e\u3002\u201d \u3000\u3000\u201c\u4e09\u516c\u4e3b\u7559\u5728\u5ba2\u6808\uff0c\u4e07\u4e00\u51fa\u4e86\u5dee\u9519\u5462\uff1f\u201d \u3000\u3000\u201c\u4e09\u516c\u4e3b\u7684\u8eab\u624b\uff0c\u5df2\u662f\u9ad8\u624b\u4e2d\u7684\u9ad8\u624b\uff1b\u800c\u4e14\u601c\u82b1\u4e5f\u975e\u7b49\u95f2\u4e4b\u8f88\uff0c\u54b1\u4eec\u6700\u665a\u660e\u65e9\u5929\u4eae\u524d\u4fbf \u53ef\u8d76\u56de\uff0c\u6599\u60f3\u4e0d\u4f1a\u51fa\u4e8b\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u5bf9\u65b9\u53ea\u6709\u4e09\u540d\u5996\u5973\uff0c\u54b1\u4eec\u5b9e\u5728\u7528\u4e0d\u7740\u5168\u90e8\u524d\u53bb\uff0c\u8fd8\u662f\u7559\u4e0b\u51e0\u4eba\uff0c\u4fdd\u62a4\u4e09\u516c\u4e3b\u4e3a \u4f73\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u628a\u97e9\u6d9b\u548c\u71d5\u98de\u4e24\u4f4d\u8001\u5f1f\u7559\u4e0b\u3002\u201d \u3000\u3000\u201c\u6700\u597d\u628a\u8001\u5996\u5a46\u5e26\u53bb\u3002\u201d \u3000\u3000\u201c\u5144\u5f1f\u4e5f\u6709\u610f\u628a\u5979\u5e26\u53bb\uff0c\u73b0\u5728\u5144\u5f1f\u5c31\u5230\u91cc\u9762\u5411\u4e09\u516c\u4e3b\u7980\u660e\u3002\u201d \u3000\u3000\u5f53\u738b\u5f64\u518d\u56de\u5230\u5ba2\u5385\uff0c\u9752\u6728\u5df2\u7528\u8fc7\u5348\u9910\uff0c\u5728\u5c0f\u71d5\u5b50\u966a\u540c\u4e0b\u4e5f\u56de\u5230\u5ba2\u5385\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u7247\u523b\uff0c\u5728\u9152\u697c\u4eab\u7528\u4e86\u4e00\u987f\u76db\u7b75\u7684\u97e9\u6d9b\u3001\u738b\u91cd\u5c71\u3001\u9648\u5b8f\u3001\u8d75\u4fdd\u7b49\u4eba\u4e5f\u56de\u6765\u4e86\u3002 \u3000\u3000\u53e6\u5916\uff0c\u518d\u628a\u9ed1\u7f57\u6c49\u4e03\u5de7\u50e7\u548c\u5c0f\u9053\u58eb\u767d\u7fbd\u4ee5\u53ca\u8001\u5996\u5a46\u4e5f\u53ec\u96c6\u8fc7\u6765\u3002 \u3000\u3000\u738b\u5f64\u5bf9\u4f17\u4eba\u8bf4\u660e\u884c\u52a8\u6982\u8981\u3002 \u3000\u3000\u7559\u4e0b\u97e9\u6d9b\u548c\u71d5\u98de\u5728\u5ba2\u6808\u7ef4\u62a4\u4e09\u516c\u4e3b\u5b89\u5168\uff0c\u5176\u4f59\u7684\u4eba\u5404\u81ea\u643a\u5e26\u5175\u5203\uff0c\u5728\u9752\u6728\u5e26\u8def\u4e0b\u51fa\u57ce\u5411 \u897f\u8fdb\u53d1\u3002 \u3000\u3000\u56db\u4e94\u5341\u91cc\u7684\u8def\u7a0b\uff0c\u4ee5\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u7b49\u4eba\u7684\u811a\u7a0b\uff0c\u6839\u672c\u4e0d\u7b97\u4e00\u56de\u4e8b\u3002 \u3000\u3000\u4e3a\u4e86\u907f\u514d\u62db\u4eba\u8033\u76ee\uff0c\u4ed6\u4eec\u5e76\u672a\u65bd\u5c55\u8f7b\u529f\u3002 \u3000\u3000\u5728\u79bb\u76ee\u7684\u5730\u5927\u7ea6\u5269\u4e0b\u5341\u91cc\u8def\u5de6\u53f3\u65f6\uff0c\u9752\u6728\u6307\u7740\u8fdc\u5904\u7684\u5c71\u5761\u9053\uff1a\u201c\u9053\u89c2\u5c31\u5728\u5c71\u5761\u4e0a\uff0c\u738b\u5927 \u4eba\u548c\u6c5f\u65bd\u4e3b\u662f\u5426\u8981\u76f4\u63a5\u524d\u53bb\uff1f\u201d \u3000\u3000\u6c5f\u5e72\u91cc\u62a2\u5148\u7b54\u9053\uff1a\u201c\u73b0\u5728\u8fd8\u662f\u767d\u5929\uff0c\u82e5\u73b0\u5728\u53bb\uff0c\u4e00\u65e6\u88ab\u5bf9\u65b9\u53d1\u89c9\uff0c\u5979\u4eec\u4eca\u665a\u5f88\u53ef\u80fd\u4e0d\u6562 \u518d\u6765\uff0c\u4e0d\u5982\u5148\u5230\u90a3\u5904\u6c11\u5bb6\uff0c\u9053\u957f\u4eec\u65e2\u7136\u6709\u591a\u4eba\u53d7\u4f24\uff0c\u6c5f\u67d0\u8eab\u8fb9\u6b63\u597d\u5e26\u6709\u836f\u7269\uff0c\u4e5f\u597d\u66ff\u4ed6\u4eec\u7597 \u6cbb\u7597\u6cbb\u3002\u201d \u3000\u3000\u9752\u6728\u9894\u9996\u9053\uff1a\u201c\u6c5f\u65bd\u4e3b\u987e\u8651\u5f97\u5bf9\uff0c\u90a3\u5904\u6c11\u5bb6\u5c31\u79bb\u8fd9\u91cc\u4e0d\u8fdc\u3002\u201d \u3000\u3000\u679c\u7136\uff0c\u53c8\u8d70\u4e86\u4e24\u4e09\u91cc\u8def\uff0c\u8def\u65c1\u6811\u6797\u6df1\u5904\uff0c\u51e0\u95f4\u8305\u5c4b\u5df2\u6620\u5165\u773c\u5e18\u3002 \u3000\u3000\u9752\u6728\u9053\uff1a\u201c\u5230\u4e86\uff0c\u8fd9\u5904\u6c11\u5bb6\u5730\u70b9\u5f88\u9690\u5bc6\uff0c\u6599\u60f3\u4e0d\u81f4\u88ab\u90a3\u4e09\u4e2a\u5973\u5b50\u53d1\u73b0\u3002\u201d \u3000\u3000\u7a7f\u8fc7\u6811\u6797\uff0c\u53c8\u8d70\u4e86\u4e00\u6bb5\u5d0e\u5c96\u5c0f\u5f84\uff0c\u624d\u6765\u5230\u6c11\u5bb6\u7684\u7bf1\u7b06\u524d\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u540d\u5e74\u7ea6\u4e09\u5341\u5de6\u53f3\u7684\u9053\u58eb\u7531\u7bf1\u7b06\u5185\u8001\u8fdc\u4fbf\u8fce\u4e86\u51fa\u6765\u3002 \u3000\u3000\u9752\u6728\u6307\u7740\u90a3\u9053\u58eb\u9053\uff1a\u201c\u8fd9\u662f\u8d2b\u9053\u7684\u5e08\u5f1f\u9752\u677e\u3002\u201d \u3000\u3000\u63a5\u7740\u9ad8\u58f0\u62db\u547c\u9752\u677e\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u738b\u5927\u4eba\u548c\u6c5f\u65bd\u4e3b\u4ed6\u4eec\u5230\u4e86\uff0c\u5feb\u5feb\u8fc7\u6765\u89c1\u793c\u3002\u201d \u3000\u3000\u9752\u677e\u6025\u6025\u8d8b\u524d\u51e0\u6b65\uff0c\u5411\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u7b49\u4eba\u2014\u4e00\u7a3d\u9996\u4e3a\u793c\u3002 \u3000\u3000\u9752\u6728\u8bed\u6c14\u5173\u5207\u7684\u95ee\u9053\uff1a\u201c\u4ed6\u4eec\u90fd\u5728\u91cc\u9762\u5427\uff01\u6211\u79bb\u5f00\u7684\u8fd9\u6bb5\u65f6\u95f4\u6ca1\u51fa\u4e8b\u5427\uff1f\u201d \u3000\u3000\u9752\u677e\u9053\uff1a\u201c\u8fd8\u597d\uff0c\u5e08\u5144\u8f9b\u82e6\u4e86\uff0c\u8fd9\u4e48\u5feb\u5c31\u628a\u738b\u5927\u4eba\u548c\u6c5f\u65bd\u4e3b\u7b49\u4eba\u8bf7\u5230\u3002\u201d \u3000\u3000\u9752\u6728\u82e6\u7b11\u9053\uff1a\u201c\u672c\u6765\u54b1\u4eec\u662f\u6765\u76f8\u52a9\u738b\u5927\u4eba\u548c\u6c5f\u65bd\u4e3b\u7684\uff0c\u60f3\u4e0d\u5230\u73b0\u5728\u5374\u8981\u4ed6\u4eec\u6765\u6551\u63f4\u54b1 \u4eec\uff0c\u771f\u662f\u60ed\u6127\uff01\u201d \u3000\u3000\u9752\u677e\u8038\u4e86\u8038\u80a9\u9053\uff1a\u201c\u90a3\u4e5f\u6ca1\u6cd5\u5b50\uff0c\u5e0c\u671b\u54b1\u4eec\u4ee5\u540e\u6709\u673a\u4f1a\u591a\u4e3a\u7687\u5bb6\u51fa\u4e9b\u529b\uff0c\u4e5f\u7b97\u5bf9\u738b\u5927\u4eba \u548c\u6c5f\u65bd\u4e3b\u7684\u4e00\u79cd\u62a5\u7b54\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u95f4\uff0c\u5df2\u8fdb\u5165\u7bf1\u7b06\u6765\u5230\u4e00\u95f4\u53a2\u623f\u95e8\u5916\u3002 \u3000\u3000\u9752\u677e\u5411\u91cc\u9ad8\u58f0\u9053\uff1a\u201c\u9752\u6728\u5e08\u5144\u628a\u738b\u5927\u4eba\u548c\u6c5f\u65bd\u4e3b\u8bf7\u6765\u4e86\uff0c\u5927\u5bb6\u5feb\u51fa\u6765\u8fce\u63a5\uff01\u201d \u3000\u3000\u738b\u5f64\u5fd9\u6447\u624b\u9053\uff1a\u201c\u542c\u8bf4\u5f88\u591a\u9053\u957f\u5df2\u7ecf\u53d7\u4e86\u4f24\uff0c\u7528\u4e0d\u7740\u591a\u793c\u3002\u201d \u3000\u3000\u53a2\u623f\u5185\u5f88\u5feb\u4fbf\u8d70\u51fa\u4e94\u516d\u540d\u9053\u4eba\u3002 \u3000\u3000\u4ed6\u4eec\u8863\u670d\u4e0a\u90fd\u6709\u8840\u8ff9\uff0c\u6709\u7684\u8fde\u8d70\u8def\u90fd\u662f\u4e00\u62d0\u4e00\u62d0\u7684\u3002 \u3000\u3000\u8fdb\u5165\u5c4b\u5185\uff0c\u53ea\u89c1\u5730\u4e0a\u6709\u597d\u51e0\u4e2a\u9053\u4eba\u5750\u5728\u90a3\u91cc\uff0c\u5176\u4e2d\u6709\u4e24\u540d\u5219\u662f\u8eba\u7740\u3002\u4e0d\u7528\u8bf4\uff0c\u8fd9\u4e9b\u4eba\u90fd \u662f\u53d7\u4f24\u8f83\u91cd\u7684\u3002 \u3000\u3000\u738b\u5f64\u8fc5\u901f\u7684\u671b\u4e86\u4e00\u904d\uff0c\u8bed\u5e26\u5b89\u6170\u7684\u9053\uff1a\u201c\u9053\u957f\u4eec\u8bf7\u5bbd\u5fc3\u4f11\u517b\uff0c\u738b\u67d0\u548c\u6c5f\u5927\u4fa0\u4eca\u665a\u4e00\u5b9a\u628a \u90a3\u4e09\u4e2a\u5973\u4eba\u751f\u64d2\u6d3b\u6349\uff0c\u4e3a\u5404\u4f4d\u51fa\u8fd9\u53e3\u6c14\u3002\u201d \u3000\u3000\u6c5f\u5e72\u91cc\u5219\u4ece\u6000\u91cc\u638f\u51fa\u4e00\u53ea\u836f\u74f6\uff0c\u4ea4\u4e0e\u9752\u6728\u9053\uff1a\u201c\u6709\u52b3\u7ed9\u4ed6\u4eec\u6bcf\u4f4d\u670d\u4e0b\u4e00\u7c92\uff0c\u8fd9\u836f\u4e38\u5bf9\u7597 \u6cbb\u5185\u5916\u4f24\u90fd\u5f88\u7075\u9a8c\u3002\u201d \u3000\u3000\u9752\u6728\u4e00\u8fb9\u9053\u8c22\uff0c\u4e00\u8fb9\u63a5\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5f53\u4e0b\uff0c\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u7b49\u4eba\u5c31\u6682\u65f6\u5728\u6c11\u5bb6\u6b47\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u665a\u9910\u4e5f\u662f\u5728\u6c11\u5bb6\u7528\u7684\u3002 \u3000\u3000\u996d\u540e\uff0c\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u7b49\u4eba\u4fbf\u5411\u90a3\u5ea7\u9053\u89c2\u51fa\u53d1\u3002 \u3000\u3000\u6b66\u5f53\u5f1f\u5b50\u4eec\u662f\u4ee5\u9752\u6728\u4e3a\u9996\u3002 \u3000\u3000\u672c\u6765\uff0c\u9752\u6728\u51c6\u5907\u8ba9\u4e0d\u66fe\u53d7\u4f24\u548c\u4f24\u52bf\u8f83\u8f7b\u7684\u5e08\u5144\u5f1f\u4e5f\u4e00\u8d77\u8ddf\u53bb\uff0c\u4f46\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u5374\u575a\u6301\u8981 \u4ed6\u4eec\u5728\u6c11\u5bb6\u4f11\u606f\uff0c\u53ea\u8ba9\u9752\u6728\u4e00\u4eba\u8d1f\u8d23\u5e26\u8def\u3002 \u3000\u3000\u4ec5\u4ec5\u4e94\u3001\u516d\u91cc\u8def\u987f\u996d\u5de5\u592b\u4fbf\u5df2\u5230\u8fbe\u3002 \u3000\u3000\u8fd9\u5ea7\u9053\u89c2\u89c4\u6a21\u867d\u4e0d\u7b97\u5c0f\uff0c\u5374\u5df2\u6b8b\u7834\u4e0d\u582a\uff0c\u800c\u4e14\u4e5f\u65e0\u4eba\u770b\u7ba1\u3002\u6b64\u523b\u5929\u8272\u867d\u7136\u5df2\u665a\uff0c\u4f46\u56e0\u6708 \u5df2\u5347\u8d77\uff0c\u5bf9\u9644\u8fd1\u60c5\u5f62\u4ecd\u80fd\u770b\u5f97\u5341\u5206\u6e05\u695a\u3002 \u3000\u3000\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u547d\u540c\u6765\u7684\u624b\u4e0b\u4eec\u8fdb\u5165\u623f\u5185\u5f85\u547d\uff0c\u53ea\u7531\u9752\u6728\u4e00\u4eba\u966a\u540c\u5bdf\u770b\u9644\u8fd1\u5f62\u52bf\u3002 \u5367\u9f99\u751f\u300a\u71d5\u5b50\u4f20\u5947\u300b \u7b2c\u4e8c\u5341\u4e94\u56de\u3000\u6b66\u5f53\u5047\u50e7 \u3000\u3000\u6c5f\u5343\u91cc\u95ee\u9053\uff1a\u201c\u4eca\u5929\u51cc\u6668\u53cc\u65b9\u6253\u6597\u7684\u5730\u70b9\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u9752\u6728\u9053\uff1a\u201c\u5c31\u5728\u5929\u4e95\u5904\uff0c\u6c5f\u65bd\u4e3b\u4ed4\u7ec6\u770b\u770b\uff0c\u5730\u4e0a\u4ecd\u53ef\u9690\u7ea6\u89c1\u5230\u8840\u8ff9\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u7559\u610f\u671b\u53bb\uff0c\u679c\u7136\u5730\u4e0a\u6709\u591a\u5904\u8840\u8ff9\u6591\u6591\u3002 \u3000\u3000\u5f53\u89c2\u5185\u89c2\u5916\u56db\u4e0b\u4fa6\u5bdf\u4e86\u4e00\u904d\u4e4b\u540e\uff0c\u9752\u6728\u9053\uff1a\u201c\u6c5f\u65bd\u4e3b\u548c\u738b\u5927\u4eba\u4e5f\u8fdb\u5185\u4f11\u606f\u5427\uff01\u73b0\u5c31\u7531\u8d2b \u9053\u5728\u95e8\u5916\u627e\u5904\u9690\u5bc6\u6240\u5728\u8d1f\u8d23\u8b66\u6212\uff0c\u4e00\u6709\u52a8\u9759\uff0c\u9a6c\u4e0a\u5411\u4e8c\u4f4d\u901a\u62a5\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u90a3\u5c31\u8f9b\u82e6\u9053\u957f\u4e86\uff01\u201d \u3000\u3000\u9752\u6728\u7acb\u5373\u5411\u89c2\u95e8\u5916\u800c\u53bb\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u548c\u738b\u5f64\u5e76\u672a\u8fdb\u5185\u4f11\u606f\uff0c\u4e24\u4eba\u4ecd\u5728\u5929\u4e95\u5185\u56db\u4e0b\u8d70\u52a8\u3002 \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u73b0\u5728\u624d\u4e0d\u8fc7\u4e00\u66f4\u5de6\u53f3\uff0c\u5bf9\u65b9\u8981\u6765\u4e5f\u7edd\u4e0d\u4f1a\u8fd9\u4e48\u5feb\uff0c\u54b1\u4eec\u8fd8\u662f\u5230\u91cc\u9762\u4f11\u606f\u4f11\u606f \u5427\uff01\u81f3\u4e8e\u8b66\u6212\uff0c\u9664\u9752\u6728\u9053\u957f\u5916\uff0c\u54b1\u4eec\u81ea\u5df1\u6700\u597d\u4e5f\u6d3e\u51fa\u4e24\u4e2a\u4eba\u53bb\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u5ffd\u7136\u5fc3\u6709\u6240\u52a8\u7684\u9053\uff1a\u201c\u738b\u8001\u5f1f\u4e0d\u89c9\u5f97\u4eca\u665a\u7684\u4e8b\u6709\u4e9b\u53ef\u7591\u5417\uff1f\u201d \u3000\u3000\u738b\u5f64\u6115\u7136\u9053\uff1a\u201c\u54ea\u91cc\u53ef\u7591\uff1f\u201d \u3000\u3000\u201c\u90a3\u4e09\u540d\u5973\u5b50\uff0c\u4e3a\u4ec0\u4e48\u4eca\u665a\u8981\u4f4f\u5728\u8fd9\u91cc\uff1f\u8fd9\u4ef6\u4e8b\u5c31\u4ee4\u6c5f\u67d0\u767e\u601d\u4e0d\u89e3\u3002\u201d \u3000\u3000\u201c\u83ab\u975e\u6c5f\u5144\u8ba4\u4e3a\u5176\u4e2d\u6709\u8bc8\uff1f\u201d \u3000\u3000\u201c\u6c5f\u67d0\u4e0d\u80fd\u4e0d\u6709\u6240\u6000\u7591\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u9752\u6728\u9053\u957f\u4e5f\u6709\u8bc8\u7f57\uff1f\u201d \u3000\u3000\u201c\u6b64\u523b\u4e0d\u80fd\u65ad\u5b9a\uff0c\u81f3\u5c11\u54b1\u4eec\u8981\u63d0\u9ad8\u8b66\u89c9\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u5982\u6b64\uff0c\u6c5f\u5144\u521a\u624d\u4e3a\u4ec0\u4e48\u4efb\u7531\u4ed6\u79bb\u5f00\uff1f\u201d \u3000\u3000\u201c\u53ea\u6709\u8ba9\u4ed6\u5355\u72ec\u79bb\u5f00\uff0c\u624d\u80fd\u5224\u65ad\u51fa\u4ed6\u7a76\u7adf\u662f\u654c\u662f\u53cb\uff1f\u201d \u3000\u3000\u201c\u6c5f\u5144\u8fd9\u8bdd\uff1f\u201d \u3000\u3000\u201c\u738b\u8001\u5f1f\u968f\u6211\u6765\uff01\u201d \u3000\u3000\u8fdb\u5165\u4e00\u95f4\u7a7a\u5ba4\uff0c\u6c5f\u5343\u91cc\u8981\u738b\u5f64\u5148\u5728\u7a7a\u5c4b\u5185\u7b49\u5019\uff0c\u7136\u540e\u81ea\u5df1\u53bb\u628a\u5c0f\u71d5\u5b50\u548c\u5c0f\u9053\u58eb\u767d\u7fbd\u53eb\u4e86 \u8fc7\u6765\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u95ee\u5c0f\u9053\u58eb\u767d\u7fbd\u9053\uff1a\u201c\u4f60\u662f\u6b66\u5f53\u51fa\u8eab\uff0c\u53ef\u8ba4\u8bc6\u5148\u524d\u5230\u5f00\u5c01\uff0c\u4e3a\u54b1\u4eec\u5e26\u8def\u7684\u9752\u6728\u9053 \u957f\uff1f\u201d \u3000\u3000\u767d\u7fbd\u6447\u6447\u5934\u9053\uff1a\u201c\u597d\u50cf\u6ca1\u89c1\u8fc7\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u4e0d\u8ba4\u8bc6\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u65e9\u8bf4\uff1f\u201d \u3000\u3000\u201c\u6c5f\u5927\u4fa0\u662f\u77e5\u9053\u7684\uff0c\u5c0f\u9053\u539f\u88ab\u6b66\u5f53\u9010\u51fa\u95e8\u5899\uff0c\u79bb\u5f00\u6b66\u5f53\u5df2\u7ecf\u597d\u51e0\u5e74\u4e86\uff0c\u4e0d\u8ba4\u8bc6\u4ed6\uff0c\u5e76\u4e0d \u8db3\u4e3a\u5947\u3002\u201d \u3000\u3000\u201c\u5728\u6c11\u5bb6\u7684\u90a3\u4e9b\u9053\u957f\uff0c\u4f60\u662f\u5426\u8ba4\u8bc6\uff1f\u201d \u3000\u3000\u201c\u4e5f\u4e0d\u8ba4\u8bc6\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u7684\u5e74\u7eaa\u90fd\u6bd4\u4f60\u5927\uff0c\u53ef\u89c1\u4ed6\u4eec\u90fd\u662f\u4f60\u7684\u5e08\u5144\u5e08\u53d4\u8f88\u5206\uff0c\u90a3\u4e48\u591a\u4eba\uff0c\u600e\u53ef\u80fd\u4e00\u4e2a\u90fd\u4e0d \u8ba4\u8bc6\uff1f\u201d \u3000\u3000\u5c0f\u9053\u58eb\u767d\u7fbd\u4e3a\u4eba\u673a\u4f36\uff0c\u54ea\u80fd\u542c\u4e0d\u6e05\u6c5f\u5343\u91cc\u95ee\u8bdd\u7528\u610f\uff0c\u554a\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u83ab\u975e\u5176\u4e2d\u6709\u8bc8\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u6b64\u523b\u8fd8\u65e0\u6cd5\u5b8c\u5168\u786e\u5b9a\uff0c\u4e0d\u8fc7\u6211OJ\u5fc5\u987b\u683c\u5916\u63d0\u9ad8\u8b66\u89c9\u3002\u201d \u3000\u3000\u5c0f\u9053\u58eb\u767d\u7fbd\u5fd9\u9053\uff1a\u201c\u5176\u5b9e\u5c0f\u9053\u56e0\u4e3a\u5bf9\u4ed6\u4eec\u4e00\u4e2a\u4e5f\u4e0d\u8ba4\u8bc6\uff0c\u81ea\u7136\u5185\u5fc3\u4e5f\u6709\u7740\u7591\u95ee\uff0c\u53ea\u662f\u56e0 \u4e3a\u2026\u2026\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u5c0f\u9053\u7684\u4e0a\u4e00\u4ee3\u5e08\u4f2f\u5e08\u53d4\u4eec\uff0c\u7684\u786e\u662f\u9752\u5b57\u8f88\uff0c\u82e5\u5c0f\u9053\u5411\u4ed6\u4eec\u76d8\u6839\u7a76\u5e95\uff0c\u603b\u662f\u6709\u4e9b\u4e0d \u793c\u8c8c\u3002\u201d \u3000\u3000\u522b\u8bf4\u8fd9\u4e9b\u4e86\uff0c\u4f60\u73b0\u5728\u5c31\u548c\u5c0f\u71d5\u5b50\u5230\u9053\u89c2\u95e8\u5916\u8d1f\u8d23\u8b66\u6212\uff0c\u4e00\u6709\u60c5\u51b5\uff0c\u7acb\u5373\u56de\u6765\u901a\u62a5\u3002\u201c \u3000\u3000\u5c0f\u71d5\u5b50\u56db\u4e0b\u671b\u4e86\u4e00\u773c\uff0c\u95ee\u9053\uff1a\u201c\u90a3\u4f4d\u9752\u6728\u9053\u957f\u5462\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u4ed6\u5df2\u5148\u5230\u95e8\u5916\u8d1f\u8d23\u8b66\u6212\u4e86\uff0c\u4f60\u4eec\u4e24\u4eba\u5fc5\u987b\u5148\u548c\u4ed6\u4f1a\u5408\uff0c\u5e76\u6697\u4e2d\u7559\u610f\u4ed6\u7684\u884c \u52a8\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u627e\u4e0d\u5230\u4ed6\u5462\uff1f\u201d \u3000\u3000\u201c\u90a3\u4ed6\u5c31\u8d8a\u53d1\u53ef\u7591\u4e86\uff0c\u540c\u6837\u4e5f\u8981\u56de\u6765\u901a\u62a5\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u9886\u547d\u800c\u53bb\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u548c\u738b\u5f64\u56de\u5230\u4f17\u4eba\u4f11\u606f\u4e4b\u5904\u3002 \u3000\u3000\u53ea\u89c1\u9ed1\u7f57\u6c49\u4e03\u5de7\u50e7\u3001\u8001\u5996\u5a46\u3001\u738b\u91cd\u5c71\u3001\u9648\u5b8f\u3001\u8d75\u4fdd\u7b49\u4eba\uff0c\u6709\u7684\u5df2\u7ecf\u7761\u7740\u4e86\uff0c\u6709\u7684\u5219\u5728\u4f4e \u58f0\u804a\u5929\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u548c\u738b\u5f64\u51b3\u5b9a\u5c31\u5728\u4e91\u623f\u4e00\u89d2\u6682\u65f6\u95ed\u76ee\u517b\u795e\u3002 \u3000\u3000\u5927\u7ea6\u987f\u996d\u4e4b\u540e\uff0c\u5ffd\u89c1\u5c0f\u71d5\u5b50\u5306\u5306\u5954\u4e86\u8fdb\u6765\u9053\uff1a\u201c\u6c5f\u53d4\u53d4\u3001\u738b\u5927\u4eba\uff0c\u90a3\u4f4d\u9752\u6728\u9053\u957f\u4e0d\u89c1 \u4e86\uff0c\u6211\u548c\u5c0f\u9053\u58eb\u627e\u4e86\u534a\u5929\u90fd\u6ca1\u627e\u7740\u4ed6\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u7acb\u5373\u7ad9\u8d77\u8eab\u6765\u9053\uff1a\u201c\u7cdf\u7cd5\uff0c\u54b1\u4eec\u4e2d\u8ba1\u4e86\uff01\u201d \u3000\u3000\u5176\u4ed6\u7684\u4eba\u4e5f\u5168\u95fb\u8eab\u800c\u8d77\uff0c\u5e76\u5404\u81ea\u6284\u8d77\u5175\u5203\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u5429\u5490\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u5feb\u51fa\u53bb\u7ee7\u7eed\u8d1f\u8d23\u8b66\u6212\uff01\u201d \u3000\u3000\u738b\u5f64\u4e5f\u547d\u4ee4\u8d75\u4fdd\u3001\u9648\u5b8f\u81f3\u9053\u89c2\u4e24\u4fa7\u53ca\u540e\u65b9\u6ce8\u610f\u76d1\u89c6\u3002 \u3000\u3000\u738b\u5f64\u53c8\u95ee\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u6c5f\u5144\uff0c\u54b1\u4eec\u8be5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u4e8b\u5230\u5982\u4eca\uff0c\u82e5\u73b0\u5728\u64a4\u8d70\uff0c\u53cd\u800c\u4e0d\u5999\uff1b\u4e0d\u5982\u5c31\u5728\u8fd9\u91cc\u548c\u5bf9\u65b9\u51b3\u4e00\u6b7b \u6218\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u4f1a\u6709\u6240\u6536\u83b7\u3002\u201d \u3000\u3000\u201c\u9053\u89c2\u540e\u662f\u7247\u6811\u6797\uff0c\u54b1\u4eec\u662f\u5426\u8be5\u64a4\u5230\u6811\u6797\u5185\uff0c\u8fce\u654c\u53cd\u800c\u65b9\u4fbf\u3002\u201d \u3000\u3000\u201c\u4e5f\u597d\uff0c\u90a3\u5c31\u9a6c\u4e0a\u884c\u52a8\u3002\u201d \u3000\u3000\u4e8e\u662f\uff0c\u4f17\u4eba\u7acb\u5373\u7531\u9053\u89c2\u540e\u95e8\u64a4\u81f3\u6811\u6797\uff0c\u4e00\u9762\u6d3e\u4eba\u901a\u77e5\u5c0f\u71d5\u5b50\u3001\u767d\u7fbd\u3001\u9648\u5b8f\u3001\u8d75\u4fdd\u4e5f\u64a4\u79bb \u539f\u5730\u3002 \u3000\u3000\u6811\u6797\u79bb\u9053\u89c2\u4e0d\u8fc7\u4e09\u4e94\u5341\u6b65\uff0c\u9644\u8fd1\u662f\u4e00\u7247\u5c71\u5761\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u548c\u738b\u5f64\u91cd\u65b0\u90e8\u7f72\uff0c\u9648\u5b8f\u548c\u8d75\u4fdd\u62c5\u4efb\u6811\u6797\u540e\u65b9\u548c\u5de6\u53f3\u4e24\u4fa7\u8b66\u6212\uff0c\u524d\u65b9\u56e0\u4e3a\u9884\u6599\u4e2d\u662f \u5bf9\u65b9\u4e3b\u653b\u5fc5\u7ecf\u4e4b\u5730\uff0c\u4ecd\u6d3e\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u8d1f\u8d23\u76d1\u89c6\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u5230\u8fbe\u6811\u6797\u4e4b\u540e\uff0c\u4e24\u4eba\u7ecf\u8fc7\u4e00\u756a\u5546\u8bae\uff0c\u51b3\u5b9a\u628a\u8b66\u6212\u5730\u70b9\u5411\u524d\u63a8\u8fdb\u81f3\u9053\u89c2\u7684\u5927 \u6bbf\u5c4b\u9876\u4e0a\u3002 \u3000\u3000\u56e0\u4e3a\u5927\u6bbf\u662f\u6574\u5ea7\u9053\u89c2\u6700\u9ad8\u7684\u5efa\u7b51\u7269\uff0c\u5c45\u9ad8\u4e34\u4e0b\uff0c\u53c8\u6709\u6708\u5149\uff0c\u81f3\u5c11\u53ef\u4ee5\u770b\u51fa\u6570\u5341\u4e08\u8fdc\u7684\u666f \u7269\u3002 \u3000\u3000\u4e24\u4eba\u5f88\u5feb\u4fbf\u8dc3\u4e0a\u5927\u6bbf\u5c4b\u810a\u540e\u65b9\uff0c\u53ea\u9732\u51fa\u534a\u4e2a\u5934\u5411\u5916\u89c2\u770b\u3002 \u3000\u3000\u6b64\u523b\uff0c\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u7684\u5fc3\u5883\u662f\u65e2\u7d27\u5f20\u53c8\u5174\u594b\uff0c\u7d27\u5f20\u7684\u662f\u8fd9\u4e00\u6218\u540e\u679c\u96be\u6599\uff0c\u5174\u594b\u7684\u662f\u7ec8\u4e8e \u6709\u673a\u4f1a\u5927\u663e\u8eab\u624b\u4e86\u3002 \u3000\u3000\u8d1f\u8d23\u8b66\u6212\u662f\u4ef6\u5e76\u4e0d\u8f7b\u677e\u7684\u4e8b\uff0c\u4e00\u5206\u4e00\u79d2\u4e5f\u4e0d\u6562\u677e\u61c8\uff0c\u8d8a\u6beb\u65e0\u52a8\u9759\uff0c\u53cd\u800c\u8d8a\u89c9\u4e0d\u5b89\u3002 \u3000\u3000\u5c31\u8fd9\u6837\u8db3\u8db3\u4e00\u4e2a\u65f6\u8fb0\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u4f30\u8ba1\u65f6\u95f4\u5df2\u662f\u4e8c\u66f4\u8fc7\u540e\uff0c\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u7ec8\u4e8e\u53d1\u73b0\u6709\u4e86\u60c5\u51b5\u3002 \u3000\u3000\u6708\u5149\u4e0b\uff0c\u53ea\u89c1\u9053\u89c2\u524d\u7684\u6811\u6797\u524d\u65b9\u5c0f\u5f84\u4e0a\uff0c\u8db3\u6709\u51e0\u5341\u6761\u4eba\u5f71\u5411\u9053\u89c2\u65b9\u5411\u7f13\u7f13\u8815\u52a8\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u5fc3\u795e\u4e3a\u4e4b\u4e00\u9707\uff0c\u7cbe\u795e\u4e5f\u4e3a\u4e4b\u4e00\u632f\u3002 \u3000\u3000\u4eba\u5f71\u867d\u7136\u8815\u52a8\u7f13\u6162\uff0c\u4f46\u5374\u5728\u4e0d\u4e45\u4e4b\u540e\u5df2\u8d8a\u8fc7\u6811\u6797\uff0c\u505c\u7559\u5728\u6811\u6797\u524d\u7f18\u7684\u9690\u5bc6\u5904\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u867d\u662f\u6b66\u529f\u6781\u9ad8\uff0c\u5374\u96be\u514d\u7f3a\u4e4f\u6218\u9635\u7ecf\u9a8c\uff0c\u4f4e\u58f0\u95ee\u767d\u7fbd\u9053\uff1a\u201c\u54b1\u4eec\u8981\u4e0d\u8981\u56de\u53bb\u4e00\u4eba\u5411 \u738b\u5927\u4eba\u548c\u6c5f\u53d4\u53d4\u901a\u62a5\uff1f\u201d \u3000\u3000\u767d\u7fbd\u9053\uff1a\u201c\u7528\u4e0d\u7740\u7740\u6025\uff0c\u770b\u60c5\u5f62\u4ed6\u4eec\u4e00\u5b9a\u8fd8\u4e0d\u6e05\u695a\u54b1\u4eec\u7684\u4eba\u5df2\u64a4\u5230\u5e99\u540e\u6811\u6797\u91cc\uff0c\u7b49\u4ed6 fIJ\u5f00\u59cb\u884c\u52a8\u540e\uff0c\u518d\u901a\u62a5\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u54b1nl\u65e9\u4e9b\u513f\u901a\u62a5\uff0c\u4ed6\u4eec\u5c31\u53ef\u591a\u4e00\u4e9b\u65f6\u95f4\u51c6\u5907\u3002\u201d \u3000\u3000\u201c\u4f60\u653e\u5fc3\uff0c\u4ed6\u4eec\u8c01\u90fd\u4e0d\u66fe\u7761\uff0c\u53ea\u8981\u5bf9\u65b9\u6709\u4e86\u58f0\u97f3\uff0c\u4ed6\u4eec\u5fc5\u5b9a\u4f1a\u542c\u5230\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u4e0d\u518d\u8bf4\u4ec0\u4e48\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5bf9\u65b9\u90a3\u6570\u5341\u4eba\u4ecd\u9690\u4f0f\u5728\u6811\u4e0b\u672a\u52a8\u3002 \u3000\u3000\u663e\u7136\uff0c\u5bf9\u65b9\u662f\u5728\u89c2\u5bdf\u52a8\u9759\uff0c\u7136\u540e\u518d\u5927\u4e3e\u8fdb\u653b\u3002 \u3000\u3000\u53ea\u542c\u4e00\u4e2a\u6c89\u6d51\u7684\u58f0\u97f3\u9053\uff1a\u201c\u4f60\u80fd\u65ad\u5b9a\u4ed6\u4eec\u5df2\u5728\u9053\u89c2\u4e2d\u4e86\u5417\uff1f\u201d \u3000\u3000\u53e6\u4e00\u4e2a\u58f0\u97f3\u9053\uff1a\u201c\u662f\u5c5e\u4e0b\u628a\u4ed6\u4eec\u5e26\u8fdb\u9053\u89c2\u53bb\u7684\uff0c\u4ed6\u4eec\u4e00\u5b9a\u5728\u9053\u89c2\u91cc\u3002\u201d \u3000\u3000\u201c\u96be\u9053\u4ed6\u4eec\u5c31\u4e0d\u53ef\u80fd\u53c8\u64a4\u8d70\u4e86\u5417\uff1f\u201d \u3000\u3000\u201c\u4e0d\u53ef\u80fd\uff0c\u54b10\uff3d\u5728\u9053\u89c2\u56db\u5468\u534a\u91cc\u4e4b\u5916\u6d3e\u6709\u8b66\u6212\uff0c\u5982\u679c\u4ed6\u4eec\u5df2\u64a4\u8d70\uff0c\u4e00\u5b9a\u4f1a\u88ab\u54b1\u4eec\u8b66\u6212 \u7684\u5f1f\u5144\u53d1\u73b0\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u4e3a\u4ec0\u4e48\u4e00\u70b9\u52a8\u9759\u4e5f\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u4e00\u5b9a\u662f\u7761\u7740\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u4e5f\u672a\u514d\u592a\u628a\u4ed6\u4eec\u5c0f\u770b\u4e86\uff0c\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u662f\u4f55\u7b49\u6837\u4eba\uff1f\u600e\u4e48\u4f1a\u8fde\u8fd9\u4e00\u70b9\u8b66\u89c9\u5fc3\u90fd\u6ca1 \u6709\uff1f\u201d\u3002\u53e6\u4e00\u4eba\u4f3c\u4e4e\u7b54\u4e0d\u4e0a\u8bdd\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u65e9\u5df2\u542c\u51fa\uff0c\u53e6\u4e00\u4eba\u6b63\u662f\u81ea\u79f0\u6b66\u5f53\u9752\u6728\u7684\u4eba\u3002 \u3000\u3000\u6c89\u5bc2\u4e86\u534a\u664c\uff0c\u90a3\u96c4\u6d51\u7684\u58f0\u97f3\u53c8\u54cd\u4e86\u8d77\u6765\u9053\uff1a\u201c\u9634\u660c\uff0c\u65e2\u7136\u4ed6\u4eec\u662f\u4f60\u5e26\u8fdb\u9053\u89c2\u7684\uff0c\u73b0\u5728\u5c31 \u7531\u4f60\u8fdb\u53bb\u63a2\u67e5\u4e00\u4e0b\uff0c\u7136\u540e\u518d\u901f\u901f\u5411\u6211\u56de\u62a5\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u8fd9\u624d\u77e5\u9053\u90a3\u5047\u626e\u9752\u6728\u7684\u53eb\u9634\u660c\u3002 \u3000\u3000\u9634\u660c\u4f3c\u4e4e\u6709\u4e9b\u80c6\u602f\uff0c\u987f\u4e86\u4e00\u987f\u9053\uff1a\u201c\u5c5e\u4e0b\u548c\u4ed6\u4eec\u79bb\u5f00\u8fd9\u4e48\u4e45\u4e86\uff0c\u53ea\u6015\u73b0\u5728\u4ed6\u4eec\u5df2\u7ecf\u8d77\u7591 \u5fc3\u4e86\u3002\u201d \u3000\u3000\u90a3\u6c89\u6d51\u7684\u58f0\u97f3\u9053\uff1a\u201c\u6211\u66fe\u8981\u4f60\u4e00\u76f4\u548c\u4ed6\u4eec\u5468\u65cb\u4e0b\u53bb\uff0c\u76f4\u5230\u6211\u4eec\u7684\u5927\u961f\u4eba\u9a6c\u5230\u6765\uff0c\u518d\u8bbe\u6cd5 \u79bb\u5f00\uff0c\u8c01\u8ba9\u4f60\u4e00\u628a\u4ed6\u4eec\u5f15\u8fdb\u9053\u89c2\u5c31\u56de\u6765\u7684\uff1f\u201d \u3000\u3000\u9634\u660c\u58f0\u97f3\u53d1\u98a4\u7684\u9053\uff1a\u201c\u5c5e\u4e0b\u662f\u4e3a\u4e86\u65e9\u70b9\u56de\u6765\u901a\u62a5\uff0c\u6240\u4ee5\u2026\u2026\u201d \u3000\u3000\u201c\u4ec0\u4e48\u6240\u4ee5\u4e0d\u6240\u4ee5\u7684\uff0c\u90a3\u5904\u6c11\u5bb6\u4f4f\u7684\u5168\u662f\u54b1\u4eec\u7684\u4eba\uff0c\u96be\u9053\u4ed6\u4eec\u4e0d\u80fd\u901a\u62a5\uff1f\u5e9f\u8bdd\u5c11\u8bf4\uff0c\u5feb \u8fdb\u53bb\u770b\u770b\uff01\u201d \u3000\u3000\u767d\u7fbd\u8f7b\u8f7b\u78b0\u4e86\u5c0f\u71d5\u5b50\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u73b0\u5728\u54b1\u4eec\u4e00\u4eba\u5e94\u4ed8\u8fd9\u53eb\u9634\u660c\u7684\uff0c\u4e00\u4eba\u56de\u53bb\u901a\u62a5\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u90a3\u5c31\u7531\u4f60\u56de\u53bb\u901a\u62a5\uff0c\u8fd9\u91cc\u4ea4\u7ed9\u6211\u4e86\u3002\u201d \u3000\u3000\u767d\u7fbd\u7acb\u5373\u7531\u5927\u6bbf\u540e\u65b9\u8dc3\u4e0b\uff0c\u5954\u5411\u540e\u9762\u6811\u6797\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u4e5f\u7531\u540e\u9762\u8dc3\u4e0b\uff0c\u7136\u540e\u7ed5\u5230\u5929\u4e95\u3002 \u3000\u3000\u5f53\u4ed6\u521a\u521a\u6765\u5230\u5929\u4e95\u4e0d\u4e45\uff0c\u4fbf\u89c1\u9634\u660c\u8e51\u624b\u8e51\u811a\u7684\u7531\u5927\u95e8\u5916\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u662f\u9752\u6728\u9053\u957f\u5417\uff1f\u201d \u3000\u3000\u9634\u660c\u5148\u662f\u4e00\u6014\uff0c\u4f46\u5f88\u5feb\u4fbf\u8d70\u8fd1\u8fc7\u6765\u9053\uff1a\u201c\u539f\u6765\u5c0f\u65bd\u4e3b\u5728\u8fd9\u91cc\uff0c\u53ef\u662f\u8d1f\u8d23\u8b66\u6212\u7684\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u9053\u957f\u4e5f\u662f\u8d1f\u8d23\u8b66\u6212\u7684\uff0c\u5728\u4e0b\u600e\u4e48\u5230\u5904\u627e\u4e0d\u5230\u4f60\uff1f\u201d \u3000\u3000\u9752\u6728\u5e72\u54b3\u4e86\u4e24\u58f0\u9053\uff1a\u201c\u8d2b\u9053\u62c5\u5fc3\u5979\u4eec\u6765\u65f6\uff0c\u738b\u5927\u4eba\u548c\u6c5f\u5927\u4fa0\u53ef\u80fd\u63aa\u624b\u4e0d\u53ca\uff0c\u53ea\u597d\u5230\u5e99\u5916 \u6811\u6797\u524d\u7f18\u4e86\u671b\uff0c\u4ee5\u4fbf\u8ba9\u738b\u5927\u4eba\u548c\u6c5f\u5927\u4fa0\u591a\u4e9b\u51c6\u5907\u65f6\u95f4\u3002\u201d \u3000\u3000\u201c\u5979\u4eec\u600e\u4e48\u5230\u73b0\u5728\u8fd8\u6ca1\u52a8\u9759\uff1f\u201d \u3000\u3000\u201c\u8d2b\u9053\u4e5f\u89c9\u5f97\u5947\u602a\uff0c\u6240\u4ee5\u624d\u4e0d\u5f97\u4e0d\u56de\u6765\u5411\u738b\u5927\u4eba\u548c\u6c5f\u5927\u4fa0\u901a\u62a5\u4e00\u58f0\uff0c\u4ed6\u4eec\u662f\u5426\u90fd\u7761\u7740 \u4e86\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u56e0\u4e3a\u4e00\u76f4\u4e0d\u89c1\u52a8\u9759\uff0c\u6599\u60f3\u5bf9\u65b9\u53ef\u80fd\u4e0d\u6765\u4e86\uff0c\u4fbf\u51b3\u5b9a\u5927\u5bb6\u90fd\u4f11\u606f\uff0c\u53ea\u7559\u5728\u4e0b\u4e00\u4eba\u8d1f \u8d23\u8b66\u6212\u3002\u201d \u3000\u3000\u4ed6\u4eec\u7761\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u201c \u3000\u3000\u5c0f\u71d5\u5b50\u968f\u4fbf\u4e00\u6307\u9053\uff1a\u201c\u5c31\u5728\u5927\u6bbf\u4e00\u4fa7\u90a3\u95f4\u7a7a\u5c4b\u91cc\uff0c\u9053\u957f\u8981\u4e0d\u8981\u8fdb\u53bb\u89c1\u89c1\u738b\u5927\u4eba\u548c\u6c5f\u5927 \u4fa0\uff1f\u201d \u3000\u3000\u9634\u660c\u6447\u5934\u9053\uff1a\u201c\u4e0d\u5fc5\u4e86\uff01\u201d \u3000\u3000\u4ed6\u7684\u8bdd\u521a\u521a\u8bf4\u5b8c\uff0c\u731b\u7136\u4e00\u6307\uff0c\u76f4\u5411\u5c0f\u71d5\u5b50\u524d\u80f8\u201c\u7384\u673a\u7a74\u201d\u6233\u6765\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u7684\u52a8\u4f5c\u6bd4\u5bf9\u65b9\u8fd8\u5feb\uff0c\u9634\u660c\u7684\u6307\u98ce\u5c1a\u672a\u53d1\u51fa\u4e00\u534a\uff0c\u4fbf\u5df2\u5148\u88ab\u5236\u4f4f\u7a74\u9053\uff0c\u5f53\u573a\u5012\u4e0b\u4eba \u4e8b\u4e0d\u77e5\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u7acb\u5373\u5939\u8d77\u9634\u660c\uff0c\u4ee5\u6700\u5feb\u7684\u901f\u5ea6\u7531\u540e\u95e8\u5954\u56de\u6811\u6797\uff0c\u5c06\u9634\u660c\u5f80\u5730\u4e0a\u4e00\u6254\uff0c\u9053\uff1a\u201c\u738b\u5927 \u4eba\u3001\u6c5f\u53d4\u53d4\uff0c\u9752\u6728\u9053\u957f\u6765\u4e86\uff01\u201d \u3000\u3000\u8fd9\u65f6\u738b\u5f64\u3001\u6c5f\u5343\u91cc\u3001\u738b\u91cd\u5c71\u3001\u9ed1\u50e7\u3001\u767d\u7fbd\u3001\u8d75\u4fdd\u3001\u9648\u5b8f\u4ee5\u53ca\u8001\u5996\u5a46\uff0c\u65e9\u5df2\u96c6\u4e2d\u4e00\u5904\uff0c\u624b \u6301\u5175\u5203\uff0c\u51c6\u5907\u505c\u5f53\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u95ee\u9053\uff1a\u201c\u662f\u6b7b\u7684\u8fd8\u662f\u6d3b\u7684\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u6d3b\u7684\uff0c\u8981\u4e0d\u8981\u89e3\u5f00\u7a74\u9053\u95ee\u8bdd\uff1f\u201d \u3000\u3000\u201c\u6765\u4e0d\u53ca\u4e86\uff0c\u5148\u628a\u4ed6\u6401\u5728\u4e00\u8fb9\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u7acb\u5373\u628a\u9634\u660c\u79fb\u5230\u8349\u4e1b\u4e2d\u3002 \u3000\u3000\u5927\u7ea6\u76cf\u8336\u5de5\u592b\u8fc7\u540e\uff0c\u5bf9\u65b9\u6570\u5341\u4eba\u5df2\u7531\u9053\u89c2\u540e\u65b9\u4ee5\u5305\u56f4\u65b9\u5f0f\u63a9\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u663e\u7136\uff0c\u4ed6\u4eec\u662f\u5df2\u641c\u7d22\u8fc7\u9053\u89c2\u5185\u7a7a\u65e0\u4e00\u4eba\uff0c\u624d\u518d\u5411\u6811\u6797\u63a5\u8fd1\u3002 \u3000\u3000\u4e00\u8f6e\u660e\u6708\uff0c\u9ad8\u6302\u4e2d\u5929\u3002 \u3000\u3000\u5bf9\u65b9\u53c8\u6e10\u884c\u6e10\u8fd1\u3002 \u3000\u3000\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u65b9\u9762\u7684\u4eba\u53ef\u4ee5\u6e05\u6670\u770b\u51fa\uff0c\u8fd9\u6570\u5341\u4eba\u5168\u662f\u6e05\u4e00\u8272\u9ed1\u8272\u8863\u888d\u6253\u626e\uff0c\u4e14\u4e2a\u4e2a\u6234\u7740 \u5934\u7f69\uff0c\u624b\u91cc\u5219\u591a\u534a\u6a2a\u7740\u660e\u6643\u6643\u7684\u957f\u5200\uff0c\u6708\u5149\u4e0b\u8000\u773c\u751f\u8f89\u3002 \u3000\u3000\u738b\u5f64\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u6c5f\u5144\u53ef\u770b\u51fa\u6765\u4eba\u7684\u8eab\u4efd\u6765\u8def\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u7d27\u76b1\u7740\u53cc\u7709\u9053\uff1a\u201c\u6c5f\u67d0\u4e00\u65f6\u4e4b\u95f4\u4e5f\u96be\u4ee5\u89c2\u5bdf\u5f97\u51fa\u3002\u201d \u3000\u3000\u201c\u54b1\u4eec\u662f\u5426\u9a6c\u4e0a\u51b2\u51fa\u53bb\u8fce\u6218\uff1f\u201d \u3000\u3000\u201c\u73b0\u5728\u5bf9\u65b9\u5fc5\u5b9a\u8fd8\u672a\u53d1\u73b0\u54b1\u4eec\uff0c\u6700\u597d\u7b49\u4ed6\u4eec\u63a5\u8fd1\u81f3\u4e24\u4e09\u4e08\u8ddd\u79bb\uff0c\u518d\u51b2\u51fa\u53bb\u6740\u4ed6\u4eec\u4e2a\u63aa\u624b \u4e0d\u53ca\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u8bf4\u7740\uff0c\u4e00\u9762\u5411\u8eab\u540e\u4f17\u4eba\u4f4e\u58f0\u4ea4\u4ee3\uff0c\u4e00\u9762\u7ee7\u7eed\u76d1\u89c6\u3002 \u3000\u3000\u5c31\u5728\u5bf9\u65b9\u8ddd\u6811\u6797\u524d\u7f18\u53ea\u6709\u4e24\u4e09\u4e08\u65f6\uff0c\u7a81\u7136\u5728\u5176\u4e2d\u4e00\u4eba\u624b\u52bf\u6307\u6325\u4e4b\u4e0b\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u63a5\u7740\uff0c\u90a3\u6307\u6325\u8005\u8f6c\u5934\u5429\u5490\u8eab\u65c1\u4e00\u4eba\u9053\uff1a\u201c\u8d75\u94ee\uff0c\u4f60\u5148\u8fdb\u53bb\u770b\u770b\uff0c\u8981\u7279\u522b\u8c28\u614e\u5c0f\u5fc3\u3002\u201d \u3000\u3000\u8fd9\u6307\u6325\u8005\u58f0\u8c03\u6c89\u6d51\uff0c\u6b63\u662f\u5148\u524d\u5728\u5e99\u524d\u6811\u6797\u91cc\u53d1\u8bdd\u7684\u90a3\u4eba\u3002 \u3000\u3000\u90a3\u53eb\u8d75\u94ee\u7684\u5949\u547d\u4e4b\u540e\uff0c\u624b\u6a2a\u957f\u5200\uff0c\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u4e00\u6b65\u4e00\u6b65\u5411\u6811\u6797\u800c\u6765\u3002 \u3000\u3000\u6b64\u523b\uff0c\u738b\u5f64\u548c\u6c5f\u5e72\u91cc\u7b49\u4e5d\u4eba\uff0c\u90fd\u96c6\u4e2d\u5728\u6797\u5185\u4e00\u4e1b\u77ee\u6811\u4e4b\u540e\uff0c\u82e5\u4e00\u79fb\u52a8\uff0c\u53cd\u800c\u66f4\u6613\u88ab\u5bf9\u65b9 \u53d1\u73b0\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u6c5f\u5343\u91cc\u5f53\u5148\u4e00\u8dc3\u800c\u51fa\uff0c\u624b\u8d77\u4e00\u5251\uff0c\u4fbf\u5c06\u8d75\u94ee\u659c\u80a9\u5e26\u80cc\u81c2\u5012\u5728\u5730\uff0c\u7136\u540e\u817e\u8eab\u76f4\u5411\u654c \u9635\u4e2d\u51b2\u53bb\u3002 \u3000\u3000\u738b\u5f64\u7b49\u4eba\u4e00\u89c1\u6c5f\u5343\u91cc\u52a8\u4e0a\u4e86\u624b\uff0c\u968f\u5373\u4e5f\u624b\u4ed7\u5175\u5203\u51b2\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5176\u4f59\u4e03\u4eba\u51e0\u4e4e\u4e5f\u5728\u540c\u4e00\u65f6\u95f4\u817e\u8eab\u800c\u51fa\u3002 \u3000\u3000\u4e00\u573a\u8165\u98ce\u8840\u96e8\u7684\u640f\u6740\u5c31\u6b64\u5c55\u5f00\u3002 \u3000\u3000\u987f\u65f6\uff0c\u5175\u5203\u4ea4\u51fb\u4e0e\u558a\u6740\u4e4b\u58f0\u4ea4\u7ec7\u6210\u4e00\u7247\u3002 \u3000\u3000\u738b\u5f64\u3001\u6c5f\u5343\u91cc\u3001\u5c0f\u71d5\u5b50\u4e09\u4eba\u5168\u79f0\u5f97\u4e0a\u662f\u5f53\u4eca\u6b66\u6797\u7edd\u9876\u9ad8\u624b\uff0c\u800c\u9ed1\u50e7\u3001\u767d\u7fbd\u4e5f\u90fd\u662f\u6b66\u6280\u8d85 \u7fa4\uff0c\u5373\u4f7f\u738b\u91cd\u5c71\u3001\u9648\u5b8f\u3001\u8d75\u4fdd\u3001\u8001\u5996\u5a46\u7b49\u4eba\uff0c\u8eab\u624b\u4e5f\u90fd\u4e00\u6d41\uff0c\u7167\u8bf4\u4ee5\u4ed6\u4eec\u4e5d\u4eba\u7684\u6b66\u529f\uff0c\u5fc5\u80fd \u4e00\u51fa\u624b\u4fbf\u7ed9\u4e88\u5bf9\u65b9\u91cd\u521b\u3002 \u3000\u3000\u5c82\u77e5\u5927\u5927\u51fa\u4e4e\u610f\u6599\u4e4b\u5916\u7684\uff0c\u5bf9\u65b9\u7684\u6570\u5341\u540d\u9ed1\u8863\u8499\u9762\u4eba\u8eab\u624b\u4e5f\u90fd\u9ad8\u5f97\u51fa\u5947\uff0c\u7eb5\u7136\u738b\u5f64\u3001\u6c5f \u5343\u91cc\u3001\u5c0f\u71d5\u5b50\u4e09\u4eba\u4e00\u51fa\u624b\u4fbf\u653e\u5012\u4e86\u597d\u51e0\u4e2a\uff0c\u4f46\u662f\u5bf9\u65b9\u5374\u4eba\u5982\u6f6e\u6d8c\u800c\u4e0a\uff0c\u4f3c\u4e4e\u4e2a\u4e2a\u90fd\u662f\u608d\u4e0d\u754f \u6b7b\u3002 \u3000\u3000\u5927\u7ea6\u76cf\u8336\u5de5\u592b\u4e4b\u540e\uff0c\u9648\u5b8f\u3001\u8d75\u4fdd\u3001\u738b\u91cd\u5c71\u4e09\u4eba\u90fd\u5df2\u8eab\u8d1f\u91cd\u4f24\u3002 \u3000\u3000\u9ed1\u50e7\u3001\u767d\u7fbd\u4e5f\u90fd\u53d7\u4e86\u8f7b\u4f24\u3002 \u3000\u3000\u738b\u5f64\u3001\u6c5f\u5e72\u91cc\u3001\u5c0f\u71d5\u5b50\u4e09\u4eba\u4e00\u65b9\u9762\u8981\u8fce\u6218\u6765\u654c\uff0c\u4e00\u65b9\u9762\u53c8\u8981\u62a4\u536b\u53d7\u4f24\u8005\u7684\u5b89\u5168\uff0c\u65e0\u5f62\u4e2d \u6218\u6597\u529b\u4e5f\u5927\u5927\u6253\u4e86\u6298\u6263\u3002 \u3000\u3000\u90a3\u6570\u5341\u540d\u9ed1\u8863\u8499\u9762\u4eba\u539f\u672c\u5e0c\u671b\u80fd\u5c06\u738b\u5f64\u3001\u6c5f\u5343\u91cc\u7b49\u4eba\u4e00\u4e3e\u6b7c\u706d\uff0c\u4f46\u56e0\u738b\u5f64\u3001\u6c5f\u5343\u91cc\u548c\u5c0f \u71d5\u5b50\u8fd9\u4e09\u4eba\u8eab\u624b\u592a\u9ad8\uff0c\u800c\u81ea\u5df1\u4e00\u65b9\u53c8\u4f24\u4ea1\u8fd1\u534a\u3002\u53c8\u6fc0\u6218\u4e86\u987f\u996d\u5de5\u592b\uff0c\u7ec8\u4e8e\u5728\u90a3\u4e3a\u9996\u8005\u7684\u53f7\u4ee4 \u4e4b\u4e0b\u8fc5\u901f\u64a4\u79bb\u73b0\u573a\u3002 \u3000\u3000\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u56e0\u5df1\u65b9\u4e5f\u53d7\u4f24\u8fc7\u534a\uff0c\u81ea\u7136\u4e5f\u987e\u4e0d\u5f97\u8ffd\u8d76\u3002 \u3000\u3000\u68c0\u89c6\u4e4b\u4e0b\uff0c\u9664\u738b\u91cd\u5c71\u3001\u8d75\u4fdd\u3001\u9648\u5b8f\u91cd\u4f24\uff0c\u9ed1\u50e7\u3001\u767d\u7fbd\u8f7b\u4f24\uff0c\u66f4\u4ee4\u4ed6\u4eec\u5403\u60ca\u7684\u662f\u8001\u5996\u5a46\u5df2 \u7ecf\u4e0d\u89c1\u3002 \u3000\u3000\u5148\u524d\u6218\u51b5\u7d27\u6025\uff0c\u51e0\u4e4e\u8c01\u4e5f\u987e\u4e0d\u5f97\u8c01\uff0c\u8001\u5996\u5a46\u7a76\u7adf\u662f\u88ab\u5bf9\u65b9\u63b3\u8d70\u6216\u8d81\u673a\u9003\u8131\uff0c\u6b64\u523b\u6839\u672c\u5df2 \u65e0\u6cd5\u67e5\u8bc1\u3002 \u3000\u3000\u518d\u5728\u9644\u8fd1\u5de1\u89c6\u4e8c\u904d\u3002 \u3000\u3000\u5730\u4e0a\u7559\u4e0b\u7684\u5c38\u4f53\u8db3\u6709\u4e8c\u5341\u51e0\u5177\u4e4b\u591a\uff0c\u4e24\u76f8\u5bf9\u7167\uff0c\u5bf9\u65b9\u53ef\u8bf4\u4ed8\u51fa\u4e86\u60e8\u91cd\u7684\u4ee3\u4ef7\uff0c\u5e76\u672a\u5360\u5230 \u4e1d\u6beb\u4fbf\u5b9c\u3002 \u3000\u3000\u738b\u5f64\u548c\u6c5f\u5e72\u91cc\u7ecf\u8fc7\u4e00\u756a\u5546\u8bae\uff0c\u51b3\u5b9a\u5c31\u5728\u9053\u89c2\u5185\u6682\u505a\u4f11\u606f\uff0c\u7b49\u5929\u4eae\u540e\u518d\u8d70\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u4f17\u4eba\u518d\u56de\u5230\u9053\u89c2\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u9996\u5148\u638f\u51fa\u8eab\u4e0a\u7684\u7597\u4f24\u836f\uff0c\u4e3a\u4f24\u8005\u5185\u670d\u5916\u6577\u8fc7\u540e\uff0c\u518d\u547d\u5c0f\u71d5\u5b50\u4e3a\u9634\u660c\u89e3\u5f00\u7a74\u9053\uff0c\u4e0e \u738b\u5f64\u4e00\u540c\u5ba1\u8baf\u3002 \u3000\u3000\u5f53\u9634\u660c\u60a0\u60a0\u9192\u8f6c\uff0c\u4e00\u89c1\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u5750\u5728\u9762\u524d\uff0c\u5c0f\u71d5\u5b50\u7ad9\u5728\u4e00\u65c1\uff0c\u4ed6\u4e0d\u7531\u6253\u4e86\u4e2a\u5bd2\u98a4\uff0c \u6323\u624e\u7740\u4e5f\u5750\u8d77\u6765\uff0c\u5374\u4f4e\u4e0b\u5934\u5e76\u672a\u542d\u58f0\u3002 \u3000\u3000\u738b\u5f64\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u9634\u660c\uff0c\u8be5\u4f60\u8bf4\u5b9e\u8bdd\u7684\u65f6\u5019\u4e86\uff01\u201d \u3000\u3000\u9634\u660c\u5e72\u54b3\u7740\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u5f88\u884c\uff0c\u5c45\u7136\u80fd\u8ba9\u6c5f\u5927\u4fa0\u548c\u738b\u67d0\u4fe1\u4ee5\u4e3a\u771f\uff0c\u968f\u4f60\u6765\u5230\u8fd9\u91cc\uff0c\u4f60\u7684\u8fd9\u573a\u529f\u52b3\u5b9e\u5728\u4e0d\u5c0f\uff0c \u5bf9\u5417\uff1f\u201d \u3000\u3000\u201c\u4ed6\u2026\u2026\u4ed6\u4eec\u600e\u4e48\u4e86\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u5df2\u5168\u6570\u88ab\u6b7c\uff0c\u5e76\u65e0\u4e00\u4e2a\u6f0f\u7f51\u4e4b\u9c7c\u3002\u201d \u3000\u3000\u201c\u6211\u2026\u2026\u4e0d\u4fe1\u2026\u2026\u201d \u3000\u3000\u201c\u4fe1\u4e0d\u4fe1\u662f\u4f60\u7684\u4e8b\uff0c\u73b0\u5728\u53ea\u8981\u8bf4\u5b9e\u8bdd\uff0c\u6211\u5c31\u53ef\u4ee5\u9976\u4f60\u4e0d\u6b7b\u3002\u201d \u3000\u3000\u201c\u738b\u5927\u4eba\u8981\u5728\u4e0b\u8bf4\u4ec0\u4e48\u5b9e\u8bdd\uff1f\u201d \u3000\u3000\u201c\u4f60\u662f\u53d7\u4ec0\u4e48\u4eba\u6307\u4f7f\uff1f\u6628\u665a\u524d\u6765\u56f4\u653b\u9053\u89c2\u7684\u90fd\u662f\u4e9b\u4ec0\u4e48\u4eba\uff1f\u4e3a\u9996\u8005\u662f\u8c01\uff1f\u201d \u3000\u3000\u53ea\u89c1\u9634\u660c\u5ffd\u7136\u95f4\u54fc\u4e00\u58f0\uff0c\u5012\u4e0b\u5730\u53bb\uff0c\u63a5\u7740\u5634\u89d2\u6dcc\u51fa\u5927\u91cf\u9c9c\u8840\u3002 \u3000\u3000\u539f\u6765\u9634\u660c\u5df2\u54ac\u820c\u81ea\u5c3d\u3002 \u3000\u3000\u5728\u5bf9\u65b9\u6765\u8bf4\uff0c\u8fd9\u8be5\u662f\u4e00\u540d\u5fe0\u5fc3\u4e0d\u4e8c\u7684\u6b7b\u58eb\u3002 \u3000\u3000\u738b\u5f64\u641c\u4e86\u4e00\u904d\u9634\u660c\u5c38\u8eab\uff0c\u5e76\u672a\u53d1\u73b0\u4efb\u4f55\u53ef\u8d44\u8fa8\u8bc6\u8eab\u4efd\u7684\u7269\u4ef6\uff0c\u53ea\u597d\u547d\u5c0f\u71d5\u5b50\u5c06\u4e4b\u62d6\u5230\u5e99 \u540e\u3002 \u3000\u3000\u4f17\u4eba\u5c0f\u61a9\u4e86\u4e00\u4e2a\u591a\u66f4\u6b21\uff0c\u5929\u5df2\u5927\u4eae\u3002 \u3000\u3000\u738b\u5f64\u518d\u547d\u5c0f\u71d5\u5b50\u5230\u6570\u91cc\u5916\u7684\u6c11\u5bb6\u96c7\u4e86\u4e24\u4e58\u9a6e\u8f7f\uff0c\u8ba9\u53d7\u4f24\u8005\u4e94\u4eba\u4e58\u8f7f\uff0c\u518d\u8e0f\u4e0a\u56de\u5f00\u5c01\u7684\u8def \u7a0b\u3002 \u3000\u3000\u8def\u4e0a\uff0c\u738b\u5f64\u4e00\u76f4\u62c5\u5fc3\u4e09\u516c\u4e3b\u7684\u5b89\u5371\u3002 \u3000\u3000\u4f46\u662f\uff0c\u6c5f\u5343\u91cc\u5374\u8ba4\u4e3a\u4e09\u516c\u4e3b\u7edd\u4e0d\u4f1a\u53d7\u5230\u60ca\u6270\u3002 \u3000\u3000\u4ed6\u6de1\u6de1\u7684\u9053\uff1a\u201c\u8001\u5f1f\u653e\u5fc3\uff0c\u4e09\u516c\u4e3b\u90a3\u8fb9\u4e0d\u53ef\u80fd\u6709\u4e8b\u3002\u201d \u3000\u3000\u201c\u6c5f\u5144\u6839\u636e\u4ec0\u4e48\u800c\u5982\u6b64\u7684\u6709\u628a\u63e1\uff1f\u201d \u3000\u3000\u201c\u6628\u665a\u7684\u4e8b\uff0c\u5f88\u660e\u663e\u7684\u5fc5\u4e0e\u9a6c\u6587\u4e2d\u6709\u5173\uff0c\u4ed6\u7684\u76ee\u7684\u662f\u60f3\u5728\u6697\u5904\u628a\u6211\u4eec\u4e00\u4e3e\u6b7c\u706d\uff0c\u800c\u4ed6\u53ef \u4ee5\u4e0d\u8d1f\u4efb\u4f55\u8d23\u4efb\u3002\u82e5\u4ed6\u516c\u7136\u5230\u5ba2\u6808\u884c\u51f6\uff0c\u90a3\u5c31\u65e0\u6240\u63a9\u9970\u4e86\uff0c\u9a6c\u6587\u4e2d\u600e\u4e48\u4f1a\u505a\u8fd9\u79cd\u50bb\u4e8b\uff1f\u201d \u3000\u3000\u201c\u5982\u6b64\u770b\u6765\uff0c\u9a6c\u6587\u4e2d\u5bf9\u54b1\u4eec\u7684\u884c\u52a8\u5fc5\u5df2\u4e86\u82e5\u6307\u638c\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u662f\u4e00\u5b9a\u7684\uff0c\u8fde\u6c5f\u67d0\u548c\u8001\u5f1f\u5728\u4e00\u8d77\u884c\u52a8\uff0c\u9a6c\u6587\u4e2d\u4e5f\u5fc5\u5b9a\u65e9\u5df2\u8c03\u67e5\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u53ea\u662f\u4e0d \u4fbf\u660e\u8a00\u800c\u5df2\u3002\u201d \u3000\u3000\u8fd1\u5348\u65f6\u5206\uff0c\u4e00\u884c\u4eba\u4f17\u5df2\u56de\u5230\u4e1c\u90fd\u5ba2\u6808\u3002 \u3000\u3000\u5ba2\u6808\u91cc\u679c\u7136\u5e76\u672a\u53d1\u751f\u4efb\u4f55\u60c5\u51b5\u3002 \u3000\u3000\u738b\u5f64\u7acb\u5373\u628a\u7ecf\u8fc7\u5411\u4e09\u516c\u4e3b\u7980\u660e\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u5e76\u672a\u8868\u793a\u4efb\u4f55\u610f\u89c1\uff0c\u6709\u5173\u4eca\u540e\u5982\u4f55\u884c\u52a8\uff0c\u53ea\u547d\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u5546\u8bae\u51b3\u5b9a\u3002\u201c \u3000\u3000\u4f9d\u76ee\u524d\u7684\u60c5\u5f62\uff0c\u56e0\u6709\u4e94\u4eba\u53d7\u4f24\uff0c\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u5546\u8bae\u7684\u7ed3\u679c\uff0c\u4e5f\u53ea\u6709\u7b49\u4e94\u4eba\u4f24\u52bf\u75ca\u6108\uff0c\u518d \u51b3\u5b9a\u4e0b\u4e00\u6b65\u7684\u884c\u52a8\u4e86\u3002 \u3000\u3000\u5f53\u65e5\u665a\u9910\u540e\uff0c\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u6b63\u5728\u5ba2\u5385\u5bf9\u8c08\uff0c\u5ffd\u7136\u6765\u4e86\u4e00\u4f4d\u4e0d\u901f\u4e4b\u5ba2\u3002 \u3000\u3000\u8fd9\u4eba\u53eb\u859b\u767e\u80dc\uff0c\u7cfb\u98ce\u96f7\u5200\u859b\u8001\u4eba\u4e4b\u5b59\u3002 \u3000\u3000\u5f53\u5e74\u88ab\u5173\u5728\u5f00\u5c01\u5927\u76f8\u56fd\u5bfa\uff0c\u5e74\u524d\u738b\u5f64\u6765\u5f00\u5c01\u65f6\uff0c\u56e0\u53c2\u4e0e\u6551\u738b\u5f64\u800c\u88ab\u653e\u51fa\uff0c\u653e\u51fa\u540e\u5373\u4e0d\u77e5 \u53bb\u5411\uff0c\u60f3\u4e0d\u5230\u4ed6\u4f1a\u4e3b\u52a8\u518d\u6765\u548c\u738b\u5f64\u76f8\u89c1\u3002 \u3000\u3000\u5f53\u738b\u5f64\u8ba4\u51fa\u6765\u4eba\u540e\uff0c\u56e0\u5bf9\u65b9\u66fe\u5bf9\u81ea\u5df1\u6709\u8fc7\u6551\u63f4\u4e4b\u6069\uff0c\u7acb\u5373\u8d77\u8eab\u76f8\u8fce\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u4e5f\u8d77\u8eab\u4ee5\u793a\u6b22\u8fce\u4e4b\u610f\u3002 \u3000\u3000\u738b\u5f64\u4eb2\u81ea\u5012\u4e86\u4e00\u676f\u8336\uff0c\u9053\uff1a\u201c\u4e0d\u77e5\u8fd9\u4e24\u5e74\u859b\u8001\u5f1f\u90fd\u5728\u54ea\u91cc\u5f97\u610f\uff1f\u201d \u3000\u3000\u859b\u767e\u80dc\u6de1\u6de1\u4e00\u7b11\uff0c\u8038\u4e86\u8038\u80a9\u9053\uff1a\u201c\u5f97\u610f\u8c08\u4e0d\u4e0a\uff0c\u5931\u610f\u5012\u662f\u6709\u4e00\u4e9b\uff0c\u738b\u5927\u4eba\u548c\u6c5f\u524d\u8f88\u6765\u5f00 \u5c01\u5df2\u7ecf\u6709\u4e0d\u5c11\u5929\u4e86\u5427\uff1f\u201d \u3000\u3000\u738b\u5f64\u70b9\u70b9\u5934\u9053\uff1a\u201c\u7b97\u6765\u5df2\u6709\u5341\u65e5\u5de6\u53f3\uff0c\u96be\u5f97\u80fd\u5728\u8fd9\u91cc\u53c8\u548c\u859b\u8001\u5f1f\u76f8\u4f1a\u3002\u201d \u3000\u3000\u201c\u738b\u5927\u4eba\u548c\u6c5f\u524d\u8f88\u6628\u665a\u5fc5\u5b9a\u5f88\u8f9b\u82e6\u5427\uff1f\u53e6\u5916\u7684\u51e0\u4f4d\u5462\uff1f\u662f\u5426\u6b63\u5728\u517b\u4f24\uff1f\u201d \u3000\u3000\u738b\u5f64\u554a\u4e86\u58f0\u9053\uff1a\u201c\u83ab\u975e\u8001\u5f1f\u77e5\u9053\u6628\u665a\u7684\u4e8b\uff1f\u201d \u3000\u3000\u201c\u82e5\u4e0d\u77e5\u9053\uff0c\u665a\u8f88\u4e5f\u4e0d\u4f1a\u524d\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u859b\u8001\u5f1f\u662f\u600e\u4e48\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u665a\u8f88\u4e5f\u53c2\u52a0\u4e86\u6628\u665a\u7684\u884c\u52a8\u3002\u201d \u3000\u3000\u201c\u4f60\u2026\u2026\u201d \u3000\u3000\u201c\u738b\u5927\u4eba\u7528\u4e0d\u7740\u5403\u60ca\uff0c\u5f85\u4f1a\u513f\u665a\u8f88\u5c31\u4f1a\u8bf4\u660e\u767d\uff0c\u73b0\u8981\u5148\u8bf4\u660e\u7684\u662f\u665a\u8f88\u6628\u665a\u6765\u548c\u738b\u5927\u4eba\u53ca \u6c5f\u524d\u8f88\u7684\u4eba\u52a8\u624b\u3002\u201d \u3000\u3000\u201c\u6628\u665a\u90a3\u4f19\u4eba\u7a76\u7adf\u662f\u90a3\u8def\u7684\uff1f\u201d \u3000\u3000\u201c\u738b\u5927\u4eba\u5fc5\u5b9a\u542c\u8bf4\u8fc7\u9c7c\u5316\u9f99\u8fd9\u4eba\u5427\uff1f\u201d \u3000\u3000\u738b\u5f64\u5fc3\u5934\u4e00\u9707\u9053\uff1a\u201c\u6d1e\u5ead\u6e56\u5341\u516b\u5be8\u603b\u5be8\u4e3b\u9c7c\u5316\u9f99\uff0c\u738b\u67d0\u600e\u80fd\u4e0d\u77e5\uff1f\u4e0a\u6b21\u738b\u67d0\u6765\u5f00\u5c01\u65f6\uff0c \u5728\u76f8\u56fd\u5bfa\u4e2d\u66fe\u906d\u4ed6\u7387\u9886\u624b\u4e0b\u56f4\u6740\u8fc7\uff0c\u542c\u8bf4\u4ed6\u65e9\u5df2\u88ab\u9a6c\u6587\u4e2d\u6536\u4e70\u3002\u201d \u3000\u3000\u859b\u767e\u80dc\u9894\u9996\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u9a6c\u6587\u4e2d\u66fe\u4e3a\u4ed6\u8bad\u7ec3\u4e86\u4e94\u767e\u540d\u6b7b\u58eb\uff0c\u800c\u4e14\u76ee\u524d\u6b7b\u58eb\u4eba\u6570\u5c1a\u5728\u7ee7\u7eed \u589e\u591a\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u6628\u665a\u4e3a\u9996\u7684\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u6b63\u662f\u9c7c\u5316\u9f99\uff01\u201d \u3000\u3000\u738b\u5f64\u7565\u4e00\u6c89\u4ed8\u9053\uff1a\u201c\u738b\u67d0\u89c9\u5f97\u5947\u602a\u7684\u662f\uff0c\u9c7c\u5316\u9f99\u8eab\u4e3a\u6d1e\u5ead\u5341\u516b\u5be8\u603b\u5be8\u4e3b\uff0c\u800c\u6d1e\u5ead\u79bb\u8fd9\u91cc \u8fe2\u8fe2\u5343\u91cc\uff0c\u9a6c\u6587\u4e2d\u600e\u4f1a\u8fd9\u4e48\u5feb\u5c31\u628a\u4ed6\u8c03\u5230\u5f00\u5c01\u6765\uff1f\u201d \u3000\u3000\u859b\u767e\u80dc\u7b11\u9053\uff1a\u201c\u738b\u5927\u4eba\u53ef\u80fd\u56e0\u4e45\u5728\u5927\u5185\u7684\u5173\u7cfb\u5427\uff1f\u5bf9\u5916\u754c\u7684\u6c5f\u6e56\u52a8\u6001\u5e76\u4e0d\u7075\u901a\uff0c\u9c7c\u5316\u9f99 \u81ea\u88ab\u9a6c\u6587\u4e2d\u6536\u4e70\u540e\uff0c\u5927\u90e8\u5206\u7684\u65f6\u95f4\u5168\u5728\u5f00\u5c01\u4e00\u5e26\u6d3b\u52a8\uff0c\u4ee5\u4fbf\u968f\u65f6\u5e94\u4ed8\u9a6c\u6587\u4e2d\u7684\u5dee\u9063\u3002\u81f3\u4e8e\u6d1e \u5ead\u65b9\u9762\uff0c\u603b\u5be8\u4e3b\u4e00\u804c\uff0c\u5219\u53e6\u6709\u4eba\u4ee3\u4ed6\u4e3b\u6301\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u9c7c\u5316\u9f99\u6628\u665a\u56f4\u653b\u90a3\u5ea7\u9053\u89c2\uff0c\u4e5f\u662f\u9a6c\u6587\u4e2d\u7684\u6388\u610f\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u4ee5\u9a6c\u6587\u4e2d\u7684\u539f\u610f\u662f\u60f3\u628a\u738b\u5927\u4eba\u548c\u6c5f\u524d\u8f88\u5e26\u53bb\u7684\u4eba\u4e00\u4e3e\u6b7c\u706d\uff0c\u7edd\u4e0d\u7559\u4e0b\u4e00\u4e2a\u6d3b \u53e3\uff0c\u4ee5\u4fbf\u6b7b\u65e0\u5bf9\u8bc1\uff0c\u53ef\u60dc\u4ed6\u7684\u5982\u610f\u7b97\u76d8\u5e76\u672a\u5151\u73b0\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u8001\u5f1f\u6628\u665a\u2026\u2026\u201d \u3000\u3000\u201c\u73b0\u5728\u665a\u8f88\u5c31\u7ec6\u8bf4\u4ece\u5934\uff0c\u665a\u8f88\u81ea\u4ece\u79bb\u5f00\u76f8\u56fd\u5bfa\u540e\uff0c\u5c31\u6295\u8eab\u5230\u6d1e\u5ead\u603b\u5be8\u53bb\uff0c\u51ed\u7740\u6240\u5b66\u7684\u4e00 \u5957\u2018\u98ce\u96f7\u5341\u516b\u5200\u2019\u548c\u4e0d\u7b97\u9ad8\u660e\u7684\u8ba1\u8c0b\uff0c\u5f88\u53d7\u9c7c\u5316\u9f99\u7684\u91cd\u7528\u3002\u201d \u3000\u3000\u738b\u5f64\u54e6\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u8001\u5f1f\u4e3a\u4ec0\u4e48\u8981\u5f03\u660e\u6295\u6697\uff1f\u201d \u3000\u3000\u859b\u767e\u80dc\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u665a\u8f88\u6295\u9760\u9c7c\u5316\u9f99\uff0c\u6b63\u662f\u60f3\u66ff\u7687\u5bb6\u505a\u4e9b\u4e8b\u60c5\u3002\u201d \u3000\u3000\u201c\u6b64\u8bdd\u600e\u8bb2\uff1f\u201d \u3000\u3000\u201c\u665a\u8f88\u65e9\u77e5\u9c7c\u5316\u9f99\u5df2\u88ab\u9a6c\u6587\u4e2d\u6536\u4e70\uff0c\u552f\u6709\u8bbe\u6cd5\u6253\u4eba\u4ed6\u4eec\u7684\u7ec4\u7ec7\uff0c\u624d\u80fd\u63a2\u6089\u4ed6\u4eec\u7684\u673a\u5bc6\uff0c \u5426\u5219\u665a\u8f88\u4eca\u665a\u80fd\u6765\u8fd9\u91cc\u8bf4\u51fa\u8fd9\u4e9b\u4e8b\u60c5\u5417\uff1f\u201d \u3000\u3000\u738b\u5f64\u4e0d\u89c9\u4e3a\u4e4b\u52a8\u5bb9\uff0c\u8fde\u5fd9\u62b1\u62f3\u62f1\u624b\u9053\uff1a\u201c\u8001\u5f1f\u6b64\u6765\uff0c\u5411\u738b\u67d0\u8bf4\u51fa\u4e0a\u60c5\uff0c\u738b\u67d0\u5b9e\u5728\u611f\u6fc0\u4e0d \u5c3d\u3002\u4e0d\u77e5\u9c7c\u5316\u9f99\u662f\u5426\u8fd8\u6709\u8fdb\u4e00\u6b65\u884c\u52a8\uff1f\u201d \u3000\u3000\u859b\u767e\u80dc\u6447\u5934\u9053\uff1a\u201c\u9c7c\u5316\u9f99\u662f\u542c\u547d\u4e8e\u9a6c\u6587\u4e2d\u7684\uff0c\u738b\u5927\u4eba\u548c\u6c5f\u524d\u8f88\u4fdd\u7740\u4e09\u516c\u4e3b\u4e0d\u79bb\u5f00\u5f00\u5c01 \u57ce\uff0c\u9a6c\u6587\u4e2d\u600e\u6562\u8f7b\u4e3e\u5984\u52a8\uff1f\u201d \u3000\u3000\u201c\u90a3\u4e48\u738b\u67d0\u548c\u6c5f\u5927\u4fa0\u76ee\u524d\u8be5\u600e\u4e48\u529e\uff1f\u5e0c\u671b\u8001\u5f1f\u80fd\u63d0\u4f9b\u4e00\u70b9\u9ad8\u89c1\u3002\u201d \u3000\u3000\u859b\u767e\u80dc\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u738b\u5927\u4eba\u8fd9\u8fb9\u5df2\u6709\u591a\u4eba\u53d7\u4f24\uff0c\u5fc5\u987b\u597d\u597d\u7597\u517b\uff0c\u4e0d\u59a8\u5c31\u5728\u8fd9\u5bb6\u5ba2\u6808\u7ee7 \u7eed\u4f4f\u4e0b\u53bb\uff0c\u665a\u8f88\u4fdd\u8bc1\u4e0d\u4f1a\u51fa\u4e8b\u3002\u201d \u3000\u3000\u738b\u5f64\u70b9\u70b9\u5934\u9053\uff1a\u201c\u6211\u548c\u6c5f\u5927\u4fa0\u4e5f\u662f\u8fd9\u79cd\u770b\u6cd5\u3002\u201d \u3000\u3000\u8fd9\u65f6\u6c5f\u5343\u91cc\u63d2\u8a00\u9053\uff1a\u201c\u859b\u8001\u5f1f\u662f\u5426\u8fd8\u8981\u518d\u56de\u5230\u9c7c\u5316\u9f99\u90a3\u8fb9\u53bb\uff1f\u201d \u3000\u3000\u859b\u767e\u80dc\u9053\uff1a\u201c\u665a\u8f88\u82e5\u4e0d\u56de\u53bb\uff0c\u4ee5\u540e\u53c8\u600e\u80fd\u518d\u5411\u6c5f\u524d\u8f88\u548c\u738b\u5927\u4eba\u63d0\u4f9b\u6d88\u606f\uff1f\u201d \u3000\u3000\u201c\u6c5f\u67d0\u5f88\u62c5\u5fc3\u4f60\u8fd9\u6b21\u51fa\u6765\u4f1a\u88ab\u9c7c\u5316\u9f99\u53d1\u89c9\u3002\u201d \u3000\u3000\u201c\u6c5f\u524d\u8f88\u653e\u5fc3\uff0c\u665a\u8f88\u8fd9\u6b21\u51fa\u6765\uff0c\u662f\u7531\u9c7c\u5316\u9f99\u7279\u51c6\u7684\uff0c\u5f00\u5c01\u8857\u4e0a\u5230\u5904\u53ef\u4ee5\u53bb\uff0c\u521a\u624d\u8fdb\u5ba2\u6808 \u65f6\uff0c\u66fe\u7279\u522b\u7559\u610f\u95e8\u5916\u884c\u4eba\uff0c\u5e76\u672a\u53d1\u73b0\u6709\u4eba\u8ddf\u8e2a\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9ed8\u4e86\u4e00\u9ed8\u9053\uff1a\u201c\u859b\u8001\u5f1f\u662f\u5426\u77e5\u9053\u6628\u665a\u7684\u4e8b\u56e0\u4f55\u800c\u8d77\uff1f\u201d \u3000\u3000\u859b\u767e\u80dc\u76b1\u4e86\u76b1\u7709\u5934\u9053\uff1a\u201c\u597d\u50cf\u662f\u4e3a\u4e86\u4e09\u4e2a\u5973\u4eba\uff0c\u800c\u8fd9\u4e09\u4e2a\u5973\u4eba\u7adf\u7136\u662f\u2018\u8fce\u6625\u9601\u2019\u7684\u59d1 \u5a18\uff0c\u5bf9\u5417\uff1f\u201d \u3000\u3000\u201c\u7684\u786e\u5982\u6b64\uff0c\u859b\u8001\u5f1f\u53ef\u6e05\u695a\u90a3\u4e09\u4e2a\u59d1\u5a18\u7684\u8eab\u4efd\u6765\u5386\uff1f\u201d \u3000\u3000\u201c\u8fd9\u53ef\u80fd\u662f\u4ef6\u7edd\u5927\u673a\u5bc6\uff0c\u8fde\u9c7c\u5316\u9f99\u4f3c\u4e4e\u90fd\u4e0d\u5927\u6e05\u695a\uff0c\u665a\u8f88\u81ea\u7136\u4e5f\u5c31\u65e0\u4ece\u63a2\u6089\u201d\uff0f\u201c\u90a3\u4e09 \u4e2a\u59d1\u5a18\u662f\u5426\u4e5f\u53c2\u4e0e\u4e86\u6628\u665a\u7684\u884c\u52a8\uff1f\u201d \u3000\u3000\u201c\u6628\u665a\u9c7c\u5316\u9f99\u5e26\u4e86\u6570\u5341\u4eba\u53bb\u56f4\u653b\u90a3\u5ea7\u9053\u89c2\uff0c\u665a\u8f88\u5e76\u672a\u53d1\u73b0\u5176\u4e2d\u6709\u5973\u4eba\uff0c\u4f46\u82e5\u662f\u5979\u4eec\u5df2\u6613 \u5bb9\u626e\u6210\u7537\u4eba\uff0c\u90a3\u5c31\u5f88\u96be\u8bf4\u4e86\u3002\u201d \u3000\u3000\u201c\u90a3\u4e09\u4e2a\u5973\u4eba\u76ee\u524d\u7684\u4e0b\u843d\uff0c\u859b\u8001\u5f1f\u603b\u8be5\u6709\u4e9b\u8033\u95fb\u5427\uff1f\u201d \u3000\u3000\u201c\u5f88\u53ef\u80fd\u5df2\u88ab\u9a6c\u6587\u4e2d\u7a9d\u85cf\u8d77\u6765\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u671b\u671b\u738b\u5f64\u9053\uff1a\u201c\u8fd9\u5c31\u96be\u529e\u4e86\uff01\u201d \u3000\u3000\u738b\u5f64\u53ea\u662f\u7d27\u51dd\u7740\u8138\u8272\uff0c\u5e76\u672a\u5f00\u53e3\u8bf4\u8bdd\u3002\u201c \u3000\u3000\u859b\u767e\u80dc\u7ad9\u8d77\u8eab\u6765\u9053\uff1a\u201c\u665a\u8f88\u8be5\u8d70\u4e86\uff01\u201d \u3000\u3000\u6c5f\u5343\u91cc\u5fd9\u9053\uff1a\u201c\u5728\u4e00\u5207\u6d88\u606f\u5168\u88ab\u9a6c\u6587\u4e2d\u5c01\u9501\u4e4b\u4e0b\uff0c\u4e00\u5207\u4ef0\u4ed7\u8001\u5f1f\u5e2e\u5fd9\uff0c\u8001\u5f1f\u4ec0\u4e48\u65f6\u5019\u518d \u6765\uff1f\u201d \u3000\u3000\u201c\u665a\u8f88\u53ea\u8981\u5f97\u5230\u65b0\u7684\u6d88\u606f\uff0c\u4e00\u5b9a\u4f1a\u627e\u673a\u4f1a\u524d\u6765\uff0c\u53cd\u6b63\u8fd9\u8fb9\u6709\u4eba\u517b\u4f24\uff0c\u4e09\u4e94\u5929\u5185\u4e0d\u53ef\u80fd\u79bb \u5f00\uff0c\u4e24\u4f4d\u4e5f\u7528\u4e0d\u7740\u592a\u6025\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u548c\u738b\u5f64\u9001\u5230\u5ba2\u5385\u95e8\u53e3\uff0c\u53cc\u65b9\u624d\u76f8\u4e92\u9053\u522b\u3002 \u3000\u3000\u859b\u767e\u80dc\u8d70\u540e\uff0c\u6c5f\u5343\u91cc\u548c\u738b\u5f64\u4ecd\u5728\u5ba2\u5385\u672a\u6563\u3002 \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u4e00\u5207\u7684\u4e00\u5207\uff0c\u7adf\u5168\u662f\u9a6c\u6587\u4e2d\u5bfc\u6f14\u51fa\u6765\u7684\uff0c\u6b64\u4eba\u4e0d\u9664\uff0c\u5b9e\u5728\u540e\u60a3\u65e0 \u7a77\uff0c\u6c5f\u5144\uff01\u4f60\u770b\u54b1\u4eec\u662f\u5426\u4e5f\u8be5\u6709\u6240\u884c\u52a8\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u6c89\u541f\u4e86\u534a\u664c\u9053\uff1a\u201c\u4f9d\u6c5f\u67d0\u7684\u770b\u6cd5\uff0c\u54b1\u4eec\u8fd8\u662f\u4ee5\u6309\u5175\u4e0d\u52a8\u4e3a\u4e0a\u7b56\uff0c\u4e0b\u6b21\u548c\u9a6c\u6587\u4e2d\u89c1 \u4e86\u9762\uff0c\u5bf9\u6628\u665a\u7684\u4e8b\uff0c\u5fc5\u987b\u4e00\u5b57\u4e0d\u63d0\uff0c\u81f3\u4e8e\u4ed6\u5fc5\u5b9a\u4e5f\u4f1a\u88c5\u505a\u4e0d\u77e5\u3002\u201d \u3000\u3000\u201c\u5144\u5f1f\u7684\u610f\u601d\uff0c\u5bf9\u4ed6\u53d1\u8d77\u4e86\u884c\u52a8\u4e5f\u672a\u5c1d\u4e0d\u53ef\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u4e0d\u89c9\u4e00\u60ca\uff0c\u5fd9\u9053\uff1a\u201c\u8fd9\u662f\u4ef6\u6709\u5173\u793e\u7a37\u5b89\u5371\u7684\u5927\u4e8b\uff0c\u8001\u5f1f\u51c6\u5907\u5982\u4f55\u884c\u52a8\uff0c\u4e0d\u59a8\u5148\u8bf4 \u51fa\u6765\u542c\u542c\uff01\u201d \u3000\u3000\u738b\u5f64\u60f3\u4e86\u60f3\u9053\uff1a\u201c\u5144\u5f1f\u8eab\u8fb9\u85cf\u6709\u7687\u4e0a\u6240\u9881\u5bc6\u65e8\uff0c\u6709\u6743\u8c03\u52a8\u5c11\u6797\u3001\u6b66\u5f53\u4ee5\u53ca\u4e10\u5e2e\u7b49\u5404\u5927\u95e8 \u6d3e\u4eba\u9a6c\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u6de1\u7136\u9053\uff1a\u201c\u8001\u5f1f\u60f3\u52a8\u7528\u5c11\u6797\u3001\u6b66\u5f53\u3001\u4e10\u5e2e\u4eba\u9a6c\u6765\u5bf9\u4ed8\u9a6c\u6587\u4e2d\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\u53ea\u8981\u8fd9\u4e09\u5927\u95e8\u6d3e\u5404\u6d3e\u51fa\u767e\u4f59\u540d\u9ad8\u624b\uff0c\u79d8\u5bc6\u6f5c\u6765\u5f00\u5c01\uff0c\u7136\u540e\u4ee5\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\u4e4b\u52bf\u653b \u8fdb\u6cb3\u5357\u629a\u7f72\uff0c\u9a6c\u6587\u4e2d\u5fc5\u53ef\u9a6c\u4e0a\u5c31\u64d2\u3002\u201d \u3000\u3000\u4e00\u9a6c\u6587\u4e2d\u5c31\u64d2\u4ee5\u540e\u5462\uff1f\u201c \u3000\u3000\u201c\u5148\u903c\u4ed6\u4ea4\u51fa\u90a3\u4e09\u4e2a\u5973\u7684\u4e3a\u4e09\u516c\u4e3b\u7597\u866b\uff0c\u7136\u540e\u518d\u5c06\u4ed6\u89e3\u9001\u8fdb\u4eac\uff0c\u4e5f\u7b97\u66ff\u7687\u4e0a\u9664\u53bb\u4e00\u6869\u5fc3 \u8179\u5927\u60a3\u3002\u201d \u3000\u3000\u8c01\u77e5\u6c5f\u5343\u91cc\u5374\u6447\u5934\u51b7\u7b11\u4e86\u51e0\u58f0\u9053\uff1a\u201c\u8001\u5f1f\u8fd9\u79cd\u60f3\u6cd5\u672a\u514d\u592a\u5929\u771f\u4e86\uff0c\u4e5f\u592a\u4e00\u53a2\u60c5\u613f\u4e86 \u5427\uff01\u201d \u3000\u3000\u738b\u5f64\u7728\u52a8\u7740\u4e24\u773c\u9053\uff1a\u201c\u5144\u5f1f\u5012\u60f3\u542c\u542c\u6c5f\u5144\u7684\u9ad8\u89c1\uff01\u201d \u3000\u3000\u201c\u4ee5\u5c11\u6797\u3001\u6b66\u5f53\u3001\u4e10\u5e2e\u4e09\u5927\u6d3e\u7684\u4eba\u9a6c\uff0c\u662f\u5426\u80fd\u5b8c\u5168\u5bf9\u4ed8\u5f97\u4e86\u9a6c\u6587\u4e2d\uff0c\u5b9e\u5728\u8fd8\u662f\u672a\u77e5\u4e4b \u6570\uff0c\u9a6c\u6587\u4e2d\u624b\u4e0b\u7684\u529b\u91cf\uff0c\u8001\u5f1f\u5fc5\u5b9a\u4e5f\u5fc3\u91cc\u6709\u6570\u5427\uff1f\u201d \u3000\u3000\u201c\u5144\u5f1f\u5f53\u7136\u77e5\u9053\uff0c\u9a6c\u6587\u4e2d\u9664\u9c7c\u5316\u9f99\u7387\u9886\u7684\u8fd1\u5343\u540d\u6562\u6b7b\u961f\u5916\uff0c\u53e6\u6709\u8d75\u4e8c\u5824\u7684\u624b\u4e0b\u4e94\u516d\u767e \u4eba\uff0c\u8fd9\u4e9b\u4eba\u53ef\u4ee5\u8bf4\u662f\u4ed6\u7684\u79c1\u5175\uff0c\u5bf9\u4ed6\u7edd\u5bf9\u662f\u5fe0\u5fc3\u4e0d\u4e8c\uff0c\u53e6\u5916\u8fd8\u6709\u9a6c\u3001\u6b65\u7edf\u9886\uff0c\u5404\u6709\u7cbe\u5175\u5343\u4f59 \u4eba\u3002\u201d \u3000\u3000\u201c\u8001\u5f1f\u77e5\u9053\u8fd9\u4e9b\u5c31\u597d\uff0c\u9a6c\u6587\u4e2d\u62e5\u6709\u5982\u6b64\u5f3a\u76db\u7684\u5175\u529b\uff0c\u5c11\u6797\u3001\u6b66\u5f53\u3001\u4e10\u5e2e\u4e09\u65b9\u9762\u7684\u9ad8\u624b\uff0c \u662f\u5426\u80fd\u5c06\u4ed6\u4eec\u4e00\u4e3e\u6b7c\u706d\uff0c\u6050\u6015\u5927\u6709\u95ee\u9898\u3002\u201d \u3000\u3000\u738b\u5f64\u4e0d\u4ee5\u4e3a\u7136\u9053\uff1a\u201c\u9a6c\u6587\u4e2d\u867d\u6709\u5982\u6b64\u5e9e\u5927\u529b\u91cf\uff0c\u4f46\u4e0d\u4e00\u5b9a\u90fd\u5728\u5f00\u5c01\uff0c\u800c\u54b1\u4eec\u5374\u662f\u51fa\u5176\u4e0d \u610f\uff0c\u653b\u5176\u4e0d\u5907\uff0c\u9a6c\u6587\u4e2d\u5fc5\u5b9a\u63aa\u624b\u4e0d\u53ca\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u6447\u5934\u4e00\u7b11\u9053\uff1a\u201c\u8fd9\u53ea\u80fd\u8bf4\u662f\u8001\u5f1f\u7684\u5982\u610f\u7b97\u76d8\uff0c\u4f60\u628a\u5c11\u6797\u3001\u6b66\u5f53\u3001\u4e10\u5e2e\u4e09\u65b9\u9762\u7684\u9ad8 \u624b\u5f81\u96c6\u5230\u5f00\u5c01\u6765\uff0c\u6d69\u6d69\u8361\u8361\u6570\u767e\u4eba\uff0c\u7eb5\u7136\u4ed6\u4eec\u662f\u626e\u6210\u5546\u8d3e\u548c\u4e00\u822c\u767e\u59d3\u6a21\u6837\uff0c\u4e5f\u5fc5\u5b9a\u775b\u4e0d\u8fc7\u9a6c \u6587\u4e2d\u7684\u8033\u76ee\uff0c\u53ea\u6015\u5c1a\u672a\u884c\u52a8\uff0c\u4fbf\u5df2\u88ab\u5bf9\u65b9\u4fa6\u77e5\uff0c\u5230\u90a3\u65f6\u54b1\u4eec\u5c31\u52bf\u5c06\u9a91\u864e\u96be\u4e0b\u4e86\u3002\u201d \u3000\u3000\u738b\u5f64\u7ec8\u4e8e\u9677\u5165\u72b9\u8c6b\uff0c\u4e0d\u518d\u8a00\u8bed\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u7ee7\u7eed\u9053\uff1a\u201c\u5c31\u7b97\u8001\u5f1f\u80fd\u4e00\u4e3e\u6210\u529f\uff0c\u4f60\u4e5f\u62c5\u5f53\u4e0d\u4e86\u8fd9\u9879\u8d23\u4efb\u3002\u201d \u3000\u3000\u738b\u5f64\u7d27\u8e59\u7740\u53cc\u7709\u9053\uff1a\u201c\u82e5\u80fd\u4e00\u4e3e\u594f\u529f\uff0c\u8fd8\u6709\u4ec0\u4e48\u8d23\u4efb\u53ef\u62c5\uff1f\u201d \u3000\u3000\u201c\u8001\u5f1f\u8fd9\u6b21\u51fa\u4eac\uff0c\u76ee\u7684\u53ea\u662f\u4e3a\u4e09\u516c\u4e3b\u7597\u866b\uff0c\u7687\u4e0a\u5e76\u672a\u4e0b\u65e8\u8981\u4f60\u5411\u9a6c\u6587\u4e2d\u4e0b\u624b\uff0c\u56fd\u5bb6\u5927 \u4e8b\uff0c\u7687\u4e0a\u6709\u7687\u4e0a\u7684\u8003\u8651\uff0c\u82e5\u73b0\u5728\u5bf9\u9a6c\u6587\u4e2d\u4e0b\u624b\uff0c\u8bf4\u4e0d\u5b9a\u53cd\u800c\u5bf9\u5927\u5c40\u6709\u5bb3\u65e0\u76ca\uff0c\u8fd9\u4e00\u70b9\u8001\u5f1f\u5fc5 \u987b\u51b7\u9759\u601d\u8003\u4e00\u4e0b\u3002\u201d \u3000\u3000\u738b\u5f64\u70b9\u4e86\u70b9\u5934\u9053\uff1a\u201c\u8fd9\u65b9\u9762\u5144\u5f1f\u7684\u786e\u6709\u6b20\u8003\u8651\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u53c8\u9053\uff1a\u201c\u8fd8\u6709\uff0c\u50cf\u8fd9\u4e48\u91cd\u5927\u7684\u4e8b\u60c5\uff0c\u5fc5\u987b\u7531\u7687\u4e0a\u6d3e\u51fa\u5927\u519b\u91c7\u53d6\u884c\u52a8\uff0c\u8001\u5f1f\u53ea\u662f\u4e00 \u540d\u5927\u5185\u4f8d\u536b\u7edf\u9886\uff0c\u600e\u53ef\u8d8a\u6743\u884c\u4e8b\uff1f\u518d\u8bf4\uff0c\u5018\u82e5\u5c11\u6797\u3001\u6b66\u5f53\u3001\u4e10\u5e2e\u4e09\u65b9\u9762\u7684\u9ad8\u624b\uff0c\u56e0\u8fd9\u4e00\u6218\u800c \u6b7b\u4f24\u6b86\u5c3d\uff0c\u4f60\u53c8\u5982\u4f55\u5411\u8fd9\u4e9b\u6b66\u6797\u540c\u9053\u4ea4\u4ee3\uff1f\u201d \u3000\u3000\u738b\u5f64\u957f\u957f\u5401\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u6c5f\u5144\u8fd9\u756a\u89c1\u89e3\uff0c\u5144\u5f1f\u5b8c\u5168\u63a5\u53d7\uff0c\u53ea\u662f\u4e8b\u5df2\u5982\u6b64\uff0c\u54b1\u4eec\u53c8\u8be5\u5982\u4f55 \u8fdb\u884c\u5462\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u4e5f\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u4e3a\u4eca\u4e4b\u8ba1\uff0c\u5f53\u7136\u8be5\u6682\u65f6\u6309\u5175\u4e0d\u52a8\uff0c\u53cd\u6b63\u4ed6\u4eec\u51e0\u4f4d\u53d7\u4f24\u7684\u5fc5\u987b\u5728 \u8fd9\u91cc\u7597\u517b\u4e00\u6bb5\u65f6\u671f\uff0c\u5728\u8fd9\u51e0\u5929\u91cc\uff0c\u5fc5\u5b9a\u4f1a\u6709\u65b0\u7684\u6d88\u606f\u5230\u6765\u3002\u201d \u3000\u3000\u201c\u5144\u5f1f\u4e0d\u80fd\u4e0d\u62c5\u5fc3\u4e09\u516c\u4e3b\u7684\u866b\u6bd2\u3002\u201d \u3000\u3000\u201c\u4e09\u516c\u4e3b\u7597\u6bd2\u7684\u4e8b\uff0c\u7528\u4e0d\u7740\u6025\u5728\u4e00\u65f6\uff0c\u53ea\u8981\u90a3\u4e09\u4e2a\u5973\u7684\u4ecd\u5728\u9a6c\u6587\u4e2d\u624b\u91cc\uff0c\u6c5f\u67d0\u81ea\u6709\u529e\u6cd5 \u628a\u5979\u4eec\u6380\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u5144\u5f1f\u51b3\u5b9a\u542c\u6c5f\u5144\u7684\uff0c\u6682\u65f6\u6309\u5175\u4e0d\u52a8\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u53c8\u5230\u4e86\u201c\u8fce\u6625\u9601\u201d\u3002 \u3000\u3000\u4ed6\u662f\u5949\u6c5f\u5343\u91cc\u548c\u738b\u5f64\u4e4b\u547d\u800c\u6765\u7684\u3002 \u3000\u3000\u5176\u5b9e\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u4e5f\u660e\u77e5\u4e0d\u53ef\u80fd\u6709\u4ec0\u4e48\u65b0\u53d1\u73b0\uff0c\u53ea\u662f\u8ba9\u5c0f\u71d5\u5b50\u6765\u770b\u770b\u201c\u8fce\u6625\u9601\u201d\u6709\u4ec0\u4e48 \u53cd\u5e94\u800c\u5df2\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u5bf9\u8fd9\u91cc\u5df2\u7b97\u662f\u8001\u5ba2\u4eba\u4e86\uff0c\u5230\u8fbe\u540e\u5c31\u76f4\u63a5\u8fdb\u5165\u62db\u5f85\u5ba2\u4eba\u7684\u623f\u95f4\u3002 \u3000\u3000\u53c8\u662f\u5c24\u4e09\u524d\u6765\u63a5\u5f85\u3002 \u3000\u3000\u8fd9\u662f\u56e0\u4e3a\u5c24\u4e09\u5df2\u548c\u5c0f\u71d5\u5b50\u65e9\u5c31\u6709\u8fc7\u63a5\u89e6\u7684\u5173\u7cfb\u3002 \u3000\u3000\u5c24\u4e09\u5012\u8fc7\u8336\uff0c\u54c8\u8170\u966a\u7b11\u7684\u9053\uff1a\u201c\u516c\u5b50\u662f\u8001\u5ba2\u4eba\u4e86\uff0c\u5bf9\u8fd9\u91cc\u7684\u59d1\u5a18\u5fc5\u5b9a\u5df2\u7ecf\u8ba4\u8bc6\u4e0d\u5c11\uff0c\u4eca \u65e5\u51c6\u5907\u53eb\u90a3\u4f4d\u59d1\u5a18\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6574\u4e86\u6574\u8138\u8272\u9053\uff1a\u201c\u6211\u53eb\u59d1\u5a18\uff0c\u90fd\u662f\u56fa\u5b9a\u7684\uff0c\u53ea\u8981\u770b\u597d\u4e86\u90a3\u4e00\u4e2a\uff0c\u5c31\u6c38\u8fdc\u662f\u90a3\u4e00 \u4e2a\u3002\u201d \u3000\u3000\u5c24\u4e09\u7728\u7740\u4e00\u5bf9\u9f20\u773c\u9053\uff1a\u201c\u516c\u5b50\u597d\u50cf\u8fd8\u6ca1\u6b63\u5f0f\u53eb\u8fc7\u59d1\u5a18\uff0c\u4f60\u559c\u6b22\u7684\u662f\u90a3\u4e00\u4f4d\uff1f\u5c0f\u7684\u8fd8\u5f04\u4e0d \u6e05\u695a\uff0c\u73b0\u5728\u5c31\u8bf7\u6307\u660e\uff0c\u5c0f\u7684\u9a6c\u4e0a\u53bb\u53eb\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u8bb0\u6027\u4e0d\u6d4e\u5fd8\u6027\u597d\uff0c\u4e0a\u6b21\u6211\u4e0d\u662f\u66fe\u8fdb\u8fc7\u6842\u82b1\u7684\u623f\u95f4\u5417\uff1f\u201d \u3000\u3000\u5c24\u4e09\u8138\u8272\u4e00\u53d8\u9053\uff1a\u201c\u6842\u82b1\u5df2\u7ecf\u8d70\u4e86\uff0c\u516c\u5b50\u4e0a\u6b21\u4e0d\u662f\u95ee\u8fc7\u4e86\u5417\uff1f\u5979\u5c31\u662f\u90a3\u5929\u8d70\u7684\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6545\u4f5c\u8bb6\u7136\u4e4b\u8272\u9053\uff1a\u201c\u5979\u4e3a\u4ec0\u4e48\u8981\u8d70\uff1f\u201d \u3000\u3000\u5c24\u4e09\u966a\u7b11\u9053\uff1a\u201c\u5979\u8bf4\u5bb6\u91cc\u6709\u4e8b\uff0c\u5fc5\u987b\u56de\u5bb6\u53bb\u3002\u201d \u3000\u3000\u201c\u8001\u677f\u5a18\u4e3a\u4ec0\u4e48\u8981\u51c6\u5979\u56de\u5bb6\uff1f\u201d \u3000\u3000\u201c\u6842\u82b1\u5e76\u65e0\u5356\u8eab\u5951\u5728\u8001\u677f\u5a18\u624b\u91cc\uff0c\u8001\u677f\u5a18\u4e0d\u60f3\u8ba9\u5979\u8d70\u4e5f\u6ca1\u529e\u6cd5\u3002\u201d \u3000\u3000\u201c\u5979\u5bb6\u4f4f\u90a3\u91cc\uff1f\u201d \u3000\u3000\u201c\u8fd9\u65b9\u9762\u5c0f\u7684\u5c31\u4e0d\u6e05\u695a\u4e86\u3002\u201d \u3000\u3000\u201c\u542c\u8bf4\u53e6\u6709\u4e24\u4f4d\u59d1\u5a18\u548c\u5979\u4e00\u8d77\u8d70\u7684\uff0c\u5bf9\u5417\uff1f\u201d \u3000\u3000\u5c24\u4e09\u5e26\u4e9b\u5403\u60ca\u795e\u95ee\u9053\uff1a\u201c\u516c\u5b50\u662f\u600e\u4e48\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u6211\u53ea\u95ee\u6709\u6ca1\u6709\u8fd9\u4ef6\u4e8b\uff0c\u81f3\u4e8e\u600e\u4e48\u77e5\u9053\u7684\uff0c\u4f60\u5c31\u4e0d\u5fc5\u7ba1\u4e86\u3002\u201d \u3000\u3000\u5c24\u4e09\u5e72\u54b3\u4e86\u4e24\u58f0\u9053\uff1a\u201c\u662f\u6709\u8fd9\u4e48\u4ef6\u4e8b\uff0c\u90a3\u5929\u4e00\u5171\u8d70\u4e86\u4e09\u4f4d\u59d1\u5a18\u3002\u201d \u3000\u3000\u201c\u8fd9\u5012\u5de7\u5f97\u5f88\uff0c\u4e00\u8d70\u5c31\u662f\u4e09\u4e2a\uff0c\u5982\u679c\u8fd9\u6837\u4e0b\u53bb\uff0c\u4f60\u4eec\u2018\u8fce\u6625\u9601\u2019\u5c82\u4e0d\u5f88\u5feb\u5c31\u8981\u5173\u95e8\u4e86 \u5417\uff1f\u201d \u3000\u3000\u201c\u516c\u5b50\u8bf4\u7b11\u8bdd\u4e86\uff0c\u2018\u8fce\u6625\u9601\u2019\u59d1\u5a18\u4e00\u767e\u591a\u4f4d\uff0c\u51e0\u4e4e\u6bcf\u5929\u90fd\u6709\u8fdb\u7684\uff0c\u4e5f\u6709\u51fa\u7684\uff0c\u81f3\u4e8e\u90a3\u5929 \u8d70\u4e86\u4e09\u4e2a\uff0c\u4e0d\u8fc7\u662f\u51d1\u5de7\u7f62\u4e86\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6599\u60f3\u95ee\u4e0d\u51fa\u6240\u4ee5\u7136\u6765\uff0c\u968f\u5373\u8d77\u8eab\u9053\uff1a\u201c\u6211\u8be5\u8d70\u4e86\uff0c\u4e0b\u6b21\u518d\u6765\uff01\u201d \u3000\u3000\u5c24\u4e09\u5fd9\u9053\uff1a\u201c\u516c\u5b50\u521a\u6765\u5c31\u8d70\uff0c\u8fde\u59d1\u5a18\u4e5f\u6ca1\u53eb\uff0c\u90a3\u4e0d\u662f\u767d\u6765\u4e86\u5417\uff1f\u201d \u3000\u3000\u201c\u6211\u5c31\u4f4f\u5728\u5f00\u5c01\uff0c\u6765\u4e00\u6b21\u8d70\u4e0d\u4e86\u591a\u5c11\u8def\uff0c\u767d\u8dd1\u4e00\u8d9f\u4e5f\u7b97\u4e0d\u4e86\u4ec0\u4e48\uff0c\u66f4\u4f55\u51b5\u6211\u627e\u7684\u53ea\u662f\u6842 \u82b1\uff0c\u65e2\u7136\u6842\u82b1\u8d70\u4e86\uff0c\u6211\u672c\u6765\u5c31\u4e0d\u6253\u7b97\u7559\u4e0b\u3002\u201d \u3000\u3000\u5c24\u4e09\u5ffd\u7136\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u8fd9\u4e48\u591a\u59d1\u5a18\uff0c\u516c\u5b50\u4e3a\u4ec0\u4e48\u53ea\u770b\u4e2d\u4e86\u6842\u82b1\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6545\u4f5c\u4e0d\u89e3\u7684\u9053\uff1a\u201c\u8fd9\u6709\u4ec0\u4e48\u5947\u602a\u7684\uff0c\u56e0\u4e3a\u6211\u770b\u5979\u770b\u5bf9\u4e86\u773c\uff0c\u5f53\u7136\u559c\u6b22\u3002\u201d \u3000\u3000\u201c\u5728\u5979\u56de\u5bb6\u7684\u5934\u5929\u665a\u4e0a\uff0c\u516c\u5b50\u597d\u50cf\u8fdb\u8fc7\u5979\u7684\u623f\u95f4\uff1f\u201d \u3000\u3000\u201c\u82e5\u4e0d\u8fdb\u5979\u7684\u623f\u95f4\uff0c\u53c8\u600e\u4f1a\u8ba4\u8bc6\u5979\uff0c\u4f60\u5e76\u672a\u628a\u5979\u53eb\u5230\u8fd9\u91cc\u6765\u8fc7\u3002\u201d \u3000\u3000\u201c\u516c\u5b50\u548c\u5979\u2026\u2026\u201d \u3000\u3000\u201c\u548c\u5979\u4ec0\u4e48\uff1f\u7528\u4e0d\u7740\u541e\u541e\u5410\u5410\u7684\uff01\u201d \u3000\u3000\u201c\u662f\u4e0d\u662f\u4eb2\u2026\u2026\u4eb2\u70ed\u8fc7\uff1f\u201d \u3000\u3000\u201c\u82e5\u6ca1\u4eb2\u70ed\u8fc7\uff0c\u4f55\u5fc5\u5bf9\u5979\u597d\uff1f\u201d \u3000\u3000\u201c\u5c0f\u7684\u662f\u8bf4\u2026\u2026\u201d \u3000\u3000\u201c\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u662f\u4e0d\u662f\u4e0a\u8fc7\u5e8a\uff1f\u201d \u3000\u3000\u201c\u4f60\u95ee\u7684\u5927\u591a\u4e86\uff0c\u8fd9\u79cd\u4e8b\u662f\u4f60\u8be5\u5411\u5ba2\u4eba\u95ee\u7684\u5417\uff1f\u201d \u3000\u3000\u5c24\u4e09\u8fde\u5fd9\u966a\u7b11\u9053\uff1a\u201c\u5c0f\u7684\u662f\u5e0c\u671b\u516c\u5b50\u6700\u597d\u4e0d\u66fe\u548c\u5979\u4e0a\u8fc7\u5e8a\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u5fc3\u4e2d\u4e00\u52a8\u9053\uff1a\u201c\u4f60\u8bf4\u8fd9\u8bdd\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u5c24\u4e09\u867d\u5df2\u89c9\u51fa\u4e0d\u8be5\u8bf4\u8fd9\u4e9b\uff0c\u4f46\u8bdd\u5df2\u51fa\u53e3\uff0c\u53c8\u4e0d\u80fd\u4e0d\u76f4\u8bf4\u4e0b\u53bb\uff0c\u987f\u4e86\u4e00\u987f\u9053\uff1a\u201c\u597d\u5728\u6842\u82b1 \u5df2\u7ecf\u8d70\u4e86\uff0c\u8bf4\u51fa\u6765\u4e5f\u4e0d\u5f71\u54cd\u751f\u610f\u3002\u636e\u5e38\u6765\u8fd9\u91cc\u7684\u5ba2\u4eba\u8bf4\uff0c\u6842\u82b1\u8eab\u4e0a\u597d\u50cf\u4e0d\u5927\u5e72\u51c0\u3002\u201d \u3000\u3000\u201c\u4e0d\u5e72\u51c0\uff1f\u90a3\u662f\u5979\u4e0d\u5e38\u6d17\u6fa1\u7684\u5173\u7cfb\u5427\uff1f\u201d \u3000\u3000\u201c\u4e0d\uff0c\u4e0d\u662f\u8fd9\u6837\uff0c\u662f\u5979\u8eab\u4f53\u91cc\u9762\u4e0d\u5e72\u51c0\u3002\u201d \u3000\u3000\u201c\u90a3\u662f\u6709\u75c5\u4e86\uff1f\u770b\u5979\u7684\u6837\u5b50\uff0c\u6839\u672c\u4e0d\u50cf\u6709\u75c5\u554a\uff1f\u201d \u3000\u3000\u201c\u90a3\u79cd\u75c5\uff0c\u5916\u8868\u6839\u672c\u770b\u4e0d\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u5979\u7a76\u7adf\u5f97\u7684\u4ec0\u4e48\u75c5\uff1f\u201d \u3000\u3000\u201c\u5509\uff01\u4e0d\u8bf4\u4e5f\u7f62\u3002\u603b\u800c\u8a00\u4e4b\u4e00\u53e5\u8bdd\uff0c\u5ba2\u4eba\u548c\u5979\u4e0a\u8fc7\u5e8a\u540e\uff0c\u90fd\u4f1a\u89c9\u5f97\u4e0d\u8212\u670d\u3002\u201d \u3000\u3000\u201c\u54ea\u91cc\u4e0d\u8212\u670d\uff1f\u201d \u3000\u3000\u201c\u7a76\u7adf\u54ea\u91cc\u4e0d\u8212\u670d\uff0c\u5c0f\u7684\u4e5f\u4e0d\u6e05\u695a\uff0c\u53cd\u6b63\u4e0d\u8212\u670d\u5c31\u662f\u4e0d\u8212\u670d\uff0c\u5982\u679c\u516c\u5b50\u5df2\u548c\u5979\u4e0a\u8fc7\u5e8a\uff0c \u4e00\u5b9a\u611f\u89c9\u5f97\u51fa\u6765\uff0c\u7528\u4e0d\u7740\u5c0f\u7684\u518d\u591a\u8d39\u53e3\u820c\u3002\u201d \u3000\u3000\u7531\u5c24\u4e09\u7684\u8fd9\u4e00\u756a\u8bdd\u542c\u6765\uff0c\u8db3\u53ef\u8bc1\u660e\u6842\u82b1\u7684\u786e\u662f\u4e07\u866b\u95e8\u7684\u4eba\uff0c\u4e5f\u53ef\u8bc1\u660e\u8001\u5996\u5a46\u5e76\u672a\u6b3a\u9a97\u81ea \u5df1\u3002 \u3000\u3000\u4ed6\u642d\u8baa\u7740\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u65e9\u8bf4\uff1f\u201d \u3000\u3000\u5c24\u4e09\u82e6\u7b11\u9053\uff1a\u201c\u8fd9\u79cd\u4e8b\u600e\u4e48\u80fd\u5bf9\u5ba2\u4eba\u8bb2\u5462\uff1f\u4f5c\u751f\u610f\u7684\u90fd\u662f\u8001\u738b\u5356\u74dc\uff0c\u5982\u679c\u5979\u8fd8\u5728\u8fd9\u91cc\uff0c \u5c0f\u7684\u4e5f\u4e0d\u53ef\u80fd\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u53e6\u4e24\u4f4d\u59d1\u5a18\u662f\u5426\u4e5f\u548c\u5979\u4e00\u6837\uff1f\u201d \u3000\u3000\u201c\u8fd9\u5c31\u4e0d\u6e05\u695a\u4e86\uff0c\u53cd\u6b63\u5979\u4eec\u662f\u4e00\u8d77\u8d70\u7684\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u603b\u7b97\u6ca1\u6709\u767d\u6765\uff0c\u53c8\u8c08\u4e86\u51e0\u53e5\uff0c\u4fbf\u79bb\u5f00\u201c\u8fce\u6625\u9601\u201d\u3002 \u3000\u3000\u4e00\u8fde\u51e0\u5929\u8fc7\u53bb\uff0c\u53d7\u4f24\u7684\u4eba\u90fd\u5df2\u597d\u5f97\u5dee\u4e0d\u591a\u3002 \u3000\u3000\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u6b64\u523b\u6700\u76fc\u671b\u7684\u4fbf\u662f\u859b\u767e\u80dc\u80fd\u518d\u5ea6\u524d\u6765\uff0c\u4ee5\u4fbf\u6709\u4e86\u65b0\u7684\u6d88\u606f\uff0c\u518d\u8ba1\u5212\u4e0b\u4e00\u6b65 \u7684\u884c\u52a8\u3002 \u3000\u3000\u5426\u5219\uff0c\u8001\u5f85\u5728\u5f00\u5c01\uff0c\u603b\u4e0d\u662f\u529e\u6cd5\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u4e00\u76f4\u6df1\u5c45\u7b80\u51fa\uff0c\u5bf9\u4eca\u540e\u6709\u5173\u884c\u52a8\uff0c\u81ea\u5df1\u4e0d\u505a\u4e3b\u5f20\uff0c\u4e00\u5207\u7686\u542c\u7531\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u7684\u51b3 \u5b9a\u3002 \u3000\u3000\u56e0\u4e4b\uff0c\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u867d\u672a\u91c7\u53d6\u4efb\u4f55\u884c\u52a8\uff0c\u5fc3\u60c5\u5374\u4e00\u76f4\u5341\u5206\u6c89\u91cd\u3002 \u3000\u3000\u6b64\u523b\uff0c\u4e24\u4eba\u53c8\u5728\u5ba2\u5385\u65e0\u804a\u95e8\u5750\u3002 \u3000\u3000\u738b\u5f64\u957f\u957f\u53f9\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u5144\u5f1f\u8eab\u4e3a\u5927\u5185\u4f8d\u536b\u7edf\u9886\uff0c\u79bb\u4eac\u65e5\u4e45\uff0c\u5b9e\u5728\u4e0d\u662f\u529e\u6cd5\uff0c\u5982\u679c\u6700\u8fd1 \u51e0\u5929\u4e8b\u60c5\u4ecd\u65e0\u6cd5\u8fdb\u884c\uff0c\u5c31\u5fc5\u987b\u56de\u4eac\u5411\u5723\u4e0a\u547d\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9894\u9996\u9053\uff1a\u201c\u8fd9\u662f\u8001\u5f1f\u7684\u4efd\u5185\u4e8b\uff0c\u6c5f\u67d0\u65e0\u6cd5\u52a0\u5165\u4efb\u4f55\u610f\u89c1\uff0c\u53ea\u662f\u4e0d\u77e5\u4e09\u516c\u4e3b\u662f\u5426\u4e5f \u8981\u968f\u8001\u5f1f\u4e00\u8d77\u56de\u4eac\uff1f\u201d \u3000\u3000\u201c\u4e09\u516c\u4e3b\u51fa\u4eac\u524d\u5df2\u5411\u7687\u4e0a\u7980\u660e\u8fc7\uff0c\u51c6\u5907\u627e\u5230\u5e08\u7236\u4e00\u9053\u98de\u8679\u82df\u6167\u6708\u524d\u8f88\uff0c\u5728\u6c5f\u6e56\u4e0a\u5386\u7ec3\u5386 \u7ec3\uff0c\u5144\u5f1f\u7684\u610f\u601d\u4e0d\u5982\u8981\u5979\u968f\u540c\u6c5f\u5144\u884c\u52a8\uff0c\u6c5f\u5144\u5927\u6982\u8fd8\u8bb0\u5f97\u82df\u5973\u4fa0\u8bf4\u8fc7\u7684\u8bdd\u5427\uff1f\u201d \u3000\u3000\u201c\u82df\u5973\u4fa0\u8bf4\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5979\u66fe\u6709\u610f\u628a\u4e09\u516c\u4e3b\u548c\u5c0f\u71d5\u5b50\u64ae\u5408\u6210\u4e00\u5bf9\uff0c\u800c\u5de8\u82df\u5973\u4fa0\u5f53\u65e5\u5728\u4eac\u65f6\uff0c\u8fd8\u66fe\u628a\u8fd9\u610f\u601d\u5411\u7687 \u4e0a\u8868\u793a\u8fc7\u3002\u201d \u3000\u3000\u201c\u7687\u4e0a\u53ef\u66fe\u7b54\u5e94\uff1f\u201d \u3000\u3000\u201c\u82df\u5973\u4fa0\u591a\u5e74\u524d\u66fe\u6551\u8fc7\u7687\u4e0a\u4e00\u547d\uff0c\u7687\u4e0a\u4e00\u76f4\u5bf9\u5979\u5f88\u611f\u6fc0\uff0c\u4e5f\u5f88\u5c0a\u91cd\uff0c\u5426\u5219\u4e5f\u4e0d\u4f1a\u8ba9\u4e09\u516c \u4e3b\u62dc\u5979\u4e60\u827a\u4e86\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u7687\u4e0a\u603b\u8be5\u6709\u6240\u8868\u793a\u5427\uff1f\u201d \u3000\u3000\u201c\u636e\u8bf4\u7687\u4e0a\u5df2\u51b3\u5b9a\u8fd9\u4ef6\u4e8b\u7531\u82df\u5973\u4fa0\u505a\u4e3b\uff0c\u4e09\u516c\u4e3b\u4e60\u4e86\u6b66\uff0c\u5df2\u7ecf\u7b97\u662f\u6c5f\u6e56\u4eba\u4e86\uff0c\u6c5f\u6e56\u4eba\u5ac1 \u7ed9\u6c5f\u6e56\u4eba\uff0c\u53c8\u6709\u4ec0\u4e48\u4e0d\u53ef\uff1f\u5982\u679c\u80fd\u8ba9\u4e09\u516c\u4e3b\u968f\u540c\u6c5f\u5144\u884c\u52a8\uff0c\u5979\u548c\u5c0f\u71d5\u5b50\u624d\u80fd\u6709\u673a\u4f1a\u7ecf\u5e38\u63a5 \u89e6\uff0c\u4e24\u4eba\u82e5\u80fd\u5904\u5f97\u6765\uff0c\u81ea\u7136\u5c31\u4f1a\u6210\u4e3a\u4e00\u5bf9\uff0c\u5426\u5219\u4e5f\u53ea\u597d\u518d\u4f5c\u6253\u7b97\u3002\u201d \u3000\u3000\u6b63\u8c08\u5230\u8fd9\u91cc\uff0c\u4e00\u4f4d\u4e0d\u901f\u4e4b\u5ba2\u6084\u7136\u8fdb\u5165\u5ba2\u5385\uff0c\u6b63\u662f\u4ed6\u4eec\u6025\u4e8e\u4e00\u89c1\u7684\u859b\u767e\u80dc\u3002 \u3000\u3000\u859b\u767e\u80dc\u4e00\u8fdb\u5ba2\u5385\u5c31\u9053\uff1a\u201c\u738b\u5927\u4eba\u548c\u6c5f\u524d\u8f88\u8fd9\u51e0\u5929\u4e00\u5b9a\u95ee\u5f97\u53d1\u614c\u5427\uff01\u665a\u8f88\u4eca\u5929\u6765\uff0c\u6709\u6700\u65b0 \u7684\u6d88\u606f\u5411\u4f60\u4eec\u62a5\u544a\u3002\u201d \u3000\u3000\u738b\u5f64\u559c\u5f62\u4e8e\u8272\uff0c\u6025\u6025\u95ee\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u597d\u6d88\u606f\uff0c\u5feb\u5feb\u8bf4\u6765\u542c\u542c\uff01\u201d \u3000\u3000\u201c\u738b\u5927\u4eba\u60f3\u542c\u90a3\u4e00\u65b9\u9762\u7684\uff1f\u201d \u3000\u3000\u201c\u81ea\u7136\u662f\u90a3\u4e09\u4e2a\u5973\u4eba\u7684\u4e0b\u843d\u3002\u201d \u3000\u3000\u201c\u5979\u4eec\u6700\u521d\u786e\u5b9e\u88ab\u9a6c\u6587\u4e2d\u7a9d\u85cf\u5728\u629a\u7f72\u540e\u8859\uff0c\u53ea\u662f\u4e24\u5929\u524d\u5df2\u88ab\u9001\u8d70\u4e86\u3002\u201d \u3000\u3000\u201c\u9a6c\u6587\u4e2d\u4e3a\u4ec0\u4e48\u8981\u9001\u8d70\u5979\u4eec\uff1f\u201d \u3000\u3000\u201c\u9a6c\u6587\u4e2d\u5f53\u7136\u4e5f\u62c5\u5fc3\u88ab\u738b\u5927\u4eba\u548c\u6c5f\u524d\u8f88\u67e5\u51fa\uff0c\u4e00\u65e6\u67e5\u51fa\uff0c\u4ed6\u7684\u6bd2\u8c0b\u5978\u8ba1\u5c82\u4e0d\u5b8c\u5168\u8d25\u9732\uff0c \u6240\u4ee5\u53ea\u597d\u628a\u90a3\u4e09\u4e2a\u5973\u7684\u9001\u56de\u897f\u57df\u3002\u201d \u3000\u3000\u201c\u90a3\u4e09\u4e2a\u5973\u7684\u662f\u897f\u57df\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u4e0d\uff0c\u90a3\u4e09\u4e2a\u5973\u7684\u539f\u662f\u82d7\u7586\u4e07\u866b\u95e8\u7684\u866b\u672f\u9ad8\u624b\uff0c\u540e\u6765\u6295\u9760\u4e86\u897f\u57df\u9b54\u6559\u3002\u201d \u3000\u3000\u201c\u5979\u4eec\u5df2\u7ecf\u8d70\u4e86\u4e24\u5929\u591a\uff0c\u8001\u5f1f\u4e3a\u4ec0\u4e48\u4e0d\u65e9\u6765\u4f20\u9012\u6d88\u606f\uff0c\u73b0\u5728\u53ea\u6015\u8ffd\u4e5f\u8ffd\u4e0d\u4e0a\u4e86\u3002\u201d \u3000\u3000\u201c\u665a\u8f88\u662f\u4eca\u5929\u4e00\u65e9\u624d\u542c\u5230\u6d88\u606f\u7684\uff0c\u600e\u53ef\u80fd\u65e9\u544a\u8bc9\u738b\u5927\u4eba\uff1f\u201d \u3000\u3000\u201c\u53ef\u77e5\u9053\u5979\u4eec\u8d70\u7684\u662f\u54ea\u4e00\u6761\u8def\uff1f\u201d \u3000\u3000\u201c\u9a6c\u6587\u4e2d\u66fe\u547d\u9c7c\u5316\u9f99\u6d3e\u51fa\u9ad8\u624b\u62a4\u9001\uff0c\u9884\u6599\u53ef\u80fd\u7ecf\u8fc7\u6d1b\u9633\uff0c\u518d\u5230\u9099\u5c71\uff0c\u4e5f\u8bb8\u4f1a\u5728\u9099\u5c71\u5c0f\u4f4f \u51e0\u5929\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u4f1a\u5728\u9099\u5c71\u5c0f\u4f4f\uff1f\u201d \u3000\u3000\u201c\u90a3\u5c31\u5b9e\u5bf9\u738b\u5927\u4eba\u8bf4\u4e86\uff0c\u9c7c\u5316\u9f99\u7684\u6d1e\u5ead\u6c34\u5be8\u5df2\u6709\u4e00\u90e8\u5206\u79fb\u5230\u90ca\u5c71\u5c71\u540e\u7684\u9ec4\u6cb3\u5357\u5cb8\u3002\u53e6 \u5916\uff0c\u5728\u9099\u5c71\u5c71\u4e0a\u4e5f\u6709\u9c7c\u5316\u9f99\u7684\u65f1\u5be8\uff0c\u90a3\u4e09\u4e2a\u5973\u7684\u8def\u7ecf\u9099\u5c71\uff0c\u81ea\u7136\u6709\u53ef\u80fd\u5728\u9c7c\u5316\u9f99\u7684\u6c34\u5be8\u6216\u65f1 \u5be8\u505c\u7559\u4e00\u4e0b\u3002\u201d \u3000\u3000\u738b\u5f64\u70b9\u4e86\u70b9\u5934\u9053\uff1a\u201c\u5982\u679c\u5979\u4eec\u80fd\u591f\u5728\u9099\u5c71\u505c\u7559\uff0c\u90a3\u5c31\u597d\u529e\u591a\u4e86\u3002\u201d \u3000\u3000\u859b\u767e\u80dc\u671b\u7740\u738b\u5f64\u9053\uff1a\u201c\u83ab\u975e\u738b\u5927\u4eba\u60f3\u548c\u9c7c\u5316\u9f99\u90e8\u5c71\u65b9\u9762\u7684\u4eba\u9a6c\u6765\u4e00\u6b21\u786c\u62da\uff1f\u201d \u3000\u3000\u738b\u5f64\u8ba4\u771f\u7684\u53cd\u95ee\u9053\uff1a\u201c\u96be\u9053\u4e0d\u53ef\u4ee5\u5417\uff1f\u201d \u3000\u3000\u859b\u767e\u80dc\u4e0d\u4ee5\u4e3a\u7136\uff0c\u9053\uff1a\u201c\u9c7c\u5316\u9f99\u5728\u90ae\u5c71\u65b9\u9762\u7684\u4eba\u9a6c\uff0c\u4e0d\u4e0b\u4e0a\u5e72\u4e4b\u4f17\uff0c\u800c\u4e14\u4e2a\u4e2a\u90fd\u662f\u9ad8 \u624b\uff0c\u738b\u5927\u4eba\u8fd8\u662f\u907f\u514d\u548c\u5bf9\u65b9\u51b2\u7a81\u4e3a\u4f73\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u4e5f\u63a5\u53e3\u9053\uff1a\u201c\u859b\u8001\u5f1f\u8bf4\u5f97\u5bf9\u3002\u5982\u679c\u90a3\u4e09\u4e2a\u5973\u7684\u771f\u80fd\u5728\u90ae\u5c71\u5c0f\u4f4f\uff0c\u5bf9\u54b1\u4eec\u6765\u8bf4\u7684\u786e \u662f\u4ef6\u597d\u4e8b\u3002\u201d \u3000\u3000\u738b\u5f64\u95ee\u9053\uff1a\u201c\u6b64\u8bdd\u600e\u8bb2\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u5979\u4eec\u5df2\u7ecf\u8d70\u4e86\u4e24\u5929\u591a\uff0c\u54b1\u4eec\u5f88\u96be\u8ffd\u5f97\u4e0a\u4e86\uff0c\u82e5\u5979\u4eec\u5728\u9099\u5c71\u505c\u4e86\u4e0b\u6765\uff0c\u54b1\u4eec \u53cd\u800c\u53ef\u4ee5\u8d76\u5728\u5979\u4eec\u524d\u9762\uff0c\u7b49\u5979\u4eec\u79bb\u5f00\u9099\u5c71\u7ee7\u7eed\u897f\u884c\u540e\uff0c\u54b1\u4eec\u5728\u8def\u4e0a\u4e0b\u624b\uff0c\u5c82\u4e0d\u66f4\u52a0\u65b9\u4fbf\u4e86 \u5417\uff1f\u201d \u3000\u3000\u201c\u6c5f\u5144\u7684\u770b\u6cd5\u56fa\u7136\u6709\u7406\uff0c\u4f46\u82e5\u5979\u4eec\u5e76\u672a\u5728\u9099\u5c71\u505c\u7559\u5462\uff1f\u201d \u3000\u3000\u859b\u767e\u80dc\u62a2\u7740\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4e2a\u529e\u6cd5\u3002\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u4ec0\u4e48\u529e\u6cd5\uff1f\u201d \u3000\u3000\u859b\u767e\u80dc\u9053\uff1a\u201c\u5979\u4eec\u7531\u9099\u5c71\u53d6\u9053\u8d76\u5f80\u897f\u57df\uff0c\u5fc5\u5b9a\u7ecf\u8fc7\u6f7c\u5173\uff0c\u738b\u5927\u4eba\u548c\u6c5f\u524d\u8f88\u5728\u901a\u89c2\u8d76\u4e0a\u5979 \u4eec\u5e76\u4e0d\u662f\u4ef6\u96be\u4e8b\u3002\u201d \u3000\u3000\u201c\u4f9d\u738b\u67d0\u5224\u65ad\uff0c\u5979\u4eec\u5728\u8def\u4e0a\uff0c\u9664\u6613\u5bb9\u4e4b\u5916\uff0c\u4e5f\u8bb8\u4f1a\u66f4\u8fdb\u4e00\u6b65\u7684\u6539\u6210\u7537\u88c5\uff0c\u6211\u4eec\u5373\u4f7f\u51d1\u5de7 \u78b0\u4e0a\u5979\u4eec\uff0c\u4e5f\u4e0d\u89c1\u5f97\u8ba4\u8bc6\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c31\u662f\u665a\u8f88\u521a\u624d\u51c6\u5907\u8bf4\u800c\u5c1a\u672a\u8bf4\u51fa\u7684\u529e\u6cd5\u3002\u201d \u3000\u3000\u201c\u8001\u5f1f\u8bf4\u8bf4\u770b\uff01\u201d \u3000\u3000\u201c\u6f7c\u5173\u8857\u4e0a\u6709\u4e2a\u9a6c\u5be1\u5987\uff0c\u53ea\u8981\u627e\u5230\u9a6c\u5be1\u5987\u5c31\u6210\u4e86\u3002\u201d \u3000\u3000\u201c\u6f7c\u5173\u90a3\u4e48\u5927\uff0c\u60f3\u627e\u4e00\u4e2a\u9a6c\u5be1\u5987\uff0c\u6050\u6015\u4e5f\u4e0d\u662f\u4ef6\u6613\u4e8b\uff0c\u5982\u679c\u8981\u8001\u5f1f\u5728\u5f00\u5c01\u627e\u4e00\u4e2a\u4ec0\u4e48\u5be1 \u5987\uff0c\u8001\u5f1f\u6709\u529e\u6cd5\u627e\u5f97\u5230\u5417\uff1f\u201d \u3000\u3000\u201c\u8fd9\u540d\u9a6c\u5be1\u5987\u5728\u6f7c\u5173\u540d\u6c14\u5927\u5f97\u5f88\uff0c\u800c\u4e14\u8c01\u90fd\u6709\u673a\u4f1a\u548c\u5979\u63a5\u8fd1\u3002\u201d \u3000\u3000\u201c\u83ab\u975e\u5979\u662f\u4e00\u4f4d\u540d\u5993\uff1f\u201d \u3000\u3000\u201c\u662f\u5f00\u9152\u9986\u7684\uff0c\u4e5f\u505a\u996d\u98df\u751f\u610f\u3002\u53e6\u5916\uff0c\u9152\u9986\u91cc\u8fd8\u6709\u623f\u95f4\u4f9b\u5ba2\u4eba\u4f4f\u5bbf\u3002\u201d \u3000\u3000\u201c\u539f\u6765\u5982\u6b64\uff0c\u53ef\u662f\u8fd9\u4e0e\u90a3\u4e09\u4e2a\u5973\u4eba\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4f4d\u9a6c\u5be1\u5987\u4e5f\u662f\u897f\u57df\u9b54\u6559\u7684\u4eba\uff0c\u800c\u4e14\u548c\u9a6c\u6587\u4e2d\u3001\u8d75\u4e8c\u5824\u7ecf\u5e38\u4fdd\u6301\u5bc6\u5207\u8054\u7edc\uff0c\u9a6c\u5be1\u5987\u540d \u4e49\u4e0a\u662f\u5be1\u5987\uff0c\u636e\u8bf4\u662f\u6709\u7537\u4eba\u7684\uff0c\u800c\u4e14\u548c\u8d75\u4e8c\u5824\u4e5f\u6709\u4e00\u624b\uff0c\u751a\u81f3\u6709\u4eba\u8bf4\u662f\u8d75\u4e8c\u5824\u6d3e\u5979\u53bb\u4e3b\u6301\u9152 \u9986\u7684\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\uff0c\u9a6c\u5be1\u5987\u4e00\u5b9a\u662f\u8eab\u8d1f\u7279\u6b8a\u4efb\u52a1\u4e86\uff1f\u201d \u3000\u3000\u859b\u767e\u80dc\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u90a3\u5bb6\u9152\u9986\u7b49\u4e8e\u662f\u9a6c\u6587\u4e2d\u548c\u897f\u57df\u9b54\u6559\u4e4b\u95f4\u7684\u8054\u7edc\u7ad9\uff0c\u6b63\u56e0\u4e3a\u8fd9 \u5173\u7cfb\uff0c\u90a3\u4e09\u4e2a\u5973\u4eba\u9014\u7ecf\u6f7c\u5173\u65f6\uff0c\u5fc5\u5b9a\u4f1a\u5411\u9a6c\u5be1\u5987\u62a5\u5230\uff0c\u4e5f\u5341\u6709\u516b\u4e5d\u4f1a\u5728\u9a6c\u5be1\u5987\u7684\u9152\u9986\u4f4f\u4e0a\u51e0 \u5929\u3002\u201d \u3000\u3000\u201c\u9a6c\u5be1\u5987\u662f\u5426\u6709\u6b66\u529f\u3002\u201d \u3000\u3000\u201c\u4e0d\u6e05\u695a\uff0c\u4e0d\u8fc7\u5728\u60f3\u50cf\u4e2d\uff0c\u9a6c\u5be1\u5987\u4e00\u5b9a\u4e0d\u662f\u7b49\u95f2\u4eba\u7269\uff0c\u9664\u4e86\u6b66\u529f\u4e4b\u5916\uff0c\u4e5f\u8bb8\u8fd8\u4f1a\u4e00\u4e9b\u5996 \u6cd5\u90aa\u672f\u3002\u201d \u3000\u3000\u738b\u5f64\u6c89\u5bc2\u4e86\u751a\u4e45\uff0c\u624d\u95ee\u9053\uff1a\u201c\u90a3\u665a\u5728\u9053\u89c2\u540e\u7684\u6811\u6797\u9644\u8fd1\u4e00\u6218\uff0c\u738b\u67d0\u8fd9\u65b9\u9762\u6709\u4e2a\u4eba\u5931\u53bb\u4e0b \u843d\uff0c\u859b\u8001\u5f1f\u662f\u5426\u542c\u8bf4\u8fc7\uff1f\u201d \u3000\u3000\u859b\u767e\u80dc\u95ee\u9053\uff1a\u201c\u662f\u54ea\u4e00\u4f4d\uff1f\u201d \u3000\u3000\u201c\u4e00\u4e2a\u53c8\u8001\u53c8\u4e11\u7684\u5973\u4eba\uff0c\u59d3\u53f2\uff0c\u53eb\u53f2\u5999\u79cb\u3002\u201d \u3000\u3000\u201c\u738b\u5927\u4eba\u7684\u624b\u4e0b\uff0c\u600e\u4e48\u4f1a\u6709\u8fd9\u4e48\u4e00\u4e2a\u8001\u5973\u4eba\u5462\uff1f\u201d \u3000\u3000\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u8fd9\u8001\u5973\u4eba\u662f\u897f\u57df\u9b54\u6559\u7684\u4e00\u540d\u957f\u8001\uff0c\u539f\u662f\u6f5c\u4f0f\u5728\u5bab\u4e2d\u63a7\u5236\u4e09\u516c\u4e3b\u884c\u52a8\u7684\uff0c\u540e \u6765\u51b3\u5b9a\u5f03\u6697\u6295\u660e\u3002\u738b\u67d0\u8fd9\u6b21\u5357\u6765\uff0c\u5c31\u628a\u5979\u4e00\u8d77\u5e26\u6765\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u8981\u5e26\u5979\u6765\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u8fd9\u8001\u5973\u4eba\u548c\u90a3\u4e09\u4e2a\u5973\u4eba\u5176\u4e2d\u7684\u4e00\u4e2a\u76f8\u8bc6\uff0c\u5e26\u7740\u5979\u6765\uff0c\u5bf9\u6211\u4eec\u8fd9\u8fb9\u7684\u884c\u52a8\u591a\u591a\u5c11\u5c11 \u6709\u4e9b\u5e2e\u52a9\u3002\u4e09\u4e2a\u5973\u4eba\u5176\u4e2d\u7684\u4e00\u4e2a\u6b63\u662f\u7531\u5979\u6307\u8ba4\u51fa\u6765\u7684\uff0c\u5426\u5219\u738b\u67d0\u6839\u672c\u4e0d\u53ef\u80fd\u67e5\u51fa\u5979\u4eec\u7684\u8eab\u4efd \u6765\u3002\u201d \u3000\u3000\u201c\u539f\u6765\u662f\u8fd9\u6837\uff0c\u90a3\u8001\u5973\u4eba\u662f\u5426\u5f53\u665a\u5c31\u6218\u6b7b\u4e86\u5462\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\uff0c\u4e8b\u540e\u738b\u67d0\u548c\u6c5f\u5927\u4fa0\u7b49\u4eba\u66fe\u5728\u73b0\u573a\u4ed4\u7ec6\u641c\u67e5\u8fa8\u8ba4\u5730\u4e0a\u7684\u5c38\u4f53\uff0c\u5e76\u672a\u53d1\u73b0\u6709\u5979\u5728 \u5185\u3002\u201d \u3000\u3000\u859b\u767e\u80dc\u5ffd\u7136\u554a\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u665a\u8f88\u60f3\u8d77\u6765\u4e86\u2026\u2026\u201d \u3000\u3000\u738b\u5f64\u8feb\u4e0d\u53ca\u5f85\u7684\u622a\u53e3\u95ee\u9053\uff1a\u201c\u859b\u8001\u5f1f\u60f3\u8d77\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u90a3\u665a\u665a\u8f88\u968f\u9c7c\u5316\u9f99\u56de\u53bb\u65f6\uff0c\u961f\u4f0d\u91cc\u597d\u50cf\u6709\u4e2a\u53c8\u8001\u53c8\u4e11\u7684\u4eba\uff0c\u4e0d\u8fc7\u90a3\u662f\u4e2a\u7537\u4eba\u3002\u201d \u3000\u3000\u201c\u90a3\u6b63\u662f\u5979\uff0c\u56e0\u4e3a\u5979\u5f53\u65f6\u662f\u6539\u6362\u4e86\u7537\u88c5\u7684\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u540e\u6765\u665a\u8f88\u5c31\u518d\u6ca1\u89c1\u5230\u8fd9\u4eba\u3002\u201d \u3000\u3000\u201c\u5f53\u65f6\u5979\u968f\u5728\u9c7c\u5316\u9f99\u7684\u961f\u4f0d\u4e2d\uff0c\u662f\u5426\u88ab\u70b9\u4e86\u7a74\u9053\uff0c\u8fd8\u662f\u7ef3\u6346\u7d22\u7ed1\uff1f\u201d \u3000\u3000\u201c\u90fd\u6ca1\u6709\uff0c\u770b\u6837\u5b50\u4e00\u5207\u5f88\u81ea\u7531\u3002\u201d \u5367\u9f99\u751f\u300a\u71d5\u5b50\u4f20\u5947\u300b \u7b2c\u4e8c\u5341\u516d\u56de\u3000\u67f3\u6697\u82b1\u660e \u3000\u3000\u7531\u859b\u767e\u80dc\u7684\u8bdd\u5224\u65ad\uff0c\u8001\u5996\u5a46\u53f2\u5999\u79cb\u663e\u7136\u662f\u5b58\u5fc3\u9003\u8131\u800c\u6295\u5954\u5411\u9c7c\u5316\u9f99\u65b9\u9762\u7684\u3002 \u3000\u3000\u5982\u6b64\u4e00\u6765\uff0c\u9c7c\u5316\u9f99\u5bf9\u8fd9\u8fb9\u7684\u4e00\u5207\u5fc5\u5b9a\u4e86\u82e5\u6307\u638c\u4e86\u3002\u800c\u9c7c\u5316\u9f99\u4e5f\u5fc5\u5b9a\u4f1a\u5c06\u4ece\u8001\u5996\u5a46\u53e3\u4e2d\u83b7 \u77e5\u7684\u4e00\u5207\u518d\u8f6c\u62a5\u9a6c\u6587\u4e2d\u3002 \u3000\u3000\u859b\u767e\u80dc\u53c8\u5750\u4e86\u4e00\u4f1a\u513f\uff0c\u4fbf\u8d77\u8eab\u544a\u8f9e\u9053\uff1a\u201c\u665a\u8f88\u4e0d\u4fbf\u5728\u8fd9\u91cc\u591a\u5f85\uff0c\u73b0\u8be5\u56de\u53bb\u4e86\u3002\u201d \u3000\u3000\u738b\u5f64\u5e76\u672a\u7acb\u5373\u653e\u4ed6\u8d70\uff0c\u518d\u95ee\u9053\uff1a\u201c\u9c7c\u5316\u9f99\u76ee\u524d\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u53ef\u80fd\u8fd8\u5728\u5f00\u5c01\uff0c\u4e0d\u8fc7\u665a\u8f88\u5df2\u6709\u4e24\u5929\u6ca1\u89c1\u7740\u4ed6\u4e86\u3002\u201d \u3000\u3000\u201c\u4ed6\u82e5\u79bb\u5f00\u5f00\u5c01\uff0c\u4f1a\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u201c\u5f88\u96be\u8bb2\uff0c\u7531\u4e8e\u9099\u5c71\u76ee\u524d\u5df2\u5efa\u6709\u6c34\u5be8\u548c\u65f1\u5be8\uff0c\u5f88\u591a\u4e8b\u60c5\u90fd\u5fc5\u987b\u4ed6\u4eb2\u53bb\u4e3b\u6301\u3002\u201d \u3000\u3000\u201c\u8001\u5f1f\u5df2\u6765\u8fc7\u8fd9\u91cc\u4e24\u6b21\uff0c\u6211\u5f88\u62c5\u5fc3\u4f60\u7684\u884c\u52a8\u4f1a\u88ab\u4ed6\u4eec\u67e5\u77e5\u3002\u201d \u3000\u3000\u201c\u738b\u5927\u4eba\u653e\u5fc3\uff0c\u81f3\u5c11\u76ee\u524d\u9c7c\u5316\u9f99\u8fd8\u4e0d\u81f3\u5bf9\u665a\u8f88\u8d77\u7591\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u9c7c\u5316\u9f99\u5df2\u628a\u665a\u8f88\u5f15\u4e3a\u5fc3\u8179\u3002\u201d \u3000\u3000\u201c\u82e5\u9a6c\u6587\u4e2d\u548c\u9c7c\u5316\u9f99\u76f8\u7ee7\u88ab\u671d\u5ef7\u7ef3\u4e4b\u4ee5\u6cd5\u540e\uff0c\u859b\u8001\u5f1f\u505a\u4f55\u6253\u7b97\uff1f\u201d \u3000\u3000\u201c\u90a3\u662f\u4ee5\u540e\u7684\u4e8b\uff0c\u53ea\u6709\u4ee5\u540e\u518d\u8bf4\u4e86\u3002\u201d \u3000\u3000\u201c\u8001\u5f1f\u6709\u529f\u4e8e\u671d\u5ef7\uff0c\u90a3\u65f6\u738b\u67d0\u5fc5\u5f53\u4fdd\u594f\u7687\u4e0a\uff0c\u4e0d\u96be\u4e3a\u8001\u5f1f\u8c0b\u4e2a\u4e00\u5b98\u534a\u804c\uff0c\u5982\u80af\u5728\u5927\u5185\u6548 \u529b\uff0c\u738b\u67d0\u5c31\u66f4\u6b22\u8fce\u4e86\u3002\u201d \u3000\u3000\u201c\u665a\u8f88\u5f53\u5e74\u5728\u76f8\u56fd\u5bfa\u66fe\u88ab\u7981\u76d1\u4e00\u5e74\u4e4b\u4e45\uff0c\u4eca\u540e\u53ea\u6c42\u80fd\u4fdd\u6709\u81ea\u7531\u4e4b\u8eab\uff0c\u738b\u5927\u4eba\u7684\u4e00\u756a\u597d \u610f\uff0c\u665a\u8f88\u53ea\u6709\u5fc3\u9886\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u77e5\u859b\u8001\u5f1f\u662f\u5426\u80fd\u5728\u738b\u67d0\u52a8\u8eab\u524d\u518d\u6765\u4e00\u6b21\uff1f\u201d \u3000\u3000\u201c\u738b\u5927\u4eba\u548c\u6c5f\u524d\u8f88\u8fd9\u8fb9\u7684\u4eba\uff0c\u60f3\u6765\u5fc5\u5b9a\u5f88\u5feb\u5c31\u6709\u884c\u52a8\u4e86\uff0c\u665a\u8f88\u53ef\u80fd\u6765\u4e0d\u53ca\u518d\u6765\uff0c\u5c31\u6b64\u544a \u8f9e\u3002\u201d \u3000\u3000\u859b\u767e\u80dc\u521a\u8d70\u4e0d\u4e45\uff0c\u5c0f\u71d5\u5b50\u4fbf\u7531\u201c\u8fce\u6625\u9601\u201d\u56de\u6765\uff0c\u5f53\u5373\u628a\u7ecf\u8fc7\u8bf4\u4e86\u4e00\u904d\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u5429\u5490\u5c0f\u71d5\u5b50\u56de\u53bb\uff0c\u518d\u548c\u738b\u5f64\u5546\u8bae\u8be5\u5982\u4f55\u884c\u52a8\u3002 \u3000\u3000\u738b\u5f64\u95ee\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u6c5f\u5144\uff0c\u4e3a\u4e86\u80fd\u8ffd\u4e0a\u90a3\u4e09\u4e2a\u5973\u4eba\uff0c\u54b1\u4eec\u662f\u5426\u4eca\u665a\u5c31\u5f00\u59cb\u884c\u52a8\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u90a3\u4e09\u4e2a\u5973\u4eba\u5df2\u7ecf\u8d70\u4e86\u4e24\u5929\u591a\u4e86\uff0c\u518d\u8ffd\u8d76\u4e5f\u6765\u4e0d\u53ca\uff0c\u82e5\u884c\u52a8\u5f97\u5927\u7a81\u7136\uff0c\u53cd\u800c \u4f1a\u5f15\u8d77\u9a6c\u6587\u4e2d\u7684\u7591\u5fc3\u3002\u201d \u3000\u3000\u201c\u4f9d\u6c5f\u5144\u4e4b\u610f\u5462\uff1f\u201d \u3000\u3000\u201c\u660e\u5929\u518d\u8d70\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u201c\u662f\u5426\u8be5\u548c\u9a6c\u6587\u4e2d\u6253\u58f0\u62db\u547c\uff1f\u201d \u3000\u3000\u201c\u82e5\u6253\u62db\u547c\uff0c\u9a6c\u6587\u4e2d\u5fc5\u5b9a\u4f1a\u4eb2\u6765\u4e3a\u4e09\u516c\u4e3b\u53ca\u8001\u5f1f\u9001\u884c\uff0c\u53cd\u800c\u4e0d\u5999\uff0c\u4e0d\u59a8\u5728\u660e\u65e5\u884c\u52a8\u524d\u4ea4 \u4ee3\u5e97\u5bb6\u901a\u77e5\u629a\u7f72\u5373\u53ef\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u5144\u5f1f\u73b0\u5c31\u5411\u4e09\u516c\u4e3b\u8bf4\u660e\u53bb\u3002\u201d \u3000\u3000\u6b21\u65e5\u4e00\u65e9\uff0c\u738b\u5f64\u548c\u6c5f\u5e72\u91cc\u4fbf\u7387\u4f17\u5f00\u59cb\u884c\u52a8\u3002 \u3000\u3000\u4ed6\u4eec\u96c7\u4e86\u4e24\u8f86\u9a6c\u8f66\uff0c\u4e09\u516c\u4e3b\u548c\u601c\u82b1\u4e00\u8f86\uff0c\u53e6\u5916\u4e00\u8f86\u7531\u4f24\u52bf\u672a\u75ca\u6108\u8005\u4e58\u5750\u3002 \u3000\u3000\u6c5f\u5e72\u91cc\u4e3a\u907f\u5154\u88ab\u9a6c\u6587\u4e2d\u7684\u624b\u4e0b\u53d1\u73b0\uff0c\u5df2\u6613\u5bb9\u6539\u626e\u6210\u4e00\u540d\u8f66\u592b\u3002 \u3000\u3000\u81f3\u4e8e\u7559\u5728\u5ba2\u6808\u63a5\u53d7\u4fdd\u62a4\u7684\u5b59\u5c0f\u51e4\uff0c\u5219\u7531\u738b\u5f64\u7ed9\u5979\u4e00\u767e\u4e24\u94f6\u5b50\uff0c\u4e8e\u524d\u4e00\u65e5\u6d3e\u4eba\u9001\u56de\u5bb6\u53bb \u4e86\u3002 \u3000\u3000\u8fd9\u662f\u56e0\u4e3a\u5c39\u77e5\u5e9c\u5df2\u88ab\u9a6c\u6587\u4e2d\u9769\u804c\u67e5\u529e\uff0c\u5b59\u5c0f\u51e4\u56de\u5bb6\u4e4b\u540e\u5df2\u4e0d\u6015\u518d\u53d7\u8feb\u5bb3\u3002 \u3000\u3000\u4e24\u5929\u540e\uff0c\u5df2\u5230\u8fbe\u6d1b\u9633\u3002 \u3000\u3000\u5f53\u665a\u5c31\u5728\u6d1b\u9633\u7684\u201c\u4e2d\u5dde\u5ba2\u6808\u201d\u5305\u4e86\u4e00\u95f4\u540e\u9662\u4f4f\u4e0b\u3002 \u3000\u3000\u8fd9\u6b21\u4ed6\u4eec\u7684\u884c\u52a8\u5341\u5206\u4fdd\u5bc6\uff0c\u4e0d\u4f46\u6d1b\u9633\u7684\u77e5\u5e9c\u6beb\u4e0d\u77e5\u60c5\uff0c\u800c\u4e14\u5c31\u8fde\u5ba2\u6808\u91cc\u7684\u4e0a\u4e0a\u4e0b\u4e0b\u4e5f\u8ba4 \u4e3a\u8fd9\u662f\u643a\u6709\u5bb6\u7737\u7684\u4e00\u822c\u5ba2\u5546\u3002 \u3000\u3000\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u5546\u91cf\u7684\u7ed3\u679c\uff0c\u90fd\u8ba4\u4e3a\u5fc5\u987b\u5148\u5f04\u6e05\u695a\u6842\u82b1\u7b49\u4e09\u4e2a\u5973\u4eba\u662f\u5426\u5df2\u5728\u9099\u5c71\u843d\u811a\uff0c\u624d \u80fd\u51b3\u5b9a\u4e0b\u4e00\u6b65\u884c\u52a8\u3002 \u3000\u3000\u4e8e\u662f\u5c31\u5728\u7fcc\u65e5\u4e00\u65e9\uff0c\u6d3e\u51fa\u5c0f\u71d5\u5b50\u548c\u5c0f\u9053\u58eb\u767d\u7fbd\u524d\u5f80\u9099\u5c71\u63a2\u542c\u865a\u5b9e\u3002 \u3000\u3000\u5c0f\u9053\u58eb\u767d\u7fbd\u4e0a\u6b21\u53d7\u7684\u53ea\u662f\u8f7b\u4f24\uff0c\u73b0\u5df2\u5b8c\u5168\u5eb7\u590d\u3002 \u3000\u3000\u9099\u5c71\u5728\u6d1b\u9633\u57ce\u5317\uff0c\u5c71\u540e\u5c31\u662f\u9ec4\u6cb3\u3002 \u3000\u3000\u5c71\u867d\u4e0d\u9ad8\uff0c\u5374\u662f\u4e2d\u539f\u7684\u4e00\u5ea7\u540d\u5c71\uff0c\u5c71\u4e0a\u53ef\u8bf4\u5230\u5904\u90fd\u662f\u540d\u80dc\u53e4\u8ff9\uff0c\u5c24\u5176\u5386\u4ee3\u5e1d\u738b\u9675\u5bdd\uff0c\u591a \u5f97\u4e0d\u53ef\u80dc\u6570\u3002 \u3000\u3000\u5982\u679c\u6765\u6d1b\u9633\u4e0d\u6e38\u9099\u5c71\uff0c\u8be5\u662f\u4eba\u751f\u4e00\u5927\u61be\u4e8b\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u4e45\u6155\u9099\u5c71\u4e4b\u540d\uff0c\u5982\u4eca\u88ab\u6d3e\u6765\u90ae\u5c71\u63a2\u67e5\u865a\u5b9e\uff0c\u6b63\u662f\u4e00\u4e3e\u4e24\u5f97\uff0c\u4e24\u4eba\u5fc3\u91cc\u90fd\u5f88 \u5174\u594b\u3002 \u3000\u3000\u5927\u7ea6\u4e00\u4e2a\u65f6\u8fb0\u4e4b\u540e\uff0c\u4fbf\u7531\u5c71\u9e93\u6e10\u6e10\u884c\u81f3\u5c71\u8170\u3002 \u3000\u3000\u6b64\u523b\uff0c\u767d\u7fbd\u4e5f\u6362\u6210\u4e86\u4fbf\u88c5\uff0c\u548c\u5c0f\u71d5\u5b50\u8d70\u5728\u4e00\u8d77\uff0c\u4e24\u4eba\u7686\u662f\u82f1\u4fca\u6f47\u6d12\uff0c\u98ce\u5ea6\u7fe9\u7fe9\uff0c\u770b\u6765\u6709 \u5982\u4e00\u5bf9\u5144\u5f1f\u3002 \u3000\u3000\u4ed6\u4eec\u4e00\u8def\u4e4b\u4e0a\uff0c\u66fe\u95ee\u8fc7\u4e0d\u5c11\u4eba\uff0c\u4e5f\u95ee\u8fc7\u5c71\u5c45\u6c11\u5bb6\uff0c\u7136\u800c\u8c01\u90fd\u4e0d\u6e05\u695a\u6cb3\u5357\u5cb8\u6709\u4ec0\u4e48\u6c34\u5be8\u3001 \u5c71\u4e0a\u6709\u4ec0\u4e48\u65f1\u5be8\u3002 \u3000\u3000\u8fd9\u5012\u8ba9\u4e24\u4eba\u5927\u611f\u4e3a\u96be\u4e86\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u82e5\u4e00\u76f4\u6253\u542c\u4e0d\u51fa\u6765\uff0c\u771f\u4e0d\u77e5\u8be5\u600e\u6837\u5411\u6c5f\u53d4\u53d4\u548c\u738b\u5927\u4eba\u4ea4\u4ee3\uff1f\u201d \u3000\u3000\u767d\u7fbd\u9053\uff1a\u201c\u65f6\u95f4\u8fd8\u65e9\u5f97\u5f88\uff0c\u4e0d\u59a8\u6162\u6162\u6253\u542c\u3002\u201d \u3000\u3000\u201c\u82e5\u54b1\u4eec\u80fd\u81ea\u884c\u53d1\u73b0\uff0c\u5c82\u4e0d\u66f4\u597d\uff01\u201d \u3000\u3000\u201c\u9099\u5c71\u8303\u56f4\u8fd9\u4e48\u5927\uff0c\u60f3\u81ea\u884c\u53d1\u73b0\u5bf9\u65b9\u7684\u6c34\u5be8\u65f1\u5be8\uff0c\u6050\u6015\u4e0d\u662f\u4ef6\u5bb9\u6613\u4e8b\u3002\u201d \u3000\u3000\u201c\u65f1\u5be8\u56fa\u7136\u96be\u627e\uff0c\u4f46\u60f3\u627e\u6c34\u5be8\u5e76\u4e0d\u96be\uff0c\u53ea\u8981\u627e\u5230\u6c34\u5be8\uff0c\u4fbf\u53ef\u95ee\u51fa\u65f1\u5be8\u7684\u5730\u70b9\u6765\u3002\u201d \u3000\u3000\u201c\u627e\u6c34\u5be8\u4e3a\u4ec0\u4e48\u4e0d\u96be\uff1f\u201d \u3000\u3000\u201c\u65e2\u7136\u79f0\u9ec4\u6cb3\u6c34\u5be8\uff0c\u53c8\u5df2\u77e5\u662f\u5728\u9ec4\u6cb3\u5357\u5cb8\uff0c\u5496\u3002\uff3d\u53ea\u8981\u7ffb\u8fc7\u5c71\u53bb\uff0c\u6cbf\u9ec4\u6cb3\u5357\u5cb8\u641c\u7d22\uff0c\u5c31 \u4e0d\u96be\u627e\u5230\u3002\u201d \u3000\u3000\u201c\u7684\u786e\u662f\u4e2a\u529e\u6cd5\uff0c\u54b1\u4eec\u5feb\u8d70\uff01\u201d \u3000\u3000\u5ffd\u542c\u5c0f\u71d5\u5b50\u5931\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u770b\u90a3\u8fb9\u8fc7\u6765\u4e00\u4eba\uff0c\u5f88\u50cf\u8001\u5996\u5a46\uff01\u201d \u3000\u3000\u767d\u7fbd\u987a\u7740\u5c0f\u71d5\u5b50\u624b\u6307\u65b9\u5411\u770b\u53bb\u2014\u2014\u53ea\u89c1\u4e8c\u4e09\u5341\u4e08\u5916\u7684\u5c71\u5761\u5c0f\u8def\u4e0a\uff0c\u6b63\u6709\u4e00\u4eba\u5feb\u6b65\u5411\u8fd9\u8fb9 \u5954\u6765\uff0c\u8eab\u5f62\u4e3e\u6b62\u679c\u7136\u548c\u8001\u5996\u5a46\u53f2\u5999\u79cb\u5341\u5206\u76f8\u4f3c\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u6025\u6025\u518d\u9053\uff1a\u201c\u54b1\u4eec\u8d76\u7d27\u65bd\u5c55\u8f7b\u529f\u8fce\u4e0a\u524d\u53bb\uff01\u201d \u3000\u3000\u82e5\u8bba\u6c5f\u6e56\u7ecf\u9a8c\u9605\u5386\uff0c\u5c0f\u9053\u58eb\u81ea\u7136\u80dc\u8fc7\u5c0f\u71d5\u5b50\u3002 \u3000\u3000\u4ed6\u8fde\u5fd9\u62c9\u8d77\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u8def\u65c1\u4e0d\u8fdc\u6709\u5757\u5927\u77f3\uff0c\u54b1\u4eec\u5feb\u8eb2\u5230\u5927\u77f3\u540e\u9762\u53bb\u3002\u201d \u3000\u3000\u4e24\u4eba\u5f88\u5feb\u4fbf\u85cf\u8eab\u5728\u5927\u77f3\u4e4b\u540e\u3002 \u3000\u3000\u5c0f\u9053\u58eb\u767d\u7fbd\u8fd9\u624d\u89e3\u91ca\u9053\uff1a\u201c\u8001\u5996\u5a46\u8d70\u7684\u90a3\u6761\u5c0f\u8def\uff0c\u6b63\u662f\u5f80\u54b1\u4eec\u8fd9\u8fb9\u6765\u7684\uff0c\u54b1\u4eec\u5728\u5979\u8eab\u540e \u8ddf\u8e2a\uff0c\u624d\u662f\u4e0a\u7b56\u3002\u5982\u679c\u73b0\u5728\u5c31\u6349\u4f4f\u5979\uff0c\u4e07\u4e00\u5979\u575a\u4e0d\u5410\u5b9e\uff0c\u6216\u662f\u54ac\u820c\u81ea\u5c3d\uff0c\u90a3\u771f\u7684\u662f\u5f04\u5de7\u6210\u62d9 \u4e86\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u70b9\u70b9\u5934\u9053\uff1a\u201c\u6709\u9053\u7406\uff0c\u54b1\u4eec\u5c31\u7b49\u7740\u5979\u3002\u201d \u3000\u3000\u5c71\u5761\u4e0a\u90a3\u4eba\u5954\u8d70\u5f97\u5341\u5206\u5feb\u6377\uff0c\u7247\u523b\u4e4b\u540e\u4fbf\u5df2\u5230\u8fbe\u8ddf\u524d\u3002 \u3000\u3000\u4e00\u70b9\u4e0d\u9519\uff0c\u6b63\u662f\u8001\u5996\u5a46\u3002 \u3000\u3000\u5c82\u77e5\u8001\u5996\u5a46\u5728\u8ddd\u5927\u77f3\u4e0d\u8fdc\u5904\u7684\u8def\u4e0a\uff0c\u4e0d\u4f46\u81ea\u52a8\u505c\u4e86\u4e0b\u6765\uff0c\u800c\u4e14\u5728\u7565\u4e00\u89c2\u671b\u4e4b\u540e\uff0c\u7adf\u5411\u5927 \u77f3\u8d70\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u5979\u8fb9\u8d70\u8fb9\u9053\uff1a\u201c\u5927\u77f3\u540e\u9762\u662f\u4e0d\u662f\u71d5\u5c0f\u5144\u5f1f\u548c\u5c0f\u9053\u58eb\uff1f\u4f60\u4eec\u600e\u4e48\u8eb2\u8d77\u6765\u4e86\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u5927\u611f\u5403\u60ca\uff0c\u51e0\u4e4e\u662f\u6014\u5728\u5f53\u573a\u3002 \u3000\u3000\u8001\u5996\u5a46\u65e2\u5df2\u770b\u51fa\u662f\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u8dd1\uff1f\u4e3a\u4ec0\u4e48\u81ea\u52a8\u9001\u4e0a\u95e8\u6765\uff1f\u83ab\u975e\u5176\u4e2d\u53e6\u6709\u539f \u56e0\uff1f\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u5df2\u6765\u4e0d\u53ca\u591a\u60f3\u4ec0\u4e48\uff0c\u7acb\u5373\u7531\u5927\u77f3\u540e\u73b0\u8eab\u800c\u51fa\u3002 \u3000\u3000\u8001\u5996\u5a46\u559c\u5f62\u4e8e\u8272\u9053\uff1a\u201c\u679c\u7136\u662f\u4f60\u4eec\uff0c\u53ef\u89c1\u8001\u8eab\u7684\u773c\u529b\u4e0d\u5dee\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u4e0d\u89e3\u7684\u9053\uff1a\u201c\u8001\u5996\u5a46\uff0c\u5230\u5e95\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u201c\u8001\u8eab\u660e\u767d\u4f60\u4eec\u6025\u4e8e\u77e5\u9053\u539f\u56e0\uff0c\u8fd9\u5730\u65b9\u4e0d\u5b89\u5168\uff0c\u54b1\u4eec\u627e\u4e2a\u9690\u5bc6\u5730\u65b9\u518d\u8c08\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u4e0d\u5b89\u5168\uff1f\u201d \u3000\u3000\u201c\u6709\u4eba\u8981\u8ffd\u6740\u8005\u8eab\u3002\u201d \u3000\u3000\u201c\u8c01\u8ffd\u6740\u4f60\uff1f\u6709\u6211\u548c\u5c0f\u9053\u58eb\u5728\uff0c\u4f60\u8fd8\u6709\u4ec0\u4e48\u53ef\u6015\u7684\uff1f\u201d \u3000\u3000\u201c\u4f60\u4eec\u56fa\u7136\u53ef\u4ee5\u4fdd\u62a4\u8001\u8eab\uff0c\u4f46\u8001\u8eab\u62c5\u5fc3\u56e0\u800c\u574f\u4e86\u4f60\u4eec\u7684\u5927\u4e8b\u3002\u201d \u3000\u3000\u201c\u8fd9\u8bdd\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u8001\u5996\u5a46\u56de\u8eab\u671b\u4e86\u4e00\u773c\uff0c\u9053\uff1a\u201c\u6765\u4e0d\u53ca\u8bf4\u660e\u539f\u56e0\u4e86\uff0c\u5feb\u5feb\u627e\u5904\u9690\u5bc6\u5730\u65b9\u85cf\u7740\uff0c\u8001\u8eab\u4f1a\u6162\u6162 \u544a\u8bc9\u4f60\u4eec\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u53ea\u597d\u628a\u8001\u5996\u5a46\u5e26\u5230\u8fdc\u5904\u4e00\u7247\u8302\u5bc6\u7684\u6811\u6797\u91cc\uff0c\u7136\u540e\u4e09\u4eba\u4e00\u8fb9\u4e24\u4e2a\u3001\u4e00\u8fb9\u4e00\u4e2a \u5750\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u50ac\u4fc3\u7740\u9053\uff1a\u201c\u6211\u548c\u5c0f\u9053\u58eb\u5df2\u7ecf\u88ab\u4f60\u5b8c\u5168\u5f04\u7cca\u6d82\u4e86\uff0c\u6709\u8bdd\u5c31\u5feb\u5feb\u8bf4\uff01\u201d \u3000\u3000\u8001\u5996\u5a46\u8f7b\u54b3\u4e86\u51e0\u58f0\uff0c\u73b0\u51fa\u4e00\u8138\u5c34\u5c2c\u4e4b\u8272\u9053\uff1a\u201c\u8001\u8eab\u60ed\u6127\uff0c\u505a\u9519\u4e86\u4e00\u4ef6\u4e8b\uff0c\u5b9e\u5728\u5bf9\u4e0d\u4f4f\u738b \u5927\u4eba\u548c\u6c5f\u5927\u4fa0\uff0c\u5f85\u4f1a\u513f\u548c\u4ed6\u4eec\u89c1\u4e86\u9762\uff0c\u771f\u4e0d\u77e5\u9053\u4ed6\u4eec\u80af\u4e0d\u80af\u539f\u8c05\u6211\u3002\u201d \u3000\u3000\u201c\u5148\u522b\u8bf4\u8fd9\u4e9b\u6211\u4eec\u542c\u4e0d\u61c2\u7684\u8bdd\uff0c\u544a\u8bc9\u6211\u4eec\u90a3\u5929\u4f60\u662f\u600e\u4e48\u88ab\u4ed6\u4eec\u6349\u8d70\u7684\uff1f\u4e3a\u4ec0\u4e48\u4f60\u88ab\u6349\u65f6 \u4e5f\u4e0d\u558a\u53eb\u4e00\u58f0\uff1f\u4e5f\u597d\u8ba9\u6211\u4eec\u628a\u4f60\u6551\u56de\u6765\u3002\u201d \u3000\u3000\u201c\u8fd9\u6b63\u662f\u8001\u8eab\u6700\u503c\u5f97\u60ed\u6127\u7684\u4e8b\uff0c\u8001\u8eab\u662f\u6709\u610f\u9003\u8d70\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u65e2\u7136\u9003\u8d70\u4e86\uff0c\u4e3a\u4ec0\u4e48\u53c8\u81ea\u52a8\u6295\u5230\u6211\u4eec\u624b\u4e0a\uff1f\u8fd9\u5c31\u5b9e\u5728\u4ee4\u4eba\u4e0d\u89e3\u4e86\uff1f\u201d \u3000\u3000\u201c\u8001\u8eab\u8fd8\u6ca1\u8bf4\u6e05\u695a\uff0c\u4f60\u4eec\u5f53\u7136\u542c\u4e0d\u61c2\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u5feb\u4e9b\u8bf4\u6e05\u695a\uff01\u201d \u3000\u3000\u201c\u8001\u8eab\u539f\u662f\u897f\u57df\u6765\u7684\uff0c\u4e0d\u7ba1\u897f\u57df\u80fd\u5426\u5bb9\u5f97\u4e0b\uff0c\u603b\u662f\u5e0c\u671b\u56de\u5230\u897f\u57df\u53bb\uff0c\u56e0\u60f3\u5230\u56f4\u653b\u9053\u89c2\u90a3 \u4f19\u4eba\u53ef\u80fd\u662f\u9a6c\u6587\u4e2d\u7684\u624b\u4e0b\uff0c\u6240\u4ee5\u5c31\u8d81\u673a\u6295\u5954\u8fc7\u53bb\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u4e3a\u4ec0\u4e48\u53c8\u8981\u56de\u5230\u6211\u4eec\u8fd9\u8fb9\u6765\uff1f\u201d \u3000\u3000\u201c\u8001\u8eab\u662f\u88ab\u903c\u7684\u3002\u201d \u3000\u3000\u201c\u88ab\u4ec0\u4e48\u4eba\u903c\u7684\uff1f\u201d \u3000\u3000\u201c\u8001\u8eab\u968f\u4ed6\u4eec\u6765\u5230\u9099\u5c71\uff0c\u539f\u610f\u662f\u60f3\u968f\u5e08\u7236\u6842\u98d8\u9999\u4e00\u8d77\u56de\u897f\u57df\uff0c\u504f\u504f\u6628\u665a\u542c\u5230\u4e00\u4e2a\u4ee4\u8001\u8eab \u9707\u60ca\u7684\u6d88\u606f\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u6d88\u606f\uff1f\u201d \u3000\u3000\u201c\u5f53\u65f6\u8001\u8eab\u662f\u4ece\u7761\u68a6\u4e2d\u9192\u6765\uff0c\u542c\u5230\u9694\u58c1\u6709\u4eba\u5728\u6084\u6084\u8bb2\u8bdd\u3002\u201d \u3000\u3000\u201c\u8bb2\u8bdd\u7684\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u6842\u98d8\u9999\u3002\u201d \u3000\u3000\u201c\u8bb2\u7684\u4ec0\u4e48\u8bdd\uff1f\u201d \u3000\u3000\u201c\u5979\u662f\u5728\u5bf9\u53e6\u5916\u4e24\u4e2a\u5973\u7684\u8bb2\uff0c\u5979\u8bf4\u5230\u8fbe\u897f\u57df\u540e\uff0c\u8981\u7980\u62a5\u6559\u4e3b\u6cbb\u6211\u7684\u7f6a\u3002\u201d \u3000\u3000\u201c\u4f60\u5bf9\u5979\u4eec\u8be5\u662f\u6709\u529f\uff0c\u54ea\u91cc\u6709\u7f6a\uff1f\u201d \u3000\u3000\u201c\u5979\u4eec\u5df2\u53d1\u73b0\u90a3\u5929\u662f\u6211\u966a\u4f60\u5230\u2018\u8fce\u6625\u9601\u2019\u53bb\u7684\uff0c\u800c\u4e14\u4f60\u95ef\u5230\u6842\u98d8\u9999\u623f\u91cc\u53bb\uff0c\u5979\u4eec\u8ba4\u5b9a\u662f \u6211\u6cc4\u7684\u5bc6\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u9ed8\u4e86\u4e00\u9ed8\u9053\uff1a\u201c\u5982\u679c\u897f\u57df\u9b54\u6559\u771f\u8981\u6cbb\u4f60\u7684\u7f6a\uff0c\u4f60\u4f1a\u53d7\u5230\u4ec0\u4e48\u60e9\u7f5a\uff1f\u201d \u3000\u3000\u8001\u5996\u5a46\u60c5\u4e0d\u81ea\u7981\u6253\u4e86\u4e2a\u5bd2\u98a4\uff0c\u9053\uff1a\u201c\u6211\u7684\u7f6a\u540d\u9664\u4e86\u901a\u654c\u4e4b\u5916\uff0c\u66f4\u6d89\u53ca\u51fa\u5356\u6559\u4e2d\u673a\u5bc6\u3002\u201d \u3000\u3000\u201c\u5904\u7f5a\u5f88\u91cd\u5417\uff1f\u201d \u3000\u3000\u201c\u9b54\u6559\u7684\u6559\u89c4\u4e25\u82db\u65e0\u6bd4\uff0c\u7eb5\u7136\u4e0d\u6b7b\uff0c\u4e5f\u5fc5\u88ab\u6253\u6210\u6b8b\u5e9f\uff0c\u7136\u540e\u518d\u843d\u4e2a\u7ec8\u751f\u76d1\u7981\uff0c\u90a3\u79cd\u9177\u5211 \u4e0d\u662f\u4eba\u80fd\u53d7\u5f97\u4e86\u7684\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u4f60\u624d\u8981\u79c1\u81ea\u6f5c\u9003\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\u3002\u5f53\u8001\u8eab\u8fd9\u6b21\u518d\u56de\u5230\u738b\u5927\u4eba\u548c\u6c5f\u5927\u4fa0\u90a3\u91cc\u540e\uff0c\u4f60\u4eec\u5c31\u53ef\u4ee5\u6c38\u8fdc\u653e\u5fc3\u6211\u4e86\uff0c\u5373\u4f7f\u6253 \u6b7b\u6211\uff0c\u6211\u4e5f\u4e0d\u4f1a\u518d\u8d70\uff0c\u4e5f\u4e0d\u4f1a\u518d\u6709\u4e8c\u5fc3\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u671b\u671b\u767d\u7fbd\u9053\uff1a\u201c\u600e\u4e48\u6837\uff1f\u662f\u4e0d\u662f\u8981\u628a\u5979\u5e26\u56de\u53bb\uff1f\u201d \u3000\u3000\u767d\u7fbd\u4f3c\u4e4e\u4e5f\u76f8\u4fe1\u4e86\u8001\u5996\u5a46\u7684\u8bdd\uff0c\u7565\u4e00\u6c89\u5fd6\u9053\uff1a\u201c\u54b1\u4eec\u5148\u529e\u54b1\u4eec\u7684\u4e8b\u8981\u7d27\uff0c\u5c31\u8ba9\u5979\u81ea\u5df1\u5230 \u5ba2\u6808\u53bb\u627e\u738b\u5927\u4eba\u548c\u6c5f\u5927\u4fa0\u5427\uff01\u201d \u3000\u3000\u8001\u5996\u5a46\u6025\u6025\u6447\u5934\u9053\uff1a\u201c\u4e0d\u884c\uff0c\u4e00\u5b9a\u8981\u7531\u4f60\u4eec\u4fdd\u62a4\u7740\u8001\u8eab\uff0c\u5148\u5230\u5ba2\u6808\u53bb\u3002\u201d \u3000\u3000\u767d\u7fbd\u6709\u4e9b\u4e0d\u89e3\u7684\u9053\uff1a\u201c\u6211\u4eec\u544a\u8bc9\u4f60\u5ba2\u6808\u5b57\u53f7\u548c\u5730\u70b9\uff0c\u4f60\u81ea\u5df1\u53bb\u6709\u4ec0\u4e48\u4e0d\u597d\uff1f\u201d \u3000\u3000\u8001\u5996\u5a46\u53c8\u6447\u5934\u9053\uff1a\u201c\u53ef\u662f\u8def\u4e0a\u8c01\u4fdd\u62a4\u8001\u8eab\u7684\u5b89\u5168\uff1f\u201d \u3000\u3000\u201c\u96be\u9053\u8def\u4e0a\u4f1a\u6709\u4eba\u8ffd\u6740\u4f60\uff1f\u201d \u3000\u3000\u201c\u4e00\u5b9a\u6709\uff0c\u5f53\u5979\u4eec\u53d1\u73b0\u8001\u8eab\u4e0d\u89c1\u4e86\uff0c\u5f53\u7136\u4f1a\u9a6c\u4e0a\u6d3e\u4eba\u8ffd\u6355\uff0c\u5982\u679c\u8001\u8eab\u6240\u6599\u4e0d\u5dee\uff0c\u8ffd\u6355\u7684 \u4eba\u9a6c\u4e0a\u5c31\u5230\u4e86\u3002\u201d \u3000\u3000\u201c\u4f1a\u6709\u4ec0\u4e48\u4eba\u8ffd\u6355\u4f60\uff1f\u201d \u3000\u3000\u201c\u4e5f\u8bb8\u662f\u6842\u98d8\u9999\u548c\u53e6\u5916\u4e24\u4e2a\u5973\u7684\uff0c\u4e5f\u8bb8\u662f\u9c7c\u5316\u9f99\u7684\u624b\u4e0b\u9ad8\u624b\u3002\u201d \u3000\u3000\u767d\u7fbd\u5fc3\u5934\u4e00\u52a8\uff0c\u5fd9\u9053\uff1a\u201c\u5c0f\u71d5\u5b50\uff0c\u54b1\u4eec\u7684\u673a\u4f1a\u6765\u4e86\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u4ec0\u4e48\u673a\u4f1a\uff1f\u201d \u3000\u3000\u767d\u7fbd\u81ea\u4fe1\u5341\u8db3\u7684\u9053\uff1a\u201c\u628a\u90a3\u4e09\u4e2a\u5973\u7684\u2018\u751f\u64d2\u6d3b\u6349\u2019\u7684\u673a\u4f1a\uff0c\u82e5\u5979\u4eec\u6765\u8ffd\u8d76\uff0c\u8d70\u7684\u5fc5\u5b9a\u4e5f \u662f\u8fd9\u6761\u8def\uff0c\u54b1\u4eec\u5c31\u5728\u8fd9\u91cc\u5b88\u682a\u5f85\u5154\uff0c\u7b49\u5979\u4eec\u5230\u8fbe\u540e\uff0c\u5c06\u5979\u4eec\u4e00\u4e3e\u64d2\u83b7\uff0c\u56de\u53bb\u540e\uff0c\u79f0\u5f97k\u662f\u573a \u4e86\u4e0d\u8d77\u7684\u5927\u529f\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u81f3\u8868\u8d5e\u540c\u9053\uff1a\u201c\u5bf9\uff0c\u7684\u786e\u662f\u4e2a\u597d\u529e\u6cd5\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u53c8\u5411\u8001\u5996\u5a46\u9053\uff1a\u201c\u8001\u5996\u5a46\uff0c\u5f85\u4f1a\u513f\u6211\u4eec\u6349\u4f4f\u4e86\u90a3\u4e09\u4e2a\u5973\u7684\uff0c\u4f60\u5c31\u6c38\u65e0\u540e\u987e\u4e4b\u5fe7 \u4e86\u3002\u201d \u3000\u3000\u8001\u5996\u5a46\u9f87\u7259\u54a7\u5634\u7684\u9053\uff1a\u201c\u8001\u8eab\u73b0\u5df2\u51b3\u5b9a\u5f03\u6697\u6295\u660e\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u53eb\u6211\u8001\u5996\u5a46\uff1f\u201d \u3000\u3000\u201c\u4f60\u672c\u6765\u5c31\u662f\u8001\u5996\u5a46\uff0c\u4e0d\u8fd9\u4e48\u53eb\uff0c\u79f0\u547c\u4f60\u4ec0\u4e48\u597d\u5462\uff1f\u201d \u3000\u3000\u201c\u53eb\u6211\u4e00\u58f0\u4f2f\u6bcd\uff0c\u4e0d\u662f\u5f88\u597d\u5417\uff1f\u201d \u3000\u3000\u201c\u53bb\u4f60\u7684\uff0c\u6211\u5c0f\u71d5\u5b50\u4e0d\u60f3\u8ddf\u4f60\u62c9\u5173\u7cfb\u3002\u201d \u3000\u3000\u201c\u5c31\u7167\u4e0a\u6b21\u5728\u2018\u8fce\u6625\u9601\u2019\u4f60\u5bf9\u6211\u7684\u79f0\u547c\u4e5f\u884c\u3002\u201d \u3000\u3000\u201c\u90a3\u6b21\u4f60\u662f\u626e\u6210\u4e00\u540d\u7cdf\u8001\u5934\u5b50\uff0c\u6211\u624d\u79f0\u547c\u4f60\u53f2\u8001\uff0c\u73b0\u5728\u4f60\u73b0\u51fa\u539f\u5f62\u4e86\uff0c\u8fd8\u5145\u7684\u4ec0\u4e48\u53f2\u8001 \u5462\uff1f\u201d \u3000\u3000\u201c\u597d\u5566\uff01\u968f\u4fbf\u4f60\u53eb\uff0c\u53cd\u6b63\u8001\u8eab\u73b0\u5728\u662f\u843d\u96be\u4e4b\u4eba\uff0c\u6ca1\u8d44\u683c\u548c\u4f60\u4eec\u8ba1\u8f83\u3002\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u4e09\u6761\u4eba\u5f71\u6709\u5982\u661f\u98de\u4e38\u6cfb\u822c\u7531\u5c71\u8170\u76f4\u5411\u8fd9\u8fb9\u800c\u6765\u3002 \u3000\u3000\u8001\u5996\u5a46\u7acb\u523b\u8138\u8272\u5927\u53d8\u9053\uff1a\u201c\u5979\u2026\u2026\u5979\u4eec\u2026\u2026\u771f\u7684\u6765\u4e86\uff01\u201d \u3000\u3000\u8fd9\u65f6\u90a3\u4e09\u6761\u4eba\u5f71\u76f8\u9694\u81f3\u5c11\u5728\u534a\u91cc\u4e4b\u5916\uff0c\u770b\u6765\u53ea\u662f\u4e09\u4e2a\u9ed1\u70b9\u3002 \u3000\u3000\u8001\u5996\u5a46\u8ba4\u5b9a\u5bf9\u65b9\u662f\u6842\u98d8\u9999\u4e09\u4eba\uff0c\u5b9e\u9645\u4e0a\u53ea\u662f\u51ed\u60f3\u8c61\u800c\u5df2\u3002 \u3000\u3000\u4e09\u6761\u4eba\u5f71\u6e10\u6765\u6e10\u8fd1\uff0c\u5df2\u53ef\u770b\u51fa\u662f\u4e09\u540d\u9ed1\u8863\u4eba\uff0c\u800c\u4e14\u5168\u662f\u7537\u7684\u3002 \u3000\u3000\u8001\u5996\u5a46\u5fd9\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u9c7c\u5316\u9f99\u7684\u624b\u4e0b\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u5fc5\u5b9a\u662f\u8ffd\u8d76\u4f60\u7684\u4e86\uff01\u201d \u3000\u3000\u201c\u5f53\u7136\u662f\u8ffd\u6355\u8001\u8eab\u7684\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u7684\u8eab\u624b\u2026\u2026\u201d \u3000\u3000\u201c\u4ed6\u4eec\u65e9\u77e5\u8001\u8eab\u4e5f\u6709\u4e00\u8eab\u6b66\u529f\uff0c\u6d3e\u6765\u7684\u5f53\u7136\u662f\u9ad8\u624b\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u8001\u5996\u5a46\uff0c\u4f60\u5c31\u8eb2\u5728\u5927\u77f3\u540e\u9762\uff0c\u6211\u548c\u5c0f\u9053\u58eb\u51fa\u53bb\u4f1a\u4f1a\u4ed6\u4eec\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u4f60\u4eec\u8981\u4e0e\u4ed6\u4eec\u6b63\u9762\u63a5\u89e6\uff0c\u5c31\u5fc5\u987b\u628a\u4ed6\u4eec\u4e00\u7f51\u6253\u5c3d\uff0c\u7edd\u4e0d\u80fd\u8ba9\u4ed6\u4eec\u9003\u8131\u4e00\u4e2a\uff0c\u82e5\u7559 \u4e0b\u6d3b\u53e3\uff0c\u4ee5\u540e\u7684\u4e8b\u5c31\u96be\u529e\u4e86\u3002\u201d \u3000\u3000\u201c\u7528\u4e0d\u7740\u4ea4\u4ee3\uff0c\u6211\u4eec\u672c\u6765\u5c31\u4e0d\u6253\u7b97\u8ba9\u4ed6\u4eec\u7559\u4e0b\u6d3b\u53e3\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u5c0f\u71d5\u5b50\u548c\u5c0f\u9053\u58eb\u767d\u7fbd\u4fbf\u5df2\u8f6c\u51fa\u5927\u77f3\uff0c\u6765\u5230\u8def\u65c1\u3002 \u3000\u3000\u4e09\u540d\u9ed1\u8863\u4eba\u5f88\u5feb\u4fbf\u5954\u884c\u5230\u8ddf\u524d\uff0c\u5e76\u540c\u65f6\u505c\u4e0b\u811a\u6b65\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u53ea\u662f\u7ad9\u5728\u539f\u5730\uff0c\u5e76\u672a\u5f00\u53e3\u8bf4\u8bdd\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u5e76\u4e0d\u60f3\u4e00\u89c1\u9762\u5c31\u6740\uff0c\u800c\u662f\u5e0c\u671b\u5148\u548c\u5bf9\u65b9\u6577\u884d\u4e00\u9635\uff0c\u8bf4\u4e0d\u5b9a\u80fd\u4ece\u5bf9\u65b9\u53e3\u4e2d\u63a2\u542c\u51fa \u4e00\u4e9b\u60f3\u77e5\u9053\u7684\u4e8b\u3002 \u3000\u3000\u4e3a\u9996\u7684\u4e00\u540d\u9ed1\u8863\u4eba\u95ee\u9053\uff1a\u201c\u4e24\u4f4d\u662f\u505a\u4ec0\u4e48\u7684\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u6e38\u5c71\u73a9\u6c34\u3002\u201d \u3000\u3000\u201c\u53ef\u66fe\u89c1\u5230\u4e00\u4e2a\u53c8\u8001\u53c8\u4e11\u7684\u5973\u4eba\u7ecf\u8fc7\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u201c\u770b\u662f\u770b\u5230\u4e00\u4e2a\uff0c\u4f46\u90a3\u5973\u4eba\u65e2\u4e0d\u8001\u53c8\u4e0d\u4e11\uff0c\u800c\u4e14\u8fd8\u597d\u770b\u5f97\u5f88\u3002\u201d \u3000\u3000\u4e3a\u9996\u7684\u9ed1\u8863\u4eba\u56de\u671b\u4e86\u53e6\u4e00\u540d\u53f3\u988a\u4e0a\u6709\u6761\u5200\u75a4\u7684\u6c49\u5b50\uff0c\u9053\uff1a\u201c\u5947\u602a\uff1f\u5979\u600e\u4e48\u4f1a\u5ffd\u7136\u53d8\u5f97\u597d \u770b\u8d77\u6765\u4e86\uff1f\u201d \u3000\u3000\u90a3\u5200\u75a4\u6c49\u5b50\u9053\uff1a\u201c\u542c\u8bf4\u5979\u7684\u6613\u5bb9\u672f\u5f88\u9ad8\u660e\u3002\u201d \u3000\u3000\u4e3a\u9996\u7684\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u50cf\u5979\u90a3\u79cd\u8001\u5996\u602a\uff0c\u518d\u600e\u4e48\u6613\u5bb9\u4e5f\u597d\u770b\u4e0d\u8d77\u6765\uff0c\u4e00\u5b9a\u4e0d\u662f\u5979\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u642d\u8baa\u7740\u9053\uff1a\u201c\u4f60\u4eec\u4e09\u4f4d\u662f\u4ece\u54ea\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u4e3a\u9996\u7684\u9ed1\u8863\u4eba\u4e24\u773c\u4e00\u77aa\u9053\uff1a\u201c\u4f60\u95ee\u8fd9\u4e9b\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u8981\u627e\u4e00\u4e2a\u53c8\u8001\u53c8\u4e11\u7684\u5973\u4eba\uff0c\u4e00\u5b9a\u662f\u4f60\u4eec\u7684\u5976\u5976\u5931\u8e2a\u4e86\u5427\uff1f\u201d \u3000\u3000\u4e3a\u9996\u7684\u9ed1\u8863\u4eba\u5446\u4e86\u4e00\u5446\uff0c\u7acb\u5373\u6c89\u58f0\u5927\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u5c45\u7136\u6562\u62ff\u8001\u5b50\u5f00\u5fc3\uff0c\u8001\u5b50\u5c31\u5148\u5bb0 \u4e86\u4f60\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u95f4\uff0c\u53f3\u624b\u4e00\u63a2\uff0c\u957f\u5200\u51fa\u9798\uff0c\u731b\u5411\u5c0f\u71d5\u5b50\u5f53\u5934\u5288\u6765\u3002 \u3000\u3000\u6b64\u4eba\u663e\u7136\u8eab\u624b\u9887\u9ad8\uff0c\u51fa\u62db\u4e4b\u5feb\u6709\u5982\u7535\u5149\u77f3\u706b\uff0c\u4ee4\u4eba\u76ee\u4e0d\u6687\u63a5\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u5e76\u672a\u8fd8\u51fb\uff0c\u5f53\u4e0b\u98d8\u8eab\u4e09\u5c3a\uff0c\u8ba9\u5f00\u4e86\u5bf9\u65b9\u4e00\u62db\u3002 \u3000\u3000\u4ed6\u7b11\u7740\u9053\uff1a\u201c\u8001\u5144\u5e72\u561b\u8fd9\u4e48\u51b2\u52a8\uff1f\u4e09\u53e5\u8bdd\u6ca1\u8bf4\u5b8c\u5c31\u52a8\u624b\u52a8\u811a\uff01\u201d \u3000\u3000\u4e3a\u9996\u7684\u9ed1\u8863\u4eba\u54ea\u80af\u7b54\u8bdd\uff0c\u7eb5\u8eab\u8ddf\u8fdb\uff0c\u53c8\u662f\u5947\u5feb\u65e0\u6bd4\u7684\u4e00\u5200\u780d\u4e0b\u3002 \u3000\u3000\u8fd9\u6b21\u5bf9\u65b9\u51fa\u62db\u7684\u624b\u6cd5\u66f4\u91cd\uff0c\u5200\u950b\u8fc7\u5904\uff0c\u9510\u98ce\u76c8\u8033\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u4f9d\u7136\u672a\u8fd8\u624b\uff0c\u90a3\u9ed1\u8863\u4eba\u5200\u52bf\u518d\u731b\uff0c\u5374\u59cb\u7ec8\u4f24\u4e0d\u5230\u4ed6\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u5230\u8fd9\u65f6\u624d\u89c9\u51fa\u5bf9\u65b9\u4e0d\u662f\u7b80\u5355\u4eba\u7269\uff0c\u540c\u65f6\u4e5f\u8d8a\u53d1\u6fc0\u8d77\u4e86\u4ed6\u7684\u6012\u706b\uff0c\u624b\u4e2d\u957f\u5200\u5de6\u780d\u53f3 \u5288\uff0c\u6709\u5982\u6cfc\u98ce\u822c\u5c55\u5f00\u4e86\u731b\u653b\u3002 \u3000\u3000\u4f46\u662f\u5c0f\u71d5\u5b50\u5374\u5982\u6d6e\u5f71\u98d8\u98ce\u822c\uff0c\u4e5f\u968f\u7740\u5bf9\u65b9\u5200\u52bf\u95ea\u8dc3\uff0c\u9ed1\u8863\u4eba\u51fa\u62db\u518d\u5feb\u518d\u731b\uff0c\u3002\u4e5f\u534a\u70b9\u4f24 \u4e0d\u5230\u4ed6\u3002 \u3000\u3000\u53e6\u4e24\u540d\u6c49\u5b50\u4e00\u89c1\u9ed1\u8863\u4eba\u50cf\u662f\u88ab\u800d\u4e86\u7684\u72d7\u718a\uff0c\u7acb\u5373\u4e5f\u4eae\u51fa\u957f\u5200\uff0c\u51c6\u5907\u5c55\u5f00\u52a9\u653b\u3002 \u3000\u3000\u5c0f\u9053\u58eb\u767d\u7fbd\u5c82\u80fd\u8896\u624b\u65c1\u89c2\uff0c\u957f\u5251\u4e00\u95ea\uff0c\u4fbf\u628a\u4e24\u540d\u6c49\u5b50\u963b\u622a\u56de\u53bb\u3002 \u3000\u3000\u8fd9\u4e24\u540d\u6c49\u5b50\u4e5f\u975e\u5f31\u624b\uff0c\u5f53\u5373\u53cc\u5200\u9f50\u6325\uff0c\u5411\u5c0f\u9053\u58eb\u5c55\u5f00\u5939\u653b\u3002 \u3000\u3000\u5c0f\u9053\u58eb\u767d\u7fbd\u548c\u5c0f\u71d5\u5b50\u4e0d\u4e00\u6837\uff0c\u4e00\u51fa\u624b\u5c31\u73a9\u771f\u7684\u3002 \u3000\u3000\u5927\u7ea6\u5341\u51e0\u4e2a\u7167\u9762\u4e4b\u540e\uff0c\u4e24\u540d\u6c49\u5b50\uff0c\u4e00\u4e2a\u662f\u88ab\u957f\u5251\u523a\u7a7f\u524d\u80f8\uff0c\u4e00\u4e2a\u5219\u8111\u888b\u76f4\u98de\u51fa\u4e03\u516b\u5c3a \u8fdc\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u8fd9\u65f6\u4e5f\u628a\u90a3\u9ed1\u8863\u4eba\u70b9\u5012\u5728\u5730\u3002 \u3000\u3000\u4e24\u4eba\u5f88\u5feb\u4fbf\u628a\u9ed1\u8863\u4eba\u548c\u4e24\u540d\u6c49\u5b50\u7684\u5c38\u4f53\u62d6\u5230\u5927\u77f3\u4e4b\u540e\u3002 \u3000\u3000\u56e0\u4e24\u540d\u6c49\u5b50\u5168\u662f\u6bd9\u547d\u5728\u8def\u65c1\u7684\u6742\u8349\u4e2d\uff0c\u5730\u4e0a\u770b\u4e0d\u51fa\u4ec0\u4e48\u8840\u8ff9\uff0c\u7eb5\u7136\u6709\u8def\u4eba\u7ecf\u8fc7\uff0c\u53ea\u8981\u672a \u53d1\u73b0\u6709\u4eba\u6253\u6597\uff0c\u4fbf\u4e0d\u53ef\u80fd\u5f97\u77e5\u5df2\u53d1\u751f\u8fc7\u4eba\u547d\u3002 \u3000\u3000\u8001\u5996\u5a46\u65e9\u5728\u5927\u77f3\u540e\u770b\u4e86\u4e2a\u4e00\u6e05\u4e8c\u695a\u3002 \u3000\u3000\u53ea\u542c\u5979\u563f\u563f\u7b11\u9053\uff1a\u201c\u4f9d\u8001\u8eab\u770b\uff0c\u4f60\u4eec\u4e24\u4e2a\u6700\u597d\u662f\u4e92\u8c03\u4e00\u4e0b\u4f4d\u7f6e\uff0c\u5c0f\u71d5\u5b50\u51fa\u5bb6\uff0c\u5c0f\u9053\u58eb\u8fd8 \u4fd7\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u76b1\u7740\u7709\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u8001\u5996\u5a46\u9053\uff1a\u201c\u4f60\u7684\u5fc3\u80a0\u592a\u6148\u60b2\uff0c\u8be5\u6740\u4e0d\u6740\uff0c\u5f53\u7136\u9002\u5408\u505a\u51fa\u5bb6\u4eba\uff1b\u4f60\u770b\u4eba\u5bb6\u5c0f\u9053\u58eb\uff0c\u51fa\u624b \u591a\u5e72\u8106\uff01\u201d \u3000\u3000\u201c\u6211\u6709\u6211\u7684\u7528\u610f\u3002\u201d \u3000\u3000\u201c\u4f60\u6709\u4ec0\u4e48\u7528\u610f\uff1f\u201d \u3000\u3000\u201c\u7559\u4e0b\u6d3b\u53e3\uff0c\u624d\u80fd\u95ee\u51fa\u4e00\u4e9b\u60f3\u77e5\u9053\u7684\u4e8b\u60c5\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u7531\u8001\u8eab\u66ff\u4f60\u95ee\u597d\u4e86\u3002\u201d \u3000\u3000\u8001\u5996\u5a46\u8bf4\u5b8c\u8bdd\uff0c\u8d70\u8fd1\u9ed1\u8863\u4eba\uff0c\u731b\u7136\u4e00\u638c\uff0c\u76f4\u5411\u9ed1\u8863\u4eba\u5934\u4e0a\u5288\u53bb\u3002 \u3000\u3000\u53ea\u542c\u201c\u7830\u201d\u7684\u4e00\u54cd\uff01 \u3000\u3000\u9ed1\u8863\u4eba\u987f\u65f6\u8111\u888b\u5f00\u82b1\uff0c\u8fde\u54fc\u90fd\u6ca1\u54fc\u5373\u5f53\u573a\u6bd9\u547d\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u5403\u4e86\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u6b7b\u8001\u5996\u5a46\uff0c\u8fd9\u7b97\u4ec0\u4e48\uff1f\u597d\u4e0d\u5bb9\u6613\u7559\u4e0b\u4e00\u4e2a\u6d3b\u53e3\uff0c\u4f60\u5374\u53c8\u8981\u4e86 \u4ed6\u7684\u547d\uff01\u201d \u3000\u3000\u8001\u5996\u5a46\u6c14\u54bb\u54bb\u7684\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u5c0f\u9b3c\u5934\uff0c\u539f\u6765\u4f60\u8fd8\u4e0d\u76f8\u4fe1\u8001\u8eab\uff01\u201d \u3000\u3000\u201c\u6b7b\u8005\u5996\u5a46\u4f60\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u201c\u6709\u4ec0\u4e48\u4e8b\uff0c\u95ee\u8001\u8eab\u8fd8\u4e0d\u4e00\u6837\uff0c\u4e3a\u4ec0\u4e48\u6709\u73b0\u6210\u7684\u81ea\u5df1\u4eba\u4e0d\u95ee\uff0c\u5374\u8981\u95ee\u522b\u4eba\uff1f\u8fd9\u5c31\u8868\u793a\u4f60 \u4e0d\u76f8\u4fe1\u8001\u8eab\uff0c\u4e0d\u4f46\u628a\u8001\u8eab\u770b\u6210\u5916\u4eba\uff0c\u751a\u81f3\u770b\u6210\u662f\u8bc8\u964d\u7684\uff0c\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6447\u6447\u5934\u9053\uff1a\u201c\u4f60\u8bef\u4f1a\u4e86\uff0c\u4ed6\u4eec\u662f\u9c7c\u5316\u9f99\u624b\u4e0b\u7684\uff0c\u60f3\u95ee\u9c7c\u5316\u9f99\u65b9\u9762\u7684\u4e8b\uff0c\u4ed6\u4eec\u5f53\u7136 \u77e5\u9053\u7684\u6bd4\u4f60\u591a\u3002\u201d \u3000\u3000\u8001\u5996\u5a46\u51b7\u7b11\u9053\uff1a\u201c\u4e0d\u89c1\u5f97\uff0c\u8001\u8eab\u548c\u4ed6\u4eec\u5728\u4e00\u8d77\u8fd9\u4e48\u591a\u5929\u4e86\uff0c\u4ec0\u4e48\u673a\u5bc6\u90fd\u77e5\u9053\uff0c\u5c24\u5176\u6842\u98d8 \u9999\u548c\u53e6\u5916\u4e24\u4e2a\u5973\u4eba\u7684\u4e8b\uff0c\u8001\u8eab\u66f4\u662f\u4e86\u5982\u6307\u638c\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u8038\u4e86\u8038\u80a9\uff0c\u4e0d\u518d\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u8001\u5996\u5a46\u671b\u4e86\u5730\u4e0a\u7684\u4e09\u5177\u5c38\u4f53\u4e00\u773c\uff0c\u9053\uff1a\u201c\u8fd8\u7b49\u4ec0\u4e48\uff0c\u5feb\u6316\u5751\u57cb\u4e86\u8981\u7d27\uff0c\u7b49\u4eba\u57cb\u597d\u4ee5\u540e\uff0c \u8001\u8eab\u5c31\u544a\u8bc9\u4f60\u4eec\u60f3\u77e5\u9053\u7684\u4e8b\u3002\u201d \u3000\u3000\u4e8e\u662f\uff0c\u4e09\u4eba\u901a\u529b\u5408\u4f5c\uff0c\u5728\u9644\u8fd1\u8349\u4e1b\u5185\u6316\u4e86\u4e00\u4e2a\u5927\u5751\uff0c\u5306\u5306\u628a\u4e09\u5177\u5c38\u4f53\u4e22\u8fdb\u57cb\u597d\u3002 \u3000\u3000\u7531\u4e8e\u5751\u6d1e\u662f\u5728\u8349\u4e1b\u5185\uff0c\u53ea\u8981\u4e0d\u8d70\u8fd1\uff0c\u8c01\u4e5f\u4e0d\u6613\u770b\u51fa\u5f02\u72b6\u3002 \u3000\u3000\u8001\u5996\u5a46\u9053\uff1a\u201c\u8fd9\u91cc\u79bb\u9053\u8def\u592a\u8fd1\uff0c\u54b1\u4eec\u53e6\u5916\u627e\u4e2a\u50fb\u9759\u4e4b\u5904\u8c08\u5427\uff01\u201d \u3000\u3000\u6b63\u597d\u79bb\u5927\u6811\u4e0d\u8fdc\u6709\u7247\u6811\u6797\uff0c\u679d\u53f6\u5341\u5206\u8302\u5bc6\uff0c\u4e09\u4eba\u4fbf\u8fdb\u5165\u6797\u5185\u5750\u4e0b\u3002 \u3000\u3000\u8fd8\u662f\u8001\u5996\u5a46\u9996\u5148\u8bf4\u9053\uff1a\u201c\u73b0\u5728\u4f60\u4eec\u53ea\u7ba1\u628a\u8001\u8eab\u5f53\u6210\u72af\u4eba\uff0c\u6765\u4e2a\u4e8c\u5802\u4f1a\u5ba1\uff0c\u7528\u4e0d\u7740\u5ba2\u6c14\uff0c \u9a6c\u4e0a\u5f00\u59cb\u5427\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u5148\u95ee\u9053\uff1a\u201c\u90a3\u4e09\u4e2a\u5973\u4eba\u76ee\u524d\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u8001\u8eab\u65e9\u77e5\u9053\u8fd9\u662f\u4f60\u4eec\u6700\u5173\u5207\u7684\u4e8b\uff0c\u5979\u4eec\u76ee\u524d\u90fd\u88ab\u62db\u5f85\u5728\u5c71\u5be8\u91cc\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u51c6\u5907\u4ec0\u4e48\u65f6\u5019\u79bb\u5f00\u9099\u5c71\uff1f\u201d \u3000\u3000\u201c\u81f3\u5c11\u8fd8\u8981\u597d\u51e0\u5929\u3002\u201d \u3000\u3000\u201c\u5c71\u5be8\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u8001\u8eab\u5c31\u662f\u4ece\u5c71\u5be8\u9003\u51fa\u6765\u7684\uff0c\u4e0d\u8fc7\u73b0\u5728\u4e0d\u80fd\u5e26\u4f60\u4eec\u53bb\u770b\uff0c\u800c\u4e14\u4f60\u4eec\u73b0\u5728\u4e5f\u4e0d\u5fc5\u53bb\uff0c\u514d\u5f97 \u6253\u8349\u60ca\u86c7\u3002\u201d \u3000\u3000\u201c\u9c7c\u5316\u9f99\u5728\u5c71\u5be8\u5417\uff1f\u201d \u3000\u3000\u201c\u6ca1\u89c1\u5230\u3002\u201d \u3000\u3000\u201c\u90a3\u662f\u5728\u9ec4\u6cb3\u5357\u5cb8\u7684\u6c34\u5be8\u4e86\uff1f\u201d \u3000\u3000\u201c\u4f9d\u8001\u8eab\u5224\u65ad\uff0c\u4ed6\u53ef\u80fd\u4ecd\u5728\u5f00\u5c01\u548c\u9a6c\u6587\u4e2d\u5728\u4e00\u8d77\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u8f6c\u8fc7\u8138\u9053\uff1a\u201c\u5c0f\u9053\u58eb\uff0c\u6211\u95ee\u5b8c\u4e86\uff0c\u7531\u4f60\u53d1\u95ee\u5427\uff01\u201d \u3000\u3000\u5c0f\u9053\u58eb\u6447\u6447\u5934\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\u53ef\u95ee\u7684\u4e86\u3002\u201d \u3000\u3000\u8001\u5996\u5a46\u9053\uff1a\u201c\u90a3\u5c31\u7531\u8001\u8eab\u95ee\u4f60\u4eec\u51e0\u4ef6\u4e8b\u5427\uff01\u201d \u3000\u3000\u5c0f\u9053\u58eb\u9053\uff1a\u201c\u4f60\u60f3\u77e5\u9053\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4f60\u4eec\u662f\u4e0d\u662f\u5168\u90fd\u5230\u6d1b\u9633\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u201c\u76ee\u7684\u662f\u4e3a\u4e86\u8ffd\u6355\u90a3\u4e09\u4e2a\u5973\u4eba\uff1f\u201d \u3000\u3000\u201c\u4e5f\u4e0d\u9519\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u7528\u4e0d\u7740\u7126\u6025\u4e86\uff0c\u90a3\u4e09\u4e2a\u5973\u4eba\u76ee\u524d\u5df2\u5728\u5c71\u5be8\u5185\u4f4f\u4e0b\uff0c\u77ed\u671f\u5185\u4e0d\u53ef\u80fd\u79bb\u5f00\uff0c\u82e5\u60f3\u64d2\u83b7 \u5979\u4eec\uff0c\u4e0d\u59a8\u6162\u6162\u518d\u60f3\u529e\u6cd5\uff0c\u73b0\u5728\u4f60\u4eec\u5c31\u5e26\u8001\u8eab\u53bb\u89c1\u738b\u5927\u4eba\u548c\u6c5f\u5927\u4fa0\u5427\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u4e5f\u8ba4\u4e3a\u65e2\u7136\u90a3\u4e09\u4e2a\u5973\u4eba\u5df2\u5728\u9099\u5c71\u843d\u811a\uff0c\u82e5\u60f3\u64d2\u83b7\u5979\u4eec\uff0c\u5fc5\u987b\u6363\u7834\u5c71\u5be8\uff0c\u4f46 \u8fd9\u662f\u4ef6\u5927\u4e8b\uff0c\u5728\u672a\u7980\u660e\u6c5f\u5343\u91cc\u548c\u738b\u5f64\u4e4b\u524d\uff0c\u7684\u786e\u83bd\u649e\u4e0d\u5f97\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u4e24\u4eba\u4fbf\u5e26\u7740\u8001\u5996\u5a46\u8fd4\u56de\u6d1b\u9633\u3002 \u3000\u3000\u4e3a\u907f\u4eba\u8033\u76ee\uff0c\u56de\u7a0b\u65f6\u4ed6\u4eec\u90fd\u7ed5\u9053\u800c\u884c\u3002 \u3000\u3000\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u6b63\u5728\u5ba2\u6808\u540e\u9662\u7684\u6811\u4e0b\u8c08\u5929\u3002 \u3000\u3000\u8001\u5996\u5a46\u4e00\u8fdb\u540e\u9662\u4fbf\u201c\u6251\u901a\u201d\u4e00\u58f0\uff0c\u8dea\u5012\u5728\u5730\u3002 \u3000\u3000\u4e24\u4eba\u90fd\u5927\u611f\u610f\u5916\uff1f\u738b\u5f64\u8bb6\u7136\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u4e5f\u597d\uff0c\u4f60\u4eec\u6349\u5230\u5979\uff0c\u603b\u7b97\u6ca1\u767d\u8dd1\u4e00 \u8d9f\u3002\u201d \u3000\u3000\u767d\u7fbd\u9053\uff1a\u201c\u4e0d\u662f\u6211\u4eec\u6349\u5230\u7684\uff0c\u662f\u5979\u81ea\u52a8\u6295\u5954\u8fc7\u6765\u7684\u3002\u201d \u3000\u3000\u201c\u6709\u8fd9\u79cd\u4e8b\uff1f\u5979\u4e3a\u4ec0\u4e48\u53c8\u8981\u56de\u6765\uff1f\u201d \u3000\u3000\u201c\u8fd8\u662f\u8ba9\u5979\u81ea\u5df1\u8bf4\u5427\uff01\u201d \u3000\u3000\u8001\u5996\u5a46\u7ad9\u8d77\u8eab\u6765\uff0c\u628a\u5728\u5c71\u4e0a\u544a\u8bc9\u767d\u7fbd\u548c\u5c0f\u71d5\u5b50\u7684\u8bdd\u53c8\u590d\u8ff0\u4e86\u4e00\u904d\u3002 \u3000\u3000\u53c8\u628a\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u5c06\u4e09\u540d\u8ffd\u6355\u5979\u7684\u4eba\u6740\u6b7b\u7684\u7ecf\u8fc7\uff0c\u4e5f\u4ee3\u4e3a\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u738b\u5f64\u5927\u4e3a\u5174\u594b\u7684\u9053\uff1a\u201c\u597d\u6781\u4e86\uff01\u6709\u8fd9\u4f4d\u53f2\u5927\u5ac2\u53bb\u800c\u590d\u8fd4\uff0c\u54b1\u4eec\u5c31\u7528\u4e0d\u7740\u53d1\u6101\u4e86\u3002\u201d \u3000\u3000\u8001\u5996\u5a46\u4e00\u542c\u738b\u5f64\u5c45\u7136\u79f0\u547c\u81ea\u5df1\u4e3a\u53f2\u5927\u5ac2\uff0c\u7b80\u76f4\u4e50\u900f\u4e86\u3002 \u3000\u3000\u4f46\u5979\u5374\u6545\u610f\u5e72\u54b3\u4e86\u4e24\u58f0\uff0c\u772f\u8d77\u773c\u775b\u9053\uff1a\u201c\u738b\u5927\u4eba\uff0c\u4eba\u5bb6\u8fd8\u6ca1\u5ac1\u4eba\u5462\uff01\u90a3\u591f\u8d44\u683c\u5f53\u5927\u5ac2 \u5462\uff1f\u201d \u3000\u3000\u738b\u5f64\u7b11\u9053\uff1a\u201c\u4f60\u5148\u6709\u4e2a\u5fc3\u7406\u51c6\u5907\uff0c\u7b49\u8fd9\u8fb9\u7684\u4e8b\u529e\u5b8c\u4ee5\u540e\uff0c\u968f\u6211\u56de\u4eac\uff0c\u90a3\u65f6\u6211\u5b9a\u4f1a\u66ff\u4f60\u7269 \u8272\u4e00\u4e2a\u5bf9\u8c61\uff0c\u5305\u4f60\u79f0\u5fc3\u5408\u610f\u3002\u201d \u3000\u3000\u8001\u5996\u5a46\u7b11\u5f97\u5408\u4e0d\u62e2\u5634\u9053\uff1a\u201c\u8fd9\u4e48\u8001\u4e86\uff0c\u4eba\u5bb6\u8c01\u80af\u8981\uff1f\u201d \u3000\u3000\u201c\u6211\u770b\u4f60\u4e00\u70b9\u4e5f\u4e0d\u8001\uff0c\u4eba\u4e5f\u957f\u5f97\u6ee1\u6f02\u4eae\uff0c\u53c8\u662f\u4e2a\u5973\u5f3a\u4eba\uff0c\u7537\u4eba\u4e00\u5b9a\u4f1a\u62a2\u7740\u8981\uff0c\u5982\u679c\u5230\u65f6 \u5019\u5ac1\u4e0d\u51fa\u53bb\uff0c\u53ea\u7ba1\u627e\u6211\u7b97\u5e10\u3002\u201d \u3000\u3000\u201c\u738b\u5927\u4eba\uff0c\u60f3\u4e0d\u5230\u60a8\u662f\u8fd9\u6837\u4e00\u4f4d\u8bc6\u8d27\u7684\u4eba\uff0c\u53ef\u60dc\u2026\u2026\u201d \u3000\u3000\u201c\u53ef\u60dc\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5982\u679c\u6211\u90a3\u7537\u4eba\u60f3\u8981\u4e2a\u5b69\u5b50\uff0c\u6050\u6015\u662f\u529e\u4e0d\u5230\u4e86\u3002\u201d \u3000\u3000\u201c\u5c82\u6709\u6b64\u7406\uff0c\u4f60\u6ca1\u542c\u8bf4\u2018\u8001\u868c\u751f\u73e0\u2019\u8fd9\u53e5\u8bdd\u5417\uff1f\u201d \u3000\u3000\u201c\u539f\u6765\u4f60\u8fd8\u662f\u628a\u4eba\u5bb6\u770b\u6210\u4e00\u4e2a\u8001\u868c\u5b50\uff01\u201d \u3000\u3000\u201c\u59dc\u662f\u8001\u7684\u8fa3\uff0c\u8001\u868c\u5b50\u5403\u8d77\u6765\u624d\u591f\u5473\u9053\u3002\u53f2\u5927\u5ac2\uff0c\u4e0d\u5fc5\u7740\u6025\uff0c\u8fd9\u5a92\u4eba\u6211\u505a\u5b9a\u4e86\uff0c\u54b1\u4eec\u73b0 \u5728\u5c31\u5230\u5c4b\u91cc\u8c08\u8c08\u5427\uff01\u201d \u3000\u3000\u201c\u8fd8\u8981\u8c08\u4ec0\u4e48\uff1f\u662f\u4e0d\u662f\u66ff\u4eba\u5bb6\u627e\u5bf9\u8c61\u7684\u4e8b\uff1f\u201d \u3000\u3000\u201c\u5148\u8c08\u70b9\u522b\u7684\uff0c\u627e\u5bf9\u8c61\u7684\u4e8b\u968f\u65f6\u90fd\u53ef\u4ee5\u8c08\u3002\u201d \u3000\u3000\u201c\u4e5f\u597d\uff0c\u53ea\u8981\u738b\u5927\u4eba\u522b\u628a\u8fd9\u4ef6\u4e8b\u5fd8\u8bb0\u5c31\u6210\u3002\u201d \u3000\u3000\u201c\u4f60\u7684\u7ec8\u8eab\u5927\u4e8b\uff0c\u738b\u67d0\u600e\u4f1a\u5fd8\u8bb0\uff0c\u53f2\u5927\u5ac2\uff01\u4f60\u597d\u50cf\u5bf9\u8fd9\u4ef6\u4e8b\u5f88\u5173\u5fc3\uff1f\u201d \u3000\u3000\u201c\u8c01\u4e0d\u5173\u5fc3\u81ea\u5df1\u7684\u7ec8\u8eab\u5927\u4e8b\uff1f\u5c24\u5176\u6211\u53f2\u5999\u79cb\uff0c\u5e74\u8fc7\u82b1\u7532\uff0c\u8fd8\u4e00\u76f4\u5c0f\u59d1\u72ec\u5904\uff0c\u82e5\u8bf4\u4e0d\u60f3\uff0c \u90a3\u662f\u9a97\u4eba\u7684\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u5982\u6b64\uff0c\u4f60\u4f55\u4e0d\u65e9\u8bb2\uff1f\u201d \u3000\u3000\u201c\u4eba\u5bb6\u600e\u4e48\u597d\u610f\u601d\u5462\uff1f\u4f60\u738b\u5927\u4eba\u4ece\u524d\u662f\u6574\u5929\u5230\u665a\u677f\u7740\u9762\u5b54\uff0c\u8c01\u4f1a\u60f3\u5230\u4f60\u4e5f\u8fd9\u4e48\u98ce\u8da3\uff0c\u65e9 \u77e5\u5982\u6b64\uff0c\u5f53\u521d\u4eba\u5bb6\u5c31\u4e0d\u8dd1\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\u5566\uff01\u201d\u54b1\u4eec\u8fdb\u5c4b\u8c08\u5427\uff01\u201c \u3000\u3000\u540e\u9662\u91cc\u4e5f\u6709\u4e00\u95f4\u5c0f\u5ba2\u5385\u3002 \u3000\u3000\u738b\u5f64\u3001\u6c5f\u5e72\u91cc\u3001\u8001\u5996\u5a46\u8fdb\u5165\u540e\uff0c\u738b\u5f64\u53c8\u56de\u5934\u62db\u547c\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4e2a\u4e5f\u8fdb\u6765\uff0c \u8fd9\u573a\u529f\u52b3\u662f\u4f60\u4eec\u7acb\u4e0b\u7684\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u738b\u5f64\u795e\u8272\u5df2\u8f6c\u8d8b\u4e25\u8083\u3002 \u3000\u3000\u5148\u524d\u4ed6\u96be\u5f97\u5728\u8001\u5996\u5a46\u9762\u524d\u8f7b\u677e\u4e86\u4e00\u4e0b\uff0c\u76ee\u7684\u53ea\u662f\u5728\u7f13\u548c\u4e00\u4e0b\u8001\u5996\u5a46\u7684\u60c5\u7eea\uff0c\u65b9\u4fbf\u95ee\u8bdd\u800c \u5df2\u3002 \u3000\u3000\u8eab\u4e3a\u5927\u5185\u4f8d\u536b\u7edf\u9886\u7684\u4ed6\uff0c\u5f53\u7136\u4e0d\u4f1a\u6c38\u8fdc\u90a3\u4e48\u968f\u968f\u4fbf\u4fbf\u3002 \u3000\u3000\u504f\u504f\u8001\u5996\u5a46\u5e74\u7eaa\u867d\u6d3b\u4e86\u4e00\u5927\u628a\uff0c\u5185\u5fc3\u5374\u8fd8\u662f\u90a3\u4e48\u5e7c\u7a1a\uff0c\u7adf\u7136\u5f53\u771f\u7684\u3002\u5426\u5219\uff0c\u738b\u5f64\u4e5f\u5c31\u4e0d \u4f1a\u7528\u8fd9\u4e00\u5957\u6765\u5bf9\u4ed8\u5979\u4e86\u3002 \u3000\u3000\u6b64\u523b\u7684\u8001\u5996\u5a46\uff0c\u7531\u4e8e\u5df2\u88ab\u5c0a\u79f0\u53f2\u5927\u5ac2\uff0c\u81ea\u7136\u8eab\u4efd\u4e5f\u5c31\u63d0\u9ad8\u4e86\u3002 \u3000\u3000\u56e0\u4e4b\uff0c\u5728\u5ba2\u5385\u91cc\u4e5f\u6709\u4e86\u5ea7\u4f4d\uff0c\u53cd\u800c\u662f\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u5f53\u7740\u6c5f\u5343\u91cc\u548c\u738b\u5f64\u7684\u9762\uff0c\u5374\u90fd\u662f\u7ad9\u7740 \u7684\u3002 \u3000\u3000\u8001\u5996\u5a46\u4e3b\u52a8\u5927\u732e\u6bb7\u52e4\u7684\u9053\uff1a\u201c\u738b\u5927\u4eba\u60f3\u77e5\u9053\u4ec0\u4e48\uff1f\u5c31\u8bf7\u53d1\u95ee\u5427\uff01 \u3000\u3000\u6211\u8fd9\u4e2a\u5f85\u5ac1\u7684\u8001\u59d1\u5a18\u662f\u77e5\u65e0\u4e0d\u8a00\uff0c\u8a00\u65e0\u4e0d\u5c3d\uff01\u201c \u3000\u3000\u63a5\u7740\u53c8\u8f6c\u5934\u671b\u5411\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u6c5f\u5927\u4fa0\u8981\u95ee\u4e5f\u662f\u4e00\u6837\uff0c\u4eba\u5bb6\u4e00\u5b9a\u6709\u4ec0\u4e48\u5c31\u8bf4\u4ec0\u4e48\uff0c\u5fc3\u91cc\u7edd \u4e0d\u7559\u534a\u70b9\u9690\u79c1\u3002\u201d \u3000\u3000\u738b\u5f64\u4e0d\u52a8\u58f0\u8272\u95ee\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u5ffd\u7136\u53d8\u5f97\u8fd9\u4e48\u597d\uff1f\u201d \u3000\u3000\u8001\u5996\u5a46\u5e72\u7b11\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u6211\u73b0\u5728\u662f\u5be1\u5987\u6b7b\u513f\u5b50\uff0c\u6ca1\u6307\u671b\u4e86\uff0c\u5982\u679c\u8bf4\u8fd8\u6709\u70b9\u6307\u671b\uff0c\u90a3\u5c31\u662f \u6307\u671b\u4f60\u738b\u5927\u4eba\u548c\u6c5f\u5927\u4fa0\u4e86\u3002\u201d \u3000\u3000\u738b\u5f64\u8138\u8272\u4e00\u6c89\u9053\uff1a\u201c\u4f60\u600e\u4e48\u9a82\u4eba\uff1f\u7adf\u6562\u8ba8\u6211\u548c\u6c5f\u5927\u4fa0\u7684\u4fbf\u5b9c\uff1f\u201d \u3000\u3000\u8001\u5996\u5a46\u60ca\u614c\u7684\u9053\uff1a\u201c\u738b\u5927\u4eba\u5343\u4e07\u522b\u8bef\u4f1a\uff0c\u6c5f\u5927\u4fa0\u4e5f\u4e0d\u8981\u8bef\u4f1a\uff0c\u4eba\u5bb6\u8bf4\u7684\u662f\u5b9e\u8bdd\uff0c\u7edd\u5bf9\u4e0d \u6562\u5b58\u5fc3\u8ba8\u4fbf\u5b9c\u3002\u201d \u3000\u3000\u770b\u8001\u5996\u5a46\u8fd9\u6837\u5b50\uff0c\u786e\u5b9e\u4e0d\u50cf\u662f\u6709\u610f\u7684\u3002 \u3000\u3000\u539f\u56e0\u4e0d\u5916\u662f\u88ab\u521a\u624d\u7684\u559c\u8baf\u51b2\u660f\u4e86\u5934\uff0c\u8bf4\u8bdd\u6765\u4e0d\u53ca\u591a\u60f3\uff0c\u56e0\u4e4b\u624d\u5f62\u6210\u4e86\u4e00\u65b9\u662f\u8a00\u8005\u65e0\u5fc3\uff0c \u4e00\u65b9\u662f\u542c\u8005\u6709\u610f\u3002 \u3000\u3000\u4f46\u738b\u5f64\u8fd8\u662f\u8ffd\u95ee\u9053\uff1a\u201c\u4f60\u8bf4\u6ca1\u6307\u671b\u4e86\uff0c\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u201c\u6211\u73b0\u5728\u6709\u5bb6\u5f52\u4e0d\u5f97\uff0c\u6b7b\u91cc\u9003\u751f\uff0c\u90a3\u91cc\u8fd8\u6709\u6307\u671b\u5462\uff1f\u201d \u3000\u3000\u201c\u96be\u9053\u6c5f\u5927\u4fa0\u548c\u6211\u53ef\u4ee5\u505a\u4f60\u7684\u6307\u671b\uff1f\u201d \u3000\u3000\u201c\u6211\u9003\u5230\u8fd9\u91cc\uff0c\u5168\u4ed7\u4e24\u4f4d\u4fdd\u62a4\u5b89\u5168\uff0c\u4f60\u4eec\u4e0d\u662f\u6211\u7684\u6307\u671b\u662f\u4ec0\u4e48\uff1f\u6240\u4ee5\u2026\u2026\u201d \u3000\u3000\u201c\u6240\u4ee5\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5f85\u4f1a\u513f\u6211\u8bf4\u7684\u8bdd\u4e00\u5b9a\u662f\u771f\u7684\uff0c\u5982\u679c\u5c06\u6765\u67e5\u51fa\u534a\u53e5\u4e0d\u5b9e\uff0c\u6211\u73b0\u5728\u5c31\u5bf9\u5929\u53d1\u8a93\u3002\u201d \u3000\u3000\u201c\u4f60\u8981\u53d1\u4ec0\u4e48\u8a93\uff1f\u201d \u3000\u3000\u201c\u5018\u82e5\u6211\u9a97\u4e86\u738b\u5927\u4eba\u548c\u6c5f\u5927\u4fa0\uff0c\u5c31\u6c38\u8fdc\u5ac1\u4e0d\u51fa\u53bb\uff0c\u60a8\u4e5f\u4e0d\u5fc5\u518d\u4e3a\u6211\u505a\u5a92\u4e86\uff01\u201d \u3000\u3000\u201c\u597d\uff0c\u6211\u76f8\u4fe1\u4f60\u3002\u5148\u95ee\u4f60\u4e00\u4ef6\u4e8b\uff0c\u90a3\u4e09\u4e2a\u5973\u4eba\u4e3a\u4ec0\u4e48\u8981\u7559\u5728\u9099\u5c71\u4e0d\u8d70\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u5c71\u5be8\u91cc\u7684\u767d\u5be8\u4e3b\u8981\u8bf7\u5979\u4eec\u5e2e\u5fd9\u529e\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u201c\u529e\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u5c31\u5728\u8fd9\u51e0\u5929\uff0c\u767d\u5be8\u4e3b\u7684\u538b\u5be8\u592b\u4eba\u4fbf\u8981\u8fc7\u95e8\uff0c\u767d\u5be8\u4e3b\u62dc\u6258\u5979\u4eec\u4e00\u4f4d\u5f53\u5973\u5bbe\u76f8\uff0c\u4e24\u4f4d\u505a\u5973 \u62db\u5f85\u3002\u201d \u3000\u3000\u201c\u767d\u5be8\u4e3b\u53ef\u662f\u9c7c\u5316\u9f99\u7684\u624b\u4e0b\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\u9c7c\u5316\u9f99\u4e3a\u4e86\u968f\u65f6\u80fd\u542c\u547d\u9a6c\u6587\u4e2d\u8c03\u9063\uff0c\u5df2\u5f88\u5c11\u518d\u56de\u6d1e\u5ead\u603b\u5be8\uff0c\u4fbf\u5728\u90ae\u5c71\u6210\u7acb\u4e00\u5904\u6c34 \u5be8\uff0c\u4e00\u5904\u5c71\u5be8\u3002\u8981\u8ba8\u538b\u5be8\u592b\u4eba\u7684\u662f\u5c71\u5be8\u5be8\u4e3b\uff0c\u53eb\u767d\u864e\u3002\u201d \u3000\u3000\u201c\u90a3\u4e09\u4e2a\u5973\u7684\u662f\u5426\u5df2\u540c\u610f\u5f53\u5973\u5bbe\u76f8\u548c\u5973\u62db\u5f85\uff1f\u201d \u3000\u3000\u201c\u5979\u4eec\u672c\u6765\u4e0d\u60f3\u56de\u897f\u57df\uff0c\u662f\u9a6c\u6587\u4e2d\u903c\u5979\u4eec\u8d70\u7684\uff0c\u5982\u4eca\u6709\u4eba\u8981\u5979\u4eec\u7559\u4e0b\u6765\uff0c\u5979\u4eec\u5f53\u7136\u613f\u610f \u4e86\u3002\u201d \u3000\u3000\u201c\u9a6c\u6587\u4e2d\u4e3a\u4ec0\u4e48\u8981\u903c\u5979\u4eec\u8d70\uff1f\u201d \u3000\u3000\u201c\u8fd8\u4e0d\u662f\u6015\u66b4\u9732\u5f62\u8ff9\uff0c\u82e5\u88ab\u60a8\u738b\u5927\u4eba\u67e5\u51fa\u901f\u5230\uff0c\u4ed6\u7684\u72d0\u72f8\u5c3e\u5df4\u8fd8\u5f80\u54ea\u91cc\u85cf\uff1f\u201d \u3000\u3000\u738b\u5f64\u559d\u4e86\u53e3\u8336\uff0c\u6682\u65f6\u4e0d\u518d\u95ee\u8bdd\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u63a5\u7740\u95ee\u9053\uff1a\u201c\u90a3\u4e09\u4e2a\u5973\u4eba\u5728\u2018\u8fce\u6625\u9601\u2019\u505a\u90a3\u79cd\u4e8b\uff0c\u9a6c\u6587\u4e2d\u662f\u5426\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u90a3\u672c\u6765\u5c31\u662f\u9a6c\u6587\u4e2d\u8981\u5979\u4eec\u505a\u7684\u3002\u201d \u3000\u3000\u201c\u9a6c\u6587\u4e2d\u4e3a\u4ec0\u4e48\u4f1a\u903c\u5979\u4eec\u5f53\u5993\u5973\uff1f\u201d \u3000\u3000\u201c\u7528\u610f\u662f\u8981\u5979\u4eec\u627e\u7279\u5b9a\u7684\u5bf9\u8c61\u4e0b\u866b\u3002\u201d \u3000\u3000\u201c\u7279\u5b9a\u5bf9\u8c61\u6307\u7684\u662f\u54ea\u4e9b\u4eba\uff1f\u201d \u3000\u3000\u201c\u5c31\u662f\u53ef\u4f9b\u5229\u7528\u7684\uff0c\u5404\u9636\u5c42\u7684\u90fd\u6709\u3002\u5f53\u8fd9\u4e9b\u4eba\u4f53\u5185\u6709\u4e86\u866b\u53d8\u4e4b\u540e\uff0c\u9a6c\u6587\u4e2d\u5c31\u6709\u529e\u6cd5\u63a7\u5236 \u4ed6\u4eec\uff0c\u4ed6\u4eec\u4e5f\u5c31\u4f1a\u6b7b\u5fc3\u584c\u5730\u7684\u4e3a\u9a6c\u6587\u4e2d\u6548\u547d\u3002\u4e09\u516c\u4e3b\u5c31\u662f\u4e00\u4e2a\u4f8b\u5b50\uff0c\u5f53\u51e0\u5e74\u524d\uff0c\u4e09\u516c\u4e3b\u4f53\u5185 \u7684\u866b\u6bd2\u5df2\u6210\u6c14\u5019\u540e\uff0c\u8fd8\u4e0d\u662f\u7167\u6837\u4e5f\u542c\u4ed6\u4eec\u7684\uff0c\u6211\u5f53\u65f6\u5c31\u662f\u63a7\u5236\u4e09\u516c\u4e3b\u5fc3\u6001\u548c\u884c\u52a8\u7684\u4e3b\u8981\u4eba\u5458 \u4e4b\u4e00\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u4e0d\u7531\u6697\u6697\u9707\u60ca\uff01 \u3000\u3000\u4ed6\u6697\u5fd6\uff1a\u9a6c\u6587\u4e2d\u7684\u624b\u6bb5\uff0c\u53ef\u8bf4\u65e0\u6240\u4e0d\u7528\u5176\u6781\uff0c\u82e5\u4e0d\u9664\u53bb\u6b64\u4eba\uff0c\u5fc5\u5c06\u917f\u6210\u671d\u5ef7\u96be\u4ee5\u6536\u62fe\u7684 \u5fc3\u8179\u5927\u60a3\u3002 \u3000\u3000\u738b\u5f64\u53c8\u95ee\u9053\uff1a\u201c\u90a3\u4f4d\u767d\u5be8\u4e3b\u7684\u5a5a\u671f\u5728\u4ec0\u4e48\u65f6\u5019\uff1f\u201d \u3000\u3000\u8001\u5996\u5a46\u6414\u4e86\u6414\u6ee1\u5934\u82b1\u767d\u5934\u53d1\uff0c\u9053\uff1a\u201c\u8be6\u7ec6\u65e5\u671f\u4e0d\u6e05\u695a\uff0c\u770b\u6837\u5b50\u662f\u5feb\u4e86\uff0c\u5927\u6982\u5c31\u5728\u8fd9\u51e0\u5929 \u5427\uff01\u201d \u3000\u3000\u201c\u662f\u8c01\u5bb6\u7684\u59d1\u5a18\uff1f\u201d \u3000\u3000\u201c\u5c31\u662f\u672c\u5730\u7684\u3002\u201d \u3000\u3000\u201c\u5973\u65b9\u540c\u610f\u5417\uff1f\u201d \u3000\u3000\u201c\u8fd9\u5c31\u4e0d\u6e05\u695a\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u53ef\u542c\u8bf4\u8fc7\u5973\u65b9\u59d3\u4ec0\u4e48\u53eb\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u597d\u50cf\u53eb\u2026\u2026\u201d \u3000\u3000\u201c\u53eb\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8001\u5996\u5a46\u5ffd\u7136\u54e6\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4eba\u5bb6\u60f3\u8d77\u6765\u4e86\uff0c\u90a3\u59d1\u5a18\u53eb\u795d\u6708\u82f1\uff0c\u800c\u4e14\u5c31\u4f4f\u5728\u9099\u5c71\u5c71\u811a\u4e0b\uff0c \u51fa\u4e86\u5317\u57ce\u95e8\u5f80\u5c71\u4e0a\u8d70\u5c31\u53ef\u627e\u5230\u3002\u201d \u3000\u3000\u201c\u795d\u6708\u82f1\u5bb6\u91cc\u7684\u60c5\u5f62\u5982\u4f55\uff0c\u4f60\u53ef\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u636e\u8bf4\u5bb6\u91cc\u4eba\u4e01\u4e0d\u591a\uff0c\u9664\u4e86\u8001\u4e24\u53e3\u548c\u795d\u59d1\u5a18\u5916\uff0c\u5c31\u662f\u8fd8\u6709\uff0c\u4e5f\u6ca1\u6709\u51e0\u4e2a\u3002\u201d \u3000\u3000\u201c\u795d\u5bb6\u7684\u5bb6\u5883\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u636e\u8bf4\u795d\u8001\u5934\u513f\u8fd8\u662f\u4e2a\u8bfb\u4e66\u4eba\uff0c\u66fe\u4e2d\u8fc7\u79c0\u624d\uff0c\u5bb6\u5883\u4e0d\u5dee\u3002\u201d \u3000\u3000\u738b\u5f64\u4e0d\u518d\u591a\u95ee\uff0c\u6325\u4e86\u6325\u624b\u9053\uff1a\u201c\u591a\u8c22\u53f2\u5927\u5ac2\u63d0\u4f9b\u4e86\u8fd9\u4e48\u591a\u7684\u6d88\u606f\uff0c\u4f60\u8f9b\u82e6\u4e86\uff0c\u90a3\u8fb9\u8fd8\u6709 \u4e24\u95f4\u7684\u7a7a\u623f\uff0c\u4f60\u53bb\u4f11\u606f\u5427\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u8fde\u5fd9\u628a\u8001\u5996\u5a46\u9886\u8fdb\u623f\u95f4\u3002 \u3000\u3000\u738b\u5f64\u95ee\u9053\uff1a\u201c\u6c5f\u5144\uff0c\u8001\u5996\u5a46\u8fd9\u4e9b\u8bdd\uff0c\u5bf9\u54b1\u4eec\u6765\u8bf4\uff0c\u7684\u786e\u5f88\u6709\u4ef7\u503c\uff0c\u53ea\u662f\u6b64\u65f6\u4f3c\u4e4e\u8fd8\u65e0\u6cd5 \u8bc1\u5b9e\u8001\u5996\u5a46\u6765\u6295\u5954\u54b1\u4eec\u7a76\u7adf\u662f\u771f\u5fc3\u8fd8\u662f\u5047\u610f\uff0c\u5982\u679c\u662f\u5047\u610f\uff0c\u54b1\u4eec\u53cd\u800c\u4e2d\u8ba1\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u8bed\u6c14\u575a\u5b9a\u7684\u9053\uff1a\u201c\u8001\u5f1f\u653e\u5fc3\uff0c\u6c5f\u67d0\u770b\u90a3\u8001\u5996\u5973\u7edd\u5bf9\u662f\u771f\u5fc3\u7684\uff0c\u5979\u65b9\u624d\u6240\u8bf4\u7684\u8fd9\u4e9b \u8bdd\uff0c\u4e5f\u7edd\u4e0d\u4f1a\u6709\u5047\uff0c\u5c3d\u7ba1\u653e\u5fc3\u3002\u201d \u3000\u3000\u738b\u5f64\u5fd9\u53c8\u95ee\u9053\uff1a\u201c\u4ee5\u6c5f\u5144\u7684\u770b\u6cd5\uff0c\u73b0\u5728\u54b1\u4eec\u8be5\u5982\u4f55\u884c\u52a8\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u6b63\u8272\u9053\uff1a\u201c\u6b64\u523b\u6700\u8981\u7d27\u7684\u662f\u627e\u5230\u90a3\u4f4d\u795d\u59d1\u5a18\u7684\u7236\u6bcd\uff0c\u67e5\u51fa\u795d\u59d1\u5a18\u7684\u559c\u4e8b\u662f\u54ea\u4e00 \u5929\uff1f\u201d \u3000\u3000\u738b\u5f64\u987f\u4e86\u987f\u9053\uff1a\u201c\u6c5f\u5144\u8ba4\u4e3a\u8fd9\u4ef6\u4e8b\u662f\u5426\u5c5e\u4e8e\u5f3a\u9738\u6c11\u5973\u80c1\u8feb\u6210\u5a5a\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u70b9\u70b9\u5934\u9053\uff1a\u201c\u5927\u6709\u53ef\u80fd\uff0c\u90a3\u4f4d\u795d\u8001\u5148\u751f\u65e2\u7136\u662f\u4f4d\u8bfb\u4e66\u4eba\uff0c\u600e\u80af\u628a\u5973\u513f\u5ac1\u7ed9\u636e\u5c71\u4e3a \u5bc7\u7684\u4eba\uff0c\u54b1\u4eec\u67e5\u6e05\u4e4b\u540e\uff0c\u5c11\u4e0d\u5f97\u4e5f\u8981\u5e2e\u4ed6\u628a\u8fd9\u4ef6\u4e8b\u6446\u5e73\u3002\u201d \u3000\u3000\u201c\u5982\u6b64\u4e00\u6765\uff0c\u5c82\u4e0d\u7275\u626f\u592a\u5927\u4e86\uff1f\u201d \u3000\u3000\u201c\u90a3\u5c31\u53ea\u6709\u89c1\u673a\u884c\u4e8b\u4e86\uff0c\u4e0b\u5348\u5c31\u518d\u6d3e\u5c0f\u71d5\u5b50\u548c\u5c0f\u9053\u58eb\u53bb\u529e\u8fd9\u4ef6\u4e8b\u3002\u201d \u5367\u9f99\u751f\u300a\u71d5\u5b50\u4f20\u5947\u300b \u7b2c\u4e8c\u5341\u4e03\u56de\u3000\u795d\u5bb6\u5bdf\u8bbf \u3000\u3000\u5f53\u65e5\u5348\u540e\uff0c\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u53c8\u6765\u5230\u9099\u5c71\u811a\u4e0b\u3002 \u3000\u3000\u4ed6\u4eec\u662f\u5949\u547d\u5230\u795d\u5bb6\u8bbf\u67e5\u7684\u3002 \u3000\u3000\u65e2\u7136\u6709\u540d\u6709\u59d3\uff0c\u53c8\u5df2\u65e9\u77e5\u6982\u7565\u4f4d\u7f6e\uff0c\u800c\u4e14\u5c71\u4e0a\u4f4f\u7684\u4eba\u5bb6\u4e0d\u591a\uff0c\u4e0d\u6d88\u4e00\u4e2a\u65f6\u8fb0\u4fbf\u5df2\u6253\u542c\u5230 \u4e86\u3002 \u3000\u3000\u795d\u5bb6\u867d\u662f\u5c71\u5c45\uff0c\u4f4f\u7684\u5374\u662f\u4e00\u5e62\u4e0d\u7b97\u5c0f\u7684\u5b85\u9662\uff0c\u7c89\u5899\u7ea2\u74e6\u7684\u56db\u5408\u9662\uff0c\u5927\u95e8\u5916\u8fd8\u56f4\u7740\u4e00\u9053\u7bf1 \u7b06\uff0c\u770b\u6765\u53e4\u8272\u53e4\u9999\u3002 \u3000\u3000\u7bf1\u95e8\u5173\u7740\uff0c\u91cc\u9762\u7684\u5927\u95e8\u4e5f\u5173\u7740\uff0c\u542c\u4e0d\u5230\u534a\u70b9\u4eba\u58f0\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u662f\u4e0d\u662f\u642c\u8d70\u4e86\uff1f\u201d \u3000\u3000\u767d\u7fbd\u6447\u6447\u5934\u9053\uff1a\u201c\u600e\u4e48\u53ef\u80fd\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u5982\u679c\u795d\u8001\u5148\u751f\u592b\u5987\u4fe9\u548c\u795d\u59d1\u5a18\u4e0d\u540c\u610f\u8fd9\u95e8\u4eb2\u4e8b\uff0c\u9664\u4e86\u5077\u5077\u642c\u8d70\uff0c\u8fd8\u6709\u4ec0\u4e48 \u529e\u6cd5\uff1f\u201d \u3000\u3000\u201c\u4f60\u8bf4\u7684\u4e5f\u6709\u9053\u7406\uff0c\u4e0d\u8fc7\u54b1\u4eec\u6700\u597d\u8fd8\u662f\u5728\u9644\u8fd1\u7b49\u4e00\u7b49\uff0c\u786e\u5b9e\u67e5\u660e\u518d\u8bf4\u3002\u201d \u3000\u3000\u5927\u7ea6\u76cf\u8336\u5de5\u592b\u4e4b\u540e\uff0c\u4e00\u540d\u519c\u592b\u6a21\u6837\u7684\u4e2d\u5e74\u4eba\u8d70\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u90a3\u519c\u592b\u8d70\u5230\u4e86\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u9762\u524d\uff0c\u81ea\u52a8\u505c\u4e86\u4e0b\u6765\uff0c\u597d\u5947\u7684\u95ee\u9053\uff1a\u201c\u4e24\u4f4d\u662f\u8981\u6765\u627e\u795d\u5148\u751f \u7684\u5417\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u6211\u4eec\u6b63\u662f\u8981\u5230\u795d\u5e9c\u6765\u7684\u3002\u201d \u3000\u3000\u201c\u4e24\u4f4d\u53ef\u662f\u5c71\u5be8\u91cc\u7684\u4eba\uff1f\u201d \u3000\u3000\u201c\u4e0d\uff0c\u6211\u4eec\u662f\u795d\u5bb6\u7684\u4eb2\u621a\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u4e0d\u4e0a\u524d\u6572\u95e8\uff1f\u201d \u3000\u3000\u201c\u5916\u8fb9\u7bf1\u95e8\u4e5f\u5173\u7740\uff0c\u8fdb\u4e0d\u53bb\uff0c\u600e\u80fd\u6572\u95e8\uff1f\u201d \u3000\u3000\u201c\u8fd9\u7bf1\u95e8\u4ece\u5916\u9762\u5c31\u53ef\u4ee5\u6253\u5f00\u2026\u2026\u201d \u3000\u3000\u90a3\u519c\u592b\u6b63\u8bf4\u7740\uff0c\u5ffd\u7136\u6307\u7740\u5c0f\u5f84\u4e0a\u7531\u8fdc\u800c\u8fd1\u7684\u4e00\u540d\u8377\u9504\u6c49\u5b50\uff0c\u9053\uff1a\u201c\u90a3\u4e0d\u662f\u6765\u4e86\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u5411\u90a3\u6c49\u5b50\u671b\u53bb\u2014\u2014\u5927\u7ea6\u56db\u65ec\u4e0a\u4e0b\u5e74\u7eaa\uff0c\u5149\u7740\u811a\uff0c\u88e4\u7ba1\u5411\u4e0a\u633d\u4e86\u4e00\u5927\u622a\uff0c\u9732 \u51fa\u534a\u622a\u9ed1\u9edd\u9edd\u7684\u5c0f\u817f\uff0c\u5b8c\u5168\u662f\u4e00\u526f\u505a\u7c97\u6d3b\u7684\u4e61\u4e0b\u4eba\u6a21\u6837\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u5446\u4e86\u4e00\u5446\u9053\uff1a\u201c\u795d\u5148\u751f\u600e\u4e48\u4f1a\u662f\u8fd9\u6837\u5b50\uff1f\u201d \u3000\u3000\u90a3\u519c\u592b\u54a7\u54a7\u5634\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u795d\u5bb6\u7684\u957f\u5de5\uff0c\u53eb\u8bb8\u6709\u7530\u3002\u201d \u3000\u3000\u63a5\u7740\u53c8\u9ad8\u58f0\u62db\u547c\u9053\uff1a\u201c\u8001\u8bb8\uff0c\u8fd9\u4e24\u4f4d\u662f\u4f60\u4eec\u4e3b\u4eba\u7684\u4eb2\u621a\uff0c\u5feb\u5e26\u4ed6\u4eec\u8fdb\u53bb\u5427\uff01\u201d \u3000\u3000\u6b64\u523b\u767d\u7fbd\u7a7f\u7684\u4e5f\u662f\u4fbf\u88c5\u3002 \u3000\u3000\u8bb8\u6709\u7530\u970e\u65f6\u671b\u4e86\u4e24\u4eba\u534a\u664c\uff0c\u624d\u9053\uff1a\u201c\u54b1\u597d\u50cf\u4ece\u6ca1\u89c1\u4f60\u4eec\u4e24\u4f4d\u6765\u8fc7\uff0c\u8bf7\u5148\u8bf4\u8bf4\u8d35\u59d3\uff0c\u4e5f\u597d \u5148\u8ba9\u6211\u4eec\u8001\u7237\u77e5\u9053\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u5e76\u672a\u9690\u7792\uff0c\u5404\u81ea\u9053\u51fa\u59d3\u6c0f\u3002 \u3000\u3000\u8bb8\u6709\u7530\u9053\uff1a\u201c\u4e24\u4f4d\u5c31\u8bf7\u5728\u5916\u9762\u7b49\u4e00\u7b49\uff0c\u8001\u7237\u8981\u4e0d\u8981\u89c1\uff0c\u9a6c\u4e0a\u5c31\u4f1a\u901a\u77e5\u4f60\u4eec\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u6253\u5f00\u7bf1\u95e8\uff0c\u518d\u6572\u5927\u95e8\u3002 \u3000\u3000\u51fa\u6765\u5e94\u95e8\u7684\u662f\u4e2a\u4e2d\u5e74\u5973\u4eba\uff0c\u4e5f\u662f\u4e61\u4e0b\u4eba\u6a21\u6837\u3002 \u3000\u3000\u8bb8\u6709\u7530\u8fdb\u53bb\u4e4b\u540e\uff0c\u590d\u53c8\u628a\u5927\u95e8\u5173\u4e0a\u3002 \u3000\u3000\u767d\u7fbd\u671b\u4e86\u90a3\u5c1a\u672a\u79bb\u53bb\u7684\u519c\u592b\u4e00\u773c\uff0c\u9053\uff1a\u201c\u795d\u5bb6\u597d\u5927\u7684\u89c4\u77e9\uff01\u201d \u3000\u3000\u90a3\u519c\u592b\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u795d\u5bb6\u6700\u8fd1\u51fa\u4e8b\u4e86\uff0c\u6240\u4ee5\u624d\u8fd9\u6837\u7684\u3002\u201d \u3000\u3000\u201c\u51fa\u4e86\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u4e24\u4f4d\u65e2\u7136\u662f\u795d\u5bb6\u7684\u4eb2\u621a\uff0c\u8fdb\u53bb\u4ee5\u540e\u81ea\u7136\u660e\u767d\uff0c\u5176\u5b9e\u795d\u5bb6\u7a76\u7adf\u51fa\u4e86\u4ec0\u4e48\u4e8b\uff1f\u6211\u4eec\u4e5f\u4e0d\u592a \u6e05\u695a\uff0c\u597d\u50cf\u548c\u8fd9\u91cc\u6700\u8fd1\u6210\u7acb\u7684\u4e00\u5ea7\u5c71\u5be8\u6709\u5173\u7cfb\u3002\u201d \u3000\u3000\u90a3\u519c\u592b\u8bf4\u5b8c\u8bdd\uff0c\u4fbf\u81ea\u884c\u8d70\u5f00\u3002 \u3000\u3000\u76f4\u7b49\u4e86\u5f88\u4e45\uff0c\u624d\u89c1\u8bb8\u6709\u7530\u6253\u5f00\u95e8\u8d70\u51fa\u6765\uff0c\u9053\uff1a\u201c\u6211\u4eec\u8001\u7237\u6709\u8bf7\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u60f3\u4e0d\u5230\u5c45\u7136\u80fd\u88ab\u63a5\u89c1\u3002 \u3000\u3000\u4ed6\u4eec\u54ea\u91cc\u77e5\u9053\uff0c\u8fd9\u662f\u795d\u79c0\u624d\u8bef\u4ee5\u4e3a\u4e24\u4eba\u662f\u4ece\u5c71\u5be8\u6765\u7684\uff0c\u4e0d\u6562\u4e0d\u4e88\u63a5\u89c1\u3002 \u3000\u3000\u8fdb\u5165\u5927\u95e8\uff0c\u5ead\u9662\u6574\u7406\u5f97\u5341\u5206\u96c5\u81f4\u3002 \u3000\u3000\u8bb8\u6709\u7530\u5f80\u6b63\u5c4b\u4e2d\u592e\u4e00\u6307\u9053\uff1a\u201c\u6211\u4eec\u8001\u7237\u5df2\u5728\u5ba2\u5385\u7b49\u5019\uff0c\u4e8c\u4f4d\u5c31\u81ea\u884c\u8fdb\u53bb\u5427\uff01\u201d \u3000\u3000\u53ea\u542c\u5ba2\u5385\u5185\u6709\u4eba\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u63a5\u7740\u95e8\u5185\u73b0\u51fa\u4e00\u4e2a\u8001\u4eba\u8eab\u5f71\u3002 \u3000\u3000\u5176\u5b9e\u4eba\u53ea\u662f\u4e94\u65ec\u5f00\u5916\u5e74\u7eaa\uff0c\u5e76\u4e0d\u5f88\u8001\u3002\u7a7f\u4e00\u4ef6\u9752\u5e03\u5916\u7f69\u9ed1\u7f0e\u9a6c\u8902\uff0c\u770b\u6765\u9762\u8c8c\u6e05\u7626\uff0c\u96cd\u5bb9 \u5112\u96c5\uff0c\u795e\u8272\u95f4\u5219\u53c8\u663e\u5f97\u4e00\u526f\u6c89\u51dd\u51b7\u8083\u3002 \u3000\u3000\u4e0d\u6d88\u8bf4\uff0c\u8fd9\u4eba\u4fbf\u662f\u5b85\u4e3b\u795d\u79c0\u624d\u4e86\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u8fde\u5fd9\u53cc\u624b\u4e00\u62f1\uff0c\u9f50\u58f0\u9053\uff1a\u201c\u795d\u8001\u5148\u751f\uff0c\u6253\u6270\u4e86\uff01\u201d \u3000\u3000\u8fd9\u79cd\u53e3\u6c14\uff0c\u8d8a\u53d1\u4ee4\u795d\u79c0\u624d\u8bef\u8ba4\u4e24\u4eba\u662f\u5c71\u5be8\u767d\u5be8\u4e3b\u6d3e\u6765\u7684\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u795d\u79c0\u624d\u96be\u514d\u6709\u4e9b\u610f\u5916\uff0c\u50cf\u8fd9\u6837\u4e24\u4f4d\u7fe9\u7fe9\u6f47\u6d12\u3001\u501c\u50a5\u4e0d\u7fa4\u7684\u5e74\u8f7b\u4eba\uff0c\u600e\u4f1a\u505a\u8d77\u8d3c\u5bc7 \u6765\u5462\uff1f\u5f53\u4e0b\uff0c\u795d\u79c0\u624d\u6beb\u65e0\u8868\u60c5\u7684\u9053\uff1a\u201c\u4e24\u4f4d\u8bf7\u5750\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u843d\u4e86\u5ea7\u3002 \u3000\u3000\u7531\u4e8e\u6c14\u6c1b\u4e0d\u5bf9\uff0c\u4e24\u4eba\u4e00\u65f6\u4e4b\u95f4\u8fd8\u771f\u4e0d\u77e5\u8be5\u5982\u4f55\u5f00\u53e3\u624d\u597d\u3002 \u3000\u3000\u795d\u79c0\u624d\u4e5f\u5728\u5bf9\u9762\u5750\u4e0b\uff0c\u8f7b\u54b3\u4e86\u58f0\u9053\uff1a\u201c\u4e24\u4f4d\u6709\u8bdd\u5c31\u8bf7\u76f4\u8bf4\u5427\uff01 \u3000\u3000\u4e8b\u60c5\u5df2\u7ecf\u5230\u4e86\u8fd9\u79cd\u5730\u6b65\uff0c\u8001\u673d\u9664\u4e86\u542c\u5929\u7531\u547d\uff0c\u8fd8\u6709\u4ec0\u4e48\u529e\u6cd5\u3002\u201c \u3000\u3000\u5c0f\u71d5\u5b50\u6014\u6014\u7684\u671b\u4e86\u767d\u7fbd\u4e00\u773c\u3002 \u3000\u3000\u767d\u7fbd\u7acb\u5373\u62b1\u62f3\u9053\uff1a\u201c\u795d\u8001\u5148\u751f\uff0c\u4f60\u53ef\u80fd\u628a\u6211\u4eec\u8bef\u4f1a\u4e86\u3002\u201d \u3000\u3000\u795d\u79c0\u624d\u4e24\u773c\u7728\u4e86\u7728\uff0c\u9053\uff1a\u201c\u83ab\u975e\u4e24\u4f4d\u4e0d\u662f\u5c71\u5be8\u6765\u7684\uff1f\u53ef\u662f\u8001\u673d\u5e76\u4e0d\u8ba4\u8bc6\u4e8c\u4f4d\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u4e0d\u662f\u5c71\u5be8\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u4e8c\u4f4d\u8fb1\u4e34\u5bd2\u820d\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u4eec\u6b63\u662f\u542c\u8bf4\u6b64\u5904\u5c71\u5be8\u767d\u5be8\u4e3b\u51c6\u5907\u5f3a\u4e0e\u4ee4\u5a9b\u6210\u4eb2\u7684\u4e8b\u800c\u6765\u7684\u3002\u201d \u3000\u3000\u795d\u79c0\u624d\u4f3c\u4e4e\u6709\u4e9b\u4e0d\u76f8\u4fe1\u81ea\u5df1\u7684\u8033\u6735\uff0c\u987f\u4e86\u4e00\u987f\u9053\uff1a\u201c\u90a3\u4e48\u4e8c\u4f4d\u662f\u2026\u2026\u201d \u3000\u3000\u767d\u7fbd\u6b63\u8272\u9053\uff1a\u201c\u6211\u4eec\u6682\u65f6\u8fd8\u4e0d\u65b9\u4fbf\u8bf4\u51fa\u8eab\u4efd\u6765\u5386\uff0c\u603b\u4e4b\uff0c\u6211\u4eec\u4e00\u5b9a\u6709\u529e\u6cd5\u5e2e\u5e9c\u4e0a\u7684\u5fd9\uff0c \u60a8\u653e\u5fc3\uff01\u201d \u3000\u3000\u795d\u79c0\u624d\u4f9d\u7136\u6014\u6014\u7684\u9053\uff1a\u201c\u5c31\u51ed\u4e8c\u4f4d\u2026\u2026\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u63a5\u9053\uff1a\u201c\u6211\u4eec\u5f53\u7136\u8fd8\u6709\u4eba\u3002\u201d \u3000\u3000\u201c\u5c31\u7b97\u4e8c\u4f4d\u8fd8\u6709\u4eba\uff0c\u4e5f\u4e0d\u53ef\u80fd\u6bd4\u5c71\u5be8\u7684\u4eba\u591a\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u56fa\u7136\u6ca1\u6709\u767d\u864e\u624b\u4e0b\u7684\u4eba\u591a\uff0c\u4f46\u5175\u4e0d\u5728\u591a\u800c\u5728\u7cbe\uff0c\u8bf7\u795d\u8001\u5148\u751f\u76f8\u4fe1\uff0c\u6211\u4eec\u5fc5\u53ef\u4ee5\u4e00 \u5f53\u5341\uff0c\u4ee5\u5341\u5f53\u767e\uff0c\u751a\u81f3\u4ee5\u767e\u5f53\u4e07\u3002\u201d \u3000\u3000\u795d\u79c0\u624d\u957f\u957f\u5401\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u4e24\u4f4d\u7684\u8fd9\u4efd\u52c7\u6c14\u548c\u4e49\u6c14\uff0c\u5f88\u4ee4\u8001\u673d\u611f\u52a8\u800c\u53c8\u611f\u6fc0\uff0c\u4f46\u4e3a\u4e8c\u4f4d \u7740\u60f3\uff0c\u8fd8\u662f\u522b\u8fc7\u95ee\u8fd9\u4ef6\u4e8b\u7684\u597d\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u5fc3\u91cc\u6709\u6570\uff0c\u5bf9\u65b9\u7684\u7528\u610f\u4e0d\u5916\u662f\u6000\u7591\u4ed6\u4eec\u662f\u5426\u6709\u80fd\u529b\u7ba1\u8fd9\u4ef6\u4e8b\uff0c\u82e5\u53ea\u662f\u51ed\u4e00 \u65f6\u610f\u6c14\u8bf4\u8bf4\u5927\u8bdd\uff0c\u90a3\u7684\u786e\u662f\u4ef6\u53cd\u800c\u5f04\u5de7\u6210\u62d9\u7684\u4e8b\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u4e24\u4eba\u4e92\u5f1f\u4e86\u4e2a\u773c\u8272\uff0c\u51b3\u5b9a\u5148\u663e\u9732\u51e0\u624b\u7edd\u6d3b\u7ed9\u795d\u79c0\u624d\u77a7\u77a7\u3002 \u3000\u3000\u53ea\u542c\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u665a\u8f88\u8dd1\u4e86\u534a\u5929\u8def\u53e3\u6e34\u5f97\u5f88\uff0c\u53ef\u5426\u501f\u676f\u8336\u559d\u559d\uff1f\u201d \u3000\u3000\u795d\u79c0\u624d\u54e6\u4e86\u4e00\u58f0\uff0c\u8fde\u5fd9\u6b49\u7136\u966a\u7b11\u9053\uff1a\u201c\u8bf7\u6055\u8001\u673d\u5931\u793c\uff0c\u8fd8\u6ca1\u66ff\u4e8c\u4f4d\u5012\u8336\u3002\u2019\uff0c\u5c0f\u71d5\u5b50\u7b49 \u5012\u597d\u8336\u540e\uff0c\u7531\u684c\u4e0a\u53d6\u8d77\u4e00\u676f\uff0c\u76f4\u5411\u5ba2\u5385\u95e8\u53e3\u8d70\u53bb\uff0c\u53c8\u5934\u4e5f\u4e0d\u56de\u7684\u731b\u4e00\u626c\u624b\uff0c\u9053\uff1a\u201d\u63a5\u4f4f\uff01\u201c \u3000\u3000\u767d\u7fbd\u539f\u5730\u672a\u52a8\uff0c\u63a2\u81c2\u4e00\u6293\uff0c\u5df2\u628a\u4e00\u676f\u8336\u63a5\u5728\u624b\u4e2d\u3002 \u3000\u3000\u5947\u602a\u5f97\u5f88\uff0c\u6ee1\u6ee1\u7684\u4e00\u676f\u8336\u7adf\u7136\u534a\u6ef4\u4e5f\u4e0d\u66fe\u6e85\u51fa\u3002 \u3000\u3000\u767d\u7fbd\u5c06\u8336\u676f\u653e\u56de\u684c\u4e0a\uff0c\u518d\u5c06\u53f3\u624b\u4e2d\u6307\u63a2\u5165\u676f\u5185\u3002 \u3000\u3000\u4f46\u89c1\u4ed6\u8138\u8272\u4e00\u51dd\uff0c\u6df1\u6df1\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u4e0d\u4e45\u4e4b\u540e\uff0c\u90a3\u8336\u6c34\u4f3c\u5df2\u6eda\u817e\u5982\u6cb8\uff0c\u63a5\u7740\u6c34\u6c14\u987a\u7740\u624b \u6307\u76f4\u5347\u624b\u81c2\u5927\u7ea6\u534a\u76cf\u8336\u65f6\u5149\uff0c\u676f\u4e2d\u8336\u6c34\u5df2\u5168\u88ab\u5438\u5c3d\uff0c\u800c\u767d\u7fbd\u7684\u4e00\u6761\u53f3\u81c2\u8863\u8896\u5374\u4ece\u8896\u53e3\u6e7f\u5230\u4e0a \u81c2\u3002 \u3000\u3000\u795d\u79c0\u624d\u51e0\u66fe\u89c1\u8fc7\u8fd9\u7b49\u60ca\u4e16\u9a87\u4fd7\u7684\u5185\u529f\u771f\u529b\uff1f\u65e9\u5df2\u770b\u5f97\u76ee\u77aa\u53e3\u5446\u3002 \u3000\u3000\u767d\u7fbd\u8fd9\u624d\u53c8\u62ff\u8d77\u8336\u676f\uff0c\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u71d5\u8001\u5f1f\uff0c\u5e72\u561b\u7ed9\u6211\u4e00\u53ea\u7a7a\u676f\uff0c\u656c\u8336\u4e0d\u662f\u8fd9\u79cd\u656c \u6cd5\uff0c\u8fd8\u662f\u628a\u7a7a\u676f\u4ea4\u8fd8\u4f60\u5427\uff01\u201d \u3000\u3000\u8bf4\u7740\uff0c\u53f3\u8155\u5fae\u626c\uff0c\u518d\u5c06\u8336\u676f\u63b7\u56de\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u626c\u624b\u63a5\u4f4f\uff0c\u5374\u628a\u8336\u676f\u7d27\u7d27\u63e1\u4f4f\uff0c\u7acb\u523b\u4e00\u7f15\u7f15\u767d\u8272\u7c89\u672b\u7531\u6307\u7f1d\u98d8\u843d\u5730\u9762\u3002 \u3000\u3000\u53ea\u542c\u5c0f\u71d5\u5b50\u7b11\u9053\uff1a\u201c\u96be\u602a\u8fd9\u8336\u676f\u88c5\u4e0d\u4f4f\u8336\u6c34\uff0c\u539f\u6765\u8f7b\u8f7b\u4e00\u634f\u5c31\u7834\u4e86\u3002\u201d \u3000\u3000\u795d\u79c0\u624d\u8d8a\u53d1\u770b\u5f97\u80dc\u65e5\u7ed3\u820c\u3002 \u3000\u3000\u7a81\u89c1\u5c0f\u71d5\u5b50\u53f3\u624b\u968f\u610f\u4e00\u62ac\uff0c\u7acb\u523b\u4fbf\u6709\u4e00\u53ea\u58c1\u864e\u7531\u5929\u82b1\u677f\u6389\u843d\u5730\u4e0a\u3002 \u3000\u3000\u8fd9\u65f6\u5ffd\u6709\u4e00\u53ea\u9ebb\u96c0\u7531\u5c4b\u5916\u6a90\u524d\u98de\u8fc7\uff0c\u5c0f\u71d5\u5b50\u4f3c\u4e4e\u8fde\u770b\u90fd\u6ca1\u770b\uff0c\u53c8\u75be\u51fa\u4e00\u6307\u7834\u7a7a\u6233\u53bb\u3002 \u3000\u3000\u90a3\u9ebb\u96c0\u79bb\u5c0f\u71d5\u5b50\u7ad9\u8eab\u4e4b\u5904\u8db3\u6709\u4e24\u4e09\u4e08\u8fdc\uff0c\u7adf\u7136\u4e5f\u5e94\u624b\u800c\u843d\u3002 \u3000\u3000\u795d\u79c0\u624d\u7b80\u76f4\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u775b\uff0c\u534a\u664c\u4e4b\u540e\u624d\u5931\u58f0\u95ee\u9053\uff1a\u201c\u4e8c\u4f4d\u5c11\u4fa0\u83ab\u975e\u662f\u795e\u4ed9\u4e0b\u754c \u554a\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u7b11\u9053\uff1a\u201c\u9752\u5929\u767d\u65e5\uff0c\u90a3\u91cc\u6765\u7684\u795e\u4ed9\uff0c\u96d5\u866b\u5c0f\u6280\uff0c\u8001\u5148\u751f\u8fd8\u8bf7\u4e0d\u8981\u89c1\u7b11\uff01\u201d \u3000\u3000\u795d\u79c0\u624d\u547c\u4e86\u4e00\u53e3\u5927\u6c14\uff0c\u7ec8\u4e8e\u5b8c\u5168\u5b9a\u8fc7\u795e\u6765\u3002 \u3000\u3000\u4ed6\u671b\u7740\u4e24\u4eba\u6df1\u6df1\u4e00\u63d6\u9053\uff1a\u201c\u4e8c\u4f4d\u9ad8\u4eba\u5feb\u5feb\u5165\u5ea7\uff0c\u8001\u673d\u6709\u773c\u4e0d\u8bc6\u6cf0\u5c71\uff0c\u5931\u656c\u4e86\uff01\u201d \u3000\u3000\u4e8e\u662f\uff0c\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u518d\u56de\u5230\u539f\u4f4d\u3002 \u3000\u3000\u795d\u79c0\u624d\u6fc0\u52a8\u65e0\u6bd4\u7684\u9053\uff1a\u201c\u4e8c\u4f4d\u9ad8\u4eba\u51c6\u5907\u5982\u4f55\u5e2e\u8001\u673d\u7684\u5fd9\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u5148\u8bf7\u8001\u5148\u751f\u8bf4\u8bf4\uff0c\u90a3\u59d3\u767d\u7684\u5c71\u5be8\u8d3c\u9996\u662f\u5426\u548c\u8001\u5148\u751f\u4ee5\u53ca\u4ee4\u5a9b\u65e9\u5c31\u76f8\u8bc6\uff1f\u201d \u3000\u3000\u795d\u79c0\u624d\u6447\u5934\u9053\uff1a\u201c\u8001\u673d\u600e\u53ef\u80fd\u548c\u90a3\u822c\u4eba\u65e9\u5c31\u76f8\u8bc6\u5462\uff1f\u81f3\u4e8e\u5c0f\u5973\uff0c\u4e00\u5411\u662f\u8db3\u4e0d\u51fa\u6237\uff0c\u66f4\u4e0d \u53ef\u80fd\u548c\u4ed6\u4eec\u8ba4\u8bc6\uff0c\u4f55\u51b5\u90a3\u5c71\u5be8\u6210\u7acb\u4e0d\u4e45\uff0c\u5c71\u5be8\u91cc\u7684\u4e0a\u4e0a\u4e0b\u4e0b\u636e\u8bf4\u90fd\u662f\u5916\u5730\u6765\u7684\uff0c\u8001\u673d\u548c\u4ed6\u4eec \u6839\u672c\u6ca1\u6709\u6765\u5f80\u7684\u53ef\u80fd\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u53eb\u767d\u864e\u7684\u5be8\u4e3b\u600e\u4f1a\u8d77\u610f\u5f3a\u9738\u4ee4\u5a9b\u4e3a\u59bb\u5462\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u81ea\u7136\u662f\u542c\u8bf4\u5c0f\u5973\u54c1\u8c8c\u4e0d\u5dee\uff0c\u8fd8\u6709\u2026\u2026\u5c0f\u5973\u5f88\u53ef\u80fd\u5df2\u88ab\u4ed6\u4eec\u770b\u5230\u4e86\u3002\u201d \u3000\u3000\u201c\u600e\u6837\u770b\u5230\u7684\uff1f\u201d \u3000\u3000\u201c\u5c0f\u5973\u867d\u8bf4\u8db3\u4e0d\u51fa\u6237\uff0c\u4f46\u820d\u4e0b\u662f\u5c71\u5c45\u4eba\u5bb6\uff0c\u6709\u65f6\u5019\u96be\u514d\u4f1a\u5230\u95e8\u5916\u7ad9\u4f1a\u513f\uff0c\u88ab\u4ed6\u4eec\u7684\u4eba\u770b \u5230\u4e5f\u5e76\u975e\u4e0d\u53ef\u80fd\u3002\u201d \u3000\u3000\u201c\u90a3\u4f4d\u767d\u5be8\u4e3b\u53ef\u66fe\u5230\u5e9c\u4e0a\u4e0b\u8fc7\u8058\uff1f\u201d \u3000\u3000\u201c\u6709\u8fc7\uff0c\u767d\u5be8\u4e3b\u6d3e\u4eba\u9001\u6765\u4e8c\u767e\u4e24\u94f6\u5b50\uff0c\u8fd8\u6709\u4e00\u5323\u9996\u9970\u3002\u201d \u3000\u3000\u201c\u8001\u5148\u751f\u62d2\u6536\u624d\u5bf9\u554a\uff01\u201d \u3000\u3000\u201c\u8001\u673d\u5f53\u7136\u575a\u51b3\u4e0d\u80af\u6536\uff0c\u4f46\u4ed6\u4eec\u653e\u4e0b\u8058\u793c\uff0c\u53ea\u8bb2\u4e86\u8bdd\u4fbf\u8d70\u4e86\uff0c\u8001\u673d\u60f3\u9000\u4e5f\u65e0\u6cd5\u9000\u3002\u201d \u3000\u3000\u201c\u90a3\u4e9b\u8058\u793c\uff0c\u8001\u5148\u751f\u662f\u5426\u52a8\u7528\u8fc7\uff1f\u201d \u3000\u3000\u201c\u4e00\u76f4\u653e\u5728\u90a3\u91cc\uff0c\u539f\u5c01\u672a\u52a8\u3002\u201d \u3000\u3000\u201c\u96be\u9053\u8001\u5148\u751f\u5c31\u6ca1\u6709\u5bf9\u4ed6\u4eec\u8bb2\u51fa\u5fc5\u987b\u62d2\u5a5a\u7684\u7406\u7531\uff1f\u201d \u3000\u3000\u201c\u8bb2\u8fc7\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u7406\u7531\uff1f\u201d \u3000\u3000\u201c\u8001\u673d\u5bf9\u4ed6\u4eec\u8bf4\u5c0f\u5973\u5df2\u7ecf\u8ba2\u8fc7\u4eb2\uff0c\u5e74\u5e95\u5c31\u8981\u8fc7\u95e8\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u600e\u4e48\u8bb2\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u6839\u672c\u4e0d\u7ba1\u8fd9\u4e9b\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u4ee4\u5a9b\u662f\u5426\u771f\u7684\u5df2\u7ecf\u8ba2\u8fc7\u4eb2\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\uff0c\u5982\u679c\u6709\uff0c\u628a\u5c0f\u5973\u7684\u672a\u5a5a\u592b\u5a7f\u627e\u6765\uff0c\u4e8b\u60c5\u4e5f\u8bb8\u597d\u529e\u4e9b\u3002\u201d \u3000\u3000\u201c\u5728\u4e0b\u5012\u6709\u4e2a\u529e\u6cd5\u3002\u201d \u3000\u3000\u201c\u5c11\u5feb\u6709\u4ec0\u4e48\u529e\u6cd5\uff1f\u201d \u3000\u3000\u201c\u8001\u5148\u751f\u53ef\u4ee5\u8fde\u591c\u642c\u8d70\uff0c\u968f\u4fbf\u8eb2\u5230\u54ea\u91cc\u53bb\uff0c\u4ed6\u4eec\u5b9a\u4e0d\u5bb9\u6613\u627e\u5230\u3002\u201d \u3000\u3000\u795d\u79c0\u624d\u6447\u6447\u5934\uff0c\u6df1\u6df1\u4e00\u53f9\u82e6\u7b11\u9053\uff1a\u201c\u8001\u673d\u600e\u4f1a\u6ca1\u60f3\u5230\u8fd9\u529e\u6cd5\uff0c\u53ef\u60dc\u8fd9\u529e\u6cd5\u5df2\u7ecf\u884c\u4e0d\u901a \u4e86\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u7728\u52a8\u7740\u4e24\u773c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u884c\u4e0d\u901a\uff1f\u5e9c\u4e0a\u4f3c\u4e4e\u5e76\u672a\u53d7\u5230\u76d1\u89c6\uff1f\u201d \u3000\u3000\u795d\u79c0\u624d\u518d\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u90a3\u4f4d\u767d\u5be8\u4e3b\u65e9\u5c31\u60f3\u5230\u8001\u673d\u4f1a\u8fd9\u6837\u505a\uff0c\u7adf\u628a\u5c0f\u513f\u63b3\u5230\u5c71\u5be8\u4f5c\u4eba\u8d28\u53bb \u4e86\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u4e0d\u77e5\u8001\u5148\u751f\u6709\u51e0\u4f4d\u4ee4\u90ce\u5343\u91d1\uff1f\u201d \u3000\u3000\u201c\u8001\u673d\u819d\u4e0b\u53ea\u6709\u4e00\u513f\u4e00\u5973\uff0c\u5c0f\u5973\u5341\u516b\uff0c\u72ac\u5b50\u53ea\u6709\u5341\u4e09\u5c81\u3002\u201d \u3000\u3000\u4ee4\u90ce\u662f\u88ab\u5f3a\u884c\u63b3\u8d70\u7684\uff1f\u201c \u3000\u3000\u201c\u4ed6\u4eec\u8bf4\u5c0f\u513f\u957f\u5f97\u806a\u660e\u6d3b\u6cfc\u53ef\u7231\uff0c\u8981\u628a\u4ed6\u5e26\u5230\u5c71\u5be8\u73a9\u73a9\uff0c\u5c31\u8fd9\u6837\u4fbf\u88ab\u4ed6\u4eec\u5f3a\u884c\u5e26\u8d70 \u4e86\u3002\u201d \u3000\u3000\u201c\u8001\u5148\u751f\u73b0\u5728\u6709\u4ee4\u90ce\u7684\u6d88\u606f\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u8001\u673d\u6599\u60f3\uff0c\u4ed6\u4eec\u4e00\u5b9a\u4f1a\u628a\u72ac\u5b50\u7167\u987e\u5f97\u5f88\u597d\uff0c\u8fd9\u4e00\u70b9\u8001\u673d\u7528\u4e0d\u7740\u6302\u5fc3\u3002\u201d \u3000\u3000\u8fd9\u65f6\u795d\u592b\u4eba\u5df2\u95fb\u58f0\u800c\u51fa\uff0c\u4e3b\u52a8\u8fdb\u5165\u5ba2\u5385\u3002 \u3000\u3000\u795d\u592b\u4eba\u6bd4\u795d\u79c0\u624d\u5e74\u8f7b\uff0c\u53ea\u6709\u56db\u5341\u51fa\u5934\u5e74\u7eaa\uff0c\u9762\u76ee\u59e3\u597d\uff0c\u7aef\u5e84\u79c0\u96c5\uff0c\u4e00\u770b\u4fbf\u77e5\u662f\u4f4d\u77e5\u4e66\u8fbe \u793c\u7684\u5973\u5b50\u3002 \u3000\u3000\u795d\u79c0\u624d\u8fde\u5fd9\u4e3a\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u4ecb\u7ecd\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u548c\u795d\u592b\u4eba\u89c1\u793c\u4e4b\u540e\uff0c\u4e5f\u62a5\u4e0a\u4e86\u59d3\u540d\u3002 \u3000\u3000\u795d\u592b\u4eba\u9053\uff1a\u201c\u4f60\u4eec\u521a\u624d\u6240\u8bb2\u7684\u8bdd\uff0c\u6211\u90fd\u542c\u5230\u4e86\uff0c\u820d\u4e0b\u73b0\u5728\u662f\u843d\u96be\u65f6\u671f\uff0c\u96be\u5f97\u8fd8\u6709\u50cf\u4e24\u4f4d \u516c\u5b50\u8fd9\u6837\u96ea\u4e2d\u9001\u70ad\u7684\u4eba\u3002\u201d \u3000\u3000\u795d\u79c0\u624d\u5fd9\u9053\uff1a\u201c\u4f60\u53ea\u542c\u5230\u4e86\u4e24\u4f4d\u516c\u5b50\u548c\u6211\u8c08\u8bdd\uff0c\u5374\u6ca1\u6709\u770b\u5230\u4e24\u4f4d\u516c\u5b50\u6240\u8868\u6f14\u7684\u795e\u6280\u3002\u201d \u3000\u3000\u795d\u592b\u4eba\u8bb6\u7136\u95ee\u9053\uff1a\u201c\u4e24\u4f4d\u516c\u5b50\u8868\u6f14\u8fc7\u4ec0\u4e48\u795e\u6280\uff1f\u201d \u3000\u3000\u795d\u79c0\u624d\u5148\u628a\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u521a\u624d\u70ab\u9732\u7684\u8eab\u624b\u53d9\u8ff0\u4e86\u4e00\u904d\u3002 \u3000\u3000\u4ed6\u63a5\u7740\u53c8\u8bf4\u9053\uff1a\u201c\u6211\u662f\u8bfb\u4e66\u4eba\uff0c\u4ece\u6ca1\u89c1\u8fc7\u8fd9\u79cd\u6280\u51fb\u529f\u592b\uff0c\u521a\u624d\u603b\u7b97\u5f00\u4e86\u773c\u754c\uff0c\u8fd9\u4e24\u4f4d\u516c \u5b50\u662f\u540d\u7b26\u5176\u5b9e\u7684\u4e00\u4ee3\u5947\u4fa0\uff0c\u65e9\u77e5\u9053\u5982\u6b64\uff0c\u8fde\u6211\u4e5f\u60f3\u5f03\u6587\u4ece\u6b66\u4e86\u3002\u5509\uff01\u53ef\u60dc\u8001\u4e86\uff0c\u5df2\u7ecf\u6765\u4e0d\u53ca \u5566\uff01\u201d \u3000\u3000\u795d\u592b\u4eba\u671b\u671b\u5c0f\u71d5\u5b50\uff0c\u518d\u671b\u671b\u767d\u7fbd\u3002 \u3000\u3000\u5979\u53e3\u4e2d\u8d5e\u9053\uff1a\u201c\u771f\u770b\u4e0d\u51fa\uff0c\u4e24\u4f4d\u516c\u5b50\u7adf\u6709\u8fd9\u4e48\u4e00\u8eab\u597d\u6b66\u827a\uff0c\u9999\u4ead\uff01\u82e5\u4f60\u4e5f\u6709\u8fd9\u822c\u7684\u597d\u8eab \u624b\uff0c\u90a3\u5c71\u8d3c\u600e\u6562\u6b3a\u4fae\u5230\u6211\u4eec\u5934\u4e0a\uff1f\u201d \u3000\u3000\u795d\u9999\u4ead\u6b49\u7136\u82e6\u7b11\u7740\u9053\uff1a\u201c\u592b\u4eba\u8bf4\u5f97\u5bf9\uff0c\u679c\u7136\u767e\u65e0\u4e00\u7528\u662f\u4e66\u751f\uff01\u201d \u3000\u3000\u5ffd\u542c\u795d\u592b\u4eba\u554a\u4e86\u58f0\u9053\uff1a\u201c\u6211\u5012\u60f3\u51fa\u4e00\u4e2a\u529e\u6cd5\uff0c\u4e0d\u77e5\u8fd9\u4f4d\u71d5\u516c\u5b50\u80af\u4e0d\u80af\u7b54\u5e94\uff1f\u201d \u3000\u3000\u795d\u9999\u4ead\u9053\uff1a\u201c\u592b\u4eba\u60f3\u51fa\u4ec0\u4e48\u529e\u6cd5\uff1f\u201d \u3000\u3000\u201c\u4f60\u4e0d\u662f\u544a\u8bc9\u90a3\u4e9b\u5c71\u8d3c\uff0c\u54b1\u4eec\u6708\u82f1\u5df2\u7ecf\u6709\u4e86\u5a46\u5bb6\u5417\uff1f\u201d \u3000\u3000\u201c\u544a\u8bc9\u4ed6\u4eec\u6709\u4ec0\u4e48\u7528\uff0c\u5230\u54ea\u91cc\u53bb\u627e\u672a\u6765\u7684\u5973\u5a7f\u51fa\u6765\u4f5c\u8bc1\uff1f\u201d \u3000\u3000\u201c\u73b0\u5728\u5c31\u6709\uff0c\u9999\u4ead\uff01\u4f60\u5c31\u6c42\u6c42\u71d5\u516c\u5b50\u5427\uff0c\u54b1\u4eec\u6708\u82f1\u548c\u71d5\u516c\u5b50\u5e74\u5c81\u76f8\u5f53\uff0c\u53ea\u8981\u71d5\u516c\u5b50\u80af\u7b54 \u5e94\uff0c\u51ed\u4ed6\u7684\u6b66\u529f\uff0c\u4e5f\u8bb8\u4e8b\u60c5\u8fd8\u6709\u633d\u56de\u7684\u4f59\u5730\u3002\u201d \u3000\u3000\u795d\u9999\u4ead\u4e00\u542c\u8fd9\u8bdd\u6709\u7406\uff0c\u6025\u6025\u671b\u5411\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u71d5\u5c11\u4fa0\uff0c\u65e2\u7136\u6709\u63f4\u6551\u8001\u673d\u5168\u5bb6\u4e4b\u5fc3\uff0c\u5c31\u7b54 \u5e94\u8fd9\u4ef6\u4e8b\u5427\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u4e0d\u89c9\u8138\u4e0a\u4e00\u70ed\uff0c\u663e\u51fa\u5927\u611f\u4e3a\u96be\u7684\u6a21\u6837\u3002 \u3000\u3000\u767d\u7fbd\u4fa7\u8138\u9053\uff1a\u201c\u53ea\u7ba1\u7b54\u5e94\u4e0b\u6765\uff0c\u4f55\u51b5\u5e76\u4e0d\u662f\u73a9\u771f\u7684\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u7d27\u76b1\u7740\u7709\u5934\u9053\uff1a\u201c\u6211\u5f53\u7136\u77e5\u9053\u8fd9\u53ea\u662f\u5728\u6f14\u620f\uff0c\u4f46\u603b\u8be5\u56de\u53bb\u7980\u660e\u5bb6\u7236\u548c\u6c5f\u53d4\u53d4\uff0c\u53ea \u8981\u4ed6\u4eec\u540c\u610f\uff0c\u6211\u8fd8\u6709\u4ec0\u4e48\u4e0d\u53ef\u4ee5\u7684\u3002\u201d \u3000\u3000\u795d\u9999\u4ead\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u539f\u6765\u4ee4\u5c0a\u5927\u4eba\u548c\u4ee4\u53d4\u4e5f\u5230\u4e86\uff0c\u4ed6\u4eec\u4e24\u4f4d\u5728\u54ea\u513f\uff1f\u201d \u3000\u3000\u767d\u7fbd\u62a2\u7740\u4ee3\u7b54\u9053\uff1a\u201c\u90fd\u5728\u6d1b\u9633\u57ce\u91cc\u3002\u201d \u3000\u3000\u795d\u9999\u4ead\u559c\u4e8e\u8272\u9053\uff1a\u201c\u90a3\u5c31\u8bf7\u4ed6\u4eec\u4e24\u4f4d\u4e5f\u4e00\u8d77\u5230\u5408\u4e0b\u6765\uff0c\u5bd2\u820d\u7a7a\u5c4b\u5f88\u591a\uff0c\u62db\u5f85\u5ba2\u4eba\u6700\u65b9\u4fbf \u4e0d\u8fc7\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u90a3\u8fb9\u7684\u4eba\u8fd8\u5f88\u591a\u3002\u201d \u3000\u3000\u201c\u592a\u597d\u4e86\uff0c\u8ba9\u4ed6\u4eec\u4e00\u8d77\u6765\uff0c\u8001\u673d\u8fd9\u5c31\u968f\u4e24\u4f4d\u5c11\u5feb\u8fdb\u57ce\u53bb\u8bf7\u3002\u201d \u3000\u3000\u795d\u9999\u4ead\u786e\u662f\u6253\u5fc3\u91cc\u9ad8\u5174\uff01 \u3000\u3000\u4ed6\u5df2\u89c1\u8bc6\u8fc7\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u7684\u8eab\u624b\uff0c\u5982\u4eca\u542c\u8bf4\u6d1b\u9633\u57ce\u5185\u8fd8\u6709\u4e0d\u5c11\u548c\u4ed6\u4eec\u4e00\u4f19\u7684\u4eba\uff0c\u4e0d\u6d88 \u8bf4\uff0c\u8fd9\u4e9b\u4eba\u5fc5\u5b9a\u4e5f\u90fd\u662f\u6b66\u529f\u4e86\u5f97\uff0c\u6709\u8fd9\u4e48\u591a\u7684\u9ad8\u624b\u76f8\u52a9\uff0c\u60f3\u6d88\u5f2d\u8fd9\u573a\u707e\u96be\uff0c\u4e5f\u8bb8\u4e0d\u662f\u96be\u4e8b\u3002 \u3000\u3000\u767d\u7fbd\u6447\u4e86\u6447\u624b\u9053\uff1a\u201c\u8001\u5148\u751f\u7528\u4e0d\u7740\u5ba2\u6c14\uff0c\u6211\u4eec\u56de\u53bb\u4ee5\u540e\uff0c\u5fc5\u5c06\u8001\u5148\u751f\u8fd9\u756a\u597d\u610f\u544a\u8bc9\u4ed6 \u4eec\uff0c\u4ed6\u4eec\u8981\u6765\u5c31\u4e00\u5b9a\u4f1a\u6765\uff0c\u5982\u679c\u4e0d\u51c6\u5907\u6765\uff0c\u8001\u5148\u751f\u518d\u8bf7\u4e5f\u4e0d\u53ef\u80fd\u8bf7\u5f97\u52a8\u3002\u201d \u3000\u3000\u795d\u9999\u4ead\u966a\u7740\u7b11\u9053\uff1a\u201c\u8fd8\u8bf7\u4e24\u4f4d\u5c11\u4fa0\u4e00\u5b9a\u8981\u5728\u4ed6\u4eec\u9762\u524d\u591a\u8bf4\u597d\u8bdd\u3002\u201d \u3000\u3000\u201c\u8001\u5148\u751f\u653e\u5fc3\uff0c\u6211\u4eec\u4e00\u5b9a\u5c3d\u529b\u3002\u201d \u3000\u3000\u201c\u90a3\u592a\u597d\u4e86\uff0c\u8001\u673d\u771f\u4e0d\u77e5\u8be5\u600e\u4e48\u8c22\u8c22\u4e24\u4f4d\uff01\u201d \u3000\u3000\u767d\u7fbd\u62db\u547c\u7740\u5c0f\u71d5\u5b50\u4e00\u540c\u8d77\u8eab\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u73b0\u5728\u5c31\u56de\u53bb\u5427\uff01\u201d \u3000\u3000\u795d\u9999\u4ead\u8fde\u5fd9\u8d77\u8eab\u9053\uff1a\u201c\u4e24\u4f4d\u4ec0\u4e48\u65f6\u5019\u518d\u6765\uff1f\u201d \u3000\u3000\u767d\u7fbd\u9053\uff1a\u201c\u65e2\u7136\u5df2\u7ecf\u51b3\u5b9a\u63d2\u624b\u7ba1\u8fd9\u4ef6\u4e8b\uff0c\u4e00\u5b9a\u4f1a\u518d\u6765\u3002\u201d \u3000\u3000\u201c\u6700\u597d\u662f\u4eca\u665a\u5c31\u6765\uff0c\u8001\u673d\u9a6c\u4e0a\u4ea4\u4ee3\u4f63\u4eba\u6574\u7406\u5f85\u5ba2\u623f\u95f4\u3002\u201d \u3000\u3000\u4ed6\u53c8\u671b\u5411\u795d\u592b\u4eba\u9053\uff1a\u201c\u4f60\u5c31\u5e26\u7740\u6708\u82f1\u4eb2\u81ea\u4e0b\u53a8\uff0c\u505a\u4e00\u684c\u62ff\u624b\u597d\u83dc\uff0c\u5f85\u4f1a\u513f\u6211\u518d\u547d\u8bb8\u6709\u7530 \u628a\u57cb\u5728\u540e\u56ed\u7684\u597d\u9152\u6316\u51fa\u4e00\u575b\u6765\u3002\u201d \u3000\u3000\u767d\u7fbd\u5fd9\u9053\uff1a\u201c\u8001\u5148\u751f\u5343\u4e07\u522b\u8fd9\u6837\uff0c\u6211\u4eec\u4eca\u665a\u662f\u5426\u80fd\u6765\uff0c\u8fd8\u4e0d\u4e00\u5b9a\u3002\u82e5\u6211\u4eec\u4e0d\u80fd\u6765\uff0c\u60a8\u5c82 \u4e0d\u662f\u767d\u51c6\u5907\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e00\u5b9a\u8981\u6765\uff0c\u4e00\u5b9a\u8981\u6765\uff01\u4e07\u4e00\u57ce\u91cc\u7684\u5927\u4fa0\u4eec\u4e0d\u80fd\u6765\uff0c\u4f60\u4eec\u4e24\u4f4d\u5374\u975e\u6765\u4e0d\u53ef\uff0c\u820d\u4e0b\u867d\u662f\u5c71 \u5c45\uff0c\u79bb\u57ce\u5e76\u4e0d\u592a\u8fdc\uff0c\u52a1\u5fc5\u522b\u8ba9\u8001\u673d\u5931\u671b\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u8d76\u56de\u6d1b\u9633\u4e2d\u5dde\u5ba2\u6808\uff0c\u7acb\u5373\u628a\u548c\u795d\u9999\u4ead\u592b\u5987\u89c1\u9762\u4ee5\u53ca\u8c08\u8bdd\u8be6\u60c5\u5411\u6c5f\u5343\u91cc\u3001\u738b \u5f64\u6c47\u62a5\u4e86\u4e00\u904d\u3002 \u3000\u3000\u738b\u5f64\u9887\u4e3a\u5174\u594b\u7684\u9053\uff1a\u201c\u592a\u597d\u4e86\uff0c\u4f60\u4eec\u4e24\u4eba\u8fd9\u4e00\u8d9f\u6536\u83b7\u5f88\u5927\uff0c\u503c\u5f97\u5609\u5956\u3002\u201d \u3000\u3000\u63a5\u7740\u518d\u5f81\u6c42\u6c5f\u5343\u91cc\u7684\u610f\u89c1\u9053\uff1a\u201c\u6c5f\u5144\uff0c\u65e2\u7136\u795d\u5bb6\u623f\u820d\u5f88\u591a\uff0c\u54b1\u4eec\u4e0d\u5982\u5c31\u642c\u5230\u90a3\u8fb9\u53bb\u4f4f\uff0c \u90a3\u8fb9\u9ad8\u5c71\u5be8\u8fd1\uff0c\u529e\u8d77\u4e8b\u6765\u4e5f\u65b9\u4fbf\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u6c89\u5fd6\u4e86\u534a\u664c\u9053\uff1a\u201c\u76ee\u524d\u6700\u597d\u8fd8\u662f\u4f4f\u5728\u5ba2\u6808\uff0c\u82e5\u5168\u4f53\u4f4f\u8fdb\u795d\u5bb6\uff0c\u96be\u514d\u4f1a\u88ab\u5bf9\u65b9\u7684\u773c \u7ebf\u53d1\u73b0\uff0c\u56e0\u4e3a\u54b1\u4eec\u4e0d\u53ef\u80fd\u8001\u8e72\u5728\u5c4b\u91cc\u4e0d\u6709\u6240\u884c\u52a8\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u5462\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u5012\u4e0d\u59a8\u4f4f\u8fc7\u53bb\uff0c\u548c\u54b1\u4eec\u8fd9\u8fb9\u968f\u65f6\u4fdd\u6301\u8054\u7cfb\uff0c\u8fd9\u5bf9\u54b1\u4eec\u662f\u4ef6\u5927\u5927\u6709\u5229\u7684\u4e8b\u3002\u201d \u3000\u3000\u201c\u8fd8\u6709\uff0c\u795d\u79c0\u624d\u8981\u5c0f\u71d5\u5b50\u5192\u5145\u4ed6\u7684\u672a\u6765\u5973\u5a7f\uff0c\u6c5f\u5144\u7b54\u4e0d\u7b54\u5e94\uff1f\u201d \u3000\u3000\u201c\u65e2\u7136\u662f\u5047\u5192\uff0c\u7b54\u5e94\u53c8\u6709\u4ec0\u4e48\u4e0d\u53ef\uff01\u201d \u3000\u3000\u6c5f\u5343\u91cc\u8bf4\u7740\uff0c\u4ea4\u4ee3\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u53ea\u7ba1\u7b54\u5e94\uff0c\u552f\u6709\u5982\u6b64\uff0c\u4e8b\u60c5\u624d\u65b9\u4fbf\u8fdb\u884c\uff0c\u4f60\u7236\u4eb2 \u90a3\u513f\u7531\u6211\u5411\u4ed6\u8bf4\u4e00\u58f0\u5c31\u6210\u4e86\u3002\u201d \u3000\u3000\u767d\u7fbd\u9053\uff1a\u201c\u65f6\u95f4\u4e0d\u65e9\uff0c\u65e2\u7136\u6c5f\u5927\u4fa0\u5df2\u7b54\u5e94\uff0c\u6211\u548c\u5c0f\u71d5\u5b50\u5c31\u8be5\u9a6c\u4e0a\u56de\u795d\u5bb6\u53bb\u4e86\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u4e5f\u8ddf\u7740\u9053\uff1a\u201c\u6c5f\u53d4\u53d4\u548c\u738b\u5927\u4eba\u4e0d\u59a8\u4e5f\u53bb\u4e00\u4e0b\uff0c\u4eba\u5bb6\u51c6\u5907\u4e86\u4e00\u684c\u9152\u5e2d\uff0c\u4e0d\u5403\u767d\u4e0d \u5403\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u671b\u4e86\u738b\u5f64\u4e00\u773c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u5e72\u8106\u54b1\u4eec\u4e24\u4e2a\u4e5f\u53bb\u4e00\u8d9f\uff0c\u80fd\u5148\u548c\u795d\u79c0\u624d\u8ba4\u8bc6\u8ba4\u8bc6\u4e5f \u597d\uff0c\u54b1\u4eec\u4e24\u4eba\u7528\u8fc7\u9152\u996d\u5c31\u56de\u6765\uff0c\u5c0f\u9053\u58eb\u548c\u5c0f\u71d5\u5b50\u5c31\u7559\u5728\u90a3\u8fb9\u3002\u201d \u3000\u3000\u738b\u5f64\u70b9\u5934\u9053\uff1a\u201c\u597d\uff0c\u5144\u5f1f\u8fd9\u5c31\u544a\u8bc9\u4e09\u516c\u4e3b\u4e00\u58f0\uff0c\u800c\u4e14\u4e5f\u8be5\u5bf9\u5176\u4ed6\u7684\u4eba\u4ea4\u4ee3\u4ea4\u4ee3\u3002\u201d \u3000\u3000\u5230\u8fbe\u795d\u5b85\u5927\u95e8\u5916\uff0c\u5927\u95e8\u662f\u865a\u63a9\u7740\u7684\uff0c\u795d\u9999\u4ead\u65e9\u5df2\u5728\u95e8\u5185\u7b49\u5019\u3002 \u3000\u3000\u6b64\u523b\uff0c\u4ed6\u5bf9\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u7684\u5230\u6765\uff0c\u62ff\u201c\u671b\u773c\u6b32\u7a7f\u201d\u6765\u5f62\u5bb9\u6700\u6070\u5f53\u4e0d\u8fc7\u3002 \u3000\u3000\u996d\u83dc\u65e9\u5df2\u505a\u597d\uff0c\u53ea\u7b49\u5ba2\u4eba\u5230\u6765\u3002 \u3000\u3000\u795d\u9999\u4ead\u5df2\u8db3\u8db3\u7b49\u4e86\u534a\u4e2a\u65f6\u8fb0\u4ee5\u4e0a\u3002 \u3000\u3000\u4ed6\u8fdc\u8fdc\u5c31\u671b\u89c1\u6765\u7684\u662f\u56db\u4eba\uff0c\u5185\u5fc3\u771f\u6709\u8bf4\u4e0d\u51fa\u7684\u5174\u594b\uff0c\u7acb\u5373\u7531\u95e8\u5185\u5feb\u6b65\u5730\u8fce\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u4e00\u8fb9\u5148\u548c\u5c0f\u71d5\u5b50\u3001\u767d\u7fbd\u62db\u547c\uff0c\u4e00\u8fb9\u95ee\u9053\uff1a\u201c\u8fd9\u4e24\u4f4d\u9ad8\u4eba\u662f\u8c01\uff1f\u71d5\u5c11\u4fa0\u548c\u81ea\u5c11\u4fa0\u5feb\u5feb\u66ff \u8001\u673d\u4ecb\u7ecd\u4ecb\u7ecd\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6307\u6307\u4e24\u4eba\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u6211\u6c5f\u53d4\u53d4\uff0c\u8fd9\u4f4d\u662f\u6211\u738b\u4f2f\u8111\u201d \u3000\u3000\u795d\u9999\u4ead\u4e0d\u89c9\u4e00\u8138\u832b\u7136\u3002 \u3000\u3000\u6c5f\u5e72\u91cc\u7684\u5e74\u7eaa\u663e\u7136\u8981\u6bd4\u738b\u5f64\u5927\uff0c\u5c0f\u71d5\u5b50\u4e3a\u4ec0\u4e48\u628a\u5e74\u7eaa\u5927\u7684\u79f0\u4e3a\u53d4\u53d4\uff0c\u5e74\u7eaa\u8f7b\u7684\u79f0\u547c\u4f2f\u4f2f \u5462\uff1f\u4f46\u662f\uff0c\u4ed6\u5374\u53c8\u4e0d\u65b9\u4fbf\u95ee\u3002 \u3000\u3000\u5176\u5b9e\u5c0f\u71d5\u5b50\u79f0\u6c5f\u5343\u91cc\u4e3a\u53d4\u53d4\uff0c\u662f\u4f9d\u7167\u5e08\u7236\u5929\u96f7\u8001\u4eba\u7684\u5e74\u9f84\u548c\u8f88\u5206\u800c\u6765\u7684\uff0c\u56e0\u4e3a\u5929\u96f7\u8001\u4eba \u548c\u6c5f\u5343\u91cc\u662f\u7ed3\u62dc\u5144\u5f1f\uff0c\u5929\u96f7\u8001\u4eba\u4e3a\u5144\uff0c\u6c5f\u5343\u91cc\u4e3a\u5f1f\uff0c\u5c0f\u71d5\u5b50\u81ea\u7136\u5e94\u8be5\u79f0\u6c5f\u5e72\u91cc\u4e3a\u53d4\u53d4\u3002\u81f3\u4e8e \u5bf9\u738b\u5f64\uff0c\u5219\u662f\u6839\u636e\u7236\u4eb2\u7684\u5e74\u9f84\u800c\u6765\uff0c\u738b\u5f64\u7684\u5e74\u7eaa\u6bd4\u71d5\u98de\u8981\u5927\u4e0a\u597d\u51e0\u5c81\u3002 \u3000\u3000\u795d\u9999\u4ead\u8fde\u5fd9\u5411\u6c5f\u5343\u91cc\u548c\u738b\u5f64\u6df1\u6df1\u4e00\u63d6\u9053\uff1a\u201c\u4e24\u4f4d\u5927\u4fa0\u8fb1\u4e34\uff0c\u771f\u4f7f\u5bd2\u820d\u84ec\u9769\u751f\u8f89\uff0c\u5feb\u5feb\u91cc \u8fb9\u8bf7\uff01\u201d \u3000\u3000\u4ed6\u56e0\u5df2\u89c1\u8bc6\u8fc7\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u7684\u7edd\u6280\uff0c\u56e0\u4e4b\u53ea\u8981\u662f\u548c\u4e24\u4eba\u4e00\u9053\u6765\u7684\uff0c\u4e00\u5f8b\u4ee5\u5927\u4fa0\u76f8\u79f0\u3002\u53ef \u89c1\u6587\u4eba\u66f4\u91cd\u89c6\u6b66\u4eba\u3002 \u3000\u3000\u5f53\u7136\uff0c\u8fd9\u4e0e\u4ed6\u6b64\u523b\u6709\u6c42\u4e8e\u4eba\u4e5f\u5927\u6709\u5173\u7cfb\u3002 \u3000\u3000\u8fdb\u5165\u5927\u95e8\uff0c\u795d\u9999\u4ead\u4fbf\u558a\u9053\uff1a\u201c\u6708\u82f1\u5979\u5a18\uff0c\u5feb\u5feb\u51fa\u6765\u8fce\u63a5\u8d35\u5bbe\uff0c\u9664\u4e86\u4e24\u4f4d\u5c11\u4fa0\uff0c\u53e6\u5916\u8fd8\u6709 \u4e24\u4f4d\u5927\u4fa0\u5149\u4e34\u3002\u201d \u3000\u3000\u795d\u592b\u4eba\u5f88\u5feb\u4fbf\u8fce\u51fa\u5929\u4e95\uff0c\u5fd9\u4e0d\u8fed\u7684\u5411\u6c5f\u5343\u91cc\u548c\u738b\u5f64\u65bd\u793c\u3002 \u3000\u3000\u8fdb\u5165\u5ba2\u5385\uff0c\u9152\u996d\u65e9\u5df2\u6446\u597d\uff0c\u679c\u7136\u5168\u662f\u73cd\u9990\u7f8e\u5473\uff0c\u8fde\u5403\u904d\u5929\u4e0b\u597d\u9152\u597d\u83dc\u7684\u6c5f\u5343\u91cc\u548c\u738b\u5f64\u90fd \u6709\u4e9b\u998b\u6d8e\u6b32\u6ef4\u3002 \u3000\u3000\u6c5f\u5e72\u91cc\u548c\u738b\u5f64\u4e0d\u4fbf\u7acb\u5373\u5165\u5e2d\uff0c\u5148\u5728\u53e6\u5916\u7684\u5ea7\u4f4d\u5750\u4e0b\u3002 \u3000\u3000\u795d\u9999\u4ead\u4e00\u8fb9\u4eb2\u81ea\u5012\u8336\uff0c\u4e00\u8fb9\u4ea4\u4ee3\u795d\u592b\u4eba\u9053\uff1a\u201c\u53bb\u628a\u6708\u82f1\u53eb\u51fa\u6765\u62dc\u89c1\u4e24\u4f4d\u5927\u4fa0\u548c\u4e24\u4f4d\u5c11 \u4fa0\uff01\u201d \u3000\u3000\u795d\u592b\u4eba\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u4e0d\u4e00\u4f1a\u513f\uff0c\u4fbf\u89c1\u795d\u592b\u4eba\u5e26\u7740\u4e00\u540d\u98ce\u59ff\u7ef0\u7ea6\u3001\u542b\u7f9e\u7b54\u7b54\u7684\u5c11\u5973\u8fdb\u5165\u5ba2\u5385\u3002 \u3000\u3000\u795d\u9999\u4ead\u5fd9\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u5c0f\u5973\u6708\u82f1\uff01\u201d \u3000\u3000\u63a5\u7740\u5429\u5490\u6708\u82f1\u9053\uff1a\u201c\u5feb\u5feb\u62dc\u89c1\u4e24\u4f4d\u5927\u4fa0\u548c\u4e24\u4f4d\u5c11\u4fa0\uff0c\u4ed6\u4eec\u5168\u662f\u54b1\u4eec\u5168\u5bb6\u7684\u6551\u547d\u6069\u4eba\uff01\u201d \u3000\u3000\u795d\u6708\u82f1\u53cc\u988a\u6cdb\u7740\u7ea2\u6655\uff0c\u4f4e\u5782\u7c89\u9888\uff0c\u4f9d\u8a00\u5411\u56db\u4eba\u5404\u81ea\u6df1\u6df1\u65bd\u4e86\u4e00\u793c\uff0c\u7136\u540e\u7ad9\u5728\u4e00\u65c1\u3002 \u3000\u3000\u8fd9\u4f4d\u795d\u79c0\u624d\u7684\u5343\u91d1\uff0c\u679c\u7136\u79f0\u5f97\u4e0a\u59ff\u8272\u51fa\u4f17\u7684\u7f8e\u5973\uff0c\u96be\u602a\u4f1a\u88ab\u5c71\u5be8\u8d3c\u9996\u5782\u6d8e\u4e09\u5c3a\uff0c\u8fdb\u800c\u8981 \u5f3a\u9738\u4e3a\u5a5a\u3002 \u3000\u3000\u5f53\u4f17\u4eba\u5728\u795d\u9999\u4ead\u7684\u62db\u8bf7\u4e4b\u4e0b\u5165\u5e2d\u540e\uff0c\u795d\u6708\u82f1\u4e5f\u81ea\u884c\u56de\u5230\u95fa\u623f\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u95fa\u9601\u5343\u91d1\u662f\u4e0d\u65b9\u4fbf\u516c\u7136\u966a\u5ba2\u5171\u9910\u7684\u3002 \u3000\u3000\u56db\u5ba2\u4e8c\u4e3b\uff0c\u795d\u9999\u4ead\u592b\u5987\u62db\u5f85\u5f97\u53ef\u8c13\u6bb7\u52e4\u5907\u81f3\u3002 \u3000\u3000\u9152\u8fc7\u4e09\u5de1\uff0c\u795d\u9999\u4ead\u9053\uff1a\u201c\u5bf9\u4e8e\u89e3\u6551\u820d\u4e0b\u8fd9\u6b21\u707e\u96be\uff0c\u4e0d\u77e5\u4e8c\u4f4d\u5927\u4fa0\u6709\u4f55\u6307\u793a\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u76ee\u524d\u8d3c\u9996\u5df2\u5c06\u4ee4\u90ce\u63b3\u53bb\u4f5c\u4eba\u8d28\uff0c\u5fc5\u987b\u5148\u6551\u51fa\u4ee4\u90ce\u624d\u662f\u4e0a\u7b56\u3002\u5426\u5219\uff0c\u6211\u4eec\u8fd9 \u4e9b\u4eba\u5982\u679c\u653e\u624b\u65bd\u4e3a\uff0c\u52bf\u5fc5\u6fc0\u6012\u5bf9\u65b9\uff0c\u5c4a\u65f6\u4ed6\u4eec\u5fc5\u4f1a\u4e0d\u62e9\u624b\u6bb5\uff0c\u4e07\u4e00\u4ee4\u90ce\u4e0d\u4fdd\uff0c\u7eb5\u7136\u6740\u4e86\u8d3c \u9996\uff0c\u4e5f\u4e8e\u4e8b\u65e0\u8865\u3002 \u3000\u3000\u795d\u9999\u4ead\u9894\u9996\u9053\uff1a\u201c\u6c5f\u5927\u4fa0\u8bf4\u5f97\u662f\uff0c\u73b0\u5728\u7b2c\u4e00\u6b65\u5fc5\u987b\u5148\u6551\u51fa\u5c0f\u513f\uff0c\u81f3\u4e8e\u5982\u4f55\u6551\u6cd5\uff0c\u6c5f\u5927\u4fa0 \u662f\u5426\u5df2\u6709\u8179\u6848\uff1f\u201d \u3000\u3000\u201c\u6c5f\u67d0\u6b63\u5728\u7b79\u601d\u826f\u7b56\uff0c\u597d\u5728\u4ee4\u90ce\u6b64\u523b\u5728\u5c71\u5be8\u5fc5\u5b9a\u9887\u53d7\u4f18\u5f85\uff0c\u4e5f\u4e0d\u5fc5\u6025\u5728\u4e00\u65f6\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u5b66\u751f\u62c5\u5fc3\u8d3c\u65b9\u518d\u5ea6\u524d\u6765\u903c\u5a5a\uff0c\u4e07\u4e00\u5bf9\u65b9\u575a\u6301\u8981\u5728\u8fd9\u4e00\u4e24\u5929\u975e\u628a\u5c0f\u5973\u5a36\u56de\u5c71\u5be8\u4e0d \u53ef\uff0c\u5b66\u751f\u53c8\u8be5\u5982\u4f55\u5e94\u4ed8\uff1f\u201d \u3000\u3000\u201c\u4e0d\u59a8\u5c3d\u91cf\u8bbe\u6cd5\u62d6\u5ef6\u3002\u201d \u3000\u3000\u201c\u5018\u5bf9\u65b9\u5f3a\u884c\u62a2\u4eb2\uff0c\u53c8\u5f53\u5982\u4f55\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u6307\u6307\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\uff0c\u7b11\u9053\uff1a\u201c\u6c5f\u67d0\u548c\u738b\u8001\u5f1f\u5df2\u51b3\u5b9a\u628a\u4ed6\u4eec\u4e24\u4e2a\u7559\u5728\u5e9c\u4e0a\uff0c\u4ee5\u4ed6\u4eec \u4e24\u4eba\u7684\u8eab\u624b\uff0c\u5c82\u80fd\u5bb9\u8d3c\u5bc7\u5c06\u4ee4\u5a9b\u62a2\u8d70\u3002\u201d \u3000\u3000\u201c\u5b66\u751f\u5df2\u89c1\u8bc6\u8fc7\u4e24\u4f4d\u5c11\u4fa0\u7684\u60ca\u4e16\u6b66\u529f\uff0c\u76f8\u4fe1\u4ed6\u4eec\u5fc5\u6709\u80fd\u529b\u4fdd\u4f4f\u5c0f\u5973\u4e0d\u88ab\u62a2\u8d70\uff0c\u53ea \u662f\u2026\u2026\u201d \u3000\u3000\u795d\u5148\u751f\u6709\u8bdd\u8bf7\u8bf4\uff01\u201c \u3000\u3000\u201c\u5b66\u751f\u6df1\u76fc\u4e8c\u4f4d\u5927\u4fa0\u4e5f\u80fd\u4f4f\u5230\u5408\u4e0b\u6765\uff0c\u636e\u8bf4\u8d35\u65b9\u8fd8\u6709\u4e0d\u5c11\u5927\u4fa0\u5728\u6d1b\u9633\u57ce\u5185\uff0c\u5e0c\u671b\u4ed6\u4eec\u4e5f \u80fd\u4e00\u8d77\u4f4f\u8fdb\u5bd2\u820d\u3002\u201d \u3000\u3000\u201c\u8fd9\u6837\u4e0d\u59a5\uff0c\u4eba\u591a\u4e86\u5fc5\u4f1a\u60f9\u4eba\u6ce8\u610f\uff0c\u5230\u90a3\u65f6\u53cd\u800c\u4e0d\u65b9\u4fbf\u884c\u52a8\u4e86\u3002\u201d \u3000\u3000\u795d\u592b\u4eba\u4e5f\u9053\uff1a\u201c\u6c5f\u5927\u4fa0\u8bf4\u5f97\u5f88\u5bf9\uff0c\u4e0d\u8fc7\u2026\u2026\u4f4f\u5728\u820d\u4e0b\u7684\u4e24\u4f4d\u5c11\u4fa0\u5fc5\u987b\u5e38\u548c\u57ce\u91cc\u4fdd\u6301\u8054\u7cfb \u624d\u6210\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u8fd9\u65b9\u9762\u8d24\u4f09\u4fea\u5c3d\u53ef\u653e\u5fc3\uff0c\u6211\u4eec\u57ce\u91cc\u7684\u4eba\u968f\u65f6\u4f1a\u524d\u6765\u652f\u63f4\uff0c\u5e76\u975e\u53ea\u8981\u4ed6\u4eec\u4e24 \u4e2a\u5e74\u8f7b\u4eba\u5355\u6253\u72ec\u6597\u7684\u3002\u201d \u3000\u3000\u5ffd\u542c\u795d\u9999\u4ead\u9053\uff1a\u201c\u71d5\u5c11\u4fa0\u7684\u4ee4\u5c0a\u4e3a\u4ec0\u4e48\u4eca\u665a\u6ca1\u6765\uff1f\u201d \u3000\u3000\u5728\u795d\u9999\u4ead\u7684\u60f3\u6cd5\uff0c\u5c0f\u71d5\u5b50\u7684\u7236\u4eb2\u5e94\u662f\u5e74\u9f84\u6700\u5927\u7684\uff0c\u4e5f\u5fc5\u662f\u8fd9\u4f19\u4eba\u4e2d\u4e3a\u9996\u7684\uff0c\u6709\u4ed6\u5728\u4fbf\u53ef \u5728\u5e2d\u95f4\u51b3\u5b9a\u4e00\u5207\u3002 \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u4ed6\u5e74\u7eaa\u5927\u4e86\uff0c\u4e0d\u60f3\u884c\u52a8\uff0c\u795d\u5148\u751f\u6709\u4e8b\u548c\u6211\u4eec\u5546\u8bae\u4fbf\u53ef\u3002\u201d \u3000\u3000\u4e00\u987f\u9152\u996d\u5403\u4e0b\u6765\uff0c\u65f6\u95f4\u5df2\u662f\u4e00\u66f4\u8fc7\u540e\u3002 \u3000\u3000\u738b\u5f64\u548c\u6c5f\u5e72\u91cc\u7acb\u523b\u544a\u8f9e\u3002 \u3000\u3000\u4e34\u8d70\u65f6\uff0c\u5e76\u4ea4\u4ee3\u4e86\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u51e0\u53e5\u8bdd\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u5219\u7559\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u795d\u9999\u4ead\u547d\u4eba\u6574\u7406\u51fa\u4e24\u4e2a\u623f\u95f4\uff0c\u4f46\u4e24\u4eba\u4e3a\u8054\u7edc\u548c\u7167\u5e94\u65b9\u4fbf\uff0c\u8fd8\u662f\u4f4f\u5728\u4e00\u5904\u3002 \u3000\u3000\u795d\u9999\u4ead\u592b\u5987\u7279\u522b\u5230\u4e24\u4eba\u623f\u95f4\u966a\u7740\u804a\u4e86\u4e00\u4f1a\u513f\uff0c\u624d\u56de\u623f\u800c\u53bb\u3002 \u3000\u3000\u7fcc\u65e5\u4e00\u65e9\uff0c\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u4fbf\u8d77\u4e86\u5e8a\uff0c\u5973\u4ec6\u4eba\u7acb\u523b\u9001\u6765\u76e5\u6d17\u7528\u6c34\u3002 \u3000\u3000\u5f53\u4e24\u4eba\u51fa\u4e86\u623f\u95f4\uff0c\u53ea\u89c1\u89c6\u9999\u4ead\u6b63\u5728\u9662\u5b50\u91cc\u6563\u6b65\u3002 \u3000\u3000\u201c\u4e24\u4f4d\u5c11\u4fa0\u8fd9\u4e48\u65e9\u5c31\u8d77\u6765\u4e86\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u591a\u7761\u4f1a\u513f\uff1f\u201d \u3000\u3000\u767d\u7fbd\u9053\uff1a\u201c\u5df2\u7ecf\u7761\u591f\u4e86\uff0c\u8001\u5148\u751f\u8981\u6563\u6b65\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u5230\u95e8\u5916\u8d70\u8d70\uff1f\u201d \u3000\u3000\u795d\u9999\u4ead\u82e6\u7b11\u9053\uff1a\u201c\u81ea\u4ece\u5408\u4e0b\u51fa\u4e86\u4e8b\u540e\uff0c\u8001\u673d\u5c31\u6ca1\u518d\u51fa\u95e8\uff0c\u8fde\u5927\u95e8\u4e5f\u662f\u5173\u95ed\u7684\uff0c\u4e24\u4f4d\u4e5f\u53ea \u597d\u59d4\u5c48\u4e9b\u4e86\uff0c\u6ca1\u6709\u8981\u7d27\u7684\u4e8b\uff0c\u5343\u4e07\u522b\u51fa\u53bb\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5ffd\u542c\u5916\u9762\u6709\u4eba\u6572\u95e8\u3002 \u3000\u3000\u795d\u9999\u4ead\u5fd9\u4f4e\u58f0\u9053\uff1a\u201c\u8fd9\u65f6\u5019\u6709\u4eba\u6765\uff0c\u5fc5\u662f\u4e0e\u5c71\u5be8\u6709\u5173\uff0c\u4e24\u4f4d\u5c11\u4fa0\u8bf7\u5230\u5c4b\u91cc\u8eb2\u4e00\u8eb2\uff0c\u8001\u673d \u51fa\u53bb\u5f00\u95e8\u3002\u201d \u3000\u3000\u767d\u7fbd\u9053\uff1a\u201c\u8001\u5148\u751f\u7528\u4e0d\u7740\u5bb3\u6015\uff0c\u6709\u6211\u4eec\u5728\uff0c\u8be5\u8bb2\u7684\u5c31\u5bf9\u4ed6\u4eec\u8bb2\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u62c9\u8d77\u5c0f\u71d5\u5b50\u4fbf\u8fdb\u5165\u623f\u95f4\uff0c\u5e76\u628a\u95e8\u5173\u4e0a\u3002 \u3000\u3000\u89c6\u9999\u4ead\u5306\u5306\u6253\u5f00\u5927\u95e8\uff0c\u679c\u7136\u662f\u5c71\u5be8\u91cc\u7684\u4e24\u540d\u5934\u76ee\u3002 \u3000\u3000\u4e24\u540d\u5934\u76ee\uff0c\u4e00\u540d\u59d3\u9ec4\uff0c\u4e00\u540d\u59d3\u8bb8\u3002\u4ed6\u4eec\u5df2\u6765\u8fc7\u795d\u5b85\u597d\u51e0\u6b21\uff0c\u65e9\u5c31\u548c\u795d\u9999\u4ead\u8ba4\u8bc6\uff0c\u4e5f\u6253\u8fc7 \u591a\u6b21\u4ea4\u9053\u3002 \u3000\u3000\u795d\u9999\u4ead\u5047\u4f5c\u8bb6\u7136\u7684\u95ee\u9053\uff1a\u201c\u4e24\u4f4d\u6709\u4e8b\u5417\uff1f\u201d \u3000\u3000\u9ec4\u5934\u76ee\u9053\uff1a\u201c\u6211\u4eec\u662f\u65e0\u4e8b\u4e0d\u767b\u4e09\u5b9d\u6bbf\uff0c\u795d\u8001\u7237\u5b50\uff0c\u54b1\u4eec\u5230\u91cc\u9762\u8c08\u5427\uff01\u201d \u3000\u3000\u201c\u8bf7\uff01\u201d \u3000\u3000\u795d\u9999\u4ead\u6b64\u523b\u5df2\u6709\u4e86\u540e\u53f0\u9760\u5c71\uff0c\u81ea\u7136\u4e0d\u4f3c\u4ece\u524d\u90a3\u822c\u80c6\u602f\uff0c\u4f46\u4ed6\u628a\u4e24\u540d\u5934\u76ee\u5f15\u8fdb\u5ba2\u5385\u540e\uff0c\u8fd8 \u662f\u4eb2\u81ea\u5012\u4e86\u8336\u3002 \u3000\u3000\u4e24\u540d\u5934\u76ee\u4e2d\u7684\u9ec4\u5934\u76ee\uff0c\u5e74\u7eaa\u8f83\u5927\uff0c\u8eab\u6750\u77ee\u80d6\uff0c\u8138\u5b54\u9ec4\u9ed1\uff0c\u53cc\u988a\u5168\u662f\u6a2a\u5411\uff0c\u5f88\u50cf\u662f\u6fa1\u5802\u91cc \u634f\u811a\u6413\u80cc\u51fa\u8eab\uff0c\u66f4\u50cf\u4e00\u53ea\u4e4c\u9f9f\u3002 \u3000\u3000\u8bb8\u59d3\u5934\u76ee\u5e74\u7eaa\u867d\u8f7b\u4e9b\uff0c\u4f46\u8111\u95e8\u4e0a\u5374\u79c3\u4e86\u4e00\u5927\u5757\uff0c\u957f\u5f97\u4e5f\u662f\u4e09\u5206\u4e0d\u50cf\u4eba\uff0c\u4e03\u5206\u5012\u4f3c\u9b3c\u3002 \u5367\u9f99\u751f\u300a\u71d5\u5b50\u4f20\u5947\u300b \u7b2c\u4e8c\u5341\u516b\u56de\u3000\u9f99\u6f6d\u864e\u7a74 \u3000\u3000\u4e24\u4eba\u5750\u4e0b\uff0c\u9ec4\u5934\u76ee\u559d\u4e86\u53e3\u8336\u9053\uff1a\u201c\u606d\u559c\u8001\u7237\u5b50\uff0c\u8d3a\u559c\u8001\u7237\u5b50\uff01\u201d \u3000\u3000\u795d\u9999\u4ead\u4e0d\u52a8\u58f0\u8272\u7684\u9053\uff1a\u201c\u8001\u673d\u559c\u4ece\u4f55\u6765\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u5be8\u4e3b\u660e\u5929\u5c31\u51c6\u5907\u5230\u5e9c\u4e0a\u8fce\u4eb2\u4e86\uff0c\u90a3\u65f6\u4f60\u8001\u4eba\u5bb6\u5c31\u662f\u6211\u4eec\u5be8\u4e3b\u7684\u8001\u6cf0\u5c71\uff0c\u8c01\u8fd8\u6562\u77a7 \u4e0d\u8d77\u4f60\u8001\u4eba\u5bb6\uff0c\u8fd9\u4e0d\u662f\u4e00\u559c\u5417\uff1f\u201d \u3000\u3000\u795d\u9999\u4ead\u51dd\u7740\u8138\u8272\u9053\uff1a\u201c\u90a3\u4e48\u4e8c\u4f4d\u6765\u662f\u2026\u2026\u201d \u3000\u3000\u9ec4\u5934\u76ee\u62bd\u52a8\u7740\u53cc\u988a\u7684\u6a2a\u8089\u9053\uff1a\u201c\u6211\u4eec\u662f\u5949\u547d\u524d\u6765\u62a5\u559c\u7684\uff0c\u597d\u8ba9\u8001\u7237\u5b50\u51c6\u5907\u4e00\u4e0b\uff0c\u514d\u5f97\u4e34 \u65f6\u6765\u4e0d\u53ca\u3002\u6211\u4eec\u5be8\u4e3b\u4e0d\u5e0c\u671b\u5e9c\u4e0a\u592a\u94fa\u5f20\uff0c\u7537\u65b9\u660e\u5929\u6765\u7684\u4eba\u4e0d\u591a\uff0c\u8001\u7237\u5b50\u53ea\u529e\u4e00\u684c\u9152\u98df\u5f85\u5ba2\u5c31 \u591f\u4e86\uff0c\u81f3\u4e8e\u5c71\u5be8\u65b9\u9762\u2026\u2026\u3002 \u3000\u3000\u201c\u5c71\u5be8\u65b9\u9762\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u201c\u5c71\u5be8\u65b9\u9762\u5374\u8981\u5927\u5927\u70ed\u95f9\u4e00\u756a\uff0c\u660e\u5929\u665a\u4e0a\u5f20\u706f\u7ed3\u5f69\uff0c\u5e2d\u5f00\u767e\u684c\u3002 \u3000\u3000\u8001\u7237\u5b50\u5e9c\u4e0a\u8fd9\u8fb9\u7684\u4eba\u4e5f\u8bf7\u4e00\u8d77\u8fc7\u53bb\uff0c\u4e5f\u597d\u8ba9\u6211\u4eec\u5be8\u4e3b\u4eb2\u81ea\u62db\u5f85\u3002\u201c \u3000\u3000\u795d\u9999\u4ead\u9759\u9759\u7684\u542c\u7740\uff0c\u6de1\u6de1\u7684\u9053\uff1a\u201c\u767d\u5be8\u4e3b\u8fd9\u756a\u597d\u610f\uff0c\u9887\u4ee4\u8001\u673d\u611f\u52a8\uff0c\u53ef\u60dc\u4ed6\u662f\u767d\u8d39\u5fc3\u673a \u4e86\u3002\u201d \u3000\u3000\u9ec4\u5934\u76ee\u4e24\u53ea\u9f20\u773c\u731b\u7728\u4e86\u51e0\u4e0b\uff0c\u9053\uff1a\u201c\u8001\u7237\u5b50\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u201c\u8001\u673d\u5f53\u521d\u5c31\u5bf9\u4e8c\u4f4d\u8bf4\u8fc7\uff0c\u5c0f\u5973\u5df2\u7ecf\u6709\u4e86\u4eba\u5bb6\uff0c\u4e8c\u4f4d\u603b\u8be5\u8fd8\u8bb0\u5f97\u5427\uff1f\u201d \u3000\u3000\u9ec4\u5934\u76ee\u8138\u8272\u4e00\u53d8\u9053\uff1a\u201c\u8001\u7237\u5b50\uff0c\u83ab\u975e\u4f60\u60f3\u53d8\u5366\uff1f\u53ef\u60dc\u73b0\u5728\u5df2\u7ecf\u6765\u4e0d\u53ca\u4e86\uff0c\u5f53\u521d\u4f60\u90a3\u4e9b\u8bdd \u8fde\u4e09\u5c81\u7684\u5c0f\u5b69\u5b50\u4e5f\u90fd\u542c\u5f97\u51fa\u662f\u9a97\u4eba\u7684\uff0c\u5982\u679c\u4ee4\u5a9b\u771f\u6709\u4e86\u4eba\u5bb6\u4e86\uff0c\u4f60\u90a3\u4f4d\u672a\u6765\u7684\u59d1\u7237\u5728\u54ea\u91cc \u5462\uff1f\u201d \u3000\u3000\u201c\u771f\u662f\u5de7\u5f97\u5f88\uff0c\u8001\u673d\u7684\u8fd9\u4f4d\u672a\u6765\u5973\u5a7f\u6628\u665a\u5230\u5408\u4e0b\u6765\u4e86\uff01\u201d \u3000\u3000\u9ec4\u5934\u76ee\u554a\u4e86\u58f0\u9053\uff1a\u201c\u6709\u8fd9\u79cd\u4e8b\uff1f\u4f60\u628a\u4ed6\u53eb\u51fa\u6765\u8ba9\u6211\u4eec\u770b\u770b\uff01\u201d \u3000\u3000\u201c\u4e0d\u5fc5\u53eb\uff0c\u5728\u4e0b\u6765\u4e86\uff01\u201d \u3000\u3000\u9ec4\u5934\u76ee\u7684\u8bdd\u521a\u521a\u8bf4\u5b8c\uff0c\u5c0f\u71d5\u5b50\u5df2\u795e\u6001\u6602\u7136\u7684\u8d70\u8fdb\u5ba2\u5385\u3002 \u3000\u3000\u9ec4\u5934\u76ee\u4e24\u53ea\u9f20\u773c\u7d27\u76ef\u7740\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u5c0a\u9a7e\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6de1\u7136\u4e00\u7b11\u9053\uff1a\u201c\u5bb6\u5cb3\u521a\u624d\u8bf4\u8fc7\uff0c\u5728\u4e0b\u521a\u624d\u4e5f\u8bf4\u8fc7\uff0c\u4f55\u5fc5\u8fd8\u8981\u591a\u95ee\uff1f\u201d \u3000\u3000\u201c\u4f60\u8d35\u59d3\uff1f\u201d \u3000\u3000\u201c\u597d\u8bf4\uff0c\u59d3\u71d5\u3002\u201d \u3000\u3000\u201c\u5728\u54ea\u91cc\u9ad8\u5c31\uff1f\u201d \u3000\u3000\u201c\u95ee\u5f97\u592a\u591a\u4e86\uff0c\u5728\u4e0b\u6709\u5fc5\u8981\u544a\u8bc9\u4f60\u5417\uff1f\u201d \u3000\u3000\u201c\u4f60\u5230\u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u8fd9\u91cc\u662f\u5728\u4e0b\u7684\u5cb3\u5bb6\uff0c\u5728\u4e0b\u81ea\u5e94\u5e38\u6765\u5e38\u5f80\uff0c\u4f55\u5fc5\u5b9a\u8981\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9ec4\u5934\u76ee\u671b\u4e86\u8bb8\u5934\u76ee\u4e00\u773c\uff0c\u4f3c\u662f\u5f81\u6c42\u5bf9\u65b9\u610f\u89c1\uff0c\u8be5\u5982\u4f55\u5904\u7f6e\u9762\u524d\u8fd9\u5c0f\u5b50\uff1f\u4f9d\u7167\u9ec4\u3001\u8bb8\u4e8c\u4eba \u6b64\u523b\u7684\u5fc3\u610f\uff0c\u771f\u60f3\u5f53\u573a\u7ed3\u679c\u5c0f\u71d5\u5b50\u7684\u6027\u547d\uff0c\u4f46\u53c8\u89c9\u5f97\u5179\u4e8b\u4f53\u5927\uff0c\u5a5a\u4e8b\u662f\u5be8\u4e3b\u7684\uff0c\u81ea\u5df1\u72af\u4e0d\u7740 \u60f9\u8fd9\u79cd\u9ebb\u70e6\uff0c\u8fd8\u662f\u56de\u53bb\u8bf7\u793a\u4e00\u4e0b\u4e3a\u5999\u3002 \u3000\u3000\u5f53\u4e0b\uff0c\u8bb8\u5934\u76ee\u4e5f\u5411\u9ec4\u5934\u76ee\u4f7f\u4e86\u4e2a\u773c\u8272\u3002 \u3000\u3000\u9ec4\u5934\u76ee\u563f\u563f\u7b11\u9053\uff1a\u201c\u8001\u7237\u5b50\uff0c\u65e2\u7136\u60a8\u7684\u59d1\u7237\u5230\u4e86\uff0c\u6211\u4eec\u5b9e\u5728\u4e5f\u6ca1\u4ec0\u4e48\u8bdd\u8bf4\uff0c\u73b0\u5728\u6211\u4eec\u5c31 \u56de\u53bb\u5c06\u8fd9\u60c5\u5f62\u8f6c\u62a5\u5be8\u4e3b\u4e86\uff0c\u81f3\u4e8e\u5be8\u4e3b\u8981\u5982\u4f55\u5904\u7f6e\uff0c\u90a3\u662f\u4ed6\u7684\u4e8b\uff0c\u4e0e\u6211\u4eec\u65e0\u5e72\uff0c\u544a\u8f9e\u4e86\uff01 \u3000\u3000\u795d\u9999\u4ead\u8fd8\u662f\u4eb2\u9001\u4e24\u5934\u76ee\u51fa\u95e8\u3002 \u3000\u3000\u518d\u56de\u5230\u5ba2\u5385\u65f6\uff0c\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u5df2\u5728\u5ba2\u5385\u5019\u7740\u3002 \u3000\u3000\u795d\u9999\u4ead\u5e26\u7740\u6781\u5ea6\u4e0d\u5b89\u7684\u9053\uff1a\u201c\u4e8c\u4f4d\u5c11\u4fa0\uff0c\u4f60\u4eec\u770b\u4e8b\u60c5\u8be5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u5fc5\u987b\u5148\u770b\u770b\u8d3c\u9996\u7684\u53cd\u5e94\u518d\u8bf4\u3002\u201d \u3000\u3000\u201c\u53cd\u5e94\u662f\u53ef\u4ee5\u60f3\u89c1\u7684\u3002\u201d \u3000\u3000\u201c\u8001\u5148\u751f\u8ba4\u4e3a\u5bf9\u65b9\u4f1a\u6709\u4ec0\u4e48\u53cd\u5e94\uff1f\u201d \u3000\u3000\u201c\u4f9d\u8001\u673d\u731c\u6d4b\uff0c\u660e\u5929\u4ed6\u4eec\u5b9a\u4f1a\u5982\u671f\u524d\u6765\uff0c\u4e8b\u60c5\u8c08\u4e0d\u62e2\u540e\u5c31\u4f1a\u5f3a\u884c\u62a2\u4eb2\u3002\u201d \u3000\u3000\u201c\u8001\u5148\u751f\u7684\u60f3\u6cd5\u5f88\u6709\u53ef\u80fd\uff0c\u4f46\u4f60\u5c3d\u7ba1\u653e\u5fc3\uff0c\u6709\u6211\u4eec\u5728\uff0c\u8c01\u4e5f\u62a2\u4e0d\u8d70\u4f60\u7684\u5343\u91d1\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\uff0c\u4e00\u573a\u6d41\u8840\u6253\u6597\u603b\u662f\u514d\u4e0d\u4e86\u7684\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u9884\u6599\u6b7b\u4f24\u8fd8\u4e0d\u5728\u5c11\u6570\u3002\u201d \u3000\u3000\u201c\u82e5\u90a3\u4e48\u591a\u4eba\u90fd\u6b7b\u5728\u820d\u4e0b\uff0c\u8fd9\u2026\u2026\u201d \u3000\u3000\u795d\u9999\u4ead\u53c8\u663e\u51fa\u6781\u5ea6\u4e0d\u5b89\u3002 \u3000\u3000\u201c\u8fd9\u4e0d\u8fc7\u662f\u9884\u6599\u800c\u5df2\uff0c\u4e5f\u8bb8\u6211\u4eec\u4f1a\u8bbe\u6cd5\u628a\u4ed6\u4eec\u8bf1\u5230\u522b\u5904\u518d\u6740\u3002\u201d \u3000\u3000\u201c\u90a3\u6837\u6700\u597d\uff0c\u5982\u679c\u80fd\u4e0d\u6740\u4eba\u800c\u628a\u4e8b\u60c5\u529e\u59a5\uff0c\u5c31\u66f4\u662f\u597d\u4e0a\u52a0\u597d\u4e86\u3002\u201d \u3000\u3000\u767d\u7fbd\u9053\uff1a\u201c\u71d5\u8001\u5f1f\uff0c\u5179\u4e8b\u4f53\u5927\uff0c\u73b0\u5728\u662f\u65e9\u4e0a\uff0c\u65f6\u95f4\u8fd8\u5145\u88d5\u5f97\u5f88\uff0c\u54b1\u4eec\u662f\u5426\u8be5\u56de\u53bb\u4e00\u4eba\uff0c \u5411\u738b\u5927\u2026\u2026\u4e0d\uff0c\u738b\u524d\u8f88\u548c\u6c5f\u524d\u8f88\u62a5\u544a\uff0c\u4ee5\u4fbf\u4f9d\u4ed6\u4eec\u7684\u6307\u793a\u884c\u4e8b\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u70b9\u70b9\u5934\u9053\uff1a\u201c\u6709\u9053\u7406\uff0c\u90a3\u5c31\u7531\u6211\u8fdb\u57ce\u4e00\u8d9f\u5427\uff01\u201d \u3000\u3000\u767d\u7fbd\u5fd9\u9053\uff1a\u201c\u4e0d\u6210\uff0c\u4f60\u4e0d\u80fd\u8d70\u3002\u201d \u3000\u3000\u201c\u6211\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u8d70\uff1f\u201d \u3000\u3000\u767d\u7fbd\u89e3\u91ca\u9053\uff1a\u201c\u4f60\u73b0\u5728\u662f\u8001\u5148\u751f\u5e9c\u4e0a\u7684\u59d1\u7237\uff0c\u82e5\u5f85\u4f1a\u513f\u53c8\u6709\u5c71\u8d3c\u6765\uff0c\u4f60\u4e0d\u51fa\u9762\u600e\u4e48 \u6210\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u4e86\u70b9\u5934\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u4f60\u5c31\u5feb\u5feb\u56de\u53bb\uff01\u201d \u3000\u3000\u767d\u7fbd\u8d70\u540e\uff0c\u795d\u9999\u4ead\u642d\u8baa\u7740\u95ee\u9053\uff1a\u201c\u90a3\u8fb9\u7a76\u7adf\u6709\u591a\u5c11\u4eba\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u4eba\u662f\u4e0d\u5c11\uff0c\u4f46\u548c\u5c71\u8d3c\u7684\u4eba\u6570\u6bd4\u8f83\uff0c\u5374\u53c8\u5dee\u5f97\u5f88\u591a\u3002\u201d \u3000\u3000\u795d\u9999\u4ead\u9053\uff1a\u201c\u8fd9\u4e2a\u8001\u673d\u77e5\u9053\uff0c\u5175\u4e0d\u5728\u591a\u800c\u5728\u7cbe\uff0c\u53ea\u8981\u80fd\u4ee5\u4e00\u5f53\u5341\u3001\u4ee5\u5341\u5f53\u767e\u5c31\u6210\u4e86\uff0c\u6628 \u665a\u6765\u7684\u90a3\u4e24\u4f4d\u5927\u4fa0\u6b66\u529f\u4e00\u5b9a\u5f88\u9ad8\u4e86\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u7b11\u9053\uff1a\u201c\u90a3\u8fd8\u7528\u8bf4\uff01\u201d \u3000\u3000\u795d\u9999\u4ead\u53c8\u95ee\u9053\uff1a\u201c\u6bd4\u8d77\u4e24\u4f4d\u5c11\u4fa0\u5982\u4f55\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6de1\u6de1\u7684\u9053\uff1a\u201c\u4e0d\u80fd\u6bd4\u3002\u201d \u3000\u3000\u795d\u9999\u4ead\u8bb6\u5f02\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u539f\u6765\u4ed6\u4eec\u2026\u2026\u201d \u3000\u3000\u201c\u662f\u6211\u4eec\u4e0d\u80fd\u548c\u4ed6\u4eec\u4e24\u4f4d\u524d\u8f88\u6bd4\uff0c\u4ed6\u4eec\u4e00\u4e2a\u81f3\u5c11\u5f53\u6211\u4eec\u5341\u4e2a\u3002\u201d \u3000\u3000\u795d\u9999\u4ead\u60ca\u559c\u7684\u9053\uff1a\u201c\u90a3\u8fd8\u5f97\u4e86\uff0c\u4e24\u4f4d\u5c11\u4fa0\u7684\u8eab\u624b\u5df2\u7ecf\u662f\u4e3e\u4e16\u7f55\u89c1\u4e86\uff0c\u82e5\u90a3\u4e24\u4f4d\u5927\u4fa0\u518d\u80dc \u5341\u500d\uff0c\u7b80\u76f4\u5c31\u662f\u2026\u2026\u5c31\u662f\u795e\u4eba\u4e86\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u6240\u4ee5\u8001\u5148\u751f\u5c3d\u7ba1\u653e\u5fc3\uff0c\u4ee4\u5a9b\u7edd\u5bf9\u4e0d\u4f1a\u88ab\u62a2\u8d70\u3002\u201d \u3000\u3000\u201c\u5c11\u4fa0\u548c\u4ed6\u4eec\u8fd9\u4e00\u5e2e\u9ad8\u4eba\uff0c\u7a76\u7adf\u662f\u4ece\u54ea\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u662f\u7531\u4eac\u57ce\u6765\u7684\u3002\u201d \u3000\u3000\u201c\u4eac\u57ce\u6765\u7684\uff1f\u662f\u5426\u548c\u5b98\u65b9\u6709\u5173\uff1f\u201d \u3000\u3000\u201c\u6211\u53ea\u80fd\u544a\u8bc9\u8001\u5148\u751f\uff0c\u6709\u7684\u548c\u5b98\u65b9\u6709\u5173\uff0c\u6709\u7684\u548c\u5b98\u65b9\u65e0\u5173\uff0c\u8001\u5148\u751f\u7528\u4e0d\u7740\u518d\u8fdb\u4e00\u6b65\u7684 \u95ee\uff0c\u8fd9\u65b9\u9762\u7684\u4e8b\uff0c\u5728\u4e0b\u65e0\u6cd5\u8fdb\u4e00\u6b65\u7684\u56de\u7b54\u3002\u201d \u3000\u3000\u201c\u4e5f\u597d\uff0c\u8001\u673d\u4e0d\u95ee\u5c31\u662f\u3002\u201d\u8fd9\u65f6\uff0c\u795d\u592b\u4eba\u5df2\u6765\u8bf7\u4e24\u4eba\u7528\u65e9\u9910\u3002 \u3000\u3000\u795d\u9999\u4ead\u9053\uff1a\u201c\u71d5\u5c11\u4fa0\uff0c\u6211\u4eec\u5403\u996d\u5427\uff01\u522b\u7684\u4e8b\u7b49\u5403\u8fc7\u996d\u518d\u8bf4\u3002\u201d \u3000\u3000\u5c31\u5728\u8fd1\u5348\u65f6\u5206\uff0c\u9ec4\u5934\u76ee\u548c\u8bb8\u5934\u76ee\u53c8\u6765\u4e86\u3002 \u3000\u3000\u8fd9\u6b21\u5c0f\u71d5\u5b50\u4e3b\u52a8\u966a\u7740\u795d\u9999\u4ead\u548c\u5bf9\u65b9\u5728\u5ba2\u5385\u76f8\u89c1\u3002 \u3000\u3000\u9ec4\u5934\u76ee\u4e00\u5750\u4e0b\u5c31\u9053\uff1a\u201c\u6211\u4eec\u4e00\u56de\u53bb\u5c31\u5bf9\u5be8\u4e3b\u8bf4\u4e86\uff0c\u5be8\u4e3b\u5f88\u9ad8w\u201d \u3000\u3000\u8fd9\u201c\u5f88\u9ad8\u5174\u201d\u4e09\u5b57\uff0c\u53ea\u542c\u5f97\u795d\u9999\u4ead\u548c\u5c0f\u71d5\u5b50\u90fd\u5927\u611f\u610f\u5916\u3002 \u3000\u3000\u795d\u9999\u4ead\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u5be8\u4e3b\u9ad8\u5174\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9ec4\u5934\u76ee\u9053\uff1a\u201c\u8001\u7237\u5b50\u771f\u7684\u6709\u4f4d\u672a\u6765\u59d1\u7237\uff0c\u5be8\u4e3b\u5f53\u7136\u9ad8\u5174\uff0c\u800c\u4e14\u9664\u4e86\u9ad8\u5174\u4e4b\u5916\uff0c\u4e5f\u611f\u5230\u62b1 \u6b49\u3002\u201d \u3000\u3000\u201c\u5be8\u4e3b\u53c8\u62b1\u7684\u4ec0\u4e48\u6b49\uff1f\u201d \u3000\u3000\u201c\u5be8\u4e3b\u4e0d\u77e5\u4ee4\u5a9b\u5df2\u7ecf\u6709\u4e86\u5a46\u5bb6\uff0c\u5f53\u521d\u6267\u610f\u8981\u548c\u4ee4\u5a9b\u6210\u4eb2\uff0c\u5b9e\u5728\u662f\u505a\u4e86\u4e00\u4ef6\u5927\u5927\u4e0d\u8be5\u7684 \u4e8b\uff0c\u4ed6\u53c8\u600e\u80fd\u4e0d\u62b1\u6b49\u5462\uff1f\u201d \u3000\u3000\u201c\u90a3\u592a\u597d\u4e86\uff0c\u60f3\u4e0d\u5230\u4e8b\u60c5\u80fd\u8fd9\u4e48\u5706\u6ee1\u89e3\u51b3\uff0c\u4e8c\u4f4d\u8bf7\u7b49\u4e00\u4e0b\uff0c\u8001\u673d\u53bb\u53bb\u5c31\u6765\u3002\u201d \u3000\u3000\u9ec4\u5934\u76ee\u8f6c\u7740\u9f20\u773c\u9053\uff1a\u201c\u8001\u7237\u5b50\u8981\u5230\u54ea\u513f\u53bb\uff1f\u201d \u3000\u3000\u795d\u9999\u4ead\u7ad9\u8d77\u8eab\u9053\uff1a\u201c\u4e0a\u6b21\u5be8\u4e3b\u9001\u6765\u7684\u8058\u793c\uff0c\u662f\u4e00\u5323\u9996\u9970\u548c\u4e8c\u767e\u4e24\u94f6\u5b50\uff0c\u90fd\u539f\u5c01\u672a\u52a8\u653e \u7740\uff0c\u8001\u673d\u73b0\u5728\u5c31\u53bb\u53d6\u6765\uff0c\u70e6\u52b3\u4e8c\u4f4d\u539f\u58c1\u5949\u8fd8\u5be8\u4e3b\u3002\u201d \u3000\u3000\u9ec4\u5934\u76ee\u63a2\u624b\u4e00\u628a\u62c9\u4f4f\u795d\u9999\u4ead\uff0c\u9053\uff1a\u201c\u8001\u7237\u5b50\uff0c\u5be8\u4e3b\u5df2\u7ecf\u8bf4\u4e86\uff01\u201d \u3000\u3000\u201c\u5be8\u4e3b\u8bf4\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5be8\u4e3b\u8bf4\uff0c\u4ed6\u5f88\u5bf9\u4e0d\u4f4f\u8001\u7237\u5b50\uff0c\u90a3\u4efd\u8058\u793c\u9001\u4ee4\u5a9b\u505a\u8d3a\u793c\u4e86\u3002\u201d \u3000\u3000\u201c\u90a3\u600e\u4e48\u53ef\u4ee5\uff1f\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u4e0d\u53ef\u4ee5\uff1f\u8001\u7237\u5b50\u4f4f\u5728\u5c71\u4e0a\uff0c\u5c71\u5be8\u4e5f\u5728\u5c71\u4e0a\uff0c\u5f7c\u6b64\u7b49\u4e8e\u662f\u90bb\u5c45\uff0c\u90bb\u5c45\u95f4\u793c\u5c1a\u5f80 \u6765\uff0c\u5be8\u4e3b\u8fd9\u6837\u505a\u662f\u6700\u5e94\u8be5\u4e0d\u8fc7\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\uff0c\u8fd9\u4efd\u8d3a\u793c\u8001\u673d\u4e00\u5b9a\u4e0d\u80fd\u6536\uff0c\u8fd8\u662f\u4e8c\u4f4d\u5e26\u56de\u53bb\u7684\u597d\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u8001\u7237\u5b50\u575a\u4e0d\u80af\u6536\uff0c\u4e5f\u5fc5\u987b\u7b49\u6211\u4eec\u56de\u53bb\u5411\u5be8\u4e3b\u62a5\u544a\u4e86\u518d\u8bf4\uff0c\u6211\u4eec\u7edd\u4e0d\u4f1a\u73b0\u5728\u5c31\u5e26\u56de \u53bb\u7684\u3002\u201d \u3000\u3000\u8fd9\u65f6\u53ea\u542c\u8bb8\u5934\u76ee\u9053\uff1a\u201c\u9ec4\u9999\u4e3b\uff0c\u522b\u8ddf\u4ed6\u7f57\u55e6\u4e86\uff0c\u628a\u6b63\u4e8b\u8bb2\u51fa\u6765\u8981\u7d27\u3002\u201d \u3000\u3000\u9ec4\u5934\u76ee\u8fd9\u624d\u671b\u5411\u5c0f\u71d5\u5b50\uff0c\u95ee\u9053\uff1a\u201c\u8fd8\u6ca1\u8bf7\u95ee\u8fd9\u4f4d\u516c\u5b50\u8d35\u59d3\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6b63\u773c\u4e5f\u4e0d\u770b\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u59d3\u71d5\u3002\u201d \u3000\u3000\u9ec4\u5934\u76ee\u6bd7\u51fa\u4e24\u6392\u9ec4\u677f\u7259\uff0c\u5217\u5634\u4e00\u7b11\u9053\uff1a\u201c\u6211\u4eec\u5be8\u4e3b\u6709\u8bf7\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u54e6\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u8bf7\u8c01\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u662f\u8bf7\u71d5\u516c\u5b50\u3002\u201d \u3000\u3000\u201c\u5728\u4e0b\u548c\u8d35\u5be8\u4e3b\u7d20\u4e0d\u76f8\u8bc6\uff0c\u4ed6\u5e72\u561b\u8bf7\u6211\uff1f\u201d \u3000\u3000\u201c\u5be8\u4e3b\u505a\u9519\u4e86\u4e8b\uff0c\u81ea\u611f\u5f88\u5bf9\u4e0d\u4f4f\u516c\u5b50\uff0c\u5fc3\u91cc\u65e2\u60ed\u6127\u53c8\u96be\u8fc7\uff0c\u51b3\u5b9a\u5f53\u9762\u5411\u516c\u5b50\u8d54\u7f6a\u3002\u201d \u3000\u3000\u201c\u8d35\u5be8\u4e3b\u65e2\u7136\u8981\u8d54\u7f6a\uff0c\u5c31\u8be5\u5230\u8fd9\u91cc\u6765\u624d\u5bf9\u3002\u201d \u3000\u3000\u201c\u4e0d\uff0c\u5be8\u4e3b\u51b3\u5b9a\u5907\u4e00\u684c\u9152\u98df\u5bb4\u8bf7\u71d5\u516c\u5b50\uff0c\u5f7c\u6b64\u4ee5\u540e\u4e5f\u597d\u505a\u4e2a\u670b\u53cb\u3002\u201d \u3000\u3000\u201c\u4ed6\u51c6\u5907\u8bf7\u5728\u4e0b\u4ec0\u4e48\u65f6\u5019\u53bb\uff1f\u201d \u3000\u3000\u201c\u5982\u679c\u71d5\u516c\u5b50\u73b0\u5728\u6709\u7a7a\uff0c\u4e0d\u59a8\u9a6c\u4e0a\u5c31\u8d70\uff0c\u4e0d\u7136\u660e\u5929\u4e0a\u5348\u53bb\u4e5f\u53ef\u4ee5\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u5728\u4e0b\u4e0d\u8bc6\u8def\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u81ea\u7136\u4f1a\u5e26\u8def\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u5c31\u8bf7\u4e8c\u4f4d\u660e\u5929\u4e0a\u5348\u518d\u6765\u4e00\u8d9f\uff0c\u5728\u4e0b\u51b3\u5b9a\u63a5\u53d7\u8d35\u5be8\u4e3b\u8fd9\u6b21\u62db\u5f85\u3002\u201d \u3000\u3000\u4e0b\u5348\uff0c\u767d\u7fbd\u56de\u6765\u4e86\uff0c\u800c\u4e14\u8fd8\u5e26\u6765\u4e86\u4e09\u4eba\u3002 \u3000\u3000\u8fd9\u4e09\u4eba\u9664\u6c5f\u5343\u91cc\u548c\u738b\u5f64\u5916\uff0c\u53e6\u4e00\u4e2a\u8d6b\u7136\u662f\u601c\u82b1\u3002 \u3000\u3000\u539f\u6765\u6c5f\u5343\u91cc\u548c\u738b\u5f64\u542c\u4e86\u767d\u7fbd\u7684\u53d9\u8ff0\u540e\uff0c\u4fbf\u51b3\u5b9a\u4eb2\u6765\u52a9\u9635\u3002 \u3000\u3000\u4ed6\u4eec\u5728\u51fa\u53d1\u524d\uff0c\u5e76\u4e00\u540c\u8fdb\u5185\u5c06\u4e0a\u60c5\u7980\u544a\u4e09\u516c\u4e3b\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u7565\u4e00\u601d\u5fd6\uff0c\u4e3b\u52a8\u6253\u53d1\u601c\u82b1\u968f\u540c\u524d\u6765\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u548c\u738b\u5f64\u5fc3\u91cc\u6709\u6570\uff0c\u4e09\u516c\u4e3b\u5fc5\u5b9a\u662f\u60f3\u5fc5\u8981\u65f6\u53ef\u5c06\u601c\u82b1\u626e\u6210\u795d\u6708\u82f1\uff0c\u4ee5\u4fbf\u5e94\u4ed8\u767d\u864e\u3002 \u3000\u3000\u5176\u5b9e\u738b\u5f64\u548c\u6c5f\u5e72\u91cc\u4e5f\u60f3\u5230\u4e86\u8fd9\u4e00\u4e2a\u65b9\u6cd5\uff0c\u53ea\u56e0\u601c\u82b1\u662f\u4e09\u516c\u4e3b\u8eab\u8fb9\u7684\u4eba\uff0c\u4e0d\u4fbf\u63d0\u51fa\u8981\u6c42\u800c \u5df2\u3002 \u3000\u3000\u5728\u5ba2\u5385\u91cc\uff0c\u795d\u9999\u4ead\u592b\u5987\u4e00\u9f50\u524d\u6765\u4f5c\u8d54\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u53c8\u628a\u9ec4\u3001\u8bb8\u4e24\u540d\u5c71\u8d3c\u5934\u76ee\u7b2c\u4e8c\u6b21\u524d\u6765\u7684\u7ecf\u8fc7\u8bf4\u4e86\u4e00\u904d\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u7d27\u8e59\u7740\u53cc\u7709\u9053\uff1a\u201c\u4f60\u7b54\u5e94\u4e86\u4ed6\u4eec\u660e\u5929\u524d\u53bb\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u70b9\u70b9\u5934\u9053\uff1a\u201c\u82e5\u4e0d\u7b54\u5e94\uff0c\u5c82\u4e0d\u8868\u793a\u80c6\u602f\uff0c\u66f4\u4f55\u51b5\u4e0d\u5165\u864e\u7a74\uff0c\u7109\u5f97\u864e\u5b50\u3002\u80fd\u85c9\u8fd9\u7406 \u7531\u8fdb\u5165\u8d3c\u5bc7\u7684\u5c71\u5be8\uff0c\u6b63\u662f\u96be\u5f97\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u51dd\u7740\u8138\u8272\u9053\uff1a\u201c\u53ef\u60dc\u4f60\u53bb\u4e86\u4e4b\u540e\uff0c\u7a7a\u6709\u4e00\u8eab\u672c\u9886\u5374\u65e0\u6cd5\u65bd\u5c55\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u4e0d\u89e3\u7684\u9053\uff1a\u201c\u6c5f\u53d4\u53d4\u8fd9\u8bdd\u2026\u2026\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u60f3\u60f3\uff0c\u73b0\u5728\u795d\u516c\u5b50\u5728\u4ed6\u4eec\u624b\u91cc\u4f5c\u4eba\u8d28\uff0c\u4f60\u53bb\u5230\u5c71\u5be8\u540e\uff0c\u4e00\u65e6\u52a8\u8d77\u624b \u6765\uff0c\u5c82\u4e0d\u53cd\u800c\u9001\u4e86\u795d\u516c\u5b50\u4e00\u547d\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u987f\u89c9\u81ea\u5df1\u6709\u6b20\u8003\u8651\u3002 \u3000\u3000\u4ed6\u987f\u4e86\u987f\u9053\uff1a\u201c\u8fd8\u8bf7\u6c5f\u53d4\u53d4\u518d\u505a\u6307\u793a\uff01\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u660e\u5929\u4f60\u53bb\u95ef\u5c71\u5be8\uff0c\u6211\u5b8c\u5168\u540c\u610f\uff0c\u4f46\u4eca\u665a\u5fc5\u987b\u5148\u628a\u89c6\u516c\u5b50\u6551\u51fa\u6765\uff0c\u90a3\u65f6\u4f60\u5c31 \u53ef\u653e\u624b\u5927\u5e72\u4e00\u573a\u4e86\u3002\u201d \u3000\u3000\u5bf9\u5c0f\u71d5\u5b50\u6765\u8bf4\uff0c\u8fd9\u662f\u4e2a\u96be\u9898\uff0c\u4f46\u5374\u53c8\u4ee4\u4ed6\u8dc3\u8dc3\u6b32\u8bd5\u3002 \u3000\u3000\u4ed6\u671b\u4e86\u767d\u7fbd\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u53ef\u4e0d\u53ef\u4ee5\u966a\u6211\u53bb\u4e00\u8d9f\uff0c\u4e5f\u597d\u6709\u4e2a\u7167\u5e94\u3002\u201d \u3000\u3000\u767d\u7fbd\u9053\uff1a\u201c\u5f53\u7136\u53ef\u4ee5\uff0c\u8fd9\u662f\u54b1\u4eec\u5171\u540c\u7684\u4e8b\uff0c\u5e76\u975e\u4f60\u4e00\u4e2a\u4eba\u7684\u8d23\u4efb\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u4e5f\u9053\uff1a\u201c\u5c0f\u9053\u58eb\u8bf4\u5f97\u6709\u7406\uff0c\u4eca\u665a\u54b1\u4eec\u4e00\u8d77\u53bb\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u5fd9\u9053\uff1a\u201c\u6740\u9e21\u7109\u7528\u725b\u5200\uff0c\u6211\u548c\u767d\u7fbd\u4e24\u4eba\u5c31\u8db3\u591f\u4e86\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u5546\u8bae\u5546\u8bae\u518d\u8bf4\u5427\uff0c\u53cd\u6b63\u8981\u5929\u9ed1\u540e\u624d\u80fd\u884c\u52a8\uff0c\u65f6\u95f4\u8fd8\u65e9\u5f97\u5f88\u5462\uff01\u201d \u3000\u3000\u5165\u591c\u540e\uff0c\u5728\u201c\u666e\u6551\u5bfa\u201d\u7684\u6811\u6797\u91cc\u51fa\u73b0\u4e86\u4e09\u6761\u4eba\u5f71\u3002 \u3000\u3000\u9099\u5c71\u4e0a\u7684\u8fd9\u5ea7\u201c\u666e\u6551\u5bfa\u201d\u975e\u5e38\u6709\u540d\uff0c\u4f20\u8bf4\u4e2d\u201c\u897f\u53a2\u8bb0\u201d\u91cc\u5f20\u541b\u745e\u4e0e\u5d14\u83ba\u83ba\u7684\u6545\u4e8b\uff0c\u5c31\u662f \u53d1\u751f\u5728\u8fd9\u91cc\u3002 \u3000\u3000\u9c7c\u5316\u9f99\u5728\u9099\u5c71\u6210\u7acb\u7684\u5c71\u5be8\uff0c\u56e0\u8425\u5be8\u5efa\u7b51\u4e0d\u53ca\uff0c\u4fbf\u5f3a\u5360\u4e86\u201c\u666e\u6551\u5bfa\u201d\u505a\u4e3a\u5be8\u5740\uff0c\u597d\u5728\u8fd9\u5ea7 \u50e7\u5e99\u5360\u5730\u9887\u5e7f\uff0c\u623f\u820d\u4e0d\u4e0b\u6570\u767e\u95f4\u4e4b\u591a\uff0c\u5bfa\u5185\u539f\u6709\u7684\u50e7\u4eba\u5e76\u672a\u88ab\u9a71\u6563\u3002 \u3000\u3000\u5728\u5bfa\u5916\u6811\u6797\u5185\u7684\u4e09\u6761\u4eba\u5f71\uff0c\u5206\u522b\u662f\u5c0f\u71d5\u5b50\u3001\u767d\u7fbd\u4e0e\u601c\u82b1\u3002 \u3000\u3000\u4e0d\u6d88\u8bf4\uff0c\u4ed6\u4eec\u6b64\u6765\u76ee\u7684\u662f\u8981\u8425\u6551\u795d\u9999\u4ead\u7684\u513f\u5b50\u795d\u82f1\u6770\u51fa\u9669\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u662f\u8fd9\u6b21\u4efb\u52a1\u7684\u65e2\u5b9a\u4eba\u9009\u3002 \u3000\u3000\u81f3\u4e8e\u601c\u82b1\uff0c\u5219\u662f\u81ea\u544a\u594b\u52c7\u524d\u6765\u534f\u52a9\u7684\u3002 \u3000\u3000\u672c\u6765\uff0c\u6c5f\u5343\u91cc\u548c\u738b\u5f64\u6700\u521d\u4e0d\u540c\u610f\u5979\u6765\uff0c\u4f46\u540e\u6765\u4e00\u60f3\uff0c\u6709\u4e9b\u4e8b\u60c5\u5973\u4eba\u505a\u8d77\u6765\u53cd\u800c\u6bd4\u7537\u4eba\u65b9 \u4fbf\uff0c\u4e5f\u5c31\u7b54\u5e94\u4e0b\u6765\u3002 \u3000\u3000\u4f46\u79c1\u4e0b\u91cc\u5374\u90d1\u91cd\u53ee\u5631\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\uff0c\u5fc5\u987b\u5c3d\u91cf\u7ef4\u62a4\u601c\u82b1\u7684\u5b89\u5168\uff0c\u4e07\u4e00\u51fa\u4e86\u5dee\u9519\uff0c\u5c06\u65e0\u6cd5 \u5411\u4e09\u516c\u4e3b\u4ea4\u4ee3\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u4e09\u4eba\u6b63\u9690\u8eab\u5728\u6811\u6797\u5185\u89c2\u5bdf\u52a8\u9759\u3002 \u3000\u3000\u6b64\u523b\u624d\u4e0d\u8fc7\u662f\u521d\u66f4\u521a\u8fc7\uff0c\u5c71\u5be8\u5185\u7684\u5506\u7f57\u4eec\u5fc5\u5b9a\u8fd8\u662f\u4eba\u6765\u4eba\u5f80\uff0c\u4f3c\u4e4e\u8fd8\u4e0d\u662f\u91c7\u53d6\u884c\u52a8\u7684\u65f6 \u5019\u3002 \u3000\u3000\u767d\u7fbd\u9053\uff1a\u201c\u4e0d\u5fc5\u7740\u6025\uff0c\u65f6\u95f4\u8fd8\u65e9\u5f97\u5f88\uff0c\u54b1\u4eec\u5c31\u627e\u5904\u9690\u5bc6\u7684\u5730\u65b9\u597d\u597d\u4f11\u606f\u4f11\u606f\u518d\u8bf4\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u4e0d\u4ee5\u4e3a\u7136\u9053\uff1a\u201c\u4f9d\u6211\u770b\uff0c\u73b0\u5728\u624d\u662f\u6700\u9002\u5408\u91c7\u53d6\u884c\u52a8\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u201c\u2018\u4ec0\u4e48\u9053\u7406\uff1f\u201d \u3000\u3000\u201c\u73b0\u5728\u8fdb\u53bb\uff0c\u60f3\u627e\u795d\u8001\u5148\u751f\u7684\u513f\u5b50\u795d\u82f1\u6770\uff0c\u5fc5\u5b9a\u6bd4\u8f83\u65b9\u4fbf\uff0c\u82e5\u7b49\u5230\u591c\u6df1\u4eba\u9759\uff0c\u795d\u82f1\u6770\u7761 \u4e86\u4e4b\u540e\uff0c\u53c8\u5230\u54ea\u91cc\u53bb\u627e\u5462\uff1f\u201d \u3000\u3000\u201c\u4f60\u8bf4\u5f97\u867d\u7136\u6709\u7406\uff0c\u4f46\u82e5\u73b0\u5728\u8fdb\u53bb\uff0c\u91cc\u9762\u5230\u5904\u90fd\u662f\u4eba\uff0c\u6839\u672c\u884c\u52a8\u4e0d\u5f97\uff0c\u8fd9\u79cd\u4e8b\u5fc5\u987b\u6697\u6697 \u7684\u6765\uff0c\u660e\u76ee\u5f20\u80c6\u7684\u5e72\uff0c\u7edd\u975e\u4e0a\u7b56\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u4e0d\u60f3\u548c\u767d\u7fbd\u8fa9\u8bba\uff0c\u4e5f\u5c31\u4e0d\u518d\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u767d\u7fbd\u8f6c\u8fc7\u5934\u6765\u9053\uff1a\u201c\u601c\u82b1\u59d1\u5a18\uff0c\u4f60\u6765\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u601c\u82b1\u7b11\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4e0d\u6b22\u8fce\u6211\u6765\uff0c\u4f46\u6211\u81ea\u4fe1\u8fd8\u80fd\u5e2e\u4e0a\u4e00\u70b9\u5fd9\u3002\u201d \u3000\u3000\u201c\u8c01\u8bf4\u6211\u767d\u7fbd\u4e0d\u6b22\u8fce\u4f60\u6765\uff1f\u201d \u3000\u3000\u201c\u8fd9\u662f\u60f3\u5f53\u7136\u7684\u4e8b\uff0c\u7528\u4e0d\u7740\u8c01\u8bf4\uff01\u201d \u3000\u3000\u767d\u7fbd\u6014\u4e86\u4e00\u6014\u9053\uff1a\u201c\u6211\u662f\u8d8a\u542c\u8d8a\u7cca\u6d82\u4e86\uff1f\u201d \u3000\u3000\u601c\u82b1\u6487\u4e86\u6487\u6a31\u5507\u9053\uff1a\u201c\u6211\u770b\u4f60\u662f\u88c5\u7cca\u6d82\uff0c\u4f60\u662f\u4e0d\u662f\u51fa\u5bb6\u4eba\uff0c\u51fa\u5bb6\u4eba\u5f53\u7136\u4e0d\u5e0c\u671b\u548c\u5973\u4eba\u5728 \u4e00\u8d77\u4e86\u3002 \u3000\u3000\u767d\u7fbd\u4e0d\u89c9\u54d1\u7136\u5931\u7b11\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u601c\u82b1\u59d1\u5a18\uff0c\u4f60\u9519\u5566\uff01\u201d \u3000\u3000\u201c\u9519\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u51fa\u5bb6\u4eba\u867d\u7136\u6212\u8272\uff0c\u4f46\u5374\u96be\u514d\u4e0d\u597d\u8272\uff0c\u6709\u4e2a\u5730\u65b9\u548c\u5c1a\u5e99\u4e0e\u5c3c\u59d1\u5eb5\u6b63\u5bf9\u95e8\uff0c\u548c\u5c1a\u5c3c\u59d1\u5929\u5929 \u89c1\u9762\uff0c\u51e0\u4e4e\u6253\u6210\u4e00\u7247\uff0c\u7ed3\u679c\u5927\u5bb6\u8fd8\u4e0d\u662f\u8fc7\u5f97\u5feb\u4e50\u65e0\u6bd4\u3002\u8fd9\u5c31\u662f\u597d\u7684\u8bc1\u660e\uff0c\u4f55\u51b5\u6211\u2026\u2026 \u6211\u2026\u2026\u201d \u3000\u3000\u601c\u82b1\u53cc\u988a\u80c0\u5f97\u53c8\u7ea2\u53c8\u70ed\uff0c\u9053\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u4f55\u51b5\u7684\uff1f\u201d \u3000\u3000\u767d\u7fbd\u7b11\u9053\uff1a\u201c\u4f55\u51b5\u6211\u5df2\u662f\u88ab\u6b66\u5f53\u9010\u51fa\u95e8\u5899\u7684\u4eba\uff0c\u65e9\u5df2\u7b97\u4e0d\u5f97\u51fa\u5bb6\u4eba\u8eab\u4efd\u4e86\u3002\u4f60\u770b\uff0c\u6211\u7a7f \u7684\u5e76\u975e\u9053\u888d\uff0c\u5403\u7684\u4e5f\u662f\u8364\u8165\u4e0d\u5fcc\u3002\u8fd9\u4e24\u5929\uff0c\u795d\u8001\u5148\u751f\u5929\u5929\u5927\u8bf7\u5ba2\uff0c\u6211\u8fd8\u4e0d\u662f\u9e21\u9e2d\u9c7c\u8089\u7167\u5403\u4e0d \u8bef\u3002\u201d \u3000\u3000\u601c\u82b1\u8138\u80c0\u5f97\u66f4\u7ea2\uff0c\u6c14\u54bb\u54bb\u7684\u9053\uff1a\u201c\u90a3\u4e3a\u4ec0\u4e48\u5927\u5bb6\u4ecd\u7136\u53eb\u4f60\u5c0f\u9053\u58eb\uff1f\u201d \u3000\u3000\u767d\u7fbd\u8038\u4e86\u8038\u80a9\u9053\uff1a\u201c\u4ed6\u4eec\u8981\u90a3\u4e48\u53eb\uff0c\u6211\u4e5f\u6ca1\u529e\u6cd5\u3002\u603b\u4e4b\uff0c\u6211\u5e76\u6ca1\u4e0d\u6b22\u8fce\u4f60\u6765\uff0c\u8fd9\u4e00\u70b9\u4f60 \u8981\u7edd\u5bf9\u653e\u5fc3\u3002\u201d \u3000\u3000\u601c\u82b1\u987f\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u6cb9\u5634\u6ed1\u820c\u7684\u51fa\u5bb6\u4eba\uff0c\u96be\u602a\u4f1a\u88ab\u6b66\u5f53\u9010\u51fa\u95e8\u5899\u3002\u201d \u3000\u3000\u767d\u7fbd\u7b11\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u522b\u628a\u6211\u767d\u7fbd\u770b\u6210\u574f\u4eba\uff0c\u6211\u88ab\u6b66\u5f53\u9010\u51fa\u95e8\u5899\uff0c\u539f\u56e0\u4e0d\u4fbf\u5bf9\u4f60\u8bf4\u660e\uff0c\u4f46 \u5374\u7edd\u5bf9\u4e0d\u662f\u72af\u4e86\u8272\u6212\u3002\u653e\u5fc3\u5427\uff01\u4f60\u5c31\u662f\u548c\u6211\u5728\u4e00\u4e2a\u5c4b\u5b50\u91cc\uff0c\u751f\u6d3b\u4e09\u5929\u4e09\u591c\uff0c\u6211\u4e5f\u4e0d\u4f1a\u5bf9\u4f60\u600e \u6837\u3002\u201d \u3000\u3000\u201c\u4e0d\u8ddf\u4f60\u8bf4\u4e86\uff0c\u73b0\u5728\u529e\u6b63\u4e8b\u8981\u7d27\u3002\u201d \u3000\u3000\u767d\u7fbd\u6b63\u4e86\u4e00\u6b63\u8138\u8272\u9053\uff1a\u201c\u73b0\u5728\u8bf4\u6b63\u7ecf\u7684\uff0c\u4e09\u516c\u4e3b\u4f53\u5185\u7684\u866b\u6bd2\uff0c\u6700\u8fd1\u6709\u53d8\u5316\u6ca1\u6709\uff1f\u201d \u3000\u3000\u601c\u82b1\u6447\u6447\u5934\u9053\uff1a\u201c\u8981\u4e24\u5e74\u624d\u80fd\u957f\u6210\u9e3d\u86cb\u90a3\u4e48\u5927\uff0c\u79bb\u5bb6\u624d\u4e0d\u8fc7\u4e00\u4e2a\u591a\u6708\uff0c\u76ee\u524d\u5f53\u7136\u8fd8\u770b\u4e0d \u51fa\u4ec0\u4e48\u53d8\u5316\uff0c\u4f60\u95ee\u8fd9\u4e9b\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u6562\u95ee\u4e09\u516c\u4e3b\uff0c\u5f53\u7136\u53ea\u6709\u95ee\u4f60\u4e86\u3002\u201d \u3000\u3000\u201c\u4e09\u516c\u4e3b\u73b0\u548c\u5927\u5bb6\u5df2\u7ecf\u6253\u6210\u4e00\u7247\uff0c\u6709\u4ec0\u4e48\u4e0d\u6562\u95ee\u7684\u3002\u201d \u3000\u3000\u201c\u5c31\u662f\u6562\u95ee\uff0c\u4e5f\u6ca1\u673a\u4f1a\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4e09\u516c\u4e3b\u867d\u7136\u8868\u9762\u4e0a\u662f\u548c\u5927\u5bb6\u751f\u6d3b\u5728\u4e00\u8d77\uff0c\u4f46\u5927\u5bb6\u90fd\u5f88\u5c11\u80fd\u770b\u5230\u5979\u3002\u8d70\u8def\u7684\u65f6\u5019\uff0c\u5979\u662f \u5728\u8f66\u8f7f\u91cc\uff1b\u4f4f\u5ba2\u6808\uff0c\u5979\u53c8\u6574\u65e5\u5728\u623f\u5185\u95ed\u95e8\u4e0d\u51fa\uff1b\u8fde\u738b\u5927\u4eba\u90fd\u4e0d\u80fd\u5e38\u5e38\u770b\u5230\u5979\uff0c\u66f4\u4f55\u51b5\u6211\u4eec \u5462\uff1f\u201d \u3000\u3000\u601c\u82b1\u5401\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u5176\u5b9e\u4e09\u516c\u4e3b\u65e9\u5c31\u60f3\u51fa\u6765\u8d70\u8d70\uff0c\u5979\u6240\u4ee5\u95ed\u95e8\u4e0d\u51fa\uff0c\u662f\u5728\u7ec3\u4e00\u79cd\u72ec\u7279\u7684 \u6b66\u529f\u3002\u201d \u3000\u3000\u767d\u7fbd\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u7ec3\u7684\u662f\u4ec0\u4e48\u6b66\u529f\uff1f\u201d \u3000\u3000\u201c\u4e0a\u6b21\u5979\u7684\u5e08\u7236\u4e00\u9053\u98de\u8679\u82df\u5973\u4fa0\uff0c\u8fdb\u5bab\u591a\u65e5\uff0c\u53c8\u4f20\u6388\u4e86\u5979\u4e00\u79cd\u6b66\u529f\u3002\u81f3\u4e8e\u662f\u4ec0\u4e48\u6b66\u529f\uff0c \u5979\u4e0d\u8bb2\uff0c\u6211\u4e5f\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c31\u96be\u4e86\u2026\u2026\u201d \u3000\u3000\u767d\u7fbd\u7684\u8bdd\u5c1a\u672a\u8bf4\u5b8c\uff0c\u5ffd\u89c1\u5c0f\u71d5\u5b50\u8eab\u5f62\u6709\u5982\u79bb\u5f26\u6025\u5f29\uff0c\u95ea\u7535\u822c\u63a0\u51fa\u6811\u6797\uff0c\u731b\u5411\u8d70\u8fd1\u8fd9\u8fb9\u7684 \u4e00\u6761\u4eba\u5f71\u6251\u53bb\u3002 \u3000\u3000\u90a3\u4eba\u5f71\u5c1a\u672a\u6765\u5f97\u53ca\u53cd\u5e94\uff0c\u4fbf\u88ab\u5c0f\u71d5\u5b50\u4e00\u6307\u70b9\u5012\uff0c\u8fde\u54fc\u90fd\u6ca1\u54fc\u2014\u58f0\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u8fc5\u5feb\u7684\u5c06\u90a3\u4eba\u62d6\u8fdb\u6811\u6797\uff0c\u9053\uff1a\u201c\u73b0\u5728\u6211\u53ef\u4ee5\u6df7\u8fdb\u53bb\u4e86\uff01\u201d \u3000\u3000\u539f\u6765\u5c0f\u71d5\u5b50\u5df2\u770b\u6e05\u8fd9\u4eba\u662f\u540d\u5de1\u591c\u7684\u55bd\u7f57\uff0c\u4e5f\u8bb8\u662f\u4e2a\u5c0f\u5934\u76ee\u3002 \u3000\u3000\u4e3a\u4fbf\u4e8e\u8bc6\u522b\u548c\u6267\u884c\u4efb\u52a1\uff0c\u5c71\u5be8\u91cc\u6709\u4e00\u9879\u89c4\u5b9a\uff0c\u51e1\u662f\u65e5\u95f4\u5949\u547d\u5916\u51fa\u529e\u4e8b\u7684\uff0c\u591a\u534a\u7a7f\u7740\u4fbf \u8863\uff0c\u800c\u665a\u95f4\u8d1f\u8d23\u5de1\u591c\u7684\uff0c\u5219\u5fc5\u987b\u7a7f\u7740\u89c4\u5b9a\u7684\u5236\u670d\uff0c\u800c\u4e14\u8fd8\u8981\u60ac\u6302\u8170\u724c\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u5306\u5306\u8131\u4e0b\u5de1\u591c\u4eba\u7684\u5236\u670d\uff0c\u518d\u6458\u4e0b\u8170\u724c\u60ac\u5728\u81ea\u5df1\u8170\u4e0a\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4f4d\u5c31\u5728\u8fd9\u91cc\u7b49 \u6211\uff0c\u5982\u679c\u8fdb\u884c\u987a\u5229\uff0c\u6211\u4e5f\u8bb8\u4f1a\u5728\u534a\u4e2a\u65f6\u8fb0\u5185\u628a\u89c6\u5c0f\u5f1f\u6551\u56de\u3002\u201d \u3000\u3000\u767d\u7fbd\u9053\uff1a\u201c\u4f60\u4e00\u4eba\u884c\u4e8b\u4e0d\u4fbf\uff0c\u6211\u966a\u4f60\u4e00\u8d77\u8fdb\u53bb\uff01\u201d \u3000\u3000\u601c\u82b1\u9053\uff1a\u201c\u8fd8\u6709\u6211\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6447\u5934\u9053\uff1a\u201c\u670d\u88c5\u53ea\u6709\u4e00\u5957\uff0c\u8170\u724c\u4e5f\u53ea\u6709\u4e00\u9762\uff0c\u4f60\u4eec\u4e8c\u4f4d\u5982\u4f55\u53bb\u5f97\uff1f\u201d \u3000\u3000\u767d\u7fbd\u548c\u601c\u82b1\u53ea\u597d\u6253\u6d88\u5ff5\u5934\u3002 \u3000\u3000\u4f46\u767d\u7fbd\u8fd8\u662f\u4e0d\u653e\u5fc3\u7684\u9053\uff1a\u201c\u4e0d\u6015\u4e00\u4e07\uff0c\u4e07\u4e00\u4f60\u5728\u91cc\u9762\u51fa\u4e86\u5dee\u9519\uff0c\u6211\u548c\u601c\u82b1\u59d1\u5a18\u603b\u8be5\u8bbe\u6cd5 \u63a5\u5e94\u624d\u6210\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u60f3\u4e86\u60f3\u9053\uff1a\u201c\u82e5\u6211\u4e00\u4e2a\u65f6\u8fb0\u4e0d\u56de\u6765\uff0c\u4f60\u4eec\u5c31\u95ef\u8fdb\u53bb\uff0c\u90a3\u65f6\u5df2\u591c\u6df1\u4eba\u9759\uff0c\u53ea\u8981\u4f60\u4eec \u884c\u52a8\u9690\u5bc6\uff0c\u4e5f\u8bb8\u4e0d\u4f1a\u60ca\u52a8\u4ed6\u4eec\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u5c31\u8fd9\u4e48\u529e\u3002\u201d \u3000\u3000\u201c\u666e\u6551\u5bfa\u201d\u7684\u5927\u95e8\u5916\uff0c\u6709\u4e24\u540d\u4f69\u5200\u5927\u6c49\u8d1f\u8d23\u8b66\u6212\u3002 \u3000\u3000\u53e6\u5916\u8fd8\u6709\u4e00\u540d\u5e26\u73ed\u7684\u5934\u76ee\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u5f53\u7136\u4e0d\u8d70\u5927\u95e8\uff0c\u7531\u56f4\u5899\u4e00\u4fa7\u7ffb\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u56f4\u5899\u5185\u4fbf\u662f\u5e7f\u5927\u7684\u9662\u843d\u3002 \u3000\u3000\u867d\u5df2\u662f\u4e00\u66f4\u8fc7\u540e\uff0c\u9662\u5185\u4ecd\u6709\u4e0d\u5c11\u4eba\u6765\u5f80\u3002 \u3000\u3000\u73b0\u5728\uff0c\u5c0f\u71d5\u5b50\u65e2\u6709\u5de1\u591c\u670d\u88c5\uff0c\u53c8\u6709\u901a\u884c\u8170\u724c\uff0c\u5c3d\u53ef\u5927\u6a21\u5927\u6837\u7684\u5230\u5904\u8d70\u52a8\u3002 \u3000\u3000\u7a7f\u8fc7\u9662\u843d\u4fbf\u662f\u63a5\u8fde\u597d\u51e0\u95f4\u5927\u6bbf\uff0c\u5927\u6bbf\u5185\u4ecd\u6709\u50e7\u4eba\u5728\u8bf5\u7ecf\u3002 \u3000\u3000\u5bb6\u4eba\u548c\u76d7\u532a\u8d3c\u5bc7\u5171\u5904\uff0c\u8fd9\u5012\u662f\u5341\u5206\u7f55\u89c1\u7684\u4e8b\u3002 \u3000\u3000\u518d\u5f80\u540e\u8d70\uff0c\u4fbf\u662f\u4e00\u6392\u6392\u4e00\u680b\u680b\u7684\u623f\u820d\u3002 \u3000\u3000\u6809\u6bd4\u9cde\u6b21\uff0c\u679c\u7136\u4e0d\u4e0b\u6570\u767e\u95f4\u4e4b\u591a\u3002 \u3000\u3000\u6b64\u523b\uff0c\u5c0f\u71d5\u5b50\u6700\u60f3\u77e5\u9053\u7684\u662f\u5be8\u4e3b\u767d\u864e\u4f4f\u5728\u54ea\u91cc\uff1f\u53ea\u8981\u627e\u5230\u767d\u864e\u4f4f\u5904\uff0c\u4fbf\u4e0d\u96be\u627e\u5230\u795d\u82f1\u6770 \u4e86\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u5728\u4ed6\u9884\u6599\uff0c\u767d\u864e\u5fc5\u5b9a\u628a\u795d\u82f1\u6770\u63a7\u5236\u5728\u81ea\u5df1\u8eab\u8fb9\u3002 \u3000\u3000\u6b63\u8d70\u7740\uff0c\u5ffd\u7136\u4e00\u4eba\u8fce\u9762\u800c\u6765\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u770b\u5f97\u51fa\uff0c\u8fd9\u4eba\u5fc5\u5b9a\u4e5f\u662f\u5de1\u591c\u7684\u3002 \u3000\u3000\u679c\u7136\uff0c\u5f53\u53cc\u65b9\u8d70\u8fd1\u65f6\uff0c\u90a3\u4eba\u95ee\u9053\uff1a\u201c\u90a3\u8fb9\u6709\u60c5\u51b5\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u4ece\u5bb9\u4e0d\u8feb\u7684\u5e94\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u4f60\u90a3\u8fb9\u8fd8\u597d\u5427\uff01\u201d \u3000\u3000\u201c\u6211\u521a\u4ece\u5be8\u4e3b\u95e8\u5916\u7ecf\u8fc7\uff0c\u5be8\u4e3b\u4e5f\u6b63\u5728\u5ba2\u5385\u559d\u9152\uff0c\u8eab\u65c1\u8fd8\u6709\u4ed6\u90a3\u5c0f\u8205\u5b50\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u5185\u5fc3\u6697\u559c\uff0c\u60f3\u4e0d\u5230\u4e8b\u60c5\u7adf\u662f\u5982\u6b64\u987a\u5229\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u4ed6\u7ee7\u7eed\u5411\u524d\u8d70\u53bb\u3002 \u3000\u3000\u6beb\u65e0\u7591\u95ee\uff0c\u767d\u864e\u7684\u4f4f\u5904\u5fc5\u5728\u8fd9\u4eba\u8eab\u540e\uff0c\u4e5f\u5c31\u662f\u5c0f\u71d5\u5b50\u7684\u524d\u9762\u3002 \u3000\u3000\u521a\u8d70\u4e86\u51e0\u6b65\uff0c\u5ffd\u542c\u90a3\u4eba\u53eb\u9053\uff1a\u201c\u6162\u70b9\u8d70\uff0c\u6211\u6709\u8bdd\u95ee\u4f60\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6b62\u6b65\u56de\u8eab\u9053\uff1a\u201c\u8001\u5144\u8981\u95ee\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u597d\u50cf\u4ece\u6ca1\u89c1\u8fc7\u4f60\uff1f\u201d \u3000\u3000\u201c\u4e00\u6837\uff0c\u6211\u4e5f\u597d\u50cf\u6ca1\u89c1\u8fc7\u4f60\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u54ea\u4e00\u5802\u7684\uff1f\u201d \u3000\u3000\u201c\u9ec4\u9999\u4e3b\u7684\u624b\u4e0b\u3002\u201d \u3000\u3000\u201c\u65b0\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u521a\u6765\u6ca1\u51e0\u5929\u3002\u201d \u3000\u3000\u201c\u4ece\u54ea\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u9a6c\u5927\u4eba\u6d3e\u6211\u5230\u603b\u5be8\u4e3b\u90a3\u8fb9\u53bb\uff0c\u603b\u5be8\u4e3b\u53c8\u628a\u6211\u6d3e\u5230\u8fd9\u91cc\u6765\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c31\u96be\u602a\u4e86\uff0c\u8001\u5f1f\uff01\u4f60\u7684\u6765\u5934\u4e0d\u5c0f\uff0c\u660e\u5929\u6211\u8bf7\u559d\u9152\u3002\u201d \u3000\u3000\u201c\u591a\u8c22\u4e86\uff0c\u660e\u5929\u5c31\u7531\u5c0f\u5f1f\u505a\u4e1c\u5427\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u8bf4\u5b8c\u8bdd\uff0c\u8f6c\u8eab\u7ee7\u7eed\u5411\u524d\u8d70\u3002 \u3000\u3000\u5927\u9662\u5b50\u540e\u9762\u53c8\u6709\u82e5\u5e72\u5c0f\u9662\u843d\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u5904\u9662\u843d\u5185\uff0c\u6b63\u5c4b\u53d1\u51fa\u706f\u5149\uff0c\u95e8\u53e3\u5e76\u7ad9\u7740\u4e00\u540d\u4f69\u5200\u5927\u6c49\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u9884\u6599\u8fd9\u91cc\u5fc5\u662f\u767d\u864e\u7684\u4f4f\u5904\u3002 \u3000\u3000\u4ed6\u4e0d\u80fd\u7531\u6b63\u95e8\u8fdb\u53bb\uff0c\u7acb\u5373\u7ed5\u5230\u540e\u65b9\uff0c\u51c6\u5907\u8d8a\u5899\u800c\u5165\u3002\u521a\u8981\u7eb5\u8eab\u8dc3\u8d77\uff0c\u5ffd\u7136\u53d1\u73b0\u540e\u7a97\u53d1\u51fa \u706f\u5149\uff0c\u800c\u4e14\u6709\u8bf4\u8bdd\u58f0\u97f3\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u6084\u6084\u6765\u5230\u540e\u7a97\u5916\uff0c\u8eab\u4f53\u4fa7\u5411\u4e00\u8fb9\uff0c\u7531\u7a97\u9699\u5411\u91cc\u671b\u53bb\u3002 \u3000\u3000\u9996\u5148\u770b\u5230\u7684\u662f\u4e00\u4e2a\u76ae\u80a4\u767d\u6670\u3001\u7709\u6e05\u76ee\u79c0\u7684\u5341\u4e8c\u4e09\u5c81\u7684\u5b69\u5b50\u3002 \u3000\u3000\u63a5\u7740\u770b\u5230\u4e00\u540d\u8eab\u6750\u9ad8\u5927\u3001\u751f\u76f8\u72f0\u72de\u7684\u9ed1\u6c49\uff0c\u6b63\u5728\u636e\u6848\u81ea\u659f\u81ea\u914c\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u4e0d\u7531\u559c\u51fa\u671b\u5916\u3002 \u3000\u3000\u8c01\u90fd\u4e0d\u96be\u5224\u65ad\u51fa\uff0c\u8fd9\u4e24\u4eba\u5fc5\u662f\u795d\u82f1\u6770\u548c\u5c71\u5be8\u8d3c\u9996\u767d\u864e\u65e0\u7591\u3002 \u3000\u3000\u795d\u82f1\u6770\u770b\u6765\u5341\u5206\u6e05\u79c0\u53ef\u7231\uff0c\u7709\u76ee\u95f4\u9690\u7ea6\u53ef\u770b\u51fa\u9887\u4f3c\u89c6\u9999\u4ead\u7684\u795e\u60c5\u3002 \u3000\u3000\u81f3\u4e8e\u767d\u864e\uff0c\u76ae\u80a4\u9ed1\u5f97\u50cf\u6d82\u4e86\u4e00\u5c42\u9505\u5e95\u7070\uff0c\u8bf4\u4ed6\u662f\u7164\u70ad\u94fa\u91cc\u7684\u4e8c\u638c\u67dc\u8fd8\u5dee\u4e0d\u591a\uff0c\u54ea\u91cc\u662f\u767d \u864e\uff1f\u7b80\u76f4\u662f\u9ed1\u864e\u3002 \u3000\u3000\u7a97\u5916\u662f\u4e00\u6761\u6781\u7a84\u7684\u9ed1\u5df7\uff0c\u53c8\u662f\u767d\u864e\u7684\u5c45\u5904\u9644\u8fd1\uff0c\u53ef\u4ee5\u60f3\u89c1\u5e73\u5e38\u7edd\u4e0d\u4f1a\u6709\u4eba\u7ecf\u8fc7\u8fd9\u91cc\uff0c\u5c0f \u71d5\u5b50\u5927\u53ef\u4ee5\u653e\u5fc3\u7684\u5077\u7aa5\u4e0b\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u767d\u864e\u559d\u4e86\u4e00\u53e3\u9152\uff0c\u4e00\u8fb9\u54a7\u5634\u7b11\u9053\uff1a\u201c\u5c0f\u8001\u5f1f\uff0c\u4f60\u4e5f\u8fc7\u6765\u5403\u4e9b\uff0c\u4e0d\u559d\u9152\u6ca1\u5173\u7cfb\uff0c\u8fd9\u4e48 \u597d\u7684\u83dc\u4e0d\u5403\u591a\u53ef\u60dc\u554a\uff01\u201d \u3000\u3000\u795d\u82f1\u6770\u4e24\u773c\u76f4\u76ef\u7740\u767d\u864e\uff0c\u51b7\u51b7\u7684\u9053\uff1a\u201c\u6211\u4e0d\u5403\uff0c\u6211\u8981\u56de\u5bb6\uff0c\u5feb\u9001\u6211\u56de\u5bb6\uff01\u201d \u3000\u3000\u767d\u864e\u563f\u563f\u7b11\u9053\uff1a\u201c\u4f4f\u5728\u6211\u8fd9\u91cc\uff0c\u54ea\u4e00\u70b9\u4e0d\u597d\uff1f\u4e3a\u4ec0\u4e48\u8001\u662f\u5435\u7740\u8981\u56de\u5bb6\uff1f\u201d \u3000\u3000\u795d\u82f1\u6770\u8dfa\u811a\u9053\uff1a\u201c\u6211\u60f3\u6211\u7239\uff0c\u60f3\u6211\u5a18\uff0c\u60f3\u6211\u59d0\u59d0\u3002\u201d \u3000\u3000\u767d\u864e\u6478\u6478\u6ee1\u662f\u866c\u987b\u7684\u5634\u5df4\u9053\uff1a\u201c\u90a3\u5f88\u7b80\u5355\uff0c\u4f60\u59d0\u59d0\u660e\u5929\u5c31\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u59d0\u59d0\u600e\u4e48\u4f1a\u5230\u4f60\u4eec\u8fd9\u79cd\u5730\u65b9\u6765\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u65e9\u5c31\u5bf9\u4f60\u8bb2\u8fc7\u4e86\u5417\uff1f\u4f60\u59d0\u59d0\u8981\u5ac1\u7ed9\u6211\u3002\u201d \u3000\u3000\u201c\u80e1\u8bf4\uff0c\u6211\u59d0\u59d0\u7edd\u4e0d\u4f1a\u5ac1\u4f60\u8fd9\u79cd\u4eba\uff01\u201d \u3000\u3000\u201c\u6211\u662f\u8fd9\u91cc\u7684\u5be8\u4e3b\uff0c\u624b\u4e0b\u6709\u597d\u51e0\u767e\u4eba\uff0c\u4f60\u59d0\u59d0\u6765\u4e86\u4e4b\u540e\uff0c\u5c31\u662f\u8fd9\u91cc\u7684\u538b\u5be8\u592b\u4eba\uff0c\u9664\u4e86 \u6211\uff0c\u5979\u662f\u6700\u5927\u7684\uff0c\u5979\u9ad8\u5174\u8fd8\u6765\u4e0d\u53ca\u5462\uff01\u201d \u3000\u3000\u201c\u4f60\u4eec\u662f\u571f\u532a\u3001\u5f3a\u76d7\uff01\u6211\u59d0\u59d0\u4e0d\u4f1a\u5ac1\u7ed9\u571f\u532a\u3001\u5f3a\u76d7\u7684\u3002\u201d \u3000\u3000\u201c\u5c0f\u5bb6\u4f19\uff0c\u522b\u4e71\u9a82\uff0c\u82e5\u4e0d\u770b\u5728\u4f60\u662f\u6211\u7684\u5c0f\u8205\u5b50\uff0c\u6211\u5c31\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u8981\u600e\u4e48\u6837\uff1f\u8c01\u662f\u4f60\u7684\u5c0f\u8205\u5b50\uff1f\u201d \u3000\u3000\u201c\u4f60\u59d0\u59d0\u662f\u6211\u7684\u538b\u5be8\u592b\u4eba\uff0c\u4f60\u5f53\u7136\u662f\u6211\u7684\u5c0f\u8205\u5b50\u3002\u201d \u3000\u3000\u201c\u6211\u4e0d\u60f3\u542c\u4f60\u4e71\u8bb2\uff0c\u5feb\u5feb\u653e\u6211\u56de\u5bb6\uff01\u201d \u3000\u3000\u767d\u864e\u679c\u7136\u6682\u65f6\u4e0d\u518d\u8bb2\u8bdd\uff0c\u53c8\u559d\u4e86\u51e0\u676f\uff0c\u624d\u9053\uff1a\u201c\u5c0f\u8001\u5f1f\uff0c\u660e\u5929\u4f60\u59d0\u59d0\u6765\u4e86\u4ee5\u540e\uff0c\u6211\u4e00\u5b9a \u4f1a\u653e\u4f60\u56de\u5bb6\u3002\u201d \u3000\u3000\u795d\u82f1\u6770\u6709\u4e9b\u7126\u614c\u7684\u9053\uff1a\u201c\u4e0d\uff0c\u4f60\u4e0d\u80fd\u78b0\u6211\u59d0\u59d0\uff01\u201d \u3000\u3000\u767d\u864e\u4ef0\u8138\u6253\u4e2a\u54c8\u54c8\uff0c\u5403\u5403\u7b11\u9053\uff1a\u201c\u6211\u78b0\u5979\u7684\u65f6\u5019\uff0c\u7edd\u4e0d\u4f1a\u8ba9\u4f60\u770b\u5230\uff0c\u800c\u4e14\u90a3\u662f\u559c\u6b22\u5979\u624d \u78b0\u5979\u3002\u201d \u3000\u3000\u795d\u82f1\u6770\u7ea2\u7740\u8138\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u6211\u4e0d\u60f3\u770b\u4f60\u90a3\u526f\u5fb7\u6027\uff0c\u4f60\u65e2\u7136\u4e0d\u9001\u6211\u56de\u5bb6\uff0c\u6211\u8981\u7761\u89c9 \u4e86\u3002\u201d \u3000\u3000\u767d\u864e\u8fde\u5fd9\u79bb\u5ea7\u8fc7\u6765\uff0c\u62c9\u8d77\u795d\u82f1\u6770\u7684\u624b\uff0c\u9053\uff1a\u201c\u6765\uff0c\u6211\u9001\u4f60\u8fdb\u623f\u7761\u89c9\u53bb\uff01\u201d \u3000\u3000\u795d\u82f1\u6770\u7528\u529b\u4e00\u7529\u624b\u81c2\u9053\uff1a\u201c\u6211\u7684\u624b\u4e0d\u7528\u4f60\u62c9\uff0c\u6211\u81ea\u5df1\u4f1a\u8d70\uff01\u201d \u3000\u3000\u4ed6\u90a3\u70b9\u529b\u91cf\uff0c\u5982\u4f55\u80fd\u7529\u5f97\u5f00\u767d\u864e\u3002 \u3000\u3000\u767d\u864e\u7275\u7740\u795d\u82f1\u6770\uff0c\u5e76\u672a\u51fa\u5ba2\u5385\uff0c\u7acb\u5373\u4ece\u58c1\u95e8\u8fdb\u5165\u8fb9\u623f\u95f4\uff0c\u8fd8\u4ea4\u4ee3\u4e86\u51e0\u53e5\u8bdd\uff0c\u51fa\u6765\u4e4b\u540e\u628a \u623f\u95e8\u7531\u5916\u9762\u53cd\u6263\u4e0a\u3002 \u3000\u3000\u63a5\u7740\uff0c\u4ed6\u53c8\u56de\u5230\u539f\u4f4d\uff0c\u518d\u5403\u559d\u4e86\u4e00\u9635\u5b50\uff0c\u624d\u7531\u58c1\u95e8\u8fdb\u5165\u53f3\u8fb9\u623f\u95f4\u3002 \u3000\u3000\u539f\u6765\u795d\u82f1\u6770\u548c\u767d\u864e\u7684\u623f\u95f4\uff0c\u4e2d\u95f4\u53ea\u9694\u4e86\u4e00\u4e2a\u5ba2\u5385\uff0c\u800c\u4e14\u5fc5\u987b\u7ecf\u8fc7\u5ba2\u5385\u7684\u95e8\u624d\u80fd\u51fa\u53bb\uff0c\u7b49 \u4e8e\u767d\u864e\u5bf9\u795d\u82f1\u6770\u4eb2\u81ea\u76d1\u63a7\u3002 \u3000\u3000\u5c31\u7b97\u795d\u82f1\u6770\u80fd\u5077\u5077\u7531\u5ba2\u5385\u7684\u95e8\u51fa\u53bb\uff0c\u4f46\u8fd9\u5e62\u9662\u843d\u7684\u5927\u95e8\u5916\u6709\u4eba\u628a\u5b88\u7740\uff0c\u4e00\u4e2a\u5341\u4e8c\u4e09\u5c81\u7684 \u5c0f\u5b50\u53c8\u5982\u4f55\u9003\u8131\u5f97\u4e86\uff1f\u4e3a\u4e86\u4e0d\u60ca\u52a8\u767d\u864e\u548c\u5c71\u5be8\u6240\u6709\u7684\u4eba\uff0c\u5c0f\u71d5\u5b50\u4e0d\u6562\u9a6c\u4e0a\u884c\u52a8\u3002 \u3000\u3000\u4ed6\u53c8\u60f3\u5230\u4e86\u82e5\u5728\u8fd9\u91cc\u803d\u6401\u592a\u4e45\uff0c\u4e00\u65e6\u767d\u7fbd\u548c\u601c\u82b1\u56e0\u4e45\u65e0\u52a8\u9759\u800c\u95ef\u8fdb\u6765\u63a5\u5e94\uff0c\u53cd\u800c\u5f04\u5de7\u6210 \u62d9\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u5c0f\u71d5\u5b50\u7ffb\u8fc7\u540e\u9762\u56f4\u5899\uff0c\u518d\u56de\u5230\u5bfa\u5916\u6811\u6797\u3002 \u3000\u3000\u767d\u7fbd\u6b63\u548c\u601c\u82b1\u4f4e\u58f0\u804a\u5929\uff0c\u4e00\u89c1\u5c0f\u71d5\u5b50\u56de\u6765\uff0c\u5fd9\u95ee\u9053\uff1a\u201c\u600e\u4e48\u6837\uff1f\u662f\u5426\u67e5\u4e0d\u51fa\u795d\u5c0f\u5f1f\u4eba\u5728 \u54ea\u91cc\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u7b11\u9053\uff1a\u201c\u4e8b\u60c5\u8fdb\u884c\u5f97\u610f\u5916\u7684\u987a\u5229\uff0c\u53ea\u662f\u65f6\u673a\u5c1a\u672a\u6210\u719f\u800c\u5df2\u3002\u201d \u3000\u3000\u201c\u5feb\u8bf4\u8bf4\u770b\uff0c\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u968f\u5373\u628a\u7ecf\u8fc7\u8bf4\u4e86\u4e00\u904d\u3002\u4e00\u767d\u7fbd\u9053\uff1a\u201c\u679c\u7136\u8fdb\u884c\u5f97\u987a\u5229\uff0c\u5f85\u4f1a\u513f\u8981\u4e0d\u8981\u6211\u548c\u601c\u82b1\u5e2e \u5fd9\uff1f\u201d \u3000\u3000\u201c\u4f60\u4eec\u6700\u597d\u8fd8\u662f\u5728\u8fd9\u513f\u7b49\u7740\uff0c\u53ea\u662f\u6551\u4e00\u4e2a\u5c0f\u5b69\u5b50\u51fa\u6765\uff0c\u4eba\u591a\u4e86\u53cd\u800c\u788d\u4e8b\u3002\u201d \u3000\u3000\u201c\u8bf4\u7684\u4e5f\u662f\uff0c\u770b\u6765\u4eca\u665a\u8fd9\u573a\u529f\u52b3\u5168\u662f\u4f60\u4e00\u4eba\u7684\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u5982\u679c\u60f3\u8981\u529f\u52b3\uff0c\u56de\u53bb\u5c3d\u7ba1\u8bf4\u4eba\u662f\u4f60\u6551\u7684\uff0c\u597d\u5417\uff1f\u201d \u3000\u3000\u201c\u597d\u554a\uff01\u4f60\u7adf\u62ff\u8fd9\u79cd\u8bdd\u6765\u53d6\u7b11\u6211\uff0c\u65e9\u77e5\u5982\u6b64\uff0c\u5f53\u521d\u5c31\u8be5\u51b3\u5b9a\u7531\u4f60\u5192\u5145\u59d1\u7237\u3002\u201d \u3000\u3000\u201c\u795d\u8001\u5148\u751f\u8bf4\u8fc7\uff0c\u4f60\u7684\u5e74\u5c81\u76f8\u5f53\uff0c\u6211\u8001\u5566\uff01\u2019\u201d \u3000\u3000\u4e24\u4eba\u53c8\u5f00\u4e86\u4e00\u9635\u73a9\u7b11\uff01 \u3000\u3000\u5c0f\u71d5\u5b50\u624d\u53c8\u518d\u56de\u5230\u5bfa\u5185\u3002 \u3000\u3000\u8fd9\u65f6\u5df2\u662f\u4e8c\u66f4\u8fc7\u540e\uff0c\u5bfa\u5185\u663e\u5f97\u9759\u6084\u6084\u7684\uff0c\u9664\u5c11\u6570\u5de1\u591c\u7684\u55bd\u7f57\u548c\u5c0f\u5934\u76ee\u5916\uff0c\u5176\u4f59\u7684\u4eba\u5168\u5df2 \u5165\u7761\u4e86\u3002 \u3000\u3000\u767d\u864e\u6240\u4f4f\u4e4b\u9662\u843d\uff0c\u95e8\u5916\u6709\u55bd\u7f57\u5b88\u62a4\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u7684\u884c\u52a8\u539f\u5219\u662f\u7edd\u4e0d\u60ca\u52a8\u4efb\u4f55\u4eba\u3002 \u3000\u3000\u4ed6\u8f7b\u8f7b\u64ac\u5f00\u5ba2\u5385\u540e\u7a97\uff0c\u65e0\u58f0\u65e0\u606f\u7684\u8fdb\u5ba2\u5385\uff0c\u518d\u628a\u623f\u95e8\u5916\u53cd\u6263\u7684\u6263\u73af\u53d6\u4e0b\uff0c\u7136\u540e\u63a8\u95e8\u800c \u5165\u3002 \u3000\u3000\u7531\u4e8e\u6709\u6708\u5149\u81ea\u524d\u7a97\u5165\uff0c\u53ea\u89c1\u795d\u82f1\u6770\u6b63\u8eba\u5728\u5e8a\u4e0a\uff0c\u9f3b\u606f\u54bb\u54bb\u7684\u7761\u5f97\u6b63\u9163\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u4e00\u6307\u70b9\u4e86\u795d\u82f1\u6770\u7684\u7761\u7a74\uff0c\u518d\u5c06\u4ed6\u7531\u5e8a\u4e0a\u4e00\u624b\u62b1\u8d77\uff0c\u4ecd\u7531\u5ba2\u5385\u540e\u7a97\u51fa\u6765\uff0c\u8dc3\u51fa\u56f4 \u5899\uff0c\u5f88\u5feb\u4fbf\u56de\u5230\u6811\u6797\u3002 \u3000\u3000\u767d\u7fbd\u548c\u601c\u82b1\u65e9\u5df2\u7ad9\u7740\u5728\u90a3\u91cc\u7b49\u5019\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u54b1\u4eec\u9a6c\u4e0a\u56de\u53bb\u5427\uff01\u201d \u3000\u3000\u4e8e\u662f\uff0c\u795d\u82f1\u6770\u4ecd\u7531\u5c0f\u71d5\u5b50\u80cc\u7740\u3002 \u3000\u3000\u4e09\u4eba\u65bd\u5c55\u8f7b\u529f\uff0c\u4e0d\u8db3\u987f\u996d\u5de5\u592b\uff0c\u4fbf\u5df2\u5230\u8fbe\u795d\u5b85\u3002 \u3000\u3000\u795d\u9999\u4ead\u592b\u5987\u4ee5\u53ca\u89c6\u6708\u82f1\uff0c\u4e3a\u4e86\u7b49\u5019\u6d88\u606f\uff0c\u8c01\u90fd\u6ca1\u7761\u3002 \u3000\u3000\u5f53\u4ed6\u4eec\u4e00\u89c1\u88ab\u63b3\u8d70\u591a\u65e5\u7684\u795d\u82f1\u6770\u8131\u9669\u5f52\u6765\uff0c\u771f\u662f\u5174\u594b\u5f97\u65e0\u4ee5\u590d\u52a0\uff0c\u7acb\u5373\u5411\u5c0f\u71d5\u5b50\u4e09\u4eba\u6bb7 \u52e4\u9053\u8c22\u3002 \u3000\u3000\u534a\u664c\u4e4b\u540e\uff0c\u795d\u9999\u4ead\u624d\u8bb6\u7136\u95ee\u9053\uff1a\u201c\u71d5\u5c11\u4fa0\u628a\u4ed6\u4e00\u8def\u80cc\u6765\uff0c\u4ed6\u600e\u4e48\u8fd8\u7761\u4e0d\u9192\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u7b11\u9053\uff1a\u201c\u6211\u5df2\u70b9\u4e86\u4ed6\u7684\u7761\u7a74\uff0c\u4ed6\u5f53\u7136\u9192\u4e0d\u6765\u3002\u201d \u3000\u3000\u201c\u90a3\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u7acb\u5373\u4e3a\u795d\u82f1\u6770\u89e3\u5f00\u7a74\u9053\u3002 \u3000\u3000\u4ed6\u5411\u795d\u9999\u4ead\u89e3\u91ca\u9053\uff1a\u201c\u4ed6\u9a6c\u4e0a\u5c31\u9192\u4e86\uff0c\u8001\u5148\u751f\u548c\u592b\u4eba\u597d\u597d\u548c\u4ed6\u8c08\u8c08\u5427\uff0c\u6211\u4eec\u8be5\u56de\u623f\u7761\u89c9 \u4e86\u3002\u201d \u3000\u3000\u6b21\u65e5\uff0c\u795d\u592b\u4eba\u5e26\u7740\u5973\u4f63\u4eba\u63d0\u524d\u505a\u597d\u65e9\u9910\u3002 \u3000\u3000\u7531\u795d\u9999\u4ead\u966a\u7740\u56db\u7537\u4e00\u5973\u2014\u2014\u4e94\u4f4d\u8d35\u5bbe\u7528\u9910\u3002 \u3000\u3000\u540c\u65f6\uff0c\u4e5f\u5546\u8bae\u597d\u4eca\u65e5\u7684\u884c\u52a8\u6b65\u9aa4\u3002 \u3000\u3000\u996d\u540e\uff0c\u6c5f\u5343\u91cc\u3001\u738b\u5f64\u3001\u601c\u82b1\u90fd\u6682\u65f6\u8eb2\u5728\u623f\u95f4\u91cc\u3002 \u3000\u3000\u6628\u665a\u6551\u56de\u7684\u795d\u82f1\u6770\uff0c\u66f4\u662f\u8981\u8eb2\u8d77\u6765\u3002 \u3000\u3000\u53ea\u6709\u795d\u9999\u4ead\u3001\u5c0f\u71d5\u5b50\u3001\u767d\u7fbd\u4e09\u4eba\u7559\u5728\u5ba2\u5385\u91cc\u3002 \u3000\u3000\u5c71\u5be8\u6d3e\u51fa\u7684\u4eba\u5f88\u5feb\u4fbf\u5230\u4e86\uff0c\u53c8\u662f\u8bb8\u5934\u76ee\u548c\u9ec4\u5934\u76ee\u3002 \u3000\u3000\u8bb8\u5934\u76ee\u4e00\u8fdb\u5ba2\u5385\u4fbf\u9053\uff1a\u201c\u71d5\u516c\u5b50\u5df2\u7ecf\u51c6\u5907\u597d\u4e86\u5427\uff1f\u6211\u4eec\u5be8\u4e3b\u542c\u8bf4\u795d\u8001\u7237\u5b50\u7684\u59d1\u7237\u5230\u4e86\uff0c \u5fc3\u91cc\u597d\u9ad8\u5174\uff0c\u5df2\u51b3\u5b9a\u53d6\u6d88\u8fd9\u95e8\u5a5a\u4e8b\uff0c\u5e76\u8981\u548c\u71d5\u516c\u5b50\u505a\u4e2a\u670b\u53cb\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u663e\u5f97\u5f88\u9547\u5b9a\uff0c\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\u53ef\u51c6\u5907\u7684\uff0c\u73b0\u5728\u5c31\u53ef\u4ee5\u968f\u4e24\u4f4d\u5230\u5c71\u5be8\u53bb\u4e86\u3002\u201d \u3000\u3000\u9ec4\u5934\u76ee\u671b\u4e86\u767d\u7fbd\u4e00\u773c\uff0c\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u8c01\uff1f\u201d \u3000\u3000\u9ec4\u5934\u76ee\u9894\u9996\u9053\uff1a\u201c\u6211\u4eec\u5be8\u4e3b\u80fd\u591a\u62db\u5f85\u4e00\u4f4d\u8d35\u5bbe\uff0c\u771f\u662f\u592a\u597d\u4e86\uff01\u201d \u3000\u3000\u63a5\u7740\uff0c\u9ec4\u5934\u76ee\u5411\u89c6\u9999\u4ead\u62f1\u62f1\u624b\u9053\uff1a\u201c\u8001\u7237\u5b50\uff0c\u6211\u4eec\u73b0\u5728\u5c31\u5230\u5c71\u5be8\u53bb\uff0c\u60a8\u653e\u5fc3\uff0c\u6211\u4eec\u5be8\u4e3b \u4e00\u5b9a\u4f1a\u597d\u597d\u62db\u5f85\u7684\u3002\u201d \u3000\u3000\u795d\u9999\u4ead\u4e00\u9762\u70b9\u5934\uff0c\u4e00\u9762\u53ee\u549b\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4f4d\u89c1\u4e86\u5be8\u4e3b\uff0c\u5343\u4e07\u8981\u6709\u793c\u8c8c\uff0c\u9152 \u8981\u5c11\u559d\uff0c\u5c3d\u91cf\u65e9\u4e00\u70b9\u56de\u6765\u3002\u201d \u3000\u3000\u767d\u7fbd\u9053\uff1a\u201c\u8001\u4f2f\u653e\u5fc3\uff0c\u6211\u4eec\u4e0d\u4f1a\u6709\u4e8b\u7684\u3002\u201d \u3000\u3000\u795d\u9999\u4ead\u628a\u56db\u4eba\u9001\u51fa\u5927\u95e8\uff0c\u53c8\u53ee\u549b\u4e86\u51e0\u53e5\uff0c\u624d\u56de\u8eab\u628a\u95e8\u95e9\u597d\u3002 \u3000\u3000\u884c\u8d70\u5728\u5c71\u8def\u4e0a\uff0c\u767d\u7fbd\u6545\u610f\u642d\u8baa\u7740\u95ee\u9053\uff1a\u201c\u8d35\u5be8\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u79bb\u8fd9\u91cc\u8fd8\u6709\u591a\u5c11\u8def\uff1f\u201d \u3000\u3000\u9ec4\u5934\u76ee\u9053\uff1a\u201c\u672c\u5c71\u5be8\u6210\u7acb\u4e0d\u4e45\uff0c\u8425\u5be8\u4fee\u5efa\u4e0d\u53ca\uff0c\u76ee\u524d\u662f\u6682\u65f6\u501f\u4f4f\u5728\u666e\u6551\u5bfa\u91cc\u3002\u201d \u3000\u3000\u201c\u539f\u6765\u5982\u6b64\uff0c\u666e\u6551\u5bfa\u5728\u4e0b\u66fe\u53bb\u8fc7\uff0c\u4e0d\u8fc7\u90a3\u5df2\u662f\u597d\u51e0\u5e74\u524d\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd8\u6ca1\u8bf7\u95ee\u8fd9\u4f4d\u516c\u5b50\u8d35\u59d3\uff1f\u201d \u3000\u3000\u201c\u597d\u8bf4\uff0c\u655d\u59d3\u767d\u3002\u201d \u3000\u3000\u201c\u59d3\u767d\uff1f\u90a3\u548c\u6211\u4eec\u5be8\u4e3b\u662f\u540c\u5b97\u4e86\uff01\u201d \u3000\u3000\u767d\u7fbd\u6545\u4f5c\u8bb6\u5f02\u9053\uff1a\u201c\u8d35\u5be8\u4e3b\u4e5f\u59d3\u767d\uff01\u60f3\u4e0d\u5230\u6211\u4eec\u59d3\u767d\u7684\u7adf\u80fd\u51fa\u8fd9\u4e00\u4f4d\u5927\u4eba\u7269\uff0c\u79f0\u5f97\u4e0a\u662f \u5149\u5b97\u8000\u7956\u4e86\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u767d\u516c\u5b50\u7fa1\u6155\u6211\u4eec\u5be8\u4e3b\uff0c\u5230\u4e86\u5c71\u5be8\uff0c\u5e72\u8106\u4e5f\u5165\u4f19\u597d\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u6050\u6015\u4e0d\u591f\u8d44\u683c\u5427\uff1f\u201d \u3000\u3000\u201c\u600e\u4e48\u4f1a\u4e0d\u591f\u8d44\u683c\uff01\u201d \u3000\u3000\u201c\u5c71\u5be8\u91cc\u5168\u662f\u82f1\u96c4\u597d\u6c49\uff0c\u50cf\u6211\u8fd9\u79cd\u4eba\u80a9\u4e0d\u80fd\u62c5\u62c5\uff0c\u624b\u4e0d\u80fd\u63d0\u7bee\uff0c\u5230\u4e86\u5c71\u5be8\u6839\u672c\u6d3e\u4e0d\u4e0a\u7528 \u573a\u3002\u201d \u3000\u3000\u201c\u767d\u516c\u5b50\u8bfb\u8fc7\u4e66\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u8fdb\u8fc7\u51e0\u5e74\u79c1\u587e\u3002\u201d \u3000\u3000\u201c\u90a3\u597d\u529e\uff0c\u5c31\u5e2e\u5fd9\u6587\u6848\u5de5\u4f5c\uff0c\u7167\u6837\u53ef\u4ee5\u6df7\u7897\u996d\u5403\uff0c\u6211\u4eec\u5c71\u5be8\u91cc\u6700\u7f3a\u4e4f\u7684\u4e5f\u5c31\u662f\u8fd9\u79cd\u4eba\u624d \u4e86\u3002\u201d \u3000\u3000\u4e00\u8def\u8bf4\u7740\u8bdd\uff0c\u4f3c\u4e4e\u4e5f\u9887\u611f\u8f7b\u677e\u3002 \u3000\u3000\u5ffd\u542c\u8bb8\u5934\u76ee\u9053\uff1a\u201c\u71d5\u516c\u5b50\u771f\u662f\u8273\u798f\u4e0d\u6d45\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6014\u4e86\u6014\u9053\uff1a\u201c\u5c0a\u9a7e\u8fd9\u8bdd\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u8bb8\u5934\u76ee\u5567\u5567\u7684\u9053\uff1a\u201c\u516c\u5b50\u662f\u795d\u5bb6\u7684\u59d1\u7237\uff0c\u80fd\u5a36\u5230\u50cf\u795d\u5bb6\u5c0f\u59d0\u90a3\u6837\u7684\u5927\u7f8e\u4eba\uff0c\u4e0d\u662f\u8273\u798f\u4e0d \u6d45\u5417\uff1f\u201d \u3000\u3000\u201c\u5c0a\u9a7e\u89c1\u8fc7\u795d\u5bb6\u5c0f\u59d0\uff1f\u201d \u3000\u3000\u201c\u89c1\u8fc7\u4e00\u9762\uff0c\u6211\u4eec\u5be8\u4e3b\u5c31\u662f\u542c\u6211\u8bf4\u795d\u5bb6\u5c0f\u59d0\u957f\u5f97\u7f8e\uff0c\u624d\u51b3\u5b9a\u8ba8\u5979\u4e3a\u59bb\u7684\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\uff0c\u5c0a\u9a7e\u6bd4\u6211\u597d\u591a\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u8fd9\u8bdd\u53c8\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u201c\u5728\u4e0b\u81f3\u4eca\u8fd8\u6ca1\u89c1\u8fc7\u795d\u5bb6\u5c0f\u59d0\u662f\u4ec0\u4e48\u6837\u5b50\uff1f\u201d \u3000\u3000\u8bb8\u5934\u76ee\u5927\u60d1\u4e0d\u89e3\u9053\uff1a\u201c\u54ea\u6709\u8fd9\u79cd\u4e8b\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u7537\u5973\u6388\u53d7\u4e0d\u4eb2\uff0c\u5c24\u5176\u89c6\u5e9c\u662f\u8bd7\u4e66\u4eba\u5bb6\uff0c\u6700\u8bb2\u793c\u4eea\u4e86\uff0c\u5973\u513f\u6ca1\u8fc7 \u95e8\uff0c\u662f\u7edd\u5bf9\u4e0d\u80fd\u8ba9\u672a\u6765\u59d1\u7237\u770b\u5230\u7684\u3002\u201d \u3000\u3000\u8bb8\u5934\u76ee\u60ca\u547c\u7740\u9053\uff1a\u201c\u6709\u8fd9\u79cd\u4e8b\u5ddd\u81ea\u8fd8\u662f\u7b2c\u4e00\u6b21\u542c\u8bf4\u7684\uff0c\u65e2\u7136\u6ca1\u770b\u5230\u4eba\uff0c\u4f60\u4eec\u5f53\u521d\u662f\u600e\u4e48 \u8ba2\u7684\u4eb2\u4e8b\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6b63\u8272\u9053\uff1a\u201c\u5f53\u7136\u662f\u53cc\u65b9\u7236\u6bcd\u505a\u4e3b\u7684\uff0c\u6211\u4eec\u662f\u6307\u8179\u4e3a\u5a5a\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u4f60\u73b0\u5728\u4f4f\u5728\u5979\u5bb6\u91cc\uff0c\u54b1\u4e0d\u76f8\u4fe1\u4f1a\u6ca1\u770b\u5230\u5979\uff1f\u201d \u3000\u3000\u201c\u5979\u8eb2\u5728\u95fa\u623f\u91cc\uff0c\u8db3\u4e0d\u51fa\u6237\uff0c\u8fde\u5403\u996d\u90fd\u662f\u5973\u4f63\u4eba\u9001\u8fdb\u53bb\u7684\uff0c\u6211\u53c8\u600e\u80fd\u89c1\u5230\u5979\uff1f\u201d\uff0d\u201c\u4f60 \u5c31\u8be5\u63a8\u95e8\u8fdb\u53bb\u770b\u770b\uff01\u201d \u3000\u3000\u201c\u5c82\u6709\u6b64\u7406\uff0c\u8bfb\u4e66\u4eba\u600e\u80fd\u505a\u51fa\u8fd9\u79cd\u4e0d\u987e\u5ec9\u803b\u7684\u4e8b\u6765\uff1f\u201d \u3000\u3000\u8bb8\u5934\u76ee\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u8fd9\u4e48\u770b\u6765\uff0c\u54b1\u6ca1\u8bfb\u4e66\u53cd\u800c\u505a\u5bf9\u4e86\uff0c\u90fd\u8bf4\u8bfb\u4e66\u597d\uff0c\u6211\u770b\u8bfb\u4e66\u7b49\u4e8e\u7ed9 \u81ea\u5df1\u627e\u9ebb\u70e6\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u70b9\u70b9\u5934\u9053\uff1a\u201c\u8bf4\u7684\u6709\u7406\uff0c\u53ef\u60dc\u5728\u4e0b\u73b0\u5728\u60f3\u6539\u4e5f\u6539\u4e0d\u8fc7\u6765\u4e86\u3002\u201d \u3000\u3000\u4e0d\u5230\u4e00\u4e2a\u65f6\u8fb0\uff0c\u4fbf\u5df2\u8fdc\u8fdc\u671b\u89c1\u666e\u6551\u5bfa\u3002 \u3000\u3000\u8bb8\u5934\u76ee\u6307\u4e86\u4e00\u6307\u9053\uff1a\u201c\u5230\u4e86\uff0c\u5bfa\u91cc\u5c31\u662f\u6211\u4eec\u7684\u5c71\u5be8\uff0c\u4f60\u4eec\u4e24\u4f4d\u8ba4\u4e3a\u628a\u5c71\u5be8\u653e\u5728\u5e99\u91cc\u597d\u4e0d \u597d\uff1f\u201d \u3000\u3000\u767d\u7fbd\u63a5\u53e3\u9053\uff1a\u201c\u5f53\u7136\u597d\uff0c\u5e99\u91cc\u662f\u51fa\u5bb6\u4eba\u6240\u4f4f\u7684\u5730\u65b9\uff0c\u51fa\u5bb6\u4eba\u90fd\u662f\u597d\u4eba\uff0c\u53ef\u89c1\u5c71\u5be8\u91cc\u4e5f\u5168 \u662f\u597d\u4eba\u3002\u201d \u3000\u3000\u201c\u4f4f\u5728\u5e99\u91cc\u679c\u7136\u4e0d\u9519\uff0c\u4f46\u6700\u597d\u522b\u4f4f\u5728\u548c\u5c1a\u5e99\u6216\u9053\u58eb\u5e99\u3002\u201d \u3000\u3000\u201c\u5c0a\u9a7e\u60f3\u4f4f\u5728\u4ec0\u4e48\u5e99\u91cc\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u662f\u5c3c\u59d1\u5eb5\u6700\u597d\uff01\u201d \u3000\u3000\u5230\u8fbe\u5bfa\u524d\uff0c\u9ec4\u3001\u8bb8\u4e24\u4eba\u4e0d\u8d70\u5927\u95e8\uff0c\u5374\u6545\u610f\u5f15\u9886\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u7ed5\u7740\u56f4\u5899\u5411\u5bfa\u540e\u65b9\u5411\u8d70\u53bb\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u5f53\u7136\u5fc3\u91cc\u6709\u6570\u3002 \u3000\u3000\u4f46\u662f\uff0c\u8c01\u90fd\u4e0d\u60f3\u591a\u95ee\u3002 \u3000\u3000\u5bfa\u540e\u6709\u4e00\u540e\u95e8\uff0c\u8fdb\u5165\u540e\u95e8\u4e0d\u4e45\uff0c\u5c0f\u71d5\u5b50\u53ea\u89c9\u8fd9\u91cc\u7684\u73af\u5883\u5f88\u773c\u719f\uff0c\u56e0\u4e3a\u5df2\u5230\u4e86\u4ed6\u6628\u591c\u6765\u8fc7 \u7684\u9662\u843d\u3002 \u3000\u3000\u9ec4\u3001\u8bb8\u4e24\u4eba\u76f4\u63a5\u628a\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u5f15\u8fdb\u5ba2\u5385\u3002 \u3000\u3000\u8fd9\u91cc\u4e5f\u6b63\u662f\u767d\u864e\u6628\u665a\u559d\u9152\u7684\u5730\u65b9\u3002 \u3000\u3000\u4e24\u4eba\u8bf7\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u5750\u4e0b\u3002 \u3000\u3000\u9ec4\u5934\u76ee\u9053\uff1a\u201c\u5be8\u4e3b\u53ef\u80fd\u5230\u5916\u9762\u53bb\u4e86\uff0c\u4e8c\u4f4d\u7a0d\u5f85\u4e00\u4f1a\u513f\uff0c\u6211\u4eec\u5230\u5916\u9762\u53bb\u627e\u627e\u770b\u3002\u201d \u3000\u3000\u9ec4\u3001\u8bb8\u4e24\u4eba\u521a\u8d70\u4e0d\u4e45\uff0c\u4fbf\u89c1\u767d\u864e\u5927\u6447\u5927\u6446\u7684\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u9ec4\u3001\u8bb8\u4e24\u4eba\u4e5f\u8ddf\u5728\u540e\u9762\u3002 \u3000\u3000\u9ec4\u5934\u76ee\u9053\uff1a\u201c\u5be8\u4e3b\u5230\u4e86\uff01\u201d \u3000\u3000\u4ed6\u63a5\u7740\u53c8\u4e3a\u767d\u864e\u4ecb\u7ecd\uff1a\u201c\u8fd9\u4f4d\u71d5\u516c\u5b50\u5c31\u662f\u795d\u5e9c\u7684\u59d1\u7237\uff0c\u53e6\u4e00\u4f4d\u767d\u516c\u5b50\uff0c\u662f\u71d5\u516c\u5b50\u7684\u597d \u53cb\u3002\u201d \u3000\u3000\u767d\u864e\u7741\u7740\u4e00\u5bf9\u51f6\u5149\u707c\u707c\u7684\u5706\u773c\uff0c\u6253\u4e2a\u54c8\u54c8\u9053\uff1a\u201c\u5e78\u4f1a\uff0c\u5e78\u4f1a\uff01 \u3000\u3000\u8bf7\u5750\uff01\u9ec4\u9999\u4e3b\u5012\u8336\uff01\u201c \u3000\u3000\u53cc\u65b9\u5206\u5bbe\u4e3b\u5750\u4e0b\u3002 \u3000\u3000\u767d\u864e\u4e24\u773c\u7d27\u76ef\u7740\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u4f60\u8001\u5f1f\u53ef\u662f\u8273\u798f\u4e0d\u6d45\u554a\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u767d\u5be8\u4e3b\u8fd9\u8bdd\u2026\u2026\u201d \u3000\u3000\u767d\u864e\u9634\u58f0\u7b11\u9053\uff1a\u201c\u795d\u5c0f\u59d0\u662f\u4e2a\u5927\u7f8e\u4eba\uff0c\u4f60\u80fd\u5a36\u5979\u505a\u8001\u5a46\uff0c\u5f53\u7136\u8273\u798f\u4e0d\u6d45\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u542c\u8bf4\u767d\u5be8\u4e3b\u4e5f\u6709\u610f\u5a36\u5979\u4e3a\u538b\u5be8\u592b\u4eba\uff01\u201d \u3000\u3000\u201c\u65e2\u7136\u90a3\u599e\u513f\u662f\u8001\u5f1f\u7684\uff0c\u672c\u5be8\u4e3b\u60f3\u6709\u4ec0\u4e48\u7528\uff1f\u201d \u3000\u3000\u201c\u5728\u4e0b\u6b64\u6765\uff0c\u6b63\u662f\u4e3a\u767d\u5be8\u4e3b\u62a5\u559c\u7684\u3002\u201d \u3000\u3000\u767d\u864e\u542c\u4e0d\u61c2\u5c0f\u71d5\u5b50\u8bdd\u4e2d\u4e4b\u610f\uff0c\u4e24\u773c\u4e00\u76f4\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u2026\u2026\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u4e00\u672c\u6b63\u7ecf\u7684\u9053\uff1a\u201c\u6210\u4eba\u4e4b\u7f8e\u662f\u4ef6\u597d\u4e8b\uff0c\u767d\u5be8\u4e3b\u65e2\u7136\u6709\u610f\u5a36\u5979\u4e3a\u538b\u5be8\u592b\u4eba\uff0c\u8fde\u8058\u793c \u90fd\u5df2\u4e0b\u4e86\uff0c\u5728\u4e0b\u51b3\u5b9a\u548c\u795d\u5bb6\u9000\u5a5a\uff0c\u6210\u5168\u767d\u5be8\u4e3b\u7684\u597d\u4e8b\u3002\u201d \u3000\u3000\u767d\u864e\u7b80\u76f4\u4e0d\u76f8\u4fe1\u81ea\u5df1\u7684\u8033\u6735\u3002 \u3000\u3000\u8fde\u7ad9\u5728\u4e00\u65c1\u7684\u9ec4\u3001\u8bb8\u5934\u76ee\u4e5f\u5168\u6014\u5728\u5f53\u573a\u3002 \u3000\u3000\u53ea\u662f\u8fd9\u4e00\u6014\u662f\u9ad8\u5174\u8fc7\u5ea6\u7684\u6014\u3002 \u3000\u3000\u534a\u664c\u4e4b\u540e\uff0c\u624d\u542c\u767d\u864e\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u8fd9\u8bdd\u5f53\u771f\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u4e0d\u4f46\u795e\u8272\u575a\u5b9a\uff0c\u8bed\u6c14\u4e5f\u575a\u5b9a\uff0c\u9053\uff1a\u201c\u8fd9\u6837\u7684\u5927\u4e8b\uff0c\u600e\u80fd\u5f00\u5f97\u73a9\u7b11\uff01\u201d \u3000\u3000\u201c\u53ef\u662f\u672c\u5be8\u4e3b\u60f3\u77e5\u9053\uff0c\u8001\u5f1f\u4e3a\u4ec0\u4e48\u8981\u8fd9\u4e48\u505a\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u6709\u539f\u56e0\uff0c\u9664\u4e86\u6210\u4eba\u4e4b\u7f8e\u5916\uff0c\u5728\u4e0b\u5df2\u7ecf\u53e6\u6709\u4e86\u5bf9\u8c61\u3002\u201d \u3000\u3000\u201c\u53e6\u6709\u4e86\u5bf9\u8c61\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\u3002\u5728\u4e0b\u548c\u795d\u5bb6\u5c0f\u59d0\uff0c\u81f3\u4eca\u8fde\u9762\u90fd\u6ca1\u89c1\u8fc7\uff0c\u5c06\u6765\u82e5\u5a36\u8fc7\u95e8\uff0c\u662f\u5426\u80fd\u548c\u7766\u76f8\u5904\uff1f\u8fd8\u5f88 \u96be\u8bf4\uff0c\u81f3\u4e8e\u73b0\u5728\u7684\u5bf9\u8c61\u2026\u2026\u201d \u3000\u3000\u201c\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u201c\u53ef\u8bf4\u5df2\u7ecf\u671d\u5915\u76f8\u5904\uff0c\u5f7c\u6b64\u60c5\u6295\u610f\u5408\uff0c\u60f3\u5206\u4e5f\u5206\u4e0d\u5f00\u4e86\u3002\u201d \u3000\u3000\u201c\u539f\u6765\u8001\u5f1f\u662f\u81ea\u7531\u604b\u7231\uff0c\u90a3\u592a\u597d\u4e86\uff01\u201d \u3000\u3000\u767d\u864e\u8bf4\u7740\uff0c\u7ad9\u8d77\u8eab\u8d70\u8fc7\u6765\u62cd\u4e86\u62cd\u5c0f\u71d5\u5b50\u80a9\u8180\uff0c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u771f\u662f\u806a\u660e\u4eba\uff0c\u5fc5\u5b9a\u662f\u8001\u5929 \u7237\u5728\u4fdd\u62a4\u4f60\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u4e0d\u89e3\u7684\u95ee\u9053\uff1a\u201c\u767d\u5be8\u4e3b\u8fd9\u8bdd\u2026\u2026\u201d \u3000\u3000\u767d\u864e\u53c8\u62cd\u4e86\u5c0f\u71d5\u5b50\u80a9\u8180\u9053\uff1a\u201c\u90a3\u5c31\u5b9e\u5bf9\u4f60\u8bf4\u5427\uff0c\u672c\u5be8\u4e3b\u51b3\u5b9a\u7684\u4e8b\uff0c\u5929\u7687\u8001\u5b50\u4e5f\u6539\u53d8\u4e0d \u4e86\uff0c\u4eca\u5929\u8bf7\u8001\u5f1f\u6765\uff0c\u672c\u6765\u662f\u60f3\u8981\u4f60\u7684\u547d\uff0c\u60f3\u4e0d\u5230\u4f60\u81ea\u52a8\u653e\u5f03\u795d\u5bb6\u5c0f\u59d0\uff0c\u8fd9\u4e48\u4e00\u6765\uff0c\u672c\u5be8\u4e3b\u8fd8 \u771f\u662f\u8981\u628a\u4f60\u5f53\u6210\u5ba2\u4eba\u770b\u5f85\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6545\u610f\u6253\u4e86\u4e2a\u51b7\u98a4\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5be8\u4e3b\u7684\u624b\u6bb5\u8fd8\u8fd9\u4e48\u5389\u5bb3\uff01\u201d \u3000\u3000\u767d\u864e\u563f\u563f\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u5e72\u6211\u4eec\u8fd9\u4e00\u884c\uff0c\u662f\u6ca1\u6cd5\u8bb2\u6148\u60b2\u4e3a\u6000\u7684\uff0c\u6709\u9053\u662f\u65e0\u6bd2\u4e0d\u4e08\u592b\uff0c\u597d \u5728\u73b0\u5728\u4f60\u5df2\u7ecf\u5b8c\u5168\u5b89\u5168\u4e86\uff0c\u4e0d\u4f46\u662f\u672c\u5be8\u4e3b\u7684\u5ea7\u4e0a\u5ba2\uff0c\u800c\u4e14\u54b1\u4eec\u8fd8\u6210\u4e86\u597d\u670b\u53cb\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u4f9d\u7136\u663e\u5f97\u80c6\u602f\u7684\u9053\uff1a\u201c\u5728\u4e0b\u4e0d\u6562\u9ad8\u6500\u3002\u201d \u3000\u3000\u8fd9\u65f6\u767d\u7fbd\u8d81\u673a\u9053\uff1a\u201c\u542c\u8bf4\u767d\u5be8\u4e3b\u539f\u6765\u5df2\u7ecf\u51c6\u5907\u4eca\u5929\u5230\u795d\u5e9c\u8fce\u4eb2\uff0c\u73b0\u5728\u53ef\u4ee5\u5982\u671f\u8fdb\u884c\u4e86 \u5427\uff1f\u201d \u3000\u3000\u767d\u864e\u731b\u62cd\u4e86\u4e00\u4e0b\u5927\u817f\u9053\uff1a\u201c\u5bf9\uff0c\u597d\u4e8b\u4e0d\u80fd\u803d\u8bef\uff01\u201d \u3000\u3000\u9ec4\u5934\u76ee\u8eac\u8eab\u9053\uff1a\u201c\u7980\u5be8\u4e3b\uff0c\u65e2\u7136\u95ee\u9898\u5df2\u7ecf\u89e3\u51b3\u4e86\uff0c\u4e0d\u5982\u628a\u5409\u671f\u6539\u5728\u660e\u5929\u3002\u201d\u4e00\u767d\u864e\u4e24\u773c \u4e00\u77aa\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u8981\u6539\u5728\u660e\u5929\uff1f\u201d \u3000\u3000\u9ec4\u5934\u76ee\u9053\uff1a\u201c\u5be8\u4e3b\u7684\u5a5a\u4e8b\u662f\u4ef6\u8f70\u8f70\u70c8\u70c8\u7684\u5927\u4e8b\uff0c\u5fc5\u987b\u529e\u5f97\u70ed\u70ed\u95f9\u95f9\u3001\u6709\u58f0\u6709\u8272\u624d\u6210\uff0c\u5f53 \u7136\u5e94\u8be5\u4e8b\u5148\u597d\u597d\u51c6\u5907\u4e00\u4e0b\u3002\u201d \u3000\u3000\u201c\u8fd8\u8981\u51c6\u5907\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5c71\u5be8\u91cc\u5fc5\u987b\u5230\u5904\u5f20\u706f\u7ed3\u5f69\uff0c\u8fde\u5927\u6bbf\u7684\u795e\u50cf\u90fd\u8981\u6302\u4e0a\u5f69\u5e26\u3002\u53e6\u5916\uff0c\u6240\u6709\u5f1f\u5144\u4eec\u5fc5\u987b\u5927\u5f00 \u7b75\u5e2d\uff0c\u8fde\u5bfa\u91cc\u7684\u548c\u5c1a\uff0c\u4e5f\u8ba9\u4ed6\u4eec\u5f00\u5f00\u6212\uff0c\u6bcf\u4eba\u5403\u4e00\u987f\u8089\u559d\u4e00\u987f\u9152\uff0c\u8fd9\u4e9b\u4e8b\u60c5\u5f53\u7136\u9700\u8981\u65f6\u95f4\u51c6 \u5907\uff0c\u8fd8\u6709\u201d\u8fd8\u6709\u4ec0\u4e48\uff1f\u201c \u3000\u3000\u201c\u5be8\u4e3b\u548c\u65b0\u5a18\u5b50\u5fc5\u987b\u5750\u6700\u597d\u7684\u82b1\u8f7f\uff0c\u4e5f\u9700\u8981\u5230\u6d1b\u9633\u57ce\u91cc\u53bb\u79df\u3002\u201d \u3000\u3000\u767d\u864e\u6478\u7740\u8111\u888b\u60f3\u4e86\u60f3\u9053\uff1a\u201c\u6709\u9053\u7406\uff0c\u90a3\u5c31\u8ba9\u672c\u5be8\u4e3b\u5fcd\u4e00\u5fcd\u5427\uff01\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u53c8\u4e00\u62cd\u8111\u888b\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u6211\u60f3\u5230\u4e86\u4e00\u4ef6\u5927\u4e8b\uff0c\u662f\u5426\u8be5\u5728\u8fce\u4eb2\u524d\u5148\u53bb\u62dc\u671b\u4e00\u4e0b \u6211\u90a3\u8001\u5cb3\u7236\u5cb3\u6bcd\uff1f\u201d \u3000\u3000\u9ec4\u5934\u76ee\u9053\uff1a\u201c\u8fd9\u662f\u5e94\u8be5\u7684\u3002\u201d \u3000\u3000\u201c\u672c\u5be8\u4e3b\u5230\u73b0\u5728\u8fd8\u6ca1\u6709\u548c\u4ed6\u4eec\u89c1\u8fc7\u9762\uff0c\u4e0d\u80fd\u8fde\u8fd9\u70b9\u793c\u8c8c\u4e5f\u6ca1\u6709\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u671b\u5411\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u5f85\u4f1a\u513f\u5230\u8fbe\u795d\u5e9c\u540e\uff0c\u8fd8\u8981\u52b3\u9a7e\u4f60\u5f53\u7740\u6211\u90a3\u5cb3\u7236\u5cb3\u6bcd\u7684\u9762 \u8bb2\u51e0\u53e5\u8bdd\u3002\u201d \u3000\u3000\u201c\u767d\u5be8\u4e3b\u8981\u5728\u4e0b\u8bb2\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u8bb2\u4e00\u8bb2\u4f60\u81ea\u52a8\u653e\u5f03\u8fd9\u95e8\u5a5a\u4e8b\u7684\u4e8b\uff0c\u8fd9\u4e48\u4e00\u6765\uff0c\u672c\u5be8\u4e3b\u5c31\u6709\u5145\u5206\u7684\u7406\u7531\u5a36\u4ed6\u4eec\u7684\u5973\u513f \u4e86\uff0c\u4ed6\u4eec\u4e5f\u6ca1\u7406\u7531\u4e0d\u540c\u610f\u3002\u201d \u3000\u3000\u201c\u5c0f\u4e8b\u4e00\u4ef6\uff0c\u5728\u4e0b\u4e00\u5b9a\u7167\u529e\u3002\u201d \u3000\u3000\u201c\u8001\u5f1f\uff0c\u8fd9\u4ef6\u4e8b\u672c\u5be8\u4e3b\u6709\u70b9\u60f3\u4e0d\u901a\u3002\u201d \u3000\u3000\u201c\u767d\u5be8\u4e3b\u4e3a\u4f55\u4e8b\u60f3\u4e0d\u901a\uff1f\u201d \u3000\u3000\u201c\u4f60\u65e2\u7136\u8981\u653e\u5f03\u8fd9\u95e8\u4eb2\u4e8b\uff0c\u4e3a\u4ec0\u4e48\u5374\u53c8\u8981\u5230\u795d\u5e9c\u505a\u5ba2\uff1f\u201d \u3000\u3000\u201c\u5bb6\u7236\u548c\u795d\u8001\u4f2f\u662f\u4e16\u4ea4\uff0c\u4e3a\u4e86\u793c\u8c8c\u8d77\u89c1\uff0c\u5728\u4e0b\u5f53\u7136\u5e94\u8be5\u4eb2\u5230\u795d\u5e9c\u628a\u8bdd\u8bf4\u660e\u3002\u201d \u3000\u3000\u201c\u8001\u5f1f\u5bf9\u4ed6\u4eec\u8bf4\u660e\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u8fd8\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u4e0d\u8bf4\uff1f\u201d \u3000\u3000\u201c\u5728\u4e0b\u5230\u4e86\u795d\u5e9c\u4ee5\u540e\uff0c\u4e00\u76f4\u96be\u4ee5\u542f\u9f7f\uff0c\u6b63\u597d\u78b0\u4e0a\u5be8\u4e3b\u8981\u5a36\u5979\u505a\u538b\u5be8\u592b\u4eba\uff0c\u53cd\u800c\u7ed9\u4e86\u5728\u4e0b \u673a\u4f1a\u3002\u8fd9\u4e48\u770b\u6765\uff0c\u5728\u4e0b\u8fd8\u5e94\u8be5\u5927\u5927\u611f\u8c22\u5be8\u4e3b\u4e86\u3002\u201d \u3000\u3000\u767d\u864e\u542c\u5f97\u5927\u4e50\uff0c\u4ef0\u8d77\u8116\u5b50\u6253\u4e2a\u54c8\u54c8\u9053\uff1a\u201c\u8001\u5f1f\u5343\u4e07\u522b\u8fd9\u4e48\u5ba2\u6c14\uff0c\u662f\u6211\u8be5\u611f\u8c22\u4f60\uff01\u201d \u3000\u3000\u63a5\u7740\u671b\u5411\u9ec4\u5934\u76ee\u9053\uff1a\u201c\u4ea4\u4ee3\u7ba1\u6742\u52a1\u7684\u9999\u4e3b\uff0c\u8981\u4ed6\u4eec\u51c6\u5907\u51e0\u767e\u6761\u9ec4\u5e03\u6761\uff0c\u4e0a\u9762\u8fd8\u8981\u5199\u4e0a \u5b57\u3002\u201d \u3000\u3000\u9ec4\u5934\u76ee\u832b\u7136\u95ee\u9053\uff1a\u201c\u5be8\u4e3b\u8981\u9ec4\u5e03\u6761\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u7b49\u6211\u5927\u559c\u7684\u65e5\u5b50\u90a3\u5929\uff0c\u8981\u6240\u6709\u5f1f\u5144\u628a\u9ec4\u5e03\u6761\u7ed1\u5728\u5934\u4e0a\uff0c\u5230\u5bfa\u95e8\u53e3\u5217\u961f\u63a5\u8f7f\uff0c\u5927\u5bb6\u867d\u7136 \u4e0d\u80fd\u8d70\u4e0a\u8857\u5934\uff0c\u8d70\u5411\u5bfa\u5916\u4e5f\u662f\u597d\u7684\u3002\u201d \u3000\u3000\u201c\u9ec4\u5e03\u6761\u4e0a\u5199\u4ec0\u4e48\u5b57\u5462\uff1f\u201d \u3000\u3000\u201c\u770b\u770b\u4ed6\u4eec\u6709\u4ec0\u4e48\u8bc9\u6c42\uff0c\u5982\u679c\u60f3\u4e0d\u51fa\u4ec0\u4e48\u70b9\u5b50\uff0c\u5c31\u5199\u2018\u5be8\u4e3b\u65b0\u5a5a\u4e07\u5c81\u2019\u597d\u4e86\uff01\u201d \u3000\u3000\u201c\u5c5e\u4e0b\u7167\u529e\uff01\u201d \u3000\u3000\u201c\u9a6c\u4e0a\u901a\u77e5\u4f19\u623f\uff0c\u529e\u4e00\u684c\u4e0a\u7b49\u9152\u98df\u9001\u5230\u8fd9\u91cc\u6765\uff0c\u6211\u8981\u597d\u597d\u62db\u5f85\u62db\u5f85\u4ed6\u4eec\u4e24\u4f4d\u3002\u201d \u3000\u3000\u201c\u5be8\u4e3b\u4ec0\u4e48\u65f6\u5019\u5230\u795d\u5e9c\u53bb\uff1f\u201d \u3000\u3000\u201c\u62db\u5f85\u8fc7\u4ed6\u4eec\u4e24\u4f4d\u5c31\u8d70\u3002\u201d \u3000\u3000\u201c\u8981\u5e26\u4ec0\u4e48\u4eba\u53bb\uff1f\u201d \u3000\u3000\u201c\u8fd9\u79cd\u4e8b\u4e0d\u5fc5\u4eba\u591a\uff0c\u4f60\u4eec\u4e24\u4f4d\u591a\u6b21\u5230\u8fc7\u795d\u5e9c\uff0c\u5c31\u7531\u4f60\u4eec\u966a\u6211\u53bb\u597d\u4e86\u3002\u201d \u5367\u9f99\u751f\u300a\u71d5\u5b50\u4f20\u5947\u300b \u7b2c\u4e8c\u5341\u4e5d\u56de\u3000\u5200\u5149\u5251\u5f71 \u3000\u3000\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u771f\u6ca1\u60f3\u5230\u8fd8\u88ab\u62db\u5f85\u4e00\u987f\u9152\u996d\u3002 \u3000\u3000\u7531\u4e8e\u9152\u996d\u662f\u63d0\u524d\u7528\u7684\uff0c\u6563\u5e2d\u540e\u8fd8\u4e0d\u5230\u5348\u65f6\u3002 \u3000\u3000\u767d\u864e\u51b3\u5b9a\u9a6c\u4e0a\u5c31\u5230\u795d\u5e9c\u53bb\u3002 \u3000\u3000\u4ed6\u679c\u7136\u53ea\u5e26\u4e86\u9ec4\u3001\u8bb8\u4e24\u540d\u5c0f\u5934\u76ee\u3002 \u3000\u3000\u518d\u52a0\u4e0a\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\uff0c\u8def\u4e0a\u4e00\u5171\u662f\u4e94\u4eba\u3002 \u3000\u3000\u4ed6\u4eec\u4ecd\u662f\u7531\u540e\u95e8\u79bb\u5f00\u5c71\u5be8\uff0c\u9ec4\u3001\u8bb8\u4e24\u4eba\u5728\u524d\u5e26\u8def\u3002 \u3000\u3000\u5927\u7ea6\u8d70\u4e86\u76cf\u8336\u5de5\u592b\uff0c\u5ffd\u89c1\u767d\u864e\u7d27\u76b1\u7740\u7709\u5934\u9053\uff1a\u201c\u4ed6\u5976\u5976\u7684\uff0c\u60f3\u5230\u5927\u559c\u7684\u65e5\u5b50\u5373\u5c06\u5230\u6765\uff0c \u5fc3\u91cc\u5b9e\u5728\u9ad8\u5174\uff0c\u504f\u504f\u6709\u4e00\u4ef6\u4e8b\u5374\u53c8\u8ba9\u4eba\u9ad8\u5174\u4e0d\u8d77\u6765\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u5be8\u4e3b\u6709\u4ec0\u4e48\u4e8b\u4e0d\u9ad8\u5174\uff1f\u201d \u3000\u3000\u201c\u8001\u5f1f\u4e00\u5b9a\u77e5\u9053\uff0c\u524d\u4e9b\u5929\u672c\u5be8\u4e3b\u66fe\u628a\u795d\u5e9c\u7684\u5c0f\u5144\u5f1f\u63a5\u5230\u5c71\u4e0a\u6765\u73a9\uff0c\u60f3\u4e0d\u5230\u2026\u2026\u201d \u3000\u3000\u201c\u83ab\u975e\u795d\u5c0f\u5144\u5f1f\u75c5\u4e86\uff1f\u201d \u3000\u3000\u201c\u75c5\u4e86\u662f\u5c0f\u4e8b\uff0c\u90a3\u7b97\u4ec0\u4e48\uff1f\u4ed6\u2026\u2026\u4ed6\u6628\u665a\u4e0d\u89c1\u4e86\uff01\u201d \u3000\u3000\u6b64\u8bed\u4e00\u51fa\uff0c\u8fde\u9ec4\u3001\u8bb8\u4e24\u540d\u5934\u76ee\u4e5f\u5403\u4e86\u4e00\u60ca\uff01 \u3000\u3000\u4f46\u4ed6\u4eec\u5374\u4e0d\u6562\u591a\u95ee\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u4e5f\u5403\u60ca\u7684\u9053\uff1a\u201c\u662f\u8d70\u5931\u4e86\uff0c\u8fd8\u662f\u88ab\u4eba\u63b3\u8d70\u4e86\uff1f\u201d \u3000\u3000\u767d\u864e\u6c14\u547c\u547c\u7684\u9053\uff1a\u201c\u6628\u665a\u662f\u6211\u9001\u4ed6\u56de\u623f\u7761\u89c9\u7684\uff0c\u51fa\u6765\u65f6\u8fd8\u628a\u623f\u95e8\u53cd\u6263\uff0c\u5927\u95e8\u5916\u53c8\u6d3e\u6709\u8b66 \u6212\uff0c\u90a3\u91cc\u4f1a\u662f\u8d70\u5931\u7684\uff01\u201d \u3000\u3000\u201c\u90a3\u662f\u88ab\u4eba\u63b3\u8d70\u7684\u4e86\uff0c\u4ec0\u4e48\u4eba\u6709\u8fd9\u4e48\u5927\u7684\u80fd\u8010\uff1f\u201d \u3000\u3000\u201c\u6211\u4e5f\u611f\u5230\u5947\u602a\uff0c\u8c01\u6709\u8fd9\u4e48\u5927\u7684\u80fd\u8010\u5462\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u5ffd\u7136\u50cf\u609f\u51fa\u4e86\u9053\u7406\uff0c\u5fd9\u9053\uff1a\u201c\u767d\u5be8\u4e3b\uff0c\u4f60\u662f\u806a\u660e\u4e00\u4e16\uff0c\u7cca\u6d82\u4e00\u65f6\u201d \u3000\u3000\u767d\u864e\u4e24\u773c\u76f4\u7728\u7684\u9053\uff1a\u201c\u83ab\u975e\u8001\u5f1f\u60f3\u5230\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4ef6\u4e8b\u6beb\u65e0\u7591\u95ee\u662f\u4f60\u7684\u624b\u4e0b\u4eba\u5e72\u7684\uff0c\u800c\u4e14\u5fc5\u5b9a\u8fd8\u662f\u4f60\u6700\u4eb2\u8fd1\u7684\u4eba\uff0c\u8fde\u90a3\u95e8\u5916\u7684\u8b66\u6212\u90fd \u8131\u4e0d\u4e86\u5acc\u7591\u3002\u201d \u3000\u3000\u201c\u8001\u5f1f\u731c\u60f3\u7684\u679c\u7136\u6709\u7406\uff0c\u9664\u4e86\u81ea\u5df1\u4eba\uff0c\u522b\u4eba\u7edd\u5bf9\u529e\u4e0d\u5230\u3002\u5976\u5976\u7684\uff0c\u56de\u53bb\u4ee5\u540e\u975e\u9a6c\u4e0a\u628a\u4ed6 \u4eec\u96c6\u5408\u8d77\u6765\u4e00\u4e2a\u4e2a\u67e5\u95ee\u4e0d\u53ef\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u60f3\u56de\u53bb\u5417\uff1f\u201d \u3000\u3000\u767d\u864e\u4e0d\u89c9\u8138\u8272\u4e00\u53d8\u9053\uff1a\u201c\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u5411\u4e00\u4fa7\u8dc3\u5f00\u6570\u6b65\uff0c\u9053\uff1a\u201c\u767d\u864e\uff0c\u5728\u4e0b\u65b9\u624d\u8d70\u5728\u4f60\u7684\u8eab\u540e\uff0c\u82e5\u60f3\u6697\u7b97\u4f60\uff0c\u4e0d\u8d39\u5439\u7070 \u4e4b\u529b\uff0c\u4f46\u660e\u4eba\u4e0d\u505a\u6697\u4e8b\uff0c\u73b0\u5728\u54b1\u4eec\u5c31\u624b\u4e0b\u89c1\u4e2a\u771f\u7ae0\u5427\uff01\u201d \u3000\u3000\u539f\u6765\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u51fa\u53d1\u524d\uff0c\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u4fbf\u5df2\u6709\u4ea4\u4ee3\uff0c\u8981\u4ed6\u4eec\u5728\u65e0\u4eba\u4e4b\u5904\uff0c\u5148\u628a\u767d\u864e\u505a \u6389\u3002 \u3000\u3000\u6b63\u597d\u73b0\u573a\u9644\u8fd1\u4e0d\u4f46\u5730\u5f62\u590d\u6742\uff0c\u4e24\u8fb9\u53c8\u6709\u6811\u6797\uff0c\u800c\u4e14\u53c8\u662f\u56db\u4e0b\u65e0\u4eba\u3002 \u3000\u3000\u767d\u864e\u54ea\u91cc\u4f1a\u628a\u5c0f\u71d5\u5b50\u653e\u5728\u5fc3\u4e0a\uff1f\u4ed6\u521a\u8981\u8fd1\u524d\u52a8\u624b\uff0c\u8c01\u77e5\u5c0f\u71d5\u5b50\u8bf4\u5b8c\u8bdd\u7adf\u8f6c\u8eab\u5411\u6811\u6797\u5185\u9003 \u53bb\u3002 \u3000\u3000\u767d\u7fbd\u4e5f\u8ddf\u7740\u5f80\u6811\u6797\u91cc\u8dd1\u3002 \u3000\u3000\u8fd9\u4e5f\u662f\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u539f\u5b9a\u7684\u8bf1\u654c\u8ba1\u5212\uff0c\u4ed6\u4eec\u8981\u628a\u5bf9\u65b9\u8bf1\u8fdb\u6811\u6797\u5185\u518d\u4e0b\u624b\uff0c\u4ee5\u8fbe\u6740\u4e86\u4eba\u800c \u53c8\u795e\u4e0d\u77e5\u9b3c\u4e0d\u89c9\u7684\u76ee\u7684\u3002 \u3000\u3000\u767d\u864e\u5e26\u7740\u9ec4\u3001\u8bb8\u4e24\u5934\u76ee\uff0c\u7acb\u5373\u968f\u540e\u8ffd\u53bb\u3002 \u3000\u3000\u5f53\u53cc\u65b9\u5df2\u6df1\u5165\u6797\u5185\u4e4b\u540e\uff0c\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u8fd4\u8eab\u4fbf\u548c\u5bf9\u65b9\u5c55\u5f00\u4e86\u62da\u640f\u3002 \u3000\u3000\u767d\u864e\u5c82\u662f\u5c0f\u71d5\u5b50\u7684\u5bf9\u624b\uff0c\u4e0d\u8db3\u5341\u62db\u4fbf\u88ab\u4ed6\u4e00\u638c\u51fb\u4e2d\u524d\u80f8\uff0c\u5012\u5730\u4e0d\u8d77\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u4fef\u8eab\u53c8\u662f\u4e00\u638c\uff0c\u767d\u864e\u95e8\u54fc\u4e00\u58f0\uff0c\u7acb\u5373\u5fc3\u80ba\u9707\u88c2\u800c\u4ea1\u3002 \u3000\u3000\u767d\u7fbd\u72ec\u6218\u9ec4\u3001\u8bb8\u4e24\u540d\u5934\u76ee\uff0c\u4e5f\u5728\u4e0d\u8db3\u534a\u76cf\u8336\u5de5\u592b\uff0c\u5c06\u4e24\u4eba\u53cc\u53cc\u9001\u4e0a\u897f\u5929\u3002 \u3000\u3000\u6b63\u597d\u6797\u5185\u5730\u4e0a\u6709\u4e2a\u5927\u5751\uff0c\u4e24\u4eba\u4fbf\u628a\u4e09\u5177\u5c38\u4f53\u4e22\u8fdb\u5751\u53bb\uff0c\u518d\u4ee5\u4e0a\u77f3\u63a9\u57cb\u59a5\u5f53\uff0c\u7136\u540e\u56de\u5230\u795d \u5b85\u3002 \u3000\u3000\u5f53\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u8fd4\u56de\u795d\u5b85\u65f6\uff0c\u6c5f\u5343\u91cc\u3001\u738b\u5f64\u3001\u795d\u9999\u4ead\u90fd\u5728\u5ba2\u5385\u7b49\u5019\u6d88\u606f\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u4ef6\u4e86\u4e0d\u8d77\u7684\u5927\u4e8b\uff0c\u8fde\u6c5f\u5343\u91cc\u548c\u738b\u5f64\u90fd\u4e0d\u80fd\u6389\u4ee5\u8f7b\u5fc3\u3002 \u3000\u3000\u5f53\u4ed6\u4eec\u542c\u5230\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u7684\u56de\u62a5\u4ee5\u540e\uff0c\u795d\u9999\u4ead\u5e72\u5927\u559c\u4e4b\u4f59\uff0c\u514d\u4e0d\u4e86\u53c8\u62c5\u5fc3\u5c06\u6709\u5927\u7978\u6765 \u4e34\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u6b63\u8272\u9053\uff1a\u201c\u795d\u5148\u751f\u653e\u5fc3\uff0c\u4e8b\u60c5\u65e2\u7136\u662f\u6211\u4eec\u5e72\u4e0b\u7684\uff0c\u6211\u4eec\u5c31\u4e00\u5b9a\u8d1f\u8d23\u5230\u5e95\uff0c\u5e9c\u4e0a\u7684 \u4eba\u6700\u597d\u80fd\u8fdb\u57ce\u8eb2\u4e00\u8eb2\uff0c\u4e0d\u8fc7\u4f60\u5fc5\u987b\u6682\u65f6\u7559\u4e0b\u3002\u201d \u3000\u3000\u795d\u9999\u4ead\u4e0d\u4f4f\u70b9\u5934\u9053\uff1a\u201c\u5b66\u751f\u57ce\u91cc\u6b63\u597d\u6709\u5bb6\u4eb2\u621a\uff0c\u90a3\u8fb9\u6709\u5730\u65b9\u53ef\u4f4f\uff0c\u6c5f\u5927\u4fa0\u770b\u4ec0\u4e48\u65f6\u5019\u884c \u52a8\u8f83\u59a5\uff1f\u201d \u3000\u3000\u201c\u65f6\u95f4\u4ee5\u5929\u9ed1\u540e\u4e3a\u4f73\uff0c\u51fa\u53d1\u65f6\u6c5f\u67d0\u81ea\u5f53\u6d3e\u4eba\u62a4\u9001\uff0c\u73b0\u5728\u65f6\u95f4\u5c1a\u65e9\uff0c\u5c31\u8bf7\u795d\u5148\u751f\u4ea4\u4ee3\u592b\u4eba \u6536\u62fe\u6536\u62fe\uff0c\u8d35\u91cd\u7684\u4e1c\u897f\u968f\u8eab\u5e26\u8d70\u3002\u201d \u3000\u3000\u201c\u5b66\u751f\u8fd9\u5c31\u4ea4\u4ee3\u53bb\u3002\u201d \u3000\u3000\u795d\u9999\u4ead\u79bb\u5f00\u5ba2\u5385\u540e\uff0c\u738b\u5f64\u5fd9\u9053\uff1a\u201c\u6c5f\u5144\u8ba4\u4e3a\u8fd9\u91cc\u662f\u5426\u9a6c\u4e0a\u5c31\u4f1a\u6709\u4e8b\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u6682\u65f6\u4e0d\u4f1a\uff0c\u5c71\u5be8\u7684\u8d3c\u5bc7\u4eec\u5341\u6709\u516b\u4e5d\u4e0d\u4f1a\u77e5\u9053\u767d\u864e\u548c\u9ec4\u3001\u8bb8\u4e24\u540d\u5c0f\u5934\u76ee\u5df2 \u6b7b\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u4ed6\u4eec\u5fc5\u5b9a\u77e5\u9053\u767d\u864e\u662f\u5230\u8fd9\u8fb9\u6765\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e5f\u4e0d\u89c1\u5f97\uff0c\u4ed6\u4eec\u5728\u5c71\u5be8\u51fa\u53d1\u65f6\u8d70\u7684\u662f\u540e\u95e8\uff0c\u53ef\u89c1\u767d\u864e\u5e76\u4e0d\u60f3\u628a\u8fd9\u4ef6\u4e8b\u8ba9\u4ed6\u7684\u5c5e\u4e0b\u4eec\u77e5 \u9053\u3002\u201d \u3000\u3000\u201c\u96be\u9053\u6c5f\u5144\u5c31\u8ba4\u4e3a\u8fd9\u91cc\u53ef\u4ee5\u5e73\u5b89\u65e0\u4e8b\uff1f\u201d \u3000\u3000\u201c\u82e5\u8fd9\u91cc\u80fd\u5e73\u5b89\u65e0\u4e8b\uff0c\u5c31\u7528\u4e0d\u7740\u8981\u795d\u5148\u751f\u628a\u5bb6\u5c0f\u9001\u5230\u57ce\u91cc\uff0c\u738b\u8001\u5f1f\uff01\u6211\u73b0\u5728\u5df2\u6709\u4e86\u4e00\u5957 \u5e94\u654c\u8179\u6848\uff0c\u4e0d\u77e5\u4f60\u662f\u5426\u540c\u610f\uff1f\u201d \u3000\u3000\u201c\u6c5f\u5144\u6709\u4f55\u9ad8\u89c1\uff1f\u201d \u3000\u3000\u201c\u628a\u4e09\u516c\u4e3b\u4e5f\u63a5\u5230\u8fd9\u91cc\u6765\uff0c\u90a3\u8fb9\u7684\u4eba\u4e5f\u5168\u8fc7\u6765\uff0c\u6700\u597d\u80fd\u7559\u4e0b\u4e24\u4eba\uff0c\u5728\u57ce\u91cc\u8d1f\u8d23\u7ef4\u62a4\u795d\u5148 \u751f\u5bb6\u5c0f\u7684\u5b89\u5168\u3002\u201d \u3000\u3000\u201c\u5144\u5f1f\u4e5f\u6709\u8fd9\u79cd\u60f3\u6cd5\uff0c\u53ea\u662f\u5bf9\u65b9\u5c71\u5be8\u91cc\u76d7\u532a\u6709\u6570\u767e\u4e4b\u4f17\uff0c\u53e6\u5916\u5c71\u540e\u9ec4\u6cb3\u5357\u5cb8\u66f4\u6709\u4e00\u4e2a\u6c34 \u5be8\uff0c\u4ee5\u54b1\u4eec\u73b0\u6709\u7684\u4eba\u624b\u6765\u5bf9\u4ed8\u4ed6\u4eec\uff0c\u603b\u5acc\u5355\u8584\u4e86\u4e9b\u3002\u201d \u3000\u3000\u201c\u64d2\u8d3c\u64d2\u738b\uff0c\u54b1\u4eec\u53ea\u8981\u6740\u4e86\u532a\u9996\uff0c\u4f59\u4e0b\u7684\u4eba\u6570\u518d\u591a\uff0c\u4e8e\u7fa4\u9f99\u65e0\u9996\u4e4b\u4e0b\u4e5f\u5fc5\u5b9a\u4e0d\u6218\u81ea\u4e71\u3002 \u82e5\u8001\u5f1f\u4ecd\u6709\u987e\u8651\uff0c\u53e6\u5916\u8fd8\u6709\u529e\u6cd5\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u529e\u6cd5\uff1f\u201d \u3000\u3000\u201c\u8fd9\u91cc\u79bb\u5c11\u6797\u5bfa\u4e0d\u8fc7\u767e\u4f59\u91cc\u8def\uff0c\u4e0d\u59a8\u6d3e\u4eba\u6301\u7687\u4e0a\u5bc6\u65e8\uff0c\u8bf7\u5c11\u6797\u638c\u95e8\u4eba\u5927\u667a\u7985\u5e08\u8fde\u591c\u8c03\u6d3e \u9ad8\u624b\u524d\u6765\u652f\u63f4\uff0c\u4e0d\u51fa\u4e24\u5929\u4fbf\u53ef\u5230\u8fbe\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u5c31\u8fd9\u4e48\u529e\uff0c\u54b1\u4eec\u4e0a\u6b21\u5230\u5f00\u5c01\u524d\uff0c\u5df2\u884c\u5728\u5c11\u6797\u4f4f\u8fc7\u51e0\u5929\uff0c\u5144\u5f1f\u4e5f\u66fe\u8ba9\u5927\u667a\u638c\u95e8\u4eba\u770b \u8fc7\u7687L\u5bc6\u65e8\uff0c\u73b0\u5728\u968f\u4fbf\u6d3e\u4e00\u4e2a\u4eba\u53bb\uff0c\u4e8b\u60c5\u5c31\u53ef\u529e\u6210\u3002\u201d \u3000\u3000\u5f53\u65e5\u665a\u996d\u540e\uff0c\u7531\u5c0f\u71d5\u5b50\u3001\u767d\u7fbd\u3001\u601c\u82b1\u62a4\u9001\u795d\u592b\u4eba\u3001\u795d\u6708\u82f1\u3001\u795d\u82f1\u6770\u8fdb\u57ce\u3002 \u3000\u3000\u81f3\u4e8e\u957f\u5de5\u8bb8\u6709\u7530\u548c\u90a3\u540d\u5973\u4f63\u4eba\uff0c\u4ecd\u7559\u5728\u8fd9\u91cc\uff0c\u4ee5\u4fbf\u8d1f\u8d23\u6253\u6742\u548c\u70e7\u996d\u6d17\u8863\u3002 \u3000\u3000\u738b\u5f64\u5e76\u4ea4\u4ee3\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\uff0c\u8981\u71d5\u98de\u3001\u8d75\u4fdd\u3001\u9648\u5b8f\u4e09\u4eba\u7559\u5728\u57ce\u5185\uff0c\u8d1f\u8d23\u795d\u5e9c\u5bb6\u5c0f\u7684\u5b89\u5168\uff0c \u97e9\u6d9b\u5219\u8d76\u5f80\u5c11\u6797\u5bfa\u8bf7\u6c42\u5927\u667a\u7985\u5e08\u8c03\u5175\u9063\u5c06\u3002 \u3000\u3000\u5f53\u591c\u4e09\u66f4\u2014\u2014\u4e09\u516c\u4e3b\u3001\u9ed1\u7f57\u6c49\u4e03\u5de7\u50e7\u3001\u738b\u91cd\u5c71\u3001\u8001\u5996\u5a46\u53f2\u5999\u79cb\u5168\u6765\u4e86\uff0c\u5c0f\u71d5\u5b50\u3001\u767d\u7fbd\u3001 \u601c\u82b1\u4e09\u4eba\u81ea\u7136\u4e5f\u90fd\u8ddf\u7740\u56de\u6765\u3002 \u3000\u3000\u4e3a\u614e\u91cd\u8d77\u89c1\uff0c\u591c\u665a\u5e76\u8f6e\u73ed\u6d3e\u51fa\u8b66\u6212\u3002 \u3000\u3000\u4f46\u662f\u5e76\u65e0\u4efb\u4f55\u60c5\u51b5\u53d1\u751f\u3002 \u3000\u3000\u6b21\u65e5\u65e9\u9910\u540e\uff0c\u5c71\u5be8\u91cc\u6765\u4e86\u4e09\u540d\u5927\u6c49\u3002 \u3000\u3000\u4e3a\u9996\u7684\u662f\u540d\u9999\u4e3b\uff0c\u53eb\u9ad8\u767b\u9b41\uff0c\u5e74\u5728\u56db\u65ec\u5de6\u53f3\uff0c\u662f\u767d\u864e\u6700\u5f97\u529b\u7684\u4eb2\u4fe1\u3002\u53e6\u4e24\u540d\u5219\u662f\u9ad8\u767b\u9b41 \u624b\u4e0b\u7684\u55bd\u7f57\uff0c\u4e00\u540d\u59d3\u9648\uff0c\u4e00\u540d\u59d3\u8c22\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u7acb\u5373\u547d\u6240\u6709\u7684\u4eba\u90fd\u8eb2\u8fdb\u5c4b\u5185\uff0c\u7531\u4ed6\u4e00\u4eba\u966a\u540c\u795d\u9999\u4ead\u7559\u5728\u5929\u4e95\u91cc\u3002 \u3000\u3000\u51fa\u53bb\u5e94\u95e8\u7684\u662f\u957f\u5de5\u8bb8\u6709\u7530\u3002 \u3000\u3000\u9ad8\u767b\u9b41\u4e00\u8fdb\u95e8\u5c31\u95ee\u9053\uff1a\u201c\u795d\u8001\u7237\u5b50\u5728\u5bb6\u5417\uff1f\u201d \u3000\u3000\u8bb8\u6709\u7530\u966a\u7740\u5c0f\u5fc3\u9053\uff1a\u201c\u8001\u7237\u5728\uff01\u201d \u3000\u3000\u8fdb\u5165\u5929\u4e95\uff0c\u795d\u9999\u4ead\u8fde\u5fd9\u8fce\u4e0a\u524d\u9053\uff1a\u201c\u9601\u4e0b\u662f\u4ec0\u4e48\u4eba\uff1f\u53ef\u662f\u8981\u627e\u8001\u673d\uff1f\u201d \u3000\u3000\u9ad8\u767b\u9b41\u9762\u65e0\u8868\u60c5\u7684\u9053\uff1a\u201c\u4f60\u5927\u6982\u5c31\u662f\u795d\u8001\u7237\u5b50\u4e86\uff1f\u54b1\u4eec\u5230\u91cc\u9762\u8c08\u3002\u201d \u3000\u3000\u795d\u9999\u4ead\u628a\u9ad8\u767b\u9b41\u5f15\u8fdb\u5ba2\u5385\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u4e5f\u8ddf\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u9648\u3001\u8c22\u4e24\u540d\u55bd\u7f57\u5219\u5206\u7ad9\u5728\u5ba2\u5385\u95e8\u53e3\u3002 \u3000\u3000\u5750\u4e0b\u540e\uff0c\u9ad8\u767b\u9b41\u4e24\u773c\u51f6\u5149\u95ea\u5c04\u7684\u671b\u7740\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u795d\u9999\u4ead\u4ee3\u7b54\u9053\uff1a\u201c\u662f\u8001\u673d\u7684\u597d\u53cb\u3002\u201d \u3000\u3000\u201c\u90a3\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u4ed6\u5c31\u4f4f\u5728\u6d1b\u9633\u57ce\u5185\u3002\u201d \u3000\u3000\u9ad8\u767b\u9b41\u4f3c\u4e4e\u4e0d\u7591\u6709\u4ed6\uff0c\u6ca1\u518d\u8bf4\u4ec0\u4e48\uff0c\u53ea\u987e\u7728\u7740\u4e24\u773c\u56db\u4e0b\u5f20\u671b\u3002 \u3000\u3000\u795d\u9999\u4ead\u642d\u8baa\u7740\u9053\uff1a\u201c\u8fd8\u6ca1\u8bf7\u95ee\u5c0a\u59d3\u5927\u540d\uff1f\u627e\u8001\u673d\u6709\u4f55\u89c1\u6559\uff1f\u201d \u3000\u3000\u201c\u5728\u4e0b\u9ad8\u767b\u9b41\uff0c\u7531\u5c71\u5be8\u6765\u7684\uff0c\u60f3\u8bf7\u95ee\u8001\u7237\u5b50\u51e0\u4ef6\u4e8b\u60c5\u3002\u201d \u3000\u3000\u201c\u597d\u6c49\u6709\u8bdd\u8bf7\u8bb2\uff0c\u4e0d\u5fc5\u5ba2\u6c14\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u5be8\u4e3b\u662f\u5426\u5230\u5e9c\u4e0a\u6765\u8fc7\uff1f\u201d \u3000\u3000\u201c\u767d\u5be8\u4e3b\u4ece\u6ca1\u5230\u820d\u4e0b\u6765\u8fc7\uff0c\u597d\u6c49\u4e3a\u4ec0\u4e48\u8981\u95ee\u8d77\u8fd9\u4e2a\uff1f\u201d \u3000\u3000\u201c\u5be8\u4e3b\u6628\u5929\u4e2d\u5348\u6709\u4e8b\u5916\u51fa\uff0c\u5230\u4eca\u5929\u5929\u4eae\u8fd8\u4e0d\u66fe\u56de\u53bb\uff0c\u5728\u4e0b\u6599\u60f3\u5f88\u53ef\u80fd\u662f\u5230\u5e9c\u4e0a\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u6ca1\u6765\u8fc7\uff0c\u5be8\u4e3b\u600e\u4f1a\u5230\u5408\u4e0b\u8fd9\u79cd\u5730\u65b9\u6765\u5462\uff1f\u201d \u3000\u3000\u9ad8\u767b\u9b41\u5ffd\u7136\u563f\u563f\u7b11\u9053\uff1a\u201c\u795d\u8001\u7237\u5b50\uff0c\u4f9d\u5728\u4e0b\u6599\u60f3\uff0c\u5be8\u4e3b\u6628\u65e5\u5916\u51fa\uff0c\u5341\u6709\u516b\u4e5d\u662f\u8981\u5230\u5e9c\u4e0a \u6765\u7684\u3002\u201d \u3000\u3000\u795d\u9999\u4ead\u4e00\u8138\u832b\u7136\u4e4b\u8272\u9053\uff1a\u201c\u8001\u673d\u542c\u4e0d\u61c2\u597d\u6c49\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u201c\u8001\u7237\u5b50\uff0c\u4f60\u597d\u50cf\u5728\u88c5\u7cca\u6d82\uff1f\u201d \u3000\u3000\u201c\u597d\u6c49\u4f55\u59a8\u628a\u8bdd\u8bf4\u660e\u767d\uff01\u201d \u3000\u3000\u201c\u4f60\u662f\u6211\u4eec\u5be8\u4e3b\u672a\u6765\u7684\u8001\u652f\u4eba\uff0c\u6211\u4eec\u5be8\u4e3b\u4e0d\u5230\u5e9c\u4e0a\u6765\uff0c\u4f1a\u5230\u54ea\u91cc\u53bb\u5462\uff1f\u201d \u3000\u3000\u201c\u539f\u6765\u597d\u6c49\u8fd8\u4e0d\u6e05\u695a\u8fd9\u4e8b\u5df2\u7ecf\u83b7\u5f97\u89e3\u51b3\u3002\u201d \u3000\u3000\u201c\u89e3\u51b3\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u8001\u673d\u65e9\u5c31\u8bf7\u9ec4\u3001\u8bb8\u4e24\u4f4d\u9999\u4e3b\u56de\u7980\u8fc7\u5be8\u4e3b\uff0c\u5c0f\u5973\u5df2\u7ecf\u6709\u4e86\u5a46\u5bb6\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u6211\u4eec\u5be8\u4e3b\u5e76\u4e0d\u76f8\u4fe1\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u5be8\u4e3b\u8981\u89c1\u5230\u8001\u673d\u90a3\u672a\u6765\u7684\u5973\u5a7f\u624d\u80af\u76f8\u4fe1\uff0c\u6240\u5e78\u4ed6\u524d\u51e0\u5929\u5df2\u7ecf\u6765\u4e86\uff0c\u7ecf\u9ec4\u3001\u8bb8\u4e24 \u4f4d\u9999\u4e3b\u544a\u77e5\u5be8\u4e3b\u540e\uff0c\u5be8\u4e3b\u5f88\u591f\u4e49\u6c14\uff0c\u5df2\u51b3\u5b9a\u53d6\u9500\u8fd9\u95e8\u5a5a\u4e8b\u3002\u201d \u3000\u3000\u9ad8\u767b\u9b41\u8f6c\u52a8\u7740\u4e00\u5bf9\u7cbe\u5149\u95ea\u95ea\u7684\u5706\u773c\u9053\uff1a\u201c\u8fd9\u4e8b\u6211\u600e\u4e48\u6ca1\u542c\u8bf4\u8fc7\uff1f\u201d \u3000\u3000\u795d\u9999\u4ead\u966a\u7740\u7b11\u9053\uff1a\u201c\u82e5\u5be8\u4e3b\u6ca1\u5bf9\u597d\u6c49\u8bf4\uff0c\u597d\u6c49\u81ea\u7136\u4e0d\u4f1a\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u77e5\u9053\uff0c\u5728\u4e0b\u73b0\u5728\u5c31\u8981\u89c1\u89c1\u5e9c\u4e0a\u8fd9\u4f4d\u672a\u6765\u7684\u59d1\u7237\uff01\u201d \u3000\u3000\u795d\u9999\u4ead\u4e0d\u7531\u4e0d\u5b89\u548c\u72b9\u8c6b\u8d77\u6765\u3002 \u3000\u3000\u53ea\u542c\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u65e2\u7136\u8fd9\u4f4d\u597d\u6c49\u8981\u89c1\u4ee4\u5a7f\uff0c\u5c31\u8981\u4ee4\u5a7f\u51fa\u6765\u89c1\u89c1\u5427\uff01\u201d \u3000\u3000\u201c\u4e5f\u597d\uff0c\u6211\u73b0\u5728\u5c31\u53bb\u628a\u4ed6\u53eb\u6765\u3002\u201d \u3000\u3000\u795d\u9999\u4ead\u5f88\u5feb\u4fbf\u5e26\u7740\u5c0f\u71d5\u5b50\u8fdb\u4eba\u5ba2\u5385\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u51b7\u51b7\u77a5\u4e86\u9ad8\u767b\u9b41\u4e00\u773c\u9053\uff1a\u201c\u662f\u90a3\u4e00\u4f4d\u627e\u6211\uff1f\u201d \u3000\u3000\u9ad8\u58f0\u95ee\u8bdd\uff0c\u65e0\u7591\u5df2\u6fc0\u8d77\u9ad8\u767b\u9b41\u7684\u6012\u706b\uff0c\u7acb\u5373\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u5ba2\u5385\u91cc\u53ea\u6709\u5728\u4e0b\u8fd9\u4e48\u4e00\u4e2a\u751f \u4eba\uff0c\u4f60\u8fd8\u95ee\u4ec0\u4e48\uff1f\u2019\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u8fde\u5fd9\u62f1\u4e86\u62f1\u624b\u9053\uff1a\u201c\u539f\u6765\u662f\u5c0a\u9a7e\u627e\u6211\uff0c\u6709\u4ec0\u4e48\u8981\u4e8b\uff1f\u201d \u3000\u3000\u9ad8\u767b\u9b41\u731b\u5730\u7ad9\u8d77\u8eab\u6765\uff0c\u4e00\u628a\u6263\u4f4f\u5c0f\u71d5\u5b50\u8155\u8109\uff0c\u9053\uff1a\u201c\u968f\u6211\u5230\u5c71\u5be8\u4e00\u8d9f\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u5e76\u672a\u7529\u5f00\u5bf9\u65b9\u624b\u81c2\uff0c\u6014\u6014\u7684\u95ee\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u8981\u6211\u5230\u5c71\u5be8\u53bb\uff1f\u201d \u3000\u3000\u201c\u522b\u95ee\u4ec0\u4e48\u7406\u7531\uff0c\u53bb\u4e00\u8d9f\u5c31\u660e\u767d\u4e86\uff01\u201d \u3000\u3000\u795d\u9999\u4ead\u88c5\u51fa\u60f6\u6050\u65e0\u4e3b\u7684\u6a21\u6837\u9053\uff1a\u201c\u597d\u6c49\uff0c\u6709\u8bdd\u597d\u8bf4\uff0c\u4ed6\u662f\u4e2a\u8bfb\u4e66\u4eba\uff0c\u62c5\u4e0d\u5f97\u8fd9\u6837\u7684\u60ca \u6015\uff01\u201d \u3000\u3000\u9ad8\u767b\u9b41\u9634\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u8001\u7237\u5b50\uff0c\u4f60\u5982\u679c\u4e0d\u653e\u5fc3\uff0c\u90a3\u5c31\u8fde\u4f60\u4e00\u8d77\u4e5f\u5230\u5c71\u5be8\u53bb\u3002\u201d \u3000\u3000\u201c\u8fd9\u2026\u2026\u201d \u3000\u3000\u201c\u5982\u679c\u4f60\u4e0d\u80af\u53bb\uff0c\u5c31\u522b\u62e6\u963b\uff0c\u53cd\u6b63\u4f60\u4eec\u4e24\u4e2a\u81f3\u5c11\u8981\u53bb\u4e00\u4e2a\uff01\u201d \u3000\u3000\u6c5f\u5343\u91cc\u7ad9\u8d77\u8eab\u6765\u9053\uff1a\u201c\u795d\u5144\uff0c\u5c71\u5be8\u91cc\u5e76\u975e\u4e0d\u8bb2\u7406\u7684\u5730\u65b9\uff0c\u5c31\u7531\u4ee4\u5a7f\u53bb\u4e00\u8d9f\u5427\uff01\u201d \u3000\u3000\u9ad8\u767b\u9b41\u9053\uff1a\u201c\u8fd8\u662f\u8fd9\u4f4d\u8001\u5144\u770b\u5f97\u5f00\uff0c\u795d\u8001\u7237\u5b50\uff0c\u5728\u4e0b\u73b0\u5728\u5c31\u5e26\u4ee4\u5a7f\u4e0a\u5c71\u4e86\uff0c\u4f60\u4eec\u4e0d\u5fc5\u9001 \u4e86\uff0c\u4ed6\u5f88\u5feb\u5c31\u4f1a\u56de\u6765\uff01\u201d \u3000\u3000\u795d\u9999\u4ead\u88c5\u4f5c\u8981\u5411\u524d\u62c9\uff0c\u88ab\u9ad8\u767b\u9b41\u4e00\u628a\u63a8\u5f00\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u8fd9\u624d\u4e00\u7529\u624b\u81c2\u9053\uff1a\u201c\u7528\u4e0d\u7740\u62c9\uff0c\u5728\u4e0b\u8ddf\u4f60\u4eec\u53bb\u5c31\u662f\u4e86\uff01\u201d \u3000\u3000\u9ad8\u767b\u9b41\u51b7\u7b11\u9053\uff1a\u201c\u8c05\u4f60\u4e5f\u8dd1\u4e0d\u6389\uff0c\u8d70\u5427\uff01\u201d \u3000\u3000\u4e8e\u662f\uff0c\u9648\u3001\u8c22\u4e24\u507b\u7f57\u5728\u524d\uff0c\u5c0f\u71d5\u5b50\u5728\u4e2d\uff0c\u9ad8\u767b\u9b41\u5728\u540e\uff0c\u79bb\u5f00\u795d\u5b85\u3002 \u3000\u3000\u795d\u9999\u4ead\u88c5\u51fa\u5927\u611f\u4e0d\u5b89\u7684\u6837\u5b50\uff0c\u8ddf\u51fa\u4e86\u5927\u95e8\uff0c\u76f4\u5230\u4eba\u5f71\u4e0d\u89c1\u624d\u56de\u8eab\u628a\u95e8\u5173\u4e0a\u3002 \u3000\u3000\u5927\u7ea6\u8d70\u51fa\u534a\u4e2a\u65f6\u8fb0\u4ee5\u540e\uff0c\u4fbf\u8fdb\u5165\u4e00\u6761\u4e24\u65c1\u5168\u662f\u5de8\u677e\u5939\u9053\u7684\u5c0f\u5f84\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u505c\u4e0b\u811a\u6b65\u9053\uff1a\u201c\u597d\u6c49\uff0c\u6211\u5b9e\u5728\u592a\u7d2f\u4e86\uff0c\u8ba9\u6211\u4f11\u606f\u4e00\u4e0b\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u9ad8\u767b\u9b41\u9053\uff1a\u201c\u53ea\u8d70\u4e86\u5341\u51e0\u91cc\u8def\u4fbf\u8d70\u4e0d\u52a8\uff0c\u4f60\u5c0f\u5b50\u7b80\u76f4\u662f\u4e2a\u996d\u6876\uff01\u201d \u3000\u3000\u201c\u597d\u6c49\u600e\u4e48\u9a82\u4eba\uff1f\u201d \u3000\u3000\u201c\u9a82\u4eba\uff1f\u73b0\u5728\u5c31\u5b9e\u5bf9\u4f60\u8bf4\u5427\uff01\u8fdb\u4e86\u5c71\u5be8\uff0c\u4f60\u5c31\u522b\u60f3\u518d\u51fa\u6765\u4e86\uff01\u201d \u3000\u3000\u201c\u4f60\u4eec\u51c6\u5907\u628a\u6211\u600e\u6837\uff1f\u201d \u3000\u3000\u201c\u90a3\u5c31\u8981\u770b\u5be8\u4e3b\u5982\u4f55\u5904\u7f6e\u4e86\uff01\u201d \u3000\u3000\u201c\u8d35\u5be8\u5be8\u4e3b\u4e0d\u662f\u8fd8\u6ca1\u56de\u53bb\u5417\uff1f\u201d \u3000\u3000\u201c\u4ed6\u8001\u4eba\u5bb6\u4e5f\u8bb8\u73b0\u5728\u65e9\u5c31\u56de\u53bbJ.\u201d \u3000\u3000\u53ea\u542c\u90a3\u59d3\u9648\u5657\u7f57\u9053\uff1a\u201c\u9999\u4e3b\uff0c\u4f60\u8bf4\u8fd9\u5c0f\u5b50\u662f\u996d\u6876\uff0c\u4f46\u4ed6\u504f\u504f\u80fd\u8ba8\u4e00\u4e2a\u5927\u7f8e\u4eba\u505a\u8001\u5a46\u3002\u201d \u3000\u3000\u201c\u53ef\u60dc\u90a3\u5927\u7f8e\u4eba\u5df2\u7ecf\u4e0d\u662f\u4ed6\u7684\u4e86\u3002\u201d \u3000\u3000\u53ea\u542c\u5c0f\u71d5\u5b50\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u65e9\u77e5\u8bfb\u4e66\u65e0\u7528\uff0c\u6211\u5c31\u8be5\u5b66\u70b9\u6b66\u529f\uff0c\u8ddf\u4f60\u4eec\u4e00\u6837\uff0c\u90a3\u6837\u8c01\u8fd8\u6562 \u9738\u5360\u6211\u7684\u8001\u5a46\uff1f\u201d \u3000\u3000\u201c\u4f60\u5982\u679c\u60f3\u5b66\uff0c\u73b0\u5728\u4e5f\u6765\u5f97\u53ca\u3002\u201d \u3000\u3000\u201c\u597d\u6c49\u8fd9\u8bdd\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u53ef\u4ee5\u9a6c\u4e0a\u6559\u4f60\u3002\u201d \u3000\u3000\u201c\u90a3\u592a\u597d\u4e86\uff0c\u597d\u6c49\u73b0\u5728\u5c31\u6559\u5427\uff01\u201d \u3000\u3000\u201c\u54b1\u4eec\u5148\u8bb2\u660e\u767d\uff0c\u5b66\u6b66\u529f\u5fc5\u5b9a\u8981\u5403\u4e9b\u82e6\u5934\uff0c\u5f85\u4f1a\u513f\u5403\u4e86\u82e6\u5934\u4e0d\u80fd\u6028\u6211\uff1f\u201d \u3000\u3000\u201c\u8fd9\u70b9\u82e6\u6211\u4e0d\u5728\u4e4e\uff0c\u4e0d\u5403\u82e6\u4e2d\u82e6\uff0c\u96be\u4e3a\u4eba\u4e0a\u4eba\uff0c\u8fd9\u9053\u7406\u6211\u61c2\u3002\u201d \u3000\u3000\u9ad8\u767b\u9b41\u671b\u4e86\u9648\u3001\u8c22\u4e24\u5624\u7f57\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u770b\uff0c\u8fd9\u5c0f\u5b50\u597d\u50cf\u6d3b\u5f97\u6709\u70b9\u4e0d\u8010\u70e6\u4e86\uff01\u201d \u3000\u3000\u9648\u59d3\u5657\u7f57\u9053\uff1a\u201c\u8fd9\u662f\u4ed6\u81ea\u5df1\u627e\u7684\uff0c\u6028\u4e0d\u5f97\u9999\u4e3b\u3002\u201d \u3000\u3000\u201c\u597d\u5427\uff01\u6211\u73b0\u5728\u5c31\u6559\u4ed6\u51e0\u624b\u3002\u201d \u3000\u3000\u9ad8\u767b\u9b41\u8bdd\u521a\u8bf4\u5b8c\uff0c\u4fbf\u4e00\u8bb0\u201c\u9ed1\u864e\u638f\u5fc3\u201d\u63a2\u624b\u5411\u5c0f\u71d5\u5b50\u524d\u80f8\u6293\u6765\u3002 \u3000\u3000\u4ed6\u773c\u89c1\u5c0f\u71d5\u5b50\u5c31\u5728\u8eab\u524d\u4e0d\u8db3\u4e09\u5c3a\uff0c\u5c82\u77e5\u8fd9\u4e00\u6293\u4e0d\u4f46\u672a\u6293\u5230\uff0c\u53cd\u800c\u8d8a\u6293\u8d8a\u8fdc\uff0c\u5f53\u7eb5\u8eab\u4e00\u6b65 \u4e94\u6307\u63a2\u51fa\u540e\uff0c\u5bf9\u65b9\u7adf\u5df2\u8fdc\u5728\u4e03\u516b\u6b65\u5916\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u7a76\u7adf\u7528\u7684\u4ec0\u4e48\u95ee\u907f\u8eab\u6cd5\uff1f\u4e0d\u4f46\u5f53\u4e8b\u8005\u7684\u9ad8\u767b\u9b41\u6ca1\u770b\u6e05\u695a\uff0c\u8fde\u7ad9\u5728\u4e00\u65c1\u89c2\u77a7\u7684\u9648\u3001 \u8c22\u4e24\u540d\u6b65\u7f57\u4e5f\u5168\u50cf\u9047\u4e0a\u4e86\u969c\u773c\u6cd5\u3002 \u3000\u3000\u9ad8\u767b\u9b41\u5446\u4e86\u4e00\u5446\uff0c\u624d\u8f6c\u52a8\u7740\u4e24\u773c\u9053\uff1a\u201c\u83ab\u975e\u4f60\u8fd9\u5c0f\u5b50\u8fd8\u5b66\u8fc7\u8ff7\u8e2a\u6b65\u6cd5\uff1f\u201d \u3000\u3000\u63a5\u7740\uff0c\u817e\u8eab\u4e00\u5f0f\u201c\u997f\u864e\u6251\u7f8a\u201d\u76f4\u5411\u5c0f\u71d5\u5b50\u6251\u53bb\uff0c\u540c\u65f6\u53c8\u8d81\u52bf\u63a8\u51fa\u4e00\u638c\u3002\u4ed6\u8fd9\u817e\u8eab\u4e00\u6251\uff0c \u81f3\u5c11\u6251\u51fa\u4e08\u4f59\u5f00\u5916\u3002 \u3000\u3000\u54ea\u77e5\u8fd9\u6b21\u817e\u8eab\u53d1\u638c\u4e4b\u540e\uff0c\u7adf\u7136\u5931\u53bb\u4e86\u5c0f\u71d5\u5b50\u7684\u4eba\u5f71\u3002 \u3000\u3000\u6b63\u5728\u5927\u611f\u6115\u7136\u4e4b\u9645\uff0c\u53ea\u542c\u5c0f\u71d5\u5b50\u5728\u8eab\u540e\u9053\uff1a\u201c\u597d\u6c49\uff0c\u8fd9\u6b21\u4f60\u51b2\u8fc7\u5934\u4e86\uff01\u201d \u3000\u3000\u9ad8\u767b\u9b41\u7ec8\u4e8e\u77e5\u9053\u5bf9\u65b9\u662f\u6df1\u85cf\u4e0d\u9732\uff0c\u4f46\u5374\u4ecd\u672a\u653e\u5728\u5fc3\u4e0a\uff0c\u8fd4\u8eab\u4e00\u8dc3\uff0c\u53c8\u662f\u4e00\u638c\u51fb\u4e0b\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u4e0d\u518d\u907f\u8ba9\uff0c\u4e5f\u62a2\u81c2\u4e00\u638c\u8fce\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u53cc\u638c\u63a5\u5b9e\u4e4b\u540e\uff0c\u201c\u5f6d\u201d\u7684\u4e00\u58f0\u5de8\u54cd\uff0c\u9ad8\u767b\u9b41\u7acb\u5373\u5411\u540e\u5012\u6454\u51fa\u4e24\u4e09\u4e08\u8fdc\uff0c\u843d\u5730\u4e4b\u540e\u5df2\u662f\u8dcc \u5f97\u4e03\u8364\u516b\u7d20\uff0c\u6839\u672c\u722c\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u9648\u3001\u8c22\u4e24\u540d\u55bd\u7f57\u65e9\u5df2\u5413\u5f97\u76ee\u77aa\u53e3\u5446\uff0c\u5728\u4e00\u5446\u4e4b\u540e\uff0c\u6492\u817f\u5c31\u8dd1\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u54ea\u80fd\u8ba9\u4ed6\u4eec\u9003\u8131\uff0c\u8eab\u5f62\u6709\u5982\u7535\u5c04\uff0c\u4eba\u5728\u534a\u7a7a\uff0c\u53cc\u638c\u9f50\u51fa\uff0c\u5206\u522b\u51fb\u4e2d\u4e24\u4eba\u540e\u5fc3\u3002 \u3000\u3000\u9648\u3001\u8c22\u4e24\u55bd\u7f57\u5404\u81ea\u55b7\u51fa\u4e00\u53e3\u9c9c\u8840\uff0c\u53cc\u53cc\u5012\u5730\u6c14\u7edd\u3002 \u3000\u3000\u5f53\u5c0f\u71d5\u5b50\u518d\u56de\u8eab\u540e\uff0c\u6b63\u597d\u9ad8\u767b\u9b41\u5df2\u7ecf\u722c\u8d77\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u8ddf\u8fc7\u53bb\u53c8\u662f\u4e00\u638c\u3002 \u3000\u3000\u9ad8\u767b\u9b41\u7d27\u8ddf\u7740\u4e5f\u4e00\u547d\u5f52\u9634\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u56e0\u8eab\u4e0a\u672a\u5e26\u5175\u5203\uff0c\u4e09\u540d\u532a\u5f92\u4e5f\u90fd\u662f\u8d64\u624b\u7a7a\u62f3\uff0c\u627e\u4e0d\u5230\u5bb6\u4f19\u6398\u5751\u57cb\u5c38\uff0c\u53ea\u597d\u628a\u4e09\u5177 \u5c38\u4f53\u4e22\u8fdb\u957f\u53ef\u53ca\u8170\u7684\u4e1b\u8349\u4e2d\u3002 \u3000\u3000\u53ea\u8981\u65e0\u4eba\u6253\u6b64\u7ecf\u8fc7\uff0c\u4e0d\u81f4\u88ab\u4eba\u53d1\u89c9\u3002 \u3000\u3000\u4ed6\u6413\u4e86\u6413\u624b\uff0c\u968f\u5373\u5b89\u95f2\u7684\u79bb\u5f00\u6811\u6797\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u56de\u5230\u795d\u5b85\uff0c\u5411\u6c5f\u5343\u91cc\u62a5\u544a\u4e86\u7ecf\u8fc7\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u6682\u65f6\u4e0d\u4f1a\u6709\u4e8b\uff0c\u7b49\u771f\u6b63\u6709\u4e8b\u65f6\uff0c\u6b63\u597d\u5c11\u6797\u7684\u4eba\u9a6c\u5df2\u5230\uff0c\u90a3\u65f6\u5c31\u5c11\u4e0d\u5f97\u8981\u6765 \u4e00\u573a\u5927\u706b\u62da\u4e86\u3002\u201d \u3000\u3000\u4e00\u8fde\u4e24\u5929\u8fc7\u53bb\uff0c\u679c\u7136\u6beb\u65e0\u52a8\u9759\u3002 \u3000\u3000\u5728\u8fd9\u4e24\u5929\u91cc\uff0c\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u66fe\u6d3e\u51fa\u5c0f\u71d5\u5b50\u3001\u4e03\u5de7\u501f\u3001\u767d\u7fbd\u7b49\u4eba\u4e8e\u5165\u591c\u540e\u6f5c\u5165\u8d3c\u5be8\u63a2\u6d4b\u865a \u5b9e\u3002 \u3000\u3000\u6839\u636e\u63a2\u67e5\u7684\u7ed3\u679c\uff0c\u6842\u98d8\u9999\u7b49\u4e09\u540d\u5973\u5996\u4ecd\u5728\u5c71\u5be8\uff0c\u672a\u66fe\u897f\u884c\u3002 \u3000\u3000\u53e6\u5916\uff0c\u9c7c\u5316\u9f99\u5728\u95fb\u77e5\u767d\u864e\u5931\u8e2a\u6d88\u606f\u540e\uff0c\u5fd9\u7531\u5f00\u5c01\u8d76\u6765\u666e\u6551\u5bfa\uff0c\u53ea\u662f\u5c1a\u4e0d\u77e5\u4ed6\u5c06\u91c7\u53d6\u4f55\u79cd \u884c\u52a8\u3002 \u3000\u3000\u5f53\u6c5f\u5343\u91cc\u548c\u738b\u5f64\u5f97\u77e5\u9c7c\u5316\u9f99\u5df2\u6765\u9099\u5c71\u7684\u6d88\u606f\uff0c\u6599\u5b9a\u5df2\u662f\u5c71\u96e8\u6b32\u6765\u3002 \u3000\u3000\u6b64\u523b\uff0c\u4ed6\u4eec\u6700\u76fc\u7684\u4fbf\u662f\u5c11\u6797\u65b9\u9762\u7684\u9ad8\u624b\u80fd\u706b\u901f\u8d76\u5230\u3002 \u3000\u3000\u5c31\u5728\u7b2c\u4e09\u5929\u7684\u5165\u591c\u540e\uff0c\u524d\u5f80\u5c11\u6797\u6c42\u63f4\u7684\u97e9\u6d9b\u966a\u540c\u4e00\u540d\u5b9d\u76f8\u5e84\u4e25\u7684\u8001\u50e7\u6765\u5230\u795d\u5b85\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u548c\u738b\u5f64\u90fd\u8ba4\u8bc6\u8fd9\u8001\u50e7\uff0c\u662f\u5c11\u6797\u5bfa\u8fbe\u6469\u9662\u4f4f\u6301\u5927\u89c9\u5927\u5e08\uff0c\u8fde\u5fd9\u4e0a\u524d\u89c1\u793c\uff0c\u5e76\u901a\u77e5\u4f4f \u5728\u795d\u5b85\u9664\u4e09\u516c\u4e3b\u4ee5\u5916\u7684\u6240\u6709\u7684\u4eba\u51fa\u6765\u76f8\u89c1\u3002 \u3000\u3000\u5927\u89c9\u5927\u5e08\u53cc\u638c\u5408\u5341\u9053\uff1a\u201c\u8d2b\u50e7\u5949\u638c\u95e8\u5e08\u5144\u4e4b\u547d\uff0c\u7387\u9886\u4e09\u5341\u516d\u540d\u5f1f\u5b50\u524d\u6765\u542c\u5019\u738b\u5927\u4eba\u548c\u6c5f \u65bd\u4e3b\u8c03\u9063\u3002\u201d \u3000\u3000\u738b\u5f64\u5927\u559c\u8fc7\u671b\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u5927\u5e08\u7684\u5404\u4f4d\u9ad8\u8db3\u4eba\u5728\u4f55\u5904\uff0c\u5feb\u5feb\u8bf7\u5230\u91cc\u9762\u6765\u3002\u201d \u3000\u3000\u5927\u89c9\u5927\u5e08\u9053\uff1a\u201c\u8fd9\u9644\u8fd1\u6709\u5ea7\u767d\u5854\u5bfa\uff0c\u5bfa\u5185\u4f4f\u6301\u51fa\u8eab\u5c11\u6797\uff0c\u662f\u8001\u8896\u7684\u5e08\u4f84\uff0c\u8001\u8872\u5df2\u628a\u5e26\u6765 \u7684\u5f1f\u5b50\u5b89\u7f6e\u5728\u767d\u5854\u5bfa\u4e86\u3002\u201d \u3000\u3000\u201c\u79bb\u8fd9\u91cc\u6709\u591a\u8fdc\uff1f\u201d \u3000\u3000\u795d\u9999\u4ead\u62a2\u7740\u7b54\u9053\uff1a\u201c\u5c31\u5728\u897f\u5317\u65b9\uff0c\u79bb\u820d\u4e0b\u4e0d\u8fc7\u4e00\u91cc\u591a\u8def\u3002\u201d \u3000\u3000\u738b\u5f64\u5927\u559c\u9053\uff1a\u201c\u90a3\u592a\u597d\u4e86\uff0c\u56de\u5934\u738b\u67d0\u5c31\u6d3e\u51fa\u4e00\u4eba\u4f4f\u8fdb\u767d\u5854\u5bfa\u8d1f\u8d23\u8fde\u7edc\uff0c\u4e5f\u8bf7\u5927\u5e08\u6d3e\u4e00\u540d \u5f1f\u5b50\u5230\u8fd9\u91cc\u6765\u3002\u201d \u3000\u3000\u201c\u8001\u8872\u9075\u547d\uff01\u201d \u3000\u3000\u201c\u5927\u5e08\u957f\u9014\u8dcb\u6d89\uff0c\u4e00\u8def\u8f9b\u82e6\uff0c\u5c31\u8bf7\u56de\u767d\u5854\u5bfa\u4f11\u606f\uff0c\u738b\u67d0\u548c\u6c5f\u5144\u660e\u5929\u4e00\u65e9\u518d\u5230\u767d\u5854\u5bfa\u62dc \u5019\u3002\u201d \u3000\u3000\u5c31\u5728\u6b21\u65e5\u51cc\u6668\u56db\u66f4\u5de6\u53f3\uff0c\u795d\u5b85\u6240\u6709\u7684\u4eba\u6b63\u5728\u719f\u7761\u4e2d\u3002 \u3000\u3000\u5ffd\u542c\u8d1f\u8d23\u8f6e\u503c\u8b66\u6212\u7684\u5c0f\u9053\u58eb\u767d\u7fbd\u5728\u5929\u4e95\u4e2d\u558a\u9053\uff1a\u201c\u5927\u5bb6\u5feb\u8d77\u6765\uff0c\u5c71\u4e0a\u6709\u5927\u961f\u4eba\u9a6c\u5f00\u5230\u54b1 \u4eec\u8fd9\u91cc\u6765\u4e86\uff01\u201d \u3000\u3000\u8fd9\u4e00\u58f0\u558a\u53eb\uff0c\u6240\u6709\u7684\u4eba\u5f88\u5feb\u4fbf\u4ece\u7761\u68a6\u4e2d\u60ca\u9192\uff0c\u7eb7\u7eb7\u5954\u51fa\u5929\u4e95\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u3001\u738b\u5f64\u98de\u8eab\u4e0a\u623f\u3002 \u3000\u3000\u767d\u7fbd\u548c\u5c0f\u71d5\u5b50\u4e5f\u8ddf\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u7531\u4e8e\u6708\u5c1a\u672a\u843d\uff0c\u56db\u4eba\u53c8\u76ee\u529b\u5f02\u4e8e\u5e38\u4eba\uff0c\u8db3\u53ef\u671b\u51fa\u534a\u91cc\u4e4b\u5916\u3002 \u3000\u3000\u53ea\u89c1\u5c71\u4e0a\u679c\u7136\u4eba\u5f71\u5e62\u5e62\uff01 \u3000\u3000\u4f30\u8ba1\u4e0d\u4e0b\u6570\u767e\u540d\u4e4b\u591a\uff0c\u6d69\u6d69\u8361\u8361\u8fe4\u4fea\u800c\u6765\u3002 \u3000\u3000\u738b\u5f64\u7acb\u5373\u4ea4\u4ee3\u5927\u89c9\u7985\u5e08\u6d3e\u6765\u7684\u90a3\u540d\u5f1f\u5b50\u901f\u901f\u8d76\u5f80\u767d\u5854\u5bfa\u901a\u62a5\uff0c\u4e00\u9762\u518d\u547d\u6240\u6709\u7684\u4eba\u56de\u623f\u53d6 \u6765\u5175\u5203\uff0c\u51c6\u5907\u8fce\u654c\u3002 \u3000\u3000\u4e0d\u6d88\u7247\u523b\uff0c\u5176\u4f59\u6240\u6709\u7684\u4eba\u5df2\u5404\u4ed7\u5175\u5203\u9f50\u96c6\u5929\u4e95\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u4e00\u6761\u4eba\u5f71\u7531\u53a2\u623f\u5916\u8dc3\u8fdb\u5929\u4e95\uff0c\u8d6b\u7136\u662f\u859b\u767e\u80dc\u3002 \u3000\u3000\u738b\u5f64\u60ca\u53eb\u9053\uff1a\u201c\u859b\u8001\u5f1f\uff0c\u4f60\u600e\u4e48\u6765\u4e86\uff1f\u201d \u3000\u3000\u201c\u6211\u6765\u5411\u738b\u5927\u4eba\u548c\u6c5f\u524d\u8f88\u793a\u8b66\uff0c\u8fd8\u597d\u8fd9\u8fb9\u7684\u4eba\u5927\u5bb6\u90fd\u8d77\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u6765\u7684\u90fd\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u666e\u6551\u5bfa\u5c71\u5be8\u91cc\u7684\u76d7\u5bc7\uff0c\u5df2\u503e\u5de2\u800c\u6765\u3002\u201d \u3000\u3000\u201c\u4e3a\u9996\u7684\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u9c7c\u5316\u9f99\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\uff1f\u9c7c\u5316\u9f99\u4e5f\u6765\u4e86\uff1f\u4ed6\u662f\u5426\u672c\u6765\u5c31\u5728\u9099\u5c71\uff1f\u201d \u3000\u3000\u201c\u4ed6\u662f\u6628\u5929\u95fb\u62a5\u7531\u5f00\u5c01\u8d76\u6765\u7684\u3002\u201d \u3000\u3000\u201c\u9c7c\u5316\u9f99\u662f\u5426\u5df2\u5f97\u77e5\u738b\u67d0\u548c\u6c5f\u5927\u4fa0\u7b49\u4eba\u4f4f\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u201c\u9c7c\u5316\u9f99\u5bf9\u8fd9\u8fb9\u7684\u60c5\u5f62\u5df2\u4e86\u82e5\u6307\u638c\u3002\u201d \u3000\u3000\u201c\u4ed6\u65e2\u77e5\u4e09\u516c\u4e3b\u4e5f\u5728\u8fd9\u91cc\uff0c\u7adf\u6562\u516c\u7136\u6765\u72af\uff0c\u4e5f\u672a\u514d\u592a\u65e0\u6cd5\u65e0\u5929\u4e86\uff0c\u83ab\u975e\u4ed6\u662f\u5949\u9a6c\u6587\u4e2d\u4e4b \u547d\u800c\u6765\uff1f\u201d \u3000\u3000\u201c\u5f88\u96be\u8bf4\u3002\u201d \u3000\u3000\u201c\u8001\u5f1f\u662f\u600e\u4e48\u77e5\u9053\u738b\u67d0\u7b49\u4eba\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u662f\u4ece\u9c7c\u5316\u9f99\u90a3\u91cc\u5f97\u6765\u7684\u6d88\u606f\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u4f60\u6765\u62a5\u8baf\uff0c\u5c31\u4e0d\u6015\u88ab\u4ed6\u4eec\u67e5\u51fa\uff1f\u201d \u3000\u3000\u201c\u665a\u8f88\u6b63\u597d\u88ab\u6d3e\u6765\u63a2\u8def\u7684\uff0c\u738b\u5927\u4eba\u3001\u6c5f\u524d\u8f88\uff0c\u4f60\u4eec\u5feb\u5feb\u51c6\u5907\u8fce\u654c\u5427\uff0c\u6211\u8d70\u4e86\uff01\u201d \u3000\u3000\u859b\u767e\u80dc\u8bf4\u5b8c\u8bdd\uff0c\u817e\u8eab\u5148\u63a0\u4e0a\u5c4b\u9876\uff0c\u4eba\u5f71\u5f88\u5feb\u4fbf\u6d88\u901d\u4e0d\u89c1\u3002 \u3000\u3000\u5f53\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u518d\u8dc3\u4e0a\u5c4b\u9876\u65f6\uff0c\u9c7c\u5316\u9f99\u7684\u4eba\u9a6c\u5df2\u6210\u5408\u56f4\u4e4b\u52bf\uff0c\u5c06\u89c6\u5b85\u56e2\u56e2\u56f4\u4f4f\u3002 \u3000\u3000\u597d\u5728\u4ed6\u4eec\u5c1a\u672a\u8fc7\u5206\u63a5\u8fd1\uff0c\u5df2\u5728\u5341\u4f59\u652f\u5916\u505c\u6b62\u4e0b\u6765\u3002 \u3000\u3000\u7531\u4e8e\u8ddd\u79bb\u5df2\u8fd1\uff0c\u5df2\u53ef\u770b\u6e05\u76d7\u532a\u4eec\u4ecd\u662f\u4e2a\u4e2a\u5e26\u7740\u9ed1\u8272\u9762\u7f69\u3002 \u3000\u3000\u8fd9\u662f\u53ef\u4ee5\u60f3\u89c1\u7684\uff0c\u4ed6\u4eec\u5df2\u77e5\u4e09\u516c\u4e3b\u548c\u5927\u5185\u5f85\u536b\u7edf\u9886\u5728\u8fd9\u91cc\uff0c\u53c8\u662f\u5949\u9a6c\u6587\u4e2d\u4e4b\u547d\u800c\u6765\uff0c\u5f53 \u7136\u4e0d\u80fd\u660e\u76ee\u5f20\u80c6\u4e0e\u671d\u5ef7\u4e3a\u654c\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u7247\u523b\uff0c\u76d7\u532a\u4eec\u7684\u5305\u56f4\u5708\u5f00\u59cb\u7f29\u5c0f\u3002 \u3000\u3000\u7247\u523b\u4e4b\u540e\uff0c\u5df2\u63a5\u8fd1\u81f3\u53ea\u6709\u4e24\u4e09\u4e08\u8ddd\u79bb\u3002 \u3000\u3000\u6570\u767e\u4e4b\u4f17\u7684\u76d7\u532a\uff0c\u8db3\u8db3\u628a\u89c6\u5b85\u56f4\u4e86\u56db\u4e94\u5c42\uff0c\u51e0\u4e4e\u662f\u6c34\u6cc4\u4e0d\u901a\u3002 \u3000\u3000\u53ea\u542c\u4e00\u4e2a\u68ee\u51b7\u9634\u6c89\u7684\u58f0\u97f3\u4f4e\u58f0\u9053\uff1a\u201c\u603b\u5be8\u4e3b\uff0c\u770b\u6837\u5b50\u4ed6\u4eec\u4f3c\u4e4e\u5c1a\u672a\u8b66\u89c9\uff0c\u4f55\u4e0d\u73b0\u5c31\u4e0b\u4ee4 \u4e00\u4e3e\u51b2\u6740\u8fdb\u53bb\u3002\u201d \u3000\u3000\u53e6\u4e00\u4e2a\u6c89\u6d51\u7684\u58f0\u97f3\u9053\uff1a\u201c\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u662f\u4f55\u7b49\u4eba\uff1f\u600e\u4f1a\u6beb\u65e0\u8b66\u89c9\uff1f\u7740\u51b2\u6740\u8fdb\u53bb\uff0c\u5fc5\u5b9a\u5bf9 \u54b1\u4eec\u5927\u5927\u4e0d\u5229\u3002\u201d \u3000\u3000\u8fd9\u6700\u540e\u8bf4\u8bdd\u7684\uff0c\u5206\u660e\u6b63\u662f\u9c7c\u5316\u9f99\u3002 \u3000\u3000\u4ed6\u4eec\u867d\u7136\u8bf4\u8bdd\u58f0\u97f3\u751a\u4f4e\uff0c\u4f46\u9690\u8eab\u5728\u5c4b\u810a\u540e\u7684\u6c5f\u5343\u91cc\u548c\u738b\u5f64\u5374\u5df2\u6e05\u6670\u53ef\u95fb\u3002 \u3000\u3000\u9c7c\u5316\u9f99\u7167\u6837\u4e5f\u662f\u5934\u6234\u9762\u7f69\uff0c\u53ea\u662f\u9762\u7f69\u989c\u8272\u6709\u522b\uff0c\u4e0d\u5916\u662f\u4fbf\u4e8e\u6307\u6325\u4e4b\u6545\u3002 \u3000\u3000\u90a3\u68ee\u51b7\u9634\u6c89\u7684\u58f0\u97f3\u95ee\u9053\uff1a\u201c\u51b2\u8fdb\u53bb\u5bf9\u54b1\u4eec\u6709\u4ec0\u4e48\u4e0d\u5229\uff1f\u201d \u3000\u3000\u9c7c\u6bd4\u9f99\u9053\uff1a\u201c\u4ed6\u4eec\u4eba\u6570\u867d\u5c11\uff0c\u4f46\u5374\u4e2a\u4e2a\u662f\u7edd\u9876\u9ad8\u624b\uff0c\u5c24\u5176\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u6b66\u529f\u66f4\u662f\u7f55\u89c1\uff0c \u5929\u4e95\u5185\u573a\u5730\u72ed\u5c0f\uff0c\u54b1\u4eec\u4eba\u867d\u7136\u591a\uff0c\u5374\u5bb9\u7eb3\u4e0d\u4e0b\u51e0\u4e2a\uff0c\u8fdb\u53bb\u4e4b\u540e\uff0c\u4e0d\u7b49\u4e8e\u662f\u7ed9\u4e86\u4ed6\u4eec\u5404\u4e2a\u51fb\u7834 \u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u201c\u4f9d\u603b\u5be8\u4e3b\u4e4b\u89c1\uff1f\u201d \u3000\u3000\u201c\u8fd8\u662f\u8bbe\u6cd5\u628a\u4ed6\u4eec\u5f15\u51fa\u6765\u7684\u597d\u3002\u201d \u3000\u3000\u201c\u90a3\u5f88\u7b80\u5355\uff0c\u6d3e\u4eba\u5728\u5c4b\u540e\u7eb5\u706b\u3002\u201d \u3000\u3000\u8fd9\u65f6\u767d\u7fbd\u6b63\u597d\u5728\u738b\u5f64\u8eab\u540e\uff0c\u5fd9\u4f4e\u58f0\u9053\uff1a\u201c\u738b\u5927\u4eba\uff0c\u9632\u706b\u7684\u4e8b\u5c31\u4ea4\u7ed9\u6211\u529e\u4e86\uff01\u201d \u3000\u3000\u8bf4\u5b8c\u8bdd\uff0c\u98d8\u8eab\u843d\u4e0b\u5929\u4e95\uff0c\u6361\u4e86\u4e00\u5927\u628a\u77f3\u5757\uff0c\u7136\u540e\u5c06\u540e\u7a97\u6253\u5f00\uff0c\u7b49\u5019\u5bf9\u65b9\u653e\u706b\u7684\u4eba\u524d\u6765\u3002 \u3000\u3000\u679c\u7136\uff0c\u5f88\u5feb\u4fbf\u6709\u516d\u4e03\u540d\u55bd\u7f57\uff0c\u5404\u81ea\u62b1\u7740\u5e72\u8349\u6765\u5230\u795d\u5e9c\u5b85\u540e\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u5c1a\u672a\u6765\u5f97\u53ca\u70b9\u71c3\uff0c\u4fbf\u88ab\u767d\u7fbd\u626c\u8155\u6295\u63b7\u51fa\u7684\u77f3\u5b50\u51fb\u4e2d\uff0c\u4e00\u4e2a\u4e2a\u60e8\u547c\u8fde\u58f0\uff0c\u7acb\u523b\u56db\u6563 \u800c\u9003\u3002 \u3000\u3000\u5176\u5b9e\uff0c\u795d\u5b85\u5168\u662f\u77f3\u5899\u74e6\u9876\uff0c\u5373\u4f7f\u7531\u5c4b\u540e\u70b9\u71c3\u4e86\u5e72\u8349\uff0c\u5b85\u9662\u4e5f\u4e0d\u89c1\u5f97\u80fd\u906d\u5230\u706b\u711a\uff0c\u9664\u975e\u7531 \u91cc\u9762\u70e7\u8d77\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u795d\u5b85\u5185\u7684\u4eba\u9664\u6d3e\u51fa\u4e24\u540d\u5b88\u5728\u5927\u95e8\u5185\uff0c\u5176\u4f59\u51e0\u4e4e\u5168\u5df2\u767b\u4e0a\u5c4b\u9876\uff0c\u53ea\u662f\u5c1a\u672a\u516c\u7136\u73b0 \u8eab\uff0c\u8fde\u4e09\u516c\u4e3b\u4e5f\u51b3\u5b9a\u4eb2\u81ea\u8fce\u6218\u3002 \u3000\u3000\u53ea\u6709\u89c6\u9999\u4ead\u548c\u957f\u5de5\u8bb8\u6709\u7530\u4ee5\u53ca\u90a3\u5973\u4f63\u8eb2\u5728\u5ba2\u5385\u91cc\u6ca1\u51fa\u6765\u3002 \u3000\u3000\u9c7c\u5316\u9f99\u89c1\u653e\u706b\u4e0d\u6210\uff0c\u53c8\u4e45\u65e0\u52a8\u9759\uff0c\u53ea\u597d\u51b3\u5b9a\u53d1\u8d77\u653b\u51fb\u3002 \u3000\u3000\u76d7\u532a\u4eec\u8f7b\u529f\u9ad8\u7684\u5168\u7531\u5916\u9762\u76f4\u63a5\u8dc3\u4e0a\u5c4b\u9876\uff0c\u51c6\u5907\u6740\u8fdb\u5929\u4e95\u3002 \u3000\u3000\u90a3\u4e9b\u8f7b\u529f\u5dee\u7684\u53ea\u80fd\u7531\u5927\u95e8\u653b\u8fdb\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u548c\u738b\u5f64\u4e00\u89c1\u5bf9\u65b9\u5df2\u6b63\u5f0f\u53d1\u8d77\u653b\u51fb\uff0c\u7acb\u5373\u5728\u5c4b\u9876\u4e0a\u5c55\u5f00\u622a\u51fb\u3002 \u3000\u3000\u76d7\u532a\u4eec\u5982\u4f55\u662f\u6c5f\u3001\u738b\u3001\u5c0f\u71d5\u5b50\u7b49\u4eba\u7684\u5bf9\u624b\uff0c\u521a\u8dc3\u4e0a\u5c4b\u9876\uff0c\u4fbf\u5341\u6709\u516b\u4e5d\u6a2a\u5c38\u5012\u6389\u800c\u4e0b\uff0c\u7b2c \u4e00\u6ce2\u51b2\u4e0a\u6765\u7684\u4e8c\u4e09\u5341\u4eba\uff0c\u5e76\u65e0\u4e00\u4eba\u80fd\u8fdb\u5165\u5929\u4e95\uff0c\u88ab\u6740\u9000\u4e0b\u53bb\u7684\u4e5f\u6b7b\u4f24\u5927\u534a\u3002 \u3000\u3000\u5b88\u62a4\u5927\u95e8\u7684\u662f\u4e03\u5de7\u50e7\u548c\u738b\u91cd\u5c71\u3002 \u3000\u3000\u4e24\u4eba\u63a9\u8eab\u5728\u5927\u95e8\u540e\u65b9\u4e24\u4fa7\uff0c\u8fdb\u4e00\u4e2a\u6740\u4e00\u4e2a\uff0c\u5f53\u6740\u5230\u7b2c\u4e03\u516b\u4e2a\u65f6\uff0c\u5927\u95e8\u5df2\u88ab\u5c38\u4f53\u5835\u4f4f\uff0c\u540e \u5230\u7684\u6839\u672c\u5df2\u65e0\u6cd5\u518d\u51b2\u8fdb\u6765\u3002 \u3000\u3000\u9c7c\u5316\u9f99\u89c1\u7b2c\u4e00\u6ce2\u653b\u51fb\u5b8c\u5168\u5931\u8d25\uff0c\u518d\u4e0b\u4ee4\u53d1\u8d77\u7b2c\u4e8c\u6ce2\u653b\u51fb\u3002 \u3000\u3000\u8fd9\u6b21\u62e5\u4e0a\u6765\u7684\u4e0d\u4e0b\u767e\u4eba\u4e4b\u6570\uff0c\u800c\u4e2a\u4e2a\u608d\u4e0d\u754f\u6b7b\u3002 \u3000\u3000\u53ef\u60dc\u608d\u4e0d\u754f\u6b7b\u662f\u4e00\u56de\u4e8b\uff0c\u7167\u6b7b\u4e0d\u8bef\u53c8\u662f\u4e00\u56de\u4e8b\u3002 \u3000\u3000\u4ed6\u4eec\u867d\u7136\u4e2a\u4e2a\u8eab\u624b\u4e0d\u5f31\uff0c\u4f46\u662f\u78b0\u4e0a\u738b\u5f64\u3001\u6c5f\u5343\u91cc\u3001\u5c0f\u71d5\u5b50\u8fd9\u4e9b\u7edd\u9876\u9ad8\u624b\uff0c\u6b7b\u5f97\u8fd8\u662f\u5341\u5206 \u5e72\u8106\u3002 \u3000\u3000\u9c7c\u5316\u9f99\u89c1\u8fd9\u6837\u4e0b\u53bb\u4f24\u4ea1\u592a\u91cd\uff0c\u53ea\u597d\u4e0b\u4ee4\u64a4\u9000\u3002 \u3000\u3000\u53cc\u65b9\u5f88\u5feb\u4fbf\u53c8\u9759\u6b62\u4e0b\u6765\u3002 \u3000\u3000\u8fd9\u65f6\u9c7c\u5316\u9f99\u6700\u76fc\u671b\u7684\u4fbf\u662f\u6c5f\u5343\u91cc\u548c\u738b\u5f64\u7b49\u4eba\u80fd\u51b2\u51fa\u6237\u5916\u3002 \u3000\u3000\u504f\u504f\u795d\u5b85\u7684\u4eba\u5374\u59cb\u7ec8\u4e0d\u79bb\u795d\u5b85\u4e00\u6b65\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5ffd\u7136\u9c7c\u5316\u9f99\u90e8\u5c5e\u540e\u65b9\u9635\u89d2\u5927\u4e71\uff0c\u63a5\u7740\u6740\u58f0\u9707\u5929\uff0c\u5175\u5203\u4ea4\u51fb\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\u3002\u6beb \u65e0\u7591\u95ee\uff0c\u5fc5\u662f\u9a7b\u624e\u5728\u767d\u5854\u5bfa\u7684\u5c11\u6797\u9ad8\u624b\u5df2\u524d\u6765\u63a5\u5e94\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u6c5f\u5343\u91cc\u548c\u738b\u5f64\u5df2\u65e0\u6cd5\u95ed\u95e8\u4e0d\u51fa\uff0c\u7acb\u5373\u4e0b\u4ee4\u51b2\u6740\u51fa\u53bb\uff0c\u5411\u76d7\u532a\u5c55\u5f00\u4e24\u9762\u5939\u51fb\u3002 \u3000\u3000\u8fd9\u4e09\u5341\u516d\u540d\u5c11\u6797\u5f1f\u5b50\uff0c\u5168\u662f\u7ecf\u8fc7\u7cbe\u6311\u7ec6\u9009\u7684\u9ad8\u624b\uff0c\u53c8\u52a0\u4e0a\u4ed6\u4eec\u91c7\u53d6\u7684\u662f\u201c\u4e00\u70b9\u7a81\u7834\u201d\u6218 \u672f\uff0c\u5f53\u771f\u6765\u52bf\u9510\u4e0d\u53ef\u5f53\u3002\u5f53\u738b\u5f64\u3001\u6c5f\u5343\u91cc\u7b49\u4eba\u548c\u5c11\u6797\u5f1f\u5b50\u53cc\u65b9\u4f1a\u5408\u4e4b\u540e\uff0c\u76d7\u532a\u4eec\u65e9\u5df2\u5c38\u6a2a\u904d \u91ce\u4e86\u3002 \u3000\u3000\u9c7c\u5316\u9f99\u4e0d\u613f\u4f24\u4ea1\u7ee7\u7eed\u52a0\u91cd\uff0c\u53ea\u6709\u4e0b\u4ee4\u5c06\u4eba\u9a6c\u64a4\u56de\u666e\u6551\u5bfa\u3002 \u3000\u3000\u8fd9\u4e00\u6218\uff0c\u7ecf\u8fc7\u6e05\u70b9\uff0c\u5c11\u6797\u65b9\u9762\u6709\u4e94\u516d\u4eba\u8f7b\u91cd\u4f24\uff0c\u738b\u5f64\u3001\u6c5f\u5343\u91cc\u65b9\u9762\u4e5f\u6709\u4e24\u4eba\u6302\u5f69\uff0c\u4f46\u4f24 \u52bf\u90fd\u4e0d\u7b97\u4e25\u91cd\uff0c\u9884\u8ba1\u4f11\u606f\u4e09\u4e94\u65e5\u4fbf\u53ef\u75ca\u6108\u3002 \u3000\u3000\u4e00\u8fde\u4e09\u65e5\u8fc7\u53bb\u4e86\uff0c\u5e76\u672a\u518d\u89c1\u9c7c\u5316\u9f99\u7387\u4f17\u6765\u653b\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u9c7c\u5316\u9f99\u66fe\u6d3e\u51fa\u767e\u4f59\u4eba\u524d\u6765\u795d\u5b85\u9644\u8fd1\u6536\u5c38\u3002 \u3000\u3000\u7b2c\u4e09\u65e5\u5165\u591c\u540e\uff0c\u859b\u767e\u80dc\u53c8\u5077\u5077\u6765\u4e86\u3002 \u3000\u3000\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u6700\u76fc\u671b\u7684\u5c31\u662f\u859b\u767e\u80dc\u7684\u5230\u6765\u3002 \u3000\u3000\u5206\u5bbe\u4e3b\u5750\u4e0b\u540e\uff0c\u859b\u767e\u80dc\u4fbf\u8feb\u4e0d\u53ca\u5f85\u7684\u9053\uff1a\u201c\u738b\u5927\u4eba\u3001\u6c5f\u524d\u8f88\uff0c\u5c31\u5728\u8fd9\u4e00\u4e24\u5929\uff0c\u9c7c\u5316\u9f99\u53c8 \u8981\u7387\u4f17\u6765\u653b\u3002\u665a\u8f88\u5e0c\u671b\u8fd9\u8fb9\u7684\u4eba\u6700\u597d\u80fd\u9000\u56de\u6d1b\u9633\u57ce\u91cc\u53bb\u3002\u201d \u3000\u3000\u738b\u5f64\u4e0d\u4ee5\u4e3a\u610f\u7684\u9053\uff1a\u201c\u4e0a\u6b21\u4ed6\u4eec\u5df2\u94e9\u7fbd\u800c\u5f52\uff0c\u96be\u9053\u4e0b\u6b21\u6765\u4f1a\u6709\u4ec0\u4e48\u65b0\u7684\u82b1\u62db\uff1f\u201d \u3000\u3000\u859b\u767e\u80dc\u795e\u8272\u51dd\u91cd\u7684\u9053\uff1a\u201c\u9c7c\u5316\u9f99\u82e5\u6ca1\u7edd\u5bf9\u628a\u63e1\uff0c\u600e\u53ef\u80fd\u518d\u6765\uff0c\u6b63\u6240\u8c13\u6765\u8005\u4e0d\u5584\uff0c\u5584\u8005\u4e0d \u6765\u3002\u738b\u5927\u4eba\u548c\u6c5f\u524d\u8f88\u5fc5\u987b\u4ece\u901f\u5e94\u53d8\u624d\u6210\u3002\u201d \u3000\u3000\u201c\u9c7c\u5316\u9f99\u7a76\u7adf\u8981\u65bd\u51fa\u4ec0\u4e48\u72e0\u6bd2\u62db\u672f\uff1f\u201d \u3000\u3000\u201c\u9c7c\u5316\u9f99\u6240\u4ee5\u4e09\u5929\u6765\u6309\u5175\u4e0d\u52a8\uff0c\u4e0d\u5916\u662f\u5728\u505a\u4e8b\u524d\u51c6\u5907\uff0c\u4e00\u65e6\u51c6\u5907\u5b8c\u6210\uff0c\u5fc5\u5b9a\u7acb\u5373\u53d1\u52a8\u653b \u52bf\uff0c\u800c\u90a3\u65f6\u738b\u5927\u4eba\u548c\u6c5f\u524d\u8f88\u7eb5\u6709\u901a\u5929\u672c\u9886\uff0c\u4e5f\u5fc5\u5b9a\u96be\u4ee5\u5e94\u4ed8\u3002\u201d \u3000\u3000\u201c\u859b\u8001\u5f1f\u60c5\u8bf4\u660e\u767d\uff01\u201d \u3000\u3000\u201c\u6b66\u6797\u4e2d\u6709\u4f4d\u4eba\u79f0\u2018\u706b\u836f\u5723\u624b\u2019\u7684\u5deb\u516c\u660e\uff0c\u738b\u5927\u4eba\u5fc5\u5b9a\u542c\u8bf4\u8fc7\u5427\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4eba\u9f0e\u9f0e\u5927\u540d\uff0c\u738b\u67d0\u5f53\u7136\u542c\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u201c\u9c7c\u5316\u9f99\u5df2\u628a\u5deb\u516c\u660e\u6e05\u5230\u5c71\u5be8\uff0c\u8981\u4ed6\u8fde\u591c\u5236\u9020\u767e\u679a\u785d\u70df\u706b\u78f7\u706b\u5f39\uff0c\u4e8e\u524d\u6765\u8fdb\u653b\u4e4b\u65f6\uff0c\u5c06 \u785d\u70df\u78f7\u706b\u5f39\u6295\u8fdb\u795d\u5b85\u3002 \u3000\u3000\u8fd9\u79cd\u78f7\u706b\u5f39\u5a01\u529b\u60ca\u4eba\uff0c\u4e0d\u8981\u8bf4\u662f\u767e\u679a\uff0c\u5c31\u662f\u4e09\u4e94\u679a\u6295\u8fdb\u5929\u4e95\u91cc\u6765\uff0c\u795d\u5b85\u4e5f\u5fc5\u5b9a\u4f1a\u53d8\u6210\u4e00 \u7247\u6c99\u783e\u7070\u7089\u3002\u201c \u3000\u3000\u738b\u5f64\u4e0d\u89c9\u5403\u4e86\u4e00\u60ca\u9053\uff1a\u201c\u8fd9\u7684\u786e\u96be\u4ee5\u5e94\u4ed8\u3002\u201d \u3000\u3000\u859b\u767e\u80dc\u7ee7\u7eed\u9053\uff1a\u201c\u9664\u6b64\u4e4b\u5916\uff0c\u9c7c\u5316\u9f99\u53c8\u5728\u547d\u4eba\u8fde\u591c\u5236\u9020\u4e00\u79cd\u53eb\u2018\u6bd2\u706b\u5f29\u2019\u7684\u6bd2\u7bad\uff0c\u7eb5\u7136 \u738b\u5927\u4eba\u548c\u6c5f\u524d\u8f88\u8fd9\u8fb9\u7684\u4eba\u80fd\u9003\u5230\u6237\u5916\uff0c\u4e5f\u96be\u4ee5\u62b5\u6321\u5f97\u4f4f\u4ed6\u7684\u4e07\u5f29\u9f50\u53d1\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u7d27\u8e59\u7740\u53cc\u7709\uff0c\u95ee\u9053\uff1a\u201c\u8001\u5f1f\u662f\u5426\u5df2\u77e5\u9c7c\u5316\u9f99\u5c06\u5728\u4ec0\u4e48\u65f6\u5019\u5c55\u5f00\u884c\u52a8\uff1f\u201d \u3000\u3000\u859b\u767e\u80dc\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u8fd9\u8981\u770b\u78f7\u706b\u5f39\u548c\u6bd2\u706b\u5f29\u662f\u5426\u5df2\u5236\u9020\u5b8c\u6210\uff0c\u636e\u665a\u8f88\u6240\u77e5\uff0c\u9c7c\u5316\u9f99\u53ef \u80fd\u660e\u665a\u5c31\u6709\u884c\u52a8\uff0c\u5373\u4f7f\u660e\u665a\u4e0d\u6765\uff0c\u6700\u8fdf\u4e5f\u4e0d\u51fa\u4e09\u65e5\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e8b\u9c7c\u5316\u9f99\u662f\u5426\u5df2\u544a\u77e5\u4e86\u9a6c\u6587\u4e2d\uff1f\u201d \u3000\u3000\u201c\u8fd9\u6837\u7684\u5927\u4e8b\uff0c\u9c7c\u5316\u9f99\u600e\u80fd\u4e0d\u5411\u9a6c\u6587\u4e2d\u8bf7\u793a\uff0c\u5982\u665a\u8f88\u6240\u6599\u4e0d\u5dee\uff0c\u9a6c\u6587\u4e2d\u5f88\u53ef\u80fd\u4f1a\u79d8\u5bc6\u8d76 \u6765\u6d1b\u9633\uff0c\u4eb2\u81ea\u7763\u6218\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u6c89\u601d\u4e86\u7247\u523b\uff0c\u5411\u859b\u767e\u80dc\u62f1\u4e86\u62f1\u624b\u9053\uff1a\u201c\u591a\u8c22\u8001\u5f1f\u4e0d\u987e\u751f\u6b7b\u524d\u6765\u4f20\u9012\u8fd9\u9879\u6d88\u606f\uff0c\u6211 \u4eec\u8fd9\u91cc\u81ea\u5f53\u5c3d\u901f\u505a\u4e00\u5904\u7f6e\uff0c\u8001\u5f1f\u4e0d\u5b9c\u5728\u8fd9\u91cc\u505c\u7559\u8fc7\u4e45\uff0c\u5c31\u8bf7\u901f\u56de\u5c71\u5be8\uff0c\u884c\u52a8\u5c3d\u91cf\u8981\u8c28\u614e\u5c0f \u5fc3\uff0c\u5f7c\u6b64\u968f\u65f6\u4fdd\u6301\u8054\u7cfb\u3002\u201d \u3000\u3000\u859b\u767e\u80dc\u968f\u5373\u8d77\u8eab\u544a\u8f9e\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u548c\u738b\u5f64\u76f4\u9001\u5230\u5927\u95e8\u53e3\u3002 \u3000\u3000\u4e24\u4eba\u518d\u56de\u5230\u5ba2\u5385\u3002 \u3000\u3000\u738b\u5f64\u9887\u611f\u4e0d\u5b89\u7684\u9053\uff1a\u201c\u6c5f\u5144\u4ee5\u4e3a\u54b1\u4eec\u8be5\u5982\u4f55\u5e94\u4ed8\uff1f\u201d \u3000\u3000\u201c\u859b\u8001\u5f1f\u8bf4\u5f97\u5bf9\uff0c\u5fc5\u987b\u901f\u901f\u64a4\u79bb\u8fd9\u91cc\uff0c\u5426\u5219\u8fde\u8fd9\u5b85\u9662\u4e5f\u5fc5\u5c06\u4e0d\u4fdd\u3002\u201d \u3000\u3000\u201c\u54b1\u4eec\u8be5\u64a4\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u201c\u64a4\u5230\u6cb3\u5357\u5e9c\u7f72\u8859\uff0c\u7531\u6cb3\u5357\u5e9c\u7f57\u77e5\u5e9c\u51fa\u9762\u63a5\u5f85\uff0c\u9c7c\u5316\u9f99\u7eb5\u7136\u662f\u80c6\u5927\u5305\u5929\uff0c\u4e5f\u4e0d\u6562\u706b\u711a\u5b98 \u7f72\u3002\u201d \u3000\u3000\u201c\u8fd9\u529e\u6cd5\u5f88\u597d\uff0c\u90a3\u54b1\u4eec\u5c31\u8be5\u516c\u5f00\u8eab\u4efd\u4e86\uff1f\u201d \u3000\u3000\u201c\u8fd9\u662f\u5fc5\u7136\u7684\uff0c\u4ee5\u4e09\u516c\u4e3b\u548c\u4f60\u8001\u5f1f\u5927\u5185\u4f8d\u536b\u7edf\u9886\u7684\u8eab\u4efd\uff0c\u7f57\u77e5\u5e9c\u600e\u6562\u4e0d\u606d\u8c28\u63a5\u5f85\uff1f\u201d \u3000\u3000\u201c\u54b1\u4eec\u662f\u5426\u8ff3\u81ea\u8fdb\u5165\u6cb3\u5357\u5e9c\uff1f\u201d \u3000\u3000\u201c\u603b\u8be5\u7ed9\u7f57\u77e5\u5e9c\u4e00\u70b9\u51c6\u5907\u65f6\u95f4\uff0c\u660e\u5929\u4e00\u65e9\uff0c\u5c31\u6d3e\u97e9\u6d9b\u8001\u5f1f\u5148\u53bb\u89c1\u7f57\u77e5\u5e9c\uff0c\u97e9\u8001\u5f1f\u539f\u662f\u9a6c \u6587\u4e2d\u624b\u4e0b\u7684\u603b\u6355\u5934\uff0c\u548c\u7f57\u77e5\u5e9c\u5fc5\u5b9a\u65e9\u5c31\u719f\u6089\uff0c\u4ee5\u6211\u9884\u6599\uff0c\u7f57\u77e5\u5e9c\u95fb\u62a5\u540e\uff0c\u5fc5\u5b9a\u4eb2\u81ea\u4e0a\u5c71\u6765\u63a5 \u4e09\u516c\u4e3b\u7684\u51e4\u9a7e\uff0c\u90a3\u65f6\u54b1\u4eec\u7684\u4eba\u5c31\u4e00\u8d77\u5f00\u8fdb\u6cb3\u5357\u5e9c\uff0c\u5c82\u4e0d\u66f4\u6709\u9762\u5b50\uff01\u201d \u3000\u3000\u201c\u5144\u5f1f\u5b8c\u5168\u540c\u610f\u6c5f\u5144\u7684\u529e\u6cd5\u3002\u201d \u3000\u3000\u201c\u8fd8\u6709\uff0c\u4eca\u665a\u5c31\u901a\u77e5\u4f4f\u5728\u767d\u5854\u5bfa\u7684\u5927\u89c9\u7985\u5e08\uff0c\u8981\u4ed6\u4eec\u4e5f\u6682\u65f6\u64a4\u5230\u522b\u7684\u5730\u65b9\u53bb\u3002\u201d \u3000\u3000\u7fcc\u65e5\u4e00\u65e9\uff0c\u97e9\u6d9b\u5c31\u5949\u547d\u8fdb\u57ce\u89c1\u6cb3\u5357\u5e9c\u7f57\u77e5\u5e9c\u3002 \u3000\u3000\u6cb3\u5357\u5e9c\u662f\u6cb3\u5357\u7701\u7684\u4e00\u5e9c\uff0c\u8f96\u6d1b\u9633\u3001\u5830\u5e08\u3001\u5b9c\u9633\u3001\u65b0\u5b89\u3001\u5de9\u3001\u5b5f\u6d25\u3001\u767b\u5c01\u3001\u6c38\u5b81\u3001\u6e11\u6c60\u3001 \u5d69\u5341\u53bf\uff0c\u5e9c\u53f0\u5728\u6d1b\u9633\u3002 \u3000\u3000\u6cb3\u5357\u5e9c\u77e5\u5e9c\u7f57\u4f20\u82b3\uff0c\u5728\u6d1b\u9633\u505a\u77e5\u5e9c\u5df2\u6709\u4e09\u5e74\u4ee5\u4e0a\u65f6\u95f4\uff0c\u4ed6\u65e9\u5c31\u548c\u97e9\u6d9b\u76f8\u8bc6\u4e86\uff0c\u5e76\u4e14\u77e5\u9053 \u97e9\u6d9b\u5df2\u4e8e\u4e24\u5e74\u524d\u8c03\u5f80\u5927\u5185\uff0c\u5df2\u662f\u7687\u4e0a\u8eab\u8fb9\u7684\u4eba\u3002\u95fb\u62a5\u4e4b\u540e\uff0c\u4eb2\u81ea\u8fce\u51fa\u5927\u95e8\uff0c\u5e76\u63a5\u81f3\u540e\u8859\u4e66\u623f \u53d9\u8bdd\u3002 \u3000\u3000\u5f53\u7f57\u77e5\u5e9c\u83b7\u77e5\u5927\u5185\u4f8d\u536b\u7edf\u9886\u738b\u5f64\u4fdd\u7740\u4e09\u516c\u4e3b\u5df2\u6765\u5230\u6d1b\u9633\uff0c\u5e76\u6709\u610f\u8fdb\u9a7b\u5e9c\u8859\uff0c\u771f\u662f\u53c8\u60ca\u53c8 \u559c\uff01 \u3000\u3000\u60ca\u7684\u662f\u539f\u6765\u4e09\u65e5\u524d\u9099\u5c71\u5c71\u811a\u4e0b\u90a3\u573a\u8840\u6218\u7adf\u662f\u5c71\u5bc7\u5bf9\u4e09\u516c\u4e3b\u4e0b\u624b\uff0c\u4ed6\u8fd9\u5c0f\u5c0f\u7684\u56db\u54c1\u77e5\u5e9c\u6240 \u8f96\u5730\u9762\u4e0a\u51fa\u4e86\u8fd9\u6837\u7684\u5927\u4e8b\uff0c\u5982\u4f55\u80fd\u62c5\u5f85\u5f97\u4e0b\uff1f\u559c\u7684\u662f\u6709\u5e78\u80fd\u63a5\u6765\u4e09\u516c\u4e3b\u7684\u51e4\u9a7e\uff0c\u53ea\u8981\u5584\u5c3d\u804c \u8d23\uff0c\u5bf9\u672a\u6765\u4ed5\u9014\u524d\u7a0b\uff0c\u5fc5\u5b9a\u5927\u6709\u5e2e\u52a9\u3002 \u3000\u3000\u5f53\u4e0b\uff0c\u7f57\u77e5\u5e9c\u5907\u4e86\u4e00\u4e58\u9526\u8f7f\u3002\u4eb2\u7387\u50da\u5c5e\uff0c\u5728\u97e9\u6d9b\u7684\u5e26\u8def\u4e0b\uff0c\u51fa\u57ce\u8d76\u5f80\u795d\u5b85\uff0c\u8fce\u63a5\u4e09\u516c\u4e3b \u7b49\u4eba\u3002 \u3000\u3000\u4e0d\u5230\u5348\u65f6\uff0c\u4e09\u516c\u4e3b\u4fbf\u5df2\u88ab\u63a5\u8fdb\u6cb3\u5357\u5e9c\u8859\u3002 \u3000\u3000\u968f\u884c\u4eba\u5458\u81ea\u7136\u4e5f\u90fd\u5168\u4f53\u8ddf\u6765\u3002 \u3000\u3000\u81f3\u4e8e\u795d\u9999\u4ead\u4ee5\u53ca\u957f\u5de5\u8bb8\u6709\u7530\u548c\u90a3\u540d\u5973\u4f63\u4eba\uff0c\u4e5f\u90fd\u968f\u540c\u4e09\u516c\u4e3b\u6765\u5230\u6d1b\u9633\u57ce\u5185\uff0c\u5230\u4eb2\u621a\u5bb6\u4e0e \u5bb6\u4eba\u56e2\u805a\u3002 \u3000\u3000\u795d\u5b85\u5df2\u7ecf\u6210\u4e86\u7a7a\u5b85\u3002 \u3000\u3000\u5927\u89c9\u7985\u5e08\u4e5f\u7387\u9886\u4e09\u5341\u516d\u540d\u5f1f\u5b50\u64a4\u81f3\u57ce\u5357\u4e00\u5ea7\u5bfa\u5e99\uff0c\u8fd9\u91cc\u8ddd\u5c11\u6797\u672c\u5bfa\u66f4\u8fd1\uff0c\u968f\u65f6\u90fd\u53ef\u548c\u672c \u5bfa\u8054\u7edc\u3002 \u3000\u3000\u7f57\u77e5\u5e9c\u867d\u7136\u4eb2\u628a\u4e09\u516c\u4e3b\u63a5\u6765\uff0c\u4f46\u4e09\u516c\u4e3b\u5374\u59cb\u7ec8\u672a\u4e0e\u4ed6\u6b63\u5f0f\u89c1\u9762\u3002 \u3000\u3000\u5f53\u65e5\u4e2d\u5348\uff0c\u7f57\u77e5\u5e9c\u8bbe\u4e0b\u76db\u7b75\u6b3e\u5f85\u5927\u5185\u9ad8\u624b\u548c\u6c5f\u5343\u91cc\u6240\u5e26\u4eba\u5458\uff0c\u800c\u4e09\u516c\u4e3b\u4e5f\u662f\u5355\u72ec\u5728\u5185\u7528 \u81b3\u3002 \u3000\u3000\u6563\u7b75\u540e\uff0c\u7f57\u77e5\u5e9c\u518d\u966a\u540c\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u5230\u4e66\u623f\u7528\u8336\u3002 \u3000\u3000\u7f57\u77e5\u5e9c\u663e\u5f97\u5f02\u6837\u60f6\u60ca\u7684\u9053\uff1a\u201c\u738b\u5927\u4eba\uff0c\u4e0b\u5b98\u77e5\u7f6a\uff0c\u4e09\u65e5\u524d\u672a\u80fd\u7ef4\u62a4\u4e09\u516c\u4e3b\u7684\u5b89\u5168\uff0c\u83ab\u975e \u4e09\u516c\u4e3b\u56e0\u800c\u9707\u6012\uff0c\u624d\u4e0d\u80af\u63a5\u89c1\u4e0b\u5b98\uff1f\u201d \u3000\u3000\u738b\u5f64\u7b11\u9053\uff1a\u201c\u7f57\u5927\u4eba\u8bf4\u54ea\u91cc\u8bdd\u6765\uff0c\u4e09\u516c\u4e3b\u4e0a\u6b21\u5728\u5f00\u5c01\uff0c\u8fde\u5de1\u629a\u9a6c\u5927\u4eba\u90fd\u4e0d\u66fe\u63a5\u89c1\uff0c\u4e09\u516c \u4e3b\u867d\u662f\u91d1\u679d\u4e09\u53f6\u8eab\u4efd\uff0c\u4f46\u7ec8\u5c5e\u5185\u7737\uff0c\u975e\u6709\u5fc5\u8981\uff0c\u4e0d\u8f7b\u6613\u63a5\u89c1\u5916\u5b98\u3002\u201d \u3000\u3000\u7f57\u77e5\u5e9c\u8d66\u7136\u9053\uff1a\u201c\u53ef\u662f\u4e09\u65e5\u524d\u9099\u5c71\u4e8b\u4ef6\uff0c\u4e0b\u5b98\u603b\u662f\u672a\u80fd\u5c3d\u5230\u804c\u8d23\uff0c\u6709\u4e8f\u804c\u5b88\u3002\u201d \u3000\u3000\u738b\u5f64\u6447\u6447\u5934\uff0c\u8bed\u5e26\u5b89\u6170\u7684\u9053\uff1a\u201c\u7f57\u5927\u4eba\uff0c\u5b9e\u5bf9\u4f60\u8bf4\u5427\uff01\u8fd9\u4ef6\u4e8b\u4f60\u5c31\u662f\u60f3\u7ba1\uff0c\u4e5f\u7ba1\u4e0d\u4e86\u3002 \u4f60\u53ef\u77e5\u90a3\u6279\u76d7\u532a\u90fd\u662f\u4e9b\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u7f57\u77e5\u5e9c\u55eb\u5685\u7740\u9053\uff1a\u201c\u4e0b\u5b98\u5b88\u571f\u6709\u8d23\uff0c\u5c82\u80fd\u4e0d\u77e5\uff0c\u90a3\u6279\u76d7\u5bc7\u4e0d\u4e0b\u5343\u4f59\u4eba\uff0c\u4e8e\u534a\u5e74\u524d\u5728\u9099\u5c71 \u6210\u7acb\u4e86\u4e24\u5904\u8425\u5be8\uff0c\u4e00\u5904\u662f\u666e\u6551\u5bfa\u7684\u5c71\u5be8\uff0c\u4e00\u5904\u662f\u5728\u5c71\u4e0b\u9ec4\u6cb3\u5357\u5cb8\u7684\u6c34\u5be8\u3002\u53ea\u56e0\u8d3c\u52bf\u5927\u4f17\uff0c\u636e \u8bf4\u53c8\u4e2a\u4e2a\u8eab\u624b\u9ad8\u5f3a\uff0c\u4ee5\u4e0b\u5b98\u73b0\u6709\u7684\u5b98\u5175\u529b\u91cf\uff0c\u6839\u672c\u65e0\u6cd5\u524d\u5f80\u5f81\u527f\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4e0b\u5b98\u65e9\u5c31\u4eb2\u81ea\u5411\u629a\u53f0\u5927\u4eba\u7980\u62a5\u8fc7\uff0c\u8bf7\u4ed6\u8001\u4eba\u5bb6\u8c03\u52a8\u9a6c\u3001\u6b65\u7cbe\u5175\uff0c\u548c\u8c03\u7528\u5404\u5dde\u53bf\u7684\u5175\u529b \u8fdb\u527f\u3002\u201d \u3000\u3000\u201c\u5de1\u629a\u9a6c\u5927\u4eba\u600e\u4e48\u7b54\u590d\uff1f\u201d \u3000\u3000\u201c\u629a\u53f0\u5927\u4eba\u4f3c\u4e4e\u5e76\u672a\u628a\u8fd9\u4ef6\u4e8b\u653e\u5728\u5fc3\u4e0a\uff0c\u81f3\u4eca\u6beb\u65e0\u52a8\u9759\u3002\u201d \u3000\u3000\u7531\u7f57\u77e5\u5e9c\u7684\u8c08\u8bdd\uff0c\u53ef\u77e5\u4ed6\u5bf9\u9a6c\u6587\u4e2d\u7684\u6240\u4f5c\u6240\u4e3a\u8fd8\u4e00\u76f4\u8499\u5728\u9f13\u91cc\uff0c\u540c\u65f6\u4e5f\u53ef\u65ad\u5b9a\u7f57\u77e5\u5e9c\u8fd8 \u662f\u5fc3\u5411\u671d\u5ef7\uff0c\u5e76\u672a\u4e3a\u9a6c\u6587\u4e2d\u6240\u6536\u4e70\u3002 \u3000\u3000\u5176\u5b9e\u9a6c\u6587\u4e2d\u662f\u4e2a\u806a\u660e\u7edd\u9876\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u867d\u65e9\u5c31\u5fc3\u6000\u4e0d\u8f68\uff0c\u4f46\u5bf9\u6240\u5c5e\u5e9c\u53bf\uff0c\u4e5f\u53ea\u80fd\u505a\u5230\u80fd\u6536\u4e70\u7684\u5219\u6536\u4e70\uff0c\u4e0d\u80fd\u6536\u4e70\u7684\u7edd\u4e0d\u8f7b\u4e3e \u5984\u52a8\uff0c\u4ee5\u514d\u56e0\u5c0f\u5931\u5927\u3002 \u3000\u3000\u738b\u5f64\u4e0d\u518d\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u4e66\u623f\u5185\u6c89\u5bc2\u4e86\u751a\u4e45\uff0c\u6c5f\u5343\u91cc\u624d\u642d\u8baa\u7740\u95ee\u9053\uff1a\u201c\u4e09\u65e5\u524d\u7684\u9099\u5c71\u4e8b\u4ef6\uff0c\u7f57\u5927\u4eba\u662f\u5426\u5df2\u5448\u62a5\u4e86 \u9a6c\u629a\u53f0\uff1f\u201d \u3000\u3000\u7f57\u77e5\u5e9c\u9053\uff1a\u201c\u4e0b\u5b98\u4e8e\u6b21\u65e5\u4e00\u65e9\u4fbf\u6d3e\u4e13\u4eba\u5448\u62a5\u4e86\u629a\u53f0\u5927\u4eba\u3002\u201d \u3000\u3000\u201c\u9a6c\u629a\u53f0\u6709\u4f55\u6307\u793a\uff1f\u201d \u3000\u3000\u201c\u629a\u53f0\u5927\u4eba\u4ea4\u4ee3\u6765\u4eba\uff0c\u8981\u4e0b\u5b98\u4e0d\u5fc5\u4ee5\u6b64\u4e3a\u5ff5\uff0c\u4ed6\u5c06\u4eb2\u6765\u6d1b\u9633\u5904\u7406\uff0c\u5982\u679c\u629a\u53f0\u5927\u4eba\u884c\u52a8\u5f97 \u5feb\uff0c\u5c31\u8be5\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u7f57\u5927\u4eba\u5f53\u65f6\u662f\u5426\u5df2\u77e5\u88ab\u56f0\u5728\u6c11\u5b85\u5185\u7684\u662f\u4e09\u516c\u4e3b\u548c\u5927\u5185\u7684\u4eba\uff1f\u2019\u201d \u3000\u3000\u201c\u5f53\u65f6\u4e0b\u5b98\u600e\u53ef\u80fd\u77e5\u9053\u8fd9\u4e9b\uff0c\u8fd9\u4e8b\u662f\u4eca\u5929\u4e00\u65e9\u738b\u5927\u4eba\u6d3e\u97e9\u5927\u4eba\u524d\u6765\uff0c\u4e0b\u5b98\u624d\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u548c\u738b\u5f64\u90fd\u6599\u5f97\u51fa\u7f57\u77e5\u5e9c\u8bf4\u7684\u662f\u5b9e\u8bdd\uff0c\u7528\u4e0d\u7740\u6709\u6240\u6000\u7591\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u4e00\u540d\u5e08\u7237\u6a21\u6837\u7684\u4eba\u8fdb\u6765\uff0c\u5411\u7f57\u77e5\u5e9c\u9053\uff1a\u201c\u5927\u4eba\uff0c\u629a\u53f0\u5927\u4eba\u5230\u4e86\uff0c\u5df2\u7ecf\u8fdb\u5165\u4e1c \u95e8\u3002\u201d \u3000\u3000\u7f57\u77e5\u5e9c\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u542c\u8c01\u8bf4\u7684\uff1f\u201d \u3000\u3000\u201c\u8d75\u603b\u6355\u5934\u6d3e\u4eba\u6765\u901a\u77e5\u5b66\u751f\uff0c\u8981\u5b66\u751f\u6765\u8f6c\u544a\u5927\u4eba\u3002\u201d \u3000\u3000\u7f57\u77e5\u5e9c\u7ad9\u8d77\u8eab\uff0c\u5411\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u5206\u522b\u4e00\u63d6\u9053\uff1a\u201c\u4e0b\u5b98\u5fc5\u987b\u53bb\u8fce\u63a5\u629a\u53f0\u5927\u4eba\uff0c\u6682\u65f6\u5931\u966a\uff0c \u8fd8\u8bf7\u591a\u591a\u539f\u8c05\u3002\u201d \u3000\u3000\u738b\u5f64\u6325\u6325\u624b\u9053\uff1a\u201c\u7f57\u5927\u4eba\u8bf7\u4fbf\uff0c\u8fd9\u662f\u516c\u4e8b\uff0c\u629a\u53f0\u5927\u4eba\u5230\u4e86\uff0c\u5f53\u7136\u8981\u8fce\u3002\u201d \u3000\u3000\u7f57\u77e5\u5e9c\u521a\u8d70\u4e86\u4e24\u6b65\uff0c\u53c8\u56de\u8eab\u9053\uff1a\u201c\u4e0b\u5b98\u89c1\u4e86\u629a\u53f0\u5927\u4eba\uff0c\u662f\u5426\u8be5\u544a\u77e5\u4ed6\u4e09\u516c\u4e3b\u548c\u738b\u5927\u4eba\u7b49 \u73b0\u4f4f\u655d\u8859\u4e4b\u4e8b\uff1f\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u53ea\u7ba1\u5411\u4ed6\u8bf4\u660e\uff0c\u4e0d\u8fc7\u4e0d\u5fc5\u544a\u8bc9\u4ed6\u6c5f\u5927\u4fa0\u4e5f\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u201c\u4e0b\u5b98\u9075\u547d\uff0c\u738b\u5927\u4eba\u8fd8\u6709\u4ec0\u4e48\u4ea4\u4ee3\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u5c3d\u91cf\u8bbe\u6cd5\u628a\u9a6c\u629a\u53f0\u63a5\u5230\u8d35\u8859\u6765\uff0c\u738b\u67d0\u5f88\u60f3\u548c\u4ed6\u5728\u8fd9\u91cc\u89c1\u89c1\uff0c\u4f46\u5343\u4e07\u522b\u5bf9\u4ed6\u8bf4\u660e\u8fd9\u662f\u738b \u67d0\u7684\u610f\u601d\u3002\u201d \u3000\u3000\u201c\u4e0b\u5b98\u4e00\u5b9a\u7167\u529e\u3002\u201d \u3000\u3000\u7f57\u77e5\u5e9c\u8d70\u540e\uff0c\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u8001\u5f1f\u662f\u5426\u8981\u5f53\u573a\u548c\u9a6c\u6587\u4e2d\u628a\u8bdd\u644a\u5f00\u6765\u8bb2\uff1f\u201d \u3000\u3000\u738b\u5f64\u9894\u9996\u9053\uff1a\u201c\u5144\u5f1f\u8003\u8651\u4e86\u5f88\u4e45\uff0c\u5fc5\u987b\u628a\u4e8b\u60c5\u516c\u5f00\uff0c\u624d\u80fd\u903c\u4f7f\u9a6c\u6587\u4e2d\u975e\u8083\u6e05\u9099\u5c71\u5730\u533a\u7684 \u6c34\u9646\u4e24\u5be8\u4e0d\u53ef\uff0c\u5426\u5219\u4ed6\u5c31\u662f\u660e\u663e\u7684\u8fdd\u6297\u5723\u610f\u4e86\uff0c\u9a6c\u6587\u4e2d\u662f\u806a\u660e\u4eba\uff0c\u8c05\u4ed6\u8fd8\u4e0d\u6562\u516c\u7136\u8868\u793a\u53db \u610f\u3002\u201d \u3000\u3000\u201c\u542c\u8001\u5f1f\u7684\u8bed\u6c14\uff0c\u662f\u51c6\u5907\u8bf7\u4e09\u516c\u4e3b\u4eb2\u81ea\u51fa\u9762\u4e0e\u9a6c\u6587\u4e2d\u76f8\u89c1\uff1f\u201d \u3000\u3000\u201c\u5144\u5f1f\u6b63\u6709\u6b64\u610f\u3002\u201d \u3000\u3000\u201c\u5bf9\uff0c\u5c31\u8be5\u8fd9\u4e48\u505a\u3002\u201d \u3000\u3000\u201c\u5144\u5f1f\u73b0\u5728\u5c31\u53bb\u89c1\u4e09\u516c\u4e3b\u3002\u201d \u3000\u3000\u201c\u4f60\u53bb\u5427\uff01\u9a6c\u6587\u4e2d\u6765\u65f6\uff0c\u6211\u81ea\u5f53\u56de\u907f\u4e00\u4e0b\u3002\u201d \u3000\u3000\u534a\u4e2a\u65f6\u8fb0\u4e0d\u5230\uff0c\u7f57\u77e5\u5e9c\u5df2\u5c06\u5de1\u629a\u9a6c\u6587\u4e2d\u63a5\u8fdb\u5e9c\u8859\u3002 \u3000\u3000\u9a6c\u6587\u4e2d\u6240\u5e26\u968f\u4ece\u4e0d\u591a\uff0c\u9664\u603b\u6355\u5934\u8f6c\u9f99\u624b\u5f20\u4e0d\u7a7a\u5916\uff0c\u53ea\u6709\u4e24\u540d\u5fc3\u8179\u957f\u968f\u3002 \u3000\u3000\u8fdb\u5165\u5ba2\u5385\uff0c\u5f20\u4e0d\u7a7a\u548c\u90a3\u4e24\u540d\u968f\u4ece\u90fd\u7559\u5728\u53e6\u5916\u4e00\u5904\u504f\u95f4\u5f85\u8336\u3002 \u3000\u3000\u5750\u4e0b\u540e\uff0c\u7f57\u77e5\u5e9c\u4eb2\u81ea\u9012\u4e0a\u8336\u53bb\u3002 \u3000\u3000\u9a6c\u6587\u4e2d\u559d\u4e86\u53e3\u8336\uff0c\u9053\uff1a\u201c\u4e09\u65e5\u524d\uff0c\u90ca\u5c71\u6c11\u5b85\u9644\u8fd1\u53d1\u751f\u7684\u4e8b\uff0c\u672c\u629a\u5df2\u8c03\u67e5\u5f97\u5dee\u4e0d\u591a\u4e86\uff0c\u6700 \u8fd1\u8fd9\u4e09\u5929\u6765\u662f\u5426\u8fd8\u6709\u52a8\u9759\uff1f\u201d \u3000\u3000\u7f57\u77e5\u5e9c\u8c28\u58f0\u7b54\u9053\uff1a\u201c\u6700\u8fd1\u8fd9\u4e09\u5929\u6765\uff0c\u5e76\u672a\u518d\u53d1\u751f\u4e8b\u6545\uff0c\u5927\u4eba\u53ef\u8c03\u67e5\u51fa\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u53ea\u662f\u4e3a\u4e86\u4e00\u70b9\u5c0f\u4e8b\u800c\u5df2\uff0c\u7b97\u4e0d\u4e86\u4ec0\u4e48\uff0c\u76f8\u4fe1\u8fd9\u79cd\u4e8b\u60c5\u4ee5\u540e\u7edd\u5bf9\u4e0d\u4f1a\u518d\u53d1\u751f\u4e86\uff0c\u4f60\u53ea\u7ba1 \u653e\u5fc3\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u63a5\u7740\u53c8\u95ee\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u548c\u5927\u5185\u4f8d\u536b\u7edf\u9886\u738b\u5927\u4eba\uff0c\u662f\u4ec0\u4e48\u65f6\u5019\u5230\u8d35\u8859\u6765\u7684\uff1f\u201d \u3000\u3000\u7f57\u77e5\u5e9c\u8eac\u8eab\u9053\uff1a\u201c\u4eca\u5929\u4e0a\u5348\u6765\u7684\u3002\u201d \u3000\u3000\u201c\u8d35\u5e9c\u53ef\u8c12\u89c1\u8fc7\u4e09\u516c\u4e3b\uff1f\u201d \u3000\u3000\u201c\u4e0b\u5b98\u6709\u610f\u8c12\u89c1\uff0c\u4f46\u4e09\u516c\u4e3b\u672a\u4e88\u63a5\u89c1\u3002\u201d \u3000\u3000\u201c\u738b\u5927\u4eba\u90fd\u548c\u8d35\u5b98\u8c08\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u8c08\u7684\u90fd\u662f\u4e00\u4e9b\u95f2\u8bdd\uff0c\u6709\u5173\u671d\u5ef7\u5927\u4e8b\u738b\u5927\u4eba\u77e2\u53e3\u4e0d\u63d0\u3002\u201d \u3000\u3000\u201c\u968f\u540c\u738b\u5927\u4eba\u6765\u7684\uff0c\u90fd\u662f\u4e9b\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u6709\u51e0\u4f4d\u5927\u5185\u4f8d\u536b\uff0c\u5982\u5f53\u5e74\u629a\u7f72\u7684\u603b\u6355\u5934\u97e9\u5927\u4eba\u4e5f\u6765\u4e86\uff0c\u53e6\u6709\u51e0\u4f4d\u4e5f\u662f\u738b\u5927\u4eba\u5e26\u6765\u7684\uff0c \u81f3\u4e8e\u4ec0\u4e48\u8eab\u4efd\uff0c\u4e0b\u5b98\u5e76\u4e0d\u6e05\u695a\u3002\u201d \u3000\u3000\u201c\u662f\u738b\u5927\u4eba\u8bf4\u8fc7\u8981\u89c1\u672c\u629a\uff1f\u201d \u3000\u3000\u201c\u738b\u5927\u4eba\u786e\u6709\u610f\u548c\u5927\u4eba\u89c1\u89c1\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u73b0\u5728\u4f60\u5c31\u5e26\u6211\u53bb\u89c1\u738b\u5927\u4eba\uff01\u201d \u3000\u3000\u201c\u738b\u5927\u4eba\u7684\u5c45\u5904\u5e76\u4e0d\u5bbd\u655e\uff0c\u5351\u804c\u8fd8\u662f\u8bf7\u4ed6\u5230\u8fd9\u91cc\u6765\u5427\uff01\u201d \u3000\u3000\u201c\u4e5f\u597d\u3002\u201d \u3000\u3000\u4e0d\u4e00\u4f1a\u513f\uff0c\u7f57\u77e5\u5e9c\u4fbf\u966a\u540c\u738b\u5f64\u8fdb\u5165\u5ba2\u5385\u3002 \u3000\u3000\u9a6c\u6587\u4e2d\u65e9\u7ad9\u5728\u95e8\u53e3\u76f8\u8fce\u3002 \u3000\u3000\u5f7c\u6b64\u5bd2\u55a7\u4e86\u51e0\u53e5\uff0c\u4fbf\u76f8\u8ba9\u4eba\u5ea7\u3002 \u3000\u3000\u7f57\u77e5\u5e9c\u5219\u5728\u4e00\u65c1\u76f8\u966a\u3002 \u3000\u3000\u9a6c\u6587\u4e2d\u9053\uff1a\u201c\u738b\u5927\u4eba\u79bb\u5f00\u5f00\u5c01\u65f6\uff0c\u4e3a\u4ec0\u4e48\u4e5f\u4e0d\u901a\u77e5\u4e0b\u5b98\u4e00\u58f0\uff0c\u4f7f\u5f97\u4e0b\u5b98\u8fde\u4e3a\u4e09\u516c\u4e3b\u9001\u884c \u7684\u673a\u4f1a\u90fd\u5931\u53bb\u4e86\u3002\u201d \u3000\u3000\u738b\u5f64\u6b49\u7136\u4e00\u7b11\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u51fa\u5bab\u662f\u79c1\u4e8b\uff0c\u4e0d\u5e94\u6253\u6270\u9a6c\u5927\u4eba\uff0c\u56e0\u4e4b\u624d\u4e0d\u8f9e\u800c\u522b\uff0c\u8fd8\u671b\u9a6c\u5927 \u4eba\u89c1\u8c05\u3002\u201d \u3000\u3000\u201c\u4e09\u516c\u4e3b\u51e4\u9a7e\u53ef\u5b89\uff1f\u201d \u3000\u3000\u201c\u4e09\u516c\u4e3b\u4e00\u5207\u6b63\u5e38\uff0c\u4e0d\u52b3\u6302\u5fc3\u3002\u201d \u3000\u3000\u201c\u4e09\u516c\u4e3b\u6765\u5230\u6d1b\u9633\u540e\uff0c\u8fd8\u51c6\u5907\u5230\u4ec0\u4e48\u5730\u65b9\u53bb\uff1f\u201d \u3000\u3000\u201c\u4e09\u516c\u4e3b\u53ea\u662f\u51fa\u5916\u968f\u4fbf\u8d70\u8d70\uff0c\u5e76\u65e0\u56fa\u5b9a\u53bb\u5904\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u53c8\u559d\u4e86\u53e3\u8336\uff0c\u4e0d\u518d\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u5ffd\u542c\u738b\u5f64\u8f7b\u54b3\u4e86\u58f0\u9053\uff1a\u201c\u9a6c\u5927\u4eba\u6765\u5f97\u6b63\u597d\uff0c\u5728\u5f00\u5c01\u65f6\uff0c\u9a6c\u5927\u4eba\u66fe\u591a\u6b21\u5e0c\u671b\u80fd\u62dc\u89c1\u4e09\u516c \u4e3b\uff0c\u504f\u504f\u5f53\u65f6\u4e09\u516c\u4e3b\u6267\u610f\u4e0d\u89c1\u5916\u5b98\u2026\u2026\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u795e\u8272\u5fae\u5fae\u4e00\u53d8\uff0c\u622a\u53e3\u9053\uff1a\u201c\u83ab\u975e\u4e09\u516c\u4e3b\u5df2\u6709\u610f\u63a5\u89c1\u4e0b\u5b98\uff1f\u201d \u3000\u3000\u201c\u9a6c\u5927\u4eba\u8bf7\u7a0d\u5f85\uff0c\u738b\u67d0\u73b0\u5728\u5c31\u8fdb\u5185\u901a\u62a5\u3002\u201d \u3000\u3000\u738b\u5f64\u7684\u8fdb\u5185\u901a\u62a5\uff0c\u4e0d\u8fc7\u662f\u505a\u505a\u6837\u5b50\u800c\u5df2\uff0c\u5176\u5b9e\u4e09\u516c\u4e3b\u65e9\u5df2\u548c\u4ed6\u7ea6\u597d\u3002 \u3000\u3000\u4ec5\u4ec5\u534a\u76cf\u8336\u5149\u666f\uff0c\u738b\u5f64\u4fbf\u56de\u5230\u5ba2\u5385\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u6709\u8bf7\u9a6c\u5927\u4eba\uff01\u201d \u5367\u9f99\u751f\u300a\u71d5\u5b50\u4f20\u5947\u300b \u7b2c\u4e09\u5341\u56de\u3000\u5be1\u5987\u9152\u5e97 \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0c\u9a6c\u6587\u4e2d\u5185\u5fc3\u60ca\u591a\u4e8e\u559c\uff0c\u4e0d\u7531\u6cdb\u8d77\u4e00\u9635\u5fd0\u5fd1\u3002 \u3000\u3000\u4ed6\u4e0d\u80fd\u4e0d\u62c5\u5fc3\u8fd9\u662f\u738b\u5f64\u548c\u4e09\u516c\u4e3b\u5b9a\u4e0b\u7684\u4e00\u8ba1\uff0c\u5c06\u4ed6\u5c31\u5730\u902e\u6355\uff0c\u7136\u540e\u79d8\u5bc6\u89e3\u8fdb\u4eac\u53bb\u6cbb\u7f6a\uff0c \u679c\u771f\u5982\u6b64\uff0c\u4ed6\u624b\u4e0b\u7684\u529b\u91cf\u518d\u5927\u4e5f\u6765\u4e0d\u53ca\u6551\u5e94\u3002 \u3000\u3000\u4f46\u4e8b\u5df2\u5982\u6b64\uff0c\u4ed6\u53c8\u662f\u4e0d\u5f97\u4e0d\u786c\u7740\u5934\u76ae\u664b\u89c1\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u7684\u4f4f\u5904\u662f\u540e\u884c\u7684\u4e00\u6240\u72ec\u7acb\u8de8\u9662\u3002 \u3000\u3000\u8de8\u9662\u91cc\u4e5f\u6709\u5ba2\u5385\uff0c\u601c\u82b1\u6b63\u7ad9\u5728\u5ba2\u5385\u95e8\u53e3\u3002 \u3000\u3000\u738b\u5f64\u53eb\u9053\uff1a\u201c\u601c\u82b1\u59d1\u5a18\uff0c\u5feb\u8fdb\u53bb\u901a\u62a5\u4e09\u516c\u4e3b\uff0c\u9a6c\u5927\u4eba\u5df2\u7ecf\u5230\u4e86\u3002\u201d \u3000\u3000\u601c\u82b1\u7acb\u5373\u5e94\u58f0\u800c\u53bb\u3002 \u3000\u3000\u8fdb\u5165\u5ba2\u5385\uff0c\u9a6c\u6587\u4e2d\u4e0d\u6562\u843d\u5ea7\uff0c\u53ea\u80fd\u7ad9\u5728\u4e00\u65c1\uff0c\u606d\u5019\u51e4\u9a7e\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u5f88\u5feb\u4fbf\u5728\u601c\u82b1\u7684\u966a\u4f8d\u4e0b\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u9a6c\u6587\u4e2d\u8fde\u5fd9\u6df1\u6df1\u6253\u4e86\u4e00\u8eac\u9053\uff1a\u201c\u5351\u804c\u53c2\u89c1\u4e09\u516c\u4e3b\uff01\u201d \u3000\u3000\u4e09\u516c\u4e3b\u542b\u7b11\u5fae\u5fae\u4e00\u7b54\u793c\u9053\uff1a\u201c\u9a6c\u5927\u4eba\u4e0d\u5fc5\u591a\u793c\uff0c\u5feb\u5feb\u8bf7\u5750\uff01\u201d \u3000\u3000\u201c\u5728\u4e09\u516c\u4e3b\u9762\u524d\uff0c\u90a3\u6709\u5fae\u81e3\u7684\u5ea7\u4f4d\uff1f\u201d \u3000\u3000\u201c\u9a6c\u5927\u4eba\u90a3\u91cc\u8bdd\uff0c\u4f60\u662f\u5c01\u7586\u5927\u5458\uff0c\u5c31\u662f\u5728\u6211\u7236\u738b\u524d\uff0c\u4e5f\u8be5\u5750\u7740\u8bb2\u8bdd\uff0c\u4f55\u51b5\u662f\u6211\uff1f\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u53ea\u5f97\u53c8\u8c26\u900a\u4e86\u4e00\u4f1a\uff0c\u624d\u5728\u4e00\u65c1\u5750\u4e0b\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u671b\u4e86\u601c\u82b1\u4e00\u773c\u9053\uff1a\u201c\u601c\u82b1\u5012\u8336\uff01\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u5fd9\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u4e0d\u6562\uff01\u4e0d\u6562\u52b3\u9a7e\u59d1\u5a18\u3002\u201d \u3000\u3000\u4f46\u601c\u82b1\u8fd8\u662f\u5012\u4e86\u8336\uff0c\u53cc\u624b\u7aef\u5411\u9a6c\u6587\u4e2d\u9762\u524d\u7684\u8336\u51e0\u4e0a\u3002 \u3000\u3000\u5ba2\u5385\u5185\u6c89\u9759\u4e86\u751a\u4e45\uff0c\u624d\u542c\u4e09\u516c\u4e3b\u51b7\u51b7\u95ee\u9053\uff1a\u201c\u4e09\u65e5\u524d\uff0c\u90ae\u5c71\u53d1\u751f\u76d7\u532a\u5305\u56f4\u6c11\u5b85\u5c60\u6740\u65e0\u8f9c \u4e4b\u4e8b\uff0c\u9a6c\u5927\u4eba\u5fc5\u5b9a\u5df2\u7ecf\u77e5\u9053\u4e86\u5427\uff1f\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u5fc3\u5934\u4e00\u9707\uff0c\u4f46\u8868\u9762\u4e0a\u4f9d\u7136\u88c5\u5f97\u5341\u5206\u9547\u5b9a\uff0c\u9053\uff1a\u201c\u5fae\u81e3\u6b63\u662f\u4e3a\u4e86\u8fd9\u4e8b\u800c\u6765\u3002\u201d \u3000\u3000\u4e09\u516c\u4e3b\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u9a6c\u5927\u4eba\u53ef\u77e5\u9053\u4f4f\u5728\u90a3\u5e62\u6c11\u5b85\u91cc\u7684\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u987f\u4e86\u987f\u9053\uff1a\u201c\u597d\u50cf\u662f\u4f4d\u59d3\u795d\u7684\u79c0\u624d\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u6279\u76d7\u532a\u548c\u795d\u79c0\u624d\u6709\u5bbf\u4ec7\u5417\uff1f\u201d \u3000\u3000\u201c\u5fae\u81e3\u6b63\u5728\u8c03\u67e5\uff01\u201d \u3000\u3000\u201c\u4e0d\u5fc5\u8c03\u67e5\u4e86\uff0c\u56e0\u4e3a\u6211\u5df2\u7ecf\u8bf7\u738b\u7edf\u9886\u8c03\u67e5\u5f97\u6e05\u6e05\u695a\u695a\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u8d8a\u52a0\u611f\u5230\u4e0d\u5b89\uff0c\u671b\u5411\u738b\u5f64\u5047\u610f\u95ee\u9053\uff1a\u201c\u90a3\u5c31\u8bf7\u738b\u5927\u4eba\u628a\u4e8b\u60c5\u8bf4\u8bf4\u6e05\u695a\uff01\u201d \u3000\u3000\u738b\u5f64\u795e\u8272\u6c89\u51dd\u7684\u9053\uff1a\u201c\u9099\u5c71\u662f\u9a6c\u5927\u4eba\u6240\u8f96\u5730\u9762\uff0c\u4ee5\u9a6c\u5927\u4eba\u7684\u7cbe\u660e\uff0c\u5bf9\u5404\u5730\u6240\u53d1\u751f\u7684\u4e8b\u60c5 \u5fc5\u5b9a\u4e86\u82e5\u6307\u638c\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u5e72\u54b3\u4e86\u58f0\u9053\uff1a\u201c\u738b\u5927\u4eba\u8bf7\u8bb2\u4e0b\u53bb\uff01\u201d \u3000\u3000\u201c\u9099\u5c71\u5728\u534a\u5e74\u524d\uff0c\u5578\u805a\u4e86\u5343\u4f59\u540d\u76d7\u5bc7\uff0c\u5206\u522b\u6210\u7acb\u4e86\u6c34\u9646\u4e24\u5be8\uff0c\u6c34\u5be8\u5728\u5c71\u540e\u9ec4\u6cb3\u5357\u5cb8\uff0c\u9646 \u5be8\u5219\u9738\u5360\u666e\u6551\u5bfa\u4e3a\u5be8\u5740\uff0c\u8fd9\u4e8b\u9a6c\u5927\u4eba\u4e0d\u80fd\u8bf4\u4e0d\u6e05\u695a\u5427\uff1f\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u795e\u8272\u95f4\u5df2\u663e\u5f97\u53c8\u60f6\u6050\u53c8\u5c34\u5c2c\u7684\u9053\uff1a\u201c\u4e0b\u5b98\u4e5f\u65e9\u6709\u8033\u95fb\uff0c\u6b63\u60f3\u6d3e\u5175\u8fdb\u527f\uff0c\u4e0d\u60f3\u4ed6\u4eec \u5374\u5148\u884c\u95f9\u4e86\u4e8b\u3002\u201d \u3000\u3000\u738b\u5f64\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u65e2\u5c5e\u76d7\u5bc7\uff0c\u5c82\u6709\u4e0d\u95f9\u4e8b\u4e4b\u7406\uff0c\u4ed6\u4eec\u56e0\u4f55\u8981\u5bf9\u795d\u5b85\u4e0b\u624b\uff0c\u9a6c\u5927\u4eba\u4e0d\u59a8 \u4ed4\u7ec6\u8c03\u67e5\u8c03\u67e5\u3002\u201d \u3000\u3000\u201c\u542c\u738b\u5927\u4eba\u7684\u8bed\u6c14\uff0c\u4f3c\u4e4e\u5bf9\u8fd9\u4e8b\u5df2\u77e5\u4e4b\u751a\u8be6\uff0c\u8fd8\u8bf7\u5bf9\u4e0b\u5b98\u8bf4\u660e\u3002\u201d \u3000\u3000\u201c\u795d\u79c0\u624d\u6709\u4e00\u5343\u91d1\uff0c\u540d\u53eb\u795d\u6708\u82f1\uff0c\u751f\u5f97\u7aef\u5e84\u79c0\u4e3d\uff1b\u5c71\u5be8\u5be8\u4e3b\u53eb\u767d\u864e\uff0c\u7adf\u7136\u8981\u5f3a\u9738\u795d\u5bb6\u59d1 \u5a18\u4e3a\u59bb\uff0c\u5e76\u628a\u795d\u79c0\u624d\u7684\u5e7c\u5b50\u63b3\u4e0a\u5c71\u53bb\u505a\u4eba\u8d28\u3002\u201d \u3000\u3000\u201c\u6709\u8fd9\u79cd\u4e8b\uff1f\u8fd9\u6279\u5c71\u5bc7\u672a\u514d\u592a\u65e0\u6cd5\u65e0\u5929\u4e86\uff0c\u738b\u5927\u4eba\u5bf9\u8fd9\u4e8b\u600e\u4f1a\u5982\u6b64\u6e05\u695a\uff1f\u201d \u3000\u3000\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u90a3\u4f4d\u795d\u79c0\u624d\u662f\u738b\u67d0\u7684\u4e00\u95e8\u8868\u4eb2\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u6545\u610f\u554a\u4e86\u58f0\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u90a3\u4e48\u90a3\u665a\u5728\u795d\u5b85\u548c\u76d7\u532a\u4eec\u5bf9\u6297\u7684\uff0c\u662f\u738b\u5927\u4eba\u548c\u4f60\u7684 \u624b\u4e0b\u4eba\u4e86\uff1f\u201d \u3000\u3000\u738b\u5f64\u6574\u4e86\u6574\u8138\u8272\u9053\uff1a\u201c\u738b\u67d0\u7b97\u5f97\u4e86\u4ec0\u4e48\uff01\u90a3\u665a\u8fde\u4e09\u516c\u4e3b\u4e5f\u4f4f\u5728\u795d\u5b85\uff0c\u90a3\u6279\u5c71\u5be8\u91cc\u7684\u8d3c\u5b9a \u5206\u660e\u5b58\u5fc3\u6740\u5bb3\u4e09\u516c\u4e3b\u548c\u6240\u6709\u5927\u5185\u7684\u4eba\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u5fd9\u6447\u5934\u9053\uff1a\u201c\u738b\u5927\u4eba\u8a00\u91cd\u4e86\u3002\u76d7\u5bc7\u4eec\u5fc5\u5b9a\u4e0d\u77e5\u60c5\uff0c\u82e5\u4ed6\u4eec\u77e5\u9053\u662f\u4e09\u516c\u4e3b\u548c\u738b\u5927\u4eba \u4f4f\u5728\u795d\u5b85\uff0c\u5c31\u662f\u501f\u5929\u505a\u80c6\u5b50\uff0c\u4e5f\u4e0d\u6562\u505a\u51fa\u8fd9\u79cd\u5927\u9006\u4e0d\u9053\u7684\u4e8b\u6765\u3002\u201d \u3000\u3000\u738b\u5f64\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u4e0d\u89c1\u5f97\u5427J\u5982\u679c\u4ed6\u4eec\u8fd8\u77e5\u738b\u6cd5\uff0c\u5c31\u4e0d\u4f1a\u5360\u5c71\u4e3a\u5bc7\u3001\u6b8b\u5bb3\u767e\u59d3\u4e86\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u89c1\u4e8b\u5df2\u5982\u6b64\uff0c\u53ea\u597d\u4e0d\u518d\u8a00\u8bed\u3002 \u3000\u3000\u53ea\u542c\u4e09\u516c\u4e3b\u8bed\u6c14\u5e73\u548c\u7684\u9053\uff1a\u201c\u738b\u7edf\u9886\uff0c\u522b\u5728\u9a6c\u5927\u4eba\u9762\u524d\u8bf4\u8fd9\u4e48\u91cd\u7684\u8bdd\uff0c\u6cb3\u5357\u4e00\u7701\u5730\u9762\u8fd9 \u4e48\u5927\uff0c\u9a6c\u5927\u4eba\u90a3\u80fd\u7ba1\u8fd9\u4e48\u591a\uff01\u5176\u5b9e\u5730\u65b9\u4e0a\u6cbb\u5b89\u4e0d\u597d\uff0c\u4e3b\u8981\u7684\u8d23\u4efb\u8fd8\u5728\u671d\u5ef7\uff0c\u4e0d\u80fd\u628a\u9519\u5168\u63a8\u5728 \u9a6c\u5927\u4eba\u8eab\u4e0a\u3002\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u8bf4\u5f97\u662f\u3002\u5176\u5b9e\u5351\u804c\u4e5f\u53ea\u662f\u5c31\u4e8b\u8bba\u4e8b\uff0c\u5e76\u975e\u6709\u610f\u8d23\u602a\u9a6c\u5927\u4eba\uff0c\u8fd8\u8bf7\u9a6c\u5927 \u4eba\u4e07\u52ff\u4ecb\u610f\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u5fd9\u9053\uff1a\u201c\u4e0b\u5b98\u4e0d\u6562\uff0c\u540c\u65f6\u4e5f\u6df1\u611f\u60ed\u6127\u3002\u201d \u3000\u3000\u4e09\u516c\u4e3b\u70b9\u70b9\u5934\uff0c\u7136\u540e\u795e\u8272\u4e25\u8083\u7684\u671b\u5411\u9a6c\u6587\u4e2d\u9053\uff1a\u201c\u9a6c\u5927\u4eba\uff0c\u65e2\u7136\u4f60\u5df2\u77e5\u9099\u5c71\u8d3c\u5bc7\u4e3a\u60a3\uff0c \u5c31\u8be5\u65e9\u65e5\u6d3e\u5175\u8fdb\u527f\uff0c\u7ee5\u9756\u5730\u65b9\u3002\u5982\u679c\u529b\u91cf\u4e0d\u8db3\uff0c\u6211\u53ef\u4ee5\u8bf7\u738b\u7edf\u9886\u4eb2\u7387\u4ed6\u7684\u51e0\u4f4d\u624b\u4e0b\u534f\u52a9\u3002\u603b \u4e4b\uff0c\u7edd\u4e0d\u80fd\u8ba9\u8d3c\u52bf\u5750\u5927\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u8eac\u8eab\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u8bad\u8beb\u7684\u662f\uff0c\u4e0b\u5b98\u4e00\u5b9a\u9a6c\u4e0a\u91c7\u53d6\u884c\u52a8\uff0c\u8fd9\u662f\u4e0b\u5b98\u4efd\u5185\u7684\u4e8b\uff0c\u4e0d\u6562 \u518d\u52b3\u52a8\u738b\u5927\u4eba\u4eb2\u81ea\u51fa\u9a6c\u3002\u201d \u3000\u3000\u201c\u6211\u5e0c\u671b\u4f60\u7684\u884c\u52a8\u8d8a\u5feb\u8d8a\u597d\uff0c\u4f60\u9884\u5b9a\u9700\u8981\u591a\u5c11\u5929\uff0c\u624d\u80fd\u628a\u8fd9\u6279\u8d3c\u5bc7\u5e73\u5b9a\uff1f\u201d \u3000\u3000\u201c\u6700\u591a\u5341\u65e5\uff0c\u4e0b\u5b98\u4e00\u5b9a\u4f1a\u8083\u6e05\u8fd9\u6279\u4e3a\u975e\u4f5c\u6b79\u7684\u8d3c\u5bc7\u3002\u201d \u3000\u3000\u201c\u90a3\u5f88\u597d\uff0c\u6211\u51b3\u5b9a\u7b49\u4f60\u5e73\u5b9a\u4e86\u8d3c\u5bc7\u4ee5\u540e\u518d\u79bb\u5f00\u6d1b\u9633\uff0c\u4f60\u73b0\u5c31\u53ef\u56de\u5230\u629a\u7f72\u8c03\u52a8\u5175\u9a6c\u51c6\u5907\u884c \u52a8\u4e86\u3002\u201d \u3000\u3000\u9a6c\u6587\u4e2d\u968f\u5373\u8eac\u8eab\u544a\u9000\u3002 \u3000\u3000\u9a6c\u6587\u4e2d\u6765\u6d1b\u9633\uff0c\u672c\u610f\u662f\u8981\u548c\u9c7c\u5316\u9f99\u5bc6\u5546\u5bf9\u7b56\uff0c\u4ee5\u4fbf\u8fdb\u4e00\u6b65\u5bf9\u4ed8\u4e09\u516c\u4e3b\u548c\u738b\u5f64\uff0c\u4e07\u60f3\u4e0d\u5230 \u4e8b\u60c5\u7adf\u4f1a\u6f14\u53d8\u5230\u8fd9\u79cd\u5730\u6b65\u3002 \u3000\u3000\u73b0\u5728\uff0c\u4ed6\u9664\u4e86\u8ba9\u9c7c\u5316\u9f99\u5c06\u624b\u4e0b\u7684\u6c34\u9646\u4e24\u5be8\u64a4\u51fa\u9099\u5c71\uff0c\u6839\u672c\u6ca1\u6709\u522b\u7684\u529e\u6cd5\uff0c\u5426\u5219\u90a3\u5c31\u662f\u660e \u76ee\u5f20\u80c6\u7684\u4e0e\u671d\u5ef7\u4f5c\u5bf9\u4e86\u3002 \u3000\u3000\u4e3a\u4e86\u4fdd\u4f4f\u524d\u7a0b\uff0c\u7ee7\u7eed\u4e3a\u897f\u57df\u9b54\u6559\u6548\u529b\uff0c\u4ed6\u5f53\u7136\u4e0d\u4f1a\u90a3\u6837\u505a\u3002 \u3000\u3000\u738b\u5f64\u522b\u8fc7\u4e09\u516c\u4e3b\uff0c\u518d\u6765\u89c1\u6c5f\u5343\u91cc\u3002 \u3000\u3000\u4ed6\u5148\u628a\u4e09\u516c\u4e3b\u53ec\u89c1\u9a6c\u6587\u4e2d\u7684\u7ecf\u8fc7\u8be6\u7ec6\u8bf4\u4e86\u4e00\u904d\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u5904\u7f6e\u5f97\u5f88\u597d\uff0c\u9a6c\u6587\u4e2d\u53ea\u8981\u5411\u9c7c\u5316\u9f99\u4ea4\u4ee3\u4e00\u58f0\uff0c\u9c7c\u5316\u9f99\u5fc5\u53ef\u5728\u5341\u65e5\u4e4b \u5185\u5c06\u624b\u4e0b\u5f92\u4f17\u64a4\u79bb\u9099\u5c71\uff0c\u8bf4\u4e0d\u5b9a\u4e8b\u540e\u9a6c\u6587\u4e2d\u8fd8\u4f1a\u6765\u5411\u4e09\u516c\u4e3b\u8bf7\u529f\u5462\uff01\u201d \u3000\u3000\u738b\u5f64\u5fc3\u6709\u6240\u8651\u7684\u9053\uff1a\u201c\u9c7c\u5316\u9f99\u7684\u624b\u4e0b\u64a4\u79bb\u9099\u5c71\u4e4b\u540e\uff0c\u795d\u8001\u5148\u751f\u5fc5\u5b9a\u8981\u56de\u5230\u6545\u5c45\uff0c\u5144\u5f1f\u5bf9 \u4ed6\u5168\u5bb6\u7684\u5b89\u5168\u4ecd\u6709\u987e\u8651\u3002\u201d \u3000\u3000\u201c\u8fd9\u65b9\u9762\u8001\u5f1f\u5c3d\u7ba1\u653e\u5fc3\uff0c\u5148\u524d\u4f60\u65e2\u5df2\u5bf9\u9a6c\u6587\u4e2d\u8bf4\u660e\u548c\u795d\u5148\u751f\u8868\u4eb2\uff0c\u4e0d\u7ba1\u9a6c\u6587\u4e2d\u662f\u5426\u76f8 \u4fe1\uff0c\u4ed6\u5fc5\u5b9a\u975e\u8d1f\u8d23\u795d\u5bb6\u7684\u5b89\u5168\u4e0d\u53ef\uff0c\u4f55\u51b5\u767d\u864e\u5df2\u6b7b\uff0c\u5176\u4ed6\u4eba\u4f3c\u4e4e\u4e5f\u4e0d\u81f4\u518d\u5bf9\u89c6\u5bb6\u59d1\u5a18\u5fc3\u5b58\u5984 \u5ff5\u3002\u201d \u3000\u3000\u201c\u6c5f\u6b7b\u4ee5\u4e3a\u9c7c\u5316\u9f99\u7684\u8fd9\u6279\u624b\u4e0b\u4f1a\u64a4\u5230\u4ec0\u4e48\u5730\u65b9\u53bb\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd9\u5c31\u5f88\u96be\u8bf4\u4e86\uff0c\u6b64\u65f6\u6b64\u523b\uff0c\u5fc5\u987b\u4fdd\u4f4f\u9a6c\u6587\u4e2d\u9762\u5b50\uff0c\u4e0d\u80fd\u8d76\u5c3d\u6740\u7edd\uff0c\u5426\u5219\uff0c\u4eba\u6025\u9020\u53cd\uff0c \u72d7\u6025\u8df3\u5899\uff0c\u5f88\u53ef\u80fd\u4e00\u53d1\u4e0d\u53ef\u6536\u62fe\u3002\u201d \u3000\u3000\u738b\u5f64\u6c89\u601d\u4e86\u7247\u523b\uff0c\u518d\u9053\uff1a\u201c\u76ee\u524d\u6700\u91cd\u8981\u7684\u4e00\u4ef6\u4e8b\uff0c\u4fbf\u662f\u902e\u4f4f\u6842\u98d8\u9999\u7b49\u4e09\u4e2a\u5973\u4eba\uff0c\u54b1\u4eec\u662f \u5426\u8be5\u9a6c\u4e0a\u8d76\u5f80\u6f7c\u5173\u9a6c\u5be1\u5987\u90a3\u91cc\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u6447\u5934\u9053\uff1a\u201c\u4f9d\u6c5f\u67d0\u611a\u89c1\uff0c\u8fd8\u662f\u6682\u4f4f\u6d1b\u9633\uff0c\u7b49\u9c7c\u5316\u9f99\u7684\u624b\u4e0b\u64a4\u51fa\u9099\u5c71\u518d\u8bf4\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u6842\u98d8\u9999\u7b49\u4e09\u4e2a\u5973\u4eba\u82e5\u5df2\u5148\u884c\u897f\u5954\uff0c\u54b1\u4eec\u518d\u8ffd\u6050\u6015\u5df2\u6765\u4e0d\u53ca\u4e86\u3002\u201d \u3000\u3000\u201c\u8001\u5f1f\u653e\u5fc3\uff0c\u90a3\u4e09\u4e2a\u5973\u7684\u7ecf\u8fc7\u8fd9\u6b21\u53d8\u52a8\uff0c\u662f\u5426\u80fd\u56de\u897f\u57df\uff0c\u8fd8\u5f88\u96be\u8bf4\u3002\u54b1\u4eec\u5fc5\u987b\u5148\u5f04\u6e05\u695a \u60c5\u51b5\uff0c\u624d\u80fd\u53d1\u8d77\u4e0b\u4e00\u6b65\u884c\u52a8\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u5982\u4f55\u624d\u80fd\u5f04\u6e05\u60c5\u51b5\uff1f\u662f\u5426\u8be5\u9a6c\u4e0a\u6d3e\u4eba\u518d\u6f5c\u5165\u666e\u6551\u5bfa\u4fa6\u5bdf\uff1f\u201d \u3000\u3000\u201c\u4e0d\u5fc5\uff0c\u8fd9\u79cd\u4e8b\u5916\u4eba\u5f88\u96be\u67e5\u51fa\u3002\u201d \u3000\u3000\u201c\u6c5f\u5144\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u8001\u5f1f\u4e0d\u5fc5\u5fc3\u6025\uff0c\u6211\u60f3\u859b\u8001\u5f1f\u4e00\u5b9a\u8fd8\u4f1a\u518d\u6765\uff0c\u53ea\u8981\u4ed6\u4e00\u5230\uff0c\u54b1\u4eec\u4ec0\u4e48\u6d88\u606f\u5168\u53ef\u77e5\u9053\u3002\u201d \u3000\u3000\u7b2c\u4e8c\u5929\u6df1\u591c\uff0c\u859b\u767e\u80dc\u679c\u7136\u6765\u4e86\u3002 \u3000\u3000\u4ed6\u662f\u7531\u884c\u5916\u76f4\u63a5\u8d8a\u5899\u8fdb\u5165\u540e\u884c\u7684\u3002 \u3000\u3000\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u5f53\u5373\u5728\u5ba2\u5385\u63a5\u5f85\u3002 \u3000\u3000\u738b\u5f64\u95ee\u9053\uff1a\u201c\u5c71\u4e0a\u7684\u60c5\u5f62\u5982\u4f55\uff1f\u201d \u3000\u3000\u859b\u767e\u80dc\u9053\uff1a\u201c\u6c34\u9646\u4e24\u5be8\u5df2\u7ecf\u51c6\u5907\u597d\u64a4\u9000\u5de5\u4f5c\uff0c\u4e0d\u8fc7\u4ed6\u4eec\u5fc5\u987b\u7b49\u5f85\u9a6c\u5de1\u629a\u5230\u6765\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u8981\u7b49\u9a6c\u6587\u4e2d\uff1f\u201d \u3000\u3000\u201c\u9a6c\u5de1\u629a\u82e5\u4e0d\u4eb2\u7387\u5b98\u5175\u524d\u6765\u6e05\u527f\uff0c\u4ed6\u4eec\u6709\u4ec0\u4e48\u7406\u7531\u64a4\u8d70\uff0c\u8fd9\u51fa\u620f\u5f53\u7136\u8981\u505a\u51fa\u6765\u7ed9\u738b\u5927\u4eba \u548c\u6c5f\u524d\u8f88\u770b\uff0c\u540c\u65f6\u4e5f\u7b49\u4e8e\u8ba9\u9a6c\u5de1\u629a\u7acb\u4e0b\u4e00\u6b21\u5927\u529f\u3002\u201d \u3000\u3000\u738b\u5f64\u9894\u9996\u9053\uff1a\u201c\u8001\u5f1f\u8bf4\u5f97\u5f88\u5bf9\uff0c\u73b0\u5728\u738b\u67d0\u6700\u60f3\u77e5\u9053\u7684\u5c31\u662f\u90a3\u4e09\u4e2a\u5973\u4eba\u7684\u53bb\u5411\u3002\u201d \u3000\u3000\u859b\u767e\u80dc\u76b1\u4e86\u76b1\u7709\u9053\uff1a\u201c\u665a\u8f88\u6b63\u662f\u4e3a\u8fd9\u4ef6\u4e8b\u800c\u6765\uff0c\u90a3\u4e09\u4e2a\u5973\u4eba\u6b64\u523b\u7684\u52a8\u5411\uff0c\u4f3c\u4e4e\u5c1a\u672a\u786e \u5b9a\u3002\u201d \u3000\u3000\u201c\u8fd9\u8bdd\u600e\u4e48\u8bb2\uff1f\u201d \u3000\u3000\u201c\u8bb0\u5f97\u665a\u8f88\u7b2c\u4e00\u6b21\u5728\u5f00\u5c01\u548c\u738b\u5927\u4eba\u89c1\u9762\u65f6\u4e5f\u66fe\u8bf4\u8fc7\uff0c\u90a3\u4e09\u4e2a\u5973\u4eba\u5bf9\u4e2d\u539f\u9887\u4e3a\u7559\u604b\uff0c\u5e76\u4e0d \u60f3\u56de\u897f\u57ce\u53bb\uff0c\u53ea\u662f\u5374\u53c8\u4e0d\u5f97\u4e0d\u542c\u4ece\u9a6c\u5de1\u629a\uff0c\u5982\u4eca\u5c71\u5be8\u6709\u53d8\uff0c\u5979\u4eec\u8bf4\u4e0d\u5b9a\u771f\u4f1a\u7559\u4e0b\uff0c\u518d\u56de\u5f00 \u5c01\u3002\u201d \u3000\u3000\u201c\u8001\u5f1f\u662f\u5426\u5728\u5c71\u5be8\u89c1\u8fc7\u5979\u4eec\uff1f\u201d \u3000\u3000\u201c\u5979\u4eec\u7684\u884c\u52a8\u5f88\u8be1\u79d8\uff0c\u60f3\u770b\u5230\u5979\u4eec\u4e0d\u662f\u4e00\u4ef6\u6613\u4e8b\uff0c\u4e0d\u8fc7\u665a\u8f88\u53ef\u4ee5\u786e\u5b9a\uff0c\u73b0\u5728\u5979\u4eec\u4ecd\u4f4f\u5728 \u5c71\u5be8\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u5982\u6b64\uff0c\u738b\u67d0\u662f\u5426\u53ef\u4ee5\u6d3e\u4eba\u591c\u5165\u666e\u6551\u5bfa\uff0c\u5c06\u5979\u4eec\u63b3\u51fa\uff1f\u201d \u3000\u3000\u201c\u6050\u6015\u5f88\u96be\uff0c\u6253\u8349\u60ca\u86c7\uff0c\u53cd\u800c\u5f04\u5de7\u6210\u62d9\u3002\u201d \u3000\u3000\u201c\u56f0\u96be\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u4e0d\u7792\u738b\u5927\u4eba\uff0c\u665a\u8f88\u867d\u53ef\u786e\u5b9a\u8fd9\u4e09\u4e2a\u5973\u4eba\u4ecd\u5728\u5c71\u5be8\uff0c\u4f46\u5374\u5f04\u4e0d\u6e05\u695a\u4f4f\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u738b\u5927 \u4eba\u6d3e\u53bb\u7684\u4eba\u53c8\u5982\u4f55\u80fd\u63a2\u6089\u5979\u4eec\u7684\u4f4f\u5904\uff1f\u201d \u3000\u3000\u201c\u4e0d\u59a8\u902e\u4f4f\u4e00\u540d\u55bd\u7f57\u903c\u95ee\u3002\u201d \u3000\u3000\u201c\u8fde\u665a\u8f88\u6211\u8fd9\u4e2a\u6709\u8d44\u683c\u53c2\u4e0e\u5c71\u5be8\u673a\u5bc6\u7684\u4eba\uff0c\u90fd\u65e0\u4ece\u5f97\u6089\uff0c\u903c\u95ee\u4e00\u540d\u55bd\u7f57\u53c8\u80fd\u95ee\u51fa\u4ec0\u4e48\u6765 \u5462\uff1f\u201d \u3000\u3000\u201c\u4f9d\u8001\u5f1f\u770b\u6cd5\uff0c\u8fd9\u4e8b\u8be5\u5982\u4f55\u5904\u7f6e\uff1f\u8001\u5f1f\u4e00\u5b9a\u77e5\u9053\uff0c\u738b\u67d0\u548c\u6c5f\u5927\u4fa0\u8fd9\u6b21\u968f\u540c\u4e09\u516c\u4e3b\u51fa\u4eac\uff0c \u76ee\u7684\u5c31\u662f\u4e3a\u4e86\u627e\u5230\u90a3\u53eb\u6842\u98d8\u9999\u7684\u5973\u4eba\uff0c\u5982\u679c\u4e0d\u80fd\u5c06\u6b64\u5973\u902e\u5230\uff0c\u90a3\u5c31\u7b49\u4e8e\u767d\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u665a\u8f88\u73b0\u5728\u5c31\u53e3\u53bb\uff0c\u4e00\u5b9a\u4f1a\u5c06\u786e\u5b9e\u6d88\u606f\u5c3d\u901f\u5411\u738b\u5927\u4eba\u62a5\u544a\u3002\u201d \u3000\u3000\u5341\u65e5\u540e\uff0c\u9a6c\u6587\u4e2d\u679c\u7136\u4eb2\u7387\u5927\u961f\u9a6c\u6b65\u7cbe\u5175\u5c06\u9099\u5c71\u7684\u6c34\u9646\u4e24\u5be8\u8d3c\u5bc7\u9a71\u51fa\u6d1b\u9633\u3002\u201c \u3000\u3000\u4e8b\u540e\uff0c\u9a6c\u6587\u4e2d\u8fd8\u7279\u5730\u518d\u6765\u664b\u89c1\u4e09\u516c\u4e3b\u3002 \u3000\u3000\u4ed6\u7684\u76ee\u7684\u4e0d\u5916\u662f\uff1a\u201c\u8868\u529f\u201d\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u4e5f\u5047\u610f\u5609\u5956\u4e00\u756a\uff0c\u5e76\u7b54\u5e94\u56de\u4eac\u4e4b\u540e\u5c06\u4ed6\u7f13\u9756\u5730\u65b9\u7684\u529f\u7ee9\u9762\u594f\u7687\u4e0a\u3002 \u3000\u3000\u9a6c\u6587\u4e2d\u4e5f\u5411\u4e09\u516c\u4e3b\u62dc\u8c22\u4e86\u4e00\u756a\u3002 \u3000\u3000\u53e6\u5916\uff0c\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u4eb2\u81ea\u53bb\u4e86\u4e00\u8d9f\u795d\u5b85\uff0c\u89c1\u4e00\u5207\u5b8c\u597d\u65e0\u635f\uff0c\u624d\u518d\u6d3e\u4eba\u62a4\u9001\u89c6\u9999\u4ead\u5168\u5bb6\u8fd4 \u56de\u6545\u5c45\u3002 \u3000\u3000\u795d\u9999\u4ead\u4e3a\u8868\u793a\u8877\u5fc3\u8c22\u610f\uff0c\u4e8e\u8fd4\u56de\u6545\u5c45\u5f53\u665a\u53c8\u5907\u4e0b\u76db\u7b75\u6b3e\u5f85\uff0c\u9664\u4e09\u516c\u4e3b\u548c\u601c\u82b1\u5916\uff0c\u5176\u4f59\u7684 \u4eba\u5168\u4f53\u8d76\u5f80\u795d\u5b85\uff0c\u5927\u5403\u5927\u559d\u4e86\u4e00\u987f\u3002 \u3000\u3000\u5e2d\u95f4\uff0c\u738b\u5f64\u7ec8\u4e8e\u900f\u9732\u4e86\u4ed6\u4eec\u8fd9\u4e00\u884c\u4eba\u7684\u8eab\u4efd\u6765\u5386\u3002 \u3000\u3000\u795d\u9999\u4ead\u5168\u5bb6\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u81ea\u5df1\u5bb6\u91cc\u63a5\u5f85\u8fc7\u7684\u8fd9\u6279\u5ba2\u4eba\uff0c\u7adf\u7136\u662f\u91d1\u679d\u7389\u53f6\u7684\u516c\u4e3b\u548c\u5927\u5185\u7687 \u4e0a\u8eab\u8fb9\u7684\u4eba\u4ee5\u53ca\u5f53\u4ee3\u6b66\u6797\u5927\u4fa0\u3002 \u3000\u3000\u56e0\u4e4b\uff0c\u7b2c\u4e8c\u5929\u7279\u5730\u518d\u56de\u6d1b\u9633\uff0c\u524d\u5f80\u5e9c\u8859\u5411\u4e09\u516c\u4e3b\u53e9\u8c22\u6069\u5178\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u5f53\u5373\u4eb2\u5207\u63a5\u89c1\uff0c\u5e76\u4ea4\u4ee3\u7f57\u77e5\u5e9c\u4eca\u540e\u5bf9\u795d\u9999\u4ead\u4e00\u5bb6\u8981\u591a\u591a\u7167\u5e94\u3002 \u3000\u3000\u53e6\u5916\uff0c\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u6700\u60f3\u77e5\u9053\u7684\u4e00\u4ef6\u4e8b\uff0c\u7ec8\u4e8e\u5f97\u5230\u4e86\u6d88\u606f\u3002 \u3000\u3000\u636e\u859b\u767d\u80dc\u6765\u62a5\uff0c\u6842\u98d8\u9999\u7b49\u4e09\u540d\u5996\u5973\u7ecf\u9a6c\u6587\u4e2d\u6307\u793a\uff0c\u4ecd\u8981\u524d\u5f80\u897f\u57df\uff0c\u800c\u4e14\u5fc5\u5b9a\u8def\u7ecf\u6f7c\u5173\u3002 \u3000\u3000\u56e0\u4e4b\uff0c\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u4e00\u884c\u4eba\u4f17\uff0c\u968f\u5373\u4fdd\u7740\u4e09\u516c\u4e3b\u79bb\u5f00\u6d1b\u9633\u5411\u897f\u8fdb\u53d1\u3002 \u3000\u3000\u6f7c\u5173\u2014\u2014\u5730\u5f53\u534e\u5c71\u4e4b\u5317\u3001\u6e2d\u6c34\u4e4b\u5357\u3001\u9ec4\u6cb3\u4e4b\u897f\u3002 \u3000\u3000\u5176\u4e1c\u7fa4\u5c71\u8fde\u4e98\uff0c\u4ee5\u81f3\u7075\u5b9d\uff1b\u5176\u897f\u5219\u65f7\u91ce\u5929\u5f00\uff0c\u5e73\u7574\u4e07\u9877\uff0c\u4e3a\u664b\u3001\u79e6\u3001\u8c6b\u4e09\u7701\u8981\u51b2\u4e4b\u5730\uff0c \u5f62\u52bf\u9669\u8981\uff0c\u81ea\u53e4\u5175\u5bb6\u5fc5\u4e89\u3002 \u3000\u3000\u738b\u5f64\u3001\u6c5f\u5343\u91cc\u7b49\u4eba\u4fdd\u7740\u4e09\u516c\u4e3b\uff0c\u4e0d\u51e0\u65e5\u4fbf\u7531\u6d1b\u9633\u6765\u5230\u6f7c\u5173\u3002 \u3000\u3000\u4e0d\u6d88\u8bf4\uff0c\u4ed6\u4eec\u7684\u8eab\u4efd\u90fd\u6781\u7aef\u4fdd\u5bc6\uff0c\u4f9d\u7136\u626e\u4f5c\u5546\u8d3e\u6a21\u6837\u3002 \u3000\u3000\u5f53\u65e5\u4e0b\u5348\uff0c\u4e00\u884c\u5341\u4f59\u4eba\u4f4f\u8fdb\u57ce\u5185\u4e1c\u5927\u8857\u4e00\u5bb6\u5ba2\u6808\u3002\u4e3a\u884c\u52a8\u65b9\u4fbf\u8d77\u89c1\uff0c\u4ecd\u662f\u5305\u4e0b\u4e86\u6574\u5ea7\u540e \u9662\u3002 \u3000\u3000\u73b0\u5728\uff0c\u6700\u91cd\u8981\u7684\u4e00\u4ef6\u4e8b\u4fbf\u662f\u627e\u5230\u9a6c\u5be1\u5987\u7684\u90a3\u95f4\u9152\u5e97\u3002 \u3000\u3000\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u4ecd\u6d3e\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u8d1f\u8d23\u8fd9\u6b21\u884c\u52a8\uff0c\u56e0\u4e3a\u4e24\u4eba\u66fe\u6709\u591a\u6b21\u5408\u4f5c\u7ecf\u9a8c\uff0c\u5df2\u6210\u4e86\u6700 \u4f73\u642d\u6863\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u7565\u4e8b\u6574\u7406\uff0c\u91cd\u65b0\u6362\u8fc7\u4e00\u8eab\u8863\u670d\uff0c\u79bb\u5f00\u4e86\u5ba2\u6808\u3002 \u3000\u3000\u4ed6\u4eec\u5148\u5728\u4e1c\u5927\u8857\u53c2\u89c2\u4e86\u4e00\u68f5\u67af\u69d0\u3002 \u3000\u3000\u539f\u6765\u8fd9\u68f5\u67af\u69d0\u5728\u4f20\u8bf4\u4e2d\u5927\u5927\u6709\u540d\uff0c\u5f53\u5730\u7684\u4eba\u53ef\u8bf4\u5bb6\u55bb\u6237\u6653\u3002 \u3000\u3000\u8fd9\u67af\u69d0\u6811\u8eab\u4e0a\u6709\u4e00\u5927\u6d1e\uff0c\u76f8\u4f20\u4e09\u56fd\u65f6\uff0c\u66f9\u64cd\u88ab\u9a6c\u8d85\u6240\u8d25\uff0c\u66fe\u7ecf\u5175\u8d25\u65ad\u888d\u5243\u987b\uff0c\u4ee5\u907f\u9a6c\u8d85 \u8ffd\u9010\u3002\u540e\u6765\u9a6c\u8d85\u8ffd\u5230\u6b64\u6811\u65c1\uff0c\u66f9\u64cd\u7ed5\u6811\u800c\u8d70\uff0c\u9a6c\u8d85\u957f\u67aa\u523a\u5165\u6811\u4e0a\uff0c\u66f9\u64cd\u5f97\u673a\u4f1a\u8131\u8eab\u3001\u540e\u6765\u66f9 \u64cd\u4fbf\u628a\u8fd9\u682a\u5de8\u69d0\u5c01\u4e3a\u6811\u738b\u3002 \u3000\u3000\u867d\u7136\u8fd9\u53ea\u662f\u4f20\u8bf4\u4e2d\u7684\u6545\u4e8b\uff0c\u4f46\u5f53\u5730\u7684\u5c45\u6c11\u5374\u8a00\u4e4b\u51ff\u51ff\uff0c\u4e09\u56fd\u6f14\u4e49\u4e0a\u66f4\u662f\u6709\u8fc7\u7cbe\u5f69\u7684\u63cf \u8ff0\u3002 \u3000\u3000\u770b\u8fc7\u67af\u69d0\uff0c\u5f88\u5feb\u4fbf\u6253\u542c\u5230\u9a6c\u5be1\u5987\u7684\u9152\u5e97\u6240\u5728\u3002 \u3000\u3000\u8fd9\u5012\u5e76\u975e\u9a6c\u5be1\u5987\u7684\u9152\u5e97\u5f00\u5f97\u5927\uff0c\u800c\u662f\u9a6c\u5be1\u5987\u672c\u4eba\u7684\u540d\u5934\u54cd\u4eae\u3002 \u3000\u3000\u636e\u8bf4\u9a6c\u5be1\u5987\u53ea\u6709\u4e8c\u5341\u51fa\u5934\u5e74\u7eaa\uff0c\u662f\u4e2a\u6807\u51c6\u7684\u7f8e\u4eba\u80da\u5b50\uff0c\u4f53\u6001\u8f7b\u76c8\uff0c\u98ce\u59ff\u7ef0\u7ea6\uff0c\u5c24\u5176\u5bf9\u5f85 \u5ba2\u4eba\u624b\u8155\u9ad8\u660e\uff0c\u51e1\u662f\u5230\u9a6c\u5be1\u5987\u9152\u5e97\u5149\u987e\uff0c\u591a\u534a\u662f\u9189\u7fc1\u4e4b\u610f\u4e0d\u5728\u9152\u3002 \u3000\u3000\u4f46\u9a6c\u5be1\u5987\u7684\u9ad8\u660e\u5904\uff0c\u4e5f\u5c31\u662f\u5728\u8fd9\u4e00\u70b9\uff0c\u867d\u7136\u62dc\u5012\u5728\u5979\u77f3\u69b4\u88d9\u4e0b\u7684\u767b\u5f92\u5b50\u591a\u5982\u8fc7\u6c5f\u4e4b\u9c7c\uff0c \u5374\u6ca1\u542c\u8bf4\u8fc7\u6709\u4e00\u4e2a\u66fe\u6cbe\u5230\u5979\u4fbf\u5b9c\u7684\u3002 \u3000\u3000\u6b63\u56e0\u5982\u6b64\uff0c\u624d\u80fd\u4f7f\u5979\u76db\u540d\u4e0d\u8870\u3002 \u3000\u3000\u5426\u5219\uff0c\u5c82\u4e0d\u5929\u4e0b\u5927\u4e71\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u8fde\u8882\u6765\u5230\u9a6c\u5be1\u5987\u7684\u9152\u5e97\u95e8\u5916\u3002 \u3000\u3000\u679c\u7136\uff0c\u95e8\u9762\u5e76\u4e0d\u5927\uff0c\u53ea\u5728\u95e8\u5916\u60ac\u4e86\u4e2a\u968f\u98ce\u98d8\u626c\u7684\u5e03\u62db\uff0c\u4e0a\u9762\u5199\u7740\u201c\u6843\u6797\u5c45\u201d\u4e09\u5b57\u3002 \u3000\u3000\u53ef\u89c1\u9a6c\u5be1\u5987\u679c\u7136\u6709\u540d\uff0c\u56e0\u4e3a\u5979\u7684\u4eba\u540d\u5df2\u7ecf\u4ee3\u66ff\u4e86\u5e97\u540d\u3002 \u3000\u3000\u4ece\u95e8\u5916\u4fbf\u53ef\u77a7\u89c1\u5e97\u91cc\u53ea\u6709\u4e94\u516d\u5f20\u684c\u5b50\uff0c\u5373\u4f7f\u5ba2\u6ee1\uff0c\u5145\u5176\u91cf\u4e5f\u4e0d\u8fc7\u5bb9\u7eb3\u4e8c\u4e09\u5341\u4eba\u800c\u5df2\u3002 \u3000\u3000\u73b0\u5728\uff0c\u5df2\u7ecf\u5ba2\u6ee1\uff0c\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u81ea\u5fd6\u5c31\u662f\u8fdb\u53bb\u4e5f\u5fc5\u5b9a\u627e\u4e0d\u5230\u5ea7\u4f4d\u3002 \u3000\u3000\u7531\u4e8e\u4ed6\u4eec\u5fc5\u987b\u4fdd\u5bc6\u8eab\u4efd\uff0c\u82e5\u8fdb\u53bb\u4eae\u4eae\u76f8\uff0c\u88ab\u8feb\u518d\u51fa\u6765\uff0c\u53cd\u800c\u4e0d\u5982\u4e0d\u8fdb\u53bb\u7684\u597d\u3002 \u3000\u3000\u4e24\u4eba\u7565\u4e00\u72b9\u8c6b\uff0c\u51b3\u5b9a\u5148\u5230\u5404\u5904\u901b\u901b\uff0c\u4ee5\u4fbf\u8fc7\u4e00\u6bb5\u65f6\u95f4\u518d\u6765\u3002 \u3000\u3000\u5c82\u77e5\u8f6c\u8eab\u521a\u8d70\u4e86\u6ca1\u51e0\u6b65\uff0c\u4fbf\u542c\u8eab\u540e\u4e00\u4e2a\u5a07\u6ef4\u6ef4\u7684\u58f0\u97f3\u9053\uff1a\u201c\u4e24\u4f4d\u5ba2\u5b98\u4e3a\u4ec0\u4e48\u4e0d\u8fdb\u53bb\u5750\u5750 \u5462\uff1f\u201d \u3000\u3000\u4e24\u4eba\u7acb\u5373\u6b62\u6b65\u56de\u8eab\uff0c\u53ea\u89c1\u8eab\u524d\u4e0d\u8fdc\u5904\u7ad9\u7740\u4e00\u4f4d\u660e\u7738\u7693\u9f7f\u3001\u987e\u76fc\u751f\u59ff\u7684\u7ea2\u8863\u5c11\u5973\u3002 \u3000\u3000\u8fd9\u540d\u7ea2\u8863\u5c11\u5973\u770b\u5e74\u7eaa\u4e0d\u8fc7\u5341\u4e03\u516b\u5c81\uff0c\u867d\u7136\u59ff\u8272\u8ff7\u4eba\uff0c\u4f46\u5728\u9884\u6599\u4e2d\uff0c\u4f3c\u4e4e\u4e0d\u53ef\u80fd\u662f\u9a6c\u5be1 \u5987\u3002 \u3000\u3000\u767d\u7fbd\u8f7b\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u521a\u624d\u8bf4\u8bdd\u7684\u53ef\u662f\u59d1\u5a18\uff1f\u201d \u3000\u3000\u7ea2\u8863\u5c11\u5973\u5ae3\u7136\u4e00\u7b11\u9053\uff1a\u201c\u6211\u91cc\u53ea\u6709\u6211\uff0c\u4e0d\u662f\u6211\u8fd8\u4f1a\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u767d\u7fbd\u987f\u4e86\u987f\u9053\uff1a\u201c\u6211\u4eec\u672c\u6765\u60f3\u8fdb\u53bb\u3002\u53ea\u662f\u91cc\u9762\u5ba2\u4eba\u5df2\u7ecf\u6ee1\u4e86\uff0c\u6240\u4ee5\u624d\u51c6\u5907\u5230\u522b\u7684\u5730\u65b9 \u53bb\u3002\u201d \u3000\u3000\u7ea2\u8863\u5c11\u5973\u5a07\u9765\u4e0a\u6cdb\u51fa\u4e24\u4e2a\u8ff7\u4eba\u7684\u9152\u7a9d\uff0c\u7b11\u5f97\u66f4\u751c\uff0c\u95ee\u9053\uff1a\u201c\u4e24\u4f4d\u5ba2\u5b98\u662f\u5426\u8fd8\u6ca1\u7528\u8fc7\u665a \u996d\uff1f\u201d \u3000\u3000\u201c\u82e5\u7528\u8fc7\u665a\u996d\uff0c\u6211\u4eec\u5c31\u4e0d\u4f1a\u518d\u5230\u8fd9\u91cc\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u968f\u6211\u6765\u5427\uff01\u201d \u3000\u3000\u7ea2\u8863\u5c11\u5973\u8bf4\u7740\uff0c\u8f6c\u8eab\u8d70\u5411\u9152\u5e97\u4e00\u4fa7\u3002 \u3000\u3000\u539f\u6765\u4fa7\u9762\u8fd8\u6709\u4e00\u4e2a\u4fa7\u95e8\uff0c\u5e73\u5e38\u662f\u5173\u7740\u7684\uff0c\u82e5\u6ca1\u4eba\u5f15\u9886\uff0c\u7eb5\u7136\u6709\u7684\u5ba2\u4eba\u77e5\u9053\u8fd9\u5904\u4fa7\u95e8\uff0c\u4e5f \u662f\u65e0\u6cd5\u8fdb\u53bb\u3002 \u3000\u3000\u4e24\u4eba\u968f\u540c\u7ea2\u8863\u5c11\u5973\u8fdb\u5165\u4fa7\u95e8\u540e\uff0c\u7ea2\u8863\u5c11\u5973\u56de\u8eab\u518d\u628a\u95e8\u5173\u4e0a\u3002 \u3000\u3000\u4fa7\u95e8\u76f4\u901a\u4e00\u5904\u5e03\u7f6e\u9ad8\u96c5\u7684\u623f\u95f4\uff0c\u548c\u5e97\u9762\u91cc\u90a3\u4e9b\u4e00\u822c\u9152\u5ba2\u5b8c\u5168\u9694\u7edd\u3002 \u3000\u3000\u7ea2\u8863\u5c11\u5973\u71c3\u4e0a\u7531\u5929\u82b1\u677f\u5782\u540a\u4e0b\u6765\u7684\u4e00\u76cf\u516b\u89d2\u5782\u82cf\u5bab\u706f\uff0c\u7b11\u5bb9\u53ef\u63ac\u7684\u95ee\u9053\uff1a\u201c\u4e24\u4f4d\u5ba2\u5b98\u89c9 \u5f97\u8fd9\u91cc\u8fd8\u597d\u5427\uff1f\u201d \u3000\u3000\u767d\u7fbd\u4e0d\u4f4f\u70b9\u5934\u9053\uff1a\u201c\u592a\u597d\u4e86\uff0c\u592a\u597d\u4e86\uff01\u80fd\u5728\u8fd9\u79cd\u9ad8\u96c5\u7684\u73af\u5883\u996e\u9152\u5403\u996d\uff0c\u8be5\u662f\u4eba\u751f\u4e00\u5927\u4eab \u53d7\uff0c\u65e2\u7136\u5916\u9762\u5ea7\u4f4d\u5df2\u6ee1\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u8ba9\u522b\u7684\u5ba2\u4eba\u5230\u8fd9\u91cc\u6765\u5462\uff1f\u201d \u3000\u3000\u7ea2\u8863\u5c11\u5973\u53c8\u662f\u5ae3\u7136\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u548c\u4ed6\u4eec\u4e0d\u540c\uff0c\u4ed6\u4eec\u662f\u4e00\u822c\u5ba2\u4eba\uff0c\u8fd9\u91cc\u662f\u62db\u5f85\u8d35\u5bbe\u7684\u5730 \u65b9\u3002 \u3000\u3000\u201c\u53ef\u662f\u6211\u4eec\u4ece\u672a\u5149\u987e\u8fc7\u8d35\u9152\u5e97\uff0c\u59d1\u5a18\u600e\u4e48\u4f1a\u628a\u6211\u4eec\u5f53\u6210\u8d35\u5bbe\u5462\uff1f\u201d \u3000\u3000\u201c\u4ece\u8863\u7740\u4e3e\u6b62\u4e0a\u5c31\u770b\u5f97\u51fa\u6765\uff0c\u4e24\u4f4d\u82f1\u4fca\u6f47\u6d12\uff0c\u98ce\u5ea6\u7fe9\u7fe9\uff0c\u6c14\u8d28\u4e0e\u4e00\u822c\u4eba\u4e0d\u76f8\u540c\uff0c\u5e72\u6211\u4eec \u8fd9\u4e00\u884c\u7684\uff0c\u5f53\u7136\u4e00\u773c\u5c31\u770b\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u5982\u6b64\u770b\u6765\uff0c\u51e1\u662f\u88ab\u62db\u5f85\u5728\u8fd9\u623f\u95f4\u7684\uff0c\u9152\u996d\u94b1\u4e00\u5b9a\u5f88\u8d35\u7f57\uff1f\u201d \u3000\u3000\u201c\u8d35\u4e5f\u8d35\u4e0d\u4e86\u591a\u5c11\uff0c\u88ab\u62db\u5f85\u5728\u8fd9\u91cc\uff0c\u662f\u770b\u4eba\u4e0d\u7740\u94b1\u7684\u3002\u201d \u3000\u3000\u201c\u8fd9\u8bdd\u600e\u4e48\u8bb2\uff1f\u201d \u3000\u3000\u201c\u51e1\u662f\u80fd\u5230\u8fd9\u623f\u95f4\u6765\u7684\uff0c\u90fd\u662f\u50cf\u6837\u7684\u5ba2\u4eba\uff0c\u5982\u679c\u98ce\u5ea6\u6c14\u8d28\u4e0d\u591f\uff0c\u5c31\u662f\u518d\u6709\u94b1\uff0c\u6211\u4eec\u4e5f\u4e0d \u62db\u5f85\u3002\u201d \u3000\u3000\u201c\u59d1\u5a18\u771f\u662f\u628a\u6211\u4eec\u8fc7\u5956\u4e86\uff0c\u5176\u5b9e\u6211\u4eec\u4e0d\u8fc7\u662f\u4e24\u4e2a\u505a\u5c0f\u751f\u610f\u7684\u4eba\uff0c\u867d\u7136\u7a7f\u7684\u4e00\u8eab\u8863\u670d\u8fd8\u4e0d \u9519\uff0c\u4e5f\u4e0d\u8fc7\u6545\u610f\u5145\u5145\u95e8\u9762\u800c\u5df2\u3002\u201d \u3000\u3000\u201c\u5ba2\u5b98\u592a\u5ba2\u6c14\u4e86\uff0c\u4e00\u4e2a\u4eba\u8eab\u5206\u9ad8\u4f4e\uff0c\u4e0d\u5728\u4e4e\u662f\u505a\u4ec0\u4e48\u7684\uff0c\u4f60\u8bf4\u662f\u5417\uff1f\u201d \u3000\u3000\u7ea2\u8863\u5c11\u5973\u8bf4\u7740\uff0c\u5012\u4e86\u4e24\u676f\u70ed\u8336\u9001\u5230\u684c\u4e0a\uff0c\u663e\u5f97\u4eb2\u5207\u65e0\u6bd4\u7684\u9053\uff1a\u201c\u53ea\u987e\u8bf4\u8bdd\uff0c\u8fd8\u5fd8\u8bb0\u95ee\u4e24 \u4f4d\u5ba2\u5b98\u8981\u559d\u4ec0\u4e48\u9152\uff1f\u7528\u4ec0\u4e48\u83dc\uff1f\u201d \u3000\u3000\u767d\u7fbd\u5fd9\u9053\uff1a\u201c\u968f\u4fbf\u3002\u201d \u3000\u3000\u201c\u5ba2\u5b98\uff0c\u6211\u4eec\u8fd9\u91cc\u6ca1\u6709\u8fd9\u79cd\u9152\uff0c\u4e5f\u6ca1\u8fd9\u9053\u83dc\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u53ea\u7ba1\u62ff\u6700\u597d\u7684\u6765\u3002\u201d \u3000\u3000\u201c\u5ba2\u5b98\uff0c\u4f60\uff20\u8d22\u5728\u9a97\u4eba\uff01\u201d \u3000\u3000\u201c\u8fd9\u8bdd\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u201c\u4f60\u521a\u624d\u8bf4\u662f\u505a\u5c0f\u751f\u610f\u7684\uff0c\u505a\u5c0f\u751f\u610f\u53ea\u662f\u8747\u5934\u5c0f\u5229\uff0c\u4f46\u73b0\u5728\u9152\u83dc\u5374\u8981\u6700\u597d\u7684\uff0c\u4e0d\u662f\u9a97\u4eba \u662f\u4ec0\u4e48\uff1f\u201d\u505a\u5c0f\u751f\u610f\u7167\u6837\u4e5f\u80fd\u53d1\u8d22\uff0c\u5c24\u5176\u73b0\u5728\u89c1\u59d1\u5a18\u62db\u5f85\u8fd9\u5f97\u4e48\u4eb2\u5207\uff0c\u53c8\u600e\u597d\u610f\u601d\u4e0d\u7167\u987e\u7167 \u987e\u8d35\u9152\u5e97\u7684\u751f\u610f\u3002\u201c \u3000\u3000\u7ea2\u8863\u5c11\u5973\u6ca1\u518d\u8bf4\u4ec0\u4e48\uff0c\u8f6c\u8eab\u51fa\u623f\u800c\u53bb\uff0c\u4e34\u5230\u95e8\u53e3\uff0c\u53c8\u56de\u5934\u5411\u4e24\u4eba\u4e00\u7b11\uff0c\u7b11\u5f97\u5f88\u591f\u8ff7\u4eba\u3002 \u3000\u3000\u5927\u7ea6\u76cf\u8336\u5de5\u592b\u4e4b\u540e\uff0c\u7ea2\u8863\u5c11\u5973\u518d\u8fdb\u6765\u65f6\u5df2\u5f00\u59cb\u4e0a\u83dc\u3002 \u3000\u3000\u679c\u7136\u6bcf\u6837\u83dc\u90fd\u662f\u73cd\u9990\u7f8e\u5473\u3002 \u3000\u3000\u4e0d\u7528\u8bf4\uff0c\u9152\u4e5f\u662f\u6700\u597d\u7684\uff0c\u53e6\u5916\u675f\u6709\u4e00\u76d8\u7f8a\u8089\u5305\u5b50\u548c\u4e00\u76d8\u9992\u5934\u3002 \u3000\u3000\u767d\u7fbd\u642d\u8baa\u7740\u95ee\u9053\uff1a\u9152\u83dc\u4e0a\u5f97\u597d\u5feb\uff0c\u90fd\u662f\u59d1\u5a18\u4eb2\u624b\u505a\u7684\u5417\uff1f\u201c \u3000\u3000\u7ea2\u8863\u5c11\u5973\u7b11\u9053\uff1a\u201c\u6211\u53ea\u7ba1\u4e0a\u9152\u7aef\u83dc\uff0c\u90a3\u91cc\u8fd8\u7ba1\u505a\u83dc\u3002\u201d \u3000\u3000\u201c\u96be\u5f97\u6709\u59d1\u5a18\u8fd9\u4e48\u7f8e\u4e3d\u5927\u65b9\u7684\u5973\u62db\u5f85\uff0c\u5916\u9762\u7684\u5ba2\u4eba\u662f\u5426\u4e5f\u7531\u59d1\u5a18\u4e0a\u9152\u7aef\u83dc\uff1f\u201d \u3000\u3000\u7ea2\u8863\u5c11\u5973\u6487\u6487\u6a31\u5507\u9053\uff1a\u201c\u5916\u9762\u7684\u5ba2\u4eba\u8981\u6211\u4e0a\u9152\u7aef\u83dc\uff0c\u60f3\u90fd\u522b\u60f3\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u90a3\u4e9b\u5ba2\u4eba\u591a\u534a\u4e0d\u8001\u5b9e\uff0c\u4e13\u60f3\u5728\u4eba\u5bb6\u8eab\u4e0a\u5360\u4fbf\u5b9c\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u5916\u9762\u662f\u4ec0\u4e48\u4eba\u8d1f\u8d23\u62db\u5f85\u5462\uff1f\u201d \u3000\u3000\u201c\u6709\u4e24\u4e2a\u5927\u5c0f\u5b50\u3002\u201d \u3000\u3000\u201c\u96be\u9053\u59d1\u5a18\u770b\u51c6\u4e86\u6211\u4eec\u4e24\u4eba\u4e0d\u4f1a\u5360\u4f60\u7684\u4fbf\u5b9c\uff1f\u201d \u3000\u3000\u201c\u6211\u770b\u5f97\u51fa\uff0c\u4f60\u4eec\u90fd\u662f\u6b63\u6b63\u5f53\u5f53\u7684\u4eba\u3002\u201d \u3000\u3000\u4e8e\u662f\uff0c\u767d\u7fbd\u548c\u5c0f\u71d5\u5b50\u5f00\u59cb\u996e\u9152\u5403\u83dc\u3002 \u3000\u3000\u767d\u7fbd\u671b\u4e86\u7ad9\u5728\u4e00\u65c1\u7684\u7ea2\u8863\u5c11\u5973\u4e00\u773c\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u4e5f\u8bf7\u559d\u70b9\u9152\u5403\u4e9b\u83dc\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u7ea2\u8863\u5c11\u5973\u7b11\u7740\u6447\u6447\u5934\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u966a\u9152\u3002\u201d \u3000\u3000\u201c\u59d1\u5a18\u8bef\u4f1a\u4e86\uff0c\u4f60\u4e00\u5b9a\u8fd8\u6ca1\u5403\u996d\uff0c\u5c31\u5728\u8fd9\u91cc\u4e00\u8d77\u5403\u4e0d\u662f\u5f88\u597d\u5417\uff1f\u53cd\u6b63\u4e0d\u7528\u4f60\u51fa\u94b1\uff0c\u7531\u6211 \u4eec\u8bf7\u5ba2\u3002\u201d \u3000\u3000\u201c\u4f60\u4eec\u4e3a\u4ec0\u4e48\u8981\u8bf7\u6211\u7684\u5ba2\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u59d1\u5a18\u5f88\u4eb2\u5207\uff0c\u8ba9\u6211\u4eec\u5927\u6709\u5bbe\u81f3\u5982\u5f52\u7684\u611f\u89c9\uff0c\u51ed\u8fd9\u4e00\u70b9\uff0c\u6211\u4eec\u5c31\u8be5\u8bf7\u4f60\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u6211\u5c31\u559d\u4e00\u676f\u9152\u656c\u4f60\u4eec\u4e24\u4f4d\u3002\u201d \u3000\u3000\u7ea2\u8863\u5c11\u5973\u6b63\u8981\u56de\u53bb\u53d6\u9152\u676f\uff0c\u767d\u7fbd\u5df2\u628a\u81ea\u5df1\u659f\u6ee1\u7684\u4e00\u676f\u9012\u8fc7\u53bb\uff0c\u9053\uff1a\u201c\u5982\u679c\u59d1\u5a18\u4e0d\u5acc\u5f03\uff0c \u94a6\u5728\u4e0b\u8fd9\u4e00\u676f\u5427\uff01\u201d \u3000\u3000\u7ea2\u8863\u5c11\u5973\u679c\u7136\u63a5\u8fc7\u676f\u53bb\uff0c\u4e00\u996e\u800c\u5c3d\u3002 \u3000\u3000\u201c\u59d1\u5a18\u597d\u9152\u91cf\uff01\u201d \u3000\u3000\u201c\u5728\u9152\u5e97\u91cc\u505a\u4e8b\uff0c\u7ec3\u4e5f\u7ec3\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u767d\u7fbd\u63a5\u8fc7\u676f\uff0c\u518d\u659f\u9152\u3002 \u3000\u3000\u4ed6\u4fa7\u8138\u95ee\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u54b1\u4eec\u4e5f\u5404\u81ea\u5e72\u4e00\u676f\uff0c\u56de\u656c\u59d1\u5a18\u3002\u201d \u3000\u3000\u7ea2\u8863\u5c11\u5973\u773c\u770b\u4e24\u4eba\u996e\u5e72\uff0c\u89c6\u7ebf\u76ef\u5728\u5c0f\u71d5\u5b50\u8138\u4e0a\uff0c\u9053\uff1a\u201c\u8fd9\u4f4d\u5ba2\u5b98\u597d\u50cf\u8fdb\u95e8\u540e\u4e00\u53e5\u8bdd\u90fd\u6ca1 \u8bb2\u8fc7\uff0c\u662f\u4e0d\u662f\u4e0d\u60f3\u8ddf\u4eba\u5bb6\u8bf4\u8bdd\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u8038\u4e86\u8038\u80a9\u9053\uff1a\u201c\u4e00\u4eba\u548c\u59d1\u5a18\u8bb2\u8bdd\u8fd8\u4e0d\u591f\u5417\uff1f\u201d \u3000\u3000\u7ea2\u8863\u5c11\u5973\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u4eba\u5bb6\u8fd8\u4ee5\u4e3a\u4f60\u8fd9\u516c\u5b50\u662f\u54d1\u5df4\u5462\uff01\u65e2\u7136\u4f1a\u8bb2\u8bdd\uff0c\u5c31\u4e0d\u8be5\u8001\u4e0d\u5f00 \u53e3\u3002\u201d \u3000\u3000\u201c\u59d1\u5a18\u8bf4\u6211\u8bb2\u4ec0\u4e48\u597d\u5462\uff1f\u201d \u3000\u3000\u201c\u90a3\u5c31\u968f\u4f60\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u60f3\u95ee\u95ee\u8d35\u9152\u5e97\u7684\u5e97\u4e3b\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u8981\u95ee\u8fd9\u4e2a\uff1f\u201d \u3000\u3000\u201c\u6211\u672c\u6765\u4e0d\u60f3\u95ee\uff0c\u504f\u504f\u59d1\u5a18\u903c\u6211\u8bb2\u8bdd\uff0c\u4e5f\u5c31\u53ea\u597d\u968f\u4fbf\u4e00\u95ee\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u60f3\u89c1\u6211\u4eec\u638c\u67dc\u7684\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u5fc5\u8981\u3002\u201d \u3000\u3000\u201c\u90a3\u6211\u5c31\u544a\u8bc9\u5ba2\u5b98\uff0c\u6211\u4eec\u638c\u67dc\u7684\u662f\u4e2a\u5973\u4eba\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c31\u96be\u602a\u4e86\uff0c\u5982\u679c\u4e0d\u662f\u5973\u638c\u67dc\u7684\uff0c\u90a3\u91cc\u6765\u7684\u5973\u62db\u5f85\uff1f\u201d \u3000\u3000\u201c\u90a3\u53ef\u4e0d\u4e00\u5b9a\u3002\u201d \u3000\u3000\u201c\u8c08\u4e86\u534a\u5929\uff0c\u8fd8\u6ca1\u8bf7\u95ee\u59d1\u5a18\u82b3\u540d\uff1f\u201d \u3000\u3000\u201c\u6211\u53eb\u5c0f\u6885\u3002\u201d \u3000\u3000\u201c\u597d\u540d\u5b57\uff0c\u59d1\u5a18\u6b63\u50cf\u4e00\u679d\u76db\u5f00\u7684\u7ea2\u6885\u82b1\u3002\u201d \u3000\u3000\u5c0f\u6885\u5a07\u9765\u4e0a\u6cdb\u8d77\u4e24\u9053\u7ea2\u971e\uff0c\u7f9e\u7b54\u7b54\u7684\u9053\uff1a\u201c\u4eba\u5bb6\u521a\u624d\u8fd8\u4ee5\u4e3a\u516c\u5b50\u4e0d\u4f1a\u8bf4\u8bdd\uff0c\u65e2\u7136\u6709\u8fd9\u4e48 \u597d\u7684\u53e3\u624d\uff0c\u65e9\u5c31\u8be5\u5f00\u53e3\u4e86\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u8fc7\u5956\u8fc7\u5956\u3002\u5982\u679c\u80fd\u5f00\u53e3\u8bf4\u8bdd\u7b97\u6709\u53e3\u624d\uff0c\u5c82\u4e0d\u662f\u6bcf\u4e2a\u4eba\u90fd\u6709\u53e3 \u624d\uff1f\u201d \u3000\u3000\u5c0f\u6885\u53ea\u662f\u63a9\u53e3\u800c\u7b11\uff0c\u5f88\u4e45\u4ee5\u540e\u518d\u9053\uff1a\u201c\u4e8c\u4f4d\u516c\u5b50\u5c31\u8bf7\u81ea\u884c\u7528\u9910\u5427\uff0c\u6211\u4e0d\u966a\u4e86\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u59d1\u5a18\u662f\u5426\u8fd8\u8981\u62db\u5f85\u522b\u7684\u5ba2\u4eba\uff1f\u201d \u3000\u3000\u201c\u4eba\u5bb6\u4e0d\u662f\u8bf4\u8fc7\u4e0d\u8d1f\u8d23\u62db\u5f85\u524d\u9762\u90a3\u4e9b\u666e\u901a\u5ba2\u4eba\u5417\uff1f\u201d \u3000\u3000\u201c\u6211\u662f\u8bf4\u8d35\u9152\u5e97\u8fd9\u6837\u7684\u5f85\u5ba2\u623f\u95f4\uff0c\u662f\u5426\u53e6\u5916\u8fd8\u6709\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u4e0d\u53ea\u8fd9\u4e00\u95f4\uff0c\u4e0d\u8fc7\u522b\u7684\u623f\u95f4\u73b0\u5728\u90fd\u6ca1\u6709\u5ba2\u4eba\u3002\u201d \u3000\u3000\u5c0f\u6885\u8bf4\u5b8c\u8bdd\uff0c\u4e0d\u518d\u7406\u4f1a\u4e24\u4eba\u5f84\u81ea\u79bb\u53bb\u3002 \u3000\u3000\u767d\u7fbd\u548c\u5c0f\u71d5\u5b50\u5f00\u59cb\u4f4e\u58f0\u8c08\u8bdd\u3002 \u3000\u3000\u767d\u7fbd\u9053\uff1a\u201c\u6c5f\u524d\u8f88\u548c\u738b\u5927\u4eba\u5e0c\u671b\u54b1\u4eec\u4e24\u4e2a\u80fd\u4f4f\u5728\u8fd9\u91cc\uff0c\u5982\u679c\u9a6c\u5be1\u5987\u4e0d\u7559\u5ba2\uff0c\u54b1\u4eec\u8be5\u60f3\u4e2a \u529e\u6cd5\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u4e0d\u5047\u601d\u7d22\u9053\uff1a\u201c\u54b1\u4eec\u8fd8\u6ca1\u95ee\uff0c\u600e\u77e5\u5979\u4e0d\u540c\u610f\uff1f\u201d \u3000\u3000\u201c\u597d\u5427\uff01\u90a3\u5c31\u7b49\u95ee\u8fc7\u4ee5\u540e\u518d\u8bf4\u3002\u201d \u3000\u3000\u4e24\u4eba\u521a\u521a\u9152\u996d\u5b8c\u6bd5\uff0c\u5c0f\u6885\u4fbf\u8fdb\u6765\u4e86\u3002 \u3000\u3000\u5979\u4e00\u8fb9\u6536\u62fe\u7897\u76d8\u4e00\u8fb9\u9053\uff1a\u201c\u5982\u679c\u4e24\u4f4d\u4f4f\u5728\u6f7c\u5173\uff0c\u5e0c\u671b\u4e0b\u6b21\u518d\u6765\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u4ed8\u4e86\u5e10\u9053\uff1a\u201c\u6211\u4eec\u5148\u524d\u8bf4\u8fc7\uff0c\u662f\u505a\u751f\u610f\u7684\uff0c\u53ea\u662f\u8def\u8fc7\u6f7c\u5173\u800c\u5df2\uff0c\u4e0d\u8fc7\u8fd9\u6b21\u603b\u8981\u5728 \u6f7c\u5173\u4f4f\u4e0a\u51e0\u5929\u3002\u201d \u3000\u3000\u201c\u6211\u65e9\u5c31\u770b\u51fa\u4e24\u4f4d\u4e0d\u662f\u672c\u5730\u4eba\uff0c\u65e2\u7136\u8981\u5728\u8fd9\u91cc\u4f4f\u51e0\u5929\uff0c\u7167\u6837\u53ef\u4ee5\u518d\u6765\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u6709\u4e2a\u95ee\u9898\uff1f\u201d\u201c \u3000\u3000\u201c\u4ec0\u4e48\u95ee\u9898\uff1f\u201d \u3000\u3000\u201c\u627e\u4e0d\u5230\u4f4f\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u5c0f\u6885\u7728\u7740\u4e00\u5bf9\u95ea\u4eae\u7684\u7738\u5b50\u9053\uff1a\u201c\u6211\u4eec\u6f7c\u5173\u6709\u597d\u51e0\u5bb6\u5ba2\u6808\uff0c\u600e\u4f1a\u627e\u4e0d\u5230\u4f4f\u7684\u5730\u65b9\u5462\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6447\u6447\u5934\u9053\uff1a\u201c\u6211\u4eec\u6240\u6709\u7684\u5ba2\u6808\u90fd\u53bb\u8fc7\uff0c\u90fd\u5ba2\u6ee1\u4e86\u3002\u201d \u3000\u3000\u4e0d\u6885\u53c8\u7728\u52a8\u7740\u7738\u5b50\u9053\uff1a\u201c\u5947\u602a\uff0c\u600e\u4e48\u4f1a\u8fd9\u6837\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u82e6\u7b11\u9053\uff1a\u201c\u6211\u4eec\u90a3\u91cc\u77e5\u9053\u5462\uff1f\u5176\u5b9e\u4e5f\u6ca1\u4ec0\u4e48\uff0c\u6f7c\u5173\u662f\u901a\u5f80\u5c71\u897f\u3001\u6cb3\u5357\u7684\u8981\u9053\uff0c\u5ba2 \u6808\u7ecf\u5e38\u5ba2\u6ee1\u8be5\u662f\u610f\u6599\u7684\u4e8b\u3002\u201d \u3000\u3000\u4ed6\u987f\u4e86\u4e00\u987f\uff0c\u95ee\u9053\uff1a\u201c\u542c\u8bf4\u8d35\u9152\u5e97\u4e5f\u53ef\u4f4f\u5bbf\u5ba2\u4eba\uff0c\u6211\u4eec\u662f\u5426\u53ef\u4ee5\u4f4f\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u5c0f\u6885\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u6211\u4eec\u9152\u9986\u91cc\uff0c\u53ea\u6709\u4e24\u4e09\u4e2a\u623f\u95f4\u53ef\u4ee5\u4f4f\u5bbf\u5ba2\u4eba\u3002\u201d \u3000\u3000\u201c\u90a3\u592a\u597d\u4e86\uff0c\u662f\u5426\u5df2\u6709\u4eba\u4f4f\uff1f\u201d \u3000\u3000\u201c\u597d\u50cf\u8fd8\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u62db\u5f85\u6211\u4eec\u4f4f\u5427\uff01\u4ef7\u94b1\u591a\u5c11\u6211\u4eec\u7edd\u4e0d\u8ba1\u8f83\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\uff0c\u4f60\u4eec\u4e8c\u4f4d\u53ef\u80fd\u6709\u95ee\u9898\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u4e00\u6014\u9053\uff1a\u201c\u6211\u4eec\u54ea\u91cc\u6709\u95ee\u9898\uff1f\u201d \u3000\u3000\u5c0f\u6885\u6b49\u7136\u966a\u7b11\u9053\uff1a\u201c\u516c\u5b50\u522b\u8bef\u4f1a\uff0c\u6211\u8bf4\u7684\u95ee\u9898\u662f\u53e6\u4e00\u56de\u4e8b\u3002\u201d \u3000\u3000\u201c\u54ea\u4e00\u56de\u4e8b\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u4e3b\u4eba\u6709\u4e25\u683c\u89c4\u5b9a\uff0c\u51e1\u662f\u4f4f\u5728\u8fd9\u91cc\u7684\u5ba2\u4eba\uff0c\u5fc5\u987b\u662f\u592b\u59bb\uff0c\u5355\u8eab\u7684\u6982\u4e0d\u62db\u5f85\u3002\u201d \u3000\u3000\u201c\u8fd9\u662f\u4ec0\u4e48\u9053\u7406\uff1f\u201d \u3000\u3000\u201c\u9053\u7406\u5f88\u7b80\u5355\uff0c\u6211\u4eec\u4e3b\u4eba\u662f\u4e2a\u5e74\u8f7b\u5973\u5b50\uff0c\u62db\u5f85\u5ba2\u4eba\u7684\uff0c\u9664\u4e86\u6211\uff0c\u8fd8\u6709\u4e00\u4f4d\u53eb\u5c0f\u5170\u7684\uff0c\u4e5f \u662f\u5341\u4e03\u516b\u5c81\u7684\u5927\u59d1\u5a18\uff0c\u5355\u8eab\u5ba2\u4eba\u4f4f\u8fdb\u6765\uff0c\u5f88\u5bb9\u6613\u51fa\u4e8b\u3002\u201d \u3000\u3000\u201c\u4f60\u770b\u6211\u4eec\u662f\u90a3\u79cd\u4eba\u5417\uff1f\u201d \u3000\u3000\u201c\u4eba\u5fc3\u9694\u809a\u76ae\uff0c\u4e8c\u4f4d\u516c\u5b50\u5916\u8868\u98ce\u5ea6\u7fe9\u7fe9\uff0c\u7684\u786e\u662f\u50cf\u6b63\u4eba\u541b\u5b50\uff0c\u4f46\u5b9e\u9645\u600e\u4e48\u6837\uff1f\u4eba\u5bb6\u53c8\u600e \u80fd\u77e5\u9053\u5462\uff1f\u201d \u3000\u3000\u5ffd\u89c1\u767d\u7fbd\u6307\u7740\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u90a3\u6b63\u597d\uff0c\u6211\u8fd9\u8001\u5f1f\u6709\u8d44\u683c\u4f4f\u8fdb\u6765\u3002\u201d \u3000\u3000\u5c0f\u6885\u671b\u5411\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u83ab\u975e\u8fd9\u4f4d\u516c\u5b50\u5df2\u7ecf\u6210\u8fc7\u4eb2\u4e86\uff1f\u201d \u3000\u3000\u767d\u7fbd\u5fd9\u4ee3\u7b54\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u5df2\u662f\u6709\u59bb\u5b50\u7684\u4eba\u3002\u201d \u3000\u3000\u5c0f\u6885\u671b\u671b\u767d\u7fbd\u9053\uff1a\u201c\u4f60\u8fd9\u4f4d\u516c\u5b50\u5e74\u7eaa\u6bd4\u4ed6\u5927\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u6ca1\u6210\u5bb6\uff1f\u201d \u3000\u3000\u767d\u7fbd\u9053\uff1a\u201c\u6210\u5bb6\u4e0d\u6210\u5bb6\uff0c\u600e\u53ef\u4ee5\u5e74\u9f84\u5927\u5c0f\u533a\u5206\uff0c\u6709\u7684\u7537\u4eba\u5341\u4e8c\u4e09\u5c81\u5c31\u505a\u4e86\u4e08\u592b\uff0c\u6709\u7684\u6d3b \u5230\u4e03\u516b\u5341\uff0c\u7167\u6837\u8fd8\u662f\u6253\u7740\u738b\u8001\u4e94\u7684\u65d7\u53f7\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u6211\u4e5f\u5feb\u4e86\u2026\u2026\u201c \u3000\u3000\u201c\u5feb\u4e86\uff1f\u662f\u4e0d\u662f\u4e5f\u5feb\u8981\u6210\u4eb2\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u6211\u7684\u5cb3\u5bb6\u5728\u957f\u5b89\uff0c\u5a5a\u671f\u5df2\u7ecf\u4e0d\u8fdc\uff0c\u4e0b\u6b21\u518d\u6765\u6f7c\u5173\u65f6\uff0c\u8bf4\u4e0d\u5b9a\u6211\u4f1a\u628a\u5185\u4eba\u5e26\u6765\uff0c \u4e5f\u4f4f\u5728\u8d35\u9152\u5e97\u91cc\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u5982\u6b64\uff0c\u4f60\u4eec\u5df2\u7ecf\u6709\u4e00\u4f4d\u591f\u8d44\u683c\u4f4f\u5728\u8fd9\u91cc\u4e86\uff0c\u516c\u5b50\u4e3a\u4ec0\u4e48\u4e0d\u628a\u5c0a\u592b\u4eba\u5e26\u6765\u4e00\u8d77\u7528\u9910 \u5462\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u53ea\u5f97\u987a\u53e3\u9053\uff1a\u201c\u5979\u5728\u8def\u4e0a\u53bb\u63a2\u671b\u4e00\u4f4d\u4eb2\u621a\uff0c\u6211\u548c\u5979\u5df2\u7ecf\u7ea6\u597d\u4f1a\u9762\u7684\u5730\u65b9\uff0c\u8981\u5929\u9ed1 \u540e\u624d\u80fd\u5230\u3002\u201d \u3000\u3000\u5c0f\u6885\u70b9\u70b9\u5934\u9053\uff1a\u201c\u597d\uff0c\u7b49\u5c0a\u592b\u4eba\u5230\u4e86\uff0c\u4f60\u5c31\u628a\u5979\u9886\u5230\u8fd9\u91cc\u6765\u5427\uff01 \u3000\u3000\u4e0d\u8fc7\u8fd9\u53ea\u662f\u6211\u7b54\u5e94\u4f60\uff0c\u8fd8\u4e0d\u80fd\u5b8c\u5168\u7b97\u6570\u3002\u201c \u3000\u3000\u201c\u600e\u6837\u624d\u80fd\u5b8c\u5168\u7b97\u6570\uff1f\u201d \u3000\u3000\u201c\u5fc5\u987b\u53bb\u8bf7\u793a\u4e00\u4e0b\u6211\u4eec\u4e3b\u4eba\uff0c\u5979\u80af\u7b54\u5e94\u624d\u6210\u3002\u201d \u3000\u3000\u201c\u59d1\u5a18\u5c31\u5feb\u53bb\u8bf7\u793a\u5427\uff01\u201d \u3000\u3000\u5c0f\u6885\u8d70\u540e\uff0c\u5c0f\u71d5\u5b50\u95ee\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u9a97\u8bf4\u6211\u6709\u59bb\u5ba4\uff1f\u201d \u3000\u3000\u767d\u7fbd\u53cd\u95ee\u9053\uff1a\u201c\u54b1\u4eec\u6765\u7684\u76ee\u7684\u662f\u4ec0\u4e48\uff1f\u5982\u679c\u4e0d\u8bbe\u6cd5\u4f4f\u8fdb\u6765\uff0c\u53c8\u5982\u4f55\u80fd\u5f97\u77e5\u90a3\u4e09\u4e2a\u5973\u7684\u662f \u5426\u5df2\u5230\u6f7c\u5173\uff1f\u201d \u3000\u3000\u201c\u53ef\u662f\u4f60\u80fd\u66ff\u6211\u627e\u4e2a\u8001\u5a46\u5417\uff1f\u201d \u3000\u3000\u201c\u4e0d\u6562\u8bf4\u7edd\u5bf9\u80fd\uff0c\u4f46\u5374\u603b\u6709\u51e0\u5206\u628a\u63e1\u3002\u201d \u3000\u3000\u201c\u83ab\u975e\u4f60\u60f3\u5230\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u731c\u5230\u4e86\uff0c\u601c\u82b1\u5c31\u53ef\u4ee5\u4e34\u65f6\u5145\u5f53\u4e00\u4e0b\u4f60\u7684\u592b\u4eba\u3002\u201d \u3000\u3000\u201c\u5c82\u6709\u6b64\u7406\uff0c\u601c\u82b1\u662f\u4e09\u516c\u4e3b\u8eab\u8fb9\u7684\u4eba\uff0c\u5979\u600e\u80af\u7b54\u5e94\u8fd9\u4e48\u505a\uff1f\u201d \u3000\u3000\u201c\u53ea\u8981\u4e09\u516c\u4e3b\u7b54\u5e94\u4e86\uff0c\u5979\u6709\u4ec0\u4e48\u4e0d\u7b54\u5e94\u7684\uff1f\u201d \u3000\u3000\u201c\u4e09\u516c\u4e3b\u6839\u672c\u4e0d\u53ef\u80fd\u7b54\u5e94\u3002\u201d \u3000\u3000\u201c\u4f60\u9519\u4e86\uff0c\u902e\u4f4f\u90a3\u4e09\u4e2a\u5973\u4eba\uff0c\u4fbf\u53ef\u4ee5\u5f7b\u5e95\u6cbb\u597d\u4e09\u516c\u4e3b\u7684\u75c5\uff0c\u8fd9\u4e48\u91cd\u8981\u7684\u5927\u4e8b\uff0c\u4e09\u516c\u4e3b\u5f53 \u7136\u8981\u4e3a\u81ea\u5df1\u7740\u60f3\uff0c\u66f4\u4f55\u51b5\u601c\u82b1\u548c\u4f60\u53ea\u662f\u505a\u4e00\u573a\u5047\u620f\u800c\u5df2\uff0c\u96be\u9053\u591c\u665a\u4e24\u4eba\u7761\u5728\u4e00\u8d77\uff0c\u4f60\u8fd8\u4f1a\u6b3a \u8d1f\u5979\u4e0d\u6210\uff1f\u201d \u3000\u3000\u201c\u65e2\u7136\u5982\u6b64\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u81ea\u5df1\u627f\u8ba4\u6709\u59bb\u5ba4\uff0c\u5374\u504f\u504f\u8981\u5f80\u6211\u8eab\u4e0a\u63a8\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u662f\u51fa\u5bb6\u4eba\uff0c\u51fa\u5bb6\u4eba\u90a3\u6709\u8ba8\u8001\u5a46\u7684\u9053\u7406\u3002\u201d \u3000\u3000\u201c\u53bb\u4f60\u7684\uff0c\u4f60\u65e9\u5df2\u4e0d\u662f\u51fa\u5bb6\u4eba\uff0c\u4e0d\u8bba\u7a7f\u6234\u6216\u996e\u98df\uff0c\u4f60\u90a3\u4e00\u70b9\u50cf\u51fa\u5bb6\u4eba\uff1f\u53c8\u6709\u90a3\u4e00\u4e2a\u770b\u5f97 \u51fa\u4f60\u662f\u51fa\u5bb6\u4eba\uff1f\u201d \u3000\u3000\u6b63\u8bf4\u5230\u8fd9\u91cc\uff0c\u5c0f\u6885\u7b11\u563b\u563b\u7684\u8fdb\u6765\u4e86\u3002 \u3000\u3000\u767d\u7fbd\u95ee\u9053\uff1a\u201c\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u5c0f\u6885\u9053\uff1a\u201c\u6211\u4eec\u4e3b\u4eba\u51c6\u4e86\uff0c\u4e0d\u4f46\u51c6\u4e86\u4ed6\uff0c\u8fde\u4f60\u4e5f\u51c6\u4e86\u3002\u201d \u3000\u3000\u767d\u7fbd\u559c\u51fa\u671b\u5916\uff0c\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u53ef\u662f\u6211\u6ca1\u6cd5\u5e26\u4e2a\u8001\u5a46\u6765\uff01\u201d \u3000\u3000\u5c0f\u6885\u7b11\u9053\uff1a\u201c\u53ea\u4f60\u4eec\u5176\u4e2d\u6709\u4e00\u4f4d\u628a\u592b\u4eba\u5e26\u6765\u5c31\u6210\u4e86\u3002\u201d \u3000\u3000\u201c\u8d35\u4e3b\u4eba\u4e3a\u4ec0\u4e48\u5bf9\u6211\u7279\u522b\u4f18\u5f85\uff1f\u201d \u3000\u3000\u201c\u7406\u7531\u5f88\u7b80\u5355\uff0c\u4f60\u4eec\u4e24\u4f4d\u516c\u5b50\u662f\u597d\u670b\u53cb\uff0c\u6709\u4e00\u4f4d\u5e26\u4e86\u592b\u4eba\u6765\uff0c\u53e6\u4e00\u4f4d\u5fc5\u5b9a\u4e5f\u89c4\u89c4\u77e9\u77e9\u7684 \u4e0d\u6562\u4e71\u6765\uff0c\u65e2\u7136\u6709\u73b0\u6210\u7684\u623f\u95f4\uff0c\u6211\u4eec\u4e3b\u4eba\u5f53\u7136\u8981\u505a\u505a\u597d\u4e8b\u3002\u201d \u3000\u3000\u201c\u90a3\u592a\u597d\u4e86\uff0c\u6211\u4eec\u73b0\u5728\u5c31\u53bb\u628a\u8fd9\u4f4d\u8001\u5f1f\u7684\u592b\u4eba\u5e26\u6765\u3002\u201d \u3000\u3000\u201c\u4e24\u4f4d\u4ec0\u4e48\u65f6\u95f4\u56de\u6765\uff1f\u201d \u3000\u3000\u201c\u53ea\u8981\u6211\u8fd9\u8001\u5f1f\u7684\u592b\u4eba\u4e00\u5230\uff0c\u6211\u4eec\u9a6c\u4e0a\u5c31\u6765\uff0c\u5982\u679c\u8fd8\u6ca1\u5230\uff0c\u90a3\u5c31\u8981\u803d\u8bef\u4e00\u4e9b\u65f6\u95f4\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u65f6\u95f4\u4e0d\u8981\u8d85\u8fc7\u4e8c\u66f4\uff0c\u82e5\u8fc7\u4e86\u65f6\u95f4\uff0c\u6211\u4eec\u4e00\u5173\u95e8\uff0c\u4fbf\u4ec0\u4e48\u5ba2\u4eba\u4e5f\u4e0d\u63a5\u5f85\u4e86\u3002\u201d \u3000\u3000\u767d\u7fbd\u548c\u5c0f\u71d5\u5b50\u56de\u5230\u5ba2\u6808\uff0c\u5f53\u5373\u628a\u7ecf\u8fc7\u5411\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u7980\u62a5\u3002 \u3000\u3000\u738b\u5f64\u7acb\u523b\u8d77\u8eab\u9053\uff1a\u201c\u770b\u6765\u4e5f\u53ea\u597d\u5982\u6b64\u4e86\uff0c\u6211\u73b0\u5728\u5c31\u53bb\u9762\u89c1\u4e09\u516c\u4e3b\uff0c\u8981\u6c42\u5979\u7b54\u5e94\u601c\u82b1\u53c2\u4e0e \u884c\u52a8\u3002\u201d \u3000\u3000\u4e0d\u8db3\u76cf\u8336\u5de5\u592b\uff0c\u4fbf\u89c1\u738b\u5f64\u56de\u5230\u5ba2\u5385\uff0c\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u540c\u610f\u4e86\uff0c\u601c\u82b1\u9a6c\u4e0a\u5c31\u51fa\u6765\u3002\u201d \u3000\u3000\u8c01\u77e5\u76f4\u7b49\u4e86\u987f\u996d\u5de5\u592b\uff0c\u624d\u89c1\u601c\u82b1\u83b2\u6b65\u59d7\u59d7\u7684\u6765\u5230\u5ba2\u5385\u3002 \u3000\u3000\u6b64\u523b\u7684\u601c\u82b1\uff0c\u8fd8\u771f\u50cf\u65b0\u5ac1\u5a18\u4e00\u6837\uff0c\u663e\u5f97\u7f9e\u7f9e\u7b54\u7b54\uff0c\u5c24\u5176\u5f53\u77a5\u89c1\u4e86\u5c0f\u71d5\u5b50\u7684\u65f6\u5019\uff0c\u66f4\u662f\u53cc \u988a\u6cdb\u7ea2\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u4e5f\u662f\u4e0d\u592a\u81ea\u7136\u7684\u611f\u89c9\u3002 \u3000\u3000\u738b\u5f64\u95ee\u9053\uff1a\u201c\u59d1\u5a18\u600e\u4e48\u8fd9\u4e48\u4e45\u624d\u51fa\u6765\uff1f\u201d \u3000\u3000\u601c\u82b1\u4f4e\u5782\u7740\u7c89\u9888\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u8bf4\uff0c\u5973\u5b69\u513f\u5bb6\u8fc7\u4e86\u95e8\uff0c\u8fde\u53d1\u5f0f\u548c\u7a7f\u6234\u90fd\u4e0d\u4e00\u6837\uff0c\u4e3a\u4e86\u88c5\u5f97 \u50cf\uff0c\u5f53\u7136\u8981\u803d\u8bef\u4e00\u4e9b\u65f6\u95f4\u3002\u201d \u3000\u3000\u738b\u5f64\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u5fc5\u5bb3\u7f9e\uff0c\u53ea\u662f\u505a\u4e00\u573a\u5047\u620f\u800c\u5df2\uff0c\u5c0f\u71d5\u5b50\u662f\u4e2a\u89c4\u89c4\u77e9\u77e9\u7684\u5e74\u8f7b\u4eba\uff0c\u5bf9 \u4ed6\u7528\u4e0d\u7740\u6709\u987e\u5fcc\uff0c\u4e8b\u6210\u4e4b\u540e\uff0c\u4e5f\u662f\u4f60\u7684\u4e00\u573a\u5927\u529f\uff0c\u5feb\u4e9b\u8ddf\u4ed6\u4eec\u4e24\u4eba\u4e00\u8d77\u53bb\u5427\uff01\u82e5\u6709\u7d27\u6025\u4e8b \u60c5\uff0c\u968f\u65f6\u628a\u6d88\u606f\u4f20\u56de\u6765\u3002\u201d \u3000\u3000\u79bb\u5f00\u5ba2\u6808\uff0c\u601c\u82b1\u51b7\u58f0\u95ee\u767d\u7fbd\u9053\uff1a\u201c\u5c0f\u9053\u58eb\uff0c\u90fd\u662f\u4f60\u51fa\u7684\u597d\u4e3b\u610f\uff0c\u7adf\u7136\u8fde\u6211\u4e5f\u7ed9\u626f\u4e86\u8fdb \u53bb\uff01\u201d \u3000\u3000\u767d\u7fbd\u966a\u7740\u7b11\u9053\uff1a\u201c\u59d1\u5976\u5976\uff0c\u8fd9\u662f\u8feb\u4e0d\u5f97\u5df2\uff0c\u4f60\u8981\u6211\u4eec\u5230\u54ea\u91cc\u53bb\u627e\u4e00\u4e2a\u65b0\u5a18\u5b50\u6765\uff1f\u96be\u9053\u8fd8 \u8981\u903c\u7740\u4e09\u516c\u4e3b\u6765\u4e0d\u6210\uff1f\u201d \u3000\u3000\u601c\u82b1\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u51fa\u5bb6\u4eba\u51fa\u4e86\u8fd9\u79cd\u4e3b\u610f\uff0c\u96be\u602a\u4f60\u4f1a\u88ab\u6b66\u5f53\u8d76\u51fa\u95e8\u53bbI\u201d \u3000\u3000\u767d\u7fbd\u4f9d\u7136\u7b11\u9053\uff1a\u201c\u5c0f\u71d5\u5b50\u90a3\u4e00\u70b9\u4e0d\u597d\uff1f\u4f60\u5982\u679c\u8fde\u4ed6\u90fd\u5acc\uff0c\u5c06\u6765\u4e00\u5b9a\u5ac1\u4e0d\u51fa\u53bb\u3002\u201d \u3000\u3000\u601c\u82b1\u671b\u671b\u5c0f\u71d5\u5b50\uff0c\u518d\u77aa\u767d\u7fbd\u4e00\u773c\uff0c\u4e0d\u518d\u5f00\u53e3\u4e86\u3002 \u3000\u3000\u5f88\u5feb\u4fbf\u5230\u8fbe\u201c\u6843\u6797\u5c45\u201d\u95e8\u53e3\u3002 \u3000\u3000\u8fd9\u65f6\u65e9\u5df2\u4e00\u66f4\u8fc7\u540e\uff0c\u5e97\u95e8\u4e5f\u5df2\u5173\u4e86\u3002 \u3000\u3000\u4e09\u4eba\u6765\u5230\u4fa7\u95e8\uff0c\u767d\u7fbd\u4e0a\u524d\u6572\u95e8\u3002 \u3000\u3000\u5e94\u95e8\u7684\u4ecd\u662f\u5c0f\u6885\u3002 \u3000\u3000\u5c0f\u6885\u671b\u4e86\u4e09\u4eba\u4e00\u773c\uff0c\u7136\u540e\u8f6c\u5411\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u516c\u5b50\u771f\u662f\u597d\u798f\u6c14\uff0c\u5a36\u4e86\u8fd9\u4e48\u4e00\u4f4d\u6f02\u4eae\u7684\u65b0\u5a18 \u5b50\uff01\u201d \u3000\u3000\u767d\u7fbd\u62a2\u7740\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u4e5f\u5f88\u7f8e\uff1f\u201d \u3000\u3000\u5c0f\u6885\u80c0\u7ea2\u7740\u8138\u9053\uff1a\u201c\u7f8e\u6709\u4ec0\u4e48\u7528\uff1f\u5230\u73b0\u5728\u8fd8\u6ca1\u5a46\u5bb6\uff01\u201d \u3000\u3000\u201c\u8981\u4e0d\u8981\u6211\u66ff\u4f60\u4f5c\u5a92\uff1f\u201d \u3000\u3000\u201c\u4ee5\u540e\u4f60\u82e5\u8fd8\u4f1a\u518d\u6765\uff0c\u5230\u65f6\u5019\u518d\u8bf4\u5427\uff01\u201d \u3000\u3000\u5c0f\u6885\u9886\u7740\u4e09\u4eba\u7a7f\u8fc7\u897f\u5802\u5c4b\uff0c\u7136\u540e\u8fdb\u5165\u4e00\u5904\u540e\u9662\uff0c\u518d\u6307\u6307\u4e24\u53a2\uff0c\u9053\uff1a\u201c\u8fd9\u4e24\u8fb9\u53a2\u623f\u90fd\u662f\u62db \u5f85\u5ba2\u4eba\u7684\u623f\u95f4\uff0c\u5c31\u8bf7\u5355\u8eab\u7684\u4f4f\u4e1c\u53a2\uff0c\u6709\u592b\u4eba\u7684\u4f4f\u897f\u53a2\u5427\uff1f\u201d \u3000\u3000\u767d\u7fbd\u642d\u8baa\u7740\u95ee\u9053\uff1a\u201c\u8d35\u9152\u5e97\u7684\u4eba\u90fd\u4f4f\u5728\u54ea\u91cc\u5462\uff1f\u201d \u3000\u3000\u201c\u9664\u4e86\u6b63\u5c4b\uff0c\u540e\u9762\u8fd8\u6709\u4e2a\u8de8\u9662\uff0c\u90fd\u662f\u6211\u4eec\u81ea\u5df1\u4eba\u4f4f\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u6211\u4eec\u548c\u8d35\u9152\u5e97\u7684\u4eba\uff0c\u4f4f\u7684\u5c31\u50cf\u4e00\u5bb6\u4eba\u4e86\u3002\u201d \u3000\u3000\u201c\u6b63\u56e0\u5982\u6b64\uff0c\u6240\u4ee5\u5ba2\u4eba\u624d\u5fc5\u987b\u5e26\u5bb6\u7737\uff0c\u800c\u4e14\u4e5f\u4e0d\u62db\u5f85\u964c\u751f\u4eba\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u4e0d\u4e5f\u662f\u964c\u751f\u4eba\u5417\uff1f\u201d \u3000\u3000\u201c\u4f60\u4eec\u4e24\u4f4d\u5df2\u5728\u8fd9\u91cc\u5403\u8fc7\u996d\uff0c\u600e\u80fd\u8fd8\u7b97\u964c\u751f\uff1f\u201d \u3000\u3000\u5c0f\u6885\u5148\u628a\u5c0f\u71d5\u5b50\u548c\u601c\u82b1\u5e26\u8fdb\u897f\u53a2\uff0c\u518d\u628a\u767d\u7fbd\u5e26\u8fdb\u4e1c\u53a2\uff0c\u7136\u540e\u81ea\u884c\u79bb\u53bb\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u5230\u73b0\u5728\u624d\u77e5\u9053\uff0c\u539f\u6765\u8fd9\u5904\u201c\u6843\u6797\u5c45\u201d\u867d\u7136\u95e8\u9762\u4e0d\u5927\uff0c\u4f46\u540e\u9762\u7684\u623f\u820d\u5374\u4e0d \u5c11\uff0c\u5f88\u663e\u7136\u7684\uff0c\u540e\u9762\u8fd9\u4e9b\u623f\u820d\u7528\u9014\u5927\u4e0d\u7b80\u5355\u3002 \u3000\u3000\u672c\u6765\uff0c\u82e5\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u7684\u623f\u95f4\u8fde\u5728\u4e00\u8d77\uff0c\u4ed6\u4eec\u5c31\u5c3d\u53ef\u5408\u4f4f\u4e00\u623f\uff0c\u53e6\u5916\u4e00\u623f\u8ba9\u7ed9\u601c\u82b1\uff0c\u4f46 \u5982\u6b64\u4e00\u6765\uff0c\u53cc\u65b9\u975e\u5206\u5f00\u4e0d\u53ef\u4e86\u3002 \u3000\u3000\u5367\u623f\u91cc\u5e03\u7f6e\u5f97\u5341\u5206\u96c5\u81f4\uff0c\u4e00\u5f20\u53cc\u4eba\u5e8a\uff0c\u7f57\u5e10\u9ad8\u60ac\uff0c\u53e6\u5916\u4e5f\u684c\u6905\u4ff1\u5168\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u786c\u7740\u5934\u76ae\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u54b1\u4eec\u600e\u4e48\u7761\u6cd5\uff1f\u201d \u3000\u3000\u601c\u82b1\u53cc\u988a\u6cdb\u7740\u7ea2\u6655\u9053\uff1a\u201c\u968f\u4f60\uff0c\u6211\u542c\u4f60\u7684\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u4f60\u7761\u5e8a\u4e0a\uff0c\u6211\u7761\u5e8a\u4e0b\u3002\u201d \u3000\u3000\u201c\u90a3\u4e0d\u592a\u59d4\u5c48\u4f60\u4e86\uff0c\u800c\u4e14\u88ab\u8925\u4e5f\u65e0\u6cd5\u5206\u914d\u3002\u201d \u3000\u3000\u201c\u6211\u4ec0\u4e48\u4e5f\u4e0d\u8981\uff0c\u8eba\u5728\u5730\u4e0a\u5c31\u884c\u3002\u201d \u3000\u3000\u201c\u90a3\u4e0d\u6210\uff01\u201d \u3000\u3000\u201c\u6709\u4ec0\u4e48\u4e0d\u6210\u7684\uff0c\u6211\u4eca\u665a\u672c\u6765\u5c31\u4e0d\u80fd\u7761\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u7b49\u591c\u6df1\u4eba\u9759\u540e\uff0c\u5fc5\u987b\u67e5\u770b\u6709\u4f55\u52a8\u9759\uff0c\u82e5\u53ea\u662f\u672a\u7761\u89c9\uff0c\u53c8\u4f55\u5fc5\u4f4f\u5728\u8fd9\u91cc\u5462\uff1f\u201d \u3000\u3000\u201c\u90a3\u4e48\u6211\u4e5f\u4e0d\u7761\u4e86\uff0c\u966a\u4f60\u804a\u804a\u5929\uff0c\u4e00\u591c\u5c31\u8fc7\u53bb\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u53ea\u7ba1\u7761\u4f60\u7684\uff0c\u82e5\u54b1\u4eec\u4e00\u76f4\u804a\u5929\uff0c\u4e07\u4e00\u88ab\u4ed6\u4eec\u542c\u5230\uff0c\u4ee5\u540e\u7684\u4e8b\u5c31\u4e0d\u597d\u529e\u4e86\u3002\u201d \u3000\u3000\u201c\u5979\u4eec\u54ea\u6709\u90a3\u4e48\u591a\u95f2\u5de5\u592b\u542c\u522b\u4eba\u804a\u5929\uff1f\u201d \u3000\u3000\u201c\u539f\u6765\u4f60\u8fd8\u6ca1\u6709\u8fdb\u4eba\u72b6\u6001\uff0c\u4f60\u8be5\u77e5\u9053\u8fd9\u662f\u4e2a\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u5ffd\u542c\u95e8\u5916\u4f20\u6765\u811a\u6b65\u58f0\uff0c\u63a5\u7740\u662f\u5c0f\u6885\u7684\u58f0\u97f3\u9053\uff1a\u201c\u516c\u5b50\u5f00\u95e8\uff0c\u9001\u8336\u6765\u4e86\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6253\u5f00\u95e8\u9053\uff1a\u201c\u59d1\u5a18\u8f9b\u82e6\u4e86\uff0c\u8fd9\u4e48\u665a\u8fd8\u5fd9\u7740\u505a\u4e8b\uff01\u201d \u3000\u3000\u201c\u8fd9\u662f\u6211\u4eec\u4efd\u5185\u7684\u5de5\u4f5c\uff0c\u5f53\u7136\u8981\u505a\u3002\u201d \u3000\u3000\u5c0f\u6885\u628a\u8336\u58f6\u653e\u4e0b\uff0c\u671b\u4e86\u5ba4\u5185\u4e00\u773c\u9053\uff1a\u201c\u4f60\u4eec\u5c0f\u4fe9\u53e3\u600e\u4e48\u8fd8\u6ca1\u4e0a\u5e8a\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u7b11\u9053\uff1a\u201c\u6211\u4eec\u77e5\u9053\u59d1\u5a18\u4f1a\u6765\u9001\u8336\uff0c\u90a3\u80fd\u73b0\u5728\u5c31\u7761\uff1f\u201d \u3000\u3000\u5c0f\u6885\u9053\u4e86\u4e00\u58f0\u665a\u5b89\uff0c\u8f6c\u8eab\u5c31\u8981\u8d70\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u5fd9\u9053\uff1a\u201c\u59d1\u5a18\u4f4f\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u5c0f\u6885\u7728\u7740\u7738\u5b50\u9053\uff1a\u201c\u516c\u5b50\u95ee\u8fd9\u4e9b\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4e07\u4e00\u6709\u4ec0\u4e48\u4e8b\uff0c\u6211\u4eec\u4e5f\u597d\u53bb\u62db\u547c\u59d1\u5a18\u3002\u201d \u3000\u3000\u201c\u591c\u665a\u54ea\u8fd8\u4f1a\u6709\u4ec0\u4e48\u4e8b\u5462\uff1f\u5982\u662f\u516c\u5b50\u60f3\u5230\u4e86\u4ec0\u4e48\uff0c\u5c3d\u7ba1\u73b0\u5728\u5429\u5490\uff01\u201d \u3000\u3000\u201c\u6ca1\u4e8b\u4e86\uff01\u201d \u3000\u3000\u5c0f\u6885\u8d70\u540e\uff0c\u5c0f\u71d5\u5b50\u5e76\u672a\u5173\u95e8\uff0c\u5374\u4e00\u5f84\u6765\u5230\u4e1c\u53a2\u3002 \u3000\u3000\u767d\u7fbd\u8fde\u5fd9\u5f00\u95e8\u8fce\u8fdb\u53bb\u9053\uff1a\u201c\u6709\u4e8b\u5417\uff1f\u201d \u3000\u3000\u201c\u4f60\u4e0d\u89c9\u5f97\u5947\u602a\u5417\uff1f\u6b63\u5c4b\u91cc\u9759\u6084\u6084\u7684\uff0c\u4e5f\u672a\u89c1\u70b9\u706f\uff0c\u5979\u4eec\u7684\u4eba\u90fd\u5230\u54ea\u91cc\u53bb\u4e86\u5462\uff1f\u201d \u3000\u3000\u201c\u53ef\u80fd\u56e0\u4e3a\u54b1\u4eec\u6765\u5f97\u665a\uff0c\u5979\u4eec\u90fd\u7761\u7740\u4e86\uff01\u201d \u3000\u3000\u201c\u4e8b\u60c5\u4e0d\u4f1a\u8fd9\u4e48\u5355\u7eaf\uff0c\u9884\u6599\u4e2d\u9a6c\u5be1\u5987\u7684\u624b\u4e0b\u4eba\u53ef\u80fd\u4e0d\u5c11\uff0c\u4f46\u73b0\u5728\u5374\u53ea\u770b\u5230\u5c0f\u6885\u4e00\u4eba\uff0c\u4e0d \u80fd\u4e0d\u4ee4\u4eba\u6000\u7591\u8fd9\u91cc\u4eca\u665a\u6709\u4e8b\u3002\u201d \u3000\u3000\u201c\u4f60\u8ba4\u4e3a\u4f1a\u6709\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u5f88\u53ef\u80fd\u6842\u98d8\u9999\u7b49\u4e09\u540d\u5996\u5973\u5df2\u7ecf\u5230\u4e86\uff0c\u9a6c\u5be1\u5987\u5df2\u5728\u522b\u5904\u548c\u5979\u4eec\u805a\u4f1a\uff0c\u679c\u771f\u5982\u6b64\uff0c\u54b1\u4eec\u5c31 \u7b49\u4e8e\u767d\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u7167\u4f60\u8fd9\u4e48\u8bf4\uff0c\u96be\u9053\u54b1\u4eec\u7684\u8eab\u4efd\u5df2\u88ab\u8bc6\u7834\uff1f\u201d \u3000\u3000\u201c\u5f88\u96be\u8bf4\uff0c\u4f60\u8be5\u6599\u5f97\u51fa\u9a6c\u5be1\u5987\u7edd\u5bf9\u4e0d\u662f\u4e00\u4e2a\u7b80\u5355\u4eba\u7269\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u5426\u51c6\u5907\u4eca\u665a\u91c7\u53d6\u4ec0\u4e48\u884c\u52a8\uff1f\u201d \u3000\u3000\u201c\u4e09\u66f4\u4ee5\u540e\uff0c\u4e5f\u8bb8\u6211\u4f1a\u6709\u884c\u52a8\uff0c\u4e0d\u8fc7\uff0c\u4f60\u6700\u597d\u6309\u5175\u4e0d\u52a8\uff0c\u82e5\u4e24\u4e2a\u4eba\u5168\u6709\u884c\u52a8\uff0c\u52bf\u5fc5\u6253\u8349 \u60ca\u86c7\uff0c\u53cd\u800c\u5f04\u5de7\u6210\u62d9\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u90a3\u5c31\u7531\u4f60\u4e00\u4eba\u8f9b\u82e6\uff0c\u6211\u51b3\u5b9a\u597d\u597d\u7761\u4e00\u89c9\u3002\u201d \u3000\u3000\u4e09\u66f4\u5de6\u53f3\uff0c\u601c\u82b1\u5df2\u7761\uff0c\u5c0f\u71d5\u5b50\u4e00\u4e2a\u4eba\u6084\u6084\u6e9c\u51fa\u897f\u53a2\u3002 \u3000\u3000\u4ed6\u5148\u5728\u6b63\u5c4b\u5404\u4e2a\u623f\u95f4\u7a97\u5916\u5411\u91cc\u7a83\u542c\uff0c\u8fde\u9f3b\u606f\u58f0\u4e5f\u6beb\u65e0\u542c\u95fb\uff0c\u663e\u7136\u6b63\u5c4b\u6240\u6709\u623f\u95f4\u90fd\u662f\u7a7a\u65e0 \u4e00\u4eba\u3002 \u3000\u3000\u63a5\u7740\uff0c\u4ed6\u65e0\u58f0\u65e0\u606f\u7684\u7eb5\u8eab\u8dc3\u4e0a\u5c4b\u9876\u3002 \u3000\u3000\u679c\u7136\u53d1\u73b0\u5728\u5c4b\u540e\u8fd8\u6709\u4e00\u5904\u8de8\u9662\u3002 \u3000\u3000\u8de8\u9662\u5185\u5404\u623f\u95f4\u4e5f\u65e0\u706f\u5149\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u5e76\u672a\u611f\u5230\u610f\u5916\uff0c\u56e0\u4e3a\u6df1\u591c\u95f4\u7184\u706f\u7761\u89c9\u662f\u5fc5\u7136\u7684\u4e8b\u3002 \u3000\u3000\u4ed6\u5728\u5c4b\u9876\u7a0d\u505a\u505c\u7559\uff0c\u4fbf\u98d8\u8eab\u8dc3\u843d\u5929\u4e95\uff0c\u7136\u540e\u4e5f\u9010\u7a97\u542c\u53bb\u3002 \u3000\u3000\u7adf\u7136\u548c\u540e\u9662\u6b63\u5c4b\u4e00\u6837\uff0c\u4e5f\u4e0d\u95fb\u534a\u70b9\u58f0\u606f\u3002 \u3000\u3000\u96be\u9053\u6240\u6709\u7684\u4eba\u90fd\u4e0d\u5728\uff1f\u90a3\u4e48\u5c0f\u6885\u53c8\u7761\u5728\u90a3\u91cc\u5462\uff1f\u4ed6\u4e0d\u4fbf\u505c\u7559\u8fc7\u4e45\uff0c\u53ea\u597d\u5148\u56de\u5230\u897f\u53a2\u518d \u8bf4\u3002 \u3000\u3000\u5173\u4e0a\u95e8\uff0c\u7531\u4e8e\u4ed6\u65e9\u5df2\u628a\u706f\u7184\u6389\uff0c\u5e8a\u4e0a\u7684\u601c\u82b1\u4f3c\u4e4e\u7761\u5f97\u6b63\u9163\uff0c\u53ea\u597d\u5c31\u5728\u5730\u4e0a\u8eba\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u521a\u521a\u6709\u4e86\u6726\u80e7\u6b32\u7761\u611f\u89c9\uff0c\u5ffd\u542c\u7a97\u5916\u5929\u4e95\u4e2d\u4f3c\u4e4e\u6709\u8f7b\u5fae\u7684\u811a\u6b65\u58f0\uff01\u4ed6\u7acb\u5373\u6084\u6084\u800c\u53c8\u8fc5\u901f\u7684 \u722c\u8d77\u6765\uff0c\u8e51\u624b\u8e51\u811a\u7684\u6765\u5230\u7a97\u524d\u5411\u5916\u671b\u53bb\u3002 \u3000\u3000\u6708\u8272\u4e0b\uff0c\u53ea\u89c1\u5929\u4e95\u4e2d\u7ad9\u7740\u4e00\u4f4d\u957f\u88d9\u66f3\u5730\u79c0\u53d1\u5782\u80a9\u7684\u5973\u4eba\u8eab\u5f71\u3002 \u3000\u3000\u867d\u7136\u8fd9\u5973\u4eba\u662f\u80cc\u5411\u897f\u53a2\u800c\u7acb\uff0c\u5c0f\u71d5\u5b50\u53ea\u80fd\u770b\u5230\u5979\u7684\u80cc\u5f71\uff0c\u4f46\u8fd9\u80cc\u5f71\u5374\u9887\u5bcc\u8bf1\u60d1\u529b\uff0c\u4e0d\u96be \u731c\u51fa\u5fc5\u662f\u4f4d\u7edd\u8272\u7f8e\u5973\u3002 \u3000\u3000\u8fd9\u5973\u4eba\u7d20\u8863\u7d20\u88d9\uff0c\u83ab\u975e\u5979\u5c31\u662f\u9a6c\u5be1\u5987\uff1f\u5ffd\u7136\uff0c\u5c0f\u71d5\u5b50\u5fc3\u5934\u4e00\u52a8\uff0c\u53ea\u89c9\u8fd9\u8eab\u5f71\u4f3c\u66fe\u76f8\u8bc6\uff0c \u4f46\u4e00\u65f6\u4e4b\u95f4\u5374\u53c8\u60f3\u4e0d\u8d77\u6765\u7a76\u7adf\u5728\u54ea\u91cc\u89c1\u8fc7\u3002 \u3000\u3000\u84e6\u5730\uff0c\u4eba\u5f71\u4e00\u95ea\uff0c\u7adf\u53c8\u6765\u4e86\u4e00\u540d\u7ea2\u8863\u5973\u5b50\u8eab\u5f71\u3002 \u3000\u3000\u8fd9\u8eab\u5f71\u6765\u5f97\u5b9e\u5728\u592a\u5feb\u4e5f\u592a\u7a81\u7136\uff0c\u5c0f\u71d5\u5b50\u53ea\u56e0\u592a\u7559\u795e\u90a3\u5148\u524d\u7684\u7d20\u8863\u5973\u5b50\uff0c\u7adf\u7136\u6ca1\u770b\u6e05\u8fd9\u7ea2 \u8863\u5973\u5b50\u662f\u4ece\u4f55\u5904\u800c\u6765\uff1f\u5f53\u5c0f\u71d5\u5b50\u770b\u6e05\u7ea2\u8863\u5c11\u5973\u540e\uff0c\u5fc3\u5934\u53c8\u662f\u4e00\u9707\uff0c\u8fd9\u4eba\u8d6b\u7136\u662f\u5c0f\u6885\u3002 \u3000\u3000\u60f3\u4e0d\u5230\u8fd9\u4f4d\u540d\u4e49\u4e0a\u662f\u201c\u6843\u6797\u5c45\u201d\u5973\u62db\u5f85\u7684\u5c0f\u59d1\u5a18\uff0c\u8f7b\u529f\u7adf\u9ad8\u5230\u5982\u6b64\u5883\u5730\u3002 \u3000\u3000\u53ea\u89c1\u5c0f\u6885\u671b\u7740\u90a3\u7d20\u8863\u5973\u5b50\u4e00\u793c\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u592b\u4eba\u600e\u4e48\u6df1\u591c\u5230\u8fd9\u91cc\u6765\u4e86\uff1f\u201d \u3000\u3000\u8fd9\u58f0\u97f3\u4f4e\u5f97\u4e0d\u80fd\u518d\u4f4e\uff0c\u82e5\u975e\u5c0f\u71d5\u5b50\u8d34\u5728\u7a97\u53e3\u805a\u7cbe\u4f1a\u795e\u7684\u542c\uff0c\u5426\u5219\u6839\u672c\u542c\u4e0d\u5230\u3002 \u3000\u3000\u90a3\u7d20\u8863\u5973\u5b50\u7f13\u7f13\u8f6c\u8fc7\u6765\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0c\u5c0f\u71d5\u5b50\u51e0\u4e4e\u8981\u5931\u58f0\u547c\u53eb\u51fa\u6765\uff01 \u3000\u3000\u8c01\u80fd\u60f3\u5230\uff0c\u8fd9\u7d20\u8863\u5973\u5b50\u7adf\u7136\u662f\u60dc\u6625\uff01 \u3000\u3000\u96be\u9053\u60dc\u6625\u5c31\u662f\u9a6c\u5be1\u5987\uff1f\u5426\u5219\u5c0f\u6885\u4e3a\u4f55\u79f0\u5979\u592b\u4eba\uff1f\u5982\u679c\u60dc\u6625\u771f\u662f\u9a6c\u5be1\u5987\uff0c\u5728\u5c0f\u71d5\u5b50\u6765\u8bf4\uff0c \u90a3\u5b9e\u5728\u592a\u4e0d\u53ef\u601d\u8bae\u4e86\uff01 \u3000\u3000\u56e0\u4e3a\u636e\u4ed6\u6240\u77e5\uff0c\u60dc\u6625\u539f\u662f\u6765\u81ea\u5c0f\u897f\u5929\u96f7\u97f3\u5bfa\uff0c\u5341\u4e09\u5c81\u5165\u5bab\uff0c\u5728\u5bab\u4e2d\u4ee5\u4e09\u516c\u4e3b\u7684\u8d34\u8eab\u4f8d\u5a62 \u8eab\u4efd\u6f5c\u4f0f\u4e94\u5e74\uff0c\u4eca\u5e74\u624d\u4e0d\u8fc7\u5341\u516b\u5468\u5c81\uff0c\u6570\u6708\u524d\u624d\u9003\u79bb\u5bab\u4e2d\uff0c\u800c\u9a6c\u5be1\u5987\u5728\u6f7c\u5173\u5f00\u9152\u5e97\u5374\u7edd\u5bf9\u4e0d \u662f\u51e0\u4e2a\u6708\u4ee5\u524d\u7684\u4e8b\uff0c\u81f3\u5c11\u4e5f\u8be5\u6709\u4e00\u4e24\u5e74\u4e4b\u4e45\uff0c\u5c24\u5176\u5c0f\u6885\u79f0\u5979\u4e3a\u592b\u4eba\uff0c\u8fd9\u8bc1\u660e\u5979\u5df2\u7ecf\u5ac1\u8fc7\u4eba\uff0c \u8fd9\u53c8\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u5c0f\u71d5\u5b50\u60c5\u6025\u4e4b\u4e0b\uff0c\u987e\u4e0d\u5f97\u7537\u5973\u4e4b\u5acc\uff0c\u6025\u6025\u628a\u5e8a\u4e0a\u719f\u7761\u7684\u601c\u82b1\u63a8\u9192\uff0c\u4e00\u9762\u4f20 \u97f3\u9053\uff1a\u201c\u59d1\u5a18\u5feb\u8d77\u6765\u770b\u770b\uff0c\u7a97\u5916\u6709\u4eba\uff01\u201d \u3000\u3000\u601c\u82b1\u5f88\u591f\u673a\u8b66\uff0c\u5979\u672c\u662f\u548c\u8863\u800c\u5367\uff0c\u95fb\u8a00\u7acb\u5373\u8dc3\u4e0b\u5e8a\u6765\uff0c\u5954\u81f3\u7a97\u524d\u3002 \u5367\u9f99\u751f\u300a\u71d5\u5b50\u4f20\u5947\u300b \u7b2c\u4e09\u5341\u4e00\u56de\u3000\u5f03\u6697\u6295\u660e \u3000\u3000\u5f53\u5979\u4e00\u773c\u77a5\u89c1\u90a3\u7d20\u8863\u5973\u5b50\uff0c\u4e5f\u5dee\u70b9\u53eb\u51fa\u58f0\u6765\uff01 \u3000\u3000\u5979\u548c\u60dc\u6625\uff0c\u6709\u4e94\u5e74\u7684\u65f6\u95f4\u671d\u5915\u76f8\u5904\uff0c\u5979\u4fe9\u540c\u662f\u4e09\u516c\u4e3b\u8eab\u8fb9\u6700\u5f97\u529b\u7684\u4f8d\u5a62\uff0c\u4e24\u4eba\u66f4\u662f\u60c5\u540c \u59d0\u59b9\u3002 \u3000\u3000\u60dc\u6625\u88ab\u9a71\u9010\u51fa\u5bab\u65f6\uff0c\u5979\u4e00\u76f4\u4f9d\u4f9d\u96be\u820d\uff0c\u4e07\u60f3\u4e0d\u5230\u7adf\u4f1a\u5728\u8fd9\u91cc\u76f8\u89c1\u3002 \u3000\u3000\u4f46\u6b64\u65f6\u6b64\u5730\uff0c\u5979\u5374\u4e0d\u80fd\u5f00\u53e3\u8bf4\u8bdd\uff0c\u53ea\u80fd\u77a0\u76ee\u7ed3\u820c\u7684\u5411\u5916\u671b\u7740\u3002 \u3000\u3000\u7d20\u8863\u5973\u5b50\u5e76\u672a\u56de\u7b54\u5c0f\u6885\u7684\u95ee\u8bdd\uff0c\u51dd\u89c6\u7740\u897f\u53a2\uff0c\u534a\u664c\u624d\u6de1\u6de1\u7684\u9053\uff1a\u201c\u90a3\u5bf9\u5c11\u5e74\u592b\u59bb\u662f\u4e0d\u662f \u5c31\u5728\u91cc\u9762\uff1f\u201d \u3000\u3000\u5c0f\u6885\u70b9\u70b9\u5934\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u4e1c\u53a2\u662f\u5355\u4eba\u7684\u5417\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u6709\u591a\u5927\u5e74\u7eaa\uff1f\u201d \u3000\u3000\u201c\u5927\u7ea6\u4e8c\u5341\u4e94\u516d\uff01\u201d \u3000\u3000\u201c\u90a3\u5bf9\u5e74\u8f7b\u592b\u59bb\u5462\uff1f\u201d \u3000\u3000\u201c\u770b\u6837\u5b50\u6700\u591a\u4e8c\u5341\u5c81\u3002\u201d \u3000\u3000\u201c\u53ef\u95ee\u8fc7\u4ed6\u4eec\u7684\u59d3\u540d\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u4e0d\u95ee\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u53ef\u80fd\u8981\u4f4f\u597d\u51e0\u5929\uff0c\u5a62\u5b50\u5c31\u4f1a\u95ee\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e5f\u6ca1\u7761\u5462\uff1f\u201d \u3000\u3000\u201c\u5a62\u5b50\u6050\u6015\u592b\u4eba\u6709\u4e8b\uff0c\u4e0d\u6562\u7761\u3002\u592b\u4eba\uff0c\u4f60\u771f\u7684\u6709\u4e8b\u5417\uff1f\u201d \u3000\u3000\u201c\u6ca1\u4ec0\u4e48\uff0c\u4f60\u968f\u6211\u6765\uff0c\u8fd9\u8fb9\u7684\u4e8b\u4e0d\u5fc5\u7ba1\u4e86\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u7f13\u6b65\u7531\u4e00\u4fa7\u7684\u901a\u9053\u7ed5\u5411\u8de8\u9662\u3002 \u3000\u3000\u5c0f\u6885\u7d27\u7d27\u8ddf\u968f\u5728\u540e\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u5c82\u80fd\u653e\u8fc7\u8fd9\u673a\u4f1a\uff0c\u4f4e\u58f0\u4ea4\u4ee3\u4e86\u601c\u82b1\u51e0\u53e5\uff0c\u4fbf\u7acb\u5373\u51fa\u623f\u8dc3\u4e0a\u5c4b\u9876\u3002\u7531\u5c4b\u9876\u770b\u5f97\u5f88\u6e05 \u695a\uff0c\u7d20\u8863\u5973\u5b50\u548c\u5c0f\u6885\u6b63\u8d70\u5411\u9662\u897f\u9996\u7684\u4e00\u95f4\u53a2\u623f\uff0c\u7136\u540e\u5173\u4e0a\u95e8\u53bb\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u4e0d\u89c9\u6697\u559c\uff01 \u3000\u3000\u56e0\u4e3a\u4ed6\u7ec8\u4e8e\u627e\u5230\u4e86\u5bf9\u65b9\u7684\u4f4f\u5904\uff0c\u4e5f\u7b49\u4e8e\u83b7\u6089\u4e86\u4e00\u9879\u673a\u5bc6\u3002 \u3000\u3000\u4ed6\u8f7b\u8f7b\u63a0\u4e0b\u5929\u4e95\uff0c\u6765\u5230\u90a3\u95f4\u53a2\u623f\u7684\u7a97\u5916\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\u91cc\u9762\u8fde\u4e00\u70b9\u58f0\u606f\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u96be\u9053\u5979\u4eec\u4f1a\u4e00\u8fdb\u623f\u5012\u5934\u5c31\u7761\uff1f\u542c\u5148\u524d\u7d20\u8863\u5973\u5b50\u7684\u8bed\u6c14\uff0c\u663e\u7136\u662f\u628a\u5c0f\u6885\u53eb\u6765\u95ee\u4e8b\u7684\uff0c\u8fdb\u5c4b \u4e4b\u540e\uff0c\u6839\u672c\u4e0d\u53ef\u80fd\u4e0d\u53d1\u51fa\u534a\u70b9\u58f0\u54cd\u3002 \u3000\u3000\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\u5462\uff1f\u5c0f\u71d5\u5b50\u53ea\u6709\u8010\u5fc3\u7684\u5728\u7a97\u5916\u7a83\u542c\u3002 \u3000\u3000\u8c01\u77e5\u8db3\u8db3\u987f\u996d\u5de5\u592b\u8fc7\u53bb\uff0c\u7adf\u662f\u8fde\u9f3b\u606f\u58f0\u90fd\u6beb\u65e0\u6240\u95fb\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u6697\u5fd6\uff1a\u8fd9\u95f4\u53a2\u623f\u7edd\u4e0d\u53ef\u80fd\u6709\u4e24\u4e2a\u95e8\u8fdb\u51fa\uff0c\u7d20\u8863\u5973\u5b50\u548c\u5c0f\u6885\u5206\u660e\u5728\u91cc\u9762\uff0c\u96be\u9053\u5979\u4eec \u4f1a\u571f\u9041\u4e0d\u6210\uff1f\u7531\u7a97\u9699\u5411\u91cc\u671b\u53bb\uff0c\u56e0\u5c4b\u5185\u5e76\u65e0\u706f\u5149\uff0c\u6708\u5149\u4e5f\u65e0\u6cd5\u900f\u8fdb\uff0c\u5c0f\u71d5\u5b50\u867d\u89c6\u529b\u8fc7\u4eba\uff0c\u4e5f \u65e0\u6cd5\u770b\u6e05\u91cc\u9762\u666f\u8c61\u3002 \u3000\u3000\u4ed6\u771f\u60f3\u7834\u7a97\u800c\u5165\uff0c\u4f46\u53c8\u89c9\u5f97\u90a3\u6837\u505a\u52bf\u5fc5\u574f\u4e86\u5927\u4e8b\u3002 \u3000\u3000\u6700\u540e\uff0c\u4ed6\u51b3\u5b9a\u8fd8\u662f\u5148\u884c\u56de\u623f\uff0c\u7b49\u5929\u4eae\u540e\u518d\u8bf4\u3002 \u3000\u3000\u8fdb\u5165\u81ea\u5df1\u6240\u4f4f\u7684\u540e\u9662\u897f\u53a2\uff0c\u601c\u82b1\u6b63\u5750\u5728\u5e8a\u6cbf\u7b49\u5019\uff0c\u4e00\u89c1\u5c0f\u71d5\u5b50\u56de\u6765\u5373\u8feb\u4e0d\u53ca\u5f85\u7684\u95ee\u9053\uff1a \u201c\u600e\u4e48\u6837\uff1f\u5979\u4eec\u90a3\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u201c\u59d1\u5a18\u5148\u4e0a\u5e8a\u7761\u5427\uff0c\u660e\u5929\u518d\u8bf4\u3002\u201d \u3000\u3000\u6b21\u65e5\u5929\u8fd8\u672a\u4eae\uff0c\u5c0f\u71d5\u5b50\u548c\u601c\u82b1\u4fbf\u5df2\u9192\u6765\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u4e3b\u52a8\u628a\u6628\u665a\u63a2\u67e5\u7684\u60c5\u5f62\u5411\u601c\u82b1\u8bf4\u4e86\u4e00\u904d\u3002 \u3000\u3000\u63a5\u7740\u4ed6\u53c8\u9053\uff1a\u201c\u90a3\u7d20\u8863\u5973\u5b50\u50cf\u6781\u4e86\u60dc\u6625\uff0c\u5230\u5e95\u662f\u4e0d\u662f\u60dc\u6625\uff0c\u4f60\u5e94\u8be5\u770b\u5f97\u51fa\u6765\uff1f\u201d \u3000\u3000\u601c\u82b1\u9053\uff1a\u201c\u7684\u786e\u592a\u50cf\u4e86\u3002\u5982\u679c\u771f\u662f\u5979\uff0c\u90a3\u4e48\u9a6c\u5be1\u5987\u5c31\u5fc5\u5b9a\u662f\u53e6\u5916\u4e00\u4e2a\u3002\u201d \u3000\u3000\u201c\u6211\u4e5f\u8fd9\u4e48\u60f3\uff0c\u636e\u8bf4\u9a6c\u5be1\u5987\u4e3b\u6301\u8fd9\u5bb6\u9152\u5e97\u5df2\u6709\u4e00\u4e24\u5e74\u65f6\u95f4\uff0c\u751a\u81f3\u65f6\u95f4\u8fd8\u8981\u66f4\u957f\uff0c\u800c\u60dc\u6625 \u79bb\u5f00\u5bab\u4e2d\u624d\u4e0d\u8fc7\u4e24\u4e09\u4e2a\u6708\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u90a3\u7d20\u8863\u5973\u5b50\u771f\u662f\u60dc\u6625\uff0c\u90a3\u5c31\u8bf4\u660e\u60dc\u6625\u4e5f\u4f4f\u5728\u8fd9\u91cc\u3002\u9a6c\u5be1\u5987\u662f\u897f\u57ce\u9b54\u6559\u6d3e\u8fc7\u6765\u7684\uff0c \u60dc\u6625\u672c\u6765\u5c31\u548c\u5979\u662f\u4e00\u9053\u4e0a\u7684\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u5c0f\u6885\u628a\u7d20\u8863\u5973\u5b50\u79f0\u4e3a\u592b\u4eba\uff0c\u60dc\u6625\u90a3\u4f1a\u8fd9\u4e48\u5feb\u5c31\u5ac1\u4e86\u4eba\uff1f\u201d \u3000\u3000\u201c\u90a3\u4e5f\u5f88\u96be\u8bf4\uff0c\u636e\u8bf4\u897f\u57ce\u9b54\u6559\u7684\u7537\u5973\u5a5a\u59fb\u6709\u4e0d\u5c11\u662f\u7531\u4e0a\u7ea7\u505a\u4e3b\u914d\u5bf9\u7684\uff0c\u5f88\u53ef\u80fd\u60dc\u6625\u4e00\u56de \u5230\u897f\u57df\u5c31\u88ab\u8bb8\u914d\u7ed9\u67d0\u4e00\u4e2a\u7537\u4eba\u3002\u201d \u3000\u3000\u201c\u90a3\u5979\u592a\u547d\u82e6\u4e86\uff0c\u521a\u5ac1\u51fa\u53bb\u5c31\u505a\u4e86\u5be1\u3002\u201d \u3000\u3000\u201c\u600e\u77e5\u5979\u505a\u4e86\u5be1\u5987\uff1f\u201d \u3000\u3000\u201c\u5982\u679c\u4e0d\u662f\u5be1\u5987\uff0c\u90a3\u6709\u65b0\u5a18\u5b50\u7a7f\u7d20\u8863\u7684\uff1f\u201d \u3000\u3000\u201c\u8bf4\u7684\u4e5f\u6709\u9053\u7406\uff0c\u597d\u5728\u5979\u6ca1\u6709\u4e08\u592b\u5374\u8fd8\u6709\u6743\uff0c\u770b\u5c0f\u6885\u5bf9\u5979\u90a3\u79cd\u6bd5\u606d\u6bd5\u656c\u7684\u6837\u5b50\uff0c\u5c31\u77e5\u9053 \u5979\u76ee\u524d\u7684\u8eab\u4efd\u5730\u4f4d\u4e00\u5b9a\u5f88\u9ad8\u3002\u201d \u3000\u3000\u201c\u6b7b\u4e86\u4e08\u592b\uff0c\u8eab\u4efd\u5730\u4f4d\u518d\u9ad8\u53c8\u6709\u4f55\u7528\uff1f\u201d \u3000\u3000\u601c\u82b1\u767d\u4e86\u5c0f\u71d5\u5b50\u4e00\u773c\u9053\uff1a\u201c\u5bf9\u4e00\u4e2a\u5973\u4eba\u6765\u8bf4\uff0c\u4e08\u592b\u5c31\u6709\u90a3\u4e48\u91cd\u8981\u5417\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u4e00\u672c\u6b63\u7ecf\u7684\u9053\uff1a\u201c\u7537\u5927\u5f53\u5a5a\uff0c\u5973\u5927\u5f53\u5ac1\u3002\u5973\u4eba\u5ac1\u51fa\u53bb\u4ee5\u540e\uff0c\u5927\u534a\u8f88\u5b50\u8981\u548c\u4e08\u592b\u751f \u6d3b\u5728\u4e00\u8d77\uff0c\u82e5\u4e00\u51fa\u5ac1\u5c31\u6b7b\u4e86\u4e08\u592b\uff0c\u80fd\u8bf4\u4e0d\u4e25\u91cd\u5417\uff1f\u201d \u3000\u3000\u201c\u4e00\u8f88\u5b50\u4e0d\u5ac1\u4eba\u7684\u5973\u4eba\uff0c\u4e5f\u4e0d\u662f\u6ca1\u6709\uff01\u201d \u3000\u3000\u201c\u90a3\u5c31\u53e6\u5f53\u522b\u8bba\u4e86\uff0c\u59d1\u5a18\uff01\u54b1\u4eec\u522b\u62ac\u6760\uff0c\u5546\u8bae\u5546\u8bae\u6b63\u4e8b\u8981\u7d27\u3002\u201d \u3000\u3000\u6b63\u8bf4\u8bdd\u95f4\uff0c\u7a97\u5916\u6709\u4e86\u811a\u6b65\u58f0\uff01 \u3000\u3000\u63a5\u7740\u662f\u5c0f\u6885\u7684\u58f0\u97f3\uff1a\u201c\u516c\u5b50\u5f00\u95e8\uff0c\u9001\u76c6\u6d17\u8138\u6c34\u6765\u4e86\uff01\u201d \u3000\u3000\u5c0f\u6885\u6765\u4e86\uff0c\u6b63\u662f\u8bbe\u6cd5\u89e3\u5f00\u8ff7\u5e95\u7684\u65f6\u5019\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u8fde\u5fd9\u6253\u5f00\u95e8\u6765\u3002 \u3000\u3000\u5c0f\u6885\u8e0f\u8fdb\u95e8\u6765\uff0c\u7b11\u7740\u9053\uff1a\u201c\u4f60\u4eec\u5c0f\u4fe9\u53e3\u8fd9\u4e48\u65e9\u5c31\u8d77\u5e8a\u4e86\uff0c\u771f\u662f\u96be\u5f97\u5440\uff01\u6628\u665a\u7761\u5f97\u8fd8\u597d \u5427\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u4f38\u4e86\u4e2a\u61d2\u8170\u9053\uff1a\u201c\u6211\u4ece\u6ca1\u4f4f\u8fc7\u8fd9\u4e48\u9759\u7684\u5ba2\u6808\uff0c\u591c\u91cc\u534a\u70b9\u5435\u95f9\u7684\u58f0\u97f3\u90fd\u6ca1\u6709\uff0c\u5f53\u7136 \u7761\u5f97\u597d\u3002\u201d \u3000\u3000\u5c0f\u6885\u7b11\u9053\uff1a\u201c\u6211\u4eec\u8fd9\u91cc\u7b49\u4e8e\u4f4f\u5bb6\u7684\uff0c\u6839\u672c\u4e0d\u662f\u5ba2\u6808\u3002\u201d \u3000\u3000\u201c\u8d35\u9152\u5e97\u4eba\u6570\u4e00\u5b9a\u4e0d\u5c11\uff0c\u4e3a\u4ec0\u4e48\u53ea\u770b\u5230\u59d1\u5a18\u4e00\u4e2a\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u2018\u6843\u6797\u5c45\u2019\u65e9\u4e0a\u4e0d\u505a\u751f\u610f\uff0c\u4e2d\u5348\u624d\u5f00\u59cb\u8425\u4e1a\uff0c\u516c\u5b50\u73b0\u5728\u5f53\u7136\u770b\u4e0d\u5230\u522b\u4eba\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u59d1\u5a18\u4e3a\u4ec0\u4e48\u4e00\u76f4\u5728\u5fd9\uff1f\u201d \u3000\u3000\u201c\u6211\u662f\u8d1f\u8d23\u670d\u4f8d\u4f60\u4eec\u4e09\u4f4d\u7684\uff0c\u4e0d\u5fd9\u884c\u5417\uff1f\u201d \u3000\u3000\u201c\u90a3\u771f\u662f\u8f9b\u82e5\u4f60\u4e00\u4eba\u4e86\u3002\u201d \u3000\u3000\u201c\u7b97\u4e0d\u4e86\u4ec0\u4e48\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u672c\u60f3\u8f6c\u5f2f\u62b9\u89d2\u95ee\u95ee\u6b63\u9898\uff0c\u4f46\u53c8\u89c9\u5f97\u95ee\u5f97\u592a\u9732\u9aa8\u53cd\u800c\u5bb9\u6613\u5f15\u8d77\u5bf9\u65b9\u7684\u7591\u5fc3\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\u4ed6\u5fc3\u91cc\u4e5f\u6709\u6570\uff0c\u5bf9\u65b9\u5f88\u53ef\u80fd\u65e9\u5c31\u7591\u5fc3\u4e86\uff0c\u8bf4\u4e0d\u5b9a\u81ea\u5df1\u8fd9\u65b9\u9762\u5df2\u7ecf\u843d\u5165\u4e86\u5bf9\u65b9\u7684\u5708 \u5957\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u4ed6\u51b3\u5b9a\u4e0d\u518d\u591a\u95ee\u4e86\u3002 \u3000\u3000\u5c0f\u6885\u8d70\u540e\uff0c\u5c0f\u71d5\u5b50\u548c\u601c\u82b1\u4e00\u5f84\u6765\u5230\u4e1c\u53a2\u3002 \u3000\u3000\u4e1c\u53a2\u7684\u767d\u7fbd\u4e5f\u65e9\u8d77\u5e8a\u4e86\u3002 \u3000\u3000\u5f53\u5c0f\u71d5\u5b50\u8bf4\u660e\u6628\u665a\u7ecf\u8fc7\u540e\uff0c\u767d\u7fbd\u9053\uff1a\u201c\u770b\u6765\u53ea\u6709\u56de\u53bb\u5411\u738b\u5927\u4eba\u548c\u6c5f\u524d\u8f88\u8bf7\u793a\u4e00\u4e0b\u518d\u8bf4\uff0c \u770b\u770b\u4ed6OJ\u6709\u4ec0\u4e48\u4e3b\u610f\u3002\u201d \u3000\u3000\u201c\u7531\u4f60\u56de\u53bb\u8fd8\u662f\u7531\u6211\u56de\u53bb\uff1f\u201d \u3000\u3000\u201c\u4f60\u662f\u6709\u65b0\u5a5a\u592b\u4eba\u7684\uff0c\u79bb\u5f00\u4e0d\u592a\u65b9\u4fbf\uff0c\u5c31\u7531\u6211\u56de\u53bb\u5427\uff01\u201d \u3000\u3000\u767d\u7fbd\u56de\u5230\u5ba2\u6808\uff0c\u628a\u7ecf\u8fc7\u5411\u738b\u5f64\u548c\u6c5f\u5e72\u91cc\u6c47\u62a5\u4e86\u4e00\u904d\u3002 \u3000\u3000\u738b\u5f64\u7d27\u76b1\u7740\u7709\u5934\u9053\uff1a\u201c\u4f60\u80fd\u786e\u5b9a\u90a3\u7d20\u8863\u5973\u5b50\u5c31\u662f\u60dc\u6625\u5417\uff1f\u201d \u3000\u3000\u767d\u7fbd\u9053\uff1a\u201c\u662f\u5c0f\u71d5\u5b50\u770b\u5230\u7684\uff0c\u601c\u82b1\u4e5f\u770b\u5230\u4e86\uff0c\u4ed6\u4eec\u8bf4\u5341\u6709\u516b\u4e5d\u662f\u60dc\u6625\u3002\u201d \u3000\u3000\u201c\u6709\u4eba\u770b\u5230\u9a6c\u5be1\u5987\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u9a6c\u5be1\u5987\u59cb\u7ec8\u672a\u51fa\u73b0\u3002\u201d \u3000\u3000\u738b\u5f64\u671b\u671b\u6c5f\u5e72\u91cc\u9053\uff1a\u201c\u6c5f\u5144\u7684\u610f\u601d\u8be5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u4ee5\u5c0f\u9053\u58eb\u8ff0\u8bf4\u7684\u60c5\u5f62\uff0c\u770b\u6765\u54b1\u4eec\u975e\u91c7\u53d6\u884c\u52a8\u4e0d\u53ef\u4e86\u3002\u201d \u3000\u3000\u201c\u6c5f\u5144\u8bf7\u8bf4\u51fa\u91c7\u53d6\u884c\u52a8\u7684\u7406\u7531\uff01\u201d \u3000\u3000\u201c\u4f9d\u6211\u6240\u6599\uff0c\u90a3\u4e09\u4e2a\u5973\u7684\u5f88\u53ef\u80fd\u5df2\u7ecf\u5230\u8fbe\u6f7c\u5173\uff0c\u82e5\u4e0d\u91c7\u53d6\u884c\u52a8\uff0c\u4e00\u65e6\u88ab\u8d70\u8131\uff0c\u4ee5\u540e\u5c06\u6c38 \u8fdc\u65e0\u6cd5\u627e\u5230\u5979\u4eec\u3002\u201d \u3000\u3000\u201c\u6c5f\u5144\u51c6\u5907\u5982\u4f55\u884c\u52a8\uff1f\u201d \u3000\u3000\u201c\u90a3\u8de8\u9662\u5185\u7684\u53a2\u623f\uff0c\u5ba4\u5185\u5fc5\u6709\u79d8\u9053\u901a\u5f80\u522b\u5904\uff0c\u53ea\u8981\u51b2\u8fdb\u79d8\u9053\uff0c\u5fc5\u6709\u6240\u83b7\uff0c\u4e0d\u8fc7\uff0c\u5fc5\u5b9a\u8981 \u5192\u6781\u5927\u7684\u5371\u9669\u3002\u201d \u3000\u3000\u201c\u8fd9\u8bdd\u600e\u8bb2\uff1f\u201d \u3000\u3000\u201c\u79d8\u9053\u5185\u5fc5\u5b9a\u8bbe\u6709\u91cd\u91cd\u673a\u5173\u57cb\u4f0f\u6216\u5176\u4ed6\u7981\u5236\uff0c\u54b1\u4eec\u7684\u4eba\u5f88\u53ef\u80fd\u4f1a\u5148\u53d7\u5176\u5bb3\uff0c\u6211\u4eec\u4e0d\u80fd\u4e0d \u5148\u6709\u5fc3\u7406\u51c6\u5907\uff01\u201d \u3000\u3000\u738b\u5f64\u4e0d\u7531\u8d77\u4e86\u72b9\u8c6b\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u6700\u597d\u8fd8\u662f\u6682\u65f6\u6309\u5175\u4e0d\u52a8\uff0c\u56e0\u4e3a\u54b1\u4eec\u8fd9\u8fb9\u7684\u4eba\u90fd\u4e0d\u61c2\u673a \u5173\u57cb\u4f0f\u4e4b\u5b66\uff0c\u88ab\u56f0\u5728\u5bc6\u9053\u4e2d\u662f\u5c0f\u4e8b\uff0c\u4e07\u4e00\u56e0\u800c\u9001\u547d\u2026\u2026\u201d \u3000\u3000\u6c5f\u5343\u91cc\u82e6\u7b11\u9053\uff1a\u201c\u53ef\u662f\uff0c\u82e5\u4e0d\u53ca\u65e9\u884c\u52a8\uff0c\u4e00\u5207\u5fc5\u5c06\u843d\u7a7a\u3002\u201d \u3000\u3000\u201c\u6c5f\u5144\u51c6\u5907\u8981\u54ea\u4e9b\u4eba\u51b2\u8fdb\u5bc6\u9053\u91c7\u53d6\u884c\u52a8\uff1f\u201d \u3000\u3000\u201c\u76ee\u524d\u4f4f\u5728\u90a3\u8fb9\u7684\u4eba\u662f\u5c0f\u9053\u58eb\u3001\u5c0f\u71d5\u5b50\u548c\u601c\u82b1\uff0c\u5f53\u7136\u5e94\u7531\u4ed6\u4eec\u4e09\u4eba\u884c\u52a8\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u8fd9\u8fb9\u6240\u6709\u7684\u4eba\u662f\u5426\u53ef\u4ee5\u4e00\u8d77\u53bb\uff1f\u201d \u3000\u3000\u201c\u90a3\u6837\u4e0d\u59a5\uff0c\u4eba\u53bb\u591a\u4e86\uff0c\u53cd\u800c\u4f1a\u6beb\u65e0\u6240\u83b7\u3002\u201d \u3000\u3000\u738b\u5f64\u4e0d\u518d\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u4ed6\u867d\u7136\u4e0d\u53cd\u5bf9\u6c5f\u5343\u91cc\u7684\u63d0\u8bae\uff0c\u5374\u53c8\u4e0d\u4fbf\u516c\u5f00\u8868\u793a\u8d5e\u540c\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u4e07\u4e00\u5c0f\u71d5\u5b50\u3001\u767d\u7fbd\u548c\u601c\u82b1\u56e0\u91c7\u53d6\u884c\u52a8\u800c\u8eab\u906d\u4e0d\u6d4b\uff0c\u4ed6\u5982\u4f55\u5411\u4e09\u516c\u4e3b\u548c\u71d5\u98de\u4ea4\u4ee3 \u5462\uff1f\u6c5f\u5343\u91cc\u81ea\u7136\u4e5f\u4e0d\u613f\u575a\u6301\u5df1\u89c1\uff0c\u6700\u540e\u671b\u5411\u5c0f\u9053\u58eb\u9053\uff1a\u201c\u4f60\u73b0\u5728\u5c31\u5148\u56de\u53bb\u5427\uff0c\u548c\u5c0f\u71d5\u5b50\u7ee7\u7eed \u5728\u6697\u4e2d\u7559\u610f\u5bf9\u65b9\u7684\u884c\u52a8\uff0c\u82e5\u6709\u65b0\u53d1\u73b0\uff0c\u80fd\u5904\u7f6e\u5c31\u5f53\u673a\u7acb\u65ad\u7684\u52a0\u4ee5\u5904\u7f6e\uff0c\u4e0d\u7136\u5c31\u5c3d\u901f\u56de\u6765\u901a \u62a5\u3002\u201d \u3000\u3000\u767d\u7fbd\u56de\u5230\u201c\u6843\u6797\u5c45\u201d\uff0c\u521a\u5230\u897f\u53a2\u548c\u5c0f\u71d5\u5b50\u3001\u601c\u82b1\u89c1\u4e86\u9762\uff0c\u8fd8\u6ca1\u6765\u5f97\u53ca\u8bf4\u8bdd\uff0c\u4fbf\u89c1\u5c0f\u6885\u63d0 \u7740\u4e00\u4e2a\u98df\u76d2\u8fdb\u6765\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u5f00\u996d\u4e0d\u662f\u5728\u9910\u5385\u91cc\u5417\uff1f\u201d \u3000\u3000\u5c0f\u6885\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u4e09\u4f4d\u548c\u4e00\u822c\u5ba2\u4eba\u4e0d\u540c\uff0c\u4e0d\u5fc5\u5230\u9910\u5385\uff0c\u672c\u6765\uff0c\u6211\u8be5\u5230\u4e1c\u53a2\u4e5f\u9001\u4e00\u4efd\u53bb\uff0c \u53ea\u56e0\u4f60\u4eec\u4e09\u4f4d\u539f\u662f\u4e00\u9053\u6765\u7684\uff0c\u5c31\u5728\u4e00\u8d77\u7528\u5427\uff01\u201d \u3000\u3000\u201c\u59d1\u5a18\uff0c\u4e3a\u4ec0\u4e48\u59cb\u7ec8\u6ca1\u770b\u5230\u638c\u67dc\u7684\uff1f\u201d \u3000\u3000\u201c\u638c\u67dc\u7684\u6628\u665a\u5c31\u6709\u4e8b\u51fa\u53bb\u4e86\u3002\u201d \u3000\u3000\u201c\u638c\u67dc\u7684\u4ee5\u5728\u5e97\u91cc\u505a\u751f\u610f\u4e3a\u4e3b\uff0c\u5916\u9762\u4f1a\u6709\u4ec0\u4e48\u4e8b\u5462\uff1f\u201d \u3000\u3000\u201c\u4f60\u8fd9\u8bdd\u9519\u4e86\uff0c\u5e97\u91cc\u5404\u4eba\u7ba1\u5404\u4eba\u7684\u4e8b\uff0c\u638c\u67dc\u7684\u4e0d\u5728\u5e76\u4e0d\u803d\u8bef\u751f\u610f\uff0c\u81f3\u4e8e\u5bf9\u5916\u7684\u6765\u5f80\u5374\u5168 \u8981\u7531\u638c\u67dc\u7684\u8d1f\u8d23\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u96be\u602a\u4e86\u3002\u201d \u3000\u3000\u5c0f\u6885\u8d70\u540e\uff0c\u9662\u5b50\u91cc\u679c\u7136\u5f00\u59cb\u6709\u4eba\u6765\u5f80\u3002 \u3000\u3000\u4e0d\u6d88\u8bf4\uff0c\u4ed6\u4eec\u662f\u201c\u6843\u6797\u5c45\u201d\u7684\u5de5\u4f5c\u4eba\u5458\uff0c\u5176\u4e2d\u8fd8\u6709\u4e24\u4e09\u4e2a\u5973\u7684\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u7b49\u4e09\u4eba\u81f3\u4eca\u4ecd\u5f04\u4e0d\u6e05\u4ed6\u4eec\u6628\u665a\u4f4f\u5728\u54ea\u91cc\uff1f\u4e00\u5929\u4e0b\u6765\uff0c\u9664\u767d\u7fbd\u65e9\u4e0a\u66fe\u56de\u8fc7\u5ba2\u6808\u5916\uff0c \u4e09\u4eba\u90fd\u672a\u79bb\u5f00\u201c\u6843\u6797\u5c45\u201d\u4e00\u6b65\u3002 \u3000\u3000\u508d\u665a\uff0c\u5c0f\u6885\u518d\u9001\u996d\u6765\u3002 \u3000\u3000\u5979\u4e0d\u89e3\u7684\u95ee\u9053\uff1a\u201c\u4e09\u4f4d\u4e3a\u4ec0\u4e48\u6574\u5929\u90fd\u4e0d\u51fa\u95e8\u5462\uff1f\u767d\u7fbd\u9053\uff1a\u201d\u6211\u4eec\u662f\u751f\u610f\u4eba\uff0c\u751f\u610f\u6628\u5929\u5df2 \u7ecf\u8c08\u59a5\u4e86\uff0c\u53ea\u7b49\u8d27\u7269\u4e00\u5230\uff0c\u4fbf\u8981\u8d76\u5f80\u957f\u5b89\uff0c\u73b0\u5728\u8d27\u672a\u5230\uff0c\u53ea\u6709\u4f11\u606f\uff0c\u4f55\u5fc5\u51fa\u53bb\uff1f\u201c \u3000\u3000\u5c0f\u6885\u53ea\u662f\u7b11\u7b11\uff0c\u5e76\u672a\u518d\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u5f53\u665a\u5165\u591c\u540e\uff0c\u540e\u9662\u91cc\u53c8\u5df2\u4e0d\u89c1\u9152\u5e97\u7684\u4eba\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u627e\u6765\u767d\u7fbd\uff0c\u548c\u601c\u82b1\u4e09\u4eba\u5f00\u59cb\u8ba1\u8bba\u8be5\u5982\u4f55\u884c\u52a8\uff1f\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u54b1\u4eec\u73b0\u5728\u7b49\u4e8e\u5b88\u682a\u5f85 \u5154\uff0c\u82e5\u5154\u5b50\u4e0d\u51fa\u6d1e\uff0c\u5c82\u4e0d\u662f\u4e00\u5207\u843d\u7a7a\uff1f\u201d \u3000\u3000\u767d\u7fbd\u70b9\u5934\u9053\uff1a\u201c\u4f60\u7684\u610f\u601d\u662f\u60f3\u8fdb\u6d1e\u67e5\u770b\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u4e0d\u5165\u864e\u7a74\u7109\u5f97\u864e\u5b50\uff01\u201d \u3000\u3000\u201c\u53ef\u662f\u79d8\u9053\u5185\u5fc5\u6709\u673a\u5173\u57cb\u4f0f\uff0c\u8fde\u738b\u5927\u4eba\u90fd\u4e0d\u5e0c\u671b\u54b1\u4eec\u8f7b\u4e3e\u5984\u52a8\u3002\u201d \u3000\u3000\u201c\u6c5f\u53d4\u53d4\u4e0d\u662f\u4e3b\u5f20\u54b101\u91c7\u53d6\u884c\u52a8\u5417\uff1f\u201d \u3000\u3000\u201c\u6c5f\u524d\u8f88\u867d\u7136\u4e3b\u5f20\u54b1\u4eec\u91c7\u53d6\u884c\u52a8\uff0c\u4f46\u7167\u6837\u4e5f\u6709\u6240\u987e\u8651\u3002\u201d \u3000\u3000\u201c\u4e0d\u7ba1\u5b83\uff0c\u8001\u6309\u5175\u4e0d\u52a8\uff0c\u7edd\u4e0d\u662f\u529e\u6cd5\u3002\u201d \u3000\u3000\u4e09\u4eba\u5546\u8bae\u7684\u7ed3\u679c\uff0c\u51b3\u5b9a\u4eca\u665a\u4e09\u66f4\u518d\u5728\u540e\u9662\u548c\u8de8\u9662\u4fa6\u5bdf\u4e00\u6b21\uff0c\u81f3\u4e8e\u662f\u5426\u6b63\u5f0f\u91c7\u53d6\u884c\u52a8\uff0c\u518d \u89c6\u5b9e\u9645\u60c5\u51b5\u800c\u5b9a\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u4e09\u4eba\u5148\u5c0f\u7761\u4e86\u4f1a\u513f\uff0c\u5f85\u4e8c\u66f4\u8fc7\u540e\uff0c\u4fbf\u5728\u5c0f\u71d5\u5b50\u7684\u5e26\u5934\u4e4b\u4e0b\uff0c\u5148\u5728\u540e\u9662\u5c55\u5f00\u641c\u67e5\u3002 \u3000\u3000\u8fd9\u65f6\u4e09\u4eba\u90fd\u5df2\u643a\u4e0a\u5175\u5203\uff0c\u5175\u5203\u662f\u6628\u5929\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u5e26\u601c\u82b1\u6765\u65f6\u88c5\u5728\u884c\u56ca\u4e2d\u5e26\u6765\u7684\uff0c\u5e76\u672a \u8ba9\u5c0f\u6885\u770b\u5230\u3002 \u3000\u3000\u4ed6\u4eec\u4e0d\u96be\u60f3\u5230\u8fd9\u91cc\u7684\u4eba\uff0c\u81ea\u9a6c\u5be1\u5987\u4ee5\u4e0b\uff0c\u5fc5\u5b9a\u4e2a\u4e2a\u90fd\u6709\u8fc7\u4eba\u8eab\u624b\uff0c\u4ec5\u770b\u5c0f\u6885\u7684\u8f7b\u529f\u4fbf\u53ef \u77e5\u9053\u3002 \u3000\u3000\u540e\u9662\u91cc\u679c\u7136\u6bcf\u4e2a\u623f\u95f4\u65e0\u4e00\u4eba\u3002 \u3000\u3000\u4e09\u4eba\u518d\u8fdb\u5165\u8de8\u9662\u3002 \u3000\u3000\u8de8\u9662\u91cc\u4ecd\u7136\u4e0d\u89c1\u4eba\u8e2a\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u6765\u5230\u90a3\u4e2a\u53a2\u623f\u7a97\u5916\uff0c\u9053\uff1a\u201c\u6628\u665a\u7d20\u8863\u5973\u5b50\u548c\u5c0f\u6885\u5c31\u662f\u8fdb\u5165\u8fd9\u623f\u95f4\u7684\uff0c\u65e2\u7136\u6c5f\u53d4\u53d4 \u65ad\u5b9a\u91cc\u9762\u6709\u79d8\u9053\uff0c\u54b1\u4eec\u73b0\u5728\u5c31\u8be5\u8fdb\u53bb\u770b\u770b\u3002\u201d \u3000\u3000\u767d\u7fbd\u9053\uff1a\u201c\u662f\u5426\u8be5\u7559\u4e00\u4e2a\u4eba\u5728\u5916\u628a\u98ce\uff0c\u4ee5\u4fbf\u4e07\u4e00\u53d1\u751f\u610f\u5916\u65f6\u8d1f\u8d23\u63a5\u5e94\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u7565\u4e00\u5bfb\u601d\u9053\uff1a\u201c\u90a3\u5c31\u4f60\u4eec\u4e24\u4f4d\u5728\u95e8\u5916\u628a\u98ce\uff0c\u7531\u6211\u4e00\u4eba\u8fdb\u53bb\u3002\u201d \u3000\u3000\u201c\u5343\u4e07\u5c0f\u5fc3\uff01\u201d \u3000\u3000\u201c\u4e0d\u52b3\u53ee\u549b\uff0c\u6211\u60f3\u673a\u5173\u57cb\u4f0f\u5728\u79d8\u9053\u91cc\u9762\uff0c\u5728\u5c4b\u5185\u7edd\u4e0d\u53ef\u80fd\u53d1\u751f\u610f\u5916\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u8bf4\u7740\uff0c\u63a8\u4e86\u63a8\u95e8\u3002 \u3000\u3000\u95e8\u662f\u5173\u7740\u7684\uff0c\u53ea\u597d\u5c06\u7a97\u64ac\u5f00\uff0c\u8f7b\u8f7b\u722c\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u767d\u7fbd\u548c\u601c\u82b1\u5219\u5168\u795e\u6212\u5907\u7684\u5b88\u62a4\u5728\u7a97\u5916\u3002 \u3000\u3000\u8fdb\u5165\u5ba4\u5185\uff0c\u89c6\u529b\u5f88\u5feb\u4fbf\u53ef\u9002\u5e94\uff0c\u5c0f\u5c0f\u4e00\u4e2a\u623f\u95f4\u4e00\u76ee\u4e86\u7136\uff0c\u9664\u4e86\u4e00\u5f20\u5e8a\u69bb\uff0c\u51e0\u628a\u6905\u5b50\u5916\uff0c \u522b\u65e0\u4ed6\u7269\uff0c\u6839\u672c\u7528\u4e0d\u7740\u641c\u7d22\u3002 \u3000\u3000\u73b0\u5728\uff0c\u5c0f\u71d5\u5b50\u6700\u91cd\u8981\u7684\u4e00\u4ef6\u4e8b\u4fbf\u662f\u5bfb\u627e\u79d8\u9053\u3002 \u3000\u3000\u4ed6\u4e0d\u96be\u9884\u6599\uff0c\u79d8\u9053\u53e3\u5fc5\u5b9a\u5728\u5730\u9762\uff0c\u800c\u6700\u53ef\u80fd\u662f\u5728\u5e8a\u4e0b\u3002 \u3000\u3000\u5ffd\u542c\u767d\u7fbd\u5728\u7a97\u5916\u9053\uff1a\u201c\u65e2\u7136\u91cc\u9762\u6ca1\u4e8b\uff0c\u90a3\u6211\u4eec\u4e5f\u8fdb\u53bb\u4e86\uff01\u201d \u3000\u3000\u201c\u597d\uff0c\u4f60\u4eec\u5c31\u8fdb\u6765\u5427\uff01\u201d \u3000\u3000\u767d\u7fbd\u548c\u601c\u82b1\u5f88\u5feb\u4fbf\u8d8a\u7a97\u800c\u8fdb\uff0c\u7136\u540e\u518d\u628a\u7a97\u5173\u4e0a\u3002 \u3000\u3000\u6b64\u523b\uff0c\u5c0f\u71d5\u5b50\u5df2\u5728\u5730\u4e0a\u5230\u5904\u6478\u7d22\u3002 \u3000\u3000\u767d\u7fbd\u9053\uff1a\u201c\u65e2\u7136\u8de8\u9662\u7a7a\u65e0\u4e00\u4eba\uff0c\u5c31\u4e0d\u59a8\u70b9\u4e0a\u706f\uff0c\u627e\u8d77\u6765\u4e5f\u5bb9\u6613\u4e9b\u3002\u201d \u3000\u3000\u201c\u4f60\u53ef\u5e26\u7740\u706b\u62c6\u5b50\uff1f\u201d \u3000\u3000\u201c\u591c\u665a\u884c\u52a8\uff0c\u600e\u80fd\u4e0d\u5e26\u8fd9\u4e2a\uff01\u201d \u3000\u3000\u767d\u7fbd\u4ece\u6000\u91cc\u6478\u51fa\u4e00\u6839\u706b\u62c6\u71c3\u4eae\uff0c\u679c\u7136\u5e8a\u5934\u8336\u51e0\u4e0a\u653e\u6709\u4e00\u76cf\u6cb9\u706f\u3002 \u3000\u3000\u70b9\u4e0a\u706f\uff0c\u7136\u540e\u5411\u5e8a\u4e0a\u7167\u53bb\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u722c\u5230\u5e8a\u4e0b\uff0c\u5f88\u5feb\u4fbf\u53d1\u73b0\u5730\u9762\u4f3c\u4e4e\u6709\u88c2\u75d5\uff0c\u63a2\u624b\u6478\u53bb\uff0c\u53ef\u4ee5\u611f\u89c9\u5f97\u51fa\u662f\u4e00\u5757\u5927\u7ea6\u4e24 \u4e09\u5c3a\u89c1\u65b9\u7684\u6728\u677f\u8986\u76d6\u5728\u5730\u9762\u4e0a\uff0c\u989c\u8272\u4e0e\u5730\u9762\u5b8c\u5168\u76f8\u540c\uff0c\u82e5\u975e\u4ed4\u7ec6\u67e5\u770b\uff0c\u5f88\u96be\u53d1\u73b0\u6709\u4ec0\u4e48\u5f02\u72b6 \u7684\u3002 \u3000\u3000\u6beb\u65e0\u7591\u95ee\uff0c\u8fd9\u5c31\u662f\u79d8\u9053\u7684\u51fa\u5165\u53e3\u4e86\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u521a\u628a\u6728\u677f\u6380\u8d77\uff0c\u4fbf\u542c\u91cc\u9762\u53d1\u51fa\u5636\u5636\u58f0\u97f3\uff01 \u3000\u3000\u767d\u7fbd\u6c5f\u6e56\u9605\u5386\u4e30\u5bcc\uff0c\u6025\u6025\u53eb\u9053\uff1a\u201c\u5feb\u9000\uff01\u201d \u3000\u3000\u4ed6\u7684\u558a\u58f0\u521a\u521a\u843d\u97f3\uff0c\u4fbf\u542c\u4e00\u8fde\u4e32\u9aa4\u6025\u7684\u201c\u54bb\u54bb\u201d\u4e4b\u58f0\u54cd\u8d77\uff0c\u8db3\u6709\u4e03\u516b\u652f\u5f29\u7bad\u7531\u5730\u4e0b\u7684\u6d1e \u5185\u5c04\u51fa\u3002 \u3000\u3000\u7bad\u529b\u5f3a\u52b2\uff0c\u7adf\u7136\u628a\u4e0a\u9762\u7684\u5e8a\u677f\u5c04\u7a7f\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u672c\u6253\u7b97\u63ed\u5f00\u6728\u677f\u540e\uff0c\u5c31\u76f4\u63a5\u7531\u6d1e\u53e3\u8dc3\u4e0b\uff0c\u8fd9\u4e00\u6765\uff0c\u4f7f\u4ed6\u4e0d\u5f97\u4e0d\u6682\u65f6\u6309\u4e0b\u8fd9\u5ff5\u5934\uff0c \u540c\u65f6\u4e5f\u6df1\u6df1\u611f\u6fc0\u767d\u7fbd\u7684\u793a\u8b66\u3002 \u3000\u3000\u201c\u5c0f\u9053\u58eb\uff0c\u4f60\u600e\u4e48\u77e5\u9053\u5fc5\u6709\u5f29\u7bad\u5c04\u51fa\uff1f\u201d \u3000\u3000\u201c\u96be\u9053\u4f60\u63ed\u5f00\u6728\u677f\u7684\u4e00\u5239\u90a3\uff0c\u4e0d\u66fe\u542c\u5230\u6709\u5636\u5636\u4e4b\u58f0\u5417\u201d\u90a3\u6b63\u662f\u673a\u5173\u5728\u53d1\u52a8\uff0c\u81f3\u4e8e\u53d1\u5c04\u51fa \u6765\u7684\u662f\u4ec0\u4e48\uff1f\u90a3\u5c31\u65e0\u6cd5\u9884\u6599\u4e86\u3002\u201c \u3000\u3000\u201c\u82e5\u4e00\u63ed\u5f00\u6728\u677f\u4fbf\u6709\u5f29\u7bad\u5c04\u51fa\uff0c\u90a3\u4e48\u5979\u4eec\u81ea\u5df1\u4eba\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u201c\u5979\u4eec\u5f53\u7136\u6709\u63a7\u5236\u5f29\u7bad\u7684\u529e\u6cd5\uff0c\u54b1\u4eec\u56e0\u4e3a\u4e0d\u660e\u5e95\u7ec6\uff0c\u624d\u4f1a\u6709\u5f29\u7bad\u53d1\u51fa\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u54b1\u4eec\u662f\u65e0\u6cd5\u8fdb\u5165\u79d8\u9053\u4e86\uff1f\u201d \u3000\u3000\u201c\u90a3\u4e5f\u4e0d\u7136\u3002\u201d \u3000\u3000\u201c\u4f60\u6709\u4ec0\u4e48\u529e\u6cd5\uff1f\u201d \u3000\u3000\u201c\u636e\u6211\u5224\u65ad\uff0c\u8fd9\u5f29\u7bad\u53ea\u80fd\u5728\u63ed\u52a8\u6728\u677f\u65f6\u53d1\u51fa\u4e00\u6b21\uff0c\u9664\u975e\u4e0b\u9762\u6709\u4eba\u64cd\u7eb5\uff0c\u5426\u5219\u4fbf\u4e0d\u53ef\u80fd\u518d \u53d1\u3002\u201d \u3000\u3000\u201c\u4f9d\u4f60\u731c\u60f3\uff0c\u4e0b\u9762\u662f\u5426\u6709\u4eba\u8d1f\u8d23\u64cd\u7eb5\uff1f\u201d \u3000\u3000\u201c\u4e0d\u592a\u53ef\u80fd\uff0c\u4e0d\u8fc7\u54b1\u4eec\u5374\u4e07\u4e07\u4e0d\u80fd\u5927\u610f\u3002\u201d \u3000\u3000\u201c\u4f60\u8ba4\u4e3a\u4e0b\u4e00\u6b65\u8be5\u5982\u4f55\u884c\u52a8\uff1f\u6211\u4eec\u603b\u4e0d\u80fd\u5230\u6b64\u4e3a\u6b62\uff0c\u4e0d\u518d\u884c\u52a8\u3002\u201d \u3000\u3000\u201c\u5148\u5728\u8fd9\u91cc\u5b88\u4e00\u9635\u5b50\u518d\u8bf4\u5427\uff01\u201d \u3000\u3000\u4e8e\u662f\uff0c\u4e09\u4eba\u8c01\u90fd\u4e0d\u518d\u8bf4\u8bdd\uff0c\u9759\u5f85\u6709\u4f55\u52a8\u9759\u548c\u53cd\u5e94\uff1f\u8db3\u8db3\u987f\u996d\u5de5\u592b\u8fc7\u540e\uff0c\u6d1e\u4e2d\u59cb\u7ec8\u4e0d\u95fb\u534a \u70b9\u58f0\u606f\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u91cd\u53c8\u722c\u5230\u5e8a\u4e0b\uff0c\u7a81\u89c1\u4ed6\u5934\u4e0b\u811a\u4e0a\u817e\u8eab\u4fbf\u5411\u6d1e\u5185\u8dc3\u53bb\u3002 \u3000\u3000\u8fd9\u52a8\u4f5c\u592a\u5feb\u4e86\uff0c\u4e8b\u5148\u53c8\u6765\u7ecf\u8fc7\u5546\u8bae\uff0c\u767d\u7fbd\u548c\u601c\u82b1\u6839\u672c\u6765\u4e0d\u53ca\u963b\u6b62\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u7531\u7a7a\u4e2d\u843d\u4e0b\u6d1e\u5e95\uff0c\u5e76\u672a\u906d\u5230\u4efb\u4f55\u7a81\u88ad\uff0c\u800c\u4e14\u7531\u5ba4\u5185\u5730\u9762\u5230\u6d1e\u5e95\uff0c\u53ea\u6709\u4e00\u4e08\u591a\u6df1\uff0c \u843d\u5730\u4e4b\u540e\u4e5f\u672a\u53d1\u51fa\u534a\u70b9\u58f0\u606f\u3002 \u3000\u3000\u53ea\u542c\u767d\u7fbd\u5728\u4e0a\u9762\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u751a\u662f\u5173\u5fc3\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u4f20\u97f3\u9053\uff1a\u201c\u6ca1\u6709\u4e8b\uff0c\u4f60\u4eec\u653e\u5fc3\uff0c\u7b49\u6709\u4e86\u52a8\u9759\uff0c\u518d\u901a\u77e5\u4f60\u4eec\u3002\u201d \u3000\u3000\u5230\u4e86\u8fd9\u79cd\u5730\u6b65\uff0c\u767d\u7fbd\u600e\u80af\u8ba9\u5c0f\u71d5\u5b50\u4e00\u4eba\u6d89\u9669\uff0c\u968f\u5373\u4e5f\u8dc3\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u601c\u82b1\u89c1\u72b6\uff0c\u4e5f\u8ddf\u7740\u8dc3\u4e0b\u3002 \u3000\u3000\u5730\u9053\u5185\u6697\u5f97\u4f38\u624b\u4e0d\u89c1\u4e94\u6307\uff0c\u4f46\u56e0\u4e09\u4eba\u90fd\u6709\u7cbe\u7eaf\u8fc7\u4eba\u7684\u5185\u529f\uff0c\u4e0d\u4e45\u4e4b\u540e\uff0c\u4fbf\u53ef\u770b\u51fa\u4e08\u4f59\u5185 \u7684\u666f\u7269\u3002 \u3000\u3000\u8fd9\u5730\u9053\u5927\u7ea6\u4e09\u56db\u5c3a\u5bbd\uff0c\u4ec5\u53ef\u5bb9\u4e00\u4eba\u901a\u8fc7\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u6211\u5728\u524d\u9762\uff0c\u4f60\u4eec\u4e24\u4f4d\u8bf7\u5728\u540e\u9762\u4fdd\u6301\u8ddd\u79bb\u8ddf\u8fdb\u3002\u201d \u3000\u3000\u767d\u7fbd\u9053\uff1a\u201c\u201d\u4f60\u8981\u5c3d\u91cf\u8d70\u5728\u5730\u9053\u4e2d\u95f4\uff0c\u624b\u4e0d\u53ef\u78b0\u649e\u6d1e\u58c1\uff0c\u4ee5\u514d\u89e6\u52a8\u673a\u5173\u3002\u201c \u3000\u3000\u5c0f\u71d5\u5b50\u70b9\u70b9\u5934\uff0c\u5f53\u4e0b\u4e00\u624b\u4ed7\u5251\uff0c\u4e00\u638c\u62a4\u80f8\uff0c\u7f13\u6b65\u5411\u524d\u8d70\u53bb\u3002 \u3000\u3000\u5927\u7ea6\u5b9a\u4e86\u767e\u4f59\u6b65\uff0c\u5730\u9053\u5f00\u59cb\u8f6c\u5f2f\uff0c\u7a7a\u95f4\u4e5f\u5f00\u59cb\u6e10\u6e10\u5bbd\u9614\u8d77\u6765\u3002 \u3000\u3000\u53c8\u8d70\u4e86\u5341\u4f59\u6b65\uff0c\u5730\u9053\u51fa\u73b0\u5c94\u8def\uff0c\u5206\u6210\u4e09\u8def\u3002 \u3000\u3000\u8fd9\u4f7f\u8d70\u5728\u524d\u9762\u7684\u5c0f\u71d5\u5b50\u4e0d\u5f97\u4e0d\u6682\u65f6\u505c\u4e0b\u811a\u6b65\u3002 \u3000\u3000\u4ed6\u4eec\u60f3\u4e0d\u5230\u5730\u4e0b\u5de5\u7a0b\u7adf\u662f\u5982\u6b64\u4e4b\u5927\uff0c\u4e5f\u65e0\u6cd5\u51b3\u5b9a\u4e09\u6761\u8def\u7a76\u7adf\u8be5\u8d70\u90a3\u4e00\u6761\u597d\uff1f\u53e6\u5916\uff0c\u4ed6\u4eec \u4e5f\u60f3\u5230\u4e86\u82e5\u7ee7\u7eed\u8d70\u8fdb\u53bb\uff0c\u4e07\u4e00\u50cf\u8fdb\u5165\u8ff7\u9b42\u9635\uff0c\u88ab\u56f0\u5728\u91cc\u9762\uff0c\u5373\u4f7f\u6709\u5929\u5927\u7684\u672c\u9886\u4e5f\u5fc5\u5b9a\u65e0\u6cd5\u65bd \u5c55\u3002 \u3000\u3000\u4f46\u65e2\u7136\u6765\u4e86\uff0c\u5374\u53c8\u4e0d\u80fd\u534a\u9014\u800c\u5e9f\u3002 \u3000\u3000\u4e09\u4eba\u7ecf\u8fc7\u4e00\u756a\u8003\u8651\uff0c\u6700\u540e\u51b3\u5b9a\u8d70\u4e2d\u95f4\u7684\u4e00\u6761\u9053\u8def\u3002 \u3000\u3000\u4e0d\u51e0\u6b65\u540e\uff0c\u4fbf\u89c1\u4e24\u65c1\u6d1e\u58c1\u4e0a\u53c8\u51fa\u73b0\u4e86\u6d1e\u53e3\u3002 \u3000\u3000\u9884\u6599\u4e2d\u6d1e\u5185\u5fc5\u5b9a\u662f\u6d1e\u5ba4\uff0c\u4e5f\u5fc5\u5b9a\u662f\u9152\u5e97\u4eba\u5c45\u4f4f\u4e4b\u5904\uff0c\u8fd9\u8bc1\u660e\u4ed6\u4eec\u5df2\u8fdb\u5165\u4e86\u5730\u9053\u5185\u7684\u4e2d\u5fc3 \u5730\u5e26\u3002 \u3000\u3000\u4e09\u4eba\u8d8a\u53d1\u63d0\u9ad8\u8b66\u89c9\uff0c\u5e76\u51b3\u5b9a\u6682\u65f6\u4e0d\u64c5\u95ef\u6d1e\u53e3\uff0c\u4ee5\u514d\u6253\u8349\u60ca\u86c7\u3002 \u3000\u3000\u5f88\u5feb\u4fbf\u8d8a\u8fc7\u56db\u4e94\u5904\u6d1e\u58c1\u4e0a\u7684\u6d1e\u53e3\u3002 \u3000\u3000\u59cb\u7ec8\u4e0d\u95fb\u4eba\u58f0\u53d1\u51fa\u3002 \u3000\u3000\u8d8a\u662f\u5982\u6b64\uff0c\u8d8a\u4ee4\u5c0f\u71d5\u5b50\u7b49\u4e09\u4eba\u83ab\u6d4b\u9ad8\u6df1\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u4e00\u5904\u6d1e\u95e8\u5185\u95ea\u51fa\u5fae\u5f31\u7684\u706f\u5149\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u5927\u559c\uff0c\u7ec8\u4e8e\u627e\u5230\u5bf9\u65b9\u7684\u4eba\u4e86\uff0c\u4ed6\u6765\u4e0d\u53ca\u548c\u767d\u7fbd\u53ca\u601c\u82b1\u6253\u62db\u547c\uff0c\u4ed7\u5251\u4fbf\u8fc5\u901f\u65e0\u6bd4\u7684 \u51b2\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u901a\u8fc7\u4e00\u95f4\u9ed1\u6697\u7684\u6d1e\u5ba4\uff0c\u518d\u91cc\u9762\u4fbf\u662f\u706f\u5149\u53d1\u51fa\u7684\u6240\u5728\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u540d\u5e74\u8f7b\u5973\u4eba\u5750\u5728\u5e8a\u5934\uff0c\u53cc\u624b\u652f\u9890\u53d1\u7740\u5446\u3002 \u3000\u3000\u8fd9\u5973\u4eba\u8d6b\u7136\u6b63\u662f\u6628\u591c\u6240\u89c1\u5c0f\u6885\u79f0\u5979\u4e3a\u592b\u4eba\uff0c\u9177\u4f3c\u60dc\u6625\u7684\u7d20\u8863\u5973\u5b50\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u6beb\u4e0d\u8fdf\u7591\uff0c\u7eb5\u8eab\u4fbf\u5411\u90a3\u7d20\u8863\u5973\u5b50\u6251\u53bb\u3002 \u3000\u3000\u5c82\u77e5\u8fd8\u662f\u6162\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u5f53\u4ed6\u6251\u5230\u8ddf\u524d\uff0c\u5bf9\u65b9\u65e9\u5df2\u8fc5\u901f\u7684\u95ea\u8fdb\u4e00\u5904\u58c1\u95e8\uff0c\u970e\u65f6\u4eba\u5f71\u4e0d\u89c1\u3002 \u3000\u3000\u8fd9\u58c1\u95e8\u539f\u662f\u4e00\u4e2a\u6697\u95e8\uff0c\u8bbe\u6709\u673a\u5173\u64cd\u7eb5\uff0c\u53ea\u56e0\u7d20\u8863\u5973\u5b50\u6765\u4e0d\u53ca\u53d1\u52a8\u673a\u5173\u5173\u95ed\uff0c\u4eba\u95f4\u8fdb\u53bb\u4e4b \u540e\u58c1\u95e8\u4ecd\u662f\u5f00\u7740\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u5c82\u80fd\u5bb9\u5bf9\u65b9\u9038\u53bb\uff0c\u7acb\u5373\u4e5f\u95ea\u8eab\u5165\u5185\u8ffd\u53bb\u3002 \u3000\u3000\u7531\u4e8e\u4e24\u4eba\u8fdb\u5165\u58c1\u95e8\u5728\u65f6\u95f4\u4e0a\u76f8\u5dee\u65e0\u51e0\uff0c\u5c0f\u71d5\u5b50\u8fdb\u5165\u4e4b\u540e\uff0c\u4ecd\u80fd\u542c\u5230\u5bf9\u65b9\u5954\u5728\u524d\u65b9\u6025\u4fc3\u7684 \u811a\u6b65\u58f0\u3002 \u3000\u3000\u73b0\u5728\uff0c\u5c0f\u71d5\u5b50\u5df2\u987e\u4e0d\u5f97\u901a\u9053\u5185\u662f\u5426\u6709\u673a\u5173\u7981\u5236\uff0c\u53ea\u987e\u98de\u6b65\u5411\u524d\u8ffd\u53bb\u3002 \u3000\u3000\u5730\u9053\u5185\u7a7a\u95f4\u72ed\u7a84\uff0c\u65e0\u6cd5\u65bd\u5c55\u8f7b\u529f\uff0c\u5426\u5219\u5c0f\u71d5\u5b50\u6709\u7edd\u5bf9\u628a\u63e1\u5f88\u5feb\u4fbf\u53ef\u8ffd\u4e0a\u5bf9\u65b9\u3002 \u3000\u3000\u5927\u7ea6\u8ffd\u51fa\u6570\u5341\u6b65\u540e\uff0c\u5730\u9053\u5df2\u5c3d\uff0c\u5728\u5730\u9053\u5c3d\u5934\u53c8\u51fa\u73b0\u4e86\u4e00\u4e2a\u6d1e\u95e8\u3002 \u3000\u3000\u6beb\u65e0\u7591\u95ee\uff0c\u8fd9\u662f\u552f\u4e00\u7684\u901a\u8def\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u8ffd\u8fdb\u6d1e\u95e8\u540e\uff0c\u4f46\u89c1\u5149\u7ebf\u7531\u6697\u53d8\u660e\uff0c\u800c\u4e14\u6d1e\u58c1\u4e5f\u53d8\u6210\u4e86\u7816\u5899\u3002 \u3000\u3000\u5730\u9053\u5185\u90a3\u91cc\u6765\u7684\u7816\u5899\uff1f\u5f53\u5c0f\u71d5\u5b50\u518d\u8d8a\u8fc7\u4e00\u9053\u6728\u95e8\u65f6\uff0c\u624d\u53d1\u89c9\u8fd9\u91cc\u5df2\u662f\u51fa\u53e3\uff0c\u662f\u4e00\u95f4\u4e34\u8fd1 \u5c71\u58c1\u7684\u7816\u5c4b\u3002 \u3000\u3000\u51fa\u4e86\u7816\u5c4b\uff0c\u4ed6\u5f00\u59cb\u65bd\u5c55\u8f7b\u529f\uff0c\u4e00\u5f0f\u201c\u4e00\u9e64\u51b2\u5929\u201d\uff0c\u8eab\u5b50\u76f4\u62d4\u8d77\u4e94\u516d\u4e08\u9ad8\uff0c\u4eba\u5728\u7a7a\u4e2d\u501f\u7740 \u6708\u5149\u5bf9\u5468\u8fd1\u5341\u4f59\u4e08\u5185\u7684\u666f\u7269\uff0c\u5168\u6536\u773c\u5e95\u3002 \u3000\u3000\u4ed6\u7ec8\u5e72\u77a5\u89c1\u7d20\u8863\u5973\u5b50\u5728\u4e03\u516b\u5341\u5916\u5954\u884c\uff0c\u800c\u4e14\u4e5f\u65bd\u5c55\u7740\u8f7b\u529f\u3002 \u3000\u3000\u518d\u5f80\u524d\u884c\uff0c\u4fbf\u662f\u4e00\u5ea7\u65ad\u58c1\u6b8b\u57a3\u7684\u7834\u5e99\u3002 \u3000\u3000\u7d20\u8863\u5973\u5b50\u6b63\u5411\u7834\u5e99\u65b9\u5411\u5954\u53bb\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u51e0\u4e2a\u7eb5\u8dc3\u4e4b\u540e\u5230\u8fbe\u5e99\u524d\u3002 \u3000\u3000\u8fd9\u65f6\u7d20\u8863\u5973\u5b50\u65e9\u5df2\u6d88\u901d\u5728\u5e99\u95e8\u5185\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u8fdb\u5165\u5e99\u5185\u4e4b\u540e\uff0c\u7acb\u5373\u7eb5\u8eab\u8dc3\u4e0a\u5927\u6bbf\u5c4b\u9876\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u5ea7\u5e99\u5b87\u867d\u7136\u6b8b\u7834\u4e0d\u582a\uff0c\u89c4\u6a21\u5374\u4e0d\u5c0f\uff0c\u82e5\u9010\u5c4b\u641c\u7d22\uff0c\u5bf9\u65b9\u5f88\u53ef\u80fd\u4fbf\u4f1a\u501f\u673a\u6e9c\u6389\uff0c\u5982 \u4eca\u8dc3\u4e0a\u6574\u5ea7\u5e99\u5b87\u6700\u9ad8\u70b9\u7684\u5927\u6bbf\u5c4b\u9876\uff0c\u9664\u975e\u5bf9\u65b9\u9690\u85cf\u5728\u5e99\u5185\u4e0d\u52a8\uff0c\u5426\u5219\u53ea\u8981\u4e00\u51fa\u5e99\u4fbf\u79bb\u4e0d\u5f00\u4ed6 \u7684\u89c6\u7ebf\u3002 \u3000\u3000\u73b0\u5728\uff0c\u5c0f\u71d5\u5b50\u5fc5\u987b\u7b49\u5019\u767d\u7fbd\u548c\u601c\u82b1\u5230\u6765\uff0c\u624d\u80fd\u8fdb\u884c\u4e0b\u4e00\u6b65\u884c\u52a8\u3002\u767d\u7fbd\u548c\u601c\u82b1\u5f88\u5feb\u4fbf\u8ffd\u4e86 \u4e0a\u6765\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u53eb\u9053\uff1a\u201c\u6211\u5728\u8fd9\u91cc\uff01\u201d \u3000\u3000\u767d\u7fbd\u5f80\u4e0a\u671b\u4e86\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u5148\u524d\u7a76\u7adf\u5728\u8ffd\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u5c31\u662f\u90a3\u540d\u957f\u5f97\u548c\u60dc\u6625\u4e00\u6a21\u4e00\u6837\u7684\u7d20\u8863\u5973\u5b50\u3002\u201d \u3000\u3000\u201c\u662f\u4e0d\u662f\u8ffd\u4e22\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e5f\u8bb8\u4ecd\u6709\u529e\u6cd5\u628a\u5979\u8ffd\u5230\u3002\u201d \u3000\u3000\u201c\u8fd9\u8bdd\u600e\u4e48\u8bb2\uff1f\u201d \u3000\u3000\u201c\u6211\u773c\u89c1\u90a3\u5973\u5b50\u9003\u8fdb\u8fd9\u5ea7\u5e99\u91cc\uff0c\u5c31\u7acb\u523b\u8dc3\u5230\u5927\u6bbf\u5c4b\u9876\uff0c\u6211\u4fdd\u8bc1\u90a3\u5973\u5b50\u7edd\u672a\u9003\u51fa\u8fd9\u5ea7\u5e99\uff0c \u53ea\u7b49\u4f60\u548c\u601c\u82b1\u59d1\u5a18\u5230\u6765\u4ee5\u540e\uff0c\u9010\u5c4b\u641c\u7d22\uff0c\u6211\u5fc5\u987b\u4ecd\u5728\u8fd9\u91cc\u8d1f\u8d23\u76d1\u89c6\u3002\u201d \u3000\u3000\u201c\u4e07\u4e00\u5e99\u5185\u53c8\u6709\u5730\u9053\u901a\u5f80\u522b\u5904\u5462\uff1f\u201d \u3000\u3000\u201c\u90a3\u5c31\u7b49\u4e8e\u88ab\u5979\u9003\u8131\u4e86\u3002\u4e0d\u8fc7\u4f60\u548c\u601c\u82b1\u8fd8\u8981\u4ed4\u7ec6\u641c\u67e5\u4e00\u904d\u624d\u6210\u3002\u201d \u3000\u3000\u767d\u7fbd\u548c\u601c\u82b1\u521a\u8981\u9010\u5c4b\u641c\u7d22\uff0c\u53ea\u542c\u4e00\u95f4\u8033\u623f\u5185\u4f20\u51fa\u4e00\u4e2a\u5973\u5b50\u58f0\u97f3\u9053\uff1a\u201c\u5c0f\u9053\u58eb\u548c\u601c\u82b1\u59d0\u4e0d \u5fc5\u641c\u4e86\uff0c\u6211\u77e5\u9053\u5df2\u7ecf\u65e0\u5904\u53ef\u8eb2\uff0c\u5c31\u5e72\u8106\u51fa\u6765\u89c1\u4f60\u4eec\u5427\uff01\u5927\u5bb6\u90fd\u662f\u719f\u4eba\u4e86\uff0c\u76f8\u4fe1\u4f60\u4eec\u4e5f\u4e0d\u4f1a\u628a \u6211\u600e\u6837\uff01\u201d \u3000\u3000\u8bdd\u58f0\u752b\u843d\uff0c\u8033\u623f\u5185\u8d70\u51fa\u4e00\u540d\u7d20\u8863\u5973\u5b50\uff0c\u6b63\u662f\u60dc\u6625\u3002 \u3000\u3000\u601c\u82b1\u4e00\u4e2a\u7bad\u6b65\u8ddf\u8fc7\u53bb\uff0c\u9053\uff1a\u201c\u60dc\u6625\uff0c\u60f3\u4e0d\u5230\u771f\u7684\u662f\u4f60\uff1f\u201d \u3000\u3000\u60dc\u6625\u75db\u82e6\u7684\u6447\u6447\u5934\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u4f60\u4eec\u60f3\u4e0d\u5230\u7684\u4e8b\u60c5\u592a\u591a\u4e86\uff0c\u8fde\u6211\u81ea\u5df1\u4e5f\u60f3\u4e0d\u5230\uff0c\u79bb\u5f00 \u5bab\u4e2d\u540e\uff0c\u4f1a\u5728\u6f7c\u5173\u2018\u6843\u6797\u5c45\u9152\u5e97\u2019\u5f53\u5dee\uff01\u201d \u3000\u3000\u8fd9\u65f6\u5c0f\u71d5\u4e8e\u7531\u5927\u6bbf\u9876\u4e0a\u8dc3\u4e0b\uff0c\u548c\u767d\u7fbd\u4e00\u9f50\u8ddf\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u60dc\u6625\u671b\u4e86\u4e24\u4eba\u4e00\u773c\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4eec\u4f1a\u6709\u5f88\u591a\u8bdd\u95ee\u6211\uff0c\u73b0\u5c31\u5230\u6bbf\u91cc\u8c08\u5427\uff01\u4f60\u4eec\u60f3\u95ee\u4ec0 \u4e48\uff1f\u53ea\u8981\u6211\u77e5\u9053\u7684\uff0c\u4e00\u5b9a\u4ece\u5b9e\u56de\u7b54\u3002\u201d \u3000\u3000\u56db\u4eba\u8fdb\u5165\u5927\u6bbf\uff0c\u56e0\u4e3a\u6ca1\u5ea7\u4f4d\uff0c\u53ea\u6709\u7ad9\u7740\u8bb2\u8bdd\u3002 \u3000\u3000\u601c\u82b1\u9996\u5148\u5f00\u53e3\u9053\uff1a\u201c\u60dc\u6625\uff0c\u54b1\u4eec\u4e24\u4eba\u4e00\u5411\u60c5\u540c\u59d0\u59b9\uff0c\u5c31\u7531\u6211\u95ee\u4f60\u51e0\u53e5\u8bdd\u5427\uff01\u201d \u3000\u3000\u201c\u4f60\u53ea\u7ba1\u95ee\u3002\u201d \u3000\u3000\u201c\u2018\u6843\u6797\u5c45\u2019\u7684\u4e3b\u6301\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u60dc\u6625\u5374\u53cd\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u5fc5\u5b9a\u4e8b\u5148\u5df2\u542c\u8bf4\u8fc7\uff1f\u201d \u3000\u3000\u601c\u82b1\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u4eec\u5728\u6d1b\u9633\u4fbf\u542c\u8bf4\u8fc7\u662f\u7531\u4e00\u540d\u53eb\u9a6c\u5be1\u5987\u7684\u4e3b\u6301\uff0c\u751a\u81f3\u6709\u4eba\u628a \u2018\u6843\u6797\u5c45\u2019\u76f4\u63a5\u79f0\u4e3a\u2018\u9a6c\u5be1\u5987\u9152\u5e97\u2019\u3002\u201d \u3000\u3000\u201c\u4f60\u4eec\u7684\u6d88\u606f\u5f88\u6b63\u786e\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u9a6c\u5be1\u5987\u600e\u4e48\u4f1a\u53d8\u6210\u4f60\u5462\uff1f\u201d \u3000\u3000\u60dc\u6625\u6014\u4e86\u4e00\u6014\u9053\uff1a\u201c\u8c01\u8bf4\u6211\u662f\u9a6c\u5be1\u5987\uff1f\u201d \u3000\u3000\u601c\u82b1\u4e00\u672c\u6b63\u7ecf\u7684\u9053\uff1a\u201c\u73b0\u5728\u8bf4\u8d77\u8be5\u662f\u524d\u5929\u591c\u95f4\u4e86\uff0c\u6211\u548c\u5c0f\u71d5\u5b50\u4f4f\u5728\u897f\u53a2\uff0c\u4eb2\u8033\u542c\u5230\u5c0f\u6885 \u79f0\u4f60\u592b\u4eba\uff0c\u800c\u4e14\u5bf9\u4f60\u4e00\u526f\u6bd5\u606d\u6bd5\u656c\u7684\u6837\u5b50\uff0c\u96be\u9053\u4f60\u8fd8\u4e0d\u627f\u8ba4\u662f\u2018\u6843\u6797\u5c45\u2019\u7684\u4e3b\u4eba\uff1f\u201d \u3000\u3000\u60dc\u6625\u6447\u7740\u5934\u51c4\u51c9\u4e00\u7b11\u9053\uff1a\u201c\u6211\u6765\u8fd9\u91cc\uff0c\u6700\u591a\u4e0d\u8fc7\u4e00\u4e2a\u6708\u65f6\u95f4\uff0c\u800c\u4e14\u6211\u4e5f\u4e0d\u59d3\u9a6c\uff0c\u4f60\u4eec\u600e \u4e48\u628a\u6211\u5f53\u6210\u4e86\u9a6c\u5be1\u5987\u5462\uff1f\u201d \u3000\u3000\u201c\u53ef\u662f\u5c0f\u6885\u4e3a\u4ec0\u4e48\u79f0\u4f60\u592b\u4eba\uff1f\u201d \u3000\u3000\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u5df2\u7ecf\u5ac1\u4eba\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u79bb\u4eac\u624d\u4e0d\u8fc7\u4e24\u4e09\u4e2a\u6708\uff0c\u8fd9\u4e48\u5feb\u5c31\u5ac1\u4eba\u4e86\uff1f\u201d \u3000\u3000\u201c\u5e76\u975e\u6211\u7684\u81ea\u613f\uff0c\u662f\u4e0a\u9762\u903c\u6211\u5ac1\u7684\uff0c\u800c\u4e14\u5bf9\u8c61\u4e5f\u662f\u7531\u4e0a\u9762\u6307\u5b9a\u7684\uff0c\u6211\u65e2\u65e0\u9009\u62e9\u7684\u4f59\u5730\uff0c \u4e5f\u65e0\u81ea\u4e3b\u7684\u6743\u5229\u3002\u201d \u3000\u3000\u201c\u4f60\u7684\u4e08\u592b\u662f\u8c01\uff1f\u53ef\u4e0d\u53ef\u4ee5\u544a\u8bc9\u6211\uff1f\u201d \u3000\u3000\u201c\u4f60\u89c1\u8fc7\uff0c\u6211\u4e0d\u60f3\u518d\u8fdb\u4e00\u6b65\u8bf4\u660e\u3002\u4ed6\u4e8e\u4e24\u5929\u524d\u79bb\u5f00\u2018\u6843\u6797\u5c45\u2019\uff0c\u4e0d\u8fc7\u8fc7\u4e9b\u5929\u8fd8\u4f1a\u56de\u6765 \u7684\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u9a6c\u5be1\u5987\u5462\uff1f\u201d \u3000\u3000\u201c\u76ee\u524d\u4e5f\u4e0d\u5728\u2018\u6843\u6797\u5c45\u2019\u3002\u201d \u3000\u3000\u201c\u90a3\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u201c\u548c\u6211\u90a3\u4e08\u592b\u4e00\u8d77\u8d70\u7684\uff0c\u8fc7\u4e9b\u5929\u4e5f\u4f1a\u4e00\u8d77\u56de\u6765\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u2018\u6843\u6797\u5c45\u2019\u7684\u4e3b\u4e8b\u8005\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u6211\u3002\u201d \u3000\u3000\u201c\u539f\u6765\u4f60\u7684\u6743\u52bf\u4e5f\u5f88\u5927\uff1f\u201d \u3000\u3000\u201c\u6211\u90a3\u4e08\u592b\u662f\u9a6c\u5be1\u5987\u7684\u52a9\u624b\uff0c\u73b0\u5728\u4ed6\u4eec\u4e24\u4eba\u5168\u79bb\u5f00\u4e86\uff0c\u5f53\u7136\u6211\u662f\u4ee3\u7406\u4eba\u3002\u201d \u3000\u3000\u201c\u4f60\u65e2\u7136\u521a\u505a\u5a18\u5b50\uff0c\u4e08\u592b\u53c8\u5728\uff0c\u4e3a\u4ec0\u4e48\u7a7f\u7740\u4e00\u8eab\u7d20\u670d\uff1f\u201d \u3000\u3000\u201c\u662f\u6211\u4e08\u592b\u89c4\u5b9a\u7684\u3002\u201d \u3000\u3000\u201c\u4ed6\u600e\u4e48\u4f1a\u6709\u8fd9\u79cd\u5947\u602a\u7684\u89c4\u5b9a\uff1f\u201d \u3000\u3000\u201c\u4ed6\u62c5\u5fc3\u9a6c\u5be1\u5987\u5403\u918b\uff0c\u4e3a\u7684\u662f\u8ba8\u597d\u9a6c\u5be1\u5987\u3002\u201d \u3000\u3000\u601c\u82b1\u5927\u611f\u4e0d\u89e3\u7684\u9053\uff1a\u201c\u60dc\u6625\uff0c\u4f60\u8d8a\u8bf4\uff0c\u6211\u53cd\u800c\u8d8a\u7cca\u6d82\u4e86\uff01\u201d \u3000\u3000\u60dc\u6625\u5ffd\u7136\u773c\u7736\u4e00\u6e7f\uff0c\u4e24\u884c\u6e05\u6cea\u987a\u816e\u800c\u4e0b\uff0c\u555c\u6ce3\u7740\u9053\uff1a\u201c\u601c\u82b1\u59d0\uff0c\u6211\u4e0d\u8be5\u79bb\u5f00\u5b98\u4e2d\uff0c \u6211\u2026\u2026\u73b0\u5728\u662f\u4e16\u754c\u4e0a\u6700\u4e0d\u5e78\u4e5f\u6700\u53ef\u601c\u7684\u5973\u4eba\u4e86\uff01\u201d \u3000\u3000\u201c\u6709\u8bdd\u522b\u85cf\u5728\u5fc3\u91cc\uff0c\u6211\u4eec\u60c5\u540c\u59d0\u59b9\uff0c\u5c0f\u9053\u58eb\u548c\u5c0f\u71d5\u5b50\u4e5f\u4e0d\u662f\u5916\u4eba\uff0c\u5c3d\u7ba1\u8bf4\u51fa\u6765\uff0c\u5982\u679c\u9700 \u8981\u6211\u4eec\u5e2e\u5fd9\u7684\u5730\u65b9\uff0c\u6211\u4eec\u4e00\u5b9a\u4f1a\u5c3d\u529b\u7684\u3002\u201d \u3000\u3000\u60dc\u6625\u62ed\u7740\u6cea\u6c34\uff0c\u4f46\u4e24\u773c\u4f9d\u7136\u6cea\u5149\u6676\u83b9\uff0c\u62bd\u564e\u7740\u9053\uff1a\u201c\u6211\u90a3\u4e08\u592b\uff0c\u8bba\u5e74\u7eaa\u8db3\u8db3\u5927\u4e86\u6211\u4e24 \u500d\uff0c\u5374\u53c8\u6027\u597d\u6e14\u8272\uff0c\u7ecf\u5e38\u5728\u5916\u62c8\u82b1\u60f9\u8349\uff0c\u5c45\u7136\u548c\u9a6c\u5be1\u5987\u6253\u5f97\u706b\u70ed\uff0c\u8fd9\u5c31\u662f\u4ed6\u903c\u6211\u7a7f\u7d20\u8863\u8ba8\u597d \u9a6c\u5be1\u5987\u7684\u539f\u56e0\u3002\u201d \u3000\u3000\u201c\u542c\u8bf4\u9a6c\u5be1\u5987\u9887\u6709\u51e0\u5206\u59ff\u8272\uff0c\u624d\u4e8c\u5341\u591a\u5c81\uff0c\u4f60\u4e08\u592b\u65e2\u7136\u5e74\u7eaa\u5927\uff0c\u9a6c\u5be1\u5987\u600e\u4e48\u4f1a\u559c\u6b22\u4e0a\u4ed6 \u5462\uff1f\u201d \u3000\u3000\u201c\u9a6c\u5be1\u5987\u6c34\u6027\u6768\u82b1\uff0c\u4eba\u5c3d\u53ef\u592b\uff0c\u9762\u9996\u4e5f\u8bb8\u4e0d\u6b62\u4ed6\u4e00\u4e2a\uff0c\u4e0d\u8fc7\u76ee\u524d\u4ed6\u4eec\u6253\u5f97\u706b\u70ed\u5374\u662f\u5343\u771f \u4e07\u786e\u7684\u4e8b\uff0c\u56e0\u4e3a\u6211\u90a3\u7537\u4eba\u5728\u67d0\u4e00\u65b9\u9762\u2026\u2026\u201d \u3000\u3000\u60dc\u6625\u672c\u610f\u60f3\u8bf4\u201c\u5728\u67d0\u4e00\u65b9\u9762\u53ef\u4ee5\u6ee1\u8db3\u5979\u201d\uff0c\u4f46\u662f\u8bdd\u5230\u5507\u8fb9\uff0c\u56e0\u89c9\u5f97\u8bf4\u51fa\u6765\u4e0d\u96c5\uff0c\u4e0d\u5f97\u4e0d \u534a\u8def\u6253\u4f4f\u3002 \u3000\u3000\u601c\u82b1\u3001\u5c0f\u71d5\u5b50\u3001\u767d\u7fbd\u5f53\u7136\u731c\u5f97\u51fa\u501f\u6625\u4e0b\u9762\u7684\u8bdd\u662f\u4ec0\u4e48\uff0c\u4e5f\u5c31\u7528\u4e0d\u7740\u518d\u5411\u4e0b\u8ffd\u95ee\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u664c\uff0c\u601c\u82b1\u624d\u53c8\u642d\u8baa\u7740\u9053\uff1a\u201c\u521a\u624d\u4f60\u8bf4\u4f60\u90a3\u4e08\u592b\u6211\u89c1\u8fc7\uff0c\u73b0\u5e94\u8be5\u53ef\u4ee5\u544a\u8bc9\u6211\u4ed6\u662f\u8c01 \u4e86\uff1f\u201d \u3000\u3000\u60dc\u6625\u54ac\u4e86\u54ac\u7259\uff0c\u4e00\u5b57\u4e00\u5b57\u7684\u9053\uff1a\u201c\u5c31\u662f\u90a3\u4e2a\u53eb\u9f99\u5728\u5929\u7684\u7537\u4eba\uff01\u201d \u3000\u3000\u601c\u82b1\u767b\u65f6\u5fc3\u5934\u4e00\u9707\uff0c\u8138\u8272\u4e5f\u968f\u4e4b\u4e00\u53d8\u3002 \u3000\u3000\u5979\u5728\u968f\u4e09\u516c\u4e3b\u88ab\u63b3\u5230\u897f\u57df\u9b54\u6559\u9b54\u7a9f\u65f6\uff0c\u7684\u786e\u89c1\u8fc7\u8fd9\u7537\u4eba\u3002 \u3000\u3000\u601c\u82b1\u5bf9\u8fd9\u7537\u4eba\u4e0d\u4f46\u6068\u4e4b\u5165\u9aa8\uff0c\u4e5f\u6c38\u8fdc\u4e0d\u4f1a\u5fd8\u8bb0\uff0c\u56e0\u4e3a\u9f99\u5728\u5929\u6b63\u662f\u73b7\u6c61\u4e09\u516c\u4e3b\u4e3a\u4e09\u516c\u4e3b\u7834 \u8eab\u7684\u7537\u4eba\u3002 \u3000\u3000\u53ea\u542c\u60dc\u6625\u957f\u957f\u53f9\u606f\u4e00\u4e0b\uff0c\u518d\u9053\uff1a\u201c\u601c\u82b1\u59d0\uff0c\u73b0\u5728\u4f60\u8be5\u660e\u767d\u6211\u662f\u591a\u4e48\u4e0d\u5e78\u4e86\u5427\uff01\u7adf\u7136\u88ab\u6307 \u5b9a\u5ac1\u7ed9\u8fd9\u6837\u7684\u4e00\u4e2a\u7537\u4eba\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u63a5\u53e3\u9053\uff1a\u201c\u4f60\u867d\u7136\u4e0d\u5e78\uff0c\u4f46\u884c\u52a8\u5e76\u672a\u53d7\u5230\u7ea6\u675f\uff0c\u5c3d\u53ef\u8bbe\u6cd5\u9003\u8d70\u79bb\u5f00\u4ed6\uff0c\u4f60\u8fd8\u5e74 \u8f7b\uff0c\u53c8\u6709\u4e00\u8eab\u6b66\u529f\uff0c\u96be\u9053\u8fd8\u6015\u4e0d\u80fd\u72ec\u7acb\u751f\u6d3b\uff1f\u201d \u3000\u3000\u60dc\u6625\u75db\u82e5\u7684\u6447\u6447\u5934\u9053\uff1a\u201c\u53ef\u60dc\u6211\u7684\u884c\u52a8\u5df2\u7ecf\u4e0d\u81ea\u7531\u4e86\uff0c\u8fde\u8fd9\u6761\u547d\u4e5f\u5b8c\u5168\u638c\u63e1\u5728\u4ed6\u7684\u624b \u91cc\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u4e0d\u89c9\u4e00\u5446\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u539f\u56e0\uff1f\u201d \u3000\u3000\u60dc\u6625\u53cc\u7738\u53c8\u95ee\u8d77\u6cea\u5149\uff0c\u9053\uff1a\u201c\u4ed6\u5df2\u5728\u6211\u4f53\u5185\u4e0b\u4e86\u4e00\u79cd\u5de8\u6bd2\uff0c\u8fd9\u662f\u9b54\u6559\u5c0f\u897f\u5929\u96f7\u97f3\u5bfa\u72ec\u95e8 \u7279\u6709\u7684\u6bd2\u836f\uff0c\u89e3\u836f\u63a7\u5236\u5728\u4ed6\u624b\u91cc\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u8981\u591a\u4e45\u624d\u80fd\u53d1\u4f5c\u4e00\u6b21\u3002\u201d \u3000\u3000\u201c\u5341\u5929\u3002\u201d \u3000\u3000\u201c\u4e07\u4e00\u9f99\u5728\u5929\u5341\u5929\u4e4b\u5185\u4e0d\u80fd\u56de\u6765\uff0c\u4f60\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u201c\u4ed6\u62c5\u5fc3\u6709\u8fd9\u79cd\u53ef\u80fd\uff0c\u6240\u4ee5\u4e34\u8d70\u65f6\u7ed9\u4e86\u6211\u4e24\u7c92\u89e3\u836f\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\uff0c\u4f60\u53ef\u4ee5\u5728\u4e8c\u5341\u5929\u5185\u4f53\u5185\u5de8\u6bd2\u4e0d\u81f4\u53d1\u4f5c\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u524d\u5929\u591c\u91cc\u4f60\u5230\u540e\u9662\u53bb\uff0c\u96be\u9053\u4e0d\u77e5\u9053\u540e\u9662\u91cc\u4f4f\u7684\u662f\u6211\u4eec\uff1f\u5982\u679c\u77e5 \u9053\uff0c\u4f60\u5c31\u8be5\u60f3\u5230\u53ef\u80fd\u88ab\u6211\u4eec\u53d1\u73b0\uff1f\u201d \u3000\u3000\u60dc\u6625\u82e6\u7b11\u9053\uff1a\u201c\u6211\u5f53\u7136\u77e5\u9053\u540e\u9662\u91cc\u4f4f\u7684\u662f\u4f60\u4eec\u4e09\u4eba\uff0c\u800c\u4e14\u5f53\u5c0f\u6885\u6765\u8bf7\u793a\u662f\u5426\u53ef\u4ee5\u7559\u5ba2\uff0c \u662f\u6211\u51c6\u8bb8\u5979\u8981\u4f60\u4eec\u4f4f\u8fdb\u6765\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u8fd9\u4e48\u505a\uff1f\u90a3\u4e0d\u7b49\u4e8e\u81ea\u627e\u9ebb\u70e6\uff1f\u201d \u3000\u3000\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u5f53\u65f6\u6211\u771f\u60f3\u4e3b\u52a8\u56de\u5230\u4f60\u4eec\u8fd9\u8fb9\uff0c\u518d\u89c1\u4e09\u516c\u4e3b\u4e00\u9762\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u53c8\u6539\u53d8\u4e86\u4e3b\u610f\uff1f\u201d \u3000\u3000\u201c\u8fd8\u4e0d\u662f\u4e3a\u4e86\u4fdd\u4f4f\u4e00\u6761\u547d\u80fd\u6d3b\u4e0b\u53bb\uff0c\u6211\u82e5\u56de\u5230\u4e09\u516c\u4e3b\u8eab\u8fb9\uff0c\u53c8\u5230\u54ea\u91cc\u53bb\u627e\u89e3\u836f\u5462\uff1f\u201d \u3000\u3000\u201c\u53ef\u662f\u4f60\u73b0\u5728\u2026\u2026\u201d \u3000\u3000\u201c\u73b0\u5728\u5df2\u88ab\u4f60\u4eec\u8ffd\u5230\uff0c\u6211\u5c31\u51b3\u5b9a\u968f\u4f60\u4eec\u8d70\uff0c\u80fd\u6d3b\u591a\u4e45\u5c31\u6d3b\u591a\u4e45\uff0c\u53ea\u8981\u518d\u89c1\u4e09\u516c\u4e3b\u4e00\u9762\uff0c \u6b7b\u4e86\u4e5f\u503c\u5f97\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u671b\u671b\u767d\u7fbd\uff0c\u518d\u671b\u671b\u601c\u82b1\u3002 \u3000\u3000\u4e24\u4eba\u7686\u662f\u4e00\u8138\u540c\u60c5\u4e4b\u8272\uff0c\u5374\u4e0d\u77e5\u8be5\u8bf4\u4ec0\u4e48\u597d\u3002 \u3000\u3000\u8bb8\u4e45\u2026\u2026 \u3000\u3000\u601c\u82b1\u624d\u95ee\u9053\uff1a\u201c\u4f60\u8fd8\u6ca1\u8bf4\u660e\u9a6c\u5be1\u5987\u548c\u9f99\u5728\u5929\u90fd\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u53bb\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u60dc\u6625\u4e0d\u7b54\u53cd\u95ee\u9053\uff1a\u201c\u4f60\u8bf7\u5148\u8bf4\u8bf4\u5927\u5bb6\u966a\u7740\u4e09\u516c\u4e3b\u6765\u5230\u6f7c\u5173\u7684\u76ee\u7684\uff1f\u201d \u3000\u3000\u201c\u539f\u6765\u4f60\u5bf9\u6211\u4eec\u7684\u884c\u52a8\u5df2\u7ecf\u5b8c\u5168\u6e05\u695a\u3002\u201d \u3000\u3000\u201c\u2018\u6843\u6797\u5c45\u2019\u662f\u897f\u57df\u9b54\u6559\u4e13\u8bbe\u7684\u60c5\u62a5\u7ad9\uff0c\u4f60\u4eec\u7684\u884c\u52a8\uff0c\u9a6c\u5be1\u5987\u65e9\u5df2\u5f97\u77e5\u3002\u81f3\u4e8e\u6211\uff0c\u81ea\u7136 \u662f\u4ece\u9a6c\u5be1\u5987\u90a3\u91cc\u5f97\u6765\u7684\u6d88\u606f\u3002\u201d \u3000\u3000\u201c\u8c01\u628a\u6d88\u606f\u4f20\u7ed9\u9a6c\u5be1\u5987\u7684\u5462\uff1f\u201d \u3000\u3000\u201c\u9664\u4e86\u897f\u57df\u9b54\u6559\u5404\u5904\u5e03\u4e0b\u7684\u773c\u7ebf\uff0c\u53e6\u5916\u9a6c\u5de1\u629a\u548c\u8d75\u4e8c\u5824\u662f\u8d1f\u8d23\u4f20\u9012\u6d88\u606f\u3002\u201d \u3000\u3000\u201c\u83ab\u975e\u9a6c\u5be1\u5987\u548c\u9a6c\u6587\u4e2d\u3001\u8d75\u4e8c\u5824\u4e5f\u76f4\u63a5\u6709\u6765\u5f80\uff1f\u201d \u3000\u3000\u201c\u5c82\u6b62\u6709\u6765\u5f80\uff0c\u636e\u8bf4\u9a6c\u5be1\u5987\u8fd8\u662f\u8d75\u4e8c\u5824\u7684\u60c5\u5987\uff0c\u8d75\u4e8c\u5824\u6bcf\u6b21\u7531\u6d1b\u9633\u5230\u897f\u57df\uff0c\u4e2d\u9014\u5fc5\u5b9a\u4f4f \u5728\u2018\u6843\u6797\u5c45\u2019\u3002\u201d \u3000\u3000\u201c\u9a6c\u5be1\u5987\u65e2\u7136\u6d88\u606f\u5982\u6b64\u7075\u901a\uff0c\u4f60\u4e5f\u5fc5\u5b9a\u77e5\u9053\u6211\u4eec\u966a\u4e09\u516c\u4e3b\u524d\u6765\u6f7c\u5173\u7684\u76ee\u7684\u4e86\uff1f\u201d \u3000\u3000\u201c\u662f\u6765\u902e\u6355\u6842\u98d8\u9999\u7b49\u4e09\u540d\u5996\u5973\u4e3a\u4e09\u516c\u4e3b\u7597\u866b\uff0c\u4e0d\u9519\u5427\uff1f\u201d \u3000\u3000\u201c\u90a3\u4e48\u6842\u98d8\u9999\u7b49\u4e09\u540d\u5996\u5973\u7a76\u7adf\u5230\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u6bd4\u4f60\u4eec\u65e9\u5230\u4e00\u5929\u3002\u201d \u3000\u3000\u601c\u82b1\u8bf4\u4e0d\u51fa\u662f\u60ca\u662f\u559c\uff0c\u554a\u4e86\u58f0\u9053\uff1a\u201c\u5979\u4eec\u73b0\u5728\u4eba\u5728\u54ea\u91cc\uff1f\u5feb\u5feb\u5e26\u6211\u4eec\u53bb\u62ff\u4eba\uff0c\u5982\u679c\u628a\u5979 \u4eec\u902e\u6355\u5230\u4e86\uff0c\u7acb\u4e0b\u5934\u529f\u7684\u5c31\u662f\u4f60\uff01\u201d \u3000\u3000\u60dc\u6625\u6447\u5934\u9053\uff1a\u201c\u53ef\u60dc\u665a\u4e86\uff01\u201d \u3000\u3000\u601c\u82b1\u4e00\u5446\u9053\uff1a\u201c\u600e\u4e48\u665a\u4e86\uff1f\u201d \u3000\u3000\u60dc\u6625\u5401\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u4f60\u4eec\u665a\u6765\u4e86\u4e00\u5929\u3002\u201d \u3000\u3000\u201c\u83ab\u975e\u5979\u4eec\u5df2\u7ecf\u8d70\u4e86\uff1f\u201d \u3000\u3000\u201c\u9a6c\u5be1\u5987\u77e5\u9053\u4f60\u4eec\u7b2c\u4e09\u5929\u5fc5\u5230\uff0c\u5c31\u5728\u5934\u5929\u591c\u91cc\u548c\u9f99\u5728\u5929\u628a\u6842\u98d8\u9999\u4e09\u540d\u5996\u5973\u9001\u8d70\u4e86\uff0c\u90a3\u4e09 \u540d\u5996\u5973\u6765\u5230\u6f7c\u5173\uff0c\u6839\u672c\u4e0d\u66fe\u505c\u7559\u3002\u201d \u3000\u3000\u201c\u9001\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u201c\u7ec8\u5357\u5c71\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u8981\u9001\u5230\u7ec8\u4ea9\u5c71\uff1f\u201d \u3000\u3000\u201c\u90a3\u91cc\u6709\u897f\u57df\u9b54\u6559\u4e00\u5904\u5206\u575b\uff0c\u9a6c\u5be1\u5987\u6b63\u53d7\u90a3\u5206\u575b\u6240\u8f96\u3002\u201d \u3000\u3000\u201c\u4f60\u53ef\u77e5\u9053\u90a3\u5206\u575b\u7684\u5730\u70b9\uff1f\u201d \u3000\u3000\u201c\u6211\u5728\u6765\u2018\u6843\u6797\u5c45\u2019\u4e4b\u524d\u53bb\u8fc7\u4e00\u6b21\uff0c\u4f46\u4e00\u8def\u4e0a\u5374\u88ab\u8499\u7740\u773c\u775b\u3002\u201d \u3000\u3000\u201c\u8fdb\u5165\u90a3\u5904\u5206\u575b\u4e4b\u540e\uff0c\u662f\u5426\u4ecd\u8499\u7740\u773c\u775b\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u5df2\u53d6\u4e0b\u6211\u7684\u8499\u773c\u9ed1\u5e03\u3002\u201d \u3000\u3000\u201c\u90a3\u4f60\u5bf9\u9644\u8fd1\u73af\u5883\u603b\u8be5\u7559\u4e0b\u5370\u8c61\uff1f\u201d \u3000\u3000\u201c\u5206\u575b\u5468\u56f4\u5168\u662f\u60ac\u5d16\u7edd\u58c1\uff0c\u6839\u672c\u65e0\u6cd5\u770b\u5230\u5916\u9762\u53bb\u3002\u201d \u3000\u3000\u201c\u8303\u56f4\u6709\u591a\u5927\uff1f\u201d \u3000\u3000\u201c\u53ea\u6709\u6570\u4ea9\u65b9\u5706\u7684\u4e00\u7247\uff0c\u7ec8\u5357\u5c71\u7ef5\u4e98\u6570\u767e\u91cc\uff0c\u60f3\u627e\u5230\u8fd9\u4e48\u4e00\u4e2a\u5c0f\u5c0f\u7684\u5730\u65b9\uff0c\u90a3\u6839\u672c\u5c31\u662f \u5927\u6d77\u635e\u9488\u3002\u201d \u3000\u3000\u201c\u5206\u575b\u91cc\u6709\u591a\u5c11\u4eba\uff1f\u201d \u3000\u3000\u201c\u770b\u6765\u4eba\u6570\u5e76\u4e0d\u591a\uff0c\u5927\u7ea6\u53ea\u6709\u767e\u591a\u4eba\uff0c\u5f53\u7136\u4e5f\u8bb8\u6709\u7684\u6211\u6ca1\u770b\u5230\u3002\u201d \u3000\u3000\u601c\u82b1\u671b\u671b\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\uff0c\u89c6\u7ebf\u518d\u8f6c\u56de\u501f\u6625\u8138\u4e0a\uff0c\u90d1\u91cd\u5176\u4e8b\u7684\u9053\uff1a\u201c\u60dc\u6625\uff0c\u4f60\u63d0\u4f9b\u4e86\u8fd9\u4e48 \u591a\u5b9d\u8d35\u7684\u6d88\u606f\uff0c\u6211\u4eec\u5f88\u611f\u6fc0\u4f60\uff0c\u4e3a\u4e86\u4f60\u80fd\u6d3b\u547d\uff0c\u73b0\u5728\u4f60\u5c31\u56de\u2018\u6843\u6797\u5c45\u2019\u53bb\u5427\uff01\u56e0\u4e3a\u4f60\u8ddf\u4e86\u6211 \u4eec\uff0c\u4e0d\u53ef\u80fd\u518d\u6709\u89e3\u836f\u3002\u201d \u3000\u3000\u8c01\u77e5\u60dc\u6625\u5374\u8bed\u6c14\u575a\u5b9a\u7684\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u4e0d\u80fd\u518d\u56de\u53bb\u4e86\uff01\u201d \u3000\u3000\u201c\u8fd9\u662f\u4e3a\u4ec0\u4e48\uff1f\u4f60\u8fd8\u5e74\u8f7b\uff0c\u4f55\u82e6\u9001\u4e0a\u4e00\u547d\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u56de\u53bb\u4e5f\u662f\u6b7b\uff0c\u80fd\u6b7b\u5728\u4e09\u516c\u4e3b\u548c\u4f60\u4eec\u5927\u5bb6\u9762\u524d\uff0c\u603b\u6bd4\u88ab\u9a6c\u5be1\u5987\u548c\u9f99\u5728\u5929\u6298\u78e8\u6b7b\u8981 \u597d\u3002\u201d \u3000\u3000\u201c\u6211\u4e0d\u61c2\u4f60\u7684\u8bdd\u3002\u4f60\u73b0\u5728\u56de\u53bb\uff0c\u795e\u4e0d\u77e5\u9b3c\u4e0d\u89c9\uff0c\u6709\u4ec0\u4e48\u53ef\u987e\u8651\u7684\uff1f\u201d \u3000\u3000\u201c\u8c01\u8bf4\u795e\u4e0d\u77e5\u9b3c\u4e0d\u89c9\uff1f\u2018\u6843\u6797\u5c45\u2019\u5730\u4e0b\u79d8\u9053\u4e2d\u5df2\u6709\u4e0d\u5c11\u4eba\u770b\u5230\u6211\u88ab\u4f60\u4eec\u8ffd\u51fa\u51fa\u53e3\u4e4b\u5916\uff0c \u8bf4\u4e0d\u5b9a\u8fd9\u5ea7\u7834\u5e99\u56db\u5468\uff0c\u4e5f\u6709\u4ed6\u4eec\u7684\u4eba\uff0c\u53ea\u8981\u4ed6\u4eec\u628a\u8fd9\u60c5\u5f62\u5bc6\u544a\u9a6c\u5be1\u5987\u548c\u9f99\u5728\u5929\uff0c\u6211\u5c31\u96be\u4ee5\u6d3b \u547d\u3002\u201d \u3000\u3000\u201c\u7eb5\u7136\u9a6c\u5be1\u5987\u60f3\u8981\u4f60\u7684\u547d\uff0c\u4f46\u9f99\u5728\u5929\u5fc5\u4f1a\u4fdd\u62a4\u4f60\uff0c\u56e0\u4e3a\u4ed6\u548c\u4f60\u603b\u662f\u592b\u59bb\u3002\u201d \u3000\u3000\u201c\u4f60\u9519\u4e86\u3002\u9f99\u5728\u5929\u5df2\u88ab\u9a6c\u5be1\u5987\u8ff7\u660f\u4e86\u5934\uff0c\u800c\u4e14\u4ed6\u4e00\u5411\u8d8b\u708e\u9644\u52bf\uff0c\u5fc5\u987b\u60f3\u5c3d\u529e\u6cd5\u5949\u627f\u9a6c\u5be1 \u5987\uff0c\u5373\u4f7f\u9a6c\u5be1\u5987\u8981\u4ed6\u6740\u6b7b\u81ea\u5df1\u7684\u8001\u5b50\uff0c\u4ed6\u4e5f\u5fc5\u5b9a\u7167\u529e\u4e0d\u8bef\u3002\u201d \u3000\u3000\u201c\u9a6c\u5be1\u5987\u7684\u52bf\u529b\u5f88\u5927\u5417\uff1f\u201d \u3000\u3000\u201c\u5979\u662f\u897f\u57df\u9b54\u6559\u7684\u5927\u7ea2\u4eba\uff0c\u8fde\u6559\u4e3b\u90fd\u5bf9\u5979\u8a00\u542c\u8ba1\u4ece\uff0c\u8bf4\u4e0d\u5b9a\u4e24\u4eba\u4e4b\u95f4\u4e5f\u6709\u66a7\u6627\u5173\u7cfb\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\uff0c\u4f60\u662f\u51b3\u5b9a\u8ddf\u968f\u6211\u4eec\u8d70\u4e86\uff1f\u201d \u3000\u3000\u201c\u7528\u4e0d\u7740\u95ee\uff0c\u4e09\u516c\u4e3b\u76ee\u524d\u4eba\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u5728\u4e1c\u5927\u8857\u4e00\u5bb6\u5ba2\u6808\u3002\u201d \u3000\u3000\u201c\u90a3\u91cc\u8fd8\u6709\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u6709\u738b\u7edf\u9886\u548c\u6c5f\u5927\u4fa0\uff0c\u53e6\u5916\u8fd8\u6709\u597d\u51e0\u4f4d\uff0c\u4f60\u5168\u8ba4\u8bc6\u3002\u201d \u3000\u3000\u201c\u90a3\u592a\u597d\u4e86\uff0c\u6211\u80fd\u5728\u4e34\u6b7b\u4e4b\u524d\uff0c\u548c\u5927\u5bb6\u518d\u89c1\u4e00\u9762\uff0c\u4e5f\u6ca1\u4ec0\u4e48\u9057\u61be\u7684\u4e86\uff0c\u73b0\u5728\u5c31\u5e26\u6211\u5230\u5ba2 \u6808\u53bb\u5427\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u3001\u767d\u7fbd\u548c\u601c\u82b1\u5e26\u7740\u60dc\u6625\u56de\u5230\u4e1c\u5927\u8857\u90a3\u5ba2\u6808\u65f6\uff0c\u624d\u4e0d\u8fc7\u56db\u66f4\u5de6\u53f3\uff0c\u8fde\u95e8\u90fd\u662f\u53eb\u5f00 \u7684\u3002 \u3000\u3000\u542c\u8bf4\u60dc\u6625\u6765\u4e86\uff0c\u6240\u6709\u7684\u4eba\u90fd\u4ece\u7761\u68a6\u4e2d\u8d77\u6765\uff0c\u5306\u5306\u8d76\u5230\u5ba2\u5385\uff0c\u8fde\u4e09\u516c\u4e3b\u4e5f\u4e0d\u4f8b\u5916\u3002 \u3000\u3000\u60dc\u6625\u4e00\u89c1\u4e09\u516c\u4e3b\u4fbf\u6251\u8eab\u8dea\u5012\uff0c\u73e0\u6cea\u6ee1\u9762\u7684\u9053\uff1a\u201c\u5a62\u5b50\u8be5\u6b7b\uff0c\u4e0d\u7ba1\u5982\u4f55\uff0c\u5a62\u5b50\u603b\u7b97\u80fd\u5728\u6d3b \u7740\u7684\u65f6\u5019\u548c\u60a8\u518d\u89c1\u4e00\u9762\uff01\u201d \u3000\u3000\u4e09\u516c\u4e3b\u8fde\u5fd9\u6276\u8d77\u60dc\u6625\u9053\uff1a\u201c\u4f60\u82e5\u80af\u518d\u56de\u6765\uff0c\u6211\u9ad8\u5174\u8fd8\u6765\u4e0d\u53ca\uff0c\u8bf4\u4ec0\u4e48\u6d3b\u7740\u4e0d\u6d3b\u7740\uff0c\u4f60\u73b0 \u5728\u4e0d\u662f\u597d\u597d\u7684\u5417\uff1f\u201d \u3000\u3000\u601c\u82b1\u6025\u6025\u8bf4\u9053\uff1a\u201c\u7980\u4e09\u516c\u4e3b\uff0c\u5979\u6d3b\u4e0d\u4e86\u51e0\u5929\u4e86\uff0c\u6240\u4ee5\u624d\u4f1a\u8bf4\u51fa\u90a3\u79cd\u8bdd\u6765\uff01\u201d \u3000\u3000\u6b64\u8bed\u4e00\u51fa\uff0c\u6240\u6709\u4e0d\u77e5\u60c5\u7684\u4eba\u5168\u90fd\u6014\u5728\u5f53\u573a\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u554a\u4e86\u58f0\u9053\uff1a\u201c\u7a76\u7adf\u600e\u4e48\u56de\u4e8b\uff1f\u5feb\u8bf4\u660e\u767d\uff01\u201d \u3000\u3000\u601c\u82b1\u968f\u5373\u4e00\u4e94\u4e00\u5341\u7684\u628a\u6628\u665a\u6240\u6709\u7ecf\u8fc7\u8be6\u7ec6\u53d9\u8ff0\u4e86\u4e00\u904d\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u65e0\u9650\u5173\u5207\u7684\u9053\uff1a\u201c\u60dc\u6625\uff0c\u8fd8\u662f\u6d3b\u547d\u8981\u7d27\uff0c\u4f60\u73b0\u5728\u56de\u53bb\u8fd8\u6765\u5f97\u53ca\uff0c\u6211\u867d\u7136\u5e0c\u671b\u4f60\u518d \u56de\u5230\u8eab\u8fb9\uff0c\u4f46\u5374\u4e0d\u80fd\u4e0d\u987e\u4f60\u7684\u751f\u6b7b\uff0c\u4f60\u5c31\u662f\u8fd8\u60f3\u670d\u4f8d\u6211\uff0c\u53c8\u80fd\u670d\u4f8d\u51e0\u5929\u5462\uff1f\u201d \u3000\u3000\u60dc\u6625\u8bed\u5e26\u6fc0\u52a8\u7684\u9053\uff1a\u201c\u5a62\u5b50\u65e9\u5df2\u2026\u2026\u65e9\u5df2\u5bf9\u4ed6\u4eec\u4e09\u4f4d\u8bf4\u8fc7\u4e86\uff0c\u56de\u53bb\u4e5f\u662f\u6b7b\uff0c\u80fd\u6b7b\u5728\u4e09\u516c \u4e3b\u8eab\u8fb9\uff0c\u8be5\u662f\u5a62\u5b50\u5fc3\u7518\u60c5\u613f\u7684\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u4e09\u516c\u4e3b\u4f4e\u4e0b\u5934\uff0c\u7d27\u76b1\u7740\u9edb\u7709\uff0c\u9ed8\u9ed8\u65e0\u8bed\u3002 \u3000\u3000\u738b\u5f64\u5401\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u4e09\u516c\u4e3b\uff0c\u5c31\u7531\u5979\u5427\uff01\u6709\u6211\u4eec\u7167\u987e\u5979\uff0c\u4e5f\u7b97\u5c3d\u4e86\u4e00\u4efd\u8d23\u4efb\uff0c\u66f4\u4f55\u51b5\u5979 \u4e5f\u672a\u5fc5\u65e0\u6551\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u7d27\u63a5\u7740\u9053\uff1a\u201c\u738b\u8001\u5f1f\u8bf4\u5f97\u5bf9\uff0c\u53ea\u8981\u6349\u5230\u9a6c\u5be1\u5987\u548c\u90a3\u4e2a\u53eb\u9f99\u5728\u5929\u7684\uff0c\u5c31\u4e00\u5b9a\u53ef\u4ee5\u53d6 \u5f97\u89e3\u836f\u3002\u201d \u3000\u3000\u5f53\u6c5f\u5343\u91cc\u63d0\u5230\u9f99\u5728\u5929\u65f6\uff0c\u6240\u6709\u7684\u4eba\u90fd\u7559\u610f\u5230\u4e09\u516c\u4e3b\u5ffd\u7136\u795e\u8272\u9aa4\u53d8\uff0c\u8138\u8272\u6709\u5982\u8499\u4e0a\u4e00\u5c42\u5bd2 \u971c\uff0c\u4e00\u526f\u54ac\u7259\u5207\u9f7f\u6a21\u6837\u3002 \u3000\u3000\u4ed6\u4eec\u90a3\u91cc\u77e5\u9053\u4e09\u516c\u4e3b\u5bf9\u9f99\u5728\u5929\u7684\u75db\u6068\u5df2\u662f\u8a00\u8bed\u65e0\u6cd5\u5f62\u5bb9\uff0c\u56e0\u4e3a\u9f99\u5728\u5929\u66fe\u593a\u53bb\u5979\u7684\u8d1e\u64cd\uff0c \u800c\u5979\u53c8\u662f\u91d1\u6280\u5de5\u53f6\u4e4b\u8eab\u3002 \u3000\u3000\u53ea\u89c1\u4e09\u516c\u4e3b\u5168\u8eab\u6296\u52a8\u7740\uff0c\u7531\u5ea7\u4f4d\u4e0a\u7ad9\u8d77\uff0c\u9053\uff1a\u201c\u6211\u6709\u70b9\u4e0d\u8212\u670d\uff0c\u8981\u56de\u623f\u518d\u7761\u4e00\u4e0b\uff0c\u6709\u5173 \u4eca\u540e\u5982\u4f55\u884c\u52a8\uff0c\u5c31\u7531\u738b\u5927\u4eba\u548c\u6c5f\u53d4\u53d4\u5546\u91cf\u7740\u529e\u5427\uff01\u53ea\u8981\u5927\u5bb6\u51b3\u5b9a\u7684\u4e8b\uff0c\u6211\u4e00\u5b9a\u542c\u4ece\u3002\u201d \u3000\u3000\u601c\u82b1\u8fde\u5fd9\u6400\u6276\u7740\u4e09\u516c\u4e3b\u56de\u623f\u800c\u53bb\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u738b\u8001\u5f1f\u6709\u4ec0\u4e48\u6253\u7b97\uff1f\u201d \u3000\u3000\u738b\u5f64\u4f3c\u5df2\u80f8\u6709\u6210\u7af9\u7684\u9053\uff1a\u201c\u5929\u4eae\u540e\u5927\u5bb6\u5373\u523b\u52a8\u8eab\uff0c\u4e5f\u8d76\u5f80\u7ec8\u5357\u5c71\u53bb\u3002\u201d \u3000\u3000\u738b\u5f64\u70b9\u70b9\u5934\u9053\uff1a\u201c\u5144\u5f1f\u4e5f\u6b63\u6709\u6b64\u610f\uff0c\u53ea\u662f\u5982\u4f55\u80fd\u627e\u5230\u897f\u57df\u9b54\u6559\u8bbe\u5728\u7ec8\u5357\u5c71\u90a3\u5904\u5206\u575b\uff0c\u8fd9 \u65b9\u9762\u5c31\u8981\u591a\u591a\u4ef0\u4ed7\u60dc\u6625\u4e86\u3002\u201d \u3000\u3000\u60dc\u6625\u82e6\u7b11\u7740\u9053\uff1a\u201c\u5a62\u5b50\u5f53\u65f6\u773c\u775b\u662f\u88ab\u8499\u7740\u7684\uff0c\u6839\u672c\u5f04\u4e0d\u6e05\u90a3\u5ea7\u5206\u575b\u5730\u70b9\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u4f60\u662f\u4ece\u4ec0\u4e48\u5730\u65b9\u5f00\u59cb\u88ab\u8499\u4e0a\u773c\u775b\u7684\uff0c\u4e00\u5171\u8499\u4e86\u591a\u5c11\u65f6\u95f4\uff0c\u4ece\u8fd9\u51e0\u65b9\u9762\u603b\u4f1a\u5224\u65ad\u51fa\u6765\u4e9b \u70b9\u5427\uff1f\u201d \u3000\u3000\u201c\u90a3\u53ea\u6709\u5230\u4e86\u7ec8\u5357\u5c71\u8bd5\u8bd5\u770b\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u53e6\u5916\u5728\u8def\u4e0a\u65f6\uff0c\u5927\u5bb6\u5fc5\u987b\u65f6\u65f6\u7559\u610f\uff0c\u8bf4\u4e0d\u5b9a\u80fd\u548c\u9a6c\u5be1\u5987\u4ee5\u53ca\u9f99\u5728\u5929\u4e0d\u671f\u800c \u9047\uff0c\u82e5\u80fd\u6349\u5230\u4ed6\u4eec\u4e24\u4eba\uff0c\u4e00\u5207\u7684\u95ee\u9898\u4fbf\u8fce\u5203\u800c\u89e3\u4e86\u3002\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u79bb\u5929\u4eae\u4e0d\u6709\u4e00\u4e2a\u66f4\u6b21\uff0c\u5927\u5bb6\u73b0\u5728\u5c31\u56de\u623f\u7ee7\u7eed\u4f11\u606f\uff0c\u517b\u597d\u7cbe\u795e\uff0c\u4ee5\u4fbf\u5929\u4eae\u540e\u8d76 \u8def\u3002\u201d \u5367\u9f99\u751f\u300a\u71d5\u5b50\u4f20\u5947\u300b \u7b2c\u4e09\u5341\u4e8c\u56de\u3000\u60c5\u53ef\u9274\u5929 \u3000\u3000\u4e09\u65e5\u540e\uff0c\u4e09\u516c\u4e3b\u7b49\u4eba\u5df2\u5230\u8fbe\u7ec8\u5357\u5c71\u4e0b\u3002 \u3000\u3000\u7531\u6f7c\u5173\u51fa\u53d1\u65f6\uff0c\u4ed6\u4eec\u662f\u5206\u6210\u4e24\u7ec4\u884c\u8fdb\u3002 \u3000\u3000\u7531\u4e09\u516c\u4e3b\u548c\u738b\u5f64\u7387\u9886\u4e00\u7ec4\uff0c\u6c5f\u5343\u91cc\u7387\u9886\u4e00\u7ec4\uff0c\u76ee\u7684\u662f\u5e0c\u671b\u80fd\u5728\u8def\u4e0a\u5c06\u56de\u7a0b\u7684\u9a6c\u5be1\u5987\u548c\u9f99 \u5728\u5929\u622a\u83b7\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u5931\u671b\u4e86\u3002 \u3000\u3000\u76f4\u5230\u5728\u84dd\u7530\u4f1a\u5408\u4ee5\u81f3\u5230\u8fbe\u7ec8\u5357\u5c71\u9e93\uff0c\u5e76\u672a\u78b0\u5230\u8fd9\u4e24\u4eba\u7684\u8e2a\u5f71\u3002 \u3000\u3000\u7ec8\u5357\u5c71\u4e3b\u5cf0\u5728\u957f\u5b89\u4e4b\u5357\uff0c\u7ef5\u4e98\u6570\u767e\u91cc\u3002\u81ea\u53e4\u4ee5\u6765\u5373\u6d41\u4f20\u7740\u4e0d\u5c11\u795e\u79d8\u7684\u4f20\u8bf4\uff0c\u4e0d\u8bba\u795e\u4ed9\u548c \u9b3c\u602a\uff0c\u4f3c\u4e4e\u90fd\u4e0e\u7ec8\u5357\u5c71\u6709\u5173\u3002 \u3000\u3000\u603b\u4e4b\uff0c\u8be5\u662f\u4e00\u5904\u5730\u7075\u4eba\u6770\u7684\u6240\u5728\u3002\u897f\u57df\u9b54\u6559\u5728\u8fd9\u91cc\u8bbe\u7acb\u4e00\u5904\u5206\u575b\uff0c\u540c\u6837\u4e5f\u589e\u52a0\u4e86\u5b83\u7684\u795e \u79d8\u6027\u3002 \u3000\u3000\u5f53\u665a\uff0c\u4ed6\u4eec\u6295\u5bbf\u5728\u5c71\u9e93\u4e00\u5ea7\u53e4\u5239\u91cc\u3002 \u3000\u3000\u8be2\u95ee\u5239\u91cc\u7684\u548c\u5c1a\uff0c\u5e76\u65e0\u4eba\u77e5\u9053\u897f\u57df\u9b54\u6559\u7684\u5206\u575b\u5728\u4f55\u5904\uff0c\u751a\u81f3\u8fde\u897f\u57df\u9b54\u6559\u4e5f\u90fd\u4ece\u672a\u542c\u8bf4 \u8fc7\u3002 \u3000\u3000\u5728\u53e4\u5239\u4f4f\u4e86\u4e00\u665a\uff0c\u6b21\u65e5\u7ee7\u7eed\u5411\u5c71\u4e0a\u8fdb\u53d1\u3002 \u3000\u3000\u672c\u6765\uff0c\u4ed6\u4eec\u62c5\u5fc3\u6e10\u5165\u5c71\u4e4b\u6df1\u5904\u540e\uff0c\u996e\u98df\u95ee\u9898\u96be\u4ee5\u89e3\u51b3\uff0c\u6240\u5e78\u5728\u5c71\u8170\u91cc\u53d1\u73b0\u4e86\u51e0\u6237\u4eba\u5bb6\u3002 \u4e8e\u662f\uff0c\u5927\u5bb6\u5c31\u51b3\u5b9a\u5728\u8fd9\u91cc\u4f4f\u4e0b\uff0c\u767d\u5929\u518d\u5206\u5934\u67e5\u63a2\u897f\u57df\u9b54\u6559\u5206\u575b\u6240\u5728\uff0c\u665a\u95f4\u5219\u4ecd\u56de\u5230\u8fd9\u91cc\u805a \u5408\u3002 \u3000\u3000\u4e00\u8fde\u4e09\u56db\u5929\u8fc7\u53bb\uff0c\u9664\u4e09\u516c\u4e3b\u548c\u601c\u82b1\u8d1f\u8d23\u7559\u5b88\u5916\uff0c\u5176\u4f59\u7684\u4eba\u5168\u5728\u5c71\u4e0a\u5404\u5904\u5c55\u5f00\u5bfb\u89c5\uff0c\u7ed3\u679c \u4ecd\u662f\u6beb\u65e0\u6240\u83b7\u3002 \u3000\u3000\u665a\u4e0a\uff0c\u738b\u5f64\u3001\u6c5f\u5343\u91cc\u628a\u6240\u6709\u7684\u4eba\u9664\u4e09\u516c\u4e3b\u5916\uff0c\u5168\u96c6\u5408\u5728\u6237\u5916\u7684\u4e00\u5904\u7a7a\u5730\u4e0a\uff0c\u5927\u5bb6\u5176\u540c\u7814 \u8ba8\u4e0b\u4e00\u6b65\u8be5\u5982\u4f55\u884c\u52a8\u3002 \u3000\u3000\u738b\u5f64\u9996\u5148\u95ee\u60dc\u6625\u9053\uff1a\u201c\u59d1\u5a18\u5728\u5c71\u4e0a\u5230\u5904\u8d70\u4e86\u4e09\u56db\u5929\uff0c\u96be\u9053\u5c31\u6ca1\u53d1\u73b0\u4e00\u5904\u8f83\u4e3a\u719f\u6089\u7684\u5730 \u65b9\uff1f\u201d \u3000\u3000\u60dc\u6625\u7d27\u8e59\u7740\u53cc\u7709\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u5176\u5b9e\u5a62\u5b50\u6bd4\u5728\u573a\u7684\u4efb\u4f55\u4eba\u90fd\u6025\uff0c\u82e5\u627e\u4e0d\u5230\u9b54\u6559\u5206\u575b\uff0c\u5a62\u5b50 \u8fd9\u6761\u547d\u4e5f\u5c31\u7b49\u4e8e\u5df2\u7ecf\u65e0\u6551\u4e86\u3002\u201d \u3000\u3000\u5ffd\u542c\u6c5f\u5343\u91cc\u95ee\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u8fd9\u51e0\u5929\u4f60\u53ef\u66fe\u8def\u7ecf\u9752\u4e91\u5d16\u5927\u8352\u6d1e\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6447\u5934\u9053\uff1a\u201c\u6211\u672c\u6765\u4e00\u76f4\u60f3\u53bb\u770b\u770b\uff0c\u4f46\u56e0\u5206\u914d\u7684\u8def\u7ebf\u6b63\u597d\u4e0d\u5728\u90a3\u4e2a\u65b9\u5411\uff0c\u6240\u4ee5\u6ca1\u6cd5 \u524d\u53bb\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u795e\u8272\u51c4\u7136\u7684\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u597d\uff0c\u660e\u5929\u54b1\u4eec\u4e24\u4eba\u5c31\u53bb\u4e00\u8d9f\u3002\u201d \u3000\u3000\u738b\u5f64\u832b\u7136\u95ee\u9053\uff1a\u201c\u9752\u4e91\u5d16\u5927\u8352\u6d1e\u5728\u54ea\u91cc\uff1f\u6c5f\u5144\u548c\u5c0f\u71d5\u5b50\u53bb\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u9752\u4e91\u5d16\u5927\u8352\u6d1e\u6b63\u662f\u6211\u90a3\u4e49\u5144\u5929\u96f7\u8001\u4eba\u4fee\u7ec3\u4e4b\u5904\uff0c\u79bb\u8fd9\u91cc\u4e0d\u8fc7\u56db\u4e94\u5341\u91cc\u7684\u8def \u7a0b\uff0c\u6c5f\u67d0\u662f\u53bb\u63a2\u671b\u4e49\u5144\uff0c\u5c0f\u71d5\u5b50\u5219\u540c\u53bb\u62dc\u89c1\u4ed6\u5e08\u7236\u3002\u201d \u3000\u3000\u738b\u5f64\u8f6c\u7740\u773c\u73e0\u9053\uff1a\u201c\u5929\u96f7\u8001\u4eba\u4e0d\u662f\u5df2\u7ecf\u81ea\u7edd\u4e86\u5417\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u795e\u8272\u9eef\u7136\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u90a3\u4e49\u5144\u7cfb\u81ea\u95ed\u5728\u4e00\u5904\u5c71\u8179\u5ca9\u6d1e\u5185\uff0c\u5e76\u81ea\u884c\u7528\u5de8\u77f3\u5c06\u6d1e\u53e3 \u5835\u4f4f\uff0c\u8981\u5c0f\u71d5\u5b50\u4e09\u5e74\u540e\u518d\u542f\u5f00\u77f3\u95e8\u67e5\u770b\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u73b0\u5728\u624d\u4e0d\u8fc7\u534a\u5e74\u591a\uff0c\u96be\u9053\u6c5f\u5144\u5c31\u8981\u542f\u5f00\u77f3\u95e8\u8fdb\u5185\u67e5\u770b\uff1f\u201d \u3000\u3000\u201c\u4e0d\uff0c\u6211\u53ea\u662f\u60f3\u5e26\u7740\u5c0f\u71d5\u5b50\u5728\u77f3\u95e8\u4e4b\u5916\u51ed\u540a\u4e00\u756a\uff0c\u987a\u4fbf\u4e5f\u67e5\u770b\u4e00\u4e0b\u90a3\u6d1e\u53e3\u662f\u5982\u4f55\u5835\u4f4f \u7684\uff1f\u201d \u3000\u3000\u201c\u6c5f\u5144\u51c6\u5907\u4ec0\u4e48\u65f6\u5019\u56de\u6765\uff1f\u201d \u3000\u3000\u201c\u660e\u5929\u4e00\u65e9\u51fa\u53d1\uff0c\u6765\u5f97\u53ca\u5c31\u5f53\u665a\u8fd4\u56de\uff0c\u5426\u5219\u53ea\u597d\u5728\u90a3\u8fb9\u8fc7\u591c\u4e86\u3002\u201d \u3000\u3000\u201c\u9664\u4e86\u5c0f\u71d5\u5b50\uff0c\u662f\u5426\u8fd8\u8981\u5e26\u522b\u4eba\u53bb\uff1f\u201d \u3000\u3000\u201c\u4e0d\u5fc5\u4e86\uff0c\u5927\u5bb6\u90fd\u6709\u4e8b\u60c5\uff0c\u4eba\u53bb\u591a\u4e86\u53cd\u800c\u6d6a\u8d39\u3002\u201d \u3000\u3000\u5927\u5bb6\u53c8\u5546\u8bae\u4e86\u4e00\u4f1a\u513f\uff0c\u624d\u5404\u81ea\u6563\u53bb\u3002 \u3000\u3000\u7fcc\u65e5\u4e00\u65e9\uff0c\u6c5f\u5343\u91cc\u4fbf\u5e26\u7740\u5c0f\u71d5\u5b50\u5f80\u9752\u4e91\u5d16\u5927\u8352\u6d1e\u800c\u6765\u3002 \u3000\u3000\u4ee5\u4ed6\u4eec\u4e24\u4eba\u7684\u8f7b\u529f\uff0c\u4ec5\u4ec5\u4e00\u4e2a\u591a\u65f6\u8fb0\uff0c\u4fbf\u5df2\u8fdc\u8fdc\u671b\u89c1\u9752\u4e91\u5d16\u3002 \u3000\u3000\u9752\u4e91\u5d16\u662f\u5904\u949f\u7075\u7389\u79c0\u6240\u5728\uff0c\u666f\u8272\u7edd\u4f73\u3002 \u3000\u3000\u5929\u96f7\u8001\u4eba\u6240\u4f4f\u7684\u5927\u8352\u6d1e\uff0c\u5176\u5b9e\u770b\u6765\u534a\u70b9\u4e0d\u8352\uff0c\u5929\u96f7\u8001\u4eba\u6240\u4ee5\u628a\u81ea\u5df1\u4f4f\u7684\u5730\u65b9\u53d6\u540d\u5927\u8352 \u6d1e\uff0c\u5f53\u7136\u662f\u6709\u539f\u56e0\u7684\uff0c\u4e5f\u662f\u4ed6\u4ece\u4e0d\u80af\u5411\u5916\u4eba\u900f\u9732\u7684\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u548c\u5c0f\u71d5\u5b50\u90fd\u77e5\u9053\uff0c\u5929\u96f7\u8001\u4eba\u5bf9\u4e00\u9053\u98de\u8679\u82df\u6167\u6708\u53ef\u8bf4\u662f\u4e00\u5f80\u60c5\u6df1\uff0c\u65e7\u60c5\u96be\u5fd8\uff0c\u5931\u53bb \u4e86\u4e00\u9053\u98de\u8679\u82df\u6167\u6708\uff0c\u5728\u4ed6\u7684\u751f\u547d\u4e2d\u7b49\u4e8e\u662f\u4e00\u7247\u8352\u51c9\u3002\u4ed6\u662f\u5728\u548c\u82df\u6167\u6708\u56e0\u8bef\u4f1a\u800c\u5206\u624b\u540e\u624d\u6765\u5230 \u9752\u4e91\u5d16\u9690\u5c45\u7684\u3002 \u3000\u3000\u56e0\u4e4b\uff0c\u624d\u628a\u5c45\u4f4f\u7684\u6d1e\u5ba4\u53d6\u540d\u5927\u8352\u6d1e\u3002\u5982\u679c\u6709\u82df\u6167\u6708\u5728\u4ed6\u8eab\u8fb9\uff0c\u4ed6\u7684\u6d1e\u5ba4\u6beb\u65e0\u7591\u95ee\u5c31\u4f1a\u53e6 \u5916\u53d6\u4e2a\u540d\u5b57\u3002 \u3000\u3000\u5f88\u5feb\u4fbf\u6765\u5230\u5927\u8352\u6d1e\u5916\uff0c\u53ea\u89c1\u90a3\u6d1e\u95e8\u679c\u7136\u88ab\u4e24\u4e09\u5757\u91cd\u53e0\u5728\u4e00\u8d77\u7684\u5de8\u77f3\u5c01\u4f4f\uff0c\u6bcf\u5757\u5de8\u77f3\u81f3\u5c11 \u5728\u5343\u65a4\u4ee5\u4e0a\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0c\u6c5f\u5343\u91cc\u5fc3\u60c5\u663e\u5f97\u65e0\u6bd4\u6c89\u91cd\uff0c\u5446\u5446\u7684\u770b\u4e86\u534a\u664c\uff0c\u624d\u8f6c\u5934\u95ee\u9053\uff1a\u201c\u8fd9\u4e24\u4e09\u5757\u5927\u77f3 \u90fd\u662f\u4f60\u5e08\u7236\u81ea\u5df1\u642c\u5230\u6d1e\u53e3\u7684\u5417\uff1f\u5c0f\u71d5\u5b50\u9eef\u7136\u70b9\u70b9\u5934\u3002 \u3000\u3000\u201c\u4f60\u6709\u6ca1\u6709\u5e2e\u5fd9\uff1f\u201d \u3000\u3000\u201c\u6211\u4e00\u76f4\u4e0d\u80af\u8ba9\u5e08\u7236\u8fd9\u4e48\u505a\uff0c\u66fe\u8dea\u5728\u5730\u4e0a\u5411\u4ed6\u8001\u4eba\u5bb6\u82e6\u6c42\uff0c\u90a3\u6709\u5f1f\u5b50\u642c\u77f3\u5934\u628a\u5e08\u7236\u5173\u95ed \u8d77\u6765\u7684\u9053\u7406\u5462\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u8fb9\u8dfa\u811a\u8fb9\u53f9\u6c14\u9053\uff1a\u201c\u4f60\u5e08\u7236\u8fd9\u662f\u4f55\u82e6\uff0c\u4ee5\u4ed6\u8eab\u5df2\u8fbe\u7089\u706b\u7eaf\u9752\u3001\u767b\u5cf0\u9020\u6781\u7684\u5947\u4e16\u6b66 \u529f\uff0c\u4e3a\u4ec0\u4e48\u7adf\u662f\u5982\u6b64\u7684\u60f3\u4e0d\u5f00\uff1f\u201d \u3000\u3000\u201c\u53ef\u662f\u4ed6\u8001\u4eba\u5bb6\u5df2\u8eab\u4e2d\u897f\u57df\u9b54\u6559\u4e03\u715e\u6bd2\u9488\uff0c\u5b9e\u5728\u75db\u82e6\u4e0d\u582a\u3002\u201d \u3000\u3000\u201c\u4ee5\u4ed6\u7684\u5185\u529f\uff0c\u5df2\u662f\u767e\u6bd2\u4e0d\u4fb5\u3002\u7eb5\u7136\u90a3\u4e03\u715e\u6bd2\u9488\u6b79\u6bd2\u65e0\u6bd4\uff0c\u4ed6\u7167\u6837\u4e5f\u71ac\u4e86\u5341\u51e0\u5e74\u4e86\uff0c\u4e3a \u4ec0\u4e48\u5c31\u4e0d\u80fd\u7ee7\u7eed\u71ac\u4e0b\u53bb\u5462\uff1f\u5c31\u51ed\u4ed6\u81ea\u5c01\u77f3\u95e8\u524d\u7684\u4f53\u6001\u72b6\u51b5\uff0c\u4e5f\u53ef\u8bc1\u660e\u4ed6\u5fc5\u5b9a\u80fd\u7ee7\u7eed\u6d3b\u4e0b\u53bb \u7684\u3002\u201d \u3000\u3000\u201c\u6c5f\u53d4\u53d4\u8fd9\u8bdd\u662f\u6839\u636e\u4ec0\u4e48\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e24\u4e09\u5757\u5de8\u77f3\uff0c\u6bcf\u5757\u90fd\u91cd\u6108\u5343\u65a4\uff0c\u4ed6\u80fd\u51ed\u81ea\u5df1\u7684\u529b\u91cf\uff0c\u4e0d\u4f46\u8981\u642c\u5230\u6d1e\u53e3\uff0c\u800c\u4e14\u8981\u91cd\u53e0 \u8d77\u6765\uff0c\u53ef\u89c1\u4ed6\u7684\u5185\u529b\u5e76\u672a\u51cf\u9000\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u8bf4\u5230\u8fd9\u91cc\uff0c\u957f\u957f\u5401\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u4ed6\u771f\u7684\u8bf4\u8fc7\u8981\u4f60\u4e09\u5e74\u4e4b\u540e\u518d\u6253\u5f00\u6d1e\u95e8\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u5e08\u7236\u6b63\u662f\u8fd9\u4e48\u8bf4\u7684\uff0c\u53ef\u662f\u4ed6\u8001\u4eba\u5bb6\u5e76\u672a\u8bf4\u660e\u4e3a\u4ec0\u4e48\u8981\u7b49\u4e09\u5e74\uff0c\u6c5f\u53d4\u53d4\u662f\u5426 \u77e5\u9053\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u201c\u6211\u81ea\u7136\u660e\u767d\u4e00\u70b9\uff0c\u4f46\u73b0\u5728\u8fd8\u662f\u4e0d\u8bf4\u7684\u597d\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u53f9\u4e86\u4e00\u58f0\uff0c\u7ee7\u7eed\u53c8\u9053\uff1a\u201c\u5c0f\u71d5\u5b50\uff0c\u4f60\u5e08\u7236\u60f3\u4e0d\u5f00\uff0c\u5e76\u975e\u56e0\u4e3a\u8eab\u4e2d\u897f\u57df\u9b54\u6559\u7684\u4e03\u715e \u6bd2\u9488\uff0c\u800c\u662f\u53e6\u6709\u539f\u56e0\uff0c\u96be\u9053\u4f60\u81f3\u4eca\u5fc3\u91cc\u8fd8\u6ca1\u6570\u5417\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u4e5f\u6df1\u6df1\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u6211\u662f\u79bb\u5f00\u5e08\u7236\u4e0b\u5c71\u540e\u624d\u542c\u8bf4\u7684\uff0c\u4ed6\u8001\u4eba\u5bb6\u5f53\u5e74\u548c\u4e00\u9053\u98de\u8679 \u82df\u5973\u4fa0\u6709\u4e00\u6bb5\u60c5\u3002\u201d \u3000\u3000\u201c\u5c82\u6b62\u662f\u4e00\u6bb5\u60c5\uff0c\u6839\u672c\u5c31\u662f\u6b64\u60c5\u96be\u5fd8\uff0c\u8bf4\u5f97\u4e25\u91cd\u4e00\u70b9\uff0c\u4f60\u5e08\u7236\u5c31\u662f\u4e3a\u5979\u800c\u751f\uff0c\u4e3a\u5979\u800c \u6b7b\uff01 \u3000\u3000\u201c\u96be\u9053\u82df\u5973\u4fa0\u4e00\u70b9\u4e5f\u4e0d\u559c\u6b22\u6211\u5e08\u7236\uff1f\u4e0d\u77e5\u9053\u6211\u5e08\u7236\u5bf9\u5979\u7528\u60c5\u4e4b\u4e13\u3002\u201d \u3000\u3000\u201c\u5979\u540c\u6837\u4e5f\u6df1\u7231\u4f60\u5e08\u7236\uff0c\u800c\u4e14\u7528\u60c5\u4e4b\u6df1\uff0c\u4e0d\u4e0b\u4e8e\u4f60\u5e08\u7236\u5bf9\u5979\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u82df\u5973\u4fa0\u4e3a\u4ec0\u4e48\u8981\u79bb\u5f00\u6211\u5e08\u7236\u5462\uff1f\u2019\u201d\u5f53\u7136\u662f\u82df\u5973\u4fa0\u8bef\u4f1a\u4e86\u4ed6\uff0c\u8fd9\u4e00\u8bef\u4f1a\u4e0d\u8981\u7d27\uff0c \u4e00\u8f6c\u773c\u5c31\u4e8c\u5341\u5e74\u3002 \u3000\u3000\u4f60\u5e08\u7236\u6700\u540e\u8fd9\u4e8c\u5341\u5e74\u53ef\u4ee5\u8bf4\u5168\u662f\u4e00\u80a1\u5e0c\u671b\u7684\u529b\u91cf\u5728\u652f\u6491\u7740\u4ed6\u6d3b\u4e0b\u53bb\u7684\u3002\u201c\u201d\u4ed6\u8001\u4eba\u5bb6\u7684 \u5e0c\u671b\u662f\u4ec0\u4e48\uff1f\u201c \u3000\u3000\u201c\u82df\u5973\u4fa0\u80fd\u8bef\u4f1a\u51b0\u91ca\uff0c\u518d\u56de\u5230\u4ed6\u8eab\u8fb9\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u5f53\u521d\u7684\u8bef\u4f1a\u56e0\u4f55\u800c\u8d77\uff0c\u6c5f\u53d4\u53d4\u4e00\u5b9a\u77e5\u9053\u5427\uff1f\u201d \u3000\u3000\u201c\u7537\u5973\u95f4\u7684\u4e8b\uff0c\u5f88\u96be\u5984\u52a0\u63e3\u6d4b\uff0c\u6211\u5f53\u7136\u77e5\u9053\u4e00\u4e9b\uff0c\u4f46\u4e5f\u4e0d\u89c1\u5f97\u5b8c\u5168\u6b63\u786e\uff0c\u4f60\u5e08\u7236\u4e00\u7b49\u4e8c \u5341\u5e74\uff0c\u5728\u611f\u89c9\u5b8c\u5168\u7edd\u671b\u4e4b\u540e\u624d\u8d70\u4e0a\u7edd\u8def\u3002\u201d \u3000\u3000\u201c\u542c\u8bf4\u6211\u5e08\u7236\u7684\u53cc\u76ee\u5c31\u662f\u88ab\u8461\u5973\u4fa0\u6253\u778e\u7684\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u90a3\u53ef\u8bf4\u662f\u4ed6\u6545\u610f\u8ba9\u82df\u5973\u4fa0\u6253\u778e\u7684\uff0c\u4ee5\u4f60\u5e08\u7236\u7684\u6b66\u529f\uff0c\u82df\u5973\u4fa0\u7684\u8eab\u624b\u518d\u9ad8\u4e5f\u4f24\u4e0d \u5230\u4ed6\u7684\u773c\u775b\uff0c\u504f\u504f\u4ed6\u5374\u4e0d\u95ea\u4e0d\u907f\u3002\u201d \u3000\u3000\u201c\u8fd9\u53c8\u662f\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4ed6\u7231\u82df\u5973\u4fa0\u7231\u5f97\u592a\u6df1\u4e86\uff0c\u56e0\u4e3a\u4ed6\u8bf4\u8fc7\u4e00\u53e5\u8ba9\u82df\u5973\u4fa0\u8fc7\u4e8e\u4f24\u5fc3\u7684\u8bdd\uff0c\u6240\u4ee5\u624d\u60c5\u613f\u63a5\u53d7\u8fd9 \u60e9\u7f5a\u3002\u5176\u5b9e\uff0c\u4ee5\u4f60\u5e08\u7236\u5728\u6b67\u9ec4\u4e4b\u672f\u65b9\u9762\u7684\u9020\u8be3\uff0c\u5fc5\u6709\u529e\u6cd5\u8ba9\u773c\u775b\u590d\u660e\uff0c\u4f46\u4ed6\u5374\u53c8\u60c5\u613f\u8ba9\u53cc\u76ee \u4ece\u6b64\u6c38\u8fdc\u5931\u660e\u3002\u201d \u3000\u3000\u201c\u96be\u9053\u6211\u5e08\u7236\u2026\u2026 \u3000\u3000\u201c\u56e0\u4e3a\u4ed6\u89c9\u5f97\u5728\u8fd9\u4e16\u754c\u4e0a\uff0c\u9664\u4e86\u82df\u5973\u4fa0\uff0c\u5df2\u7ecf\u6ca1\u6709\u4ec0\u4e48\u503c\u5f97\u4ed6\u770b\u7684\uff0c\u82e5\u5f53\u65f6\u82df\u5973\u4fa0\u80af\u56de \u5230\u4ed6\u8eab\u8fb9\uff0c\u4ed6\u5c31\u4e0d\u53ef\u80fd\u518d\u778e\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e48\u770b\u6765\uff0c\u82df\u5973\u4fa0\u672a\u514d\u592a\u6b8b\u5fcd\u592a\u51b7\u9177\u4e86\uff1f\u201d \u3000\u3000\u201c\u8bdd\u4e5f\u4e0d\u80fd\u8fd9\u4e48\u8bf4\uff0c\u4f9d\u6211\u5224\u65ad\uff0c\u82df\u5973\u4fa0\u4e8b\u540e\u5fc5\u5b9a\u4e5f\u77e5\u9053\u8fd9\u662f\u4e00\u573a\u8bef\u4f1a\uff0c\u5374\u56e0\u4e3a\u5979\u751f\u6027\u592a \u5014\u5f3a\uff0c\u4e0d\u80af\u8ba4\u9519\uff0c\u800c\u4e14\u4e5f\u6ca1\u6709\u89e3\u91ca\u7684\u673a\u4f1a\uff0c\u5c31\u53ea\u6709\u8ba9\u5b83\u6c38\u8fdc\u8bef\u4f1a\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u5176\u5b9e\u5979\u5185\u5fc3\u6df1\u5904\u548c\u4f60\u5e08\u7236\u662f\u540c\u6837\u75db\u82e6\uff0c\u56e0\u4e3a\u5728\u8fd9\u4e16\u754c\u4e0a\uff0c\u4e5f\u53ea\u6709\u4f60\u5e08\u7236\u662f\u5979\u552f\u4e00\u770b\u5f97\u8d77 \u7684\u7537\u4eba\u3002\u201c \u3000\u3000\u5c0f\u71d5\u5b50\u5fc3\u60c5\u4ece\u672a\u5982\u6b64\u6c89\u91cd\u8fc7\uff0c\u4e5f\u4ece\u672a\u5982\u6b64\u6fc0\u52a8\u8fc7\u3002 \u3000\u3000\u4ed6\u7981\u4e0d\u4f4f\u6d8c\u51fa\u4e24\u884c\u6cea\u6c34\uff0c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u79cd\u7537\u5973\u4e4b\u95f4\u7684\u5927\u60b2\u5267\u7adf\u53d1\u751f\u5728\u6211\u5e08\u7236\u548c\u82df\u5973\u4fa0 \u8eab\u4e0a\uff01\u201d \u3000\u3000\u6c5f\u5343\u91cc\u82e6\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u8fd9\u79cd\u7537\u5973\u4e4b\u95f4\u611f\u60c5\u4e0a\u7684\u4e8b\uff0c\u53ef\u8bf4\u592a\u591a\u4e86\uff0c\u53ea\u662f\u4e0d\u4e3a\u5916\u4eba\u6240\u77e5\u7f62 \u4e86\uff01\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u53ea\u542c\u8eab\u540e\u53d1\u51fa\u91cd\u91cd\u7684\u54b3\u55fd\u58f0\u9053\uff1a\u201c\u5c0f\u71d5\u5b50\uff0c\u662f\u4f60\u56de\u6765\u4e86\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6025\u6025\u56de\u5934\uff0c\u539f\u6765\u8bf4\u8bdd\u7684\u662f\u670d\u4f8d\u4e86\u5e08\u7236\u5927\u534a\u8f88\u5b50\u7684\u8001\u4ec6\u4eba\u5305\u5fe0\u3002 \u3000\u3000\u4ed6\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u5305\u4f2f\u4f2f\uff0c\u6211\u521a\u6765\uff0c\u8fd8\u6ca1\u6765\u5f97\u53ca\u53bb\u770b\u60a8\uff01\u201d \u3000\u3000\u5305\u5fe0\u518d\u671b\u5411\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u6c5f\u5927\u4fa0\u4e5f\u6765\u4e86\uff01\u201d \u3000\u3000\u4ed6\u8bf4\u7740\uff0c\u671b\u4e86\u90a3\u88ab\u5de8\u77f3\u5c01\u4f4f\u7684\u6d1e\u95e8\u4e00\u773c\uff0c\u7981\u4e0d\u4f4f\u6eda\u51fa\u4e24\u884c\u8001\u6cea\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u95ee\u9053\uff1a\u201c\u8001\u4eba\u5bb6\uff0c\u8fd9\u534a\u5e74\u6765\u4f60\u8fd8\u4e00\u76f4\u4f4f\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u5305\u5fe0\u62ed\u4e86\u62ed\u6cea\u6c34\u9053\uff1a\u201c\u8001\u4e3b\u4eba\u81ea\u95ed\u77f3\u6d1e\u524d\uff0c\u66fe\u4ea4\u4ee3\u8fc7\u4e09\u5e74\u4ee5\u540e\u624d\u80fd\u5f00\u77f3\u95e8\uff0c\u8001\u5974\u51b3\u5b9a\u5c31 \u5728\u8fd9\u91cc\u5b88\u4e0a\u4e09\u5e74\uff0c\u7b49\u4e09\u5e74\u4e00\u5230\uff0c\u6253\u5f00\u6d1e\u95e8\u540e\uff0c\u518d\u548c\u8001\u4e3b\u4eba\u89c1\u4e0a\u6700\u540e\u4e00\u9762\u3002\u201d \u3000\u3000\u201c\u4f60\u4f4f\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u5305\u5fe0\u6307\u6307\u4e0d\u8fdc\u5904\u642d\u76d6\u7684\u4e00\u5904\u8349\u68da\uff0c\u9053\uff1a\u201c\u5c31\u5728\u90a3\u8349\u68da\u91cc\uff0c\u8fd8\u6709\u8001\u5974\u7684\u8001\u4f34\u513f\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9ed8\u4e86\u4e00\u9ed8\u9053\uff1a\u201c\u8fd9\u534a\u5e74\u591a\u6765\uff0c\u6211\u4e49\u5144\u81ea\u95ed\u7684\u8fd9\u5ea7\u5927\u8352\u6d1e\u53ef\u6709\u4ec0\u4e48\u52a8\u9759\uff1f\u201d \u3000\u3000\u201c\u4eba\u6b7b\u4e86\u54ea\u91cc\u8fd8\u4f1a\u6709\u4ec0\u4e48\u52a8\u9759\uff1f\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u77e5\u9053\u4eba\u5df2\u7ecf\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e00\u4e2a\u4eba\u4e0d\u5403\u4e0d\u559d\uff0c\u6700\u591a\u53ea\u80fd\u6d3b\u4e03\u5929\u4e03\u591c\uff0c\u8001\u4e3b\u4eba\u5df2\u7ecf\u81ea\u95ed\u5728\u5c71\u6d1e\u91cc\u534a\u5e74\u591a\uff0c\u8fd8\u80fd\u6d3b \u5417\uff1f\u9664\u975e\u4ed6\u662f\u795e\u4ed9\uff01\u201d \u3000\u3000\u6c5f\u5343\u91cc\u53c8\u662f\u4e00\u9635\u9eef\u7136\uff0c\u8bb8\u4e45\u624d\u95ee\u9053\uff1a\u201c\u4f60\u662f\u5426\u5e0c\u671b\u80fd\u548c\u8001\u4e3b\u4eba\u63d0\u524d\u89c1\u9762\uff1f\u201d \u3000\u3000\u5305\u5fe0\u70b9\u70b9\u5934\u9053\uff1a\u201c\u8001\u5974\u65e9\u5c31\u6709\u8fd9\u610f\u601d\uff0c\u53ef\u60dc\u65e0\u6cd5\u5c06\u5835\u4f4f\u6d1e\u53e3\u7684\u5de8\u77f3\u79fb\u5f00\uff0c\u83ab\u975e\u6c5f\u5927\u4fa0\u6709 \u610f\u6253\u5f00\u77f3\u95e8\uff1f\u90a3\u5927\u597d\u4e86\uff01\u201d \u3000\u3000\u201c\u4f60\u82e5\u5f97\u89c1\u8001\u4e3b\u4eba\u4ee5\u540e\uff0c\u662f\u5426\u5c31\u8981\u9a6c\u4e0a\u79bb\u5f00\u8fd9\u91cc\u5462\uff1f\u201d \u3000\u3000\u201c\u6c5f\u5927\u4fa0\u8bf4\u54ea\u91cc\u8bdd\u6765\uff0c\u8001\u5974\u5df2\u51b3\u5b9a\u6c38\u8fdc\u7559\u5728\u8fd9\u91cc\uff0c\u4e3a\u8001\u4e3b\u4eba\u5b88\u5893\u4e00\u8f88\u5b50\uff0c\u8001\u4e3b\u4eba\u5f85\u8001\u5974 \u6069\u91cd\u5982\u5c71\uff0c\u4e0d\u7ba1\u4ed6\u8001\u4eba\u5bb6\u751f\u524d\u6b7b\u540e\uff0c\u8001\u5974\u90fd\u4e0d\u80fd\u79bb\u5f00\u4ed6\u3002\u201d \u3000\u3000\u53ea\u542c\u5c0f\u71d5\u5b50\u95ee\u9053\uff1a\u201c\u6c5f\u53d4\u53d4\uff0c\u60a8\u771f\u7684\u73b0\u5728\u5c31\u8981\u628a\u6d1e\u95e8\u6253\u5f00\u5417\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u4e0d\u7b54\uff0c\u5374\u53cd\u95ee\u9053\uff1a\u201c\u4f60\u662f\u5426\u5e0c\u671b\u63d0\u524d\u628a\u6d1e\u95e8\u6253\u5f00\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u5e0c\u671b\uff0c\u4f46\u662f\u4ed6\u8001\u4eba\u5bb6\u7684\u4ea4\u4ee3\u5374\u53c8\u4e0d\u80fd\u4e0d\u9075\u3002\u201d \u3000\u3000\u201c\u4f9d\u4f60\u6599\u60f3\uff0c\u6253\u5f00\u77f3\u95e8\u4e4b\u540e\uff0c\u91cc\u9762\u8be5\u662f\u4ec0\u4e48\u6837\u5b50\uff1f\u201d \u3000\u3000\u201c\u5e08\u7236\u5f53\u7136\u65e9\u5df2\u5f52\u5929\u53bb\u4e86\u3002\u201d \u3000\u3000\u201c\u4e5f\u8bb8\u4e0d\u4f1a\u90a3\u6837\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u60ca\u559c\u7684\u9053\uff1a\u201c\u96be\u9053\u4ed6\u8001\u4eba\u5bb6\u8fd8\u80fd\u6d3b\u7740\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u81ea\u8a00\u81ea\u8bed\u822c\u9053\uff1a\u201c\u636e\u6211\u6240\u77e5\uff0c\u6b66\u6797\u4e2d\u6709\u4e00\u79cd\u95ed\u8109\u5927\u6cd5\uff0c\u4e5f\u53eb\u9f9f\u606f\u5927\u6cd5\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u8feb\u4e0d\u53ca\u5f85\u7684\u95ee\u9053\uff1a\u201c\u5b66\u4f1a\u8fd9\u79cd\u5927\u6cd5\u4fbf\u4f1a\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u201c\u4fbf\u53ef\u4e0d\u996e\u4e0d\u98df\uff0c\u8fde\u547c\u5438\u4e5f\u5b8c\u5168\u505c\u6b62\uff0c\u8868\u9762\u4e0a\u770b\u5df2\u7ecf\u6b7b\u53bb\u4e86\uff0c\u4f46\u662f\u6700\u540e\u5374\u8fd8\u80fd\u591f\u518d\u6d3b\u8fc7 \u6765\u3002\u201d \u3000\u3000\u201c\u80fd\u652f\u6301\u591a\u4e45\uff1f\u201d \u3000\u3000\u9020\u8be3\u6d45\u4e9b\u7684\uff0c\u53ef\u4ee5\u652f\u6301\u4e09\u5929\u4e09\u591c\uff1b\u9020\u8be3\u6df1\u7684\uff0c\u636e\u8bf4\u80fd\u591f\u5c01\u7a74\u8fbe\u4e09\u4e2a\u6708\u4e4b\u4e45\u3002\u201c\u5c0f\u71d5\u5b50\u7684 \u4e00\u80a1\u5e0c\u671b\u4e0d\u89c9\u7acb\u523b\u53c8\u8d8b\u5e7b\u706d\uff0c\u6447\u4e86\u6447\u5934\u9053\uff1a\u201d\u53ef\u662f\u73b0\u5728\u5df2\u7ecf\u8fc7\u4e86\u534a\u5e74\u591a\uff0c\u5e08\u7236\u5c31\u662f\u5728\u8fd9\u65b9\u9762 \u6210\u5c31\u518d\u5927\uff0c\u4e5f\u4e0d\u53ef\u80fd\u518d\u6d3b\u4e86\uff01\u201c \u3000\u3000\u6c5f\u5343\u91cc\u6c89\u541f\u7740\u9053\uff1a\u201c\u5982\u679c\u4ed6\u5728\u5c01\u8109\u4ee5\u524d\u670d\u4e0b\u67d0\u79cd\u7279\u5236\u836f\u7269\uff0c\u65f6\u95f4\u4e5f\u8bb8\u53ef\u4ee5\u652f\u6301\u5f97\u66f4\u4e45\u4e00 \u4e9b\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u5982\u6b64\uff0c\u6c5f\u53d4\u53d4\uff01\u6211\u4eec\u5c31\u8d76\u7d27\u6253\u5f00\u77f3\u95e8\u5427\uff1f\u201d \u3000\u3000\u6c5f\u5e72\u91cc\u6447\u5934\u9053\uff1a\u201c\u4e0d\u53ef\u592a\u83bd\u649e\uff0c\u8fd9\u4ef6\u4e8b\u5fc5\u987b\u614e\u91cd\u5904\u7406\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u53c8\u4e0d\u53ef\u5462\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u5e08\u7236\u65e2\u6709\u4ea4\u4ee3\u4e09\u5e74\u540e\u624d\u80fd\u79fb\u5f00\u77f3\u95e8\uff0c\u5f53\u7136\u5fc5\u6709\u91cd\u5927\u7684\u539f\u56e0\uff0c\u6211\u4eec\u82e5\u8d38\u7136\u884c\u4e8b\uff0c \u4e07\u4e00\u56e0\u800c\u94f8\u6210\u5927\u9519\uff0c\u5c82\u4e0d\u53cd\u800c\u6210\u4e86\u4f60\u6211\u7ec8\u751f\u61be\u4e8b\uff01\u201d \u3000\u3000\u201c\u53ef\u662f\u6211OJ\u82e5\u80fd\u63d0\u524d\u6253\u5f00\u77f3\u95e8\uff0c\u603b\u662f\u591a\u4e00\u4e9b\u5e0c\u671b\u3002\u201d \u3000\u3000\u201c\u6b7b\u751f\u6709\u547d\uff0c\u5bcc\u8d35\u5728\u5929\u3002\u4e5f\u8bb8\u8fd9\u662f\u4f60\u5e08\u7236\u6599\u5b9a\u547d\u4e2d\u5982\u6b64\uff0c\u4f60\u4eec\u53c8\u4f55\u80fd\u5f3a\u6c42\u3002\u5c0f\u71d5\u5b50\uff0c\u8fd9 \u662f\u4e00\u4ef6\u4e86\u4e0d\u8d77\u7684\u5927\u4e8b\uff0c\u6211\u5fc5\u987b\u8be6\u52a0\u8003\u8651\uff0c\u624d\u80fd\u51b3\u5b9a\u8be5\u5982\u4f55\u505a\u6cd5\uff1f\u201d \u3000\u3000\u5305\u5fe0\u671b\u671b\u5929\u8272\u9053\uff1a\u201c\u6c5f\u5927\u4fa0\u548c\u5c0f\u71d5\u5b50\u4e00\u8def\u8f9b\u82e6\uff0c\u5c31\u5148\u5230\u8001\u5974\u4f4f\u5904\u559d\u676f\u8336\u5427\uff01\u201d \u3000\u3000\u6765\u5230\u8349\u68da\u95e8\u53e3\uff0c\u5305\u5fe0\u5411\u91cc\u53eb\u9053\uff1a\u201c\u8001\u4f34\u513f\uff0c\u5feb\u5feb\u51fa\u6765\uff0c\u770b\u8c01\u6765\u4e86\uff01\u201d \u3000\u3000\u4e00\u540d\u5e74\u8fc7\u82b1\u7532\u767d\u53d1\u76a4\u76a4\u7684\u8001\u5987\u4eba\u5f88\u5feb\u4fbf\u4ece\u8349\u68da\u91cc\u63a2\u51fa\u5934\u6765\u3002 \u3000\u3000\u5f53\u5979\u770b\u6e05\u6765\u4eba\u65f6\uff0c\u8fde\u5fd9\u8fce\u4e86\u51fa\u6765\uff0c\u5174\u594b\u65e0\u6bd4\u7684\u53eb\u9053\uff1a\u201c\u539f\u6765\u662f\u6c5f\u5927\u4fa0\u548c\u5c0f\u71d5\u5b50\uff0c\u5feb\u5feb\u8bf7 \u5230\u91cc\u9762\u5750\uff01\u201d \u3000\u3000\u867d\u7136\u53ea\u662f\u4f9d\u5c71\u800c\u5efa\u7684\u4e00\u95f4\u8349\u68da\uff0c\u91cc\u9762\u5374\u6536\u62fe\u5f97\u6574\u6574\u9f50\u9f50\uff0c\u4e00\u5c18\u4e0d\u67d3\u3002 \u3000\u3000\u5750\u4e0b\u4e0d\u4e45\uff0c\u5305\u8001\u592a\u592a\u4fbf\u7aef\u8fc7\u6765\u4e24\u676f\u70ed\u817e\u817e\u7684\u9999\u8317\u3002 \u3000\u3000\u5305\u5fe0\u9053\uff1a\u201c\u6211\u6765\u966a\u6c5f\u5927\u4fa0\u548c\u5c0f\u71d5\u5b50\u8bf4\u8bdd\uff0c\u4f60\u5feb\u53bb\u51c6\u5907\u7740\u3002 \u3000\u3000\u53a8\u623f\u662f\u5728\u8349\u68da\u7684\u540e\u9762\u53e6\u5916\u642d\u5efa\u7684\u4e00\u6728\u677f\u5c4b\uff0c\u56e0\u4e4b\u8349\u68da\u91cc\u624d\u80fd\u4fdd\u6301\u5f97\u5e72\u5e72\u51c0\u51c0\u3002 \u3000\u3000\u5348\u9910\u65f6\uff0c\u9910\u684c\u4e0a\u5c45\u7136\u6709\u9c7c\u6709\u8089\uff0c\u66f4\u6709\u4e00\u575b\u597d\u9152\u3002 \u3000\u3000\u5728\u6df1\u5c71\u91cc\u80fd\u5403\u5230\u8fd9\u6837\u7684\u597d\u9152\u597d\u83dc\uff0c\u79f0\u5f97\u4e0a\u662f\u4e00\u5927\u4eab\u53d7\u4e86\u3002\u4f46\u6c5f\u5343\u91cc\u548c\u5c0f\u71d5\u4e86\u60f3\u8d77\u81ea\u95ed\u5728 \u5c71\u6d1e\u5185\u7684\u5929\u96f7\u8001\u4eba\uff0c\u867d\u6709\u597d\u9152\u597d\u83dc\uff0c\u5374\u96be\u4ee5\u5c3d\u5174\u3002 \u3000\u3000\u996d\u540e\uff0c\u5305\u5fe0\u4e34\u65f6\u79bb\u5f00\u8349\u68da\uff0c\u51c6\u5907\u5230\u9644\u8fd1\u5c71\u6da7\u6253\u6876\u6c34\u56de\u6765\u3002 \u3000\u3000\u8c01\u77e5\u4ed6\u521a\u51fa\u95e8\u4e0d\u4e45\uff0c\u53c8\u6298\u56de\u6765\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u6c5f\u5927\u4fa0\uff0c\u8001\u4e3b\u4eba\u7684\u77f3\u6d1e\u5916\u7ad9\u4e86\u4e2a\u4eba\uff0c\u597d\u50cf \u662f\u2026\u2026\u201d \u3000\u3000\u6c5f\u5343\u91cc\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u597d\u50cf\u2026\u2026\u662f\u82df\u5973\u4fa0\uff01\u201d \u3000\u3000\u6c5f\u5343\u91cc\u970d\u7136\u800c\u8d77\uff0c\u53eb\u9053\uff1a\u201c\u5c0f\u71d5\u5b50\uff0c\u54b1\u4eec\u5feb\u53bb\u770b\u770b\uff01\u201d \u3000\u3000\u51fa\u4e86\u8349\u68da\uff0c\u679c\u7136\u8fdc\u8fdc\u671b\u89c1\u4e00\u4e2a\u9ed1\u8272\u7684\u5973\u5b50\u8eab\u5f71\u7ad9\u5728\u5927\u8352\u6d1e\u95e8\u5916\u3002\u53ea\u56e0\u6c5f\u5343\u91cc\u548c\u5c0f\u71d5\u5b50\u6240 \u770b\u5230\u7684\u662f\u4e00\u4e2a\u80cc\u5f71\uff0c\u4e00\u65f6\u4e4b\u95f4\u65e0\u6cd5\u786e\u5b9a\u662f\u8c01\uff1f\u4f46\u7684\u786e\u5f88\u50cf\u4e00\u9053\u98de\u8679\u82df\u6167\u6708\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u4f4e\u58f0\u9053\uff1a\u201c\u54b1\u4eec\u8fc7\u53bb\u770b\u770b\uff01\u201d \u3000\u3000\u6e10\u884c\u6e10\u8fd1\uff0c\u90a3\u8eab\u7a7f\u9ed1\u8863\u7684\u5973\u5b50\u80cc\u5f71\u4e5f\u8d8a\u6765\u8d8a\u50cf\u82df\u6167\u6708\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u6545\u610f\u91cd\u91cd\u7684\u54b3\u55fd\u4e86\u4e00\u58f0\uff01 \u3000\u3000\u9ed1\u5b50\u5973\u5b50\u84e6\u7136\u56de\u9996\uff0c\u679c\u7136\u771f\u662f\u4e00\u9053\u98de\u8679\u82df\u6167\u6708\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u60ca\u559c\u7684\u53eb\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u82df\u5973\u4fa0\u4e5f\u6765\u5230\u8fd9\u91cc\uff0c\u771f\u662f\u592a\u5de7\u4e86\uff01\u201d \u3000\u3000\u8bf4\u7740\uff0c\u624b\u62c9\u5c0f\u71d5\u5b50\u6025\u6b65\u8d70\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u82df\u6167\u6708\u9762\u8272\u4e00\u7247\u51dd\u8083\uff0c\u957f\u957f\u5401\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u6c5f\u5927\u4fa0\u6765\u8fd9\u91cc\u591a\u4e45\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e5f\u662f\u521a\u6765\u4e0d\u4e45\u3002\u201d \u3000\u3000\u82df\u6167\u6708\u51dd\u89c6\u7740\u90a3\u88ab\u5de8\u77f3\u5c01\u95ed\u7684\u77f3\u95e8\uff0c\u50cf\u81ea\u8a00\u81ea\u8bed\u822c\u9053\uff1a\u201c\u4ed6\u8fd9\u662f\u4f55\u82e6\uff0c\u597d\u597d\u7684\u65e5\u5b50\u4e0d \u8fc7\uff0c\u5374\u504f\u8981\u8d70\u4e0a\u81ea\u7edd\u4e4b\u8def\uff01\u201d \u3000\u3000\u6c5f\u5343\u91cc\u5e72\u54b3\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u6211\u90a3\u4e49\u5144\u6240\u4ee5\u8981\u8fd9\u4e48\u505a\uff0c\u82df\u5973\u4fa0\u603b\u8be5\u660e\u767d\u539f\u56e0\u5427\uff1f\u201d \u3000\u3000\u82df\u6167\u6708\u970e\u65f6\u8138\u8272\u4e00\u53d8\uff0c\u7136\u540e\u7f13\u7f13\u7684\u522b\u8fc7\u5934\u53bb\uff0c\u51b7\u58f0\u9053\uff1a\u201c\u6c5f\u5927\u4fa0\u53ef\u662f\u6028\u6211\u5bb3\u4e86\u4ed6\uff1f\u201d \u3000\u3000\u6c5f\u5e72\u91cc\u5fd9\u6b49\u7136\u966a\u7b11\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u6211\u53ea\u662f\u89c9\u5f97\u2026\u2026\u201d \u3000\u3000\u201c\u89c9\u5f97\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u82e5\u82df\u5973\u4fa0\u80fd\u5728\u534a\u5e74\u524d\u5230\u8fd9\u91cc\u6765\uff0c\u4e5f\u8bb8\u6211\u90a3\u4e49\u5144\u4e0d\u81f4\u505a\u51fa\u8fd9\u79cd\u4e8b\u6765\u3002\u201d \u3000\u3000\u201c\u7167\u4f60\u7684\u8bf4\u6cd5\uff0c\u8d23\u4efb\u8fd8\u662f\u5728\u6211\u8eab\u4e0a\u4e86\uff1f\u201d \u3000\u3000\u201c\u6c5f\u67d0\u4e0d\u6562\u8fd9\u4e48\u8bf4\u3002\u201d \u3000\u3000\u4ed6\u662f\u5426\u8bf4\u8fc7\u8981\u7b49\u4e09\u5e74\u4ee5\u540e\u518d\u6253\u5f00\u77f3\u95e8\u5bdf\u770b\u7684\u8bdd\uff1f\u201c \u3000\u3000\u201c\u4e0d\u9519\uff0c\u5f53\u65f6\u6c5f\u67d0\u4e0d\u5728\uff0c\u662f\u5bf9\u5c0f\u71d5\u5b50\u8bf4\u7684\u3002\u201d \u3000\u3000\u201c\u4ed6\u81ea\u95ed\u77f3\u5ba4\uff0c\u8be5\u6709\u534a\u5e74\u4ee5\u4e0a\u7684\u65f6\u95f4\u4e86\u5427\uff1f\u201d \u3000\u3000\u201c\u786e\u662f\u534a\u5e74\u4ee5\u4e0a\u4e86\u3002\u201d \u3000\u3000\u201c\u6c5f\u5927\u4fa0\uff0c\u54b1\u4eec\u627e\u4e2a\u5730\u65b9\u8c08\u8c08\u5427\uff01\u201d \u3000\u3000\u82df\u6167\u6708\u8fb9\u8bf4\u8fb9\u4e3e\u6b65\uff0c\u5411\u5c71\u58c1\u65c1\u4e00\u68f5\u5927\u6811\u4e0b\u8d70\u53bb\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u4ea4\u4ee3\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u4f60\u5728\u8fd9\u91cc\u7b49\u4e00\u4e0b\uff0c\u6211\u548c\u82df\u5973\u4fa0\u5355\u72ec\u8c08\u8c08\u3002\u201d \u3000\u3000\u5728\u6811\u836b\u4e0b\u7684\u9752\u77f3\u4e0a\uff0c\u82df\u6167\u6708\u548c\u6c5f\u5343\u91cc\u76f8\u5bf9\u5750\u4e0b\u3002 \u3000\u3000\u82df\u6167\u6708\u7684\u795e\u8272\u4f3c\u4e4e\u8d8a\u6765\u8d8a\u51dd\u91cd\uff0c\u51e0\u5ea6\u53e3\u9f7f\u542f\u52a8\uff0c\u5374\u53c8\u6ca1\u8bf4\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u4f3c\u4e4e\u4e0d\u4fbf\u6b63\u89c6\u5bf9\u65b9\u7684\u53cd\u5e94\uff0c\u4f4e\u4e0b\u5934\u9053\uff1a\u201c\u82df\u5973\u4fa0\u6709\u8bdd\u8bf7\u8bb2\uff0c\u8fd9\u91cc\u6ca1\u6709\u5916\u4eba\u3002\u201d \u3000\u3000\u82df\u6167\u6708\u53f9\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u6c5f\u5927\u4fa0\uff0c\u8fd9\u4e8c\u5341\u5e74\u6765\uff0c\u4f60\u4e00\u5b9a\u8ba4\u4e3a\u662f\u6211\u8f9c\u8d1f\u4e86\u4ed6\uff0c\u4e5f\u8bef\u4f1a\u4e86\u4ed6\uff0c \u751a\u81f3\u53ef\u80fd\u8ba4\u4e3a\u6211\u662f\u4e2a\u65e0\u60c5\u65e0\u4e49\u7684\u4eba\uff0c\u5bf9\u5417\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u5fd9\u6447\u5934\u9053\uff1a\u201c\u6211\u5e76\u6ca1\u6709\u8fd9\u4e48\u60f3\uff0c\u5982\u679c\u82df\u5973\u4fa0\u80fd\u63d0\u51fa\u89e3\u91ca\uff0c\u90a3\u5f53\u7136\u5c31\u66f4\u597d\u4e86\u3002\u201d \u3000\u3000\u201c\u5f53\u7740\u6211\u7684\u9762\uff0c\u7528\u4e0d\u7740\u8a00\u4e0d\u7531\u8877\uff0c\u6709\u8bdd\u5c31\u76f4\u63a5\u95ee\u5427\uff01\u201d \u3000\u3000\u201c\u82df\u5973\u4fa0\u65e2\u7136\u5e76\u672a\u8f9c\u8d1f\u4ed6\uff0c\u4e3a\u4ec0\u4e48\u5728\u4ed6\u751f\u524d\u7684\u4e8c\u5341\u5e74\u4e00\u76f4\u4e0d\u66fe\u518d\u6765\u770b\u4ed6\uff1f\u201d \u3000\u3000\u201c\u6211\u60f3\u6765\uff0c\u4f46\u6211\u5374\u4e0d\u80fd\u7a7a\u7740\u624b\u6765\u3002\u201d \u3000\u3000\u201c\u82df\u5973\u4fa0\u8fd9\u8bdd\u2026\u2026\u201d \u3000\u3000\u201c\u4ed6\u7684\u773c\u775b\u662f\u88ab\u6211\u5931\u624b\u6233\u4f24\u7684\u2026\u2026\u201d \u3000\u3000\u82df\u6167\u6708\u8bf4\u5230\u8fd9\u91cc\uff0c\u987f\u4e86\u4e00\u987f\u624d\u53c8\u63a5\u7740\u9053\uff1a\u201c\u5176\u5b9e\u8bf4\u6210\u5931\u624b\uff0c\u4f3c\u4e4e\u5e76\u4e0d\u59a5\u5f53\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u832b\u7136\u95ee\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u636e\u6211\u9884\u6599\uff0c\u5f53\u65f6\u4ed6\u4e00\u5b9a\u4f1a\u51fa\u624b\u963b\u6321\uff0c\u51ed\u4ed6\u7684\u6b66\u529f\uff0c\u6211\u5373\u4f7f\u5b58\u5fc3\u4f24\u4ed6\uff0c\u4e5f\u5fc5\u5b9a\u4e0d\u80fd \u5f97\u624b\uff0c\u8c01\u77e5\u4ed6\u7adf\u6beb\u4e0d\u95ea\u907f\uff0c\u4efb\u7531\u6211\u7684\u62db\u52bf\u9012\u6ee1\uff0c\u6211\u867d\u7136\u5728\u5927\u5403\u4e00\u60ca\u4e4b\u4e0b\u6025\u6025\u6536\u62db\uff0c\u4f46\u5728\u65f6\u95f4 \u4e0a\u5df2\u7ecf\u6765\u4e0d\u53ca\u4e86\uff0c\u6240\u5e78\u56e0\u6211\u5df2\u6709\u8b66\u89c9\uff0c\u624b\u6cd5\u5df2\u51cf\u8f7b\u4e86\u8bb8\u591a\uff0c\u5426\u5219\u4f24\u52bf\u53ea\u6015\u201d\u82df\u5973\u4fa0\u521a\u624d\u6240\u8bf4 \u7684\u4e0d\u80fd\u7a7a\u624b\u800c\u6765\uff0c\u53c8\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201c \u3000\u3000\u201c\u6c5f\u5927\u4fa0\u53ef\u77e5\u9053\u4e8c\u5341\u5e74\u6765\u6211\u90fd\u5728\u505a\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6c5f\u67d0\u53ea\u77e5\u9053\u82df\u5973\u4fa0\u548c\u6211\u4e49\u5144\u5931\u548c\u4e4b\u540e\uff0c\u4fbf\u4e00\u76f4\u9690\u5c45\u5728\u9ec4\u5c71\uff0c\u6709\u65f6\u4e5f\u4f4f\u8fc7\u6d1e\u5ead\u541b\u5c71\uff0c\u4ece \u6b64\u4e0d\u518d\u8fc7\u95ee\u6b66\u6797\u4e2d\u4e8b\u3002\u9664\u4e86\u516b\u4e5d\u5e74\u524d\u6536\u4e09\u516c\u4e3b\u4e3a\u5f92\u5916\uff0c\u51e0\u4e4e\u5df2\u548c\u6240\u6709\u6b66\u6797\u4eba\u7269\u4e0d\u518d\u5f80\u6765 \u4e86\u3002\u201d \u3000\u3000\u201c\u6c5f\u5927\u5feb\u77e5\u9053\u5f97\u5f88\u6e05\u695a\uff0c\u5176\u5b9e\u8fd9\u4e8c\u5341\u5e74\u6765\uff0c\u6211\u5e76\u672a\u95f2\u7740\uff0c\u4e00\u76f4\u5954\u6ce2\u4e8e\u5404\u5927\u540d\u5c71\u5927\u5ddd\u4e4b \u95f4\u3002\u201d \u3000\u3000\u201c\u83ab\u975e\u662f\u5728\u91c7\u836f\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\u3002\u6211\u66fe\u8bf7\u6559\u8fc7\u5723\u624b\u533b\u9690\u5357\u5929\u4eea\uff0c\u7ecf\u4ed6\u5f00\u51fa\u836f\u65b9\uff0c\u7136\u540e\u6df1\u5165\u5404\u5927\u540d\u5c71\u91c7\u96c6\u5404\u79cd\u73cd\u5947 \u836f\u6750\uff0c\u4ec5\u4ec5\u2018\u4ed9\u733f\u6cea\u2019\u548c\u2018\u4e39\u9e64\u9ad3\u2019\u5c31\u6574\u6574\u5316\u4e86\u4e09\u5e74\u5de5\u592b\u3002\u201d \u3000\u3000\u201c\u82df\u5973\u4fa0\u8fd9\u6b21\u6765\uff0c\u662f\u5426\u5df2\u5e26\u6765\u836f\u7269\uff1f\u201d \u3000\u3000\u201c\u672c\u6765\uff0c\u4e3a\u4ed6\u590d\u660e\u7684\u836f\u7269\uff0c\u5723\u624b\u533b\u9690\u5357\u5929\u4eea\u5df2\u5728\u534a\u5e74\u524d\u70bc\u5236\u6210\u529f\uff0c\u4f46\u8fd8\u662f\u665a\u4e86\u4e00\u6b65\u3002\u81f3 \u4e8e\u8fd9\u6b21\u6765\uff0c\u6211\u53c8\u53e6\u5916\u5e26\u6765\u66f4\u4e3a\u8d35\u91cd\u7684\u836f\u7269\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u836f\u7269\uff1f\u201d \u3000\u3000\u201c\u6c5f\u5927\u4fa0\u5fc5\u5b9a\u542c\u8bf4\u8fc7\u2018\u8fd4\u5143\u5927\u8fd8\u4e39\u2019\u5427\uff1f\u201d \u3000\u3000\u201c\u6c5f\u67d0\u542c\u8bf4\u8fc7\u3002\u82df\u5973\u4fa0\u662f\u600e\u4e48\u5f97\u5230\u8fd9\u79cd\u582a\u79f0\u5929\u4e0b\u81f3\u5c0a\u81f3\u5b9d\u7684\u836f\u7269\uff1f\u201d \u3000\u3000\u201c\u4e5f\u662f\u5723\u624b\u533b\u9690\u5357\u5929\u4eea\u6240\u8d60\u7684\u3002\u4ed6\u7a77\u6bd5\u751f\u7cbe\u529b\uff0c\u53ea\u70bc\u51fa\u5341\u9897\uff0c\u73b0\u5728\u628a\u4ec5\u4f59\u7684\u4e24\u9897\u8d60\u7ed9\u6211 \u4e00\u9897\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u6447\u6447\u5934\uff0c\u6fc0\u52a8\u65e0\u6bd4\u7684\u5401\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u53ef\u60dc\u5df2\u7ecf\u592a\u8fdf\u4e86\uff01\u201d \u3000\u3000\u82df\u6167\u6708\u53cc\u7738\u4e2d\u4ecd\u95ea\u5c04\u7740\u4e00\u4e1d\u5e0c\u671b\u4e4b\u5149\uff0c\u50cf\u5728\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u2018\u8fd4\u5143\u5927\u8fd8\u4e39\u2019\u80fd\u8d77\u6b7b\u56de \u751f\uff0c\u65e2\u7136\u5df2\u7ecf\u5e26\u6765\uff0c\u5c31\u5fc5\u7136\u8bd5\u4e0a\u4e00\u8bd5\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u4eba\u6b7b\u4e0d\u80fd\u590d\u751f\uff0c\u6240\u8c13\u8d77\u6b7b\u56de\u751f\u6307\u7684\u4e0d\u8fc7\u662f\u4eba\u5728\u75c5\u5165\u818f\u80b2\u4e4b\u540e\uff0c\u5999\u624b\u56de\u6625\u800c\u5df2\uff0c\u82e5 \u5df2\u53d8\u6210\u4e00\u5806\u67af\u9aa8\uff0c\u2018\u8fd4\u5143\u5927\u8fd8\u4e39\u2019\u53c8\u6709\u4f55\u7528\uff1f\u201d \u3000\u3000\u201c\u4e0d\u7ba1\u5982\u4f55\uff0c\u6211\u7684\u610f\u601d\u662f\u5148\u6253\u5f00\u77f3\u95e8\u518d\u8bf4\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u81ea\u7136\u4e5f\u6025\u4e8e\u67e5\u770b\u6d1e\u5185\u771f\u76f8\uff0c\u968f\u5373\u5411\u540e\u62db\u62db\u624b\u9053\uff1a\u201c\u5c0f\u71d5\u5b50\uff0c\u5feb\u8fc7\u6765\u62dc\u89c1\u8461\u5973\u4fa0 \u5427\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u5fd9\u5954\u4e86\u8fc7\u6765\uff0c\u606d\u606d\u656c\u656c\u7684\u5411\u82df\u6167\u6708\u65bd\u4e86\u4e00\u793c\u3002 \u3000\u3000\u82df\u6167\u6708\u8fd9\u624d\u53c8\u95ee\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u8fd8\u6709\u4ec0\u4e48\u4eba\u524d\u6765\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u6765\u5230\u9752\u4e91\u5d16\u7684\u53ea\u6709\u6c5f\u67d0\u548c\u5c0f\u71d5\u5b50\u4e24\u4e2a\u4eba\u3002\u201d \u3000\u3000\u201c\u90a3\u662f\u8bf4\u8fd8\u6709\u540c\u6765\u7684\u5728\u522b\u5904\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u201c\u8fd8\u6709\u54ea\u4e9b\u4eba\uff1f\u4e91\u513f\u6765\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u968f\u5373\u628a\u4e09\u516c\u4e3b\u3001\u738b\u5f64\u7b49\u4eba\u8fd9\u6b21\u79bb\u4eac\u540e\u7684\u7ecf\u8fc7\u8bf4\u4e86\u4e00\u904d\u3002 \u3000\u3000\u82df\u6167\u6708\u671b\u4e86\u5c0f\u71d5\u5b50\u4e00\u773c\uff0c\u4f4e\u58f0\u95ee\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u6211\u66fe\u6709\u610f\u5c06\u4e91\u513f\u548c\u5c0f\u71d5\u5b50\u914d\u6210\u4e00\u5bf9\uff0c\u4e0d\u77e5 \u4ed6\u4eec\u5728\u611f\u60c5\u65b9\u9762\u8fdb\u5c55\u5f97\u5982\u4f55\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u4e00\u542c\u82df\u6167\u6708\u8c08\u5230\u8fd9\u4ef6\u4e8b\uff0c\u7acb\u523b\u8fdc\u8fdc\u8eb2\u5230\u4e00\u8fb9\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u6447\u6447\u5934\u9053\uff1a\u201c\u6beb\u65e0\u8fdb\u5c55\u3002\u201d \u3000\u3000\u82df\u6167\u6708\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u96be\u9053\u4ed6\u4eec\u4e24\u4eba\u6027\u60c5\u4e0d\u80fd\u76f8\u6295\uff1f\u201d \u3000\u3000\u201c\u90a3\u5012\u4e0d\u662f\u3002\u201d \u3000\u3000\u201c\u7a76\u7adf\u4e3a\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u201c\u4e09\u516c\u4e3b\u4e0d\u8bba\u4f4f\u5ba2\u6808\u6216\u8d76\u8def\u65f6\u4e58\u8f7f\uff0c\u7edd\u5c11\u79bb\u5f00\u623f\u95e8\u6216\u8f7f\u95e8\u4e00\u6b65\uff0c\u8fde\u738b\u7edf\u9886\u90fd\u96be\u5f97\u6709\u548c\u5979 \u63a5\u89e6\u7684\u673a\u4f1a\u3002\u4f55\u51b5\u5c0f\u71d5\u5b50\u3002\u201d \u3000\u3000\u82df\u6167\u6708\u7d27\u76b1\u7740\u53cc\u7709\u9053\uff1a\u201c\u539f\u6765\u662f\u8fd9\u6837\u3002\u7b49\u8fd9\u8fb9\u7684\u4e8b\u529e\u5b8c\u540e\uff0c\u6211\u4e00\u5b9a\u4f1a\u627e\u673a\u4f1a\u548c\u4e91\u513f\u4ed4\u7ec6 \u8c08\u8c08\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u6447\u5934\u9053\uff1a\u201c\u82df\u5973\u4fa0\u4e0d\u5fc5\u4e3a\u8fd9\u4e8b\u8d39\u5fc3\u3002\u201d \u3000\u3000\u82df\u6167\u6708\u4e00\u6014\u9053\uff1a\u201c\u83ab\u975e\u5c0f\u71d5\u5b50\u4e0d\u540c\u610f\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u6b49\u7136\u4e00\u7b11\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u7ec8\u7a76\u662f\u751f\u957f\u5728\u5e1d\u738b\u4e4b\u5bb6\u7684\u5bcc\u8d35\u4e2d\u4eba\uff0c\u800c\u5c0f\u71d5\u5b50\u5219\u53ea\u662f\u4e00\u540d \u6c5f\u6e56\u5b50\u5f1f\uff0c\u4e0d\u8bba\u662f\u751f\u6d3b\u73af\u5883\u6216\u5bb6\u4e16\uff0c\u4e24\u4eba\u5339\u914d\uff0c\u7edd\u4e0d\u76f8\u79f0\uff0c\u82df\u5973\u4fa0\u65e9\u8be5\u60f3\u5230\u8fd9\u65b9\u9762\u624d\u5bf9\u3002 \u3000\u3000\u82df\u6167\u6708\u6c89\u541f\u4e86\u534a\u664c\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u5c31\u6162\u6162\u518d\u8bf4\u5427\uff01\u73b0\u5728\u5148\u542f\u5f00\u77f3\u95e8\u8981\u7d27\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u5411\u5c0f\u71d5\u5b50\u62db\u62db\u624b\u9053\uff1a\u201c\u4f60\u8fc7\u6765\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6765\u5230\u8ddf\u524d\uff0c\u9053\uff1a\u201c\u6c5f\u53d4\u53d4\u6709\u4ec0\u4e48\u5429\u5490\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u90d1\u91cd\u5176\u4e8b\u7684\u9053\uff1a\u201c\u82df\u5973\u4fa0\u5df2\u51b3\u5b9a\u5c06\u77f3\u95e8\u6253\u5f00\uff0c\u73b0\u5728\u6211\u60f3\u8bd5\u8bd5\u4f60\u7684\u5185\u529b\uff0c\u662f\u5426\u80fd\u5c06 \u5835\u4f4f\u6d1e\u95e8\u7684\u5de8\u77f3\u79fb\u5f00\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u4e24\u773c\u970e\u970e\u7684\u9053\uff1a\u201c\u771f\u7684\u8981\u6253\u5f00\uff1f\u53ef\u662f\u5e08\u7236\u4ea4\u4ee3\u8981\u7b49\u4e09\u5e74\uff01\u201d \u3000\u3000\u201c\u82df\u5973\u4fa0\u662f\u4f60\u5e08\u7236\u7684\u4ec0\u4e48\u4eba\uff0c\u4f60\u8be5\u660e\u767d\uff0c\u5979\u8981\u6253\u5f00\uff0c\u662f\u4e3a\u4e86\u6551\u4f60\u5e08\u7236\uff0c\u96be\u9053\u4f60\u5c31\u4e0d\u60f3\u770b \u5e08\u7236\u73b0\u5728\u662f\u4ec0\u4e48\u6837\u5b50\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u4e00\u58f0\u4e0d\u54cd\u7684\u8d70\u5230\u6d1e\u95e8\u8fb9\uff0c\u5148\u642c\u5f00\u6700\u4e0a\u9762\u90a3\u5757\u91cd\u903e\u5343\u65a4\u7684\u5de8\u77f3\uff0c\u63a5\u7740\u518d\u5c06\u53e6\u5916\u4e24\u5757 \u5de8\u77f3\u642c\u5f00\uff0c\u770b\u6765\u4f3c\u4e4e\u6beb\u4e0d\u5403\u529b\u3002 \u3000\u3000\u8fd9\u7b49\u5185\u529f\uff0c\u8fd9\u7b49\u795e\u529b\uff0c\u8fde\u82df\u6167\u6708\u90fd\u770b\u5f97\u6697\u6697\u8d5e\u53f9\uff0c\u6df1\u5e86\u5929\u96f7\u8001\u4eba\u540e\u7ee7\u6709\u4eba\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u4e0d\u6562\u5148\u884c\u8fdb\u5165\uff0c\u642c\u5f00\u5de8\u77f3\u4e4b\u540e\u9000\u56de\u6c5f\u5343\u91cc\u8eab\u8fb9\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u5411\u4fa7\u65b9\u95ea\u5f00\u4e00\u6b65\uff0c\u9053\uff1a\u201c\u82df\u5973\u4fa0\u8bf7\uff01\u201d \u3000\u3000\u82df\u6167\u6708\u5e76\u672a\u5ba2\u6c14\uff0c\u4e3e\u6b65\u5411\u6d1e\u5185\u8d70\u53bb\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u548c\u5c0f\u71d5\u5b50\u7d27\u968f\u5728\u540e\u3002 \u3000\u3000\u6d1e\u5185\u4e00\u7247\u5e7d\u6697\uff0c\u4f38\u624b\u4e0d\u89c1\u4e94\u6307\u3002 \u3000\u3000\u82df\u6167\u6708\u8fb9\u8d70\u8fb9\u95ee\u9053\uff1a\u201c\u5c0f\u71d5\u5b50\uff0c\u4f60\u5e08\u7236\u95ed\u6d1e\u540e\uff0c\u5fc5\u5b9a\u5728\u91cc\u9762\u6253\u5750\uff0c\u4ed6\u6253\u5750\u4e4b\u5904\u5728\u54ea\u91cc \u5462\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u5e08\u7236\u6253\u5750\u7684\u5730\u65b9\u5e76\u4e0d\u56fa\u5b9a\uff0c\u5fc5\u987b\u6162\u6162\u627e\u624d\u6210\u3002\u201d \u3000\u3000\u82df\u6167\u6708\u811a\u4e0b\u5f00\u59cb\u663e\u5f97\u5f02\u5e38\u7f13\u6162\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u548c\u5c0f\u71d5\u5b50\u5fc3\u91cc\u90fd\u6709\u6570\uff0c\u5979\u4e00\u5b9a\u662f\u62c5\u5fc3\u884c\u8fdb\u65f6\u649e\u5230\u5929\u96f7\u8001\u4eba\u3002 \u3000\u3000\u76f4\u6478\u7d22\u524d\u884c\u4e86\u76cf\u8336\u5de5\u592b\uff0c\u5df2\u6765\u5230\u5929\u96f7\u8001\u4eba\u5c45\u4f4f\u7684\u77f3\u5ba4\u3002 \u3000\u3000\u521a\u8fdb\u5ba4\u95e8\uff0c\u4fbf\u542c\u5c0f\u71d5\u5b50\u5931\u58f0\u53eb\u9053\uff1a\u201c\u77f3\u5e8a\u4e0a\u90a3\u4e0d\u662f\u5e08\u7236\u5417\uff1f\u201d \u3000\u3000\u82df\u6167\u6708\u548c\u6c5f\u5343\u91cc\u51dd\u795e\u671b\u53bb\uff0c\u679c\u7136\u5728\u9760\u58c1\u77f3\u5e8a\u4e0a\u6d8c\u73b0\u7740\u4e00\u4e2a\u4eba\u5f62\u9ed1\u5f71\uff0c\u5206\u660e\u662f\u4ecd\u5728\u6253\u5750\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0c\u4e09\u4eba\u4e0d\u77e5\u662f\u60ca\u662f\u559c\uff0c\u662f\u60b2\u51c4\u8fd8\u662f\u60f6\u60ca\uff1f\u201c \u3000\u3000\u4ed6\u4eec\u7f13\u7f13\u79fb\u52a8\u811a\u6b65\uff0c\u6765\u5230\u5e8a\u524d\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u6fc0\u52a8\u65e0\u6bd4\u7684\u53eb\u9053\uff1a\u201c\u5e08\u7236\uff01\u5e08\u7236\u2026\u2026\u201d \u3000\u3000\u6c5f\u5343\u91cc\u8f7b\u62cd\u4e86\u4e00\u4e0b\u5c0f\u71d5\u5b50\u80a9\u8180\u9053\uff1a\u201c\u522b\u53eb\uff0c\u4f60\u5e08\u7236\u4e0d\u53ef\u80fd\u542c\u5230\u7684\u3002\u201d \u3000\u3000\u82df\u6167\u6708\u63a2\u624b\u5728\u5e8a\u4e0a\u4eba\u5f71\u7684\u53e3\u9f3b\u524d\u8bd5\u4e86\u4e00\u4e0b\uff0c\u6beb\u65e0\u547c\u5438\u611f\u89c9\u3002 \u3000\u3000\u5979\u7f29\u56de\u624b\uff0c\u95ee\u9053\uff1a\u201c\u6c5f\u5927\u4fa0\u662f\u5426\u5e26\u6709\u706b\u6298\u5b50\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u6c5f\u67d0\u8eab\u8fb9\u51c6\u5907\u4e86\u706b\u6298\u5b50\uff0c\u53ea\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u83ab\u975e\u2026\u2026\u201d \u3000\u3000\u201c\u4e49\u5144\u73b0\u5728\u663e\u7136\u662f\u5165\u5b9a\u72b6\u6001\uff0c\u82e5\u51b2\u4e86\u706b\u5149\uff0c\u53ea\u6015\u5bf9\u4ed6\u4e0d\u5229\u3002\u201d \u3000\u3000\u201c\u8fd9\u65b9\u9762\u6211\u4e5f\u60f3\u5230\u4e86\uff0c\u4f46\u5ba4\u5185\u82e5\u65e0\u7167\u660e\uff0c\u53c8\u5982\u4f55\u5bdf\u770b\u4ed6\u76ee\u524d\u7684\u60c5\u5f62\uff1f\u201d \u3000\u3000\u201c\u82df\u5973\u4fa0\u8bf4\u7684\u6709\u7406\uff0c\u53ea\u662f\u82e5\u71c3\u4eae\u706b\u6298\uff0c\u5fc5\u5b9a\u95ea\u5149\uff0c\u4e0d\u5982\u5230\u5305\u5fe0\u90a3\u513f\u7aef\u4e00\u76cf\u6cb9\u706f\u6765\u8f83\u4e3a\u59a5 \u5f53\u3002\u201d \u3000\u3000\u201c\u8bf4\u7684\u6709\u7406\uff0c\u5c0f\u71d5\u5b50\u5c31\u53bb\u62ff\u706f\u6765\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u5e94\u4e86\u4e00\u58f0\u201c\u662f\u201d\u3002 \u3000\u3000\u521a\u8d70\u4e86\u51e0\u6b65\uff0c\u4fbf\u542c\u82df\u6167\u6708\u53c8\u4ea4\u4ee3\u9053\uff1a\u201c\u8981\u628a\u706f\u5728\u5916\u9762\u70b9\u597d\uff0c\u518d\u7aef\u5230\u91cc\u9762\u6765\uff0c\u8d70\u8def\u811a\u6b65\u8981 \u8f7b\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u8d70\u540e\uff0c\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u770b\u73b0\u5728\u8fd9\u60c5\u5f62\uff0c\u8bf4\u4e0d\u5b9a\u771f\u7684\u6709\u6551\u3002\u201d \u3000\u3000\u82df\u6167\u6708\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u6c5f\u5927\u4fa0\u662f\u6839\u636e\u4ec0\u4e48\u800c\u8a00\uff1f\u201d \u3000\u3000\u201c\u82df\u5973\u4fa0\u5fc5\u5b9a\u5df2\u770b\u5f97\u5f88\u6e05\u695a\u4e86\uff0c\u4e49\u5144\u7684\u8eab\u5f62\u4ecd\u5b8c\u597d\u5982\u6614\uff0c\u4e5f\u672a\u53d1\u751f\u5f02\u5473\uff0c\u8fd9\u4fbf\u662f\u6700\u597d\u7684 \u8bc1\u660e\u3002\u201d \u3000\u3000\u201c\u7684\u786e\uff0c\u4ee5\u4ed6\u7684\u7cbe\u7eaf\u5185\u529f\uff0c\u8bf4\u4e0d\u5b9a\u771f\u7684\u5df2\u65bd\u4e86\u9f9f\u606f\u5927\u6cd5\uff0c\u679c\u771f\u5982\u6b64\uff0c\u8fd9\u9897\u2018\u8fd4\u5143\u5927\u8fd8 \u4e39\u2019\u5c31\u6d3e\u4e0a\u7528\u573a\u4e86\u3002\u201d \u3000\u3000\u201c\u82df\u5973\u4fa0\u518d\u6478\u6478\u4ed6\u8eab\u4e0a\u7684\u808c\u8089\u662f\u5426\u50f5\u786c\uff1f\u201d \u3000\u3000\u201c\u5c31\u8bf7\u6c5f\u5927\u4fa0\u8bd5\u8bd5\u770b\uff01\u201d \u3000\u3000\u6c5f\u5343\u91cc\u8f7b\u8f7b\u63a2\u8fc7\u624b\u53bb\uff0c\u89e6\u6478\u5230\u5e8a\u4e0a\u4eba\u7684\u80a9\u90e8\uff0c\u53ea\u89c9\u808c\u8089\u5df2\u5b8c\u5168\u50f5\u4f4f\uff0c\u800c\u4e14\u89e6\u624b\u751f\u5bd2\u3002 \u3000\u3000\u4ed6\u554a\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u600e\u4e48\u4f1a\u662f\u8fd9\u6837\u5b50\uff0c\u82df\u5973\u4fa0\uff01\u53ea\u6015\u6ca1\u6709\u5e0c\u671b\u4e86\uff01\u201d \u3000\u3000\u82df\u6167\u6708\u5e76\u672a\u5403\u60ca\uff0c\u7f13\u7f13\u5730\u9053\uff1a\u201c\u808c\u8089\u53c8\u50f5\u53c8\u51b7\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u201c\u6b63\u662f\u8fd9\u6837\u5b50\u3002\u201d \u3000\u3000\u201c\u8fd9\u662f\u5fc5\u7136\u7684\u73b0\u8c61\uff0c\u4e0d\u503c\u5f97\u5927\u60ca\u5c0f\u602a\u3002\u4e00\u4e2a\u4eba\u5f53\u5b9e\u65bd\u9f9f\u606f\u5927\u6cd5\u4e4b\u540e\uff0c\u5c31\u50cf\u6b7b\u53bb\u4e00\u6837\uff0c\u4f53 \u5185\u7684\u70ed\u91cf\u5fc5\u7136\u51cf\u9000\uff0c\u5426\u5219\u4e0d\u5403\u4e0d\u559d\uff0c\u70ed\u91cf\u53c8\u5982\u4f55\u4f9b\u5e94\uff1f\u201d \u3000\u3000\u201c\u53ef\u662f\u636e\u6c5f\u67d0\u6240\u77e5\uff0c\u9f9f\u606f\u5927\u6cd5\u6700\u591a\u53ea\u80fd\u652f\u6491\u4e09\u4e2a\u6708\uff0c\u73b0\u5728\u5df2\u8fc7\u4e86\u4e09\u6708\uff0c\u800c\u4e14\u4e49\u5144\u53c8\u66fe\u5429 \u5490\u8fc7\u8981\u4e09\u5e74\u4e4b\u540e\u624d\u80fd\u5f00\u6d1e\u95e8\uff0c\u8fd9\u53c8\u662f\u4ec0\u4e48\u539f\u56e0\uff1f\u201d \u3000\u3000\u82df\u6167\u6708\u53f9\u606f\u4e00\u58f0\u9053\uff1a\u201c\u4ed6\u7684\u76ee\u7684\u4e0d\u5916\u662f\u5e0c\u671b\u5c31\u6b64\u6b7b\u53bb\uff0c\u5982\u679c\u4ed6\u80fd\u9884\u6599\u5230\u6211\u4f1a\u524d\u6765\uff0c\u60c5\u5f62 \u4e5f\u8bb8\u5c31\u4e0d\u662f\u8fd9\u6837\u4e86\u3002\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u77f3\u5ba4\u5916\u4f20\u6765\u811a\u6b65\u58f0\uff01\u60f3\u5fc5\u662f\u5c0f\u71d5\u5b50\u638c\u706f\u800c\u6765\u3002 \u3000\u3000\u706f\u5149\u7167\u5165\u5ba4\u5185\uff0c\u8fdb\u6765\u7684\u9664\u5c0f\u71d5\u5b50\u5916\uff0c\u5305\u5fe0\u592b\u5987\u4e5f\u8ddf\u7740\u6765\u4e86\u3002 \u3000\u3000\u5305\u5fe0\u592b\u5987\u5bf9\u8001\u4e3b\u4eba\u7684\u5173\u5207\u7edd\u4e0d\u4e9a\u4e8e\u82df\u6167\u6708\u3001\u6c5f\u5343\u91cc\u6216\u5c0f\u71d5\u5b50\u3002 \u3000\u3000\u4f17\u4eba\u6765\u5230\u77f3\u5e8a\u524d\uff0c\u53ea\u89c1\u5e8a\u4e0a\u6253\u5750\u7684\u5929\u96f7\u8001\u4eba\u679c\u7136\u6beb\u53d1\u672a\u53d8\u3002 \u3000\u3000\u4f46\u4f17\u4eba\u90fd\u6ca1\u6709\u8fc7\u4efd\u60ca\u559c\u7684\u8868\u60c5\uff0c\u5728\u5305\u5fe0\u8fd9\u5bf9\u8001\u592b\u5987\u7684\u60f3\u6cd5\uff0c\u8001\u4e3b\u4eba\u867d\u7136\u6829\u6829\u5982\u751f\uff0c\u4f46\u5374 \u672a\u5fc5\u771f\u7684\u80fd\u6d3b\u8fc7\u6765\u3002 \u3000\u3000\u81f3\u4e8e\u82df\u6167\u6708\uff0c\u867d\u7136\u5185\u5fc3\u5b58\u7740\u6781\u5927\u7684\u5e0c\u671b\uff0c\u5bf9\u662f\u5426\u80fd\u5c06\u5929\u96f7\u8001\u4eba\u6062\u590d\u751f\u547d\uff0c\u4e5f\u5e76\u65e0\u7edd\u5bf9\u7684 \u628a\u63e1\u3002 \u3000\u3000\u77f3\u5ba4\u5185\u9759\u5f97\u8fde\u6839\u7ee3\u82b1\u9488\u843d\u5730\u7684\u58f0\u97f3\u90fd\u80fd\u542c\u5f97\u89c1\u3002 \u3000\u3000\u8bb8\u4e45\uff0c\u624d\u542c\u6c5f\u5343\u91cc\u95ee\u9053\uff1a\u201c\u82df\u5973\u4fa0\u6253\u7b97\u600e\u4e48\u529e\uff1f\u73b0\u5728\u5c31\u5429\u5490\uff0c\u8ba9\u6c5f\u67d0\u548c\u5c0f\u71d5\u5b50\u4e5f\u5c3d\u4e00\u70b9 \u529b\u3002\u201d \u3000\u3000\u82df\u6167\u6708\u6447\u5934\u9053\uff1a\u201c\u73b0\u5728\u4e0d\u53ef\u79fb\u52a8\u4ed6\uff0c\u8c01\u90fd\u7528\u4e0d\u7740\u5e2e\u5fd9\uff0c\u6709\u6211\u4e00\u4eba\u5c31\u884c\u4e86\u3002\u4f60\u4eec\u90fd\u8bf7\u51fa \u53bb\uff0c\u5f85\u4f1a\u513f\u6211\u81ea\u4f1a\u544a\u8bc9\u4f60\u4eec\u8fd9\u91cc\u7684\u60c5\u5f62\u3002\u201d \u3000\u3000\u63a5\u7740\u53c8\u5429\u5490\u5305\u5fe0\u9053\uff1a\u201c\u9ebb\u70e6\u8001\u4eba\u5bb6\u51c6\u5907\u4e00\u6876\u70ed\u6c34\u548c\u4e00\u58f6\u70ed\u8336\uff0c\u9001\u5230\u8fd9\u91cc\u6765\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u3001\u5c0f\u71d5\u5b50\u3001\u5305\u5fe0\u592b\u5987\u4f9d\u8a00\u79bb\u5f00\u77f3\u5ba4\u3002 \u3000\u3000\u4ed6\u4eec\u518d\u56de\u5230\u8349\u68da\uff0c\u5305\u5fe0\u592b\u5987\u5fd9\u7740\u70e7\u6c34\u70e7\u8336\u3002 \u3000\u3000\u4e0d\u4e00\u4f1a\u513f\uff0c\u5305\u5fe0\u4fbf\u5c06\u70ed\u6c34\u70ed\u8336\u9001\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5f53\u5305\u5fe0\u56de\u6765\u65f6\uff0c\u6c5f\u5343\u91cc\u95ee\u9053\uff1a\u201c\u4f60\u53ef\u770b\u5230\u82df\u5973\u4fa0\u5bf9\u8001\u4e3b\u4eba\u7528\u7684\u662f\u4ec0\u4e48\u89e3\u6551\u4e4b\u6cd5\uff1f\u201d \u3000\u3000\u5305\u5fe0\u9053\uff1a\u201c\u82df\u5973\u4fa0\u4e0d\u51c6\u5974\u624d\u770b\uff0c\u8001\u5974\u653e\u4e0b\u6c34\u548c\u8336\uff0c\u5c31\u88ab\u5979\u8d76\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u4e8e\u662f\uff0c\u6c5f\u5343\u91cc\u548c\u5c0f\u71d5\u5b50\u53ea\u6709\u8010\u5fc3\u7684\u7b49\u4e0b\u53bb\u3002 \u3000\u3000\u8db3\u8db3\u4e00\u4e2a\u65f6\u8fb0\u4e4b\u540e\uff0c\u624d\u89c1\u82df\u6167\u6708\u6ee1\u5934\u5927\u6c57\u5730\u8d70\u8fdb\u8349\u68da\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u8feb\u4e0d\u53ca\u5f85\u7684\u95ee\u9053\uff1a\u201c\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u201c\u76ee\u524d\u8fd8\u5f88\u96be\u8bf4\uff0c\u4e0d\u8fc7\u5927\u5bb6\u4e0d\u5fc5\u7d27\u5f20\uff0c\u56e0\u4e3a\u2018\u8fd4\u5143\u5927\u8fd8\u4e39\u2019\u867d\u662f\u4ed9\u4e39\u7075\u836f\uff0c\u4e5f\u4e0d\u53ef\u80fd\u4e00 \u670d\u4e0b\u53bb\u5c31\u9a6c\u4e0a\u89c1\u6548\uff0c\u603b\u8981\u4e00\u6bb5\u65f6\u95f4\u624d\u80fd\u53d1\u6325\u836f\u6548\u3002\u201d \u3000\u3000\u201c\u8981\u7b49\u591a\u4e45\u65f6\u95f4\u624d\u80fd\u770b\u51fa\u836f\u529b\u662f\u5426\u6709\u6548\uff1f\u201d \u3000\u3000\u201c\u4e5f\u5f88\u96be\u8bf4\u3002\u201d \u3000\u3000\u201c\u90a3\u8fb9\u662f\u5426\u8981\u6d3e\u4e2a\u4eba\u7167\u5e94\uff1f\u201d \u3000\u3000\u201c\u4e0d\u5fc5\u4e86\uff0c\u6211\u9a6c\u4e0a\u5c31\u8981\u8fc7\u53bb\u3002\u201d \u3000\u3000\u82df\u6167\u6708\u679c\u771f\u53ea\u5750\u4e86\u4e00\u4f1a\u513f\uff0c\u4fbf\u53c8\u56de\u5230\u5927\u8352\u6d1e\u90a3\u77f3\u5ba4\u53bb\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u3001\u5c0f\u71d5\u5b50\u4ee5\u53ca\u5305\u5fe0\u592b\u5987\uff0c\u6b64\u523b\u5185\u5fc3\u53cd\u800c\u8d8a\u53d1\u7d27\u5f20\u4e0e\u7126\u6025\u3002 \u3000\u3000\u82df\u6167\u6708\u8fd9\u4e00\u53bb\uff0c\u76f4\u5230\u5929\u8272\u5c06\u665a\u624d\u56de\u6765\u3002 \u3000\u3000\u5305\u5fe0\u592b\u5987\u5219\u5fd9\u7740\u51c6\u5907\u665a\u9910\u3002 \u3000\u3000\u8fd9\u65f6\u82df\u6167\u6708\u7684\u795e\u8272\u5df2\u89c1\u7f13\u548c\uff0c\u4e00\u8fdb\u95e8\u5c31\u9053\uff1a\u201c\u4f60\u4eec\u653e\u5fc3\uff0c\u770b\u6765\u5927\u6709\u5e0c\u671b\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u548c\u5c0f\u71d5\u5b50\u4ece\u672a\u5982\u6b64\u60ca\u559c\u8fc7\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u4ece\u4ec0\u4e48\u5730\u65b9\u770b\u5f97\u51fa\u6765\uff1f\u201d \u3000\u3000\u82df\u6167\u6708\u9053\uff1a\u201c\u867d\u7136\u5c1a\u672a\u9192\u6765\uff0c\u4f46\u6b64\u523b\u5df2\u4f3c\u4e4e\u6e10\u6709\u4f53\u6e29\uff0c\u808c\u8089\u4e5f\u4e0d\u4f3c\u5148\u524d\u90a3\u822c\u50f5\u786c\u3002\u201d \u3000\u3000\u201c\u4ee5\u8461\u5973\u4fa0\u9884\u6599\uff0c\u4ec0\u4e48\u65f6\u5019\u624d\u53ef\u9192\u6765\uff1f\u201d \u3000\u3000\u201c\u6211\u662f\u7b2c\u4e00\u6b21\u7528\u8fd9\u79cd\u65b9\u6cd5\u6551\u4eba\uff0c\u4e5f\u662f\u7b2c\u4e00\u6b21\u8bd5\u7528\u2018\u8fd4\u5143\u5927\u8fd8\u4e39\u2019\uff0c\u5f88\u96be\u7b54\u590d\u6c5f\u5927\u4fa0\u8fd9\u95ee \u9898\uff0c\u4e0d\u8fc7\u4ed6\u53ef\u4ee5\u6d3b\u8fc7\u6765\uff0c\u5374\u5e94\u5f53\u53ef\u4ee5\u786e\u5b9a\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u90a3\u4e49\u5144\u7a76\u7adf\u662f\u771f\u6b7b\u4e86\uff1f\u8fd8\u662f\u5047\u6b7b\uff1f\u201d \u3000\u3000\u82df\u6167\u6708\u88ab\u95ee\u5f97\u795e\u8272\u6709\u4e9b\u5c34\u5c2c\u3002 \u3000\u3000\u5979\u987f\u4e86\u4e00\u987f\u9053\uff1a\u201c\u53ef\u4ee5\u8bf4\u662f\u771f\u6b7b\uff0c\u4e5f\u53ef\u4ee5\u8bf4\u662f\u5047\u6b7b\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u76b1\u7709\u9053\uff1a\u201c\u6b64\u8bdd\u600e\u8bb2\uff1f\u201d \u3000\u3000\u201c\u4f60\u4e0d\u662f\u8bf4\u8fc7\u5417\uff1f\u9f9f\u606f\u5927\u6cd5\u6700\u957f\u53ea\u80fd\u95ed\u8109\u4e09\u4e2a\u6708\uff0c\u4ed6\u81ea\u7136\u4e5f\u4e0d\u80fd\u8d85\u8fc7\u6b64\u9650\uff1b\u82e5\u5728\u4e09\u4e2a\u6708 \u4e4b\u5185\uff0c\u65bd\u6cd5\u8005\u5fc5\u53ef\u81ea\u52a8\u9192\u6765\uff0c\u8fd9\u5c31\u662f\u5047\u6b7b\u3002\u201d \u3000\u3000\u201c\u6211\u90a3\u4e49\u5144\u5462\uff1f\u201d \u3000\u3000\u201c\u4ed6\u5df2\u8d85\u8fc7\u534a\u5e74\uff0c\u65e0\u6cd5\u81ea\u5df1\u9192\u6765\uff0c\u5fc5\u987b\u4ee5\u4ed9\u4e39\u7075\u836f\u6551\u6cbb\uff0c\u5426\u5219\u5fc5\u6b7b\u65e0\u7591\uff0c\u8fd9\u4f3c\u4e4e\u5e94\u8be5\u662f \u771f\u6b7b\u4e86\u3002\u800c\u4ed6\u6b63\u662f\u5c5e\u4e8e\u540e\u8005\u3002\u201d \u3000\u3000\u201c\u542c\u4e86\u82df\u5973\u4fa0\u8fd9\u79cd\u8bf4\u6cd5\uff0c\u6c5f\u67d0\u6709\u4e00\u4e8b\u4e0d\u89e3\uff1f\u201d \u3000\u3000\u201c\u6c5f\u5927\u4fa0\u6709\u4f55\u4e0d\u660e\u4e4b\u5904\uff1f\u201d \u3000\u3000\u201c\u6211\u90a3\u4e49\u5144\u5728\u65bd\u7528\u9f9f\u606f\u5927\u6cd5\u4e4b\u524d\u5fc5\u4e0d\u77e5\u82df\u5973\u4fa0\u4f1a\u524d\u6765\u6551\u4ed6\uff0c\u800c\u4e14\u66fe\u90d1\u91cd\u4ea4\u4ee3\u5c0f\u71d5\u5b50\u5fc5\u987b \u4e09\u5e74\u4e4b\u540e\u518d\u542f\u5f00\u77f3\u95e8\uff0c\u8fd9\u8868\u793a\u4ed6\u5df2\u4e0b\u5b9a\u4e86\u6b7b\u7684\u51b3\u5fc3\uff0c\u65e2\u7136\u5df2\u4e0d\u60f3\u518d\u6d3b\u4e86\uff0c\u53c8\u4f55\u5fc5\u65bd\u7528\u9f9f\u606f\u5927 \u6cd5\u5462\uff1f\u201d \u3000\u3000\u201c\u7406\u7531\u5f88\u7b80\u5355\uff0c\u4ed6\u60f3\u4fdd\u6301\u8089\u8eab\u4e0d\u574f\uff0c\u4ee5\u4f9b\u540e\u8f88\u4e4b\u4eba\u77bb\u4ef0\uff0c\u540c\u65f6\u4e5f\u60f3\u8ba9\u6c5f\u5927\u4fa0\u548c\u5c0f\u71d5\u5b50\u4ecd \u80fd\u770b\u5230\u751f\u524d\u7684\u6837\u5b50\u3002\u201d \u3000\u3000\u201c\u539f\u6765\u9f9f\u606f\u5927\u6cd5\u80fd\u4fdd\u6301\u8089\u8eab\u4e0d\u574f\uff0c\u636e\u8bf4\u51fa\u5bb6\u4eba\u4e00\u751f\u82e6\u4fee\uff0c\u6700\u5e0c\u671b\u7684\u5c31\u662f\u6b7b\u540e\u8089\u8eab\u4e0d\u574f\uff0c \u4ed6\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u4fee\u4e60\u9f9f\u606f\u5927\u6cd5\u5462\uff1f\u8fd9\u5c82\u4e0d\u662f\u4e00\u6761\u6377\u5f84\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u5f53\u7136\u60f3\uff0c\u4f46\u9f9f\u606f\u5927\u6cd5\u53c8\u5c82\u662f\u90a3\u4e48\u5bb9\u6613\u4fee\u4e60\u7684\uff1f\u5373\u4ee5\u6b66\u6797\u4e2d\u4eba\u800c\u8bba\uff0c\u4fee\u4e60\u6210\u529f\u7684\u53c8 \u6709\u51e0\u4eba\uff1f\u66f4\u4f55\u51b5\u90a3\u4e9b\u51fa\u5bb6\u4eba\u5c31\u7b97\u4fee\u4e60\u6210\u529f\uff0c\u4e5f\u6d3e\u4e0d\u4e0a\u7528\u573a\u3002\u201d \u3000\u3000\u201c\u8fd9\u53c8\u5982\u4f55\u89e3\u91ca\uff1f\u201d \u3000\u3000\u201c\u7406\u7531\u5f88\u7b80\u5355\uff0c\u4ed6\u4eec\u7eb5\u5df2\u4e60\u5f97\u9f9f\u606f\u5927\u6cd5\uff0c\u4f46\u4eba\u5230\u4e34\u6b7b\uff0c\u771f\u6c14\u5df2\u65e0\u6cd5\u51dd\u805a\uff0c\u6839\u672c\u4e0d\u80fd\u518d\u65bd \u5c55\u5927\u6cd5\uff0c\u8c01\u613f\u610f\u5728\u5927\u9650\u672a\u6ee1\u3001\u8840\u6c14\u6b63\u65fa\u4e4b\u65f6\u81ea\u6c42\u4e00\u6b7b\u5462\uff1f\u201d \u3000\u3000\u8fd9\u65f6\u5305\u5fe0\u592b\u5987\u5df2\u6446\u4e0a\u9152\u996d\uff0c\u4e09\u4eba\u7acb\u5373\u5165\u5e2d\u3002 \u3000\u3000\u82df\u6167\u6708\u9053\uff1a\u201c\u6c5f\u5927\u4fa0\u548c\u5c0f\u71d5\u5b50\u90a3\u8fb9\u8fd8\u6709\u91cd\u8981\u4e8b\u60c5\uff0c\u7528\u8fc7\u996d\u540e\u4f60\u4eec\u5c31\u56de\u5230\u90a3\u8fb9\u53bb\u5427\uff01\u201d \u3000\u3000\u6c5f\u5343\u91cc\u8bed\u6c14\u90d1\u91cd\u7684\u9053\uff1a\u201c\u4e0d\uff0c\u6c5f\u67d0\u548c\u5c0f\u71d5\u5b50\u51b3\u5b9a\u7559\u5728\u8fd9\u91cc\uff0c\u867d\u7136\u6211\u4eec\u5e2e\u4e0d\u4e0a\u82df\u5973\u4fa0\u7684 \u5fd9\uff0c\u591a\u4e24\u4e2a\u4eba\u603b\u662f\u591a\u4e00\u4e9b\u7167\u5e94\u3002\u201d \u3000\u3000\u201c\u6c5f\u5927\u4fa0\u7528\u4e0d\u7740\u575a\u6301\uff0c\u636e\u6211\u6240\u6599\u4ed6\u80fd\u5b8c\u5168\u9192\u6765\uff0c\u81f3\u5c11\u4e0d\u9700\u4e09\u5929\u65f6\u95f4\uff0c\u4f60\u4eec\u7559\u5728\u8fd9\u91cc\uff0c\u5c82 \u4e0d\u628a\u65f6\u95f4\u767d\u767d\u6d6a\u8d39\uff1f\u800c\u90a3\u8fb9\u7684\u4e8b\u5374\u5c11\u4e0d\u4e86\u4f60\u4eec\u3002\u201d \u3000\u3000\u201c\u82e5\u7531\u82df\u5973\u4fa0\u4e00\u4eba\u5728\u8fd9\u91cc\uff0c\u5c82\u4e0d\u592a\u8f9b\u82e6\u4e86\uff1f\u201d \u3000\u3000\u201c\u6211\u8f9b\u82e6\u7684\u65f6\u95f4\u5df2\u7ecf\u8fc7\u4e86\uff0c\u4ed6\u5df2\u670d\u4e0b\u2018\u8fd4\u5143\u5927\u8fd8\u4e39\u2019\uff0c\u4ee5\u540e\u4e09\u5929\u53ea\u9700\u89c2\u5bdf\u4ed6\u7684\u53d8\u5316\u800c \u5df2\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u82df\u5973\u4fa0\u603b\u4e0d\u80fd\u4e09\u5929\u4e09\u591c\u4e0d\u7761\u3002\u201d \u3000\u3000\u201c\u6211\u7167\u6837\u8fd8\u53ef\u4ee5\u4f11\u606f\uff0c\u6211\u5df2\u51b3\u5b9a\u591c\u91cc\u5c31\u7761\u5728\u90a3\u95f4\u77f3\u5ba4\u91cc\uff0c\u6240\u8c13\u89c2\u5bdf\u4e0d\u8fc7\u662f\u9759\u5f85\u53d8\u5316\u800c \u5df2\uff0c\u5e76\u975e\u4e24\u76ee\u4e0d\u77ac\u7684\u770b\u7740\u4ed6\uff0c\u800c\u4e14\u5305\u5fe0\u8001\u4eba\u5bb6\u4e5f\u53ef\u4ee5\u5e2e\u6211\u6b64\u5fd9\u3002\u201d \u3000\u3000\u65e2\u7136\u82df\u6167\u6708\u5982\u6b64\u8bf4\u8bdd\uff0c\u6c5f\u5343\u91cc\u4e5f\u5c31\u4e0d\u518d\u575a\u6301\u7559\u4e0b\u3002 \u3000\u3000\u996d\u540e\uff0c\u5728\u82df\u6167\u6708\u7684\u5e26\u9886\u4e0b\uff0c\u518d\u8fdb\u5927\u8352\u6d1e\u770b\u4e86\u5929\u96f7\u8001\u4eba\u4e00\u6b21\uff0c\u4fbf\u5411\u6167\u6708\u544a\u8f9e\u3002 \u3000\u3000\u82df\u6167\u6708\u9053\uff1a\u201c\u5982\u679c\u6c5f\u5927\u4fa0\u62bd\u5f97\u51fa\u65f6\u95f4\uff0c\u4e09\u5929\u540e\u5c31\u5e26\u5c0f\u71d5\u5b50\u518d\u6765\u770b\u770b\uff0c\u5426\u5219\u8fd8\u662f\u5148\u529e\u90a3\u8fb9 \u7684\u4e8b\u8981\u7d27\uff0c\u8fd9\u8fb9\u4e00\u5207\u6709\u6211\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u6b63\u5bb9\u9053\uff1a\u201c\u6c5f\u67d0\u662f\u770b\u4e49\u5144\uff0c\u5c0f\u71d5\u5b50\u662f\u770b\u5e08\u7236\uff0c\u4e09\u65e5\u540e\u4e00\u5b9a\u8981\u6765\u3002\u201d \u5367\u9f99\u751f\u300a\u71d5\u5b50\u4f20\u5947\u300b \u7b2c\u4e09\u5341\u4e09\u56de\u3000\u63a2\u5bdf\u654c\u60c5 \u3000\u3000\u7531\u4e8e\u591c\u8def\u96be\u884c\uff0c\u8352\u5c71\u91ce\u5cad\u5728\u89c6\u7ebf\u4e0d\u660e\u4e4b\u4e0b\u4e0d\u5b9c\u65bd\u5c55\u8f7b\u529f\uff0c\u6c5f\u5343\u91cc\u548c\u5c0f\u71d5\u5b50\u56de\u5230\u539f\u6765\u6240\u4f4f \u7684\u6c11\u5bb6\u65f6\u5df2\u7ecf\u662f\u4e09\u66f4\u5c06\u5c3d\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u4e09\u516c\u4e3b\u3001\u738b\u5f64\u548c\u6240\u6709\u7684\u4eba\u90fd\u5df2\u5165\u7761\uff0c\u6c5f\u5343\u91cc\u548c\u5c0f\u71d5\u5b50\u4e5f\u5c31\u4e0d\u518d\u60ca\u52a8\u4efb\u4f55\u4eba\u3002 \u3000\u3000\u76f4\u5230\u7fcc\u65e5\u4e00\u65e9\uff0c\u6240\u6709\u7684\u4eba\u624d\u77e5\u9053\u6c5f\u5343\u91cc\u548c\u5c0f\u71d5\u5b50\u5df2\u7ecf\u56de\u6765\u3002 \u3000\u3000\u5f53\u4ed6\u4eec\u5f97\u77e5\u5929\u96f7\u8001\u4eba\u5df2\u7ecf\u6709\u6551\u65f6\uff0c\u4e2a\u4e2a\u90fd\u60ca\u559c\u4e0d\u5df2\uff01 \u3000\u3000\u4e09\u516c\u4e3b\u8fd8\u7279\u522b\u6765\u5230\u6c5f\u5343\u91cc\u623f\u4e2d\uff0c\u8be2\u95ee\u5e08\u7236\u82df\u6167\u6708\u7684\u4e8b\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u82df\u5973\u4fa0\u5bf9\u4e09\u516c\u4e3b\u4f53\u5185\u866b\u6bd2\u672a\u9664\u7684\u4e8b\u4e5f\u662f\u5173\u5fc3\u4e0d\u5df2\uff0c\u5979\u4e3a\u4e86\u6551\u5c0f\u71d5\u5b50\u7684\u5e08\u7236 \u5929\u96f7\u8001\u4eba\uff0c\u5fc5\u5b9a\u4f1a\u5728\u9752\u4e91\u5d16\u5927\u8352\u6d1e\u4f5c\u8f83\u957f\u65f6\u95f4\u7684\u505c\u7559\uff0c\u4e5f\u8bb8\u8fc7\u4e9b\u5929\u4f1a\u4e3b\u52a8\u524d\u6765\u770b\u671b\u4e09\u516c\u4e3b \u7684\u3002\u201d \u3000\u3000\u4e09\u516c\u4e3b\u6447\u6447\u5934\u9053\uff1a\u201c\u4e0d\uff01\u90a3\u6709\u5e08\u7236\u63a2\u671b\u5f92\u513f\u7684\u9053\u7406\uff0c\u6211\u8be5\u53bb\u62dc\u89c1\u5979\u8001\u4eba\u5bb6\uff0c\u9752\u4e91\u5d16\u79bb\u8fd9 \u91cc\u6709\u591a\u8fdc\uff0c\u6211\u73b0\u5728\u5c31\u53bb\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u5fd9\u9053\uff1a\u201c\u8fd9\u8fb9\u7684\u4e8b\u4e5f\u5f88\u91cd\u8981\uff0c\u8fde\u6211\u548c\u5c0f\u71d5\u5b50\u60f3\u7559\u5728\u90a3\u91cc\u5979\u90fd\u4e0d\u80af\uff0c\u4e09\u516c\u4e3b\u5982\u679c\u975e \u5148\u53bb\u89c1\u5979\u4e0d\u53ef\uff0c\u8fc7\u51e0\u5929\u6211\u548c\u5c0f\u71d5\u5b50\u81ea\u5f53\u966a\u4f60\u524d\u53bb\uff0c\u4e0d\u5fc5\u6025\u5728\u4e00\u65f6\u3002\u201d \u3000\u3000\u4e09\u516c\u4e3b\u7ec8\u4e8e\u6253\u6d88\u539f\u610f\uff0c\u56de\u623f\u800c\u53bb\u3002 \u3000\u3000\u5f53\u65e5\u4e0a\u5348\uff0c\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u53c8\u628a\u6240\u6709\u7684\u4eba\u5206\u6210\u56db\u7ec4\uff0c\u5206\u5934\u5404\u5904\u5bfb\u89c5\u897f\u57df\u9b54\u6559\u5728\u7ec8\u5357\u5c71\u7684\u5206 \u575b\u3002 \u3000\u3000\u51fa\u4e8e\u610f\u6599\u4e4b\u5916\u7684\uff0c\u8fd9\u6b21\u4e09\u516c\u4e3b\u4e5f\u81ea\u544a\u594b\u52c7\u8981\u53c2\u52a0\u3002 \u3000\u3000\u5979\u8fd9\u4e00\u7ec4\u5168\u662f\u5973\u7684\uff0c\u7531\u601c\u82b1\u548c\u60dc\u6625\u968f\u884c\u3002 \u3000\u3000\u738b\u5f64\u5efa\u8bae\u4e09\u516c\u4e3b\u6700\u597d\u518d\u6311\u9009\u4e00\u4eba\u968f\u884c\uff0c\u56e0\u4e3a\u6709\u4e9b\u4e8b\u5fc5\u987b\u6709\u7537\u6027\u51fa\u9762\u624d\u8f83\u65b9\u4fbf\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u8d81\u673a\u6d3e\u51fa\u5c0f\u71d5\u5b50\u968f\u540c\u4e09\u516c\u4e3b\u884c\u52a8\u3002 \u3000\u3000\u4e0d\u6d88\u8bf4\uff0c\u4ed6\u7684\u8fd9\u4e00\u5b89\u6392\u662f\u610f\u603b\u4e3a\u7684\u3002 \u3000\u3000\u504f\u504f\u5c0f\u71d5\u5b50\u53cd\u800c\u6709\u4e9b\u4e0d\u5927\u60c5\u613f\u3002 \u3000\u3000\u867d\u7136\u4ed6\u660e\u77e5\u4e00\u9053\u98de\u8679\u82df\u6167\u6708\u6709\u610f\u64ae\u5408\u4ed6\u548c\u4e09\u516c\u4e3b\u6210\u4e3a\u4e00\u5bf9\uff0c\u5374\u603b\u89c9\u6709\u4e9b\u4e0d\u59a5\uff0c\u800c\u4e14\u4ed6\u628a \u4e00\u5207\u8363\u534e\u5bcc\u8d35\u90fd\u770b\u5f97\u5f88\u6de1\uff0c\u53ea\u60f3\u9075\u7167\u5e08\u7236\u7684\u8bdd\uff0c\u5728\u6b66\u6797\u4e2d\u505a\u4e00\u4e2a\u884c\u4fa0\u4ed7\u4e49\u3001\u5802\u5802\u6b63\u6b63\u7684\u4eba\uff0c \u81f3\u4e8e\u5176\u4ed6\u65b9\u9762\uff0c\u6beb\u65e0\u6240\u6c42\uff0c\u4e5f\u7edd\u4e0d\u60f3\u5f3a\u6c42\u3002 \u3000\u3000\u4f46\u5982\u4eca\u6c5f\u5343\u91cc\u5429\u5490\u4e0b\u6765\uff0c\u4ed6\u4e5f\u53ea\u6709\u670d\u4ece\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u6c38\u8fdc\u8bb0\u5f97\u5e08\u7236\u5728\u81ea\u95ed\u6d1e\u95e8\u524d\u6240\u6bb7\u6bb7\u53ee\u549b\u7684\u4e09\u4ef6\u4e8b\u3002 \u3000\u3000\u8fd9\u4e09\u4ef6\u4e8b\u4fbf\u662f\uff1a\u4e00\u3001\u5c3d\u529b\u9010\u51fa\u9b54\u6559\uff0c\u4ee5\u89e3\u6b66\u6797\u4e4b\u5371\u3002 \u3000\u3000\u4e8c\u3001\u4e0d\u5f97\u51fa\u4ed5\u671d\u5ef7\u4e3a\u5b98\u7267\u6c11\u3002 \u3000\u3000\u4e09\u3001\u8981\u603b\u542c\u6c5f\u53d4\u53d4\u7684\u5429\u5490\u884c\u4e8b\uff0c\u4e0d\u5f97\u8fdd\u547d\u6297\u62d2\u3002 \u3000\u3000\u5b9e\u9645\u4e0a\uff0c\u4ece\u5e08\u7236\u81ea\u95ed\u77f3\u5ba4\u540e\uff0c\u5c0f\u71d5\u5b50\u65e9\u5df2\u628a\u6c5f\u5343\u91cc\u770b\u6210\u50cf\u5e08\u7236\u4e00\u6837\u3002 \u3000\u3000\u4ed6\u4e0d\u4f1a\u5fd8\u8bb0\uff0c\u8bbe\u82e5\u6ca1\u6709\u6c5f\u5343\u91cc\u7684\u8bc6\u62d4\u628a\u4ed6\u9001\u5165\u5929\u96f7\u8001\u4eba\u95e8\u4e0b\uff0c\u81ea\u5df1\u90a3\u91cc\u4f1a\u6709\u4eca\u5929\u7684\u6b66\u5b66 \u6210\u5c31\u3002\u601c\u82b1\u548c\u60dc\u6625\u5f53\u7136\u6b22\u8fce\u5c0f\u71d5\u5b50\u52a0\u5165\u4e09\u516c\u4e3b\u8fd9\u4e00\u7ec4\u3002 \u3000\u3000\u81f3\u4e8e\u4e09\u516c\u4e3b\uff0c\u53ef\u80fd\u8fd8\u4e0d\u6e05\u695a\u5e08\u7236\u5bf9\u81ea\u5df1\u7ec8\u8eab\u5927\u4e8b\u7684\u5b89\u6392\uff0c\u5426\u5219\u573a\u9762\u5fc5\u5b9a\u5f88\u5c34\u5c2c\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u4e09\u5973\u4e00\u7537\u5f00\u59cb\u51fa\u53d1\u3002 \u3000\u3000\u8def\u4e2d\uff0c\u601c\u82b1\u95ee\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u4e3a\u4ec0\u4e48\u5ffd\u7136\u8981\u4eb2\u81ea\u629b\u5934\u9732\u9762\u5462\uff1f\u201d \u3000\u3000\u4e09\u516c\u4e3b\u9053\uff1a\u201c\u5927\u5bb6\u4ece\u79bb\u4eac\u5230\u73b0\u5728\uff0c\u6240\u6709\u7684\u884c\u52a8\uff0c\u53ef\u8bf4\u5168\u662f\u4e3a\u4e86\u6211\u4e00\u4eba\uff0c\u6211\u82e5\u518d\u8eb2\u5728\u5c4b\u91cc \u4e0d\u51fa\u6765\uff0c\u90a3\u5c31\u592a\u5bf9\u4f4f\u5927\u5bb6\u4e86\uff01\u201d \u3000\u3000\u63a5\u7740\u671b\u4e86\u60dc\u6625\u4e00\u773c\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u662f\u552f\u4e00\u5230\u8fc7\u9b54\u6559\u7ec8\u5357\u5206\u575b\u7684\u4eba\uff0c\u8def\u4e0a\u52a1\u5fc5\u591a\u591a\u7559\u610f\u89c2 \u5bdf\u3002\u201d \u3000\u3000\u60dc\u6625\u9053\uff1a\u201c\u5a62\u5b50\u65e9\u5bf9\u4e09\u516c\u4e3b\u8bf4\u8fc7\uff0c\u5f53\u65f6\u662f\u88ab\u8499\u7740\u773c\u775b\u7684\u3002\u201d \u3000\u3000\u201c\u4e0d\u7ba1\u600e\u6837\uff0c\u4f60\u603b\u4ed6\u7684\u4eba\u73af\u5883\u8981\u719f\u6089\u4e9b\u3002\u201d \u3000\u3000\u8fd9\u65f6\u5c0f\u71d5\u5b50\u5df2\u8dc3\u8eab\u8d70\u5728\u6700\u524d\u9762\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u95ee\u9053\uff1a\u201c\u83ab\u975e\u71d5\u5c11\u4fa0\u5bf9\u8fd9\u91cc\u7684\u8def\u5f84\u5f88\u719f\u5417\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u8349\u6c11\u662f\u4e2a\u7537\u4eba\uff0c\u8d70\u5728\u524d\u9762\u662f\u5e94\u8be5\u7684\u3002\u201d \u3000\u3000\u53ea\u542c\u601c\u82b1\u201c\u6251\u55e4\u201d\u4e00\u7b11\u9053\uff1a\u201c\u71d5\u5c11\u4fa0\uff0c\u4f60\u4e5f\u662f\u5403\u996d\u957f\u5927\u7684\uff0c\u4e3a\u4ec0\u4e48\u81ea\u79f0\u8349\u6c11\uff1f\u201d \u3000\u3000\u5979\u56e0\u4e3a\u66fe\u7ecf\u4e0e\u5c0f\u71d5\u5b50\u76f8\u5904\u8fc7\uff0c\u5f7c\u6b64\u5df2\u7ecf\u5341\u5206\u76f8\u719f\uff0c\u56e0\u4e4b\u624d\u4f1a\u8a00\u8bed\u65e0\u5fcc\uff0c\u5f00\u5f00\u73a9\u7b11\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u7acb\u5373\u53f1\u9053\uff1a\u201c\u601c\u82b1\uff0c\u600e\u4e48\u53ef\u4ee5\u548c\u71d5\u5c11\u4fa0\u8fd9\u4e48\u8bf4\u8bdd\uff1f\u4f60\u6765\u81ea\u5bab\u4e2d\uff0c\u60f3\u4e0d\u5230\u8fd8\u8fd9\u4e48\u7f3a \u4e4f\u6559\u517b\uff01\u201d \u3000\u3000\u601c\u82b1\u90a3\u91cc\u8fd8\u6562\u8bf4\u4ec0\u4e48\uff0c\u7ea2\u7740\u8138\u4f4e\u4e0b\u4e86\u5934\u3002 \u3000\u3000\u5927\u7ea6\u4e00\u4e2a\u65f6\u8fb0\u4e4b\u540e\uff0c\u5df2\u8fdb\u5165\u5c71\u4e4b\u6df1\u5904\u3002 \u3000\u3000\u6b63\u884c\u8d70\u95f4\uff0c\u5ffd\u89c1\u8fdc\u5904\u5c71\u8170\u4e00\u4e2a\u7070\u8863\u4eba\u5f71\u6b63\u6cbf\u7740\u5d0e\u5c96\u5c71\u8def\u5411\u8fd9\u8fb9\u800c\u6765\u3002 \u3000\u3000\u60dc\u6625\u60ca\u559c\u7684\u9053\uff1a\u201c\u7980\u4e09\u516c\u4e3b\uff0c\u8fd9\u4eba\u5f88\u50cf\u6469\u6559\u6559\u5f92\uff0c\u60a8\u548c\u71d5\u5c11\u4fa0\u3001\u601c\u82b1\u59d0\u6700\u597d\u80fd\u6682\u65f6\u907f\u5f00 \u4e00\u4e0b\u3002\u201d \u3000\u3000\u4e09\u516c\u4e3b\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u9694\u5f97\u8fd9\u4e48\u8fdc\uff0c\u81f3\u5c11\u8fd8\u6709\u534a\u91cc\u8def\uff0c\u4f60\u600e\u4e48\u80fd\u591f\u770b\u5f97\u6e05\u695a\u4ed6\u662f\u9b54\u6559\u6559 \u5f92\uff1f\u201d \u3000\u3000\u201c\u5a62\u5b50\u53ea\u662f\u7531\u8863\u670d\u7684\u989c\u8272\u5206\u8fa8\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u8981\u6211\u4eec\u907f\u5f00\u5462\uff1f\u201d \u3000\u3000\u201c\u5982\u679c\u771f\u662f\u9b54\u6559\u6559\u5f92\uff0c\u5a62\u5b50\u56e0\u4e3a\u5728\u5206\u575b\u4f4f\u8fc7\u597d\u51e0\u5929\uff0c\u4ed6\u5fc5\u5b9a\u8ba4\u8bc6\uff0c\u5a62\u5b50\u81ea\u4fe1\u6709\u529e\u6cd5\u5957\u51fa \u5e95\u7ec6\u6765\uff0c\u82e5\u4e09\u516c\u4e3b\u4e09\u4eba\u5728\u65c1\uff0c\u4ed6\u53c8\u600e\u4e48\u80af\u8bf4\u5b9e\u8bdd\uff1f\u201d \u3000\u3000\u201c\u6709\u6211\u4eec\u5728\uff0c\u53ef\u4ee5\u903c\u4ed6\u8bf4\u5b9e\u8bdd\u3002\u201d \u3000\u3000\u201c\u4e09\u516c\u4e3b\u628a\u4e8b\u60c5\u770b\u5f97\u592a\u7b80\u5355\u4e86\u3002\u9b54\u6559\u7684\u4eba\u90fd\u88ab\u8bad\u7ec3\u5f97\u628a\u751f\u6b7b\u4e0d\u5f53\u505a\u4e00\u56de\u4e8b\uff0c\u4e3a\u4e86\u4fdd\u5b88\u6559 \u4e2d\u673a\u5bc6\uff0c\u4ed6\u4eec\u7edd\u5bf9\u4e0d\u4f1a\u5728\u5a01\u529b\u4e4b\u4e0b\u5c48\u670d\uff0c\u4e07\u4e00\u4ed6\u54ac\u820c\u81ea\u5c3d\uff0c\u90a3\u54b1\u4eec\u5c82\u4e0d\u5c31\u65ad\u4e86\u7ebf\u7d22\uff1f\u201d \u3000\u3000\u4e09\u516c\u4e3b\u89c1\u60dc\u6625\u8bf4\u5f97\u6709\u7406\uff0c\u8fde\u5fd9\u548c\u601c\u82b1\u3001\u5c0f\u71d5\u5b50\u8eb2\u5230\u8def\u65c1\u4e00\u5757\u5927\u77f3\u4e4b\u540e\u3002 \u3000\u3000\u60dc\u6625\u4e5f\u968f\u5373\u5728\u8def\u8fb9\u7684\u4e00\u68f5\u6811\u836b\u4e0b\u5750\u7740\u3002 \u3000\u3000\u5927\u7ea6\u76cf\u8336\u5de5\u592b\u4e4b\u540e\uff0c\u90a3\u7070\u8863\u6c49\u5b50\u5df2\u7ecf\u8d70\u8fd1\u3002 \u3000\u3000\u7070\u8863\u6c49\u5b50\u5927\u7ea6\u56db\u53e5\u5de6\u53f3\u5e74\u7eaa\uff0c\u867d\u7136\u4e00\u8eab\u7070\u8863\uff0c\u4f46\u5374\u662f\u6cb9\u5934\u7c89\u9762\uff0c\u770b\u6765\u5f88\u50cf\u662f\u4e00\u4e2a\u201c\u76f8 \u516c\u3002\u201d \u3000\u3000\u5f53\u7070\u8863\u6c49\u5b50\u4e00\u773c\u77a5\u89c1\u5750\u5728\u8def\u65c1\u7684\u60dc\u6625\u540e\uff0c\u4e0d\u89c9\u54a6\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u9f99\u592b\u4eba\u5417\uff1f\u4e3a\u4ec0\u4e48 \u4e0d\u5728\u6f7c\u5173\u5374\u6765\u5230\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u60dc\u6625\u7ad9\u8d77\u8eab\u6765\uff0c\u6ee1\u9762\u542b\u7b11\u7684\u9053\uff1a\u201c\u6211\u662f\u6709\u4e8b\u8981\u5230\u5206\u575b\u7684\u3002\u201d \u3000\u3000\u201c\u592b\u4eba\u73b0\u5728\u4e0d\u662f\u5728\u6f7c\u5173\u4ee3\u7406\u4e3b\u6301\u2018\u6843\u6797\u5c45\u2019\u7684\u5417\uff1f\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u8fde\u8fd9\u4e9b\u4e8b\u4e5f\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u6211\u662f\u542c\u9a6c\u5802\u4e3b\u548c\u9f99\u526f\u5802\u4e3b\u8bf4\u7684\uff0c\u4ed6\u4eec\u4e24\u4eba\u6709\u4e8b\u6765\u5206\u575b\uff0c\u90a3\u8fb9\u7531\u4f60\u4ee3\u7406\uff0c\u4e0d\u662f\u5417\uff1f\u201d \u3000\u3000\u201c\u53ef\u662f\u6211\u6709\u66f4\u91cd\u8981\u7684\u4e8b\uff0c\u5fc5\u987b\u4eb2\u6765\u4e00\u8d9f\u5206\u575b\u4e0d\u53ef\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u91cd\u8981\u5927\u4e8b\uff1f\u201d \u3000\u3000\u201c\u73b0\u5728\u4e0d\u65b9\u4fbf\u8bb2\uff0c\u89c1\u4e86\u5206\u575b\u4e3b\uff0c\u6211\u81ea\u7136\u4f1a\u5bf9\u4ed6\u8bf4\u3002\u201d \u3000\u3000\u7070\u8863\u6c49\u5b50\u7728\u7740\u4e00\u5bf9\u8272\u8ff7\u8ff7\u7684\u9f20\u773c\uff0c\u54a7\u5634\u95ee\u9053\uff1a\u201c\u592b\u4eba\u65e2\u7136\u5230\u5206\u575b\uff0c\u4e3a\u4ec0\u4e48\u5374\u8fd8\u5750\u5728\u8def \u8fb9\uff1f\u201d \u3000\u3000\u60dc\u6625\u5598\u606f\u7740\u9053\uff1a\u201c\u6211\u8d76\u4e86\u5927\u534a\u5929\u7684\u8def\uff0c\u5f53\u7136\u8981\u4f11\u606f\u4f11\u606f\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u5982\u6b64\uff0c\u6211\u5c31\u966a\u592b\u4eba\u4e00\u8d77\u4f11\u606f\u4f11\u606f\uff0c\u6211\u4e5f\u7d2f\u4e86\uff01\u201d \u3000\u3000\u6beb\u65e0\u7591\u95ee\uff0c\u7070\u8863\u6c49\u5b50\u662f\u60f3\u8d81\u673a\u4e00\u4eb2\u82b3\u6cfd\u3002 \u3000\u3000\u60dc\u6625\u6b63\u662f\u6c42\u4e4b\u4e0d\u5f97\uff0c\u4fbf\u53c8\u518d\u5750\u4e0b\u6765\u3002 \u3000\u3000\u7070\u8863\u6c49\u5b50\u56db\u4e0b\u671b\u4e86\u671b\uff0c\u7136\u540e\u7d27\u9760\u7740\u60dc\u6625\u8eab\u8fb9\u5750\u4e0b\u3002 \u3000\u3000\u60dc\u6625\u642d\u4ed9\u7740\u95ee\u9053\uff1a\u201c\u6700\u8fd1\u5206\u575b\u91cc\u5f88\u5fd9\u5427\uff1f\u201d \u3000\u3000\u7070\u8863\u6c49\u5b50\u6de1\u7b11\u9053\uff1a\u201c\u8fd8\u4e0d\u662f\u8001\u6837\u5b50\u3002\u201d \u3000\u3000\u201c\u53ea\u8981\u4e0d\u51fa\u4e8b\u5c31\u597d\u3002\u201d \u3000\u3000\u201c\u5728\u7ec8\u5357\u5c71\u4f1a\u51fa\u4ec0\u4e48\u4e8b\u5462\uff1f\u53ea\u662f\u9a6c\u5802\u4e3b\u548c\u9f99\u526f\u5802\u4e3b\u4e00\u76f4\u5f88\u62c5\u5fc3\u6f7c\u5173\u2018\u6843\u6797\u5c45\u2019\u65b9\u9762\u4f1a\u51fa \u4e8b\u3002\u201d \u3000\u3000\u201c\u90a3\u8fb9\u4e5f\u4e0d\u4f1a\u51fa\u4e8b\u3002\u201d \u3000\u3000\u201c\u8bdd\u4e0d\u80fd\u8fd9\u4e48\u8bf4\uff0c\u636e\u9a6c\u5802\u4e3b\u548c\u9f99\u526f\u5802\u4e3b\u8bb2\uff0c\u4eac\u57ce\u5927\u5185\u65b9\u9762\u6709\u4e9b\u4e86\u4e0d\u8d77\u7684\u9ad8\u624b\uff0c\u5f88\u53ef\u80fd\u5230 \u6f7c\u5173\u6765\u3002\u201d \u3000\u3000\u201c\u6211\u600e\u4e48\u6ca1\u542c\u8bf4\u8fc7\uff1f\u201d \u3000\u3000\u201c\u96be\u9053\u5802\u4e3b\u548c\u9f99\u526f\u5802\u4e3b\u79bb\u5f00\u2018\u6843\u6797\u5c45\u2019\u65f6\uff0c\u4e0d\u66fe\u4ea4\u4ee3\u8fc7\u592b\u4eba\uff1f\u201d \u3000\u3000\u201c\u4e00\u5b57\u90fd\u672a\u63d0\u8fc7\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u8c08\u70b9\u522b\u7684\u5427\uff01\u201d \u3000\u3000\u7070\u8863\u6c49\u5b50\u6545\u610f\u628a\u8eab\u5b50\u518d\u5411\u60dc\u6625\u9760\u4e86\u4e00\u9760\uff0c\u7b11\u54aa\u54aa\u7684\u9053\uff1a\u201c\u592b\u4eba\uff0c\u54b1\u4eec\u8bf4\u4e86\u8001\u534a\u5929\u8bdd\uff0c\u4f60 \u5230\u5e95\u8ba4\u4e0d\u8ba4\u8bc6\u6211\uff1f\u201d \u3000\u3000\u60dc\u6625\u4e5f\u900f\u7740\u65e0\u9650\u60c5\u610f\u7684\u67d4\u58f0\u9053\uff1a\u201c\u6211\u5f53\u7136\u8ba4\u8bc6\u4f60\u8fd9\u4f4d\u5927\u54e5\uff0c\u4e0d\u7136\u6211\u4f1a\u968f\u4fbf\u548c\u4e00\u4e2a\u964c\u751f\u4eba \u5750\u5f97\u8fd9\u4e48\u8fd1\u8bf4\u8bdd\u5417\uff1f\u201d \u3000\u3000\u201c\u592b\u4eba\u662f\u600e\u4e48\u8ba4\u8bc6\u6211\u7684\uff1f\u201d \u3000\u3000\u201c\u90a3\u6211\u8981\u5148\u95ee\u95ee\uff0c\u4f60\u662f\u600e\u4e48\u8ba4\u8bc6\u6211\u7684\uff1f\u201d \u3000\u3000\u201c\u592b\u4eba\u5728\u5206\u575b\u4f4f\u8fc7\u597d\u51e0\u5929\uff0c\u5206\u575b\u91cc\u6839\u672c\u6ca1\u6709\u51e0\u4e2a\u5973\u7684\uff0c\u800c\u4f60\u957f\u5f97\u53c8\u90a3\u4e48\u7f8e\uff0c\u6211\u4eec\u7537\u4eba\u8c01 \u90fd\u4f1a\u5bf9\u4f60\u7559\u610f\u4e09\u5206\u3002\u201d \u3000\u3000\u201c\u4f60\u770b\u6211\u7f8e\u5417\uff1f\u201d \u3000\u3000\u7070\u8863\u6c49\u5b50\u987f\u65f6\u6709\u5982\u5fc3\u82b1\u6012\u653e\uff0c\u54a7\u54a7\u5634\uff0c\u6d8e\u7740\u8138\u9053\uff1a\u201c\u5f53\u7136\u7f8e\uff0c\u7f8e\u5f97\u6bd4\u82b1\u8fd8\u597d\u770b\uff0c\u5f88\u591a\u4eba \u90fd\u5728\u80cc\u540e\u8bf4\u8fc7\u4e00\u53e5\u76f8\u540c\u7684\u8bdd\u2026\u2026\u201d \u3000\u3000\u60dc\u6625\u7728\u52a8\u7740\u6ef4\u6e9c\u6e9c\u7684\u7738\u5b50\u9053\uff1a\u201c\u4ed6\u4eec\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7070\u8863\u6c49\u5b50\u54bd\u4e0b\u4e00\u53e3\u553e\u6cab\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u8bf4\u2026\u2026\u8bf4\u9f99\u526f\u5802\u4e3b\u771f\u662f\u8273\u798f\u4e0d\u6d45\uff01\u201d \u3000\u3000\u60dc\u6625\u4f3c\u4e4e\u4e0d\u4ee5\u4e3a\u610f\uff0c\u6de1\u7136\u4e00\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u80e1\u8bf4\u516b\u9053\uff0c\u76f8\u4fe1\u4f60\u8fd9\u4f4d\u5927\u54e5\u4e00\u5b9a\u4e0d\u4f1a\u8bf4\u8fd9\u79cd \u65e0\u804a\u7684\u8bdd\u3002\u201d \u3000\u3000\u7070\u8863\u6c49\u5b50\u4f38\u957f\u8116\u5b50\uff0c\u987f\u4e86\u987f\u9053\uff1a\u201c\u6211\u561b\u2026\u2026\u6211\u561b\u2026\u2026\u201d \u3000\u3000\u60dc\u6625\u7d27\u8ffd\u4e0d\u820d\u7684\u9053\uff1a\u201c\u4f60\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u201c\u6211\u2026\u2026\u6211\u4e5f\u662f\u4eba\uff0c\u5f53\u7136\u770b\u6cd5\u548c\u4ed6\u4eec\u4e00\u6837\uff0c\u4e0d\u8fc7\u6211\u53ea\u662f\u6ca1\u6709\u8bb2\u51fa\u6765\uff0c\u53ea\u662f\u5fc3\u91cc\u5728\u60f3\u800c \u5df2\u2026\u2026\u201d \u3000\u3000\u201c\u60f3\u4e0d\u5230\u8fd9\u4f4d\u5927\u54e5\u8fd8\u662f\u4e2a\u591a\u60c5\u4eba\uff01\u201d \u3000\u3000\u201c\u591a\u60c5\u6709\u4ec0\u4e48\u7528\uff0c\u5982\u679c\u5c06\u6765\u80fd\u8ba8\u5230\u8001\u5a46\uff0c\u4e0d\u8981\u8bf4\u50cf\u592b\u4eba\u8fd9\u6837\u7684\uff0c\u53ea\u8981\u662f\u4e2a\u5973\u4eba\uff0c\u53ea\u8981\u6709 \u5934\u6709\u8138\u3001\u56db\u80a2\u4ff1\u5168\u3001\u4e94\u5b98\u4e0d\u7f3a\uff0c\u5c31\u5fc3\u6ee1\u610f\u8db3\u4e86\u3002\u201d \u3000\u3000\u60dc\u6625\u970e\u65f6\u50cf\u611f\u5230\u4e00\u9635\u6005\u60d8\uff0c\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u4f4e\u4e0b\u5934\u4e0d\u518d\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u7070\u8863\u6c49\u5b50\u832b\u7136\u95ee\u9053\uff1a\u201c\u8c08\u5f97\u597d\u597d\u7684\uff0c\u592b\u4eba\u4e3a\u4ec0\u4e48\u53f9\u6c14\uff1f\u201d \u3000\u3000\u60dc\u6625\u53c8\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u6211\u662f\u89e6\u666f\u751f\u60c5\uff0c\u81ea\u53f9\u547d\u8584\uff0c\u5927\u54e5\u5c31\u4e0d\u5fc5\u518d\u95ee\u4e86\uff01\u201d \u3000\u3000\u7070\u8863\u6c49\u5b50\u4e24\u53ea\u9f20\u773c\u4e0d\u505c\u7684\u8f6c\u52a8\u7740\u9053\uff1a\u201c\u592b\u4eba\u5ac1\u7ed9\u9f99\u526f\u5802\u4e3b\uff0c\u751f\u6d3b\u7f8e\u6ee1\u65e0\u6bd4\uff0c\u600e\u4e48\u5ffd\u7136\u81ea \u6028\u547d\u8584\u8d77\u6765\uff1f\u201d \u3000\u3000\u201c\u6211\u548c\u9f99\u5728\u5929\u4e4b\u95f4\u7684\u79c1\u4e8b\uff0c\u5927\u54e5\u600e\u4e48\u4f1a\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u73b0\u5728\u95f2\u7740\u4e5f\u662f\u95f2\u7740\uff0c\u592b\u4eba\u8bf4\u51fa\u6765\u542c\u542c\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u201c\u4f60\u60f3\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u60f3\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u60f3\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u6211\u2026\u2026\u6211\u662f\u5173\u5fc3\u592b\u4eba\uff01\u201d \u3000\u3000\u201c\u597d\uff0c\u6211\u5c31\u544a\u8bc9\u4f60\uff0c\u6211\u548c\u9f99\u5728\u5929\u4e4b\u95f4\u76f8\u5904\u5f97\u5e76\u4e0d\u7f8e\u6ee1\u3002\u201d \u3000\u3000\u201c\u600e\u4e48\u4f1a\u6709\u8fd9\u79cd\u4e8b\uff1f\u201d \u3000\u3000\u201c\u4ed6\u5a36\u4e86\u6211\uff0c\u8fd8\u4e0d\u77e5\u8db3\uff0c\u5728\u649e\u5173\u2018\u6843\u6797\u5c45\u2019\u7ecf\u5e38\u591c\u51fa\u4e0d\u5f52\uff0c\u5728\u5916\u9762\u62c8\u82b1\u60f9\u8349\u2026\u2026\u201d \u3000\u3000\u7070\u8863\u6c49\u5b50\u542c\u5230\u8fd9\u91cc\uff0c\u4e24\u773c\u77aa\u5f97\u7728\u90fd\u4e0d\u7728\uff0c\u731b\u4e00\u8dfa\u811a\u9053\uff1a\u201c\u9f99\u526f\u5802\u4e3b\u7b80\u76f4\u592a\u90a3\u4e2a\u4e86\uff0c\u5982\u679c \u6362\u4e86\u6211\u2026\u2026\u201d \u3000\u3000\u60dc\u6625\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u6362\u4e86\u5927\u54e5\u4fbf\u600e\u6837\uff1f\u201d \u3000\u3000\u201c\u5982\u679c\u6211\u80fd\u8ba8\u5230\u50cf\u592b\u4eba\u8fd9\u4e48\u4e00\u6735\u82b1\u4f3c\u7684\u8001\u5a46\uff0c\u4e00\u5b9a\u65e5\u65e5\u591c\u591c\u5b88\u7740\u5979\uff0c\u5c31\u662f\u62ff\u97ad\u5b50\u8d76\u6211 \u8d70\uff0c\u6211\u4e5f\u4e0d\u60f3\u79bb\u5f00\u5979\u534a\u523b\u949f\u3002\u201d \u3000\u3000\u201c\u65e9\u77e5\u5982\u6b64\uff0c\u6211\u5c31\u8be5\u5ac1\u7ed9\u5927\u54e5\uff0c\u80fd\u6709\u4e08\u592b\u4e00\u523b\u4e0d\u79bb\u7684\u5b88\u5728\u8eab\u8fb9\uff0c\u90a3\u591a\u597d\uff0c\u6709\u53e5\u5f62\u5bb9\u8bcd\uff0c \u5927\u54e5\u5fc5\u5b9a\u542c\u8bf4\u8fc7\u5427\uff1f\u201d \u3000\u3000\u7070\u8863\u6c49\u5b50\u8fde\u5fd9\u5f97\u610f\u5fd8\u5f62\u7684\u9053\uff1a\u201c\u53ea\u7fa1\u9e33\u9e2f\u4e0d\u7fa1\u4ed9\uff0c\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u60dc\u6625\u70b9\u70b9\u5934\u9053\uff1a\u201c\u539f\u6765\u5927\u54e5\u809a\u5b50\u91cc\u8fd8\u88c5\u4e86\u4e0d\u5c11\u58a8\u6c34\uff0c\u771f\u662f\u4eba\u6709\u4eba\u624d\uff0c\u6587\u6709\u6587\u624d\uff01\u201d \u3000\u3000\u7070\u8863\u6c49\u5b50\u62cd\u62cd\u80f8\u812f\u9053\uff1a\u201c\u6211\u7684\u786e\u8bfb\u8fc7\u4e0d\u5c11\u4e66\uff0c\u5c0f\u65f6\u5019\u672c\u6765\u60f3\u53c2\u52a0\u79d1\u4e3e\u7684\uff0c\u8c01\u77e5\u540e\u6765\u5e72\u4e86 \u8fd9\u4e00\u884c\uff0c\u6709\u5b66\u95ee\u65e0\u5904\u7528\uff0c\u73b0\u5728\u8fd8\u53ea\u662f\u4e00\u540d\u6559\u4e01\uff0c\u5982\u679c\u5f53\u521d\u53bb\u5e94\u8bd5\uff0c\u8bf4\u4e0d\u5b9a\u4f1a\u8fde\u4e2d\u4e09\u5143\uff0c\u4e5f\u8bb8 \u73b0\u5728\u4e0d\u662f\u4e00\u54c1\u4e5f\u662f\u4e8c\u54c1\u7684\u5927\u5b98\u4e86\uff01\u201d \u3000\u3000\u201c\u771f\u53ef\u60dc\uff0c\u8f9c\u8d1f\u4e86\u5927\u54e5\u8fd9\u6837\u7684\u4eba\u624d\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4e00\u4e2a\u4eba\u662f\u5426\u8ba9\u4eba\u770b\u5f97\u76f4\uff0c\u5e76\u4e0d\u5728\u4e4e\u4ed6\u5b98\u5927\u5b98\u5c0f\u3001\u8eab\u4efd\u9ad8\u4f4e\uff0c\u53ea\u8981\u5b66\u95ee\u597d\uff0c\u522b\u4eba\u5c31\u4f1a\u5d07 \u62dc\u4ed6\uff0c\u50cf\u5927\u54e5\u8fd9\u79cd\u4eba\u6b63\u662f\u6211\u5d07\u62dc\u7684\u5bf9\u8c61\u3002\u201d \u3000\u3000\u8fd9\u65f6\u7684\u7070\u8863\u6c49\u5b50\u5168\u8eab\u9aa8\u5934\u4f3c\u4e4e\u5df2\u4e0d\u8db3\u56db\u4e24\u91cd\uff0c\u7b80\u76f4\u6709\u98d8\u98d8\u6b32\u4ed9\u7684\u611f\u89c9\uff0c\u54a7\u4e86\u54a7\u5634\u9053\uff1a \u201c\u592b\u4eba\u522b\u8001\u662f\u6367\u6211\u4e86\uff0c\u8fde\u4e2a\u8001\u5a46\u90fd\u6ca1\u6709\uff0c\u6367\u6709\u4ec0\u4e48\u7528\uff01\u201d \u3000\u3000\u60dc\u6625\u67d4\u58f0\u5b89\u6170\u9053\uff1a\u201c\u4f60\u53c8\u4e0d\u662f\u4e03\u8001\u516b\u5341\uff0c\u51ed\u7740\u4e00\u8868\u4eba\u624d\uff0c\u5c06\u6765\u4f55\u6101\u8ba8\u4e0d\u5230\u8001\u5a46\u3002\u201d \u3000\u3000\u7070\u8863\u6c49\u5b50\u5e72\u54b3\u4e24\u58f0\u9053\uff1a\u201c\u53ef\u662f\u2026\u2026\u53ef\u662f\u6211\u73b0\u5728\u5c31\u60f3\u8981\uff01\u201d \u3000\u3000\u201c\u8ba8\u8001\u5a46\u662f\u7ec8\u8eab\u5927\u4e8b\uff0c\u600e\u53ef\u80fd\u60f3\u8981\u5c31\u6709\uff1f\u201d \u3000\u3000\u201c\u800c\u4e14\u8fd8\u60f3\u8981\u4e2a\u50cf\u592b\u4eba\u8fd9\u6837\u7684\u3002\u201d \u3000\u3000\u201c\u5927\u54e5\u4e00\u5b9a\u6709\u673a\u4f1a\u3002\u201d \u3000\u3000\u7070\u8863\u6c49\u5b50\u542c\u51fa\u60dc\u6625\u8bdd\u4e2d\u6709\u8bdd\uff0c\u987f\u4e86\u4e00\u987f\u9053\uff1a\u201c\u83ab\u975e\u592b\u4eba\u2026\u2026\u201d \u3000\u3000\u60dc\u6625\u4f3c\u771f\u4f3c\u5047\u7684\u9053\uff1a\u201c\u73b0\u5728\u56db\u4e0b\u65e0\u4eba\uff0c\u5c31\u5bf9\u5927\u54e5\u8bf4\u4e86\u4e5f\u6ca1\u5173\u7cfb\uff0c\u6211\u5df2\u7ecf\u4e0d\u60f3\u518d\u8ddf\u9f99\u5728\u5929 \u5b88\u6d3b\u5be1\u4e86\u3002\u201d \u3000\u3000\u7070\u8863\u6c49\u5b50\u5927\u559c\u8fc7\u671b\u7684\u95ee\uff1a\u201c\u771f\u7684\uff1f\u201d \u3000\u3000\u60dc\u6625\u6574\u4e86\u6574\u8138\u8272\u9053\uff1a\u201c\u8fd9\u79cd\u8bdd\u53ef\u662f\u968f\u4fbf\u4e71\u8bf4\u7684\u5417\uff1f\u201d \u3000\u3000\u8c01\u77e5\u7070\u8863\u6c49\u5b50\u5374\u53c8\u50cf\u76ae\u7403\u6cc4\u6c14\u822c\u9053\uff1a\u201c\u53ef\u60dc\u6211\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u53ef\u60dc\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5c31\u7b97\u592b\u4eba\u548c\u9f99\u526f\u5802\u4e3b\u5206\u4e86\u624b\uff0c\u6211\u4e5f\u4e0d\u6562\u5a36\u4f60\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u53ea\u662f\u4e00\u540d\u6559\u4e01\uff0c\u600e\u6562\u5a36\u526f\u5802\u4e3b\u7684\u5802\u4e0b\u59bb\uff0c\u5c31\u8fde\u6559\u89c4\u4e5f\u4e0d\u5141\u8bb8\u3002\u201d \u3000\u3000\u201c\u4f60\u771f\u6b7b\u5fc3\u773c\u3002\u201d \u3000\u3000\u201c\u83ab\u975e\u592b\u4eba\u8fd8\u4f1a\u9003\u5230\u5f88\u8fdc\u5f88\u8fdc\u7684\u5730\u65b9\u5417\uff1f\u201d\u90a3\u2026\u2026\u597d\u50cf\u4e66\u4e0a\u8bf4\u8fc7\uff0c\u90a3\u53eb\u79c1\u5954\u3002\u201c \u3000\u3000\u201c\u5927\u54e5\uff0c\u4f60\u9519\u4e86\uff0c\u8fd9\u4e0d\u53eb\u79c1\u5954\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u4e66\u4e0a\u662f\u8fd9\u4e48\u8bf4\u7684\u3002\u201d \u3000\u3000\u201c\u79c1\u5954\u662f\u6307\u6709\u4e08\u592b\u7684\u5973\u4eba\u6216\u6709\u59bb\u5b50\u7684\u7537\u4eba\u8ddf\u522b\u4eba\u8dd1\u4e86\uff0c\u800c\u6211\u90a3\u65f6\u5df2\u548c\u9f99\u5728\u5929\u6b63\u5f0f\u5206\u624b\uff0c \u4f60\u53c8\u662f\u6ca1\u59bb\u5b50\u7684\u4eba\uff0c\u54b1\u4eec\u4e00\u8d77\u8d70\uff0c\u600e\u80fd\u79f0\u4f5c\u79c1\u5954\uff1f\u201d \u3000\u3000\u201c\u53ef\u662f\u4e07\u4e00\u9f99\u526f\u5802\u4e3b\u4e0d\u540c\u610f\u548c\u4f60\u5206\u624b\u5462\uff1f\u201d \u3000\u3000\u201c\u90a3\u5c31\u53ea\u597d\u79c1\u5954\u4e86\uff01\u201d \u3000\u3000\u201c\u8fd9\u6837\u5c31\u592a\u597d\u4e86\uff0c\u592b\u4eba\uff01\u6211\u73b0\u5728\u5c31\u53ef\u4ee5\u5411\u4f60\u63d0\u51fa\u4fdd\u8bc1\u3002\u201d \u3000\u3000\u201c\u4fdd\u8bc1\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5728\u4e00\u8d77\u9003\u8d70\u7684\u8def\u4e0a\uff0c\u5982\u679c\u6211\u96c7\u4e0d\u8d77\u8f66\u548c\u8f7f\uff0c\u5c31\u80cc\u7740\u4f60\u6216\u8005\u62b1\u7740\u4f60\uff0c\u603b\u800c\u8a00\u4e4b\uff0c\u4e00\u5207\u8f9b \u82e6\u4e8b\u90fd\u7531\u6211\u505a\uff0c\u6211\u60c5\u613f\u7ed9\u4f60\u4f5c\u725b\u4f5c\u9a6c\u3002\u201d \u3000\u3000\u201c\u51ed\u5927\u54e5\u8fd9\u51e0\u53e5\u8bdd\uff0c\u5c31\u592a\u8ba9\u6211\u611f\u52a8\u4e86\uff0c\u4e3a\u4e86\u7ec8\u8eab\u5e78\u798f\uff0c\u770b\u6765\u6211\u975e\u8ddf\u4f60\u4e0d\u53ef\u4e86\u3002\u201d \u3000\u3000\u60dc\u6625\u8bf4\u5230\u8fd9\u91cc\uff0c\u7ad9\u8d77\u8eab\u6765\u9053\uff1a\u201c\u5927\u54e5\u4e0b\u5c71\uff0c\u662f\u5426\u6709\u4ec0\u4e48\u8981\u7d27\u7684\u4e8b\uff1f\u201d \u3000\u3000\u7070\u8863\u6c49\u5b50\u4e24\u773c\u4e0d\u7728\u7684\u9053\uff1a\u201c\u592b\u4eba\u95ee\u8fd9\u4e9b\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5982\u679c\u5927\u54e5\u6ca1\u6709\u522b\u7684\u8981\u7d27\u4e8b\uff0c\u5c31\u9ebb\u70e6\u4f60\u966a\u6211\u4e00\u8d77\u5230\u5206\u575b\u53bb\u3002\u201d \u3000\u3000\u201c\u8fd9\u662f\u5f53\u7136\u7684\u4e8b\uff0c\u4f55\u7528\u592b\u4eba\u5429\u5490\uff01\u6211\u867d\u7136\u4e0b\u5c71\u529e\u4e8b\uff0c\u4f46\u4e5f\u6ca1\u6709\u966a\u592b\u4eba\u4e0a\u5c71\u91cd\u8981\uff0c\u6765\uff01\u6211 \u73b0\u5728\u5c31\u966a\u592b\u4eba\u8d70\uff01\u201d \u3000\u3000\u7070\u8863\u6c49\u5b50\u7ad9\u8d77\u8eab\uff0c\u4f38\u624b\u4fbf\u8981\u62c9\u60dc\u6625\u7684\u624b\u3002\u60dc\u6625\u540e\u9000\u4e00\u6b65\uff0c\u9762\u5e26\u8c22\u610f\u7684\u7b11\u9053\uff1a\u201c\u4e0d\u5fc5\u5566\uff0c \u6211\u81ea\u5df1\u8d70\u5f97\u52a8\uff0c\u9762\u4e14\u7740\u54b1\u4eec\u624b\u7275\u624b\uff0c\u4e07\u4e00\u88ab\u4eba\u770b\u5230\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u4f1a\u7684\uff0c\u8fd9\u79cd\u5730\u65b9\u54ea\u4f1a\u6709\u4eba\u770b\u5230\uff1f\u201d \u3000\u3000\u201c\u90a3\u53ef\u4e0d\u4e00\u5b9a\uff0c\u8c01\u80fd\u4fdd\u8bc1\u9644\u8fd1\u6ca1\u6709\u5206\u575b\u7684\u4eba\uff0c\u4e00\u65e6\u88ab\u4ed6\u4eec\u53d1\u73b0\uff0c\u54b1\u4eec\u5c31\u8c01\u90fd\u522b\u60f3\u6d3b\u4e86\uff0c \u5927\u54e5\uff01\u53ea\u8981\u54b1\u4eec\u7684\u884c\u52a8\u6210\u529f\uff0c\u5c06\u6765\u8981\u600e\u6837\u4eb2\u70ed\u5c31\u600e\u6837\u4eb2\u70ed\uff0c\u4f55\u5fc5\u8fd9\u4e48\u6025\u5462\uff1f\u201d \u3000\u3000\u201c\u592b\u4eba\u8bf4\u7684\u5bf9\uff0c\u54b1\u4eec\u5c31\u4fdd\u6301\u8ddd\u79bb\u8d70\u5427\uff01\u201d \u3000\u3000\u201c\u4e8e\u662f\uff0c\u4e24\u4eba\u4e00\u524d\u4e00\u540e\u5f80\u5c71\u4e0a\u8d70\u53bb\u3002 \u3000\u3000\u60dc\u6625\u8fb9\u8d70\u8fb9\u95ee\u9053\uff1a\u201c\u542c\u4f60\u521a\u624d\u7684\u8bed\u6c14\uff0c\u9a6c\u5802\u4e3b\u548c\u9f99\u5728\u5929\u597d\u50cf\u4ecd\u5728\u5206\u575b\uff0c\u5e76\u672a\u56de\u6f7c\u5173\u53bb \u7f57\uff1f\u201d \u3000\u3000\u7070\u8863\u6c49\u5b50\u9894\u9996\u9053\uff1a\u201c\u6211\u60f3\u4ed6\u4eec\u8fd8\u6ca1\u8d70\uff0c\u56e0\u4e3a\u4eca\u5929\u4e00\u65e9\u6211\u8fd8\u770b\u5230\u4ed6\u4eec\u3002\u201d \u3000\u3000\u201c\u6f7c\u5173\u2018\u6843\u6797\u5c45\u2019\u90a3\u8fb9\u8d23\u4efb\u91cd\u5927\uff0c\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u8d70\u5462\uff1f\u201d \u3000\u3000\u201c\u73b0\u5728\u8d23\u4efb\u5df2\u7ecf\u4e0d\u5927\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u8bdd\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u201c\u6700\u8fd1\u2018\u6843\u6797\u5c45\u2019\u6700\u91cd\u8981\u7684\u4e00\u4ef6\u4e8b\uff0c\u4fbf\u662f\u628a\u5f00\u5c01\u65b9\u9762\u7684\u4e09\u4f4d\u59d1\u5a18\u63a5\u6765\uff0c\u73b0\u5728\u4ed6\u4eec\u5df2\u7ecf\u628a \u4e09\u4f4d\u59d1\u5a18\u9001\u56de\u5206\u575b\uff0c\u5927\u4e8b\u5b8c\u6210\uff0c\u9a6c\u5802\u4e3b\u548c\u9f99\u526f\u5802\u4e3b\u5df2\u7ecf\u8f7b\u677e\u591a\u4e86\uff0c\u6240\u4ee5\u4ed6\u4eec\u4e0d\u7528\u6025\u7740\u56de \u53bb\u3002\u201d \u3000\u3000\u201c\u90a3\u4e09\u4f4d\u59d1\u5a18\u76ee\u524d\u662f\u5426\u4ecd\u5728\u5206\u575b\uff1f\u6709\u6ca1\u6709\u6d88\u606f\u8981\u56de\u897f\u57df\u53bb\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e8b\u6211\u8fd8\u5f04\u4e0d\u6e05\u695a\uff0c\u4e0d\u8fc7\u5979\u4eec\u73b0\u5728\u4e00\u5b9a\u6ca1\u8d70\u3002\u201d \u3000\u3000\u201c\u4f60\u89c1\u8fc7\u5979\u4eec\uff1f\u201d \u3000\u3000\u201c\u53ea\u89c1\u8fc7\u4e00\u6b21\u3002\u201d \u3000\u3000\u201c\u5979\u4eec\u4e5f\u5f88\u6f02\u4eae\uff0c\u96be\u9053\u4f60\u5c31\u4e0d\u60f3\u770b\u770b\uff1f\u201d \u3000\u3000\u201c\u5979\u4eec\u88ab\u62db\u5f85\u5728\u5185\u5bab\u91cc\uff0c\u6211\u53ea\u662f\u4e00\u540d\u6559\u4e01\uff0c\u6839\u672c\u4e0d\u591f\u8d44\u683c\u8fdb\u5185\u5bab\uff0c\u800c\u5de8\u73b0\u5728\u6211\u5df2\u6ca1\u5fc5\u8981 \u518d\u770b\u5979\u4eec\u4e86\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u53ea\u8981\u770b\u592b\u4eba\u5c31\u591f\u4e86\uff0c\u5c06\u6765\u7231\u600e\u4e48\u770b\u5c31\u600e\u4e48\u770b\uff0c\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u5f53\u7136\u5bf9\uff0c\u90a3\u65f6\u4f60\u5df2\u662f\u6211\u7684\u4e08\u592b\uff0c\u6211\u4f1a\u5b8c\u5168\u542c\u4f60\u7684\u3002\u201c \u3000\u3000\u7070\u8863\u6c49\u5b50\u81ea\u4ece\u5a18\u80ce\u957f\u5927\uff0c\u4e5f\u6ca1\u4eca\u5929\u8fd9\u4e48\u5f00\u5fc3\u8fc7\uff0c\u4ed6\u771f\u6000\u7591\u662f\u4e0d\u662f\u5728\u505a\u68a6\uff0c\u4e5f\u6000\u7591\u662f\u5426\u8033 \u6735\u51fa\u4e86\u6bdb\u75c5\u3002 \u3000\u3000\u53ea\u542c\u60dc\u6625\u5a07\u6ef4\u6ef4\u7684\u58f0\u97f3\u95ee\u9053\uff1a\u201c\u5927\u54e5\uff0c\u8fd8\u6709\u591a\u8fdc\uff1f\u201d \u3000\u3000\u7070\u8863\u6c49\u5b50\u6014\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u592b\u4eba\u96be\u9053\u4e0d\u8ba4\u8bc6\u8def\u5417\uff1f\u201d \u3000\u3000\u60dc\u6625\u9053\uff1a\u201c\u6211\u7684\u8bb0\u6027\u5dee\uff0c\u5c24\u5176\u5c71\u8fd9\u4e48\u5927\uff0c\u7b80\u76f4\u6709\u4e9b\u2018\u4e91\u6df1\u4e0d\u77e5\u5904\u2019\u2026\u201d \u3000\u3000\u201c\u592b\u4eba\u8bf4\u5f97\u5bf9\uff0c\u7b2c\u4e00\u53e5\u662f\u2018\u677e\u4e0b\u95ee\u7ae5\u5b50\u201d\u3002\u201c \u3000\u3000\u201c\u6211\u771f\u4f69\u670d\u5927\u54e5\u6709\u8fd9\u4e48\u597d\u7684\u5b66\u95ee\uff0c\u4eca\u5929\u82e5\u4e0d\u662f\u5728\u8def\u4e0a\u9047\u5230\u4f60\uff0c\u6211\u8fd8\u771f\u662f\u8fdb\u4e0d\u4e86\u5206\u575b \u5462\uff01\u201d \u3000\u3000\u201c\u5927\u7ea6\u518d\u534a\u4e2a\u65f6\u8fb0\u5c31\u5230\u4e86\u3002\u201d \u3000\u3000\u679c\u7136\uff0c\u534a\u4e2a\u65f6\u8fb0\u4e4b\u540e\uff0c\u5230\u8fbe\u4e86\u5c71\u8170\u91cc\u4e00\u5904\u72ed\u8c37\u5165\u53e3\u3002 \u3000\u3000\u7070\u8863\u6c49\u5b50\u5411\u8c37\u53e3\u4e00\u6307\u9053\uff1a\u201c\u73b0\u5728\u592b\u4eba\u8be5\u770b\u6e05\u4e86\u5427\uff01\u5206\u575b\u5728\u91cc\u9762\u3002\u201d \u3000\u3000\u60dc\u6625\u62c5\u5fc3\u8c37\u5185\u53e6\u6709\u5c94\u8def\uff0c\u8bd5\u63a2\u7740\u95ee\u9053\uff1a\u201c\u539f\u6765\u5c31\u5728\u8fd9\u91cc\uff0c\u5982\u679c\u8ba9\u6211\u81ea\u5df1\u627e\uff0c\u8fd8\u771f\u662f\u627e\u4e0d \u5230\u5462\uff01\u662f\u5426\u8fdb\u5165\u8c37\u53e3\u5c31\u5230\u4e86\uff1f\u201d \u3000\u3000\u201c\u592b\u4eba\u4e0d\u662f\u6765\u8fc7\u4e00\u6b21\u5417\uff1f\u201d \u3000\u3000\u201c\u4f60\u8be5\u56de\u7b54\u6211\u7684\u8bdd\uff0c\u522b\u53cd\u6765\u95ee\u6211\u3002\u201d \u3000\u3000\u201c\u8fdb\u5165\u8c37\u53e3\uff0c\u5927\u7ea6\u8fd8\u8981\u8d70\u76cf\u8336\u5de5\u592b\uff0c\u7136\u540e\u518d\u5411\u53f3\u8fb9\u8f6c\uff0c\u518d\u8fdb\u53bb\u5c31\u5230\u4e86\u3002\u201d \u3000\u3000\u8fd9\u65f6\u60dc\u6625\u65e9\u62d4\u51fa\u957f\u5251\uff0c\u731b\u5411\u7070\u8863\u6c49\u5b50\u80cc\u5fc3\u523a\u53bb\u3002\u7070\u8863\u6c49\u5b50\u4f5c\u68a6\u4e5f\u6ca1\u60f3\u5230\u6709\u6b64\u7a81\u53d8\uff0c\u53ea\u60e8 \u53eb\u51fa\u534a\u58f0\uff0c\u4fbf\u5012\u4e0b\u5730\u53bb\u3002 \u3000\u3000\u60dc\u6625\u628a\u7070\u8863\u6c49\u5b50\u7684\u5c38\u4f53\u62d6\u51fa\u8c37\u53e3\u5916\uff0c\u653e\u5728\u9690\u79d8\u5904\uff0c\u7136\u540e\u7b49\u5f85\u4e09\u516c\u4e3b\u7b49\u4eba\u5230\u6765\u3002 \u3000\u3000\u7247\u8be5\u4e4b\u540e\uff0c\u4e09\u516c\u4e3b\u3001\u5c0f\u71d5\u5b50\u3001\u601c\u82b1\u4e09\u4eba\u679c\u7136\u968f\u540e\u8d76\u5230\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u95ee\u9053\uff1a\u201c\u90a3\u4eba\u5462\uff1f\u201d \u3000\u3000\u60dc\u6625\u6307\u6307\u8eab\u8fb9\u4e00\u5904\u8349\u4e1b\u9053\uff1a\u201c\u5728\u91cc\u9762\u3002\u201d \u3000\u3000\u4e09\u516c\u4e3b\u9053\uff1a\u201c\u4e0d\u80fd\u66b4\u5c38\u5728\u5916\uff0c\u5fc5\u987b\u8bbe\u6cd5\u63a9\u57cb\u597d\uff0c\u6765\uff0c\u5927\u5bb6\u4e00\u8d77\u52a8\u624b\uff01\u201d \u3000\u3000\u4e8e\u662f\uff0c\u56db\u4eba\u5404\u81ea\u62ff\u51fa\u5175\u5203\uff0c\u5728\u9644\u8fd1\u6398\u4e86\u4e00\u4e2a\u5927\u5751\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u53c8\u9053\uff1a\u201c\u8bf7\u71d5\u5c11\u4fa0\u641c\u641c\u4ed6\u8eab\u4e0a\uff0c\u770b\u770b\u662f\u5426\u6709\u8170\u724c\u6216\u5176\u4ed6\u6807\u8bc6\uff0c\u6709\u5c31\u7559\u4e0b\uff0c\u54b1\u4eec\u81ea \u5df1\u597d\u7528\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u5728\u7070\u8863\u6c49\u5b50\u8eab\u4e0a\u641c\u4e86\u641c\uff0c\u679c\u7136\u641c\u51fa\u4e00\u5757\u8170\u724c\u3002 \u3000\u3000\u57cb\u597d\u5c38\u4f53\u540e\uff0c\u601c\u82b1\u95ee\u9053\uff1a\u201c\u54b1\u4eec\u662f\u5426\u8be5\u73b0\u5728\u5c31\u51b2\u8fdb\u53bb\u4e86\uff1f\u201d \u3000\u3000\u4e09\u516c\u4e3b\u6c89\u541f\u4e86\u534a\u664c\uff0c\u5374\u671b\u5411\u5c0f\u71d5\u4e8e\u9053\uff1a\u201c\u71d5\u5c11\u4fa0\u7684\u610f\u601d\u5462\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u60dc\u6625\u59d1\u5a18\u66fe\u8bf4\u8fc7\uff0c\u9b54\u6559\u7ec8\u5357\u5206\u575b\u91cc\u81f3\u5c11\u6709\u4e00\u767e\u591a\u4eba\uff0c\u54b1\u4eec\u53ea\u6709\u56db\u4eba\uff0c\u82e5\u73b0 \u5728\u51b2\u8fdb\u53bb\uff0c\u603b\u5acc\u8f7b\u4e3e\u5984\u52a8\u4e86\u4e9b\u3002\u201d \u3000\u3000\u4e09\u516c\u4e3b\u70b9\u70b9\u5934\u9053\uff1a\u201c\u6211\u4e5f\u8fd9\u4e48\u60f3\uff0c\u73b0\u5728\u5c31\u56de\u53bb\u5427\uff01\u7b49\u548c\u738b\u5927\u4eba\u3001\u6c5f\u5927\u4fa0\u5546\u8bae\u5546\u8bae\u518d\u8bf4\uff0c \u65e2\u7136\u5df2\u7ecf\u627e\u5230\u9b54\u7a74\uff0c\u884c\u52a8\u4e0d\u5fc5\u6025\u5728\u4e00\u65f6\u3002\u201d \u3000\u3000\u601c\u82b1\u9053\uff1a\u201c\u53ef\u662f\u60dc\u6625\u7684\u89e3\u836f\u4e0d\u80fd\u803d\u8bef\uff0c\u5979\u597d\u50cf\u5269\u4e0b\u6ca1\u51e0\u5929\u4e86\u3002\u201d \u3000\u3000\u60dc\u6625\u5fd9\u9053\uff1a\u201c\u4e0d\u5fc5\u4e3a\u6211\u6302\u5fc3\uff0c\u6211\u81f3\u5c11\u8fd8\u53ef\u4ee5\u652f\u6491\u5341\u5929\u3002\u201d \u3000\u3000\u4e09\u516c\u4e3b\u9053\uff1a\u201c\u90a3\u5f88\u597d\uff0c\u73b0\u5728\u5c31\u56de\u53bb\u5427\uff01\u201d \u3000\u3000\u56db\u4eba\u968f\u5373\u4e0b\u4e86\u5c71\uff0c\u5f80\u56de\u8d70\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u4e00\u884c\u56db\u4eba\u56de\u5230\u6240\u4f4f\u7684\u6c11\u5bb6\uff0c\u8fd8\u4e0d\u5230\u4e2d\u5348\u3002 \u3000\u3000\u7531\u4e8e\u5176\u4ed6\u4e09\u7ec4\u8fd8\u672a\u56de\u6765\uff0c\u4ed6\u4eec\u7528\u8fc7\u5348\u9910\u540e\uff0c\u53ea\u597d\u4e00\u8fb9\u4f11\u606f\uff0c\u4e00\u8fb9\u7b49\u5f85\u3002 \u3000\u3000\u76f4\u7b49\u5230\u5929\u9ed1\uff0c\u4e09\u7ec4\u624d\u9646\u7eed\u56de\u6765\u3002 \u3000\u3000\u5f53\u5927\u5bb6\u5f97\u77e5\u4e09\u516c\u4e3b\u8fd9\u4e00\u7ec4\u5df2\u627e\u5230\u9b54\u6559\u5206\u575b\u5de2\u7a74\uff0c\u83ab\u4e0d\u6b22\u6b23\u9f13\u821e\uff0c\u90fd\u8bf4\u8fd8\u662f\u4e09\u516c\u4e3b\u6709\u529e \u6cd5\uff0c\u4e00\u51fa\u9a6c\u5c31\u65d7\u5f00\u5f97\u80dc\u3002 \u3000\u3000\u665a\u9910\u540e\uff0c\u6240\u6709\u7684\u4eba\uff01\u9f50\u96c6\u5728\u6c11\u5b85\u5916\u7684\u7a7a\u5730\u4e0a\uff0c\u5f00\u4e00\u6b21\u884c\u52a8\u524d\u7684\u5927\u4f1a\u3002 \u3000\u3000\u738b\u5f64\u9996\u5148\u53d1\u8a00\u9053\uff1a\u201c\u65e2\u7136\u5df2\u627e\u5230\u9b54\u7a74\uff0c\u5c31\u8be5\u5c3d\u5feb\u884c\u52a8\uff0c\u5426\u5219\u82e5\u90a3\u4e09\u4e2a\u5973\u7684\u4ee5\u53ca\u9a6c\u5be1\u5987\u3001 \u9f99\u5728\u5929\u4e00\u8d70\uff0c\u54b1\u4eec\u5c31\u662f\u6363\u5e73\u9b54\u7a74\uff0c\u8fd8\u662f\u529f\u4e8f\u4e00\u7bd1\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u672c\u6765\u4e0d\u540c\u610f\u738b\u5f64\u7684\u770b\u6cd5\uff0c\u56e0\u4e3a\u4ed6\u5e0c\u671b\u80fd\u7a0d\u5f85\u6570\u65e5\uff0c\u4ee5\u4fbf\u628a\u4e00\u9053\u98de\u8679\u82df\u6167\u6708\u4e5f\u8bf7\u6765 \u52a9\u9635\uff0c\u6709\u5979\u52a0\u5165\uff0c\u5fc5\u53ef\u5927\u529f\u544a\u6210\u3002\u4f46\u662f\u56e0\u60dc\u6625\u5fc5\u987b\u53ca\u65e9\u53d6\u5f97\u89e3\u836f\uff0c\u4e5f\u5c31\u4e0d\u65b9\u4fbf\u628a\u8fd9\u610f\u89c1\u63d0\u51fa \u6765\u3002 \u3000\u3000\u5012\u662f\u4e09\u516c\u4e3b\u4e5f\u60f3\u5230\u4e86\u8fd9\u70b9\uff0c\u8f6c\u8eab\u95ee\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u6c5f\u53d4\u53d4\uff0c\u6211\u5e08\u7236\u5728\u9752\u4e91\u5d16\u90a3\u8fb9\uff0c\u7a76\u7adf\u8981 \u591a\u4e45\u65f6\u95f4\u624d\u80fd\u6551\u6d3b\u5929\u96f7\u8001\u524d\u8f88\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u542c\u82df\u5973\u4fa0\u7684\u8bed\u6c14\uff0c\u6700\u5c11\u8fd8\u8981\u4e09\u5929\u65f6\u95f4\uff0c\u4e0d\u8fc7\u4eca\u5929\u7b97\u8d77\uff0c\u5e94\u8be5\u53ea\u5269\u4e0b\u4e24\u5929 \u4e86\u3002\u201d \u3000\u3000\u4e09\u516c\u4e3b\u518d\u5bf9\u738b\u5f64\u9053\uff1a\u201c\u738b\u5927\u4eba\u63d0\u8bae\u9a6c\u4e0a\u91c7\u53d6\u884c\u52a8\uff0c\u6211\u4eec\u5b8c\u5168\u540c\u610f\uff0c\u4e0d\u8fc7\u662f\u5426\u80fd\u518d\u7b49\u4e24 \u5929\uff1f\u8ba9\u6211\u5230\u9752\u4e91\u5d16\u628a\u5e08\u7236\u4e5f\u8bf7\u6765\u52a9\u9635\u3002\u201d \u3000\u3000\u738b\u5f64\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u8fdf\u4e24\u5929\u884c\u52a8\uff0c\u5f53\u7136\u53ef\u4ee5\u3002\u4e0d\u8fc7\u4e07\u4e00\u5728\u8fd9\u4e24\u5929\u91cc\uff0c\u9a6c\u5be1\u5987\u3001\u9f99\u5728\u5929\u548c \u90a3\u4e09\u4e2a\u5973\u4eba\u79bb\u5f00\u9b54\u7a74\u5462\uff1f\u201d \u3000\u3000\u4e09\u516c\u4e3b\u671b\u7740\u60dc\u6625\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u803d\u8bef\u4e24\u5929\u5bf9\u4f60\u7684\u89e3\u836f\u5e76\u65e0\u5f71\u54cd\uff0c\u73b0\u5728\u6211\u95ee\u4f60\uff0c\u9b54\u6559\u5206\u575b \u7684\u5bf9\u5916\u4ea4\u901a\u662f\u5426\u53ea\u6709\u90a3\u9053\u72ed\u8c37\uff1f\u201d \u3000\u3000\u60dc\u6625\u72b9\u8c6b\u4e86\u4e00\u4e0b\u9053\uff1a\u201c\u8fd9\u65b9\u9762\u5a62\u5b50\u5e76\u4e0d\u6e05\u695a\u3002\u201d \u3000\u3000\u4e09\u516c\u4e3b\u9053\uff1a\u201c\u5c31\u7b97\u9003\u8131\u4e86\u9a6c\u5be1\u5987\u548c\u9f99\u5728\u5929\uff0c\u6211\u60f3\u9b54\u6559\u5206\u575b\u575b\u4e3b\u90a3\u91cc\u5fc5\u5b9a\u4e5f\u6709\u89e3\u836f\uff0c\u6240\u4ee5 \u8fd8\u662f\u7b49\u6211\u5e08\u7236\u6765\u4e86\u4ee5\u540e\u518d\u91c7\u53d6\u884c\u52a8\u8f83\u597d\u3002 \u3000\u3000\u81f3\u4e8e\u90a3\u6761\u8c37\u53e3\uff0c\u6700\u597d\u4eca\u665a\u5c31\u6d3e\u4e24\u4e2a\u4eba\u53bb\u8d1f\u8d23\u76d1\u89c6\uff0c\u660e\u5929\u5929\u4eae\u540e\u518d\u6d3e\u4eba\u524d\u53bb\u63a5\u66ff\uff0c\u5927\u5bb6\u8c01 \u613f\u610f\u524d\u53bb\uff0c\u8bf7\u81ea\u52a8\u8868\u793a\uff01\u201c \u3000\u3000\u5c0f\u71d5\u5b50\u7acb\u5373\u8d77\u8eab\u9053\uff1a\u201c\u4eca\u5929\u662f\u6211\u968f\u4e09\u516c\u4e3b\u524d\u53bb\u7684\uff0c\u6211\u77e5\u9053\u8def\uff0c\u5e94\u8be5\u7531\u6211\u53bb\u3002\u201d \u3000\u3000\u4e09\u516c\u4e3b\u9053\uff1a\u201c\u597d\uff01\u8fd8\u6709\u54ea\u4e00\u4f4d\u613f\u610f\u53bb\uff1f\u201d \u3000\u3000\u60dc\u6625\u4e5f\u8fc5\u5feb\u7684\u7ad9\u8d77\u8eab\u9053\uff1a\u201c\u5a62\u5973\u9664\u4e86\u5df2\u77e5\u8def\u5f84\uff0c\u800c\u4e14\u8fd8\u8ba4\u8bc6\u91cc\u9762\u4e0d\u5c11\u4eba\uff0c\u5c31\u7531\u5a62\u5b50\u53bb \u5427\uff01\u201d \u3000\u3000\u4e09\u516c\u4e3b\u5f53\u7136\u540c\u610f\u3002 \u3000\u3000\u5c31\u5728\u6563\u4f1a\u540e\uff0c\u5c0f\u71d5\u5b50\u548c\u60dc\u6625\u518d\u5230\u8fbe\u9b54\u6559\u5206\u575b\u90a3\u6761\u8c37\u53e3\u5916\u3002 \u3000\u3000\u4e24\u4eba\u627e\u4e86\u4e00\u5904\u9690\u79d8\u6240\u5728\u505c\u4e0b\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u767d\u5929\u88ab\u59d1\u5a18\u6240\u6740\u7684\u90a3\u540d\u6559\u4e01\uff0c\u4ed6\u4eec\u6b64\u523b\u4f1a\u4e0d\u4f1a\u5df2\u7ecf\u77e5\u9053\u4e86\uff1f\u201d \u3000\u3000\u60dc\u6625\u60f3\u4e86\u60f3\u9053\uff1a\u201c\u53ef\u80fd\u8fd8\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u59d1\u5a18\u8fd9\u8bdd\u662f\u6839\u636e\u4ec0\u4e48\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u90a3\u6559\u4e86\u5fc5\u5b9a\u662f\u5949\u547d\u4e0b\u5c71\u529e\u4e8b\uff0c\u5730\u70b9\u591a\u534a\u662f\u957f\u5b89\uff0c\u8def\u8fdc\uff0c\u665a\u4e0a\u56de\u4e0d\u6765\u662f\u5e38\u6709\u7684\u4e8b\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u5ffd\u7136\u5fc3\u5934\u4e00\u52a8\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u54b1\u4eec\u8001\u5728\u8c37\u53e3\u5916\u4e5f\u4e0d\u662f\u529e\u6cd5\uff0c\u6211\u60f3\u8d70\u53bb\u770b\u770b\uff0c\u4f60\u4ee5\u4e3a \u5982\u4f55\uff1f\u201d \u3000\u3000\u60dc\u6625\u4e0d\u4ee5\u4e3a\u7136\u9053\uff1a\u201c\u6211\u4eec\u53ea\u662f\u6765\u8c37\u53e3\u8d1f\u8d23\u76d1\u89c6\u7684\uff0c\u82e5\u95ef\u8fdb\u53bb\uff0c\u5c82\u4e0d\u53cd\u800c\u6253\u8349\u60ca\u86c7\uff1f\u201d \u3000\u3000\u201c\u6211\u6709\u8170\u724c\uff0c\u7eb5\u7136\u88ab\u53d1\u73b0\uff0c\u4e5f\u6709\u529e\u6cd5\u5e94\u4ed8\u8fc7\u53bb\u3002\u201d \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u53bb\u4e0d\u53ef\uff1f\u201d \u3000\u3000\u201c\u65e2\u7136\u6765\u4e86\uff0c\u603b\u8be5\u505a\u70b9\u4e8b\u60c5\uff0c\u5982\u679c\u4eca\u665a\u6211\u80fd\u628a\u9b54\u6559\u5206\u575b\u8be6\u7ec6\u4fa6\u5bdf\u4e00\u904d\uff0c\u5bf9\u4e0b\u6b21\u7684\u6b63\u5f0f\u884c \u52a8\u5c82\u975e\u5927\u6709\u5e2e\u52a9\uff01\u201d \u3000\u3000\u201c\u4f60\u8bf4\u5f97\u56fa\u7136\u6709\u7406\uff0c\u4f46\u6211\u603b\u62c5\u5fc3\u4f1a\u6253\u8349\u60ca\u86c7\u3002\u201d \u3000\u3000\u201c\u4e0d\u4f1a\u7684\u3002\u540c\u65f6\u6211\u4e5f\u5e0c\u671b\u59d1\u5a18\u80fd\u4e00\u8d77\u8fdb\u53bb\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\uff1f\u8981\u6211\u4e5f\u4e00\u8d77\u53bb\uff1f\u201d \u3000\u3000\u201c\u59d1\u5a18\u5bf9\u91cc\u9762\u7684\u73af\u5883\u5f88\u719f\u6089\uff0c\u82e5\u80fd\u4e00\u8d77\u53bb\uff0c\u7b49\u4e8e\u7701\u4e86\u6211\u4e0d\u5c11\u9ebb\u70e6\u3002\u201d \u3000\u3000\u201c\u4f60\u9519\u4e86\uff0c\u6b63\u56e0\u4e3a\u4ed6\u4eec\u90fd\u8ba4\u8bc6\u6211\uff0c\u6240\u4ee5\u6211\u624d\u4e0d\u80fd\u53bb\u3002\u82e5\u4ed6\u4eec\u770b\u5230\u6211\uff0c\u6211\u62ff\u4ec0\u4e48\u7406\u7531\u5411\u4ed6 \u4eec\u89e3\u91ca\uff1f\u4e07\u4e00\u88ab\u9a6c\u5be1\u5987\u6216\u9f99\u5728\u5929\u770b\u5230\uff0c\u90a3\u65f6\u53c8\u8be5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u89c9\u5f97\u60dc\u6625\u7684\u8bdd\u6709\u7406\uff0c\u53ea\u597d\u51b3\u5b9a\u4e00\u4eba\u8fdb\u53bb\u3002 \u3000\u3000\u60dc\u6625\u62e6\u963b\u4e0d\u4f4f\u5c0f\u71d5\u5b50\uff0c\u4e5f\u53ea\u6709\u4efb\u7531\u4ed6\u8981\u600e\u4e48\u505a\u5c31\u600e\u4e48\u505a\u4e86\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u884c\u52a8\u524d\u4ea4\u5f85\u9053\uff1a\u201c\u59d1\u5a18\u8bf7\u522b\u79bb\u5f00\u73b0\u5730\uff0c\u514d\u5f97\u6211\u56de\u6765\u65f6\u8054\u7edc\u4e0d\u4e0a\u3002\u201d \u3000\u3000\u201c\u71d5\u5c11\u4fa0\u51c6\u5907\u4ec0\u4e48\u65f6\u5019\u56de\u6765\uff1f\u201d \u3000\u3000\u201c\u90a3\u8981\u770b\u8fdb\u884c\u5f97\u662f\u5426\u987a\u5229\u4e86\uff0c\u4e0d\u8fc7\u4e00\u5b9a\u4f1a\u5728\u4e94\u66f4\u524d\u56de\u6765\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u6211\u7b49\u4f60\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u968f\u5373\u5168\u795e\u6212\u5907\u7684\u8fdb\u5165\u8c37\u4e2d\u3002 \u3000\u3000\u5c82\u77e5\u521a\u8fdb\u5165\u8c37\u53e3\u4e0d\u8fdc\uff0c\u8033\u8fb9\u4fbf\u54cd\u8d77\u4e00\u58f0\u559d\u95ee\u3002 \u3000\u3000\u8fd9\u58f0\u97f3\u7adf\u662f\u53d1\u81ea\u8c37\u58c1\u4e0a\u65b9\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u505c\u4e0b\u811a\u6b65\uff0c\u5411\u4e0a\u671b\u53bb\u3002 \u3000\u3000\u4e24\u8fb9\u8c37\u58c1\u5ced\u7acb\u5982\u524a\uff0c\u9ad8\u53ef\u64ad\u5929\u3002\u5728\u6a21\u7cca\u7684\u5149\u7ebf\u4e2d\uff0c\u597d\u4e0d\u5bb9\u6613\u624d\u770b\u5230\u5927\u7ea6\u79bb\u8c37\u5e95\u7ea6\u4e09\u56db\u4e08 \u9ad8\u5904\uff0c\u4e00\u5757\u7a81\u51fa\u7684\u5927\u77f3\u4e0a\u7ad9\u7740\u4e24\u4e2a\u4eba\u5f71\uff0c\u4e0d\u6d88\u8bf4\uff0c\u8fd9\u662f\u8d1f\u8d23\u8c37\u53e3\u8b66\u6212\u7684\u4e24\u540d\u9b54\u6559\u6559\u4e01\u3002 \u3000\u3000\u5176\u4e2d\u4e00\u4e2a\u4f4e\u6c89\u7684\u58f0\u97f3\u559d\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u9887\u4e3a\u9547\u5b9a\uff0c\u4ece\u5bb9\u4e0d\u8feb\u7684\u7b54\u9053\uff1a\u201c\u5949\u547d\u5916\u51fa\u529e\u4e8b\u8fd4\u56de\u5206\u575b\u7684\u3002\u201d \u3000\u3000\u201c\u62a5L\u540d\u6765\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u65e9\u5df2\u6709\u5907\uff0c\u987a\u53e3\u7b54\u9053\uff1a\u201c\u5f20\u4e09\u9633\u3002\u201d \u3000\u3000\u90a3\u6559\u4e86\u987f\u4e86\u4e00\u987f\u9053\uff1a\u201c\u600e\u4e48\u6ca1\u542c\u8bf4\u6709\u4f60\u8fd9\u4e48\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u201c\u5206\u575b\u91cc\u8fd9\u4e48\u591a\u4eba\uff0c\u4f60\u8001\u5144\u4e0d\u53ef\u80fd\u5168\u8ba4\u8bc6\u5427\uff1f\u201d \u3000\u3000\u201c\u5206\u575b\u603b\u5171\u4e0d\u5230\u4e24\u767e\u4eba\uff0c\u6211\u5f53\u7136\u5168\u8ba4\u8bc6\u3002\u201d \u3000\u3000\u201c\u6211\u662f\u65b0\u6765\u7684\u3002\u201d \u3000\u3000\u90a3\u6559\u4e86\u987f\u4e86\u987f\u9053\uff1a\u201c\u4e0a\u6765\u8ba9\u6211\u68c0\u67e5\u4e00\u4e0b\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u9053\uff1a\u8c37\u58c1\u90a3\u4e48\u9ad8\uff0c\u600e\u4e48\u4e0a\u5f97\u53bb\uff1f\u201c \u3000\u3000\u90a3\u6559\u4e86\u817e\u8eab\u4e00\u8dc3\uff0c\u7acb\u5373\u843d\u4e0b\u5730\u6765\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u4e0d\u89c9\u6697\u6697\u5403\u60ca\uff0c\u4e00\u540d\u8d1f\u8d23\u7ad9\u5c97\u653e\u54e8\u7684\u6559\u4e01\uff0c\u5c45\u7136\u6709\u5982\u6b64\u9020\u8be3\u7684\u8f7b\u529f\uff0c\u8fd9\u4e48\u770b\u6765\uff0c \u60f3\u653b\u7834\u8fd9\u5ea7\u5206\u575b\u8fd8\u5b9e\u5728\u4e0d\u662f\u4e00\u4ef6\u6613\u4e8b\u3002 \u3000\u3000\u90a3\u6559\u4e01\u843d\u4e0b\u5730\u6765\uff0c\u5148\u4ed4\u7ec6\u770b\u4e86\u5c0f\u71d5\u5b50\u4e00\u773c\uff0c\u7136\u540e\u4e00\u4f38\u624b\u9053\uff1a\u201c\u628a\u8170\u724c\u62ff\u51fa\u6765\u6211\u770b\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u628a\u8170\u724c\u9012\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u90a3\u6559\u4e86\u770b\u4e86\u4e00\u4e0b\uff0c\u53c8\u4ea4\u8fd8\u5c0f\u71d5\u5b50\uff0c\u9053\uff1a\u201c\u8fdb\u53bb\u5427\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u7ee7\u7eed\u524d\u884c\u3002 \u3000\u3000\u53ef\u80fd\u5173\u5361\u53ea\u6709\u8fd9\u4e00\u9053\uff0c\u4e00\u8def\u4e4b\u4e0a\u518d\u65e0\u5176\u4ed6\u62e6\u963b\u3002 \u3000\u3000\u76cf\u8336\u5de5\u592b\u4e4b\u540e\uff0c\u8c37\u9053\u5411\u5de6\u65b9\u5f2f\u53bb\uff0c\u518d\u524d\u8fdb\u4e0d\u4e45\u4fbf\u8fdb\u5165\u4e00\u7247\u9887\u4e3a\u5bbd\u9614\u5e73\u5766\u7684\u8c37\u5730\u3002 \u3000\u3000\u8fd9\u7247\u8c37\u5730\uff0c\u5f53\u771f\u663e\u793a\u51fa\u9020\u7269\u4e4b\u5947\u3002 \u3000\u3000\u4f46\u89c1\u56db\u5468\u5168\u662f\u9ad8\u8038\u63a5\u4e91\u7684\u5c71\u58c1\uff0c\u4f7f\u8fd9\u8c37\u5730\u4e0d\u4f46\u6210\u4e86\u771f\u6b63\u7684\u5c94\u5730\uff0c\u7b80\u76f4\u53ef\u4ee5\u5f62\u5bb9\u6210\u4e00\u4e2a\u540d \u526f\u5176\u5b9e\u7684\u5927\u5929\u4e95\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5c0f\u71d5\u4e8e\u53cd\u800c\u6709\u7740\u65e0\u6240\u9002\u4ece\u4e4b\u611f\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u7247\u8c37\u5730\u8db3\u6709\u6570\u5341\u4ea9\u65b9\u5706\uff0c\u800c\u91cc\u9762\u4f4f\u7684\u4e0d\u8fc7\u767e\u4f59\u4eba\uff0c\u5728\u770b\u4e0d\u5230\u623f\u820d\u4e4b\u4e0b\u53c8\u5230\u90a3\u91cc\u53bb \u5bfb\u627e\u9b54\u6559\u5206\u575b\u7684\u771f\u6b63\u6240\u5728\u5462\uff1f\u4ed6\u6f2b\u65e0\u5934\u7eea\u7684\u8d70\u7740\uff0c\u7ec8\u4e8e\u53d1\u73b0\u5728\u9760\u5357\u8fb9\u7684\u5c71\u58c1\u4e0b\u51fa\u73b0\u4e86\u4e00\u6392\u6392 \u7684\u77f3\u5c4b\uff0c\u770b\u6765\u5c31\u50cf\u4e00\u5904\u5c0f\u6751\u843d\u822c\u3002 \u3000\u3000\u77f3\u5c4b\u5916\u6709\u4e24\u4e09\u4e2a\u4eba\u5f71\u5728\u6e38\u52a8\u3002 \u3000\u3000\u5206\u660e\u662f\u8d1f\u8d23\u5de1\u591c\u7684\u9b54\u5f92\u3002 \u3000\u3000\u4e3a\u4e86\u4e0d\u6253\u8349\u60ca\u86c7\uff0c\u5c0f\u71d5\u5b50\u51b3\u5b9a\u4e0d\u518d\u8fdb\u4e00\u6b65\u884c\u52a8\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u4ed6\u6b64\u6765\u5df2\u7ecf\u6709\u5f88\u5927\u7684\u6536\u83b7\uff0c\u81f3\u5c11\u5df2\u628a\u91cc\u9762\u7684\u72b6\u51b5\u6982\u7565\u6478\u6e05\uff0c\u4e0b\u6b21\u5927\u961f\u4eba\u9a6c\u524d \u6765\uff0c\u4ed6\u76f8\u4fe1\u5df2\u7ecf\u6709\u8d44\u683c\u505a\u5411\u5bfc\u4e86\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u4ed6\u5faa\u7740\u539f\u8def\u51fa\u8c37\u3002 \u3000\u3000\u5f53\u5c06\u901a\u5411\u8c37\u53e3\u65f6\uff0c\u4ed6\u653e\u8f7b\u811a\u6b65\uff0c\u7d27\u8d34\u8c37\u58c1\u800c\u884c\uff0c\u679c\u7136\u628a\u4e0a\u65b9\u8d1f\u8d23\u8b66\u6212\u7684\u6559\u4e01\u7ed9\u7792\u8fc7\u4e86\u3002 \u3000\u3000\u6765\u5230\u8c37\u53e3\u5916\u7684\u7ea6\u5b9a\u4e4b\u5904\uff0c\u60dc\u6625\u5f88\u5feb\u4fbf\u4e3b\u52a8\u8fce\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u201c\u600e\u4e48\u56de\u6765\u5f97\u8fd9\u4e48\u5feb\uff1f\u83ab\u975e\u65e0\u6cd5\u6df7\u8fdb\u53bb\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u628a\u7ecf\u8fc7\u8bf4\u4e86\u4e00\u904d\u3002 \u3000\u3000\u8fd9\u65f6\u4e0d\u5230\u4e09\u66f4\uff0c\u4e24\u4eba\u53c8\u4e0d\u80fd\u56de\u53bb\uff0c\u53ea\u597d\u8f6e\u6d41\u4f11\u606f\u8f6e\u6d41\u76d1\u89c6\u3002 \u3000\u3000\u76f4\u5230\u5929\u4eae\u540e\uff0c\u4e03\u5de7\u501f\u548c\u767d\u7fbd\u524d\u6765\u6362\u73ed\uff0c\u5c0f\u71d5\u5b50\u548c\u60dc\u6625\u624d\u56de\u53bb\u4f11\u606f\u3002 \u3000\u3000\u5230\u8fbe\u6240\u4f4f\u7684\u6c11\u5bb6\uff0c\u5f53\u4e09\u516c\u4e3b\u83b7\u6089\u6628\u591c\u60c5\u5f62\u540e\uff0c\u7ecf\u548c\u738b\u5f64\u3001\u6c5f\u5343\u91cc\u7b49\u4eba\u5546\u8bae\uff0c\u4e00\u81f4\u8ba4\u4e3a\u4e8b \u4e0d\u5b9c\u8fdf\uff0c\u8fdf\u5219\u6709\u53d8\uff0c\u5fc5\u987b\u53ca\u65e9\u884c\u52a8\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u4e09\u516c\u4e3b\u5728\u6c5f\u5343\u91cc\u7684\u966a\u540c\u4e0b\uff0c\u5f53\u5373\u8d76\u5f80\u9752\u4e91\u5d16\uff0c\u5e0c\u671b\u80fd\u628a\u5e08\u7236\u4e00\u9053\u98de\u8679\u82df\u6167\u6708\u8bf7\u6765 \u52a9\u9635\u3002 \u3000\u3000\u5f53\u665a\uff0c\u4e09\u516c\u4e3b\u548c\u6c5f\u5343\u91cc\u53c8\u5306\u5306\u8fd4\u56de\u3002 \u3000\u3000\u636e\u8bf4\u5929\u96f7\u8001\u4eba\u4ecd\u672a\u6e05\u9192\uff0c\u4e00\u9053\u98de\u8679\u82df\u6167\u6708\u65e0\u6cd5\u79bb\u5f00\u3002 \u3000\u3000\u5982\u6b64\u4e00\u6765\uff0c\u4ed6\u4eec\u53ea\u6709\u81ea\u5df1\u91c7\u53d6\u884c\u52a8\u4e86\u3002 \u3000\u3000\u672c\u6765\uff0c\u4e09\u516c\u4e3b\u6709\u610f\u5f53\u665a\u5c31\u91c7\u53d6\u884c\u52a8\u3002 \u3000\u3000\u4f46\u6c5f\u5343\u91cc\u5219\u5efa\u8bae\u628a\u65f6\u95f4\u6539\u5728\u7b2c\u4e8c\u5929\u51cc\u6668\uff0c\u7406\u7531\u662f\u591c\u95f4\u89c6\u7ebf\u4e0d\u826f\uff0c\u654c\u6697\u6211\u660e\uff0c\u653b\u51fb\u8005\u52bf\u5c06 \u5904\u4e8e\u4e0d\u5229\u5730\u4f4d\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u91c7\u7eb3\u4e86\u6c5f\u5343\u91cc\u7684\u610f\u89c1\u3002 \u3000\u3000\u6b21\u65e5\u56db\u66f4\u5de6\u53f3\u2014\u2014\u6240\u6709\u7684\u4eba\u90fd\u5df2\u8d77\u5e8a\uff0c\u6d17\u6bd5\u540e\u7acb\u5373\u63d0\u524d\u7528\u9910\uff0c\u5e76\u51c6\u5907\u4e86\u4e00\u4e9b\u5e72\u7cae\uff0c\u7136\u540e \u5404\u81ea\u88c5\u675f\u59a5\u5f53\uff0c\u643a\u5e26\u5175\u5203\uff0c\u7531\u5c0f\u71d5\u5b50\u548c\u60dc\u6625\u5e26\u8def\u51fa\u53d1\u3002 \u3000\u3000\u5230\u8fbe\u8c37\u53e3\uff0c\u5929\u5df2\u5fae\u660e\u3002 \u3000\u3000\u8fd9\u65f6\u5728\u8c37\u53e3\u5916\u8d1f\u8d23\u76d1\u89c6\u7684\u662f\u8d75\u4fdd\u548c\u9648\u5b8f\u3002 \u3000\u3000\u4e24\u4eba\u4e00\u89c1\u5927\u961f\u4e00\u9a6c\u5230\u6765\uff0c\u7acb\u5373\u8fce\u6765\u4f1a\u5408\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u81ea\u544a\u594b\u52c7\uff0c\u4ed6\u8981\u5148\u628a\u8c37\u53e3\u5185\u90a3\u4e24\u540d\u8d1f\u8d23\u8b66\u6212\u7684\u6559\u4e86\u6e05\u9664\uff0c\u4e8e\u662f\uff0c\u4f17\u4eba\u6682\u65f6\u5728\u8c37\u5916 \u4f11\u606f\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u72ec\u81ea\u8fdb\u5165\u8c37\u5185\u3002 \u3000\u3000\u8d70\u4e0d\u5230\u51e0\u6b65\uff0c\u4fbf\u5df2\u88ab\u8c37\u58c1\u4e0a\u7684\u6559\u4e01\u53d1\u73b0\u3002 \u3000\u3000\u53ea\u542c\u8c37\u58c1\u4e0a\u6709\u4eba\u559d\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u5f20\u4e09\u9633\u3002\u201d \u3000\u3000\u201c\u6709\u8170\u724c\u6ca1\u6709\uff1f\u201d\u201c\u6709\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u638f\u51fa\u8170\u724c\uff0c\u626c\u81c2\u6643\u4e86\u4e00\u6643\u3002 \u3000\u3000\u58c1\u4e0a\u5927\u6c49\u53c8\u559d\u9053\uff1a\u201c\u4f60\u4e0a\u6765\uff0c\u6211\u8981\u4ed4\u7ec6\u67e5\u770b\u4e00\u4e0b\uff01\u201d \u3000\u3000\u8fd9\u6b63\u662f\u5c0f\u71d5\u5b50\u6240\u5e0c\u671b\u7684\u3002 \u3000\u3000\u4e09\u56db\u4e08\u7684\u9ad8\u5ea6\uff0c\u5728\u4ed6\u6765\u8bf4\u8dc3\u4e0a\u53bb\u4e0d\u8d39\u5439\u7070\u4e4b\u529b\uff0c\u4f46\u4ed6\u5374\u5fc5\u987b\u88c5\u51fa\u5f88\u5403\u529b\u7684\u6837\u5b50\uff0c\u4e00\u8fde\u7eb5 \u8dc3\u4e86\u4e09\u6b21\uff0c\u624d\u624b\u6500\u8db3\u767b\u7684\u722c\u4e0a\u4e86\u90a3\u5757\u7a81\u51fa\u7684\u5de8\u77f3\u3002 \u3000\u3000\u5de8\u77f3\u4e0a\u4ecd\u662f\u4e24\u540d\u6c49\u5b50\u3002 \u3000\u3000\u4e00\u540d\u5927\u6c49\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u7684\u8f7b\u529f\u5f88\u4e0d\u9519\uff0c\u662f\u90a3\u4e00\u5802\u7684\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u5e76\u4e0d\u7b54\u8bdd\uff0c\u95ea\u7535\u822c\u53cc\u638c\u9f50\u51fa\u3002 \u3000\u3000\u53ea\u542c\u4e24\u58f0\u60e8\u547c\uff01 \u3000\u3000\u4e24\u540d\u5927\u6c49\u7686\u88ab\u51fb\u4e2d\u524d\u80f8\uff0c\u8eab\u4e0d\u7531\u5df1\u6454\u4e0b\u5de8\u77f3\uff0c\u7eb5\u7136\u5f53\u573a\u672a\u88ab\u51fb\u6bd9\uff0c\u6454\u4e0b\u60ac\u5d16\u4e4b\u540e\u4e5f\u5168\u90fd \u6454\u6b7b\u4e86\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u817e\u8eab\u8dc3\u4e0b\uff0c\u6765\u5230\u8c37\u53e3\u5411\u4f17\u4eba\u62db\u4e86\u62db\u624b\u3002 \u3000\u3000\u5927\u961f\u4eba\u9a6c\u5f88\u5feb\u4fbf\u6d8c\u8fdb\u8c37\u5185\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u4ecd\u662f\u5728\u524d\u5f00\u8def\u3002 \u3000\u3000\u987f\u996d\u5de5\u592b\u4e4b\u540e\uff0c\u4eba\u9a6c\u4fbf\u5df2\u62b5\u8fbe\u9b54\u6559\u5206\u575b\u4e4b\u524d\u3002 \u3000\u3000\u9b54\u6559\u5206\u575b\u575b\u4e3b\u4e5f\u65e9\u5df2\u7387\u4f17\u6765\u8fce\u3002 \u3000\u3000\u9635\u5f0f\u5217\u5f00\u4e4b\u540e\uff0c\u8db3\u6709\u4e0a\u767e\u4eba\u4e4b\u591a\u3002 \u5367\u9f99\u751f\u300a\u71d5\u5b50\u4f20\u5947\u300b \u7b2c\u4e09\u5341\u56db\u56de\u3000\u8840\u6d17\u9b54\u7a9f \u3000\u3000\u8fd9\u8fb9\u867d\u4ee5\u4e09\u516c\u4e3b\u4e3a\u9996\uff0c\u4f46\u51fa\u9762\u7684\u5374\u662f\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u4f4e\u58f0\u95ee\u60dc\u6625\u9053\uff1a\u201c\u59d1\u5a18\u662f\u5426\u8ba4\u8bc6\u9b54\u6559\u5206\u575b\u575b\u4e3b\uff1f\u201d \u3000\u3000\u60dc\u6625\u9053\uff1a\u201c\u8ba4\u8bc6\uff0c\u5c31\u662f\u5f53\u4e2d\u7a7f\u9ec4\u8863\u7684\u90a3\u4e00\u4e2a\u3002\u201d \u3000\u3000\u201c\u4ed6\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u201c\u53eb\u9ec4\u9f99\u3002\u201d \u3000\u3000\u201c\u9a6c\u5be1\u5987\u548c\u9f99\u5728\u5929\u662f\u5426\u5728\u91cc\u9762\uff1f\u201d \u3000\u3000\u60dc\u6625\u641c\u89c6\u4e86\u5bf9\u65b9\u7684\u4eba\u9a6c\u4e00\u904d\uff0c\u9053\uff1a\u201c\u597d\u50cf\u4e0d\u5728\u91cc\u9762\u3002\u201d \u3000\u3000\u738b\u5f64\u518d\u95ee\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u4f60\u662f\u89c1\u8fc7\u6842\u98d8\u9999\u7684\uff0c\u662f\u5426\u5728\u91cc\u9762\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u65e9\u5c31\u7559\u610f\u5bf9\u65b9\u9635\u4e2d\u662f\u5426\u6709\u6842\u98d8\u9999\u5728\uff0c\u7acb\u523b\u6447\u6447\u5934\u9053\uff1a\u201c\u665a\u8f88\u6ca1\u770b\u5230\u3002\u201d \u3000\u3000\u7ad9\u5728\u540e\u9762\u7684\u8001\u5996\u5a46\u53f2\u5999\u79cb\u5fd9\u9053\uff1a\u201c\u6842\u98d8\u9999\u548c\u53e6\u5916\u4e24\u4e2a\u5973\u7684\u7b97\u662f\u8fd9\u8fb9\u7684\u5ba2\u4eba\uff0c\u5979\u4eec\u4e0d\u53ef\u80fd \u968f\u9ec4\u9f99\u51fa\u6765\u7684\uff0c\u5f85\u4f1a\u513f\u628a\u9ec4\u9f99\u5236\u670d\u540e\uff0c\u4e0d\u6101\u6349\u4e0d\u5230\u5979\u4eec\u3002\u201d \u3000\u3000\u53ea\u542c\u9ec4\u9f99\u5df2\u7ecf\u53d1\u4e86\u8bdd\uff0c\u559d\u9053\uff1a\u201c\u4f60\u4eec\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba\uff0c\u80c6\u6562\u95ef\u8fdb\u897f\u5929\u5723\u6559\u5206\u575b\uff1f\u201d \u3000\u3000\u738b\u5f64\u6717\u58f0\u9053\uff1a\u201c\u5728\u4e0b\u738b\u5f64\uff0c\u73b0\u4efb\u5927\u5185\u4f8d\u536b\u7edf\u9886\uff0c\u5949\u7687\u4e0a\u5bc6\u65e8\uff0c\u524d\u6765\u527f\u706d\u4f60\u4eec\u8fd9\u897f\u57df\u9b54\u6559 \u7ec8\u5357\u5206\u575b\uff01\u201d \u3000\u3000\u9ec4\u9f99\u987f\u65f6\u53cc\u988a\u9752\u7b4b\u66b4\u8d77\uff0c\u51b7\u58f0\u5927\u7b11\u9053\uff1a\u201c\u597d\u54c7\uff01\u4ec5\u4ec5\u5341\u51e0\u4e2a\u4eba\uff0c\u7adf\u6562\u53e3\u51fa\u72c2\u8a00\uff0c\u8fd9\u662f\u4f60 \u4eec\u81ea\u5bfb\u6b7b\u8def\uff0c\u5f85\u4f1a\u513f\u602a\u4e0d\u5f97\u672c\u575b\u4e3b\u624b\u4e0b\u65e0\u60c5\uff01\u201d \u3000\u3000\u4ed6\u8bf4\u5230\u8fd9\u91cc\uff0c\u5ffd\u7136\u4e24\u773c\u4e00\u76f4\uff0c\u89c6\u7ebf\u505c\u5728\u60dc\u6625\u8138\u4e0a\u3002 \u3000\u3000\u4ed6\u6014\u4e86\u4e00\u4e0b\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u2026\u2026\u4e0d\u662f\u9f99\u526f\u5802\u4e3b\u592b\u4eba\u5417\uff1f\u600e\u4f1a\u548c\u4ed6\u4eec\u5728\u4e00\u8d77\uff1f\u83ab\u975e\u662f\u843d\u5728\u4ed6 \u4eec\u624b\u4e2d\u53d7\u80c1\u8feb\u800c\u6765\uff1f\u201d \u3000\u3000\u60dc\u6625\u53f1\u9053\uff1a\u201c\u6211\u53eb\u60dc\u6625\uff0c\u522b\u518d\u5728\u6211\u9762\u524d\u63d0\u9f99\u5728\u5929\uff0c\u4e0d\u8fc7\u4f60\u82e5\u80fd\u628a\u4ed6\u548c\u9a6c\u5f69\u4e91\u4ea4\u51fa\u6765\uff0c\u6211 \u4eec\u738b\u5927\u4eba\u4e5f\u8bb8\u53ef\u4ee5\u9976\u4f60\u4e0d\u6b7b\uff01\u201d \u3000\u3000\u9ec4\u9f99\u542c\u60dc\u6625\u7684\u53e3\u6c14\uff0c\u518d\u60f3\u8d77\u5979\u4ee5\u524d\u66fe\u5728\u5bab\u4e2d\u505a\u8fc7\u5bab\u5973\uff0c\u6599\u5b9a\u738b\u5f64\u6240\u8bf4\u7684\u8bdd\u53ef\u80fd\u4e0d\u5047\uff0c\u4f46 \u4ed6\u5c82\u80af\u675f\u624b\u5c31\u7f1a\uff1f\u6b64\u523b\u9ec4\u9f99\u5df2\u5f88\u5feb\u62ff\u5b9a\u4e3b\u610f\uff0c\u90a3\u5c31\u662f\u5c06\u738b\u5f64\u5e26\u6765\u7684\u8fd9\u4e00\u4f19\u4eba\u6765\u4e2a\u4e00\u7f51\u6253\u5c3d\uff0c \u5c82\u975e\u4eba\u4e0d\u77e5\u9b3c\u4e0d\u89c9\uff0c\u5c31\u7b97\u7687\u5e1d\u8001\u513f\u4e5f\u67e5\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u4ed6\u7acb\u523b\u4e0b\u4ee4\u624b\u4e0b\u7684\u4e0a\u767e\u4f4d\u9b54\u5f92\u4eec\uff0c\u5c06\u738b\u5f64\u4e00\u4f19\u4eba\u56e2\u56e2\u56f4\u4f4f\uff0c\u56f4\u4e86\u4e2a\u6c34\u6cc4\u4e0d\u901a\uff0c\u53e6 \u5916\u53c8\u6d3e\u51fa\u5341\u4f59\u4eba\u5c06\u5206\u575b\u7684\u8fdb\u51fa\u53e3\u5835\u4f4f\uff0c\u7edd\u4e0d\u7559\u4e0b\u4e00\u4e2a\u6d3b\u53e3\u3002 \u3000\u3000\u4ee5\u6b64\u523b\u7684\u60c5\u52bf\uff0c\u4e09\u516c\u4e3b\u4e00\u4f19\u4eba\u7b49\u4e8e\u5fc5\u987b\u80cc\u6c34\u4e00\u6218\u3002 \u3000\u3000\u53ea\u542c\u9ec4\u9f99\u5927\u559d\u4e00\u58f0\u9053\uff1a\u201c\u7f29\u5c0f\u5305\u56f4\u5708\uff0c\u5f00\u6740\uff01\u201d \u3000\u3000\u987f\u65f6\u6740\u58f0\u9707\u5929\uff0c\u9b54\u5f92\u4eec\u4eba\u5982\u6f6e\u6d8c\uff0c\u5175\u5203\u4ea4\u9519\u7684\u51b2\u6740\u8fc7\u6765\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u8fd9\u8fb9\u4ee5\u4e09\u516c\u4e3b\u3001\u738b\u5f64\u3001\u6c5f\u5343\u91cc\u3001\u5c0f\u71d5\u5b50\u6b66\u529f\u6700\u9ad8\uff0c\u56db\u4eba\u5206\u56db\u65b9\u5404\u5f53\u4e00\u9762\uff0c\u5176\u4f59\u6b66 \u529f\u8f83\u5f31\u7684\uff0c\u5219\u5728\u7b2c\u4e8c\u7ebf\u8fce\u654c\u3002 \u3000\u3000\u8fd9\u4e00\u573a\u4eba\u5982\u6f6e\u6d8c\u7684\u8fd1\u8eab\u640f\u51fb\uff0c\u76f4\u6740\u5f97\u660f\u5929\u9ed1\u5730\u3002 \u3000\u3000\u9b54\u5f92\u4eec\u867d\u7136\u4eba\u4eba\u8eab\u624b\u4e0d\u5f31\uff0c\u4f46\u9047\u4e0a\u4e86\u4e09\u516c\u4e3b\u3001\u738b\u5f64\u3001\u6c5f\u5343\u91cc\u3001\u5c0f\u71d5\u5b50\u7b49\u4eba\uff0c\u6839\u672c\u65e0\u6cd5\u62db \u67b6\uff0c\u4e00\u4e2a\u4e2a\u88ab\u6740\u5f97\u6709\u5982\u5241\u74dc\u5207\u83dc\u4e00\u822c\uff0c\u8f6c\u77ac\u95f4\u4fbf\u5df2\u5c38\u6a2a\u904d\u5730\u8840\u6d41\u6210\u6e20\u3002 \u3000\u3000\u504f\u504f\u9b54\u5f92\u4eec\u5374\u53c8\u4e2a\u4e2a\u608d\u4e0d\u754f\u6b7b\uff0c\u660e\u77e5\u975e\u6b7b\u4e0d\u53ef\uff0c\u8fd8\u662f\u4e00\u6ce2\u4e00\u6ce2\u7684\u6d8c\u5411\u524d\u6765\u3002 \u3000\u3000\u5927\u7ea6\u987f\u996d\u5de5\u592b\u4e4b\u540e\uff0c\u9b54\u5f92\u4eec\u5df2\u6b7b\u53bb\u5927\u534a\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u51b3\u5b9a\u4e0d\u8ba9\u5bf9\u65b9\u7559\u4e0b\u6d3b\u53e3\uff0c\u770b\u770b\u8fd9\u8fb9\u5df2\u538b\u529b\u5927\u51cf\uff0c\u7acb\u523b\u7387\u9886\u60dc\u6625\uff0c\u601c\u82b1\u4ee5\u53ca\u5c0f\u9053\u58eb \u767d\u7fbd\u8d76\u5f80\u5206\u575b\u51fa\u5165\u53e3\uff0c\u5c06\u5835\u4f4f\u51fa\u5165\u53e3\u7684\u9b54\u5f92\uff0c\u4e0d\u6d88\u534a\u76cf\u8336\u5149\u666f\u4fbf\u5df2\u5168\u6570\u6740\u5149\uff0c\u7136\u540e\u518d\u8fd4\u56de\u7ee7 \u7eed\u5927\u5f00\u6740\u6212\u3002 \u3000\u3000\u8fd9\u65f6\u9b54\u5f92\u4eec\u5df2\u5269\u4e0b\u4e0d\u8db3\u4e8c\u5341\u4eba\uff0c\u5c0f\u71d5\u5b50\u4e5f\u76f4\u63a5\u548c\u9ec4\u9f99\u4ea4\u4e0a\u4e86\u624b\u3002 \u3000\u3000\u5341\u51e0\u56de\u5408\u8fc7\u53bb\uff0c\u9ec4\u9f99\u5df2\u96be\u4ee5\u62db\u67b6\uff0c\u5fc3\u614c\u603b\u4e71\u4e4b\u4e0b\uff0c\u53ea\u597d\u8f6c\u8eab\u5411\u5206\u575b\u7684\u77f3\u5c4b\u5954\u53bb\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u5c82\u80fd\u5bb9\u4ed6\u9003\u8131\uff1f\u4ed6\u9065\u7a7a\u4e00\u6307\uff0c\u4fbf\u5c06\u9ec4\u9f99\u70b9\u5012\u5728\u5730\u3002 \u3000\u3000\u5f53\u5c0f\u71d5\u5b50\u518d\u56de\u8eab\u65f6\uff0c\u9b54\u5f92\u4eec\u5df2\u5168\u90e8\u5c31\u6b7c\uff0c\u679c\u7136\u662f\u4e00\u7f51\u6253\u5c3d\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u738b\u5f64\u4e0b\u4ee4\uff0c\u6240\u6709\u540c\u6765\u7684\u4eba\u5c55\u5f00\u9010\u5c4b\u641c\u7d22\u3002\u9664\u9a6c\u5f69\u4e91\u3001\u9f99\u5728\u5929\u4ee5\u53ca\u6842\u98d8\u9999\u7b49\u4e09\u5996\u5973 \u5916\uff0c\u8981\u4e00\u5f8b\u683c\u6740\uff0c\u7edd\u4e0d\u7559\u4e0b\u534a\u4e2a\u6d3b\u53e3\u3002 \u3000\u3000\u53c8\u662f\u987f\u996d\u8fc7\u53bb\uff0c\u5728\u77f3\u5c4b\u5185\u672a\u73b0\u8eab\u7684\u51e0\u5341\u540d\u9b54\u5f92\u4e5f\u88ab\u5168\u90e8\u6740\u5149\u3002 \u3000\u3000\u8fd9\u4e00\u6218\uff0c\u540d\u526f\u5176\u5b9e\u7684\u8840\u6d17\u9b54\u7a9f\u3002 \u3000\u3000\u6700\u4ee4\u4f17\u4eba\u5174\u594b\u7684\u662f\u6842\u98d8\u9999\u7b49\u4e09\u540d\u5973\u5996\u4e5f\u88ab\u902e\u4f4f\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u547d\u738b\u5f64\u5e26\u7740\u97e9\u6d9b\u3001\u71d5\u98de\u62bc\u7740\u6842\u98d8\u9999\u4e09\u4eba\u5148\u56de\u6c11\u5bb6\uff0c\u7136\u540e\u5c31\u5728\u4e00\u95f4\u77f3\u5c4b\u5185\u5c06\u9ec4\u9f99\u63d0 \u6765\u5ba1\u8baf\u3002 \u3000\u3000\u8d1f\u8d23\u5ba1\u8baf\u7684\u662f\u6c5f\u5343\u91cc\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u5c06\u9ec4\u9f99\u63d0\u8fdb\u77f3\u5c4b\uff0c\u7136\u540e\u89e3\u5f00\u4ed6\u7684\u7a74\u9053\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u3001\u60dc\u6625\u3001\u601c\u82b1\u3001\u8001\u5996\u5a46\u90fd\u5b88\u5728\u65c1\u8fb9\u3002 \u3000\u3000\u9ec4\u9f99\u5bf9\u9762\u524d\u4f17\u4eba\u9664\u60dc\u6625\u5916\uff0c\u5176\u4f59\u7684\u4e00\u4e2a\u4e5f\u4e0d\u8ba4\u8bc6\uff0c\u5f53\u4ed6\u5f00\u7a74\u9192\u6765\u540e\uff0c\u53ea\u662f\u5446\u5446\u7684\u4e0d\u53d1\u4e00 \u8bed\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u4e0d\u52a8\u58f0\u8272\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u8fd9\u5206\u575b\u73b0\u53ea\u5269\u4e0b\u4f60\u4e00\u4e2a\u4eba\u4e86\uff0c\u4f60\u8be5\u5fc3\u91cc\u6709\u6570\u5427\uff1f\u201d \u3000\u3000\u9ec4\u9f99\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u8981\u6740\u4fbf\u6740\uff0c\u7528\u4e0d\u7740\u591a\u8bf4\u5e9f\u8bdd\uff01\u201d \u3000\u3000\u201c\u4f60\u53ea\u8981\u80af\u8bf4\u5b9e\u8bdd\uff0c\u4e5f\u8bb8\u4f1a\u9976\u4f60\u4e0d\u6b7b\u3002\u9a6c\u5f69\u4e91\u548c\u9f99\u5728\u5929\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e0d\u77e5\u9053\uff01\u201d \u3000\u3000\u201c\u771f\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u4e0d\u77e5\u9053\u5c31\u662f\u4e0d\u77e5\u9053\uff0c\u8001\u5c0f\u5b50\uff01\u4f11\u60f3\u5728\u672c\u575b\u4e3b\u53e3\u4e2d\u5957\u51fa\u534a\u53e5\u8bdd\u6765\uff01\u201d \u3000\u3000\u6c5f\u5343\u91cc\u671b\u671b\u4e09\u516c\u4e3b\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u9053\uff1a\u201c\u65e2\u7136\u4e0d\u80af\u8bf4\u5b9e\u8bdd\uff0c\u90a3\u5c31\u6210\u5168\u4ed6\u5427\uff01\u201d \u3000\u3000\u6c5f\u5343\u91cc\u626c\u624b\u4e00\u638c\uff0c\u5411\u9ec4\u9f99\u524d\u80f8\u5288\u53bb\u3002 \u3000\u3000\u9ec4\u9f99\u55b7\u51fa\u4e00\u53e3\u9c9c\u8840\uff0c\u4ef0\u9762\u5012\u5730\u6bd9\u547d\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u5429\u5490\u9053\uff1a\u201c\u518d\u5230\u5904\u4ed4\u7ec6\u641c\u67e5\u4e00\u904d\uff0c\u7edd\u4e0d\u80fd\u8ba9\u9a6c\u5f69\u4e91\u548c\u9f99\u5728\u5929\u53d8\u6210\u6f0f\u7f51\u4e4b\u9c7c\u3002\u8fd8 \u6709\uff0c\u60dc\u6625\u5230\u9ec4\u9f99\u4f4f\u5904\u4e5f\u4ed4\u7ec6\u641c\u67e5\u4e00\u4e0b\uff0c\u770b\u80fd\u4e0d\u80fd\u627e\u5230\u89e3\u836f\u3002\u201d \u3000\u3000\u4e00\u4e2a\u65f6\u8fb0\u4e4b\u540e\uff0c\u6240\u6709\u7684\u4eba\u90fd\u5df2\u56de\u6765\uff0c\u5e76\u672a\u53d1\u73b0\u4e00\u540d\u6d3b\u53e3\u3002 \u3000\u3000\u81f3\u4e8e\u60dc\u6625\uff0c\u4e5f\u672a\u80fd\u5728\u9ec4\u9f99\u5ba4\u5185\u627e\u5230\u89e3\u836f\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u53ea\u597d\u5429\u5490\u5927\u5bb6\u4e00\u8d77\u8fd4\u56de\u6240\u4f4f\u4e4b\u5904\u3002 \u3000\u3000\u6842\u98d8\u9999\u3001\u77f3\u69b4\u3001\u51e4\u4ed9\u4e09\u540d\u5996\u5973\u88ab\u738b\u5f64\u5355\u72ec\u7981\u95ed\u5728\u4e00\u95f4\u7a7a\u5c4b\u91cc\u3002 \u3000\u3000\u738b\u5f64\u66fe\u5bf9\u4e09\u5973\u505a\u8fc7\u521d\u6b65\u5ba1\u8baf\uff0c\u5f97\u77e5\u6842\u98d8\u9999\u5728\u9b54\u6559\u5c0f\u897f\u5929\u96f7\u97f3\u5bfa\u5730\u4f4d\u4e0d\u4f4e\uff0c\u662f\u4e00\u540d\u9999\u4e3b\u8eab \u4efd\u3002 \u3000\u3000\u81f3\u4e8e\u5728\u201c\u8fce\u6625\u9601\u201d\u5316\u540d\u77f3\u69b4\u548c\u51e4\u4ed9\u7684\u4e24\u540d\u5996\u5973\u5219\u662f\u6842\u98d8\u9999\u7684\u5fc3\u8179\u4f8d\u5a62\uff0c\u5973\u4e3b\u4eba\u548c\u5fc3\u8179\u4f8d \u5a62\u4e00\u540c\u5230\u5993\u9662\u505a\u5993\u5973\uff0c\u8fd9\u5012\u662f\u7f55\u89c1\u7f55\u95fb\u7684\u4e8b\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u56de\u6765\u540e\uff0c\u5148\u8981\u5c0f\u71d5\u5b50\u548c\u8001\u5996\u5a46\u518d\u5bf9\u4e09\u540d\u5996\u5973\u590d\u5ba1\u4e00\u904d\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u628a\u8001\u5996\u5a46\u62db\u547c\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u53f2\u8001\uff0c\u73b0\u5728\u8be5\u54b1\u4eec\u4e24\u4e2a\u53bb\u770b\u770b\u4f60\u5e08\u7236\u4e86\u3002\u201d \u3000\u3000\u8c01\u77e5\u8001\u5996\u5a46\u7adf\u7136\u5413\u5f97\u53d1\u6296\uff0c\u6253\u4e86\u4e2a\u54c6\u55e6\u9053\uff1a\u201c\u8001\u8eab\u6211\u2026\u2026\u6211\u4e0d\u6562\u53bb\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u5fcd\u4e0d\u4f4f\u7b11\u9053\uff1a\u201c\u771f\u5947\u602a\uff0c\u4f60\u7adf\u6015\u5979\u6015\u6210\u8fd9\u79cd\u6837\u5b50\uff0c\u5979\u73b0\u5728\u5df2\u7ecf\u505a\u4e86\u9636\u4e0b\u56da\uff0c\u751f\u6b7b \u5927\u6743\u64cd\u5728\u54b1\u4eec\u624b\u91cc\uff0c\u8fd8\u6709\u4ec0\u4e48\u53ef\u6015\u7684\uff1f\u201d \u3000\u3000\u201c\u53ef\u662f\u5979\u2026\u2026\u5979\u662f\u8001\u8eab\u7684\u5e08\u7236\uff01\u201d \u3000\u3000\u201c\u5979\u4e0d\u8fc7\u6559\u4e86\u4f60\u4e00\u70b9\u866b\u672f\uff0c\u7b97\u4ec0\u4e48\u5e08\u7236\uff1f\u90a3\u6709\u5e08\u7236\u6bd4\u5f92\u5f1f\u5e74\u7eaa\u8f7b\u7684\uff0c\u82e5\u8bba\u5e74\u7eaa\uff0c\u4f60\u505a\u5979 \u7684\u7956\u6bcd\u8db3\u8db3\u6709\u4f59\u4e86\u3002\u201d \u3000\u3000\u8001\u5996\u5a46\u7ec8\u4e8e\u80c6\u6c14\u6e10\u58ee\uff0c\u6296\u4e86\u6296\u80a9\u8180\u9053\uff1a\u201c\u597d\u5427\uff01\u6211\u8ddf\u4f60\u53bb\uff0c\u4e0d\u8fc7\u6709\u4ec0\u4e48\u8bdd\u95ee\u5979\uff0c\u4e00\u5207\u4ee5 \u4f60\u4e3a\u4e3b\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u968f\u5373\u548c\u8001\u5996\u5a46\u6765\u5230\u90a3\u4e2a\u7a7a\u5c4b\u3002 \u3000\u3000\u53ea\u89c1\u6842\u98d8\u9999\u3001\u77f3\u69b4\u3001\u51e4\u4ed9\u90fd\u5750\u5728\u5730\u4e0a\uff0c\u60f3\u5fc5\u5df2\u88ab\u70b9\u4e86\u9ebb\u7a74\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u8f7b\u54b3\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u6842\u82b1\uff0c\u4f60\u8be5\u8fd8\u8ba4\u8bc6\u5728\u4e0b\u5427\uff1f\u201d \u3000\u3000\u6842\u98d8\u9999\u8fd8\u6ca1\u6709\u6765\u5f97\u53ca\u5f00\u53e3\uff0c\u4fbf\u89c1\u8001\u5996\u5a46\u8eac\u8eab\u65bd\u4e86\u4e00\u793c\uff0c\u9053\uff1a\u201c\u5e08\u7236\u597d\uff0c\u5f1f\u5b50\u53f2\u5999\u79cb\u62dc\u89c1 \u4f60\u8001\u4eba\u5bb6\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u771f\u6068\u4e0d\u5f97\u6253\u8001\u5996\u5a46\u4e00\u8033\u5149\uff0c\u4f46\u4ed6\u8fd8\u662f\u6781\u529b\u5fcd\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u6842\u98d8\u9999\u89c6\u7ebf\u76ef\u7740\u8001\u5996\u5a46\uff0c\u53f1\u9053\uff1a\u201c\u53f2\u5999\u79cb\uff0c\u4e8f\u4f60\u8fd8\u662f\u897f\u5929\u5723\u6559\u4e00\u540d\u8d44\u6df1\u5f1f\u4e8e\uff0c\u7adf\u7136\u505a\u4e86 \u6295\u964d\u53d8\u8282\u7684\u4e8b\uff0c\u8fd8\u6709\u4ec0\u4e48\u8138\u9762\u6765\u89c1\u6211\uff0c\u5e72\u8106\u53bb\u6b7b\u597d\u5566\uff01\u201d \u3000\u3000\u8001\u5996\u5a46\u5168\u8eab\u53d1\u6296\u7684\u9053\uff1a\u201c\u5e08\u7236\uff0c\u5f1f\u5b50\u662f\u5f03\u6697\u6295\u660e\uff0c\u4e0d\u662f\u6295\u964d\u53d8\u8282\u3002\u201d \u3000\u3000\u6842\u98d8\u9999\u9762\u8272\u5982\u7f69\u5bd2\u971c\uff0c\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u80e1\u8bf4\uff0c\u8fd8\u4e0d\u7ed9\u6211\u8dea\u4e0b\uff01\u201d \u3000\u3000\u53ea\u542c\u201c\u6251\u901a\u201d\u4e00\u58f0\uff0c\u8001\u5996\u5a46\u5df2\u53cc\u819d\u8dea\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u7b80\u76f4\u706b\u5192\u4e09\u4e08\uff0c\u559d\u9053\uff1a\u201c\u8001\u5996\u5a46\uff0c\u60f3\u4e0d\u5230\u4f60\u7adf\u8fd9\u6837\u4e0b\u8d31\uff0c\u4e8f\u4f60\u6d3b\u4e86\u8fd9\u4e48\u4e00\u5927\u628a\u5e74 \u7eaa\uff0c\u8ba9\u6211\u8bf4\u4ec0\u4e48\u597d\u5462\uff1f\u201d \u3000\u3000\u4ed6\u5634\u91cc\u8fd9\u4e48\u8bf4\uff0c\u5185\u5fc3\u5374\u5df2\u6697\u751f\u8b66\u60d5\u3002 \u3000\u3000\u770b\u6842\u98d8\u9999\u7684\u8868\u73b0\uff0c\u663e\u7136\u662f\u5b81\u6b7b\u4e0d\u5c48\uff0c\u4e3a\u4e86\u8981\u5979\u4e3a\u4e09\u516c\u4e3b\u9664\u866b\uff0c\u7edd\u4e0d\u53ef\u628a\u6c14\u6c1b\u5f04\u5f97\u592a\u7d27 \u5f20\uff0c\u5426\u5219\u4e07\u4e00\u5979\u5bfb\u4e86\u81ea\u5c3d\uff0c\u5c82\u4e0d\u8bef\u4e86\u5927\u4e8b\u3002 \u3000\u3000\u4ed6\u53ea\u597d\u7f13\u4e0b\u58f0\u97f3\u9053\uff1a\u201c\u6842\u82b1\uff0c\u4f60\u8fd8\u6ca1\u7b54\u590d\u6211\u7684\u8bdd\uff01\u201d \u3000\u3000\u6842\u98d8\u9999\u51b7\u7b11\u51e0\u58f0\u9053\uff1a\u201c\u6211\u5f53\u7136\u8ba4\u8bc6\u4f60\uff0c\u90a3\u665a\u8bb2\u597d\u4e86\u4f60\u8981\u5bbf\u5728\u6211\u623f\u91cc\uff0c\u4e3a\u4ec0\u4e48\u540e\u6765\u53d8\u4e86 \u5366\uff1f\u201d \u3000\u3000\u201c\u4f60\u95ee\u7684\u7b49\u4e8e\u5e9f\u8bdd\uff0c\u96be\u9053\u5728\u4e0b\u4f1a\u5fc3\u7518\u60c5\u613f\u7684\u8ba9\u4f60\u4e0b\u866b\u5417\uff1f\u201d \u3000\u3000\u6842\u98d8\u9999\u89c6\u7ebf\u518d\u8f6c\u5411\u8001\u5996\u5a46\uff0c\u79c0\u7709\u5e26\u715e\u7684\u53f1\u9053\uff1a\u201c\u53f2\u5999\u79cb\uff0c\u4f60\u8fd9\u4e2a\u6df7\u5e10\u4e1c\u897f\uff0c\u4e8b\u60c5\u5168\u574f\u5728 \u4f60\u4e00\u4eba\u8eab\u4e0a\uff01\u201d \u3000\u3000\u53f2\u5999\u79cb\u4f9d\u7136\u8dea\u5728\u5730\u4e0a\uff0c\u4f4e\u7740\u5934\u9053\uff1a\u201c\u5f1f\u5b50\u505a\u9519\u4e86\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u90a3\u665a\u82e5\u4e0d\u662f\u4f60\u6307\u70b9\u4ed6\uff0c\u4ed6\u600e\u4f1a\u77e5\u9053\u6211\u7684\u771f\u6b63\u8eab\u4efd\uff1f\u201d \u3000\u3000\u201c\u5f1f\u5b50\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u4ee5\u4e3a\u6211\u6ca1\u770b\u5230\u4f60\uff1f\u6211\u65e9\u5c31\u770b\u5230\u4f60\u626e\u6210\u4e00\u4e2a\u8001\u5934\u5b50\uff0c\u548c\u4ed6\u8d70\u5728\u4e00\u8d77\uff0c\u800c\u4e14\u8fd8\u5411\u6211\u623f\u91cc \u6307\u6307\u70b9\u70b9\u3002\u201d \u3000\u3000\u8001\u5996\u5a46\u55eb\u5685\u7740\u9053\uff1a\u201c\u5f1f\u4e86\u65e2\u7136\u5df2\u5f03\u6697\u6295\u660e\uff0c\u5c31\u4e0d\u5f97\u4e0d\u5e2e\u4ed6\u4eec\u505a\u4e9b\u4e8b\u60c5\u3002\u201d \u3000\u3000\u6842\u98d8\u9999\u53cc\u76ee\u5706\u7741\uff0c\u559d\u9053\uff1a\u201c\u597d\u554a\uff01\u4f60\u60f3\u627e\u6b7b\uff1f\u6211\u73b0\u5728\u5c31\u8ba9\u4f60\u6b7b\uff01\u201d \u3000\u3000\u5979\u8bf4\u7740\uff0c\u7528\u529b\u60f3\u5c06\u53f3\u638c\u4e3e\u8d77\uff0c\u53ef\u60dc\u56e0\u5df2\u5168\u8eab\u762b\u75ea\uff0c\u6839\u672c\u65e0\u6cd5\u4e3e\u8d77\u624b\u6765\u3002 \u3000\u3000\u5c82\u77e5\u8fd9\u4e00\u6765\u6b63\u597d\u58ee\u4e86\u8001\u5996\u5a46\u7684\u80c6\u3002 \u3000\u3000\u53ea\u89c1\u5979\u970d\u5730\u7ad9\u8d77\u8eab\u6765\uff0c\u51b7\u54fc\u4e00\u58f0\u9053\uff1a\u201c\u6842\u98d8\u9999\uff0c\u8001\u8eab\u4ece\u73b0\u5728\u8d77\uff0c\u5df2\u6ca1\u6709\u4f60\u8fd9\u4e2a\u5e08\u7236\u4e86\uff0c \u4f60\u7ed9\u8001\u8eab\u505a\u5b59\u5973\u8fd8\u5dee\u592a\u591a\u5462\uff01\u8001\u8eab\u73b0\u5728\u4e0d\u6253\u4f60\u5c41\u80a1\uff0c\u5c31\u5df2\u7ecf\u7b97\u5bf9\u4f60\u5ba2\u6c14\u4e86\uff0c\u4f60\u60f3\u8981\u8001\u8eab\u6b7b\uff0c \u90a3\u662f\u5984\u60f3\uff01\u201d \u3000\u3000\u6842\u98d8\u9999\u987f\u65f6\u6c14\u5f97\u4e24\u773c\u53d1\u9ed1\uff0c\u9669\u4e9b\u660f\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8001\u5996\u5a46\u72b9\u81ea\u54fa\u54fa\u8bf4\u9053\uff1a\u201c\u59d3\u6842\u7684\uff0c\u81ed\u4e2b\u5934\uff0c\u522b\u7ed9\u8138\u4e0d\u8981\u8138\uff0c\u4f60\u4ee5\u4e3a\u8001\u8eab\u6015\u4e86\u4f60\u5417\uff1f\u8001\u8eab \u73b0\u5728\u60f3\u6740\u4f60\uff0c\u6bd4\u634f\u6b7b\u4e00\u53ea\u82cd\u8747\u8fd8\u8981\u5bb9\u6613\uff0c\u81ea\u5df1\u4e5f\u4e0d\u6492\u6ce1\u5c3f\u7167\u7167\uff0c\u4f60\u7b97\u4e2a\u4ec0\u4e48\u4e1c\u897f\uff01\u201d \u3000\u3000\u6842\u98d8\u9999\u5f53\u771f\u5df2\u88ab\u6c14\u7ed3\uff0c\u90a3\u91cc\u8fd8\u80fd\u8bf4\u51fa\u8bdd\u6765\uff1f\u5c82\u77e5\u8001\u5996\u5a46\u7adf\u8d8a\u6765\u8d8a\u706b\u5927\uff0c\u8d70\u8fc7\u53bb\u5de6\u53f3\u5f00 \u5f13\uff0c\u4e00\u9635\u50fb\u50fb\u556a\u556a\uff0c\u628a\u6842\u98d8\u9999\u6253\u5f97\u7ffb\u5012\u5730\u4e0a\uff0c\u534a\u664c\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u8fd9\u65f6\u5c0f\u71d5\u5b50\u53cd\u800c\u4e0d\u5f97\u4e0d\u8fde\u5fd9\u963b\u6b62\uff0c\u56de\u8eab\u9053\uff1a\u53f2\u8001\uff0c\u8be5\u6d88\u6c14\u4e86\uff0c\u7528\u4e0d\u7740\u518d\u548c\u5979\u8ba1\u8f83\u3002\u201c \u3000\u3000\u8001\u5996\u5a46\u51b7\u54fc\u9053\uff1a\u201c\u82e5\u4e0d\u8fd9\u6837\uff0c\u8fd9\u81ed\u4e2b\u5934\u600e\u4f1a\u77e5\u9053\u8001\u8eab\u7684\u5389\u5bb3\uff01\u201d \u3000\u3000\u201c\u5979\u73b0\u5728\u65e9\u5c31\u77e5\u9053\u4f60\u5389\u5bb3\u4e86\uff0c\u8fde\u6211\u4e5f\u6015\u4f60\uff0c\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u201c\u8001\u8eab\u4e00\u89c1\u5979\u5c31\u6709\u6c14\uff0c\u5c0f\u5144\u5f1f\uff01\u73b0\u5728\u5c31\u7531\u4f60\u95ee\u5979\u8bdd\u5427\uff01\u8001\u8eab\u8d70\u4e86\u3002\u201d \u3000\u3000\u8001\u5996\u5a46\u4e00\u8f6c\u8eab\u4fbf\u51fa\u95e8\u800c\u53bb\uff0c\u5634\u91cc\u4ecd\u5728\u561f\u56b7\u7740\u9a82\u4e2a\u4e0d\u4f11\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u4f38\u624b\u628a\u6842\u98d8\u9999\u62c9\u8d77\uff0c\u8bed\u6c14\u5e73\u9759\u7684\u9053\uff1a\u201c\u73b0\u5728\u8be5\u4f60\u6d88\u6d88\u6c14\u4e86\uff0c\u522b\u7406\u5979\uff01\u201d \u3000\u3000\u6842\u98d8\u9999\u53cc\u988a\u7ea2\u80bf\uff0c\u6c14\u5598\u5401\u5401\u7684\u9053\uff1a\u201c\u4f60\u662f\u4eb2\u773c\u770b\u5230\u7684\uff0c\u8fd9\u8fd8\u50cf\u8bdd\u5417\uff1f\u201d \u3000\u3000\u201c\u662f\u4e0d\u662f\u50cf\u8bdd\uff0c\u7b97\u4f60\u5f53\u521d\u770b\u8d70\u4e86\u773c\uff0c\u6559\u51fa\u8fd9\u4e48\u4e00\u4e2a\u6b3a\u5e08\u706d\u7956\u7684\u5f92\u5f1f\u6765\uff0c\u4e0b\u6b21\u518d\u8c03\u4e2a\u597d\u7684 \u5c31\u662f\u4e86\u3002\u201d \u3000\u3000\u522b\u770b\u8fd9\u51e0\u53e5\u4f3c\u771f\u4e0d\u771f\u3001\u4f3c\u5047\u4e0d\u5047\u7684\u8bdd\uff0c\u7adf\u7136\u4f7f\u5f97\u6842\u98d8\u9999\u5bf9\u5c0f\u71d5\u5b50\u4ea7\u751f\u4e86\u597d\u611f\u3002 \u3000\u3000\u5979\u629a\u6478\u7740\u53cc\u988a\uff0c\u534a\u664c\u624d\u7f13\u7f13\u4ef0\u8d77\u5934\u6765\u9053\uff1a\u201c\u4f60\u6765\u770b\u6211\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u4e00\u672c\u6b63\u7ecf\u7684\u9053\uff1a\u201c\u8fd9\u65f6\u9664\u4e86\u4f60\u90a3\u4e0d\u8096\u5f1f\u5b50\u53f2\u8001\u5a46\u5b50\u4ee5\u5916\uff0c\u548c\u4f60\u8ba4\u8bc6\u7684\u5c31\u53ea\u6709\u6211 \u4e86\uff01\u201d \u3000\u3000\u201c\u4f60\u9519\u4e86\uff0c\u4f60\u4eec\u4e09\u516c\u4e3b\u6731\u4e91\u548c\u60dc\u6625\u3001\u601c\u82b1\u4e5f\u8ba4\u8bc6\u6211\u3002\u201d \u3000\u3000\u201c\u5979\u4eec\u867d\u7136\u8ba4\u8bc6\u4f60\uff0c\u5374\u548c\u4f60\u8c08\u4e0d\u5230\u6709\u4ea4\u60c5\u3002\u201d \u3000\u3000\u201c\u4f60\u548c\u6211\u53c8\u6709\u4ec0\u4e48\u4ea4\u60c5\uff1f\u201d \u3000\u3000\u201c\u6211\u66fe\u5728\u4f60\u623f\u95f4\u803d\u4e86\u5f88\u4e45\uff0c\u8fde\u4f60\u7684\u5e8a\u90fd\u4e0a\u8fc7\uff0c\u600e\u4e48\u8bf4\u6ca1\u6709\u4ea4\u60c5\uff1f\u201d \u3000\u3000\u201c\u90a3\u65f6\u4f60\u662f\u6211\u7684\u5ba2\u4eba\uff0c\u6211\u5f53\u7136\u8981\u62db\u5f85\u4f60\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c31\u662f\u4ea4\u60c5\uff0c\u5929\u4e0b\u5973\u4eba\u6210\u5343\u4e0a\u4e07\uff0c\u6211\u4e3a\u4ec0\u4e48\u5728\u90a3\u79cd\u5730\u65b9\u53ea\u8ba4\u8bc6\u4f60\u5462\uff1f\u8fd9\u5c31\u662f\u6709\u7f18\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u6709\u7f18\uff0c\u90a3\u5929\u665a\u4e0a\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u6765\uff1f\u201d \u3000\u3000\u201c\u771f\u4eba\u9762\u524d\u4e0d\u8bf4\u5047\u8bdd\uff0c\u6211\u4e0d\u80fd\u4e0d\u9632\u4f60\u5728\u6211\u8eab\u4e0a\u4e0b\u866b\u3002\u201d \u3000\u3000\u6842\u98d8\u9999\u6447\u6447\u5934\u82e6\u7b11\u9053\uff1a\u201c\u71d5\u5c11\u4fa0\uff0c\u4f60\u9519\u4e86\uff0c\u6211\u4e0b\u866b\u662f\u770b\u5ba2\u4e0b\u83dc\u7684\uff0c\u5982\u679c\u4f60\u662f\u771f\u5fc3\u5f85\u6211\uff0c \u6211\u53c8\u4f55\u82e6\u5bb3\u4f60\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd9\u8bdd\u5f53\u771f\uff1f\u201d \u3000\u3000\u201c\u5982\u679c\u4e0d\u76f8\u4fe1\uff0c\u4eca\u591c\u6211\u60c5\u613f\u966a\u4f60\u4e00\u665a\u4e0a\u3002\u201d \u3000\u3000\u201c\u522b\u8bf4\u7b11\u8bdd\u4e86\uff0c\u6211\u73b0\u5728\u5df2\u7ecf\u4e0d\u505a\u90a3\u79cd\u4e8b\u60c5\u4e86\uff0c\u6211\u6709\u4ec0\u4e48\u7406\u7531\u627e\u4f60\u966a\u3002\u201d \u3000\u3000\u201c\u5c31\u8ba9\u6211\u8fd8\u4f60\u90a3\u4e00\u665a\u4e0a\u7684\u503a\uff0c\u4e0d\u597d\u5417\uff1f\u522b\u5fd8\u4e86\uff0c\u6211\u66fe\u767d\u6536\u4e86\u4f60\u5341\u4e24\u94f6\u5b50\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u4ee5\u540e\u518d\u8bf4\u5427\uff01\u201d \u3000\u3000\u201c\u4f60\u8981\u8ba2\u4e2a\u65e5\u671f\uff1f\u201d \u3000\u3000\u201c\u5148\u529e\u5b8c\u6b63\u4e8b\u518d\u8bf4\u3002\u201d \u3000\u3000\u201c\u4f60\u8bf4\u7684\u6b63\u4e8b\u6211\u660e\u767d\u3002\u201d \u3000\u3000\u201c\u8bf4\u8bf4\u770b\uff01\u201d \u3000\u3000\u201c\u4e3a\u4e09\u516c\u4e3b\u6731\u4e91\u9664\u866b\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u4f60\u77e5\u9053\uff0c\u90a3\u6700\u597d\u4e0d\u8fc7\u4e86\u3002\u4e0a\u6b21\u4f60\u90a3\u5f92\u5f1f\u53f2\u5999\u79cb\u4e3a\u4e09\u516c\u4e3b\u9664\u866b\uff0c\u4e00\u76f4\u65e0\u6cd5\u6839\u9664\uff0c\u4f60 \u8fd9\u505a\u5e08\u7236\u7684\uff0c\u5f53\u7136\u8981\u6bd4\u5f92\u5f1f\u9ad8\u660e\u3002\u201d \u3000\u3000\u6842\u98d8\u9999\u6b64\u523b\u663e\u7136\u5df2\u610f\u5fd7\u52a8\u6447\uff0c\u9ed8\u4e86\u4e00\u9ed8\u9053\uff1a\u201c\u6211\u5982\u679c\u628a\u4f60\u4eec\u4e09\u516c\u4e3b\u7684\u866b\u6bd2\u6cbb\u597d\uff0c\u5979\u62ff\u4ec0 \u4e48\u8c22\u6211\uff1f\u4f60\u53c8\u62ff\u4ec0\u4e48\u8c22\u6211\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6574\u4e86\u6574\u8138\u8272\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u66ff\u4e09\u516c\u4e3b\u6cbb\u866b\uff0c\u5fc5\u5b9a\u6027\u547d\u96be\u4fdd \u3000\u3000\u2026\u2026\u201c \u3000\u3000\u201c\u6211\u82e5\u66ff\u5979\u6cbb\u866b\u5462\uff1f\u201d \u3000\u3000\u201c\u5979\u4e00\u5b9a\u4f1a\u9976\u4f60\u4e0d\u6b7b\uff0c\u4f60\u8fd8\u5e74\u8f7b\uff0c\u5c06\u6765\u8fd8\u6709\u7f8e\u597d\u7684\u65e5\u5b50\u7b49\u7740\u4f60\uff0c\u603b\u4e0d\u4f1a\u4e0d\u5e0c\u671b\u6d3b\u4e0b\u53bb \u5427\uff01\u201d \u3000\u3000\u201c\u4f60\u80fd\u66ff\u6211\u4fdd\u8bc1\u5417\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e8b\u5305\u5728\u6211\u8eab\u4e0a\uff0c\u6211\u613f\u610f\u4ee5\u6027\u547d\u62c5\u4fdd\u3002\u201d \u3000\u3000\u6842\u98d8\u9999\u8f6c\u5934\u671b\u671b\u77f3\u69b4\u548c\u51e4\u4ed9\uff0c\u5374\u6ca1\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u77f3\u69b4\u5fd9\u9053\uff1a\u201c\u9999\u4e3b\u5c31\u7b54\u5e94\u4e86\u5427\uff01\u71d5\u5c11\u4fa0\u8bf4\u5f97\u5bf9\uff0c\u54b1\u4eec\u90fd\u8fd8\u5e74\u8f7b\uff0c\u65e2\u7136\u6709\u6d3b\u8def\uff0c\u4e3a\u4ec0\u4e48\u975e \u6b7b\u4e0d\u53ef\u5462\uff1f\u201d \u3000\u3000\u6842\u98d8\u9999\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u518d\u671b\u5411\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u597d\uff0c\u6211\u7b54\u5e94\uff01\u201d \u3000\u3000\u6842\u98d8\u9999\u5f00\u59cb\u4e3a\u4e09\u516c\u4e3b\u6cbb\u866b\u4e86\u3002 \u3000\u3000\u4e3a\u9632\u5979\u52a8\u522b\u7684\u624b\u811a\uff0c\u8001\u5996\u5a46\u3001\u60dc\u6625\u3001\u601c\u82b1\u90fd\u7d27\u7d27\u5b88\u5728\u65c1\u8fb9\u76d1\u89c6\u3002 \u3000\u3000\u636e\u6842\u98d8\u9999\u8bf4\uff0c\u8981\u60f3\u628a\u4e09\u516c\u4e3b\u4f53\u5185\u7684\u866b\u6bd2\u5f7b\u5e95\u9664\u5c3d\uff0c\u81f3\u5c11\u9700\u4e09\u5929\u65f6\u95f4\u3002 \u3000\u3000\u56e0\u4e4b\uff0c\u4e09\u516c\u4e3b\u51b3\u5b9a\u7ee7\u7eed\u7559\u5728\u7ec8\u5357\u5c71\u8fd9\u5904\u6c11\u5bb6\u4e09\u5929\u3002 \u3000\u3000\u53e6\u5916\u7559\u4e0b\u7684\u5219\u6709\u6c5f\u5343\u91cc\u3001\u5c0f\u71d5\u5b50\u3001\u738b\u91cd\u5c71\u3001\u4e03\u5de7\u50e7\u3001\u767d\u7fbd\u3001\u8001\u5996\u5a46\u7b49\u3002 \u3000\u3000\u81f3\u4e8e\u738b\u5f64\uff0c\u5219\u5e26\u7740\u97e9\u6d9b\u3001\u8d75\u4fdd\u3001\u9648\u5b8f\u7b49\u5927\u5185\u9ad8\u624b\u53c8\u8fd4\u56de\u6f7c\u5173\u3002 \u3000\u3000\u8fd9\u662f\u56e0\u4e3a\u4ed6\u4eec\u5fc5\u987b\u518d\u5bf9\u201c\u6843\u6797\u5c45\u201d\u4e0b\u624b\uff0c\u4ee5\u4fbf\u6349\u4f4f\u9a6c\u5be1\u5987\u548c\u9f99\u5728\u5929\u3002\u636e\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u7b49 \u4eba\u5224\u65ad\uff0c\u9a6c\u5be1\u5987\u548c\u9f99\u5728\u5929\u5fc5\u5b9a\u5df2\u56de\u5230\u201c\u6843\u6797\u5c45\u201d\u3002 \u3000\u3000\u4e0d\u8fc7\u4e09\u516c\u4e3b\u6709\u4ea4\u4ee3\uff0c\u8981\u738b\u5f64\u548c\u4ed6\u7684\u624b\u4e0b\u53ea\u8d1f\u8d23\u76d1\u89c6\u201c\u6843\u6797\u5c45\u201d\u7684\u52a8\u9759\uff0c\u7b49\u5230\u7ec8\u5357\u5c71\u8fd9\u8fb9 \u7684\u4eba\u9a6c\u4e5f\u5230\u8fc7\u6f7c\u5173\u540e\uff0c\u518d\u91c7\u53d6\u884c\u52a8\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u95f2\u6765\u65e0\u4e8b\uff0c\u51b3\u5b9a\u5e26\u5c0f\u71d5\u5b50\u518d\u5230\u9752\u4e91\u5d16\u5927\u8352\u6d1e\u4e00\u8d9f\u3002 \u3000\u3000\u8fd9\u6b21\u867d\u7136\u6d1e\u95e8\u5df2\u5f00\uff0c\u4f46\u4ed6\u4fe9\u5374\u4e0d\u6562\u901e\u81ea\u95ef\u8fdb\uff0c\u53ea\u80fd\u5148\u5230\u8349\u68da\u627e\u5305\u5fe0\u592b\u5987\u3002 \u3000\u3000\u636e\u5305\u5fe0\u8bf4\uff0c\u82df\u6167\u6708\u6b64\u523b\u4ecd\u5728\u5927\u8352\u6d1e\u4e2d\uff0c\u81f3\u4e8e\u5929\u96f7\u8001\u4eba\u5219\u5df2\u4e8e\u6628\u591c\u9192\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u8fd9\u6d88\u606f\u7b80\u76f4\u8ba9\u6c5f\u5343\u91cc\u548c\u5c0f\u71d5\u5b50\u5174\u594b\u5230\u4e86\u6781\u9876\uff0c\u201c\u8fd4\u5143\u5927\u8fd8\u4e39\u201d \u3000\u3000\u679c\u7136\u771f\u6709\u8d77\u6b7b\u56de\u751f\u4e4b\u6548\uff0c\u540c\u65f6\u5bf9\u82df\u6167\u6708\u7684\u611f\u6fc0\u4e5f\u65e0\u4ee5\u540d\u72b6\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u8feb\u4e0d\u53ca\u5f85\u7684\u9053\uff1a\u201c\u6211\u548c\u5c0f\u71d5\u5b50\u662f\u5426\u53ef\u4ee5\u8fdb\u53bb\u770b\u770b\uff1f\u201d \u3000\u3000\u5305\u5fe0\u6447\u5934\u9053\uff1a\u201c\u82df\u5973\u4fa0\u6709\u5429\u5490\uff0c\u6ca1\u6709\u5979\u7684\u8bb8\u53ef\uff0c\u522b\u4eba\u4e0d\u80fd\u5411\u91cc\u4e71\u95ef\uff0c\u6c5f\u5927\u4fa0\u4e0d\u5fc5\u6025\uff0c\u5979 \u9a6c\u4e0a\u5c31\u4f1a\u56de\u6765\u3002\u201d \u3000\u3000\u679c\u7136\uff0c\u5305\u5fe0\u521a\u5012\u4e86\u8336\uff0c\u4e00\u9053\u98de\u8679\u82df\u6167\u6708\u4fbf\u8fdb\u4e86\u8349\u68da\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u548c\u5c0f\u71d5\u5b50\u8fde\u5fd9\u8d77\u7acb\u76f8\u8fce\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u62b1\u62f3\u6df1\u6df1\u4e00\u793c\u9053\uff1a\u201c\u82df\u5973\u4fa0\u5999\u624b\u56de\u6625\uff0c\u7adf\u7136\u771f\u80fd\u8ba9\u6211\u90a3\u4e49\u5144\u82cf\u9192\u8fc7\u6765\uff0c\u73b0\u5728\u60c5\u5f62 \u600e\u4e48\u6837\u4e86\uff1f\u201d \u3000\u3000\u82df\u6167\u6708\u63e9\u8bd5\u4e86\u4e00\u4e0b\u989d\u89d2\u7684\u6c57\u6c34\u9053\uff1a\u201c\u53ef\u662f\u5305\u8001\u4eba\u5bb6\u544a\u8bc9\u6c5f\u5927\u4fa0\u7684\uff1f\u4ed6\u9192\u6765\u540e\uff0c\u795e\u5fd7\u4e5f\u5b8c \u5168\u6e05\u9192\uff0c\u6b64\u523b\u5df2\u80fd\u5f00\u53e3\u8bf4\u8bdd\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u8d8a\u53d1\u559c\u51fa\u671b\u5916\uff0c\u9053\uff1a\u201c\u4ed6\u7684\u773c\u775b\u662f\u5426\u5df2\u7ecf\u590d\u660e\uff1f\u201d \u3000\u3000\u82df\u6167\u6708\u7565\u4e00\u8fdf\u7591\u9053\uff1a\u201c\u6211\u73b0\u5728\u5df2\u7ecf\u5f00\u59cb\u4e3a\u4ed6\u8fdb\u884c\u590d\u660e\u624b\u672f\uff0c\u9664\u836f\u7269\u5916\uff0c\u53e6\u65bd\u4ee5\u91d1\u9488\u8fc7\u7a74 \u4e4b\u672f\uff0c\u6211\u867d\u7136\u6ca1\u6709\u7edd\u5bf9\u628a\u63e1\uff0c\u81f3\u5c11\u6709\u4e03\u6210\u4ee5\u4e0a\u5e0c\u671b\u3002\u201d \u3000\u3000\u201c\u65f6\u95f4\u4e0a\u5927\u7ea6\u9700\u8981\u591a\u4e45\uff1f\u201d \u3000\u3000\u201c\u6211\u5e0c\u671b\u80fd\u5728\u4e09\u5929\u4e4b\u5185\u5b8c\u6210\u624b\u672f\u3002\u201d \u3000\u3000\u201c\u6211\u548c\u5c0f\u71d5\u5b50\u73b0\u5728\u662f\u5426\u80fd\u8fdb\u53bb\u770b\u770b\uff1f\u201d \u3000\u3000\u201c\u597d\u5427\uff01\u4f60\u4eec\u8f9b\u8f9b\u82e6\u82e6\u6765\u4e00\u8d9f\u4e0d\u5bb9\u6613\uff0c\u5c31\u968f\u6211\u6765\u5427\uff01\u201d \u3000\u3000\u8fdb\u5165\u5927\u8352\u6d1e\uff0c\u6c5f\u5343\u91cc\u548c\u5c0f\u71d5\u5b50\u90fd\u4e0d\u81ea\u7981\u653e\u8f7b\u811a\u6b65\uff0c\u51e0\u4e4e\u8fde\u5927\u6c14\u4e5f\u4e0d\u6562\u51fa\u3002 \u3000\u3000\u8de8\u8fdb\u77f3\u5c4b\uff0c\u5c4b\u5185\u70b9\u7740\u6cb9\u706f\uff0c\u7167\u89c1\u5929\u96f7\u8001\u4eba\u6b63\u8eba\u5728\u5e8a\u4e0a\uff0c\u624b\u811a\u90fd\u5728\u6d3b\u52a8\u3002 \u3000\u3000\u82df\u6167\u6708\u56de\u8eab\u793a\u610f\u4e0d\u8981\u58f0\u5f20\uff0c\u7136\u540e\u6765\u5230\u5e8a\u524d\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5929\u96f7\uff0c\u4f60\u90a3\u4e49\u5f1f\u6c5f\u5927\u4fa0\u548c\u7231\u5f92\u5c0f \u71d5\u5b50\u6765\u770b\u4f60\u4e86\uff01\u201d \u3000\u3000\u5929\u96f7\u8001\u4eba\u987f\u65f6\u8138\u4e0a\u73b0\u51fa\u6fc0\u52a8\u6a21\u6837\uff0c\u4f3c\u4e4e\u60f3\u8981\u652f\u6491\u7740\u5750\u8d77\u6765\u3002 \u3000\u3000\u82df\u6167\u6708\u6025\u6025\u4fef\u8eab\u5c06\u4ed6\u6276\u8d77\u3002 \u3000\u3000\u53ea\u542c\u5929\u96f7\u8001\u4eba\u6296\u52a8\u7740\u58f0\u97f3\u9053\uff1a\u201c\u4ed6\u4eec\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u8fde\u5fd9\u8d8b\u524d\u51e0\u6b65\uff0c\u4fef\u8eab\u63e1\u4f4f\u5929\u96f7\u8001\u4eba\u7684\u624b\uff0c\u58f0\u8c03\u6025\u4fc3\u7684\u9053\uff1a\u201c\u5927\u54e5\uff0c\u5144\u5f1f\u5728\u8fd9 \u91cc\u2026\u2026\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u4e5f\u51e0\u4e4e\u5728\u540c\u4e00\u65f6\u95f4\uff0c\u6025\u6025\u8d8b\u524d\u53eb\u9053\uff1a\u201c\u5e08\u7236\uff0c\u5f92\u513f\u5c0f\u71d5\u6765\u770b\u60a8\u8001\u4eba\u5bb6\u4e86\uff01\u201d \u3000\u3000\u5929\u96f7\u8001\u4eba\u4f38\u51fa\u53e6\u4e00\u53ea\u624b\uff0c\u628a\u5c0f\u71d5\u5b50\u7684\u624b\u7d27\u7d27\u63e1\u4f4f\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u5174\u594b\u5f97\u6d41\u4e0b\u4e86\u773c\u6cea\u9053\uff1a\u201c\u5927\u54e5\uff0c\u60a8\u73b0\u5728\u603b\u8be5\u660e\u767d\u4e86\uff0c\u82df\u5973\u4fa0\u5e76\u6ca1\u6709\u5fd8\u6000\u4e8e\u60a8\uff0c\u4e3a \u4e86\u60a8\uff0c\u8fd9\u4e9b\u5929\u6765\u5979\u4e0d\u5206\u663c\u591c\u4e3a\u60a8\u6062\u590d\u5143\u6c14\u8f9b\u82e6\u2026\u2026\u201d \u3000\u3000\u8fd9\u65f6\u5929\u96f7\u8001\u4eba\u53cc\u76ee\u4e2d\u4e5f\u6d41\u4e0b\u773c\u6cea\uff0c\u70b9\u70b9\u5934\u9053\uff1a\u201c\u5f53\u5e74\u5979\u8bef\u4f1a\u4e86\u6211\uff0c\u6211\u4e5f\u8bef\u4f1a\u4e86\u5979\uff0c\u73b0\u5728 \u5df2\u7528\u4e0d\u7740\u518d\u89e3\u91ca\u4e86\uff0c\u4f46\u613f\u4ee5\u540e\u7684\u65e5\u5b50\u2026\u2026\u201d \u3000\u3000\u5929\u96f7\u8001\u4eba\u518d\u63e1\u7d27\u5c0f\u71d5\u5b50\u7684\u624b\u9053\uff1a\u201c\u4f60\u4e0b\u5c71\u540e\uff0c\u662f\u5426\u4e00\u76f4\u8ddf\u968f\u6c5f\u53d4\u53d4\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u70ed\u6cea\u76c8\u7736\u7684\u9053\uff1a\u201c\u5f1f\u5b50\u6c38\u8fdc\u4e0d\u4f1a\u5fd8\u8bb0\u5e08\u7236\u81ea\u95ed\u6d1e\u95e8\u524d\u6240\u8bad\u8beb\u7684\u90a3\u4e09\u4ef6\u4e8b\uff0c\u8fd9\u534a\u5e74 \u6765\u6211\u4e00\u76f4\u8ddf\u968f\u7740\u6c5f\u53d4\u53d4\u3002\u201d \u3000\u3000\u5929\u96f7\u8001\u4eba\u795e\u8272\u95f4\u73b0\u51fa\u5b89\u6170\u4e4b\u610f\uff0c\u9053\uff1a\u201c\u90a3\u6211\u5c31\u653e\u5fc3\u4e86\u3002\u201d \u3000\u3000\u53c8\u8c08\u4e86\u51e0\u53e5\uff0c\u82df\u6167\u6708\u793a\u610f\u4e24\u4eba\u5fc5\u987b\u79bb\u5f00\u3002 \u3000\u3000\u4e24\u4eba\u56de\u5230\u8349\u68da\u4e0d\u4e45\uff0c\u82df\u6167\u6708\u4e5f\u56de\u6765\u4e86\u3002 \u3000\u3000\u5305\u5fe0\u592b\u5987\u53c8\u5012\u4e86\u8336\u3002 \u3000\u3000\u6c5f\u5e72\u91cc\u95ee\u9053\uff1a\u201c\u5148\u524d\u8fd8\u5fd8\u8bb0\u4e00\u4ef6\u4e8b\uff0c\u6211\u90a3\u4e49\u5144\u5f53\u5e74\u66fe\u8eab\u4e2d\u897f\u57df\u9b54\u6559\u4e03\u715e\u6bd2\u9488\uff0c\u82df\u5973\u4fa0\u662f \u5426\u6709\u529e\u6cd5\u4e3a\u4ed6\u7597\u53bb\u4f53\u5185\u6bd2\u4f24\u3002\u201d \u3000\u3000\u82df\u6167\u6708\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u4e0d\u5fc5\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u8bb6\u7136\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u5fc5\u4e86\uff1f\u201d \u3000\u3000\u201c\u6211\u5df2\u7528\u91d1\u9488\u8fc7\u7a74\u4e4b\u6cd5\u6d4b\u8bd5\u8fc7\uff0c\u4ed6\u4f53\u5185\u7684\u6bd2\u9488\u5267\u6bd2\u5df2\u5168\u90e8\u6d88\u5931\u3002\u201d \u3000\u3000\u201c\u600e\u4f1a\u8fd9\u6837\u5462\uff1f\u201d \u3000\u3000\u201c\u7406\u7531\u5f88\u7b80\u5355\uff0c\u8fd9\u534a\u5e74\u591a\u4ed6\u4e0d\u996e\u4e0d\u98df\uff0c\u8840\u8109\u6d3b\u52a8\u5df2\u5b8c\u5168\u9759\u6b62\uff0c\u65e0\u5f62\u4e2d\u4ed6\u4f53\u5185\u7684\u6bd2\u9488\u5267\u6bd2 \u4e5f\u56e0\u505c\u6b62\u6d3b\u52a8\u800c\u81ea\u884c\u6d88\u89e3\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u4e0d\u4e45\u4e4b\u540e\u4ed6\u4fbf\u53ef\u4ee5\u5b8c\u5168\u6062\u590d\u6b63\u5e38\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u4ed6\u4f1a\u6062\u590d\u5230\u50cf\u4e8c\u5341\u5e74\u524d\u7684\u4ed6\u5b8c\u5168\u4e00\u6837\u3002\u201d \u3000\u3000\u63a5\u7740\uff0c\u82df\u6167\u6708\u95ee\u8d77\u4e09\u516c\u4e3b\u90a3\u8fb9\u7684\u4e8b\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u76ee\u524d\u90a3\u8fb9\u7684\u4eba\u9a6c\u5206\u6210\u4e24\u90e8\u5206\uff0c\u4e09\u516c\u4e3b\u5728\u8fd9\u8fb9\u7597\u6cbb\u4f53\u5185\u866b\u6bd2\uff1b\u6c5f\u67d0\u7684\u51e0\u540d\u624b \u4e0b\u4e5f\u5168\u7559\u5728\u8fd9\u8fb9\uff1b\u738b\u7edf\u9886\u5219\u5e26\u7740\u624b\u4e0b\u7684\u4f8d\u536b\u9ad8\u624b\u53bb\u4e86\u6f7c\u5173\uff0c\u4ee5\u4fbf\u5bf9\u2018\u6843\u6797\u5c45\u2019\u8d1f\u8d23\u76d1\u89c6\u3002\u201d \u3000\u3000\u82df\u6167\u6708\u9ed8\u4e86\u4e00\u9ed8\u9053\uff1a\u201c\u5982\u679c\u90a3\u8fb9\u7684\u4eba\u624b\u4e0d\u591f\uff0c\u6211\u51b3\u5b9a\u4e5f\u53bb\u5e2e\u5fd9\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u5927\u559c\u9053\uff1a\u201c\u53ef\u662f\u8fd9\u8fb9\u7684\u4e8b\uff0c\u82df\u5973\u4fa0\u600e\u53ef\u653e\u4e0b\u4e0d\u7ba1\uff1f\u201d \u3000\u3000\u201c\u6211\u5f53\u7136\u8981\u5728\u4ed6\u5b8c\u5168\u590d\u539f\u4e4b\u540e\u624d\u80fd\u5230\u4e91\u513f\u90a3\u8fb9\u53bb\uff0c\u8bf4\u4e0d\u5b9a\u6211\u4f1a\u548c\u4ed6\u4e00\u8d77\u53bb\u3002\u201d \u3000\u3000\u201c\u90a3\u592a\u597d\u4e86\uff0c\u82e5\u6709\u82df\u5973\u4fa0\u548c\u6211\u90a3\u4e49\u5144\u4eb2\u81ea\u51fa\u9a6c\u76f8\u52a9\uff0c\u4e00\u5207\u5c31\u597d\u529e\u4e86\u3002\u201d \u3000\u3000\u82df\u6167\u6708\u4ece\u6000\u91cc\u638f\u51fa\u4e00\u53ea\u7eff\u8272\u7389\u74f6\uff0c\u5012\u51fa\u4e09\u7c92\u4e39\u4e38\uff0c\u9053\uff1a\u201c\u4e0a\u6b21\u4e91\u513f\u5230\u8fd9\u91cc\u6765\uff0c\u66fe\u8bf4\u8fc7\u60dc \u6625\u90a3\u4e2b\u5934\u6025\u9700\u89e3\u836f\uff0c\u6c5f\u5927\u4fa0\u5c31\u8bf7\u628a\u8fd9\u4e09\u7c92\u4e39\u4e38\u5e26\u56de\u53bb\u4ea4\u7ed9\u60dc\u6625\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u559c\u51fa\u671b\u5916\uff0c\u4f38\u624b\u63a5\u8fc7\u9053\uff1a\u201c\u636e\u8bf4\u60dc\u6625\u6240\u9700\u7684\u89e3\u836f\u662f\u897f\u57df\u9b54\u6559\u72ec\u95e8\u6240\u6709\uff0c\u82df\u5973\u4fa0\u662f \u4ece\u54ea\u91cc\u5f97\u5230\u7684\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e39\u4e5d\u53ef\u89e3\u767e\u6bd2\uff0c\u6211\u76f8\u4fe1\u60dc\u6625\u4f53\u5185\u4e4b\u6bd2\u7edd\u4e0d\u4f1a\u8d85\u51fa\u767e\u6bd2\u4e4b\u5916\uff0c\u867d\u975e\u72ec\u95e8\u7814\u5236\uff0c\u60dc\u6625\u670d \u7528\u4e4b\u540e\u7167\u6837\u4e5f\u4f1a\u6709\u6548\u3002\u201d \u3000\u3000\u201c\u90a3\u6211\u5c31\u4ee3\u66ff\u60dc\u6625\u591a\u8c22\u82df\u5973\u4fa0\u4e86\uff01\u201d \u3000\u3000\u4f60\u4eec\u73b0\u5728\u5c31\u56de\u53bb\u5427\uff0c\u6211\u76f8\u4fe1\u5929\u96f7\u4e00\u5b9a\u4f1a\u5f88\u5feb\u590d\u539f\uff0c\u4e0d\u4e45\u7684\u5c06\u6765\u54b1\u4eec\u518d\u5728\u6f7c\u5173\u76f8\u89c1\u3002\u201c \u5367\u9f99\u751f\u300a\u71d5\u5b50\u4f20\u5947\u300b \u7b2c\u4e09\u5341\u4e94\u56de\u3000\u6377\u62a5\u9891\u4f20 \u3000\u3000\u4e09\u65e5\u540e\uff0c\u6842\u98d8\u9999\u58f0\u79f0\u4e09\u516c\u4e3b\u4f53\u5185\u7684\u866b\u6bd2\u5df2\u5b8c\u5168\u9664\u5c3d\u3002 \u3000\u3000\u7ecf\u8fc7\u8001\u5996\u5a46\u4ed4\u7ec6\u68c0\u67e5\uff0c\u679c\u7136\u627e\u4e0d\u51fa\u4f59\u6bd2\u4e86\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u63a5\u7eb3\u5c0f\u71d5\u5b50\u7684\u5efa\u8bae\uff0c\u51b3\u5b9a\u7559\u4e0b\u6842\u98d8\u9999\u3001\u77f3\u69b4\u3001\u51e4\u4ed9\u4e00\u547d\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u5979\u5374\u4e0d\u613f\u7acb\u5373\u628a\u4e09\u4eba\u653e\u8d70\uff0c\u4ee5\u514d\u53c8\u751f\u540e\u60a3\u3002\u4e8e\u662f\uff0c\u4fbf\u5e9f\u4e86\u4e09\u4eba\u6b66\u529f\uff0c\u8981\u5979\u4eec\u4ecd\u7136 \u968f\u540c\u884c\u52a8\uff0c\u7b49\u5230\u4e86\u6f7c\u5173\u518d\u8bf4\u3002 \u3000\u3000\u4e00\u884c\u4eba\u9a6c\uff0c\u5f88\u5feb\u4fbf\u91cd\u53cd\u6f7c\u5173\uff0c\u5728\u5ba2\u6808\u91cc\u4e0e\u738b\u5f64\u7b49\u4eba\u4f1a\u5408\u3002 \u3000\u3000\u636e\u738b\u5f64\u544a\u77e5\uff0c\u7ecf\u8fc7\u8fde\u65e5\u6765\u7684\u4fa6\u5bdf\uff0c\u9a6c\u5be1\u5987\u548c\u9f99\u5728\u5929\u679c\u7136\u5df2\u56de\u5230\u201c\u6843\u82b1\u5c45\u201d\u3002 \u3000\u3000\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u7ecf\u8fc7\u4e00\u756a\u7814\u5546\uff0c\u5728\u4e8b\u4e0d\u5b9c\u8fdf\u3001\u8fdf\u5219\u6709\u53d8\u7684\u987e\u8651\u4e0b\uff0c\u51b3\u5b9a\u5f53\u665a\u4fbf\u91c7\u53d6\u884c\u52a8\uff0c \u52a1\u5fc5\u628a\u5bf9\u65b9\u4e00\u7f51\u6253\u5c3d\u3002 \u3000\u3000\u665a\u9910\u540e\uff0c\u6240\u6709\u7684\u4eba\u90fd\u9f50\u96c6\u5728\u540e\u9662\u5385\u3002 \u3000\u3000\u7531\u6c5f\u5e72\u91cc\u548c\u738b\u5f64\u5206\u914d\u4efb\u52a1\u3002 \u3000\u3000\u9664\u97e9\u6d9b\u3001\u71d5\u98de\u7559\u5728\u5ba2\u6808\u8d1f\u8d23\u8054\u7edc\u548c\u770b\u5b88\u6842\u98d8\u9999\u4e09\u4eba\u5916\uff0c\u5176\u4f59\u7684\u4eba\u5206\u6210\u4e09\u7ec4\u3002 \u3000\u3000\u7b2c\u4e00\u7ec4\u662f\u6c5f\u5343\u91cc\u7387\u9886\u738b\u91cd\u5c71\u3001\u4e03\u5de7\u50e7\u3001\u8001\u5996\u5a46\u5b88\u4f4f\u201c\u6843\u6797\u5c45\u201d\u524d\u95e8\u3002 \u3000\u3000\u7b2c\u4e8c\u7ec4\u4eba\u6570\u6700\u591a\uff0c\u4e5f\u662f\u6267\u884c\u8fd9\u6b21\u4efb\u52a1\u7684\u4e3b\u529b\u3002 \u3000\u3000\u8ba1\u6709\u4e09\u516c\u4e3b\u3001\u60dc\u6625\u3001\u738b\u5f64\u3001\u8d75\u4fdd\u3001\u9648\u5b8f\uff0c\u8fd9\u4e9b\u4eba\u8d1f\u8d23\u5b88\u5728\u79d8\u9053\u51fa\u53e3\u7684\u7816\u5c4b\u5185\uff0c\u4ee5\u5b88\u682a\u5f85 \u5154\u65b9\u5f0f\u51c6\u5907\u5c06\u5bf9\u65b9\u751f\u64d2\u6d3b\u6349\u3002 \u3000\u3000\u81f3\u4e8e\u7b2c\u4e09\u7ec4\uff0c\u5219\u662f\u5c0f\u71d5\u5b50\u3001\u767d\u7fbd\u548c\u601c\u82b1\uff0c\u4ed6\u4eec\u4ecd\u626e\u6210\u6295\u5bbf\u5ba2\u4eba\u6a21\u6837\uff0c\u4ee5\u4fbf\u6df7\u8fdb\u201c\u6843\u6797 \u5c45\u201d\u5185\u90e8\u7b56\u5e94\u3002 \u3000\u3000\u5c31\u5728\u4e8c\u66f4\u5de6\u53f3\uff0c\u5f53\u8857\u5e02\u4e0a\u5df2\u4e0d\u89c1\u884c\u4eba\u65f6\uff0c\u7b2c\u4e00\u7ec4\u548c\u7b2c\u4e8c\u7ec4\u5168\u5df2\u5728\u9884\u5b9a\u5730\u70b9\u5e03\u7f6e\u505c\u5f53\u3002 \u3000\u3000\u7b2c\u4e09\u7ec4\u7684\u5c0f\u71d5\u5b50\u3001\u767d\u7fbd\u3001\u601c\u82b1\u5219\u6765\u5230\u201c\u6843\u6797\u5c45\u201d\u4fa7\u95e8\u6572\u95e8\u3002 \u3000\u3000\u6572\u4e86\u5f88\u4e45\uff0c\u624d\u6709\u4eba\u51fa\u6765\u5e94\u95e8\u3002 \u3000\u3000\u5f00\u95e8\u7684\u53c8\u662f\u5c0f\u6885\u3002 \u3000\u3000\u5f53\u5c0f\u6885\u4e00\u773c\u77a5\u89c1\u5c0f\u71d5\u5b50\u7b49\u4e09\u4eba\uff0c\u7acb\u523b\u8138\u8272\u5927\u53d8\uff0c\u60f3\u628a\u95e8\u5173\u4e0a\u5df2\u4e0d\u53ca\uff0c\u53ea\u597d\u6025\u6025\u8f6c\u8eab\u5c31 \u8dd1\u3002 \u3000\u3000\u4f46\u5979\u5c1a\u672a\u8dd1\u51fa\u4e24\u6b65\uff0c\u4fbf\u88ab\u5c0f\u71d5\u5b50\u4e00\u6307\u70b9\u4e2d\u7a74\u9053\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u628a\u5c0f\u6885\u8fc5\u5feb\u7684\u5e26\u5230\u4e0a\u6b21\u7528\u9910\u7684\u5ba2\u623f\u4e2d\uff0c\u7136\u540e\u518d\u89e3\u5f00\u5979\u7684\u7a74\u9053\u3002 \u3000\u3000\u8fd9\u95f4\u5ba2\u623f\u79bb\u540e\u9662\u5c1a\u8fdc\uff0c\u53ea\u8981\u5c0f\u6885\u4e0d\u51fa\u58f0\u558a\u53eb\uff0c\u4fbf\u4e0d\u81f4\u60ca\u52a8\u91cc\u9762\u7684\u4eba\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u4e0d\u5f97\u4e0d\u5148\u628a\u5c0f\u6885\u5413\u4e0a\u4e00\u5413\u3002 \u3000\u3000\u4ed6\u4ee5\u5251\u5c16\u62b5\u4f4f\u5979\u7684\u54bd\u5589\uff0c\u9053\uff1a\u201c\u4f60\u82e5\u558a\u53eb\uff0c\u53ea\u6709\u6b7b\u8def\u4e00\u6761\uff1b\u5982\u679c\u80af\u5408\u4f5c\uff0c\u5728\u4e0b\u7edd\u4e0d\u4f24\u4f60 \u4e00\u6839\u6beb\u6bdb\u3002\u201d \u3000\u3000\u5c0f\u6885\u76ee\u77aa\u53e3\u5446\u7684\u9053\uff1a\u201c\u4f60\u8981\u6211\u5408\u4f5c\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u53ea\u8981\u4f60\u8bf4\u5b9e\u8bdd\uff01\u201d \u3000\u3000\u201c\u8981\u6211\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u9a6c\u5f69\u4e91\u548c\u9f99\u5728\u5929\u4f4f\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u2026\u2026\u4ed6\u4eec\u2026\u2026\u3002\u201d \u3000\u3000\u201c\u5feb\u8bf4\uff01\u201d \u3000\u3000\u201c\u4ed6\u4eec\u5df2\u77e5\u8fd9\u51e0\u5929\u5916\u9762\u98ce\u58f0\u5f88\u7d27\uff0c\u90fd\u2026\u2026\u90fd\u7761\u5728\u79d8\u9053\u5185\u7684\u623f\u95f4\u91cc\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u4f60\u8d1f\u8d23\u5e26\u8def\u3002\u201d \u3000\u3000\u5c0f\u6885\u5168\u8eab\u98a4\u6296\u7684\u7ad9\u8d77\u8eab\u6765\uff0c\u51fa\u4e86\u5ba2\u623f\u5411\u540e\u9662\u8d70\u3002\u5c0f\u71d5\u5b50\u3001\u767d\u7fbd\u3001\u601c\u82b1\u5404\u81ea\u624b\u6a2a\u5175\u5203\uff0c\u7d27 \u7d27\u968f\u5728\u540e\u9762\u3002 \u3000\u3000\u5f88\u5feb\u4fbf\u5230\u8fbe\u8de8\u9662\u8bbe\u6709\u79d8\u9053\u7684\u90a3\u95f4\u623f\u95f4\uff0c\u767d\u7fbd\u548c\u601c\u82b1\u4e3b\u52a8\u628a\u5e8a\u94fa\u79fb\u5f00\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u81ea\u7136\u4e0d\u80fd\u8ba9\u5c0f\u6885\u5148\u4e0b\u53bb\uff0c\u7acb\u5373\u4ea4\u4ee3\u601c\u82b1\u9053\uff1a\u201c\u59d1\u5a18\u548c\u6211\u5148\u4e0b\u53bb\uff0c\u5c0f\u9053\u58eb\u8bf7\u6bbf \u540e\u3002\u201d \u3000\u3000\u5f53\u4e24\u4eba\u4e0b\u53bb\u4e0d\u4e45\uff0c\u5c0f\u6885\u4e5f\u843d\u4e0b\u5730\u6765\u3002 \u3000\u3000\u63a5\u7740\u662f\u767d\u7fbd\u968f\u540e\u8dc3\u4e0b\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u4f4e\u58f0\u5429\u5490\u5c0f\u6885\u9053\uff1a\u201c\u73b0\u5728\u53c8\u8be5\u5e26\u8def\u4e86\uff0c\u8d70\u8def\u811a\u6b65\u8981\u8f7b\uff0c\u522b\u53d1\u51fa\u58f0\u54cd\u3002\u201d \u3000\u3000\u5c0f\u6885\u70b9\u4e86\u70b9\u5934\uff0c\u5411\u524d\u8d70\u53bb\uff0c\u679c\u7136\u811a\u6b65\u5f88\u8f7b\uff0c\u672a\u53d1\u51fa\u534a\u70b9\u58f0\u606f\u3002 \u3000\u3000\u5927\u7ea6\u8d70\u4e86\u51e0\u5341\u6b65\uff0c\u521a\u8f6c\u8fc7\u4e00\u6761\u5f2f\u8def\uff0c\u5c0f\u6885\u505c\u4e0b\u811a\u6b65\u9053\uff1a\u201c\u6211\u4eec\u5927\u638c\u67dc\u7684\u548c\u4e8c\u638c\u67dc\u7684\u5c31\u5728 \u8fd9\u623f\u95f4\u91cc\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6b64\u523b\u89c6\u529b\u5df2\u6e10\u6e10\u9002\u5e94\uff0c\u8f6c\u5934\u770b\u53bb\uff0c\u679c\u7136\u6d1e\u58c1\u4e0a\u73b0\u51fa\u4e00\u6247\u672f\u95e8\uff0c\u4fbf\u538b\u4f4e\u58f0\u97f3\u95ee\u9053\uff1a \u201c\u4ed6\u4eec\u4e0d\u53ef\u80fd\u7761\u5728\u4e00\u4e2a\u623f\u95f4\u5427\uff1f\u53e6\u5916\u4e00\u4eba\u4f4f\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u5c0f\u6885\u9053\uff1a\u201c\u592b\u4eba\u4e0d\u5728\u65f6\uff0c\u4ed6\u4eec\u672c\u6765\u5c31\u5e38\u5e38\u4f4f\u5728\u4e00\u4e2a\u623f\u95f4\u91cc\uff0c\u5e97\u91cc\u7684\u4eba\u4e5f\u90fd\u89c1\u602a\u4e0d\u602a\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u6b63\u51c6\u5907\u51b2\u8fdb\u53bb\uff0c\u5ffd\u89c1\u773c\u524d\u4eba\u5f71\u4e00\u95ea\uff0c\u5c0f\u6885\u5df2\u95ea\u7535\u822c\u5411\u524d\u5954\u53bb\uff0c\u4e00\u9762\u5927\u53eb\u9053\uff1a\u201c\u5927 \u5bb6\u5feb\u51c6\u5907\u884c\u52a8\uff0c\u4ed6\u4eec\u5df2\u8fdb\u5165\u79d8\u9053\u6765\u4e86\uff01\u201d \u3000\u3000\u5c31\u5728\u5c0f\u71d5\u5b50\u8fd9\u4e00\u6014\u4e4b\u95f4\uff0c\u5c0f\u6885\u65e9\u5df2\u4eba\u5f71\u4e0d\u89c1\u3002 \u3000\u3000\u9877\u523b\u4e4b\u540e\uff0c\u79d8\u9053\u4e2d\u5df2\u6d8c\u51fa\u4e03\u516b\u6761\u4eba\u5f71\uff0c\u76f4\u5411\u5c0f\u71d5\u5b50\u7b49\u4eba\u51b2\u6765\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u3001\u767d\u7fbd\u3001\u601c\u82b1\u6025\u6025\u6325\u52a8\u5175\u5203\u8fce\u654c\u3002 \u3000\u3000\u90a3\u4e03\u516b\u540d\u7537\u5973\u867d\u7136\u4e2a\u4e2a\u8eab\u624b\u4e0d\u5f31\uff0c\u4f46\u5982\u4f55\u662f\u5c0f\u71d5\u5b50\u3001\u767d\u7fbd\u3001\u601c\u82b1\u4e09\u4eba\u5bf9\u624b\u3002\u5c24\u5176\u5c0f\u71d5\u5b50 \u4eba\u5728\u6700\u524d\uff0c\u4e00\u67c4\u5251\u51fa\u62db\u5982\u95ea\u7535\u8b66\u9e3f\uff0c\u5f88\u5feb\u4fbf\u780d\u5012\u4e86\u597d\u51e0\u4e2a\uff0c\u5176\u4f59\u7684\u4e00\u89c1\u4e0d\u5999\uff0c\u53ea\u597d\u56de\u8eab\u5954 \u9003\u3002 \u3000\u3000\u5728\u5947\u6697\u65e0\u5149\u4e4b\u4e0b\uff0c\u5c0f\u71d5\u5b50\u7b49\u4eba\u53c8\u4e0d\u8c19\u79d8\u9053\u5185\u7684\u8def\u7ebf\u4e0e\u73af\u5883\uff0c\u4f59\u4e0b\u7684\u51e0\u4eba\u5f88\u5feb\u4fbf\u8e2a\u5f71\u5168 \u65e0\uff0c\u8fde\u58f0\u97f3\u4e5f\u4e0d\u95fb\u534a\u70b9\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u5c0f\u71d5\u5b50\u3001\u767d\u7fbd\u4e0e\u601c\u82b1\u53ea\u6709\u5c55\u5f00\u9010\u5c4b\u641c\u67e5\u3002 \u3000\u3000\u867d\u7136\u641c\u51fa\u4e86\u51e0\u4eba\uff0c\u4f46\u5e76\u65e0\u9a6c\u5be1\u5987\u548c\u9f99\u5728\u5929\u5728\u5185\uff0c\u8fde\u5c0f\u6885\u4e5f\u4e0d\u77e5\u90a3\u91cc\u53bb\u4e86\u3002 \u3000\u3000\u4e09\u4eba\u53ea\u6709\u91cd\u590d\u7684\u7ee7\u7eed\u641c\u5bfb\uff0c\u6700\u540e\u8fd8\u662f\u6beb\u65e0\u6240\u83b7\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u53ea\u542c\u5916\u9762\u901a\u9053\u4e0a\u54cd\u8d77\u4e86\u4e00\u4e2a\u5a07\u8106\u7684\u58f0\u97f3\u9053\uff1a\u201c\u71d5\u5c11\u4fa0\uff0c\u4f60\u4eec\u5728\u54ea\u91cc\uff1f\u4e0d\u5fc5\u641c \u4e86\u3002\u201d \u3000\u3000\u4e09\u4eba\u542c\u51fa\u662f\u60dc\u6625\u7684\u58f0\u97f3\uff0c\u8fde\u5fd9\u5faa\u58f0\u627e\u53bb\u3002 \u3000\u3000\u5f88\u5feb\u4fbf\u627e\u5230\u4e86\uff0c\u679c\u7136\u662f\u60dc\u6625\u7ad9\u5728\u901a\u9053\u4e0a\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u95ee\u9053\uff1a\u201c\u59d1\u5a18\u662f\u600e\u4e48\u8fdb\u6765\u7684\uff1f\u4e3a\u4ec0\u4e48\u4e0d\u5fc5\u641c\u4e86\uff1f\u201d \u3000\u3000\u60dc\u6625\u9053\uff1a\u201c\u8fd9\u6761\u79d8\u9053\u6211\u6e05\u695a\uff0c\u81ea\u7136\u662f\u4ece\u540e\u9762\u51fa\u53e3\u8fdb\u6765\u7684\uff0c\u4e09\u516c\u4e3b\u548c\u738b\u5927\u4eba\u4ed6\u4eec\u5df2\u6349\u5230\u4e03 \u516b\u4e2a\uff0c\u9a6c\u5f69\u4e91\u548c\u9f99\u5728\u5929\u90fd\u5728\u5185\u7684\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u5927\u559c\u9053\uff1a\u201c\u662f\u5426\u8fd8\u6709\u6f0f\u7f51\u7684\uff1f\u201d \u3000\u3000\u201c\u542c\u8bf4\u5b88\u5728\u524d\u9762\u7684\u6c5f\u5927\u4fa0\u751f\u64d2\u6d3b\u6349\u7684\u66f4\u591a\uff0c\u8db3\u6709\u5341\u51e0\u4e2a\u3002\u201d \u3000\u3000\u201c\u4e09\u516c\u4e3b\u73b0\u5728\u51c6\u5907\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u201c\u4e09\u516c\u4e3b\u548c\u738b\u5927\u4eba\u5546\u8bae\u540e\uff0c\u54b1\u4eec\u7684\u4eba\u9a6c\u51b3\u5b9a\u8fdb\u9a7b\u2018\u6843\u6797\u5c45\u2019\u3002\u201d \u3000\u3000\u201c\u8fd9\u91cc\u539f\u6709\u7684\u4eba\u5462\uff1f\u201d \u3000\u3000\u201c\u4e5f\u8ba9\u4ed6\u4eec\u4ecd\u4f4f\u8fdb\u6765\uff0c\u751f\u610f\u8fd8\u662f\u7167\u505a\uff0c\u53ea\u662f\u6362\u4e86\u638c\u67dc\u7684\u800c\u5df2\u3002\u201d \u3000\u3000\u201c\u662f\u5426\u7531\u59d1\u5a18\u505a\u638c\u67dc\u7684\uff1f\u201d \u3000\u3000\u201c\u4e5f\u8bb8\u5427\uff01\u4e0d\u8fc7\u8868\u9762\u4e0a\u8fd8\u662f\u9a6c\u5f69\u4e91\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u4e09\u4eba\u73b0\u5728\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u201c\u4f60\u4eec\u4e09\u4f4d\u8bf7\u56de\u5230\u540e\u9662\uff0c\u5f85\u4f1a\u513f\u6240\u6709\u7684\u4eba\u90fd\u4f1a\u62bc\u8fc7\u53bb\u3002\u201d \u3000\u3000\u60dc\u6625\u8bf4\u5b8c\u8bdd\uff0c\u5f88\u5feb\u53c8\u56de\u5230\u540e\u9762\u51fa\u53e3\u3002 \u3000\u3000\u5c0f\u71d5\u5b50\u3001\u767d\u7fbd\u3001\u601c\u82b1\u8fd4\u56de\u540e\u9662\u4e0d\u4e45\uff0c\u53e6\u4e24\u7ec4\u4eba\u9a6c\u4fbf\u5df2\u628a\u9a6c\u5f69\u4e91\u3001\u9f99\u5728\u5929\u4ee5\u53ca\u4ed6\u4eec\u7684\u624b\u4e0b \u62bc\u5230\uff0c\u8db3\u6709\u4e8c\u5341\u4f59\u4eba\u4e4b\u591a\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u5429\u5490\u628a\u9a6c\u5f69\u4e91\u3001\u9f99\u5728\u5929\u62bc\u8fdb\u5ba2\u5385\uff0c\u5176\u4f59\u7684\u4eba\u5168\u6682\u65f6\u7559\u5728\u5929\u4e95\u91cc\u3002 \u3000\u3000\u5f53\u5c0f\u71d5\u5b50\u8fdb\u5165\u5ba2\u5385\u65f6\uff0c\u4e09\u516c\u4e3b\u3001\u6c5f\u5343\u91cc\u3001\u738b\u5f64\u3001\u60dc\u6625\u5df2\u5168\u5230\u9f50\u4e86\u3002 \u3000\u3000\u5730\u4e0a\u8eba\u7740\u4e00\u7537\u4e00\u5973\uff0c\u6b63\u662f\u9f99\u5728\u5929\u548c\u9a6c\u5f69\u4e91\u3002 \u3000\u3000\u4ed6\u4eec\u5df2\u88ab\u70b9\u4e86\u7a74\u9053\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u5429\u5490\u9053\uff1a\u201c\u89e3\u5f00\u4ed6\u4eec\u7684\u7a74\u9053\uff01\u201d \u3000\u3000\u738b\u5f64\u4e0a\u524d\u5728\u4e24\u4eba\u8eab\u4e0a\u62cd\u4e86\u51e0\u4e0b\uff0c\u4e00\u7537\u4e00\u5973\u7741\u5f00\u773c\uff0c\u6323\u624e\u7740\u5750\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5ba2\u5385\u5185\u6240\u6709\u4eba\u7684\u89c6\u7ebf\u7686\u96c6\u4e2d\u5728\u4e24\u4eba\u8eab\u4e0a\u3002 \u3000\u3000\u9f99\u5728\u5929\u5728\u4e94\u65ec\u5de6\u53f3\uff0c\u8eab\u6750\u9b41\u68a7\u9ad8\u5927\uff0c\u4e24\u9053\u6d53\u7709\uff0c\u53cc\u76ee\u7cbe\u5149\u70af\u70af\uff0c\u4e00\u8138\u9634\u9e37\u9669\u6076\u7684\u795e\u8272\u3002 \u3000\u3000\u9a6c\u5f69\u4e91\u679c\u7136\u5929\u751f\u5c24\u7269\uff0c\u9e45\u86cb\u8138\uff0c\u67f3\u9edb\u7709\uff0c\u80a4\u767d\u4f3c\u96ea\uff0c\u51e4\u59ff\u7ef0\u7ea6\uff0c\u5c24\u5176\u7709\u76ee\u4e4b\u95f4\uff0c\u987e\u76fc\u751f \u59ff\uff0c\u4e00\u6d3e\u52fe\u4eba\u5fc3\u9b44\u7684\u6deb\u5987\u6a21\u6837\u3002 \u3000\u3000\u9f99\u5728\u5929\u4e00\u7741\u5f00\u773c\uff0c\u89c6\u7ebf\u4fbf\u76ef\u4f4f\u60dc\u6625\u3002 \u3000\u3000\u4ed6\u54a6\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u600e\u4e48\u662f\u4f60\uff1f\u201d \u3000\u3000\u60dc\u6625\u54ac\u7259\u5207\u9f7f\u7684\u9053\uff1a\u201c\u9f99\u5728\u5929\uff0c\u4f60\u73b0\u5728\u603b\u8be5\u8ba4\u8bc6\u6211\u4e86\u5427\uff1f\u201d \u3000\u3000\u9f99\u5728\u5929\u5f20\u53e3\u7ed3\u820c\u4e86\u534a\u664c\uff0c\u624d\u9f87\u7259\u54a7\u5634\u7684\u9053\uff1a\u201c\u592b\u4eba\uff0c\u54b1\u4eec\u662f\u2026\u2026 \u3000\u3000\u592b\u59bb\u2026\u2026\u201c \u3000\u3000\u60dc\u6625\u54fc\u9053\uff1a\u201c\u8c01\u548c\u4f60\u8fd9\u79cd\u79bd\u517d\u4e0d\u5982\u7684\u4e1c\u897f\u4f5c\u592b\u59bb\uff1f\u9f99\u5728\u5929\uff0c\u4f60\u8fd8\u60f3\u6d3b\u547d\u5417\uff1f\u201d \u3000\u3000\u522b\u770b\u9f99\u5728\u5929\u4e00\u5411\u4f5c\u5a01\u4f5c\u798f\uff0c\u5230\u4e86\u8fd9\u79cd\u5730\u6b65\u7adf\u6ca1\u6709\u534a\u70b9\u9aa8\u6c14\uff0c\u7adf\u7136\u4e00\u4e0b\u5b50\u7531\u5750\u53d8\u8dea\uff0c\u9053\uff1a \u201c\u592b\u4eba\uff0c\u53ea\u8981\u4f60\u80af\u9976\u4e86\u6211\uff0c\u8ba9\u6211\u505a\u4ec0\u4e48\u90fd\u53ef\u4ee5\uff01\u201d \u3000\u3000\u201c\u5148\u628a\u89e3\u836f\u62ff\u51fa\u6765\uff01\u201d \u3000\u3000\u201c\u89e3\u836f\u653e\u5728\u9a6c\u5802\u4e3b\u623f\u91cc\uff0c\u90a3\u7eff\u8272\u5730\u74f6\u5b50\u91cc\u8db3\u6709\u51e0\u767e\u7c92\uff0c\u53e6\u6709\u4e00\u53ea\u767d\u8272\u5c0f\u74f6\uff0c\u670d\u4e0b\u4e4b\u540e\u53ef \u4ee5\u4e00\u6b21\u65ad\u6839\u3002\u201d \u3000\u3000\u53ea\u542c\u4e09\u516c\u4e3b\u9053\uff1a\u201c\u60dc\u6625\uff0c\u5148\u53bb\u628a\u89e3\u836f\u627e\u6765\uff0c\u73b0\u5728\u7531\u6211\u6765\u95ee\u4ed6\u51e0\u53e5\u8bdd\u3002\u201d \u3000\u3000\u60dc\u6625\u5e94\u58f0\u800c\u53bb\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u4e0d\u613f\u8ba9\u9a6c\u5f69\u4e91\u77e5\u9053\u5982\u4f55\u5904\u7f6e\u9f99\u5728\u5929\u7684\u4e8b\uff0c\u5148\u4e00\u6307\u5c06\u9a6c\u5f69\u4e91\u70b9\u660f\u8fc7\u53bb\uff0c\u624d\u4e0d\u52a8\u58f0\u8272 \u7684\u95ee\u9053\uff1a\u201c\u9f99\u5728\u5929\uff0c\u8fd8\u8ba4\u8bc6\u6211\u5417\uff1f\u201d \u3000\u3000\u9f99\u5728\u5929\u54ea\u91cc\u8fd8\u6562\u6b63\u89c6\u4e09\u516c\u4e3b\uff0c\u6253\u4e86\u4e2a\u54c6\u55e6\u9053\uff1a\u201c\u4e09\u2026\u2026\u516c\u2026\u2026 \u3000\u3000\u4e3b\u2026\u2026\u9976\u547d\u2026\u2026\u201c \u3000\u3000\u4e09\u516c\u4e3b\u54ac\u4e86\u54ac\u7259\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u5df2\u7f6a\u5927\u6076\u6781\uff0c\u8fde\u4e0a\u5929\u90fd\u5bb9\u4e0d\u5f97\u4f60\uff0c\u6211\u53c8\u5c82\u80fd\u518d\u8ba9\u4f60\u6d3b\u4e0b \u53bb\uff1f\u201d \u3000\u3000\u201c\u4e8c\u516c\u535e\u2026\u2026\u9976\u547d\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u5f53\u521d\u7684\u80c6\u5b50\u54ea\u91cc\u53bb\u4e86\uff0c\u4e3a\u4ec0\u4e48\u5c31\u8fd9\u4e48\u6ca1\u6709\u9aa8\u6c14\uff01\u201d \u3000\u3000\u4e09\u516c\u4e3b\u8bf4\u5230\u8fd9\u91cc\uff0c\u671b\u5411\u5c0f\u71d5\u5b50\u95ee\u9053\uff1a\u201c\u71d5\u5c11\u4fa0\uff0c\u4f60\u548c\u5c0f\u9053\u58eb\u3001\u601c\u82b1\u662f\u5426\u5728\u5730\u9053\u91cc\u6740\u8fc7\u51e0 \u4e2a\u4eba\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u70b9\u70b9\u5934\u9053\uff1a\u201c\u5927\u7ea6\u6740\u4e86\u4ed6\u4eec\u56db\u4e94\u4e2a\u3002\u201d \u3000\u3000\u8fd9\u65f6\u53ea\u89c1\u60dc\u6625\u62ff\u7740\u4e24\u74f6\u89e3\u836f\u8d70\u4e86\u8fdb\u6765\uff0c\u9053\uff1a\u201c\u7980\u4e09\u516c\u4e3b\uff0c\u89e3\u836f\u5df2\u7ecf\u627e\u5230\u4e86\u3002\u201d \u3000\u3000\u4e09\u516c\u4e3b\u5429\u5490\u738b\u5f64\u9053\uff1a\u201c\u73b0\u5728\u5c31\u8bf7\u738b\u5927\u4eba\u6d3e\u51e0\u4e2a\u4eba\u62bc\u7740\u9f99\u5728\u5929\u7531\u540e\u9762\u5730\u9053\u51fa\u53bb\uff0c\u987a\u4fbf\u628a\u90a3 \u51e0\u5177\u5c38\u4f53\u4e5f\u5e26\u51fa\u53bb\u3002\u201d \u3000\u3000\u738b\u5f64\u95ee\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u51c6\u5907\u600e\u6837\u5904\u7f6e\u8fd9\u59d3\u9f99\u7684\uff1f\u201d \u3000\u3000\u201c\u5c31\u5728\u540e\u9762\u5c71\u4e0b\u6316\u4e2a\u5751\uff0c\u5c06\u90a3\u51e0\u5177\u5c38\u4f53\u63a9\u57cb\u3002\u81f3\u4e8e\u8fd9\u59d3\u9f99\u7684\uff0c\u3002 \u3000\u3000\u4e0d\u5fc5\u5148\u6740\u6b7b\uff0c\u5c31\u628a\u4ed6\u6d3b\u57cb\u4e86\u5427\uff01\u201c \u3000\u3000\u60dc\u6625\u5fd9\u9053\uff1a\u201c\u4e09\u516c\u4e3b\uff0c\u5a62\u5b50\u4e5f\u60f3\u8ddf\u7740\u53bb\uff0c\u4ee5\u4fbf\u4eb2\u624b\u628a\u4ed6\u6d3b\u57cb\u3002\u201d \u3000\u3000\u4e09\u516c\u4e3b\u5362\u70b9\u5934\u9053\uff1a\u201c\u4f60\u5c31\u8ddf\u738b\u5927\u4eba\u4e00\u8d77\u53bb\u5427\uff01\u201d \u3000\u3000\u4e8e\u662f\uff0c\u738b\u5f64\u70b9\u4e86\u9f99\u5728\u5929\u7a74\u9053\uff0c\u631f\u5728\u814b\u4e0b\u4fbf\u4e0e\u60dc\u6625\u51fa\u4e86\u5ba2\u5385\uff0c\u518d\u628a\u9648\u5b8f\u3001\u8d75\u4fdd\u3001\u738b\u91cd\u5c71\u4e00 \u8d77\u53eb\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5929\u4e95\u91cc\u7684\u4e8c\u5341\u4f59\u4eba\u4e5f\u5168\u88ab\u70b9\u4e86\u7a74\u9053\uff0c\u7531\u4e03\u5de7\u501f\u3001\u767d\u7fbd\u548c\u8001\u5996\u5a46\u8d1f\u8d23\u770b\u5b88\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u95ee\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u6c5f\u53d4\u53d4\uff0c\u4f60\u770b\u8be5\u600e\u4e48\u5904\u7f6e\u9a6c\u5f69\u4e91\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u6700\u597d\u4e0d\u5fc5\u4e25\u5211\u903c\u4f9b\uff0c\u65e2\u7136\u5fc5\u987b\u8ba9\u2018\u6843\u6797\u5c45\u2019\u7ee7\u7eed\u8425\u4e1a\uff0c\u5c31\u8be5\u4ecd\u8ba9\u5979\u4e3b\u6301\u8fd9 \u91cc\u7684\u5e97\u52a1\u3002\u201d \u3000\u3000\u4e09\u516c\u4e3b\u9053\uff1a\u201c\u6211\u4e5f\u8fd9\u4e48\u60f3\uff0c\u5f85\u4f1a\u513f\u5c31\u7531\u6c5f\u53d4\u53d4\u51b3\u5b9a\u5982\u4f55\u5904\u7f6e\u5427\uff01\u201d \u3000\u3000\u6c5f\u5343\u91cc\u4e0d\u613f\u5f84\u81ea\u4f5c\u4e3b\uff0c\u5fd9\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u8fd8\u662f\u5728\u573a\u7684\u597d\uff0c\u6c5f\u67d0\u5904\u7406\u82e5\u6709\u4e0d\u59a5\u4e4b\u5904\uff0c\u4e0d\u59a8\u968f \u65f6\u52a0\u4ee5\u88c1\u793a\u3002\u201d \u3000\u3000\u4e09\u516c\u4e3b\u8c26\u900a\u7684\u9053\uff1a\u201c\u6c5f\u53d4\u53d4\u8a00\u91cd\u4e86\uff0c\u65e2\u7136\u4f60\u8981\u6211\u7559\u4e0b\uff0c\u6211\u5c31\u7559\u4e0b\uff0c\u4e0d\u8fc7\u4e00\u5207\u8fd8\u662f\u4ee5\u60a8\u4e3a \u4e3b\uff0c\u6211\u53ea\u662f\u5728\u5fc5\u8981\u65f6\u4e5f\u8bb8\u4f1a\u52a0\u5165\u4e00\u70b9\u610f\u89c1\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u4e0d\u518d\u793c\u8ba9\uff0c\u5429\u5490\u5c0f\u71d5\u5b50\u9053\uff1a\u201c\u628a\u9a6c\u5f69\u4e91\u7684\u7a74\u9053\u89e3\u5f00\uff01\u201d \u3000\u3000\u9a6c\u5f69\u4e91\u60a0\u60a0\u9192\u8f6c\uff0c\u56db\u4e0b\u5f20\u671b\u4e86\u4e00\u773c\u3002 \u3000\u3000\u5979\u6014\u6014\u7684\u95ee\u9053\uff1a\u201c\u9f99\u526f\u2026\u2026\u4e0d\u2026\u2026\u9f99\u5728\u5929\u5462\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u5df2\u7ecf\u95ee\u5b8c\u4e86\u8bdd\uff0c\u800c\u4e14\u4ed6\u5df2\u7ecf\u4ece\u5b9e\u62db\u8ba4\uff0c\u73b0\u5728\u5df2\u628a\u4ed6\u6682\u65f6\u9001\u5f80\u522b\u5904 \u53bb\u4e86\u3002\u201d \u3000\u3000\u9a6c\u5f69\u4e91\u4e00\u53cc\u5a9a\u773c\u76f4\u7728\u7684\u9053\uff1a\u201c\u4ed6\u2026\u2026\u4ed6\u62db\u8ba4\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u4e0d\u75be\u4e0d\u5f90\u7684\u9053\uff1a\u201c\u4ed6\u62db\u8ba4\u4ed6\u7684\uff0c\u4f60\u62db\u8ba4\u4f60\u7684\uff0c\u73b0\u5728\u662f\u6211\u95ee\u4f60\u8bdd\uff0c\u800c\u4e0d\u662f\u4f60\u5728\u95ee\u6211 \u8bdd\u3002\u201d \u3000\u3000\u9a6c\u5f69\u4e91\u53cc\u7738\u8109\u8109\u7684\u6ce8\u89c6\u7740\u6c5f\u5e72\u91cc\uff0c\u9053\uff1a\u201c\u6211\u731c\u5f97\u51fa\uff0c\u5750\u5728\u65c1\u8fb9\u7684\u90a3\u4e00\u4f4d\u662f\u91d1\u679d\u7389\u53f6\u7684\u4e09 \u516c\u4e3b\uff0c\u4f60\u4e00\u5b9a\u662f\u4f4d\u4e86\u4e0d\u8d77\u7684\u5927\u4eba\u7269\u5427\uff1f\u201d \u3000\u3000\u6c5f\u5e72\u91cc\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u597d\u8bf4\uff0c\u5728\u4e0b\u6c5f\u5343\u91cc\uff0c\u4e00\u4e2a\u533a\u533a\u65e0\u540d\u7684\u6c5f\u6e56\u5c0f\u5352\uff0c\u9a6c\u5802\u4e3b\u53ef\u80fd\u4e0d\u4f1a \u542c\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u9a6c\u5f69\u4e91\u795e\u8272\u4e00\u53d8\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u4f60\u662f\u9f0e\u9f0e\u5927\u540d\u7684\u6c5f\u5343\u91cc\uff1f\u96be\u602a\u8fde\u4e09\u516c\u4e3b\u4e5f\u8981\u4ee5\u4f60\u4e3a\u4e3b\uff0c\u4f60 \u60f3\u8981\u6211\u62db\u8ba4\u4ec0\u4e48\uff1f\u8bf4\u5427\uff01\u201d \u3000\u3000\u201c\u6211\u5df2\u77e5\u9053\u4f60\u662f\u5802\u4e3b\u8eab\u4efd\uff0c\u88ab\u6d3e\u5728\u6f7c\u5173\u4e3b\u6301\u2018\u6843\u6797\u5c45\u2019\uff0c\u4e0d\u77e5\u4f60\u8fd9\u5802\u4e3b\u5f52\u8c01\u7ba1\u8f96\uff1f\u201d \u3000\u3000\u201c\u7ec8\u5357\u5206\u575b\u3002\u201d \u3000\u3000\u201c\u4f60\u548c\u7ec8\u5357\u5206\u575b\u5e73\u5e38\u5982\u4f55\u8054\u7cfb\uff1f\u201d \u3000\u3000\u201c\u90a3\u8fb9\u7684\u9ec4\u575b\u4e3b\u6709\u4e8b\u65f6\u4f1a\u6d3e\u4e13\u4f7f\u524d\u6765\u4f20\u8fbe\u4ee4\u8c15\uff0c\u6211\u8fd9\u8fb9\u6709\u4e8b\u65f6\u4e5f\u4f1a\u6d3e\u4e13\u4f7f\u8fc7\u53bb\u3002\u201d \u3000\u3000\u201c\u7ec8\u5357\u5206\u575b\u6700\u8fd1\u662f\u5426\u6709\u4eba\u6765\u8fc7\uff1f\u201d \u3000\u3000\u201c\u6211\u521a\u4ece\u5206\u575b\u56de\u6765\u4e0d\u4e45\uff0c\u77ed\u65f6\u95f4\u5185\u90a3\u8fb9\u7528\u4e0d\u7740\u518d\u6d3e\u4eba\u6765\u3002\u201d \u3000\u3000\u7531\u9a6c\u5f69\u4e91\u7684\u5bf9\u7b54\u542c\u6765\uff0c\u8bf4\u7684\u5168\u662f\u5b9e\u8bdd\uff0c\u540c\u65f6\u4e5f\u53ef\u65ad\u5b9a\u5979\u548c\u9f99\u5728\u5929\u662f\u5728\u7ec8\u5357\u5206\u575b\u51fa\u4e8b\u524d\u79bb \u5f00\u7684\uff0c\u56e0\u4e4b\u5e76\u4e0d\u6e05\u695a\u5206\u575b\u5df2\u906d\u8840\u6d17\u7684\u4e8b\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u5916\u9762\u7684\u4eba\u90fd\u53eb\u4f60\u9a6c\u5be1\u5987\uff0c\u53ef\u89c1\u4f60\u662f\u6709\u8fc7\u4e08\u592b\u7684\u4eba\uff0c\u90a3\u4eba\u662f\u8c01\uff1f\u662f \u6b7b\u4e86\u8fd8\u662f\u5df2\u7ecf\u548c\u4f60\u79bb\u5f02\uff1f\u201d \u3000\u3000\u9a6c\u5f69\u4e91\u51dd\u7738\u8f6c\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u8fd9\u4e9b\u4e8b\u4f60\u4e5f\u60f3\u77e5\u9053\uff1f\u6c5f\u5927\u4fa0\u4e0d\u89c9\u5f97\u95ee\u5f97\u592a\u591a\u4e86\u5417\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u987f\u4e86\u987f\u9053\uff1a\u201c\u597d\uff0c\u6c5f\u67d0\u4e0d\u95ee\u8fd9\u65b9\u9762\u7684\u4e8b\u5c31\u662f\uff0c\u53ea\u8981\u4f60\u80af\u5408\u4f5c\uff0c\u6c5f\u67d0\u613f\u610f\u5f53\u7740\u4e09\u516c \u4e3b\u7684\u9762\uff0c\u5411\u4f60\u63d0\u51fa\u4fdd\u8bc1\uff01\u201d \u3000\u3000\u201c\u4fdd\u8bc1\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4e0d\u4f46\u7559\u4e0b\u4f60\u4e00\u547d\uff0c\u800c\u4e14\u4f60\u82e5\u6709\u529f\uff0c\u8bf4\u4e0d\u5b9a\u5c06\u6765\u8fd8\u4f1a\u8bba\u529f\u884c\u8d4f\u3002\u201d \u3000\u3000\u201c\u6c5f\u5927\u4fa0\u8981\u6211\u5408\u4f5c\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u8fd9\u5bb6\u2018\u6843\u6797\u5c45\u2019\u7ee7\u7eed\u7531\u4f60\u51fa\u9762\u4e3b\u6301\uff0c\u81f3\u4e8e\u5bf9\u5916\u8054\u7edc\uff0c\u5f53\u7136\u4e00\u5207\u7531\u6211\u4eec\u8d1f\u8d23\u3002\u201d \u3000\u3000\u9a6c\u5f69\u4e91\u7f04\u9ed8\u4e86\u534a\u664c\uff0c\u9053\uff1a\u201c\u8981\u6211\u66ff\u4f60\u4eec\u7acb\u529f\uff0c\u6307\u7684\u5c31\u662f\u8fd9\u4e2a\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u8fd9\u53ea\u662f\u4e00\u90e8\u5206\uff0c\u53e6\u5916\u8fd8\u6709\u5176\u4ed6\u7684\u4e8b\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\uff1f\u6c5f\u5927\u4fa0\u5c31\u8bf7\u8bf4\u5427\uff01\u6211\u73b0\u5728\u5df2\u7ecf\u843d\u5728\u4f60\u4eec\u624b\u91cc\uff0c\u4e3a\u4e86\u6d3b\u547d\uff0c\u8fd8\u6709\u4ec0\u4e48\u4e0d\u80af\u7b54\u5e94 \u7684\u3002\u201d \u3000\u3000\u201c\u5f88\u62b1\u6b49\uff0c\u73b0\u5728\u53c8\u8981\u8c08\u5230\u4f60\u7684\u79c1\u4e8b\u4e86\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u53c8\u8981\u95ee\u6211\u7684\u79c1\u4e8b\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u7684\u79c1\u4e8b\u4e5f\u8bb8\u662f\u6211\u4eec\u7684\u516c\u4e8b\u3002\u201d \u3000\u3000\u201c\u4f60\u95ee\u5427\uff01\u201d \u3000\u3000\u4f60\u548c\u6cb3\u5357\u5de1\u629a\u9a6c\u6587\u4e2d\u6709\u6ca1\u6709\u6765\u5f80\uff1f\u201c \u3000\u3000\u9a6c\u5f69\u4e91\u4e24\u773c\u76f4\u7728\u7684\u9053\uff1a\u201c\u6c5f\u5927\u4fa0\u8fde\u8fd9\u79cd\u4e8b\u4e5f\u60f3\u77e5\u9053\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9894\u9996\u9053\uff1a\u201c\u6211\u521a\u624d\u8bf4\u8fc7\uff0c\u8fd9\u662f\u6211\u4eec\u7684\u516c\u4e8b\u3002\u201d \u3000\u3000\u201c\u6211\u66fe\u89c1\u8fc7\u4ed6\u3002\u201d \u3000\u3000\u201c\u5728\u4ec0\u4e48\u5730\u65b9\u89c1\u8fc7\uff1f\u201d \u3000\u3000\u201c\u5f00\u5c01\u3002\u201d \u3000\u3000\u201c\u4f60\u5230\u5f00\u5c01\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u90a3\u662f\u4e00\u5e74\u524d\uff0c\u6211\u5949\u5206\u575b\u4e4b\u547d\u5230\u8fc7\u4e00\u6b21\u5f00\u5c01\uff0c\u5e76\u63a5\u53d7\u8fc7\u4ed6\u7684\u62db\u5f85\uff0c\u4ec5\u6b64\u800c\u5df2\uff0c\u201d \u3000\u3000\u201c\u597d\uff0c\u6c5f\u67d0\u76f8\u4fe1\u4f60\u7684\u8bdd\uff0c\u73b0\u5728\u6c5f\u67d0\u8981\u63d0\u5230\u7b2c\u4e8c\u4e2a\u4eba\uff0c\u4f60\u548c\u8fd9\u4eba\u4e0d\u4f46\u6709\u6765\u5f80\uff0c\u800c\u4e14\u6765\u5f80\u5bc6 \u5207\uff0c\u8fd9\u662f\u6c5f\u67d0\u8c03\u67e5\u5f97\u5f88\u6e05\u695a\u7684\u4e00\u4ef6\u4e8b\uff0c\u4f60\u82e5\u62b5\u8d56\u4e0d\u62db\uff0c\u90a3\u5c31\u522b\u602a\u6c5f\u67d0\u624b\u4e0b\u65e0\u60c5\u3002\u201d \u3000\u3000\u9a6c\u5f69\u4e91\u53cc\u988a\u62bd\u6410\u4e86\u51e0\u4e0b\uff0c\u7741\u5927\u4e24\u773c\u9053\uff1a\u201c\u6211\u5df2\u8868\u793a\u8fc7\u613f\u610f\u5408\u4f5c\uff0c\u6c5f\u5927\u4fa0\u53c8\u4f55\u5fc5\u518d\u6765\u5413 \u4eba\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u5e26\u70b9\u6b49\u610f\u9053\uff1a\u201c\u6c5f\u67d0\u662f\u4e0d\u5f97\u4e0d\u5982\u6b64\u3002\u201d \u3000\u3000\u201c\u4f60\u8981\u63d0\u7684\u7b2c\u4e8c\u4e2a\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u8d75\u4e8c\u5824\uff01\u201d \u3000\u3000\u9a6c\u5f69\u4e91\u7acb\u65f6\u53cc\u988a\u80c0\u7ea2\uff0c\u4f4e\u4e0b\u4e86\u5934\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u6574\u4e86\u6574\u8138\u8272\u9053\uff1a\u201c\u4f60\u548c\u4ed6\u4e0d\u4f46\u6765\u5f80\u5bc6\u5207\uff0c\u5173\u7cfb\u66f4\u662f\u5bc6\u5207\uff0c\u8fd9\u65b9\u9762\u7528\u4e0d\u7740\u6211\u591a\u8bf4\u4e86 \u5427\uff1f\u201d \u3000\u3000\u9a6c\u5f69\u4e91\u5934\u5782\u5f97\u66f4\u4f4e\uff0c\u987f\u4e86\u987f\u9053\uff1a\u201c\u6c5f\u5927\u4fa0\u4e3a\u4ec0\u4e48\u5ffd\u7136\u63d0\u8d77\u4ed6\u6765\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u8bed\u6c14\u5e73\u548c\u7684\u9053\uff1a\u201c\u6211\u8981\u529e\u4e00\u4ef6\u4e8b\uff01\u201d \u3000\u3000\u201c\u529e\u4e00\u4ef6\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u8981\u4ed6\u6765\u4e00\u8d9f\u6f7c\u5173\u548c\u4f60\u89c1\u9762\u3002\u201d \u3000\u3000\u201c\u6211\u6709\u4ec0\u4e48\u8d44\u683c\u8981\u4ed6\u975e\u6765\u4e0d\u53ef\u5462\uff1f\u201d \u3000\u3000\u201c\u4eb2\u7b14\u5199\u4e00\u5c01\u4fe1\uff0c\u4ed6\u4e00\u5b9a\u4f1a\u6765\u3002\u81f3\u4e8e\u4fe1\u4e0a\u5199\u4e9b\u4ec0\u4e48\uff0c\u7531\u6c5f\u67d0\u51b3\u5b9a\uff0c\u4f60\u53ea\u8981\u7167\u6284\u5c31\u6210\u3002\u53ea \u8981\u5199\u4e86\u4fe1\uff0c\u4ed6\u6765\u4e0d\u6765\uff0c\u90a3\u5c31\u4e0d\u662f\u4f60\u7684\u8d23\u4efb\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u6211\u5199\u3002\u201d \u3000\u3000\u201c\u660e\u5929\u8fd8\u8981\u505a\u751f\u610f\uff0c\u4f60\u73b0\u5728\u53ef\u4ee5\u56de\u5230\u81ea\u5df1\u7684\u623f\u95f4\u4f11\u606f\u4e86\u3002\u201d \u3000\u3000\u63a5\u7740\uff0c\u6c5f\u5e72\u91cc\u518d\u5429\u5490\u5c0f\u71d5\u5b50\u7b49\u4eba\uff0c\u628a\u5929\u4e95\u4e2d\u201c\u6843\u6797\u5c45\u201d\u539f\u6709\u7684\u4eba\u5168\u89e3\u5f00\u7a74\u9053\uff0c\u5e76\u7531\u9a6c\u5f69 \u4e91\u4ecd\u4ee5\u4e3b\u4eba\u8eab\u4efd\u51fa\u9762\u5411\u4ed6\u4eec\u89e3\u8bf4\u3002 \u3000\u3000\u201c\u6843\u6797\u5c45\u201d\u8fd9\u4e9b\u4e0b\u4eba\uff0c\u5305\u62ec\u53a8\u623f\u505a\u83dc\u7684\u3001\u70e7\u996d\u7684\u3001\u8dd1\u5802\u7684\u3001\u3002\u6253\u6742\u7684\uff0c\u9664\u5c11\u6570\u51e0\u4e2a\u5c5e\u4e8e \u9b54\u6559\u6559\u4e86\u5916\uff0c\u5176\u4f59\u90fd\u662f\u96c7\u6765\u7684\u3002 \u3000\u3000\u4e3a\u4e86\u6d3b\u547d\uff0c\u4e3a\u4e86\u751f\u6d3b\uff0c\u4ed6\u4eec\u5f53\u7136\u613f\u610f\u5408\u4f5c\uff0c\u5c24\u5176\u8fde\u4ed6\u4eec\u7684\u4e3b\u6301\u4eba\u9a6c\u5f69\u4e91\u90fd\u5df2\u7ecf\u6295\u4e86\u964d\uff0c \u4ed6\u4eec\u8c01\u8fd8\u6562\u53cd\u6297\uff1f\u5f53\u4e0b\uff0c\u4e2a\u4e2a\u90fd\u4e0d\u58f0\u4e0d\u54cd\u7684\u56de\u53bb\u4f11\u606f\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u7279\u522b\u89c4\u5b9a\uff0c\u4e0d\u51c6\u4ed6\u4eec\u518d\u8fdb\u5165\u79d8\u9053\uff0c\u6240\u6709\u7684\u4eba\u90fd\u96c6\u4e2d\u5728\u540e\u9662\u91cc\u3002 \u3000\u3000\u4e0d\u4e45\uff0c\u738b\u5f64\u5e26\u7740\u60dc\u6625\u3001\u738b\u91cd\u5c71\u3001\u8d75\u4fdd\u3001\u9648\u5b8f\u6267\u884c\u4efb\u52a1\u5b8c\u6bd5\u56de\u6765\u4e86\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u4f17\u4eba\u53c8\u56de\u5230\u5ba2\u5385\u8bae\u4e8b\u3002 \u3000\u3000\u7ecf\u5546\u8bae\u7ed3\u679c\uff0c\u51b3\u5b9a\u4e86\u51e0\u4ef6\u4e8b\u60c5\u3002 \u3000\u3000\u4ed6\u4eec\u5e26\u6765\u7684\u4eba\u9a6c\uff0c\u534a\u6570\u4f4f\u5728\u5730\u4e0b\u79d8\u9053\u77f3\u5ba4\uff0c\u534a\u6570\u5206\u522b\u4f4f\u5728\u5730\u4e0a\u7684\u540e\u9662\u548c\u8de8\u9662\uff0c\u5e76\u5206\u522b\u8d1f \u8d23\u76d1\u89c6\u201c\u6843\u6797\u5c45\u201d\u539f\u6709\u7684\u4eba\u4ee5\u53ca\u5de5\u4f5c\u76d1\u7763\u3002 \u3000\u3000\u5176\u6b21\uff0c\u5c06\u4f4f\u5728\u5ba2\u6808\u7684\u4eba\u4e5f\u8c03\u56de\u201c\u6843\u6797\u5c45\u201d\uff0c\u6842\u98d8\u9999\u3001\u77f3\u69b4\u3001\u51e4\u4ed9\u4e09\u4eba\u5219\u5145\u4efb\u5973\u62db\u5f85\uff0c\u6b63 \u597d\u53ef\u5c06\u88ab\u6740\u7684\u51e0\u4eba\u4eba\u6570\u8865\u9f50\u3002 \u3000\u3000\u4e3a\u4e86\u76d1\u7763\u65b9\u4fbf\uff0c\u5c0f\u71d5\u5b50\u3001\u767d\u7fbd\u3001\u738b\u91cd\u5c71\u540c\u9648\u5b8f\u3001\u8d75\u4fdd\u7b49\u5e74\u8f7b\u4e00\u8f88\u7684\uff0c\u4e5f\u5168\u90fd\u914d\u5408\u7167\u6599\u5e97 \u52a1\u548c\u5de5\u4f5c\u3002 \u3000\u3000\u9a6c\u5f69\u4e91\u5f53\u665a\u4fbf\u5199\u597d\u4e86\u7ed9\u8d75\u4e8c\u5824\u7684\u4eb2\u7b14\u4fe1\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u7279\u6d3e\u7f57\u6c49\u4e03\u5de7\u50e7\u9001\u5f80\u5f00\u5c01\uff0c\u5f53\u7136\u5230\u8fbe\u5f00\u5c01\u4ee5\u540e\uff0c\u4e03\u5de7\u50e7\u5fc5\u5b9a\u518d\u8f6c\u624b\u9001\u5230\u8d75\u4e8c\u5824\u624b \u4e0a\uff0c\u81ea\u5df1\u4e0d\u4fbf\u51fa\u9762\u7684\u3002 \u3000\u3000\u5f53\u665a\u76f4\u5fd9\u5230\u56db\u66f4\u5c06\u5c3d\uff0c\u624d\u5404\u4f11\u606f\u3002 \u3000\u3000\u4e3a\u9632\u751f\u53d8\uff0c\u5e76\u4ecd\u6d3e\u51fa\u8b66\u6212\u548c\u5de1\u591c\u7684\u4eba\u3002 \u3000\u3000\u73b0\u5728\uff0c\u4e09\u516c\u4e3b\u3001\u738b\u5f64\u3001\u6c5f\u5343\u91cc\u4e00\u884c\u5341\u4f59\u4eba\u7b49\u4e8e\u5df2\u5728\u201c\u6843\u6797\u5c45\u201d\u957f\u4f4f\u4e0b\u6765\uff0c\u4ee5\u4fbf\u7b49\u5f85\u8d75\u4e8c \u5824\u5230\u8fbe\u3002 \u3000\u3000\u53e6\u5916\uff0c\u738b\u5f64\u6d3e\u51fa\u97e9\u6d9b\uff0c\u5feb\u9a6c\u52a0\u97ad\u8fdb\u4eac\u9762\u5448\u3002 \u3000\u3000\u9664\u4e86\u594f\u62a5\u4e09\u516c\u4e3b\u548c\u4ed6\u4eec\u4e00\u884c\u4eba\u5728\u6f7c\u5173\u7684\u73b0\u51b5\u5916\uff0c\u5e76\u8bf7\u793a\u662f\u5426\u8be5\u5bf9\u9a6c\u6587\u4e2d\u91c7\u53d6\u884c\u52a8\u3002 \u3000\u3000\u4e09\u65e5\u540e\uff0c\u7531\u7ec8\u5357\u5c71\u6765\u4e86\u4e24\u4eba\uff0c\u4ed6\u4eec\u8d6b\u7136\u662f\u5929\u96f7\u8001\u4eba\u5cb3\u5929\u96f7\u548c\u4e00\u9053\u957f\u8679\u82df\u6167\u6708\u3002 \u3000\u3000\u7b80\u76f4\u4ee4\u4eba\u96be\u4ee5\u76f8\u4fe1\uff0c\u5929\u96f7\u8001\u4eba\u7684\u4e24\u773c\u5df2\u7ecf\u5b8c\u5168\u590d\u660e\u3002\u636e\u82df\u6167\u6708\u8bf4\uff0c\u5929\u96f7\u8001\u4eba\u4f53\u5185\u7684\u5267\u6bd2 \u4e5f\u5df2\u65e0\u5f62\u6d88\u5931\uff0c\u6b66\u529f\u4e5f\u4e00\u5982\u5f80\u6614\u3002 \u3000\u3000\u5f53\u4e0b\uff0c\u6c5f\u5343\u91cc\u3001\u5c0f\u71d5\u5b50\u7b49\u4eba\u559c\u51fa\u671b\u5916\u7684\u628a\u5929\u96f7\u8001\u4eba\u548c\u82df\u6167\u6708\u63a5\u5f85\u5728\u5ba2\u5385\u91cc\uff0c\u5176\u4ed6\u7684\u4eba\u95fb \u8baf\u540e\u4e5f\u90fd\u6d8c\u8fdb\u5ba2\u5385\u62dc\u89c1\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u4e5f\u7acb\u5373\u8d76\u6765\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\uff0c\u4e00\u4f4d\u662f\u5979\u7684\u5e08\u7236\uff0c\u53e6\u4e00\u4f4d\u5219\u662f\u548c\u5e08\u7236\u5173\u7cfb\u6700\u5bc6\u5207\u7684\u4eba\uff0c\u5bf9\u5929\u96f7\u8001\u4eba\uff0c\u5979\u7167\u6837\u4e5f \u6267\u5f1f\u5b50\u793c\u62dc\u89c1\u3002 \u3000\u3000\u5f53\u665a\uff0c\u4e09\u516c\u4e3b\u8bbe\u4e0b\u76db\u7b75\uff0c\u4e3a\u5929\u96f7\u8001\u4eba\u548c\u82df\u6167\u6708\u63a5\u98ce\u6d17\u5c18\u3002 \u3000\u3000\u9152\u5e97\u662f\u81ea\u5df1\u5f00\u7684\uff0c\u201c\u6843\u6797\u5c45\u201d\u6709\u6700\u597d\u7684\u53a8\u5e08\uff0c\u8fd9\u987f\u7b75\u5e2d\u7684\u4e30\u76db\u7cbe\u81f4\uff0c\u81ea\u4e0d\u5f85\u8a00\u3002 \u3000\u3000\u5929\u96f7\u8001\u4eba\u548c\u82df\u6167\u6708\u56e0\u5df1\u591a\u5e74\u4e0d\u518d\u8fc7\u95ee\u6b66\u6797\u4e2d\u4e8b\uff0c\u672c\u6b32\u6b21\u65e5\u5373\u8fd4\u56de\u7ec8\u5357\u5c71\u5927\u8352\u6d1e\uff0c\u4f46\u7ecf\u4e0d \u4f4f\u4e09\u516c\u4e3b\u3001\u6c5f\u5343\u91cc\u3001\u738b\u5f64\u7b49\u4eba\u7684\u82e6\u82e6\u6073\u6c42\uff0c\u800c\u4e14\u8fd9\u6b21\u53c8\u662f\u4e3a\u671d\u5ef7\u51fa\u529b\u62a5\u6548\uff0c\u7ec8\u4e8e\u7b54\u5e94\u7559\u4e0b\u6765 \u52a9\u9635\u3002 \u3000\u3000\u6709\u4e86\u8fd9\u4e24\u4f4d\u5f53\u4ee3\u76d6\u4e16\u9ad8\u4eba\u7684\u76f8\u52a9\uff0c\u5bf9\u8fd9\u6b21\u5e73\u4e71\uff0c\u8d8a\u53d1\u7ed9\u4e09\u516c\u4e3b\u3001\u738b\u5f64\u3001\u6c5f\u5343\u91cc\u7b49\u4eba\u589e\u52a0 \u4e86\u65e0\u6bd4\u4fe1\u5fc3\u3002 \u3000\u3000\u534a\u4e2a\u6708\u540e\uff0c\u8d75\u4e8c\u5824\u771f\u7684\u6765\u5230\u6f7c\u5173\u3002 \u3000\u3000\u9a6c\u5f69\u4e91\u662f\u4ed6\u7684\u60c5\u5987\uff0c\u60c5\u5987\u6709\u6025\u4e8b\u7ea6\u4ed6\u524d\u6765\uff0c\u800c\u4ed6\u5bf9\u9a6c\u5f69\u4e91\u53c8\u65e9\u5c31\u601d\u5ff5\u4e0d\u5df2\uff0c\u7109\u6709\u4e0d\u6765\u4e4b \u7406\u3002 \u3000\u3000\u81f3\u4e8e\u524d\u5f80\u5f00\u5c01\u9001\u4fe1\u7684\u9ed1\u7f57\u6c49\u4e03\u5de7\u50e7\uff0c\u5728\u4efb\u52a1\u8fbe\u6210\u4e4b\u540e\u4e8e\u524d\u4e00\u65e5\u8fd4\u56de\u3002 \u3000\u3000\u8d75\u4e8c\u5824\u53ea\u5e26\u6709\u4e24\u540d\u4eb2\u4fe1\u968f\u540e\uff0c\u4ed6\u5148\u628a\u4e24\u540d\u968f\u4ece\u548c\u9a6c\u5339\u5b89\u7f6e\u5728\u5ba2\u6808\uff0c\u7136\u540e\u4e00\u4e2a\u4eba\u6765\u5230\u201c\u6843 \u6797\u5c45\u201d\u3002 \u3000\u3000\u201c\u6843\u6797\u5c45\u201d\u662f\u8d75\u4e8c\u5824\u5f80\u8fd4\u897f\u57df\u5fc5\u5b9a\u843d\u811a\u4e4b\u5904\uff0c\u5373\u4f7f\u4e0d\u5230\u897f\u57df\uff0c\u4ed6\u6bcf\u9694\u4e00\u6bb5\u65f6\u95f4\u4e5f\u5fc5\u5b9a\u62bd \u7a7a\u6765\u548c\u9a6c\u5f69\u4e91\u5e7d\u4f1a\u3002 \u3000\u3000\u56e0\u4e4b\uff0c\u5bf9\u8fd9\u91cc\u4ed6\u662f\u6700\u719f\u6089\u4e0d\u8fc7\u3002 \u3000\u3000\u8fdb\u4eba\u5e97\u95e8\uff0c\u5e97\u4f19\u4eec\u591a\u534a\u8fd8\u662f\u8001\u9762\u5b54\uff0c\u7eb5\u6709\u51e0\u4e2a\u964c\u751f\u9762\u5b54\uff0c\u4ed6\u4e5f\u4f1a\u8ba4\u4e3a\u662f\u65b0\u6765\u7684\uff0c\u5e76\u672a\u8d77 \u7591\u3002 \u3000\u3000\u9996\u5148\u8fce\u4e0a\u6765\u62db\u547c\u7684\u662f\u5c0f\u6885\uff0c\u5c0f\u6885\u56e0\u4e3a\u662f\u9a6c\u5f69\u4e91\u7684\u5fc3\u8179\uff0c\u4eba\u53c8\u751f\u5f97\u5a07\u4fcf\u4f36\u5229\uff0c\u8d75\u4e8c\u5824\u548c\u5979 \u6700\u719f\u6089\u4e0d\u8fc7\u3002 \u3000\u3000\u5c0f\u6885\u900f\u7740\u4eb2\u5207\u65e0\u6bd4\u7684\u7b11\u5bb9\uff0c\u65bd\u4e86\u4e00\u793c\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u98ce\u628a\u8d75\u7237\u5439\u6765\u4e86\uff0c\u5feb\u5230\u540e\u9662\u5ba2\u5385\u8bf7 \u5750\uff01\u201d \u3000\u3000\u8d75\u4e8c\u5824\u4e5f\u5806\u7740\u7b11\u8138\u9053\uff1a\u201c\u8001\u677f\u5a18\u5728\u5417\uff1f\u201d \u3000\u3000\u201c\u5728\u91cc\u9762\uff0c\u5a62\u5b50\u73b0\u5728\u53bb\u901a\u77e5\u5979\u8d75\u7237\u6765\u4e86\uff01\u201d \u3000\u3000\u201c\u4e0d\u5fc5\u5566\uff01\u6211\u76f4\u63a5\u5230\u5979\u5c4b\u91cc\u53bb\u4e5f\u662f\u4e00\u6837\u3002\u201d \u3000\u3000\u9a6c\u5f69\u4e91\u7684\u5367\u623f\uff0c\u8d75\u4e8c\u5824\u662f\u8f7b\u8f66\u719f\u8def\u3002 \u3000\u3000\u4ed6\u6765\u5230\u540e\u9662\uff0c\u4fbf\u63ed\u5e18\u76f4\u5165\u3002 \u3000\u3000\u53ea\u89c1\u9a6c\u5f69\u4e91\u6b63\u5728\u5bf9\u955c\u68b3\u5986\u3002 \u3000\u3000\u522b\u770b\u8d75\u4e8c\u5824\u9762\u56e2\u4e00\u526f\u65af\u6587\u548c\u6c14\u6a21\u6837\uff0c\u89c1\u4e86\u9a6c\u5f69\u53bb\u5374\u662f\u4e11\u6001\u6bd5\u9732\uff0c\u997f\u864e\u6251\u7f8a\u822c\u7684\u5c31\u628a\u9a6c\u5f69 \u4e91\u7531\u540e\u9762\u62e6\u8170\u62b1\u4f4f\u3002 \u3000\u3000\u9a6c\u5f69\u4e91\u88ab\u5413\u4e86\u4e00\u8df3\uff0c\u4f46\u5374\u8fd8\u662f\u55f2\u58f0\u95ee\u9053\uff1a\u201c\u662f\u54ea\u4e00\u4f4d\u8fd9\u6837\u4e0d\u8001\u5b9e\uff1f\u201d \u3000\u3000\u8d75\u4e8c\u5824\u563f\u563f\u7b11\u9053\uff1a\u201c\u5c0f\u5b9d\u8d1d\uff0c\u9664\u4e86\u6211\uff0c\u8c01\u6562\u5bf9\u4f60\u8fd9\u6837\u4eb2\u70ed\uff1f\u201d \u3000\u3000\u201c\u539f\u6765\u662f\u8d75\u7237\uff0c\u5feb\u653e\u624b\uff01\u6211\u5012\u8336\u7ed9\u6211\u559d\uff01\u201d \u3000\u3000\u8d75\u4e8c\u5824\u677e\u5f00\u624b\uff0c\u5f84\u81ea\u5728\u5e8a\u6cbf\u5750\u4e0b\uff0c\u4e24\u773c\u4f9d\u7136\u4e0d\u505c\u7684\u5728\u9a6c\u5f69\u4e91\u8eab\u4e0a\u8f6c\u6765\u8f6c\u53bb\u3002 \u3000\u3000\u9a6c\u5f69\u4e91\u7528\u5feb\u52a8\u4f5c\u68b3\u597d\u5934\uff0c\u6574\u597d\u5986\uff0c\u8fde\u5fd9\u5012\u4e86\u4e00\u676f\u70ed\u8336\u5949\u4e0a\uff0c\u4e00\u9762\u7b11\u9765\u8fce\u4eba\u7684\u9053\uff1a\u201c\u4eba\u5bb6 \u8fd8\u4ee5\u4e3a\u4f60\u4e0d\u6765\u4e86\u5462\uff01\u201d \u3000\u3000\u8d75\u4e8c\u5824\u63a5\u8fc7\u8336\uff0c\u6d8e\u7740\u8138\u9053\uff1a\u201c\u5927\u59b9\u5b50\u4e0b\u4ee4\u8981\u6211\u6765\uff0c\u5c31\u50cf\u5723\u65e8\u4e00\u6837\uff0c\u6211\u8d75\u4e8c\u5824\u600e\u6562\u4e0d\u6765 \u5462\uff1f\u201d \u3000\u3000\u201c\u4e00\u8def\u8f9b\u82e6\u4e86\uff01\u201d \u3000\u3000\u201c\u53ea\u8981\u80fd\u770b\u5230\u4f60\uff0c\u518d\u8f9b\u82e6\u4e5f\u503c\u5f97\uff01\u5927\u59b9\u5b50\uff0c\u4fe1\u4e0a\u53ea\u8bf4\u6709\u8981\u7d27\u7684\u4e8b\u8981\u6211\u6765\uff0c\u4e5f\u4e0d\u8bf4\u660e\u767d\uff0c \u7a76\u7adf\u662f\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u8fd8\u4e0d\u662f\u4eba\u5bb6\u60f3\u4f60\u3002\u201d \u3000\u3000\u8d75\u4e8c\u5824\u54a7\u5634\u7b11\u7740\u9053\uff1a\u201c\u54b1\u4eec\u771f\u662f\u5fc3\u5fc3\u76f8\u5370\uff0c\u4f60\u60f3\u6211\uff0c\u6211\u53c8\u4f55\u5c1d\u4e0d\u60f3\u4f60\u3002\u6765\uff01\u73b0\u5728\u5c31\u4e0a\u5e8a \u53d9\u53d9\u65e7\u60c5\u5427\uff01\u201d \u3000\u3000\u9a6c\u5f69\u4e91\u659c\u7768\u4e86\u8d75\u4e8c\u5824\u4e00\u773c\uff0c\u9053\uff1a\u201c\u65e2\u7136\u6765\u4e86\uff0c\u4f55\u5fc5\u8fd9\u4e48\u6025\uff1f\u73b0\u5728\u662f\u5927\u767d\u5929\uff0c\u4ed6\u4eec\u968f\u65f6\u90fd \u4f1a\u6709\u4e8b\u6765\u627e\u6211\uff0c\u7b49\u591c\u665a\u751f\u610f\u6253\u4e86\u70ca\uff0c\u90a3\u65f6\u968f\u4f60\u600e\u4e48\u6837\u4fbf\u600e\u4e48\u6837\u3002\u201d \u3000\u3000\u201c\u8bf4\u7684\u4e5f\u662f\uff0c\u8fd1\u6765\u751f\u610f\u8fd8\u4e0d\u9519\u5427\uff1f\u201d \u3000\u3000\u201c\u8fd8\u4e0d\u662f\u8001\u6837\u5b50\u3002\u201d \u3000\u3000\u8d75\u4e8c\u5824\u5ffd\u7136\u795e\u8272\u53d8\u5f97\u6b63\u7ecf\u8d77\u6765\uff0c\u9053\uff1a\u201c\u6211\u8981\u544a\u8bc9\u4f60\u4e00\u4ef6\u4e8b\uff0c\u4f60\u4e00\u5411\u6d88\u606f\u7075\u901a\uff0c\u4e5f\u8bb8\u65e9\u5c31 \u542c\u5230\u8033\u98ce\u4e86\u5427\uff1f\u201d \u3000\u3000\u9a6c\u5f69\u4e91\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u8fd9\u4e48\u9b3c\u9b3c\u795f\u795f\u7684\uff1f\u201d \u3000\u3000\u201c\u5927\u5185\u4f8d\u536b\u7edf\u9886\u738b\u5f64\u5e26\u7740\u4e0d\u5c11\u624b\u4e0b\u6708\u524d\u66fe\u5230\u8fc7\u5f00\u5c01\uff0c\u7136\u540e\u53c8\u8f6c\u5f80\u6d1b\u9633\uff0c\u786c\u662f\u628a\u9c7c\u5316\u9f99\u65b0 \u8bbe\u7acb\u7684\u6c34\u9646\u4e24\u5be8\u903c\u8d70\uff0c\u5c24\u5176\u9646\u5be8\u4f24\u4ea1\u60e8\u91cd\uff0c\u8fde\u5be8\u4e3b\u767d\u864e\u90fd\u6b7b\u5728\u4ed6\u4eec\u624b\u4e2d\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e8b\u6211\u4e5f\u542c\u5230\u4e00\u4e9b\uff0c\u4f60\u4e3a\u4ec0\u4e48\u5ffd\u7136\u63d0\u8d77\u8fd9\u4ef6\u4e8b\u6765\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u738b\u5f64\u4e00\u4f19\u4eba\uff0c\u636e\u8bf4\u5e76\u672a\u56de\u4eac\uff0c\u8bf4\u4e0d\u5b9a\u4f1a\u5728\u6f7c\u5173\u51fa\u73b0\uff0c\u6240\u4ee5\u4f60\u8981\u7279\u522b\u5c0f\u5fc3\u3002\u201d \u3000\u3000\u201c\u4f60\u653e\u5fc3\uff0c\u51ed\u6211\u9a6c\u5f69\u4e91\uff0c\u8fd8\u4e0d\u81f3\u4e8e\u5403\u4ed6\u4eec\u7684\u4e8f\uff0c\u3002\u7ec8\u5357\u5206\u575b\u9ec4\u575b\u4e3b\u4e5f\u4e3a\u8fd9\u4e8b\u52a0\u5f3a\u4e86\u6212 \u5907\u3002 \u3000\u3000\u9a6c\u5f69\u4e91\u8bf4\u5230\u8fd9\u91cc\uff0c\u7ad9\u8d77\u8eab\u6765\u9053\uff1a\u201c\u4f60\u5148\u5750\u4f1a\uff0c\u559d\u676f\u8336\uff0c\u6211\u8fd8\u8981\u5230\u5916\u9762\u4ea4\u4ee3\u4ed6\u4eec\u51e0\u4ef6\u4e8b\uff0c \u9a6c\u4e0a\u5c31\u56de\u6765\u966a\u4f60\u3002\u201d \u3000\u3000\u8d75\u4e8c\u5824\u559d\u4e86\u53e3\u8336\u9053\uff1a\u201c\u4f60\u53bb\u5427\uff01\u8981\u5feb\u4e9b\u56de\u6765\uff01\u201d \u3000\u3000\u8fd9\u65f6\u6c5f\u5343\u91cc\u548c\u738b\u5f64\u7b49\u4eba\u65e9\u5df2\u5f97\u77e5\u8d75\u4e8c\u5824\u5230\u6765\uff0c\u53ea\u56e0\u8d75\u4e8c\u5824\u5728\u9a6c\u5f69\u4e91\u623f\u91cc\uff0c\u552f\u6050\u4f24\u53ca\u9a6c\u5f69 \u4e91\uff0c\u4e0d\u4fbf\u4e0b\u624b\u800c\u5df2\u3002 \u3000\u3000\u9a6c\u5f69\u4e91\u6765\u5230\u5916\u9762\uff0c\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u7b49\u4eba\u65e9\u5df2\u7b49\u5019\u5728\u5ba2\u5385\u91cc\u3002 \u3000\u3000\u9a6c\u5f69\u4e91\u89c1\u4e86\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u4fbf\u9053\uff1a\u201c\u4eba\u5728\u6211\u623f\u91cc\uff0c\u73b0\u5728\u738b\u5927\u4eba\u548c\u6c5f\u5927\u4fa0\u5c31\u8bf7\u5feb\u5feb\u91c7\u53d6\u884c\u52a8 \u5427\uff01\u201d \u3000\u3000\u738b\u5f64\u9053\uff1a\u201c\u4f60\u5c3d\u53ef\u51b7\u4e0d\u9632\u70b9\u4e86\u4ed6\u7684\u7a74\u9053\u3002\u201d \u3000\u3000\u9a6c\u5f69\u4e91\u5e26\u7740\u6b49\u7136\u795e\u8272\u9053\uff1a\u201c\u4ed6\u7684\u6b66\u529f\u5b9e\u5728\u592a\u9ad8\u4e86\uff0c\u4e07\u2014\u4e00\u51fb\u4e0d\u4e2d\uff0c\u6211\u8fd9\u6761\u547d\u53ea\u6015\u5c31\u6ca1\u6709 \u4e86\u3002\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u90a3\u5c31\u7531\u54b1\u4eec\u81ea\u5df1\u52a8\u624b\u5427\uff01\u201d \u3000\u3000\u4e8e\u662f\uff0c\u6c5f\u5343\u91cc\u3001\u738b\u5f64\u3001\u5c0f\u71d5\u5b50\u4ee5\u53ca\u9a6c\u5f69\u4e91\u5168\u6765\u5230\u5929\u4e95\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u5411\u9a6c\u5f69\u4e91\u4f7f\u4e86\u4e2a\u773c\u8272\u3002 \u3000\u3000\u9a6c\u5f69\u4e91\u7acb\u5373\u5411\u5c4b\u5185\u53eb\u9053\uff1a\u201c\u8d75\u7237\uff0c\u6709\u4eba\u6765\u627e\u4f60\u4e86\uff01\u201d \u3000\u3000\u8d75\u4e8c\u5824\u6765\u5230\u95e8\u53e3\uff0c\u5f53\u4ed6\u4e00\u773c\u77a5\u89c1\u95e8\u5916\u4f17\u4eba\uff0c\u4e0d\u7531\u8138\u8272\u5927\u53d8\u3002 \u3000\u3000\u4f46\u4ed6\u5f88\u5feb\u4fbf\u53c8\u9547\u5b9a\u4e0b\u6765\uff0c\u671b\u7740\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u4e00\u62b1\u62f3\u9053\uff1a\u201c\u539f\u6765\u662f\u738b\u5927\u4eba\u548c\u6c5f\u5927\u4fa0\uff0c\u5e78 \u4f1a\uff0c\u5e78\u4f1a\uff01\u201d \u3000\u3000\u738b\u5f64\u559d\u9053\uff1a\u201c\u8d75\u4e8c\u5824\uff0c\u7528\u4e0d\u7740\u518d\u6765\u8fd9\u4e00\u5957\uff0c\u4f60\u662f\u4e56\u4e56\u53d7\u7f1a\u5462\uff1f\u8fd8\u662f\u4ecd\u60f3\u53cd\u6297\uff1f\u201d \u3000\u3000\u60c5\u52bf\u81f3\u6b64\uff0c\u8d75\u4e8c\u5824\u5df2\u7528\u4e0d\u7740\u518d\u88c5\u6a21\u505a\u6837\u4e86\u3002 \u3000\u3000\u53ea\u542c\u4ed6\u51b7\u7b11\u4e86\u51e0\u58f0\u9053\uff1a\u201c\u4f60\u4eec\u60f3\u62ff\u5c31\u62ff\u5427\uff01\u8d75\u67d0\u4eba\u867d\u7136\u843d\u4e86\u5355\uff0c\u4e5f\u7edd\u4e0d\u4f1a\u5411\u4f60\u4eec\u5c48\u670d \u7684\uff01\u201d \u3000\u3000\u63a5\u7740\uff0c\u624b\u6307\u9a6c\u5f69\u4e91\u5927\u9a82\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u8d31\u4eba\uff0c\u539f\u6765\u4f60\u662f\u4e3a\u4e86\u8fd9\u4e2a\u624d\u628a\u6211\u9a97\u4e86\u6765\uff0c\u4f60\u8eab\u4e3a\u5723 \u6559\u5802\u4e3b\uff0c\u7adf\u7136\u505a\u51fa\u8fd9\u79cd\u5927\u9006\u4e0d\u9053\u7684\u4e8b\u6765\uff01\u201d \u3000\u3000\u9a6c\u5f69\u4e91\u51b7\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u8d75\u7237\uff0c\u53ef\u60dc\u4f60\u73b0\u5728\u660e\u767d\u5df2\u7ecf\u665a\u4e86\uff01\u201d\u3002 \u3000\u3000\u8d75\u4e8c\u5824\u54ac\u4e86\u54ac\u7259\u9053\uff1a\u201c\u5f85\u4f1a\u513f\u8d75\u67d0\u4eba\u5b9a\u628a\u4f60\u8fd9\u8d31\u4eba\u788e\u5c38\u4e07\u6bb5\uff01\u201d \u3000\u3000\u201c\u90a3\u5c31\u5f85\u4f1a\u513f\u518d\u8c08\u5427\uff01\u201d \u3000\u3000\u8d75\u4e8c\u5824\u6599\u5b9a\u4f9d\u76ee\u524d\u60c5\u52bf\uff0c\u82e5\u51b2\u5230\u5929\u4e95\uff0c\u52bf\u5fc5\u65e0\u6cd5\u62db\u67b6\uff0c\u53ea\u6709\u5728\u95e8\u5185\u5f85\u654c\u65b9\u4e3a\u4e0a\u7b56\u3002\u56e0\u4e3a \u8fd9\u6837\u5bf9\u65b9\u53ea\u80fd\u51b2\u8fdb\u6765\u4e00\u4eba\uff0c\u4ee5\u4e00\u5bf9\u4e00\uff0c\u81f3\u5c11\u53ef\u4ee5\u81ea\u4fdd\uff0c\u82e5\u80fd\u5c06\u6765\u4eba\u5236\u4f4f\u4f5c\u4e3a\u4eba\u8d28\uff0c\u8bf4\u4e0d\u5b9a\u5c31 \u53ef\u8131\u51fa\u91cd\u56f4\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u4ed6\u731b\u5438\u4e00\u53e3\u771f\u6c14\uff0c\u5168\u795e\u6212\u5907\uff0c\u53ea\u5f85\u5bf9\u65b9\u6765\u653b\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u671b\u4e86\u5c0f\u71d5\u5b50\u4e00\u773c\u9053\uff1a\u201c\u8fdb\u53bb\u628a\u4ed6\u62ff\u4e0b\uff0c\u8981\u6d3b\u7684\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u5e94\u4e86\u4e00\u58f0\uff0c\u5927\u6b65\u5411\u95e8\u5185\u8d70\u53bb\u3002 \u3000\u3000\u8d75\u4e8c\u5824\u53cd\u800c\u6709\u4e9b\u6014\u4f4f\uff01\u6c5f\u5e72\u91cc\u600e\u4f1a\u8981\u4e00\u4e2a\u65e0\u540d\u5c0f\u5352\u524d\u6765\u9001\u6b7b\uff0c\u83ab\u975e\u5176\u4e2d\u6709\u8bc8\uff1f\u4ed6\u5fc3\u5ff5\u5c1a \u672a\u8f6c\u5b8c\uff0c\u5c0f\u71d5\u5b50\u65e9\u5df2\u51b2\u4e86\u8fdb\u6765\uff0c\u4e00\u51fa\u624b\u4fbf\u8feb\u5f97\u8d75\u4e8c\u5824\u8fde\u9000\u4e24\u6b65\uff0c\u5927\u611f\u5403\u60ca\uff01 \u3000\u3000\u8d75\u4e8c\u5824\u867d\u7136\u8eab\u624b\u5947\u9ad8\uff0c\u4f46\u662f\u9047\u4e0a\u4e86\u5929\u96f7\u8001\u4eba\u7684\u8863\u94b5\u4f20\u4eba\u5c0f\u71d5\u5b50\uff0c\u5374\u5b9e\u5728\u662f\u96be\u4ee5\u6297\u8861\u3002\u4e24 \u4eba\u5728\u5c4b\u5185\u5bf9\u62c6\u4e86\u4e0d\u5230\u5341\u62db\uff0c\u8d75\u4e8c\u5824\u4fbf\u88ab\u5c0f\u71d5\u5b50\u4e00\u6307\u70b9\u4e2d\u9ebb\u7a74\uff0c\u91cd\u91cd\u7684\u6454\u5012\u5730\u4e0a\uff0c\u518d\u4e5f\u722c\u4e0d\u8d77 \u6765\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u53eb\u9053\uff1a\u201c\u628a\u4ed6\u5e26\u5230\u5ba2\u5385\uff01\u201d \u3000\u3000\u5c0f\u71d5\u5b50\u63d0\u8d77\u8d75\u4e8c\u5824\uff0c\u5230\u8fbe\u5ba2\u5385\uff0c\u518d\u628a\u4ed6\u6454\u56de\u5730\u4e0a\u3002 \u3000\u3000\u8fd9\u65f6\u6c5f\u5343\u91cc\u548c\u738b\u5f64\u65e9\u5df2\u5728\u5ba2\u5385\u5750\u597d\u3002 \u3000\u3000\u7ad9\u5728\u4e00\u65c1\u7684\u5c1a\u6709\u9a6c\u5f69\u4e91\u3001\u767d\u7fbd\u3001\u60dc\u6625\u7b49\u4eba\u3002 \u3000\u3000\u738b\u5f64\u7b11\u9053\uff1a\u201c\u8d75\u4e8c\u5824\uff0c\u60f3\u4e0d\u5230\u5427\uff1f\u73b0\u5728\u8fd8\u6709\u4ec0\u4e48\u8bdd\u8bb2\uff1f\u201d \u3000\u3000\u8d75\u4e8c\u5824\u6012\u76ee\u6a2a\u7709\u7684\u9053\uff1a\u201c\u738b\u5927\u4eba\uff0c\u4f60\u8eab\u4e3a\u5927\u5185\u4f8d\u536b\u7edf\u9886\uff0c\u4e3a\u4ec0\u4e48\u77e5\u6cd5\u72af\u6cd5\uff1f\u201d \u3000\u3000\u738b\u5f64\u51b7\u7b11\u9053\uff1a\u201c\u738b\u67d0\u72af\u7684\u4ec0\u4e48\u6cd5\uff0c\u4f60\u8bf4\uff01\u201d \u3000\u3000\u201c\u8d75\u67d0\u53ea\u662f\u4e00\u540d\u5bfb\u5e38\u767e\u59d3\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u5bf9\u4ed8\u6211\uff1f\u201d \u3000\u3000\u201c\u59d3\u8d75\u7684\uff0c\u4f60\u672a\u514d\u592a\u5ba2\u6c14\u4e86\u5427\uff1f\u5929\u4e0b\u53ef\u6709\u4f60\u8fd9\u6837\u7684\u5bfb\u5e38\u767e\u59d3\u5417\uff1f\u201d \u3000\u3000\u201c\u4f60\u8bf4\u8d75\u67d0\u90a3\u4e00\u70b9\u4e0d\u5bfb\u5e38\uff1f\u201d \u3000\u3000\u201c\u4f60\u6562\u8bf4\u4f60\u4e0d\u662f\u897f\u57df\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u4f46\u897f\u57df\u4e5f\u662f\u5927\u660e\u56fd\u571f\uff0c\u897f\u57df\u7684\u4eba\u6765\u5230\u4e2d\u539f\u72af\u6cd5\u5417\uff1f\u201d \u3000\u3000\u201c\u4f60\u6562\u8bf4\u4e0d\u662f\u9a6c\u6587\u4e2d\u7684\u540c\u515a\uff1f\u4f60\u9886\u5bfc\u6570\u767e\u540d\u624b\u4e0b\u4e13\u66ff\u9a6c\u6587\u4e2d\u5356\u547d\uff0c\u96be\u9053\u8fd8\u80fd\u5426\u8ba4\uff1f\u201d \u3000\u3000\u201c\u738b\u5927\u4eba\uff0c\u5c31\u51ed\u4f60\u8bb2\u8fd9\u79cd\u8bdd\uff0c\u5c31\u53ef\u8bc1\u660e\u662f\u5b58\u5fc3\u548c\u6211\u8d75\u67d0\u4eba\u8fc7\u4e0d\u53bb\uff01\u201d \u3000\u3000\u201c\u738b\u67d0\u542c\u4e0d\u61c2\u4f60\u5728\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u9a6c\u5927\u4eba\u662f\u671d\u5ef7\u7684\u547d\u5b98\uff0c\u4ed6\u7684\u5b98\u804c\u662f\u5f53\u4eca\u7687\u4e0a\u5c01\u8d4f\u7684\uff0c\u8d75\u67d0\u534f\u52a9\u4ed6\u7ef4\u62a4\u5730\u65b9\u6cbb\u5b89\uff0c\u505a\u7684 \u662f\u4ef6\u5927\u5927\u6709\u529f\u7684\u4e8b\uff0c\u738b\u5927\u4eba\u65b9\u624d\u8bf4\u90a3\u79cd\u8bdd\uff0c\u672a\u514d\u592a\u5c82\u6709\u6b64\u7406\u4e86\u5427\uff1f\u201d \u3000\u3000\u201c\u8d75\u4e8c\u5824\uff0c\u4f60\u5c31\u518d\u72e1\u8fa9\uff0c\u4e5f\u534a\u70b9\u7528\u6ca1\u6709\uff0c\u4f60\u4ee5\u4e3a\u738b\u67d0\u5f04\u4e0d\u6e05\u9a6c\u6587\u4e2d\u7684\u51fa\u8eab\u6765\u5386\uff1f\u4ed6\u6839\u672c \u5c31\u662f\u897f\u57df\u9b54\u6559\u6d3e\u6765\u56fe\u8c0b\u98a0\u8986\u5927\u660e\u6c5f\u5c71\u7684\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u5982\u6b64\uff0c\u4f60\u4eec\u4e3a\u4ec0\u4e48\u5728\u5f00\u5c01\u65f6\u4e0d\u5bf9\u4ed6\u4e0b\u624b\uff1f\u201d \u3000\u3000\u201c\u90a3\u662f\u65f6\u673a\u672a\u5230\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\uff0c\u73b0\u5728\u662f\u65f6\u673a\u5230\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u5c0a\u9a7e\u662f\u9a6c\u6587\u4e2d\u7684\u5de6\u53f3\u624b\uff0c\u7b49\u738b\u67d0\u628a\u4ed6\u7684\u515a\u7fbd\u5404\u4e2a\u6d88\u706d\uff0c\u4e5f\u5c31\u662f\u9a6c\u6587\u4e2d\u89e3\u4eac\u53d7\u5ba1 \u7684\u65f6\u5019\u5230\u4e86\u3002\u201d \u3000\u3000\u8d75\u4e8c\u5824\u6012\u76ee\u5706\u7741\uff0c\u8138\u5b54\u53d1\u9752\uff0c\u4f46\u5374\u4e0d\u518d\u5f00\u53e3\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u201c\u738b\u8001\u5f1f\u4e0d\u5fc5\u518d\u95ee\u4e86\uff0c\u4e0d\u59a8\u628a\u4ed6\u62bc\u5230\u5730\u9053\u5185\u77f3\u5ba4\u4e2d\u518d\u8bf4\u3002\u201d \u3000\u3000\u738b\u5f64\u5bf9\u6c5f\u5343\u91cc\u4e00\u5411\u8a00\u542c\u8ba1\u4ece\uff0c\u968f\u5373\u5c06\u8d75\u4fdd\u3001\u9648\u5b8f\u53eb\u6765\uff0c\u628a\u8d75\u4e8c\u5824\u62bc\u8fdb\u4e86\u5730\u9053\u5185\u7684\u77f3\u5ba4\u3002 \u3000\u3000\u738b\u5f64\u8fd9\u624d\u95ee\u9053\uff1a\u201c\u6c5f\u5144\u4e3a\u4ec0\u4e48\u4e0d\u8ba9\u5144\u5f1f\u591a\u95ee\u4ed6\u51e0\u53e5\u8bdd\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u9053\uff1a\u4e00\u96be\u9053\u8001\u5f1f\u8fd8\u770b\u4e0d\u51fa\uff0c\u6b64\u4eba\u5bf9\u897f\u57df\u9b54\u6559\u5df2\u4e2d\u9b54\u592a\u6df1\u4e86\uff0c\u60f3\u8981\u4ed6\u5f03\u6697\u6295\u660e\uff0c\u6839 \u672c\u662f\u4e0d\u53ef\u80fd\u7684\u4e8b\uff0c\u8001\u5f1f\u4e00\u65e6\u5bf9\u4ed6\u903c\u8feb\u592a\u751a\uff0c\u4ed6\u5f88\u53ef\u80fd\u5c31\u4f1a\u81ea\u884c\u4e86\u65ad\uff0c\u5230\u90a3\u65f6\u5019\u5c82\u4e0d\u5f04\u5de7\u6210 \u62d9\uff1f\u201c \u3000\u3000\u201c\u6c5f\u5144\u7684\u610f\u601d\u662f\u60f3\u628a\u4ed6\u6682\u65f6\u7559\u4e0b\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u7559\u4e0b\u4ed6\u505a\u4eba\u8d28\uff0c\u5c06\u6765\u5bf9\u4ed8\u9a6c\u6587\u4e2d\uff0c\u54b1\u4eec\u5fc5\u5b9a\u53ef\u4ee5\u7701\u4e0b\u4e0d\u5c11\u529b\u91cf\u3002\u9a6c\u6587\u4e2d\u624b\u4e0b\u7684 \u529b\u91cf\uff0c\u8d75\u4e8c\u5824\u548c\u9c7c\u5316\u9f99\u662f\u4e24\u5927\u652f\u67f1\uff0c\u73b0\u5728\u9a6c\u6587\u4e2d\u5931\u53bb\u8d75\u4e8c\u5824\u8fd9\u4e00\u81c2\u52a9\uff0c\u4e5f\u5c31\u53ea\u80fd\u9760\u9c7c\u5316\u9f99 \u4e86\u3002\u201d \u3000\u3000\u201c\u662f\u5426\u8981\u5148\u6d88\u706d\u4e86\u9c7c\u5316\u9f99\uff0c\u624d\u80fd\u5bf9\u9a6c\u6587\u4e2d\u4e0b\u624b\uff1f\u201d \u3000\u3000\u201c\u5e0c\u671b\u5982\u6b64\uff0c\u4f46\u5374\u4e0d\u5bb9\u6613\u505a\u5230\u3002\u9a6c\u6587\u4e2d\u5e76\u975e\u7b49\u95f2\u4eba\u7269\uff0c\u9c7c\u5316\u9f99\u4e5f\u4e0d\u50cf\u8d75\u4e8c\u5824\u8fd9\u4e48\u5bb9\u6613\u5c31 \u624b\u5230\u64d2\u6765\uff0c\u54b1\u4eec\u53ea\u80fd\u8d70\u4e00\u6b65\u7b97\u4e00\u6b65\u4e86\u3002\u201d \u3000\u3000\u201c\u97e9\u6d9b\u521a\u8fdb\u4eac\u9762\u5723\u4e0d\u4e45\uff0c\u81f3\u5c11\u8981\u5341\u5929\u540e\u624d\u80fd\u56de\u6765\uff0c\u5728\u8fd9\u671f\u95f4\u6c5f\u5144\u8ba4\u4e3a\u54b1\u4eec\u662f\u5426\u8fd8\u8be5\u6709\u522b \u7684\u884c\u52a8\uff1f\u201d \u3000\u3000\u201c\u4e00\u52a8\u4e0d\u5982\u4e00\u9759\u3002\u5426\u5219\u82e5\u9a6c\u6587\u4e2d\u5148\u6709\u5fc3\u91cc\u51c6\u5907\uff0c\u4ee5\u540e\u7684\u884c\u52a8\u53cd\u800c\u4e0d\u6613\u8fdb\u884c\u4e86\u3002 \u3000\u3000\u53c8\u8c08\u4e86\u4e00\u4f1a\u513f\uff0c\u4f17\u4eba\u4fbf\u5404\u81ea\u79bb\u5f00\u5ba2\u5385\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u7b49\u4e00\u884c\u4eba\u4f17\u4e00\u76f4\u5728\u201c\u6843\u6797\u5c45\u201d\u6df1\u5c45\u7b80\u51fa\u3002 \u3000\u3000\u201c\u6843\u6797\u5c45\u201d\u7684\u751f\u610f\u5219\u662f\u7167\u5e38\u8425\u4e1a\u3002 \u3000\u3000\u4e00\u5411\u56db\u6d77\u4e3a\u5bb6\u884c\u8e2a\u4e0d\u5b9a\u7684\u6c5f\u5343\u91cc\u7b49\u4eba\uff0c\u8fd8\u662f\u9996\u6b21\u5c1d\u5230\u8fd9\u79cd\u86f0\u5c45\u751f\u6d3b\u3002 \u3000\u3000\u5341\u65e5\u540e\uff0c\u6d3e\u8d74\u4eac\u5e08\u7684\u97e9\u6d9b\u5feb\u9a6c\u52a0\u97ad\u8fd4\u56de\u6f7c\u5173\u3002 \u3000\u3000\u4ed6\u5e26\u6765\u7687\u4e0a\u7684\u5bc6\u65e8\uff0c\u8981\u738b\u5f64\u7b49\u4eba\u5bf9\u9a6c\u6587\u4e2d\u91c7\u53d6\u884c\u52a8\uff0c\u7136\u540e\u89e3\u4eac\u95ee\u7f6a\uff0c\u82e5\u5728\u8feb\u4e0d\u5f97\u5df2\u60c5\u5f62 \u4e0b\uff0c\u4e5f\u4e0d\u59a8\u5148\u65a9\u540e\u594f\u3002 \u3000\u3000\u53e6\u5916\uff0c\u7687\u4e0a\u53c8\u9881\u53d1\u4e86\u51e0\u9053\u8c03\u6d3e\u5c11\u6797\u3001\u6b66\u5f53\u65b9\u9762\u534f\u52a9\u5e73\u4e71\u7684\u5bc6\u65e8\uff0c\u4e00\u5e76\u4ea4\u7ed9\u738b\u5f64\u5904\u7406\u3002 \u3000\u3000\u738b\u5f64\u7acb\u5373\u624b\u6367\u5bc6\u65e8\u5f80\u89c1\u4e09\u516c\u4e3b\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u5e76\u672a\u591a\u8bf4\u4ec0\u4e48\uff0c\u53ea\u4ea4\u4ee3\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u5546\u8bae\u5982\u4f55\u884c\u4e8b\u3002 \u3000\u3000\u738b\u5f64\u518d\u6765\u89c1\u6c5f\u5343\u91cc\u3002 \u3000\u3000\u6c5f\u5343\u91cc\u4f3c\u5df2\u80f8\u6709\u6210\u7af9\uff0c\u8981\u738b\u5f64\u5c06\u4eba\u9a6c\u7acb\u5373\u8d77\u7a0b\u5148\u884c\u5f00\u5f80\u6d1b\u9633\u3002 \u3000\u3000\u738b\u5f64\u518d\u5f81\u8be2\u6c5f\u5343\u91cc\u7684\u610f\u89c1\u9053\uff1a\u201c\u662f\u5426\u8be5\u5148\u6d3e\u4eba\u6301\u7687\u4e0a\u5bc6\u65e8\u5230\u5c11\u6797\u3001\u6b66\u5f53\u65b9\u9762\u8c03\u6d3e\u9ad8\u624b\u4e5f \u5230\u6d1b\u9633\u4f1a\u5408\u5462\uff1f\u201d \u3000\u3000\u6c5f\u5343\u91cc\u6447\u5934\u9053\uff1a\u201c\u6700\u597d\u4e0d\u5fc5\uff0c\u9a6c\u6587\u4e2d\u548c\u9c7c\u5316\u9f99\u773c\u7ebf\u4f17\u591a\uff0c\u4e00\u65e6\u5c11\u6797\u3001\u6b66\u5f53\u65b9\u9762\u8d76\u6765\u652f \u63f4\uff0c\u53cd\u800c\u5bb9\u6613\u6d88\u606f\u5916\u6cc4\u3002\u201d \u3000\u3000\u201c\u6c5f\u5144\u8ba4\u4e3a\u6211\u4eec\u7684\u529b\u91cf\u591f\u5417\uff1f\u201d \u3000\u3000\u201c\u90a3\u8981\u770b\u662f\u91c7\u53d6\u4ec0\u4e48\u65b9\u5f0f\uff0c\u5982\u679c\u9a6c\u6587\u4e2d\u5df2\u4e8b\u5148\u5c06\u5175\u529b\u90e8\u7f72\u59a5\u5f53\uff0c\u54b1\u4eec\u7684\u4eba\u624b\u7684\u786e\u4e0d\u591f\uff0c \u4f46\u82e5\u4ee5\u667a\u53d6\u65b9\u5f0f\u8ba9\u4ed6\u81ea\u6295\u7f57\u7f51\uff0c\u54b1\u4eec\u73b0\u5728\u7684\u4eba\u624b\u53cd\u800c\u5927\u591a\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u5c31\u4f9d\u6c5f\u5144\u7684\u8ba1\u5212\u884c\u4e8b\u3002\u201d \u3000\u3000\u201c\u56de\u5934\u6211\u518d\u548c\u4e49\u5144\u4ee5\u53ca\u82df\u5973\u4fa0\u5546\u8bae\u4e00\u4e0b\uff0c\u660e\u5929\u54b1\u4eec\u5c31\u542f\u7a0b\u8d76\u5f80\u6d1b\u9633\u3002\u201d \u3000\u3000\u5c31\u5728\u7fcc\u65e5\u51cc\u6668\uff0c\u4e09\u516c\u4e3b\u4e00\u884c\u4eba\u4f17\u4ee5\u79d8\u5bc6\u65b9\u5f0f\u79bb\u5f00\u4e86\u6f7c\u5173\u3002 \u3000\u3000\u968f\u884c\u7684\u539f\u73ed\u4eba\u9a6c\u4e2d\uff0c\u4ec5\u7559\u4e0b\u97e9\u6d9b\u4e00\u4eba\uff0c\u589e\u52a0\u7684\u5219\u662f\u4e24\u4f4d\u5f53\u4ee3\u9ad8\u4eba\u2014\u2014\u5929\u96f7\u8001\u4eba\u5cb3\u5929\u96f7\u548c \u4e00\u9053\u98de\u8679\u82df\u6167\u6708\u3002 \u3000\u3000\u81f3\u4e8e\u97e9\u6d9b\u7559\u5728\u6f7c\u5173\uff0c\u4e0d\u5916\u662f\u6682\u65f6\u8d1f\u8d23\u4e3b\u6301\u201c\u6843\u6797\u5c45\u201d\u3002 \u3000\u3000\u56e0\u4e3a\u201c\u6843\u6797\u5c45\u201d\u81ea\u9a6c\u5f69\u4e91\u4ee5\u4e0b\uff0c\u6709\u4e8c\u5341\u4f59\u4eba\uff0c\u4e00\u65e6\u5ba3\u5e03\u6b47\u4e1a\uff0c\u5f88\u591a\u4eba\u52bf\u5c06\u65ad\u4e86\u751f\u8ba1\uff0c\u800c \u4e14\u4ed6\u4eec\u88ab\u9063\u6563\u540e\uff0c\u4e09\u516c\u4e3b\u3001\u738b\u5f64\u7b49\u4eba\u7684\u884c\u8e2a\uff0c\u4fbf\u5f88\u5bb9\u6613\u6cc4\u6f0f\u51fa\u53bb\u3002\u6240\u4ee5\uff0c\u4e0d\u5f97\u4e0d\u7559\u4e0b\u97e9\u6d9b\u6682 \u65f6\u4e3b\u6301\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u97e9\u6d9b\u62c5\u5fc3\u9a6c\u5f69\u4e91\u7b49\u4eba\u751f\u53d8\uff0c\u5df2\u4e8b\u5148\u7ea6\u6765\u5341\u51e0\u540d\u4e10\u5e2e\u5f1f\u5b50\u4e2d\u7684\u9ad8\u624b\u524d\u6765\u5e2e\u5fd9\uff0c\u5982\u6b64 \u4e00\u6765\uff0c\u201c\u6843\u6797\u5c45\u201d\u539f\u6709\u7684\u4eba\u81ea\u7136\u4e0d\u6562\u8f7b\u4e3e\u5984\u52a8\uff0c\u4f55\u51b5\u4ed6\u4eec\u53c8\u90fd\u5df2\u7ecf\u88ab\u5e9f\u4e86\u6b66\u529f\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u7b49\u4eba\u4ecd\u626e\u6210\u5546\u8d3e\u548c\u7737\u5c5e\u6a21\u6837\u3002 \u3000\u3000\u6709\u7684\u4e14\u5df2\u6613\u4e86\u5bb9\uff0c\u81f3\u4e8e\u8d75\u4e8c\u5824\uff0c\u81ea\u7136\u4e5f\u5728\u4e25\u5bc6\u76d1\u63a7\u4e0b\u968f\u540c\u884c\u52a8\uff0c\u88ab\u8f6f\u7981\u5728\u4e00\u4e58\u8f7f\u9a84\u91cc\uff0c \u4ed6\u662f\u6700\u6709\u5229\u7528\u4ef7\u503c\u7684\u4eba\u8d28\uff0c\u738b\u5f64\u548c\u6c5f\u5343\u91cc\u5f53\u7136\u5fc5\u987b\u52a0\u4ee5\u5229\u7528\u3002 \u3000\u3000\u968f\u540c\u8d75\u4e8c\u5824\u8d76\u6765\u6f7c\u5173\u7684\u90a3\u4e24\u540d\u4eb2\u968f\uff0c\u5219\u5df2\u5728\u5236\u670d\u8d75\u4e8c\u5824\u7684\u5f53\u665a\u88ab\u5c0f\u71d5\u5b50\u548c\u767d\u7fbd\u4e24\u4eba\u8bf1\u51fa \u5ba2\u6808\uff0c\u5728\u5c71\u91ce\u95f4\u52a0\u4ee5\u5904\u7f6e\u4e86\u3002 \u3000\u3000\u6570\u65e5\u540e\uff0c\u4e09\u516c\u4e3b\u4e00\u884c\u5df2\u5230\u8fbe\u4e86\u6d1b\u9633\uff0c\u4e3a\u4e86\u907f\u514d\u62db\u4eba\u8033\u76ee\uff0c\u4ed6\u4eec\u4ecd\u4f4f\u5728\u4f4d\u4e8e\u90ae\u5c71\u811a\u4e0b\u7684\u795d \u5b85\u3002 \u3000\u3000\u795d\u9999\u4ead\u8001\u592b\u5987\u4e00\u89c1\u6069\u4eba\u4eec\u518d\u5ea6\u524d\u6765\uff0c\u771f\u662f\u559c\u51fa\u671b\u5916\uff0c\u5f53\u5373\u7387\u540c\u7231\u5973\u795d\u6708\u82f1\u3001\u5e7c\u5b50\u795d\u82f1\u6770 \u4ee5\u53ca\u957f\u5de5\u8bb8\u6709\u7530\u548c\u5973\u5eb8\u8fce\u51fa\u5927\u95e8\u4e4b\u5916\u3002 \u3000\u3000\u636e\u795d\u9999\u4ead\u8bf4\uff0c\u9099\u5c71\u4ee5\u53ca\u9ec4\u6cb3\u5357\u5cb8\u7684\u6c34\u9646\u4e24\u5be8\uff0c\u81ea\u64a4\u8d70\u540e\u5e76\u672a\u518d\u6765\uff0c\u6d1b\u9633\u5730\u9762\u8fd9\u4e9b\u5929\u6765\u4e00 \u76f4\u5341\u5206\u5b81\u9759\u3002 \u3000\u3000\u5f53\u665a\uff0c\u795d\u9999\u4ead\u592b\u5987\u8bbe\u4e0b\u76db\u7b75\u6b3e\u5f85\u968f\u540c\u4e09\u516c\u4e3b\u524d\u6765\u7684\u5927\u961f\u4eba\u9a6c\u3002 \u3000\u3000\u738b\u5f64\u3001\u6c5f\u5343\u91cc\u3001\u5929\u96f7\u8001\u4eba\u3001\u82df\u6167\u6708\u7ecf\u8fc7\u4e00\u756a\u8ba1\u8bae\uff0c\u51b3\u5b9a\u5c06\u9a6c\u6587\u4e2d\u8bf1\u6765\u6d1b\u9633\uff0c\u4ee5\u4fbf\u5c31\u8fd1\u902e \u6355\u3002 \u3000\u3000\u8fd9\u662f\u56e0\u4e3a\u5f00\u5c01\u662f\u9a6c\u6587\u4e2d\u7684\u6839\u636e\u5730\uff0c\u4ed6\u7684\u624b\u4e0b\u529b\u91cf\u51e0\u4e4e\u5168\u5728\u5f00\u5c01\uff0c\u82e5\u662f\u5728\u5f00\u5c01\u884c\u4e8b\uff0c\u52bf\u5fc5 \u906d\u5230\u4ed6\u624b\u4e0b\u52bf\u529b\u53cd\u6251\uff0c\u53cc\u65b9\u4e5f\u52bf\u5fc5\u9020\u6210\u6781\u5927\u4f24\u4ea1\uff0c\u56e0\u6b64\u8ba4\u4e3a\u8fd8\u662f\u4ee5\u5175\u4e0d\u8840\u5203\uff0c\u65b9\u4e3a\u4e0a\u4e0a\u4e4b \u7b56\u3002 \u3000\u3000\u8ba1\u8bae\u65e2\u5b9a\uff0c\u7531\u738b\u5f64\u4eb2\u81f3\u57ce\u4e2d\u9762\u89c1\u7f57\u77e5\u5e9c\u3002 \u3000\u3000\u7531\u7f57\u77e5\u5e9c\u8c0e\u62a5\u60c5\u51b5\uff0c\u8bf1\u4f7f\u9a6c\u6587\u4e2d\u524d\u6765\u6d1b\u9633\u3002 \u3000\u3000\u7f57\u77e5\u5e9c\u548c\u9a6c\u6587\u4e2d\u5e76\u65e0\u6e0a\u6e90\uff0c\u800c\u4e14\u4e00\u5411\u5fe0\u5fc3\u4e3a\u56fd\uff0c\u5f53\u7136\u7167\u8ba1\u800c\u884c\u3002 \u3000\u3000\u4e94\u65e5\u540e\uff0c\u9a6c\u6587\u4e2d\u679c\u7136\u53ea\u5e26\u4e86\u5c11\u6570\u4eb2\u4fe1\uff0c\u6765\u5230\u6d1b\u9633\u3002 \u3000\u3000\u7f57\u77e5\u5e9c\u7acb\u5373\u6d3e\u4eba\u5230\u795d\u5b85\u5411\u738b\u5f64\u62a5\u4fe1\u3002 \u3000\u3000\u5f53\u4e0b\uff0c\u738b\u5f64\u4f1a\u540c\u6c5f\u5343\u91cc\u3001\u5c0f\u71d5\u5b50\u4ee5\u53ca\u4e03\u5de7\u50e7\u3001\u767d\u7fbd\u7b49\u4eba\u8fc5\u901f\u8d76\u5f80\u5e9c\u8859\uff0c\u5728\u7f57\u77e5\u5e9c\u7684\u4e66\u623f \u4e2d\u6beb\u4e0d\u8d39\u529b\u7684\u4fbf\u5c06\u9a6c\u6587\u4e2d\u4e00\u4e3e\u64d2\u83b7\uff0c\u5f53\u5373\u89e3\u5f80\u795d\u5b85\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\u4f20\u6765\u4e00\u4e2a\u4ee4\u4eba\u632f\u594b\u7684\u6d88\u606f\uff0c\u90a3\u5c31\u662f\u9c7c\u5316\u9f99\u5df2\u88ab\u859b\u767e\u80dc\u6697\u6740\uff0c\u4e3a\u671d\u5ef7\u6d88\u9664\u4e86\u4e00\u6869 \u5fc3\u8179\u5927\u60a3\uff0c\u4e5f\u4f7f\u9a6c\u6587\u4e2d\u7684\u624b\u4e0b\u5931\u53bb\u53cd\u6251\u7684\u529b\u91cf\u3002 \u3000\u3000\u738b\u5f64\u5f53\u665a\u4fbf\u6d3e\u4eba\u5230\u649e\u5173\uff0c\u901a\u77e5\u97e9\u6d9b\u7531\u6f7c\u5173\u76f4\u63a5\u8fd4\u4eac\uff0c\u81f3\u4e8e\u201c\u6843\u6797\u5c45\u201d\u5219\u4ecd\u4ea4\u7531\u9a6c\u5f69\u4e91\u4e3b \u6301\u3002 \u3000\u3000\u6b21\u65e5\uff0c\u4e09\u516c\u4e3b\u3001\u738b\u5f64\u3001\u8d75\u4fdd\u3001\u9648\u5b8f\u3001\u60dc\u6625\u3001\u601c\u82b1\u7b49\u4eba\u4fbf\u8d77\u7a0b\uff0c\u62bc\u89e3\u9a6c\u6587\u4e2d\u3001\u8d75\u4e8c\u5824\u56de \u4eac\u3002 \u3000\u3000\u672c\u6765\uff0c\u4e09\u516c\u4e3b\u548c\u738b\u5f64\u4e00\u518d\u8981\u6c5f\u5343\u91cc\u3001\u5c0f\u71d5\u5b50\u4ee5\u53ca\u5929\u96f7\u8001\u4eba\u3001\u4e00\u9053\u98de\u8679\u7b49\u5171\u540c\u8fdb\u4eac\uff0c\u5e76\u63a5 \u53d7\u7687\u4e0a\u5c01\u8d4f\u3002 \u3000\u3000\u4f46\u5929\u96f7\u8001\u4eba\u3001\u82df\u6167\u6708\u3001\u6c5f\u5343\u91cc\u7b49\u4eba\u90fd\u7897\u8a00\u8c22\u7edd\u3002\u552f\u6709\u859b\u767e\u80dc\uff0c\u4ed6\u56e0\u7acb\u529f\u6700\u5927\uff0c\u4e09\u516c\u4e3b\u548c \u738b\u5f64\u975e\u8981\u4ed6\u8fdb\u4eac\u53d7\u8d4f\u4e0d\u53ef\uff0c\u859b\u767e\u80dc\u8d77\u521d\u4e5f\u575a\u6301\u4e0d\u80af\uff0c\u6700\u540e\u5728\u76db\u60c5\u96be\u5374\u4e4b\u4e0b\uff0c\u4e5f\u5c31\u53ea\u597d\u968f\u540c\u8fdb \u4eac\u4e86\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u548c\u738b\u5f64\u7b49\u5927\u5185\u9ad8\u624b\u62bc\u89e3\u9a6c\u6587\u4e2d\u542f\u7a0b\u5317\u4e0a\u540e\uff0c\u5929\u96f7\u8001\u4eba\u3001\u82df\u6167\u6708\u3001\u6c5f\u5343\u91cc\u3001\u5c0f\u71d5\u5b50 \u4ee5\u53ca\u4e03\u5de7\u50e7\u3001\u767d\u7fbd\u3001\u738b\u91cd\u5c71\u7b49\u4eba\u4ecd\u7559\u5728\u795d\u5b85\uff0c\u53e6\u5916\u8fd8\u7559\u4e0b\u5c0f\u71d5\u5b50\u7684\u7236\u4eb2\u71d5\u98de\u3002 \u3000\u3000\u539f\u6765\u5c0f\u71d5\u5b50\u8981\u548c\u795d\u9999\u4ead\u7684\u7231\u5973\u795d\u6708\u82f1\u6210\u4eb2\u4e86\u3002 \u3000\u3000\u8fd9\u559c\u8baf\u662f\u5929\u96f7\u8001\u4eba\u548c\u82df\u6167\u6708\u5171\u540c\u5ba3\u5e03\u7684\uff0c\u8fde\u5f53\u4e8b\u4eba\u5c0f\u71d5\u5b50\u4e8b\u5148\u90fd\u6beb\u4e0d\u77e5\u60c5\uff0c\u6c5f\u5343\u91cc\u548c\u71d5 \u98de\u4e8b\u5148\u4e5f\u90fd\u8499\u5728\u9f13\u91cc\u3002 \u3000\u3000\u539f\u6765\u5f53\u5929\u96f7\u8001\u4eba\u548c\u82df\u6167\u6708\u4f4f\u8fdb\u795d\u5b85\u7684\u7b2c\u4e09\u5929\u591c\u665a\uff0c\u795d\u9999\u4ead\u4fbf\u6765\u5411\u4ed6\u4eec\u4e24\u4eba\u4e3a\u5973\u513f\u6c42\u4eb2\u3002 \u3000\u3000\u5929\u96f7\u8001\u4eba\u548c\u82df\u6167\u6708\u90fd\u5df2\u89c1\u8fc7\u795d\u6708\u82f1\uff0c\u800c\u4e14\u5370\u8c61\u81f3\u4f73\u3002\u53ea\u56e0\u5929\u96f7\u8001\u4eba\u5df2\u77e5\u82df\u6167\u6708\u6709\u610f\u628a\u4e09 \u516c\u4e3b\u3001\u5c0f\u71d5\u5b50\u64ae\u5408\u6210\u4e00\u5bf9\uff0c\u6545\u800c\u4e0d\u4fbf\u8868\u793a\u610f\u89c1\u3002 \u3000\u3000\u5c82\u77e5\u82df\u6167\u6708\u7adf\u6b23\u7136\u540c\u610f\u3002 \u3000\u3000\u5979\u4e4b\u6240\u4ee5\u6539\u53d8\u4e3b\u610f\uff0c\u662f\u56e0\u4e3a\u5df2\u77e5\u4e09\u516c\u4e3b\u548c\u5c0f\u71d5\u5b50\u4e4b\u95f4\uff0c\u867d\u7136\u591a\u65e5\u76f8\u5904\uff0c\u4f3c\u4e4e\u5e76\u672a\u5efa\u7acb\u8d77 \u5e94\u6709\u7684\u611f\u60c5\uff0c\u800c\u4e14\u5c0f\u71d5\u5b50\u5728\u5929\u96f7\u8001\u4eba\u7684\u6559\u8bf2\u4e0b\uff0c\u6ce8\u5b9a\u662f\u6c5f\u6e56\u4e2d\u4eba\uff0c\u4e0d\u5b9c\u548c\u7687\u5bb6\u6cbe\u4eb2\u5e26\u6545\uff0c\u9f50 \u5927\u975e\u5076\uff0c\u5fc5\u987b\u5f15\u4ee5\u4e3a\u6212\uff0c\u56e0\u4e4b\u624d\u505a\u4e86\u8fd9\u9879\u51b3\u5b9a\u3002 \u3000\u3000\u4e24\u65e5\u540e\uff0c\u5c0f\u71d5\u5b50\u548c\u795d\u6708\u82f1\u4e3e\u884c\u4e86\u6587\u5b9a\u4eea\u5f0f\u3002\u7531\u53cc\u65b9\u5bb6\u957f\u71d5\u98de\u548c\u795d\u9999\u4ead\u7b49\u4eb2\u53cb\u53c2\u52a0\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u4e00\u5929\uff0c\u5c0f\u71d5\u5b50\u966a\u540c\u7236\u4eb2\u71d5\u98de\u56de\u4eac\uff0c\u4ee5\u4fbf\u548c\u6bcd\u4eb2\u56e2\u805a\u51e0\u65e5\u3002 \u3000\u3000\u81f3\u4e8e\u5929\u96f7\u8001\u4eba\u3001\u82df\u6167\u6708\u3001\u6c5f\u5343\u91cc\u7b49\u4eba\uff0c\u4e5f\u51b3\u5b9a\u6682\u56de\u7ec8\u5357\u5c71\u5927\u8352\u6d1e\uff0c\u7b49\u5c0f\u71d5\u5b50\u7531\u4eac\u57ce\u8fd4\u56de \u540e\uff0c\u518d\u7ee7\u7eed\u4ed6\u4eec\u7684\u6b66\u6797\u751f\u6daf\u3002"}),showOnHomepage:!0}]}}]);