"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[35205],{12271:(e,n,t)=>{t.r(n),t.d(n,{default:()=>o});var a=t(74848);const o=[{name:"\u4e00",content:(0,a.jsx)(a.Fragment,{children:"\u5f97\u5f97\u5f97\uff0c\u5f97\u5f97\u5f97\u2026\u2026 \u3000\u3000\u5f97\u5f97\u5f97\uff0c\u5f97\u5f97\u5f97\u2026\u2026 \u3000\u3000\u5728\u9ec4\u6c99\u83bd\u83bd\u7684\u56de\u7586\u5927\u6f20\u4e4b\u4e0a\uff0c\u5c18\u6c99\u98de\u8d77\u4e24\u4e08\u6765\u9ad8\uff0c\u4e24\u9a91\u9a6c\u4e00\u524d\u4e00\u540e\u7684\u6025\u9a70\u800c\u6765\u3002\u524d\u9762\u662f\u5339\u9ad8\u817f\u957f\u8eab\u7684\u767d\u9a6c\uff0c\u9a6c\u4e0a\u9a91\u7740\u4e2a\u5c11\u5987\uff0c\u6000\u4e2d\u6402\u7740\u4e2a\u4e03\u516b\u5c81\u7684\u5c0f\u59d1\u5a18\u3002\u540e\u9762\u662f\u5339\u67a3\u7ea2\u9a6c\uff0c\u9a6c\u80cc\u4e0a\u4f0f\u7740\u7684\u662f\u4e2a\u9ad8\u7626\u7684\u6c49\u5b50\u3002 \u3000\u3000\u90a3\u6c49\u5b50\u5de6\u8fb9\u80cc\u5fc3\u4e0a\u5374\u63d2\u7740\u4e00\u652f\u957f\u7bad\u3002\u9c9c\u8840\u4ece\u4ed6\u80cc\u5fc3\u6d41\u5230\u9a6c\u80cc\u4e0a\uff0c\u53c8\u6d41\u5230\u5730\u4e0b\uff0c\u6ef4\u5165\u4e86\u9ec4\u6c99\u4e4b\u4e2d\u3002\u4ed6\u4e0d\u6562\u4f38\u624b\u62d4\u7bad\uff0c\u53ea\u6015\u8fd9\u652f\u7bad\u4e00\u62d4\u4e0b\u6765\uff0c\u5c31\u4f1a\u652f\u6301\u4e0d\u4f4f\uff0c\u7acb\u65f6\u5012\u6bd9\u3002\u8c01\u4e0d\u6b7b\u5462\uff1f\u90a3\u4e5f\u6ca1\u4ec0\u4e48\u3002\u53ef\u662f\u8c01\u6765\u7167\u6599\u524d\u9762\u7684\u5a07\u59bb\u5e7c\u5973\uff1f\u5728\u8eab\u540e\uff0c\u51f6\u608d\u6bd2\u8fa3\u7684\u654c\u4eba\u6b63\u5728\u7d27\u7d27\u8ffd\u8e2a\u3002 \u3000\u3000\u4ed6\u8de8\u4e0b\u7684\u67a3\u7ea2\u9a6c\u5954\u9a70\u4e86\u6570\u5341\u91cc\u5730\uff0c\u65e9\u5df2\u7b4b\u75b2\u529b\u5c3d\uff0c\u5728\u4e3b\u4eba\u6ca1\u547d\u4ef7\u7684\u97ad\u6253\u50ac\u8e22\u4e4b\u4e0b\uff0c\u903c\u5f97\u6c14\u4e5f\u5598\u4e0d\u8fc7\u6765\u4e86\uff0c\u8fd9\u65f6\u5634\u8fb9\u5df2\u5168\u662f\u767d\u6cab\uff0c\u731b\u5730\u91cc\u524d\u817f\u4e00\u8f6f\uff0c\u8dea\u5012\u5728\u5730\u3002\u90a3\u6c49\u5b50\u7528\u529b\u4e00\u63d0\u7f30\u7ef3\uff0c\u90a3\u7ea2\u9a6c\u4e00\u58f0\u54c0\u5636\uff0c\u62bd\u6410\u4e86\u51e0\u4e0b\uff0c\u4fbf\u5df2\u8131\u529b\u800c\u6b7b\u3002\u90a3\u5c11\u5987\u542c\u5f97\u58f0\u54cd\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u5ffd\u89c1\u7ea2\u9a6c\u5012\u6bd9\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u5927\u54e5\u2026\u2026\u600e\u2026\u2026\u600e\u4e48\u5566\uff1f\u201d\u90a3\u6c49\u5b50\u76b1\u7709\u6447\u4e86\u6447\u5934\u3002\u4f46\u89c1\u8eab\u540e\u6570\u91cc\u5916\u5c18\u6c99\u98de\u626c\uff0c\u5927\u961f\u654c\u4eba\u8ffd\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u90a3\u5c11\u5987\u5708\u8f6c\u9a6c\u6765\uff0c\u9a70\u5230\u4e08\u592b\u8eab\u65c1\uff0c\u84e6\u7136\u89c1\u5230\u4ed6\u80cc\u4e0a\u7684\u957f\u7bad\uff0c\u80cc\u5fc3\u4e0a\u7684\u5927\u6ee9\u9c9c\u8840\uff0c\u4e0d\u7981\u5927\u60ca\u5931\u8272\uff0c\u9669\u9669\u6655\u4e86\u8fc7\u53bb\u3002\u90a3\u5c0f\u59d1\u5a18\u4e5f\u5931\u58f0\u60ca\u53eb\u8d77\u6765\uff1a\u201c\u7239\uff0c\u7239\uff0c\u4f60\u80cc\u4e0a\u6709\u7bad\uff01\u201d\u90a3\u6c49\u5b50\u82e6\u7b11\u4e86\u4e00\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u788d\u4e8b\uff01\u201d\u4e00\u8dc3\u800c\u8d77\uff0c\u8f7b\u8f7b\u5de7\u5de7\u7684\u843d\u5728\u59bb\u5b50\u8eab\u540e\u978d\u4e0a\uff0c\u4ed6\u867d\u8eab\u53d7\u91cd\u4f24\uff0c\u8eab\u6cd5\u4ecd\u662f\u8f7b\u6377\u5229\u843d\u3002\u90a3\u5c11\u5987\u56de\u5934\u671b\u7740\u4ed6\uff0c\u6ee1\u8138\u5173\u6000\u75db\u60dc\u4e4b\u60c5\uff0c\u8f7b\u58f0\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u2026\u2026\u201d\u90a3\u6c49\u5b50\u53cc\u817f\u4e00\u631f\uff0c\u626f\u8d77\u9a6c\u7f30\u3002\u767d\u9a6c\u56db\u8e44\u7ffb\u98de\uff0c\u5411\u524d\u75be\u9a70\u3002 \u3000\u3000\u767d\u9a6c\u867d\u7136\u795e\u9a8f\uff0c\u4f46\u4e0d\u505c\u4e0d\u606f\u7684\u957f\u9014\u5954\u8dd1\u4e0b\u6765\uff0c\u6bd5\u7adf\u7d2f\u4e86\uff0c\u4f55\u51b5\u8fd9\u65f6\u80cc\u4e0a\u4e58\u4e86\u4e09\u4eba\u3002\u767d\u9a6c\u4f3c\u4e4e\u77e5\u9053\u8fd9\u662f\u4e3b\u4eba\u7684\u751f\u6b7b\u5173\u5934\uff0c\u4e0d\u7528\u50ac\u6253\uff0c\u7adf\u81ea\u4e0d\u987e\u6027\u547d\u7684\u594b\u529b\u5954\u8dd1\u3002 \u3000\u3000\u4f46\u518d\u5954\u9a70\u6570\u91cc\uff0c\u7ec8\u4e8e\u6e10\u6e10\u7684\u6162\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u540e\u9762\u8ffd\u6765\u7684\u654c\u4eba\u4e00\u6b65\u6b65\u8feb\u8fd1\u4e86\u3002\u4e00\u5171\u516d\u5341\u4e09\u4eba\uff0c\u5374\u5e26\u4e86\u4e00\u767e\u4e5d\u5341\u591a\u5339\u5065\u9a6c\uff0c\u53ea\u8981\u9a6c\u529b\u7a0d\u4e4f\uff0c\u5c31\u6362\u4e00\u5339\u9a6c\u4e58\u5750\u3002\u90a3\u662f\u5fd7\u5728\u5fc5\u5f97\uff0c\u975e\u8ffd\u4e0a\u4e0d\u53ef\u3002 \u3000\u3000\u90a3\u6c49\u5b50\u56de\u8fc7\u5934\u6765\uff0c\u5728\u6eda\u6eda\u9ec4\u5c18\u4e4b\u4e2d\uff0c\u770b\u5230\u4e86\u654c\u4eba\u7684\u8eab\u5f62\uff0c\u518d\u8fc7\u4e00\u9635\uff0c\u8fde\u9762\u76ee\u4e5f\u770b\u5f97\u6e05\u695a\u4e86\u3002\u90a3\u6c49\u5b50\u4e00\u54ac\u7259\uff0c\u8bf4\u9053\uff1a\u201c\u8679\u59b9\uff0c\u6211\u6c42\u4f60\u4e00\u4ef6\u4e8b\uff0c\u4f60\u7b54\u4e0d\u7b54\u5e94\uff1f\u201d\u90a3\u5c11\u5987\u56de\u5934\u6765\uff0c\u6e29\u67d4\u7684\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e00\u751f\u4e4b\u4e2d\uff0c\u6211\u8fdd\u62d7\u8fc7\u4f60\u4e00\u6b21\u4e48\uff1f\u201d\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u597d\uff0c\u4f60\u5e26\u4e86\u79c0\u513f\u9003\u547d\uff0c\u4fdd\u5168\u54b1\u4e24\u4e2a\u7684\u9aa8\u8840\uff0c\u4fdd\u5168\u8fd9\u5e45\u9ad8\u660c\u8ff7\u5bab\u7684\u5730\u56fe\u3002\u201d\u8bf4\u5f97\u6781\u662f\u575a\u51b3\uff0c\u4fbf\u5982\u662f\u4e0b\u4ee4\u4e00\u822c\u3002 \u3000\u3000\u90a3\u5c11\u5987\u58f0\u97f3\u53d1\u98a4\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u628a\u5730\u56fe\u7ed9\u4e86\u4ed6\u4eec\uff0c\u54b1\u4eec\u8ba4\u8f93\u4fbf\u662f\u3002\u4f60\u2026\u2026\u4f60\u7684\u8eab\u5b50\u8981\u7d27\u3002\u201d\u90a3\u6c49\u5b50\u4f4e\u5934\u4eb2\u4e86\u4eb2\u5979\u7684\u5de6\u988a\uff0c\u58f0\u97f3\u7a81\u7136\u53d8\u5f97\u5341\u5206\u6e29\u67d4\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4fe9\u4e00\u8d77\u7ecf\u5386\u8fc7\u65e0\u6570\u5371\u96be\uff0c\u8fd9\u6b21\u6216\u8bb8\u4e5f\u80fd\u9003\u8131\u3002\u2018\u5415\u6881\u4e09\u6770\u2019\u4e0d\u4f46\u8981\u5730\u56fe\uff0c\u4ed6\u4eec\u2026\u2026 \u3000\u3000\u4ed6\u4eec\u8fd8\u4e3a\u4e86\u4f60\u3002\u201d\u90a3\u5c11\u5987\u9053\uff1a\u201c\u4ed6\u2026\u2026\u4ed6\u603b\u8be5\u8fd8\u6709\u51e0\u5206\u540c\u95e8\u4e4b\u60c5\uff0c\u8bf4\u4e0d\u5b9a\uff0c\u6211\u80fd\u6c42\u6c42\u4ed6\u4eec\u2026\u2026\u201d\u90a3\u6c49\u5b50\u5389\u58f0\u9053\uff1a\u201c\u96be\u9053\u6211\u592b\u5987\u8fd8\u80fd\u4f4e\u5934\u5411\u4eba\u54c0\u6c42\uff1f\u8fd9\u9a6c\u8d1f\u4e0d\u8d77\u6211\u4eec\u4e09\u4e2a\u3002\u5feb\u53bb\uff01\u201d\u63d0\u8eab\u7eb5\u8d77\uff0c\u5927\u53eb\u4e00\u58f0\uff0c\u6454\u4e0b\u9a6c\u6765\u3002 \u3000\u3000\u90a3\u5c11\u5987\u52d2\u5b9a\u4e86\u9a6c\uff0c\u60f3\u4f38\u624b\u53bb\u62c9\uff0c\u5374\u89c1\u4e08\u592b\u6ee1\u8138\u6012\u5bb9\uff0c\u8ddf\u7740\u542c\u5f97\u4ed6\u5389\u58f0\u559d\u9053\uff1a\u201c\u5feb\u8d70\uff01\u201d\u5979\u4e00\u5411\u5bf9\u4e08\u592b\u987a\u4ece\u60ef\u4e86\u7684\uff0c\u53ea\u5f97\u62cd\u9a6c\u63d0\u7f30\uff0c\u5411\u524d\u5954\u9a70\uff0c\u4e00\u9897\u5fc3\u5374\u5df2\u5982\u5bd2\u51b0\u4e00\u6837\uff0c\u4e0d\u4f46\u662f\u5fc3\uff0c\u5168\u8eab\u7684\u8840\u90fd\u4f3c\u4e4e\u5df2\u7ed3\u6210\u4e86\u51b0\u3002 \u3000\u3000\u81ea\u540e\u8ffd\u5230\u7684\u4f17\u4eba\u671b\u89c1\u90a3\u6c49\u5b50\u843d\u9a6c\uff0c\u4e00\u9f50\u5927\u58f0\u6b22\u547c\u8d77\u6765\uff1a\u201c\u767d\u9a6c\u674e\u4e09\u5012\u5566\uff01\u767d\u9a6c\u674e\u4e09\u5012\u5566\uff01\u201d\u5341\u4f59\u4eba\u7eb5\u9a6c\u56f4\u4e86\u4e0a\u53bb\u3002\u5176\u4f59\u56db\u5341\u4f59\u4eba\u7ee7\u7eed\u8ffd\u8d76\u5c11\u5987\u3002 \u3000\u3000\u90a3\u6c49\u5b50\u8737\u66f2\u7740\u5367\u5728\u5730\u4e0b\uff0c\u4e00\u52a8\u4e5f\u4e0d\u52a8\uff0c\u4f3c\u4e4e\u5df2\u7ecf\u6b7b\u4e86\u3002\u4e00\u4eba\u633a\u8d77\u957f\u67aa\uff0c\u55e4\u7684\u4e00\u58f0\uff0c\u5728\u4ed6\u53f3\u80a9\u523a\u4e86\u8fdb\u53bb\u3002\u62d4\u67aa\u51fa\u6765\uff0c\u9c9c\u8840\u76f4\u55b7\uff0c\u767d\u9a6c\u674e\u4e09\u4ecd\u662f\u4e0d\u52a8\u3002\u9886\u5934\u7684\u866c\u9aef\u6c49\u5b50\u9053\uff1a\u201c\u6b7b\u5f97\u900f\u4e86\uff0c\u8fd8\u6015\u4ec0\u4e48\uff1f\u5feb\u641c\u4ed6\u8eab\u4e0a\u3002\u201d\u4e24\u4eba\u7ffb\u8eab\u4e0b\u9a6c\uff0c\u53bb\u6273\u4ed6\u8eab\u5b50\u3002\u731b\u5730\u91cc\u767d\u5149\u95ea\u52a8\uff0c\u767d\u9a6c\u674e\u4e09\u957f\u5200\u56de\u65cb\uff0c\u64e6\u64e6\u4e24\u4e0b\uff0c\u5df2\u5c06\u4e24\u4eba\u780d\u7ffb\u5728\u5730\u3002 \u3000\u3000\u4f17\u4eba\u4e07\u6599\u4e0d\u5230\u4ed6\u9002\u624d\u7adf\u662f\u88c5\u6b7b\uff0c\u8fde\u957f\u67aa\u523a\u5165\u8eab\u5b50\u90fd\u6d51\u4f3c\u4e0d\u89c9\uff0c\u6597\u7136\u95f4\u53c8\u4f1a\u5ffd\u65bd\u53cd\u51fb\uff0c\u4e00\u60ca\u4e4b\u4e0b\uff0c\u516d\u4e03\u4eba\u52d2\u9a6c\u9000\u5f00\u3002\u866c\u9aef\u5927\u6c49\u6325\u52a8\u624b\u4e2d\u96c1\u7fce\u5200\uff0c\u559d\u9053\uff1a\u201c\u674e\u4e09\uff0c\u4f60\u5f53\u771f\u662f\u4e2a\u786c\u6c49\uff01\u201d\u547c\u7684\u4e00\u5200\u5411\u4ed6\u5934\u9876\u780d\u843d\u3002\u674e\u4e09\u4e3e\u5200\u6321\u67b6\uff0c\u4ed6\u53cc\u80a9\u90fd\u53d7\u4e86\u91cd\u4f24\uff0c\u624b\u81c2\u65e0\u529b\uff0c\u817e\u817e\u817e\u9000\u51fa\u4e09\u6b65\uff0c\u54c7\u7684\u4e00\u53e3\u9c9c\u8840\u55b7\u4e86\u51fa\u6765\u3002\u5341\u4f59\u4eba\u7eb5\u9a6c\u56f4\u4e0a\uff0c\u5200\u67aa\u5e76\u4e3e\uff0c\u5288\u523a\u4e0b\u53bb\u3002 \u3000\u3000\u767d\u9a6c\u674e\u4e09\u4e00\u751f\u82f1\u96c4\uff0c\u4e00\u76f4\u5230\u6b7b\uff0c\u59cb\u7ec8\u6ca1\u6709\u5c48\u670d\uff0c\u5728\u6700\u540e\u5012\u4e0b\u53bb\u4e4b\u65f6\uff0c\u53c8\u624b\u5203\u4e86\u4e24\u540d\u5f3a\u654c\u3002 \u3000\u3000\u90a3\u5c11\u5987\u8fdc\u8fdc\u542c\u5f97\u4e08\u592b\u7684\u4e00\u58f0\u6012\u543c\uff0c\u5f53\u771f\u662f\u5fc3\u5982\u5200\u5272\uff1a\u201c\u4ed6\u5df2\u6b7b\u4e86\uff0c\u6211\u8fd8\u6d3b\u7740\u5e72\u4e48\uff1f\u201d\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u5757\u7f8a\u6bdb\u7ec7\u6210\u7684\u624b\u5e15\uff0c\u585e\u5728\u5973\u513f\u6000\u91cc\uff0c\u8bf4\u9053\uff1a\u201c\u79c0\u513f\uff0c\u4f60\u597d\u597d\u7167\u6599\u81ea\u5df1\uff01\u201d\u6325\u9a6c\u97ad\u5728\u767d\u9a6c\u81c0\u4e0a\u4e00\u62bd\uff0c\u53cc\u8db3\u4e00\u6491\uff0c\u8eab\u5b50\u5df2\u79bb\u9a6c\u978d\u3002\u4f46\u89c1\u90a3\u767d\u9a6c\u978d\u4e0a\u4e00\u8f7b\uff0c\u9a6e\u7740\u5973\u5b69\u513f\u5982\u98ce\u75be\u9a70\uff0c\u5fc3\u4e2d\u7565\u611f\u5b89\u6170\uff1a\u201c\u6b64\u9a6c\u811a\u529b\u5929\u4e0b\u65e0\u53cc\uff0c\u79c0\u513f\u8eab\u5b50\u53c8\u8f7b\uff0c\u8fd9\u4e00\u4e0b\uff0c\u4ed6\u4eec\u518d\u4e5f\u8ffd\u5979\u4e0d\u4e0a\u4e86\u3002\u201d\u524d\u9762\uff0c\u5973\u513f\u7684\u54ed\u558a\u58f0\u201c\u5988\u5988\uff0c\u5988\u5988\u201d\u6e10\u6e10\u9690\u53bb\uff0c\u8eab\u540e\u9a6c\u8e44\u58f0\u5374\u8d8a\u54cd\u8d8a\u8fd1\uff0c\u5fc3\u4e2d\u9ed8\u9ed8\u7977\u795d\uff1a\u201c\u8001\u5929\u554a\u8001\u5929\uff0c\u613f\u4f60\u4fdd\u4f51\u79c0\u513f\u50cf\u6211\u4e00\u822c\uff0c\u5ac1\u7740\u4e2a\u597d\u4e08\u592b\uff0c\u867d\u7136\u4e00\u751f\u98a0\u6c9b\u6d41\u79bb\uff0c\u5374\u662f\u4e00\u751f\u5feb\u6d3b\uff01\u201d \u3000\u3000\u5979\u6574\u4e86\u6574\u8863\u886b\uff0c\u63a0\u597d\u4e86\u5934\u53d1\uff0c\u8f6c\u77ac\u95f4\u6570\u5341\u9a91\u9a6c\u5148\u540e\u9a70\u5230\uff0c\u5f53\u5148\u4e00\u4eba\u662f\u5415\u6881\u4e09\u6770\u4e2d\u8001\u4e8c\u53f2\u4ef2\u4fca\u3002 \u3000\u3000\u5415\u6881\u4e09\u6770\u662f\u7ed3\u4e49\u5144\u5f1f\u3002\u8001\u5927\u201c\u795e\u5200\u9707\u5173\u897f\u201d\u970d\u5143\u9f99\uff0c\u4fbf\u662f\u6740\u6b7b\u767d\u9a6c\u674e\u4e09\u7684\u866c\u9aef\u6c49\u5b50\u3002\u8001\u4e8c\u201c\u6885\u82b1\u67aa\u201d\u53f2\u4ef2\u4fca\u662f\u4e2a\u7626\u7626\u957f\u957f\u7684\u6c49\u5b50\u3002\u8001\u4e09\u201c\u9752\u87d2\u5251\u201d\u9648\u8fbe\u6d77\u77ed\u5c0f\u7cbe\u608d\uff0c\u539f\u662f\u8fbd\u4e1c\u9a6c\u8d3c\u51fa\u8eab\uff0c\u540e\u6765\u5374\u5728\u5c71\u897f\u843d\u811a\uff0c\u548c\u970d\u53f2\u4e8c\u4eba\u610f\u6c14\u76f8\u6295\uff0c\u5728\u5c71\u897f\u7701\u592a\u8c37\u53bf\u5f00\u8bbe\u4e86\u664b\u5a01\u9556\u5c40\u3002 \u3000\u3000\u53f2\u4ef2\u4fca\u548c\u767d\u9a6c\u674e\u4e09\u7684\u59bb\u5b50\u4e0a\u5b98\u8679\u539f\u662f\u540c\u95e8\u5e08\u5144\u59b9\uff0c\u4e24\u4eba\u81ea\u5e7c\u4e00\u8d77\u5b66\u827a\u3002\u53f2\u4ef2\u4fca\u5fc3\u4e2d\u4e00\u76f4\u7231\u7740\u8fd9\u4e2a\u5a07\u5c0f\u6e29\u67d4\u7684\u5c0f\u5e08\u59b9\uff0c\u5e08\u7236\u4e5f\u6709\u610f\u4ece\u4e2d\u64ae\u5408\uff0c\u56e0\u6b64\u540c\u95e8\u7684\u5e08\u5144\u5f1f\u4eec\u65e9\u628a\u4ed6\u4eec\u5f53\u4f5c\u662f\u4e00\u5bf9\u672a\u5a5a\u592b\u5987\u3002\u5c82\u77e5\u4e0a\u5b98\u8679\u65e0\u610f\u4e2d\u548c\u767d\u9a6c\u674e\u4e09\u76f8\u9047\uff0c\u7adf\u5c14\u4e00\u89c1\u949f\u60c5\uff0c\u5bb6\u4e2d\u4e0d\u8bb8\u4ed6\u4fe9\u7684\u5a5a\u4e8b\uff0c\u4e0a\u5b98\u8679\u4fbf\u8ddf\u7740\u4ed6\u8dd1\u4e86\u3002\u53f2\u4ef2\u4fca\u4f24\u5fc3\u4e4b\u4f59\uff0c\u5927\u75c5\u4e86\u4e00\u573a\uff0c\u6027\u60c5\u4e5f\u4ece\u6b64\u53d8\u4e86\u3002\u4ed6\u5bf9\u5e08\u59b9\u59cb\u7ec8\u4f59\u60c5\u4e0d\u65ad\uff0c\u4e5f\u4e00\u76f4\u6ca1\u5a36\u4eb2\u3002 \u3000\u3000\u4e00\u522b\u5341\u5e74\uff0c\u60f3\u4e0d\u5230\u5415\u6881\u4e09\u6770\u548c\u674e\u4e09\u592b\u5987\u7adf\u5728\u7518\u51c9\u9053\u4e0a\u91cd\u9022\uff0c\u66f4\u4e3a\u4e86\u4e89\u593a\u4e00\u5f20\u5730\u56fe\u800c\u52a8\u8d77\u624b\u6765\u3002\u4ed6\u4eec\u516d\u5341\u4f59\u4eba\u56f4\u653b\u674e\u4e09\u592b\u5987\uff0c\u4ece\u7518\u51c9\u76f4\u8ffd\u9010\u5230\u4e86\u56de\u7586\u3002\u53f2\u4ef2\u4fca\u5992\u6068\u4ea4\u8ff8\uff0c\u51fa\u624b\u5c24\u72e0\uff0c\u674e\u4e09\u80cc\u4e0a\u90a3\u652f\u957f\u7bad\uff0c\u5c31\u662f\u4ed6\u6697\u4e2d\u5c04\u7684\u3002 \u3000\u3000\u8fd9\u65f6\u674e\u4e09\u7ec8\u4e8e\u4e27\u8eab\u5927\u6f20\u4e4b\u4e2d\uff0c\u53f2\u4ef2\u4fca\u9a91\u9a6c\u9a70\u6765\uff0c\u53ea\u89c1\u4e0a\u5b98\u8679\u5b64\u96f6\u96f6\u7684\u7ad9\u5728\u4e00\u7247\u5927\u5e73\u91ce\u4e0a\uff0c\u4e0d\u7531\u5f97\u9690\u9690\u6709\u4e9b\u5185\u759a\uff1a\u201c\u6211\u4eec\u6740\u4e86\u5979\u7684\u4e08\u592b\u3002\u4ece\u4eca\u800c\u540e\uff0c\u8fd9\u4e00\u751f\u4e2d\u6211\u8981\u597d\u597d\u7684\u5f85\u5979\u3002\u201d\u5927\u6f20\u4e0a\u7684\u897f\u98ce\u5439\u52a8\u7740\u5979\u7684\u8863\u5e26\uff0c\u5c31\u8ddf\u5341\u5e74\u4ee5\u524d\uff0c\u5728\u5e08\u7236\u7684\u7ec3\u6b66\u573a\u4e0a\u770b\u5230\u5979\u65f6\u4e00\u6a21\u4e00\u6837\u3002\u4e0a\u5b98\u8679\u7684\u5175\u5203\u662f\u4e00\u5bf9\u5315\u9996\uff0c\u4e00\u628a\u91d1\u67c4\uff0c\u4e00\u628a\u94f6\u67c4\uff0c\u6c5f\u6e56\u4e0a\u6709\u4e2a\u5916\u53f7\uff0c\u53eb\u4f5c\u2018\u91d1\u94f6\u5c0f\u5251\u4e09\u5a18\u5b50\u201d\u3002 \u3000\u3000\u8fd9\u65f6\u5979\u624b\u4e2d\u5374\u4e0d\u62ff\u5175\u5203\uff0c\u8138\u4e0a\u9732\u7740\u6de1\u6de1\u7684\u5fae\u7b11\u3002 \u3000\u3000\u53f2\u4ef2\u4fca\u5fc3\u4e2d\u84e6\u5730\u5347\u8d77\u4e86\u6307\u671b\uff0c\u80f8\u53e3\u53d1\u70ed\uff0c\u82cd\u767d\u7684\u8138\u4e0a\u6d8c\u8d77\u4e86\u4e00\u9635\u7ea2\u6f6e\u3002\u4ed6\u5c06\u6885\u82b1\u67aa\u5f80\u9a6c\u978d\u4e00\u6401\uff0c\u7ffb\u8eab\u4e0b\u9a6c\uff0c\u53eb\u9053\uff1a\u201c\u5e08\u59b9\uff01\u201d \u3000\u3000\u4e0a\u5b98\u8679\u9053\uff1a\u201c\u674e\u4e09\u6b7b\u5566\uff01\u201d\u53f2\u4ef2\u4fca\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u59b9\uff0c\u6211\u4eec\u5206\u522b\u4e86\u5341\u5e74\uff0c\u6211\u2026\u2026\u6211\u5929\u5929\u5728\u60f3\u4f60\u3002\u201d\u4e0a\u5b98\u8679\u5fae\u7b11\u9053\uff1a\u201c\u771f\u7684\u5417\uff1f\u4f60\u53c8\u5728\u9a97\u4eba\u3002\u201d\u53f2\u4ef2\u4fca\u4e00\u9897\u5fc3\u6026\u6026\u4e71\u8df3\uff0c\u8fd9\u4e2a\u7b11\u9765\uff0c\u8fd9\u822c\u5a07\u55d4\uff0c\u8ddf\u5341\u5e74\u524d\u90a3\u4e2a\u5c0f\u59d1\u5a18\u6ca1\u534a\u70b9\u5206\u522b\u3002\u4ed6\u67d4\u58f0\u9053\uff1a\u201c\u5e08\u59b9\uff0c\u4ee5\u540e\u4f60\u8ddf\u7740\u6211\uff0c\u6c38\u8fdc\u4e0d\u6559\u4f60\u53d7\u534a\u70b9\u59d4\u5c48\u3002\u201d\u4e0a\u5b98\u8679\u773c\u4e2d\u5ffd\u7136\u95ea\u51fa\u4e86\u5947\u5f02\u7684\u5149\u8292\uff0c\u53eb\u9053\uff1a\u201c\u5e08\u54e5\uff0c\u4f60\u5f85\u6211\u771f\u597d\uff01\u201d\u5f20\u5f00\u53cc\u81c2\uff0c\u5f80\u4ed6\u6000\u4e2d\u6251\u53bb\u3002 \u3000\u3000\u53f2\u4ef2\u4fca\u5927\u559c\uff0c\u4f38\u5f00\u624b\u5c06\u5979\u7d27\u7d27\u7684\u6402\u4f4f\u4e86\u3002\u970d\u5143\u9f99\u548c\u9648\u8fbe\u6d77\u76f8\u89c6\u4e00\u7b11\uff0c\u5fc3\u60f3\uff1a\u201c\u8001\u4e8c\u5bb3\u4e86\u5341\u5e74\u76f8\u601d\u75c5\uff0c\u4eca\u65e5\u7ec8\u4e8e\u5f97\u507f\u5fc3\u613f\u3002\u201d \u3000\u3000\u53f2\u4ef2\u4fca\u9f3b\u4e2d\u53ea\u95fb\u5230\u4e00\u9635\u6de1\u6de1\u7684\u5e7d\u9999\uff0c\u5fc3\u91cc\u8ff7\u8ff7\u7cca\u7cca\u7684\uff0c\u53c8\u611f\u5230\u4e0a\u5b98\u8679\u7684\u53cc\u624b\u4e5f\u8fd8\u62b1\u7740\u81ea\u5df1\uff0c\u771f\u4e0d\u76f8\u4fe1\u8fd9\u662f\u771f\u7684\u3002\u7a81\u7136\u4e4b\u95f4\uff0c\u5c0f\u8179\u4e0a\u611f\u5230\u4e00\u9635\u5267\u75db\uff0c\u50cf\u4ec0\u4e48\u5229\u5668\u63d2\u4e86\u8fdb\u6765\u3002\u4ed6\u5927\u53eb\u4e00\u58f0\uff0c\u8fd0\u52b2\u53cc\u81c2\uff0c\u8981\u5c06\u4e0a\u5b98\u8679\u63a8\u5f00\uff0c\u54ea\u77e5\u5979\u53cc\u81c2\u7d27\u7d27\u62b1\u7740\u4ed6\u6b7b\u547d\u4e0d\u653e\uff0c\u7ec8\u4e8e\u4e24\u4eba\u4e00\u8d77\u5012\u5728\u5730\u4e0b\u3002 \u3000\u3000\u8fd9\u4e00\u7740\u53d8\u8d77\u4ed3\u5352\uff0c\u970d\u5143\u9f99\u548c\u9648\u8fbe\u6d77\u4e00\u60ca\u4e4b\u4e0b\uff0c\u6025\u5fd9\u7ffb\u8eab\u4e0b\u9a6c\uff0c\u4e0a\u524d\u62a2\u6551\u3002\u6273\u8d77\u4e0a\u5b98\u8679\u7684\u8eab\u5b50\u65f6\uff0c\u53ea\u89c1\u5979\u80f8\u53e3\u4e00\u6ee9\u9c9c\u8840\uff0c\u63d2\u7740\u4e00\u628a\u5c0f\u5c0f\u7684\u91d1\u67c4\u5315\u9996\uff0c\u53e6\u4e00\u628a\u94f6\u67c4\u5315\u9996\uff0c\u5374\u63d2\u5728\u53f2\u4ef2\u4fca\u7684\u5c0f\u8179\u4e4b\u4e2d\uff0c\u539f\u6765\u91d1\u94f6\u5c0f\u5251\u4e09\u5a18\u5b50\u51b3\u5fc3\u4e00\u6b7b\u6b89\u592b\uff0c\u5728\u8863\u886b\u4e2d\u6697\u85cf\u53cc\u5251\uff0c\u4e00\u5251\u5411\u5916\uff0c\u4e00\u5251\u5411\u5df1\u3002\u53f2\u4ef2\u4fca\u4e00\u62b1\u7740\u5979\uff0c\u4e24\u4eba\u540c\u65f6\u4e2d\u5251\u3002 \u3000\u3000\u4e0a\u5b98\u8679\u5f53\u573a\u6c14\u7edd\uff0c\u53f2\u4ef2\u4fca\u5374\u4e00\u65f6\u4e0d\u5f97\u6bd9\u547d\uff0c\u60f3\u5230\u81ea\u5df1\u547d\u4e27\u5e08\u59b9\u4e4b\u624b\uff0c\u5fc3\u4e2d\u7684\u60b2\u75db\uff0c\u6bd4\u8eab\u4e0a\u7684\u521b\u4f24\u66f4\u662f\u96be\u53d7\uff0c\u53eb\u9053\uff1a\u201c\u4e09\u5f1f\u5feb\u5e2e\u6211\u4e86\u65ad\uff0c\u514d\u6211\u591a\u53d7\u75db\u82e6\u3002\u201d\u9648\u8fbe\u6d77\u89c1\u4ed6\u4f24\u91cd\u96be\u6cbb\uff0c\u773c\u671b\u5927\u54e5\u3002\u970d\u5143\u9f99\u70b9\u70b9\u5934\u3002\u9648\u8fbe\u6d77\u4e00\u54ac\u7259\uff0c\u633a\u5251\u5bf9\u51c6\u4e86\u53f2\u4ef2\u4fca\u7684\u5fc3\u53e3\u523a\u5165\u3002 \u3000\u3000\u970d\u5143\u9f99\u53f9\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u91d1\u94f6\u5c0f\u5251\u4e09\u5a18\u5b50\u7adf\u7136\u8fd9\u822c\u70c8\u6027\u3002\u201d\u8fd9\u65f6\u624b\u4e0b\u4e00\u540d\u9556\u5934\u9a70\u9a6c\u6765\u62a5\uff1a\u201c\u767d\u9a6c\u674e\u4e09\u7684\u5c38\u8eab\u4e0a\u53c8\u641c\u4e86\u4e00\u904d\uff0c\u6ca1\u6709\u5730\u56fe\u3002\u201d\u970d\u5143\u9f99\u6307\u7740\u4e0a\u5b98\u8679\u9053\uff1a\u201c\u90a3\u4e48\u5b9a\u662f\u5728\u5979\u8eab\u4e0a\u3002\u201d \u3000\u3000\u4e00\u756a\u7ec6\u7ec6\u641c\u7d22\uff0c\u4e0a\u5b98\u8679\u8eab\u4e0a\u9664\u4e86\u96f6\u788e\u94f6\u4e24\u3001\u51e0\u4ef6\u66ff\u6362\u8863\u670d\u4e4b\u5916\uff0c\u518d\u65e0\u522b\u7269\u3002\u970d\u5143\u9f99\u548c\u9648\u8fbe\u6d77\u9762\u9762\u76f8\u89d1\uff0c\u53c8\u662f\u5931\u671b\uff0c\u53c8\u662f\u5947\u602a\u3002\u4ed6\u4eec\u4ece\u7518\u51c9\u9053\u4e0a\u8ffd\u5230\u56de\u7586\uff0c\u59cb\u7ec8\u7d27\u7d27\u76ef\u7740\u674e\u4e09\u592b\u5987\uff0c\u5730\u56fe\u5982\u5728\u4e2d\u9014\u8f6c\u624b\uff0c\u51b3\u4e0d\u80fd\u9003\u8fc7\u4ed6\u4eec\u6570\u5341\u4eba\u7684\u773c\u775b\uff0c\u4f55\u51b5\u4ed6\u592b\u5987\u820d\u547d\u4fdd\u56fe\uff0c\u7edd\u65e0\u968f\u4fbf\u4ea4\u7ed9\u65c1\u4eba\u4e4b\u7406\u3002\u9648\u8fbe\u6d77\u518d\u5c06\u4e0a\u5b98\u8679\u5c0f\u5305\u88f9\u4e2d\u4e4b\u7269\u7ec6\u7ec6\u68c0\u89c6\u4e00\u904d\uff0c\u7ffb\u5230\u4e00\u5957\u5c0f\u5973\u5b69\u7684\u886b\u88e4\u65f6\uff0c\u731b\u5730\u60f3\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u5feb\u8ffd\u90a3\u5c0f\u5973\u5b69\uff01\u201d\u970d\u5143\u9f99\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u7528\u614c\uff0c\u8c05\u8fd9\u5973\u5a03\u5a03\u5728\u5927\u6f20\u4e0a\u9003\u5f97\u5230\u54ea\u91cc\uff1f\u201d\u5de6\u81c2\u4e00\u6325\uff0c\u53eb\u9053\uff1a\u201c\u7559\u4e0b\u4e24\u4eba\u628a\u53f2\u4e8c\u7237\u5b89\u846c\u4e86\uff0c\u4f59\u4e0b\u7684\u8ddf\u6211\u6765\uff01\u201d \u3000\u3000\u4e00\u63d0\u9a6c\u7f30\uff0c\u5f53\u5148\u9a70\u53bb\u3002\u8e44\u58f0\u6742\u6c93\uff0c\u5406\u559d\u8fde\u8fde\uff0c\u767e\u4f59\u5339\u9a6c\u8ffd\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u90a3\u5c0f\u5973\u5b69\u9a70\u51fa\u5df2\u4e45\uff0c\u8fd9\u65f6\u65e9\u5728\u4e8c\u5341\u4f59\u91cc\u4e4b\u5916\u3002\u53ea\u662f\u5728\u5e73\u5766\u65e0\u57a0\u7684\u5927\u6f20\u4e4b\u4e0a\uff0c\u4e00\u773c\u671b\u53bb\u770b\u5f97\u5230\u5341\u4f59\u91cc\u8fdc\u8fd1\uff0c\u90a3\u5c0f\u5973\u5b69\u867d\u5df2\u9003\u8fdc\uff0c\u65f6\u5019\u4e00\u957f\uff0c\u7ec8\u80fd\u8ffd\u4e0a\u3002\u679c\u7136\u8d76\u5230\u508d\u665a\uff0c\u9648\u8fbe\u6d77\u5ffd\u7136\u5927\u58f0\u6b22\u547c\uff1a\u201c\u5728\u524d\u9762\uff01\u201d \u3000\u3000\u53ea\u89c1\u8fdc\u8fdc\u4e00\u4e2a\u9ed1\u70b9\uff0c\u6b63\u5728\u5929\u5730\u4ea4\u754c\u5904\u79fb\u52a8\u3002\u8981\u77e5\u90a3\u767d\u9a6c\u867d\u7136\u795e\u9a8f\uff0c\u4f46\u81ea\u671d\u81f3\u665a\u8db3\u4e0d\u505c\u8e44\u7684\u5954\u8dd1\uff0c\u7ec8\u4e8e\u4e5f\u652f\u6301\u4e0d\u4f4f\u4e86\u3002 \u3000\u3000\u970d\u5143\u9f99\u548c\u9648\u8fbe\u6d77\u4e0d\u4f4f\u6389\u6362\u751f\u529b\u5750\u9a91\uff0c\u6e10\u6e10\u8ffd\u8fd1\u3002 \u3000\u3000\u5c0f\u5973\u5b69\u674e\u6587\u79c0\u4f0f\u5728\u767d\u9a6c\u80cc\u4e0a\uff0c\u5fc3\u529b\u4ea4\u75b2\uff0c\u65e9\u5df2\u660f\u660f\u7761\u53bb\u3002 \u3000\u3000\u5979\u4e00\u6574\u65e5\u4e0d\u996e\u4e0d\u98df\uff0c\u5728\u5927\u6c99\u6f20\u7684\u70c8\u65e5\u4e0b\u6652\u5f97\u53e3\u5507\u90fd\u7126\u4e86\u3002\u767d\u9a6c\u751a\u6709\u7075\u6027\uff0c\u77e5\u9053\u540e\u9762\u8ffd\u6765\u7684\u654c\u4eba\u5c06\u4e0d\u5229\u4e8e\u5c0f\u4e3b\u4eba\uff0c\u8fce\u7740\u8840\u4e5f\u4f3c\u7ea2\u7684\u5915\u9633\uff0c\u594b\u529b\u5954\u8dd1\u3002\u7a81\u7136\u4e4b\u95f4\uff0c\u524d\u8db3\u63d0\u8d77\uff0c\u957f\u5636\u4e00\u58f0\uff0c\u5b83\u55c5\u5230\u4e86\u4e00\u80a1\u7279\u5f02\u7684\u6c14\u606f\uff0c\u5636\u58f0\u4e2d\u9690\u9690\u6709\u6050\u6016\u4e4b\u610f\u3002 \u3000\u3000\u970d\u5143\u9f99\u548c\u9648\u8fbe\u6d77\u90fd\u662f\u6b66\u529f\u7cbe\u6e5b\uff0c\u957f\u9014\u9a70\u9a8b\uff0c\u539f\u4e0d\u5728\u610f\uff0c\u4f46\u8fd9\u65f6\u4e24\u4eba\u90fd\u611f\u5230\u80f8\u53e3\u585e\u95f7\uff0c\u6c14\u5598\u96be\u5f53\u3002\u970d\u5143\u9f99\u9053\uff1a\u201c\u4e09\u5f1f\uff0c\u597d\u50cf\u6709\u70b9\u4e0d\u5bf9\uff01\u201d\u9648\u8fbe\u6d77\u6e38\u76ee\u56db\u987e\uff0c\u6253\u91cf\u5468\u906d\u60c5\u666f\uff0c\u53ea\u89c1\u897f\u5317\u89d2\u4e0a\u8840\u7ea2\u7684\u5915\u9633\u4e4b\u65c1\uff0c\u5347\u8d77\u4e00\u7247\u9ec4\u8499\u8499\u7684\u4e91\u96fe\uff0c\u9ec4\u4e91\u4e2d\u4e0d\u4f4f\u6709\u7d2b\u8272\u7684\u5149\u8292\u95ea\u52a8\uff0c\u666f\u8272\u4e4b\u5947\u4e3d\uff0c\u5b9e\u662f\u751f\u5e73\u4ece\u6240\u672a\u7779\u3002 \u3000\u3000\u4f46\u89c1\u90a3\u9ec4\u4e91\u5927\u5f97\u597d\u5feb\uff0c\u4e0d\u5230\u4e00\u987f\u996d\u65f6\u5206\uff0c\u5df2\u5c06\u534a\u8fb9\u5929\u90fd\u906e\u4f4f\u4e86\u3002\u8fd9\u65f6\u9a6c\u961f\u4e2d\u6570\u5341\u4eba\u4e2a\u4e2a\u6c57\u5982\u96e8\u4e0b\uff0c\u6c14\u5598\u8fde\u8fde\u3002\u9648\u8fbe\u6d77\u9053\uff1a\u201c\u5927\u54e5\uff0c\u50cf\u662f\u6709\u5927\u98ce\u6c99\u3002\u201d\u970d\u5143\u9f99\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5feb\u8ffd\uff0c\u5148\u628a\u5973\u5a03\u5a03\u6349\u5230\uff0c\u518d\u60f3\u6cd5\u8eb2\u2026\u2026\u201d\u4e00\u53e5\u8bdd\u672a\u6bd5\uff0c\u7a81\u7136\u4e00\u80a1\u75be\u98ce\u522e\u5230\uff0c\u5e26\u7740\u4e00\u5927\u7247\u9ec4\u6c99\uff0c\u53ea\u5439\u5f97\u4ed6\u6ee1\u53e3\u6ee1\u9f3b\u90fd\u662f\u6c99\u571f\uff0c\u4e0b\u534a\u622a\u8bdd\u4e5f\u8bf4\u4e0d\u51fa\u6765\u4e86\u3002 \u3000\u3000\u5927\u6f20\u4e0a\u7684\u98ce\u6c99\u8bf4\u6765\u4fbf\u6765\uff0c\u970e\u65f6\u95f4\u5927\u98ce\u5377\u5730\u800c\u81f3\u3002\u4e03\u516b\u4eba\u8eab\u5b50\u4e00\u6643\uff0c\u90fd\u88ab\u5927\u98ce\u5439\u4e0b\u9a6c\u6765\u3002\u970d\u5143\u9f99\u5927\u53eb\uff1a\u201c\u5927\u4f19\u513f\u4e0b\u9a6c\uff0c\u56f4\u62e2\u6765\uff01\u201d \u3000\u3000\u4f17\u4eba\u529b\u6297\u98ce\u6c99\uff0c\u5c06\u4e00\u767e\u591a\u5339\u5065\u9a6c\u62c9\u4e86\u8fc7\u6765\uff0c\u56f4\u6210\u4e00\u4e2a\u5927\u5708\u5b50\uff0c\u4eba\u9a6c\u4e00\u9f50\u5367\u5012\u3002\u5404\u4eba\u624b\u633d\u7740\u624b\uff0c\u9760\u5728\u9a6c\u8179\u4e4b\u4e0b\uff0c\u53ea\u89c9\u75be\u98ce\u5e26\u7740\u9ec4\u6c99\u5439\u5728\u8138\u4e0a\uff0c\u6709\u5982\u5200\u5272\u4e00\u822c\uff0c\u8138\u4e0a\u624b\u4e0a\uff0c\u767b\u65f6\u8d77\u4e86\u4e00\u6761\u6761\u8840\u75d5\u3002 \u3000\u3000\u8fd9\u4e00\u961f\u867d\u7136\u4eba\u9a6c\u4f17\u591a\uff0c\u4f46\u5728\u65e0\u8fb9\u65e0\u9645\u7684\u5927\u6c99\u6f20\u4e4b\u4e2d\uff0c\u5728\u90a3\u906e\u5929\u94fa\u5730\u7684\u5927\u98ce\u6c99\u4e0b\uff0c\u4fbf\u5982\u5927\u6d77\u6d0b\u4e2d\u7684\u4e00\u53f6\u5c0f\u821f\u4e00\u822c\uff0c\u53ea\u80fd\u542c\u5929\u7531\u547d\uff0c\u5168\u65e0\u534a\u5206\u81ea\u4e3b\u4e4b\u529b\u3002 \u3000\u3000\u98ce\u6c99\u8d8a\u522e\u8d8a\u731b\uff0c\u4eba\u9a6c\u8eab\u4e0a\u7684\u9ec4\u6c99\u8d8a\u5806\u8d8a\u539a\u2026\u2026\u3002 \u3000\u3000\u8fde\u970d\u5143\u9f99\u548c\u9648\u8fbe\u6d77\u90a3\u6837\u4ec0\u4e48\u4e5f\u4e0d\u6015\u7684\u527d\u608d\u6c49\u5b50\uff0c\u8fd9\u65f6\u5728\u5929\u5730\u53d8\u8272\u7684\u5927\u98ce\u66b4\u5a01\u529b\u4e4b\u4e0b\uff0c\u4e5f\u53ea\u6709\u6218\u6817\u7684\u4efd\u513f\u3002\u8fd9\u4e24\u4eba\u5fc3\u5e95\uff0c\u540c\u65f6\u95ea\u8d77\u4e00\u4e2a\u5ff5\u5934\uff1a\u201c\u6ca1\u6765\u7531\u7684\u8981\u627e\u4ec0\u4e48\u9ad8\u660c\u8ff7\u5bab\uff0c\u4ece\u5c71\u897f\u5df4\u5df4\u7684\u8d76\u5230\u8fd9\u5927\u6c99\u6f20\u4e2d\u6765\uff0c\u5374\u846c\u8eab\u5728\u8fd9\u513f\u3002\u201d \u3000\u3000\u5927\u98ce\u547c\u5578\u7740\uff0c\u50cf\u5343\u5343\u4e07\u4e07\u4e2a\u6076\u9b3c\u5728\u540c\u65f6\u53d1\u5a01\u3002 \u3000\u3000\u5927\u6f20\u4e0a\u7684\u98ce\u66b4\u547c\u5578\u4e86\u4e00\u591c\uff0c\u76f4\u5230\u7b2c\u4e8c\u5929\u65e9\u6668\uff0c\u624d\u6e10\u6e10\u7684\u5e73\u9759\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u970d\u5143\u9f99\u548c\u9648\u8fbe\u6d77\u4ece\u9ec4\u6c99\u4e4b\u4e2d\u722c\u8d77\u8eab\u6765\uff0c\u68c0\u70b9\u4eba\u9a6c\uff0c\u603b\u7b97\u635f\u5931\u4e0d\u5927\uff0c\u6b7b\u4e86\u4e24\u540d\u4f19\u4f34\uff0c\u4e94\u5339\u9a6c\u3002\u4f46\u4eba\u4eba\u90fd\u5df2\u71ac\u5f97\u7b4b\u75b2\u529b\u5c3d\uff0c\u66f4\u7cdf\u7684\u662f\uff0c\u767d\u9a6c\u80cc\u4e0a\u7684\u5c0f\u5973\u5b69\u4e0d\u77e5\u5230\u4e86\u4f55\u5904\uff0c\u5341\u4e5d\u662f\u846c\u8eab\u5728\u8fd9\u573a\u5927\u98ce\u6c99\u4e2d\u4e86\u3002\u8eab\u8d1f\u6b66\u529f\u7684\u7c97\u58ee\u6c49\u5b50\u5c1a\u4e14\u62b5\u4e0d\u4f4f\uff0c\u4f55\u51b5\u8fd9\u6837\u5a07\u5ae9\u7684\u4e00\u4e2a\u5c0f\u5973\u5b69\u513f\u3002 \u3000\u3000\u4f17\u4eba\u5728\u6c99\u6f20\u4e0a\u751f\u706b\u505a\u996d\uff0c\u4f11\u606f\u4e86\u534a\u5929\uff0c\u970d\u5143\u9f99\u4f20\u4e0b\u53f7\u4ee4\uff1a \u3000\u3000\u201c\u8c01\u53d1\u73b0\u767d\u9a6c\u548c\u5c0f\u5973\u5b69\u7684\u8e2a\u8ff9\uff0c\u8d4f\u9ec4\u91d1\u4e94\u5341\u4e24\uff01\u201d\u8ddf\u968f\u4ed6\u6765\u5230\u56de\u7586\u7684\uff0c\u4e2a\u4e2a\u90fd\u662f\u664b\u9655\u7518\u51c9\u4e00\u5e26\u7684\u6c5f\u6e56\u8c6a\u5ba2\uff0c\u51fa\u95e8\u5343\u91cc\u53ea\u4e3a\u8d22\uff0c\u4e94\u5341\u4e24\u9ec4\u91d1\u53ef\u4e0d\u662f\u5c0f\u6570\u76ee\u3002\u4f17\u4eba\u6b22\u58f0\u547c\u5578\uff0c\u4e94\u5341\u591a\u4eba\u5728\u83bd\u83bd\u9ec4\u6c99\u4e0a\u6563\u4e86\u5f00\u53bb\uff0c\u50cf\u4e00\u9762\u5927\u6247\u5b50\u822c\u3002\u201c\u767d\u9a6c\uff0c\u5c0f\u5973\u5b69\uff0c\u4e94\u5341\u4e24\u9ec4\u91d1\uff01\u201d\u6bcf\u4e2a\u4eba\u5fc3\u4e2d\uff0c\u90fd\u662f\u5728\u8f6c\u7740\u8fd9\u4e09\u4e2a\u5ff5\u5934\u3002 \u3000\u3000\u6709\u7684\u4eba\u4e00\u76f4\u5411\u897f\uff0c\u6709\u7684\u5411\u897f\u5317\uff0c\u6709\u7684\u5411\u897f\u5357\uff0c\u7ea6\u5b9a\u5929\u9ed1\u4e4b\u65f6\uff0c\u5728\u6b63\u897f\u516d\u5341\u91cc\u5904\u4f1a\u5408\u3002 \u3000\u3000\u4e24\u5934\u86c7\u4e01\u540c\u8de8\u4e0a\u4e00\u5339\u5065\u9a6c\uff0c\u7eb5\u9a6c\u5411\u897f\u5317\u65b9\u51b2\u53bb\u3002\u4ed6\u662f\u664b\u5a01\u9556\u5c40\u4e2d\u5df2\u5e72\u4e86\u5341\u4e03\u5e74\u7684\u9556\u5e08\uff0c\u6b66\u529f\u867d\u7136\u7b97\u4e0d\u4e0a\u5982\u4f55\u4e86\u5f97\uff0c\u4f46\u7cbe\u660e\u5e72\u7ec3\uff0c\u5b9e\u662f\u5415\u6881\u4e09\u6770\u624b\u4e0b\u4e00\u540d\u6781\u5f97\u529b\u7684\u52a9\u624b\u3002\u4ed6\u4e00\u53e3\u6c14\u9a70\u51fa\u4e8c\u5341\u4f59\u91cc\uff0c\u4f17\u540c\u4f34\u90fd\u5df2\u5f71\u8e2a\u4e0d\u89c1\uff0c\u5728\u832b\u832b\u7684\u5927\u6f20\u4e2d\uff0c\u7a81\u7136\u8d77\u4e86\u5b64\u5bc2\u548c\u6050\u6016\u4e4b\u611f\u3002\u7eb5\u9a6c\u4e0a\u4e86\u4e00\u4e2a\u6c99\u4e18\uff0c\u5411\u524d\u671b\u53bb\uff0c\u53ea\u89c1\u897f\u5317\u89d2\u4e0a\u4e00\u7247\u9752\u7eff\uff0c\u9ad8\u8038\u7740\u4e03\u516b\u68f5\u5927\u67f3\u6811\u3002\u5728\u5bf8\u8349\u4e0d\u751f\u7684\u5927\u6c99\u6f20\u4e2d\u5ffd\u7136\u89c1\u5230\u8fd9\u4e00\u5927\u5757\u7eff\u6d32\uff0c\u5fc3\u4e2d\u5f53\u771f\u8bf4\u4e0d\u51fa\u7684\u6b22\u559c\uff1a \u3000\u3000\u201c\u8fd9\u5927\u7247\u7eff\u6d32\u4e2d\u5fc5\u6709\u6c34\u6cc9\uff0c\u5c31\u7b97\u6ca1\u6709\u4eba\u5bb6\uff0c\u5927\u961f\u4eba\u9a6c\u4e5f\u53ef\u597d\u597d\u7684\u5c06\u606f\u4e00\u756a\u3002\u201d\u4ed6\u8de8\u4e0b\u7684\u5750\u9a91\u4e5f\u671b\u89c1\u4e86\u6c34\u8349\uff0c\u9661\u7136\u95f4\u7cbe\u795e\u767e\u500d\uff0c\u4e0d\u7b49\u4e01\u540c\u63d0\u7f30\u50ac\u903c\uff0c\u6cfc\u524c\u524c\u653e\u5f00\u56db\u8e44\uff0c\u5954\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5341\u4f59\u91cc\u8def\u7a0b\u7247\u523b\u5373\u5230\uff0c\u8fdc\u8fdc\u671b\u53bb\uff0c\u4f46\u89c1\u4e00\u7247\u7eff\u6d32\uff0c\u671b\u4e0d\u5230\u8fb9\u9645\uff0c\u904d\u91ce\u90fd\u662f\u725b\u7f8a\u3002\u6781\u897f\u5904\u642d\u7740\u4e00\u4e2a\u4e2a\u5e10\u7bf7\uff0c\u5bc6\u5bc6\u5c42\u5c42\u7684\u7adf\u6709\u516d\u4e03\u767e\u4e2a\u3002 \u3000\u3000\u4e01\u540c\u89c1\u5230\u8fd9\u7b49\u58f0\u52bf\uff0c\u4e0d\u7531\u5f97\u5403\u4e86\u4e00\u60ca\u3002\u4ed6\u81ea\u5165\u56de\u7586\u4ee5\u6765\uff0c\u6240\u89c1\u5230\u7684\u5e10\u7bf7\u4eba\u5bb6\uff0c\u805a\u5728\u4e00\u8d77\u7684\u6700\u591a\u4e0d\u8fc7\u4e09\u56db\u5341\u4e2a\uff0c\u8fd9\u6837\u7684\u4e00\u4e2a\u5927\u90e8\u65cf\u5374\u662f\u7b2c\u4e00\u6b21\u89c1\u5230\u3002\u77a7\u90a3\u5e10\u7bf7\u5f0f\u6837\uff0c\u663e\u662f\u54c8\u8428\u514b\u65cf\u4eba\u3002 \u3000\u3000\u54c8\u8428\u514b\u4eba\u5728\u56de\u7586\u8bf8\u65cf\u4e2d\u6700\u4e3a\u52c7\u6b66\uff0c\u4e0d\u8bba\u7537\u5973\uff0c\u516d\u4e03\u5c81\u8d77\u5c31\u957f\u4e8e\u9a6c\u80cc\u4e4b\u4e0a\u3002\u7537\u5b50\u8eab\u4e0a\u4eba\u4eba\u5e26\u5200\uff0c\u9a91\u5c04\u5200\u672f\uff0c\u5a01\u9707\u897f\u9672\u3002 \u3000\u3000\u5411\u6765\u6709\u4e00\u53e5\u8bdd\u8bf4\u9053\uff1a\u201c\u4e00\u4e2a\u54c8\u8428\u514b\u4eba\uff0c\u62b5\u5f97\u4e00\u767e\u4e2a\u61e6\u592b\uff1b\u4e00\u767e\u4e2a\u54c8\u8428\u514b\u4eba\uff0c\u5c31\u53ef\u6a2a\u884c\u56de\u7586\u3002\u201d \u3000\u3000\u4e01\u540c\u66fe\u542c\u89c1\u8fc7\u8fd9\u53e5\u8bdd\uff0c\u5bfb\u601d\uff1a\u201c\u5728\u54c8\u8428\u514b\u7684\u90e8\u65cf\u4e4b\u4e2d\uff0c\u53ef\u5f97\u5c0f\u5fc3\u5728\u610f\u3002\u201d \u3000\u3000\u53ea\u89c1\u4e1c\u5317\u89d2\u7684\u4e00\u5ea7\u5c0f\u5c71\u811a\u4e0b\uff0c\u5b64\u96f6\u96f6\u7684\u6709\u4e00\u5ea7\u8349\u68da\u3002\u8fd9\u68da\u5c4b\u571f\u5899\u8349\u9876\uff0c\u5f62\u5f0f\u5b9b\u5982\u5185\u5730\u6c49\u4eba\u7684\u7816\u5c4b\uff0c\u53ea\u662f\u751a\u4e3a\u7b80\u964b\u3002\u4e01\u540c\u5fc3\u60f3\uff1a\u201c\u5148\u5230\u8fd9\u5c0f\u5c4b\u53bb\u77a7\u77a7\u3002\u201d\u4e8e\u662f\u7eb5\u9a6c\u5f80\u5c0f\u5c4b\u8d70\u53bb\u3002\u4ed6\u8de8\u4e0b\u7684\u5750\u9a91\u5df2\u997f\u4e86\u4e00\u65e5\u4e00\u591c\uff0c\u5ffd\u7136\u89c1\u5230\u6ee1\u5730\u9752\u8349\uff0c\u8d70\u4e00\u6b65\uff0c\u5403\u4e24\u53e3\uff0c\u884c\u5f97\u6781\u662f\u7f13\u6162\u3002 \u3000\u3000\u4e01\u540c\u63d0\u811a\u72e0\u547d\u5728\u9a6c\u809a\u4e0a\u4e00\u8e22\uff0c\u90a3\u9a6c\u5403\u75db\uff0c\u4e00\u53e3\u6c14\u5954\u5411\u5c0f\u5c4b\u3002\u4e01\u540c\u4e00\u659c\u773c\uff0c\u53ea\u89c1\u5c0f\u5c4b\u4e4b\u540e\u7cfb\u7740\u4e00\u5339\u9ad8\u5934\u767d\u9a6c\uff0c\u5065\u817f\u957f\u9b23\uff0c\u6b63\u662f\u767d\u9a6c\u674e\u4e09\u7684\u5750\u9a91\u3002\u4ed6\u5fcd\u4e0d\u4f4f\u53eb\u51fa\u58f0\u6765\uff1a\u201c\u767d\u9a6c\uff0c\u767d\u9a6c\u5728\u8fd9\u513f\uff01\u201d\u5fc3\u5ff5\u4e00\u52a8\uff0c\u7ffb\u8eab\u4e0b\u9a6c\uff0c\u4ece\u9774\u7b52\u4e2d\u62bd\u51fa\u4e00\u67c4\u950b\u5229\u7684\u77ed\u5200\uff0c\u7b3c\u5728\u5de6\u624b\u8863\u8896\u4e4b\u4e2d\uff0c\u6084\u6084\u7684\u63a9\u5411\u5c0f\u5c4b\u540e\u9762\uff0c\u6b63\u60f3\u63a2\u5934\u4ece\u7a97\u5b50\u5411\u5c4b\u5185\u5f20\u671b\uff0c\u51b7\u4e0d\u9632\u90a3\u767d\u9a6c\u201c\u545c\u54e9\u54e9\u2026\u2026\u201d\u4e00\u58f0\u957f\u5636\uff0c\u4f3c\u662f\u53d1\u89c9\u4e86\u4ed6\u3002 \u3000\u3000\u4e01\u540c\u5fc3\u4e2d\u6012\u9a82\uff1a\u201c\u755c\u7272\uff01\u201d\u5b9a\u4e00\u5b9a\u795e\uff0c\u518d\u5ea6\u63a2\u5934\u671b\u7a97\u4e2d\u5f20\u53bb\u65f6\uff0c\u54ea\u77e5\u7a97\u5185\u6709\u4e00\u5f20\u8138\u540c\u65f6\u63a2\u4e86\u4e0a\u6765\u3002\u4e01\u540c\u7684\u9f3b\u5b50\u521a\u597d\u548c\u4ed6\u7684\u9f3b\u5b50\u76f8\u78b0\uff0c\u4f46\u89c1\u8fd9\u4eba\u6ee1\u8138\u76b1\u7eb9\uff0c\u76ee\u5149\u70af\u70af\u3002\u4e01\u540c\u5927\u5403\u4e00\u60ca\uff0c\u53cc\u8db3\u4e00\u70b9\uff0c\u5012\u7eb5\u51fa\u53bb\uff0c\u559d\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d\u90a3\u4eba\u51b7\u51b7\u7684\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u5230\u6b64\u4f55\u5e72\uff1f\u201d\u8bf4\u7684\u5374\u662f\u6c49\u8bed\u3002 \u3000\u3000\u4e01\u540c\u60ca\u9b42\u7565\u5b9a\uff0c\u6ee1\u8138\u7b11\u5bb9\uff0c\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u59d3\u4e01\u540d\u540c\uff0c\u65e0\u610f\u95f4\u5230\u6b64\uff0c\u60ca\u52a8\u4e86\u8001\u4e08\u3002\u8bf7\u95ee\u8001\u4e08\u9ad8\u59d3\u5927\u540d\u3002\u201d\u90a3\u8001\u4eba\u9053\uff1a\u201c\u8001\u6c49\u59d3\u8ba1\u3002\u201d\u4e01\u540c\u966a\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u8ba1\u8001\u4e08\uff0c\u5927\u6c99\u6f20\u4e2d\u9047\u5230\u4e61\u4eb2\uff0c\u771f\u662f\u89c1\u5230\u4eb2\u4eba\u4e86\u3002\u5728\u4e0b\u6597\u80c6\u8981\u8ba8\u53e3\u8336\u559d\u3002\u201d\u8ba1\u8001\u4eba\u9053\uff1a\u201c\u4f60\u6709\u591a\u5c11\u4eba\u540c\u6765\uff1f\u201d\u4e01\u540c\u9053\uff1a\u201c\u4fbf\u662f\u5728\u4e0b\u4e00\u4eba\u5728\u6b64\u3002\u201d\u8ba1\u8001\u4eba\u54fc\u4e86\u4e00\u58f0\uff0c\u4f3c\u662f\u4e0d\u4fe1\uff0c\u51b7\u51b7\u7684\u773c\u5149\u5728\u4ed6\u8138\u4e0a\u6765\u6765\u56de\u56de\u7684\u626b\u89c6\u3002\u4e01\u540c\u7ed9\u4ed6\u77a7\u5f97\u5fc3\u795e\u4e0d\u5b9a\uff0c\u53ea\u6709\u5f3a\u7b11\u3002 \u3000\u3000\u4e00\u4e2a\u51b7\u51b7\u7684\u659c\u89c6\uff0c\u4e00\u4e2a\u7b11\u563b\u563b\u5730\u5341\u5206\u5c34\u5c2c\uff0c\u50f5\u6301\u7247\u523b\u3002\u8ba1\u8001\u4eba\u9053\uff1a\u201c\u8981\u559d\u8336\uff0c\u4fbf\u8d70\u5927\u95e8\uff0c\u4e0d\u7528\u722c\u7a97\u5b50\u5427\uff01\u201d\u4e01\u540c\u7b11\u9053\uff1a\u201c\u662f\uff0c\u662f\uff01\u201d\u8f6c\u8eab\u7ed5\u5230\u95e8\u524d\uff0c\u8d70\u4e86\u8fdb\u53bb\u3002\u5c0f\u5c4b\u4e2d\u9648\u8bbe\u7b80\u964b\uff0c\u4f46\u684c\u6905\u6574\u6d01\uff0c\u6253\u626b\u5f97\u5e72\u5e72\u51c0\u51c0\u3002\u4e01\u540c\u5750\u4e0b\u540e\u56db\u4e0b\u6253\u91cf\uff0c\u53ea\u89c1\u540e\u5802\u8f6c\u51fa\u4e00\u4e2a\u5c0f\u5973\u5b69\u6765\uff0c\u624b\u4e2d\u6367\u7740\u4e00\u7897\u8336\u3002\u4e24\u4eba\u76ee\u5149\u76f8\u63a5\uff0c\u90a3\u5973\u5b69\u5403\u4e86\u4e00\u60ca\uff0c\u545b\u5577\u4e00\u54cd\uff0c\u8336\u7897\u5931\u624b\u6389\u5728\u5730\u4e0b\uff0c\u6253\u5f97\u7c89\u788e\u3002 \u3000\u3000\u4e01\u540c\u767b\u65f6\u5fc3\u82b1\u6012\u653e\u3002\u8fd9\u5c0f\u5973\u5b69\u6b63\u662f\u970d\u5143\u9f99\u60ac\u4e0b\u91cd\u8d4f\u8981\u8ffd\u5bfb\u4e4b\u4eba\uff0c\u4ed6\u89c1\u5230\u767d\u9a6c\u540e\uff0c\u672c\u5df2\u6709\u516b\u5206\u6599\u5230\u90a3\u5973\u5b69\u4f1a\u5728\u5c4b\u4e2d\uff0c\u4f46\u9661\u7136\u95f4\u89c1\u5230\uff0c\u4ecd\u662f\u4e0d\u514d\u559c\u51fa\u671b\u5916\u3002 \u3000\u3000\u6628\u591c\u4e00\u665a\u5927\u98ce\u6c99\uff0c\u674e\u6587\u79c0\u660f\u6655\u5728\u9a6c\u80cc\u4e4b\u4e0a\uff0c\u4eba\u4e8b\u4e0d\u7701\uff0c\u767d\u9a6c\u95fb\u5230\u6c34\u8349\u6c14\u606f\uff0c\u51b2\u98ce\u5192\u6c99\uff0c\u5954\u5230\u4e86\u8fd9\u7eff\u8349\u539f\u4e0a\u3002\u8ba1\u8001\u4eba\u89c1\u5230\u5c0f\u5973\u5b69\u662f\u6c49\u4eba\u88c5\u675f\uff0c\u5fd9\u628a\u5979\u6551\u4e86\u4e0b\u6765\u3002\u534a\u591c\u4e2d\u674e\u6587\u79c0\u9192\u8f6c\uff0c\u4e0d\u89c1\u4e86\u7236\u6bcd\uff0c\u557c\u54ed\u4e0d\u6b62\u3002\u8ba1\u8001\u4eba\u89c1\u5979\u7389\u96ea\u53ef\u7231\uff0c\u4e0d\u7981\u5927\u8d77\u601c\u60dc\u4e4b\u5fc3\uff0c\u95ee\u5979\u4f55\u4ee5\u5230\u8fd9\u5927\u6f20\u6765\uff0c\u5979\u7236\u6bcd\u662f\u8c01\u3002\u674e\u6587\u79c0\u8bf4\u7236\u4eb2\u53eb\u4f5c\u201c\u767d\u9a6c\u674e\u4e09\u201d\uff0c\u5988\u5988\u5374\u5c31\u662f\u5988\u5988\uff0c\u53ea\u542c\u5230\u8ffd\u8d76\u4ed6\u4eec\u7684\u6076\u4eba\u8fdc\u8fdc\u53eb\u5979\u201c\u4e09\u5a18\u5b50\u201d\uff0c\u81f3\u4e8e\u5230\u56de\u7586\u6765\u5e72\u4ec0\u4e48\uff0c\u5979\u5374\u8bf4\u4e0d\u4e0a\u6765\u4e86\u3002 \u3000\u3000\u8ba1\u8001\u4eba\u5583\u5583\u7684\u9053\uff1a\u201c\u767d\u9a6c\u674e\u4e09\uff0c\u767d\u9a6c\u674e\u4e09\uff0c\u90a3\u662f\u6a2a\u884c\u6c5f\u5357\u7684\u4fa0\u76d7\uff0c\u600e\u5730\u5230\u56de\u7586\u6765\u5566\uff1f\u201d \u3000\u3000\u4ed6\u7ed9\u674e\u6587\u79c0\u9971\u9971\u7684\u559d\u4e86\u4e00\u5927\u7897\u4e73\u916a\uff0c\u8ba9\u5979\u7761\u4e86\u3002\u8001\u4eba\u5fc3\u4e2d\uff0c\u5374\u7ffb\u6765\u8986\u53bb\u7684\u60f3\u8d77\u4e86\u5341\u5e74\u6765\u7684\u5f80\u4e8b\uff0c\u601d\u6f6e\u8d77\u4f0f\uff0c\u518d\u4e5f\u7761\u4e0d\u7740\u4e86\u3002 \u3000\u3000\u674e\u6587\u79c0\u8fd9\u4e00\u89c9\u7761\u5230\u6b21\u65e5\u8fb0\u65f6\u624d\u9192\uff0c\u4e00\u8d77\u8eab\uff0c\u4fbf\u6c42\u8ba1\u7237\u7237\u5e26\u5979\u53bb\u5bfb\u7238\u7238\u5988\u5988\u3002\u5c31\u5728\u6b64\u65f6\uff0c\u4e24\u5934\u86c7\u4e01\u540c\u9b3c\u9b3c\u795f\u795f\u7684\u8fc7\u6765\uff0c\u5728\u7a97\u5916\u63a2\u5934\u63a2\u8111\uff0c\u8fd9\u4e00\u5207\u5168\u770b\u5728\u8ba1\u8001\u4eba\u7684\u773c\u4e2d\u3002 \u3000\u3000\u674e\u6587\u79c0\u624b\u4e2d\u7684\u8336\u7897\u4e00\u6454\u4e0b\uff0c\u8ba1\u8001\u4eba\u5e94\u58f0\u8d70\u4e86\u8fc7\u6765\u3002\u674e\u6587\u79c0\u5954\u8fc7\u53bb\u6251\u5728\u4ed6\u7684\u6000\u91cc\uff0c\u53eb\u9053\uff1a\u201c\u7237\u7237\uff0c\u4ed6\u2026\u2026\u4ed6\u5c31\u662f\u8ffd\u6211\u7684\u6076\u4eba\u3002\u201d\u8ba1\u8001\u4eba\u629a\u6478\u7740\u5979\u7684\u5934\u53d1\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u4e0d\u6015\uff0c\u4e0d\u6015\u3002\u4ed6\u4e0d\u662f\u6076\u4eba\u3002\u201d\u674e\u6587\u79c0\u9053\uff1a\u201c\u662f\u7684\uff0c\u662f\u7684\u3002\u4ed6\u4eec\u51e0\u5341\u4e2a\u4eba\u8ffd\u6211\u4eec\uff0c\u6253\u6211\u7238\u7238\u5988\u5988\u3002\u201d\u8ba1\u8001\u4eba\u5fc3\u60f3\uff1a\u201c\u767d\u9a6c\u674e\u4e09\u8ddf\u6211\u65e0\u4eb2\u65e0\u6545\uff0c\u4e0d\u77e5\u7ed3\u4e0b\u4e86\u4ec0\u4e48\u4ec7\u5bb6\uff0c\u6211\u53ef\u4e0d\u5fc5\u5377\u5165\u8fd9\u662f\u975e\u5708\u5b50\u3002\u201d \u3000\u3000\u4e01\u540c\u4fa7\u76ee\u6253\u91cf\u8ba1\u8001\u4eba\uff0c\u4f46\u89c1\u4ed6\u6ee1\u5934\u767d\u53d1\uff0c\u7adf\u65e0\u4e00\u6839\u662f\u9ed1\u7684\uff0c\u8eab\u6750\u751a\u662f\u9ad8\u5927\uff0c\u53ea\u662f\u5f13\u8170\u66f2\u80cc\uff0c\u8870\u8001\u5df2\u6781\uff0c\u5bfb\u601d\uff1a\u201c\u8fd9\u7cdf\u8001\u5934\u6ca1\u4e00\u767e\u5c81\uff0c\u4e5f\u6709\u4e5d\u5341\uff0c\u5c4b\u4e2d\u82e5\u65e0\u522b\u4eba\uff0c\u5c06\u4ed6\u4e00\u4e0b\u5b50\u6253\u6655\uff0c\u5e26\u4e86\u5973\u5b69\u548c\u767d\u9a6c\u4fbf\u8d70\uff0c\u514d\u5f97\u591c\u957f\u68a6\u591a\uff0c\u518d\u751f\u53d8\u6545\u3002\u201d\u7a81\u7136\u5c06\u624b\u638c\u653e\u5728\u53f3\u8033\u65c1\u8fb9\uff0c\u4f5c\u503e\u542c\u4e4b\u72b6\uff0c\u8bf4\u9053\uff1a\u201c\u6709\u4eba\u6765\u4e86\u3002\u201d\u8ddf\u7740\u5feb\u6b65\u8d70\u5230\u7a97\u53e3\u3002 \u3000\u3000\u8ba1\u8001\u4eba\u5374\u6ca1\u542c\u5230\u4eba\u58f0\uff0c\u4f46\u542c\u4e01\u540c\u8bf4\u5f97\u771f\u5207\uff0c\u8d70\u5230\u7a97\u53e3\u4e00\u671b\uff0c\u53ea\u89c1\u539f\u91ce\u4e0a\u725b\u7f8a\u4f4e\u5934\u56bc\u8349\uff0c\u56db\u4e0b\u91cc\u4e00\u7247\u5bc2\u9759\uff0c\u5e76\u65e0\u751f\u4eba\u5230\u6765\uff0c\u521a\u95ee\u4e86\u4e00\u53e5\uff1a\u201c\u54ea\u91cc\u6709\u4eba\u554a\uff1f\u201d\u5ffd\u542c\u5f97\u4e01\u540c\u4e00\u58f0\u72de\u7b11\uff0c\u5934\u9876\u638c\u98ce\u98d2\u7136\uff0c\u4e00\u638c\u731b\u5288\u4e0b\u6765\u3002 \u3000\u3000\u54ea\u77e5\u8ba1\u8001\u4eba\u867d\u662f\u8001\u6001\u9f99\u949f\uff0c\u8eab\u624b\u53ef\u7740\u5b9e\u654f\u6377\uff0c\u4e01\u540c\u7684\u624b\u638c\u4e0e\u4ed6\u5934\u9876\u76f8\u8ddd\u5c1a\u6709\u6570\u5bf8\uff0c\u4ed6\u8eab\u5f62\u4e00\u4fa7\uff0c\u5df2\u6ed1\u4e86\u5f00\u53bb\uff0c\u8ddf\u7740\u53cd\u624b\u4e00\u52fe\uff0c\u65bd\u5c55\u5927\u64d2\u62ff\u624b\uff0c\u5c06\u4ed6\u53f3\u8155\u52fe\u4f4f\u4e86\u3002\u4e01\u540c\u53d8\u62db\u751a\u662f\u8d3c\u6ed1\uff0c\u53f3\u624b\u4e00\u6323\u6ca1\u6323\u8131\uff0c\u5de6\u624b\u5411\u524d\u4e00\u9001\uff0c\u85cf\u5728\u8863\u8896\u4e2d\u7684\u5315\u9996\u5df2\u523a\u4e86\u51fa\u53bb\uff0c\u767d\u5149\u95ea\u5904\uff0c\u6ce2\u7684\u4e00\u54cd\uff0c\u5315\u9996\u950b\u5229\u7684\u5203\u53e3\u5df2\u523a\u5165\u8ba1\u8001\u4eba\u7684\u5de6\u80cc\u3002 \u3000\u3000\u674e\u6587\u79c0\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u554a\u54df\uff01\u201d\u5979\u8ddf\u7236\u6bcd\u5b66\u8fc7\u4e24\u5e74\u6b66\u529f\uff0c\u773c\u89c1\u8ba1\u8001\u4eba\u4e2d\u5200\uff0c\u7eb5\u8eab\u800c\u4e0a\uff0c\u4e24\u4e2a\u5c0f\u62f3\u5934\u4fbf\u5f80\u4e01\u540c\u80cc\u5fc3\u8170\u773c\u91cc\u6253\u53bb\u3002\u4fbf\u5728\u6b64\u65f6\uff0c\u8ba1\u8001\u4eba\u5de6\u624b\u4e00\u4e2a\u8098\u6376\uff0c\u6376\u4e2d\u4e86\u4e01\u540c\u7684\u5fc3\u53e3\uff0c\u8fd9\u4e00\u6376\u529b\u9053\u6781\u731b\uff0c\u4e01\u540c\u4f4e\u54fc\u4e00\u58f0\uff0c\u8eab\u5b50\u8f6f\u8f6f\u5782\u4e0b\uff0c\u59d4\u987f\u5728\u5730\uff0c\u53e3\u4e2d\u55b7\u8840\uff0c\u4fbf\u6ca1\u6c14\u4e86\u3002 \u3000\u3000\u674e\u6587\u79c0\u98a4\u58f0\u9053\uff1a\u201c\u7237\u7237\uff0c\u4f60\u2026\u2026\u4f60\u80cc\u4e0a\u7684\u5200\u5b50\u2026\u2026\u201d\u8ba1\u8001\u4eba\u89c1\u5979\u6cea\u5149\u83b9\u7136\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u5973\u5b69\u5b50\u5fc3\u5730\u5012\u597d\u3002\u201d\u674e\u6587\u79c0\u53c8\u9053\uff1a\u201c\u7237\u7237\uff0c\u4f60\u7684\u4f24\u2026\u2026\u6211\u7ed9\u4f60\u628a\u5200\u5b50\u62d4\u4e0b\u6765\u5427\uff1f\u201d\u8bf4\u7740\u4f38\u624b\u53bb\u63e1\u5200\u67c4\u3002\u8ba1\u8001\u4eba\u8138\u8272\u4e00\u6c89\uff0c\u6012\u9053\uff1a\u201c\u4f60\u522b\u7ba1\u6211\u3002\u201d\u6276\u7740\u684c\u5b50\uff0c\u8eab\u5b50\u6643\u4e86\u51e0\u6643\uff0c\u98a4\u5dcd\u5dcd\u8d70\u5411\u5185\u5ba4\uff0c\u556a\u7684\u4e00\u58f0\uff0c\u5173\u4e0a\u4e86\u677f\u95e8\u3002\u674e\u6587\u79c0\u89c1\u4ed6\u7a81\u7136\u5927\u6012\uff0c\u5f88\u662f\u5bb3\u6015\uff0c\u53c8\u89c1\u4e01\u540c\u5728\u5730\u4e0b\u8737\u7f29\u6210\u4e00\u56e2\uff0c\u53ea\u6015\u4ed6\u8d77\u6765\u52a0\u5bb3\u81ea\u5df1\uff0c\u8d8a\u60f3\u8d8a\u6015\uff0c\u53ea\u60f3\u98de\u5954\u51fa\u5916\uff0c\u4f46\u60f3\u8d77\u8ba1\u8001\u4eba\u8eab\u53d7\u91cd\u4f24\uff0c\u65e0\u4eba\u670d\u4f8d\uff0c\u53c8\u4e0d\u5fcd\u7f6e\u4e4b\u4e0d\u7406\u3002 \u3000\u3000\u5979\u60f3\u4e86\u4e00\u60f3\uff0c\u8d70\u5230\u5ba4\u95e8\u5916\uff0c\u8f7b\u8f7b\u62cd\u4e86\u51e0\u4e0b\uff0c\u542c\u5f97\u5ba4\u4e2d\u6ca1\u534a\u70b9\u58f0\u97f3\uff0c\u53eb\u9053\uff1a\u201c\u7237\u7237\uff0c\u7237\u7237\uff0c\u4f60\u75db\u5417\uff1f\u201d\u53ea\u542c\u5f97\u8ba1\u8001\u4eba\u7c97\u58f0\u9053\uff1a\u201c\u8d70\u5f00\uff0c\u8d70\u5f00\uff01\u522b\u6765\u5435\u6211\uff01\u201d\u8fd9\u58f0\u97f3\u548c\u4ed6\u539f\u6765\u6148\u548c\u7684\u8bf4\u8bdd\u5927\u4e0d\u76f8\u540c\uff0c\u674e\u6587\u79c0\u5413\u5f97\u4e0d\u6562\u518d\u8bf4\uff0c\u6014\u6014\u7684\u5750\u5728\u5730\u4e0b\uff0c\u62b1\u7740\u5934\u545c\u545c\u54bd\u54bd\u7684\u54ed\u8d77\u6765\u3002\u5ffd\u7136\u5440\u7684\u4e00\u58f0\uff0c\u5ba4\u95e8\u6253\u5f00\uff0c\u4e00\u53ea\u624b\u6e29\u67d4\u5730\u629a\u6478\u5979\u5934\u53d1\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u522b\u54ed\uff0c\u522b\u54ed\uff0c\u7237\u7237\u7684\u4f24\u4e0d\u788d\u4e8b\u3002\u201d \u3000\u3000\u674e\u6587\u79c0\u62ac\u8d77\u5934\u6765\uff0c\u89c1\u8ba1\u8001\u4eba\u8138\u5e26\u5fae\u7b11\uff0c\u5fc3\u4e2d\u4e00\u559c\uff0c\u767b\u65f6\u7834\u6d95\u4e3a\u7b11\u3002\u8ba1\u8001\u4eba\u7b11\u9053\uff1a\u201c\u53c8\u54ed\u53c8\u7b11\uff0c\u4e0d\u5bb3\u7f9e\u4e48\uff1f\u201d\u674e\u6587\u79c0\u628a\u5934\u85cf\u5728\u4ed6\u6000\u91cc\u3002\u4ece\u8fd9\u8001\u4eba\u8eab\u4e0a\uff0c\u5979\u53c8\u627e\u5230\u4e86\u4e00\u4e9b\u7236\u6bcd\u7684\u4eb2\u60c5\u6e29\u6696\u3002 \u3000\u3000\u8ba1\u8001\u4eba\u76b1\u8d77\u7709\u5934\uff0c\u6253\u91cf\u4e01\u540c\u7684\u5c38\u8eab\uff0c\u5fc3\u60f3\uff1a\u201c\u4ed6\u8ddf\u6211\u65e0\u51a4\u65e0\u4ec7\uff0c\u4e3a\u4ec0\u4e48\u5ffd\u4e0b\u6bd2\u624b\uff1f\u201d\u674e\u6587\u79c0\u5173\u5fc3\u5730\u95ee\uff1a\u201c\u7237\u7237\uff0c\u4f60\u80cc\u4e0a\u7684\u4f24\u597d\u4e9b\u4e86\u4e48\uff1f\u201d\u8fd9\u65f6\u8ba1\u8001\u4eba\u5df2\u6362\u8fc7\u4e86\u4e00\u4ef6\u957f\u888d\uff0c\u4e5f\u4e0d\u77e5\u4ed6\u4f24\u5f97\u5982\u4f55\u3002 \u3000\u3000\u54ea\u77e5\u4ed6\u542c\u5230\u674e\u6587\u79c0\u91cd\u63d0\u6b64\u4e8b\uff0c\u4f3c\u4e4e\u9002\u624d\u7ed9\u523a\u4e86\u8fd9\u4e00\u5200\u5b9e\u662f\u5947\u803b\u5927\u8fb1\uff0c\u8138\u4e0a\u53c8\u73b0\u607c\u6012\uff0c\u7c97\u58f0\u9053\uff1a\u201c\u4f60\u7f57\u5506\u4ec0\u4e48\uff1f\u201d\u53ea\u542c\u5f97\u5c4b\u5916\u90a3\u767d\u9a6c\u5618\u6e9c\u6e9c\u4e00\u58f0\u957f\u5636\uff0c\u5fae\u4e00\u6c89\u541f\uff0c\u5230\u67f4\u623f\u4e2d\u63d0\u4e86\u4e00\u6876\u9ec4\u8272\u67d3\u6599\u51fa\u6765\u3002\u90a3\u662f\u7267\u7f8a\u4eba\u5728\u7272\u53e3\u8eab\u4e0a\u6d82\u67d3\u8bb0\u53f7\u6240\u7528\uff0c\u4f7f\u5f97\u5404\u5bb6\u7684\u725b\u7f8a\u4e0d\u81f4\u6df7\u6742\uff0c\u867d\u7ecf\u98ce\u971c\uff0c\u4ea6\u4e0d\u8131\u843d\u3002\u4ed6\u7275\u8fc7\u767d\u9a6c\uff0c\u7528\u5237\u5b50\u81ea\u5934\u81f3\u5c3e\u90fd\u5237\u4e0a\u4e86\u9ec4\u8272\uff0c\u53c8\u5230\u54c8\u8428\u514b\u4eba\u7684\u5e10\u7bf7\u4e4b\u4e2d\uff0c\u8ba8\u4e86\u4e00\u5957\u54c8\u8428\u514b\u7537\u5b69\u7684\u65e7\u8863\u670d\u6765\uff0c\u53eb\u674e\u6587\u79c0\u6362\u4e0a\u4e86\u3002\u674e\u6587\u79c0\u5f88\u662f\u806a\u660e\uff0c\u8bf4\u9053\uff1a\u201c\u7237\u7237\uff0c\u4f60\u8981\u90a3\u4e9b\u6076\u4eba\u8ba4\u4e0d\u51fa\u6211\u6765\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u8ba1\u8001\u4eba\u70b9\u4e86\u70b9\u5934\uff0c\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u7237\u7237\u8001\u4e86\u3002\u5509\uff0c\u521a\u624d\u7adf\u7ed9\u4ed6\u523a\u4e86\u4e00\u5200\u3002\u201d\u8fd9\u4e00\u6b21\u4ed6\u81ea\u5df1\u63d0\u8d77\uff0c\u674e\u6587\u79c0\u5374\u4e0d\u6562\u63a5\u53e3\u4e86\u3002 \u3000\u3000\u8ba1\u8001\u4eba\u57cb\u4e86\u4e01\u540c\u7684\u5c38\u4f53\uff0c\u53c8\u5c06\u4ed6\u4e58\u6765\u7684\u5750\u9a91\u4e5f\u5bb0\u4e86\uff0c\u6ca1\u7559\u4e0b\u4e1d\u6beb\u75d5\u8ff9\uff0c\u7136\u540e\u5750\u5728\u5927\u95e8\u53e3\uff0c\u62ff\u7740\u4e00\u67c4\u957f\u5200\u5728\u78e8\u5200\u77f3\u4e0a\u4e0d\u4f4f\u624b\u7684\u78e8\u7740\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u756a\u529f\u592b\u679c\u7136\u6ca1\u767d\u505a\uff0c\u5c31\u5728\u5f53\u5929\u665a\u4e0a\uff0c\u970d\u5143\u9f99\u548c\u9648\u8fbe\u6d77\u6240\u7387\u9886\u7684\u8c6a\u5ba2\uff0c\u51b2\u8fdb\u4e86\u8fd9\u7247\u7eff\u6d32\u4e4b\u4e2d\uff0c\u5927\u8086\u63b3\u63a0\u3002\u8fd9\u4e00\u5e26\u7d20\u6765\u6ca1\u6709\u76d7\u532a\uff0c\u54c8\u8428\u514b\u4eba\u867d\u7136\u52c7\u6b66\u5584\u6218\uff0c\u4f46\u4e8b\u5148\u7edd\u65e0\u9632\u5907\uff0c\u65cf\u4e2d\u7cbe\u58ee\u7537\u5b50\u53c8\u521a\u597d\u5927\u4e3e\u5728\u5317\u8fb9\u730e\u6740\u4e3a\u5bb3\u725b\u7f8a\u7684\u72fc\u7fa4\uff0c\u5728\u5e10\u7bf7\u4e2d\u7559\u5b88\u7684\u90fd\u662f\u8001\u5f31\u5987\u5b7a\uff0c\u7adf\u7ed9\u8fd9\u6279\u6765\u81ea\u4e2d\u539f\u7684\u8c6a\u5ba2\u653b\u4e86\u4e2a\u63aa\u624b\u4e0d\u53ca\u3002\u4e03\u540d\u54c8\u8428\u514b\u7537\u5b50\u88ab\u6740\uff0c\u4e94\u4e2a\u5987\u5973\u88ab\u63b3\u4e86\u53bb\u3002\u8fd9\u7fa4\u8c6a\u5ba2\u4e5f\u66fe\u95ef\u8fdb\u8ba1\u8001\u4eba\u7684\u5c4b\u91cc\uff0c\u4f46\u8c01\u4e5f\u6ca1\u5bf9\u4e00\u4e2a\u8001\u4eba\u3001\u4e00\u4e2a\u54c8\u8428\u514b\u5b69\u5b50\u8d77\u7591\u3002\u674e\u6587\u79c0\u6ee1\u8138\u6ce5\u6c61\uff0c\u8eb2\u5728\u5c4b\u89d2\u843d\u4e2d\uff0c\u8c01\u4e5f\u6ca1\u7559\u610f\u5230\u5979\u773c\u4e2d\u95ea\u8000\u7740\u7684\u4ec7\u6068\u5149\u8292\u3002\u5979\u5374\u770b\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u7236\u4eb2\u7684\u4f69\u5251\u60ac\u5728\u970d\u5143\u9f99\u7684\u8170\u95f4\uff0c\u6bcd\u4eb2\u7684\u91d1\u94f6\u5c0f\u5251\u63d2\u5728\u9648\u8fbe\u6d77\u7684\u8170\u5e26\u4e4b\u4e2d\u3002\u8fd9\u662f\u5979\u7236\u6bcd\u51b3\u4e0d\u79bb\u8eab\u7684\u5175\u5203\uff0c\u5979\u5e74\u7eaa\u867d\u5c0f\uff0c\u5374\u4e5f\u731c\u5230\u7236\u6bcd\u5b9a\u662f\u906d\u5230\u4e86\u4e0d\u5e78\u3002 \u3000\u3000\u7b2c\u56db\u5929\u4e0a\uff0c\u54c8\u8428\u514b\u7684\u7537\u5b50\u4eec\u4ece\u5317\u65b9\u62d6\u4e86\u4e00\u6279\u72fc\u5c38\u56de\u6765\u4e86\uff0c\u5f53\u5373\u7ec4\u7ec7\u4e86\u961f\u4f0d\uff0c\u53bb\u627e\u8fd9\u6279\u6c49\u4eba\u5f3a\u76d7\u62a5\u4ec7\u3002\u4f46\u5728\u832b\u832b\u7684\u5927\u6f20\u4e4b\u4e2d\uff0c\u5374\u5df2\u5931\u5374\u4e86\u4ed6\u4eec\u7684\u8e2a\u8ff9\uff0c\u53ea\u627e\u5230\u4e86\u90a3\u4e94\u4e2a\u88ab\u63b3\u53bb\u7684\u5987\u5973\u3002\u90a3\u662f\u4e94\u5177\u5c38\u8eab\uff0c\u5168\u8eab\u8863\u670d\u88ab\u8131\u5149\u4e86\uff0c\u60e8\u6b7b\u5728\u5927\u6f20\u4e4b\u4e0a\u3002\u4ed6\u4eec\u4e5f\u627e\u5230\u4e86\u767d\u9a6c\u674e\u4e09\u548c\u91d1\u94f6\u5c0f\u5251\u4e09\u5a18\u5b50\u7684\u5c38\u8eab\uff0c\u4e00\u8d77\u90fd\u5e26\u4e86\u56de\u6765\u3002 \u3000\u3000\u674e\u6587\u79c0\u6251\u5728\u7236\u6bcd\u7684\u5c38\u8eab\u4e0a\u54c0\u54c0\u75db\u54ed\u3002\u4e00\u4e2a\u54c8\u8428\u514b\u4eba\u63d0\u8d77\u76ae\u9774\uff0c\u91cd\u91cd\u8e22\u4e86\u5979\u4e00\u811a\uff0c\u7c97\u58f0\u9a82\u9053\uff1a\u201c\u771f\u4e3b\u964d\u7f5a\u7684\u5f3a\u76d7\u6c49\u4eba\uff01\u201d \u3000\u3000\u8ba1\u8001\u4eba\u62b1\u4e86\u674e\u6587\u79c0\u56de\u5bb6\uff0c\u4e0d\u53bb\u8ddf\u8fd9\u4e2a\u54c8\u8428\u514b\u4eba\u4e89\u95f9\u3002\u674e\u6587\u79c0\u5c0f\u5c0f\u7684\u5fc3\u7075\u4e4b\u4e2d\uff0c\u53ea\u662f\u60f3\uff1a\u201c\u4e3a\u4ec0\u4e48\u6076\u4eba\u8fd9\u4e48\u591a\uff1f\u8c01\u90fd\u6765\u6b3a\u4fae\u6211\uff1f\u201d \u3000\u3000\u534a\u591c\u91cc\uff0c\u674e\u6587\u79c0\u53c8\u4ece\u7761\u68a6\u4e2d\u54ed\u9192\u4e86\uff0c\u4e00\u7741\u5f00\u773c\uff0c\u53ea\u89c1\u5e8a\u6cbf\u4e0a\u5750\u7740\u4e00\u4e2a\u4eba\u3002\u5979\u60ca\u547c\u4e00\u58f0\uff0c\u5750\u4e86\u8d77\u6765\uff0c\u5374\u89c1\u8ba1\u8001\u4eba\u51dd\u671b\u7740\u5979\uff0c\u76ee\u5149\u4e2d\u7231\u601c\u6a2a\u6ea2\uff0c\u4f38\u624b\u6e29\u67d4\u5730\u629a\u6478\u5979\u7684\u5934\u53d1\uff0c\u8bf4\u9053\uff1a\u201c\u522b\u6015\uff0c\u522b\u6015\uff0c\u662f\u7237\u7237\u3002\u201d\u674e\u6587\u79c0\u6cea\u6c34\u5982\u73cd\u73e0\u65ad\u7ebf\u822c\u6d41\u4e86\u4e0b\u6765\uff0c\u4f0f\u5728\u8ba1\u8001\u4eba\u7684\u6000\u91cc\uff0c\u628a\u4ed6\u7684\u8863\u895f\u5168\u54ed\u6e7f\u4e86\u3002\u8ba1\u8001\u4eba\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u6ca1\u4e86\u7239\u5a18\uff0c\u5c31\u5f53\u6211\u662f\u4f60\u7684\u4eb2\u7237\u7237\uff0c\u8ddf\u6211\u4f4f\u5728\u4e00\u8d77\u3002\u7237\u7237\u4f1a\u597d\u597d\u7684\u7167\u6599\u4f60\u3002\u201d \u3000\u3000\u674e\u6587\u79c0\u54ed\u7740\u70b9\u5934\uff0c\u60f3\u8d77\u4e86\u90a3\u4e9b\u6740\u5bb3\u7238\u7238\u5988\u5988\u7684\u6076\u4eba\uff0c\u53c8\u60f3\u8d77\u4e86\u8e22\u4e86\u5979\u4e00\u811a\u7684\u90a3\u4e2a\u51f6\u6076\u7684\u54c8\u8428\u514b\u6c49\u5b50\u3002\u8fd9\u4e00\u811a\u8e22\u5f97\u597d\u91cd\uff0c\u4f7f\u5979\u8170\u91cc\u80bf\u8d77\u4e86\u4e00\u5927\u5757\uff0c\u5979\u4e0d\u7981\u53c8\u95ee\uff1a\u201c\u4e3a\u4ec0\u4e48\u8c01\u90fd\u6765\u6b3a\u4fae\u6211\uff1f\u6211\u53c8\u6ca1\u505a\u574f\u4e8b\uff1f\u201d \u3000\u3000\u8ba1\u8001\u4eba\u53f9\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e16\u754c\u4e0a\u7ed9\u4eba\u6b3a\u4fae\u7684\uff0c\u603b\u662f\u90a3\u4e9b\u6ca1\u505a\u574f\u4e8b\u7684\u4eba\u3002\u201d\u4ed6\u4ece\u74e6\u58f6\u91cc\u5012\u4e86\u4e00\u7897\u70ed\u5976\u916a\uff0c\u77a7\u7740\u5979\u559d\u4e0b\u4e86\uff0c\u53c8\u66ff\u5979\u62e2\u597d\u88ab\u7a9d\uff0c\u8bf4\u9053\uff1a\u201c\u79c0\u513f\uff0c\u90a3\u4e2a\u8e22\u4e86\u4f60\u4e00\u811a\u7684\u4eba\uff0c\u53eb\u505a\u82cf\u9c81\u514b\u3002\u4ed6\u662f\u4e2a\u6b63\u76f4\u7684\u597d\u4eba\u3002\u201d\u674e\u6587\u79c0\u7741\u7740\u5706\u5706\u7684\u773c\u73e0\uff0c\u5f88\u662f\u5947\u602a\uff0c\u9053\uff1a\u201c\u4ed6\u2026\u2026\u4ed6\u662f\u597d\u4eba\u4e48\uff1f\u201d\u8ba1\u8001\u4eba\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u662f\u597d\u4eba\u3002\u4ed6\u8ddf\u4f60\u4e00\u6837\uff0c\u5728\u4e00\u5929\u4e4b\u4e2d\u6b7b\u4e86\u4e24\u4e2a\u6700\u4eb2\u7231\u7684\u4eba\uff0c\u4e00\u4e2a\u662f\u4ed6\u59bb\u5b50\uff0c\u4e00\u4e2a\u662f\u4ed6\u7684\u5927\u513f\u5b50\u3002\u90fd\u662f\u7ed9\u90a3\u6279\u6076\u4eba\u5f3a\u76d7\u5bb3\u6b7b\u7684\u3002\u4ed6\u53ea\u9053\u6c49\u4eba\u90fd\u662f\u574f\u4eba\u3002\u4ed6\u7528\u54c8\u8428\u514b\u8bdd\u9a82\u4f60\uff0c\u8bf4\u4f60\u662f\u2018\u771f\u4e3b\u964d\u7f5a\u7684\u5f3a\u76d7\u6c49\u4eba\u2019\u3002\u4f60\u522b\u6068\u4ed6\uff0c\u4ed6\u5fc3\u91cc\u7684\u60b2\u75db\uff0c\u5b9e\u5728\u8ddf\u4f60\u4e00\u6a21\u4e00\u6837\u3002\u4e0d\uff0c\u4ed6\u5e74\u7eaa\u5927\u4e86\uff0c\u5fc3\u91cc\u611f\u5230\u7684\u60b2\u75db\uff0c\u53ef\u6bd4\u4f60\u591a\u5f97\u591a\uff0c\u6df1\u5f97\u591a\u3002\u201d \u3000\u3000\u674e\u6587\u79c0\u6014\u6014\u7684\u542c\u7740\uff0c\u5979\u672c\u6765\u4e5f\u6ca1\u600e\u4e48\u6068\u8fd9\u4e2a\u6ee1\u8138\u80e1\u5b50\u7684\u54c8\u8428\u514b\u4eba\uff0c\u53ea\u662f\u89c1\u4e86\u4ed6\u51f6\u72e0\u7684\u6a21\u6837\u5f88\u662f\u5bb3\u6015\uff0c\u8fd9\u65f6\u5ffd\u7136\u60f3\u8d77\uff0c\u90a3\u4e2a\u5927\u80e1\u5b50\u7684\u53cc\u773c\u4e4b\u4e2d\u6ee1\u542b\u7740\u773c\u6cea\uff0c\u53ea\u5dee\u6ca1\u6389\u4e0b\u6765\u3002\u5979\u4e0d\u61c2\u8ba1\u8001\u4eba\u8bf4\u7684\uff0c\u4e3a\u4ec0\u4e48\u5927\u4eba\u7684\u60b2\u75db\u4f1a\u6bd4\u5c0f\u5b69\u5b50\u66f4\u6df1\u66f4\u591a\uff0c\u4f46\u5bf9\u8fd9\u4e2a\u5927\u80e1\u5b50\u5374\u4e0d\u81ea\u7981\u7684\u8d77\u4e86\u540c\u60c5\u3002 \u3000\u3000\u7a97\u5916\u4f20\u8fdb\u6765\u4e00\u9635\u5947\u5999\u7684\u5b9b\u8f6c\u7684\u9e1f\u9e23\uff0c\u58f0\u97f3\u5f88\u8fdc\uff0c\u4f46\u542c\u5f97\u5f88\u6e05\u695a\uff0c\u53c8\u662f\u751c\u7f8e\uff0c\u53c8\u662f\u51c4\u51c9\uff0c\u50cf\u662f\u4e00\u4e2a\u5c11\u5973\u5728\u5531\u7740\u6e05\u8106\u800c\u67d4\u548c\u7684\u6b4c\u3002 \u3000\u3000\u674e\u6587\u79c0\u4fa7\u8033\u542c\u7740\uff0c\u9e23\u6b4c\u4e4b\u58f0\u6e10\u6e10\u8fdc\u53bb\uff0c\u7ec8\u4e8e\u4f4e\u5fae\u5f97\u542c\u4e0d\u89c1\u4e86\u3002\u5979\u60b2\u75db\u7684\u5fc3\u7075\u4e2d\u5f97\u5230\u4e86\u4e00\u4e9b\u5b89\u6170\uff0c\u5446\u5446\u7684\u51fa\u4e86\u4e00\u4f1a\u795e\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u7237\u7237\uff0c\u8fd9\u9e1f\u513f\u5531\u5f97\u771f\u597d\u542c\u3002\u201d \u3000\u3000\u8ba1\u8001\u4eba\u9053\uff1a\u201c\u662f\u7684\uff0c\u5531\u5f97\u771f\u597d\u542c\uff01\u90a3\u662f\u5929\u94c3\u9e1f\uff0c\u9e1f\u513f\u7684\u6b4c\u58f0\u50cf\u662f\u5929\u4e0a\u7684\u94f6\u94c3\u3002\u8fd9\u9e1f\u513f\u53ea\u5728\u665a\u4e0a\u5531\u6b4c\uff0c\u767d\u5929\u7761\u89c9\u3002\u6709\u4eba\u8bf4\uff0c\u8fd9\u662f\u5929\u4e0a\u7684\u661f\u661f\u6389\u4e0b\u6765\u4e4b\u540e\u53d8\u7684\u3002\u53c8\u6709\u4e9b\u54c8\u8428\u514b\u4eba\u8bf4\uff0c\u8fd9\u662f\u8349\u539f\u4e0a\u4e00\u4e2a\u6700\u7f8e\u4e3d\u3001\u6700\u4f1a\u5531\u6b4c\u7684\u5c11\u5973\u6b7b\u4e86\u4e4b\u540e\u53d8\u7684\u3002\u5979\u7684\u60c5\u90ce\u4e0d\u7231\u5979\u4e86\uff0c\u5979\u4f24\u5fc3\u6b7b\u7684\u3002\u201d\u674e\u6587\u79c0\u8ff7\u60d8\u5730\u9053\uff1a\u201c\u5979\u6700\u7f8e\u4e3d\uff0c\u53c8\u6700\u4f1a\u5531\u6b4c\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u7231\u5979\u4e86\uff1f\u201d \u3000\u3000\u8ba1\u8001\u4eba\u51fa\u4e86\u4e00\u4f1a\u795e\uff0c\u957f\u957f\u7684\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4e16\u754c\u4e0a\u6709\u8bb8\u591a\u4e8b\uff0c\u4f60\u5c0f\u5b69\u5b50\u662f\u4e0d\u61c2\u7684\u3002\u201d\u8fd9\u65f6\u5019\uff0c\u8fdc\u5904\u8349\u539f\u4e0a\u7684\u5929\u94c3\u9e1f\u53c8\u5531\u8d77\u6b4c\u6765\u4e86\u3002 \u3000\u3000\u5531\u5f97\u4ee4\u4eba\u5fc3\u4e2d\u53c8\u662f\u751c\u871c\uff0c\u53c8\u662f\u51c4\u51c9\u3002"}),showOnHomepage:!0},{name:"\u4e8c",content:(0,a.jsx)(a.Fragment,{children:"\u5c31\u8fd9\u6837\uff0c\u674e\u6587\u79c0\u4f4f\u5728\u8ba1\u8001\u4eba\u7684\u5bb6\u91cc\uff0c\u5e2e\u4ed6\u7267\u7f8a\u716e\u996d\uff0c\u4e24\u4e2a\u4eba\u5c31\u50cf\u4eb2\u7237\u7237\u3001\u4eb2\u5b59\u5973\u4e00\u822c\u3002\u665a\u4e0a\uff0c\u674e\u6587\u79c0\u6709\u65f6\u5019\u4ece\u68a6\u4e2d\u9192\u6765\uff0c\u542c\u7740\u5929\u94c3\u9e1f\u7684\u6b4c\u5531\uff0c\u53c8\u5728\u5929\u94c3\u9e1f\u7684\u6b4c\u58f0\u4e2d\u56de\u5230\u68a6\u91cc\u3002\u5979\u68a6\u4e2d\u6709\u6c5f\u5357\u7684\u6768\u67f3\u548c\u6843\u82b1\uff0c\u7238\u7238\u7684\u6000\u62b1\uff0c\u5988\u5988\u7684\u7b11\u8138\u2026\u2026 \u3000\u3000\u8fc7\u4e86\u79cb\u5929\uff0c\u8fc7\u4e86\u51ac\u5929\uff0c\u674e\u6587\u79c0\u5e73\u5e73\u9759\u9759\u5730\u8fc7\u7740\u65e5\u5b50\uff0c\u5979\u5b66\u4f1a\u4e86\u54c8\u8428\u514b\u8bdd\uff0c\u5b66\u4f1a\u4e86\u8349\u539f\u4e0a\u7684\u8bb8\u8bb8\u591a\u591a\u4e8b\u60c5\u3002 \u3000\u3000\u8ba1\u8001\u4eba\u4f1a\u917f\u53c8\u9999\u53c8\u70c8\u7684\u7f8e\u9152\uff0c\u54c8\u8428\u514b\u7684\u7537\u4eba\u5c31\u6700\u7231\u559d\u53c8\u9999\u53c8\u70c8\u7684\u7f8e\u9152\u3002\u8ba1\u8001\u4eba\u4f1a\u533b\u725b\u7f8a\u9a6c\u5339\u7684\u75be\u75c5\uff0c\u54c8\u8428\u514b\u4eba\u6cbb\u4e0d\u597d\u7684\u7272\u53e3\uff0c\u5f80\u5f80\u5c31\u7ed9\u4ed6\u6cbb\u597d\u4e86\u3002\u725b\u7f8a\u9a6c\u5339\u662f\u54c8\u8428\u514b\u4eba\u7684\u6027\u547d\uff0c\u4ed6\u4eec\u867d\u7136\u4e0d\u559c\u6b22\u6c49\u4eba\uff0c\u5374\u4e5f\u5c11\u4ed6\u4e0d\u5f97\uff0c\u53ea\u597d\u7528\u725b\u7f8a\u6765\u6362\u4ed6\u53c8\u9999\u53c8\u70c8\u7684\u7f8e\u9152\uff0c\u8bf7\u4e86\u4ed6\u53bb\u7ed9\u7272\u53e3\u6cbb\u75c5\u3002 \u3000\u3000\u54c8\u8428\u514b\u4eba\u7684\u5e10\u7bf7\u5728\u8349\u539f\u4e0a\u4e1c\u897f\u5357\u5317\u7684\u8fc1\u79fb\u3002\u8ba1\u8001\u4eba\u6709\u65f6\u8ddf\u7740\u4ed6\u4eec\u8fc1\u79fb\uff0c\u6709\u65f6\u5c31\u7559\u5728\u68da\u5c4b\u4e4b\u4e2d\uff0c\u7b49\u7740\u4ed6\u4eec\u56de\u6765\u3002 \u3000\u3000\u4e00\u5929\u665a\u4e0a\uff0c\u674e\u6587\u79c0\u53c8\u542c\u5230\u4e86\u5929\u94c3\u9e1f\u7684\u6b4c\u58f0\uff0c\u53ea\u662f\u5b83\u8d8a\u5531\u8d8a\u8fdc\uff0c\u9690\u9690\u7ea6\u7ea6\u5730\uff0c\u968f\u7740\u98ce\u58f0\u98d8\u6765\u4e86\u4e00\u4e9b\uff0c\u8ddf\u7740\u53c8\u542c\u4e0d\u5230\u4e86\u3002 \u3000\u3000\u674e\u6587\u79c0\u6084\u6084\u7a7f\u8863\u8d77\u6765\uff0c\u5230\u5c4b\u5916\u7275\u4e86\u767d\u9a6c\uff0c\u751f\u6015\u60ca\u9192\u8ba1\u8001\u4eba\uff0c\u5c06\u767d\u9a6c\u7275\u5f97\u8fdc\u8fdc\u5730\uff0c\u8fd9\u624d\u8de8\u4e0a\u9a6c\uff0c\u8ddf\u7740\u6b4c\u58f0\u8d70\u53bb\u3002 \u3000\u3000\u8349\u539f\u4e0a\u7684\u591c\u665a\uff0c\u5929\u5f88\u9ad8\u3001\u5f88\u84dd\uff0c\u661f\u661f\u5f88\u4eae\uff0c\u9752\u8349\u548c\u5c0f\u82b1\u6563\u64ad\u7740\u82b3\u9999\u3002 \u3000\u3000\u6b4c\u58f0\u5f88\u6e05\u6670\u4e86\uff0c\u5531\u5f97\u53c8\u662f\u5a49\u8f6c\uff0c\u53c8\u662f\u5a07\u5a9a\u3002\u674e\u6587\u79c0\u7684\u5fc3\u8ddf\u7740\u6b4c\u58f0\u800c\u72c2\u559c\uff0c\u8f7b\u8f7b\u8de8\u4e0b\u9a6c\u80cc\uff0c\u8ba9\u767d\u9a6c\u81ea\u7531\u81ea\u5728\u7684\u56bc\u7740\u9752\u8349\u3002\u5979\u4ef0\u5929\u8eba\u5728\u8349\u5730\u4e0a\uff0c\u6c89\u9189\u5728\u6b4c\u58f0\u4e4b\u4e2d\u3002 \u3000\u3000\u90a3\u5929\u94c3\u9e1f\u5531\u4e86\u4e00\u4f1a\uff0c\u4fbf\u98de\u8fdc\u51e0\u4e08\u3002\u674e\u6587\u79c0\u5728\u5730\u4e0b\u722c\u7740\u8ddf\u968f\uff0c\u5979\u542c\u5230\u4e86\u9e1f\u513f\u6251\u7fc5\u7684\u58f0\u97f3\uff0c\u770b\u5230\u4e86\u8fd9\u53ea\u6de1\u9ec4\u8272\u7684\u5c0f\u5c0f\u9e1f\u513f\uff0c\u89c1\u5b83\u5728\u5730\u4e0b\u5544\u98df\u3002\u5b83\u5544\u4e86\u51e0\u53e3\uff0c\u53c8\u5411\u524d\u98de\u4e00\u6bb5\u8def\uff0c\u53c8\u627e\u5230\u4e86\u98df\u7269\u3002 \u3000\u3000\u5929\u94c3\u9e1f\u5403\u5f97\u5f88\u9ad8\u5174\uff0c\u7a81\u7136\u95f4\u556a\u7684\u4e00\u58f0\uff0c\u957f\u8349\u4e2d\u98de\u8d77\u9ed1\u9edd\u9edd\u7684\u4e00\u4ef6\u7269\u4ef6\uff0c\u5c06\u5929\u94c3\u9e1f\u7f69\u4f4f\u4e86\u3002 \u3000\u3000\u674e\u6587\u79c0\u7684\u60ca\u547c\u58f0\u4e2d\uff0c\u6df7\u548c\u7740\u4e00\u4e2a\u7537\u5b69\u7684\u6b22\u53eb\uff0c\u53ea\u89c1\u957f\u8349\u4e2d\u8df3\u51fa\u6765\u4e00\u4e2a\u54c8\u8428\u514b\u7537\u5b69\uff0c\u5f97\u610f\u5730\u53eb\u9053\uff1a\u201c\u6349\u4f4f\u4e86\uff0c\u6349\u4f4f\u4e86\uff01\u201d \u3000\u3000\u4ed6\u7528\u5916\u8863\u88f9\u7740\u5929\u94c3\u9e1f\uff0c\u9e1f\u513f\u60ca\u614c\u7684\u53eb\u58f0\uff0c\u90c1\u95f7\u5730\u9694\u7740\u5916\u8863\u4f20\u51fa\u6765\u3002 \u3000\u3000\u674e\u6587\u79c0\u53c8\u662f\u5403\u60ca\uff0c\u53c8\u662f\u6124\u6012\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u90a3\u7537\u5b69\u9053\uff1a\u201c\u6211\u6349\u5929\u94c3\u9e1f\u3002\u4f60\u4e5f\u6765\u6349\u4e48\uff1f\u201d\u674e\u6587\u79c0\u9053\uff1a\u201c\u5e72\u4e48\u6349\u5b83\uff1f \u3000\u3000\u8ba9\u5b83\u5feb\u5feb\u6d3b\u6d3b\u7684\u5531\u6b4c\u4e0d\u597d\u4e48\uff1f\u201d\u90a3\u7537\u5b69\u7b11\u9053\uff1a\u201c\u6349\u6765\u73a9\u3002\u201d\u5c06\u53f3\u624b\u4f38\u5230\u5916\u8863\u4e4b\u4e2d\uff0c\u518d\u4f38\u51fa\u6765\u65f6\uff0c\u624b\u91cc\u5df2\u6293\u7740\u90a3\u53ea\u6de1\u9ec4\u8272\u7684\u5c0f\u9e1f\u3002\u5929\u94c3\u9e1f\u4e0d\u4f4f\u6251\u7740\u7fc5\u8180\uff0c\u4f46\u54ea\u91cc\u98de\u5f97\u51fa\u7537\u5b69\u7684\u638c\u63e1\uff1f \u3000\u3000\u674e\u6587\u79c0\u9053\uff1a\u201c\u653e\u4e86\u5b83\u5427\uff0c\u4f60\u77a7\u5b83\u591a\u53ef\u601c\uff1f\u201d\u90a3\u7537\u5b69\u9053\uff1a\u201c\u6211\u4e00\u8def\u6492\u4e86\u9ea6\u5b50\uff0c\u5f15\u5f97\u8fd9\u9e1f\u513f\u8fc7\u6765\u3002\u8c01\u53eb\u5b83\u5403\u6211\u7684\u9ea6\u5b50\u554a\uff1f\u54c8\u54c8\uff01\u201d \u3000\u3000\u674e\u6587\u79c0\u4e00\u5446\uff0c\u5728\u8fd9\u4e16\u754c\u4e0a\uff0c\u5979\u7b2c\u4e00\u6b21\u61c2\u5f97\u201c\u9677\u9631\u201d\u7684\u610f\u4e49\u3002\u4eba\u5bb6\u77e5\u9053\u5c0f\u9e1f\u513f\u8981\u5403\u9ea6\u5b50\uff0c\u4fbf\u6492\u4e86\u9ea6\u5b50\uff0c\u5f15\u7740\u5b83\u8d70\u8fdb\u4e86\u6b7b\u8def\u3002\u5979\u5e74\u7eaa\u8fd8\u5c0f\uff0c\u4e0d\u77e5\u9053\u51e0\u5343\u5e74\u6765\uff0c\u4eba\u4eec\u65e9\u4fbf\u5728\u8bf4\u7740\u201c\u4eba\u4e3a\u8d22\u6b7b\uff0c\u9e1f\u4e3a\u98df\u4ea1\u201d\u8fd9\u4e24\u53e5\u8bdd\u3002\u5979\u53ea\u9690\u9690\u7684\u611f\u5230\u4e86\u673a\u8c0b\u7684\u53ef\u6015\uff0c\u89c9\u5230\u4e86\u201c\u5f15\u8bf1\u201d\u7684\u4ee4\u4eba\u96be\u4ee5\u6297\u62d2\u3002\u5f53\u7136\uff0c\u5979\u53ea\u611f\u5230\u4e86\u4e00\u4e9b\u6781\u6a21\u7cca\u7684\u5f71\u5b50\uff0c\u60f3\u4e0d\u660e\u767d\u4e2d\u95f4\u5305\u85cf\u7740\u7684\u9053\u7406\u3002 \u3000\u3000\u90a3\u7537\u5b69\u73a9\u5f04\u7740\u5929\u94c3\u9e1f\uff0c\u4f7f\u5b83\u53d1\u51fa\u4e00\u4e9b\u75db\u82e6\u7684\u58f0\u97f3\u3002\u674e\u6587\u79c0\u9053\uff1a\u201c\u4f60\u628a\u5c0f\u9e1f\u513f\u7ed9\u4e86\u6211\uff0c\u597d\u4e0d\u597d\uff1f\u201d\u90a3\u7537\u5b69\u9053\uff1a\u201c\u90a3\u4f60\u7ed9\u6211\u4ec0\u4e48\uff1f\u201d\u674e\u6587\u79c0\u4f38\u624b\u5230\u6000\u91cc\u4e00\u6478\uff0c\u5979\u4ec0\u4e48\u4e5f\u6ca1\u6709\uff0c\u4e0d\u7981\u6709\u4e9b\u53d1\u7a98\uff0c\u60f3\u4e86\u4e00\u60f3\uff0c\u9053\uff1a\u201c\u8d76\u660e\u513f\u6211\u7ed9\u4f60\u7f1d\u4e00\u53ea\u597d\u770b\u7684\u8377\u5305\uff0c\u7ed9\u4f60\u6302\u5728\u8eab\u4e0a\u3002\u201d\u90a3\u7537\u5b69\u7b11\u9053\uff1a\u201c\u6211\u624d\u4e0d\u4e0a\u8fd9\u4e2a\u5f53\u5462\u3002\u660e\u513f\u4f60\u4fbf\u8d56\u4e86\u3002\u201d\u674e\u6587\u79c0\u80c0\u7ea2\u4e86\u8138\uff0c\u9053\uff1a\u201c\u6211\u8bf4\u8fc7\u7ed9\u4f60\uff0c\u4e00\u5b9a\u7ed9\u4f60\uff0c\u4e3a\u4ec0\u4e48\u8981\u8d56\u5462\uff1f\u201d\u90a3\u7537\u5b69\u6447\u5934\u9053\uff1a\u201c\u6211\u4e0d\u4fe1\u3002\u201d\u6708\u5149\u4e4b\u4e0b\uff0c\u89c1\u674e\u6587\u79c0\u5de6\u8155\u4e0a\u5957\u7740\u4e00\u53ea\u7389\u956f\uff0c\u53d1\u51fa\u6676\u83b9\u67d4\u548c\u7684\u5149\u8292\uff0c\u968f\u53e3\u4fbf\u9053\uff1a\u201c\u9664\u975e\u4f60\u628a\u8fd9\u4e2a\u7ed9\u6211\u3002\u201d \u3000\u3000\u7389\u956f\u662f\u5988\u5988\u7ed9\u7684\uff0c\u9664\u4e86\u8fd9\u53ea\u7389\u956f\uff0c\u5df2\u6ca1\u6709\u7eaa\u5ff5\u5988\u5988\u7684\u4e1c\u897f\u4e86\u3002\u5979\u5f88\u820d\u4e0d\u5f97\uff0c\u4f46\u770b\u4e86\u90a3\u5929\u94c3\u9e1f\u53ef\u601c\u7684\u6837\u5b50\uff0c\u7ec8\u4e8e\u628a\u7389\u956f\u892a\u4e86\u4e0b\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u7ed9\u4f60\uff01\u201d \u3000\u3000\u90a3\u7537\u5b69\u6ca1\u60f3\u5230\u5979\u5c45\u7136\u4f1a\u80af\uff0c\u63a5\u8fc7\u7389\u956f\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u4f1a\u518d\u8981\u56de\u5427\uff1f\u201d\u674e\u6587\u79c0\u9053\uff1a\u201c\u4e0d\uff01\u201d\u90a3\u7537\u5b69\u9053\uff1a\u201c\u597d\uff01\u201d\u4e8e\u662f\u5c06\u5929\u94c3\u9e1f\u9012\u4e86\u7ed9\u5979\u3002\u674e\u6587\u79c0\u53cc\u624b\u5408\u7740\u9e1f\u513f\uff0c\u624b\u638c\u4e2d\u611f\u89c9\u5230\u5b83\u67d4\u8f6f\u7684\u8eab\u4f53\uff0c\u611f\u89c9\u5230\u5b83\u8fc5\u901f\u800c\u5fae\u5f31\u7684\u5fc3\u8df3\u3002\u5979\u7528\u53f3\u624b\u7684\u4e09\u6839\u624b\u6307\u8f7b\u8f7b\u629a\u6478\u4e00\u4e0b\u9e1f\u513f\u80cc\u4e0a\u7684\u7fbd\u6bdb\uff0c\u5f20\u5f00\u53cc\u638c\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u53bb\u5427\uff01\u4e0b\u6b21\u8981\u5c0f\u5fc3\u4e86\uff0c\u53ef\u522b\u518d\u7ed9\u4eba\u6349\u4f4f\u3002\u201d\u5929\u94c3\u9e1f\u5c55\u5f00\u7fc5\u8180\uff0c\u98de\u5165\u4e86\u8349\u4e1b\u4e4b\u4e2d\u3002 \u3000\u3000\u7537\u5b69\u5f88\u662f\u5947\u602a\uff0c\u95ee\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u653e\u4e86\u9e1f\u513f\uff1f\u4f60\u4e0d\u662f\u7528\u7389\u956f\u6362\u4e86\u6765\u7684\u4e48\uff1f\u201d\u4ed6\u7d27\u7d27\u6293\u4f4f\u4e86\u956f\u5b50\uff0c\u751f\u6015\u674e\u6587\u79c0\u53c8\u5411\u4ed6\u8981\u8fd8\u3002\u674e\u6587\u79c0\u9053\uff1a\u201c\u5929\u94c3\u9e1f\u53c8\u98de\uff0c\u53c8\u5531\u6b4c\uff0c\u4e0d\u662f\u5f88\u5feb\u6d3b\u4e48\uff1f\u201d \u3000\u3000\u7537\u5b69\u4fa7\u7740\u5934\u77a7\u4e86\u5979\u4e00\u4f1a\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d\u674e\u6587\u79c0\u9053\uff1a\u201c\u6211\u53eb\u674e\u6587\u79c0\uff0c\u4f60\u5462\uff1f\u201d\u7537\u5b69\u9053\uff1a\u201c\u6211\u53eb\u82cf\u666e\u3002\u201d\u8bf4\u7740\u4fbf\u8df3\u4e86\u8d77\u6765\uff0c\u626c\u7740\u5589\u5499\u5927\u53eb\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u82cf\u666e\u6bd4\u5979\u5927\u4e86\u4e24\u5c81\uff0c\u957f\u5f97\u5f88\u9ad8\uff0c\u7ad9\u5728\u8349\u5730\u4e0a\u5f88\u6709\u70b9\u5a01\u6b66\u3002 \u3000\u3000\u674e\u6587\u79c0\u9053\uff1a\u201c\u4f60\u529b\u6c14\u5f88\u5927\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u82cf\u666e\u975e\u5e38\u9ad8\u5174\uff0c\u8fd9\u5c0f\u5973\u5b69\u968f\u53e3\u4e00\u53e5\u8bdd\uff0c\u6b63\u8bf4\u4e2d\u4e86\u4ed6\u6700\u5f15\u4ee5\u4e3a\u50b2\u7684\u4e8b\u3002\u4ed6\u4ece\u8170\u95f4\u62d4\u51fa\u4e00\u67c4\u77ed\u5200\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4e0a\u4e2a\u6708\uff0c\u6211\u7528\u8fd9\u628a\u5200\u780d\u4f24\u4e86\u4e00\u5934\u72fc\uff0c\u5dee\u70b9\u513f\u5c31\u780d\u6b7b\u4e86\uff0c\u53ef\u60dc\u7ed9\u9003\u8d70\u4e86\u3002\u201d \u3000\u3000\u674e\u6587\u79c0\u5f88\u662f\u60ca\u5947\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u4e48\u5389\u5bb3\uff1f\u201d\u82cf\u666e\u66f4\u52a0\u5f97\u610f\u4e86\uff0c\u9053\uff1a\u201c\u6709\u4e24\u5934\u72fc\u534a\u591c\u91cc\u6765\u54ac\u6211\u5bb6\u7684\u7f8a\uff0c\u7239\u4e0d\u5728\u5bb6\uff0c\u6211\u4fbf\u63d0\u5200\u51fa\u53bb\u8d76\u72fc\u3002\u5927\u72fc\u89c1\u4e86\u706b\u628a\u4fbf\u9003\u4e86\uff0c\u6211\u4e00\u5200\u780d\u4e2d\u4e86\u53e6\u5916\u4e00\u5934\u3002\u201d\u674e\u6587\u79c0\u9053\uff1a\u201c\u4f60\u780d\u4f24\u4e86\u90a3\u5934\u5c0f\u7684\uff1f\u201d\u82cf\u666e\u6709\u4e9b\u4e0d\u597d\u610f\u601d\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u4f46\u968f\u5373\u52a0\u4e0a\u4e00\u53e5\uff1a\u201c\u90a3\u5927\u72fc\u5018\u4f7f\u4e0d\u9003\u8d70\uff0c\u6211\u5c31\u4e00\u5200\u6740\u4e86\u5b83\u3002\u201d \u3000\u3000\u4ed6\u867d\u662f\u8fd9\u4e48\u8bf4\uff0c\u81ea\u5df1\u5374\u5b9e\u5728\u6ca1\u6709\u628a\u63e1\u3002\u4f46\u674e\u6587\u79c0\u6df1\u4fe1\u4e0d\u7591\uff0c\u9053\uff1a\u201c\u6076\u72fc\u6765\u54ac\u5c0f\u7ef5\u7f8a\uff0c\u90a3\u662f\u8be5\u6740\u7684\u3002\u4e0b\u6b21\u4f60\u6740\u5230\u4e86\u72fc\uff0c\u6765\u53eb\u6211\u770b\uff0c\u597d\u4e0d\u597d\uff1f\u201d\u82cf\u666e\u5927\u559c\u9053\uff1a\u201c\u597d\u554a\uff01\u7b49\u6211\u6740\u4e86\u72fc\uff0c\u5c31\u5265\u4e86\u72fc\u76ae\u9001\u7ed9\u4f60\u3002\u201d\u674e\u6587\u79c0\u9053\uff1a\u201c\u8c22\u8c22\u4f60\u5566\uff0c\u90a3\u6211\u5c31\u7ed9\u7237\u7237\u505a\u4e00\u6761\u72fc\u76ae\u57ab\u5b50\u3002\u4ed6\u81ea\u5df1\u90a3\u6761\u5df2\u7ed9\u4e86\u6211\u5566\u3002\u201d\u82cf\u666e\u9053\uff1a\u201c\u4e0d\uff01\u6211\u9001\u7ed9\u4f60\u7684\uff0c\u4f60\u81ea\u5df1\u7528\u3002\u4f60\u628a\u7237\u7237\u7684\u8fd8\u7ed9\u4ed6\u4fbf\u4e86\u3002\u201d\u674e\u6587\u79c0\u70b9\u5934\u9053\uff1a\u201c\u90a3\u4e5f\u597d\u3002\u201d \u3000\u3000\u5728\u4e24\u4e2a\u5c0f\u5c0f\u7684\u5fc3\u7075\u4e4b\u4e2d\uff0c\u672a\u6765\u7684\u8fd8\u6ca1\u6709\u5b9e\u73b0\u7684\u5e0c\u671b\uff0c\u548c\u8fc7\u53bb\u7684\u4e8b\u5b9e\u6ca1\u6709\u591a\u5927\u5206\u522b\u3002\u4ed6\u4eec\u60f3\u5230\u8981\u6740\u72fc\uff0c\u597d\u50cf\u90a3\u5934\u6076\u72fc\u771f\u7684\u5df2\u7ecf\u6740\u6b7b\u4e86\u3002 \u3000\u3000\u4fbf\u8fd9\u6837\uff0c\u4e24\u4e2a\u5c0f\u5b69\u5b50\u4ea4\u4e0a\u4e86\u670b\u53cb\u3002\u54c8\u8428\u514b\u7684\u7537\u6027\u7684\u7c97\u72b7\u8c6a\u8fc8\uff0c\u548c\u6c49\u65cf\u5973\u6027\u7684\u6e29\u67d4\u4ec1\u5584\uff0c\u76f8\u5904\u5f97\u5f88\u662f\u548c\u8c10\u3002 \u3000\u3000\u8fc7\u4e86\u51e0\u5929\uff0c\u674e\u6587\u79c0\u505a\u4e86\u4e00\u53ea\u5c0f\u5c0f\u7684\u8377\u5305\uff0c\u88c5\u6ee1\u4e86\u9ea6\u7cd6\uff0c\u62ff\u53bb\u9001\u7ed9\u82cf\u666e\u3002\u8fd9\u4e00\u4ef6\u793c\u7269\u4f7f\u8fd9\u5c0f\u7537\u5b69\u5f88\u51fa\u4e4e\u610f\u6599\u4e4b\u5916\uff0c\u4ed6\u7528\u5c0f\u9e1f\u513f\u6362\u4e86\u7389\u956f\uff0c\u5df2\u7ecf\u89c9\u5f97\u5360\u4e86\u4fbf\u5b9c\u3002\u54c8\u8428\u514b\u4eba\u5929\u6027\u7684\u6b63\u76f4\uff0c\u4f7f\u4ed6\u8ba4\u4e3a\u5e94\u5f53\u6709\u6240\u8865\u507f\uff0c\u4e8e\u662f\u4ed6\u4e00\u665a\u4e0d\u7761\uff0c\u5728\u8349\u539f\u4e0a\u6349\u4e86\u4e24\u53ea\u5929\u94c3\u9e1f\uff0c\u7b2c\u4e8c\u5929\u62ff\u53bb\u9001\u7ed9\u674e\u6587\u79c0\u3002\u8fd9\u4e00\u4ef6\u6177\u6168\u7684\u4e3e\u52a8\u672a\u514d\u662f\u4f1a\u9519\u4e86\u610f\u3002\u674e\u6587\u79c0\u8d39\u4e86\u5f88\u591a\u5507\u820c\uff0c\u624d\u4f7f\u8fd9\u7537\u5b69\u660e\u767d\uff0c\u5979\u6240\u559c\u6b22\u7684\u662f\u8ba9\u5929\u94c3\u9e1f\u81ea\u7531\u81ea\u5728\uff0c\u800c\u4e0d\u662f\u8981\u6349\u4e86\u6765\u8ba9\u5b83\u53d7\u82e6\u3002\u82cf\u666e\u6700\u540e\u7ec8\u4e8e\u61c2\u4e86\uff0c\u4f46\u5728\u5fc3\u5e95\uff0c\u603b\u662f\u89c9\u5f97\u5979\u7684\u5584\u5fc3\u6709\u4e9b\u50bb\u6c14\uff0c\u53e4\u602a\u800c\u53ef\u7b11\u3002 \u3000\u3000\u65e5\u5b50\u4e00\u5929\u5929\u7684\u8fc7\u53bb\uff0c\u5728\u674e\u6587\u79c0\u7684\u68a6\u91cc\uff0c\u7238\u7238\u5988\u5988\u51fa\u73b0\u7684\u6b21\u6570\u6e10\u6e10\u7a00\u4e86\uff0c\u5979\u6795\u5934\u4e0a\u7684\u6cea\u75d5\u4e5f\u6e10\u6e10\u5c11\u4e86\u3002\u5979\u8138\u4e0a\u6709\u4e86\u66f4\u591a\u7684\u7b11\u9765\uff0c\u5634\u91cc\u6709\u4e86\u66f4\u591a\u7684\u6b4c\u58f0\u3002\u5f53\u5979\u548c\u82cf\u666e\u4e00\u8d77\u7267\u7f8a\u7684\u65f6\u5019\uff0c\u8349\u539f\u4e0a\u5e38\u5e38\u98d8\u6765\u4e86\u8fdc\u5904\u9752\u5e74\u7537\u5973\u5bf9\u7b54\u7684\u60c5\u6b4c\u3002\u674e\u6587\u79c0\u89c9\u5f97\u8fd9\u4e9b\u60c5\u81f4\u7f20\u7ef5\u7684\u6b4c\u513f\u5f88\u597d\u542c\uff0c\u542c\u5f97\u591a\u4e86\uff0c\u968f\u53e3\u4fbf\u80fd\u54fc\u4e86\u51fa\u6765\u3002\u5f53\u7136\uff0c\u5979\u8fd8\u4e0d\u61c2\u6b4c\u91cc\u7684\u610f\u4e49\uff0c\u4e3a\u4ec0\u4e48\u4e00\u4e2a\u7537\u4eba\u4f1a\u5bf9\u4e00\u4e2a\u5973\u90ce\u8fd9\u4e48\u98a0\u5012\uff1f\u4e3a\u4ec0\u4e48\u4e00\u4e2a\u5973\u90ce\u8981\u5bf9\u4e00\u4e2a\u7537\u4eba\u8fd9\u4e48\u503e\u5fc3\uff1f\u4e3a\u4ec0\u4e48\u60c5\u4eba\u7684\u811a\u6b65\u58f0\u4f7f\u5fc3\u623f\u5267\u70c8\u5730\u8df3\u52a8\uff1f\u4e3a\u4ec0\u4e48\u7a88\u7a95\u7684\u8eab\u5b50\u53eb\u4eba\u6574\u665a\u7761\u4e0d\u7740\uff1f\u53ea\u662f\u5979\u6e05\u8106\u5730\u52a8\u542c\u5730\u5531\u4e86\u51fa\u6765\uff0c\u542c\u5230\u7684\u4eba\u90fd\u8bf4\uff1a\u201c\u8fd9\u5c0f\u5973\u5b69\u7684\u6b4c\u513f\u5531\u5f97\u771f\u597d\uff0c\u90a3\u4e0d\u50cf\u8349\u539f\u4e0a\u7684\u4e00\u53ea\u5929\u94c3\u9e1f\u4e48\uff1f\u201d \u3000\u3000\u5230\u4e86\u5bd2\u51b7\u7684\u51ac\u5929\uff0c\u5929\u94c3\u9e1f\u98de\u5230\u5357\u65b9\u6e29\u6696\u7684\u5730\u65b9\u53bb\u4e86\uff0c\u4f46\u5728\u8349\u539f\u4e0a\uff0c\u674e\u6587\u79c0\u7684\u6b4c\u513f\u4ecd\u65e7\u54cd\u7740\uff1a \u3000\u3000\u201c\u554a\uff0c\u4eb2\u7231\u7684\u7267\u7f8a\u5c11\u5e74\uff0c \u3000\u3000\u8bf7\u95ee\u4f60\u591a\u5927\u5e74\u7eaa\uff1f \u3000\u3000\u4f60\u534a\u591c\u91cc\u5728\u6c99\u6f20\u72ec\u884c\uff0c \u3000\u3000\u6211\u548c\u4f60\u4f5c\u4f34\u613f\u4e0d\u613f\u610f\uff1f\u201d \u3000\u3000\u6b4c\u58f0\u5728\u8fd9\u91cc\u987f\u4e86\u4e00\u987f\uff0c\u542c\u5230\u7684\u4eba\u5fc3\u4e2d\u90fd\u5728\u8bf4\uff1a\u201c\u542c\u7740\u8fd9\u6837\u7f8e\u4e3d\u7684\u6b4c\u513f\uff0c\u8c01\u4e0d\u613f\u610f\u8981\u4f60\u4f5c\u4f34\u5462\uff1f\u201d \u3000\u3000\u8ddf\u7740\u6b4c\u58f0\u53c8\u54cd\u4e86\u8d77\u6765\uff1a \u3000\u3000\u201c\u554a\uff0c\u4eb2\u7231\u7684\u4f60\u522b\u751f\u6c14\uff0c \u3000\u3000\u8c01\u597d\u8c01\u574f\u4e00\u65f6\u96be\u77e5\u3002 \u3000\u3000\u8981\u6208\u58c1\u6c99\u6f20\u53d8\u4e3a\u82b1\u56ed\uff0c \u3000\u3000\u53ea\u987b\u4e00\u5bf9\u597d\u4eba\u805a\u5728\u4e00\u8d77\u3002\u201d \u3000\u3000\u542c\u5230\u6b4c\u58f0\u7684\u4eba\u5fc3\u5e95\u91cc\u90fd\u5f00\u4e86\u4e00\u6735\u82b1\uff0c\u4fbf\u662f\u6700\u51b7\u9177\u6700\u8352\u829c\u7684\u5fc3\u5e95\uff0c\u4e5f\u5347\u8d77\u4e86\u6e29\u6696\uff1a\u201c\u5018\u82e5\u662f\u4e00\u5bf9\u597d\u4eba\u805a\u5728\u4e00\u8d77\uff0c\u6208\u58c1\u6c99\u6f20\u81ea\u7136\u6210\u4e86\u82b1\u56ed\uff0c\u8c01\u53c8\u4f1a\u6765\u751f\u4f60\u7684\u6c14\u554a\uff1f\u201d\u8001\u5e74\u4eba\u5e74\u8f7b\u4e86\u4e8c\u5341\u5c81\uff0c\u5e74\u8f7b\u4eba\u5fc3\u4e2d\u6d0b\u6ea2\u6b22\u4e50\u3002\u4f46\u5531\u7740\u60c5\u6b4c\u7684\u674e\u6587\u79c0\uff0c\u5374\u4e0d\u61c2\u5f97\u6b4c\u4e2d\u7684\u610f\u601d\u3002 \u3000\u3000\u542c\u5979\u6b4c\u58f0\u6700\u591a\u7684\uff0c\u662f\u82cf\u666e\u3002\u4ed6\u4e5f\u4e0d\u61c2\u8fd9\u4e9b\u8349\u539f\u4e0a\u60c5\u6b4c\u7684\u542b\u4e49\uff0c\u76f4\u5230\u6709\u4e00\u5929\uff0c\u4ed6\u4eec\u5728\u96ea\u5730\u91cc\u9047\u4e0a\u4e86\u4e00\u5934\u6076\u72fc\u3002 \u3000\u3000\u8fd9\u4e00\u5934\u72fc\u6765\u5f97\u975e\u5e38\u7a81\u7136\u3002\u82cf\u666e\u548c\u674e\u6587\u79c0\u6b63\u5e76\u80a9\u5750\u5728\u4e00\u4e2a\u5c0f\u4e18\u4e0a\uff0c\u671b\u7740\u6563\u5728\u8349\u539f\u4e0a\u7684\u7f8a\u7fa4\u3002 \u3000\u3000\u5c31\u50cf\u5e73\u65f6\u4e00\u6837\uff0c\u674e\u6587\u79c0\u8ddf\u4ed6\u8bf4\u7740\u6545\u4e8b\u3002\u8fd9\u4e9b\u6545\u4e8b\u6709\u4e9b\u662f\u5988\u5988\u4ece\u524d\u8bf4\u7684\uff0c\u6709\u4e9b\u662f\u8ba1\u8001\u4eba\u8bf4\u7684\uff0c\u53e6\u5916\u7684\u662f\u5979\u81ea\u5df1\u7f16\u7684\u3002\u82cf\u666e\u6700\u559c\u6b22\u542c\u8ba1\u8001\u4eba\u90a3\u4e9b\u60ca\u9669\u7684\u51fa\u751f\u5165\u6b7b\u7684\u6545\u4e8b\uff0c\u6700\u4e0d\u6b23\u8d4f\u674e\u6587\u79c0\u81ea\u5df1\u90a3\u4e9b\u5b69\u5b50\u6c14\u7684\u5973\u6027\u6545\u4e8b\uff0c\u4f46\u4e00\u4e2a\u60ca\u9669\u6545\u4e8b\u7ffb\u6765\u8986\u53bb\u7684\u8bf4\u4e86\u51e0\u904d\uff0c\u4fbf\u53d8\u6210\u4e86\u4e0d\u60ca\u4e0d\u9669\uff0c\u4e8e\u662f\u4ed6\u4e5f\u53ea\u5f97\u8010\u5fc3\u7684\u542c\u7740\uff1a \u3000\u3000\u767d\u5154\u513f\u600e\u6837\u627e\u4e0d\u5230\u5988\u5988\uff0c\u5c0f\u82b1\u72d7\u600e\u6837\u53bb\u5e2e\u5b83\u5bfb\u627e\u3002\u7a81\u7136\u4e4b\u95f4\uff0c\u674e\u6587\u79c0\u201c\u554a\u201d\u7684\u4e00\u58f0\uff0c\u5411\u540e\u7ffb\u5012\uff0c\u4e00\u5934\u5927\u7070\u72fc\u5c16\u5229\u7684\u7259\u9f7f\u54ac\u5411\u5979\u7684\u54bd\u5589\u3002 \u3000\u3000\u8fd9\u5934\u72fc\u4ece\u80cc\u540e\u6084\u65e0\u58f0\u606f\u7684\u88ad\u6765\uff0c\u4e24\u4e2a\u5c0f\u5b69\u8c01\u90fd\u6ca1\u6709\u53d1\u89c9\u3002 \u3000\u3000\u674e\u6587\u79c0\u66fe\u8ddf\u5988\u5988\u5b66\u8fc7\u4e00\u4e9b\u6b66\u529f\uff0c\u81ea\u7136\u800c\u7136\u7684\u5c06\u5934\u4e00\u4fa7\uff0c\u907f\u5f00\u4e86\u51f6\u72fc\u5bf9\u51c6\u7740\u5979\u54bd\u5589\u7684\u4e00\u54ac\u3002\u82cf\u666e\u89c1\u8fd9\u5934\u6076\u72fc\u8fd9\u822c\u9ad8\u5927\uff0c\u5413\u5f97\u811a\u4e5f\u8f6f\u4e86\uff0c\u4f46\u4ed6\u7acb\u5373\u60f3\u8d77\uff1a\u201c\u975e\u6551\u5979\u4e0d\u53ef\uff01\u201d\u4ece\u8170\u95f4\u62d4\u51fa\u77ed\u5200\uff0c\u6251\u4e0a\u53bb\u4e00\u5200\u523a\u5728\u5927\u7070\u72fc\u7684\u80cc\u4e0a\u3002 \u3000\u3000\u7070\u72fc\u7684\u9aa8\u5934\u5f88\u786c\uff0c\u77ed\u5200\u4ece\u5b83\u80cc\u810a\u4e0a\u6ed1\u5f00\u4e86\uff0c\u53ea\u4f24\u4e86\u4e00\u4e9b\u76ae\u8089\u3002\u4f46\u7070\u72fc\u4e5f\u5bdf\u89c9\u4e86\u5371\u9669\uff0c\u653e\u5f00\u4e86\u674e\u6587\u79c0\uff0c\u5f20\u5f00\u8840\u76c6\u5927\u53e3\uff0c\u7a81\u7136\u7eb5\u8d77\uff0c\u53cc\u8db3\u642d\u5728\u82cf\u666e\u7684\u80a9\u5934\uff0c\u4fbf\u5f80\u4ed6\u8138\u4e0a\u54ac\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u82cf\u666e\u4e00\u60ca\u4e4b\u4e0b\uff0c\u5411\u540e\u4fbf\u5012\u3002\u90a3\u7070\u72fc\u6765\u52bf\u4f3c\u7535\uff0c\u53cc\u8db3\u8ddf\u7740\u6309\u4e86\u4e0b\u53bb\uff0c\u767d\u68ee\u68ee\u7684\u7360\u7259\u5df2\u89e6\u5230\u82cf\u666e\u8138\u988a\u3002\u674e\u6587\u79c0\u6781\u662f\u5bb3\u6015\uff0c\u4f46\u4ecd\u662f\u9f13\u8d77\u52c7\u6c14\uff0c\u62c9\u4f4f\u7070\u72fc\u5c3e\u5df4\u7528\u529b\u5411\u540e\u62c9\u626f\u3002\u5927\u7070\u72fc\u7ed9\u5979\u4e00\u62c9\u4e4b\u4e0b\uff0c\u5411\u540e\u9000\u4e86\u4e00\u6b65\uff0c\u4f46\u5b83\u997f\u5f97\u614c\u4e86\uff0c\u540e\u8db3\u7262\u7262\u636e\u5730\uff0c\u53eb\u674e\u6587\u79c0\u518d\u4e5f\u62c9\u5b83\u4e0d\u52a8\uff0c\u8ddf\u7740\u53c8\u662f\u4e00\u53e3\u54ac\u843d\u3002 \u3000\u3000\u53ea\u542c\u5f97\u82cf\u666e\u5927\u53eb\u4e00\u58f0\uff0c\u51f6\u72fc\u5df2\u54ac\u4e2d\u4ed6\u5de6\u80a9\u3002\u674e\u6587\u79c0\u60ca\u5f97\u51e0\u4e4e\u8981\u54ed\u4e86\u51fa\u6765\uff0c\u9f13\u8d77\u5e73\u751f\u4e4b\u529b\u4e00\u62c9\u3002\u7070\u72fc\u5403\u75db\uff0c\u5f20\u53e3\u547c\u53f7\uff0c\u5374\u628a\u54ac\u5728\u82cf\u666e\u80a9\u5934\u7684\u7259\u9f7f\u677e\u4e86\u3002\u82cf\u666e\u8ff7\u8ff7\u7cca\u7cca\u7684\u9001\u51fa\u4e00\u5200\uff0c\u6b63\u597d\u523a\u4e2d\u7070\u72fc\u809a\u8179\u4e0a\u67d4\u8f6f\u4e4b\u5904\uff0c\u8fd9\u4e00\u5200\u76f4\u6ca1\u81f3\u67c4\u3002\u4ed6\u60f3\u8981\u62d4\u51fa\u5200\u6765\u518d\u523a\uff0c\u90a3\u7070\u72fc\u731b\u5730\u8dc3\u8d77\uff0c\u5728\u96ea\u5730\u91cc\u6253\u4e86\u51e0\u4e2a\u6eda\uff0c\u4ef0\u5929\u6b7b\u4e86\u3002 \u3000\u3000\u7070\u72fc\u8fd9\u4e00\u7ffb\u6eda\uff0c\u5e26\u5f97\u674e\u6587\u79c0\u4e5f\u6454\u4e86\u51e0\u4e2a\u7b4b\u6597\uff0c\u53ef\u662f\u5979\u5140\u81ea\u62c9\u4f4f\u7070\u72fc\u7684\u5c3e\u5df4\uff0c\u59cb\u7ec8\u4e0d\u653e\u3002\u82cf\u666e\u6323\u624e\u7740\u7ad9\u8d77\u8eab\u6765\uff0c\u770b\u89c1\u8fd9\u4e48\u5de8\u5927\u7684\u4e00\u5934\u7070\u72fc\u6b7b\u5728\u96ea\u5730\u4e4b\u4e2d\uff0c\u4e0d\u7981\u60ca\u5f97\u5446\u4e86\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u624d\u6b22\u7136\u53eb\u9053\uff1a\u201c\u6211\u6740\u6b7b\u4e86\u5927\u72fc\uff0c\u6211\u6740\u6b7b\u4e86\u5927\u72fc\uff01\u201d\u4f38\u624b\u6276\u8d77\u674e\u6587\u79c0\uff0c\u9a84\u50b2\u5730\u9053\uff1a\u201c\u963f\u79c0\uff0c\u4f60\u77a7\uff0c\u6211\u6740\u4e86\u5927\u72fc\uff01\u201d\u5f97\u610f\u4e4b\u4e0b\uff0c\u867d\u662f\u80a9\u5934\u9c9c\u8840\u957f\u6d41\uff0c\u4e00\u65f6\u7adf\u4e5f\u4e0d\u89c9\u75bc\u75db\u3002\u674e\u6587\u79c0\u89c1\u4ed6\u7684\u7f8a\u76ae\u8884\u5b50\u5de6\u895f\u4e0a\u67d3\u6ee1\u4e86\u8840\uff0c\u5fd9\u7ffb\u5f00\u4ed6\u76ae\u8884\uff0c\u4ece\u6000\u91cc\u62ff\u51fa\u624b\u5e15\uff0c\u6309\u4f4f\u4ed6\u4f24\u53e3\u4e2d\u4e0d\u4f4f\u6d41\u51fa\u7684\u9c9c\u8840\uff0c\u95ee\u9053\uff1a\u201c\u75db\u4e0d\u75db\uff1f\u201d\u82cf\u666e\u82e5\u662f\u72ec\u81ea\u4e00\u4e2a\u513f\uff0c\u65e9\u5c31\u75db\u5f97\u5927\u54ed\u5927\u558a\uff0c\u4f46\u8fd9\u65f6\u5fc3\u4e2d\u5145\u6ee1\u4e86\u82f1\u96c4\u6c14\u6982\uff0c\u6447\u6447\u5934\u8bf4\uff1a\u201c\u6211\u4e0d\u6015\u75db\uff01\u201d \u3000\u3000\u5ffd\u542c\u5f97\u8eab\u540e\u4e00\u4eba\u8bf4\u9053\uff1a\u201c\u963f\u666e\uff0c\u4f60\u5728\u5e72\u4ec0\u4e48\uff1f\u201d\u4e24\u4eba\u56de\u8fc7\u5934\u6765\uff0c\u53ea\u89c1\u4e00\u4e2a\u6ee1\u8138\u866c\u9aef\u7684\u5927\u6c49\uff0c\u9a91\u5728\u9a6c\u4e0a\u3002 \u3000\u3000\u82cf\u666e\u53eb\u9053\uff1a\u201c\u7239\uff0c\u4f60\u77a7\uff0c\u6211\u6740\u6b7b\u4e86\u4e00\u5934\u5927\u72fc\u3002\u201d\u90a3\u5927\u6c49\u5927\u559c\uff0c\u7ffb\u8eab\u4e0b\u9a6c\uff0c\u53ea\u89c1\u513f\u5b50\u8138\u4e0a\u6e85\u6ee1\u4e86\u8840\uff0c\u773c\u5149\u53c8\u63a0\u8fc7\u674e\u6587\u79c0\u7684\u8138\uff0c\u95ee\u82cf\u666e\u9053\uff1a\u201c\u4f60\u7ed9\u72fc\u54ac\u4e86\uff1f\u201d\u82cf\u666e\u9053\uff1a\u201c\u6211\u5728\u8fd9\u513f\u542c\u963f\u79c0\u8bf4\u6545\u4e8b\uff0c\u5ffd\u7136\u8fd9\u5934\u72fc\u6765\u54ac\u5979\u2026\u2026\u201d\u7a81\u7136\u4e4b\u95f4\uff0c\u90a3\u5927\u6c49\u8138\u4e0a\u7f69\u4e0a\u4e86\u4e00\u5c42\u9634\u5f71\uff0c\u671b\u7740\u674e\u6587\u79c0\u51b7\u51b7\u7684\u9053\uff1a\u201c\u4f60\u4fbf\u662f\u90a3\u4e2a\u771f\u4e3b\u964d\u7f5a\u7684\u6c49\u4eba\u5973\u5b69\u513f\u4e48\uff1f\u201d \u3000\u3000\u8fd9\u65f6\u674e\u6587\u79c0\u5df2\u8ba4\u51fa\u4ed6\u6765\uff0c\u90a3\u4fbf\u662f\u8e22\u8fc7\u5979\u4e00\u811a\u7684\u82cf\u9c81\u514b\u3002\u5979\u8bb0\u8d77\u4e86\u8ba1\u8001\u4eba\u7684\u8bdd\uff1a\u201c\u4ed6\u7684\u59bb\u5b50\u548c\u5927\u513f\u5b50\uff0c\u4e00\u591c\u4e4b\u95f4\u90fd\u7ed9\u6c49\u4eba\u5f3a\u76d7\u6740\u4e86\uff0c\u56e0\u6b64\u4ed6\u6068\u6781\u4e86\u6c49\u4eba\u3002\u201d\u5979\u70b9\u4e86\u70b9\u5934\uff0c\u6b63\u60f3\u8bf4\uff1a\u201c\u6211\u7239\u7239\u5988\u5988\u4e5f\u662f\u7ed9\u90a3\u4e9b\u5f3a\u76d7\u5bb3\u7684\u3002\u201d\u8bdd\u8fd8\u6ca1\u51fa\u53e3\uff0c\u7a81\u7136\u5237\u7684\u4e00\u58f0\uff0c\u82cf\u666e\u8138\u4e0a\u80bf\u8d77\u4e86\u4e00\u6761\u957f\u957f\u7684\u7ea2\u75d5\uff0c\u662f\u7ed9\u7236\u4eb2\u7528\u9a6c\u97ad\u91cd\u91cd\u7684\u62bd\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u82cf\u9c81\u514b\u559d\u9053\uff1a\u201c\u6211\u53eb\u4f60\u4e16\u4e16\u4ee3\u4ee3\uff0c\u90fd\u8981\u618e\u6068\u6c49\u4eba\uff0c\u4f60\u5fd8\u4e86\u6211\u7684\u8bdd\uff0c\u504f\u53bb\u8ddf\u6c49\u4eba\u7684\u5973\u5b69\u513f\u73a9\uff0c\u8fd8\u4e3a\u6c49\u4eba\u7684\u5973\u513f\u62da\u547d\u6d41\u8840\uff01\u201d \u3000\u3000\u5237\u7684\u4e00\u58f0\uff0c\u5939\u5934\u5939\u8111\u7684\u53c8\u62bd\u4e86\u513f\u5b50\u4e00\u97ad\u3002 \u3000\u3000\u82cf\u666e\u7adf\u4e0d\u95ea\u907f\uff0c\u53ea\u662f\u5446\u5446\u7684\u671b\u7740\u674e\u6587\u79c0\uff0c\u95ee\u9053\uff1a\u201c\u5979\u662f\u771f\u4e3b\u964d\u7f5a\u7684\u6c49\u4eba\u4e48\uff1f\u201d\u82cf\u9c81\u514b\u543c\u9053\uff1a\u201c\u96be\u9053\u4e0d\u662f\uff1f\u201d\u56de\u8fc7\u9a6c\u97ad\uff0c\u5237\u7684\u4e00\u4e0b\u53c8\u62bd\u5728\u674e\u6587\u79c0\u8138\u4e0a\u3002\u674e\u6587\u79c0\u9000\u4e86\u4e24\u6b65\uff0c\u4f38\u624b\u6309\u4f4f\u4e86\u8138\u3002 \u3000\u3000\u82cf\u666e\u7ed9\u7070\u72fc\u54ac\u540e\u53d7\u4f24\u672c\u91cd\uff0c\u8ddf\u7740\u53c8\u88ab\u72e0\u72e0\u7684\u62bd\u4e86\u4e24\u97ad\uff0c\u518d\u4e5f\u652f\u6301\u4e0d\u4f4f\uff0c\u8eab\u5b50\u4e00\u6643\uff0c\u6454\u5012\u5728\u5730\u3002 \u3000\u3000\u82cf\u9c81\u514b\u89c1\u4ed6\u53cc\u76ee\u7d27\u95ed\uff0c\u6655\u4e86\u8fc7\u53bb\uff0c\u4e5f\u5403\u4e86\u4e00\u60ca\uff0c\u6025\u5fd9\u8df3\u4e0b\u9a6c\u6765\uff0c\u62b1\u8d77\u513f\u5b50\uff0c\u8ddf\u7740\u548c\u8eab\u7eb5\u8d77\uff0c\u843d\u5728\u9a6c\u80cc\u4e4b\u4e0a\uff0c\u4e00\u4e2a\u7ef3\u5708\u7529\u51fa\uff0c\u5957\u4f4f\u6b7b\u72fc\u5934\u9888\uff0c\u53cc\u817f\u4e00\u631f\uff0c\u7eb5\u9a6c\u4fbf\u884c\u3002\u6b7b\u72fc\u5728\u96ea\u5730\u4e2d\u4e00\u8def\u62d6\u7740\u8ddf\u53bb\uff0c\u96ea\u5730\u91cc\u4e24\u884c\u8e44\u5370\u4e4b\u95f4\uff0c\u7559\u7740\u4e00\u884c\u957f\u957f\u7684\u8840\u8ff9\u3002\u82cf\u9c81\u514b\u9a70\u51fa\u5341\u4f59\u4e08\uff0c\u56de\u8fc7\u5934\u6765\u6076\u6bd2\u5730\u671b\u4e86\u674e\u6587\u79c0\u4e00\u773c\uff0c\u773c\u5149\u4e2d\u4f3c\u4e4e\u5728\u8bf4\uff1a\u201c\u4e0b\u6b21\u4f60\u518d\u649e\u5728\u6211\u7684\u624b\u91cc\uff0c\u77a7\u6211\u4e0d\u597d\u597d\u7684\u6253\u4f60\u4e00\u987f\u3002\u201d \u3000\u3000\u674e\u6587\u79c0\u5012\u4e0d\u5bb3\u6015\u8fd9\u4e2a\u773c\u8272\uff0c\u53ea\u662f\u5fc3\u4e2d\u4e00\u7247\u7a7a\u865a\uff0c\u77e5\u9053\u82cf\u666e\u4ece\u4eca\u4e4b\u540e\uff0c\u518d\u4e0d\u4f1a\u505a\u5979\u7684\u670b\u53cb\uff0c\u518d\u4e5f\u4e0d\u4f1a\u6765\u542c\u5979\u5531\u6b4c\u3001\u6765\u542c\u5979\u8bf4\u6545\u4e8b\u4e86\u3002\u53ea\u89c9\u5f97\u6714\u98ce\u66f4\u52a0\u51b7\u5f97\u96be\u53d7\uff0c\u8138\u4e0a\u7684\u97ad\u4f24\u968f\u7740\u8109\u640f\u7684\u8df3\u52a8\uff0c\u4e00\u62bd\u4e00\u62bd\u5730\u66f4\u52a0\u5267\u70c8\u7684\u75bc\u75db\u3002 \u3000\u3000\u5979\u832b\u832b\u7136\u7684\u8d76\u4e86\u7f8a\u7fa4\u56de\u5bb6\u3002\u8ba1\u8001\u4eba\u770b\u5230\u5979\u8863\u886b\u4e0a\u8bb8\u591a\u9c9c\u8840\uff0c\u8138\u4e0a\u53c8\u662f\u80bf\u8d77\u4e00\u6761\u97ad\u75d5\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u5fd9\u95ee\u5979\u4ec0\u4e48\u4e8b\u3002\u674e\u6587\u79c0\u53ea\u6de1\u6de1\u7684\u9053\uff1a\u201c\u662f\u6211\u4e0d\u5c0f\u5fc3\u6454\u7684\u3002\u201d\u8ba1\u8001\u4eba\u5f53\u7136\u4e0d\u4fe1\u3002\u53ef\u662f\u4e00\u518d\u76f8\u8be2\uff0c\u674e\u6587\u79c0\u53ea\u662f\u8fd9\u6837\u56de\u7b54\uff0c\u95ee\u5f97\u6025\u4e86\uff0c\u5979\u54c7\u4e86\u4e00\u58f0\u5927\u54ed\u8d77\u6765\uff0c\u7adf\u662f\u4e00\u53e5\u8bdd\u4e5f\u4e0d\u80af\u518d\u8bf4\u3002 \u3000\u3000\u90a3\u5929\u665a\u4e0a\uff0c\u674e\u6587\u79c0\u53d1\u7740\u9ad8\u70e7\uff0c\u5c0f\u8138\u86cb\u513f\u70e7\u5f97\u8840\u7ea2\uff0c\u8bf4\u4e86\u8bb8\u591a\u80e1\u8bdd\uff0c\u4ec0\u4e48\u201c\u5927\u7070\u72fc\uff01\u201d\u201c\u82cf\u666e\uff0c\u82cf\u666e\uff0c\u5feb\u6551\u6211\uff01\u201d\u4ec0\u4e48\u201c\u771f\u4e3b\u964d\u7f5a\u7684\u6c49\u4eba\u3002\u201d\u8ba1\u8001\u4eba\u731c\u5230\u4e86\u51e0\u5206\uff0c\u5fc3\u4e2d\u5f88\u662f\u7126\u6025\u3002\u5e78\u597d\u5230\u9ece\u660e\u65f6\uff0c\u5979\u7684\u70e7\u9000\u4e86\uff0c\u6c89\u6c89\u7761\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u573a\u75c5\u76f4\u751f\u4e86\u4e00\u4e2a\u591a\u6708\uff0c\u5230\u5979\u8d77\u5e8a\u65f6\uff0c\u5bd2\u51ac\u5df2\u7ecf\u8fc7\u53bb\uff0c\u5929\u5c71\u4e0a\u7684\u767d\u96ea\u5f00\u59cb\u878d\u5316\uff0c\u4e00\u9053\u9053\u96ea\u6c34\u6c47\u6210\u7684\u5c0f\u6eaa\uff0c\u6d41\u5230\u8349\u539f\u4e0a\u6765\u3002\u539f\u91ce\u4e0a\u5df2\u8301\u8d77\u4e86\u4e00\u4e1d\u4e1d\u7684\u5ae9\u8349\u3002 \u3000\u3000\u8fd9\u4e00\u5929\uff0c\u674e\u6587\u79c0\u4e00\u65e9\u8d77\u6765\uff0c\u6253\u5f00\u5927\u95e8\uff0c\u60f3\u8d76\u4e86\u7f8a\u7fa4\u51fa\u53bb\u653e\u7267\uff0c\u53ea\u89c1\u95e8\u5916\u653e\u7740\u4e00\u5f20\u5927\u72fc\u76ae\uff0c\u505a\u6210\u4e86\u57ab\u5b50\u7684\u6a21\u6837\u3002\u674e\u6587\u79c0\u5403\u4e86\u4e00\u60ca\uff0c\u770b\u8fd9\u72fc\u76ae\u7684\u6bdb\u8272\uff0c\u6b63\u662f\u90a3\u5929\u5728\u96ea\u5730\u4e2d\u54ac\u5979\u7684\u90a3\u5934\u5927\u7070\u72fc\u3002\u5979\u4fef\u4e0b\u8eab\u6765\uff0c\u89c1\u72fc\u76ae\u7684\u809a\u8179\u5904\u6709\u4e2a\u5203\u5b54\u3002\u5979\u5fc3\u4e2d\u6026\u6026\u8df3\u7740\uff0c\u77e5\u9053\u82cf\u666e\u5e76\u6ca1\u5fd8\u8bb0\u5979\uff0c\u4e5f\u6ca1\u5fd8\u8bb0\u4ed6\u81ea\u5df1\u8bf4\u8fc7\u7684\u8bdd\uff0c\u534a\u591c\u91cc\u5077\u5077\u5c06\u8fd9\u72fc\u76ae\u653e\u5728\u5979\u7684\u95e8\u524d\u3002\u5979\u5c06\u72fc\u76ae\u6536\u5728\u81ea\u5df1\u623f\u4e2d\uff0c\u4e0d\u8ddf\u8ba1\u8001\u4eba\u8bf4\u8d77\uff0c\u8d76\u4e86\u7f8a\u7fa4\uff0c\u4fbf\u5230\u60ef\u5e38\u548c\u82cf\u666e\u76f8\u4f1a\u7684\u5730\u65b9\u53bb\u7b49\u4ed6\u3002 \u3000\u3000\u4f46\u5979\u4e00\u76f4\u7b49\u5230\u65e5\u843d\u897f\u5c71\uff0c\u82cf\u666e\u59cb\u7ec8\u6ca1\u6765\u3002\u5979\u8ba4\u5f97\u82cf\u666e\u5bb6\u91cc\u7684\u7f8a\u7fa4\uff0c\u8fd9\u4e00\u5929\u5374\u7531\u4e00\u4e2a\u5341\u4e03\u516b\u5c81\u7684\u9752\u5e74\u653e\u7267\u3002\u674e\u6587\u79c0\u60f3\uff1a \u3000\u3000\u201c\u96be\u9053\u82cf\u666e\u7684\u4f24\u8fd8\u6ca1\u6709\u597d\uff1f\u600e\u5730\u4ed6\u53c8\u9001\u72fc\u76ae\u7ed9\u6211\uff1f\u201d\u5979\u5f88\u60f3\u5230\u4ed6\u5e10\u7bf7\u91cc\u53bb\u77a7\u77a7\u4ed6\uff0c\u53ef\u662f\u8ddf\u7740\u4fbf\u60f3\u5230\u4e86\u82cf\u9c81\u514b\u7684\u97ad\u5b50\u3002 \u3000\u3000\u8fd9\u5929\u534a\u591c\u91cc\uff0c\u5979\u7ec8\u4e8e\u9f13\u8d77\u4e86\u52c7\u6c14\uff0c\u8d70\u5230\u82cf\u666e\u7684\u5e10\u7bf7\u540e\u9762\u3002 \u3000\u3000\u5979\u4e0d\u77e5\u9053\u4e3a\u4ec0\u4e48\u8981\u53bb\uff0c\u662f\u4e3a\u4e86\u60f3\u8bf4\u4e00\u53e5\u201c\u8c22\u8c22\u4f60\u7684\u72fc\u76ae\u201d\uff1f\u4e3a\u4e86\u60f3\u77a7\u77a7\u4ed6\u7684\u4f24\u597d\u4e86\u6ca1\u6709\uff1f\u5979\u81ea\u5df1\u4e5f\u8bf4\u4e0d\u4e0a\u6765\u3002\u5979\u8eb2\u5728\u5e10\u7bf7\u540e\u9762\u3002\u82cf\u666e\u7684\u7267\u7f8a\u72ac\u8bc6\u5f97\u5979\uff0c\u8fc7\u6765\u5728\u5979\u8eab\u4e0a\u55c5\u4e86\u51e0\u4e0b\u4fbf\u8d70\u5f00\u4e86\uff0c\u4e00\u58f0\u4e5f\u6ca1\u5420\u3002\u5e10\u7bf7\u4e2d\u8fd8\u4eae\u7740\u725b\u6cb9\u70db\u7684\u70db\u5149\uff0c\u82cf\u9c81\u514b\u7c97\u5927\u7684\u55d3\u5b50\u5728\u5927\u58f0\u5486\u54ee\u7740\u3002 \u3000\u3000\u201c\u4f60\u7684\u72fc\u76ae\u62ff\u53bb\u9001\u7ed9\u4e86\u54ea\u4e00\u4e2a\u59d1\u5a18\uff1f\u597d\u5c0f\u5b50\uff0c\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u4e5f\u61c2\u5f97\u628a\u7b2c\u4e00\u6b21\u7684\u730e\u7269\u62ff\u53bb\u9001\u7ed9\u5fc3\u7231\u7684\u59d1\u5a18\u3002\u201d\u4ed6\u6bcf\u547c\u559d\u4e00\u53e5\uff0c\u674e\u6587\u79c0\u7684\u5fc3\u4fbf\u5267\u70c8\u5730\u8df3\u52a8\u4e00\u4e0b\u3002\u5979\u542c\u5f97\u82cf\u666e\u5728\u8bb2\u6545\u4e8b\u65f6\u8bf4\u8fc7\u54c8\u8428\u514b\u4eba\u7684\u4e60\u4fd7\uff0c\u6bcf\u4e00\u4e2a\u9752\u5e74\u6700\u5b9d\u8d35\u81ea\u5df1\u7b2c\u4e00\u6b21\u7684\u730e\u7269\uff0c\u603b\u662f\u62ff\u53bb\u9001\u7ed9\u4ed6\u5fc3\u7231\u7684\u59d1\u5a18\uff0c\u4ee5\u8868\u793a\u60c5\u610f\u3002\u8fd9\u65f6\u5979\u542c\u5230\u82cf\u9c81\u514b\u8fd9\u822c\u559d\u95ee\uff0c\u5c0f\u5c0f\u7684\u8138\u86cb\u513f\u7ea2\u4e86\uff0c\u5fc3\u4e2d\u611f\u5230\u4e86\u9a84\u50b2\u3002\u4ed6\u4eec\u4e8c\u4eba\u5e74\u7eaa\u90fd\u8fd8\u5c0f\uff0c\u4e0d\u77e5\u9053\u771f\u6b63\u7684\u60c5\u7231\u662f\u4ec0\u4e48\uff0c\u4f46\u9690\u9690\u7ea6\u7ea6\u7684\uff0c\u4e5f\u5c1d\u5230\u4e86\u521d\u604b\u7684\u751c\u871c\u548c\u82e6\u6da9\u3002 \u3000\u3000\u201c\u4f60\u5b9a\u662f\u62ff\u53bb\u9001\u7ed9\u4e86\u90a3\u4e2a\u771f\u4e3b\u964d\u7f5a\u7684\u6c49\u4eba\u59d1\u5a18\uff0c\u90a3\u4e2a\u53eb\u505a\u674e\u4ec0\u4e48\u7684\u8d31\u79cd\uff0c\u662f\u4e0d\u662f\uff1f\u597d\uff0c\u4f60\u4e0d\u8bf4\uff0c\u77a7\u662f\u4f60\u5389\u5bb3\uff0c\u8fd8\u662f\u4f60\u7239\u7239\u7684\u97ad\u5b50\u5389\u5bb3\uff1f\u201d \u3000\u3000\u53ea\u542c\u5f97\u5237\u5237\u5237\u5237\uff0c\u51e0\u4e0b\u97ad\u5b50\u62bd\u6253\u5728\u8089\u4f53\u4e0a\u7684\u58f0\u97f3\u3002\u50cf\u82cf\u9c81\u514b\u8fd9\u4e00\u7c7b\u7684\u54c8\u8428\u514b\u4eba\uff0c\u7d20\u6765\u76f8\u4fe1\u53ea\u6709\u97ad\u5b50\u4e0b\u624d\u80fd\u4ea7\u751f\u5f3a\u608d\u7684\u597d\u6c49\u5b50\uff0c\u7ba1\u6559\u513f\u5b50\u4e0d\u80fd\u7528\u6e29\u548c\u7684\u6cd5\u5b50\u3002\u4ed6\u7956\u7236\u8fd9\u6837\u97ad\u6253\u4ed6\u7236\u4eb2\uff0c\u4ed6\u7236\u4eb2\u8fd9\u6837\u97ad\u6253\u4ed6\uff0c\u4ed6\u81ea\u5df1\u4fbf\u4e5f\u8fd9\u6837\u97ad\u6253\u513f\u5b50\uff0c\u7236\u5b50\u4e4b\u7231\u5e76\u4e0d\u56e0\u6b64\u800c\u51cf\u5f31\u3002\u7537\u513f\u6c49\u5bf9\u4ed8\u7537\u513f\u6c49\uff0c\u5728\u670b\u53cb\u548c\u4eb2\u4eba\u662f\u62f3\u5934\u548c\u97ad\u5b50\uff0c\u5728\u654c\u4eba\u4fbf\u662f\u77ed\u5200\u548c\u957f\u5251\u3002\u4f46\u5bf9\u4e8e\u674e\u6587\u79c0\uff0c\u5979\u7239\u7239\u5988\u5988\u4ece\u5c0f\u8fde\u91cd\u8bdd\u4e5f\u4e0d\u5bf9\u5979\u8bf4\u4e00\u53e5\uff0c\u53ea\u8981\u8138\u4e0a\u5c11\u4e86\u4e00\u4e1d\u7b11\u5bb9\uff0c\u5c11\u4e86\u4e00\u4e9b\u7231\u629a\uff0c\u90a3\u4fbf\u662f\u75db\u82e6\u7684\u60e9\u7f5a\u4e86\u3002\u8fd9\u65f6\u6bcf\u4e00\u97ad\u90fd\u5982\u6253\u5728\u5979\u7684\u8eab\u4e0a\u4e00\u822c\u75db\u695a\u3002\u201c\u82cf\u666e\u7684\u7239\u7239\u4e00\u5b9a\u6068\u6781\u4e86\u6211\uff0c\u81ea\u5df1\u4eb2\u751f\u7684\u513f\u5b50\u90fd\u6253\u5f97\u8fd9\u4e48\u51f6\u72e0\uff0c\u4f1a\u4e0d\u4f1a\u6253\u6b7b\u4e86\u4ed6\u5462\uff1f\u201d \u3000\u3000\u201c\u597d\uff01\u4f60\u4e0d\u56de\u7b54\uff01\u4f60\u56de\u4e0d\u56de\u7b54\uff1f\u6211\u731c\u5230\u4f60\u5b9a\u662f\u62ff\u53bb\u9001\u7ed9\u4e86\u90a3\u4e2a\u6c49\u4eba\u59d1\u5a18\u3002\u201d\u97ad\u5b50\u4e0d\u4f4f\u7684\u5f80\u4e0b\u62bd\u6253\u3002\u82cf\u666e\u8d77\u521d\u54ac\u7740\u7259\u786c\u5fcd\uff0c\u5230\u540e\u6765\u7ec8\u4e8e\u54ed\u558a\u8d77\u6765\uff1a\u201c\u7239\u7239\uff0c\u522b\u6253\u5566\uff0c\u522b\u6253\u5566\uff0c\u6211\u75db\uff0c\u6211\u75db\uff01\u201d \u3000\u3000\u82cf\u9c81\u514b\u9053\uff1a\u201c\u90a3\u4f60\u8bf4\uff0c\u662f\u4e0d\u662f\u5c06\u72fc\u76ae\u9001\u7ed9\u4e86\u90a3\u4e2a\u6c49\u4eba\u59d1\u5a18\uff1f\u4f60\u5988\u6b7b\u5728\u6c49\u4eba\u5f3a\u76d7\u624b\u91cc\uff0c\u4f60\u54e5\u54e5\u662f\u6c49\u4eba\u5f3a\u76d7\u6740\u7684\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\uff1f \u3000\u3000\u4ed6\u4eec\u53eb\u6211\u54c8\u8428\u514b\u7b2c\u4e00\u52c7\u58eb\uff0c\u53ef\u662f\u6211\u7684\u8001\u5a46\u513f\u5b50\u5374\u8ba9\u6c49\u4eba\u5f3a\u76d7\u6740\u4e86\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\uff1f\u4e3a\u4ec0\u4e48\u90a3\u5929\u6211\u504f\u504f\u4e0d\u5728\u5bb6\uff1f\u4e3a\u4ec0\u4e48\u603b\u662f\u627e\u4e0d\u5230\u8fd9\u7fa4\u5f3a\u76d7\uff0c\u597d\u8ba9\u6211\u7ed9\u4f60\u5988\u5988\u54e5\u54e5\u62a5\u4ec7\u96ea\u6068\uff1f\u201d \u3000\u3000\u82cf\u9c81\u514b\u8fd9\u65f6\u7684\u97ad\u5b50\u65e9\u5df2\u4e0d\u662f\u7ba1\u6559\u513f\u5b50\uff0c\u800c\u662f\u5728\u53d1\u6cc4\u5fc3\u4e2d\u7684\u72c2\u6012\u3002\u4ed6\u6bcf\u4e00\u97ad\u4e0b\u53bb\uff0c\u90fd\u4f3c\u5728\u97ad\u6253\u654c\u4eba\uff0c\u201c\u4e3a\u4ec0\u4e48\u90a3\u72d7\u5f3a\u76d7\u4e0d\u6765\u8ddf\u6211\u660e\u5200\u660e\u67aa\u7684\u51b3\u4e00\u6b7b\u6218\uff1f\u4f60\u8bf4\u4e0d\u8bf4\uff1f\u96be\u9053\u6211\u82cf\u9c81\u514b\u662f\u54c8\u8428\u514b\u7b2c\u4e00\u52c7\u58eb\uff0c\u8fd8\u6253\u4e0d\u8fc7\u51e0\u4e2a\u6c49\u4eba\u7684\u6bdb\u8d3c\u2026\u2026\u201d \u3000\u3000\u4ed6\u88ab\u970d\u5143\u9f99\u3001\u9648\u8fbe\u6d77\u4ed6\u4eec\u6240\u6740\u6b7b\u7684\u5b69\u5b50\uff0c\u662f\u4ed6\u6700\u5fc3\u7231\u7684\u957f\u5b50\uff0c\u88ab\u4ed6\u4eec\u4fae\u8fb1\u800c\u6b7b\u7684\u59bb\u5b50\uff0c\u662f\u81ea\u5e7c\u548c\u4ed6\u4e00\u8d77\u957f\u5927\u7684\u7231\u4fa3\u3002 \u3000\u3000\u800c\u4ed6\u81ea\u5df1\uff0c\u4e8c\u5341\u4f59\u5e74\u6765\u4eba\u4eba\u90fd\u79f0\u4ed6\u662f\u54c8\u8428\u514b\u65cf\u7684\u7b2c\u4e00\u52c7\u58eb\uff0c\u4e0d\u8bba\u7ade\u529b\u3001\u6bd4\u62f3\u3001\u6597\u529b\u3001\u8d5b\u9a6c\uff0c\u4ed6\u4ece\u6765\u6ca1\u8f93\u8fc7\u7ed9\u4eba\u3002 \u3000\u3000\u674e\u6587\u79c0\u53ea\u89c9\u82cf\u666e\u7ed9\u7236\u4eb2\u6253\u5f97\u5f88\u53ef\u601c\uff0c\u82cf\u9c81\u514b\u5e26\u7740\u54ed\u58f0\u7684\u8fd9\u822c\u53eb\u558a\u4e5f\u5f88\u53ef\u601c\u3002\u201c\u4ed6\u6253\u5f97\u8fd9\u6837\u72e0\uff0c\u4e00\u5b9a\u6c38\u8fdc\u4e0d\u7231\u82cf\u666e\u4e86\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u513f\u5b50\u4e86\uff0c\u82cf\u666e\u4e5f\u6ca1\u6709\u7239\u7239\u4e86\u3002\u90fd\u662f\u6211\u4e0d\u597d\uff0c\u90fd\u662f\u6211\u8fd9\u4e2a\u771f\u4e3b\u964d\u7f5a\u7684\u6c49\u4eba\u59d1\u5a18\u4e0d\u597d\uff01\u201d\u5ffd\u7136\u4e4b\u95f4\uff0c\u5979\u4e5f\u53ef\u601c\u8d77\u81ea\u5df1\u6765\u3002 \u3000\u3000\u5979\u4e0d\u80fd\u518d\u542c\u82cf\u666e\u8fd9\u822c\u54ed\u53eb\uff0c\u4e8e\u662f\u56de\u5230\u4e86\u8ba1\u8001\u4eba\u5bb6\u4e2d\uff0c\u4ece\u88ab\u8925\u5e95\u4e0b\u62ff\u51fa\u90a3\u5f20\u72fc\u76ae\u6765\uff0c\u770b\u4e86\u5f88\u4e45\u5f88\u4e45\u3002\u5979\u548c\u82cf\u666e\u7684\u5e10\u7bf7\u76f8\u9694\u4e24\u91cc\u591a\u5730\uff0c\u4f46\u9690\u9690\u7684\u4f3c\u4e4e\u542c\u5230\u4e86\u82cf\u666e\u7684\u54ed\u58f0\uff0c\u542c\u5230\u4e86\u82cf\u9c81\u514b\u7684\u97ad\u5b50\u5728\u8f9f\u556a\u4f5c\u54cd\u3002\u5979\u867d\u7136\u5f88\u559c\u6b22\u8fd9\u5f20\u72fc\u76ae\uff0c\u4f46\u662f\u5979\u4e0d\u80fd\u8981\u3002 \u3000\u3000\u201c\u5982\u679c\u6211\u8981\u4e86\u8fd9\u5f20\u72fc\u76ae\uff0c\u82cf\u666e\u4f1a\u7ed9\u4ed6\u7239\u7239\u6253\u6b7b\u7684\u3002\u53ea\u6709\u54c8\u8428\u514b\u7684\u5973\u5b69\u5b50\uff0c\u4ed6\u4eec\u4f0a\u65af\u5170\u7684\u5973\u5b69\u5b50\u624d\u80fd\u8981\u4e86\u8fd9\u5f20\u5927\u72fc\u76ae\u3002\u54c8\u8428\u514b\u90a3\u8bb8\u591a\u5973\u5b69\u5b50\u4e2d\uff0c\u54ea\u4e00\u4e2a\u6700\u7f8e\u4e3d\uff1f\u6211\u5f88\u559c\u6b22\u8fd9\u5f20\u72fc\u76ae\uff0c\u662f\u82cf\u666e\u6253\u6b7b\u7684\u72fc\uff0c\u4ed6\u4e3a\u4e86\u6551\u6211\u624d\u4e0d\u987e\u81ea\u5df1\u6027\u547d\u53bb\u6253\u6b7b\u7684\u72fc\u3002\u82cf\u666e\u9001\u4e86\u7ed9\u6211\uff0c\u53ef\u662f\u2026\u2026\u53ef\u662f\u4ed6\u7239\u7239\u8981\u6253\u6b7b\u4ed6\u7684\u2026\u2026\u201d \u3000\u3000\u7b2c\u4e8c\u5929\u65e9\u6668\uff0c\u82cf\u9c81\u514b\u5e26\u7740\u6ee1\u5e03\u7ea2\u4e1d\u7684\u773c\u775b\u4ece\u5e10\u7bf7\u4e2d\u51fa\u6765\uff0c\u53ea\u542c\u5f97\u8f66\u5c14\u5e93\u5927\u58f0\u54fc\u7740\u5c71\u6b4c\uff0c\u54e9\u5566\u54e9\u5566\u7684\u5531\u4e86\u8fc7\u6765\u3002\u4ed6\u4fa7\u7740\u5934\u5411\u82cf\u9c81\u514b\u671b\u7740\uff0c\u8138\u4e0a\u7684\u795e\u8272\u5f88\u5947\u602a\uff0c\u7b11\u54aa\u54aa\u7684\uff0c\u773c\u4e2d\u900f\u7740\u4eb2\u5584\u7684\u610f\u601d\u3002\u8f66\u5c14\u5e93\u4e5f\u662f\u54c8\u8428\u514b\u65cf\u4e2d\u51fa\u540d\u7684\u52c7\u58eb\uff0c\u5343\u91cc\u5916\u7684\u4eba\u90fd\u77e5\u9053\u4ed6\u9a6f\u670d\u91ce\u9a6c\u7684\u672c\u9886\u3002\u4ed6\u5954\u8dd1\u8d77\u6765\u5feb\u5f97\u4e86\u4e0d\u5f97\uff0c\u6709\u4eba\u8bf4\u5728\u4e00\u91cc\u8def\u4e4b\u5185\uff0c\u4efb\u4f55\u9a8f\u9a6c\u90fd\u8ffd\u4ed6\u4e0d\u4e0a\uff0c\u5373\u4f7f\u5728\u4e00\u91cc\u8def\u4e4b\u5916\u8f93\u7ed9\u4e86\u90a3\u5339\u9a6c\uff0c\u4f46\u4e5f\u53ea\u76f8\u5dee\u4e00\u4e2a\u9f3b\u5b50\u3002\u539f\u91ce\u4e0a\u7684\u7267\u6c11\u4eec\u56f4\u7740\u706b\u5806\u95f2\u8c08\u65f6\uff0c\u8bb8\u591a\u4eba\u90fd\u8bf4\uff0c\u5982\u679c\u8f66\u5c14\u5e93\u7684\u9f3b\u5b50\u4e0d\u662f\u8fd9\u6837\u6241\u7684\u8bdd\uff0c\u90a3\u4e48\u8fd8\u662f\u4ed6\u80dc\u4e86\u3002 \u3000\u3000\u82cf\u9c81\u514b\u548c\u8f66\u5c14\u5e93\u4e4b\u95f4\u5411\u6765\u6ca1\u591a\u5927\u597d\u611f\u3002\u82cf\u9c81\u514b\u7684\u540d\u58f0\u5f88\u5927\uff0c\u5200\u6cd5\u548c\u62f3\u6cd5\u90fd\u662f\u6240\u5411\u65e0\u654c\uff0c\u8f66\u5c14\u5e93\u6697\u4e2d\u5f88\u6709\u70b9\u5992\u5fcc\u3002\u4ed6\u6bd4\u82cf\u9c81\u514b\u8981\u5c0f\u7740\u516d\u5c81\u3002\u6709\u4e00\u6b21\u4e24\u4eba\u6bd4\u8bd5\u5200\u6cd5\uff0c\u8f66\u5c14\u5e93\u8f93\u4e86\uff0c\u80a9\u5934\u4e0a\u7ed9\u5272\u7834\u957f\u957f\u4e00\u6761\u4f24\u75d5\u3002\u4ed6\u8bf4\uff1a\u201c\u4eca\u5929\u6211\u8f93\u4e86\uff0c\u4f46\u4e94\u5e74\u4e4b\u540e\uff0c\u5341\u5e74\u4e4b\u540e\uff0c\u54b1\u4eec\u518d\u8d70\u7740\u77a7\u3002\u201d\u82cf\u9c81\u514b\u9053\uff1a\u201c\u518d\u8fc7\u4e8c\u5341\u5e74\uff0c\u54b1\u54e5\u513f\u4fe9\u53c8\u6bd4\u4e00\u6b21\uff0c\u90a3\u65f6\u6211\u4e0b\u624b\u53ef\u4e0d\u4f1a\u50cf\u8fd9\u6837\u8f7b\u4e86\uff01\u201d \u3000\u3000\u4eca\u5929\uff0c\u8f66\u5c14\u5e93\u7684\u7b11\u5bb9\u4e4b\u4e2d\u5374\u4e1d\u6beb\u6ca1\u6709\u654c\u610f\u3002\u82cf\u9c81\u514b\u5fc3\u5934\u7684\u6c14\u607c\u8fd8\u6ca1\u6709\u6d88\uff0c\u72e0\u72e0\u7684\u77aa\u4e86\u4ed6\u4e00\u773c\u3002\u8f66\u5c14\u5e93\u7b11\u9053\uff1a\u201c\u8001\u82cf\uff0c\u4f60\u7684\u513f\u5b50\u5f88\u6709\u773c\u5149\u554a\uff01\u201d\u82cf\u9c81\u514b\u9053\uff1a\u201c\u4f60\u8bf4\u82cf\u666e\u4e48\uff1f\u201d\u4ed6\u4f38\u624b\u6309\u4f4f\u5200\u67c4\uff0c\u773c\u4e2d\u53d1\u51fa\u51f6\u72e0\u7684\u795e\u8272\u6765\uff0c\u5fc3\u60f3\uff1a\u201c\u4f60\u5632\u7b11\u6211\u513f\u5b50\u5c06\u72fc\u76ae\u9001\u7ed9\u4e86\u6c49\u4eba\u59d1\u5a18\u3002\u201d \u3000\u3000\u8f66\u5c14\u5e93\u4e00\u53e5\u8bdd\u5df2\u51b2\u5230\u4e86\u53e3\u8fb9\uff1a\u201c\u5018\u82e5\u4e0d\u662f\u82cf\u666e\uff0c\u96be\u9053\u4f60\u53e6\u5916\u8fd8\u6709\u513f\u5b50\uff1f\u201d\u4f46\u8fd9\u53e5\u8bdd\u5374\u6ca1\u8bf4\u51fa\u53e3\uff0c\u4ed6\u53ea\u5fae\u7b11\u7740\u9053\uff1a\u201c\u81ea\u7136\u662f\u82cf\u666e\uff01\u8fd9\u5b69\u5b50\u76f8\u8c8c\u4e0d\u5dee\uff0c\u4eba\u4e5f\u633a\u80fd\u5e72\uff0c\u6211\u5f88\u559c\u6b22\u4ed6\u3002\u201d\u505a\u7236\u4eb2\u7684\u542c\u5230\u65c1\u4eba\u79f0\u8d5e\u4ed6\u513f\u5b50\uff0c\u81ea\u7136\u5fcd\u4e0d\u4f4f\u9ad8\u5174\uff0c\u4f46\u4ed6\u548c\u8f66\u5c14\u5e93\u4e00\u5411\u53e3\u89d2\u60ef\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u773c\u70ed\u5427\uff1f\u5c31\u53ef\u60dc\u4f60\u751f\u4e0d\u51fa\u4e00\u4e2a\u513f\u5b50\u3002\u201d \u3000\u3000\u8f66\u5c14\u5e93\u5374\u4e0d\u751f\u6c14\uff0c\u7b11\u9053\uff1a\u201c\u6211\u5973\u513f\u963f\u66fc\u4e5f\u4e0d\u9519\uff0c\u5426\u5219\u4f60\u513f\u5b50\u600e\u4e48\u4f1a\u770b\u4e0a\u4e86\u5979\uff1f\u201d \u3000\u3000\u82cf\u9c81\u514b\u201c\u5478\u201d\u7684\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u522b\u81ed\u7f8e\u5566\uff0c\u8c01\u8bf4\u6211\u513f\u5b50\u770b\u4e0a\u4e86\u963f\u66fc\uff1f\u201d\u8f66\u5c14\u5e93\u4f38\u624b\u633d\u4f4f\u4e86\u4ed6\u8180\u5b50\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u8ddf\u6211\u6765\uff0c\u6211\u7ed9\u4f60\u77a7\u4e00\u4ef6\u4e1c\u897f\u3002\u201d\u82cf\u9c81\u514b\u5fc3\u4e2d\u5947\u602a\uff0c\u4fbf\u8ddf\u4ed6\u5e76\u80a9\u8d70\u7740\u3002\u8f66\u5c14\u5e93\u9053\uff1a\u201c\u4f60\u513f\u5b50\u524d\u4e9b\u65f6\u5019\u6740\u6b7b\u4e86\u4e00\u5934\u5927\u7070\u72fc\u3002\u5c0f\u5c0f\u5b69\u5b50\uff0c\u771f\u662f\u4e86\u4e0d\u8d77\uff0c\u5c06\u6765\u5927\u8d77\u6765\uff0c\u53ef\u4e0d\u8ddf\u8001\u5b50\u4e00\u6837\uff1f\u7236\u662f\u82f1\u96c4\u513f\u597d\u6c49\u3002\u201d \u3000\u3000\u82cf\u9c81\u514b\u4e0d\u7b54\u8154\uff0c\u8ba4\u5b9a\u4ed6\u662f\u6446\u4e0b\u4e86\u4ec0\u4e48\u5708\u5957\uff0c\u8981\u81ea\u5df1\u4e0a\u5f53\uff0c\u5fc3\u60f3\uff1a\u201c\u4e00\u5207\u987b\u5f97\u5c0f\u5fc3\u5728\u610f\u3002\u201d \u3000\u3000\u5728\u8349\u539f\u4e0a\u8d70\u4e86\u4e09\u91cc\u591a\u8def\uff0c\u5230\u4e86\u8f66\u5c14\u5e93\u7684\u5e10\u7bf7\u524d\u9762\u3002\u82cf\u9c81\u514b\u8fdc\u8fdc\u4fbf\u77a7\u89c1\u4e00\u5f20\u5927\u72fc\u76ae\u6302\u5728\u5e10\u7bf7\u5916\u8fb9\u3002\u4ed6\u5954\u8fd1\u51e0\u6b65\uff0c\u563f\uff0c\u53ef\u4e0d\u662f\u82cf\u666e\u6253\u6b7b\u7684\u90a3\u5934\u7070\u72fc\u7684\u76ae\u662f\u4ec0\u4e48\uff1f\u8fd9\u662f\u513f\u5b50\u751f\u5e73\u6253\u6b7b\u7684\u7b2c\u4e00\u5934\u91ce\u517d\uff0c\u4ed6\u662f\u8ba4\u5f97\u6e05\u6e05\u695a\u695a\u7684\u3002\u4ed6\u5fc3\u4e0b\u4e00\u9635\u6df7\u4e71\uff0c\u968f\u5373\u53c8\u662f\u9ad8\u5174\uff0c\u53c8\u662f\u8ff7\u60d8\uff1a\u201c\u6211\u9519\u602a\u4e86\u963f\u666e\uff0c\u6628\u665a\u8fd9\u4e48\u7ed3\u7ed3\u5b9e\u5b9e\u7684\u6253\u4e86\u4ed6\u4e00\u987f\uff0c\u539f\u6765\u4ed6\u628a\u72fc\u76ae\u9001\u4e86\u7ed9\u963f\u66fc\uff0c\u5374\u4e0d\u662f\u7ed9\u90a3\u6c49\u4eba\u59d1\u5a18\u3002\u8be5\u6b7b\u7684\uff0c\u600e\u4e48\u4ed6\u4e0d\u8bf4\u5462\uff1f\u5b69\u5b50\u8138\u5ae9\uff0c\u6ca1\u5f97\u8bf4\u7684\u3002\u8981\u662f\u4ed6\u5988\u5988\u5728\u4e16\uff0c\u5979\u5c31\u4f1a\u529d\u6211\u4e86\u3002\u5509\uff0c\u5b69\u5b50\u6709\u4ec0\u4e48\u5fc3\u4e8b\uff0c\u5bf9\u5988\u5988\u4e00\u5b9a\u80af\u8bb2\u2026\u2026\u201d \u3000\u3000\u8f66\u5c14\u5e93\u7c97\u5927\u7684\u624b\u638c\u5728\u4ed6\u80a9\u4e0a\u4e00\u62cd\uff0c\u8bf4\u9053\uff1a\u201c\u559d\u7897\u9152\u53bb\u3002\u201d \u3000\u3000\u8f66\u5c14\u5e93\u7684\u5e10\u7bf7\u4e2d\u6536\u62fe\u5f97\u5f88\u6574\u6d01\uff0c\u4e00\u5f20\u5f20\u7ec7\u7740\u7ea2\u82b1\u7eff\u8349\u7684\u7f8a\u6bdb\u6bef\u6302\u5728\u56db\u5468\u3002\u4e00\u4e2a\u8eab\u6750\u82d7\u6761\u7684\u5973\u5b69\u5b50\u6367\u4e86\u9152\u6d46\u51fa\u6765\u3002\u8f66\u5c14\u5e93\u5fae\u7b11\u9053\uff1a\u201c\u963f\u66fc\uff0c\u8fd9\u662f\u82cf\u666e\u7684\u7239\u3002\u4f60\u6015\u4e0d\u6015\u4ed6\uff1f\u8fd9\u5927\u80e1\u5b50\u53ef\u51f6\u5f97\u5f88\u5462\uff01\u201d\u963f\u66fc\u7f9e\u7ea2\u4e86\u7684\u8138\u663e\u5f97\u66f4\u7f8e\u4e86\uff0c\u773c\u5149\u4e2d\u95ea\u70c1\u7740\u7b11\u610f\uff0c\u597d\u50cf\u662f\u8bf4\uff1a\u201c\u6211\u4e0d\u6015\u3002\u201d\u82cf\u9c81\u514b\u5475\u5475\u7b11\u4e86\u8d77\u6765\uff0c\u7b11\u9053\uff1a\u201c\u8001\u8f66\uff0c\u6211\u542c\u4eba\u5bb6\u8bf4\u8fc7\u7684\uff0c\u8bf4\u4f60\u6709\u4e2a\u5973\u513f\uff0c\u662f\u8349\u539f\u4e0a\u4e00\u6735\u4f1a\u8d70\u8def\u7684\u82b1\u3002\u4e0d\u9519\uff0c\u4e00\u6735\u4f1a\u8d70\u8def\u7684\u82b1\uff0c\u8fd9\u8bdd\u8bf4\u5f97\u771f\u597d\u3002\u201d \u3000\u3000\u4e24\u4e2a\u4e89\u95f9\u4e86\u5341\u591a\u5e74\u7684\u6c49\u5b50\uff0c\u7a81\u7136\u95f4\u4eb2\u5bc6\u8d77\u6765\u4e86\u3002\u4f60\u656c\u6211\u4e00\u7897\u9152\uff0c\u6211\u656c\u4f60\u4e00\u7897\u9152\u3002\u82cf\u9c81\u514b\u7ec8\u4e8e\u559d\u5f97\u9169\u914a\u5927\u9189\uff0c\u772f\u7740\u773c\u4f0f\u5728\u9a6c\u80cc\uff0c\u56de\u5230\u5bb6\u4e2d\u3002 \u3000\u3000\u8fc7\u4e86\u4e9b\u65e5\u5b50\uff0c\u8f66\u5c14\u5e93\u9001\u6765\u4e86\u4e24\u5f20\u7cbe\u81f4\u7684\u7f8a\u6bdb\u6bef\u5b50\u3002\u4ed6\u8bf4\uff1a \u3000\u3000\u201c\u8fd9\u662f\u963f\u66fc\u7ec7\u7684\uff0c\u4e00\u5f20\u7ed9\u8001\u7684\uff0c\u4e00\u5f20\u7ed9\u5c0f\u7684\u3002\u201d \u3000\u3000\u4e00\u5f20\u6bdb\u6bef\u4e0a\u7ec7\u7740\u4e00\u4e2a\u5927\u6c49\uff0c\u624b\u6301\u957f\u5200\uff0c\u780d\u7ffb\u4e86\u4e00\u5934\u8c79\u5b50\uff0c\u8fdc\u5904\u4e00\u5934\u8c79\u5b50\u6b63\u631f\u7740\u5c3e\u5df4\u9003\u8d70\u3002\u53e6\u4e00\u5f20\u6bdb\u6bef\u4e0a\u7ec7\u7740\u4e00\u4e2a\u7537\u5b69\uff0c\u523a\u6b7b\u4e86\u4e00\u5934\u5927\u7070\u72fc\u3002\u90a3\u4e8c\u4eba\u4e00\u5927\u4e00\u5c0f\uff0c\u90fd\u662f\u5a01\u98ce\u51db\u51db\uff0c\u82f1\u59ff\u98d2\u723d\u3002\u82cf\u9c81\u514b\u4e00\u89c1\u5927\u559c\uff0c\u8fde\u8d5e\uff1a\u201c\u597d\u624b\u827a\uff0c\u597d\u624b\u827a\uff01\u201d\u539f\u6765\u56de\u7586\u4e4b\u5730\u672c\u6765\u6781\u5c11\u8c79\u5b50\uff0c\u90a3\u4e00\u5e74\u5374\u4e0d\u77e5\u4ece\u54ea\u91cc\u6765\u4e86\u4e24\u5934\uff0c\u4e3a\u5bb3\u4eba\u755c\u3002\u82cf\u9c81\u514b\u5f53\u5e74\u594b\u52c7\u8ffd\u5165\u96ea\u5c71\uff0c\u780d\u6b7b\u4e86\u4e00\u5934\u5927\u8c79\uff0c\u53e6\u4e00\u5934\u8d1f\u4f24\u8fdc\u9041\u3002\u8fd9\u65f6\u89c1\u963f\u66fc\u5728\u6bdb\u6bef\u4e0a\u7ec7\u4e86\u4ed6\u751f\u5e73\u6700\u5f97\u610f\u7684\u82f1\u52c7\u4e8b\u8ff9\uff0c\u81ea\u662f\u5927\u4e3a\u9ad8\u5174\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\uff0c\u559d\u5f97\u5927\u9189\u800c\u4f0f\u5728\u9a6c\u80cc\u4e0a\u56de\u5bb6\u53bb\u7684\uff0c\u5374\u662f\u8f66\u5c14\u5e93\u4e86\u3002\u82cf\u9c81\u514b\u53eb\u513f\u5b50\u9001\u4ed6\u56de\u53bb\u3002\u5728\u8f66\u5c14\u5e93\u7684\u5e10\u7bf7\u4e4b\u4e2d\uff0c\u82cf\u666e\u89c1\u5230\u4e86\u81ea\u5df1\u7684\u72fc\u76ae\u3002\u4ed6\u6b63\u5728\u5927\u60d1\u4e0d\u89e3\uff0c\u963f\u66fc\u5df2\u7ea2\u7740\u8138\u5728\u5411\u4ed6\u9053\u8c22\u3002\u82cf\u666e\u5583\u5583\u7684\u8bf4\u4e86\u51e0\u53e5\u8bdd\uff0c\u5168\u7136\u4e0d\u77e5\u6240\u4e91\uff0c\u4ed6\u4e0d\u6562\u8ffd\u95ee\u4e3a\u4ec0\u4e48\u8fd9\u5f20\u72fc\u76ae\u7adf\u4f1a\u5230\u4e86\u963f\u66fc\u624b\u4e2d\u3002\u7b2c\u4e8c\u5929\uff0c\u4ed6\u4e00\u65e9\u4fbf\u5230\u90a3\u4e2a\u6740\u72fc\u7684\u5c0f\u4e18\u53bb\uff0c\u76fc\u671b\u89c1\u5230\u674e\u6587\u79c0\u95ee\u5979\u4e00\u95ee\u3002\u53ef\u662f\u674e\u6587\u79c0\u5e76\u6ca1\u6709\u6765\u3002 \u3000\u3000\u4ed6\u7b49\u4e86\u4e24\u5929\uff0c\u90fd\u662f\u4e00\u573a\u7a7a\u3002\u5230\u7b2c\u4e09\u5929\u4e0a\uff0c\u7ec8\u4e8e\u9f13\u8d77\u4e86\u52c7\u6c14\u8d70\u5230\u8ba1\u8001\u4eba\u5bb6\u4e2d\u3002\u674e\u6587\u79c0\u51fa\u6765\u5f00\u95e8\uff0c\u4e00\u89c1\u662f\u4ed6\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4ece\u6b64\u4e0d\u8981\u89c1\u4f60\u3002\u201d\u556a\u7684\u4e00\u58f0\uff0c\u4fbf\u628a\u677f\u95e8\u5173\u4e0a\u4e86\u3002\u82cf\u666e\u5446\u4e86\u534a\u664c\uff0c\u83ab\u540d\u5176\u5999\u7684\u56de\u5230\u81ea\u5df1\u5bb6\u91cc\uff0c\u5fc3\u91cc\u611f\u5230\u4e00\u9635\u6005\u60d8\uff1a\u201c\u5509\uff0c\u6c49\u4eba\u7684\u59d1\u5a18\uff0c\u4e0d\u77e5\u5979\u5fc3\u91cc\u5728\u60f3\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u81ea\u7136\u4e0d\u4f1a\u77e5\u9053\uff0c\u674e\u6587\u79c0\u662f\u8eb2\u5728\u677f\u95e8\u4e4b\u540e\u63a9\u9762\u54ed\u6ce3\u3002\u6b64\u540e\u4e00\u76f4\u54ed\u4e86\u5f88\u4e45\u5f88\u4e45\u3002\u5979\u5f88\u559c\u6b22\u518d\u548c\u82cf\u666e\u5728\u4e00\u8d77\u73a9\uff0c\u8bf4\u6545\u4e8b\u7ed9\u4ed6\u542c\uff0c\u53ef\u662f\u5979\u77e5\u9053\u53ea\u8981\u7ed9\u4ed6\u7236\u4eb2\u53d1\u89c9\u4e86\uff0c\u4ed6\u53c8\u5f97\u72e0\u72e0\u6328\u4e00\u987f\u97ad\u5b50\uff0c\u8bf4\u4e0d\u5b9a\u4f1a\u7ed9\u4ed6\u7236\u4eb2\u6253\u6b7b\u7684\u3002 \u3000"}),showOnHomepage:!0},{name:"\u4e09",content:(0,a.jsx)(a.Fragment,{children:"\u65f6\u65e5\u4e00\u5929\u4e00\u5929\u7684\u8fc7\u53bb\uff0c\u4e09\u4e2a\u5b69\u5b50\u7ed9\u8349\u539f\u4e0a\u7684\u98ce\u5439\u5f97\u9ad8\u4e86\uff0c\u7ed9\u5929\u5c71\u811a\u4e0b\u7684\u51b0\u96ea\u51bb\u5f97\u957f\u5927\u4e86\uff0c\u4f1a\u8d70\u8def\u7684\u82b1\u66f4\u52a0\u8885\u5a1c\u7f8e\u4e3d\uff0c\u6740\u72fc\u7684\u5c0f\u5b69\u53d8\u6210\u4e86\u82f1\u4fca\u7684\u9752\u5e74\uff0c\u90a3\u8349\u539f\u4e0a\u7684\u5929\u94c3\u9e1f\u5462\uff0c\u4e5f\u662f\u5531\u5f97\u66f4\u52a0\u5a07\u67d4\u52a8\u542c\u4e86\u3002\u53ea\u662f\u5979\u5531\u5f97\u5f88\u5c11\uff0c\u53ea\u6709\u5728\u534a\u591c\u65e0\u4eba\u7684\u65f6\u5019\uff0c\u72ec\u81ea\u5728\u82cf\u666e\u6740\u8fc7\u7070\u72fc\u7684\u5c0f\u4e18\u4e0a\u5531\u4e00\u652f\u6b4c\u513f\u3002\u5979\u6ca1\u4e00\u5929\u5fd8\u8bb0\u8fc7\u8fd9\u4e2a\u513f\u65f6\u7684\u6e38\u4f34\uff0c\u5e38\u5e38\u671b\u5230\u4ed6\u548c\u963f\u66fc\u5e76\u9a91\u51fa\u6e38\uff0c\u6709\u65f6\uff0c\u4e5f\u542c\u5230\u4ed6\u4fe9\u4e92\u76f8\u5bf9\u7b54\uff0c\u5531\u7740\u60c5\u81f4\u7f20\u7ef5\u7684\u6b4c\u513f\u3002 \u3000\u3000\u8fd9\u4e9b\u6b4c\u4e2d\u7684\u542b\u610f\uff0c\u674e\u6587\u79c0\u5c0f\u65f6\u5019\u5e76\u4e0d\u61c2\u5f97\uff0c\u8fd9\u65f6\u5019\u5374\u5acc\u61c2\u5f97\u592a\u591a\u4e86\u3002\u5982\u679c\u5979\u4ecd\u65e7\u4e0d\u61c2\uff0c\u5c82\u4e0d\u662f\u5c11\u4e86\u8bb8\u591a\u4f24\u5fc3\uff1f\u5c11\u4e86\u8bb8\u591a\u4e0d\u7720\u7684\u957f\u591c\uff1f\u53ef\u662f\u4e0d\u660e\u767d\u7684\u4e8b\u60c5\uff0c\u4e00\u65e6\u660e\u767d\u4e4b\u540e\uff0c\u6c38\u8fdc\u4e0d\u80fd\u518d\u56de\u5230\u4ece\u524d\u5e7c\u5c0f\u65f6\u90a3\u6837\u8ff7\u60d8\u7684\u5fc3\u5883\u4e86\u3002 \u3000\u3000\u662f\u4e00\u4e2a\u6625\u6df1\u7684\u665a\u4e0a\uff0c\u674e\u6587\u79c0\u9a91\u4e86\u767d\u9a6c\uff0c\u72ec\u81ea\u5230\u90a3\u4e2a\u6740\u72fc\u7684\u5c0f\u5c71\u4e0a\u53bb\u3002\u767d\u9a6c\u7ed9\u67d3\u9ec4\u4e86\u7684\u6bdb\u65e9\u5df2\u8131\u5c3d\uff0c\u5168\u8eab\u53c8\u662f\u50cf\u5929\u5c71\u9876\u4e0a\u7684\u96ea\u90a3\u6837\u767d\u3002 \u3000\u3000\u5979\u7acb\u5728\u90a3\u4e2a\u5c0f\u5c71\u4e18\u4e0a\uff0c\u8fdc\u8fdc\u671b\u89c1\u54c8\u8428\u514b\u4eba\u7684\u5e10\u7bf7\u4e4b\u95f4\u70e7\u7740\u4e00\u5806\u5927\u706b\uff0c\u97f3\u4e50\u548c\u6b22\u95f9\u7684\u58f0\u97f3\u4e00\u9635\u9ad8\uff0c\u4e00\u9635\u4f4e\u7684\u4f20\u6765\u3002\u539f\u6765\u8fd9\u5929\u662f\u54c8\u8428\u514b\u4eba\u7684\u4e00\u4e2a\u8282\u65e5\uff0c\u9752\u5e74\u7537\u5973\u805a\u5728\u706b\u5806\u4e4b\u65c1\uff0c\u8df3\u821e\u5531\u6b4c\uff0c\u6781\u5c3d\u6b22\u4e50\u3002 \u3000\u3000\u674e\u6587\u79c0\u5fc3\u60f3\uff1a\u201c\u4ed6\u548c\u5979\u4eca\u5929\u4e00\u5b9a\u7279\u522b\u5feb\u4e50\uff0c\u8fd9\u4e48\u70ed\u95f9\uff0c\u8fd9\u4e48\u6b22\u559c\u3002\u201d\u5979\u5fc3\u4e2d\u7684\u201c\u4ed6\u201d\uff0c\u6ca1\u6709\u7b2c\u4e8c\u4e2a\u4eba\uff0c\u81ea\u7136\u662f\u82cf\u666e\uff0c\u90a3\u4e2a\u201c\u5979\u201d\u81ea\u7136\u662f\u90a3\u6735\u4f1a\u8d70\u8def\u7684\u82b1\uff0c\u963f\u66fc\u3002 \u3000\u3000\u4f46\u8fd9\u4e00\u6b21\u674e\u6587\u79c0\u5374\u6ca1\u731c\u5bf9\uff0c\u82cf\u666e\u548c\u963f\u66fc\u8fd9\u65f6\u5019\u5e76\u4e0d\u7279\u522b\u5feb\u4e50\uff0c\u5374\u662f\u5728\u7279\u522b\u7684\u7d27\u5f20\u3002\u5728\u706b\u5806\u4e4b\u65c1\uff0c\u82cf\u666e\u6b63\u5728\u548c\u4e00\u4e2a\u7626\u957f\u7684\u9752\u5e74\u6454\u4ea4\u3002\u8fd9\u662f\u8282\u65e5\u4e2d\u6700\u91cd\u8981\u7684\u4e00\u4e2a\u9879\u76ee\uff0c\u6454\u4ea4\u7b2c\u4e00\u7684\u6709\u4e09\u4ef6\u5956\u54c1\uff1a\u4e00\u5339\u9a8f\u9a6c\uff0c\u4e00\u5934\u80a5\u7f8a\uff0c\u8fd8\u6709\u4e00\u5f20\u7f8e\u4e3d\u7684\u6bdb\u6bef\u3002 \u3000\u3000\u82cf\u666e\u5df2\u63a5\u8fde\u80dc\u4e86\u56db\u4e2a\u597d\u6c49\uff0c\u90a3\u4e2a\u7626\u957f\u7684\u9752\u5e74\u53eb\u505a\u6851\u65af\u513f\u3002 \u3000\u3000\u4ed6\u662f\u82cf\u666e\u7684\u597d\u670b\u53cb\uff0c\u53ef\u4e5f\u8981\u5206\u4e00\u4e2a\u80dc\u8d25\u3002\u4f55\u51b5\uff0c\u4ed6\u5fc3\u4e2d\u4e00\u76f4\u5728\u7231\u7740\u90a3\u6735\u4f1a\u8d70\u8def\u7684\u82b1\u3002\u8fd9\u6837\u7f8e\u4e3d\u7684\u8138\uff0c\u8fd9\u6837\u5a40\u5a1c\u7684\u8eab\u6750\uff0c\u8fd9\u6837\u5de7\u5999\u7684\u624b\u827a\uff0c\u8c01\u4e0d\u7231\u5462\uff1f\u6851\u65af\u513f\u660e\u77e5\u82cf\u666e\u548c\u963f\u66fc\u4ece\u5c0f\u4fbf\u5f88\u8981\u597d\uff0c\u4f46\u4ed6\u662f\u5014\u5f3a\u7684\u9ad8\u50b2\u7684\u9752\u5e74\u3002\u8349\u539f\u4e0a\u8c01\u7684\u9a6c\u5feb\uff0c\u8c01\u7684\u529b\u5927\uff0c\u8c01\u4fbf\u5904\u5904\u5360\u4e86\u4e0a\u98ce\u3002\u4ed6\u5fc3\u4e2d\u65e9\u4fbf\u5728\u8fd9\u6837\u60f3\uff1a\u201c\u53ea\u8981\u6211\u5728\u516c\u5f00\u7684\u89d2\u529b\u4e2d\u6253\u8d25\u4e86\u82cf\u666e\uff0c\u963f\u66fc\u4fbf\u4f1a\u559c\u6b22\u6211\u7684\u3002\u201d\u4ed6\u5df2\u7528\u5fc3\u7684\u7ec3\u4e86\u4e09\u5e74\u6454\u4ea4\u548c\u5200\u6cd5\u3002\u4ed6\u7684\u5e08\u7236\uff0c\u4fbf\u662f\u963f\u66fc\u7684\u7236\u4eb2\u8f66\u5c14\u5e93\u3002 \u3000\u3000\u81f3\u4e8e\u82cf\u666e\u7684\u6b66\u529f\uff0c\u5374\u662f\u7236\u4eb2\u4eb2\u4f20\u7684\u3002 \u3000\u3000\u4e24\u4e2a\u9752\u5e74\u626d\u7ed3\u5728\u4e00\u8d77\u3002\u7a81\u7136\u95f4\u6851\u65af\u513f\u80a9\u5934\u4e2d\u4e86\u91cd\u91cd\u7684\u4e00\u62f3\uff0c\u4ed6\u811a\u4e0b\u4e00\u4e2a\u8e09\u8dc4\uff0c\u5411\u540e\u4fbf\u5012\uff0c\u4f46\u4ed6\u5728\u5012\u4e0b\u65f6\u53f3\u8db3\u4e00\u52fe\uff0c\u82cf\u666e\u4e5f\u5012\u4e0b\u4e86\u3002\u4e24\u4eba\u4e00\u540c\u8dc3\u8d77\u8eab\u6765\uff0c\u4e24\u5bf9\u773c\u775b\u4e92\u76f8\u51dd\u89c6\uff0c\u8eab\u5b50\u5de6\u53f3\u76d8\u65cb\uff0c\u627e\u5bfb\u5bf9\u65b9\u7684\u7834\u7efd\uff0c\u8c01\u4e5f\u4e0d\u6562\u5148\u51fa\u624b\u3002 \u3000\u3000\u82cf\u9c81\u514b\u5750\u5728\u4e00\u65c1\u77a7\u7740\uff0c\u624b\u5fc3\u4e2d\u5168\u662f\u6c57\u6c34\uff0c\u53ea\u662f\u53eb\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u53ef\u60dc\uff01\u201d\u8f66\u5c14\u5e93\u7684\u5fc3\u60c5\u5374\u5f88\u96be\u8bf4\u5f97\u660e\u767d\u3002\u4ed6\u77e5\u9053\u5973\u513f\u7684\u5fc3\u610f\uff0c\u4fbf\u662f\u6851\u65af\u513f\u6253\u80dc\u4e86\uff0c\u963f\u66fc\u559c\u6b22\u7684\u8fd8\u662f\u82cf\u666e\uff0c\u8bf4\u4e0d\u5b9a\u53ea\u6709\u66f4\u52a0\u559c\u6b22\u5f97\u66f4\u5389\u5bb3\u4e9b\u3002\u53ef\u662f\u6851\u65af\u513f\u662f\u4ed6\u7684\u5f92\u5f1f\uff0c\u8fd9\u4e00\u573a\u89d2\u529b\uff0c\u5c31\u5982\u662f\u4ed6\u81ea\u5df1\u548c\u201c\u54c8\u8428\u514b\u7b2c\u4e00\u52c7\u58eb\u201d\u82cf\u9c81\u514b\u7684\u6bd4\u8d5b\u3002\u8f66\u5c14\u5e93\u7684\u5f92\u5f1f\u5982\u679c\u6253\u8d25\u4e86\u82cf\u9c81\u514b\u7684\u513f\u5b50\uff0c\u90a3\u53ef\u6709\u591a\u5149\u5f69\uff01\u8fd9\u4ef6\u4e8b\u4f1a\u4f20\u904d\u6570\u5343\u91cc\u7684\u8349\u539f\u3002\u5f53\u7136\uff0c\u963f\u66fc\u5c06\u4f1a\u5f88\u4e45\u5f88\u4e45\u7684\u90c1\u90c1\u4e0d\u4e50\uff0c\u53ef\u662f\u8fd9\u4e9b\u4e8b\u4e0d\u53bb\u7ba1\u5b83\u3002\u4ed6\u8fd8\u662f\u76fc\u671b\u6851\u65af\u513f\u6253\u80dc\u3002\u867d\u7136\u82cf\u666e\u662f\u4e2a\u597d\u5b69\u5b50\uff0c\u4ed6\u4e00\u76f4\u5f88\u559c\u6b22\u4ed6\u3002 \u3000\u3000\u56f4\u7740\u706b\u5806\u7684\u4eba\u4eec\u4e3a\u4e24\u4e2a\u9752\u5e74\u5450\u558a\u52a9\u5a01\u3002\u8fd9\u662f\u4e00\u573a\u52bf\u5747\u529b\u654c\u7684\u89d2\u6597\u3002\u82cf\u666e\u8eab\u58ee\u529b\u5927\uff0c\u6851\u65af\u513f\u5374\u66f4\u52a0\u7075\u6d3b\u4e9b\uff0c\u5230\u5e95\u8c01\u4f1a\u6700\u540e\u83b7\u80dc\uff0c\u8c01\u4e5f\u8bf4\u4e0d\u4e0a\u6765\u3002 \u3000\u3000\u53ea\u89c1\u6851\u65af\u513f\u4e1c\u4e00\u95ea\uff0c\u897f\u4e00\u907f\uff0c\u82cf\u666e\u6570\u6b21\u4f38\u624b\u626d\u4ed6\uff0c\u90fd\u7ed9\u8eb2\u5f00\u4e86\u3002\u9752\u5e74\u7537\u5973\u4eec\u5450\u558a\u52a9\u5a01\u7684\u58f0\u97f3\u8d8a\u6765\u8d8a\u54cd\u3002\u201c\u82cf\u666e\uff0c\u5feb\u4e9b\uff0c\u5feb\u4e9b\uff01\u201d\u201c\u6851\u65af\u513f\uff0c\u53cd\u653b\u554a\uff01\u522b\u5c3d\u9003\u6765\u9003\u53bb\u7684\u3002\u201d\u201c\u554a\u54df\uff0c\u82cf\u666e\u6454\u4e86\u4e00\u4ea4\uff01\u201d\u201c\u4e0d\u8981\u7d27\uff0c\u7528\u529b\u6273\u5012\u4ed6\u3002\u201d \u3000\u3000\u58f0\u97f3\u8fdc\u8fdc\u4f20\u4e86\u51fa\u53bb\uff0c\u674e\u6587\u79c0\u9690\u9690\u542c\u5230\u4e86\u5927\u5bb6\u53eb\u7740\u201c\u82cf\u666e\uff0c\u82cf\u666e\u201d\u3002\u5979\u6709\u4e9b\u5947\u602a\uff1a\u201c\u4e3a\u4ec0\u4e48\u5927\u5bb6\u53eb\u82cf\u666e\uff1f\u201d\u4e8e\u662f\u9a91\u4e86\u767d\u9a6c\uff0c\u5411\u7740\u547c\u53eb\u7684\u58f0\u97f3\u5954\u53bb\u3002\u5728\u4e00\u68f5\u5927\u6811\u7684\u540e\u9762\uff0c\u5979\u770b\u5230\u82cf\u666e\u6b63\u5728\u548c\u6851\u65af\u513f\u640f\u6597\uff0c\u65c1\u89c2\u7684\u4eba\u5174\u9ad8\u91c7\u70c8\u5730\u53eb\u56b7\u7740\u3002\u7a81\u7136\u95f4\uff0c\u5979\u5728\u706b\u5149\u65c1\u770b\u5230\u4e86\u963f\u66fc\u7684\u8138\uff0c\u8138\u4e0a\u95ea\u52a8\u7740\u5173\u5207\u548c\u5174\u594b\uff0c\u6cea\u5149\u83b9\u83b9\uff0c\u4e00\u4f1a\u513f\u62c5\u5fe7\uff0c\u4e00\u4f1a\u513f\u6b22\u559c\u3002\u674e\u6587\u79c0\u4ece\u6765\u6ca1\u8fd9\u6837\u6e05\u695a\u7684\u770b\u8fc7\u963f\u66fc\uff0c\u5fc3\u60f3\uff1a\u201c\u539f\u6765\u5979\u662f\u8fd9\u6837\u7684\u559c\u6b22\u82cf\u666e\u3002\u201d \u3000\u3000\u84e6\u5730\u91cc\u4f17\u4eba\u4e00\u58f0\u5927\u53eb\uff0c\u82cf\u666e\u548c\u6851\u65af\u513f\u4e00\u9f50\u5012\u4e86\u4e0b\u53bb\u3002\u9694\u7740\u4eba\u5899\uff0c\u674e\u6587\u79c0\u770b\u4e0d\u5230\u5730\u4e0b\u4e24\u4e2a\u4eba\u640f\u6597\u7684\u60c5\u5f62\u3002\u4f46\u542c\u7740\u4f17\u4eba\u7684\u53eb\u58f0\uff0c\u53ef\u4ee5\u60f3\u5230\u4e00\u65f6\u662f\u82cf\u666e\u7ffb\u5230\u4e86\u4e0a\u9762\uff0c\u4e00\u65f6\u53c8\u662f\u7ed9\u6851\u65af\u513f\u538b\u4e86\u4e0b\u53bb\u3002\u674e\u6587\u79c0\u624b\u4e2d\u4e5f\u662f\u6c57\u6c34\uff0c\u56e0\u4e3a\u77a7\u4e0d\u89c1\u5730\u4e0b\u7684\u4e24\u4eba\uff0c\u5979\u53ea\u6709\u66f4\u52a0\u7126\u6025\u4e9b\u3002\u5ffd\u7136\u95f4\uff0c\u4f17\u4eba\u7684\u547c\u58f0\u5168\u90e8\u6b62\u6b47\uff0c\u674e\u6587\u79c0\u6e05\u6e05\u695a\u695a\u542c\u5230\u76f8\u6597\u4e24\u4eba\u7c97\u91cd\u7684\u547c\u5438\u58f0\u3002\u53ea\u89c1\u4e00\u4e2a\u4eba\u6447\u6447\u6643\u6643\u7684\u7ad9\u4e86\u8d77\u6765\u3002\u4f17\u4eba\u6b22\u58f0\u547c\u53eb\uff1a\u201c\u82cf\u666e\uff0c\u82cf\u666e\uff01\u201d \u3000\u3000\u963f\u66fc\u51b2\u8fdb\u4eba\u5708\u4e4b\u4e2d\uff0c\u62c9\u4f4f\u4e86\u82cf\u666e\u7684\u624b\u3002 \u3000\u3000\u674e\u6587\u79c0\u89c9\u5f97\u53c8\u662f\u9ad8\u5174\uff0c\u53c8\u662f\u51c4\u51c9\u3002\u5979\u5708\u8f6c\u9a6c\u5934\uff0c\u6162\u6162\u7684\u8d70\u4e86\u5f00\u53bb\u3002\u4f17\u4eba\u56f4\u7740\u82cf\u666e\uff0c\u8c01\u4e5f\u6ca1\u6ce8\u610f\u5230\u5979\u3002 \u3000\u3000\u5979\u4e0d\u518d\u62c9\u7f30\u7ef3\uff0c\u4efb\u7531\u767d\u9a6c\u5728\u6c99\u6f20\u4e2d\u6f2b\u6b65\u800c\u884c\u3002\u4e5f\u4e0d\u77e5\u8d70\u4e86\u591a\u5c11\u65f6\u5019\uff0c\u5979\u84e6\u5730\u53d1\u89c9\uff0c\u767d\u9a6c\u5df2\u662f\u8d70\u5230\u4e86\u8349\u539f\u7684\u8fb9\u7f18\uff0c\u518d\u8fc7\u53bb\u4fbf\u662f\u6208\u58c1\u6c99\u6f20\u4e86\u3002\u5979\u4f4e\u58f0\u65a5\u9053\uff1a\u201c\u4f60\u5e26\u6211\u5230\u8fd9\u91cc\u6765\u5e72\u4e48\uff1f\u201d \u3000\u3000\u4fbf\u5728\u8fd9\u65f6\uff0c\u6c99\u6f20\u4e0a\u51fa\u73b0\u4e86\u4e24\u4e58\u9a6c\uff0c\u63a5\u7740\u53c8\u662f\u4e24\u4e58\u3002\u6708\u5149\u4e0b\u9690\u7ea6\u53ef\u89c1\uff0c\u9a6c\u4e0a\u4e58\u5ba2\u90fd\u662f\u6c49\u4eba\u6253\u626e\uff0c\u624b\u4e2d\u63e1\u7740\u957f\u5200\u3002 \u3000\u3000\u674e\u6587\u79c0\u5403\u4e86\u4e00\u60ca\uff1a\u201c\u83ab\u975e\u662f\u6c49\u4eba\u5f3a\u76d7\uff1f\u201d\u53ea\u4e00\u8fdf\u7591\u95f4\uff0c\u53ea\u542c\u4e00\u4eba\u53eb\u9053\uff1a\u201c\u767d\u9a6c\uff0c\u767d\u9a6c\uff01\u201d\u7eb5\u9a6c\u51b2\u4e86\u8fc7\u6765\uff0c\u53e3\u4e2d\u53eb\u9053\uff1a\u201c\u7ad9\u4f4f\uff01\u7ad9\u4f4f\uff01\u201d\u674e\u6587\u79c0\u559d\u9053\uff1a\u201c\u5feb\u5954\uff01\u201d\u7eb5\u9a6c\u5f80\u6765\u8def\u9a70\u56de\uff0c\u4f46\u542c\u5f97\u8e44\u58f0\u6025\u54cd\uff0c\u8fce\u9762\u53c8\u6709\u51e0\u9a91\u9a6c\u622a\u4e86\u8fc7\u6765\u3002\u8fd9\u65f6\u4e1c\u5357\u5317\u4e09\u9762\u90fd\u6709\u654c\u4eba\uff0c\u5979\u4e0d\u6687\u7ec6\u60f3\uff0c\u53ea\u5f97\u50ac\u9a6c\u5f80\u897f\u75be\u9a70\u3002 \u3000\u3000\u4f46\u5411\u897f\u662f\u6c38\u6ca1\u5c3d\u5934\u7684\u5927\u6208\u58c1\u3002 \u3000\u3000\u5979\u5c0f\u65f6\u5019\u66fe\u542c\u82cf\u666e\u8bf4\u8fc7\uff0c\u5927\u6208\u58c1\u4e2d\u6709\u9b3c\uff0c\u8d70\u8fdb\u4e86\u5927\u6208\u58c1\u7684\uff0c\u6ca1\u4e00\u4e2a\u4eba\u80fd\u6d3b\u7740\u51fa\u6765\u3002\u4e0d\uff0c\u5c31\u662f\u53d8\u6210\u4e86\u9b3c\u4e5f\u4e0d\u80fd\u51fa\u6765\u3002\u8d70\u8fdb\u4e86\u5927\u6208\u58c1\uff0c\u5c31\u4f1a\u4e0d\u4f4f\u7684\u5927\u515c\u5708\u5b50\uff0c\u5728\u6c99\u6f20\u4e2d\u4e0d\u4f4f\u7684\u8d70\u7740\u8d70\u7740\uff0c\u7a81\u7136\u4e4b\u95f4\uff0c\u5728\u6c99\u6f20\u4e2d\u53d1\u73b0\u4e86\u4e00\u884c\u8db3\u8ff9\u3002\u90a3\u4eba\u5f53\u7136\u5927\u559c\u82e5\u72c2\uff0c\u4ee5\u4e3a\u627e\u5230\u4e86\u9053\u8def\uff0c\u8ddf\u7740\u8db3\u8ff9\u800c\u884c\uff0c\u4f46\u8d70\u5230\u540e\u6765\uff0c\u4ed6\u7ec8\u4e8e\u53d1\u89c9\uff0c\u8fd9\u8db3\u8ff9\u539f\u6765\u5c31\u662f\u81ea\u5df1\u7559\u4e0b\u7684\uff0c\u4ed6\u8d70\u6765\u8d70\u53bb\uff0c\u53ea\u662f\u5728\u515c\u5708\u5b50\u3002\u8fd9\u6837\u6b7b\u5728\u5927\u6208\u58c1\u4e2d\u7684\u4eba\uff0c\u53d8\u6210\u4e86\u9b3c\u4e5f\u662f\u4e0d\u5f97\u5b89\u606f\uff0c\u4ed6\u4e0d\u80fd\u8fdb\u5929\u4e0a\u7684\u4e50\u56ed\uff0c\u59cb\u7ec8\u8981\u8db3\u4e0d\u505c\u6b65\u7684\u5927\u515c\u5708\u5b50\uff0c\u5343\u5e74\u4e07\u5e74\u3001\u65e5\u65e5\u591c\u591c\u7684\u515c\u4e0b\u53bb\u6c38\u8fdc\u4e0d\u505c\u3002 \u3000\u3000\u674e\u6587\u79c0\u66fe\u95ee\u8fc7\u8ba1\u8001\u4eba\uff0c\u5927\u6208\u58c1\u4e2d\u662f\u4e0d\u662f\u771f\u7684\u8fd9\u6837\u53ef\u6015\uff0c\u662f\u4e0d\u662f\u8d70\u8fdb\u53bb\u4e4b\u540e\uff0c\u6c38\u8fdc\u4e0d\u80fd\u518d\u51fa\u6765\u3002\u8ba1\u8001\u4eba\u542c\u5230\u5979\u8fd9\u6837\u95ee\uff0c\u7a81\u7136\u95f4\u8138\u4e0a\u7684\u808c\u8089\u75c9\u631b\u8d77\u6765\uff0c\u9732\u51fa\u4e86\u975e\u5e38\u6050\u6016\u7684\u795e\u8272\uff0c\u773c\u775b\u5411\u7740\u7a97\u5916\u5077\u671b\uff0c\u4f3c\u4e4e\u89c1\u5230\u4e86\u9b3c\u602a\u4e00\u822c\u3002\u674e\u6587\u79c0\u4ece\u6765\u6ca1\u6709\u89c1\u8fc7\u4ed6\u4f1a\u5413\u5f97\u8fd9\u822c\u6a21\u6837\uff0c\u4e0d\u6562\u518d\u95ee\u4e86\uff0c\u5fc3\u60f3\u8fd9\u4e8b\u4e00\u5b9a\u4e0d\u5047\uff0c\u8bf4\u4e0d\u5b9a\u8ba1\u7237\u7237\u8fd8\u89c1\u8fc7\u90a3\u4e9b\u9b3c\u5462\u3002 \u3000\u3000\u5979\u9a91\u7740\u767d\u9a6c\u72c2\u5954\uff0c\u773c\u89c1\u524d\u9762\u9ec4\u6c99\u83bd\u83bd\uff0c\u65e0\u7a77\u65e0\u5c3d\u7684\u90fd\u662f\u6c99\u6f20\uff0c\u60f3\u5230\u4e86\u6208\u58c1\u4e2d\u6c38\u8fdc\u5728\u515c\u5708\u5b50\u7684\u9b3c\uff0c\u8d8a\u6765\u8d8a\u662f\u5bb3\u6015\uff0c\u4f46\u540e\u9762\u7684\u5f3a\u76d7\u5728\u98de\u9a70\u7740\u8ffd\u6765\u3002\u5979\u60f3\u8d77\u4e86\u7238\u7238\u5988\u5988\uff0c\u60f3\u8d77\u4e86\u82cf\u666e\u7684\u5988\u5988\u548c\u54e5\u54e5\uff0c\u77e5\u9053\u8981\u662f\u7ed9\u90a3\u4e9b\u5f3a\u76d7\u8ffd\u4e0a\u4e86\uff0c\u90a3\u662f\u6709\u6b7b\u65e0\u751f\uff0c\u751a\u81f3\u8981\u6bd4\u6b7b\u8fd8\u60e8\u4e9b\u3002\u53ef\u662f\u8d70\u8fdb\u5927\u6208\u58c1\u5462\uff0c\u90a3\u662f\u53d8\u6210\u4e86\u9b3c\u4e5f\u4e0d\u5f97\u5b89\u606f\u3002\u5979\u771f\u60f3\u52d2\u4f4f\u767d\u9a6c\u4e0d\u518d\u9003\u4e86\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u54c8\u8428\u514b\u4eba\u7684\u5e10\u7bf7\u548c\u7eff\u8272\u7684\u8349\u539f\u65e9\u5df2\u4e0d\u89c1\u4e86\uff0c\u4e24\u4e2a\u5f3a\u76d7\u5df2\u843d\u5728\u540e\u9762\uff0c\u4f46\u8fd8\u662f\u6709\u4e94\u4e2a\u5f3a\u76d7\u5406\u559d\u7740\u7d27\u7d27\u8ffd\u6765\u3002\u674e\u6587\u79c0\u542c\u5230\u7c97\u66b4\u7684\u3001\u5145\u6ee1\u4e86\u559c\u60a6\u548c\u5174\u594b\u7684\u53eb\u58f0\uff1a\u201c\u662f\u90a3\u5339\u767d\u9a6c\uff0c\u9519\u4e0d\u4e86\uff01\u6349\u4f4f\u5979\uff0c\u6349\u4f4f\u5979\uff01\u201d \u3000\u3000\u9690\u85cf\u5728\u80f8\u4e2d\u7684\u591a\u5e74\u4ec7\u6068\u7a81\u7136\u95f4\u8ff8\u53d1\u4e86\u51fa\u6765\uff0c\u5979\u5fc3\u60f3\uff1a\u201c\u7239\u7239\u548c\u5988\u5988\u662f\u4ed6\u4eec\u5bb3\u6b7b\u7684\u3002\u6211\u5f15\u4ed6\u4eec\u5230\u5927\u6208\u58c1\u91cc\uff0c\u8ddf\u4ed6\u4eec\u540c\u5f52\u4e8e\u5c3d\u3002\u6211\u4e00\u6761\u6027\u547d\uff0c\u6362\u4e86\u4e94\u4e2a\u5f3a\u76d7\uff0c\u53cd\u6b63\u2026\u2026\u53cd\u6b63\u2026\u2026\u4fbf\u662f\u6d3b\u5728\u4e16\u4e0a\uff0c\u4e5f\u6ca1\u4ec0\u4e48\u4e50\u8da3\u3002\u201d\u5979\u773c\u4e2d\u542b\u7740\u6cea\u6c34\uff0c\u5fc3\u4e2d\u518d\u4e0d\u72b9\u8c6b\uff0c\u50ac\u52a8\u767d\u9a6c\u5411\u7740\u897f\u65b9\u75be\u9a70\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u6b63\u662f\u970d\u5143\u9f99\u548c\u9648\u8fbe\u6d77\u9556\u5c40\u4e2d\u7684\u4e0b\u5c5e\uff0c\u4ed6\u4eec\u8ffd\u8d76\u767d\u9a6c\u674e\u4e09\u592b\u5987\u6765\u5230\u56de\u7586\uff0c\u867d\u7136\u5c06\u674e\u4e09\u592b\u5987\u6740\u4e86\uff0c\u4f46\u90a3\u5c0f\u5973\u5b69\u5374\u4ece\u6b64\u4e0d\u77e5\u4e0b\u843d\u3002\u4ed6\u4eec\u786e\u77e5\u674e\u4e09\u5f97\u5230\u4e86\u9ad8\u660c\u8ff7\u5bab\u7684\u5730\u56fe\u3002\u8fd9\u5f20\u5730\u56fe\u65e2\u7136\u5728\u674e\u4e09\u592b\u5987\u8eab\u4e0a\u904d\u5bfb\u4e0d\u83b7\uff0c\u90a3\u4e48\u4e00\u5b9a\u662f\u5728\u90a3\u5c0f\u5973\u5b69\u8eab\u4e0a\u3002\u9ad8\u660c\u8ff7\u5bab\u4e2d\u85cf\u7740\u6570\u4e0d\u5c3d\u7684\u73cd\u5b9d\uff0c\u664b\u5a01\u9556\u5c40\u4e00\u5e72\u4eba\u8c01\u90fd\u4e0d\u6b7b\u5fc3\uff0c\u5728\u8fd9\u4e00\u5e26\u5230\u5904\u6e38\u8361\uff0c\u627e\u5bfb\u90a3\u4e2a\u5973\u5b69\u3002\u8fd9\u4e00\u803d\u6401\u4fbf\u662f\u5341\u5e74\uff0c\u4ed6\u4eec\u4e0d\u4e8b\u751f\u4ea7\uff0c\u4ed7\u7740\u6709\u7684\u662f\u6b66\u827a\uff0c\u725b\u7f8a\u9a7c\u9a6c\uff0c\u81ea\u6709\u8349\u539f\u4e0a\u7684\u7267\u6c11\u7ed9\u4ed6\u4eec\u7267\u517b\u3002\u4ed6\u4eec\u53ea\u987b\u62d4\u51fa\u5200\u5b50\u6765\uff0c\u6740\u4eba\uff0c\u653e\u706b\uff0c\u62a2\u52ab\u3001\u5978\u6deb\u2026\u2026 \u3000\u3000\u8fd9\u5341\u5e74\u4e4b\u4e2d\uff0c\u5927\u5bb6\u6c38\u8fdc\u4e0d\u505c\u7684\u5728\u627e\u8fd9\u5c0f\u5973\u5b69\uff0c\u8349\u539f\u5343\u91cc\uff0c\u5374\u5f80\u54ea\u91cc\u627e\u53bb\uff1f\u53ea\u6015\u8fd9\u5c0f\u5973\u5b69\u65e9\u6b7b\u4e86\uff0c\u9aa8\u5934\u4e5f\u5316\u4e86\u7070\uff0c\u4f46\u5728\u8349\u539f\u4e0a\u505a\u5f3a\u76d7\uff0c\u81ea\u7531\u81ea\u5728\uff0c\u53ef\u6bd4\u5728\u4e2d\u539f\u8d70\u9556\u900d\u9065\u5feb\u6d3b\u5f97\u591a\uff0c\u53c8\u4f55\u5fc5\u56de\u4e2d\u539f\u53bb\uff1f \u3000\u3000\u6709\u65f6\u5019\uff0c\u5927\u5bb6\u8c08\u5230\u9ad8\u660c\u8ff7\u5bab\u4e2d\u7684\u73cd\u5b9d\uff0c\u8c08\u5230\u767d\u9a6c\u674e\u4e09\u7684\u5973\u513f\u3002\u8fd9\u5c0f\u59d1\u5a18\u5c31\u7b97\u4e0d\u6b7b\uff0c\u4e5f\u957f\u5927\u5f97\u8ba4\u4e0d\u51fa\u4e86\uff0c\u53ea\u6709\u90a3\u5339\u767d\u9a6c\u624d\u4e0d\u4f1a\u53d8\u3002\u8fd9\u6837\u9ad8\u5927\u7684\u5168\u8eab\u96ea\u767d\u7684\u767d\u9a6c\u751a\u662f\u5e0c\u6709\uff0c\u8001\u8fdc\u4e00\u89c1\u5c31\u8ba4\u51fa\u6765\u4e86\u3002\u4f46\u5982\u767d\u9a6c\u4e5f\u6b7b\u4e86\u5462\uff1f\u9a6c\u5339\u7684\u5bff\u547d\u53ef\u6bd4\u4eba\u77ed\u5f97\u591a\u3002\u65f6\u5019\u4e00\u5929\u5929\u8fc7\u53bb\uff0c\u8c01\u90fd\u65e9\u4e0d\u5b58\u4e86\u6307\u671b\u3002 \u3000\u3000\u54ea\u77e5\u9053\u7a81\u7136\u4e4b\u95f4\uff0c\u89c1\u5230\u4e86\u8fd9\u5339\u767d\u9a6c\u3002\u90a3\u6ca1\u9519\uff0c\u6b63\u662f\u8fd9\u5339\u767d\u9a6c\uff01 \u3000\u3000\u90a3\u767d\u9a6c\u8fd9\u65f6\u5019\u5e74\u9f7f\u5df2\u589e\uff0c\u811a\u529b\u5df2\u4e0d\u5982\u5c11\u5e74\u4e4b\u65f6\uff0c\u4f46\u4ecd\u6bd4\u5e38\u9a6c\u5954\u8dd1\u8d77\u6765\u5feb\u5f97\u591a\uff0c\u5230\u5f97\u9ece\u660e\u65f6\uff0c\u7adf\u5df2\u5c06\u4e94\u4e2a\u5f3a\u76d7\u629b\u5f97\u5f71\u8e2a\u4e0d\u89c1\uff0c\u540e\u9762\u8ffd\u6765\u7684\u8e44\u58f0\u4e5f\u5df2\u4e0d\u518d\u542c\u5230\u3002\u53ef\u662f\u674e\u6587\u79c0\u77e5\u9053\u6c99\u6f20\u4e0a\u7559\u4e0b\u9a6c\u8e44\u8db3\u8ff9\uff0c\u90a3\u4e94\u4e2a\u5f3a\u76d7\u867d\u7136\u4e00\u65f6\u8ffd\u8d76\u4e0d\u4e0a\uff0c\u7ec8\u4e8e\u8fd8\u662f\u4f1a\u4f9d\u5faa\u8db3\u5370\u8ffd\u6765\uff0c\u56e0\u6b64\u7adf\u662f\u4e1d\u6beb\u4e0d\u6562\u505c\u7559\u3002 \u3000\u3000\u53c8\u5954\u51fa\u5341\u4f59\u91cc\uff0c\u5929\u5df2\u5927\u660e\uff0c\u8fc7\u4e86\u51e0\u4e2a\u6c99\u4e18\uff0c\u7a81\u7136\u4e4b\u95f4\uff0c\u897f\u5317\u65b9\u51fa\u73b0\u4e86\u4e00\u7247\u5c71\u9675\uff0c\u5c71\u4e0a\u6811\u6728\u82cd\u8471\uff0c\u5728\u6c99\u6f20\u4e2d\u7a81\u7136\u770b\u5230\uff0c\u771f\u5982\u89c1\u5230\u4e16\u5916\u4ed9\u5c71\u4e00\u822c\u3002\u5927\u6c99\u6f20\u4e0a\u6c99\u4e18\u8d77\u4f0f\uff0c\u51e0\u4e2a\u5927\u6c99\u4e18\u5c06\u8fd9\u7247\u5c71\u9675\u906e\u4f4f\u4e86\uff0c\u56e0\u6b64\u8fdc\u5904\u5b8c\u5168\u671b\u4e0d\u89c1\u3002\u674e\u6587\u79c0\u5fc3\u4e2d\u4e00\u9707\uff1a\u201c\u83ab\u975e\u8fd9\u662f\u9b3c\u5c71\uff1f\u4e3a\u4ec0\u4e48\u6c99\u6f20\u4e0a\u6709\u8fd9\u8bb8\u591a\u5c71\uff0c\u5374\u4ece\u6765\u6ca1\u542c\u4eba\u8bf4\u8fc7\uff1f\u201d \u3000\u3000\u8f6c\u5ff5\u4e00\u60f3\uff1a\u201c\u662f\u9b3c\u5c71\u6700\u597d\uff0c\u6b63\u597d\u5f15\u8fd9\u4e94\u4e2a\u6076\u8d3c\u8fdb\u53bb\u3002\u201d \u3000\u3000\u767d\u9a6c\u811a\u6b65\u8fc5\u6377\uff0c\u4e0d\u591a\u65f6\u5230\u4e86\u5c71\u524d\uff0c\u8ddf\u7740\u9a70\u5165\u5c71\u8c37\u3002\u53ea\u89c1\u4e24\u5c71\u4e4b\u95f4\u6d41\u51fa\u4e00\u6761\u5c0f\u6eaa\u6765\u3002\u767d\u9a6c\u4e00\u58f0\u6b22\u5636\uff0c\u76f4\u5954\u5230\u6eaa\u8fb9\u3002\u674e\u6587\u79c0\u7ffb\u8eab\u4e0b\u9a6c\uff0c\u4f38\u624b\u6367\u4e86\u4e9b\u6e05\u6c34\u6d17\u53bb\u8138\u4e0a\u6c99\u5c18\uff0c\u518d\u559d\u51e0\u53e3\uff0c\u53ea\u89c9\u6eaa\u6c34\u5fae\u5e26\u751c\u5473\uff0c\u751a\u662f\u6e05\u51c9\u53ef\u53e3\u3002 \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u540e\u8111\u4e0a\u5ffd\u88ab\u4e00\u4ef6\u786c\u7269\u9876\u4f4f\u4e86\uff0c\u53ea\u542c\u5f97\u4e00\u4e2a\u5636\u54d1\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u5230\u8fd9\u91cc\u5e72\u4e48\uff1f\u201d\u674e\u6587\u79c0\u5927\u5403\u4e00\u60ca\uff0c\u5f85\u8981\u8f6c\u8eab\uff0c\u90a3\u58f0\u97f3\u9053\uff1a\u201c\u6211\u8fd9\u6756\u5934\u5bf9\u51c6\u4e86\u4f60\u7684\u540e\u8111\uff0c\u53ea\u987b\u7a0d\u4e00\u7528\u52b2\uff0c\u4f60\u7acb\u65f6\u4fbf\u91cd\u4f24\u800c\u6b7b\u3002\u201d\u674e\u6587\u79c0\u4f46\u89c9\u90a3\u786c\u7269\u5fae\u5411\u524d\u4e00\u9001\uff0c\u679c\u89c9\u5934\u8111\u4e00\u9635\u6655\u7729\uff0c\u5f53\u4e0b\u4e0d\u6562\u52a8\u5f39\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u4eba\u4f1a\u8bf4\u8bdd\uff0c\u60f3\u6765\u4e0d\u662f\u9b3c\u602a\u3002\u4ed6\u53c8\u95ee\u6211\u5230\u8fd9\u91cc\u5e72\u4e48\uff0c\u90a3\u4e48\u81ea\u662f\u4f4f\u5728\u6b64\u5904\u4e4b\u4eba\uff0c\u4e0d\u662f\u5f3a\u76d7\u4e86\u3002\u201d \u3000\u3000\u90a3\u58f0\u97f3\u53c8\u9053\uff1a\u201c\u6211\u95ee\u4f60\u554a\uff0c\u600e\u5730\u4e0d\u7b54\uff1f\u201d\u674e\u6587\u79c0\u9053\uff1a\u201c\u6709\u574f\u4eba\u8ffd\u6211\uff0c\u6211\u9003\u5230\u4e86\u8fd9\u91cc\u3002\u201d\u90a3\u4eba\u9053\uff1a\u201c\u4ec0\u4e48\u574f\u4eba\uff1f\u201d\u674e\u6587\u79c0\uff1a \u3000\u3000\u201c\u662f\u8bb8\u591a\u5f3a\u76d7\u3002\u201d\u90a3\u4eba\u9053\uff1a\u201c\u4ec0\u4e48\u5f3a\u76d7\uff1f\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d\u674e\u6587\u79c0\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u3002\u4ed6\u4eec\u4ece\u524d\u662f\u4fdd\u9556\u7684\uff0c\u5230\u4e86\u56de\u7586\uff0c\u4fbf\u505a\u4e86\u5f3a\u76d7\u3002\u201d \u3000\u3000\u90a3\u4eba\u9053\uff1a\u201c\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u7236\u4eb2\u662f\u8c01\uff1f\u5e08\u7236\u662f\u8c01\uff1f\u201d\u674e\u6587\u79c0\u9053\uff1a\u201c\u6211\u53eb\u674e\u6587\u79c0\uff0c\u6211\u7239\u7239\u662f\u767d\u9a6c\u674e\u4e09\uff0c\u5988\u5988\u662f\u91d1\u94f6\u5c0f\u5251\u4e09\u5a18\u5b50\u3002 \u3000\u3000\u6211\u6ca1\u5e08\u7236\u3002\u201d\u90a3\u4eba\u201c\u54e6\u201d\u7684\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5514\uff0c\u539f\u6765\u91d1\u94f6\u5c0f\u5251\u4e09\u5a18\u5b50\u5ac1\u4e86\u767d\u9a6c\u674e\u4e09\u3002\u4f60\u7239\u7239\u5988\u5988\u5462\uff1f\u201d\u674e\u6587\u79c0\u9053\uff1a\u201c\u90fd\u7ed9\u90a3\u4e9b\u5f3a\u76d7\u5bb3\u6b7b\u4e86\u3002\u4ed6\u4eec\u8fd8\u8981\u6740\u6211\u3002\u201d \u3000\u3000\u90a3\u4eba\u201c\u5514\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u7ad9\u8d77\u6765\uff01\u201d\u674e\u6587\u79c0\u7ad9\u8d77\u8eab\u6765\u3002\u90a3\u4eba\u9053\uff1a\u201c\u8f6c\u8fc7\u8eab\u6765\u3002\u201d\u674e\u6587\u79c0\u6162\u6162\u8f6c\u8eab\uff0c\u90a3\u4eba\u6728\u6756\u7684\u94c1\u5c16\u79bb\u5f00\u4e86\u5979\u540e\u8111\uff0c\u4e00\u7f29\u4e00\u4f38\uff0c\u53c8\u70b9\u5728\u5979\u5589\u5934\u3002\u4f46\u4ed6\u6756\u4e0a\u5e76\u4e0d\u4f7f\u52b2\uff0c\u53ea\u662f\u865a\u865a\u7684\u70b9\u7740\u3002\u674e\u6587\u79c0\u5411\u4ed6\u4e00\u770b\uff0c\u5fc3\u4e0b\u5f88\u662f\u8be7\u5f02\uff0c\u542c\u5230\u90a3\u5636\u54d1\u51b7\u9177\u7684\u55d3\u97f3\u4e4b\u65f6\uff0c\u6599\u60f3\u80cc\u540e\u8fd9\u4eba\u5b9a\u662f\u5341\u5206\u7684\u51f6\u6076\u53ef\u6016\uff0c\u54ea\u77e5\u773c\u524d\u8fd9\u4eba\u5374\u662f\u4e2a\u8001\u7fc1\uff0c\u8eab\u5f62\u7626\u5f31\uff0c\u5f62\u5bb9\u67af\u69c1\uff0c\u6101\u7709\u82e6\u8138\uff0c\u8eab\u4e0a\u7a7f\u7684\u662f\u6c49\u4eba\u88c5\u675f\uff0c\u8863\u5e3d\u90fd\u5df2\u7834\u70c2\u4e0d\u582a\u3002\u4f46\u4ed6\u5934\u53d1\u5377\u66f2\uff0c\u5374\u53c8\u4e0d\u5927\u50cf\u6c49\u4eba\u3002 \u3000\u3000\u674e\u6587\u79c0\u9053\uff1a\u201c\u8001\u4f2f\u4f2f\uff0c\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u8fd9\u91cc\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u90a3\u8001\u4eba\u773c\u89c1\u674e\u6587\u79c0\u5bb9\u8c8c\u5a07\u7f8e\uff0c\u4e5f\u662f\u5927\u51fa\u610f\u6599\u4e4b\u5916\uff0c\u4e00\u6014\u4e4b\u4e0b\uff0c\u51b7\u51b7\u7684\u9053\uff1a\u201c\u6211\u6ca1\u540d\u5b57\uff0c\u4e5f\u4e0d\u77e5\u9053\u8fd9\u91cc\u662f\u4ec0\u4e48\u5730\u65b9\u3002\u201d\u4fbf\u5728\u6b64\u65f6\uff0c\u8fdc\u5904\u8e44\u58f0\u9690\u9690\u54cd\u8d77\u3002\u674e\u6587\u79c0\u60ca\u9053\uff1a\u201c\u5f3a\u76d7\u6765\u5566\uff0c\u8001\u4f2f\u4f2f\uff0c\u5feb\u8eb2\u8d77\u6765\u3002\u201d\u90a3\u4eba\u9053\uff1a\u201c\u5e72\u4e48\u8981\u8eb2\uff1f\u201d\u674e\u6587\u79c0\u9053\uff1a\u201c\u90a3\u4e9b\u5f3a\u76d7\u6076\u5f97\u5f88\uff0c\u4f1a\u5bb3\u6b7b\u4f60\u7684\u3002\u201d\u90a3\u4eba\u51b7\u51b7\u7684\u9053\uff1a\u201c\u4f60\u8ddf\u6211\u7d20\u4e0d\u76f8\u8bc6\uff0c\u4f55\u5fc5\u7ba1\u6211\u7684\u6b7b\u6d3b\uff1f\u201d\u8fd9\u65f6\u9a6c\u8e44\u58f0\u66f4\u52a0\u8fd1\u4e86\u3002\u674e\u6587\u79c0\u4e5f\u4e0d\u7406\u4ed6\u5c06\u6756\u5c16\u70b9\u5728\u81ea\u5df1\u5589\u5934\uff0c\u4e00\u4f38\u624b\u4fbf\u62c9\u4f4f\u4ed6\u624b\u81c2\uff0c\u9053\uff1a\u201c\u8001\u4f2f\u4f2f\uff0c\u54b1\u4eec\u4e00\u8d77\u9a91\u9a6c\u9003\u5427\uff0c\u518d\u8fdf\u4fbf\u6765\u4e0d\u53ca\u4e86\u3002\u201d \u3000\u3000\u90a3\u4eba\u5c06\u624b\u4e00\u7529\uff0c\u8981\u6323\u8131\u674e\u6587\u79c0\u7684\u624b\uff0c\u54ea\u77e5\u4ed6\u8fd9\u4e00\u7529\u5fae\u5f31\u65e0\u529b\uff0c\u7adf\u662f\u6323\u4e4b\u4e0d\u8131\u3002\u674e\u6587\u79c0\u5947\u9053\uff1a\u201c\u4f60\u6709\u75c5\u4e48\uff1f\u6211\u6276\u4f60\u4e0a\u9a6c\u3002\u201d \u3000\u3000\u8bf4\u7740\u53cc\u624b\u6258\u4f4f\u4ed6\u8170\uff0c\u5c06\u4ed6\u9001\u4e0a\u4e86\u9a6c\u978d\u3002\u8fd9\u4eba\u7626\u9aa8\u4f36\u4ec3\uff0c\u867d\u662f\u7537\u5b50\uff0c\u8eab\u91cd\u5374\u8fd8\u4e0d\u53ca\u9aa8\u8089\u5a77\u5300\u7684\u674e\u6587\u79c0\uff0c\u5750\u5728\u978d\u4e0a\u6447\u6447\u6643\u6643\uff0c\u4f3c\u4e4e\u968f\u65f6\u90fd\u4f1a\u6454\u4e0b\u978d\u6765\u3002\u674e\u6587\u79c0\u8ddf\u7740\u4e0a\u9a6c\uff0c\u5750\u5728\u4ed6\u8eab\u540e\uff0c\u7eb5\u9a6c\u5411\u4e1b\u5c71\u4e4b\u4e2d\u9a70\u53bb\u3002 \u3000\u3000\u4e24\u4eba\u8fd9\u4e00\u803d\u6401\uff0c\u53ea\u542c\u5f97\u4e94\u9a91\u9a6c\u5df2\u9a70\u8fdb\u4e86\u5c71\u8c37\uff0c\u4e94\u4e2a\u5f3a\u4eba\u7684\u547c\u53f1\u4e4b\u58f0\u4e5f\u5df2\u9690\u7ea6\u53ef\u95fb\u3002\u90a3\u4eba\u7a81\u7136\u56de\u8fc7\u5934\u6765\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8ddf\u4ed6\u4eec\u662f\u4e00\u8d77\u7684\uff0c\u662f\u4e0d\u662f\uff1f\u4f60\u4eec\u5b89\u6392\u4e86\u8be1\u8ba1\uff0c\u60f3\u9a97\u6211\u4e0a\u5f53\u3002\u201d\u674e\u6587\u79c0\u89c1\u4ed6\u6ee1\u8138\u75c5\u5bb9\u731b\u5730\u8f6c\u4e3a\u72f0\u72de\u53ef\u6016\uff0c\u773c\u4e2d\u4e5f\u5c04\u51fa\u51f6\u5149\uff0c\u4e0d\u7981\u5927\u4e3a\u5bb3\u6015\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u662f\u7684\uff0c\u4e0d\u662f\u7684\uff0c\u6211\u4ece\u6765\u6ca1\u89c1\u8fc7\u4f60\uff0c\u9a97\u4f60\u4e0a\u4ec0\u4e48\u5f53\uff1f\u201d\u90a3\u4eba\u5389\u58f0\u9053\uff1a\u201c\u4f60\u8981\u9a97\u6211\u5e26\u4f60\u53bb\u9ad8\u660c\u8ff7\u5bab\u2026\u2026\u201d\u4e00\u53e5\u8bdd\u6ca1\u8bf4\u5b8c\uff0c\u7a81\u7136\u4f4f\u53e3\u3002 \u3000\u3000\u8fd9\u201c\u9ad8\u660c\u8ff7\u5bab\u201d\u56db\u5b57\uff0c\u674e\u6587\u79c0\u5e7c\u65f6\u968f\u7236\u6bcd\u9003\u6765\u56de\u7586\u4e4b\u65f6\uff0c\u66fe\u542c\u7236\u6bcd\u4eb2\u8c08\u8bdd\u4e2d\u63d0\u8fc7\u51e0\u6b21\uff0c\u4f46\u5f53\u65f6\u4e0d\u89e3\uff0c\u5e76\u672a\u6ce8\u610f\uff0c\u73b0\u5728\u53c8\u4e8b\u9694\u5341\u5e74\uff0c\u8fd9\u8001\u4eba\u5ffd\u7136\u8bf4\u53ca\uff0c\u5979\u4e00\u65f6\u60f3\u4e0d\u8d77\u4ec0\u4e48\u65f6\u5019\u4f3c\u4e4e\u66fe\u542c\u5230\u4eba\u8bf4\u8fc7\uff0c\u832b\u7136\u9053\uff1a\u201c\u9ad8\u660c\u8ff7\u5bab\uff1f\u90a3\u662f\u4ec0\u4e48\u554a\uff1f\u201d\u8001\u4eba\u89c1\u5979\u795e\u8272\u771f\u8bda\uff0c\u4e0d\u4f3c\u4f5c\u4f2a\uff0c\u58f0\u97f3\u7f13\u548c\u4e00\u4e9b\uff0c\u9053\uff1a\u201c\u4f60\u5f53\u771f\u4e0d\u77e5\u9ad8\u660c\u8ff7\u5bab\uff1f\u201d \u3000\u3000\u674e\u6587\u79c0\u6447\u5934\u9053\uff1a\u201c\u4e0d\u77e5\u9053\uff0c\u554a\uff0c\u662f\u4e86\u2026\u2026\u201d\u8001\u4eba\u5389\u58f0\u95ee\u9053\uff1a\u201c\u662f\u4e86\u4ec0\u4e48\uff1f\u201d\u674e\u6587\u79c0\u9053\uff1a\u201c\u6211\u5c0f\u65f6\u5019\u8ddf\u7740\u7239\u7239\u5988\u5988\u9003\u6765\u56de\u7586\uff0c\u66fe\u542c\u4ed6\u4eec\u8bf4\u8fc7\u2018\u9ad8\u660c\u8ff7\u5bab\u2019\u3002\u90a3\u662f\u5f88\u597d\u73a9\u7684\u5730\u65b9\u4e48\uff1f\u201d\u8001\u4eba\u75be\u8a00\u5389\u8272\u7684\u95ee\u9053\uff1a\u201c\u4f60\u7239\u5a18\u8fd8\u8bf4\u8fc7\u4ec0\u4e48\uff1f\u53ef\u4e0d\u8bb8\u7792\u6211\u3002\u201d\u674e\u6587\u79c0\u51c4\u7136\u9053\uff1a\u201c\u4f46\u613f\u6211\u80fd\u591f\u591a\u8bb0\u5f97\u4e00\u4e9b\u7239\u5988\u8bf4\u8fc7\u7684\u8bdd\uff0c\u4fbf\u662f\u591a\u4e00\u4e2a\u5b57\uff0c\u4e5f\u662f\u597d\u7684\u3002\u5c31\u53ef\u60dc\u518d\u4e5f\u542c\u4e0d\u5230\u4ed6\u4eec\u7684\u58f0\u97f3\u4e86\u3002\u8001\u4f2f\u4f2f\uff0c\u6211\u5e38\u5e38\u8fd9\u6837\u50bb\u60f3\uff0c\u53ea\u8981\u7239\u7239\u5988\u5988\u80fd\u6d3b\u8fc7\u6765\u4e00\u6b21\uff0c\u8ba9\u6211\u518d\u89c1\u4e0a\u4e00\u773c\u3002\u5509\uff01\u53ea\u8981\u7239\u5988\u6d3b\u7740\uff0c\u4fbf\u662f\u5929\u5929\u4e0d\u505c\u7684\u6253\u6211\u9a82\u6211\uff0c\u6211\u4e5f\u5f88\u5feb\u6d3b\u554a\u3002\u5f53\u7136\uff0c\u4ed6\u4eec\u6c38\u8fdc\u4e0d\u4f1a\u6253\u6211\u7684\u3002\u201d\u7a81\u7136\u4e4b\u95f4\uff0c\u5979\u8033\u4e2d\u4f3c\u4e4e\u51fa\u73b0\u4e86\u82cf\u9c81\u514b\u72e0\u6253\u82cf\u666e\u7684\u97ad\u5b50\u58f0\uff0c\u6124\u6012\u7684\u65a5\u9a82\u58f0\u3002 \u3000\u3000\u90a3\u8001\u4eba\u8138\u8272\u7a0d\u8f6c\u67d4\u548c\uff0c\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u7a81\u7136\u53c8\u5927\u58f0\u95ee\uff1a \u3000\u3000\u201c\u4f60\u5ac1\u4e86\u4eba\u6ca1\u6709\uff1f\u201d\u674e\u6587\u79c0\u7ea2\u7740\u8138\u6447\u4e86\u6447\u5934\u3002\u8001\u4eba\u9053\uff1a\u201c\u8fd9\u51e0\u5e74\u4f60\u8ddf\u8c01\u4f4f\u5728\u4e00\u8d77\uff1f\u201d\u674e\u6587\u79c0\u9053\uff1a\u201c\u8ddf\u8ba1\u7237\u7237\u3002\u201d\u8001\u4eba\u9053\uff1a\u201c\u8ba1\u7237\u7237\uff1f\u4ed6\u591a\u5927\u5e74\u7eaa\u4e86\uff1f\u76f8\u8c8c\u600e\u6837\uff1f\u201d\u674e\u6587\u79c0\u5bf9\u767d\u9a6c\u9053\uff1a\u201c\u597d\u9a6c\u513f\uff0c\u5f3a\u76d7\u8ffd\u6765\u5566\uff0c\u5feb\u8dd1\u5feb\u8dd1\u3002\u201d\u5fc3\u60f3\uff1a\u201c\u5728\u8fd9\u7d27\u6025\u5f53\u513f\uff0c\u4f60\u8001\u662f\u95ee\u8fd9\u4e9b\u4e0d\u76f8\u5e72\u7684\u4e8b\u5e72\u4e48\uff1f\u201d\u4f46\u89c1\u4ed6\u6ee1\u8138\u7591\u4e91\uff0c\u7ec8\u4e8e\u8fd8\u662f\u8bf4\u4e86\uff1a \u3000\u3000\u201c\u8ba1\u7237\u7237\u603b\u6709\u516b\u5341\u591a\u5c81\u4e86\u5427\uff0c\u4ed6\u6ee1\u5934\u767d\u53d1\uff0c\u8138\u4e0a\u5168\u662f\u76b1\u7eb9\uff0c\u5f85\u6211\u5f88\u597d\u7684\u3002\u201d\u8001\u4eba\u9053\uff1a\u201c\u4f60\u5728\u56de\u7586\u53c8\u8bc6\u5f97\u4ec0\u4e48\u6c49\u4eba\uff1f\u8ba1\u7237\u7237\u5bb6\u4e2d\u8fd8\u6709\u4ec0\u4e48\uff1f\u201d\u674e\u6587\u79c0\u9053\uff1a\u201c\u8ba1\u7237\u7237\u5bb6\u91cc\u518d\u6ca1\u522b\u4eba\u4e86\u3002\u6211\u8fde\u54c8\u8428\u514b\u4eba\u4e5f\u4e0d\u8bc6\u5f97\uff0c\u522b\u8bf4\u6c49\u4eba\u5566\u3002\u201d\u6700\u540e\u8fd9\u4e24\u53e5\u8bdd\u5374\u662f\u6124\u6fc0\u4e4b\u8a00\uff0c\u5979\u60f3\u8d77\u4e86\u82cf\u666e\u548c\u963f\u66fc\uff0c\u5fc3\u60f3\u867d\u662f\u8bc6\u5f97\u4ed6\u4eec\uff0c\u4e5f\u7b49\u4e8e\u4e0d\u8bc6\u3002 \u3000\u3000\u767d\u9a6c\u80cc\u4e0a\u4e58\u4e86\u4e24\u4eba\uff0c\u5954\u8dd1\u4e0d\u5feb\uff0c\u540e\u9762\u4e94\u4e2a\u5f3a\u76d7\u8ffd\u5f97\u66f4\u52a0\u8fd1\u4e86\uff0c\u53ea\u542c\u5f97\u55d6\u55d6\u51e0\u58f0\uff0c\u4e09\u652f\u7fbd\u7bad\u63a5\u8fde\u4ece\u8eab\u65c1\u63a0\u8fc7\u3002\u90a3\u4e9b\u5f3a\u76d7\u60f3\u64d2\u6d3b\u53e3\uff0c\u5e76\u4e0d\u60f3\u7528\u7bad\u5c04\u6b7b\u5979\uff0c\u8fd9\u51e0\u7bad\u53ea\u662f\u5a01\u5413\uff0c\u8981\u5979\u505c\u9a6c\u3002 \u3000\u3000\u674e\u6587\u79c0\u5fc3\u60f3\uff1a\u201c\u6a2a\u7ad6\u6211\u5df2\u51b3\u5fc3\u548c\u8fd9\u4e94\u4e2a\u6076\u8d3c\u540c\u5f52\u4e8e\u5c3d\uff0c\u5c31\u8ba9\u8fd9\u4f4d\u4f2f\u4f2f\u72ec\u81ea\u9003\u751f\u5427\uff01\u201d\u5f53\u5373\u8dc3\u4e0b\u5730\u6765\uff0c\u5728\u9a6c\u81c0\u4e00\u62cd\uff0c\u53eb\u9053\uff1a\u201c\u767d\u9a6c\uff0c\u767d\u9a6c\uff01\u5feb\u5e26\u4e86\u4f2f\u4f2f\u5148\u9003\uff01\u201d\u8001\u4eba\u4e00\u6014\uff0c\u6ca1\u6599\u5230\u5979\u5fc3\u5730\u5982\u6b64\u4ec1\u5584\uff0c\u7adf\u4f1a\u53eb\u81ea\u5df1\u72ec\u81ea\u9003\u5f00\uff0c\u7a0d\u4e00\u72b9\u8c6b\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u63a5\u4f4f\u6211\u624b\u91cc\u7684\u9488\uff0c\u5c0f\u5fc3\u522b\u78b0\u7740\u9488\u5c16\u3002\u201d\u674e\u6587\u79c0\u4f4e\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u4ed6\u53f3\u624b\u4e24\u6839\u624b\u6307\u95f4\u631f\u7740\u4e00\u679a\u7ec6\u9488\uff0c\u5f53\u4e0b\u4f38\u624b\u6307\u62ff\u4f4f\u4e86\uff0c\u5374\u4e0d\u660e\u5176\u610f\u3002\u8001\u4eba\u9053\uff1a\u201c\u8fd9\u9488\u5c16\u4e0a\u5582\u6709\u5267\u6bd2\uff0c\u90a3\u4e9b\u5f3a\u76d7\u82e5\u662f\u6349\u4f4f\u4f60\uff0c\u53ea\u8981\u8f7b\u8f7b\u4e00\u4e0b\u523a\u5728\u4ed6\u4eec\u8eab\u4e0a\uff0c\u5f3a\u76d7\u5c31\u6b7b\u4e86\u3002\u201d\u674e\u6587\u79c0\u5403\u4e86\u4e00\u60ca\uff0c\u9002\u624d\u65e9\u89c1\u5230\u4ed6\u624b\u4e2d\u6301\u9488\uff0c\u5f53\u65f6\u4e5f\u6ca1\u5728\u610f\uff0c\u770b\u6765\u8fd9\u4e00\u756a\u5bf9\u7b54\u82e5\u662f\u4e0d\u6ee1\u4ed6\u610f\uff0c\u4ed6\u5df2\u7528\u6bd2\u9488\u523a\u5728\u81ea\u5df1\u8eab\u4e0a\u4e86\u3002\u90a3\u8001\u4eba\u5f53\u4e0b\u50ac\u9a6c\u4fbf\u884c\u3002 \u3000\u3000\u4e94\u4e58\u9a6c\u9a70\u8fd1\u8eab\u6765\uff0c\u56e2\u56e2\u5c06\u674e\u6587\u79c0\u56f4\u5728\u5793\u5fc3\u3002\u4e94\u4e2a\u5f3a\u4eba\u89c1\u5230\u4e86\u8fd9\u822c\u5e74\u8f7b\u8c8c\u7f8e\u7684\u59d1\u5a18\uff0c\u8c01\u4e5f\u6ca1\u60f3\u5230\u53bb\u8ffd\u90a3\u8001\u5934\u513f\u3002 \u3000\u3000\u4e94\u4e2a\u5f3a\u76d7\u7eb7\u7eb7\u8df3\u4e0b\u9a6c\u6765\uff0c\u8138\u4e0a\u90fd\u662f\u72de\u7b11\u3002\u674e\u6587\u79c0\u5fc3\u4e2d\u6026\u6026\u4e71\u8df3\uff0c\u6697\u60f3\u90a3\u8001\u4f2f\u4f2f\u867d\u8bf4\u8fd9\u6bd2\u9488\u80fd\u5236\u4eba\u6b7b\u547d\uff0c\u4f46\u8fd9\u6837\u5c0f\u5c0f\u4e00\u679a\u9488\u513f\uff0c\u5982\u4f55\u6321\u5f97\u4f4f\u773c\u524d\u8fd9\u4e94\u4e2a\u51f6\u6a2a\u53ef\u6016\u7684\u5927\u6c49\uff0c\u4fbf\u7b97\u771f\u80fd\u523a\u5f97\u6b7b\u4e00\u4eba\uff0c\u5374\u5c1a\u6709\u56db\u4e2a\u3002\u8fd8\u662f\u4e00\u9488\u523a\u6b7b\u4e86\u81ea\u5df1\u5427\uff0c\u4e5f\u514d\u5f97\u906d\u5f3a\u4eba\u7684\u51cc\u8fb1\u3002\u53ea\u542c\u5f97\u4e00\u4eba\u53eb\u9053\uff1a\u201c\u597d\u6f02\u4eae\u7684\u599e\u513f\uff01\u201d\u4fbf\u6709\u4e24\u4eba\u5411\u5979\u6251\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u5de6\u9996\u4e00\u4e2a\u6c49\u5b50\u7830\u7684\u4e00\u62f3\uff0c\u5c06\u53e6\u4e00\u4e2a\u6c49\u5b50\u6253\u7ffb\u5728\u5730\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4f60\u8ddf\u6211\u4e89\u4e48\uff1f\u201d\u8ddf\u7740\u4fbf\u62b1\u4f4f\u4e86\u674e\u6587\u79c0\u7684\u8170\u3002\u674e\u6587\u79c0\u614c\u4e71\u4e4b\u4e2d\uff0c\u5c06\u9488\u5728\u4ed6\u53f3\u81c2\u4e00\u523a\uff0c\u5927\u53eb\uff1a\u201c\u6076\u5f3a\u76d7\uff0c\u653e\u5f00\u6211\u3002\u201d\u90a3\u5927\u6c49\u5446\u5446\u7684\u77aa\u7740\u5979\u3002\u7a81\u7136\u4e0d\u52a8\u3002\u6454\u5728\u5730\u4e0b\u7684\u6c49\u5b50\u4f38\u51fa\u53cc\u624b\uff0c\u62b1\u4f4f\u674e\u6587\u79c0\u7684\u5c0f\u817f\uff0c\u4f7f\u52b2\u4e00\u62d6\uff0c\u5c06\u5979\u62c9\u5012\u5728\u5730\u3002\u674e\u6587\u79c0\u5de6\u624b\u6491\u62d2\uff0c\u53f3\u624b\u5411\u524d\u4e00\u4f38\uff0c\u4e00\u9488\u523a\u5165\u4ed6\u7684\u80f8\u819b\u3002\u90a3\u5927\u6c49\u6b63\u5728\u54c8\u54c8\u5927\u7b11\uff0c\u5ffd\u7136\u95f4\u7b11\u58f0\u4e2d\u7edd\uff0c\u5f20\u5927\u4e86\u53e3\uff0c\u4e5f\u662f\u8eab\u5f62\u50f5\u4f4f\uff0c\u4e00\u52a8\u4e5f\u4e0d\u52a8\u4e86\u3002 \u3000\u3000\u674e\u6587\u79c0\u722c\u8d77\u8eab\u6765\uff0c\u62a2\u7740\u8dc3\u4e0a\u4e00\u5339\u9a6c\u7684\u9a6c\u80cc\uff0c\u7eb5\u9a6c\u5411\u5c71\u4e2d\u9003\u53bb\u3002\u4f59\u4e0b\u4e09\u4e2a\u5f3a\u76d7\u89c1\u90a3\u4e8c\u4eba\u7a81\u7136\u50f5\u4f4f\uff0c\u5b9b\u4f3c\u4e2d\u90aa\uff0c\u90fd\u9053\u88ab\u674e\u6587\u79c0\u70b9\u4e2d\u4e86\u7a74\u9053\uff0c\u5fc3\u60f3\u8fd9\u5c11\u5973\u6b66\u529f\u5947\u9ad8\uff0c\u4e0d\u6562\u8ffd\u8d76\u3002\u4ed6\u4e09\u4e2a\u4eba\u90fd\u4e0d\u4f1a\u70b9\u7a74\u89e3\u7a74\uff0c\u53ea\u6709\u5e26\u4e24\u4e2a\u540c\u4f34\u53bb\u89c1\u9996\u9886\uff0c\u5c82\u77e5\u4e00\u6478\u4e8c\u4eba\u7684\u8eab\u5b50\uff0c\u7adf\u662f\u6e10\u6e10\u51b0\u51b7\uff0c\u518d\u4e00\u63a2\u9f3b\u606f\uff0c\u5df2\u662f\u6c14\u7edd\u8eab\u6b7b\u3002 \u3000\u3000\u4e09\u4eba\u5927\u60ca\u4e4b\u4e0b\uff0c\u534a\u664c\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002\u4e00\u4e2a\u59d3\u5b8b\u7684\u8f83\u6709\u89c1\u8bc6\uff0c\u89e3\u5f00\u4e24\u4eba\u7684\u8863\u670d\u4e00\u770b\uff0c\u53ea\u89c1\u4e00\u4eba\u624b\u81c2\u4e0a\u6709\u4e00\u5757\u94b1\u5927\u9ed1\u5370\uff0c\u9ed1\u5370\u4e4b\u4e2d\uff0c\u6709\u4e2a\u7ec6\u5c0f\u7684\u9488\u5b54\uff0c\u53e6\u4e00\u4eba\u5374\u662f\u80f8\u53e3\u6709\u4e2a\u9ed1\u5370\u3002\u4ed6\u767b\u65f6\u7701\u609f\uff1a\u201c\u8fd9\u599e\u513f\u7528\u9488\u523a\u4eba\uff0c\u9488\u4e0a\u5582\u6709\u5267\u6bd2\u3002\u201d\u4e00\u4e2a\u59d3\u5168\u7684\u9053\uff1a\u201c\u90a3\u5c31\u4e0d\u6015\uff01\u54b1\u4eec\u8fdc\u8fdc\u7684\u7528\u6697\u9752\u5b50\u6253\uff0c\u4e0d\u8ba9\u8fd9\u5c0f\u8d31\u4eba\u8fd1\u8eab\u4fbf\u662f\u3002\u201d\u53e6\u4e00\u4e2a\u5f3a\u4eba\u59d3\u4e91\uff0c\u8bf4\u9053\uff1a\u201c\u77e5\u9053\u4e86\u5979\u7684\u9b3c\u8ba1\uff0c\u4fbf\u4e0d\u6015\u518d\u7740\u5979\u7684\u9053\u513f\uff01\u201d\u8bdd\u662f\u8fd9\u4e48\u8bf4\uff0c\u4e09\u4eba\u7ec8\u7a76\u4e0d\u6562\u6025\u8ffd\uff0c\u4e00\u9762\u5546\u91cf\uff0c\u4e00\u9762\u63d0\u5fc3\u540a\u80c6\u7684\u8ffd\u8fdb\u5c71\u8c37\u3002 \u3000\u3000\u674e\u6587\u79c0\u4e24\u9488\u594f\u529f\uff0c\u4e0d\u7981\u53c8\u60ca\u53c8\u559c\uff0c\u4f46\u4e5f\u77e5\u5176\u4f59\u4e09\u4eba\u5fc5\u4f1a\u53d1\u89c9\uff0c\u53ea\u8981\u6709\u4e86\u9632\u5907\uff0c\u51b3\u4e0d\u5bb9\u81ea\u5df1\u518d\u65bd\u6bd2\u9488\u3002\u7eb5\u9a6c\u6b63\u9003\u4e4b\u95f4\uff0c\u5ffd\u542c\u5f97\u5de6\u9996\u6709\u4eba\u53eb\u9053\uff1a\u201c\u5230\u8fd9\u513f\u6765\uff01\u201d\u6b63\u662f\u90a3\u8001\u4eba\u7684\u58f0\u97f3\u3002 \u3000\u3000\u674e\u6587\u79c0\u6025\u5fd9\u4e0b\u9a6c\uff0c\u542c\u90a3\u58f0\u97f3\u4ece\u4e00\u4e2a\u5c71\u6d1e\u4e2d\u4f20\u51fa\uff0c\u5f53\u5373\u5954\u8fdb\u3002\u90a3\u8001\u4eba\u7ad9\u5728\u6d1e\u53e3\uff0c\u95ee\uff1a\u201c\u600e\u4e48\u6837\uff1f\u201d\u674e\u6587\u79c0\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u523a\u4e2d\u4e86\u4e24\u4e2a\u2026\u2026\u4e24\u4e2a\u5f3a\u76d7\uff0c\u9003\u4e86\u51fa\u6765\u3002\u201d\u8001\u4eba\u9053\uff1a\u201c\u5f88\u597d\uff0c\u54b1\u4eec\u8fdb\u53bb\u3002\u201d\u8fdb\u6d1e\u540e\u53ea\u89c1\u5c71\u6d1e\u5f88\u6df1\uff0c\u674e\u6587\u79c0\u8ddf\u968f\u5728\u8001\u4eba\u4e4b\u540e\uff0c\u90a3\u5c71\u6d1e\u8d8a\u884c\u8d8a\u662f\u72ed\u7a84\u3002 \u3000\u3000\u884c\u4e86\u6570\u5341\u4e08\uff0c\u5c71\u6d1e\u8c41\u7136\u5f00\u6717\uff0c\u7adf\u53ef\u5bb9\u5f97\u4e00\u4e8c\u767e\u4eba\u3002\u8001\u4eba\u9053\uff1a\u201c\u54b1\u4eec\u5b88\u4f4f\u72ed\u7a84\u7684\u5165\u53e3\u4e4b\u5904\uff0c\u90a3\u4e09\u4e2a\u5f3a\u4eba\u4fbf\u4e0d\u6562\u8fdb\u6765\u3002\u8fd9\u53eb\u4e00\u592b\u5f53\u5173\uff0c\u4e07\u592b\u83ab\u5f00\u3002\u201d\u674e\u6587\u79c0\u6101\u9053\uff1a\u201c\u53ef\u662f\u54b1\u4eec\u4e5f\u8d70\u4e0d\u51fa\u53bb\u7684\u3002\u8fd9\u5c71\u6d1e\u91cc\u9762\u53e6\u6709\u901a\u9053\u4e48\uff1f\u201d\u8001\u4eba\u9053\uff1a\u201c\u901a\u9053\u662f\u6709\u7684\uff0c\u4e0d\u8fc7\u7ec8\u662f\u901a\u4e0d\u5230\u5c71\u5916\u53bb\u3002\u201d\u674e\u6587\u79c0\u60f3\u8d77\u9002\u624d\u4e4b\u4e8b\uff0c\u72b9\u662f\u5fc3\u6709\u4f59\u60b8\uff0c\u95ee\u9053\uff1a\u201c\u4f2f\u4f2f\uff0c\u90a3\u4e24\u4e2a\u5f3a\u76d7\u7ed9\u6211\u4e00\u523a\uff0c\u5ffd\u7136\u4e00\u52a8\u4e5f\u4e0d\u52a8\u4e86\uff0c\u96be\u9053\u5f53\u771f\u6b7b\u4e86\u4e48\uff1f\u201d\u8001\u4eba\u50b2\u7136\u9053\uff1a\u201c\u5728\u6211\u6bd2\u9488\u4e4b\u4e0b\uff0c\u5c82\u6709\u6d3b\u53e3\u7559\u4e0b\uff1f\u201d\u674e\u6587\u79c0\u4f38\u8fc7\u624b\u53bb\uff0c\u5c06\u6bd2\u9488\u9012\u7ed9\u4ed6\u3002\u8001\u4eba\u4f38\u624b\u6b32\u63a5\uff0c\u7a81\u7136\u53c8\u7f29\u56de\u4e86\u624b\uff0c\u9053\uff1a\u201c\u653e\u5728\u5730\u4e0b\u3002\u201d\u674e\u6587\u79c0\u4f9d\u8a00\u653e\u4e0b\u3002\u8001\u4eba\u9053\uff1a\u201c\u4f60\u9000\u5f00\u4e09\u6b65\u3002\u201d\u674e\u6587\u79c0\u89c9\u5f97\u5947\u602a\uff0c\u4fbf\u9000\u4e86\u4e09\u6b65\u3002\u90a3\u8001\u4eba\u8fd9\u624d\u4fef\u8eab\u62fe\u8d77\u6bd2\u9488\uff0c\u653e\u5165\u4e00\u4e2a\u9488\u7b52\u4e4b\u4e2d\u3002\u674e\u6587\u79c0\u8fd9\u624d\u660e\u767d\uff0c\u539f\u6765\u4ed6\u7591\u5fc3\u5f88\u91cd\uff0c\u9632\u5907\u81ea\u5df1\u7a81\u7136\u7528\u6bd2\u9488\u5bb3\u4ed6\u3002 \u3000\u3000\u90a3\u8001\u4eba\u9053\uff1a\u201c\u6211\u8ddf\u4f60\u7d20\u4e0d\u76f8\u8bc6\uff0c\u4e3a\u4ec0\u4e48\u521a\u624d\u4f60\u8ba9\u9a6c\u7ed9\u6211\uff0c\u8981\u6211\u72ec\u81ea\u9003\u547d\uff1f\u201d\u674e\u6587\u79c0\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u554a\u3002\u6211\u89c1\u4f60\u8eab\u4e0a\u6709\u75c5\uff0c\u6015\u5f3a\u76d7\u5bb3\u4f60\u3002\u201d\u90a3\u8001\u4eba\u8eab\u5b50\u6643\u4e86\u6643\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u8eab\u4e0a\u2026\u2026\u8eab\u4e0a\u6709\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u7a81\u7136\u95f4\u6ee1\u8138\u808c\u8089\u62bd\u52a8\uff0c\u795e\u60c5\u75db\u82e6\u4e0d\u582a\uff0c\u989d\u5934\u4e0d\u4f4f\u6e17\u51fa\u9ec4\u8c46\u822c\u5927\u7684\u6c57\u73e0\u6765\uff0c\u53c8\u8fc7\u4e00\u4f1a\uff0c\u5ffd\u7136\u5927\u53eb\u4e00\u58f0\uff0c\u5728\u5730\u4e0b\u6eda\u6765\u6eda\u53bb\uff0c\u9ad8\u58f0\u547b\u541f\u3002 \u3000\u3000\u674e\u6587\u79c0\u53ea\u5413\u5f97\u624b\u8db3\u65e0\u63aa\uff0c\u4f46\u89c1\u4ed6\u8eab\u5b50\u5f2f\u6210\u4e86\u5f13\u5f62\uff0c\u624b\u8db3\u75c9\u631b\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u662f\u80cc\u4e0a\u75db\u5f97\u5389\u5bb3\u4e48\uff1f\u201d\u4f38\u624b\u66ff\u4ed6\u8f7b\u8f7b\u6572\u51fb\u80cc\u5fc3\uff0c\u53c8\u5728\u4ed6\u81c2\u5f2f\u819d\u5f2f\u5173\u8282\u63a8\u62ff\u63c9\u62cd\u3002\u8001\u4eba\u75db\u695a\u6e10\u51cf\uff0c\u70b9\u5934\u793a\u8c22\uff0c\u8fc7\u4e86\u4e00\u70b7\u9999\u65f6\u5206\uff0c\u8fd9\u624d\u75bc\u75db\u6d88\u5931\uff0c\u7ad9\u4e86\u8d77\u6765\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u77e5\u9053\u6211\u662f\u8c01\uff1f\u201d\u674e\u6587\u79c0\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u201d\u8001\u4eba\u9053\uff1a\u201c\u6211\u662f\u6c49\u4eba\uff0c\u59d3\u534e\u540d\u8f89\uff0c\u6c5f\u5357\u4eba\u6c0f\uff0c\u6c5f\u6e56\u4e0a\u4eba\u79f0\u2018\u4e00\u6307\u9707\u6c5f\u5357\u2019\u7684\u4fbf\u662f\u3002\u201d \u3000\u3000\u674e\u6587\u79c0\u9053\uff1a\u201c\u5514\uff0c\u662f\u534e\u8001\u4f2f\u4f2f\u3002\u201d\u534e\u8f89\u9053\uff1a\u201c\u4f60\u6ca1\u542c\u89c1\u8fc7\u6211\u7684\u540d\u5934\u4e48\uff1f\u201d\u8a00\u4e0b\u5fae\u611f\u5931\u671b\uff0c\u5fc3\u60f3\u81ea\u5df1\u201c\u4e00\u6307\u9707\u6c5f\u5357\u201d\u534e\u8f89\u7684\u540d\u5934\u5f53\u5e74\u8f70\u52a8\u5927\u6c5f\u5357\u5317\uff0c\u6b66\u6797\u4e2d\u65e0\u4eba\u4e0d\u77e5\uff0c\u4f46\u77a7\u674e\u6587\u79c0\u7684\u795e\u60c5\uff0c\u7adf\u662f\u6beb\u65e0\u60ca\u5f02\u7684\u6a21\u6837\u3002 \u3000\u3000\u674e\u6587\u79c0\u9053\uff1a\u201c\u6211\u7239\u7239\u5988\u5988\u4e00\u5b9a\u77e5\u9053\u4f60\u7684\u540d\u5b57\uff0c\u6211\u5230\u56de\u7586\u6765\u65f6\u53ea\u6709\u516b\u5c81\uff0c\u4ec0\u4e48\u4e5f\u4e0d\u61c2\u3002\u201d\u534e\u8f89\u8138\u8272\u8f6c\u6109\uff0c\u9053\uff1a\u201c\u90a3\u5c31\u662f\u4e86\u3002\u4f60\u2026\u2026\u201d\u4e00\u53e5\u8bdd\u6ca1\u8bf4\u5b8c\uff0c\u5ffd\u542c\u6d1e\u5916\u5c71\u9053\u4e2d\u6709\u4eba\u8bf4\u9053\uff1a\u201c\u5b9a\u662f\u8eb2\u5728\u8fd9\u513f\uff0c\u5c0f\u5fc3\u5979\u7684\u6bd2\u9488\uff01\u201d\u8ddf\u7740\u811a\u6b65\u58f0\u54cd\uff0c\u4e09\u4e2a\u4eba\u4e00\u6b65\u4e00\u505c\u7684\u8fdb\u6765\u3002 \u3000"}),showOnHomepage:!0},{name:"\u56db",content:(0,a.jsx)(a.Fragment,{children:"\u534e\u8f89\u5fd9\u53d6\u51fa\u6bd2\u9488\uff0c\u5c06\u9488\u5c3e\u63d2\u5165\u6728\u6756\u7684\u6756\u5934\uff0c\u4ea4\u4e86\u7ed9\u5979\uff0c\u6307\u7740\u8fdb\u53e3\u4e4b\u5904\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u7b49\u4eba\u8fdb\u6765\u540e\u523a\u4ed6\u80cc\u5fc3\uff0c\u5343\u4e07\u4e0d\u53ef\u6027\u6025\u800c\u523a\u4ed6\u524d\u80f8\u3002\u201d \u3000\u3000\u674e\u6587\u79c0\u5fc3\u60f3\uff1a\u201c\u8fd9\u8fdb\u53e3\u5904\u5982\u6b64\u72ed\u7a84\uff0c\u4e58\u4ed6\u8fdb\u6765\u65f6\u523a\u4ed6\u524d\u80f8\uff0c\u4e0d\u662f\u6613\u4e2d\u5f97\u591a\u4e48\uff1f\u201d\u534e\u8f89\u89c1\u5979\u8138\u6709\u8fdf\u7591\u4e4b\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u751f\u6b7b\u5b58\u4ea1\uff0c\u5728\u6b64\u4e00\u523b\uff0c\u4f60\u6562\u4e0d\u542c\u6211\u7684\u8bdd\u4e48\uff1f\u201d\u8bf4\u8bdd\u58f0\u97f3\u867d\u8f7b\uff0c\u8bed\u6c14\u5374\u662f\u5341\u5206\u4e25\u5cfb\u3002\u4fbf\u5728\u6b64\u65f6\uff0c\u53ea\u89c1\u8fdb\u53e3\u5904\u4e00\u67c4\u660e\u6643\u6643\u7684\u957f\u5200\u4f38\u4e86\u8fdb\u6765\uff0c\u6025\u901f\u6325\u52a8\uff0c\u62a4\u4f4f\u4e86\u9762\u95e8\u524d\u80f8\uff0c\u4ee5\u9632\u654c\u4eba\u5077\u88ad\uff0c\u8ddf\u7740\u4fbf\u6709\u4e00\u4e2a\u9ed1\u5f71\u6162\u6162\u722c\u8fdb\uff0c\u5374\u662f\u90a3\u59d3\u4e91\u7684\u5f3a\u76d7\u3002 \u3000\u3000\u674e\u6587\u79c0\u8bb0\u7740\u534e\u8f89\u7684\u8bdd\uff0c\u7f29\u5728\u4e00\u65c1\uff0c\u4e1d\u6beb\u4e0d\u6562\u52a8\u5f39\u3002\u534e\u8f89\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u770b\u6211\u624b\u4e2d\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d\u4f38\u624b\u865a\u626c\u3002\u90a3\u59d3\u4e91\u7684\u4e00\u95ea\u8eab\uff0c\u6a2a\u5200\u8eab\u524d\uff0c\u51dd\u795e\u77a7\u7740\u4ed6\uff0c\u9632\u4ed6\u53d1\u5c04\u6697\u5668\u3002\u534e\u8f89\u559d\u9053\uff1a\u201c\u523a\u4ed6\uff01\u201d\u674e\u6587\u79c0\u624b\u8d77\u6756\u843d\uff0c\u6756\u5934\u5728\u4ed6\u80cc\u5fc3\u4e0a\u4e00\u70b9\uff0c\u6bd2\u9488\u5df2\u5165\u808c\u80a4\u3002\u90a3\u59d3\u4e91\u7684\u53ea\u89c9\u80cc\u4e0a\u5fae\u5fae\u4e00\u75db\uff0c\u4f3c\u4e4e\u88ab\u871c\u8702\u523a\u4e86\u4e00\u4e0b\uff0c\u5927\u53eb\u4e00\u58f0\uff0c\u5c31\u6b64\u50f5\u6bd9\u3002\u90a3\u59d3\u5168\u7684\u7d27\u968f\u5728\u540e\uff0c\u89c1\u4ed6\u53c8\u4e2d\u6bd2\u9488\u800c\u6b7b\uff0c\u53ea\u9053\u662f\u534e\u8f89\u624b\u53d1\u6bd2\u9488\uff0c\u53ea\u5413\u5f97\u9b42\u98de\u5929\u5916\uff0c\u4e0d\u53ca\u8f6c\u8eab\u9003\u547d\uff0c\u5012\u9000\u7740\u624b\u811a\u9f50\u65bd\u7684\u722c\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u534e\u8f89\u53f9\u9053\uff1a\u201c\u5018\u82e5\u6211\u6b66\u529f\u4e0d\u5931\uff0c\u533a\u533a\u4e94\u4e2a\u6bdb\u8d3c\uff0c\u4f55\u8db3\u9053\u54c9\uff01\u201d \u3000\u3000\u674e\u6587\u79c0\u5fc3\u60f3\u4ed6\u5916\u53f7\u201c\u4e00\u6307\u9707\u6c5f\u5357\u201d\uff0c\u81ea\u662f\u6b66\u529f\u6781\u5f3a\uff0c\u600e\u5730\u89c1\u4e86\u4e94\u4e2a\u5c0f\u5f3a\u76d7\uff0c\u7adf\u7136\u4e00\u70b9\u6cd5\u5b50\u4e5f\u6ca1\u6709\uff0c\u8bf4\u9053\uff1a\u201c\u534e\u4f2f\u4f2f\uff0c\u4f60\u56e0\u4e3a\u751f\u75c5\uff0c\u6240\u4ee5\u6b66\u529f\u65bd\u5c55\u4e0d\u51fa\uff0c\u662f\u4e48\uff1f\u201d\u534e\u8f89\u9053\uff1a\u201c\u4e0d\u662f\u7684\uff0c\u4e0d\u662f\u7684\u3002\u6211\u2026\u2026\u6211\u7acb\u8fc7\u91cd\u8a93\uff0c\u5018\u82e5\u4e0d\u5230\u751f\u6b7b\u5173\u5934\uff0c\u51b3\u4e0d\u8f7b\u6613\u65bd\u5c55\u6b66\u529f\u3002\u201d\u674e\u6587\u79c0\u201c\u55ef\u201d\u7684\u4e00\u58f0\uff0c\u89c9\u5f97\u4ed6\u8a00\u4e0d\u7531\u8877\uff0c\u521a\u624d\u660e\u660e\u8bf4\u201c\u6b66\u529f\u5df2\u5931\u201d\uff0c\u5374\u53c8\u652f\u543e\u63a9\u9970\uff0c\u4f46\u4ed6\u65e2\u4e0d\u80af\u8bf4\uff0c\u4e5f\u5c31\u4e0d\u4fbf\u8ffd\u95ee\u3002 \u3000\u3000\u534e\u8f89\u4e5f\u5bdf\u89c9\u81ea\u5df1\u8a00\u8bed\u4e2d\u6709\u4e86\u7834\u7efd\uff0c\u5f53\u5373\u5c94\u5f00\u8bdd\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u53eb\u4f60\u523a\u4ed6\u540e\u5fc3\uff0c\u4f60\u660e\u767d\u5176\u4e2d\u9053\u7406\u4e48\uff1f\u4ed6\u653b\u8fdb\u6d1e\u6765\uff0c\u5168\u795e\u9632\u5907\u7684\u662f\u9762\u524d\u654c\u4eba\uff0c\u4f60\u4e0d\u4f1a\u4ec0\u4e48\u6b66\u529f\uff0c\u88ad\u51fb\u4ed6\u6b63\u9762\u662f\u4e0d\u80fd\u5f97\u624b\u7684\u3002\u6211\u5f15\u5f97\u4ed6\u51dd\u795e\u63d0\u9632\u6211\uff0c\u4f60\u5728\u4ed6\u80cc\u5fc3\u4e00\u523a\uff0c\u81ea\u662f\u5e94\u624b\u800c\u4e2d\u3002\u201d \u3000\u3000\u674e\u6587\u79c0\u70b9\u5934\u9053\uff1a\u201c\u4f2f\u4f2f\u7684\u8ba1\u7b56\u5f88\u597d\u3002\u201d\u987b\u77e5\u534e\u8f89\u7684\u6c5f\u6e56\u9605\u5386\u4f55\u7b49\u4e30\u5bcc\uff0c\u8981\u6446\u5e03\u8fd9\u6837\u4e00\u4e2a\u5c0f\u6bdb\u8d3c\uff0c\u81ea\u662f\u6e38\u5203\u6709\u4f59\u3002 \u3000\u3000\u534e\u8f89\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u5927\u5757\u871c\u74dc\u7684\u74dc\u5e72\uff0c\u9012\u7ed9\u674e\u6587\u79c0\uff0c\u9053\uff1a\u201c\u5148\u5403\u4e00\u4e9b\u3002\u90a3\u4e24\u4e2a\u6bdb\u8d3c\u518d\u4e5f\u4e0d\u6562\u8fdb\u6765\u4e86\uff0c\u53ef\u662f\u54b1\u4eec\u4e5f\u4e0d\u80fd\u51fa\u53bb\u3002\u5f85\u6211\u60f3\u4e2a\u8ba1\u8f83\uff0c\u987b\u5f97\u4e00\u4e3e\u5c06\u4e24\u4eba\u6740\u4e86\u3002\u8981\u662f\u53ea\u6740\u4e00\u4eba\uff0c\u4f59\u4e0b\u90a3\u4eba\u5fc5\u5b9a\u9003\u53bb\u62a5\u8baf\uff0c\u5927\u961f\u4eba\u9a6c\u8ddf\u7740\u8d76\u6765\uff0c\u53ef\u5c31\u68d8\u624b\u5f97\u5f88\u3002\u201d \u3000\u3000\u674e\u6587\u79c0\u89c1\u4ed6\u601d\u8651\u5468\u8be6\uff0c\u667a\u8c0b\u4e30\u5bcc\uff0c\u53cd\u6b63\u81ea\u5df1\u51b3\u8ba1\u60f3\u4e0d\u51fa\u6bd4\u4ed6\u66f4\u9ad8\u660e\u7684\u6cd5\u5b50\uff0c\u90a3\u4e5f\u4e0d\u7528\u591a\u4f24\u8111\u7b4b\u4e86\uff0c\u4e8e\u662f\u9971\u9910\u4e86\u4e00\u987f\u74dc\u5e72\uff0c\u9760\u5728\u77f3\u58c1\u4e0a\u517b\u795e\u3002 \u3000\u3000\u7ea6\u83ab\u8fc7\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u674e\u6587\u79c0\u7a81\u7136\u95fb\u5230\u4e00\u9635\u7126\u81ed\uff0c\u8ddf\u7740\u4fbf\u54b3\u55fd\u8d77\u6765\u3002\u534e\u8f89\u9053\uff1a\u201c\u4e0d\u597d\uff01\u6bdb\u8d3c\u7528\u70df\u6765\u85b0\uff01\u5feb\u5835\u4f4f\u6d1e\u53e3\uff01\u201d\u674e\u6587\u79c0\u6367\u8d77\u5730\u4e0b\u7684\u6c99\u571f\u77f3\u5757\uff0c\u5835\u585e\u8fdb\u53e3\u4e4b\u5904\uff0c\u597d\u5728\u6d1e\u53e3\u751a\u5c0f\uff0c\u4e00\u5835\u4e4b\u4e0b\uff0c\u6d8c\u8fdb\u6d1e\u6765\u7684\u70df\u96fe\u4fbf\u5927\u4e3a\u51cf\u5c11\uff0c\u800c\u4e14\u5185\u6d1e\u751a\u5927\uff0c\u70df\u96fe\u5439\u8fdb\u6765\u4e4b\u540e\uff0c\u53c8\u4ece\u540e\u6d1e\u6563\u51fa\u3002 \u3000\u3000\u5982\u6b64\u53c8\u76f8\u6301\u826f\u4e45\uff0c\u4ece\u540e\u6d1e\u6620\u8fdb\u6765\u7684\u65e5\u5149\u8d8a\u6765\u8d8a\u4eae\uff0c\u4f3c\u4e4e\u5df2\u662f\u6b63\u5348\u3002\u7a81\u7136\u95f4\u534e\u8f89\u201c\u554a\u201d\u7684\u4e00\u58f0\u53eb\uff0c\u6454\u5012\u5728\u5730\uff0c\u53c8\u662f\u5168\u8eab\u62bd\u52a8\u8d77\u6765\u3002\u4f46\u8fd9\u65f6\u6bd4\u4e0a\u6b21\u4f3c\u4e4e\u66f4\u52a0\u75db\u695a\uff0c\u624b\u8db3\u72c2\u821e\uff0c\u7adf\u662f\u4e0d\u53ef\u6291\u5236\u3002\u674e\u6587\u79c0\u5fc3\u4e2d\u60ca\u614c\uff0c\u5fd9\u53c8\u8d70\u8fd1\u53bb\u7ed9\u4ed6\u63a8\u62ff\u63c9\u62cd\u3002\u534e\u8f89\u75db\u695a\u7a0d\u51cf\uff0c\u5598\u606f\u9053\uff1a\u201c\u59d1\u2026\u2026\u59d1\u5a18\uff0c\u8fd9\u4e00\u6b21\u6211\u53ea\u6015\u662f\u597d\u4e0d\u4e86\u5566\u3002\u201d\u674e\u6587\u79c0\u5b89\u6170\u9053\uff1a\u201c\u5feb\u522b\u8fd9\u822c\u60f3\uff0c\u4eca\u65e5\u9047\u5230\u5f3a\u4eba\uff0c\u4e0d\u514d\u52b3\u795e\uff0c\u4f11\u606f\u4e00\u4f1a\u4fbf\u597d\u4e86\u3002\u201d\u534e\u8f89\u6447\u5934\u9053\uff1a\u201c\u4e0d\u6210\uff0c\u4e0d\u6210\uff01\u6211\u53cd\u6b63\u8981\u6b7b\u4e86\uff0c\u6211\u8ddf\u4f60\u5b9e\u8bf4\uff0c\u6211\u662f\u540e\u5fc3\u7684\u7a74\u9053\u4e0a\u4e2d\u4e86\u2026\u2026\u4e2d\u4e86\u4e00\u679a\u6bd2\u9488\u3002\u201d \u3000\u3000\u674e\u6587\u79c0\u9053\uff1a\u201c\u554a\uff0c\u4f60\u4e2d\u4e86\u6bd2\u9488\uff0c\u51e0\u65f6\u4e2d\u7684\uff1f\u662f\u4eca\u5929\u4e48\uff1f\u201d\u534e\u8f89\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u4e2d\u4e86\u5341\u4e8c\u5e74\u5566\uff01\u201d\u674e\u6587\u79c0\u9a87\u9053\uff1a\u201c\u4e5f\u662f\u8fd9\u4e48\u5389\u5bb3\u7684\u6bd2\u9488\u4e48\uff1f\u201d\u534e\u8f89\u9053\uff1a\u201c\u4e00\u822c\u65e0\u5f02\u3002\u53ea\u662f\u6211\u8fd0\u529f\u62b5\u5fa1\uff0c\u6bd2\u6027\u53d1\u4f5c\u8f83\u6162\uff0c\u540e\u6765\u53c8\u670d\u4e86\u89e3\u836f\uff0c\u8fd9\u624d\u6328\u4e86\u4e00\u5341\u4e8c\u5e74\uff0c\u4f46\u5230\u4eca\u5929\uff0c\u90a3\u662f\u518d\u4e5f\u6328\u4e0d\u4e0b\u53bb\u4e86\u3002\u5509\uff01\u8eab\u4e0a\u7559\u7740\u8fd9\u679a\u9b3c\u9488\uff0c\u8fd9\u4e00\u5341\u4e8c\u5e74\u4e2d\uff0c\u6bcf\u5929\u603b\u8981\u5927\u75db\u4e24\u4e09\u573a\uff0c\u65e9\u77e5\u5982\u6b64\uff0c\u5012\u662f\u5f53\u65e5\u4e0d\u670d\u89e3\u836f\u7684\u597d\uff0c\u591a\u75db\u8fd9\u4e00\u5341\u4e8c\u5e74\uff0c\u5230\u5934\u6765\u53c8\u6709\u4ec0\u4e48\u597d\u5904\uff1f\u201d \u3000\u3000\u674e\u6587\u79c0\u80f8\u53e3\u4e00\u9707\uff0c\u8fd9\u53e5\u8bdd\u52fe\u8d77\u4e86\u5979\u7684\u5fc3\u4e8b\u3002\u5341\u5e74\u524d\u5018\u82e5\u8ddf\u7740\u7239\u7239\u5988\u5988\u4e00\u8d77\u6b7b\u5728\u5f3a\u4eba\u624b\u4e2d\uff0c\u540e\u6765\u4e5f\u5c11\u53d7\u8bb8\u591a\u82e6\u695a\u3002 \u3000\u3000\u7136\u800c\u8fd9\u5341\u5e74\u4e4b\u4e2d\uff0c\u90fd\u662f\u82e6\u695a\u4e48\uff1f\u4e0d\uff0c\u4e5f\u6709\u8fc7\u5feb\u6d3b\u7684\u65f6\u5019\u3002 \u3000\u3000\u5341\u4e03\u516b\u5c81\u7684\u5e74\u8f7b\u59d1\u5a18\uff0c\u867d\u7136\u5bc2\u5bde\u4f24\u5fc3\uff0c\u82b1\u4e00\u822c\u7684\u5e74\u6708\u4e4b\u4e2d\uff0c\u603b\u662f\u6709\u4e0d\u5c11\u7684\u6b22\u7b11\u548c\u751c\u871c\u3002 \u3000\u3000\u53ea\u89c1\u534e\u8f89\u54ac\u7d27\u7259\u5173\uff0c\u7aed\u529b\u5fcd\u53d7\u5168\u8eab\u7684\u75bc\u75db\uff0c\u674e\u6587\u79c0\u9053\uff1a\u201c\u4f2f\u4f2f\uff0c\u4f60\u8bbe\u6cd5\u628a\u6bd2\u9488\u62d4\u4e86\u51fa\u6765\uff0c\u8bf4\u4e0d\u5b9a\u4f1a\u597d\u4e9b\u3002\u201d\u534e\u8f89\u65a5\u9053\uff1a\u201c\u5e9f\u8bdd\uff01\u8fd9\u8c01\u4e0d\u77e5\u9053\uff1f\u6211\u72ec\u4e2a\u513f\u5728\u8fd9\u8352\u5c71\u4e4b\u4e2d\uff0c\u6709\u8c01\u6765\u8ddf\u6211\u62d4\u9488\uff1f\u8fdb\u5c71\u6765\u7684\u5c31\u6ca1\u4e00\u4e2a\u5b89\u7740\u597d\u5fc3\uff0c\u54fc\uff0c\u54fc\u2026\u2026\u201d\u674e\u6587\u79c0\u6ee1\u8179\u7591\u56e2\uff1a\u201c\u4ed6\u4e3a\u4ec0\u4e48\u4e0d\u5230\u5916\u9762\u53bb\u6c42\u4eba\u533b\u6cbb\uff0c\u4e00\u4e2a\u4eba\u5728\u8fd9\u8352\u5c71\u4e2d\u4e00\u4f4f\u4fbf\u662f\u5341\u4e8c\u5e74\uff0c\u6709\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u663e\u89c1\u4ed6\u5bf9\u81ea\u5df1\u8fd8\u662f\u5b58\u7740\u6781\u5927\u7684\u731c\u7591\u63d0\u9632\u4e4b\u5fc3\uff0c\u4f46\u773c\u770b\u4ed6\u75db\u5f97\u5b9e\u5728\u53ef\u601c\uff0c\u8bf4\u9053\uff1a\u201c\u4f2f\u4f2f\uff0c\u6211\u6765\u8bd5\u8bd5\u3002\u4f60\u653e\u5fc3\uff0c\u6211\u51b3\u4e0d\u4f1a\u5bb3\u4f60\u3002\u201d \u3000\u3000\u534e\u8f89\u51dd\u89c6\u7740\u5979\uff0c\u53cc\u7709\u7d27\u9501\uff0c\u5fc3\u4e2d\u8f6c\u8fc7\u4e86\u65e0\u6570\u5ff5\u5934\uff0c\u4f3c\u4e4e\u59cb\u7ec8\u6253\u4e0d\u5b9a\u4e3b\u610f\u3002\u674e\u6587\u79c0\u62d4\u4e0b\u6756\u5934\u4e0a\u7684\u6bd2\u9488\uff0c\u9012\u4e86\u7ed9\u4ed6\uff0c\u9053\uff1a\u201c\u8ba9\u6211\u77a7\u77a7\u4f60\u80cc\u4e0a\u7684\u4f24\u75d5\u3002\u82e5\u662f\u4f60\u89c1\u6211\u5fc3\u5b58\u4e0d\u826f\uff0c\u4f60\u4fbf\u7528\u6bd2\u9488\u523a\u6211\u5427\uff01\u201d\u534e\u8f89\u9053\uff1a\u201c\u597d\uff01\u201d\u89e3\u5f00\u8863\u886b\uff0c\u9732\u51fa\u80cc\u5fc3\u3002\u674e\u6587\u79c0\u4e00\u770b\u4e4b\u4e2d\uff0c\u5fcd\u4e0d\u4f4f\u4f4e\u58f0\u60ca\u547c\uff0c\u4f46\u89c1\u4ed6\u80cc\u4e0a\u70b9\u70b9\u6591\u6591\uff0c\u4e0d\u77e5\u6709\u51e0\u5343\u767e\u5904\u4f24\u75a4\u3002\u534e\u8f89\u9053\uff1a\u201c\u6211\u5343\u65b9\u767e\u8ba1\u8981\u6316\u6bd2\u9488\u51fa\u6765\uff0c\u603b\u662f\u53d6\u4e0d\u51fa\u3002\u201d \u3000\u3000\u8fd9\u4e9b\u4f24\u75a4\u6709\u7684\u4f3c\u4e4e\u662f\u5728\u5c16\u77f3\u4e0a\u649e\u7834\u7684\uff0c\u6709\u7684\u4f3c\u4e4e\u662f\u7528\u6307\u5c16\u786c\u751f\u751f\u525c\u7834\u7684\uff0c\u674e\u6587\u79c0\u77a7\u7740\u8fd9\u4e9b\u4f24\u75a4\uff0c\u60f3\u8d77\u8fd9\u5341\u4e8c\u5e74\u6765\u4ed6\u4e0d\u77e5\u53d7\u5c3d\u4e86\u591a\u5c11\u6298\u78e8\uff0c\u5fc3\u4e0b\u5927\u662f\u607b\u7136\uff0c\u95ee\u9053\uff1a\u201c\u90a3\u6bd2\u9488\u523a\u5728\u54ea\u91cc\uff1f\u201d\u534e\u8f89\u9053\uff1a\u201c\u4e00\u5171\u6709\u4e09\u679a\uff0c\u4e00\u5728\u2018\u9b44\u6237\u7a74\u2019\uff0c\u4e00\u5728\u2018\u5fd7\u5ba4\u7a74\u2019\uff0c\u4e00\u5728\u2018\u81f3\u9633\u7a74\u2019\u3002\u201d\u4e00\u9762\u8bf4\uff0c\u4e00\u9762\u53cd\u624b\u6307\u70b9\u6bd2\u9488\u523a\u5165\u7684\u90e8\u4f4d\uff0c\u53ea\u56e0\u65f6\u65e5\u76f8\u9694\u5df2\u4e45\uff0c\u53c8\u662f\u6ee1\u80cc\u4f24\u75a4\uff0c\u65e9\u5df2\u77a7\u4e0d\u51fa\u9488\u5b54\u7684\u6240\u5728\u3002 \u3000\u3000\u674e\u6587\u79c0\u60ca\u9053\uff1a\u201c\u5171\u6709\u4e09\u679a\u4e48\uff1f\u4f60\u8bf4\u662f\u4e2d\u4e86\u4e00\u679a\uff1f\u201d\u534e\u8f89\u6012\u9053\uff1a\u201c\u5148\u524d\u4f60\u53c8\u6ca1\u8bf4\u8981\u7ed9\u6211\u62d4\u9488\uff0c\u6211\u4f55\u5fc5\u8ddf\u4f60\u8bf4\u5b9e\u8bdd\uff1f\u201d\u674e\u6587\u79c0\u77e5\u4ed6\u731c\u5fcc\u4e4b\u5fc3\u6781\u91cd\uff0c\u5b9e\u5219\u662f\u4e2d\u4e86\u4e09\u679a\u6bd2\u9488\u540e\u6b66\u529f\u5168\u5931\uff0c\u751f\u6015\u81ea\u5df1\u52a0\u5bb3\u4e8e\u4ed6\uff0c\u6545\u610f\u8bf4\u66fe\u7ecf\u53d1\u4e0b\u91cd\u8a93\uff0c\u4e0d\u5f97\u8f7b\u6613\u52a8\u6b66\uff0c\u4fbf\u662f\u6240\u4e2d\u6bd2\u9488\u4e4b\u6570\uff0c\u4e5f\u662f\u5c11\u8bf4\u4e86\u4e24\u679a\uff0c\u90a3\u4e48\u81ea\u5df1\u5982\u6709\u5bb3\u4ed6\u4e4b\u610f\uff0c\u4e5f\u53ef\u591a\u4e00\u4e9b\u987e\u5fcc\u3002\u5979\u5b9e\u5728\u4e0d\u559c\u4ed6\u8fd9\u4e9b\u673a\u8bc8\u7591\u5fcc\u7684\u7528\u5fc3\uff0c\u4f46\u60f3\u6551\u4eba\u6551\u5230\u5e95\uff0c\u8fd9\u8001\u4eba\u4e5f\u5b9e\u5728\u53ef\u601c\uff0c\u4e00\u65f6\u4e5f\u7406\u4f1a\u4e0d\u5f97\u8fd9\u8bb8\u591a\uff0c\u5fc3\u4e2d\u6c89\u541f\uff0c\u76d8\u7b97\u5982\u4f55\u66ff\u4ed6\u62d4\u51fa\u6df1\u5165\u808c\u8089\u4e2d\u7684\u6bd2\u9488\u3002 \u3000\u3000\u534e\u8f89\u95ee\u9053\uff1a\u201c\u4f60\u77a7\u6e05\u695a\u4e86\u5427\uff1f\u201d\u674e\u6587\u79c0\u9053\uff1a\u201c\u6211\u77a7\u4e0d\u89c1\u9488\u5c3e\uff0c\u4f60\u8bf4\u8be5\u5f53\u600e\u6837\u62d4\u624d\u597d\uff1f\u201d\u534e\u8f89\u9053\uff1a\u201c\u987b\u5f97\u7528\u5229\u5668\u5256\u5f00\u808c\u8089\uff0c\u65b9\u80fd\u89c1\u5230\u3002\u6bd2\u9488\u6df1\u5165\u6570\u5bf8\uff0c\u5f88\u96be\u5bfb\u7740\u3002\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u58f0\u97f3\u5df2\u662f\u53d1\u98a4\u3002\u674e\u6587\u79c0\u9053\uff1a\u201c\u55ef\uff0c\u53ef\u60dc\u6211\u6ca1\u5e26\u7740\u5c0f\u5200\u3002\u201d\u534e\u8f89\u9053\uff1a\u201c\u6211\u4e5f\u6ca1\u5200\u5b50\u3002\u201d\u5ffd\u7136\u6307\u7740\u5730\u4e0b\u6454\u7740\u7684\u90a3\u67c4\u957f\u5200\u8bf4\u9053\uff1a\u201c\u5c31\u7528\u8fd9\u67c4\u5200\u597d\u4e86\uff01\u201d\u90a3\u957f\u5200\u9752\u5149\u95ea\u95ea\uff0c\u751a\u662f\u950b\u9510\uff0c\u6a2a\u5728\u90a3\u59d3\u4e91\u7684\u5f3a\u4eba\u8eab\u65c1\uff0c\u6b64\u65f6\u4eba\u4ea1\u5200\u5728\uff0c\u4f46\u4ecd\u662f\u4ee4\u4eba\u89c1\u4e4b\u751f\u60e7\u3002 \u3000\u3000\u674e\u6587\u79c0\u89c1\u8981\u7528\u8fd9\u6837\u4e00\u67c4\u957f\u5200\u5256\u5272\u4ed6\u7684\u80cc\u5fc3\uff0c\u5927\u4e3a\u8fdf\u7591\u3002\u534e\u8f89\u731c\u77e5\u4e86\u5979\u7684\u5fc3\u610f\uff0c\u8bed\u8f6c\u6e29\u548c\uff0c\u8bf4\u9053\uff1a\u201c\u674e\u59d1\u5a18\uff0c\u4f60\u53ea\u987b\u52a9\u6211\u62d4\u51fa\u6bd2\u9488\uff0c\u6211\u8981\u7ed9\u4f60\u8bb8\u8bb8\u591a\u591a\u91d1\u94f6\u73e0\u5b9d\u3002\u6211\u4e0d\u9a97\u4f60\uff0c\u771f\u7684\u662f\u8bb8\u8bb8\u591a\u591a\u91d1\u94f6\u73e0\u5b9d\u3002\u201d\u674e\u6587\u79c0\u9053\uff1a\u201c\u6211\u4e0d\u8981\u91d1\u94f6\u73e0\u5b9d\uff0c\u4e5f\u4e0d\u7528\u4f60\u8c22\u3002\u53ea\u8981\u4f60\u8eab\u4e0a\u4e0d\u75db\uff0c\u90a3\u5c31\u597d\u4e86\u3002\u201d\u534e\u8f89\u9053\uff1a\u201c\u597d\u5427\uff0c\u90a3\u4f60\u5feb\u4e9b\u52a8\u624b\u3002\u201d \u3000\u3000\u674e\u6587\u79c0\u8fc7\u53bb\u62fe\u8d77\u957f\u5200\uff0c\u5728\u90a3\u59d3\u4e91\u5f3a\u4eba\u8863\u670d\u4e0a\u5272\u6495\u4e0b\u5341\u51e0\u6761\u5e03\u6761\uff0c\u4ee5\u5907\u6b62\u8840\u548c\u88f9\u624e\u4f24\u53e3\uff0c\u8bf4\u9053\uff1a\u201c\u4f2f\u4f2f\uff0c\u6211\u662f\u5c3d\u529b\u800c\u4e3a\uff0c\u4f60\u5fcd\u4e00\u5fcd\u75db\u3002\u201d\u54ac\u7d27\u7259\u5173\uff0c\u4ee5\u5200\u5c16\u5bf9\u51c6\u4e86\u4ed6\u6240\u6307\u70b9\u7684\u201c\u9b44\u6237\u7a74\u201d\u65c1\u6570\u5206\u4e4b\u5904\uff0c\u8f7b\u8f7b\u4e00\u5272\u3002 \u3000\u3000\u5200\u5165\u808c\u8089\uff0c\u9c9c\u8840\u8ff8\u6d41\uff0c\u534e\u8f89\u7adf\u662f\u54fc\u4e5f\u6ca1\u54fc\u4e00\u58f0\uff0c\u95ee\u9053\uff1a\u201c\u89c1\u5230\u4e86\u5417\uff1f\u201d\u8fd9\u5341\u4e8c\u5e74\u4e2d\u4ed6\u71ac\u60ef\u4e86\u75db\u695a\uff0c\u5bf9\u8fd9\u5229\u5203\u4e00\u5272\uff0c\u7adf\u662f\u4e1d\u6beb\u4e0d\u4ee5\u4e3a\u610f\u3002\u674e\u6587\u79c0\u4ece\u5934\u4e0a\u62d4\u4e0b\u53d1\u7c2a\uff0c\u5728\u4f24\u53e3\u4e2d\u4e00\u63a2\uff0c\u679c\u7136\u63a2\u5230\u4e00\u679a\u7ec6\u9488\uff0c\u7262\u7262\u7684\u9489\u5728\u9aa8\u4e2d\u3002 \u3000\u3000\u5979\u4e24\u6839\u624b\u6307\u4f38\u8fdb\u4f24\u53e3\uff0c\u634f\u4f4f\u9488\u5c3e\uff0c\u7528\u529b\u4e00\u62c9\uff0c\u624b\u6307\u6ed1\u8131\uff0c\u6bd2\u9488\u5374\u62d4\u4e0d\u51fa\u6765\uff0c\u76f4\u62d4\u5230\u7b2c\u56db\u4e0b\uff0c\u624d\u5c06\u6bd2\u9488\u62d4\u51fa\u3002\u534e\u8f89\u5927\u53eb\u4e00\u58f0\uff0c\u75db\u5f97\u6655\u4e86\u8fc7\u53bb\u3002\u674e\u6587\u79c0\u5fc3\u60f3\uff1a\u201c\u4ed6\u6655\u4e86\u8fc7\u53bb\uff0c\u5012\u53ef\u5c11\u53d7\u4e9b\u75db\u695a\u3002\u201d\u5256\u8089\u53d6\u9488\uff0c\u8ddf\u7740\u5c06\u53e6\u5916\u4e24\u679a\u6bd2\u9488\u62d4\u51fa\uff0c\u7528\u5e03\u6761\u7ed9\u4ed6\u88f9\u624e\u4f24\u53e3\u3002 \u3000\u3000\u8fc7\u4e86\u597d\u4e00\u4f1a\uff0c\u534e\u8f89\u624d\u60a0\u60a0\u9192\u8f6c\uff0c\u4e00\u7741\u5f00\u773c\uff0c\u4fbf\u89c1\u9762\u524d\u653e\u7740\u4e09\u679a\u4e4c\u9ed1\u7684\u6bd2\u9488\uff0c\u6068\u6068\u7684\u9053\uff1a\u201c\u9b3c\u9488\uff0c\u8d3c\u9488\uff01\u4f60\u4eec\u5728\u6211\u8089\u91cc\u5f85\u4e86\u5341\u4e8c\u5e74\uff0c\u4eca\u65e5\u603b\u7b97\u51fa\u6765\u4e86\u7f62\u3002\u201d\u5411\u674e\u6587\u79c0\u9053\uff1a\u201c\u674e\u59d1\u5a18\uff0c\u4f60\u6551\u6211\u6027\u547d\uff0c\u8001\u592b\u65e0\u4ee5\u4e3a\u62a5\uff0c\u4fbf\u5c06\u8fd9\u4e09\u679a\u6bd2\u9488\u8d60\u9001\u4e8e\u4f60\u3002\u8fd9\u4e09\u679a\u6bd2\u9488\u867d\u5728\u6211\u4f53\u5185\u6f5c\u4f0f\u4e00\u5341\u4e8c\u5e74\uff0c\u6bd2\u6027\u4f9d\u7136\u5c1a\u5728\u3002\u201d\u674e\u6587\u79c0\u6447\u5934\u9053\uff1a\u201c\u6211\u4e0d\u8981\u3002\u201d\u534e\u8f89\u5947\u9053\uff1a\u201c\u6bd2\u9488\u7684\u5a01\u529b\uff0c\u4f60\u4eb2\u773c\u89c1\u8fc7\u4e86\u3002 \u3000\u3000\u4f60\u6709\u6b64\u4e00\u9488\u5728\u624b\uff0c\u8c01\u90fd\u4f1a\u6015\u4f60\u4e09\u5206\u3002\u201d\u674e\u6587\u79c0\u4f4e\u58f0\u9053\uff1a\u201c\u6211\u4e0d\u8981\u522b\u4eba\u6015\u6211\u3002\u201d\u5979\u5fc3\u4e2d\u5374\u662f\u60f3\u8bf4\uff1a\u201c\u6211\u53ea\u8981\u522b\u4eba\u559c\u6b22\u6211\uff0c\u8fd9\u6bd2\u9488\u53ef\u65e0\u80fd\u4e3a\u529b\u3002\u201d \u3000\u3000\u6bd2\u9488\u53d6\u51fa\u540e\uff0c\u534e\u8f89\u867d\u56e0\u6d41\u8840\u751a\u591a\uff0c\u5341\u5206\u865a\u5f31\uff0c\u4f46\u5fc3\u60c5\u7545\u5feb\uff0c\u7cbe\u795e\u5065\u65fa\uff0c\u95ed\u76ee\u5b89\u7761\u4e86\u4e00\u4e2a\u591a\u65f6\u8fb0\u3002\u7761\u68a6\u4e2d\u5ffd\u542c\u5f97\u6709\u4eba\u5927\u58f0\u5492\u9a82\uff0c\u4ed6\u4e00\u60ca\u800c\u9192\uff0c\u53ea\u542c\u5f97\u90a3\u59d3\u5b8b\u7684\u5f3a\u4eba\u5728\u6d1e\u5916\u6c61\u8a00\u79fd\u8bed\u7684\u8fb1\u9a82\uff0c\u6240\u8bf4\u7684\u8a00\u8bcd\u6076\u6bd2\u4e0d\u582a\u3002\u663e\u662f\u4ed6\u4e0d\u6562\u8fdb\u6765\uff0c\u5374\u662f\u8981\u6fc0\u654c\u4eba\u51fa\u53bb\u3002\u534e\u8f89\u8d8a\u542c\u8d8a\u6012\uff0c\u7ad9\u8d77\u8eab\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4f53\u5185\u6bd2\u9488\u5df2\u53bb\uff0c\u4e00\u6307\u9707\u6c5f\u5357\u8fd8\u60e7\u6015\u533a\u533a\u4e24\u4e2a\u6bdb\u8d3c\uff1f\u201d\u4f46\u4e00\u52a0\u8fd0\u6c14\uff0c\u52b2\u529b\u7adf\u662f\u63d0\u4e0d\u4e0a\u6765\uff0c\u53f9\u9053\uff1a\u201c\u6bd2\u9488\u5728\u6211\u4f53\u5185\u505c\u7559\u8fc7\u4e45\uff0c\u770b\u6765\u4e09\u56db\u4e2a\u6708\u5185\u6b66\u529f\u96be\u590d\u3002\u201d\u8033\u542c\u90a3\u5f3a\u76d7\u201c\u5343\u8001\u8d3c\uff0c\u4e07\u8001\u8d3c\u201d\u7684\u72e0\u9a82\uff0c\u6012\u9053\uff1a\u201c\u96be\u9053\u6211\u8981\u7b49\u4f60\u8fb1\u9a82\u6570\u6708\uff0c\u518d\u6765\u5bb0\u4f60\uff1f\u201d\u53c8\u60f3\uff1a\u201c\u4ed6\u4eec\u82e5\u662f\u59cb\u7ec8\u4e0d\u6562\u8fdb\u6d1e\uff0c\u518d\u50f5\u4e0b\u53bb\uff0c\u7ec8\u4e8e\u56de\u53bb\u642c\u4e86\u5927\u6279\u5e2e\u624b\u524d\u6765\uff0c\u90a3\u53ef\u7cdf\u4e86\u3002\u8fd9\u4fbf\u5982\u4f55\u662f\u597d\uff1f\u201d \u3000\u3000\u7a81\u7136\u95f4\u5fc3\u5ff5\u4e00\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u674e\u59d1\u5a18\uff0c\u6211\u6765\u6559\u4f60\u4e00\u8def\u6b66\u529f\uff0c\u4f60\u51fa\u53bb\u5c06\u8fd9\u4e24\u4e2a\u6bdb\u8d3c\u6536\u62fe\u4e86\u3002\u201d\u674e\u6587\u79c0\u9053\uff1a\u201c\u8981\u591a\u4e45\u624d\u80fd\u5b66\u4f1a\uff1f \u3000\u3000\u6ca1\u8fd9\u4e48\u5feb\u5427\u3002\u201d\u534e\u8f89\u6c89\u541f\u9053\uff1a\u201c\u82e5\u662f\u6559\u4f60\u72ec\u6307\u70b9\u7a74\u3001\u5200\u6cd5\u62f3\u6cd5\uff0c\u81f3\u5c11\u4e5f\u5f97\u534a\u5e74\u624d\u80fd\u594f\u529f\uff0c\u773c\u524d\u975e\u901f\u6210\u4e0d\u53ef\uff0c\u90a3\u53ea\u6709\u7ec3\u89c1\u529f\u6781\u5feb\u7684\u65c1\u95e8\u5175\u5203\uff0c\u5fc5\u987b\u4e00\u4e24\u62db\u95f4\u4fbf\u80fd\u53d6\u80dc\u3002\u53ea\u662f\u8fd9\u5c71\u6d1e\u4e4b\u4e2d\uff0c\u54ea\u91cc\u53bb\u627e\u4ec0\u4e48\u504f\u95e8\u7684\u5175\u5668\uff1f\u201d\u4e00\u62ac\u5934\u95f4\uff0c\u7a81\u7136\u559c\u9053\uff1a\u201c\u6709\u4e86\uff0c\u53bb\u628a\u90a3\u8fb9\u7684\u846b\u82a6\u6458\u4e24\u4e2a\u4e0b\u6765\uff0c\u8981\u8fde\u7740\u957f\u85e4\uff0c\u54b1\u4eec\u6765\u7ec3\u6d41\u661f\u9524\u3002\u201d \u3000\u3000\u674e\u6587\u79c0\u89c1\u5c71\u6d1e\u900f\u5149\u5165\u6765\u4e4b\u5904\uff0c\u60ac\u7740\u5341\u6765\u4e2a\u67af\u840e\u5df2\u4e45\u7684\u846b\u82a6\uff0c\u4e0d\u77e5\u662f\u54ea\u4e00\u5e74\u751f\u5728\u90a3\u91cc\u7684\uff0c\u4e8e\u662f\u7528\u5200\u8fde\u85e4\u5272\u4e86\u4e24\u4e2a\u4e0b\u6765\u3002 \u3000\u3000\u534e\u8f89\u9053\uff1a\u201c\u5f88\u597d\uff01\u4f60\u7528\u5200\u5728\u846b\u82a6\u85e4\u4e0a\u6316\u4e00\u4e2a\u5b54\uff0c\u704c\u6c99\u8fdb\u53bb\uff0c\u518d\u7528\u846b\u82a6\u85e4\u585e\u4f4f\u4e86\u5c0f\u5b54\u3002\u201d\u674e\u6587\u79c0\u4f9d\u8a00\u800c\u4e3a\u3002\u4e24\u4e2a\u846b\u82a6\u4e2d\u704c\u6ee1\u4e86\u6c99\uff0c\u6bcf\u4e2a\u90fd\u6709\u4e03\u516b\u65a4\u91cd\uff0c\u679c\u7136\u662f\u4e00\u5bf9\u6d41\u661f\u9524\u6a21\u6837\u3002\u534e\u8f89\u63a5\u5728\u624b\u4e2d\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5148\u6559\u4f60\u4e00\u62db\u2018\u661f\u6708\u4e89\u8f89\u2019\u3002\u201d\u5f53\u4e0b\u63d0\u8d77\u4e00\u5bf9\u846b\u82a6\u6d41\u661f\u9524\uff0c\u6162\u6162\u7684\u7ec3\u4e86\u4e00\u4e2a\u59ff\u52bf\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u201c\u661f\u6708\u4e89\u8f89\u201d\u5de6\u9524\u6253\u654c\u80f8\u8179\u4e4b\u4ea4\u7684\u201c\u5546\u66f2\u7a74\u201d\uff0c\u53f3\u9524\u5148\u7eb5\u540e\u6536\uff0c\u5f2f\u8fc7\u6765\u6253\u654c\u4eba\u80cc\u5fc3\u7684\u201c\u7075\u53f0\u7a74\u201d\uff0c\u867d\u53ea\u4e00\u62db\uff0c\u4f46\u5176\u4e2d\u5305\u542b\u7740\u624b\u52b2\u773c\u529b\u3001\u8361\u9524\u8ba4\u7a74\u7684\u5404\u79cd\u6cd5\u95e8\uff0c\u53c8\u8981\u63d0\u9632\u654c\u4eba\u5de6\u53f3\u95ea\u907f\uff0c\u501f\u52bf\u53cd\u51fb\uff0c\u56e0\u6b64\u674e\u6587\u79c0\u8db3\u8db3\u5b66\u4e86\u4e00\u4e2a\u591a\u65f6\u8fb0\uff0c\u65b9\u59cb\u51fa\u9524\u65e0\u8bef\u3002 \u3000\u3000\u5979\u62b9\u4e86\u62b9\u989d\u5934\u6c57\u6c34\uff0c\u6b49\u7136\u9053\uff1a\u201c\u6211\u771f\u7b28\uff0c\u5b66\u4e86\u8fd9\u4e48\u4e45\uff01\u201d\u534e\u8f89\u9053\uff1a\u201c\u4f60\u4e00\u70b9\u4e5f\u4e0d\u7b28\uff0c\u53ef\u8bf4\u662f\u806a\u660e\u5f97\u5f88\u3002\u4f60\u522b\u5c0f\u89d1\u8fd9\u4e00\u62db\u2018\u661f\u6708\u4e89\u8f89\u2019\uff0c\u867d\u662f\u504f\u95e8\u529f\u592b\uff0c\u4f46\u53d8\u5316\u5947\u5e7b\uff0c\u5927\u6709\u5a01\u529b\uff0c\u5bfb\u5e38\u4eba\u5b66\u5b83\u5341\u5929\u516b\u5929\uff0c\u4e5f\u672a\u6709\u4f60\u8fd9\u822c\u6210\u5c31\u5462\u3002\u4ee5\u4e4b\u5bf9\u4ed8\u6b66\u6797\u597d\u624b\uff0c\u5355\u662f\u4e00\u62db\u81ea\u4e0d\u4e2d\u7528\uff0c\u4f46\u8981\u6253\u5012\u4e24\u4e2a\u6bdb\u8d3c\uff0c\u5374\u5df2\u7ef0\u7ef0\u6709\u4f59\u3002\u4f60\u4f11\u606f\u4e00\u4f1a\uff0c\u4fbf\u51fa\u53bb\u5bb0\u4e86\u4ed6\u4eec\u5427\u3002\u201d \u3000\u3000\u674e\u6587\u79c0\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u53ea\u662f\u8fd9\u4e00\u62db\u4fbf\u6210\u4e86\uff1f\u201d\u534e\u8f89\u7b11\u9053\uff1a\u201c\u6211\u867d\u53ea\u6559\u4f60\u4e00\u62db\uff0c\u4f60\u603b\u7b97\u5df2\u662f\u6211\u7684\u5f1f\u5b50\uff0c\u4e00\u6307\u9707\u6c5f\u5357\u7684\u5f1f\u5b50\uff0c\u5bf9\u4ed8\u4e24\u4e2a\u5c0f\u6bdb\u8d3c\uff0c\u8fd8\u8981\u7528\u4e24\u62db\u4e48\uff1f\u4f60\u4e5f\u4e0d\u6015\u635f\u4e86\u5e08\u7236\u7684\u5a01\u540d\uff1f\u201d \u3000\u3000\u674e\u6587\u79c0\u5e94\u9053\uff1a\u201c\u662f\u3002\u201d\u534e\u8f89\u9053\uff1a\u201c\u4f60\u4e0d\u60f3\u62dc\u6211\u4e3a\u5e08\u4e48\uff1f\u201d\u674e\u6587\u79c0\u5b9e\u5728\u4e0d\u60f3\u62dc\u4ec0\u4e48\u5e08\u7236\uff0c\u4e0d\u7531\u5f97\u8fdf\u8fdf\u4e0d\u7b54\uff0c\u4f46\u89c1\u4ed6\u8138\u8272\u6781\u662f\u5931\u671b\uff0c\u5230\u540e\u6765\u66f4\u4f3c\u9887\u4e3a\u4f24\u5fc3\uff0c\u751a\u611f\u4e0d\u5fcd\uff0c\u4e8e\u662f\u8dea\u4e0b\u6765\u62dc\u4e86\u51e0\u62dc\uff0c\u53eb\u9053\uff1a\u201c\u5e08\u7236\u3002\u201d \u3000\u3000\u534e\u8f89\u53c8\u662f\u559c\u6b22\uff0c\u53c8\u662f\u96be\u8fc7\uff0c\u6006\u7136\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u6211\u4e5d\u6b7b\u4e4b\u4f59\uff0c\u8fd8\u80fd\u6536\u8fd9\u6837\u4e00\u4e2a\u806a\u660e\u7075\u6167\u7684\u5f1f\u5b50\u3002\u201d\u674e\u6587\u79c0\u51c4\u7136\u4e00\u7b11\uff0c\u5fc3\u60f3\uff1a \u3000\u3000\u201c\u6211\u5728\u8fd9\u4e16\u4e0a\u9664\u4e86\u8ba1\u7237\u7237\u5916\uff0c\u518d\u65e0\u4e00\u4e2a\u4eb2\u4eba\u3002\u5b66\u4e0d\u5b66\u6b66\u529f\uff0c\u90a3\u4e5f\u7f62\u4e86\u3002\u4e0d\u8fc7\u591a\u4e86\u4e2a\u5e08\u7236\uff0c\u603b\u662f\u591a\u4e86\u4e00\u4e2a\u4e0d\u4f1a\u5bb3\u6211\u3001\u80af\u6765\u7406\u776c\u6211\u7684\u4eba\u3002\u201d \u3000\u3000\u534e\u8f89\u9053\uff1a\u201c\u5929\u5feb\u9ed1\u5566\uff0c\u4f60\u7528\u6d41\u661f\u9524\u5f00\u8def\uff0c\u51b2\u5c06\u51fa\u53bb\uff0c\u5230\u4e86\u5bbd\u655e\u7684\u6240\u5728\uff0c\u4fbf\u6536\u62fe\u4e86\u8fd9\u4e24\u4e2a\u8d3c\u5b50\u3002\u201d\u674e\u6587\u79c0\u5f88\u6709\u70b9\u5bb3\u6015\u3002\u534e\u8f89\u6012\u9053\uff1a\u201c\u4f60\u65e2\u4fe1\u4e0d\u8fc7\u6211\u7684\u6b66\u529f\uff0c\u4f55\u5fc5\u62dc\u6211\u4e3a\u5e08\uff1f\u5f53\u5e74\u95fd\u5317\u53cc\u96c4\u4fbf\u53cc\u53cc\u4e27\u751f\u5728\u8fd9\u62db\u2018\u661f\u6708\u4e89\u8f89\u2019\u4e4b\u4e0b\u3002\u8fd9\u4e24\u4e2a\u5c0f\u6bdb\u8d3c\u7684\u672c\u4e8b\uff0c\u6bd4\u8d77\u95fd\u5317\u53cc\u96c4\u5374\u53c8\u5982\u4f55\uff1f\u201d\u674e\u6587\u79c0\u54ea\u77e5\u9053\u95fd\u5317\u53cc\u96c4\u7684\u6b66\u529f\u5982\u4f55\uff0c\u89c1\u4ed6\u53d1\u6012\uff0c\u53ea\u5f97\u786c\u4e86\u5934\u76ae\uff0c\u642c\u5f00\u5835\u5728\u6d1e\u53e3\u7684\u77f3\u5757\uff0c\u53f3\u624b\u62ff\u4e86\u90a3\u5bf9\u846b\u82a6\u6d41\u661f\u9524\uff0c\u5de6\u624b\u4ece\u5730\u4e0b\u62fe\u8d77\u4e00\u679a\u6bd2\u9488\uff0c\u559d\u9053\uff1a\u201c\u8be5\u6b7b\u7684\u6076\u8d3c\uff0c\u6bd2\u9488\u6765\u4e86\uff01\u201d \u3000\u3000\u90a3\u59d3\u5b8b\u548c\u59d3\u5168\u7684\u4e24\u4e2a\u5f3a\u4eba\u5b88\u5728\u6d1e\u53e3\uff0c\u542c\u5230\u201c\u6bd2\u9488\u6765\u4e86\u201d\u56db\u5b57\uff0c\u53ea\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u6025\u5fd9\u9000\u51fa\u3002\u90a3\u59d3\u5b8b\u7684\u539f\u4e5f\u60f3\u5230\uff0c\u5979\u82e5\u8981\u65bd\u653e\u6bd2\u9488\uff0c\u51b3\u65e0\u5148\u884c\u63d0\u9192\u4e00\u53e5\u4e4b\u7406\uff0c\u65e2\u7136\u8fd9\u822c\u547c\u559d\uff0c\u90a3\u4fbf\u662f\u4e0d\u653e\u6bd2\u9488\uff0c\u53ef\u662f\u773c\u89c1\u4e09\u4e2a\u540c\u4f34\u63a5\u8fde\u547d\u4e27\u6bd2\u9488\u4e4b\u4e0b\uff0c\u5374\u6559\u4ed6\u5982\u4f55\u6562\u4e8e\u6258\u5927\u4e0d\u7406\uff1f \u3000\u3000\u674e\u6587\u79c0\u6162\u6162\u8ffd\u51fa\uff0c\u5fc3\u4e2d\u7684\u5bb3\u6015\u5b9e\u5728\u4e0d\u5728\u4e24\u4e2a\u5f3a\u4eba\u4e4b\u4e0b\u3002\u4e09\u4e2a\u4eba\u80c6\u6218\u5fc3\u60ca\uff0c\u7ec8\u4e8e\u90fd\u8fc7\u4e86\u90a3\u5341\u4f59\u4e08\u72ed\u7a84\u7684\u901a\u9053\u3002 \u3000\u3000\u90a3\u59d3\u5168\u7684\u4e00\u56de\u5934\uff0c\u674e\u6587\u79c0\u5de6\u624b\u4fbf\u662f\u4e00\u626c\uff0c\u59d3\u5168\u7684\u4e00\u614c\uff0c\u811a\u4e0b\u4e00\u4e2a\u8e09\u8dc4\uff0c\u6454\u4e86\u4e2a\u7b4b\u6597\u3002\u90a3\u59d3\u5b8b\u7684\u8fd8\u9053\u4ed6\u4e2d\u4e86\u6bd2\u9488\uff0c\u811a\u4e0b\u52a0\u5feb\uff0c\u76f4\u51b2\u51fa\u6d1e\u3002\u59d3\u5168\u7684\u8ddf\u7740\u4e5f\u5954\u5230\u4e86\u6d1e\u5916\u3002\u4e24\u4eba\u957f\u5200\u62a4\u8eab\uff0c\u4e00\u4e2a\u9053\uff1a\u201c\u8fd8\u662f\u5728\u8fd9\u91cc\u5bf9\u4ed8\u90a3\u4e2b\u5934\uff01\u201d\u4e00\u4e2a\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5979\u53d1\u6bd2\u9488\u65f6\u4e5f\u597d\u77a7\u5f97\u6e05\u695a\u4e9b\u3002\u201d \u3000\u3000\u8fd9\u65f6\u5915\u9633\u5728\u5c71\uff0c\u95ea\u95ea\u91d1\u5149\u6b63\u7167\u5728\u5b8b\u5168\u4e8c\u4eba\u7684\u8138\u4e0a\uff0c\u4e24\u4eba\u5fae\u5fae\u4fa7\u5934\uff0c\u4e0d\u4ee4\u65e5\u5149\u76f4\u5c04\u8fdb\u773c\uff0c\u731b\u542c\u5f97\u5c71\u6d1e\u4e2d\u4e00\u58f0\u5a07\u559d\uff1a\u201c\u6bd2\u9488\u6765\u5566\uff01\u201d\u4e24\u4eba\u6025\u5fd9\u5411\u65c1\u4e00\u95ea\uff0c\u53ea\u89c1\u5c71\u6d1e\u4e2d\u98de\u51fa\u4e24\u4e2a\u846b\u82a6\uff0c\u674e\u6587\u79c0\u8ddf\u7740\u8df3\u4e86\u51fa\u6765\u3002\u4e24\u4eba\u5148\u662f\u4e00\u60ca\uff0c\u5f85\u89c1\u5979\u624b\u4e2d\u63d0\u7740\u7684\u7adf\u662f\u4e24\u4e2a\u67af\u69c1\u7684\u846b\u82a6\uff0c\u4e0d\u7531\u5f97\u5931\u7b11\uff0c\u4e0d\u8fc7\u7b11\u58f0\u4e4b\u4e2d\uff0c\u5374\u4e5f\u514d\u4e0d\u4e86\u6212\u60e7\u4e4b\u610f\u3002 \u3000\u3000\u674e\u6587\u79c0\u5fc3\u4e2d\u6026\u6026\u800c\u8df3\uff0c\u5979\u53ea\u5b66\u4e86\u4e00\u62db\u6b66\u529f\uff0c\u53ef\u4e0d\u77e5\u8fd9\u4e00\u62db\u662f\u5426\u5f53\u771f\u7ba1\u7528\uff0c\u5e7c\u65f6\u867d\u8ddf\u7236\u6bcd\u5b66\u8fc7\u4e00\u4e9b\u6b66\u827a\uff0c\u4f46\u7236\u6bcd\u6b7b\u540e\u5c31\u629b\u8352\u4e86\uff0c\u65e9\u5df2\u5fd8\u8bb0\u5e72\u51c0\u3002\u5979\u5bf9\u8fd9\u4e24\u4e2a\u9762\u8c8c\u51f6\u6076\u7684\u5f3a\u4eba\u5b9e\u662f\u5bb3\u6015\u4e4b\u6781\uff0c\u82e5\u80fd\u4e0d\u6597\uff0c\u80fd\u591f\u865a\u5f20\u58f0\u52bf\u7684\u5c06\u4ed6\u4eec\u5413\u8dd1\uff0c\u90a3\u662f\u6700\u5999\u4e0d\u8fc7\uff0c\u4e8e\u662f\u5927\u58f0\u559d\u9053\uff1a\u201c\u4f60\u4eec\u518d\u4e0d\u9003\u8d70\uff0c\u6211\u5e08\u7236\u4e00\u6307\u9707\u6c5f\u5357\u4f7f\u51fa\u6765\u5566\uff01\u4ed6\u8001\u4eba\u5bb6\u6bd2\u9488\u6740\u4eba\uff0c\u72b9\u5982\u63a2\u56ca\u53d6\u7269\u4e00\u822c\uff0c\u4f60\u4eec\u80c6\u6562\u548c\u4ed6\u4f5c\u5bf9\uff0c\u5f53\u771f\u662f\u597d\u5927\u7684\u80c6\u5b50\uff01\u201d \u3000\u3000\u8fd9\u4e24\u4e2a\u5f3a\u4eba\u90fd\u662f\u5bfb\u5e38\u89d2\u8272\uff0c\u201c\u4e00\u6307\u9707\u6c5f\u5357\u201d\u7684\u540d\u5934\u5f53\u5e74\u5012\u4e5f\u4f3c\u4e4e\u542c\u89c1\u8fc7\uff0c\u4f46\u8ddf\u4ed6\u6beb\u65e0\u74dc\u845b\uff0c\u5411\u6765\u4e0d\u653e\u5728\u5fc3\u4e0a\uff0c\u76f8\u4e92\u4f7f\u4e2a\u773c\u8272\uff0c\u5fc3\u4e2d\u90fd\u60f3\uff1a\u201c\u4e58\u65e9\u6293\u4e86\u8fd9\u4e2b\u5934\u53bb\u89c1\u970d\u5927\u7237\u3001\u9648\u4e8c\u7237\uff0c\u4fbf\u662f\u5929\u5927\u7684\u529f\u52b3\uff0c\u7ba1\u4ed6\u4ec0\u4e48\u9707\u6c5f\u5357\u3001\u9707\u6c5f\u5317\uff1f\u201d\u9f50\u58f0\u547c\u53f1\uff0c\u5206\u4ece\u5de6\u53f3\u6251\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u674e\u6587\u79c0\u5927\u5403\u4e00\u60ca\uff1a\u201c\u4ed6\u4e8c\u4eba\u4e00\u9f50\u4e0a\u6765\uff0c\u8fd9\u62db\u661f\u6708\u4e89\u8f89\u5374\u5982\u4f55\u7528\u6cd5\uff1f\u201d\u4e5f\u662f\u534e\u8f89\u4e00\u5fc3\u4e00\u610f\u7684\u6559\u5979\u5982\u4f55\u51fa\u62db\u6253\u7a74\uff0c\u7adf\u5fd8\u4e86\u6559\u5979\u600e\u751f\u5bf9\u4ed8\u4e24\u4eba\u9f50\u4e0a\u3002\u8981\u77e5\u5bf9\u654c\u8fc7\u62db\uff0c\u5343\u53d8\u4e07\u5316\uff0c\u4e00\u4e24\u4e2a\u65f6\u8fb0\u4e4b\u4e2d\uff0c\u53c8\u6559\u5f97\u4e86\u591a\u5c11\uff1f \u3000\u3000\u674e\u6587\u79c0\u624b\u5fd9\u811a\u4e71\uff0c\u5411\u53f3\u8df3\u5f00\u4e09\u5c3a\u3002\u90a3\u59d3\u5168\u7684\u7ad9\u5728\u53f3\u9996\uff0c\u62a2\u5148\u5954\u8fd1\uff0c\u674e\u6587\u79c0\u4e0d\u7ba1\u4e09\u4e03\u4e8c\u5341\u4e00\uff0c\u4e24\u679a\u846b\u82a6\u6325\u51fa\uff0c\u60f6\u6025\u4e4b\u4e0b\uff0c\u8fd9\u4e00\u62db\u201c\u661f\u6708\u4e89\u8f89\u201d\u53ea\u4f7f\u5bf9\u4e86\u4e00\u534a\uff0c\u5de6\u9524\u5012\u662f\u6253\u4e2d\u4e86\u4ed6\u80f8\u53e3\u7684\u201c\u5546\u66f2\u7a74\u201d\uff0c\u53f3\u9524\u5374\u6b63\u78b0\u5728\u4ed6\u7684\u957f\u5200\u53e3\uff0c\u5237\u7684\u4e00\u54cd\uff0c\u846b\u82a6\u88ab\u5200\u950b\u5272\u5f00\uff0c\u9ec4\u6c99\u98de\u6e85\u3002 \u3000\u3000\u90a3\u59d3\u5b8b\u7684\u6b63\u62a2\u6b65\u5954\u5230\uff0c\u6ca1\u6599\u5230\u846b\u82a6\u4e2d\u7adf\u4f1a\u6709\u5927\u7247\u9ec4\u6c99\u98de\u51fa\uff0c\u5341\u6570\u7c92\u6c99\u5b50\u94bb\u5165\u4e86\u773c\u4e2d\uff0c\u5fd9\u4f38\u624b\u63c9\u773c\u3002\u674e\u6587\u79c0\u53c8\u662f\u4e00\u9524\u51fb\u51fa\uff0c\u53ea\u56e0\u53f3\u9524\u7834\u88c2\uff0c\u5c11\u4e86\u501f\u52a9\u4e4b\u52bf\uff0c\u53ea\u6253\u4e2d\u4e86\u4ed6\u7684\u80cc\u5fc3\uff0c\u5374\u6ca1\u4e2d\u201c\u7075\u53f0\u7a74\u201d\u3002\u4f46\u8fd9\u4e00\u4e0b\u4e03\u516b\u65a4\u91cd\u7684\u98de\u9524\u51fb\u5728\u8eab\u4e0a\uff0c\u90a3\u59d3\u5b8b\u7684\u4e5f\u662f\u7ad9\u4e0d\u4f4f\u811a\uff0c\u5411\u524d\u4e00\u6251\uff0c\u773c\u4e5f\u6ca1\u7741\u5f00\uff0c\u4fbf\u62b1\u4f4f\u4e86\u674e\u6587\u79c0\u7684\u80a9\u5934\u3002\u674e\u6587\u79c0\u53eb\u58f0\uff1a\u201c\u554a\u54df\uff01\u201d\u5de6\u624b\u5fd9\u4f38\u624b\u51fa\u53bb\u63a8\uff0c\u614c\u4e71\u4e2d\u5fd8\u4e86\u624b\u4e2d\u8fd8\u6301\u7740\u4e00\u679a\u6bd2\u9488\uff0c\u8fd9\u4e00\u63a8\uff0c\u5374\u662f\u5c06\u6bd2\u9488\u523a\u5165\u4e86\u4ed6\u809a\u8179\u3002\u90a3\u59d3\u5b8b\u7684\u53cc\u81c2\u4e00\u7d27\uff0c\u4fbf\u6b64\u6b7b\u53bb\u3002 \u3000\u3000\u8fd9\u5f3a\u4eba\u867d\u6b7b\uff0c\u624b\u81c2\u5374\u662f\u62b1\u5f97\u6781\u7d27\uff0c\u674e\u6587\u79c0\u731b\u529b\u6323\u624e\uff0c\u59cb\u7ec8\u6446\u8131\u4e0d\u4e86\u3002\u534e\u8f89\u53f9\u9053\uff1a\u201c\u8822\u4e2b\u5934\uff0c\u5b66\u7684\u65f6\u5019\u5012\u5934\u5934\u662f\u9053\uff0c\u4f7f\u5c06\u8d77\u6765\uff0c\u4fbf\u4e71\u4e03\u516b\u7cdf\uff01\u201d\u63d0\u811a\u5728\u90a3\u59d3\u5b8b\u7684\u5c3e\u95fe\u9aa8\u4e0a\u8e22\u4e86\u4e00\u811a\u3002 \u3000\u3000\u90a3\u6b7b\u5c38\u677e\u5f00\u53cc\u81c2\uff0c\u5f80\u540e\u4fbf\u5012\u3002 \u3000\u3000\u674e\u6587\u79c0\u60ca\u9b42\u672a\u5b9a\uff0c\u8f6c\u5934\u770b\u90a3\u59d3\u5168\u7684\u5f3a\u4eba\u65f6\uff0c\u53ea\u89c1\u4ed6\u76f4\u633a\u633a\u7684\u8eba\u5728\u5730\u4e0a\uff0c\u53cc\u76ee\u5706\u7741\uff0c\u4e00\u52a8\u4e5f\u4e0d\u52a8\uff0c\u7adf\u5df2\u88ab\u5979\u4ee5\u704c\u6c99\u846b\u82a6\u51fb\u4e2d\u8981\u7a74\u800c\u6b7b\u3002\u674e\u6587\u79c0\u4e00\u65e5\u4e4b\u4e2d\u8fde\u6740\u4e94\u4eba\uff0c\u867d\u8bf4\u662f\u62a5\u7236\u6bcd\u4e4b\u4ec7\uff0c\u53c8\u662f\u62b5\u5fa1\u5f3a\u66b4\uff0c\u5fc3\u4e2d\u603b\u662f\u751a\u611f\u4e0d\u5b89\uff0c\u6014\u6014\u7684\u671b\u7740\u4e24\u5177\u5c38\u4f53\uff0c\u5fcd\u4e0d\u4f4f\u4fbf\u54ed\u4e86\u51fa\u6765\u3002 \u3000\u3000\u534e\u8f89\u5fae\u7b11\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u54ed\u4e86\uff1f\u5e08\u7236\u6559\u4f60\u7684\u8fd9\u4e00\u62db\u2018\u661f\u6708\u4e89\u8f89\u2019\uff0c\u53ef\u597d\u4e0d\u597d\uff1f\u201d\u674e\u6587\u79c0\u545c\u54bd\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u53c8\u6740\u4e86\u4eba\u3002\u201d\u534e\u8f89\u9053\uff1a\u201c\u6740\u51e0\u4e2a\u5c0f\u6bdb\u8d3c\u7b97\u5f97\u4e86\u4ec0\u4e48\uff1f\u6211\u6b66\u529f\u56de\u590d\u4e4b\u540e\uff0c\u5c31\u5c06\u4e00\u8eab\u529f\u592b\u90fd\u4f20\u4e86\u4e8e\u4f60\uff0c\u5f85\u6b64\u95f4\u5927\u4e8b\u4e00\u4e86\uff0c\u54b1\u4eec\u56de\u5f52\u4e2d\u539f\uff0c\u5e08\u5f92\u4fe9\u7eb5\u6a2a\u5929\u4e0b\uff0c\u6709\u8c01\u80fd\u5f53\uff1f\u6765\u6765\u6765\uff0c\u5230\u6211\u5c4b\u91cc\u53bb\u6b47\u6b47\uff0c\u559d\u4e24\u676f\u70ed\u8336\u3002\u201d\u8bf4\u7740\u5f15\u5bfc\u674e\u6587\u79c0\u8d70\u53bb\u5de6\u9996\u4e1b\u6797\u4e4b\u540e\uff0c\u884c\u5f97\u91cc\u8bb8\uff0c\u7ecf\u8fc7\u4e00\u6392\u767d\u6866\u6811\uff0c\u5230\u4e86\u4e00\u95f4\u8305\u5c4b\u4e4b\u524d\u3002 \u3000\u3000\u674e\u6587\u79c0\u8ddf\u7740\u4ed6\u8fdb\u5c4b\uff0c\u53ea\u89c1\u5c4b\u5185\u9648\u8bbe\u867d\u7136\u7b80\u964b\uff0c\u5374\u9887\u96c5\u6d01\uff0c\u5802\u4e2d\u60ac\u7740\u4e00\u526f\u6728\u677f\u5bf9\u8054\uff0c\u6bcf\u4e00\u5757\u6728\u677f\u4e0a\u523b\u7740\u4e03\u4e2a\u5b57\uff0c\u4e0a\u8054\u9053\uff1a\u201c\u767d\u9996\u76f8\u77e5\u72b9\u6309\u5251\u3002\u201d\u4e0b\u8054\u9053\uff1a\u201c\u6731\u95e8\u65e9\u8fbe\u7b11\u5f39\u51a0\u3002\u201d\u5979\u81ea\u6765\u56de\u7586\u4e4b\u540e\uff0c\u4ece\u672a\u89c1\u8fc7\u5bf9\u8054\uff0c\u4e5f\u4ece\u6765\u6ca1\u4eba\u6559\u8fc7\u5979\u8bfb\u4e66\uff0c\u597d\u5728\u8fd9\u5341\u56db\u4e2a\u5b57\u5747\u4e0d\u8270\u6df1\uff0c\u5c0f\u65f6\u5019\u5979\u6bcd\u4eb2\u90fd\u66fe\u6559\u8fc7\u7684\uff0c\u6587\u4e49\u5374\u5168\u7136\u4e0d\u61c2\uff0c\u5583\u5583\u7684\u9053\uff1a\u201c\u767d\u9996\u76f8\u77e5\u72b9\u6309\u5251\u2026\u2026\u201d\u534e\u8f89\u9053\uff1a\u201c\u4f60\u8bfb\u8fc7\u8fd9\u9996\u8bd7\u4e48\uff1f\u201d\u674e\u6587\u79c0\u9053\uff1a\u201c\u6ca1\u6709\u3002\u8fd9\u5341\u56db\u4e2a\u5b57\u5199\u7684\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u534e\u8f89\u6587\u6b66\u5168\u624d\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u738b\u7ef4\u7684\u4e24\u53e5\u8bd7\u3002\u4e0a\u8054\u8bf4\u7684\u662f\uff0c\u4f60\u5982\u6709\u4e2a\u77e5\u5df1\u670b\u53cb\uff0c\u8ddf\u4ed6\u76f8\u4ea4\u4e00\u751f\uff0c\u4e24\u4e2a\u4eba\u5934\u53d1\u90fd\u767d\u4e86\uff0c\u4f46\u4f60\u8fd8\u662f\u522b\u76f8\u4fe1\u4ed6\uff0c\u4ed6\u6697\u5730\u91cc\u4ecd\u4f1a\u52a0\u5bb3\u4f60\u7684\u3002\u4ed6\u8d70\u5230\u4f60\u9762\u524d\uff0c\u4f60\u8fd8\u662f\u6309\u7740\u5251\u67c4\u7684\u597d\u3002\u8fd9\u4e24\u53e5\u8bd7\u7684\u4e0a\u4e00\u53e5\uff0c\u53eb\u505a\u2018\u4eba\u60c5\u7ffb\u8986\u4f3c\u6ce2\u6f9c\u2019\u3002\u81f3\u4e8e\u2018\u6731\u95e8\u65e9\u8fbe\u7b11\u5f39\u51a0\u2019\u8fd9\u4e00\u53e5\uff0c\u90a3\u662f\u8bf4\u4f60\u7684\u597d\u670b\u53cb\u5f97\u610f\u4e86\uff0c\u9752\u4e91\u76f4\u4e0a\uff0c\u8981\u662f\u4f60\u76fc\u671b\u4ed6\u6765\u63d0\u62d4\u4f60\u3001\u5e2e\u52a9\u4f60\uff0c\u53ea\u4e0d\u8fc7\u60f9\u5f97\u4ed6\u4e00\u756a\u803b\u7b11\u7f62\u4e86\u3002\u201d \u3000\u3000\u674e\u6587\u79c0\u81ea\u8ddf\u4ed6\u4f1a\u9762\u4ee5\u540e\uff0c\u89c1\u4ed6\u5904\u5904\u5bf9\u81ea\u5df1\u731c\u7591\u63d0\u9632\uff0c\u76f4\u81f3\u7ed9\u4ed6\u62d4\u53bb\u4f53\u5185\u6bd2\u9488\uff0c\u4ed6\u624d\u76f8\u4fe1\u81ea\u5df1\u5e76\u65e0\u76f8\u5bb3\u4e4b\u610f\uff0c\u518d\u770b\u4e86\u8fd9\u526f\u5bf9\u8054\uff0c\u60f3\u662f\u4ed6\u4e00\u751f\u4e4b\u4e2d\uff0c\u66fe\u53d7\u5230\u65c1\u4eba\u6781\u5927\u7684\u635f\u5bb3\uff0c\u800c\u4e14\u8fd9\u4eba\u6050\u6015\u8fd8\u662f\u4ed6\u7684\u77e5\u4ea4\u597d\u53cb\uff0c\u56e0\u6b64\u624d\u5982\u6b64\u6124\u6fc0\uff0c\u5982\u6b64\u6212\u60e7\u3002\u8fd9\u65f6\u4e5f\u4e0d\u4fbf\u591a\u95ee\uff0c\u5f53\u4e0b\u81ea\u53bb\u70f9\u6c34\u6ce1\u8336\u3002 \u3000\u3000\u4e24\u4eba\u5404\u81ea\u559d\u4e86\u4e24\u676f\u70ed\u8336\uff0c\u7cbe\u795e\u4e00\u632f\u3002\u674e\u6587\u79c0\u9053\uff1a\u201c\u5e08\u7236\uff0c\u6211\u5f97\u56de\u53bb\u5566\u3002\u201d\u534e\u8f89\u4e00\u6014\uff0c\u8138\u4e0a\u9732\u51fa\u5341\u5206\u5931\u671b\u7684\u795e\u8272\uff0c\u9053\uff1a\u201c\u4f60\u8981\u8d70\u4e86\uff1f\u4f60\u4e0d\u8ddf\u6211\u5b66\u6b66\u827a\u4e86\uff1f\u201d \u3000\u3000\u674e\u6587\u79c0\u9053\uff1a\u201c\u4e0d\uff01\u6211\u6628\u665a\u6574\u591c\u4e0d\u5f52\uff0c\u8ba1\u7237\u7237\u4e00\u5b9a\u5f88\u7275\u8bb0\u6211\u3002 \u3000\u3000\u5f85\u6211\u8ddf\u4ed6\u8bf4\u8fc7\u4e4b\u540e\uff0c\u518d\u6765\u8ddf\u4f60\u5b66\u6b66\u827a\u3002\u201d\u534e\u8f89\u7a81\u7136\u53d1\u6012\uff0c\u80c0\u7ea2\u4e86\u8138\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4f60\u82e5\u662f\u8ddf\u4ed6\u8bf4\u4e86\uff0c\u90a3\u5c31\u6c38\u8fdc\u522b\u6765\u89c1\u6211\u3002\u201d\u674e\u6587\u79c0\u5413\u4e86\u4e00\u8df3\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4e0d\u80fd\u8ddf\u8ba1\u7237\u7237\u8bf4\u4e48\uff1f\u4ed6\u2026\u2026\u4ed6\u5f88\u75bc\u6211\u7684\u554a\u3002\u201d\u534e\u8f89\u9053\uff1a\u201c\u8ddf\u8c01\u4e5f\u4e0d\u80fd\u8bf4\u3002\u4f60\u5feb\u7acb\u4e0b\u4e00\u4e2a\u6bd2\u8a93\uff0c\u4eca\u65e5\u4e4b\u4e8b\uff0c\u5bf9\u8c01\u4e5f\u4e0d\u8bb8\u8bf4\u8d77\uff0c\u5426\u5219\u7684\u8bdd\uff0c\u6211\u4e0d\u8bb8\u4f60\u79bb\u5f00\u6b64\u5c71\u2026\u2026\u201d\u4ed6\u4e00\u6012\u4e4b\u4e0b\uff0c\u80cc\u4e0a\u4f24\u53e3\u7a81\u7136\u5267\u75db\uff0c\u201c\u554a\u201d\u7684\u4e00\u58f0\uff0c\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u674e\u6587\u79c0\u5fd9\u5c06\u4ed6\u6276\u8d77\uff0c\u5728\u4ed6\u989d\u5934\u6cfc\u4e86\u4e9b\u6e05\u6c34\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u534e\u8f89\u60a0\u60a0\u9192\u8f6c\uff0c\u5947\u9053\uff1a\u201c\u4f60\u8fd8\u6ca1\u8d70\uff1f\u201d\u674e\u6587\u79c0\u5374\u95ee\uff1a\u201c\u4f60\u80cc\u4e0a\u5f88\u75db\u4e48\uff1f\u201d\u534e\u8f89\u9053\uff1a\u201c\u597d\u4e00\u4e9b\u5566\u3002\u4f60\u8bf4\u8981\u56de\u53bb\uff0c\u600e\u4e48\u8fd8\u4e0d\u8d70\uff1f\u201d\u674e\u6587\u79c0\u5fc3\u60f3\uff1a\u201c\u8ba1\u7237\u7237\u6700\u591a\u4e0d\u8fc7\u5fc3\u4e2d\u8bb0\u6302\uff0c\u4f46\u5e08\u7236\u91cd\u521b\u4e4b\u540e\uff0c\u82e5\u662f\u6211\u4e0d\u7559\u7740\u7167\u6599\uff0c\u8bf4\u4e0d\u5b9a\u4ed6\u7adf\u4f1a\u6b7b\u4e86\u3002\u201d\u4fbf\u9053\uff1a\u201c\u5e08\u7236\u6ca1\u5927\u597d\uff0c\u8ba9\u6211\u7559\u7740\u670d\u4f8d\u4f60\u51e0\u65e5\u3002\u201d\u534e\u8f89\u5927\u559c\u3002 \u3000\u3000\u5f53\u665a\u4e24\u4eba\u4fbf\u5728\u8305\u5c4b\u4e2d\u6b47\u5bbf\u3002\u674e\u6587\u79c0\u627e\u4e9b\u67af\u8349\uff0c\u5728\u5385\u4e0a\u505a\u4e86\u4e2a\u7761\u94fa\uff0c\u7761\u68a6\u4e4b\u4e2d\u63a5\u8fde\u60ca\u9192\u4e86\u51e0\u6b21\uff0c\u4e0d\u662f\u68a6\u5230\u7a81\u7136\u88ab\u5f3a\u4eba\u6349\u4f4f\uff0c\u4fbf\u662f\u89c1\u5230\u8840\u6dcb\u6dcb\u7684\u6076\u9b3c\u6765\u5411\u81ea\u5df1\u7d22\u547d\u3002 \u3000\u3000\u6b21\u6668\u8d77\u8eab\uff0c\u89c1\u534e\u8f89\u4f11\u606f\u4e86\u4e00\u665a\uff0c\u7cbe\u795e\u5df2\u5927\u662f\u5065\u65fa\u3002\u65e9\u996d\u540e\uff0c\u534e\u8f89\u4fbf\u6307\u70b9\u5979\u4fee\u4e60\u6b66\u529f\uff0c\u4ece\u624e\u6839\u57fa\u5185\u529f\u6559\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5e74\u7eaa\u5df2\u5927\uff0c\u8fd9\u65f6\u8d77\u59cb\u7ec3\u4e0a\u4e58\u6b66\u529f\uff0c\u539f\u662f\u8fdf\u4e86\u4e00\u4e9b\u3002\u4f46\u4e00\u6765\u5f92\u513f\u8d44\u8d28\u806a\u660e\uff0c\u4e8c\u6765\u5e08\u7236\u66f4\u4e0d\u662f\u6cdb\u6cdb\u4e4b\u8f88\u3002\u660e\u5e08\u6536\u4e86\u9ad8\u5f92\uff0c\u8fd8\u6015\u4e9b\u4ec0\u4e48\uff1f\u4e94\u5e74\u4e4b\u540e\uff0c\u53eb\u4f60\u6b66\u6797\u4e2d\u7f55\u9047\u654c\u624b\u3002\u201d \u3000\u3000\u5982\u6b64\u7ec3\u4e86\u4e03\u516b\u65e5\uff0c\u674e\u6587\u79c0\u7ec3\u529f\u7684\u8fdb\u5883\u5f88\u5feb\uff0c\u534e\u8f89\u80cc\u4e0a\u7684\u521b\u53e3\u4e5f\u9010\u6e10\u5e73\u590d\uff0c\u5979\u8fd9\u624d\u62dc\u522b\u5e08\u7236\uff0c\u9a91\u4e86\u767d\u9a6c\u56de\u53bb\u3002\u534e\u8f89\u6ca1\u518d\u903c\u7740\u5979\u7acb\u8a93\u3002\u5979\u56de\u53bb\u4e4b\u540e\uff0c\u5374\u4e5f\u6ca1\u6709\u8ddf\u8ba1\u7237\u7237\u8bf4\u8d77\uff0c\u53ea\u8bf4\u5728\u5927\u6f20\u4e2d\u8ff7\u4e86\u8def\uff0c\u8d8a\u8d70\u8d8a\u8fdc\uff0c\u5e78\u597d\u9047\u5230\u4e00\u961f\u9a86\u9a7c\u961f\uff0c\u624d\u4e0d\u81f4\u6e34\u6b7b\u5728\u6c99\u6f20\u4e4b\u4e2d\u3002 \u3000"}),showOnHomepage:!0},{name:"\u4e94",content:(0,a.jsx)(a.Fragment,{children:"\u81ea\u6b64\u6bcf\u8fc7\u5341\u5929\u534a\u6708\uff0c\u674e\u6587\u79c0\u4fbf\u5230\u534e\u8f89\u5904\u5c45\u4f4f\u6570\u65e5\u3002\u5979\u751f\u6015\u518d\u9047\u5230\u5f3a\u4eba\uff0c\u51fa\u6765\u65f6\u603b\u662f\u7a7f\u4e86\u54c8\u8428\u514b\u7684\u7537\u5b50\u670d\u88c5\u3002\u8fd9\u6570\u65e5\u4e2d\u534e\u8f89\u603b\u662f\u6089\u5fc3\u6559\u5bfc\u5979\u6b66\u529f\u3002\u674e\u6587\u79c0\u5fc3\u7075\u65e0\u6240\u5bc4\u6258\uff0c\u4fbf\u4e00\u5fc3\u4e00\u610f\u7684\u5b66\u6b66\uff0c\u679c\u7136\u662f\u9ad8\u5f92\u5f97\u9047\u660e\u5e08\uff0c\u8fdb\u5883\u5947\u5feb\u3002 \u3000\u3000\u8fd9\u822c\u8fc7\u4e86\u4e24\u5e74\uff0c\u534e\u8f89\u5e38\u5e38\u8d5e\u9053\uff1a\u201c\u4ee5\u4f60\u4eca\u65e5\u7684\u672c\u4e8b\uff0c\u6c5f\u6e56\u4e0a\u5df2\u53ef\u7b97\u5f97\u662f\u4e00\u6d41\u597d\u624b\uff0c\u82e5\u662f\u56de\u5230\u4e2d\u539f\uff0c\u53ea\u8981\u4e00\u51fa\u624b\uff0c\u7acb\u65f6\u4fbf\u53ef\u626c\u540d\u7acb\u4e07\u3002\u201d\u4f46\u674e\u6587\u79c0\u5374\u4e00\u70b9\u4e5f\u4e0d\u60f3\u56de\u5230\u4e2d\u539f\u53bb\uff0c\u5728\u6c5f\u6e56\u4e0a\u5e72\u4ec0\u4e48\u201c\u626c\u540d\u7acb\u4e07\u201d\u7684\u4e8b\uff0c\u4f46\u8981\u62a5\u7236\u6bcd\u7684\u5927\u4ec7\uff0c\u8981\u514d\u5f97\u518d\u9047\u4e0a\u5f3a\u4eba\u65f6\u53d7\u4ed6\u4eec\u4fb5\u5bb3\uff0c\u6b66\u529f\u5374\u975e\u7ec3\u597d\u4e0d\u53ef\u3002\u5728\u5979\u5185\u5fc3\u6df1\u5904\uff0c\u53e6\u6709\u4e00\u4e2a\u5ff5\u5934\u5728\u6fc0\u52b1\uff1a\u201c\u5b66\u597d\u4e86\u6b66\u529f\uff0c\u6211\u80fd\u628a\u82cf\u666e\u62a2\u56de\u6765\u3002\u201d\u53ea\u4e0d\u8fc7\u8fd9\u4e2a\u5ff5\u5934\u4ece\u6765\u4e0d\u6562\u591a\u60f3\uff0c\u6bcf\u6b21\u60f3\u5230\uff0c\u81ea\u5df1\u5c31\u4f1a\u6ee1\u8138\u901a\u7ea2\u3002 \u3000\u3000\u5979\u867d\u4e0d\u6562\u591a\u60f3\uff0c\u8fd9\u5ff5\u5934\u5374\u6df1\u6df1\u85cf\u5728\u5fc3\u5e95\uff0c\u4e8e\u662f\uff0c\u5728\u8ba1\u8001\u4eba\u5904\u7684\u65f6\u5019\u8d8a\u6765\u8d8a\u5c11\uff0c\u5728\u5e08\u7236\u5bb6\u4e2d\u7684\u65e5\u5b50\u8d8a\u6765\u8d8a\u591a\u3002\u8ba1\u8001\u4eba\u95ee\u4e86\u4e00\u4e24\u6b21\u89c1\u5979\u4e0d\u80af\u8bf4\uff0c\u77e5\u5979\u4ece\u5c0f\u4fbf\u6027\u60c5\u6267\u62d7\uff0c\u6253\u5b9a\u4e86\u7684\u4e3b\u610f\u518d\u4e5f\u4e0d\u4f1a\u56de\u5934\uff0c\u4e5f\u5c31\u4e0d\u95ee\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u65e5\u674e\u6587\u79c0\u9a91\u4e86\u767d\u9a6c\uff0c\u4ece\u5e08\u7236\u5904\u56de\u5bb6\uff0c\u8d70\u5230\u534a\u8def\uff0c\u5ffd\u89c1\u5929\u4e0a\u5f64\u4e91\u5bc6\u5e03\uff0c\u5927\u6f20\u4e2d\u5929\u6c14\u8bf4\u53d8\u5c31\u53d8\uff0c\u4f46\u89c1\u5317\u98ce\u8d8a\u522e\u8d8a\u7d27\uff0c\u770b\u6765\u8f6c\u773c\u4fbf\u6709\u4e00\u573a\u5927\u98ce\u96ea\u3002\u5979\u7eb5\u9a6c\u75be\u9a70\uff0c\u53ea\u89c1\u7267\u4eba\u4eec\u8d76\u7740\u7f8a\u7fa4\u6025\u901f\u56de\u5bb6\uff0c\u5929\u4e0a\u7684\u9e26\u96c0\u4e5f\u662f\u4e00\u53ea\u90fd\u6ca1\u6709\u4e86\u3002\u5feb\u5230\u5bb6\u65f6\uff0c\u84e6\u5730\u91cc\u8e44\u58f0\u5f97\u5f97\uff0c\u4e00\u4e58\u9a6c\u5feb\u6b65\u5954\u6765\u3002\u674e\u6587\u79c0\u5fae\u89c9\u5947\u602a\uff1a\u201c\u773c\u4e0b\u98ce\u96ea\u4fbf\u4f5c\uff0c\u600e\u4e48\u8fd8\u6709\u4eba\u4ece\u5bb6\u91cc\u51fa\u6765\uff1f\u201d\u90a3\u4e58\u9a6c\u4e00\u5954\u8fd1\uff0c\u53ea\u89c1\u9a6c\u4e0a\u4e58\u8005\u62ab\u7740\u4e00\u4ef6\u5927\u7ea2\u7f8a\u6bdb\u62ab\u98ce\uff0c\u662f\u4e2a\u54c8\u8428\u514b\u5973\u5b50\u3002 \u3000\u3000\u674e\u6587\u79c0\u8fd9\u65f6\u7684\u773c\u529b\u548c\u4e24\u5e74\u524d\u5df2\u5927\u4e0d\u76f8\u540c\uff0c\u8fdc\u8fdc\u4fbf\u671b\u89c1\u8fd9\u5973\u5b50\u8eab\u5f62\u8885\u5a1c\uff0c\u9762\u76ee\u59e3\u597d\uff0c\u6b63\u662f\u963f\u66fc\u3002\u674e\u6587\u79c0\u4e0d\u613f\u8ddf\u5979\u6b63\u9762\u76f8\u9022\uff0c\u8f6c\u8fc7\u9a6c\u5934\uff0c\u5230\u4e86\u4e00\u5ea7\u5c0f\u5c71\u4e18\u4e4b\u5357\uff0c\u52d2\u9a6c\u6811\u540e\u3002\u5374\u89c1\u963f\u66fc\u9a91\u7740\u9a6c\u4e5f\u5411\u5c0f\u4e18\u5954\u6765\uff0c\u5979\u9a70\u5230\u4e18\u8fb9\uff0c\u53e3\u4e2d\u553f\u54e8\u4e00\u58f0\uff0c\u5c0f\u4e18\u4e0a\u6811\u4e1b\u4e2d\u7adf\u4e5f\u6709\u4e00\u4e0b\u54e8\u58f0\u76f8\u5e94\u3002\u963f\u66fc\u7ffb\u8eab\u4e0b\u9a6c\uff0c\u4e00\u4e2a\u7537\u4eba\u5411\u5979\u5954\u4e86\u8fc7\u53bb\uff0c\u4e24\u4eba\u62e5\u62b1\u5728\u4e00\u8d77\uff0c\u4f20\u51fa\u4e86\u9635\u9635\u6b22\u7b11\u3002\u90a3\u7537\u4eba\u9053\uff1a\u201c\u8f6c\u773c\u4fbf\u6709\u5927\u98ce\u96ea\uff0c\u4f60\u600e\u5730\u8fd8\u51fa\u6765\uff1f\u201d\u5374\u662f\u82cf\u666e\u7684\u58f0\u97f3\u3002 \u3000\u3000\u963f\u66fc\u7b11\u9053\uff1a\u201c\u5c0f\u50bb\u5b50\uff0c\u4f60\u77e5\u9053\u6709\u5927\u98ce\u96ea\uff0c\u53c8\u4e3a\u4ec0\u4e48\u5927\u7740\u80c6\u5b50\u5728\u8fd9\u91cc\u7b49\u6211\uff1f\u201d\u82cf\u666e\u7b11\u9053\uff1a\u201c\u54b1\u4e24\u4e2a\u5929\u5929\u5728\u8fd9\u513f\u76f8\u4f1a\uff0c\u6bd4\u5403\u996d\u8fd8\u8981\u7d27\u3002\u4fbf\u662f\u843d\u5200\u843d\u5251\uff0c\u6211\u4e5f\u4f1a\u5728\u8fd9\u91cc\u7b49\u4f60\u3002\u201d \u3000\u3000\u4ed6\u4e8c\u4eba\u5e76\u80a9\u5750\u5728\u5c0f\u4e18\u4e4b\u4e0a\uff0c\u60c5\u8bdd\u7ef5\u7ef5\uff0c\u674e\u6587\u79c0\u9694\u7740\u51e0\u682a\u5927\u6811\uff0c\u4e0d\u7531\u5f97\u75f4\u4e86\u3002\u4ed6\u4fe9\u7684\u8bf4\u8bdd\u6709\u65f6\u5f88\u54cd\uff0c\u4fbf\u542c\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u6709\u65f6\u53d8\u6210\u4e86\u5581\u5581\u4f4e\u8bed\uff0c\u5c31\u4e00\u53e5\u4e5f\u542c\u4e0d\u89c1\u3002\u84e6\u5730\u91cc\uff0c\u4e24\u4eba\u4e0d\u77e5\u8bf4\u5230\u4e86\u4ec0\u4e48\u597d\u7b11\u7684\u4e8b\uff0c\u4e00\u9f50\u7eb5\u58f0\u5927\u7b11\u8d77\u6765\u3002 \u3000\u3000\u4f46\u5373\u4f7f\u662f\u5f88\u54cd\u7684\u8bf4\u8bdd\uff0c\u674e\u6587\u79c0\u5176\u5b9e\u4e5f\u662f\u542c\u800c\u4e0d\u95fb\uff0c\u5979\u4e0d\u662f\u5728\u5077\u542c\u4ed6\u4eec\u8bf4\u60c5\u8bdd\u3002\u5979\u773c\u524d\u4f3c\u4e4e\u770b\u89c1\u4e00\u4e2a\u5c0f\u7537\u5b69\uff0c\u4e00\u4e2a\u5c0f\u5973\u5b69\uff0c\u4e5f\u8fd9\u4e48\u5e76\u80a9\u7684\u5750\u7740\uff0c\u4e5f\u662f\u5750\u5728\u8349\u5730\u4e0a\u3002\u5c0f\u7537\u5b69\u662f\u82cf\u666e\uff0c\u5c0f\u5973\u5b69\u5374\u662f\u5979\u81ea\u5df1\u3002\u4ed6\u4eec\u5728\u8bb2\u6545\u4e8b\uff0c\u8bb2\u4ec0\u4e48\u6545\u4e8b\uff0c\u5979\u65e9\u5df2\u5fd8\u8bb0\u4e86\uff0c\u4f46\u5341\u5e74\u524d\u7684\u60c5\u666f\uff0c\u5374\u6e05\u6e05\u695a\u695a\u5730\u51fa\u73b0\u5728\u8138\u524d\u2026\u2026\u3002 \u3000\u3000\u9e21\u6bdb\u822c\u7684\u5927\u96ea\u4e00\u7247\u7247\u7684\u98d8\u4e0b\u6765\uff0c\u843d\u5728\u4e09\u5339\u9a6c\u4e0a\uff0c\u843d\u5728\u4e09\u4eba\u7684\u8eab\u4e0a\u3002\u82cf\u666e\u548c\u963f\u66fc\u7b11\u8bed\u6b63\u6d53\uff0c\u6d51\u6ca1\u5728\u610f\uff1b\u674e\u6587\u79c0\u5374\u662f\u6ca1\u6709\u89c9\u5f97\u3002\u96ea\u82b1\u5728\u4e09\u4eba\u7684\u5934\u53d1\u4e0a\u5806\u79ef\u8d77\u6765\uff0c\u4e09\u4eba\u7684\u5934\u53d1\u90fd\u767d\u4e86\u3002 \u3000\u3000\u51e0\u5341\u5e74\u4e4b\u540e\uff0c\u5f53\u4e09\u4e2a\u4eba\u7684\u5934\u53d1\u771f\u7684\u90fd\u767d\u4e86\uff0c\u662f\u4e0d\u662f\u82cf\u666e\u548c\u963f\u66fc\u4ecd\u7136\u8fd9\u822c\u8a00\u7b11\u664f\u664f\uff0c\u674e\u6587\u79c0\u4ecd\u7136\u8fd9\u822c\u5bc2\u5bde\u5b64\u5355\uff1f\u5979\u4ecd\u662f\u8bb0\u7740\u522b\u4eba\uff0c\u522b\u4eba\u7684\u5fc3\u4e2d\u5374\u65e9\u6ca1\u4e86\u4e00\u4e1d\u5979\u7684\u5f71\u5b50\uff1f \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u6811\u679d\u4e0a\u5237\u5566\u5566\u7684\u4e00\u9635\u6025\u54cd\uff0c\u82cf\u666e\u548c\u963f\u66fc\u4e00\u9f50\u8df3\u4e86\u8d77\u6765\uff0c\u53eb\u9053\uff1a\u201c\u843d\u51b0\u96f9\u5566\uff01\u5feb\u56de\u53bb\uff01\u201d\u4e24\u4eba\u7ffb\u8eab\u4e0a\u4e86\u9a6c\u80cc\u3002 \u3000\u3000\u674e\u6587\u79c0\u542c\u5230\u4e24\u4eba\u7684\u53eb\u58f0\uff0c\u4e00\u60ca\u9192\u89c9\uff0c\u624b\u6307\u5927\u7684\u51b0\u96f9\u5df2\u843d\u5728\u5934\u4e0a\u3001\u8138\u4e0a\u3001\u624b\u4e0a\uff0c\u611f\u5230\u5f88\u662f\u75bc\u75db\uff0c\u5fd9\u89e3\u4e0b\u9a6c\u978d\u4e0b\u7684\u6bdb\u6be1\uff0c\u515c\u5728\u5934\u4e0a\uff0c\u8fd9\u624d\u9a70\u9a6c\u56de\u5bb6\u3002 \u3000\u3000\u5c06\u5230\u5bb6\u95e8\u53e3\u65f6\uff0c\u53ea\u89c1\u5eca\u67f1\u4e0a\u7cfb\u7740\u4e24\u5339\u9a6c\uff0c\u5176\u4e2d\u4e00\u5339\u6b63\u662f\u963f\u66fc\u6240\u4e58\u3002\u674e\u6587\u79c0\u4e00\u6014\uff1a\u201c\u4ed6\u4eec\u5230\u6211\u5bb6\u6765\u5e72\u4ec0\u4e48\uff1f\u201d\u8fd9\u65f6\u51b0\u96f9\u8d8a\u4e0b\u8d8a\u5927\uff0c\u5979\u7275\u7740\u767d\u9a6c\uff0c\u4ece\u540e\u95e8\u8d70\u8fdb\u5c4b\u53bb\uff0c\u53ea\u542c\u5f97\u82cf\u666e\u723d\u6717\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u8001\u4f2f\u4f2f\uff0c\u51b0\u96f9\u4e0b\u5f97\u8fd9\u4e48\u5927\uff0c\u6211\u4eec\u53ea\u597d\u591a\u803d\u4e00\u4f1a\u5566\u3002\u201d\u8ba1\u8001\u4eba\u9053\uff1a\u201c\u5e73\u65f6\u8bf7\u4e5f\u8bf7\u4f60\u4eec\u4e0d\u5230\u3002\u6211\u53bb\u51b2\u4e00\u58f6\u8336\u3002\u201d \u3000\u3000\u81ea\u4ece\u664b\u5a01\u9556\u5c40\u4e00\u5e72\u8c6a\u5ba2\u5728\u8fd9\u5e26\u8349\u539f\u4e0a\u5927\u65bd\u52ab\u63a0\u4e4b\u540e\uff0c\u54c8\u8428\u514b\u4eba\u5bf9\u6c49\u4eba\u6781\u662f\u618e\u6068\uff0c\u867d\u7136\u8ba1\u8001\u4eba\u5728\u5f53\u5730\u5c45\u4f4f\u5df2\u4e45\uff0c\u54c8\u8428\u514b\u4eba\u53c8\u751f\u6027\u597d\u5ba2\uff0c\u5c1a\u4e0d\u81f4\u5c06\u4ed6\u9a71\u9010\u51fa\u5883\uff0c\u4f46\u5927\u5bb6\u5bf9\u4ed6\u5374\u5341\u5206\u758f\u8fdc\uff0c\u82e5\u4e0d\u662f\u5927\u559c\u5e86\u4e8b\uff0c\u8c01\u4e5f\u4e0d\u5411\u4ed6\u4e70\u9152\uff1b\u82e5\u4e0d\u662f\u5f53\u771f\u8981\u7d27\u7684\u7272\u53e3\u5f97\u75c5\u96be\u6cbb\uff0c\u8c01\u4e5f\u4e0d\u4f1a\u53bb\u8bf7\u4ed6\u6765\u533b\u3002\u82cf\u666e\u548c\u963f\u66fc\u7684\u5e10\u7bf7\u8fd9\u65f6\u53c8\u8fc1\u5f97\u8fdc\u4e86\uff0c\u5018\u82e5\u4e0d\u662f\u8eb2\u907f\u98ce\u96ea\uff0c\u53ea\u6015\u518d\u8fc7\u5341\u5e74\uff0c\u4e5f\u672a\u5fc5\u4f1a\u5230\u4ed6\u5bb6\u6765\u3002 \u3000\u3000\u8ba1\u8001\u4eba\u8d70\u5230\u7076\u8fb9\uff0c\u53ea\u89c1\u674e\u6587\u79c0\u6ee1\u8138\u901a\u7ea2\uff0c\u6b63\u81ea\u6014\u6014\u7684\u51fa\u795e\uff0c\u8bf4\u9053\uff1a\u201c\u554a\u2026\u2026\u4f60\u56de\u2026\u2026\u201d\u674e\u6587\u79c0\u7eb5\u8d77\u8eab\u6765\uff0c\u4f38\u624b\u6309\u4f4f\u4ed6\u5634\uff0c\u5728\u4ed6\u8033\u8fb9\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u522b\u8ba9\u4ed6\u4eec\u77e5\u9053\u6211\u5728\u8fd9\u513f\u3002\u201d\u8ba1\u8001\u4eba\u5f88\u662f\u5947\u602a\uff0c\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u8ba1\u8001\u4eba\u62ff\u7740\u7f8a\u4e73\u9152\u3001\u4e73\u916a\u3001\u7ea2\u8336\u51fa\u53bb\u62db\u5f85\u5ba2\u4eba\u3002\u674e\u6587\u79c0\u5750\u5728\u706b\u65c1\uff0c\u9690\u9690\u542c\u5f97\u82cf\u666e\u548c\u963f\u66fc\u7684\u7b11\u8bed\u58f0\u4ece\u5385\u5802\u4e0a\u4f20\u6765\uff0c\u5979\u5fc3\u5e95\u4e00\u4e2a\u5ff5\u5934\u7adf\u662f\u4e0d\u53ef\u6291\u5236\uff1a\u201c\u6211\u8981\u53bb\u89c1\u89c1\u4ed6\uff0c\u8ddf\u4ed6\u8bf4\u51e0\u53e5\u8bdd\u3002\u201d\u4f46\u8ddf\u7740\u4fbf\u60f3\u5230\u4e86\u82cf\u666e\u7684\u7236\u4eb2\u7684\u65a5\u9a82\u548c\u97ad\u5b50\uff0c\u5341\u5e74\u6765\uff0c\u97ad\u5b50\u7684\u58f0\u97f3\u65e0\u65f6\u65e0\u523b\u4e0d\u5728\u5979\u5fc3\u5934\u54cd\u7740\u3002 \u3000\u3000\u8ba1\u8001\u4eba\u56de\u5230\u7076\u4e0b\uff0c\u9012\u4e86\u4e00\u7897\u6df7\u548c\u7740\u5976\u6cb9\u7684\u70ed\u8336\u7ed9\u5979\uff0c\u773c\u5149\u4e2d\u6d41\u9732\u51fa\u6148\u7231\u7684\u795e\u8272\u3002\u4e24\u4eba\u5171\u5c45\u4e86\u5341\u5e74\uff0c\u4fbf\u50cf\u662f\u4eb2\u7237\u7237\u548c\u4eb2\u751f\u7684\u5b59\u5973\u4e00\u822c\uff0c\u4e92\u76f8\u4f53\u8d34\u5173\u6000\uff0c\u53ef\u662f\u5bf9\u65b9\u7684\u5fc3\u5e95\u6df1\u5904\u5230\u5e95\u60f3\u7740\u4e9b\u4ec0\u4e48\uff0c\u5374\u8c01\u4e5f\u4e0d\u5927\u660e\u767d\u3002 \u3000\u3000\u7ec8\u7a76\uff0c\u4ed6\u4eec\u4e0d\u662f\u9aa8\u8089\uff0c\u6ca1\u6709\u90a3\u4e00\u4efd\u4e0e\u751f\u4ff1\u6765\u7684\u3001\u8840\u8089\u76f8\u8fde\u7684\u611f\u5e94\u3002 \u3000\u3000\u674e\u6587\u79c0\u7a81\u7136\u4f4e\u58f0\u9053\uff1a\u201c\u6211\u4e0d\u6362\u8863\u670d\u4e86\uff0c\u5047\u88c5\u662f\u4e2a\u54c8\u8428\u514b\u7537\u5b50\uff0c\u5230\u4f60\u8fd9\u513f\u6765\u907f\u51b0\u96ea\uff0c\u4f60\u5343\u4e07\u522b\u8bf4\u7a7f\u3002\u201d\u4e5f\u4e0d\u7b49\u8ba1\u8001\u4eba\u56de\u7b54\uff0c\u4ece\u540e\u95e8\u51fa\u53bb\u7275\u4e86\u767d\u9a6c\uff0c\u5192\u7740\u6f2b\u5929\u904d\u91ce\u7684\u5927\u98ce\u96ea\uff0c\u6084\u6084\u8d70\u8fdc\u3002 \u3000\u3000\u4e00\u76f4\u8d70\u51fa\u91cc\u8bb8\uff0c\u624d\u9a91\u4e0a\u9a6c\u80cc\uff0c\u515c\u4e86\u4e2a\u5708\u5b50\uff0c\u9a70\u5411\u524d\u95e8\u3002\u5927\u98ce\u4e4b\u4e2d\uff0c\u53ea\u89c9\u5929\u4e0a\u7684\u9ed1\u4e91\u50cf\u8981\u538b\u5230\u5934\u9876\u6765\u4e00\u822c\u3002\u5979\u5728\u56de\u7586\u5341\u4e8c\u5e74\uff0c\u4ece\u672a\u89c1\u8fc7\u8fd9\u822c\u53e4\u602a\u7684\u5929\u8272\uff0c\u5fc3\u4e0b\u4e5f\u4e0d\u81ea\u7981\u7684\u5bb3\u6015\uff0c\u5fd9\u7eb5\u9a6c\u5954\u5230\u95e8\u524d\uff0c\u4f38\u624b\u6572\u95e8\uff0c\u7528\u54c8\u8428\u514b\u8bed\u8bf4\u9053\uff1a\u201c\u501f\u5149\uff0c\u501f\u5149\uff01\u201d \u3000\u3000\u8ba1\u8001\u4eba\u5f00\u95e8\u51fa\u6765\uff0c\u4e5f\u4ee5\u54c8\u8428\u514b\u8bed\u5927\u58f0\u95ee\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u674e\u6587\u79c0\u9053\uff1a\u201c\u8fd9\u573a\u5927\u98ce\u96ea\u53ef\u4e86\u4e0d\u5f97\uff0c\u8001\u4e08\uff0c\u6211\u8981\u5728\u5c0a\u5904\u8eb2\u4e00\u8eb2\u3002\u201d \u3000\u3000\u8ba1\u8001\u4eba\u9053\uff1a\u201c\u597d\u6781\uff0c\u597d\u6781\uff01\u51fa\u95e8\u4eba\u54ea\u6709\u628a\u5c4b\u5b50\u968f\u8eab\u5e26\u7684\uff0c\u5df2\u5148\u6709\u4e24\u4f4d\u670b\u53cb\u5728\u8fd9\u91cc\u8eb2\u907f\u98ce\u96ea\u3002\u5144\u5f1f\u8bf7\u8fdb\u7f62\uff01\u201d\u8bf4\u7740\u8ba9\u674e\u6587\u79c0\u8fdb\u53bb\uff0c\u53c8\u95ee\uff1a\u201c\u5144\u5f1f\u8981\u4e0a\u54ea\u91cc\u53bb\uff1f\u201d\u674e\u6587\u79c0\u9053\uff1a\u201c\u6211\u8981\u4e0a\u9ed1\u77f3\u56f4\u5b50\uff0c\u6253\u4ece\u8fd9\u91cc\u53bb\u8fd8\u6709\u591a\u8fdc\uff1f\u201d\u5fc3\u4e2d\u5374\u60f3\uff1a\u201c\u8ba1\u7237\u7237\u88c5\u5f97\u771f\u50cf\uff0c\u4e00\u70b9\u7834\u7efd\u4e5f\u77a7\u4e0d\u51fa\u6765\u3002\u201d\u8ba1\u8001\u4eba\u5047\u4f5c\u60ca\u8bb6\uff0c\u8bf4\u9053\uff1a\u201c\u554a\u54df\uff0c\u8981\u4e0a\u9ed1\u77f3\u56f4\u5b50\uff1f\u5929\u6c14\u8fd9\u4e48\u574f\uff0c\u4eca\u5929\u65e0\u8bba\u5982\u4f55\u5230\u4e0d\u4e86\u7684\u5566\uff0c\u4e0d\u5982\u5728\u8fd9\u513f\u803d\u4e00\u665a\uff0c\u660e\u5929\u518d\u8d70\u3002\u8981\u662f\u8ff7\u4e86\u8def\uff0c\u53ef\u4e0d\u662f\u73a9\u7684\u3002\u201d\u674e\u6587\u79c0\u9053\uff1a\u201c\u8fd9\u53ef\u6253\u6270\u4e86\u3002\u201d \u3000\u3000\u5979\u8d70\u8fdb\u5385\u5802\uff0c\u6296\u53bb\u4e86\u8eab\u4e0a\u7684\u96ea\u82b1\u3002\u53ea\u89c1\u82cf\u666e\u548c\u963f\u66fc\u5e76\u80a9\u5750\u7740\uff0c\u56f4\u7740\u4e00\u5806\u706b\u70e4\u706b\u3002\u82cf\u666e\u7b11\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u6211\u4eec\u4e5f\u662f\u6765\u8eb2\u98ce\u96ea\u7684\uff0c\u8bf7\u8fc7\u6765\u4e00\u8d77\u70e4\u5427\u3002\u201d\u674e\u6587\u79c0\u9053\uff1a\u201c\u597d\uff0c\u591a\u8c22\uff01\u201d\u8d70\u8fc7\u53bb\u5750\u5728\u4ed6\u8eab\u65c1\u3002\u963f\u66fc\u542b\u7b11\u62db\u547c\u3002\u82cf\u666e\u548c\u5979\u516b\u4e5d\u5e74\u6ca1\u89c1\uff0c\u674e\u6587\u79c0\u4ece\u5c0f\u59d1\u5a18\u53d8\u6210\u4e86\u5c11\u5973\uff0c\u53c8\u6539\u4e86\u7537\u88c5\uff0c\u82cf\u666e\u54ea\u91cc\u8fd8\u8ba4\u5f97\u51fa\uff1f\u8ba1\u8001\u4eba\u9001\u4e0a\u996e\u98df\uff0c\u674e\u6587\u79c0\u4e00\u9762\u5403\uff0c\u4e00\u9762\u8be2\u95ee\u4e09\u4eba\u7684\u59d3\u540d\uff0c\u81ea\u5df1\u8bf4\u53eb\u4f5c\u963f\u65af\u6258\uff0c\u662f\u4e8c\u767e\u591a\u91cc\u5916\u4e00\u4e2a\u54c8\u8428\u514b\u90e8\u843d\u7684\u7267\u4eba\u3002 \u3000\u3000\u82cf\u666e\u4e0d\u4f4f\u5230\u7a97\u53e3\u53bb\u89c2\u770b\u5929\u8272\uff0c\u5176\u5b9e\uff0c\u5355\u662f\u542c\u90a3\u64bc\u52a8\u5899\u58c1\u7684\u98ce\u58f0\uff0c\u4e0d\u7528\u770b\u5929\uff0c\u4e5f\u77e5\u9053\u8d70\u4e0d\u4e86\u3002\u963f\u66fc\u62c5\u5fc3\u9053\uff1a\u201c\u4f60\u8bf4\u5c4b\u5b50\u4f1a\u4e0d\u4f1a\u7ed9\u98ce\u5439\u5012\uff1f\u201d\u82cf\u666e\u9053\uff1a\u201c\u6211\u5012\u662f\u62c5\u5fc3\u8fd9\u573a\u96ea\u592a\u5927\uff0c\u5c4b\u9876\u5403\u4e0d\u4f4f\uff0c\u5f85\u4f1a\u6211\u722c\u4e0a\u5c4b\u9876\u53bb\u94f2\u4e00\u94f2\u96ea\u3002\u201d\u963f\u66fc\u9053\uff1a\u201c\u53ef\u522b\u8ba9\u5927\u98ce\u628a\u4f60\u522e\u4e0b\u6765\u3002\u201d\u82cf\u666e\u7b11\u9053\uff1a\u201c\u5730\u4e0b\u7684\u96ea\u5df2\u79ef\u5f97\u8fd9\u822c\u539a\uff0c\u4fbf\u662f\u6454\u4e0b\u6765\uff0c\u4e5f\u8dcc\u4e0d\u6b7b\u3002\u201d \u3000\u3000\u674e\u6587\u79c0\u62ff\u7740\u8336\u7897\u7684\u624b\u5fae\u5fae\u53d1\u98a4\uff0c\u5fc3\u4e2d\u5ff5\u5934\u6742\u4e71\uff0c\u4e0d\u77e5\u60f3\u4e9b\u4ec0\u4e48\u624d\u597d\u3002\u513f\u65f6\u7684\u670b\u53cb\u4fbf\u5750\u5728\u81ea\u5df1\u8eab\u8fb9\u3002\u4ed6\u662f\u771f\u7684\u8ba4\u4e0d\u51fa\u81ea\u5df1\u5462\uff0c\u8fd8\u662f\u8ba4\u51fa\u4e86\u5374\u5047\u88c5\u4e0d\u77e5\u9053\uff1f\u4ed6\u5df2\u628a\u81ea\u5df1\u5168\u7136\u5fd8\u4e86\uff0c\u8fd8\u662f\u5fc3\u4e2d\u5e76\u6ca1\u6709\u5fd8\u8bb0\uff0c\u4e0d\u8fc7\u4e0d\u613f\u8ba9\u963f\u66fc\u77e5\u9053\uff1f \u3000\u3000\u5929\u8272\u6e10\u6e10\u9ed1\u4e86\uff0c\u674e\u6587\u79c0\u5750\u5f97\u8fdc\u4e86\u4e9b\u3002\u82cf\u666e\u548c\u963f\u66fc\u624b\u63e1\u7740\u624b\uff0c\u8f7b\u8f7b\u8bf4\u7740\u4e00\u4e9b\u65c1\u4eba\u542c\u6765\u6beb\u65e0\u610f\u4e49\u3001\u4f46\u5728\u604b\u4eba\u7684\u8033\u4e2d\u5fc3\u5934\u5374\u662f\u751c\u871c\u65e0\u6bd4\u7684\u60c5\u8bdd\u3002\u706b\u5149\u5ffd\u659c\u5ffd\u4eae\uff0c\u7167\u7740\u4e24\u4eba\u7684\u8138\u3002 \u3000\u3000\u674e\u6587\u79c0\u5750\u5728\u706b\u5149\u7684\u5708\u5b50\u4e4b\u5916\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u674e\u6587\u79c0\u542c\u5230\u4e86\u9a6c\u8e44\u8df5\u8e0f\u96ea\u5730\u7684\u58f0\u97f3\u3002\u4e00\u4e58\u9a6c\u6b63\u5411\u7740\u8fd9\u5c4b\u5b50\u8d70\u6765\u3002\u8349\u539f\u4e0a\u79ef\u96ea\u5df2\u6df1\uff0c\u9a6c\u8db3\u62d4\u8d77\u6765\u65f6\u5f88\u8d39\u529b\uff0c\u5df2\u7ecf\u8dd1\u4e0d\u5feb\u4e86\u3002 \u3000\u3000\u9a6c\u5339\u6e10\u6e10\u884c\u8fd1\uff0c\u8ba1\u8001\u4eba\u4e5f\u542c\u89c1\u4e86\uff0c\u5583\u5583\u7684\u9053\uff1a\u201c\u53c8\u662f\u4e2a\u907f\u98ce\u96ea\u7684\u4eba\u3002\u201d\u82cf\u666e\u548c\u963f\u66fc\u6216\u8005\u6ca1\u6709\u542c\u89c1\uff0c\u6216\u8005\u4fbf\u542c\u89c1\u4e86\u4e5f\u4e0d\u7406\u4f1a\uff0c\u4e24\u4eba\u56db\u624b\u63e1\u7740\uff0c\u504e\u501a\u7740\u5581\u5581\u7ec6\u8bed\u3002 \u3000\u3000\u8fc7\u4e86\u597d\u4e00\u4f1a\uff0c\u90a3\u4e58\u9a6c\u5230\u4e86\u95e8\u524d\uff0c\u63a5\u7740\u4fbf\u7830\u7830\u7830\u7684\u6572\u8d77\u95e8\u6765\u3002\u6253\u95e8\u58f0\u5f88\u662f\u7c97\u66b4\uff0c\u4e0d\u50cf\u662f\u6c42\u5bbf\u8005\u7684\u793c\u8c8c\u3002\u8ba1\u8001\u4eba\u76b1\u4e86\u76b1\u7709\u5934\uff0c\u53bb\u5f00\u4e86\u95e8\u3002\u53ea\u89c1\u95e8\u53e3\u7ad9\u7740\u4e00\u4e2a\u8eab\u7a7f\u7f8a\u76ae\u8884\u7684\u9ad8\u5927\u6c49\u5b50\uff0c\u866c\u9aef\u6ee1\u816e\uff0c\u8170\u95f4\u6302\u7740\u4e00\u67c4\u957f\u5251\uff0c\u5927\u58f0\u9053\uff1a\u201c\u5916\u8fb9\u98ce\u96ea\u5f88\u5927\uff0c\u9a6c\u8d70\u4e0d\u4e86\u5566\uff01\u201d\u8bf4\u7684\u54c8\u8428\u514b\u8bed\u5f88\u4e0d\u7eaf\u6b63\uff0c\u76ee\u5149\u70af\u70af\uff0c\u5411\u5c4b\u4e2d\u5404\u4eba\u6253\u91cf\u3002\u8ba1\u8001\u4eba\u9053\uff1a\u201c\u8bf7\u8fdb\u6765\u3002\u5148\u559d\u7897\u9152\u5427\uff01\u201d\u8bf4\u7740\u7aef\u4e86\u4e00\u7897\u9152\u7ed9\u4ed6\u3002\u90a3\u4eba\u4e00\u996e\u800c\u5c3d\uff0c\u5750\u5230\u4e86\u706b\u5806\u4e4b\u65c1\uff0c\u89e3\u5f00\u4e86\u5916\u8863\uff0c\u53ea\u89c1\u4ed6\u8170\u5e26\u4e0a\u5de6\u53f3\u5404\u63d2\u7740\u4e00\u67c4\u7cbe\u5149\u95ea\u4eae\u7684\u77ed\u5251\u3002\u4e24\u67c4\u5251\u7684\u5251\u628a\u4e00\u67c4\u91d1\u8272\uff0c\u4e00\u67c4\u94f6\u8272\u3002 \u3000\u3000\u674e\u6587\u79c0\u4e00\u89c1\u5230\u8fd9\u5bf9\u5c0f\u5251\uff0c\u5fc3\u4e2d\u4e00\u51db\uff0c\u5589\u5934\u4fbf\u4f3c\u4e00\u5757\u4ec0\u4e48\u4e1c\u897f\u585e\u4f4f\u4e86\uff0c\u773c\u524d\u4e00\u9635\u6655\u7729\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u662f\u5988\u5988\u7684\u53cc\u5251\u3002\u201d\u91d1\u94f6\u5c0f\u5251\u4e09\u5a18\u5b50\u901d\u4e16\u65f6\u674e\u6587\u79c0\u867d\u8fd8\u5e74\u5e7c\uff0c\u4f46\u8fd9\u5bf9\u5c0f\u5251\u5374\u662f\u8ba4\u5f97\u6e05\u6e05\u695a\u695a\u7684\uff0c\u51b3\u4e0d\u4f1a\u9519\u3002\u5979\u659c\u773c\u5411\u8fd9\u6c49\u5b50\u4e00\u77a5\uff0c\u8ba4\u5f97\u5206\u660e\uff0c\u8fd9\u4eba\u6b63\u662f\u5f53\u5e74\u6307\u6325\u4eba\u4f17\u3001\u8ffd\u6740\u4ed6\u7236\u6bcd\u7684\u4e09\u4e2a\u9996\u9886\u4e4b\u4e00\uff0c\u7ecf\u8fc7\u4e86\u5341\u4e8c\u5e74\uff0c\u5979\u81ea\u5df1\u7684\u76f8\u8c8c\u4f53\u6001\u5168\u7136\u53d8\u4e86\uff0c\u4f46\u4e00\u4e2a\u4e09\u5341\u591a\u5c81\u7684\u6c49\u5b50\u957f\u4e86\u5341\u4e8c\u5c81\u5e74\u7eaa\uff0c\u5374\u6ca1\u591a\u5927\u6539\u53d8\u3002\u5979\u751f\u6015\u4ed6\u8ba4\u51fa\u81ea\u5df1\uff0c\u4e0d\u6562\u5411\u4ed6\u591a\u770b\uff0c\u6697\u60f3\uff1a\u201c\u5018\u82e5\u4e0d\u662f\u8fd9\u573a\u5927\u98ce\u96ea\uff0c\u6211\u89c1\u4e0d\u5230\u82cf\u666e\uff0c\u4e5f\u89c1\u4e0d\u5230\u8fd9\u4e2a\u8d3c\u5b50\u3002\u201d \u3000\u3000\u8ba1\u8001\u4eba\u9053\uff1a\u201c\u5ba2\u4eba\u4ece\u54ea\u91cc\u6765\uff1f\u8981\u53bb\u5f88\u8fdc\u7684\u5730\u65b9\u5427\uff1f\u201d\u90a3\u4eba\u9053\uff1a\u201c\u55ef\uff0c\u55ef\uff01\u201d\u81ea\u5df1\u53c8\u5012\u4e86\u4e00\u7897\u9152\u559d\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u706b\u5806\u8fb9\u56f4\u5750\u4e86\u4e94\u4e2a\u4eba\uff0c\u82cf\u666e\u5df2\u4e0d\u80fd\u518d\u548c\u963f\u66fc\u8bf4\u4f53\u5df1\u8bdd\u513f\uff0c\u4ed6\u5411\u8ba1\u8001\u4eba\u51dd\u89c6\u4e86\u7247\u523b\uff0c\u5ffd\u9053\uff1a\u201c\u8001\u4f2f\u4f2f\uff0c\u6211\u5411\u4f60\u6253\u542c\u4e00\u4e2a\u4eba\u3002\u201d\u8ba1\u8001\u4eba\u9053\uff1a\u201c\u8c01\u554a\uff1f\u201d\u82cf\u666e\u9053\uff1a\u201c\u90a3\u662f\u6211\u5c0f\u65f6\u5019\u5e38\u8ddf\u5979\u5728\u4e00\u8d77\u73a9\u513f\u7684\uff0c\u4e00\u4e2a\u6c49\u4eba\u5c0f\u59d1\u5a18\u2026\u2026\u201d\u4ed6\u8bf4\u5230\u8fd9\u91cc\uff0c\u674e\u6587\u79c0\u5fc3\u4e2d\u7a81\u7684\u4e00\u8df3\uff0c\u5c06\u5934\u8f6c\u5f00\u4e86\uff0c\u4e0d\u6562\u77a7\u4ed6\u3002\u53ea\u542c\u82cf\u666e\u7eed\u9053\uff1a\u201c\u5979\u53eb\u505a\u963f\u79c0\uff0c\u540e\u6765\u9694\u4e86\u516b\u4e5d\u5e74\uff0c\u4e00\u76f4\u6ca1\u518d\u89c1\u5230\u5979\u3002\u5979\u662f\u8ddf\u4e00\u4f4d\u6c49\u4eba\u8001\u516c\u516c\u4f4f\u5728\u4e00\u8d77\u7684\u3002\u90a3\u4e00\u5b9a\u5c31\u662f\u4f60\u4e86\uff1f\u201d\u8ba1\u8001\u4eba\u54b3\u55fd\u4e86\u51e0\u58f0\uff0c\u60f3\u4ece\u674e\u6587\u79c0\u8138\u4e0a\u5f97\u5230\u4e00\u4e9b\u793a\u610f\u3002\u4f46\u674e\u6587\u79c0\u8f6c\u5f00\u4e86\u5934\uff0c\u4ed6\u4e0d\u77e5\u5982\u4f55\u56de\u7b54\u624d\u597d\uff0c\u53ea\u662f\u201c\u55ef\u3001\u55ef\u201d\u7684\u4e0d\u7f6e\u53ef\u5426\u3002 \u3000\u3000\u82cf\u666e\u53c8\u9053\uff1a\u201c\u5979\u7684\u6b4c\u5531\u5f97\u6700\u597d\u542c\u7684\u4e86\uff0c\u6709\u4eba\u8bf4\u5979\u6bd4\u5929\u94c3\u9e1f\u5531\u5f97\u8fd8\u597d\u3002\u4f46\u8fd9\u51e0\u5e74\u6765\uff0c\u6211\u4e00\u76f4\u6ca1\u542c\u5230\u5979\u5531\u6b4c\u3002\u5979\u8fd8\u4f4f\u5728\u4f60\u8fd9\u91cc\u4e48\uff1f\u201d\u8ba1\u8001\u4eba\u5f88\u662f\u5c34\u5c2c\uff0c\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\uff0c\u5979\u4e0d\u2026\u2026\u5979\u4e0d\u5728\u4e86\u2026\u2026\u201d\u674e\u6587\u79c0\u63d2\u53e3\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u90a3\u4e2a\u6c49\u4eba\u59d1\u5a18\uff0c\u6211\u5012\u4e5f\u8bc6\u5f97\u3002 \u3000\u3000\u5979\u65e9\u6b7b\u4e86\u597d\u51e0\u5e74\u5566\uff01\u201d \u3000\u3000\u82cf\u666e\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u554a\uff0c\u5979\u6b7b\u4e86\uff0c\u600e\u4e48\u4f1a\u6b7b\u7684\uff1f\u201d\u8ba1\u8001\u4eba\u5411\u674e\u6587\u79c0\u77a7\u4e86\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u751f\u75c5\u2026\u2026\u751f\u75c5\u2026\u2026\u201d\u82cf\u666e\u773c\u7736\u5fae\u6e7f\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5c0f\u65f6\u5019\u5e38\u548c\u5979\u4e00\u540c\u53bb\u7267\u7f8a\uff0c\u5979\u5531\u4e86\u5f88\u591a\u6b4c\u7ed9\u6211\u542c\uff0c\u8fd8\u8bf4\u4e86\u5f88\u591a\u6545\u4e8b\u3002\u597d\u51e0\u5e74\u4e0d\u89c1\uff0c\u60f3\u4e0d\u5230\u5979\u2026\u2026\u5979\u7adf\u6b7b\u4e86\u3002\u201d\u8ba1\u8001\u4eba\u53f9\u9053\uff1a\u201c\u5509\uff0c\u53ef\u601c\u7684\u5b69\u5b50\u3002\u201d \u3000\u3000\u82cf\u666e\u671b\u7740\u706b\u7130\uff0c\u51fa\u4e86\u4e00\u4f1a\u795e\uff0c\u53c8\u9053\uff1a\u201c\u5979\u8bf4\u5979\u7239\u5988\u90fd\u7ed9\u6076\u4eba\u5bb3\u6b7b\u4e86\uff0c\u5b64\u82e6\u4f36\u4ec3\u7684\u5230\u8fd9\u5730\u65b9\u6765\u2026\u2026\u201d\u963f\u66fc\u9053\uff1a\u201c\u8fd9\u59d1\u5a18\u5f88\u7f8e\u4e3d\u5427\uff1f\u201d\u82cf\u666e\u9053\uff1a\u201c\u90a3\u65f6\u5019\u6211\u5e74\u7eaa\u5c0f\uff0c\u4e5f\u4e0d\u8bb0\u5f97\u4e86\u3002\u53ea\u8bb0\u5f97\u5979\u7684\u6b4c\u5531\u5f97\u597d\u542c\uff0c\u6545\u4e8b\u8bf4\u5f97\u597d\u542c\u2026\u2026\u201d \u3000\u3000\u90a3\u8170\u4e2d\u63d2\u7740\u5c0f\u5251\u7684\u6c49\u5b50\u7a81\u7136\u9053\uff1a\u201c\u4f60\u8bf4\u662f\u4e00\u4e2a\u6c49\u4eba\u5c0f\u59d1\u5a18\uff1f\u5979\u7236\u6bcd\u88ab\u5bb3\uff0c\u72ec\u4e2a\u513f\u5230\u8fd9\u91cc\u6765\uff1f\u201d\u82cf\u666e\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u4e5f\u8ba4\u5f97\u5979\u4e48\uff1f\u201d\u90a3\u6c49\u5b50\u4e0d\u7b54\uff0c\u53c8\u95ee\uff1a\u201c\u5979\u9a91\u4e00\u5339\u767d\u9a6c\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u82cf\u666e\u9053\uff1a\u201c\u662f\u554a\uff0c\u90a3\u4f60\u4e5f\u89c1\u8fc7\u5979\u4e86\u3002\u201d\u90a3\u6c49\u5b50\u7a81\u7136\u7ad9\u8d77\u8eab\u6765\uff0c\u5bf9\u8ba1\u8001\u4eba\u5389\u58f0\u9053\uff1a\u201c\u5979\u6b7b\u5728\u4f60\u8fd9\u513f\u7684\uff1f\u201d\u8ba1\u8001\u4eba\u53c8\u542b\u7cca\u7684\u7b54\u5e94\u4e86\u4e00\u58f0\u3002\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u5979\u7559\u4e0b\u6765\u7684\u4e1c\u897f\u5462\uff1f\u4f60\u90fd\u597d\u597d\u6536\u7740\u4e48\uff1f\u201d \u3000\u3000\u8ba1\u8001\u4eba\u5411\u4ed6\u6a2a\u4e86\u4e00\u773c\uff0c\u5947\u9053\uff1a\u201c\u8fd9\u5e72\u4f60\u4ec0\u4e48\u4e8b\uff1f\u201d\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u6211\u6709\u4e00\u4ef6\u8981\u7d27\u7269\u4e8b\uff0c\u7ed9\u90a3\u5c0f\u59d1\u5a18\u5077\u4e86\u53bb\u3002\u6211\u5230\u5904\u627e\u5979\u4e0d\u5230\uff0c\u54ea\u6599\u5230\u5979\u7adf\u7136\u6b7b\u4e86\u2026\u2026\u201d\u82cf\u666e\u970d\u5730\u7ad9\u8d77\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4f60\u522b\u80e1\u8bf4\u516b\u9053\uff0c\u963f\u79c0\u600e\u4f1a\u5077\u4f60\u7684\u4e1c\u897f\uff1f\u201d\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u82cf\u666e\u9053\uff1a\u201c\u963f\u79c0\u4ece\u5c0f\u8ddf\u6211\u4e00\u8d77\uff0c\u5979\u662f\u4e2a\u5f88\u597d\u5f88\u597d\u7684\u59d1\u5a18\uff0c\u51b3\u4e0d\u4f1a\u62ff\u4eba\u5bb6\u7684\u4e1c\u897f\u3002\u201d\u90a3\u6c49\u5b50\u5634\u4e00\u659c\uff0c\u505a\u4e2a\u8f7b\u8511\u7684\u8138\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u53ef\u662f\u5979\u504f\u5de7\u4fbf\u5077\u4e86\u6211\u7684\u4e1c\u897f\u3002\u201d\u82cf\u666e\u4f38\u624b\u6309\u4f4f\u8170\u95f4\u4f69\u5200\u7684\u5200\u67c4\uff0c\u559d\u9053\uff1a\u201c\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u6211\u770b\u4f60\u4e0d\u662f\u54c8\u8428\u514b\u4eba\uff0c\u8bf4\u4e0d\u5b9a\u4fbf\u662f\u90a3\u4f19\u6c49\u4eba\u5f3a\u76d7\u3002\u201d \u3000\u3000\u90a3\u6c49\u5b50\u8d70\u5230\u95e8\u8fb9\uff0c\u6253\u5f00\u5927\u95e8\u5411\u5916\u5f20\u671b\u3002\u95e8\u4e00\u5f00\uff0c\u4e00\u9635\u75be\u98ce\u5377\u7740\u65e0\u6570\u96ea\u7247\u76f4\u5377\u8fdb\u6765\u3002\u4f46\u89c1\u539f\u91ce\u4e0a\u6f2b\u5929\u98ce\u96ea\uff0c\u4eba\u9a6c\u5df2\u65e0\u6cd5\u884c\u8d70\u3002\u90a3\u6c49\u5b50\u5fc3\u60f3\uff1a\u201c\u5916\u9762\u662f\u4e0d\u4f1a\u518d\u6709\u4eba\u6765\u4e86\u3002\u8fd9\u5c4b\u4e2d\u4e00\u4e2a\u5973\u5b50\uff0c\u4e00\u4e2a\u8001\u4eba\uff0c\u4e00\u4e2a\u7626\u9aa8\u4f36\u4ec3\u7684\u5c11\u5e74\uff0c\u90fd\u662f\u624b\u4e00\u70b9\u4fbf\u5012\u3002\u53ea\u6709\u8fd9\u4e2a\u7c97\u8c6a\u5c11\u5e74\uff0c\u8981\u8d39\u51e0\u4e0b\u624b\u811a\u6253\u53d1\u3002\u201d\u5f53\u4e0a\u4e5f\u4e0d\u653e\u5728\u5fc3\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u6c49\u4eba\u600e\u6837\uff1f\u6211\u59d3\u9648\uff0c\u540d\u8fbe\u6d77\uff0c\u6c5f\u6e56\u4e0a\u5916\u53f7\u53eb\u505a\u9752\u87d2\u5251\uff0c\u4f60\u542c\u8fc7\u6ca1\u6709\uff1f\u201d \u3000\u3000\u82cf\u666e\u4e5f\u4e0d\u61c2\u8fd9\u4e9b\u6c49\u4eba\u7684\u6c5f\u6e56\u89c4\u77e9\uff0c\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u6211\u6ca1\u542c\u89c1\u8fc7\u3002\u4f60\u662f\u6c49\u4eba\u5f3a\u76d7\u4e48\uff1f\u201d\u9648\u8fbe\u6d77\u9053\uff1a\u201c\u6211\u662f\u9556\u5e08\uff0c\u662f\u9760\u6253\u5f3a\u76d7\u5403\u996d\u7684\u3002\u600e\u4e48\u4f1a\u662f\u5f3a\u76d7\u4e86\uff1f\u201d\u82cf\u666e\u542c\u8bf4\u4ed6\u4e0d\u662f\u5f3a\u76d7\uff0c\u8138\u4e0a\u795e\u8272\u767b\u65f6\u4fbf\u7f13\u548c\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u662f\u6c49\u4eba\u5f3a\u76d7\uff0c\u90a3\u4fbf\u597d\u5566\uff01\u6211\u65e9\u8bf4\u6c49\u4eba\u4e2d\u4e5f\u6709\u5f88\u591a\u597d\u4eba\uff0c\u53ef\u662f\u6211\u7239\u7239\u504f\u504f\u4e0d\u4fe1\u3002\u4f60\u4ee5\u540e\u522b\u518d\u8bf4\u963f\u79c0\u62ff\u4f60\u4e1c\u897f\u3002\u201d \u3000\u3000\u9648\u8fbe\u6d77\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u5c0f\u59d1\u5a18\u4eba\u90fd\u6b7b\u5566\uff0c\u4f60\u8fd8\u8bb0\u7740\u5979\u5e72\u4e48\uff1f\u201d\u82cf\u666e\u9053\uff1a\u201c\u5979\u6d3b\u7740\u7684\u65f6\u5019\u662f\u6211\u670b\u53cb\uff0c\u6b7b\u4e86\u4e4b\u540e\u4ecd\u65e7\u662f\u6211\u670b\u53cb\u3002\u6211\u4e0d\u8bb8\u4eba\u5bb6\u8bf4\u5979\u574f\u8bdd\u3002\u201d\u9648\u8fbe\u6d77\u6ca1\u5fc3\u601d\u8ddf\u4ed6\u4e89\u8fa9\uff0c\u8f6c\u5934\u53c8\u95ee\u8ba1\u8001\u4eba\u9053\uff1a\u201c\u90a3\u5c0f\u59d1\u5a18\u7684\u4e1c\u897f\u5462\uff1f\u201d \u3000\u3000\u674e\u6587\u79c0\u542c\u5230\u82cf\u666e\u4e3a\u81ea\u5df1\u8fa9\u62a4\uff0c\u5fc3\u4e2d\u5341\u5206\u6fc0\u52a8\uff1a\u201c\u4ed6\u6ca1\u5fd8\u4e86\u6211\uff0c\u6ca1\u5fd8\u4e86\u6211\uff01\u4ed6\u8fd8\u662f\u5bf9\u6211\u5f88\u597d\u3002\u201d\u4f46\u542c\u9648\u8fbe\u6d77\u4e00\u518d\u67e5\u95ee\u81ea\u5df1\u7559\u4e0b\u7684\u4e1c\u897f\uff0c\u4e0d\u7981\u5947\u602a\uff1a\u201c\u6211\u6ca1\u62ff\u8fc7\u4ed6\u4ec0\u4e48\u7269\u4e8b\u554a\uff0c\u4ed6\u8981\u627e\u5bfb\u4e9b\u4ec0\u4e48\uff1f\u201d\u53ea\u542c\u8ba1\u8001\u4eba\u4e5f\u95ee\u9053\uff1a\u201c\u5ba2\u5b98\u5931\u843d\u4e86\u4ec0\u4e48\u4e1c\u897f\uff1f\u90a3\u4e2a\u5c0f\u59d1\u5a18\u81ea\u6765\u8bda\u5b9e\uff0c\u8001\u6c49\u5f88\u4fe1\u5f97\u8fc7\u7684\uff0c\u5979\u51b3\u8ba1\u4e0d\u4f1a\u62ff\u522b\u4eba\u7684\u7269\u4e8b\u3002\u201d \u3000\u3000\u9648\u8fbe\u6d77\u5fae\u4e00\u6c89\u541f\uff0c\u9053\uff1a\u201c\u90a3\u662f\u4e00\u5f20\u56fe\u753b\u3002\u5728\u5e38\u4eba\u662f\u5f97\u4e4b\u65e0\u7528\uff0c\u4f46\u56e0\u4e3a\u90a3\u662f\u2026\u2026\u90a3\u662f\u5148\u7236\u624b\u7ed8\u7684\uff0c\u6211\u5b9a\u8981\u627e\u56de\u90a3\u5e45\u56fe\u753b\u3002 \u3000\u3000\u8fd9\u5c0f\u59d1\u5a18\u65e2\u66fe\u4f4f\u5728\u8fd9\u91cc\uff0c\u4f60\u53ef\u66fe\u89c1\u8fc7\u8fd9\u5e45\u56fe\u4e48\uff1f\u201d\u8ba1\u8001\u4eba\u9053\uff1a\u201c\u662f\u600e\u4e48\u6837\u7684\u56fe\u753b\uff0c\u753b\u7684\u662f\u5c71\u6c34\u8fd8\u662f\u4eba\u7269\uff1f\u201d\u9648\u8fbe\u6d77\u9053\uff1a\u201c\u662f\u2026\u2026\u662f\u5c71\u6c34\u5427\uff1f\u201d \u3000\u3000\u82cf\u666e\u51b7\u7b11\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u6837\u7684\u56fe\u753b\u4e5f\u4e0d\u77e5\u9053\uff0c\u8fd8\u8bec\u8d56\u4eba\u5bb6\u5077\u4e86\u4f60\u7684\u3002\u201d\u9648\u8fbe\u6d77\u5927\u6012\uff0c\u5237\u7684\u4e00\u58f0\u62d4\u51fa\u8170\u95f4\u957f\u5251\uff0c\u559d\u9053\uff1a\u201c\u5c0f\u8d3c\uff0c\u4f60\u662f\u6d3b\u5f97\u4e0d\u8010\u70e6\u4e86\uff1f\u8001\u7237\u6740\u4e2a\u628a\u4eba\u8fd8\u4e0d\u653e\u5728\u5fc3\u4e0a\u3002\u201d\u82cf\u666e\u4e5f\u4ece\u8170\u95f4\u62d4\u51fa\u77ed\u5200\uff0c\u51b7\u51b7\u7684\u9053\uff1a\u201c\u8981\u6740\u4e00\u4e2a\u54c8\u8428\u514b\u4eba\uff0c\u53ea\u6015\u6ca1\u8fd9\u4e48\u5bb9\u6613\u3002\u201d\u963f\u66fc\u9053\uff1a\u201c\u82cf\u666e\uff0c\u522b\u8ddf\u4ed6\u4e00\u822c\u89c1\u8bc6\u3002\u201d\u82cf\u666e\u542c\u4e86\u963f\u66fc\u7684\u8bdd\uff0c\u628a\u62d4\u51fa\u7684\u5200\u5b50\u7f13\u7f13\u653e\u5165\u9798\u5185\u3002 \u3000\u3000\u9648\u8fbe\u6d77\u4e00\u5fc3\u4e00\u610f\u8981\u5f97\u5230\u90a3\u5f20\u9ad8\u660c\u8ff7\u5bab\u7684\u5730\u56fe\uff0c\u4ed6\u4eec\u5728\u6c99\u6f20\u4e0a\u803d\u4e86\u5341\u5e74\uff0c\u8e0f\u904d\u4e86\u6570\u5343\u91cc\u7684\u6c99\u6f20\u8349\u539f\uff0c\u4fbf\u662f\u4e3a\u4e86\u627e\u5bfb\u674e\u6587\u79c0\uff0c\u773c\u4e0b\u597d\u5bb9\u6613\u542c\u5230\u4e86\u4e00\u70b9\u97f3\u8baf\uff0c\u4ed6\u867d\u751f\u6027\u608d\u6076\uff0c\u5374\u4e5f\u77e5\u9053\u5c0f\u4e0d\u5fcd\u5219\u4e71\u5927\u8c0b\u7684\u9053\u7406\uff0c\u5f53\u4e0b\u5411\u82cf\u666e\u72e0\u72e0\u7684\u77aa\u4e86\u4e00\u773c\uff0c\u8f6c\u5934\u5411\u8ba1\u8001\u4eba\u8bf4\uff1a\u201c\u90a3\u5e45\u753b\u561b\uff0c\u4e5f\u53ef\u8bf4\u662f\u4e00\u5e45\u5730\u56fe\uff0c\u7ed8\u7684\u662f\u5927\u6f20\u4e2d\u4e00\u4e9b\u5c71\u5ddd\u5730\u5f62\u4e4b\u7c7b\u3002\u201d \u3000\u3000\u8ba1\u8001\u4eba\u8eab\u5b50\u5fae\u5fae\u4e00\u98a4\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u600e\u2026\u2026\u600e\u77e5\u8fd9\u5730\u56fe\u662f\u5728\u90a3\u59d1\u5a18\u7684\u624b\u4e2d\uff1f\u201d\u9648\u8fbe\u6d77\u9053\uff1a\u201c\u6b64\u4e8b\u5343\u771f\u4e07\u786e\u3002\u4f60\u82e5\u662f\u5c06\u8fd9\u5e45\u56fe\u5bfb\u51fa\u6765\u7ed9\u6211\uff0c\u81ea\u5f53\u91cd\u91cd\u916c\u8c22\u3002\u201d\u8bf4\u7740\u4ece\u6000\u4e2d\u53d6\u51fa\u4e24\u53ea\u94f6\u5143\u5b9d\u6765\u653e\u5728\u684c\u4e0a\uff0c\u706b\u5149\u7167\u8000\u4e4b\u4e0b\uff0c\u95ea\u95ea\u53d1\u4eae\u3002 \u3000\u3000\u8ba1\u8001\u4eba\u6c89\u601d\u7247\u523b\uff0c\u7f13\u7f13\u6447\u5934\uff0c\u9053\uff1a\u201c\u6211\u4ece\u6765\u6ca1\u89c1\u8fc7\u3002\u201d\u9648\u8fbe\u6d77\u9053\uff1a\u201c\u6211\u8981\u77a7\u77a7\u90a3\u5c0f\u59d1\u5a18\u7684\u9057\u7269\u3002\u201d\u8ba1\u8001\u4eba\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u8fd9\u4e2a\u2026\u2026\u201d\u9648\u8fbe\u6d77\u5de6\u624b\u4e00\u8d77\uff0c\u62d4\u51fa\u94f6\u67c4\u5c0f\u5251\uff0c\u767b\u7684\u4e00\u58f0\uff0c\u63d2\u5728\u6728\u684c\u4e4b\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u8fd9\u4e2a\u90a3\u4e2a\u7684\uff1f\u6211\u81ea\u5df1\u8fdb\u53bb\u77a7\u77a7\u3002\u201d\u8bf4\u7740\u70b9\u71c3\u4e86\u4e00\u6839\u7f8a\u8102\u8721\u70db\uff0c\u63a8\u95e8\u8fdb\u623f\u3002\u4ed6\u5148\u8fdb\u53bb\u7684\u662f\u8ba1\u8001\u4eba\u7684\u5367\u623f\uff0c\u4e00\u770b\u9648\u8bbe\u4e0d\u4f3c\uff0c\u968f\u624b\u5728\u7bb1\u7b3c\u91cc\u7ffb\u4e86\u4e00\u4e0b\uff0c\u4fbf\u5230\u674e\u6587\u79c0\u7684\u5367\u5ba4\u4e2d\u53bb\u3002 \u3000\u3000\u4ed6\u770b\u5230\u674e\u6587\u79c0\u5306\u5306\u6362\u4e0b\u7684\u8863\u670d\uff0c\u8bf4\u9053\uff1a\u201c\u54c8\uff0c\u5979\u957f\u5927\u4e86\u624d\u6b7b\u554a\u3002\u201d\u8fd9\u4e00\u6b21\u4ed6\u53ef\u641c\u68c0\u5f97\u5341\u5206\u4ed4\u7ec6\uff0c\u8fde\u674e\u6587\u79c0\u5e7c\u65f6\u7684\u8863\u7269\u4e5f\u90fd\u7ffb\u4e86\u51fa\u6765\u3002\u674e\u6587\u79c0\u56e0\u8fd9\u4e9b\u5b69\u5b50\u8863\u670d\u90fd\u662f\u6bcd\u4eb2\u7684\u624b\u6cfd\uff0c\u81ea\u5df1\u5e74\u7eaa\u867d\u7136\u5927\u4e86\uff0c\u4e0d\u80fd\u518d\u7a7f\uff0c\u4f46\u8fd8\u662f\u4e00\u4ef6\u4ef6\u597d\u597d\u7684\u4fdd\u5b58\u7740\u3002\u9648\u8fbe\u6d77\u4e00\u89c1\u5230\u8fd9\u51e0\u4ef6\u5c0f\u5b69\u7684\u82b1\u5e03\u8863\u670d\uff0c\u4f9d\u7a00\u8bb0\u5f97\u5341\u5e74\u524d\u5728\u5927\u6f20\u4e2d\u8ffd\u8d76\u5979\u7684\u60c5\u666f\uff0c\u6b22\u58f0\u53eb\u9053\uff1a\u201c\u662f\u4e86\uff0c\u662f\u4e86\uff0c\u4fbf\u662f\u5979\uff01\u201d\u53ef\u662f\u4ed6\u5c06\u90a3\u5367\u5ba4\u51e0\u4e4e\u7ffb\u4e86\u4e00\u4e2a\u8f6c\u8eab\uff0c\u6bcf\u4e00\u4ef6\u8863\u670d\u7684\u91cc\u5b50\u90fd\u5272\u5f00\u6765\u7ec6\u770b\uff0c\u5374\u54ea\u91cc\u627e\u5f97\u5230\u5730\u56fe\u7684\u5f71\u5b50\uff1f \u3000\u3000\u82cf\u666e\u89c1\u4ed6\u8fd9\u822c\ue411\u8e4b\u674e\u6587\u79c0\u7684\u9057\u7269\uff0c\u51e0\u6b21\u6309\u5200\u6b32\u8d77\uff0c\u6bcf\u6b21\u5747\u7ed9\u963f\u66fc\u963b\u4f4f\u3002\u8ba1\u8001\u4eba\u5076\u5c14\u659c\u773c\u77a7\u674e\u6587\u79c0\u4e00\u773c\uff0c\u53ea\u89c1\u5979\u773c\u671b\u706b\u5806\uff0c\u5bf9\u9648\u8fbe\u6d77\u7684\u66b4\u884c\u4f3c\u4e4e\u89c6\u800c\u4e0d\u89c1\u3002\u8ba1\u8001\u4eba\u5fc3\u4e2d\u96be\u8fc7\uff1a\u201c\u5728\u8fd9\u66b4\u5ba2\u7684\u5200\u5b50\u4e4b\u524d\uff0c\u5979\u6709\u4ec0\u4e48\u6cd5\u5b50\uff1f\u201d \u3000\u3000\u674e\u6587\u79c0\u770b\u770b\u82cf\u666e\u7684\u795e\u60c5\uff0c\u5fc3\u4e2d\u53c8\u662f\u51c4\u51c9\uff0c\u53c8\u662f\u751c\u871c\uff1a\u201c\u4ed6\u4e00\u76f4\u8bb0\u7740\u6211\uff0c\u4ed6\u4e3a\u4e86\u4fdd\u62a4\u6211\u7684\u9057\u7269\uff0c\u7adf\u8981\u8ddf\u4eba\u62d4\u5200\u5b50\u62da\u547d\u3002\u201d \u3000\u3000\u4f46\u5fc3\u4e2d\u53c8\u5f88\u5947\u602a\uff1a\u201c\u8fd9\u6076\u5f3a\u76d7\u8bf4\u6211\u5077\u4e86\u4ed6\u7684\u5730\u56fe\uff0c\u5230\u5e95\u662f\u4ec0\u4e48\u5730\u56fe\uff1f\u201d\u5f53\u65e5\u5979\u6bcd\u4eb2\u901d\u4e16\u4e4b\u524d\uff0c\u5c06\u4e00\u526f\u5730\u56fe\u585e\u5728\u5979\u7684\u8863\u5185\uff0c\u5176\u65f6\u5371\u673a\u7d27\u8feb\uff0c\u6ca1\u6765\u5f97\u53ca\u7a0d\u52a0\u8bf4\u660e\uff0c\u6bcd\u5973\u4fe9\u5c31\u6b64\u5206\u624b\uff0c\u4ece\u6b64\u518d\u65e0\u76f8\u89c1\u4e4b\u65e5\u3002\u664b\u5a01\u9556\u5c40\u90a3\u4e00\u5e72\u5f3a\u4eba\u5341\u5e74\u6765\u8db3\u8ff9\u904d\u53ca\u5929\u5c71\u5357\u5317\uff0c\u627e\u5bfb\u5979\u7684\u4e0b\u843d\uff0c\u674e\u6587\u79c0\u81ea\u5df1\u5374\u662f\u534a\u70b9\u4e5f\u4e0d\u77e5\u60c5\u3002 \u3000\u3000\u9648\u8fbe\u6d77\u7ffb\u5bfb\u826f\u4e45\uff0c\u5168\u65e0\u5934\u7eea\uff0c\u5fc3\u4e2d\u6cae\u4e27\u4e4b\u6781\uff0c\u7a81\u7136\u5389\u58f0\u95ee\u9053\uff1a\u201c\u5979\u7684\u575f\u846c\u5728\u54ea\u91cc\uff1f\u201d\u8ba1\u8001\u4eba\u4e00\u5446\uff0c\u9053\uff1a\u201c\u846c\u5f97\u5f88\u8fdc\uff0c\u5f88\u8fdc\u3002\u201d\u9648\u8fbe\u6d77\u4ece\u5899\u4e0a\u53d6\u4e0b\u4e00\u67c4\u94c1\u936a\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5e26\u6211\u53bb\uff01\u201d\u82cf\u666e\u7ad9\u8d77\u8eab\u6765\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8981\u53bb\u5e72\u4e48\uff1f\u201d\u9648\u8fbe\u6d77\u9053\uff1a\u201c\u4f60\u7ba1\u5f97\u7740\u4e48\uff1f \u3000\u3000\u6211\u8981\u53bb\u6316\u5f00\u5979\u7684\u575f\u6765\u77a7\u77a7\uff0c\u8bf4\u4e0d\u5b9a\u90a3\u5e45\u5730\u56fe\u7ed9\u5979\u5e26\u5230\u4e86\u575f\u91cc\uff1f\u201d \u3000\u3000\u82cf\u666e\u6a2a\u5200\u62e6\u5728\u95e8\u53e3\uff0c\u559d\u9053\uff1a\u201c\u6211\u4e0d\u8bb8\u4f60\u53bb\u52a8\u5979\u575f\u5893\u3002\u201d\u9648\u8fbe\u6d77\u4e3e\u8d77\u94c1\u936a\uff0c\u5288\u780d\u6253\u53bb\uff0c\u559d\u9053\uff1a\u201c\u95ea\u5f00\uff01\u201d\u82cf\u666e\u5411\u5de6\u4e00\u8ba9\uff0c\u624b\u4e2d\u5200\u5b50\u9012\u4e86\u51fa\u53bb\u3002\u9648\u8fbe\u6d77\u629b\u5f00\u94c1\u936a\uff0c\u4ece\u8170\u95f4\u62d4\u51fa\u957f\u5251\uff0c\u53ee\u5f53\u4e00\u58f0\uff0c\u5200\u5251\u76f8\u4ea4\uff0c\u4e24\u4eba\u5404\u81ea\u5411\u540e\u8dc3\u5f00\u4e00\u6b65\uff0c\u968f\u5373\u540c\u65f6\u653b\u4e0a\uff0c\u6597\u5728\u4e00\u8d77\u3002 \u3000\u3000\u8fd9\u5c4b\u5b50\u7684\u5385\u5802\u672c\u4e0d\u751a\u5927\uff0c\u5200\u5251\u6325\u5904\uff0c\u8ba1\u8001\u4eba\u548c\u963f\u66fc\u90fd\u9000\u5728\u4e00\u65c1\uff0c\u9760\u58c1\u800c\u7acb\uff0c\u53ea\u6709\u674e\u6587\u79c0\u4ecd\u662f\u7ad9\u5728\u7a97\u524d\u3002\u963f\u66fc\u62a2\u8fc7\u53bb\u62d4\u8d77\u9648\u8fbe\u6d77\u63d2\u5728\u684c\u4e0a\u7684\u5c0f\u5251\uff0c\u60f3\u8981\u76f8\u52a9\u82cf\u666e\uff0c\u4f46\u4ed6\u4e8c\u4eba\u6597\u5f97\u6b63\u7d27\uff0c\u5374\u63d2\u4e0d\u4e0b\u624b\u53bb\u3002 \u3000\u3000\u82cf\u666e\u8fd9\u65f6\u5df2\u5c3d\u5f97\u4ed6\u7236\u4eb2\u82cf\u9c81\u514b\u7684\u4eb2\u4f20\uff0c\u5200\u6cd5\u53d8\u5e7b\uff0c\u62db\u6570\u6781\u662f\u51f6\u608d\uff0c\u521d\u65f6\u9648\u8fbe\u6d77\u9887\u843d\u4e0b\u98ce\uff0c\u5fc3\u4e2d\u6697\u6697\u60ca\u5f02\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u4e2a\u54c8\u8428\u514b\u5c0f\u5b50\uff0c\u6b66\u529f\u7adf\u4e0d\u5728\u4e2d\u539f\u7684\u597d\u624b\u4e4b\u4e0b\u3002\u201d\u4fbf\u5728\u6b64\u65f6\uff0c\u80cc\u540e\u98ce\u58f0\u5fae\u54cd\uff0c\u4e00\u67c4\u5c0f\u5251\u63b7\u4e86\u8fc7\u6765\uff0c\u5374\u662f\u963f\u66fc\u5ffd\u65bd\u5077\u88ad\u3002\u9648\u8fbe\u6d77\u5411\u53f3\u4e00\u8ba9\u907f\u5f00\uff0c\u55e4\u7684\u4e00\u58f0\u54cd\uff0c\u5de6\u81c2\u5df2\u88ab\u82cf\u666e\u7684\u77ed\u5200\u5212\u4e86\u4e00\u9053\u53e3\u5b50\u3002\u9648\u8fbe\u6d77\u5927\u6012\uff0c\u5237\u5237\u5237\u8fde\u523a\u4e09\u5251\uff0c\u4f7f\u51fa\u4ed6\u6210\u540d\u7edd\u6280\u201c\u9752\u87d2\u5251\u6cd5\u201d\u6765\u3002\u82cf\u666e\u4f46\u89c1\u773c\u524d\u5251\u5c16\u95ea\u52a8\uff0c\u72b9\u5982\u87d2\u86c7\u5410\u4fe1\u4e00\u822c\uff0c\u4e0d\u77e5\u4ed6\u5251\u5c16\u8981\u523a\u5411\u4f55\u5904\uff0c\u4e00\u4e2a\u6321\u67b6\u4e0d\u53ca\uff0c\u654c\u4eba\u7684\u957f\u5251\u5df2\u523a\u5230\u9762\u95e8\uff0c\u6025\u5fd9\u4fa7\u5934\u907f\u8ba9\uff0c\u9888\u65c1\u5df2\u7136\u4e2d\u5251\uff0c\u9c9c\u8840\u957f\u6d41\u3002\u9648\u8fbe\u6d77\u5f97\u7406\u4e0d\u8ba9\u4eba\uff0c\u53c8\u662f\u4e00\u5251\uff0c\u523a\u4e2d\u82cf\u666e\u624b\u8155\uff0c\u5f53\u5577\u4e00\u58f0\uff0c\u77ed\u5200\u6389\u5728\u5730\u4e0b\u3002 \u3000\u3000\u773c\u89c1\u4ed6\u7b2c\u4e09\u5251\u8ddf\u7740\u523a\u51fa\uff0c\u82cf\u666e\u65e0\u53ef\u62b5\u5fa1\uff0c\u52bf\u5c06\u6b7b\u4e8e\u975e\u547d\uff0c\u674e\u6587\u79c0\u8e0f\u51fa\u4e00\u6b65\uff0c\u53ea\u5f85\u4ed6\u523a\u5230\u7b2c\u4e09\u5251\u65f6\uff0c\u4fbf\u65bd\u5c55\u201c\u5927\u64d2\u62ff\u624b\u201d\u6293\u4ed6\u624b\u81c2\uff0c\u5374\u89c1\u963f\u66fc\u4e00\u8dc3\u800c\u524d\uff0c\u62e6\u5728\u82cf\u666e\u8eab\u524d\uff0c\u53eb\u9053\uff1a\u201c\u4e0d\u80fd\u4f24\u4ed6\uff01\u201d \u3000\u3000\u9648\u8fbe\u6d77\u89c1\u963f\u66fc\u5bb9\u989c\u5982\u82b1\uff0c\u5374\u6ee1\u8138\u662f\u60f6\u6025\u7684\u795e\u8272\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8fd9\u4e00\u5251\u4fbf\u4e0d\u523a\u51fa\uff0c\u5251\u5c16\u6307\u5728\u5979\u7684\u80f8\u53e3\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u822c\u5173\u5fc3\u4ed6\uff0c\u8fd9\u5c0f\u5b50\u662f\u4f60\u7684\u60c5\u90ce\u4e48\uff1f\u201d\u963f\u66fc\u8138\u4e0a\u4e00\u7ea2\uff0c\u70b9\u4e86\u70b9\u5934\u3002\u9648\u8fbe\u6d77\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8981\u6211\u9976\u4ed6\u6027\u547d\u4e5f\u4f7f\u5f97\uff0c\u660e\u5929\u98ce\u96ea\u4e00\u6b62\uff0c\u4f60\u4fbf\u5f97\u8ddf\u6211\u8d70\uff01\u201d \u3000\u3000\u82cf\u666e\u5927\u6012\uff0c\u543c\u53eb\u4e00\u58f0\uff0c\u4ece\u963f\u66fc\u8eab\u540e\u6251\u4e86\u51fa\u6765\u3002\u9648\u8fbe\u6d77\u957f\u5251\u4e00\u6296\uff0c\u5df2\u6307\u4f4f\u4ed6\u54bd\u5589\uff0c\u5de6\u811a\u53c8\u5728\u4ed6\u5c0f\u817f\u4e0a\u4e00\u626b\uff0c\u82cf\u666e\u6251\u5730\u6454\u5012\uff0c\u90a3\u957f\u5251\u4ecd\u662f\u6307\u5728\u4ed6\u5589\u5934\u3002\u674e\u6587\u79c0\u7ad9\u5728\u4e00\u65c1\uff0c\u770b\u5f97\u751a\u51c6\uff0c\u53ea\u8981\u9648\u8fbe\u6d77\u771f\u6709\u76f8\u5bb3\u82cf\u666e\u4e4b\u610f\uff0c\u5979\u7acb\u65f6\u4fbf\u51fa\u624b\u89e3\u6551\u3002\u8fd9\u65f6\u4ee5\u5979\u6b66\u529f\uff0c\u8981\u5bf9\u4ed8\u8fd9\u4eba\u5b9e\u662f\u6e38\u5203\u6709\u4f59\u3002 \u3000\u3000\u4f46\u963f\u66fc\u600e\u77e5\u5927\u63f4\u4fbf\u5728\u8eab\u65c1\uff0c\u60c5\u6025\u4e4b\u4e0b\uff0c\u53ea\u5f97\u8bf4\u9053\uff1a\u201c\u4f60\u522b\u523a\uff0c\u6211\u7b54\u5e94\u4e86\u4fbf\u662f\u3002\u201d\u9648\u8fbe\u6d77\u5927\u559c\uff0c\u5251\u5c16\u5374\u4e0d\u79fb\u5f00\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7b54\u5e94\u660e\u5929\u8ddf\u7740\u6211\u8d70\uff0c\u53ef\u4e0d\u8bb8\u53cd\u6094\u3002\u201d\u963f\u66fc\u54ac\u7259\u9053\uff1a\u201c\u6211\u4e0d\u53cd\u6094\uff0c\u4f60\u628a\u5251\u62ff\u5f00\u3002\u201d\u9648\u8fbe\u6d77\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4fbf\u8981\u53cd\u6094\uff0c\u4e5f\u9003\u4e0d\u4e86\uff01\u201d\u5c06\u957f\u5251\u6536\u5165\u9798\u4e2d\uff0c\u53c8\u628a\u82cf\u666e\u7684\u77ed\u5200\u6361\u4e86\u8d77\u6765\uff0c\u63e1\u5728\u624b\u4e2d\u3002\u8fd9\u4e48\u4e00\u6765\uff0c\u5c4b\u4e2d\u4fbf\u53ea\u4ed6\u4e00\u4eba\u8eab\u4e0a\u5e26\u6709\u5175\u5203\uff0c\u66f4\u52a0\u4e0d\u6015\u5404\u4eba\u53cd\u6297\u3002\u4ed6\u5411\u7a97\u5916\u4e00\u671b\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f1a\u513f\u4e0d\u80fd\u51fa\u53bb\uff0c\u53ea\u597d\u7b49\u5929\u6674\u4e86\u518d\u53bb\u6398\u575f\u3002\u201d \u3000\u3000\u963f\u66fc\u5c06\u82cf\u666e\u6276\u5728\u4e00\u65c1\uff0c\u89c1\u4ed6\u5934\u9888\u4e2d\u6c69\u6c69\u6d41\u51fa\u9c9c\u8840\uff0c\u5f88\u662f\u614c\u4e71\uff0c\u4fbf\u8981\u6495\u4e0b\u81ea\u5df1\u8863\u895f\u7ed9\u4ed6\u88f9\u4f24\u3002\u82cf\u666e\u4ece\u6000\u4e2d\u638f\u51fa\u4e00\u5757\u5927\u624b\u5e15\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u7528\u8fd9\u624b\u5e15\u5305\u4f4f\u5427\uff01\u201d\u963f\u66fc\u63a5\u4f4f\u624b\u5e15\uff0c\u66ff\u4ed6\u5305\u597d\u4e86\u4f24\u53e3\uff0c\u60f3\u5230\u81ea\u5df1\u843d\u5165\u4e86\u8fd9\u5f3a\u4eba\u624b\u91cc\uff0c\u4e0d\u77e5\u662f\u5426\u6709\u8131\u8eab\u4e4b\u673a\uff0c\u4e0d\u7981\u6389\u4e0b\u6cea\u6765\u3002\u82cf\u666e\u4f4e\u58f0\u9a82\u9053\uff1a\u201c\u72d7\u5f3a\u76d7\uff0c\u8d3c\u5f3a\u76d7\uff01\u201d\u8fd9\u65f6\u65e9\u5df2\u6253\u5b9a\u4e86\u4e3b\u610f\uff0c\u5982\u679c\u8fd9\u5f3a\u76d7\u771f\u7684\u8981\u5e26\u963f\u66fc\u8d70\uff0c\u4fbf\u662f\u660e\u77e5\u8981\u9001\u4e86\u6027\u547d\uff0c\u4e5f\u662f\u51b3\u6b7b\u4e00\u62da\u3002 \u3000"}),showOnHomepage:!0},{name:"\u516d",content:(0,a.jsx)(a.Fragment,{children:"\u7ecf\u8fc7\u4e86\u9002\u624d\u8fd9\u4e00\u573a\u4e89\u6597\uff0c\u4e94\u4e2a\u4eba\u56f4\u5728\u706b\u5806\u4e4b\u65c1\uff0c\u5fc3\u60c5\u90fd\u662f\u5341\u5206\u7d27\u5f20\u3002\u9648\u8fbe\u6d77\u4e00\u624b\u6301\u5200\uff0c\u4e00\u624b\u62ff\u7740\u9152\u7897\uff0c\u65f6\u65f6\u77a7\u77a7\u963f\u66fc\uff0c\u53c8\u77a7\u77a7\u82cf\u666e\u3002\u5c4b\u5916\u5317\u98ce\u6012\u53f7\uff0c\u5377\u8d77\u4e00\u56e2\u56e2\u96ea\u5757\uff0c\u62cd\u6253\u5728\u5899\u58c1\u5c4b\u9876\u3002\u8c01\u90fd\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u674e\u6587\u79c0\u5fc3\u4e2d\u5728\u60f3\uff1a\u201c\u4e14\u8ba9\u8fd9\u6076\u8d3c\u518d\u7316\u72c2\u4e00\u4f1a\uff0c\u4e0d\u5fd9\u4fbf\u6740\u4ed6\u3002\u201d\u7a81\u7136\u95f4\u706b\u5806\u4e2d\u4e00\u4e2a\u67f4\u8282\u7206\u88c2\u4e86\u8d77\u6765\uff0c\u556a\u7684\u4e00\u54cd\uff0c\u706b\u5934\u6697\u4e86\u4e00\u6697\uff0c\u8ddf\u7740\u4fbf\u5341\u5206\u660e\u4eae\uff0c\u7167\u5f97\u5404\u4eba\u7684\u8138\u8272\u6e05\u6e05\u695a\u695a\u3002\u674e\u6587\u79c0\u770b\u5230\u4e86\u82cf\u666e\u5934\u9888\u4e2d\u88f9\u7740\u7684\u624b\u5e15\uff0c\u5fc3\u4e2d\u4e00\u51db\uff0c\u76ee\u4e0d\u8f6c\u77ac\u7684\u77a7\u7740\u3002\u8ba1\u8001\u4eba\u89c1\u5230\u5979\u76ee\u5149\u6709\u5f02\uff0c\u4e5f\u5411\u90a3\u624b\u5e15\u671b\u4e86\u51e0\u773c\uff0c\u95ee\u9053\uff1a\u201c\u82cf\u666e\uff0c\u4f60\u8fd9\u5757\u624b\u5e15\u662f\u54ea\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u82cf\u666e\u4e00\u695e\uff0c\u624b\u629a\u5934\u9888\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u8fd9\u5757\u624b\u5e15\u4e48\uff1f\u5c31\u662f\u90a3\u6b7b\u4e86\u7684\u963f\u79c0\u7ed9\u6211\u7684\u3002\u5c0f\u65f6\u5019\u6211\u4eec\u5728\u4e00\u8d77\u7267\u7f8a\uff0c\u6709\u4e00\u53ea\u5927\u7070\u72fc\u6765\u54ac\u6211\u4eec\uff0c\u6211\u6740\u4e86\u90a3\u5934\u72fc\uff0c\u4f46\u4e5f\u7ed9\u72fc\u54ac\u4f24\u4e86\u3002\u963f\u79c0\u5c31\u7528\u8fd9\u624b\u5e15\u7ed9\u6211\u88f9\u4f24\u2026\u2026\u201d \u3000\u3000\u674e\u6587\u79c0\u542c\u7740\u8fd9\u4e9b\u8bdd\u65f6\uff0c\u770b\u51fa\u6765\u7684\u4e1c\u897f\u90fd\u6a21\u7cca\u4e86\uff0c\u539f\u6765\u773c\u4e2d\u5df2\u5145\u6ee1\u4e86\u6cea\u6c34\u3002 \u3000\u3000\u8ba1\u8001\u4eba\u8d70\u8fdb\u5185\u5ba4\uff0c\u53d6\u4e86\u4e00\u5757\u767d\u5e03\u51fa\u6765\uff0c\u4ea4\u7ed9\u82cf\u666e\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7528\u8fd9\u5757\u5e03\u88f9\u4f24\uff0c\u8bf7\u4f60\u628a\u624b\u5e15\u89e3\u4e0b\u6765\u7ed9\u6211\u77a7\u77a7\u3002\u201d\u82cf\u666e\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u9648\u8fbe\u6d77\u5f53\u8ba1\u8001\u4eba\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u4e00\u76f4\u5bf9\u82cf\u666e\u9888\u4e2d\u90a3\u5757\u624b\u5e15\u6ce8\u76ee\u7ec6\u770b\uff0c\u8fd9\u65f6\u7a81\u7136\u63d0\u5200\u7ad9\u8d77\uff0c\u559d\u9053\uff1a\u201c\u53eb\u4f60\u89e3\u4e0b\u6765\u4fbf\u89e3\u4e0b\u6765\u3002\u201d\u82cf\u666e\u6012\u76ee\u4e0d\u52a8\u3002\u963f\u66fc\u6015\u9648\u8fbe\u6d77\u7528\u5f3a\uff0c\u66ff\u82cf\u666e\u89e3\u4e0b\u624b\u5e15\uff0c\u4ea4\u7ed9\u4e86\u8ba1\u8001\u4eba\uff0c\u968f\u5373\u53c8\u7528\u767d\u5e03\u66ff\u82cf\u666e\u88f9\u4f24\u3002 \u3000\u3000\u8ba1\u8001\u4eba\u5c06\u90a3\u67d3\u4e86\u9c9c\u8840\u7684\u624b\u5e15\u94fa\u5728\u684c\u4e0a\uff0c\u5254\u4eae\u6cb9\u706f\uff0c\u9644\u8eab\u7ec6\u770b\u3002\u9648\u8fbe\u6d77\u77aa\u89c6\u4e86\u4e00\u4f1a\uff0c\u7a81\u7136\u559c\u547c\uff1a\u201c\u662f\u4e86\uff0c\u662f\u4e86\uff0c\u8fd9\u4fbf\u662f\u9ad8\u660c\u8ff7\u5bab\u7684\u5730\u56fe\uff01\u201d\u4e00\u4f38\u624b\u4fbf\u6293\u8d77\u4e86\u624b\u5e15\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u559c\u4e0d\u81ea\u80dc\u3002 \u3000\u3000\u8ba1\u8001\u4eba\u53f3\u81c2\u4e00\u52a8\uff0c\u4f3c\u6b32\u62a2\u593a\u624b\u5e15\uff0c\u4f46\u7ec8\u4e8e\u5f3a\u81ea\u5fcd\u4f4f\u3002 \u3000\u3000\u4fbf\u5728\u6b64\u65f6\uff0c\u5ffd\u542c\u5f97\u8fdc\u5904\u6709\u4eba\u53eb\u9053\uff1a\u201c\u82cf\u666e\uff0c\u82cf\u666e\u2026\u2026\u201d\u53c8\u6709\u4eba\u5927\u58f0\u53eb\u9053\uff1a\u201c\u963f\u66fc\uff0c\u963f\u66fc\u54ea\u2026\u2026\u201d\u82cf\u666e\u548c\u963f\u66fc\u540c\u65f6\u8dc3\u8d77\u8eab\u6765\uff0c\u9f50\u58f0\u53eb\u9053\uff1a\u201c\u7239\u7239\u5728\u627e\u54b1\u4eec\u3002\u201d\u82cf\u666e\u5954\u5230\u95e8\u8fb9\uff0c\u5f85\u8981\u5f00\u95e8\uff0c\u7a81\u7136\u540e\u9888\u4e00\u51c9\uff0c\u4e00\u67c4\u957f\u5251\u67b6\u5728\u9888\u4e2d\u3002\u9648\u8fbe\u6d77\u51b7\u51b7\u7684\u9053\uff1a\u201c\u7ed9\u6211\u5750\u4e0b\uff0c\u4e0d\u8bb8\u52a8\uff01\u201d\u82cf\u666e\u65e0\u5948\uff0c\u53ea\u5f97\u9893\u7136\u5750\u4e0b\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u4e24\u4e2a\u4eba\u7684\u811a\u6b65\u58f0\u8d70\u5230\u4e86\u95e8\u53e3\u3002\u53ea\u542c\u82cf\u9c81\u514b\u9053\uff1a\u201c\u8fd9\u662f\u90a3\u8d3c\u6c49\u4eba\u7684\u5bb6\u5417\uff1f\u6211\u4e0d\u8fdb\u53bb\u3002\u201d\u8f66\u5c14\u5e93\u9053\uff1a\u201c\u4e0d\u8fdb\u53bb\uff1f\u5374\u5230\u54ea\u91cc\u907f\u98ce\u96ea\u53bb\uff1f\u6211\u8033\u6735\u90fd\u51bb\u5f97\u8981\u6389\u4e0b\u6765\u5566\u3002\u201d \u3000\u3000\u82cf\u9c81\u514b\u624b\u4e2d\u62ff\u7740\u4e2a\u9152\u846b\u82a6\uff0c\u4e00\u76f4\u5728\u8def\u4e0a\u559d\u9152\u4ee5\u9a71\u5bd2\u6c14\uff0c\u8fd9\u65f6\u5df2\u6709\u516b\u4e5d\u5206\u9152\u610f\uff0c\u9189\u91ba\u91ba\u7684\u9053\uff1a\u201c\u6211\u5b81\u53ef\u51bb\u6389\u8111\u888b\uff0c\u4e5f\u4e0d\u8fdb\u6c49\u4eba\u7684\u5bb6\u91cc\u3002\u201d\u8f66\u5c14\u5e93\u9053\uff1a\u201c\u4f60\u4e0d\u8fdb\u53bb\uff0c\u5728\u98ce\u96ea\u91cc\u51bb\u6b7b\u4e86\u5427\uff0c\u6211\u53ef\u8981\u8fdb\u53bb\u4e86\u3002\u201d\u82cf\u9c81\u514b\u9053\uff1a\u201c\u6211\u513f\u5b50\u548c\u4f60\u5973\u513f\u90fd\u6ca1\u627e\u5230\uff0c\u600e\u4e48\u5c31\u5230\u8d3c\u6c49\u4eba\u7684\u5bb6\u91cc\u8eb2\u907f\uff1f\u4f60\u2026\u2026\u4f60\u534a\u5206\u82f1\u96c4\u6c14\u6982\u4e5f\u6ca1\u6709\u3002\u201d\u8f66\u5c14\u5e93\u9053\uff1a\u201c\u4e00\u8def\u4e0a\u6ca1\u89c1\u4ed6\u4e8c\u4eba\uff0c\u5b9a\u662f\u5728\u54ea\u91cc\u8eb2\u8d77\u6765\u4e86\uff0c\u4e0d\u7528\u62c5\u5fc3\u3002\u522b\u8981\u4e24\u4e2a\u5c0f\u7684\u6ca1\u627e\u5230\uff0c\u4e24\u4e2a\u8001\u7684\u5148\u51bb\u6b7b\u4e86\u3002\u201d \u3000\u3000\u82cf\u666e\u89c1\u9648\u8fbe\u6d77\u633a\u8d77\u957f\u5251\u8eb2\u5728\u95e8\u8fb9\uff0c\u53ea\u5f85\u6709\u4eba\u8fdb\u6765\u4fbf\u662f\u4e00\u5251\uff0c\u60c5\u52bf\u6781\u662f\u5371\u6025\uff0c\u53eb\u9053\uff1a\u201c\u4e0d\u80fd\u8fdb\u6765\uff01\u201d\u9648\u8fbe\u6d77\u77aa\u76ee\u559d\u9053\uff1a\u201c\u4f60\u518d\u51fa\u58f0\uff0c\u6211\u7acb\u65f6\u6740\u4e86\u4f60\u3002\u201d\u82cf\u666e\u89c1\u7236\u4eb2\u5904\u5883\u5371\u9669\uff0c\u63d0\u8d77\u51f3\u5b50\u4fbf\u5411\u9648\u8fbe\u6d77\u6251\u5c06\u8fc7\u53bb\u3002\u9648\u8fbe\u6d77\u4fa7\u8eab\u907f\u5f00\uff0c\u5237\u7684\u4e00\u5251\uff0c\u6b63\u4e2d\u82cf\u666e\u5927\u817f\u3002\u82cf\u666e\u5927\u53eb\u4e00\u58f0\uff0c\u7ffb\u5012\u5728\u5730\u3002\u4ed6\u8eab\u624b\u751a\u662f\u654f\u6377\uff0c\u751f\u6015\u654c\u4eba\u53c8\u662f\u4e00\u5251\u780d\u4e0b\uff0c\u5f53\u5373\u4e00\u4e2a\u6253\u6eda\uff0c\u6eda\u51fa\u6570\u5c3a\u3002 \u3000\u3000\u9648\u8fbe\u6d77\u5374\u4e0d\u8ffd\u51fb\uff0c\u53ea\u662f\u4e3e\u5251\u5b88\u5728\u95e8\u540e\uff0c\u5fc3\u60f3\u8fd9\u54c8\u8428\u514b\u5c0f\u5b50\u8f6c\u773c\u4fbf\u80fd\u6599\u7406\uff0c\u4e14\u8ba9\u4ed6\u591a\u6d3b\u7247\u523b\uff0c\u5916\u9762\u6765\u7684\u4e8c\u4eba\u5374\u987b\u5148\u884c\u780d\u7ffb\u3002 \u3000\u3000\u53ea\u542c\u95e8\u5916\u82cf\u9c81\u514b\u5927\u7740\u820c\u5934\u53eb\u9053\uff1a\u201c\u4f60\u8981\u8fdb\u8be5\u6b7b\u7684\u6c49\u4eba\u5bb6\u91cc\uff0c\u6211\u5c31\u6253\u4f60\uff01\u201d\u8bf4\u7740\u4fbf\u662f\u4e00\u62f3\uff0c\u6b63\u597d\u6253\u5728\u8f66\u5c14\u5e93\u7684\u80f8\u53e3\u3002\u8f66\u5c14\u5e93\u82e5\u5728\u5e73\u65f6\uff0c\u77e5\u4ed6\u662f\u4e2a\u9189\u6c49\uff0c\u867d\u5403\u4e86\u91cd\u91cd\u4e00\u62f3\uff0c\u81ea\u4e5f\u4e0d\u4f1a\u8ddf\u4ed6\u8ba1\u8f83\uff0c\u4f46\u8fd9\u65f6\u809a\u91cc\u7684\u9152\u4e5f\u6d8c\u4e86\u4e0a\u6765\uff0c\u4f38\u8db3\u4fbf\u662f\u4e00\u52fe\u3002\u82cf\u9c81\u514b\u672c\u5df2\u7ad9\u7acb\u4e0d\u5b9a\uff0c\u7ed9\u4ed6\u4e00\u7eca\uff0c\u767b\u65f6\u6454\u5012\uff0c\u4f46\u8d81\u52bf\u62b1\u4f4f\u4e86\u4ed6\u7684\u5c0f\u817f\u3002\u4e24\u4eba\u4fbf\u5728\u96ea\u5730\u4e2d\u7ffb\u7ffb\u6eda\u6eda\u7684\u6253\u4e86\u8d77\u6765\u3002 \u3000\u3000\u84e6\u5730\u91cc\u82cf\u9c81\u514b\u6293\u8d77\u5730\u4e0b\u4e00\u56e2\u96ea\uff0c\u585e\u5728\u8f66\u5c14\u5e93\u5634\u91cc\uff0c\u8f66\u5c14\u5e93\u6025\u5fd9\u4f38\u624b\u4e71\u6293\u4e71\u6316\uff0c\u82cf\u9c81\u514b\u4e50\u5f97\u54c8\u54c8\u5927\u7b11\u3002\u8f66\u5c14\u5e93\u5410\u51fa\u4e86\u5634\u91cc\u7684\u96ea\uff0c\u7830\u7684\u4e00\u62f3\uff0c\u6253\u5f97\u82cf\u9c81\u514b\u9f3b\u5b50\u4e0a\u9c9c\u8840\u957f\u6d41\u3002\u82cf\u9c81\u514b\u5e76\u4e0d\u89c9\u5f97\u75db\uff0c\u4ecd\u662f\u7b11\u58f0\u4e0d\u7edd\uff0c\u5374\u63ea\u4f4f\u4e86\u8f66\u5c14\u5e93\u7684\u5934\u53d1\u4e0d\u653e\u3002\u4e24\u4eba\u90fd\u662f\u54c8\u8428\u514b\u65cf\u4e2d\u5343\u91cc\u9a70\u540d\u7684\u52c7\u58eb\uff0c\u4f46\u9152\u9189\u4e4b\u540e\u76f8\u640f\uff0c\u7adf\u5982\u987d\u7ae5\u6253\u67b6\u4e00\u822c\u3002 \u3000\u3000\u82cf\u666e\u548c\u963f\u66fc\u5fc3\u4e2d\u7126\u6025\u5f02\u5e38\uff0c\u90fd\u76fc\u82cf\u9c81\u514b\u6253\u80dc\uff0c\u4fbf\u53ef\u963b\u6b62\u8f66\u5c14\u5e93\u8fdb\u6765\u3002\u4f46\u542c\u5f97\u95e8\u5916\u7830\u7830\u562d\u562d\u4e4b\u58f0\u4e0d\u7edd\uff0c\u4f60\u6253\u6211\u4e00\u62f3\uff0c\u6211\u6253\u4f60\u4e00\u62f3\uff0c\u53c8\u7b11\u53c8\u9a82\uff0c\u9189\u8bdd\u8fde\u7bc7\u3002\u7a81\u7136\u4e4b\u95f4\uff0c\u8f70\u9686\u4e00\u58f0\u5927\u54cd\uff0c\u677f\u95e8\u649e\u5f00\uff0c\u5bd2\u98ce\u5939\u96ea\u6251\u8fdb\u95e8\u6765\uff0c\u540c\u65f6\u82cf\u9c81\u514b\u548c\u8f66\u5c14\u5e93\u4e92\u76f8\u6402\u62b1\uff0c\u7740\u5730\u7ffb\u6eda\u800c\u8fdb\u3002\u677f\u95e8\u8fd9\u4e00\u4e0b\u84e6\u5730\u649e\u5f00\uff0c\u5374\u5c06\u9648\u8fbe\u6d77\u5939\u5728\u95e8\u540e\uff0c\u4ed6\u8fd9\u4e00\u5251\u4fbf\u780d\u4e0d\u4e0b\u53bb\u3002\u53ea\u89c1\u82cf\u9c81\u514b\u548c\u8f66\u5c14\u5e93\u8fdb\u4e86\u5c4b\u91cc\uff0c\u4ecd\u662f\u626d\u6253\u4e0d\u4f11\u3002 \u3000\u3000\u8f66\u5c14\u5e93\u9053\uff1a\u201c\u4f60\u8fd9\u4e0d\u662f\u8fdb\u6765\u4e86\u5417\uff1f\u201d\u82cf\u9c81\u514b\u5927\u6012\uff0c\u624b\u81c2\u627c\u4f4f\u4ed6\u8116\u5b50\uff0c\u53ea\u56b7\uff1a\u201c\u51fa\u53bb\uff0c\u51fa\u53bb\uff01\u201d\u4e24\u4eba\u5728\u5730\u4e0b\u4e71\u626d\uff0c\u4e00\u4e2a\u8981\u62d6\u7740\u5bf9\u65b9\u51fa\u53bb\uff0c\u53e6\u4e00\u4e2a\u5374\u60f3\u6309\u4f4f\u5bf9\u65b9\uff0c\u4e0d\u8ba9\u4ed6\u52a8\u5f39\u3002\u5ffd\u7136\u95f4\u82cf\u9c81\u514b\u5531\u8d77\u6b4c\u6765\uff0c\u53c8\u53eb\uff1a\u201c\u4f60\u6253\u6211\u4e0d\u8fc7\uff0c\u6211\u662f\u54c8\u8428\u514b\u7b2c\u4e00\u52c7\u58eb\uff0c\u82cf\u666e\u7b2c\u4e8c\uff0c\u82cf\u666e\u5c06\u6765\u751f\u7684\u513f\u5b50\u7b2c\u4e09\u2026\u2026\u4f60\u8f66\u5c14\u5e93\u7b2c\u4e94\u2026\u2026\u201d \u3000\u3000\u9648\u8fbe\u6d77\u89c1\u662f\u4e24\u4e2a\u9189\u6c49\uff0c\u5fc3\u60f3\u90a3\u4e5f\u4e0d\u8db3\u4e3a\u60e7\u3002\u5176\u65f6\u98ce\u52bf\u751a\u52b2\uff0c\u53ea\u522e\u5f97\u706b\u5806\u4e2d\u706b\u661f\u4e71\u98de\uff0c\u9648\u8fbe\u6d77\u5fd9\u7528\u529b\u5173\u4e0a\u4e86\u95e8\u3002\u82cf\u666e\u548c\u963f\u66fc\u89c1\u81ea\u5df1\u7236\u4eb2\u6eda\u5411\u706b\u5806\uff0c\u5fd9\u8fc7\u53bb\u6276\uff0c\u540c\u65f6\u53eb\uff1a\u201c\u7239\u7239\uff0c\u7239\u7239\u3002\u201d\u4f46\u4e24\u4eba\u8eab\u8eaf\u6c89\u91cd\uff0c\u4e00\u65f6\u54ea\u91cc\u6276\u5f97\u8d77\u6765\uff1f \u3000\u3000\u82cf\u666e\u53eb\u9053\uff1a\u201c\u7239\uff0c\u7239\uff01\u8fd9\u4eba\u662f\u6c49\u4eba\u5f3a\u76d7\uff01\u201d \u3000\u3000\u82cf\u9c81\u514b\u867d\u7136\u5927\u9189\uff0c\u4f46\u5341\u5e74\u6765\u5ff5\u5ff5\u4e0d\u5fd8\u6c49\u4eba\u5f3a\u76d7\u7684\u6df1\u4ec7\u5927\u6068\uff0c\u4e00\u542c\u201c\u6c49\u4eba\u5f3a\u76d7\u201d\u56db\u5b57\uff0c\u767b\u65f6\u6e05\u9192\u4e86\u4e09\u5206\uff0c\u4e00\u8dc3\u800c\u8d77\uff0c\u53eb\u9053\uff1a\u201c\u6c49\u4eba\u5f3a\u76d7\u5728\u54ea\u91cc\uff1f\u201d\u82cf\u666e\u5411\u9648\u8fbe\u6d77\u4e00\u6307\u3002\u82cf\u9c81\u514b\u4f38\u624b\u4fbf\u53bb\u8170\u95f4\u62d4\u5200\uff0c\u4f46\u4ed6\u548c\u8f66\u5c14\u5e93\u4e8c\u4eba\u4e71\u6253\u4e00\u9635\uff0c\u5c06\u5200\u5b50\u90fd\u6389\u5728\u95e8\u5916\u96ea\u5730\u4e4b\u4e2d\uff0c\u4ed6\u6478\u4e86\u4e2a\u7a7a\uff0c\u53eb\u9053\uff1a\u201c\u5200\u5462\uff0c\u5200\u5462\uff1f\u6211\u6740\u4e86\u4ed6\uff01\u201d \u3000\u3000\u9648\u8fbe\u6d77\u957f\u5251\u4e00\u633a\uff0c\u6307\u5728\u4ed6\u5589\u5934\uff0c\u559d\u9053\uff1a\u201c\u8dea\u4e0b\uff01\u201d\u82cf\u9c81\u514b\u5927\u6012\uff0c\u548c\u8eab\u6251\u4e0a\uff0c\u4f46\u7ec8\u662f\u9152\u540e\u4e4f\u529b\uff0c\u6ca1\u6251\u5230\u654c\u4eba\u8eab\u524d\uff0c\u81ea\u5df1\u4fbf\u5df2\u6454\u5012\u3002\u9648\u8fbe\u6d77\u4e00\u58f0\u51b7\u7b11\uff0c\u6325\u5251\u780d\u4e0b\uff0c\u767b\u65f6\u82cf\u9c81\u514b\u80a9\u5934\u8840\u5149\u8ff8\u73b0\u3002\u82cf\u9c81\u514b\u5927\u58f0\u60e8\u53eb\uff0c\u8981\u7ad9\u8d77\u62da\u547d\uff0c\u53ef\u662f\u4e24\u6761\u817f\u4fbf\u5982\u70c2\u6ce5\u76f8\u4f3c\uff0c\u8bf4\u4ec0\u4e48\u4e5f\u7ad9\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u8f66\u5c14\u5e93\u6012\u543c\u7eb5\u8d77\uff0c\u5411\u9648\u8fbe\u6d77\u5954\u8fc7\u53bb\u3002\u9648\u8fbe\u6d77\u4e00\u5251\u523a\u51fa\uff0c\u6b63\u4e2d\u4ed6\u53f3\u817f\uff0c\u8f66\u5c14\u5e93\u7acb\u65f6\u6454\u5012\u3002 \u3000\u3000\u8ba1\u8001\u4eba\u8f6c\u5934\u5411\u674e\u6587\u79c0\u77a7\u53bb\uff0c\u53ea\u89c1\u5979\u795e\u8272\u9547\u5b9a\uff0c\u7adf\u65e0\u60e7\u6015\u4e4b\u610f\u3002 \u3000\u3000\u9648\u8fbe\u6d77\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u8fd9\u4e9b\u54c8\u8428\u514b\u72d7\uff0c\u4eca\u65e5\u4e00\u4e2a\u4e2a\u90fd\u628a\u4f60\u4eec\u5bb0\u4e86\u3002\u201d\u963f\u66fc\u5954\u4e0a\u53bb\u6321\u5728\u7236\u4eb2\u8eab\u524d\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u6211\u7b54\u5e94\u8ddf\u4f60\u53bb\uff0c\u4f60\u5c31\u4e0d\u80fd\u6740\u4ed6\u4eec\u3002\u201d\u8f66\u5c14\u5e93\u6012\u9053\uff1a\u201c\u4e0d\u884c\uff01\u4e0d\u80fd\u8ddf\u8fd9\u72d7\u5f3a\u76d7\u53bb\uff0c\u8ba9\u4ed6\u6740\u6211\u597d\u4e86\u3002\u201d \u3000\u3000\u9648\u8fbe\u6d77\u4ece\u5899\u4e0a\u53d6\u4e0b\u4e00\u6761\u5957\u7f8a\u7684\u957f\u7d22\uff0c\u5c06\u5708\u5b50\u5957\u5728\u963f\u66fc\u7684\u9888\u91cc\uff0c\u72de\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f60\u662f\u6211\u7684\u4fd8\u864f\uff0c\u662f\u6211\u5974\u96b6\uff01\u4f60\u7acb\u4e0b\u8a93\u6765\uff0c\u4ece\u4eca\u4e0d\u5f97\u80cc\u53db\u4e86\u6211\uff0c\u90a3\u5c31\u9976\u4e86\u8fd9\u51e0\u4e2a\u54c8\u8428\u514b\u72d7\u5b50\uff01\u201d \u3000\u3000\u963f\u66fc\u6cea\u6c34\u6251\u7c0c\u7c0c\u7684\u6d41\u4e0b\uff0c\u5fc3\u60f3\u81ea\u5df1\u82e5\u4e0d\u7b54\u5e94\uff0c\u7236\u4eb2\u548c\u82cf\u666e\u90fd\u8981\u7ed9\u4ed6\u6740\u4e86\uff0c\u53ea\u5f97\u8d77\u8a93\u9053\uff1a\u201c\u963f\u62c9\u771f\u4e3b\u5728\u4e0a\uff0c\u4ece\u4eca\u4ee5\u540e\uff0c\u6211\u662f\u6211\u4e3b\u4eba\u7684\u5974\u96b6\uff0c\u542c\u4ed6\u4e00\u5207\u5429\u5490\uff0c\u6c38\u8fdc\u4e0d\u6562\u9003\u8d70\uff0c\u4e0d\u6562\u8fdd\u80cc\u4ed6\u547d\u4ee4\uff01\u5426\u5219\u6b7b\u540e\u5815\u5165\u706b\u7a9f\uff0c\u4e07\u52ab\u4e0d\u5f97\u8d85\u751f\u3002\u201d \u3000\u3000\u9648\u8fbe\u6d77\u54c8\u54c8\u5927\u7b11\uff0c\u5f97\u610f\u4e4b\u6781\uff0c\u4eca\u665a\u65e2\u5f97\u9ad8\u660c\u8ff7\u5bab\u7684\u5730\u56fe\uff0c\u53c8\u5f97\u4e86\u8fd9\u4e2a\u5982\u6b64\u7f8e\u8c8c\u5c11\u5973\uff0c\u5f53\u771f\u662f\u5feb\u6d3b\u80dc\u4e8e\u767b\u4ed9\u3002\u4ed6\u4e45\u5728\u56de\u7586\uff0c\u77e5\u9053\u54c8\u8428\u514b\u4eba\u8654\u4fe1\u56de\u6559\uff0c\u53ea\u8981\u51ed\u7740\u771f\u4e3b\u963f\u62c9\u7684\u540d\u8d77\u8a93\uff0c\u7ec8\u751f\u4e0d\u6562\u80cc\u53db\uff0c\u4e8e\u662f\u4e00\u62c9\u957f\u7d22\uff0c\u8bf4\u9053\uff1a\u201c\u8fc7\u6765\uff0c\u5750\u5728\u4f60\u4e3b\u4eba\u7684\u811a\u8fb9\uff01\u201d\u963f\u66fc\u5fc3\u4e2d\u59d4\u5c48\u4e07\u5206\uff0c\u53ea\u5f97\u8d70\u5230\u4ed6\u8db3\u8fb9\u5750\u4e0b\u3002\u9648\u8fbe\u6d77\u4f38\u624b\u629a\u6478\u5979\u7684\u5934\u53d1\uff0c\u963f\u66fc\u5fcd\u4e0d\u4f4f\u653e\u58f0\u5927\u54ed\u3002 \u3000\u3000\u82cf\u666e\u8fd9\u65f6\u54ea\u91cc\u8fd8\u5fcd\u8010\u5f97\u4f4f\uff0c\u7eb5\u8eab\u8dc3\u8d77\uff0c\u5411\u9648\u8fbe\u6d77\u6251\u53bb\u3002\u9648\u8fbe\u6d77\u957f\u5251\u633a\u51fa\uff0c\u6307\u4f4f\u4ed6\u7684\u80f8\u819b\u3002\u82cf\u666e\u53ea\u987b\u518d\u4e0a\u524d\u534a\u5c3a\uff0c\u4fbf\u662f\u5c06\u81ea\u5df1\u80f8\u53e3\u523a\u5165\u4e86\u5251\u5c16\u3002\u963f\u66fc\u53eb\u9053\uff1a\u201c\u82cf\u666e\uff0c\u9000\u4e0b\uff01\u201d\u82cf\u666e\u53cc\u76ee\u4e2d\u5982\u8981\u55b7\u51fa\u706b\u6765\uff0c\u54ac\u7259\u5207\u9f7f\uff0c\u7ad9\u5728\u5f53\u5730\uff0c\u8fc7\u4e86\u597d\u4e00\u4f1a\uff0c\u7ec8\u4e8e\u4e00\u6b65\u6b65\u7684\u9000\u56de\uff0c\u9893\u7136\u5750\u5012\u5728\u5730\u3002 \u3000\u3000\u9648\u8fbe\u6d77\u659f\u4e86\u4e00\u7897\u9152\uff0c\u559d\u4e86\u4e00\u53e3\uff0c\u5c06\u90a3\u5757\u624b\u5e15\u53d6\u4e86\u51fa\u6765\uff0c\u653e\u5728\u819d\u5934\u7ec6\u770b\u3002 \u3000\u3000\u8ba1\u8001\u4eba\u5ffd\u9053\uff1a\u201c\u4f60\u600e\u77e5\u9053\u8fd9\u662f\u9ad8\u660c\u8ff7\u5bab\u7684\u5730\u56fe\uff1f\u201d\u8bf4\u7684\u662f\u6c49\u8bed\u3002\u9648\u8fbe\u6d77\u5fc3\u60f3\uff1a\u201c\u53cd\u6b63\u4f60\u4eec\u8fd9\u4e9b\u4eba\u4e00\u4e2a\u4e2a\u90fd\u6d3b\u4e0d\u8fc7\u4eca\u5929\uff0c\u8ddf\u4f60\u8bf4\u4e86\u4e5f\u81ea\u4e0d\u59a8\u3002\u201d\u4ed6\u5bfb\u8bbf\u5341\u4e8c\u5e74\uff0c\u5fc3\u613f\u7ec8\u4e8e\u5f97\u507f\uff0c\u6ee1\u8154\u6b22\u559c\uff0c\u539f\u662f\u4e0d\u5410\u4e0d\u5feb\uff0c\u8ba1\u8001\u4eba\u5c31\u7b97\u4e0d\u95ee\uff0c\u4ed6\u81ea\u8a00\u81ea\u8bed\u4e5f\u8981\u8bf4\u4e86\u51fa\u6765\uff0c\u4ed6\u53cc\u624b\u62ff\u7740\u624b\u5e15\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u67e5\u5f97\u5343\u771f\u4e07\u786e\uff0c\u9ad8\u660c\u8ff7\u5bab\u7684\u5730\u56fe\u662f\u767d\u9a6c\u674e\u4e09\u592b\u5987\u5f97\u4e86\u53bb\u3002\u4ed6\u4e8c\u4eba\u5c38\u8eab\u4e0a\u627e\u4e0d\u5230\uff0c\u5b9a\u662f\u5728\u4ed6\u4eec\u5973\u513f\u624b\u91cc\u3002\u8fd9\u5757\u624b\u5e15\u662f\u90a3\u59d3\u674e\u5c0f\u59d1\u5a18\u7684\uff0c\u4e0a\u9762\u53c8\u6709\u5c71\u5ddd\u9053\u8def\uff0c\u90a3\u81ea\u7136\u51b3\u8ba1\u4e0d\u4f1a\u9519\u4e86\u3002\u201d\u6307\u7740\u624b\u5e15\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u77a7\uff0c\u8fd9\u624b\u5e15\u662f\u4e1d\u7684\uff0c\u90a3\u4e9b\u5c71\u5ddd\u6c99\u6f20\u7684\u56fe\u5f62\uff0c\u662f\u7528\u68c9\u7ebf\u7ec7\u5728\u4e2d\u95f4\u3002\u4e1d\u662f\u9ec4\u4e1d\uff0c\u68c9\u7ebf\u4e5f\u662f\u9ec4\u7ebf\uff0c\u5e73\u65f6\u77a7\u4e0d\u51fa\u6765\uff0c\u4f46\u4e00\u67d3\u4e0a\u8840\uff0c\u68c9\u7ebf\u5438\u8840\u6bd4\u4e1d\u591a\uff0c\u90a3\u4fbf\u5206\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u674e\u6587\u79c0\u51dd\u76ee\u5411\u624b\u5e15\u770b\u53bb\uff0c\u679c\u5982\u4ed6\u6240\u8bf4\uff0c\u9ec4\u8272\u7684\u4e1d\u5e15\u4e0a\u67d3\u4e86\u9c9c\u8840\uff0c\u4fbf\u663e\u51fa\u56fe\u5f62\uff0c\u4e0d\u67d3\u8840\u4e4b\u5904\uff0c\u5374\u662f\u4e00\u7247\u9ec4\u8272\u3002\u5f53\u65e5\u82cf\u666e\u53d7\u4e86\u72fc\u54ac\uff0c\u6d41\u8840\u4e0d\u591a\uff0c\u624b\u5e15\u4e0a\u6240\u663e\u56fe\u5f62\u53ea\u662f\u4e00\u89d2\uff0c\u4eca\u665a\u4e2d\u4e86\u5251\u4f24\uff0c\u56fe\u5f62\u4fbf\u663e\u4e86\u4e00\u5927\u534a\u51fa\u6765\u3002\u5979\u81f3\u6b64\u65b9\u624d\u7701\u609f\uff0c\u5c4b\u6765\u8fd9\u624b\u5e15\u4e4b\u4e2d\uff0c\u8fd8\u85cf\u7740\u8fd9\u6837\u7684\u4e00\u4e2a\u5927\u79d8\u5bc6\u3002 \u3000\u3000\u82cf\u9c81\u514b\u548c\u8f66\u5c14\u5e93\u6240\u53d7\u7684\u4f24\u90fd\u5e76\u4e0d\u91cd\uff0c\u4e24\u4eba\u5fc3\u91cc\u5747\u60f3\uff1a\u201c\u7b49\u6211\u9152\u9192\u4e86\u4e9b\uff0c\u5b9a\u8981\u5c06\u8fd9\u6c49\u4eba\u5f3a\u76d7\u6740\u4e86\u3002\u201d\u8f66\u5c14\u5e93\u9053\uff1a\u201c\u8001\u4eba\uff0c\u7ed9\u6211\u4e9b\u6c34\u559d\u3002\u201d\u8ba1\u8001\u4eba\u9053\uff1a\u201c\u597d\uff01\u201d\u7ad9\u8d77\u6765\u8981\u53bb\u62ff\u6c34\u3002\u9648\u8fbe\u6d77\u5389\u58f0\u559d\u9053\uff1a\u201c\u7ed9\u6211\u5750\u7740\uff0c\u8c01\u90fd\u4e0d\u8bb8\u52a8\u3002\u201d\u8ba1\u8001\u4eba\u54fc\u4e86\u4e00\u58f0\uff0c\u5750\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u9648\u8fbe\u6d77\u5fc3\u4e0b\u76d8\u7b97\uff1a\u201c\u8fd9\u51e0\u4eba\u5982\u679c\u5408\u529b\u5bf9\u4ed8\u6211\uff0c\u4e00\u62e5\u800c\u4e0a\uff0c\u90a3\u53ef\u4e0d\u5999\u3002\u4e58\u7740\u8fd9\u4e24\u6761\u54c8\u8428\u514b\u8001\u72d7\u8fd8\u6ca1\u9192\uff0c\u5148\u884c\u6740\u4e86\uff0c\u4ee5\u7b56\u4e07\u5168\u3002\u201d\u6162\u6162\u8d70\u5230\u82cf\u9c81\u514b\u8eab\u524d\uff0c\u7a81\u7136\u4e4b\u95f4\u62d4\u51fa\u957f\u5251\uff0c\u4e00\u5251\u4fbf\u5411\u4ed6\u5934\u4e0a\u780d\u4e86\u4e0b\u53bb\u3002\u8fd9\u4e00\u4e0b\u62d4\u5251\u6325\u51fb\uff0c\u65e2\u662f\u7a81\u5982\u5176\u6765\uff0c\u884c\u52a8\u53c8\u662f\u5feb\u6781\uff0c\u82cf\u9c81\u514b\u5168\u65e0\u95ea\u907f\u7684\u4f59\u5730\u3002\u82cf\u666e\u5927\u53eb\u4e00\u58f0\uff0c\u5f85\u8981\u6251\u4e0a\u76f8\u6551\uff0c\u54ea\u91cc\u6765\u5f97\u53ca\uff1f \u3000\u3000\u9648\u8fbe\u6d77\u4e00\u5251\u6b63\u8981\u780d\u5230\u82cf\u9c81\u514b\u5934\u4e0a\uff0c\u84e6\u542c\u5f97\u547c\u7684\u4e00\u58f0\u54cd\uff0c\u4e00\u7269\u63b7\u5411\u81ea\u5df1\u9762\u524d\uff0c\u6765\u52bf\u5947\u6025\uff0c\u614c\u4e71\u4e2d\u987e\u4e0d\u5f97\u4f24\u4eba\uff0c\u75be\u5411\u5de6\u8dc3\uff0c\u4e52\u4e53\u4e00\u58f0\u54cd\u4eae\uff0c\u90a3\u7269\u649e\u5728\u5899\u4e0a\uff0c\u767b\u65f6\u7c89\u788e\uff0c\u5374\u539f\u6765\u662f\u4e00\u53ea\u8336\u7897\uff0c\u4e00\u5b9a\u795e\uff0c\u624d\u770b\u6e05\u695a\u7528\u8336\u7897\u63b7\u4ed6\u7684\u5374\u662f\u674e\u6587\u79c0\u3002 \u3000\u3000\u9648\u8fbe\u6d77\u5927\u6012\uff0c\u4e00\u76f4\u89c1\u8fd9\u54c8\u8428\u514b\u5c11\u5e74\u7626\u5f31\u767d\u6670\uff0c\u6709\u5982\u5973\u5b50\uff0c\u6ca1\u53bb\u7406\u4f1a\uff0c\u54ea\u77e5\u7adf\u6562\u6765\u8001\u864e\u5934\u4e0a\u62cd\u82cd\u8747\uff0c\u633a\u5251\u6307\u7740\u5979\u9a82\u9053\uff1a\u201c\u54c8\u8428\u514b\u5c0f\u72d7\uff0c\u4f60\u6d3b\u5f97\u4e0d\u8010\u70e6\u4e86\uff1f\u201d \u3000\u3000\u674e\u6587\u79c0\u6162\u6162\u89e3\u5f00\u54c8\u8428\u514b\u5916\u8863\uff0c\u9664\u4e86\u4e0b\u6765\uff0c\u9732\u51fa\u91cc\u9762\u7684\u6c49\u88c5\u77ed\u8884\uff0c\u4ee5\u54c8\u8428\u514b\u8bed\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u662f\u54c8\u8428\u514b\u4eba\u3002\u6211\u662f\u6c49\u4eba\u3002\u201d\u5de6\u624b\u6307\u7740\u82cf\u9c81\u514b\u9053\uff1a\u201c\u8fd9\u4f4d\u54c8\u8428\u514b\u4f2f\u4f2f\uff0c\u4ee5\u4e3a\u6c49\u4eba\u90fd\u662f\u5f3a\u76d7\u574f\u4eba\u3002 \u3000\u3000\u6211\u8981\u4ed6\u77e5\u9053\uff0c\u6211\u4eec\u6c49\u4eba\u5e76\u975e\u4e2a\u4e2a\u90fd\u662f\u5f3a\u76d7\uff0c\u4e5f\u6709\u597d\u4eba\u3002\u201d \u3000\u3000\u9002\u624d\u9648\u8fbe\u6d77\u90a3\u4e00\u5251\uff0c\u4eba\u4eba\u90fd\u770b\u5f97\u6e05\u695a\uff0c\u82e5\u4e0d\u662f\u674e\u6587\u79c0\u63b7\u7897\u76f8\u6551\uff0c\u82cf\u9c81\u514b\u6b64\u523b\u65e9\u5df2\u6bd9\u547d\uff0c\u542c\u5f97\u5979\u8fd9\u4e48\u8bf4\uff0c\u82cf\u666e\u9996\u5148\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u4f60\u6551\u6211\u7239\u7239\uff01\u201d\u82cf\u9c81\u514b\u5374\u662f\u5341\u5206\u5014\u5f3a\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4f60\u662f\u6c49\u4eba\uff0c\u6211\u4e0d\u8981\u4f60\u6551\uff0c\u8ba9\u8fd9\u5f3a\u76d7\u6740\u4e86\u6211\u597d\u5566\u3002\u201d \u3000\u3000\u9648\u8fbe\u6d77\u8e0f\u4e0a\u4e00\u6b65\uff0c\u95ee\u674e\u6587\u79c0\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u4f60\u662f\u6c49\u4eba\uff0c\u5230\u8fd9\u91cc\u6765\u5e72\u4ec0\u4e48\uff1f\u201d\u674e\u6587\u79c0\u5fae\u5fae\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u8ba4\u5f97\u6211\uff0c\u6211\u5374\u8ba4\u5f97\u4f60\u3002\u62a2\u52ab\u54c8\u8428\u514b\u90e8\u843d\uff0c\u5bb3\u6b7b\u4e0d\u5c11\u54c8\u8428\u514b\u4eba\u7684\uff0c\u5c31\u662f\u4f60\u8fd9\u6279\u6c49\u4eba\u5f3a\u76d7\u3002\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u58f0\u97f3\u53d8\u5f97\u751a\u662f\u82e6\u6da9\uff0c\u5fc3\u4e2d\u5728\u60f3\uff1a\u201c\u5982\u679c\u4e0d\u662f\u4f60\u4eec\u8fd9\u4e9b\u5f3a\u76d7\u4f5c\u4e86\u8fd9\u8bb8\u591a\u574f\u4e8b\uff0c\u82cf\u9c81\u514b\u4e5f\u4e0d\u4f1a\u8fd9\u6837\u618e\u6068\u6211\u4eec\u6c49\u4eba\u3002\u201d\u9648\u8fbe\u6d77\u5927\u58f0\u9053\uff1a\u201c\u662f\u8001\u5b50\u4fbf\u53c8\u600e\u6837\uff1f\u201d \u3000\u3000\u674e\u6587\u79c0\u6307\u7740\u963f\u66fc\u9053\uff1a\u201c\u5979\u662f\u4f60\u7684\u5973\u5974\uff0c\u6211\u8981\u593a\u5979\u8fc7\u6765\uff0c\u505a\u6211\u7684\u5973\u5974\uff01\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\u3002\u4eba\u4eba\u90fd\u662f\u5927\u51fa\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u9648\u8fbe\u6d77\u4e00\u6014\u4e4b\u4e0b\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u597d\uff0c\u4f60\u6709\u672c\u4e8b\u4fbf\u6765\u593a\u5427\u3002\u201d\u957f\u5251\u4e00\u626c\uff0c\u5251\u5203\u6296\u52a8\uff0c\u55e1\u55e1\u4f5c\u54cd\u3002 \u3000\u3000\u674e\u6587\u79c0\u8f6c\u5934\u5bf9\u963f\u66fc\u9053\uff1a\u201c\u4f60\u51ed\u7740\u771f\u4e3b\u963f\u62c9\u4e4b\u540d\uff0c\u7acb\u8fc7\u4e86\u8a93\uff0c\u4e00\u8f88\u5b50\u8ddf\u7740\u4ed6\u505a\u5973\u5974\u3002\u5982\u679c\u4ed6\u6253\u6211\u4e0d\u8fc7\uff0c\u4f60\u7ed9\u6211\u593a\u8fc7\u6765\uff0c\u90a3\u4e48\u4f60\u4e00\u8f88\u5b50\u5c31\u662f\u6211\u7684\u5973\u5974\u4e86\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u54c8\u8428\u514b\u4eba\u4e0e\u522b\u65cf\u4eba\u6253\u4ed7\uff0c\u4fd8\u864f\u4e86\u654c\u4eba\u4fbf\u5f53\u4f5c\u5974\u96b6\uff0c\u56de\u6559\u7684\u53ef\u5170\u7ecf\u4e2d\u539f\u6709\u660e\u6587\u89c4\u5b9a\u3002 \u3000\u3000\u5974\u96b6\u7684\u8eab\u5206\u548c\u7272\u53e3\u65e0\u522b\uff0c\u5168\u65e0\u81ea\u4e3b\u4e4b\u6743\uff0c\u542c\u51ed\u4e3b\u4eba\u652f\u914d\u4e70\u5356\uff0c\u4e3b\u4eba\u82e5\u662f\u7ed9\u4eba\u5236\u670d\uff0c\u4ed6\u7684\u5bb6\u4ea7\u3001\u7272\u53e3\u3001\u5974\u96b6\u90fd\u4e0d\u514d\u5c5e\u4e8e\u65c1\u4eba\u3002 \u3000\u3000\u963f\u66fc\u542c\u5979\u8fd9\u4e48\u8bf4\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u53cd\u6b63\u5df2\u6210\u4e86\u5973\u5974\uff0c\u4e0e\u5176\u8ddf\u4e86\u8fd9\u6076\u5f3a\u76d7\u53bb\u53d7\u4ed6\u6298\u78e8\uff0c\u4e0d\u5982\u5949\u4f60\u4e3a\u4e3b\u4eba\u3002\u201d\u4e8e\u662f\u70b9\u5934\u9053\uff1a\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u8ddf\u7740\u53c8\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u6253\u4e0d\u8fc7\u4ed6\u7684\u3002\u8fd9\u5f3a\u76d7\u7684\u6b66\u529f\u5f88\u597d\u3002\u201d\u674e\u6587\u79c0\u9053\uff1a\u201c\u90a3\u4f60\u4e0d\u7528\u62c5\u5fc3\uff0c\u6211\u6253\u4ed6\u4e0d\u8fc7\uff0c\u81ea\u7136\u4f1a\u7ed9\u4ed6\u6740\u4e86\u3002\u201d\u53cc\u624b\u4e00\u62cd\uff0c\u5bf9\u9648\u8fbe\u6d77\u9053\uff1a\u201c\u4e0a\u5427\uff01\u201d \u3000\u3000\u9648\u8fbe\u6d77\u5947\u9053\uff1a\u201c\u4f60\u7a7a\u624b\u8ddf\u6211\u6597\uff1f\u201d\u674e\u6587\u79c0\u9053\uff1a\u201c\u6740\u4f60\u8fd9\u6076\u5f3a\u76d7\uff0c\u7528\u5f97\u7740\u4ec0\u4e48\u5175\u5668\uff1f\u201d\u9648\u8fbe\u6d77\u5fc3\u60f3\uff1a\u201c\u8fd9\u91cc\u4e2a\u4e2a\u90fd\u662f\u654c\u4eba\uff0c\u591a\u6328\u65f6\u523b\uff0c\u4fbf\u591a\u5371\u9669\uff0c\u4ed6\u81ea\u5df1\u6258\u5927\uff0c\u518d\u597d\u4e0d\u8fc7\u3002\u201d\u559d\u9053\uff1a\u201c\u770b\u5251\uff01\u201d \u3000\u3000\u5229\u5251\u633a\u51fa\uff0c\u4e00\u62db\u201c\u6bd2\u86c7\u51fa\u6d1e\u201d\uff0c\u5411\u674e\u6587\u79c0\u5f53\u80f8\u523a\u53bb\uff0c\u52bf\u9053\u751a\u662f\u52b2\u6025\u3002 \u3000\u3000\u8ba1\u8001\u4eba\u53eb\u9053\uff1a\u201c\u5feb\u9000\u4e0b\uff01\u201d\u4ed6\u6599\u60f3\u674e\u6587\u79c0\u4e07\u96be\u62b5\u6321\uff0c\u54ea\u77e5\u674e\u6587\u79c0\u8eab\u5f62\u4e00\u6643\uff0c\u8f7b\u8f7b\u5de7\u5de7\u7684\u907f\u8fc7\u4e86\uff0c\u62a2\u5230\u9648\u8fbe\u6d77\u5de6\u9996\uff0c\u5de6\u8098\u540e\u633a\uff0c\u649e\u5411\u4ed6\u7684\u8170\u95f4\u3002\u9648\u8fbe\u6d77\u53eb\u9053\uff1a\u201c\u597d\uff01\u201d\u957f\u5251\u5708\u8f6c\uff0c\u524a\u5411\u5979\u624b\u81c2\u3002\u674e\u6587\u79c0\u98de\u8d77\u53f3\u8db3\uff0c\u8e22\u4ed6\u624b\u8155\uff0c\u8fd9\u4e00\u62db\u201c\u53f6\u5e95\u98de\u71d5\u201d\u662f\u534e\u8f89\u7684\u7edd\u62db\u4e4b\u4e00\uff0c\u674e\u6587\u79c0\u82e6\u7ec3\u4e86\u4e03\u516b\u5929\u65b9\u624d\u7ec3\u6210\uff0c\u8f7b\u5de7\u8fc5\u6377\uff0c\u751a\u662f\u4e86\u5f97\u3002\u9648\u8fbe\u6d77\u6025\u5fd9\u7f29\u624b\uff0c\u5df2\u7136\u4e0d\u53ca\uff0c\u624b\u8155\u4e00\u75db\uff0c\u5df2\u88ab\u8e22\u4e2d\uff0c\u603b\u7b97\u5bf9\u65b9\u811a\u529b\u4e0d\u751a\u5f3a\u52b2\uff0c\u9648\u8fbe\u6d77\u957f\u5251\u8fd9\u624d\u6ca1\u6709\u8131\u624b\u3002\u4ed6\u5927\u58f0\u6012\u543c\uff0c\u8dc3\u540e\u4e00\u6b65\u3002\u8ba1\u8001\u4eba\u201c\u54a6\u201d\u7684\u4e00\u58f0\uff0c\u60ca\u5947\u4e4b\u6781\u3002 \u3000\u3000\u9648\u8fbe\u6d77\u629a\u4e86\u629a\u624b\u8155\uff0c\u633a\u5251\u53c8\u4e0a\uff0c\u548c\u674e\u6587\u79c0\u6597\u5728\u4e00\u8d77\u3002\u8fd9\u65f6\u4ed6\u5fc3\u4e2d\u5df2\u7136\u6beb\u4e0d\u6562\u5c0f\u89d1\u4e86\u8fd9\u4e2a\u7626\u5f31\u5c11\u5e74\uff0c\u773c\u89c1\u4ed6\u51fa\u624b\u6295\u8db3\uff0c\u529f\u592b\u7740\u5b9e\u4e86\u5f97\uff0c\u5f53\u4e0b\u65bd\u5c55\u201c\u9752\u87d2\u5251\u6cd5\u201d\uff0c\u62db\u62db\u72e0\u6bd2\uff0c\u8981\u594b\u529b\u5c06\u8fd9\u5c11\u5e74\u523a\u6b7b\u3002\u674e\u6587\u79c0\u5f97\u5e08\u7236\u534e\u8f89\u4f20\u6388\uff0c\u8eab\u624b\u7075\u654f\uff0c\u62db\u5f0f\u7cbe\u5947\uff0c\u53ea\u662f\u4ece\u672a\u4e0e\u4eba\u62c6\u62db\u76f8\u6597\uff0c\u4e34\u9635\u5168\u65e0\u7ecf\u9a8c\uff0c\u521d\u65f6\u5168\u51ed\u7740\u4e00\u80a1\u4ec7\u6068\u4e4b\u610f\uff0c\u8981\u6740\u6b64\u6076\u76d7\u4e3a\u7236\u6bcd\u62a5\u4ec7\uff0c\u6597\u5230\u540e\u6765\uff0c\u5bf9\u654c\u4eba\u7684\u5251\u6cd5\u5df2\u6e10\u6e10\u6478\u5230\u4e86\u95e8\u8def\uff0c\u5fc3\u795e\u6162\u6162\u5b81\u5b9a\u3002 \u3000\u3000\u8ba1\u8001\u4eba\u8fd9\u8305\u5c4b\u672c\u751a\u72ed\u7a84\uff0c\u5385\u4e2d\u53c8\u751f\u4e86\u706b\u5806\uff0c\u9648\u674e\u4e8c\u4eba\u5728\u706b\u5806\u65c1\u7eb5\u8dc3\u76f8\u640f\uff0c\u5251\u950b\u62f3\u638c\u76f8\u53bb\u5f80\u5f80\u95f4\u4e0d\u903e\u5bf8\uff0c\u4f3c\u4e4e\u9648\u8fbe\u6d77\u6bcf\u4e00\u5251\u90fd\u80fd\u5236\u674e\u6587\u79c0\u7684\u6b7b\u547d\uff0c\u53ef\u662f\u5979\u603b\u662f\u6216\u53cd\u6253\u3001\u6216\u95ea\u907f\uff0c\u4e00\u4e00\u62c6\u89e3\u5f00\u53bb\u3002\u82cf\u9c81\u514b\u7b49\u53ea\u770b\u5f97\u5f20\u5927\u4e86\u5634\u3002\u8ba1\u8001\u4eba\u5374\u8d8a\u770b\u8d8a\u662f\u5bb3\u6015\uff0c\u5168\u8eab\u4e0d\u4f4f\u7684\u7c0c\u7c0c\u53d1\u6296\u3002 \u3000\u3000\u4e24\u4eba\u6597\u5230\u9163\u5904\uff0c\u9648\u8fbe\u6d77\u4e00\u5251\u201c\u7075\u86c7\u5410\u4fe1\u201d\uff0c\u5251\u5c16\u70b9\u5411\u674e\u6587\u79c0\u7684\u54bd\u5589\u3002\u674e\u6587\u79c0\u4e00\u4f4e\u5934\uff0c\u4ece\u5251\u5e95\u4e0b\u6251\u4e86\u4e0a\u53bb\uff0c\u5de6\u81c2\u4e00\u683c\u654c\u4eba\u7684\u53f3\u81c2\uff0c\u5c06\u4ed6\u957f\u5251\u63a0\u5411\u5916\u95e8\uff0c\u53cc\u624b\u5df2\u6293\u4f4f\u9648\u8fbe\u6d77\u8170\u95f4\u7684\u4e24\u67c4\u91d1\u94f6\u5c0f\u5251\uff0c\u4e00\u62d4\u4e00\u9001\uff0c\u5657\u7684\u4e00\u58f0\u54cd\uff0c\u540c\u65f6\u63d2\u5165\u4e86\u4ed6\u5de6\u53f3\u80a9\u7a9d\u3002 \u3000\u3000\u9648\u8fbe\u6d77\u201c\u554a\u201d\u7684\u4e00\u58f0\u60e8\u547c\uff0c\u957f\u5251\u8131\u624b\uff0c\u8e09\u8e09\u8dc4\u8dc4\u7684\u63a5\u8fde\u5012\u9000\uff0c\u80cc\u9760\u5899\u58c1\uff0c\u53ea\u662f\u5598\u6c14\u3002\u8fd9\u4e24\u67c4\u5c0f\u5251\u63d2\u5165\u80a9\u7a9d\uff0c\u76f4\u6ca1\u81f3\u67c4\uff0c\u5251\u5c16\u4ece\u80cc\u5fc3\u7a7f\u4e86\u51fa\u6765\uff0c\u4ed6\u7b4b\u8109\u5df2\u65ad\uff0c\u53cc\u81c2\u66f4\u65e0\u534a\u5206\u529b\u6c14\uff0c\u60f3\u4f38\u53f3\u624b\u53bb\u62d4\u5de6\u80a9\u7684\u5c0f\u5251\uff0c\u53f3\u81c2\u5374\u54ea\u91cc\u62ac\u5f97\u8d77\u6765\uff1f \u3000\u3000\u53ea\u542c\u5f97\u5c4b\u4e2d\u4f17\u4eba\u6b22\u547c\u4e4b\u58f0\u5927\u4f5c\uff0c\u5927\u53eb\uff1a\u201c\u6253\u8d25\u4e86\u6076\u5f3a\u76d7\uff0c\u6253\u8d25\u4e86\u6076\u5f3a\u76d7\uff01\u201d\u8fde\u82cf\u9c81\u514b\u4e5f\u662f\u7eb5\u58f0\u5927\u53eb\u3002\u82cf\u666e\u548c\u963f\u66fc\u62e5\u62b1\u5728\u4e00\u8d77\uff0c\u559c\u4e0d\u81ea\u80dc\u3002\u53ea\u6709\u8ba1\u8001\u4eba\u5374\u4ecd\u662f\u4e0d\u4f4f\u53d1\u6296\uff0c\u7259\u5173\u76f8\u51fb\uff0c\u683c\u683c\u6709\u58f0\u3002 \u3000\u3000\u674e\u6587\u79c0\u77e5\u4ed6\u4e3a\u81ea\u5df1\u62c5\u5fc3\u800c\u5bb3\u6015\uff0c\u8d70\u8fc7\u53bb\u63e1\u4f4f\u4ed6\u7c97\u5927\u7684\u624b\u638c\uff0c\u5c06\u5634\u5df4\u51d1\u5230\u4ed6\u8033\u7554\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8ba1\u7237\u7237\uff0c\u522b\u5bb3\u6015\uff0c\u8fd9\u6076\u5f3a\u76d7\u6253\u6211\u4e0d\u8fc7\u7684\u3002\u201d\u53ea\u89c9\u4ed6\u624b\u638c\u51b0\u51b7\uff0c\u4ecd\u662f\u6296\u5f97\u5341\u5206\u5389\u5bb3\u3002 \u3000\u3000\u674e\u6587\u79c0\u8f6c\u8fc7\u5934\u6765\uff0c\u89c1\u82cf\u666e\u7d27\u7d27\u6402\u7740\u963f\u66fc\uff0c\u5fc3\u4e2d\u672c\u6765\u5145\u6ea2\u7740\u7684\u80dc\u5229\u559c\u60a6\u970e\u65f6\u95f4\u5316\u4e3a\u4e4c\u6709\uff0c\u53ea\u89c9\u81ea\u5df1\u4e5f\u5728\u53d1\u6296\uff0c\u8ba1\u8001\u4eba\u7684\u624b\u638c\u4e5f\u4e0d\u51b7\u4e86\uff0c\u539f\u6765\u81ea\u5df1\u7684\u624b\u638c\u4e5f\u53d8\u6210\u4e86\u51b0\u51c9\u3002 \u3000\u3000\u5979\u653e\u5f00\u4e86\u8ba1\u8001\u4eba\u7684\u624b\uff0c\u8d70\u8fc7\u53bb\u7275\u4f4f\u4ecd\u662f\u5957\u5728\u963f\u66fc\u9888\u4e2d\u7684\u957f\u7d22\uff0c\u51b7\u51b7\u7684\u9053\uff1a\u201c\u4f60\u662f\u6211\u7684\u5973\u5974\uff0c\u5f97\u4e00\u8f88\u5b50\u8ddf\u7740\u6211\u3002\u201d \u3000\u3000\u82cf\u666e\u548c\u963f\u66fc\u5fc3\u4e2d\u540c\u65f6\u4e00\u5bd2\uff0c\u76f8\u6402\u76f8\u62b1\u7684\u56db\u53ea\u624b\u81c2\u90fd\u677e\u4e86\u5f00\u6765\u3002\u4ed6\u4eec\u77e5\u9053\u8fd9\u662f\u54c8\u8428\u514b\u4e16\u4e16\u4ee3\u4ee3\u76f8\u4f20\u7684\u89c4\u77e9\uff0c\u662f\u65e0\u53ef\u8fdd\u6297\u7684\u547d\u8fd0\u3002\u4e24\u4eba\u7684\u8138\u8272\u90fd\u53d8\u6210\u4e86\u60e8\u767d\uff01 \u3000\u3000\u674e\u6587\u79c0\u53f9\u4e86\u53e3\u6c14\uff0c\u5c06\u7d22\u5708\u4ece\u963f\u66fc\u9888\u4e2d\u53d6\u4e86\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u82cf\u666e\u559c\u6b22\u4f60\uff0c\u6211\u2026\u2026\u6211\u4e0d\u4f1a\u8ba9\u4ed6\u4f24\u5fc3\u7684\u3002\u4f60\u662f\u82cf\u666e\u7684\u4eba\uff01\u201d\u8bf4\u7740\u8f7b\u8f7b\u5c06\u963f\u66fc\u4e00\u63a8\uff0c\u8ba9\u5979\u504e\u501a\u5728\u82cf\u666e\u7684\u6000\u91cc\u3002 \u3000\u3000\u82cf\u666e\u548c\u963f\u66fc\u51e0\u4e4e\u4e0d\u76f8\u4fe1\u81ea\u5df1\u7684\u8033\u6735\uff0c\u9f50\u58f0\u95ee\u9053\uff1a\u201c\u771f\u7684\u4e48\uff1f\u201d\u674e\u6587\u79c0\u82e6\u7b11\u9053\uff1a\u201c\u81ea\u7136\u662f\u771f\u7684\u3002\u201d\u82cf\u666e\u548c\u963f\u66fc\u5206\u522b\u6293\u4f4f\u4e86\u5979\u4e00\u53ea\u624b\uff0c\u4e0d\u4f4f\u6447\u6643\uff0c\u9053\uff1a\u201c\u591a\u8c22\u4f60\uff0c\u591a\u8c22\u4f60\uff01\u201d \u3000\u3000\u4ed6\u4eec\u72c2\u559c\u4e4b\u4e0b\uff0c\u5168\u6ca1\u53d1\u89c9\u81ea\u5df1\u7684\u624b\u81c2\u4e0a\u591a\u4e86\u51e0\u6ef4\u773c\u6cea\uff0c\u662f\u4ece\u674e\u6587\u79c0\u773c\u4e2d\u843d\u4e0b\u6765\u7684\u6cea\u6c34\u3002 \u3000\u3000\u82cf\u9c81\u514b\u6323\u624e\u7740\u7ad9\u8d77\uff0c\u5927\u624b\u5728\u674e\u6587\u79c0\u80a9\u5934\u91cd\u91cd\u4e00\u62cd\uff0c\u8bf4\u9053\uff1a\u201c\u6c49\u4eba\u4e4b\u4e2d\uff0c\u679c\u7136\u4e5f\u6709\u597d\u4eba\u3002\u4e0d\u8fc7\u2026\u2026\u4e0d\u8fc7\uff0c\u6050\u6015\u53ea\u6709\u4f60\u4e00\u4e2a\uff01\u201d \u3000\u3000\u8f66\u5c14\u5e93\u53eb\u9053\uff1a\u201c\u62ff\u9152\u6765\uff0c\u62ff\u9152\u6765\u3002\u6211\u8bf7\u5927\u5bb6\u559d\u9152\uff0c\u8bf7\u54c8\u8428\u514b\u7684\u597d\u4eba\u559d\u9152\uff0c\u8bf7\u6c49\u4eba\u7684\u597d\u4eba\u559d\u9152\uff0c\u5e86\u795d\u6293\u4f4f\u4e86\u6076\u5f3a\u76d7\uff0c\u54a6\uff01\u90a3\u5f3a\u76d7\u5462\uff1f\u201d \u3000\u3000\u4f17\u4eba\u56de\u8fc7\u5934\u6765\uff0c\u5374\u89c1\u9648\u8fbe\u6d77\u5df2\u7136\u4e0d\u77e5\u53bb\u5411\u3002\u539f\u6765\u5404\u4eba\u521a\u624d\u90fd\u6ce8\u89c6\u7740\u674e\u6587\u79c0\u548c\u963f\u66fc\uff0c\u5374\u7ed9\u8fd9\u5f3a\u76d7\u4e58\u673a\u4ece\u540e\u95e8\u4e2d\u9003\u8d70\u4e86\u3002 \u3000\u3000\u82cf\u9c81\u514b\u5927\u6012\uff0c\u53eb\u9053\uff1a\u201c\u54b1\u4eec\u5feb\u8ffd\uff01\u201d\u6253\u5f00\u677f\u95e8\uff0c\u4e00\u9635\u5927\u98ce\u522e\u8fdb\u6765\uff0c\u4ed6\u811a\u4e0b\u5140\u81ea\u65e0\u529b\uff0c\u8eab\u5b50\u4e00\u6643\uff0c\u6454\u5012\u5728\u5730\u3002 \u3000\u3000\u5bd2\u98ce\u5939\u96ea\uff0c\u731b\u6076\u96be\u5f53\uff0c\u4eba\u4eba\u90fd\u89c9\u5f97\u6c14\u4e5f\u900f\u4e0d\u8fc7\u6765\u3002\u963f\u66fc\u9053\uff1a\u201c\u8fd9\u822c\u5927\u98ce\u96ea\u4e2d\uff0c\u8c05\u4ed6\u4e5f\u8d70\u4e0d\u8fdc\uff0c\u52c9\u5f3a\u6323\u624e\uff0c\u975e\u6b7b\u5728\u96ea\u5730\u4e2d\u4e0d\u53ef\u3002\u5f85\u5929\u660e\u540e\u98ce\u5c0f\u4e86\uff0c\u54b1\u4eec\u5230\u96ea\u5730\u4e2d\u627e\u8fd9\u6076\u8d3c\u7684\u5c38\u9996\u4fbf\u4e86\u3002\u201d\u82cf\u666e\u70b9\u70b9\u5934\uff0c\u5173\u4e0a\u4e86\u95e8\u3002 \u3000\u3000\u82cf\u9c81\u514b\u77aa\u89c6\u7740\u674e\u6587\u79c0\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u662f\u54c8\u8428\u514b\u4eba\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u674e\u6587\u79c0\u6447\u5934\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u662f\u6c49\u4eba\uff01\u201d\u82cf\u9c81\u514b\u9053\uff1a\u201c\u4e0d\u53ef\u80fd\u7684\uff0c\u4f60\u662f\u6c49\u4eba\uff0c\u4e3a\u4ec0\u4e48\u53cd\u800c\u6253\u5012\u90a3\u4e2a\u6c49\u4eba\u5f3a\u76d7\uff0c\u6551\u6211\u4eec\u54c8\u8428\u514b\u4eba\uff1f\u201d\u674e\u6587\u79c0\u9053\uff1a\u201c\u6c49\u4eba\u4e2d\u6709\u574f\u4eba\uff0c\u4e5f\u6709\u597d\u4eba\u3002\u6211\u2026\u2026\u6211\u4e0d\u662f\u574f\u4eba\u3002\u201d \u3000\u3000\u82cf\u9c81\u514b\u5583\u5583\u7684\u9053\uff1a\u201c\u6c49\u4eba\u4e2d\u4e5f\u6709\u597d\u4eba\uff1f\u201d\u7f13\u7f13\u6447\u4e86\u6447\u5934\u3002\u53ef\u662f\u4ed6\u7684\u6027\u547d\uff0c\u4ed6\u513f\u5b50\u7684\u6027\u547d\uff0c\u660e\u660e\u662f\u8fd9\u4e2a\u5c11\u5e74\u6c49\u4eba\u6551\u7684\uff0c\u5374\u4e0d\u7531\u5f97\u4ed6\u4e0d\u4fe1\u3002 \u3000\u3000\u4ed6\u4e00\u751f\u618e\u6068\u6c49\u4eba\uff0c\u73b0\u5728\u8fd9\u4fe1\u5ff5\u5728\u52a8\u6447\u4e86\u3002\u4ed6\u607c\u6012\u81ea\u5df1\uff0c\u4e3a\u4ec0\u4e48\u504f\u504f\u6628\u665a\u559d\u9189\u4e86\u9152\uff0c\u4e0d\u80fd\u8ddf\u6c49\u4eba\u5f3a\u76d7\u62da\u6597\u4e00\u573a\uff0c\u5374\u8981\u53e6\u4e00\u4e2a\u6c49\u4eba\u6765\u6551\u4e86\u81ea\u5df1\u7684\u6027\u547d\uff1f \u3000\u3000\u4ed6\u4e00\u751f\u4e4b\u4e2d\uff0c\u4ec0\u4e48\u4e8b\u60c5\u5230\u4e86\u7d27\u8981\u5173\u5934\uff0c\u603b\u662f\u90a3\u4e48\u4e0d\u5de7\uff0c\u603b\u662f\u8fd0\u6c14\u4e0d\u597d\u3002\u7136\u800c\uff0c\u521a\u624d\u90a3\u5f3a\u76d7\u7684\u957f\u5251\u5df2\u780d\u5230\u4e86\u81ea\u5df1\u5934\u9876\uff0c\u5e78\u597d\u90a3\u5c11\u5e74\u53ca\u65f6\u76f8\u6551\uff0c\u96be\u9053\u8fd9\u4e5f\u662f\u4e0d\u5de7\u5417\uff1f\u4e5f\u662f\u8fd0\u6c14\u4e0d\u597d\u4e48\uff1f \u3000"}),showOnHomepage:!0},{name:"\u4e03",content:(0,a.jsx)(a.Fragment,{children:"\u5230\u5f97\u9ece\u660e\u65f6\uff0c\u5927\u98ce\u96ea\u7ec8\u4e8e\u6b62\u6b47\u4e86\u3002 \u3000\u3000\u82cf\u9c81\u514b\u548c\u8f66\u5c14\u5e93\u7acb\u5373\u51fa\u53d1\u53bb\u53ec\u96c6\u65cf\u4eba\u8ffd\u8e2a\u90a3\u6c49\u4eba\u5f3a\u76d7\u3002 \u3000\u3000\u96ea\u5730\u91cc\u8db3\u5370\u5341\u5206\u6e05\u695a\uff0c\u4f55\u51b5\u4ed6\u53d7\u4e86\u91cd\u4f24\uff0c\u4e00\u5b9a\u9003\u4e0d\u8fdc\u3002\u6700\u597d\u662f\u4ed6\u53bb\u548c\u5176\u4f59\u7684\u6c49\u4eba\u5f3a\u76d7\u76f8\u4f1a\uff0c\u5341\u4e8c\u5e74\u6765\u7684\u5927\u4ec7\uff0c\u8fd9\u6b21\u5c31\u53ef\u5f97\u62a5\u4e86\u3002 \u3000\u3000\u54c8\u8428\u514b\u4eba\u7684\u7cbe\u58ee\u7537\u5b50\u4e09\u767e\u591a\u4eba\u7acb\u5373\u7ec4\u6210\u4e86\u7b2c\u4e00\u6279\u8ffd\u8e2a\u961f\uff0c\u5176\u4f59\u7b2c\u4e8c\u3001\u7b2c\u4e09\u6279\u7684\u9646\u7eed\u8ffd\u6765\u3002\u5355\u662f\u6349\u62ff\u9648\u8fbe\u6d77\u4e00\u4eba\uff0c\u5f53\u7136\u7528\u4e0d\u7740\u8fd9\u8bb8\u591a\u4eba\uff0c\u7136\u800c\u4e3b\u65e8\u662f\u5728\u4e00\u9f13\u6b7c\u706d\u4e3a\u7978\u5927\u8349\u539f\u7684\u6c49\u4eba\u5f3a\u76d7\u3002 \u3000\u3000\u82cf\u9c81\u514b\u548c\u8f66\u5c14\u5e93\u4f5c\u5148\u950b\u3002\u4ed6\u4eec\u8981\u5176\u4f59\u65cf\u4eba\u8fdc\u8fdc\u7684\u76f8\u9694\u5341\u51e0\u91cc\u8def\uff0c\u5728\u540e\u6162\u6162\u8ddf\u6765\uff0c\u514d\u5f97\u7ed9\u9648\u8fbe\u6d77\u53d1\u89c9\u4e86\uff0c\u5c31\u6b64\u4e0d\u53bb\u548c\u540c\u4f19\u76f8\u4f1a\u3002\u82cf\u666e\u6628\u665a\u53d7\u4e86\u4f24\uff0c\u4f46\u4f24\u52bf\u4e0d\u91cd\uff0c\u8981\u8ddf\u7740\u7236\u4eb2\u3002\u963f\u66fc\u575a\u6301\u4e5f\u8981\u8ddf\u7740\u7236\u4eb2\uff0c\u4f46\u8c01\u90fd\u77e5\u9053\uff0c\u5979\u662f\u4e0d\u613f\u79bb\u5f00\u82cf\u666e\u3002\u8f66\u5c14\u5e93\u6311\u4e86\u4e24\u4e2a\u5f92\u5f1f\u76f8\u968f\uff0c\u4e00\u4e2a\u662f\u654f\u6377\u7684\u6851\u65af\u513f\uff1b\u4e00\u4e2a\u662f\u529b\u5927\u5982\u9a86\u9a7c\u7684\u9752\u5e74\uff0c\u7ef0\u53f7\u5c31\u53eb\u4f5c\u201c\u9a86\u9a7c\u201d\uff0c\u4eba\u4eba\u90fd\u53eb\u4ed6\u9a86\u9a7c\uff0c\u4ed6\u7684\u672c\u540d\u53cd\u800c\u7ed9\u4eba\u5fd8\u8bb0\u4e86\u3002 \u3000\u3000\u674e\u6587\u79c0\u4e5f\u8981\u53c2\u52a0\u5148\u950b\u961f\uff0c\u82cf\u666e\u9996\u5148\u6b22\u8fce\u3002\u7ecf\u8fc7\u4e86\u6628\u665a\u7684\u4e8b\u540e\uff0c\u674e\u6587\u79c0\u5df2\u6210\u4e3a\u4f17\u6240\u5c0a\u656c\u7684\u82f1\u96c4\u3002\u8f66\u5c14\u5e93\u5e76\u4e0d\u53cd\u5bf9\u5979\u53c2\u52a0\u3002\u82cf\u9c81\u514b\u6709\u4e9b\u4e0d\u613f\uff0c\u4f46\u53cd\u5bf9\u7684\u8bdd\u5374\u8bf4\u4e0d\u51fa\u53e3\u3002 \u3000\u3000\u8ba1\u8001\u4eba\u4f3c\u4e4e\u7ed9\u6628\u665a\u7684\u4e8b\u5413\u574f\u4e86\uff0c\u65e9\u6668\u559d\u7f8a\u5976\u65f6\uff0c\u5931\u624b\u6253\u788e\u4e86\u5976\u7897\u3002\u674e\u6587\u79c0\u659f\u8336\u7ed9\u4ed6\uff0c\u4ed6\u53cc\u624b\u53d1\u6296\uff0c\u63a5\u8fc7\u8336\u7897\u65f6\u5c06\u8336\u6e85\u6cfc\u5728\u8863\u895f\u4e0a\u3002\u674e\u6587\u79c0\u95ee\u4ed6\u600e\u6837\uff0c\u4ed6\u773c\u5149\u4e2d\u9732\u51fa\u53c8\u6050\u60e7\u53c8\u6c14\u607c\u7684\u795e\u8272\uff0c\u7a81\u7136\u56de\u8eab\u8fdb\u623f\uff0c\u91cd\u91cd\u5173\u4e0a\u4e86\u623f\u95e8\u3002 \u3000\u3000\u904d\u5730\u79ef\u96ea\u751a\u6df1\uff0c\u96be\u4ee5\u4e58\u9a6c\uff0c\u5148\u950b\u961f\u4e03\u4eba\u90fd\u662f\u6b65\u884c\uff0c\u6cbf\u7740\u96ea\u5730\u91cc\u7684\u8db3\u5370\u4e00\u8def\u8ffd\u8e2a\u3002\u773c\u89c1\u9648\u8fbe\u6d77\u7684\u8db3\u5370\u7b14\u76f4\u5411\u897f\uff0c\u4f3c\u4e4e\u4e00\u76f4\u901a\u5f80\u6208\u58c1\u6c99\u6f20\u3002\u6599\u662f\u4ed6\u53cc\u81c2\u867d\u7136\u53d7\u4f24\uff0c\u811a\u4e0b\u529f\u592b\u4ecd\u7136\u5341\u5206\u4e86\u5f97\u3002\u516d\u4e2a\u54c8\u8428\u514b\u4eba\u60f3\u8d77\u81ea\u6765\u76f8\u4f20\u6208\u58c1\u6c99\u6f20\u4e2d\u591a\u6709\u6076\u9b3c\uff0c\u90fd\u4e0d\u7981\u5fc3\u4e0b\u5600\u5495\u3002 \u3000\u3000\u82cf\u9c81\u514b\u5927\u58f0\u9053\uff1a\u201c\u4eca\u65e5\u4fbf\u662f\u660e\u77e5\u8981\u649e\u5230\u6076\u9b3c\uff0c\u4e5f\u975e\u53bb\u628a\u5f3a\u76d7\u6349\u4f4f\u4e0d\u53ef\u3002\u82cf\u666e\uff0c\u4f60\u66ff\u4e0d\u66ff\u4f60\u5988\u548c\u54e5\u54e5\u62a5\u4ec7\uff01\u201d\u82cf\u666e\u9053\uff1a\u201c\u6211\u81ea\u662f\u8ddf\u7239\u7239\u540c\u53bb\u3002\u963f\u66fc\uff0c\u4f60\u8fd8\u662f\u56de\u53bb\u5427\uff01\u201d\u963f\u66fc\u9053\uff1a\u201c\u4f60\u53bb\u5f97\uff0c\u6211\u4e5f\u53bb\u5f97\u3002\u201d\u5979\u5fc3\u4e2d\u5374\u662f\u5728\u8bf4\uff1a\u201c\u8981\u662f\u4f60\u6b7b\u4e86\uff0c\u96be\u9053\u6211\u4e00\u4e2a\u4eba\u8fd8\u80fd\u6d3b\u4e48\uff1f\u201d\u82cf\u9c81\u514b\u9053\uff1a\u201c\u963f\u66fc\uff0c\u4f60\u8fd8\u662f\u8ddf\u4f60\u7239\u7239\u56de\u5bb6\u7684\u597d\u3002\u8f66\u5c14\u5e93\u80c6\u5c0f\u5f97\u5f88\uff0c\u6700\u6015\u9b3c\uff01\u201d\u8f66\u5c14\u5e93\u72e0\u72e0\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u62a2\u5148\u4fbf\u8d70\u3002 \u3000\u3000\u6208\u58c1\u6c99\u6f20\u4e2d\u6700\u6559\u4eba\u5bb3\u6015\u7684\u4e8b\u662f\u5343\u91cc\u65e0\u6c34\uff0c\u53ea\u8981\u643a\u5e26\u7684\u6e05\u6c34\u4e00\u559d\u5e72\uff0c\u4fbf\u975e\u6e34\u6b7b\u4e0d\u53ef\uff0c\u4f46\u8fd9\u573a\u5927\u96ea\u4e00\u4e0b\uff0c\u4fef\u8eab\u5373\u662f\u51b0\u96ea\uff0c\u5c11\u4e86\u4e3b\u8981\u7684\u987e\u8651\u3002\u867d\u7136\u4e0d\u80fd\u4e58\u5750\u7272\u53e3\uff0c\u5374\u4e5f\u5c11\u4e86\u9ec4\u6c99\u6251\u9762\u4e4b\u82e6\u3002\u8d8a\u5411\u897f\u884c\uff0c\u773c\u89c1\u9648\u8fbe\u6d77\u7559\u4e0b\u7684\u8db3\u8ff9\u8d8a\u662f\u660e\u663e\uff0c\u5230\u540e\u6765\u4ed6\u8db3\u5370\u4e4b\u4e0a\u5df2\u65e0\u767d\u96ea\u63a9\u76d6\uff0c\u90a3\u81ea\u662f\u98ce\u96ea\u505c\u6b62\u4e4b\u540e\u6240\u7559\u4e0b\u6765\u7684\u4e86\u3002 \u3000\u3000\u8f66\u5c14\u5e93\u5583\u5583\u7684\u9053\uff1a\u201c\u8fd9\u6076\u8d3c\u5012\u4e5f\u5389\u5bb3\uff0c\u8fd9\u573a\u5927\u98ce\u96ea\u7adf\u7136\u56f0\u4ed6\u4e0d\u6b7b\u3002\u201d\u82cf\u9c81\u514b\u5ffd\u7136\u53eb\u9053\uff1a\u201c\u54a6\uff0c\u53c8\u6709\u4e00\u4e2a\u4eba\u7684\u811a\u5370\uff01\u201d\u4ed6\u6307\u7740\u8db3\u5370\u9053\uff1a\u201c\u8fd9\u4eba\u6bcf\u4e00\u6b65\u90fd\u8e0f\u5728\u90a3\u5f3a\u76d7\u7684\u811a\u5370\u4e4b\u4e2d\uff0c\u4e0d\u7559\u5fc3\u5c31\u77a7\u4e0d\u51fa\u6765\u3002\u201d\u4f17\u4eba\u4ed4\u7ec6\u4e00\u77a7\uff0c\u679c\u89c1\u6bcf\u4e2a\u8db3\u5370\u4e2d\u90fd\u6709\u6df1\u6d45\u4e24\u5c42\u3002 \u3000\u3000\u5927\u5bb6\u7eb7\u7eb7\u731c\u6d4b\uff0c\u4e0d\u77e5\u662f\u4ec0\u4e48\u7f18\u6545\u3002\u9a86\u9a7c\u5ffd\u7136\u9053\uff1a\u201c\u96be\u9053\u662f\u9b3c\uff1f\u201d\u8fd9\u662f\u4eba\u4eba\u5fc3\u91cc\u65e9\u5c31\u60f3\u8bf4\u7684\u8bdd\uff0c\u7ed9\u4ed6\u7a81\u7136\u8bf4\u4e86\u51fa\u6765\uff0c\u5404\u4eba\u5fcd\u4e0d\u4f4f\u90fd\u6253\u4e86\u4e2a\u5bd2\u5664\u3002 \u3000\u3000\u4e00\u884c\u4eba\u9f13\u52c7\u7eed\u5411\u897f\u884c\u3002\u5927\u96ea\u6df1\u6ca1\u53ca\u80eb\uff0c\u884c\u8d70\u751a\u662f\u7f13\u6162\uff0c\u5f53\u665a\u4fbf\u5728\u96ea\u5730\u4e2d\u9732\u5bbf\u3002\u626b\u5f00\u79ef\u96ea\uff0c\u6316\u6398\u6c99\u5751\uff0c\u4ee5\u6bdb\u6bef\u88f9\u8eab\uff0c\u5367\u5728\u5751\u4e2d\uff0c\u4fbf\u4e0d\u5982\u4f55\u5bd2\u51b7\u3002 \u3000\u3000\u674e\u6587\u79c0\u7684\u6c99\u5751\u662f\u9a86\u9a7c\u7ed9\u6398\u7684\u3002\u4ed6\u8182\u529b\u5f88\u5927\uff0c\u5fc3\u4e2d\u656c\u91cd\u8fd9\u4f4d\u6c49\u4eba\u82f1\u96c4\uff0c\u4fbf\u7ed9\u5979\u6398\u4e86\u6c99\u5751\uff0c\u90a3\u662f\u5728\u9a86\u9a7c\u548c\u82cf\u666e\u7684\u6c99\u5751\u4e4b\u95f4\uff0c\u4e03\u4e2a\u6c99\u5751\u56f4\u6210\u4e00\u4e2a\u5706\u5708\uff0c\u4e2d\u95f4\u751f\u7740\u4e00\u5806\u5927\u706b\u3002 \u3000\u3000\u5934\u9876\u7684\u5929\u5f88\u84dd\uff0c\u660e\u4eae\u7684\u661f\u661f\u7728\u7740\u773c\u775b\u3002\u4e00\u9635\u98ce\u522e\u6765\uff0c\u5377\u8d77\u4e86\u5730\u4e0b\u7684\u767d\u96ea\uff0c\u5728\u98ce\u4e2d\u98de\u821e\u3002\u674e\u6587\u79c0\u671b\u7740\u4e24\u7247\u4e0a\u4e0b\u98de\u821e\u7684\u767d\u96ea\uff0c\u81ea\u8a00\u81ea\u8bed\uff1a\u201c\u771f\u50cf\u4e00\u5bf9\u7389\u8774\u8776\u3002\u201d \u3000\u3000\u82cf\u666e\u63a5\u53e3\u9053\uff1a\u201c\u662f\uff0c\u771f\u50cf\uff01\u5f88\u4e45\u4ee5\u524d\uff0c\u6709\u4e00\u4e2a\u6c49\u4eba\u5c0f\u59d1\u5a18\uff0c\u66fe\u8ddf\u6211\u8bf4\u4e86\u4e2a\u8774\u8776\u7684\u6545\u4e8b\u3002\u8bf4\u6709\u4e2a\u6c49\u4eba\u5c11\u5e74\uff0c\u6709\u4e2a\u6c49\u4eba\u59d1\u5a18\uff0c\u4e24\u4e2a\u513f\u5f88\u8981\u597d\uff0c\u53ef\u662f\u90a3\u59d1\u5a18\u7684\u7238\u7238\u4e0d\u8bb8\u90a3\u5c11\u5e74\u5a36\u4ed6\u5973\u513f\u3002\u90a3\u5c11\u5e74\u5f88\u4f24\u5fc3\uff0c\u751f\u4e86\u4e00\u573a\u75c5\u4fbf\u6b7b\u4e86\u3002\u6709\u4e00\u5929\uff0c\u90a3\u59d1\u5a18\u7ecf\u8fc7\u60c5\u90ce\u7684\u575f\u5893\uff0c\u5c31\u4f0f\u5728\u575f\u4e0a\u75db\u54ed\u3002\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u5728\u82cf\u666e\u548c\u674e\u6587\u79c0\u5fc3\u5e95\uff0c\u90fd\u51fa\u73b0\u4e86\u516b\u4e5d\u5e74\u524d\u7684\u60c5\u666f\uff1a\u5728\u5c0f\u5c71\u4e18\u4e0a\uff0c\u4e00\u4e2a\u7537\u5b69\u548c\u4e00\u4e2a\u5973\u5b69\u5e76\u80a9\u5750\u7740\u7167\u987e\u7f8a\u7fa4\u3002 \u3000\u3000\u5973\u5b69\u8bf4\u7740\u6545\u4e8b\uff0c\u7537\u5b69\u60a0\u7136\u795e\u5f80\u5730\u542c\u7740\uff0c\u8bf4\u5230\u90a3\u6c49\u4eba\u59d1\u5a18\u4f0f\u5728\u60c5\u90ce\u7684\u575f\u4e0a\u54ed\u6ce3\uff0c\u5973\u5b69\u7684\u773c\u4e2d\u5145\u6ee1\u4e86\u773c\u6cea\uff0c\u7537\u5b69\u4e5f\u611f\u5230\u4f24\u5fc3\u96be\u53d7\u3002 \u3000\u3000\u53ea\u662f\uff0c\u674e\u6587\u79c0\u77e5\u9053\u90a3\u7537\u5b69\u4fbf\u662f\u773c\u524d\u7684\u82cf\u666e\uff0c\u82cf\u666e\u5374\u4ee5\u4e3a\u90a3\u4e2a\u5c0f\u5973\u5b69\u5df2\u7ecf\u6b7b\u4e86\u3002 \u3000\u3000\u82cf\u666e\u7ee7\u7eed\u9053\uff1a\u201c\u90a3\u4e2a\u59d1\u5a18\u4f0f\u5728\u575f\u4e0a\u54ed\u5f97\u5f88\u60b2\u4f24\uff0c\u7a81\u7136\u4e4b\u95f4\uff0c\u575f\u5893\u88c2\u5f00\u4e86\u4e00\u6761\u5927\u7f1d\uff0c\u90a3\u4e2a\u7f8e\u4e3d\u7684\u59d1\u5a18\u5c31\u8df3\u4e86\u8fdb\u53bb\u3002\u540e\u6765\u8fd9\u5bf9\u60c5\u4eba\u53d8\u6210\u4e86\u4e00\u53cc\u8774\u8776\uff0c\u603b\u662f\u98de\u5728\u4e00\u8d77\uff0c\u6c38\u8fdc\u4e0d\u518d\u5206\u79bb\u3002\u201d\u963f\u66fc\u63d2\u53e3\u9053\uff1a\u201c\u8fd9\u6545\u4e8b\u5f88\u597d\u3002\u8bf4\u8fd9\u6545\u4e8b\u7684\uff0c\u5c31\u662f\u7ed9\u4f60\u5730\u56fe\u624b\u5e15\u7684\u5c0f\u59d1\u5a18\u4e48\uff1f\u5979\u6b7b\u4e86\u4e48\uff1f\u201d\u82cf\u666e\u9eef\u7136\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5c31\u662f\u5979\u3002\u90a3\u8001\u6c49\u4eba\u8bf4\u5979\u5df2\u7ecf\u6b7b\u4e86\u3002\u201d\u674e\u6587\u79c0\u9053\uff1a\u201c\u4f60\u8fd8\u8bb0\u5f97\u5979\u4e48\uff1f\u201d\u82cf\u666e\u9053\uff1a\u201c\u81ea\u7136\u8bb0\u5f97\u3002\u90a3\u600e\u4e48\u4f1a\u5fd8\u8bb0\uff1f\u201d\u674e\u6587\u79c0\u9053\uff1a\u201c\u4f60\u600e\u4e48\u4e0d\u53bb\u77a7\u77a7\u5979\u7684\u575f\u5893\uff1f\u201d\u82cf\u666e\u9053\uff1a\u201c\u5bf9\uff01\u7b49\u6211\u4eec\u6740\u4e86\u90a3\u6279\u5f3a\u76d7\uff0c\u6211\u8981\u90a3\u5356\u9152\u7684\u8001\u6c49\u4eba\u5e26\u6211\u53bb\u77a7\u77a7\u3002\u201d\u674e\u6587\u79c0\u9053\uff1a\u201c\u8981\u662f\u90a3\u5893\u4e0a\u4e5f\u88c2\u5f00\u4e86\u4e00\u6761\u5927\u7f1d\uff0c\u4f60\u4f1a\u4e0d\u4f1a\u8df3\u8fdb\u53bb\uff1f\u201d \u3000\u3000\u82cf\u666e\u7b11\u9053\uff1a\u201c\u90a3\u662f\u6545\u4e8b\u4e2d\u8bf4\u7684\uff0c\u4e0d\u4f1a\u771f\u7684\u662f\u8fd9\u6837\u3002\u201d\u674e\u6587\u79c0\u9053\uff1a\u201c\u5982\u679c\u90a3\u5c0f\u59d1\u5a18\u5f88\u662f\u60f3\u5ff5\u4f60\uff0c\u65e5\u65e5\u591c\u591c\u7684\u76fc\u671b\u4f60\u53bb\u966a\u5979\uff0c\u56e0\u6b64\u575f\u4e0a\u771f\u7684\u88c2\u5f00\u4e86\u4e00\u6761\u5927\u7f1d\uff0c\u4f60\u80af\u8df3\u8fdb\u575f\u53bb\uff0c\u6c38\u8fdc\u966a\u5979\u4e48\uff1f\u201d \u3000\u3000\u82cf\u666e\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u4e0d\u3002\u90a3\u4e2a\u5c0f\u59d1\u5a18\u53ea\u662f\u6211\u5c0f\u65f6\u7684\u597d\u670b\u53cb\u3002\u8fd9\u4e00\u751f\u4e00\u4e16\uff0c\u6211\u662f\u8981\u966a\u963f\u66fc\u7684\u3002\u201d\u8bf4\u7740\u4f38\u51fa\u624b\u53bb\uff0c\u548c\u963f\u66fc\u53cc\u624b\u76f8\u63e1\u3002 \u3000\u3000\u674e\u6587\u79c0\u4e0d\u518d\u95ee\u4e86\u3002\u8fd9\u51e0\u53e5\u8bdd\u5979\u672c\u6765\u4e0d\u60f3\u95ee\u7684\uff0c\u5979\u5176\u5b9e\u65e9\u5df2\u77e5\u9053\u4e86\u7b54\u6848\uff0c\u53ef\u662f\u5fcd\u4e0d\u4f4f\u8fd8\u662f\u8981\u95ee\u3002\u73b0\u4e0b\u542c\u5230\u7b54\u6848\uff0c\u5f92\u7136\u589e\u6dfb\u4e86\u4f24\u5fc3\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u8fdc\u5904\u6709\u4e00\u53ea\u5929\u94c3\u9e1f\u8f7b\u8f7b\u7684\u5531\u8d77\u6765\uff0c\u5531\u5f97\u90a3\u4e48\u5b9b\u8f6c\u52a8\u542c\uff0c\u90a3\u4e48\u51c4\u51c9\u54c0\u6028\u3002 \u3000\u3000\u82cf\u666e\u9053\uff1a\u201c\u4ece\u524d\uff0c\u6211\u5e38\u5e38\u53bb\u6349\u5929\u94c3\u9e1f\u6765\u73a9\uff0c\u73a9\u5b8c\u4e4b\u540e\u5c31\u5f04\u6b7b\u4e86\u3002\u4f46\u90a3\u4e2a\u5c0f\u5973\u5b69\u5f88\u559c\u6b22\u5929\u94c3\u9e1f\uff0c\u9001\u4e86\u4e00\u53ea\u7389\u956f\u5b50\u7ed9\u6211\uff0c\u53eb\u6211\u653e\u4e86\u9e1f\u513f\u3002\u4ece\u6b64\u6211\u4e0d\u518d\u6349\u4e86\uff0c\u53ea\u542c\u5929\u9f84\u9e1f\u5728\u534a\u591c\u91cc\u5531\u6b4c\u3002\u4f60\u4eec\u542c\uff0c\u5531\u5f97\u591a\u597d\uff01\u201d\u674e\u6587\u79c0\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u95ee\u9053\uff1a\u201c\u90a3\u53ea\u7389\u956f\u5b50\u5462\uff0c\u4f60\u5e26\u5728\u8eab\u8fb9\u4e48\uff1f\u201d\u82cf\u666e\u9053\uff1a\u201c\u90a3\u662f\u5f88\u4e45\u5f88\u4e45\u4ee5\u524d\u7684\u4e8b\u4e86\uff0c\u65e9\u5c31\u6253\u788e\u4e86\uff0c\u4e0d\u89c1\u4e86\u3002\u201d \u3000\u3000\u674e\u6587\u79c0\u5e7d\u5e7d\u7684\u9053\uff1a\u201c\u5514\uff0c\u90a3\u662f\u5f88\u4e45\u5f88\u4e45\u4ee5\u524d\u7684\u4e8b\u4e86\uff0c\u65e9\u5c31\u6253\u788e\u4e86\uff0c\u4e0d\u89c1\u4e86\u3002\u201d \u3000\u3000\u5929\u94c3\u9e1f\u4e0d\u65ad\u7684\u5728\u5531\u6b4c\u3002\u5728\u5bd2\u51b7\u7684\u51ac\u5929\u591c\u665a\uff0c\u5929\u94c3\u9e1f\u672c\u6765\u4e0d\u5531\u6b4c\u7684\uff0c\u4e0d\u77e5\u9053\u5b83\u6709\u4ec0\u4e48\u4f24\u5fc3\u7684\u4e8b\uff0c\u5fcd\u4e0d\u4f4f\u8981\u503e\u5410\uff1f \u3000\u3000\u82cf\u9c81\u514b\u3001\u8f66\u5c14\u5e93\u3001\u9a86\u9a7c\u4ed6\u4eec\u7684\u9f3e\u58f0\uff0c\u53ef\u6bd4\u5929\u94c3\u9e1f\u7684\u6b4c\u58f0\u54cd\u5f97\u591a\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u5929\u4e00\u4eae\uff0c\u4e03\u4eba\u8d77\u8eab\u5403\u4e86\u5e72\u7cae\uff0c\u8ddf\u7740\u8db3\u5370\u53c8\u8ffd\u3002\u9633\u5149\u6de1\u6de1\u7684\uff0c\u7167\u5728\u8eab\u4e0a\u53ea\u5fae\u6709\u6696\u6c14\u3002\u4f46\u6709\u4e86\u592a\u9633\u5149\uff0c\u8c01\u4e5f\u4e0d\u6015\u6076\u9b3c\u4e86\u3002 \u3000\u3000\u8ffd\u5230\u4e0b\u5348\uff0c\u6c99\u6f20\u4e2d\u7684\u4e00\u9053\u8db3\u5370\u53d8\u6210\u4e86\u4e24\u9053\u3002\u90a3\u7b2c\u4e8c\u4e2a\u4eba\u663e\u7136\u4e0d\u8010\u70e6\u518d\u8e0f\u5728\u524d\u4eba\u7684\u811a\u5370\u4e4b\u4e2d\u8d70\u8def\u3002\u82cf\u9c81\u514b\u90fd\u6b22\u547c\u8d77\u6765\u3002 \u3000\u3000\u8fd9\u662f\u4eba\uff0c\u4e0d\u662f\u9b3c\u3002\u7136\u800c\u90a3\u662f\u8c01\uff1f \u3000\u3000\u4e03\u4eba\u8fd9\u65f6\u6240\u8d70\u7684\u65b9\u5411\uff0c\u65e9\u5df2\u4e0d\u662f\u674e\u6587\u79c0\u5e73\u65e5\u53bb\u5e08\u7236\u5c45\u6240\u7684\u9014\u5f84\u3002\u5979\u5ffd\u7136\u60f3\u8d77\uff1a\u201c\u8fd9\u5f3a\u76d7\u6050\u6015\u4e0d\u662f\u53bb\u548c\u76d7\u4f19\u76f8\u4f1a\uff0c\u800c\u662f\u7167\u7740\u624b\u5e15\u4e0a\u6240\u7ec7\u7684\u5730\u56fe\uff0c\u72ec\u81ea\u5bfb\u9ad8\u660c\u8ff7\u5bab\u53bb\u4e86\u3002\u201d\u5979\u8bf4\u51fa\u4e86\u5fc3\u4e2d\u7684\u63a8\u6d4b\uff0c\u82cf\u9c81\u514b\u7b49\u5446\u4e86\u4e00\u9635\uff0c\u9f50\u58f0\u79f0\u662f\u3002\u6851\u65af\u513f\u9053\uff1a\u201c\u8fd9\u4e00\u5e26\u6c99\u6f20\u5e73\u65e5\u534a\u6ef4\u6c34\u90fd\u6ca1\u6709\uff0c\u6c49\u4eba\u5f3a\u76d7\u4e0d\u4f1a\u5230\u8fd9\u91cc\u6765\u7684\u3002\u201d\u82cf\u9c81\u514b\u5927\u58f0\u9053\uff1a\u201c\u4ed6\u9003\u53bb\u8ff7\u5bab\uff0c\u54b1\u4eec\u5c31\u8ffd\u5230\u8ff7\u5bab\u3002\u5c31\u662f\u8ffd\u5230\u5929\u8fb9\uff0c\u4e5f\u8981\u6349\u5230\u8fd9\u6076\u5f3a\u76d7\u3002\u201d \u3000\u3000\u90e8\u65cf\u4e2d\u4e16\u4ee3\u76f8\u4f20\uff0c\u5927\u6208\u58c1\u4e2d\u6709\u4e00\u5ea7\u8ff7\u5bab\uff0c\u5bab\u91cc\u6709\u6570\u4e0d\u5c3d\u7684\u73cd\u5b9d\uff0c\u53ea\u662f\u8c01\u4e5f\u4e0d\u8ba4\u8bc6\u53bb\u8ff7\u5bab\u7684\u9053\u8def\uff0c\u5728\u5927\u6208\u58c1\u4e2d\u8ff7\u4e86\u8def\u53ef\u4e0d\u662f\u73a9\u7684\uff0c\u56e0\u6b64\u4ece\u6765\u6ca1\u6709\u4eba\u6562\u5192\u9669\u5bfb\u8bbf\u3002\u4f46\u73b0\u5728\u6709\u4e86\u5730\u56fe\uff0c\u6c99\u6f20\u4e2d\u7684\u51b0\u96ea\u4e8c\u4e09\u5341\u5929\u4e5f\u4e0d\u4f1a\u6d88\u5c3d\uff0c\u540e\u9762\u53c8\u6709\u5927\u961f\u4eba\u9a6c\u63a5\u5e94\uff0c\u90a3\u8fd8\u6015\u4ec0\u4e48\uff1f \u3000\u3000\u4f55\u51b5\uff0c\u82cf\u9c81\u514b\u5411\u6765\u81ea\u8d1f\u662f\u5927\u8349\u539f\u4e0a\u7684\u7b2c\u4e00\u52c7\u58eb\u3002\u4ed6\u53ea\u76fc\u8f66\u5c14\u5e93\u793a\u5f31\uff0c\u9000\u7f29\u4e86\u4e0d\u6562\u518d\u8ffd\u3002\u53ef\u662f\u8f66\u5c14\u5e93\u4e1d\u6beb\u6ca1\u6709\u5bb3\u6015\u7684\u6a21\u6837\u3002 \u3000\u3000\u674e\u6587\u79c0\u9053\uff1a\u201c\u5bf9\uff0c\u6211\u4eec\u4e00\u8d77\u53bb\u77a7\u77a7\uff0c\u5230\u5e95\u4e16\u4e0a\u662f\u4e0d\u662f\u771f\u6709\u4e00\u5ea7\u9ad8\u660c\u8ff7\u5bab\u3002\u201d\u5979\u60f3\u7236\u6bcd\u4e3a\u6b64\u4e27\u8eab\uff0c\u5982\u679c\u81ea\u5df1\u80fd\u627e\u5230\u8ff7\u5bab\uff0c\u4e5f\u7b97\u662f\u5b8c\u6210\u4e86\u7236\u6bcd\u7684\u9057\u5fd7\u3002 \u3000\u3000\u963f\u66fc\u9053\uff1a\u201c\u65cf\u91cc\u7684\u8001\u4eba\u4eec\u90fd\u8bf4\uff0c\u9ad8\u660c\u8ff7\u5bab\u4e2d\u7684\u5b9d\u7269\uff0c\u80fd\u8ba9\u5929\u5c71\u5357\u5317\u5343\u5343\u4e07\u4e07\u4eba\u6c38\u8fdc\u8fc7\u5feb\u6d3b\u65e5\u5b50\u3002\u5343\u767e\u5e74\u6765\u8fd9\u6837\u4f20\u8bf4\uff0c\u53ef\u662f\u8c01\u4e5f\u627e\u4e0d\u5230\u3002\u201d\u82cf\u666e\u559c\u9053\uff1a\u201c\u8981\u662f\u6211\u4eec\u627e\u5230\u4e86\uff0c\u5927\u5bb6\u90fd\u8fc7\u5feb\u6d3b\u65e5\u5b50\uff0c\u90a3\u53ef\u771f\u597d\uff01\u201d\u963f\u66fc\u9053\uff1a\u201c\u96be\u9053\u6211\u4eec\u73b0\u5728\u7684\u65e5\u5b50\u4e0d\u5feb\u6d3b\u4e48\uff1f\u201d\u82cf\u666e\u6414\u6414\u5934\uff0c\u7b11\u9053\uff1a\u201c\u5feb\u6d3b\u5f97\u5f88\uff0c\u5feb\u6d3b\u5f97\u5f88\u3002\u201d\u4ed6\u5b9e\u5728\u60f3\u4e0d\u51fa\uff0c\u4e16\u4e0a\u8fd8\u6709\u4ec0\u4e48\u4e1c\u897f\uff0c\u80fd\u4ee4\u4ed6\u8fc7\u7684\u65e5\u5b50\u6bd4\u73b0\u5728\u8fd8\u5feb\u6d3b\u3002 \u3000\u3000\u674e\u6587\u79c0\u5374\u5728\u60f3\uff1a\u201c\u4e0d\u8bba\u9ad8\u660c\u8ff7\u5bab\u4e2d\u6709\u591a\u5c11\u73cd\u5947\u7684\u5b9d\u7269\uff0c\u4e5f\u51b3\u4e0d\u80fd\u8ba9\u6211\u7684\u65e5\u5b50\u8fc7\u5f97\u5feb\u6d3b\u3002\u201d \u3000\u3000\u5728\u7b2c\u516b\u5929\u4e0a\uff0c\u4e03\u4eba\u4f9d\u7740\u8db3\u8ff9\uff0c\u8fdb\u5165\u4e86\u4e1b\u5c71\u3002\u5c71\u77f3\u5d99\u5ccb\uff0c\u8d8a\u884c\u8d8a\u662f\u96be\u8d70\uff0c\u597d\u5728\u96ea\u5730\u91cc\u8db3\u8ff9\u6781\u662f\u660e\u663e\uff0c\u53ea\u662f\u5c71\u52bf\u9669\u6076\uff0c\u9053\u8def\u5d0e\u5c96\uff0c\u5176\u5b9e\u6839\u672c\u5c31\u6ca1\u6709\u8def\uff0c\u53ea\u6709\u8ddf\u7740\u524d\u4eba\u7684\u8db3\u5370\u5728\u5c71\u5761\u5c71\u8c37\u95f4\u7a7f\u884c\u800c\u5df2\uff0c\u773c\u89c1\u9762\u524d\u8def\u7a0b\u65e0\u7a77\u65e0\u5c3d\uff0c\u96ea\u5730\u91cc\u7684\u4e24\u884c\u8db3\u8ff9\u4f3c\u4e4e\u76f4\u901a\u5230\u5730\u72f1\u4e2d\u53bb\u3002 \u3000\u3000\u82cf\u9c81\u514b\u548c\u8f66\u5c14\u5e93\u89c1\u56db\u5468\u60c5\u52bf\u51f6\u9669\uff0c\u5fc3\u4e2d\u4e5f\u65e9\u81ea\u53d1\u6bdb\uff0c\u4f46\u4e24\u4eba\u4f60\u4e00\u53e5\u6211\u4e00\u53e5\u5140\u81ea\u6597\u53e3\u3002\u82cf\u9c81\u514b\u8bf4\uff1a\u201c\u8f66\u5c14\u5e93\uff0c\u4f60\u5728\u6d51\u8eab\u53d1\u6296\uff0c\u5413\u7834\u4e86\u80c6\u5b50\u53ef\u4e0d\u662f\u73a9\u7684\u3002\u4e0d\u5982\u5c31\u5728\u8fd9\u91cc\u7b49\u6211\u5427\uff0c\u5018\u82e5\u627e\u5230\u8d22\u5b9d\uff0c\u4e00\u5b9a\u5206\u7ed9\u4f60\u4e00\u4efd\u3002\u201d\u8f66\u5c14\u5e93\u8bf4\uff1a\u201c\u8fd9\u4f1a\u513f\u901e\u82f1\u96c4\u597d\u6c49\uff0c\u5f85\u4f1a\u513f\u6076\u9b3c\u51fa\u6765\uff0c\u77a7\u662f\u4f60\u5148\u9003\u5462\uff0c\u8fd8\u662f\u4f60\u513f\u5b50\u5148\u9003\uff1f\u201d\u82cf\u9c81\u514b\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u54b1\u7237\u513f\u4fe9\u89c1\u4e86\u6076\u9b3c\u8fd8\u6709\u529b\u6c14\u9003\u8d70\uff0c\u603b\u4e0d\u50cf\u4f60\u90a3\u6837\uff0c\u5413\u5f97\u8dea\u5728\u5730\u4e0b\u53d1\u6296\u3002\u201d \u3000\u3000\u4e24\u4e2a\u8bf4\u6765\u8bf4\u53bb\uff0c\u603b\u662f\u79bb\u4e0d\u5f00\u6c99\u6f20\u7684\u6076\u9b3c\uff0c\u518d\u8d70\u4e00\u4f1a\uff0c\u56db\u4e0b\u91cc\u5df2\u662f\u9ed1\u6f06\u6f06\u4e00\u7247\u3002\u82cf\u666e\u9053\uff1a\u201c\u7239\uff0c\u4fbf\u5728\u8fd9\u91cc\u6b47\u5bbf\uff0c\u660e\u5929\u518d\u8d70\u7f62\uff01\u201d\u82cf\u9c81\u514b\u8fd8\u6ca1\u56de\u7b54\uff0c\u8f66\u5c14\u5e93\u7b11\u9053\uff1a\u201c\u5f88\u597d\uff0c\u4f60\u7237\u513f\u4fe9\u5728\u8fd9\u91cc\u6b47\u7740\uff0c\u4ee5\u514d\u5371\u9669\u3002\u963f\u66fc\uff0c\u4f60\u8ddf\u7239\u7239\u6765\uff0c\u9a86\u9a7c\uff0c\u6851\u65af\u513f\uff0c\u54b1\u4eec\u4e0d\u6015\u9b3c\uff0c\u8d70\uff01\u201d\u82cf\u9c81\u514b\u201c\u5478\u201d\u7684\u4e00\u58f0\uff0c\u5728\u5730\u4e0b\u5410\u53e3\u553e\u6db2\uff0c\u5f53\u5148\u8fc8\u6b65\u4fbf\u884c\u3002\u674e\u6587\u79c0\u773c\u89c1\u4ed6\u4eec\u4e8c\u4eba\u6597\u6c14\u901e\u5f3a\uff0c\u8c01\u4e5f\u4e0d\u80af\u793a\u5f31\uff0c\u53ea\u5f97\u4e5f\u8ddf\u968f\u5728\u540e\uff0c\u963f\u66fc\u5374\u7d2f\u5f97\u8981\u652f\u6301\u4e0d\u4f4f\u4e86\u3002\u82cf\u666e\u3001\u6851\u65af\u513f\u6361\u4e86\u4e9b\u67af\u679d\uff0c\u505a\u6210\u706b\u628a\u3002\u4e03\u4eba\u5728\u68ee\u6797\u4e4b\u4e2d\uff0c\u5bfb\u89c5\u8db3\u5370\u800c\u884c\u3002\u9ed1\u591c\u91cc\u8d70\u5728\u8fd9\u822c\u9b3c\u6c14\u68ee\u68ee\u7684\u6240\u5728\uff0c\u8c01\u90fd\u5fc3\u60ca\u8089\u8df3\uff0c\u5076\u5c14\u591c\u9e1f\u4e00\u58f0\u557c\u53eb\uff0c\u6216\u662f\u6811\u679d\u4e0a\u6389\u4e0b\u4e00\u5757\u79ef\u96ea\uff0c\u90fd\u4f7f\u4eba\u5413\u4e00\u5927\u8df3\u3002\u5947\u602a\u7684\u662f\uff0c\u68ee\u6797\u4e2d\u7adf\u6709\u9053\u8def\uff0c\u867d\u7136\u957f\u8349\u6ca1\u5f84\uff0c\u4f46\u53e4\u9053\u7684\u75d5\u8ff9\u8fd8\u662f\u4f9d\u7a00\u53ef\u8fa8\u3002 \u3000\u3000\u4e03\u4eba\u5728\u68ee\u6797\u4e2d\u8d70\u4e86\u826f\u4e45\uff0c\u963f\u66fc\u5ffd\u7136\u53eb\u9053\uff1a\u201c\u554a\u54df\uff0c\u4e0d\u597d\u3002\u201d \u3000\u3000\u82cf\u666e\u5fd9\u95ee\uff1a\u201c\u600e\u4e48\uff1f\u201d\u963f\u66fc\u6307\u7740\u524d\u9762\u8def\u65c1\u7684\u4e00\u53ea\u95ea\u95ea\u53d1\u5149\u7684\u94f6\u956f\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u77a7\uff01\u8fd9\u662f\u6211\u5148\u524d\u6389\u4e0b\u7684\u956f\u5b50\u3002\u201d\u90a3\u956f\u5b50\u5728\u4e03\u4eba\u4e4b\u524d\u4e24\u4e09\u4e08\u5904\uff0c\u5374\u4e0d\u77e5\u4f55\u4ee5\u5ffd\u7136\u4f1a\u5728\u8fd9\u91cc\u51fa\u73b0\u3002\u963f\u66fc\u9053\uff1a\u201c\u6211\u6389\u4e86\u956f\u5b50\uff0c\u5fc3\u60f3\u53ea\u5f97\u56de\u6765\u65f6\u518d\u627e\uff0c\u600e\u4e48\u53c8\u4f1a\u5230\u4e86\u8fd9\u91cc\uff1f\u201d\u8f66\u5c14\u5e93\u9053\uff1a\u201c\u4f60\u77a7\u77a7\u6e05\u695a\uff0c\u5230\u5e95\u662f\u4e0d\u662f\u7684\u3002\u201d\u963f\u66fc\u4e0d\u6562\u53bb\u62fe\uff0c\u82cf\u666e\u4e0a\u524d\u62ac\u4e86\u8d77\u6765\uff0c\u4e0d\u7b49\u963f\u66fc\u8fa8\u8ba4\uff0c\u4ed6\u65e9\u5df2\u8ba4\u51fa\uff0c\u8bf4\u9053\uff1a\u201c\u6ca1\u9519\uff0c\u662f\u5979\u7684\uff01\u201d\u8bf4\u7740\u5c06\u956f\u5b50\u9012\u7ed9\u5979\u3002 \u3000\u3000\u963f\u66fc\u4e0d\u6562\u53bb\u63a5\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u4e22\u5728\u5730\u4e0b\uff0c\u6211\u4e0d\u8981\u4e86\u3002\u201d \u3000\u3000\u82cf\u666e\u9053\uff1a\u201c\u96be\u9053\u771f\u662f\u6076\u9b3c\u73a9\u7684\u628a\u620f\uff1f\u201d\u706b\u5149\u4e4b\u4e0b\uff0c\u4e03\u4eba\u7684\u8138\u8272\u90fd\u662f\u5341\u5206\u53e4\u602a\u3002 \u3000\u3000\u9694\u4e86\u534a\u664c\uff0c\u674e\u6587\u79c0\u9053\uff1a\u201c\u8bf4\u4e0d\u5b9a\u6bd4\u6076\u9b3c\u8fd8\u8981\u7cdf\uff0c\u54b1\u4eec\u8d70\u4e0a\u8001\u8def\u6765\u5566\u3002\u8fd9\u6761\u8def\u54b1\u4eec\u5148\u524d\u8d70\u8fc7\u7684\u3002\u201d\u970e\u65f6\u4e4b\u95f4\uff0c\u4eba\u4eba\u90fd\u60f3\u8d77\u4e86\u90a3\u8457\u540d\u7684\u4f20\u8bf4\uff1a\u6c99\u6f20\u4e2d\u7684\u65c5\u4eba\u8ff7\u4e86\u8def\uff0c\u8d70\u554a\u8d70\u554a\uff0c\u7a81\u7136\u53d1\u73b0\u4e86\u8db3\u8ff9\uff0c\u4ed6\u5927\u559c\u82e5\u72c2\uff0c\u8ddf\u7740\u8db3\u8ff9\u8d70\u53bb\uff0c\u5374\u4e0d\u77e5\u90a3\u4fbf\u662f\u4ed6\u81ea\u5df1\u7684\u8db3\u8ff9\uff0c\u5faa\u7740\u65e7\u8def\u515c\u4e86\u4e00\u4e2a\u5708\u5b50\u53c8\u662f\u4e00\u4e2a\u5708\u5b50\uff0c\u76f4\u8d70\u5230\u6b7b\u3002 \u3000\u3000\u5927\u5bb6\u90fd\u4e0d\u613f\u76f8\u4fe1\u674e\u6587\u79c0\u7684\u8bdd\uff0c\u53ef\u662f\u660e\u660e\u963f\u66fc\u6389\u4e0b\u956f\u5b50\u5df2\u7ecf\u5f88\u4e45\uff0c\u8d70\u4e86\u534a\u5929\uff0c\u5ffd\u7136\u5728\u524d\u9762\u8def\u4e0a\u89c1\u5230\u956f\u5b50\uff0c\u90a3\u81ea\u7136\u662f\u515c\u4e86\u4e00\u4e2a\u5708\u5b50\uff0c\u91cd\u53c8\u8d70\u4e0a\u8001\u8def\u3002\u9ed1\u6697\u4e4b\u4e2d\uff0c\u75b2\u7d2f\u4e4b\u9645\uff0c\u8c01\u4e5f\u6ca1\u8fa8\u660e\u521a\u624d\u8def\u4e0a\u7684\u8db3\u5370\u5230\u5e95\u53ea\u662f\u4e24\u4e2a\u4eba\u7684\uff0c\u8fd8\u662f\u5df2\u52a0\u4e0a\u4e86\u4e03\u4e2a\u4eba\u7684\u3002\u9a86\u9a7c\u8d70\u4e0a\u51e0\u6b65\uff0c\u62ff\u706b\u628a\u4e00\u7167\u96ea\u5730\u91cc\u7684\u811a\u5370\uff0c\u53eb\u9053\uff1a\u201c\u597d\u591a\u4eba\u7684\u811a\u5370\uff0c\u662f\u54b1\u4eec\u81ea\u5df1\u7684\uff01\u201d\u58f0\u97f3\u4e2d\u5145\u6ee1\u4e86\u60e7\u610f\u3002\u4e03\u4e2a\u4eba\u9762\u9762\u76f8\u89d1\u3002\u82cf\u9c81\u514b\u548c\u8f66\u5c14\u5e93\u518d\u4e5f\u4e0d\u80fd\u81ea\u5439\u81ea\u64c2\u3001\u8ba5\u7b11\u5bf9\u65b9\u4e86\u3002 \u3000\u3000\u674e\u6587\u79c0\u9053\uff1a\u201c\u54b1\u4eec\u662f\u8ddf\u7740\u90a3\u5f3a\u76d7\u548c\u53e6\u5916\u4e00\u4e2a\u4eba\u7684\u8db3\u8ff9\u8d70\u7684\uff0c\u5018\u82e5\u4ed6\u4eec\u4e5f\u5728\u515c\u5708\u5b50\uff0c\u90a3\u4e48\u8fc7\u4e86\u4e00\u4f1a\uff0c\u4ed6\u4eec\u8fd8\u4f1a\u8d70\u5230\u8fd9\u91cc\u3002\u54b1\u4eec\u5c31\u5728\u8fd9\u91cc\u6b47\u5bbf\uff0c\u4e14\u77a7\u4ed6\u4eec\u6765\u662f\u4e0d\u6765\u3002\u201d\u5230\u8fd9\u5730\u6b65\uff0c\u4eba\u4eba\u90fd\u540c\u610f\u4e86\u5979\u7684\u8bdd\u3002\u5f53\u4e0b\u626b\u5f00\u8def\u4e0a\u79ef\u96ea\uff0c\u6253\u5f00\u6bdb\u6bef\uff0c\u5750\u4e86\u4e0b\u6765\u3002\u9a86\u9a7c\u548c\u6851\u65af\u513f\u751f\u4e86\u4e00\u5806\u706b\uff0c\u4e03\u4e2a\u4eba\u56e2\u56e2\u5750\u7740\u3002\u8c01\u4e5f\u7761\u4e0d\u7740\uff0c\u8c01\u4e5f\u4e0d\u60f3\u8bf4\u8bdd\u3002\u4ed6\u4eec\u7b49\u5019\u9648\u8fbe\u6d77\u548c\u53e6\u5916\u4e00\u4e2a\u4eba\u8d70\u6765\uff0c\u53ef\u662f\u53c8\u5bb3\u6015\u4ed6\u4eec\u771f\u7684\u51fa\u73b0\uff0c\u5018\u82e5\u4ed6\u4eec\u515c\u4e86\u4e00\u4e2a\u5708\u5b50\u53c8\u56de\u5230\u65e7\u8def\u4e0a\u6765\uff0c\u53ea\u6015\u81ea\u5df1\u7684\u547d\u8fd0\u548c\u4ed6\u4eec\u4e5f\u4f1a\u4e00\u6837\u3002 \u3000\u3000\u7b49\u4e86\u826f\u4e45\u826f\u4e45\uff0c\u5ffd\u7136\uff0c\u542c\u5230\u4e86\u811a\u6b65\u58f0\u3002 \u3000\u3000\u4e03\u4eba\u542c\u5230\u811a\u6b65\u58f0\uff0c\u4e00\u9f50\u8dc3\u8d77\u8eab\u6765\uff0c\u5374\u542c\u90a3\u811a\u6b65\u58f0\u7a81\u7136\u505c\u987f\u3002\u5728\u8fd9\u77ed\u77ed\u7684\u4e00\u5ffd\u513f\u4e4b\u95f4\uff0c\u4e03\u4e2a\u4eba\u8fde\u81ea\u5df1\u7684\u5fc3\u8df3\u90fd\u542c\u89c1\u4e86\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u811a\u6b65\u58f0\u53c8\u54cd\u4e86\u8d77\u6765\uff0c\u5374\u662f\u5411\u897f\u5317\u65b9\u9010\u6e10\u8fdc\u53bb\u3002\u4fbf\u5728\u6b64\u65f6\uff0c\u4e00\u9635\u75be\u98ce\u5439\u6765\uff0c\u522e\u8d77\u5730\u4e0b\u4e00\u5927\u7247\u767d\u96ea\uff0c\u90fd\u6253\u5728\u706b\u5806\u4e4b\u4e2d\uff0c\u90a3\u706b\u767b\u65f6\u7184\u4e86\uff0c\u56db\u4e0b\u91cc\u9ed1\u6f06\u4e00\u56e2\u3002 \u3000\u3000\u53ea\u542c\u5f97\u5237\u5237\u5237\u51e0\u54cd\uff0c\u82cf\u9c81\u514b\u3001\u674e\u6587\u79c0\u7b49\u516d\u4eba\u5200\u5251\u4e00\u9f50\u51fa\u9798\u3002\u963f\u66fc\u201c\u554a\u201d\u7684\u4e00\u58f0\u60ca\u547c\uff0c\u6251\u5728\u82cf\u666e\u6000\u91cc\u3002\u767d\u96ea\u6620\u7167\u4e4b\u4e0b\uff0c\u5200\u5251\u7684\u5203\u950b\u53d1\u51fa\u4e00\u95ea\u95ea\u7684\u5149\u8292\u3002\u90a3\u811a\u6b65\u58f0\u8d8a\u53bb\u8d8a\u8fdc\uff0c\u7ec8\u4e8e\u542c\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u76f4\u5230\u5929\u660e\uff0c\u68ee\u6797\u4e2d\u6ca1\u518d\u6709\u4f55\u5f02\u72b6\u3002\u65e9\u6668\u7b2c\u4e00\u7f15\u9633\u5149\u4ece\u6811\u53f6\u4e4b\u95f4\u5c04\u8fdb\u6765\uff0c\u4f17\u4eba\u7cbe\u795e\u4e3a\u4e4b\u4e00\u632f\uff0c\u4e8e\u662f\u53c8\u518d\u89c5\u8def\u524d\u884c\u3002\u8d70\u4e86\u4e00\u4f1a\uff0c\u963f\u66fc\u53d1\u89c9\u5de6\u9996\u7684\u704c\u6728\u538b\u6298\u4e86\u51e0\u6839\uff0c\u53eb\u9053\uff1a\u201c\u77a7\u8fd9\u91cc\uff01\u201d \u3000\u3000\u82cf\u666e\u62e8\u5f00\u6811\u6728\uff0c\u89c1\u5730\u4e0b\u6709\u4e24\u884c\u811a\u5370\uff0c\u6b22\u547c\u9053\uff1a\u201c\u4ed6\u4eec\u4ece\u8fd9\u91cc\u53bb\u4e86\uff01\u201d\u963f\u66fc\u9053\uff1a\u201c\u90a3\u5f3a\u76d7\u5b9a\u662f\u770b\u9519\u4e86\u5730\u56fe\uff0c\u515c\u4e86\u4e2a\u5708\u5b50\uff0c\u518d\u4ece\u8fd9\u91cc\u8d70\u53bb\uff0c\u7d2f\u5f97\u54b1\u4eec\u60ca\u5413\u4e86\u4e00\u665a\u3002\u201d\u82cf\u9c81\u514b\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u662f\u554a\uff0c\u8f66\u5c14\u5e93\u5bb6\u7684\u80c6\u5c0f\u9b3c\u5413\u4e86\u4e00\u665a\u3002\u82cf\u9c81\u514b\u5bb6\u7684\u4e24\u4e2a\u52c7\u58eb\u5374\u53ea\u76fc\u6076\u9b3c\u51fa\u73b0\uff0c\u597d\u63ea\u4f4f\u6076\u9b54\u7684\u8033\u6735\u6765\u77a7\u4e2a\u660e\u767d\u3002\u201d\u8f66\u5c14\u5e93\u4e00\u773c\u4e5f\u6ca1\u77a7\u4ed6\uff0c\u4f3c\u4e4e\u6ca1\u6709\u542c\u89c1\uff0c\u7a81\u7136\u4e4b\u95f4\uff0c\u53cd\u8fc7\u624b\u6765\u63ea\u4f4f\u4e86\u4ed6\u7684\u8033\u6735\u3002\u82cf\u9c81\u514b\u5927\u53eb\u4e00\u58f0\uff0c\u7830\u7684\u4fbf\u662f\u4e00\u62f3\uff0c\u6253\u5728\u4ed6\u80cc\u5fc3\u3002\u8f66\u5c14\u5e93\u8eab\u5b50\u4e00\u6643\uff0c\u63ea\u4f4f\u82cf\u9c81\u514b\u8033\u6735\u7684\u624b\u5374\u6ca1\u653e\u5f00\uff0c\u53ea\u62c9\u5f97\u4ed6\u8033\u6735\u4e0a\u9c9c\u8840\u957f\u6d41\uff0c\u518d\u4e00\u4f7f\u529b\uff0c\u53ea\u6015\u8033\u6735\u4e5f\u62c9\u8131\u4e86\u3002 \u3000\u3000\u674e\u6587\u79c0\u89c1\u8fd9\u4e24\u4eba\u90fd\u5df2\u56db\u5341\u6765\u5c81\u5e74\u7eaa\uff0c\u5140\u81ea\u548c\u987d\u7ae5\u4e00\u822c\u4e89\u95f9\u4e0d\u4f11\uff0c\u4e00\u534a\u662f\u771f\uff0c\u4e00\u534a\u662f\u5047\u3002\u5f53\u771f\u4ee4\u4eba\u597d\u7b11\u3002\u53ea\u89c1\u82cf\u9c81\u514b\u548c\u8f66\u5c14\u5e93\u7830\u7830\u7830\u7684\u4e92\u6bb4\u6570\u62f3\uff0c\u8fd9\u624d\u5206\u5f00\u3002\u4e00\u4e2a\u9f3b\u9752\uff0c\u4e00\u4e2a\u773c\u80bf\u3002 \u3000\u3000\u4e24\u4eba\u4e00\u8def\u4e89\u5435\uff0c\u4e00\u8def\u524d\u884c\u3002\u8fd9\u65f6\u9053\u8def\u9ad8\u4f4e\u66f2\u6298\uff0c\u5341\u5206\u96be\u884c\uff0c\u4e00\u65f6\u7ed5\u8fc7\u5c71\u5777\uff0c\u4e00\u65f6\u94bb\u8fdb\u5c71\u6d1e\uff0c\u82e5\u4e0d\u662f\u6709\u96ea\u5730\u4e2d\u7684\u8db3\u8ff9\u9886\u8def\uff0c\u4e07\u96be\u8fa8\u8ba4\u3002\u674e\u6587\u79c0\u5fc3\u60f3\uff1a\u201c\u8fd9\u8ff7\u5bab\u679c\u662f\u9690\u79d8\u4e4b\u6781\uff0c\u82e5\u65e0\u5730\u56fe\u6307\u5f15\uff0c\u600e\u80fd\u627e\u5bfb\u5f97\u5230\uff1f\u201d \u3000\u3000\u884c\u5230\u4e2d\u5348\uff0c\u5404\u4eba\u4e00\u665a\u6ca1\u7761\uff0c\u90fd\u5df2\u75b2\u7d2f\u4e4b\u6781\uff0c\u53ea\u6709\u674e\u6587\u79c0\u6b64\u65f6\u5185\u529f\u4fee\u4e3a\u5df2\u9887\u6709\u6839\u57fa\uff0c\u4ecd\u662f\u795e\u91c7\u5955\u5955\u3002\u82cf\u666e\u9053\uff1a\u201c\u7239\uff0c\u963f\u66fc\u8d70\u4e0d\u52a8\u5566\uff0c\u54b1\u4eec\u6b47\u4e00\u6b47\u5427\uff01\u201d\u82cf\u9c81\u514b\u8fd8\u672a\u56de\u7b54\uff0c\u53ea\u542c\u5f97\u8d70\u5728\u6700\u524d\u7684\u8f66\u5c14\u5e93\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u554a\uff01\u201d\u82cf\u9c81\u514b\u62a2\u4e0a\u524d\u53bb\uff0c\u8f6c\u8fc7\u4e86\u4e00\u6392\u6811\u6728\uff0c\u53ea\u89c1\u5bf9\u9762\u4e00\u5ea7\u77f3\u5c71\u4e0a\u5d4c\u7740\u4e24\u6247\u94c1\u94f8\u7684\u5927\u95e8\u3002\u95e8\u4e0a\u94c1\u9508\u6591\u9a73\uff0c\u663e\u662f\u5386\u65f6\u5df2\u4e45\u7684\u65e7\u7269\u3002 \u3000\u3000\u4e03\u4eba\u9f50\u58f0\u6b22\u547c\uff1a\u201c\u9ad8\u660c\u8ff7\u5bab\uff01\u201d\u5feb\u6b65\u5954\u8fd1\u3002\u82cf\u9c81\u514b\u4f38\u624b\u7528\u529b\u4e00\u63a8\u94c1\u95e8\uff0c\u4e24\u6247\u95e8\u7adf\u662f\u7eb9\u4e1d\u4e0d\u52a8\uff0c\u8f66\u5c14\u5e93\u9053\uff1a\u201c\u90a3\u6076\u8d3c\u5728\u91cc\u9762\u4e0a\u4e86\u95e9\u3002\u201d\u963f\u66fc\u7ec6\u770b\u94c1\u95e8\u5468\u56f4\u6709\u65e0\u673a\u62ec\uff0c\u4f46\u89c1\u90a3\u95e8\u5b9b\u5982\u5929\u751f\u5728\u77f3\u5c71\u4e2d\u4e00\u822c\uff0c\u7adf\u65e0\u534a\u70b9\u7f1d\u9699\u3002\u963f\u66fc\u62c9\u4f4f\u95e8\u73af\uff0c\u5411\u5de6\u4e00\u8f6c\uff0c\u8f6c\u4e4b\u4e0d\u52a8\uff0c\u8fd9\u8ff7\u5bab\u5efa\u6210\u5df2\u4e0d\u77e5\u6709\u51e0\u767e\u5e74\uff0c\u867d\u7136\u5927\u6f20\u4e4b\u4e2d\u5341\u5206\u5e72\u71e5\uff0c\u4f46\u94c1\u95e8\u4e5f\u5fc5\u751f\u9508\uff0c\u5c31\u7b97\u6709\u673a\u62ec\u4e5f\u8be5\u8f6c\u4e0d\u52a8\u4e86\uff0c\u54ea\u77e5\u5979\u518d\u5411\u53f3\u8f6c\uff0c\u5c45\u7136\u751a\u662f\u677e\u52a8\u3002\u5979\u8f6c\u4e86\u51e0\u8f6c\uff0c\u82cf\u9c81\u514b\u548c\u8f66\u5c14\u5e93\u672c\u5728\u5927\u529b\u63a8\u95e8\uff0c\u7a81\u7136\u94c1\u95e8\u5411\u91cc\u6253\u5f00\uff0c\u4e24\u4eba\u51fa\u5176\u4e0d\u610f\uff0c\u4e00\u9f50\u6454\u4e86\u8fdb\u53bb\u3002\u4e24\u4eba\u4e00\u60ca\u4e4b\u4e0b\uff0c\u5927\u7b11\u7740\u722c\u8d77\u8eab\u6765\u3002 \u3000\u3000\u95e8\u5185\u662f\u6761\u9ed1\u6c89\u6c89\u7684\u957f\u752c\u9053\uff0c\u82cf\u666e\u70b9\u71c3\u706b\u628a\uff0c\u4e00\u624b\u6267\u4e86\uff0c\u53e6\u5916\u4e00\u624b\u62ff\u7740\u957f\u5200\uff0c\u5f53\u5148\u9886\u8def\u3002\u8d70\u5b8c\u752c\u9053\uff0c\u773c\u524d\u51fa\u73b0\u4e86\u4e09\u6761\u5c94\u8def\u3002\u8ff7\u5bab\u4e4b\u5185\u5e76\u65e0\u96ea\u5730\u8db3\u8ff9\u6307\u5f15\uff0c\u4e0d\u77e5\u90a3\u4e24\u4eba\u5411\u54ea\u4e00\u6761\u8def\u8d70\u53bb\u3002\u5404\u4eba\u4fef\u8eab\u7ec6\u770b\uff0c\u89c1\u5de6\u9996\u548c\u53f3\u9996\u4e24\u6761\u8def\u4e0a\u90fd\u6709\u6de1\u6de1\u7684\u8db3\u5370\u3002 \u3000\u3000\u82cf\u9c81\u514b\u9053\uff1a\u201c\u56db\u4e2a\u8d70\u5de6\u8fb9\u7684\uff0c\u4e09\u4e2a\u8d70\u53f3\u8fb9\u7684\uff0c\u5f85\u4f1a\u513f\u518d\u5728\u8fd9\u91cc\u4f1a\u5408\u3002\u201d\u674e\u6587\u79c0\u9053\uff1a\u201c\u90a3\u4e0d\u597d\uff01\u8fd9\u5730\u65b9\u65e2\u7136\u53eb\u4f5c\u8ff7\u5bab\uff0c\u9053\u8def\u4e00\u5b9a\u66f2\u6298\uff0c\u54b1\u4eec\u8fd8\u662f\u4e00\u8d77\u7684\u597d\u3002\u201d\u82cf\u9c81\u514b\u6447\u5934\u9053\uff1a\u201c\u8c05\u8fd9\u5c71\u6d1e\u4e4b\u4e2d\uff0c\u80fd\u6709\u591a\u5927\u5730\u65b9\uff1f\u6c49\u4eba\u751f\u6765\u80c6\u5c0f\uff0c\u771f\u6ca1\u6cd5\u5b50\u3002\u201d\u4ed6\u8bdd\u662f\u8fd9\u4e48\u8bf4\uff0c\u4f46\u4e03\u4e2a\u4eba\u8fd8\u662f\u4e00\u9f50\u8d70\u4e86\uff0c\u89c1\u53f3\u9996\u4e00\u6761\u8def\u5bbd\u4e9b\uff0c\u4fbf\u90fd\u5411\u53f3\u884c\u3002 \u3000\u3000\u53ea\u8d70\u51fa\u5341\u4f59\u4e08\u8fdc\uff0c\u82cf\u9c81\u514b\u4fbf\u60f3\uff1a\u201c\u8fd9\u6c49\u4eba\u7684\u8bdd\u5012\u662f\u4e0d\u9519\u3002\u201d \u3000\u3000\u53ea\u89c1\u524d\u9762\u53c8\u51fa\u73b0\u4e86\u5c94\u8def\u3002\u4e03\u4e2a\u4eba\u7ec6\u7ec6\u8fa8\u8ba4\u811a\u5370\uff0c\u4e00\u8def\u8ddf\u8e2a\u800c\u8fdb\uff0c\u6709\u65f6\u5c94\u8def\u4e0a\u4e24\u8fb9\u90fd\u6709\u811a\u5370\uff0c\u53ea\u5f97\u4efb\u610f\u9009\u4e00\u6761\u8def\u3002\u8d70\u4e86\u597d\u534a\u5929\uff0c\u5c71\u6d1e\u4e2d\u5c94\u8def\u4e0d\u77e5\u51e1\u51e0\uff0c\u6bcf\u5230\u4e00\u5904\u5c94\u8def\uff0c\u963f\u66fc\u4fbf\u5728\u5c71\u58c1\u4e0a\u7528\u5200\u5212\u4e0b\u8bb0\u53f7\uff0c\u4ee5\u514d\u56de\u51fa\u6765\u65f6\u627e\u4e0d\u5230\u539f\u8def\u3002\u7a81\u7136\u4e4b\u95f4\uff0c\u773c\u524d\u8c41\u7136\u5f00\u6717\uff0c\u51fa\u73b0\u4e00\u5927\u7247\u7a7a\u5730\uff0c\u5c3d\u5934\u5904\u53c8\u6709\u4e24\u6247\u94c1\u95e8\uff0c\u5d4c\u5728\u5927\u5c71\u5ca9\u4e2d\u3002 \u3000\u3000\u4e03\u4e2a\u4eba\u8d70\u8fc7\u7a7a\u5730\uff0c\u6765\u5230\u95e8\u524d\u3002\u82cf\u9c81\u514b\u53c8\u53bb\u8f6c\u95e8\u73af\uff0c\u4e0d\u6599\u8fd9\u6247\u95e8\u5374\u662f\u865a\u63a9\u7684\uff0c\u8f7b\u8f7b\u4e00\u78b0\uff0c\u4fbf\u201c\u5440\u201d\u7684\u4e00\u58f0\u5f00\u4e86\u3002\u4e03\u4eba\u8d70\u4e86\u8fdb\u53bb\uff0c\u53ea\u89c1\u91cc\u9762\u662f\u4e00\u95f4\u6bbf\u5802\uff0c\u56db\u58c1\u4f9b\u7684\u90fd\u662f\u6ce5\u5851\u6728\u96d5\u7684\u4f5b\u50cf\uff0c\u4ece\u8fd9\u6bbf\u5802\u8fdb\u53bb\uff0c\u8fde\u7ef5\u4e0d\u65ad\u7684\u662f\u4e00\u5217\u623f\u820d\u3002\u6bcf\u4e00\u95f4\u623f\u4e2d\u5927\u90fd\u4f9b\u6709\u4f5b\u50cf\u3002\u5076\u7136\u5728\u58c1\u4e0a\u89c1\u5230\u51e0\u4e2a\u6c49\u6587\uff0c\u5199\u7684\u662f\u201c\u9ad8\u660c\u56fd\u56fd\u738b\u201d\u3001\u201c\u6587\u6cf0\u201d\u3001\u201c\u5927\u5510\u8d1e\u89c2\u5341\u4e09\u5e74\u201d\u7b49\u7b49\u5b57\u6837\u3002\u6709\u4e00\u5ea7\u6bbf\u5802\u4e2d\u4f9b\u7684\u90fd\u662f\u6c49\u4eba\u5851\u50cf\uff0c\u4e2d\u95f4\u4e00\u4e2a\u8001\u4eba\uff0c\u533e\u4e0a\u5199\u7684\u662f\u201c\u5927\u6210\u81f3\u5723\u5148\u5e08\u5b54\u5b50\u4f4d\u201d\uff0c\u5de6\u53f3\u5404\u6709\u6570\u5341\u4eba\uff0c\u5199\u7740\u201c\u989c\u56de\u201d\u3001\u201c\u5b50\u8def\u201d\u3001\u201c\u5b50\u8d21\u201d\u3001\u201c\u5b50\u590f\u201d\u3001\u201c\u5b50\u5f20\u201d\u7b49\u540d\u5b57\u3002\u82cf\u9c81\u514b\u4e00\u89c1\u5230\u8fd9\u8bb8\u591a\u6c49\u4eba\u5851\u50cf\uff0c\u7709\u5934\u4e00\u76b1\uff0c\u8f6c\u5934\u4fbf\u8d70\u3002 \u3000\u3000\u674e\u6587\u79c0\u5fc3\u60f3\uff1a\u201c\u8fd9\u91cc\u7684\u4eba\u90fd\u4fe1\u56de\u6559\uff0c\u600e\u4e48\u8ff7\u5bab\u91cc\u4f9b\u7684\u65e2\u6709\u4f5b\u50cf\uff0c\u53c8\u6709\u6c49\u4eba\uff1f\u58c1\u4e0a\u5199\u7684\u53c8\u90fd\u662f\u6c49\u5b57\uff0c\u771f\u662f\u5947\u602a\u4e4b\u6781\u3002\u201d \u3000\u3000\u4e03\u4eba\u8fc7\u4e86\u4e00\u5ba4\uff0c\u53c8\u662f\u4e00\u5ba4\uff0c\u53ea\u89c1\u5927\u534a\u5bab\u5ba4\u5df2\u7136\u6bc1\u572e\uff0c\u6709\u4e9b\u6bbf\u5802\u4e2d\u5806\u6ee1\u4e86\u9ec4\u6c99\uff0c\u8fde\u95e8\u6237\u4e5f\u6709\u5835\u585e\u7684\u3002\u8ff7\u5bab\u4e2d\u7684\u9053\u8def\u672c\u5df2\u5f02\u5e38\u7e41\u590d\u66f2\u6298\uff0c\u518d\u52a0\u4e0a\u5899\u5012\u6c99\u963b\uff0c\u66f4\u662f\u4ee4\u4eba\u6655\u5934\u8f6c\u5411\u3002\u6709\u65f6\u901a\u9053\u4e0a\u51fa\u73b0\u51e0\u5177\u767d\u9aa8\u9ab7\u9ac5\uff0c\u5bab\u4e2d\u7684\u5668\u7269\u7528\u5177\u5374\u90fd\u4e0d\u662f\u56de\u7586\u6240\u6709\uff0c\u674e\u6587\u79c0\u4f9d\u7a00\u8bb0\u5f97\uff0c\u8fd9\u4e9b\u90fd\u662f\u4e2d\u571f\u6c49\u4eba\u7684\u7269\u4e8b\u3002\u53ea\u628a\u5404\u4eba\u770b\u5f97\u773c\u82b1\u7f2d\u4e71\uff0c\u79f0\u5f02\u4e0d\u6b62\u3002\u4f46\u4f20\u8bf4\u4e2d\u7684\u4ec0\u4e48\u91d1\u94f6\u73e0\u5b9d\u5374\u534a\u4ef6\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u4e03\u4eba\u6cbf\u7740\u4e00\u6761\u9ed1\u6c89\u6c89\u7684\u752c\u9053\u5411\u524d\u8d70\u53bb\uff0c\u7a81\u7136\u4e4b\u95f4\uff0c\u524d\u9762\u4e00\u4e2a\u9634\u68ee\u68ee\u7684\u58f0\u97f3\u559d\u9053\uff1a\u201c\u6211\u5728\u8fd9\u91cc\u5df2\u5b89\u5b89\u9759\u9759\u7684\u4f4f\u4e86\u4e00\u5343\u5e74\uff0c\u8c01\u4e5f\u4e0d\u6562\u6765\u6253\u6270\u6211\u3002\u54ea\u4e00\u4e2a\u5927\u80c6\u8fc7\u6765\uff0c\u7acb\u523b\u5c31\u6b7b\uff01\u201d\u8bf4\u7684\u662f\u54c8\u8428\u514b\u8bed\uff0c\u97f3\u8c03\u5341\u5206\u7eaf\u6b63\uff0c\u58f0\u97f3\u5e76\u4e0d\u751a\u54cd\uff0c\u5374\u662f\u542c\u5f97\u6e05\u6e05\u695a\u695a\u3002 \u3000\u3000\u963f\u66fc\u60ca\u9053\uff1a\u201c\u662f\u6076\u9b3c\uff01\u4ed6\u2026\u2026\u4ed6\u8bf4\u5728\u8fd9\u91cc\u5df2\u4f4f\u4e86\u4e00\u5343\u5e74\u3002\u201d \u3000\u3000\u62c9\u7740\u82cf\u666e\u7684\u624b\uff0c\u5411\u540e\u9000\u4e86\u51e0\u6b65\u3002\u9a86\u9a7c\u53eb\u9053\uff1a\u201c\u8fd9\u662f\u4eba\uff0c\u4e0d\u662f\u9b3c\uff01\u201d \u3000\u3000\u9ad8\u4e3e\u706b\u628a\uff0c\u5411\u524d\u8d70\u53bb\u3002\u6851\u65af\u513f\u4e0d\u7518\u793a\u5f31\uff0c\u62a2\u4e0a\u51e0\u6b65\uff0c\u548c\u4ed6\u5e76\u80a9\u800c\u884c\uff0c\u521a\u8d70\u5230\u4e00\u4e2a\u5f2f\u89d2\u4e0a\uff0c\u84e6\u5730\u91cc\u4e24\u4eba\u9f50\u58f0\u5927\u53eb\uff0c\u8eab\u5b50\u5411\u540e\u6454\u4e86\u51fa\u6765\u3002\u4f17\u4eba\u5927\u5403\u4e00\u60ca\uff0c\u82cf\u9c81\u514b\u548c\u8f66\u5c14\u5e93\u629b\u53bb\u624b\u4e2d\u706b\u628a\uff0c\u62a2\u4e0a\u6276\u8d77\uff0c\u53ea\u542c\u5f97\u524d\u9762\u4f20\u6765\u4e00\u9635\u6840\u6840\u602a\u7b11\uff0c\u90a3\u58f0\u97f3\u9053\uff1a\u201c\u6211\u5728\u8fd9\u91cc\u5df2\u4f4f\u4e86\u4e00\u5343\u5e74\uff0c\u4f4f\u4e86\u4e00\u5343\u5e74\u3002\u8fdb\u6765\u7684\u4e00\u4e2a\u4e2a\u90fd\u6b7b\u3002\u201d \u3000\u3000\u8f66\u5c14\u5e93\u66f4\u4e0d\u591a\u60f3\uff0c\u62b1\u7740\u9a86\u9a7c\u6025\u5954\u800c\u51fa\uff0c\u82cf\u9c81\u514b\u62b1\u4e86\u6851\u65af\u513f\uff0c\u548c\u4f59\u4eba\u8ddf\u7740\u51fa\u53bb\uff0c\u4f46\u542c\u5f97\u602a\u7b11\u4e4b\u58f0\u5145\u585e\u4e86\u752c\u9053\u3002\u6765\u5230\u5929\u4e95\u4e2d\uff0c\u770b\u9a86\u9a7c\u548c\u6851\u65af\u513f\u65f6\uff0c\u4e24\u4eba\u53e3\u89d2\u6d41\u51fa\u9c9c\u8840\uff0c\u7adf\u5df2\u4e00\u9f50\u6bd9\u547d\u3002\u4e94\u4eba\u9762\u9762\u76f8\u89d1\uff0c\u53c8\u662f\u96be\u8fc7\uff0c\u53c8\u662f\u60ca\u6050\u3002 \u3000\u3000\u963f\u66fc\u9053\uff1a\u201c\u8fd9\u6076\u9b3c\u4e0d\u8bb8\u4eba\u53bb\u2026\u2026\u53bb\u6253\u6270\uff0c\u54b1\u4eec\u5feb\u8d70\u5427\uff01\u201d \u3000\u3000\u5230\u8fd9\u5730\u6b65\uff0c\u82cf\u9c81\u514b\u548c\u8f66\u5c14\u5e93\u54ea\u91cc\u8fd8\u6562\u901e\u4ec0\u4e48\u521a\u52c7\uff1f\u62b1\u7740\u4e24\u5177\u5c38\u4f53\uff0c\u5faa\u7740\u5148\u524d\u6240\u5212\u7684\u8bb0\u53f7\uff0c\u56de\u5230\u4e86\u8ff7\u5bab\u4e4b\u5916\u3002 \u3000\u3000\u8f66\u5c14\u5e93\u6b7b\u4e86\u4e24\u540d\u5fc3\u7231\u7684\u5f1f\u5b50\uff0c\u5fc3\u91cc\u5341\u5206\u96be\u8fc7\uff0c\u4e0d\u4f4f\u7684\u62ed\u6cea\u3002\u82cf\u9c81\u514b\u518d\u4e5f\u4e0d\u8ba5\u8bbd\u4ed6\u4e86\uff0c\u53cd\u800c\u51fa\u8a00\u5b89\u6170\uff0c\u53c8\u9053\uff1a\u201c\u90a3\u4e24\u4e2a\u6c49\u4eba\u5f3a\u76d7\u8fdb\u4e86\u8ff7\u5bab\u4e4b\u540e\u5f71\u8e2a\u5168\u65e0\uff0c\u5b9a\u662f\u4e5f\u7ed9\u5bab\u91cc\u7684\u6076\u9b3c\u5f04\u6b7b\u4e86\uff0c\u90a3\u4e5f\u597d\uff0c\u53eb\u8fd9\u4e24\u4e2a\u5f3a\u76d7\u6ca1\u597d\u4e0b\u573a\u3002\u201d\u963f\u66fc\u9053\uff1a\u201c\u54b1\u4eec\u4ece\u539f\u8def\u56de\u53bb\u5427\uff0c\u4ee5\u540e\u2026\u2026\u4ee5\u540e\u6c38\u8fdc\u522b\u6765\u8fd9\u5730\u65b9\u4e86\u3002\u201d\u8f66\u5c14\u5e93\u9053\uff1a\u201c\u54b1\u4eec\u65cf\u4eba\u5927\u961f\u4eba\u9a6c\u5c31\u5feb\u5230\u6765\uff0c\u53ef\u5f97\u544a\u8bc9\u4ed6\u4eec\uff0c\u522b\u8ba9\u5144\u5f1f\u4eec\u95ef\u8fdb\u5bab\u53bb\uff0c\u4e00\u4e2a\u4e2a\u7684\u6b7b\u4e8e\u975e\u547d\u3002\u201d\u82cf\u9c81\u514b\u9053\uff1a\u201c\u5bf9\uff01\u53ea\u8981\u662f\u5728\u8ff7\u5bab\u4e4b\u5916\uff0c\u90a3\u2026\u2026\u90a3\u5c31\u6ca1\u6709\u5e72\u7cfb\u3002\u201d \u3000\u3000\u662f\u4e0d\u662f\u771f\u7684\u6ca1\u6709\u5e72\u7cfb\uff0c\u90a3\u53ef\u8c01\u4e5f\u4e0d\u77e5\u9053\u3002\u4e3a\u4e86\u7a33\u59a5\u8d77\u89c1\uff0c\u4e94\u4e2a\u4eba\u76f4\u9000\u51fa\u516d\u4e03\u91cc\u5730\uff0c\u5230\u4e86\u4e00\u5927\u7247\u65f7\u5730\u4e0a\uff0c\u8fd9\u624d\u505c\u4f4f\u3002\u82cf\u9c81\u514b\u9053\uff1a\u201c\u6076\u9b3c\u6015\u592a\u9633\uff0c\u8981\u8d70\u8fc7\u8fd9\u7247\u65f7\u5730\uff0c\u975e\u6652\u5230\u592a\u9633\u4e0d\u53ef\u3002\u201d \u3000\u3000\u963f\u66fc\u9053\uff1a\u201c\u665a\u4e0a\u5462\uff1f\u201d\u82cf\u9c81\u514b\u6414\u4e86\u6414\u5934\u76ae\uff0c\u65e0\u6cd5\u56de\u7b54\u3002"}),showOnHomepage:!0},{name:"\u516b",content:(0,a.jsx)(a.Fragment,{children:"\u5e78\u597d\u6ca1\u5230\u665a\u4e0a\uff0c\u7b2c\u4e00\u961f\u4eba\u9a6c\u5df2\u7ecf\u8d76\u5230\u3002\u82cf\u9c81\u514b\u7b49\u5fd9\u5c06\u53d1\u73b0\u8ff7\u5bab\u3001\u5bab\u4e2d\u6709\u6076\u9b3c\u5bb3\u4eba\u7684\u4e8b\u8bf4\u4e86\u3002 \u3000\u3000\u867d\u7136\u4eba\u591a\u80c6\u58ee\uff0c\u4f46\u8c01\u4e5f\u6ca1\u6709\u63d0\u8bae\u524d\u53bb\u63a2\u9669\u3002\u8fc7\u5f97\u4e24\u4e2a\u65f6\u8fb0\uff0c\u7b2c\u4e8c\u961f\u3001\u7b2c\u4e09\u961f\u5148\u540e\u5230\u6765\uff0c\u6570\u767e\u4eba\u4fbf\u5728\u65f7\u5730\u4e0a\u9732\u5bbf\u3002\u6bcf\u9694\u5f97\u5341\u4f59\u4eba\uff0c\u4fbf\u70b9\u8d77\u4e86\u4e00\u5806\u5927\u706b\uff0c\u6599\u60f3\u6076\u9b3c\u518d\u51f6\uff0c\u4e5f\u5fc5\u6015\u4e86\u8fd9\u8bb8\u591a\u706b\u5806\u3002 \u3000\u3000\u674e\u6587\u79c0\u501a\u5728\u4e00\u5757\u5ca9\u77f3\u4e4b\u65c1\uff0c\u5fc3\u91cc\u5728\u60f3\uff1a\u201c\u6211\u7239\u7239\u5988\u5988\u4e07\u91cc\u8fe2\u8fe2\u7684\u4ece\u4e2d\u539f\u6765\u5230\u56de\u7586\uff0c\u4e3a\u7684\u662f\u627e\u9ad8\u660c\u8ff7\u5bab\u3002\u4ed6\u4eec\u6ca1\u627e\u5230\u8ff7\u5bab\uff0c\u5c31\u9001\u4e86\u6027\u547d\u3002\u5176\u5b9e\u5c31\u7b97\u627e\u5230\u4e86\uff0c\u591a\u534a\u4e5f\u4f1a\u7ed9\u5bab\u91cc\u7684\u6076\u9b3c\u5bb3\u6b7b\uff0c\u9664\u975e\u4ed6\u4eec\u4e00\u542c\u5230\u6076\u9b3c\u7684\u58f0\u97f3\u7acb\u523b\u5c31\u9000\u51fa\u3002\u53ef\u662f\u7239\u7239\u5988\u5988\u4e00\u8eab\u6b66\u529f\uff0c\u4e00\u5b9a\u4e0d\u80af\u542c\u6076\u9b3c\u7684\u8bdd\u3002\u5509\uff0c\u4eba\u7684\u6b66\u529f\u518d\u9ad8\uff0c\u53c8\u54ea\u91cc\u6597\u5f97\u8fc7\u9b3c\u602a\uff1f\u201d\u5ffd\u7136\u80cc\u540e\u811a\u6b65\u58f0\u8f7b\u54cd\uff0c\u4e00\u4eba\u8d70\u4e86\u8fc7\u6765\uff0c\u4f4e\u58f0\u53eb\u9053\uff1a\u201c\u963f\u79c0\u3002\u201d \u3000\u3000\u674e\u6587\u79c0\u5927\u559c\uff0c\u8df3\u8d77\u8eab\u6765\uff0c\u53eb\u9053\uff1a\u201c\u8ba1\u7237\u7237\uff0c\u4f60\u4e5f\u6765\u4e86\u3002\u201d\u8ba1\u8001\u4eba\u9053\uff1a\u201c\u6211\u4e0d\u653e\u5fc3\u4f60\uff0c\u8ddf\u7740\u5927\u4f19\u513f\u6765\u77a7\u7740\u4f60\u3002\u201d\u674e\u6587\u79c0\u5fc3\u4e2d\u611f\u6fc0\uff0c\u62c9\u4f4f\u4ed6\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u9053\u4e0a\u5f88\u96be\u8d70\uff0c\u4f60\u5e74\u7eaa\u8fd9\u4e48\u5927\u4e86\uff0c\u8f9b\u82e6\u5f97\u5f88\uff0c\u5feb\u5750\u4e0b\u6b47\u6b47\u3002\u201d \u3000\u3000\u8ba1\u8001\u4eba\u521a\u5728\u5979\u8eab\u8fb9\u5750\u4e0b\uff0c\u5ffd\u542c\u5f97\u897f\u65b9\u54cd\u8d77\u51e0\u4e0b\u5c16\u9510\u7684\u67ad\u9e23\u4e4b\u58f0\uff0c\u5f02\u5e38\u523a\u8033\u96be\u542c\u3002\u4f17\u4eba\u4e0d\u7981\u9f50\u5411\u9e23\u58f0\u6765\u5904\u671b\u53bb\uff0c\u53ea\u89c1\u767d\u6643\u6643\u7684\u4e00\u56e2\u7269\u4e8b\uff0c\u4ece\u9ed1\u6697\u4e2d\u8fc5\u901f\u5f02\u5e38\u7684\u51b2\u6765\uff0c\u51b2\u5230\u79bb\u4f17\u4eba\u7ea6\u83ab\u56db\u4e08\u4e4b\u5904\uff0c\u731b\u5730\u76f4\u7acb\u4e0d\u52a8\uff0c\u770b\u4e0a\u53bb\u4f9d\u7a00\u662f\u4e2a\u4eba\u5f62\uff0c\u706b\u5149\u6620\u7167\u4e0b\uff0c\u53ea\u89c1\u8fd9\u9b3c\u602a\u8eab\u62ab\u767d\u8272\u7f69\u888d\uff0c\u6ee1\u8138\u90fd\u662f\u9c9c\u8840\uff0c\u767d\u888d\u4e0a\u4e5f\u662f\u8840\u8ff9\u6dcb\u6f13\uff0c\u8eab\u5f62\u9ad8\u5927\u4e4b\u6781\uff0c\u81f3\u5c11\u6bd4\u5e38\u4eba\u9ad8\u4e86\u4e94\u5c3a\u3002\u9759\u591c\u770b\u6765\uff0c\u6050\u6016\u65e0\u6bd4\uff0c\u90a3\u9b3c\u602a\u9661\u7136\u95f4\u53cc\u624b\u524d\u4f38\uff0c\u5341\u6839\u6307\u7532\u6bd4\u624b\u6307\u8fd8\u957f\uff0c\u6ee1\u624b\u4e5f\u90fd\u662f\u9c9c\u8840\u3002 \u3000\u3000\u4f17\u4eba\u5c4f\u606f\u51dd\u6c14\uff0c\u5bc2\u65e0\u58f0\u606f\u7684\u671b\u7740\u4ed6\u3002 \u3000\u3000\u90a3\u9b3c\u602a\u6840\u6840\u602a\u7b11\uff0c\u5c16\u58f0\u9053\uff1a\u201c\u6211\u5728\u8ff7\u5bab\u91cc\u5df2\u4f4f\u4e86\u4e00\u5343\u5e74\uff0c\u4e0d\u8bb8\u8c01\u6765\u6253\u6270\uff0c\u8c01\u53eb\u4f60\u4eec\u8fd9\u6837\u5927\u80c6\uff1f\u201d\u8bf4\u7684\u662f\u54c8\u8428\u514b\u8bed\uff0c\u6b63\u662f\u674e\u6587\u79c0\u65e5\u95f4\u5728\u8ff7\u5bab\u4e2d\u542c\u5230\u7684\u58f0\u97f3\u3002\u90a3\u9b3c\u602a\u6162\u6162\u8f6c\u8eab\uff0c\u53cc\u624b\u5bf9\u7740\u4e09\u4e08\u5916\u7684\u4e00\u5339\u9a6c\uff0c\u53eb\u9053\uff1a\u201c\u7ed9\u6211\u6b7b\uff01\u201d\u7a81\u7136\u95f4\u56de\u8fc7\u8eab\u6765\uff0c\u75be\u9a70\u800c\u53bb\uff0c\u7247\u523b\u95f4\u8d70\u5f97\u65e0\u5f71\u65e0\u8e2a\u3002 \u3000\u3000\u8fd9\u9b3c\u602a\u7a81\u7136\u800c\u6765\uff0c\u7a81\u7136\u800c\u53bb\uff0c\u6c14\u52bf\u6151\u4eba\uff0c\u76f4\u7b49\u4ed6\u8d70\u4e86\u597d\u4e00\u4f1a\uff0c\u4f17\u4eba\u65b9\u624d\u60ca\u547c\u51fa\u6765\u3002\u53ea\u89c1\u4ed6\u53cc\u624b\u6307\u8fc7\u7684\u90a3\u5339\u9a6c\u56db\u819d\u8dea\u5012\uff0c\u7ffb\u8eab\u6bd9\u547d\u3002\u4f17\u4eba\u62e5\u8fc7\u53bb\u770b\u65f6\uff0c\u4f46\u89c1\u90a3\u9a6c\u5468\u8eab\u6ca1\u534a\u70b9\u4f24\u75d5\uff0c\u53e3\u9f3b\u4ea6\u4e0d\u6d41\u8840\uff0c\u5374\u4e0d\u77e5\u5982\u4f55\uff0c\u7adf\u662f\u4e2d\u4e86\u9b54\u6cd5\u800c\u6b7b\u3002 \u3000\u3000\u4f17\u4eba\u90fd\u8bf4\uff1a\u201c\u662f\u9b3c\uff0c\u662f\u9b3c\u3002\u201d\u6709\u4eba\u9053\uff1a\u201c\u6211\u65e9\u8bf4\u5927\u6208\u58c1\u4e2d\u6709\u9b3c\u3002\u201d\u6709\u4eba\u9053\uff1a\u201c\u90a3\u8ff7\u5bab\u5343\u5e74\u65e0\u4eba\u8fdb\u53bb\uff0c\u81ea\u7136\u6709\u9b3c\u602a\u770b\u5b88\u3002\u201d\u53c8\u6709\u4eba\u9053\uff1a\u201c\u542c\u8bf4\u9b3c\u602a\u65e0\u811a\uff0c\u77a7\u77a7\u90a3\u9b3c\u6709\u6ca1\u811a\u5370\u3002\u201d\u5f53\u4e0b\u4f17\u4eba\u62ff\u4e86\u706b\u628a\uff0c\u987a\u7740\u90a3\u9b3c\u602a\u7684\u53bb\u8def\u77a7\u53bb\uff0c\u4f46\u89c1\u6c99\u5730\u4e4b\u4e0a\u6bcf\u9694\u4e94\u5c3a\u4fbf\u662f\u4e00\u4e2a\u5c0f\u5c0f\u7684\u5706\u6d1e\uff0c\u4eba\u7684\u811a\u5370\u65e2\u4e0d\u4f1a\u8fd9\u6837\u7ec6\u7ec6\u4e00\u70b9\uff0c\u800c\u4e24\u70b9\u4e4b\u95f4\uff0c\u76f8\u8ddd\u53c8\u4e0d\u4f1a\u8fd9\u6837\u8fdc\u3002 \u3000\u3000\u8fd9\u6837\u4e00\u6765\uff0c\u5404\u4eba\u518d\u65e0\u7591\u4e49\uff0c\u90fd\u8ba4\u5b9a\u662f\u8ff7\u5bab\u4e2d\u7684\u9b3c\u602a\u4f5c\u795f\uff0c\u5927\u5bb6\u90fd\u8bf4\uff1a\u201c\u4e0d\u8bba\u8ff7\u5bab\u4e2d\u6709\u4ec0\u4e48\u4e1c\u897f\uff0c\u90a3\u4e5f\u4e0d\u80fd\u8981\u4e86\u3002\u660e\u5929\u4e00\u65e9\uff0c\u5927\u5bb6\u5feb\u5feb\u56de\u53bb\u3002\u201d \u3000\u3000\u6574\u665a\u4eba\u4eba\u5fc3\u60ca\u80c6\u6218\uff0c\u4f46\u7b2c\u4e8c\u5929\u592a\u9633\u4e00\u51fa\u6765\uff0c\u5ffd\u7136\u4e4b\u95f4\uff0c\u6bcf\u4e2a\u4eba\u5fc3\u91cc\u90fd\u4e0d\u600e\u4e48\u6015\u4e86\u3002\u6709\u4e9b\u5e74\u8f7b\u4eba\u5546\u91cf\u7740\u8981\u53bb\u8ff7\u5bab\u77a7\u77a7\u3002\u82cf\u9c81\u514b\u548c\u8f66\u5c14\u5e93\u5389\u58f0\u559d\u963b\uff0c\u8bf4\u9053\u4fbf\u662f\u8981\u53bb\u8ff7\u5bab\uff0c\u4e5f\u5f97\u5546\u8bae\u51fa\u4e00\u4e2a\u597d\u6cd5\u5b50\u6765\u3002 \u3000\u3000\u53ef\u662f\u5546\u8bae\u4e86\u4e00\u6574\u5929\uff0c\u53c8\u6709\u4ec0\u4e48\u597d\u6cd5\u5b50\uff1f\u552f\u4e00\u7684\u7ed3\u679c\uff0c\u662f\u5927\u5bb6\u540c\u610f\u5728\u8fd9\u91cc\u4f4f\u4e00\u665a\uff0c\u660e\u5929\u518d\u4ece\u957f\u8ba1\u8bae\u3002 \u3000\u3000\u5c06\u8fd1\u4ea5\u65f6\uff0c\u4fbf\u662f\u6628\u665a\u9b3c\u602a\u51fa\u73b0\u7684\u65f6\u523b\uff0c\u53ea\u542c\u5f97\u897f\u65b9\u53c8\u54cd\u8d77\u4e86\u4e09\u4e0b\u5c16\u9510\u7684\u67ad\u9e23\uff0c\u4f17\u4eba\u6bdb\u9aa8\u609a\u7136\u3002\u4f46\u89c1\u90a3\u767d\u8863\u957f\u817f\u3001\u6ee1\u8eab\u8840\u6c61\u7684\u9b3c\u602a\u53c8\u98de\u9a70\u800c\u6765\uff0c\u5728\u6570\u4e08\u5916\u8fdc\u8fdc\u7ad9\u5b9a\uff0c\u5c16\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u8fd8\u4e0d\u56de\u53bb\uff1f\u54fc\uff0c\u518d\u5728\u8fd9\u91cc\u9644\u8fd1\u9017\u7559\u4e00\u665a\uff0c\u4e00\u4e2a\u4e00\u4e2a\uff0c\u53eb\u4ed6\u90fd\u4e0d\u5f97\u597d\u6b7b\uff0c\u6211\u5728\u5bab\u91cc\u4f4f\u4e86\u4e00\u5343\u5e74\uff0c\u8c01\u90fd\u4e0d\u6562\u8fdb\u6765\uff0c\u4f60\u4eec\u8fd9\u6837\u5927\u80c6\uff01\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u6162\u6162\u8f6c\u8eab\uff0c\u53cc\u624b\u6307\u7740\u8fdc\u5904\u4e00\u4e2a\u9752\u5e74\uff0c\u53eb\u9053\uff1a\u201c\u7ed9\u6211\u6b7b\uff01\u201d\u8bf4\u4e86\u8fd9\u4e09\u4e2a\u5b57\uff0c\u731b\u5730\u91cc\u56de\u8fc7\u8eab\u6765\uff0c\u75be\u9a70\u800c\u53bb\uff0c\u6708\u5149\u4e0b\u4f46\u89c1\u4ed6\u8d8a\u8d70\u8d8a\u8fdc\uff0c\u7ec8\u4e8e\u4e0d\u89c1\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u9752\u5e74\u6162\u6162\u59d4\u987f\uff0c\u4e00\u53e5\u8bdd\u4e5f\u4e0d\u8bf4\uff0c\u5c31\u6b64\u6bd9\u547d\uff0c\u8eab\u4e0a\u4ecd\u662f\u6ca1\u534a\u70b9\u4f24\u75d5\u3002\u6628\u665a\u8fd8\u4e0d\u8fc7\u5bb3\u6b7b\u4e00\u5339\u9a6c\uff0c\u4eca\u65e5\u5374\u5bb3\u6b7b\u4e86\u4e00\u4e2a\u58ee\u5065\u7684\u9752\u5e74\u3002 \u3000\u3000\u8fd9\u6837\u4e00\u6765\uff0c\u8fd8\u6709\u8c01\u6562\u518d\u9017\u7559\uff1f\u4f55\u51b5\u542c\u5f97\u82cf\u9c81\u514b\u4ed6\u4eec\u8bf4\uff0c\u8ff7\u5bab\u4e2d\u6839\u672c\u6ca1\u6709\u4ec0\u4e48\u73cd\u5b9d\uff0c\u8fde\u4e00\u5757\u91d1\u5b50\u94f6\u5b50\u4e5f\u6ca1\u6709\u3002\u82e5\u4e0d\u662f\u5929\u9ed1\uff0c\u5927\u5bb6\u65e9\u5c31\u5f80\u6765\u8def\u75be\u5954\u4e86\u3002\u6b21\u65e5\u5929\u8272\u5fae\u660e\uff0c\u4f17\u4eba\u5c31\u4e71\u54c4\u54c4\u7684\u5feb\u6b65\u56de\u53bb\u3002 \u3000\u3000\u674e\u6587\u79c0\u6628\u5929\u5df2\u53bb\u4ed4\u7ec6\u770b\u8fc7\u4e86\u90a3\u5339\u9a6c\u7684\u5c38\u4f53\uff0c\u8fd9\u65f6\u518d\u53bb\u770b\u90a3\u9752\u5e74\u7684\u5c38\u4f53\uff0c\u5fc3\u4e0b\u66f4\u65e0\u6000\u7591\uff0c\u81ea\u8a00\u81ea\u8bed\u7684\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u6076\u9b3c\uff01\u201d \u3000\u3000\u5ffd\u7136\u8eab\u540e\u6709\u4eba\u98a4\u58f0\u9053\uff1a\u201c\u662f\u6076\u9b3c\uff0c\u662f\u6076\u9b3c\uff01\u963f\u79c0\uff0c\u8fd9\u6bd4\u6076\u9b3c\u8fd8\u8981\u53ef\u6015\uff0c\u54b1\u4eec\u5feb\u8d70\u3002\u201d\u539f\u6765\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\uff0c\u8ba1\u8001\u4eba\u5df2\u5230\u4e86\u5979\u7684\u8eab\u540e\u3002 \u3000\u3000\u674e\u6587\u79c0\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u8d70\u5427\uff01\u201d \u3000\u3000\u5ffd\u7136\u95f4\u542c\u5f97\u82cf\u666e\u957f\u58f0\u5927\u53eb\uff1a\u201c\u963f\u66fc\uff0c\u963f\u66fc\uff0c\u4f60\u5728\u54ea\u91cc\uff1f\u201d\u8f66\u5c14\u5e93\u60ca\u9053\uff1a\u201c\u963f\u66fc\u6ca1\u8ddf\u4f60\u5728\u4e00\u8d77\u5417\uff1f\u201d\u4ed6\u4e5f\u7eb5\u58f0\u5927\u53eb\uff1a\u201c\u963f\u66fc\uff0c\u963f\u66fc\uff01\u54b1\u4eec\u56de\u53bb\u5566\u3002\u201d\u6765\u56de\u5954\u8dd1\u5bfb\u627e\u5973\u513f\u3002 \u3000\u3000\u82cf\u666e\u4e00\u9762\u5927\u53eb\u201c\u963f\u66fc\uff01\u201d\u4e00\u9762\u5954\u4e0a\u5c0f\u4e18\uff0c\u56db\u4e0b\ue412\u671b\uff0c\u5ffd\u7136\u671b\u89c1\u897f\u8fb9\u8def\u4e0a\u6709\u4e00\u5757\u82b1\u5934\u5dfe\uff0c\u4f3c\u662f\u963f\u66fc\u4e4b\u7269\uff0c\u6025\u5fd9\u5954\u5c06\u8fc7\u53bb\uff0c\u62fe\u8d77\u4e00\u770b\uff0c\u6b63\u662f\u963f\u66fc\u7684\u5934\u5dfe\u3002\u4ed6\u4e00\u6025\u975e\u540c\u5c0f\u53ef\uff0c\u53eb\u9053\uff1a\u201c\u963f\u66fc\u7ed9\u6076\u9b3c\u6349\u53bb\u4e86\uff01\u201d \u3000\u3000\u8fd9\u65f6\u4f17\u65cf\u4eba\u65e9\u5df2\u8fdc\u53bb\uff0c\u8fde\u9a86\u9a7c\u3001\u6851\u65af\u513f\u3001\u4ee5\u53ca\u53e6\u4e00\u4e2a\u9752\u5e74\u7684\u5c38\u8eab\u90fd\u5df2\u62ac\u53bb\uff0c\u5f53\u5730\u53ea\u5269\u4e0b\u82cf\u9c81\u514b\u3001\u8f66\u5c14\u5e93\u3001\u82cf\u666e\u3001\u674e\u6587\u79c0\u3001\u8ba1\u8001\u4eba\u4e94\u4eba\u3002\u82cf\u9c81\u514b\u7b49\u542c\u5f97\u82cf\u666e\u7684\u60ca\u547c\u4e4b\u58f0\uff0c\u5fd9\u5954\u8fc7\u53bb\u8be2\u95ee\u3002 \u3000\u3000\u82cf\u666e\u62ff\u7740\u90a3\u4e2a\u82b1\u5934\u5dfe\uff0c\u6c14\u6025\u8d25\u574f\u7684\u9053\uff1a\u201c\u8fd9\u662f\u963f\u66fc\u7684\u3002\u5979\u2026\u2026\u5979\u2026\u2026\u5979\u7ed9\u6076\u9b3c\u6349\u53bb\u4e86\u3002\u201d\u674e\u6587\u79c0\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u65f6\u5019\u6349\u53bb\u7684\uff1f\u201d\u82cf\u666e\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u3002\u4e00\u5b9a\u662f\u6628\u665a\u534a\u591c\u91cc\u3002\u5979\u2026\u2026\u5979\u8ddf\u5973\u4f34\u4eec\u7761\u5728\u4e00\u8d77\u7684\uff0c\u4eca\u65e9\u6211\u5c31\u627e\u5979\u4e0d\u5230\u4e86\u3002\u201d\u4ed6\u5446\u4e86\u4e00\u9635\uff0c\u5ffd\u7136\u5411\u7740\u8ff7\u5bab\u7684\u65b9\u5411\u53d1\u8db3\u72c2\u5954\uff0c\u53eb\u9053\uff1a\u201c\u6211\u8981\u53bb\u8ddf\u963f\u66fc\u6b7b\u5728\u4e00\u8d77\u3002\u201d \u3000\u3000\u963f\u66fc\u65e2\u7ed9\u6076\u9b3c\u6349\u53bb\u4e86\uff0c\u4ed6\u81ea\u7136\u6ca1\u672c\u4e8b\u6551\u5979\u56de\u6765\u3002\u4f46\u963f\u66fc\u65e2\u7136\u6b7b\u4e86\uff0c\u4ed6\u4e5f\u4e0d\u60f3\u6d3b\u4e86\u3002 \u3000\u3000\u82cf\u9c81\u514b\u53eb\u9053\uff1a\u201c\u82cf\u666e\uff0c\u82cf\u666e\uff0c\u50bb\u5c0f\u5b50\uff0c\u5feb\u56de\u6765\uff0c\u4f60\u4e0d\u6015\u6b7b\u5417\uff1f\u201d\u89c1\u513f\u5b50\u8d8a\u5954\u8d8a\u8fdc\uff0c\u7231\u5b50\u4e4b\u60c5\u7ec8\u4e8e\u80dc\u8fc7\u4e86\u5bf9\u6076\u9b3c\u7684\u6050\u60e7\uff0c\u4e8e\u662f\u968f\u540e\u8ffd\u53bb\u3002\u8f66\u5c14\u5e93\u4e00\u5446\uff0c\u53eb\u9053\uff1a\u201c\u963f\u66fc\uff0c\u963f\u66fc\uff01\u201d\u4e5f\u8ddf\u4e86\u53bb\u3002 \u3000\u3000\u8ba1\u8001\u4eba\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u963f\u79c0\uff0c\u54b1\u4eec\u56de\u53bb\u5427\u3002\u201d\u674e\u6587\u79c0\u9053\uff1a\u201c\u4e0d\uff0c\u8ba1\u7237\u7237\uff0c\u6211\u5f97\u53bb\u6551\u4ed6\u4eec\u3002\u201d\u8ba1\u8001\u4eba\u9053\uff1a\u201c\u4f60\u6597\u4e0d\u8fc7\u6076\u9b3c\u7684\u3002\u201d \u3000\u3000\u674e\u6587\u79c0\u9053\uff1a\u201c\u4e0d\u662f\u6076\u9b3c\uff0c\u662f\u4eba\u3002\u201d\u8ba1\u8001\u4eba\u5ffd\u7136\u4f38\u51fa\u5de6\u624b\uff0c\u7d27\u7d27\u63e1\u4f4f\u4e86\u674e\u6587\u79c0\u7684\u624b\u81c2\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u963f\u79c0\uff0c\u5c31\u7b97\u662f\u4eba\uff0c\u4ed6\u4e5f\u6bd4\u6076\u9b3c\u8fd8\u8981\u53ef\u6015\u3002\u4f60\u542c\u6211\u8bdd\uff0c\u54b1\u4eec\u56de\u53bb\u5427\uff0c\u8d70\u5f97\u8fdc\u8fdc\u7684\u3002\u54b1\u4eec\u662f\u6c49\u4eba\uff0c\u522b\u5728\u56de\u7586\u4f4f\u4e86\uff0c\u4f60\u548c\u6211\u4e00\u8d77\u56de\u4e2d\u539f\u53bb\u3002\u201d \u3000\u3000\u674e\u6587\u79c0\u773c\u89c1\u82cf\u666e\u7b49\u4e09\u4eba\u8d8a\u5954\u8d8a\u8fdc\uff0c\u5fc3\u4e2d\u7126\u6025\uff0c\u7528\u529b\u4e00\u6323\uff0c\u54ea\u77e5\u8ba1\u8001\u4eba\u867d\u7136\u5e74\u8fc8\uff0c\u624b\u52b2\u7adf\u662f\u5927\u5f97\u5f02\u4e4e\u5bfb\u5e38\uff0c\u63a5\u8fde\u4f7f\u52b2\uff0c\u90fd\u662f\u6ca1\u80fd\u6323\u8131\u3002\u5979\u53eb\u9053\uff1a\u201c\u5feb\u653e\u5f00\u6211\uff01\u82cf\u666e\uff0c\u82cf\u666e\u4f1a\u7ed9\u4ed6\u5bb3\u6b7b\u7684\uff01\u201d \u3000\u3000\u8ba1\u8001\u4eba\u89c1\u5979\u6da8\u7ea2\u4e86\u8138\uff0c\u795e\u60c5\u7d27\u8feb\uff0c\u4e0d\u7531\u5f97\u53f9\u4e86\u53e3\u6c14\uff0c\u653e\u677e\u4e86\u5979\u624b\u81c2\uff0c\u8f7b\u58f0\u9053\uff1a\u201c\u4e3a\u4e86\u8fd9\u4e2a\u54c8\u8428\u514b\u5c11\u5e74\uff0c\u4f60\u4ec0\u4e48\u90fd\u4e0d\u987e\u4e86\uff01\u201d \u3000\u3000\u674e\u6587\u79c0\u624b\u81c2\u4e0a\u4e00\u677e\uff0c\u7acb\u5373\u8f6c\u8eab\u98de\u5954\uff0c\u4e5f\u6ca1\u542c\u89c1\u8ba1\u8001\u4eba\u7684\u8bf4\u8bdd\u3002\u4e00\u53e3\u6c14\u5954\u5230\u8ff7\u5bab\u4e4b\u524d\uff0c\u53ea\u89c1\u82cf\u666e\u624b\u821e\u957f\u5200\uff0c\u6b63\u5728\u5927\u53eb\u5927\u56b7\uff1a\u201c\u8be5\u6b7b\u7684\u6076\u9b3c\uff0c\u4f60\u5bb3\u6b7b\u4e86\u963f\u66fc\uff0c\u8fde\u6211\u4e5f\u4e00\u8d77\u5bb3\u6b7b\u5427\u3002\u963f\u66fc\u6b7b\u4e86\uff0c\u6211\u4e5f\u4e0d\u8981\u6d3b\u4e86\uff01\u6211\u662f\u82cf\u666e\uff0c\u4f60\u51fa\u6765\uff0c\u6211\u8ddf\u4f60\u51b3\u6597\uff01\u4f60\u6015\u4e86\u6211\u5417\uff1f\u201d\u4ed6\u4f38\u624b\u53bb\u8f6c\u95e8\u73af\uff0c\u4f46\u5fc3\u795e\u6df7\u4e71\u4e4b\u4e0b\uff0c\u8f6c\u6765\u8f6c\u53bb\u90fd\u63a8\u4e0d\u5f00\u95e8\u3002 \u3000\u3000\u82cf\u9c81\u514b\u5728\u4e00\u65c1\u53eb\u9053\uff1a\u201c\u82cf\u666e\uff0c\u50bb\u5c0f\u5b50\uff0c\u522b\u8fdb\u53bb\uff01\u201d\u82cf\u666e\u5374\u54ea\u91cc\u80af\u542c\uff1f \u3000\u3000\u674e\u6587\u79c0\u89c1\u5230\u4ed6\u8fd9\u822c\u75f4\u60c5\u7684\u6a21\u6837\uff0c\u5fc3\u4e2d\u53c8\u662f\u4e00\u9178\uff0c\u5927\u58f0\u9053\uff1a\u201c\u963f\u66fc\u6ca1\u6709\u6b7b\uff01\u201d \u3000\u3000\u82cf\u666e\u9661\u7136\u95ee\u542c\u5230\u8fd9\u53e5\u8bdd\uff0c\u8111\u7b4b\u767b\u65f6\u6e05\u9192\u4e86\uff0c\u8f6c\u8eab\u95ee\u9053\uff1a\u201c\u963f\u66fc\u6ca1\u6709\u6b7b\uff1f\u4f60\u600e\u2026\u2026\u600e\u4e48\u77e5\u9053\uff1f\u201d\u674e\u6587\u79c0\u9053\uff1a\u201c\u8ff7\u5bab\u91cc\u7684\u4e0d\u662f\u6076\u9b3c\uff0c\u662f\u4eba\uff01\u201d\u82cf\u666e\u3001\u82cf\u9c81\u514b\u3001\u8f66\u5c14\u5e93\u4e09\u4eba\u9f50\u58f0\u9053\uff1a\u201c\u660e\u660e\u662f\u6076\u9b3c\uff0c\u600e\u4e48\u662f\u4eba\uff1f\u201d \u3000\u3000\u674e\u6587\u79c0\u9053\uff1a\u201c\u8fd9\u662f\u4eba\u626e\u7684\u3002\u4ed6\u7528\u4e00\u79cd\u6781\u5fae\u7ec6\u7684\u5267\u6bd2\u6697\u5668\u5c04\u6b7b\u4e86\u9a6c\u5339\u548c\u4eba\uff0c\u4f24\u75d5\u4e0d\u5bb9\u6613\u770b\u51fa\u6765\u3002\u4ed6\u811a\u4e0b\u8e29\u4e86\u9ad8\u8df7\uff0c\u5916\u9762\u7528\u957f\u888d\u7f69\u4f4f\u4e86\uff0c\u6240\u4ee5\u5728\u6c99\u5730\u4e2d\u884c\u8d70\u6ca1\u6709\u811a\u5370\uff0c\u8eab\u6750\u53c8\u8fd9\u4e48\u9ad8\uff0c\u8d70\u8d77\u6765\u8fd9\u4e48\u5feb\u3002\u201d\u5979\u53e6\u5916\u6709\u4e24\u53e5\u8bdd\u5374\u6ca1\u6709\u8bf4\uff1a\u201c\u6211\u77e5\u9053\u8fd9\u4eba\u662f\u8c01\uff0c\u56e0\u4e3a\u6211\u8ba4\u5f97\u4ed6\u653e\u6697\u5668\u7684\u624b\u6cd5\u3002\u5728\u6b7b\u9a6c\u548c\u90a3\u9752\u5e74\u7684\u5c38\u4f53\u4e0a\uff0c\u6211\u4e5f\u5df2\u627e\u5230\u4e86\u6697\u5668\u7684\u4f24\u75d5\u3002\u201d \u3000\u3000\u8fd9\u4e9b\u89e3\u91ca\u5408\u60c5\u5408\u7406\uff0c\u53ef\u662f\u82cf\u9c81\u514b\u7b49\u4e00\u65f6\u5374\u4e5f\u96be\u4ee5\u76f8\u4fe1\u3002\u8fd9\u65f6\u8ba1\u8001\u4eba\u4e5f\u5df2\u5230\u4e86\uff0c\u4ed6\u7f13\u7f13\u7684\u9053\uff1a\u201c\u6211\u77e5\u9053\u662f\u5389\u5bb3\u7684\u6076\u9b3c\uff0c\u5927\u5bb6\u522b\u8fdb\u8ff7\u5bab\uff0c\u514d\u5f97\u9001\u4e86\u6027\u547d\u3002\u6211\u662f\u8001\u4eba\uff0c\u8bf4\u8bdd\u4e00\u5b9a\u4e0d\u9519\u7684\u3002\u201d \u3000\u3000\u82cf\u666e\u9053\uff1a\u201c\u662f\u6076\u9b3c\u4e5f\u7f62\u3001\u662f\u4eba\u4e5f\u7f62\uff0c\u6211\u603b\u662f\u8981\u53bb\u2026\u2026\u8981\u53bb\u6551\u963f\u66fc\u3002\u201d\u4ed6\u76fc\u671b\u8fd9\u6076\u9b3c\u679c\u771f\u5982\u674e\u6587\u79c0\u6240\u8bf4\u662f\u4eba\u626e\u7684\uff0c\u90a3\u4e48\u4fbf\u6709\u4e86\u642d\u6551\u963f\u66fc\u7684\u6307\u671b\u3002\u4ed6\u53c8\u53bb\u65cb\u8f6c\u95e8\u73af\uff0c\u8fd9\u4e00\u6b21\u5374\u8f6c\u5f00\u4e86\u3002 \u3000\u3000\u674e\u6587\u79c0\u9053\uff1a\u201c\u6211\u8ddf\u4f60\u4e00\u8d77\u53bb\u3002\u201d\u82cf\u666e\u8f6c\u8fc7\u5934\u6765\uff0c\u5fc3\u4e2d\u8bf4\u4e0d\u51fa\u7684\u611f\u6fc0\uff0c\u8bf4\u9053\uff1a\u201c\u674e\u82f1\u96c4\uff0c\u4f60\u522b\u8fdb\u53bb\u4e86\uff0c\u5f88\u5371\u9669\u7684\u3002\u201d\u674e\u6587\u79c0\u9053\uff1a\u201c\u4e0d\u8981\u7d27\uff0c\u6211\u966a\u7740\u4f60\uff0c\u5c31\u4e0d\u4f1a\u5371\u9669\u3002\u201d\u82cf\u666e\u70ed\u6cea\u76c8\u7736\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u591a\u8c22\uff0c\u8c22\u8c22\u4f60\u3002\u201d\u674e\u6587\u79c0\u5fc3\u60f3\uff1a\u201c\u4f60\u8fd9\u6837\u611f\u6fc0\u6211\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e3a\u4e86\u963f\u66fc\u3002\u201d\u8f6c\u5934\u5bf9\u8ba1\u8001\u4eba\u9053\uff1a\u201c\u8ba1\u7237\u7237\uff0c\u4f60\u5728\u8fd9\u91cc\u7b49\u6211\u3002\u201d \u3000\u3000\u8ba1\u8001\u4eba\u9053\uff1a\u201c\u4e0d\uff01\u6211\u8ddf\u4f60\u4e00\u8d77\u8fdb\u53bb\uff0c\u90a3\u2026\u2026\u90a3\u4eba\u5f88\u51f6\u6076\u7684\u3002\u201d\u674e\u6587\u79c0\u9053\uff1a\u201c\u4f60\u5e74\u7eaa\u8fd9\u4e48\u5927\u4e86\uff0c\u53c8\u4e0d\u4f1a\u6b66\u529f\uff0c\u5728\u5916\u9762\u7b49\u7740\u6211\u597d\u4e86\u3002 \u3000\u3000\u6211\u4e0d\u4f1a\u6709\u5371\u9669\u7684\u3002\u201d\u8ba1\u8001\u4eba\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u9053\uff0c\u975e\u5e38\u975e\u5e38\u5371\u9669\u7684\u3002\u6211\u8981\u7167\u987e\u4f60\u3002\u201d \u3000\u3000\u674e\u6587\u79c0\u62d7\u4e0d\u8fc7\u4ed6\uff0c\u5fc3\u60f3\uff1a\u201c\u4f60\u80fd\u7167\u987e\u6211\u4ec0\u4e48\uff1f\u53cd\u800c\u8981\u6211\u6765\u7167\u987e\u4f60\u624d\u662f\u3002\u201d\u5f53\u4e0b\u4e94\u4e2a\u4eba\u70b9\u8d77\u4e86\u706b\u628a\uff0c\u5faa\u7740\u65e7\u8def\u53c8\u5411\u8ff7\u5bab\u91cc\u8fdb\u53bb\u3002 \u3000\u3000\u4e94\u4eba\u66f2\u66f2\u6298\u6298\u7684\u8d70\u4e86\u826f\u4e45\u3002\u82cf\u666e\u4e00\u8def\u4e0a\u5927\u53eb\uff1a\u201c\u963f\u66fc\uff0c\u963f\u66fc\uff0c\u4f60\u5728\u54ea\u91cc\uff1f\u201d\u59cb\u7ec8\u4e0d\u542c\u89c1\u4ec0\u4e48\u58f0\u97f3\u3002\u674e\u6587\u79c0\u5fc3\u60f3\uff1a\u201c\u8fd8\u662f\u628a\u4ed6\u5413\u8d70\u4e86\u7684\u597d\u3002\u201d\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u4e00\u8d77\u5927\u53eb\uff0c\u8bf4\u5927\u961f\u4eba\u9a6c\u6765\u6551\u4eba\u5566\uff0c\u8bf4\u4e0d\u5b9a\u80fd\u5c06\u90a3\u6076\u4eba\u5413\u8d70\u3002\u201d\u82cf\u9c81\u514b\u3001\u8f66\u5c14\u5e93\u548c\u82cf\u666e\u4f9d\u8ba1\u5927\u53eb\uff1a\u201c\u963f\u66fc\uff0c\u963f\u66fc\uff0c\u4f60\u522b\u6015\uff0c\u54b1\u4eec\u5927\u961f\u4eba\u9a6c\u6765\u6551\u4f60\u5566\u3002\u201d\u8ff7\u5bab\u4e2d\u6bbf\u5802\u7a7a\u5ed3\uff0c\u4e00\u9635\u9635\u56de\u58f0\u56db\u4e0b\u9707\u8361\u3002 \u3000\u3000\u53c8\u8d70\u4e86\u4e00\u9635\uff0c\u5ffd\u542c\u5f97\u4e00\u4e2a\u5973\u5b50\u5c16\u58f0\u5927\u53eb\uff0c\u4f9d\u7a00\u6b63\u662f\u963f\u66fc\u3002 \u3000\u3000\u82cf\u666e\u5faa\u58f0\u5954\u53bb\uff0c\u63a8\u5f00\u4e00\u6247\u95e8\uff0c\u53ea\u89c1\u963f\u66fc\u7f29\u5728\u5c4b\u89d2\u4e4b\u4e2d\uff0c\u53cc\u624b\u88ab\u53cd\u7ed1\u5728\u80cc\u540e\u3002\u4e24\u4eba\u60ca\u559c\u4ea4\u96c6\uff0c\u9f50\u58f0\u53eb\u4e86\u51fa\u6765\u3002 \u3000\u3000\u82cf\u666e\u62a2\u4e0a\u53bb\u677e\u5f00\u4e86\u5979\u7684\u7ed1\u7f1a\uff0c\u95ee\uff1a\u201c\u90a3\u6076\u9b54\u5462\uff1f\u201d\u963f\u66fc\u9053\uff1a\u201c\u4ed6\u4e0d\u662f\u9b3c\uff0c\u662f\u4eba\u3002\u521a\u624d\u4ed6\u8fd8\u5728\u8fd9\u91cc\uff0c\u6240\u5230\u4f60\u4eec\u7684\u58f0\u97f3\uff0c\u4fbf\u60f3\u62b1\u4e86\u6211\u9003\u8d70\uff0c\u6211\u62da\u547d\u6323\u624e\uff0c\u4ed6\u542c\u5f97\u4f60\u4eec\u4eba\u591a\uff0c\u5c31\u5306\u5306\u5fd9\u5fd9\u7684\u9003\u8d70\u4e86\u3002\u201d \u3000\u3000\u82cf\u666e\u8212\u4e86\u53e3\u6c14\uff0c\u53c8\u95ee\uff1a\u201c\u90a3\u2026\u2026\u90a3\u662f\u600e\u4e48\u6837\u4e00\u4e2a\u4eba\uff1f\u4ed6\u600e\u4e48\u4f1a\u5c06\u4f60\u6349\u4e86\u6765\uff1f\u201d\u963f\u66fc\u9053\uff1a\u201c\u4e00\u8def\u4e0a\u4ed6\u7ed1\u4f4f\u4e86\u6211\u773c\u775b\uff0c\u5230\u4e86\u8ff7\u5bab\uff0c\u9ed1\u6c89\u6c89\u7684\uff0c\u59cb\u7ec8\u6ca1\u80fd\u89c1\u5230\u4ed6\u7684\u76f8\u8c8c\u3002\u201d\u82cf\u666e\u8f6c\u5934\u77a7\u7740\u674e\u6587\u79c0\uff0c\u773c\u5149\u4e2d\u5e26\u6709\u611f\u6fc0\u4e4b\u60c5\u3002 \u3000\u3000\u963f\u66fc\u8f6c\u5411\u8f66\u5c14\u5e93\uff0c\u8bf4\u9053\uff1a\u201c\u7239\uff0c\u8fd9\u4eba\u8bf4\u4ed6\u540d\u53eb\u74e6\u8033\u62c9\u9f50\uff0c\u4f60\u8ba4\u2026\u2026\u201d\u4ed6\u4e00\u8a00\u672a\u6bd5\uff0c\u8f66\u5c14\u5e93\u548c\u82cf\u9c81\u514b\u9f50\u58f0\u53eb\u4e86\u51fa\u6765\uff1a\u201c\u74e6\u8033\u62c9\u9f50\uff01\u201d\u8fd9\u4e24\u4eba\u4e00\u58f0\u53eb\u5524\uff0c\u542b\u610f\u975e\u5e38\u660e\u767d\uff0c\u4ed6\u4eec\u4e0d\u4f46\u77e5\u9053\u74e6\u8033\u62c9\u9f50\uff0c\u800c\u4e14\u8fd8\u5bf9\u4ed6\u5341\u5206\u719f\u6089\u3002 \u3000\u3000\u8f66\u5c14\u5e93\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u74e6\u8033\u62c9\u9f50\uff1f\u51b3\u8ba1\u4e0d\u4f1a\u7684\u3002\u4ed6\u81ea\u5df1\u8bf4\u53eb\u505a\u74e6\u8033\u62c9\u9f50\uff1f\u4f60\u6ca1\u542c\u9519\uff1f\u201d \u3000\u3000\u963f\u66fc\u9053\uff1a\u201c\u4ed6\u8bf4\u4ed6\u8ba4\u5f97\u6211\u5988\u3002\u201d \u3000\u3000\u82cf\u9c81\u514b\u9053\uff1a\u201c\u90a3\u5c31\u662f\u4e86\uff0c\u662f\u771f\u7684\u74e6\u8033\u62c9\u9f50\u3002\u201d\u8f66\u5c14\u5e93\u5583\u5583\u7684\u9053\uff1a\u201c\u4ed6\u8ba4\u5f97\u4f60\u5988\uff1f\u662f\u74e6\u8033\u62c9\u9f50\uff1f\u600e\u2026\u2026\u600e\u4e48\u4f1a\u53d8\u6210\u4e86\u8ff7\u5bab\u91cc\u7684\u6076\u9b3c\uff1f\u201d\u963f\u66fc\u9053\uff1a\u201c\u4ed6\u4e0d\u662f\u9b3c\uff0c\u662f\u4eba\u3002\u4ed6\u8bf4\u4ed6\u4ece\u5c0f\u5c31\u559c\u6b22\u6211\u5988\uff0c\u53ef\u662f\u6211\u5988\u4e0d\u751f\u773c\u73e0\u5b50\uff0c\u5ac1\u4e86\u6211\u7239\u7239\u8fd9\u4e2a\u5927\u6df7\u86cb\u2026\u2026\u554a\u54df\uff0c\u7239\uff0c\u4f60\u522b\u751f\u6c14\uff0c\u662f\u8fd9\u574f\u4eba\u8bf4\u7684\u3002\u201d\u82cf\u9c81\u514b\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u74e6\u8033\u62c9\u9f50\u662f\u574f\u4eba\uff0c\u53ef\u662f\u8fd9\u53e5\u8bdd\u5012\u6ca1\u8bf4\u9519\uff0c\u4f60\u7239\u679c\u7136\u662f\u4e2a\u5927\u6df7\u2026\u2026\u201d\u8f66\u5c14\u5e93\u4e00\u62f3\u6253\u53bb\u3002\u82cf\u9c81\u514b\u4e00\u7b11\u907f\u5f00\uff0c\u53c8\u9053\uff1a\u201c\u74e6\u8033\u62c9\u9f50\u4ece\u524d\u8ddf\u4f60\u7239\u7239\u4e89\u4f60\u5988\uff0c\u74e6\u8033\u62c9\u9f50\u8f93\u4e86\u3002\u8fd9\u4eba\u4e0d\u662f\u597d\u6c49\u5b50\uff0c\u534a\u591c\u91cc\u62ff\u4e86\u5200\u5b50\u53bb\u6740\u4f60\u7239\u7239\u3002\u4f60\u77a7\uff0c\u4ed6\u8033\u6735\u8fb9\u8fd9\u4e2a\u5200\u75a4\uff0c\u5c31\u662f\u7ed9\u74e6\u8033\u62c9\u9f50\u780d\u7684\u3002\u201d\u4f17\u4eba\u4e00\u9f50\u671b\u5411\u8f66\u5c14\u5e93\uff0c\u679c\u89c1\u4ed6\u5de6\u8033\u8fb9\u6709\u4e2a\u957f\u957f\u7684\u5200\u75a4\u3002\u8fd9\u75a4\u75d5\u5927\u5bb6\u4ee5\u524d\u65e9\u5c31\u89c1\u5230\u4e86\uff0c\u4e0d\u8fc7\u4e0d\u77e5\u5176\u6765\u5386\u800c\u5df2\u3002 \u3000\u3000\u963f\u66fc\u62c9\u7740\u7236\u4eb2\u7684\u624b\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u7239\uff0c\u90a3\u65f6\u4f60\u4f24\u5f97\u5f88\u5389\u5bb3\u4e48\uff1f\u201d \u3000\u3000\u8f66\u5c14\u5e93\u9053\uff1a\u201c\u4f60\u7239\u867d\u7136\u4e2d\u4e86\u4ed6\u7684\u6697\u7b97\uff0c\u4f46\u8fd8\u662f\u6253\u5012\u4e86\u4ed6\uff0c\u628a\u4ed6\u6380\u5728\u5730\u4e0b\uff0c\u7ed1\u4e86\u8d77\u6765\u3002\u201d\u8bf4\u8fd9\u51e0\u53e5\u8bdd\u65f6\uff0c\u8bed\u6c14\u4e2d\u9887\u6709\u81ea\u8c6a\u4e4b\u610f\uff0c\u53c8\u9053\uff1a\u201c\u7b2c\u4e8c\u5929\u65cf\u957f\u805a\u96c6\u65cf\u4eba\uff0c\u5ba3\u5e03\u5c06\u8fd9\u574f\u86cb\u9010\u51fa\u672c\u65cf\uff0c\u6c38\u8fdc\u4e0d\u8bb8\u56de\u6765\uff0c\u5018\u82e5\u5077\u5077\u56de\u6765\uff0c\u4fbf\u5373\u5904\u6b7b\u3002\u8fd9\u4e9b\u5e74\u6765\u4e00\u76f4\u5c31\u6ca1\u89c1\u4ed6\u3002\u8fd9\u5bb6\u4f19\u8eb2\u5728\u8fd9\u8ff7\u5bab\u91cc\u5e72\u4ec0\u4e48\uff1f\u4f60\u600e\u4e48\u4f1a\u7ed9\u4ed6\u6349\u53bb\u7684\uff1f\u201d \u3000\u3000\u963f\u66fc\u9053\uff1a\u201c\u4eca\u671d\u5929\u5feb\u4eae\u65f6\uff0c\u6211\u8d77\u6765\u5230\u6811\u6797\u4e2d\u89e3\u624b\uff0c\u54ea\u77e5\u9053\u8fd9\u574f\u4eba\u8eb2\u5728\u540e\u9762\uff0c\u7a81\u7136\u6251\u4e86\u8fc7\u6765\uff0c\u6309\u4f4f\u6211\u5634\u5df4\uff0c\u4e00\u76f4\u62b1\u7740\u6211\u5230\u4e86\u8fd9\u91cc\u3002\u4ed6\u8bf4\u4ed6\u5f97\u4e0d\u5230\u6211\u5988\uff0c\u5c31\u8981\u6211\u6765\u4ee3\u66ff\u6211\u5988\u3002\u6211\u6c42\u4ed6\u653e\u6211\u56de\u53bb\uff0c\u6211\u8bf4\u6211\u5988\u4e0d\u559c\u6b22\u4ed6\uff0c\u6211\u4e5f\u51b3\u8ba1\u4e0d\u4f1a\u559c\u6b22\u4ed6\u7684\u3002\u4ed6\u8bf4\uff1a\u2018\u4f60\u559c\u6b22\u4e5f\u597d\uff0c\u4e0d\u559c\u6b22\u4e5f\u597d\uff0c\u603b\u4e4b\u4f60\u662f\u6211\u7684\u4eba\u4e86\u3002\u90a3\u4e9b\u54c8\u8428\u514b\u80c6\u5c0f\u9b3c\uff0c\u6ca1\u4e00\u4e2a\u6562\u8fdb\u8ff7\u5bab\u6765\u6551\u4f60\u7684\u3002\u2019\u4ed6\u7684\u8bdd\u4e0d\u5bf9\uff0c\u7239\uff0c\u82cf\u9c81\u514b\u4f2f\u4f2f\uff0c\u4f60\u4eec\u90fd\u662f\u82f1\u96c4\uff0c\u8fd8\u6709\u674e\u82f1\u96c4\uff0c\u82cf\u666e\uff0c\u8ba1\u7237\u7237\u4e5f\u6765\u4e86\uff0c\u5e78\u4e8f\u4f60\u4eec\u6765\u6551\u6211\u3002\u201d\u8f66\u5c14\u5e93\u6068\u6068\u7684\u9053\uff1a\u201c\u4ed6\u5bb3\u6b7b\u4e86\u9a86\u9a7c\uff0c\u6851\u65af\u513f\uff0c\u54b1\u4eec\u5feb\u8ffd\uff0c\u6349\u5230\u4ed6\u6765\u5904\u6b7b\u3002\u201d \u3000\u3000\u674e\u6587\u79c0\u672c\u5df2\u6599\u5230\u8fd9\u5047\u626e\u6076\u9b3c\u4e4b\u4eba\u662f\u8c01\uff0c\u54ea\u77e5\u9053\u81ea\u5df1\u7684\u731c\u60f3\u7adf\u5b8c\u5168\u9519\u4e86\uff0c\u4e0d\u7981\u6697\u6697\u60ed\u6127\uff0c\u5b9e\u4e0d\u8be5\u51a4\u6789\u4e86\u597d\u4eba\uff0c\u5e78\u597d\u5fc3\u91cc\u7684\u8bdd\u6ca1\u8bf4\u51fa\u53e3\u6765\uff0c\u53c8\u60f3\uff1a\u201c\u600e\u4e48\u8fd9\u4e2a\u54c8\u8428\u514b\u4eba\u4e5f\u4f1a\u53d1\u6bd2\u9488\uff1f\u53d1\u9488\u7684\u624b\u6cd5\u53c8\u4e00\u6a21\u4e00\u6837\uff1f\u96be\u9053\u4ed6\u4e5f\u662f\u8ddf\u6211\u5e08\u7236\u5b66\u7684\uff1f\u201d \u3000\u3000\u82cf\u9c81\u514b\u7b49\u65e2\u77e5\u6076\u9b3c\u662f\u74e6\u8033\u62c9\u9f50\u5047\u626e\uff0c\u54ea\u91cc\u8fd8\u6709\u4ec0\u4e48\u60e7\u6015\uff1f \u3000\u3000\u4f55\u51b5\u7d20\u77e5\u8fd9\u4eba\u6b66\u529f\u5e73\u5e73\uff0c\u4e00\u89c1\u9762\uff0c\u8fd8\u4e0d\u624b\u5230\u64d2\u6765\uff1f\u8f66\u5c14\u5e93\u4e3a\u4e86\u8981\u62a5\u6740\u5f92\u4e4b\u4ec7\uff0c\u9ad8\u4e3e\u706b\u628a\uff0c\u5f53\u5148\u800c\u884c\u3002 \u3000\u3000\u8ba1\u8001\u4eba\u4e00\u62c9\u674e\u6587\u79c0\u7684\u8863\u8896\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8fd9\u662f\u4ed6\u4eec\u54c8\u8428\u514b\u4eba\u81ea\u5df1\u65cf\u91cc\u7684\u4e8b\uff0c\u54b1\u4eec\u4e0d\u7528\u7406\u4f1a\uff0c\u5728\u5916\u9762\u7b49\u7740\u4ed6\u4eec\u5427\u3002\u201d\u674e\u6587\u79c0\u542c\u4ed6\u8bed\u97f3\u53d1\u98a4\uff0c\u663e\u662f\u5bb3\u6015\u4e4b\u6781\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u8ba1\u7237\u7237\uff0c\u4f60\u5750\u5728\u90a3\u8fb9\u5929\u4e95\u91cc\u7b49\u6211\uff0c\u597d\u4e0d\u597d\uff1f\u90a3\u4e2a\u54c8\u8428\u514b\u574f\u4eba\u6b66\u529f\u5f88\u5f3a\u7684\uff0c\u53ea\u6015\u82cf\u2026\u2026\u82cf\u9c81\u514b\u4ed6\u4eec\u6253\u4e0d\u8fc7\uff0c\u6211\u5f97\u5e2e\u7740\u4ed6\u4eec\u3002\u201d\u8ba1\u8001\u4eba\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u90a3\u4e48\u6211\u4e5f\u4e00\u8d77\u53bb\u3002\u201d\u674e\u6587\u79c0\u5411\u4ed6\u6e29\u67d4\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u5feb\u5b8c\u7ed3\u4e86\uff0c\u4f60\u4e0d\u7528\u62c5\u5fc3\u3002\u201d\u8ba1\u8001\u4eba\u548c\u5979\u5e76\u80a9\u800c\u884c\uff0c\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u5feb\u5b8c\u7ed3\u4e86\uff0c\u5b8c\u7ed3\u4e4b\u540e\uff0c\u6211\u8981\u56de\u4e2d\u539f\u53bb\u4e86\u3002\u963f\u79c0\uff0c\u4f60\u548c\u6211\u4e00\u8d77\u56de\u53bb\u5417\uff1f\u201d \u3000\u3000\u674e\u6587\u79c0\u5fc3\u91cc\u4e00\u9635\u96be\u8fc7\uff0c\u4e2d\u539f\u6545\u4e61\u7684\u60c5\u5f62\uff0c\u5728\u5979\u5fc3\u91cc\u65e9\u4e0d\u8fc7\u662f\u4e00\u7247\u6a21\u7cca\u7684\u5f71\u5b50\uff0c\u5979\u5728\u8fd9\u5927\u8349\u539f\u4e0a\u4f4f\u4e86\u5341\u4e8c\u5e74\uff0c\u53ea\u7231\u8fd9\u91cc\u7684\u70c8\u98ce\u3001\u5927\u96ea\u3001\u9ec4\u6c99\u3001\u65e0\u8fb9\u65e0\u9645\u7684\u5e73\u91ce\u3001\u725b\u7f8a\uff0c\u534a\u591c\u91cc\u5929\u94c3\u9e1f\u7684\u6b4c\u58f0\u2026\u2026 \u3000\u3000\u8ba1\u8001\u4eba\u89c1\u5979\u4e0d\u7b54\uff0c\u53c8\u9053\uff1a\u201c\u6211\u4eec\u6c49\u4eba\u5728\u4e2d\u539f\uff0c\u53ef\u6bd4\u8fd9\u91cc\u597d\u5f97\u591a\u4e86\uff0c\u7a7f\u5f97\u597d\uff0c\u5403\u5f97\u597d\u3002\u4f60\u8ba1\u7237\u7237\u5df2\u79ef\u4e86\u4e9b\u94b1\uff0c\u56de\u53bb\u54b1\u4eec\u53ef\u4ee5\u8212\u8212\u670d\u670d\u7684\u3002\u4e2d\u539f\u7684\u82b1\u82b1\u4e16\u754c\uff0c\u6bd4\u8fd9\u91cc\u7e41\u534e\u767e\u500d\uff0c\u90a3\u624d\u662f\u4eba\u8fc7\u7684\u65e5\u5b50\u3002\u201d\u674e\u6587\u79c0\u9053\uff1a\u201c\u4e2d\u539f\u8fd9\u4e48\u597d\uff0c\u4f60\u600e\u4e48\u4e00\u76f4\u4e0d\u56de\u53bb\uff1f\u201d \u3000\u3000\u8ba1\u8001\u4eba\u4e00\u6014\uff0c\u8d70\u4e86\u51e0\u6b65\uff0c\u624d\u7f13\u7f13\u7684\u9053\uff1a\u201c\u6211\u5728\u4e2d\u539f\u6709\u4e2a\u4ec7\u5bb6\u5bf9\u5934\uff0c\u6211\u5230\u56de\u7586\u6765\uff0c\u662f\u4e3a\u4e86\u907f\u7978\u3002\u9694\u4e86\u8fd9\u4e48\u591a\u5e74\uff0c\u90a3\u4ec7\u5bb6\u4e00\u5b9a\u6b7b\u4e86\u3002\u963f\u79c0\uff0c\u54b1\u4eec\u5728\u5916\u9762\u7b49\u4ed6\u4eec\u5427\u3002\u201d\u674e\u6587\u79c0\u9053\uff1a\u201c\u4e0d\uff0c\u8ba1\u7237\u7237\uff0c\u54b1\u4eec\u5f97\u8d70\u5feb\u4e9b\uff0c\u522b\u79bb\u5f97\u4ed6\u4eec\u592a\u8fdc\u3002\u201d\u8ba1\u8001\u4eba\u201c\u55ef\u3001\u55ef\u201d \u3000\u3000\u8fde\u58f0\uff0c\u811a\u4e0b\u5374\u4e1d\u6beb\u6ca1\u6709\u52a0\u5feb\u3002\u674e\u6587\u79c0\u89c1\u4ed6\u5e74\u8fc8\uff0c\u4e0d\u5fcd\u50ac\u4fc3\u3002 \u3000\u3000\u8ba1\u8001\u4eba\u9053\uff1a\u201c\u56de\u5230\u4e86\u4e2d\u539f\uff0c\u54b1\u4eec\u53bb\u6c5f\u5357\u4f4f\u3002\u54b1\u4eec\u4e70\u4e00\u5ea7\u5e84\u5b50\uff0c\u56db\u5468\u79cd\u6ee1\u4e86\u6768\u67f3\u6843\u82b1\uff0c\u4e00\u682a\u95f4\u7740\u4e00\u682a\uff0c\u4e00\u5230\u6625\u5929\uff0c\u7ea2\u7684\u6843\u82b1\uff0c\u7eff\u7684\u6768\u67f3\uff0c\u9ed1\u8272\u7684\u71d5\u5b50\u5728\u67f3\u679d\u5e95\u4e0b\u7a7f\u6765\u7a7f\u53bb\u3002\u963f\u79c0\uff0c\u54b1\u4eec\u518d\u8d77\u4e00\u4e2a\u5927\u9c7c\u6c60\uff0c\u517b\u6ee1\u4e86\u91d1\u9c7c\uff0c\u91d1\u8272\u7684\u3001\u7ea2\u8272\u7684\u3001\u767d\u8272\u7684\u3001\u9ec4\u8272\u7684\uff0c\u4f60\u4e00\u5b9a\u4f1a\u975e\u5e38\u5f00\u5fc3\u2026\u2026\u53ef\u6bd4\u8fd9\u513f\u597d\u5f97\u591a\u4e86\u2026\u2026\u201d \u3000\u3000\u674e\u6587\u79c0\u7f13\u7f13\u6447\u4e86\u6447\u5934\uff0c\u5fc3\u91cc\u5728\u8bf4\uff1a\u201c\u4e0d\u7ba1\u6c5f\u5357\u591a\u4e48\u597d\uff0c\u6211\u8fd8\u662f\u559c\u6b22\u4f4f\u5728\u8fd9\u91cc\uff0c\u53ef\u662f\u2026\u2026\u8fd9\u4ef6\u4e8b\u5c31\u8981\u5b8c\u7ed3\u4e86\uff0c\u82cf\u666e\u5c31\u4f1a\u548c\u963f\u66fc\u7ed3\u5a5a\uff0c\u90a3\u65f6\u5019\u4ed6\u4eec\u4f1a\u6709\u76db\u5927\u7684\u53fc\u7f8a\u5927\u4f1a\u3001\u6454\u4ea4\u6bd4\u8d5b\u3001\u706b\u5806\u65c1\u7684\u6b4c\u821e\u2026\u2026\u201d\u5979\u62ac\u8d77\u5934\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u7684\uff0c\u8ba1\u7237\u7237\uff0c\u54b1\u4eec\u56de\u5bb6\u4e4b\u540e\uff0c\u7b2c\u4e8c\u5929\u5c31\u52a8\u8eab\u56de\u4e2d\u539f\u53bb\u3002\u201d\u8ba1\u8001\u4eba\u773c\u4e2d\u7a81\u7136\u95ea\u51fa\u4e86\u5149\u8f89\uff0c\u90a3\u662f\u559c\u60a6\u65e0\u6bd4\u7684\u5149\u8292\uff0c\u5927\u58f0\u9053\uff1a\u201c\u597d\u6781\u4e86\uff01\u54b1\u4eec\u56de\u5bb6\u4e4b\u540e\uff0c\u7b2c\u4e8c\u5929\u5c31\u52a8\u8eab\u56de\u4e2d\u539f\u53bb\u3002\u201d \u3000\u3000\u5ffd\u7136\u4e4b\u95f4\uff0c\u674e\u6587\u79c0\u6709\u4e9b\u53ef\u601c\u90a3\u4e2a\u74e6\u8033\u62c9\u9f50\u8d77\u6765\u3002\u4ed6\u5f97\u4e0d\u5230\u81ea\u5df1\u5fc3\u7231\u7684\u4eba\uff0c\u53c8\u7ed9\u9010\u51fa\u4e86\u672c\u65cf\uff0c\u4e00\u76f4\u5b64\u96f6\u96f6\u7684\u4f4f\u5728\u8fd9\u8ff7\u5bab\u91cc\u3002\u963f\u66fc\u662f\u5341\u516b\u5c81\uff0c\u4ed6\u5728\u8fd9\u8ff7\u5bab\u91cc\u5df2\u4f4f\u4e86\u4e8c\u5341\u5e74\u5427\uff1f\u6216\u8bb8\u8fd8\u66f4\u957f\u4e45\u4e9b\u3002 \u3000"}),showOnHomepage:!0},{name:"\u4e5d",content:(0,a.jsx)(a.Fragment,{children:"\u74e6\u8033\u62c9\u9f50\uff01\u7ad9\u4f4f\uff01\u201d \u3000\u3000\u7a81\u7136\u524d\u9762\u4f20\u6765\u4e86\u8f66\u5c14\u5e93\u7684\u6012\u559d\u3002\u674e\u6587\u79c0\u987e\u4e0d\u5f97\u518d\u7b49\u8ba1\u8001\u4eba\uff0c\u6025\u6b65\u5faa\u58f0\u5954\u53bb\u3002 \u3000\u3000\u8d70\u5230\u4e00\u5ea7\u5927\u6bbf\u95e8\u53e3\uff0c\u53ea\u89c1\u6bbf\u5802\u4e4b\u4e2d\uff0c\u4e00\u4eba\u7a9c\u9ad8\u4f0f\u4f4e\uff0c\u6b63\u5728\u548c\u624b\u821e\u957f\u5200\u7684\u8f66\u5c14\u5e93\u6076\u6597\u3002\u90a3\u4eba\u7a7a\u7740\u53cc\u624b\uff0c\u8eab\u62ab\u767d\u8272\u957f\u888d\uff0c\u5934\u4e0a\u5957\u7740\u767d\u5e03\u7f69\u5b50\uff0c\u53ea\u9732\u51fa\u4e86\u4e24\u4e2a\u773c\u5b54\uff0c\u5934\u7f69\u548c\u957f\u888d\u4e0a\u90fd\u67d3\u6ee1\u4e86\u8840\u6e0d\uff0c\u6b63\u662f\u524d\u4e24\u665a\u5047\u626e\u6076\u9b3c\u90a3\u4eba\u7684\u8863\u670d\uff0c\u81ea\u4fbf\u662f\u63b3\u52ab\u963f\u66fc\u7684\u74e6\u8033\u62c9\u9f50\u4e86\uff0c\u53ea\u662f\u8fd9\u65f6\u5019\u4ed6\u811a\u4e0b\u4e0d\u8e29\u9ad8\u8df7\uff0c\u957f\u888d\u7684\u4e0b\u6446\u4fbf\u7ffb\u4e86\u4e0a\u6765\u7f20\u5728\u8170\u95f4\u3002 \u3000\u3000\u82cf\u9c81\u514b\u3001\u82cf\u666e\u7236\u5b50\u89c1\u8f66\u5c14\u5e93\u624b\u4e2d\u6709\u5200\u800c\u5bf9\u65b9\u53ea\u662f\u7a7a\u624b\uff0c\u6599\u60f3\u5fc5\u80dc\uff0c\u4fbf\u4e0d\u4e0a\u524d\u76f8\u52a9\uff0c\u4e24\u4eba\u9ad8\u4e3e\u706b\u628a\uff0c\u53e3\u4e2d\u5406\u559d\u7740\u52a9\u5a01\u3002 \u3000\u3000\u674e\u6587\u79c0\u53ea\u770b\u5f97\u6570\u62db\uff0c\u4fbf\u77e5\u4e0d\u5999\uff0c\u53eb\u9053\uff1a\u201c\u5c0f\u5fc3\uff01\u201d\u6b63\u6b32\u51fa\u624b\uff0c\u53ea\u542c\u5f97\u7830\u7684\u4e00\u58f0\uff0c\u8f66\u5c14\u5e93\u53f3\u80f8\u5df2\u4e2d\u4e86\u4e00\u638c\uff0c\u53e3\u55b7\u9c9c\u8840\uff0c\u76f4\u6454\u51fa\u6765\u3002\u82cf\u9c81\u514b\u7236\u5b50\u5927\u60ca\uff0c\u4e00\u9f50\u629b\u53bb\u624b\u4e2d\u706b\u628a\uff0c\u633a\u5200\u4e0a\u524d\uff0c\u5408\u653b\u654c\u4eba\u3002\u4e24\u6839\u706b\u628a\u6389\u5728\u5730\u4e0b\u5140\u81ea\u71c3\u70e7\uff0c\u6bbf\u4e2d\u5374\u5df2\u9ed1\u6c89\u6c89\u7684\u4ec5\u53ef\u8fa8\u7269\u3002 \u3000\u3000\u674e\u6587\u79c0\u63d0\u7740\u6d41\u661f\u9524\uff0c\u53eb\u9053\uff1a\u201c\u82cf\u666e\uff0c\u9000\u5f00\uff01\u82cf\u9c81\u514b\u4f2f\u4f2f\uff0c\u9000\u5f00\uff0c\u6211\u6765\u6597\u4ed6\u3002\u201d\u82cf\u9c81\u514b\u6012\u9053\uff1a\u201c\u4f60\u9000\u5f00\uff0c\u522b\u5927\u547c\u5c0f\u53eb\u7684\u3002\u201d \u3000\u3000\u4e00\u67c4\u957f\u5200\u4f7f\u5c06\u5f00\u6765\uff0c\u547c\u547c\u751f\u98ce\u3002\u4ed6\u54c8\u8428\u514b\u7684\u5200\u6cd5\u53e6\u6210\u4e00\u8def\uff0c\u5374\u4e5f\u662f\u521a\u731b\u72e0\u8fa3\u3002\u53ea\u662f\u74e6\u8033\u62c9\u9f50\u8eab\u624b\u7075\u6d3b\u4e4b\u6781\uff0c\u84e6\u5730\u91cc\u98de\u51fa\u4e00\u817f\uff0c\u5c06\u82cf\u666e\u624b\u4e2d\u7684\u957f\u5200\u8e22\u98de\u4e86\u3002 \u3000\u3000\u674e\u6587\u79c0\u5fd9\u5c06\u6d41\u661f\u9524\u5f80\u5730\u4e0b\u4e00\u63b7\uff0c\u7eb5\u8eab\u800c\u4e0a\uff0c\u63a5\u4f4f\u534a\u7a7a\u4e2d\u843d\u4e0b\u7684\u957f\u5200\uff0c\u5237\u5237\u4e24\u5200\uff0c\u5411\u74e6\u8033\u62c9\u9f50\u780d\u53bb\u3002\u5979\u8ddf\u5e08\u7236\u5b66\u7684\u662f\u62f3\u811a\u548c\u6d41\u661f\u9524\uff0c\u5200\u6cd5\u5e76\u672a\u5b66\u8fc7\uff0c\u53ea\u662f\u6b64\u523b\u56db\u4eba\u7f20\u6597\uff0c\u5979\u9524\u6cd5\u672a\u81fb\u4e00\u6d41\u4e4b\u5883\uff0c\u4e00\u4f7f\u6d41\u661f\u9524\uff0c\u975e\u8bef\u4f24\u4e86\u82cf\u9c81\u514b\u7236\u5b50\u4e0d\u53ef\uff0c\u53ea\u5f97\u5728\u62f3\u811a\u4e2d\u5939\u4e0a\u5200\u780d\uff0c\u51dd\u795e\u63a5\u6218\u3002\u82cf\u9c81\u514b\u5931\u4e86\u5175\u5203\uff0c\u51fa\u62f3\u6325\u51fb\u3002\u74e6\u8033\u62c9\u9f50\u4ee5\u4e00\u654c\u4e09\uff0c\u4ecd\u5360\u4e0a\u98ce\u3002 \u3000\u3000\u6597\u5f97\u5341\u4f59\u5408\uff0c\u74e6\u8033\u62c9\u9f50\u5927\u559d\u4e00\u58f0\uff0c\u5de6\u62f3\u6325\u51fa\uff0c\u6b63\u4e2d\u82cf\u666e\u9f3b\u6881\uff0c\u8ddf\u7740\u4e00\u817f\uff0c\u8e22\u4e2d\u4e86\u82cf\u9c81\u514b\u7684\u5c0f\u8179\u3002\u82cf\u9c81\u514b\u7236\u5b50\u5148\u540e\u6454\u5012\uff0c\u518d\u4e5f\u722c\u4e0d\u8d77\u6765\u3002\u539f\u6765\u74e6\u8033\u62c9\u9f50\u7684\u62f3\u811a\u4e2d\u5185\u529b\u6df1\u539a\uff0c\u51fb\u4e2d\u540e\u6781\u96be\u62b5\u6321\uff0c\u82cf\u9c81\u514b\u867d\u7136\u608d\u52c7\uff0c\u53c8\u662f\u76ae\u7c97\u8089\u539a\uff0c\u5374\u4e5f\u7ecf\u53d7\u4e0d\u8d77\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u53d8\u6210\u4e86\u674e\u6587\u79c0\u72ec\u6597\u5f3a\u654c\u7684\u5c40\u9762\uff0c\u5de6\u652f\u53f3\u7ecc\uff0c\u767b\u65f6\u4fbf\u843d\u5728\u4e0b\u98ce\u3002\u74e6\u8033\u62c9\u9f50\u559d\u9053\uff1a\u201c\u5feb\u51fa\u53bb\uff0c\u5c31\u9976\u4f60\u7684\u5c0f\u547d\u3002\u201d\u674e\u6587\u79c0\u773c\u89c1\u81ea\u5df1\u82e5\u64a4\u9000\u4e00\u9003\uff0c\u6700\u591a\u662f\u62c9\u4e86\u8ba1\u8001\u4eba\u540c\u8d70\uff0c\u82cf\u666e\u7b49\u4e09\u4eba\u975e\u906d\u6bd2\u624b\u4e0d\u53ef\uff0c\u5f53\u4e0b\u594b\u4e0d\u987e\u8eab\uff0c\u62da\u529b\u62b5\u5fa1\u3002\u74e6\u8033\u62c9\u9f50\u5de6\u624b\u4e00\u626c\uff0c\u674e\u6587\u79c0\u5411\u53f3\u4e00\u95ea\uff0c\u54ea\u77e5\u4ed6\u8fd9\u4e00\u4e0b\u5374\u662f\u865a\u62db\uff0c\u53f3\u638c\u8ddf\u7740\u75be\u5288\u800c\u4e0b\uff0c\u5657\u7684\u4e00\u58f0\uff0c\u6b63\u4e2d\u5979\u5de6\u80a9\u3002\u674e\u6587\u79c0\u4e00\u4e2a\u8e09\u8dc4\uff0c\u9669\u4e9b\u6454\u5012\uff0c\u5fc3\u4e2d\u4fbf\u5982\u7535\u5149\u822c\u95ea\u8fc7\u4e00\u4e2a\u5ff5\u5934\uff1a\u201c\u8fd9\u4e00\u62db\u2018\u58f0\u4e1c\u51fb\u897f\u2019\uff0c\u5e08\u7236\u6559\u8fc7\u6211\u7684\uff0c\u600e\u5730\u5fd8\u4e86\uff1f\u201d\u74e6\u8033\u62c9\u9f50\u559d\u9053\uff1a\u201c\u4f60\u518d\u4e0d\u8d70\uff0c\u6211\u8981\u6740\u4f60\u4e86\uff01\u201d \u3000\u3000\u674e\u6587\u79c0\u5ffd\u7136\u95f4\u8d77\u4e86\u81ea\u66b4\u81ea\u5f03\u7684\u5ff5\u5934\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u6740\u6b7b\u6211\u597d\u4e86\uff01\u201d\u7eb5\u8eab\u53c8\u4e0a\uff0c\u4e0d\u6570\u62db\uff0c\u8170\u95f4\u4e2d\u4e86\u4e00\u62f3\uff0c\u75db\u5f97\u629b\u4e0b\u957f\u5200\u8e72\u4e0b\u8eab\u6765\uff0c\u5fc3\u4e2d\u6b63\u53eb\uff1a\u201c\u6211\u8981\u6b7b\u4e86\uff01\u201d\u5ffd\u7136\u8eab\u65c1\u547c\u7684\u4e00\u58f0\uff0c\u6709\u4eba\u6251\u5411\u74e6\u8033\u62c9\u9f50\u3002 \u3000\u3000\u674e\u6587\u79c0\u5728\u5730\u4e0b\u4e00\u4e2a\u6253\u6eda\uff0c\u56de\u5934\u770b\u65f6\uff0c\u51e0\u4e4e\u4e0d\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u775b\uff0c\u5374\u539f\u6765\u8ba1\u8001\u4eba\u53f3\u624b\u62ff\u7740\u4e00\u67c4\u5315\u9996\uff0c\u5c55\u5f00\u8eab\u6cd5\uff0c\u5df2\u548c\u74e6\u8033\u62c9\u9f50\u6597\u5728\u4e00\u8d77\uff0c\u4f46\u89c1\u8ba1\u8001\u4eba\u8eab\u624b\u77eb\u6377\uff0c\u51fa\u62db\u5982\u98ce\uff0c\u7adf\u662f\u4e1d\u6beb\u6ca1\u6709\u9f99\u949f\u8001\u6001\u3002 \u3000\u3000\u66f4\u5947\u7684\u662f\uff0c\u8ba9\u8001\u4eba\u4e3e\u624b\u51fa\u8db3\uff0c\u62db\u6570\u548c\u74e6\u8033\u62c9\u9f50\u5168\u65e0\u5206\u522b\uff0c\u4e5f\u4fbf\u662f\u5979\u5e08\u7236\u534e\u8f89\u6240\u6388\u7684\u90a3\u4e9b\u6b66\u529f\u3002\u674e\u6587\u79c0\u968f\u5373\u7701\u609f\uff1a\u201c\u662f\u4e86\uff0c\u4e2d\u539f\u7684\u6b66\u529f\u90fd\u662f\u8fd9\u6837\u7684\u3002\u8ba1\u7237\u7237\u548c\u8fd9\u54c8\u8428\u514b\u6076\u4eba\u90fd\u5b66\u8fc7\u4e2d\u539f\u7684\u6b66\u529f\uff0c\u8ba1\u7237\u7237\u539f\u6765\u4f1a\u6b66\u529f\u7684\uff0c\u6211\u53ef\u4e00\u76f4\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u773c\u89c1\u4e8c\u4eba\u8d8a\u6597\u8d8a\u7d27\uff0c\u74e6\u8033\u62c9\u9f50\u5ffd\u7136\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u9a6c\u5bb6\u9a8f\uff0c\u4f60\u597d\uff01\u201d\u8ba1\u8001\u4eba\u8eab\u5b50\u4e00\u98a4\uff0c\u5411\u540e\u9000\u4e86\u4e00\u6b65\uff0c\u74e6\u8033\u62c9\u9f50\u5de6\u624b\u4e00\u626c\uff0c\u4f7f\u7684\u6b63\u662f\u534a\u62db\u201c\u58f0\u4e1c\u51fb\u897f\u201d\uff0c\u8ba1\u8001\u4eba\u5374\u4e0d\u4e0a\u4ed6\u5f53\uff0c\u5315\u9996\u5411\u53f3\u6233\u51fa\uff0c\u54ea\u77e5\u74e6\u8033\u62c9\u9f50\u5374\u4e0d\u4f7f\u5168\u8fd9\u4e0b\u534a\u62db\u201c\u58f0\u4e1c\u51fb\u897f\u201d\uff0c\u5de6\u624b\u75be\u63a0\u800c\u4e0b\uff0c\u4e00\u628a\u6293\u4f4f\u8ba1\u8001\u4eba\u7684\u8138\uff0c\u786c\u751f\u751f\u5c06\u4ed6\u7684\u4e00\u5f20\u9762\u76ae\u63ed\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u674e\u6587\u79c0\u3001\u82cf\u9c81\u514b\u3001\u963f\u66fc\u4e09\u4eba\u9f50\u58f0\u60ca\u547c\u3002\u674e\u6587\u79c0\u66f4\u662f\u9669\u4e9b\u4fbf\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u74e6\u8033\u62c9\u9f50\u8df3\u8d77\u8eab\u6765\uff0c\u5de6\u4e00\u817f\uff0c\u53f3\u4e00\u817f\uff0c\u53cc\u817f\u9e33\u9e2f\u8fde\u73af\uff0c\u90fd\u8e22\u4e2d\u5728\u8ba1\u8001\u4eba\u8eab\u4e0a\uff0c\u4fbf\u5728\u8fd9\u65f6\uff0c\u767d\u5149\u4e00\u95ea\uff0c\u8ba1\u8001\u4eba\u5315\u9996\u8131\u624b\u6fc0\u5c04\u800c\u51fa\uff0c\u63d2\u5165\u4e86\u654c\u4eba\u7684\u5c0f\u8179\u3002 \u3000\u3000\u74e6\u8033\u62c9\u9f50\u60e8\u547c\u4e00\u58f0\uff0c\u53cc\u62f3\u4e00\u62db\u201c\u4e94\u96f7\u8f70\u9876\u201d\uff0c\u5f80\u8ba1\u8001\u4eba\u5929\u7075\u76d6\u731b\u51fb\u4e0b\u53bb\u3002\u674e\u6587\u79c0\u77e5\u9053\u8fd9\u4e24\u62f3\u4e00\u51fb\u4e0b\u53bb\uff0c\u8ba1\u8001\u4eba\u518d\u96be\u6d3b\u547d\uff0c\u5f53\u4e0b\u594b\u8d77\u751f\u5e73\u4e4b\u529b\uff0c\u8dc3\u8fc7\u53bb\u4e3e\u81c2\u4e00\u683c\uff0c\u5580\u5587\u4e00\u58f0\uff0c\u53cc\u81c2\u53ea\u9707\u5f97\u5982\u6b32\u65ad\u6298\u3002\u970e\u65f6\u4e4b\u95f4\uff0c\u4e24\u4eba\u52bf\u6210\u50f5\u6301\uff0c\u74e6\u8033\u62c9\u9f50\u53cc\u62f3\u51fb\u4e0d\u4e0b\u6765\uff0c\u674e\u6587\u79c0\u4e5f\u65e0\u6cd5\u5c06\u4ed6\u683c\u5f00\u3002 \u3000\u3000\u82cf\u9c81\u514b\u8fd9\u65f6\u5df2\u53ef\u52a8\u5f39\uff0c\u8df3\u8d77\u8eab\u6765\uff0c\u594b\u8d77\u5e73\u751f\u4e4b\u529b\uff0c\u4e00\u62f3\u6253\u5728\u74e6\u8033\u62c9\u9f50\u4e0b\u988f\u3002\u74e6\u8033\u62c9\u9f50\u5411\u540e\u63bc\u51fa\uff0c\u5728\u5899\u4e0a\u4e00\u649e\uff0c\u8f6f\u5012\u5728\u5730\u3002 \u3000\u3000\u674e\u6587\u79c0\u53eb\u9053\uff1a\u201c\u8ba1\u7237\u7237\uff0c\u8ba1\u7237\u7237\u3002\u201d\u6276\u8d77\u8ba1\u8001\u4eba\uff0c\u5979\u4e0d\u6562\u7741\u773c\uff0c\u6599\u60f3\u4ed6\u8138\u4e0a\u5b9a\u662f\u8840\u8089\u6a21\u7cca\uff0c\u53ef\u6016\u4e4b\u6781\uff0c\u54ea\u77e5\u773c\u5f00\u4e00\u7ebf\uff0c\u770b\u5230\u7684\u7adf\u662f\u4e00\u5f20\u58ee\u5e74\u7537\u5b50\u7684\u8138\u5b54\u3002\u5979\u5403\u4e86\u4e00\u60ca\uff0c\u773c\u775b\u7741\u5927\u4e86\u4e9b\uff0c\u53ea\u89c1\u8fd9\u5f20\u8138\u80e1\u5b50\u5243\u5f97\u7cbe\u5149\uff0c\u9762\u76ee\u9887\u4e3a\u82f1\u4fca\uff0c\u5728\u65f6\u660e\u65f6\u6697\u7684\u706b\u628a\u5149\u8292\u4e0b\uff0c\u770b\u6765\u4e00\u7247\u60e8\u767d\uff0c\u5168\u65e0\u8840\u8272\uff0c\u8fd9\u4eba\u4e0d\u8fc7\u4e09\u5341\u591a\u5c81\uff0c\u53ea\u6709\u4e00\u53cc\u773c\u775b\u7684\u773c\u795e\uff0c\u5374\u662f\u5411\u6765\u6240\u719f\u6089\u7684\uff0c\u4f46\u914d\u5728\u8fd9\u5f20\u5168\u7136\u964c\u751f\u7684\u8138\u4e0a\uff0c\u53cd\u800c\u663e\u5f97\u8bf4\u4e0d\u51fa\u7684\u8be1\u5f02\u3002 \u3000\u3000\u674e\u6587\u79c0\u5446\u4e86\u534a\u664c\u3002\u8fd9\u624d\u201c\u554a\u201d\u7684\u4e00\u58f0\u60ca\u547c\uff0c\u5c06\u8ba1\u8001\u4eba\u7684\u8eab\u5b50\u4e00\u63a8\uff0c\u5411\u540e\u8dc3\u5f00\u3002\u5979\u8eab\u4e0a\u53d7\u4e86\u62f3\u811a\u4e4b\u4f24\uff0c\u843d\u4e0b\u6765\u65f6\u7ad9\u7acb\u4e0d\u7a33\uff0c\u5750\u5012\u5728\u5730\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u2026\u2026\u201d \u3000\u3000\u8ba1\u8001\u4eba\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u4e0d\u662f\u4f60\u8ba1\u7237\u7237\uff0c\u6211\u2026\u2026\u6211\u2026\u2026\u201d\u5ffd\u7136\u54c7\u7684\u4e00\u58f0\uff0c\u55b7\u51fa\u4e00\u5927\u53e3\u9c9c\u8840\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u9a6c\u5bb6\u9a8f\uff0c\u4e00\u76f4\u626e\u4f5c\u4e86\u4e2a\u8001\u5934\u513f\u3002\u963f\u79c0\uff0c\u4f60\u4e0d\u602a\u6211\u5417\uff1f\u201d\u8fd9\u4e00\u53e5\u201c\u963f\u79c0\u201d\uff0c\u4ecd\u662f\u548c\u5341\u5e74\u6765\u4e00\u822c\u7684\u5145\u6ee1\u4e86\u4eb2\u5207\u5173\u6000\u4e4b\u610f\u3002\u674e\u6587\u79c0\u9053\uff1a\u201c\u6211\u4e0d\u602a\u4f60\uff0c\u5f53\u7136\u4e0d\u602a\u4f60\u3002\u4f60\u4e00\u76f4\u5f85\u6211\u662f\u5f88\u597d\u5f88\u597d\u7684\u3002\u201d\u5979\u77a7\u77a7\u9a6c\u5bb6\u9a8f\uff0c\u77a7\u77a7\u9760\u5728\u5899\u4e0a\u7684\u74e6\u8033\u62c9\u9f50\uff0c\u5fc3\u4e2d\u5145\u6ee1\u4e86\u7591\u56e2\u3002 \u3000\u3000\u8fd9\u65f6\u963f\u66fc\u5df2\u6276\u8d77\u4e86\u7236\u4eb2\uff0c\u66ff\u4ed6\u63a8\u62ff\u80f8\u53e3\u7684\u4f24\u5904\u3002\u82cf\u9c81\u514b\u3001\u82cf\u666e\u7236\u5b50\u62fe\u8d77\u4e86\u957f\u5200\uff0c\u4e24\u4eba\u4e00\u8ddb\u4e00\u62d0\u7684\u8d70\u5230\u74e6\u8033\u62c9\u9f50\u8eab\u524d\u3002 \u3000\u3000\u74e6\u8033\u62c9\u9f50\u9053\uff1a\u201c\u963f\u79c0\uff0c\u521a\u624d\u6211\u53eb\u4f60\u5feb\u8d70\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u8d70\uff1f\u201d \u3000\u3000\u4ed6\u8bf4\u7684\u662f\u6c49\u8bed\uff0c\u58f0\u8c03\u53c8\u548c\u5979\u5e08\u7236\u534e\u8f89\u5b8c\u5168\u76f8\u540c\uff0c\u674e\u6587\u79c0\u60f3\u4e5f\u6ca1\u60f3\uff0c\u5f53\u5373\u8131\u53e3\u800c\u51fa\uff1a\u201c\u5e08\u7236\uff01\u201d \u3000\u3000\u74e6\u8033\u62c9\u9f50\u9053\uff1a\u201c\u4f60\u7ec8\u4e8e\u8ba4\u6211\u4e86\u3002\u201d\u4f38\u624b\u7f13\u7f13\u53d6\u4e0b\u767d\u5e03\u5934\u7f69\uff0c\u679c\u7136\u4fbf\u662f\u534e\u8f89\u3002 \u3000\u3000\u674e\u6587\u79c0\u53c8\u662f\u60ca\u8bb6\uff0c\u53c8\u662f\u96be\u8fc7\uff0c\u62a2\u8fc7\u53bb\u4f0f\u5728\u4ed6\u7684\u811a\u8fb9\uff0c\u53eb\u9053\uff1a\u201c\u5e08\u7236\uff0c\u5e08\u7236\uff0c\u6211\u771f\u7684\u4e0d\u77e5\u9053\u662f\u4f60\u3002\u6211\u2026\u2026\u6211\u8d77\u521d\u731c\u5230\u662f\u4f60\uff0c\u4f46\u4ed6\u4eec\u8bf4\u4f60\u662f\u54c8\u8428\u514b\u4eba\u74e6\u8033\u62c9\u9f50\uff0c\u4f60\u81ea\u5df1\u53c8\u8ba4\u4e86\u3002\u201d\u74e6\u8033\u62c9\u9f50\u6da9\u7136\u9053\uff1a\u201c\u6211\u662f\u54c8\u8428\u514b\u4eba\uff0c\u6211\u662f\u74e6\u8033\u62c9\u9f50\uff01\u201d\u674e\u6587\u79c0\u5947\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u4e0d\u662f\u6c49\u4eba\uff1f\u201d\u74e6\u8033\u62c9\u9f50\u9053\uff1a\u201c\u6211\u662f\u54c8\u8428\u514b\u4eba\uff0c\u65cf\u91cc\u8d76\u4e86\u6211\u51fa\u6765\uff0c\u6c38\u8fdc\u4e0d\u8bb8\u6211\u56de\u53bb\u3002\u6211\u5230\u4e86\u4e2d\u539f\uff0c\u6c49\u4eba\u7684\u5730\u65b9\uff0c\u5b66\u4e86\u6c49\u4eba\u7684\u6b66\u529f\uff0c\u563f\u563f\uff0c\u6536\u4e86\u6c49\u4eba\u505a\u5f92\u5f1f\uff0c\u9a6c\u5bb6\u9a8f\uff0c\u4f60\u597d\uff0c\u4f60\u597d\uff01\u201d \u3000\u3000\u9a6c\u5bb6\u9a8f\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u867d\u4e8e\u6211\u6709\u6069\uff0c\u53ef\u662f\u2026\u2026\u201d\u674e\u6587\u79c0\u53c8\u662f\u5927\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u8ba1\u7237\u7237\uff0c\u4f60\u2026\u2026\u4ed6\u2026\u2026\u4ed6\u4e5f\u662f\u4f60\u5e08\u7236\uff1f\u201d \u3000\u3000\u9a6c\u5bb6\u9a8f\u9053\uff1a\u201c\u4f60\u522b\u53eb\u6211\u8ba1\u7237\u7237\u3002\u6211\u662f\u9a6c\u5bb6\u9a8f\u3002\u4ed6\u662f\u6211\u5e08\u7236\uff0c\u6559\u4e86\u6211\u4e00\u8eab\u6b66\u529f\uff0c\u540c\u6211\u4e00\u8d77\u6765\u5230\u56de\u7586\uff0c\u534a\u591c\u91cc\u5e26\u6211\u5230\u54c8\u8428\u514b\u7684\u94c1\u5ef6\u90e8\u6765\uff0c\u4ed6\u7528\u6bd2\u9488\u5bb3\u6b7b\u4e86\u963f\u66fc\u7684\u5988\u5988\u2026\u2026\u201d\u4ed6\u8bf4\u7684\u662f\u6c49\u8bed\u3002\u674e\u6587\u79c0\u8d8a\u542c\u8d8a\u5947\uff0c\u7528\u54c8\u8428\u514b\u8bed\u95ee\u963f\u66fc\u9053\uff1a\u201c\u4f60\u5988\u662f\u7ed9\u4ed6\u7528\u6bd2\u9488\u5bb3\u6b7b\u7684\uff1f\u201d \u3000\u3000\u963f\u66fc\u8fd8\u6ca1\u56de\u7b54\uff0c\u8f66\u5c14\u5e93\u8df3\u8d77\u8eab\u6765\uff0c\u53eb\u9053\uff1a\u201c\u662f\u4e86\uff0c\u662f\u4e86\u3002 \u3000\u3000\u963f\u66fc\u7684\u5988\uff0c\u6211\u4eb2\u7231\u7684\u96c5\u4e3d\u4ed9\uff0c\u4e00\u5929\u665a\u4e0a\u5ffd\u7136\u5168\u8eab\u4e4c\u9ed1\uff0c\u5f97\u6025\u75c5\u6b7b\u4e86\uff0c\u539f\u6765\u662f\u4f60\u74e6\u8033\u62c9\u9f50\uff0c\u4f60\u8fd9\u6076\u68cd\uff0c\u662f\u4f60\u5bb3\u6b7b\u5979\u7684\u3002\u201d\u4ed6\u8981\u6251\u8fc7\u53bb\u548c\u74e6\u8033\u62c9\u9f50\u62da\u547d\uff0c\u4f46\u91cd\u4f24\u4e4b\u4f59\uff0c\u7a0d\u4e00\u52a8\u5f39\u4fbf\u4f24\u53e3\u5267\u75db\uff0c\u53c8\u5012\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u74e6\u8033\u62c9\u9f50\u9053\uff1a\u201c\u4e0d\u9519\u3002\u96c5\u4e3d\u4ed9\u662f\u6211\u6740\u6b7b\u7684\uff0c\u8c01\u6559\u5979\u6ca1\u751f\u773c\u73e0\uff0c\u5ac1\u4e86\u4f60\u8fd9\u5927\u6df7\u86cb\uff0c\u53c8\u4e0d\u80af\u8ddf\u6211\u9003\u8d70\uff1f\u201d\u8f66\u5c14\u5e93\u5927\u53eb\uff1a\u201c\u4f60\u8fd9\u6076\u8d3c\uff0c\u4f60\u8fd9\u6076\u8d3c\uff01\u201d \u3000\u3000\u9a6c\u5bb6\u9a8f\u4ee5\u54c8\u8428\u514b\u8bed\u9053\uff1a\u201c\u4ed6\u672c\u6765\u8981\u60f3\u6740\u6b7b\u8f66\u5c14\u5e93\uff0c\u4f46\u8fd9\u5929\u665a\u4e0a\u8f66\u5c14\u5e93\u4e0d\u77e5\u5230\u54ea\u91cc\u53bb\u4e86\uff0c\u5230\u5904\u627e\u4ed6\u4e0d\u5230\uff0c\u6211\u5e08\u7236\u81ea\u5df1\u53bb\u627e\u5bfb\u8f66\u5c14\u5e93\uff0c\u8981\u6211\u5728\u6c34\u4e95\u91cc\u4e0b\u6bd2\uff0c\u628a\u5168\u65cf\u7684\u4eba\u4e00\u8d77\u6bd2\u6b7b\u3002\u53ef\u662f\u6211\u4eec\u5728\u4e00\u5bb6\u54c8\u8428\u514b\u4eba\u5bb6\u91cc\u501f\u5bbf\uff0c\u4e3b\u4eba\u5f85\u6211\u5f88\u597d\uff0c\u5c3d\u4ed6\u4eec\u6240\u6709\u7684\u6b3e\u5f85\uff0c\u6211\u60f3\u6765\u60f3\u53bb\uff0c\u603b\u662f\u4e0b\u4e0d\u4e86\u624b\u3002\u6211\u5e08\u7236\u56de\u6765\uff0c\u8bf4\u627e\u4e0d\u5230\u8f66\u5c14\u5e93\uff0c\u4e00\u95ee\u4e4b\u4e0b\uff0c\u77e5\u9053\u6211\u6ca1\u542c\u547d\u5728\u6c34\u4e95\u91cc\u4e0b\u6bd2\uff0c\u4ed6\u5c31\u5927\u53d1\u813e\u6c14\uff0c\u8bf4\u6211\u4e00\u5b9a\u4f1a\u6cc4\u6f0f\u4ed6\u7684\u79d8\u5bc6\uff0c\u5b9a\u8981\u6740\u4e86\u6211\u706d\u53e3\u3002\u4ed6\u903c\u5f97\u5b9e\u5728\u72e0\u4e86\uff0c\u4e8e\u662f\u6211\u5148\u4e0b\u624b\u4e3a\u5f3a\uff0c\u51fa\u5176\u4e0d\u610f\u7684\u5728\u4ed6\u80cc\u5fc3\u4e0a\u5c04\u4e86\u4e09\u679a\u6bd2\u9488\u3002\u201d\u74e6\u8033\u62c9\u9f50\u6068\u6068\u7684\u9053\uff1a\u201c\u4f60\u8fd9\u5fd8\u6069\u8d1f\u4e49\u7684\u72d7\u8d3c\uff0c\u4eca\u65e5\u603b\u6559\u4f60\u6b7b\u5728\u6211\u7684\u624b\u91cc\u3002\u201d \u3000\u3000\u9a6c\u5bb6\u9a8f\u5bf9\u674e\u6587\u79c0\u9053\uff1a\u201c\u963f\u79c0\uff0c\u90a3\u5929\u665a\u4e0a\u4f60\u8ddf\u9648\u8fbe\u6d77\u90a3\u5f3a\u76d7\u52a8\u624b\uff0c\u4e00\u663e\u793a\u6b66\u529f\uff0c\u6211\u5c31\u77e5\u9053\u4f60\u662f\u8ddf\u6211\u5e08\u7236\u5b66\u7684\uff0c\u5c31\u77e5\u9053\u90a3\u4e09\u679a\u6bd2\u9488\u6ca1\u5c04\u6b7b\u4ed6\u3002\u201d\u74e6\u8033\u62c9\u9f50\u9053\uff1a\u201c\u54fc\uff0c\u51ed\u4f60\u8fd9\u70b9\u513f\u81ed\u529f\u592b\uff0c\u4e5f\u5c04\u5f97\u6b7b\u6211\uff1f\u201d\u9a6c\u5bb6\u9a8f\u4e0d\u53bb\u7406\u4ed6\uff0c\u5bf9\u674e\u6587\u79c0\u9053\uff1a\u201c\u8fd9\u5341\u591a\u5e74\u6765\u6211\u8eb2\u5728\u56de\u7586\uff0c\u8eb2\u5728\u94c1\u5ef6\u90e8\u91cc\uff0c\u88c5\u4f5c\u4e86\u4e00\u4e2a\u8001\u4eba\uff0c\u5c31\u662f\u6015\u5e08\u7236\u6ca1\u6b7b\u3002\u53ea\u6709\u8fd9\u4e2a\u5730\u65b9\uff0c\u4ed6\u662f\u4e0d\u6562\u56de\u6765\u7684\u3002\u6211\u4e00\u77e5\u9053\u4ed6\u5c31\u5728\u9644\u8fd1\uff0c\u6211\u7b2c\u4e00\u4e2a\u5ff5\u5934\uff0c\u5c31\u662f\u8981\u9003\u56de\u4e2d\u539f\u53bb\u3002\u201d \u3000\u3000\u674e\u6587\u79c0\u89c1\u4ed6\u6c14\u606f\u6e10\u6e10\u5fae\u5f31\uff0c\u77e5\u4ed6\u7ed9\u74e6\u8033\u62c9\u9f50\u4ee5\u91cd\u811a\u6cd5\u63a5\u8fde\u8e22\u4e2d\u4e24\u4e0b\uff0c\u5185\u810f\u9707\u88c2\uff0c\u5df2\u7136\u96be\u4ee5\u6d3b\u547d\uff0c\u56de\u8fc7\u5934\u6765\u770b\u74e6\u8033\u62c9\u9f50\u65f6\uff0c\u4ed6\u5c0f\u8179\u4e0a\u90a3\u628a\u5315\u9996\u76f4\u6ca1\u81f3\u67c4\uff0c\u4e5f\u662f\u5df2\u65e0\u6d3b\u7406\u3002\u81ea\u5df1\u5728\u56de\u7586\u5341\u5e74\uff0c\u53ea\u6709\u8fd9\u4e24\u4eba\u662f\u771f\u6b63\u7167\u987e\u81ea\u5df1\u3001\u5173\u6000\u81ea\u5df1\u7684\uff0c\u54ea\u77e5\u4ed6\u4e24\u4eba\u6069\u6028\u7275\u7f20\uff0c\u7adf\u81f4\u81ea\u76f8\u6b8b\u6740\uff0c\u4e24\u8d25\u4ff1\u4f24\u3002\u5979\u773c\u7736\u4e2d\u5145\u6ee1\u4e86\u6cea\u6c34\uff0c\u95ee\u9a6c\u5bb6\u9a8f\u9053\uff1a\u201c\u8ba1\u2026\u2026\u9a6c\u5927\u53d4\uff0c\u4f60\u2026\u2026\u4f60\u65e2\u7136\u77e5\u9053\u4ed6\u6ca1\u6b7b\uff0c\u800c\u4e14\u5c31\u5728\u9644\u8fd1\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u7acb\u523b\u56de\u4e2d\u539f\u53bb\uff1f\u201d \u3000\u3000\u9a6c\u5bb6\u9a8f\u5634\u89d2\u8fb9\u9732\u51fa\u51c4\u7136\u7684\u82e6\u7b11\uff0c\u8f7b\u8f7b\u7684\u9053\uff1a\u201c\u6c5f\u5357\u7684\u6768\u67f3\uff0c\u5df2\u62bd\u51fa\u5ae9\u82bd\u4e86\uff0c\u963f\u79c0\uff0c\u4f60\u72ec\u81ea\u56de\u53bb\u5427\uff0c\u4ee5\u540e\u2026\u2026\u4ee5\u540e\u53ef\u5f97\u5c0f\u5fc3\uff0c\u8ba1\u7237\u7237\uff0c\u8ba1\u7237\u7237\u4e0d\u80fd\u7167\u987e\u4f60\u4e86\u2026\u2026\u201d\u58f0\u97f3\u8d8a\u8bf4\u8d8a\u4f4e\uff0c\u7ec8\u4e8e\u6ca1\u4e86\u58f0\u606f\u3002 \u3000\u3000\u674e\u6587\u79c0\u6251\u5728\u4ed6\u8eab\u4e0a\uff0c\u53eb\u9053\uff1a\u201c\u8ba1\u7237\u7237\uff0c\u8ba1\u7237\u7237\uff0c\u4f60\u522b\u6b7b\u3002\u201d \u3000\u3000\u9a6c\u5bb6\u9a8f\u6ca1\u56de\u7b54\u5979\u7684\u95ee\u8bdd\u5c31\u6b7b\u4e86\uff0c\u53ef\u662f\u674e\u6587\u79c0\u5fc3\u4e2d\u5374\u5df2\u660e\u767d\u5f97\u5f88\u3002\u9a6c\u5bb6\u9a8f\u975e\u5e38\u975e\u5e38\u7684\u6015\u4ed6\u7684\u5e08\u7236\uff0c\u53ef\u662f\u975e\u4f46\u4e0d\u7acb\u5373\u9003\u56de\u4e2d\u539f\uff0c\u53cd\u800c\u8ddf\u7740\u5979\u6765\u5230\u8ff7\u5bab\uff1b\u53ea\u8981\u4ed6\u59cb\u7ec8\u626e\u4f5c\u8001\u4eba\uff0c\u74e6\u8033\u62c9\u9f50\u6c38\u8fdc\u4e0d\u4f1a\u8ba4\u51fa\u4ed6\u6765\uff0c\u53ef\u662f\u4ed6\u7ec8\u4e8e\u51fa\u624b\uff0c\u53bb\u548c\u81ea\u5df1\u6700\u60e7\u6015\u7684\u4eba\u52a8\u624b\u3002\u90a3\u5168\u662f\u4e3a\u4e86\u5979\uff01 \u3000\u3000\u8fd9\u5341\u5e74\u4e4b\u4e2d\uff0c\u4ed6\u59cb\u7ec8\u5982\u7237\u7237\u822c\u7231\u62a4\u81ea\u5df1\uff0c\u5176\u5b9e\u4ed6\u662f\u4e2a\u58ee\u5e74\u4eba\u3002\u4e16\u754c\u4e0a\u4eb2\u7956\u7236\u5bf9\u81ea\u5df1\u7684\u5b59\u5973\uff0c\u4e5f\u6709\u8fd9\u822c\u597d\u5417\uff1f\u6216\u8bb8\u6709\uff0c\u6216\u8bb8\u6ca1\u6709\uff0c\u5979\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u6bbf\u4e0a\u5730\u4e0b\u7684\u4e24\u6839\u706b\u628a\uff0c\u4e00\u6839\u65e9\u5df2\u7184\u706d\u4e86\uff0c\u53e6\u4e00\u6839\u4e5f\u5feb\u70e7\u5230\u5c3d\u5934\u3002 \u3000\u3000\u82cf\u9c81\u514b\u5ffd\u9053\uff1a\u201c\u771f\u662f\u5947\u602a\uff0c\u521a\u624d\u4e24\u4e2a\u6c49\u4eba\u8ddf\u4e00\u4e2a\u54c8\u8428\u514b\u4eba\u76f8\u6253\uff0c\u6211\u60f3\u4e5f\u4e0d\u60f3\uff0c\u8fc7\u53bb\u4e00\u62f3\uff0c\u5c31\u6253\u5728\u90a3\u4e2a\u54c8\u8428\u514b\u4eba\u7684\u8138\u4e0a\u3002\u201d \u3000\u3000\u674e\u6587\u79c0\u95ee\u9053\uff1a\u201c\u90a3\u4e3a\u4ec0\u4e48\uff1f\u4e3a\u4ec0\u4e48\u4f60\u5ffd\u7136\u5e2e\u6c49\u4eba\u6253\u54c8\u8428\u514b\u4eba\uff1f\u201d \u3000\u3000\u82cf\u9c81\u514b\u6414\u4e86\u6414\u5934\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u3002\u201d\u9694\u4e86\u4e00\u4f1a\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u597d\u4eba\uff0c\u4ed6\u662f\u574f\u4eba\uff01\u201d \u3000\u3000\u4ed6\u7ec8\u4e8e\u627f\u8ba4\uff1a\u6c49\u4eba\u4e2d\u6709\u505a\u5f3a\u76d7\u7684\u574f\u4eba\uff0c\u4e5f\u6709\u674e\u82f1\u96c4\u90a3\u6837\u7684\u597d\u4eba\uff0c\uff08\u90a3\u4e2a\u5047\u626e\u8001\u5934\u513f\u7684\u6c49\u4eba\uff0c\u4e0d\u80af\u5728\u6c34\u4e95\u4e2d\u4e0b\u6bd2\uff0c\u4e5f\u8be5\u7b97\u597d\u4eba\u5427\uff1f\uff09\u54c8\u8428\u514b\u4eba\u4e2d\u6709\u81ea\u5df1\u90a3\u6837\u7684\u597d\u4eba\uff0c\u4e5f\u6709\u74e6\u8033\u62c9\u9f50\u90a3\u6837\u7684\u574f\u4eba\u3002 \u3000\u3000\u674e\u6587\u79c0\u5fc3\u60f3\uff1a\u201c\u5982\u679c\u5f53\u5e74\u4f60\u77e5\u9053\u4e86\uff0c\u5c31\u4e0d\u4f1a\u90a3\u6837\u72e0\u72e0\u7684\u97ad\u6253\u82cf\u666e\uff0c\u4e00\u5207\u5c31\u4f1a\u4e0d\u540c\u4e86\u3002\u53ef\u662f\uff0c\u771f\u7684\u4f1a\u4e0d\u540c\u5417\uff1f\u5c31\u7b97\u82cf\u666e\u5c0f\u65f6\u5019\u8ddf\u6211\u505a\u597d\u670b\u53cb\uff0c\u4ed6\u5e74\u7eaa\u5927\u4e86\u4e4b\u540e\uff0c\u89c1\u5230\u4e86\u963f\u66fc\uff0c\u8fd8\u662f\u4f1a\u7231\u4e0a\u5979\u7684\u3002\u4eba\u7684\u5fc3\uff0c\u771f\u592a\u5947\u602a\u4e86\uff0c\u6211\u4e0d\u61c2\u3002\u201d \u3000\u3000\u82cf\u9c81\u514b\u5927\u58f0\u9053\uff1a\u201c\u74e6\u8033\u62c9\u9f50\uff0c\u6211\u77a7\u4f60\u4e5f\u6d3b\u4e0d\u6210\u4e86\uff0c\u6211\u4eec\u4e5f\u4e0d\u7528\u6740\u4f60\uff0c\u518d\u89c1\u4e86\uff01\u201d\u74e6\u8033\u62c9\u9f50\u7a81\u7136\u76ee\u9732\u51f6\u5149\uff0c\u53f3\u624b\u4e00\u63d0\u3002\u674e\u6587\u79c0\u77e5\u4ed6\u8981\u53d1\u5c04\u6bd2\u9488\uff0c\u53eb\u9053\uff1a\u201c\u5e08\u7236\uff0c\u522b\u2014\u2014\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u4e00\u4e2a\u706b\u661f\u7206\u4e86\u5f00\u6765\uff0c\u6700\u540e\u4e00\u4e2a\u706b\u628a\u4e5f\u7184\u706d\u4e86\uff0c\u6bbf\u5802\u4e2d\u4f38\u624b\u4e0d\u89c1\u4e94\u6307\u3002\u74e6\u8033\u62c9\u9f50\u5c31\u662f\u60f3\u53d1\u6bd2\u9488\u5bb3\u4eba\uff0c\u4e5f\u5df2\u53d6\u4e0d\u5230\u51c6\u5934\u3002\u674e\u6587\u79c0\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u5feb\u51fa\u53bb\uff0c\u8c01\u4e5f\u522b\u53d1\u51fa\u58f0\u54cd\u3002\u201d \u3000\u3000\u82cf\u9c81\u514b\u3001\u82cf\u666e\u3001\u8f66\u5c14\u5e93\u548c\u963f\u66fc\u56db\u4eba\u4e92\u76f8\u6276\u6301\uff0c\u6084\u6084\u7684\u9000\u4e86\u51fa\u53bb\u3002\u5927\u5bb6\u77e5\u9053\u74e6\u8033\u62c9\u9f50\u7684\u6bd2\u9488\u5389\u5bb3\uff0c\u4ed6\u867d\u547d\u5728\u9877\u523b\uff0c\u5374\u8fd8\u80fd\u53d1\u9488\u5bb3\u4eba\u3002\u56db\u4eba\u9000\u51fa\u6bbf\u5802\uff0c\u89c1\u674e\u6587\u79c0\u6ca1\u6709\u51fa\u6765\uff0c\u82cf\u666e\u53eb\u9053\uff1a\u201c\u674e\u82f1\u96c4\uff0c\u674e\u82f1\u96c4\uff0c\u5feb\u51fa\u6765\u3002\u201d\u674e\u6587\u79c0\u7b54\u5e94\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u74e6\u8033\u62c9\u9f50\u9053\uff1a\u201c\u963f\u79c0\uff0c\u4f60\u2026\u2026\u4f60\u4e5f\u8981\u53bb\u4e86\u5417\uff1f\u201d\u58f0\u97f3\u751a\u662f\u51c4\u51c9\u3002\u674e\u6587\u79c0\u5fc3\u4e2d\u4e0d\u5fcd\uff0c\u6697\u60f3\u4ed6\u867d\u7136\u505a\u4e86\u8bb8\u591a\u574f\u4e8b\uff0c\u5bf9\u81ea\u5df1\u53ef\u6bd5\u7adf\u662f\u5f88\u597d\u7684\uff0c\u8ba9\u4ed6\u4e00\u4e2a\u4eba\u5728\u8fd9\u9ed1\u6697\u4e2d\u7b49\u6b7b\uff0c\u5b9e\u5728\u662f\u592a\u6b8b\u5fcd\u4e86\uff0c\u4e8e\u662f\u5750\u4e86\u4e0b\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u7236\uff0c\u6211\u5728\u8fd9\u91cc\u966a\u4f60\u3002\u201d \u3000\u3000\u82cf\u666e\u5728\u5916\u9762\u53c8\u53eb\u4e86\u51e0\u58f0\u3002\u674e\u6587\u79c0\u5927\u58f0\u9053\uff1a\u201c\u4f60\u4eec\u5148\u51fa\u53bb\u5427\uff0c\u6211\u7b49\u4e00\u4f1a\u51fa\u6765\u3002\u201d\u82cf\u666e\u53eb\u9053\uff1a\u201c\u8fd9\u4eba\u5f88\u51f6\u6076\u7684\uff0c\u674e\u82f1\u96c4\uff0c\u4f60\u53ef\u5f97\u5c0f\u5fc3\u4e86\u3002\u201d\u674e\u6587\u79c0\u4e0d\u518d\u56de\u7b54\u3002 \u3000\u3000\u963f\u66fc\u9053\uff1a\u201c\u4f60\u600e\u4e48\u8001\u662f\u53eb\u5979\u674e\u82f1\u96c4\uff0c\u4e0d\u53eb\u674e\u59d1\u5a18\uff1f\u201d\u82cf\u666e\u5947\u9053\uff1a\u201c\u674e\u59d1\u5a18\uff0c\u5979\u662f\u5973\u5b50\u5417\uff1f\u201d\u963f\u66fc\u9053\uff1a\u201c\u4f60\u662f\u88c5\u50bb\uff0c\u8fd8\u662f\u771f\u7684\u770b\u4e0d\u51fa\u6765\uff1f\u201d\u82cf\u666e\u9053\uff1a\u201c\u6211\u88c5\u4ec0\u4e48\u50bb\uff0c\u4ed6\u2026\u2026\u4ed6\u6b66\u529f\u8fd9\u6837\u597d\uff0c\u600e\u4e48\u4f1a\u662f\u5973\u5b50\uff1f\u201d \u3000\u3000\u963f\u66fc\u9053\uff1a\u201c\u90a3\u5929\u5927\u98ce\u96ea\u7684\u665a\u4e0a\uff0c\u5728\u8ba1\u8001\u4eba\u7684\u5bb6\u91cc\uff0c\u5979\u593a\u4e86\u6211\u505a\u5973\u5974\uff0c\u540e\u6765\u53c8\u653e\u4e86\u6211\u3002\u90a3\u65f6\u5019\u6211\u5c31\u77e5\u9053\u5979\u662f\u5973\u5b50\u4e86\u3002\u201d\u82cf\u666e\u62cd\u624b\u9053\uff1a\u201c\u554a\uff0c\u662f\u4e86\u3002\u5982\u679c\u5979\u662f\u7537\u4eba\uff0c\u600e\u80af\u653e\u4e86\u5f97\u4f60\u8fd9\u6837\u7f8e\u4e3d\u7684\u5973\u5974\uff1f\u201d\u963f\u66fc\u8138\u4e0a\u5fae\u5fae\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u4e0d\u662f\u7684\u3002\u90a3\u65f6\u5019\u6211\u89c1\u5230\u4e86\u5979\u77a7\u7740\u4f60\u7684\u773c\u8272\uff0c\u5c31\u77e5\u9053\u5979\u662f\u59d1\u5a18\u3002\u5929\u4e0b\u54ea\u4f1a\u6709\u4e00\u4e2a\u7537\u5b50\uff0c\u7528\u8fd9\u6837\u7684\u773c\u5149\u75f4\u75f4\u7684\u77a7\u7740\u4f60\uff01\u201d \u3000\u3000\u82cf\u666e\u6414\u4e86\u6414\u5934\uff0c\u50bb\u7b11\u9053\uff1a\u201c\u6211\u53ef\u4e00\u70b9\u4e5f\u6ca1\u77a7\u51fa\u6765\u3002\u201d\u963f\u66fc\u6b22\u7545\u5730\u7b11\u4e86\uff0c\u7b11\u5f97\u771f\u50cf\u4e00\u6735\u82b1\u3002\u5979\u77e5\u9053\u82cf\u666e\u7684\u773c\u5149\u4e00\u76f4\u505c\u5728\u81ea\u5df1\u8eab\u4e0a\uff0c\u4fbf\u6709\u4e00\u4e07\u4e2a\u59d1\u5a18\u75f4\u60c5\u5730\u77a7\u7740\u4ed6\uff0c\u4ed6\u4e5f\u6c38\u4e0d\u4f1a\u77e5\u9053\u3002 \u3000\u3000\u6bbf\u5802\u4e2d\u4e00\u7247\u6f06\u9ed1\uff0c\u674e\u6587\u79c0\u548c\u74e6\u8033\u62c9\u9f50\u8c01\u4e5f\u89c1\u4e0d\u5230\u8c01\u3002\u674e\u6587\u79c0\u5750\u5728\u5e08\u7236\u8eab\u7554\uff0c\u5728\u4e07\u7c41\u4ff1\u5bc2\u4e4b\u4e2d\uff0c\u542c\u5230\u82cf\u666e\u548c\u963f\u66fc\u7684\u5b09\u7b11\u58f0\u6e10\u6e10\u8fdc\u53bb\uff0c\u542c\u5230\u56db\u4e2a\u4eba\u7684\u811a\u6b65\u58f0\u6e10\u6e10\u8fdc\u53bb\u3002 \u3000\u3000\u6bbf\u5802\u91cc\u53ea\u5269\u4e0b\u4e86\u674e\u6587\u79c0\uff0c\u966a\u7740\u5782\u6b7b\u7684\u74e6\u8033\u62c9\u9f50\uff0c\u8fd8\u6709\uff0c\u201c\u8ba1\u7237\u7237\u201d\u7684\u5c38\u8eab\u3002 \u3000\u3000\u74e6\u8033\u62c9\u9f50\u53c8\u95ee\uff1a\u201c\u521a\u624d\u6211\u53eb\u4f60\u51fa\u53bb\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u542c\u8bdd\uff1f\u8981\u662f\u4f60\u51fa\u53bb\u4e86\u2026\u2026\u5509\u3002\u201d \u3000\u3000\u674e\u6587\u79c0\u8f7b\u8f7b\u7684\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u5f97\u4e0d\u5230\u5fc3\u7231\u7684\u4eba\uff0c\u5c31\u5c06\u5979\u6740\u6b7b\u3002\u6211\u5f97\u4e0d\u5230\u5fc3\u7231\u7684\u4eba\uff0c\u5374\u4e0d\u5fcd\u5fc3\u8ba9\u4ed6\u7ed9\u4eba\u6740\u4e86\u3002\u201d \u3000\u3000\u74e6\u8033\u62c9\u9f50\u51b7\u7b11\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u539f\u6765\u662f\u8fd9\u6837\u3002\u201d\u6c89\u9ed8\u534a\u664c\uff0c\u53f9\u9053\uff1a\u201c\u4f60\u4eec\u6c49\u4eba\u771f\u662f\u5947\u602a\u3002\u6709\u9a6c\u5bb6\u9a8f\u90a3\u6837\u5fd8\u6069\u8d1f\u4e49\u3001\u6740\u5bb3\u5e08\u7236\u7684\u6076\u68cd\uff0c\u6709\u970d\u5143\u9f99\u3001\u9648\u8fbe\u6d77\u4ed6\u4eec\u90a3\u6837\u6740\u4eba\u4e0d\u7728\u773c\u7684\u5f3a\u76d7\uff0c\u4e5f\u6709\u4f60\u8fd9\u6837\u5fc3\u5730\u4ec1\u5584\u7684\u59d1\u5a18\u3002\u201d \u3000\u3000\u674e\u6587\u79c0\u95ee\u9053\uff1a\u201c\u5e08\u7236\uff0c\u9648\u8fbe\u6d77\u90a3\u5f3a\u76d7\u600e\u6837\u4e86\uff1f\u6211\u4eec\u4e00\u8def\u8ffd\u8e2a\u4ed6\uff0c\u5374\u5728\u96ea\u5730\u91cc\u770b\u5230\u4e86\u4e24\u4e2a\u4eba\u7684\u811a\u5370\u3002\u53e6\u4e00\u4e2a\u662f\u4f60\u7684\u5417\uff1f\u201d \u3000\u3000\u74e6\u8033\u62c9\u9f50\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u6211\u7684\u3002\u81ea\u4ece\u6211\u7ed9\u9a6c\u5bb6\u9a8f\u8fd9\u9006\u5f92\u6253\u4e86\u6bd2\u9488\u4e4b\u540e\uff0c\u8eab\u5b50\u8870\u5f31\uff0c\u5341\u591a\u5e74\u6765\u5728\u5c71\u6d1e\u91cc\u517b\u4f24\uff0c\u53ea\u9053\u8fd9\u4e00\u751f\u5c31\u6b64\u5b8c\u4e86\uff0c\u60f3\u4e0d\u5230\u7adf\u4f1a\u6709\u4f60\u6765\u6551\u6211\uff0c\u7ed9\u6211\u62d4\u53bb\u4e86\u6bd2\u9488\u3002\u6211\u4f24\u6108\u4e4b\u540e\uff0c\u534a\u591c\u91cc\u65f6\u5e38\u53bb\u94c1\u5ef6\u90e8\u7684\u5e10\u7bf7\u5916\u7aa5\u63a2\uff0c\u6211\u8981\u6740\u4e86\u8f66\u5c14\u5e93\uff0c\u6740\u4e86\u9a71\u9010\u6211\u7684\u65cf\u957f\u3002\u53ea\u662f\u4e3a\u4e86\u4f60\uff0c\u6211\u624d\u6ca1\u5728\u6c34\u4e95\u91cc\u4e0b\u6bd2\u3002\u90a3\u5929\u5927\u98ce\u96ea\u7684\u665a\u4e0a\uff0c\u6211\u5b88\u5728\u4f60\u5c4b\u5b50\u5916\uff0c\u89c1\u5230\u4f60\u62ff\u4f4f\u4e86\u9648\u8fbe\u6d77\uff0c\u542c\u5230\u4f60\u4eec\u53d1\u73b0\u4e86\u8ff7\u5bab\u7684\u5730\u56fe\u3002\u9648\u8fbe\u6d77\u4e00\u9003\u8d70\uff0c\u6211\u5c31\u8ddf\u5728\u4ed6\u540e\u9762\uff0c\u4e00\u76f4\u8ddf\u8fdb\u4e86\u8ff7\u5bab\u3002\u6211\u5728\u4ed6\u540e\u8111\u4e0a\u4e00\u62f3\uff0c\u6253\u6655\u4e86\u4ed6\uff0c\u628a\u4ed6\u5173\u5728\u8ff7\u5bab\u91cc\uff0c\u524d\u5929\u4e0b\u5348\uff0c\u6211\u4ece\u4ed6\u6000\u91cc\u62ff\u4e86\u90a3\u5e45\u624b\u5e15\u5730\u56fe\u51fa\u6765\uff0c\u62bd\u53bb\u4e86\u5341\u6765\u6839\u6bdb\u7ebf\uff0c\u653e\u56de\u4ed6\u6000\u91cc\uff0c\u518d\u8499\u4e86\u4ed6\u773c\u775b\uff0c\u7ed1\u4ed6\u5728\u9a6c\u80cc\u4e4b\u4e0a\uff0c\u8d76\u4ed6\u8fdc\u8fdc\u7684\u53bb\u4e86\u3002\u201d \u3000\u3000\u674e\u6587\u79c0\u60f3\u4e0d\u5230\u8fd9\u4e2a\u6027\u5b50\u6b8b\u9177\u7684\u4eba\u5c45\u7136\u80af\u9976\u4eba\u6027\u547d\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u62bd\u53bb\u5730\u56fe\u4e0a\u7684\u6bdb\u7ebf\uff1f\u201d\u74e6\u8033\u62c9\u9f50\u5e72\u7b11\u6570\u58f0\uff0c\u5341\u5206\u5f97\u610f\uff1a\u201c\u4ed6\u4e0d\u77e5\u9053\u6211\u62bd\u53bb\u4e86\u6bdb\u7ebf\u7684\u3002\u5730\u56fe\u4e2d\u5c11\u4e86\u5341\u51e0\u6839\u7ebf\uff0c\u8fd9\u8ff7\u5bab\u518d\u4e5f\u627e\u4e0d\u5230\u4e86\u3002\u8fd9\u6076\u5f3a\u76d7\uff0c\u4ed6\u5b9a\u8981\u53bb\u4f1a\u9f50\u4e86\u5176\u4f59\u7684\u76d7\u4f19\uff0c\u51ed\u7740\u5730\u56fe\u53c8\u6765\u627e\u5bfb\u8ff7\u5bab\u3002\u4ed6\u4eec\u5c31\u8981\u5728\u5927\u6208\u58c1\u4e2d\u515c\u6765\u515c\u53bb\uff0c\u6c38\u8fdc\u56de\u4e0d\u5230\u8349\u539f\u53bb\u3002\u8fd9\u6279\u6076\u5f3a\u76d7\u4e00\u4e2a\u4e2a\u7684\u8981\u5728\u6c99\u6f20\u4e2d\u6e34\u6b7b\uff0c\u4e00\u76f4\u5230\u6b7b\uff0c\u8fd8\u662f\u60f3\u6765\u8ff7\u5bab\u53d1\u8d22\uff0c\u54c8\u54c8\uff0c\u563f\u563f\uff0c\u6709\u8da3\uff0c\u6709\u8da3\uff01\u201d \u3000\u3000\u60f3\u5230\u4e00\u7fa4\u4eba\u5728\u70c8\u65e5\u70e4\u7099\u4e4b\u4e0b\uff0c\u5728\u6570\u767e\u91cc\u5185\u6ca1\u4e00\u6ef4\u6c34\u7684\u5927\u6c99\u6f20\u4e0a\u4e0d\u65ad\u515c\u5708\u5b50\u7684\u53ef\u6016\u60c5\u666f\uff0c\u674e\u6587\u79c0\u5fcd\u4e0d\u4f4f\u4f4e\u4f4e\u7684\u547c\u4e86\u4e00\u58f0\u3002\u8fd9\u7fa4\u5f3a\u76d7\u662f\u6740\u5bb3\u5979\u7236\u6bcd\u7684\u5927\u4ec7\u4eba\uff0c\u4f46\u5982\u6b64\u906d\u53d7\u9177\u62a5\uff0c\u5374\u4e0d\u7531\u5f97\u4e3a\u4ed6\u4eec\u96be\u53d7\u3002\u8981\u662f\u5979\u80fd\u6709\u673a\u4f1a\u9047\u4e0a\u4e86\uff0c\u4f1a\u4e0d\u4f1a\u5bf9\u4ed6\u4eec\u8bf4\uff1a\u201c\u8fd9\u5f20\u5730\u56fe\u662f\u4e0d\u5bf9\u7684\uff1f\u201d \u3000\u3000\u5979\u591a\u534a\u4f1a\u8bf4\u7684\u3002\u53ea\u4e0d\u8fc7\uff0c\u970d\u5143\u9f99\u3001\u9648\u8fbe\u6d77\u4ed6\u4eec\u51b3\u8ba1\u4e0d\u4f1a\u76f8\u4fe1\u3002\u4ed6\u4eec\u4e00\u5b9a\u8981\u6ee1\u6000\u7740\u53d1\u8d22\u7684\u5ff5\u5934\uff0c\u5728\u6c99\u6f20\u91cc\u5927\u515c\u5708\u5b50\uff0c\u76f4\u5230\u4e00\u4e2a\u4e2a\u7684\u6e34\u6b7b\u3002\u4ed6\u4eec\u8fd8\u662f\u76f8\u4fe1\u5728\u8d70\u5411\u8ff7\u5bab\uff0c\u56e0\u4e3a\u9648\u8fbe\u6d77\u66fe\u51ed\u7740\u8fd9\u5e45\u5730\u56fe\uff0c\u4eb2\u8eab\u5230\u8fc7\u8ff7\u5bab\uff0c\u90a3\u662f\u51b3\u8ba1\u4e0d\u4f1a\u9519\u7684\u3002\u8ff7\u5bab\u91cc\u6709\u6570\u4e0d\u5c3d\u7684\u73cd\u73e0\u5b9d\u8d1d\uff0c\u5927\u5bb6\u90fd\u8fd9\u4e48\u8bf4\u7684\uff0c\u90a3\u8fd8\u80fd\u5047\u4e48\uff1f \u3000\u3000\u74e6\u8033\u62c9\u9f50\u5403\u5403\u7684\u7b11\u4e2a\u4e0d\u505c\uff0c\u8bf4\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u8ff7\u5bab\u91cc\u4e00\u5757\u624b\u6307\u5927\u7684\u9ec4\u91d1\u4e5f\u6ca1\u6709\uff0c\u8ff7\u5bab\u91cc\u6240\u85cf\u7684\u6bcf\u4e00\u4ef6\u4e1c\u897f\uff0c\u4e2d\u539f\u90fd\u662f\u591a\u5f97\u4e0d\u5f97\u4e86\u3002\u684c\u5b50\u3001\u6905\u5b50\u3001\u5e8a\u3001\u5e10\u5b50\uff0c\u8bb8\u8bb8\u591a\u591a\u7684\u4e66\u672c\uff0c\u56f4\u68cb\u5566\u3001\u4e03\u5f26\u7434\u5566\u3001\u7076\u5934\u3001\u7897\u789f\u3001\u955c\u5b50\u2026\u2026\u4ec0\u4e48\u90fd\u6709\uff0c\u5c31\u662f\u6ca1\u6709\u73cd\u5b9d\u3002\u5728\u6c49\u4eba\u7684\u5730\u65b9\uff0c\u8fd9\u4e9b\u4e1c\u897f\u904d\u5730\u90fd\u662f\uff0c\u90a3\u4e9b\u6c49\u4eba\u5374\u62da\u4e86\u6027\u547d\u6765\u627e\u5bfb\uff0c\u563f\u563f\uff0c\u771f\u662f\u7b11\u6b7b\u4eba\u4e86\u3002\u201d \u3000\u3000\u674e\u6587\u79c0\u4e24\u6b21\u8fdb\u5165\u8ff7\u5bab\uff0c\u89c1\u5230\u4e86\u65e0\u6570\u65e5\u5e38\u7528\u5177\uff0c\u56de\u7586\u6c14\u5019\u5e72\u71e5\uff0c\u5386\u65f6\u867d\u4e45\uff0c\u8bf8\u7269\u5e76\u672a\u8150\u673d\uff0c\u904d\u5386\u6bbf\u5802\u623f\u820d\uff0c\u679c\u7136\u6ca1\u89c1\u5230\u8fc7\u4e1d\u6beb\u91d1\u94f6\u73e0\u5b9d\uff0c\u8bf4\u9053\uff1a\u201c\u4eba\u5bb6\u7684\u4f20\u8bf4\uff0c\u5927\u90fd\u9760\u4e0d\u4f4f\u7684\uff0c\u8fd9\u5ea7\u8ff7\u5bab\u867d\u5927\uff0c\u5374\u6ca1\u6709\u5b9d\u7269\u3002\u5509\uff0c\u8fde\u6211\u7684\u7239\u7239\u5988\u5988\uff0c\u4e5f\u56e0\u6b64\u800c\u6789\u9001\u4e86\u6027\u547d\u3002\u201d \u3000\u3000\u74e6\u8033\u62c9\u9f50\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u8fd9\u8ff7\u5bab\u7684\u6765\u5386\uff1f\u201d\u674e\u6587\u79c0\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u5e08\u7236\uff0c\u4f60\u77e5\u9053\u4e48\uff1f\u201d\u74e6\u8033\u62c9\u9f50\u9053\uff1a\u201c\u6211\u5728\u8ff7\u5bab\u91cc\u89c1\u5230\u4e86\u4e24\u5ea7\u77f3\u7891\uff0c\u4e0a\u9762\u523b\u660e\u4e86\u5efa\u9020\u8ff7\u5bab\u7684\u7ecf\u8fc7\uff0c\u539f\u6765\u662f\u5510\u592a\u5b97\u65f6\u5019\u5efa\u9020\u7684\u3002\u201d\u674e\u6587\u79c0\u4e5f\u4e0d\u77e5\u9053\u5510\u592a\u5b97\u662f\u4ec0\u4e48\u4eba\uff0c\u4e8e\u662f\u74e6\u8033\u62c9\u9f50\u65ad\u65ad\u7eed\u7eed\u7684\u7ed9\u5979\u8bf4\u4e86\u8ff7\u5bab\u7684\u6765\u5386\u3002 \u3000\u3000\u539f\u6765\u8fd9\u5730\u65b9\u5728\u5510\u671d\u65f6\u662f\u9ad8\u660c\u56fd\u7684\u6240\u5728\u3002 \u3000\u3000\u90a3\u65f6\u9ad8\u660c\u662f\u897f\u57df\u5927\u56fd\uff0c\u7269\u4ea7\u4e30\u76db\uff0c\u56fd\u52bf\u5f3a\u76db\u3002\u5510\u592a\u5b97\u8d1e\u89c2\u5e74\u95f4\uff0c\u9ad8\u660c\u56fd\u7684\u56fd\u738b\u53eb\u505a\u97a0\u6587\u6cf0\uff0c\u81e3\u670d\u4e8e\u5510\u3002\u5510\u671d\u6d3e\u4f7f\u8005\u5230\u9ad8\u660c\uff0c\u8981\u4ed6\u4eec\u9075\u5b88\u8bb8\u591a\u6c49\u4eba\u7684\u89c4\u77e9\u3002\u97a0\u6587\u6cf0\u5bf9\u4f7f\u8005\u8bf4\uff1a\u201c\u9e70\u98de\u4e8e\u5929\uff0c\u96c9\u4f0f\u4e8e\u84bf\uff0c\u732b\u6e38\u4e8e\u5802\uff0c\u9f20\u564d\u4e8e\u7a74\uff0c\u5404\u5f97\u5176\u6240\uff0c\u5c82\u4e0d\u80fd\u81ea\u751f\u90aa\uff1f\u201d\u610f\u601d\u8bf4\uff0c\u867d\u7136\u4f60\u4eec\u662f\u731b\u9e70\uff0c\u5728\u5929\u4e0a\u98de\uff0c\u4f46\u6211\u4eec\u662f\u91ce\u9e21\uff0c\u8eb2\u5728\u8349\u4e1b\u4e4b\u4e2d\uff0c\u867d\u7136\u4f60\u4eec\u662f\u732b\uff0c\u5728\u5385\u5802\u4e0a\u8d70\u6765\u8d70\u53bb\uff0c\u4f46\u6211\u4eec\u662f\u5c0f\u9f20\uff0c\u8eb2\u5728\u6d1e\u91cc\u557e\u557e\u7684\u53eb\uff0c\u4f60\u4eec\u4e5f\u5948\u4f55\u6211\u4eec\u4e0d\u5f97\u3002\u5927\u5bb6\u5404\u8fc7\u5404\u7684\u65e5\u5b50\uff0c\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u5f3a\u8feb\u6211\u4eec\u9075\u5b88\u4f60\u4eec\u6c49\u4eba\u7684\u89c4\u77e9\u4e60\u4fd7\u5462\uff1f\u5510\u592a\u5b97\u542c\u4e86\u8fd9\u8bdd\uff0c\u5f88\u662f\u6124\u6012\uff0c\u8ba4\u4e3a\u4ed6\u4eec\u91ce\u86ee\uff0c\u4e0d\u670d\u738b\u5316\uff0c\u4e8e\u662f\u6d3e\u51fa\u4e86\u5927\u5c06\u4faf\u541b\u96c6\u53bb\u8ba8\u4f10\u3002 \u3000\u3000\u97a0\u6587\u6cf0\u5f97\u5230\u6d88\u606f\uff0c\u5bf9\u767e\u5b98\u9053\uff1a\u201c\u5927\u5510\u79bb\u6211\u4eec\u4e03\u5343\u91cc\uff0c\u4e2d\u95f4\u4e8c\u5343\u91cc\u662f\u5927\u6c99\u6f20\uff0c\u5730\u65e0\u6c34\u8349\uff0c\u5bd2\u98ce\u5982\u5200\uff0c\u70ed\u98ce\u5982\u70e7\uff0c\u600e\u80fd\u6d3e\u5927\u519b\u5230\u6765\uff1f\u4ed6\u6765\u6253\u6211\u4eec\uff0c\u5982\u679c\u5175\u6d3e\u5f97\u5f88\u591a\uff0c\u7cae\u8fd0\u4fbf\u63a5\u6d4e\u4e0d\u4e0a\u3002 \u3000\u3000\u8981\u662f\u6d3e\u5175\u5728\u4e09\u4e07\u4ee5\u4e0b\uff0c\u4fbf\u4e0d\u7528\u6015\u3002\u54b1\u4eec\u4ee5\u9038\u5f85\u52b3\uff0c\u575a\u5b88\u90fd\u57ce\uff0c\u53ea\u987b\u5b88\u5230\u4e8c\u5341\u65e5\uff0c\u5510\u5175\u98df\u5c3d\uff0c\u4fbf\u4f1a\u9000\u8d70\u3002\u201d\u4ed6\u77e5\u9053\u5510\u5175\u5389\u5bb3\uff0c\u5b9a\u4e0b\u4e86\u53ea\u5b88\u4e0d\u6218\u7684\u8ba1\u7b56\uff0c\u4e8e\u662f\u5927\u96c6\u4eba\ue413\uff0c\u5728\u6781\u9690\u79d8\u4e4b\u5904\uff0c\u9020\u4e0b\u4e86\u4e00\u5ea7\u8ff7\u5bab\uff0c\u4e07\u4e00\u90fd\u57ce\u4e0d\u5b88\uff0c\u8fd8\u6709\u53ef\u4ee5\u9000\u907f\u7684\u5730\u65b9\u3002\u5f53\u65f6\u9ad8\u660c\u56fd\u529b\u6bb7\u5bcc\uff0c\u897f\u57df\u5de7\u5320\uff0c\u591a\u96c6\u4e8e\u5f7c\u3002\u8fd9\u5ea7\u8ff7\u5bab\u5efa\u9020\u5f97\u66f2\u6298\u5947\u5e7b\u4e4b\u6781\uff0c\u56fd\u5185\u7684\u73cd\u5947\u5b9d\u7269\uff0c\u5c3d\u6570\u85cf\u5728\u5bab\u4e2d\u3002\u97a0\u6587\u6cf0\u5fc3\u60f3\uff0c\u4fbf\u7b97\u5510\u519b\u653b\u8fdb\u4e86\u8ff7\u5bab\uff0c\u4e5f\u672a\u5fc5\u80fd\u627e\u5230\u6211\u7684\u6240\u5728\u3002 \u3000\u3000\u4faf\u541b\u96c6\u66fe\u8ddf\u674e\u9756\u5b66\u4e60\u5175\u6cd5\uff0c\u5584\u80fd\u7528\u5175\uff0c\u4e00\u8def\u4e0a\u52bf\u5982\u7834\u7af9\uff0c\u6e21\u8fc7\u4e86\u5927\u6c99\u6f20\u3002\u97a0\u6587\u6cf0\u542c\u5f97\u5510\u671d\u5927\u519b\u5230\u6765\uff0c\u5fe7\u60e7\u4e0d\u77e5\u6240\u4e3a\uff0c\u5c31\u6b64\u5413\u6b7b\u3002\u4ed6\u513f\u5b50\u97a0\u667a\u76db\u7ee7\u7acb\u4e3a\u56fd\u738b\u3002\u4faf\u541b\u96c6\u7387\u9886\u5927\u519b\uff0c\u653b\u5230\u57ce\u4e0b\uff0c\u8fde\u6253\u51e0\u4ed7\uff0c\u9ad8\u660c\u519b\u90fd\u662f\u5927\u8d25\u3002\u5510\u519b\u6709\u4e00\u79cd\u653b\u57ce\u9ad8\u8f66\uff0c\u9ad8\u5341\u4e08\uff0c\u56e0\u4e3a\u9ad8\u5f97\u50cf\u9e1f\u5de2\u4e00\u822c\uff0c\u6240\u4ee5\u540d\u4e3a\u5de2\u8f66\u3002\u8fd9\u5de2\u8f66\u63a8\u5230\u57ce\u8fb9\uff0c\u519b\u58eb\u5c45\u9ad8\u4e34\u4e0b\uff0c\u6295\u77f3\u5c04\u7bad\uff0c\u9ad8\u660c\u519b\u96be\u4ee5\u62b5\u5fa1\u3002\u97a0\u667a\u76db\u6765\u4e0d\u53ca\u9003\u8fdb\u8ff7\u5bab\uff0c\u90fd\u57ce\u5df2\u88ab\u653b\u7834\uff0c\u53ea\u5f97\u6295\u964d\u3002\u9ad8\u660c\u56fd\u81ea\u97a0\u5609\u7acb\u56fd\uff0c\u4f20\u4e5d\u4e16\uff0c\u5171\u4e00\u767e\u4e09\u5341\u56db\u5e74\uff0c\u81f3\u5510\u8d1e\u89c2\u5341\u56db\u5e74\u800c\u4ea1\u3002\u5f53\u65f6\u56fd\u571f\u4e1c\u897f\u516b\u767e\u91cc\uff0c\u5357\u5317\u4e94\u767e\u91cc\uff0c\u5b9e\u662f\u897f\u57df\u7684\u5927\u56fd\u3002 \u3000\u3000\u4faf\u541b\u96c6\u4fd8\u864f\u4e86\u56fd\u738b\u97a0\u667a\u76db\u53ca\u5176\u6587\u6b66\u767e\u5b98\uff0c\u5927\u65cf\u8c6a\u6770\uff0c\u56de\u5230\u957f\u5b89\uff0c\u5c06\u8ff7\u5bab\u4e2d\u6240\u6709\u7684\u73cd\u5b9d\u4e5f\u90fd\u641c\u4e86\u53bb\u3002\u5510\u592a\u5b97\u8bf4\uff0c\u9ad8\u660c\u56fd\u4e0d\u670d\u6c49\u5316\uff0c\u4e0d\u77e5\u4e2d\u534e\u4e0a\u56fd\u6587\u7269\u8863\u51a0\u7684\u597d\u5904\uff0c\u4e8e\u662f\u8e22\u4e86\u5927\u6279\u6c49\u4eba\u7684\u4e66\u7c4d\u3001\u8863\u670d\u3001\u7528\u5177\u3001\u4e50\u5668\u7b49\u7ed9\u9ad8\u660c\u3002\u9ad8\u660c\u4eba\u79c1\u4e0b\u8bf4\uff1a \u3000\u3000\u201c\u91ce\u9e21\u4e0d\u80fd\u5b66\u9e70\u98de\uff0c\u5c0f\u9f20\u4e0d\u80fd\u5b66\u732b\u53eb\uff0c\u4f60\u4eec\u4e2d\u534e\u6c49\u4eba\u7684\u4e1c\u897f\u518d\u597d\uff0c\u6211\u4eec\u9ad8\u660c\u91ce\u4eba\u4e5f\u662f\u4e0d\u559c\u6b22\u3002\u201d\u5c06\u5510\u592a\u5b97\u6240\u8d50\u7684\u4e66\u7c4d\u6587\u7269\u3001\u8bf8\u822c\u7528\u5177\u3001\u4ee5\u53ca\u4f5b\u50cf\u3001\u5b54\u5b50\u50cf\u3001\u9053\u6559\u7684\u8001\u541b\u50cf\u7b49\u7b49\u90fd\u653e\u5728\u8ff7\u5bab\u4e4b\u4e2d\uff0c\u8c01\u4e5f\u4e0d\u53bb\u591a\u77a7\u4e0a\u4e00\u773c\u3002 \u3000\u3000\u5343\u4f59\u5e74\u6765\uff0c\u6c99\u6f20\u53d8\u8fc1\uff0c\u6811\u6728\u4e1b\u751f\uff0c\u8fd9\u672c\u6765\u5df2\u662f\u5341\u5206\u9690\u79d8\u7684\u53e4\u5bab\uff0c\u66f4\u52a0\u9690\u79d8\u4e86\u3002\u82e5\u4e0d\u662f\u6709\u5730\u56fe\u6307\u5f15\uff0c\u8c01\u4e5f\u627e\u5bfb\u4e0d\u5230\u3002\u73b0\u5728\u5f53\u5730\u6240\u5c45\u7684\u54c8\u8428\u514b\u4eba\uff0c\u548c\u53e4\u65f6\u7684\u9ad8\u660c\u4eba\u4e5f\u662f\u6beb\u4e0d\u76f8\u5e72\u3002 \u3000\u3000\u74e6\u8033\u62c9\u9f50\u5728\u4e2d\u539f\u65f6\u5b66\u6587\u5b66\u6b66\uff0c\u591a\u8bfb\u6c49\u4eba\u7684\u4e66\u7c4d\uff0c\u6240\u4ee5\u719f\u77e5\u5510\u4ee3\u53f2\u4e8b\u3002\u674e\u6587\u79c0\u867d\u662f\u6c49\u4eba\uff0c\u53cd\u800c\u534a\u70b9\u4e5f\u4e0d\u77e5\u9053\uff0c\u4e5f\u4e0d\u611f\u5174\u8da3\u3002\u5979\u542c\u74e6\u8033\u62c9\u9f50\u6c14\u606f\u6e10\u5f31\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u6b47\u6b47\u5427\uff0c\u522b\u8bf4\u4e86\u3002\u8fd9\u4e2a\u6c49\u4eba\u7687\u5e1d\u4e5f\u771f\u591a\u4e8b\uff0c\u4eba\u5bb6\u559c\u6b22\u600e\u6837\u8fc7\u65e5\u5b50\uff0c\u5c31\u7531\u4ed6\u4eec\u53bb\uff0c\u4f55\u5fc5\u52c9\u5f3a\uff1f\u5509\uff0c\u4f60\u5fc3\u91cc\u771f\u6b63\u559c\u6b22\u7684\uff0c\u5e38\u5e38\u5f97\u4e0d\u5230\u3002\u522b\u4eba\u786c\u8981\u7ed9\u4f60\u7684\uff0c\u5c31\u7b97\u597d\u5f97\u4e0d\u5f97\u4e86\uff0c\u6211\u4e0d\u559c\u6b22\uff0c\u7ec8\u7a76\u662f\u4e0d\u559c\u6b22\u3002\u201d \u3000\u3000\u74e6\u8033\u62c9\u9f50\u9053\uff1a\u201c\u963f\u79c0\uff0c\u6211\u2026\u2026\u6211\u5b64\u5355\u5f97\u5f88\uff0c\u4ece\u6765\u6ca1\u4eba\u966a\u6211\u8bf4\u8fc7\u8fd9\u4e48\u4e45\u7684\u8bdd\uff0c\u4f60\u80af\u2026\u2026\u80af\u966a\u7740\u6211\u4e48\uff1f\u201d\u674e\u6587\u79c0\u9053\uff1a\u201c\u5e08\u7236\uff0c\u6211\u5728\u8fd9\u91cc\u966a\u7740\u4f60\u3002\u201d\u74e6\u8033\u62c9\u9f50\u9053\uff1a\u201c\u6211\u5feb\u6b7b\u4e86\uff0c\u6211\u6b7b\u4e86\u540e\uff0c\u4f60\u5c31\u8981\u8d70\u4e86\uff0c\u6c38\u8fdc\u4e0d\u4f1a\u56de\u6765\u4e86\u3002\u201d\u674e\u6587\u79c0\u65e0\u8a00\u53ef\u7b54\uff0c\u53ea\u611f\u5230\u4e00\u9635\u51c4\u51c9\u4f24\u5fc3\uff0c\u4f38\u51fa\u53f3\u624b\u53bb\uff0c\u8f7b\u8f7b\u63e1\u4f4f\u4e86\u5e08\u7236\u7684\u5de6\u624b\uff0c\u53ea\u89c9\u4ed6\u7684\u624b\u638c\u5728\u6162\u6162\u51b7\u4e0b\u53bb\u3002 \u3000\u3000\u74e6\u8033\u62c9\u9f50\u9053\uff1a\u201c\u6211\u8981\u4f60\u6c38\u8fdc\u5728\u8fd9\u91cc\u966a\u6211\uff0c\u6c38\u8fdc\u4e0d\u79bb\u5f00\u6211\u2026\u2026\u201d \u3000\u3000\u4ed6\u4e00\u9762\u8bf4\uff0c\u53f3\u624b\u6162\u6162\u7684\u63d0\u8d77\uff0c\u62c7\u6307\u548c\u98df\u6307\u4e4b\u95f4\u63e1\u7740\u4e24\u679a\u6bd2\u9488\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u4e24\u679a\u6bd2\u9488\u5728\u4f60\u8eab\u4e0a\u8f7b\u8f7b\u4e00\u523a\uff0c\u4f60\u5c31\u6c38\u8fdc\u5728\u8ff7\u5bab\u91cc\u966a\u7740\u6211\uff0c\u4e5f\u4e0d\u4f1a\u79bb\u5f00\u6211\u4e86\u3002\u201d\u8f7b\u58f0\u9053\uff1a\u201c\u963f\u79c0\uff0c\u4f60\u53c8\u7f8e\u4e3d\u53c8\u6e29\u67d4\uff0c\u771f\u662f\u4e2a\u597d\u5973\u5b69\uff0c\u4f60\u6c38\u8fdc\u5728\u6211\u8eab\u8fb9\u966a\u7740\u3002\u6211\u4e00\u751f\u5bc2\u5bde\u5b64\u5355\u5f97\u5f88\uff0c\u8c01\u4e5f\u4e0d\u6765\u7406\u6211\u2026\u2026\u963f\u79c0\uff0c\u4f60\u771f\u4e56\uff0c\u771f\u662f\u4e2a\u597d\u5b69\u5b50\u2026\u2026\u201d \u3000\u3000\u4e24\u679a\u6bd2\u9488\u6162\u6162\u5411\u674e\u6587\u79c0\u79fb\u8fd1\uff0c\u9ed1\u6697\u4e4b\u4e2d\uff0c\u5979\u4ec0\u4e48\u4e5f\u770b\u4e0d\u89c1\u3002 \u3000\u3000\u74e6\u8033\u62c9\u9f50\u5fc3\u60f3\uff1a\u201c\u6211\u624b\u4e0a\u534a\u70b9\u529b\u6c14\u4e5f\u6ca1\u6709\u4e86\uff0c\u5f97\u6162\u6162\u7684\u523a\u5979\uff0c\u51fa\u624b\u5feb\u4e86\uff0c\u5979\u53ea\u8981\u4e00\u63a8\uff0c\u6211\u5c31\u518d\u4e5f\u523a\u5979\u4e0d\u5230\u4e86\u3002\u201d\u6bd2\u9488\u4e00\u5bf8\u4e00\u5bf8\u7684\u5411\u7740\u5979\u7684\u9762\u988a\u79fb\u8fd1\uff0c\u76f8\u8ddd\u53ea\u6709\u4e24\u5c3a\uff0c\u53ea\u6709\u4e00\u5c3a\u4e86\u2026\u2026 \u3000\u3000\u674e\u6587\u79c0\u4e1d\u6beb\u4e0d\u77e5\u9053\u6bd2\u9488\u79bb\u5f00\u81ea\u5df1\u5df2\u4e0d\u8fc7\u4e03\u516b\u5bf8\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u7236\uff0c\u963f\u66fc\u7684\u5988\u5988\uff0c\u5f88\u7f8e\u4e3d\u5417\uff1f\u201d \u3000\u3000\u74e6\u8033\u62c9\u9f50\u5fc3\u5934\u4e00\u9707\uff0c\u8bf4\u9053\uff1a\u201c\u963f\u66fc\u7684\u5988\u5988\u2026\u2026\u96c5\u4e3d\u4ed9\u2026\u2026\u201d\u7a81\u7136\u95f4\u5168\u8eab\u7684\u529b\u6c14\u6d88\u5931\u5f97\u65e0\u5f71\u65e0\u8e2a\uff0c\u63d0\u8d77\u4e86\u7684\u53f3\u624b\u5782\u4e86\u4e0b\u6765\uff0c\u4ed6\u4e00\u751f\u4e4b\u4e2d\uff0c\u518d\u4e5f\u6ca1\u6709\u529b\u6c14\u5c06\u53f3\u624b\u63d0\u8d77\u6765\u4e86\u3002 \u3000\u3000\u674e\u6587\u79c0\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u4e00\u76f4\u5f85\u6211\u5f88\u597d\uff0c\u6211\u4f1a\u6c38\u8fdc\u8bb0\u7740\u4f60\u3002\u201d \u3000\u3000\u5728\u901a\u5411\u7389\u95e8\u5173\u7684\u6c99\u6f20\u4e4b\u4e2d\uff0c\u4e00\u4e2a\u59d1\u5a18\u9a91\u7740\u4e00\u5339\u767d\u9a6c\uff0c\u5411\u4e1c\u7f13\u7f13\u800c\u884c\u3002 \u3000\u3000\u5979\u5fc3\u4e2d\u5728\u60f3\u7740\u548c\u54c8\u8428\u514b\u94c1\u5ef6\u90e8\u65cf\u4eba\u5206\u522b\u65f6\u4ed6\u4eec\u6240\u8bf4\u7684\u8bdd\uff1a\u82cf\u9c81\u514b\u9053\uff1a\u201c\u674e\u59d1\u5a18\uff0c\u4f60\u522b\u8d70\uff0c\u5728\u6211\u4eec\u8fd9\u91cc\u4f4f\u4e0b\u6765\u3002\u6211\u4eec\u8fd9\u91cc\u6709\u5f88\u597d\u7684\u5c0f\u4f19\u5b50\uff0c\u6211\u4eec\u7ed9\u4f60\u6311\u4e00\u4e2a\u6700\u597d\u7684\u505a\u4e08\u592b\u3002\u6211\u4eec\u8981\u9001\u4f60\u5f88\u591a\u725b\uff0c\u5f88\u591a\u7f8a\uff0c\u7ed9\u4f60\u642d\u6700\u597d\u7684\u5e10\u7bf7\u3002\u201d \u3000\u3000\u674e\u6587\u79c0\u7ea2\u7740\u8138\uff0c\u6447\u4e86\u6447\u5934\u3002 \u3000\u3000\u82cf\u9c81\u514b\u9053\uff1a\u201c\u4f60\u662f\u6c49\u4eba\uff0c\u90a3\u4e0d\u8981\u7d27\uff0c\u6c49\u4eba\u4e4b\u4e2d\u4e5f\u6709\u597d\u4eba\u7684\u3002 \u3000\u3000\u6c49\u4eba\u53ef\u4ee5\u8ddf\u54c8\u8428\u514b\u4eba\u7ed3\u5a5a\u5417\uff0c\u55ef\uff1f\u201d\u4ed6\u6414\u4e86\u6414\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u53bb\u95ee\u957f\u8001\u54c8\u535c\u62c9\u59c6\u3002\u201d \u3000\u3000\u54c8\u535c\u62c9\u59c6\u662f\u94c1\u5ef6\u90e8\u4e2d\u7cbe\u901a\u300a\u53ef\u5170\u7ecf\u300b\uff0c\u6700\u806a\u660e\u6700\u6709\u5b66\u95ee\u7684\u8001\u4eba\u3002 \u3000\u3000\u4ed6\u4f4e\u5934\u6c89\u601d\u4e86\u4e00\u4f1a\uff0c\u9053\uff1a\u201c\u6211\u662f\u4e2a\u5351\u5fae\u7684\u4eba\uff0c\u4ec0\u4e48\u4e5f\u4e0d\u61c2\u3002\u201d \u3000\u3000\u82cf\u9c81\u514b\u9053\uff1a\u201c\u5982\u679c\u6709\u5b66\u95ee\u7684\u54c8\u535c\u62c9\u59c6\u4e5f\u8bf4\u4e0d\u61c2\uff0c\u90a3\u4e48\u522b\u4eba\u662f\u66f4\u52a0\u4e0d\u61c2\u4e86\u3002\u201d\u54c8\u535c\u62c9\u59c6\u9053\uff1a\u201c\u300a\u53ef\u5170\u7ecf\u300b\u7b2c\u56db\u5341\u4e5d\u7ae0\u4e0a\u8bf4\uff1a\u2018\u4f17\u4eba\u554a\uff0c\u6211\u786e\u5df2\u4ece\u4e00\u7537\u4e00\u5973\u521b\u9020\u4f60\u4eec\uff0c\u6211\u4f7f\u4f60\u4eec\u6210\u4e3a\u8bb8\u591a\u6c11\u65cf\u548c\u5b97\u65cf\uff0c\u4ee5\u4fbf\u4f60\u4eec\u4e92\u76f8\u8ba4\u8bc6\u3002\u5728\u963f\u62c9\u770b\u6765\uff0c\u4f60\u4eec\u4e4b\u4e2d\u6700\u5c0a\u8d35\u7684\uff0c\u4fbf\u662f\u4f60\u4eec\u4e4b\u4e2d\u6700\u5584\u826f\u7684\u3002\u2019\u4e16\u754c\u4e0a\u5404\u4e2a\u6c11\u65cf\u548c\u5b97\u65cf\uff0c\u90fd\u662f\u771f\u795e\u963f\u62c9\u521b\u9020\u7684\u3002\u4ed6\u53ea\u8bf4\u51e1\u662f\u6700\u5584\u826f\u7684\uff0c\u4fbf\u662f\u6700\u5c0a\u8d35\u7684\u3002\u300a\u53ef\u5170\u7ecf\u300b\u7b2c\u56db\u7ae0\u4e0a\u8bf4\uff1a\u2018\u4f60\u4eec\u5f53\u4eb2\u7231\u8fd1\u90bb\u3001\u8fdc\u90bb\u3001\u4f34\u4fa3\uff0c\u5f53\u6b3e\u5f85\u65c5\u5ba2\u3002\u2019\u6c49\u4eba\u662f\u6211\u4eec\u7684\u8fdc\u90bb\uff0c\u5982\u679c\u4ed6\u4eec\u4e0d\u6765\u4fb5\u72af\u6211\u4eec\uff0c\u6211\u4eec\u8981\u5bf9\u4ed6\u4eec\u4eb2\u7231\uff0c\u6b3e\u5f85\u4ed6\u4eec\u3002\u201d \u3000\u3000\u82cf\u9c81\u514b\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u5f88\u5bf9\u3002\u6211\u4eec\u7684\u5973\u513f\u80fd\u5ac1\u7ed9\u6c49\u4eba\u4e48\uff1f\u6211\u4eec\u7684\u5c0f\u4f19\u5b50\uff0c\u80fd\u5a36\u6c49\u4eba\u7684\u59d1\u5a18\u5417\uff1f\u201d\u54c8\u535c\u62c9\u59c6\u9053\uff1a\u201c\u771f\u7ecf\u7b2c\u4e8c\u7ae0\u7b2c\u4e8c\u767e\u5eff\u4e00\u8282\u8bf4\uff1a\u2018\u4f60\u4eec\u4e0d\u8981\u5a36\u5d07\u62dc\u591a\u795e\u7684\u5987\u5973\uff0c\u76f4\u5230\u5979\u4eec\u4fe1\u9053\u3002\u4f60\u4eec\u4e0d\u8981\u628a\u81ea\u5df1\u7684\u5973\u513f\uff0c\u5ac1\u7ed9\u5d07\u62dc\u591a\u795e\u7684\u7537\u5b50\uff0c\u76f4\u5230\u4ed6\u4eec\u4fe1\u9053\u3002\u2019\u771f\u7ecf\u7b2c\u56db\u7ae0\u7b2c\u5eff\u4e09\u8282\u4e2d\uff0c\u4e25\u7981\u5a36\u6709\u4e08\u592b\u7684\u5987\u5973\uff0c\u4e0d\u8bb8\u5a36\u81ea\u5df1\u7684\u76f4\u7cfb\u4eb2\u5c5e\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u90fd\u662f\u5408\u6cd5\u7684\u3002\u4fbf\u662f\u5a36\u5974\u5a62\u548c\u4fd8\u864f\u4e5f\u53ef\u4ee5\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u548c\u6c49\u4eba\u5a5a\u5ac1\u5462\uff1f\u201d \u3000\u3000\u5f53\u54c8\u535c\u62c9\u59c6\u80cc\u8bf5\u300a\u53ef\u5170\u7ecf\u300b\u7684\u7ecf\u6587\u4e4b\u65f6\uff0c\u4f17\u65cf\u4eba\u90fd\u662f\u606d\u606d\u656c\u656c\u7684\u8083\u7acb\u503e\u542c\u3002\u7ecf\u6587\u66ff\u4ed6\u4eec\u89e3\u51b3\u7591\u96be\uff0c\u5927\u5bb6\u5fc3\u4e2d\u660e\u767d\u4e86\uff0c\u90fd\u8bf4\uff1a\u201c\u7a46\u5723\u7684\u6307\u793a\uff0c\u90a3\u662f\u518d\u4e5f\u4e0d\u4f1a\u9519\u7684\u3002\u201d\u6709\u4eba\u4fbf\u79f0\u8d5e\u54c8\u535c\u62c9\u59c6\u806a\u660e\u6709\u5b66\u95ee\uff1a\u201c\u6211\u4eec\u6709\u4ec0\u4e48\u4e8b\u60c5\u4e0d\u660e\u767d\uff0c\u53ea\u8981\u53bb\u95ee\u54c8\u535c\u62c9\u59c6\uff0c\u4ed6\u603b\u662f\u80fd\u597d\u597d\u7684\u6559\u5bfc\u6211\u4eec\u3002\u201d \u3000\u3000\u53ef\u662f\u54c8\u535c\u62c9\u59c6\u518d\u806a\u660e\u3001\u518d\u6709\u5b66\u95ee\uff0c\u6709\u4e00\u4ef6\u4e8b\u5374\u662f\u4ed6\u4e0d\u80fd\u89e3\u7b54\u7684\uff0c\u56e0\u4e3a\u5305\u7f57\u4e07\u8c61\u7684\u300a\u53ef\u5170\u7ecf\u300b\u4e0a\u4e5f\u6ca1\u6709\u7b54\u6848\uff1b\u5982\u679c\u4f60\u6df1\u6df1\u7231\u7740\u7684\u4eba\uff0c\u5374\u6df1\u6df1\u7684\u7231\u4e0a\u4e86\u522b\u4eba\uff0c\u6709\u4ec0\u4e48\u6cd5\u5b50\uff1f \u3000\u3000\u767d\u9a6c\u5e26\u7740\u5979\u4e00\u6b65\u6b65\u7684\u56de\u5230\u4e2d\u539f\u3002\u767d\u9a6c\u5df2\u7ecf\u8001\u4e86\uff0c\u53ea\u80fd\u6162\u6162\u7684\u8d70\uff0c\u4f46\u7ec8\u662f\u80fd\u56de\u5230\u4e2d\u539f\u7684\u3002\u6c5f\u5357\u6709\u6768\u67f3\u3001\u6843\u82b1\uff0c\u6709\u71d5\u5b50\u3001\u91d1\u9c7c\u2026\u2026\u6c49\u4eba\u4e2d\u6709\u7684\u662f\u82f1\u4fca\u52c7\u6b66\u7684\u5c11\u5e74\uff0c\u501c\u50a5\u6f47\u6d12\u7684\u5c11\u5e74\u2026\u2026\u4f46\u8fd9\u4e2a\u7f8e\u4e3d\u7684\u59d1\u5a18\u5c31\u50cf\u53e4\u9ad8\u660c\u56fd\u4eba\u90a3\u6837\u56fa\u6267\uff1a\u201c\u90a3\u90fd\u662f\u5f88\u597d\u5f88\u597d\u7684\uff0c\u53ef\u662f\u6211\u504f\u4e0d\u559c\u6b22\u3002\u201d \u3000\u3000\uff08\u5b8c\uff09"}),showOnHomepage:!0}]}}]);