"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[45123],{9695:(e,n,s)=>{s.r(n),s.d(n,{default:()=>a});var t=s(74848);const a=[{name:"\u6e58\u5983\u5251",content:(0,t.jsx)(t.Fragment,{children:"\u66ae\u8272\u82cd\u832b\u2014\u2014\u3002 \u3000\u3000\u843d\u65e5\u7684\u4f59\u8f89\uff0c\u5c06\u5929\u7554\u6620\u5f71\u5f97\u591a\u5f69\u800c\u7eda\u4e3d\uff0c\u65e0\u4eba\u7684\u5c71\u9053\u4e0a\uff0c\u6f47\u6d12\u800c\u633a\u79c0\u7684\u9a91\u58eb\uff0c\u4e5f\u88ab\u8fd9\u79cb \u65e5\u7684\u665a\u971e\uff0c\u6620\u5f71\u5f97\u66f4\u6f47\u6d12\u800c\u633a\u79c0\u4e86\u3002 \u3000\u3000\u6ca1\u6709\u708a\u70df\uff0c\u56e0\u4e3a\u8fd9\u91cc\u5e76\u6ca1\u6709\u4f9d\u7740\u5c71\u9e93\u800c\u7ed3\u5e90\u7684\u4eba\u5bb6\uff0c\u5927\u5730\u662f\u5bc2\u9759\u7684\uff0c\u751a\u81f3\u8fd8\u6709\u4e9b\u6c89\u91cd\u7684 \u610f\u5473\u3002 \u201c\u4eca\u5929\u8be5\u4f1a\u6709\u6708\u4eae\u5427\u2014\u2014\u201d\u9a6c\u4e0a\u7684\u9a91\u58eb\u843d\u5bde\u5730\u6325\u52a8\u7740\u9a6c\u97ad\uff0c\u5583\u5583\u5730\u4f4e\u8bed\u7740\uff0c\u82f1\u4fca\u7684\u9762 \u5e9e\uff0c\u56e0\u7740\u592a\u591a\u7684\u98ce\u5c18\u4e4b\u8272\uff0c\u800c\u4f7f\u4eba\u770b\u8d77\u6765\u6709\u4e00\u79cd\u8427\u7d22\u7684\u611f\u89c9\uff0c\u8584\u8584\u7684\u5634\u5507\uff0c\u7d27\u95ed\u6210\u4e00\u9053\u4e24\u7aef \u4e0b\u5f2f\u7684\u5f27\u7ebf\uff0c\u5634\u89d2\u4e0a\u5e26\u7740\u7684\u662f\u4e00\u4e9b\u5632\u5f04\uff0c\u548c\u4e00\u4e9b\u538c\u5026\u3002 \u3000\u3000\u4e5f\u8bb8\u662f\u4ed6\u5bf9\u4e16\u754c\u4e0a\u7f8e\u4e3d\u7684\u548c\u4e11\u6076\u7684\u4e8b\u90fd\u770b\u5f97\u592a\u591a\u4e86\u5427\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u5fae\u772f\u7740\u773c\uff0c\u4efb\u51ed\u80ef\u4e0b\u7684\u9a6c\u5728\u8fd9\u65e0\u4eba\u7684\u5c71\u9053\u4e0a\u7f13\u7f13\u8e31\u7740\u6b65\u5b50\uff0c\u9a6c\u8e44\u6572\u7740\u5c71\u8def\u4e0a\u7684\u77f3 \u5b50\u6240\u53d1\u51fa\u7684\u58f0\u97f3\uff0c\u6df7\u5408\u4e86\u4ed6\u8170\u7554\u7684\u957f\u5251\u6572\u5728\u9a6c\u978d\u4e0a\u7684\u58f0\u97f3\uff0c\u5f62\u6210\u4e86\u4e00\u79cd\u867d\u4e0d\u60a6\u8033\uff0c\u4f46\u6709\u8282\u594f \u7684\u97f3\u4e50\u3002 \u3000\u3000\u8fdc\u5904\uff0c\u4e00\u9635\u79cb\u9e26\u98de\u8d77\u2014\u2014\u4ed6\u5fae\u5fae\u62ac\u4e86\u62ac\u773c\u76ae\uff0c\u7709\u5fc3\u5fae\u76b1\u4e86\u76b1\uff0c\u7136\u540e\u4ecd\u7136\u5408\u8d77\u773c\u6765\uff0c\u4f3c\u4e4e \u662f\u60f3\u8d77\u4e86\u4ec0\u4e48\uff0c\u53c8\u4f3c\u4e4e\u662f\u53d1\u73b0\u4e86\u4ec0\u4e48\uff0c\u53ea\u662f\u4ed6\u5bf9\u4ed6\u81ea\u5df1\u6240\u60f3\u8d77\u7684\uff0c\u6216\u662f\u53d1\u73b0\u7684\u4e8b\uff0c\u4e1d\u6beb\u6ca1\u6709 \u653e\u5728\u5fc3\u4e0a\u800c\u5df2\u3002 \u3000\u3000\u66ae\u8272\u8d8a\u6765\u8d8a\u91cd\uff0c\u5165\u5c71\u4e5f\u8d8a\u6765\u8d8a\u6df1\u2014\u2014\u591c\u5df2\u7ecf\u6765\u4e86\uff0c\u5927\u5730\u4e0a\u4e00\u7247\u9ed1\u6697\uff0c\u56e0\u4e3a\u51fa\u4e4e\u610f\u6599\u4e4b\u5916 \u7684\uff0c\u8fd9\u4e2a\u79cb\u5929\u7684\u665a\u4e0a\u5c45\u7136\u6ca1\u6709\u6708\u4eae\u3002 \u3000\u3000\u5c71\u9053\u8d8a\u53d1\u9661\u659c\u3002\u72ed\u5c0f\u3001\u5f2f\u66f2\u800c\u9661\u659c\u7684\u5c71\u9053\uff0c\u5e76\u6ca1\u6709\u4f7f\u8fd9\u4e00\u4eba\u4e00\u9a6c\u9732\u51fa\u4e1d\u6beb\u8fdf\u6ede\uff0c\u4ed6\u4eec\u4ecd \u7136\u662f\u4f9d\u7740\u4e0d\u53d8\u7684\u901f\u5ea6\u884c\u8d70\u7740\u3002 \u3000\u3000\u6e10\u6e10\uff0c\u6df1\u5c71\u91cc\u5f00\u59cb\u6709\u4e86\u5404\u79cd\u58f0\u97f3\uff0c\u79cb\u866b\u7684\u591c\u9e23\uff0c\u7350\u5154\u7684\u5954\u8dd1\uff0c\u5f52\u9e26\u7684\u98de\u7fd4\u2014\u2014\u7a81\u5730\uff0c\u5728 \u8fd9\u8bb8\u591a\u79cd\u58f0\u97f3\u4e4b\u4e2d\uff0c\u6709\u53e6\u4e00\u79cd\u5947\u5f02\u7684\u58f0\u97f3\u53d1\u51fa\uff0c\u90a3\u662f\u50cf\u8702\u7fa4\u98de\u8d77\u65f6\u6240\u53d1\u51fa\u7684\u58f0\u97f3\uff0c\u4f46\u662f\u6240\u5e26 \u8d77\u7684\u98ce\u58f0\uff0c\u5374\u53c8\u8fdc\u6bd4\u8702\u7fa4\u5927\u3002 \u3000\u3000\u9a6c\u4e0a\u7684\u9a91\u58eb\u5fae\u772f\u7740\u7684\u773c\u775b\u4e5f\u7a81\u5730\u5f20\u5f00\uff0c\u50cf\u662f\u4e24\u9053\u7535\u5149\uff0c\u5728\u9ed1\u591c\u6df1\u5c71\u7684\u4e1b\u6797\u91cc\u6253\u4e86\u4e00\u4e2a\u5708 \u5b50\uff0c\u5634\u89d2\u4e00\u626c\uff0c\u91cd\u91cd\u5730\u53d1\u51fa\u4e00\u58f0\u51b7\u7b11\u3002 \u3000\u3000\u4e5f\u8bb8\u4ed6\u8fd9\u58f0\u51b7\u7b11\u5e76\u6ca1\u6709\u610f\u5473\u7740\u4ec0\u4e48\uff0c\u4f46\u662f\u4ed6\u9762\u4e0a\u7684\u795e\u8272\uff0c\u5374\u4f7f\u4eba\u6709\u4e00\u79cd\u51db\u7136\u7684\u611f\u89c9\uff0c\u53ea \u662f\u6df1\u5c71\u5bc2\u5bc2\uff0c\u53c8\u6709\u8c01\u770b\u5f97\u89c1\u4ed6\u9762\u4e0a\u7684\u795e\u8272\u2014\u2014\u51b7\u7b11\u58f0\u65b9\u81ea\u5c71\u6797\u95f4\u6d88\u5931\uff0c\u7126\u96f7\u4f3c\u5730\u4e00\u58f0\u66b4\u559d\uff0c \u5374\u53c8\u81ea\u5c71\u6797\u95f4\u53d1\u51fa\uff0c\u58f0\u97f3\u4f4e\u6c89\u800c\u91cd\u6d4a\uff0c\u542c\u8d77\u6765\u50cf\u6709\u6839\u6c89\u91cd\u7684\u9f13\u69cc\uff0c\u6572\u5728\u4f60\u7684\u5fc3\u91cc\u3002 \u3000\u3000\u9a6c\u4e0a\u7684\u9a91\u58eb\u9762\u8272\u5fae\u53d8\uff0c\u53cc\u76ee\u5fae\u4e00\u987e\u76fc\u3002 \u3000\u3000\u84e6\u5730\u767e\u5341\u4ef6\u6697\u5668\uff0c\u631f\u7740\u52b2\u8361\u7684\u98ce\u58f0\uff0c\u4ece\u5c71\u6797\u7684\u56db\u5468\u51fb\u5411\u9a6c\u4e0a\u7684\u9a91\u58eb\u3002 \u3000\u3000\u6697\u5668\u6765\u5f97\u90a3\u4e48\u5feb\uff0c\u5728\u559d\u58f0\u5c06\u4f4f\u672a\u4f4f\u7684\u90a3\u4e00\u5239\u90a3\uff0c\u5df2\u7ecf\u5feb\u51fb\u5728\u9a6c\u4e0a\u9a91\u58eb\u8eab\u4e0a\uff0c\u770b\u8d77\u6765\uff0c\u90a3 \u51e0\u4e4e\u662f\u65e0\u6cd5\u8eb2\u907f\u7684\u3002\u56e0\u4e3a\u90a3\u662f\u8fd9\u6837\u5730\u7a81\u5982\u5176\u6765\uff0c\u8fd9\u6837\u5730\u731d\u4e0d\u53ca\u9632\uff0c\u4f3c\u4e4e\u6ca1\u6709\u4efb\u4f55\u4eba\u7684\u80fd\u529b\u80fd \u907f\u5f00\u8fd9\u4e9b\u6697\u5668\u3002 \u3000\u3000\u8fd9\u4e00\u5239\u90a3\uff0c\u53ef\u4ee5\u8bf4\u662f\u51b3\u5b9a\u6b66\u6797\u4eca\u540e\u6570\u5341\u5e74\u547d\u8fd0\u7684\u4e00\u4e2a\u91cd\u5927\u7684\u5173\u952e\uff0c\u56e0\u4e3a\u8fd9\u9a6c\u4e0a\u9a91\u58eb\u7684 \u751f\u3001\u6b7b\u3001\u5b58\u3001\u4ea1\uff0c\u65ad\u7136\u5730\u53ef\u4ee5\u5f71\u54cd\u5230\u6b66\u6797\u7684\u547d\u8fd0\u3002 \u3000\u3000\u5728\u8fd9\u79cd\u4e25\u91cd\u7684\u5173\u5934\uff0c\u9a6c\u4e0a\u7684\u9a91\u58eb\u53ef\u663e\u793a\u51fa\u4e86\u4ed6\u8d85\u51e1\u5165\u5723\u7684\u6b66\u529f\u3002 \u3000\u3000\u4ed6\u4ecd\u7136\u7a33\u5982\u5c71\u5cb3\u822c\u5730\u5750\u5728\u9a6c\u4e0a\uff0c\u8138\u4e0a\u4ecd\u7136\u662f\u5e26\u7740\u90a3\u79cd\u6de1\u6de1\u7684\u5632\u5f04\u548c\u538c\u5026\u7684\u795e\u8272\uff0c\u53cc\u81c2\u770b \u4f3c\u7f13\u6162\u7684\u62a1\u8d77\uff0c\u5947\u602a\u7684\u662f\u90a3\u4e9b\u631f\u7740\u65e0\u6bd4\u5f3a\u52b2\u7684\u98ce\u58f0\uff0c\u4ee5\u65e0\u6bd4\u901f\u5ea6\u51fb\u5411\u8eab\u4e0a\u7684\u6697\u5668\uff0c\u50cf\u662f\u7a81\u7136 \u53d7\u4e86\u4e00\u79cd\u4e0d\u53ef\u6297\u62d2\u7684\u529b\u91cf\u7684\u5438\u5f15\uff0c\u5728\u4e2d\u9014\u7a81\u7136\u6539\u53d8\u4e86\u65b9\u5411\uff0c\u800c\u6295\u5411\u4ed6\u53cc\u81c2\u6240\u62a1\u8d77\u7684\u534a\u5706\u4e4b \u5185\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u6643\u773c\u4e4b\u95f4\uff0c\u98de\u8757\u822c\u7684\u767e\u5341\u4ef6\u6697\u5668\uff0c\u7a81\u7136\u53c8\u6d88\u58f0\u706d\u8ff9\u4e86\uff0c\u5728\u90a3\u5339\u9a6c\u8eab\u7684\u4e24\u4fa7\uff0c\u96f6\u4e71 \u5730\u6563\u5e03\u7740\u4e00\u4e9b\u6b8b\u65ad\u7684\u9556\u7bad\u3002 \u3000\u3000\u4ed6\u8fd9\u79cd\u60ca\u4eba\u7684\u624b\u6cd5\uff0c\u7684\u786e\u662f\u4e0d\u53ef\u601d\u8bae\u7684\uff0c\u4f46\u662f\u4ed6\u81ea\u5df1\uff0c\u4ecd\u7136\u662f\u6f20\u7136\u7684\u3002 \u3000\u3000\u7f13\u7f13\u5730\uff0c\u4ed6\u52d2\u4f4f\u4e86\u9a6c\u7f30\uff0c\u773c\u5149\u61d2\u6563\u5730\u5411\u56db\u5468\u626b\u89c6\u7740\uff1a\u201c\u4eca\u5929\u53c8\u662f\u54ea\u4e00\u8def\u7684\u670b\u53cb\u6765\u627e\u6211\u59d3 \u4ec7\u7684\u6666\u6c14\uff1f\u201d \u3000\u3000\u4ed6\u51b7\u7b11\u7740\uff0c\u50cf\u662f\u5bf9\u8fd9\u79cd\u4e8b\u65e9\u5df2\u53f8\u7a7a\u89c1\u60ef\u4e86\uff0c\u6f20\u7136\u4ed6\u8bf4\uff1a\u201c\u5404\u4f4d\u65e2\u7136\u6709\u79cd\uff0c\u4e5f\u8be5\u51fa\u6765\u4eae\u4eae \u76f8\u5440\u3002\u201d \u3000\u3000\u8bed\u58f0\u65b9\u843d\uff0c\u5c0f\u5f84\u65c1\u7684\u5c71\u6797\u91cc\uff0c\u7206\u53d1\u4e86\u4e00\u8fde\u4e32\u7684\u7b11\u58f0\u3002 \u3000\u3000\u968f\u7740\u8fd9\u7b11\u58f0\uff0c\u5c71\u6797\u91cc\u63a0\u51fa\u5341\u6570\u6761\u8eab\u5f71\uff0c\u51e0\u4e4e\u662f\u540c\u4e00\u52a8\u4f5c\uff0c\u5728\u8fd9\u4e00\u4eba\u4e00\u9a6c\u7684\u56db\u4fa7\uff0c\u5e03\u4e0b\u4e00 \u9053\u5708\u5b50\u3002 \u3000\u3000\u201c\u600e\u4e48\u4eca\u5929\u53ea\u6709\u8fd9\u4e48\u51e0\u4f4d\u2014\u2014\u201d\u9a6c\u4e0a\u7684\u9a91\u58eb\u5632\u5f04\u4ed6\u8bf4\u3002\u56db\u5468\u662f\u9ed1\u6697\u7684\uff0c\u7b49\u5230\u4ed6\u4ece\u9ed1\u6697\u4e2d \u8fa8\u51fa\u8fd9\u81ea\u6811\u6797\u4e2d\u63a0\u51fa\u7684\u8eab\u5f71\u662f\u8c01\u4e4b\u540e\uff0c\u4ed6\u8bed\u6c14\u4e2d\u7684\u5632\u5f04\uff0c\u663e\u7136\u5730\u51cf\u5c11\u4e86\uff0c\u63a5\u7740\u8bf4\uff1a\u201c\u5662\uff0c\u60f3 \u4e0d\u5230\uff0c\u60f3\u4e0d\u5230\uff0c\u539f\u6765\u79f0\u96c4\u6b66\u6797\u7684\u4e03\u5251\u4e09\u97ad\uff0c\u4eca\u65e5\u5168\u6765\u9f50\u4e86\uff01\u201d \u3000\u3000\u201c\u9601\u4e0b\u679c\u7136\u597d\u773c\u529b\uff0c\u8d2b\u9053\u59d3\u67f3\uff0c\u627f\u6c5f\u6e56\u670b\u53cb\u62ac\u7231\uff0c\u4e5f\u628a\u6211\u5728\u2018\u4e03\u5251\u4e09\u97ad\u2019\u91cc\u7b97\u4e0a\u4e00 \u4efd\u3002\u201d\u7ad9\u5728\u9a6c\u9996\u524d\u7684\u7626\u957f\u9053\u4eba\uff0c\u6b63\u662f\u5ddd\uff0c\u9ed4\u4e00\u5e26\u7684\u6b66\u6797\u9b41\u9996\uff0c\u5df4\u5c71\u5251\u5ba2\u67f3\u590d\u660e\u3002 \u3000\u3000\u4ed6\u6e05\u6717\u7684\u53e3\u58f0\uff0c\u5728\u9ed1\u591c\u4e2d\u4f20\u51fa\u8001\u8fdc\uff0c\u76ee\u5149\u4e00\u62ac\uff0c\u5728\u9a6c\u4e0a\u9a91\u58eb\u7684\u9762\u5e9e\u4e0a\u8f7b\u8f7b\u4e00\u77a5\uff0c\u63a5\u7740\u8bf4 \u9053\uff1a\u201c\u8d2b\u9053\u4e45\u4ef0\u2018\u4ec7\u5148\u751f\uff0c\u7684\u5927\u540d\uff0c\u4eca\u65e5\u5f97\u7779\uff0c\u5b9e\u5728\u662f\u5feb\u6170\u751f\u5e73\uff0c\u5c24\u5176\u662f\u2019\u4ec7\u5148\u751f\u2019\u65b9\u624d\u6240 \u65bd\u7684\u90a3\u4e00\u624b\u2018\u4e07\u6d41\u5f52\u5b97\u2019\uff0c\u786e\u5b9e\u5df2\u5230\u4e86\u4f20\u8bf4\u4e2d\u2018\u6444\u91d1\u5438\u94c1\u2019\u7684\u5883\u754c\u3002\u201d\u4ed6\u5e72\u7b11\u4e86\u4e24\u58f0\uff0c\u9053\uff0c \u201c\u8d2b\u9053\u6709\u7f18\uff0c\u80fd\u4f1a\u5230\u5929\u4e0b\u7b2c\u4e00\u5947\u4eba\u2014\u2014\u201d\u9a6c\u4e0a\u7684\u9a91\u58eb\u51b7\u7b11\u7740\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211 \u5c31\u662f\u4ec7\u72ec\u3002\u201d \u3000\u3000\u4ed6\u8138\u4e0a\u77ac\u5373\u6062\u590d\u4e86\u90a3\u79cd\u6f20\u7136\u7684\u795e\u8272\uff0c\u201c\u9601\u4e0b\u773c\u5149\u5012\u4e5f\u4e0d\u9519\u3002\u201d \u3000\u3000\u4ed6\u7565\u4e00\u505c\u987f\uff0c\u53cc\u76ee\u7535\u4e5f\u4f3c\u5730\u5f20\u5f00\uff0c\u77aa\u5728\u5df4\u5c71\u5251\u5ba2\u8138\u4e0a\uff0c\u51b7\u7136\u9053\uff1a\u201c\u4e03\u5251\u4e09\u97ad\u90fd\u662f\u6b66\u6797\u4e2d \u5149\u660e\u78ca\u843d\u7684\u4fa0\u58eb\uff0c\u4eca\u65e5\u5374\u5077\u5077\u5730\u8eb2\u5728\u6df1\u5c71\u91cc\u5411\u6211\u653e\u51b7\u7bad\uff0c\u53ef\u771f\u6559\u6211\u5bf9\u9601\u4e0b\u4eec\u8fd9\u4e9b\u6b66\u6797\u4e2d\u89c6\u4e3a \u6cf0\u6597\u7684\u4fa0\u58eb\u4eec\u5931\u671b\u5f97\u5f88\u3002\u201d \u3000\u3000\u5df4\u5c71\u5251\u5ba2\u76ee\u5149\u4e00\u77ac\uff0c\u907f\u5f00\u4e86\u2018\u4ec7\u72ec\u2019\u7684\u76ee\u5149\uff0c\u6b63\u8003\u8651\u7740\u8be5\u5982\u4f55\u56de\u7b54\uff0c\u4ed6\u8eab\u4fa7\u53e6\u4e00\u4e2a\u66f4\u7626 \u957f\u7684\u9ed1\u8863\u4eba\uff0c\u80a9\u5934\u4e00\u6643\uff0c\u8eab\u5f62\u5982\u884c\u4e91\u6d41\u6c34\u822c\u63a0\u4e86\u8fc7\u6765\uff0c\u51b7\u7b11\u7740\u9053\uff1a\u201c\u59d3\u4ec7\u7684\uff0c\u4f60\u4e5f\u662f\u806a\u660e \u4eba\uff0c\u8be5\u4e5f\u77e5\u9053\uff0c\u5bf9\u4ed8\u5351\u9119\u7684\u4eba\u6700\u597d\u4e5f\u7528\u5351\u9119\u7684\u624b\u6bb5\u3002\u201d\u4ed6\u5c16\u523b\u4ed6\u8bf4\uff0c\u201c\u4e0d\u9519\uff0c\u4eca\u5929\u6211\u4eec\u7528\u7684 \u4e0d\u662f\u5149\u660e\u6b63\u5927\u7684\u624b\u6bb5\uff0c\u53ef\u662f\u7528\u8fd9\u79cd\u624b\u6bb5\u6765\u5bf9\u4ed8\u9601\u4e0b\uff0c\u6211\u59d3\u6bdb\u7684\u8fd8\u89c9\u5f97\u592a\u5ba2\u6c14\u4e86\u5462\uff01\u201d \u3000\u3000\u88ab\u5f53\u4eca\u6b66\u6797\u4e2d\u89c6\u4e3a\u86c7\u874e\u7684\u201c\u4ec7\u5148\u751f\u201d\u4ec7\u72ec\uff0c\u81ea\u51fa\u9053\u4ee5\u6765\uff0c\u65e0\u8bba\u9ed1\u767d\u4e24\u9053\uff0c\u89c1\u4e86\u4ed6\u90fd\u662f\u656c \u800c\u8fdc\u4e4b\uff0c\u907f\u4e4b\u552f\u6050\u4e0d\u53ca\uff0c\u5728\u8fd9\u79cd\u73af\u5883\u4e0b\uff0c\u4ed6\u7684\u4e00\u8eab\u65e0\u53ef\u6bd4\u654c\u7684\u6b66\u529f\uff0c\u517b\u6210\u4e86\u4ed6\u521a\u610e\u81ea\u7528\u3001\u4efb \u6027\u800c\u4e3a\u7684\u6027\u683c\u3002 \u3000\u3000\u5728\u4ed6\u7684\u60f3\u6cd5\u4e2d\uff0c\u4ed6\u6240\u505a\u7684\u4e8b\uff0c\u90fd\u662f\u53ef\u4ee5\u7528\u9053\u7406\u6765\u89e3\u91ca\u7684\uff0c\u53ef\u662f\u4ed6\u5374\u4e0d\u77e5\u9053\uff0c\u4ed6\u6240\u4f5c\u6240 \u4e3a\uff0c\u4e0d\u4f46\u6709\u8bb8\u591a\u662f\u8fdd\u80cc\u4e86\u5929\u7406\u4eba\u60c5\uff0c\u66f4\u6709\u8bb8\u591a\u72af\u4e86\u6b66\u6797\u5927\u5fcc\uff0c\u9664\u4e86\u4ed6\u81ea\u5df1\u4e4b\u5916\uff0c\u6050\u6015\u5f88\u96be\u627e \u51fa\u7b2c\u4e8c\u4e2a\u4eba\u4f1a\u8ba4\u4e3a\u4ed6\u662f\u6b63\u76f4\u7684\uff0c\u53ea\u662f\u4ed6\u81ea\u5df1\u5e76\u4e0d\u77e5\u9053\u8fd9\u70b9\u800c\u5df2\u3002 \u3000\u3000\u8fd9\u5c31\u662f\u4eba\u7c7b\u6f5c\u5728\u7684\u5351\u52a3\u6027\u683c\uff0c\u5bf9\u522b\u4eba\u7684\u8fc7\u5931\uff0c\u8fdc\u6bd4\u5bf9\u81ea\u5df1\u770b\u5f97\u6e05\u695a\u3002 \u3000\u3000\u8bb8\u591a\u5e74\u6765\uff0c\u6b66\u6797\u4e2d\u4eba\u4e0d\u6b62\u4e00\u6b21\u5730\u60f3\u9664\u53bb\u4ed6\uff0c\u53ef\u662f\u4ed6\u6b66\u529f\u592a\u9ad8\uff0c\u6bcf\u6b21\u90fd\u4ee4\u5bf9\u65b9\u94e9\u7fbd\u800c\u5f52\u3002 \u3000\u3000\u8fd9\u4e48\u4e00\u6765\uff0c\u4ed6\u7684\u6027\u683c\u81ea\u7136\u4e5f\u66f4\u72c2\u50b2\uff0c\u884c\u4e8b\u4e5f\u81ea\u7136\u66f4\u4efb\u6027\u4e86\u3002 \u3000\u3000\u201c\u4ec7\u5148\u751f\u201d\u7684\u6076\u540d\uff0c\u4e00\u5929\u6bd4\u4e00\u5929\u5730\u4f20\u5f97\u66f4\u5927\uff0c\u66f4\u8fdc\uff0c\u6709\u4e9b\u4ed6\u6240\u505a\u7684\u4e8b\uff0c\u5373\u4f7f\u4ed6\u662f\u5b8c\u5168\u5730 \u6ca1\u6709\u534a\u70b9\u8fc7\u9519\uff0c\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u4e5f\u53d8\u5f97\u662f\u4ed6\u7684\u9519\u4e86\u3002 \u3000\u3000\u8fd9\u5f53\u7136\u662f\u4e0d\u516c\u5e73\u7684\uff0c\u4f46\u662f\u9020\u6210\u8fd9\u79cd\u503e\u5411\u7684\u56e0\u7d20\uff0c\u9664\u4e86\u4ed6\u81ea\u5df1\uff0c\u53c8\u80fd\u602a\u8c01\u5462\uff1f \u3000\u3000\u4e8e\u662f\uff0c\u5206\u5e03\u5728\u4e2d\u539f\u6b66\u6797\u6bcf\u4e00\u7701\u7684\u8c6a\u58eb\uff0c\u5168\u90fd\u5bf9\u4ed6\u8d77\u4e86\u65e0\u6bd4\u7684\u4ec7\u89c6\uff0c\u88ab\u4e2d\u539f\u6b66\u6797\u5c0a\u4e3a\u6cf0\u5c71 \u5317\u6597\u7684\u201c\u4e03\u5251\u4e09\u97ad\u201d\uff0c\u4e5f\u7ecf\u8fc7\u8bb8\u591a\u6b21\u7b79\u5546\uff0c\u8ba1\u5212\u7740\u9664\u53bb\u8fd9\u4e2a\u6b66\u6797\u4e2d\u7684\u201c\u8d25\u7c7b\u201d\u3002 \u3000\u3000\u5df4\u5c71\u5251\u5ba2\u67f3\u590d\u660e\uff0c\u662f\u5ddd\u9ed4\u4e00\u5e26\u7684\u6b66\u6797\u4eba\u7269\uff0c\u4ed6\u548c\u6c5f\u5357\u5927\u4fa0\u9752\u840d\u5251\u5b8b\u4ee4\u516c\u672c\u662f\u81f3\u4ea4\uff0c\u4e8e \u662f\uff0c\u4ed6\u4fbf\u8054\u5408\u4e86\u5b8b\u4ee4\u516c\uff0c\u505a\u8fd9\u4ef6\u4e8b\u7684\u5021\u5bfc\u8005\u3002 \u3000\u3000\u539f\u6765\u5f53\u65f6\u6b66\u6797\u4e2d\uff0c\u6700\u4eab\u76db\u540d\u7684\uff0c\u7537\u5973\u5171\u6709\u5341\u4eba\uff0c\u9664\u4e86\u5df4\u5c71\u5251\u5ba2\u67f3\u590d\u660e\u5916\uff0c\u8fd8\u6709\u6cb3\u6714\u53cc \u5251\uff0c\u6c6a\u4e00\u9e4f\u3001\u6c6a\u4e00\u9e23\u6606\u4ef2\uff0c\u5e7f\u897f\u5927\u8c6a\uff0c\u2018\u5b50\u6bcd\u53cc\u98de\u2019\u5de6\u624b\u795e\u5251\u4e01\u8863\uff0c\u9655\u7518\u4e24\u7701\u7684\u592b\u5987\u53cc\u4fa0\uff0c \u9e33\u9e2f\u53cc\u5251\u7a0b\u67ab\u3001\u6797\u7433\u3002 \u3000\u3000\u8fd9\u4e03\u4eba\u88ab\u79f0\u4e3a\u201c\u4e03\u5251\u201d\u518d\u52a0\u4e0a\u6d59\u6c5f\u7684\u7075\u86c7\u6bdb\u81ec\uff0c\u5173\u5916\u5927\u4fa0\u4e03\u661f\u97ad\u675c\u4ef2\u5947\uff0c\u4e91\u5357\u70b9\u82cd\u95e8\u4e0b \u7684\u4fa0\u5973\uff0c\u767e\u6b65\u98de\u82b1\u6797\u7426\u7b5d\uff0c\u5c31\u662f\u201c\u4e03\u5251\u4e09\u97ad\u201d\uff0c\u5728\u5f53\u65f6\u6b66\u6797\u4e2d\uff0c\u201c\u4e03\u5251\u4e09\u97ad\u201d\u6240\u5904\u7684\u5730\u4f4d\uff0c \u6240\u4eab\u7684\u76db\u540d\uff0c\u51e0\u4e4e\u662f\u96be\u4ee5\u6307\u8ff0\u7684\u3002 \u3000\u3000\u4ed6\u4eec\u5341\u4eba\u867d\u7136\u4e92\u4e0d\u76f8\u8bc6\uff0c\u4f46\u662f\u5728\u6b66\u6797\u4e2d\u7684\u5730\u4f4d\u76f8\u7b49\uff0c\u58f0\u606f\u81ea\u7136\u76f8\u901a\uff0c\u5df4\u5c71\u5251\u5ba2\u67f3\u590d\u660e\uff0c \u548c\u6c5f\u5357\u5927\u4fa0\u9752\u840d\u5251\u5b8b\u4ee4\u516c\uff0c\u672c\u7740\u4e49\u6124\uff0c\u6697\u4f20\u98de\u67ec\u901a\u77e5\u201c\u4e03\u5251\u4e09\u97ad\u201d\u91cc\u7684\u53e6\u5916\u516b\u4eba\uff0c\u8981\u8054\u624b\u9664 \u53bb\u6b66\u6797\u6b64\u5bb3\uff0c\u5176\u4f59\u516b\u4eba\u81ea\u7136\u4e00\u53e3\u7b54\u5e94\uff0c\u7ecf\u8fc7\u8bb8\u591a\u65e5\u5b50\u7684\u7b79\u5212\uff0c\u4ed6\u4eec\u5728\u8fd9\u8352\u50fb\u7684\u718a\u8033\u5c71\u91cc\uff0c\u622a \u4f4f\u4e86\u4e00\u5411\u72ec\u884c\u7684\u201c\u4ec7\u5148\u751f\u201d\u4ec7\u72ec\u3002 \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u5c16\u523b\u4ed6\u8bf4\u5b8c\u4e86\u8bdd\uff0c\u8fd9\u79cd\u8bdd\u81ea\u7136\u6df1\u6df1\u5730\u6fc0\u6012\u4e86\u4ec7\u72ec\uff0c\u5728\u4ed6\u7684\u60f3\u6cd5\u4e2d\uff0c\u4ed6\u662f\u5168\u7136\u6b63 \u76f4\u7684\uff0c\u201c\u5351\u9119\u201d\u8fd9\u4e2a\u8bcd\u5bf9\u4ed6\u662f\u592a\u751f\u758f\u4e86\u3002 \u3000\u3000\u4ed6\u4ef0\u5929\u957f\u7b11\u4e86\u51e0\u58f0\uff0c\u662f\u6012\u6781\u6240\u53d1\u51fa\u7684\u7b11\uff0c\u9ad8\u4ea2\u7684\u7b11\u58f0\uff0c\u538b\u4e0b\u4e86\u79cb\u591c\u6df1\u5c71\u91cc\u7684\u5404\u79cd\u58f0\u97f3\u3002 \u3000\u3000\u201c\u5351\u9119\uff0c\u201d\u4ed6\u6025\u7a81\u5730\u6b62\u4f4f\u7b11\uff0c\u51db\u7136\u9053\uff0c\u201c\u59d3\u6bdb\u7684\uff0c\u4f60\u8ba4\u4e3a\u6211\u59d3\u4ec7\u7684\u5351\u9119\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\uff01\u201d\u7075\u86c7\u6bdb\u81ec\u4f3c\u4e4e\u60f3\u8d77\u4e86\u67d0\u4ef6\u4e8b\uff0c\u4ee5\u81f4\u672a\u80fd\u5f88\u5feb\u4ed6\u8bf4\u51fa\u4e0b\u9762\u7684\u8bdd\u3002 \u3000\u3000\u5df4\u5c71\u5251\u5ba2\u63a5\u8fc7\u4e86\u4ed6\u7684\u8bdd\uff0c\u6717\u58f0\u9053\uff1a\u201c\u9601\u4e0b\u600e\u5730\u4eca\u65e5\u4e5f\u754f\u7f29\u4e86\u8d77\u6765\uff0c\u82e5\u662f\u8d2b\u9053\u4e5f\u505a\u4e86\u5351\u9119 \u7684\u4e8b\uff0c\u5c31\u4e0d\u6015\u522b\u4eba\u8bf4\u6211\u5351\u9119\u3002\u201d \u3000\u3000\u5a07\u7b11\u58f0\u81ea\u4ec7\u72ec\u7684\u9a6c\u540e\u4f20\u6765\uff0c\u4ec7\u72ec\u5f80\u540e\u4e00\u8f6c\u8eab\uff0c\u76ee\u5149\u843d\u5728\u5632\u7b11\u7740\u7684\u767e\u6b65\u98de\u82b1\u6797\u7426\u7b5d\u7684\u4e00\u53cc \u6c34\u7075\u7075\u7684\u4fcf\u773c\u4e0a\uff0c\u538c\u6076\u5730\u4e00\u76b1\u7709\uff0c\u4e0d\u5c51\u7684\u56de\u8fc7\u5934\u53bb\uff0c\u5fc3\u91cc\u6cdb\u8d77\u53e6\u4e00\u4e2a\u7f8e\u4e3d\u800c\u7eaf\u6d01\u7684\u5f71\u5b50\u3002 \u3000\u3000\u67f3\u590d\u660e\u6697\u5730\u8c03\u6574\u4e86\u4e00\u4e0b\u4ed6\u80cc\u540e\u80cc\u7684\u5251\uff0c\u968f\u65f6\u51c6\u5907\u7740\u52a8\u624b\u3002 \u3000\u3000\u7136\u540e\u4ed6\u53c8\u6717\u58f0\u9053\uff1a\u201c\u56db\u5ddd\u6210\u90fd\u5e9c\u7684\u8001\u6b66\u5e08\u4e07\u80dc\u5200\u738b\u5929\u6c11\uff0c\u8bbe\u573a\u6388\u5f92\u6570\u5341\u5e74\uff0c\u4e00\u5411\u5b89\u4efd\u5b88 \u5df1\uff0c\u521a\u6b63\u4e0d\u963f\uff0c\u4e0e\u9601\u4e0b\u53c8\u6709\u4ec0\u4e48\u51a4\u4ec7\uff1f\u9601\u4e0b\u7adf\u5f53\u7740\u4ed6\u6570\u5341\u5f1f\u5b50\u4e4b\u9762\uff0c\u8e22\u4e86\u4ed6\u7684\u573a\u5b50\uff0c\u53c8\u91cd\u91cd \u7684\u7f9e\u8fb1\u4e86\u4ed6\u4e00\u987f\uff0c\u4f7f\u5f97\u4ed6\u5728\u98ce\u70db\u4e4b\u5e74\uff0c\u5410\u8840\u800c\u4ea1\uff0c\u8fd9\u53eb\u4e0d\u53eb\u2018\u5351\u9119\u2019\uff1f\u201d \u3000\u3000\u201c\u738b\u8001\u5934\u5b50\u8bef\u4eba\u5b50\u5f1f\uff0c\u5c06\u6570\u5341\u767e\u4e2a\u9752\u5e74\u7684\u5927\u597d\u65f6\u5149\uff0c\u6d6a\u8d39\u5728\u4ed6\u90a3\u5957\u6beb\u65e0\u7528\u5904\u7684\u5200\u6cd5\u4e4b \u4e0a\uff0c\u6211\u6ca1\u6709\u4eb2\u624b\u6740\u4ed6\uff0c\u5df2\u7ecf\u662f\u5ba2\u6c14\u7684\u4e86\u3002\u201d \u3000\u3000\u4ec7\u72ec\u7acb\u523b\u5728\u8111\u6d77\u6cdb\u8d77\u8fd9\u4e48\u4e00\u79cd\u60f3\u6cd5\uff0c\u4f46\u662f\u4ed6\u5374\u4e0d\u5c51\u4e8e\u5c06\u4ed6\u5fc3\u4e2d\u7684\u4e8b\uff0c\u8bf4\u7ed9\u8fd9\u4e9b\u4ed6\u8ba4\u4e3a\u662f \u201c\u6b3a\u4e16\u76d7\u540d\u201d\u4e4b\u8f88\u7684\u4eba\u542c\u3002 \u3000\u3000\u201c\u6d59\u6c5f\u6c38\u5609\u7684\u9556\u5e08\u6ca1\u7fbd\u7bad\u8d75\u56fd\u660e\uff0c\u59bb\u5b50\u4e0d\u5b88\u5987\u9053\uff0c\u4e58\u8d75\u56fd\u660e\u8d70\u9556\u5728\u5916\uff0c\u5077\u4eba\u517b\u6c49\uff0c\u8d75\u56fd \u660e\u4e0d\u7518\u53d7\u8fb1\uff0c\u81ea\u7136\u8981\u5c06\u90a3\u4e00\u5bf9\u5978\u592b\u6deb\u5987\u6740\u4e4b\u800c\u5feb\uff0c\u54fc\uff01\u201d\u67f3\u590d\u660e\u8bcd\u8272\u6e10\u5389\uff0c\u9053\uff0c\u201c\u53ef\u662f\u9601 \u4e0b\uff0c\u5374\u5c06\u8d75\u56fd\u660e\u70b9\u4f4f\u8981\u7a74\uff0c\u4efb\u51ed\u90a3\u4e00\u5bf9\u5978\u592b\u6deb\u5987\u9003\u8d70\uff0c\u8fd9\u79cd\u8fdd\u80cc\u5929\u7406\u3001\u56fd\u6cd5\u3001\u4eba\u60c5\u7684\u884c\u4e3a\uff0c \u53c8\u53eb\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4e24\u4eba\u771f\u60c5\u6d41\u9732\uff0c\u7537\u5973\u4e24\u60c5\u6b22\u60a6\uff0c\u53c8\u6709\u8c01\u6709\u8fd9\u6743\u5229\u963b\u6321\uff0c\u8d75\u56fd\u660e\u4e0d\u77e5\u7231\u62a4\u81ea\u5df1\u7684\u59bb \u5b50\uff0c\u5c82\u80fd\u7981\u6b62\u522b\u4eba\u7231\u62a4\u5462\uff1f\u201d \u3000\u3000\u4ec7\u72ec\u51b7\u7b11\u6697\u5fd6\uff0c\u60f3\u5230\u90a3\u4e00\u5bf9\u201c\u5978\u592b\u6deb\u5987\u201d\u5728\u8d75\u56fd\u660e\u5200\u4e0b\u76f8\u62e5\u4f4e\u6ce3\u7684\u72b6\u51b5\uff0c\u66f4\u65ad\u7136\u8ba4\u4e3a\u81ea \u5df1\u7684\u6240\u4f5c\u6240\u4e3a\u662f\u6b63\u786e\u7684\u3002 \u3000\u3000\u201c\u6cb3\u5357\u5f00\u5c01\u5e9c\u7684\u795e\u67aa\u6c6a\u9c81\u5e73\uff0c\u6709\u5b50\u5fe4\u9006\uff0c\u4ed6\u6b32\u6b63\u4e4b\u5bb6\u6cd5\uff0c\u9601\u4e0b\u53c8\u6709\u4ec0\u4e48\u6743\u5229\u5e72\u6d89\uff1f\u201d \u3000\u3000\u201c\u4eba\u547d\u5f97\u4e4b\u4e8e\u5929\uff0c\u8001\u5b50\u6709\u4ec0\u4e48\u8d44\u683c\u6740\u6b7b\u513f\u5b50\uff1f\u201d\u4ec7\u72ec\u4e0d\u5e73\u5730\u60f3\u7740\uff0c\u7ec8\u4e8e\uff0c\u4ed6\u4e0d\u8010\u5730\u53eb \u9053\uff1a\u201c\u59d3\u67f3\u7684\uff0c\u4f4f\u5634\uff01\u201d \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u51b7\u7b11\u9053\uff1a\u201c\u59d3\u4ec7\u7684\u607c\u7f9e\u6210\u6012\u4e86\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u4ed6\u5c06\u58f0\u97f3\u653e\u5f97\u66f4\u523a\u8033\uff0c\u9053\uff0c\u201c\u53ef\u662f \u8fd8\u6709\u6bd4\u8fd9\u4e9b\u66f4\u5351\u9119\u7684\u4e8b\u5462\uff01\u201d \u3000\u3000\u201c\u6cb3\u5317\u4fdd\u5b9a\u5e9c\u7684\u79bb\u9b42\u5708\u8bf8\u845b\u4e00\u5e73\u65e0\u610f\u4e2d\u5f97\u7f6a\u4e86\u4f60\uff0c\u88ab\u4f60\u903c\u5f97\u65e0\u5730\u5bb9\u8eab\uff0c\u9003\u5230\u5f00\u5dde\u53bf\u5916\u7684 \u516b\u516c\u6865\uff0c\u57cb\u5934\u4e00\u5fcd\u3002\u201d \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u51b7\u7b11\u7740\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u8fd8\u8981\u8d76\u5c3d\u6740\u7edd\uff0c\u5230\u516b\u516c\u6865\u53bb\u5c06\u4ed6\u5927\u5378\u516b\u5757\uff0c\u6b7b\u72b6\u60e8\u4e0d\u5fcd \u7779\uff0c\u6211\u8bf4\u59d3\u4ec7\u7684\uff0c\u4f60\u4e5f\u672a\u514d\u5927\u6bd2\u4e86\u5427\uff01\u201d \u3000\u3000\u201c\u8bf8\u845b\u4e00\u5e73\u9c7c\u8089\u4e61\u91cc\uff0c\u7ed3\u4ea4\u5b98\u5e9c\uff0c\u4e3a\u975e\u4f5c\u6b79\uff0c\u6b64\u4eba\u4e0d\u6b7b\uff0c\u7b80\u76f4\u662f\u6beb\u65e0\u5929\u7406\u4e86\uff01\u201d\u4ec7\u72ec\u81ea \u601d\u5fd6\u81f3\u6b64\uff0c\u5374\u542c\u6bdb\u81ec\u53c8\u51b7\u7b11\u9053\uff1a\u201c\u5c31\u7b97\u8bf8\u845b\u4e00\u5e73\u4e0e\u4f60\u6709\u4ec7\uff0c\u4ed6\u7684\u59bb\u5b50\u4e0e\u4f60\u53c8\u6709\u4f55\u4ec7\uff1f\u4f60\u4e0d\u4f46 \u6740\u4e86\u4ed6\uff0c\u8fd8\u5c06\u4ed6\u59bb\u5b50\u5265\u5f97\u7cbe\u5149\uff0c\u540a\u5728\u6811\u4e0a\uff0c\u6063\u610f\u5632\u5f04\uff0c\u6211\u8bf4\u59d3\u4ec7\u7684\uff0c\u4f60\u7b80\u76f4\u5351\u9119\u5f97\u50cf\u6761\u6ca1\u6709 \u4eba\u6027\u7684\u755c\u7272\u3002\u201d \u3000\u3000\u201c\u8bf8\u845b\u4e00\u5e73\u7684\u59bb\u5b50\u5728\u4fdd\u5b9a\u5e9c\u5f15\u8bf1\u826f\u5bb6\u5987\u5973\uff0c\u903c\u826f\u6210\u5a3c\uff0c\u8fd9\u5c31\u662f\u5979\u7684\u62a5\u5e94\u3002\u201d \u3000\u3000\u4ec7\u72ec\u6697\u5730\u5c06\u5bf9\u65b9\u8bc9\u8bf4\u7684\u81ea\u5df1\u7684\u7f6a\u72b6\uff0c\u4e00\u4e00\u8fa9\u767d\uff0c\u7b49\u5230\u4ed6\u786e\u5207\u5730\u8ba4\u4e3a\u81ea\u5df1\u662f\u6beb\u65e0\u8fc7\u5931\u7684\u65f6 \u5019\uff0c\u4ed6\u7684\u5fc3\u7406\u66f4\u6cf0\u7136\u4e86\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u5632\u5f04\u5730\u5411\u7075\u86c7\u6bdb\u81ec\u9053\uff1a\u201c\u5c31\u7b97\u6211\u6240\u505a\u7684\u8fd9\u4ef6\u4e8b\u662f\u5351\u9119\u7684\uff0c\u53ef\u662f\u8fd9\u8fdc\u6bd4\u4e0d\u4e0a\u4f60\u59d3\u6bdb \u7684\u5728\u8861\u5dde\u6240\u505a\u7684\u90a3\u6837\u4e8b\u7684\u4e07\u5206\u4e4b\u4e00\u3002\u201d \u3000\u3000\u4ed6\u51b7\u7b11\u7740\uff0c\u7528\u9a6c\u97ad\u7684\u97ad\u68a2\u6307\u7740\u6bdb\u81ec\uff0c\u9053\uff1a\u201c\u59d3\u6bdb\u7684\uff0c\u4f60\u82e5\u662f\u4ee5\u4e3a\u4f60\u505a\u7684\u4e8b\u795e\u4e0d\u77e5\uff0c\u9b3c\u4e0d \u89c9\uff0c\u90a3\u4f60\u5c31\u5927\u9519\u4e86\uff01\u201d \u3000\u3000\u201c\u6c6a\u4e00\u9e4f\uff0c\u6c6a\u4e00\u9e23\uff0c\u201d\u4ed6\u7528\u97ad\u68a2\u6307\u7740\u7f6e\u8eab\u53f3\u4fa7\u7684\u6cb3\u6714\u53cc\u5251\uff0c\u53c8\u56de\u8fc7\u5934\uff0c\u6307\u5411\u6797\u7426\u7b5d\uff0c \u9053\uff1a\u201c\u8fd8\u6709\u4f60\uff0c\u4f60\u4eec\u90fd\u8981\u8bb0\u7740\uff0c\u82e5\u8981\u4eba\u4e0d\u77e5\uff0c\u9664\u975e\u5df1\u83ab\u4e3a\uff0c\u4f60\u2014\u2014\u201d\u201c\u5e9f\u8bdd\u5c11\u8bf4\uff01\u201d\u6c6a\u4e00\u9e4f \u5389\u559d\u7740\uff0c\u8eab\u5f62\u7a81\u7136\u63a0\u8d77\uff0c\u6a2a\u5251\u659c\u524a\uff0c\u5e26\u8d77\u4e00\u6e9c\u9752\u5149\uff0c\u5241\u5411\u9a6c\u4e0a\u7684\u4ec7\u72ec\u3002 \u3000\u3000\u6c6a\u4e00\u9e23\u4e5f\u5728\u540c\u4e00\u5239\u90a3\u91cc\uff0c\u81ea\u76f8\u53cd\u7684\u65b9\u5411\uff0c\u6a2a\u5251\u800c\u5c55\uff0c\u4e24\u9053\u9752\u84dd\u8272\u7684\u5251\u5149\uff0c\u5e26\u7740\u5c16\u9510\u7684\u98ce \u58f0\uff0c\u76f4\u53d6\u4ec7\u72ec\u201c\u80a9\u4e95\u201d\u548c\u201c\u80a9\u8d1e\u201d\u4e24\u5904\u5927\u7a74\u3002 \u3000\u3000\u6cb3\u6714\u53cc\u5251\u79f0\u96c4\u4e24\u6cb3\uff0c\u5251\u6cd5\u4e0a\u679c\u7136\u6709\u5f88\u6df1\u7684\u9020\u8be3\uff0c\u9ed1\u591c\u4e2d\u8ba4\u7a74\uff0c\u5c45\u7136\u4e0d\u5dee\u6beb\u5398\uff0c\u8eab\u6cd5\u4e4b \u5feb\uff0c\u4e5f\u662f\u8fe5\u5f02\u4e8e\u4e00\u822c\u6b66\u6797\u4e2d\u4eba\u7269\u7684\u3002 \u3000\u3000\u5251\u5149\u582a\u582a\u5df2\u8fbe\u5230\u4ec7\u72ec\u8eab\u4e0a\uff0c\u5c31\u5728\u8fd9\u95f4\u4e0d\u5bb9\u53d1\u7684\u4e00\u523b\u91cc\uff0c\u4ec7\u72ec\u53f3\u638c\u6240\u63e1\u7684\u9a6c\u97ad\uff0c\u201c\u5530\u201d\u5730 \u7535\u4e5f\u4f3c\u7684\u53cd\u5377\u4e86\u4e0a\u53bb\uff0c\u97ad\u68a2\u8f7b\u8f7b\u5728\u6c6a\u4e00\u9e4f\u7684\u5251\u8eab\u4e0a\u4e00\u642d\uff0c\u6c6a\u4e00\u9e4f\u7acb\u523b\u89c9\u5f97\u6709\u4e00\u79cd\u5947\u5f02\u7684\u529b \u91cf\uff0c\u4f7f\u5f97\u4ed6\u7684\u624b\u4e2d\u5251\u4e0d\u7531\u81ea\u4e3b\u5730\u5411\u5de6\u4e0b\u65b9\u5212\u4e86\u4e0b\u53bb\uff0c\u201c\u545b\u201d\u7136\u4e00\u58f0\uff0c\u7adf\u548c\u6c6a\u4e00\u545c\u7684\u5251\u76f8\u51fb\uff0c \u53d1\u51fa\u4e00\u58f0\u60a0\u957f\u7684\u97f3\u541f\u3002 \u3000\u3000\u4ec7\u72ec\u8fd9\u4e00\u51fa\u624b\uff0c\u65f6\u95f4\u62ff\u634f\u4e4b\u51c6\uff0c\u4e34\u654c\u7ecf\u9a8c\u4e4b\u4e30\uff0c\u5185\u529b\u4e4b\u6df1\u539a\uff0c\u8fd9\u4e9b\u6b66\u6797\u4e2d\u7684\u540d\u624b\uff0c\u7109\u6709 \u770b\u4e0d\u51fa\u6765\u7684\u9053\u7406\uff0c\u6c5f\u5357\u5927\u4fa0\u9752\u840d\u5251\u5b8b\u4ee4\u516c\u5fae\u4e00\u9894\u9996\uff0c\u8131\u53e3\u800c\u547c\uff1a\u201c\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff0c\u597d\uff01\u201d \u3000\u3000\u6cb3\u6714\u53cc\u5251\u8eab\u5f62\u5fae\u4e00\u987f\u632b\uff0c\u811a\u5c16\u4e00\u7740\u5730\u9762\uff0c\u53c8\u63a0\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u4e5f\u968f\u624b\u6325\u51fa\u4ed6\u90a3\u6761\u4ed7\u4ee5\u6210\u540d\u7684\u5947\u5f62\u957f\u97ad\uff0c\u97ad\u8eab\u5f2f\u66f2\u95f4\uff0c\u70b9\u5411\u4ec7\u72ec\u524d\u80f8\u7684\u201c\u5c06 \u53f0\u201d\u3002 \u3000\u3000\u6cb3\u6714\u53cc\u5251\u5251\u52bf\u8fde\u7ef5\uff0c\u7075\u86c7\u6bdb\u81ec\u97ad\u5982\u7075\u86c7\uff0c\u5251\u5149\u97ad\u5f71\u6f2b\u5929\u800c\u6765\uff0c\u4ed6\u4eec\u5404\u6709\u4e8f\u5fc3\u4e4b\u4e8b\u88ab\u6293\u5728 \u4ec7\u72ec\u624b\u4e2d\uff0c\u51b3\u5fc3\u8d8a\u65e9\u5c06\u4ec7\u72ec\u6bc1\u53bb\u8d8a\u597d\u3002 \u3000\u3000\u4eba\u4eec\u7684\u5fc3\u7406\uff0c\u5927\u591a\u662f\u53ef\u6015\u7684\u81ea\u79c1\uff0c\u5df4\u5c71\u5251\u5ba2\u67f3\u590d\u660e\uff0c\u9752\u840d\u5251\u5b8b\u4ee4\u516c\uff0c\u4ee5\u516c\u9053\u4e4b\u5fc3\u4f20\u4e0b\u56f4 \u6b7c\u4ec7\u72ec\u7684\u6b66\u6797\u98de\u67ec\uff0c\u4ed6\u4eec\u5374\u4e0d\u77e5\u9053\u63a5\u5230\u6b66\u6797\u98de\u67ec\u7684\u4eba\uff0c\u5fc3\u91cc\u7684\u6253\u7b97\u53c8\u6709\u51e0\u4e2a\u548c\u4ed6\u4eec\u4e00\u6837\u5462\uff1f \u3000\u3000\u4ec7\u72ec\u4e00\u58f0\u6e05\u5578\uff0c\u53f3\u624b\u7684\u9a6c\u97ad\u5212\u8d77\u4e00\u9053\u5708\u5b50\uff0c\u9a6c\u97ad\u7684\u540e\u67c4\u70b9\u5411\u6c6a\u4e00\u9e23\u53f3\u638c\u638c\u7f18\u6b63\u4e2d\u7684\u201c\u5408 \u8c37\u201d\u7a74\uff0c\u97ad\u68a2\u642d\u4f4f\u7075\u86c7\u6bdb\u81ec\u7684\u97ad\u68a2\uff0c\u5411\u4e0a\u4e00\u6296\uff0c\u4e24\u6761\u8f6f\u97ad\u201c\u5530\u201d\u5730\u5411\u4e0a\u98de\u8d77\uff0c\u5de6\u624b\u500f\u5730\u4f38 \u51fa\uff0c\u5feb\u5982\u7535\u5149\u77f3\u706b\uff0c\u6c6a\u4e00\u9e4f\u624b\u8155\u4e00\u7d27\uff0c\u5df2\u88ab\u4ec7\u72ec\u5201\u4f4f\u53f3\u8155\uff0c\u4ed6\u75be\u5730\u624b\u8155\u53cd\u7ffb\uff0c\u60f3\u4ee5\u201c\u5c0f\u64d2\u62ff \u624b\u201d\u6323\u8131\u4ec7\u72ec\u64d2\u4f4f\u7684\u624b\u3002 \u3000\u3000\u54ea\u77e5\u4ed6\u5df2\u8fdf\u4e86\u4e00\u6b65\uff0c\u4ec7\u72ec\u5de6\u624b\u4e00\u62c9\uff0c\u4e00\u626d\uff0c\u201c\u53e8\u201d\u5730\u4e00\u58f0\uff0c\u6c6a\u4e00\u9e4f\u7684\u53f3\u81c2\u4fbf\u786c\u751f\u751f\u5730\u88ab \u4ed6\u626f\u843d\u4e86\u4e0b\u6765\uff0c\u865a\u8f6f\u5730\u642d\u5728\u8eab\u4fa7\u3002 \u3000\u3000\u4e09\u4e2a\u6b66\u6797\u540d\u624b\u540c\u65f6\u653b\u51fb\u4e00\u4eba\uff0c\u54ea\u77e5\u4e0d\u4f46\u88ab\u5bf9\u65b9\u4ee5\u4e00\u62db\u5316\u89e3\uff0c\u8fd8\u4e58\u9699\u800c\u51fb\uff0c\u4f24\u4e86\u81ea\u5df1\u4e00 \u4eba\uff0c\u8fd9\u79cd\u60c5\u5f62\u6b66\u6797\u4e2d\u4eba\u82e5\u975e\u4eb2\u89c1\uff0c\u662f\u518d\u4e5f\u4e0d\u4f1a\u76f8\u4fe1\u7684\u3002 \u3000\u3000\u767e\u6b65\u98de\u82b1\u6797\u7426\u7b5d\u54ac\u4e86\u54ac\u5634\uff0c\u60f3\u5230\u4ec7\u72ec\u6240\u77e5\u9053\u7684\u5979\u7684\u4e11\u4e8b\uff0c\u8138\u7acb\u5373\u53d8\u5f97\u98de\u7ea2\uff0c\u5979\u5e74\u7eaa\u8fd8 \u8f7b\uff0c\u8fd8\u4e0d\u5230\u4e8c\u5341\u5c81\uff0c\u80fd\u5728\u6b66\u6797\u4e2d\u4eab\u6b64\u76db\u540d\uff0c\u4e00\u5927\u534a\u662f\u9760\u4e86\u5979\u5df2\u6545\u4e16\u7684\u5e08\u5144\u795e\u5251\u624b\u8c22\u94ff\u3002 \u3000\u3000\u4e00\u5e74\u524d\u5979\u60c5\u7aa6\u521d\u5f00\uff0c\u5bf9\u7537\u5973\u95f4\u4e8b\u6709\u5fcd\u4e0d\u4f4f\u7684\u597d\u5947\u7684\u6e34\u671b\u3002 \u3000\u3000\u90a3\u65f6\u795e\u5251\u624b\u8c22\u94ff\u65b9\u53bb\u4e16\uff0c\u4e5f\u5c31\u662f\u767e\u6b65\u98de\u82b1\u6797\u7426\u7b5d\u521a\u521a\u626c\u540d\u6c5f\u6e56\u7684\u65f6\u5019\uff0c\u6797\u7426\u7b5d\u5c11\u5973\u65e0 \u77e5\uff0c\u53c8\u88ab\u76db\u540d\u51b2\u660f\u4e86\u5934\uff0c\u5f88\u5e72\u4e86\u51e0\u4ef6\u89c1\u4e0d\u5f97\u4eba\u7684\u574f\u4e8b\uff0c\u201c\u4ec7\u5148\u751f\u201d\u6d6a\u8ff9\u5929\u6daf\uff0c\u65e0\u610f\u4e4b\u4e2d\uff0c\u4e5f \u7ed9\u649e\u4e0a\u4e86\u51e0\u4ef6\u3002 \u3000\u3000\u5979\u672c\u6765\u5bf9\u4ec7\u72ec\u6ca1\u6709\u4e1d\u6beb\u6076\u611f\uff0c\u751a\u81f3\u8fd8\u6709\u4e9b\u88ab\u4ec7\u72ec\u7684\u90a3\u79cd\u5947\u7279\u7684\u98ce\u5ea6\u6240\u8ff7\u9189\u3002 \u3000\u3000\u4f46\u662f\u5728\u8fd9\u79cd\u60c5\u51b5\u4e4b\u4e0b\uff0c\u81ea\u5bb6\u7684\u5229\u76ca\u8fdc\u8d85\u51fa\u4e86\u4e00\u5207\uff0c\u7389\u8155\u7ffb\u5904\uff0c\u4e00\u6761\u94f6\u5149\u707f\u7136\u7684\u4eae\u94f6\u7ec3\u5b50 \u97ad\u5149\u534e\u7f20\u7ed5\uff0c\u51fb\u5411\u9a6c\u4e0a\u7684\u4ec7\u72ec\u3002 \u3000\u3000\u6700\u602a\u7684\u662f\u90a3\u5339\u9a6c\u975e\u4f46\u6ca1\u6709\u56e0\u7740\u8fd9\u97ad\u5251\u7684\u5149\u534e\u800c\u88ab\u60ca\u5413\uff0c\u800c\u4e14\u5c45\u7136\u8fd8\u4f1a\u968f\u7740\u5200\u5251\u7684\u6765\u52bf\uff0c \u66ff\u81ea\u8eab\u548c\u4ec7\u72ec\u9009\u4e00\u4e2a\u6700\u4f18\u826f\u7684\u5730\u52bf\u6765\u8eb2\u907f\u8fd9\u4e9b\u4e2d\u539f\u6b66\u6797\u9876\u513f\u5c16\u513f\u7684\u9ad8\u624b\u540c\u65f6\u6240\u53d1\u51fa\u7684\u88ad\u51fb\u3002 \u3000\u3000\u8fd9\u4e8c\u4eba\u62db\u5f0f\u4e00\u51fa\uff0c\u7aef\u7684\u662f\u4e0d\u540c\u51e1\u54cd\uff0c\u4ec7\u72ec\u9f3b\u5b54\u91cc\u51b7\u51b7\u4e00\u54fc\uff0c\u6697\u5fd6\uff1a\u201c\u4e03\u5251\u4e09\u97ad\u539f\u6765\u4e5f\u4e0d \u8fc7\u5982\u6b64\u3002\u201d\u53f3\u624b\u9a6c\u97ad\u6d8c\u8d77\u5982\u5c71\uff0c\u5de6\u638c\u6216\u6293\uff0c\u6216\u524a\uff0c\u81ea\u6f2b\u5929\u97ad\u5f71\u91cc\u5de7\u5999\u5730\u53d1\u62db\uff0c\u5e94\u4ed8\u8fd9\u4e9b\u9ad8 \u624b\uff0c\u5c45\u7136\u7ef0\u7ef0\u6709\u4f59\u3002 \u3000\u3000\u6c6a\u4e00\u9e4f\u53f3\u81c2\u88ab\u6298\uff0c\u9762\u8272\u82cd\u767d\u5730\u7ad9\u5230\u4e00\u65c1\uff0c\u4e03\u661f\u97ad\u675c\u4ef2\u5947\u63a0\u5230\u4ed6\u8eab\u4fa7\uff0c\u63a2\u624b\u4e00\u6478\uff0c\u4e0d\u7981\u6697 \u6697\u76b1\u7709\uff0c\u53e3\u91cc\u5374\u5b89\u6170\u4ed6\u8bf4\u9053\uff1a\u201c\u6c6a\u5144\u522b\u5fc3\u6025\uff0c\u8fd9\u4f24\u5927\u7ea6\u4e0d\u59a8\u4e8b\u7684\u3002\u201d\u5176\u5b9e\u4ed6\u4e5f\u77e5\u9053\u6c6a\u4e00\u9e4f\u8fd9 \u6761\u53f3\u81c2\u7b97\u662f\u5e9f\u4e86\u3002 \u3000\u3000\u201c\u4e03\u5251\u4e09\u97ad\u201d\u4e2d\u4ee5\u6c5f\u5357\u5927\u4fa0\u9752\u840d\u5251\u5b8b\u4ee4\u516c\u9605\u5386\u6700\u4e30\uff0c\u57ce\u5e9c\u6700\u6df1\uff0c\u884c\u4e8b\u4e5f\u6700\u614e\u91cd\u3002\u6b64\u523b\u4ed6 \u89c1\u6c6a\u6c0f\u6606\u4ef2\uff0c\u767e\u6b65\u98de\u82b1\u7b49\u4eba\u8fd9\u79cd\u6253\u6cd5\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6697\u5fd6\uff1a\u201c\u96be\u9053\u8fd9\u51e0\u4eba\u771f\u6709\u4ec0\u4e48\u89c1\u4e0d\u5f97\u4eba\u7684 \u4e11\u4e8b\u2014\u2014\u201d\u201c\u65e0\u8bba\u5982\u4f55\uff0c\u8fd9\u4ec7\u72ec\u5374\u4e5f\u7edd\u4e0d\u80fd\u5bb9\u4ed6\u7559\u5728\u4e16\u4e0a\uff0c\u4eca\u65e5\u82e5\u4e0d\u9664\u53bb\u6b64\u4eba\uff0c\u53ea\u6015\u6b64\u540e\u6b66 \u6797\u4e2d\u6c38\u65e0\u5b81\u65e5\u4e86\u3002\u201d\u9752\u840d\u5251\u53cd\u590d\u601d\u7d22\uff0c\u65ad\u7136\u5730\u66ff\u81ea\u5df1\u4e0b\u4e86\u4e2a\u51b3\u5b9a\uff1a\u201c\u5c31\u7b97\u4eca\u65e5\u6211\u4eec\u7528\u7684\u662f\u6700 \u5351\u9119\u7684\u624b\u6cd5\uff0c\u53ea\u8981\u80fd\u4e3a\u6b66\u6797\u4e2d\u9664\u6b64\u5927\u5bb3\uff0c\u4e5f\u662f\u503c\u5f97\u7684\u3002\u201d \u3000\u3000\u4e8e\u662f\uff0c\u4ed6\u5411\u5df4\u5c71\u5251\u5ba2\u5fae\u4e00\u9894\u9996\u3002 \u3000\u3000\u5df4\u5c71\u5251\u5ba2\u67f3\u590d\u660e\u888d\u8896\u4e00\u5c55\uff0c\u7075\u5de7\u5730\u5c06\u80cc\u540e\u957f\u5251\u64a4\u5230\u8eab\u524d\uff0c\u968f\u7740\u8eab\u5f62\u7684\u6d41\u52a8\uff0c\u53d1\u51fa\u4e00\u58f0\u60a0 \u957f\u7684\u6e05\u5578\u3002 \u3000\u3000\u5c31\u662f\u8fd9\u5578\u58f0\u5f00\u59cb\u5230\u7ed3\u675f\u7684\u8fd9\u5239\u90a3\u95f4\uff0c\u9e33\u9e2f\u53cc\u5251\uff0c\u4e03\u661f\u97ad\u675c\u4ef2\u5947\uff0c\u5b50\u6bcd\u53cc\u98de\u4e01\u8863\uff0c\u4ee5\u53ca\u9752 \u840d\u5251\u5b8b\u4ee4\u516c\u90fd\u4ee5\u6781\u5feb\u7684\u901f\u5ea6\u64a4\u51fa\u5175\u5203\u3002 \u3000\u3000\u800c\u5728\u52a8\u7740\u624b\u7684\u7075\u86c7\u6bdb\u81ec\uff0c\u6c6a\u4e00\u9e23\uff0c\u6797\u7426\u7b5d\uff0c\u5374\u500f\u5730\u505c\u987f\u4e86\u653b\u52bf\u3002 \u3000\u3000\u9664\u4e86\u53f3\u81c2\u88ab\u6298\u7684\u6cb3\u6714\u53cc\u5251\u4e2d\u7684\u6c6a\u4e00\u9e4f\u5916\uff0c\u4e5d\u4ef6\u5bd2\u5149\u95ea\u707f\u7684\u5175\u5203\uff0c\u88ab\u63e1\u5728\u4e5d\u4e2a\u8eab\u6000\u7edd\u6280\u7684 \u6b66\u6797\u9ad8\u624b\u624b\u91cc\uff0c\u5728\u4ecd\u7aef\u5750\u9a6c\u4e0a\u7684\u4ec7\u72ec\u8eab\u4fa7\u4e24\u5c3a\u4e4b\u5185\uff0c\u7d27\u7d27\u5730\u7ed3\u6210\u4e00\u9053\u5708\u5b50\u3002 \u3000\u3000\u8fd9\u79cd\u88ab\u56f4\u653b\u7684\u6ecb\u5473\uff0c\u5728\u4ec7\u72ec\u8bf4\u6765\uff0c\u662f\u7ecf\u5386\u5f97\u5927\u591a\u4e86\uff0c\u672c\u6765\u4ed6\u5df2\u7ecf\u53ef\u4ee5\u6ca1\u6709\u4efb\u4f55\u5947\u5f02\u7684\u611f \u89c9\u3002 \u3000\u3000\u7136\u800c\uff0c\u6b64\u65f6\u7684\u4ec7\u72ec\uff0c\u8111\u6d77\u4e2d\u7a81\u7136\u6cdb\u8d77\u201c\u6b7b\u201d\u7684\u610f\u5ff5\u6765\u3002 \u3000\u3000\u201c\u5c31\u7b97\u662f\u6b7b\uff0c\u6211\u4e5f\u662f\u503c\u5f97\u7684\u4e86\u3002\u201d\u90a3\u7f8e\u4e3d\u800c\u5723\u6d01\u7684\u5c11\u5973\u8eab\u5f71\uff0c\u53c8\u81ea\u4ed6\u5fc3\u5e95\u5347\u8d77\uff0c\u201c\u6211\u5df2 \u7ecf\u5f97\u5230\u4e86\u6211\u4e00\u751f\u4e2d\u6700\u6e34\u671b\u7684\u4e1c\u897f\u2014\u2014\u201d\u4ed6\u7684\u601d\u6f6e\u88ab\u9752\u840d\u5251\u5b8b\u4ee4\u516c\u51b7\u5cfb\u7684\u8bed\u97f3\u6253\u65ad\u3002 \u3000\u3000\u201c\u4ec7\u5148\u751f\uff01\u201d\u6c5f\u5357\u5927\u4fa0\u81ea\u6043\u8eab\u4efd\uff0c\u5634\u4e2d\u7edd\u4e0d\u80af\u5410\u51fa\u534a\u4e2a\u810f\u5b57\u6765\u3002 \u3000\u3000\u4ed6\u4ecd\u7136\u5ba2\u6c14\u4ed6\u8bf4\u9053\uff0c\u201c\u4eca\u65e5\u5144\u5f1f\u4eec\u5728\u6b64\u8352\u5c71\u91cc\u9080\u622a\u9601\u4e0b\u7684\u610f\u601d\uff0c\u5c31\u662f\u5144\u5f1f\u4eec\u4e0d\u8bf4\uff0c\u9601\u4e0b \u5e94\u8be5\u4e5f\u77e5\u9053\u5f97\u6e05\u695a\u5f97\u5f88\u3002\u201d \u3000\u3000\u4ec7\u72ec\u53c8\u91cd\u91cd\u5730\u54fc\u4e00\u58f0\uff0c\u5b8b\u4ee4\u516c\u6ca1\u6709\u505c\u987f\u4ed6\u8bf4\u4e0b\u53bb\uff1a\u201c\u4e45\u95fb\u9601\u4e0b\u6b66\u529f\u76d6\u4e16\uff0c\u800c\u4e14\u884c\u4e8b\u4e5f\u75db \u5feb\u5f97\u5f88\uff0c\u90a3\u4e48\u5728\u4e0b\u4e5f\u4e0d\u5fc5\u591a\u8bf4\u5e9f\u8bdd\u3002\u201d\u4ed6\u7565\u4e00\u6325\u52a8\u638c\u4e2d\u7684\u5251\uff0c\u7acb\u5373\u5e26\u8d77\u4e00\u9053\u5bd2\u8292\u3002 \u3000\u3000\u7136\u540e\u4ed6\u63a5\u7740\u8bf4\uff1a\u201c\u8001\u5b9e\u8bf4\uff0c\u4eca\u65e5\u9601\u4e0b\u82e5\u4e0d\u80fd\u80dc\u5f97\u5144\u5f1f\u4eec\u624b\u4e2d\u7684\u5341\u4ef6\u5175\u5668\uff0c\u9601\u4e0b\u4e5f\u4e0d\u5fc5\u5962 \u671b\u518d\u80fd\u51fa\u5c71\u4e86\u3002\u201d \u3000\u3000\u4ec7\u72ec\u51b7\u7136\u542c\u7740\u4ed6\u7684\u8bdd\uff0c\u5fc3\u4e2d\u53cd\u800c\u5e73\u9759\u5f97\u5f88\uff0c\u9762\u4e0a\u4e5f\u4e1d\u6beb\u6ca1\u6709\u9732\u51fa\u4efb\u4f55\u8868\u60c5\u3002 \u3000\u3000\u4ed6\u8fd9\u79cd\u51b7\u9759\u7684\u6001\u5ea6\uff0c\u5012\u4f7f\u5b8b\u4ee4\u516c\u7565\u4e3a\u611f\u5230\u6709\u4e9b\u610f\u5916\uff0c\u7565\u4e3a\u6c89\u541f\u4e86\u4e00\u4f1a\uff0c\u8bf4\u9053\uff1a\u201c\u6b63\u5982\u9601 \u4e0b\u6240\u8bf4\uff0c\u4eca\u65e5\u6211\u7b49\u6240\u4e3a\uff0c\u786e\u5b9e\u6709\u6b20\u5149\u660e\uff0c\u4f46\u662f\u806a\u660e\u7684\u9601\u4e0b\uff0c\u60f3\u5fc5\u80fd\u77e5\u9053\u8fd9\u5176\u4e2d\u7684\u539f\u56e0\u5427\u3002\u201d \u3000\u3000\u4ec7\u72ec\u6e05\u8d8a\u5730\u4ef0\u5929\u4e00\u9635\u957f\u7b11\uff0c\u51b7\u7136\u9053\uff1a\u201c\u9601\u4e0b\u8bdd\u8bf4\u5f97\u5012\u7684\u786e\u5ba2\u6c14\u5f97\u5f88\uff0c\u53ea\u662f\u7528\u8fd9\u79cd\u65af\u6587\u8bdd \u6765\u5bf9\u6211\u8bf4\uff0c\u5b8c\u5168\u662f\u5bf9\u725b\u5f39\u7434\u3002\u201d\u4ed6\u8bed\u6c14\u4e2d\u5632\u5f04\u7684\u610f\u5473\uff0c\u4f7f\u5f97\u5b8b\u4ee4\u516c\u9762\u4e0a\u5fae\u5fae\u4e00\u7ea2\u3002 \u3000\u3000\u201c\u6211\u59d3\u4ec7\u7684\u81ea\u5df1\u77e5\u9053\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u9601\u4e0b\u4e5f\u4e0d\u5fc5\u8d39\u5fc3\u6765\u89e3\u91ca\uff0c\u8981\u52a8\u624b\uff0c\u5404\u4f4d\u53ea\u7ba1\u8bf7\u4e0a\u3002\u201d \u3000\u3000\u4ed6\u8ba5\u8bbd\u5730\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u83ab\u8bf4\u53ea\u6709\u5341\u4e2a\u4eba\uff0c\u5c31\u7b97\u518d\u591a\u4e0a\u51e0\u500d\uff0c\u6211\u59d3\u4ec7\u7684\u4e5f\u89c1\u8bc6\u8fc7\u3002\u201d \u3000\u3000\u4ed6\u6781\u5feb\u5730\u5c06\u9a6c\u97ad\u4ea4\u5230\u5de6\u624b\uff0c\u53f3\u624b\u62bd\u51fa\u978d\u8fb9\u6302\u7740\u7684\u957f\u5251\uff0c\u5728\u4ed6\u81ea\u5df1\u7684\u5251\u5149\u63a5\u89e6\u5230\u4ed6\u7684\u773c\u5e18 \u7684\u65f6\u5019\uff0c\u5343\u767e\u79cd\u601d\u6f6e\uff0c\u98de\u5feb\u5730\u81ea\u4ed6\u8111\u6d77\u4e2d\u5347\u8d77\uff1a\u201c\u4e00\u4ef6\u4e8b\u7684\u5e78\u4e0e\u4e0d\u5e78\uff0c\u7684\u786e\u4e0d\u662f\u4e8b\u5148\u53ef\u4ee5\u6599 \u60f3\u5f97\u5230\u7684\u3002\u547d\u8fd0\uff0c\u7684\u786e\u662f\u4eba\u4eec\u6700\u96be\u6349\u6478\u7684\u4e1c\u897f\u3002\u6211\u82e5\u6ca1\u6709\u9047\u5230\u5979\uff0c\u4eca\u5929\u65e0\u8bba\u5982\u4f55\u4e5f\u4e0d\u4f1a\u6709\u4e1d \u6beb\u5371\u9669\uff0c\u5c31\u7b97\u6211\u62b5\u654c\u4e0d\u4f4f\u8fd9\u5341\u4e2a\u4eba\uff0c\u8981\u4e00\u8d70\u4e86\u4e4b\uff0c\u4e5f\u662f\u6700\u7b80\u5355\u4e0d\u8fc7\u7684\uff0c\u53ef\u662f\u2014\u2014\u201d\u4ed6\u52aa\u529b\u5730 \u7981\u6b62\u7740\u81ea\u5df1\u518d\u5f80\u8fd9\u4e00\u9762\u60f3\u4e0b\u53bb\uff1a\u201c\u5230\u5e95\uff0c\u6211\u5df2\u5f97\u5230\u4e86\u6211\u771f\u6b63\u6240\u8981\u7684\uff0c\u90a3\u4e48\uff0c\u2018\u6b7b\u2019\uff0c\u53c8\u7b97\u5f97 \u4e86\u4ec0\u4e48\uff1f\u201d\u4ed6\u5e78\u798f\u5730\u6362\u4e86\u53e6\u4e00\u79cd\u60f3\u6cd5\uff1a\u201c\u82e5\u662f\u6211\u6ca1\u6709\u9047\u5230\u5979\uff0c\u6d3b\u7740\u53c8\u6709\u4ec0\u4e48\u610f\u5473\uff1f\u201d \u3000\u3000\u201c\u671d\u95fb\u9053\uff0c\u5915\u6b7b\u53ef\u77e3\u3002\u201d\u4ed6\u7a81\u7136\u60f3\u8d77\u8fd9\u53e5\u8bdd\u91cc\u7684\u6db5\u4e49\uff0c\u5634\u89d2\u4e0d\u7981\u6cdb\u8d77\u4e00\u4e1d\u7b11\u5bb9\uff0c\u6697\u5fd6\uff1a \u201c\u8fd9\u662f\u591a\u4e48\u5947\u5999\u7684\u4e00\u53e5\u8bdd\u5440\uff0c\u53e4\u4eba\u6240\u8bf4\u7684\u2018\u9053\u2019\uff0c\u5176\u4e2d\u8be5\u662f\u5305\u62ec\u4e86\u8bb8\u591a\u79cd\u610f\u4e49\u5427\u3002\u201d \u3000\u3000\u7b2c\u4e00\u6b21\uff0c\u4ed6\u611f\u89c9\u5230\u751f\u547d\u867d\u7136\u91cd\u8981\uff0c\u53ef\u662f\u4e16\u4e0a\u8fd8\u6709\u8bb8\u591a\u79cd\u4e1c\u897f\uff0c\u8fdc\u6bd4\u751f\u547d\u66f4\u53ef\u8d35\uff0c\u5f97\u5230\u4e86 \u8fd9\u4e9b\u4e1c\u897f\uff0c\u7eb5\u7136\u5176\u4ee3\u4ef7\u662f\u4ee5\u751f\u547d\u6765\u4ea4\u6362\uff0c\u5728\u4ed6\u6b64\u65f6\u8bf4\u6765\uff0c\u4e5f\u8ba4\u4e3a\u662f\u503c\u5f97\u4e86\u3002 \u3000\u3000\u4ed6\u7684\u6c89\u9ed8\u548c\u4ed6\u7684\u7b11\u5bb9\uff0c\u4f7f\u5f97\u73af\u4f3a\u5728\u4ed6\u8eab\u4fa7\u7684\u6b66\u6797\u9ad8\u624b\u4eec\u90fd\u89c9\u5f97\u6709\u4e9b\u8be7\u5f02\u3002 \u3000\u3000\u201c\u96be\u9053\u4ed6\u81ea\u5df1\u8ba4\u4e3a\u4ed6\u7a33\u64cd\u80dc\u7b97\u5417\uff1f\u201d\u4ed6\u4eec\u90fd\u6709\u8fd9\u79cd\u60f3\u6cd5\u3002\u53ea\u6709\u7075\u86c7\u6bdb\u81ec\u5728\u5fc3\u91cc\u51b7\u7b11\uff1a \u201c\u6211\u77e5\u9053\u4f60\u7b11\u7684\u662f\u4ec0\u4e48\uff0c\u4f60\u5fc3\u91cc\u9ad8\u5174\u4f60\u80fd\u5f97\u5230\u4e86\u8bb8\u591a\u662f\u4e0d\u662f\uff0c\u54fc\u2014\u2014\u201d\u4ed6\u8138\u4e0a\u6cdb\u8d77\u4e00\u4e1d\u8be1\u5f02 \u7684\u7b11\u5bb9\uff1a\u201c\u6211\u8ba9\u4f60\u4e34\u6b7b\u7684\u65f6\u5019\uff0c\u53eb\u4f60\u8fd8\u8981\u53d7\u5230\u6bd4\u2018\u6b7b\uff0c\u66f4\u5927\u7684\u75db\u82e6\u3002\u201d\u591c\u66f4\u6df1\u4e86\uff0c\u6df1\u5c71\u91cc\u6709 \u7247\u523b\u9759\u5bc2\uff0c\u4f46\u6bcf\u4e00\u4e2a\u4eba\u90fd\u77e5\u9053\u8fd9\u4e0d\u8fc7\u662f\u4e00\u573a\u66b4\u98ce\u96e8\u7684\u524d\u594f\u800c\u5df2\u3002\u201c\u5404\u4f4d\u8fd8\u4e0d\u5feb\u52a8\u624b\u62db\u547c \u4ed6\uff1f\u201d\u7ad9\u5728\u5708\u5916\u7684\u6c6a\u4e00\u9e4f\u7a81\u7136\u53d1\u51fa\u4e86\u4e00\u58f0\u5389\u547c\uff0c\u4ed6\u53f3\u81c2\u88ab\u6298\uff0c\u75db\u5165\u5fc3\u813e\uff0c\u5bf9\u4ec7\u72ec\u81ea\u7136\u66f4\u662f\u6068 \u5165\u5207\u9aa8\u3002\u4ec7\u72ec\u51b7\u7b11\u7740\uff0c\u9053\uff1a\u201c\u6b63\u662f\uff0c\u518d\u4e0d\u52a8\u624b\uff0c\u5929\u5c31\u8981\u4eae\u4e86\uff0c\u88ab\u8fc7\u8def\u7684\u770b\u5230\u5802\u5802\u2018\u4e03\u5251\u4e09 \u97ad\u2019\u7adf\u7136\u56f4\u6bb4\uff0c\u65e5\u540e\u4f20\u8bf4\u51fa\u53bb\uff0c\u6015\u4e5f\u4e0d\u597d\u542c\u5462\u3002\u201d \u3000\u3000\u968f\u7740\u8bf4\u8bdd\uff0c\u4ed6\u731b\u5730\u5347\u8d77\u4e00\u4e2a\u5ff5\u5934\uff1a\u201c\u4eca\u65e5\u6211\u82e5\u88ab\u6b64\u5341\u4eba\u6740\u6b7b\uff0c\u6c5f\u6e56\u4e2d\u8fde\u77e5\u9053\u7684\u4eba\u90fd\u4e0d\u4f1a \u6709\u4e00\u4e2a\u3002\u201d\u8f6c\u5ff5\u53c8\u5fd6\u9053\uff1a\u201c\u5509\uff01\u6211\u72ec\u5f80\u72ec\u6765\uff0c\u7ed3\u6028\u53c8\u591a\uff0c\u5c31\u662f\u6709\u4eba\u77e5\u9053\uff0c\u53c8\u6709\u8c01\u4f1a\u6765\u4e3a\u6211\u590d \u4ec7\uff1f\u201d \u3000\u3000\u4e00\u5ff5\u81f3\u6b64\uff0c\u4ed6\u5fae\u5fae\u89c9\u5f97\u6709\u4e9b\u5fc3\u9178\u3002 \u3000\u3000\u4eba\u4eec\u5728\u8fd9\u79cd\u65f6\u5019\uff0c\u6700\u5bb9\u6613\u60f3\u8d77\u6700\u4eb2\u8fd1\u7684\u4eba\uff0c\u4ed6\u6697\u5730\u601d\u91cf\uff1a\u201c\u53ea\u6709\u5979\uff0c\u53ef\u60dc\u5979\u4ec5\u4ec5\u662f\u4e2a\u5f31 \u5973\u800c\u5df2\uff0c\u5c31\u7b97\u5979\u77e5\u9053\uff0c\u53c8\u80fd\u5982\u4f55\uff1f\u201d \u3000\u3000\u7a81\u7136\u60f3\u8d77\u201c\u5979\u201d\uff0c\u4eca\u540e\u4e5f\u662f\u53ea\u5269\u4e0b\u4e00\u4e2a\u4eba\u4e86\uff0c\u6c42\u751f\u4e4b\u5ff5\uff0c\u731b\u53c8\u5347\u8d77\uff1a\u201c\u6211\u4e0d\u80fd\u6b7b\uff0c\u6211\u8fd8 \u8981\u7167\u987e\u5979\uff01\u201d\u62ac\u773c\u671b\u5230\u56f4\u5217\u5728\u4ed6\u56db\u5468\u7684\u5251\u5f71\u97ad\u5149\uff0c\u5fc3\u5934\u4e00\u51b7\uff1a\u201c\u53ef\u662f\u6211\u2014\u2014\u201d\u6b64\u523b\u5df2\u4e0d\u518d\u6709 \u65f6\u95f4\u5bb9\u4ed6\u601d\u8651\u4e86\u3002 \u3000\u3000\u50cf\u662f\u4e00\u9635\u7a81\u6765\u7684\u9aa4\u96e8\uff0c\u4e5d\u4ef6\u5175\u5203\u4e00\u9f50\u53d1\u52a8\uff0c\u53c8\u50cf\u662f\u66b4\u96e8\u4e2d\u7684\u95ea\u7535\uff0c\u9f50\u90fd\u51fb\u5411\u9a6c\u4e0a\u7684\u4ec7 \u72ec\u3002 \u3000\u3000\u4ed6\u53ea\u5f97\u6536\u4e86\u4e00\u5207\u7684\u6742\u5ff5\uff0c\u51e0\u4e4e\u662f\u51fa\u4e8e\u672c\u80fd\u5730\uff0c\u4e00\u58f0\u6e05\u5578\uff0c\u53f3\u5251\u5de6\u97ad\uff0c\u500f\u7136\u800c\u821e\u3002 \u3000\u3000\u970e\u65f6\u95f4\u5bc2\u9759\u7684\u5c71\u8c37\u91cc\u7a81\u7136\u9a9a\u52a8\u4e86\uff0c\u5c0f\u5f84\u4e24\u65c1\u7684\u6797\u6728\uff0c\u88ab\u8fd9\u4e9b\u5185\u5bb6\u9ad8\u624b\u5175\u5203\u4e0a\u6240\u5e26\u8d77\u7684\u98ce \u58f0\u626b\u5f97\u7c41\u7c41\u4f5c\u54cd\uff0c\u6797\u53f6\u7247\u7247\u98de\u843d\u3002 \u3000\u3000\u4ec7\u72ec\u4ee5\u65e0\u6bd4\u66fc\u5999\u7684\u62db\u5f0f\u4ee5\u53ca\u96c4\u6d51\u7684\u5185\u5bb6\u771f\u529b\u5e94\u4ed8\u7740\u8fd9\u4e5d\u4ef6\u5175\u5203\uff0c\u56e0\u4e3a\u4ed6\u5750\u5728\u9a6c\u4e0a\uff0c\u8eab\u5f62 \u4e0d\u4fbf\u52a8\u8f6c\uff0c\u62db\u5f0f\u4e0a\u81ea\u7136\u5927\u5927\u5730\u6253\u4e86\u4e2a\u6298\u6263\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4ecd\u7136\u4e0d\u4e0b\u9a6c\uff0c\u4ed6\u80ef\u4e0b\u7684\u5750\u9a91\u867d\u7136\u7075\u5f02\uff0c\u6b64\u523b\u4e5f\u4e0d\u514d\u4e0d\u5b89\u5730\u9a9a\u52a8\u7740\uff0c\u8fd9\u4e48\u4e00\u6765\uff0c\u4ed6 \u5e94\u4ed8\u5f97\u66f4\u662f\u663e\u5f97\u52c9\u5f3a\u3002 \u3000\u3000\u5df4\u5c71\u5251\u5ba2\u5251\u5149\u5982\u8679\uff0c\u5251\u5251\u4e0d\u79bb\u4ec7\u72ec\u7684\u8981\u5bb3\uff0c\u82e5\u7136\u4e0d\u662f\u4ec7\u72ec\u5251\u4e0a\u6240\u53d1\u51fa\u7684\u90a3\u4e00\u79cd\u201c\u6444\u91d1\u5438 \u94c1\u201d\u7684\u529b\u91cf\uff0c\u4ed6\u6015\u4e0d\u65e9\u5728\u4ec7\u72ec\u8eab\u4e0a\u523a\u4e86\u51e0\u4e2a\u900f\u660e\u7a9f\u7abf\u3002 \u3000\u3000\u53ea\u662f\u5df4\u5c71\u5251\u5ba2\u5fc3\u4e2d\u4e0d\u514d\u5947\u602a\uff1a\u201c\u8fd9\u4ec7\u72ec\u4e3a\u4f55\u8981\u5728\u9a6c\u4e0a\u52a8\u624b\uff0c\u8fd9\u6837\u5c82\u975e\u81ea\u5df1\u9650\u5236\u4f4f\u4e86\u81ea\u5df1 \u7684\u8eab\u6cd5\uff1f\u201d \u3000\u3000\u8fd9\u611f\u89c9\u51e0\u4e4e\u662f\u6bcf\u4e2a\u4eba\u5fc3\u4e2d\u90fd\u6709\u7684\uff0c\u9664\u4e86\u6bdb\u81ec\u3002 \u3000\u3000\u201c\u679c\u7136\u5979\u4e0d\u8d1f\u6211\u6240\u671b\uff0c\u5b8c\u6210\u6211\u7684\u4f7f\u547d\uff0c\u4ec7\u72ec\u5440\u4ec7\u72ec\uff0c\u4f60\u6b66\u529f\u518d\u9ad8\uff0c\u4eca\u65e5\u4e5f\u6015\u96be\u9003\u516c\u9053 \u4e86\u3002\u201d\u7075\u86c7\u6bdb\u81ec\u5f97\u610f\u5730\u6697\u5fd6\u7740\u3002 \u3000\u3000\u4ed6\u638c\u4e2d\u7684\u957f\u97ad\uff0c\u4f20\u81ea\u4e94\u53f0\uff0c\u4e0e\u5173\u5916\u7684\u4e03\u661f\u97ad\u675c\u4ef2\u5947\uff0c\u88ab\u79f0\u4e3a\u97ad\u6cd5\u4e0a\u7684\u201c\u5357\u5b97\u5317\u7956\u201d\uff0c\u51fa \u62db\u65f6\u5b9b\u5982\u7075\u86c7\u4f38\u7f29\uff0c\u7adf\u5c06\u4e08\u8bb8\u957f\u7684\u97ad\u505a\u70b9\u7a74\u6485\u4f7f\uff0c\u8fe5\u7136\u4e0d\u662f\u666e\u901a\u97ad\u6cd5\u6a2a\u626b\u659c\u62bd\u7684\u8def\u5b50\u3002 \u3000\u3000\u4ed6\u5ff5\u5934\u95ea\u52a8\u8fc7\u4e4b\u540e\uff0c\u5634\u89d2\u53c8\u6302\u8d77\u90a3\u79cd\u8be7\u5f02\u7684\u7b11\u5bb9\uff0c\u7a81\u7136\u81ea\u5251\u5f71\u4e2d\u64a4\u51fa\u81ea\u5df1\u7684\u97ad\u6765\uff0c\u5fae\u4e00 \u6296\u52a8\uff0c\u97ad\u68a2\u8212\u5c55\uff0c\u4e0d\u53d6\u4eba\u800c\u51fb\u9a6c\u3002 \u3000\u3000\u4ec7\u72ec\u9762\u8272\u7acb\u53d8\uff0c\u4f46\u662f\u4ed6\u6b64\u523b\u6240\u8981\u5e94\u4ed8\u7684\u662f\u53e6\u5916\u516b\u4eba\u51cc\u5389\u7684\u653b\u52bf\uff0c\u7edd\u5bf9\u65e0\u6cd5\u518d\u7167\u5e94\u81ea\u5df1\u7684 \u5750\u9a91\u3002 \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u7684\u957f\u97ad\u77ac\u5373\u5377\u4f4f\u4e86\u9a6c\u817f\uff0c\u5fae\u4e00\u6c89\u8170\uff0c\u5411\u5916\u4e00\u64a4\uff0c\u90a3\u9a6c\u518d\u662f\u7075\u5f02\uff0c\u600e\u7981\u5f97\u8d77\u4ed6\u8fd9 \u79cd\u5185\u5bb6\u9ad8\u624b\u7684\u771f\u529b\uff1f\u6602\u9996\u4e00\u58f0\u957f\u5636\uff0c\u8f6f\u762b\u5728\u5730\u4e0a\u3002 \u3000\u3000\u5df4\u5c71\u5251\u5ba2\u5fae\u4e00\u76b1\u7709\uff0c\u6697\u5fd6\uff1a\u201c\u7075\u86c7\u6bdb\u81ec\u7d20\u6765\u4ee5\u673a\u667a\u95fb\u540d\u6c5f\u6e56\uff0c\u4eca\u5929\u600e\u7684\u8822\u4e86\u8d77\u6765\uff0c\u4f60\u5c06 \u4ed6\u5750\u9a91\u51fb\u5012\uff0c\u4ed6\u4e0d\u518d\u6709\u987e\u5fcc\uff0c\u8eab\u6cd5\u5c82\u4e0d\u66f4\u8981\u7075\u4fbf\uff0c\u6211\u4eec\u8981\u5236\u4f4f\u4ed6\uff0c\u5c82\u4e0d\u66f4\u8d39\u529b\u4e86\u2014\u2014\u201d\u4ed6\u5ff5 \u5934\u5c1a\u672a\u8f6c\u5b8c\uff0c\u54ea\u77e5\u4ec7\u72ec\u5750\u9a91\u5012\u5730\u540e\uff0c\u8eab\u5f62\u5374\u6ca1\u6709\u8dc3\u8d77\u6765\uff0c\u4ecd\u7136\u5750\u5728\u5012\u5728\u5730\u4e0a\u7684\u9a6c\u80cc\u4e0a\u3002\u3000\u3000 \u90a3\u9a6c\u5728\u7aed\u529b\u6323\u624e\uff0c\u60f3\u7ad9\u8d77\u6765\u3002 \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u8fde\u8fde\u51b7\u7b11\uff0c\u97ad\u68a2\u5982\u96e8\uff0c\u53c8\u5728\u9a6c\u8eab\u4e0a\u62bd\u4e86\u51e0\u97ad\uff0c\u90a3\u9a6c\u5589\u5499\u91cc\u4f4e\u559d\u4e86\u51e0\u58f0\uff0c\u5012\u5728\u5730 \u4e0a\u6c14\u7edd\u4e86\u3002 \u3000\u3000\u4ec7\u72ec\u6b64\u523b\u5df2\u7ecf\u7b49\u4e8e\u5750\u5728\u5730\u4e0a\u4e86\uff0c\u638c\u4e2d\u7684\u9a6c\u97ad\u548c\u5251\uff0c\u66f4\u4e3a\u5403\u529b\u5730\u6325\u52a8\u7740\uff0c\u4ed6\u8f7b\u529f\u7edd\u4e16\uff0c\u4f46 \u662f\u6b64\u523b\u4ed6\u597d\u50cf\u5168\u7136\u5fd8\u8bb0\u4e86\u8fd9\u4e9b\u3002 \u3000\u3000\u987b\u77e5\u4ee5\u5be1\u654c\u4f17\uff0c\u6700\u91cd\u8981\u7684\u662f\u8981\u4ee5\u81ea\u5bb6\u8eab\u5f62\u7684\u6377\u4fbf\uff0c\u5728\u654c\u4eba\u7684\u5175\u5203\u4e2d\u5bfb\u627e\u7a7a\u9699\uff0c\u4f7f\u5f97\u654c\u4eba \u81ea\u5df1\u7684\u5175\u5203\uff0c\u4e92\u76f8\u649e\u51fb\uff0c\u7136\u540e\u518d\u4e58\u9699\u53cd\u51fb\u3002 \u3000\u3000\u6b64\u65f6\u4ed6\u8eab\u5f62\u56fa\u5b9a\uff0c\u53d8\u6210\u4e86\u53ea\u6709\u62db\u67b6\u800c\u4e0d\u80fd\u8fd8\u51fb\u7684\u5c40\u9762\uff0c\u4e5f\u5c31\u662f\u8bf4\uff0c\u4ed6\u6700\u591a\u53ea\u80fd\u81ea\u4fdd\uff0c\u8981 \u60f3\u5236\u80dc\uff0c\u90a3\u7b80\u76f4\u662f\u7edd\u65e0\u53ef\u80fd\u7684\u4e86\u3002\u3000\u3000\u5e78\u597d\u4ed6\u8eab\u6000\u6b66\u6797\u4e2d\u4e45\u5df2\u5931\u4f20\u7684\u201c\u4e07\u6d41\u5f52\u5b97\u201d\u7684\u5185\u529f\u5fc3 \u6cd5\uff0c\u53d1\u51fa\u7684\u62db\u5f0f\uff0c\u90fd\u5e26\u6709\u4e00\u79cd\u201c\u6444\u91d1\u5438\u94c1\u201d\u7684\u529b\u91cf\uff0c\u4f46\u9976\u662f\u8fd9\u6837\uff0c\u4e5f\u662f\u5c8c\u5c8c\u53ef\u5371\u4e86\u3002 \u3000\u3000\u201c\u4ed6\u4e3a\u4ec0\u4e48\u4e0d\u8dc3\u8d77\u6765\uff1f\u201d \u3000\u3000\u8fd9\u662f\u6bcf\u4e00\u4e2a\u4eba\u5fc3\u4e2d\u90fd\u5b58\u5728\u7684\u7591\u95ee\uff0c\u867d\u7136\u4ed6\u4eec\u7684\u5fc3\u4e2d\uff0c\u53c8\u90fd\u5728\u5e0c\u671b\u7740\u4ec7\u72ec\u6c38\u8fdc\u4e0d\u80fd\u8dc3\u8d77 \u6765\u3002 \u3000\u3000\u201c\u96be\u9053\u4ed6\u4e24\u6761\u817f\u5e9f\u4e86\uff1f\u201d\u5df4\u5c71\u5c71\u5251\u5ba2\u5fc3\u4e2d\u500f\u5730\u8d77\u4e86\u8fd9\u5ff5\u5934\uff0c\u201c\u53ef\u662f\u53c8\u662f\u8c01\u4f7f\u5f97\u4ed6\u4e24\u6761\u817f \u5e9f\u4e86\u5462\uff1f\u4eca\u65e5\u6c5f\u6e56\u4e0a\uff0c\u53c8\u6709\u8c01\u6709\u5982\u6b64\u529f\u529b\uff1f\u201d \u3000\u3000\u201c\u82e5\u7136\u4ed6\u4e24\u6761\u817f\u771f\u7684\u5e9f\u4e86\uff0c\u4eca\u65e5\u4e00\u6218\uff0c\u4ed6\u662f\u7edd\u65e0\u6d3b\u8def\u7684\u4e86\u3002\u53ea\u662f\u6211\u7b49\u4ee5\u4e5d\u9ad8\u624b\uff0c\u6765\u7fa4\u6218 \u4e00\u4e2a\u5e9f\u4eba\uff0c\u5012\u771f\u6709\u4e9b\u60ed\u6127\u4e86\u3002\u201d\u5df4\u5c71\u5251\u5ba2\u67f3\u590d\u660e\u5fc3\u4e2d\u7591\u7aa6\u4ece\u751f\uff0c\u77db\u76fe\u4e0d\u5df2\uff0c\u4f46\u624b\u4e2d\u7684\u5251\uff0c\u5374 \u4e1d\u6beb\u4e5f\u677e\u61c8\u4e0d\u5f97\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u8981\u5c0f\u5fc3\u5730\u8fd0\u7528\u81ea\u5df1\u7684\u771f\u6c14\uff0c\u6765\u548c\u4ec7\u72ec\u5251\u4e0a\u6240\u53d1\u51fa\u7684\u201c\u6444\u5438\u4e4b\u529b\u201d\u76f8\u6297\u3002 \u3000\u3000\u4ec7\u72ec\u601d\u6f6e\u5982\u6d8c\uff0c\u4ed6\u81ea\u5df1\u4e5f\u77e5\u9053\uff0c\u4ee5\u81ea\u5df1\u5c1a\u5269\u7684\u529f\u529b\uff0c\u6700\u591a\u53ea\u80fd\u518d\u7ef4\u6301\u534a\u4e2a\u65f6\u8fb0\uff0c\u987b\u77e5\u8fd9 \u79cd\u201c\u4e07\u6d41\u5f52\u5b97\u201d\u7684\u8089\u5bb6\u529f\u592b\u6700\u662f\u6d88\u8017\u7cbe\u529b\uff0c\u800c\u4ed6\u5047\u5982\u4e0d\u7528\u8fd9\u79cd\u5947\u5999\u7684\u5185\u529f\uff0c\u4ed6\u66f4\u65e0\u6cd5\u6765\u548c\u8fd9 \u4e9b\u9ad8\u624b\u76f8\u6297\u3002 \u3000\u3000\u6b64\u523b\u552f\u4e00\u4f7f\u4ed6\u5c1a\u80fd\u652f\u6301\u7684\u529b\u91cf\uff0c\u5c31\u662f\u4ed6\u5bf9\u201c\u5979\u201d\u7684\u601d\u5ff5\uff0c\u867d\u7136\u201c\u5979\u201d\u4f7f\u5f97\u4ed6\u51e0\u4e4e\u53d8\u6210\u5e9f \u4eba\uff0c\u4f46\u662f\u4ed6\u4e00\u70b9\u4e5f\u4e0d\u6028\u201c\u5979\u201d\u3002 \u3000\u3000\u201c\u56e0\u4e3a\u5979\u662f\u65e0\u610f\u7684\u5440\uff01\u201d\u7231\u60c5\u4f7f\u5f97\u4ed6\u80fd\u5bbd\u6055\u4e00\u5207\uff0c\u5bf9\u4e8e\u67d0\u4e9b\u4eba\u6765\u8bf4\uff0c\u4e16\u754c\u4e0a\u6ca1\u6709\u4e00\u79cd\u529b \u91cf\u518d\u80fd\u6bd4\u7231\u60c5\u5f3a\u70c8\u7684\u4e86\u3002 \u3000\u3000\u4ea4\u624b\u7684\u5c40\u52bf\uff0c\u56e0\u4e3a\u4ed6\u5fc3\u91cc\u7684\u7eb7\u4e71\uff0c\u800c\u5bf9\u4ed6\u66f4\u4e3a\u4e0d\u5229\u4e86\u3002 \u3000\u3000\u5728\u8fd9\u79cd\u4e25\u91cd\u7684\u60c5\u51b5\u4e0b\uff0c\u4ed6\u4ecd\u7136\u4e0d\u80fd\u5c06\u7cbe\u795e\u4e13\u6ce8\u5728\u6bd4\u6597\u4e0a\u3002 \u3000\u3000\u6bcf\u4e00\u4ef6\u6709\u5173\u201c\u5979\u201d\u7684\u4e8b\uff0c\u6b64\u523b\u90fd\u5728\u4ed6\u8111\u6d77\u91cc\u7535\u95ea\u800c\u8fc7\uff0c\u56e0\u4e3a\u4ed6\u8981\u5728\u4ed6\u751f\u547d\u7684\u6700\u540e\u4e00\u523b \u91cc\uff0c\u91cd\u6e29\u4e00\u904d\u8fd9\u6e29\u99a8\u7684\u65e7\u68a6\u3002 \u3000\u3000\u201c\u591a\u4e48\u5076\u7136\u5440\uff0c\u6211\u9047\u89c1\u4e86\u5979\uff0c\u5c31\u7231\u4e0a\u4e86\u5979\uff0c\u6ca1\u6709\u4efb\u4f55\u4e00\u79cd\u60c5\u611f\uff0c\u80fd\u6bd4\u6211\u7b2c\u4e00\u773c\u770b\u5230\u5979\u65f6 \u6240\u751f\u51fa\u7684\u90a3\u79cd\u60c5\u611f\u66f4\u5f3a\u70c8\u3002\u201d \u3000\u3000\u4ed6\u5634\u89d2\u5fae\u7b11\u7740\uff0c\u5de6\u624b\u9a6c\u97ad\u53cd\u5377\uff0c\u97ad\u68a2\u6263\u4f4f\u9e33\u9e2f\u53cc\u5251\u91cc\u4e00\u5b57\u5251\u7a0b\u67ab\u7684\u4e00\u62db\u201c\u5927\u6f20\u5782\u98ce\u201d\uff0c \u97ad\u8eab\u6321\u4f4f\u7d20\u5973\u6797\u7433\u7684\u4e00\u62db\u3002\u201c\u6d41\u6c99\u843d\u65e5\u201d\u3002 \u3000\u3000\u53f3\u624b\u7684\u5251\uff0c\u771f\u529b\u6ee1\u6ce8\uff0c\u5212\u4e86\u4e2a\u6781\u5927\u7684\u5708\u5b50\uff0c\u5251\u8eab\u5728\u4ed6\u8eab\u4fa7\u6392\u8d77\u4e00\u9053\u5149\u5899\uff0c\u6321\u4f4f\u4e86\u5176\u4f59\u4e94 \u4eba\u7684\u97ad\uff0c\u5251\uff0c\u9a6c\u97ad\u7684\u540e\u67c4\u540e\u51fb\uff0c\u6f47\u6d12\u5730\u649e\u5411\u4e03\u661f\u97ad\u675c\u4ef2\u5947\u7684\u97ad\u68a2\uff0c\u5fc3\u91cc\u5374\u4e0d\u65ad\u5730\u5728\u601d\u5fc6\u7740\uff1a \u201c\u540e\u6765\u5979\u544a\u8bc9\u6211\uff0c\u5f53\u65f6\u5979\u5c31\u4ece\u6211\u7684\u76ee\u5149\u91cc\uff0c\u770b\u51fa\u6211\u5bf9\u5979\u7684\u60c5\u610f\u3002\u201d \u3000\u3000\u201c\u8fd9\u771f\u662f\u5947\u5999\uff0c\u6211\u548c\u5979\u4e4b\u95f4\uff0c\u7adf\u50cf\u662f\u6709\u4e00\u79cd\u795e\u7075\u7684\u9ed8\u5951\uff0c\u8fd9\u5927\u6982\u5c31\u662f\u6240\u8c13\u5fc3\u6709\u7075\u7280\u4e00\u70b9 \u901a\u5427\uff1f\u201d\u5728\u6fd2\u4e34\u6b7b\u4ea1\u7684\u8fb9\u7f18\uff0c\u4ed6\u7684\u5fc3\u91cc\u4ecd\u7136\u751c\u751c\u5730\uff1a\u201c\u4e0d\u5230\u534a\u6708\u7684\u76f8\u5904\uff0c\u5979\u5c31\u5c06\u5979\u7684\u4e00\u5207\u5168 \u4ea4\u7ed9\u4e86\u6211\uff0c\u6211\u4e5f\u5c06\u6211\u7684\u4e00\u5207\u5168\u4ea4\u7ed9\u4e86\u5979\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u65e5\u4ee5\u7ee7\u591c\u5730\u5728\u4e00\u8d77\u76f8\u5904\u7740\uff0c\u9664\u4e86\u6bcf\u5929\u4e8e\u591c\u6211\u7ec3\u529f\u7684\u65f6\u5019\u4e4b\u5916\uff0c\u56e0\u4e3a\u6211\u2018\u4e07\u6d41\u5f52 \u5b97\u2019\u7684\u5185\u529f\u5c1a\u672a\u7ec3\u6210\uff0c\u6bcf\u5929\u4e00\u5b9a\u8981\u62bd\u51fa\u4e00\u6bb5\u65f6\u95f4\u6765\u7ec3\u529f\uff0c\u53ea\u662f\u6211\u6709\u4e86\u5979\u4e4b\u540e\uff0c\u751a\u81f3\u8fde\u7ec3\u529f\u90fd \u4e0d\u80fd\u4e13\u5fc3\u4e86\u3002\u201d \u3000\u3000\u201c\u5509\uff0c\u8fd9\u662f\u5929\u547d\u3002\u201d\u4ed6\u7684\u53cc\u817f\u662f\u9ebb\u6728\u7684\uff0c\u4e0b\u534a\u8eab\u50cf\u662f\u5df2\u4e0d\u5c5e\u4e8e\u4ed6\u4e86\uff0c\u4ed6\u82e6\u7b11\u4e86\u7b11\uff0c\u53c8\u594b \u529b\u62db\u67b6\u4e86\u51e0\u4ef6\u5175\u5203\u4e00\u62db\u3002\u6697\u5fd6\uff1a\u201c\u6709\u4e00\u5929\u6211\u7ec3\u529f\u7684\u65f6\u5019\uff0c\u5979\u7a81\u7136\u95ef\u4e86\u8fdb\u6765\uff0c\u4e0d\u77e5\u600e\u5730\u8dcc\u4e86\u4e00 \u8de4\uff0c\u80a9\u5934\u6b63\u597d\u649e\u5728\u6211\u8170\u4e0b\u7684\u2018\u9501\u8170\u7a74\uff0c\u4e0a\u3002\u201d\u201c\u90a3\u65f6\u6211\u6b63\u662f\u7ec3\u529f\u6700\u5403\u7d27\u7684\u65f6\u5019\uff0c\u52a8\u4e5f\u4e0d\u80fd \u52a8\uff0c\u88ab\u5979\u8fd9\u4e00\u649e\uff0c\u6211\u5f53\u65f6\u4e0b\u534a\u8eab\u5c31\u9ebb\u6728\u4e86\uff0c\u6ca1\u6709\u4efb\u4f55\u77e5\u89c9\u3002\uff0c\uff0c\u4ed6\u53c8\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u201d\u53ef\u662f\u6211 \u600e\u80fd\u602a\u5979\u5462\uff1f\u5979\u4e1d\u6beb\u4e0d\u61c2\u6b66\u529f\uff0c\u5f53\u7136\u66f4\u4e0d\u77e5\u9053\u8fd9\u4e00\u7c7b\u4e8b\u60c5\u7684\u5229\u5bb3\u3002\u201d\u6c5f\u5357\u5927\u4fa0\u5b8b\u4ee4\u516c\u957f\u5251\u5982 \u96ea\uff0c\u7a81\u5730\u8d34\u5730\u5e73\u524a\uff0c\u5feb\u5982\u7535\u5149\u77f3\u706b\u822c\uff0c\u5728\u4ec7\u72ec\u53f3\u817f\u4e0a\u5212\u4e86\u4e00\u9053\u5c3a\u8bb8\u957f\u7684\u4f24\u53e3\uff0c\u9c9c\u8840\u6c69\u7136\u6d41 \u51fa\u3002\u4f46\u662f\u4ec7\u72ec\u5374\u4e1d\u6beb\u4e0d\u611f\u75db\u82e6\uff0c\u56e0\u4e3a\u4ed6\u7684\u817f\u3002\u5df2\u4e0d\u80fd\u6709\u4efb\u4f55\u611f\u89c9\u4e86\uff0c\u957f\u5251\u4e00\u6325\uff0c\u81ea\u5251\u5f71\u4e2d\u7a7f \u51fa\uff0c\u523a\u5411\u7075\u86c7\u6bdb\u81ec\u7684\u524d\u80f8\u3002\u4ed6\u8fd9\u4e00\u5251\u53ea\u8981\u8eab\u5f62\u80fd\u5411\u524d\u632a\u52a8\u5c3a\u8bb8\uff0c\u8d64\u86c7\u6bdb\u81ec\u4fbf\u8981\u4f24\u5728\u4ed6\u7684\u5251 \u4e0b\uff0c\u53ea\u662f\u4ed6\u8eab\u5b50\u52a8\u4e5f\u4e0d\u80fd\u52a8\uff0c\u5251\u5f0f\u65e0\u6cd5\u591f\u5f97\u4e0a\u90e8\u4f4d\u3002\u7075\u86c7\u6bdb\u81ec\u53c8\u662f\u4e00\u58f0\u8be1\u5f02\u7684\u51b7\u7b11\uff0c\u7a81\u5730\u5c16 \u523b\u4ed6\u8bf4\u9053\uff1a\u201c\u670b\u53cb\u8fd8\u6323\u4ec0\u4e48\u547d\uff1f\u4e24\u6761\u817f\u90fd\u7ed9\u4eba\u5bb6\u5e9f\u4e86\uff0c\u6d3b\u7740\u8fd8\u6709\u4ec0\u4e48\u610f\u601d\u5462\uff1f\u8d81\u65e9\u8fd8\u662f\u81ea\u5df1 \u4e86\u7ed3\u5427\uff01\u201d\u3000\u3000\u4ec7\u72ec\u9762\u5982\u51dd\u971c\uff0c\u64a4\u5251\u56de\u4fdd\uff0c\u5374\u542c\u5f97\u7075\u86c7\u6bdb\u81ec\u53c8\u51b7\u7b11\u9053\uff1a\u201c\u6b64\u523b\u4f60\u629b\u4e0b\u5175\u5203\uff0c \u675f\u624b\u5c31\u7f1a\uff0c\u6bdb\u5927\u7237\u4e5f\u8bb8\u8fd8\u770b\u5728\u6211\u59b9\u59b9\u7684\u9762\u4e0a\uff0c\u8ba9\u4f60\u843d\u4e2a\u5168\u5c38\u3002\u201d \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u6b64\u8bdd\u4e00\u51fa\uff0c\u4ec7\u72ec\u6d51\u8eab\u4e00\u51db\uff0c\u5fae\u6014\u4e4b\u95f4\uff0c\u80a9\u5934\u4e0a\u53c8\u7740\u4e86\u675c\u4ef2\u5947\u4e00\u97ad\u3002 \u3000\u3000\u201c\u544a\u8bc9\u4f60\uff0c\u8ba9\u4f60\u6b7b\u5f97\u6e05\u695a\u4e9b\u3002\u201d\u7075\u86c7\u6bdb\u81ec\u51c4\u5389\u5730\u957f\u7b11\u7740\uff0c\u8bf4\u9053\uff0c\u201c\u9ad8\u51b0\u5c31\u662f\u6bdb\u51b0\uff0c\u6bdb\u51b0 \u5c31\u662f\u6211\u7684\u59b9\u59b9\u3002\u201d \u3000\u3000\u4ec7\u72ec\u4e00\u542c\uff0c\u5f53\u65f6\u89c9\u5f97\u4e00\u80a1\u5bd2\u610f\u81ea\u5fc3\u5e95\u5347\u8d77\uff0c\u673a\u4f36\u4f36\u6253\u4e86\u4e2a\u51b7\u6218\uff0c\u624b\u4e0b\u4e00\u6162\uff0c\u5de6\u80f8\u53c8\u88ab\u4e00 \u5b57\u5251\u7a0b\u67ab\u5212\u4e86\u9053\u53e3\u5b50\uff0c\u9c9c\u8840\u6e17\u51fa\uff0c\u6e17\u5f97\u4ed6\u6de1\u9752\u8272\u7684\u8863\u88f3\uff0c\u53d8\u6210\u4e00\u79cd\u4e11\u6076\u7684\u6de1\u7d2b\u4e4b\u8272\u3002 \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u7b11\u58f0\u8d8a\u53d1\u51c4\u5389\uff0c\u9053\uff1a\u201c\u59d3\u4ec7\u7684\uff0c\u8fd9\u4e0b\u4f60\u53ef\u660e\u767d\u4e86\u5427\uff1f\u201d \u3000\u3000\u4ec7\u72ec\u8eab\u4e0a\u8fde\u53d7\u51e0\u5904\u91cd\u521b\uff0c\u75db\u5165\u9aa8\u9ad3\uff0c\u4f46\u662f\uff0c\u6bd4\u8fd9\u4f24\u52bf\u66f4\u75db\u7684\uff0c\u5374\u662f\u4ed6\u7684\u5fc3\u3002 \u3000\u3000\u6b64\u523b\u604d\u7136\u4e86\u89e3\u4e86\uff0c\u4ed6\u6240\u6df1\u6df1\u7231\u7740\u7684\u4eba\uff0c\u4e5f\u662f\u4ed6\u4ee5\u4e3a\u6df1\u6df1\u7231\u7740\u4ed6\u7684\u4eba\uff0c\u7adf\u662f\u4ec7\u5bb6\u6240\u6d3e\u6765\u7684 \u5de5\u5177\u3002 \u3000\u3000\u201c\u539f\u6765\u8fd9\u90fd\u662f\u522b\u4eba\u7684\u5b89\u6392\uff0c\u539f\u6765\u5979\u5e76\u4e0d\u7231\u6211\uff0c\u5979\u4f7f\u6211\u53d7\u7684\u4f24\uff0c\u4e5f\u4e0d\u662f\u65e0\u610f\u7684\u3002\u201d \u3000\u3000\u201c\u6211\u4e3a\u4ec0\u4e48\u8fd9\u4e48\u50bb\uff0c\u5f53\u5979\u6bb7\u52e4\u5730\u53eb\u6211\u79bb\u5f00\u5979\u53bb\u6cbb\u4f24\uff0c\u8fd8\u8bf4\u5979\u4e00\u5b9a\u7b49\u7740\u6211\u65f6\uff0c\u6211\u7adf\u7136\u611f\u52a8 \u5f97\u6d41\u4e0b\u6cea\u6765\u3002\u201d\u4ed6\u7d27\u54ac\u7740\u7259\uff0c\u7259\u7f1d\u7684\u8840\u6c34\uff0c\u81ea\u5634\u89d2\u6e17\u4e86\u51fa\u6765\uff0c\u8138\u4e0a\u6d41\u52a8\u7740\u6c34\u73e0\uff0c\u4ed6\u4e5f\u4e0d\u77e5\u9053 \u662f\u6cea\u6c34\u6291\u662f\u6c57\u6c34\uff0c\u987f\u65f6\uff0c\u4ed6\u89c9\u5f97\u4e07\u5ff5\u4ff1\u7070\uff0c\u672c\u6765\u5f3a\u81ea\u6323\u624e\u7740\u7684\uff0c\u73b0\u5728\u4e5f\u5931\u53bb\u4e86\u6323\u624e\u7684\u529b\u91cf\uff0c \u7247\u523b\u4e4b\u95f4\uff0c\u8eab\u4e0a\u53c8\u4e2d\u4e86\u4e09\u5251\u3002 \u3000\u3000\u4ed6\u5168\u8eab\u90fd\u88ab\u8840\u6c34\u6e17\u6ee1\u4e86\uff0c\u4ed6\u7684\u5fc3\uff0c\u4e5f\u6b63\u50cf\u88ab\u4eba\u7528\u5c16\u5200\u5728\u4e00\u7247\u7247\u5730\u5bb0\u5272\uff0c\u8fd9\u6253\u51fb\u5bf9\u4ed6\u8bf4 \u6765\uff0c\u662f\u592a\u6b8b\u9177\u4e86\u4e9b\u3002 \u3000\u3000\u201c\u5929\u5440\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u8ba9\u6211\u77e5\u9053\u8fd9\u4e9b\uff0c\u6211\u5b81\u613f\u88ab\u6b3a\u9a97\u81f3\u6b7b\uff0c\u4e5f\u4e0d\u613f\u610f\u53d7\u5230\u6b64\u523b\u7684\u75db\u82e6\uff01\u201d \u3000\u3000\u4ed6\u771f\u6c14\u66f4\u52a0\u4e0d\u7ee7\uff0c\u62db\u5f0f\u4e5f\u66f4\u96f6\u4e71\uff0c\u6839\u672c\u518d\u4e5f\u65e0\u6cd5\u62b5\u6321\u8fd9\u4e5d\u5927\u9ad8\u624b\u7280\u5229\u7684\u529f\u52bf\u3002 \u3000\u3000\u7075\u86c7\u6bdb\u684c\u97ad\u68a2\u524d\u63a0\uff0c\u201c\u5427\u201d\u5730\u5728\u4ed6\u8138\u4e0a\u6253\u4e86\u4e00\u9053\u8840\u8ff9\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u8eab\u4e0a\u6240\u53d7\u7684\u4f24\uff0c\u5df2\u6709\u6570\u5341\u5904\u4e86\uff0c\u4f46\u662f\u4ed6\u7edd\u4e0d\u653e\u5f03\u6700\u540e\u6323\u624e\u7684\u673a\u4f1a\uff0c\u8fd9\u5e76\u4e0d\u662f\u8bf4\u4ed6 \u5bf9\u8fd9\u4eba\u4e16\u8fd8\u6709\u4efb\u4f55\u7559\u604b\u7684\u5730\u65b9\uff0c\u56e0\u4e3a\u8fd9\u4e16\u754c\u6240\u65bd\u4e8e\u4ed6\u7684\uff0c\u7684\u786e\u662f\u592a\u6b8b\u9177\u4e86\u4e9b\uff0c\u5f53\u7136\uff0c\u8fd9\u4e5f\u8bb8 \u5927\u591a\u662f\u4ed6\u81ea\u53d6\u7684\u3002 \u3000\u3000\u4f46\u662f\u4e00\u79cd\u672c\u80fd\u7684\u6c42\u751f\u7684\u6b32\u671b\uff0c\u4ecd\u4f7f\u4ed6\u5f3a\u81ea\u6323\u624e\u7740\uff0c\u5e94\u4ed8\u7740\u8fd9\u4e5d\u5927\u9ad8\u624b\u7280\u5229\u7684\u529f\u52bf\u3002 \u3000\u3000\u60f3\u5230\u201c\u5979\u201d\uff0c\u4ed6\u4e0d\u7981\u5fc3\u91cc\u4e00\u9635\u9635\u5267\u75db\u3002 \u3000\u3000\u5fc3\u91cc\u7684\u75bc\u75db\uff0c\u4f7f\u4ed6\u5fd8\u8bb0\u4e86\u6240\u53d7\u7684\u4f24\uff0c\u4f46\u662f\u81ea\u5bb6\u4f53\u5185\u771f\u6c14\u7684\u4e0d\u7ee7\uff0c\u4ed6\u5f53\u7136\u975e\u5e38\u6e05\u695a\u3002 \u3000\u3000\u201c\u6ca1\u6709\u591a\u4e45\u53ef\u6d3b\u4e86\u5427\uff01\u201d\u4ed6\u6697\u5fd6\uff0c\u5de6\u624b\u7684\u9a6c\u97ad\u5fae\u4e00\u758f\u5ffd\uff0c\u5728\u90a3\u4e0d\u662f\u7edd\u9876\u9ad8\u624b\u7edd\u96be\u53d1\u73b0\u7684 \u7a7a\u9699\uff0c\u9e33\u9e2f\u53cc\u5251\uff0c\u5251\u6263\u8fde\u73af\uff0c\u201c\u6bd4\u7ffc\u53cc\u98de\u201d\uff0c\u5530\u3001\u5530\u4e24\u5251\uff0c\u53c8\u5728\u4ed6\u5de6\u9762\u80f8\u8179\u4e4b\u95f4\u523a\u4e86\u4e24\u5251\u3002 \u3000\u3000\u8fd9\u65f6\u5019\uff0c\u5373\u4f7f\u4ed6\u6709\u518d\u5927\u7684\u96be\u5fc3\u58ee\u5fd7\uff0c\u4e5f\u90fd\u88ab\u6d88\u78e8\u6b86\u5c3d\u4e86\u3002 \u3000\u3000\u552f\u4e00\u4f7f\u4ed6\u4ecd\u672a\u5fd8\u6000\u7684\uff0c\u5c31\u662f\u4ed6\u7684\u8eab\u540e\u4e4b\u4e8b\uff0c\u5728\u8fd9\u6fd2\u4e34\u6b7b\u4ea1\u8fb9\u7f18\u4e00\u523b\uff0c\u8fd9\u4e00\u751f\u90e8\u5728\u5ac9\u4e16\u6124 \u4fd7\u7684\u8c6a\u58eb\uff0c\u4e5f\u672a\u80fd\u5154\u4fd7\u4e86\u3002 \u3000\u3000\u987b\u77e5\u4ed6\u81ea\u5df1\u4e5f\u77e5\u9053\uff0c\u4eca\u65e5\u4ed6\u4e00\u6b7b\uff0c\u6b66\u6797\u4e2d\u662f\u5f88\u5c11\u6709\u4eba\u5bf9\u4ed6\u60cb\u60dc\uff0c\u6216\u662f\u540c\u60c5\u7684\u3002 \u3000\u3000\u201c\u6b7b\uff0c\u672c\u4e0d\u8db3\u60dc\uff01\u201d\u4ed6\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u5de6\u97ad\u53f3\u5251\uff0c\u5c3d\u529b\u6321\u5f00\u4e86\u7075\u86c7\u7684\u4e09\u97ad\uff0c\u6797\u7426\u7b5d\u4e01\u8863\u7684 \u4e24\u5251\uff0c\u6697\u5fd6\u7740\uff1a\u201c\u4f46\u662f\u4eca\u65e5\u6211\u4e00\u6b7b\uff0c\u5374\u4e0d\u514d\u6b7b\u5f97\u592a\u60b2\u54c0\u4e86\uff0c\u6b7b\u5728\u8fd9\u822c\u4eba\u624b\u91cc\uff0c\u4e5f\u672a\u514d\u592a\u4e0d\u503c \u5f97\u4e86\u3002\u201d \u3000\u3000\u5fae\u4e00\u758f\u795e\uff0c\u80cc\u540e\u53c8\u4e2d\u4e86\u4e00\u5251\uff0c\u82e5\u4e0d\u662f\u4ed6\u5185\u529f\u5df2\u5230\u4e86\u7089\u706b\u7eaf\u9752\u7684\u5730\u6b65\uff0c\u82e5\u662f\u6362\u4e86\u4efb\u4f55\u4e00\u4e2a \u4eba\uff0c\u6050\u6015\u4e5f\u4e0d\u80fd\u518d\u652f\u6301\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u201c\u5c06\u6ca1\u6709\u4e00\u4e2a\u4eba\u77e5\u9053\u8fd9\u4ef6\u4e8b\u7684\u771f\u8c61\u3002\u201d\u59d4\u5c48\u548c\u4e0d\u5e73\uff0c\u4f7f\u4ed6\u7b2c\u4e00\u6b21\u611f\u89c9\u5230\u771f\u6b63\u7684\u60b2\u54c0\uff0c\u4ed6 \u6697\u5fd6\uff1a\u201c\u6240\u6709\u7684\u4eba\u90fd\u5c06\u4ee5\u4e3a\u6211\u662f\u6b7b\u5728\u8fd9\u2018\u4e03\u5251\u4e09\u97ad\uff0c\u624b\u91cc\uff0c\u2014\u2014\u53ef\u662f\uff0c\u53c8\u6709\u8c01\u4f1a\u77e5\u9053\u6211\u662f\u6b7b \u5728\u4e00\u4e2a\u5973\u4eba\u624b\u91cc\uff0c\u4e00\u4e2a\u6beb\u65e0\u5ec9\u803b\uff0c\u4e5f\u6beb\u65e0\u60c5\u611f\u7684\u5973\u4eba\u624b\u91cc\u3002\u201d\u4ed6\u5b8c\u5168\u8f6f\u5f31\u4e86\u4e00\u4e00\u7075\u86c7\u6bdb\u81ec\u5f97 \u610f\u5730\u6840\u6840\u602a\u7b11\u7740\uff0c\u8bf4\u9053\uff1a\u201c\u59d3\u4ec7\u7684\uff0c\u6709\u4ec0\u4e48\u540e\u4e8b\uff0c\u8d81\u4f60\u8fd8\u5269\u6700\u540e\u4e00\u53e3\u6c14\uff0c\u5feb\u8bf4\u51fa\u6765\u5427\uff0c\u6211\u770b \u5728\u6211\u90a3\u4f4d\u597d\u59b9\u59b9\u9762\u5b50\u4e0a\uff0c\u4e5f\u8bb8\u8fd8\u4f1a\u66ff\u4f60\u529e\u4e00\u529e\uff0c\u4f60\u8981\u662f\u518d\u4e0d\u8bf4\uff0c\u563f\u563f\uff01\u6050\u6015\u4f60\u518d\u4e5f\u2014\u2014\u201d\u4ec7 \u72ec\u4e00\u751f\u4e2d\uff0c\u4f55\u66fe\u88ab\u4eba\u5982\u6b64\u595a\u843d\u8fc7\uff1f \u3000\u3000\u66f4\u4f7f\u4ed6\u6c14\u6124\u7684\uff0c\u662f\u522b\u4eba\u5bf9\u4ed6\u5c3d\u60c5\u7684\u5632\u5f04\uff0c\u4ed6\u5c3d\u529b\u4e00\u58f0\u6012\u559d\uff0c\u53f3\u624b\u731b\u6325\uff0c\u5251\u5316\u957f\u8679\uff0c\u8131\u624b \u800c\u98de\uff0c\u76f4\u53d6\u7075\u86c7\u6bdb\u81ec\u3002 \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u518d\u4e5f\u60f3\u4e0d\u5230\u4ed6\u4f1a\u6709\u6b64\u4e00\u7740\uff0c\u7b49\u4ed6\u53d1\u89c9\u7684\u65f6\u5019\uff0c\u5251\u5149\u5df2\u5230\u4e86\u4ed6\u54bd\u5589\u4e4b\u95f4\uff0c\u5251\u7684\u6765 \u52bf\u592a\u5feb\uff0c\u8fd9\u6b66\u6797\u7b2c\u4e00\u5947\u4eba\u4e34\u6b7b\u524d\u6700\u540e\u7684\u4e00\u5251\uff0c\u58f0\u52bf\u4f55\u7b49\u60ca\u4eba\uff0c\u7075\u86c7\u6bdb\u81ec\u773c\u770b\u5c31\u8981\u88ab\u4f24\u5728\u8fd9\u4e00 \u5251\u4e4b\u4e0b\u3002 \u3000\u3000\u7a81\u5730\uff0c\u201c\u545b\u5577\u201d\u4e00\u58f0\u5de8\u54cd\uff0c\u539f\u6765\u5de6\u624b\u795e\u5251\u4e01\u8863\u4e00\u62db\u201c\u7075\u9e64\u5c55\u7ffc\u201d\uff0c\u672c\u662f\u659c\u524a\u4ec7\u72ec\u7684\u5de6 \u80a9\uff0c\u6b64\u523b\u4ed6\u89c1\u52bf\u5982\u6b64\uff0c\u5251\u5f0f\u5fae\u8f6c\uff0c\u786c\u751f\u751f\u5241\u5728\u90a3\u4ec7\u72ec\u8131\u624b\u63b7\u5411\u7075\u86c7\u6bdb\u81ec\u7684\u957f\u5251\u4e0a\u3002 \u3000\u3000\u4f46\u9976\u662f\u5982\u6b64\uff0c\u4ee5\u5de6\u624b\u795e\u5251\u4e01\u8863\u90a3\u6837\u7684\u529f\u529b\uff0c\u5c24\u4e0d\u80fd\u5c06\u90a3\u5251\u5288\u843d\u5728\u5730\u4e0a\uff0c\u53ea\u662f\u7a0d\u8bb8\u5288\u504f\u4e86 \u4e9b\u3002 \u3000\u3000\u5251\u7684\u53bb\u52bf\u4e5f\u7a0d\u5fae\u51cf\u5f31\u4e86\u4e9b\uff0c\u7075\u86c7\u6bdb\u81ec\u5f80\u540e\u4ef0\u8eab\uff0c\u5530\u5730\uff0c\u957f\u5251\u81ea\u4ed6\u9888\u4fa7\u63a0\u4e86\u8fc7\u53bb\uff0c\u53ea\u8981\u7a0d \u4e3a\u518d\u504f\u5c11\u8bb8\uff0c\u7075\u86c7\u6bdb\u81ec\u54ea\u91cc\u8fd8\u6709\u547d\u5728\u3002 \u3000\u3000\u4ed6\u60ca\u9b42\u521d\u5b9a\uff0c\u638c\u5fc3\u5df2\u6c81\u51fa\u51b7\u6c57\uff0c\u989d\u4e0a\u4e5f\u73b0\u51fa\u8c46\u5927\u6c57\u73e0\u3002 \u3000\u3000\u5de6\u624b\u795e\u5251\u4e01\u8863\u4e5f\u81ea\u9762\u76ee\u53d8\u8272\uff0c\u4ed6\u5168\u529b\u4e00\u5251\uff0c\u5288\u5728\u4ec7\u72ec\u5df2\u7ecf\u8131\u624b\u7684\u5251\u4e0a\uff0c\u624b\u8155\u4ecd\u88ab\u9707\u5f97\u9690 \u9690\u9690\u4f5c\u75db\uff0c\u5fc3\u91cc\u4e0d\u7981\u6697\u9a87\u4ec7\u72ec\u7684\u529f\u529b\u3002 \u3000\u3000\u4e5f\u5c31\u5728\u8fd9\u540c\u4e00\u5239\u90a3\u91cc\uff0c\u4ec7\u72ec\u957f\u5251\u65b9\u81ea\u8131\u624b\uff0c\u56e0\u4e3a\u4ed6\u662f\u5168\u529b\u4e00\u51fb\uff0c\u5de6\u624b\u7684\u97ad\u52bf\u529b\u81ea\u7136\u4e5f\u505c \u987f\u4e86\uff0c\u8fd9\u6837\u4ed6\u5b88\u52bf\u5168\u5931\uff0c\u5728\u8fd9\u79cd\u5c40\u9762\u4e0a\u7109\u5bb9\u4f60\u6709\u7247\u523b\u7684\u505c\u987f\uff0c\u4ed6\u751a\u81f3\u770b\u90fd\u6ca1\u6709\u770b\u6e05\u4ed6\u7684\u5251\u6709 \u6ca1\u6709\u51fb\u4e2d\u6bdb\u81ec\uff0c\u9e33\u9e2f\u53cc\u5251\uff0c\u5df4\u5c71\u5251\u5ba2\uff0c\u9752\u840d\u5251\uff0c\u6cb3\u6714\u53cc\u5251\u91cc\u7684\u6c6a\u4e00\u9e23\uff0c\u767e\u6b65\u98de\u82b1\u6797\u7426\u7b5d\uff0c\u4e03 \u661f\u97ad\u675c\u4ef2\u5947\u7684\u4e94\u67c4\u957f\u5251\uff0c\u4e24\u6761\u957f\u97ad\uff0c\u5251\u5149\u4ea4\u9519\uff0c\u5954\u96f7\u9a87\u7535\u822c\uff0c\u90fd\u5241\u5728\u4ec7\u72ec\u8eab\u4e0a\u3002 \u3000\u3000\u5927\u5730\u4ecd\u7136\u662f\u65e0\u661f\u65e0\u6708\uff0c\u4e00\u7247\u9ed1\u6697\uff0c\u5c71\u6797\u91cc\u6851\u9e1f\u591c\u557c\uff0c\u4f3c\u4e4e\u5728\u4e3a\u8fd9\u4e00\u4ee3\u5947\u4eba\u7684\u6b7b\u800c\u60b2\u54c0\u3002 \u3000\u3000\u7b49\u5230\u7075\u86c7\u6bdb\u81ec\u795e\u77e5\u6e05\u695a\u7684\u65f6\u5019\uff0c\u4ec7\u72ec\u5df2\u5b8c\u5168\u6c14\u7edd\u4e86\uff0c\u4eba\u4e16\u95f4\u7684\u8363\u8fb1\uff0c\u5df2\u4e0d\u518d\u80fd\u5f71\u54cd\u5230 \u4ed6\u3002 \u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u7247\u523b\u9759\u5bc2\u4e00\u4e00 \u3000\u3000\u7a81\u7136\u7075\u86c7\u6bdb\u81ec\u8fde\u58f0\u602a\u7b11\uff0c\u8eab\u5f62\u52a8\u5904\uff0c\u4e00\u4e2a\u7bad\u6b65\u7a9c\u4e86\u4e0a\u53bb\uff0c\u731b\u5730\u4e00\u97ad\uff0c\u6253\u5728\u4ec7\u72ec\u7684\u5c38\u8eab \u4e0a\u3002 \u3000\u3000\u4ed6\u7684\u957f\u97ad\u4e43\u767e\u70bc\u7f05\u94c1\u6240\u6253\u9020\u7684\uff0c\u518d\u52a0\u4e0a\u60ca\u4eba\u7684\u5185\u529b\uff0c\u8fd9\u4e00\u97ad\u4f55\u6b62\u5343\u767e\u65a4\u529b\u91cf\u3002 \u3000\u3000\u9c9c\u8840\u4ecd\u6e29\uff0c\u8fdc\u8fdc\u6e85\u5230\u5730\u4e0a\uff0c\u4ec7\u72ec\u7684\u4e00\u6761\u5de6\u81c2\uff0c\u5df2\u88ab\u51fb\u65ad\u3002 \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u97ad\u68a2\u4e00\u6643\uff0c\u4e00\u5e26\uff0c\u5c06\u4ec7\u72ec\u7684\u65ad\u81c2\uff0c\u5377\u4e0a\u53bb\uff0c\u5de6\u624b\u5fae\u6284\uff0c\u6284\u5728\u624b\u91cc\uff0c\u7b11\u58f0\u663e\u5f97\u66f4 \u72f0\u72de\u548c\u66f4\u523a\u8033\u4e86\u3002 \u3000\u3000\u6c5f\u5357\u5927\u4fa0\u5b8b\u4ee4\u516c\u7709\u5fc3\u5fae\u76b1\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4ec7\u67d0\u4eba\u5df2\u7ecf\u6b7b\u4e86\uff0c\u6bdb\u5144\u4f55\u82e6\u8fd8\u8981\u4f5c\u8d31\u4ed6\u7684\u5c38 \u4f53\uff1f\u201d\u9752\u840d\u5251\u5b8b\u4ee4\u516c\u4e00\u751f\u6b63\u76f4\uff0c\u65b9\u624d\u4ed6\u542c\u4e86\u7075\u86c7\u6bdb\u81ec\u7684\u8bdd\uff0c\u5df2\u7565\u4e3a\u6709\u4e9b\u77e5\u9053\u5728\u8fd9\u65e5\u4e4b\u524d\uff0c\u7075 \u86c7\u6bdb\u81ec\u5df2\u7528\u8be1\u8ba1\u4f24\u4e86\u4ec7\u72ec\uff0c\u662f\u4ee5\u4ec7\u72ec\u624d\u4f1a\u4e0d\u80fd\u8d77\u7acb\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u5fc3\u91cc\u5df2\u5fae\u6709\u4e86\u4e9b\u60ed\u6127\uff0c\u4f46\u662f\u4ec7\u72ec\u7684\u6240\u4f5c\u6240\u4e3a\uff0c\u66f4\u4f7f\u5b88\u6b63\u4e0d\u963f\u7684\u4ed6\u89c9\u5f97\u618e\u6068\uff0c\u4f55\u51b5 \u53d1\u8d77\u6b7c\u706d\u4ec7\u72ec\uff0c\u672c\u662f\u4ed6\u81ea\u5df1\uff0c\u7565\u4e00\u6743\u8861\uff0c\u4ed6\u5c31\u987e\u4e0d\u5f97\u5185\u5fc3\u7684\u60ed\u6127\uff0c\u800c\u4e0b\u624b\u53bb\u56f4\u653b\u4e00\u4e2a\u5df2\u662f\u534a \u8eab\u4f24\u6b8b\u7684\u4eba\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u89c1\u4e86\u7075\u86c7\u6bdb\u81ec\u7684\u4e3e\u6b62\uff0c\u5fc3\u91cc\u8d8a\u53d1\u4e0d\u6ee1\uff0c\u624d\u53d1\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u6bdb\u81ec\u602a\u7b11\u7740\u8bf4\uff1a\u201c\u8fd9\u59d3\u4ec7\u7684\u6215\u5bb3\u6b66\u6797\u540c\u7c7b\uff0c\u4e0d\u77e5\u6709\u591a\u5c11\u4e2a\u6c5f\u6e56\u540c\u9053\u88ab\u8fd9\u53ae\u5bb3\u5f97\u5bb6\u7834\u4eba \u4ea1\uff0c\u6211\u6068\u4e0d\u80fd\u98df\u5176\u8089\uff0c\u5bdd\u5176\u76ae\uff01\u201d \u3000\u3000\u4ed6\u4f83\u4f83\u800c\u8a00\uff0c\u5fc3\u91cc\u5c45\u7136\u6ca1\u6709\u4e00\u4e1d\u60ed\u6127\uff1a\u201c\u4eca\u65e5\u4f60\u6211\u5144\u5f1f\u65e2\u7136\u5c06\u8fd9\u53ae\u9664\u53bb\uff0c\u6b66\u6797\u4e2d\u4e0d\u77e5\u6709 \u591a\u5c11\u4eba\u8981\u629a\u638c\u79f0\u5feb\uff0c\u5144\u5f1f\u8fd9\u91cc\u5012\u6709\u4e2a\u5efa\u8bae\uff0c\u4f60\u6211\u5927\u5bb6\u5c06\u8fd9\u53ae\u4e71\u5200\u5206\u5c38\uff0c\u4e00\u4eba\u62ff\u53bb\u4e00\u5757\uff0c\u5e26\u7ed9 \u6b66\u6797\u4e2d\u7684\u5f1f\u5144\u4eec\u770b\u770b\uff0c\u4e5f\u8ba9\u5927\u5bb6\u5fc3\u91cc\u6b22\u559c\u3002\u201d \u3000\u3000\u6cb3\u6714\u53cc\u5251\uff0c\u767e\u6b65\u98de\u82b1\u7b49\uff0c\u5fc3\u91cc\u5404\u6709\u5bf9\u4ec7\u72ec\u7684\u6028\u6bd2\uff0c\u95fb\u8a00\u7acb\u523b\u54c4\u7136\u79f0\u597d\u3002 \u3000\u3000\u9e33\u9e2f\u53cc\u5251\uff0c\u5de6\u624b\u795e\u5251\u4e01\u8863\uff0c\u4e03\u661f\u97ad\u675c\u4ef2\u5947\u7b49\uff0c\u5fc3\u91cc\u65e0\u751a\u8ba1\u8f83\uff0c\u4f46\u4e00\u60f3\u5230\u82e5\u62ff\u5230\u4ec7\u72ec\u7684\u4e00 \u5757\u80a2\u4f53\uff0c\u56de\u5230\u6545\u4e61\uff0c\u81ea\u5df1\u5728\u6c5f\u6e56\u4e2d\u7684\u5730\u4f4d\u5fc5\u7136\u589e\u9ad8\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u53cd\u5bf9\u4e86\u3002 \u3000\u3000\u6c6a\u4e00\u9e4f\u53f3\u81c2\u88ab\u6298\uff0c\u65b0\u4ec7\u66f4\u6df1\uff0c\u5927\u6b65\u8de8\u4e86\u4e0a\u53bb\uff0c\u4e00\u628a\u593a\u8fc7\u6c6a\u4e00\u545c\u624b\u91cc\u7684\u5251\uff0c\u5530\u5730\uff0c\u53c8\u5c06\u4ec7 \u72ec\u7684\u53f3\u81c2\u5378\u4e0b\uff0c\u6311\u5728\u5251\u5c16\u4e0a\uff0c\u54ac\u7259\u8bf4\u9053\uff1a\u201c\u6211\u8981\u5c06\u8fd9\u53ae\u7684\u9aa8\u5934\uff0c\u597d\u597d\u4fdd\u7559\u5728\u5bb6\u91cc\uff0c\u4f20\u4e4b\u540e \u4ee3\uff0c\u8ba9\u8fd9\u53ae\u7684\u5c38\u9aa8\uff0c\u5343\u767e\u5e74\u4e5f\u4e0d\u80fd\u590d\u53e4\u3002\u54c8\uff0c\u8fd9\u624d\u6d88\u4e86\u6211\u5fc3\u5934\u4e4b\u6068\uff01\u201d \u3000\u3000\u6c6a\u4e00\u9e4f\u518d\u53c8\u4e00\u5251\u5288\u4e0b\uff0c\u53e3\u4e2d\u559d\u9053\uff1a\u201c\u5404\u4f4d\uff0c\u8fd8\u7b49\u4ec0\u4e48\uff0c\u4e0a\u5440\uff01\u201d \u3000\u3000\u970e\u773c\u4e4b\u95f4\uff0c\u4ec7\u72ec\u7684\u5c38\u8eab\u5df2\u662f\u80a2\u65ad\u9aa8\u6b8b\u4e86\u3002 \u3000\u3000\u5df4\u5c71\u5251\u5ba2\u4e00\u58f0\u957f\u53f9\uff0c\u671d\u9752\u840d\u5251\u9053\uff1a\u201c\u4e8b\u5df2\u81f3\u6b64\uff0c\u592b\u590d\u4f55\u8a00\u4ed6\u4e3a\u4eba\u6700\u662f\u51b2\u548c\uff0c\u4e0d\u613f\u5728\u8fd9\u4e9b \u4eba\u91cc\u663e\u5f97\u592a\u8fc7\u7279\u6b8a\uff0c\u66f4\u4e0d\u613f\u88ab\u522b\u4eba\u8ba4\u4e3a\u4ed6\u662f\u6545\u4f5c\u4f2a\u5584\u7684\uff0c\u5530\u5730\uff0c\u4e5f\u5728\u4ec7\u72ec\u7684\u5c38\u8eab\u4e0a\u53d6\u4e86\u4e00\u7247 \u6b8b\u9aa8\u3002\u8840\u8165\u4e4b\u6c14\uff0c\u5728\u6df1\u591c\u6e05\u51b7\u7684\u79cb\u98ce\u91cc\uff0c\u4f20\u51fa\u53bb\u8001\u8fdc\uff0c\u8001\u8fdc\u2014\u2014\u7a81\u7136\u4e00\u5c71\u6797\u91cc\u6709\u4e00\u58f0\u51b7\u7b11\uff0c \u4e00\u4e2a\u4ee4\u4eba\u542c\u4e86\u6781\u4e3a\u4e0d\u8212\u670d\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u597d\u72e0\uff01\u201d \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u66b4\u559d\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d\u5934\u4e5f\u672a\u56de\uff0c\u8eab\u5f62\u5012\u7eb5\uff0c\u7a9c\u5411\u5c71\u6797\u91cc\u3002 \u3000\u3000\u8fd9\u5341\u4eba\u4ff1\u662f\u6b66\u6797\u4e2d\u9876\u513f\u5c16\u513f\u7684\u9ad8\u624b\uff0c\u95fb\u58f0\u4e4b\u540e\uff0c\u5404\u5404\u8eab\u5f62\u66b4\u52a8\uff0c\u7a9c\u56de\u5c71\u6797\u91cc\u3002 \u3000\u3000\u6c5f\u5357\u5927\u4fa0\u5b8b\u4ee4\u516c\u5374\u4ecd\u5c79\u7acb\u4e0d\u52a8\uff0c\u770b\u7740\u4ec7\u72ec\u7684\u5c38\u8eab\uff0c\u5fc3\u91cc\u4e0d\u89c9\u611f\u6168\u4e07\u5343\u3002 \u3000\u3000\u8fd9\u4e8b\u662f\u4ed6\u53d1\u52a8\u7684\uff0c\u4f46\u662f\u4ed6\u7edd\u672a\u60f3\u5230\u4f1a\u6709\u8fd9\u6837\u6b8b\u9177\u7684\u540e\u679c\u3002 \u3000\u3000\u867d\u7136\u4ed6\u6781\u7aef\u4e0d\u6ee1\u610f\u4ec7\u72ec\u5728\u6b66\u6797\u4e2d\u7684\u6240\u4f5c\u6240\u4e3a\uff0c\u4f46\u662f\u5982\u4eca\u4ed6\u770b\u4e86\u8fd9\u88ab\u6b66\u6797\u4e2d\u89c6\u4e3a\u9b3c\u602a\u7684\u5947 \u4eba\uff0c\u80a2\u4f53\u51c4\u60e8\u5730\u3001\u96f6\u4e71\u5730\u840e\u987f\u5728\u5730\u4e0a\uff0c\u5fc3\u4e2d\u5374\u53c8\u6709\u4e9b\u4fa7\u7136\u3002 \u3000\u3000\u65c1\u8fb9\u662f\u4ed6\u90a3\u5339\u5c3d\u5fe0\u4e3a\u4e3b\u7684\u826f\u9a79\uff0c\u9c9c\u8840\u56db\u4e0b\u6d41\u843d\u5728\u5730\u4e0a\u3002 \u3000\u3000\u5c71\u6797\u91cc\u53c8\u6709\u591c\u884c\u4eba\u8863\u8882\u5e26\u98ce\u548c\u53f1\u54a4\u95ee\u8bdd\u7684\u58f0\u97f3\u3002 \u3000\u3000\u591c\u98ce\u5df2\u6709\u4e9b\u51c9\u610f\uff0c\u5439\u5f97\u6811\u679d\u4e0a\u5c06\u843d\u672a\u843d\u7684\u53f6\u5b50\u98d2\u7136\u4f5c\u54cd\u3002 \u3000\u3000\u8fd9\u666f\u8c61\u662f\u51c4\u51c9\u7684\u3002 \u3000\u3000\u6c5f\u5357\u5927\u4fa0\u4e00\u54ac\u7259\uff0c\u5fc3\u91cc\u65ad\u7136\u6709\u4e86\u4e2a\u51b3\u5b9a\uff0c\u8dd1\u8fc7\u53bb\u4e00\u628a\u62b1\u8d77\u4ec7\u72ec\u5269\u4e0b\u5934\u548c\u8eaf\u5e72\u7684\u5c38\u9ab8\uff0c\u4e5f \u4e0d\u987e\u8840\u6d41\u5728\u4ed6\u5e72\u51c0\u7684\u8863\u88f3\u4e0a\u3002 \u3000\u3000\u4ed6\u7565\u4e3a\u671d\u56db\u5468\u671b\u4e86\u671b\uff0c\u811a\u5c16\u987f\u5904\uff0c\u8eab\u5f62\u63a0\u8d77\uff0c\u5411\u5c71\u4e0b\u5954\u53bb\u3002 \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u7eb5\u5165\u5c71\u6797\uff0c\u60ca\u5f97\u5c71\u6797\u91cc\u7684\u5bbf\u9e1f\uff0c\u96f6\u4e71\u5730\u98de\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u8eab\u5f62\u5728\u6811\u5e72\u4e0e\u6811\u5e72\u4e4b\u95f4\uff0c\u6781\u5feb\u5730\u79fb\u52a8\u7740\uff0c\u624b\u91cc\u7684\u957f\u97ad\uff0c\u6392\u8d77\u4e00\u5ea7\u97ad\u5c71\uff0c\u56db\u4e0b\u6325\u6253\u3002 \u3000\u3000\u4f46\u662f\u5c71\u6797\u9664\u4e86\u5bbf\u9e1f\u7684\u60ca\u8d77\u4e4b\u5916\uff0c\u7edd\u6ca1\u6709\u4efb\u4f55\u5176\u4ed6\u7684\u53cd\u5e94\u3002 \u3000\u3000\u8fd9\u65f6\u9e33\u9e2f\u53cc\u5251\uff0c\u6cb3\u6714\u53cc\u5251\u4ee5\u53ca\u5de6\u624b\u795e\u5251\uff0c\u5df4\u5c71\u5251\u5ba2\u7b49\u7b49\uff0c\u4e5f\u90fd\u63a0\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u201c\u5927\u4f19\u56db\u4e0b\u641c\u641c\u770b\u3002\u201d\u7075\u86c7\u6bdb\u81ec\u4ee5\u4f4e\u6c89\u7684\u58f0\u97f3\u671d\u4ed6\u4eec\u8bf4\u3002 \u3000\u3000\u4e03\u661f\u97ad\u675c\u4ef2\u5947\u9ad8\u559d\uff1a\u201c\u76f8\u597d\u7684\uff0c\u6709\u79cd\u5c31\u51fa\u6765\u4eae\u4e2a\u76f8\uff0c\u522b\u85cf\u5934\u7f29\u5c3e\u7684\uff0c\u50cf\u4e2a\u8017\u5b50\u3002\u201d \u3000\u3000\u4ed6\u5173\u5916\u7c97\u8c6a\u7684\u53e3\u97f3\uff0c\u5728\u9759\u591c\u91cc\u66f4\u662f\u6d2a\u4eae\u3002 \u3000\u3000\u4f46\u662f\u5c71\u6797\u4e2d\u5374\u50cf\u4e1d\u6beb\u6ca1\u6709\u4eba\u8ff9\u7684\u6837\u5b50\uff0c\u9976\u662f\u8fd9\u4e9b\u6b66\u6797\u9ad8\u624b\u4ee5\u7edd\u5999\u7684\u8f7b\u529f\u641c\u7d22\u7740\uff0c\u4f46\u5374\u4e5f \u6ca1\u6709\u4efb\u4f55\u4eba\u88ab\u641c\u51fa\u6765\u3002 \u3000\u3000\u201c\u8fd9\u5c0f\u5b50\u7684\u8eab\u6cd5\u5012\u633a\u5feb\u3002\u201d\u7075\u86c7\u6bdb\u81ec\u4f4e\u9a82\u7740\uff0c\u624b\u91cc\u7684\u97ad\u51fb\u5f97\u6811\u5e72\u5427\u5427\u4f5c\u54cd\u3002 \u3000\u3000\u5de6\u624b\u795e\u5251\u4e01\u8863\u9053\uff1a\u201c\u641c\u4e0d\u5230\u5c31\u7b97\u4e86\uff0c\u53cd\u6b63\u6211\u4eec\u4e5f\u5e76\u4e0d\u5728\u4e4e\u3002\u201d\u5728\u4ed6\u5fc3\u4e2d\u6240\u60f3\u7684\u662f\uff0c\u53cd\u6b63 \u4eca\u65e5\u4e4b\u4e8b\u662f\u8981\u516c\u8bf8\u6b66\u6797\uff0c\u6709\u4eba\u77e5\u9053\u53c8\u6709\u4f55\u59a8\u3002 \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u773c\u73e0\u4e00\u52a8\uff0c\u6709\u4e9b\u4e8b\u4ed6\u867d\u7136\u4e0d\u613f\u522b\u4eba\u77e5\u9053\uff0c\u4f46\u662f\u8fd9\u4ef6\u4e8b\u662f\u522b\u4eba\u7edd\u96be\u77e5\u9053\u7684\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u4e5f\u9ad8\u58f0\u8bf4\u3002 \u3000\u3000\u201c\u5bf9\uff0c\u8c05\u4ed6\u4e0d\u8fc7\u53ea\u662f\u4e2a\u89c1\u4e0d\u5f97\u4eba\u7684\u9f20\u8f88\uff01\u201d \u3000\u3000\u8bdd\u4e00\u8bf4\u5b8c\uff0c\u4ed6\u9996\u5148\u7eb5\u51fa\u6797\u53bb\uff0c\u4f46\u662f\u6797\u5916\u6b64\u523b\u4e5f\u4e0d\u662f\u4ed6\u4eec\u79bb\u5f00\u65f6\u7684\u6837\u4e8e\u3002 \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u9996\u5148\u53d1\u73b0\u7684\u662f\uff0c\u5730\u4e0a\u4ec7\u72ec\u7684\u6b8b\u5c38\u5df2\u5931\u8e2a\u3002 \u3000\u3000\u4ed6\u5440\u5730\u4e00\u58f0\uff0c\u63a0\u4e86\u8fc7\u53bb\uff0c\u5ffd\u7136\u77a5\u5230\u9a6c\u8eab\u4e0a\u516b\u4e2a\u7528\u8840\u5199\u6210\u7684\u5927\u5b57\uff1a\u201c\u5341\u5e74\u4e4b\u540e\uff0c\u4ee5\u8840\u8fd8 \u8840\uff01\u201d \u3000\u3000\u4ed6\u7684\u8138\u8272\uff0c\u987f\u65f6\u53d8\u5f97\u5f02\u6837\u7684\u82cd\u767d\uff0c\u62ff\u7740\u4ec7\u72ec\u6b8b\u9aa8\u7684\u5de6\u624b\uff0c\u4e5f\u4e0d\u514d\u6709\u4e9b\u5fae\u5fae\u98a4\u6296\u3002 \u3000\u3000\u7b49\u5230\u5176\u4ed6\u7684\u4eba\u770b\u5230\u8fd9\u5b57\u8ff9\u65f6\uff0c\u4ed6\u4eec\u7684\u8868\u60c5\u4e5f\u662f\u540c\u6837\u5730\uff1a\u201c\u8fd9\u5b57\u662f\u8c01\u5199\u7684\u5462\uff1f\u201d \u3000\u3000\u4ed6\u4eec\u5fc3\u91cc\u4e0d\u7ea6\u800c\u540c\u5730\u6709\u7740\u540c\u4e00\u60f3\u6cd5\uff0c\u4e03\u661f\u97ad\u675c\u4ef2\u5947\u56db\u4e0b\u987e\u76fc\uff0c\u5ffd\u7136\u53eb\u9053\uff1a\u201c\u9752\u840d\u5251\u5b8b\u5927 \u4fa0\u5462\uff1f\u201d \u7b2c\u4e8c\u7ae0>> \u53e4\u9f99\u300a\u6e58\u5983\u5251\u300b \u7b2c\u4e8c\u7ae0 \u3000\u3000\u6c5f\u5357\u7684\u6625\u5929\uff0c\u662f\u591a\u5f69\u800c\u7eda\u4e3d\u7684\u3002 \u3000\u3000\u6c5f\u5357\u7684\u79cb\u5929\uff0c\u5374\u4e5f\u5e76\u4e0d\u8427\u7d22\u3002 \u3000\u3000\u5929\u9ad8\u6c14\u723d\uff0c\u6cbf\u8fd0\u6cb3\u81f3\u889c\u9675\u7684\u5b98\u9053\u4e0a\uff0c\u5c18\u571f\u98de\u626c\uff0c\u7ed3\u4f19\u5954\u6765\u4e00\u7fa4\u5feb\u9a6c\uff0c\u9a6c\u53e3\u767d\u6cab\u6a2a\u98de\uff0c\u9a6c \u4e0a\u7684\u4eba\u5374\u662f\u4e2a\u4e2a\u6c14\u5b9a\u795e\u95f2\uff0c\u50cf\u662f\u5e76\u6ca1\u6709\u5c06\u8fd9\u957f\u9014\u7684\u5954\u9a70\u653e\u5728\u5fc3\u4e0a\uff0c\u4f46\u662f\u5947\u602a\u7684\u5374\u662f\u9a6c\u4e0a\u7684\u4eba \u6bcf\u4e00\u4e2a\u90fd\u53cc\u7709\u6df1\u9501\uff0c\u6bcf\u4e2a\u4eba\u90fd\u4eff\u4f5b\u6709\u7740\u5f88\u5927\u7684\u5fc3\u4e8b\u3002 \u3000\u3000\u5b98\u9053\u7684\u884c\u4eba\u8fdc\u8fdc\u5730\u671b\u89c1\u8fd9\u4e00\u7fa4\u5feb\u9a6c\u5954\u81f3\uff0c\u90fd\u8d76\u7d27\u8eb2\u5f00\uff0c\u8be7\u5f02\u5730\u76f8\u8be2\uff1a\u201c\u8fd9\u4e00\u7fa4\u4eba\u662f\u4ec0\u4e48 \u6765\u8def\uff1f\u201d \u3000\u3000\u7686\u56e0\u8fd9\u4e00\u7fa4\u9a91\u58eb\u4e0d\u4f46\u4e2a\u4e2a\u88c5\u675f\u8be1\u5f02\uff0c\u800c\u4e14\u6709\u7537\u6709\u5973\uff0c\u8eab\u4e0a\u90fd\u5e26\u7740\u5175\u5203\uff0c\u5728\u8fd9\u6587\u91c7\u98ce\u6d41\u7684 \u6c5f\u5357\u9053\u4e0a\uff0c\u663e\u5f97\u592a\u8fc7\u624e\u773c\u3002 \u3000\u3000\u84e6\u5730\uff0c\u8def\u7684\u4e00\u7aef\u54cd\u8d77\u5639\u4eae\u7684\u547c\u58f0\uff1a\u201c\u632f\u6b66\u2014\u2014\u626c\u5a01\u2014\u2014\u3002\u201d \u3000\u3000\u58f0\u54cd\u9ad8\u8fdc\u800c\u60a0\u957f\uff0c\u6563\u5e03\u5728\u56db\u91ce\u3002 \u3000\u3000\u8def\u4e0a\u6709\u7684\u4e45\u8d70\u6c5f\u6e56\u7684\u884c\u4eba\uff0c\u4e00\u542c\u5c31\u77e5\u9053\u8fd9\u662f\u6c5f\u5357\u6700\u5927\u7684\u9556\u5c40\uff0c\u6c5f\u82cf\u9547\u6c5f\u5e9c\u632f\u6b66\u9556\u5c40\u7684\u8d9f \u5b50\u624b\u5728\u8d70\u7f25\u65f6\u558a\u9556\u7684\u58f0\u97f3\u3002 \u3000\u3000\u9a6c\u4e0a\u7684\u9a91\u58eb\u4eec\u7565\u4e00\u56de\u5934\uff0c\u4ecd\u7136\u6025\u9a70\u5411\u524d\uff0c\u773c\u770b\u5c31\u8981\u95ef\u5165\u632f\u6b66\u9556\u5c40\u8d70\u9556\u7684\u961f\u4f0d\u3002 \u3000\u3000\u4e8e\u662f\u6709\u597d\u4e8b\u7684\u8def\u4eba\u90fd\u9a7b\u4e86\u811a\uff0c\u4f4e\u58f0\u5730\u9053\uff1a\u201c\u6709\u70ed\u95f9\u77a7\u4e86\u3002\u201d \u3000\u3000\u987b\u77e5\u6c5f\u6e56\u4e0a\u884c\u9053\u7684\uff0c\u9664\u975e\u5b98\u5e9c\u6216\u662f\u5175\u5352\u4e4b\u5916\uff0c\u5c31\u7b97\u662f\u6210\u7fa4\u7ed3\u961f\u7684\u5ba2\u5546\uff0c\u82e5\u662f\u89c1\u4e86\u8d70\u9556\u7684 \u9556\u961f\uff0c\u4e5f\u591a\u662f\u8fdc\u8fdc\u907f\u5f00\uff0c\u4ece\u6765\u4e0d\u4f1a\u6709\u4eba\u95ef\u5165\u9556\u961f\u7684\uff0c\u8fd9\u4e00\u6765\u56fa\u7136\u662f\u884c\u8def\u7684\u4eba\u8c01\u4e5f\u4e0d\u613f\u610f\u6dfb\u9ebb \u70e6\u3001\u591a\u4e8b\uff0c\u4e8c\u6765\u4e5f\u662f\u9556\u5c40\u5728\u5f53\u65f6\u7684\u52bf\u529b\u592a\u5927\uff0c\u51b2\u6563\u4e86\u4ed6\u4eec\u7684\u9556\uff0c\u5373\u662f\u72af\u4e86\u4ed6\u4eec\u7684\u5927\u5fcc\uff0c\u975e\u8981 \u548c\u4f60\u89c1\u4e2a\u771f\u7ae0\u4e0d\u53ef\u3002 \u3000\u3000\u8fd9\u4e9b\u5feb\u9a6c\u9a91\u58eb\uff0c\u770b\u4e0a\u53bb\u56fa\u7136\u662f\u6709\u4e9b\u65a4\u4e24\uff0c\u4f46\u632f\u6b66\u9556\u5c40\u7684\u603b\u9556\u5934\u98de\u8679\u5251\u5c60\u68a6\u5e73\uff0c\u5728\u6c5f\u5357\u4e5f \u662f\u7d20\u79f0\u624e\u624b\u7684\u4eba\u7269\uff0c\u624b\u4e0b\u7684\u9556\u5e08\u4eec\uff0c\u4e5f\u90fd\u662f\u6840\u50b2\u4e0d\u9a6f\u7684\u89d2\u8272\uff0c\u600e\u4f1a\u5bb9\u5f97\u522b\u4eba\u95ef\u6563\u81ea\u5bb6\u7684\u9556 \u961f\u3002 \u3000\u3000\u662f\u4ee5\u90a3\u4e9b\u4e45\u8d70\u6c5f\u6e56\u7684\u8def\u4eba\u4eec\uff0c\u90fd\u77e5\u9053\u8fd9\u4e00\u5b9a\u6709\u70ed\u95f9\u597d\u770b\u4e86\uff0c\u4e8b\u4e0d\u5173\u5df2\uff0c\u53c8\u90fd\u77e5\u9053\u4e71\u4e8b\u4e0d \u4f1a\u6ce2\u53ca\u5230\u81ea\u5df1\u5934\u4e0a\uff0c\u5927\u5bb6\u4e5f\u90fd\u4e50\u5f97\u770b\u4e2a\u70ed\u95f9\u3002 \u3000\u3000\u54ea\u77e5\u4e8b\u60c5\u5927\u8c2c\u4e0d\u7136\u2014\u2014\u3002 \u3000\u3000\u90a3\u7fa4\u5065\u9a6c\uff0c\u9a6c\u4e0d\u505c\u8e44\uff0c\u98ce\u9a70\u7535\u63a3\u822c\u5954\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u632f\u6b66\u9556\u5c40\u7684\u8d9f\u5b50\u624b\u770b\u89c1\u4e86\uff0c\u679c\u7136\u6c14\u5f80\u4e0a\u649e\uff0c\u7709\u4e00\u7ad6\uff0c\u773c\u4e00\u77aa\uff0c\u5c31\u51c6\u5907\u7834\u53e3\u5927\u9a82\u3002 \u3000\u3000\u94c1\u53eb\u4e8e\u5c0f\u6c88\uff0c\u662f\u632f\u6b66\u9556\u5c40\u6700\u5f97\u529b\u7684\u8d9f\u5b50\u624b\uff0c\u5f80\u65e5\u706b\u6c14\u6700\u5927\uff0c\u4eca\u65e5\u89c1\u4e86\u6709\u4eba\u95ef\u961f\uff0c\u6697\u9a82\uff1a \u201c\u8fd9\u7fa4\u9e1f\u86cb\uff0c\u771f\u662f\u6d3b\u5f97\u4e0d\u8010\u70e6\u4e86\u3002\u201d\u3000\u3000\u4e24\u7247\u8584\u5634\u5507\u4e00\u6380\uff0c\u7834\u53e3\u9053\uff1a\u201c\u76f8\u597d\u7684\u2014\u2014\u201d\u773c\u89d2\u4e00 \u98d8\uff0c\u89c1\u7b2c\u4e00\u3001\u4e8c\u5339\u9a6c\u4e0a\u9a91\u58eb\u7684\u8138\u5b54\uff0c\u51db\u7136\u4e00\u60ca\uff0c\u8d76\u7d27\u5c06\u4e0b\u9762\u7684\u8bdd\uff0c\u54bd\u4e86\u56de\u809a\u91cc\u3002 \u3000\u3000\u4ed6\u4e00\u7f29\u8116\uff0c\u6697\u81ea\u79f0\u5e78\uff1a\u201c\u8fd8\u7b97\u6211\u59d3\u6c88\u7684\u798f\u5927\u9020\u5316\u5927\uff0c\u603b\u7b97\u8ba4\u5f97\u8fd9\u51e0\u4f4d\u4e3b\u513f\uff0c\u563f\uff01\u6211\u8fd9\u8981 \u662f\u4e00\u9a82\u5440\uff0c\u6211\u5c0f\u6c88\u7684\u4e50\u5b50\u5c31\u5927\u4e86\u3002\u201d\u4ed6\u662f\u5317\u65b9\u4eba\uff0c\u867d\u7136\u4e45\u5c45\u6c5f\u5357\uff0c\u8bed\u58f0\u91cc\u4ecd\u4e0d\u8131\u5317\u65b9\u5473\u513f\u3002 \u3000\u3000\u53e6\u4e00\u4e2a\u8d9f\u5b50\u624b\u5927\u7ea6\u89c1\u5f97\u8fd8\u4e0d\u5e7f\uff0c\u4e0d\u5206\u9752\u7ea2\u7682\u767d\uff0c\u5c31\u9a82\u4e86\u51fa\u6765\uff1a\u201c\u9f9f\u5b59\u5b50\uff0c\u8d70\u8def\u6ca1\u6709\u5e26\u7740 \u773c\u775b\u5440\uff01\u201d \u3000\u3000\u8bdd\u8fd8\u6ca1\u6709\u9a82\u5b8c\uff0c\u88ab\u5bf9\u9762\u9a6c\u4e0a\u7684\u9a91\u58eb\uff0c\u9a6c\u97ad\u4e00\u62bd\uff0c\u7adf\u5c06\u81ea\u5df1\u4ece\u9a6c\u978d\u4e0a\u76f4\u98de\u4e86\u51fa\u53bb\uff0c\u201c\u5427\u201d \u5730\u4e00\u58f0\uff0c\u91cd\u91cd\u5730\u6454\u5728\u8def\u65c1\u7684\u4e71\u8349\u91cc\u3002 \u3000\u3000\u9556\u961f\u5fae\u4e71\u3002 \u3000\u3000\u90a3\u7fa4\u5feb\u9a6c\u4e5f\u5f53\u7136\u88ab\u963b\uff0c\u9a6c\u4e0a\u7684\u4eba\u4e2a\u4e2a\u94c1\u9752\u7740\u8138\uff0c\u51b7\u773c\u671b\u7740\u9556\u5c40\u91cc\u7684\u9556\u4f19\uff0c\u8d9f\u5b50\u624b\u4eec\u5fd9 \u4e71\uff0c\u559d\u9a82\uff0c\u6709\u7684\u5df2\u7ecf\u8981\u6284\u5bb6\u4f19\u52a8\u624b\u4e86\u3002 \u3000\u3000\u94c1\u53eb\u5b50\u5c0f\u6c88\u5b9a\u4e86\u5b9a\u795e\uff0c\u4e24\u53cc\u4e4c\u5149\u6e9c\u6e9c\u7684\u5c0f\u773c\u775b\uff0c\u518d\u5728\u90a3\u7fa4\u5feb\u9a6c\u4e0a\u7684\u9a91\u58eb\u8eab\u4e0a\u6253\u4e86\u4e00\u8f6c\u3002 \u3000\u3000\u4ed6\u5fcd\u4e0d\u4f4f\u54bd\u4e86\u4e00\u53e3\u5410\u6cab\uff0c\u6697\u81ea\u64e6\u6c57\uff0c\u5fd6\u9053\uff1a\u201c\u4e56\u4e56\uff0c\u539f\u6765\u5168\u6765\u4e86\u5440\uff01\u201d \u3000\u3000\u9556\u5c40\u91cc\u7684\u8d9f\u5b50\u624b\u4ee5\u53ca\u9556\u4f19\u4eec\uff0c\u4e2a\u4e2a\u90fd\u5c06\u5175\u5203\u6284\u5728\u624b\u4e0a\u3002 \u3000\u3000\u6709\u7684\u5708\u9a6c\u56de\u9a70\uff0c\u51c6\u5907\u53bb\u62a5\u544a\u8fd9\u6b21\u62bc\u9556\u7684\u5e08\u5085\uff0c\u5c0f\u4e27\u95e8\u5218\u5b9a\u56fd\uff0c\u795e\u9556\u5ba2\u94b1\u5b97\u6e0a\uff0c\u5176\u5b9e\u4ed6\u4eec \u5e72\u8fd9\u884c\u7684\u773c\u775b\u53ef\u662f\u96ea\u4eae\u7684\uff0c\u7109\u6709\u770b\u4e0d\u51fa\u8fd9\u4e00\u7fa4\u4eba\u96be\u7f20\u7684\u9053\u7406\uff0c\u53ea\u662f\u4ed6\u4eec\u8fd8\u4e0d\u77e5\u9053\u8fd9\u7fa4\u4eba\u7a76\u7adf \u662f\u8c01\u7f62\u4e86\u3002 \u3000\u3000\u9556\u8f66\u4e00\u884c\u5341\u4f59\u8f86\uff0c\u663e\u89c1\u5f97\u8fd9\u8d9f\u4ed6\u4eec\u4fdd\u7684\u5b9a\u662f\u91cd\u9556\uff0c\u9556\u4f19\u4eec\u66f4\u7d27\u5f20\uff0c\u751f\u6015\u8fd9\u7fa4\u4eba\u662f\u6765\u52ab \u9556\u3002 \u3000\u3000\u201c\u4f46\u662f\u53c8\u6709\u8c01\u4f1a\u5728\u5149\u5927\u5316\u65e5\u4e4b\u4e0b\uff0c\u884c\u4eba\u4f17\u591a\u7684\u9053\u4e0a\u660e\u76ee\u5f20\u80c6\u5730\u52ab\u9556\u5462\uff1f\u201d \u3000\u3000\u9556\u5c40\u91cc\u7684\u9556\u4f19\u4eec\uff0c\u5251\u62d4\u5f29\u5f20\uff0c\u773c\u770b\u5c31\u8981\u6709\u4e00\u756a\u6df7\u6218\uff0c\u8d9f\u5b50\u624b\u94c1\u53eb\u5b50\u5c0f\u6c88\u4e00\u770b\u4e8b\u60c5\u4e0d\u5999\uff0c \u6025\u5f97\u9ad8\u58f0\u558a\u9053\uff1a\u201c\u54e5\u513f\u4eec\uff0c\u5feb\u522b\u52a8\u624b\u3002\u201d \u3000\u3000\u9556\u4f19\u4eec\u4e00\u6115\uff0c\u65b9\u81ea\u9519\u7591\u5e73\u65e5\u706b\u66b4\u706b\u71ce\u7684\u5c0f\u6c88\u4eca\u5929\u600e\u4ed6\u8bf4\u51fa\u4e86\u8fd9\u7b49\u8bdd\u6765\uff0c\u94c1\u53eb\u5b50\u5c0f\u6c88\u5df2\u8fde \u7740\u558a\u9053\uff1a\u201c\u8fd9\u51e0\u4f4d\u5c31\u662f\u2018\u4e03\u5251\u4e09\u97ad\u2019\u3002\u201d \u3000\u3000\u8fd9\u53ef\u771f\u662f\uff1a\u201c\u4eba\u7684\u540d\u513f\uff0c\u6811\u7684\u5f71\u513f\u3002\u201d\u4e03\u5251\u4e09\u97ad\u5728\u6c5f\u6e56\u4e0a\u58f0\u540d\u663e\u8d6b\uff0c\u632f\u6b66\u9556\u5c40\u7684\u603b\u9556\u5934 \u98de\u8679\u5251\u5c60\u68a6\u5e73\uff0c\u4e5f\u662f\u201c\u4e03\u5251\u4e09\u97ad\u201d\u91cc\u6c5f\u5357\u5927\u4fa0\u9752\u840d\u5251\u5b8b\u4ee4\u516c\u7684\u4eb2\u4f20\u5f1f\u5b50\uff0c\u632f\u6b66\u9556\u5c40\u5f97\u4ee5\u7acb\u8db3 \u6c5f\u5357\uff0c\u591a\u591a\u5c11\u5c11\u4e5f\u6cbe\u4e86\u6c5f\u5357\u5927\u4fa0\u9752\u840d\u5251\u5b8b\u4ee4\u516c\u7684\u5149\u3002 \u3000\u3000\u632f\u6b66\u9556\u5c40\u7684\u9556\u4f19\u4eec\u4e00\u542c\u5230\u4e03\u5251\u4e09\u97ad\u56db\u4e2a\u5b57\uff0c\u968f\u65f6\u51c6\u5907\u6301\u80f3\u818a\u6253\u67b6\u7684\u76db\u6c14\uff0c\u4e0d\u7531\u6536\u5f97\u5e72\u5e72 \u51c0\u51c0\uff0c\u8fd9\u51e0\u4e4e\u662f\u4e00\u79cd\u8fd1\u4e8e\u672c\u80fd\u7684\u4e3e\u6b62\uff0c\u5f53\u4eba\u4eec\u542c\u4e86\u4e00\u4ef6\u8db3\u4ee5\u4ee4\u4ed6\u60ca\u9519\u7684\u4e8b\u65f6\uff0c\u5927\u534a\u4f1a\u6709\u8fd9\u79cd \u73b0\u8c61\u53d1\u751f\u3002 \u3000\u3000\u4e00\u77ac\u95f4\uff0c\u7a7a\u6c14\u50cf\u662f\u7a81\u7136\u51dd\u7ed3\u4e86\uff0c\u53ea\u6709\u9a6c\u5339\u5728\u4e0d\u5b89\u5730\u79fb\u52a8\u65f6\u6240\u53d1\u51fa\u7684\u8e44\u58f0\uff0c\u6572\u6253\u7740\u4eba\u4eec\u672c \u6765\u5df2\u7ecf\u975e\u5e38\u7d27\u5f20\u7684\u5fc3\u3002 \u3000\u3000\u4e03\u5251\u4e09\u97ad\u4ecd\u7136\u662f\u4e2a\u4e2a\u9762\u5982\u51dd\u971c\uff0c\u94c1\u53eb\u5b50\u5c0f\u6c88\u770b\u770b\u7b2c\u4e00\u5339\u9a6c\u4e0a\u6325\u97ad\u6454\u4eba\u7684\u9a91\u58eb\uff0c\u4e5f\u5c31\u662f\u6d59 \u6c5f\u5927\u8c6a\u7075\u86c7\u6bdb\u81ec\u7684\u90a3\u79cd\u51b7\u51b0\u51b0\u7684\u9762\u5bb9\uff0c\u5fc3\u91cc\u89c9\u5f97\u4e00\u80a1\u51b7\u6c14\u76f4\u5f80\u4e0a\u5192\uff0c\u6084\u6084\u5730\u5c06\u9a6c\u5f80\u5916\u5708\uff0c\u8fd9 \u4ef6\u4e8b\u4ed6\u5b9a\u4e0d\u4e0b\u4efb\u4f55\u4e3b\u610f\uff0c\u53ea\u6709\u53bb\u8bf7\u793a\u62bc\u9556\u7684\u9556\u5e08\u4e86\u3002 \u3000\u3000\u539f\u6765\u62bc\u9556\u7684\u9556\u5e08\u5c0f\u4e27\u95e8\u5218\u5b9a\u56fd\uff0c\u795e\u9556\u5ba2\u94b1\u5b97\u6e0a\uff0c\u5e73\u65e5\u67b6\u5b50\u751a\u5927\uff0c\u518d\u8005\u4e5f\u662f\u4ed7\u7740\u632f\u6b66\u9556\u5c40 \u5728\u6c5f\u5357\u4e00\u5e26\u6240\u6811\u7acb\u7684\u58f0\u5a01\uff0c\u7edd\u5bf9\u77e5\u9053\u4e0d\u4f1a\u6709\u4eba\u52ab\u9556\u3002 \u3000\u3000\u56e0\u6b64\u4ed6\u4eec\u5c45\u7136\u8fdc\u8d70\u5728\u540e\u9762\uff0c\u5bf9\u8fd9\u5341\u51e0\u8f86\u9556\u8f66\uff0c\u7b80\u76f4\u6709\u70b9\u4e0d\u95fb\u4e0d\u95ee\u7684\uff0c\u6b64\u523b\u542c\u4e86\u6709\u4eba\u6765\u95ef \u9556\u961f\uff0c\u50cf\u662f\u8981\u52ab\u9556\u4f3c\u7684\uff0c\u4e24\u4eba\u8fd9\u624d\u7740\u614c\uff0c\u4e00\u7d27\u9a6c\u7f30\uff0c\u98de\u5feb\u5730\u8d76\u5230\u524d\u9762\u6765\u3002 \u3000\u3000\u4e8e\u662f\u9556\u5c40\u7684\u9556\u4f19\u4eec\u8fd9\u624d\u677e\u4e86\u4e00\u53e3\u6c14\uff0c\u6709\u7684\u751a\u81f3\u8fdc\u8fdc\u5730\u7ad9\u4e86\u5f00\u53bb\u3002\u795e\u9556\u5ba2\u94b1\u5b97\u6e0a\u6765\u81ea\u5173 \u5916\uff0c\u9a91\u5728\u9a6c\u80cc\u4e0a\u603b\u6bd4\u522b\u4eba\u8981\u9ad8\u51fa\u534a\u4e2a\u5934\uff0c\u5a01\u98ce\u51db\u51db\u5730\uff0c\u5012\u4e5f\u50cf\u662f\u6761\u6c49\u5b50\uff0c\u770b\u5230\u9556\u4f19\u4eec\u5f80\u540e \u9000\uff0c\u6c14\u5f97\u5927\u9a82\u9053\uff1a\u201c\u5988\u62c9\u4e2a\u5df4\u5b50\uff0c\u4f60\u4eec\u5f80\u540e\u9000\u4e2a\u4ec0\u4e48\u52b2\u513f\uff1f\u201d\u773c\u795e\u5f80\u5bf9\u9762\u7684\u9a91\u58eb\u4e00\u626b\uff0c\u4ed6\u4e45 \u8d70\u6c5f\u6e56\uff0c\u522b\u4eba\u4e0d\u8bf4\uff0c\u5c31\u5728\u6c5f\u82cf\u9694\u58c1\u7684\u6d59\u6c5f\u7701\u7684\u7075\u86c7\u6bdb\u81ec\uff0c\u4ed6\u5f53\u7136\u8ba4\u5f97\uff0c\u4e0d\u7531\u5f97\u5934\u76ae\u53d1\u9ebb\uff0c\u5750 \u5728\u9a6c\u4e0a\u6602\u85cf\u8eab\u8eaf\uff0c\u4e5f\u50cf\u662f\u7a81\u7136\u77ee\u4e86\u4e24\u5bf8\u3002 \u3000\u3000\u201c\u600e\u5730\u662f\u8fd9\u4f4d\u4e3b\u513f\uff1f\u201d\u4ed6\u6697\u5fd6\u9053\uff0c\u56de\u5934\u4e00\u671b\uff0c\u770b\u5230\u5c0f\u4e27\u95e8\u4e5f\u662f\u60ca\u7591\u6ee1\u9762\uff0c\u539f\u6765\u5c0f\u4e27\u95e8\u8d70 \u6c5f\u6e56\u7684\u65e5\u5b50\u66f4\u957f\uff0c\u201c\u4e03\u5251\u4e09\u97ad\u201d\u4ed6\u5012\u8ba4\u4e5d\u4f4d\u3002 \u3000\u3000\u201c\u600e\u5730\u8fd9\u51e0\u4f4d\u4f1a\u805a\u5230\u4e00\u5757\u513f\u6765\u4e86\uff1f\u201d\u5c0f\u4e27\u95e8\u6697\u6697\u5403\u60ca\uff0c\u8d76\u7d27\u7ffb\u8eab\u4e0b\u9a6c\uff0c\u62b1\u62f3\u62f1\u624b\u9053\uff1a \u201c\u524d\u8f88\u4eec\u600e\u5730\u4eca\u65e5\u6709\u5174\u6e38\u4fa0\u5230\u6c5f\u5357\u6765\uff1f\u201d \u3000\u3000\u4ed6\u9a71\u5f00\u4e86\u8fd8\u7ad9\u5728\u8def\u5f53\u4e2d\u7684\u9556\u4f19\uff0c\u62c9\u5f00\u4e86\u5927\u8f66\uff0c\u5728\u9053\u5f53\u4e2d\u8ba9\u51fa\u4e86\u4e00\u6761\u5bbd\u5bbd\u7684\u8def\u6765\uff0c\u53e3\u91cc\u966a \u7740\u7b11\u9053\uff1a\u201c\u665a\u8f88\u5f85\u547d\u5728\u8eab\uff0c\u8def\u9014\u4e2d\u4e5f\u4e0d\u4fbf\u62db\u5f85\u524d\u8f88\u4e00\u4e00\u201d\u7075\u86c7\u6bdb\u81ec\u9634\u51c4\u51c4\u7684\u4e00\u58f0\u51b7\u7b11\uff0c\u8bf4 \u9053\uff1a\u201c\u8c01\u8981\u4f60\u62db\u5f85\u5440\uff1f\u201d \u3000\u3000\u5c0f\u4e27\u95e8\u4e00\u6115\uff1a\u201c\u600e\u5730\u4ed6\u4eca\u65e5\u7684\u795e\u8272\u4e0d\u5bf9\u52b2\uff1f\u201d\u4ed6\u9519\u6115\u5730\u5728\u5fc3\u91cc\u601d\u5fd6\u7740\uff0c\u518d\u4e00\u770b\u53e6\u516b\u4eba\u7684 \u8138\u8272\uff0c\u5fc3\u91cc\u66f4\u662f\u6253\u9f13\uff1a\u201c\u600e\u5730\u8fd9\u51e0\u4f4d\u4eca\u5929\u770b\u8d77\u6765\u5168\u4e0d\u5bf9\uff0c\u7b80\u76f4\u6709\u70b9\u513f\u50cf\u6765\u751f\u4e8b\u5bfb\u4ec7\u7684\u6837\u5b50\uff0c \u53ef\u662f\u6211\u4eec\u9556\u5c40\u5e76\u6ca1\u6709\u5f97\u7f6a\u4ed6\u4eec\u5440\uff01\u6211\u4eec\u5c60\u603b\u9556\u5934\u8bf4\u8d77\u6765\u8ddf\u4ed6\u4eec\u8fd8\u662f\u4e00\u5bb6\u4eba\u5462\u3002\u201d \u3000\u3000\u4ed6\u7684\u731c\u6d4b\u53ef\u8fd8\u771f\u6ca1\u6709\u79bb\u8c31\uff0c\u4e03\u5251\u4e09\u97ad\u91cc\u7684\u7075\u86c7\u6bdb\u81ec\uff0c\u4e03\u661f\u97ad\u675c\u4ef2\u5947\uff0c\u767e\u6b65\u98de\u82b1\u6797\u7426\u7b5d\uff0c \u9e33\u9e2f\u53cc\u5251\uff0c\u5de6\u624b\u795e\u5251\u4ee5\u53ca\u6cb3\u6714\u53cc\u5251\u7b49\u4eba\uff0c\u6b64\u756a\u9080\u7ed3\u524d\u6765\uff0c\u679c\u771f\u662f\u4e3a\u4e86\u5bfb\u4ec7\u751f\u4e8b\u7684\u3002 \u3000\u3000\u718a\u8033\u5c71\u7554\uff0c\u4e03\u5251\u4e09\u97ad\u56f4\u6b7c\u4ec7\u72ec\u5f97\u624b\uff0c\u5c71\u6797\u7a81\u4f20\u51b7\u8bed\uff0c\u4ec7\u72ec\u6b8b\u9ab8\u987f\u5931\uff0c\u9a6c\u5c38\u4e0a\u5374\u7559\u4e0b\u4ee5\u8840 \u8fd8\u8840\u7684\u60ca\u8bed\uff0c\u8fd9\u4e5d\u4e2a\u6b66\u6797\u4e2d\u7684\u9b41\u9996\uff0c\u5168\u90fd\u4e00\u610f\u8ba4\u4e3a\u8fd9\u4e9b\u4e8b\u662f\u6c5f\u5357\u5927\u4fa0\u9752\u840d\u5251\u5b8b\u4ee4\u516c\u6240\u4e3a\u7684\u3002 \u3000\u3000\u4e8e\u662f\u9752\u840d\u5251\u6210\u4e86\u4e03\u5251\u4e09\u97ad\u4e2d\u53e6\u4e5d\u4eba\u7684\u5171\u540c\u7684\u654c\u4eba\uff0c\u7075\u86c7\u6bdb\u81ec\u66f4\u662f\u9a82\u53e3\u4e0d\u7edd\uff0c\u5df4\u5c71\u5251\u5ba2\u67f3 \u590d\u660e\u867d\u7136\u548c\u9752\u840d\u5251\u662f\u591a\u5e74\u4e4b\u4ea4\uff0c\u5fc3\u91cc\u4e5f\u4e0d\u514d\u5bf9\u9752\u840d\u5251\u5f88\u4e0d\u6ee1\uff0c\u8ba4\u4e3a\u4ed6\u8fd9\u4e8b\u672a\u514d\u505a\u5f97\u6709\u8fdd\u9053 \u4e49\u3002 \u3000\u3000\u82e5\u4ee5\u60c5\u7406\u800c\u8bba\uff0c\u8fd9\u201c\u4ee5\u8840\u8fd8\u8840\u201d\u51e0\u4e2a\u5b57\uff0c\u679c\u771f\u662f\u9752\u840d\u5251\u6240\u5199\u7684\u8bdd\uff0c\u90a3\u4e48\u8fd9\u6c5f\u5357\u5927\u4fa0\u7684\u6240 \u4f5c\u6240\u4e3a\uff0c\u4e5f\u5b9e\u5728\u6709\u4e9b\u83ab\u540d\u5176\u5999\uff0c\u56e0\u4e3a\u8fd9\u4e8b\u7684\u5021\u5bfc\u8005\uff0c\u4ed6\u81ea\u5df1\u4e5f\u662f\u5176\u4e2d\u4e4b\u4e00\u5440\uff01\u800c\u4ee5\u5f53\u65f6\u7684\u60c5 \u51b5\u800c\u8bba\uff0c\u4e5f\u5b9e\u4ee5\u4ed6\u7684\u53ef\u80fd\u6027\u80fd\u6700\u5927\uff0c\u7b49\u5230\u5df4\u5c71\u5251\u5ba2\u7b49\u786e\u5b9e\u5730\u6253\u542c\u51fa\u4ec7\u72ec\u7684\u6b8b\u9ab8\u679c\u7136\u662f\u5728\u9752\u840d \u5251\u4e4b\u5904\uff0c\u4ed6\u4eec\u5fc3\u4e2d\u81ea\u7136\u66f4\u65e0\u7591\u5ff5\u4e86\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4eec\u54ea\u91cc\u77e5\u9053\u6b64\u4e8b\u5176\u5b9e\u53e6\u6709\u6587\u7ae0\uff0c\u5176\u4e2d\u7684\u5965\u5999\uff0c\u53c8\u5c82\u662f\u4ed6\u4eec\u6240\u80fd\u6599\u60f3\u7684\u5462\uff1f \u3000\u3000\u4e8e\u662f\u7075\u86c7\u6bdb\u81ec\uff0c\u767e\u6b65\u98de\u82b1\uff0c\u6cb3\u6714\u53cc\u5251\u7b49\uff0c\u7387\u5148\u5728\u6c5f\u6e56\u4e0a\u6563\u5e03\u4e86\u6d41\u8a00\uff0c\u8bf4\u9752\u840d\u5251\u5b8b\u4ee4\u516c\u8868 \u9762\u4e0a\u867d\u7136\u505a\u51fa\u4ec1\u4e49\u9053\u5fb7\u7684\u9762\u5b54\uff0c\u5176\u5b9e\u5374\u548c\u4ec7\u72ec\u662f\u4e00\u4e18\u4e4b\u8c89\uff0c\u5e76\u4e14\u516c\u7136\u53d6\u51fa\u4ec7\u72ec\u7684\u6b8b\u9aa8\uff0c\u4f20\u89c6 \u6c5f\u6e56\uff0c\u8bf4\u4ec7\u72ec\u5df2\u7136\u4e27\u8eab\uff0c\u7b2c\u4e8c\u4e2a\u5c31\u8981\u8f6e\u5230\u9752\u840d\u5251\u4e86\u3002 \u3000\u3000\u4ec7\u72ec\u88ab\u6740\uff0c\u8fd9\u6d88\u606f\u662f\u7684\u786e\u4f7f\u5f97\u6b66\u6797\u9707\u60ca\u7684\uff0c\u987b\u77e5\u4ec7\u72ec\u5728\u5f53\u65e5\u6b66\u6797\u4e2d\u7684\u5730\u4f4d\uff0c\u662f\u65e0\u4e0e\u4f26\u6bd4 \u7684\uff0c\u8fd9\u4e48\u4e00\u6765\u7075\u86c7\u6bdb\u81ec\u5728\u6b66\u6797\u4e2d\u7684\u5730\u4f4d\u81ea\u7136\u4e5f\u5c31\u66f4\u63d0\u9ad8\u4e86\uff0c\u4ee4\u6b66\u6797\u540c\u9053\u4e0d\u89e3\u7684\u662f\uff0c\u7d20\u5f97\u4eba\u671b \u7684\u6c5f\u5357\u5927\u4fa0\u5b8b\u4ee4\u516c\uff0c\u600e\u4f1a\u548c\u6c5f\u6e56\u4e2d\u7684\u9b54\u661f\u4ec7\u72ec\u662f\u4e00\u8def\u7684\u5462\uff1f \u3000\u3000\u4f46\u662f\u7075\u86c7\u6bdb\u81ec\u5bf9\u4eba\u8bf4\u5f97\u6d3b\u7075\u6d3b\u73b0\uff0c\u53c8\u4f3c\u4e4e\u4e0d\u5bb9\u6000\u7591\u3002 \u3000\u3000\u6c5f\u6e56\u81ea\u7136\u662f\u4f20\u8bf4\u7eb7\u7eb7\uff0c\u7b49\u5230\u8fd9\u4ef6\u4e8b\u4f20\u5230\u6c5f\u5357\u65f6\uff0c\u7075\u86c7\u6bdb\u81ec\u5df2\u5b9a\u4e0b\u6bd2\u8ba1\uff0c\u8981\u5357\u4e0b\u79e3\u9675\uff0c\u56f4 \u6b7c\u9752\u840d\u5251\uff0c\u8981\u4f7f\u5f97\u4ed6\u5728\u6c5f\u6e56\u4e0a\u65e0\u6cd5\u7acb\u8db3\uff0c\u8fd8\u8981\u4ee4\u4ed6\u5bb6\u8d25\u4eba\u4ea1\uff0c\u5176\u5b9e\u4ed6\u4eec\u5982\u6b64\u505a\u7684\u7528\u610f\uff0c\u8fd8\u4e0d \u662f\u4e3a\u4e86\u60e7\u6015\u65e5\u540e\u7684\u62a5\u590d\uff0c\u201c\u4ee5\u8840\u8fd8\u8840\u201d\u8fd9\u56db\u4e2a\u5b57\uff0c\u4f7f\u5f97\u8fd9\u4e9b\u4e2a\u76ee\u65e0\u4f59\u5b50\u7684\u6b66\u6797\u9ad8\u624b\u4eec\uff0c\u98df\u4e0d \u5b89\u5473\uff0c\u5bdd\u4e0d\u5b89\u6795\u4e86\u3002 \u3000\u3000\u8fd9\u4ef6\u4e8b\u7684\u59cb\u672a\uff0c\u5c0f\u4e27\u95e8\u5218\u5b9a\u56fd\u81ea\u7136\u4e0d\u4f1a\u77e5\u9053\uff0c\u4ed6\u6bb7\u52e4\u800c\u606d\u8c28\u5730\u56de\u7740\u8bdd\uff0c\u751f\u4f2f\u4f7f\u5f97\u8fd9\u4e9b\u6b66 \u6797\u9ad8\u624b\u52a8\u6012\uff0c\u4f46\u662f\u4ed6\u5728\u7528\u5fc3\u673a\uff0c\u4eba\u5bb6\u5168\u4e0d\u5356\u8fd9\u4e2a\u5e10\u3002 \u3000\u3000\u4ed6\u5fc3\u91cc\u867d\u7136\u5df2\u5f00\u59cb\u4e0d\u5b89\uff0c\u4f46\u8fd8\u5e76\u4e0d\u5341\u5206\u60ca\u614c\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u8fd9\u4e9b\u4eba\u7eb5\u7136\u53d1\u6012\uff0c\u4f46\u5374\u7edd\u4e0d\u4f1a \u52a8\u624b\u52ab\u9556\uff0c\u4ee5\u8fd9\u4e9b\u4eba\u5728\u6b66\u6797\u4e2d\u7684\u5730\u4f4d\uff0c\u6700\u591a\u4e0d\u8fc7\u7ed9\u4ed6\u4e00\u4e2a\u96be\u582a\u800c\u5df2\uff0c\u8fd9\u79cd\u96be\u582a\uff0c\u4ed6\u4e5f\u81ea\u4fe1\u53ef \u4ee5\u5fcd\u53d7\u7684\u3002 \u3000\u3000\u201c\u4f60\u4eec\u7684\u603b\u9556\u5934\u53ef\u662f\u53eb\u98de\u8679\u5251\u7684\u5427\uff01\u201d\u7075\u86c7\u6bdb\u81ec\u4e0d\u5c51\u5730\u6253\u91cf\u7740\u5c0f\u4e27\u95e8\u548c\u795e\u9556\u5ba2\uff0c\u50b2\u7136\u5730 \u95ee\u7740\u8bdd\u3002 \u3000\u3000\u4e03\u661f\u97ad\u675c\u4ef2\u5947\u5728\u65c1\u8fb9\u63a5\u53e3\u9053\uff1a\u201c\u98de\u8679\u5251\u5c60\u68a6\u5e73\u53ef\u5c31\u662f\u9752\u840d\u5251\u5b8b\u8001\u513f\u7684\u5f92\u5f1f\uff1f\u201d \u3000\u3000\u5c0f\u4e27\u95e8\u6ca1\u6709\u542c\u51fa\u4ed6\u8bdd\u4e2d\u7684\u610f\u5473\uff0c\u5df4\u7ed3\u4ed6\u8bf4\u9053\uff1a\u201c\u662f\uff0c\u662f\uff0c\u6211\u4eec\u603b\u9556\u5934\u7684\u5e08\u5085\u5c31\u662f\u6c5f\u5357\u5927 \u4fa0\u5b8b\u8001\u524d\u8f88\uff0c\u4f60\u8001\u53ef\u8ba4\u8bc6\u4ed6\u8001\u4eba\u5bb6\uff1f\u201d \u3000\u3000\u5c0f\u4e27\u95e8\u5218\u5b9a\u56fd\u5728\u6b66\u6797\u4e2d\u7684\u5730\u4f4d\uff0c\u81ea\u7136\u65e0\u6cd5\u548c\u4e03\u5251\u4e09\u97ad\u76f8\u6bd4\uff0c\u662f\u4ee5\u4ed6\u65e0\u53ef\u5948\u4f55\u5730\u81ea\u5df1\u59d4\u66f2 \u7740\u81ea\u5df1\uff0c\u5180\u6c42\u5c06\u6bcf\u4e00\u4ef6\u4e8b\u90fd\u5b89\u6392\u5f97\u5f88\u597d\u3002 \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u7a81\u7136\u9ad8\u58f0\u4ef0\u5929\u800c\u53f7\uff0c\u53f7\u58f0\u7684\u523a\u8033\uff0c\u7b80\u76f4\u662f\u96be\u4ee5\u5f62\u5bb9\u7684\u3002 \u3000\u3000\u5c0f\u4e27\u95e8\u5218\u5b9a\u56fd\u5168\u7136\u6115\u4f4f\u4e86\uff0c\u795e\u9556\u5ba2\u4e5f\u4e0d\u7981\u7528\u8be7\u5f02\u7684\u76ee\u5149\u671b\u7740\u8fd9\u540d\u6ee1\u6c5f\u6e56\u7684\u6b66\u6797\u8c6a\u5ba2\u3002 \u3000\u3000\u53f7\u58f0\u7a81\u7136\u4e2d\u65ad\uff0c\u7075\u86c7\u6bdb\u81ec\u5c16\u523b\u4ed6\u8bf4\u9053\uff1a\u201c\u597d\u6781\u4e86\uff01\u597d\u6781\u4e86\uff01\u201d \u3000\u3000\u56de\u8fc7\u5934\u53bb\uff0c\u671d\u59cb\u7ec8\u6c89\u9ed8\u7740\u7684\u5176\u4ed6\u516b\u4eba\u4e00\u6325\u624b\uff0c\u9053\uff1a\u201c\u5404\u4f4d\uff0c\u770b\u5c0f\u5f1f\u7ed9\u8fd9\u4e9b\u4eba\u4e00\u4e2a\u6559 \u8bad\u3002\u201d\u81ea\u4ece\u718a\u8033\u5c71\u7554\u4e00\u5f79\u4e4b\u540e\uff0c\u7075\u86c7\u6bdb\u81ec\u65e0\u5f62\u4e2d\u6210\u4e86\u4e03\u5251\u4e09\u97ad\u7684\u9b41\u9996\uff0c\u5df4\u5c71\u5251\u5ba2\u67f3\u590d\u660e\u53cd\u800c \u9000\u5c45\u5176\u540e\u4e86\u3002 \u3000\u3000\u8bed\u58f0\u65b9\u4f4f\uff0c\u7075\u86c7\u6bdb\u81ec\u8155\u7ffb\u5904\uff0c\u5728\u6781\u5feb\u7684\u4e00\u5239\u90a3\u91cc\uff0c\u5df2\u5c06\u8170\u4e2d\u7684\u8f6f\u97ad\u64a4\u5728\u638c\u4e2d\uff0c\u4f38\u7f29\u4e4b \u95f4\uff0c\u97ad\u68a2\u6240\u5e26\u8d77\u7684\u98ce\u58f0\uff0c\u547c\u5578\u4f5c\u54cd\u3002 \u3000\u3000\u5c0f\u4e27\u95e8\u5218\u5b9a\u56fd\uff0c\u795e\u9556\u5ba2\u94b1\u5b97\u6e0a\u4ff1\u5404\u4e00\u60ca\uff0c\u4ed6\u4eec\u518d\u4e5f\u6599\u60f3\u4e0d\u5230\u7075\u86c7\u6bdb\u81ec\u4f1a\u64a4\u5175\u5203\u52a8\u624b\uff0c\u5218 \u5b9a\u56fd\u5728\u5200\u53e3\u8ba8\u751f\u6d3b\u5df2\u4e0d\u6b62\u4e00\u5e74\uff0c\u9047\u4e0a\u8fd9\u79cd\u4e8b\uff0c\u5012\u8fd8\u6c89\u5f97\u4f4f\u6c14\uff0c\u95f4\u9053\uff1a\u201c\u6bdb\u5927\u4fa0\uff0c\u8fd9\u662f\u5e72\u4ec0 \u4e48\uff1f\u201d\u8bf4\u8bdd\u4e5f\u6709\u4e9b\u4e0d\u81ea\u7136\u7684\u5473\u9053\u4e86\u3002 \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u9762\u5982\u5bd2\u51b0\uff0c\u8155\u65f6\u5fae\u4e00\u66f2\u4f38\uff0c\u957f\u97ad\u500f\u7136\u800c\u51fa\u201c\u795e\u868a\u51fa\u4e91\u201d\uff0c\u97ad\u68a2\u7b14\u76f4\u5730\u70b9\u5411\u5c0f\u4e27 \u95e8\u5218\u5b9a\u56fd\u7684\u53f3\u80f8\u7684\u201c\u671f\u95e8\u91cd\u7a74\u201d\u3002 \u3000\u3000\u5c0f\u4e27\u95e8\u5927\u60ca\uff0c\u5f80\u540e\u6025\u4ef0\uff0c\u4ed7\u7740\u4ed6\u5df2\u4e0b\u4e86\u9a6c\uff0c\u8eab\u5f62\u8f83\u4e3a\u7075\u6d3b\uff0c\u8eb2\u5f00\u6b64\u62db\uff0c\u5e76\u672a\u663e\u5f97\u592a\u8fc7\u5403 \u529b\uff0c\u5fc3\u4e2d\u65b9\u81ea\u6697\u5fd6\uff1a\u201c\u7075\u86c7\u4e0d\u8fc7\u5982\u6b64\u3002\u201d \u3000\u3000\u54ea\u77e5\u4ed6\u5ff5\u5934\u5c1a\u672a\u8f6c\u5b8c\uff0c\u97ad\u5f71\u5982\u4e1d\uff0c\u53c8\u5230\u81ea\u5df1\u5934\u4e0a\uff0c\u4ed6\u66f4\u5403\u60ca\uff0c\u8eab\u5f62\u5411\u5de6\u6025\u8f6c\uff0c\u54ea\u77e5\u90a3\u957f \u97ad\u5374\u50cf\u957f\u4e86\u773c\u775b\uff0c\u97ad\u62db\u7a81\u7136\u4e00\u5f2f\uff0c\u5c0f\u4e27\u95e8\u53ea\u89c9\u80c1\u4e0b\u4e00\u9ebb\uff0c\u8033\u7554\u542c\u5f97\u7075\u86c7\u6bdb\u81ec\u7684\u51b7\u54fc\uff0c\u4eba\u5df2\u7ecf \u865a\u8f6f\u5730\u5012\u5728\u5730\u4e0a\u3002 \u3000\u3000\u795e\u9556\u5ba2\u94b1\u5b97\u6e0a\u5389\u54a4\u4e00\u58f0\uff0c\u731b\u4e00\u626c\u8155\uff0c\u4e09\u9053\u9556\u5149\uff0c\u5728\u540c\u4e00\u65f6\u523b\u91cc\u7535\u95ea\u800c\u51fa\uff0c\u8fd9\u201c\u4e00\u624b\u4e09 \u9556\u201d\u672c\u662f\u795e\u9556\u5ba2\u94b1\u5b97\u6e0a\u626c\u540d\u6c5f\u6e56\u7684\u7edd\u6280\uff0c\u5bf9\u65b9\u7684\u4e0a\u4e2d\u4e0b\u4e09\u8def\uff0c\u51e0\u4e4e\u90fd\u5728\u4ed6\u7684\u9556\u5149\u7b3c\u7f69\u4e4b\u5185\u3002 \u3000\u3000\u795e\u9556\u5ba2\u51ed\u7740\u8fd9\u201c\u4e00\u624b\u4e09\u9556\u201d\u5012\u4e5f\u7684\u786e\u95ef\u8fc7\u4e0d\u5c11\u98ce\u9669\uff0c\u54ea\u77e5\u6b64\u523b\u9047\u89c1\u4e86\u7075\u86c7\u6bdb\u81ec\uff0c\u5374\u5b9b\u5982 \u513f\u620f\u4e86\u3002 \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u957f\u97ad\u6325\u52a8\uff0c\u4e00\u62db\uff0c\u2018\u5982\u86c6\u9644\u9aa8\u201d\uff0c\u4f24\u4e86\u5c0f\u4e27\u95e8\uff0c\u5934\u4e5f\u4e0d\u56de\uff0c\u53cd\u624b\u4e00\u97ad\uff0c\u5c06\u795e\u9556 \u5ba2\u94b1\u5b97\u6e0a\u4ed7\u4ee5\u6210\u540d\u7684\u4e09\u9556\uff0c\u8f7b\u6613\u5730\u51fb\u843d\u5728\u5730\u4e0a\u3002\u9556\u5c40\u91cc\u7684\u9556\u4f19\u4eec\u770b\u5230\u9556\u5e08\u88ab\u4f24\uff0c\u987f\u65f6\u5927\u4e71\uff0c \u8def\u65c1\u7684\u884c\u4eba\u4e5f\u6599\u4e0d\u5230\u771f\u4f1a\u52a8\u624b\u4f24\u4eba\uff0c\u800c\u4e14\u4f24\u7684\u8fd8\u662f\u632f\u6b66\u9556\u5c40\u7684\u9556\u5e08\uff0c\u6709\u4e9b\u6015\u4e8b\u7684\u811a\u5e95\u63e9\u6cb9\uff0c \u65e9\u5df2\u6e9c\u4e4b\u5927\u5409\u4e86\u3002\u4eba\u58f0\u6742\u4e71\u9a6c\u58f0\u957f\u5636\uff0c\u9053\u8def\u4e5f\u4e3a\u4e4b\u963b\u585e\uff0c\u7075\u86c7\u6bdb\u81ec\u505a\u7136\u56db\u987e\uff0c\u5ffd\u5730\u7eb5\u9a6c\u524d \u9a70\uff0c\u795e\u9556\u5ba2\u6a2a\u9a6c\u60f3\u62e6\u4f4f\u4ed6\uff0c\u7075\u86c7\u51b7\u7b11\u6325\u97ad\uff0c\u53e3\u91cc\u559d\u9a82\u9053\uff1a\u201c\u4f60\u627e\u6b7b\uff01\u201d \u3000\u3000\u638c\u4e2d\u957f\u97ad\u659c\u63a0\uff0c\u5728\u4e2d\u9014\u5ffd\u7136\u53d8\u4e86\u65b9\u5411\uff0c\u6539\u63a0\u4e3a\u70b9\uff0c\u62db\u5f0f\u4e4b\u8be7\u5f02\uff0c\u4f7f\u5f97\u5728\u6b66\u529f\u4e0a\u5e76\u6ca1\u6709\u591a \u5927\u6839\u57fa\u7684\u94b1\u5b97\u6e0a\u614c\u4e71\u5931\u63aa\uff0c\u7529\u8e6c\u4e0b\u9a6c\uff0c\u60f3\u907f\u5f00\u6b64\u62db\uff0c\u4f46\u4ee5\u4ed6\u8fd9\u79cd\u8eab\u624b\uff0c\u60f3\u907f\u5f00\u7075\u86c7\u6bdb\u81ec\u7684\u62db \u5f0f\uff0c\u8fd8\u5dee\u5f97\u5f88\u8fdc\u5462\u3002 \u3000\u3000\u4ed6\u5750\u4e0b\u7684\u9a6c\uff0c\u4e5f\u53d7\u5230\u60ca\u5413\uff0c\u53d1\u72c2\u5954\u53bb\uff0c\u795e\u9556\u5ba2\u94b1\u5b97\u6e0a\u7684\u5de6\u811a\uff0c\u8fd8\u5728\u9a6c\u8e6c\u4e0a\uff0c\u88ab\u9a6c\u62d6\u51fa\u53bb \u5f88\u8fdc\uff0c\u5730\u4e0a\u7684\u7802\u77f3\uff0c\u64e6\u5f97\u4ed6\u5168\u8eab\u51e0\u65e0\u4e00\u5904\u5b8c\u80a4\uff0c\u795e\u9556\u5ba2\u4e00\u8eab\u803f\u76f4\uff0c\u5374\u843d\u5f97\u8fd9\u822c\u4e0b\u573a\u3002 \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u7167\u9762\u90fd\u6ca1\u6709\u659c\u4e00\u4e0b\uff0c\u8eab\u5f62\u5ffd\u7136\u79bb\u978d\u800c\u8d77\uff0c\u8759\u8760\u822c\u5730\u98de\u63a0\u800c\u8fc7\uff0c\u5728\u7b2c\u4e00\u8f86\u9556\u8f66\u4e0a \u843d\u4e86\u4e0b\u6765\uff0c\u53e3\u4e2d\u559d\u4e00\u58f0\uff0c\u5de6\u638c\u7acb\u638c\u5982\u5200\uff0c\u6c14\u8d2f\u638c\u7f18\uff0c\u5530\u7684\u4e00\u638c\uff0c\u5c06\u5927\u8f66\u4e0a\u6728\u5236\u7684\u94f6\u9798\uff0c\u5288\u5f97 \u7247\u7247\u98de\u821e\uff0c\u94f6\u9798\u91cc\u4e94\u5341\u4e24\u4e00\u952d\u7684\u5b98\u5b9d\uff0c\u201c\u54d7\u7136\u201d\u4e00\u58f0\u6eda\u843d\u5728\u5730\u4e0a\u3002 \u3000\u3000\u65e5\u5149\u672a\u843d\uff0c\u7167\u5728\u8fd9\u4e9b\u94f6\u952d\u4e0a\uff0c\u53d1\u51fa\u4e00\u79cd\u4ee4\u4eba\u795e\u8361\u5fc3\u7729\u7684\u5149\u4eae\u3002 \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u5c79\u7136\u7ad9\u5728\u8f66\u4e0a\uff0c\u602a\u7b11\u7740\u8bf4\u8feb\uff1a\u201c\u8fd9\u4e9b\u94f6\u5b50\u5168\u662f\u4f60\u4eec\u7684\u4e86\uff0c\u8c01\u8981\u7684\uff0c\u5c3d\u7ba1\u62ff\u597d \u4e86\u3002\u201d\u773c\u795e\u56db\u626b\uff0c\u671b\u7740\u90a3\u4e9b\u4e24\u773c\u53d1\u76f4\u7684\u9556\u4f19\uff0c\u811a\u592b\uff0c\u4ee5\u53ca\u7ad9\u5728\u8def\u65c1\u4ecd\u5728\u770b\u70ed\u95f9\u7684\u4eba\u3002 \u3000\u3000\u5df4\u5c71\u5251\u5ba2\u5fae\u4e00\u76b1\u7709\uff0c\u6717\u58f0\u9053\uff1a\u201c\u6bdb\u8d24\u5f1f\u5207\u83ab\u9020\u6b21\u3002\u201d\u4ed6\u5b9e\u5728\u4e0d\u613f\u81ea\u5df1\u88ab\u7275\u5165\u8fd9\u4ef6\u4e8b\u7684\u6f29 \u6da1\u4e2d\uff0c\u4f46\u4ed6\u7d20\u6027\u65e0\u4e3a\uff0c\u4e5f\u6ca1\u6709\u65b9\u6cd5\u963b\u6b62\u3002 \u3000\u3000\u201c\u67f3\u9053\u957f\uff01\u201d\u7075\u86c7\u6bdb\u81ec\u5f97\u610f\u4ed6\u8bf4\uff1a\u201c\u4f60\u770b\u6211\u7684\u5427\uff01\u201d \u3000\u3000\u8eab\u5f62\u52a8\u5904\uff0c\u53c8\u63a0\u5230\u7b2c\u4e8c\u8f86\u5927\u8f66\u4e0a\uff0c\u7167\u65b9\u6293\u836f\uff0c\u6ca1\u6709\u591a\u5927\u4f1a\u529f\u592b\uff0c\u5341\u51e0\u8f86\u5927\u8f66\u91cc\u7684\u5341\u591a\u4e07 \u4e24\u94f6\u5b50\uff0c\u5168\u88ab\u5288\u843d\u5230\u5730\u4e0a\u3002 \u3000\u3000\u4f46\u89c1\u94f6\u5149\u707f\u7136\uff0c\u8000\u76ee\u751f\u82b1\uff0c\u8fd9\u79cd\u666f\u8c61\u7684\u786e\u662f\u96be\u4ee5\u63cf\u8ff0\u7684\u3002 \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u9ad8\u58f0\u9053\uff1a\u201c\u62ff\u5440\uff01\u62ff\u5440\uff01\u8fd9\u4e9b\u94f6\u5b50\u5168\u662f\u4f60\u4eec\u7684\u4e86\u3002\u201d\u957f\u97ad\u6325\u52a8\uff0c\u5c06\u5730\u4e0a\u7684\u94f6\u952d \u51fb\u5f97\u56db\u4e0b\u98de\u821e\uff0c\u6709\u7684\u751a\u81f3\u843d\u5230\u8def\u8fb9\u7684\u91ce\u8349\u91cc\u53bb\u4e86\u3002 \u3000\u3000\u8d22\u5e1b\u4e4b\u80fd\u6253\u52a8\u4eba\u5fc3\uff0c\u8fd9\u79cd\u529b\u91cf\u7684\u786e\u662f\u65e0\u6cd5\u6297\u62d2\u7684\uff0c\u9556\u5c40\u91cc\u7684\u9556\u4f19\uff0c\u811a\u592b\u4eec\u4e00\u751f\u4e2d\u51e0\u66fe\u89c1 \u8fc7\u8fd9\u8bb8\u591a\u94f6\u5b50\uff0c\u867d\u7136\u4e5f\u660e\u77e5\u8fd9\u4e9b\u94f6\u5b50\u662f\u62ff\u4e0d\u5f97\u7684\uff0c\u4f46\u5728\u8fd9\u79cd\u529b\u91cf\u7684\u8bf1\u60d1\u4e0b\uff0c\u4e0d\u7981\u5168\u7136\u5931\u53bb\u4e86 \u7406\u6027\uff0c\u518d\u4e5f\u987e\u4e0d\u5f97\u4e00\u5207\uff0c\u8fde\u6eda\u5e26\u722c\u5730\u5f2f\u4e0b\u8170\uff0c\u5c3d\u81ea\u5df1\u6700\u5927\u7684\u53ef\u80fd\u6765\u62fe\u53d6\u94f6\u952d\u3002 \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u5f97\u610f\u5730\u5927\u7b11\u7740\uff0c\u770b\u7740\u4eba\u4eec\u66b4\u9732\u51fa\u4eba\u6027\u7684\u5f31\u70b9\uff0c\u4ed6\u8ba4\u4e3a\u662f\u6700\u4ee4\u4ed6\u5174\u594b\u7684\u4e8b\u3002 \u3000\u3000\u4ed6\u6325\u52a8\u7740\u957f\u97ad\uff0c\u5728\u7a7a\u4e2d\u51fb\u5f97\u201c\u53ed\uff0c\u53ed\u201d\u4f5c\u54cd\u3002 \u3000\u3000\u5df2\u7ecf\u62ff\u5230\u4e86\u94f6\u5b50\u7684\u9556\u4f19\uff0c\u811a\u592b\u4eec\uff0c\u50cf\u662f\u4e00\u53ea\u53ea\u5077\u4e86\u4eba\u5bb6\u841d\u535c\u7684\u5154\u5b50\uff0c\u56db\u4e0b\u5954\u9003\u7740\uff0c\u8def\u65c1 \u7684\u884c\u4eba\u770b\u7684\u5982\u6b64\uff0c\u4e5f\u7981\u4e0d\u4f4f\u60f3\u53bb\u5206\u5f97\u4e00\u676f\u9152\uff0c\u524d\u6d8c\u540e\u4ec6\u5730\u5954\u4e0a\u53bb\uff0c\u970e\u773c\u95f4\uff0c\u666f\u8c61\u66f4\u4e71\uff0c\u53c8\u50cf \u662f\u4e00\u7fa4\u5728\u62a2\u7740\u4eba\u5bb6\u6254\u4e0b\u7684\u9aa8\u5934\u7684\u91ce\u72d7\u3002 \u3000\u3000\u5df4\u5c71\u5251\u5ba2\u67f3\u590d\u660e\u7d27\u76b1\u7740\u7709\uff0c\u957f\u53f9\u7740\uff0c\u54c0\u60bc\u7740\u4eba\u6027\u7684\u5351\u4e0b\u3002 \u3000\u3000\u4ed6\u773c\u5149\u4e00\u77ac\uff0c\u5ffd\u7136\u770b\u5230\u4e00\u4e2a\u7a7f\u7740\u5df2\u7ecf\u6d17\u5f97\u53d1\u767d\u7684\u84dd\u5e03\u957f\u886b\u7684\u5c11\u5e74\u6587\u58eb\uff0c\u52a8\u4e5f\u4e0d\u52a8\u5730\u7ad9\u5728 \u6df7\u4e71\u7684\u4eba\u7fa4\u91cc\uff0c\u5bf9\u811a\u4e0b\u7684\u94f6\u952d\uff0c\u8fde\u671b\u90fd\u4e0d\u671b\u4e00\u773c\uff0c\u4f3c\u4e4e\u5c06\u8fd9\u4e9b\u963f\u5835\u7269\uff0c\u770b\u5f97\u4e0d\u5c51\u4e00\u987e\uff0c\u98ce\u5ea6 \u6e05\u6807\uff0c\u5728\u8fd9\u4eba\u7fa4\u4e2d\uff0c\u5353\u7136\u800c\u7acb\uff0c\u5b9b\u5982\u9e21\u7fa4\u4e2d\u7684\u4ed9\u9e64\u3002 \u3000\u3000\u5df4\u5c71\u5251\u5ba2\u67f3\u590d\u660e\u5fc3\u91cc\u4e00\u52a8\uff0c\u52d2\u8f6c\u9a6c\u5934\uff0c\u8d70\u4e86\u8fc7\u53bb\uff0c\u671d\u90a3\u5e74\u9752\u6587\u58eb\u9053\uff1a\u201c\u9601\u4e0b\u5c82\u65e0\u610f\u4e8e\u8d22 \u5e1b\u4e4e\uff1f\u201d\u4ed6\u80f8\u4e2d\u79ef\u58a8\u751a\u591a\uff0c\u5bf9\u8fd9\u5c11\u5e74\u6587\u58eb\u8bf4\u8d77\u6d3b\u6765\uff0c\u4e5f\u4e0d\u81ea\u89c9\u5730\u6587\u7ec9\u7ec9\u7684\u3002 \u3000\u3000\u90a3\u5e74\u9752\u6587\u58eb\u4e00\u6115\uff0c\u968f\u5373\u6b63\u5bb9\u9053\uff1a\u201c\u4e34\u8d22\u6bcb\u82df\u5f97\uff0c\u5c0f\u5b50\u867d\u7136\u65e0\u624d\u65e0\u80fd\uff0c\u5bf9\u5723\u4eba\u7684\u9057\u8bad\uff0c\u5374 \u662f\u65f6\u523b\u4e0d\u6562\u5fd8\u6000\u7684\u3002\u201d \u3000\u3000\u5df4\u5c71\u5251\u5ba2\u67f3\u590d\u660e\u6697\u5730\u70b9\u5934\u79f0\u8d5e\uff0c\u60a6\u8272\u9053\uff1a\u201c\u9601\u4e0b\u5012\u7684\u786e\u662f\u96c5\u4eba\u3002\u201d\u4ed6\u671d\u90a3\u5c11\u5e74\u6587\u58eb\u8eab\u4e0a \u7834\u65e7\u7684\u8863\u670d\u770b\u4e86\u4e00\u773c\uff0c\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u8d2b\u9053\u6709\u53e5\u5931\u793c\u7684\u8bdd\u3002\u201d \u3000\u3000\u4ed6\u987f\u4e86\u987f\uff0c\u53c8\u9053\uff1a\u201c\u9601\u4e0b\u6e05\u6807\u4e30\u9038\uff0c\u7684\u786e\u662f\u4eba\u4e2d\u4e4b\u9f99\uff0c\u5982\u80fd\u5b66\u6b66\uff0c\u5b9a\u5fc5\u5927\u6210\uff0c\u9601\u4e0b\u5982\u679c \u6709\u610f\u7684\u8bdd\uff0c\u8d2b\u9053\u5012\u53ef\u4e3a\u9601\u4e0b\u89c5\u540d\u5e08\u3002\u597d\u7537\u513f\u7acb\u8eab\u5f53\u81ea\u5f3a\uff0c\u7ec8\u65e5\u57cb\u6ca1\u5728\u65e7\u4e66\u4e2d\uff0c\u5c82\u4e0d\u662f\u5927\u5927\u5730 \u53ef\u60dc\u4e86\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5e74\u6587\u58eb\u5fae\u4e00\u6c89\u541f\uff0c\u76ee\u5149\u5728\u5df4\u5c71\u5251\u5ba2\u8eab\u4e0a\u4e00\u779f\uff0c\u6717\u58f0\u9053\uff1a\u201c\u9053\u957f\u8a00\u4e4b\u6709\u7406\uff0c\u5c0f\u5b50\u672c\u5e94 \u4ece\u547d\uff0c\u4f46\u5c0f\u5b50\u5bb6\u6709\u9ad8\u5802\uff0c\u4eb2\u547d\u4e0d\u4ee4\u8fdc\u79bb\u3002\u201d \u3000\u3000\u4ed6\u53cc\u76ee\u4e00\u5f20\uff0c\u6b63\u6c14\u51db\u7136\uff0c\u63a5\u7740\u53c8\u8bf4\uff1a\u201c\u4f55\u51b5\u5b66\u4e66\u65e2\u6210\uff0c\u5b66\u5251\u4e5f\u8fd8\u4e0d\u665a\uff0c\u5728\u5c0f\u5b50\u8bfb\u4e66\u672a\u6210 \u7684\u65f6\u5019\uff0c\u522b\u7684\u4e8b\u8fd8\u8c08\u4e0d\u5230\u5462\u3002\u201d \u3000\u3000\u5df4\u5c71\u5251\u5ba2\u67f3\u590d\u660e\u4e0d\u4f4f\u70b9\u9996\uff0c\u4ed6\u5bf9\u8fd9\u6b63\u6c14\u51db\u7136\u7684\u5e74\u8f7b\u4eba\uff0c\u5fc3\u4e2d\u786e\u5b9e\u559c\u7231\u5df2\u6781\uff0c\u6709\u5fc3\u5c06\u4ed6\u6536 \u5f52\u81ea\u5df1\u95e8\u4e0b\uff0c\u4f46\u6b64\u523b\u542c\u4e86\u4eba\u5bb6\u7684\u8bdd\uff0c\u5fc3\u4e2d\u867d\u7136\u89c9\u5f97\u6709\u4e9b\u53ef\u60dc\uff0c\u4f46\u5374\u4e5f\u4e0d\u80fd\u52c9\u5f3a\u4eba\u5bb6\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u548c\u8a00\u60a6\u8272\u5730\u671d\u5c11\u5e74\u6587\u58eb\u7b11\u9053\uff1a\u201c\u4eba\u5404\u6709\u5fd7\uff0c\u8d2b\u9053\u4e5f\u4e0d\u80fd\u76f8\u5f3a\uff0c\u4ed6\u65e5\u6709\u7f18\uff0c\u8fd8\u5f53\u518d \u89c1\uff0c\u4eca\u65e5\u4e48\u2026\u2026\u201d \u3000\u3000\u8bdd\u672a\u8bf4\u5b8c\uff0c\u7075\u86c7\u6bdb\u81ec\u5ffd\u5730\u63a0\u6765\uff0c\u7b11\u9053\uff1a\u201c\u67f3\u9053\u957f\uff0c\u4eca\u65e5\u4e4b\u4e8b\uff0c\u4f60\u770b\u8fd8\u7b97\u75db\u5feb\u5427\uff01\u201d\u4e00\u773c \u770b\u5230\u90a3\u5c11\u5e74\u6587\u58eb\uff0c\u4e0d\u7981\u95ee\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u8c01\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5e74\u6587\u58eb\u538c\u6076\u5730\u671b\u4e86\u4ed6\u4e00\u773c\uff0c\u7709\u5fc3\u5fae\u76b1\uff0c\u4e24\u7709\u4e4b\u95f4\uff0c\u73b0\u51fa\u4e00\u9053\u5f88\u6df1\u7684\u76b1\u7eb9\uff0c\u671d\u5df4\u5c71\u5251 \u5ba2\u4e00\u62f1\u624b\uff0c\u8f6c\u8eab\u8d70\u4e86\u3002 \u3000\u3000\u5df4\u5c71\u5251\u5ba2\u5fae\u7b11\u4e00\u7b11\uff0c\u652f\u543e\u4ed6\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u4e2a\u6545\u4eba\u4e4b\u5b50\uff0c\u60f3\u4e0d\u5230\u73b0\u5728\u957f\u5f97\u8fd9\u4e48\u5927\u4e86\u3002\u201d \u3000\u3000\u7075\u86c7\u6bdb\u684c\u867d\u7136\u6709\u4e9b\u6000\u7591\uff0c\u4f46\u662f\u5374\u4e5f\u5e76\u672a\u5b8c\u5168\u653e\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u5174\u9ad8\u91c7\u70c8\u5730\u5938\u8000\u7740\u81ea\u5df1\u7684\u884c\u4e3a\u3002\u4ed6\u672c\u4e0d\u662f\u4e00\u4e2a\u559c\u6b22\u5938\u8000\u81ea\u5df1\u7684\u4eba\u7269\uff0c\u56e0\u4e3a\u4ed6\u662f \u9634\u6c89\u7684\u4eba\uff0c\u4f46\u6b64\u523b\u4ed6\u88ab\u65b9\u624d\u6240\u53d1\u751f\u7684\u4e8b\u6df1\u6df1\u5730\u5174\u594b\u7740\uff0c\u56e0\u6b64\u6001\u5ea6\u4e5f\u4e0d\u514d\u6709\u4e9b\u5931\u5e38\u4e86\u3002 \u3000\u3000\u8fd9\u6b63\u5982\u4e00\u4e2a\u7231\u9152\u7684\u4eba\uff0c\u5728\u559d\u4e86\u8db3\u91cf\u7684\u4f73\u917f\u4e4b\u540e\u7684\u5fc3\u60c5\u4e00\u6837\u3002 \u3000\u3000\u5df4\u5c71\u5251\u5ba2\u6de1\u6de1\u5730\u6577\u884d\u7740\uff0c\u770b\u5230\u8def\u4e0a\u6240\u5269\u4e0b\u7684\uff0c\u53ea\u6709\u5c0f\u4e27\u95e8\u8f6f\u762b\u5728\u5730\u4e0a\u7684\u8eab\u8eaf\u4e86\u3002 \u3000\u3000\u90a3\u5c31\u662f\u8bf4\u5730\u4e0a\u7684\u94f6\u5b50\uff0c\u5df2\u88ab\u4eba\u62ff\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u800c\u62ff\u4e86\u94f6\u5b50\u7684\u4eba\uff0c\u4e5f\u65e9\u5df2\u8d70\u5f97\u4e0d\u77e5\u53bb\u5411 \u4e86\u3002 \u3000\u3000\u5df4\u5c71\u5251\u5ba2\u4e0d\u7981\u611f\u6168\u5730\u5fae\u7b11\u7740\uff0c\u52d2\u8f6c\u9a6c\uff0c\u7b11\u9053\uff1a\u201c\u6211\u4eec\u8be5\u8d70\u4e86\u5427\u3002\u201d \u3000\u3000\u201c\u8fd9\u79cd\u662f\u975e\u4e4b\u5730\uff0c\u6211\u770b\u8fd8\u662f\u6108\u65e9\u79bb\u5f00\u6108\u597d\u3002\u201d\u4e00\u5b57\u5251\u7a0b\u67ab\u671b\u4e86\u5730\u4e0a\u6b8b\u7834\u7684\u94f6\u9798\u4e00\u773c\uff0c\u975e \u5e38\u4e16\u6545\u5730\u63a5\u4e0b\u6765\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u5728\u6c5f\u5357\u4eba\u5730\u751f\u758f\uff0c\u4e00\u4e9b\u4e0d\u5fc5\u8981\u7684\u9ebb\u70e6\uff0c\u80fd\u591f\u907f\u514d\u8fd8\u662f\u907f\u514d\u7684 \u597d\u3002\u201d \u3000\u3000\u9e33\u9e2f\u53cc\u5251\u4e45\u5c45\u9655\u7518\uff0c\u6c5f\u5357\u4e00\u5e26\uff0c\u5012\u7684\u786e\u6ca1\u6709\u6765\u8fc7\u4e24\u8d9f\u3002 \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u5fd7\u5f97\u610f\u6ee1\u4ed6\u8bf4\u9053\uff1a\u201c\u5bf9\uff0c\u5bf9\uff0c\u6211\u4eec\u4e5f\u8be5\u8d70\u4e86\u3002\u201d\u4ed6\u8d70\u8fc7\u53bb\uff0c\u671d\u4ecd\u5012\u5367\u5728\u5730\u4e0a\u7684 \u5c0f\u4e27\u95e8\u5218\u5b9a\u56fd\u8e22\u4e86\u4e24\u811a\u3002 \u3000\u3000\u5218\u5b9a\u56fd\u60a0\u60a0\u9192\u4e86\u8fc7\u6765\uff0c\u4ed6\u65b9\u624d\u7a74\u9053\u88ab\u95ed\uff0c\u6b64\u523b\u624d\u89e3\u4e86\u8fc7\u6765\uff0c\u91cd\u91cd\u547c\u5438\u4e86\u4e00\u53e3\uff0c\u5589\u5499\u95f4\u50cf \u662f\u585e\u6ee1\u4e86\u75f0\uff0c\u91cd\u91cd\u5730\u54b3\u55fd\u4e86\u4e00\u58f0\uff0c\u5410\u51fa\u4e00\u53e3\u6d53\u75f0\uff0c\u5f20\u773c\u4e00\u770b\uff0c\u5374\u89c1\u7075\u86c7\u6bdb\u81ec\u6b63\u5e26\u7740\u5947\u5f02\u7684\u7b11 \u5bb9\u671b\u7740\u4ed6\u3002 \u3000\u3000\u4ed6\u6323\u624e\u7740\u722c\u5382\u8d77\u6765\uff0c\u7565\u4e3a\u6d3b\u52a8\u4e86\u4e00\u4e0b\uff0c\u56db\u80a2\u65b9\u80fd\u8fd0\u8f6c\uff0c\u7075\u86c7\u6bdb\u81ec\u4e00\u957f\u8eab\uff0c\u5de6\u81c2\u5982\u5c01\u4f3c \u95ed\uff0c\u53f3\u638c\u7684\u8f6f\u97ad\u5708\u505a\u4e00\u8f6c\uff0c\u6a2a\u626b\u4ed6\u7684\u9762\u95e8\u3002 \u3000\u3000\u5c0f\u4e27\u95e8\u60ca\u5f13\u4e4b\u9e1f\uff0c\u521a\u521a\u5b9a\u4e86\u5b9a\u795e\uff0c\u6b64\u523b\u53c8\u88ab\u9a87\u51fa\u4e00\u8eab\u51b7\u6c57\u6765\uff0c\u7adf\u8fde\u6b66\u529f\uff0c\u90fd\u50cf\u662f\u5168\u5fd8\u8bb0 \u4e86\u3002 \u3000\u3000\u4ed6\u9519\u6b65\uff0c\u62d7\u8170\uff0c\u9f3b\u7aef\u5c16\u98ce\u65b9\u8fc7\uff0c\u811a\u4e0b\u4e00\u8f6f\uff0c\u53c8\u88ab\u7075\u86c7\u6bdb\u81ec\u7eca\u4e86\u4e00\u8de4\uff0c\u5c45\u7136\u8dcc\u5750\u5728\u5730\u4e0a\uff0c \u8fde\u722c\u90fd\u722c\u4e0d\u8d77\u6765\u4e86\u3002 \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u8138\u5b54\u4e00\u677f\uff0c\u9762\u4e0a\u7acb\u523b\u6362\u4e86\u4e00\u79cd\u795e\u8272\uff0c\u5389\u58f0\u9053\uff1a\u201c\u9752\u840d\u5251\u5b8b\u4ee4\u516c\u73b0\u5728\u8fd8\u5728\u4e0d\u5728\u5357 \u4eac\uff1f\u5feb\u8bf4\uff01\u201d \u3000\u3000\u5df4\u5c71\u5251\u5ba2\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u6697\u5fd6\uff1a\u201c\u6b64\u4eba\u771f\u7684\u5fc3\u72e0\u624b\u8fa3\uff0c\u5c45\u7136\u60f3\u8d76\u5c3d\u6740\u7edd\u4e86\u3002\u201d \u3000\u3000\u5c0f\u4e27\u95e8\u7565\u4e00\u8fdf\u7591\uff0c\u7075\u86c7\u6bdb\u81ec\u97ad\u68a2\u5ffd\u7136\u7535\u5c04\u800c\u51fa\uff0c\u6781\u5feb\u5730\u5728\u4ed6\u8138\u4e0a\u7559\u4e0b\u4e00\u9053\u8840\u69fd\uff0c\u4ed6\u5267\u75db \u96be\u5fcd\uff0c\u5802\u5802\u6602\u85cf\u4e03\u5c3a\u4e4b\u8eaf\uff0c\u7adf\u75db\u5f97\u6d41\u4e0b\u6cea\u6765\u3002 \u3000\u3000\u201c\u5feb\u8bf4\uff01\u201d\u7075\u86c7\u6bdb\u81ec\u50ac\u4fc3\u7740\uff0c\u773c\u4e2d\u7684\u51f6\u5149\uff0c\u8fde\u5df4\u5c71\u5251\u5ba2\u89c1\u4e86\uff0c\u90fd\u6709\u4e9b\u60ca\u6817\u7684\u611f\u89c9\u3002 \u5176\u5b9e\u5230\u76ee\u524d\u4e3a\u6b62\uff0c\u5c0f\u4e27\u95e8\u5218\u5b9a\u56fd\u8fd8\u4e0d\u77e5\u9053\u4ed6\u4eec\u7a76\u7adf\u4e3a\u4f1a\u4f55\u82e6\u82e6\u5bfb\u8bbf\u9752\u840d\u5251\uff0c\u5728\u8def\u4e0a\u516c\u7136\u62e6 \u622a\uff0c\u52ab\u8f66\u7684\u539f\u56e0\uff0c\u4ed6\u4e5f\u5e76\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u4ed6\u5e76\u6ca1\u6709\u5c06\u8fd9\u4e8b\u770b\u5f97\u5f88\u4e25\u91cd\uff0c\u7adf\u8bf4\u9053\uff1a\u201c\u5b8b\u8001\u524d\u8f88\u9690\u5c45\u591a\u5e74\uff0c\u4e0a\u6708\u51fa\u5c71\u4e00\u6b21\uff0c\u6b64\u523b\u60f3\u5fc5 \u4e5f\u56de\u6765\u4e86\uff0c\u4ed6\u8001\u4eba\u5bb6\u5e76\u4e0d\u65f6\u5e38\u51fa\u53bb\u7684\u3002\u201d \u3000\u3000\u4ed6\u518d\u4e5f\u6ca1\u6709\u60f3\u5230\uff0c\u7075\u86c7\u6bdb\u81ec\u8ffd\u5bfb\u9752\u840d\u5251\u7684\u7684\u4f01\u56fe\uff0c\u51e0\u4e4e\u662f\u60e8\u7edd\u4eba\u5bf0\u7684\u3002 \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u5f97\u5230\u4e86\u9752\u840d\u5251\u5b8b\u4ee4\u516c\u7684\u786e\u8baf\uff0c\u517c\u7a0b\u800c\u5954\uff0c\u9ec4\u660f\u8fc7\u540e\uff0c\u4ed6\u4eec\u4e00\u884c\u4e5d\u4eba\uff0c\u4fbf\u5df2\u5230\u4e86 \u6c5f\u5357\u9996\u5584\u4e4b\u533a\u7684\u79e3\u9675\u5e9c\u3002 \u3000\u3000\u5165\u6c34\u897f\u95e8\uff0c\u76f4\u5954\u79e6\u6dee\u6cb3\u7554\u7684\u592b\u5b50\u5e99\uff0c\u98ce\u5c18\u4ec6\u4ec6\uff0c\u9762\u5bd2\u5982\u6c34\u7684\u8fd9\u4e00\u884c\u4e5d\u4eba\uff0c\u4e0e\u8fd9\u91d1\u7c89\u7b19\u6b4c \u7684\u9500\u91d1\u4e4b\u7a9f\uff0c\u66f4\u662f\u663e\u5f97\u6781\u4e0d\u8c03\u548c\u3002 \u3000\u3000\u4ed6\u4eec\u770b\u8d77\u6765\uff0c\u4e5f\u662f\u5728\u6781\u529b\u6536\u655b\u81ea\u5df1\u7684\u884c\u85cf\uff0c\u4e5f\u4e0d\u613f\u663e\u5f97\u5927\u8fc7\u7279\u6b8a\uff0c\u8fd9\u5e76\u4e0d\u662f\u8bf4\u4ed6\u4eec\u5bf9\u4efb \u4f55\u4eba\u6709\u4ec0\u4e48\u60e7\u6015\uff0c\u800c\u4ec5\u4e0d\u8fc7\u662f\u4eba\u7c7b\u4e00\u79cd\u5f88\u81ea\u7136\u7684\u5fc3\u7406\u7f62\u4e86\u3002 \u3000\u3000\u592b\u5b50\u5e99\u4e00\u5e26\uff0c\u8336\u697c\u9152\u9986\u4e5f\u5f88\u591a\uff0c\u8fd9\u4e00\u884c\u4e5d\u4eba\u4e5f\u77e5\u9053\u81ea\u5bb6\u7684\u884c\u85cf\u592a\u8fc7\u624e\u76ee\uff0c\u51e0\u4eba\u4e00\u5546\u8bae\uff0c \u5206\u505a\u4e86\u4e09\u62e8\uff1a\u9e33\u9e2f\u53cc\u5251\uff0c\u5e26\u767e\u6b65\u98de\u82b1\u662f\u5230\u8857\u5c3d\u5934\u7684\u8001\u6b63\u5174\uff0c\u7075\u86c7\u6bdb\u81ec\uff0c\u4e03\u661f\u97ad\u675c\u4ef2\u5947\u4ee5\u53ca\u5b50 \u6bcd\u53cc\u98de\u5de6\u624b\u795e\u5251\u4e01\u8863\uff0c\u662f\u5230\u8857\u5357\u7aef\u7684\u9189\u6708\u697c\u3002 \u3000\u3000\u5df4\u5c71\u5251\u5ba2\u67f3\u590d\u660e\u5374\u548c\u53d7\u4e86\u4f24\uff0c\u4ecd\u672a\u75ca\u6108\u7684\u6c6a\u4e00\u9e4f\u4ee5\u53ca\u6c6a\u4e00\u9e23\u6606\u4ef2\u4e00\u9f50\u8dd1\u5230\u9999\u79ef\u53a8\u53bb\u5403\u7d20 \u83dc\u3002 \u3000\u3000\u51e0\u4eba\u8fd9\u4e48\u4e00\u5206\u6563\u5f00\uff0c\u76ee\u6807\u679c\u7136\u51cf\u5c11\u4e86\u8bb8\u591a\uff0c\u53cd\u6b63\u8fd9\u51e0\u5bb6\u9152\u697c\u5f7c\u6b64\u76f8\u9694\u5f88\u8fd1\uff0c\u82e5\u51fa\u4e86\u4e8b \u60c5\uff0c\u58f0\u606f\u4e5f\u4e0d\u96be\u76f8\u901a\uff0c\u4f55\u51b5\u4ed6\u4eec\u4e5f\u6839\u672c\u4e0d\u5728\u4e4e\u51fa\u4efb\u4f55\u4e8b\u5462\u3002 \u3000\u3000\u5df4\u5c71\u5251\u5ba2\u4e00\u9886\u9053\u888d\uff0c\u80cc\u540e\u5374\u659c\u80cc\u7740\u957f\u5251\uff0c\u6253\u626e\u5f97\u975e\u9053\u975e\u4fd7\uff0c\u6c6a\u4e00\u9e4f\u53d7\u4e86\u4f24\uff0c\u53f3\u81c2\u5939\u7740\u4e24 \u5757\u6728\u5757\uff0c\u540a\u5728\u8eab\u524d\uff0c\u8fde\u52a8\u90fd\u52a8\u4e0d\u4e86\u4e00\u4e0b\uff0c\u8fd9\u4e24\u4eba\u672c\u8be5\u662f\u8fd9\u7fa4\u4eba\u91cc\u6700\u62a2\u773c\u7684\u4eba\u7269\u4e86\u3002 \u3000\u3000\u54ea\u77e5\u592b\u5b50\u5e99\u4e00\u5e26\uff0c\u9f99\u86c7\u6df7\u6742\uff0c\u4e09\u6559\u4e5d\u6d41\u5343\u5947\u767e\u602a\uff0c\u4ec0\u4e48\u6837\u7684\u4eba\u90fd\u6709\uff0c\u6839\u672c\u6ca1\u6709\u5c06\u4ed6\u4eec\u5f53 \u505a\u4e00\u56de\u4e8b\u770b\uff0c\u5df4\u5c71\u5251\u5ba2\u6697\u81ea\u751f\u7b11\uff1a\u201c\u770b\u8d77\u6765\uff0c\u6211\u4eec\u5012\u591a\u8651\u4e86\u3002\u201d \u3000\u3000\u9999\u79ef\u53a8\u662f\u4e00\u5bb6\u5f88\u7cbe\u81f4\u7684\u7d20\u83dc\u9986\uff0c\u53ef\u662f\u91cc\u9762\u7684\u83dc\u636e\u8bf4\u5168\u662f\u7528\u9e21\u6c64\u706b\u817f\u716e\u6210\u7684\uff0c\u5927\u5bb6\u773c\u4e0d\u89c1 \u4e3a\u51c0\uff0c\u8c01\u4e5f\u6ca1\u6709\u53bb\u6df1\u7a76\u3002 \u3000\u3000\u7528\u9e21\u6c64\u706b\u817f\u716e\u7684\u7d20\u83dc\uff0c\u53e3\u5473\u81ea\u7136\u597d\uff0c\u56e0\u6b64\u9999\u79ef\u53a8\u7684\u751f\u610f\u4e5f\u4e0d\u9519\uff0c\u697c\u4e0a\u697c\u4e0b\u5012\u4e5f\u5750\u4e86\u4e0d\u5c11 \u4eba\uff0c\u9999\u79ef\u53a8\u6709\u4e00\u4e2a\u7279\u8272\uff0c\u5c31\u662f\u7279\u522b\u5e72\u51c0\uff0c\u67f3\u590d\u660e\u65c5\u9014\u52b3\u987f\uff0c\u9aa4\u7136\u5f97\u5230\u6041\u5730\u597d\u53bb\u5904\uff0c\u51c0\u4e86\u51c0\u9762 \u6f31\u4e86\u6f31\u53e3\uff0c\u5f80\u7cbe\u81f4\u5c0f\u5de7\u7684\u7d2b\u7af9\u6905\u4e0a\u4e00\u5750\uff0c\u7684\u786e\u8212\u670d\u5f97\u5f88\u3002 \u3000\u3000\u6c6a\u4e00\u9e23\u5750\u5728\u5df4\u5c71\u5251\u5ba2\u5bf9\u9762\uff0c\u4e3e\u8d77\u8336\u676f\u6765\uff0c\u6b63\u60f3\u559d\u4e0b\uff0c\u5ffd\u7136\u770b\u5230\u5df4\u5c71\u5251\u5ba2\u9762\u5bb9\u9aa4\u53d8\uff0c\u5fd9\u4e5f \u4e00\u56de\u5934\uff0c\u5374\u770b\u89c1\u6c5f\u5357\u5927\u4fa0\u9752\u840d\u5251\u5b8b\u4ee4\u516c\u6b63\u542b\u7740\u7b11\u5bb9\u671d\u91cc\u9762\u8d70\u8fc7\u6765\uff0c\u867d\u7136\u5728\u4ed6\u770b\u6765\uff0c\u90a3\u7b11\u5bb9\u662f \u6781\u4e3a\u52c9\u5f3a\u7684\u3002 \u3000\u3000\u4efb\u4f55\u4eba\u7684\u5fc3\u60c5\uff0c\u6050\u6015\u90fd\u4e0d\u4f1a\u6bd4\u5df4\u5c71\u5251\u5ba2\u6b64\u523b\u7684\u66f4\u590d\u6742\u4e86\uff0c\u4ed6\u548c\u9752\u840d\u5251\u5b8b\u4ee4\u516c\u672c\u662f\u81f3\u4ea4\uff0c \u4ed6\u4eec\u76f8\u4ea4\u4e86\u591a\u5e74\uff0c\u90fd\u662f\u4ee5\u9053\u4e49\u4e3a\u5148\uff0c\u6b64\u523b\u4ed6\u770b\u5230\u9752\u840d\u5251\u7626\u957f\u7684\u8eab\u6750\uff0c\u6e05\u704c\u7684\u9762\u5bb9\uff0c\u4ee5\u53ca\u4e24\u9b13 \u5fae\u5fae\u6591\u767d\u7684\u5934\u53d1\uff0c\u8111\u4e2d\u7075\u86c7\u6bdb\u81ec\u7684\u6bd2\u8fa3\u624b\u6bb5\uff0c\u53c8\u6cdb\u4e86\u8d77\u6765\uff0c\u4f7f\u8fd9\u4f4d\u7d20\u6027\u5e73\u548c\uff0c\u6700\u65e0\u4e3b\u89c1\u7684\u7384 \u95e8\u5251\u5ba2\uff0c\u4e00\u65f6\u7adf\u695e\u4f4f\u4e86\u3002 \u3000\u3000\u6b64\u523b\u4e5f\u4e0d\u8fc7\u662f\u620c\u65f6\u65b9\u8fc7\uff0c\u8ddd\u79bb\u7075\u86c7\u6bdb\u81ec\u6240\u8ba1\u5212\u7684\u5bf9\u9752\u840d\u5251\u706d\u7edd\u6ee1\u95e8\u7684\u65f6\u95f4\uff0c\u8fd8\u5dee\u7740\u597d\u51e0 \u4e2a\u65f6\u8fb0\uff0c\u5df4\u5c71\u5251\u5ba2\u4e00\u77ac\u76ee\uff0c\u770b\u5230\u6c5f\u6c0f\u6606\u4ef2\u9762\u4e0a\u7684\u795e\u8272\uff0c\u4e5f\u662f\u9634\u6697\u4e0d\u5b9a\u7684\uff0c\u5fc3\u91cc\u5ffd\u7136\u52a8\u4e86\u4e00 \u52a8\u3002 \u3000\u3000\u9752\u840d\u5251\u5b8b\u4ee4\u516c\u5df2\u542b\u7b11\u8d70\u4e86\u8fc7\u6765\uff0c\u4ed6\u4eff\u4f5b\u4ec0\u4e48\u4e5f\u4e0d\u77e5\u9053\uff0c\u7b14\u76f4\u5730\u8d70\u5230\u5df4\u5c71\u5251\u5ba2\u7684\u5ea7\u4f4d\u65c1\uff0c \u6717\u58f0\u7b11\u9053\uff1a\u201c\u771f\u662f\u5de7\u9047\uff0c\u771f\u662f\u5de7\u9047\uff0c\u5c0f\u5f1f\u8db3\u4e0d\u51fa\u6237\u5df2\u6709\u591a\u65e5\uff0c\u60f3\u4e0d\u5230\u4e00\u51fa\u6765\u5c31\u9047\u4e0a\u4e86\u9601\u4e0b\u51e0 \u4f4d\u3002\u201d \u3000\u3000\u8fd9\u58f0\u97f3\uff0c\u8fd9\u7b11\u8c8c\uff0c\u90fd\u662f\u5df4\u5c71\u5251\u5ba2\u6240\u719f\u6089\u7684\uff0c\u4ed6\u5fc3\u91cc\u4e00\u9635\u9eef\u7136\uff0c\u5bf9\u81ea\u5df1\u6240\u4f5c\u6240\u4e3a\uff0c\u7a81\u7136\u6709 \u4e86\u4e00\u79cd\u81ea\u8d23\u548c\u4e0d\u5b89\u7684\u611f\u89c9\u3002 \u3000\u3000\u8fd9\u79cd\u611f\u89c9\uff0c\u4e5f\u4e0d\u662f\u9752\u840d\u5251\u5b8b\u4ee4\u516c\u6240\u80fd\u6ce8\u610f\u5f97\u5230\u7684\uff0c\u4ed6\u6beb\u65e0\u62d8\u675f\u5730\u5750\u4e86\u4e0b\u6765\uff0c\u548c\u6cb3\u6714\u53cc\u5251 \u4ee5\u53ca\u5df4\u5c71\u5251\u5ba2\u968f\u610f\u7b11\u8c08\u7740\uff0c\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\u8fd9\u9762\u524d\u7684\u4e09\u4e2a\u4eba\u7adf\u662f\u4e13\u7a0b\u5230\u8fd9\u6765\u53d6\u4ed6\u6027\u547d\u7684\u3002 \u3000\u3000\u5343\u4e07\u79cd\u611f\u6168\uff0c\u5728\u5df4\u5c71\u5251\u5ba2\u8111\u6d77\u91cc\u95ea\u8fc7\uff0c\u6700\u540e\u53ea\u5269\u4e0b\u4e00\u79cd\uff0c\u5728\u4ed6\u8111\u6d77\u91cc\u53cd\u8986\u4e0d\u53bb\u3002 \u3000\u3000\u201c\u544a\u8bc9\u4ed6\uff0c\u8ba9\u4ed6\u5728\u8fd9\u51e0\u4e2a\u65f6\u8fb0\u91cc\u4e58\u9699\u9003\u8d70\u3002\u201d\u4ed6\u671b\u4e86\u671b\u6cb3\u6714\u53cc\u5251\uff0c\u770b\u5230\u4ed6\u4eec\u8138\u4e0a\uff0c\u4e5f\u6709 \u7740\u60ed\u6127\u7684\u795e\u8272\uff0c\u8fde\u8bf4\u8bdd\u65f6\u7684\u6001\u5ea6\u90fd\u663e\u5f97\u90a3\u4e48\u4e0d\u81ea\u7136\u4e86\u3002 \u3000\u3000\u201c\u4f46\u662f\uff0c\u6211\u8be5\u600e\u4e48\u8bf4\u5462\uff1f\u201d\u5df4\u5c71\u5251\u5ba2\u5fc3\u4e2d\uff0c\u4ecd\u7136\u662f\u4e3e\u68cb\u4e0d\u5b9a\u7684\u3002 \u3000\u3000\u4ed6\u4eec\u56db\u4e2a\u4eba\u8868\u9762\u867d\u662f\u5728\u8c08\u7b11\u7740\uff0c\u4e00\u4e1d\u4e5f\u770b\u4e0d\u51fa\u4e0d\u5bf9\u7684\u795e\u8272\u6765\uff0c\u53ef\u662f\u82e5\u6709\u4eba\u77e5\u9053\u4ed6\u4eec\u4e4b\u95f4 \u7684\u5173\u7cfb\u7adf\u590d\u6742\u81f3\u65af\uff0c\u4e5f\u4f1a\u611f\u89c9\u5230\u8fd9\u79cd\u573a\u9762\u7684\u5c34\u5c2c\uff0c\u51e0\u4e4e\u662f\u4ee4\u4eba\u96be\u4ee5\u5fcd\u53d7\u7684\u3002 \u3000\u3000\u5c24\u5176\u662f\u5df4\u5c71\u5251\u5ba2\u67f3\u590d\u660e\uff0c\u4ed6\u4e13\u7a0b\u800c\u6765\u6c5f\u5357\uff0c\u5c31\u662f\u4e3a\u4e86\u9664\u53bb\u6b64\u4eba\uff0c\u53ef\u662f\u89c1\u4e86\u9752\u840d\u5251\u7684\u9762\uff0c \u4ed6\u5374\u4e0d\u5f97\u4e0d\u53d9\u65e7\uff0c\u8c08\u5929\uff0c\u8fd9\u5e76\u4e0d\u662f\u6577\u884d\uff0c\u800c\u662f\u4e00\u79cd\u51fa\u4e4e\u672c\u6027\u7684\u60c5\u611f\u7684\u6d41\u9732\uff0c\u4f46\u8fd9\u60c5\u51b5\u5c82\u4e0d\u662f \u592a\u5947\u5f02\u4e86\u5417\uff1f \u3000\u3000\u7ec8\u4e8e\uff0c\u5df2\u5c71\u5251\u5ba2\u7acb\u4e0b\u4e86\u51b3\u5b9a\u7684\u610f\u5ff5\uff0c\u4e3a\u7740\u53cb\u60c5\uff0c\u6709\u751f\u4ee5\u6765\uff0c\u4ed6\u7b2c\u4e00\u6b21\u7acb\u4e0b\u5982\u6b64\u8270\u5de8\u7684\u51b3 \u5fc3\uff0c\u4e5f\u662f\u7b2c\u4e00\u6b21\u6709\u4e86\u4e2a\u5978\u8be1\u7684\u8ba1\u5212\u3002 \u3000\u3000\u4ed6\u518d\u671b\u4e86\u6cb3\u6714\u53cc\u5251\u4e00\u773c\uff0c\u770b\u5230\u4e86\u6c6a\u4e00\u9e23\u7684\u624b\uff0c\u6b63\u4e0d\u5b89\u5730\u5728\u81ea\u5df1\u4e0b\u9894\u4e0a\u79fb\u52a8\u7740\uff0c\u6c6a\u4e00\u9e4f\u5219 \u7528\u5de6\u624b\u62ff\u7740\u7b77\u5b50\uff0c\u8f7b\u8f7b\u5730\u6572\u7740\u9171\u6cb9\u789f\u5b50\u7684\u8fb9\u6cbf\uff0c\u4f46\u662f\u6709\u4e00\u4e2a\u4e8b\u662f\u53ef\u4ee5\u786e\u4fe1\u7684\uff0c\u90a3\u5c31\u662f\u4ed6\u4eec\u9762 \u4e0a\u7684\u7f9e\u6127\u4e4b\u8272\uff0c\u5df2\u8fdc\u4e0d\u53ca\u65b9\u624d\u9752\u840d\u5251\u8d70\u5165\u65f6\u7684\u6d53\u539a\u4e86\u3002 \u3000\u3000\u6c6a\u4e00\u9e23\u5728\u684c\u5b50\u4e0b\u9762\u62ac\u811a\uff0c\u6084\u6084\u8e22\u4e86\u5df4\u5c71\u5251\u5ba2\u4e00\u4e0b\uff0c\u5634\u91cc\u5374\u5728\u548c\u9752\u840d\u5251\u5b8b\u4ee4\u516c\u626f\u4e0d\u7740\u8fb9\u9645 \u7684\u8bdd\uff0c\u4f46\u5df2\u53ef\u542c\u51fa\u90a3\u662f\u5728\u6577\u884d\u7740\u7684\u4e86\u3002 \u3000\u3000\u5df4\u5c71\u5251\u5ba2\u518d\u4e00\u6b21\u4e0b\u4e86\u51b3\u5fc3\uff0c\u4e0d\u7ecf\u610f\u5730\u7ad9\u4e86\u8d77\u6765\uff0c\u7f13\u7f13\u7ed5\u5230\u6cb3\u6714\u53cc\u5251\u7684\u8eab\u540e\uff0c\u4e24\u53ea\u624b\u7f29\u5728 \u5bbd\u5927\u7684\u9053\u888d\u8896\u91cc\uff0c\u5374\u5df2\u529b\u8d2f\u6307\u5c16\u4e86\u3002 \u3000\u3000\u6cb3\u6714\u53cc\u5251\u4e0d\u7591\u6709\u4ed6\uff0c\u751a\u81f3\u8fde\u5934\u90fd\u6ca1\u6709\u56de\u4e00\u4e0b\uff0c\u5df4\u5c71\u5251\u5ba2\u73af\u987e\u56db\u9762\u7684\u9152\u5ba2\uff0c\u7136\u540e\u8d70\u8fd1\u4e00\u65e0 \u6240\u89c9\u7684\u6c6a\u6c0f\u6606\u4ef2\uff0c\u4e24\u53ea\u7f29\u5728\u9053\u888d\u91cc\u7684\u624b\uff0c\u7f13\u7f13\u62cd\u5411\u6c6a\u6c0f\u6606\u4ef2\u4e24\u4eba\u6beb\u672a\u8bbe\u9632\u7684\u80cc\u4e0a\u3002 \u3000\u3000\u8fd9\u65f6\u82e5\u662f\u6c6a\u6c0f\u6606\u4ef2\u4e2d\u6709\u4e00\u4e2a\u5076\u4e00\u56de\u8eab\uff0c\u90a3\u4e48\u60c5\u51b5\u4e5f\u8bb8\u5c31\u4f1a\u5b8c\u5168\u6539\u53d8\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u5df4\u5c71\u5251\u5ba2\u6240\u7acb\u4e0b\u7684\u51b3\u5fc3\uff0c\u5e76\u975e\u662f\u5b8c\u5168\u4e0d\u53ef\u52a8\u6447\u7684\u3002 \u3000\u3000\u9752\u840d\u5251\u5b8b\u4ee4\u516c\u5750\u5728\u6c6a\u4e00\u9e4f\u7684\u5bf9\u9762\uff0c\u8fd9\u662f\u4e00\u5f20\u5e76\u4e0d\u592a\u5927\u7684\u5c0f\u5706\u684c\u5b50\uff0c\u4e24\u4eba\u5750\u5728\u4e00\u8d77\uff0c\u90a3\u79cd \u89d2\u5ea6\u8fdc\u4e0d\u5982\u5750\u516b\u4ed9\u684c\u5b50\u5927\u3002 \u3000\u3000\u662f\u4ee5\u5df4\u5c71\u5251\u5ba2\u6b64\u523b\u6240\u7ad9\u7684\u5730\u52bf\uff0c\u662f\u6c6a\u6c0f\u6606\u4ef2\u4e0d\u56de\u8eab\u7edd\u96be\u770b\u5230\u7684\uff0c\u800c\u9752\u840d\u5251\u4e00\u62ac\u5934\uff0c\u5374\u6b63 \u597d\u770b\u4ed6\u5e26\u7740\u4e00\u8138\u5947\u602a\u7684\u8868\u60c5\uff0c\u7ad9\u5728\u6cb3\u6714\u53cc\u5251\u7684\u8eab\u540e\uff0c\u4ed6\u65b9\u81ea\u89c9\u5f97\u6709\u4e9b\u5947\u602a\u3002 \u3000\u3000\u5728\u624b\u6307\u5c06\u8981\u89e6\u53ca\u6c6a\u6c0f\u6606\u4ef2\u8eab\u4f53\u7684\u90a3\u4e00\u523b\uff0c\u5df4\u5c71\u5251\u5ba2\u7a81\u7136\u52a0\u5feb\u4e86\u901f\u5ea6\uff0c\u9a88\u6307\u5982\u98ce\uff0c\u5de6\u6307\u70b9 \u5728\u6c6a\u4e00\u545c\u7684\u53f3\u80a9\u4e95\u7a74\u4e0a\uff0c\u53f3\u6307\u70b9\u5411\u6c6a\u4e00\u9e4f\u5de6\u80a9\u771f\u7a74\u4e0a\uff0c\u5728\u4ed6\u4e24\u4eba\u7a74\u9053\u88ab\u95ed\uff0c\u5c06\u5012\u672a\u5012\u7684\u8fd9\u4e00 \u5239\u90a3\uff0c\u5df4\u5c71\u5251\u5ba2\u500f\u5730\u4e24\u8098\u4e0b\u6c89\uff0c\u4ee5\u7cbe\u5999\u7684\u5185\u5bb6\u771f\u529b\uff0c\u7a33\u4f4f\u6c6a\u6c0f\u6606\u4ef2\u5c06\u8981\u5012\u4e0b\u7684\u8eab\u8eaf\uff0c\u201c\u7830\u201d \u5730\u4e00\u58f0\uff0c\u6c6a\u4e00\u9e4f\u5de6\u624b\u7684\u7af9\u7b77\uff0c\u843d\u5728\u684c\u4e0a\uff0c\u4ed6\u4e24\u4eba\u7684\u5934\uff0c\u4e5f\u5411\u524d\u865a\u8f6f\u5730\u642d\u4e0b\u3002 \u3000\u3000\u82e5\u975e\u7559\u610f\u7684\u4eba\uff0c\u662f\u7edd\u96be\u53d1\u73b0\u8fd9\u4e00\u62db\uff0c\u9752\u840d\u5251\u4e5f\u662f\u51fa\u4e4e\u610f\u5916\uff0c\u201c\u5662\u201d\u4e86\u4e00\u58f0\uff0c\u60ca\u5f02\u5730\u7ad9\u4e86 \u8d77\u6765\uff0c\u5df4\u5c71\u5251\u5ba2\u8d76\u7d27\u4ee5\u76ee\u793a\u610f\uff0c\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u4ee4\u516c\u5144\uff0c\u6c6a\u6c0f\u6606\u4ef2\u5927\u7ea6\u662f\u75c5\u4e86\u3002\u201d\u4ed6\u53c8\u4ee5\u773c\u8272 \u963b\u4f4f\u9752\u840d\u5251\u7684\u53d1\u95ee\uff0c\u8d76\u7d27\u4ee5\u76ee\u793a\u610f\uff0c\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u5148\u6276\u4ed6\u4e24\u5144\u5f1f\u56de\u53bb\u627e\u4e2a\u5927\u592b\u518d\u8bf4\u3002\u201d \u3000\u3000\u9752\u840d\u5251\u4e0d\u7981\u66f4\u4e3a\u6000\u7591\uff0c\u4f46\u4ed6\u77e5\u9053\u5df4\u5c71\u5251\u5ba2\u7684\u8fd9\u4e00\u4e2a\u4e3e\u52a8\uff0c\u7edd\u4e0d\u4f1a\u65e0\u7531\u800c\u53d1\u7684\uff0c\u52c9\u5f3a\u5fcd\u4f4f \u5fc3\u91cc\u7684\u7591\u7aa6\uff0c\u968f\u624b\u638f\u51fa\u4e00\u952d\u94f6\u5b50\uff0c\u629b\u5728\u684c\u4e0a\uff0c\u548c\u5df4\u5c71\u5251\u5ba2\u6276\u7740\u6c6a\u6c0f\u6606\u4ef2\uff0c\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5176\u4f59\u7684\u5403\u5ba2\uff0c\u5f53\u7136\u90fd\u4ee5\u8be7\u5f02\u7684\u773c\u5149\u671b\u7740\u4ed6\u4eec\uff0c\u4f46\u9752\u840d\u5251\u5b8b\u4ee4\u516c\u5728\u6c5f\u9675\u5e9c\u53ef\u79f0\u662f\u5987\u5b7a\u7686\u77e5 \u7684\u4eba\u7269\uff0c\u662f\u4ee5\u4e5f\u6ca1\u6709\u4eba\u6000\u7591\u5230\u5176\u4ed6\u7684\u4e8b\u4e0a\u9762\u53bb\u3002 \u3000\u3000\u8d70\u51fa\u9999\u79ef\u53a8\uff0c\u662f\u4e00\u6761\u975e\u5e38\u70ed\u95f9\u7684\u8857\u9053\uff0c\u5df4\u5c71\u5251\u5ba2\u6276\u7740\u6c6a\u4e00\u9e4f\uff0c\u614c\u5f20\u5730\u5de6\u53f3\u56de\u987e\uff0c\u5728\u4eba\u4ece \u4e2d\u6025\u901f\u5730\u671d\u51fa\u57ce\u7684\u65b9\u5411\u800c\u53bb\u3002 \u3000\u3000\u9752\u840d\u5251\u518d\u5fcd\u4e0d\u4f4f\u5fc3\u4e2d\u7684\u5c42\u5c42\u7591\u4e91\uff0c\u8131\u53e3\u95ee\u9053\uff1a\u201c\u67f3\u5144\uff0c\u8fd9\u5230\u5e95\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u5df4\u5c71\u5251\u5ba2\u4e00\u6446\u624b\uff0c\u9053\uff1a\u201c\u6162\u6162\u518d\u8bf4\uff0c\u5148\u51fa\u57ce\u8981\u7d27\u3002\u201d\u9752\u840d\u5251\u7591\u4e91\u66f4\u751a\uff0c\u5f80\u524d\u53c8\u8d70\u4e86\u4e24 \u6b65\uff0c\u62db\u624b\u5524\u4e86\u4e00\u8f86\u505c\u7559\u5728\u9152\u697c\u95e8\u53e3\u7684\u9a6c\u8f66\uff0c\u5c06\u6c6a\u6c0f\u6606\u4ef2\u6276\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u90a3\u8f66\u592b\u672c\u4e5f\u8ba4\u5f97\u8fd9\u4f4d\u6c5f\u5357\u5927\u4fa0\uff0c\u5df4\u7ed3\u5730\u95ee\u9053\uff1a\u201c\u4f60\u5bb6\u8981\u5230\u54ea\u5757\u53bb\uff1f\u201d\u5b8b\u4ee4\u516c\u9053\uff1a\u201c\u6c34\u897f \u95e8\u5916\u3002\u201d \u3000\u3000\u8f66\u592b\u6ee1\u8138\u5806\u6b22\uff0c\u4e00\u9762\u56de\u8eab\u5173\u597d\u8f66\u95e8\uff0c\u4e00\u9762\u6325\u52a8\u7740\u9a6c\u97ad\uff0c\u9053\uff1a\u201c\u4f60\u5bb6\u5174\u8da3\u771f\u597d\u3002\u201d\u53e3\u4e2d\u547c \u54e8\u4e00\u58f0\uff0c\u76ae\u5236\u7684\u9a6c\u97ad\u201c\u5427\u54d2\u201d\u4e00\u54cd\uff0c\u9a6c\u8f66\u7f13\u7f13\u51fa\u57ce\u800c\u53bb\u3002 \u3000\u3000\u5230\u4e86\u8f66\u53a2\u91cc\uff0c\u5df4\u5c71\u5251\u5ba2\u9762\u4e0a\u7684\u795e\u8272\uff0c\u624d\u7565\u4e3a\u677e\u9a70\u4e00\u4e9b\uff0c\u624d\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u6084\u58f0\u5411\u9752\u840d\u5251 \u9053\uff1a\u201c\u6211\u8bf4\u5b8b\u5144\uff0c\u4f60\u4e5f\u672a\u514d\u592a\u5927\u610f\u4e86\u3002\u201d\u4ed6\u7f13\u4e86\u53e3\u6c14\uff0c\u53c8\u9053\uff1a\u201c\u4ece\u6b64\u5904\u51fa\u57ce\u8981\u591a\u5c11\u65f6\u95f4\uff1f\u201d \u3000\u3000\u9752\u840d\u5251\u9053\uff1a\u201c\u5f88\u5feb\uff0c\u67f3\u5144\uff0c\u8fd9\u5230\u5e95\u2014\u2014\u201d\u4ed6\u65b9\u81ea\u8981\u95ee\u53ca\u5fc3\u4e2d\u6240\u7591\u4e4b\u4e8b\uff0c\u5374\u53c8\u88ab\u5df4\u5c71\u5251\u5ba2 \u53e6\u4e00\u4e00\u53e5\u7a81\u5140\u7684\u8bdd\u6253\u65ad\u4e86\u8bdd\u5934\u3002 \u3000\u3000\u201c\u5b8b\u5144\u5bb6\u91cc\u53ef\u8fd8\u6709\u4ec0\u4e48\u653e\u4e0d\u4e0b\u7684\u4e8b\u6ca1\u6709\uff1f\u201d\u5df4\u5c71\u5251\u5ba2\u7a81\u7136\u95ee\u9053\u3002 \u3000\u3000\u9752\u840d\u5251\u53c8\u4e00\u695e\uff0c\u6697\u5fd6\uff1a\u201c\u600e\u5730\u4ed6\u4eca\u65e5\u5c3d\u505a\u4e9b\u65e0\u5934\u65e0\u5c3e\u7684\u4e8b\uff0c\u8bf4\u4e9b\u65e0\u5934\u65e0\u5c3e\u7684\u8bdd\uff1f\u201d\u8f6c\u8138 \u4e00\u770b\uff0c\u5374\u89c1\u5df4\u5c71\u5251\u5ba2\u8138\u4e0a\u7684\u795e\u8272\u751a\u662f\u614e\u91cd\uff0c\u9042\u9053\uff1a\u201c\u5c0f\u5f1f\u5bb6\u91cc\u5927\u534a\u662f\u4e9b\u8fd1\u4eb2\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u653e \u4e0d\u4e0b\u7684\u4e8b\u3002\u201d \u3000\u3000\u5df4\u5c71\u5251\u5ba2\u67f3\u590d\u660e\u4e00\u677e\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u6837\u8fd8\u597d\u2014\u2014\u201d\u9752\u840d\u5251\u5fcd\u4e0d\u4f4f\u5fc3\u91cc\u7684\u7591\u56e2\uff0c\u518d\u6b21\u626d\u8f6c\u8bdd \u9898\uff0c\u95ee\u9053\uff1a\u201c\u67f3\u5144\uff0c\u4eca\u65e5\u5230\u5e95\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u5df4\u5c71\u5251\u5ba2\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9042\u5c06\u4e8b\u60c5\u7684\u59cb\u672a\uff0c\u6e90\u6e90\u672c\u672c\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8f66\u53a2\u91cc\u6c89\u9ed8\u4e86\u8bb8\u4e45\uff0c\u9664\u4e86\u8f9a\u8f9a\u7684\u8f66\u58f0\u4e4b\u5916\uff0c\u5df4\u5c71\u5251\u5ba2\u548c\u9752\u840d\u5251\u5b8b\u4ee4\u516c\u6ca1\u6709\u8bf4\u8bdd\uff0c\u6cb3\u7554\u4e1d \u7af9\u4e4b\u58f0\u76c8\u8033\uff0c\u9752\u840d\u5251\u63a2\u9996\u5916\u671b\uff0c\u79e6\u6dee\u6cb3\u7554\uff0c\u6708\u8272\u751a\u7f8e\uff0c\u5c06\u79e6\u6dee\u70df\u6c34\u5012\u6620\u5f97\u76f4\u5982\u4ed9\u5883\u3002 \u3000\u3000\u201c\u4e8b\u5df2\u81f3\u6b64\u4e00\u4e00\u201d\u9752\u840d\u5251\u5e7d\u7136\u53f9\u9053\uff0c\u5fc3\u4e2d\u771f\u662f\u611f\u6168\u4e07\u5343\u3002 \u3000\u3000\u5df4\u5c71\u5251\u5ba2\u63a5\u53e3\u9053\uff1a\u201c\u4e8b\u5df2\u81f3\u6b64\uff0c\u6211\u770b\u522b\u65e0\u4ed6\u6cd5\u4e86\uff0c\u5b8b\u5144\u4f60\u6211\u90fd\u5df2\u5c4a\u82b1\u7532\u4e4b\u9f84\uff0c\u5c11\u5e74\u65f6\u7684 \u610f\u6c14\uff0c\u6211\u770b\u4e5f\u8be5\u6d88\u78e8\u6b86\u5c3d\u4e86\uff0c\u53c8\u4f55\u82e6\u518d\u548c\u4ed6\u4eec\u53bb\u4e89\u4e00\u65e5\u4e4b\u77ed\u957f\uff01\u201d\u550f\u5618\u611f\u53f9\uff0c\u82f1\u96c4\u5782\u66ae\u4e4b \u60c5\uff0c\u6cb9\u7136\u73b0\u4e8e\u8a00\u8868\u3002 \u3000\u3000\u9752\u840d\u5251\u53cc\u638c\u731b\u4e00\u51fb\u819d\uff0c\u6012\u9053\uff1a\u201c\u6211\u5c31\u504f\u4e0d\u670d\u8001\uff0c\u6211\u5012\u8981\u770b\u770b\uff0c\u7075\u86c7\u6bdb\u81ec\u90a3\u73ed\u4eba\u6709\u591a\u5927\u9053 \u884c\uff1f\u201d\u4ed6\u54fc\u4e86\u4e00\u58f0\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u4f55\u51b5\u662f\u5728\u79e3\u9675\uff0c\u67f3\u5144\uff0c\u4f60\u4e14\u7f6e\u8eab\u4e8b\u5916\uff0c\u5c0f\u5f1f\u5012\u8981\u548c\u4ed6\u5468\u65cb\u5468 \u65cb\u3002\u201d \u3000\u3000\u5df4\u5c71\u5251\u5ba2\u8f7b\u8f7b\u62cd\u4e86\u62cd\u4ed6\u7684\u80a9\u5934\uff0c\u9053\uff1a\u201c\u5b8b\u5144\u8fd9\u53c8\u4f55\u82e6\uff0c\u5982\u6b64\u4e00\u6765\uff0c\u6b66\u6797\u4e2d\u4e0d\u514d\u53c8\u8981\u751f\u51fa \u591a\u5c11\u4e8b\u7aef\u4e86\u3002\u201d\u4ed6\u63a8\u5f00\u8f66\u7a97\uff0c\u6708\u8272\u4ece\u7a97\u53e3\u7167\u4e86\u8fdb\u6765\uff0c\u7e41\u661f\u6ee1\u5929\uff0c\u56db\u91ce\u5bc2\u7136\uff0c\u9a6c\u8f66\u65e9\u5df2\u51fa\u4e86\u57ce \u5916\u4e86\u3002 \u3000\u3000\u4e24\u4eba\u5fc3\u4e8b\u91cd\u91cd\uff0c\u53c8\u6c89\u9ed8\u4e86\u8bb8\u4e45\uff0c\u5df4\u5c71\u5251\u5ba2\u9053\uff1a\u201c\u6211\u4fe9\u8db3\u8ff9\u867d\u5df2\u53ef\u8bf4\u904d\u53ca\u6d77\u5185\u4e86\uff0c\u53ea\u662f\u585e \u5916\u5374\u59cb\u7ec8\u672a\u66fe\u53bb\u8fc7\uff0c\u5c0f\u5f1f\u65e9\u5c31\u6709\u610f\u53bb\u9886\u7565\u9886\u7565\u90a3\u5927\u6f20\u98ce\u5149\uff0c\u5b8b\u5144\uff0c\u4f60\u662f\u5426\u6709\u5174\u966a\u5c0f\u5f1f\u4e00\u884c \u5462\uff1f\u201d \u3000\u3000\u9752\u840d\u5251\u611f\u6fc0\u5730\u62cd\u4e86\u62cd\u4ed6\u7684\u624b\u80cc\uff0c\u8fdc\u8fdc\u7a81\u7136\u4f20\u6765\u4e00\u58f0\u591c\u9e1f\u7684\u54c0\u9e23\uff0c\u6709\u98ce\u5439\u8fc7\uff0c\u5439\u5f97\u5df4\u5c71\u5251 \u5ba2\u988a\u4e0b\u7684\u987b\u9aef\uff0c\u5fae\u5fae\u98d8\u52a8\u3002 \u3000\u3000\u5c31\u7740\u6708\u8272\u4e00\u770b\uff0c\u5df4\u5c71\u5251\u5ba2\u8138\u4e0a\u7684\u76b1\u7eb9\uff0c\u6e05\u6670\u53ef\u89c1\u3002 \u3000\u3000\u201c\u6211\u4eec\u5168\u8001\u4e86\uff01\u201d\u9752\u840d\u5251\u6697\u53f9\u7740\uff0c\u4e00\u8154\u96c4\u5fc3\u58ee\u5fd7\uff0c\u987f\u65f6\u6d88\u5931\u5f97\u65e0\u5f71\u65e0\u8e2a\u3002 \u3000\u3000\u4ed6\u5f00\u59cb\u6709\u4e9b\u540e\u6094\uff0c\u540e\u6094\u4ed6\u4e0d\u8be5\u53c2\u4e0e\u718a\u8033\u5c71\u90a3\u4e00\u6b21\u4e8b\u3002 \u3000\u3000\u201c\u5509\uff01\u4e8b\u8fc7\u5883\u8fc1\uff0c\u8fd8\u60f3\u5b83\u4f5c\u4ec0\u4e48\uff1f\u201d\u4ed6\u9eef\u7136\u81ea\u8bed\u9053\u3002 \u3000\u3000\u5df4\u5c71\u5251\u5ba2\u4ea6\u5728\u6c89\u601d\uff0c\u95fb\u8a00\u62ac\u5934\u95f4\u9053\uff1a\u201c\u5b8b\u5144\u5728\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9752\u840d\u5251\u4e00\u7b11\uff0c\u5c55\u989c\u9053\uff1a\u201c\u6211\u5728\u8bf4\u65e5\u540e\u4f60\u6211\u8001\u5144\u5f1f\u7545\u6e38\u5927\u6f20\u98ce\u5149\uff0c\u8be5\u662f\u4f55\u7b49\u6709\u8da3\u3002\u201d \u3000\u3000\u5df4\u5c71\u5251\u5ba2\u4e86\u89e3\u5730\u4e00\u7b11\uff0c\u7a81\u7136\u9053\uff1a\u201c\u8fd9\u59d3\u6c6a\u7684\u4e24\u4e2a\u5c0f\u5b50\u600e\u4e48\u529e\uff1f\uff0c\uff0c\u9752\u840d\u5251\u4e00\u76b1\u7709\uff0c\u9053\uff1a \u201c\u63a8\u4ed6\u4e0b\u8f66\u5c31\u5b8c\u4e86\uff0c\u53cd\u6b63\u518d\u8fc7\u51e0\u4e2a\u65f6\u8fb0\uff0c\u4ed6\u4eec\u7a74\u9053\u4e00\u89e3\uff0c\u96be\u9053\u81ea\u5df1\u8fd8\u8d70\u4e0d\u56de\u53bb\u5417\uff1f\u201d \u3000\u3000\u67f3\u590d\u660e\u7b11\u9053\uff1a\u201c\u5bf9\uff01\u201d\u968f\u624b\u5c31\u63a8\u5f00\u8f66\u95e8\uff0c\u8f7b\u8f7b\u4e00\u63a8\uff0c\u201c\u5657\uff0c\u5657\uff0c\u201d\u4e24\u58f0\uff0c\u6cb3\u6714\u53cc\u5251\u7adf\u771f \u5730\u88ab\u63a8\u5728\u8f66\u5916\u4e86\u3002 \u3000\u3000\u8d76\u8f66\u7684\u8f66\u592b\u542c\u5230\u6709\u58f0\u97f3\uff0c\u56de\u8fc7\u5934\u5927\u58f0\u95ee\u9053\uff1a\u201c\u5b8b\u7237\uff0c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u9752\u840d\u5251\u7b11\u7b54\uff1a\u201c\u6ca1\u4e8b\u3002\u8d76\u8f66\u7684\u8f66\u592b\u5662\u4e86\u4e00\u58f0\uff0c\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4f4d\u73b0\u5728\u8981\u5230\u54ea\u5757\u53bb\uff1f\u201d \u3000\u3000\u9752\u840d\u5251\u7565\u4e00\u6c89\u541f\uff0c\u9053\uff1a\u201c\u4f60\u5c06\u8f66\u5f80\u524d\u9762\u8d76\u597d\u4e86\uff0c\u5230\u5929\u4eae\u65f6\uff0c\u8d70\u5230\u54ea\u91cc\u5c31\u7b97\u54ea\u91cc\u3002\u201d \u3000\u3000\u8f66\u592b\u614c\u5fd9\u79f0\u662f\u3002 \u3000\u3000\u5df4\u5c71\u5251\u5ba2\u5ffd\u7136\u81ea\u6000\u4e2d\u53d6\u51fa\u5c3a\u8bb8\u5927\u4e00\u4e2a\u5305\u88b1\uff0c\u5305\u88b1\u4e0a\u9690\u9690\u8fd8\u770b\u5f97\u51fa\u4e00\u4e9b\u5df2\u7ecf\u53d1\u6697\u7684\u8840\u8ff9\uff0c \u9053\uff1a\u201c\u8fd9\u4ec7\u72ec\u7684\u6b8b\u9aa8\uff0c\u5c0f\u5f1f\u4e5f\u4e0d\u60f3\u518d\u5e26\u5728\u8eab\u4e0a\u4e86\u3002\u201d\u968f\u8bf4\u7740\u8bdd\uff0c\u968f\u624b\u4e00\u629b\uff0c\u5c06\u90a3\u5305\u88b1\u629b\u5728\u8f66 \u5916\u3002 \u3000\u3000\u9752\u840d\u5251\u4e00\u76b1\u7709\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u53c8\u4f55\u82e6\u5c06\u4eba\u5bb6\u7684\u5c38\u9aa8\u629b\u5728\u8fd9\u8352\u5730\u91cc\u5462\uff1f\u201d \u3000\u3000\u4ed6\u53c8\u53f9\u6c14\u9053\uff1a\u201c\u4f46\u613f\u4ec7\u72ec\u6ca1\u6709\u540e\u4eba\uff0c\u4e0d\u7136\u8fd9\u8840\u6d77\u6df1\u4ec7\uff0c\u600e\u4e48\u62a5\u5f97\u6e05\u5462\uff1f\u201d\u60f3\u5230\u81ea\u5df1\u6240\u643a \u8d70\u7684\u4ec7\u72ec\u6b8b\u9ab8\uff0c\u6b64\u523b\u4ecd\u5806\u5728\u5bb6\u4e2d\u65e7\u7269\u95f4\u91cc\uff0c\u5fc3\u91cc\u53c8\u4e0d\u89c9\u4e00\u9635\u6b49\u7136\u3002 \u3000\u3000\u201c\u5b8b\u5144\uff0c\u90a3\u2018\u5341\u5e74\u4e4b\u540e\uff0c\u4ee5\u8840\u8fd8\u8840\u2019\u516b\u5b57\uff0c\u5230\u5e95\u662f\u5426\u5144\u6240\u5199\u7684\uff1f\u201d \u3000\u3000\u5df4\u5c71\u5251\u5ba2\u95ee\u9053\uff0c\u9752\u840d\u5251\u5b8b\u4ee4\u516c\u5fae\u4e00\u6447\u5934\uff0c\u5e76\u6ca1\u6709\u56de\u7b54\u4ed6\u7684\u8bdd\uff0c\u5fc3\u91cc\u4eff\u4f5b\u5728\u601d\u7d22\u7740\u4e00\u4e2a\u96be \u89e3\u7684\u95ee\u9898\u3002 \u3000\u3000\u8f66\u8f9a\u9a6c\u5636\uff0c\u8f66\u884c\u7a81\u6025\uff0c\u6643\u773c\u4fbf\u6d88\u5931\u5728\u9ed1\u6697\u91cc\u3002 \u7b2c\u4e09\u7ae0>> \u53e4\u9f99\u300a\u6e58\u5983\u5251\u300b \u7b2c\u4e09\u7ae0 \u3000\u3000\u3000\u79cb\u65e5\u6643\u773c\u5373\u53bb\uff0c\u4e25\u5bd2\u7684\u51ac\u5929\u5df2\u968f\u7740\u67ab\u53f6\u7684\u98d8\u843d\uff0c\u767d\u663c\u7684\u9aa4\u77ed\u800c\u6765\u4e86\u3002 \u3000\u3000\u3000\u65e5\u5b50\u53d8\u5f97\u5bc2\u5bde\u800c\u8427\u7d22\uff0c\u5b64\u72ec\u800c\u7f8e\u4e3d\u7684\u6bdb\u51b0\uff0c\u5728\u8fd9\u79cd\u65e5\u5b50\u91cc\uff0c\u5fc3\u60c5\u662f\u843d\u5bde\u800c\u60b2\u54c0\u3000\u7684\u3002 \u3000\u3000\u3000\u7a97\u5916\u96ea\u82b1\u7eb7\u98de\uff0c\u5979\u6253\u5f00\u7a97\u5b50\uff0c\u8ba9\u96ea\u82b1\u98d8\u8fdb\u6765\uff0c\u867d\u7136\u90a3\u662f\u5982\u6b64\u5bd2\u51b7\uff0c\u4f46\u662f\u5979\u5374\u613f\u3000\u610f\u8ba9 \u81ea\u5df1\u7684\u8eab\u4f53\u53d7\u7740\u6298\u78e8\uff0c\u56e0\u4e3a\u552f\u6709\u5979\u8eab\u4f53\u4e0a\u53d7\u7740\u6298\u78e8\u7684\u65f6\u5019\uff0c\u5979\u5185\u5fc3\u7684\u75db\u82b3\uff0c\u624d\u3000\u4f1a\u7a0d\u4e3a\u51cf\u5c11 \u4e00\u4e9b\u3002 \u3000\u3000\u3000\u4e00\u4e2a\u9880\u957f\u7684\u5c11\u5f52\u63a8\u5f00\u4e86\u5979\u90a3\u95f4\u7cbe\u81f4\u7684\u95fa\u623f\u7684\u95e8\uff0c\u8d70\u5382\u8fdb\u6765\uff0c\u624b\u91cc\u62b1\u7740\u4e00\u4e2a\u4ecd\u5728\u3000\u8941\u8913 \u4e2d\u7684\u5a74\u513f\uff0c\u671d\u5979\u5fae\u7b11\u7740\u8bf4\uff1a\u201c\u51b0\u59b9\uff0c\u8fd9\u4e9b\u65e5\u5b50\u6765\u4f60\u8fd8\u597d\u5417\uff1f\u201d\u62ac\u5934\u4e00\u671b\u7a97\u5916\u7684\u3000\u96ea\u82b1\uff0c\u5e7d\u5e7d \u4ed6\u8bf4\u9053\uff1a\u201c\u4f60\u5927\u54e5\u4e0d\u77e5\u600e\u4e48\u641e\u7684\uff0c\u90fd\u5feb\u8fc7\u5e74\u4e86\uff0c\u4ed6\u8fd8\u4e0b\u56de\u6765\u3002\u201d \u3000\u3000\u3000\u6bdb\u51b0\u8f7b\u8f7b\u4e00\u7b11\uff0c\u6ca1\u6709\u56de\u7b54\u5979\u7684\u8bdd\u3002 \u3000\u3000\u3000\u90a3\u5c11\u5987\u5728\u623f\u4e2d\u8e31\u4e86\u4e24\u6b65\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u51b7\u5440\uff01\u201d\u5c06\u6000\u4e2d\u7684\u5a74\u513f\u62b1\u5f97\u66f4\u7d27\u4e86\u4e9b\uff0c\u4e00\u3000\u9762 \u8bf4\uff1a\u201c\u51b0\u59b9\uff0c\u4f60\u597d\u751f\u5c06\u606f\u7740\uff0c\u5343\u4e07\u522b\u80e1\u601d\u4e71\u60f3\uff0c\u4ec0\u4e48\u4e8b\u7b49\u4f60\u809a\u91cc\u7684\u5b69\u5b50\u51fa\u6765\u65f6\u518d\u3000\u8bf4\uff0c\u77e5\u9053 \u4e86\u5417\uff1f\u201d \u3000\u3000\u3000\u6bdb\u51b0\u70b9\u4e86\u70b9\u5934\uff0c\u201c\u77e5\u9053\u4e86\uff0c\u5927\u5ac2\uff0c\u8c22\u8c22\u4f60\u3002\u201d\u90a3\u5c11\u5987\u4e00\u7b11\uff0c\u8d70\u4e86\u51fa\u53bb\uff0c\u6000\u4e2d\u7684\u3000\u5a74\u513f \u7a81\u7136\u54ed\u4e86\u8d77\u6765\uff0c\u5979\u8f7b\u8f7b\u7528\u624b\u62cd\u7740\uff0c\u6ee1\u9762\u4ff1\u662f\u6148\u6bcd\u7684\u6e29\u99a8\uff0c\u8f6f\u8bed\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u522b\u3000\u54ed\uff0c\u4f60\u7238\u7238 \u5c31\u5feb\u56de\u6765\u4e86\u3002\u201d\u53c8\u56de\u5934\u671d\u6bdb\u51b0\u4e00\u7b11\uff0c\u8d70\u51fa\u623f\u53bb\u3002 \u3000\u3000\u3000\u6bdb\u51b0\u5a07\u6175\u5730\u7ad9\u4e86\u8d77\u6765\uff0c\u8d70\u8fc7\u53bb\u5e26\u4e0a\u623f\u95e8\uff0c\u4fa7\u9762\u671b\u4e86\u671b\u5de6\u9762\u7684\u7d2b\u94dc\u83f1\u82b1\u5927\u955c\uff0c\u955c\u3000\u4e2d\u4eba \u5f71\u4e0d\u662f\u6bd4\u4ee5\u524d\u6194\u60b4\u591a\u4e86\u5417\uff1f \u3000\u3000\u3000\u5979\u8f6c\u4e86\u4e00\u4e2a\u8eab\uff0c\u82e6\u7b11\u7740\uff0c\u671b\u7740\u81ea\u5df1\u8fd1\u65e5\u6765\u5df2\u6e10\u5f62\u81c3\u80bf\u7684\u8170\u80a2\uff0c\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u6697\u3000\u5fd6\uff1a \u201c\u600e\u4e48\u8fd9\u6837\u5feb\uff0c\u770b\u6837\u5b50\u5b69\u5b50\u771f\u8981\u51fa\u6765\u4e86\u5462\u3002\u201d \u3000\u3000\u3000\u5979\u7a81\u7136\u611f\u5230\u4e00\u9635\u60b2\u54c0\uff1a\u201c\u53ef\u662f\u5b69\u5b50\u7684\u7238\u7238\u5462\uff1f\u201d\u5979\u5f20\u5f00\u53e3\uff0c\u96ea\u767d\u7684\u7259\u9f7f\u7d27\u54ac\u7740\u3000\u5634 \u5507\uff1a\u201c\u5b69\u5b50\u7684\u7238\u7238\u53ef\u6c38\u8fdc\u4e5f\u56de\u4e0d\u6765\u4e86\uff01\u201d\u4ec7\u72ec\u6e05\u766f\u800c\u82f1\u4fca\u7684\u9762\u5bb9\uff0c\u843d\u5bde\u800c\u6f47\u6d12\u7684\u3000\u8eab\u5f71\uff0c\u84e6 \u5730\u5728\u5979\u5fc3\u4e2d\u5347\u8d77\u3002 \u3000\u3000\u3000\u8fd1\u65e5\u6b66\u6797\u4e2d\uff0c\u4f3c\u4e4e\u8d77\u4e86\u5f88\u5927\u7684\u6ce2\u6d6a\uff0c\u6bdb\u51b0\u867d\u7136\u5df2\u4e0d\u518d\u5728\u6c5f\u6e56\u4e2d\u8d70\u52a8\uff0c\u4f46\u662f\u6b66\u6797\u3000\u4e2d\u7684 \u79cd\u79cd\u6d88\u606f\uff0c\u90fd\u6709\u5979\u5927\u54e5\u6d59\u4e1c\u5927\u8c6a\u7075\u86c7\u6bdb\u81ec\u7684\u5f1f\u5b50\u95e8\u4eba\u6765\u6b64\u53d9\u8bf4\u7740\uff0c\u56e0\u6b64\uff0c\u5979\u4e5f\u3000\u77e5\u9053\u5f97\u975e\u5e38 \u6e05\u695a\u3002 \u3000\u3000\u3000\u4ec7\u5148\u751f\u6b7b\u4e86\uff0c\u5df4\u5c71\u5251\u5ba2\u67f3\u590d\u660e\u548c\u9752\u840d\u5251\u5b8b\u4ee4\u516c\u7a81\u7136\u5728\u6b66\u6797\u4e2d\u6d88\u58f0\u706d\u8ff9\uff0c\u7075\u86c7\u6bdb\u3000\u81ec\u7387 \u9886\u7740\u4e03\u5251\u4e09\u97ad\u53e6\u5916\u4e03\u4eba\uff0c\u5f88\u5e72\u4e86\u51e0\u4ef6\u9707\u52a8\u6b66\u6797\u7684\u5927\u4e8b\uff0c\u5728\u6c5f\u5357\uff0c\u51e1\u662f\u4e0e\u9752\u840d\u5251\u3000\u5b8b\u4ee4\u516c\u6709\u5173 \u7684\u9556\u5c40\uff0c\u628a\u5f0f\u573a\uff0c\u751a\u81f3\u4efb\u4f55\u4e00\u4e2a\u548c\u9752\u840d\u5251\u6cbe\u7740\u4e9b\u4eb2\u6545\u7684\u6b66\u6797\u4eba\u7269\uff0c\u5168\u90e8\u3000\u88ab\u4ed6\u94f2\u9664\u4e86\uff0c\u4e8e\u662f \u7075\u86c7\u6bdb\u81ec\uff0c\u6210\u4e86\u8fd1\u65e5\u4e2d\u539f\u6b66\u6797\u7684\u9b41\u9996\u3002 \u3000\u3000\u3000\u4ed6\u7684\u5f1f\u5b50\u4eec\u8fd8\u5174\u594b\u5730\u544a\u8bc9\u6bdb\u51b0\uff1a\u201c\u5927\u7237\u73b0\u5728\u53ef\u771f\u7684\u4e86\u4e0d\u8d77\u4e86\uff0c\u542c\u8bf4\u5927\u7237\u8fd8\u8981\u5f00\u3000\u5b97\u7acb \u6d3e\uff0c\u81ea\u4e0a\u95e8\u6237\uff0c\u548c\u4e2d\u539f\u6b66\u6797\u7684\u51e0\u4e2a\u5927\u5b97\u6d3e\u4e00\u8f83\u77ed\u957f\u5462\uff01\u201d \u3000\u3000\u3000\u5bf9\u4e8e\u8fd9\u4e00\u5207\uff0c\u6bdb\u51b0\u53ea\u662f\u6de1\u6de1\u5730\u542c\u7740\uff0c\u975e\u4f46\u6ca1\u6709\u4e00\u4e1d\u5174\u594b\uff0c\u800c\u4e14\u8fd8\u611f\u5230\u7f9e\u8fb1\uff0c\u60ed\u3000\u6127\uff0c \u548c\u75db\u82e6\u3002 \u3000\u3000\u3000\u5979\u6068\u81ea\u5df1\u4e3a\u4ec0\u4e48\u4f1a\u505a\u51fa\u8fd9\u79cd\u4e8b\uff0c\u5979\u6068\u5979\u7684\u54e5\u54e5\u7684\u65e0\u803b\uff0c\u4f46\u662f\u8fd9\u4e9b\u8bdd\uff0c\u5979\u53ea\u80fd\u6df1\u3000\u6df1\u5730 \u57cb\u85cf\u5728\u5fc3\u5e95\uff0c\u56e0\u4e3a\u6700\u4ee4\u5979\u75db\u6068\u7684\uff0c\u5374\u662f\u5979\u81ea\u5df1\u5440\uff11\u4e8e\u662f\u5bf9\u4e8e\u4ec7\u72ec\u7684\u6000\u5ff5\u548c\u5979\u81ea\u3000\u5df1\u7684\u81ea\u8d23\uff0c \u6210\u4e86\u5979\u5fc3\u4e2d\u6700\u5927\u7684\u8d1f\u62c5\uff0c\u556e\u566c\u7740\u5979\u7684\u5fc3\uff0c\u7ec8\u4e8e\uff0c\u5979\u4e0d\u518d\u80fd\u5fcd\u53d7\u4e86\uff0c\u5979\u4e0d\u3000\u613f\u518d\u5728\u8fd9\u4e2a\u4ee4\u5979\u75db \u6068\u7684\u5bb6\u5ead\u4e2d\u751f\u6d3b\u4e0b\u53bb\uff0c\u5979\u4e5f\u4e0d\u518d\u613f\u610f\u89c1\u5230\u5979\u7684\u54e5\u54e5\u2014\u2014\u7075\u86c7\u6bdb\u81ec\u3000\u3002 \u3000\u3000\u3000\u5c31\u5728\u90a3\u4e2a\u98ce\u96ea\u4e4b\u591c\uff0c\u6bdb\u51b0\u8fde\u591c\u5954\u51fa\u6545\u5b85\uff0c\u6708\u9ed1\u65e0\u5f71\uff0c\u98ce\u96ea\u6f2b\u5929\uff0c\u5728\u6ce5\u6cde\u800c\u79ef\u96ea\u3000\u7684\u8def \u4e0a\uff0c\u5979\u97ad\u7b56\u7740\u5750\u9a91\uff0c\u5fc3\u4e2d\u832b\u7136\u4e00\u7247\uff0c\u4e0d\u77e5\u4f55\u53bb\u4f55\u4ece\u3002 \u3000\u3000\u3000\u5bd2\u51ac\u7684\u676d\u5dde\uff0c\u5e02\u9762\u8fdc\u4e0d\u53ca\u6625\u65e5\u7684\u7e41\u534e\u4e86\uff0c\u5979\u7f13\u7f13\u9a91\u7740\u9a6c\u3002\u51fa\u57ce\u4e1c\u53bb\uff0c\u5b64\u8eab\u800c\u7f8e\u3000\u8c8c\u7684 \u5c11\u5973\uff0c\u5f15\u5f97\u884c\u4eba\u5f53\u7136\u6ce8\u76ee\uff0c\u6709\u7684\u8fd8\u6307\u7740\u5979\u8bc4\u5934\u8bba\u8db3\u8d77\u6765\uff0c\u5bd2\u98ce\u5439\u8fc7\uff0c\u5979\u98ce\u6c05\u6380\u3000\u8d77\u4e00\u89d2\uff0c\u6709 \u4eba\u7a83\u7a83\u79c1\u8bed\uff1a\u201c\u563f\uff01\u8fd9\u5a18\u513f\u4eec\u809a\u5b50\u600e\u4e48\u8fd9\u4e48\u5927\uff0c\u96be\u9053\u662f\u5077\u4eba\u517b\u6c49\uff0c\u2014\u2014\u3000\u201d\u8bf4\u5230\u4e00\u534a\uff0c\u5934\u4e0a \u88ab\u4eba\u62cd\u5730\u6253\u4e86\u4e00\u4e0b\uff0c\u4e00\u4e2a\u5c0f\u5730\u75de\u5728\u4ed6\u8eab\u65c1\u76f4\u7709\u77aa\u773c\u4ed6\u8bf4\u9053\uff1a\u201c\u5c0f\u3000\u5b50\uff0c\u4f60\u4ed6\u5988\u7684\u4e71\u8bf4\u4e9b\u4ec0 \u4e48\uff0c\u4f60\u77e5\u9053\u8fd9\u4f4d\u59d1\u5a18\u662f\u8c01\uff1f\u201d\u4ed6\u54fc\u4e86\u4e00\u58f0\u63a5\u7740\u8bf4\uff0c\u201c\u5979\u5c31\u662f\u3000\u6bdb\u5927\u592a\u7237\u7684\u4eb2\u59b9\u5b50\uff0c\u4f60\u5fd6\u91cf\u5fd6 \u91cf\uff0c\u518d\u8bf4\u8001\u5b50\u5c31\u5265\u4e86\u4f60\u7684\u76ae\uff01\u201d \u3000\u3000\u3000\u88ab\u6253\u7684\u4eba\u65b9\u81ea\u6012\u706b\u6ee1\u9762\uff0c\u4e00\u542c\u5230\u6bdb\u5927\u592a\u7237\u7684\u540d\u5b50\uff0c\u5413\u5f97\u4e00\u58f0\u4e0d\u54cd\uff0c\u8d76\u7d27\u56de\u5934\u5c31\u3000\u8d70 \u4e86\u3002 \u3000\u3000\u3000\u6bdb\u51b0\u82b3\u5fc3\u7d0a\u4e71\uff0c\u4ec0\u4e48\u8bdd\u90fd\u6ca1\u6709\u542c\u5230\uff0c\u9a6c\u7684\u98a4\u52a8\uff0c\u4f7f\u5979\u6709\u8981\u5455\u5410\u7684\u611f\u89c9\uff0c\u5979\u88f9\u7d27\u3000\u4e86\u8eab \u4e0a\u7684\u98ce\u6c05\uff0c\u671b\u7740\u4e1c\u9762\u7684\u4e91\u971e\uff0c\u51fa\u57ce\u800c\u53bb\u3002 \u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u98ce\u96ea\u7a0d\u715e\u2014\u2014 \u3000\u3000\u3000\u676d\u5dde\u9053\u4e0a\u884c\u4eba\u9887\u591a\uff0c\u4f3c\u4e4e\u90fd\u5c06\u8fd9\u4e25\u5bd2\u89c6\u82e5\u65e0\u7779\uff0c\u6bdb\u51b0\u5fc3\u91cc\u5947\u602a\uff0c\u7ee7\u800c\u4e00\u60f3\uff0c\u539f\u3000\u6765\u8fd9 \u4e9b\u90fd\u662f\u5192\u7740\u98ce\u96ea\u56de\u5bb6\uff0c\u548c\u59bb\u513f\u56e2\u805a\u8fc7\u5e74\u7684\u4eba\u4eec\u3002 \u3000\u3000\u3000\u6bdb\u51b0\u5fc3\u60c5\u4e0d\u7981\u66f4\u5bc2\u5bde\uff0c\u773c\u5149\u7fa1\u6155\u5730\u505c\u7559\u5728\u90a3\u4e9b\u77e5\u8db3\u7684\u5c0f\u4eba\u7269\u8eab\u4e0a\uff0c\u8fc7\u5f80\u7684\u4eba\u4eec\u3000\uff0c\u4e5f \u90fd\u4ee5\u8be7\u5f02\u7684\u773c\u5149\u6253\u91cf\u7740\u8fd9\u5b64\u8eab\u7684\u5c11\u5973\u3002 \u3000\u3000\u3000\u7a81\u7136\uff0c\u6bdb\u51b0\u7684\u773c\u775b\u4eff\u4f5b\u4e00\u82b1\uff0c\u5728\u7edc\u7ece\u4e0d\u7edd\u7684\u884c\u4eba\u4e2d\uff0c\u5979\u7a81\u7136\u53d1\u73b0\u4e86\u4e00\u4e2a\u5947\u5f02\u7684\u3000\u666f \u8c61\u3002 \u3000\u3000\u3000\u539f\u6765\u8fdc\u8fdc\u8d70\u8fc7\u6765\u4e24\u4eba\uff0c\u8eab\u6750\u90fd\u9ad8\u5f97\u60ca\u4eba\uff0c\u5374\u662f\u4e00\u80d6\u4e00\u7626\uff0c\u80d6\u7684\u80d6\u5f97\u53ef\u4ee5\uff0c\u7626\u7684\u3000\u5374\u53ef \u7626\u5f97\u60ca\u4eba\uff0c\u6700\u602a\u7684\u662f\u8fd9\u4e24\u4eba\u8eab\u4e0a\u7a7f\u7684\u8863\u670d\uff0c\u5c45\u7136\u4f1a\u53ee\u94db\u4f5c\u54cd\uff0c\u8d70\u8fd1\u4e86\u4e00\u770b\uff0c\u539f\u3000\u6765\u80d6\u5b50\u8eab\u4e0a \u7684\u201c\u8863\u670d\u201d\u662f\u4e00\u7247\u7247\u7d2b\u94dc\uff0c\u7626\u5b50\u8eab\u4e0a\u7a7f\u7684\u201c\u8863\u670d\u201d\u7adf\u662f\u4e00\u7247\u7247\u9ec4\u91d1\u3002 \u3000\u3000\u3000\u6bdb\u51b0\u4e09\u66f4\u8fc7\u540e\u51fa\u95e8\uff0c\u6b64\u65f6\u5df2\u662f\u4e0a\u5348\uff0c\u5929\u4e0a\u867d\u65e0\u9633\u5149\uff0c\u4f46\u6f2b\u5730\u96ea\u5149\u53cd\u6620\uff0c\u5c06\u90a3\u4e24\u3000\u4eba\u8eab \u4e0a\u7684\u8863\u670d\u6620\u5f97\u8000\u76ee\u751f\u82b1\uff0c\u518d\u4e00\u770b\u4e24\u4eba\u7684\u9762\u5bb9\uff0c\u6bdb\u51b0\u5fc3\u4e2d\u987f\u65f6\u5192\u51fa\u4e00\u80a1\u5bd2\u6c14\uff0c\u8d76\u3000\u7d27\u5c06\u5934\u8f6c\u4e86 \u8fc7\u53bb\u3002 \u3000\u3000\u3000\u7686\u56e0\u90a3\u4e24\u4eba\u975e\u4f46\u5bb9\u8c8c\u602a\u5f02\uff0c\u800c\u4e14\u773c\u4e2d\u8fd8\u6709\u4e00\u79cd\u8bf4\u4e0d\u51fa\u7684\u6151\u4eba\u4e4b\u529b\uff0c\u6bdb\u51b0\u5fc3\u4e2d\u6697\u3000\u81ea\u6253 \u9f13\uff1a\u201c\u8fd9\u4e24\u4eba\u662f\u4ec0\u4e48\u6765\u8def\uff1f\u201d\u5979\u751f\u957f\u5728\u6b66\u5b66\u4e16\u5bb6\uff0c\u81ea\u8eab\u7684\u6b66\u529f\uff0c\u867d\u56e0\u53d7\u4e86\u4f53\u8d28\u3000\u592a\u5f31\u7684\u9650 \u5236\uff0c\u5e76\u4e0d\u592a\u9ad8\uff0c\u4f46\u662f\u6b66\u5b66\u4e00\u9053\uff0c\u5979\u5374\u4e86\u89e3\u5f97\u975e\u5e38\u6e05\u695a\u3002 \u3000\u3000\u3000\u5979\u6697\u5fd6\uff1a\u201c\u8fd9\u4e24\u4eba\u7684\u6b66\u529f\uff0c\u770b\u6765\u7adf\u8fd8\u5728\u5927\u54e5\u4e4b\u4e0a\u3002\u201d\u5ff5\u5934\u4e00\u8f6c\uff0c\u53c8\u60f3\u5230\u4ec7\u72ec\uff1a\u3000\u201c\u5927 \u6982\u5df2\u7ecf\u548c\u72ec\u54e5\u4e0d\u76f8\u4e0a\u4e0b\u4e86\uff0c\u53ef\u662f\u4e2d\u539f\u6b66\u6797\uff0c\u53ef\u4ece\u6765\u6ca1\u6709\u542c\u8d77\u8fc7\u6709\u8fd9\u4e48\u4e24\u4e2a\u4eba\u7269\u3000\u5440\uff0c\u96be\u9053\u662f \u6765\u81ea\u6d77\u5916\u7684\u5417\uff1f\u201d \u3000\u3000\u3000\u6bdb\u51b0\u4e00\u671b\u90a3\u5f62\u5bb9\u8be1\u5f02\u7684\u4e24\u4eba\uff0c\u4fbf\u77e5\u9053\u4ed6\u4eec\u6709\u9ad8\u6df1\u7684\u6b66\u529f\uff0c\u662f\u6709\u5979\u7684\u9053\u7406\u7684\u3002 \u3000\u3000\u3000\u987b\u77e5\u51e1\u662f\u91d1\u94c1\u4e4b\u5c5e\uff0c\u90fd\u4e0d\u80fd\u5fa1\u5bd2\uff0c\u662f\u4ee5\u7a7f\u5728\u8eab\u4e0a\uff0c\u4f60\u4f1a\u66f4\u51b7\uff0c\u6b64\u523b\u6b63\u503c\u814a\u6708\uff0c\u3000\u6c14\u5019 \u6700\u51b7\uff0c\u522b\u4eba\u7a7f\u7740\u72d0\u88d8\uff0c\u5c24\u81ea\u5728\u6253\u7740\u6296\u98a4\uff0c\u8fd9\u4e24\u4eba\u5168\u8eab\u4e0a\u4e0b\uff0c\u770b\u8d77\u6765\u50cf\u662f\u53ea\u6302\u7740\u3000\u767e\u5341\u7247\u91d1\u94c1 \u6253\u9020\u7684\u8584\u7247\uff0c\u65e2\u4e0d\u80fd\u6321\u98ce\uff0c\u66f4\u4e0d\u80fd\u5fa1\u5bd2\uff0c\u4f46\u8fd9\u4e24\u5165\u5374\u4f3c\u4e00\u70b9\u4e5f\u672a\u611f\u89c9\u5230\u3000\u5bd2\u51b7\uff0c\u5927\u8e0f\u6b65\u5730\u8d70 \u7740\uff0c\u4e00\u6b65\u5728\u96ea\u5730\u4e0a\u7559\u4e0b\u4e2a\u811a\u5370\uff0c\u6574\u9f50\u5f97\u6709\u5982\u5200\u5212\uff0c\u6bdb\u51b0\u5fc3\u91cc\u6709\u6570\u3000\uff0c\u8fd9\u4e24\u4eba\u7684\u5185\u529f\uff0c\u4e0d\u662f\u5df2 \u7ec3\u5230\u4e86\u201c\u5bd2\u6691\u4e0d\u4fb5\u201d\u7684\u5730\u6b65\u662f\u4ec0\u4e48\uff1f \u3000\u3000\u3000\u662f\u4ee5\u6bdb\u51b0\u8d76\u7d27\u56de\u8fc7\u5934\u53bb\uff0c\u514d\u5f97\u62db\u60f9\u8fd9\u4e24\u4e2a\u884c\u52a8\u8be1\u5f02\u7684\u89d2\u8272\u3002 \u3000\u3000\u3000\u54ea\u77e5\u90a3\u4e24\u4eba\u773c\u775b\u5374\u505c\u7559\u5728\u6bdb\u51b0\u8138\u4e0a\uff0c\u518d\u4e5f\u4e0d\u653e\u677e\uff0c\u6bdb\u51b0\u5fc3\u91cc\u53d1\u51b7\uff0c\u8138\u4e0a\u53d1\u70e7\uff0c\u3000\u52a0\u7d27 \u97ad\u4e86\u4e00\u4e0b\u9a6c\uff0c\u60f3\u8d70\u8fc7\u53bb\u5c31\u7b97\u4e86\u3002 \u3000\u3000\u3000\u90a3\u4e24\u4eba\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u7a81\u7136\u56de\u8fc7\u4e86\u5934\uff0c\u8ddf\u5728\u6bdb\u51b0\u540e\u9762\uff0c\u8def\u4e0a\u884c\u4eba\uff0c\u770b\u5230\u8fd9\u4e24\u4eba\uff0c\u3000\u90fd\u8fdc \u8fdc\u907f\u5f00\uff0c\u5374\u53c8\u5fcd\u4e0d\u4f4f\u5077\u5077\u56de\u8fc7\u5934\u6765\u770b\u3002 \u3000\u3000\u3000\u90a3\u4e24\u4eba\u4e00\u58f0\u4e0d\u54cd\uff0c\u8d70\u5728\u6bdb\u51b0\u9a6c\u540e\u9762\uff0c\u6bdb\u51b0\u8d8a\u6765\u8d8a\u7d27\u5f20\uff0c\u624b\u638c\u5fc3\u7684\u51b7\u6c57\uff0c\u76f4\u5f80\u5916\u3000\u5192\uff0c \u8def\u4e0a\u884c\u4eba\u5927\u591a\uff0c\u5979\u53c8\u4e0d\u80fd\u653e\u9a6c\u6025\u9a70\uff0c\u6025\u5f97\u82b3\u5fc3\u5fd0\u5fd1\uff0c\u4e0d\u77e5\u600e\u751f\u662f\u597d\uff1f \u3000\u3000\u3000\u53ef\u8d70\u4e86\u4e00\u6bb5\u8def\uff0c\u524d\u9762\u662f\u4e2a\u4e09\u5c94\u8def\u53e3\uff0c\u4e00\u6761\u662f\u5f80\u7b15\u6865\u7684\uff0c\u884c\u4eba\u8f83\u591a\uff0c\u53e6\u4e00\u6761\u8def\u4e0a\u3000\u7684\u884c \u4eba\u5374\u5c11\u5f97\u5f88\uff0c\u6bdb\u51b0\u5fc3\u91cc\u4e00\u76d8\u7b97\uff1a\u201c\u4ed6\u4eec\u8fd9\u6837\u8ddf\u7740\u6211\uff0c\u6211\u53ef\u771f\u5403\u4e0d\u6d88\u4e86\u3002\u201d\u6697\u5fd6\u3000\u81ea\u5df1\u7684\u5750 \u9a91\uff0c\u662f\u5339\u5343\u4e2d\u9009\u4e00\u7684\u826f\u9a79\uff0c\u653e\u9a6c\u4e00\u9a70\u6216\u8bb8\u80fd\u5c06\u4ed6\u4eec\u7529\u5f00\u3002 \u3000\u3000\u3000\u4e8e\u662f\u5979\u4e00\u52d2\u9a6c\u7f30\uff0c\u653e\u5f00\u9a6c\u5411\u8f83\u504f\u50fb\u7684\u8def\u4e0a\u9a70\u53bb\uff0c\u9a6c\u679c\u7136\u8dd1\u5f97\u5f88\u5feb\uff0c\u5979\u80c3\u91cc\u4e00\u9635\u3000\u9635\u53d1 \u9178\uff0c\u5979\u4e5f\u987e\u4e0d\u5f97\uff0c\u4f0f\u5728\u9a6c\u4e0a\u8dd1\u4e86\u51e0\u91cc\u8def\uff0c\u8def\u4e0a\u7b80\u76f4\u8fde\u4e00\u4e2a\u884c\u4eba\u90fd\u6ca1\u6709\u4e86\uff0c\u5979\u81ea\u3000\u5fd6\u5927\u7ea6\u5df2\u5c06 \u90a3\u4e24\u4eba\u6389\u5728\u540e\u9762\u4e86\uff0c\u5fae\u5fae\u7f13\u4f4f\u4e86\u9a6c\uff0c\u56de\u5934\u4e00\u770b\uff0c\u987f\u65f6\u53c8\u662f\u4e00\u80a1\u5bd2\u6c14\u4e0a\u5192\u3000\uff0c\u539f\u6765\u90a3\u88c5\u675f\u602a \u5f02\uff0c\u884c\u8e2a\u8be1\u79d8\u7684\u4e24\u4eba\uff0c\u4e0d\u6025\u4e0d\u7f13\u5730\u8ddf\u5728\u5979\u540e\u9762\uff0c\u9762\u4e0a\u5f62\u5bb9\u4ecd\u7136\u5446\u677f\u3000\u677f\u5730\u6ca1\u6709\u4e00\u4e1d\u53d8\u5316\uff0c\u8138 \u65e2\u6ca1\u6709\u7ea2\uff0c\u66f4\u6ca1\u6709\u5598\u6c14\uff0c\u6bdb\u51b0\u5927\u60ca\u3002\uff1a\u201c\u96be\u9053\u8fd9\u4e24\u4eba\u4f1a\u7f29\u5730\u3000\u4e0d\u6210\uff1f\u201d \u3000\u3000\u3000\u90a3\u4e24\u4eba\u4e5f\u4e0d\u8bf4\u8bdd\uff0c\u65bd\u65bd\u7136\u8ddf\u5728\u5979\u540e\u9762\uff0c\u6bdb\u51b0\u516d\u795e\u65e0\u4e3b\uff0c\u7981\u4e0d\u4f4f\u8001\u662f\u56de\u5934\u53bb\u770b\uff0c\u3000\u53ef\u662f \u4e00\u63a5\u89e6\u5230\u90a3\u4e24\u4eba\u7684\u76ee\u5149\uff0c\u53c8\u5413\u5f97\u8d76\u7d27\u56de\u8fc7\u5934\u53bb\u3002 \u3000\u3000\u3000\u201c\u8fd9\u4e24\u4e2a\u5bb6\u4f19\u5230\u5e95\u5b89\u7740\u4ec0\u4e48\u5fc3\uff0c\u96be\u9053\u2014\u2014\u201d\u60f3\u5230\u8fd9\u91cc\uff0c\u5979\u8138\u4e0a\u66f4\u53d1\u7ea2\uff0c\u518d\u4e5f\u60f3\u3000\u4e0d\u4e0b \u53bb\u3002 \u3000\u3000\u3000\u5979\u5b64\u8eab\u4e00\u4eba\uff0c\u6b66\u529f\u5e76\u4e0d\u592a\u597d\uff0c\u8eab\u4e0a\u53c8\u6709\u8eab\u5b55\uff0c\u5728\u8fd9\u8352\u51c9\u7684\u9053\u8def\u4e0a\uff0c\u771f\u662f\u547c\u5929\u4e0d\u3000\u5e94\uff0c \u547c\u5730\u4e0d\u7075\uff0c\u5979\u6697\u602a\u81ea\u5df1\uff0c\u4e3a\u4ec0\u4e48\u9009\u4e86\u8fd9\u4e48\u6837\u4e00\u6761\u8def\u6765\u8d70\uff0c\u770b\u5230\u524d\u9762\u4ecd\u662f\u65e0\u4eba\u70df\u3000\uff0c\u800c\u4e14\u4eff\u4f5b \u8fd8\u6709\u4e00\u4e2a\u5c0f\u6811\u6797\u5b50\uff0c\u5fc3\u91cc\u66f4\u6025\uff0c\u5dee\u4e00\u70b9\u5c31\u8981\u54ed\u51fa\u6765\u4e86\u3002 \u3000\u3000\u3000\u5979\u77e5\u9053\u8eb2\u4e0d\u5f00\u8fd9\u4e24\u4eba\uff0c\u7d22\u6027\u653e\u7f13\u4e86\u9a6c\uff0c\u5fc3\u91cc\u6253\u7740\u4e3b\u610f\u3002 \u3000\u3000\u3000\u54ea\u77e5\u5ffd\u7136\u5934\u4e00\u6655\uff0c\u90a3\u9a6c\u7adf\u50cf\u817e\u96fe\u9a7e\u4e91\u822c\uff0c\u5f80\u524d\u76f4\u5954\uff0c\u800c\u4e14\u81ea\u5df1\u5750\u5728\u4e0a\u9762\uff0c\u5e73\u5e73\u3000\u7a33\u7a33 \u5730\uff0c\u6ca1\u6709\u4e00\u4e1d\u98a4\u52a8\uff0c\u53ea\u89c9\u4e24\u65c1\u6797\u6728\uff0c\u5982\u98de\u5730\u540e\u9000\uff0c\u90a3\u79cd\u901f\u5ea6\u7b80\u76f4\u662f\u5979\u4ece\u6765\u6ca1\u6709\u3000\u7ecf\u5386\u8fc7\u7684\u3002 \u3000\u3000\u3000\u5979\u5e7c\u7a1a\u5730\u60f3\u7740\uff1a\u201c\u96be\u9053\u771f\u662f\u4f5b\u7956\u663e\u5723\uff0c\u5c06\u6211\u6551\u8131\u8fd9\u4e24\u4eba\u7684\u9b54\u638c\uff1f\u201d\u4f46\u5979\u7a76\u7adf\u5fc3\u3000\u667a\u6e05 \u660e\uff0c\u968f\u5373\u63a8\u7ffb\u4e86\u81ea\u5df1\u7684\u60f3\u6cd5\u3002 \u3000\u3000\u3000\u201c\u4e0d\u8fc7\u8fd9\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\u5462\uff1f\u201d\u5979\u5fc3\u91cc\u66f4\u5947\u602a\uff0c\u60f3\u56de\u5934\u53bb\u770b\u90a3\u4e24\u4eba\u8fd8\u5728\u4e0d\u5728\u540e\u3000\u9762\uff0c \u4f46\u662f\uff0c\u901f\u5ea6\u59d4\u5b9e\u592a\u60ca\u4eba\uff0c\u5979\u751a\u81f3\u8fde\u770b\u4e5f\u770b\u4e0d\u6e05\u695a\u3002 \u3000\u3000\u3000\u7a81\u7136\uff0c\u5979\u5934\u66f4\u6655\uff0c\u4e00\u53cd\u80c3\uff0c\u54c7\u5730\u5410\u4e86\u51fa\u6765\uff0c\u63a5\u7740\u5c31\u4e0d\u7701\u4eba\u4e8b\u4e86\uff0c\u987b\u77e5\u5979\u6000\u7740\u8eab\u3000\u5b55\uff0c \u4f53\u5f31\u53c8\u60ca\u6050\uff0c\u600e\u7ecf\u5f97\u6041\u5730\u5954\u8dd1\u3002 \u3000\u3000\u3000\u7b49\u5230\u5979\u9192\u6765\u7684\u65f6\u5019\uff0c\u5979\u53d1\u89c9\u6709\u4e24\u53ea\u624b\u5728\u5979\u80f8\u8179\u79fb\u52a8\uff0c\u6469\u5a11\u7740\u5979\u7684\u8138\u819b\u548c\u809a\u5b50\uff0c\u3000\u5979\u53c8 \u7f9e\u53c8\u6025\uff0c\u4f46\u662f\u88ab\u90a3\u4e24\u53ea\u624b\u6478\u8fc7\u7684\u5730\u65b9\uff0c\u53c8\u6696\u6d0b\u6d0b\u5730\u8212\u670d\u5df2\u6781\uff0c\u6d51\u8eab\u6ca1\u6709\u534a\u4e1d\u529b\u3000\u91cf\uff0c\u5077\u5077\u7741 \u5f00\u773c\u775b\u4e00\u770b\uff0c\u90a3\u4e00\u80d6\u4e00\u7626\u4e24\u4e2a\u5bb6\u4f19\uff0c\u6b63\u772f\u7740\u773c\uff0c\u4f4e\u7740\u5934\u5728\u671b\u81ea\u5df1\uff0c\u4e24\u53ea\u3000\u624b\u6b63\u5728\u4e0d\u505c\u5730\u5728\u81ea \u5df1\u8eab\u4e0a\u52a8\u7740\u3002\u5979\u4e00\u60f3\u5230\u5c06\u8981\u53d1\u751f\u7684\u540e\u679c\uff0c\u5fc3\u91cc\u66f4\u6025\uff0c\u53cc\u8098\u4e00\u7528\u529b\u3000\uff0c\u60f3\u6323\u624e\u7740\u8df3\u8d77\u6765\uff0c\u54ea\u77e5 \u773c\u4e00\u9ed1\uff0c\u53c8\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u3000\u5979\u518d\u6b21\u9192\u8fc7\u6765\u7684\u65f6\u5019\uff0c\u60c5\u51b5\u4ecd\u4e00\u6837\uff0c\u4ecd\u7136\u6709\u4e24\u53ea\u624b\u5728\u6478\u7740\u5979\u7684\u80f8\u8179\uff0c\u5979\u4e0d\u7981\u5947\u3000\u602a\uff1a \u201c\u600e\u4e48\u8fd9\u5bb6\u4f19\u8001\u662f\u6478\u7740\u6211\uff0c\u96be\u9053\u4ed6\u522b\u7684\u4e8b\u5168\u4e0d\u61c2\u5417\uff1f\u201d\u60f3\u7740\u8fd9\u91cc\uff0c\u5979\u8138\u4e00\u7ea2\uff0c\u3000\u6697\u9a82\u81ea\u5df1\u600e \u4e48\u4f1a\u60f3\u5230\u8fd9\u79cd\u4e8b\u3002 \u3000\u3000\u3000\u4f46\u662f\u4e8b\u5b9e\u5982\u6b64\uff0c\u53c8\u600e\u80fd\u602a\u5f97\u5979\u5982\u4f55\u60f3\u5462\uff1f\u90a3\u884c\u5bb9\u8be1\u5f02\u7684\u4e24\u4e2a\u602a\u5ba2\u5230\u5e95\u662f\u8c01\uff0c\u4e3a\u3000\u4ec0\u4e48 \u8001\u8ddf\u7740\u5979\uff0c\u53c8\u4e3a\u4ec0\u4e48\u5bf9\u5979\u5982\u6b64\u5462\uff1f \u3000\u3000\u3000\u84e6\u5730\uff0c\u4e00\u58f0\u66b4\u559d\uff0c\u4e00\u4e2a\u5979\u9887\u4e3a\u719f\u6089\u7684\u58f0\u97f3\uff0c\u5389\u559d\u9053\uff1a\u201c\u597d\u4e0d\u8981\u8138\uff01\u201d\u516d\u9053\u5bd2\u5f71\u3000\uff0c\u7535 \u95ea\u800c\u81f3\uff0c\u51fb\u5411\u5f2f\u7740\u8170\uff0c\u66f2\u7740\u811a\uff0c\u6b63\u5728\u6478\u7740\u6bdb\u51b0\u4e24\u4eba\u7684\u540e\u5fc3\u3002 \u3000\u3000\u3000\u6bdb\u51b0\u5fc3\u4e2d\u6697\u559c\uff0c\u8fd9\u4e0b\u6765\u4e86\u6551\u5175\u4e86\uff0c\u4e00\u65f6\u5934\u8111\u6df7\u4e71\uff0c\u53ef\u60f3\u4e0d\u8d77\u8fd9\u53e3\u97f3\u662f\u5c5e\u4e8e\u8c01\u7684\u3000\uff0c\u4f46 \u65e0\u8bba\u5982\u4f55\uff0c\u603b\u662f\u4e2a\u719f\u4eba\u5c31\u662f\u4e86\uff0c\u800c\u4e14\u8fd9\u719f\u4eba\u662f\u6765\u6551\u81ea\u5df1\u7684\uff0c\u4e8e\u662f\u5979\u5fc3\u91cc\u7a0d\u7a0d\u4e00\u3000\u5bbd\u3002 \u3000\u3000\u3000\u54ea\u77e5\u90a3\u4e24\u4eba\u5934\u4e5f\u4e0d\u56de\uff0c\u52a8\u4e5f\u4e0d\u52a8\uff0c\u6bdb\u51b0\u53ea\u542c\u5230\u201c\u94db\uff01\u94db\uff01\u201d\u51e0\u54cd\uff0c\u90a3\u4e24\u53ea\u624b\u4ecd\u3000\u5728\u5979 \u8eab\u4e0a\u52a8\u7740\uff0c\u7531\u638c\u5fc3\u4f20\u5230\u5979\u8eab\u4e0a\u7684\u70ed\u529b\uff0c\u4e5f\u6108\u6765\u6108\u70ed\uff0c\u5979\u5168\u8eab\u8212\u6cf0\uff0c\u51e0\u4e4e\u613f\u610f\u8ba9\u3000\u8fd9\u4e24\u53ea\u624b\u6c38 \u8fdc\u6469\u4e0b\u53bb\u3002 \u3000\u3000\u3000\u4ed6\u4eec\u6240\u5b58\u8eab\u7684\u662f\u4e00\u4e2a\u6811\u6797\u5b50\uff0c\u968f\u7740\u90a3\u4e00\u201c\u58f0\u5389\u559d\uff0c\u51e0\u9053\u9556\u5149\u4e00\u6761\u4eba\u5f71\uff0c\u81ea\u6797\u5916\u3000\u500f\u7136 \u63a0\u4e86\u8fdb\u6765\uff0c\u5634\u91cc\u559d\u9053\uff1a\u201c\u5c0f\u5b50\u8fd8\u4e0d\u4f4f\u624b\uff01\u201d \u3000\u3000\u3000\u638c\u4e2d\u957f\u5251\u5e26\u8d77\u98ce\u58f0\uff0c\u5530\u5530\u4e24\u5251\uff0c\u76f4\u53d6\u90a3\u4e24\u4e2a\u602a\u5ba2\u3002 \u3000\u3000\u3000\u8fd9\u4eba\u5f71\u6765\u52bf\u795e\u901f\uff0c\u5251\u5149\u51cc\u5389\uff0c\u8fd9\u4e24\u5251\u4e00\u53d6\u80d6\u5b50\u8111\u540e\u7684\u201c\u85cf\u8840\u7a74\u201d\uff0c\u4e00\u53d6\u7626\u5b50\u9888\u3000\u4e0a\u5927 \u690e\u9aa8\u4e0b\u6570\u7b2c\u516d\u9aa8\u8282\u4e4b\u5185\u7684\u201c\u7075\u53f0\u7a74\u201d\uff0c\u8ba4\u7a74\u4e4b\u51c6\uff0c\u4e0d\u5dee\u6beb\u5398\uff0c\u51fa\u624b\u4e4b\u5feb\uff0c\u4e5f\u8db3\u3000\u60ca\u4eba\uff0c\u663e\u89c1 \u5f97\u662f\u540d\u5bb6\u8eab\u624b\u3002 \u3000\u3000\u3000\u90a3\u4e24\u4e2a\u602a\u5ba2\u4f9d\u7136\u8fde\u5934\u4e5f\u4e0d\u56de\uff0c\u80d6\u5b50\u7684\u5de6\u624b\u548c\u7626\u5b50\u7684\u53f3\u624b\u4e5f\u4f9d\u7136\u5728\u6bdb\u51b0\u7684\u80f8\u8179\u4e4b\u3000\u95f4\u79fb \u7740\uff0c\u5269\u4e0b\u7684\u4e24\u53ea\u624b\uff0c\u80d6\u5b50\u53f3\u638c\u659c\u634f\uff0c\u500f\u5730\u81ea\u65f6\u4e0b\u5012\u7a7f\u800c\u51fa\uff0c\u51fb\u5411\u540e\u9762\u90a3\u5251\u624b\u7684\u3000\u80c1\u4e0b\uff0c\u811a\u8ddf \u4e00\u65cb\uff0c\u5de6\u8db3\u53cd\u8e22\u90a3\u5251\u624b\u7684\u4e0b\u9634\u201c\u4e2d\u6781\u7a74\u201d\uff0c\u7626\u5b50\u4e94\u6307\u5982\u94a9\uff0c\u53cd\u624b\u4e00\u628a\uff0c\u3000\u5c45\u7136\u53bb\u6293\u90a3\u5251\u624b\u7684 \u957f\u5251\uff0c\u90a3\u5251\u624b\u4e00\u60ca\uff0c\u8eab\u5f62\u5fae\u52a8\uff0c\u9000\u540e\u4e86\u4e09\u5c3a\uff0c\u53c8\u63a0\u4e86\u4e0a\u6765\uff0c\u5251\u5149\u3000\u5982\u8679\uff0c\u7ecf\u5929\u800c\u4e0b\uff0c\u53c8\u75be\u5730 \u524a\u5411\u90a3\u4e24\u4e2a\u602a\u5ba2\u7684\u540e\u5fc3\uff0c\u5de6\uff0c\u53f3\u201c\u5fd7\u5802\u201d\u4e24\u7a74\u3002 \u3000\u3000\u3000\u90a3\u4e24\u4e2a\u602a\u5ba2\u9f3b\u5b54\u91cc\u4eff\u4f5b\u54fc\u4e86\u4e00\u58f0\uff0c\u7626\u5b50\u7684\u624b\u80cc\u7a81\u7136\u50cf\u662f\u8131\u4e86\u8282\u4e00\u6837\uff0c\u5411\u4e0a\u9762\u5f39\u3000\u4e86\u8d77 \u6765\u3002 \u3000\u3000\u3000\u90a3\u5251\u624b\u4e00\u5251\u659c\u63a0\uff0c\u7a81\u7136\u624b\u4e2d\u7684\u5251\u4e00\u9707\uff0c\u81ea\u5df1\u7adf\u7136\u628a\u6301\u4e0d\u4f4f\uff0c\u624b\u8155\u4e00\u677e\uff0c\u8131\u624b\u800c\u3000\u53bb\uff0c \u5e26\u7740\u4e00\u6e9c\u84dd\u5149\uff0c\u98de\u5f97\u8001\u8fdc\u3002 \u3000\u3000\u3000\u90a3\u5251\u624b\u5927\u60ca\uff0c\u6697\u5fd6\uff1a\u201c\u8fd9\u4e24\u4eba\u662f\u4ec0\u4e48\u6b66\u529f\uff1f\u201d\u987b\u77e5\u4eba\u4f53\u7684\u5173\u8282\uff0c\u591a\u534a\u53ea\u80fd\u5411\u4e00\u3000\u65b9\u5f2f \u66f2\uff0c\u4e00\u4e1d\u4e5f\u52c9\u5f3a\u4e0d\u5f97\uff0c\u8fd9\u7626\u5b50\u7684\u624b\u81c2\uff0c\u5374\u5c45\u7136\u80fd\u591f\u968f\u610f\u5411\u540e\u626d\u8f6c\uff0c\u8fd9\u7b80\u76f4\u662f\u9a87\u3000\u4eba\u542c\u95fb\uff0c\u532a \u5937\u6240\u601d\u7684\u4e86\u3002 \u3000\u3000\u3000\u4f46\u662f\u90a3\u5251\u624b\u6b66\u529f\u4e0d\u51e1\uff0c\u4e3a\u6c5f\u6e56\u4e0a\u6709\u6570\u7684\u540e\u8d77\u4e4b\u79c0\uff0c\u5fc3\u91cc\u867d\u7136\u5403\u60ca\uff0c\u5374\u5e76\u4e0d\u5341\u5206\u3000\u60e7 \u6015\uff0c\u811a\u6b65\u4e00\u9519\uff0c\u66f2\u65f6\u6c89\u81c2\uff0c\u4e24\u6761\u817f\u50cf\u4e24\u6761\u9489\u5728\u5730\u4e0a\u7684\u77f3\u6869\u5b50\u822c\u7ad9\u5728\u5730\u4e0a\uff0c\u5251\u7709\u5fae\u3000\u8f69\uff0c\u5389\u58f0 \u95ee\u9053\uff1a\u201c\u4f60\u4eec\u662f\u8c01\uff1f\u5728\u4e8e\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u3000\u90a3\u4e24\u4e2a\u884c\u8e2a\u8be1\u5f02\u7684\u602a\u4eba\uff0c\u5374\u50cf\u6839\u672c\u6ca1\u6709\u542c\u89c1\u4ed6\u7684\u8bdd\u4e00\u6837\uff0c\u6bdb\u51b0\u6b64\u523b\u5fc3\u91cc\u5df2\u7565\u4e3a\u3000\u6e05 \u695a\uff0c\u542c\u5230\u8fd9\u5251\u624b\u7684\u58f0\u97f3\uff0c\u5fc3\u4e2d\u6697\u559c\uff1a\u201c\u539f\u6765\u662f\u77f3\u78f7\u3002\u201d\u6084\u6084\u5f20\u5f00\u773c\u6765\uff0c\u5374\u770b\u5230\u90a3\u3000\u4e24\u4e2a\u602a\u4eba \u7684\u8138\u4e0a\uff0c\u795e\u8272\u5e84\u91cd\u5df2\u6781\u3002 \u3000\u3000\u3000\u5979\u5fc3\u91cc\u53c8\u662f\u4e00\u52a8\uff0c\u90a3\u4e24\u4e2a\u602a\u4eba\u5374\u7a81\u7136\u76f4\u8d77\u8170\u6765\uff0c\u624b\u821e\u8db3\u8e48\uff0c\u6ee1\u9762\u4ff1\u662f\u6b22\u60a6\u4e4b\u8272\u3000\uff0c\u8eab \u4e0a\u6302\u7740\u7684\u94c1\u7247\uff0c\u53ee\u5f53\u4e0d\u7edd\u5730\u4f5c\u54cd\u3002 \u3000\u3000\u3000\u90a3\u5c11\u5e74\u5251\u624b\u672c\u540d\u77f3\u78f7\uff0c\u662f\u5f53\u4ee3\u540d\u5251\u5ba2\uff0c\u6b66\u5f53\u6d3e\u7684\u7075\u7a7a\u5251\u5ba2\u7684\u5165\u5ba4\u5f1f\u5b50\uff0c\u51fa\u5e08\u624d\u3000\u53ea\u6570 \u5e74\uff0c\u5728\u6c5f\u6e56\u4e2d\u5df2\u5927\u6709\u540d\u58f0\uff0c\u95ef\u8361\u6c5f\u6e56\uff0c\u4e5f\u53ef\u8bf4\u6709\u4e0d\u5c11\u65e5\u5b50\u4e86\uff0c\u6b64\u523b\u89c1\u4e86\u8fd9\u4e24\u4f4d\u3000\u602a\u4eba\u7684\u8fd9\u4e00 \u4e2a\u52a8\u4f5c\uff0c\u5374\u53ea\u6709\u7741\u5927\u4e86\u773c\u775b\uff0c\u6115\u5728\u90a3\u91cc\uff0c\u4e0d\u77e5\u9053\u8fd9\u4e8b\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\u3002 \u3000\u3000\u3000\u90a3\u4e24\u4e2a\u602a\u4eba\u9ad8\u5174\u4e86\u4e00\u9635\uff0c\u80d6\u7684\u90a3\u4e2a\u7a81\u7136\u638f\u51fa\u4e00\u6837\u4e1c\u897f\uff0c\u62ff\u7ed9\u6bdb\u51b0\u770b\uff0c\u5634\u91cc\u5431\u5431\u3000\u5495\u5495 \u5730\uff0c\u4e0d\u77e5\u5728\u8bb2\u4e9b\u4ec0\u4e48\u8bdd\uff0c\u53c8\u50cf\u662f\u9e1f\u8bed\u3002 \u3000\u3000\u3000\u6bdb\u51b0\u8eba\u5728\u5730\u4e0a\uff0c\u4e00\u65f6\u8fd8\u4e0d\u6562\u8d77\u6765\uff0c\u5979\u867d\u7136\u5c06\u8fd9\u4e24\u4f4d\u602a\u4eba\u6068\u4e4b\u5165\u9aa8\uff0c\u6b64\u523b\u89c1\u4e86\u90a3\u3000\u80d6\u5b50 \u624b\u4e2d\u7684\u7269\u4e8b\uff0c\u5374\u7a81\u7136\u60ca\u5524\u4e86\u8d77\u6765\uff0c\u56db\u80a2\u4e00\u7528\u529b\uff0c\u4eba\u50cf\u5f39\u7c27\u4f3c\uff0c\u76f4\u8dc3\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u3000\u8fd9\u4e00\u8dc3\u5c11\u8bf4\u4e5f\u6709\u4e08\u8bb8\uff0c\u77f3\u78f7\u5927\u5947\uff1a\u201c\u600e\u5730\u5c0f\u51b0\u7684\u8f7b\u529f\u6041\u5730\u597d\uff1f\u201d \u3000\u3000\u3000\u987b\u77e5\u4ece\u5730\u4e0a\u5e73\u5367\u7740\u800c\u8dc3\u8d77\uff0c\u5176\u60c5\u51b5\u81ea\u7136\u8981\u6bd4\u7ad9\u5728\u5730\u4e0a\u56f0\u96be\u5f97\u591a\u3002 \u3000\u3000\u3000\u6bdb\u51b0\u81ea\u5df1\uff0c\u5374\u6ca1\u6709\u6ce8\u610f\u5230\u8fd9\u4e9b\uff0c\u8eab\u8eaf\u521a\u4e00\u843d\u4e0b\u5730\uff0c\u53e3\u91cc\u5df2\u5728\u53eb\u9053\uff1a\u201c\u8fd8\u7ed9\u6211\uff0c\u3000\u8fd8\u7ed9 \u6211\uff01\u201d\u4eff\u4f5b\u5bf9\u8fd9\u6837\u4e1c\u897f\uff0c\u770b\u5f97\u73cd\u8d35\u5df2\u6781\u3002 \u3000\u3000\u3000\u77f3\u78f7\u5fc3\u4e2d\u6697\u53f9\uff1a\u201c\u5979\u770b\u5230\u6211\u600e\u5730\u8fde\u62db\u547c\u90fd\u4e0d\u6253\u4e00\u4e2a\uff1f\u201d \u3000\u3000\u3000\u90a3\u4e24\u4e2a\u602a\u4eba\u5374\u50cf\u6839\u672c\u6ca1\u6709\u542c\u61c2\u5979\u8bf4\u7684\u662f\u4ec0\u4e48\u8bdd\uff0c\u4f9d\u7136\u563b\u76ae\u7b11\u8138\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u624b\u3000\u91cc\u62ff \u7740\u4e00\u4e2a\u5c0f\u76ae\u76d2\u5b50\uff0c\u4e0a\u9762\u7528\u4e00\u6761\u6781\u7ec6\u7684\u91d1\u7ec3\u540a\u4f4f\uff0c\u6447\u52a8\u7684\u65f6\u5019\uff0c\u53d1\u51fa\u4e00\u8fde\u4e32\u6781\u4e3a\u3000\u60a6\u8033\u7684\u54cd \u58f0\u3002 \u3000\u3000\u3000\u5c0f\u76ae\u76d2\u5b50\u540a\u5728\u7ec3\u5b50\u4e0a\u6643\u52a8\uff0c\u6bdb\u51b0\u7684\u773c\u775b\u4e5f\u968f\u7740\u8fd9\u5c0f\u76ae\u76d2\u5b50\u6253\u8f6c\uff0c\u77f3\u78f7\u5fc3\u91cc\u5947\u602a\u3000\uff1a \u201c\u8fd9\u4e2a\u5c0f\u76ae\u76d2\u5b50\u91cc\uff0c\u53c8\u6709\u4ec0\u4e48\u53e4\u602a\u4e0d\u6210\uff1f\u201d \u3000\u3000\u3000\u90a3\u4e00\u80d6\u4e00\u7626\u4e24\u4e2a\u602a\u4eba\uff0c\u89c1\u5230\u6bdb\u51b0\u8138\u4e0a\u7684\u795e\u8272\uff0c\u5431\u5431\u5495\u5495\u5730\u53c8\u8bb2\u4e86\u51e0\u53e5\u8bdd\uff0c\u9762\u4e0a\u3000\u795e \u8272\uff0c\u66f4\u662f\u6b22\u559c\uff0c\u90a3\u80d6\u5b50\u5927\u5634\u4e00\u88c2\uff0c\u671d\u6bdb\u51b0\u54c8\u54c8\u76f4\u7b11\uff0c\u4e00\u53ea\u624b\u4f38\u8fc7\u53bb\uff0c\u50cf\u662f\u60f3\u62c9\u4f4f\u3000\u6bdb\u51b0\u7684\u7389 \u624b\u7684\u6837\u5b50\u3002 \u3000\u3000\u3000\u77f3\u78f7\u66f4\u662f\u5927\u6012\uff0c\u5389\u559d\u9053\uff1a\u201c\u4e07\u6076\u6deb\u5f92\uff0c\u8fd8\u4e0d\u5feb\u62ff\u547d\u6765\uff01\u201d\u8bdd\u58f0\u65b9\u843d\uff0c\u53c8\u590d\u51fa\u624b\u3000\uff0c\u62f3 \u98ce\u62db\u5c55\uff0c\u6a2a\u51fb\u90a3\u4eba\u7684\u7435\u7436\u9aa8\u4fa7\u7684\u201c\u80a9\u4e95\u7a74\u201d\u3002 \u3000\u3000\u3000\u90a3\u4eba\u8138\u8272\u4e00\u53d8\uff0c\u624b\u81c2\u4e00\u4f38\u4e00\u7f29\uff0c\u50cf\u662f\u4e00\u6761\u86c7\u4e00\u6837\uff0c\u500f\u5730\u53cd\u7a7f\u800c\u51fa\uff0c\u53bb\u62ff\u77f3\u78f7\u63e1\u3000\u62f3\u7684 \u624b\u8155\u3002 \u3000\u3000\u3000\u77f3\u78f7\u518d\u4e5f\u60f3\u4e0d\u5230\u90a3\u4eba\u4f1a\u4ece\u8fd9\u79cd\u90e8\u4f4d\u51fa\u62db\uff0c\u5927\u60ca\u4e4b\u4e0b\uff0c\u731b\u4e00\u6c89\u65f6\uff0c\u6307\u5c16\u4e0a\u6311\uff0c\u54ea\u3000\u77e5\u90a3 \u4eba\u7684\u624b\u81c2\u5374\u53ef\u4ee5\u968f\u610f\u626d\u66f2\uff0c\u4e94\u6307\u7b95\u5f20\uff0c\u624b\u8155\u7a81\u5730\u6574\u4e2a\u53cd\u4e86\u8fc7\u6765\uff0c\u5feb\u5982\u7535\u5149\u77f3\u706b\u3000\uff0c\u6293\u4f4f\u4e86\u77f3 \u78f7\u7684\u53f3\u8155\u3002 \u3000\u3000\u3000\u8fd9\u4e00\u62db\u975e\u4f46\u5176\u5feb\u65e0\u6bd4\uff0c\u51fa\u624b\u4e4b\u602a\uff0c\u66f4\u662f\u4ee4\u4eba\u89c1\u6240\u672a\u89c1\uff0c\u95fb\u6240\u672a\u95fb\uff0c\u77f3\u78f7\u8eab\u53d7\u6b66\u3000\u5f53\u6d3e \u7edd\u9876\u9ad8\u624b\u7075\u7a7a\u771f\u4eba\u5341\u5e74\u8033\u63d0\u9762\u547d\uff0c\u6b66\u529f\u5b9e\u6709\u5f88\u6df1\u7684\u6839\u57fa\uff0c\u54ea\u77e5\u9047\u89c1\u8fd9\u602a\u4eba\uff0c\u5168\u3000\u8eab\u6b66\u529f\u7adf\u4e00 \u70b9\u4e5f\u65bd\u5c55\u4e0d\u51fa\u6765\uff0c\u4e00\u62db\u4e4b\u5185\uff0c\u5c31\u88ab\u4eba\u5bb6\u64d2\u4f4f\u624b\u8155\uff0c\u4ed6\u60ca\u6012\u4ea4\u96c6\uff0c\u7adf\u8c41\u51fa\u3000\u53f3\u81c2\u4e0d\u8981\uff0c\u5de6\u624b\u9a88 \u6307\u75be\u5730\u70b9\u5411\u90a3\u4eba\u9e20\u5c3e\u4e0b\u4e00\u5bf8\u7684\u201c\u5de8\u9619\u201d\u5927\u7a74\u3002 \u3000\u3000\u3000\u54ea\u77e5\u90a3\u4eba\u5374\u50cf\u6d51\u5982\u672a\u89c9\uff0c\u77f3\u78f7\u7684\u624b\u6307\u65b9\u81ea\u70b9\u5728\u90a3\u4eba\u8eab\u4e0a\uff0c\u5374\u8f7b\u8f7b\u5411\u65c1\u8fb9\u6ed1\u4e86\u5f00\u3000\u53bb\uff0c \u4ed6\u84e6\u5730\u4e00\u60ca\uff0c\u9661\u7136\u60f3\u8d77\u90a3\u4eba\u8eab\u4e0a\u7684\u8863\u670d\uff0c\u4e43\u91d1\u94c1\u6240\u5236\uff0c\u4ee5\u4ed6\u6b64\u65f6\u7684\u529f\u529b\uff0c\u60f3\u9694\u3000\u7740\u4e00\u5c42\u91d1\u5c5e \u51fb\u7a74\uff0c\u8fd8\u4e0d\u80fd\u591f\u5462\u3002 \u3000\u3000\u3000\u90a3\u4eba\u63e1\u7740\u77f3\u78f7\u7684\u624b\u8155\uff0c\u5374\u4ecd\u865a\u865a\u5730\u672a\u7528\u5168\u529b\uff0c\u53ea\u77aa\u7740\u773c\u671d\u77f3\u78f7\u770b\u7740\uff0c\u5634\u91cc\u8bf4\u4e9b\u3000\u77f3\u78f7 \u4e00\u53e5\u4e5f\u542c\u4e0d\u61c2\u7684\u8bdd\u3002 \u3000\u3000\u3000\u77f3\u78f7\u60ca\u6012\u4ea4\u96c6\uff0c\u624b\u8155\u731b\u5730\u4e00\u7ffb\uff0c\u60f3\u4ee5\u6b66\u5f53\u6d3e\u79d8\u4f20\u7684\u201c\u5c0f\u64d2\u62ff\u624b\u201d\u6323\u8131\u90a3\u4eba\u7684\u624b\u3000\u638c\uff0c \u54ea\u77e5\u90a3\u4eba\u7684\u624b\u8155\u5374\u50cf\u662f\u4e00\u6761\u725b\u7b4b\u7d22\u5b50\uff0c\u4efb\u4f60\u600e\u5730\u7ffb\u8f6c\uff0c\u4ed6\u4e5f\u80fd\u591f\u968f\u7740\u4f60\u7ffb\u8f6c\uff0c\u3000\u77f3\u78f7\u5fc3\u4e2d\u7a81 \u5730\u4e00\u52a8\uff0c\u60f3\u8d77\u5e08\u5085\u66fe\u7ecf\u5bf9\u4ed6\u8bf4\u8d77\u7684\u4e00\u79cd\u4e2d\u571f\u65e9\u5df2\u7edd\u4f20\u7684\u62f3\u6cd5\uff0c\u518d\u4e00\u770b\u90a3\u3000\u80d6\u5b50\u7684\u624b\u638c\u4ee5\u53ca\u808c \u8089\u679c\u7136\u662f\u8272\u5982\u83b9\u767d\uff0c\u5728\u767d\u91cc\u9690\u9690\u900f\u73b0\u4e00\u4e1d\u6de1\u9752\u4e4b\u8272\u6765\uff0c\u5927\u60ca\u4e4b\u4e0b\u3000\uff0c\u9762\u4e0a\u4e5f\u4e0d\u81ea\u89c9\u5730\u53d8\u4e86\u989c \u8272\uff0c\u671d\u6bdb\u51b0\u5927\u559d\u9053\uff1a\u201c\u59b9\u5b50\u5feb\u9003\uff0c\u8fd9\u662f\u2018\u5316\u9aa8\u795e\u62f3\u2019\u3002\u201d \u3000\u3000\u3000\u6bdb\u51b0\u5fc3\u4e2d\u867d\u7136\u6d51\u6d51\u9968\u9968\u5730\uff0c\u55e1\u7136\u4e00\u7247\uff0c\u4e5f\u4e0d\u77e5\u5728\u60f3\u7740\u4e9b\u4ec0\u4e48\uff0c\u4f46\u662f\u8fd9\u201c\u5316\u9aa8\u795e\u3000\u62f3\u201d \u56db\u5b57\uff0c\u5374\u5982\u91d1\u94c1\u63b7\u5730\uff0c\u9707\u5f97\u5979\u795e\u667a\u9661\u7136\u4e00\u6e05\uff01 \u3000\u3000\u3000\u5979\u5e7d\u5e7d\u5730\u4ece\u5e7b\u68a6\u4e2d\u9192\u4e86\u8fc7\u6765\uff0c\u5979\u867d\u7136\u6b66\u529f\u4e0d\u751a\u9ad8\uff0c\u4f46\u662f\u201c\u5316\u9aa8\u795e\u62f3\u201d\u8fd9\u56db\u5b57\u6240\u3000\u4ee3\u8868 \u7684\u610f\u601d\uff0c\u5979\u662f\u975e\u5e38\u4e86\u89e3\u7684\uff0c\u6570\u5341\u5e74\u524d\u6b66\u6797\u4e2d\u51fa\u4e86\u4e2a\u5927\u5927\u7684\u5947\u4eba\uff0c\u53eb\u6d77\u5929\u5b64\u71d5\uff0c\u3000\u4e5f\u4ece\u6765\u6ca1\u6709 \u4eba\u77e5\u9053\u4ed6\u7684\u6765\u8e2a\u53bb\u8ff9\u3002\u4ed6\u5728\u4e2d\u539f\u6b66\u6797\u9732\u9762\u867d\u7136\u53ea\u6709\u77ed\u77ed\u6570\u5e74\u529f\u592b\uff0c\u4f46\u662f\u3000\u58f0\u540d\u4e4b\u663e\u8d6b\uff0c\u5374\u662f \u65e0\u53ef\u6bd4\u654c\u7684\uff0c\u66fe\u7ecf\u8d64\u624b\u7a7a\u62f3\uff0c\u8fde\u8d25\u4e2d\u539f\u6b66\u6797\u5404\u95e8\u5404\u6d3e\u7684\u4e8c\u5341\u4e03\u4e2a\u3000\u638c\u95e8\u4eba\uff0c\u6bcf\u4e2a\u4eba\u5728\u4ed6\u624b\u4e0b \u90fd\u672a\u66fe\u8d70\u6ee1\u5341\u62db\uff0c\u5f53\u65f6\u6c5f\u6e56\u5927\u9a87\uff0c\u90fd\u9053\u5343\u767e\u5e74\u6765\uff0c\u6b66\u6797\u4e2d\u3000\u90fd\u672a\u6709\u4eba\u80fd\u548c\u4ed6\u5339\u654c\u7684\u3002 \u3000\u3000\u3000\u800c\u6d77\u5929\u5b64\u71d5\u6240\u4f7f\u7684\u62f3\u6cd5\uff0c\u5c31\u662f\u8fd9\u201c\u5316\u9aa8\u795e\u62f3\u201d\u3002 \u3000\u3000\u3000\u81ea\u6d77\u5929\u5b64\u71d5\u7a81\u7136\u9690\u8eab\u4e4b\u540e\uff0c\u82b8\u82b8\u6c5f\u6e56\u4e2d\uff0c\u518d\u6ca1\u6709\u4e00\u4e2a\u4eba\u4f1a\u4f7f\u8fd9\u79cd\u602a\u5f02\u7edd\u4f26\u7684\u62f3\u3000\u6cd5\uff0c \u4f46\u6570\u5341\u5e74\u6765\uff0c\u6b66\u6797\u4e2d\u63d0\u8d77\u201c\u5316\u9aa8\u795e\u62f3\u201d\uff0c\u5374\u4ecd\u7136\u662f\u8c08\u864e\u800c\u8272\u53d8\u7684\uff0c\u662f\u4ee5\u77f3\u78f7\u4e00\u3000\u63d0\u8fd9\u56db\u5b57\uff0c \u6bdb\u51b0\u7acb\u65f6\u5927\u60ca\uff01 \u3000\u3000\u3000\u5979\u695e\u4e86\u4e00\u4f1a\uff0c\u671d\u8fd9\u884c\u5bb9\u8be1\u5f02\u7684\u4e24\u4eba\u671b\u4e86\u4e00\u773c\uff0c\u60ca\u5947\u5730\u601d\u5fd6\u7740\uff1a\u201c\u96be\u9053\u8fd9\u4e24\u4e2a\u602a\u3000\u4eba\u6240 \u4f7f\u7684\uff0c\u771f\u662f\u2018\u5316\u9aa8\u795e\u62f3\u2019\u5417\uff1f\u201d \u3000\u3000\u3000\u6b64\u65f6\u77f3\u78f7\u7a81\u7136\u4e00\u58f0\u95f7\u54fc\uff0c\u8eab\u5b50\u8f6f\u8f6f\u5730\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u3000\u7b14\u4e0b\u5199\u6765\u867d\u6162\uff0c\u7136\u8fd9\u4e9b\u5728\u5f53\u65f6\u5374\u53ea\u4e0d\u8fc7\u662f\u4e00\u77ac\u95f4\u7684\u4e8b\u3002 \u3000\u3000\u3000\u6bdb\u51b0\u5fc3\u91cc\u518d\u65e0\u601d\u8003\u7684\u4f59\u5730\uff0c\u77f3\u78f7\u4e3a\u4e86\u6551\u5979\uff0c\u5979\u53c8\u5c82\u80fd\u6492\u624b\u4e00\u8d70\uff0c\u4f55\u51b5\u6700\u91cd\u8981\u7684\u3000\u662f\u90a3 \u4e2a\u5c0f\u76ae\u76d2\u5b50\u6b64\u523b\u4ecd\u5728\u522b\u4eba\u624b\u4e0a\uff0c\u5979\u6697\u54ac\u94f6\u7259\uff0c\u6697\u9053\uff1a\u201c\u5373\u4f7f\u6211\u5931\u53bb\u6027\u547d\uff0c\u4e5f\u8981\u3000\u5c06\u8fd9\u5c0f\u76d2\u5b50 \u62ff\u56de\u6765\u7684\u3002\u201d \u3000\u3000\u3000\u4f46\u662f\u5979\u4e5f\u77e5\u9053\uff0c\u4ee5\u5979\u81ea\u8eab\u7684\u529b\u91cf\uff0c\u8981\u60f3\u62b5\u654c\u8fd9\u4e24\u4e2a\u602a\u4eba\uff0c\u7edd\u65e0\u53ef\u80fd\uff0c\u79c0\u7709\u5fae\u98a6\u3000\uff0c\u5728 \u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u5979\u53c8\u80fd\u6709\u4ec0\u4e48\u9009\u62e9\uff1f \u3000\u3000\u3000\u90a3\u4e24\u4e2a\u602a\u4eba\u671b\u4e5f\u4e0d\u671b\u5012\u5728\u5730\u4e0a\u7684\u77f3\u78f7\u4e00\u773c\uff0c\u4ecd\u5bf9\u5979\u770b\u7740\uff0c\u7626\u5b50\u624b\u4e2d\u7684\u5c0f\u76ae\u76d2\u8d8a\u3000\u6643\u8d8a \u6025\uff0c\u76d2\u5b50\u91cc\u53d1\u51fa\u7684\u58f0\u97f3\u4e5f\u8d8a\u6765\u8d8a\u6025\u9aa4\uff0c\u90a3\u80d6\u5b50\u5927\u7ea6\u4e5f\u5df2\u77e5\u9053\u5bf9\u65b9\u542c\u4e0d\u61c2\u81ea\u5df1\u7684\u3000\u8bdd\uff0c\u6025\u5f97\u6293 \u8033\u6478\u989d\uff0c\u4e71\u6253\u624b\u5f0f\u3002\u6bdb\u51b0\u867d\u7136\u806a\u660e\u7edd\u9879\uff0c\u4f46\u662f\u6b64\u523b\u5979\u5f53\u5c40\u8005\u8ff7\uff0c\u7adf\u6ca1\u6709\u3000\u770b\u6e05\u773c\u524d\u7684\u60c5\u52bf\uff0c \u66f4\u6ca1\u6709\u5206\u8fa8\u51fa\u90a3\u80d6\u5b50\u6240\u6253\u624b\u5f0f\u7684\u610f\u4e49\u3002 \u3000\u3000\u3000\u5979\u7a81\u7136\u671d\u90a3\u7626\u5b50\u4e00\u7b11\uff0c\u90a3\u7626\u5b50\u5fd9\u4e5f\u671d\u5979\u4e00\u7b11\uff0c\u54ea\u77e5\u5979\u8fd9\u4e00\u7b11\u5374\u662f\u7528\u6765\u5206\u6563\u4eba\u5bb6\u3000\u5fc3\u795e \u7684\u3002\u968f\u7740\u8fd9\u4e00\u7b11\uff0c\u5979\u4e00\u4e2a\u7bad\u6b65\u7a9c\u4e86\u4e0a\u53bb\uff0c\u5288\u624b\u53bb\u593a\u90a3\u7626\u5b50\u624b\u4e0a\u7684\u76ae\u76d2\u5b50\uff0c\u90a3\u7626\u3000\u5b50\u50cf\u662f\u4e0d\u4f1a \u9632\u5907\uff0c\u624b\u81c2\u52a8\u4e5f\u672a\u52a8\u3002 \u3000\u3000\u3000\u6bdb\u51b0\u624b\u4e00\u63a5\u89e6\u90a3\u76ae\u76d2\u5b50\uff0c\u4e0d\u7981\u5927\u559c\uff0c\u624b\u8155\u4e00\u7529\u529b\uff0c\u8eab\u5f62\u540e\u9000\uff0c\u4ee5\u4e3a\u5df2\u5c06\u90a3\u76ae\u76d2\u3000\u5b50\u62a2 \u4e86\u8fc7\u6765\uff0c\u731b\u4e00\u65cb\u8eab\uff0c\u811a\u5c16\u987f\u5904\uff0c\u63a0\u8d77\u4e09\u4e24\u4e08\u8fdc\u8fd1\uff0c\u60f3\u4e58\u9699\u9003\u8d70\uff0c\u8fd9\u65f6\u5019\u5979\u751a\u81f3\u3000\u5df2\u5c06\u4e3a\u5979\u62fc \u547d\u7684\u77f3\u78f7\u5fd8\u8bb0\u4e86\u3002 \u3000\u3000\u3000\u54ea\u77e5\u5728\u5979\u811a\u6b65\u5fae\u4e00\u505c\u987f\u7684\u65f6\u5019\uff0c\u5979\u773c\u524d\u4e00\u82b1\uff0c\u90a3\u7626\u5b50\u4ecd\u7136\u5e26\u7740\u4e00\u8138\u83ab\u6d4b\u9ad8\u6df1\u7684\u3000\u795e \u8272\uff0c\u7ad9\u5728\u5979\u5bf9\u9762\u3002 \u3000\u3000\u3000\u800c\u5979\u624b\u4e0a\u90a3\u76ae\u76d2\u5b50\u7684\u53e6\u4e00\u7aef\u91d1\u7ec3\u5b50\uff0c\u4e5f\u4ecd\u7136\u597d\u597d\u5730\u63e1\u5728\u90a3\u7626\u5b50\u624b\u91cc\uff0c\u5979\u8fd9\u4e00\u60ca\u3000\uff0c\u66f4 \u662f\u975e\u540c\u5c0f\u53ef\uff0c\u5979\u518d\u4e5f\u60f3\u4e0d\u5230\uff0c\u8fd9\u7626\u5b50\u7684\u8f7b\u529f\u5c45\u7136\u5df2\u5230\u8fd9\u6837\u7684\u5730\u6b65\uff0c\u5e76\u975e\u9a87\u4eba\u542c\u3000\u95fb\uff0c\u7b80\u76f4\u532a \u5937\u6240\u601d\u4e86\u3002 \u3000\u3000\u3000\u90a3\u80d6\u5b50\u4e5f\u8ddf\u4e86\u8fc7\u6765\uff0c\u811a\u6b65\u5e76\u672a\u79fb\u52a8\uff0c\u8eab\u5f62\u5374\u5982\u884c\u4e91\u6d41\u6c34\uff0c\u5e73\u7a33\u5f97\u8fde\u8eab\u4e0a\u7684\u91d1\u7247\u3000\u90fd\u6ca1 \u6709\u53d1\u51fa\u534a\u70b9\u58f0\u97f3\u6765\u3002 \u3000\u3000\u3000\u4ed6\u4e00\u63a0\u5230\u6bdb\u51b0\u7684\u8eab\u4fa7\uff0c\u53c8\u5431\u5431\u5495\u5495\u4ed6\u8bf4\u8d77\u8bdd\u6765\uff0c\u53ef\u662f\u6bdb\u51b0\u5374\u4e0d\u61c2\uff0c\u5979\u53ea\u80fd\u53d1\u7740\u3000\u695e\uff0c \u4e0d\u77e5\u9053\u81ea\u5df1\u7a76\u7adf\u5e94\u8be5\u600e\u4e48\u529e\u624d\u597d\u3002 \u3000\u3000\u3000\u4eba\u5bb6\u7684\u8f7b\u529f\uff0c\u4e0d\u77e5\u6bd4\u81ea\u5df1\u9ad8\u660e\u591a\u5c11\u500d\uff0c\u6b66\u529f\uff0c\u66f4\u4e0d\u7528\u8bf4\u4e86\uff0c\u81ea\u5df1\u6253\u53c8\u6253\u4e0d\u8fc7\uff0c\u3000\u9003\u4e5f \u9003\u4e0d\u6389\uff0c\u96be\u9053\u53ea\u6709\u675f\u7740\u53cc\u624b\u542c\u51ed\u4eba\u5bb6\u5bb0\u5272\u5417\uff1f \u3000\u3000\u3000\u5979\u662f\u771f\u6b63\u5730\u60ca\u60e7\u800c\u60b2\u54c0\u4e86\u3002\u90a3\u80d6\u5b50\u8bf4\u4e86\u4e00\u5806\uff0c\u5f53\u7136\u6ca1\u6709\u4e00\u4e1d\u6548\u679c\u3002 \u3000\u3000\u3000\u90a3\u7626\u5b50\u53cc\u7709\u7d27\u76b1\uff0c\u8d39\u529b\u5730\u601d\u7d22\u4e86\u534a\u664c\uff0c\u7a81\u5730\u4e00\u6258\u8111\u888b\uff0c\u4f38\u51fa\u90a3\u53ea\u867d\u7136\u7626\u5982\u4e4c\u722a\u3000\uff0c\u4f46 \u5374\u4ecd\u7136\u8272\u5982\u83b9\u7389\u7684\u624b\u6765\uff0c\u671d\u6bdb\u51b0\u624b\u4e0a\u7d27\u5f20\u6293\u4f4f\u7684\u76ae\u76d2\u5b50\u4e00\u6307\uff0c\u53c8\u671d\u6bdb\u51b0\u7684\u8116\u5b50\u3000\u4e00\u6307\uff0c\u671f\u671b \u5730\u671b\u7740\u6bdb\u51b0\u3002 \u3000\u3000\u3000\u6bdb\u51b0\u8d8a\u5f04\u8d8a\u7cca\u6d82\uff0c\u6b64\u65f6\u5979\u53c8\u751f\u51fa\u4e00\u4e9b\u597d\u5947\u5fc3\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u4e24\u4e2a\u5bb6\u4f19\u5230\u5e95\u8981\u5e72\u4ec0\u3000 \u4e48\uff1f\u201d\u4e0d\u7981\u4f4e\u5934\u671d\u81ea\u5df1\u7684\u8116\u5b50\u4e00\u770b\u3002 \u3000\u3000\u3000\u5979\u4e00\u770b\u4e4b\u4e0b\uff0c\u518d\u4e5f\u5fcd\u4e0d\u4f4f\u53eb\u51fa\u58f0\u6765\uff0c\u539f\u6765\u5979\u7684\u8116\u5b50\u4e0b\u9762\uff0c\u4ecd\u7136\u597d\u597d\u5730\u6302\u7740\u4e00\u4e2a\u3000\u548c\u90a3 \u4e00\u5f0f\u4e00\u6837\u7684\u76ae\u76d2\u5b50\u3002 \u3000\u3000\u3000\u5979\u624b\u4e00\u677e\uff0c\u5fc3\u4e2d\u7591\u7aa6\u4e1b\u751f\uff1a\u201c\u539f\u6765\u8fd9\u7626\u5b50\u624b\u4e0a\u7684\u76ae\u76d2\u5b50\u4e0d\u662f\u6211\u7684\uff0c\u4f46\u662f\u90a3\u53c8\u662f\u3000\u4ece\u54ea \u91cc\u6765\u7684\u5462\uff1f\u96be\u9053\u8fd9\u4e24\u4e2a\u5bb6\u4f19\u7adf\u548c\u4ed6\u6709\u4ec0\u4e48\u5173\u8fde\u5417\uff1f\u8fd9\u5012\u771f\u5947\u602a\u4e86\uff0c\u90a3\u4e48\u8fd9\u4e24\u4eba\u3000\u53c8\u662f\u4ece\u54ea\u91cc \u6765\u7684\u5462\uff1f\u4ed6\u4eec\u8fd9\u6837\u82e6\u82e6\u903c\u6211\uff0c\u5374\u53c8\u662f\u4e3a\u7740\u4ec0\u4e48\u5462\uff1f\u201d\u5979\u767e\u601d\u4e0d\u89e3\uff0c\u53c8\u5446\u3000\u4f4f\u4e86\u3002 \u7b2c\u56db\u7ae0>> \u53e4\u9f99\u300a\u6e58\u5983\u5251\u300b \u7b2c\u56db\u7ae0 \u3000\u3000\u3000\u6bdb\u51b0\u4e00\u4f4e\u5934\uff0c\u5374\u53d1\u89c9\u90a3\u88ab\u5979\u81ea\u5df1\u7231\u82e5\u6027\u547d\u7684\u76ae\u76d2\uff0c\u4ecd\u597d\u597d\u5730\u6302\u5728\u5979\u8116\u5b50\u4e0b\u9762\uff0c\u3000\u5fc3\u5934 \u4e0d\u7981\u731b\u5730\u4e00\u9635\u5267\u8df3\uff0c\u867d\u7136\u559c\u51fa\u671b\u5916\uff0c\u4f46\u5728\u5979\u5fc3\u4e2d\u6240\u751f\u7684\u90a3\u4e00\u4efd\u7591\u5fcc\uff0c\u5374\u4e5f\u5e76\u4e0d\u3000\u5728\u8fd9\u559c\u60a6\u7684 \u611f\u89c9\u4e4b\u4e0b\u3002 \u3000\u3000\u3000\u5979\u60d8\u7136\u8fdb\u5165\u56de\u5fc6\u91cc\uff0c\u9762\u524d\u90a3\u8be1\u79d8\u7684\u80d6\u7626\u4e24\u4eba\u7684\u8eab\u5f71\uff0c\u5728\u5979\u773c\u4e2d\u5df2\u662f\u6de1\u832b\u4e00\u7247\uff0c\u3000\u800c\u4ec7 \u72ec\u82f1\u4fca\u3001\u6e05\u766f\u7684\u9762\u5bb9\uff0c\u53c8\u6e05\u6670\u5730\u5728\u5979\u8111\u6d77\u4e2d\u6cdb\u4e86\u8d77\u6765\u3002 \u3000\u3000\u3000\u5979\u8bb0\u8d77\u90a3\u4e00\u5929\uff0c\u5f53\u4ec7\u72ec\u5e26\u7740\u6ee1\u8138\u60b2\u6ca7\u7684\u60c5\u610f\u79bb\u5f00\u5979\u65f6\uff0c\u5979\u5fc3\u4e2d\u7684\u90a3\u4e00\u4efd\u81ea\u759a\u548c\u3000\u6127 \u4f5c\uff0c\u7136\u800c\u4ec7\u72ec\u5374\u4ee5\u4e3a\u5979\u662f\u4e3a\u4e86\u79bb\u5f00\u81ea\u5df1\u800c\u96be\u53d7\uff0c\u4e8e\u662f\u4ed6\u4ece\u6000\u4e2d\u62ff\u51fa\u8fd9\u76ae\u76d2\u6765\u7ed9\u5979\u3000\uff0c\u5e76\u4e14\u8bf4 \u8fd9\u662f\u4ed6\u5e73\u751f\u6700\u5bcc\u7eaa\u5ff5\u4ef7\u503c\u7684\u4e00\u4ef6\u4e1c\u897f\uff0c\u5979\u770b\u5f97\u51fa\u4ed6\u5f53\u65f6\u8138\u4e0a\u90d1\u91cd\u7684\u795e\u8272\u3002 \u3000\u3000\u3000\u6b64\u540e\uff0c\u8fd9\u76ae\u76d2\u4fbf\u65f6\u523b\u4e0d\u79bb\u5730\u8ddf\u968f\u5979\u8eab\u65c1\uff0c\u6bcf\u5f53\u5979\u5fc6\u8d77\u4ec7\u72ec\uff0c\u5fc6\u8d77\u81ea\u5df1\u5bf9\u4ec7\u72ec\u6240\u3000\u6b20\u8d1f \u7684\u90a3\u4e00\u4efd\u60c5\u611f\u548c\u826f\u5fc3\u4e0a\u7684\u503a\uff0c\u5979\u5c31\u4f1a\u65e0\u8a00\u5730\u5c06\u8fd9\u76ae\u76d2\u62ff\u51fa\u6765\uff0c\u9759\u9759\u5730\u51dd\u671b\u548c\u628a\u3000\u73a9\u7740\uff0c\u8ba9\u81ea \u5df1\u56de\u5230\u4ee5\u5f80\u53bb\u3002 \u3000\u3000\u3000\u662f\u4ee5\u5f53\u5979\u770b\u5230\u90a3\u8be1\u79d8\u7684\u4e24\u4e2a\u4eba\u624b\u4e2d\u62ff\u7740\u8fd9\u76ae\u76d2\u65f6\uff0c\u5979\u5fc3\u4e2d\u7684\u6025\uff0c\u7adf\u8fdc\u5728\u4efb\u4f55\u4e8b\u3000\u4e4b \u4e0a\uff0c\u8fd9\u5f53\u7136\u662f\u7531\u4e8e\u5979\u5bf9\u4ec7\u72ec\u6df1\u539a\u7684\u60c5\u611f\u6240\u81f4\u3002 \u3000\u3000\u3000\u4f46\u662f\u5979\u5374\u53d1\u73b0\u81ea\u5df1\u7684\u8116\u4e86\u4e0a\u4f55\u4ee5\u597d\u7aef\u7aef\u5730\u6302\u7740\u4e00\u4e2a\u76ae\u76d2\uff0c\u4e8e\u662f\u5979\u66f4\u60ca\u5f02\uff0c\u8fd9\u4e24\u3000\u4e2a\u602a \u5ba2\u4e3a\u4ec0\u4e48\u6709\u548c\u8fd9\u4e00\u6837\u4e00\u5f0f\u7684\u76ae\u76d2\u5462\uff1f\u96be\u9053\u4ed6\u4eec\u548c\u4ec7\u72ec\u4e4b\u95f4\u6709\u7740\u4ec0\u4e48\u5173\u8fde\u5417\uff1f\u4ed6\u3000\u4eec\u5bf9\u81ea\u5df1\u8fd9 \u6837\u53c8\u662f\u4e3a\u4ec0\u4e48\u5462\uff1f \u3000\u3000\u3000\u8fd9\u5b9e\u5728\u662f\u4ee4\u6bdb\u51b0\u4e0d\u89e3\uff0c\u5979\u832b\u7136\u62ac\u8d77\u5934\u6765\uff0c\u90a3\u4e2a\u602a\u5ba2\u4ecd\u5e26\u7740\u7b11\u5bb9\u671b\u7740\u5979\uff0c\u6b64\u65f6\u5979\u3000\u5bf9\u8fd9 \u4e24\u4e2a\u602a\u5ba2\u7684\u6050\u60e7\u4e4b\u5fc3\uff0c\u867d\u5df2\u5b8c\u5168\u6d88\u5931\u4e86\uff0c\u4f46\u5979\u4e5f\u6ca1\u6709\u65b9\u6cd5\u6765\u5411\u4ed6\u4eec\u8868\u8fbe\u81ea\u5df1\u5fc3\u3000\u4e2d\u7684\u610f\u601d\u3002 \u3000\u3000\u3000\u8fd9\u79cd\u8a00\u8bed\u7684\u9694\u9602\uff0c\u662f\u5979\u7b2c\u4e00\u6b21\u611f\u89c9\u5230\u7684\uff0c\u5979\u6697\u5fd6\uff1a\u201c\u5728\u4ed6\u4eec\u9762\u524d\uff0c\u6211\u7b80\u76f4\u548c\u54d1\u3000\u5df4\u4e00 \u6837\u2014\u2014\u201d\u4e00\u5ff5\u81f3\u6b64\uff0c\u5fc3\u4e2d\u5ffd\u5730\u4e00\u52a8\uff0c\u8f6c\u5ff5\u5fd6\u9053\uff1a\uff0c\u2018\u5c31\u662f\u54d1\u5df4\uff0c\u4e5f\u53ef\u4ee5\u5411\u5bf9\u65b9\u3000\u8868\u9732\u5fc3\u610f\u7684 \u5440\uff0c\u6211\u8bf4\u7684\u4ed6\u4eec\u542c\u4e0d\u61c2\uff0c\u96be\u9053\u6211\u5199\u7684\u5b57\u4ed6\u4eec\u4e5f\u770b\u4e0d\u61c2\u5417\uff1f\u201d\u5979\u8138\u4e0a\u5fae\u5fae\u3000\u9732\u51fa\u559c\u60a6\u4e4b\u8272\uff0c\u8fd9 \u662f\u56e0\u4e3a\u5979\u53d1\u73b0\u4e86\u4e00\u79cd\u65b9\u6cd5\u53ef\u4ee5\u89e3\u51b3\u81ea\u5df1\u5fc3\u4e2d\u7684\u7591\u56e2\uff0c\u800c\u7edd\u4e0d\u662f\u56e0\u3000\u4e3a\u81ea\u5df1\u5fc3\u91cc\u5f00\u5fc3\u4e4b\u6545\u3002\u90a3 \u4e24\u4e2a\u602a\u5ba2\u89c1\u5979\u9762\u4e0a\u9732\u51fa\u559c\u8272\uff0c\u8fd9\u79cd\u60c5\u611f\u4e0a\u7684\u6d41\u9732\uff0c\u4ed6\u4eec\u81ea\u3000\u7136\u770b\u5f97\u51fa\u6765\uff0c\u90a3\u80d6\u5b50\u4e00\u8f6c\u8138\uff0c\u671d \u90a3\u7626\u5b50\u8bf4\u4e86\u51e0\u53e5\u8bdd\uff0c\u6bdb\u51b0\u5f53\u7136\u4ecd\u662f\u4e0d\u61c2\uff0c\u4f46\u770b\u4ed6\u4eec\u3000\u7684\u8bed\u6c14\uff0c\u4e5f\u542c\u5f97\u51fa\u4ed6\u4eec\u662f\u5728\u9ad8\u5174\u3002\u4e8e\u662f \u5979\u8e72\u4e86\u4e0b\u53bb\uff0c\u7528\u624b\u4e0a\u7559\u7740\u7684\u5e76\u4e0d\u592a\u957f\uff0c\u4f46\u4e5f\u3000\u4e0d\u592a\u77ed\u7684\u6307\u7532\uff0c\u5728\u5730\u4e0a\u5212\u4e86\u201c\u4ec7\u72ec\u201d\u4e24\u5b57\u3002\u90a3 \u4e24\u4e2a\u602a\u5ba2\uff0c\u770b\u5230\u4e86\u5979\u8fd9\u52a8\u4f5c\uff0c\u4e5f\u8d76\u7d27\u3000\u8e72\u4e86\u4e0b\u53bb\uff0c\u8eab\u4e0a\u7684\u91d1\u94c1\u7247\u5b50\u54d7\u5566\u54d7\u5566\u5730\u54cd\u7740\uff0c\u4e0b\u6446\u5df2 \u62c2\u5728\u5730\u4e0a\u3002\u4e24\u4eba\u671d\u90a3\u201c\u4ec7\u72ec\u201d\u3000\u770b\u4e86\u534a\u664c\uff0c\u5ffd\u7136\u4e00\u9f50\u8df3\u4e86\u8d77\u6765\uff0c\u8fde\u8fde\u70b9\u5934\uff0c\u8fd9\u4e24\u4eba\u4e0d\u4f46\u6b66\u529f \u5df2\u51fa\u795e\u5165\u5316\uff0c\u5916\u8868\u770b\u8d77\u3000\u6765\uff0c\u4e5f\u662f\u5947\u5f02\u8be1\u79d8\uff0c\u518d\u52a0\u4e0a\u4e00\u70b9\u513f\u51f6\u6076\u7684\u6837\u5b50\uff0c\u7136\u800c\u4e24\u4eba\u6b64\u523b\u7684\u795e \u6001\uff0c\u5374\u50cf\u4e2a\u5929\u771f\u3000\u7684\u5b69\u7ae5\u3002\u6bdb\u51b0\u5fae\u5fae\u4e00\u7b11\uff0c\u5979\u77e5\u9053\u8fd9\u4e24\u4eba\u5fc5\u5b9a\u662f\u548c\u4ec7\u72ec\u6709\u7740\u5173\u7cfb\u4e86\uff0c\u800c\u4e14\u5979 \u53ef\u4ee5\u786e\u5b9a\u3000\uff0c\u8fd9\u4e24\u4eba\u5fc5\u975e\u4e2d\u571f\u6b66\u6797\u4eba\u7269\uff0c\u4ed6\u4eec\u5230\u4e2d\u539f\u6765\uff0c\u540c\u65f6\u4e5f\u662f\u4e3a\u7740\u5bfb\u627e\u4ec7\u72ec\uff0c\u7136\u800c\u4ec7\u72ec \u5462\u3000\uff1f\u5979\u53c8\u4e0d\u7981\u4e00\u9635\u60d8\u7136\u3002\u82e5\u6362\u4e86\u5e73\u65e5\u5979\u5934\u8111\u6e05\u695a\u7684\u65f6\u5019\uff0c\u5979\u7acb\u523b\u53ef\u4ee5\u53d1\u73b0\u8fd9\u4e24\u4eba\u975e\u4f46\u3000\u4e0d \u4e86\u89e3\u5979\u6240\u8bf4\u7684\u8bdd\uff0c\u751a\u81f3\u8fde\u5979\u5199\u7684\u5b57\u4e5f\u4e0d\u592a\u8ba4\u5f97\uff0c\u8fd9\u4ece\u4e24\u4eba\u8fde\u7b80\u7b80\u5355\u5355\u7684\u4ec7\u72ec\u4e24\u5b57\u3000\uff0c\u90fd\u770b\u4e86 \u534a\u664c\u624d\u8ba4\u51fa\u6765\u7684\u4e8b\u4e0a\uff0c\u5c31\u53ef\u4ee5\u77e5\u9053\uff0c\u7136\u800c\u5979\u6b64\u523b\u5fc3\u601d\u500f\u4e71\uff0c\u6839\u672c\u6ca1\u6709\u6ce8\u610f\u3000\u5230\u8fd9\u4e9b\uff0c\u662f\u4ee5\u5979 \u671f\u671b\u7740\u8fd9\u4e24\u4e2a\u4eba\u80fd\u591f\u5199\u51e0\u4e2a\u5b57\uff0c\u6765\u89e3\u5f00\u4e00\u4e9b\u5979\u6240\u4e0d\u80fd\u4e86\u89e3\u7684\u4e8b\u3002\u90a3\u3000\u4e24\u4e2a\u602a\u5ba2\u6b22\u8dc3\u4e86\u4e00\u4f1a\uff0c \u53c8\u8e72\u4e86\u4e0b\u6765\uff0c\u671d\u6bdb\u51b0\u8fde\u8fde\u70b9\u5934\u5fae\u7b11\uff0c\u73b0\u51fa\u975e\u5e38\u4eb2\u70ed\u7684\u6837\u5b50\uff0c\u3000\u63a5\u7740\u53c8\u6ce8\u89c6\u6bdb\u51b0\u7684\u624b\uff0c\u50cf\u662f\u8981 \u5979\u518d\u5199\u4e0b\u53bb\uff0c\u800c\u6bdb\u51b0\u5374\u5728\u7b49\u7740\u4ed6\u4eec\u5199\uff0c\u8fd9\u6837\u4e09\u4eba\u8e72\u5728\u3000\u5730\u4e0a\uff0c\u9762\u9762\u76f8\u5bf9\uff0c\u5374\u4e0d\u77e5\u9053\u5bf9\u65b9\u7a76\u7adf \u60f3\u4e8e\u4ec0\u4e48\uff0c\u53ea\u6709\u77aa\u5927\u4e86\u773c\u775b\u671b\u7740\u3002\u6bdb\u51b0\u5f53\u7136\u4e0d\u3000\u77e5\u9053\u8fd9\u4e24\u4e2a\u602a\u4eba\u7684\u6765\u5386\uff0c\u751a\u81f3\u8fde\u82b8\u82b8\u4e2d\u539f\u6b66 \u6797\u4e2d\uff0c\u80fd\u77e5\u9053\u8fd9\u4e24\u4eba\u6765\u5386\u7684\u4e5f\u4e0d\u591a\uff0c\u867d\u3000\u7136\u5728\u770b\u4e86\u4ed6\u4eec\u6240\u65bd\u5c55\u7684\u62f3\u6cd5\u4e4b\u540e\uff0c\u6bcf\u4e2a\u4eba\u90fd\u4f1a\u77e5\u9053 \u4ed6\u4eec\u5fc5\u5b9a\u662f\u548c\u201c\u6d77\u5929\u5b64\u71d5\u201d\u6709\u7740\u3000\u5173\u7cfb\u3002\u4f46\u6d77\u5927\u5b64\u71d5\u672c\u8eab\u5c31\u662f\u4e2a\u8c1c\uff0c\u6839\u672c\u4e5f\u6ca1\u6709\u4eba\u77e5\u9053\u4ed6\u7684 \u6765\u5386\uff0c\u4e5f\u6ca1\u6709\u4eba\u77e5\u9053\u4ed6\u7684\u3000\u53bb\u5904\uff0c\u8fd9\u4f4d\u88ab\u6b66\u6797\u5c0a\u4e3a\u5343\u767e\u5e74\u6765\u7b2c\u4e00\u4eba\u7684\u5947\u4eba\uff0c\u5176\u6765\u5982\u795e\u9f99\uff0c\u5176 \u53bb\u4ea6\u5982\u795e\u9f99\uff0c\u8c01\u4f1a\u3000\u77e5\u9053\u4ed6\u975e\u4f46\u548c\u8fd9\u4e24\u4e2a\u602a\u5ba2\u6709\u7740\u5173\u7cfb\uff0c\u548c\u5f53\u4eca\u6b66\u6797\u7684\u5947\u4eba\u201c\u4ec7\u72ec\u201d\uff0c\u4e5f\u6709 \u7740\u5173\u8fde\u5462\uff1f\u3000\u4ec7\u72ec\u4e00\u751f\u4e8b\u8ff9\uff0c\u7eda\u4e3d\u591a\u5f69\uff0c\u5728\u4ed6\u77ed\u77ed\u7684\u4e09\u6570\u5341\u5e74\u6027\u547d\u4e2d\uff0c\u9664\u4e86\u4e00\u4e9b\u4eba\u4eec\u90fd\u77e5\u9053 \u7684\u4e8b\u3000\u4e4b\u5916\uff0c\u8fd8\u6709\u66f4\u591a\u4eba\u4eec\u4e0d\u77e5\u9053\u7684\u4e8b\u3002\u4ed6\u66fe\u7ecf\u8fdc\u8d74\u6d77\u5916\uff0c\u5728\u9ec4\u6d77\u7684\u4e00\u4e2a\u5b64\u5c9b\u4e0a\uff0c\u7adf\u8ba4\u8bc6\u3000 \u4e86\u8bb8\u591a\u4e45\u5df2\u88ab\u6b66\u6797\u4e2d\u8ba4\u4e3a\u6b7b\u53bb\u7684\u4eba\u7269\uff0c\u800c\u8fd9\u201c\u4eba\u4e2d\u4e4b\u9f99\u201d\u6d77\u5929\u5b64\u71d5\uff0c\u7adf\u4e5f\u662f\u5176\u4e2d\u4e4b\u3000\u4e00\u3002\u8fd9 \u8bb8\u591a\u4f4d\u6b66\u6797\u4e2d\u7684\u524d\u8f88\uff0c\u90fd\u662f\u5728\u81ea\u5df1\u9047\u7740\u4e86\u4ec0\u4e48\u4e0d\u53ef\u89e3\u7684\u56f0\u96be\uff0c\u6216\u8005\u662f\u81ea\u5df1\u4e5f\u3000\u538c\u5026\u4e86\u4eba\u751f\u7684 \u65f6\u5019\uff0c\u88ab\u201c\u6d77\u5929\u5b64\u71d5\u201d\u63a5\u5f15\u5230\u8fd9\u5c0f\u5c9b\u4e0a\uff0c\u8fc7\u7740\u6563\u4ed9\u822c\u7684\u751f\u6d3b\uff0c\u5f53\u4ec7\u72ec\u3000\u65e0\u610f\u95f4\u95ef\u4e0a\u8fd9\u5c0f\u5c9b \u65f6\uff0c\u7acb\u523b\u53d1\u89c9\u81ea\u5df1\u90a3\u4e00\u8eab\u5728\u4e2d\u539f\u6b66\u6797\u5df2\u662f\u9876\u513f\u5c16\u513f\u7684\u8eab\u624b\uff0c\u5728\u8fd9\u3000\u91cc\u7adf\u8fde\u51e0\u4e2a\u4e3a\u8fd9\u4e9b\u6b66\u6797\u524d \u8f88\u505a\u4e9b\u6742\u4e8b\u7684\u9ece\u4eba\u90fd\u4e0d\u5982\u3002\u4f5c\u4e3a\u4e00\u4e2a\u6b66\u6797\u4e2d\u4eba\uff0c\u9047\u7740\u4e86\u8fd9\u3000\u79cd\u5343\u8f7d\u96be\u9022\u7684\u673a\u7f18\uff0c\u5176\u5fc3\u4e2d\u7684\u559c \u60a6\uff0c\u662f\u53ef\u60f3\u800c\u77e5\u7684\uff0c\u4ec7\u72ec\u81ea\u5df1\u4e0d\u4f1a\u4f8b\u5916\uff0c\u4ed6\u6781\u613f\u610f\u3000\u7559\u5728\u8fd9\u5c0f\u5c9b\u4e0a\uff0c\u60f3\u5b66\u4e00\u4e9b\u4ed6\u867d\u7136\u4e45\u5df2\u542c \u8bf4\uff0c\u5374\u8fde\u89c1\u4e5f\u6ca1\u6709\u89c1\u8fc7\u7684\u6b66\u529f\u3002\u4f46\u662f\uff0c\u4f46\u662f\u3000\u5e74\u9f84\u6050\u6015\u5df1\u8d85\u8fc7\u767e\u5c81\uff0c\u800c\u7cbe\u795e\u5374\u6781\u77cd\u70c1\u7684\u201c\u6d77 \u5929\u5b64\u71d5\u201d\u5374\u5bf9\u4ed6\u8bf4\uff1a\u201c\u7559\u5728\u8fd9\u91cc\u7684\u4eba\u3000\u90fd\u53d1\u8a93\u518d\u4e0d\u79bb\u5c9b\u4e86\uff0c\u4f60\u80fd\u591f\u505a\u5230\u5417\uff1f\u201d \u3000\u3000\u3000\u4ec7\u72ec\u542c\u4e86\u65e0\u8a00\u5730\u6115\u4f4f\u4e86\uff0c\u90a3\u65f6\u4ed6\u624d\u4e8c\u5341\u591a\u5c81\uff0c\u6b63\u662f\u751f\u547d\u4e2d\u6700\u7f8e\u597d\u7684\u65f6\u5149\uff0c\u8ba9\u4ed6\u3000\u727a\u7272 \u5168\u90e8\u65f6\u65e5\u6765\u6362\u53d6\u6b66\u529f\uff0c\u90a3\u65f6\u4ed6\u786e\u7136\u89c9\u5f97\u5e76\u4e0d\u503c\u5f97\uff0c\u56e0\u4e3a\u4f60\u7eb5\u7136\u5b66\u6210\u4e86\u76d6\u4e16\u795e\u901a\u3000\uff0c\u7136\u800c\u5728\u8fd9 \u5b64\u5c9b\u4f60\u53c8\u80fd\u600e\u4e48\u5462\uff1f \u3000\u3000\u3000\u8fd9\u6b63\u5982\u6709\u4eba\u613f\u610f\u7ed9\u4f60\u5de8\u5927\u6570\u91cf\u7684\u8d22\u5bcc\uff0c\u800c\u53ea\u51c6\u4f60\u56f0\u5728\u4e00\u95f4\u623f\u5b50\u4e0d\u80fd\u51fa\u53bb\u534a\u6b65\uff0c\u3000\u800c\u4f60 \u4e5f\u7edd\u5bf9\u4e0d\u53ef\u80fd\u7b54\u5e94\u4ed6\u4e00\u6837\u3002 \u3000\u3000\u3000\u8fd9\u79cd\u5fc3\u7406\uff0c\u6d77\u5929\u5b64\u71d5\u5f53\u7136\u4f53\u4f1a\u5f97\u51fa\uff0c\u4e8e\u662f\u4ed6\u829c\u7136\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u522b\u4e0d\u597d\u610f\u601d\uff0c\u82e5\u3000\u6211\u5728 \u4f60\u8fd9\u4e2a\u5e74\u7eaa\uff0c\u4e5f\u4e0d\u80af\u8fd9\u4e48\u505a\u7684\u3002\u201d \u3000\u3000\u3000\u4eba\u7c7b\u4e4b\u95f4\u7684\u60c5\u611f\uff0c\u6700\u53ef\u8d35\u7684\u5c31\u662f\u5f7c\u6b64\u95f4\u7684\u540c\u60c5\u4e0e\u4e86\u89e3\uff0c\u4ec7\u72ec\u4e00\u751f\u6700\u4e0d\u670d\u4eba\uff0c\u7136\u3000\u800c\u6b64 \u523b\u5374\u5bf9\u8fd9\u6d77\u5916\u5947\u4eba\u751a\u4e3a\u503e\u5012\uff0c\u800c\u6d77\u5929\u5b64\u71d5\u4e5f\u5bf9\u8fd9\u6b66\u6797\u4e2d\u7684\u540e\u8d77\u4e4b\u79c0\u6781\u4e3a\u6b23\u8d4f\uff0c\u3000\u8fd9\u4e24\u4e2a\u5e74\u9f84 \u51e0\u4e4e\u5dee\u4e86\u4e00\u7532\u5b50\u7684\u4eba\uff0c\u7adf\u7ed3\u6210\u597d\u53cb\uff0c\u4ec7\u72ec\u5728\u90a3\u5b64\u5c9b\u4e0a\u4e5f\u7834\u4f8b\u5730\u803d\u4e86\u4e00\u4e2a\u3000\u6708\u3002 \u3000\u3000\u3000\u8fd9\u4e00\u4e2a\u6708\u5185\uff0c\u6d77\u5929\u5b64\u71d5\u867d\u7136\u7edd\u53e3\u4e0d\u8c08\u6b66\u529f\uff0c\u4f46\u5374\u5c06\u4e9b\u5185\u529f\u4e2d\u7684\u4e0d\u4f20\u4e4b\u79d8\uff0c\u6709\u610f\u3000\u65e0\u610f \u4ed6\u8bf4\u51fa\u6765\uff0c\u4ec7\u72ec\u662f\u4f55\u7b49\u806a\u660e\u4eba\uff0c\u81ea\u662f\u5f97\u76ca\u975e\u6d45\uff0c\u4ed6\u9707\u60ca\u6b66\u6797\u7684\u201c\u4e07\u6d41\u5f52\u5b97\u201d\u5fc3\u3000\u6cd5\uff0c\u4ea6\u56e0\u6b64 \u5f97\u6210\u3002 \u3000\u3000\u3000\u5728\u8fd9\u5b64\u5c9b\u4e0a\u7684\u4eba\uff0c\u6bcf\u4eba\u90fd\u5b58\u4e00\u4e2a\u6781\u5c0f\u7684\u76ae\u76d2\uff0c\u91cc\u8fb9\u662f\u4ec0\u4e48\uff0c\u8c01\u4e5f\u6ca1\u6253\u5f00\u6765\u8fc7\uff0c\u3000\u4ec7\u72ec \u4e34\u53bb\u4e4b\u9645\uff0c\u6d77\u5929\u5b64\u71d5\u4e5f\u5c06\u8fd9\u79cd\u76ae\u76d2\u62ff\u4e86\u4e00\u4e2a\u7ed9\u4ed6\uff0c\u5e76\u4e14\u8c06\u8c06\u53ee\u549b\uff0c\u8bf4\u8fd9\u76ae\u76d2\u4e5f\u3000\u8bb8\u4f1a\u7ed9\u4ed6\u5e2e \u52a9\u5f88\u5927\uff0c\u4f46\u662f\u4e0d\u5230\u5341\u5206\u5371\u6025\u65f6\uff0c\u5374\u5343\u4e07\u4e0d\u80fd\u6253\u5f00\u5b83\u3002 \u3000\u3000\u3000\u4ec7\u72ec\u8e0f\u4e0a\u90a3\u6765\u65f6\u4e58\u6765\u7684\u53cc\u6845\u5c0f\u8239\u65f6\uff0c\u6d77\u5929\u5b64\u71d5\u8bf4\uff1a\u201c\u5047\u5982\u4f60\u538c\u5026\u4e86\u6b66\u6797\u751f\u6daf\uff0c\u3000\u968f\u65f6 \u53ef\u5230\u8fd9\u91cc\u6765\u3002\u201d\u4ed6\u957f\u53f9\u4e86\u53e3\u6c14\u53c8\u9053\uff0c\u201c\u6211\u65e0\u8bba\u5728\u4e0d\u5728\uff0c\u8fd9\u91cc\u603b\u662f\u6b22\u8fce\u4f60\u6765\u7684\u3002\u3000\u201d \u3000\u3000\u3000\u8a00\u4e0b\u5927\u6709\u81ea\u77e5\u6b7b\u671f\u5df2\u8fd1\u4e4b\u610f\uff0c\u5206\u79bb\u5728\u5373\uff0c\u518d\u89c1\u65e0\u671f\uff0c\u4ec7\u72ec\u987f\u89c9\u60dc\u522b\u4e4b\u60c5\uff0c\u6cb9\u7136\u3000\u800c \u751f\u3002 \u3000\u3000\u3000\u6d77\u5357\u5c9b\u4e0a\u7684\u4e94\u6307\u5c71\uff0c\u4e5f\u662f\u5251\u5ba2\u51fa\u6ca1\u7684\u5730\u65b9\u4e4b\u4e00\uff0c\u201c\u6d77\u5357\u5251\u6d3e\u201d\u4ee5\u8f9b\u8fa3\u8be1\u5f02\u4e3a\u4e3b\u3000\uff0c\u867d \u7136\u4e0e\u4e2d\u539f\u6b66\u6797\u6240\u6d41\u4f20\u7684\u5251\u6cd5\u4e0d\u540c\uff0c\u4f46\u81ea\u53e4\u4ee5\u6765\uff0c\u5251\u6cd5\u7684\u6e90\u6d41\uff0c\u672c\u662f\u4e00\u7edf\uff0c\u53ea\u662f\u3000\u6bcf\u6d3e\u6240\u8d70\u7684 \u5251\u8def\u5404\u5f02\u800c\u5df2\u3002 \u3000\u3000\u3000\u8fd9\u8eab\u7a7f\u7d2b\u94dc\u3001\u9ec4\u91d1\u8863\u886b\u7684\u4e24\u4e2a\u602a\u5ba2\uff0c\u672c\u662f\u6d77\u5357\u5251\u6d3e\u7684\u9ad8\u624b\uff0c\u8db3\u8ff9\u867d\u672a\u51fa\u5357\u6d77\uff0c\u3000\u4f46\u5251 \u6cd5\u4ea6\u81ea\u4e0d\u51e1\uff0c\u4ed6\u4e24\u4eba\u751f\u6027\u5947\u7279\uff0c\u6614\u5e74\u5728\u6d77\u5357\u5c9b\u4e0a\uff0c\u884c\u4e8b\u5c31\u4ee5\u504f\u6fc0\u8457\u540d\uff0c\u54ea\u77e5\u7a81\u3000\u7136\u8fd9\u4e24\u4eba\u7adf \u4e00\u9f50\u5931\u8e2a\uff0c\u6d77\u5357\u5c9b\u4e0a\u7684\u6c5f\u6e56\u4eba\u58eb\uff0c\u5404\u5404\u79f0\u5f02\uff0c\u56e0\u4e3a\u8fd9\u4e24\u4eba\u7edd\u4e0d\u662f\u4f1a\u5f52\u9690\u3000\u6797\u4e0b\u7684\u4eba\uff0c\u800c\u4e2d\u539f \u6b66\u6797\uff0c\u4e5f\u672a\u4f20\u51fa\u6709\u8fd9\u4e24\u4eba\u7684\u884c\u8e2a\u3002 \u3000\u3000\u3000\u54ea\u77e5\u8fd9\u4e24\u4eba\u5374\u662f\u88ab\u6d77\u5929\u5b64\u71d5\u5f15\u5230\u90a3\u5b64\u5c9b\u4e0a\uff0c\u6f5c\u4e60\u6b66\u5b66\uff0c\u56e0\u4e3a\u751f\u6027\u4e5f\u662f\u6781\u4e3a\u5947\u7279\u3000\u7684\u6d77 \u5929\u5b64\u71d5\uff0c\u5bf9\u8fd9\u4e24\u4eba\u7adf\u6781\u4e3a\u9752\u7750\u3002 \u3000\u3000\u3000\u4ec7\u72ec\u6614\u5e74\u5b64\u8eab\u95ef\u4e0a\u90a3\u5b64\u5c9b\u65f6\uff0c\u4e0e\u8fd9\u4e24\u4eba\u9887\u4e3a\u76f8\u6295\uff0c\u4eba\u7c7b\u7684\u7f18\u4efd\uff0c\u603b\u662f\u90a3\u4e48\u5947\u602a\u3000\uff0c\u4ec7 \u72ec\u4e0e\u8fd9\u4e24\u4eba\uff0c\u5e73\u65e5\u90fd\u662f\u843d\u843d\u5be1\u5408\u7684\u505a\u5cb8\u4e4b\u58eb\uff0c\u5374\u4e0d\u77e5\u600e\u5730\uff0c\u7ed3\u4ea4\u4e86\u5bf9\u65b9\u8fd9\u548c\u81ea\u3000\u5bb6\u5b8c\u5168\u4e0d\u540c \u5178\u578b\u7684\u4eba\u7269\u3002 \u3000\u3000\u3000\u8fd9\u4e24\u4eba\u672c\u662f\u4e2d\u8868\u5144\u5f1f\uff0c\u80d6\u7684\u53eb\u7a0b\u9a79\uff0c\u7626\u7684\u53eb\u6f58\u91d1\uff0c\u5728\u90a3\u5b64\u5c9b\u4e0a\u4e00\u803d\u5341\u5e74\uff0c\u7adf\u518d\u3000\u4e5f\u5fcd \u4e0d\u5f97\u5b64\u5c9b\u4e0a\u5bc2\u5bde\u7684\u5c81\u6708\uff0c\u5077\u5077\u6e9c\u4e86\u51fa\u6765\uff0c\u8fd9\u4e00\u65b9\u9762\u56fa\u7136\u662f\u56e0\u4e3a\u4ed6\u4eec\u751f\u6027\u672c\u5c31\u4e0d\u3000\u7518\u5bc2\u5bde\uff0c\u53e6 \u4e00\u65b9\u9762\u4e5f\u56e0\u4e3a\u4ed6\u4eec\u5e74\u7eaa\u8fd8\u6ca1\u6709\u5230\u8fbe\u5c06\u4e00\u5207\u90fd\u80fd\u6de1\u7136\u89c6\u4e4b\u7684\u9636\u6bb5\uff0c\u5c24\u5176\u662f\u3000\u4ec7\u72ec\u53e3\u91cc\u7684\u4e2d\u539f\u6b66 \u6797\uff0c\u6c5f\u5357\u98ce\u7269\uff0c\u66f4\u4f7f\u4ed6\u4eec\u5fc3\u5411\u5f80\u4e4b\uff0c\u795e\u601d\u4e0d\u5df2\u3002 \u3000\u3000\u3000\u4ed6\u4eec\u60f3\u5230\u5c31\u505a\uff0c\u5c45\u7136\u8fde\u8882\u6765\u5230\u6c5f\u5357\uff0c\u4ed6\u4eec\u8db3\u8ff9\u4ece\u672a\u6765\u81f3\u4e2d\u571f\uff0c\u4e00\u5207\u90fd\u751f\u758f\u5f97\u5f88\u3000\uff0c\u5c24 \u5176\u662f\u4ed6\u4eec\u8fd9\u79cd\u8be1\u5f02\u88c5\u675f\uff0c\u66f4\u5904\u5904\u5f15\u8d77\u4e0d\u4fbf\uff0c\u4e8e\u662f\u81ea\u7136\u60f3\u5728\u8fd9\u91cc\u627e\u4e2a\u670b\u53cb\uff0c\u800c\u4ed6\u3000\u4eec\u5728\u4e2d\u539f\u6b66 \u6797\u4e2d\u552f\u4e00\u7684\u670b\u53cb\uff0c\u5c31\u662f\u4ec7\u72ec\u4e86\u3002 \u3000\u3000\u3000\u662f\u4ee5\uff0c\u4ed6\u4eec\u770b\u5230\u6bdb\u51b0\u9888\u4e0a\u6240\u6302\u7684\u90a3\u4e2a\u5c0f\u76ae\u76d2\u5b50\uff0c\u4e0d\u7981\u72c2\u559c\uff0c\u56e0\u4e3a\u4ed6\u4eec\u591a\u65e5\u6765\u6253\u3000\u542c\u4ec7 \u72ec\u7684\u884c\u8ff9\uff0c\u6beb\u65e0\u7ed3\u679c\uff0c\u8fd9\u81ea\u7136\u662f\u56e0\u4e3a\u4ed6\u4eec\u672c\u8eab\u884c\u8e2a\u8be1\u5f02\uff0c\u800c\u6240\u6253\u542c\u7684\u5bf9\u8c61\u53c8\u662f\u3000\u4ec7\u72ec\uff0c\u4eba\u5bb6 \u5f53\u7136\u4e0d\u613f\u610f\u544a\u8bc9\u4ed6\u4eec\u771f\u8c61\u3002 \u3000\u3000\u3000\u53ea\u662f\u4ed6\u4eec\u90a3\u79cd\u5357\u7ca4\u65b9\u8a00\uff0c\u751f\u957f\u5728\u6c5f\u5357\u6df1\u95fa\u91cc\u7684\u6bdb\u51b0\u600e\u4f1a\u542c\u5f97\u61c2\uff1f\u8a00\u8bed\u4e0d\u901a\uff0c\u81ea\u3000\u7136\u96be \u514d\u5f15\u8d77\u8bef\u4f1a\uff0c\u5c31\u8fde\u4ed6\u4eec\u4ee5\u7edd\u9876\u5185\u529b\u4e3a\u56e0\u60ca\u60b8\u800c\u6655\u53a5\u7684\u6bdb\u51b0\u63a8\u62ff\u65f6\uff0c\u4e5f\u88ab\u6bdb\u51b0\u8ba4\u3000\u4e3a\u4ed6\u4eec\u5728\u6545 \u610f\u8f7b\u8584\u3002 \u3000\u3000\u3000\u4ed6\u4eec\u4e24\u4eba\uff0c\u8d39\u4e86\u5f88\u4e45\u7684\u4e8b\uff0c\u624d\u4f7f\u6bdb\u51b0\u7565\u4e3a\u4e86\u89e3\u4e86\u4e00\u4e9b\u4ed6\u4eec\u548c\u4ec7\u72ec\u4e4b\u95f4\u7684\u5173\u7cfb\uff0c\u3000\u6bdb\u51b0 \u5374\u51c4\u51c9\u5730\u5728\u5730\u4e0a\u5199\u6210\u7684\u4ec7\u72ec\u4e24\u5b57\u4e0b\u9762\uff0c\u52a0\u4e0a\u201c\u6b7b\u4e86\u201d\u4e24\u5b57\uff0c\u7a0b\u9a79\u3001\u6f58\u91d1\u7684\u773c\u775b\u3000\uff0c\u5728\u770b\u5230\u8fd9 \u4e24\u4e2a\u5b57\u4ee5\u540e\uff0c\u7a81\u7136\u5c04\u51fa\u4e00\u80a1\u9a87\u4eba\u7684\u5149\u8292\uff0c\u5404\u5404\u72c2\u543c\u4e86\u4e00\u58f0\uff0c\u7eb5\u4e0a\u524d\u53bb\uff0c\u3000\u6349\u4f4f\u6bdb\u51b0\u7684\u81c2\u8180\uff0c \u5589\u95f4\u53d1\u51fa\u4e00\u8fde\u4e32\u6025\u5207\u7684\u95f4\u8bdd\u3002 \u3000\u3000\u3000\u6bdb\u51b0\u7684\u4e24\u53ea\u81c2\u8180\u88ab\u6293\u5f97\u5176\u75db\u5f7b\u9aa8\uff0c\u773c\u776b\u6bdb\u4e0a\u7adf\u6709\u6cea\u73e0\u6d41\u4e0b\uff0c\u4f46\u5979\u7684\u6cea\u73e0\u5374\u4e0d\u662f\u3000\u56e0\u4e3a \u75db\u82e6\u800c\u6d41\u4e0b\u7684\uff0c\u800c\u662f\u56e0\u7740\u5feb\u4e50\u3002 \u3000\u3000\u3000\u8fd9\u662f\u56e0\u4e3a\u4ed6\u4eec\u4e24\u4eba\u771f\u60c5\u7684\u6d41\u9732\u3002\u4ece\u5f00\u59cb\u5230\u73b0\u5728\uff0c\u6ca1\u6709\u4efb\u4f55\u4e00\u4e2a\u4eba\u4f1a\u4e3a\u4ec7\u72ec\u7684\u6b7b\u3000\u800c\u6709 \u4efb\u4f55\u60b2\u54c0\u7684\u8868\u60c5\uff0c\u5373\u4f7f\u5979\u81ea\u5df1\uff0c\u5728\u601d\u5ff5\u7740\u4ec7\u72ec\u65f6\uff0c\u4e5f\u53ea\u662f\u6697\u5730\u6d41\u7740\u773c\u6cea\uff0c\u5c06\u771f\u3000\u5b9e\u7684\u60c5\u611f\u9690 \u85cf\u8d77\u6765\uff0c\u90a3\u786e\u662f\u4eba\u751f\u6700\u75db\u82e6\u7684\u4e8b\uff0c\u4f46\u662f\u5979\u5374\u4e0d\u5f97\u4e0d\u5982\u6b64\uff0c\u56e0\u7740\u6240\u80fd\u63a5\u89e6\u3000\u5230\u7684\u4eba\uff0c\u90fd\u662f\u4ec7\u72ec \u7684\u654c\u4eba\u800c\u975e\u670b\u53cb\u3002 \u3000\u3000\u3000\u4f46\u6b64\u523b\uff0c\u5979\u5374\u770b\u5230\u4ec7\u72ec\u7684\u771f\u6b63\u670b\u53cb\u4e86\uff0c\u5979\u6fc0\u52a8\u5f97\u6d41\u4e0b\u5feb\u4e50\u7684\u6cea\u73e0\uff0c\u5f53\u5979\u77e5\u9053\u4ec7\u3000\u72ec\u4e5f \u6709\u670b\u53cb\u7684\u65f6\u5019\uff0c\u90a3\u8fdc\u6bd4\u5979\u53d1\u73b0\u81ea\u5df1\u7684\u670b\u53cb\u8fd8\u8981\u6109\u5feb\u3002 \u3000\u3000\u3000\u7a0b\u9a79\u3001\u6f58\u91d1\u6ee1\u8138\u4ff1\u662f\u60f6\u6025\u7684\u795e\u8272\uff0c\u4ed6\u4eec\u7740\u6025\u5730\u95ee\u7740\uff1a\u201c\u4ec7\u72ec\u662f\u600e\u4e48\u6b7b\u7684\uff1f\u662f\u88ab\u3000\u4eba\u6240 \u6740\u5417\uff1f\u4ed6\u7684\u4ec7\u4eba\u662f\u8c01\uff1f\u201d\u6bdb\u51b0\u5374\u4e00\u53e5\u542c\u4e0d\u61c2\uff0c\u5c31\u7b97\u542c\u61c2\u4e86\uff0c\u5979\u53c8\u600e\u80fd\u5c06\u4ec7\u72ec\u7684\u3000\u4ec7\u5bb6\u8bf4\u51fa \u6765\uff0c\u56e0\u4e3a\u90a3\u662f\u5979\u5ae1\u4eb2\u7684\u54e5\u54e5\u5440\u3002 \u3000\u3000\u3000\u7a0b\u9a79\u3001\u6f58\u91d1\u867d\u7136\u6027\u60c5\u602a\u5f02\uff0c\u4f46\u5374\u90fd\u662f\u6027\u60c5\u4e2d\u4eba\uff0c\u6b64\u523b\u5fc3\u91cc\u8d8a\u6025\uff0c\u5374\u4e5f\u8d8a\u4e0d\u80fd\u5c06\u3000\u5fc3\u4e2d \u7684\u610f\u601d\u8868\u8fbe\u51fa\u6765\uff0c\u4e24\u4eba\u6025\u5f97\u6349\u7740\u6bdb\u51b0\u7684\u81c2\u8180\u76f4\u6643\uff0c\u7a81\u5730\uff0c\u5251\u5149\u4e00\u95ea\uff0c\u76f4\u524a\u7a0b\u9a79\u3000\u8033\u7554\u7684\u201c\u7384 \u73e0\u201d\u7a74\u3002 \u3000\u3000\u3000\u4e24\u4eba\u5fc3\u4e2d\u5168\u5728\u60f3\u7740\u4ec7\u72ec\u4e4b\u4e8b\uff0c\u5bf9\u8fd9\u5251\u5149\u7684\u6765\u8def\u5b8c\u5168\u6ca1\u6ce8\u610f\u5230\uff0c\u518d\u52a0\u4e0a\u8fd9\u5251\u5149\u6765\u3000\u52bf\u6781 \u901f\uff0c\u6309\u8bf4\u4ed6\u4eec\u4f3c\u5df2\u7edd\u65e0\u53ef\u80fd\u8eb2\u5f00\u6b64\u62db\u3002 \u3000\u3000\u3000\u5251\u6c14\u5bd2\u8292\uff0c\u773c\u770b\u5df2\u626b\u7740\u7a0b\u9a79\u7684\u53f3\u8033\uff0c\u5c31\u5728\u8fd9\u95f4\u4e0d\u5bb9\u53d1\u7684\u4e00\u5239\u90a3\u91cc\uff0c\u7a0b\u9a79\u80a5\u80d6\u7684\u3000\u9888\u5b50 \u500f\u7136\u5411\u5de6\u4e00\u626d\uff0c\u5251\u5149\u70b9\u95ea\u800c\u8fc7\uff0c\u4f7f\u5251\u7684\u4eba\u4e00\u58f0\u5389\u53f1\uff0c\u9a82\u9053\uff1a\u201c\u6b3a\u51cc\u5f31\u5973\uff0c\u7b97\u4ec0\u3000\u4e48\u4eba\u7269\uff1f\u59d3 \u77f3\u7684\u4eca\u5929\u548c\u4f60\u62fc\u4e86\uff01\u201d \u3000\u3000\u3000\u5251\u5c16\u5fae\u4e00\u98a4\u6296\uff0c\u5251\u5149\u9519\u843d\uff0c\u5168\u5411\u7a0b\u9a79\u7684\u5934\u4e0a\u62db\u547c\u3002 \u3000\u3000\u3000\u7a0b\u9a79\u4e0d\u60f3\u4f24\u4eba\uff0c\u5148\u6c42\u81ea\u4fdd\uff0c\u53cd\u81c2\u4e00\u6307\uff0c\u201c\u545b\u7136\u201d\u4e00\u58f0\u957f\u541f\uff0c\u7adf\u5c06\u90a3\u5251\u5f39\u5f00\u4e94\u5bf8\u3000\uff0c\u4f46 \u4f7f\u5251\u7684\u4eba\u4e1d\u6beb\u4e0d\u4e3a\u8fd9\u79cd\u60ca\u4eba\u7684\u6b66\u529f\u6240\u60e7\uff0c\u5251\u5f0f\u4e00\u3001\u5708\uff0c\u201c\u5530\u3001\u5530\u201d\u53c8\u662f\u4e24\u5251\uff0c\u3000\u8f7b\u7075\u5de7\u5feb\uff0c \u6b63\u662f\u540d\u91cd\u6b66\u6797\u7684\u201c\u4e03\u5341\u4e8c\u8def\u8fde\u73af\u5251\u201d\u3002 \u3000\u3000\u3000\u6bdb\u51b0\u770b\u5230\u77f3\u78f7\u8fd0\u5251\u5982\u98ce\uff0c\u518d\u542c\u5230\u77f3\u78f7\u6240\u9a82\u7684\u8bdd\uff0c\u77e5\u9053\u4ed6\u5fc5\u5b9a\u5bf9\u8fd9\u4e24\u4e2a\u6d77\u5916\u6765\u5ba2\u3000\u6709\u4e86 \u8bef\u4f1a\uff0c\u5a07\u559d\u9053\uff1a\u201c\u77f3\u78f7\uff0c\u5feb\u522b\u52a8\u624b\uff01\u201d \u3000\u3000\u3000\u77f3\u78f7\u4e00\u695e\uff0c\u638c\u4e2d\u5251\u53c8\u88ab\u4eba\u5bb6\u5f39\u4e86\u4e00\u4e0b\uff0c\u4f46\u6b66\u5f53\u5251\u6cd5\uff0c\u5251\u5f0f\u8fde\u7ef5\uff0c\u5251\u8def\u5e76\u6ca1\u6709\u56e0\u3000\u4e3a\u8fd9 \u4e00\u5f39\u4e4b\u529b\u800c\u6709\u6240\u963b\u6ede\uff0c\u53ea\u662f\u4ed6\u542c\u4e86\u6bdb\u51b0\u7684\u8bdd\uff0c\u5374\u4e0d\u5f97\u4e0d\u786c\u751f\u751f\u5730\u5c06\u53d1\u51fa\u7684\u4e00\u62db\u3000\u201c\u6c5f\u6cb3\u65e5 \u4e0b\u201d\u64a4\u4e86\u56de\u6765\u3002 \u3000\u3000\u3000\u4ed6\u4ee5\u5403\u60ca\u7684\u76ee\u5149\uff0c\u8be2\u95ee\u6bdb\u51b0\uff0c\u6bdb\u51b0\u9053\uff1a\u201c\u4ed6\u4eec\u90fd\u662f\u81ea\u5df1\u4eba\u4e00\u4e00\u201d\u5979\u7684\u8138\uff0c\u7565\u4e3a\u3000\u7ea2\u4e86 \u4e00\u4e0b\uff0c\u4fee\u6b63\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u5bf9\u6211\u5e76\u6ca1\u6709\u6076\u610f\u3002\u201d \u3000\u3000\u3000\u77f3\u78f7\u66f4\u5947\u602a\u9053\uff1a\u201c\u8fd9\u4e2a\u6837\u5b50\u8fd8\u8bf4\u662f\u6ca1\u6709\u6076\u610f\uff1f\u201d \u3000\u3000\u3000\u77f3\u78f7\u65b9\u624d\u867d\u7136\u88ab\u70b9\u4e2d\u4e86\u7a74\u9053\uff0c\u4f46\u4eba\u5bb6\u5bf9\u4ed6\u53ef\u5e76\u6ca1\u6709\u6076\u610f\uff0c\u662f\u4ee5\u4e0b\u624b\u5e76\u4e0d\u91cd\uff0c\u7528\u3000\u7684\u4e5f \u4e0d\u662f\u72ec\u95e8\u624b\u6cd5\uff0c\u77f3\u78f7\u81ea\u5df1\u8fd0\u6c14\u884c\u529f\uff0c\u7adf\u4ee5\u6b66\u5f53\u6b63\u5b97\u7684\u5185\u529f\u89e3\u5f00\u4e86\u7a74\u9053\uff0c\u4ed6\u548c\u6bdb\u3000\u51b0\u672c\u662f\u51e0\u65f6 \u9752\u6d77\u7af9\u9a6c\u7684\u670b\u53cb\uff0c\u81ea\u662f\u6781\u4e3a\u5173\u5fc3\u6bdb\u51b0\u7684\u5b89\u5371\uff0c\u6361\u8d77\u65b9\u624d\u88ab\u4eba\u5bb6\u51fb\u843d\u7684\u957f\u3000\u5251\uff0c\u53c8\u8d76\u4e86\u56de\u6765\uff0c \u5374\u770b\u5230\u6bdb\u51b0\u6cea\u6d41\u6ee1\u9762\uff0c\u90a3\u4e24\u4e2a\u4eba\u624b\u63e1\u7740\u5979\u7684\u80a9\u8180\u3002 \u3000\u3000\u3000\u8fd9\u666f\u8c61\u4e00\u843d\u77f3\u78f7\u4e4b\u76ee\uff0c\u4ed6\u7adf\u4e0d\u518d\u987e\u5fcc\u4eba\u5bb6\u7684\u201c\u5316\u9aa8\u795e\u62f3\u201d\uff0c\u62fc\u547d\u6251\u4e86\u4e0a\u6765\uff0c\u53ea\u3000\u662f\u81ea \u5df1\u6b66\u529f\u548c\u4eba\u5bb6\u5dee\u5f97\u5927\u8fdc\uff0c\u867d\u7136\u62fc\u547d\uff0c\u4e5f\u6ca1\u6709\u7528\u3002 \u3000\u3000\u3000\u6bdb\u51b0\u559d\u6b62\u4e86\u4ed6\uff0c\u4ed6\u5374\u89c9\u5f97\u8be7\u5f02\uff0c\u4f4e\u4e0b\u5934\uff0c\u773c\u89d2\u52a8\u5904\uff0c\u5ffd\u7136\u770b\u5230\u4ed6\u4eec\u65b9\u624d\u5728\u5730\u4e0a\u3000\u6240\u5199 \u7684\u201c\u4ec7\u72ec\u201d\u4e24\u5b57\uff0c\u5fc3\u91cc\u4e00\u9178\uff0c\u957f\u5251\u65e0\u529b\u5730\u5782\u843d\u5230\u5730\u4e0a\u3002 \u3000\u3000\u3000\u4ed6\u5bf9\u6bdb\u51b0\u60c5\u6839\u6df1\u79cd\uff0c\u540e\u6765\u6bdb\u51b0\u4e0d\u60dc\u727a\u7272\u81ea\u5df1\u6765\u5e2e\u52a9\u5979\u54e5\u54e5\u7684\u65f6\u5019\uff0c\u4ed6\u6070\u5de7\u4e0d\u5728\u3000\u6c5f \u5357\uff0c\u7b49\u5230\u56de\u6765\u65f6\uff0c\u6bdb\u51b0\u5bb9\u8c8c\u867d\u4f9d\u65e7\uff0c\u53ef\u662f\u5fc3\u5883\u5374\u5927\u4e0d\u76f8\u540c\u4e86\u3002 \u3000\u3000\u3000\u77f3\u78f7\u77e5\u9053\u4ec7\u72ec\u548c\u6bdb\u51b0\u4e4b\u95f4\u7684\u5173\u7cfb\uff0c\u6b64\u523b\u518d\u5728\u5730\u4e0a\u770b\u5230\u4ec7\u72ec\u4e24\u5b57\uff0c\u604d\u7136\u800c\u609f\uff0c\u96be\u3000\u53d7\u5730 \u6697\u5fd6\u9053\uff1a\u201c\u96be\u602a\u5979\u8bf4\u662f\u81ea\u5df1\u4eba\uff01\u201d\u8d8a\u53d1\u9178\u6e9c\u6e9c\u5730\uff0c\u4e00\u53e3\u6c14\u50cf\u662f\u618b\u5728\u5589\u5499\u91cc\uff0c\u5410\u3000\u4e0d\u51fa\u6765\u3002 \u201c\u90a3\u5012\u602a\u6211\u591a\u4e8b\u4e86\u3002\u201d\u4ed6\u7565\u6709\u4e9b\u6c14\u6124\u4ed6\u8bf4\u9053\uff0c\u6bdb\u51b0\u4e5f\u96be\u53d7\uff0c\u89c9\u5f97\u5bf9\u4ed6\u6709\u4e9b\u3000\u6b49\u610f\u3002 \u3000\u3000\u3000\u7a0b\u9a79\u3001\u6f58\u91d1\u72e0\u72e0\u77aa\u4e86\u77f3\u78f7\u51e0\u773c\uff0c\u4ed6\u4eec\u670b\u53cb\u867d\u5c11\uff0c\u4f46\u5bf9\u670b\u53cb\u5374\u6781\u4e3a\u70ed\u8bda\uff0c\u4ed6\u4eec\u77e5\u3000\u9053\u6bdb \u51b0\u5fc5\u5b9a\u548c\u4ec7\u72ec\u6709\u6781\u6df1\u7684\u5173\u7cfb\uff0c\u4e5f\u731c\u51fa\u6bdb\u51b0\u8179\u4e2d\u7684\u5fc5\u5b9a\u662f\u4ec7\u72ec\u7684\u5b69\u5b50\uff0c\u6b64\u523b\u770b\u5230\u3000\u77f3\u78f7\u548c\u5979\u56db \u76ee\u76f8\u5bf9\u7684\u8868\u60c5\uff0c\u5fc3\u91cc\u5927\u5927\u5730\u4e0b\u8212\u670d\uff0c\u4e24\u4eba\u4f4e\u4f4e\u8bf4\u4e86\u51e0\u53e5\u8bdd\uff0c\u6bdb\u51b0\u548c\u77f3\u78f7\u3000\u4e5f\u542c\u4e0d\u61c2\u3002 \u3000\u3000\u3000\u4ed6\u4eec\u8eab\u5f62\u84e6\u5730\u4e00\u52a8\uff0c\u8eab\u4e0a\u7684\u94dc\u7247\uff0c\u54cd\u4e5f\u672a\u54cd\uff0c\u4eba\u5f71\u4e00\u6643\uff0c\u5c31\u63a0\u4e86\u51fa\u53bb\uff0c\u6bdb\u51b0\u53c8\u3000\u662f\u5947 \u602a\uff0c\u76ee\u5149\u65b9\u624d\u56de\u5230\u77f3\u78f7\u8eab\u4e0a\uff0c\u773c\u524d\u53c8\u7a81\u5730\u4e00\u82b1\uff0c\u4ed6\u4e24\u4eba\u53c8\u63a0\u4e86\u8fdb\u6765\uff0c\u4e00\u4eba\u624b\u4e2d\u3000\u62ff\u7740\u4e24\u53ea\u9a6c \u817f\uff0c\u7adf\u5c06\u9a6c\u4e3e\u4e86\u8d77\u6765\uff0c\u5979\u5fc3\u4e2d\u4e00\u52a8\uff0c\u604d\u7136\u77e5\u9053\u4e86\u65b9\u624d\u5979\u6240\u7ecf\u5386\u90a3\u79cd\u9a6c\u8eab\u3000\u672a\u52a8\uff0c\u800c\u81ea\u5df1\u5374\u50cf \u817e\u4e91\u9a7e\u96fe\u7684\u611f\u89c9\u7684\u7531\u6765\u3002 \u3000\u3000\u3000\u77f3\u78f7\u4e00\u76f4\u671b\u7740\u6bdb\u51b0\uff0c\u4f46\u6b64\u523b\u76ee\u5149\u5374\u4e5f\u4e0d\u514d\u88ab\u4ed6\u4eec\u6240\u5438\u5f15\uff0c\u60ca\u5f02\u4e8e\u4ed6\u4eec\u6b66\u529f\u4e4b\u6df1\u3000\u548c\u884c \u4e8b\u4e4b\u5f02\uff0c\u4ed6\u51fa\u9053\u867d\u7136\u5e76\u4e0d\u592a\u4e45\uff0c\u4f46\u5374\u81ea\u5e7c\u88ab\u6b66\u6797\u540d\u5bb6\u6240\u85b0\u9676\uff0c\u6b66\u6797\u4e2d\u7684\u4e8b\uff0c\u4ed6\u3000\u4e5f\u542c\u5230\u7684\u6781 \u591a\uff0c\u4f46\u6b64\u523b\u4ed6\u5374\u518d\u4e5f\u60f3\u4e0d\u51fa\u8fd9\u4e24\u4eba\u662f\u4ec0\u4e48\u6765\u8def\u3002 \u3000\u3000\u3000\u7a0b\u9a79\u3001\u6f58\u4f65\u5c06\u9a6c\u4e3e\u5230\u6bdb\u51b0\u8ddf\u524d\uff0c\u653e\u4e0b\u4e86\uff0c\u671d\u6bdb\u51b0\u4e00\u7b11\uff0c\u53cc\u624b\u5982\u7535\uff0c\u500f\u7136\u7a7f\u5165\u6bdb\u3000\u51b0\u808b \u4e0b\uff0c\u6781\u5feb\u5730\u5c06\u6bdb\u51b0\u653e\u5230\u9a6c\u978d\uff0c\u77f3\u78f7\u53c8\u4e00\u60ca\uff0c\u53f1\u9053\uff1a\u201c\u5e72\u4ec0\u4e48\uff1f\u201d\u8bed\u58f0\u672a\u4e86\uff0c\u4ed6\u3000\u4e24\u4eba\u5df2\u5c06\u6bdb \u51b0\u8fde\u4eba\u5e26\u9a6c\u4e3e\u4e86\u8d77\u6765\uff0c\u8eab\u5f62\u52a8\u5904\uff0c\u6643\u773c\u4fbf\u6d88\u5931\u4e86\u3002 \u3000\u3000\u3000\u77f3\u78f7\u695e\u4e86\u8bb8\u4e45\uff0c\u4ed6\u77e5\u9053\u51ed\u81ea\u5df1\u5fc5\u5b9a\u8feb\u4e0d\u4e0a\u4eba\u5bb6\uff0c\u6b64\u523b\u4ed6\u4e5f\u77e5\u9053\u4e86\u8fd9\u4e24\u4eba\u4e3e\u6b62\u867d\u3000\u7136\u6781 \u7aef\u8be1\u5f02\uff0c\u4f46\u5374\u4e95\u6ca1\u6709\u4ec0\u4e48\u6076\u610f\uff0c\u4f46\u8fd9\u4e24\u4eba\u5374\u4e3a\u4ec0\u4e48\u5c06\u6bdb\u51b0\u63b3\u4e86\u53bb\u5462\uff1f\u63b3\u5230\u54ea\u91cc\u3000\u53bb\u4e86\u5462\uff1f\u6bdb \u51b0\u4f53\u8d28\u672c\u5f31\uff0c\u52a0\u4ee5\u8eab\u6000\u516d\u7532\uff0c\u4f1a\u4e0d\u4f1a\u56e0\u6b64\u800c\u53d7\u5230\u4f24\u5bb3\u5462\uff1f \u3000\u3000\u3000\u4ed6\u6697\u4e2d\u54ac\u7259\uff0c\u5fd6\u9053\uff1a\u201c\u65e0\u8bba\u5982\u4f55\uff0c\u6211\u4e5f\u8981\u5c06\u5979\u7684\u4e0b\u843d\u67e5\u660e\uff0c\u4e5f\u8bb8\u6211\u662f\u591a\u7ba1\u95f2\u4e8b\u3000\uff0c\u4f46 \u6211\u5982\u4e0d\u8fd9\u6837\u505a\uff0c\u6211\u7684\u5fc3\u5c06\u6c38\u8fdc\u4e5f\u65e0\u6cd5\u5b89\u5b81\u4e86\u3002\u201d \u3000\u3000\u3000\u4ed6\u867d\u7136\u6781\u5e7c\u65f6\u5c31\u5165\u4e86\u6b66\u5f53\u5c71\uff0c\u548c\u90a3\u4e9b\u6e05\u5fc3\u5be1\u6b32\u7684\u9053\u58eb\u76f8\u5904\uff0c\u4f46\u5929\u6027\u591a\u60c5\uff0c\u6709\u5173\u3000\u60c5\u611f \u4e0a\u7684\u4e8b\uff0c\u4ed6\u603b\u662f\u653e\u4e0d\u4e0b\u3002 \u3000\u3000\u3000\u4e8e\u662f\u4ed6\u632f\u4f5c\u4e86\u7cbe\u795e\uff0c\u5c06\u5012\u63d0\u7740\u7684\u957f\u5251\uff0c\u653e\u56de\u5251\u9798\u91cc\uff0c\u4e3e\u6b65\u5411\u524d\u8ffd\u53bb\u3002 \u3000\u3000\u3000\u51ac\u65e5\u672c\u77ed\uff0c\u6b64\u523b\u5df2\u8fd1\u9ec4\u660f\uff0c\u9ed1\u6697\u867d\u8fd1\uff0c\u4f46\u9ece\u660e\u4e0d\u4f1a\u592a\u8fdc\u4e86\u3002 \u3000\u3000\u3000\u82e5\u4f60\u662f\u8001\u4e8e\u6c5f\u6e56\u884c\u8d70\u7684\uff0c\u90a3\u4e48\u65e0\u8bba\u4f60\u5728\u4e2d\u539f\u82cd\u832b\u7684\u53e4\u9053\uff0c\u6c5f\u5357\u5982\u753b\u7684\u5c0f\u6865\uff0c\u751a\u3000\u81f3\u662f \u9e21\u58f0\u65e9\u9e23\u7684\u8305\u5e97\uff0c\u706f\u706b\u665a\u7167\u7684\u95f9\u5e02\u4e0a\uff0c\u4f60\u90fd\u53ef\u80fd\u4f1a\u53d1\u73b0\u4e00\u4e2a\u957f\u8eab\u7389\u7acb\uff0c\u9762\u76ee\u5374\u3000\u5e26\u7740\u91cd\u4f18\u7684 \u4e2d\u5e74\u7537\u5b50\uff0c\u8d1f\u624b\u8e48\u8e48\u72ec\u884c\uff0c\u4ed6\u795e\u8272\u91cc\uff0c\u4f3c\u4e4e\u5728\u5bfb\u627e\u4ec0\u4e48\uff0c\u4f46\u53c8\u4f3c\u4e4e\u56e0\u7740\u3000\u592a\u4e45\u7684\u5931\u671b\uff0c\u4ed6\u5bf9 \u4ed6\u81ea\u5df1\u7684\u5bfb\u627e\uff0c\u4e5f\u5e76\u6ca1\u6709\u62b1\u7740\u5927\u591a\u5e0c\u671b\u3002 \u3000\u3000\u3000\u662f\u4ee5\uff0c\u4e00\u773c\u770b\u53bb\uff0c\u4ed6\u5168\u8eab\u6ee1\u542b\u7740\u61d2\u6563\u7684\u5473\u9053\uff0c\u8170\u7554\u6302\u7740\u7684\u957f\u5251\uff0c\u4e5f\u61d2\u6563\u5730\u62d6\u4e86\u3000\u4e0b \u6765\uff0c\u5251\u9798\u751a\u81f3\u5df2\u62d6\u5230\u5730\u4e0a\uff0c\u4e0e\u5730\u76f8\u64e6\uff0c\u5e38\u4f1a\u53d1\u751f\u523a\u8033\u4e4b\u58f0\u3002 \u3000\u3000\u3000\u82e5\u4f60\u4e0d\u4f46\u8001\u4e8e\u6c5f\u6e56\uff0c\u8fd8\u662f\u719f\u6089\u6b66\u6797\u638c\u6545\u7684\u4eba\u7269\uff0c\u4f60\u5c31\u4f1a\u77e5\u9053\uff0c\u8fd9\u6f47\u6d12\u800c\u61d2\u6563\u7684\u3000\u4e2d\u5e74 \u6c49\u5b50\uff0c\u5374\u662f\u5341\u4e03\u5e74\u524d\u5927\u5927\u6709\u540d\u7684\u4eba\u7269\uff0c\u4e5f\u662f\u6614\u5e74\u7684\u540d\u5251\u5ba2\uff0c\u6b66\u5f53\u5c71\u7075\u7a7a\u5251\u5ba2\u7684\u3000\u4eb2\u4f20\u5f1f\u5b50\u2014 \u2014\u77f3\u78f7\u3002 \u3000\u3000\u3000\u82e5\u4f60\u66f4\u719f\u6089\u5185\u60c5\uff0c\u4f60\u8fd8\u4f1a\u4ece\u4ed6\u8eab\u4e0a\u77e5\u9053\u4e00\u6bb5\u51c4\u5d0e\u52a8\u4eba\u7684\u6545\u4e8b\uff0c\u53ea\u662f\u82e5\u6709\u4eba\u77e5\u9053\u3000\u8fd9\u6545 \u4e8b\uff0c\u4e5f\u53ea\u662f\u5c06\u4ed6\u6df1\u85cf\u5728\u5fc3\u91cc\uff0c\u4e0d\u6562\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u3000\u56e0\u4e3a\uff0c\u8fd9\u6545\u4e8b\u9664\u4e86\u77f3\u78f7\uff0c\u8fd8\u5173\u7cfb\u7740\u4eca\u65e5\u6b66\u6797\u4e2d\u7684\u7b2c\u4e00\u4eba\u7269\u2014\u2014\u7075\u86c7\u6bdb\u81ec\uff0c\u73b0\u5728\u3000\u7684\u6b66 \u6797\u4e2d\u4eba\uff0c\u8c01\u8981\u5f97\u7f6a\u4e86\u6bdb\u5927\u7237\uff0c\u90a3\u4e0d\u557b\u662f\u81ea\u5df1\u627e\u81ea\u5df1\u7684\u9ebb\u70e6\uff0c\u800c\u7075\u86c7\u6bdb\u81ec\u5374\u6700\u6015\u3000\u522b\u4eba\u8bf4\u8d77\u8fd9 \u6545\u4e8b\u3002 \u3000\u3000\u3000\u65f6\u65e5\u5306\u5306\uff0c\u6b64\u65f6\u8ddd\u79bb\u4ec7\u72ec\u8eab\u6b7b\uff0c\u5df2\u6709\u5341\u4e03\u5e74\u4e86\u3002\u8fd9\u5341\u516d\u5e74\u6765\uff0c\u6b66\u6797\u4e2d\u81ea\u7136\u53d1\u751f\u3000\u4e86\u8bb8 \u591a\u4e8b\uff0c\u4f46\u5374\u5df2\u90fd\u5728\u4eba\u7684\u8bb0\u5fc6\u91cc\u6d88\u5931\u4e86\uff0c\u50cf\u6ce1\u6cab\u6d88\u5931\u5728\u6c34\u91cc\u4e00\xb7\u6837\uff0c\u8fde\u4e00\u70b9\u6d9f\u6f2a\u3000\u90fd\u672a\u66fe\u6fc0 \u8d77\uff0c\u4f46\u662f\u4e00\u4e00\u53ea\u6709\u4ec7\u72ec\u5374\u4ecd\u5b58\u5728\u4e8e\u5927\u5bb6\u7684\u5fc3\u91cc\uff0c\u56e0\u4e3a\u4ed6\u4eba\u867d\u6b7b\u4e86\uff0c\u4f46\u4ed6\u7684\u3000\u6b8b\u9aa8\uff0c\u5374\u4ecd\u5728\u6b66 \u6797\u4e2d\u5360\u7740\u6781\u91cd\u8981\u7684\u5730\u4f4d\uff0c\u8fd9\u662f\u6b66\u6797\u4e2d\u6570\u767e\u5e74\u6765\uff0c\u672a\u66fe\u51fa\u73b0\u8fc7\u7684\u4e8b\u3002 \u3000\u3000\u3000\u7075\u86c7\u6bdb\u81ec\uff0c\u5229\u7528\u4ec7\u72ec\u7684\u6b8b\u9aa8\uff0c\u5728\u6b66\u6797\u53d6\u5f97\u9738\u4e1a\uff0c\u4ed6\u867d\u7136\u6ca1\u6709\u81ea\u7acb\u95e8\u6237\uff0c\u4f46\u662f\u4ed6\u3000\u7684 \u201c\u6b8b\u9aa8\u4ee4\u201d\uff0c\u5374\u88ab\u6b66\u6797\u4e2d\u4eba\u89c6\u4e3a\u81f3\u5b9d\uff0c\u56e0\u4e3a\u65e0\u8bba\u4efb\u4f55\u4eba\uff0c\u53ea\u8981\u8fd8\u60f3\u5728\u6c5f\u6e56\u4e0a\u6df7\u7684\u3000\uff0c\u5c31\u5f97\u542c \u8fd9\u201c\u6b8b\u9aa8\u4ee4\u201d\u7684\u547d\u4ee4\u3002 \u3000\u3000\u3000\u8fd9\u201c\u6b8b\u9aa8\u4ee4\u201d\u5c31\u662f\u4ec7\u72ec\u7684\u6b8b\u9ab8\u6240\u5236\uff0c\u5f53\u5e74\u7684\u201c\u4e03\u5251\u4e09\u97ad\u201d\uff0c\u73b0\u5728\u5df2\u53bb\u5176\u4e8c\uff0c\u6c6a\u3000\u4e00\u9e4f \u65ad\u81c2\u540e\uff0c\u58f0\u5a01\u4e5f\u4e0d\u5982\u524d\uff0c\u4f46\u4ed6\u4eec\u4ed7\u7740\u90a3\u4ee5\u4ec7\u72ec\u6b8b\u9aa8\u6240\u5236\u7684\u201c\u6b8b\u9aa8\u4ee4\u201d\uff0c\u90fd\u5728\u6b66\u3000\u6797\u4e2d\u5360\u4e86\u9738 \u4e1a\u3002 \u3000\u3000\u3000\u8fd9\u4e9b\u4e8b\uff0c\u5374\u90fd\u672a\u653e\u5728\u77f3\u78f7\u5fc3\u4e0a\uff0c\u4ed6\u6d6a\u8ff9\u5927\u6daf\uff0c\u65e0\u975e\u662f\u60f3\u5bfb\u627e\u6bdb\u51b0\uff0c\u4f46\u5341\u4e03\u5e74\u6765\u3000\uff0c\u4ed6 \u8db3\u8ff9\u8d70\u904d\u4e24\u6cb3\u4e1c\u897f\uff0c\u5927\u6c5f\u5357\u5317\uff0c\u751a\u81f3\u8fde\u5173\u5916\u585e\u5317\u90fd\u8d70\u904d\u4e86\uff0c\u4f46\u662f\uff0c\u6bdb\u51b0\u5374\u50cf\u6d77\u3000\u4e2d\u4e4b\u9488\uff0c\u518d \u4e5f\u627e\u4e0d\u5230\u3002 \u3000\u3000\u3000\u4e8e\u662f\u77f3\u78f7\u4e5f\u53d8\u4e86\uff0c\u4ed6\u53d8\u5f97\u843d\u843d\u5be1\u5408\uff0c\u4e5f\u53d8\u5f97\u6d6a\u8361\u4e0d\u7f81\uff0c\u90a3\u548c\u4ed6\u4ee5\u524d\u7684\u6027\u683c\uff0c\u662f\u3000\u7edd\u4e0d \u76f8\u540c\u7684\uff0c\u4ed6\u7684\u6388\u4e1a\u6069\u5e08\u7075\u7a7a\u5251\u5ba2\u4e3a\u6b64\u5f88\u4f24\u5fc3\u3002\u6c5f\u6e56\u4e0d\u5c11\u8ba4\u8bc6\u4ed6\u7684\u4eba\uff0c\u4e5f\u5728\u4e3a\u4ed6\u3000\u6df1\u6df1\u60cb\u501f \u7740\u3002 \u3000\u3000\u3000\u662f\u6625\u5929\uff0c\u6c5f\u5357\u9a7f\u9053\u4e0a\uff0c\u9a6c\u8e44\u5306\u5fd9\uff0c\u77f3\u78f7\u4e5f\u56de\u5230\u4e86\u6c5f\u5357\uff0c\u4ed6\u8863\u886b\u867d\u4e0d\u534e\u4e3d\uff0c\u4f46\u5374\u3000\u6781\u4e3a \u6574\u6d01\uff0c\u90a3\u5728\u4e00\u4e2a\u6d6a\u8ff9\u5929\u6daf\u7684\u4eba\u6765\u8bf4\uff0c\u662f\u6781\u4e3a\u96be\u5f97\u7684\u3002 \u3000\u3000\u3000\u4ed6\u843d\u5bde\u5730\u9a91\u5728\u7626\u9a6c\u4e0a\uff0c\u9a6c\u7684\u7f30\u7ef3\uff0c\u7d27\u5728\u9a6c\u978d\u4e0a\uff0c\u4ed6\u8ba9\u90a3\u9a6c\u968f\u610f\u884c\u7740\uff0c\u773c\u5149\u5374\u6b63\u3000\u6d4f\u89c8 \u7740\u6c5f\u5357\u9053\u4e0a\u7684\u884c\u4eba\uff0c\u4ee5\u53ca\u9053\u65c1\u5df2\u9752\u8471\u7684\u6797\u6728\uff0c\u5df2\u6e10\u8301\u957f\u7684\u79c0\u8349\uff0c\u53e3\u4e2d\u5fae\u5fae\u4f4e\u541f\u3000\u7740\uff1a\u201c\u6c5f\u5357 \u597d\uff0c\u98ce\u666f\u65e7\u66fe\u8c19\u4e00\u4e00\u201d\u6c5f\u5357\u662f\u4ed6\u65e7\u6e38\u4e4b\u5730\u5440\u3002 \u3000\u3000\u3000\u84e6\u5730\uff0c\u5f81\u5c18\u7a81\u8d77\u2014\u2014\u77f3\u78f7\u4e0d\u7ecf\u610f\u5730\u671b\u8fc7\u53bb\uff0c\u8fdc\u5904\u6709\u4e00\u7fa4\u5feb\u9a6c\u5954\u81f3\uff0c\u6562\u5728\u8fd9\u79cd\u884c\u3000\u4eba\u7a20 \u5bc6\u7684\u8def\u4e0a\u653e\u9a6c\u800c\u9a70\u7684\uff0c\u82e5\u975e\u5b98\u5e9c\u516c\u5dee\uff0c\u4e0d\u95ee\u53ef\u77e5\uff0c\u4fbf\u662f\u7075\u86c7\u6bdb\u81ec\u7684\u624b\u4e0b\u6b66\u58eb\uff0c\u3000\u77f3\u78f7\u5fc3\u4e2d\u52a8 \u4e86\u4e00\u4e0b\uff0c\u5fd6\u9053\uff1a\u201c\u51fa\u4e86\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u3000\u90a3\u7fa4\u5954\u9a6c\uff0c\u500f\u5ffd\u800c\u81f3\uff0c\u5728\u6eda\u6eda\u5f81\u5c18\u4e2d\uff0c\u4e5f\u770b\u4e0d\u6e05\u7a76\u7adf\u662f\u4e9b\u4ec0\u4e48\u4eba\u7269\uff0c\u6643\u773c\u4fbf\u53c8\u3000\u7edd\u5c18 \u800c\u53bb\uff0c\u7559\u4e0b\u4e00\u80a1\u9ec4\u5c18\u3002 \u3000\u3000\u3000\u77f3\u78f7\u538c\u6076\u5730\u62c2\u53bb\u4e86\u9762\u4e0a\u7684\u5c18\u571f\uff0c\u653e\u9a6c\u524d\u884c\uff0c\u4f9d\u7a00\u89c9\u5f97\u53e6\u6709\u4e24\u9a91\u5c31\u5728\u4ed6\u8eab\u540e\uff0c\u4ed6\u3000\u4e5f\u6ca1 \u6709\u56de\u5934\u53bb\u770b\uff0c\u56e0\u4e3a\u8fd9\u4e9b\u5e74\u6765\uff0c\u4ed6\u548c\u6b66\u6797\u4e2d\u4eba\u5df2\u65e0\u6069\u6028\u53ef\u8a00\uff0c\u662f\u4ee5\u4ed6\u4e5f\u4e0d\u9700\u8981\u50cf\u3000\u6614\u65e5\u4e00\u6837\u968f \u65f6\u7559\u5fc3\u522b\u4eba\u7684\u6697\u7b97\u3002 \u3000\u3000\u3000\u4f46\u662f\uff0c\u540e\u9762\u90a3\u4e24\u4eba\u968f\u98ce\u4f20\u6765\u7684\u8bdd\u58f0\uff0c\u4ed6\u5374\u65e0\u6cd5\u4e0d\u542c\u4e00\u201c\u7075\u86c7\u8fd9\u6b21\u53ef\u771f\u78b0\u4e0a\u5b9a\u5934\u3000\u8d27 \u4e86\uff0c\u770b\u4ed6\u624b\u4e0b\u5341\u5927\u5f1f\u5b50\uff0c\u5c45\u7136\u5168\u51fa\u52a8\u4e86\uff0c\u5c31\u77e5\u9053\u4ed6\u53ef\u4e5f\u7740\u4e86\u6025\uff0c\u5144\u5f1f\u8fd9\u6b21\u4ece\u5317\u65b9\u3000\u6765\uff0c\u5728\u4fdd \u5b9a\u5e9c\u90a3\u8fb9\u5c31\u542c\u5230\u4e86\u8fd9\u4e2a\u6d88\u606f\uff0c\u636e\u8bf4\u6bdb\u8001\u5927\u5df2\u98de\u4f20\u2018\u6b8b\u9aa8\u4ee4\u2019\uff0c\u60f3\u52a8\u7528\u6240\u6709\u3000\u7684\u529b\u91cf\u6765\u5bf9\u4ed8\u90a3 \u4e2a\u5c11\u5e74\u54e9\u3002\u201d \u3000\u3000\u3000\u53e6\u5916\u4e00\u4e2a\u58f0\u97f3\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u4e5f\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u6211\u5012\u4e0d\u5927\u6e05\u695a\uff0c\u4e0d\u8fc7\u6709\u4eba\u627e\u6bdb\u8001\u3000\u5927\u7684 \u9ebb\u70e6\uff0c\u53ef\u6709\u70b9\u4e0d\u5f00\u773c\u5427\uff1f\u201d \u3000\u3000\u3000\u201c\u662f\u5440\uff01\u201d\u5148\u524d\u90a3\u5317\u65b9\u53e3\u97f3\u7684\u4eba\u8bf4\u9053\uff0c\u201c\u8d77\u5148\u6211\u4e5f\u4ee5\u4e3a\u90a3\u4eba\u62db\u5b50\u4e0d\u4eae\uff0c\u540e\u6765\u518d\u3000\u4e00\u542c \u8bf4\uff0c\u90a3\u4eba\u867d\u7136\u521d\u51fa\u9053\uff0c\u4e07\u513f\u8fd8\u4e0d\u54cd\uff0c\u624b\u5e95\u53ef\u771f\u6709\u4e24\u4e0b\u5b50\uff0c\u6bdb\u8001\u5927\u624b\u4e0b\u7684\u9556\u5c40\uff0c\u3000\u65e0\u8bba\u4fdd\u7684\u660e \u9556\uff0c\u6697\u9556\uff0c\u4ed6\u90fd\u6709\u529e\u6cd5\u52ab\u4e86\u6765\u3002\u201d \u3000\u3000\u3000\u4f60\u4e3a\u505c\u987f\u4e00\u4e0b\uff0c\u53c8\u63a5\u7740\u9053\uff1a\u201c\u6700\u602a\u7684\u662f\uff0c\u4ed6\u52ab\u4e86\u9556\uff0c\u4e5f\u4e0d\u62ff\u8d77\u8d70\uff0c\u5374\u5c06\u9556\u94f6\uff0c\u3000\u73e0\u5b9d \u6ee1\u5730\u4e71\u4e22\uff0c\u4efb\u51ed\u4eba\u5bb6\u53bb\u6361\uff0c\u4ed6\u81ea\u5df1\u5374\u4e00\u6587\u4e5f\u4e0d\u8981\u3002\u201d \u3000\u3000\u3000\u8fd9\u4eba\u4f3c\u4e4e\u6781\u7231\u8bf4\u8bdd\uff0c\u4e00\u53e3\u7684\u5317\u65b9\u53e3\u97f3\uff0c\u55d3\u95e8\u53c8\u5927\uff0c\u77f3\u78f7\u542c\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u7a81\u7136\u5fc3\u3000\u4e2d\u4e00 \u52a8\uff0c\u5fd6\u9053\uff1a\u201c\u83ab\u4e0d\u662f\u6709\u4eba\u4e3a\u4ec7\u72ec\u590d\u4ec7\uff1f\u201d\u5f88\u81ea\u7136\u5730\uff0c\u4ed6\u53c8\u8054\u60f3\u5230\u6bdb\u51b0\u8eab\u4e0a\uff0c\u4e8e\u3000\u662f\u4ed6\u53c8\u7559\u610f \u5730\u53bb\u542c\u4e00\u201c\u8fd9\u4eba\u5012\u662f\u4e2a\u5947\u4eba\uff0c\u5582\uff01\u4f9d\u4f60\u7684\u610f\u601d\uff0c\u8fd9\u4eba\u662f\u4e0d\u662f\u548c\u5341\u591a\u5e74\u524d\u3000\u7684\u90a3\u4ef6\u4e8b\u6709\u5173 \u7cfb\uff1f\u201d\u4ed6\u54fc\u4e86\u4e00\u58f0\uff0c\u53c8\u9053\uff0c\u201c\u6211\u8d70\u9556\u9655\u897f\u7684\u65f6\u5019\uff0c\u66fe\u548c\u9e33\u9e2f\u53cc\u5251\u7684\u4e00\u3000\u4e2a\u5f92\u5f1f\u4ea4\u4e0a\u597d\u670b\u53cb\uff0c \u4ed6\u5c31\u544a\u8bc9\u6211\uff0c\u8bf4\u662f\u90a3\u4e3b\u513f\u5fc5\u5b9a\u4e0d\u80af\u5c31\u8fd9\u4e48\u6837\u7b97\u4e86\u7684\uff0c\u8fd8\u6709\u7740\u4ec0\u3000\u4e48\u2018\u5341\u5e74\u4ee5\u540e\uff0c\u4ee5\u8840\u8fd8\u8840\u2019 \u8fd9\u53e5\u8bdd\uff0c\u6211\u770b\u5440\u2014\u2014\u201d\u4ed6\u542b\u84c4\u5730\u6b62\u4f4f\u4e86\u8bdd\u3002 \u3000\u3000\u3000\u53e6\u4e00\u4eba\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u5012\u662f\u542c\u89c1\u98ce\u5c31\u662f\u96e8\u7684\u813e\u6c14\uff0c\u59d3\u4ec7\u7684\u4eba\u5df2\u6b7b\u4e86\uff0c\u4e0d\u8fd9\u6837\u7b97\u3000\u4e86\u53c8 \u600e\u6837\uff0c\u4f55\u51b5\u4ed6\u65e2\u65e0\u5b50\u5973\u5f92\u5f1f\uff0c\u4e5f\u6ca1\u6709\u81f3\u4eb2\u597d\u53cb\uff0c\u6b7b\u4e86\u8fde\u4e2a\u82e6\u4e3b\u513f\u90fd\u6ca1\u6709\uff0c\u8fd8\u6709\u3000\u8c01\u66ff\u4ed6\u62a5 \u4ec7\uff1f\u201d \u3000\u3000\u3000\u53e6\u4e00\u4eba\u4e0d\u4ee5\u4e3a\u7136\u5730\u54fc\u4e86\u4e00\u4e0b\uff0c\u90a3\u4eba\u53c8\u9053\uff1a\u201c\u5341\u5e74\u4e4b\u540e\uff0c\u4ee5\u8840\u8fd8\u8840\uff0c\u73b0\u5728\u53ef\u4e8c\u5341\u3000\u5e74\u90fd \u5feb\u5230\u4e86\uff0c\u8001\u5b9e\u544a\u8bc9\u4f60\uff0c\u52ab\u6bdb\u8001\u5927\u9556\u7684\u90a3\u4e2a\u4e3b\u513f\uff0c\u542c\u8bf4\u662f\u4e2a\u4e09\u5341\u51e0\u5c81\u7684\u6c49\u5b50\uff0c\u4ece\u3000\u6765\u90fd\u662f\u72ec\u5f80 \u72ec\u884c\uff0c\u9047\u89c1\u4e0d\u5e73\u7684\u4e8b\uff0c\u4ed6\u5c31\u8981\u7ba1\uff0c\u7ba1\u5b8c\u4e86\uff0c\u5c31\u7559\u4e0b\u4e00\u53ea\u5c0f\u91d1\u5251\u4f5c\u8868\u8bb0\uff0c\u3000\u5927\u5bb6\u4e0d\u77e5\u9053\u4ed6\u7684\u540d \u5b57\uff0c\u5c31\u7ba1\u4ed6\u53eb\u2018\u91d1\u5251\u4fa0\u2019\uff0c\u54e5\u513f\u4eec\u4f60\u6700\u8fd1\u7a9d\u5728\u5bb6\u91cc\u4e0d\u51fa\u6765\uff0c\u5927\u6982\u3000\u6ca1\u6709\u542c\u8fc7\u8fd9\u4e2a\u540d\u5b57\u5427\uff1f\u201d \u3000\u3000\u3000\u53e6\u4e00\u4eba\u7b11\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u8c01\u50cf\u4f60\uff0c\u50cf\u4e2a\u5931\u5fc3\u75af\u4f3c\u7684\uff0c\u6574\u5e74\u5728\u5916\u9762\u8dd1\uff0c\u563f\uff01\u6211\u8bf4\u3000\u4f60 \u5440\uff0c\u4e09\u5341\u591a\u5c81\u4e86\uff0c\u4e5f\u8be5\u5a36\u4e2a\u8001\u5a46\u4e86\u5427\uff01\u201d \u3000\u3000\u3000\u4e24\u4eba\u4e00\u9635\u563b\u7b11\uff0c\u518d\u8c08\u4e0b\u53bb\u5c31\u662f\u4e9b\u8a00\u4e0d\u53ca\u4e49\u7684\u8bdd\uff0c\u77f3\u78f7\u66f4\u653e\u7f13\u4e86\u9a6c\uff0c\u8ba9\u90a3\u4e24\u9a91\u5148\u3000\u8d70\u8fc7 \u53bb\uff0c\u4ed6\u81ea\u5df1\u5374\u4f4e\u5934\u6c89\u541f\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u91d1\u5251\u4fa0\u53c8\u662f\u8c01\u5462\u3001\u6211\u5148\u524d\u4ee5\u4e3a\u4ed6\u4f1a\u662f\u51b0\u59b9\u3000\u809a\u91cc\u90a3\u4e2a\u5b69 \u5b50\uff0c\u4f46\u4eba\u5bb6\u5df2\u4e09\u5341\u591a\u5c81\u4e86\uff0c\u770b\u6765\u53c8\u4e0d\u50cf\u4f1a\u662f\u4ed6\u3002\u201d \u3000\u3000\u3000\u201c\u4e09\u5341\u591a\u5c81\u7684\u4eba\uff0c\u624d\u5f00\u59cb\u5728\u6c5f\u6e56\u4e0a\u95ef\u4e07\u513f\u7684\uff0c\u53ea\u6709\u4e24\u79cd\u60c5\u5f62\uff0c\u4e00\u4e00\u79cd\u662f\u4ed6\u4e60\u827a\u3000\u672c \u665a\uff0c\u662f\u4ee5\u827a\u6210\u4e5f\u665a\uff0c\u53e6\u4e00\u79cd\u60c5\u5f62\u5c31\u662f\u4ed6\u672c\u6765\u5df2\u95ef\u8fc7\u6c5f\u6e56\uff0c\u73b0\u5728\u5374\u6539\u5934\u6362\u9762\uff0c\u4ee5\u53e6\u3000\u4e00\u756a\u9762\u76ee \u51fa\u73b0\uff0c\u8fd9\u201d\u91d1\u5251\u4fa0\u201d\u662f\u54ea\u4e00\u79cd\u5462\uff1f\u201d\u4ed6\u54b3\u4e86\u4e00\u58f0\uff0c\u8f6c\u5ff5\u5fd6\u9053\uff1a\u201c\u6211\u53bb\u60f3\u8fd9\u3000\u4e9b\u5e72\u4ec0\u4e48\uff0c\u53cd\u6b63 \u8fd9\u4e9b\u5168\u5173\u4e0d\u7740\u6211\u7684\u4e8b\u3002\u201d \u3000\u3000\u3000\u5251\u9798\u5c31\u5728\u9a6c\u8e6c\u4e0a\uff0c\u53ee\u5f53\u4f5c\u54cd\uff0c\u4ed6\u5c06\u5251\u7a0d\u4e3a\u63d0\u4e86\u4e00\u4e9b\uff0c\u62ac\u5934\u770b\u5230\u5929\u5df2\u4e0d\u65e9\u4e86\uff0c\u897f\u3000\u9762\u5df2 \u6709\u843d\u65e5\u65f6\u7684\u665a\u971e\uff0c\u4e8e\u662f\u4ed6\u5c06\u9a6c\u7a0d\u5fae\u8d76\u5feb\u4e86\u4e9b\u3002 \u3000\u3000\u3000\u8fdb\u4e86\u9547\u6c5f\u5e9c\uff0c\u4ed6\u4e0b\u4e86\u9a6c\uff0c\u7f13\u7f13\u7275\u7740\u7f30\u7ef3\u524d\u884c\uff0c\u4fe1\u6b65\u8d70\u5165\u4e00\u5bb6\u5ba2\u6808\uff0c\u5c06\u9a6c\u4ea4\u7ed9\u4e86\u3000\u5e97 \u4f19\uff0c\u62ac\u5934\u4e00\u671b\uff0c\u5374\u89c1\u4e00\u9762\u9556\u65d7\u63d2\u5728\u8fdb\u53e3\u7684\u95e8\u6846\u4e0a\uff0c\u4e0d\u7981\u5fae\u4e00\u76b1\u7709\uff0c\u6697\u602a\u81ea\u5df1\u9009\u9519\u3000\u4e86\u5730\u65b9\uff0c \u4f46\u4eba\u5df2\u8fdb\u6765\u4e86\uff0c\u53c8\u4e0d\u597d\u610f\u601d\u518d\u51fa\u53bb\uff0c\u53ea\u5f97\u968f\u610f\u9009\u4e86\u95f4\u623f\u4f4f\u4e0b\u3002 \u3000\u3000\u3000\u4e0a\u706f\u540e\uff0c\u679c\u7136\u4e0d\u51fa\u4ed6\u6240\u6599\uff0c\u5ba2\u6808\u91cc\u5608\u58f0\u523a\u8033\uff0c\u90a3\u4e9b\u9556\u5c40\u91cc\u7684\u9556\u4f19\u4eec\uff0c\u5406\u4e94\u559d\u516d\u3000\uff0c\u731c \u62f3\u559d\u9152\uff0c\u8fd8\u53eb\u4e9b\u7c89\u5934\u6765\u5531\u66f2\u3002 \u3000\u3000\u3000\u77f3\u78f7\u5934\u76ae\u53d1\u70b8\uff0c\u63a8\u95e8\u8d70\u4e86\u51fa\u53bb\uff0c\u9662\u5b50\u91cc\u867d\u7136\u6ca1\u6709\u91cc\u8fb9\u95f7\uff0c\u4f46\u8fd8\u4e0d\u662f\u5435\u5f97\u4e00\u6837\u5389\u3000\u5bb3\uff0c \u8fd9\u4e9b\u9556\u4f19\u8ddf\u8d9f\u5b50\u624b\uff0c\u6574\u5929\u98ce\u5c18\u5fd9\u788c\uff0c\u8fd9\u5929\u5927\u6982\u662f\u521a\u53d1\u4e86\u94f6\u5b50\uff0c\u518d\u52a0\u4e0a\u6240\u4f4f\u7684\u5927\u3000\u57ce\uff0c\u4e0d\u6015\u4f1a \u6709\u5f3a\u76d7\uff0c\u653e\u5fc3\u4e4b\u4e0b\uff0c\u5f53\u7136\u8981\u5c3d\u91cf\u5730\u4f5c\u4e50\uff0c\u6253\u6270\u522b\u4eba\uff0c\u4ed6\u4eec\u6839\u672c\u4e0d\u7ba1\u3002 \u3000\u3000\u3000\u4ed6\u4eec\u8fd9\u6837\u653e\u8086\uff0c\u539f\u56e0\u4e4b\u4e00\u5374\u662f\u56e0\u4e3a\u4ed6\u4eec\u5e73\u5b89\u9556\u5c40\u7684\u603b\u9556\u5934\u516b\u9762\u73b2\u73ab\u80e1\u4e4b\u8f89\u662f\u201c\u3000\u6bdb\u5927 \u592a\u7237\u201d\u7684\u62dc\u628a\u5b50\u5144\u5f1f\uff0c\u5173\u7cfb\u62c9\u5f97\u975e\u5e38\u597d\uff0c\u518d\u52a0\u4e0a\u8fd9\u6b21\u8d70\u9556\uff0c\u662f\u80e1\u4e4b\u8f89\u4eb2\u81ea\u51fa\u9a6c\u3000\u7684\uff0c\u5927\u4f19\u513f \u90fd\u653e\u5fc3\u5f97\u5f88\u3002 \u3000\u3000\u3000\u77f3\u78f7\u7981\u4e0d\u5f97\u5435\uff0c\u8d8a\u5435\uff0c\u4ed6\u5c31\u8d8a\u70e6\uff0c\u4ed6\u4e0d\u613f\u610f\u548c\u522b\u4eba\u4e89\u5435\uff0c\u5c31\u8d70\u4e86\u51fa\u53bb\uff0c\u7ad9\u5728\u5ba2\u3000\u6808\u95e8 \u53e3\uff0c\u671b\u7740\u9752\u77f3\u996d\u94fa\u6210\u7684\u8def\uff0c\u5fc3\u91cc\u5012\u89c9\u5f97\u6e05\u9759\u4e0d\u5c11\u3002 \u3000\u3000\u3000\u4ed6\u968f\u610f\u95f2\u773a\uff0c\u5374\u770b\u5230\u4e00\u9876\u8f6f\u8f7f\u5728\u5ba2\u6808\u95e8\u524d\u505c\u4e86\u4e0b\u6765\uff0c\u4ed6\u4e0d\u7981\u6ce8\u610f\u53bb\u770b\uff0c\u56e0\u4e3a\u5728\u3000\u6c5f\u6e56 \u4e0a\u884c\u8d70\u7684\u4eba\uff0c\u5750\u8f7f\u5b50\u7684\u6781\u5c11\uff0c\u8fd9\u4e00\u6765\u662f\u56e0\u4e3a\u5750\u8f7f\u5b50\u4e0d\u5982\u9a91\u9a6c\u4e58\u8f66\u65b9\u4fbf\uff0c\u901f\u5ea6\u4e5f\u3000\u592a\u6162\uff0c\u518d\u6765 \u5374\u662f\u56e0\u4e3a\u5750\u8f7f\u5b50\u7684\u82b1\u8d39\u592a\u5927\uff0c\u8c01\u4e5f\u4e0d\u613f\u610f\u82b1\u8fd9\u4e2a\u51a4\u6789\u94b1\u3002 \u3000\u3000\u3000\u8f7f\u5b50\u5e73\u7a33\u5730\u653e\u5728\u5730\u4e0a\uff0c\u8d70\u51fa\u4e00\u4e2a\u5c11\u5e74\uff0c\u77f3\u78f7\u5fae\u76b1\u7709\uff0c\u4ed6\u672c\u4ee5\u4e3a\u8f7f\u5b50\u91cc\u5750\u7684\u4e0d\u662f\u3000\u4f24\u75c5 \u4e4b\u4eba\uff0c\u5c31\u662f\u8001\u5934\u5b50\uff0c\u6216\u5a18\u513f\u4eec\uff0c\u54ea\u77e5\u662f\u4e2a\u5f31\u51a0\u5c11\u5e74\uff1f \u3000\u3000\u3000\u201c\u8fd9\u4e48\u5a07\u5ae9\uff0c\u8fd8\u51fa\u6765\u5e72\u4ec0\u4e48\uff0c\u8eb2\u5728\u5bb6\u91cc\u5f53\u5c11\u7237\u597d\u4e86\u3002\u201d\u4ed6\u8511\u89c6\u5730\u671b\u4e86\u90a3\u5c11\u5e74\u4e00\u3000\u773c\uff0c \u773c\u524d\u5374\u662f\u4e00\u4eae\uff0c\u90a3\u5c11\u5e74\u8138\u4e0a\u7684\u8f6e\u5ed3\uff0c\u6781\u4e3a\u6e05\u79c0\u800c\u52a8\u4eba\uff0c\u773c\u775b\u5927\u800c\u6df1\u8fdc\uff0c\u9f3b\u5b50\u9ad8\u3000\u800c\u633a\u79c0\uff0c\u867d \u7136\u957f\u5f97\u6781\u7f8e\uff0c\u5374\u6ca1\u6709\u534a\u70b9\u513f\u8102\u7c89\u6c14\uff0c\u518d\u52a0\u90a3\u8eab\u6781\u5300\u79f0\u5408\u4f53\u7684\u8863\u88f3\uff0c\u770b\u8d77\u3000\u6765\u8d8a\u53d1\u7ed9\u4eba\u5bb6\u4e00\u79cd \u8212\u670d\u548c\u987a\u773c\u7684\u611f\u89c9\u3002 \u3000\u3000\u3000\u77f3\u78f7\u5e74\u5c11\u65f6\uff0c\u4e5f\u7d20\u6709\u201c\u7f8e\u7537\u5b50\u201d\u4e4b\u79f0\uff0c\u6b64\u65f6\u89c1\u4e86\u8fd9\u7f8e\u5c11\u5e74\uff0c\u76f8\u60dc\u4e4b\u610f\uff0c\u6cb9\u7136\u800c\u3000\u751f\uff0c \u4e0d\u7981\u5c06\u65b9\u624d\u7684\u538c\u6076\u4e4b\u5fc3\uff0c\u6d88\u5931\u4e86\u5927\u534a\u3002 \u3000\u3000\u3000\u90a3\u5c11\u5e74\u4e00\u4e0b\u8f7f\uff0c\u5e97\u91cc\u7684\u4f19\u8ba1\u7acb\u5373\u606d\u8c28\u5730\u4e0a\u6765\u62db\u547c\uff0c\u5e97\u4f19\u4eec\u7684\u773c\u775b\u8be5\u6709\u591a\u5389\u5bb3\uff0c\u3000\u8d2b\u5bcc \u8d35\u8d31\uff0c\u4e00\u671b\u800c\u77e5\uff0c\u8fd9\u5c11\u5e74\u8863\u88f3\u534e\u4e3d\uff0c\u4e3e\u6b62\u4e0d\u51e1\uff0c\u6c14\u6d3e\u53c8\u8fd9\u4e48\u5927\uff0c\u5e97\u4f19\u4eec\u4e0d\u5df4\u7ed3\u3000\u8fd9\u79cd\u4eba\u5df4\u7ed3 \u8c01\u53bb\uff1f \u3000\u3000\u3000\u77f3\u78f7\u76ee\u9001\u90a3\u5c11\u5e74\u7684\u80cc\u5f71\u4eba\u4e86\u5e97\uff0c\u8f6c\u773c\u5374\u770b\u5230\u4e00\u4e2a\u5c11\u5e74\u4e5e\u4e10\u5c31\u7740\u5ba2\u6808\u524d\u7684\u706f\u7b3c\u4e4b\u3000\u5149\u5728 \u6349\u86a4\u5b50\uff0c\u6697\u53f9\u4e86\u4e00\u58f0\uff0c\u4eba\u95f4\u4e0d\u5e73\u4e8b\uff0c\u4e3e\u76ee\u7686\u662f\uff0c\u8fd9\u5c11\u5e74\u4e0e\u8fd9\u4e5e\u4e10\u7684\u547d\u8fd0\uff0c\u96be\u9053\u3000\u751f\u6765\u5c31\u5982\u6b64 \u7684\u5417\uff1f \u3000\u3000\u3000\u4ed6\u65bd\u65bd\u7136\u5728\u8def\u4e0a\u95f2\u901b\u4e86\u4e00\u4f1a\uff0c\u5728\u94fa\u5b50\u91cc\u4e70\u4e86\u4e9b\u9189\u9e21\u9171\u8089\uff0c\u53c8\u6cbd\u4e86\u4e9b\u9152\uff0c\u51c6\u5907\u4eca\u3000\u665a\u4e00 \u9189\u89e3\u6101\uff0c\u4ed6\u4e0d\u559c\u6b22\u5728\u996d\u9986\u91cc\u559d\u9152\uff0c\u56e0\u4e3a\u90a3\u8fdc\u4e0d\u53ca\u5728\u81ea\u5df1\u623f\u5b50\u91cc\u81ea\u7531\uff0c\u800c\u559d\u9152\u5374\u3000\u662f\u6700\u9700\u8981\u81ea \u7531\u7684\u3002 \u3000\u3000\u3000\u4ed6\u8d70\u8fdb\u5ba2\u6808\uff0c\u4e00\u9762\u6697\u7b11\u81ea\u5df1\uff0c\u73b0\u5728\u5c45\u7136\u4e5f\u53d8\u6210\u9152\u9b3c\u4e86\uff0c\u5bc2\u5bde\u4e0e\u5fe7\u90c1\uff0c\u662f\u4ed6\u559d\u9152\u3000\u6700\u5927 \u7684\u539f\u56e0\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u4eba\u5728\u5fae\u9187\u65f6\u7684\u5fc3\u5883\uff0c\u603b\u662f\u8f83\u4e3a\u6109\u5feb\u7684\u3002 \u3000\u3000\u3000\u4ed6\u8d70\u8fdb\u9662\u5b50\uff0c\u6b64\u523b\u7adf\u8fde\u9662\u5b50\u91cc\u90fd\u6324\u6ee1\u4e86\uff0c\u4ed6\u4e0d\u77e5\u9053\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u8d70\u8fc7\u53bb\u4e00\u770b\uff0c\u3000\u770b\u89c1 \u4e00\u5927\u5806\u4eba\u56f4\u7740\u4e00\u5f20\u5706\u684c\u9762\uff0c\u5728\u63b7\u7740\u9ab0\u5b50\uff0c\u8fd9\u4e9b\u4eba\u5927\u6982\u662f\u5acc\u623f\u5b50\u4e0d\u591f\u5bbd\u655e\uff0c\u7adf\u642c\u3000\u5230\u9662\u5b50\u91cc\u8d4c \u8d77\u6765\u3002 \u3000\u3000\u3000\u77f3\u78f7\u53c8\u6324\u4e86\u51fa\u6765\uff0c\u5173\u8d77\u95e8\uff0c\u81ea\u5df1\u559d\u4e86\u51e0\u676f\u95f7\u9152\uff0c\u5fc3\u4e2d\u6709\u4e9b\u98d8\u98d8\u7136\uff0c\u8fd9\u4e48\u591a\u5e74\u6765\u3000\uff0c\u4ed6 \u5df2\u5b66\u4f1a\u600e\u4e48\u6837\u5728\u559d\u4e86\u9152\u4e4b\u540e\u5fd8\u8bb0\u4e00\u4e9b\u81ea\u5df1\u4e0d\u8be5\u60f3\u7684\u4e8b\u3002 \u3000\u3000\u3000\u9662\u5b50\u91cc\u7684\u5608\u58f0\u8d8a\u6765\u8d8a\u5927\uff0c\u4ed6\u5728\u5c4b\u5b50\u91cc\u8f6c\u4e86\u4e24\u8f6c\uff0c\u5fcd\u4e0d\u4f4f\u53c8\u63a8\u95e8\u8d70\u4e86\u51fa\u6765\uff0c\u4ed6\u770b\u3000\u89c1\u90a3 \u5706\u684c\u65c1\u7684\u4eba\u8d8a\u6765\u8d8a\u591a\uff0c\u4e0d\u7981\u6fc0\u53d1\u4e86\u597d\u5947\u5fc3\uff0c\u4e5f\u6324\u4e86\u8fc7\u53bb\uff0c\u5374\u770b\u5230\u684c\u5b50\u4e0a\u5806\u7740\u4e00\u3000\u5927\u5806\u94f6\u5b50\uff0c \u7ad9\u5728\u94f6\u5b50\u540e\u9762\uff0c\u624b\u91cc\u6447\u7740\u9ab0\u5b50\u7684\uff0c\u5374\u662f\u90a3\u4e2a\u534e\u670d\u7f8e\u5c11\u5e74\u3002 \u3000\u3000\u3000\u4ed6\u5fae\u5fae\u6709\u4e9b\u60ca\u8be7\uff0c\u6ce8\u610f\u5730\u770b\u7740\u90a3\u7f8e\u5c11\u5e74\uff0c\u65c1\u8fb9\u6709\u4eba\u8bf4\u9053\uff1a\u201c\u8fd9\u6b21\u4ed6\u603b\u8be5\u8f93\u4e00\u6b21\u3000\u4e86 \u5427\uff1f\u6211\u4e0d\u76f8\u4fe1\u4ed6\u63b7\u7684\u70b9\u5b50\u6bd4\u8001\u738b\u8fd8\u5927\u3002\u201d \u3000\u3000\u3000\u53e6\u4e00\u4eba\u5934\u524a\u80a9\uff0c\u4e00\u53cc\u8001\u9f20\u773c\uff0c\u7d27\u7d27\u77aa\u7740\u90a3\u5c11\u5e74\u7684\u624b\uff0c\u53e3\u4e2d\u5406\u559d\u9053\uff1a\u201c\u4e48\u3001\u4e8c\u3001\u3000\u4e09\u201d \u4ed6\u5728\u5e0c\u671b\u7740\u90a3\u5c11\u5e74\u63b7\u51fa\u7684\u70b9\u5b50\u662f\u4e48\u3001\u4e8c\u3001\u4e09\uff0c\u77f3\u78f7\u6697\u7b11\u5fd6\u9053\uff1a\u201c\u8fd9\u53ae\u60f3\u5fc5\u5c31\u662f\u3000\u8001\u738b\u4e86\u3002\u201d \u3000\u3000\u3000\u90a3\u5c11\u5e74\u4e0d\u52a8\u58f0\u8272\uff0c\u624b\u4e00\u653e\uff0c\u5c06\u90a3\u516d\u7c92\u9ab0\u5b50\u63b7\u5728\u6d77\u7897\u91cc\uff0c\u516d\u7c92\u9ab0\u5b50\u5728\u7897\u91cc\u6253\u8f6c\uff0c\u3000\u4f17\u4eba \u7684\u773c\u775b\u4e5f\u8ddf\u7740\u6253\u8f6c\uff0c\u5c31\u8fde\u77f3\u78f7\uff0c\u4e5f\u6ce8\u610f\u5730\u53bb\u770b\uff0c\u90a3\u516d\u7c92\u9ab0\u5b50\uff0c\u4e00\u7c92\u4e00\u7c92\u5730\u505c\u4e86\u3000\u4e0b\u6765\uff0c\u6b63\u9762 \u5168\u662f\u56db\u70b9\uff0c\u6700\u540e\u4e24\u7c92\u9ab0\u4e8e\u4ecd\u5728\u6eda\u52a8\u7740\uff0c\u4e00\u7c92\u5c06\u8981\u505c\u4e86\u4e0b\u6765\uff0c\u4f3c\u4e4e\u662f\u4e2a\u9ed1\u3000\u70b9\uff0c\u4f46\u4e0d\u77e5\u600e\u5730\uff0c \u88ab\u53e6\u4e00\u7c92\u9ab0\u5b50\u4e00\u649e\uff0c\u4e24\u7c92\u4e00\u9f50\u505c\u4e0b\u6765\uff0c\u4e5f\u662f\u201c\u56db\u70b9\u201d\uff0c\u7adf\u662f\u4e2a\u201c\u3000\u5168\u7ea2\u8c79\u5b50\u201d\uff0c\u7edf\u5403\u3002 \u3000\u3000\u3000\u3002\u4f17\u4eba\u4e00\u58f0\u60ca\u547c\uff0c\u8001\u738b\u8138\u5982\u6b7b\u7070\uff0c\u90a3\u5c11\u5e74\u7b11\u563b\u563b\u5730\u5c06\u684c\u9762\u4e0a\u4e00\u5c0f\u5806\u94f6\u5b50\uff0c\u52a0\u5230\u3000\u4ed6\u90a3 \u4e00\u5927\u5806\u94f6\u5b50\u4e0a\u3002\u77f3\u78f7\u4e00\u751f\u4e2d\uff0c\u8fd8\u662f\u7b2c\u4e00\u6b21\u89c1\u5230\u522b\u4eba\u63b7\u9ab0\u5b50\u63b7\u51fa\u516d\u4e2a\u7ea2\u8272\u56db\u70b9\u6765\u3000\uff0c\u4e5f\u770b\u5f97\u5446 \u4e86\u3002 \u3000\u3000\u3000\u8001\u738b\u5927\u6982\u8f93\u5149\u4e86\uff0c\u7a81\u5730\u4f38\u624b\u4e00\u638f\uff0c\u81ea\u9774\u7edf\u4e2d\u638f\u51fa\u4e00\u628a\u5315\u9996\u6765\uff0c\u4eae\u6676\u6676\u5730\uff0c\u201c\u593a\u3000\u5730\u201d \u4e00\u58f0\uff0c\u63d2\u5728\u684c\u9762\u4e0a\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u8001\u5b50\u8f93\u5149\u4e86\uff0c\u8001\u5b50\u8d4c\u8eab\u4e0a\u7684\u4e00\u65a4\u8089\uff0c\u8001\u5b50\u8981\u3000\u662f\u8f93\u4e86\uff0c\u5c31 \u4ece\u8eab\u4e0a\uff0c\u5272\u4e00\u65a4\u8089\uff0c\u8981\u662f\u8d62\u4e86\uff0c\u4f60\u5c31\u5f97\u628a\u94f6\u5b50\u5168\u7ed9\u6211\u3002\u201d \u3000\u3000\u3000\u4ed6\u8f93\u5f97\u7740\u6025\uff0c\u7adf\u800d\u8d77\u65e0\u8d56\u6765\uff0c\u56f4\u7740\u684c\u9762\u7ad9\u7740\u7684\u4eba\uff0c\u5168\u8ddf\u8001\u738b\u662f\u670b\u53cb\uff0c\u90fd\u5728\u66ff\u8001\u3000\u738b\u52a9 \u5a01\uff0c\u539f\u6765\u90a3\u5c11\u5e74\u4e00\u4e0a\u6765\uff0c\u624b\u98ce\u5947\u4f73\uff0c\u7adf\u5c06\u8fd9\u822c\u9556\u4f19\u4eec\u7684\u94f6\u5b50\u5168\u8d62\u4e86\u8fc7\u53bb\uff0c\u5927\u5bb6\u3000\u81ea\u7136\u5168\u6709 \u6c14\u3002 \u3000\u3000\u3000\u90a3\u5c11\u5e74\u770b\u4e86\u90a3\u5200\u5b50\u4e00\u773c\uff0c\u8138\u4e0a\u795e\u8272\u4e1d\u6beb\u672a\u52a8\uff0c\u51b7\u7136\u8bf4\u9053\uff1a\u201c\u4e00\u65a4\u8089\u5c31\u62b5\u8fd9\u4e48\u591a\u3000\u94f6 \u5b50\uff0c\u670b\u53cb\uff0c\u4f60\u7684\u8089\u4e5f\u672a\u514d\u592a\u503c\u94b1\u5427\u3002\u201d \u3000\u3000\u3000\u77f3\u78f7\u95fb\u8a00\u4e5f\u4e00\u60ca\uff0c\u5fd6\u9053\uff1a\u201c\u770b\u4e0d\u51fa\u4ed6\u5012\u6709\u8fd9\u4e48\u58ee\u7684\u80c6\u5b50\u3002\u201d \u3000\u3000\u3000\u679c\u7136\uff0c\u4ed6\u6b64\u8bdd\u4e00\u51fa\uff0c\u7acb\u523b\u5f15\u8d77\u4f17\u6012\uff0c\u6709\u4eba\u7adf\u9a82\u9053\uff1a\u201c\u4f60\u4ed6\u5988\u7684\u662f\u4ec0\u4e48\u4e1c\u897f\uff01\u201d\u3000\u8001\u738b \u62d4\u8d77\u684c\u4e0a\u7684\u5315\u9996\uff0c\u55d6\u5730\u4e00\u4e0b\u5b50\u8df3\u5230\u684c\u9762\u4e0a\uff0c\u53eb\u7740\u9053\uff1a\u201c\u4f60\u8d4c\u4e0d\u8d4c\uff1f\u201d\u5927\u6709\u4f60\u82e5\u3000\u4e0d\u8d4c\uff0c\u6211\u5c31 \u5bb0\u4e86\u4f60\u4e4b\u610f\u3002 \u3000\u3000\u3000\u77f3\u78f7\u6697\u6697\u8d70\u8fd1\u90a3\u5c11\u5e74\uff0c\u4ed6\u5bf9\u8fd9\u5c11\u5e74\u6709\u4e86\u597d\u611f\uff0c\u51c6\u5907\u4e07\u4e00\u6709\u4e8b\uff0c\u4ed6\u5c31\u51fa\u624b\u76f8\u6551\uff0c\u3000\u90a3\u5c11 \u5e74\u5374\u884c\u82e5\u65e0\u4e8b\u4ed6\u8bf4\u9053\uff1a\u201c\u8d4c\u94b1\u8fd8\u6709\u5f3a\u8feb\u7684\u5440\uff0c\u4e0d\u548c\u4f60\u8d4c\uff0c\u4f60\u53c8\u5f53\u600e\u7684\uff0c\u8981\u62fc\u547d\u3000\u5417\uff1f\u201d\u5c45\u7136 \u4e00\u70b9\u513f\u4e5f\u4e0d\u542b\u7cca\u3002 \u3000\u3000\u3000\u77f3\u78f7\u65b9\u624d\u770b\u6765\u770b\u53bb\uff0c\u4e5f\u770b\u4e0d\u51fa\u8fd9\u5c11\u5e74\u8eab\u4e0a\u6709\u534a\u70b9\u7ec3\u5bb6\u5b50\u7684\u7279\u5fbd\uff0c\u4e24\u53ea\u624b\u638c\u53c8\u767d\u3000\u53c8 \u5ae9\uff0c\u50cf\u662f\u4eba\u5bb6\u95fa\u5973\u7684\u624b\uff0c\u6b64\u523b\u89c1\u4ed6\u80c6\u6c14\u5982\u6b64\u4e4b\u8c6a\uff0c\u4e00\u9762\u4e3a\u4ed6\u62c5\u5fc3\uff0c\u4e00\u9762\u4e5f\u89c9\u5f97\u6b64\u3000\u4eba\u53ef\u7231\u5f97 \u5f88\u3002 \u3000\u3000\u3000\u201c\u8001\u738b\u201d\u773c\u775b\u4e00\u77aa\uff0c\u51f6\u5149\u5916\u9732\uff0c\u5389\u559d\u9053\uff1a\u201c\u8001\u5b50\u8ddf\u4f60\u62fc\u4e86\u53c8\u600e\u5730\uff1f\u201d\u4ed6\u867d\u7136\u4e5f\u3000\u770b\u51fa \u8fd9\u5c11\u5e74\u4e3e\u6b62\u4e0d\u51e1\uff0c\u4f3c\u4e4e\u662f\u8c6a\u95e8\u9614\u5c11\uff0c\u4f46\u9047\u5230\u8fd9\u79cd\u72af\u4e86\u6027\u5b50\uff0c\u672c\u662f\u6210\u5e74\u5728\u5200\u5c16\u6253\u3000\u6eda\u7684\u4ea1\u547d\u4e4b \u5f92\uff0c\u4ec0\u4e48\u4e8b\u505a\u4e0d\u51fa\u6765\uff1f \u3000\u3000\u3000\u4ed6\u62ff\u7740\u5315\u9996\u53c8\u4e00\u6bd4\u5212\uff0c\u559d\u9053\uff1a\u201c\u6211\u8d64\u811a\u7684\u8fd8\u6015\u4e86\u4f60\u7a7f\u978b\u7684\u4e0d\u6210\uff1f\u201d\u4f5c\u52bf\u7adf\u8981\u6251\u3000\u4e0a \u53bb\uff0c\u90a3\u5c11\u5e74\u773c\u5149\u4e00\u52a8\uff0c\u50cf\u662f\u4e5f\u6709\u4e9b\u5bb3\u6015\u4e86\uff0c\u540e\u9000\u4e86\u4e24\u6b65\uff0c\u9053\uff1a\u201c\u4f60\u8981\u5f53\u5f3a\u76d7\u5440\uff01\u3000\u201d\u773c\u5149\u5374 \u779f\u7740\u5c4b\u5b50\u7684\u95e8\u3002 \u3000\u3000\u3000\u77f3\u78f7\u6697\u7b11\uff1a\u201c\u8fd9\u79cd\u6587\u5f31\u4e66\u751f\u8fd8\u662f\u7981\u4e0d\u5f97\u552c\u3002\u201d\u5fae\u8fd0\u771f\u6c14\uff0c\u51c6\u5907\u62d4\u5200\u76f8\u52a9\u4e86\u3002 \u3000\u3000\u3000\u8001\u738b\u4e3e\u5200\u4f5c\u52bf\uff0c\u8116\u5b50\u540e\u9762\u5374\u84e6\u5730\u4e00\u7d27\uff0c\u88ab\u4eba\u6349\u4f4f\u8863\u9886\uff0c\u4e00\u628a\u63ea\u4e86\u8fc7\u53bb\uff0c\u5427\u5730\uff0c\u3000\u4ece\u684c \u9762\u4e0a\u63b7\u5230\u5730\u4e0a\uff0c\u8dcc\u5f97\u4ef0\u9762\u671d\u5929\u3002 \u3000\u3000\u3000\u5728\u5730\u4e0a\u6253\u4e86\u4e2a\u6eda\uff0c\u4ed6\u722c\u4e86\u8d77\u6765\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u628a\u8981\u9a82\u51fa\u6765\u7684\u8bdd\u8d76\u7d27\u7f29\u56de\u809a\u91cc\u3002\u77f3\u3000\u78f7\u773c \u5149\u56db\u8f6c\uff0c\u770b\u5230\u4eba\u4eba\u8138\u4e0a\u90fd\u6709\u754f\u60e7\u4e4b\u8272\uff0c\u4e5f\u4e0d\u7981\u7528\u773c\u775b\u53bb\u6253\u91cf\u90a3\u4eba\uff0c\u773c\u5149\u65b9\u81ea\u8f6c\u3000\u5230\u90a3\u4eba\u8eab \u4e0a\uff0c\u53c8\u8d76\u7d27\u8f6c\u8fc7\u5934\u53bb\u3002 \u3000\u3000\u3000\u90a3\u4eba\u662f\u4e2a\u80d6\u5b50\uff0c\u8eab\u6750\u5374\u4e0d\u9ad8\uff0c\u770b\u8d77\u6765\u6574\u4e2a\u4eba\u50cf\u662f\u65b9\u7684\uff0c\u5374\u662f\u9556\u4e1a\u91cc\u7684\u5de8\u5b50\u2014\u2014\u3000\u516b\u9762 \u73b2\u73d1\u80e1\u4e4b\u8f89\uff0c\u4e5f\u5c31\u662f\u5e73\u5b89\u9556\u5c40\u7684\u603b\u9556\u5934\u3002 \u3000\u3000\u3000\u77f3\u78f7\u4e0e\u4ed6\u672c\u662f\u65e7\u8bc6\uff0c\u5bf9\u6b64\u4eba\u5374\u9887\u4e0d\u6b23\u8d4f\uff0c\u7531\u4ed6\u7684\u201c\u516b\u9762\u73b2\u73d1\u201d\u8fd9\u540d\u5b57\u4e0a\u770b\u6765\uff0c\u3000\u5c31\u53ef \u4ee5\u77e5\u9053\u6b64\u4eba\u4e3a\u4eba\u7684\u4f5c\u98ce\uff0c\u800c\u77f3\u78f7\u5374\u662f\u6700\u538c\u6076\u8fd9\u79cd\u4f5c\u98ce\u7684\u3002 \u3000\u3000\u3000\u56e0\u6b64\u4ed6\u8f6c\u8fc7\u5934\uff0c\u4e0d\u613f\u610f\u548c\u4ed6\u62db\u547c\uff0c\u80e1\u4e4b\u8f89\u53e3\u4e2d\u4e00\u9762\u559d\u9053\uff1a\u201c\u4e0d\u6210\u6750\u7684\u8822\u8d27\uff0c\u8f93\u3000\u4e86\u94b1 \u60f3\u800d\u8d56\u5417\uff1f\u201d\u4e00\u9762\u5374\u8d70\u8fc7\u53bb\u5411\u77f3\u78f7\u62db\u547c\u9053\uff1a\u201c\u77f3\u5144\u5f1f\uff0c\u8fd9\u4e48\u4e45\u4e0d\u89c1\u4e86\uff0c\u89c1\u4e86\u6545\u3000\u4eba\u4e4b\u9762\uff0c\u4e5f \u4e0d\u6253\u4e2a\u62db\u547c\uff1f\u201d \u3000\u3000\u3000\u77f3\u78f7\u65e0\u53ef\u5948\u4f55\u5730\u56de\u8fc7\u5934\uff0c\u7b11\u9053\uff1a\u201c\u6211\u5f53\u662f\u8c01\uff1f\u539f\u6765\u662f\u80e1\u5927\u54e5\u3002\u201d \u3000\u3000\u3000\u80e1\u4e4b\u8f89\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u96be\u5f97\uff0c\u96be\u5f97\uff0c\u5144\u5f1f\u4f60\u8fd8\u8bb0\u5f97\u6211\u3002\u201d\u4ed6\u9f3b\u5b50\u4e00\u52a8\u53c8\u7b11\u9053\uff1a\u201c\u3000\u591a\u5e74 \u4e0d\u89c1\uff0c\u5144\u5f1f\u4f60\u8fd8\u662f\u8001\u6837\u5b50\uff0c\u8fd8\u5b66\u4f1a\u4e86\u559d\u9152\uff0c\u597d\u6781\u4e86\uff0c\u4eca\u5929\u6211\u4eec\u53ef\u8981\u559d\u4e0a\u4e24\u676f\u3002\u3000\u201d \u3000\u3000\u3000\u4ed6\u7b11\u58f0\u4e0d\u7edd\uff0c\u53c8\u5411\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u8fd9\u4f4d\u8001\u5f1f\u53f0\u5982\u679c\u4e0d\u5acc\u5f03\u7684\u8bdd\uff0c\u4e5f\u8bf7\u6765\u559d\u4e24\u676f\u7b97\u3000\u662f\u5728 \u4e0b\u5411\u9601\u4e0b\u966a\u7f6a\u597d\u5417\uff1f\u201d \u3000\u3000\u3000\u4ed6\u867d\u7136\u662f\u5f81\u6c42\u522b\u4eba\u540c\u610f\u7684\u8bdd\uff0c\u7136\u800c\u5374\u8bf4\u5f97\u50cf\u522b\u4eba\u5df2\u7b54\u5e94\u4e86\u4f3c\u7684\uff0c\u53c8\u559d\u9053\uff1a\u201c\u66ff\u3000\u8fd9\u4f4d \u76f8\u516c\u5c06\u684c\u4e0a\u7684\u94f6\u5b50\u6536\u8d77\u6765\uff0c\u4ee5\u540e\u4f60\u4eec\u8981\u518d\u50cf\u8fd9\u6837\u80e1\u95f9\uff0c\u6211\u53ef\u5c31\u4e0d\u7b54\u5e94\u4e86\u3002\u201d\u500f\u3000\u7136\u4e4b\u95f4\uff0c\u53c8 \u6362\u4e86\u53e6\u5916\u4e00\u79cd\u9762\u76ee\u8bf4\u8bdd\uff0c\u77f3\u78f7\u6447\u9996\u6697\u53f9\uff1a\u201c\u8fd9\u4eba\u5b9e\u5728\u662f\u6807\u51c6\u7684\u5c0f\u4eba\u3002\u201d \u3000\u3000\u3000\u90a3\u5c11\u5e74\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4e9b\u94f6\u5b50\uff0c\u9601\u4e0b\u62ff\u53bb\u7ed9\u624b\u4e0b\u5f1f\u5144\u5206\u4e86\u5427\uff01\u201d\u80e1\u4e4b\u8f89\u4e00\u3000\u6014\uff0c \u772f\u7740\u773c\u775b\u671d\u90a3\u5806\u94f6\u5b50\u770b\u4e86\u4e00\u773c\uff0c\u90a3\u5e76\u4e0d\u662f\u4e00\u7b14\u5c0f\u6570\u76ee\uff0c\u8fde\u80e1\u4e4b\u8f89\u89c1\u4e86\uff0c\u90fd\u4e0d\u89c9\u3000\u5fc3\u52a8\u3002 \u3000\u3000\u3000\u4ed6\u8f6c\u52a8\u7740\u80d6\u8138\u4e0a\u7684\u7ec6\u5c0f\u773c\u73e0\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u6015\u4e0d\u597d\u610f\u601d\u5427\u3002\u201d\u90a3\u5c11\u5e74\u542b\u7b11\u9053\uff1a\u201c\u3000\u6208\u6208 \u4e4b\u6570\uff0c\u53c8\u7b97\u5f97\u4e86\u4ec0\u4e48\uff0c\u9601\u4e0b\u5343\u4e07\u4e0d\u8981\u5ba2\u6c14\u3002\u201d \u3000\u3000\u3000\u80e1\u4e4b\u8f89\u773c\u73e0\u4e00\u8f6c\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u90a3\u5c31\u606d\u656c\u4e0d\u5982\u4ece\u547d\u4e86\uff0c\u53ea\u662f\u9601\u4e0b\u5374\u4e00\u5b9a\u8981\u8d4f\u5149\u3000\uff0c\u548c \u5728\u4e0b\u5144\u5f1f\u559d\u4e24\u676f\u3002\u201d \u3000\u3000\u3000\u90a3\u5c11\u5e74\u7acb\u523b\u9053\uff1a\u201c\u8fd9\u4e2a\u81ea\u7136\u3002\u201d\u7b54\u5e94\u5f97\u975e\u5e38\u5e72\u8106\uff0c\u50cf\u662f\u5fc3\u91cc\u975e\u5e38\u4e50\u610f\u7684\u6837\u5b50\u3002 \u3000\u3000\u3000\u77f3\u78f7\u4ed4\u7ec6\u6253\u91cf\u8fd9\u5c11\u5e74\uff0c\u89c9\u5f97\u4ed6\u5b9e\u5728\u6709\u8bb8\u591a\u5f02\u5904\uff0c\u50cf\u4ed6\u8fd9\u6837\u5e74\u7eaa\u7684\u4eba\uff0c\u8bf4\u8bdd\u4e3e\u6b62\u3000\u7edd\u4e0d \u8be5\u8fd9\u4e48\u8001\u8fa3\uff0c\u50cf\u662f\u6709\u7740\u5f88\u591a\u5904\u4e16\u7ecf\u9a8c\u4f3c\u7684\u3002 \u3000\u3000\u3000\u4e8e\u662f\u77f3\u78f7\u5f00\u59cb\u5bf9\u8fd9\u5c11\u5e74\u53d1\u751f\u4e86\u5174\u8da3\uff0c\u9042\u4e5f\u6ca1\u6709\u62d2\u7edd\u80e1\u4e4b\u8f89\u7684\u9080\u8bf7\uff0c\u4ea4\u8c08\u4e4b\u4e0b\uff0c\u3000\u90a3\u5c11 \u5e74\u81ea\u7b54\u59d3\u7f2a\uff0c\u540d\u6587\uff0c\u662f\u7ca4\u4e1c\u5546\u4eba\u4e4b\u5b50\uff0c\u6b64\u756a\u662f\u6765\u6c5f\u5357\u5f00\u62d3\u773c\u754c\u7684\u3002\u77f3\u78f7\u5374\u6709\u4e9b\u3000\u6000\u7591\uff0c\u56e0\u4e3a \u4ed6\u5e76\u4e0d\u50cf\u662f\u4e2a\u5546\u4eba\u4e4b\u5b50\uff0c\u518d\u4e00\u6ce8\u610f\uff0c\u7f2a\u6587\u8a00\u8c08\u95ee\u4f3c\u4e4e\u5bf9\u80e1\u4e4b\u8f89\u751a\u4e3a\u62c9\u62e2\u3000\uff0c\u77f3\u78f7\u66f4\u5947\u602a\uff0c\u56e0 \u4e3a\u4ed6\u6ca1\u6709\u62c9\u62e2\u80e1\u4e4b\u8f89\u7684\u5fc5\u8981\uff0c\u4e5f\u4e0d\u4f1a\u4e0e\u8fd9\u6ee1\u8eab\u4e16\u4fd7\u6c14\u7684\u80d6\u5b50\u6c14\u5473\u3000\u76f8\u6295\u7684\u3002 \u3000\u3000\u3000\u80e1\u4e4b\u8f89\u8981\u7f2a\u6587\u548c\u4ed6\u7ed3\u4f34\u800c\u884c\uff0c\u7f2a\u6587\u4e5f\u4e00\u53e3\u7b54\u5e94\u4e86\uff0c\u9762\u4e0a\u4e14\u9732\u51fa\u559c\u8272\uff0c\u77f3\u78f7\u6697\u5730\u3000\u731c \u6d4b\uff0c\u8ba4\u4e3a\u8fd9\u7f2a\u6587\u5fc5\u5b9a\u6709\u7740\u4ec0\u4e48\u4f01\u56fe\uff0c\u53ea\u662f\u4ed6\u4e5f\u4e0d\u77e5\u9053\u8fd9\u5c11\u5e74\u7684\u4f01\u56fe\u7a76\u7adf\u6709\u4e9b\u4ec0\u4e48\u3000\u7528\u610f\u7f62 \u4e86\u3002 \u3000\u3000\u3000\u8fd9\u4e00\u6765\uff0c\u53ef\u628a\u77f3\u78f7\u4e5f\u5438\u5f15\u4f4f\u4e86\uff0c\u4ed6\u840d\u8e2a\u6d6a\u8ff9\uff0c\u672c\u6765\u5c31\u6ca1\u6709\u56fa\u5b9a\u53bb\u5904\uff0c\u7b2c\u4e8c\u65e5\u6e05\u3000\u6668\uff0c \u4e09\u4eba\u7adf\u7ed3\u4f34\u540c\u884c\uff0c\u8ddf\u5728\u4e00\u8fde\u4e32\u9556\u8f66\u540e\u9762\u3002\u542c\u7740\u8d9f\u5b50\u624b\u5639\u4eae\u7684\u547c\u58f0\uff0c\u5728\u6c5f\u5357\u5c71\u6c34\u3000\u4e2d\uff0c\u77f3\u78f7\u4e5f \u4e0d\u89c9\u6709\u813e\u8089\u590d\u751f\u4e4b\u611f\u3002 \u3000\u3000\u3000\u4e09\u4eba\u4e00\u8def\u8c08\u7b11\uff0c\u7f2a\u6587\u4f3c\u4e4e\u5bf9\u6b66\u6797\u4e2d\u4e8b\u9887\u6709\u5174\u8da3\uff0c\u4e00\u8def\u4e0a\u4e0d\u65ad\u5730\u5411\u77f3\u78f7\u548c\u80e1\u4e4b\u8f89\u3000\u8bf7 \u6559\uff0c\u8c08\u8d77\u6b66\u6797\u4eba\u7269\uff0c\u80e1\u4e4b\u8f89\u5c31\u4f38\u8d77\u5927\u59c6\u6307\u9053\uff1a\u201c\u8bba\u5230\u6b66\u6797\u4eba\u7269\uff0c\u9664\u4e86\u6211\u5927\u54e5\u7075\u86c7\u3000\u6bdb\u81ec\u4e4b \u5916\uff0c\u5c31\u4e0d\u4f5c\u7b2c\u4e8c\u4eba\u60f3\u4e86\u3002\u201d \u3000\u3000\u3000\u7f2a\u6587\u8138\u4e0a\u9732\u51fa\u4e00\u4e2a\u5947\u602a\u7684\u7b11\u5bb9\uff0c\u7b11\u9053\uff1a\u201c\u7b2c\u4e8c\u4eba\u6050\u6015\u5c31\u662f\u80e1\u5927\u54e5\u4e86\u5427\u3002\u201d\u80e1\u4e4b\u3000\u8f89\u54c8 \u54c8\u7b11\u9053\uff1a\u201c\u5144\u5f1f\u8fd8\u8c08\u4e0d\u4e0a\u3002\u201d\u5374\u662f\u5f97\u610f\u5f97\u5f88\u3002 \u3000\u3000\u3000\u77f3\u78f7\u51b7\u773c\u65c1\u89c2\uff0c\u8d8a\u6765\u8d8a\u53d1\u73b0\u8fd9\u5c11\u5e74\u7684\u5f02\u5904\u9887\u591a\uff0c\u51fa\u624b\u4e4b\u8c6a\u9614\uff0c\u751f\u50cf\u4ed6\u5bb6\u85cf\u94f6\u5c71\u3000\u4f3c \u7684\uff0c\u80e1\u4e4b\u8f89\u5374\u832b\u7136\uff0c\u53ea\u662f\u4e0d\u65ad\u5730\u5439\u5618\u7740\u6bdb\u81ec\uff0c\u5f53\u7136\uff0c\u4e5f\u4e0d\u65ad\u5730\u5439\u5618\u7740\u81ea\u5df1\uff0c\u7f2a\u6587\u3000\u9762\u5e26\u7b11 \u5bb9\uff0c\u4e5f\u603b\u662f\u7559\u5fc3\u503e\u542c\uff0c\u867d\u7136\u4ed6\u7684\u7b11\u5bb9\u6709\u4e9b\u53e4\u602a\uff0c\u4f46\u77f3\u78f7\u5374\u4e5f\u6ce8\u610f\u5f97\u5230\u3002 \u3000\u3000\u3000\u9556\u8f66\u7531\u9547\u6c5f\u51fa\u57ce\uff0c\u7ecf\u4e39\u9633\u3001\u6b66\u8fdb\u3001\u5f80\u65e0\u9521\u53bb\uff0c\u8fd9\u6c5f\u5357\u66ae\u6625\u7684\u98ce\u5149\uff0c\u7f2a\u6587\u89c1\u4e86\u610f\u3000\u5174\u795e \u9a70\uff0c\u5012\u7684\u786e\u662f\u50cf\u7b2c\u4e00\u6b21\u6765\u5230\u6c5f\u5357\u7684\u6837\u5b50\u3002 \u3000\u3000\u3000\u80e1\u4e4b\u8f89\u50cf\u662f\u5e76\u4e0d\u6025\u7740\u8d76\u8def\uff0c\u5929\u8fd8\u6ca1\u6709\u5165\u9ed1\uff0c\u4ed6\u5c31\u65e9\u65e9\u843d\u5e97\uff0c\u8fd9\u6837\u8d70\u4e86\u4e09\u5929\uff0c\u4e5f\u3000\u6ca1\u6709 \u8d70\u51fa\u591a\u5c11\u8def\u53bb\uff0c\u77f3\u78f7\u5fc3\u91cc\u5947\u602a\uff0c\u6697\u5fd6\uff1a\u201c\u8fd9\u54ea\u91cc\u50cf\u8d70\u9556\u7684\u6837\u5b50\u3002\u201d \u3000\u3000\u3000\u518d\u8fc7\u4e86\u4e00\u5929\uff0c\u77f3\u78f7\u53c8\u53d1\u73b0\u4e86\u4e00\u4ef6\u5947\u4e8b\uff0c\u9556\u8f66\u884c\u65f6\uff0c\u4e24\u65c1\u603b\u6709\u4e9b\u867d\u7136\u7a7f\u7740\u5546\u65c5\u8863\u3000\u670d\uff0c \u4f46\u4e00\u671b\u800c\u77e5\u662f\u7ec3\u5bb6\u5b50\u7684\u4eba\uff0c\u4e0d\u5373\u4e0d\u79bb\u5730\u8ddf\u5728\u65c1\u8fb9\uff0c\u8d77\u5148\uff0c\u4ed6\u8fd8\u4ee5\u4e3a\u8fd9\u4e9b\u662f\u7eff\u6797\u3000\u9053\u8e29\u76d8\u5b50 \u7684\uff0c\u4f46\u540e\u6765\u4e00\u770b\uff0c\u8fd9\u4e9b\u4eba\u867d\u7136\u88c5\u7740\u548c\u80e1\u4e4b\u8f89\u4e0d\u8ba4\u8bc6\u7684\u6837\u5b50\uff0c\u4f46\u6709\u610f\u65e0\u610f\u95f4\u3000\uff0c\u5374\u4e0d\u65ad\u5730\u548c\u80e1 \u4e4b\u8f89\u5728\u6253\u7740\u773c\u8272\uff0c\u6bd4\u7740\u624b\u5f0f\u3002 \u3000\u3000\u3000\u77f3\u78f7\u4e45\u8d70\u6c5f\u80e1\uff0c\u4ec0\u4e48\u4e8b\u6ca1\u89c1\u8fc7\uff0c\u4f46\u6b64\u523b\u7684\u60c5\u5f62\u4ed6\u5374\u6709\u4e9b\u7cca\u6d82\u4e86\uff0c\u4fdd\u9556\u672c\u662f\u5149\u660e\u3000\u6b63\u5927 \u7684\u4e8b\uff0c\u6b64\u523b\u4ed6\u4eec\u5374\u600e\u5730\u5077\u5077\u6478\u6478\u8d77\u6765\u3002 \u3000\u3000\u3000\u9556\u8f66\u79bb\u4e86\u4e39\u9633\u4e4b\u540e\uff0c\u524d\u9762\u5c31\u662f\u4e00\u6bb5\u8f83\u4e3a\u8352\u50fb\u7684\u8e0f\uff0c\u77f3\u78f7\u4ee5\u4e3a\u80e1\u4e4b\u8f89\u4e00\u5b9a\u4f1a\u66f4\u65e9\u3000\u843d \u5e97\uff0c\u54ea\u77e5\u80e1\u4e4b\u8f89\u5374\u4e00\u53cd\u5e38\u6001\uff0c\u7adf\u50ac\u7740\u9556\u4f19\uff0c\u811a\u592b\u8d76\u8d77\u591c\u8def\u6765\u4e86\uff0c\u77f3\u78f7\u8d8a\u53d1\u77e5\u9053\u4e8b\u3000\u6709\u8e22\u8df7\uff0c \u4f46\u5374\u5e76\u4e0d\u8868\u9732\u51fa\u6765\u3002 \u3000\u3000\u3000\u987b\u77e5\u901a\u5e38\u9556\u5c40\u8d70\u9556\u7684\u9053\u7406\uff0c\u5728\u901a\u5546\u8981\u9053\u4e0a\uff0c\u8d76\u8d76\u591c\u8def\u5012\u6ca1\u6709\u4ec0\u4e48\u5173\u7cfb\uff0c\u4f46\u4e00\u5165\u3000\u4e86\u8352 \u51c9\u7684\u5730\u65b9\uff0c\u603b\u662f\u4e58\u4eae\u627e\u5730\u65b9\u6b47\u606f\uff0c\u8fd9\u5f53\u7136\u4e5f\u662f\u9632\u5907\u7eff\u6797\u9053\u670b\u53cb\u7684\u5149\u987e\u3002\u516b\u9762\u73b2\u3000\u73d1\u4e00\u5411\u5c0f\u5fc3 \u8c28\u614e\uff0c\u505a\u4ec0\u4e48\u4e8b\u90fd\u5148\u8981\u77e5\u9053\u5341\u62ff\u4e5d\u7a33\u624d\u80af\u51fa\u624b\uff0c\u6b64\u523b\u6041\u5730\u505a\uff0c\u81ea\u7136\u5947\u602a\u3000\u3002 \u3000\u3000\u3000\u7f2a\u6587\u5374\u5168\u7136\u4e0d\u61c2\u8fd9\u4e9b\uff0c\u9a91\u5728\u9a6c\u4e0a\uff0c\u4ef0\u671b\u5929\u4e0a\u661f\u6597\uff0c\u6781\u9ad8\u5174\u4ed6\u8bf4\u9053\uff1a\u201c\u80e1\u5144\uff0c\u6211\u3000\u4eec\u65e9 \u8be5\u5728\u591c\u95f4\u8d76\u8def\u4e86\uff0c\u4ef0\u89c6\u7e41\u661f\u7693\u6708\uff0c\u4fef\u9006\u6625\u98ce\uff0c\u5c82\u975e\u5feb\u4e8b\uff1f\u201d\u77f3\u78f7\u6697\u53f9\u4e00\u58f0\uff0c\u5fd6\u3000\u9053\uff1a\u201c\u4f60\u771f \u662f\u4e2a\u4ec0\u4e48\u4e8b\u90fd\u4e0d\u61c2\u7684\u516c\u5b50\u54e5\u513f\u3002\u201d \u3000\u3000\u3000\u53c8\u8d70\u4e86\u4e00\u6bb5\u8def\uff0c\u524d\u9762\u9ed1\u9edd\u9edd\u7684\u4e00\u7247\uff0c\u662f\u4e2a\u6811\u6797\u5b50\uff0c\u524d\u884c\u7684\u8d9f\u5b50\u624b\u515c\u56de\u6765\uff0c\u5411\u80e1\u3000\u4e4b\u8f89 \u9053\uff1a\u201c\u524d\u9762\u7684\u9752\u7eb1\u5e10\u5f88\u5bc6\uff0c\u8981\u4e0d\u8981\u5148\u8fdb\u53bb\u8e29\u4e2a\u9053\uff1f\uff0c\uff0c\u80e1\u4e4b\u8f89\u597d\u6574\u4ee5\u6687\u5730\u4e00\u6325\u3000\u9a6c\u97ad\uff0c\u8bf4 \u9053\uff1a\u201c\u4e0d\u5fc5\u4e86\u3002\u201d\u56de\u8fc7\u5934\u5411\u7f2a\u6587\u7b11\u9053\uff1a\u201c\u6211\u505a\u4e8b\u5c31\u662f\u8fd9\u6837\uff0c\u4ece\u6765\u4e0d\u5a46\u5a46\u5988\u3000\u5988\u7684\u987e\u5fcc\u3002\u201d\u7f2a \u6587\u4e00\u4f38\u5927\u59c6\u6307\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u6b63\u662f\u82f1\u96c4\u672c\u8272\u3002\u201d \u3000\u3000\u3000\u8bdd\u58f0\u672a\u4e86\uff0c\u540e\u9762\u7a81\u7136\u4f20\u6765\u4e00\u9635\u6025\u5267\u7684\u8e44\u58f0\uff0c\u77f3\u78f7\u56de\u5934\u53bb\u770b\uff0c\u54ea\u77e5\u90a3\u7fa4\u9a6c\u5374\u4e0d\u662f\u3000\u5411\u8fd9 \u4e2a\u65b9\u5411\u5954\u6765\uff0c\u4f3c\u4e4e\u7ed5\u4e86\u4e00\u4e2a\u5708\u5b50\u3002 \u3000\u3000\u3000\u4ed6\u4e00\u8038\u80a9\uff0c\u6697\u7b11\u81ea\u5df1\u7adf\u6709\u4e9b\u5927\u60ca\u5c0f\u602a\uff0c\u4f46\u968f\u7740\u9556\u8f66\u540e\u9762\u7ecf\u8fc7\u90a3\u9ed1\u9edd\u9edd\u7684\u6811\u6797\u65f6\u3000\uff0c\u4ed6 \u5012\u771f\u6709\u4e9b\u62c5\u5fc3\uff0c\u56e0\u4e3a\u8fd9\u91cc\u7684\u786e\u662f\u7eff\u6797\u670b\u53cb\u51fa\u6ca1\u7684\u597d\u5730\u65b9\uff0c\u6c5f\u5357\u9053\u4e0a\u518d\u60f3\u53e6\u627e\u4e00\u3000\u5904\uff0c\u5374\u4e0d\u592a \u5bb9\u6613\u54e9\uff01 \u3000\u3000\u3000\u4ed6\u4fa7\u76ee\u4e00\u770b\u80e1\u4e4b\u8f89\uff0c\u5728\u8fd9\u79cd\u5149\u7ebf\u4e0b\uff0c\u4ed6\u7684\u8138\u8272\u6839\u672c\u65e0\u6cd5\u770b\u51fa\u6765\uff0c\u4f46\u662f\u4ed6\u7684\u624b\uff0c\u3000\u5374\u6709 \u4e9b\u6296\uff0c\u90a3\u4ece\u88ab\u4ed6\u63e1\u7740\u7684\u7f30\u7ef3\u7684\u98a4\u52a8\u4e0a\u53ef\u4ee5\u770b\u51fa\u6765\u3002 \u3000\u3000\u3000\u201c\u6bd5\u7adf\u4ed6\u8fd8\u662f\u6709\u4e9b\u5bb3\u6015\u7684\u3002\u201d\u77f3\u78f7\u5fd6\u9053\uff0c\u201c\u4f46\u662f\u4ed6\u65e2\u7136\u5bb3\u6015\uff0c\u5374\u53c8\u4e3a\u4ec0\u4e48\u8981\u5982\u3000\u6b64\u505a \u5462\uff1f\u201d\u77f3\u78f7\u82e6\u601d\uff0c\u5374\u4e0d\u5f97\u5176\u89e3\u3002 \u3000\u3000\u3000\u4ed6\u4eec\u6697\u4e2d\u90fd\u634f\u7740\u4e00\u628a\u51b7\u6c57\uff0c\u4f46\u9556\u8f66\u5374\u5e73\u5e73\u5b89\u5b89\u5730\u8d70\u8fc7\u53bb\u4e86\uff0c\u4e00\u70b9\u513f\u4e8b\u4e5f\u6ca1\u6709\u53d1\u3000\u751f\uff0c \u4e00\u8d70\u51fa\u6797\u5b50\uff0c\u80e1\u4e4b\u8f89\u5c31\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u50cf\u662f\u5fc3\u60c5\u5df2\u677e\u61c8\u4e86\uff0c\u4f46\u662f\u5728\u8fd9\u53f9\u606f\u58f0\u4e2d\u3000\uff0c\u5374\u7adf\u4e5f\u9690 \u542b\u7740\u4e00\u4e9b\u5931\u671b\u7684\u610f\u5473\u3002 \u3000\u3000\u3000\u2018\u8fd9\u6811\u6797\u91cc\u53ef\u771f\u95f7\u5f97\u7d27\u3002\u201d\u7f2a\u6587\u7b11\u9053\uff0c\u9a6c\u97ad\u4e00\u6447\uff0c\u97ad\u68a2\u6307\u5411\u524d\u9014\uff0c\u95ee\u9053\uff1a\u201c\u600e\u3000\u5730\u90a3 \u8fb9\u8fd8\u6709\u5c0f\u6811\u6797\u5b50\uff1f\u201d\u77f3\u78f7\u968f\u7740\u4ed6\u7684\u624b\u4e00\u770b\uff0c\u524d\u9762\u679c\u7136\u53c8\u662f\u9ed1\u9edd\u9edd\u5730\u4e00\u7247\uff0c\u4e5f\u50cf\u3000\u662f\u4e2a\u6811\u6797\u7684 \u6837\u5b50\u3002 \u3000\u3000\u3000\u54ea\u77e5\u4ed6\u5ff5\u5934\u5c1a\u672a\u8f6c\u5b8c\uff0c\u90a3\u7247\u201c\u6811\u6797\u5b50\u201d\u7adf\u52a8\u4e86\u8d77\u6765\uff0c\u8e44\u58f0\u7eb7\u6c93\uff0c\u539f\u6765\u524d\u9762\u7adf\u662f\u3000\u4e00\u7fa4 \u4eba\u9a6c\uff0c\u9ed1\u6697\u4e2d\u8fdc\u8fdc\u671b\u53bb\uff0c\u81ea\u7136\u5206\u8fa8\u4e0d\u6e05\u3002 \u3000\u3000\u3000\u7f2a\u6587\u7b11\u9053\uff1a\u201c\u539f\u6765\u6211\u770b\u9519\u4e86\u3002\u201d\u77f3\u78f7\u5374\u5728\u62c5\u5fc3\uff0c\u9ed1\u6697\u4e4b\u4e2d\uff0c\u805a\u7740\u8fd9\u4e48\u4e9b\u4eba\uff0c\u9664\u3000\u4e86\u4e0a \u7ebf\u5f00\u6252\uff0c\u8fd8\u6709\u4ec0\u4e48\u522b\u7684\u610f\u601d\uff1f \u3000\u3000\u3000\u4ed6\u6709\u4e9b\u4e3a\u96be\uff0c\u5047\u5982\u771f\u9047\u4e0a\u4e86\u4e8b\uff0c\u4ed6\u5012\u6709\u4e9b\u8fdb\u9000\u7ef4\u8c37\uff0c\u82e5\u662f\u5e2e\u80e1\u4e4b\u8f89\u7684\u5fd9\uff0c\u4ed6\u89c9\u3000\u5f97\u6709 \u4e9b\u4e0d\u503c\u5f97\uff0c\u82e5\u662f\u4e0d\u5e2e\u5462\uff1f\u81ea\u5df1\u548c\u4eba\u5bb6\u5230\u5e95\u662f\u4e00\u8def\uff0c\u4eba\u5bb6\u9047\u4e0a\u4e8b\uff0c\u81ea\u5df1\u8896\u624b\u65c1\u89c2\u3000\uff0c\u5728\u60c5\u5728\u7406 \u90fd\u8bf4\u4e0d\u8fc7\u53bb\u3002 \u3000\u3000\u3000\u90a3\u7fa4\u4eba\u9a6c\u6765\u5230\u8fd1\u524d\uff0c\u5373\u500f\u7136\u800c\u4f4f\uff0c\u4f46\u5947\u602a\u7684\u662f\u8fd9\u4e9b\u4eba\u7adf\u4e0d\u53bb\u7406\u4f1a\u524d\u9762\u8d70\u7740\u7684\u9556\u3000\u8f66\uff0c \u800c\u901e\u76f4\u8d70\u5230\u516b\u9762\u73d1\u73d1\u80e1\u4e4b\u8f89\u7684\u9762\u524d\u3002 \u3000\u3000\u3000\u80e1\u4e4b\u8f89\u6717\u58f0\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5f1f\u5144\u4eec\u8f9b\u82e6\u4e86\u3002\u201d\u90a3\u4e9b\u4eba\u54c4\u7136\u9053\uff1a\u201c\u80e1\u4e09\u54e5\uff0c\u8fd9\u662f\u4ec0\u3000\u4e48 \u8bdd\u3002\u201d\u80e1\u4e4b\u8f89\u9053\uff1a\u201c\u90a3\u53eb\u91d1\u5251\u4fa0\u7684\u5c0f\u5b50\uff0c\u8fd9\u6b21\u5c45\u7136\u6ca1\u6709\u6765\uff0c\u4e5f\u7b97\u4ed6\u8d70\u8fd0\u4e86\u3002\u201d\u4ed6\u3000\u957f\u957f\u4e00 \u7b11\uff0c\u53c8\u9053\uff1a\u201c\u4e0a\u6b21\u6c5f\u5b81\u5e9c\u7684\u2018\u5357\u79c0\u9556\u5c40\uff0c\u662f\u4e0d\u662f\u5c31\u5728\u8fd9\u91cc\u51fa\u7684\u4e8b\uff1f\u201d\u4e00\u4eba\u7b54\u3000\u9053\uff1a\u201c\u4e00\u70b9\u4e5f \u4e0d\u9519\uff0c\u5c31\u5728\u8fd9\u6811\u6797\u5b50\u91cc\u3002\u201d \u3000\u3000\u3000\u4ed6\u4eec\u4e00\u95ee\u4e00\u7b54\uff0c\u77f3\u78f7\u604d\u7136\u5927\u609f\uff1a\u201c\u539f\u6765\u4ed6\u4eec\u8fd9\u662f\u505a\u597d\u7684\u5708\u5957\uff0c\u6765\u8bf1\u90a3\u2018\u91d1\u5251\u4fa0\u3000\u2019\u5165 \u5f40\u7684\u3002\u6211\u5012\u662f\u53c8\u4f5c\u4e86\u675e\u4eba\u4e4b\u5fe7\u4e86\u3002\u201d \u3000\u3000\u3000\u80e1\u4e4b\u8f89\u53c8\u9053\uff1a\u201c\u524d\u9014\u60f3\u5df2\u4e0d\u4f1a\u6709\u4e8b\uff0c\u660e\u65e5\u665a\u95f4\u5c31\u53ef\u5230\u4e86\uff0c\u5404\u4f4d\u65e0\u4e8b\uff0c\u4e0d\u59a8\u968f\u5144\u3000\u5f1f\u6211 \u5230\u65e0\u9521\uff0c\u5c06\u9556\u4ea4\u5f85\u4e86\u5927\u4f19\u513f\u75db\u996e\u4e00\u573a\u3002\u201d \u3000\u3000\u3000\u90a3\u7fa4\u4eba\u5171\u6709\u4e5d\u9a91\uff0c\u4e2a\u4e2a\u90fd\u662f\u7a84\u8170\u718a\u81c2\u7684\u7cbe\u58ee\u6c49\u5b50\uff0c\u4e24\u53ea\u773c\u775b\u5728\u9ed1\u6697\u4e2d\uff0c\u81ea\u7136\u4e00\u3000\u95ea\u4e00 \u95ea\u5730\uff0c\u663e\u89c1\u5f97\u90fd\u662f\u6b66\u529f\u4e0d\u5f31\u7684\u7ec3\u5bb6\u5b50\u3002 \u3000\u3000\u3000\u90a3\u4e3a\u9996\u4e00\u4eba\uff0c\u8eab\u6750\u7626\u524a\uff0c\u53cc\u76ee\u795e\u91c7\u66f4\u662f\u593a\u4eba\uff0c\u5728\u9a6c\u4e0a\u4e00\u62b1\u62f3\uff0c\u7b11\u9053\uff1a\u201c\u80e1\u4e09\u54e5\u3000\u7684\u76db \u60c5\uff0c\u5c0f\u5f1f\u4eec\u5fc3\u9886\u4e86\uff0c\u53ea\u662f\u5c0f\u5f1f\u4eec\u5374\u8981\u9a6c\u4e0a\u8d76\u56de\u53bb\uff0c\u6bdb\u5927\u54e5\u6050\u6015\u8fd8\u53e6\u6709\u5dee\u9063\u5462\uff01\u3000\u201d\u80e1\u4e4b\u8f89 \u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u7b11\u9053\uff1a\u201c\u6bdb\u5927\u54e5\u5982\u6709\u4e8b\uff0c\u5f1f\u5144\u4eec\u8fd8\u662f\u8d76\u7d27\u56de\u53bb\uff0c\u53ef\u5343\u4e07\u522b\u3000\u5fd8\u4e86\u4ee3\u6211\u95ee\u5927\u54e5 \u7684\u597d\u3002\u201d\u90a3\u7fa4\u9a91\u58eb\u54c4\u7136\u79f0\u662f\uff0c\u53c8\u6709\u4eba\u9053\uff1a\u201c\u8981\u4e0d\u8981\u6211\u4eec\u5148\u5c06\u80e1\u4e09\u54e5\u3000\u9001\u5230\u5730\u5934\u518d\u56de\u53bb\uff1f\u201d\u80e1 \u4e4b\u8f89\u7b11\u9053\uff1a\u201c\u5f1f\u5144\u4eec\u628a\u54e5\u54e5\u6211\u770b\u5f97\u592a\u4e0d\u503c\u94b1\u5566\uff0c\u524d\u9762\u90a3\u4e00\u70b9\u3000\u513f\u8def\uff0c\u96be\u9053\u6211\u8fd8\u95ef\u4e0d\u8fc7\u53bb\uff1f\u201d \u3000\u3000\u3000\u90a3\u7fa4\u9a91\u58eb\u54c4\u7136\u58f0\u4e2d\uff0c\u8d76\u7740\u9a6c\u4ece\u53e6\u4e00\u65b9\u5411\u8d70\u4e86\u3002\u80e1\u4e4b\u8f89\u5f97\u610f\u5730\u6325\u52a8\u7740\u624b\u4e2d\u7684\u9a6c\u97ad\u3000\uff0c\u7b11 \u9053\uff1a\u201c\u5728\u6c5f\u5357\u8def\u4e0a\uff0c\u6709\u4eba\u60f3\u52a8\u6211\u5144\u5f1f\u7684\u9556\uff0c\u90a3\u62db\u5b50\u662f\u592a\u4e0d\u4eae\u5566\u3002\u201d\u77f3\u78f7\u7b11\u95ee\u9053\u3000\uff1a\u201c\u90a3\u4e9b\u9a91 \u58eb\u662f\u8c01\uff1f\u201d \u3000\u3000\u3000\u201c\u7eb5\u6a2a\u6c5f\u6e56\u7684\u2018\u94c1\u9a91\u795e\u97ad\u961f\uff0c\uff0c\u5c31\u662f\u6211\u90a3\u73ed\u5f1f\u5144\u4e86\u3002\u201d\u80e1\u4e4b\u8f89\u5f97\u610f\u4ed6\u8bf4\uff0c\u4fa7\u76ee\u3000\u56de \u5934\uff0c\u8be7\u7136\u95ee\u9053\uff1a\u201c\u7f2a\u6587\u7f2a\u5144\u5f1f\u5462\uff1f\u201d \u3000\u3000\u3000\u77f3\u78f7\u4e00\u770b\uff0c\u672c\u6765\u59cb\u7ec8\u5750\u5728\u9a6c\u4e0a\u5fae\u7b11\u7684\u7f2a\u6587\uff0c\u6b64\u523b\u679c\u7136\u4e0d\u77e5\u53bb\u5411\u4e86\uff0c\u4ed6\u4e00\u60ca\uff0c\u7f2a\u3000\u6587\u624b \u65e0\u7f1a\u9e21\u4e4b\u529b\uff0c\u5728\u8fd9\u9ed1\u591c\u8352\u6797\u4e2d\u8d70\u5931\u4e86\uff0c\u5012\u7684\u786e\u53ef\u8651\uff0c\u4e0d\u7981\u76b1\u7740\u7709\u9053\uff1a\u201c\u6211\u4e5f\u6ca1\u3000\u6709\u6ce8\u610f\u5230 \u4ed6\u3002\u201d\u60f3\u5230\u7f2a\u6587\u4e00\u8def\u4e0a\u5750\u5728\u9a6c\u4e0a\u6447\u6643\u4e0d\u5b9a\u7684\u6837\u5b50\uff0c\u53cc\u7709\u4e0d\u7981\u76b1\u5f97\u66f4\u7d27\u3002 \u3000\u3000\u3000\u201c\u7f2a\u5144\u4e0d\u5584\u9a91\u9a6c\uff0c\u8eab\u4f53\u53c8\u5355\u8584\uff0c\u5982\u679c\u51fa\u4e86\u4e8b\uff0c\u5012\u771f\u662f\u6211\u4eec\u7684\u8fc7\u5931\u3002\u201d \u3000\u3000\u3000\u4ed6\u4e0d\u7981\u6709\u4e9b\u540e\u6094\uff0c\u65b9\u624d\u6ce8\u610f\u529b\u90fd\u653e\u5728\u90a3\u73ed\u9a91\u58eb\u8eab\u4e0a\uff0c\u7adf\u6ca1\u6709\u770b\u5230\u7f2a\u6587\u7684\u52a8\u6001\u3002\u3000\u80e1\u4e4b \u8f89\u4e5f\u6709\u4e9b\u7740\u6025\uff0c\u9053\uff1a\u201c\u77f3\u5144\u5f1f\uff0c\u6211\u4eec\u53bb\u627e\u627e\u4ed6\u53bb\u3002\u201d\u77f3\u78f7\u55d6\u5730\u4e0b\u4e86\u9a6c\uff0c\u5411\u6797\u4e2d\u3000\u63a0\u53bb\u3002 \u3000\u3000\u3000\u4ed6\u4eec\u4e24\u4eba\u5c55\u5f00\u8eab\u6cd5\uff0c\u5728\u9644\u8fd1\u63a0\u4e86\u534a\u5708\uff0c\u84e6\u5730\u542c\u5230\u51e0\u58f0\u8fde\u7eed\u7684\u60e8\u547c\uff0c\u77f3\u78f7\u9762\u8272\u7a81\u3000\u53d8\uff0c \u4f4e\u559d\u9053\uff1a\u201c\u80e1\u5144\uff0c\u5feb\u8fc7\u53bb\u770b\u770b\uff01\u201d \u3000\u3000\u3000\u4ed6\u731b\u4e00\u957f\u8eab\uff0c\u63a0\u8d77\u5982\u96c1\uff0c\u80e1\u4e4b\u8f89\u4e5f\u8ddf\u4e86\u4e0a\u53bb\uff0c\u5728\u8fd9\u79cd\u5730\u65b9\uff0c\u5c31\u53ef\u4ee5\u770b\u51fa\u77f3\u78f7\u6b66\u3000\u5f53\u5ae1 \u4f20\u7684\u5fc3\u6cd5\u679c\u81ea\u4e0d\u51e1\uff0c\u55d6\uff0c\u55d6\u3001\u51e0\u4e2a\u8d77\u843d\uff0c\u5df2\u5c06\u516b\u9762\u73b2\u73d1\u80e1\u4e4b\u8f89\u4e22\u4e0b\u4e86\u4e00\u7bad\u591a\u5730\u3000\u3002\u80e1\u4e4b\u8f89\u6025 \u547c\uff1a\u201c\u77f3\u5144\u5f1f\u6162\u4e9b\u3002\u201d \u3000\u3000\u3000\u77f3\u78f7\u5fc3\u4e2d\u7126\u6025\uff0c\u5c55\u5f00\u201c\u516b\u6b65\u8d76\u8749\u201d\u7684\u7edd\u9876\u8f7b\u529f\uff0c\u5728\u8fd9\u5bc6\u6797\u91cc\u641c\u7d22\u7740\u60e8\u547c\u53d1\u751f\u7684\u3000\u539f \u56e0\uff0c\u80e1\u4e4b\u8f89\u8eab\u5f62\u867d\u81c3\u80bf\uff0c\u4f46\u4ed6\u5728\u6b66\u6797\u4e2d\u4ea6\u9887\u6709\u58f0\u540d\uff0c\u8f7b\u529f\u4ea6\u4e0d\u5f31\uff0c\u7d27\u8ddf\u5728\u540e\u9762\uff0c\u3000\u5374\u542c\u5f97\u77f3 \u78f7\u4e5f\u53d1\u51fa\u4e00\u58f0\u60ca\u547c\u3002 \u3000\u3000\u3000\u80e1\u4e4b\u8f89\u60f3\u62c9\u62e2\u8fd9\u4e00\u63b7\u5343\u91d1\u65e0\u541d\u8272\u7684\u5bcc\u5bb6\u516c\u5b50\u2014\u2014\u7f2a\u6587\uff0c\u542c\u5230\u77f3\u78f7\u7684\u60ca\u547c\uff0c\u4ee5\u4e3a\u3000\u7f2a\u6587 \u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\uff0c\u55d6\u5730\uff0c\u4e5f\u8ddf\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u3000\u4ed6\u770b\u7740\u77f3\u78f7\u53d1\u6115\u5730\u80cc\u7740\u4ed6\u7ad9\u7740\uff0c\u518d\u4e00\u7eb5\u8eab\uff0c\u770b\u5230\u5730\u4e0a\u7684\u666f\u51b5\uff0c\u4e5f\u4e0d\u7531\u53d1\u51fa\u4e00\u58f0\u3000\u60e8 \u547c\uff0c\u771f\u6c14\u731b\u4e00\u6da3\u6563\uff0c\u7adf\u4e0d\u80fd\u518d\u63a0\u8d77\u8eab\u5f62\uff0c\u9893\u7136\u843d\u5728\u5730\u4e0a\u3002 \u3000\u3000\u3000\u5730\u4e0a\u51cc\u4e71\u5730\u8eba\u7740\u4e5d\u5177\u5c38\u8eab\uff0c\u5374\u6b63\u662f\u90a3\u7fa4\u201c\u94c1\u9a91\u795e\u97ad\u961f\u3002\u201d\u80e1\u4e4b\u8f89\u9762\u5982\u6b7b\u7070\uff0c\u4f4e\u3000\u8bed \u9053\uff1a\u201c\u8fd9\u2026\u2026\u8fd9\u2026\u2026\u201d\u4e0b\u9762\u7684\u8bdd\u7adf\u8bf4\u4e0d\u4e0b\u53bb\u3002 \u3000\u3000\u3000\u6709\u5177\u5c38\u8eab\u4f4e\u5fae\u5730\u547b\u541f\u4e86\u4e00\u4e0b\uff0c\u60f3\u662f\u8fd8\u6ca1\u6709\u5b8c\u5168\u6c14\u7edd\uff0c\u80e1\u4e4b\u8f89\u500f\u7136\u63a0\u8fc7\u53bb\uff0c\u4fef\u8eab\u3000\u7740\u6025 \u4ed6\u8bf4\u9053\uff1a\u201c\u600e\u4e48\u56de\u4e8b\uff1f\u600e\u4e48\u56de\u4e8b\u2014\u2014\u201d\u90a3\u4eba\u773c\u775b\u5df2\u7a81\u51fa\u7736\u5916\uff0c\u6ee1\u9762\u4ff1\u662f\u60ca\u60e7\u4e4b\u3000\u8272\uff0c\u5f20\u5f00 \u5634\uff0c\u60f3\u8bf4\u4ec0\u4e48\uff0c\u4f46\u4e00\u53e3\u6c14\u63d0\u4e0d\u4e0a\u6765\uff0c\u773c\u76ae\u4e00\u7ffb\uff0c\u4e5f\u81ea\u6c14\u7edd\u4e86\u3002 \u3000\u3000\u3000\u80e1\u4e4b\u8f89\u60e8\u7136\u56de\u987e\uff0c\u8fd9\u4e9b\u7075\u86c7\u6bdb\u81ec\u7684\u6b7b\u58eb\uff0c\u7eb5\u6a2a\u6c5f\u6e56\u7684\u201c\u94c1\u9a91\u795e\u97ad\u961f\u201d\u91cc\u7684\u4e5d\u4e2a\u3000\u597d \u624b\uff0c\u7adf\u5728\u8fd9\u4e00\u6bb5\u6781\u77ed\u7684\u65f6\u95f4\u91cc\uff0c\u540c\u65f6\u88ab\u4eba\u6740\u4e86\uff0c\u7adf\u6ca1\u6709\u4e00\u4e2a\u6d3b\u53e3\u3002 \u3000\u3000\u3000\u516b\u9762\u73b2\u73d1\u7f13\u7f13\u7ad9\u4e86\u8d77\u6765\uff0c\u4ef0\u5929\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u60e8\u7136\u9053\uff1a\u201c\u8fd9\u4f1a\u662f\u4ec0\u4e48\u4eba\uff1f\u96be\u9053\u53c8\u3000\u662f \u2018\u91d1\u5251\u4fa0\u2019\u5417\uff1f\u201d\u4ed6\u6df1\u77e5\u8fd9\u4e9b\u201c\u94c1\u9a91\u795e\u97ad\u9a91\u58eb\u201d\u7684\u6b66\u529f\uff0c\u4f46\u5c45\u7136\u7adf\u5728\u540c\u65f6\u88ab\u6740\uff0c\u3000\u7b80\u76f4\u6709\u4e9b \u532a\u5937\u6240\u601d\u3002 \u3000\u3000\u3000\u77f3\u78f7\u4e5f\u4fef\u4e0b\u8eab\uff0c\u5c06\u5c38\u8eab\u642c\u8d77\u6765\u770b\u4e86\u770b\uff0c\u8eab\u4e0a\u7adf\u6ca1\u6709\u4e00\u5904\u4f24\u75d5\uff0c\u518d\u770b\u522b\u4eba\uff0c\u4e5f\u662f\u3000\u4e00 \u6837\uff0c\u8fd9\u4e5d\u4eba\u7adf\u662f\u88ab\u4eba\u70b9\u4e86\u6781\u91cd\u7684\u7a74\u9053\u800c\u6bd9\u547d\u7684\uff0c\u6709\u4eba\u624b\u4f38\u5728\u8170\u95f4\uff0c\u50cf\u662f\u60f3\u64a4\u51fa\u8170\u3000\u4e2d\u7684\u957f \u97ad\uff0c\u4f46\u97ad\u5c1a\u672a\u64a4\u51fa\uff0c\u5df2\u81ea\u88ab\u5236\uff0c\u77f3\u78f7\u4e5f\u4e0d\u7981\u957f\u5618\u4e86\u4e00\u53e3\u6c14\uff0c\u6697\u5fd6\uff1a\u201c\u5f53\u4eca\u6b66\u3000\u6797\u4e2d\uff0c\u80fd\u6709\u8fd9 \u79cd\u8eab\u624b\u7684\u4eba\uff0c\u4f1a\u662f\u8c01\u5462\uff1f\u201d\u4e8e\u662f\u4ed6\u66ff\u81ea\u5df1\u89e3\u91ca\u7740\uff1a\u201c\u8fd9\u4e5f\u8bb8\u4e0d\u662f\u4e00\u4e2a\u3000\u4eba\u5e72\u7684\uff0c\u5047\u5982\u662f\u4e5d\u4eba \u4e00\u9f50\u4e0b\u624b\uff0c\u6765\u5bf9\u4ed8\u8fd9\u4e5d\u4e2a\u9a91\u58eb\uff0c\u90a3\u4e48\u8fd9\u4ef6\u4e8b\u5c31\u53ef\u4ee5\u89e3\u91ca\u4e86\u3002\u201d \u3000\u3000\u3000\u80e1\u4e4b\u8f89\u5931\u53bb\u4e86\u8138\u4e0a\u60ef\u6709\u7684\u7b11\u5bb9\uff0c\u6115\u4e86\u8bb8\u4e45\uff0c\u7a81\u5730\u795e\u667a\u4e00\u52a8\uff0c\u5fd9\u559d\u9053\uff1a\u201c\u77f3\u5144\u5f1f\u3000\uff0c\u5feb \u8d70\uff01\u201d\u8eab\u5f62\u500f\u7136\u7a9c\u4e86\u51fa\u53bb\uff0c\u4ed6\u6015\u4e2d\u4e86\u522b\u4eba\u8c03\u864e\u79bb\u5c71\u4e4b\u8ba1\uff0c\u81ea\u5df1\u8dd1\u5230\u8fd9\u91cc\uff0c\u4eba\u5bb6\u3000\u5374\u53bb\u52ab\u9556 \u4e86\u3002 \u3000\u3000\u3000\u662f\u4ee5\u4ed6\u8d76\u7d27\u8d76\u53bb\uff0c\u4ed6\u5374\u672a\u60f3\u5230\uff0c\u6b64\u4eba\u82e5\u8981\u52ab\u4ed6\u7684\u9556\uff0c\u5c31\u7b97\u4ed6\u4eba\u5728\u90a3\u91cc\uff0c\u53c8\u6709\u4f55\u3000\u7528\uff1f \u50cf\u4ed6\u8fd9\u4ed8\u8eab\u624b\uff0c\u6bd4\u8d77\u4eba\u5bb6\u6765\uff0c\u8fd8\u5dee\u5f97\u8fdc\u5462\u3002 \u3000\u3000\u3000\u80e1\u4e4b\u8f89\u8eab\u5f62\u66b4\u9000\uff0c\u51e0\u4e2a\u8d77\u843d\uff0c\u77f3\u78f7\u5df2\u8ffd\u4e0a\u4e86\uff0c\u4e24\u4eba\u5e76\u80a9\u63a0\u51fa\u6797\u5916\uff0c\u6797\u5916\u7684\u9556\u8f66\u3000\u4ecd\u5b89 \u9759\u5730\u6392\u5217\u5728\u9ed1\u591c\u91cc\uff0c\u4e00\u4eba\u9053\uff1a\u201c\u4e24\u4f4d\u5144\u53f0\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d\u77f3\u78f7\u4e00\u770b\uff0c\u90a3\u4eba\u4e0d\u662f\u3000\u5931\u8e2a\u4e86\u7684\u7f2a \u6587\u662f\u8c01\uff1f \u3000\u3000\u3000\u77f3\u78f7\u8fde\u5fd9\u63a0\u4e86\u8fc7\u53bb\uff0c\u9053\uff1a\u201c\u7f2a\u5144\u5230\u90a3\u91cc\u53bb\u4e86\uff1f\u5012\u6559\u5c0f\u5f1f\u7740\u6025\u3002\u201d \u3000\u3000\u3000\u8bed\u58f0\u867d\u662f\u57cb\u6028\uff0c\u4f46\u5374\u6709\u7740\u5341\u5206\u771f\u5b9e\u7684\u53cb\u60c5\uff0c\u7f2a\u6587\u7684\u8138\u8272\uff0c\u5728\u591c\u8272\u4e2d\u4e0d\u5b89\u5730\u53d8\u52a8\u3000\u4e86\u4e00 \u4e0b\uff0c\u4f3c\u4e4e\u4e5f\u88ab\u8fd9\u4efd\u53cb\u60c5\u6240\u52a8\u3002 \u3000\u3000\u3000\u4f46\u662f\u4ed6\u7acb\u5373\u6062\u590d\u4e86\u7b11\u5bb9\uff0c\u8fd9\u5e74\u8f7b\u7684\u5c11\u5e74\u50cf\u662f\u51c6\u5907\u5c06\u6240\u6709\u7684\u60c5\u611f\u90fd\u57cb\u85cf\u8d77\u6765\u4f3c\u7684\u3000\uff0c\u6de1 \u7136\u7b11\u9053\uff1a\u201c\u4e0d\u7792\u5144\u53f0\u8bf4\uff0c\u5c0f\u5f1f\u5b9e\u5728\u4e0d\u80fd\u9a91\u9a6c\uff0c\u8fd9\u51e0\u5929\u6765\u4e24\u6761\u811a\u9178\u75bc\u4e0d\u5df2\uff0c\u4eca\u5929\u3000\u8d76\u4e86\u8fd9\u4e48\u591a \u8def\uff0c\u66f4\u662f\u96be\u53d7\uff0c\u65b9\u624d\u4e58\u7a7a\u53bb\u6e9c\u8fbe\u4e86\u4e00\u4e0b\uff0c\u73b0\u5728\u5012\u89c9\u597d\u4e9b\u4e86\u3002\u201d \u3000\u3000\u3000\u77f3\u78f7\u4e00\u7b11\uff0c\u60f3\u8d77\u4ee5\u524d\u4ed6\u662f\u5750\u8f7f\u5b50\uff0c\u9053\uff1a\u201c\u5bf9\u6781\uff01\u5bf9\u6781\uff01\u201d\u4eba\u5bb6\u65e0\u8bba\u8bf4\u4ec0\u4e48\u8bdd\uff0c\u3000\u4ed6\u603b \u662f\u9644\u548c\uff0c\u81f3\u4e8e\u4ed6\u5fc3\u91cc\u5728\u60f3\u7740\u4ec0\u4e48\uff0c\u90a3\u5374\u53ea\u6709\u4ed6\u81ea\u5df1\u77e5\u9053\u4e86\u3002 \u3000\u3000\u3000\u80e1\u4e4b\u8f89\u4e5f\u8d70\u4e86\u8fc7\u6765\uff0c\u8fde\u58f0\u9053\uff1a\u201c\u5e78\u597d\u9556\u8f66\u65e0\u4e8b\uff0c\u6211\u4eec\u5feb\u4e9b\u79bb\u5f00\u8fd9\u662f\u975e\u4e4b\u5730\u5427\u3002\u3000\u201d\u5bf9 \u90a3\u4e5d\u5177\u5c38\u8eab\uff0c\u7adf\u7f6e\u4e4b\u4e0d\u7406\u4e86\uff0c\u77f3\u78f7\u5fc3\u4e2d\u4e00\u5bd2\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u516b\u9762\u73b2\u73d1\u7684\u786e\u662f\u4e2a\u53ea\u3000\u987e\u81ea\u5df1\uff0c\u81ea \u79c1\u81ea\u5229\u7684\u5c0f\u4eba\u3002\u201d \u3000\u3000\u3000\u4f46\u662f\u4ed6\u5374\u4e0d\u8bf4\u4ec0\u4e48\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u4ed6\u5df2\u517b\u6210\u4e86\u8fd9\u79cd\u813e\u6c14\uff0c\u6709\u4e9b\u8bdd\u4ed6\u8ba4\u4e3a\u4e0d\u503c\u5f97\u8bf4\u3000\u7684\uff0c \u4ed6\u5c31\u4e0d\u8bf4\uff0c\u6709\u4e9b\u4e8b\u4ed6\u8ba4\u4e3a\u4e0d\u503c\u5f97\u505a\u7684\uff0c\u4ed6\u5c31\u4e0d\u505a\uff0c\u5c11\u5e74\u65f6\u7684\u4efb\u6c14\uff0c\u73b0\u5728\u4ed6\u5df2\u6d88\u3000\u78e8\u6b86\u5c3d\u4e86\u3002 \u3000\u3000\u3000\u9556\u8f66\u7acb\u523b\u8d77\u884c\uff0c\u4e0d\u5230\u4e00\u4e2a\u65f6\u8fb0\uff0c\u5c31\u8d76\u5230\u524d\u9014\u7684\u4e00\u4e2a\u5c0f\u9547\u4e0a\uff0c\u80e1\u4e4b\u8f89\u5df2\u662f\u60ca\u5f13\u4e4b\u3000\u9e1f\uff0c \u8d76\u7d27\u843d\u5e97\uff0c\u8fd8\u62db\u547c\u9556\u4f19\uff0c\u4e0d\u51c6\u559d\u9152\u95f9\u4e8b\uff0c\u77f3\u78f7\u6697\u7b11\uff1a\u201c\u8fd9\u5927\u6982\u662f\u4ed6\u7b2c\u4e00\u6b21\u53d1\u51fa\u3000\u8fd9\u547d\u4ee4 \u5427\u3002\u201d \u3000\u3000\u3000\u80e1\u4e4b\u8f89\u53eb\u522b\u4eba\u4e0d\u559d\u9152\uff0c\u4ed6\u81ea\u5df1\u53ef\u8fd8\u662f\u7167\u559d\u4e0d\u8bef\uff0c\u5728\u8fd9\u5c0f\u9547\u4e0a\u3002 \u3000\u3000\u3000\u53c8\u8fd9\u4e48\u665a\u4e86\uff0c\u54ea\u91cc\u627e\u5f97\u5230\u4ec0\u4e48\u5403\u98df\uff0c\u4ed6\u80e1\u4e71\u5f04\u4e86\u4e9b\u8c46\u5e72\u3001\u82b1\u751f\u7c73\u3001\u9e2d\u5934\u4e4b\u7c7b\u7684\u3000\u4e1c\u897f \u6765\uff0c\u6311\u4eae\u4e86\u706f\uff0c\u62c9\u7740\u77f3\u78f7\u548c\u7f2a\u6587\u95f2\u8c08\u3002 \u3000\u3000\u3000\u7f2a\u6587\u770b\u7740\u90a3\u4e9b\u98df\u7269\u7b11\u4e86\u7b11\uff0c\u8d77\u8eab\u51fa\u53bb\u8f6c\u4e86\u4e00\u8d9f\uff0c\u53c8\u56de\u6765\u5750\u4e0b\u4e86\u62ff\u8d77\u9152\u6765\u6d45\u6d45\u555c\u3000\u7740\uff0c \u5012\u662f\u4e0d\u574f\u7684\u7af9\u53f6\u9752\uff0c\u4e0d\u4e00\u4f1a\uff0c\u5e97\u91cc\u7684\u5c0f\u4e8c\u7aef\u8fdb\u4e24\u4e2a\u76d8\u5b50\u6765\uff0c\u80e1\u4e4b\u8f89\u4e00\u770b\uff0c\u76d8\u5b50\u3000\u91cc\u7adf\u662f\u4e24\u53ea \u70e7\u9e21\u3002 \u3000\u3000\u3000\u77f3\u78f7\u6697\u5fd6\uff1a\u201c\u8fd9\u7f2a\u6587\u5012\u662f\u61c2\u5f97\u82b1\u94b1\u7684\u4eba\u3002\u201d\u80e1\u4e4b\u8f89\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fd8\u662f\u7f2a\u6587\u5144\u5f1f\u3000\u6709\u529e \u6cd5\u3002\u201d\u6495\u5f00\u4e00\u53ea\u9e21\u817f\uff0c\u5927\u5403\u8d77\u6765\uff0c\u5bf9\u65b9\u624d\u90a3\u4e5d\u5177\u9762\u5e26\u60ca\u6050\u7684\u5c38\u8eab\uff0c\u4f3c\u4e4e\u5df2\u7ecf\u5fd8\u3000\u5f97\u5e72\u5e72\u51c0 \u51c0\u3002 \u3000\u3000\u3000\u77f3\u78f7\u5374\u5fd8\u4e0d\u4e86\uff0c\u95ee\u9053\uff1a\u201c\u90a3\u2018\u94c1\u9a91\u795e\u97ad\u961f\u2019\u7684\u5927\u540d\uff0c\u5c0f\u5f1f\u8fd1\u5e74\u6765\u4e5f\u5e38\u542c\u5230\u8fc7\uff0c\u3000\u636e\u8bf4 \u795e\u97ad\u9a91\u58eb\uff0c\u6b66\u529f\u4e2a\u4e2a\u4e0d\u5f31\uff0c\u800c\u4e14\u662f\u652f\u6b63\u4e49\u4e4b\u519b\uff0c\u4e13\u95e8\u6392\u89e3\u6c5f\u6e56\u4e0a\u7684\u7ea0\u7eb7\uff0c\u6b64\u523b\u3000\u600e\u5730\u4e00\u201d\u4ed6 \u6b62\u4f4f\u4e86\u8bdd\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u5982\u679c\u518d\u8bf4\u4e0b\u53bb\uff0c\u5c31\u4f1a\u4f24\u53ca\u522b\u4eba\u7684\u989c\u9762\u3002 \u3000\u3000\u3000\u7f2a\u6587\u4f3c\u4e4e\u975e\u5e38\u597d\u5947\u5730\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u662f\u2018\u94c1\u9a91\u795e\u97ad\u2019\u5440\uff1f\u201d\u80e1\u4e4b\u8f89\u6b64\u65f6\u5df2\u6709\u4e9b\u91ba\u3000\u7136\uff0c \u7b11\u9053\uff1a\u201c\u8fd9\u2018\u94c1\u9a91\u795e\u97ad\u961f\u2019\uff0c\u5728\u6b66\u6797\u4e2d\u771f\u53ef\u8bf4\u5f97\u4e0a\u662f\u8d6b\u8d6b\u6709\u540d\uff0c\u5168\u961f\u4e00\u767e\u4e8c\u5341\u3000\u4e2a\u9a91\u58eb\u4e0d \u8bf4\uff0c\u961f\u957f\u5c31\u662f\u5f53\u4eca\u6b66\u6797\u7684\u7b2c\u4e00\u53f7\u82f1\u96c4\u2014\u2014\u6211\u7684\u6bdb\u5927\u54e5\u3002\u201d \u3000\u3000\u3000\u4ed6\u5f97\u610f\u5730\u5927\u7b11\u4e86\u51e0\u58f0\uff0c\u7a81\u7136\u60f3\u5230\u8fd9\u201c\u8d6b\u8d6b\u6709\u540d\u201d\u7684\u795e\u97ad\u961f\uff0c\u4eca\u591c\u5df2\u4e0d\u660e\u4e0d\u767d\u5730\u3000\u6b7b\u4e86 \u4e5d\u4e2a\uff0c\u5f97\u610f\u7684\u8bdd\uff0c\u518d\u4e5f\u8bf4\u4e0d\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u3000\u5929\u65f6\u672c\u665a\uff0c\u4ed6\u4eec\u6311\u706f\u591c\u8c08\uff0c\u65f6\u95f4\u8fc7\u53bb\u771f\u5feb\uff0c\u7f2a\u6587\u7684\u8138\u8272\u5728\u4e8c\u66f4\u65f6\u4f3c\u4e4e\u7565\u4e3a\u53d8\u4e86\u3000\u4e00 \u4e0b\uff0c\u4f46\u77ac\u5373\u6062\u590d\u5e38\u6001\uff0c\u80e1\u4e4b\u8f89\u5374\u5df2\u6c89\u6c89\u5927\u9189\uff0c\u7f2a\u6587\u548c\u77f3\u78f7\u4e5f\u50cf\u6709\u4e86\u516b\u5206\u9189\u610f\uff0c\u8bdd\u3000\u90fd\u8bf4\u4e0d\u5468 \u5168\u4e86\u3002 \u3000\u3000\u3000\u7b2c\u4e8c\u5929\u65e9\u4e0a\uff0c\u8fd9\u5c0f\u9547\u7adf\u53d1\u751f\u4e86\u4e00\u4ef6\u5947\u4e8b\uff0c\u8fd9\u4ef6\u5947\u4e8b\u4f7f\u5f97\u5c0f\u9547\u4e0a\u8d2b\u82e6\u7684\u4eba\u4eec\uff0c\u8138\u3000\u4e0a\u6cdb \u8d77\u591a\u5e74\u6765\u672a\u6709\u7684\u7b11\u5bb9\uff0c\u7136\u800c\u80e1\u4e4b\u8f89\u5728\u542c\u5230\u8fd9\u4ef6\u5947\u4e8b\u4e4b\u540e\uff0c\u4e0d\u4f46\u9152\u610f\u5b8c\u5168\u6d88\u9000\uff0c\u3000\u591a\u5e74\u6765\u672a\u66fe \u6d41\u4e0b\u7684\u773c\u6cea\uff0c\u90fd\u51e0\u4e4e\u6d41\u4e86\u51fa\u6765\u3002 \u3000\u3000\u3000\u539f\u6765\u8fd9\u5c0f\u9547\u5927\u5927\u5c0f\u5c0f\u7684\u8857\u9053\u4e0a\uff0c\u9ad8\u9ad8\u4f4e\u4f4e\u7684\u8352\u5730\u91cc\uff0c\u9694\u4e0d\u4e86\u591a\u8fdc\u5c31\u6709\u4e00\u952d\u4e94\u5341\u3000\u4e24\u91cd \u7684\u5143\u5b9d\uff0c\u603b\u7b97\u8d77\u6765\uff0c\u7adf\u6709\u5341\u4e07\u4e24\u3002 \u3000\u3000\u3000\u770b\u5230\u8fd9\u94f6\u5b50\u7684\u4eba\uff0c\u51c6\u4e0d\u8d76\u5feb\u6361\u56de\u5bb6\u53bb\uff1f\u8fd9\u4ef6\u5947\u4e8b\u7acb\u523b\u54c4\u4f20\u5168\u9547\uff0c\u5bb3\u5f97\u6ca1\u6709\u6361\u5230\u3000\u94f6\u5b50 \u7684\u4eba\uff0c\u4eca\u540e\u51e0\u5e74\u8fde\u8d70\u8def\u90fd\u4e0d\u6562\u62ac\u5934\uff0c\u56e0\u4e3a\u6015\u9519\u8fc7\u6361\u94f6\u5b50\u7684\u673a\u4f1a\uff0c\u6709\u4e00\u4e2a\u79c0\u624d\uff0c\u3000\u6b64\u540e\u5341\u5e74\u91cc \u7adf\u5728\u5730\u4e0a\u6361\u5230\u4e03\u5341\u4e5d\u679a\u5236\u94b1\uff0c\u516b\u767e\u4e8c\u5341\u516d\u4e2a\u94ae\u5b50\uff0c\u4e00\u767e\u4e09\u5341\u4e03\u4e2a\u6247\u7a57\uff0c\u3000\u5f04\u5f97\u80cc\u4e5f\u5f2f\u4e86\uff0c\u4f46 \u5374\u518d\u4e5f\u6ca1\u6709\u6361\u5230\u4e94\u5341\u4e24\u4e00\u952d\u7684\u5143\u5b9d\uff0c\u95f2\u8a00\u8868\u8fc7\u4e0d\u63d0\u3002 \u3000\u3000\u3000\u80e1\u4e4b\u8f89\u542c\u4e86\u8fd9\u201c\u5947\u4e8b\u201d\uff0c\u5413\u5f97\u7acb\u523b\u4ece\u5e8a\u4e0a\u8df3\u4e86\u8d77\u6765\uff0c\u8d76\u5230\u653e\u94f6\u9798\u7684\u623f\u95f4\u91cc\uff0c\u94f6\u3000\u9798\u4ecd \u5728\uff0c\u4f46\u91cc\u9762\u7684\u94f6\u5b50\uff0c\u5374\u4e00\u952d\u4e5f\u6ca1\u6709\u4e86\u3002 \u3000\u3000\u3000\u4ed6\u4eff\u4f5b\u88ab\u66b4\u96f7\u6240\u8f70\uff0c\u5468\u8eab\u90fd\u8f6f\u4e86\u4e0b\u6765\uff0c\u4fa7\u9996\u4e00\u671b\uff0c\u770b\u5b88\u94f6\u9798\u7684\u9556\u4f19\uff0c\u501a\u5728\u5899\u4e0a\u3000\u6c89\u6c89 \u7761\u719f\u4e86\uff0c\u8d70\u8fc7\u53bb\u201c\u556a\uff01\u201d\u201c\u556a\uff01\u201d\u6253\u4e86\u4e24\u4e2a\u8033\u5149\uff0c\u5374\u53d1\u73b0\u8fd9\u4e9b\u9556\u4f19\u90fd\u662f\u88ab\u4eba\u70b9\u3000\u4e86\u7761\u7a74\uff0c\u518d \u4e00\u770b\uff0c\u5899\u89d2\u91d1\u5149\u707f\u70c2\uff0c\u63a0\u8fc7\u53bb\uff0c\u53d6\u8d77\u4e00\u770b\uff0c\u90a3\u7adf\u662f\u4e00\u679d\u7eaf\u91d1\u6253\u9020\u7684\u5c0f\u5251\u3000\u3002 \u3000\u3000\u3000\u5341\u4e07\u4e24\u94f6\u5b50\uff0c\u5728\u4e00\u591c\u4e4b\u4e2d\u5c3d\u6570\u5931\u8e2a\uff0c\u800c\u4e14\u5df2\u5206\u522b\u6536\u5230\u8fd9\u5c0f\u9547\u91cc\u6bcf\u4e00\u5bb6\u4eba\u5bb6\u6700\u4e0b\u3000\u9762\u7684 \u90a3\u53e3\u7bb1\u5b50\u91cc\uff0c\u518d\u4e5f\u522b\u60f3\u62ff\u5f97\u56de\u6765\u4e86\u3002 \u7b2c\u4e94\u7ae0>> \u53e4\u9f99\u300a\u6e58\u5983\u5251\u300b \u7b2c\u4e94\u7ae0 \u3000\u3000\u3000\u90a3\u662f\u4e00\u6280\u901a\u4f53\u7eaf\u91d1\u6253\u5c31\u7684\u5c0f\u5251\uff0c\u957f\u4e0d\u8fc7\u4e94\u5bf8\uff0c\u5f62\u5f0f\u5947\u53e4\uff0c\u4eff\u4f5b\u662f\u4e00\u67c4\u540d\u5251\u7684\u96cf\u3000\u578b\uff0c \u5251\u67c4\u4e0a\u7528\u8bf8\u8272\u7684\u4e1d\u5e26\u6253\u4e86\u4e2a\u5982\u610f\u7ed3\u3002\u770b\u8d77\u6765\uff0c\u8fd9\u50cf\u662f\u4e2a\u5bcc\u8d35\u4eba\u5bb6\u5c0f\u5b69\u5b50\u7684\u73a9\u7269\u3000\uff0c\u8c01\u77e5\u9053\u8fd9 \u5374\u662f\u4ee4\u6b66\u6797\u9707\u60ca\u7684\u4e00\u4ef6\u8868\u8bb0\u3002 \u3000\u3000\u3000\u516b\u9762\u73b2\u73d1\u80e1\u4e4b\u8f89\u6014\u6014\u5730\u6367\u7740\u8fd9\u67c4\u201c\u91d1\u5251\u201d\u56de\u5230\u623f\u91cc\uff0c\u5341\u4e07\u4e24\u5b98\u94f6\u4e22\u4e86\uff0c\u5e73\u5b89\u9556\u3000\u5c40\u5341 \u5e74\u6765\u8f9b\u82e6\u521b\u7acb\u7684\u5a01\u540d\uff0c\u4e5f\u968f\u7740\u8fd9\u5341\u4e07\u4e24\u9556\u94f6\u65ad\u9001\uff0c\u80e1\u4e4b\u8f89\u7684\u5fc3\u50cf\u662f\u521a\u7531\u51b7\u6c34\u91cc\u3000\u635e\u51fa\u6765\uff0c\u6f6e \u6e7f\u800c\u51b0\u51c9\u3002 \u3000\u3000\u3000\u4ed6\u56de\u5230\u623f\u91cc\uff0c\u77f3\u78f7\u548c\u7f2a\u6587\u90fd\u5df2\u8d77\u6765\uff0c\u4ed6\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5b8c\u4e86\uff0c\u5b8c\u4e86\u3002\u201d\u5c06\u90a3\u3000\u67c4\u91d1 \u5251\u4e22\u5230\u684c\u5b50\u4e0a\uff0c\u7f2a\u6587\u8d70\u8fc7\u53bb\u62ff\u8d77\u6765\uff0c\u8fb9\u770b\u8fb9\u95ee\u9053\uff1a\u201c\u8fd9\u4e0d\u5c31\u662f\u90a3\u2018\u91d1\u5251\u4fa0\u2019\u7684\u3000\u8868\u8bb0\u5417\uff1f\u201d \u3000\u3000\u3000\u77f3\u78f7\u770b\u7740\u80e1\u4e4b\u8f89\u90a3\u79cd\u5782\u5934\u4e27\u6c14\u7684\u6837\u5b50\uff0c\u5fc3\u91cc\u5df2\u660e\u767d\u4e86\u516b\u4e5d\u5206\uff0c\u4f46\u5374\u4e0d\u80af\u76f8\u4fe1\u5730\u3000\u95ee \u9053\uff1a\u201c\u6628\u591c\u6709\u4ec0\u4e48\u4e8b\u6545\u5417\uff1f\u201d \u3000\u3000\u3000\u80e1\u4e4b\u8f89\u5782\u7740\u5934\u8bf4\u4e86\uff0c\u77f3\u78f7\u4e0d\u89c9\u9a87\u7136\uff0c\u4ed6\u4eec\u90fd\u5750\u5728\u8fd9\u623f\u5b50\u91cc\uff0c\u90bb\u5c4b\u7684\u4eba\u88ab\u70b9\u4e86\u7a74\u3000\uff0c\u5341 \u4e07\u4e24\u94f6\u5b50\u88ab\u4eba\u642c\u8d70\uff0c\u4ed6\u4eec\u5374\u8fde\u5f71\u5b50\u90fd\u4e0d\u77e5\u9053\uff0c\u77f3\u78f7\u53c8\u4e0d\u89c9\u6709\u4e9b\u60ed\u6127\uff0c\u5728\u623f\u91cc\u8e31\u3000\u7740\u65b9\u6b65\uff0c\u4e5f \u8bb2\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u3000\u9556\u8f66\u90fd\u53c8\u4e0a\u9053\u4e86\uff0c\u7136\u800c\u5374\u662f\u4f4f\u56de\u8d70\u4e86\uff0c\u8d9f\u5b50\u624b\u4e0d\u518d\u558a\u9556\uff0c\u8eb2\u5728\u8f66\u8f95\u91cc\u7f29\u7740\uff0c\u9556\u3000\u65d7\u4e5f \u5377\u6210\u4e00\u5377\uff0c\u6536\u5230\u7bb1\u5b50\u91cc\u53bb\u4e86\u3002 \u3000\u3000\u3000\u80e1\u4e4b\u8f89\u65e0\u7cbe\u6253\u91c7\u5730\u9a91\u5728\u9a6c\u4e0a\uff0c\u5439\u725b\u7684\u8bdd\uff0c\u4e00\u53e5\u4e5f\u8bf4\u4e0d\u51fa\u6765\uff0c\u77f3\u78f7\u4e5f\u6709\u4e9b\u8baa\u8baa\u5730\u3000\uff0c\u4ed6 \u662f\u6b66\u6797\u4e2d\u6210\u540d\u4eba\u7269\uff0c\u8fd9\u4ef6\u4e8b\u53d1\u751f\u65f6\u4ed6\u4e5f\u5728\u573a\uff0c\u81ea\u7136\u4e5f\u8fde\u7740\u4e22\u4e86\u9762\u5b50\uff0c\u7f2a\u6587\u5374\u4ecd\u3000\u5e26\u7740\u6ee1\u9762\u7b11 \u5bb9\uff0c\u6309\u8bf4\u6b64\u523b\u4ed6\u8be5\u79bb\u53bb\u624d\u662f\uff0c\u4f46\u4ed6\u5374\u63d0\u4e5f\u4e0d\u63d0\uff0c\u4ecd\u7136\u8ddf\u5728\u65c1\u8fb9\u3002 \u3000\u3000\u3000\u4ed6\u4e0d\u8bf4\u8d70\uff0c\u77f3\u78f7\u81ea\u4e5f\u4e0d\u4fbf\u8d70\u4e86\uff0c\u5728\u8fd9\u79cd\u60c5\u5f62\u4e0b\uff0c\u53ef\u7684\u786e\u6709\u4e9b\u4e0d\u597d\u53d7\u3002 \u3000\u3000\u3000\u8d70\u4e86\u4e24\u5929\uff0c\u53c8\u56de\u5230\u5f80\u9547\u6c5f\u5e9c\u7684\u5b98\u9053\u4e0a\uff0c\u80e1\u4e4b\u8f89\u679c\u7136\u4e0d\u6127\u516b\u9762\u73b2\u73d1\uff0c\u5c45\u7136\u53c8\u6709\u8bf4\u3000\u6709\u7b11 \u8d77\u6765\uff0c\u5bf9\u7f2a\u6587\u62c9\u62e2\u5f97\u66f4\u5389\u5bb3\uff0c\u539f\u6765\u4ed6\u5fc3\u91cc\u6253\u7740\u5982\u610f\u7b97\u76d8\uff0c\u60f3\u628a\u90a3\u5931\u53bb\u7684\u5341\u4e07\u4e24\u3000\u9556\u94f6\u7740\u843d\u5728 \u8fd9\u201c\u8c6a\u95e8\u9614\u5c11\u201d\u8eab\u4e0a\u3002 \u3000\u3000\u3000\u8fdb\u4e86\u9547\u6c5f\u5e9c\uff0c\u4ed6\u4eec\u4ecd\u5728\u90a3\u5bb6\u5ba2\u6808\u4f4f\u4e0b\uff0c\u80e1\u4e4b\u8f89\u5374\u53eb\u9556\u4f19\u4eec\u62bc\u7740\u7a7a\u9556\u8f66\u5148\u56de\u53bb\u4e86\u3000\uff0c\u4ed6 \u5706\u6ed1\u5730\u8fd0\u7528\u8d77\u4e16\u6545\u7684\u624b\u8155\uff0c\u7ed3\u4ea4\u90a3\u521d\u51fa\u8305\u5e90\u7684\u7f2a\u6587\uff0c\u77f3\u78f7\u51b7\u773c\u65c1\u89c2\uff0c\u55e4\u4e4b\u4ee5\u9f3b\u3000\u800c\u5df2\u3002 \u3000\u3000\u3000\u9664\u4e86\u6b66\u6797\u638c\u6545\u4e4b\u5916\uff0c\u4ed6\u8fd8\u8bf4\u4e9b\u98ce\u82b1\u96ea\u6708\uff0c\u7f2a\u6587\u5e26\u7740\u7b11\u5bb9\u542c\u7740\uff0c\u77f3\u78f7\u5374\u6e10\u6e10\u4e0d\u8010\u3000\uff0c\u6f2b \u6b65\u884c\u51fa\u53bb\uff0c\u5374\u53c8\u770b\u5230\u4e00\u4ef6\u5947\u4e8b\u3002 \u3000\u3000\u3000\u4ed6\u521a\u8d70\u5230\u5ba2\u6808\u95e8\u53e3\uff0c\u56db\u5339\u5065\u9a6c\u98de\u9a70\u800c\u6765\uff0c\u5728\u5ba2\u6808\u524d\u500f\u5730\u4e0b\u9a6c\uff0c\u8eab\u624b\u77eb\u5065\u5df2\u6781\uff0c\u3000\u77f3\u78f7 \u6697\u5fd6\uff1a\u201c\u6c5f\u5357\u6b66\u6797\uff0c\u679c\u7136\u4eba\u6750\u6d4e\u6d4e\u3002\u201d \u3000\u3000\u3000\u9a6c\u4e0a\u7684\u9a91\u58eb\u4e00\u8272\u91d1\u8272\u7d27\u8eab\u8863\u88e4\uff0c\u663e\u5f97\u975e\u5e38\u523a\u773c\uff0c\u4e0b\u9a6c\u540e\u5374\u4e0d\u7acb\u5373\u5165\u5e97\uff0c\u6574\u4e86\u6574\u3000\u8863 \u886b\uff0c\u7adf\u5728\u5ba2\u6808\u95e8\u53e3\u8083\u7acb\u7740\uff0c\u77f3\u78f7\u53c8\u5947\u602a\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u6084\u6084\u8d70\u5230\u67dc\u53f0\u540e\u9762\u3000\uff0c\u9887\u4e3a\u6ce8 \u610f\u5730\u770b\u7740\u3002 \u3000\u3000\u3000\u7247\u65f6\u8857\u4e0a\u53c8\u5954\u6765\u56db\u9a91\u5065\u9a6c\uff0c\u5728\u8857\u4e0a\u7684\u4eba\u7fa4\u4e2d\uff0c\u4efb\u610f\u9a70\u9a8b\uff0c\u5374\u53c8\u5de7\u5999\u5730\u907f\u5f00\u5c06\u8981\u3000\u88ab\u4ed6 \u4eec\u649e\u5012\u7684\u4eba\uff0c\u9a6c\u4e0a\u529f\u592b\u6781\u9ad8\u3002 \u3000\u3000\u3000\u4ed6\u4eec\u4e5f\u5728\u5ba2\u6808\u95e8\u53e3\u505c\u4e0b\uff0c\u4e5f\u4e0b\u4e86\u9a6c\uff0c\u539f\u5148\u90a3\u56db\u4e2a\u91d1\u8272\u9a91\u58eb\u8fce\u4e86\u4e0a\u53bb\uff0c\u516b\u4e2a\u4eba\u7565\u3000\u4e3a\u5600 \u5495\u4e86\u4e00\u4e0b\uff0c\u4ecd\u7136\u672a\u8fdb\u5e97\uff0c\u7ad9\u5728\u95e8\u53e3\u3002 \u3000\u3000\u3000\u77f3\u78f7\u5c06\u8eab\u8eaf\u66f4\u7ad9\u540e\u4e86\u4e9b\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u8fd9\u4e9b\u4eba\u4e00\u5b9a\u6709\u5173\u4ec0\u4e48\u79d8\u5bc6\u7684\u5e2e\u4f1a\uff0c\u800c\u8fd9\u5e2e\u3000\u4f1a\u91cc \u7684\u4e00\u5207\u63aa\u65bd\uff0c\u5374\u662f\u6700\u5fcc\u5916\u4eba\u5077\u7aa5\u5e72\u9884\u7684\u3002 \u3000\u3000\u3000\u5c11\u65f6\uff0c\u8857\u4e0a\u53c8\u5954\u6765\u4e00\u5339\u5065\u9a6c\uff0c\u77f3\u78f7\u4e00\u770b\u4fbf\u77e5\u9053\u4ed6\u548c\u5148\u524d\u90a3\u516b\u4eba\u6709\u5173\uff0c\u56e0\u4e3a\u4ed6\u4e5f\u3000\u662f\u91d1 \u8272\u8863\u886b\uff0c\u6700\u602a\u7684\u662f\uff0c\u4ed6\u53cc\u624b\u5e76\u672a\u7275\u7740\u9a6c\u7f30\uff0c\u5374\u6367\u7740\u4e00\u4e2a\u9ed1\u7f0e\u5305\u88b1\uff0c\u53ea\u9760\u4e24\u6761\u817f\u3000\u9a7e\u5fa1\u7740\u9a6c\uff0c \u5374\u4ecd\u6f47\u6d12\u81ea\u5982\u3002 \u3000\u3000\u3000\u4ed6\u4e5f\u5728\u5ba2\u6808\u524d\u505c\u4f4f\u4e86\uff0c\u8eab\u5f62\u4e00\u98d8\uff0c\u5df2\u4e0b\u4e86\u9a6c\uff0c\u77f3\u78f7\u6697\u6697\u559d\u5f69\u3002 \u3000\u3000\u3000\u201c\u597d\u5feb\u7684\u8eab\u624b\u3002\u201d \u3000\u3000\u3000\u4ed6\u7a7f\u7684\u5374\u662f\u91d1\u8272\u957f\u886b\uff0c\u5e74\u7eaa\u4e0d\u5927\uff0c\u9762\u8c8c\u82f1\u4fca\uff0c\u4e24\u53ea\u773c\u775b\u5fae\u5fae\u4e0a\u7ffb\uff0c\u5e26\u7740\u4e00\u80a1\u50b2\u3000\u6c14\uff0c \u90a3\u516b\u4e2a\u91d1\u8863\u58ee\u6c49\u606d\u8c28\u5730\u8fce\u4e86\u4e0a\u53bb\uff0c\u66ff\u4ed6\u63a5\u8fc7\u4e86\u9a6c\uff0c\u4ed6\u5374\u6367\u7740\u90a3\u9ed1\u7f0e\u5305\u88b1\uff0c\u5f84\u76f4\u3000\u8d70\u5165\u5e97\u91cc\u3002 \u3000\u3000\u3000\u5e97\u4f19\u4eec\u8fde\u5fd9\u8fce\u4e0a\u53bb\uff0c\u5bf9\u4ed6\u4f3c\u4e4e\u4e5f\u606d\u8c28\u5f97\u5f88\uff0c\u77f3\u78f7\u6697\u5fd6\uff1a\u201c\u8fd9\u53ae\u662f\u4f55\u6765\u8def\uff1f\u201d \u3000\u3000\u3000\u672c\u6709\u51e0\u4e2a\u770b\u6765\u4e5f\u662f\u6b66\u6797\u4eba\u7269\u7684\u58ee\u6c49\u7ad9\u5728\u8d70\u9053\u4e0a\u95f2\u8c08\u7740\uff0c\u770b\u5230\u8fd9\u91d1\u886b\u5c11\u5e74\u6765\u4e86\uff0c\u3000\u90fd\u8fdc \u8fdc\u907f\u5f00\uff0c\u800c\u4e14\u8eac\u8eab\u4e3a\u793c\uff0c\u8138\u4e0a\u5e26\u7740\u60ca\u6050\u4e4b\u8272\u3002 \u3000\u3000\u3000\u91d1\u886b\u5c11\u5e74\u770b\u4e5f\u4e0d\u770b\u4ed6\u4eec\u4e00\u773c\uff0c\u7b14\u76f4\u5730\u8d70\u8fdb\u5e97\u91cc\uff0c\u770b\u7740\u4ed6\u7684\u80cc\u5f71\uff0c\u8d70\u8def\u65f6\u811a\u4e0d\u6cbe\u3000\u5c18\uff0c \u4e0a\u8eab\u52a8\u4e5f\u4e0d\u52a8\uff0c\u6b66\u529f\u5f53\u7136\u6781\u9ad8\uff0c\u6697\u53f9\u5fd6\u9053\uff1a\u201c\u5c11\u5e74\u4eba\u6043\u6280\u800c\u9a84\uff0c\u603b\u4e0d\u662f\u4ef6\u597d\u4e8b\u3000\u3002\u201d \u3000\u3000\u3000\u90a3\u516b\u4e2a\u91d1\u886b\u58ee\u6c49\u4e5f\u8ddf\u7740\u8d70\u8fdb\uff0c\u72e0\u72e0\u6253\u91cf\u4e86\u77f3\u78f7\u51e0\u773c\uff0c\u77f3\u78f7\u4e0d\u613f\u60f9\u4e8b\uff0c\u8d70\u56de\u623f\u53bb\u3000\uff0c\u5728 \u9662\u5b50\u91cc\uff0c\u5374\u770b\u5230\u90a3\u505a\u5cb8\u7684\u91d1\u886b\u5c11\u5e74\u5728\u548c\u80e1\u4e4b\u8f89\u8bf4\u8bdd\u3002\u91d1\u886b\u5c11\u5e74\u7684\u624b\u7b14\u76f4\u5730\u5411\u524d\u3000\u4f38\u7740\uff0c\u624b\u4ecd \u6367\u7740\u90a3\u9ed1\u7f0e\u5305\u88b1\u3002 \u3000\u3000\u3000\u7f2a\u6587\u4e5f\u7ad9\u5728\u65c1\u8fb9\uff0c\u5e26\u7740\u4ed6\u60ef\u6709\u7684\u7b11\u5bb9\uff0c\u80e1\u4e4b\u8f89\u4f3c\u4e4e\u5df2\u4e3a\u4ed6\u4eec\u5f15\u89c1\u8fc7\u4e86\uff0c\u77f3\u78f7\u4e0d\u3000\u613f\u610f \u591a\u565c\u55e6\uff0c\u6b63\u60f3\u8d70\u5f00\uff0c\u80e1\u4e4b\u8f89\u5374\u9ad8\u58f0\u5524\u9053\uff1a\u201c\u77f3\u8001\u5f1f\u8bf7\u8fc7\u6765\uff0c\u6211\u66ff\u4f60\u5f15\u89c1\u4e00\u4f4d\u5c11\u3000\u5e74\u82f1\u96c4\u3002\u201d \u77f3\u78f7\u65e0\u5948\uff0c\u53ea\u5f97\u8d70\u8fc7\u53bb\uff0c\u80e1\u4e4b\u8f89\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u5c31\u662f\u6b66\u5f53\u540d\u5251\u5ba2\u77f3\u78f7\u77f3\u5927\u3000\u4fa0\u3002\u201d\u77f3\u78f7\u4e00\u70b9 \u5934\uff0c\u671b\u89c1\u90a3\u91d1\u886b\u5c11\u5e74\u53ea\u5fae\u5fae\u4e00\u7b11\uff0c\u4ecd\u5e26\u7740\u90a3\u80a1\u50b2\u6c14\u3002 \u3000\u3000\u3000\u80e1\u4e4b\u8f89\u53c8\u6307\u7740\u90a3\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u5c31\u662f\u6211\u6bdb\u5927\u54e5\u7684\u9ad8\u8db3\uff0c\u6c5f\u6e56\u95fb\u540d\u7684\u2018\u7389\u9aa8\u4f7f\u3000\u8005\uff0c \u4e2d\u7684\u7b2c\u4e8c\u4f4d\uff0c\u7389\u9762\u4f7f\u8005\u5e9e\u58eb\u6e5b\u3002\u201d\u77f3\u78f7\u5fc3\u4e2d\u6709\u6c14\uff0c\u4e5f\u53ea\u5fae\u5fae\u4e00\u7b11\uff0c\u4e5f\u6545\u610f\u5e26\u7740\u3000\u4e00\u4e9b\u90a3\u79cd\u50b2 \u6c14\u3002\u5e9e\u58eb\u6e5b\u8138\u8272\u7acb\u5373\u53d8\u4e86\u4e00\u4e0b\uff0c\u516b\u9762\u73b2\u73d1\u8d76\u7d27\u7b11\u9053\uff1a\u201c\u8d24\u4f84\u6b64\u6b21\u5e26\u7740\u2018\u3000\u6b8b\u9aa8\u4ee4\uff0c\uff0c\u611a\u53d4\u5012 \u6b63\u597d\u6d3e\u4e0a\u4e86\u7528\u573a\uff0c\u78b0\u89c1\u8d24\u4f84\uff0c\u771f\u662f\u597d\u6781\u4e86\u3002\u201d\u5e9e\u58eb\u6e5b\u6b63\u60f3\u7b54\u8a00\uff0c\u3000\u7f2a\u6587\u5374\u63d2\u53e3\u95ee\u9053\uff1a\u201c\u8fd9\u5c31 \u662f\u2018\u6b8b\u9aa8\u4ee4\u2019\u5417\uff1f\u201d \u3000\u3000\u3000\u77f3\u78f7\u4fa7\u76ee\u4e00\u671b\uff0c\u770b\u5230\u7f2a\u6587\u8138\u4e0a\u7684\u808c\u8089\u597d\u50cf\u8d77\u4e86\u4e00\u79cd\u4e0d\u81ea\u7136\u7684\u626d\u66f2\uff0c\u624b\u638c\u4e5f\u7d27\u7d27\u3000\u63e1\u5728 \u4e00\u8d77\uff0c\u5fc3\u4e2d\u4e0d\u7981\u52a8\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u3000\u7389\u9762\u4f7f\u8005\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u5bf9\u4ed6\u4f3c\u4e4e\u4e5f\u5e76\u65e0\u6076\u611f\uff0c\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u8fd9\u5c31\u662f\u2018\u3000\u6b8b\u9aa8 \u4ee4\uff0c\u3002\u201d\u5fae\u4e00\u505c\u987f\uff0c\u63a5\u7740\u80e1\u4e4b\u8f89\u7684\u8bdd\u9898\u9053\uff0c\u201c\u80e1\u4e09\u53d4\u8981\u8fd9\u2019\u6b8b\u9aa8\u4ee4\u2019\u7528\uff0c\u83ab\u975e\u3000\u51fa\u4e86\u4ec0\u4e48\u4e8b \u5417\uff1f\u201d\u80e1\u4e4b\u8f89\u8bf4\u4e86\uff0c\u5e9e\u58eb\u6e5b\u4e24\u9053\u5251\u7709\u7d27\u7d27\u76b1\u5728\u4e00\u8d77\uff0c\u9053\uff1a\u201c\u5bb6\u5e08\u6b64\u6b21\u547d\u3000\u5c0f\u4f84\u5e26\u8fd9\u2018\u6b8b\u9aa8 \u4ee4\u2019\u6765\u6b64\uff0c\u4e3a\u7684\u4e5f\u662f\u8fd9\u2018\u91d1\u5251\u4fa0\uff0c\u4e00\u4eba\uff0c\u80e1\u4e09\u53d4\u4f60\u53ef\u77e5\u9053\uff0c\u4e3a\u4e86\u5bf9\u3000\u4ed8\u8fd9\u2019\u91d1\u5251\u4fa0\u2019\uff0c\u6614\u5e74 \u7684\u4e03\u5251\u4e09\u97ad\uff0c\u5df2\u6709\u56db\u4f4d\u8d76\u5230\u4e86\u676d\u5dde\u54e9\u3002\u201d \u3000\u3000\u3000\u7f2a\u6587\u63a5\u53e3\u9053\uff1a\u201c\u662f\u54ea\u56db\u4f4d\u5440\uff1f\u201d\u77ac\u5373\u53c8\u8865\u5145\u7740\u8bf4\u9053\uff1a\u201c\u4e03\u5251\u4e09\u97ad\u53c8\u662f\u4e9b\u4ec0\u4e48\u4eba\u3000\uff1f\u201d \u3000\u3000\u3000\u51e0\u4e4e\u5728\u4ed6\u8bf4\u8bdd\u7684\u540c\u4e00\u65f6\u95f4\uff0c\u80e1\u4e4b\u8f89\u95ee\u9053\uff1a\u201c\u662f\u54ea\u56db\u4f4d\u5230\u4e86\u676d\u5dde\uff1f\u201d\u77f3\u78f7\u4e5f\u4e0d\u7981\u3000\u7559\u5fc3 \u503e\u542c\uff0c\u4e03\u5251\u4e09\u97ad\u591a\u534a\u5df2\u540d\u6210\u5229\u5c31\uff0c\u5728\u5bb6\u91cc\u7eb3\u798f\uff0c\u672a\u5728\u6c5f\u6e56\u95f4\u8d70\u52a8\uff0c\u5df2\u6709\u591a\u5e74\uff0c\u3000\u6b64\u756a\u91cd\u51fa\uff0c \u53ef\u60f3\u4ed6\u4eec\u5bf9\u201c\u91d1\u5251\u4fa0\u201d\u7684\u91cd\u89c6\u3002 \u3000\u3000\u3000\u4ed6\u4fa7\u76ee\u4e00\u770b\u7f2a\u6587\uff0c\u7f2a\u6587\u8138\u4e0a\u7adf\u9732\u51fa\u7126\u6025\u800c\u671f\u5f85\u7684\u795e\u60c5\uff0c\u4f3c\u4e4e\u975e\u5e38\u6e34\u671b\u77e5\u9053\u8fd9\u4e9b\u3000\u4e8b\uff0c \u77f3\u78f7\u6697\u5fd6\uff1a\u201c\u4ed6\u82e5\u662f\u5bcc\u5bb6\u516c\u5b50\uff0c\u4e3a\u4ec0\u4e48\u4f1a\u5bf9\u6b66\u6797\u4e2d\u8fd9\u4e48\u5173\u5207\u5462\uff1f\u201d \u3000\u3000\u3000\u201c\u9e33\u9e2f\u53cc\u5251\u592b\u5987\uff0c\u5de6\u624b\u795e\u5251\u548c\u767e\u6b65\u98de\u82b1\u5168\u6765\u4e86\uff0c\u4e3a\u4e86\u8fd9\u2018\u91d1\u5251\u4fa0\uff0c\u4e00\u4eba\uff0c\u5bb6\u5e08\u3000\u7adf\u4f3c \u975e\u5e38\u614e\u91cd\uff0c\u4e00\u5b9a\u8981\u5f97\u5230\u4ed6\u624d\u7518\u5fc3\u3002\u201d\u5e9e\u58eb\u6e5b\u505a\u7136\u7b11\u4e86\u4e00\u4e0b\uff0c\u63a5\u7740\u9053\uff1a\u201c\u5c0f\u4f84\u66fe\u3000\u7ecf\u5bf9\u5bb6\u5e08 \u8bf4\uff0c\u4e3a\u4e86\u4ed6\u4e00\u4eba\uff0c\u53c8\u4f55\u5fc5\u60ca\u52a8\u8001\u4e00\u8f88\u7684\u5462\uff0c\u5bb6\u5e08\u795e\u8272\u5374\u975e\u5e38\u614e\u91cd\uff0c\u8bf4\u8fd9\u4e5f\u3000\u8bb8\u5173\u7cfb\u7740\u5341\u51e0\u5e74 \u524d\u7684\u4e00\u6bb5\u516c\u6848\uff0c\u662f\u4ee5\u975e\u5f97\u5230\u6c34\u843d\u77f3\u51fa\u4e0d\u53ef\uff0c\u4f9d\u5c0f\u4f84\u770b\uff0c\u5176\u5b9e\u4e5f\u4e0d\u5fc5\u3000\u8981\u8fd9\u4e48\u614e\u91cd\uff0c\u6709\u6211\u4eec\u5e08 \u5144\u5f1f\u51e0\u4e2a\u51fa\u624b\uff0c\u4e5f\u5c31\u8db3\u591f\u4e86\u3002\u201d\u81ea\u6ee1\u4e4b\u610f\uff0c\u6ea2\u4e8e\u8a00\u8868\u3002 \u3000\u3000\u3000\u201c\u8fd9\u6837\u4e5f\u597d\u3002\u201d\u80e1\u4e4b\u8f89\u7b11\u9053\uff1a\u201c\u4e03\u5251\u4e09\u97ad\u4e4b\u51fa\uff0c\u53ef\u8ba9\u5c0f\u8f88\u7684\u4eba\uff0c\u4e5f\u6709\u673a\u4f1a\u770b\u770b\u3000\u524d\u8f88 \u7684\u98ce\u91c7\u3002\u201d\u4ed6\u7565\u4e00\u987f\uff0c\u53c8\u9053\uff0c\u201c\u4e0d\u8fc7\u6211\u770b\u5927\u54e5\u4e5f\u662f\u592a\u8fc7\u8651\u4e86\uff0c\u8fd9\u2018\u91d1\u5251\u4fa0\uff0c\u53c8\u3000\u4f1a\u548c\u90a3\u59d3\u4ec7 \u7684\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d\u201c\u662f\u5440\u201d\u7389\u9762\u4f7f\u8005\u9887\u4ee5\u4e3a\u7136\u5730\u70b9\u5934\u9053\uff0c\u201c\u5bb6\u5e08\u7adf\u5c06\u6211\u3000\u4eec\u5e08\u5144\u5f1f\u4e5d\u4e2a\uff0c\u90fd \u8c03\u6d3e\u4e86\u51fa\u6765\uff0c\u53ea\u7559\u4e0b\u5927\u5e08\u5144\u5728\u5bb6\u91cc\uff0c\u5341\u51e0\u5e74\u6765\uff0c\u8fd9\u8fd8\u662f\u7b2c\u4e00\u6b21\u5462\u3000\uff01\u201d\u77f3\u78f7\u4e00\u671b\u7f2a\u6587\uff0c\u5374\u89c1 \u4ed6\u4f4e\u5934\u6c89\u601d\uff0c\u53c8\u50cf\u6ca1\u6709\u6ce8\u610f\u4ed6\u4eec\u7684\u8c08\u8bdd\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u4eba\u5012\u3000\u771f\u602a\u3002\u201d \u3000\u3000\u3000\u80e1\u4e4b\u8f89\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u7a81\u7136\u9644\u8033\u5bf9\u5e9e\u58eb\u6e5b\u8bf4\u4e86\u51e0\u53e5\u6d3b\uff0c\u5e9e\u58eb\u6e5b\u9762\u8272\u7a81\u53d8\uff0c\u5389\u58f0\u9053\u3000\uff1a \u201c\u6709\u8fd9\u79cd\u4e8b\uff1f\u201d\u4e00\u8dfa\u811a\uff0c\u5c06\u9662\u5b50\u94fa\u5730\u7684\u9752\u77f3\uff0c\u7adf\u8dfa\u788e\u4e86\u4e00\u5757\uff0c\u529f\u529b\u4e4b\u6df1\uff0c\u5b9e\u662f\u9a87\u3000\u4eba\u542c\u95fb\u3002 \u3000\u3000\u3000\u201c\u6211\u5c31\u4e0d\u76f8\u4fe1\uff0c\u795e\u97ad\u9a91\u58eb\u7adf\u4f1a\u5728\u7247\u523b\u4e4b\u95f4\u88ab\u4eba\u5bb0\u4e86\u4e5d\u4e2a\uff0c\u597d\uff01\u597d\uff01\u8fd9\u5012\u63d0\u8d77\u6211\u3000\u7684\u5174 \u8da3\u6765\u4e86\uff0c\u6211\u5012\u8981\u548c\u4ed6\u5468\u65cb\u5468\u65cb\u3002\u201d\u4ed6\u6068\u58f0\u8bf4\u9053\uff0c\u8a00\u4e0b\u4e4b\u610f\uff0c\u7adf\u662f\u51ed\u4ed6\u4e00\u4eba\uff0c\u5df2\u3000\u8db3\u591f\u5bf9\u4ed8\u522b \u4eba\u4e86\u3002 \u3000\u3000\u3000\u7f2a\u6587\u62ac\u8d77\u5934\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u77f3\u78f7\u65b9\u81ea\u89c9\u5f97\u4ed6\u7b11\u5f97\u5947\u602a\uff0c\u4ed6\u5df2\u8bf4\u9053\uff1a\u201c\u4f55\u5fc5\u5728\u9662\u5b50\u3000\u7ad9\u7740 \u8c08\u8bdd\uff0c\u5c0f\u5f1f\u4f5c\u4e1c\uff0c\u66ff\u8fd9\u4f4d\u5e9e\u5144\u53f0\u6d17\u5c18\uff0c\u987a\u4fbf\u6211\u4eec\u4e5f\u53bb\u5403\u4e9b\u4e1c\u897f\u3002\u201d\u4ed6\u62ac\u8d77\u5934\uff0c\u3000\u53c8\u7b11\u9053\uff1a \u201c\u5c0f\u5f1f\u59d4\u5b9e\u771f\u4e5f\u6709\u4e9b\u997f\u4e86\u54e9\u3002\u201d \u3000\u3000\u3000\u4ed6\u5fae\u5fae\u4e00\u7b11\uff0c\u53c8\u9053\uff1a\u201c\u5e9e\u5144\u8fd9\u6837\u62ff\u7740\u8fd9\u2018\u6b8b\u9aa8\u4ee4\u2019\uff0c\u4e0d\u89c9\u5f97\u7d2f\u5417\uff1f\u201d\u539f\u6765\u7389\u9762\u3000\u4f7f\u8005 \u4e00\u76f4\u53cc\u624b\u7b14\u76f4\u5730\u6367\u7740\u90a3\u9ed1\u7f0e\u5305\u88b1\uff0c\u6b64\u523b\u95fb\u8a00\u7b11\u9053\uff1a\u201c\u8fd9\u7b97\u4ec0\u4e48\uff1f\u6211\u6367\u4e00\u5e74\uff0c\u4e5f\u3000\u4e0d\u89c1\u5f97\u5728 \u4e4e\u3002\u201d \u3000\u3000\u3000\u8bdd\u58f0\u672a\u843d\uff0c\u4e00\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u53e3\u6c14\u5012\u4e0d\u5c0f\u3002\u201d\u7389\u9762\u4f7f\u8005\u4e00\u60ca\uff0c\u9662\u5b50\u91cc\u7a7a\u8361\u8361\u5730\u3000\uff0c\u9664 \u4e86\u4ed6\u4eec\u4e24\u4eba\uff0c\u54ea\u91cc\u8fd8\u6709\u522b\u4eba\u5728\u3002 \u3000\u3000\u3000\u7389\u9762\u4f7f\u8005\u767d\u60e8\u60e8\u7684\u9762\u5b54\u6b64\u523b\u53d8\u6210\u4e86\u732a\u809d\u8272\uff0c\u6012\u559d\u9053\uff1a\u201c\u597d\u670b\u53cb\u8bf4\u8bdd\u4f55\u5fc5\u85cf\u5934\u9732\u3000\u5c3e \u7684\uff0c\u8981\u8bf4\u4ec0\u4e48\uff0c\u4e0b\u4f1a\u5f53\u7740\u6211\u59d3\u5e9e\u7684\u9762\u8bf4\u5417\uff1f\u201d\u80e1\u4e4b\u8f89\uff0c\u77f3\u78f7\u4e5f\u90fd\u60ca\u8be7\uff0c\u6709\u8c01\u4f1a\u8fd9\u3000\u6837\u8bf4\u8bdd\uff1f \u3000\u3000\u3000\u7389\u9762\u4f7f\u8005\u5389\u53f1\u58f0\u65b9\u4f4f\uff0c\u90a3\u58f0\u97f3\u53c8\u9053\uff1a\u201c\u5f53\u7740\u4f60\u9762\u8bb2\u53c8\u600e\u6837\uff1f\u201d\u4eba\u5f71\u4e00\u82b1\uff0c\u9762\u524d\u3000\u5df2\u591a \u4e86\u4e00\u4eba\uff0c\u6765\u52bf\u4e4b\u5feb\uff0c\u76f4\u5982\u60ca\u9e3f\uff0c\u5e9e\u58eb\u6e5b\u6ee1\u8138\u7684\u6012\u5bb9\uff0c\u5728\u89c1\u4e86\u8fd9\u4eba\u4e4b\u540e\uff0c\u7acb\u523b\u70df\u3000\u6d88\u4e91\u6563\uff0c\u53cd \u800c\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\u3002\u201d \u3000\u3000\u3000\u90a3\u4eba\u9053\uff1a\u201c\u6211\u6765\u4e86\uff0c\u4f60\u8981\u600e\u6837\uff1f\u201d \u3000\u3000\u3000\u77f3\u78f7\u3001\u7f2a\u6587\uff0c\u89c1\u4e86\u8fd9\u4eba\uff0c\u5fc3\u4e2d\u4e5f\u4e0d\u7981\u52a0\u901f\u4e86\u8df3\u52a8\uff0c\u4e0d\u7ea6\u800c\u540c\u5730\u5fd6\u9053\uff1a\u201c\u4e16\u95f4\u7adf\u3000\u6709\u5982 \u6b64\u7f8e\u4eba\u3002\u201d\u80e1\u4e4b\u8f89\u5374\u88c2\u5f00\u5927\u5634\u7b11\u9053\uff1a\u201c\u6bdb\u6bdb\u4f60\u600e\u4e48\u4e5f\u6765\u4e86\uff1f\u201d \u3000\u3000\u3000\u90a3\u4eba\u4fcf\u751f\u751f\u5730\u4e00\u7b11\uff0c\u86d4\u5a1c\u800c\u7ea4\u7ec6\u7684\u8170\u80a2\u95ea\u52a8\u4e86\u4e00\u4e0b\uff0c\u4e24\u53ea\u7075\u6d3b\u800c\u660e\u5a9a\u7684\u5927\u773c\u775b\u3000\u4e00 \u8f6c\uff0c\u5a07\u58f0\u9053\uff1a\u201c\u54df\uff01\u539f\u6765\u662f\u80e1\u4e09\u53d4\u5440\uff1f\u6211\u600e\u4e48\u4e5f\u6ca1\u770b\u5230\u60a8\uff1f\u201d\u7adf\u662f\u4e00\u53e3\u6807\u51c6\u7684\u5317\u3000\u65b9\u6d3b\u3002 \u3000\u3000\u3000\u80e1\u4e4b\u8f89\u7684\u773c\u775b\u7b11\u6210\u4e24\u6761\u53c8\u77ed\u53c8\u7c97\u7684\u7ebf\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u8ddf\u7740\u4f60\u5e08\u5085\uff0c\u53c8\u8dd1\u56de\u6765\u5e72\u3000\u4ec0 \u4e48\uff1f\u201d\u201c\u6bdb\u6bdb\u201d\u4f38\u624b\u4e00\u63a0\u9b13\u53d1\uff0c\u5a07\u7b11\u9053\uff1a\u201c\u6211\u56de\u6765\u770b\u7238\u7238\uff01\u201d\u660e\u7738\u5982\u6d41\u73e0\uff0c\u8f6c\u5230\u3000\u7f2a\u6587\u8138 \u4e0a\u3002\u7f2a\u6587\u8138\u4e0a\u7adf\u6709\u4e9b\u53d1\u70ed\uff0c\u6df1\u85cf\u7740\u7684\u60c5\u611f\u7adf\u88ab\u6fc0\u8d77\u4e00\u7247\u706b\u82b1\u3002 \u3000\u3000\u3000\u201c\u6bdb\u6bdb\u201d\u56de\u8fc7\u5934\uff0c\u671b\u7740\u5e9e\u58eb\u6e5b\u9053\uff1a\u201c\u7238\u7238\u597d\u5417\uff1f\u201d\u5e9e\u58eb\u6e5b\u9053\uff1a\u201c\u5e08\u5085\u4ed6\u8001\u4eba\u5bb6\u3000\u597d\u5f97 \u5f88\u3002\u201d\u201c\u6bdb\u6bdb\u201d\u7b11\u9053\uff1a\u201c\u4f60\u53c8\u6367\u7740\u8fd9\u73a9\u610f\u51fa\u6765\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u3000\u77f3\u78f7\u6697\u5fd6\uff1a\u201c\u539f\u6765\u5979\u662f\u7075\u86c7\u6bdb\u684c\u7684\u5973\u513f\u3002\u201d\u770b\u5230\u5979\u7ea4\u7ec6\u7684\u8eab\u5f71\uff0c\u60f3\u8d77\u6bdb\u51b0\uff0c\u5fc3\u3000\u4e2d\u4e0d \u7981\u9eef\u7136\u3002 \u3000\u3000\u3000\u5979\u679c\u7136\u5c31\u662f\u7075\u86c7\u6bdb\u81ec\u7684\u72ec\u751f\u5973\u513f\u6bdb\u6587\u742a\uff0c\u662f\u5728\u6bdb\u51b0\u8d70\u7684\u90a3\u4e00\u5e74\u751f\u7684\uff0c\u4eca\u5e74\u5341\u516b\u3000\u5c81 \u4e86\uff0c\u201c\u6bdb\u5927\u592a\u7237\u201d\u7684\u5973\u513f\uff0c\u81ea\u7136\u662f\u5a07\u7eb5\u6210\u6027\uff0c\u602a\u7684\u5374\u662f\u5979\u4e0d\u8ddf\u5979\u90a3\u540d\u6ee1\u6b66\u6797\u7684\u7236\u3000\u4eb2\u5b66\u6b66\uff0c \u5374\u8fdc\u8fdc\u8dd1\u5230\u6cb3\u5317\u53bb\uff0c\u6c5f\u6e56\u4e0a\u8c01\u4e5f\u4e0d\u77e5\u9053\u5979\u7684\u5e08\u5085\u7a76\u7adf\u662f\u8c01\u3002 \u3000\u3000\u3000\u5e9e\u58eb\u6e5b\u671b\u7740\u5979\uff0c\u773c\u4e2d\u9732\u51fa\u706b\u4e00\u6837\u7684\u5149\u8292\uff0c\u5979\u5fae\u5fae\u8f6c\u52a8\u4e86\u4e00\u4e0b\u8eab\u5b50\uff0c\u5a07\u7b11\u9053\uff1a\u201c\u3000\u4f60\u4eec \u8981\u53bb\u5403\u996d\uff0c\u8bf7\u4e0d\u8bf7\u6211\u53bb\u5440\uff1f\u201d \u3000\u3000\u3000\u3000\u3000\u672c\u5728\u4f4e\u5934\u6c89\u601d\u7684\u7f2a\u6587\uff0c\u542c\u4e86\u8fd9\u8bdd\u62ac\u8d77\u5934\u6765\uff0c\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u80af\u8d4f\u5149\uff0c\u90a3\u518d\u597d\u6ca1\u6709\u3000 \u4e86\u3002\u201d\u77f3\u78f7\u770b\u7740\u6bdb\u6587\u742a\u8eab\u540e\u7684\u5251\uff0c\u5374\u6ca1\u6709\u770b\u5230\u7f2a\u6587\u7b11\u5bb9\u7684\u52c9\u5f3a\u3002 \u3000\u3000\u3000\u6bdb\u6587\u742a\u8eab\u540e\u80cc\u7740\u7684\u5251\uff0c\u96be\u602a\u77f3\u78f7\u4f1a\u7559\u610f\uff0c\u56e0\u4e3a\u90a3\u7684\u786e\u5947\u602a\u5f97\u5f88\uff0c\u5251\u9798\u975e\u91d1\uff0c\u975e\u3000\u94c1\uff0c \u5374\u50cf\u662f\u4e00\u5927\u5757\u8fde\u7f00\u5728\u4e00\u8d77\u7684\u732b\u76ae\u6240\u5236\uff0c\u7528\u732b\u76ae\u505a\u5251\u9798\u7684\u5251\uff0c\u5929\u4e0b\u6050\u6015\u53ea\u6709\u8fd9\u4e00\u3000\u67c4\u5427\u3002 \u3000\u3000\u3000\u201c\u4f60\u8bf7\u6211\uff0c\u6211\u8fd8\u4e0d\u53bb\u54e9\u3002\u201d\u6bdb\u6587\u742a\u5a07\u7b11\u7740\uff0c\u56de\u8f6c\u8eab\u9053\uff1a\u2018\u6211\u53ef\u5f97\u8d70\u4e86\uff0c\u5582\uff0c\u5e9e\u3000\u8001 \u4e8c\uff0c\u4ee5\u540e\u53ef\u522b\u5c3d\u5439\u5927\u6c14\u5440\uff0c\u5c0f\u5fc3\u98ce\u5927\u95ea\u4e86\u4f60\u7684\u820c\u5934\u3002\u201d\u7389\u9762\u4f7f\u8005\u82e6\u7b11\u7740\uff0c\u671b\u7740\u5979\u3000\u7684\u80cc\u5f71\u3002 \u8fd9\u5a07\u7eb5\u7684\u5c11\u5973\u6765\u5982\u60ca\u9e3f\uff0c\u53bb\u4e5f\u5982\u60ca\u9e3f\u3002\u80e1\u4e4b\u8f89\u6447\u9996\u7b11\u9053\uff1a\u201c\u8fd9\u5201\u94bb\u53e4\u602a\u7684\u3000\u5c0f\u4e2b\u5934\uff0c\u4ee5\u540e\u8c01 \u8981\u5a36\u7740\u4ed6\uff0c\u90a3\u624d\u53eb\u5012\u9709\u5462\uff01\u201d \u3000\u3000\u3000\u7f2a\u6587\u6115\u4e86\u8bb8\u4e45\uff0c\u624d\u7b11\u9053\uff1a\u201c\u9547\u6c5f\u7684\u540d\u83dc\u542c\u8bf4\u4e0d\u9519\uff0c\u5c0f\u5f1f\u8fd8\u6ca1\u6709\u5403\u8fc7\u54e9\u3002\u201d\u4fa7\u76ee\u3000\u671b\u7740 \u4e5f\u5728\u53d1\u6014\u7684\u5e9e\u58eb\u6e5b\u9053\uff0c\u201c\u5e9e\u5144\u5c31\u62ff\u7740\u8fd9\u4e1c\u897f\u53bb\u5417\uff1f\u201d \u3000\u3000\u3000\u201c\u6211\u60f3\u53ea\u6709\u8fd9\u6837\u5427\u3002\u201d\u5e9e\u58eb\u6e5b\u9053\uff0c\u201c\u4e0d\u7136\uff0c\u53c8\u6709\u4ec0\u4e48\u5176\u4ed6\u7684\u529e\u6cd5\u5462\uff1f\u201d\u89c1\u5230\u6bdb\u3000\u6587\u742a \u4e4b\u540e\uff0c\u4ed6\u8bf4\u8bdd\u7684\u5473\u9053\u90fd\u50cf\u4e24\u6837\u4e86\uff0c\u80e1\u4e4b\u8f89\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8d24\u4f84\u5bf9\u6bdb\u6bdb\u4e0d\u9519\u5427\uff1f\u201d\u3000\u5e9e\u58eb\u6e5b\u8138\u7ade \u6709\u4e9b\u7ea2\uff0c\u7f2a\u6587\u5374\u4e0d\u7981\u6cdb\u8d77\u4e00\u9635\u9178\u6e9c\u6e9c\u7684\u611f\u89c9\u3002 \u3000\u3000\u3000\u6bcf\u5929\u65e9\u4e0a\u63d0\u7740\u6eda\u6c34\u5f80\u6bcf\u95f4\u623f\u95f4\u9012\u9001\u7684\u5e97\u5c0f\u4e8c\uff0c\u5728\u91cc\u9762\u9662\u5b50\u7684\u4e00\u95f4\u4e0a\u623f\u95e8\u53e3\u5c0f\u5fc3\u3000\u5730\u6572 \u7740\u95e8\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u8fd9\u91cc\u9762\u4f4f\u7740\u7684\u4eba\uff0c\u5927\u6709\u6765\u5934\uff0c\u662f\u6bdb\u5927\u592a\u7237\u7684\u5f92\u5f1f\uff0c\u8fde\u9547\u6c5f\u5ba2\u3000\u6808\u91cc\u7684\u5e97\u5c0f \u4e8c\u90fd\u77e5\u9053\u4e86\u201c\u6bdb\u5927\u592a\u7237\u201d\u7684\u540d\u5934\uff0c\u7075\u86c7\u6bdb\u81ec\u786e\u662f\u8be5\u5f97\u610f\u4e86\u3002 \u3000\u3000\u3000\u5e97\u5c0f\u4e8c\u6572\u4e86\u51e0\u58f0\u95e8\uff0c\u91cc\u9762\u6ca1\u6709\u7b54\u5e94\uff0c\u8f7b\u8f7b\u4e00\u63a8\uff0c\u5374\u63a8\u5f00\u4e86\uff0c\u4ed6\u63a2\u8fdb\u5934\u671d\u91cc\u9762\u4e00\u3000\u671b\uff0c \u7a81\u7136\u53d1\u51fa\u4e00\u58f0\u60ca\u547c\uff0c\u62d4\u811a\u98de\u5954\uff0c\u6eda\u70eb\u7684\u5f00\u6c34\u6d12\u5f97\u4e00\u5730\uff0c\u6c34\u58f6\u4e5f\u6254\u4e86\uff0c\u50cf\u662f\u649e\u7740\u3000\u9b3c\u4e00\u6837\u3002 \u3000\u3000\u3000\u77f3\u78f7\u521a\u597d\u8d70\u51fa\u623f\u95e8\uff0c\u5e97\u5c0f\u4e8c\u5dee\u70b9\u649e\u5728\u4ed6\u8eab\u4e0a\uff0c\u88ab\u4ed6\u4e00\u628a\u63ea\u4f4f\uff0c\u53f1\u95ee\u9053\uff1a\u201c\u5e72\u4ec0\u3000 \u4e48\uff1f\u201d\u5e97\u5c0f\u4e8c\u4e00\u770b\u662f\u4ed6\uff0c\u624b\u6307\u7740\u5e9e\u58eb\u6e5b\u7684\u623f\u95e8\uff0c\u7ed3\u7ed3\u5df4\u5df4\u4ed6\u8bf4\u9053\uff1a\u201c\u5927\u7237\u2026\u2026\u4f60\u8001\u3000\u4eba\u5bb6\u7684 \u670b\u53cb\uff01\u4e0d\u5f97\u4e86\u5566\u3002\u201d \u3000\u3000\u3000\u867d\u7136\u77f3\u78f7\u6ca1\u6709\u4ec0\u4e48\u5207\u8eab\u7684\u4e8b\uff0c\u4f46\u8fd9\u51e0\u5929\u4ed6\u7684\u795e\u7ecf\u90fd\u662f\u7d27\u5f20\u7740\u7684\uff0c\u8fd9\u4e0e\u4ed6\u524d\u4e9b\u65e5\u3000\u5b50\u91cc \u7684\u968f\u5fc3\u6240\u4e4b\u5927\u4e0d\u76f8\u540c\uff0c\u6b64\u523b\u542c\u4e86\u5e97\u5c0f\u4e8c\u7684\u8bdd\uff0c\u53c8\u662f\u4e00\u60ca\uff0c\u4e09\u811a\u4e24\u6b65\u5730\u5954\u4e86\u8fc7\u53bb\u3000\uff0c\u63a8\u95e8\u4e00\u770b \u2014\u2014\u4ed6\u4e5f\u53d1\u51fa\u4e00\u58f0\u4f4e\u4f4e\u7684\u60ca\u547c\uff0c\u9000\u4e86\u51fa\u6765\uff0c\u8dd1\u5230\u80e1\u4e4b\u8f89\u7684\u95e8\u53e3\uff0c\u9ad8\u58f0\u53eb\u3000\u7740\uff1a\u201c\u80e1\u5144\uff0c\u80e1 \u5144\u2026\u2026\u201d\u80e1\u4e4b\u8f89\u7761\u773c\u60fa\u5fea\u5730\u8dd1\u4e86\u51fa\u6765\uff0c\u77f3\u78f7\u6697\u5fd6\uff1a\u201c\u4f60\u5012\u7761\u5f97\u719f\u3002\u3000\u201d\u80e1\u4e4b\u8f89\u629a\u7740\u5927\u809a\u5b50 \u9053\uff1a\u201c\u77f3\u5144\uff0c\u4ec0\u4e48\u4e8b\uff1f\u201d\u8fd8\u751f\u50cf\u662f\u4e0d\u9ad8\u5174\u4eba\u5bb6\u60ca\u7834\u4ed6\u7684\u597d\u68a6\u4f3c\u3000\u7684\u3002 \u3000\u3000\u3000\u77f3\u78f7\u5374\u6ca1\u6709\u5fc3\u601d\u53bb\u987e\u53ca\u4ed6\u7684\u4e0d\u9ad8\u5174\uff0c\u7565\u4e3a\u6709\u4e9b\u60ca\u614c\u4ed6\u8bf4\u9053\uff1a\u201c\u7389\u9762\u4f7f\u8005\u51fa\u4e86\u4e8b\u3000\uff0c\u80e1 \u5144\u8bf7\u8fc7\u53bb\u770b\u770b\u3002\u201d\u80e1\u4e4b\u8f89\u978b\u90fd\u6765\u4e0d\u53ca\u7a7f\uff0c\u8d64\u4e86\u811a\u8dd1\u4e86\u51fa\u53bb\uff0c\u9661\u5ced\u7684\u6625\u5bd2\u4f7f\u5f97\u4ed6\u3000\u8eab\u4e0a\u7684\u80a5\u8089 \u98a4\u6296\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u3000\u4ed6\u6025\u5207\u5730\u63a8\u5f00\u90a3\u95ee\u623f\u5b50\u7684\u623f\u95e8\uff0c\u89e6\u5165\u4ed6\u773c\u5e18\u7684\u666f\u8c61\uff0c\u4f7f\u5f97\u4ed6\u4e5f\u4e0d\u7981\u53d1\u51fa\u4e00\u58f0\u60ca\u3000\u547c\uff0c \u8d76\u7d27\u4f38\u624b\u6276\u7740\u95e8\u6846\uff0c\u514d\u5f97\u81ea\u5df1\u5012\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u3000\u7389\u9762\u4f7f\u8005\u5f53\u95e8\u800c\u7acb\uff0c\u4e24\u53ea\u773c\u73e0\u5b50\u7a81\u51fa\u773c\u7736\u5916\uff0c\u8138\u4e0a\u662f\u4e00\u7247\u60ca\u60e7\u4e4b\u8272\uff0c\u5de6\u638c\u524d\u626c\u3000\uff0c\u4f46 \u5230\u534a\u9014\u5c31\u4e2d\u6b62\u4e86\uff0c\u662f\u4ee5\u4fbf\u5947\u7a81\u5730\u505c\u7559\u5728\u534a\u7a7a\uff0c\u53f3\u624b\u81ea\u65f6\u4ee5\u4e0b\uff0c\u5374\u786c\u751f\u751f\u5730\u63d2\u5728\u3000\u5899\u58c1\u91cc\uff0c\u662f \u4ee5\u4ed6\u867d\u7136\u65e9\u5df2\u6c14\u7edd\u6b7b\u53bb\uff0c\u5374\u4ecd\u7136\u7ad9\u7740\uff0c\u6ca1\u6709\u5012\u4e0b\u6765\u3002 \u3000\u3000\u3000\u6e05\u6668\u7684\u5149\u7ebf\u4ece\u95e8\u4e2d\u7167\u5165\u8fd9\u9634\u6697\u7684\u623f\u95f4\uff0c\u7167\u5728\u5e9e\u58eb\u6e5b\u5c38\u8eab\u5de6\u4fa7\u8138\u4e0a\uff0c\u4f7f\u5f97\u8fd9\u666f\u8c61\u3000\u770b\u8d77 \u6765\u66f4\u4e3a\u9634\u68ee\u53ef\u6016\u3002\u80e1\u4e4b\u8f89\u52c9\u5f3a\u7ad9\u76f4\u4e86\u8eab\u8eaf\uff0c\u80a5\u8138\u4e0a\u7684\u4e24\u53ea\u5c0f\u773c\u775b\u5728\u623f\u91cc\u6253\u7740\u8f6c\u3000\uff0c\u7a81\u7136\u53c8\u4e00 \u58f0\u60ca\u547c\uff0c\u5954\u4e86\u8fc7\u53bb\uff0c\u5c06\u63d2\u5728\u684c\u5b50\u4e0a\u7684\u4e00\u6837\u4e1c\u897f\u62ff\u4e86\u8d77\u6765\u2014\u2014\u8ddf\u5728\u540e\u9762\u7684\u3000\u77f3\u78f7\u95ea\u773c\u4e00\u770b\uff0c\u90a3 \u4e1c\u897f\u970d\u7136\u53c8\u662f\u4e00\u628a\u91d1\u5251\u3002 \u3000\u3000\u3000\u201c\u53c8\u662f\u8fd9\u6df7\u5e10\u4e1c\u897f\u2026\u2026\u53c8\u662f\u8fd9\u6df7\u5e10\u4e1c\u897f\u2026\u2026\u201d\u80e1\u4e4b\u8f89\u8138\u5982\u6b7b\u7070\uff0c\u62ff\u7740\u90a3\u5251\u5583\u5583\u3000\u4f4e\u8bed \u7740\uff0c\u4e00\u62ac\u5934\uff0c\u8138\u8272\u53c8\u4e00\u53d8\uff0c\u53d8\u5f97\u6bd4\u6b7b\u7070\u8fd8\u7070\u9eef\u2014\u2014\u3002 \u3000\u3000\u3000\u539f\u6765\u5899\u4e0a\u5f20\u7740\u4e00\u65b9\u9ed1\u7f0e\uff0c\u90a3\u5c31\u662f\u5305\u7740\u201c\u6b8b\u9aa8\u4ee4\u201d\u7684\u9ed1\u7f0e\uff0c\u9ed1\u7f0e\u5b50\u4e0a\u9762\uff0c\u7528\u767d\u8272\u3000\u7684\u7c89 \u57a9\u5199\u7740\u56db\u4e2a\u5927\u5b57\uff1a\u201c\u4ee5\u8840\u8fd8\u8840\uff01\u201d \u3000\u3000\u3000\u5230\u73b0\u5728\u4e3a\u6b62\uff0c\u4f3c\u4e4e\u5df2\u7ecf\u5b8c\u5168\u8bc1\u5b9e\u4e86\uff0c\u8fd9\u201c\u91d1\u5251\u4fa0\u201d\u786e\u5b9e\u662f\u548c\u5341\u516d\u5e74\u524d\u7684\u201c\u4ec7\u72ec\u3000\u4e4b \u6b7b\u201d\u6709\u7740\u5173\u7cfb\uff0c\u80e1\u4e4b\u8f89\u624b\u91cc\u62ff\u7740\u90a3\u679d\u91d1\u5251\uff0c\u5583\u54fa\u4f4e\u8bed\u9053\uff1a\u201c\u8fd9\u662f\u7b2c\u4e8c\u67c4\u4e86\u3002\u201d\u5ffd\u3000\u7136\u4e00\u62ac \u5934\uff0c\u5411\u77f3\u78f7\u95ee\u9053\uff1a\u201c\u5148\u524d\u90a3\u67c4\u91d1\u5251\uff0c\u77f3\u5144\u53ef\u66fe\u770b\u5230\uff1f\u201d \u3000\u3000\u3000\u77f3\u78f7\u6447\u4e86\u6447\u5934\uff0c\u968f\u53e3\u8bf4\u9053\uff1a\u201c\u4e5f\u8bb8\u5728\u7f2a\u5144\u90a3\u91cc\u3002\u201d\u4e24\u4eba\u8dd1\u8fdb\u7f2a\u6587\u7684\u623f\u95f4\uff0c\u7f2a\u6587\u3000\u4e5f\u65b9 \u7761\u9192\u8d77\u6765\uff0c\u80e1\u4e4b\u8f89\u8bf4\u4e86\u90a3\u4e8b\uff0c\u7f2a\u6587\u5403\u60ca\u9053\uff1a\u201c\u600e\u4e48\uff1f\u5e9e\u5144\u4e5f\u6b7b\u4e86\uff01\u201d \u3000\u3000\u3000\u80e1\u4e4b\u8f89\u53c8\u95ee\u90a3\u91d1\u5251\uff0c\u7f2a\u6587\u4f4e\u5934\u6c89\u541f\u4e86\u534a\u664c\uff0c\u6447\u9996\u9053\uff1a\u201c\u6211\u770b\u662f\u770b\u8fc7\uff0c\u5230\u54ea\u91cc\u53bb\u3000\u4e86\uff0c \u6211\u4e5f\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u3000\u91d1\u5251\u5931\u8e2a\u4e86\uff0c\u4f46\u8fd9\u4f3c\u4e4e\u5e76\u4e0d\u662f\u4ef6\u4ec0\u4e48\u503c\u5f97\u91cd\u89c6\u7684\u4e8b\u60c5\uff0c\u80e1\u4e4b\u8f89\u968f\u5373\u653e\u8fc7\u4e86\uff0c\u81ea\u3000\u9053\uff1a \u201c\u4e22\u4e86\u5c31\u7b97\u4e86\uff0c\u7f2a\u5144\u4e0d\u5fc5\u6302\u5728\u5fc3\u4e0a\u3002\u201d \u3000\u3000\u3000\u4ed6\u8d70\u5230\u9760\u7a97\u7684\u684c\u5b50\u65c1\uff0c\u5c06\u624b\u4e2d\u7684\u91d1\u5251\u653e\u5728\u684c\u4e0a\uff0c\u5012\u4e86\u4e00\u676f\u65b0\u6ce1\u7684\u8336\uff0c\u5477\u4e86\u4e24\u53e3\u3000\uff0c\u53f9 \u9053\uff1a\u201c\u5e9e\u8001\u4e8c\u4e00\u6b7b\uff0c\u6bdb\u5927\u54e5\u5012\u771f\u662f\u53bb\u4e86\u4e00\u4e2a\u6709\u529b\u7684\u5e2e\u624b\uff0c\u5509\uff01\u6211\u771f\u60f3\u4e0d\u901a\uff0c\u8fd9\u3000\u91d1\u5251\u4fa0\u600e\u80fd \u6709\u8fd9\u79cd\u901a\u5929\u5f7b\u5730\u7684\u672c\u4e8b\uff1f\u201d\u4ed6\u8138\u4e0a\u4e5f\u4e0d\u7981\u7f69\u4e0a\u4e00\u5c42\u5fe7\u8272\u3002 \u3000\u3000\u3000\u7389\u9762\u4f7f\u8005\u5e9e\u58eb\u6e5b\u7684\u6b66\u529f\uff0c\u77f3\u78f7\u662f\u4eb2\u773c\u770b\u89c1\u8fc7\u7684\uff0c\u4ed6\u811a\u788e\u9752\u77f3\uff0c\u6c14\u529f\u82e5\u65e0\u6839\u57fa\uff0c\u3000\u7109\u80fd \u81fb\u6b64\uff0c\u6b64\u523b\u77f3\u78f7\u6697\u5fd6\uff1a\u201c\u8fd9\u91d1\u5251\u4fa0\u7684\u6b66\u529f\uff0c\u7684\u786e\u4e0d\u53ef\u601d\u8bae\uff0c\u5e9e\u58eb\u6e5b\u90a3\u6837\u7684\u6b66\u529f\u3000\uff0c\u5728\u6b66\u6797\u4e2d \u5df2\u53ef\u7b97\u662f\u4e00\u6d41\u9ad8\u624b\u4e86\uff0c\u5728\u4ed6\u624b\u4e0b\uff0c\u5374\u53c8\u6b7b\u5f97\u8fd9\u4e48\u60e8\u6cd5\u3002\u201d \u3000\u3000\u3000\u7f2a\u6587\u8d70\u8fc7\u53bb\uff0c\u4e5f\u5012\u4e86\u676f\u8336\uff0c\u8d70\u8fc7\u6765\u9053\uff1a\u201c\u6211\u5c31\u4f4f\u5728\u5e9e\u5144\u7684\u9694\u58c1\uff0c\u6628\u665a\u600e\u7684\u4e00\u70b9\u3000\u58f0\u97f3 \u4e5f\u6ca1\u6709\u542c\u5230\uff1f\u201d\u80e1\u4e4b\u8f89\u957f\u53f9\u4e00\u58f0\u9053\uff1a\u201c\u4ed6\u5728\u6211\u4eec\u9694\u58c1\u642c\u8d70\u5341\u4e07\u4e24\u94f6\u5b50\uff0c\u6211\u4eec\u5c1a\u3000\u4e14\u4e0d\u77e5\u9053 \u5462\uff01\u201d \u3000\u3000\u3000\u77f3\u78f7\u5fae\u6709\u4e9b\u9762\u8d64\uff0c\u4e00\u9762\u5374\u53c8\u5947\u602a\uff0c\u8fd9\u91d1\u5251\u4fa0\u770b\u6765\u662f\u4e3a\u4ec7\u72ec\u590d\u4ec7\uff0c\u90a3\u4e48\u4ed6\u5fc5\u5b9a\u548c\u3000\u4ec7\u72ec \u6709\u7740\u4e0d\u5bfb\u5e38\u7684\u5173\u7cfb\u2014\u2014\u4ed6\u5ff5\u5934\u4e00\u8f6c\uff0c\u53c8\u5fd6\u9053\uff1a\u201c\u636e\u6211\u6240\u77e5\uff0c\u4ec7\u72ec\u65e0\u4eb2\u65e0\u53cb\uff0c\u548c\u3000\u4ed6\u6709\u7740\u5173\u7cfb \u7684\uff0c\u53ea\u6709\u51b0\u59b9\u4e00\u4eba\u3002\u201d\u4ed6\u60f3\u5230\u6bdb\u51b0\u7684\u53bb\u5904\uff0c\u53c8\u60f3\u5230\u90a3\u7a7f\u7740\u7d2b\u94dc\u3001\u9ec4\u91d1\u8863\u3000\u886b\u7684\u5947\u4eba\uff0c\u5fd6\u9053\uff1a \u201c\u8fd9\u4ef6\u4e8b\u5fc5\u5b9a\u548c\u4ed6\u4eec\u6709\u5173\u8fde\u3002\u201d\u4f46\u7a76\u7adf\u6709\u4ec0\u4e48\u5173\u8fde\uff1f\u4ed6\u60f3\u6765\u60f3\u53bb\u3000\uff0c\u4e5f\u60f3\u4e0d\u51fa\u4e2a\u7ed3\u679c\u6765\u3002 \u3000\u3000\u3000\u6bdb\u51b0\u79bb\u5bb6\u4e4b\u540e\uff0c\u4e2d\u539f\u6b66\u6797\u4e2d\u4eba\u53ea\u6709\u4ed6\u4e00\u4eba\u66fe\u7ecf\u89c1\u8fc7\uff0c\u6bdb\u51b0\u88ab\u4e8c\u4e2a\u5947\u4eba\u201c\u63b3\u8d70\u201d\u3000\uff0c\u4e5f \u53ea\u6709\u4ed6\u4e00\u4eba\u77e5\u9053\uff0c\u4ed6\u5374\u4e0d\u613f\u610f\u8bf4\u51fa\u6765\uff0c\u4ed6\u4ee5\u4e3a\u81ea\u5df1\u4ec0\u4e48\u90fd\u4e0d\u77e5\u9053\uff0c\u5176\u5b9e\u4ed6\u6240\u77e5\u3000\u9053\u7684\uff0c\u5df2\u6bd4 \u522b\u4eba\u591a\u5f97\u591a\u4e86\u3002 \u3000\u3000\u3000\u516b\u9762\u73b2\u73d1\u559d\u5b8c\u4e86\u676f\u4e2d\u7684\u8336\uff0c\u8d70\u5230\u684c\u65c1\uff0c\u60f3\u518d\u5012\uff1d\u676f\uff0c\u7a81\u5730\u53c8\u4e00\u58f0\u60ca\u547c\uff1a\u201c\u90a3\u67c4\u3000\u91d1\u5251 \u5462\uff1f\u201d\u62ac\u5934\u4e00\u671b\uff0c\u7a97\u5b50\u672c\u662f\u5f00\u7740\u7684\uff0c\u4ed6\u53cc\u624b\u4e00\u6309\u684c\u9762\uff0c\u55d6\u5730\u7a9c\u4e86\u51fa\u53bb\uff0c\u7a97\u5916\u662f\u3000\u4e2a\u5c0f\u9662\u5b50\uff0c \u6e3a\u65e0\u4eba\u8e2a\u3002 \u3000\u3000\u3000\u4ed6\u6025\u6012\u4ea4\u96c6\uff0c\u53d1\u75af\u4f3c\u5730\u63a0\u4e0a\u5c4b\u9762\uff0c\u6b64\u65f6\u671d\u9633\u521d\u5347\uff0c\u6625\u65e5\u7684\u9633\u5149\u7167\u5f97\u5c4b\u9762\u95ea\u95ea\u53d1\u3000\u5149\uff0c \u6781\u76ee\u8fdc\u671b\uff0c\u5c4b\u9876\u6809\u6bd4\uff0c\u54ea\u91cc\u6709\u4eba\u5f71\u5728\u3002 \u3000\u3000\u3000\u4e09\u4e2a\u4eba\u90fd\u597d\u7aef\u7aef\u5730\u5750\u5728\u623f\u91cc\uff0c\u4f46\u662f\u5c31\u5728\u4ed6\u4eec\u65c1\u8fb9\u7684\u684c\u5b50\u4e0a\u653e\u7740\u7684\u4e1c\u897f\uff0c\u7adf\u4f1a\u5931\u3000\u4e86 \u8e2a\uff0c\u800c\u4e14\u8fd9\u4e09\u4e2a\u4eba\u91cc\u7adf\u6709\u4e24\u4e2a\u8fd8\u662f\u6b66\u6797\u9ad8\u624b\u3002 \u3000\u3000\u3000\u80e1\u4e4b\u8f89\u7a97\u53e3\u63a0\u8fdb\u6765\uff0c\u4e00\u53cc\u811a\u4ecd\u7136\u6ca1\u6709\u7a7f\u978b\u5b50\uff0c\u4e5f\u4e0d\u89c9\u5f97\u51b7\uff0c\u77f3\u78f7\u8be7\u7136\u95ee\u9053\uff1a\u201c\u3000\u90a3\u67c4 \u91d1\u5251\u53c8\u5931\u53bb\u4e86\u5417\uff1f\u201d \u3000\u3000\u3000\u516b\u9762\u73b2\u73d1\u9893\u7136\u5750\u5728\u6905\u5b50\u4e0a\uff0c\u82e6\u7b11\u70b9\u9996\uff0c\u80a5\u5927\u7684\u809a\u5b50\uff0c\u4e0d\u4f4f\u5730\u5598\u6c14\uff0c\u50cf\u53ea\u559d\u591a\u4e86\u3000\u6c34\u7684 \u86e4\u87c6\uff0c\u6837\u5b50\u663e\u5f97\u65e2\u6ed1\u7a3d\uff0c\u53c8\u53ef\u601c\u3002 \u3000\u3000\u3000\u7f2a\u6587\u8d70\u8fc7\u6765\uff0c\u6e05\u4fca\u7684\u8138\u4e0a\uff0c\u5e26\u7740\u4e00\u4e1d\u522b\u4eba\u65e0\u6cd5\u4e86\u89e3\u7684\u795e\u8272\uff0c\u4ed6\u62ac\u8d77\u624b\uff0c\u7565\u6574\u4e86\u3000\u6574\u8863 \u51a0\uff0c\u6717\u7136\u9053\uff1a\u201c\u91d1\u5251\u65e2\u5931\uff0c\u4f24\u4e5f\u65e0\u76ca\uff0c\u80e1\u5144\u8fd8\u662f\u5feb\u60f3\u4e2a\u5e94\u4ed8\u7684\u5bf9\u7b56\u624d\u662f\u3002\u201d\u4ece\u3000\u7a97\u53e3\u5c04\u8fdb\u6765 \u7684\u9633\u5149\uff0c\u6620\u5f97\u4ed6\u5bbd\u5927\u7684\u888d\u8896\u91cc\u4f3c\u4e4e\u6709\u91d1\u5149\u4e00\u95ea\uff0c\u4f46\u77f3\u78f7\u548c\u80e1\u4e4b\u8f89\u90fd\u6ca1\u6709\u3000\u770b\u5230\u3002 \u3000\u3000\u3000\u521d\u81f3\u676d\u5dde\u7684\u7f2a\u6587\uff0c\u8fce\u7740\u6625\u65e5\uff0c\u6df1\u6df1\u5730\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u7a7a\u6c14\u4eff\u4f5b\u6709\u4ed6\u719f\u6089\u7684\u5473\u9053\uff0c\u3000\u4ed6\u8d2a \u5a6a\u5730\u518d\u5438\u4e86\u4e00\u53e3\uff0c\u77e5\u9053\u4ed6\u7684\u8840\u6db2\u91cc\u672c\u6765\u662f\u6709\u7740\u676d\u5dde\u7684\u7a7a\u6c14\u7684\uff0c\u4e8e\u662f\u4ed6\u82e5\u6709\u6df1\u610f\u3000\u5730\u7b11\u4e86\u3002 \u3000\u3000\u3000\u80e1\u4e4b\u8f89\u906d\u53d7\u4e86\u8fd9\u4e48\u591a\u6b21\u53d8\u6545\u4e4b\u540e\uff0c\u552f\u4e00\u7684\u529e\u6cd5\uff0c\u5c31\u662f\u5411\u6bdb\u81ec\u6c42\u52a9\uff0c\u5b9e\u9645\u4e0a\uff0c\u771f\u3000\u6b63\u906d \u53d7\u6253\u51fb\u7684\u5e76\u4e0d\u662f\u4ed6\uff0c\u800c\u662f\u6bdb\u81ec\u3002 \u3000\u3000\u3000\u3000\u3000\u4ed6\u7740\u6025\u8981\u89c1\u6bdb\u81ec\uff0c\u7f2a\u6587\u5374\u8981\u5148\u53bb\u6e38\u6e56\uff0c\u53bb\u676d\u5dde\u800c\u4e0d\u6e38\u897f\u6e56\u7684\u4eba\uff0c\u81ea\u53e4\u4ee5\u6765\uff0c\u4f3c\u3000 \u4e4e\u8fd8\u672a\u5c1d\u6709\u8fc7\uff0c\u80e1\u4e4b\u8f89\u5bf9\u7f2a\u6587\u5b58\u5fc3\u62c9\u62e2\uff0c\u81ea\u7136\u7b54\u5e94\u3002 \u3000\u3000\u3000\u6e56\u5149\u5c71\u8272\uff0c\u63a9\u6620\u534a\u6e56\u83b2\u8377\uff0c\u5fae\u98ce\u5439\u8fc7\uff0c\u6e56\u9762\u4e0a\u7684\u6d9f\u6f2a\u50cf\u662f\u4e00\u4e2a\u4e2a\u7f8e\u4eba\u7684\u9152\u6da1\uff0c\u3000\u7f2a\u6587 \u7b49\u6f2b\u6b65\u5824\u4e0a\uff0c\u4f46\u89c9\u5fc3\u80f8\u795e\u813e\u7686\u6e05\u3002 \u3000\u3000\u3000\u5ffd\u5730\u5824\u7554\u67f3\u836b\u6df1\u5904\uff0c\u8361\u51fa\u4e00\u53ea\u753b\u80aa\uff0c\u6731\u680f\u7eff\u6237\uff0c\u8239\u4e0a\u4eba\u4e00\u6380\u5e18\u5b50\uff0c\u5a07\u5524\u9053\uff1a\u201c\u3000\u4e09 \u53d4\uff0c\u4f60\u4eec\u4e5f\u6765\u4e86\u3002\u201d\u5b9a\u773c\u770b\u53bb\uff0c\u7adf\u662f\u6bdb\u6587\u742a\u3002 \u3000\u3000\u3000\u7f2a\u6587\u8138\u4e0a\u6709\u559c\u8272\uff0c\u53ea\u662f\u4ed6\u6b22\u559c\u7684\u539f\u56e0\u96be\u4ee5\u731c\u900f\uff0c\u80e1\u4e4b\u8f89\u54c8\u54c8\u7684\u7b11\u9053\uff1a\u201c\u6211\u4eec\u60f3\u3000\u6e38 \u6e56\uff0c\u5374\u82e6\u65e0\u8239\uff0c\u78b0\u89c1\u4f60\u771f\u597d\u6781\u4e86\u3002\u201d\u6bdb\u6587\u742a\u683c\u683c\u7b11\u9053\uff1a\u201c\u6211\u4e00\u4e2a\u4eba\u6e38\u6e56\uff0c\u95f7\u5f97\u65e0\u3000\u804a\uff0c\u78b0\u89c1 \u4f60\u4eec\u66f4\u597d\u6781\u4e86\u3002\u201d \u3000\u3000\u3000\u5979\u51fa\u8bed\u5982\u9ec4\u9a7e\uff0c\u7b11\u5982\u767e\u5408\uff0c\u5728\u8fd9\u80dc\u7edd\u5929\u4e0b\u7684\u6e56\u5149\u5c71\u8272\u91cc\uff0c\u663e\u5f97\u66f4\u7f8e\u5982\u5929\u4eba\uff0c\u7f2a\u3000\u6587\u76ee \u4e0d\u8f6c\u775b\u5730\u7740\u5979\uff0c\u7adf\u50cf\u75f4\u4e86\u3002 \u3000\u3000\u3000\u753b\u80aa\u8361\u4e86\u8fc7\u6765\uff0c\u6bdb\u6587\u742a\u8d70\u5230\u8239\u5934\u4e0a\uff0c\u8863\u88d9\u968f\u98ce\u98d8\u821e\uff0c\u6e56\u6c34\u4e2d\u4f46\u89c1\u4e00\u4e2a\u5189\u5189\u800c\u821e\u3000\u7684\u4ed9 \u5973\u5f71\u5b50\uff0c\u5374\u662f\u5979\u7684\u5012\u5f71\uff0c\u80e1\u4e4b\u8f89\u8df3\u5230\u8239\u4e0a\uff0c\u655e\u58f0\u7b11\u9053\uff1a\u201c\u6bdb\u6bdb\uff0c\u4f60\u5012\u771f\u662f\u8d8a\u6765\u3000\u8d8a\u6f02\u4eae \u4e86\u3002\u201d \u3000\u3000\u3000\u201c\u8fd9\u4e24\u4f4d\u662f\u8c01\u5440\uff1f\u201d\u6bdb\u6587\u742a\u5a07\u7b11\u7740\u6307\u7740\u77f3\u78f7\u548c\u7f2a\u6587\u95f4\u9053\uff0c\u80e1\u4e4b\u8f89\u4e3a\u4ed6\u4eec\u5f15\u89c1\u4e86\u3000\uff0c\u6bdb \u6587\u742a\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u660e\u5982\u897f\u6e56\u4e4b\u6c34\u7684\u773c\u775b\uff0c\u7d27\u76ef\u5728\u77f3\u78f7\u8eab\u4e0a\uff0c\u9053\uff1a\u201c\u4f60\u5c31\u662f\u77f3\u3000\u78f7\u5927\u53d4 \u5440\uff01\u201d\u5979\u4e00\u7b11\u53c8\u9053\uff1a\u201c\u6211\u5e38\u542c\u7239\u7239\u8bf4\u8d77\u4f60\uff0c\u8bf4\u4f60\u662f\u59d1\u59d1\u7684\u597d\u670b\u53cb\u3002\u201d \u3000\u3000\u3000\u77f3\u78f7\u76ee\u5149\u8fdc\u8fdc\u671b\u5728\u8239\u8231\u5916\uff0c\u8fdc\u5904\u5c71\u5cf0\u5982\u753b\uff0c\u6bdb\u6587\u742a\u8138\u4e0a\u9732\u51fa\u51c4\u5a49\u7684\u795e\u8272\uff0c\u5e7d\u5e7d\u3000\u8bf4 \u9053\uff1a\u201c\u59d1\u59d1\u5728\u6211\u51fa\u751f\u7684\u90a3\u5e74\u5c31\u79bb\u4e86\u5bb6\u3002\u7239\u7239\u5230\u5904\u627e\u5979\uff0c\u4e5f\u627e\u4e0d\u7740\uff0c\u6211\u5c31\u4e0d\u61c2\uff0c\u5979\u3000\u4f1a\u8dd1\u5230\u54ea \u91cc\u53bb\u4e86\u5462\uff1f\u201d \u3000\u3000\u3000\u77f3\u78f7\u957f\u53f9\u4e00\u58f0\uff0c\u76ee\u5149\u4ece\u8231\u5916\u6536\u56de\u6765\uff0c\u7ecf\u8fc7\u7f2a\u6587\u8138\u4e0a\u65f6\uff0c\u5374\u89c1\u4ed6\u8138\u4e0a\u7684\u808c\u8089\u53c8\u5728\u3000\u5947\u602a \u5730\u626d\u66f2\u7740\uff0c\u624b\u638c\u7d27\u63e1\u7740\u8336\u676f\uff0c\u597d\u8c61\u751f\u6015\u676f\u5b50\u4f1a\u6389\u4e0b\u53bb\u4f3c\u7684\uff0c\u77f3\u78f7\u7981\u4e0d\u4f4f\u53c8\u671b\u4e86\u3000\u4ed6\u4e24\u773c\uff0c\u5fc3 \u4e2d\u601d\u6f6e\u5982\u6f6e\u6d8c\u8d77\u3002 \u3000\u3000\u3000\u5927\u5bb6\u4eff\u4f5b\u90fd\u9677\u5165\u60b2\u54c0\u7684\u56de\u5fc6\u91cc\uff0c\u516b\u9762\u73b2\u73d1\u4e00\u62cd\u684c\u5b50\uff0c\u7b11\u9053\uff1a\u201c\u5f80\u4e8b\u4f11\u63d0\u4e5f\u7f62\uff0c\u3000\u4eca\u65e5 \u5c3d\u6b22\u4e3a\u4f73\uff0c\u77f3\u5144\uff0c\u4f60\u672c\u662f\u5802\u5802\u5927\u4e08\u592b\uff0c\u4eca\u65e5\u5374\u600e\u7684\u6548\u8d77\u5c0f\u513f\u5973\u4e4b\u6001\u6765\u4e86\uff0c\u54c8\u54c8\u3000\u54c8\u54c8\uff01\u8be5 \u7f5a\uff0c\u8be5\u7f5a\u3002\u201d\u4ed6\u5374\u4e0d\u77e5\u9053\uff0c\u81ea\u53e4\u4ee5\u6765\uff0c\u591a\u60c5\u6700\u662f\u5927\u4e08\u592b\u54e9\u3002 \u3000\u3000\u3000\u753b\u80aa\u7f13\u7f13\u8361\u5f00\uff0c\u4e24\u4fa7\u83b2\u5982\u7e41\u82b1\uff0c\u6e05\u9999\u88ad\u4eba\uff0c\u7f2a\u6587\u8d70\u5230\u7a97\u524d\uff0c\u6df1\u5438\u4e86\u4e00\u53e3\uff0c\u56de\u8fc7\u3000\u5934\u6765 \u65f6\uff0c\u8138\u4e0a\u53c8\u56de\u590d\u4e86\u5b89\u9759\u4e86\u3002 \u3000\u3000\u3000\u201c\u4f60\u7236\u4eb2\u5462\uff1f\u201d\u80e1\u4e4b\u8f89\u95ee\u9053\u3002\u6bdb\u6587\u742a\u5fae\u98a6\u9edb\u7709\u9053\uff1a\u201c\u7239\u7239\u6574\u5929\u6101\u7709\u4e0d\u5c55\u7684\uff0c\u542c\u3000\u8bf4 \u2018\u795e\u97ad\u9a91\u58eb\u2019\u4e00\u4e0b\u6b7b\u4e86\u4e5d\u4e2a\uff0c\u4ed6\u8001\u4eba\u5bb6\u5927\u6012\uff0c\u8bf4\u662f\u518d\u6709\u8fd9\u79cd\u4e8b\u53d1\u751f\uff0c\u4ed6\u8001\u4eba\u5bb6\u5c31\u3000\u8981\u4eb2\u81ea\u51fa \u9a6c\u4e86\u3002\u201d \u3000\u3000\u3000\u516b\u9762\u73b2\u73d1\u53c8\u53f9\u4e86\u4e00\u58f0\uff0c\u672c\u60f3\u8bf4\u51fa\u7389\u9762\u4f7f\u8005\u5df2\u6b7b\u4e4b\u4e8b\uff0c\u770b\u4e86\u6bdb\u6587\u742a\u4e00\u773c\uff0c\u5374\u6b62\u4f4f\u3000\u4e86\uff0c \u8033\u7554\u7a81\u95fb\u4e1d\u7af9\u4e4b\u58f0\uff0c\u8fd8\u9690\u9690\u6709\u96cf\u5993\u7684\u6b4c\u58f0\uff0c\u4ed6\u65b9\u5c55\u989c\u4e00\u7b11\uff0c\u5374\u84e6\u7136\u201c\u7830\u201d\u7136\u4e00\u3000\u58f0\u5927\u9707\uff0c\u4ed6 \u624b\u91cc\u8336\u676f\u9707\u5728\u5730\u4e0a\uff0c\u4eba\u4e5f\u51e0\u4e4e\u4ece\u6905\u5b50\u4e0a\u7ffb\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u3000\u6bdb\u6587\u742a\u8d76\u7d27\u4e00\u4f38\u624b\uff0c\u6276\u7740\u684c\u5b50\uff0c\u8239\u8eab\u867d\u7136\u88ab\u6447\u5f97\u731b\u4e00\u503e\u4e1c\uff0c\uff0c\u684c\u5b50\u4e0a\u7684\u4e1c\u897f\u5374\u3000\u4e00\u6837 \u4e5f\u6ca1\u6709\u6389\u4e0b\u6765\uff0c\u5979\u67f3\u7709\u4e00\u7ad6\uff0c\u7709\u95f4\u7acb\u523b\u73b0\u51fa\u5bd2\u610f\uff0c\u63a2\u9996\u7a97\u5916\uff0c\u53e6\u4e00\u8258\u753b\u822b\u8fd8\u6a2a\u3000\u5728\u65c1\u8fb9\u3002 \u3000\u3000\u3000\u201c\u5582\uff01\u4f60\u4eec\u6ca1\u6709\u957f\u7740\u773c\u775b\u5417\uff1f\u201d\u5979\u5a07\u559d\u7740\uff0c\u5bf9\u9762\u753b\u80aa\u91cc\u500f\u5730\u4f38\u51fa\u4e24\u4e2a\u5934\u6765\uff0c\u8138\u3000\u5df2\u7ecf \u56e0\u4e3a\u559d\u4e86\u5927\u591a\u7684\u9152\uff0c\u800c\u53d8\u5f97\u50cf\u521a\u8d77\u9505\u7684\u8783\u87f9\u90a3\u4e48\u7ea2\u4e86\uff0c\u7529\u7740\u9189\u773c\u671b\u7740\u6bdb\u6587\u742a\uff0c\u3000\u72e0\u7410\u5730\u7b11\u7740 \u8bf4\uff1a\u201c\u54df\uff0c\u597d\u51f6\u7684\u5a46\u5a18\uff01\u201d \u3000\u3000\u3000\u201c\u4f60\u7684\u8239\u82e5\u649e\u574f\u4e86\uff0c\u5c31\u8fc7\u6765\u966a\u5927\u7237\u5750\uff0c\u5927\u7237\u7ba1\u4fdd\u8d54\u4f60\u4e00\u6761\u65b0\u7684\u3002\u201d\u53e6\u4e00\u4e2a\u4eba\u66f4\u3000\u8ba8\u538c \u4ed6\u8bf4\uff0c\u6bdb\u6587\u742a\u7c89\u8138\u53d8\u5f97\u7389\u822c\u715e\u767d\u3002 \u3000\u3000\u3000\u80e1\u4e4b\u8f89\u5954\u5230\u7a97\u524d\uff0c\u9a82\u9053\uff1a\u201c\u778e\u4e86\u773c\u7684\u72d7\u5b50\u4f60\u77e5\u9053\u8fd9\u662f\u8c01\u2014\u2014\u201d\u4e0b\u9762\u7684\u8bdd\uff0c\u5374\u88ab\u3000\u6bdb\u6587 \u742a\u62e6\u4f4f\u4e86\uff0c\u4e0d\u8ba9\u4ed6\u8bf4\u4e0b\u53bb\uff0c\u56e0\u4e3a\u5979\u60f3\u6253\u67b6\uff0c\u800c\u4e00\u8bf4\u51fa\u81ea\u5df1\u7684\u8eab\u4efd\uff0c\u8fd9\u67b6\u5c31\u6253\u4e0d\u3000\u8d77\u6765\u4e86\u3002 \u3000\u3000\u3000\u5979\u5ffd\u7136\u8d70\u51fa\u8231\u53bb\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5979\u521a\u8dd1\u8fdb\u6765\uff0c\u4ed6\u4eec\u6240\u5750\u7684\u8fd9\u8258\u753b\u80aa\u4fbf\u7a81\u7136\u8f6c\u4e86\u4e2a\u3000\u5934\uff0c \u5bf9\u51c6\u90a3\u8258\u6253\u6a2a\u7684\u753b\u80aa\u649e\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u3000\u81ea\u7136\u4e5f\u662f\u201c\u7830\u201d\u7684\u4e00\u58f0\u5927\u9707\uff0c\u4f38\u5728\u7a97\u5b50\u5916\u9762\u4ecd\u5728\u772f\u7740\u8272\u773c\u7684\u90a3\u4e24\u9897\u50cf\u6b7b\u8783\u87f9\u4f3c\u3000\u7684 \u5934\uff0c\u4e00\u9707\u4e4b\u4e0b\uff0c\u5934\u9876\u201c\u7830\u201d\uff0c\u201c\u7830\u201d\u4e24\u58f0\uff0c\u649e\u5728\u7a97\u6237\u4e0a\u9762\uff0c\u751f\u50cf\u662f\u65b9\u624d\u90a3\u58f0\u5927\u9707\u3000\u7684\u4f59\u97f3\u4f3c \u7684\u3002 \u3000\u3000\u3000\u6bdb\u6587\u742a\u5a07\u7b11\u4e86\u8d77\u6765\uff0c\u6b7b\u8783\u87f9\u4f3c\u7684\u5934\u7f29\u4e86\u56de\u53bb\uff0c\u7f2a\u6587\u7b11\u563b\u563b\u5730\u671b\u7740\u5979\uff0c\u50cf\u662f\u5bf9\u5979\u3000\u6781\u6709 \u5174\u8da3\uff0c\u77f3\u78f7\u5fc3\u4e2d\u5374\u5728\u60f3\u7740\u4e00\u4e8b\uff1a\u201c\u65b9\u624d\u8fd9\u8239\u4e00\u9707\uff0c\u80e1\u80d6\u5b50\u624b\u91cc\u7684\u8336\u676f\u90fd\u6389\u5728\u5730\u3000\u4e0a\uff0c\u53ef\u662f\u7f2a \u6587\u624b\u91cc\u7684\u676f\u5b50\u5374\u62ff\u5f97\u7a33\u7a33\u5730\uff0c\u8fde\u4e00\u6ef4\u6c34\u90fd\u6ca1\u6709\u6f0f\u51fa\u6765\uff0c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\u5462\u3000\uff1f\u96be\u9053\u4ed6\u8eab\u6000\u7edd \u6280\uff0c\u5374\u6df1\u85cf\u4e0d\u9732\u5417\uff1f\u4f46\u662f\uff0c\u770b\u4ed6\u7684\u5916\u8868\uff0c\u5374\u4e00\u70b9\u513f\u4e5f\u4e0d\u50cf\u5440\u3002\u201d \u3000\u3000\u3000\u987b\u77e5\u8981\u662f\u7ec3\u5bb6\u5b50\uff0c\u5fc5\u5b9a\u6709\u4e00\u4e9b\u548c\u666e\u901a\u4eba\u4e24\u6837\u7684\u7279\u5f81\uff0c\u7ec3\u5916\u95e8\u529f\u592b\u7684\uff0c\u5927\u591a\u7b4b\u9aa8\u3000\u5f3a \u58ee\uff0c\u624b\u811a\u7c97\u7cd9\uff0c\u8170\u6b65\u6c89\u7a33\uff0c\u4f7f\u5185\u5bb6\u529f\u592b\u7684\uff0c\u5927\u591a\u4e24\u773c\u795e\u5149\u6ee1\u8db3\uff0c\u4e24\u8fb9\u592a\u9633\u7a74\u9ad8\u9ad8\u3000\u9f13\u8d77\uff0c\u81f3 \u4e8e\u7ec3\u6709\u91d1\u949f\u7f69\u3001\u94c1\u5e03\u886b\u3001\u6cb9\u9524\u8d2f\u9876\u3001\u5341\u4e09\u592a\u4fdd\u6a2a\u7ec3\u8fd9\u4e00\u4e00\u7c7b\u529f\u592b\u7684\uff0c\u90a3\u7279\u3000\u5f81\u81ea\u7136\u66f4\u4e3a\u660e \u663e\uff0c\u65ad\u65e0\u522b\u4eba\u770b\u4e0d\u51fa\u6765\u7684\u9053\u7406\u3002 \u3000\u3000\u3000\u77f3\u78f7\u6b63\u5728\u601d\u7d22\uff0c\u8239\u8eab\u53c8\u6447\u6643\u4e86\u51e0\u4e0b\uff0c\u50cf\u662f\u6709\u4eba\u8df3\u4e0a\u8239\u6765\u7684\u6837\u5b50\uff0c\u6bdb\u6587\u742a\u51b7\u51b7\u4e00\u3000\u7b11\uff0c \u4ece\u58c1\u95f4\u62ff\u8d77\u90a3\u67c4\u4ee5\u732b\u76ae\u4e3a\u9798\u7684\u957f\u5251\uff0c\u4fa7\u987e\u80e1\u4e4b\u8f89\u9053\uff1a\u201c\u4e09\u53d4\uff0c\u4f60\u542c\u7239\u7239\u8bf4\u8d77\u8fc7\u3000\u8fd9\u628a\u5251 \u5417\uff1f\u201d \u3000\u3000\u3000\u80e1\u4e4b\u8f89\u5fae\u7b11\u6447\u5934\uff0c\u6bdb\u6587\u742a\u5a07\u58f0\u9053\uff1a\u201c\u90a3\u6211\u73b0\u5728\u8ba9\u4e09\u53d4\u770b\u770b\u3002\u201d\u4e00\u6380\u5e18\u5b50\uff0c\u8d70\u4e86\u3000\u51fa \u53bb\uff0c\u7f2a\u6587\u50cf\u662f\u6025\u4e8e\u8981\u770b\u5979\u7684\u6b66\u529f\u4f3c\u7684\uff0c\u5f88\u5feb\u5730\u8ddf\u4e86\u51fa\u53bb\uff0c\u516b\u9762\u73b2\u73d1\u4fa7\u987e\u77f3\u78f7\u9053\uff1a\u3000\u201c\u77f3\u5144\uff0c \u6211\u4eec\u4e5f\u51fa\u53bb\u770b\u70ed\u95f9\u5427\uff0c\u5c06\u95e8\u65e0\u72ac\u5b50\uff0c\u8fd9\u4e2b\u5934\u7684\u6b66\u529f\uff0c\u7edd\u5bf9\u9519\u4e0d\u4e86\u3002\u201d \u3000\u3000\u3000\u77f3\u78f7\u4e5f\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u522b\u7684\u4e0d\u8bf4\uff0c\u6211\u770b\u5979\u638c\u4e2d\u90a3\u67c4\u5251\uff0c\u5c31\u7edd\u975e\u51e1\u54c1\u3002\u53ea\u4e0d\u8fc7\u5979\u62ff\u3000\u7740\u8fd9 \u5251\u53bb\u5bf9\u4ed8\u8fd9\u6279\u65e0\u8d56\u5c11\u5e74\uff0c\u672a\u514d\u6709\u4e9b\u5927\u6750\u5c0f\u7528\u4e86\u5427\u3002\u201d \u3000\u3000\u3000\u4e24\u4eba\u4e00\u7b11\u8d70\u51fa\u8231\uff0c\u6839\u672c\u6ca1\u6709\u5c06\u8fd9\u573a\u5c06\u8981\u53d1\u751f\u7684\u6253\u6597\u653e\u5728\u773c\u91cc\uff0c\u54ea\u77e5\u4e00\u51fa\u8231\uff0c\u624d\u3000\u77e5\u9053 \u4e8b\u60c5\u5927\u51fa\u610f\u6599\u4e4b\u5916\uff0c\u8fd9\u573a\u67b6\u8981\u6253\u8d77\u6765\uff0c\u6050\u6015\u4e0d\u5927\u7b80\u5355\u54e9\u3002 \u3000\u3000\u3000\u5728\u753b\u822b\u524d\u9762\u90a3\u4e00\u5757\u7ea6\u4e24\u4e08\u65b9\u5706\u7684\u8239\u9762\u4e0a\uff0c\u6b64\u523b\u505a\u7136\u5353\u7acb\u7740\u4e94\u4e2a\u6025\u88c5\u52b2\u670d\u7684\u6c49\u5b50\u3000\uff0c\u624b \u4e2d\u957f\u5251\u68ee\u7136\uff0c\u80e1\u4e4b\u8f89\u5e76\u4e0d\u5341\u5206\u6ce8\u610f\uff0c\u56e0\u4e3a\u90a3\u4e24\u5934\u201c\u6b7b\u8783\u87f9\u201d\u4e5f\u5728\u5176\u4e2d\uff0c\u80e1\u4e4b\u8f89\u3000\u7684\u773c\u5149\uff0c\u5374 \u843d\u5728\u7ad9\u5728\u8239\u5934\u7684\u4e24\u4e2a\u7626\u957f\u6c49\u5b50\u8eab\u4e0a\uff0c\u4ed6\u4eff\u4f5b\u89c9\u5f97\u8fd9\u4e24\u4eba\u5f88\u719f\uff0c\u867d\u7136\u4e0d\u8ba4\u3000\u8bc6\uff0c\u4f46\u81f3\u5c11\u603b\u5728\u4ec0 \u4e48\u5730\u65b9\u89c1\u8fc7\u3002 \u3000\u3000\u3000\u4ed6\u731b\u5730\u4e00\u51fb\u638c\uff0c\u84e6\u7136\u60f3\u8d77\u4e86\u8fd9\u4e24\u4eba\u662f\u8c01\uff0c\u6025\u5fd9\u62a2\u4e86\u8fc7\u53bb\uff0c\u558a\u9053\uff1a\u201c\u5927\u5bb6\u5148\u8bf7\u522b\u3000\u52a8 \u624b\uff0c\u5927\u5bb6\u90fd\u662f\u81ea\u5df1\u4eba\uff0c\u6709\u8bdd\u2014\u2014\u201d\u90a3\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u5b8c\uff0c\u90a3\u8eab\u8eaf\u7626\u957f\u7684\u4e24\u4eba\u4e00\u9f50\u66b4\u559d\u3000\u9053\uff1a\u201c\u5c11 \u5e9f\u8bdd\u3002\u201d \u3000\u3000\u3000\u5176\u4e2d\u4e00\u4eba\u63a0\u4e86\u8fc7\u6765\uff0c\u8eab\u5f62\u7edd\u5feb\uff0c\u5de6\u638c\u55d6\u5730\u4e00\u638c\uff0c\u76f4\u5288\u80e1\u4e4b\u8f89\u7684\u9762\u95e8\uff0c\u638c\u98ce\u5982\u5200\u3000\uff0c\u638c \u672a\u5230\u65f6\uff0c\u5df2\u6fc0\u5f97\u80e1\u4e4b\u8f89\u8138\u4e0a\u706b\u8fa3\u8fa3\u5730\u75db\u3002 \u3000\u3000\u3000\u80e1\u4e4b\u8f89\u6025\u5fd9\u4fa7\u5934\uff0c\u62e7\u8eab\uff0c\u907f\u5f00\u6b64\u62db\uff0c\u767e\u5fd9\u4e2d\u770b\u5230\u6b64\u4eba\u53f3\u81c2\u7a7a\u7a7a\uff0c\u5fc3\u4e2d\u66f4\u80af\u5b9a\u4e86\u3000\u6b64\u4eba \u662f\u8c01\uff0c\u8d8a\u53d1\u4e0d\u6562\u56de\u624b\uff0c\u4f46\u6b64\u4eba\u51fa\u62db\u5982\u5954\u96f7\u8fc5\u7535\uff0c\u5530\u3001\u5530\u3001\u53c8\u662f\u4e24\u638c\uff0c\u4e13\u62a2\u504f\u950b\u3000\uff0c\u867d\u7136\u5931\u53bb \u53f3\u81c2\uff0c\u638c\u6cd5\u5374\u66f4\u51cc\u5389\u3002 \u3000\u3000\u3000\u80e1\u4e4b\u8f89\u88ab\u903c\u5f97\u8fde\u8bdd\u90fd\u8bf4\u4e0d\u51fa\u6765\uff0c\u53c8\u4e0d\u6562\u56de\u624b\uff0c\u60c5\u5f62\u6781\u5371\uff0c\u6bdb\u6587\u742a\u4e00\u58f0\u5a07\u53f1\uff0c\u63a0\u3000\u4e86\u8fc7 \u6765\uff0c\u53e6\u4e00\u7626\u957f\u6c49\u5b50\u66b4\u559d\u4e00\u58f0\uff0c\u53cc\u638c\u9f50\u51fa\uff0c\u5c06\u6bdb\u6587\u742a\u903c\u5230\u53e6\u4e00\u4fa7\uff0c\u8fd9\u8239\u5934\u7a7a\u5730\u672c\u3000\u4e0d\u5927\uff0c\u56db\u4eba \u642d\u4e0a\u624b\uff0c\u4fbf\u518d\u65e0\u7a7a\u9699\uff0c\u7f2a\u6587\u8fdc\u8fdc\u7ad9\u5728\u8231\u95e8\u4fa7\uff0c\u773c\u775b\u968f\u7740\u52a8\u624b\u7684\u56db\u4eba\u6253\u8f6c\u3000\uff0c\u77f3\u78f7\u4e0d\u4fbf\u63d2\u624b\uff0c \u671b\u7740\u8fd9\u4e24\u4e2a\u7626\u957f\u6c49\u5b50\u5feb\u5230\u6781\u70b9\u7684\u8eab\u6cd5\uff0c\u6697\u5fd6\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u7a76\u662f\u8c01\u3000\u5462\uff1f\u201d \u3000\u3000\u3000\u80e1\u4e4b\u8f89\u4e09\u62db\u8fc7\u540e\uff0c\u5df2\u662f\u624b\u811a\u5fd9\u4e71\uff0c\u4ed6\u6b66\u529f\u8fdc\u4e0d\u5982\u4ed6\u7684\u540d\u58f0\u4eae\uff0c\u8fd9\u4e24\u5e74\u517b\u5c0a\u5904\u4f18\u3000\uff0c\u8eab \u5f62\u66f4\u81c3\u80bf\uff0c\u809a\u5b50\u4e5f\u5927\u4e86\uff0c\u624b\u811a\u81ea\u7136\u66f4\u4e0d\u7075\u4fbf\uff0c\u90a3\u7626\u957f\u6c49\u5b50\u5e26\u51b7\u7b11\uff0c\u5355\u638c\u64a4\u8d77\u4e00\u3000\u7247\u638c\u5f71\uff0c\u5c06 \u6ee1\u5934\u5927\u6c57\u7684\u516b\u9762\u73b2\u73d1\u7f69\u5728\u638c\u98ce\u91cc\uff0c\u7adf\u4e0d\u5bb9\u4eba\u5bb6\u6709\u8bf4\u8bdd\u7684\u4f59\u5730\u3002 \u3000\u3000\u3000\u6bdb\u6587\u742a\u5de6\u624b\u62ff\u7740\u90a3\u732b\u76ae\u4e3a\u9798\u7684\u957f\u5251\uff0c\u8eab\u5f62\u66fc\u5999\u5982\u98de\u4ed9\uff0c\u53f3\u638c\u8f7b\u9001\uff0c\u98d8\u98d8\u6570\u638c\uff0c\u3000\u5982\u7f24 \u7eb7\u4e4b\u843d\u82f1\uff0c\u6f2b\u5929\u800c\u821e\uff0c\u90a3\u7626\u957f\u6c49\u5b50\u7684\u5982\u5c71\u638c\u98ce\uff0c\u7adf\u88ab\u5979\u8fd9\u79cd\u8f7b\u63cf\u6de1\u5199\u5730\u51e0\u638c\uff0c\u3000\u4ece\u5bb9\u5316\u89e3\u4e86 \u53bb\u3002 \u3000\u3000\u3000\u77f3\u78f7\u7cfb\u51fa\u540d\u95e8\uff0c\u5bf9\u6b66\u529f\u4e00\u9053\uff0c\u81ea\u662f\u8bc6\u8d27\uff0c\u770b\u4e86\u90a3\u4e24\u4e2a\u7626\u957f\u6c49\u5b50\u7684\u638c\u6cd5\uff0c\u5df2\u89c9\u529f\u3000\u529b\u9887 \u6df1\uff0c\u518d\u770b\u5230\u6bdb\u6587\u742a\u7684\u638c\u6cd5\uff0c\u66f4\u662f\u60ca\u5f02\uff0c\u4ee5\u4ed6\u7684\u9605\u5386\uff0c\u7adf\u4ecd\u770b\u4e0d\u51fa\u5979\u7684\u638c\u6cd5\u7a76\u7adf\u3000\u662f\u4f55\u95e8\u4f55\u6d3e \u6765\u3002 \u3000\u3000\u3000\u90a3\u4e24\u4e2a\u7626\u957f\u6c49\u5b50\uff0c\u4f7f\u7684\u662f\u5317\u6d3e\u5288\u6302\u638c\u4e00\u8def\u7684\u638c\u6cd5\uff0c\u62db\u5f0f\u867d\u4e0d\u5947\u5999\uff0c\u4f46\u51fa\u62db\u4e4b\u5feb\u3000\uff0c\u4ee4 \u4eba\u76ee\u4e0d\u6687\u63a5\uff0c\u638c\u98ce\u864e\u864e\uff0c\u529f\u529b\u5c24\u6df1\uff0c\u80e1\u4e4b\u8f89\u903c\u4e0d\u5f97\u5df2\uff0c\u65b9\u5f85\u8fd8\u62db\uff0c\u4f46\u5fc3\u4e2d\u4ecd\u6709\u3000\u4e9b\u865a\uff0c\u90a3\u72ec \u81c2\u6c49\u5b50\u5de6\u638c\u4e00\u7a7f\uff0c\u201c\u7075\u9f99\u51fa\u4e91\u201d\uff0c\u4ece\u80e1\u4e4b\u8f89\u4e24\u81c2\u7684\u7a7a\u9699\u4e2d\u51fb\u5411\u4ed6\u80c1\u4e0b\u3002 \u3000\u3000\u3000\u80e1\u4e4b\u8f89\u5927\u60ca\u626d\u8eab\uff0c\u72ec\u81c2\u6c49\u5b50\u51b7\u7b11\u4e00\u58f0\uff0c\u8155\u65f6\u731b\u4e00\u4f38\u7f29\uff0c\u80e1\u4e4b\u8f89\u4e00\u58f0\u95f7\u54fc\uff0c\u5df2\u88ab\u3000\u51fb\u4e2d \u201c\u671f\u95e8\u201d\u91cd\u7a74\uff0c\u8f6f\u8f6f\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u3000\u72ec\u81c2\u6c49\u5b50\u4e00\u62db\u5f97\u624b\uff0c\u90a3\u8fb9\u6bdb\u6587\u742a\u5374\u5df2\u7a33\u5360\u4e0a\u98ce\uff0c\u5a07\u559d\u9053\uff1a\u201c\u60f3\u4f60\u8fd9\u6837\u7684\u8eab\u624b\uff0c\u3000\u8fd8\u51fa \u6765\u73b0\u4ec0\u4e48\u4e16\uff1f\u201d\u90a3\u7626\u957f\u6c49\u5b50\u5927\u6012\uff0c\u957f\u5578\u4e00\u58f0\uff0c\u8eab\u5f62\u66b4\u9000\uff0c\u5411\u72ec\u81c2\u6c49\u5b50\u62db\u624b\u9053\uff1a\u3000\u201c\u8001\u5927\uff0c\u64a4 \u9752\u5b50\u62db\u547c\u4ed6\u3002\u201d \u3000\u3000\u3000\u7f2a\u6587\u770b\u5230\u6bdb\u6587\u742a\u7684\u6b66\u529f\uff0c\u4ea6\u6709\u5f02\u5bb9\uff0c\u77f3\u78f7\u5fae\u5fae\u611f\u53f9\uff1a\u201c\u6c5f\u5c71\u4ee3\u6709\u4eba\u624d\u51fa\uff0c\u65b0\u4eba\u3000\u603b\u662f \u6362\u65e7\u4eba\uff0c\u8fd9\u5c0f\u5c0f\u5973\u5b50\u7adf\u6709\u5982\u6b64\u6b66\u529f\u2014\u2014\u201d\u4e89\u5f3a\u4e4b\u5fc3\uff0c\u66f4\u662f\u4e00\u70b9\u513f\u4e5f\u6ca1\u6709\u4e86\uff0c\u7d22\u3000\u6027\u8896\u624b\u65c1\u89c2 \u8d77\u6765\uff0c\u8fd9\u6614\u65e5\u88ab\u6b66\u6797\u516c\u8ba4\u524d\u9014\u65e0\u91cf\u7684\u5e74\u8f7b\u5251\u624b\uff0c\u6b64\u523b\u610f\u6c14\u6d88\u6c89\uff0c\u4e0e\u4e16\u65e0\u3000\u4e89\uff0c\u8fd8\u4e0d\u662f\u4e3a\u60c5\u6240 \u7d2f\u3002 \u3000\u3000\u3000\u90a3\u4e24\u4e2a\u7626\u957f\u6c49\u5b50\u8eab\u5f62\u4e00\u77ee\uff0c\u5530\u5730\uff0c\u540e\u9000\u4e86\u51fa\u53bb\uff0c\u811a\u5c16\u7aef\u7740\u8239\u6cbf\uff0c\u811a\u8ddf\u5374\u5df2\u60ac\u7acb\u3000\u5728\u6c34 \u9762\u4e0a\uff0c\u6bdb\u6587\u742a\u9762\u5e26\u5fae\u7b11\uff0c\u6f2b\u4e0d\u7ecf\u5fc3\u5730\uff0c\u4f3c\u4e4e\u6ee1\u4e0d\u5728\u4e4e\u3002 \u3000\u3000\u3000\u80e1\u4e4b\u8f89\u7a74\u9053\u88ab\u70b9\uff0c\u773c\u775b\u5374\u4ecd\u770b\u5f97\u5230\uff0c\u5fc3\u91cc\u66f4\u7740\u6025\uff1a\u201c\u6bdb\u6bdb\u771f\u83bd\u649e\uff0c\u600e\u5730\u548c\u2018\u6cb3\u3000\u6714\u53cc \u5251\uff0c\u52a8\u8d77\u624b\u6765\u3002\u201d\u539f\u6765\u8fd9\u4e2a\u7626\u957f\u6c49\u5b50\u7adf\u662f\u540d\u95fb\u5929\u4e0b\u7684\u201c\u4e03\u5251\u4e09\u97ad\u201d\u4e2d\u7684\u201c\u6cb3\u6714\u3000\u53cc\u5251\u201d\u6c6a\u6c0f \u6606\u4ef2\uff0c\u90a3\u72ec\u81c2\u7684\u4e00\u4e2a\u5c31\u662f\u6614\u5e74\u88ab\u4ec7\u72ec\u4ee5\u91cd\u624b\u6cd5\u6298\u9aa8\uff0c\u4f24\u5904\u8150\u70c2\uff0c\u4e0d\u5f97\u4e0d\u3000\u5207\u53bb\u65ad\u81c2\u7684\u6c6a\u4e00 \u9e4f\uff0c\u53e6\u4e00\u4e2a\u81ea\u662f\u6c6a\u4e00\u9e23\u4e86\u3002\u6cb3\u6714\u53cc\u5251\u8eab\u5f62\u4e00\u9000\uff0c\u4e24\u4eba\u5e76\u80a9\u800c\u7acb\uff0c\u500f\u3000\u5730\u53c8\u98de\u63a0\u4e0a\u524d\uff0c\u5251\u5149\u5e76 \u8d77\uff0c\u5b9b\u5982\u4e24\u6761\u7ecf\u5929\u957f\u9f99\uff0c\u4ea4\u5c3e\u800c\u4e0b\uff0c\u6c6a\u4e00\u9e4f\u7684\u5251\u5149\u81ea\u5de6\u800c\u53f3\u3000\uff0c\u6c6a\u4e00\u9e23\u81ea\u53f3\u800c\u5de6\uff0c\u5530\u3001\u5530\u3001 \u4e24\u5251\uff0c\u5251\u5c3e\u5e26\u7740\u98a4\u52a8\u7684\u5bd2\u8292\uff0c\u76f4\u53d6\u6bdb\u6587\u742a\uff0c\u540d\u5bb6\u8eab\u624b\u3000\uff0c\u679c\u81ea\u4e0d\u51e1\uff0c\u77f3\u78f7\u79f0\u8d5e\uff1a\u2018\u597d\u5251 \u6cd5\u3002\u201d\u6bdb\u6587\u742a\u52a8\u4e5f\u4e0d\u52a8\uff0c\u8fd9\u4e24\u5251\u679c\u7136\u662f\u865a\u62db\uff0c\u5251\u5230\u3000\u4e2d\u9014\uff0c\u500f\u7136\u53d8\u4e86\u4e2a\u65b9\u5411\uff0c\u5728\u7a7a\u4e2d\u5212\u4e86\u4e2a \u534a\u5708\uff0c\u5237\u5730\uff0c\u76f4\u53d6\u6bdb\u6587\u742a\u7684\u54bd\u5589\u3001\u4e0b\u8179\u3002\u8fd9\u3000\u4e24\u5251\u540c\u65f6\u53d8\u62db\uff0c\u540c\u65f6\u51fa\u62db\uff0c\u4e0d\u5dee\u6beb\u5398\uff0c\u914d\u5408\u5f97 \u5929\u8863\u65e0\u7f1d\uff0c\u6c6a\u4e00\u9e4f\u53f3\u624b\u5df2\u65ad\uff0c\u5de6\u624b\u8fd0\u3000\u7528\u8d77\u5251\u6765\uff0c\u5374\u66f4\u89c1\u72e0\u8fa3\uff0c\u539f\u6765\u8fd9\u5144\u5f1f\u4e24\u4eba\uff0c\u8fd9\u4e9b\u5e74\u6765 \u7ade\u82e6\u7ec3\u6210\u4e86\u201c\u4e24\u4eea\u5251\u6cd5\u201d\uff0c\u4e24\u3000\u4eba\u8054\u624b\u653b\u654c\uff0c\u5a01\u529b\u4f55\u6b62\u589e\u4e86\u4e00\u500d\u3002\u6bdb\u6587\u742a\u8f7b\u7b11\u4e00\u58f0\uff0c\u811a\u6b65\u5fae \u9519\u95f4\uff0c\u4eba\u5df2\u6e9c\u5f00\u4e09\u5c3a\uff0c\u3000\u624b\u4e00\u52a8\uff0c\u4f17\u4eba\u53ea\u89c1\u773c\u524d\u7ea2\u5149\u4e00\u95ea\uff0c\u773c\u775b\u5374\u4e0d\u7981\u7728\u4e86\u4e00\u4e0b\uff0c\u6bdb\u6587\u742a\u5df2 \u62d4\u51fa\u5251\u6765\u3002\u2019\u5251\u3000\u5149\u4e0d\u662f\u5bfb\u5e38\u7684\u9752\u84dd\u8272\uff0c\u800c\u662f\u4e00\u79cd\u8fd1\u4e8e\u73ca\u745a\u822c\u7684\u7ea2\u8272\uff0c\u53d1\u51fa\u60ca\u4eba\u7684\u5149\uff0c\u5251\u8eab \u4e0a\u7adf\u4f3c\u8fd8\u3000\u5e26\u7740\u4e9b\u706b\u82b1\uff0c\u7adf\u4e0d\u77e5\u662f\u4ec0\u4e48\u6253\u5c31\u7684\u3002 \u3000\u3000\u3000\u6b64\u5251\u4e00\u51fa\uff0c\u6240\u6709\u7684\u4eba\u90fd\u5403\u4e86\u4e00\u60ca\uff0c\u77f3\u78f7\u4e45\u8d70\u6c5f\u6e56\uff0c\u53ef\u4e5f\u770b\u4e0d\u51fa\u8fd9\u5251\u7684\u6765\u8def\uff0c\u7f2a\u3000\u6587\u66f4 \u662f\u773c\u775b\u77ac\u4e5f\u4e0d\u77ac\u5730\u76ef\u5728\u8fd9\u67c4\u5251\u4e0a\u3002 \u3000\u3000\u3000\u6c6a\u6c0f\u6606\u4ef2\u662f\u4f7f\u5251\u7684\u540d\u5bb6\uff0c\u5e73\u65e5\u770b\u8fc7\u7684\u5251\uff0c\u4f55\u6b62\u5343\u6570\uff0c\u6b64\u523b\u4ea6\u662f\u9762\u5bb9\u4e00\u53d8\uff0c\u5251\u5149\u3000\u66b4 \u957f\uff0c\u4e24\u5251\u5404\u5212\u4e86\u4e2a\u6781\u5927\u7684\u534a\u5708\uff0c\u500f\u5730\u4e2d\u5fc3\u523a\u51fa\uff0c\u5251\u5c3e\u88ab\u4ed6\u4eec\u771f\u529b\u6240\u9707\uff0c\u55e1\u55e1\u4f5c\u54cd\u3000\uff0c\u7a81\u53c8\u5316 \u6210\u5341\u6570\u4e2a\u6781\u5c0f\u7684\u5251\u5708\u4e00\u70b9\uff0c\u88ad\u5411\u6bdb\u6587\u742a\uff0c\u6b63\u662f\u201c\u4e24\u4eea\u5251\u201d\u6cd5\u91cc\u7684\u7edd\u62db\u201c\u65e5\u3000\u6708\u4e89\u8f89\u201d\u3002 \u3000\u3000\u3000\u4e5f\u6b63\u662f\u201c\u6cb3\u6714\u53cc\u5251\u201d\u529f\u529b\u4e4b\u6240\u805a\u3002 \u3000\u3000\u3000\u80e1\u4e4b\u8f89\u8eba\u5728\u5730\u4e0a\uff0c\u773c\u775b\u867d\u7741\u5f00\uff0c\u5374\u770b\u4e0d\u89c1\u4ed6\u4eec\u7684\u52a8\u624b\uff0c\u539f\u6765\u4ed6\u7684\u5934\u5012\u4e0b\u53bb\u65f6\u662f\u3000\u4fa7\u5411 \u53e6\u4e00\u9762\uff0c\u6b64\u523b\u56e0\u8eab\u4e0d\u80fd\u52a8\u5f39\uff0c\u5934\u66f4\u65e0\u6cd5\u8f6c\u8fc7\u53bb\uff0c\u6b64\u65f6\u6025\u5f97\u8ddf\u5c60\u592b\u5200\u4e0b\u7684\u80a5\u732a\u4f3c\u3000\u7684\uff0c\u5374\u4e5f\u6ca1 \u6709\u529e\u6cd5\u3002 \u3000\u3000\u3000\u6bdb\u6587\u742a\u7b11\u5bb9\u672a\u53d8\uff0c\u638c\u4e2d\u5251\u7ea2\u5149\u66b4\u957f\uff0c\u5411\u6cb3\u6714\u53cc\u5251\u7684\u5251\u5149\u8fce\u4e86\u4e0a\u53bb\uff0c\u6cb3\u6714\u53cc\u5251\u53ea\u3000\u89c9\u638c \u4e2d\u5251\u7a81\u7136\u9047\u7740\u4e00\u80a1\u6781\u5f3a\u7684\u5438\u529b\uff0c\u81ea\u5df1\u7adf\u628a\u6301\u4e0d\u4f4f\uff0c\u786c\u8981\u5411\u4eba\u5bb6\u5251\u4e0a\u8d34\u53bb\uff0c\u6bdb\u6587\u3000\u742a\u5a07\u7b11\u559d \u9053\uff1a\u201c\u62ff\u6765\u3002\u201d\u6ee1\u5929\u5149\u96e8\u4e2d\uff0c\u4eba\u5f71\u4e4d\u5206\uff0c\u6cb3\u6714\u53cc\u5251\u5530\u5730\u540c\u65f6\u540e\u9000\uff0c\u624b\u4e2d\u7a7a\u3000\u7a7a\uff0c\u4e24\u773c\u53d1\u76f4\uff0c \u5403\u60ca\u5730\u671b\u7740\u5bf9\u65b9\u3002 \u3000\u3000\u3000\u6bdb\u6587\u742a\u7b11\u5bb9\u66f4\u5a9a\uff0c\u624b\u81c2\u5e73\u4f38\u4e86\u51fa\u6765\uff0c\u6c6a\u6c0f\u6606\u4ef2\u7684\u4e24\u67c4\u9752\u94a2\u957f\u5251\uff0c\u6b64\u523b\u7adf\u88ab\u5438\u5728\u3000\u5979\u90a3 \u67c4\u5f02\u7ea2\u8272\u7684\u957f\u5251\u4e0a\u3002 \u3000\u3000\u3000\u5c06\u5251\u4e00\u6325\uff0c\u6c6a\u6c0f\u6606\u4ef2\u7684\u53cc\u5251\uff0c\u500f\u5730\u98de\u4e86\u51fa\u53bb\uff0c\u8fdc\u8fdc\u843d\u5165\u6e56\u6c34\u91cc\uff0c\u4f17\u4eba\u4e0d\u7981\u9a87\u7136\u3000\uff0c\u8fd9 \u79cd\u529f\u529b\u7b80\u76f4\u532a\u5937\u6240\u601d\uff0c\u795e\u4e4e\u5176\u7384\u4e86\u3002 \u3000\u3000\u3000\u6cb3\u6714\u53cc\u5251\u4eab\u540d\u6b66\u6797\u5782\u4e09\u5341\u5e74\uff0c\u9664\u4e86\u6614\u65e5\u66fe\u5728\u201c\u4ec7\u5148\u751f\u201d\u624b\u4e0b\u53d7\u632b\u5916\uff0c\u6570\u5341\u5e74\u6765\u3000\u53ef\u8bf4 \u672a\u66fe\u9047\u8fc7\u654c\u624b\uff0c\u6b64\u523b\u4e09\u62db\u4e4b\u5185\uff0c\u5c31\u88ab\u4e00\u4e2a\u540d\u4e0d\u89c1\u7ecf\u4f20\u7684\u9ec4\u6bdb\u4e2b\u5934\u593a\u53bb\u624b\u4e2d\u4e4b\u5251\u3000\uff0c\u5fc3\u60c5\u53ef\u60f3 \u800c\u77e5\u3002 \u3000\u3000\u3000\u7ec3\u5bb6\u5b50\u88ab\u4eba\u593a\u53bb\u624b\u4e2d\u5175\u5203\uff0c\u4e43\u662f\u5947\u803b\u5927\u8fb1\uff0c\u4f55\u51b5\u201c\u6cb3\u6714\u53cc\u5251\u201d\u8fd9\u79cd\u8eab\u4efd\uff0c\u6c6a\u6c0f\u3000\u6606\u4ef2 \u6b64\u523b\u5fc3\u4e2d\u5b9b\u5982\u5200\u5272\uff0c\u53d1\u6014\u5730\u671b\u7740\u6bdb\u6587\u742a\uff0c\u8fd9\u5c11\u5973\u6b66\u529f\uff0c\u786e\u662f\u628a\u4ed6\u4eec\u5927\u5927\u5730\u60ca\u9a87\u3000\u4f4f\u4e86\u3002 \u3000\u3000\u3000\u4ed6\u7684\u4e94\u4e2a\u5f1f\u5b50\uff0c\u5e73\u65e5\u90fd\u628a\u5e08\u7236\u656c\u5982\u5929\u795e\uff0c\u6b64\u65f6\u5fc3\u4e2d\u4e5f\u4e0d\u7981\u96be\u53d7\uff0c\u8138\u4e0a\u989c\u8272\u5728\u53d8\u3000\uff0c\u90a3 \u4e24\u4e2a\u201c\u6b7b\u8783\u87f9\u201d\uff0c\u73b0\u5728\u8138\u4e5f\u4e0d\u7ea2\u4e86\uff0c\u53cd\u800c\u6709\u4e9b\u94c1\u9752\uff0c\u638c\u4e2d\u867d\u7136\u90fd\u62ff\u7740\u5251\uff0c\u8c01\u4e5f\u3000\u4e0d\u6562\u4e0a\u53bb\u548c \u4eba\u5bb6\u52a8\u624b\u3002 \u3000\u3000\u3000\u6cb3\u6714\u53cc\u5251\u8eab\u5f62\u8fd9\u4e00\u9000\uff0c\u80e1\u4e4b\u8f89\u53ef\u770b\u5230\u4e86\uff0c\u4ed6\u770b\u5230\u4ed6\u4eec\u7684\u795e\u8272\uff0c\u548c\u7a7a\u7740\u7684\u624b\uff0c\u77e5\u3000\u9053\u4ed6 \u4eec\u5df2\u7ecf\u5403\u4e86\u4e8f\uff0c\u5fc3\u91cc\u5374\u60ca\u559c\u4ea4\u96c6\uff0c\u60ca\u7684\u662f\u6bdb\u6587\u742a\u7adf\u5c06\u6cb3\u6714\u53cc\u5251\u7684\u62db\u724c\u62c6\u4e86\uff0c\u6cb3\u3000\u6714\u53cc\u5251\u5374\u662f \u5979\u7236\u4eb2\u7684\u670b\u53cb\uff0c\u8fd9\u7b14\u5e10\u4e0d\u77e5\u600e\u4e48\u4e2a\u7b97\u6cd5\uff1f \u3000\u3000\u3000\u559c\u7684\u5374\u662f\u670b\u53cb\u4e4b\u5973\uff0c\u6709\u8fd9\u79cd\u8eab\u624b\uff0c\u5728\u6b64\u65f6\u8fd9\u591a\u4e8b\u4e4b\u79cb\uff0c\u65e0\u7591\u591a\u4e86\u4e2a\u6781\u597d\u7684\u5e2e\u624b\u3000\uff0c\u80fd \u5c06\u201c\u6cb3\u6714\u53cc\u5251\u201d\u4e00\u4e3e\u800c\u51fb\u8d25\u7684\uff0c\u6b66\u6797\u4e2d\u6050\u6015\u771f\u8fd8\u6ca1\u6709\u51e0\u4e2a\u54e9\u3002 \u3000\u3000\u3000\u201c\u4e24\u4f4d\u7684\u5251\u6cd5\u9ad8\u660e\u5f97\u5f88\u3002\u201d\u6bdb\u6587\u742a\u5fae\u7b11\u7740\uff0c\u5c06\u90a3\u67c4\u5251\uff0c\u653e\u56de\u732b\u76ae\u5251\u9798\u91cc\uff0c\u8bf4\u9053\u3000\uff1a \u201c\u4e0d\u8fc7\u4e24\u4f4d\u82e5\u51ed\u7740\u8fd9\u70b9\u513f\u5251\u6cd5\u5c31\u60f3\u5728\u676d\u5dde\u897f\u6e56\u4e0a\u6492\u91ce\uff0c\u968f\u4fbf\u7528\u8239\u649e\u4eba\uff0c\u90a3\u8fd8\u5dee\u7740\u3000\u4e00\u5927\u8282\u5b50 \u54e9\u3002\u201d \u3000\u3000\u3000\u6cb3\u6714\u53cc\u5251\u8138\u4e0a\u9752\u4e00\u9635\uff0c\u767d\u4e00\u9635\uff0c\u6c14\u5f97\u6d51\u8eab\u53d1\u6296\uff0c\u4e00\u53e5\u8bdd\u4e5f\u8bb2\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u3000\u6bdb\u6587\u742a\u53c8\u8ba5\u8bbd\u5730\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\u4e24\u4f4d\u5fc5\u5b9a\u4e0d\u670d\u6c14\u5bf9\u5417\uff1f\u90a3\u4e5f\u6ca1\u6709\u5173\u7cfb\uff0c\u4e24\u4f4d\u4ee5\u3000\u540e\u5982 \u679c\u8981\u627e\u6211\uff0c\u5230\u676d\u5dde\u6765\u627e\u59d3\u6bdb\u7684\u597d\u4e86\u3002\u201d \u3000\u3000\u3000\u5979\u5a07\u58f0\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u5fc5\u5df2\u5728\u6c5f\u6e56\u4e0a\u6df7\u4e86\u4e0d\u5c11\u5e74\uff0c\u6211\u53ef\u4e0d\u662f\u62ac\u6211\u7236\u4eb2\u7684\u62db\u724c\u51fa\u3000\u6765\u5413 \u552c\u4f60\u4eec\u3002\u201d \u3000\u3000\u3000\u6cb3\u6714\u53cc\u5251\u9762\u5bb9\u84e6\u5730\u5927\u53d8\uff0c\u9f50\u58f0\u800c\u9053\uff1a\u201c\u7075\u86c7\u6bdb\u81ec\u3002\u201d\u6bdb\u6587\u742a\u9053\uff1a\u201c\u5bf9\u4e86\u201d\u3002\u6cb3\u3000\u6714\u53cc \u5251\u4e00\u58f0\u4e0d\u53d1\uff0c\u4e00\u8dfa\u811a\uff0c\u540c\u65f6\u957f\u8eab\u800c\u8d77\uff0c\u5728\u7a7a\u4e2d\u4e00\u62e7\u8eab\uff0c\u55d6\u5730\uff0c\u7a9c\u5230\u4ed6\u4eec\u81ea\u5df1\u7684\u3000\u90a3\u8258\u753b\u822b\u4e0a \u53bb\u4e86\u3002 \u3000\u3000\u3000\u6bdb\u6587\u742a\u671d\u90a3\u4e94\u4e2a\u52b2\u88c5\u6301\u5251\u7684\u5c11\u5e74\u4e00\u7b11\uff0c\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u8fd8\u4e0d\u6eda\uff1f\u201d\u58f0\u97f3\u6e29\u67d4\u3000\u5f97 \u5f88\uff0c\u90a3\u4e94\u4e2a\u5c11\u5e74\u542c\u4e86\uff0c\u90a3\u79cd\u6ecb\u5473\u8fd8\u771f\u4e0d\u597d\u53d7\uff0c\u4e94\u4eba\u4e0d\u7ea6\u800c\u540c\u5730\u4e00\u8f6c\u8eab\uff0c\u671d\u90a3\u4e00\u8258\u3000\u753b\u822b\u4e0a\u7eb5 \u53bb\uff0c\u6025\u5207\u4e4b\u4e0b\uff0c\u5374\u672a\u60f3\u5230\u81ea\u5df1\u529f\u529b\u5c1a\u4e0d\u591f\uff0c\u5657\u901a\uff0c\u5657\u901a\uff0c\u51e0\u4e2a\u90fd\u6389\u4e0b\u6cb3\u91cc\u3000\u53bb\u4e86\u3002 \u3000\u3000\u3000\u6bdb\u6587\u742a\u7b11\u5f97\u5982\u82b1\u679d\u4e71\u98a4\uff0c\u770b\u5230\u80e1\u4e4b\u8f89\u4ecd\u8eba\u5728\u5730\u4e0a\uff0c\u8d70\u8fc7\u53bb\u770b\u4e86\u770b\uff0c\u968f\u624b\u4e00\u62cd\uff0c\u3000\u80e1\u4e4b \u8f89\u7684\u7a74\u9053\u5c31\u89e3\u5f00\u4e86\uff0c\u7ad9\u8d77\u54b3\u55fd\u4e00\u58f0\uff0c\u5410\u51fa\u4e00\u53e3\u6d53\u75f0\uff0c\u957f\u957f\u5618\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u3000\u201c\u4e09\u53d4\uff0c\u53ef\u8f9b\u82e6\u4f60\u4e86\u3002\u201d\u6bdb\u6587\u742a\u7b11\u9053\uff0c\u80e1\u4e4b\u8f89\u82e6\u7740\u8138\uff0c\u5598\u7740\u6c14\u8bf4\u9053\uff1a\u201c\u82e6\u4e86\u6211\u3000\u5230\u6ca1 \u6709\u4ec0\u4e48\u5173\u7cfb\uff0c\u53ef\u662f\u59d1\u5a18\u4f60\u5374\u95ef\u4e86\u5927\u7978\u4e86\u3002\u201d \u3000\u3000\u3000\u6bdb\u6587\u742a\u8be7\u7136\u9053\uff1a\u201c\u6211\u95ef\u4e86\u4ec0\u4e48\u7978\uff1f\u201d\u80e1\u4e4b\u8f89\u53f9\u9053\uff1a\u201c\u6211\u7684\u5927\u59d1\u5a18\uff0c\u4f60\u628a\u4eba\u5bb6\u595a\u3000\u843d\u5f97 \u6ee1\u8212\u670d\uff0c\u6253\u4e5f\u6253\u4e86\u534a\u5929\uff0c\u4f60\u53ef\u77e5\u9053\u4eba\u5bb6\u662f\u8c01\u5417\uff1f\u201d \u3000\u3000\u3000\u6bdb\u6587\u742a\u6447\u6447\u5934\uff0c\u80e1\u4e4b\u8f89\u9053\uff1a\u201c\u4f60\u5f53\u7136\u4e0d\u77e5\u9053\uff0c\u4f60\u8981\u662f\u77e5\u9053\uff0c\u4f60\u4e5f\u4e0d\u4f1a\u6253\u4e86\u3002\u201d\u3000\u6bdb\u6587 \u742a\u6709\u4e9b\u7740\u6025\uff0c\u95ee\u9053\uff1a\u201c\u4ed6\u4eec\u5230\u5e95\u662f\u8c01\uff1f\u4e09\u53d4\u8bb2\u8bdd\u8001\u662f\u8fd9\u6837\u62d6\u6ce5\u5e26\u6c34\u7684\u3002\u201d \u3000\u3000\u3000\u201c\u4ed6\u4eec\u5c31\u662f\u548c\u4f60\u7236\u4eb2\u9f50\u540d\u7684\u2018\u6cb3\u6714\u53cc\u5251\uff0c\u5440\uff01\u201d\u80e1\u4e4b\u8f89\u8bf4\u9053\u3002\u6bdb\u6587\u742a\u542c\u4e86\uff0c\u4e5f\u3000\u4e0d\u89c9 \u5f97\u5446\u4e86\u4e00\u5446\uff0c\u77f3\u78f7\u8fc7\u6765\uff0c\u60ca\u9053\uff1a\u201c\u4ed6\u4eec\u5c31\u662f\u2018\u6cb3\u6714\u53cc\u5251\u2019\u5417\uff1f\u201d\u7f2a\u6587\u7ad9\u5728\u9634\u5f71\u3000\u91cc\uff0c\u8138\u4e0a\u4f3c \u7b11\u975e\u7b11\u4e0d\u77e5\u5fc3\u91cc\u5728\u8f6c\u4ec0\u4e48\u5ff5\u5934\uff0c\u6bdb\u6587\u742a\u5374\u671d\u4ed6\u8d70\u8fc7\u53bb\uff0c\u5a07\u7b11\u7740\u8bf4\u9053\uff1a\u201c\u3000\u4f60\u770b\u4ec0\u4e48\u5440\uff1f\u6211\u5728 \u6253\u67b6\uff0c\u4f60\u4e5f\u4e0d\u6765\u5e2e\u5fd9\u3002\u201d \u3000\u3000\u3000\u7f2a\u6587\u6447\u5934\u4f5c\u82e6\u7b11\u72b6\u9053\uff1a\u201c\u975e\u4e0d\u4e3a\u4e5f\uff0c\u4e43\u4e0d\u80fd\u4e5f\uff0c\u5c0f\u751f\u975e\u4e0d\u613f\u6253\u67b6\u4e5f\uff0c\u5b9e\u4e43\u529b\u6709\u3000\u6240\u4e0d \u902e\uff0c\u4e0d\u6562\u81ea\u53d6\u5176\u8fb1\u8033\u3002\u201d \u3000\u3000\u3000\u6bdb\u6587\u742a\u7b11\u5f97\u683c\u683c\u5730\u54cd\uff0c\u9053\uff1a\u201c\u4f60\u77a7\u4f60\uff0c\u8bf4\u5f97\u8fd8\u50cf\u4eba\u8bdd\u5417\uff1f\u201d\u5979\u548c\u7f2a\u6587\u672c\u4e0d\u719f\uff0c\u3000\u53ef\u662f \u5374\u4e00\u70b9\u513f\u4e5f\u4e0d\u5bb3\u7f9e\uff0c\u77f3\u78f7\u6709\u4e9b\u5947\u602a\uff0c\u5374\u4e0d\u77e5\u9053\u6bdb\u6587\u742a\u6709\u751f\u4ee5\u6765\uff0c\u8fd8\u4e0d\u77e5\u9053\u5bb3\u7f9e\u3000\u662f\u600e\u4e48\u56de\u4e8b \u5462\u3002 \u3000\u3000\u3000\u7f2a\u6587\u770b\u7740\u5979\u5929\u771f\u7684\u795e\u6001\uff0c\u5634\u89d2\u6cdb\u8d77\u7b11\u5bb9\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u7684\u5251\uff0c\u59d4\u5b9e\u597d\u73a9\u5f97\u7d27\uff0c\u5c0f\u3000\u751f\u53ef \u4ee5\u770b\u770b\u5417\uff1f\u2019\uff0c\u201d\u53ef\u4ee5\u662f\u53ef\u4ee5\uff0c\u4e0d\u8fc7\u2014\u2014\u201d\u6bdb\u6587\u742a\u5a07\u7b11\u7740\uff0c\u62d6\u957f\u4e86\u58f0\u97f3\uff0c\u7f2a\u6587\u3000\u7b11\u9053\uff1a\u201c\u4e0d \u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u3000\u201c\u4e0d\u8fc7\u4f60\u4ee5\u540e\u8bf4\u8d77\u8bdd\u6765\uff0c\u53ef\u4e0d\u51c6\u5c0f\u751f\u5c0f\u751f\u7684\uff0c\u542c\u8d77\u6765\u522b\u626d\u6b7b\u4e86\u3002\u201d\u5979\u7b11\u7740\u9053\u3002\u3000\u77f3\u78f7 \u4e0d\u7981\u5fae\u7b11\u6697\u5fd6\uff1a\u201c\u8fd9\u5973\u5b69\u5b50\u5012\u662f\u5929\u771f\u672a\u6cef\u3002\u201d \u3000\u3000\u3000\u5979\u5c06\u638c\u4e2d\u7684\u5251\u62d4\u4e86\u51fa\u6765\uff0c\u7f2a\u6587\u5f80\u540e\u9000\u4e86\u4e24\u6b65\uff0c\u4f3c\u4e4e\u5413\u4e86\u4e00\u8df3\uff0c\u77f3\u78f7\u4e00\u60ca\uff1a\u201c\u8fd9\u3000\u5251\u5149 \u600e\u5730\u8fd9\u79cd\u989c\u8272\uff1f\u201d \u3000\u3000\u3000\u6bdb\u6587\u742a\u7b11\u9053\uff1a\u201c\u4f60\u6478\u6478\u770b\u3002\u201d\u7f2a\u6587\u7ad9\u5f97\u8fdc\u8fdc\u7684\uff0c\u76f4\u6447\u5934\uff0c\u80e1\u4e4b\u8f89\u7b11\u7740\u8d70\u8fc7\u53bb\uff0c\u3000\u9053\uff1a \u201c\u6478\u6478\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d \u3000\u3000\u3000\u679c\u7136\u8d70\u8fc7\u53bb\u6478\u4e86\u4e00\u4e0b\uff0c\u624b\u6307\u521a\u4e00\u89e6\u53ca\u5251\u8eab\uff0c\u5168\u8eab\u7a81\u5730\u4e00\u9707\uff0c\u8df3\u8d77\u4e00\u5c3a\u9ad8\uff0c\u8fde\u5fd9\u3000\u9000\u4e86 \u5f00\u53bb\uff0c\u8138\u4e0a\u715e\u767d\uff0c\u60ca\u53eb\u9053\uff1a\u201c\u8fd9\u67c4\u5251\u6709\u4ec0\u4e48\u53e4\u602a\uff1f\u201d \u3000\u3000\u3000\u6bdb\u6587\u742a\u7b11\u5f97\u8d8a\u53d1\u5389\u5bb3\uff0c\u9053\uff1a\u201c\u4e09\u53d4\uff0c\u4f60\u4e0a\u4e86\u5f53\u5427\u3002\u201d\u660e\u7738\u4e00\u98d8\u7f2a\u6587\uff0c\u53c8\u9053\uff1a\u201c\u3000\u8fd8\u662f \u4f60\u806a\u660e\uff0c\u201d\u77f3\u78f7\u867d\u5931\u7b11\uff0c\u4f46\u4e5f\u60ca\u5f02\uff0c\u4ed6\u8d70\u904d\u5929\u4e0b\uff0c\u5374\u4e5f\u6ca1\u6709\u89c1\u8fc7\u4eba\u4e00\u6478\u5c31\u4f1a\u8df3\u3000\u8d77\u6765\u7684\u5251\uff0c \u751a\u81f3\u8fde\u542c\u4e5f\u6ca1\u542c\u8bf4\u8fc7\u54e9\u3002 \u3000\u3000\u3000\u84e6\u5730\u6e56\u4e2d\u7bad\u4e5f\u4f3c\u5730\u9a76\u6765\u4e00\u8258\u5c0f\u8239\uff0c\u6447\u8239\u7684\u4eba\u4e0d\u4f46\u6c34\u6027\u7cbe\u719f\uff0c\u624b\u52b2\u4e5f\u7279\u522b\u5927\uff0c\u6643\u3000\u773c\u95f4 \u4fbf\u9a76\u5230\u8fd1\u524d\uff0c\u53cc\u6868\u4e00\u7ffb\uff0c\u5c0f\u8239\u4fbf\u505c\u4e0b\u6765\uff0c\u6447\u8239\u7684\u4eba\u5c06\u6868\u653e\u4e0b\u4e86\uff0c\u55d6\uff01\u4fbf\u8df3\u5230\u8fd9\u3000\u8258\u753b\u822b\u4e0a \u6765\uff0c\u8eab\u624b\u4e4b\u77eb\u5065\uff0c\u5728\u6b66\u6797\u4e2d\u53ef\u7b97\u4e00\u6d41\u4eba\u7269\u3002 \u3000\u3000\u3000\u4ed6\u957f\u8eab\u7389\u7acb\uff0c\u4e0a\u4e86\u8239\u5c31\u5411\u6bdb\u6587\u742a\u9053\uff1a\u201c\u4f60\u95ef\u4e86\u7978\u4e86\u5427\uff1f\u201d\u76ee\u5149\u56db\u987e\uff0c\u5411\u5927\u5bb6\u4e00\u3000\u7b11\uff0c \u7f2a\u6587\u89c1\u4e86\u8fd9\u4eba\uff0c\u5168\u8eab\u5374\u751f\u51fa\u4e00\u9635\u51c9\u610f\uff0c\u76f4\u900f\u80cc\u810a\uff0c\u4ece\u6765\u5f88\u5c11\u53d8\u8272\u7684\u8138\uff0c\u6b64\u523b\u4ea6\u3000\u53d8\u6210\u4e86\u60e8\u767d \u8272\u3002 \u7b2c\u516d\u7ae0>> \u53e4\u9f99\u300a\u6e58\u5983\u5251\u300b \u7b2c\u516d\u7ae0 \u3000\u3000\u3000\u5728\u5927\u5bb6\u90fd\u60ca\u5f02\u4e8e\u6bdb\u6587\u742a\u638c\u4e2d\u73ca\u745a\u8272\u7684\u5b9d\u5251\u6240\u5177\u6709\u7684\u90a3\u79cd\u795e\u5947\u7684\u529f\u80fd\u7684\u65f6\u5019\uff0c\u897f\u3000\u6e56\u4e2d \u7a81\u5730\u7bad\u4e5f\u4f3c\u7684\u9a76\u6765\u4e00\u8258\u5c0f\u8239\uff0c\u64cd\u6868\u4e4b\u4eba\uff0c\u624b\u52b2\u7279\u5927\uff0c\u970e\u65f6\u95f4\u4fbf\u9a76\u5230\u8fd1\u524d\uff0c\u500f\u7136\u3000\u505c\u4e0b\u4e86\u5c0f \u8239\uff0c\u8f7b\u7075\u654f\u6377\u5730\u8df3\u4e0a\u8239\u6765\u2014\u2014\u3002 \u3000\u3000\u3000\u7f2a\u6587\u4e00\u89c1\u90a3\u4eba\uff0c\u957f\u8eab\u7389\u7acb\uff0c\u7a7f\u7740\u91d1\u8272\u957f\u886b\uff0c\u9762\u8c8c\u9887\u4e3a\u82f1\u4fca\uff0c\u4e24\u53ea\u773c\u775b\u5fae\u5fae\u4e0a\u7ffb\u3000\uff0c\u5e26 \u7740\u4e00\u79cd\u903c\u4eba\u7684\u50b2\u6c14\uff0c\u4e0d\u662f\u90a3\u5728\u5ba2\u6808\u4e2d\u60e8\u88ab\u201c\u91d1\u5251\u4fa0\u201d\u51fb\u6bd9\u7684\u201c\u7389\u9762\u4f7f\u8005\u201d\u5e9e\u58eb\u3000\u6e5b\u662f\u51c6\uff1f \u3000\u3000\u3000\u7f2a\u6587\u4e0d\u7981\u9762\u8272\u5927\u53d8\uff0c\u5168\u8eab\u8d77\u4e86\u4e00\u9635\u60ca\u6817\u7684\u611f\u89c9\uff0c\u4ed6\u4eb2\u773c\u6240\u89c1\u5df2\u7ecf\u60e8\u6b7b\u4e4b\u4eba\uff0c\u6b64\u3000\u523b\u7adf \u53c8\u91cd\u73b0\uff0c\u81ea\u7136\u96be\u602a\u4ed6\u5403\u60ca\uff0c\u53d8\u8272\u3002 \u3000\u3000\u3000\u77f3\u78f7\u4ea6\u5927\u60ca\uff0c\u54ea\u77e5\u6bdb\u6587\u742a\u548c\u80e1\u4e4b\u8f89\u4ecd\u5fae\u5fae\u542b\u7b11\uff0c\u4eff\u4f5b\u8fd9\u4e8b\u4e1d\u6beb\u4e0d\u503c\u5f97\u60ca\u5f02\u4f3c\u7684\u3000\uff0c\u6bdb \u6587\u742a\u7f13\u7f13\u5c06\u5251\u653e\u56de\u5251\u9798\uff0c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u54a6\uff01\u4f60\u600e\u4e48\u77e5\u9053\u6211\u95ef\u4e86\u7978\u4e86\uff1f\u201d\u80e1\u4e4b\u8f89\u3000\u5374\u9053\uff1a\u201c\u662f \u5426\u90a3\u6cb3\u6714\u53cc\u5251\u6c6a\u6c0f\u6606\u4ef2\u5df2\u5230\u6bdb\u5927\u54e5\u90a3\u91cc\uff0c\u4ed6\u4eec\u7684\u811a\u7a0b\u5012\u771f\u5feb\uff01\u201d \u3000\u3000\u3000\u90a3\u82f1\u4fca\u5c11\u5e74\u76ee\u5149\u53c8\u4e00\u8f6c\uff0c\u4e5f\u4e0d\u671f\u7136\u505c\u7559\u5728\u7f2a\u6587\u8138\u4e0a\uff0c\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u8fd8\u6ca1\u6709\u5230\u5e08\u3000\u7236\u90a3 \u91cc\uff0c\u53ea\u662f\u88ab\u5c0f\u4f84\u6070\u6070\u5728\u6e56\u7554\u9047\u7740\uff0c\u4ed6\u5144\u5f1f\u4e8c\u4eba\u5927\u53d1\u4e86\u4e00\u9635\u96f7\u9706\uff0c\u800c\u4e14\u8bf4\u8981\u7acb\u5373\u3000\u8d76\u56de\u6cb3\u6714\uff0c \u8fd9\u91cc\u7684\u4e8b\u4e0d\u518d\u7ba1\u3002\u201d\u4ed6\u5fae\u5fae\u4e00\u7b11\uff0c\u76ee\u5149\u671d\u6bdb\u6587\u742a\u4e00\u8f6c\uff0c\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u8fd9\u3000\u4e24\u4e2a\u8001\u602a\u7269\u81ea\u5df1\u8981 \u62db\u60f9\u742a\u59b9\u7684\u2018\u7425\u73c0\u795e\u5251\u2019\uff0c\u90a3\u4e0d\u662f\u4ed6\u4eec\u8981\u81ea\u53d6\u5176\u8fb1\uff0c\u53ef\u602a\u5f97\u4e86\u8c01\u3000\uff1f\u201d\u8bed\u6c14\u4e4b\u4e2d\uff0c\u663e\u7136\u5730\u663e \u51fa\u4e86\u5bf9\u201c\u6cb3\u6714\u53cc\u5251\u201d\u7684\u8f7b\u89c6\uff0c\u66f4\u9732\u51fa\u4e86\u5bf9\u6bdb\u6587\u742a\u7684\u8ba8\u597d\u3002 \u3000\u3000\u3000\u6bdb\u6587\u742a\u679c\u7136\u751c\u751c\u4e00\u7b11\uff0c\u90a3\u957f\u8eab\u5c11\u5e74\u5374\u5bf9\u7f2a\u6587\u8d70\u4e86\u4e24\u6b65\uff0c\u9762\u4e0a\u5140\u81ea\u5e26\u7740\u7b11\u5bb9\uff0c\u7f2a\u3000\u6587\u888d \u8896\u4e00\u62c2\uff0c\u867d\u7136\u5f3a\u81ea\u9547\u9759\uff0c\u4f46\u9762\u8272\u60e8\u767d\u3002 \u3000\u3000\u3000\u80e1\u4e4b\u8f89\u52c9\u5f3a\u5730\u7b11\u4e86\u51e0\u58f0\uff0c\u8d70\u8fc7\u6765\u9053\uff1a\u201c\u7f2a\u8001\u5144\u4e0d\u8ba4\u8bc6\u5427\uff0c\u8ba9\u6211\u6765\u5f15\u89c1\u4e00\u4f4d\u9ad8\u4eba\u3000\u3002\u201d \u4ed6\u76ee\u5149\u671d\u7f2a\u6587\u5fae\u4e00\u793a\u610f\uff0c\u6307\u7740\u90a3\u957f\u8eab\u5c11\u5e74\u9053\uff1a\u201c\u8fd9\u4f4d\u5c31\u662f\u7075\u86c7\u6bdb\u81ec\u5927\u54e5\u7684\u5341\u5927\u3000\u5f1f\u5b50\uff0c\u7389\u9aa8 \u4f7f\u8005\u4e2d\u7684\u7b2c\u4e09\u4f4d\uff0c\u2018\u51cc\u98ce\u4f7f\u8005\uff0c\u5e9e\u826f\u6e5b\u5e9e\u4e8c\u4fa0\uff0c\u4f60\u4eec\u4e8c\u4f4d\u5c11\u5e74\u82f1\u53d1\uff0c\u4ee5\u3000\u540e\u591a\u4eb2\u8fd1\u4eb2\u8fd1\u3002\u201d \u5e9e\u826f\u6e5b\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u770b\u8fd9\u4f4d\u7f2a\u5144\u7684\u795e\u8272\uff0c\u60f3\u5fc5\u662f\u8ba4\u8bc6\u5bb6\u5144\uff0c\u3000\u6c5f\u6e56\u4e2d\u4eba\u5c06\u6211\u5144\u5f1f\u8bef\u4e3a\u4e00 \u4eba\u7684\uff0c\u4e0d\u77e5\u6709\u591a\u5c11\u3002\u201d\u4ed6\u8f6c\u8138\u5411\u80e1\u4e4b\u8f89\u4e00\u77aa\uff0c\u9053\uff1a\u201c\u80e1\u4e09\u3000\u53d4\u4e0d\u5fc5\u5411\u7f2a\u5144\u505a\u773c\u8272\uff0c\u5bb6\u5144\u7684\u6b7b \u8baf\uff0c\u6211\u65e9\u5df2\u77e5\u9053\u4e86\uff0c\u662f\u4ee5\u8fd9\u4f4d\u89c1\u7740\u6211\uff0c\u4ee5\u4e3a\u6b7b\u4eba\u590d\u3000\u6d3b\uff0c\u624d\u4f1a\u9732\u51fa\u60ca\u5f02\u4e4b\u8272\u6765\u7684\u3002\u201d \u3000\u3000\u3000\u7f2a\u6587\u604d\u7136\uff0c\u5374\u4e0d\u7981\u66f4\u7559\u610f\u5730\u6253\u91cf\u7740\u8fd9\u201c\u51cc\u98ce\u4f7f\u8005\u201d\u3002\u53e3\u4e2d\u81ea\u7136\u6781\u4e3a\u5ba2\u6c14\u5730\u5e94\u4ed8\u3000\u4e86\u51e0 \u53e5\uff0c\u5fc3\u4e2d\u5374\u4e0d\u7981\u6697\u81ea\u601d\u91cf\u7740\uff1a\u201c\u8fd9\u2018\u51cc\u98ce\u4f7f\u8005\u2019\u5fc3\u601d\u4e4b\u51b7\u9177\u3001\u673a\u667a\uff0c\u770b\u6765\u7adf\u8fd8\u3000\u5728\u4ed6\u5144\u957f\u4e4b \u4e0a\uff0c\u4ed6\u77e5\u9053\u4e86\u54e5\u54e5\u7684\u6b7b\u8baf\uff0c\u8138\u4e0a\u7adf\u6beb\u65e0\u60b2\u621a\u4e4b\u5bb9\uff0c\u90a3\u80e1\u4e4b\u8f89\u53ea\u5fae\u5fae\u505a\u4e86\u3000\u4e2a\u773c\u8272\uff0c\u4ed6\u5374\u5df2\u77e5 \u9053\u4e86\u4eba\u5bb6\u7684\u7528\u610f\uff0c\u800c\u4e14\u6beb\u4e0d\u7559\u60c5\u4ed6\u8bf4\u4e86\u51fa\u6765\uff0c\u5509\uff01\u8fd9\u79cd\u4eba\u5fc3\u667a\u8d8a\u3000\u9ad8\uff0c\u5c06\u6765\u6050\u6015\u4e3a\u5bb3\u4e5f\u8d8a\u5389 \u5bb3\uff01\u201d \u3000\u3000\u3000\u80e1\u4e4b\u8f89\u53ea\u5f97\u5c34\u5c2c\u5730\u4e00\u7b11\uff0c\u8f6c\u5f00\u8bdd\u9898\uff0c\u53c8\u4e3a\u4ed6\u5f15\u89c1\u4e86\u77f3\u78f7\uff0c\u77f3\u78f7\u8bcd\u8272\u51b7\u6f20\uff0c\u60f3\u5fc5\u3000\u4e5f\u662f \u5bf9\u4ed6\u7684\u8fd9\u79cd\u201c\u51b7\u9177\u201d\uff0c\u9887\u4e3a\u4e0d\u6ee1\u3002 \u3000\u3000\u3000\u5e9e\u826f\u6e5b\u5374\u8f6c\u5411\u7f2a\u6587\uff0c\u9053\uff1a\u201c\u5bb6\u5144\u6b7b\u65f6\uff0c\u7f2a\u5144\u4e5f\u5728\u573a\u5427\uff1f\u201d\u7f2a\u6587\u5fae\u4e00\u70b9\u5934\uff0c\u795e\u8272\u3000\u5df2\u6062 \u590d\u5148\u524d\u7684\u90a3\u79cd\u65e0\u52a8\u4e8e\u8877\uff0c\u80e1\u4e4b\u8f89\u8d70\u524d\u4e00\u6b65\uff0c\u957f\u53f9\u7740\u9053\uff1a\u201c\u4ee4\u5144\u6b7b\u5f97\u5b9e\u5728\u4ee4\u4eba\u627c\u3000\u8155\uff0c\u4f46\u5e9e\u8d24 \u4f84\u4e5f\u4e0d\u5fc5\u8fc7\u4e8e\u60b2\u4f24\u4e00\u4e00\u201d\u4ed6\u7f13\u7f13\u5730\u6b62\u4f4f\u4e86\u8bdd\uff0c\u77f3\u78f7\u5fae\u6652\u4e00\u4e0b\uff0c\u5fd6\u9053\uff1a\u201c\u3000\u4ed6\u6839\u672c\u5168\u65e0\u60b2\u4f24\u4e4b \u610f\uff0c\u8fd9\u2018\u516b\u9762\u73b2\u73d1\uff0c\u7684\u5e9f\u8bdd\uff0c\u5012\u771f\u4e0d\u5c11\uff01\u201d\u5e9e\u826f\u6e5b\u4f3c\u4e4e\u4e5f\u5bf9\u4ed6\u8fd9\u3000\u4f4d\u201c\u80e1\u4e09\u53d4\u201d\u9887\u4e0d\u6b23\u8d4f\uff0c \u800c\u4e14\u4ed6\u4e5f\u6beb\u4e0d\u5ba2\u6c14\u5730\u5c06\u8fd9\u79cd\u201c\u4e0d\u6b23\u8d4f\u201d\u653e\u5728\u8138\u4e0a\uff0c\u6839\u672c\u4e0d\u3000\u7406\u80e1\u4e4b\u8f89\u7684\u8bdd\uff0c\u5374\u5411\u6bdb\u6587\u742a\u9053\uff1a \u201c\u5e08\u5085\u4e00\u76f4\u60e6\u8bb0\u7740\u4f60\uff0c\u6015\u4f60\u53c8\u51fa\u4e86\u4e8b\uff0c\u5176\u5b9e\u4ed6\u8001\u4eba\u3000\u5bb6\u4e5f\u592a\u8fc7\u5c0f\u5fc3\uff0c\u5c31\u51ed\u7740\u4f60\u8fd9\u67c4\u5251\uff0c\u4f60\u8d70 \u5230\u54ea\u91cc\u53bb\u8fd8\u4f1a\u5403\u4e8f\u5417\uff1f\u201d \u3000\u3000\u3000\u6bdb\u6587\u742a\u5a07\u55d4\u7740\u9053\uff1a\u201c\u54e6\uff01\u6211\u5c31\u5168\u51ed\u7740\u8fd9\u67c4\u5251\u662f\u4e0d\u662f\uff1f\u4f60\u522b\u4ee5\u4e3a\u4f60\u6b66\u529f\u86ee\u4e0d\u9519\u7684\u3000\uff0c\u6211 \u7a7a\u7740\u624b\u7167\u6837\u53ef\u4ee5\u628a\u4f60\u6253\u5012\u3002\u201d \u3000\u3000\u3000\u7f2a\u6587\u5fae\u5fae\u4e00\u7b11\uff0c\u5e9e\u826f\u6e5b\u679c\u7136\u4e5f\u6709\u4e9b\u8272\u53d8\uff0c\u4f46\u5374\u7acb\u523b\u5fcd\u8010\u7740\uff0c\u53cd\u800c\u5fae\u7b11\u9053\uff1a\u201c\u5f53\u3000\u7136\uff0c \u5f53\u7136\uff0c\u5c60\u9f99\u4ed9\u5b50\u7684\u7231\u5f92\uff0c\u522b\u8bf4\u6211\uff0c\u5c31\u628a\u6211\u4eec\u5144\u5f1f\u5341\u4e2a\u4e00\u9f50\u51d1\u4e0a\u4e5f\u4e0d\u884c\u5440\uff01\u201d\u6bdb\u3000\u6587\u742a\u8dfa\u811a\uff0c \u771f\u7684\u751f\u6c14\u7740\u9053\uff1a\u201c\u597d\uff01\u4f60\u6562\u8bf4\u51fa\u6211\u5e08\u5085\u4ed6\u8001\u4eba\u5bb6\u7684\u540d\u5b57\uff0c\u4f60\u6562\u60c5\u6d3b\u5f97\u4e0d\u3000\u8010\u70e6\u4e86\u5417\uff1f\u201d\u7f8e\u76ee \u7535\u5c04\uff0c\u5927\u6709\u968f\u65f6\u53ef\u4ee5\u7ffb\u8138\u52a8\u624b\u7684\u6837\u5b50\u3002\u201d \u3000\u3000\u3000\u80e1\u4e4b\u8f89\u8d76\u5fd9\u8dd1\u8fc7\u6765\uff0c\u8138\u4e0a\u9732\u7740\u4ed6\u60ef\u6709\u7684\u90a3\u79cd\u5473\u9053\uff0c\u7b11\u8bf4\uff1a\u201c\u4f60\u4eec\u8fd8\u8ddf\u5341\u5e74\u524d\u4e00\u3000\u6837\uff0c \u4e00\u89c1\u9762\u5c31\u5435\u67b6\uff0c\u4e5f\u4e0d\u6015\u4eba\u5bb6\u89c1\u4e86\u7b11\u8bdd\uff0c\u201d\u77f3\u78f7\u6697\u4e2d\u5bfb\u601d\uff0c\u5fd6\u9053\uff1a\u201c\u770b\u6765\u8fd9\u5e9e\u826f\u3000\u6e5b\u4e5f\u5bf9\u6bdb\u59d1 \u5a18\u5f88\u6709\u610f\u601d\u3002\u201d\u7f2a\u6587\u4e24\u773c\u671b\u5929\uff0c\u4eff\u4f5b\u56e0\u4e3a\u67d0\u4e00\u4e2a\u540d\u5b57\uff0c\u800c\u5728\u6c89\u601d\u7740\u3002 \u3000\u3000\u3000\u5e9e\u826f\u6e5b\u8bf4\u51fa\u201c\u5c60\u9f99\u4ed9\u5b50\u201d\u56db\u5b57\uff0c\u50cf\u662f\u6839\u672c\u6ca1\u6709\u5f15\u8d77\u522b\u4eba\u7684\u6ce8\u610f\uff0c\u4e5f\u50cf\u662f\u8fd9\u201c\u5c60\u3000\u9f99\u4ed9 \u5b50\u201d\u56db\u5b57\uff0c\u6839\u672c\u4e0d\u503c\u5f97\u5f15\u8d77\u522b\u4eba\u7684\u6ce8\u610f\uff0c\u8fd9\u5e76\u4e0d\u602a\u4ed6\u4eec\u5b64\u964b\u5be1\u95fb\uff0c\u53ea\u662f\u4ed6\u4eec\u8fdf\u3000\u751f\u4e86\u8bb8\u591a \u5e74\uff0c\u662f\u4ee5\u5bf9\u6614\u5e74\u4e2d\u539f\u6b66\u6797\u552f\u4e00\u80fd\u548c\u201c\u6d77\u5929\u5b64\u71d5\u201d\u5bf9\u624b\u767e\u62db\u7684\u5973\u5251\u624b\u7684\u540d\u5b57\u3000\uff0c\u9887\u4e3a\u751f\u758f\uff0c\u8fd9 \u5f53\u7136\u4e5f\u662f\u56e0\u4e3a\u201c\u5c60\u9f99\u4ed9\u5b50\u201d\u751f\u6027\u672c\u5c31\u5b64\u50fb\uff0c\u867d\u5177\u5c60\u9f99\u7edd\u6280\uff0c\u5374\u5f88\u5c11\u3000\u5728\u6c5f\u6e56\u4e2d\u9732\u9762\u7684\u7f18\u6545\u3002 \u3000\u3000\u3000\u80e1\u4e4b\u8f89\u8bf4\u8fc7\u4e86\u8bdd\uff0c\u8239\u8231\u91cc\u5c31\u9677\u5165\u4e86\u6c89\u5bc2\uff0c\u6709\u7684\u4eba\u65e0\u8bdd\u53ef\u8bf4\uff0c\u6709\u7684\u4eba\u4e0d\u613f\u8bf4\u8bdd\uff0c\u3000\u80e1\u4e4b \u8f89\u5f20\u7740\u624b\uff0c\u51f8\u7740\u809a\u5b50\uff0c\u4ed6\u5728\u4eba\u751f\u821e\u53f0\u4e0a\u626e\u6f14\u7684\u89d2\u8272\uff0c\u6b64\u523b\u770b\u8d77\u6765\u4e0d\u4f46\u53ef\u7b11\uff0c\u800c\u3000\u4e14\u5df2\u6709\u4e9b\u53ef \u601c\u4e86\u3002 \u3000\u3000\u3000\u5e9e\u826f\u6e5b\u6014\u4e86\u4e00\u4e0b\uff0c\u8138\u4e0a\u5ffd\u9634\u5ffd\u6674\uff0c\u5f53\u7740\u8fd9\u4e48\u591a\u7684\u4eba\u5403\u4e86\u8fd9\u4e48\u5927\u7684\u8e69\uff0c\u4ed6\u5f53\u7136\u4e0d\u3000\u597d \u53d7\uff0c\u4f46\u53e6\u4e00\u79cd\u60c5\u611f\uff0c\u5374\u53c8\u4f7f\u4ed6\u4e0d\u5f97\u4e0d\u5fcd\u4f4f\u5fc3\u4e2d\u7684\u201c\u4e0d\u597d\u53d7\u201d\uff0c\u7f13\u7f13\u8e31\u5230\u8239\u5934\uff0c\u5ffd\u3000\u7136\u53c8\u56de\u8eab \u8bf4\u9053\uff1a\u201c\u5404\u4f4d\u5148\u8bf7\u6e38\u6e56\uff0c\u6211\u5148\u56de\u53bb\u7980\u544a\u5e08\u5085\uff0c\u5c31\u8bf4\u80e1\u4e09\u53d4\u548c\u6b66\u5f53\u5251\u5ba2\u77f3\u5927\u3000\u4fa0\u5df2\u7ecf\u5230\u4e86\u3002\u201d \u77f3\u78f7\u5fae\u4e00\u52a8\u5ff5\uff0c\u77e5\u9053\u6c5f\u6e56\u4e2d\u8fd8\u6ca1\u6709\u5fd8\u8bb0\u81ea\u5df1\u7684\u540d\u5b57\u3002 \u3000\u3000\u3000\u5e9e\u826f\u6e5b\u53c8\u4e00\u62b1\u62f3\uff0c\u6b64\u523b\u4ed6\u6240\u4e58\u6765\u7684\u5c0f\u8239\u5df2\u98d8\u5230\u4e24\u4e08\u5f00\u5916\uff0c\u80e1\u4e4b\u8f89\u548c\u7f2a\u6587\u3001\u77f3\u78f7\u3000\u4e5f\u8ddf \u4e86\u51fa\u6765\uff0c\u5e9e\u826f\u6e5b\u5374\u626d\u5934\u671b\u4e86\u8231\u91cc\u7684\u6bdb\u6587\u742a\u4e00\u773c\uff0c\u5927\u58f0\u9053\uff1a\u201c\u5c0f\u53ef\u5148\u8d70\u4e00\u6b65\u3002\u201d\u3000\u8170\u5fae\u5f13\u8d77\uff0c \u8eab\u5f62\u51b2\u5929\u800c\u8d77\uff0c\u53cc\u81c2\u4e00\u6295\uff0c\u5411\u524d\u9762\u63a0\u4e86\u8fc7\u53bb\uff0c\u8eab\u6cd5\u4e4b\u4e2d\uff0c\u663e\u7136\u4e5f\u6709\u4e86\u51e0\u3000\u5206\u5356\u5f04\u7684\u610f\u5473\u3002 \u3000\u3000\u3000\u3000\u3000\u4ed6\u8f7b\u529f\u9887\u9ad8\uff0c\u6b64\u523b\u7740\u610f\u65bd\u4e3a\uff0c\u679c\u7136\u6781\u4e3a\u8f7b\u7075\u66fc\u5999\uff0c\u53cc\u76ee\u6ce8\u5b9a\u90a3\u8258\u5c0f\u8239\uff0c\u51c6\u5907\u8f7b\u3000 \u98d8\u98d8\u5730\u843d\u5728\u8239\u4e0a\uff0c\u5f53\u7136\u662f\u5e0c\u671b\u6bdb\u6587\u742a\u80fd\u770b\u5230\u3002 \u3000\u3000\u3000\u54ea\u77e5\u9053\u5c31\u5728\u4ed6\u771f\u6c14\u5fae\u6563\uff0c\u53cc\u8db3\u5df2\u5c06\u843d\u5728\u8239\u4e0a\u90a3\u4e00\u5239\u90a3\uff0c\u5c0f\u8239\u5374\u8c61\u662f\u6709\u4eba\u7a81\u7136\u5728\u3000\u65c1\u8fb9 \u4e00\u62c9\uff0c\u500f\u7136\u5728\u6e56\u9762\u4e0a\u6ed1\u5f00\u6570\u5c3a\u3002 \u3000\u3000\u3000\u201c\u5657\u901a\u201d\u4e00\u58f0\uff0c\u6c34\u82b1\u56db\u6e85\uff0c\u7acb\u5728\u8239\u5934\u671b\u7740\u7684\u80e1\u4e4b\u8f89\u7b49\u4eba\uff0c\u90fd\u4e0d\u7981\u60ca\u5524\u4e00\u58f0\uff0c\u77f3\u3000\u78f7\u4e5f \u89c9\u6b64\u4e8b\u5927\u51fa\u610f\u5916\uff0c\u773c\u89d2\u52a8\u5904\uff0c\u7f2a\u6587\u6b63\u5728\u4ee5\u624b\u6574\u53d1\uff0c\u9762\u4e0a\u4ecd\u7136\u6beb\u65e0\u6240\u52a8\uff0c\u77f3\u78f7\u5fc3\u3000\u4e2d\uff0c\u53c8\u4e0d\u7981 \u52a8\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u3000\u5e9e\u826f\u6e5b\u6c42\u8363\u53cd\u8fb1\uff0c\u7adf\u843d\u5165\u6c34\u4e2d\uff0c\u5e78\u597d\u4ed6\u751f\u957f\u4e8e\u6c5f\u5357\uff0c\u81ea\u5e7c\u5373\u8bc6\u6c34\u6027\uff0c\u4e0b\u6c89\u540e\u53c8\u3000\u7acb\u523b \u5192\u4e86\u4e0a\u6765\uff0c\u81ea\u7136\u53c8\u6e38\u56de\u753b\u822b\u8fb9\uff0c\u53cc\u624b\u4e00\u6273\u8239\u8237\uff0c\u7ffb\u4e0a\u4e86\u8239\uff0c\u843d\u6c34\u4e4b\u9e21\uff0c\u5f62\u5bb9\u81ea\u3000\u662f\u72fc\u72c8\uff0c\u548c \u4ed6\u7b2c\u4e00\u6b21\u4e0a\u8239\u65f6\u7684\u90a3\u79cd\u8f7b\u7075\u3001\u98d8\u9038\u7684\u82f1\u59ff\uff0c\u5df2\u5927\u4e0d\u76f8\u540c\u4e86\u3002 \u3000\u3000\u3000\u4ed6\u6068\u58f0\u9053\uff1a\u201c\u8fd9\u662f\u8c01\u5728\u6363\u9b3c\uff1f\u6211\u4e00\u4e00\u201d\u6c14\u5f97\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u6bdb\u6587\u742a\u5a40\u5a1c\u5730\u81ea\u8231\u4e2d\u3000\u8d70\u51fa \u6765\uff0c\u89c1\u4e86\u4ed6\uff0c\u201c\u5657\u55e4\u201d\u4e00\u7b11\uff0c\u5927\u6709\u5e78\u707e\u4e50\u7978\u4e4b\u610f\u3002 \u3000\u3000\u3000\u4f46\u662f\u8fd9\u79cd\u4e8b\u8c01\u4e5f\u65e0\u6cd5\u77e5\u9053\u771f\u8c61\uff0c\u4f46\u5374\u53ea\u6709\u4e24\u79cd\u53ef\u80fd\uff0c\u82e5\u6709\u4eba\u6f5c\u4e8e\u6c34\u4e0b\uff0c\u7b49\u5230\u4ed6\u3000\u843d\u4e0b \u65f6\uff0c\u731b\u529b\u5c06\u8239\u62c9\u5f00\uff0c\u6216\u8005\u662f\u8239\u4e0a\u4e4b\u4eba\uff0c\u5176\u4e2d\u6709\u4e00\u4eba\u4ee5\u7edd\u9879\u7684\u5185\u5bb6\u5288\u7a7a\u638c\u4e00\u7c7b\u7684\u3000\u529f\u592b\uff0c\u9694\u7740 \u4e24\u4e09\u4e08\u8fdc\uff0c\u5c06\u8239\u5288\u5f00\u3002 \u3000\u3000\u3000\u53ea\u662f\u8fd9\u4e24\u79cd\u53ef\u80fd\uff0c\u5374\u53c8\u50cf\u662f\u90fd\u4e0d\u53ef\u80fd\uff0c\u5c24\u5176\u662f\u540e\u8005\uff0c\u5f53\u4e16\u6b66\u6797\u4e2d\uff0c\u6709\u8fd9\u79cd\u529f\u529b\u3000\u7684\u4eba \u53ef\u8bf4\u5c11\u4e4b\u53c8\u5c11\uff0c\u800c\u8fd9\u753b\u822b\u4e0a\u7684\u51e0\u4eba\uff0c\u867d\u7136\u90fd\u53ef\u8bf4\u662f\u6b66\u6797\u540d\u4eba\uff0c\u4f46\u662f\u4e5f\u7edd\u4e0d\u53ef\u80fd\u3000\u6709\u8fd9\u79cd\u529f\u529b \u5440\uff01 \u3000\u3000\u3000\u662f\u4ee5\u5c3d\u7ba1\u5e9e\u826f\u6e5b\u66b4\u6012\uff0c\u5374\u7edd\u65e0\u51fa\u6c14\u7684\u5bf9\u8c61\uff0c\u6bdb\u6587\u742a\u5bf9\u4ed6\u707f\u7b11\uff0c\u4ed6\u4e5f\u53ea\u6709\u9690\u5fcd\uff0c\u3000\u5176\u5b9e \u5c31\u662f\u4e0d\u5fcd\uff0c\u4ed6\u53c8\u6709\u4ec0\u4e48\u529e\u6cd5\u5462\uff1f \u3000\u3000\u3000\u4f17\u4eba\u4e58\u5174\u6e38\u6e56\uff0c\u5374\u8d25\u5174\u800c\u5f52\uff0c\u53ea\u6709\u5728\u7f2a\u6587\u548c\u6bdb\u6587\u742a\u8138\u4e0a\uff0c\u4ecd\u53ef\u770b\u5230\u7b11\u5bb9\uff0c\u5e9e\u826f\u3000\u6e5b\u867d \u7136\u4e0d\u5b8c\u5168\u7b97\u201c\u9762\u5982\u6b7b\u7070\u201d\uff0c\u4f46\u81f3\u5c11\u5df2\u662f\u201c\u5782\u5934\u4e27\u6c14\u201d\u4e86\u3002 \u3000\u3000\u3000\u8239\u4e00\u9760\u5cb8\uff0c\u7075\u86c7\u6bdb\u81ec\u5728\u676d\u5dde\u7684\u52bf\u529b\uff0c\u7acb\u523b\u5c31\u53ef\u4ee5\u770b\u51fa\u6765\u4e86\uff0c\u6e56\u7554\u7684\u4eba\uff0c\u65e0\u8bba\u4e09\u3000\u6559\u4e5d \u6d41\uff0c\u770b\u5230\u72fc\u72c8\u4e0d\u582a\u7684\u5e9e\u826f\u6e5b\uff0c\u90fd\u4ecd\u606d\u656c\u5730\u62db\u547c\u7740\uff0c\u8138\u4e0a\u7edd\u4e0d\u6562\u9732\u51fa\u4e00\u4e9b\u5f02\u5bb9\u6765\u3000\uff0c\u6b66\u6797\u4e2d\u4eba \u80fd\u5728\u5730\u9762\u4e0a\u5360\u7740\u8fd9\u4e48\u5927\u52bf\u529b\u7684\uff0c\u7075\u86c7\u6bdb\u81ec\u4e5f\u8bb8\u53ef\u7b97\u662f\u7b2c\u4e00\u4eba\u54e9\u3002 \u3000\u3000\u3000\u7075\u86c7\u6bdb\u81ec\u7684\u5c45\u5904\uff0c\u66f4\u662f\u60ca\u4eba\uff0c\u6050\u6015\u8fde\u676d\u5dde\u5e9c\u7684\u5e9c\u5c39\u7684\u5e9c\u90aa\uff0c\u90fd\u4e0d\u53ca\u4ed6\u3002 \u3000\u3000\u3000\u6731\u7ea2\u8272\u7684\u5927\u95e8\uff0c\u5b8c\u5168\u662f\u5f00\u7740\u7684\uff0c\u95e8\u53e3\u4e24\u5ea7\u77f3\u72ee\uff0c\u5de8\u5927\u800c\u72f0\u72de\uff0c\u4fef\u89c6\u5f80\u6765\u7684\u4eba\u4eec\u3000\uff0c\u50cf \u662f\u7075\u86c7\u6bdb\u81ec\u4fef\u89c6\u7740\u82b8\u82b8\u6b66\u6797\u7fa4\u8c6a\u4e00\u6837\u3002 \u3000\u3000\u3000\u8ddf\u7740\u6bdb\u81ec\u7684\u7231\u5973\u548c\u7231\u5f92\uff0c\u81ea\u7136\u7528\u4e0d\u7740\u901a\u62a5\u3001\u6c42\u89c1\u4e00\u7c7b\u7684\u4e8b\uff0c\u4ed6\u4eec\u76f4\u63a5\u5730\u8fdb\u5165\u4e86\u3000\u90a3\u5e03 \u7f6e\u5f97\u6781\u5176\u534e\u4e3d\u7684\u5ba2\u5385\u3002 \u3000\u3000\u3000\u7f2a\u6587\u8d70\u5728\u80e1\u4e4b\u8f89\u8eab\u4fa7\uff0c\u7a81\u7136\u6084\u6084\u4e00\u62c9\u4ed6\u7684\u8896\u5b50\uff0c\u4f4e\u58f0\u8bf4\u9053\u3002 \u3000\u3000\u3000\u201c\u80e1\u5144\uff0c\u4f60\u6211\u591a\u65e5\u76f8\u5904\uff0c\u53ef\u79f0\u77e5\u5df1\uff0c\u80e1\u5144\u7684\u5fc3\u4e8b\uff0c\u5c0f\u5f1f\u4e5f\u770b\u51fa\u6765\u4e86\uff0c\u80e1\u5144\u5bf9\u5c0f\u3000\u5f1f\u5e2e \u52a9\u751a\u591a\uff0c\u4e0d\u77e5\u53ef\u5426\u8ba9\u5c0f\u5f1f\u5bf9\u80e1\u5144\u4e5f\u4e00\u6548\u5fae\u52b3\u3002\u201d \u3000\u3000\u3000\u80e1\u4e4b\u8f89\u5927\u559c\uff0c\u60f3\u4e0d\u5230\u4ed6\u591a\u65e5\u672a\u80fd\u63d0\u51fa\u6765\u7684\u4e8b\uff0c\u6b64\u523b\u5374\u88ab\u4eba\u5bb6\u5148\u63d0\u51fa\u6765\u4e86\u3002\u4f46\u53e3\u3000\u4e2d\u5374 \u4ecd\u6545\u610f\u88c5\u7740\u4e0d\u597d\u610f\u601d\u4ed6\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u54ea\u91cc\u8bdd\uff0c\u8fd9\u662f\u54ea\u91cc\u8bdd\u2014\u2014\u201d\u7f2a\u6587\u5fae\u7b11\u9053\uff1a\u3000\u201c\u80e1\u5144\u5931 \u9556\uff0c\u5c0f\u5f1f\u968f\u884c\u5728\u4fa7\uff0c\u53ea\u662f\u5c0f\u5f1f\u65e0\u7f1a\u9e21\u4e4b\u529b\uff0c\u4e5f\u4e0d\u80fd\u52a9\u80e1\u5144\u4e00\u81c2\uff0c\u8bf4\u6765\u60ed\u6127\u3000\uff0c\u5c0f\u5f1f\u627f\u53d7\u5148\u4eba \u4f59\u836b\u2026\u2026\u201d\u4ed6\u6545\u610f\u8bed\u58f0\u4e00\u987f\uff0c\u80e1\u4e4b\u8f89\u518d\u4e5f\u5fcd\u4e0d\u4f4f\uff0c\u5df4\u7ed3\u5730\u7b11\u9053\uff1a\u201c\u3000\u5c0f\u5f1f\u4e5f\u77e5\u9053\u7f2a\u5144\u5bb6\u8d22\u4e07 \u8d2f\uff0c\u5c0f\u5f1f\u6240\u5931\u7684\u9556\u94f6\uff0c\u522b\u4eba\u770b\u6765\u4e00\u5b9a\u4e3a\u6570\u751a\u5de8\uff0c\u4f46\u5374\u7edd\u5bf9\u4e0d\u3000\u4f1a\u653e\u5728\u7f2a\u5144\u5fc3\u4e0a\uff0c\u53ea\u662f\u5c0f\u5f1f\u65e0 \u529f\uff0c\u600e\u6562\u53d7\u7984\uff0c\u4e0d\u7792\u7f2a\u5144\u8bf4\uff0c\u5c0f\u5f1f\u867d\u65e9\u6709\u6b64\u610f\uff0c\u5374\u4e00\u3000\u76f4\u4e0d\u6562\u542f\u53e3\u5462\uff01\u201d \u3000\u3000\u3000\u7f2a\u6587\u6697\u4e2d\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u201d\u80e1\u5144\u8fd9\u4e48\u8bf4\uff0c\u5c31\u662f\u89c1\u5916\u4e86\uff0c\u9556\u94f6\u7684\u4e8b\uff0c\u5168\u653e\u5728\u5c0f\u5f1f\u8eab\u3000\u4e0a\u597d \u4e86\u3002\u201d \u3000\u3000\u3000\u80e1\u4e4b\u8f89\u518d\u4e5f\u60f3\u4e0d\u5230\u8fd9\u5bcc\u5bb6\u516c\u5b50\u7adf\u5982\u6b64\u6177\u6168\uff0c\u81ea\u7136\u5343\u6069\u4e07\u8c22\uff0c\u5374\u542c\u7f2a\u6587\u53c8\u9053\uff1a\u201c\u3000\u7b49\u4f1a \u89c1\u4e86\u6bdb\u5927\u4fa0\uff0c\u80e1\u5144\u5c31\u8bf4\u548c\u5c0f\u5f1f\u662f\u591a\u5e74\u76f8\u4ea4\u597d\u4e86\uff0c\u90a3\u4e48\u5c31\u7b97\u5c0f\u5f1f\u5bf9\u9556\u94f6\u4e00\u529b\u62c5\u5f53\u3000\uff0c\u522b\u4eba\u4e5f\u5c31 \u4e0d\u4f1a\u6709\u4ec0\u4e48\u95f2\u8a00\u4e86\u3002\u201d \u3000\u3000\u3000\u80e1\u4e4b\u8f89\u81ea\u7136\u7acb\u523b\u8fde\u58f0\u79f0\u662f\uff0c\u5fc3\u4e2d\u66f4\u611f\u6fc0\u7f2a\u6587\u4e3a\u4ed6\u8bbe\u60f3\u5468\u5230\uff0c\u6b64\u523b\u7f2a\u6587\u82e5\u53eb\u4ed6\u8ba4\u3000\u81ea\u5df1 \u505a\u7238\u7238\uff0c\u4ed6\u4e5f\u4f1a\u6beb\u4e0d\u8003\u8651\u5730\u7b54\u5e94\u3002 \u3000\u3000\u3000\u7f2a\u6587\u5634\u89d2\u5fae\u62bf\uff0c\u5634\u89d2\u4e2d\u663e\u793a\u7740\u4e00\u4e2a\u4eba\u5728\u8fbe\u6210\u67d0\u4e00\u79cd\u76ee\u7684\u65f6\uff0c\u6240\u611f\u53d7\u5230\u7684\u90a3\u4efd\u5f97\u3000\u610f\u548c \u6109\u5feb\u3002 \u3000\u3000\u3000\u4ed6\u4eec\u6b63\u5728\u4f4e\u58f0\u8c08\u8bdd\u65f6\uff0c\u95e8\u91cc\u7a81\u7136\u6709\u54b3\u55fd\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u80e1\u8001\u4e09\u5e26\u7740\u77f3\u8001\u5f1f\u4e00\u3000\u9f50\u6765 \u4e86\u5417\uff1f\u201d\u4e2d\u6c14\u867d\u8db3\uff0c\u4f46\u5929\u751f\u7684\u90a3\u79cd\u5c16\u9510\u523a\u8033\u7684\u58f0\u8c03\uff0c\u4ecd\u4f7f\u4eba\u542c\u8d77\u6765\uff0c\u6781\u4e3a\u4e0d\u8212\u3000\u670d\u3002 \u3000\u3000\u3000\u5927\u5bb6\u4e0d\u7ea6\u800c\u540c\u5730\u8f6c\u8fc7\u5934\uff0c\u95e8\u91cc\u5927\u8e0f\u6b65\u8d70\u51fa\u4e00\u4eba\uff0c\u8eab\u8eaf\u7626\u957f\uff0c\u98a7\u9aa8\u9ad8\u8038\uff0c\u9f3b\u5982\u9e70\u3000\u96bc\uff0c \u4e24\u773c\u6df1\u9677\uff0c\u4f46\u76ee\u5149\u4e5f\u50cf\u9e70\u96bc\u4e00\u6837\u7684\u9510\u5229\uff0c\u867d\u7136\u9762\u4e0a\u6ee1\u5e03\u7684\u76b1\u7eb9\u5df2\u544a\u8bc9\u522b\u4eba\u4ed6\u7684\u3000\u5e74\u9f84\uff0c\u4f46\u6b65 \u5c65\u4e4b\u95f4\uff0c\u77eb\u5065\u5982\u6614\uff0c\u4ecd\u7136\u6ca1\u6709\u663e\u51fa\u4e00\u4e1d\u8001\u6001\u3002 \u3000\u3000\u3000\u80e1\u4e4b\u8f89\u8fde\u5fd9\u8d70\u4e0a\u51e0\u6b65\uff0c\u6df1\u6df1\u5730\u4f5c\u7740\u63d6\uff0c\u8bcc\u5a9a\u5730\u7b11\u7740\u8bf4\u9053\uff1a\u201c\u6bdb\u5927\u54e5\u4f60\u597d\uff0c\u5c0f\u5f1f\u3000\u597d\u4e45 \u6ca1\u6709\u6765\u5411\u5927\u54e5\u95ee\u5b89\u4e86\u3002\u201d\u6bdb\u81ec\u54c8\u54c8\u5927\u7b11\uff0c\u987e\u76fc\u4e4b\u95f4\uff0c\u9887\u591a\u505a\u4f5c\uff0c\u4e00\u628a\u62c9\u7740\u80e1\u4e4b\u3000\u8f89\u9053\uff1a\u201c\u4f60 \u6211\u81ea\u5df1\u5144\u5f1f\uff0c\u5ba2\u6c14\u4f5c\u751a\uff1f\u201d\u76ee\u5149\u56db\u626b\uff0c\u5728\u6bcf\u4e2a\u4eba\u8138\u4e0a\u626b\u8fc7\uff0c\u5927\u7b11\u7740\u8d70\u5230\u3000\u77f3\u78f7\u9762\u524d\u9053\uff1a\u201c\u591a \u5e74\u4e0d\u89c1\uff0c\u60f3\u4e0d\u5230\u8001\u5f1f\u8fd8\u662f\u5e74\u8f7b\u5f97\u5f88\uff0c\u4e0d\u50cf\u54e5\u54e5\u6211\uff0c\u5df2\u7ecf\u8001\u4e86\uff0c\u8001\u3000\u4e86\u2014\u2014\u201d\u4ed6\u4ee5\u4e00\u4e2a\u8fd1\u4e8e\u611f \u53f9\u7684\u58f0\u97f3\uff0c\u7ed3\u675f\u4e86\u4ed6\u7684\u8bdd\uff0c\u4f46\u6bcf\u4e2a\u4eba\u90fd\u53ef\u4ee5\u770b\u51fa\uff0c\u4ed6\u5634\u4e0a\u3000\u867d\u8bf4\u8001\u4e86\uff0c\u4f46\u5fc3\u4e2d\u5374\u7edd\u672a\u670d\u8001 \u54e9\u3002 \u3000\u3000\u3000\u5927\u5bb6\u7684\u76ee\u5149\uff0c\u90fd\u843d\u5728\u8fd9\u4f4d\u6b66\u6797\u9b41\u9996\u7684\u8eab\u4e0a\uff0c\u5bf9\u7f2a\u6587\u4ee5\u53ca\u4ed6\u9762\u4e0a\u9732\u51fa\u7684\u5f02\u5bb9\uff0c\u4e5f\u3000\u5c31\u6ca1 \u6709\u6ce8\u610f\u5230\u4e86\u3002 \u3000\u3000\u3000\u4f46\u662f\u50cf\u7f2a\u6587\u8fd9\u79cd\u4eba\uff0c\u4ed6\u5728\u4efb\u4f55\u5730\u65b9\uff0c\u90fd\u7edd\u4e0d\u4f1a\u6c38\u8fdc\u88ab\u51b7\u843d\u7684\uff0c\u6bdb\u684c\u76ee\u5149\u4e00\u8f6c\uff0c\u3000\u4e5f\u843d \u5728\u4ed6\u8eab\u4e0a\uff0c\u9614\u5634\u4e00\u88c2\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u8001\u5f1f\u9762\u751f\u5f97\u5f88\uff0c\u60f3\u6765\u662f\u6c5f\u6e56\u4e2d\u7684\u540e\u8d77\u9ad8\u624b\u3000\u3002\u201d\u4ed6\u6717\u58f0 \u4e00\u7b11\uff0c\u53c8\u9053\uff1a\u201c\u8001\u592b\u8fd9\u4e9b\u5e74\u6765\u8db3\u8ff9\u672a\u51fa\u676d\u5dde\uff0c\u5bf9\u6c5f\u6e56\u4e2d\u7684\u540e\u8d77\u4e4b\u79c0\uff0c\u90fd\u3000\u751f\u758f\u5f97\u5f88\u3002\u201d\u8bdd\u6c14 \u4e4b\u95f4\uff0c\u7765\u7768\u4f5c\u6001\uff0c\u50b2\u6c14\u66b4\u9732\u3002 \u3000\u3000\u3000\u80e1\u4e4b\u8f89\u5df4\u7ed3\u5730\u7b11\u9053\uff1a\u201c\u6bdb\u5927\u54e5\u8fd9\u6b21\u770b\u8d70\u4e86\u773c\u4e86\uff0c\u8fd9\u4f4d\u7f2a\u8001\u5f1f\uff0c\u662f\u6614\u5e74\u5c0f\u5f1f\u8d70\u9556\u3000\u7ca4\u4e1c \u65f6\u6240\u7ed3\u8bc6\u7684\uff0c\u867d\u7136\u4fca\u9038\u4e0d\u51e1\uff0c\u4f46\u5374\u4e0d\u6298\u4e0d\u6263\u7684\u662f\u4e2a\u4e66\u751f\u3002\u201d \u3000\u3000\u3000\u4ed6\u5e72\u7b11\u4e86\u4e24\u58f0\uff0c\u53c8\u9053\uff1a\u201c\u4e0d\u8fc7\u662f\u4e2a\u5bb6\u8d22\u4e07\u8d2f\u7684\u4e66\u751f\u7f62\u4e86\uff0c\u5c0f\u5f1f\u8fd9\u6b21\u6240\u5931\u7684\u9556\uff0c\u3000\u82e5\u975e \u7f2a\u8001\u5f1f\uff0c\u6050\u6015\u54b1\u4eec\u5e73\u5b89\u9556\u5c40\u7684\u62db\u724c\u5c31\u5012\u4e86\u54e9\u3002\u201d \u3000\u3000\u3000\u6bdb\u81ed\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u80e1\u4e4b\u8f89\u4f3c\u4e4e\u89c9\u5f97\u610f\u72b9\u6765\u5c3d\uff0c\u53c8\u9053\uff1a\u201c\u8fd9\u5e74\u5934\u50cf\u7f2a\u8001\u5f1f\u8fd9\u79cd\u3000\u4ed7\u4e49 \u758f\u8d22\u7684\u670b\u53cb\uff0c\u8fd8\u771f\u5c11\u89c1\uff0c\u6bdb\u5927\u54e5\uff0c\u4f60\u8bf4\u662f\u4e0d\u662f\uff1f\u201d\u6bdb\u81ec\u8fde\u8fde\u70b9\u5934\uff0c\u53e3\u4e2d\u4e0d\u65ad\u91cd\u3000\u8986\u7740\uff1a\u201c\u96be \u5f97\uff01\u96be\u5f97\uff01\u201d \u3000\u3000\u3000\u4e8e\u662f\u7f2a\u6587\u5f88\u8f7b\u6613\u5730\uff0c\u5728\u7b2c\u4e00\u6b21\u89c1\u5230\u6bdb\u81ec\u65f6\uff0c\u5c31\u4f7f\u8fd9\u6b66\u6797\u9b41\u9996\u5bf9\u4ed6\u751f\u4e86\u6781\u5927\u7684\u597d\u3000\u611f\uff0c \u4e16\u4e0a\u6709\u8bb8\u591a\u65b9\u6cd5\u53ef\u4ee5\u4f7f\u4eba\u5bf9\u81ea\u5df1\u751f\u51fa\u597d\u611f\uff0c\u4f46\u6beb\u65e0\u7591\u95ee\u7684\uff0c\u91d1\u94b1\u603b\u662f\u6700\u5bb9\u6613\u751f\u3000\u51fa\u6548\u529b\u7684\u4e00 \u8db4\u8fd9\u5176\u95f4\uff0c\u53ea\u6709\u77f3\u78f7\u5fc3\u4e2d\u7591\u7aa6\u4e1b\u751f\uff0c\u56e0\u4e3a\u53ea\u6709\u4ed6\u77e5\u9053\uff0c\u7f2a\u6587\u548c\u80e1\u4e4b\u8f89\u4ec5\u3000\u662f\u521d\u8bc6\u800c\u5df2\uff0c\u800c\u4e14 \u7f2a\u6587\u4e3a\u4ec0\u4e48\u8981\u4ee5\u5404\u79cd\u65b9\u6cd5\uff0c\u6765\u6c42\u5f97\u80e1\u4e4b\u8f89\u548c\u6bdb\u81ec\u7684\u597d\u611f\uff0c\u4e5f\u4f7f\u77f3\u3000\u78f7\u89c9\u5f97\u975e\u5e38\u96be\u4ee5\u89e3\u91ca\u3002 \u3000\u3000\u3000\u4ed6\u77e5\u9053\u8fd9\u5176\u95f4\u5fc5\u5b9a\u9690\u85cf\u7740\u4e00\u4e2a\u6781\u5927\u7684\u79d8\u5bc6\uff0c\u4ed6\u867d\u7136\u5df2\u770b\u51fa\u4e00\u4e9b\u7aef\u502a\uff0c\u4f46\u4ed6\u7edd\u5bf9\u3000\u4e0d\u613f \u8bf4\u7834\uff0c\u751a\u81f3\u5e0c\u671b\u4ed6\u7684\u731c\u6d4b\uff0c\u80fd\u591f\u63a5\u8fd1\u4e8b\u5b9e\u54e9\u3002 \u3000\u3000\u3000\u7b49\u5230\u6bdb\u81ec\u77e5\u9053\u8fd9\u4e9b\u65e5\u5b50\u6765\u6240\u53d1\u751f\u7684\u4e00\u8fde\u4e32\u4e0d\u5982\u610f\u7684\u4e8b\u7684\u6700\u540e\u4e24\u4ef6\u7684\u65f6\u5019\uff0c\u4ed6\u8138\u3000\u4e0a\u90a3 \u79cd\u5fd7\u5f97\u610f\u6ee1\u7684\u7b11\u5bb9\uff0c\u5c31\u6e10\u6e10\u9eef\u6de1\u4e86\u3002 \u3000\u3000\u3000\u4f46\u662f\uff0c\u5728\u8fd9\u4e9b\u4eba\u9762\u524d\uff0c\u4ed6\u4ecd\u505a\u4f5c\u7740\uff0c\u63a5\u7740\u80e1\u4e4b\u8f89\u544a\u8bc9\u4ed6\u6709\u5173\u201c\u91d1\u5251\u4fa0\u201d\u7684\u8bdd\u9053\u3000\uff1a \u201c\u80e1\u8001\u4e09\uff0c\u4f60\u6211\u81ea\u5df1\u5144\u5f1f\uff0c\u53ef\u4e0d\u51c6\u957f\u4ed6\u4eba\u5fd7\u6c14\uff0c\u706d\u81ea\u5df1\u5a01\u98ce\uff0c\u90a3\u53eb\u91d1\u5251\u4fa0\u7684\u5bb6\u4f19\u3000\u7eb5\u7136\u4e09\u5934 \u516d\u81c2\uff0c\u53ef\u518d\u4e5f\u522b\u60f3\u9003\u51fa\u6211\u7684\u624b\u53bb\u3002\u201d \u3000\u3000\u3000\u7f2a\u6587\u7684\u76ee\u5149\uff0c\u76f4\u5230\u6b64\u523b\u624d\u4ece\u6bdb\u81ec\u8eab\u4e0a\u6536\u56de\u6765\uff0c\u6253\u91cf\u7740\u8fd9\u5927\u5385\u3002 \u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u84e6\u5730\u2014\u2014 \u3000\u3000\u3000\u4ed6\u7684\u76ee\u5149\u88ab\u8fd9\u5927\u5385\u91cc\u7684\u4e00\u4ef6\u4e1c\u897f\u5438\u5f15\u4f4f\u4e86\uff0c\u539f\u6765\u5728\u8fd9\u5927\u5385\u7684\u6b63\u4e2d\uff0c\u6709\u7740\u4e00\u4e2a\u6302\u3000\u7740\u9ed1 \u7f0e\u7684\u795e\u9f9b\uff0c\u8fd9\u548c\u5927\u5385\u4e2d\u7684\u5176\u4ed6\u6446\u8bbe\u6781\u4e0d\u76f8\u540c\u3002 \u3000\u3000\u3000\u4ed6\u7684\u76ee\u5149\u53c8\u5f00\u59cb\u6d41\u8f6c\u7740\u90a3\u79cd\u4ee4\u4eba\u96be\u6d4b\u7684\u5149\u8292\uff0c\u88c5\u4f5c\u65e0\u610f\u5730\u8d70\u8fc7\u53bb\uff0c\u5728\u90a3\u795e\u9f9b\u524d\u3000\u7559\u8fde \u7740\uff0c\u80e1\u4e4b\u8f89\u679c\u7136\u6084\u6084\u8d70\u8fc7\u53bb\uff0c\u4f4e\u8bed\u9053\uff1a\u201c\u8fd9\u91cc\u9762\u653e\u7740\u7684\u5c31\u662f\u6211\u6bdb\u5927\u54e5\u541b\u547d\u5929\u4e0b\u3000\u6b66\u6797\u7684\u2018\u6b8b \u9aa8\u4ee4\u2019\uff0c\u8001\u5f1f\uff0c\u4f60\u53ef\u77e5\u9053\uff0c\u8fd9\u91cc\u9762\u53ef\u6709\u7740\u4e00\u6bb5\u60ca\u5929\u52a8\u5730\u7684\u6545\u4e8b\u54e9\uff01\u201d \u3000\u3000\u3000\u7f2a\u6587\u76ee\u5149\u4e0b\u5782\u7740\uff0c\u6f2b\u5e94\u4e86\u4e00\u58f0\uff0c\u624b\u7f29\u5728\u886b\u8896\u91cc\uff0c\u9690\u85cf\u7740\u4ed6\u7d27\u63e1\u7740\u7684\u53cc\u62f3\u3002 \u3000\u3000\u3000\u5728\u4e3b\u4eba\u6bb7\u52e4\u7559\u5ba2\uff0c\u5ba2\u4eba\u4e5f\u65e0\u610f\u575a\u8f9e\u7684\u60c5\u51b5\u4e0b\uff0c\u7f2a\u6587\u548c\u77f3\u78f7\u665a\u4e0a\u4fbf\u7559\u5bbf\u5728\u8fd9\u6b66\u6797\u3000\u9b41\u9996 \u7684\u5de8\u5b85\u4e2d\u3002 \u3000\u3000\u3000\u66ae\u8272\u6df1\u5782\uff0c\u6625\u591c\u4ecd\u7136\u5e26\u7740\u4e9b\u5bd2\u610f\u7684\u98ce\uff0c\u5439\u5f97\u6bdb\u5b85\u540e\u56ed\u91cc\u7684\u65b0\u751f\u7684\u6811\u679d\u5fae\u5fae\u6447\u66f3\u3000\uff0c\u548c \u7740\u8349\u4e2d\u7684\u866b\u545c\uff0c\u534f\u8c03\u5730\u4e92\u76f8\u5e94\u548c\u7740\u3002 \u3000\u3000\u3000\u65e0\u6708\u6709\u661f\u3002 \u3000\u3000\u3000\u6726\u80e7\u7684\u661f\u5149\u4e2d\uff0c\u6bdb\u5b85\u540e\u56ed\u91cc\u7a81\u5730\u63a0\u8d77\u4e00\u6761\u4eba\u5f71\uff0c\u662f\u8c01\u6562\u5728\u8fd9\u540d\u6ee1\u5929\u4e0b\u7684\u7075\u86c7\u6bdb\u3000\u81ec\u7684 \u4f4f\u5b85\u91cc\uff0c\u65bd\u5c55\u5f00\u591c\u884c\u4eba\u7684\u8eab\u624b\uff1f \u3000\u3000\u3000\u8fd9\u4eba\u5f71\u4f3c\u4e4e\u81ea\u6043\u81ea\u5bb6\u7684\u8f7b\u529f\uff0c\u65e0\u8bba\u5728\u4efb\u4f55\u60c5\u51b5\u4e0b\u90fd\u4e0d\u4f1a\u53d1\u51fa\u4e00\u4e9b\u58f0\u606f\u6765\uff0c\u8f7b\u8f7b\u3000\u4e00 \u63a0\uff0c\u7adf\u5728\u67d4\u8f6f\u5982\u7ef5\u7684\u6811\u679d\u4e0a\u9a7b\u8db3\uff0c\u4f3c\u4e4e\u5728\u6253\u91cf\u7740\u5730\u5f62\u3002 \u3000\u3000\u3000\u7136\u540e\u4ed6\u8eab\u5f62\u4e00\u6298\uff0c\u8f7b\u5982\u98de\u9e3f\u822c\u63a0\u51fa\u4e09\u4e08\uff0c\u5728\u5c4b\u9762\u4e0a\u5fae\u4e00\u76d8\u65cb\uff0c\u63a5\u8fde\u4e24\u4e2a\u8d77\u843d\uff0c\u3000\u53c8\u63a0 \u53bb\u6570\u4e08\u5f00\u5916\uff0c\u5fae\u4e00\u505c\u987f\uff0c\u654f\u6377\u5730\u4e00\u7ffb\uff0c\u85cf\u8eab\u5728\u4e00\u4e2a\u5de8\u5927\u7684\u5c4b\u693d\u4e4b\u4e0b\uff0c\u6717\u76ee\u5185\u671b\u3000\uff0c\u91cc\u9762\u6b63\u662f \u6bdb\u5b85\u7684\u5927\u5385\u3002 \u3000\u3000\u3000\u8fd9\u4eba\u5f71\u8f7b\u8eab\u529f\u592b\u5df2\u5165\u5316\u5883\uff0c\u4ed7\u7740\u8fd9\u79cd\u8f7b\u529f\uff0c\u4f7f\u4ed6\u5c06\u4efb\u4f55\u591c\u884c\u4eba\u90fd\u5fc5\u6709\u7684\u4e00\u4e9b\u63aa\u3000\u65bd\u90fd \u7701\u7565\u4e86\uff0c\u8eab\u5f62\u518d\u4e00\u7ffb\uff0c\u98d8\u7136\u843d\u5728\u5730\u4e0a\u3002 \u3000\u3000\u3000\u8fd9\u4e9b\u5e74\u6765\u6bdb\u81ec\u4ece\u672a\u62c5\u5fc3\u8fc7\u6709\u591c\u884c\u4eba\u4f1a\u5230\u4ed6\u7684\u5bb6\u91cc\u6765\u505a\u624b\u811a\uff0c\u662f\u4ee5\u8fd9\u4f4d\u6b66\u6797\u9b41\u9996\u3000\u7684\u5b85 \u7b2c\uff0c\u6b64\u523b\u662f\u5b8c\u5168\u9759\u5bc2\u7684\uff0c\u56db\u65e0\u4eba\u5f71\u3002 \u3000\u3000\u3000\u661f\u5149\u5fae\u6620\uff0c\u53ef\u4ee5\u770b\u51fa\u8fd9\u4eba\u5168\u8eab\u6697\u7070\u8272\u7684\u591c\u884c\u8863\uff0c\u8fde\u8138\u4e0a\u90fd\u8499\u7740\u4e00\u65b9\u7070\u4e2d\uff0c\u662f\u4ee5\u3000\u9664\u4e86 \u4ed6\u5300\u79f0\u7684\u8eab\u6750\u5916\uff0c\u522b\u4eba\u4fbf\u4e00\u65e0\u6240\u77e5\u3002 \u3000\u3000\u3000\u4ed6\u5728\u5927\u5385\u5916\u5fae\u4e00\u5f20\u671b\uff0c\u4fbf\u8f7b\u5de7\u5730\u63a8\u5f00\u95e8\uff0c\u8db3\u5c16\u4e00\u70b9\uff0c\u7b14\u76f4\u5730\u5f80\u90a3\u9ed1\u7f0e\u795e\u9f9b\u524d\u63a0\u3000\u4e86\u8fc7 \u53bb\uff0c\u8f7b\u4f38\u53f3\u624b\uff0c\u4fbf\u8981\u5c06\u8fd9\u9ed1\u7f0e\u5e54\u5e03\u6380\u5f00\u2014\u2014\u84e6\u5730\uff0c\u4e00\u58f0\u8f7b\u53f1\u54cd\u8d77\u540e\uff0c\u4ed6\u5927\u60ca\u8f6c\u3000\u8eab\uff0c\u5374\u89c1\u4e00 \u4eba\u51b7\u51b7\u5f53\u95e8\u800c\u7acb\u3002 \u3000\u3000\u3000\u3000\u3000\u4ed6\u4f3c\u4e4e\u4e0d\u613f\u548c\u8fd9\u4eba\u671d\u76f8\uff0c\u8eab\u8eaf\u4e00\u6298\uff0c\u659c\u659c\u63a0\u51fa\uff0c\u8f7b\u53f1\u4e00\u58f0\u7684\u5374\u662f\u6bdb\u6587\u742a\uff0c\u67f3\u8170\u3000 \u4e00\u8f6c\uff0c\u5982\u5f71\u9644\u5f62\u5730\u8ddf\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u3000\u54ea\u77e5\u90a3\u591c\u884c\u4eba\u8f7b\u529f\u8fe5\u5f02\u4fd7\u6d41\uff0c\u5c31\u5728\u6bdb\u6587\u742a\u63a0\u5411\u4ed6\u7684\u53bb\u8def\u7684\u4e00\u5239\u90a3\u91cc\uff0c\u4ed6\u53cc\u81c2\u731b\u3000\u4e00\u8f6c \u6298\uff0c\u8eab\u5f62\u50cf\u662f\u6c34\u4e2d\u7684\u6e38\u9c7c\u4f3c\u7684\uff0c\u84e6\u5730\u8f6c\u5f2f\u6362\u4e86\u4e2a\u65b9\u5411\uff0c\u5feb\u5982\u7535\u5149\u4e00\u95ea\u5730\u63a0\u51fa\u4e86\u3000\u95e8\u3002 \u3000\u3000\u3000\u6bdb\u6587\u742a\u4e00\u6b65\u53d7\u611a\uff0c\u6c14\u5f97\u7c89\u9762\u51dd\u971c\uff0c\u4e00\u8dfa\u811a\uff0c\u53c8\u8ffd\u4e86\u51fa\u53bb\uff0c\u5979\u597d\u80dc\u5fc3\u7279\u5f3a\uff0c\u7adf\u4e0d\u3000\u613f\u60ca \u52a8\u522b\u7684\u4eba\uff0c\u53ea\u51ed\u7740\u81ea\u5bb6\u4e4b\u529b\uff0c\u5c31\u60f3\u628a\u4eba\u5bb6\u7559\u4e0b\u6765\u3002 \u3000\u3000\u3000\u8fd9\u6b63\u662f\u90a3\u591c\u884c\u4eba\u6240\u6df1\u5207\u76fc\u671b\u7684\uff0c\u4e00\u51fa\u5385\u95e8\uff0c\u4ed6\u5c31\u5411\u5899\u5916\u63a0\u53bb\u3002 \u3000\u3000\u3000\u4ed6\u8f7b\u529f\u867d\u9ad8\uff0c\u6bdb\u6587\u742a\u5374\u4e5f\u4e0d\u5f31\uff0c\u8fd9\u4e24\u6761\u4eba\u5f71\u4e00\u524d\u4e00\u540e\uff0c\u5feb\u5982\u6d41\u661f\u98de\u63a0\u7740\uff0c\u970e\u773c\u3000\u4e4b \u95f4\uff0c\u5df2\u79bb\u5f00\u6bdb\u81ec\u7684\u5b85\u7b2c\u6709\u6570\u5341\u4e08\u4e86\u3002 \u3000\u3000\u3000\u6bdb\u6587\u742a\u8fd9\u65f6\u624d\u5a07\u559d\u9053\uff1a\u201c\u670b\u53cb\u65e2\u7136\u6709\u79cd\u5230\u8fd9\u91cc\u6765\uff0c\u53c8\u4f55\u5fc5\u50cf\u53ea\u89c1\u4e0d\u5f97\u4eba\u7684\u8017\u5b50\u3000\u4f3c\u5730 \u9003\u8d70\uff1f\u201d\u5979\u8bed\u58f0\u65b9\u987f\uff0c\u90a3\u591c\u884c\u4eba\u54c8\u54c8\u4e00\u7b11\uff0c\u7adf\u4e5f\u500f\u7136\u987f\u8db3\uff0c\u8eab\u8eaf\u4e00\u8f6c\uff0c\u8fce\u5411\u6bdb\u3000\u6587\u742a\uff0c\u8eab\u8eaf \u7684\u6536\u53d1\u81ea\u5982\uff0c\u786e\u5df2\u5999\u5230\u6beb\u98a0\u3002 \u3000\u3000\u3000\u6bdb\u6587\u742a\u60f3\u4e0d\u5230\u4ed6\u7a81\u7136\u56de\u8eab\u987f\u8db3\uff0c\u8eab\u5f62\u63a0\u5904\uff0c\u7adf\u5feb\u649e\u5012\u90a3\u591c\u884c\u4eba\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u3000\u987b\u77e5\u4ed6\u4e24\u4eba\u8eab\u5f62\u4e4b\u5feb\uff0c\u5982\u975e\u773c\u89c1\uff0c\u5b9e\u5728\u96be\u4ee5\u5f62\u5bb9\uff0c\u90a3\u51e0\u4e4e\u6709\u548c\u58f0\u97f3\u540c\u6837\u7684\u901f\u5ea6\u3000\uff0c\u662f \u4ee5\u6bdb\u6587\u742a\u8bed\u58f0\u65b9\u843d\uff0c\u4eba\u5230\u4e86\u4eba\u5bb6\u8eab\u524d\u3002 \u3000\u3000\u3000\u5979\u52bf\u53d1\u96be\u6536\uff0c\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u5979\u4e00\u4e0b\u771f\u6c14\u731b\u6563\uff0c\u7adf\u8f7b\u98d8\u98d8\u843d\u4e86\u4e0b\u6765\uff0c\u4f46\u6b64\u523b\u5979\u3000\u548c\u90a3 \u591c\u884c\u4eba\u4e4b\u95f4\u7684\u8ddd\u79bb\uff0c\u5df2\u4e0d\u8fc7\u4e00\u5c3a\u4e86\u3002\u751a\u81f3\u5979\u8eab\u4e0a\u6240\u6563\u53d1\u7684\u90a3\u79cd\u6de1\u6de1\u7684\u5904\u4e8e\u5e7d\u9999\u3000\uff0c\u4eba\u5bb6\u90fd\u80fd \u55c5\u5230\u3002 \u3000\u3000\u3000\u90a3\u591c\u884c\u4eba\u53c8\u8f7b\u8f7b\u7b11\u4e86\u51fa\u6765\uff0c\u6bdb\u6587\u742a\u8138\u4e00\u7ea2\uff0c\u5e26\u7740\u6012\u610f\u9053\uff1a\u201c\u670b\u53cb\uff0c\u4f60\u7741\u5f00\u773c\u775b\u3000\u770b\u2014 \u2014\u201d\u5979\u8bdd\u672a\u8bf4\u5b8c\uff0c\u5c31\u88ab\u4eba\u5bb6\u7684\u7b11\u58f0\u6253\u65ad\uff1a\u201c\u4e00\u4e2a\u59d1\u5a18\u5bb6\uff0c\u8bf4\u8bdd\u600e\u4e48\u50cf\u6c5f\u6e56\u5f3a\u76d7\u3000\u4f3c\u7684\u3002\u201d\u90a3 \u591c\u884c\u4eba\u7c97\u7740\u58f0\u97f3\u9053\uff0c\u7adf\u4e5f\u662f\u5341\u5206\u7eaf\u6b63\u7684\u5317\u65b9\u53e3\u97f3\uff0c\u53ea\u662f\u58f0\u97f3\u9887\u4e3a\u6c99\u54d1\u3002 \u3000\u3000\u3000\u6bdb\u6587\u742a\u7684\u8138\uff0c\u4e0d\u7981\u7ea2\u4e86\u4e00\u4e0b\uff0c\u5979\u751f\u957f\u5728\u8fd9\u79cd\u5bb6\u5ead\uff0c\u8a00\u8bcd\u4e4b\u95f4\uff0c\u81ea\u7136\u96be\u514d\u7ed9\u67d3\u5230\u3000\u4e00\u4e9b \u6c5f\u6e56\u4e60\u6c14\uff0c\u5979\u4ee5\u5f80\u4e0d\u81ea\u89c9\uff0c\u6b64\u523b\u5374\u8d67\u7136\uff0c\u5973\u5b69\u5b50\u5bb6\uff0c\u90fd\u613f\u610f\u81ea\u5df1\u6587\u6587\u9759\u9759\u7684\uff0c\u3000\u8c01\u4e5f\u4e0d\u613f\u610f \u88ab\u4eba\u8ba5\u7b11\u6210\u6c5f\u6e56\u5f3a\u76d7\u3002 \u3000\u3000\u3000\u4e8e\u662f\u8fd9\u672c\u6765\u662f\u201c\u6293\u5f3a\u76d7\u201d\u7684\u4eba\uff0c\u6b64\u523b\u88ab\u4eba\u6307\u505a\u201c\u5f3a\u76d7\u201d\u4e4b\u540e\uff0c\u53cd\u800c\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u3000\u90a3\u591c\u884c\u4eba\u8499\u5728\u7070\u4e2d\u4e4b\u540e\u7684\u4e24\u53ea\u773c\u775b\uff0c\u77ac\u4e5f\u4e0d\u77ac\u5730\u671b\u7740\u5979\uff0c\u4f3c\u4e4e\u4e5f\u6709\u4e9b\u597d\u7b11\u7684\u610f\u3000\u601d\uff0c \u76ee\u5149\u4e00\u8f6c\uff0c\u8f6c\u5230\u5979\u80a9\u5934\u9732\u51fa\u7684\u5251\u9798\uff0c\u53c8\u5e26\u7740\u8ba5\u4fcf\u4e4b\u610f\u4ed6\u8bf4\u9053\uff1a\u201c\u8d77\u5148\u6211\u53ea\u5f53\u676d\u3000\u5dde\u6bdb\u5bb6\u662f\u4ec0 \u4e48\u4e86\u4e0d\u8d77\u7684\u6240\u5728\uff0c\u54ea\u77e5\u2014\u2014\u54fc\uff01\u201d\u65e0\u6bd4\u7684\u8f7b\u8511\uff0c\u65e0\u6bd4\u7684\u85d0\u89c6\uff0c\u90fd\u5728\u8fd9\u201c\u3000\u54fc\u201d\u58f0\u91cc\u8868\u9732\u51fa \u6765\u3002 \u3000\u3000\u3000\u6bdb\u6587\u742a\u53ef\u518d\u4e5f\u53d7\u4e0d\u4e86\uff0c\u4ece\u5979\u8bb0\u5fc6\u5f00\u59cb\uff0c\u8fd8\u672a\u66fe\u6709\u4eba\u6562\u5bf9\u6bdb\u5bb6\u8bf4\u8fc7\u4efb\u4f55\u4e0d\u656c\u4e4b\u540e\u3000\uff0c\u8fd9 \u4e00\u58f0\u201c\u54fc\u201d\uff0c\u4f7f\u5f97\u5979\u7f8e\u76ee\u6012\u5f20\uff0c\u53ea\u662f\u5979\u672c\u6765\u80fd\u8a00\u5584\u8fa9\uff0c\u53ef\u662f\u5728\u8fd9\u591c\u884c\u4eba\u9762\u524d\uff0c\u3000\u5374\u50cf\u662f\u6709\u4e9b \u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u3000\u4e8e\u662f\u5979\u6839\u672c\u5c31\u4e0d\u8bf4\u8bdd\u4e86\uff0c\u5a07\u53f1\u4e00\u58f0\uff0c\u5de6\u624b\u4e00\u5f15\uff0c\u53f3\u638c\u659c\u524a\uff0c\u4e00\u62db\u201c\u7fe0\u9e1f\u68b3\u7fbd\u201d\u3000\uff0c\u5e26 \u7740\u98ce\u58f0\u76f4\u53d6\u90a3\u591c\u884c\u4eba\u7684\u5de6\u9888\u3002 \u3000\u3000\u3000\u8fd9\u4e00\u62db\u4e0d\u4f46\u5feb\u5982\u98d8\u98ce\uff0c\u800c\u4e14\u7a81\u5982\u5176\u6765\uff0c\u6bdb\u6587\u742a\u6ee1\u4ee5\u4e3a\u8fd9\u4e00\u638c\u7eb5\u4f7f\u4e0d\u80fd\u514b\u654c\u594f\u529f\u3000\uff0c\u81f3 \u5c11\u4e5f\u5f97\u8ba9\u5bf9\u65b9\u4e00\u60ca\uff0c\u81ea\u5df1\u62a2\u5f97\u5148\u673a\u3002 \u3000\u3000\u3000\u54ea\u77e5\u4eba\u5bb6\u5de6\u638c\u4f38\u66f2\u95f4\uff0c\u8fde\u6d88\u5e26\u6253\uff0c\u53f3\u638c\u201c\u55d6\u201d\u5730\u5212\u4e86\u4e2a\u5708\u5708\uff0c\u7a81\u5730\u4e2d\u95f4\u62a2\u51fa\uff0c\u3000\u5374\u5316 \u638c\u4e3a\u62f3\uff0c\u98df\u3001\u4e2d\u4e24\u6307\u51f8\u51fa\u3002\u76f4\u70b9\u6bdb\u6587\u742a\u7684\u201c\u80a9\u4e95\u201d\u7a74\u3002 \u3000\u3000\u3000\u6bdb\u6587\u742a\u5fc3\u4e2d\u4e00\u60ca\uff0c\u8fd9\u591c\u884c\u4eba\u4e0d\u4f46\u51fa\u624b\u5feb\uff0c\u6700\u5389\u5bb3\u7684\u662f\u4ed6\u5de6\u3001\u53f3\u4e24\u624b\u6240\u7528\u7684\u62f3\u8def\u3000\uff0c\u7adf \u5b8c\u5168\u4e0d\u540c\u3002\u4ed6\u53f3\u638c\u540e\u53d1\u5148\u81f3\uff0c\u62f3\u98ce\u521a\u731b\uff0c\u6307\u8282\u51fb\u7a74\uff0c\u867d\u7136\u5df2\u662f\u7edd\u62db\uff0c\u4f46\u662f\u4ed6\u7684\u3000\u5de6\u624b\u90a3\u5fae\u4e00 \u66f2\u4f38\u95f4\u6240\u8d70\u7684\u62f3\u8def\uff0c\u7adf\u662f\u81ea\u5df1\u524d\u6240\u672a\u89c1\u7684\uff0c\u7adf\u6709\u8bf4\u4e0d\u51fa\u7684\u5965\u5999\u3002 \u3000\u3000\u3000\u5979\u5fc3\u4e2d\u5728\u7b97\u8ba1\u7740\uff0c\u624b\u5e95\u5e76\u672a\u95f2\u4e0b\uff0c\u53cc\u638c\u8fde\u8fde\u6325\u51fa\uff0c\u8f6c\u77ac\u4e4b\u95f4\uff0c\u5df2\u548c\u5bf9\u65b9\u62c6\u4e86\u4e09\u3000\u638c\uff0c \u638c\u98ce\u552c\u552c\uff0c\u8d70\u7684\u5c45\u7136\u4e5f\u662f\u521a\u731b\u4e00\u8def\u3002 \u3000\u3000\u3000\u539f\u6765\u201c\u5c60\u9f99\u4ed9\u4e8e\u201d\u751f\u5177\u5f02\u7980\uff0c\u795e\u529b\u60ca\u4eba\uff0c\u867d\u662f\u5973\u6d41\uff0c\u4f46\u81ea\u521b\u7684\u201c\u5c60\u9f99\u516b\u4e00\u5f0f\u201d\u3000\u6eb6\u5408 \u5185\u5916\u4e4b\u529f\uff0c\u8d70\u7684\u5374\u662f\u9633\u521a\u4e4b\u8def\uff0c\u5979\u4ee5\u6b64\u6210\u540d\uff0c\u6b66\u6797\u4e2d\u5c1a\u672a\u95fb\u6709\u80fd\u5728\u5979\u8fd9\u638c\u6cd5\u4e0b\u3000\u8ba8\u5f97\u4fbf\u5b9c\u3002 \u3000\u3000\u3000\u53ef\u662f\u6b64\u523b\u6bdb\u6587\u742a\u4f7f\u51fa\u6765\uff0c\u5374\u6709\u4e9b\u900a\u8272\u4e86\uff0c\u5973\u5b69\u5b50\u4f7f\u7528\u8fd9\u81f3\u9633\u81f3\u521a\u7684\u638c\u6cd5\uff0c\u603b\u4e0d\u3000\u719f \u8def\uff0c\u4f55\u51b5\u5bf9\u65b9\u6240\u4f7f\u7684\u62db\u5f0f\uff0c\u66f4\u662f\u8be1\u5f02\u83ab\u6d4b\u54e9\u3002 \u3000\u3000\u3000\u5341\u62db\u8fc7\u53bb\uff0c\u6bdb\u6587\u742a\u5df2\u611f\u4e0d\u652f\uff0c\u5979\u6781\u4e3a\u60ca\u6050\u4f55\u6765\u8fd9\u79cd\u6b66\u6797\u9ad8\u624b\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u7a81\u5730\u3000\u5a07\u559d \u9053\uff1a\u201c\u4f4f\u624b\uff01\u201d \u3000\u3000\u3000\u90a3\u591c\u884c\u4eba\u679c\u7136\u4e00\u6014\uff0c\u624b\u4e0b\u4e00\u6162\uff0c\u6bdb\u6587\u742a\u5df2\u6a2a\u63a0\u4e94\u5c3a\uff0c\u5374\u500f\u7136\u53cd\u624b\u62bd\u51fa\u5251\u6765\u3002\u7acb\u3000\u523b\u7ea2 \u5149\u66b4\u957f\uff0c\u5b9b\u5982\u7535\u95ea\u3002 \u3000\u3000\u3000\u5979\u51b7\u51b7\u4e00\u7b11\uff0c\u559d\u9053\uff1a\u201c\u4f60\u518d\u8bd5\u8bd5\u8fd9\u4e2a\u3002\u201d\u5de6\u624b\u5fae\u634f\u5251\u8bc0\uff0c\u53f3\u624b\u957f\u5251\u4e00\u6296\uff0c\u5239\u90a3\u3000\u95f4\u5251 \u5f71\u6ee1\u5929\uff0c\u55e1\u7136\u4e00\u58f0\uff0c\u90a3\u73ca\u745a\u8272\u7684\u957f\u5251\u5316\u505a\u65e0\u6570\u4e2a\u6781\u5c0f\u7684\u5251\u56e2\uff0c\u50cf\u662f\u65e0\u6570\u56e2\u8d64\u7ea2\u3000\u7684\u706b\u7130\uff0c\u6295 \u5411\u90a3\u591c\u884c\u4eba\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u3000\u90a3\u591c\u884c\u4eba\u8fd9\u624d\u77e5\u9053\u6bdb\u6587\u742a\u90a3\u4e00\u58f0\u201c\u4f4f\u624b\u201d\uff0c\u53ea\u662f\u7f13\u5175\u4e4b\u8ba1\u7f62\u4e86\uff0c\u65b9\u81ea\u6697\u7b11\u81ea\u5df1\u3000\uff0c\u6bdb \u6587\u742a\u8fd9\u602a\u5f02\u4e4b\u6781\u7684\u957f\u5251\u5df2\u524a\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u3000\u5251\u8eab\u672a\u81f3\uff0c\u4ed6\u5df2\u9690\u9690\u89c9\u51fa\u4e00\u80a1\u70ed\u529b\uff0c\u8fd9\u73ca\u745a\u8272\u7684\u957f\u5251\u7adf\u548c\u4e16\u4e0a\u6240\u6709\u7684\u5251\u90fd\u4e0d\u76f8\u3000\u540c\uff0c \u5251\u8eab\u4e0a\u53d1\u51fa\u7684\u4e0d\u662f\u5bd2\u610f\u800c\u662f\u70ed\u6c14\uff0c\u4ed6\u4e0d\u6562\u5192\u7136\u63a5\u6b64\u4e00\u62db\uff0c\u811a\u6b65\u5fae\u9519\uff0c\u8eab\u5f62\u6ed1\u5f00\u3000\uff0c\u907f\u5f00\u4e86\u6b64 \u62db\u3002 \u3000\u3000\u3000\u6bdb\u6587\u742a\u5a07\u53f1\u4e00\u58f0\uff0c\u5251\u52bf\u53c8\u4e00\u5708\uff0c\u7531\u65e0\u6570\u56e2\u5c0f\u7684\u706b\u7130\uff0c\u5316\u4e3a\u4e00\u5708\u6781\u5927\u7684\u706b\u7130\uff0c\u659c\u3000\u659c\u4e00 \u5212\uff0c\u53c8\u53d8\u6210\u4e00\u6761\u8d64\u7ea2\u7684\u706b\u9f99\uff0c\u5377\u5411\u90a3\u591c\u884c\u4eba\u3002 \u3000\u3000\u3000\u90a3\u591c\u884c\u4eba\u4ecd\u662f\u4e0d\u6562\u8fd8\u62db\uff0c\u53c8\u9000\u5f00\u6570\u5c3a\uff0c\u6bdb\u6587\u742a\u518d\u4e00\u8f6c\u5251\u52bf\uff0c\u6b65\u6b65\u8fdb\u8feb\uff0c\u90a3\u591c\u884c\u3000\u4eba\u957f \u5578\u4e00\u58f0\uff0c\u8eab\u5f62\u6597\u7136\u62d4\u8d77\u4e24\u4e08\u4f59\uff0c\u53cc\u81c2\u4e00\u5f20\uff0c\u55d6\u5730\uff0c\u53c8\u62d4\u8d77\u4e03\u5c3a\uff0c\u7adf\u662f\u8f7b\u529f\u4e2d\u767b\u3000\u5cf0\u9020\u6781\u7684 \u201c\u4e0a\u5929\u68af\u201d\u3002 \u3000\u3000\u3000\u4ed6\u8fd9\u4e00\u8d77\u4e4b\u52bf\uff0c\u5df2\u8fc7\u4e09\u4e08\uff0c\u6bdb\u6587\u742a\u53ef\u671b\u800c\u4e0d\u53ef\u53ca\uff0c\u6697\u5fd6\uff1a\u201c\u53ea\u8981\u4f60\u8eab\u5b50\u843d\u4e0b\u6765\u3000\uff0c\u6211 \u5c31\u518d\u7ed9\u4f60\u4e00\u5251\u3002\u201d \u3000\u3000\u3000\u54ea\u77e5\u90a3\u591c\u884c\u4eba\u5728\u7a7a\u4e2d\u4e00\u4e2a\u5927\u8f6c\u8eab\uff0c\u5934\u4e0b\u811a\u4e0a\uff0c\u7adf\u7bad\u4e00\u6837\u5730\u659c\u7a9c\u4e86\u51fa\u53bb\uff0c\u5728\u65c1\u8fb9\u3000\u7684\u6797 \u6728\u4e0a\uff0c\u5fae\u4e00\u6cbe\u8db3\uff0c\u5530\u5730\uff0c\u53c8\u51b2\u5929\u800c\u8d77\uff0c\u8fdc\u8fdc\u9038\u53bb\u3002 \u3000\u3000\u3000\u8fd9\u4e00\u4e0b\uff0c\u6bdb\u6587\u742a\u624d\u77e5\u9053\u4eba\u5bb6\u7684\u8f7b\u529f\u4e4b\u9ad8\uff0c\u8fdc\u8fdc\u5728\u81ea\u5df1\u4e4b\u4e0a\uff0c\u65b9\u624d\u4eba\u5bb6\u4e5f\u8bb8\u662f\u6709\u3000\u5fc3\u8bf1 \u654c\uff0c\u624d\u548c\u81ea\u5df1\u82e5\u5373\u82e5\u79bb\u5730\u4fdd\u6301\u7740\u4e00\u6bb5\u8ddd\u79bb\u3002 \u3000\u3000\u3000\u5979\u81ea\u521d\u51fa\u6c5f\u6e56\uff0c\u6ee1\u6000\u58ee\u5fd7\uff0c\u4e4d\u4e00\u51fa\u624b\uff0c\u4fbf\u632b\u4e86\u201c\u6cb3\u6714\u53cc\u5251\u201d\uff0c\u6ee1\u4ee5\u4e3a\u81ea\u5df1\u5df2\u662f\u3000\u9ad8\u624b \u4e86\uff0c\u54ea\u77e5\u6b64\u523b\u9047\u7740\u8fd9\u4e0d\u77e5\u540d\u7684\u591c\u884c\u4eba\uff0c\u4eba\u5bb6\u65e0\u8bba\u8f7b\u529f\uff0c\u638c\u529b\uff0c\u90fd\u6bd4\u81ea\u5df1\u9ad8\u660e\u5f97\u3000\u591a\uff0c\u81ea\u5df1\u867d \u4ed7\u7740\u6b66\u6797\u4e2d\u7edd\u65e0\u4eba\u77e5\u7684\u5b9d\u5251\u5c06\u4e4b\u51fb\u9000\uff0c\u4f46\u5374\u4e5f\u7b97\u4e0d\u5f97\u8363\u8000\u5440\uff01 \u3000\u3000\u3000\u5979\u5fc3\u91cc\u81ea\u95ee\uff0c\u4e0d\u77e5\u9053\u8fd9\u591c\u884c\u4eba\u7a76\u7adf\u662f\u4f55\u6765\u8def\uff1f\u600f\u600f\u5730\u8d70\u4e86\u56de\u53bb\uff0c\u8fdc\u5904\u7684\u66f4\u9f13\uff0c\u3000\u968f\u540c \u4f20\u6765\uff0c\u949f\u58f0\u56db\u54cd\uff0c\u5df2\u7ecf\u662f\u56db\u66f4\u4e86\u3002 \u3000\u3000\u3000\u7b2c\u4e8c\u5929\uff0c\u77f3\u78f7\u8d77\u6765\u7684\u65f6\u5019\uff0c\u53d1\u73b0\u548c\u4ed6\u540c\u5c4b\u800c\u7720\u7684\u7f2a\u6587\u4ecd\u5728\u8499\u5934\u5927\u7761\uff0c\u4fbf\u4e5f\u6ca1\u6709\u3000\u53bb\u60ca \u52a8\u4ed6\uff0c\u6084\u7136\u8d70\u5230\u9662\u5b50\u91cc\u53bb\u3002 \u3000\u3000\u3000\u671d\u9732\u5df2\u5e72\uff0c\u6625\u65e5\u65e9\u5347\u3002 \u3000\u3000\u3000\u77f3\u78f7\u6697\u53f9\u4e00\u58f0\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u4ed6\u5df2\u8d77\u5f97\u8f83\u4ee5\u524d\u665a\u4e86\uff0c\u4ed6\u6000\u7591\u81ea\u5df1\u662f\u5426\u8001\u4e86\uff0c\u8fce\u7740\u3000\u6e05\u6668 \u7684\u51b7\u98ce\uff0c\u6df1\u5438\u4e00\u53e3\u6e05\u65b0\u800c\u6f6e\u6e7f\u7684\u7a7a\u6c14\uff0c\u610f\u5174\u987f\u751f\uff0c\u5728\u56ed\u4e2d\u8f6f\u8f6f\u7684\u6ce5\u5730\u4e0a\uff0c\u5fae\u5fae\u3000\u6d3b\u4e86\u6d3b\u6b65 \u773c\uff0c\u53cc\u81c2\u4e0b\u5782\uff0c\u53cc\u819d\u5fae\u66f2\uff0c\u7adf\u7f13\u7f13\u5730\u5c06\u6b66\u5f53\u5fc3\u6cd5\u5341\u6bb5\u9526\u4e00\u62db\u4e00\u5f0f\u5730\u8d70\u4e86\u8d77\u3000\u6765\u3002 \u3000\u3000\u3000\u4ed6\u51fa\u62db\u867d\u7f13\uff0c\u4f46\u6bcf\u4e00\u62db\u90fd\u662f\u795e\u5b8c\u6c14\u8db3\uff0c\u52b2\u5f0f\uff0c\u529f\u529b\uff0c\u65e0\u4e00\u4e0d\u662f\u6070\u5230\u597d\u5904\uff0c\u8fd9\u79cd\u3000\u5185\u5bb6 \u7684\u62db\u5f0f\uff0c\u9aa4\u7136\u671b\u53bb\uff0c\u867d\u7136\u5e76\u6ca1\u6709\u4ec0\u4e48\u5999\u5904\uff0c\u4f46\u5b66\u6b66\u7684\u4eba\u60f3\u7ec3\u5230\u8fd9\u79cd\u529f\u529b\uff0c\u5374\u4e5f\u3000\u975e\u662f\u4e00\u671d\u4e00 \u5915\u4e4b\u529f\u54e9\uff01 \u3000\u3000\u3000\u4ed6\u4e00\u5957\u62f3\u65b9\u8d70\u5b8c\uff0c\u5ffd\u7136\u542c\u5f97\u6709\u4eba\u559d\u91c7\uff0c\u8f6c\u5934\u4e00\u671b\uff0c\u5374\u89c1\u7f2a\u6587\u62d6\u7740\u978b\uff0c\u655e\u7740\u8863\u895f\u3000\uff0c\u659c \u501a\u5728\u95e8\u65c1\uff0c\u5411\u81ea\u5df1\u542b\u7b11\u8bf4\u9053\uff1a\u201c\u77f3\u5144\u597d\u4fca\u7684\u8eab\u624b\u3002\u201d\u77f3\u78f7\u5fae\u5fae\u4e00\u7b11\uff0c\u9887\u4e3a\u5f97\u610f\u3000\u5730\u671b\u4e86\u4ed6\u742a \u773c\uff0c\u9053\uff1a\u201c\u4ee5\u7f2a\u5144\u7684\u6839\u9aa8\uff0c\u5b66\u8d77\u6b66\u6765\uff0c\u6015\u4e0d\u6bd4\u5c0f\u5f1f\u5f3a\u80dc\u767e\u500d\u3002\u201d \u3000\u3000\u3000\u7f2a\u6587\u548c\u4ed6\u5bf9\u89c6\u4e00\u773c\uff0c\u4e5f\u4e00\u7b11\uff0c\u5927\u5bb6\u90fd\u4f3c\u4e4e\u6709\u201c\u5fc3\u7167\u4e0d\u5ba3\u201d\u4e4b\u610f\uff0c\u5374\u89c1\u56ed\u4e2d\u6797\u6728\u3000\u63a9\u6620 \u5904\uff0c\u8885\u8885\u884c\u6765\u4e00\u4e2a\u7fe0\u88c5\u5c11\u5973\uff0c\u8fdc\u8fdc\u5c31\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u5012\u8d77\u6765\u5f97\u65e9\u3002\u201d\u7f2a\u6587\u4e00\u7b11\uff0c\u3000\u4e5f\u9053\uff1a\u201c\u59d1 \u5a18\u4e5f\u65e9\u3002\u201d\u539f\u6765\u6b63\u662f\u6bdb\u6587\u742a\uff0c\u5979\u5634\u4e00\u561f\uff0c\u5a07\u55d4\u7740\u9053\uff1a\u201c\u6211\u4e0d\u662f\u8d77\u5f97\u65e9\uff0c\u3000\u6211\u6839\u672c\u4e00\u591c\u6ca1\u7761 \u5462\uff01\u201d\u987f\u4e86\u987f\uff0c\u53c8\u9053\uff0c\u201c\u4f60\u4eec\u8bf4\u5947\u602a\u4e0d\u5947\u602a\uff0c\u201d\u6628\u5929\u665a\u4e0a\u8fd9\u91cc\u5c45\u7136\u3000\u95f9\u8d3c\uff0c\u6709\u4eba\u60f3\u6765\u5077\u4e1c \u897f\uff0c\u4e8f\u7684\u2014\u2014\u4e8f\u7684\u88ab\u6211\u53d1\u73b0\uff0c\u624d\u628a\u4ed6\u7ed9\u6253\u8dd1\u4e86\u3002\u201d \u3000\u3000\u3000\u7f2a\u6587\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4ee5\u59d1\u5a18\u7684\u8eab\u624b\uff0c\u5bf9\u4ed8\u4e00\u4e2a\u5c0f\u8d3c\u81ea\u7136\u6ca1\u6709\u95ee\u9898\u3002\u201d\u6bdb\u6587\u742a\u8138\u4e00\u3000\u7ea2\uff0c \u5782\u9996\u73a9\u5f04\u7740\u8863\u89d2\uff0c\u5ffd\u7136\u62ac\u8d77\u5934\uff0c\u671d\u77f3\u78f7\u671b\u53bb\uff0c\u7b11\u9053\uff1a\u201c\u77f3\u53d4\u53d4\uff0c\u4f60\u8bf4\u6211\u5012\u9709\u4e0d\u3000\u5012\u9709\uff0c\u8fd9\u51e0 \u5929\u676d\u5dde\u6b63\u70ed\u95f9\uff0c\u542c\u8bf4\u5de6\u624b\u795e\u5251\uff0c\u9e33\u9e2f\u53cc\u5251\u867d\u7136\u6682\u65f6\u53bb\u4e86\uff0c\u4f46\u4e0d\u51fa\u4e24\u5929\uff0c\u3000\u4ed6\u4eec\u8fd8\u8981\u56de\u6765\uff0c\u53ef \u662f\u6211\u5440\uff0c\u5374\u504f\u504f\u518d\u8fc7\u4e24\u5929\u5c31\u8981\u79bb\u5f00\u8fd9\u513f\u4e86\u3002\u201d \u3000\u3000\u3000\u5979\u5634\u867d\u5728\u5bf9\u77f3\u78f7\u8bf4\u8bdd\uff0c\u773c\u89d2\u5374\u6709\u610f\u65e0\u610f\u95ee\u98d8\u5411\u7f2a\u6587\uff0c\u77f3\u78f7\u542b\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u54ea\u91cc\u3000 \u53bb\uff1f\u201d \u3000\u3000\u3000\u201c\u56de\u5230\u5e08\u5085\u90a3\u91cc\u53bb\u5440\uff01\u6211\u676d\u5dde\u3001\u6cb3\u5317\u6765\u56de\u5730\u8dd1\uff0c\u6bcf\u5e74\u603b\u8981\u8dd1\u4e0a\u4e00\u6b21\u3002\u201d\u5979\u5a07\u58f0\u3000\u8bf4 \u7740\uff0c\u7f2a\u6587\u7a81\u7136\u63a5\u8fc7\u8bdd\u9898\uff0c\u6717\u58f0\u9053\uff1a\u201c\u5c0f\u53ef\u4e5f\u6b63\u60f3\u5230\u6cb3\u5317\u53bb\uff0c\u4e0d\u77e5\u2026\u2026\u2026\u201d\u4ed6\u8bdd\u672a\u3000\u8bf4\u5b8c\uff0c\u6bdb \u6587\u742a\u5df2\u9ad8\u5174\u4ed6\u8bf4\u9053\uff1a\u201c\u4f60\u5047\u5982\u80fd\u548c\u6211\u4e00\u8d77\u8d70\uff0c\u90a3\u597d\u6781\u4e86\uff0c\u6211\u4e5f\u591a\u4e2a\u4f34\u3002\u201d\u3000\u5979\u5929\u771f\u672a\u6cef\uff0c\u5bf9 \u7f2a\u6587\u5df1\u9887\u6709\u597d\u611f\uff0c\u7adf\u4e00\u4e9b\u4e5f\u4e0d\u865a\u9970\u5730\u5c06\u5fc3\u4e2d\u4e4b\u8bdd\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u3000\u4e8e\u662f\u7f2a\u6587\u5634\u89d2\uff0c\u53c8\u6cdb\u8d77\u4e86\u90a3\u79cd\u96be\u6d4b\u7684\u7b11\u610f\uff0c\u77f3\u78f7\u51b7\u773c\u65c1\u89c2\uff0c\u5fc3\u4e2d\u7a81\u5730\u4e00\u51db\uff0c\u7adf\u3000\u6000\u7740 \u5e26\u6709\u6050\u60e7\u7684\u773c\u5149\uff0c\u671b\u4e86\u7f2a\u6587\u4e00\u773c\u3002 \u3000\u3000\u3000\u4ed6\u6697\u6697\u53f9\u606f\u7740\uff0c\u8f6c\u8eab\u8d70\u4e86\u5f00\u53bb\uff0c\u81ea\u5df1\u89c9\u5f97\u81ea\u5df1\u597d\u50cf\u5df2\u77e5\u9053\u4e86\u4e00\u4e9b\u81ea\u5df1\u4e0d\u8be5\u77e5\u9053\u3000\u7684\u4e1c \u897f\u3002\u8fce\u76ee\u4e00\u671b\uff0c\u5374\u53c8\u89c1\u4e09\u4e2a\u91d1\u886b\u5c11\u5e74\u75be\u6b65\u800c\u6765\u3002 \u3000\u3000\u3000\u4ed6\u6545\u610f\u4f4e\u7740\u5934\uff0c\u4e0d\u53bb\u671b\u4ed6\u4eec\uff0c\u90a3\u4e09\u4e2a\u91d1\u886b\u5c11\u5e74\u4e5f\u4ec5\u671b\u4e86\u4ed6\u4e00\u773c\uff0c\u4fbf\u81ea\u8d70\u8fc7\uff0c\u9694\u3000\u7740\u597d \u8fdc\uff0c\u4e09\u4eba\u53e3\u4e2d\u5c31\u4e0d\u7ea6\u800c\u540c\u5730\u53eb\u7740\uff1a\u201c\u742a\u59b9\uff0c\u6211\u4eec\u56de\u6765\u4e86\u3002\u201d\u5927\u8e0f\u6b65\u8d70\u5230\u6bdb\u6587\u742a\u3000\u8eab\u4fa7\uff0c\u770b\u5230 \u659c\u501a\u5728\u95e8\u4fa7\u7684\u7f2a\u6587\uff0c\u5404\u81ea\u6014\u4e86\u4e00\u4e0b\uff0c\u6bdb\u6587\u742a\u5374\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u56de\u6765\u4e86\u3000\u5c31\u56de\u6765\u4e86\u561b\u3002\u8fd9\u4e48 \u5927\u60ca\u5c0f\u602a\u5730\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u3000\u8fd9\u4e09\u4eba\u53c8\u90fd\u4e00\u6014\uff0c\u7f2a\u6587\u89c1\u8fd9\u4e09\u4e2a\u91d1\u886b\u5c11\u5e74\u4ff1\u90fd\u9762\u76ee\u82f1\u633a\uff0c\u957f\u8eab\u7389\u7acb\uff0c\u7709\u76ee\u4e4b\u95f4\u3000\uff0c\u4e5f \u4ff1\u90fd\u662f\u50b2\u6c14\u51cc\u4eba\uff0c\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u60f3\u6765\u8fd9\u4e9b\u4e5f\u90fd\u662f\u2018\u7389\u9aa8\u4f7f\u8005\uff0c\u4e86\uff0c\u770b\u8d77\u6765\u5012\u8fd8\u3000\u90fd\u662f\u89d2 \u8272\u3002\u201d\u4ed6\u5728\u6253\u91cf\u7740\u4eba\u5bb6\uff0c\u90a3\u4e09\u4e2a\u91d1\u886b\u5c11\u5e74\u53c8\u4f55\u5c1d\u4e0d\u5728\u6253\u91cf\u7740\u4ed6\uff0c\u7f2a\u6587\u5fae\u5fae\u4e00\u3000\u7b11\uff0c\u8f6c\u8eab\u8d70\u4e86 \u8fdb\u53bb\uff0c\u4f46\u5fc3\u76ee\u4e2d\u5374\u5c06\u8fd9\u4e09\u4e2a\u91d1\u886b\u5c11\u5e74\u7684\u9762\u76ee\u8bb0\u4e86\u4e0b\u6765\u3002\u4ed6\u4e5f\u77e5\u9053\u6bdb\u6587\u3000\u742a\u8fd8\u5728\u671b\u7740\u4ed6\uff0c\u5fc3\u4e2d \u7981\u4e0d\u4f4f\u751f\u51fa\u4e00\u4e1d\u751c\u610f\uff0c\u4f46\u662f\u4ed6\u7acb\u523b\u5c06\u8fd9\u4efd\u60c5\u611f\u5f3a\u81ea\u6309\u637a\u4e0b\u53bb\uff0c\u3000\u4e00\u9762\u8b66\u544a\u7740\u81ea\u5df1\u3002\u201c\u4f60\u8981\u662f \u4e3a\u4efb\u4f55\u4eba\u800c\u6c89\u9677\u4e8e\u60c5\u611f\u7684\u8bdd\uff0c\u90a3\u5bf9\u4f60\u81ea\u5df1\u5c31\u662f\u592a\u5927\u7684\u635f\u3000\u5931\u4e86\uff0c\u60c5\u611f\uff01\u60c5\u611f\uff01\u4f60\u96be\u9053\u5df2\u4e0d\u8bb0 \u5f97\u4f60\u5230\u8fd9\u4e16\u4e0a\u6765\uff0c\u662f\u4e0d\u8be5\u5b58\u7740\u60c5\u611f\u7684\u5417\uff1f\u201d \u7b2c\u4e03\u7ae0>> \u53e4\u9f99\u300a\u6e58\u5983\u5251\u300b \u7b2c\u4e03\u7ae0 \u3000\u3000\u3000\u4e24\u5929\u4e4b\u540e\uff0c\u5f53\u5de6\u624b\u795e\u5251\u548c\u767e\u6b65\u98de\u82b1\u4e24\u4eba\u5230\u8fbe\u6bdb\u5b85\u65f6\uff0c\u7f2a\u6587\u5df2\u7ecf\u4ea4\u7ed9\u80e1\u4e4b\u8f89\u5341\u4e07\u3000\u4e24\u94f6 \u7968\uff0c\u8f9e\u522b\u4e86\u4e5f\u5c06\u4ed6\u53bb\u7684\u77f3\u78f7\uff0c\u5e26\u7740\u80e1\u4e4b\u8f89\u7684\u5343\u6069\u4e07\u8c22\uff0c\u548c\u6bdb\u81ec\u7684\u7231\u5973\u4e00\u9f50\u51fa\u57ce\u3000\u5317\u53bb\u4e86\u3002 \u3000\u3000\u3000\u4ece\u676d\u5dde\u5230\u6cb3\u5317\u7684\u8def\uff0c\u6bdb\u6587\u742a\u5b64\u8eab\u5f80\u6765\uff0c\u4e0d\u77e5\u6709\u591a\u5c11\u6b21\u4e86\uff0c\u53ef\u8bf4\u662f\u719f\u4e4b\u53c8\u719f\uff0c\u7f2a\u3000\u6587\u5b89 \u9759\u5730\u5750\u5728\u9a6c\u4e0a\uff0c\u8ddf\u7740\u5979\u8d70\uff0c\u53ef\u662f\u4e24\u53ea\u773c\u775b\u5374\u6781\u4e3a\u4e0d\u5b89\u9759\uff0c\u4e0a\u4e0a\u4e0b\u4e0b\u5730\u671b\u7740\u5979\uff0c\u3000\u4f7f\u5f97\u5979\u82b3\u5fc3 \u4e2d\u597d\u50cf\u6709\u5343\u767e\u53ea\u5c0f\u9e7f\u5728\u649e\u7740\u3002 \u3000\u3000\u3000\u8fd9\u79cd\u611f\u89c9\uff0c\u6bdb\u6587\u742a\u6709\u751f\u4ee5\u6765\uff0c\u8fd8\u662f\u7b2c\u4e00\u6b21\u611f\u5230\uff0c\u53ea\u89c9\u5f97\u53d7\u7528\u5f97\u5f88\uff0c\u4eff\u4f5b\u6709\u79cd\u8bf4\u3000\u4e0d\u4e0a \u6765\u7684\u5473\u9053\u3002 \u3000\u3000\u3000\u521a\u51fa\u676d\u5dde\u57ce\uff0c\u540e\u9762\u5c31\u5954\u6765\u51e0\u9a91\u9a6c\uff0c\u7f2a\u6587\u4e00\u76b1\u7709\uff0c\u5411\u6bdb\u6587\u742a\u9053\uff1a\u201c\u5927\u6982\u53c8\u662f\u4f60\u7684\u3000\u5e08\u5144 \u8d76\u6765\u4e86\u3002\u201d \u3000\u3000\u3000\u6bdb\u6587\u742a\u7b11\u95ee\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u3000\u8bed\u58f0\u65b9\u843d\uff0c\u540e\u9762\u7684\u9a91\u58eb\u679c\u7136\u5df2\u7ecf\u9ad8\u58f0\u53eb\u7740\uff1a\u201c\u742a\u5e08\u59b9\uff01\u201d\u7f2a\u6587\u5411\u6bdb\u6587\u742a\u4e00\u8038\u80a9\u3000\uff0c\u6bdb \u6587\u742a\u683c\u683c\u7b11\u4e86\u8d77\u6765\u3002 \u3000\u3000\u3000\u540e\u9762\u8ffd\u4e0a\u6765\u7684\u56db\u9a91\uff0c\u679c\u7136\u90fd\u662f\u201c\u7389\u9aa8\u4f7f\u8005\u201d\u3002\u90a3\u9634\u6c89\u673a\u72e1\u7684\u201c\u51cc\u98ce\u4f7f\u8005\u201d\u5e9e\u826f\u3000\u6e5b\uff0c \u4e5f\u5728\u5176\u4e2d\uff0c\u89c1\u4e86\u7f2a\u6587\uff0c\u5012\u5148\u5ba2\u6c14\u5f97\u5f88\uff0c\u53e6\u4e09\u4e2a\u91d1\u886b\u5c11\u5e74\u5374\u770b\u4e5f\u4e0d\u770b\u7f2a\u6587\u4e00\u773c\uff0c\u3000\u62e5\u5230\u6bdb\u6587\u742a \u56db\u4fa7\uff0c\u5176\u4e2d\u4e00\u4e2a\u76ae\u80a4\u767d\u7699\uff0c\u4f46\u5374\u751f\u5f97\u4e00\u4ed8\u5355\u8584\u4e4b\u76f8\u7684\u5c11\u5e74\u9053\uff1a\u201c\u5e08\u7236\u547d\u3000\u6211\u5230\u5180\u3001\u8c6b\u3001\u9102\u3001 \u8d63\u56db\u7701\uff0c\u6211\u4eec\u51c6\u5907\u5206\u5934\u884c\u4e8b\uff0c\u5e08\u59b9\uff0c\u4f60\u770b\u54ea\u4e00\u4e2a\u5230\u5180\u7701\u6700\u4e3a\u9002\u5f53\u3000\u5462\uff1f\u201d\u8bf4\u65f6\uff0c\u4ed6\u5e26\u7740\u4e00\u4ed8 \u963f\u8c00\u7684\u7b11\u5bb9\u3002 \u3000\u3000\u3000\u6bdb\u6587\u742a\u5374\u6ee1\u809a\u5b50\u4e0d\u9ad8\u5174\u5730\u9053\uff1a\u201c\u6211\u7ba1\u4f60\u4eec\u8c01\u53bb\uff1f\u201d\u5e9e\u826f\u6e5b\u9a6c\u7f30\u4e00\u8f6c\uff0c\u5de6\u624b\u63d0\u7740\u3000\u7f30 \u7ef3\u3002\u53f3\u624b\u5374\u63e1\u7740\u51e0\u679a\u5236\u94b1\uff0c\u9053\uff1a\u201c\u8c01\u731c\u51fa\u6211\u624b\u4e2d\u5236\u94b1\u7684\u6570\u76ee\uff0c\u8c01\u5c31\u966a\u742a\u59b9\u5230\u5180\u5317\u3000\u53bb\uff0c\u8981\u662f \u4f60\u4eec\u90fd\u662f\u731c\u4e0d\u5230\uff0c\u90a3\u2014\u2014\u90a3\u6211\u2026\u2026\u201d \u3000\u3000\u3000\u7f2a\u6587\u6697\u6697\u597d\u7b11\uff0c\u4ed8\u9053\uff1a\u201c\u770b\u6765\u4ed6\u4eec\u5e08\u5144\u5f1f\u51e0\u4eba\uff0c\u90fd\u5bf9\u742a\u59b9\u6000\u7740\u540c\u6837\u7684\u5fc3\u601d\u3002\u201d \u3000\u3000\u3000\u4ed6\u9762\u5e26\u5fae\u7b11\uff0c\u770b\u7740\u8fd9\u5e08\u5144\u5f1f\u56db\u4eba\u731c\u679a\uff0c\u4f46\u82e5\u8fd9\u5e08\u5144\u5f1f\u56db\u4eba\u770b\u51fa\u4ed6\u7b11\u5bb9\u540e\u7684\u542b\u610f\u3000\uff0c\u6050 \u6015\u8c01\u4e5f\u4e0d\u613f\u610f\u8ba8\u53d6\u8fd9\u4ef7\u201c\u7f8e\u5dee\u201d\u4e86\u3002 \u3000\u3000\u3000\u6700\u540e\uff0c\u90a3\u9762\u8c8c\u767d\u7699\u7684\u5c11\u5e74\u662f\u201c\u5e78\u8fd0\u8005\u201d\uff0c\u5176\u4f59\u4e09\u4eba\u90fd\u600f\u600f\u8d70\u4e86\uff0c\u7f2a\u6587\u542b\u7b11\u8d70\u8fc7\u3000\u7b54\u6c55 \u9053\uff1a\u201c\u5144\u53f0\u9ad8\u59d3\uff1f\u201d\u90a3\u9762\u8c8c\u767d\u7699\u7684\u5c11\u5e74\u53cc\u76ee\u4e00\u7ffb\uff0c\u50b2\u7136\u7b54\u9053\uff1a\u201c\u5c0f\u5f1f\u5b54\u5e0c\uff0c\u4e0d\u3000\u8fc7\u6c5f\u6e56\u4e2d\u4eba \u90fd\u79f0\u6211\u4e3a\u2018\u7389\u58c1\u4f7f\u8005\u2019\u2026\u2026\u8bdd\u672a\u8bf4\u5b8c\uff0c\u5c31\u56de\u8fc7\u5934\u53bb\u5411\u6bdb\u6587\u742a\u8bf4\u8bdd\uff0c\u7acb\u65f6\u3000\u53c8\u6362\u4e86\u53e6\u4e00\u79cd\u8138 \u8272\u3002\u7f2a\u6587\u5374\u4e1d\u6beb\u4e0d\u4ee5\u4e3a\u6775\uff0c\u4ecd\u7136\u7b11\u563b\u563b\u7684\uff0c\u6bdb\u6587\u742a\u561f\u7740\u5634\uff0c\u6068\u4e0d\u5f97\u3000\u53eb\u8fd9\u4f4d\u201d\u7389\u58c1\u4f7f\u8005\u201d\u5feb \u4e9b\u6eda\u5f00\u624d\u5bf9\u5fc3\u601d\uff0c\u53ea\u662f\u7709\u68a2\u773c\u89d2\u779f\u5411\u7f2a\u6587\u65f6\uff0c\u5374\u4ecd\u5e26\u7740\u4e00\u4efd\u3000\u7b11\u610f\u3002 \u3000\u3000\u3000\u5b54\u5e0c\u4e0d\u662f\u50bb\u5b50\uff0c\u4e00\u8def\u4e0a\u4ece\u6bdb\u6587\u742a\u90a3\u91cc\u53d7\u6765\u7684\u6028\u6c14\uff0c\u5c31\u5168\u90e8\u53d1\u6cc4\u5728\u624b\u65e0\u7f1a\u9e21\u4e4b\u529b\u3000\u7684\u7f2a \u6587\u8eab\u4e0a\u3002 \u3000\u3000\u3000\u7f2a\u6587\u5374\u4ecd\u4e0d\u95fb\u4e0d\u95ee\uff0c\u50cf\u662f\u6839\u672c\u6ca1\u6709\u542c\u5230\u4ed6\u7684\u8bdd\uff0c\u6bdb\u6587\u742a\u4ee5\u524d\u90a3\u79cd\u72c2\u6001\uff0c\u6b64\u65f6\u7adf\u3000\u6536\u655b \u5f97\u65e0\u5f71\u65e0\u8e2a\uff0c\u7adf\u50cf\u4e2a\u542b\u7f9e\u7b54\u7b54\u7684\u6df1\u95fa\u5973\u5b50\uff0c\u662f\u4ec0\u4e48\u4e1c\u897f\u4f7f\u5f97\u8fd9\u4ece\u6765\u4e0d\u77e5\u9053\u7f9e\u6da9\u3000\u7684\u5c11\u5973\u6709\u4e86 \u8fd9\u4e48\u5927\u7684\u6539\u53d8\u5462\uff1f \u3000\u3000\u3000\u5230\u4e86\u5434\u5174\uff0c\u5e97\u623f\u4e0d\u591a\uff0c\u7f2a\u6587\u53ea\u5f97\u548c\u201c\u7389\u58c1\u4f7f\u8005\u201d\u4e00\u8d77\u6b47\u4e86\u3002 \u3000\u3000\u3000\u6df1\u591c\uff0c\u7389\u58c1\u4f7f\u8005\u5b54\u5e0c\u7a81\u5730\u542c\u5230\u7a97\u5916\u6709\u591c\u884c\u4eba\u5f39\u6307\u7684\u58f0\u97f3\uff0c\u4ed6\u4e45\u8d70\u6c5f\u6e56\uff0c\u53cd\u5e94\u6781\u3000\u5feb\uff0c \u55d6\u5730\uff0c\u8df3\u4e0b\u4e86\u5e8a\uff0c\u767b\u4e0a\u8584\u5e95\u9774\uff0c\u5374\u89c1\u7f2a\u6587\u8499\u7740\u5934\uff0c\u6b63\u5728\u5927\u7761\u3002\u4ed6\u51b7\u7b11\u4e00\u58f0\uff0c\u6697\u3000\u9a82\uff1a\u201c\u8822 \u7269\uff01\uff0c\u2019\u8eab\u5f62\u4e00\u5f13\uff0c\u500f\u7136\u7a7f\u7a97\u800c\u51fa\uff0c\u60f3\u770b\u770b\u7a97\u5916\u7a76\u7adf\u6709\u4ec0\u4e48\u4e8b\u3002\u524d\u9762\uff0c\u679c\u3000\u7136\u6709\u4eba\u5f71\u4e00\u6643\uff0c \u4f46\u8eab\u624b\u5374\u662f\u6781\u4e3a\u8fdf\u949d\uff0c\u5b54\u5e0c\u53c8\u51b7\u7b11\u4e00\u58f0\u731b\u4e00\u957f\u8eab\uff0c\u4e00\u4e2a\u8d77\u843d\uff0c\u4fbf\u63a0\u3000\u5411\u90a3\u9b3c\u795f\u7684\u9ed1\u5f71\u3002\u6bdb\u6587 \u742a\u4e5f\u60ca\u9192\u5f97\u5f88\uff0c\u4e5f\u53d1\u89c9\u4e86\u7a97\u5916\u4f3c\u6709\u5f02\u58f0\uff0c\u5306\u5306\u7ed3\u675f\u4e86\u4e00\u4e0b\u8863\u3000\u886b\uff0c\u7136\u540e\u4e5f\u7a7f\u7a97\u800c\u51fa\uff0c\u4f46\u7a97\u5916 \u5374\u4f3c\u9759\u6084\u6084\u5730\uff0c\u6ca1\u6709\u4eba\u5f71\u3002\u5979\u5fae\u4e00\u8fdf\u7591\uff0c\u7adf\u6beb\u4e0d\u8fdf\u7591\u3000\u5730\u63a0\u4e86\u8fc7\u53bb\u3002\u591c\u8272\u6df1\u6d53\uff0c\u90bb\u623f\u91cc\u6709\u72ac \u5420\u4e4b\u58f0\uff0c\u4e0d\u77e5\u662f\u5b83\u4e5f\u53d1\u89c9\u4e86\u591c\u884c\u4eba\uff0c\u6291\u6216\u662f\u4e0d\u3000\u8010\u6625\u591c\u7684\u5bc2\u5bde\uff0c\u50cf\u6625\u65e5\u7684\u91ce\u732b\u4e00\u6837\u5730\u53eb\u4e86\u8d77 \u6765\u3002\u6bdb\u6587\u742a\u4e0d\u6562\u592a\u5927\u610f\uff0c\u4e5f\u6ca1\u6709\u51fa\u58f0\uff0c\u3000\u8eab\u5f62\u4e00\u62f3\uff0c\u5728\u767d\u6768\u6811\u524d\u500f\u7136\u987f\u4f4f\uff0c\u95ea\u76ee\u4e00\u671b\uff0c\u89c1\u4e00 \u4eba\u5f71\u4f3c\u4e4e\u6311\u6218\u4f3c\u7684\uff0c\u52a8\u4e5f\u4e0d\u52a8\u5730\u3000\u7ad9\u5728\u767d\u6768\u6811\u4e0a\uff0c\u5979\u53cc\u7738\u6012\u5f20\uff0c\u53e3\u4e2d\u4f4e\u53f1\u4e00\u58f0\uff0c\u4e09\u70b9\u5bd2\u661f\u7535 \u5c04\u800c\u51fa\u3002\u54ea\u77e5\u90a3\u4eba\u5f71\u4ecd\u7136\u3000\u4e0d\u52a8\uff0c\u6bdb\u6587\u742a\u7684\u4e09\u679a\u201d\u5c60\u9f99\u9488\u201d\uff0c\u7adf\u90fd\u6253\u5230\u4ed6\u8eab\u4e0a\uff0c\u6bdb\u6587\u742a\u6697\u5668 \u594f\u529f\uff0c\u5374\u89c1\u4eba\u5f71\u4ecd\u3000\u76f4\u633a\u633a\u5730\u7ad9\u7740\uff0c\u975e\u4f46\u52a8\u4e5f\u4e0d\u52a8\uff0c\u5c31\u8fde\u54fc\u58f0\u90fd\u6ca1\u6709\u53d1\u51fa\uff0c\u50cf\u662f\u8fd9\u201c\u5c60\u9f99\u4ed9 \u5b50\u201d\u7684\u7edd\u6280\u3000\uff0c\u6b66\u6797\u4e2d\u626c\u540d\u7684\u201c\u5c60\u9f99\u9488\u201d\u5bf9\u4ed6\u6beb\u65e0\u4f5c\u7528\u4e00\u6837\u3002 \u3000\u3000\u3000\u6bdb\u6587\u742a\u4e00\u60ca\uff0c\u500f\u7136\u62bd\u51fa\u957f\u5251\uff0c\u706b\u7130\u822c\u7684\u7ea2\u5149\u4e00\u95ea\uff0c\u6bdb\u6587\u742a\u5374\u4e0d\u7981\u60ca\u547c\u51fa\u6765\u3002 \u3000\u3000\u3000\u539f\u6765\u7ea2\u5149\u95ea\u5904\uff0c\u5979\u53d1\u73b0\u6811\u4e0a\u7684\u4eba\u5f71\uff0c\u7adf\u662f\u90a3\u7389\u58c1\u4f7f\u8005\u5b54\u5e0c\uff0c\u5979\u5251\u52bf\u4e00\u9886\uff0c\u8eab\u968f\u3000\u5251 \u8d70\uff0c\u5fae\u4e00\u7eb5\u8eab\uff0c\u4e5f\u7a9c\u5230\u767d\u6768\u6811\u4e0a\u85c9\u7740\u5251\u5149\u548c\u661f\u5149\u4e00\u770b\uff0c\u7c89\u9762\u518d\u4e5f\u9547\u9759\u4e0d\u4e86\uff0c\u7acb\u65f6\u3000\u53d8\u5f97\u60e8 \u767d\u3002 \u3000\u3000\u3000\u539f\u6765\u8fd9\u7389\u58c1\u4f7f\u8005\u5b54\u5e0c\uff0c\u7adf\u5728\u4e00\u6bb5\u6781\u77ed\u7684\u65f6\u95f4\u4e2d\uff0c\u5df2\u88ab\u4eba\u70b9\u4e2d\u8111\u540e\u6b7b\u7a74\u2014\u2014\u7389\u6795\u3000\uff0c\u7528 \u7ec6\u94c1\u4e1d\u540a\u5728\u6811\u4e0a\uff0c\u800c\u6bdb\u6587\u742a\u7684\u4e09\u679a\u201c\u5c60\u9f99\u9488\u201d\uff0c\u4e5f\u6574\u6574\u9f50\u9f50\u5730\u63d2\u5728\u4ed6\u524d\u80f8\u7684\u201c\u3000\u4e73\u6cc9\u201d\uff0c \u201c\u671f\u95e8\u201d\u4e24\u5904\u5927\u7a74\u4e0a\uff0c\u53ea\u5269\u4e0b\u9488\u7684\u5c3e\u7aef\uff0c\u5728\u9ed1\u6697\u4e2d\u95ea\u95ea\u53d1\u5149\u3002 \u3000\u3000\u3000\u591c\u8272\uff0c\u4f7f\u5f97\u4ed6\u767d\u7699\u7684\u8138\uff0c\u94c1\u9752\u800c\u72f0\u72de\uff0c\u773c\u73e0\u65e0\u52a9\u5730\u7a81\u51fa\u7736\u5916\uff0c\u50cf\u662f\u4ed6\u81ea\u5df1\u5bf9\u81ea\u3000\u5df1\u7684 \u6b7b\uff0c\u4e5f\u50cf\u522b\u4eba\u4e00\u6837\u5730\u832b\u65e0\u6240\u77e5\u3002 \u3000\u3000\u3000\u6709\u98ce\u5439\u8fc7\uff0c\u6bdb\u6587\u742a\u673a\u4f36\u4f36\u6253\u4e86\u4e2a\u51b7\u6218\uff0c\u56de\u8fc7\u5934\uff0c\u4e0d\u6562\u518d\u770b\u8fd9\u5e45\u666f\u8c61\uff0c\u76f4\u5230\u73b0\u5728\u3000\uff0c\u5979 \u624d\u53d1\u73b0\u81ea\u5df1\u662f\u4e2a\u5973\u5b50\uff0c\u6709\u8bb8\u591a\u4e8b\uff0c\u7684\u786e\u4e0d\u662f\u5979\u72ec\u81ea\u80fd\u591f\u5e94\u4ed8\u7684\uff0c\u5c24\u5176\u662f\u6709\u5173\u6b7b\u3000\u4ea1\u8fd9\uff1d\u7c7b\u7684 \u4e8b\u3002 \u3000\u3000\u3000\u7a81\u5730\uff0c\u5979\u60f3\u8d77\u7f2a\u6587\uff0c\u5fc3\u4e2d\u4e0d\u7981\u53c8\u8d77\u4e86\u4e00\u9635\u5bd2\u610f\uff0c\u500f\u7136\u56de\u8eab\uff0c\u5411\u5ba2\u5e97\u90a3\u8fb9\u63a0\u53bb\uff0c\u3000\u201c\u4ed6 \u4f1a\u4e0d\u4f1a\u4e5f\u2026\u2026\u201d\u5979\u5fc3\u4e27\u9b42\u843d\u4e86\u3002 \u3000\u3000\u3000\u6697\u4e2d\u8fd9\u9b3c\u9b45\u822c\u7684\u4eba\u7269\uff0c\u50cf\u662f\u5730\u72f1\u4e2d\u7684\u6076\u9b54\u4f3c\u7684\uff0c\u968f\u65f6\u4f38\u51fa\u4ed6\u7684\u9b54\u638c\uff0c\u652b\u53bb\u4e16\u4e0a\u3000\u7684\u4e00 \u4e9b\u4eba\uff0c\u800c\u8fd9\u4e9b\u4eba\uff0c\u53c8\u90fd\u662f\u548c\u7075\u86c7\u6bdb\u81ec\u6709\u7740\u5173\u7cfb\u7684\u3002 \u3000\u3000\u3000\u6bdb\u6587\u742a\u5fc3\u4e2d\u6df7\u9968\uff0c\u604d\u60da\uff0c\u5fc3\u667a\u5728\u8fd9\u4e00\u5239\u90a3\u4e2d\uff0c\u4f3c\u4e4e\u90fd\u5b8c\u5168\u5931\u53bb\u4e86\u3002 \u3000\u3000\u3000\u201c\u8fd9\u4f1a\u662f\u8c01\u5462\uff1f\u201d\u5979\u6697\u8ba8\u7740\uff1a\u201c\u91d1\u5251\u4fa0\uff1f\u90a3\u8499\u7740\u9ed1\u5e03\u7684\u591c\u884c\u4eba\uff1f\u201d \u3000\u3000\u3000\u661f\u5149\u5c06\u4e00\u68f5\u6811\u7684\u5f71\u5b50\uff0c\u53d8\u5f97\u5947\u5f62\u800c\u626d\u66f2\uff0c\u5c31\u50cf\u9b3c\u9b45\u4f3c\u7684\uff0c\u6321\u5728\u6bdb\u6587\u742a\u524d\u9762\uff0c\u6bdb\u3000\u6587\u742a \u53c8\u4e0d\u7981\u8d77\u4e86\u4e00\u9635\u60ca\u6817\uff0c\u51b7\u6c57\u90fd\u6d41\u4e0b\u6765\u4e86\u3002 \u3000\u3000\u3000\u201c\u96be\u9053\u662f\u575f\u5893\u4e2d\u7684\u4eba\uff0c\u7a81\u7136\u590d\u6d3b\uff0c\u800c\u6765\u590d\u4ec7\u4e86\u5417\uff1f\u201d\u5979\u4e0d\u6562\u518d\u5f80\u4e0b\u60f3\uff0c\u4e5f\u4e0d\u6562\u3000\u5411\u81ea \u5df1\u89e3\u91ca\u81ea\u5df1\u8fd9\u79cd\u6050\u60e7\u7684\u7531\u6765\uff0c\u8111\u6d77\u4e2d\u6ce2\u6d9b\u4e91\u6d8c\uff0c\u5979\u867d\u7136\u4e0d\u77e5\u8be5\u600e\u4e48\u60f3\uff0c\u7136\u800c\u7f2a\u3000\u6587\u7684\u5f71\u4e8e\uff0c \u5374\u50cf\u5c71\u77f3\u4f3c\u7684\uff0c\u5728\u5979\u8111\u6d77\u4e2d\u7684\u6ce2\u6d9b\u91cc\u5c79\u7acb\u7740\u3002 \u3000\u3000\u3000\u4e8e\u662f\u5979\u98de\u5feb\u5730\u51e0\u4e2a\u7eb5\u8eab\uff0c\u63a0\u5411\u90a3\u4e5f\u6c89\u4e8e\u9634\u5f71\u4e2d\u7684\u5ba2\u5e97\u623f\u5c4b\u3002 \u3000\u3000\u3000\u4f55\u6d88\u51e0\u4e2a\u8d77\u843d\uff0c\u5979\u5df2\u8dc3\u5165\u5ba2\u5e97\u4e2d\uff0c\u5fae\u4e00\u5ba1\u5ea6\uff0c\u53d1\u73b0\u7f2a\u6587\u7684\u4f4f\u623f\u7684\u7a97\u5b50\uff0c\u4ecd\u7136\u662f\u3000\u655d\u5f00 \u7740\u7684\u3002 \u3000\u3000\u3000\u5979\u6beb\u4e0d\u8003\u8651\u5730\u4e00\u8dc3\u800c\u5165\uff0c\u7f2a\u6587\u6839\u672c\u6beb\u65e0\u6240\u89c9\uff0c\u4ecd\u5728\u8499\u5934\u5927\u7761\uff0c\u5979\u6025\u5fd9\u8d70\u8fc7\u53bb\uff0c\u3000\u4f38\u624b \u62cd\u4e86\u62cd\u88ab\uff0c\u54ea\u77e5\u89e6\u624b\u4e4b\u5904\uff0c\u5374\u4e0d\u4f3c\u4eba\u4f53\u3002 \u3000\u3000\u3000\u5979\u53c8\u4e00\u60ca\uff0c\u62c9\u5f00\u88ab\uff0c\u91cc\u9762\u53ea\u5806\u7740\u4e00\u5377\u68c9\u88ab\u800c\u5df2\uff0c\u54ea\u91cc\u6709\u7f2a\u6587\u7684\u5f71\u5b50\uff1f \u3000\u3000\u3000\u5979\u6014\u5728\u5e8a\u524d\u4e86\uff0c\u7591\u5ff5\u4e1b\u751f\uff0c\u5374\u542c\u5230\u5e8a\u6846\u540e\u6709\u4eba\u8f7b\u8f7b\u95ee\u9053\uff1a\u201c\u662f\u6bdb\u6587\u742a\u59d1\u5a18\u5417\uff1f\u3000\u201d\u6bdb \u6587\u742a\u811a\u8ddf\u4e00\u8f6c\uff0c\u63a0\u5230\u67dc\u540e\uff0c\u5374\u89c1\u7f2a\u6587\u754f\u7f29\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u770b\u89c1\u6bdb\u6587\u742a\uff0c\u6ee1\u6000\u60ca\u60e7\u3000\u7684\u5fc3\u624d\u677e\u9a70 \u4e86\u4e0b\u6765\u3002 \u3000\u3000\u3000\u4ed6\u4eff\u4f5b\u518d\u4e5f\u652f\u6301\u4e0d\u4f4f\u4e86\uff0c\u865a\u8f6f\u5730\u5012\u5728\u8863\u67dc\u65c1\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u4f60\u518d\u4e0d\u6765\uff0c\u6211\u53ef\u8981\u5413\u3000\u6b7b \u4e86\u3002\u201d\u4ed6\u6218\u5162\u7740\u4f4f\u5899\u4e0a\u4e00\u6307\uff0c\u6bdb\u6587\u742a\u968f\u7740\u671b\u53bb\uff0c\u5374\u89c1\u767d\u57a9\u5899\u4e0a\uff0c\u6b64\u523b\u591a\u4e86\u4e00\u65b9\u9ed1\u3000\u7f0e\uff0c\u85c9\u7740 \u5fae\u5f31\u7684\u5149\u7ebf\uff0c\u90a3\u4e0a\u9762\u4ecd\u53ef\u770b\u5230\u56db\u4e2a\u5b57\uff0c\u8d6b\u7136\u7adf\u662f\u201c\u4ee5\u8840\u8fd8\u8840\u3002\u201d \u3000\u3000\u3000\u6bdb\u6587\u742a\u5fc3\u5934\u53c8\u4e00\u9707\uff0c\u5341\u516d\u5e74\u524d\u7684\u6545\u4e8b\uff0c\u5979\u4e5f\u66fe\u542c\u5230\u8fc7\uff0c\u8fd9\u201c\u4ee5\u8840\u8fd8\u8840\u201d\u56db\u5b57\uff0c\u3000\u4e5f\u4f7f \u5979\u4eba\u76ee\u60ca\u5fc3\uff0c\u80cc\u810a\u53c8\u751f\u51fa\u4e00\u4e1d\u51c9\u610f\u3002 \u3000\u3000\u3000\u7f2a\u6587\u53c8\u98a4\u6296\u7740\u8bf4\u9053\uff1a\u201c\u521a\u521a\u6211\u7761\u5f97\u6b63\u719f\uff0c\u5ffd\u7136\u7a97\u53e3\u8dc3\u8fdb\u4e2a\u4eba\u6765\uff0c\u5c06\u8fd9\u5757\u9ed1\u7f0e\u5b50\u3000\uff0c\u6302 \u5728\u5899\u4e0a\uff0c\u53c8\u628a\u6211\u53eb\u9192\u4e86\uff0c\u95ee\u6e05\u4e86\u6211\u662f\u4ec0\u4e48\u4eba\uff0c\u624d\u53c8\u4ece\u7a97\u53e3\u8d70\u4e86\u3002\u201d \u3000\u3000\u3000\u6bdb\u6587\u742a\u957f\u53f9\u4e00\u58f0\uff0c\u95ee\u9053\uff1a\u201c\u90a3\u4eba\u662f\u4ec0\u4e48\u6837\u5b50\uff1f\u662f\u4e0d\u662f\u5168\u8eab\u7a7f\u7740\u9ed1\u8863\uff0c\u8fde\u5934\u4e0a\u90fd\u3000\u8499\u7740 \u9ed1\u5e03\u7684\uff1f\u201d \u3000\u3000\u3000\u7f2a\u6587\u70b9\u5934\u9053\uff1a\u201c\u5c31\u662f\u8fd9\u6837\u7684\u4eba\u3002\u201d\u8bed\u58f0\u4e00\u987f\uff0c\u53c8\u9053\uff0c\u201c\u539f\u6765\u59d1\u5a18\u8ba4\u5f97\u4ed6\u7684\u3002\u201d \u3000\u3000\u3000\u6bdb\u6587\u742a\u6447\u4e86\u6447\u5934\uff0c\u671b\u7740\u5899\u4e0a\u7684\u90a3\u56db\u4e2a\u5b57\u51fa\u795e\uff0c\u7f2a\u6587\u6276\u7740\u8863\u67dc\u8d70\u8fc7\u6765\uff0c\u671b\u7740\u5979\u7684\u3000\u80cc \u5f71\uff0c\u8138\u4e0a\u5374\u65e0\u4ed6\u6240\u8bf4\u7684\u534a\u70b9\u60ca\u60e7\u4e4b\u8272\u3002 \u3000\u3000\u3000\u4f46\u6bdb\u6587\u742a\u4e00\u56de\u5934\uff0c\u4ed6\u8138\u4e0a\u7684\u808c\u8089\u53c8\u50cf\u662f\u56e0\u7740\u60ca\u60e7\u800c\u626d\u66f2\u4e86\u8d77\u6765\uff0c\u6bdb\u6587\u742a\u601c\u60dc\u5730\u3000\u671b\u7740 \u8fd9\u6587\u8d28\u5f6c\u5f6c\u7684\u7f8e\u5c11\u5e74\uff0c\u6084\u6084\u8d70\u8fc7\u53bb\uff0c\u9053\uff1a\u201c\u4f60\u522b\u6015\uff0c\u6211\u5728\u8fd9\u91cc\u966a\u7740\u4f60\u597d\u4e86\u3002\u201d\u3000\u8bdd\u4e00\u51fa\u53e3\uff0c \u8138\u4e0a\u4e0d\u7981\u5c31\u7ea2\u4e86\u8d77\u6765\u3002\u7f2a\u6587\u5374\u8fde\u58f0\u559c\u9053\uff1a\u201c\u6709\u59d1\u5a18\u5728\u8fd9\u91cc\u966a\u7740\u6211\uff0c\u90a3\u597d\u3000\u6781\u4e86\uff0c\u4e0d\u7136\u4e00\u201d\u4e0d \u7136\u600e\u4e48\u6837\uff0c\u4ed6\u867d\u672a\u8bf4\u4e0b\u53bb\uff0c\u4f46\u6bdb\u6587\u742a\u5374\u5df2\u66ff\u81ea\u5df1\u627e\u5230\u4e86\u7559\u5728\u8fd9\u623f\u3000\u91cc\u7684\u7406\u7531\u4e86\u3002 \u3000\u3000\u3000\u70b9\u4eae\u4e86\u6cb9\u706f\uff0c\u4ed6\u4eec\u7aef\u5750\u5728\u81ec\u5b50\u7684\u4e24\u4fa7\uff0c\u6bdb\u6587\u742a\u53ea\u89c9\u5f97\u7f2a\u6587\u7684\u53cc\u7738\uff0c\u50cf\u662f\u706b\u4e00\u6837\u3000\u5730\u71c3 \u70e7\u7740\u81ea\u5df1\u7684\u5fc3\uff0c\u81ea\u5df1\u7684\u5fc3\u4e5f\u5f00\u59cb\u71c3\u70e7\u4e86\u3002 \u3000\u3000\u3000\u4e8e\u662f\uff0c\u5979\u8bb0\u8d77\u8fd9\u662f\u6625\u591c\u2014\u2014\u867d\u7136\u6625\u591c\u7684\u661f\u5149\uff0c\u6625\u591c\u7684\u6c14\u606f\uff0c\u4ee5\u53ca\u5c4b\u9876\u732b\u513f\u7684\u5636\u3000\u53eb\uff0c \u90fd\u6ca1\u6709\u5e26\u7ed9\u5979\u201c\u6625\u201d\u7684\u611f\u89c9\uff0c\u7136\u800c\u7f2a\u6587\u7684\u773c\u775b\u5374\u544a\u8bc9\u5979\uff0c\u8fd9\u662f\u6625\u5929\u3002 \u3000\u3000\u3000\u4e5f\u8bb8\u662f\u6625\u5bd2\u6599\u5ced\u5427\uff01\u4ed6\u4eec\u7684\u624b\uff0c\u4e0d\u77e5\u5728\u4ec0\u4e48\u65f6\u5019\u7d27\u63e1\u4f4f\u4e86\u3002 \u3000\u3000\u3000\u4e8e\u662f\u4ece\u6df1\u591c\u5230\u5929\u660e\uff0c\u4ed6\u4eec\u5c31\u8fd9\u6837\u5750\u7740\uff0c\u6bdb\u6587\u742a\u5fd8\u8bb0\u4e86\u4e00\u5207\uff0c\u751a\u81f3\u5fd8\u8bb0\u90a3\u5916\u9762\u7684\u3000\u767d\u6768 \u6811\u4e0a\uff0c\u4ecd\u6302\u7740\u5979\u5e08\u5144\u60e8\u4e0d\u5fcd\u7779\u7684\u5c38\u8eab\u3002 \u3000\u3000\u3000\u7136\u800c\u7f2a\u6587\u5462\uff1f\u4ed6\u4e5f\u5fd8\u53bb\u4e86\u4e00\u5207\u5417\uff1f\u8fd9\u4ece\u4ed6\u5634\u89d2\u7684\u7b11\u5bb9\u4e0a\uff0c\u4f60\u53ef\u4ee5\u5f97\u5230\u660e\u786e\u7684\u7b54\u3000\u8986\uff0c \u53ea\u662f\u6b64\u523b\u7684\u6bdb\u6587\u742a\u5df2\u4e0d\u80fd\u6ce8\u610f\u5230\u4e86\u3002 \u3000\u3000\u3000\u7b2c\u4e8c\u5929\u65e9\u4e0a\uff0c\u5434\u5174\u5e9c\u7684\u6355\u600f\u5fd9\u788c\u4e86\uff0c\u4e09\u73ed\u73ed\u5934\u94c1\u5c3a\u738b\u7ef4\u6770\uff0c\u88ab\u8fd9\u5177\u65e0\u540d\u7537\u5c38\u6240\u3000\u56f0 \u60d1\uff0c\u800c\u8fd9\u5177\u5c38\u8eab\u4e0a\u7684\u91d1\u8272\u8863\u886b\uff0c\u53c8\u4f7f\u4ed6\u60ca\u6050\u3002 \u3000\u3000\u3000\u4f46\u662f\u8fd9\u4e00\u5207\u90fd\u662f\u4e2a\u8c1c\uff0c\u975e\u5230\u8c1c\u5e95\u63ed\u6653\u7684\u90a3\u4e00\u5929\uff0c\u6ca1\u6709\u4eba\u80fd\u77e5\u9053\u771f\u8c61\u3002 \u3000\u3000\u3000\u8fc7\u592a\u6e56\u4e09\u4e07\u516d\u5343\u9877\uff0c\u7f2a\u6587\u548c\u6bdb\u6587\u742a\u6307\u70b9\u7740\u6d69\u7ff0\u70df\u6ce2\uff0c\u522b\u4eba\u8c01\u4e0d\u7fa1\u6155\u8fd9\u4e00\u5bf9\u624d\u5b50\u3000\u4f73 \u4eba\uff0c\u4f46\u4e16\u4e0a\u4e4b\u4e8b\uff0c\u5176\u5185\u5bb9\u6709\u8bb8\u591a\u662f\u4efb\u4f55\u4eba\u4e5f\u65e0\u6cd5\u4ece\u8868\u9762\u4e0a\u770b\u51fa\u6765\u7684\uff0c\u7f2a\u6587\u548c\u6bdb\u6587\u3000\u742a\u8fd9\u4e00 \u5bf9\uff0c\u4e5f\u8bb8\u6b63\u662f\u5982\u6b64\u3002 \u3000\u3000\u3000\u4f46\u65e0\u8bba\u5982\u4f55\uff0c\u8fd9\u4e00\u5bf9\u65e0\u8bba\u4ece\u4ec0\u4e48\u5730\u65b9\u770b\u53bb\u90fd\u6781\u5176\u914d\u5408\u7684\u5c11\u5e74\u7537\u5973\uff0c\u8fd9\u4e00\u8def\u4e0a\u8033\u3000\u9b13\u53ae \u78e8\uff0c\u5f53\u7136\u96be\u514d\u6697\u751f\u60c5\u612b\uff0c\u5c24\u5176\u662f\u6bdb\u6587\u742a\uff0c\u5979\u4e0d\u4f46\u53d8\u5f97\u6e29\u67d4\uff0c\u542b\u7f9e\uff0c\u800c\u4e14\u5c06\u5973\u5b50\u3000\u7167\u6599\u7537\u5b50\u7684 \u672c\u80fd\uff0c\u90fd\u7528\u5728\u7f2a\u6587\u8eab\u4e0a\uff0c\u4f7f\u5f97\u4ed6\u7b2c\u4e00\u6b21\u4eab\u53d7\u5230\u5f02\u6027\u7684\u6e29\u99a8\u3002 \u3000\u3000\u3000\u81ea\u6b64\u4ee5\u540e\uff0c\u6bdb\u6587\u742a\u90a3\u6d01\u767d\u5982\u7eb8\u7684\u5fc3\u7075\uff0c\u4fbf\u8ba9\u7f2a\u6587\u7ed9\u5199\u4e86\u5de8\u5927\u800c\u6df1\u900f\u7684\u4e00\u4e2a\u201c\u60c5\u3000\u201d \u5b57\u3002\u800c\u4efb\u4f55\u4eba\u90fd\u77e5\u9053\uff0c\u5c11\u5973\u7684\u7b2c\u4e00\u6b21\u52a8\u60c5\uff0c\u6c38\u8fdc\u662f\u6700\u7eaf\u771f\u548c\u7f8e\u4e3d\u7684\uff0c\u5f53\u7136\uff0c\u4e5f\u662f\u3000\u6c38\u96be\u5fd8\u6000 \u7684\u3002 \u3000\u3000\u3000\u5b54\u5e0c\u7684\u60e8\u6b7b\uff0c\u867d\u7136\u8ba9\u6bdb\u6587\u742a\u611f\u5230\u60b2\u54c0\u4e00\u4e00\u56e0\u4e3a\u4ed6\u7ec8\u7a76\u662f\u66fe\u548c\u5979\u81ea\u5e7c\u76f8\u5904\u7684\u540c\u4f34\u3000\uff0c\u90a3 \u5899\u4e0a\u89e6\u76ee\u60ca\u5fc3\u7684\u56db\u4e2a\u5b57\uff0c\u4e5f\u8ba9\u5979\u611f\u5230\u6050\u60e7\u3002 \u3000\u3000\u3000\u2014\u2014\u56e0\u4e3a\u5979\u81ea\u5e7c\u5c31\u4e0d\u65ad\u542c\u5230\u6709\u5173\u8fd9\u56db\u4e2a\u5b57\u7684\u6545\u4e8b\u3002 \u3000\u3000\u3000\u4f46\u662f\uff0c\u8fd9\u4efd\u60b2\u54c0\u548c\u6050\u60e7\uff0c\u5df2\u65e0\u6cd5\u518d\u5728\u5979\u5fc3\u4e2d\u5360\u5f97\u4e00\u4e9b\u4f4d\u7f6e\uff0c\u56e0\u4e3a\u5979\u6574\u4e2a\u7684\u5904\u5b50\u3000\u82b3 \u5fc3\uff0c\u5df2\u5168\u88ab\u90a3\u201c\u60c5\u201d\u5b57\u5360\u5f97\u6ee1\u6ee1\u7684\u4e86\u3002 \u3000\u3000\u3000\u7f2a\u6587\u5f53\u7136\u4e5f\u80fd\u53d1\u89c9\u8fd9\u201c\u60c5\u201d\u5b57\u5728\u5979\u5fc3\u4e2d\u6240\u9020\u6210\u7684\u529b\u91cf\u4e00\u90a3\u4ece\u6bdb\u6587\u742a\u65e5\u76ca\u6e29\u67d4\u7684\u3000\u4e3e\u6b62 \u548c\u8a00\u8bcd\u4e0a\uff0c\u5c31\u53ef\u4ee5\u53d1\u89c9\u3002 \u3000\u3000\u3000\u4f46\u662f\uff0c\u4ed6\u4ecd\u50cf\u5f80\u5e38\u4e00\u6837\uff0c\u6c38\u8fdc\u5e26\u7740\u90a3\u4e00\u4efd\u8c1c\u4e00\u6837\u7684\u7b11\u5bb9\uff0c\u8ba9\u4eba\u6c38\u8fdc\u65e0\u6cd5\u4ece\u90a3\u4fca\u3000\u7f8e\u800c \u633a\u9038\u7684\u5916\u8868\u4e2d\uff0c\u731c\u900f\u4ed6\u7684\u5fc3\u4e8b\u3002 \u3000\u3000\u3000\u4ed6\uff0c\u662f\u4e2a\u8c1c\u4e00\u6837\u7684\u4eba\u7269\u3002 \u3000\u3000\u3000\u53ea\u662f\u6bdb\u6587\u742a\u5374\u4e1d\u6beb\u611f\u89c9\u4e0d\u5230\uff0c\u4e00\u8def\u4e0a\uff0c\u5979\u50cf\u5b88\u62a4\u795e\u4e00\u6837\u5730\u4fdd\u62a4\u7740\u8fd9\u201c\u624b\u65e0\u7f1a\u9e21\u3000\u4e4b \u529b\u201d\u7684\u4e66\u751f\uff0c\u50cf\u6148\u6bcd\u4e00\u6837\u5730\u7167\u6599\u7740\u4ed6\u7684\u996e\u98df\u8d77\u5c45\uff0c\u53c8\u50cf\u59bb\u5b50\u4e00\u6837\u5730\u548c\u4ed6\u5a13\u5a13\u8c08\u7740\u3000\u60c5\u8bdd\u4e00\u4e98 \u53e4\u4ee5\u6765\uff0c\u76f8\u7231\u7740\u7684\u4eba\u4eec\uff0c\u90fd\u662f\u5728\u540c\u6837\u5730\u8c08\u8bf4\u7740\u7684\u8bdd\u3002\u8fd9\u662f\u4e0d\u53d8\u7684\uff0c\u4e5f\u662f\u6c38\u3000\u6052\u7684\u3002 \u3000\u3000\u3000\u7531\u676d\u5dde\u5317\u4e0a\uff0c\u53ef\u6cbf\u8fd0\u6cb3\u800c\u884c\uff0c\u4e00\u8def\u4e0a\u90fd\u662f\u4eba\u70df\u7a20\u5bc6\u4e4b\u5904\uff0c\u5c24\u5176\u6c5f\u3001\u6d59\u5883\u5185\uff0c\u4eba\u3000\u7269\u98ce \u534e\uff0c\u81ea\u53e4\u4ee5\u6765\uff0c\u5c24\u79f0\u4e2d\u539f\u4e4b\u6700\u3002 \u3000\u3000\u3000\u662f\u4ee5\u4e00\u8def\u4e0a\uff0c\u672c\u6765\u4e5f\u6839\u672c\u4e0d\u53ef\u80fd\u6709\u4ec0\u4e48\u51f6\u6740\u4e4b\u4e8b\u53d1\u751f\uff0c\u53ea\u662f\u201c\u91d1\u5251\u4fa0\u201d\u4e00\u51fa\uff0c\u3000\u8fd9\u672c \u6765\u7d20\u79f0\u5b89\u5b81\u7684\u6c5f\u3001\u6d59\u9053\u4e0a\uff0c\u6b66\u6797\u4eba\u7269\u4fbf\u5448\u73b0\u4e86\u4e00\u79cd\u5174\u594b\u72b6\u6001\uff0c\u8fd9\u539f\u56e0\u5374\u662f\u56e0\u4e3a\u3000\u6b66\u6797\u4e2d\u4e45\u5df2 \u65e0\u4e8b\uff0c\u6b64\u523b\u90a3\u4e9b\u548c\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u7d20\u65e0\u6765\u5f80\uff0c\u4e00\u4e9b\u548c\u201c\u7075\u86c7\u201d\u6709\u7740\u5919\u6028\u7684\u3000\u4eba\uff0c\u4fbf\u62b1\u7740\u201c\u770b\u70ed \u95f9\u201d\u7684\u5e78\u707e\u4e50\u7978\u5fc3\u7406\uff0c\u770b\u7740\u8fd9\u96c4\u8e1e\u6b66\u6797\u591a\u5e74\u7684\u201c\u6bdb\u5927\u592a\u7237\u201d\u5728\u53d7\u3000\u5230\u90a3\u4e48\u591a\u6253\u51fb\u4e4b\u540e\uff0c\u80fd\u6709 \u4ec0\u4e48\u51fa\u5947\u5236\u80dc\u7684\u624b\u6bb5\uff0c\u5bf9\u8fd9\u5982\u795e\u9f99\u822c\u7684\u201c\u91d1\u5251\u4fa0\u201d\u4f5c\u4e00\u53cd\u3000\u51fb\u3002 \u3000\u3000\u3000\u800c\u90a3\u4e9b\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u7684\u515a\u7fbd\uff0c\u4e0d\u7528\u8bf4\uff0c\u66f4\u662f\u7d27\u5f20\u5f97\u5f88\uff0c\u56e0\u4e3a\u4ed6\u4eec\u4e0d\u77e5\u8fd9\u4f4d\u201c\u91d1\u3000\u5251 \u4fa0\u201d\u4ec0\u4e48\u65f6\u5019\u4f1a\u7167\u987e\u5230\u81ea\u5df1\u5934\u4e0a\u6765\u3002 \u3000\u3000\u3000\u6bdb\u6587\u742a\u6765\u5f80\u6b64\u8def\u5df2\u6709\u8bb8\u591a\u6b21\u4e86\uff0c\u8fd9\u8def\u4e0a\u548c\u6bdb\u81ec\u6709\u7740\u5173\u8054\u7684\u6c5f\u6e56\u4eba\u7269\uff0c\u5f53\u7136\u5168\u8ba4\u3000\u8bc6\u8fd9 \u4f4d\u6b66\u6797\u9b41\u9996\u7684\u5973\u516c\u5b50\uff0c\u51e0\u4e4e\u6bcf\u5230\u4e00\u4e2a\u5730\u65b9\uff0c\u53ea\u8981\u6bdb\u6587\u742a\u5728\u95f9\u5e02\u4e0a\u4e00\u9732\u9762\uff0c\u7acb\u523b\u3000\u5c31\u6709\u5f53\u5730\u7684 \u6b66\u6797\u4eba\u7269\u524d\u6765\u62dc\u5019\u3002 \u3000\u3000\u3000\u6bdb\u6587\u742a\u50cf\u662f\u6709\u4e9b\u8ba8\u538c\uff0c\u4f46\u7f2a\u6587\u5374\u50cf\u662f\u5bf9\u8fd9\u4e9b\u5e94\u916c\u6781\u611f\u5174\u8da3\uff0c\u4ed6\u751a\u81f3\u548c\u6bcf\u4e00\u4e2a\u6765\u3000\u62dc\u5019 \u7684\u6b66\u6797\u4eba\u7269\u90fd\u8c08\u5f97\u6765\uff0c\u6ed4\u6ed4\u4e0d\u7edd\u5730\u548c\u90a3\u4e9b\u6c5f\u6e56\u83bd\u6c49\u8c08\u7740\u6d3b\uff0c\u8be6\u7ec6\u5730\u95ee\u4ed6\u4eec\u7684\u59d3\u3000\u540d\uff0c\u4f4f\u5740\u3002 \u3000\u3000\u3000\u6bdb\u6587\u742a\u6709\u4e9b\u5947\u602a\u8fd9\u6587\u8d28\u5f6c\u5f6c\u7684\u5bcc\u5bb6\u516c\u5b50\u4e3a\u4ec0\u4e48\u4f1a\u5bf9\u8fd9\u4e9b\u8349\u83bd\u8c6a\u58eb\u5982\u6b64\u53d1\u751f\u5174\u8da3\u3000\uff0c\u4f46 \u53ea\u8981\u7f2a\u6587\u9ad8\u5174\u7684\uff0c\u5979\u4e5f\u5c31\u9ad8\u5174\u4e86\u3002 \u3000\u3000\u3000\u5230\u4e86\u5bbf\u8fc1\uff0c\u6295\u4e86\u5e97\uff0c\u5929\u5df2\u7ecf\u9ed1\u4e86\uff0c\u521d\u590f\u7684\u665a\u4e0a\uff0c\u6c38\u8fdc\u662f\u7f8e\u7684\uff0c\u6bdb\u6587\u742a\u8f7b\u8f7b\u6253\u5f00\u3000\u7a97 \u5b50\uff0c\u671b\u7740\u7a97\u5916\u7684\u6ee1\u5929\u7e41\u661f\uff0c\u6084\u8bed\u9053\uff1a\u201c\u6211\u4eec\u522b\u51fa\u53bb\u5427\uff0c\u968f\u4fbf\u53eb\u51e0\u6837\u5c0f\u83dc\uff0c\u5c31\u5728\u8fd9\u3000\u91cc\u5403\u4e86\u7b97 \u4e86\u3002\u201d \u3000\u3000\u3000\u7f2a\u6587\u4e00\u7b11\uff0c\u8d70\u8fc7\u53bb\uff0c\u8f7b\u629a\u7740\u5979\u7684\u80a9\uff0c\u8fd8\u672a\u66fe\u8bf4\u8bdd\uff0c\u6bdb\u6587\u742a\u5df2\u7b11\u9053\uff1a\u201c\u4e00\u5b9a\u8981\u51fa\u3000\u53bb\u662f \u4e0d\u662f\uff1f\u201d\u5979\u5a07\u8eaf\u4e00\u626d\uff1a\u201c\u6211\u771f\u5947\u602a\uff0c\u4e3a\u4ec0\u4e48\u4f60\u603b\u662f\u559c\u6b22\u548c\u90a3\u4e9b\u81ed\u7537\u4eba\u6253\u4ea4\u9053\uff0c\u3000\u6211\u4eec\u4e24\u4eba\u9759 \u9759\u5730\u5403\u4e00\u987f\u996d\u591a\u597d\u3002\u201d \u3000\u3000\u3000\u7f2a\u6587\u4ecd\u7136\u4e0d\u8bf4\u8bdd\uff0c\u4f46\u7ed3\u679c\u4e24\u4eba\u4ecd\u7136\u8d70\u4e86\u51fa\u53bb\u3002\u5bbf\u8fc1\u591c\u5e02\uff0c\u867d\u4e0d\u9f0e\u76db\uff0c\u4f46\u8fd9\u5730\u5f53\u3000\u6f5c\u8fd0 \u8981\u51b2\u7684\u57ce\u95f9\u591c\u5e02\u4ecd\u7136\u662f\u8f89\u714c\u7684\u3002 \u3000\u3000\u3000\u51fa\u4e86\u5e97\u95e8\uff0c\u6cbf\u7740\u5357\u8857\u5411\u5de6\u4e00\u8f6c\uff0c\u7f2a\u6587\u7a81\u7136\u773c\u524d\u4e00\u4eae\uff0c\u4fa7\u987e\u6bdb\u6587\u742a\u4e00\u7b11\uff0c\u6bdb\u6587\u742a\u3000\u968f\u773c \u671b\u53bb\uff0c\u4e24\u9053\u6625\u5c71\u4f3c\u7684\u9edb\u7709\uff0c\u5374\u8f7b\u8f7b\u76b1\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u3000\u539f\u6765\u653e\u773c\u671b\u53bb\uff0c\u8fd9\u6761\u8857\u4e0a\u7684\u4eba\uff0c\u8863\u886b\u7adf\u5b8c\u5168\u90fd\u662f\u91d1\u8272\uff0c\u4efb\u4f55\u4e00\u79cd\u522b\u7684\u989c\u8272\u90fd\u6ca1\u3000\u6709\uff0c \u8fd9\u5f53\u7136\u4e0d\u662f\u5de7\u5408\uff0c\u800c\u53ea\u6709\u552f\u4e00\u4e00\u79cd\u539f\u56e0\uff0c\u90a3\u5c31\u662f\u8fd9\u6761\u8857\u4e0a\u6240\u6709\u7684\u4eba\uff0c\u90fd\u662f\u201c\u7075\u3000\u86c7\u201d\u6bdb\u81ec\u7684 \u76f4\u5c5e\u90e8\u5c5e\u3002 \u3000\u3000\u3000\u4e24\u4eba\u65b9\u81ea\u4e92\u89c6\u95f4\uff0c\u7a81\u7136\u4e24\u4e2a\u4e5f\u7a7f\u7740\u91d1\u8272\u7d27\u8eab\u8863\u88e4\u7684\u9880\u957f\u5927\u6c49\uff0c\u5288\u9762\u62e6\u5728\u4ed6\u4eec\u9762\u3000\u524d\uff0c \u5406\u559d\u9053\uff1a\u201c\u8fd9\u6761\u8857\u4eca\u5929\u665a\u4e0a\u5df2\u7ecf\u88ab\u94c1\u624b\u4ed9\u733f\u4faf\u56db\u7237\u501f\u7528\u4e86\uff0c\u4f60\u4eec\u8981\u5403\u996d\u5230\u522b\u7684\u3000\u5730\u65b9\u53bb\uff0c\u8fd9 \u6761\u8857\u4e0a\u6240\u6709\u7684\u996d\u9986\u5b50\u4eca\u5929\u665a\u4e0a\u90fd\u6ca1\u5f97\u7a7a\u3002\u201d \u3000\u3000\u3000\u6bdb\u6587\u742a\u53c8\u4e00\u76b1\u7709\uff0c\u7f2a\u6587\u5374\u54c8\u54c8\u7b11\u7740\uff0c\u5fae\u5fae\u4e00\u6307\u6bdb\u6587\u742a\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u8fd9\u4f4d\u59d1\u5a18\u3000\u662f\u8c01 \u5417\uff1f\u201d\u4ed6\u8bdd\u672a\u8bf4\u5b8c\uff0c\u5c31\u88ab\u6bdb\u6587\u742a\u62d6\u7740\u5c31\u8d70\uff0c\u4e00\u9762\u4f4e\u58f0\u57cb\u6028\u7740\u9053\uff1a\u201c\u4f60\u4f55\u5fc5\u8bf4\u51fa\u3000\u6765\u5462\uff1f\u770b\u6837 \u5b50\u8fd9\u91cc\u6709\u9ebb\u70e6\uff0c\u6211\u53ef\u4e0d\u613f\u60f9\u3002\u201d\u7f2a\u6587\u773c\u73e0\u4e00\u8f6c\uff0c\u5fae\u7b11\u4e86\u4e00\u4e0b\uff0c\u7a81\u7136\u770b\u5230\u3000\u5341\u4f59\u4eba\u8fce\u9762\u800c\u6765\u3002 \u3000\u3000\u3000\u7f2a\u6587\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u56e0\u4e3a\u8fd9\u5341\u4f59\u4eba\u7adf\u90fd\u7a7f\u7740\u767e\u7ed3\u9e26\u8863\uff0c\u663e\u7136\u90fd\u662f\u4e5e\u4e10\u3002\u2018\u54ea\u6709\u3000\u4e5e\u4e10 \u5728\u8def\u4e0a\u6210\u7fa4\u7ed3\u515a\u7684\u9053\u7406\uff1f\u201d\u4ed6\u65b9\u81ea\u601d\u5fd6\u95ee\uff0c\u5374\u89c1\u4e3a\u9996\u7684\u90a3\u4e2a\u4e10\u8005\u76ee\u5149\u5411\u4ed6\u4e00\u626b\u3000\uff0c\u7adf\u7136\u9510\u5229 \u5982\u7535\u3002\u4ed6\u5fc3\u4e2d\u53c8\u4e00\u52a8\uff0c\u90a3\u961f\u4e5e\u4e10\u7adf\u7b14\u76f4\u5730\u8d70\u8fdb\u90a3\u6761\u8857\uff0c\u90a3\u4e24\u4e2a\u7a7f\u91d1\u8863\u7684\u3000\u9880\u957f\u5927\u6c49\u975e\u4f46\u6ca1\u6709 \u963b\u62e6\uff0c\u800c\u4e14\u8fdc\u8fdc\u7ad9\u4e86\u5f00\u53bb\u3002\u7f2a\u6587\u5947\u602a\uff0c\u6bdb\u6587\u742a\u770b\u4e86\u4e00\u773c\uff0c\u5374\u89c1\u5979\u3000\u6b63\u5728\u671b\u7740\u90a3\u7fa4\u4e5e\u4e10\u7684\u80cc\u5f71 \u51fa\u795e\uff0c\u5583\u5583\u81ea\u8bed\u7740\uff1a\u2018\u5947\u602a\uff0c\u4ed6\u4eec\u600e\u4e48\u4f1a\u548c\u7a77\u5bb6\u5e2e\u751f\u51fa\u7ea0\u3000\u7eb7\u6765\uff0c\u662f\u8c01\u60f9\u7684\u7978\uff1f\u201d\u8138\u4e0a\u7684\u795e \u91c7\uff0c\u7a81\u7136\u4e4b\u95f4\uff0c\u8d77\u4e86\u4e00\u79cd\u5947\u5f02\u7684\u5149\u8292\uff0c\u7f2a\u6587\u4e00\u7b11\uff0c\u3000\u5fd6\u9053\uff1a\u201c\u539f\u6765\u4f60\u4e5f\u662f\u559c\u6b22\u51d1\u70ed\u95f9\u7684\u4eba \u5440\uff01\u201d \u3000\u3000\u3000\u6bdb\u6587\u742a\u4f4e\u7740\u5934\u6c89\u541f\u4e86\u4e00\u4f1a\uff0c\u7a81\u7136\u63a5\u7740\u7f2a\u6587\u7684\u81c2\u56de\u5934\u5c31\u8d70\uff0c\u4e00\u9762\u9053\uff1a\u201c\u9ad8\u5174\u5427\uff0c\u3000\u6211\u5e26 \u4f60\u53bb\u770b\u70ed\u95f9\u53bb\u3002\u201d\u7f2a\u6587\u9664\u4e86\u5fae\u7b11\u4e4b\u5916\uff0c\u4f3c\u4e4e\u4e0d\u4f1a\u6709\u4ec0\u4e48\u5176\u4ed6\u7684\u8868\u60c5\uff0c\u968f\u7740\u6bdb\u6587\u3000\u742a\u56de\u5230\u8857 \u53e3\uff0c\u5374\u89c1\u90a3\u4e24\u4e2a\u5927\u6c49\u8fdc\u8fdc\u5c31\u5f2f\u4e0b\u8eab\u6765\u3002 \u3000\u3000\u3000\u7f2a\u6587\u4e00\u6123\uff0c\u5fd6\u9053\uff1a\u201c\u96be\u9053\u4ed6\u4eec\u5c31\u8ba4\u51fa\u5979\u662f\u8c01\u4e86\uff1f\u201d\u6bdb\u6587\u742a\u5f53\u7136\u4e5f\u6709\u540c\u6837\u7684\u611f\u89c9\u3000\uff0c\u54ea \u77e5\u80cc\u540e\u7a81\u7136\u6709\u4eba\u91cd\u91cd\u54b3\u55fd\u4e86\u4e00\u58f0\uff0c\u7f2a\u6587\u56de\u5934\u4e00\u671b\uff0c\u770b\u5230\u4e00\u4e2a\u91d1\u886b\u6c49\u5b50\u548c\u53e6\u5916\u4e09\u3000\u4eba\u5e76\u80a9\u7ad9\u5728 \u8eab\u540e\uff0c\u539f\u6765\u8fd9\u56db\u4eba\u81ea\u4ed6\u4eec\u8eab\u540e\u884c\u6765\uff0c\u811a\u6b65\u58f0\u4e3a\u5e02\u58f0\u6240\u63a9\uff0c\u662f\u4ee5\u4ed6\u4eec\u6ca1\u6709\u3000\u542c\u5230\u3002 \u3000\u3000\u3000\u2018\u539f\u6765\u4eba\u5bb6\u5f2f\u8170\u7684\u5bf9\u8c61\u4e0d\u662f\u6211\u4eec\u3002\u201d\u7f2a\u6587\u4f1a\u8fc7\u610f\u6765\uff0c\u4e0d\u7981\u54d1\u7136\u5931\u7b11\u3002\u90a3\u91d1\u886b\u6c49\u3000\u5b50\u4e24 \u773c\u4e0a\u7ffb\uff0c\u770b\u4e5f\u4e0d\u770b\u4ed6\u4eec\u4e00\u773c\uff0c\u6bdb\u6587\u742a\u6c14\u5f97\u54fc\u4e86\u4e00\u58f0\uff0c\u7a81\u7136\u4f38\u624b\u671d\u4ed6\u80a9\u524d\u91cd\u91cd\u63a8\u3000\u4e86\u4e00\u4e0b\u3002\u90a3 \u91d1\u886b\u4eba\u7adf\u88ab\u5979\u63a8\u5f97\u5012\u9000\u4e09\u6b65\uff0c\u51e0\u4e4e\u7ad9\u90fd\u7ad9\u4e0d\u7a33\u4e86\uff1a\u53e6\u5916\u90a3\u4e09\u4eba\u7acb\u523b\u6012\u53f1\u3000\u4e00\u58f0\uff0c\u5176\u4e2d\u4e00\u4e2a\u9762 \u8272\u8d64\u7ea2\u7684\u4e2d\u5e74\u58ee\u6c49\u4e00\u4e2a\u7bad\u6b65\u7a9c\u4e86\u4e0a\u6765\uff0c\u5de6\u624b\u4e00\u9886\u6bdb\u6587\u742a\u773c\u795e\uff0c\u53f3\u3000\u624b\u55d6\u5730\u4e00\u6325\uff0c\u6253\u5411\u5979\u80f8 \u524d\uff0c\u53e3\u4e2d\u559d\u9053\u201c\u5c0f\u4e2b\u5934\uff0c\u4f60\u627e\u6b7b\u5417\uff1f\u201d\u6bdb\u6587\u742a\u8138\u8272\u4e00\u53d8\uff0c\u987b\u3000\u77e5\u8fd9\u4eba\u7684\u4e00\u638c\u6253\u5f97\u751a\u4e3a\u4e0d\u662f\u5730 \u65b9\uff0c\u6b66\u6797\u4e2d\u6b63\u6d3e\u4eba\u7269\uff0c\u7adf\u4f1a\u671d\u4e00\u4e2a\u5987\u4eba\u5bb6\u8fd9\u79cd\u5730\u65b9\u51fa\u3000\u624b\uff0c\u5979\u7f9e\u607c\u4e4b\u4e0b\uff0c\u67f3\u8170\u4e00\u6298\uff0c\u65b9\u5f85\u51fa \u624b\uff0c\u54ea\u77e5\u90a3\u6c49\u5b50\u5e9e\u5927\u7684\u8eab\u8eaf\uff0c\u7adf\u786c\u751f\u751f\u88ab\u4eba\u62d6\u3000\u4e86\u56de\u53bb\u3002\u7f2a\u6587\u770b\u5f97\u809a\u4e2d\u597d\u7b11\uff0c\u539f\u6765\u90a3\u91d1\u886b\u6c49 \u5b50\u8eab\u5b50\u7ad9\u7a33\u540e\u6b63\u81ea\u6c14\u5f97\u53d8\u8272\uff0c\u76ee\u5149\u4e00\u77ac\u3000\uff0c\u5927\u6982\u770b\u6e05\u695a\u4e86\u90a3\u63a8\u81ea\u5df1\u7684\u662f\u8c01\uff0c\u8fde\u5fd9\u4e5f\u662f\u4e00\u4e2a\u7bad \u6b65\u7a9c\u4e86\u8fc7\u53bb\uff0c\u7adf\u4e00\u628a\u62c9\u7740\u90a3\u4e3a\u4ed6\u3000\u52a8\u624b\u7684\u6c49\u5b50\u7684\u80a9\u81c2\uff0c\u5c06\u4ed6\u62c9\u4e86\u56de\u6765\u3002\u90a3\u5927\u6c49\u75db\u5f97\u76f4\u54a7\u5634\uff0c \u539f\u6765\u8fd9\u7a7f\u7740\u91d1\u8272\u957f\u886b\u7684\u7626\u3000\u524a\u6c49\u5b50\uff0c\u5c31\u662f\u6c5f\u6d59\u4e00\u5e26\u540d\u58f0\u9887\u4e3a\u54cd\u4eae\u7684\u94c1\u624b\u4ed9\u733f\u4faf\u6797\uff0c\u8fd9\u4e00\u62c9\u60c5 \u6025\u4e4b\u4e0b\uff0c\u7adf\u4f7f\u51fa\u4e86\u3000\u4ed6\u4ed7\u4ee5\u6210\u540d\u7684\u201c\u9e70\u722a\u529f\u201d\u6765\uff0c\u90a3\u6c49\u5b50\u600e\u5403\u5f97\u6d88\uff1f\u4faf\u6797\u4e0d\u7ba1\u8fd9\u5927\u6c49\u9762\u4e0a\u7684 \u8868\u60c5\u7684\u96be\u770b\u3000\u548c\u5947\u602a\uff0c\u5374\u8d70\u5230\u6bdb\u6587\u742a\u8eab\u524d\uff0c\u4e00\u63d6\u5230\u5730\uff0c\u7b11\u7740\u9053\uff1a\u201c\u539f\u6765\u662f\u6bdb\u5927\u59d1\u5a18\uff0c\u8001\u53d4\u53d4 \u6ca1\u6709\u3000\u770b\u5230\u4f60\uff0c\u4f60\u53ef\u522b\u751f\u6c14\u3002\u201d \u3000\u3000\u3000\u6bdb\u6587\u742a\u4e00\u6487\u5634\uff0c\u9053\uff1a\u2018\u6211\u8fd8\u4ee5\u4e3a\u4faf\u56db\u53d4\u4e0d\u8ba4\u5f97\u6211\u4e86\u5462\uff1f\u201d\u5979\u4e0d\u5c51\u5730\u7768\u4e86\u90a3\u5927\u6c49\u3000\u4e00 \u773c\uff0c\u201c\u90a3\u4f4d\u82f1\u96c4\u597d\u4fca\u7684\u62f3\u811a\uff0c\u6211\u5012\u60f3\u5411\u4ed6\u9886\u6559\u4e00\u4e0b\u3002\u201d\u90a3\u9762\u8272\u8d64\u7ea2\u7684\u5927\u6c49\u542c\u5230\u4e86\u3000\u8fd9\u4e00\u95ee\u4e00 \u7b54\uff0c\u4e5f\u731c\u5230\u4e86\u8fd9\u88ab\u4ed6\u9a82\u4e3a\u201c\u4e2b\u5934\u201d\u7684\u5973\u5b50\u662f\u8c01\uff0c\u539f\u6765\u5c31\u8d64\u7ea2\u7684\u9762\u5b54\u53d8\u5f97\u8d8a\u3000\u53d1\u7ea2\u4e86\uff0c\u542c\u4e86\u6bdb \u6587\u742a\u7684\u201c\u6311\u8845\u201d\uff0c\u88c5\u4f5c\u597d\u50cf\u6839\u672c\u6ca1\u6709\u542c\u5230\u4e00\u6837\uff0c\u4ed6\u7eb5\u7136\u5728\u6c5f\u6e56\u4e0a\u4e5f\u3000\u5c0f\u6709\u540d\u58f0\uff0c\u4f46\u4ed6\u53ef\u4e0d\u6562 \u548c\u201c\u6bdb\u5927\u592a\u7237\u201d\u7684\u5973\u513f\u8f83\u91cf\u3002\u201c\u4f55\u524d\u508d\u800c\u540e\u606d\u4e5f\u3002\u201d\u7f2a\u6587\u6697\u3000\u6697\u597d\u7b11\uff0c\u4f46\u662f\u7b11\u5bb9\u4e2d\u50cf\u5f80\u5e38\u4e00 \u6837\uff0c\u542b\u8574\u7740\u4e00\u79cd\u4ee4\u4eba\u731c\u4e0d\u900f\u7684\u610f\u601d\u3002\u201c\u4f60\u6765\u5f97\u771f\u597d\u6781\u3000\u4e86\u3002\u597d\u6781\u4e86\u4e00\u201d\u94c1\u624b\u4ed9\u733f\u7b11\u7684\u65f6\u5019\uff0c \u679c\u7136\u4ee4\u4eba\u4e0d\u77e5\u4e0d\u89c9\u5730\u60f3\u8d77\u4e00\u53ea\u7334\u5b50\uff0c\u53ea\u662f\u4ed6\u660e\u3000\u9510\u7684\u773c\u795e\u548c\u90a3\u79cd\u5185\u5bb6\u9ad8\u624b\u6240\u72ec\u5177\u7684\u7279\u5fbd\u4e00\u4e24 \u65c1\u51f8\u51fa\u7684\u5927\u9633\u7a74\uff0c\u4f7f\u4eba\u5728\u6697\u7b11\u4ed6\u9762\u5bb9\u4e4b\u3000\u964b\u4ee5\u5916\u3002\u4ecd\u4e0d\u6562\u8f7b\u89c6\u3002\u201c\u4f60\u4eec\u8fdc\u6765\uff0c\u8001\u53d4\u53d4\u53ef\u5f97\u597d \u597d\u8bf7\u4f60\u4eec\u5403\u4e00\u987f\uff0c\u4eca\u5929\uff0c\u521a\u597d\u6211\u3000\u2026\u2026\u201d\u201c\u4ed6\u63a5\u7740\u8bf4\u9053\uff0c\u6bdb\u6587\u742a\u5374\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff1a\u201c\u4faf\u56db\u53d4 \u7684\u996d\u8fd8\u662f\u5403\u5f97\u7684\u5440\uff1f\u6050\u6015\u3000\u996d\u8fd8\u6ca1\u6709\u5403\u5b8c\uff0c\u5c31\u5f97\u6328\u4e0a\u4e00\u987f\u6253\u72d7\u68d2\u4e86\u3002\u201d\u5979\u5a07\u7b11\u7740\uff0c\u6545\u610f\u4e00\u62c9 \u7f2a\u6587\uff0c\u5411\u5916\u9762\u8d70\uff0c\u3000\u4e00\u9762\u9053\uff1a\u201c\u6211\u4eec\u8fd8\u662f\u8d70\u5427\uff01\u201d \u3000\u3000\u3000\u201c\u59d1\u5a18\uff0c\u4f60\u53ef\u4e0d\u8981\u518d\u5f00\u6211\u7684\u73a9\u7b11\u4e86\uff0c\u4eca\u5929\u771f\u662f\u9047\u7740\u5927\u4e8b\uff0c\u672c\u6765\u6211\u5df2\u5dee\u4eba\u98de\u9a91\u8d76\u3000\u53bb\u676d \u5dde\uff0c\u901a\u77e5\u4f60\u7684\u5c0a\u5927\u4eba\u3002\u53ef\u662f\u76f4\u5230\u4eca\u5929\u8fd8\u6ca1\u6709\u6d88\u606f\uff0c\u6211\u6b63\u6025\u5f97\u8981\u547d\uff0c\u6070\u6070\u9047\u7740\u4f60\u3000\u6765\uff0c\u771f\u597d\u6781 \u4e86\u3002\u201d \u3000\u3000\u3000\u94c1\u624b\u4ed9\u733f\u7b11\u7740\u9053\uff0c\u4e00\u9762\u505a\u7740\u624b\u5f0f\uff0c\u8bf7\u6bdb\u6587\u742a\u8fdb\u53bb\uff0c\u6bdb\u6587\u742a\u5374\u4e00\u6574\u9762\u8272\uff0c\u5e84\u5bb9\u8bf4\u3000\u9053\uff1a \u201c\u4faf\u56db\u53d4\uff0c\u4f60\u600e\u4e48\u4f1a\u60f9\u4e0a\u7a77\u5bb6\u5e2e\u7684\uff1f\u6211\u7239\u7239\u4e0d\u65e9\u5c31\u8bf4\u8fc7\uff0c\u4e0d\u8981\u627e\u8fd9\u73ed\u602a\u7269\u7684\u9ebb\u3000\u70e6\uff0c\u8001\u5b9e \u8bf4\uff0c\u8fd9\u73ed\u4eba\u5728\u6c5f\u6e56\u4e0a\u65e0\u5b54\u4e0d\u5165\uff0c\u60f9\u4e0a\u4ed6\u4eec\u53ef\u771f\u6709\u70b9\u8ba8\u538c\u3002\u201d\u53e3\u6c14\u4e00\u53d8\uff0c\u5c45\u3000\u7136\u5934\u5934\u662f\u9053\u3002 \u3000\u3000\u3000\u94c1\u624b\u4ed9\u733f\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8bf4\u6765\u8bdd\u957f\uff0c\u8fdb\u53bb\u518d\u8bb2\u5427\uff0c\u7a77\u5bb6\u5e2e\u8ba8\u538c\uff0c\u96be\u9053\u6211\u4e0d\u77e5\u3000\u9053 \u5417\uff1f\u201d \u3000\u3000\u3000\u51e0\u4eba\u5411\u8377\u5185\u5b9a\u53bb\uff0c\u8fd9\u5176\u4e2d\u53ea\u6709\u7f2a\u6587\u6700\u4e3a\u5fc3\u5b89\u7406\u5f97\uff0c\u65bd\u7136\u6f2b\u6b65\uff0c\u50cf\u662f\u901b\u8857\u4f3c\u7684\uff0c\u3000\u56db\u4e0b \u6253\u91cf\uff0c\u8fd9\u624d\u77e5\u9053\u90a3\u94c1\u624b\u4ed9\u733f\u53e3\u4e2d\u6240\u8bf4\u7684\uff1a\u2018\u5927\u4e8b\u201d\u679c\u7136\u5e76\u975e\u865a\u8bed\uff0c\u5c31\u51b2\u8fd9\u6761\u8857\u3000\u4e0a\u7684\u618e\u5f62\u770b \u6765\uff0c\u5149\u662f\u201c\u5927\u4e8b\u201d\u4e24\u5b57\uff0c\u8fd8\u50cf\u662f\u5e76\u4e0d\u8db3\u4ee5\u5f62\u5bb9\u4f3c\u7684\u3002\u539f\u6765\u8fd9\u6761\u957f\u7ea6\u5341\u4f59\u3000\u4e08\u7684\u6a2a\u8857\u3002\u4e24\u65c1\u7adf \u90fd\u662f\u9152\u697c\u996d\u94fa\uff0c\u60f3\u5fc5\u662f\u8fd9\u5bbf\u8fc1\u57ce\u9152\u697c\u996d\u94fa\u7684\u96c6\u4e2d\u5730\uff0c\u6b64\u523b\u8fd9\u4e24\u65c1\u3000\u5c11\u8bf4\u4e5f\u6709\u4e09\u3001\u56db\u5341\u95f4\u7684\u9152 \u697c\uff0c\u4e0a\u4e0a\u4e0b\u4e0b\uff0c\u91cc\u91cc\u5916\u5916\uff0c\u7adf\u7136\u5168\u90fd\u5750\u6ee1\u4e86\u4eba\u3002\u800c\u4ee5\u7f2a\u6587\u3000\u81ea\u5bb6\u65b9\u624d\u6240\u7ecf\u5386\u8fc7\u7684\u60c5\u5f62\u5fd6\u5ea6\uff0c \u8fd9\u4e09\u3001\u56db\u5341\u95f4\u9152\u697c\u91cc\u5750\u7740\u7684\u4eba\u7269\uff0c\u5f53\u7136\u90fd\u662f\u201c\u7075\u86c7\u3000\u201d\u624b\u4e0b\uff0c\u6216\u662f\u88ab\u4ed6\u4eec\u8bf7\u6765\u7684\u89d2\u8272\u3002\u5947\u602a \u7684\u662f\uff0c\u8fd9\u4e09\u3001\u56db\u5341\u95f4\u9152\u697c\u4e2d\u65e2\u5750\u6ee1\u4e86\u6b66\u6797\u8c6a\u3000\u58eb\uff0c\u90a3\u4e48\u54d7\u7b11\u4e4b\u58f0\u5e94\u8be5\u975e\u5e38\u5927\u624d\u5bf9\uff0c\u54ea\u77e5\u8fd9\u4e9b \u9152\u697c\u91cc\u9762\u5374\u5e76\u65e0\u8fd9\u79cd\u60c5\u51b5\uff0c\u867d\u7136\u4e5f\u6709\u3000\u8c08\u8bdd\u4e4b\u58f0\u4f20\u51fa\uff0c\u4f46\u7edd\u5bf9\u4e0d\u201c\u54d7\u7b11\u201d\u3002\u7f2a\u6587\u76ee\u5149\u56db\u8f6c\uff0c \u8138\u4e0a\u867d\u4ecd\u7136\u662f\u5e26\u7740\u5fae\u7b11\uff0c\u4f46\u4ece\u3000\u4ed6\u7684\u76ee\u5149\u4e2d\uff0c\u5df2\u53ef\u770b\u51fa\u8fd9\u795e\u79d8\u7684\u5c11\u5e74\u5fc3\u4e2d\uff0c\u53c8\u5728\u8f6c\u7740\u4e00\u4e9b\u5ff5 \u5934\u3002\u51e0\u4e4e\u6bcf\u4e09\u6b65\u4e00\u9694\uff0c\u3000\u5c31\u7ad9\u7740\u4e00\u4e2a\u91d1\u8863\u58ee\u6c49\uff0c\u770b\u5230\u4ed6\u4eec\u8fd9\u4e00\u884c\u8d70\u5230\uff0c\u5404\u5404\u8eac\u8eab\u4e3a\u793c\u3002\u94c1\u624b \u4ed9\u733f\u8d70\u5728\u6bdb\u6587\u742a\u3000\u8eab\u4fa7\uff0c\u7b14\u76f4\u8d70\u5411\u8fd9\u6761\u8857\u4e0a\u95e8\u9762\u6700\u4e3a\u5bbd\u9614\u7684\u4e00\u4e2a\u9152\u697c\uff0c\u6bdb\u6587\u742a\u81ea\u7136\u4e5f\u770b\u51fa\u60c5 \u5f62\u6709\u5f02\uff0c\u3000\u751a\u81f3\u6bd4\u5979\u60f3\u50cf\u4e2d\u529b\u8fd8\u8981\u9ebb\u70e6\uff0c\u6b64\u523b\u4e5f\u6536\u8d77\u4e86\u5a07\u7b11\uff0c\u9762\u4e0a\u5e26\u7740\u8083\u7136\u4e4b\u8272\u3002\u65b9\u81ea\u8d70\u5230 \u9152\u3000\u697c\u95e8\u53e3\uff0c\u8857\u7684\u5c3d\u5934\u53c8\u8d77\u4e86\u4e00\u9635\u9a9a\u52a8\uff0c\u5927\u5bb6\u56de\u5934\u53bb\u770b\uff0c\u5374\u89c1\u53c8\u6709\u5341\u4f59\u4eba\u8d70\u4e86\u8fdb\u6765\uff0c\u8fdc\u3000\u8fdc \u671b\u53bb\uff0c\u53ea\u89c1\u8fd9\u6279\u4eba\u5168\u90fd\u7a7f\u7740\u5bbd\u5927\u7684\u8888\u88df\uff0c\u5934\u4e0a\u5149\u79c3\u79c3\u7684\uff0c\u7adf\u7136\u5168\u90fd\u662f\u548c\u5c1a\uff01\u94c1\u624b\u3000\u4ed9\u733f\u8138\u4e0a \u7684\u795e\u8272\uff0c\u66f4\u53d8\u5f97\u6781\u5176\u96be\u770b\uff0c\u5374\u89c1\u90a3\u4e9b\u548c\u5c1a\u8fdb\u4e86\u8857\u540e\uff0c\u5c31\u90fd\u505c\u4e0b\u6765\uff0c\u53ea\u6709\u4e3a\u3000\u9996\u4e09\u4e2a\uff0c\u8fc8\u7740\u5927 \u6b65\u5b50\u8fc7\u6765\u3002\u7f2a\u6587\u4eff\u4f5b\u4e8b\u4e0d\u5173\u5df1\uff0c\u5176\u5b9e\u4ed6\u5374\u5728\u7559\u610f\u770b\u7740\uff0c\u53ea\u89c1\u8fd9\u4e09\u4e2a\u3000\u50e7\u4eba\u8eab\u6750\u867d\u7136\u90fd\u6781\u4e3a\u7626 \u524a\uff0c\u4f46\u5374\u90fd\u9f99\u884c\u864e\u6b65\uff0c\u4e00\u671b\u800c\u77e5\uff0c\u5927\u6709\u6765\u5934\u3002\u6bdb\u6587\u742a\u4e5f\u5927\u9732\u3000\u60ca\u5f02\u4e4b\u8272\uff0c\u4fcf\u6b65\u4e00\u6e9c\uff0c\u7ad9\u5728\u7f2a \u6587\u8eab\u4fa7\uff0c\u4fdd\u62a4\u7740\u5979\u7684\u8fd9\u4f4d\u201c\u6587\u5f31\u4e66\u751f\u201d\uff0c\u5374\u542c\u5f97\u4e00\u58f0\u3000\u201c\u963f\u5f25\u9640\u4f5b\u201d\uff0c\u9707\u8033\u55e1\u7136\u3002\u90a3\u4e3a\u9996\u7684 \u4e00\u4e2a\u50e7\u4eba\uff0c\u5df2\u6709\u53e4\u7a00\u4e4b\u9f84\u4e86\uff0c\u8138\u4e0a\u5e72\u5f97\u5df2\u65e0\u4e00\u3000\u4e1d\u8089\uff0c\u76b1\u7eb9\u6ee1\u5e03\uff0c\u957f\u7709\u5782\u76ee\uff0c\u4eff\u4f5b\u5df2\u5c06\u5165 \u4e0a\uff0c\u4f46\u4e00\u58f0\u4f5b\u53f7\u5ba3\u8fc7\uff0c\u53cc\u76ee\u4e00\u5f20\uff0c\u7f2a\u6587\u53ea\u3000\u89c9\u5f97\u8fd9\u8001\u50e7\u67af\u7626\u800c\u6697\u6de1\u7684\u9762\u5b54\u4e0a\uff0c\u50cf\u662f\u7a81\u7136\u4eae\u4e86 \u4e00\u76cf\u660e\u706f\u4e00\u6837\uff0c\u987f\u65f6\u7115\u53d1\u4e86\u8d77\u6765\u3002\u3000\u4ed6\u53cc\u624b\u5408\u5341\uff0c\u6717\u58f0\u9053\uff1a\u201c\u8d2b\u50e7\u58a8\u4e00\uff0c\u6765\u81ea\u5d69\u5c71\uff0c\u5b9e\u662f\u4e0d \u901f\u4e4b\u5ba2\uff0c\u4f46\u4faf\u6a80\u8d8a\u6b64\u4e3e\u65e2\u7136\u3000\u6709\u5173\u5929\u4e0b\u6b66\u6797\uff0c\u5c11\u6797\u606d\u4e3a\u6b66\u6797\u4e00\u6d3e\uff0c\u60f3\u4faf\u6a80\u8d8a\u4e5f\u4e0d\u4f1a\u62d2\u8d2b\u50e7\u4e8e \u95e8\u5916\u5427\u3002\u201d \u3000\u3000\u3000\u8fd9\u201c\u5d69\u5c71\u58a8\u4e00\u201d\u56db\u5b57\u4e00\u51fa\uff0c\u94c1\u624b\u4ed9\u733f\u548c\u53e6\u4e09\u4e2a\u6c49\u5b50\u9762\u76ee\u53c8\u4e00\u53d8\uff0c\u7f2a\u6587\u4e0d\u7981\u4ed4\u7ec6\u3000\u5730\u6253 \u91cf\u7740\u8fd9\u6765\u81ea\u5c11\u6797\u7684\u8001\u50e7\uff0c\u5374\u542c\u94c1\u624b\u4ed9\u733f\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u4faf\u6797\uff0c\u4e45\u95fb\u5c11\u6797\u5404\u4f4d\u3000\u795e\u50e7\u5927\u540d\uff0c \u4f46\u533a\u533a\u4ee5\u4e3a\u5404\u4f4d\u795e\u50e7\u90fd\u5df2\u52d8\u7834\u4e16\u60c5\uff0c\u53c2\u900f\u9020\u5316\uff0c\u662f\u4ee5\u624d\u672a\u60ca\u52a8\uff0c\u5982\u4eca\u4e0a\u3000\u4eba\u5c45\u7136\u6765\u4e86\uff0c\u771f\u6559 \u5728\u4e0b\u559c\u51fa\u671b\u5916\u3002\u201d\u867d\u7136\u6709\u8bf4\u6709\u7b11\uff0c\u4f46\u523a\u4eba\u7684\u7b11\u58f0\u4e2d\uff0c\u5df2\u6709\u52c9\u5f3a\u7684\u3000\u610f\u5473\u3002 \u3000\u3000\u3000\u58a8\u4e00\u4e0a\u4eba\u53c8\u5fae\u5fae\u5782\u4e0b\u53cc\u76ee\uff0c\u53cc\u624b\u5408\u5341\uff0c\u4f4e\u8bf5\u4f5b\u53f7\uff0c\u5e76\u6ca1\u6709\u7406\u4f1a\u4faf\u6797\u8bdd\u4e2d\u7684\u950b\u9510\u3000\uff0c\u901e \u81ea\u5e26\u7740\u8eab\u540e\u7684\u4e24\u4eba\u8d70\u5165\u9152\u697c\u3002 \u3000\u3000\u3000\u6bdb\u6587\u742a\u8d8a\u53d1\u8be7\u5f02\uff0c\u5979\u4e0d\u660e\u767d\u8fd9\u4f4d\u94c1\u624b\u4ed9\u733f\u5230\u5e95\u60f9\u4e86\u4ec0\u4e48\u98ce\u6ce2\uff0c\u7adf\u8fde\u8fd1\u5341\u5e74\u6765\u5df2\u3000\u4e0d\u8fc7 \u95ee\u6b66\u6797\u4e2d\u4e8b\u7684\u5c11\u6797\u95e8\u4eba\u4e5f\u60ca\u52a8\u4e86\u3002\u800c\u4e14\u4ee5\u6b64\u60c5\u63e3\u6d4b\uff0c\u81ea\u5df1\u7684\u7236\u4eb2\u5e76\u4e0d\u77e5\u9053\u6b64\u4e8b\u3000\uff0c\u800c\u662f\u8fd9\u94c1 \u624b\u4ed9\u733f\u4e00\u624b\u9020\u6210\u7684\u3002 \u3000\u3000\u3000\u5979\u4e0d\u7981\u5e26\u7740\u4e9b\u8d23\u5907\u7684\u76ee\u5149\u671b\u4e86\u4faf\u6797\u4e00\u773c\uff0c\u8981\u77e5\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u8fd1\u5e74\u6765\u867d\u5df2\u53d6\u4e86\u6b66\u3000\u6797\u9738 \u4e1a\uff0c\u4f46\u8fd9\u4e0d\u8fc7\u662f\u6307\u666e\u901a\u4e00\u73ed\u6c5f\u6e56\u8349\u83bd\u800c\u8a00\uff0c\u81f3\u4e8e\u90a3\u4e9b\u5728\u6b66\u6797\u4e2d\u57fa\u4e1a\u6df1\u56fa\u7684\u95e8\u6d3e\u3000\u4e00\u4e00\u5982\u5c11 \u6797\u3001\u6b66\u5f53\u3001\u6606\u4ed1\u7b49\u6d3e\uff0c\u4ed6\u4ecd\u4e0d\u6562\u8f7b\u6613\u62db\u60f9\uff0c\u800c\u8fd9\u4e9b\u95e8\u6d3e\u4e2d\u7684\u957f\u8005\u3002\u4e5f\u591a\u5df2\u3000\u4e0d\u95ee\u4e16\u4e8b\uff0c\u4e0b\u5c71 \u884c\u9053\u7684\u5f1f\u5b50\uff0c\u4e5f\u6ca1\u6709\u8fc7\u95ee\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u7684\u4e8b\uff0c\u8fd9\u5f53\u7136\u4e5f\u56e0\u4e3a\u201c\u7075\u86c7\u3000\u201d\u6bdb\u81ec\u8001\u8c0b\u6df1\u7b97\uff0c\u884c\u4e8b \u90fd\u6302\u7740\u5149\u660e\u6b63\u5927\u7684\u62db\u724c\uff0c\u8fd1\u5e74\u6765\u6bdb\u81ec\u66f4\u662f\u5c0f\u5fc3\u7ffc\u7ffc\uff0c\u5c31\u4ee5\u524d\u3000\u4e00\u4e9b\u89c1\u4e0d\u5f97\u4eba\u7684\u52fe\u5f53\u90fd\u5c11\u505a\u4e86 \u4e9b\uff0c\u4e3a\u7684\u4e5f\u4e0d\u8fc7\u662f\u6015\u5f15\u8d77\u5404\u540d\u95e8\u5927\u6d3e\u7684\u5ac9\u89c6\uff0c\u5c06\u81ea\u5df1\u3000\u8f9b\u82e6\u521b\u4e0b\u7684\u57fa\u4e1a\u6bc1\u53bb\u3002 \u3000\u3000\u3000\u6b64\u523b\u6bdb\u6587\u742a\u4e00\u89c1\u4eca\u65e5\u6b64\u4f1a\uff0c\u5149\u662f\u81ea\u5df1\u4eb2\u773c\u770b\u5230\u7684\uff0c\u5df2\u6709\u7a77\u5bb6\u5e2e\u548c\u5c11\u6797\u6d3e\uff0c\u697c\u4e0a\u3000\u5750\u7740 \u81ea\u5df1\u6ca1\u6709\u770b\u5230\u7684\uff0c\u8fd8\u4e0d\u77e5\u6709\u4e9b\u4ec0\u4e48\u4eba\u7269\uff0c\u5979\u7adf\u7136\u6697\u602a\u94c1\u624b\u4ed9\u733f\u600e\u4f1a\u4e3a\u4ed6\u7239\u7239\u60f9\u3000\u6765\u8fd9\u4e9b\u715e\u661f \u4e86\u3002 \u3000\u3000\u3000\u94c1\u624b\u4ed9\u733f\u4e5f\u81ea\u9762\u5e26\u6101\u5bb9\uff0c\u53f9\u7740\u6c14\u5f53\u5148\u4e0a\u4e86\u697c\uff0c\u6bdb\u6587\u742a\u4e00\u62c9\u7f2a\u6587\u8d70\u4e86\u4e0a\u53bb\uff0c\u7f2a\u6587\u3000\u53ea\u89c9 \u5f97\u5979\u638c\u5fc3\u6709\u4e9b\u6e7f\u6e7f\u5730\uff0c\u4e0d\u7981\u53c8\u4e00\u7b11\u3002 \u3000\u3000\u3000\u5927\u51fa\u6bdb\u6587\u742a\u6240\u6599\u7684\u662f\uff0c\u8fd9\u9152\u697c\u4e0a\u7684\u5341\u4f59\u5f20\u5e2d\u9762\u4e0a\uff0c\u53ea\u5be5\u5be5\u5750\u4e86\u4e8c\u3001\u4e09\u5341\u4e2a\u4eba\uff0c\u3000\u5176\u4e2d \u5750\u5728\u6700\u8fd1\u697c\u68af\u4e4b\u5904\u7684\u4e00\u4e2a\u80d6\u5b50\uff0c\u770b\u5230\u94c1\u624b\u4ed9\u733f\u4e0a\u6765\uff0c\u7adf\u7830\u7136\u5728\u684c\u4e0a\u62cd\u4e86\u4e00\u4e0b\uff0c\u3000\u5927\u58f0\u4ed6\u8bf4 \u9053\uff1a\u201c\u597d\u5927\u7684\u67b6\u5b50\uff0c\u53eb\u6211\u9b4f\u80d6\u5b50\u5750\u5728\u8fd9\u513f\u7b49\u4e86\u5feb\u4e00\u4e2a\u65f6\u8fb0\uff01\u201d \u3000\u3000\u3000\u94c1\u624b\u4ed9\u733f\u53cc\u76ee\u4e00\u5f20\uff0c\u50cf\u662f\u8981\u53d1\u4f5c\uff0c\u4f46\u53c8\u5fcd\u4e0b\u6c14\uff0c\u53cc\u624b\u5411\u56db\u5468\u4e00\u62f1\uff0c\u52c9\u5f3a\u5730\u6717\u58f0\u3000\u7b11 \u9053\uff1a\u201c\u5c0f\u53ef\u65e0\u72b6\uff0c\u81f4\u4ee4\u5404\u4f4d\u6b66\u6797\u524d\u8f88\u5728\u6b64\u4e45\u5019\uff0c\u5343\u7948\u6055\u7f6a\u3002\u201d\u6bdb\u6587\u742a\u53c8\u4e00\u76b1\u7709\uff0c\u3000\u5979\u77e5\u9053\u8fd9 \u4f4d\u201c\u4faf\u56db\u53d4\u201d\u5e73\u65e5\u6027\u5982\u70c8\u706b\uff0c\u4eca\u65e5\u5374\u5947\u602a\u4ed6\u600e\u4f1a\u5fcd\u5f97\u4e0b\u6c14\uff0c\u5979\u66f4\u5947\u602a\u7684\u662f\u3000\uff0c\u8fd9\u4f4d\u201c\u4faf\u56db \u53d4\u201d\u4e0d\u4f46\u4e00\u8eab\u8f6f\u786c\u529f\u592b\u90fd\u5df2\u6709\u4e86\u76f8\u5f53\u706b\u5019\uff0c\u800c\u4e14\u8fd8\u662f\u5979\u7239\u7239\u5e73\u65e5\u6700\u501a\u3000\u91cd\u7684\u4e00\u4e2a\u597d\u624b\uff0c\u90a3\u540d \u9707\u6b66\u6797\u7684\u201c\u94c1\u9a91\u795e\u97ad\u201d\u961f\uff0c\u5b9e\u9645\u4e0a\u4e5f\u662f\u4ed6\u5728\u7edf\u7387\u7740\uff0c\u5728\u6b66\u6797\u4e2d\u3000\u53ef\u79f0\u7099\u624b\u53ef\u70ed\uff0c\u8dfa\u4e00\u8dfa\u811a\u56db \u57ce\u4e71\u98a4\u7684\u4eba\u7269\uff0c\u4eca\u65e5\u5374\u53c8\u600e\u4f1a\u6709\u4eba\u5bf9\u4ed6\u5982\u6b64\u4e0d\u656c\uff1f \u3000\u3000\u3000\u5979\u4e0d\u7981\u671d\u90a3\u80d6\u5b50\u76ef\u4e86\u51e0\u773c\uff0c\u5374\u5e76\u4e0d\u8ba4\u5f97\uff0c\u5979\u76ee\u5149\u518d\u4e00\u8f6c\u3002\u770b\u5230\u8fd9\u697c\u4e0a\u7684\u4e8c\u5341\u4f59\u3000\u4eba\uff0c \u89c1\u5230\u94c1\u624b\u4ed9\u733f\u4e0a\u6765\uff0c\u6709\u7684\u5fae\u5fae\u6b20\u8eab\uff0c\u6709\u7684\u4ec5\u5750\u7740\u5fae\u4e00\u62b1\u62f3\uff0c\u8fd8\u6709\u7684\u51e0\u4e2a\u7adf\u8fde\u52a8\u3000\u90fd\u6ca1\u6709\u52a8\u4e00 \u4e0b\uff0c\u751f\u50cf\u662f\u90fd\u6ca1\u6709\u5c06\u8fd9\u4f4d\u201c\u6b66\u6797\u9b41\u9996\u201d\u7684\u628a\u5f1f\uff0c\u79f0\u96c4\u6c5f\u6d59\u7684\u4e00\u9738\uff0c\u6dee\u5357\u3000\u9e70\u722a\u6d3e\u7684\u9ad8\u624b\uff0c\u7387 \u9886\u94c1\u9a91\u795e\u97ad\u7684\u94c1\u624b\u4ed9\u733f\u770b\u5728\u773c\u5185\u3002 \u3000\u3000\u3000\u8fd9\u79cd\u60c5\u5f62\uff0c\u53ef\u592a\u4e0d\u5bfb\u5e38\uff0c\u6bdb\u6587\u742a\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6697\u6697\u5fd6\u9053\uff1a\u201c\u96be\u9053\u8fd9\u4e9b\u4eba\u5168\u90fd\u662f\u540d\u3000\u95e8\u540d \u6d3e\u7684\u9ad8\u4eba\uff1f\u201d\u5979\u518d\u4e00\u6253\u91cf\uff0c\u8fd9\u4e9b\u4eba\u867d\u7136\u9ad8\u3001\u77ee\u3001\u80d6\u3001\u7626\u5404\u5f02\uff0c\u4f46\u5927\u5bb6\u5374\u90fd\u6709\u4e00\u3000\u4e2a\u76f8\u540c\u7684\u7279 \u8272\uff0c\u90a3\u5c31\u662f\u8fd9\u4e9b\u4eba\u7684\u76ee\u5149\uff0c\u90fd\u6709\u7740\u50cf\u5200\u4e00\u822c\u7684\u9510\u5229\u7684\u5149\u91c7\u3002 \u3000\u3000\u3000\u5979\u4e0d\u7981\u66f4\u6697\u4e2d\u5947\u602a\uff0c\u987b\u77e5\u5979\u5e74\u5e7c\u4efb\u6c14\uff0c\u53c8\u6043\u6280\u800c\u9a84\uff0c\u5012\u4e0d\u662f\u6015\u4e86\u8fd9\u4e9b\u4eba\u7269\uff0c\u800c\u3000\u662f\u5947 \u602a\u8fd9\u4e00\u5411\u7a33\u5065\u5e72\u7ec3\u7684\u94c1\u624b\u4ed9\u733f\u600e\u4f1a\u5728\u6ca1\u6709\u5f97\u5230\u81ea\u5df1\u7239\u7239\u540c\u610f\u4e4b\u524d\uff0c\u5c31\u62db\u60f9\u4e86\u8fd9\u3000\u4e9b\u4eba\u6765\uff1f \u3000\u3000\u3000\u5979\u5374\u4e0d\u77e5\u9053\u8fd9\u4f4d\u94c1\u624b\u4ed9\u733f\uff0c\u809a\u5b50\u91cc\u9762\u4e5f\u6b63\u5728\u53eb\u82e6\u4e0d\u8fed\u554a\uff01 \u3000\u3000\u3000\u94c1\u624b\u4ed9\u733f\u5e72\u7b11\u4e86\u4e00\u9635\uff0c\u6307\u7740\u6bdb\u6587\u742a\u9053\uff1a\u201c\u8fd9\u4f4d\u5c31\u662f\u6211\u6bdb\u5927\u54e5\u7684\u638c\u73e0\uff0c\u4eca\u65e5\u662f\u51d1\u3000\u5de7\u8d76 \u6765\u6b64\u95f4\u7684\u3002\u201d \u3000\u3000\u3000\u6bdb\u6587\u742a\u53ea\u89c9\u5f97\u6570\u5341\u9053\u9510\u5229\u7684\u76ee\u5149\uff0c\u90fd\u626b\u5411\u81ea\u5df1\u8eab\u4e0a\uff0c\u4f46\u662f\u5979\u5374\u4ecd\u7136\u6602\u9996\u800c\u89c6\uff0c\u3000\u795e\u8272 \u81ea\u5982\uff0c\u7f2a\u6587\u5728\u65c1\u8fb9\u6697\u6697\u70b9\u5934\uff0c\u4f3c\u4e4e\u9887\u4e3a\u8d5e\u8bb8\u3002 \u3000\u3000\u3000\u8fd9\u4e8c\u5341\u4f59\u4eba\u751f\u5f97\u6781\u602a\uff0c\u5e76\u4e0d\u5750\u5728\u540c\u4e8c\u684c\u4e0a\uff0c\u53ea\u662f\u6bcf\u4e09\u4e94\u4eba\u4fbf\u636e\u4e86\u4e00\u5e2d\uff0c\u5374\u8fd8\u6709\u3000\u4e09\u6570 \u5e2d\u7a7a\u7740\uff0c\u94c1\u624b\u4ed9\u733f\u4fbf\u5411\u5bf9\u7740\u697c\u68af\u4e2d\u90a3\u5f20\u4e3b\u5e2d\u5750\u4e86\u4e0b\u6765\uff0c\u4e5f\u5c31\u662f\u521a\u597d\u5750\u5728\u65b9\u624d\u5411\u3000\u4ed6\u62cd\u684c\u5b50\u7684 \u201c\u9b4f\u80d6\u5b50\u201d\u65c1\u8fb9\u3002 \u7b2c\u516b\u7ae0>> \u53e4\u9f99\u300a\u6e58\u5983\u5251\u300b \u7b2c\u516b\u7ae0 \u3000\u3000\u3000\u7f2a\u6587\u5728\u8fd9\u91cc\uff0c\u4f3c\u4e4e\u5168\u7136\u662f\u751f\u758f\u7684\uff0c\u4ed6\u4e00\u526f\u6587\u8d28\u5f6c\u5f6c\u7684\u6837\u5b50\uff0c\u53c8\u751f\u5f97\u4e0d\u751a\u9ad8\u5927\uff0c\u3000\u4f46\u4ed6 \u98ce\u534e\u6e05\u6807\uff0c\u5374\u81ea\u7136\u5f15\u5f97\u5927\u5bb6\u5bf9\u4ed6\u6ce8\u89c6\uff0c\u4ed6\u5fae\u7b11\u7740\uff0c\u4e00\u8bed\u4e0d\u53d1\uff0c\u9ed8\u9ed8\u5730\u968f\u7740\u4faf\u6797\u3000\u5750\u5217\u5e2d\u4e0a\u3002 \u3000\u3000\u3000\u94c1\u624b\u4ed9\u733f\u5e72\u54b3\u4e86\u51e0\u58f0\uff0c\u4f3c\u4e4e\u8981\u5c06\u5927\u5bb6\u7684\u6ce8\u610f\u529b\u5f15\u5230\u81ea\u5df1\u8fd9\u9762\u6765\uff0c\u7136\u540e\u7ad9\u8d77\u5411\u56db\u3000\u5ea7\u53c8 \u4e00\u62f1\u624b\uff0c\u5e72\u7b11\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u5c0f\u5f1f\u5728\u6b66\u6797\u4e2d\u867d\u8584\u6709\u5fae\u540d\uff0c\u53ef\u662f\u5c0f\u5f1f\u4e5f\u6709\u81ea\u77e5\u4e4b\u3000\u660e\uff0c\u77e5\u9053\u5c31 \u51ed\u6211\u8fd9\u5757\u62db\u724c\uff0c\u4e5f\u5f15\u4e0d\u52a8\u5404\u4f4d\u7684\u4fa0\u9a7e\u2014\u2014\u2014\u201d\u4ed6\u8bf4\u5230\u8fd9\u91cc\uff0c\u7a81\u7136\u90a3\u201c\u9b4f\u3000\u80d6\u5b50\u201d\u53c8\u54fc\u4e86\u4e00 \u58f0\uff0c\u9053\uff1a\u201c\u5bf9\u6781\u4e86\uff0c\u4e00\u70b9\u4e0d\u9519\u3002\u201d\u4faf\u6797\u5374\u4f3c\u50cf\u6ca1\u6709\u542c\u5230\u4f3c\u7684\uff0c\u63a5\u4e0b\u3000\u53bb\u9053\uff1a\u201c\u5c24\u5176\u662f\u5c11\u6797\u6d3e \u7684\u58a8\u4e00\u4e0a\u4eba\uff0c\u6b66\u5f53\u6d3e\u7684\u6e05\u98ce\u5251\u6731\u5927\u4fa0\uff0c\u7a77\u5bb6\u5e2e\u7684\u51e0\u4f4d\u957f\u8001\uff0c\u3000\u548c\u5f52\u9690\u6d2a\u6cfd\u7684\u8001\u524d\u8f88\uff0c\u6614\u5e74\u5929 \u4e0b\u4e09\u5341\u516d\u9053\u6c34\u8def\u7684\u603b\u5de1\u9605\uff0c\u706b\u773c\u91d1\u96d5\u8427\u4e8c\u7237\uff0c\u90fd\u662f\u5fb7\u3000\u9ad8\u671b\u91cd\u7684\u6b66\u6797\u524d\u8f88\u2014\u2014\u2014\u201d\u90a3\u201c\u9b4f\u80d6 \u5b50\u201d\u53c8\u6781\u4e3a\u4e0d\u60a6\u5730\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u628a\u624b\u4e2d\u7684\u8336\u676f\u3000\u91cd\u91cd\u6401\u5230\u684c\u4e0a\uff0c\u539f\u6765\u94c1\u624b\u4ed9\u733f\u5bf9\u4ed6\u6709\u6c14\uff0c\u6545 \u610f\u4e0d\u8bf4\u4ed6\u7684\u540d\u5b57\u3002 \u3000\u3000\u3000\u4faf\u6797\u773c\u89d2\u4e00\u98d8\uff0c\u63a5\u7740\u9053\uff1a\u201c\u4f46\u662f\u5374\u77e5\u9053\u4e86\u5404\u4f4d\u7684\u4fa0\u9a7e\uff0c\u662f\u51b2\u7740\u90a3\u4ef6\u4e8b\u6765\u7684\uff0c\u53ea\u3000\u662f\u90a3 \u4ef6\u4e8b\u533a\u533a\u5728\u4e0b\u5374\u4f5c\u4e0d\u5f97\u4e3b\u3002\u201d \u3000\u3000\u3000\u4ed6\u8bdd\u58f0\u4e00\u987f\uff0c\u90a3\u201c\u9b4f\u80d6\u5b50\u201d\u53c8\u201c\u5427\u201d\u5730\u4e00\u62cd\u684c\u5b50\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u8fd9\u4e2a\u8001\u7334\u5b50\uff0c\u4f60\u3000\u8457\u4f5c \u4e0d\u5f97\u4e3b\uff0c\u5374\u53c8\u6709\u8c01\u505a\u5f97\u4e86\u4e3b\u5440\uff1f\u201d \u3000\u3000\u3000\u94c1\u624b\u4ed9\u733f\u9762\u76ee\u53c8\u4e00\u53d8\uff0c\u65b9\u81ea\u5927\u6012\uff0c\u5374\u542c\u5f97\u697c\u68af\u53e3\u4f20\u6765\u4e86\u4e00\u4e2a\u5c16\u7ec6\u7684\u58f0\u97f3\uff0c\u7b11\u7740\u3000\u8bf4 \u9053\uff1a\u201c\u9b4f\u80d6\u5b50\u53c8\u5728\u8fd9\u91cc\u53d1\u4ec0\u4e48\u5a01\uff1f\u4eba\u5bb6\u8001\u7334\u5b50\u4f5c\u4e0d\u5f97\u4e3b\u4f60\u53c8\u4e0d\u662f\u4e0d\u77e5\u9053\u7684\u3002\u201d\u58f0\u3000\u97f3\u867d\u5c16 \u7ec6\uff0c\u4f46\u5927\u5bb6\u542c\u8d77\u6765\u4ecd\u7136\u9707\u8033\u5f97\u5f88\u3002 \u3000\u3000\u3000\u90a3\u201c\u9b4f\u80d6\u5b50\u201d\u55d6\u5730\u7ad9\u4e86\u8d77\u6765\uff0c\u76ee\u5149\u4e2d\u6ee1\u542b\u6012\u610f\uff0c\u543c\u7740\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u4eba\u6562\u53eb\u6211\u2018\u3000\u9b4f\u80d6 \u5b50\u2019\uff1f\u6211\u9b4f\u80d6\u5b50\u5012\u8981\u770b\u770b\u4f60\u662f\u4ec0\u4e48\u53d8\u7684\uff1f\u201d\u6bdb\u6587\u742a\u548c\u7f2a\u6587\u5bf9\u89c6\u5fae\u5fae\u4e00\u7b11\uff0c\u5fc3\u4e2d\u3000\u5404\u5404\u5fd6\u9053\uff1a \u8fd9\u9b4f\u80d6\u5b50\u53e3\u53e3\u58f0\u58f0\u81ea\u79f0\u201c\u9b4f\u80d6\u5b50\u201d\uff0c\u5374\u4e0d\u51c6\u522b\u4eba\u53eb\u4ed6\u201c\u9b4f\u80d6\u5b50\u201d\u3002\u4e24\u4eba\u3000\u809a\u4e2d\u6b63\u81ea\u89c9\u5f97\u597d \u7b11\uff0c\u697c\u68af\u4e0a\u5df2\u65bd\u65bd\u7136\u8d70\u4e0a\u4e00\u4eba\uff0c\u7b11\u7740\u9053\uff1a\u201c\u54ce\u5440\uff01\u4e86\u4e0d\u5f97\uff01\u6211\u4eec\u9b4f\u3000\u5927\u4fa0\u53c8\u53d1\u8d77\u813e\u6c14\u6765\u4e86\uff0c \u6211\u8fd9\u51e0\u6839\u8001\u9aa8\u5934\u53ef\u5f53\u4e0d\u8d77\u9b4f\u5927\u4fa0\u7684\u2018\u516d\u9633\u624b\u2019\uff0c\u6765\uff0c\u6765\uff0c\u6765\u3000\uff0c\u9b4f\u5927\u4fa0\uff0c\u4f60\u8981\u4e0d\u8981\u6211\u8001\u5934\u5b50 \u7ed9\u4f60\u966a\u793c\uff1f\u201d\u8bf4\u7740\uff0c\u5411\u90a3\u201c\u9b4f\u80d6\u5b50\uff0c\u2019\uff0c\u8d70\u4e86\u8fc7\u53bb\u3002\u3000\u6b64\u4eba\u4e00\u4e0a\u697c\uff0c\u5e2d\u95f4\u7acb\u5373\u8d77\u4e86\u4e00\u9635\u4f4e \u8bed\uff0c\u90a3\u201d\u9b4f\u80d6\u5b50\u201d\u867d\u7136\u4ecd\u662f\u6c14\u864e\u864e\u7684\uff0c\u5374\u5750\u4e86\u4e0b\u3000\u6765\uff0c\u9053\uff1a\u201c\u6211\u5f53\u662f\u8c01\uff0c\u5374\u662f\u4f60\u8fd9\u4e2a\u8001\u5316 \u5b50\u3002\u201d\u7f2a\u6587\u95ea\u65e5\u671b\u53bb\uff0c\u53ea\u89c1\u8fd9\u4eba\u7626\u5f97\u50cf\u6839\u7af9\u3000\u7aff\u7684\uff0c\u7a7f\u7740\u7684\u4e5f\u662f\u767e\u7ed3\u9e51\u8863\uff0c\u4f46\u5374\u6d17\u5f97\u9887\u4e3a\u5e72 \u51c0\uff0c\u76ae\u80a4\u4e4b\u767d\u7699\u3001\u66f4\u5b9b\u5982\u5904\u5b50\uff0c\u7b11\u8d77\u3000\u6765\u7684\u65f6\u5019\uff0c\u773c\u89d2\u867d\u6709\u76b1\u7eb9\uff0c\u4f46\u4e00\u773c\u671b\u53bb\uff0c\u5916\u8868\u5374\u53ea\u6709 \u56db\u5341\u5c81\u5de6\u53f3\u3002 \u3000\u3000\u3000\u4ed6\u53c8\u54c8\u54c8\u4e00\u7b11\uff0c\u95ee\u201c\u9b4f\u80d6\u5b50\u201d\u9053\uff1a\u201c\u9b4f\u5927\u4fa0\uff0c\u6211\u8001\u5934\u5b50\u5fe0\u8a00\u9006\u8033\uff0c\u542c\u4e0d\u542c\u7531\u4f60\u3000\uff0c\u4f60 \u8fd9\u4e48\u5927\u5e74\u7eaa\uff0c\u53c8\u8fd9\u4e48\u80d6\uff0c\u8fd8\u662f\u5c11\u53d1\u813e\u6c14\u4e3a\u5999\uff0c\u5426\u5219\u4e2d\u4e86\u98ce\u53ef\u4e0d\u662f\u73a9\u7684\u3002\uff0c\uff0c\u4ed6\u3000\u51b7\u5632\u70ed\u9a82\uff0c \u90a3\u6c14\u6982\u4e0d\u53ef\u4e00\u4e16\u7684\u2018\u9b4f\u80d6\u5b50\uff0c\u5374\u59cb\u7ec8\u5750\u5728\u90a3\u8fb9\uff0c\u8138\u4e0a\u9752\u4e00\u9635\uff0c\u767d\u4e00\u9635\uff0c\u3000\u6b64\u523b\u53c8\u7ad9\u4e86\u8d77\u6765\uff0c \u5927\u58f0\u9053\uff1a\u201c\u51cc\u5316\u5b50\uff0c\u6211\u2018\u9b4f\u80d6\u5b50\u2019\u6b20\u4f60\u7684\u60c5\uff0c\u6ca1\u6cd5\u5b50\u548c\u4f60\u5435\u67b6\uff0c\u3000\u53ef\u662f\u4f60\u4e5f\u4e0d\u8981\u60f9\u607c\u4e86\u6211\uff0c \u5426\u5219\u4f60\u7684\u90a3\u4e9b\u5f92\u5b50\u5f92\u5b59\u5c31\u8981\u5012\u9709\u3002\u201d \u3000\u3000\u3000\u201c\u51cc\u5316\u5b50\u201d\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e0d\u60f9\u4f60\uff0c\u4e0d\u60f9\u4f60\u3002\u201d\u4e5f\u4e0d\u7406\u90a3\u7ad9\u8d77\u6765\u671d\u4ed6\u62f1\u624b\u7684\u94c1\u624b\u3000\u4ed9 \u733f\uff0c\u8ff3\u81ea\u5411\u7a77\u5bb6\u5e2e\u5750\u7740\u7684\u90a3\u4e00\u684c\u8d70\u8fc7\u53bb\uff0c\u7a77\u5bb6\u5e2e\u51e0\u4e2a\u770b\u8d77\u6765\u90fd\u662f\u5e2e\u4e2d\u4e3b\u8981\u4eba\u7269\u7684\u3000\u4e10\u8005\uff0c\u6b64 \u523b\u90fd\u7ad9\u4e86\u8d77\u6765\uff0c\u5411\u4ed6\u8eac\u8eab\u65bd\u793c\u3002 \u3000\u3000\u3000\u94c1\u624b\u4ed9\u733f\u53f9\u4e86\u53e3\u6c14\uff0c\u5750\u4e86\u4e0b\u6765\uff0c\u6bdb\u6587\u742a\u4e00\u62c9\u4ed6\u7684\u8896\u5b50\uff0c\u4f4e\u8bed\u9053\uff1a\u201c\u6b64\u4eba\u53ef\u5c31\u662f\u3000\u4e8c\u5341 \u5e74\u524d\u51fa\u540d\u7684\u96be\u60f9\u4eba\u7269\u7a77\u795e\u51cc\u9f99\uff0c\u90a3\u80d6\u5b50\u60f3\u5fc5\u5c31\u662f\u2018\u6606\u4ed1\u4e94\u8001\uff0c\u4e2d\u7684\u795e\u97e6\u9b4f\u51cc\u98ce\u3000\u4e86\uff0c\u4faf\u56db \u53d4\uff0c\u6211\u771f\u4e0d\u61c2\uff0c\u8fde\u5c11\u6797\u7684\u90a3\u4e2a\u8005\u548c\u5c1a\u548c\u8427\u8001\u96d5\uff0c\u6731\u767d\u7fbd\u90fd\u7b97\u4e0a\uff0c\u8fd9\u4e9b\u4eba\u90fd\u3000\u548c\u4f60\u8001\u4e00\u70b9\u513f\u5173 \u7cfb\u4e5f\u6328\u4e0d\u4e0a\uff0c\u4f60\u8001\u600e\u5730\u5c06\u4ed6\u4eec\u5168\u62db\u4e86\u6765\uff1f\u201d\u94c1\u624b\u4ed9\u733f\u5374\u53ea\u662f\u6447\u5934\uff0c\u3000\u53f9\u6c14\uff0c\u4f4e\u4f4e\u541f\u9053\uff1a\u201c\u7b97 \u6211\u5012\u9709\u3002\u201d\u5176\u5b9e\u4ed6\u4e5f\u771f\u7684\u5012\u9709\u3002\u8fd9\u4e9b\u4eba\u90fd\u662f\u591a\u5e74\u672a\u6d89\u6b66\u6797\uff0c\u3000\u4eca\u65e5\u7adf\u7136\u5168\u8dd1\u5230\u8fd9\u91cc\u6765\uff0c\u5f53\u7136 \u4e0d\u662f\u4e3a\u7740\u4ed6\uff0c\u53ea\u662f\u4ed6\u5374\u5012\u9709\u5730\u201c\u9996\u5f53\u5176\u51b2\u201d\u7f62\u4e86\u3002 \u3000\u3000\u3000\u4e00\u4f1a\u513f\uff0c\u4e0a\u4e86\u51b7\u76d8\uff0c\u6709\u7684\u5927\u5403\u5927\u559d\uff0c\u65c1\u82e5\u65e0\u4eba\uff0c\u6709\u7684\u5374\u8fde\u7b77\u5b50\u90fd\u672a\u66fe\u52a8\u4e00\u4e0b\uff0c\u3000\u6bdb\u6587 \u742a\u53c8\u5947\u602a\u3002 \u3000\u3000\u3000\u201c\u8fd9\u5230\u5e95\u662f\u4e3a\u7740\u4ec0\u4e48\u4e8b\uff1f\u4faf\u56db\u53d4\u8bf4\u4e86\u534a\u5929\uff0c\u4e5f\u6ca1\u6709\u8bf4\u51fa\u6765\uff01\u8fd9\u4e9b\u4eba\u4e5f\u4e0d\u7740\u6025\uff0c\u3000\u4e5f\u4e0d \u8bf4\u8bdd\u3002\u201d\u5979\u5fc3\u91cc\u7740\u6025\uff0c\u770b\u5230\u4faf\u6797\u7684\u6837\u5b50\uff0c\u53ef\u4e0d\u4fbf\u53d1\u95ee\uff0c\u53ea\u5f97\u95f7\u5728\u5fc3\u91cc\uff0c\u5f53\u7136\u4e5f\u3000\u5403\u4e0d\u4e0b\u53bb\u3002 \u3000\u3000\u3000\u83dc\u4e00\u9053\u4e00\u9053\u5730\u4e0a\uff0c\u9152\u7b75\u4e30\u5bcc\u5f97\u5f88\uff0c\u968f\u7740\u65f6\u95f4\u7684\u8fc7\u53bb\uff0c\u94c1\u624b\u4ed9\u733f\u9762\u4e0a\u7684\u795e\u60c5\u8d8a\u6765\u3000\u8d8a\u7740 \u6025\uff0c\u60f3\u662f\u5728\u7b49\u5f85\u7740\u4ec0\u4e48\u4eba\u4f3c\u7684\u3002 \u3000\u3000\u3000\u7f2a\u6587\u4ecd\u7136\u5fae\u5fae\u7b11\u7740\uff0c\u5403\u7740\u83dc\uff0c\u4e0a\u5230\u751c\u83dc\u7684\u65f6\u5019\u4ed6\u7ad9\u4e86\u8d77\u6765\uff0c\u8d70\u5230\u7a97\u53e3\uff0c\u671b\u7740\u5929\u3000\u4e0a\u7684 \u7e41\u661f\uff0c\u6df1\u6df1\u547c\u4e86\u51e0\u53e3\u6c14\u3002 \u3000\u3000\u3000\u5ea7\u4e2d\u7a81\u7136\u6709\u4e00\u4eba\u7ad9\u4e86\u8d77\u6765\uff0c\u4e5f\u8d70\u5230\u7a97\u53e3\uff0c\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u7269\u5439\u4e86\u4e24\u4e0b\uff0c\u58f0\u97f3\u5c16\u9510\u3000\u800c\u4eae \u4ea2\u3002 \u3000\u3000\u3000\u54e8\u58f0\u65b9\u843d\uff0c\u5bf9\u8857\u7684\u4e24\u5bb6\u9152\u697c\u91cc\u7a81\u7136\u5954\u51fa\u767e\u5341\u6761\u5927\u6c49\u6765\u62e5\u5728\u8857\u4e0a\uff0c\u90fd\u662f\u4e00\u8272\u9ed1\u8863\u3000\u52b2 \u88c5\uff0c\u80a9\u5934\u4e0a\u5fae\u5fae\u9732\u7740\u7f20\u7740\u4e1d\u7ef8\u7684\u5200\u67c4\u3002 \u3000\u3000\u3000\u90a3\u5439\u54e8\u4f5c\u54cd\u7684\u662f\u4e2a\u864e\u80cc\u718a\u8170\u7684\u5927\u6c49\uff0c\u65b9\u9762\u5927\u53e3\uff0c\u751f\u76f8\u751a\u4e3a\u5a01\u731b\uff0c\u4ed6\u5f53\u7a97\u800c\u7acb\uff0c\u3000\u58f0\u82e5 \u6d2a\u949f\u5730\u671d\u697c\u4e0b\u7684\u6570\u767e\u5927\u6c49\u9053\uff1a\u201c\u6b64\u95f4\u5df2\u7528\u4e0d\u7740\u4f60\u4eec\uff0c\u4f17\u5bb6\u5144\u5f1f\u8fd8\u662f\u5206\u505a\u4e03\u62e8\uff0c\u3000\u8fde\u591c\u56de\u5c71\u53bb \u597d\u4e86\u3002\u201d\u697c\u4e0b\u7684\u6c49\u5b50\u9f50\u58f0\u5406\u559d\u4e86\u4e00\u58f0\uff0c\u4e00\u8f6c\u8eab\uff0c\u4fbf\u6cbf\u7740\u8857\u7684\u5357\u9762\u8d70\u4e86\u3002 \u3000\u3000\u3000\u7f2a\u6587\u52a8\u4e5f\u4e0d\u52a8\u5730\u7ad9\u7740\uff0c\u7a81\u7136\u540e\u9762\u6709\u4e00\u4e2a\u6e29\u8f6f\u7684\u8eaf\u4f53\u9760\u8fd1\u4ed6\uff0c\u4ed6\u4e0d\u7528\u56de\u5934\uff0c\u5c31\u77e5\u3000\u9053\u90a3 \u662f\u6bdb\u6587\u742a\u3002\u8fd9\u4ece\u4ed6\u8eab\u540e\u4f20\u6765\u7684\u5e7d\u9999\u5c31\u53ef\u4ee5\u77e5\u9053\u3002 \u3000\u3000\u3000\u6bdb\u6587\u742a\u6307\u7740\u90a3\u4e9b\u6c49\u5b50\u7684\u540e\u5f71\u4f4e\u8bed\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u5c71\u897f\u5927\u884c\u5c71\u7684\u5feb\u5200\u4f1a\uff0c\u90a3\u4f4d\u5927\u6982\u3000\u5c31\u662f \u592a\u884c\u53cc\u6770\u7684\u4e00\u4f4d\u4e86\uff0c\u6211\u672c\u5c31\u542c\u7239\u7239\u8bf4\u60f3\u5c06\u201d\u5feb\u5200\u4f1a\uff0c\u62c9\u5728\u81ea\u5df1\u624b\u4e0b\uff0c\u5982\u4eca\u4e00\u770b\u3000\uff0c\u8fd9\u201c\u5feb\u5200 \u4f1a\u201d\u679c\u7136\u6709\u4e9b\u95e8\u9053\uff0c\u602a\u4e0d\u5f97\u7239\u7239\u7740\u6025\u3002\u201d \u3000\u3000\u3000\u7f2a\u6587\u4e0d\u7740\u8fb9\u9645\u5730\u201c\u55ef\u201d\u4e86\u4e00\u4e0b\uff0c\u684c\u4e2d\u4e00\u4e2a\u957f\u7740\u82b1\u767d\u987b\u7684\u8001\u8005\u4f4e\u4f4e\u5bf9\u8eab\u65c1\u7684\u4e00\u4e2a\u3000\u5c11\u5e74 \u8bf4\u4e86\u4e24\u53e5\u8bdd\uff0c\u90a3\u5c11\u5e74\u4fbf\u4e5f\u7ad9\u4e86\u8d77\u6765\uff0c\u8d70\u5230\u7a97\u53e3\uff0c\u64ae\u5507\u547c\u5578\u4e86\u4e00\u58f0\uff0c\u58f0\u97f3\u957f\u5f97\u4f7f\u3000\u6bdb\u6587\u742a\u60f3\u63a9 \u8033\u6735\u3002 \u3000\u3000\u3000\u5578\u58f0\u4e00\u4f4f\uff0c\u8857\u4e0a\u53c8\u9c7c\u8d2f\u8d70\u51fa\u6570\u5341\u767e\u4eba\uff0c\u5374\u4e0d\u7b49\u90a3\u5c11\u5e74\u8bf4\u8bdd\uff0c\u4e5f\u671d\u8857\u5916\u8d70\u53bb\uff0c\u53ea\u3000\u662f\u4e09 \u4e94\u6210\u7fa4\uff0c\u884c\u5217\u5374\u65e0\u5feb\u5200\u4f1a\u6574\u9f50\u3002 \u3000\u3000\u3000\u6bdb\u6587\u742a\u53c8\u4f4e\u8bed\u9053\uff1a\u201c\u8fd9\u4eba\u5750\u5728\u8427\u8001\u96d5\u65c1\u8fb9\uff0c\u5927\u6982\u662f\u6c34\u8def\u4e0a\u7684\u4eba\u7269\uff0c\u4ed6\u4eec\u4e00\u5411\u5f88\u3000\u5c11\u4e0a \u5cb8\uff0c\u8fd9\u6b21\u5374\u4e0d\u77e5\u600e\u7684\u4e5f\u8dd1\u4e86\u6765\uff0c\u8fd9\u771f\u8ba9\u6211\u5f04\u4e0d\u61c2\u3002\u201d \u3000\u3000\u3000\u7f2a\u6587\u65b9\u5f85\u7b54\u8bdd\uff0c\u5374\u89c1\u90a3\u7a77\u795e\u51cc\u9f99\u8eab\u5f62\u4e00\u52a8\uff0c\u4e0d\u77e5\u600e\u7684\u4e5f\u8dd1\u5230\u7a97\u53e3\uff0c\u5927\u58f0\u5730\u559d\u9053\u3000\uff1a \u201c\u5b69\u5b50\u4eec\uff01\u4eba\u5bb6\u90fd\u8d70\u4e86\uff0c\u4f60\u4eec\u4e5f\u8d70\u5427\u3002\u201d \u3000\u3000\u3000\u58f0\u97f3\u4e00\u843d\uff0c\u897f\u8fb9\u9152\u697c\u4e0a\u68af\u68af\u62d6\u62d6\u8d70\u51fa\u4e86\u4e00\u5927\u5806\u4e5e\u4e10\uff0c\u4e5f\u4e0d\u77e5\u9053\u6709\u591a\u5c11\u4eba\uff0c\u63a8\u63a8\u3000\u62c9 \u62c9\uff0c\u62c9\u62c9\u626f\u626f\u5730\uff0c\u4e5f\u671d\u8857\u5916\u8d70\u4e86\u3002 \u3000\u3000\u3000\u8fd9\u4e9b\u4eba\u4e00\u8d70\uff0c\u8fd9\u6761\u8857\u5c31\u7a7a\u4e86\u5927\u534a\uff0c\u5269\u4e0b\u6765\u7684\u60f3\u5fc5\u90fd\u662f\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u7684\u624b\u4e0b\u4e86\uff0c\u3000\u7f2a\u6587 \u4e00\u56de\u5934\uff0c\u671d\u6bdb\u6587\u742a\u7b11\u9053\uff1a\u201c\u6211\u6700\u559c\u6b22\u770b\u70ed\u95f9\uff0c\u4eca\u5929\u6211\u53ef\u771f\u662f\u5e78\u8fd0\uff0c\u80fd\u770b\u5230\u8fd9\u6d0b\u3000\u6d0b\u5927\u89c2\u7684\u573a \u9762\u3002\u201d \u3000\u3000\u3000\u6bdb\u6587\u742a\u8f7b\u8f7b\u54ac\u7740\u4e0b\u5507\uff0c\u5a07\u55d4\u7740\u9053\uff1a\u201c\u4f60\u8fd8\u5f97\u610f\u5462\uff1f\u4eba\u5bb6\u6025\u90fd\u6025\u6b7b\u4e86\uff0c\u53c8\u5f04\u4e0d\u6e05\u3000\u662f\u600e \u4e48\u56de\u4e8b\uff0c\u7239\u7239\u4e5f\u4e0d\u6765\uff0c\u4faf\u56db\u53d4\u53c8\u9634\u9633\u602a\u6c14\u5730\uff0c\u4ec0\u4e48\u8bdd\u90fd\u4e0d\u80af\u8bf4\u3002\u201d \u3000\u3000\u3000\u7f2a\u6587\u4e00\u7b11\u9053\uff1a\u201c\u742a\u59b9\uff01\u4f60\u8fd9\u53ef\u771f\u53eb\u505a\u675e\u4eba\u5fe7\u5929\u3002\u8bd5\u60f3\u4ee4\u5c0a\u5927\u4eba\u5728\u6b66\u6797\u4e2d\u7684\u58f0\u5a01\u3000\u3001\u5730 \u4f4d\uff0c\u8fd8\u6709\u4ec0\u4e48\u4e0d\u80fd\u89e3\u51b3\u7684\u4e8b\uff0c\u4f60\u4f55\u82e6\u7740\u6025\u5462\uff1f\u201d \u3000\u3000\u3000\u751c\u83dc\u8fc7\u540e\uff0c\u53c8\u4e0a\u8d77\u83dc\u6765\uff0c\u5374\u82e6\u4e86\u5c11\u6797\u5bfa\u7684\u4e09\u4f4d\u618e\u4eba\uff0c\u770b\u7740\u684c\u4e0a\u7684\u5927\u9c7c\u5927\u8089\uff0c\u9c7c\u3000\u7fc5\u6d77 \u53c2\uff0c\u5c71\u73cd\u6d77\u5473\uff0c\u5374\u52a8\u4e5f\u4e0d\u80fd\u52a8\u4e00\u4e0b\u3002 \u3000\u3000\u3000\u53ea\u662f\u8fd9\u4e09\u4f4d\u9ad8\u50e7\u65e2\u4e0d\u8bf4\u8bdd\uff0c\u9762\u4e0a\u4e5f\u672a\u9732\u4e1d\u6beb\u8868\u60c5\uff0c\u751f\u50cf\u662f\u5df2\u7ecf\u4eba\u5b9a\u4e86\u4f3c\u7684\uff0c\u5916\u3000\u754c\u7684 \u4e00\u5207\uff0c\u4ed6\u4eec\u90fd\u5168\u7136\u4e0d\u95fb\u4e0d\u95ee\u3002 \u3000\u3000\u3000\u65f6\u95f4\u5728\u96be\u582a\u7684\u6c89\u9ed8\u4e2d\u901d\u53bb\uff0c\u8fd9\u79cd\u6c89\u9ed8\u538b\u5f97\u4eba\u50cf\u662f\u51e0\u4e4e\u900f\u4e0d\u8fc7\u6c14\u6765\uff0c\u7f2a\u6587\u4ef0\u671b\u7a97\u3000\u5916\u7684 \u661f\u8fb0\uff0c\u77e5\u9053\u6b64\u523b\u5df2\u7ecf\u662f\u5b50\u3001\u4e11\u4e4b\u4ea4\u4e86\u3002 \u3000\u3000\u3000\u500f\u5730\uff0c\u90a3\u9b4f\u51cc\u98ce\u731b\u4e00\u62cd\u684c\u5b50\uff0c\u5927\u58f0\u543c\u53eb\u7740\u9053\uff1a\u201c\u6211\u9b4f\u80d6\u5b50\u53ef\u53d7\u4e0d\u4e86\u8fd9\u79cd\u9e1f\u6c14\uff0c\u3000\u5c0f\u7334 \u5b50\uff0c\u6211\u95ee\u4f60\u4e00\u53e5\u8bdd\uff0c\u4f60\u53ef\u5f97\u8001\u8001\u5b9e\u5b9e\u5730\u544a\u8bc9\u6211\u3002\u201d \u3000\u3000\u3000\u94c1\u624b\u4ed9\u733f\u51b7\u54fc\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u51ed\u4ec0\u4e48\uff1f\u201d\u4ed6\u4e5f\u662f\u6c5f\u6e56\u4e2d\u7684\u6210\u540d\u4eba\u7269\uff0c\u53ef\u518d\u4e0d\u80fd\u5f53\u3000\u7740\u90a3 \u8bb8\u591a\u6b66\u6797\u4eba\u7269\u88ab\u4eba\u5bb6\u574d\u8fd9\u4e2a\u53f0\uff0c\u662f\u4ee5\u4e5f\u6ee1\u542b\u6311\u8845\u610f\u5473\u4ed6\u8bf4\u4e86\u4e00\u53e5\u3002 \u3000\u3000\u3000\u9b4f\u51cc\u98ce\u679c\u7136\u5927\u6012\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4f60\u6562\u5bf9\u6211\u2018\u9b4f\u80d6\u5b50\u2019\u8fd9\u4e48\u8bf4\u8bdd\uff0c\u597d\uff0c\u597d\uff01\u6211\u5012\u8981\u3000\u770b\u770b \u4f60\u8fd9\u53ea\u7334\u5b50\u6709\u591a\u5c11\u5e74\u9053\u884c\uff01\u201d \u3000\u3000\u3000\u8eab\u5f62\u8038\u52a8\uff0c\u5c31\u5728\u4ed6\u201c\u884c\u201d\u5b57\u8bed\u97f3\u65b9\u843d\u4e4b\u95f4\uff0c\u4ed6\u90a3\u81c3\u80bf\u7684\u8eab\u8eaf\uff0c\u5c31\u4ece\u6905\u5b50\u4e0a\u76f4\u98de\u3000\u51fa \u6765\uff0c\u4e5f\u672a\u89c1\u4f5c\u52bf\uff0c\u5374\u5feb\u5982\u6d41\u661f\u4e00\u62b9\u3002 \u3000\u3000\u3000\u4ed6\u548c\u94c1\u624b\u4ed9\u733f\u539f\u672c\u5750\u5f97\u6781\u8fd1\uff0c\u8eab\u5f62\u4e00\u95ea\uff0c\u4fbf\u5df2\u5230\u4e86\u4faf\u6797\u8eab\u4fa7\uff0c\u55d6\u7684\u4e00\u638c\uff0c\u4fbf\u5411\u3000\u94c1\u624b \u4ed9\u733f\u7684\u80a9\u5934\u62cd\u53bb\uff0c\u98ce\u58f0\u6c89\u539a\u96c4\u6d51\u3002 \u3000\u3000\u3000\u94c1\u624b\u4ed9\u733f\u65e9\u5df2\u77e5\u9053\u8fd9\u9b4f\u51cc\u98ce\u7684\u624e\u624b\uff0c\u6b64\u523b\u773c\u89d2\u77ac\u5904\uff0c\u770b\u5230\u4ed6\u638c\u5fc3\u7adf\u6cdb\u51fa\u73e0\u7ea2\u8272\u3000\uff0c\u6606 \u4ed1\u6d3e\u7684\u8fd9\u79cd\u201c\u516d\u9633\u624b\u201d\u80fd\u591f\u79f0\u8a89\u6b66\u6797\u6570\u5341\u5e74\uff0c\u81f3\u4eca\u4e2d\u539f\u6b66\u6797\u5c1a\u6ca1\u6709\u4efb\u4f55\u4e00\u79cd\u638c\u3000\u529b\u80fd\u4e0e\u4e4b\u988c \u987d\uff0c\u53ef\u7edd\u975e\u5e78\u81f4\uff0c\u4faf\u6797\u77e5\u9053\u53ea\u8981\u8ba9\u8fd9\u638c\u6307\u642d\u4e0a\u4e00\u70b9\uff0c\u4fbf\u662f\u6b7b\u8def\u3002 \u3000\u3000\u3000\u4f46\u94c1\u624b\u4ed9\u733f\u4e45\u5386\u6c5f\u6e56\uff0c\u522b\u7684\u4e0d\u8bf4\uff0c\u5149\u662f\u90a3\u4efd\u52a8\u4eba\u7684\u9605\u5386\uff0c\u5c31\u7edd\u975e\u5e38\u4eba\u80fd\u53ca\uff0c\u53f3\u3000\u638c\u4e00 \u6309\u684c\u9762\uff0c\u8eab\u5f62\u98d8\u7136\u9000\u5f00\u4e09\u5c3a\u3002 \u3000\u3000\u3000\u987b\u77e5\u4ed6\u201c\u5927\u529b\u9e70\u722a\u624b\u201d\u867d\u7136\u4e5f\u662f\u638c\u529b\u4e0a\u6781\u9738\u9053\u7684\u529f\u529b\uff1a\u4f46\u53ef\u4e5f\u4e0d\u6562\u548c\u201c\u516d\u9633\u624b\u3000\u201d\u786c \u5bf9\u4e00\u638c\uff0c\u53ea\u6709\u8eab\u5f62\u540e\u9000\uff0c\u907f\u4e86\u5f00\u53bb\uff0c\u53e3\u4e2d\u5374\u559d\u9053\uff1a\u201c\u59d3\u9b4f\u7684\uff01\u8fd9\u91cc\u53ef\u4e0d\u662f\u52a8\u624b\u3000\u7684\u5730\u65b9\uff0c\u4f60 \u4e5f\u662f\u6b66\u6797\u9ad8\u624b\uff0c\u600e\u7684\u4e5f\u50cf\u4e2a\u6751\u592b\u4e00\u6837\uff0c\u5f20\u53e3\u5c31\u9a82\uff0c\u4f38\u624b\u5c31\u6253\uff0c\u6210\u4e2a\u4ec0\u4e48\u3000\u2014\u2014\u201d\u5c31\u5728\u8bf4\u8fd9\u53e5 \u8bdd\u7684\u529f\u592b\uff0c\u4ed6\u5df2\u8fde\u53d8\u4e86\u4e09\u79cd\u8eab\u6cd5\uff0c\u907f\u5f00\u4e86\u9b4f\u51cc\u98ce\u7684\u56db\u638c\u3002 \u3000\u3000\u3000\u9b4f\u51cc\u98ce\u88ab\u4ed6\u8fd9\u79cd\u950b\u5229\u7684\u8a00\u8bcd\u4e00\u6fc0\uff0c\u95f7\u54fc\u4e00\u58f0\uff0c\u53cc\u638c\u9f50\u5730\u63a8\u51fa\uff0c\u54d7\u7136\u4e00\u58f0\uff0c\u5c06\u4faf\u3000\u6797\u8eab \u540e\u90a3\u5f20\u684c\u5b50\u4e0a\u7684\u7897\u76cf\u90fd\u9707\u5f97\u98de\u4e86\u8d77\u6765\u3002 \u3000\u3000\u3000\u4faf\u6797\u88ab\u8fd9\u60ca\u4eba\u638c\u529b\u6240\u9707\uff0c\u8bed\u58f0\u4e2d\u65ad\uff0c\u638c\u672a\u9012\u5230\uff0c\u5c31\u662f\u638c\u98ce\u63a0\u5230\u8eab\u4e0a\uff0c\u4e5f\u4f7f\u4ed6\u6709\u3000\u4e00\u79cd \u6781\u4e3a\u4e0d\u8212\u670d\u7684\u611f\u89c9\uff0c\u50cf\u662f\u7acb\u523b\u8981\u95ed\u8fc7\u6c14\u53bb\u3002 \u3000\u3000\u3000\u4ed6\u8fd9\u624d\u77e5\u9053\uff0c\u81ea\u5df1\u4e07\u4e07\u4e0d\u662f\u4eba\u5bb6\u7684\u654c\u624b\uff0c\u4f46\u6b64\u523b\u4ed6\u5904\u5728\u8fd9\u5730\u65b9\u672c\u5c31\u72ed\u5c0f\uff0c\u53c8\u6446\u3000\u6ee1\u4e86 \u5706\u684c\u9762\u7684\u9152\u697c\uff0c\u88ab\u8fd9\u65b9\u5706\u5f84\u4e08\u5185\u5168\u90fd\u6709\u7740\u5a01\u529b\u7684\u638c\u98ce\u4e00\u538b\uff0c\u987f\u89c9\u5f97\u8fde\u907f\u90fd\u6ca1\u6709\u3000\u529e\u6cd5\u907f\u4e86\u3002 \u3000\u3000\u3000\u9b4f\u51cc\u98ce\u5fae\u5fae\u51b7\u7b11\uff0c\u6b63\u5f85\u5168\u529b\u4e00\u51fb\uff0c\u81f3\u5c11\u8981\u628a\u8fd9\u201c\u8001\u7334\u5b50\u201d\u5f04\u4e2a\u5927\u5927\u7684\u7070\u5934\u571f\u8138\u3000\uff0c\u54ea \u77e5\u7a81\u7136\u7ea2\u5149\u4e00\u95ea\uff0c\u523a\u5411\u81ea\u5bb6\u8eab\u540e\u7684\u85cf\u4e94\u7a74\uff0c\u65b9\u81ea\u4e00\u60ca\uff0c\u786c\u751f\u751f\u626d\u5934\u7529\u80a9\uff0c\u64a4\u56de\u3000\u638c\u529b\u3002 \u3000\u3000\u3000\u54ea\u77e5\u90a3\u5251\u52bf\u5feb\u5982\u95ea\u7535\uff0c\u5251\u70b9\u5fae\u98a4\u95f4\uff0c\u5251\u5c16\u4e0b\u79fb\u6570\u5bf8\uff0c\u5212\u5411\u201c\u80a9\u4e95\u201d\uff0c\u90e8\u4f4d\u3002\u65f6\u3000\u95f4\uff0c \u62ff\u634f\u5f97\u4e4b\u5999\uff0c\u7adf\u53eb\u4ed6\u4e5f\u4e3a\u4e4b\u4e00\u60ca\u3002 \u3000\u3000\u3000\u4ed6\u811a\u6b65\u4e00\u6e9c\uff0c\u8eab\u5f62\u7684\u6e9c\u6e9c\u5411\u53f3\u4e00\u8f6c\uff0c\u4f46\u90a3\u5251\u52bf\u5df2\u5feb\u5982\u75be\u77e2\u5730\u987a\u52bf\u4e00\u5212\uff0c\u5728\u4ed6\u54bd\u3000\u5589\u4e0b \u4e09\u5bf8\u4e8c\u5206\u95f4\u7684\u201c\u5929\u7a81\u201d\u3001\u201c\u7ffc\u76d6\u201d\u4e24\u7a74\u4e4b\u95f4\u4e00\u98a4\uff0c\u5251\u5149\u50cf\u662f\u7ea2\u8272\u7684\u706b\u7130\u4f3c\u7684\uff0c\u3000\u6620\u5f97\u4ed6\u8000\u76ee \u751f\u82b1\u3002 \u3000\u3000\u3000\u8fd9\u51e0\u62db\u51e0\u4e4e\u5728\u540c\u4e00\u5239\u54ea\u91cc\u5b8c\u6210\uff0c\u4ed6\u6765\u4e0d\u53ca\u601d\u7d22\uff0c\u811a\u6b65\u4e00\u6e9c\uff0c\u53c8\u540e\u9000\u4e24\u5c3a\uff0c\u54ea\u77e5\u3000\u8eab\u540e \u5df2\u7ecf\u9760\u7740\u5899\u4e86\uff0c\u800c\u90a3\u5251\u5149\u5374\u5982\u9644\u9aa8\u4e4b\u86c6\u8ddf\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u3000\u9b4f\u51cc\u98ce\u4ee5\u201c\u516d\u9633\u624b\u201d\u6df1\u6e5b\u7684\u529f\u529b\uff0c\u996e\u8a89\u6b66\u6797\u6570\u5341\u5e74\uff0c\u770b\u8d77\u6765\u5e74\u7eaa\u4e0d\u5927\uff0c\u867d\u5df2\u662f\u3000\u76f8\u8fd1 \u53e4\u7a00\u4e86\uff0c\u4f46\u813e\u6c14\u5374\u4ecd\u706b\u7206\u5f97\u5f88\u3002 \u3000\u3000\u3000\u4ed6\u540d\u5217\u201c\u6606\u4ed1\u4e94\u8001\u201d\u4e2d\u7684\u7b2c\u4e8c\u4f4d\uff0c\u6b66\u529f\u786e\u5b9e\u4e5f\u5f88\u5c11\u9047\u7740\u654c\u624b\u2014\u2014\u8fd9\u5f53\u7136\u4e5f\u6709\u4e9b\u3000\u56e0\u4e3a \u4ed6\u6839\u672c\u5f88\u5c11\u5728\u6c5f\u6e56\u884c\u52a8\u7684\u7f18\u6545\u4e00\u6b64\u523b\u88ab\u4eba\u5bb6\u4e0d\u660e\u4e0d\u767d\u5730\u51e0\u62db\uff0c\u5c31\u903c\u5f97\u8fde\u8fde\u540e\u9000\u3000\uff0c\u8fde\u5bf9\u624b\u7684 \u9762\u76ee\u90fd\u672a\u66fe\u770b\u6e05\uff0c\u4ed6\u5927\u6012\u4e4b\u4e0b\uff0c\u66b4\u559d\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u3000\u968f\u7740\u559d\u58f0\uff0c\u4ed6\u5de6\u638c\u659c\u524a\uff0c\u53f3\u638c\u5374\u53cd\u624b\u4e0a\u6325\uff0c\u51ed\u7740\u4ed6\u6570\u5341\u5e74\u6b66\u529f\u7684\u9020\u8be3\uff0c\u8fd9\u4e00\u638c\u3000\u4ed6\u7adf \u662f\u6325\u5411\u4eba\u5bb6\u90a3\u67c4\u957f\u5251\u7684\u5251\u810a\u3002 \u3000\u3000\u3000\u8fd9\u4e00\u62db\u7528\u5f97\u7edd\u9669\uff0c\u4e5f\u7edd\u5999\uff0c\u5728\u5ea7\u7684\u90fd\u662f\u6b66\u6797\u597d\u624b\uff0c\u90fd\u4e0d\u7981\u6697\u6697\u559d\u91c7\uff0c\u201c\u6606\u4ed1\u795e\u3000\u638c\u7684 \u516d\u9633\u624b\u201d\uff0c\u679c\u7136\u540d\u4e0d\u865a\u4f20\u3002 \u3000\u3000\u3000\u54ea\u77e5\u5927\u5bb6\u5fc3\u5ff5\u65b9\u52a8\uff0c\u9b4f\u51cc\u98ce\u7a81\u7136\u60e8\u53eb\u4e00\u58f0\uff0c\u5168\u8eab\u8df3\u4e86\u8d77\u6765\uff0c\u518d\u843d\u5230\u5730\u4e0a\u540e\uff0c\u5168\u3000\u8eab\u7684 \u80a5\u8089\u4ecd\u5728\u4e0d\u4f4f\u98a4\u6296\u7740\uff0c\u4e24\u773c\u6050\u60e7\u5730\u671b\u7740\u524d\u65b9\uff0c\u800c\u5728\u4ed6\u9762\u524d\u624b\u6301\u73ca\u745a\u822c\u7684\u957f\u5251\u5fae\u3000\u5fae\u800c\u7b11\u7684\uff0c \u6b63\u662f\u6bdb\u6587\u742a\u3002 \u3000\u3000\u3000\u8fd9\u4e9b\u6b66\u6797\u9ad8\u624b\u90fd\u83ab\u540d\u5176\u5999\u5730\u671b\u7740\u8fd9\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u7684\u638c\u73e0\uff0c\u5a07\u8273\u5982\u4ed9\u7684\u5c11\u5973\uff0c\u60ca\u3000\u5f02\u7740 \u5979\u7684\u6b66\u5b66\u4e4b\u6df1\uff0c\u7b80\u76f4\u795e\u4e4e\u5176\u795e\u3002 \u3000\u3000\u3000\u5927\u5bb6\u90fd\u5947\u602a\u8fd9\u201c\u6606\u4ed1\u4e94\u8001\u201d\u4e2d\uff0c\u6b66\u529f\u5411\u79f0\u6df1\u6e5b\u7684\u795e\u638c\u9b4f\u51cc\u98ce\uff0c\u600e\u4e48\u5728\u8fde\u907f\u6570\u62db\u3000\u4e4b \u4e0b\uff0c\u65b9\u4e00\u56de\u624b\uff0c\u5c31\u843d\u5f97\u8fd9\u6837\u7684\u5730\u6b65\uff1f \u3000\u3000\u3000\u8fd9\u4e0d\u602a\u8fd9\u4e9b\u4e2a\u4e2a\u90fd\u8eab\u6000\u7edd\u6280\u7684\u6b66\u6797\u9ad8\u624b\u60ca\u5f02\uff0c\u5c31\u8fde\u9b4f\u51cc\u98ce\u672c\u4eba\uff0c\u4ed6\u53ef\u4e5f\u5f04\u4e0d\u6e05\u3000\u695a\u662f \u600e\u4e48\u4f1a\u843d\u8d25\u7684\uff1f \u3000\u3000\u3000\u539f\u6765\u65b9\u624d\u4ed6\u638c\u7f18\u65b9\u81ea\u89e6\u7740\u4eba\u5bb6\u7684\u5251\u810a\uff0c\u5c31\u611f\u89c9\u5230\u6709\u4e00\u79cd\u4ed6\u751f\u5e73\u672a\u66fe\u7ecf\u5386\u8fc7\u7684\u5f3a\u3000\u5927\u529b \u91cf\uff0c\u4f7f\u5f97\u4ed6\u6d51\u8eab\u6240\u6709\u7684\u529f\u529b\uff0c\u5168\u90fd\u5931\u53bb\u4e86\u529f\u80fd\uff0c\u800c\u63a7\u5236\u4e0d\u4f4f\u7684\u5168\u8eab\u9661\u7136\u8d77\u4e86\u4e00\u3000\u79cd\u5f3a\u70c8\u7684\u98a4 \u6296\u3002 \u3000\u3000\u3000\u4ed6\u843d\u8d25\u4e86\uff0c\u4f46\u662f\u5374\u8d25\u5f97\u83ab\u540d\u5176\u5999\u3002 \u3000\u3000\u3000\u4ed6\u671b\u7740\u9762\u524d\u7684\u654c\u624b\uff0c\u90a3\u53ea\u662f\u4e00\u4e2a\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u5a07\u7f8e\u5982\u82b1\u854a\uff0c\u4eff\u4f5b\u7981\u4e0d\u8d77\u8f7b\u8f7b\u4e00\u6298\u3000\u7684\u5c11 \u5973\u3002 \u3000\u3000\u3000\u5979\u624b\u91cc\u90a3\u67c4\u53d1\u51fa\u73ca\u745a\u822c\u5149\u91c7\u7684\u957f\u5251\uff0c\u659c\u659c\u5411\u4e0b\u5782\u7740\u3002 \u3000\u3000\u3000\u201c\u8fd9\u79cd\u5947\u5f02\u7684\u529b\u91cf\uff0c\u662f\u4f55\u95e8\u4f55\u6d3e\u7684\u5185\u529f\u5462\uff1f\u96be\u9053\u8fd9\u5973\u5b50\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u5df2\u80fd\u5c06\u81ea\u8eab\u3000\u8fd9\u79cd \u95fb\u6240\u672a\u95fb\u7684\u5185\u5bb6\u529f\u529b\uff0c\u81ea\u5251\u8eab\u4e0a\u903c\u53d1\u51fa\u6765\uff1f\u201d \u3000\u3000\u3000\u9b4f\u51cc\u98ce\u5341\u4e00\u5c81\u6295\u5165\u5e08\u95e8\uff0c\u4e60\u827a\u81f3\u4eca\uff0c\u5df2\u6709\u4e94\u5341\u4f59\u5e74\uff0c\u867d\u7136\u9650\u4e8e\u5929\u8d44\uff0c\u4e0d\u80fd\u767b\u5cf0\u3000\u9020 \u6781\uff0c\u4f46\u65e0\u8bba\u5982\u4f55\uff0c\u4e5f\u662f\u6b66\u6797\u6709\u6570\u9ad8\u624b\u4e4b\u4e00\uff0c\u5982\u4eca\u7adf\u8fde\u4eba\u5bb6\u7528\u7684\u662f\u54ea\u4e00\u95e8\u54ea\u4e00\u6d3e\u7684\u3000\u529f\u592b\u90fd\u770b \u4e0d\u51fa\u6765\u3002 \u3000\u3000\u3000\u4ed6\u773c\u4e2d\u6781\u5feb\u5730\u95ea\u4e86\u51e0\u95ea\uff0c\u6bdb\u6587\u742a\u4ecd\u7136\u4fcf\u751f\u751f\u5730\u6301\u5251\u800c\u7acb\u3002 \u3000\u3000\u3000\u94c1\u624b\u4ed9\u733f\u5f20\u5927\u4e86\u5634\u5df4\uff0c\u6115\u5728\u90a3\u91cc\uff0c\u7f2a\u6587\u671b\u7740\u5979\u624b\u4e2d\u7684\u5b9d\u5251\uff0c\u4e5f\u9677\u5165\u6df1\u601d\u4e4b\u4e2d\uff0c\u3000\u5634\u89d2 \u5374\u4ecd\u5e26\u7740\u90a3\u4efd\u4f3c\u7b11\u975e\u7b11\u7684\u8868\u60c5\u3002 \u3000\u3000\u3000\u7a77\u795e\u51cc\u9f99\u54c8\u54c8\u4e00\u58f0\u957f\u7b11\uff0c\u7ad9\u4e86\u8d77\u6765\uff0c\u8eab\u5f62\u4e00\u8f6c\uff0c\u5df2\u8f6c\u5230\u6bdb\u6587\u742a\u9762\u524d\uff0c\u6717\u58f0\u5927\u7b11\u3000\u7740 \u9053\uff1a\u201c\u957f\u6c5f\u540e\u6d6a\u63a8\u524d\u6d6a\uff0c\u54c8\u54c8\uff01\u8fd9\u5973\u5a03\u5a03\u4eca\u5929\u5012\u771f\u53eb\u6211\u8001\u627f\u5b50\u5f00\u4e86\u773c\u754c\u4e00\u4e00\u201d\u4ed6\u3000\u8bdd\u672a\u8bf4 \u5b8c\uff0c\u9b4f\u51cc\u98ce\u832b\u7136\u4e00\u58f0\u957f\u53f9\uff0c\u53cc\u81c2\u5fae\u5f20\uff0c\u8eab\u5f62\u500f\u7136\u5f80\u8eab\u540e\u7684\u7a97\u6237\u4e2d\u5012\u9000\u800c\u53bb\uff0c\u3000\u82e6\u53f9\u58f0\u4e2d\uff0c\u5df2 \u9690\u6ca1\u5728\u7a97\u5916\u7684\u591c\u8272\u91cc\u3002 \u3000\u3000\u3000\u7a77\u795e\u51cc\u9f99\u5fae\u559f\u9053\uff1a\u201c\u8fd9\u9b4f\u8001\u4e8c\u4eba\u867d\u662f\u706b\u7206\u813e\u6c14\uff0c\u4f46\u884c\u4e8b\u521a\u76f4\uff0c\u800c\u4e14\u6700\u662f\u6069\u6028\u5206\u3000\u660e\uff0c \u7b97\u5f97\u4e0a\u662f\u4e2a\u5927\u4e08\u592b\uff0c\u60f3\u4e0d\u5230\u6b64\u6b21\u4e0b\u5c71\uff0c\u5982\u6b64\u56de\u53bb\uff01\u201d \u3000\u3000\u3000\u8bf4\u7f62\u4e00\u62c2\u7834\u8896\uff0c\u8083\u7136\u5411\u94c1\u624b\u4ed9\u733f\u9053\uff1a\u201c\u5c0f\u670b\u53cb\uff0c\u4f60\u867d\u7136\u60f3\u4e0d\u5230\u6211\u4eec\u8fd9\u73ed\u8001\u538c\u7269\u3000\u4f1a\u5168 \u6765\u81f3\u6b64\u95f4\uff0c\u53ef\u662f\u4f60\u5927\u6982\u603b\u4e5f\u77e5\u9053\u6211\u4eec\u662f\u4e3a\u7740\u4ec0\u4e48\u6765\u7684\u3002\u201d \u3000\u3000\u3000\u4ed6\u5c06\u5df2\u903e\u77e5\u547d\u4e4b\u5e74\u7684\u94c1\u624b\u4ed9\u733f\u79f0\u505a\u201c\u5c0f\u670b\u53cb\u201d\uff0c\u94c1\u624b\u4ed9\u733f\u5374\u975e\u4f46\u4e0d\u89c9\u5f97\u523a\u8033\uff0c\u3000\u800c\u4e14 \u8fde\u4e00\u70b9\u597d\u7b11\u7684\u6837\u5b50\u90fd\u6ca1\u6709\uff0c\u53ea\u6709\u6b63\u5bb9\u9053\uff1a\u2018\u665a\u8f88\u6b64\u6b21\u59d4\u5b9e\u6ca1\u6709\u60ca\u52a8\u5404\u4f4d\u8001\u524d\u8f88\u3000\u7684\u610f\u601d\uff0c\u53ea \u662f\u5949\u4e86\u6211\u6bdb\u5927\u54e5\u4e4b\u547d\uff0c\u63a5\u5f85\u5feb\u5200\u4f1a\u7684\u7fa4\u8c6a\u548c\u6c5f\u6e56\u4e0a\u4e00\u5e26\u7684\u6c34\u4e0a\u82f1\u96c4\u5728\u6b64\u3000\u4e00\u805a\uff0c\u8fde\u7a77\u5bb6\u5e2e\u7684 \u5404\u4f4d\u957f\u8001\u90fd\u672a\u66fe\u6562\u60ca\u52a8\u5f97\u2014\u2014\u201d\u7a77\u795e\u51cc\u9f99\u54fc\u4e86\u4e00\u58f0\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u3000\u8fd9\u4e2a\u6211\u8001\u5934\u5b50\u4e5f\u77e5\u9053\u3002\u201d \u3000\u3000\u3000\u94c1\u624b\u4ed9\u733f\u5e72\u54b3\u4e86\u51e0\u58f0\uff0c\u53c8\u9053\uff1a\u201c\u53ea\u662f\u665a\u8f88\u5374\u60f3\u4e0d\u5230\u673a\u7f18\u51d1\u5de7\uff0c\u8ba9\u665a\u8f88\u9047\u7740\u53e6\u4e00\u3000\u6869 \u4e8b\uff0c\u81f3\u4e8e\u60ca\u52a8\u4e86\u5404\u4f4d\u8001\u524d\u8f88\u7684\u4fa0\u9a7e\u3002\u201d\u4ed6\u53e3\u4e2d\u5728\u8bf4\u7740\u2018\u673a\u7f18\u51d1\u5de7\u2019\uff0c\u6697\u4e2d\u5374\u5728\u5927\u3000\u53f9\u5012 \u9709\uff0c\uff1a\u2018\u8001\u524d\u8f88\u90fd\u662f\u5fb7\u9ad8\u671b\u91cd\u7684\u4eba\uff0c\u60f3\u5fc5\u4e5f\u80fd\u4f53\u8c05\u665a\u8f88\u7684\u82e6\u8877\u3002\u53ea\u662f\u2014\u2014\u201d\u4ed6\u3000\u6478\u7740\u4e0b\u9894\uff0c \u6c89\u541f\u534a\u664c\u9053\uff1a\u201c\u53ea\u662f\u665a\u8f88\u5374\u6709\u4e00\u4e8b\u4e0d\u5927\u660e\u767d\u2014\u2014\u201d\u7a77\u795e\u51cc\u9f99\u5927\u7b11\u4e00\u58f0\uff0c\u3000\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u660e\u767d \u6211\u4eec\u600e\u4f1a\u5ffd\u7136\u90fd\u77e5\u9053\u4e86\u8fd9\u4ef6\u4e8b\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u3000\u4ed6\u6545\u610f\u4e00\u987f\uff0c\u770b\u5230\u94c1\u624b\u4ed9\u733f\u8fde\u8fde\u70b9\u5934\uff0c\u624d\u63a5\u7740\u8bf4\u4e0b\u53bb\u9053\uff1a\u201c\u2018\u8fd9\u7b97\u4f60\u4eec\u5012\u9709\uff0c\u3000\u8ba9\u4f60 \u4eec\u7684\u4e00\u4e2a\u5bf9\u5934\u77e5\u9053\u6b64\u4e8b\u3002\u201d\u4ed6\u624b\u4e00\u6446\uff0c\u963b\u4f4f\u4e86\u94c1\u624b\u4ed9\u733f\u60f3\u53d1\u95ee\u7684\u4f01\u56fe\uff0c\u63a5\u7740\u9053\u3000\uff1a\u201c\u8fd9\u4e2a\u4f60 \u4e0d\u5fc5\u95ee\uff0c\u56e0\u4e3a\u95f4\u4e86\u6211\u8001\u5934\u5b50\u4e5f\u4e0d\u4f1a\u544a\u8bc9\u4f60\uff0c\u5176\u5b9e\u6211\u8fd9\u4e5f\u7b97\u591a\u8bdd\u4e86\uff0c\u7167\u4f60\u3000\u4eec\u8fd9\u5341\u6570\u5e74\u5728\u6c5f\u6e56 \u4e0a\u7684\u6240\u505a\u6240\u4e3a\uff0c\u6211\u8001\u5934\u5b50\u65e9\u8be5\u81ea\u5df1\u52a8\u624b\u4e86\u3002\u201d\u4ed6\u770b\u4e86\u6bdb\u6587\u742a\u4e00\u773c\u3000\uff0c\u5ffd\u7136\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u53ea\u662f \u6211\u770b\u8fd9\u5973\u5a03\u5a03\u624b\u4e2d\u6240\u4f7f\u7684\u957f\u5251\uff0c\u6781\u4f3c\u6211\u6614\u5e74\u4e00\u4e2a\u6545\u4eba\u66fe\u7ecf\u3000\u63d0\u8fc7\u4e4b\u7269\uff0c\u662f\u4ee5\u624d\u591a\u4e86\u51e0\u53e5 \u5634\u3002\u201d\u4ed6\u8bf4\u4e86\u534a\u5929\uff0c\u5374\u50cf\u6253\u54d1\u8c1c\u4f3c\u7684\uff0c\u6bdb\u6587\u742a\u8d8a\u542c\u8d8a\u7cca\u3000\u6d82\uff0c\u8d8a\u53d1\u4e0d\u77e5\u9053\u662f\u600e\u4e48\u56de\u4e8b\u4e86\u3002 \u3000\u3000\u3000\u5176\u5b9e\u8fd9\u4e8b\u9519\u7efc\u590d\u6742\uff0c\u5c31\u8fde\u5176\u4e2d\u7684\u5f53\u4e8b\u4eba\uff0c\u90fd\u6709\u7684\u5f04\u4e0d\u6e05\u695a\uff0c\u81f3\u4e8e\u90a3\u5feb\u5200\u4f1a\uff0c\u592a\u3000\u884c\u53cc \u6770\u4e2d\u7684\u4e8c\u4fa0\uff0c\u91d1\u8d85\u6770\uff0c\u592a\u6e56\u8001\u96d5\u8427\u8fdf\uff0c\u4ee5\u53ca\u53e6\u5916\u4e00\u4e9b\u5728\u5ea7\u7684\u8c6a\u5ba2\uff0c\u9664\u4e86\u58a8\u4e00\u4e0a\u3000\u4eba\uff0c\u6e05\u98ce\u5251 \u6731\u767d\u7fbd\u6709\u9650\u51e0\u4eba\u5916\uff0c\u4ec5\u77e5\u9053\u8fd9\u4e8b\u5173\u7cfb\u751a\u5927\uff0c\u4e5f\u662f\u4e00\u65e0\u6240\u77e5\u7684\u3002 \u3000\u3000\u3000\u56db\u5ea7\u7fa4\u8c6a\u4e5f\u6709\u4e9b\u5728\u7a83\u7a83\u79c1\u8bed\u7740\uff0c\u5374\u4e0d\u662f\u5b8c\u5168\u90fd\u662f\u8c08\u8bba\u7740\u8fd9\u4e9b\u4e8b\uff0c\u6709\u7684\u53ea\u662f\u4e5f\u5728\u3000\u731c\u6d4b \u90a3\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u638c\u73e0\u90a3\u79cd\u5947\u5999\u529b\u91cf\u7684\u6765\u6e90\u3002 \u3000\u3000\u3000\u6bdb\u6587\u742a\u6084\u7136\u5c06\u90a3\u67c4\u5251\u6536\u56de\u9798\u4e2d\uff0c\u7a77\u795e\u51cc\u9f99\u671d\u5979\u4e00\u7b11\uff0c\u8f6c\u8eab\u8d70\u56de\u5ea7\u4f4d\u4e0a\uff0c\u7f2a\u6587\u5374\u3000\u5446\u5446 \u5730\u671b\u7740\u4ed6\u7626\u524a\u7684\u80cc\u5f71\u51fa\u795e\u3002 \u3000\u3000\u3000\u94c1\u624b\u4ed9\u733f\u5374\u6115\u6115\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u6bdb\u6587\u742a\u8d70\u8fc7\u53bb\uff0c\u4f4e\u8bed\u9053\uff1a\u201c\u4faf\u56db\u53d4\uff0c\u5230\u5e95\u662f\u4ec0\u4e48\u3000\u4e8b\uff1f \u4f60\u518d\u4e0d\u544a\u8bc9\u6211\uff0c\u6211\u53ef\u5f97\u95f7\u6b7b\u4e86\u3002\u201d \u3000\u3000\u3000\u94c1\u624b\u4ed9\u733f\u53c8\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u53ef\u66fe\u542c\u4f60\u7239\u7239\u8bf4\u8d77\u8fc7\uff0c\u90a3\u6709\u5173\u4e00\u4e00\u201d\u8bdd\u3000\u58f0\u672a \u4e86\uff0c\u7a81\u7136\u697c\u4e0b\u8857\u9053\u4e0a\u4f20\u6765\u4e00\u9635\u6025\u5267\u7684\u9a6c\u8e44\u58f0\uff0c\u4f3c\u4e4e\u8fd8\u4e0d\u6b62\u4e00\u5339\uff0c\u94c1\u624b\u4ed9\u733f\u9762\u4e0a\u3000\u500f\u7136\u9732\u51fa\u559c \u8272\uff0c\u56de\u8eab\u7a97\u53e3\u4e00\u4e0b\u770b\uff0c\u9152\u697c\u4e0b\u505c\u7740\u56db\u5339\u7a7a\u9a6c\uff0c\u6709\u4e24\u4e2a\u91d1\u8863\u7c97\u6c49\u5728\u638c\u7740\uff0c\u3000\u9a6c\u4e0a\u4eba\u4f3c\u5df2\u8d70\u4e86\u8fdb \u6765\u3002 \u3000\u3000\u3000\u63a5\u7740\uff0c\u697c\u68af\u8f7b\u54cd\uff0c\u8fde\u8882\u8d70\u4e0a\u4e86\u56db\u4eba\uff0c\u94c1\u624b\u4ed9\u733f\u4e00\u770b\uff0c\u5927\u559c\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u624d\u6765\u3000\u4e00 \u4e00\u201d\u6bdb\u6587\u742a\u5374\u201c\u6a31\u201d\u5730\u4e00\u58f0\uff0c\u6251\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u3000\u4e0a\u6765\u7684\u56db\u4eba\u5934\u4e00\u4f4d\u8eab\u6750\u7626\u524a\uff0c\u76ee\u5149\u5982\u9e70\uff0c\u6c14\u6d3e\u5728\u4e25\u5cfb\u4e2d\uff0c\u4ecd\u4e0d\u80fd\u63a9\u4f4f\u9634\u9e37\u4e4b\u6001\u3000\uff0c\u89c1 \u4e86\u6bdb\u6587\u742a\uff0c\u624d\u5fae\u5fae\u9732\u51fa\u4e00\u4e1d\u559c\u8272\uff0c\u9053\uff1a\u201c\u4f60\u600e\u5730\u4e5f\u5728\u8fd9\u91cc\uff1f\u201d\u4e0d\u95ee\u53ef\u77e5\uff0c\u6b64\u4eba\u3000\u4fbf\u662f\u8fd1\u5341\u5e74 \u6765\u8349\u83bd\u95f4\u7684\u9b41\u9996\uff0c\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u4e86\u3002 \u3000\u3000\u3000\u7b2c\u4e8c\u4eba\u80a9\u5bbd\u8170\u7a84\uff0c\u80cc\u810a\u633a\u5f97\u7b14\u76f4\uff0c\u867d\u77e5\u4e5f\u6709\u4e94\u5341\u4e0a\u4e0b\u4e86\uff0c\u4f46\u987e\u76fc\u4e4b\u95f4\uff0c\u795e\u91c7\u98de\u3000\u626c\uff0c \u5de6\u80a9\u540e\u5fae\u5fae\u9732\u51fa\u674f\u9ec4\u8272\u7684\u5251\u67c4\uff0c\u4ece\u4ed6\u8fd9\u80cc\u5251\u7684\u65b9\u5411\uff0c\u5c31\u53ef\u77e5\u9053\u6b64\u4eba\u6b63\u662f\u201c\u4e03\u5251\u3000\u4e09\u97ad\u201d\u4e2d\u7684 \u5b50\u6bcd\u53cc\u98de\u201c\u5de6\u624b\u795e\u5251\u201d\u4e01\u8863\u3002 \u3000\u3000\u3000\u7b2c\u4e09\u4eba\u5374\u662f\u4e2a\u5973\u5b50\uff0c\u4fcf\u751f\u751f\u5730\u6768\u67f3\u8170\uff0c\u767d\u7d20\u7d20\u5730\u6e05\u6c34\u8138\uff0c\u4e24\u53ea\u5927\u773c\u775b\u6c34\u6c6a\u6c6a\u7684\u3000\uff0c\u4e0d \u7b11\u7684\u65f6\u5019\u90fd\u4eff\u4f5b\u53ef\u4ee5\u770b\u5230\u4e24\u8fb9\u7684\u9152\u6da1\uff0c\u773c\u89d2\u867d\u6709\u6de1\u6de1\u7684\u9c7c\u5c3e\uff0c\u4f46\u5728\u7ec6\u5fc3\u7684\u4fee\u9970\u3000\u4e0b\uff0c\u5df2\u4e0d\u751a \u663e\u8457\u3002 \u3000\u3000\u3000\u8fd9\u8ba9\u4eba\u65e0\u6cd5\u731c\u900f\u5979\u7684\u5e74\u9f84\u7684\u5973\u5b50\uff0c\u5374\u6b63\u662f\u6614\u65e5\u70b9\u82cd\u638c\u95e8\u4eba\u4e4b\u59b9\u7684\u7231\u5f92\uff0c\u4eca\u65e5\u70b9\u3000\u82cd\u638c \u95e8\u4eba\u7684\u5e08\u59b9\uff0c\u767e\u6b65\u98de\u82b1\u6797\u7426\u7b5d\u3002 \u3000\u3000\u3000\u7f2a\u6587\u7684\u76ee\u5149\u5728\u8fd9\u4e09\u4eba\u8138\u4e0a\u4e00\u6e9c\u800c\u8fc7\uff0c\u5374\u505c\u7559\u5728\u7b2c\u56db\u4eba\u8138\u4e0a\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u539f\u6765\u8ddf\u3000\u5728\u6797 \u7426\u7b5d\u8eab\u540e\u7684\uff0c\u6b63\u662f\u516b\u9762\u73b2\u73d1\u80e1\u4e4b\u8f89\u3002 \u3000\u3000\u3000\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u5306\u5306\u548c\u6bdb\u6587\u742a\u4f4e\u8bed\u4e86\u4e24\u53e5\uff0c\u76ee\u5149\u5411\u56db\u5468\u4e00\u626b\uff0c\u7765\u7768\u4e4b\u95f4\uff0c\u5012\u4e5f\u6709\u3000\u51e0\u5206 \u201c\u6b66\u6797\u9b41\u9996\u201d\u7684\u59ff\u6001\u3002 \u3000\u3000\u3000\u800c\u5728\u5ea7\u7684\u7fa4\u8c6a\uff0c\u4e5f\u4e0d\u50cf\u5148\u524d\u89c1\u4e86\u94c1\u624b\u4ed9\u733f\u65f6\u4e00\u6837\uff0c\u9664\u4e86\u7a77\u795e\u51cc\u9f99\uff0c\u58a8\u4e00\u4e0a\u4eba\u7b49\u3000\u6709\u9650 \u51e0\u4eba\u4e4b\u5916\uff0c\u4e5f\u90fd\u7ad9\u4e86\u8d77\u6765\u62f1\u624b\u4e3a\u793c\u3002 \u3000\u3000\u3000\u987b\u77e5\u5341\u4f59\u5e74\u524d\u201c\u4e03\u5251\u4e09\u97ad\u201d\u5728\u6c5f\u6e56\u4e2d\u7684\u540d\u5934\u5df2\u6781\u54cd\u4eae\uff0c\u6b64\u65f6\u6bdb\u81ec\u7684\u8eab\u4efd\u66f4\u662f\u5927\u3000\u4e0d\u76f8 \u540c\uff0c\u94c1\u624b\u4ed9\u733f\u867d\u4e5f\u662f\u6210\u540d\u7acb\u4e07\u7684\u4eba\u7269\uff0c\u4f46\u548c\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u4e00\u6bd4\uff0c\u8eab\u5206\u3001\u5730\u4f4d\uff0c\u3000\u90fd\u5dee\u4e86\u5f88 \u8fdc\u3002 \u3000\u3000\u3000\u7075\u86c7\u6bdb\u684c\u76ee\u5149\u4e00\u626b\u4e4b\u95f4\uff0c\u7709\u5934\u5fae\u5fae\u4e00\u76b1\uff0c\u5927\u6982\u4ed6\u4e5f\u60f3\u4e0d\u5230\u4f1a\u6709\u8fd9\u51e0\u4e2a\u624e\u624b\u7684\u4eba\u3000\u7269\u5728 \u5ea7\uff0c\u4f46\u53cc\u7738\u968f\u5373\u4e00\u5c55\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6bdb\u684c\u6765\u8fdf\uff0c\u81f4\u52b3\u5404\u4f4d\u670b\u53cb\u548c\u524d\u8f88\u4e45\u5019\uff0c\u8be5\u3000\u6b7b\u8be5\u6b7b\u3002\u201d \u4ed6\u4e00\u987e\u4faf\u6797\uff1a\u201c\u8001\u56db\uff0c\u4f60\u600e\u4e48\u4e5f\u4e0d\u66fe\u544a\u8bc9\u6211\uff0c\u4e0d\u4f46\u8427\u3001\u51cc\u4e24\u4f4d\u8001\u524d\u8f88\u6765\u3000\u4e86\uff0c\u5c11\u6797\u795e\u50e7\u4e5f\u6765 \u4e86\u4e00\u4f4d\uff0c\u5426\u5219\u6bdb\u81ec\u5929\u5927\u7684\u80c6\uff0c\u4e5f\u4e0d\u6562\u52b3\u52a8\u5404\u4f4d\u8001\u524d\u8f88\u7684\u4fa0\u9a7e\uff0c\u5728\u3000\u6b64\u4e45\u5019\u3002\u201d \u3000\u3000\u3000\u8fd9\u7075\u86c7\u679c\u7136\u7075\u6781\uff0c\u5c31\u8fd9\u4e00\u987e\u4e4b\u95f4\uff0c\u5df2\u5c06\u8fd9\u51e0\u4eba\u90fd\u8ba4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u3000\u7a77\u795e\u51cc\u9f99\u5927\u7b11\u9053\uff1a\u201c\u5c0f\u6bdb\u5b50\uff0c\u6211\u4eec\u53ef\u4e0d\u662f\u7b49\u4f60\uff0c\u4f60\u4e5f\u522b\u96be\u53d7\u3002\uff0c\uff0c\u4ed6\u76ee\u5149\u5fae\u5fae\u3000\u4e00 \u51db\uff1a\u201c\u6211\u4eec\u7b49\u7684\u662f\u4ec0\u4e48\uff0c\u4e0d\u7528\u8bf4\uff0c\u4f60\u4e5f\u8be5\u77e5\u9053\u5427\uff1f\u201d \u3000\u3000\u3000\u6bdb\u81ec\u7b11\u58f0\u672a\u4f4f\uff0c\u4e00\u6b65\u8fc8\u5230\u684c\u524d\uff0c\u5c06\u7f2a\u6587\u9762\u524d\u7684\u9152\u676f\u62ff\u4e86\u8d77\u6765\uff0c\u8eab\u5b50\u4e00\u8f6c\uff0c\u5927\u7b11\u3000\u7740 \u9053\uff1a\u201c\u522b\u7684\u4e8b\u4e14\u6162\u8bf4\uff0c\u6bdb\u81ec\u5148\u656c\u5404\u4f4d\u4e00\u676f\u3002\u201d\u62ff\u7740\u9152\u676f\uff0c\u76ee\u5149\u56db\u5c04\uff0c\u8fde\u706b\u773c\u91d1\u96d5\u3000\u8427\u8fdf\u90fd\u7ad9 \u4e86\u8d77\u6765\uff0c\u6bdb\u81ec\u54c8\u54c8\u4e00\u7b11\uff0c\u4ef0\u5934\u4e00\u996e\u800c\u5c3d\u3002 \u3000\u3000\u3000\u6bdb\u6587\u742a\u5fc3\u91cc\u6709\u4e9b\u5f97\u610f\uff0c\u4eba\u5bb6\u5bf9\u4ed6\u7239\u7239\uff0c\u65e0\u8bba\u5982\u4f55\u8fd8\u662f\u770b\u91cd\u7684\uff0c\u76ee\u5149\u4e00\u8f6c\uff0c\u8f6c\u5230\u3000\u7f2a\u6587 \u8eab\u4e0a\uff0c\u5374\u89c1\u4ed6\u4ecd\u7aef\u5750\u672a\u52a8\u3002 \u3000\u3000\u3000\u5979\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6084\u6084\u8d70\u4e86\u8fc7\u53bb\uff0c\u4f4e\u8bed\u9053\uff1a\u201c\u7239\u7239\u656c\u9152\uff0c\u4f60\u600e\u4e48\u4e0d\u559d\u5440\uff1f\u201d\u58f0\u97f3\u91cc\u3000\u6709\u51e0 \u5206\u57cb\u6028\uff0c\u51e0\u5206\u8d23\u602a\uff0c\u5374\u4e5f\u6709\u7740\u51e0\u5206\u6000\u7591\u3002 \u3000\u3000\u3000\u7f2a\u6587\u5fae\u5fae\u4e00\u8038\u80a9\uff0c\u7b11\uff1a\u201c\u6211\u7684\u9152\u676f\u7ed9\u4f60\u7239\u7239\u62ff\u8d70\u4e86\uff0c\u6211\u559d\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u3000\u6bdb\u6587\u742a\u4e00\u7b11\uff0c\u5c06\u65c1\u8fb9\u7a7a\u7740\u7684\u5ea7\u4f4d\u4e0a\u7684\u4e00\u676f\u9152\u9001\u7ed9\u7f2a\u6587\uff0c\u4f46\u8fd9\u65f6\u7fa4\u8c6a\u90fd\u5df2\u843d\u5ea7\uff0c\u3000\u6bdb\u81ec \u7684\u9152\u4e5f\u65e9\u559d\u5e72\u4e86\uff0c\u7f2a\u6587\u62ff\u7740\u9152\u676f\uff0c\u4ef0\u9996\u4e00\u996e\uff0c\u4f46\u8fd9\u676f\u5b50\u5374\u6839\u672c\u6ca1\u6709\u5012\u9152\uff0c\u6839\u672c\u3000\u5c31\u662f\u7a7a\u7684\u3002 \u3000\u3000\u3000\u53ea\u662f\u6bdb\u6587\u742a\u4e0d\u66fe\u6ce8\u610f\uff0c\u4e5f\u4e0d\u66fe\u5728\u610f\u7f62\u4e86\u3002 \u3000\u3000\u3000\u7075\u86c7\u6bdb\u81ec\u7765\u7768\u4f5c\u6001\uff0c\u6717\u58f0\u9053\uff1a\u201c\u4eca\u65e5\u5bb4\u805a\u7fa4\u8c6a\uff0c\u6bdb\u81ec\u606d\u4e3a\u4e3b\u4eba\u3002\u201d\u4ed6\u76ee\u5149\u671d\u7a77\u3000\u795e\u51cc \u9f99\u4e00\u626b\uff0c\u4e00\u7b11\uff1a\u201c\u4e0d\u7ba1\u5404\u4f4d\u662f\u4e3a\u7740\u4ec0\u4e48\u539f\u56e0\u6765\u7684\uff0c\u6211\u6bdb\u81ec\u603b\u662f\u9ad8\u5174\u5f97\u5f88\u3002\u201d \u3000\u3000\u3000\u4ed6\u5fae\u5fae\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u65e5\u524d\u6211\u4faf\u56db\u5f1f\u5feb\u9a6c\u9001\u4fe1\u81f3\u676d\u5dde\uff0c\u8bf4\u662f\u5df2\u5c06\u5c71\u897f\u592a\u884c\u5c71\u7684\u3000\uff1a\u592a \u884c\u53cc\u4e49\u2019\u548c\u2018\u6d2a\u6cfd\u2019\u2018\u9ad8\u90ae\u2019\u5341\u4e03\u8def\u6c34\u5be8\u7684\u603b\u74e2\u628a\u5b50\u91d1\u9ca4\u8427\u5c11\u4fa0\u8bf7\u4e86\u6765\uff0c\u6bdb\u81ec\u3000\u6b63\u81ea\u9ad8\u5174\uff0c \u54ea\u77e5\u9053\u7b2c\u4e8c\u5929\u6bdb\u81ec\u53c8\u63a5\u5230\u6211\u4faf\u56db\u5f1f\u7684\u5feb\u62a5\uff0c\u5728\u6d2a\u6cfd\u3001\u9ad8\u90ae\u4e24\u6e56\u4e4b\u95f4\uff0c\u53d1\u3000\u73b0\u4e86\u4e00\u4e2a\u5df2\u57cb\u85cf\u6570 \u767e\u5e74\u7684\u79d8\u5bc6\u3002\u201d\u4ed6\u76ee\u5149\u518d\u4e00\u626b\uff0c\u4e00\u7b11\u53c8\u9053\uff1a\u201c\u5404\u4f4d\u90fd\u662f\u660e\u773c\u4eba\uff0c\u3000\u51e0\u4f4d\u8001\u524d\u8f88\u77e5\u9053\u8fd9\u4e8b\u66f4\u6bd4 \u6bdb\u81ec\u6e05\u695a\uff0c\u6bdb\u81ec\u660e\u4eba\u9762\u524d\u4e0d\u8bf4\u6697\u8bdd\uff0c\u6c89\u4e8e\u6d2a\u6cfd\u3001\u9ad8\u90ae\u4e24\u6e56\u3000\u95f4\u7684\u90a3\u4e9b\u4e1c\u897f\uff0c\u5929\u4e0b\u4eba\u65e0\u4e0d\u60f3\u5f97 \u5230\uff0c\u6bdb\u81ec\u81ea\u4e5f\u4e0d\u4f8b\u5916\uff0c\u4f46\u5148\u8bf4\u6bdb\u81ec\u662f\u4e3a\u4e86\u8fd9\u4e9b\u4e1c\u897f\u3000\u800c\u60f3\u7ed3\u4ea4\u91d1\u9ca4\u8427\u5c11\u4fa0\uff0c\u90a3\u5374\u662f\u51a4\u6789\u4e86\u6bdb \u81ec\uff0c\u82e5\u5982\u6b64\u8bf4\uff0c\u5728\u5ea7\u7684\u592a\u884c\u91d1\u4e8c\u4fa0\uff0c\u6dee\u9633\u5c71\u3000\u7684\u6d82\u5927\u4fa0\uff0c\u4f0f\u725b\u5c71\u7684\u5357\u53ec\u5251\u5ba2\uff0c\u65b9\u57ce\u5927\u4fa0\uff0c\u4ed6 \u4eec\u7684\u8f96\u533a\u4e2d\u5e76\u65e0\u79d8\u85cf\uff0c\u6bdb\u81ec\u4e0d\u4e5f\u4e00\u6837\u3000\u8bf7\u4e86\u6765\u3002\u201d \u3000\u3000\u3000\u3000\u3000\u7a77\u795e\u51cc\u9f99\u4eff\u4f5b\u54fc\u4e86\u4e00\u58f0\uff0c\u58a8\u4e00\u4e0a\u4eba\u5374\u4ecd\u7aef\u5750\u672a\u52a8\uff0c\u6bdb\u81ec\u53c8\u9053\uff1a\u201c\u6bdb\u81ec\u5f97\u5230\u8fd9\u6d88\u3000 \u606f\u540e\uff0c\u5c31\u548c\u5de6\u624b\u795e\u5251\u4e01\u5927\u4fa0\uff0c\u767e\u6b65\u98de\u82b1\u6797\u5973\u4fa0\uff0c\u548c\u6211\u80e1\u4e09\u5f1f\u517c\u7a0b\u800c\u6765\uff0c\u56e0\u4e3a\u6bdb\u81ec\u77e5\u3000\u9053\u8fd9\u8baf \u606f\u53ea\u8981\u7a0d\u4e3a\u9732\u51fa\u4e00\u70b9\uff0c\u5c31\u96be\u514d\u8981\u60ca\u52a8\u522b\u4eba\uff0c\u6211\u4faf\u56db\u5f1f\u867d\u7136\u8c28\u614e\uff0c\u4f46\u5982\u5404\u4f4d\u8001\u3000\u524d\u8f88\uff0c\u8fd8\u662f\u4e00 \u6837\u4f1a\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u3000\u7a77\u795e\u51cc\u9f99\u7a81\u7136\u6717\u58f0\u957f\u7b11\uff0c\u6e05\u8d8a\u7684\u957f\u7b11\u58f0\uff0c\u9707\u5f97\u4ed6\u9762\u524d\u7684\u676f\u76cf\u76f4\u6b32\u98de\u53bb\u3002\u767e\u6b65\u98de\u3000\u82b1\u6797 \u7426\u7b5d\u7a81\u7136\u8f7b\u8f7b\u7b11\u4e86\u8d77\u6765\uff0c\u5a07\u58f0\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u4f60\u8001\u4eba\u5bb6\u7b11\u5f97\u58f0\u97f3\u8fd9\u6837\u5927\uff0c\u628a\u4eba\u3000\u5bb6\u7684\u8033\u6735\u90fd \u5feb\u9707\u7834\u4e86\u3002\u201d \u3000\u3000\u3000\u7a77\u795e\u51cc\u9f99\u7b11\u58f0\u987f\u4f4f\uff0c\u53cc\u76ee\u4e2d\u7cbe\u5149\u66b4\u5c04\uff0c\u72e0\u72e0\u4e00\u77aa\u6797\u7426\u7b5d\uff0c\u6797\u7426\u7b5d\u5374\u4ecd\u82e6\u82e5\u65e0\u5176\u3000\u4e8b\u5730 \u51b2\u7740\u4ed6\u5a07\u7b11\u7740\uff0c\u7ea4\u624b\u4e00\u7406\u9b13\u811a\uff0c\u4fcf\u58f0\u9053\uff1a\u201c\u51cc\u8001\u524d\u8f88\u7684\u2018\u6df7\u5143\u4e00\u6c14\u2019\u529f\uff0c\u6211\u8fd8\u3000\u5c0f\u7684\u65f6\u5019\u5c31 \u5982\u96f7\u704c\u8033\u4e86\uff0c\u6c5f\u6e56\u4e0a\u8c01\u4e0d\u77e5\u9053\uff0c\u4f60\u8001\u4eba\u5bb6\u5c31\u662f\u4e0d\u8fd9\u6837\u7b11\uff0c\u4eba\u5bb6\u4e5f\u77e5\u9053\u7684\u3000\u3002\u201d \u3000\u3000\u3000\u7a77\u795e\u51cc\u9f99\u4e00\u751f\u8a00\u8bcd\u950b\u5229\uff0c\u53e3\u820c\u4e0a\u672a\u66fe\u8f93\u8fc7\u4eba\u5bb6\u534a\u7b79\uff0c\u6b64\u523b\u5374\u9047\u7740\u4e86\u53e3\u820c\u6bd4\u4ed6\u66f4\u3000\u950b\u5229 \u7684\u4eba\uff0c\u800c\u4e14\u8fd8\u662f\u4e2a\u5973\u5b50\uff0c\u6bdb\u6587\u742a\u773c\u770b\u65b9\u624d\u8c03\u4f83\u9b4f\u51cc\u98ce\u65f6\u7684\u60c5\u51b5\uff0c\u6b64\u523b\u4e0d\u89c9\u597d\u7b11\u3000\uff0c\u201c\u771f\u662f\u62a5 \u5e94\u3002\u201d \u3000\u3000\u3000\u6797\u7426\u7b5d\u4eff\u4f5b\u77e5\u9053\u4ee5\u4eba\u5bb6\u7684\u8eab\u4efd\uff0c\u7edd\u5bf9\u4e0d\u4f1a\u548c\u81ea\u5df1\u52a8\u624b\uff0c\u662f\u4ee5\u5979\u8bdd\u8bf4\u8fc7\u4e86\uff0c\u4ecd\u7136\u3000\u5fc3\u5b89 \u7406\u5f97\uff0c\u5a9a\u76ee\u6d41\u6ce2\uff0c\u770b\u5230\u7f2a\u6587\uff0c\u5374\u8f7b\u8f7b\u4e00\u7b11\u3002 \u3000\u3000\u3000\u7f2a\u6587\u4e5f\u4e00\u7b11\uff0c\u4e00\u7b11\u4e4b\u4e0b\uff0c\u66f4\u89c9\u4fca\u9038\uff0c\u6bdb\u6587\u742a\u7684\u8138\u4e0a\u7acb\u523b\u7f69\u4e0a\u4e00\u5c42\u5bd2\u971c\uff0c\u809a\u91cc\u6697\u3000\u6697\u9a82 \u7740\uff1a\u201c\u8001\u72d0\u72f8\uff01\u8001\u5996\u602a\uff01\u201d \u3000\u3000\u3000\u88ab\u6797\u7426\u7b5d\u8fd9\u4e48\u4e00\u6270\uff0c\u7a77\u795e\u51cc\u9f99\u5df2\u5230\u5634\u8fb9\u7684\u8bdd\u51e0\u4e4e\u54bd\u4e86\u56de\u53bb\uff0c\u7075\u86c7\u6bdb\u81ec\u5374\u4e58\u6b64\u673a\u3000\u4f1a\u53c8 \u9053\uff1a\u201c\u5404\u4f4d\u8001\u524d\u8f88\u6b64\u523b\u548c\u6bdb\u684c\u5f53\u9762\u4e00\u89c1\uff0c\u6bdb\u81ec\u5374\u653e\u5fc3\u4e86\uff0c\u56e0\u4e3a\u5c31\u51ed\u5404\u4f4d\u8001\u524d\u8f88\u3000\u7684\u58f0\u671b\uff0c\u7edd \u5bf9\u4e0d\u4f1a\u5c06\u6211\u5144\u5f1f\u5bfb\u5f97\u4e4b\u7269\u5982\u4f55\u7684\u2014\u2014\u201d\u4ed6\u8bdd\u58f0\u672a\u4e86\uff0c\u53c8\u6709\u4e00\u9635\u5639\u4eae\u7684\u7b11\u3000\u58f0\u54cd\u8d77\uff0c\u5374\u662f\u706b\u773c \u91d1\u96d5\u53d1\u51fa\u7684\u3002\u4ed6\u81ea\u59cb\u81f3\u7ec8\uff0c\u4e00\u8a00\u672a\u53d1\uff0c\u6b64\u523b\u5374\u6253\u7740\u957f\u9aef\u9053\uff1a\u201c\u6bdb\u3000\u5927\u4fa0\u7684\u8bdd\uff0c\u6211\u8001\u5934\u5b50\u6709\u4e9b \u4e0d\u61c2\uff0c\u65e5\u524d\u94c1\u624b\u4ed9\u733f\u4faf\u56db\u4fa0\u5230\u5c0f\u513f\u8427\u5e73\u7684\u6c34\u5be8\u4e2d\u53bb\uff0c\u8bf4\u662f\u3000\u4eca\u65e5\u6b66\u6797\u6c34\u3001\u9646\u4e24\u9053\uff0c\u754c\u7ebf\u5206\u5f97 \u592a\u6e05\uff0c\u8fd9\u4e48\u6709\u5931\u5929\u4e0b\u6b66\u6797\u4e00\u5bb6\u7684\u672c\u610f\uff0c\u8bf4\u662f\u6c34\u9646\u5e94\u3000\u8be5\u8054\u76df\uff0c\u5c0f\u513f\u5e94\u8be5\u548c\u6bdb\u5927\u4fa0\u7ed3\u4e3a\u76df\u53cb\uff0c \u5c0f\u5f1f\u8003\u8651\u4e4b\u4e0b\uff0c\u4e00\u6765\u8ba4\u4e3a\u6bdb\u5927\u4fa0\u662f\u4e2a\u4eba\u7269\uff0c\u3000\u4e8c\u6765\u4e5f\u662f\u4faf\u56db\u4fa0\u7684\u8bdd\u7684\u786e\u975e\u5e38\u4e2d\u80af\uff0c\u56e0\u6b64\u5c31\u7b54 \u5e94\u4e86\uff0c\u4faf\u56db\u4fa0\u53c8\u77e5\u9053\u5c71\u897f\u7684\u592a\u884c\u53cc\u4e49\u3000\u662f\u5c0f\u513f\u7684\u7ed3\u4e49\u5144\u5f1f\uff0c\u60f3\u62c9\u592a\u884c\u53cc\u4e49\u4e00\u9f50\uff0c\u5c0f\u513f\u4e5f\u7b54\u5e94 \u4e86\u4e00\u4e00\u4e00\u201d\u4ed6\u773c\u671b\u7740\u94c1\u624b\u4ed9\u733f\u3000\uff0c\u8f7b\u8f7b\u4e00\u58f0\u51b7\u7b11\uff0c\u53c8\u63a5\u7740\u5f80\u4e0b\u8bf4\u9053\uff1a\u201c\u8fd9\u5f53\u7136\u662f\u56e0\u4e3a\u4faf\u56db\u4fa0 \u6709\u82cf\u79e6\u4e4b\u624d\uff0c\u5f20\u4eea\u4e4b\u820c\u3000\uff0c\u820c\u5e95\u751f\u83b2\uff0c\u5c06\u5c0f\u513f\u8bf4\u5f97\u4e94\u4f53\u6295\u5730\uff0c\u518d\u8005\u4e48\u2014\u2014\u201d\u4ed6\u53c8\u51b7\u51b7\u4e00\u7b11\uff0c \u9053\uff1a\u201c\u5374\u662f\u56e0\u4e3a\u3000\u5c0f\u513f\u5e74\u8f7b\u8bc6\u6d45\uff0c\u5c1a\u5206\u4e0d\u51fa\u597d\u6b79\uff0c\u800c\u6211\u8001\u5934\u5b50\u5f53\u7136\u53c8\u4e0d\u5728\u5be8\u4e2d\uff0c\u7b49\u5230\u6211\u8001\u5934 \u5b50\u56de\u6765\u65f6\u3000\uff0c\u4faf\u56db\u4fa0\u5df2\u7ecf\u8d70\u4e86\uff0c\u8fd9\u4e14\u4e0d\u8bf4\uff0c\u4f46\u6211\u8001\u5934\u5b50\u867d\u7136\u5e74\u7eaa\u6d3b\u4e86\u8fd9\u4e48\u4e00\u5927\u628a\uff0c\u89c1\u8fc7\u7684\u597d \u4eba\u3000\u574f\u4eba\u4e5f\u6709\u4e0d\u5c11\u4e86\uff0c\u5374\u8fd8\u60f3\u4e0d\u5230\u4faf\u56db\u4fa0\u7adf\u5982\u6b64\u9ad8\u660e\uff0c\u82e5\u4e0d\u662f\u5f97\u5230\u4e00\u4f4d\u5947\u4eba\u7684\u901a\u77e5\uff0c\u6211\u3000\u8001 \u5934\u5b50\u8fd8\u4e0d\u77e5\u9053\u7d20\u6765\u6807\u699c\u4e3a\u6b66\u6797\u4e3b\u6301\u516c\u9053\uff0c\u6b63\u4e49\u7684\u94c1\u9a91\u795e\u97ad\u7684\u7edf\u9886\u4faf\u56db\u4fa0\uff0c\u7adf\u85c9\u7740\u3000\u9ad8\u3001\u6d2a\u6c34 \u5be8\u603b\u8235\u4e3b\u8d35\u5bbe\u7684\u8eab\u4efd\uff0c\u5c06\u655d\u6e56\u7684\u79d8\u85cf\u63a2\u6d4b\u4e86\u53bb\u3002\u201d \u3000\u3000\u3000\u59dc\u662f\u8001\u7684\u8fa3\uff0c\u8fd9\u4f4d\u95ef\u8361\u6c5f\u6e56\u6570\u5341\u5e74\uff0c\u8001\u5f97\u4e0d\u80fd\u518d\u8001\u7684\u6c5f\u6e56\uff0c\u6ed4\u6ed4\u4e00\u5e2d\u8bdd\uff0c\u8bf4\u5f97\u3000\u94c1\u624b \u4ed9\u733f\u8138\u4e0a\u9752\u4e00\u9635\uff0c\u767d\u4e00\u9635\uff0c\u4f7f\u5f97\u4ed6\u672c\u6765\u5c31\u4e0d\u6562\u606d\u7ef4\u7684\u9762\u5b54\uff0c\u6b64\u523b\u8981\u53d8\u5f97\u6709\u4e9b\u50cf\u3000\u98ce\u5e72\u4e86\u7684\u6a58 \u5b50\u76ae\u6ce1\u5728\u9648\u918b\u91cc\u9762\u7684\u5473\u9053\u3002 \u3000\u3000\u3000\u7075\u86c7\u6bdb\u81ec\uff0c\u4e5f\u81ea\u9762\u76ee\u53d8\u8272\uff0c\u6b63\u5f85\u53d1\u8a00\uff0c\u90a3\u8427\u8001\u96d5\u5374\u53c8\u9053\uff1a\u201c\u6211\u8001\u5934\u5b50\u4eca\u65e5\u6765\u89c1\u3000\u6bdb\u5927 \u4fa0\uff0c\u5c31\u662f\u4e3a\u4e86\u8981\u544a\u8bc9\u6bdb\u5927\u4fa0\u4e00\u53e5\u8bdd\uff0c\u9ad8\u3001\u6d2a\u4e24\u6e56\u7684\u79d8\u85cf\uff0c\u6bdb\u5927\u4fa0\u66a8\u5144\u5f1f\u867d\u7136\u5df2\u3000\u7ecf\u77e5\u9053\u4e86\u786e \u5b9e\u5730\u70b9\uff0c\u800c\u6211\u8001\u5934\u5b50\u90fd\u4e0d\u77e5\u9053\uff0c\u4f46\u662f\u6bdb\u5927\u4fa0\u82e5\u8fd8\u60f3\u85c9\u7740\u201d\u76df\u53cb\u201d\u7684\u8eab\u4efd\u3000\uff0c\u5230\u6211\u6c34\u5be8\u4e2d\u53bb\u63a2 \u5b9d\uff0c\u90a3\u4e48\u4f60\u6bdb\u5927\u4fa0\u7eb5\u7136\u662f\u4e2d\u539f\u9646\u4e0a\u6b66\u6797\u76df\u4e3b\u7684\u8eab\u4efd\uff0c\u6211\u8001\u5934\u5b50\u4e5f\u3000\u8981\u51ed\u7740\u6211\u8427\u5bb6\u7956\u5b59\u56db\u4ee3\u5728 \u6c34\u9762\u4e0a\u7684\u4e00\u70b9\u529b\u91cf\uff0c\u548c\u4f60\u6bdb\u5927\u4fa0\u5468\u65cb\u4e00\u4e0b\uff0c\u6211\u8001\u5934\u5b50\u5728\u5c11\u3000\u6797\u795e\u50e7\u548c\u6b66\u6797\u795e\u4e10\u9762\u524d\uff0c\u4e5f\u4e0d\u6562 \u592a\u8fc7\u653e\u8086\uff0c\u662f\u4ee5\u5c06\u5e26\u6765\u6b64\u95f4\u7684\u6570\u767e\u4e2a\u5feb\u5200\u5f1f\u5144\u548c\u6c34\u3000\u4e0a\u7684\u5f1f\u5144\u4eec\uff0c\u90fd\u5148\u9063\u4e86\u56de\u53bb\uff0c\u8a00\u5c3d\u4e8e \u6b64\uff0c\u6211\u8001\u5934\u5b50\u5c31\u6b64\u544a\u522b\u3002\u201d \u3000\u3000\u3000\u6bdb\u6587\u742a\u81f3\u6b64\uff0c\u624d\u7b97\u7a0d\u4e3a\u6478\u7740\u4e86\u4eca\u65e5\u6b64\u4e8b\u7684\u4e00\u4e9b\u7aef\u502a\uff0c\u867d\u7136\u4ecd\u4e0d\u751a\u6e05\u695a\uff0c\u4e5f\u5f04\u4e0d\u3000\u6e05\u8fd9 \u6d2a\u6cfd\u3001\u9ad8\u90ae\u6e56\u4e2d\u7684\u79d8\u85cf\uff0c\u5012\u662f\u4f55\u7269\uff0c\u66f4\u5f04\u4e0d\u6e05\u94c1\u624b\u4ed9\u733f\u662f\u600e\u4e48\u77e5\u9053\u8fd9\u4e9b\u7684\uff0c\u4f46\u3000\u542c\u4e86\u8427\u8001\u96d5 \u7684\u8bdd\uff0c\u521a\u5fcd\u4e0d\u4f4f\u8981\u8bf4\u8bdd\uff0c\u90a3\u767e\u6b65\u98de\u82b1\u6797\u7426\u7b5d\u53c8\u54af\u54af\u5a07\u7b11\u7740\u9053\uff1a\u201c\u8427\u8001\u7237\u3000\u5b50\uff0c\u4f60\u8001\u4eba\u5bb6\u53ef\u662f \u5fb7\u9ad8\u671b\u91cd\u7684\u4eba\uff0c\u4e0d\u8fc7\u4f60\u8001\u4eba\u5bb6\u7684\u8bdd\u6211\u4e5f\u6709\u4e9b\u4e0d\u61c2\uff0c\u7167\u4f60\u8001\u4eba\u5bb6\u7684\u3000\u8bf4\u6cd5\uff0c\u90a3\u6d2a\u6cfd\u9ad8\u90ae\u6e56\u7adf\u7b97 \u662f\u4f60\u4eec\u8427\u5bb6\u7684\u4e86\uff0c\u6e56\u91cc\u66f2\u7684\u4e1c\u897f\uff0c\u9664\u4e86\u8427\u5bb6\u7684\u4eba\u4e4b\u5916\uff0c\u8fd8\u3000\u90fd\u4e0d\u80fd\u52a8\u5417\uff1f\u201d \u3000\u3000\u3000\u8427\u8001\u96d5\u53cc\u76ee\u4e00\u5f20\uff0c\u4e24\u9053\u957f\u957f\u7684\u5bff\u7709\uff0c\u50cf\u9488\u4e00\u6837\u7acb\u4e86\u8d77\u6765\uff0c\u5389\u53f1\u9053\uff1a\u201c\u662f\u53c8\u600e\u6837\u3000\uff1f\u4e0d \u662f\u53c8\u600e\u6837\uff1f\u4f60\u8981\u5728\u6211\u9762\u524d\u5145\u5b57\u53f7\uff0c\u4f60\u8fd8\u5dee\u5f97\u8fdc\u5462\uff0c\u53bb\u53bb\uff01\u8fd9\u513f\u53ef\u6ca1\u6709\u4f60\u8fd9\u79cd\u5973\u3000\u5b50\u8bf4\u8bdd\u7684\u5730 \u65b9\uff01\u201d \u3000\u3000\u3000\u8981\u77e5\u7a77\u795e\u51cc\u9f99\u867d\u7136\u6e38\u620f\u4eba\u95f4\uff0c\u73a9\u4e16\u4e0d\u606d\uff0c\u4f46\u5374\u662f\u6b63\u6d3e\u4e2d\u6709\u6570\u7684\u51e0\u4e2a\u957f\u8001\u4e4b\u4e00\uff0c\u3000\u662f\u4ee5 \u5bf9\u6797\u7426\u7b5d\u7684\u8bdd\uff0c\u53ea\u80fd\u5fcd\u4e0b\uff0c\u5e76\u975e\u4e0d\u80fd\uff0c\u4e43\u4e0d\u5c51\u4e8e\u6b64\u4e5f\u3002 \u3000\u3000\u3000\u4f46\u8427\u8001\u96d5\u53ef\u4e0d\u540c\u4e86\uff0c\u4ed6\u867d\u7136\u4e5f\u662f\u6b66\u6797\u524d\u8f88\uff0c\u4f46\u7ec8\u7a76\u662f\u9ed1\u9053\u4e2d\u4eba\uff0c\u662f\u4ee5\u51b2\u7740\u540e\u8f88\u3000\u5973\u5b50 \u53d1\u5a01\uff0c\u77aa\u773c\uff0c\u4e5f\u65e0\u6240\u8c13\u3002 \u3000\u3000\u3000\u6797\u7426\u7b5d\u8fd9\u4e00\u4e0b\u78b0\u4e86\u9489\u5b50\uff0c\u9762\u5b50\u4e0a\u53ef\u4e0b\u4e0d\u6765\uff0c\u5c24\u5176\u7f2a\u6587\u5728\u90a3\u91cc\u4f3c\u7b11\u975e\u7b11\u5730\u671b\u7740\u5979\u3000\uff0c\u5979 \u5e74\u9f84\u3001\u8f88\u4efd\uff0c\u867d\u7136\u90fd\u6bd4\u8427\u8001\u96d5\u5dee\u7740\u4e00\u8282\uff0c\u4f46\u5728\u6b66\u6797\u4e2d\u7684\u8eab\u4efd\uff0c\u5374\u4e5f\u672a\u89c1\u5f97\u4f4e\u4e8e\u3000\u8427\u8001\u96d5\u591a \u5c11\u3002 \u3000\u3000\u3000\u5979\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5929\u4e0b\u4eba\u4e4b\u7269\uff0c\u5929\u4e0b\u4eba\u6562\u5f97\uff0c\u5230\u65f6\u5019\u6bdb\u5927\u54e5\u4e0d\u53bb\u53d6\uff0c\u6211\u6797\u59d1\u3000\u5a18\u5012 \u504f\u8981\u53bb\u770b\u770b\u4f60\u8fd9\u53ea\u8001\u96d5\u6709\u591a\u5927\u7684\u5a01\u98ce\uff1f\u591a\u5927\u7684\u672c\u4e8b\u201d\u8427\u8001\u96d5\u4e00\u62cd\u684c\u5b50\uff0c\u5750\u5728\u4ed6\u3000\u8eab\u4fa7\u7684\u4e00\u4e2a \u4e09\u5341\u5de6\u53f3\u7684\u7cbe\u7ec3\u6c49\u5b50\u5530\u5730\u7ad9\u4e86\u8d77\u6765\uff0c\u6ee1\u8138\u4ff1\u662f\u6012\u5bb9\uff0c\u6b64\u4eba\u6b63\u662f\u9ad8\u90ae\u3001\u6d2a\u3000\u6cfd\u4e24\u6e56\uff0c\u5341\u4e03\u4e2a\u8fde \u73af\u6c34\u5be8\u7684\u603b\u8235\u4e3b\uff0c\u6c34\u4e0a\u540d\u95e8\u201c\u8427\u5bb6\u201d\u7684\u7b2c\u56db\u4ee3\uff0c\u91d1\u9ca4\u8427\u5e73\u3002 \u3000\u3000\u3000\u4ed6\u4e4b\u610f\u601d\uff0c\u5f53\u7136\u662f\u4ee3\u7236\u51fa\u624b\uff0c\u54ea\u77e5\u7a77\u795e\u51cc\u9f99\u53c8\u6717\u58f0\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u65b9\u624d\u8427\u8001\u96d5\u4e00\u3000\u8bf4\uff0c \u6211\u8001\u8981\u996d\u7684\u624d\u77e5\u9053\u4f60\u4eec\u4e4b\u95f4\u6709\u8fd9\u4e48\u4e00\u6bb5\u66f2\u6298\uff0c\u53ef\u662f\u8bf4\u6765\u8bf4\u53bb\uff0c\u4f60\u4eec\u53ef\u77e5\u9053\u8fd9\u7a76\u3000\u7adf\u662f\u600e\u4e48\u56de \u4e8b\u5462\uff1f\u201d \u3000\u3000\u3000\u4ed6\u76ee\u5149\u6ce8\u5b9a\u5728\u6bdb\u81ec\u8eab\u4e0a\uff0c\u53c8\u9053\uff1a\u201c\u65b9\u624d\u5c0f\u7334\u5b50\u8bf4\u4ed6\u4ec0\u4e48\u4e8b\u90fd\u4e0d\u80fd\u505a\u4e3b\uff0c\u73b0\u5728\u4f60\u3000\u8be5\u662f \u80fd\u505a\u4e3b\u4e86\u5427\uff1f\u6211\u4e14\u95ee\u4f60\uff0c\u4f60\u4ece\u2018\u7a77\u5bb6\u5e2e\u2019\u624b\u4e2d\u5f97\u5230\u4e86\u8fd9\u85cf\u5b9d\u4e4b\u5730\uff0c\u82e5\u771f\u662f\u795e\u4e0d\u3000\u77e5\uff0c\u9b3c\u4e0d \u89c9\uff0c\u5012\u8fd8\u7f62\u4e86\uff0c\u5982\u4eca\u6211\u8001\u5934\u5b50\u65e2\u7136\u77e5\u9053\u4e86\uff0c\u6211\u8001\u8981\u996d\u7684\u5e26\u7740\u5c0f\u8981\u996d\u7684\u4e00\u9f50\u3000\u95ee\u95ee\u4f60\uff0c\u4f60\u8bf4\u8be5 \u600e\u4e48\u529e\uff1f\uff0c\uff0c\u7a77\u795e\u51cc\u9f99\u8fd9\u4e00\u5e2d\u8bdd\uff0c\u5374\u53c8\u63ed\u5f00\u4e00\u5c42\u8c1c\u9762\uff1a\u201c\u539f\u6765\u6b64\u4e8b\u3000\u7adf\u548c\u7a77\u5bb6\u5e2e\u6709\u5173\u3002\u201d\u5927 \u5bb6\u5fc3\u91cc\u90fd\u4e00\u8f6c\uff0c\u4e0d\u77e5\u5185\u60c5\u7684\u5c31\u5728\u731c\u6d4b\uff0c\u8fd9\u6d2a\u6cfd\u3001\u9ad8\u90ae\u4e24\u6e56\u4e2d\u3000\u6240\u85cf\u7684\u7a77\u7adf\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u4f7f\u5f97 \u4e00\u5411\u4e0d\u613f\u591a\u60f9\u4ec7\u5bb6\u7684\u201c\u7075\u86c7\u201d\u6bdb\u81ec\uff0c\u4eca\u65e5\u5c45\u7136\u60f9\u4e86\u4e00\u3000\u5411\u96be\u60f9\u7684\u201c\u7a77\u5bb6\u5e2e\u201d\uff01\u8427\u8001\u96d5\u4e5f\u53c8\u4e00 \u76b1\u7709\uff0c\u6cbe\u4e0a\u7a77\u5bb6\u5e2e\u7684\u4e8b\uff0c\u603b\u4e0d\u597d\u529e\u3002 \u3000\u3000\u3000\u8fd9\u65f6\u5019\uff0c\u624d\u53ef\u770b\u51fa\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u679c\u7136\u4e0d\u6127\u4e3a\u201c\u67ad\u96c4\u201d\u4e4b\u624d\uff0c\u5728\uff0c\u8fd9\u79cd\u573a\u9762\u4e4b\u4e0b\u3000\uff0c\u795e \u8272\u4ecd\u4e0d\u53d8\uff0c\u6717\u58f0\u7b11\u7740\u8bf4\u9053\u3002 \u3000\u3000\u3000\u2018\u5929\u4e0b\u4e4b\u7269\uff0c\u6709\u5fb7\u8005\u5c45\u4e4b\uff0c\u65e0\u5fb7\u8005\u5931\u4e4b\uff0c\u6bdb\u81ec\u867d\u4e0d\u624d\uff0c\u5374\u4e0d\u6562\u8fdd\u5929\u547d\uff0c\u6bdb\u684c\u65e2\u3000\u5f97\u77e5 \u8fd9\u79d8\u85cf\u4e4b\u5730\uff0c\u5c31\u5fc5\u5b9a\u5c3d\u5168\u529b\u53bb\u83b7\u5f97\u6b64\u7269\uff0c\u81f3\u4e8e\u522b\u7684\u95ee\u9898\uff0c\u6bdb\u81ec\u611a\u6627\u5f97\u5f88\uff0c\u5374\u4e0d\u3000\u77e5\u9053\u5982\u4f55\u7b54 \u8986\u4e86\u3002\u4ed6\u201c\u5929\u547d\u201d\u8bf4\u4e4b\u5728\u524d\uff0c\u201c\u611a\u6627\u201d\u8bf4\u4e4b\u5728\u540e\uff0c\u603b\u800c\u8a00\u4e4b\uff0c\u4ed6\u7adf\u7136\u4ec0\u3000\u4e48\u90fd\u4e0d\u7ba1\u4e86\uff0c\u610f\u601d \u7adf\u662f\u4f60\u4eec\u6709\u672c\u4e8b\uff0c\u81ea\u7ba1\u6765\u548c\u6211\u62a2\u5427\uff01\u7a77\u795e\u51cc\u9f99\u6012\u6781\u800c\u7b11\uff0c\u5389\u58f0\u9053\u3000\uff1a\u2018\u65e2\u7136\u5982\u6b64\uff0c\u6211\u8001\u8981\u996d \u7684\u5c31\u5148\u6765\u9886\u6559\u9886\u6559\u4f60\u8fd9\u4f4d\u6b66\u6797\u4e00\u9738\u7684\u8eab\u624b\uff01\u201d\u7f2a\u6587\u59cb\u7ec8\u6c89\u3000\u9ed8\u7740\uff0c\u867d\u7136\u6709\u65f6\u548c\u8eab\u4fa7\u7684\u6bdb\u6587\u742a \u8bf4\u7b11\u51e0\u53e5\uff0c\u4f46\u5c31\u7b97\u5728\u4ed6\u81ea\u5df1\u8bf4\u8bdd\u7684\u65f6\u5019\uff0c\u4ed6\u4ecd\u672a\u653e\u3000\u5f03\u5176\u4e2d\u80fd\u542c\u5230\u7684\u6bcf\u4e00\u4e2a\u5b57\u3002\u6b64\u523b\uff0c\u4ed6\u542c \u5230\u8fd9\u8fd1\u5341\u6570\u5e74\u6765\u4ece\u672a\u4eb2\u81ea\u518d\u52a8\u624b\u8fc7\u7684\u7a77\u795e\u51cc\u3000\u9f99\u6012\u6781\u4e4b\u4e0b\uff0c\u7adf\u8981\u4eb2\u81ea\u51fa\u624b\u4e86\uff0c\u795e\u8272\u4e00\u53d8\uff0c\u50cf \u662f\u751f\u6015\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u8d25\u5728\u4eba\u5bb6\u624b\u4e0b\u4f3c\u3000\u7684\uff0c\u5173\u5207\u4e4b\u5bb9\uff0c\u73b0\u4e8e\u989c\u8272\u3002\u6bdb\u6587\u742a\u89c1\u4ed6\u5173\u5fc3\u81ea\u5df1\u7684\u7239 \u7239\uff0c\u82b3\u5fc3\u5927\u6170\uff0c\u5fd6\u9053\uff1a\u201c\u8d77\u5148\u3000\u6211\u8fd8\u4ee5\u4e3a\u4ed6\u5bf9\u7239\u7239\u6709\u4ec0\u4e48\u4e0d\u5bf9\u5462\uff0c\u539f\u6765\u6ca1\u6709\u3002\u201d \u3000\u3000\u3000\u7075\u86c7\u6bdb\u81ec\u9762\u76ee\u5df2\u81ea\u53d8\u8272\uff0c\u5728\u5ea7\u7fa4\u8c6a\uff0c\u76ee\u5149\u4e0d\u7981\u90fd\u770b\u7740\u4ed6\uff0c\u54ea\u77e5\u7a81\u7136\u4e00\u58f0\u4f5b\u53f7\uff0c\u3000\u6253\u7834 \u4e86\u8fd9\u77ed\u6682\u800c\u96be\u582a\u7684\u6c89\u9ed8\uff0c\u63a5\u7740\u8fd9\u58f0\u4f5b\u53f7\uff0c\u4eff\u4f5b\u5165\u5b9a\u7684\u5c11\u6797\u795e\u50e7\uff0c\u58a8\u4e00\u4e0a\u4eba\uff0c\u4e5f\u3000\u8bf4\u51fa\u4e00\u756a\u4ee4 \u5c40\u52bf\u5168\u90e8\u6539\u89c2\u7684\u8bdd\u6765\u3002"}),showOnHomepage:!0},{name:"\u7b2c\u4e5d\u7ae0",content:(0,t.jsx)(t.Fragment,{children:"\u59cb\u7ec8\u6c89\u9ed8\u7740\u7684\u5c11\u6797\u795e\u50e7\u58a8\u4e00\u4e0a\u4eba\uff0c\u6b64\u523b\u7a81\u5730\u5fae\u5c55\u5bff\u7709\uff0c\u6717\u541f\u4e00\u58f0\u4f5b\u53f7\uff0c\u76ee\u5149\u5728\u3000\u56db\u5ea7 \u7fa4\u8c6a\u7684\u8138\u4e0a\u4e00\u626b\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u65bd\u4e3b\u4eec\u90fd\u662f\u5f53\u4eca\u6b66\u6797\u4e2d\u7684\u9ad8\u4eba\uff0c\u8001\u8896\u867d\u7136\u50fb\u5904\u3000\u6df1\u5c71\uff0c\u5bf9\u5404 \u4f4d\u7684\u5927\u540d\uff0c\u7d20\u6765\u5374\u90fd\u4ef0\u6155\u5f97\u5f88\u2014\u2014\u201d\u4ed6\u5fae\u5fae\u4e00\u987f\uff0c\u7a77\u795e\u51cc\u9f99\u7b11\u9053\uff1a\u201c\u5927\u3000\u5e08\u592a\u8c26\u4e86\uff0c\u60f3\u6211\u8f88 \u51e1\u592b\u4fd7\u5b50\uff0c\u788c\u788c\u6c5f\u6e56\uff0c\u600e\u6bd4\u5f97\u4e0a\u5927\u5e08\u7684\u900d\u9065\u81ea\u5728\u3002\u201d\u4ed6\u6717\u58f0\u4e00\u7b11\u3000\uff1a\u201c\u4f55\u51b5\u5c11\u6797\u795e\u6280\uff0c\u5929\u4e0b \u95fb\u540d\uff0c\u5927\u5e08\u82e5\u5c06\u5c0f\u53ef\u4eec\u8bf4\u6210\u6b66\u6797\u9ad8\u4eba\uff0c\u522b\u4eba\u4e0d\u8bf4\uff0c\u6211\u8001\u5316\u3000\u5b50\u5b9e\u5728\u6709\u4e9b\u6c57\u989c\uff0c\u4e0d\u8fc7\u2014\u2014\u201d\u4ed6 \u76ee\u5149\u70af\u7136\u4e00\u626b\uff0c\u63a5\u7740\u9053\uff1a\u201c\u5927\u5e08\u4e45\u5df2\u4e0d\u95ee\u4e16\u4e8b\uff0c\u6b64\u3000\u6b21\u5927\u9a7e\u4e0b\u5c71\uff0c\u96be\u9053\u4e5f\u662f\u4e3a\u7740\u8fd9\u4e9b\u4fd7\u4e16\u4e2d \u7684\u73cd\u5b9d\u5417\uff1f\u201d\u6570\u5341\u5e74\u6765\uff0c\u7a77\u795e\u51cc\u9f99\u5728\u6c5f\u6e56\u4e0a\u3000\u6709\u540d\u7684\u96be\u60f9\u96be\u7f20\uff0c\u6b64\u523b\u8bf4\u51fa\u8bdd\u6765\uff0c\u8bdd\u4e2d\u66f4\u6ee1\u5e26 \u673a\u950b\uff0c\u8a00\u4e0b\u4e4b\u610f\uff0c\u5c31\u662f\u8bf4\u4f60\u4eec\u8fd9\u4e9b\u5df2\u3000\u7ecf\u52d8\u7834\u4e16\u60c5\u7684\u51fa\u5bb6\u4eba\uff0c\u5374\u600e\u7684\u8fde\u8fd9\u201c\u8d2a\u201d\u4e4b\u4e00\u6212\uff0c\u90fd \u672a\u66fe\u53c2\u900f\u5462\uff1f \u3000\u3000\u3000\u58a8\u4e00\u4e0a\u4eba\u5782\u9996\u5408\u5341\uff0c\u7b49\u4ed6\u8bdd\u8bf4\u5b8c\u4e86\uff0c\u624d\u53e3\u55a7\u4f5b\u53f7\uff0c\u53c8\u9053\uff1a\u201c\u5584\u54c9\uff0c\u5584\u54c9\uff0c\u8001\u8896\u3000\u867d\u7136 \u4e0d\u624d\uff0c\u4f46\u9762\u58c1\u6df1\u5c71\uff0c\u8499\u6211\u4f5b\u6148\u60b2\uff0c\u603b\u7b97\u5df2\u5c06\u2018\u8d2a\u2019\u2018\u55d4\u2019\u4e24\u5b57\u52d8\u7834\uff0c\u65bd\u4e3b\u4eec\u53e3\u3000\u4e2d\u7684\u85cf\u5b9d\uff0c \u867d\u662f\u767e\u5e74\u6b66\u6797\u4e2d\u4eba\u65e0\u4e0d\u5782\u6d8e\u7684\u2018\u4e09\u624d\u5b9d\u85cf\u2019\uff0c\u4f46\u8001\u8896\u5374\u8fd8\u6ca1\u6709\u8fd9\u4efd\u8d2a\u5fc3\u3000\uff0c\u60f3\u5c06\u8fd9\u79d8\u5b9d\uff0c\u636e \u4e3a\u5df1\u6709\uff0c\u65bd\u4e3b\u4e5f\u65e0\u5eb8\u591a\u8651\u3002\u201d \u3000\u3000\u3000\u7075\u86c7\u6bdb\u81ec\u53cc\u7709\u4e00\u5c55\uff0c\u6717\u58f0\u7b11\u9053\uff1a\u201c\u4e0a\u4eba\u65e0\u987b\u89e3\u91ca\uff0c\u5c0f\u53ef\u5374\u4e5f\u77e5\u9053\u50cf\u4e0a\u4eba\u8fd9\u6837\u7684\u3000\u6b66\u6797 \u524d\u8f88\uff0c\u53c8\u600e\u4f1a\u548c\u665a\u8f88\u4eec\u6765\u4e89\u8fd9\u4e9b\u8eab\u5916\u4e4b\u7269\uff0c\u82e5\u662f\u5982\u6b64\uff0c\u4e5f\u5c31\u7b97\u4e0d\u4e0a\u662f\u6b66\u6797\u524d\u8f88\u3000\u4e86\u3002\u201d \u3000\u3000\u3000\u8bf4\u5b8c\uff0c\u4ed6\u53c8\u81ea\u6717\u58f0\u5927\u7b11\uff0c\u773c\u89d2\u5374\u5411\u7a77\u795e\u51cc\u9f99\u5fae\u8198\uff0c\u8bdd\u4e2d\u542b\u610f\uff1a\u663e\u7136\u662f\u53d6\u745f\u800c\u6b4c\u3000\uff0c\u522b \u6709\u6240\u5bc4\uff0c\u6697\u8bbd\u90a3\u4e9b\u548c\u81ea\u5df1\u4e89\u5b9d\u7684\u4eba\uff0c\u7b97\u4e0d\u4e0a\u662f\u6b66\u6797\u524d\u8f88\u3002 \u3000\u3000\u3000\u7a77\u795e\u51cc\u9f99\u7a81\u5730\u4e5f\u4ef0\u5929\u800c\u7b11\uff0c\u7b11\u58f0\u7a7f\u91d1\u88c2\u77f3\uff0c\u5c06\u7075\u86c7\u6bdb\u81ec\u7684\u7b0b\u58f0\u538b\u4e86\u4e0b\u53bb\uff0c\u7136\u540e\u3000\uff0c\u4ed6 \u7b11\u58f0\u731b\u987f\uff0c\u53cc\u76ee\u51db\u7136\u4e00\u5f20\uff0c\u5389\u58f0\u9053\uff1a\u201c\u6211\u8001\u5316\u5b50\u505a\u4e8b\uff0c\u4e00\u5411\u4e00\u6e05\u4e8c\u695a\uff0c\u5206\u5f97\u660e\u3000\u660e\u767d\u767d\uff0c\u59d3 \u6bdb\u7684\uff0c\u4f60\u53ef\u8981\u5c06\u8bdd\u8bf4\u6e05\u695a\u4e9b\uff0c\u6211\u8001\u5316\u5b50\u867d\u7136\u6709\u540d\u7684\u7a77\uff0c\u5374\u4e5f\u4e0d\u4f1a\u4ee5\u5927\u6b3a\u3000\u5c0f\uff0c\u6765\u62a2\u4f60\u8fd9\u5c0f\u8f88 \u7684\u4e1c\u897f\uff0c\u53ea\u662f\u8fd9\u2018\u4e09\u624d\u5b9d\u85cf\u2019\u7684\u79d8\u56fe\uff0c\u4e43\u6211\u7a77\u5bb6\u5e2e\u95e8\u4e0b\u7684\u5f1f\u5b50\u4eec\u3000\u8d39\u4e86\u65e0\u7a77\u5fc3\u529b\u624d\u5f97\u5230\u7684\uff0c \u82e5\u6709\u4eba\u8981\u6043\u5f3a\u593a\u53bb\uff0c\u6211\u8001\u5316\u5b50\u53ef\u4e5f\u4e0d\u662f\u4efb\u4eba\u6b3a\u8d1f\u7684\u3002\u201d \u3000\u3000\u3000\u7075\u86c7\u6bdb\u81ec\u4e5f\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u85cf\u5b9d\u4e4b\u56fe\u662f\u6211\u4faf\u56db\u5f1f\u81ea\u4f60\u7a77\u5bb6\u5e2e\u4eec\u4e0b\u7684\u3000\u5f1f\u5b50 \u624b\u4e2d\u6240\u5f97\uff0c\u53ea\u662f\u90a3\u65f6\u8d35\u5e2e\u7684\u5f1f\u5b50\u5df2\u8bef\u95ef\u9ad8\u3001\u6d2a\u6c34\u5be8\u7684\u6697\u5361\uff0c\u88ab\u4eba\u5bb6\u7684\u94c1\u5f29\u6240\u4f24\u3000\uff0c\u6211\u4faf\u56db\u5f1f \u4ed7\u4e49\u63f4\u6551\uff0c\u8d35\u5e2e\u90a3\u5f1f\u5b50\u5fc3\u611f\u5927\u6069\uff0c\u624d\u4ee5\u6b64\u56fe\u76f8\u8d60\u7684\u3002\u201d \u3000\u3000\u3000\u7a77\u795e\u51cc\u9f99\u5389\u58f0\u559d\u9053\uff1a\u201c\u59d3\u6bdb\u7684\uff0c\u4f60\u7eb5\u7136\u820c\u707f\u83b2\u82b1\uff0c\u4e5f\u662f\u65e0\u7528\uff0c\u6211\u6559\u4e0b\u5f1f\u5b50\u867d\u7136\u3000\u88ab\u6697 \u5f29\u6240\u4f24\uff0c\u53ef\u662f\u82e5\u6ca1\u6709\u4f60\u90a3\u4f4d\u4faf\u56db\u5f1f\u7684\u2018\u76f8\u6551\u2019\uff0c\u6015\u8fd8\u4e0d\u81f4\u9001\u547d\u3002\u201d \u3000\u3000\u3000\u4ed6\u51b7\u54fc\u4e00\u58f0\uff1a\u201c\u4f60\u82e5\u4ee5\u4e3a\u6b64\u4e8b\u505a\u5f97\u795e\u4e0d\u77e5\u9b3c\u4e0d\u89c9\uff0c\u90a3\u5c31\u5927\u5927\u5730\u9519\u4e86\uff0c\u987b\u77e5\u82e5\u8981\u3000\u4eba\u4e0d \u77e5\uff0c\u5374\u9664\u975e\u5df1\u83ab\u4e3a\u54e9\uff01\u201d \u3000\u3000\u3000\u4e00\u9762\u8bf4\u7740\u8bdd\uff0c\u8fd9\u4f4d\u4ee5\u201c\u6df7\u5143\u4e00\u6c14\u7ae5\u5b50\u529f\u201d\u95fb\u540d\u6b66\u6797\u7684\u5f02\u4eba\uff0c\u4e00\u9762\u5728\u684c\u4e0a\u62ff\u4e86\u4e24\u3000\u53ea\u76db \u9152\u7684\u9521\u58f6\uff0c\u968f\u624b\u62e8\u5f04\u4e4b\u4e0b\uff0c\u90a3\u4e24\u53ea\u9521\u58f6\u5728\u4ed6\u624b\u638c\u4e2d\uff0c\u7adf\u53d8\u6210\u4e86\u4e00\u6761\u957f\u7ea6\u4e09\u5c3a\u7684\u3000\u9521\u68cd\uff0c\u5ea7\u4e0a \u7fa4\u8c6a\u53ef\u90fd\u662f\u8bc6\u8d27\u7684\uff0c\u6b64\u65f6\u5df2\u5728\u56e0\u7740\u4ed6\u8fd9\u79cd\u8d85\u4eba\u7684\u529f\u529b\u800c\u60ca\u559f\u4e86\u3002 \u3000\u3000\u3000\u7a77\u795e\u51cc\u9f99\u5c06\u8fd9\u6761\u9521\u68cd\u5728\u684c\u4e0a\u4e00\u6572\uff0c\u968f\u7740\u8fd9\u201c\u7830\u7136\u201d\u4e00\u54cd\uff0c\u4ed6\u53c8\u51b7\u51b7\u9053\uff1a\u201c\u59d3\u6bdb\u3000\u7684\uff0c \u4f60\u82e5\u8bc6\u8da3\uff0c\u5feb\u5c06\u90a3\u85cf\u5b9d\u4e4b\u56fe\u8fd8\u7ed9\u6211\u8001\u5316\u5b50\uff0c\u6211\u8001\u5316\u5b50\u770b\u5728\u4f60\u6b7b\u53bb\u7684\u5e08\u5085\u4e94\u53f0\u548c\u3000\u5c1a\u9762\u4e0a\uff0c\u975e \u4f46\u5f80\u4e8b\u4e0d\u63d0\uff0c\u800c\u4e14\u53ea\u8981\u4f60\u5728\u6c5f\u6e56\u4e0a\u4e0d\u4ecd\u4e3a\u975e\u4f5c\u6b79\uff0c\u6211\u8001\u5316\u5b50\u4e5f\u7edd\u4e0d\u8fc7\u95ee\u3000\uff0c\u4e0d\u7136\u7684\u8bdd\uff0c\u4f60\u8f9b \u82e6\u521b\u4e0b\u7684\u8fd9\u4efd\u57fa\u4e1a\uff0c\u53ef\u5c31\u6709\u4e9b\u4e0d\u7a33\u5f53\u4e86\u3002\u201d \u3000\u3000\u3000\u7075\u86c7\u6bdb\u81ec\u76ee\u5149\u4e00\u8f6c\uff0c\u65b9\u81ea\u7b54\u8a00\uff0c\u5374\u89c1\u90a3\u706b\u773c\u91d1\u96d5\u8427\u8fdf\u7ad9\u4e86\u8d77\u6765\uff0c\u62a2\u7740\u9053\uff1a\u201c\u6211\u3000\u8001\u5934 \u5b50\u4e0d\u7ba1\u8fd9\u4efd\u85cf\u5b9d\u4e4b\u56fe\u88ab\u4f60\u4eec\u4f55\u4eba\u6240\u5f97\uff0c\u53ea\u77e5\u9053\u53ea\u8981\u5728\u9ad8\u3001\u6d2a\u4e24\u6e56\u4e2d\u7684\u4e1c\u897f\uff0c\u5c31\u3000\u5f97\u5f52\u6211\u201d\u8427 \u95e8\u6c34\u5be8\u201d\u6240\u6709\uff0c\u4f60\u4eec\u9646\u9053\u4e0a\u7684\u670b\u53cb\u82e5\u60f3\u52a8\u6211\u4eec\u6c34\u91cc\u7684\u4e1c\u897f\uff0c\u9664\u975e\u5c06\u5929\u4e0b\u3000\u4e09\u5341\u516d\u8def\u6c34\u9053\u4e0a\u7684 \u5144\u5f1f\u5200\u5200\u523a\u5c3d\uff0c\u4e2a\u4e2a\u6740\u7edd\uff0c\u5426\u5219\u518d\u4e5f\u4f11\u60f3\uff01\u201d \u3000\u3000\u3000\u4ed6\u751f\u50cf\u672c\u6781\u5a01\u731b\uff0c\u6b64\u523b\u76db\u6012\u4e4b\u4e0b\uff0c\u4e24\u9053\u957f\u7709\uff0c\u6839\u6839\u76f4\u7acb\uff0c\u76ee\u5149\u66f4\u662f\u51db\u51bd\u5982\u5200\uff0c\u3000\u518d\u52a0 \u4e0a\u8bed\u58f0\u6709\u5982\u5b8f\u949f\uff0c\u8bdd\u4e2d\u7684\u542b\u610f\uff0c\u4e5f\u6781\u5176\u7280\u5229\uff0c\u679c\u7136\u4e0d\u6127\u4e3a\u603b\u9886\u5929\u4e0b\u6c34\u8def\u82f1\u96c4\uff0c\u3000\u5929\u4e0b\u4e09\u5341\u516d \u8def\u6c34\u9053\u7684\u603b\u5de1\u9605\u3002 \u3000\u3000\u3000\u7f2a\u6587\u7f13\u7f13\u4f38\u51fa\u7b77\u5b50\uff0c\u5728\u76d8\u4e2d\u631f\u4e86\u4e00\u5757\u201c\u51b0\u7cd6\u8098\u5b50\u201d\uff0c\u653e\u5728\u5634\u91cc\u6162\u6162\u5480\u56bc\u7740\uff0c\u8fd9\u3000\u4e09\u65b9 \u9762\u6b63\u662f\u5404\u6709\u6240\u636e\u7684\u52bf\u529b\uff0c\u5404\u4e0d\u76f8\u8ba9\uff0c\u4ed6\u5750\u5c71\u89c2\u864e\u6597\uff0c\u5927\u6709\u5f97\u5176\u6240\u54c9\uff0c\u5fc3\u5b89\u7406\u5f97\u3000\u7684\u610f\u601d\u3002 \u3000\u3000\u3000\u6b64\u523b\u7075\u86c7\u6bdb\u81ec\u4ee5\u5f53\u4eca\u7eff\u6797\u9738\u4e3b\u7684\u8eab\u4efd\uff0c\u7ad9\u5728\u8fd9\u7a77\u795e\u51cc\u9f99\u548c\u8427\u3002\u706b\u773c\u91d1\u96d5\u4e4b\u95f4\uff0c\u3000\u4ecd\u7136 \u50cf\u662f\u6beb\u65e0\u6240\u60e7\uff0c\u8fd9\u4e09\u4eba\u76ee\u5149\u4e92\u89c6\uff0c\u5176\u4e2d\u7684\u5173\u7cfb\uff0c\u4e5f\u6b63\u662f\u6781\u5176\u5fae\u5999\u590d\u6742\uff0c\u4e92\u76f8\u7275\u3000\u5236\uff0c\u6ca1\u6709\u4e00 \u65b9\u662f\u7a33\u5360\u4e0a\u98ce\u7684\u3002 \u3000\u3000\u3000\u662f\u4ee5\u6b64\u523b\u8fd9\u4e09\u4eba\u8c01\u4e5f\u6ca1\u6709\u53d1\u8bdd\uff0c\u5404\u5404\u5fc3\u4e2d\u90fd\u5728\u76d8\u7b97\u7740\uff0c\u600e\u6837\u80fd\u4f7f\u5f97\u53e6\u5916\u4e24\u4eba\u5148\u3000\u6597\u4e0a \u4e00\u6597\uff0c\u81ea\u5df1\u518d\u5728\u65c1\u8fb9\u6361\u6361\u4fbf\u5b9c\u3002 \u3000\u3000\u3000\u5ea7\u4e0a\u7fa4\u8c6a\uff0c\u867d\u7136\u4e5f\u4ff1\u662f\u6210\u540d\u7acb\u4e07\u7684\u6b66\u6797\u670b\u53cb\uff0c\u4f46\u6b64\u523b\u5374\u8c01\u4e5f\u4e0d\u613f\u591a\u5634\uff0c\u56e0\u4e3a\u5927\u3000\u5bb6\u90fd \u77e5\u9053\uff0c\u8fd9\u4e09\u4eba\u4e4b\u4e2d\uff0c\u6ca1\u6709\u4e00\u4e2a\u662f\u597d\u60f9\u7684\uff0c\u867d\u7136\u5176\u4e2d\u6709\u4eba\u548c\u5176\u4e00\u65b9\u5173\u7cfb\u8f83\u6df1\uff0c\u53ef\u3000\u4e5f\u6ca1\u6709\u4eba\u51fa \u624b\u8d9f\u8fd9\u8d9f\u6d51\u6c34\u3002 \u3000\u3000\u3000\u58a8\u4e00\u4e0a\u4eba\u53cc\u7709\u53c8\u5fae\u4e00\u5c55\uff0c\u8fd9\u4f4d\u5c11\u6797\u9ad8\u50e7\u5728\u65c1\u4eba\u8bf4\u8bdd\u7684\u65f6\u5019\uff0c\u4ed6\u59cb\u7ec8\u662f\u5c0a\u4f5b\u50cf\u4f3c\u3000\u7684\uff0c \u52a8\u4e5f\u4e0d\u52a8\uff0c\u8138\u4e0a\u66f4\u6ca1\u6709\u4efb\u4f55\u8868\u60c5\uff0c\u6b64\u523b\u7fa4\u8c6a\u7a0d\u4e00\u9759\u9ed8\uff0c\u4ed6\u6717\u541f\u4e00\u58f0\u4f5b\u53f7\uff0c\u53c8\u7f13\u3000\u7f13\u5f00\u53e3\u8bf4 \u9053\uff1a\u201c\u65bd\u4e3b\u4eec\u4e89\u4e86\u534a\u5929\uff0c\u5374\u4e5f\u65e0\u76ca\uff0c\u56e0\u4e3a\u8fd9\u2018\u4e09\u624d\u5b9d\u85cf\uff0c\u7684\u5f97\u4e3b\uff0c\u5e76\u4e0d\u662f\u3000\u65bd\u4e3b\u4eec\u4e09\u4eba\u4e4b\u4e89 \u53ef\u4ee5\u89e3\u51b3\u7684\u3002\u201d\u8fd9\u5c11\u6797\u795e\u50e7\u6b64\u8bdd\u4e00\u51fa\uff0c\u6ee1\u697c\u7fa4\u8c6a\u7684\u76ee\u5149\uff0c\u4e0d\u7981\u90fd\u4e00\u3000\u9f50\u671b\u5728\u4ed6\u8eab\u4e0a\uff0c\u7a77\u795e\u51cc \u9f99\u6d53\u7709\u5fae\u7ad6\u9053\uff1a\u201c\u5927\u5e08\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u6211\u8001\u5316\u5b50\u5374\u5f04\u4e0d\u61c2\u3000\u3002\u201d \u3000\u3000\u3000\u7075\u86c7\u6bdb\u81ec\u4e5f\u7acb\u523b\u63a5\u53e3\u9053\uff1a\u201c\u96be\u9053\u4e0a\u4eba\u4e5f\u6709\u610f\u95ee\u9f0e\u6b64\u7269\u5417\uff1f\u201d \u3000\u3000\u3000\u706b\u773c\u91d1\u96d5\u5374\u4e00\u62cd\u684c\u5b50\uff0c\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u597d\u6781\u4e86\uff0c\u597d\u6781\u4e86\uff0c\u8fd8\u662f\u5c11\u6797\u795e\u50e7\u51fa\u6765\u4e3a\u3000\u5929\u4e0b \u6b66\u6797\u4e3b\u6301\u516c\u9053\u3002\u201d \u3000\u3000\u3000\u4ed6\u5de8\u5927\u7684\u624b\u638c\u671d\u5750\u5728\u4ed6\u8eab\u4fa7\u7684\u91d1\u9ca4\u8427\u5e73\u80a9\u4e0a\u4e00\u62cd\uff0c\u53c8\u9053\uff1a\u201c\u5e73\u513f\uff0c\u4f60\u53ef\u8bb0\u5f97\u4e3a\u3000\u7236\u5e38 \u8ddf\u4f60\u8bf4\uff0c\u82b8\u82b8\u6b66\u6797\u4e2d\uff0c\u53ea\u6709\u5c11\u6797\u4e00\u8109\u624d\u53ef\u5f53\u5f97\u4e0a\u662f\u6b66\u5b66\u6b63\u5b97\uff0c\u5982\u4eca\u4f60\u770b\u770b\u5929\u4e0b\u3000\u6b66\u6797\u90fd\u5c06\u6614 \u5e74\u6c34\u9646\u4e24\u9053\u79e6\u5cad\u4e4b\u4f1a\u4e2d\u6240\u5236\u5b9a\u7684\u2018\u6c34\u3001\u9646\u4e24\u8def\uff0c\u5404\u6709\u6240\u5206\uff0c\u5176\u4e2d\u4e0d\u5f97\u6709\u3000\u4efb\u4f55\u4e00\u65b9\u5984\u81ea\u4fb5\u5360 \u4ed6\u65b9\u5730\u76d8\u2019\u8fd9\u4e00\u6761\u6700\u91cd\u8981\u7684\u89c4\u7ea6\u5fd8\u8bb0\u4e86\u7684\u65f6\u5019\uff0c\u5374\u6709\u5c11\u6797\u795e\u50e7\u51fa\u3000\u6765\u4e3a\u6211\u4eec\u4e3b\u6301\u516c\u9053\u3002\u201d \u3000\u3000\u3000\u7f2a\u6587\u6697\u4e2d\u4e00\u7b11\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u8001\u5934\u5b50\u679c\u7136\u5389\u5bb3\uff0c\u6b64\u523b\u5df2\u5c06\u70ed\u5c71\u828b\u629b\u5230\u90a3\u8001\u548c\u5c1a\u624b\u3000\u91cc \u4e86\u3002\u201d \u3000\u3000\u3000\u987b\u77e5\u8fd9\u4e8b\u5df2\u6210\u96be\u9898\uff0c\u6b63\u5982\u4e00\u4e2a\u70eb\u4e0d\u7559\u624b\u7684\u5c71\u828b\u4e00\u6837\uff0c\u8c01\u4e5f\u65e0\u6cd5\u5c06\u5b83\u63a5\u4f4f\u5265\u5f00\uff0c\u3000\u6b64\u523b \u8fd9\u706b\u773c\u91d1\u96d5\u5374\u5c06\u201c\u4e3b\u6301\u516c\u9053\u201d\u8fd9\u5927\u5e3d\u5b50\u538b\u5230\u58a8\u4e00\u4e0a\u4eba\u5934\u4e0a\uff0c\u7f2a\u6587\u4e0d\u7981\u6ce8\u89c6\u7740\u8fd9\u3000\u5c11\u6797\u795e\u50e7\uff0c \u770b\u770b\u4ed6\u8981\u5c06\u8fd9\u6eda\u70eb\u7684\u5c71\u828b\u5982\u4f55\u5904\u7406\u6cd5\uff1f \u3000\u3000\u3000\u5ea7\u4e2d\u7fa4\u8c6a\uff0c\u4e5f\u90fd\u5728\u6697\u8d5e\u8fd9\u8001\u96d5\u7684\u8001\u8fa3\uff0c\u5927\u5bb6\u90fd\u662f\u773c\u91cc\u4e0d\u63c9\u6c99\u5b50\u7684\u5149\u68cd\uff0c\u6b64\u523b\u8001\u3000\u96d5\u8bdd \u4e2d\u7684\u542b\u610f\uff0c\u8fd8\u6709\u8c01\u542c\u4e0d\u51fa\u6765\u7684\u3002 \u3000\u3000\u3000\u54ea\u77e5\u58a8\u4e00\u4e0a\u4eba\u4ecd\u7136\u5782\u76ee\u5408\u638c\uff0c\u4e1d\u6beb\u65e0\u52a8\u4e8e\u8877\uff0c\u53ea\u662f\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u65bd\u4e3b\u4eec\u4e0d\u501f\u5404\u3000\u4ee5\u4e00 \u6d3e\u5b97\u4e3b\u7684\u8eab\u4efd\uff0c\u6765\u4e89\u593a\u8fd9\u2018\u4e09\u624d\u5b9d\u85cf\u2019\uff0c\u60f3\u5fc5\u662f\u56e0\u4e3a\u8fd9\u2018\u4e09\u624d\u5b9d\u85cf\uff0c\u4e2d\uff0c\u9664\u4e86\u3000\u5de8\u4e07\u91d1\u94f6\u4e4b \u5916\uff0c\u8fd8\u6709\u7740\u795e\u5175\u5229\u5668\uff0c\u548c\u529f\u80fd\u8d77\u6b7b\u4eba\u6d3b\u767d\u9aa8\u7684\u5999\u836f\u4ed9\u65b9\uff0c\u53ef\u662f\uff0c\u65bd\u4e3b\u4eec\u3000\u53ef\u66fe\u77e5\u9053\u8fd9\u2019\u4e09\u624d \u5b9d\u85cf\uff0c\u7684\u6e0a\u6e90\u6765\u5386\uff0c\u7a76\u7a76\u7adf\u5982\u4f55\u5417\uff1f\u201d \u3000\u3000\u3000\u8fd9\u4e00\u95ee\u5374\u5c06\u5ea7\u4e2d\u7fa4\u8c6a\u90fd\u95ee\u695e\u4f4f\u4e86\uff0c\u5927\u5bb6\u5148\u524d\u90fd\u5728\u5947\u602a\uff0c\u51ed\u7740\u8fd9\u4e9b\u4eba\u7684\u8eab\u4efd\uff0c\u4e3a\u3000\u4ec0\u4e48 \u4f1a\u4e3a\u4e00\u4e9b\u85cf\u5b9d\u800c\u4e89\u5f97\u5982\u6b64\u5389\u5bb3\uff0c\u90a3\u4e48\u53ef\u662f\u4ec0\u4e48\u4e1c\u897f\u80fd\u4f7f\u5f97\u8fd9\u4e9b\u672c\u8eab\u5df2\u5177\u9738\u4e1a\u7684\u3000\u6b66\u6797\u9ad8\u624b\uff0c \u4e0d\u501f\u4e00\u6597\u5462\uff1f \u3000\u3000\u3000\u540e\u6765\u5927\u5bb6\u542c\u5230\u201c\u4e09\u624d\u4e3b\u85cf\u201d\u56db\u5b57\uff0c\u624d\u6709\u4e9b\u77e5\u9053\u8fd9\u662f\u6b66\u6797\u4e2d\u7684\u8001\u8f88\u4f20\u79d8\u6c89\u767e\u5e74\u7684\u3000\u4e00\u5b97 \u5de8\u5927\u5b9d\u85cf\uff0c\u53ea\u662f\u8fd9\u5b9d\u85cf\u91cc\u7a76\u7adf\u5305\u62ec\u4e9b\u4ec0\u4e48\u4e1c\u897f\uff0c\u5927\u5bb6\u5e76\u4e0d\u6e05\u695a\uff0c\u5bf9\u4e8e\u8fd9\u5b9d\u85cf\u7684\u3000\u6e0a\u6e90\u6765\u5386\uff0c \u5927\u5bb6\u5c31\u66f4\u4e3a\u8ff7\u60d8\u4e86\u3002 \u3000\u3000\u3000\u6b64\u523b\u58a8\u4e00\u4e0a\u4eba\u8bf4\u5b8c\u4e86\u8bdd\uff0c\u5ea7\u4e2d\u7fa4\u8c6a\u6709\u7684\u5c31\u4e0d\u7981\u4e92\u76f8\u8033\u8bed\uff0c\u5f7c\u6b64\u63a2\u8be2\u7740\uff1a\u201c\u5229\u5668\u3000\u795e \u5175\u201d\u3001\u201c\u5999\u836f\u4ed9\u65b9\u201d\u3001\u201c\u5de8\u4e07\u91d1\u94f6\u201d\uff0c\u8fd9\u4e9b\u65e0\u8bba\u5bf9\u4e8e\u4efb\u4f55\u4e00\u4e2a\u4eba\u6765\u8bf4\uff0c\u90fd\u662f\u592a\u8fc7\u3000\u5145\u6ee1\u8bf1\u60d1 \u7684\u540d\u8bcd\u4e86\u3002 \u3000\u3000\u3000\u6bdb\u6587\u742a\u5e74\u8f7b\u597d\u5947\uff0c\u542c\u5230\u8fd9\u4e9b\u6b66\u6797\u4e2d\u795e\u79d8\u7684\u4f20\u8bf4\uff0c\u773c\u775b\u90fd\u77aa\u76f4\u4e86\uff0c\u6b64\u523b\u773c\u89d2\u5fae\u8198\u3000\uff0c\u770b \u5230\u7f2a\u6587\u5634\u91cc\u7adf\u8fd8\u5728\u5403\u7740\u4e1c\u897f\uff0c\u4e0d\u7981\u201c\u5657\u55e4\u201d\u4e00\u7b11\uff0c\u6084\u6084\u62c9\u4e86\u4ed6\u4e00\u4e0b\u8896\u5b50\uff0c\u4f4e\u4f4e\u3000\u5730\u7b11\u7740\u8bf4\uff1a \u201c\u4f60\u80c3\u53e3\u5012\u771f\u597d\uff0c\u8fd8\u5403\u5f97\u4e0b\u4e1c\u897f\u3002\u201d\u6ee1\u697c\u7fa4\u8c6a\uff0c\u9664\u4e86\u7f2a\u6587\u4ee5\u5916\uff0c\u5728\u8fd9\u79cd\u3000\u60c5\u51b5\u4e0b\uff0c\u786e\u5b9e\u4e5f\u6ca1 \u6709\u4e00\u4e2a\u4eba\u6709\u5fc3\u60c5\u5403\u4e1c\u897f\u4e86\u3002 \u3000\u3000\u3000\u7a77\u795e\u51cc\u9f99\u76ee\u5149\u56db\u626b\uff0c\u770b\u5230\u4eba\u4eba\u90fd\u95ed\u7740\u5634\u5427\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5927\u5e08\u95ee\u5f97\u597d\uff0c\u8fd9\u3000\u2018\u4e09 \u624d\u5b9d\u85cf\u2019\u7684\u6765\u5386\uff0c\u6211\u8001\u5316\u5b50\u5012\u77e5\u9053\u4e00\u4e9b\u3002\u201d \u3000\u3000\u3000\u7075\u86c7\u6bdb\u81ec\u51b7\u54fc\u4e00\u58f0\uff0c\u7a77\u795e\u51cc\u9f99\u4e0d\u7406\u4ed6\uff0c\u63a5\u7740\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u767e\u5341\u5e74\u524d\uff0c\u6b66\u6797\u4e2d\u6709\u3000\u4e09\u4e2a \u524d\u8f88\u5f02\u4eba\uff0c\u5404\u6000\u79d8\u6280\uff0c\u79f0\u96c4\u6c5f\u6e56\uff0c\u4ee5\u2018\u4e09\u624d\u8054\u76df\u2019\u4e4b\u540d\uff0c\u4e3b\u6301\u5929\u4e0b\u7eff\u6797\u7684\u4e70\u5356\u3000\u2014\u2014\u201d\u4ed6\u8bdd \u672a\u8bf4\u5b8c\uff0c\u706b\u773c\u91d1\u96d5\u8427\u8fdf\u5df2\u62a2\u7740\u63a5\u53e3\u9053\uff1a\u201c\u5929\u533b\u3001\u5730\u715e\u3001\u4eba\u9b54\uff0c\u4ee5\u4e0d\u4e16\u4e4b\u3000\u624d\uff0c\u541b\u4e34\u7eff\u6797\uff0c\u5929 \u4e0b\u7eff\u6797\u9053\u53ea\u8981\u505a\u5f97\u4e00\u5b97\u4e70\u5356\uff0c\u5c31\u5f97\u732e\u51fa\u4e09\u6210\u732e\u91d1\uff0c\u6211\u8001\u5934\u5b50\u867d\u7136\u3000\u5b64\u964b\u5be1\u95fb\uff0c\u53ef\u662f\u8fd9\u5b97\u6b66\u6797 \u638c\u6545\uff0c\u5374\u591a\u591a\u5c11\u5c11\u5c11\u8fd8\u77e5\u9053\u4e00\u4e9b\u3002\u201d\u773c\u89d2\u5411\u7a77\u795e\u51cc\u9f99\u4e00\u7768\u3000\uff0c\u5f97\u610f\u4e4b\u8272\uff0c\u6ea2\u4e8e\u8a00\u8868\u3002 \u3000\u3000\u3000\u6bdb\u6587\u742a\u6084\u7136\u4e00\u7b11\uff0c\u8033\u8bed\u7f2a\u6587\u9053\uff1a\u201c\u539f\u6765\u8fd9\u6279\u5b9d\u7269\uff0c\u662f\u4e09\u4e2a\u5f3a\u76d7\u5934\u5b50\u7559\u4e0b\u6765\u7684\u3002\u3000\u201d\u7f2a \u6587\u5374\u4e0d\u7f6e\u53ef\u5426\u5730\u5fae\u7b11\u4e00\u4e0b\uff0c\u5374\u542c\u5230\u516b\u9762\u73b2\u73d1\u80e1\u4e4b\u8f89\u6084\u6084\u4e0e\u94c1\u624b\u4ed9\u733f\u9053\uff1a\u201c\u8001\u56db\u3000\uff0c\u4f60\u770b\u90a3\u4f4d \u6e05\u98ce\u5251\u6731\u767d\u7fbd\u600e\u4e48\u8c61\u6b7b\u4eba\u4e00\u6837\u5730\uff0c\u5750\u5728\u90a3\u91cc\u52a8\u4e5f\u4e0d\u52a8\uff0c\u4e5f\u4e0d\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u3000\u94c1\u624b\u4ed9\u733f\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u65b9\u81ea\u4e5f\u6697\u4e2d\u5947\u602a\uff0c\u5374\u542c\u58a8\u4e00\u4e0a\u4eba\u6717\u7136\u53c8\u9053\uff1a\u201c\u4e45\u95fb\u8427\u3000\u8001\u65bd \u4e3b\u535a\u53e4\u901a\u4eca\uff0c\u5982\u4eca\u4e00\u89c1\uff0c\u679c\u7136\u662f\u89c1\u591a\u8bc6\u5e7f\u3002\u201d\u8fd9\u4f4d\u5c11\u6797\u9ad8\u618e\u8fd9\u51e0\u53e5\u8bdd\uff0c\u9669\u4e9b\u4f7f\u3000\u5f97\u8427\u8fdf\u5f97\u610f \u5f97\u7b11\u51fa\u58f0\u6765\uff0c\u4ed6\u4e00\u6325\u957f\u987b\uff0c\u6b63\u51c6\u5907\u4e5f\u8bf4\u4e0a\u4e24\u53e5\u8bdd\uff0c\u4f46\u58a8\u4e00\u4e0a\u4eba\u5374\u5df2\u63a5\u7740\u3000\u8bf4\u9053\uff1a\u201c\u5929\u3001\u5730\u3001 \u4eba\u3001\u4e09\u624d\u8054\u76df\uff0c\u867d\u7136\u8ff9\u8fd1\u5750\u5730\u5206\u8d43\uff0c\u4f46\u8fd9\u4e09\u4f4d\u6b66\u6797\u524d\u8f88\u6b64\u4e3e\uff0c\u5374\u3000\u4e5f\u4e3a\u6b66\u6797\u4e2d\u51cf\u53bb\u4e0d\u5c11\u662f \u975e\uff0c\u9020\u5c31\u4e0d\u5c11\u529f\u5fb7\uff0c\u8427\u8001\u65bd\u4e3b\u5fb7\u9ad8\u671b\u91cd\uff0c\u867d\u7136\u77e5\u9053\u6b64\u4e8b\uff0c\u5374\u3000\u8fd8\u77e5\u9053\u8fd9\u4e09\u4f4d\u524d\u8f88\u5f02\u4eba\u7559\u4e0b\u7684 \u662f\u4ec0\u4e48\u5b9d\u7269\u5417\uff1f\u201d \u3000\u3000\u3000\u7fa4\u8c6a\u4e0d\u7981\u90fd\u4f38\u76f4\u8116\u5b50\u53bb\u542c\uff0c\u54ea\u77e5\u8427\u8001\u96d5\u201c\u54e6\u201d\u4e86\u4e24\u58f0\uff0c\u5374\u6ca1\u6709\u4e86\u4e0b\u6587\uff0c\u539f\u6765\u4ed6\u3000\u53ea\u77e5 \u5176\u7136\uff0c\u5374\u5e76\u4e0d\u77e5\u9053\u5176\u6240\u4ee5\u7136\u5462\u3002 \u3000\u3000\u3000\u8fd9\u6b21\uff0c\u7075\u86c7\u6bdb\u81ec\u5374\u6717\u58f0\u7b11\u9053\uff1a\u201c\u5929\u533b\u3001\u5730\u715e\u3001\u4eba\u9b54\uff0c\u79f0\u96c4\u6b66\u6797\u5782\u4e09\u5341\u5e74\uff0c\u540e\u6765\u3000\u5374\u4e0d \u77e5\u600e\u7684\uff0c\u4e00\u9f50\u5931\u8e2a\uff0c\u81ea\u6b64\u4ee5\u540e\uff0c\u8fd9\u4e09\u4f4d\u6b66\u6797\u524d\u8f88\u591a\u5e74\u6765\u7684\u8d44\u8d22\uff0c\u548c\u5730\u715e\u5e38\u8001\u524d\u3000\u8f88\u638c\u4e2d\u7684\u4e00 \u53e3\u7edd\u4ee3\u795e\u5175\u8f98\u8f73\u53e4\u5251\uff0c\u4eba\u9b54\u53f8\u7a7a\u8001\u524d\u8f88\u4ed7\u4ee5\u79f0\u9738\u6c5f\u6e56\u7684\u65e0\u6bd4\u9738\u9053\u7684\u6697\u5668\u3000\u2018\u5317\u6597\u4e03\u661f\u9488\u2019\uff0c \u518d\u52a0\u4e0a\u5929\u533b\u5434\u8001\u524d\u8f88\u7684\u4e00\u4e9b\u7eed\u547d\u4e39\u65b9\uff0c\u5c31\u6210\u4e86\u6b66\u6797\u4e2d\u8c01\u60f3\u90fd\u5f97\u5230\u3000\u7684\u5b9d\u7269\u3002\u201d \u3000\u3000\u3000\u4ed6\u76ee\u5149\u56db\u626b\uff0c\u7765\u7768\u4f5c\u6001\uff0c\u53c8\u9053\uff1a\u201c\u53ea\u662f\u767e\u5341\u5e74\u524d\uff0c\u8fd9\u4e9b\u6b66\u6797\u79d8\u5b9d\uff0c\u4e5f\u50cf\u8fd9\u4e09\u4f4d\u3000\u6b66\u6797 \u524d\u8f88\u4e00\u6837\uff0c\u6c38\u672a\u5728\u6c5f\u6e56\u4e0a\u51fa\u73b0\u8fc7\uff0c\u2018\u4e09\u624d\u5b9d\u85cf\u2019\u4e5f\u6210\u4e86\u6b66\u6797\u6545\u8001\u76f8\u4f20\u7684\u4e00\u4ef6\u79d8\u3000\u5bc6\uff0c\u5c0f\u53ef\u56db \u5341\u5e74\u524d\uff0c\u5c31\u66fe\u542c\u5bb6\u5e08\u8bf4\u8fc7\uff0c\u60f3\u4e0d\u5230\u2014\u2014\u201d\u4ed6\u542b\u84c4\u5730\u6b62\u4f4f\u4e86\u8bdd\uff0c\u8a00\u4e0b\u4e4b\u610f\u3000\uff0c\u5f53\u7136\u5c31\u662f\u60f3\u4e0d\u5230 \u8fd9\u4ef6\u79d8\u5bc6\u6b64\u523b\u5374\u634f\u5728\u6211\u624b\u4e2d\u4e86\u3002 \u3000\u3000\u3000\u58a8\u4e00\u4e0a\u4eba\u76ee\u5149\u4e00\u62ac\uff0c\u9053\uff1a\u201c\u963f\u5f25\u9640\u4f5b\uff0c\u60f3\u4e0d\u5230\u6bdb\u65bd\u4e3b\u5e74\u7eaa\u867d\u8f7b\uff0c\u89c1\u95fb\u5374\u6e0a\u535a\u5f97\u3000\u5f88\uff0c \u53ea\u662f\u65bd\u4e3b\u53ef\u77e5\u9053\u8fd9\u4e09\u4f4d\u6b66\u6797\u524d\u8f88\u4e3a\u4f55\u7a81\u7136\u5931\u8e2a\uff0c\u4ed6\u4eec\u6240\u7559\u4e0b\u7684\u79d8\u5b9d\uff0c\u53c8\u4e3a\u4ec0\u4e48\u3000\u5728\u6b66\u6797\u4e2d\u6df9 \u6ca1\u5982\u8bb8\u591a\u5e74\u7684\u539f\u56e0\u5417\uff1f\u201d \u3000\u3000\u3000\u8fd9\u4f4d\u5c11\u6797\u540d\u50e7\uff0c\u7684\u786e\u6c89\u5f97\u4f4f\u6c14\uff0c\u6162\u6761\u65af\u7406\u5730\u4e00\u53e5\u53e5\u8bf4\u7740\uff0c\u5374\u4ee4\u5728\u5ea7\u7fa4\u8c6a\u90fd\u6025\u5f97\u3000\u6068\u4e0d \u5f97\u62c9\u4f4f\u4ed6\u7684\u9886\u5b50\uff0c\u53eb\u4ed6\u75db\u75db\u5feb\u5feb\u4ed6\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u3000\u4f46\u662f\u8fd9\u58a8\u4e00\u4e0a\u4eba\u5728\u6b66\u6797\u4e2d\u8eab\u4efd\u751a\u9ad8\uff0c\u867d\u7136\u5173\u5b50\u5356\u5f97\u4ee4\u5927\u5bb6\u90fd\u7259\u75d2\u75d2\u7684\uff0c\u4f46\u5927\u5bb6\u3000\u5374\u53ea \u6709\u5e72\u77aa\u7740\u773c\uff0c\u76f4\u52fe\u52fe\u5730\u671b\u7740\u4ed6\u3002 \u3000\u3000\u3000\u8fd9\u5176\u4e2d\u53ea\u6709\u7a77\u795e\u51cc\u9f99\u5374\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u8fd9\u4e9b\u539f\u56e0\u6211\u8001\u5316\u5b50\u6b7b\u4e86\u4e4b\u540e\uff0c\u5012\u8981\u8fdb\u5165\u3000\u5341\u516b \u5c42\u5730\u72f1\u91cc\u53bb\u95ee\u95ee\u90a3\u4e09\u4f4d\u524d\u8f88\u3002\u201d\u8bf4\u7f62\uff0c\u53c8\u662f\u4e00\u9635\u5927\u7b11\uff0c\u5f15\u5f97\u7fa4\u8c6a\u4e5f\u6709\u4e9b\u5fcd\u4fca\u4e0d\u3000\u4f4f\u3002 \u3000\u3000\u3000\u6bdb\u6587\u742a\u7adf\u4f38\u51fa\u7ea4\u624b\u63a9\u7740\u5634\uff0c\u751f\u6015\u5657\u55e4\u7b11\u51fa\u6765\u58f0\u6765\u3002 \u3000\u3000\u3000\u58a8\u4e00\u4e0a\u4eba\u5374\u50cf\u662f\u5168\u7136\u4e0d\u61c2\u4ed6\u6d3b\u4e2d\u7684\u8ba5\u5632\uff0c\u4f9d\u7136\u5408\u5341\u9053\uff1a\u201c\u8fd9\u4e8b\u672c\u662f\u6b66\u6797\u4e2d\u7684\u4e00\u3000\u4ef6\u79d8 \u5bc6\uff0c\u8001\u8896\u6b64\u523b\u5374\u4e0d\u5f97\u4e0d\u8bf4\u51fa\u6765\u3002\u201d\u4ed6\u5fae\u987f\u4e00\u4e0b\uff0c\u50cf\u662f\u5728\u5fc3\u4e2d\u5c06\u8fd9\u4e8b\u7684\u5934\u7eea\u6574\u7406\u3000\u4e00\u4e0b\uff0c\u7136\u540e \u624d\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u5929\u533b\u3002\u5730\u715e\u3001\u4eba\u9b54\uff0c\u8fd9\u4e09\u4eba\u867d\u662f\u7ed3\u76df\u5144\u5f1f\uff0c\u4f46\u5fc3\u6027\u5374\u6781\u3000\u4e3a\u4e0d\u540c\uff0c\u5929\u533b\u5434\u4e0d \u53ef\u867d\u7136\u8eab\u7f6e\u7eff\u6797\uff0c\u5374\u662f\u53e6\u6709\u7528\u5fc3\uff0c\u4e0d\u8fc7\u53ea\u662f\u60f3\u5c06\u7eb7\u4e89\u6700\u591a\u7684\u7eff\u6797\u3000\u9053\u6574\u987f\u4e00\u4e0b\uff0c\u800c\u5730\u715e\u5e38\u601d \u5948\uff0c\u4eba\u9b54\u53f8\u7a7a\uff0c\u5374\u662f\u6b66\u6797\u4e2d\u7684\u9b54\u5934\uff0c\u53ea\u4e0d\u8fc7\u4ed6\u4eec\u5728\u5929\u533b\u6069\u3000\u5a01\u5e76\u65bd\u4e4b\u4e0b\uff0c\u6b66\u529f\u53c8\u4e3a\u5176\u6240\u6151\uff0c \u662f\u4ee5\u591a\u5e74\u6765\uff0c\u2018\u4e09\u624d\u8054\u76df\u2019\u5728\u6b66\u6797\u4e2d\u9887\u8457\u4fa0\u540d\u3002\u201d \u3000\u3000\u3000\u4ed6\u5fae\u559f\u4e00\u58f0\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u662f\u4ee5\u5730\u715e\u3001\u4eba\u9b54\uff0c\u8868\u9762\u867d\u5982\u6b64\uff0c\u6697\u4e2d\u5374\u5bf9\u5929\u533b\u5434\u8001\u3000\u524d\u8f88 \u79ef\u6028\u9887\u6df1\uff0c\u540e\u6765\u7adf\u4e58\u5434\u8001\u524d\u8f88\u4e0d\u5907\uff0c\u70b9\u4e86\u4ed6\u8001\u4eba\u5bb6\u7684\u2018\u5929\u6b8b\uff0c\u91cd\u7a74\uff0c\u53ea\u662f\u4ed6\u4e24\u3000\u4eba\u4e8b\u60c5\u505a\u5f97 \u6781\u4e3a\u9690\u79d8\uff0c\u5929\u4e0b\u6b66\u6797\u90fd\u7edd\u4e0d\u77e5\u9053\u3002\u201d\u201c\u5434\u8001\u524d\u8f88\u88ab\u70b9\u4e2d\u201d\u5929\u6b8b\u201d\u7a74\u540e\uff0c\u3000\u6b66\u529f\u81ea\u7136\u5c3d\u5931\uff0c\u53c8 \u88ab\u8f6f\u7981\uff0c\u81ea\u6b64\u5730\u715e\u3001\u4eba\u9b54\u4fbf\u518d\u65e0\u987e\u5fcc\uff0c\u4e3a\u6240\u6b32\u4e3a\u8d77\u6765\uff0c\u90a3\u4f4d\u5434\u8001\u3000\u524d\u8f88\u4f24\u5fc3\u4e4b\u4e0b\uff0c\u4e00\u5fc3\u5411 \u4f5b\uff0c\u8fd9\u4f4d\u8001\u524d\u8f88\u672c\u662f\u6781\u5177\u6167\u6839\u4e4b\u4eba\uff0c\u7688\u4f9d\u6211\u4f5b\u540e\uff0c\u7adf\u53c2\u900f\u4e09\u3000\u4e58\u5999\u8c1b\uff0c\u4ee5\u4e0d\u53ef\u601d\u8bae\u7684\u80fd\u529b\uff0c \u7ec8\u65e5\u5411\u4ed6\u4e24\u4e2a\u6ee1\u8eab\u9b54\u969c\u7684\u76df\u5f1f\u5ba3\u626c\u4f5b\u529b\uff0c\u6211\u4f5b\u666e\u6e21\u4f17\u3000\u751f\uff0c\u5c45\u7136\u4f7f\u5f97\u90a3\u4e24\u4e2a\u9b54\u5934\u4e5f\u4e3a\u4e4b\u653e\u4e0b \u5c60\u5200\u4e86\u3002\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u8fd9\u4f4d\u9ad8\u50e7\u5782\u9996\u4f4e\u8bf5\u4e86\u4e00\u3000\u58f0\u4f5b\u53f7\uff0c\u7136\u540e\u53cc\u76ee\u5fae\u5f20\uff0c\u53c8\u9053\uff1a\u201c\u8fd9\u4e09\u4f4d\u6b66 \u6797\u524d\u8f88\u5f02\u4eba\u653e\u4e0b\u5c60\u5200\u540e\uff0c\u5c31\u5c06\u751f\u5e73\u6240\u5f97\u3000\u4e4b\u94b1\u8d22\uff0c\u4ee5\u53ca\u795e\u5175\u5229\u5668\u7b49\u7269\uff0c\u6c89\u4e4b\u4e8e\u6e56\u5e95\uff0c\u7136\u540e\u4fbf \u8fde\u8882\u800c\u4e0a\u5c11\u5ba4\uff0c\u5728\u5f53\u5e74\u655d\u6559\u638c\u95e8\u3000\u7956\u5e08\u7684\u5243\u5ea6\u4e4b\u4e0b\uff0c\u7688\u4f9d\u4e09\u5b9d\uff0c\u51fa\u5bb6\u4e3a\u50e7\u4e86\u3002\u201d \u3000\u3000\u3000\u8fd9\u6bb5\u6b66\u6797\u79d8\u8f9b\uff0c\u7ecf\u8fd9\u4f4d\u9ad8\u50e7\u5a13\u5a13\u9053\u6765\uff0c\u5728\u5ea7\u7fa4\u8c6a\uff0c\u679c\u542c\u5f97\u90fd\u4e3a\u4e4b\u795e\u5f80\u3002 \u3000\u3000\u3000\u58a8\u4e00\u4e0a\u4eba\u5fae\u987f\u4e00\u4e0b\uff0c\u53c8\u9053\uff1a\u201c\u8fd9\u4e09\u4f4d\u524d\u8f88\u5243\u5ea6\u4e8e\u6211\u5c11\u6797\u5bfa\u540e\uff0c\u5c31\u5c06\u90a3\u85cf\u5b9d\u4e4b\u5730\u3000\uff0c\u7980 \u544a\u4e86\u638c\u6559\u7956\u5e08\uff0c\u8bf7\u638c\u6559\u7956\u5e08\u7269\u8272\u4e00\u4e2a\u771f\u80fd\u4e0a\u4f53\u5929\u5fc3\uff0c\u4e0b\u4f53\u82cd\u751f\u7684\u6b63\u76f4\u4fa0\u58eb\uff0c\u5c06\u3000\u8fd9\u4efd\u79d8\u85cf\u4ea4 \u7ed9\u8fd9\u4eba\uff1b\u8ba9\u4ed6\u4e3a\u82cd\u751f\u9020\u4e9b\u798f\u5229\u3002\u201d \u3000\u3000\u3000\u201c\u4f46\u90a3\u65f6\u638c\u6559\u7956\u5e08\u65e9\u5df2\u4e0d\u95f4\u4e16\u4e8b\uff0c\u5374\u5c06\u8fd9\u85cf\u5b9d\u4e4b\u56fe\u5212\u6210\u4e09\u4efd\uff0c\u4e00\u4efd\u4ea4\u7ed9\u5f53\u65f6\u6b66\u3000\u5f53\u6d3e \u7684\u4e00\u4ee3\u5251\u8c6a\u767d\u8001\u5b97\u7956\uff0c\u4e00\u4efd\u4ea4\u7ed9\u672c\u5bfa\u7684\u524d\u8f88\u795e\u50e7\u6f84\u7a7a\u7956\u5e08\uff0c\u53e6\u4e00\u4efd\u5374\u4ea4\u7ed9\u4e86\u4ed6\u3000\u8001\u4eba\u5bb6\u552f\u4e00 \u5668\u91cd\u7684\u5fd8\u5e74\u4e4b\u4ea4\uff0c\u4e5f\u5c31\u662f\u6614\u5e74\u540d\u9707\u5929\u4e0b\u7684\u2018\u6d77\u5929\u5b64\u71d5\uff0c\u3002\u201d\u4e00\u8bf4\u51fa\u8fd9\u4e2a\u3000\u540d\u5b57\uff0c\u5728\u5ea7\u7fa4\u8c6a\u90fd \u4e0d\u7981\u5fae\u201c\u5440\u201d\u4e00\u58f0\uff0c\u7f2a\u6587\u7aef\u8d77\u9762\u524d\u7684\u8336\u676f\uff0c\u559d\u4e86\u4e00\u53e3\u676f\u4e2d\u7684\u51b7\u8336\u3000\uff0c\u56de\u5934\u4e00\u671b\u6bdb\u6587\u742a\uff0c\u5583\u5583 \u4f4e\u8bed\u9053\uff1a\u201c\u742a\u59b9\uff0c\u4f60\u770b\uff0c\u5929\u5df2\u7ecf\u4eae\u4e86\u3002\u201d \u3000\u3000\u3000\u6bdb\u6587\u742a\u56de\u9996\u7a97\u5916\uff0c\u679c\u7136\u5df2\u6cdb\u9c7c\u9752\uff0c\u5728\u5ea7\u7fa4\u8c6a\u51dd\u795e\u503e\u542c\u8fd9\u4ef6\u79d8\u8f9b\uff0c\u7adf\u4e0d\u77e5\u4e1c\u65b9\u4e4b\u3000\u65e2 \u767d\u3002 \u3000\u3000\u3000\u58a8\u4e00\u4e0a\u4eba\u5fae\u54b3\u4e00\u58f0\uff0c\u53c8\u9053\uff1a\u201c\u90a3\u65f6\u638c\u6559\u7956\u5e08\u7684\u610f\u601d\uff0c\u5f53\u7136\u662f\u5e0c\u671b\u8fd9\u4e09\u4f4d\u524d\u8f88\u80fd\u3000\u5229\u7528 \u8fd9\u85cf\u7269\u505a\u4e00\u756a\u9020\u798f\u82cd\u751f\u7684\u4e8b\u4e1a\uff0c\u53ea\u662f\u5f53\u65f6\u8fd9\u4e09\u4f4d\u524d\u8f88\u90fd\u5df2\u529f\u53c2\u9020\u5316\uff0c\u5f53\u7136\u4e5f\u7528\u3000\u4e0d\u7740\u8fd9\u4e9b\uff0c \u672c\u5bfa\u6f84\u7a7a\u7956\u5e08\u7684\u90a3\u4efd\u85cf\u5b9d\u4e4b\u56fe\uff0c\u4ee3\u4ee3\u76f8\u4f20\uff0c\u73b0\u5728\u6b63\u4f20\u5230\u8005\u8896\u8fd9\u91cc\uff0c\u81f3\u4e8e\u3000\u53e6\u4e24\u4efd\u85cf\u5b9d\u4e4b\u56fe\uff0c \u60f3\u5fc5\u4e5f\u662f\u4e3a\u4e86\u627e\u4e0d\u7740\u9002\u5f53\u7684\u4eba\u9009\uff0c\u662f\u4ee5\u767e\u5341\u5e74\u6765\uff0c\u8fd9\u5b9d\u85cf\u4fbf\u5728\u6b66\u3000\u6797\u4e2d\u6df9\u6ca1\u4e86\u3002\u201d \u3000\u3000\u3000\u5728\u5ea7\u7fa4\u8c6a\uff0c\u6b64\u523b\u624d\u604d\u7136\u900f\u51fa\u4e00\u53e3\u6c14\uff0c\u5bf9\u8fd9\u4ef6\u4e8b\u7684\u771f\u8c61\uff0c\u660e\u767d\u4e86\u4e00\u4e9b\u3002 \u3000\u3000\u3000\u58a8\u4e00\u4e0a\u4eba\u63a5\u7740\u53c8\u9053\uff1a\u201c\u4f46\u76ee\u524d\u5374\u7a81\u7136\u6709\u4eba\u98de\u67ec\u5c11\u5ba4\uff0c\u8bf4\u662f\u8fd9\u4efd\u85cf\u5b9d\u56fe\u5df2\u51fa\u73b0\u4e8e\u3000\u6c5f \u6e56\uff0c\u8001\u8872\u95fb\u8baf\uff0c\u624d\u7acb\u523b\u4e0b\u5c71\uff0c\u56e0\u4e3a\u4f17\u6240\u5468\u77e5\uff0c\u8fd9\u4efd\u5b9d\u85cf\u5173\u7cfb\u751a\u5927\uff0c\u82e5\u4e3a\u4e0d\u8096\u4e4b\u5f92\u3000\u6240\u5f97\uff0c\u4fbf \u662f\u7978\u5bb3\uff0c\u8001\u8872\u6b64\u523b\u4e0b\u5c71\uff0c\u4fbf\u662f\u8981\u95ee\u6e05\u695a\u6b64\u523b\u5f97\u5230\u79d8\u85cf\u4e4b\u56fe\u7684\u4eba\uff0c\u5f97\u5230\u6b64\u56fe\u3000\u7684\u771f\u8c61\u2026\u2026\u201d \u3000\u3000\u3000\u4ed6\u53cc\u76ee\u7a81\u5730\u4e00\u5f20\uff0c\u51b7\u7535\u822c\u7684\u76ee\u5149\uff0c\u5728\u6bdb\u81ec\u7b49\u4e09\u4eba\u9762\u4e0a\u4e00\u626b\uff0c\u53c8\u9053\uff1a\u201c\u82e5\u662f\u6b64\u4eba\u3000\u7684\u85cf \u5b9d\u4e4b\u56fe\uff0c\u679c\u771f\u5f97\u81ea\u6b66\u5f53\uff0c\u751a\u81f3\u6216\u662f\u5f97\u81ea\u2018\u6d77\u5929\u5b64\u71d5\u2019\uff0c\u90a3\u8001\u8896\u5bf9\u8fd9\u4e24\u4f4d\u6240\u6258\u4e4b\u3000\u4eba\uff0c\u81ea\u7136\u653e \u5fc3\u5f97\u5f88\uff0c\u4f46\u6b64\u4eba\u5f97\u5230\u8fd9\u4efd\u79d8\u85cf\u7684\u6765\u5386\u82e5\u662f\u4e0d\u6b63\uff0c\u90a3\u4e48\u8001\u8872\u867d\u7136\u7f6e\u8eab\u65b9\u5916\u3000\uff0c\u4f46\u8d23\u4efb\u6240\u5728\uff0c\u8bf4 \u4e0d\u5f97\u4e5f\u8981\u4e3a\u6b64\u51fa\u624b\u4e86\u3002\u201d \u3000\u3000\u3000\u8bf4\u5230\u6b64\u5904\uff0c\u8fd9\u4f4d\u5c11\u6797\u9ad8\u50e7\u7684\u8bed\u58f0\u4e2d\u5df2\u900f\u51fa\u4e00\u79cd\u6151\u4eba\u7684\u5a01\u4e25\u6765\u3002 \u3000\u3000\u3000\u76ee\u4e2d\u7684\u5bd2\u5149\uff0c\u5728\u7075\u86c7\u6bdb\u81ec\u3001\u7a77\u795e\u51cc\u9f99\u548c\u706b\u773c\u91d1\u96d5\u4e09\u4eba\u7684\u9762\u4e0a\u51db\u7136\u79fb\u52a8\u7740\u3002 \u3000\u3000\u3000\u5728\u5ea7\u7fa4\u8c6a\uff0c\u6b64\u523b\u5404\u5404\u8179\u4e2d\u5df2\u81ea\u96ea\u4eae\uff0c\u77e5\u9053\u8fd9\u58a8\u4e00\u4e0a\u4eba\u8bdd\u867d\u672a\u660e\u8bf4\u51fa\u6765\uff0c\u4f46\u8bdd\u4e2d\u3000\u7684\u542b \u610f\uff0c\u65e0\u5f02\u5df2\u5728\u8bf4\u8fd9\u7075\u86c7\u6bdb\u81ec\u5f97\u6b64\u79d8\u56fe\u7684\u6765\u5386\u4e0d\u6b63\uff0c\u4e5f\u5c31\u662f\u8bf4\u4ed6\u662f\u52a8\u4e0d\u5f97\u8fd9\u4efd\u5b9d\u3000\u85cf\u7684\u4e86\u3002 \u3000\u3000\u3000\u7a77\u795e\u51cc\u9f99\u867d\u7136\u6e38\u620f\u6c5f\u6e56\uff0c\u884c\u4e8b\u4ecb\u4e8e\u6b63\u90aa\u4e4b\u95f4\uff0c\u4f46\u5230\u5e95\u662f\u6b66\u6797\u4e2d\u6709\u6570\u7684\u524d\u8f88\u9ad8\u4eba\u3000\uff0c\u6b64 \u523b\u542c\u4e86\u58a8\u4e00\u4e0a\u4eba\u7684\u4e00\u756a\u8bdd\uff0c\u5148\u524d\u9732\u5728\u9762\u4e0a\u7684\u90a3\u79cd\u4e0d\u5fff\u3001\u4e0d\u6ee1\u7684\u795e\u8272\uff0c\u5df2\u7136\u6d88\u5931\u3000\u3002\u706b\u773c\u91d1\u96d5 \u8427\u8fdf\u5df2\u662f\u795e\u8272\u5fae\u53d8\uff0c\u673a\u950b\u8001\u8fa3\u7684\u8bdd\uff0c\u4e5f\u518d\u8bf4\u4e0d\u51fa\u53e3\u3002 \u3000\u3000\u3000\u7075\u86c7\u6bdb\u81ec\u8138\u4e0a\uff0c\u5374\u4ecd\u5e26\u7740\u4e00\u79cd\u83ab\u6d4b\u9ad8\u6df1\u7684\u6837\u5b50\uff0c\u50cf\u662f\u65e9\u5df2\u80f8\u6709\u6210\u7af9\u4f3c\u7684\uff0c\u524a\u85cf\u3000\u7684\u5634 \u5507\u8fb9\uff0c\u5374\u6302\u7740\u4e00\u4e1d\u5978\u72e1\u7684\u7b11\u5bb9\u3002 \u3000\u3000\u3000\u7a77\u795e\u51cc\u9f99\u6c89\u601d\u534a\u664c\uff0c\u624d\u5fae\u5fae\u53f9\u9053\uff1a\u201c\u6211\u8001\u5316\u5b50\u5b9e\u5728\u4e0d\u77e5\u9053\u8fd9\u5176\u4e2d\u7adf\u6709\u7740\u8fd9\u4e48\u591a\u3000\u5939 \u7f20\uff0c\u8bf4\u6765\u5947\u602a\uff0c\u6211\u8001\u5316\u5b50\u77e5\u9053\u8fd9\u4e8b\uff0c\u4e5f\u662f\u56e0\u4e3a\u95e8\u4e0b\u5f1f\u5b50\u7a81\u7136\u63a5\u5230\u4e00\u5c01\u5bc6\u5c01\u7684\u4fe1\u67ec\u3000\uff0c\u4f46\u662f\u5f97 \u5230\u8fd9\u4efd\u85cf\u5b9d\u4e4b\u56fe\u7684\u5f1f\u5b50\uff0c\u6b64\u523b\u5df2\u6b7b\u5728\u6d2a\u6cfd\u6e56\u4e0a\uff0c\u4ed6\u8fd9\u4efd\u79d8\u56fe\u7684\u6765\u5386\uff0c\u6211\u8001\u3000\u5316\u5b50\u5374\u4e0d\u77e5 \u9053\u3002\u201d \u3000\u3000\u3000\u58a8\u4e00\u4e0a\u4eba\u7684\u76ee\u5149\u70af\u7136\u4e00\u8f6c\uff0c\u671b\u7740\u90a3\u59cb\u7ec8\u4e0d\u53d1\u4e00\u8a00\u7684\u6e05\u98ce\u5251\u6731\u767d\u7fbd\u9053\uff1a\u201c\u6731\u5927\u4fa0\u3000\u8fdc\u6765 \u6b64\u95f4\uff0c\u60f3\u5fc5\u4e5f\u662f\u4e3a\u4e86\u6b64\u4e8b\uff0c\u8001\u8872\u8bf7\u6559\u4e00\u53e5\uff0c\u8fd9\u4efd\u85cf\u5b9d\u4e4b\u56fe\uff0c\u662f\u5426\u51fa\u81ea\u6731\u5927\u4fa0\u4e4b\u3000\u624b\uff0c\u4ea4\u7ed9\u90a3 \u4f4d\u4e10\u95e8\u5f1f\u5b50\u7684\u5462\uff1f\u201d \u3000\u3000\u3000\u6e05\u98ce\u5251\u6731\u767d\u7fbd\u59cb\u7ec8\u6c89\u5750\u7740\u4e00\u8a00\u672a\u53d1\uff0c\u9664\u4e86\u5728\u542c\u7740\u58a8\u4e00\u4e0a\u4eba\u7684\u8bdd\u65f6\uff0c\u9762\u8272\u66fe\u53d8\u4e86\u3000\u4e00\u4e0b \u4e4b\u5916\uff0c\u5374\u6b63\u548c\u516b\u9762\u73b2\u73d1\u53e3\u4e2d\u6240\u5f62\u5bb9\u7684\u201c\u6b7b\u4eba\u201d\u65e0\u5f02\u3002 \u3000\u3000\u3000\u6b64\u523b\u8fd9\u4f4d\u6b66\u5f53\u6d3e\u7684\u540d\u5251\u624b\uff0c\u5374\u500f\u7136\u7ad9\u4e86\u8d77\u6765\uff0c\u7ed5\u8fc7\u4e00\u5f20\u684c\u5b50\uff0c\u8d70\u5230\u90a3\u58a8\u4e00\u4e0a\u4eba\u3000\u7684\u8eab \u4fa7\uff0c\u7adf\u4fef\u4e0b\u8eab\u53bb\uff0c\u5728\u58a8\u4e00\u4e0a\u4eba\u7684\u8033\u8fb9\u8bf4\u4e86\u51e0\u53e5\u8bdd\u3002 \u3000\u3000\u3000\u7fa4\u8c6a\u6570\u5341\u53cc\u773c\u775b\uff0c\u6b64\u523b\u4e0d\u7981\u90fd\u671b\u5728\u8fd9\u4e24\u4eba\u9762\u4e0a\uff0c\u5374\u89c1\u8fd9\u4e24\u4f4d\u540d\u91cd\u6b66\u6797\u7684\u5f53\u4ee3\u540d\u3000\u4eba\uff0c \u6b64\u523b\u8138\u4e0a\u7adf\u90fd\u6d8c\u73b0\u7740\u4e00\u79cd\u96be\u4ee5\u9886\u4f1a\u7684\u7b11\u5bb9\u3002 \u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u7136\u540e\u2014\u2014 \u3000\u3000\u3000\u90a3\u6e05\u98ce\u5251\u6731\u767d\u7fbd\u4e00\u957f\u8eab\uff0c\u5411\u56db\u5ea7\u7fa4\u8c6a\u5fae\u4e00\u62b1\u62f3\uff0c\u4e00\u8a00\u4e0d\u53d1\u5730\u8d70\u5411\u697c\u68af\u53e3\uff0c\u8e6d\uff0c\u3000\u8e6d\uff0c \u8e6d\uff0c\u8fd9\u6b66\u5f53\u540d\u5251\u5ba2\u7adf\u4e0b\u697c\u8d70\u4e86\u3002 \u3000\u3000\u3000\u7fa4\u8c6a\u4e0d\u7981\u83ab\u540d\u5176\u5999\uff0c\u6bdb\u6587\u742a\u4e5f\u5fae\u98a6\u9edb\u7709\uff0c\u6084\u7136\u8bed\u5411\u7f2a\u6587\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u3000\u771f\u6559 \u4eba\u4e0d\u61c2\uff1f\u201d \u3000\u3000\u3000\u7f2a\u6587\u4e00\u957f\u8eab\uff0c\u6253\u4e86\u534a\u4e2a\u5475\u6b20\uff0c\u773c\u89d2\u5374\u4e5f\u6d8c\u73b0\u7740\u4e00\u4e1d\u548c\u58a8\u4e00\u4e0a\u4eba\u3001\u6731\u81ea\u7fbd\u76f8\u540c\u7684\u3000\u7b11 \u610f\uff0c\u7136\u540e\u4ed6\u53c8\u5411\u6bdb\u6587\u742a\u4e00\u7b11\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4e0d\u61c2\u7684\u4e8b\uff0c\u4f60\u603b\u6709\u4e00\u5929\u4f1a\u77e5\u9053\u7684\uff0c\u4f60\u7740\u3000\u4e2a\u4ec0\u4e48\u6025 \u5462\u201d\u6bdb\u6587\u742a\u5c0f\u5634\u4e00\u561f\uff0c\u9053\uff1a\u201c\u6211\u7b49\u4e0d\u53ca\u4e86\u2014\u2014\u201d\u8bed\u72b9\u672a\u5b8c\uff0c\u62ac\u773c\u5904\uff0c\u5374\u89c1\u3000\u90a3\u58a8\u4e00\u4e0a\u4eba\u4e5f\u7ad9 \u4e86\u8d77\u6765\uff0c\u53e3\u4e2d\u957f\u541f\u4e00\u58f0\u4f5b\u53f7\uff0c\u53cc\u638c\u5408\u5341\uff0c\u6717\u58f0\u9053\uff1a\u201c\u5929\u9053\u5faa\u73af\uff0c\u62a5\u3000\u5e94\u4e0d\u723d\uff0c\u4e00\u996e\u4e00\u5544\uff0c\u90fd \u6709\u5b9a\u6570\uff0c\u975e\u6211\u5e94\u5f97\u4e4b\u7269\uff0c\u6025\u4e5f\u65e0\u76ca\uff0c\u4f46\u671b\u65bd\u4e3b\u4eec\u4e0a\u4f53\u5929\u5fc3\uff0c\u3000\u597d\u81ea\u4e3a\u4e4b\uff0c\u597d\u81ea\u4e3a\u4e4b\u3002\u201d \u3000\u3000\u3000\u8fd9\u5c11\u6797\u795e\u50e7\u4e5f\u4e0d\u77e5\u5bf9\u8c01\u8bf4\u4e86\u8fd9\u51e0\u53e5\u8bdd\uff0c\u4e00\u5c55\u5bbd\u5927\u7684\u8888\u88df\uff0c\u7adf\u4e5f\u98d8\u7136\u4e0b\u697c\u53bb\u4e86\uff0c\u3000\u5bf9\u90a3 \u201c\u4e09\u624d\u5b9d\u85cf\u201d\uff0c\u7adf\u4e0d\u518d\u8fc7\u95ee\u3002 \u3000\u3000\u3000\u7fa4\u8c6a\u9762\u9762\u76f8\u89d1\uff0c\u90fd\u4f5c\u58f0\u4e0d\u5f97\uff0c\u6b64\u4e8b\u672c\u5df2\u5982\u4e00\u6761\u672c\u5df2\u66f2\u6298\u7684\u7f8a\u80a0\u5c0f\u5f84\uff0c\u6e10\u884c\u6e10\u4e3a\u3000\u5f00\u6717 \u5e73\u5766\uff0c\u54ea\u77e5\u81f3\u6b64\u5374\u53c8\u5947\u5cf0\u7a81\u8d77\uff0c\u628a\u524d\u9762\u7684\u8def\u90fd\u6321\u4f4f\u4e86\uff0c\u524d\u9762\u5230\u5e95\u662f\u4ec0\u4e48\u6837\u5b50\uff0c\u3000\u5927\u5bb6\u867d\u7740 \u6025\uff0c\u5374\u4e00\u70b9\u4e5f\u770b\u4e0d\u5230\u3002 \u3000\u3000\u3000\u8fd9\u4e24\u4eba\u4e00\u8d70\uff0c\u7a77\u795e\u51cc\u9f99\u4f4e\u5934\u6115\u4e86\u534a\u664c\uff0c\u7a81\u7136\u4e00\u987f\u8db3\uff0c\u56de\u9996\u5411\u4ed6\u95e8\u4e0b\u7684\u53e6\u51e0\u4e2a\u957f\u3000\u8001\u53f9 \u9053\uff1a\u201c\u6211\u4eec\u7a77\u5bb6\u5e2e\u5929\u751f\u7684\u7a77\u547d\uff0c\u8fd9\u79cd\u5b9d\u7269\u5927\u6982\u4e5f\u65e0\u7f18\u5f97\u5230\uff0c\u201d\u8fd9\u4f4d\u6b66\u6797\u5f02\u4eba\u7adf\u3000\u6717\u7136\u4e00\u9635\u957f \u7b11\uff0c\u6325\u624b\u9053\uff1a\u201c\u8d70\uff0c\u8d70\uff0c\u8d70\uff0c\u4f60\u6211\u9152\u8db3\u996d\u9971\uff0c\u8fd8\u7559\u5728\u8fd9\u91cc\u5e72\u4ec0\u4e48\uff1f\u201d\u7834\u3000\u8896\u4e00\u62c2\uff0c\u5f53\u5148\u8d70 \u4e86\u3002 \u3000\u3000\u3000\u6b64\u523b\u53e6\u4e00\u7626\u957f\u7684\u4e10\u8005\uff0c\u7ad9\u4e86\u8d77\u6765\uff0c\u6df1\u9677\u7684\u53cc\u76ee\uff0c\u4e00\u77aa\u7075\u86c7\u6bdb\u81ec\uff0c\u50cf\u662f\u60f3\u8bf4\u4ec0\u4e48\u3000\uff0c\u4f46 \u5374\u53ef\u89c1\u7a77\u795e\u51cc\u9f99\u5fae\u4e00\u62db\u624b\uff0c\u4ed6\u4fbf\u4e5f\u548c\u5176\u4ed6\u51e0\u4e2a\u4e10\u8005\uff0c\u4e00\u8a00\u4e0d\u53d1\u5730\u8d70\u4e86\u4e0b\u697c\uff0c\u7531\u3000\u6b64\uff0c\u7075\u86c7\u6bdb \u81ec\u4fbf\u5c06\u7a77\u5bb6\u5e2e\u5f97\u7f6a\u4e86\u3002 \u3000\u3000\u3000\u7f2a\u6587\u671b\u7740\u8fd9\u4e9b\u9690\u8eab\u4e8e\u4e5e\u4e10\u4e4b\u4e2d\u7684\u4fa0\u58eb\uff0c\u50cf\u662f\u5fae\u5fae\u70b9\u4e86\u4e00\u4e0b\u5934\uff0c\u5583\u5583\u4f4e\u8bed\u7740\uff1a\u201c\u3000\u786e\u662f \u8fbe\u4eba\uff01\u786e\u662f\u8fbe\u4eba\uff01\u201d \u3000\u3000\u3000\u6bdb\u6587\u742a\u4fcf\u76ee\u4e00\u8f6c\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u2026\u7f2a\u6587\u54c8\u54c8\u4e00\u7b11\uff0c\u5374\u4f38\u8fc7\u5934\u53bb\uff0c\u5411\u90a3\u8fb9\u3000\u5750\u7740 \u7684\u516b\u9762\u73b2\u73d1\u80e1\u4e4b\u8f89\u9053\uff1a\u201c\u5c0f\u5f1f\u8bf7\u6559\u80e1\u5144\u4e00\u4e8b\uff0c\u6bdb\u5927\u4fa0\u548c\u80e1\u5144\u90fd\u5230\u6b64\u5904\u6765\u4e86\uff0c\u90a3\u3000\u4e48\u6b64\u523b\u5df2\u5728 \u6d2a\u6cfd\u6e56\u6309\u56fe\u5bfb\u5b9d\u7684\uff0c\u53c8\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u3000\u80e1\u4e4b\u8f89\u5148\u6014\u4e86\u4e00\u4e0b\uff0c\u7136\u540e\u4e5f\u7b11\u9053\uff1a\u201c\u7f2a\u8001\u5f1f\u771f\u662f\u806a\u660e\u4eba\uff0c\u806a\u660e\u4eba\u4e00\u201d\u4ed6\u8bed\u58f0\u4e00\u3000\u987f\uff0c \u7a81\u7136\u653e\u4f4e\u58f0\u8c03\u9053\uff1a\u201c\u8001\u5f1f\u4f60\u65e2\u7136\u5df2\u731c\u51fa\u6765\u4e86\uff0c\u6211\u4e0d\u59a8\u544a\u8bc9\u4f60\uff0c\u5230\u6d2a\u6cfd\u6e56\u53bb\u7684\uff0c\u3000\u662f\u6211\u8ba1\u4e8c \u54e5\uff0c\u8001\u5f1f\u4f60\u5374\u8fd8\u6ca1\u6709\u89c1\u5230\u8fc7\u4ed6\u54e9\u3002\u201d \u3000\u3000\u3000\u5750\u5728\u4e2d\u95f4\u7684\u6bdb\u6587\u742a\uff0c\u5f53\u7136\u542c\u5230\u4e86\u4ed6\u4eec\u7684\u8bdd\uff0c\u4e0d\u7981\u8131\u53e3\u9053\uff1a\u201c\u539f\u6765\u8ba1\u4e8c\u53d4\u5148\u5230\u6d2a\u3000\u6cfd\u6e56 \u53bb\u4e86\uff01\u201d \u3000\u3000\u3000\u706b\u773c\u91d1\u96d5\u8427\u8fdf\u672c\u4e5f\u56e0\u6e05\u98ce\u5251\u6731\u767d\u7fbd\u3002\u5c11\u6797\u58a8\u4e00\u4e0a\u4eba\u4ee5\u53ca\u7a77\u795e\u51cc\u9f99\u7684\u7a81\u7136\u79bb\u53bb\uff0c\u3000\u7591\u60d1 \u4e0d\u5df2\uff0c\u6b63\u81ea\u4fef\u9996\u6c89\u601d\uff0c\u4e00\u65f6\u5fd8\u8bb0\u4e86\u8be5\u5982\u4f55\u5904\u7406\u73b0\u4e0b\u7684\u5c40\u9762\uff0c\u6b64\u523b\u6bdb\u6587\u742a\u7684\u4e00\u53e5\u3000\u8bdd\uff0c\u5374\u4f7f\u4ed6 \u84e6\u7136\u60ca\u9192\u4e86\u3002 \u3000\u3000\u3000\u4ed6\u5fc3\u4e2d\u4e00\u8f6c\uff0c\u4e5f\u81ea\u731c\u900f\u4e86\u6b64\u4e8b\u5176\u4e2d\u7684\u9690\u79d8\uff0c\u4e0d\u7981\u6697\u9a82\u7740\uff1a\u201c\u6211\u8001\u5934\u5b50\u4eca\u65e5\u5012\u771f\u3000\u662f\u4e03 \u5341\u5c81\u8001\u5a18\u5012\u8e66\u5b69\u513f\uff0c\u60f3\u4e0d\u5230\u7adf\u5403\u8fd9\u59d3\u6bdb\u7684\u5c0f\u5b50\u611a\u5f04\u4e86\uff0c\u5c06\u9ad8\u3001\u6d2a\u6c34\u5be8\u91cc\u5f97\u529b\u7684\u3000\u4eba\uff0c\u90fd\u8c03\u5230 \u8fd9\u91cc\u6765\uff0c\u5374\u6559\u4eba\u5bb6\u4ece\u4ece\u5bb9\u5bb9\u5730\u5230\u9ad8\u3001\u6d2a\u4e24\u6e56\u53bb\u5bfb\u5b9d\uff0c\u201d\u4e00\u5ff5\u81f3\u6b64\uff0c\u8fd9\u6c34\u3000\u4e0a\u7684\u67ad\u96c4\u4e0d\u7981\u5927\u6012 \u8d77\u6765\uff0c\u201c\u556a\u201d\u5730\u4e00\u62cd\u684c\u5b50\uff0c\u5c06\u684c\u9762\u4e0a\u7684\u676f\u76d8\u7897\u76cf\u9707\u8d77\u8001\u9ad8\uff0c\u968f\u7740\u3000\u8fd9\u4e00\u62cd\u4e4b\u52bf\u500f\u7136\u7ad9\u4e86\u8d77 \u6765\uff0c\u53cc\u773c\u56db\u4e0b\u4e00\u8f6c\uff0c\u770b\u5230\u56db\u5ea7\u7fa4\u96c4\u5927\u591a\u9762\u5e26\u6115\u5bb9\uff0c\u50cf\u662f\u8fd8\u5f04\u3000\u4e0d\u6e05\u8fd9\u5176\u4e2d\u7684\u7a76\u7adf\uff0c\u4e0d\u7981\u68ee\u68ee \u4e00\u7b11\uff0c\u5389\u559d\u9053\uff1a\u201c\u59d3\u6bdb\u7684\uff0c\u6211\u8001\u5934\u5b50\u5148\u8fd8\u5f53\u4f60\u662f\u4e2a\u89d2\u3000\u8272\uff0c\u4f60\u8fd9\u624b\u73a9\u5f97\u53ef\u592a\u4e0d\u6f02\u4eae\u4e86\uff0c\u7eb5\u7136 \u4f60\u9a97\u8fc7\u4e86\u8001\u592b\uff0c\u96be\u9053\u4f60\u5c31\u4e0d\u6015\u88ab\u5929\u4e0b\u6b66\u6797\u9a82\u4e3a\u3000\u624b\u6bb5\u5351\u9119\u7684\u65e0\u803b\u4e4b\u5f92\uff1f\u54fc\uff0c\u8001\u592b\u6d3b\u4e86\u4e03\u5341 \u5c81\uff0c\u4eca\u5929\u624d\u5f00\u4e86\u773c\u754c\uff0c\u624d\u77e5\u9053\u6b66\u6797\u4e4b\u4e2d\uff0c\u3000\u7adf\u6709\u8fd9\u79cd\u6ee1\u9762\u4ec1\u4e49\uff0c\u6ee1\u8179\u5a3c\u76d7\u7684\u5c0f\u4eba\uff01\u201d \u3000\u3000\u3000\u65b9\u624d\u6bdb\u6587\u742a\u4e00\u6f0f\u53e3\uff0c\u7075\u86c7\u6bdb\u81ec\u9762\u8272\u5c31\u4e0d\u7981\u8f7b\u5fae\u5730\u53d8\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u3000\u539f\u6765\u7075\u86c7\u6bdb\u81ec\u7ed3\u76df\u7684\u5144\u5f1f\u56db\u4eba\uff0c\u5176\u4e2d\u4ee5\u8001\u4e8c\u94c1\u7b97\u5b50\u8ba1\u8c0b\u4e3a\u4eba\u6700\u662f\u597d\u72e1\u767e\u51fa\uff0c\u673a\u3000\u667a\u6df1 \u6c89\uff0c\u5c24\u5728\u8fd9\u4e00\u4ee3\u67ad\u96c4\u7075\u86c7\u6bdb\u81ec\u4e4b\u4e0a\u3002 \u3000\u3000\u3000\u4e09\u624d\u5b9d\u85cf\u7684\u79d8\u56fe\uff0c\u81ea\u4ece\u94c1\u624b\u4ed9\u733f\u4faf\u6797\u4ee5\u66b4\u529b\u5f97\u81ea\u4e10\u5e2e\u95e8\u5f92\u7684\u624b\u4e0a\u4e4b\u540e\uff0c\u5c31\u7acb\u523b\u3000\u98de\u9a6c \u9001\u5230\u6bdb\u81ec\u7684\u624b\u91cc\uff0c\u8fd9\u4efd\u4e3a\u5929\u4e0b\u6b66\u6797\u4e2d\u4eba\u5782\u6d8e\u7684\u5de8\u5b9d\uff0c\u81ea\u7136\u4e5f\u4f7f\u5f97\u7075\u86c7\u6bdb\u81ec\u5927\u5927\u3000\u5730\u8d77\u4e86\u8d2a \u5fc3\u3002 \u3000\u3000\u3000\u4f46\u662f\uff0c\u4ed6\u4e5f\u77e5\u9053\uff0c\u53ea\u8981\u8fd9\u6d88\u606f\u7a0d\u6709\u6cc4\u9732\uff0c\u7acb\u523b\u4fbf\u662f\u6b66\u6797\u4e2d\u4e00\u573a\u7edd\u5927\u7684\u98ce\u6ce2\uff0c\u81ea\u3000\u5df1\u8fd1 \u5e74\u6765\u5728\u6b66\u6797\u4e2d\u867d\u5df2\u6709\u5730\u4f4d\uff0c\u4fbf\u81ea\u5df1\u82e5\u60f3\u5b89\u5b89\u7a33\u7a33\u5730\u5f97\u5230\u8fd9\u4efd\u5b9d\u7269\uff0c\u4ed6\u81ea\u77e5\u4e5f\u7edd\u3000\u4e0d\u53ef\u80fd\u3002 \u3000\u3000\u3000\u4e8e\u662f\uff0c\u8fd9\u94c1\u7b97\u5b50\u8ba1\u8c0b\u5c31\u5b9a\u4e0b\u4e86\u72e1\u8ba1\uff0c\u4ed6\u7d22\u6027\u5c06\u6b64\u4e8b\u7565\u4f5c\u6e32\u67d3\uff0c\u4f7f\u5f97\u6b66\u6797\u4e2d\u4eba\u90fd\u3000\u5c06\u76ee \u5149\u6ce8\u610f\u5230\u94c1\u624b\u4ed9\u733f\u7684\u5bbf\u8fc1\u4e4b\u4f1a\u4e0a\u3002 \u3000\u3000\u3000\u800c\u4ed6\u81ea\u5df1\u5374\u5e26\u7740\u7389\u9aa8\u4f7f\u8005\u4e2d\u7684\u4e8c\u3001\u4e09\u5f1f\u5b50\uff0c\u4ee5\u53ca\u94c1\u9a91\u795e\u97ad\u961f\u4e2d\u7cbe\u5a34\u6c34\u6027\u7684\u9a91\u58eb\u3000\uff0c\u8f7b \u9a91\u7b80\u88c5\u98de\u9a70\u6d2a\u6cfd\u6e56\uff0c\u60f3\u5728\u4eba\u4e0d\u77e5\u9b3c\u4e0d\u89c9\u4e2d\uff0c\u53d6\u5f97\u5b9d\u85cf\u3002 \u3000\u3000\u3000\u8fd9\u660e\u4fee\u6808\u9053\uff0c\u6697\u6e21\u9648\u4ed3\u4e4b\u8ba1\uff0c\u679c\u7136\u9a97\u8fc7\u4e86\u6b66\u6797\u4e2d\u4eba\u7684\u8033\u76ee\uff0c\u4e0d\u4f46\u5c11\u6797\u795e\u50e7\uff0c\u58a8\u3000\u4e00\u4e0a \u4eba\uff0c\u7a77\u795e\u51cc\u9f99\u7b49\u4eba\u4e0a\u4e86\u5f53\uff0c\u5c31\u8fde\u4e00\u5411\u4e16\u6545\u6781\u6df1\uff0c\u8001\u5f97\u4e0d\u80fd\u518d\u8005\u7684\u6c5f\u6e56\u706b\u773c\u91d1\u96d5\u3000\u8427\u8fdf\uff0c\u4e5f\u88ab \u8fd9\u6761\u5999\u8ba1\u9a97\u4e86\u3002 \u3000\u3000\u3000\u800c\u6b64\u523b\uff0c\u8fd9\u4e09\u624d\u5b9d\u85cf\uff0c\u5374\u6b63\u5982\u4e00\u6761\u9c9c\u9c7c\uff0c\u5df2\u8db3\u4ee5\u8bf1\u60d1\u8fd9\u6761\u998b\u732b\u505a\u51fa\u4efb\u4f55\u4e8b\u6765\u4e86\u3000\uff0c\u8fd9 \u4e9b\u6b66\u6797\u4e2d\u7684\u540d\u4eba\u4ee5\u541b\u5b50\u4e4b\u5fc3\u6765\u63e3\u5ea6\u6bdb\u81ec\u4e4b\u8179\uff0c\u81ea\u7136\u4f1a\u843d\u5165\u4e86\u5708\u5957\uff0c\u7075\u86c7\u6bdb\u81ec\u4e5f\u3000\u81ea\u7136\u6697\u4e2d\u5927 \u611f\u5f97\u610f\u4e86\u3002 \u3000\u3000\u3000\u4f46\u8fd9\u6761\u795e\u4e0d\u77e5\u9b3c\u4e0d\u89c9\u7684\u79d8\u8ba1\uff0c\u5374\u4e0d\u77e5\u600e\u5730\u7adf\u88ab\u4e00\u4e2a\u4e66\u751f\u8bc6\u7834\u4e86\uff0c\u7f2a\u6587\u8f7b\u63cf\u6de1\u5199\u3000\u5730\u5411 \u516b\u9762\u73b2\u73d1\u80e1\u4e4b\u8f89\u95ee\u4e86\u90a3\u53e5\u8bdd\uff0c\u80e1\u4e4b\u8f89\u5fc3\u91cc\u81ea\u7136\u6709\u6570\uff0c\u4ed6\u7565\u4e3a\u5403\u60ca\u4e4b\u4e0b\uff0c\u4f46\u4e3a\u4e86\u3000\u5411\u7f2a\u6587\u2014\u2014 \u8fd9\u6325\u91d1\u5982\u571f\u7684\u9614\u5c11\u8ba8\u597d\uff0c\u4e0d\u77e5\u8f7b\u91cd\uff0c\u7adf\u5931\u58f0\u5c06\u5b83\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u3000\u7075\u86c7\u6bdb\u81ec\u77e5\u9053\u8fd9\u8427\u8001\u96d5\u7d20\u79f0\u8001\u5230\uff0c\u542c\u4e86\u8fd9\u53e5\u8bdd\uff0c\u600e\u6709\u731c\u4e0d\u900f\u7684\u9053\u7406\uff0c\u4e8e\u662f\u4ed6\u5c31\u3000\u5728\u6697 \u4e2d\u4e3a\u8fd9\u5c06\u8981\u7206\u53d1\u7684\u98ce\u6ce2\u51c6\u5907\u4e86\u3002 \u3000\u3000\u3000\u4e00\u9762\uff0c\u4ed6\u53c8\u5728\u6697\u4e2d\u5e86\u5e78\uff0c\u6e05\u98ce\u5251\u6731\u767d\u7fbd\u3001\u5d69\u5c71\u58a8\u4e00\u3001\u7a77\u795e\u51cc\u9f99\u8fd9\u4e9b\u5f3a\u654c\u591a\u5df2\u79bb\u3000\u53bb\uff0c \u53ea\u5269\u4e00\u4e2a\u706b\u773c\u91d1\u96d5\uff0c\u5012\u5e76\u4e0d\u600e\u5730\u653e\u5728\u8fd9\u6b66\u6797\u67ad\u96c4\u7684\u773c\u4e0b\u3002 \u3000\u3000\u3000\u4f46\u662f\u4ed6\u806a\u660e\u4e00\u4e16\uff0c\u5374\u7cca\u6d82\u4e00\u65f6\uff0c\u8bd5\u60f3\u8fd9\u4e9b\u6b66\u6797\u9ad8\u624b\uff0c\u663e\u7136\u5df2\u4e3a\u7740\u6b64\u4e8b\u6765\u5230\u8fd9\u91cc\u3000\uff0c\u82e5 \u4e0d\u662f\u5df2\u522b\u6709\u6240\u89c1\uff0c\u53c8\u600e\u4f1a\u7a81\u7136\u79bb\u53bb\u5462\uff1f\u201d \u3000\u3000\u3000\u6b64\u523b\u8fd9\u8427\u8001\u96d5\u4e00\u62cd\u684c\u5b50\uff0c\u7834\u53e3\u5927\u9a82\uff0c\u5728\u5ea7\u7684\u4eba\u5c31\u7b97\u6709\u4e0d\u77e5\u9053\u7684\uff0c\u7a0d\u52a0\u7422\u78e8\uff0c\u81ea\u3000\u7136\u4e5f \u5927\u591a\u731c\u7834\u4e86\u7a76\u7adf\uff0c\u53ea\u662f\u8fd9\u4e9b\u4eba\u4e5f\u90fd\u662f\u8001\u6c5f\u6e56\u4e86\uff0c\u4e5f\u5782\u6d8e\u5b9d\u7269\uff0c\u4f46\u81ea\u77e5\u60f9\u4e0d\u8d77\u6bdb\u3000\u5927\u592a\u7237\uff0c\u4e00 \u4e2a\u4e2a\u4e5f\u5c31\u88c5\u804b\u4f5c\u54d1\u5730\u8896\u624b\u65c1\u89c2\uff0c\u770b\u8fd9\u53f0\u597d\u620f\u600e\u4e48\u5531\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u3000\u7075\u86c7\u6bdb\u81ec\u5fae\u5fae\u7b11\u7740\uff0c\u6545\u610f\u4f5c\u51fa\u4e0d\u5c51\u7684\u795e\u8272\u6765\uff0c\u4ee5\u793a\u81ea\u5df1\u7684\u8eab\u4efd\u4e0e\u4f17\u4e0d\u540c\uff0c\u5176\u5b9e\u3000\u4ed6\u4eba \u591a\u52bf\u4f17\uff0c\u6709\u6043\u65e0\u6050\uff0c\u81ea\u7136\u5fc3\u5b89\u7406\u5f97\u5f97\u5f88\u3002 \u3000\u3000\u3000\u4ed6\u8fd9\u91cc\u6b63\u81ea\u7765\u7768\u4f5c\u6001\uff0c\u54ea\u77e5\u540c\u58f0\u4e00\u54cd\uff0c\u4ed6\u8eab\u540e\u500f\u7136\u62a2\u51fa\u4e00\u4e2a\u4eba\u6765\uff0c\u53ea\u89c1\u6b64\u4eba\u7a7f\u3000\u7740\u6de1 \u9752\u8272\u7684\u957f\u886b\uff0c\u80cc\u63d2\u957f\u5251\uff0c\u5374\u6b63\u662f\u5e7f\u897f\u5927\u8c6a\uff0c\u5b50\u6bcd\u53cc\u98de\uff0c\u5de6\u624b\u795e\u5251\u4e01\u8863\u3002 \u3000\u3000\u3000\u4ed6\u4e00\u6b65\u63a0\u5728\u6bdb\u81ec\u524d\u9762\uff0c\u53f1\u9053\uff1a\u201c\u59d3\u8427\u7684\uff0c\u6211\u5f1f\u5144\u770b\u4f60\u662f\u4e2a\u7cdf\u8001\u5934\u5b50\uff0c\u51e1\u4e8b\u624d\u8ba9\u3000\u4f60\u4e09 \u5206\uff0c\u53ef\u662f\u4f60\u8981\u662f\u501a\u8001\u5356\u8001\uff0c\u4e0d\u8bc6\u62ac\u4e3e\uff0c\u5c31\u522b\u602a\u6211\u5f1f\u5144\u5bf9\u4f60\u4e0d\u5ba2\u6c14\u4e86\u3002\u201d \u3000\u3000\u3000\u8427\u8001\u96d5\u6c14\u5f97\u9762\u76ee\u53d8\u8272\uff0c\u6012\u6781\u5374\u53cd\u800c\u72c2\u7b11\u8d77\u6765\uff0c\u767e\u6b65\u98de\u82b1\u6797\u7426\u7b5d\u5728\u65c1\u51b7\u51b7\u8bf4\u9053\uff1a\u3000\u201c\u8427 \u8001\u5934\u5b50\uff0c\u4f60\u8981\u662f\u77e5\u8da3\u7684\uff0c\u8fd8\u662f\u5feb\u5939\u7740\u5c3e\u5df4\u6eda\u5427\uff0c\u8981\u7b49\u5230\u6211\u4e01\u5927\u54e5\u4e00\u51fa\u624b\uff0c\u4f60\u60f3\u3000\u6eda\u53ef\u5c31\u6765\u4e0d \u53ca\u4e86\u3002\u201d\u5979\u65b9\u624d\u5403\u8fd9\u8427\u8001\u96d5\u78b0\u4e86\u4e00\u9f3b\u5b50\u7070\uff0c\u6b64\u523b\u89c1\u654c\u6211\u5f3a\u5f31\u60ac\u6b8a\uff0c\u5c31\u8bf4\u3000\u8d77\u523b\u8584\u8bdd\u6765\u3002 \u3000\u3000\u3000\u8427\u8fdf\u57ce\u5e9c\u518d\u6df1\uff0c\u6b64\u523b\u4e5f\u88ab\u6c14\u5f97\u6d51\u8eab\u53d1\u6296\uff0c\u6ee1\u697c\u7fa4\u8c6a\u4e0d\u7981\u90fd\u5c4f\u4f4f\u547c\u5438\uff0c\u56e0\u4e3a\u5927\u5bb6\u3000\u90fd\u77e5 \u9053\u73b0\u4e0b\u5df2\u662f\u4e00\u89e6\u5373\u53d1\u7684\u5c40\u9762\u4e86\u3002 \u3000\u3000\u3000\u7f2a\u6587\u671b\u7740\u6bdb\u6587\u742a\u5fae\u5fae\u4e00\u7b11\uff0c\u4ed6\u7684\u7b11\u5bb9\u8fd8\u6ca1\u6709\u6536\u655b\uff0c\u5df2\u542c\u5f97\u4e00\u58f0\u66b4\u53f1\uff1a\u201c\u4eca\u65e5\u8001\u3000\u592b\u5c31 \u8981\u6559\u8bad\u6559\u8bad\u4f60\u4eec\u8fd9\u4e9b\u76ee\u65e0\u5c0a\u957f\u7684\u72c2\u5f92\u3002\u201d \u3000\u3000\u3000\u539f\u6765\u90a3\u8427\u8001\u96d5\u76db\u6012\u4e4b\u4e0b\uff0c\u5df2\u987e\u4e0d\u5f97\u6b64\u65f6\u6b63\u662f\u5728\u4eba\u5bb6\u7684\u52bf\u529b\u8303\u56f4\u4e4b\u5185\uff0c\u968f\u7740\u5389\u53f1\u3000\u4e4b \u58f0\uff0c\u4eba\u5df2\u7bad\u6b65\u62a2\u51fa\uff0c\u8fd9\u4f4d\u79f0\u96c4\u6c34\u4e0a\u7684\u706b\u773c\u91d1\u96d5\uff0c\u6b64\u523b\u5728\u8fd9\u72ed\u7a84\u7684\u9152\u697c\u4e4b\u4e0a\uff0c\u7adf\u5c31\u3000\u5df2\u84e6\u7136\u51fa \u624b\u4e86\u3002 \u3000\u3000\u3000\u4ed6\u5e74\u7eaa\u867d\u5927\uff0c\u529f\u592b\u53ef\u4e00\u4e1d\u4e5f\u6ca1\u6709\u6401\u4e0b\uff0c\u53ea\u89c1\u4ed6\u8eab\u5f62\u5fae\u52a8\u4e4b\u95f4\uff0c\u5e9e\u5927\u7684\u8eab\u8eaf\u7ed5\u8fc7\u3000\u4e24\u5f20 \u684c\u5b50\uff0c\u5411\u90a3\u5de6\u624b\u795e\u5251\u659c\u659c\u5288\u8fc7\u4e00\u638c\u3002 \u3000\u3000\u3000\u5de6\u624b\u795e\u5251\u51b7\u7b11\u4e00\u58f0\uff0c\u5fae\u4e00\u62e7\u8eab\uff0c\u54ea\u77e5\u8fd9\u706b\u773c\u91d1\u96d5\u867d\u4ee5\u6c34\u4e0a\u7edd\u6280\u6210\u540d\uff0c\u638c\u6cd5\u4e0a\u7684\u3000\u706b\u5019 \u4e5f\u4e0d\u540c\u51e1\u54cd\uff0c\u8fd9\u62db\u867d\u7136\u98ce\u58f0\u864e\u864e\uff0c\u5374\u4ec5\u662f\u865a\u62db\u800c\u5df2\uff0c\u773c\u770b\u8fd9\u638c\u5df2\u5288\u5230\u4e2d\u9014\uff0c\u5374\u3000\u5c06\u638c\u950b\u5fae \u504f\uff0c\u5212\u4e86\u4e2a\u659c\u5b64\uff0c\u500f\u7136\u8f6c\u53d8\u4e86\u65b9\u5411\uff0c\u6a2a\u7740\u671d\u4e01\u8863\u8179\u4e0b\u5207\u53bb\u3002 \u3000\u3000\u3000\u5de6\u624b\u795e\u5251\u5fae\u4e00\u758f\u795e\uff0c\u8d76\u7d27\u5438\u6c14\u51f9\u8179\uff0c\u54ea\u77e5\u773c\u524d\u638c\u5f71\u4e00\u82b1\uff0c\u90a3\u8427\u8fdf\u7684\u5de6\u638c\u7adf\u7136\u540e\u3000\u53d1\u5148 \u81f3\uff0c\u4e94\u6307\u5206\u5f20\uff0c\u6293\u5411\u4e01\u8863\u7684\u9762\u95e8\u3002 \u3000\u3000\u3000\u4e01\u8863\u6b64\u523b\u624d\u5927\u5403\u4e00\u60ca\uff0c\u6325\u53f3\u638c\uff0c\u8e0f\u504f\u5bab\uff0c\u786c\u751f\u751f\u4e00\u62e7\u8eab\u5f62\uff0c\u582a\u582a\u8eb2\u5f00\u8fd9\u4e24\u62db\uff0c\u3000\u4f46\u662f \u4e00\u62db\u7740\u9519\uff0c\u5c31\u5df2\u88ab\u4eba\u5bb6\u62a2\u4e86\u5148\u673a\uff0c\u53ea\u89c9\u90a3\u8427\u8001\u96d5\u7684\u638c\u5f71\uff0c\u524d\u540e\u5de6\u53f3\u671d\u81ea\u5df1\u5288\u4e86\u3000\u8fc7\u6765\uff0c\u81ea\u5df1 \u7adf\u662f\u8fd8\u624b\u65e0\u529b\u3002 \u3000\u3000\u3000\u8fd9\u65f6\u9152\u697c\u4e0a\u5df2\u7136\u5927\u4e71\uff0c\u7fa4\u8c6a\u591a\u5df1\u907f\u5e2d\u800c\u8d77\uff0c\u7f2a\u6587\u66f4\u662f\u8fdc\u8fdc\u7ad9\u5230\u7a97\u53e3\uff0c\u751f\u50cf\u662f\u5c31\u3000\u6015\u6cbe \u7740\u4eba\u5bb6\u7684\u4e00\u4e1d\u638c\u98ce\uff0c\u56e0\u800c\u53d7\u4f24\u4f3c\u7684\u3002 \u3000\u3000\u3000\u6797\u7426\u7b5d\u5374\u9762\u5e26\u72e1\u7b11\uff0c\u5a9a\u76ee\u4e00\u779f\u7075\u86c7\u6bdb\u81ec\uff0c\u53ea\u89c1\u8fd9\u4f4d\u6b66\u6797\u67ad\u96c4\u5634\u89d2\u6b63\u6302\u7740\u4e00\u4e1d\u72de\u3000\u7b11\uff0c \u6b63\u662f\u5fc3\u4e2d\u5df2\u52a8\u6740\u673a\uff0c\u7acb\u5fc3\u8981\u5c06\u8427\u8001\u96d5\u6bc1\u5728\u8fd9\u91cc\u4e86\u3002 \u3000\u3000\u3000\u91d1\u9ca4\u8427\u5e73\u5219\u7741\u7740\u773c\u775b\u7ad9\u5728\u65c1\u8fb9\uff0c\u4e3a\u4ed6\u7684\u8001\u7236\u63a0\u9635\uff0c\u53ea\u662f\u6b64\u523b\u5de6\u624b\u795e\u5251\u8eab\u5f62\u95ea\u52a8\u3000\uff0c\u4f3c \u4e4e\u53ea\u6709\u8eb2\u907f\u3001\u62db\u67b6\u7684\u4efd\u513f\uff0c\u663e\u7136\u5df2\u843d\u4e0b\u98ce\u3002 \u3000\u3000\u3000\u7fa4\u8c6a\u4e0d\u7981\u7a83\u7a83\u79c1\u8bae\u3002 \u3000\u3000\u3000\u201c\u8fd9\u5de6\u624b\u795e\u5251\u672c\u6765\u662f\u6c5f\u6e56\u4e0a\u6709\u540d\u7684\u786c\u628a\u5b50\uff0c\u600e\u4e48\u4eca\u65e5\u5982\u6b64\u4e0d\u6d4e\uff0c\u96be\u9053\u4ed6\u4eab\u8a89\u6c5f\u3000\u6e56\u591a \u5e74\uff0c\u4ec5\u4ec5\u662f\u5f92\u62e5\u865a\u540d\u800c\u5df2\u5417\u201d\u6709\u7684\u5c31\u8bf4\uff1a\u201c\u4f60\u8001\u54e5\u5c11\u8bf4\u4e24\u53e5\u5427\uff0c\u4eba\u5bb6\u5b50\u6bcd\u53cc\u98de\u3000\u7684\u7edd\u6d3b\u513f\u8fd8 \u6ca1\u6709\u62ff\u51fa\u6765\u5566\u3002\u201d \u3000\u3000\u3000\u8fd9\u4e9b\u8bdd\u542c\u5230\u516b\u9762\u73b2\u73d1\u80e1\u4e4b\u8f89\u7684\u8033\u91cc\uff0c\u4ed6\u5c31\u4e00\u62c9\u7f2a\u6587\uff0c\u6084\u6084\u8bf4\u9053\uff1a\u201c\u7f2a\u8001\u5f1f\uff0c\u4f60\u3000\u5e38\u8bf4 \u6ca1\u6709\u89c1\u8bc6\u8fc7\u6b66\u6797\u9ad8\u624b\u7684\u529f\u592b\uff0c\u7b49\u4e0b\u4f60\u5c31\u53ef\u4ee5\u89c1\u5230\u4e86\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\uff0c\u4e03\u5251\u4e09\u97ad\u91cc\u3000\u82e5\u8bba\u624b\u5e95\u4e0b \u7684\u72e0\u8fa3\uff0c\u9664\u4e86\u6211\u4eec\u6bdb\u5927\u54e5\u4e4b\u5916\uff0c\u6050\u6015\u5c31\u8981\u6570\u8fd9\u4f4d\u5de6\u624b\u795e\u5251\u4e01\u5927\u7237\u4e86\u3002\u201d \u3000\u3000\u3000\u7f2a\u6587\u53c8\u662f\u5fae\u5fae\u4e00\u7b11\uff0c\u4ed6\u867d\u7136\u4f5c\u51fa\u4e00\u4ed8\u6015\u4e8b\u7684\u6837\u5b50\u6765\uff0c\u76ee\u5149\u5374\u2014\u2014\u76f4\u968f\u7740\u4e01\u8863\u548c\u3000\u8427\u8001 \u96d5\u6253\u8f6c\uff0c\u53ea\u89c1\u8fd9\u4e24\u4eba\u8eab\u5f62\u5154\u8d77\u9e58\u843d\uff0c\u811a\u5e95\u4e0b\u5374\u6ca1\u6709\u5e26\u51fa\u4ec0\u4e48\u58f0\u606f\u6765\uff0c\u53ea\u662f\u8eab\u5f62\u3000\u52a8\u5904\uff0c\u81ea\u7136 \u5c06\u684c\u6905\u6380\u7ffb\uff0c\u676f\u76d8\u7897\u76cf\uff0c\u9152\u6c41\u83dc\u6c64\uff0c\u5f04\u5f97\u72fc\u7c4d\u4e00\u5730\u3002 \u3000\u3000\u3000\u53c8\u662f\u5341\u51e0\u56de\u7167\u9762\u8fc7\u53bb\uff0c\u53ea\u89c1\u8427\u8001\u96d5\u7a81\u53d1\u4e00\u58f0\u72c2\u7b11\uff0c\u53cc\u638c\u4e00\u5206\u3001\u4e00\u8f6c\u3001\u4e00\u5408\uff0c\u7adf\u3000\u4ee5\u9634 \u9633\u628a\u5201\u4f4f\u5de6\u624b\u795e\u5251\u7684\u53f3\u8155\uff0c\u773c\u770b\u4e01\u8863\u53f3\u8155\u5c31\u5f97\u5e9f\u4e86\uff0c\u7fa4\u8c6a\u4e0d\u7981\u90fd\u9f50\u58f0\u60ca\u559f\uff0c\u6bdb\u3000\u81ec\u7b49\u66f4\u662f\u9762 \u8272\u5fae\u53d8\u3002 \u3000\u3000\u3000\u54ea\u77e5\u5b50\u6bcd\u53cc\u98de\u4e01\u8863\u79f0\u96c4\u591a\u5e74\uff0c\u5e76\u975e\u5e78\u81f4\uff0c\u5012\u771f\u662f\u51ed\u7740\u62f3\u811a\u5728\u5200\u5c71\u5251\u6797\u4e2d\u95ef\u51fa\u6765\u3000\u7684\u3002 \u6b64\u523b\u8fd9\u6b66\u6797\u9ad8\u624b\u867d\u7136\u624b\u8155\u88ab\u62ff\uff0c\u4f46\u5374\u4e34\u5371\u4e0d\u4e71\uff0c\u5c31\u5728\u6bdb\u81ec\u7b49\u8981\u60f3\u51fa\u624b\u63f4\u6551\u7684\u65f6\u3000\u5019\uff0c\u53e3\u4e2d\u95f7 \u201c\u54fc\u201d\u4e00\u58f0\uff0c\u55d6\u5730\u8e22\u51fa\u4e00\u817f\u3002 \u3000\u3000\u3000\u4e01\u8863\u6210\u540d\u4e8e\u4e24\u5e7f\uff0c\u8fd9\u4e00\u811a\u6b63\u662f\u5357\u6d3e\u6b66\u672f\u4e2d\u7684\u5999\u7740\uff0c\u975e\u4f46\u65e0\u5f71\u65e0\u5f62\uff0c\u5feb\u5982\u95ea\u7535\uff0c\u3000\u800c\u4e14 \u7a9d\u5fc3\u8e22\u51fa\uff0c\u6b63\u662f\u653b\u654c\u4e4b\u6240\u5fc5\u6551\u3002 \u3000\u3000\u3000\u706b\u773c\u91d1\u96d5\u4e00\u62db\u5f97\u624b\uff0c\u8fd9\u9634\u9633\u628a\u53ea\u8981\u4e00\u62e7\uff0c\u5de6\u624b\u795e\u5251\u7684\u8155\u9aa8\u4fbf\u2019\u5f97\u5206\u5bb6\uff0c\u54ea\u77e5\u4eba\u3000\u5bb6\u5c31 \u5728\u8fd9\u95f4\u4e0d\u5bb9\u53d1\u7684\u4e00\u5239\u90a3\u91cc\uff0c\u7adf\u4e00\u811a\u8e22\u5411\u81ea\u5df1\u7684\u5fc3\u7a9d\uff0c\u90a3\u4e48\u81ea\u5df1\u82e5\u662f\u8fd8\u4e0d\u6492\u624b\uff0c\u3000\u4e01\u8863\u56fa\u53d7\u4e0d \u4e86\uff0c\u81ea\u5df1\u4e5f\u5f97\u53d7\u4f24\u3002 \u3000\u3000\u3000\u4e8e\u662f\u4ed6\u53ea\u5f97\u53cc\u624b\u4e00\u5206\uff0c\u8eab\u5f62\u4e00\u9000\uff0c\u5927\u4ef0\u8eab\uff0c\u5012\u7a9c\u56de\u53bb\u3002 \u3000\u3000\u3000\u8fd9\u65f6\u94c1\u624b\u4ed9\u733f\u3001\u516b\u9762\u73b2\u73d1\u3002\u767e\u6b65\u98de\u82b1\u8fd9\u4e9b\u4eba\u624d\u900f\u51fa\u4e00\u53e3\u6c14\u6765\uff0c\u94c1\u624b\u4ed9\u733f\u62a2\u524d\u4e00\u3000\u6b65\uff0c \u633d\u4e86\u633d\u8896\u5b50\uff0c\u5c06\u957f\u886b\u7684\u4e0b\u895f\u5f80\u8170\u5e26\u4e0a\u4e00\u6396\uff0c\u9053\uff1a\u201c\u4e01\u5927\u54e5\uff0c\u4f60\u4e0b\u6765\u6b47\u6b47\uff0c\u8ba9\u5c0f\u3000\u5f1f\u6765\u66ff\u4e01\u5927 \u54e5\u63a5\u4e24\u62db\uff01\u201d \u3000\u3000\u3000\u706b\u773c\u91d1\u96d5\u54c8\u54c8\u4e00\u9635\u72c2\u7b11\u3002\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u59d3\u4faf\u7684\uff0c\u4f60\u53ea\u7ba1\u4e0a\u6765\uff0c\u4f60\u4eec\u5c31\u662f\u7fa4\u6bb4\uff0c\u3000\u8001\u592b \u4e5f\u4e0d\u4f1a\u542b\u7cca\u4f60\u4eec\u3002\u201d \u3000\u3000\u3000\u5de6\u624b\u795e\u5251\u4e01\u8863\u5374\u9762\u5bd2\u5982\u6c34\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u53f3\u624b\u7a81\u8d77\u4e00\u626f\uff0c\u90a3\u4ef6\u6de1\u9752\u957f\u886b\u4e0a\u7684\u5341\u51e0\u3000\u7c92\u94ae \u5b50\u767b\u65f6\u5c31\u88ab\u626f\u843d\u4e86\u4e0b\u6765\uff0c\u8863\u895f\u5927\u5f00\u3002 \u3000\u3000\u3000\u4ed6\u53cc\u624b\u4e00\u7529\uff0c\u4e00\u4e22\uff0c\u5c31\u5c06\u90a3\u4ef6\u957f\u886b\u7529\u4e86\u4e0b\u6765\uff0c\u9732\u51fa\u91cc\u9762\u9752\u84dd\u8272\u7684\u7d27\u8eab\u8863\u9760\uff0c\u6700\u3000\u60f9\u773c \u7684\u5374\u662f\u4ed6\u8170\u95f4\u7684\u4e00\u9053\u6de1\u9752\u8272\u76ae\u8170\u5e26\uff0c\u548c\u8fd9\u6761\u8170\u5e26\u4e0a\u5fae\u5fae\u9732\u51fa\u7684\u4e03\u67c4\u5e26\u7740\u674f\u9ec4\u8272\u3000\u4e1d\u7a57\u7684\u5c0f\u5251 \u628a\u3002 \u3000\u3000\u3000\u8fd9\u4e00\u4e0b\uff0c\u6ee1\u697c\u7fa4\u8c6a\u4ff1\u90fd\u52a8\u5bb9\uff0c\u7075\u86c7\u6bdb\u81ec\u72de\u7b11\u4e00\u4e0b\uff0c\u4fa7\u987e\u6bdb\u6587\u742a\u9053\uff1a\u201c\u742a\u513f\uff0c\u4f60\u3000\u4e01\u5927 \u53d4\u4eca\u5929\u52a8\u4e86\u771f\u6012\u4e86\uff0c\u4f60\u4e5f\u53ef\u4ee5\u4e58\u6b64\u89c1\u89c1\u4f60\u4e01\u5927\u53d4\u626c\u540d\u5929\u4e0b\u7684\u2018\u5b50\u6bcd\u53cc\u98de\u2019\u7edd\u6280\u3000\uff0c\u53ef\u662f\u8981\u5c0f \u5fc3\u70b9\uff0c\u522b\u8ba9\u7ed9\u8bef\u4f24\u4e86\u3002\u201d \u3000\u3000\u3000\u6bdb\u6587\u742a\u561f\u8d77\u5634\uff0c\u7b54\u5e94\u7740\uff0c\u5fc3\u91cc\u5374\u4e00\u4e07\u4e2a\u4e0d\u670d\u6c14\uff0c\u8fd9\u4e9b\u65e5\u5b50\u6765\uff0c\u5979\u8fde\u632b\u9ad8\u624b\uff0c\u5c31\u3000\u8fde\u5979 \u7239\u7239\uff0c\u4e5f\u4e0d\u89c1\u5f97\u653e\u5728\u5979\u773c\u4e0b\uff0c\u4f55\u51b5\u4e01\u8863\u5462\uff1f \u3000\u3000\u3000\u53ef\u662f\u4e01\u8863\u8fd9\u4e00\u62c9\u6263\u5b50\uff0c\u4e00\u7529\u957f\u886b\uff0c\u5148\u522b\u8bf4\u4ed6\u7684\u529f\u592b\u600e\u6837\uff0c\u5c31\u51ed\u4ed6\u8fd9\u4efd\u5e72\u51c0\u3001\u4fd0\u3000\u843d\u3001 \u6f02\u4eae\uff0c\u5c31\u4e0d\u662f\u666e\u901a\u6b66\u6797\u9053\u80fd\u671b\u5176\u9879\u80cc\u7684\u4e86\u3002 \u3000\u3000\u3000\u6b64\u523b\u4ed6\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u59d3\u8427\u7684\uff0c\u4f60\u5feb\u62bd\u51fa\u5bb6\u4f19\uff0c\u6765\u9001\u6b7b\u5427\uff01\u201d \u3000\u3000\u3000\u8427\u6c0f\u7236\u5b50\u6b64\u523b\u9762\u8272\u4ff1\u90fd\u96be\u770b\u5df2\u6781\uff0c\u91d1\u9ca4\u8427\u5e73\u4e00\u95ea\u8eab\uff0c\u63a0\u5230\u4ed6\u7236\u4eb2\u8eab\u4fa7\uff0c\u8f7b\u58f0\u9053\u3000\uff1a \u201c\u7239\u7239\uff0c\u8ba9\u5b69\u513f\u4ee3\u4f60\u8001\u4eba\u5bb6\u63a5\u4e0b\u8fd9\u573a\u5427\uff0c\u201d\u4ed6\u770b\u5230\u5de6\u624b\u795e\u5251\u8fd9\u4e48\u4e00\u6765\uff0c\u5fc3\u91cc\u5148\u6709\u3000\u4e86\u4e24\u5206\u60c5 \u610f\uff0c\u751f\u6015\u8001\u7236\u5e94\u4ed8\u4e0d\u6765\uff0c\u5c06\u6570\u5341\u5e74\u6765\u7684\u58f0\u540d\uff0c\u683d\u5728\u8fd9\u5bbf\u8fc1\u7684\u9152\u697c\u4e0a\uff0c\u56e0\u6b64\u3000\u5c31\u8bf4\u51fa\u8fd9\u8bdd\u6765\u3002 \u3000\u3000\u3000\u767e\u6b65\u98de\u82b1\u5374\u53c8\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u5c0f\u4f19\u5b50\uff0c\u4f60\u6025\u4e2a\u4ec0\u4e48\uff0c\u8981\u9001\u6b7b\u4e5f\u4e0d\u6025\u5728\u8fd9\u4e00\u9635\u5b50\u3000\u5440\uff0c \u4e0d\u8fc7\u2014\u2014\u201d\u5979\u53c8\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8427\u8001\u5934\u5b50\uff0c\u4f60\u8981\u662f\u89c9\u5f97\u6c14\u529b\u4e0d\u7ee7\u7684\u8bdd\uff0c\u4e0b\u53bb\u3000\u5598\u4e24\u53e3\u6c14\u4e5f \u597d\u3002\uff0c\uff0c\u706b\u773c\u91d1\u96d5\u662f\u4ec0\u4e48\u8eab\u4efd\uff0c\u8fd9\u79cd\u6b66\u6797\u4e2d\u6210\u540d\u9732\u8138\u7684\u4eba\u7269\uff0c\u5c31\u662f\u5200\u67b6\u3000\u5728\u8116\u5b50\u4e0a\uff0c\u4e5f\u4e0d\u4f1a \u76b1\u4e00\u76b1\u7709\u5934\uff0c\u6b64\u523b\u600e\u5403\u5f97\u4e0b\u8fd9\u79cd\u5c16\u9178\u523b\u8584\u7684\u8bdd\u3002\u4ed6\u731b\u7529\u957f\u987b\uff0c\u4e00\u3000\u7ffb\u8155\u5b50\uff0c\u7adf\u4ece\u8896\u53e3\u91cc\u64a4\u51fa \u4e00\u5bf9\u5175\u5203\u6765\uff0c\u7cbe\u5149\u8000\u76ee\uff0c\u957f\u624d\u5c3a\u8bb8\uff0c\u5374\u662f\u4e00\u5bf9\u7f55\u89c1\u7684\u5916\u95e8\u3000\u5229\u5668\u201d\u5206\u6c34\u5ce8\u5d4b\u523a\uff3c\u8427\u8001\u96d5\u8fd9\u4e00 \u64a4\u51fa\u5175\u5203\u6765\uff0c\u7fa4\u8c6a\u53c8\u4e0d\u7981\u53d1\u51fa\u60ca\u559f\uff0c\u77e5\u9053\u8fd9\u6c34\u4e0a\u540d\u3000\u4eba\u7684\u624b\u4e0b\uff0c\u679c\u771f\u4e0d\u51e1\uff0c\u987b\u77e5\u5927\u5bb6\u90fd\u662f\u773c \u775b\u96ea\u4eae\u7684\u7ec3\u5bb6\u5b50\uff0c\u4e00\u770b\u89c1\u8fd9\u5bf9\u5175\u5203\uff0c\u5c31\u77e5\u9053\u3000\u82e5\u975e\u6709\u771f\u529f\u592b\uff0c\u4e5f\u4e0d\u6562\u4f7f\u8fd9\u79cd\u5175\u5203\u3002 \u3000\u3000\u3000\u4f46\u5de6\u624b\u795e\u5251\u5374\u4ecd\u7136\u5fae\u5fae\u51b7\u7b11\uff0c\u6beb\u4e0d\u5728\u610f\uff0c\u624b\u8155\u4e00\u8f6c\uff0c\u9752\u5149\u66b4\u957f\uff0c\u5df2\u5c06\u80cc\u540e\u7684\u957f\u3000\u5251\u64a4 \u5728\u624b\u91cc\uff0c\u4e00\u53cd\u5e38\u89c4\uff0c\u5374\u662f\u5de6\u624b\u6301\u5251\u3002 \u3000\u3000\u3000\u53ea\u89c1\u4ed6\u864e\u8170\u4e00\u626d\uff0c\u5e62\u8155\u5fae\u632b\uff0c\u8fd9\u67c4\u5251\u7531\u4e0b\u800c\u4e0a\uff0c\u659c\u659c\u4e0a\u626c\uff0c\u731b\u5730\u5410\u6c14\u95ed\u58f0\uff0c\u5389\u3000\u53f1\u4e00 \u58f0\uff1a\u201c\u770b\u62db\uff01\u201d \u3000\u3000\u3000\u5251\u52bf\u5982\u8679\uff0c\u7adf\u53c8\u500f\u7136\u843d\u4e0b\uff0c\u5206\u5fc3\u523a\u51fa\uff0c\u5e26\u7740\u4e00\u7f15\u5c16\u98ce\uff0c\u88ad\u5411\u8427\u8fdf\u3002 \u3000\u3000\u3000\u7fa4\u8c6a\u53ea\u542c\u545b\u5577\u4e00\u54cd\uff0c\u4eba\u5f71\u53c8\u4e4d\u5206\uff0c\u539f\u6765\u5c31\u5728\u4e01\u8863\u8fd9\u4e00\u5251\u98de\u53bb\u4e4b\u9645\uff0c\u90a3\u8427\u8001\u96d5\u638c\u3000\u4e2d\u4e00 \u53cc\u5ce8\u5d4b\u523a\u5411\u5916\u4e00\u5c01\uff0c\u8eab\u5f62\u7ea6\u8fdb\u4e00\u6b65\uff0c\u4ed6\u7adf\u4ee5\u638c\u4e2d\u8fd9\u4efd\u91cf\u751a\u8f7b\u7684\u77ed\u5175\u5203\uff0c\u786c\u63a5\u4e86\u3000\u4e01\u8863\u7684\u4e00 \u5251\u3002 \u3000\u3000\u3000\u5de6\u624b\u638c\u4e2d\u7684\u8fd9\u53e3\u5251\uff0c\u5f97\u81ea\u5e08\u5085\uff0c\u7cbe\u94a2\u767e\u70bc\uff0c\u4e00\u51fb\u4e4b\u4e0b\uff0c\u81ea\u7136\u5206\u6beb\u65e0\u635f\uff0c\u90a3\u8427\u8001\u3000\u96d5\u53f3 \u624b\u7684\u5ce8\u5d4b\u523a\u4e0a\u4e3e\u9f50\u7709\uff0c\u5de6\u624b\u7684\u5ce8\u5d4b\u523a\u5e73\u4e3e\u5f53\u80f8\uff0c\u51dd\u7136\u5353\u7acb\uff0c\u7a33\u5982\u5c71\u5cb3\uff0c\u4e00\u62db\u8fc7\u3000\u540e\uff0c\u5e76\u4e0d\u8fdb \u51fb\u3002 \u3000\u3000\u3000\u4f46\u662f\u7fa4\u8c6a\u5374\u770b\u5f97\u66f4\u4e3a\u7d27\u5f20\uff0c\u8fd9\u4e24\u4eba\u5bf9\u9762\u5353\u7acb\uff0c\u6b63\u5982\u4e24\u53ea\u5f85\u673a\u800c\u6597\u7684\u96c4\u9e21\uff0c\u867d\u7136\u3000\u6b64\u523b \u4ff1\u90fd\u672a\u518d\u51fa\u624b\uff0c\u4f46\u5374\u4e0d\u8fc7\u662f\u5267\u6597\u7684\u7247\u523b\u9759\u5bc2\u7f62\u4e86\u3002 \u3000\u3000\u3000\u679c\u7136\uff0c\u970e\u773c\u95f4\uff0c\u53ea\u89c1\u90a3\u5de6\u624b\u795e\u5251\u4e01\u8863\u659c\u8eab\u4fa7\u6b65\uff0c\u5de6\u624b\u5251\u9752\u5149\u9519\u843d\uff0c\u731b\u5730\u4e00\u4e2a\u5251\u3000\u82b1\uff0c \u4ece\u4e0a\u5f80\u4e0b\u4e00\u65cb\uff0c\u5251\u8d70\u8f7b\u7075\uff0c\u5f80\u53f3\u4e00\u62a2\u6b65\uff0c\u5251\u950b\u518d\u4e00\u8f6c\uff0c\u63c9\u8eab\u800c\u4e0a\uff0c\u5530\uff01\u5530\uff01\u8fde\u3000\u63a5\u4e24\u5251\u3002 \u3000\u3000\u3000\u706b\u773c\u91d1\u96d5\u6012\u53f1\u4e00\u58f0\uff0c\u53f3\u624b\u5ce8\u5d4b\u523a\u4e00\u5c01\u5251\u8eab\uff0c\u5de6\u624b\u5ce8\u5d4b\u523a\u201c\u9752\u9f99\u51fa\u4e91\u201d\uff0c\u55d6\u5730\uff0c\u3000\u7adf\u7136 \u5b88\u4e2d\u5e26\u653b\uff0c\u9aa4\u5f80\u4e01\u8863\u9f3b\u4fa7\u201c\u6c89\u9999\u7a74\u201d\u70b9\u53bb\u3002 \u3000\u3000\u3000\u4f46\u662f\u5de6\u624b\u795e\u5251\u5728\u8fd9\u8d9f\u5251\u6cd5\u4e0a\uff0c\u5df2\u6709\u6570\u5341\u5e74\u6027\u547d\u53cc\u4fee\u7684\u9020\u8be3\uff0c\u7aef\u7684\u53d8\u5316\u5de7\u6377\uff0c\u865a\u3000\u5b9e\u83ab \u6d4b\uff0c\u6b64\u523b\u4e00\u9886\u5251\u52bf\uff0c\u5fae\u4e00\u659c\u8eab\uff0c\u201c\u5012\u8f6c\u9634\u9633\u201d\uff0c\u5de6\u624b\u5251\u4e00\u6c89\uff0c\u4e00\u63d0\uff0c\u8fd9\u4e00\u5251\u64a9\u3000\u4e0a\uff0c\u7acb\u523b\u4fbf \u5f97\u6d1e\u8179\u7a7f\u80f8\u3002 \u3000\u3000\u3000\u4ed6\u5de6\u624b\u6301\u5251\uff0c\u5251\u76f4\u504f\u950b\uff0c\u6b63\u662f\u6b66\u6797\u4e2d\u6781\u4e3a\u7f55\u89c1\u7684\u62db\u6570\uff0c\u91d1\u9ca4\u8427\u5e73\u7741\u7740\u773c\u775b\uff0c\u638c\u3000\u5fc3\u76f4 \u6ccc\u51b7\u6c57\uff0c\u4e3a\u4ed6\u7684\u8001\u7236\u62c5\u5fc3\u4e0d\u5df2\u3002 \u3000\u3000\u3000\u4f46\u8427\u8fdf\u638c\u4e2d\u4e00\u5bf9\u5ce8\u5d4b\u523a\uff0c\u683c\u3001\u6458\u3001\u523a\uff0c\u7ffb\u98de\u516b\u6253\uff0c\u8fd9\u5e74\u5df2\u53e4\u7a00\u7684\u8001\u8005\uff0c\u4ed7\u7740\u8fd9\u3000\u5bf9\u5916 \u95e8\u7684\u77ed\u5175\u5203\uff0c\u7adf\u7136\u8fde\u8d70\u9669\u62db\uff0c\u5e9e\u5927\u7684\u8eab\u8eaf\uff0c\u5728\u8fd9\u9152\u697c\u4e0a\u65b9\u5706\u4e0d\u8fc7\u4e00\u4e08\u7684\u5730\u65b9\u4e0a\u3000\u95ea\u5c55\u817e\u632a\uff0c \u82b1\u767d\u7684\u957f\u987b\u88ab\u5e26\u5f97\u56db\u4e0b\u98de\u821e\uff0c\u4f46\u811a\u5e95\u4e0b\u5374\u4ecd\u7136\u6ca1\u6709\u5e26\u51fa\u534a\u70b9\u58f0\u97f3\u6765\u3002 \u3000\u3000\u3000\u4f46\u89c1\u9152\u697c\u5149\u534e\u4e71\u95ea\uff0c\u5374\u9759\u5f97\u4e0d\u53d1\u4e00\u4e1d\u58f0\u606f\uff0c\u706b\u773c\u91d1\u96d5\u201c\u602a\u87d2\u7ffb\u8eab\u201d\uff0c\u864e\u8eaf\u5fae\u65cb\u3000\uff0c \u201c\u8eab\u4e34\u516b\u89d2\u201d\uff0c\u5de6\u53f3\u53cc\u523a\u631f\u52b2\u98ce\uff0c\u94f6\u661f\u4e07\u70b9\uff0c\u6b3a\u8eab\u8fdb\u903c\uff0c\u6b63\u662f\u201c\u4e00\u5bf8\u77ed\uff0c\u4e00\u5bf8\u9669\u3000\u201d\uff0c\u62db\u62db \u72e0\u8fa3\uff0c\u706b\u5019\u8001\u5230\u3002 \u3000\u3000\u3000\u5de6\u624b\u795e\u5251\u9762\u5e26\u51b7\u7b11\uff0c\u5251\u950b\u4e00\u6302\u8427\u8001\u96d5\u5de6\u8155\uff0c\u201c\u62bd\u8f99\u8fde\u73af\u201d\uff0c\u5251\u5c16\u8ddf\u7740\u5f80\u5916\u4e00\u9001\u3000\uff0c\u4e00 \u62db\u4e24\u5f0f\uff0c\u659c\u524a\u8427\u8fdf\u80c1\u4e0b\u3002 \u3000\u3000\u3000\u8427\u8001\u96d5\u731b\u4e00\u62e7\u8eab\uff0c\u5de6\u624b\u5ce8\u5d4b\u523a\u6284\u5251\u5e95\u5f80\u4e0a\u5d29\uff0c\u53f3\u624b\u5ce8\u5d4b\u523a\u7ffb\u8155\u523a\u51fa\uff0c\u53c8\u662f\u201c\u545b\u3000\u5577\u201d \u4e00\u58f0\u8f7b\u5578\uff0c\u5374\u89c1\u5de6\u624b\u795e\u5251\u811a\u4e0b\u5fae\u9519\uff0c\u201c\u9c7c\u8dc3\u9f99\u95e8\u201d\uff0c\u5251\u5149\u75be\u5982\u7535\u63a3\uff0c\u76f4\u523a\u8427\u8fdf\u3000\u8033\u65c1\u7684\u201c\u7acb \u73e0\u7a74\u201d\u3002 \u3000\u3000\u3000\u706b\u773c\u91d1\u96d5\u65b9\u624d\u4e24\u6b21\u786c\u63a5\u786c\u67b6\uff0c\u5df2\u7136\u8bd5\u51fa\u5bf9\u65b9\u7684\u8155\u529b\u4e0d\u5982\u81ea\u5df1\uff0c\u4ed6\u5728\u6b66\u6797\u7ffb\u6eda\u8fd9\u3000\u4e48\u591a \u5e74\uff0c\u52a8\u624b\u7684\u7ecf\u9a8c\uff0c\u53ef\u79f0\u591a\u5f97\u4e0d\u53ef\u80dc\u6570\uff0c\u4e00\u627e\u51fa\u654c\u624b\u7684\u5f31\u70b9\uff0c\u4fbf\u518d\u4e5f\u4e0d\u80af\u653e\u677e\uff0c\u3000\u5fae\u4e00\u9000\u6b65\uff0c \u201c\u6a2a\u67b6\u91d1\u6881\u201d\uff0c\u53cc\u593a\u7adf\u53c8\u731b\u5730\u4e0a\u7ffb\uff0c\u627e\u7740\u4e01\u8863\u7684\u5251\u950b\u683c\u53bb\u3002 \u3000\u3000\u3000\u54ea\u77e5\u5de6\u624b\u795e\u5251\u8fd9\u53e3\u5251\u65b9\u5230\u4e2d\u9014\uff0c\u5c31\u786c\u751f\u751f\u64a4\u4e86\u56de\u53bb\uff0c\u8eab\u968f\u5251\u8d70\uff0c\u811a\u4e0b\u201c\u5012\u8e29\u4e03\u3000 \u661f\u201d\uff0c\u8fde\u73af\u51e0\u6b65\uff0c\u5411\u540e\u9762\u9000\u4e86\u4e03\u5c3a\uff0c\u53f3\u624b\u5f80\u8170\u95f4\u4e00\u63a2\uff0c\u63a5\u7740\u624b\u8155\u4e00\u7529\uff0c\u53ea\u89c1\u9752\u5149\u4e00\u3000\u95ea\uff0c\u8131 \u624b\u98de\u53bb\u3002 \u3000\u3000\u3000\u706b\u773c\u91d1\u96d5\u5fae\u5fae\u4e00\u987f\u65f6\uff0c\u5df2\u89c1\u5bd2\u5149\u4e00\u7f15\uff0c\u95ea\u7535\u822c\u88ad\u5411\u81ea\u5df1\u7684\u524d\u80f8\uff0c\u53cc\u624b\u201c\u5ce8\u5d4b\u201d\u3000\u523a\u521a \u5f80\u524d\u4e00\u5c01\uff0c\u54ea\u77e5\u5530\uff01\u5530\uff01\u4e24\u9053\u52b2\u6025\u7684\u98ce\u58f0\uff0c\u53ea\u89c1\u4e24\u9053\u5bd2\u5149\u7adf\u7136\u540e\u53d1\u5148\u81f3\uff0c\u4e00\u5de6\u3000\uff0c\u4e00\u53f3\uff0c\u88ad \u5411\u81ea\u5df1\u53cc\u811a\u3002 \u3000\u3000\u3000\u4ed6\u5927\u60ca\u4e4b\u540e\uff0c\u5f80\u540e\u731b\u5730\u4e00\u4ef0\u8eab\uff0c\u957f\u987b\u7ffb\u98de\u5904\uff0c\u5e9e\u5927\u7684\u8eab\u8eaf\u5411\u540e\u76f4\u5012\uff0c\u4ed6\u7adf\u5728\u8fd9\u3000\u79cd\u5730 \u65b9\uff0c\u7528\u8d77\u6b66\u6797\u4e2d\u7684\u7edd\u6280\u201c\u94c1\u62cd\u6478\u201d\u6765\u4e86\u3002 \u3000\u3000\u3000\u7fa4\u8c6a\u8fd9\u65f6\u773c\u775b\u90fd\u770b\u76f4\u4e86\uff0c\u6709\u7684\u8131\u53e3\u53eb\u4e86\u58f0\uff1a\u201c\u5b50\u6bcd\u53cc\u98de\uff01\u201d \u3000\u3000\u3000\u53ea\u89c1\u8fd9\u5e7f\u897f\u5927\u5bb6\u7684\u6210\u540d\u7edd\u6280\uff0c\u679c\u7136\u4e0d\u540c\u51e1\u54cd\uff0c\u5149\u534e\u4e09\u95ea\u4e4b\u5904\uff0c\u5251\u52bf\u4e00\u9886\uff0c\u5de6\u638c\u3000\u4e2d\u7684 \u8fd9\u53e3\u5251\uff0c\u7adf\u7136\u50cf\u662f\u548c\u90a3\u4e09\u67c4\u5c0f\u5251\u540c\u65f6\u98de\u4e86\u51fa\u53bb\u4f3c\u7684\uff0c\u5c31\u5728\u8427\u8001\u96d5\u8eab\u8eaf\u65b9\u81ea\u540e\u4ef0\u3000\u7684\u90a3\u5239\u90a3 \u91cc\uff0c\u5de6\u624b\u795e\u5251\u4e00\u584c\u8170\uff0c\u5f80\u524d\u9762\u63a0\u4e86\u8fc7\u53bb\uff0c\u5de6\u624b\u5251\u5e26\u8d77\u4e00\u6e9c\u5149\u534e\uff0c\u671d\u7740\u90a3\u4e24\u3000\u6761\u817f\u50cf\u662f\u77f3\u6869\u4f3c \u5730\u9489\u5728\u5730\u4e0a\u7684\u8427\u8001\u96d5\u5288\u4e0b\uff0c\u800c\u4ed6\u7684\u53f3\u624b\uff0c\u5374\u4ecd\u7136\u6309\u5728\u8170\u95f4\u76ae\u8170\u5e26\u4e0a\u3000\u63d2\u7740\u7684\u5251\u67c4\u4e4b\u4e0a\u3002 \u3000\u3000\u3000\u7fa4\u8c6a\u4e0d\u7981\u5012\u62bd\u4e00\u53e3\u51c9\u6c14\uff0c\u90fd\u77e5\u9053\u8427\u8001\u96d5\u5373\u4f7f\u80fd\u591f\u9003\u5f97\u8fc7\u4ed6\u5de6\u624b\u201c\u6bcd\u5251\u201d\u7684\u8fd9\u4e00\u3000\u5241\uff0c \u53ef\u662f\u5374\u518d\u4e5f\u65e0\u6cd5\u907f\u5f00\u4ed6\u53f3\u624b\u5373\u5c06\u53d1\u51fa\u7684\u53e6\u4e00\u67c4\u201c\u5b50\u5251\u201d\u4e86\u3002 \u7b2c\u5341\u96f6\u7ae0>> \u53e4\u9f99\u300a\u6e58\u5983\u5251\u300b \u7b2c\u5341\u96f6\u7ae0 \u3000\u3000\u3000\u5b50\u6bcd\u53cc\u98de\uff0c\u5de6\u624b\u795e\u5251\u4e01\u8863\uff0c\u9aa4\u65bd\u7edd\u6280\uff0c\u773c\u89c1\u79f0\u96c4\u6c34\u4e0a\u6570\u5341\u5e74\u7684\u706b\u773c\u91d1\u96d5\u8427\u8fdf\uff0c\u3000\u7acb\u523b \u5c31\u5f97\u4e27\u5728\u4ed6\u8fd9\u201c\u5b50\u6bcd\u53cc\u98de\u201d\u4e4b\u4e0b\u3002 \u3000\u3000\u3000\u5728\u8fd9\u5df2\u5c06\u51b3\u5b9a\u4e00\u4e2a\u4eba\u7684\u751f\u6b7b\u7684\u4e00\u77ac\u95f4\uff0c\u5404\u4eba\u9762\u4e0a\uff0c\u795e\u8272\u8fe5\u7136\u4e0d\u540c\uff0c\u663e\u89c1\u5f97\u8fd9\u4e9b\u3000\u4eba\u5fc3 \u4e2d\u6240\u601d\u5fd6\u7684\uff0c\u4e5f\u5927\u6709\u5dee\u5f02\u3002 \u3000\u3000\u3000\u7075\u86c7\u6bdb\u81ec\u9762\u5e26\u72de\u7b11\uff0c\u767e\u6b65\u98de\u82b1\u9690\u542b\u5f97\u8272\uff0c\u94c1\u624b\u4ed9\u733f\u76ee\u5149\u95ea\u52a8\uff0c\u516b\u9762\u73b2\u73d1\u5f20\u5927\u4e86\u3000\u5634\uff0c \u6bdb\u6587\u742a\u5374\u5728\u5fc3\u91cc\u5730\u601d\u5fd6\u7740\uff1a\u201c\u8fd9\u4e00\u62db\u4e5f\u6ca1\u6709\u4ec0\u4e48\u4e86\u4e0d\u8d77\uff0c\u6362\u4e86\u6211\u4ed6\u5c31\u4e00\u70b9\u529e\u6cd5\u3000\u90fd\u6ca1\u6709 \u4e86\u3002\u201d \u3000\u3000\u3000\u800c\u7f2a\u6587\u5462\uff0c\u5374\u4ecd\u7136\u5e26\u7740\u90a3\u79cd\u5fae\u7b11\uff0c\u53ea\u662f\u8fd9\u6b21\uff0c\u4ed6\u90a3\u83ab\u6d4b\u9ad8\u6df1\u7684\u5fae\u7b11\uff0c\u4f3c\u4e4e\u56e0\u7740\u3000\u4e9b\u987b \u601c\u60dc\u7684\u6210\u5206\uff0c\u800c\u53d8\u5f97\u6709\u4e9b\u4eba\u60c5\u5473\u8d77\u6765\u3002 \u3000\u3000\u3000\u91d1\u9ca4\u8427\u5e73\u53cc\u76ee\u706b\u8d64\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u6251\u4e0a\u524d\u53bb\uff0c\u53ea\u89c9\u9762\u524d\u98ce\u58f0\u4e00\u51db\uff0c\u539f\u6765\u65b9\u624d\u90a3\u4e09\u3000\u53e3\u5c0f \u5251\uff0c\u6b63\u52bf\u631f\u4f59\u5a01\uff0c\u4ece\u4ed6\u8eab\u524d\u63a0\u8fc7\u3002 \u3000\u3000\u3000\u63a5\u7740\u201c\u593a\u3001\u593a\u3001\u593a\u201d\u4e09\u58f0\uff0c\u8fd9\u4e09\u53e3\u5251\u90fd\u9489\u5728\u8fd9\u9152\u697c\u7684\u4e00\u5f88\u5927\u67f1\u5b50\u4e0a\uff0c\u53ea\u5269\u4e0b\u4e09\u3000\u5bf8\u591a \u957f\u7684\u5251\u67c4\uff0c\u9732\u5728\u5916\u9762\uff0c\u674f\u9ec4\u8272\u7684\u4e1d\u7a57\uff0c\u5fae\u5fae\u98a4\u6296\u3002 \u3000\u3000\u3000\u8fd9\u4e9b\u4e8b\u7b14\u4e0b\u5199\u6765\u867d\u6162\uff0c\u7136\u800c\u5728\u5f53\u65f6\u5373\u5feb\u5982\u7535\u5149\u4e00\u95ea\uff0c\u706b\u773c\u91d1\u96d5\u76ee\u5149\u52a8\u5904\uff0c\u5df1\u7136\u3000\u770b\u5230 \u9752\u5149\u4e00\u6e9c\uff0c\u659c\u659c\u5411\u81ea\u5df1\u5241\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u3000\u4ed6\u65b9\u81ea\u6697\u53f9\u4e00\u58f0\uff0c\u54ea\u77e5\u90a3\u9053\u672c\u5df1\u5c06\u8981\u5288\u5728\u81ea\u5df1\u8eab\u4e0a\u7684\u9752\u5149\uff0c\u4e0d\u77e5\u600e\u5730\u53c8\u7a81\u7136\u5730\u3000\u64a4\u4e86 \u56de\u53bb\uff0c\u4ed6\u5fae\u6115\u4e4b\u4e0b\uff0c\u5de6\u817f\u671d\u5916\u4e00\u8e74\uff0c\u8170\u4e0a\u4e00\u4f7f\u529b\uff0c\u5de6\u624b\u7684\u5ce8\u5d4b\u523a\u4e00\u70b9\u697c\u677f\uff0c\u5530\u3000\u5730\uff0c\u63a0\u4e86\u8d77 \u6765\u3002 \u3000\u3000\u3000\u4ed6\u8eab\u5f62\u752b\u81ea\u7ad9\u7a33\uff0c\u53c8\u542c\u5f97\u201c\u593a\u201d\u5730\u4e00\u58f0\uff0c\u76ee\u5149\u95ea\u5904\uff0c\u5374\u89c1\u4e00\u4ef6\u6697\u5668\uff0c\u9489\u5165\u58c1\u91cc\u3000\uff0c\u800c \u6ee1\u697c\u7fa4\u8c6a\uff0c\u5374\u53c8\u8d77\u4e86\u4e00\u9635\u9a9a\u52a8\u3002 \u3000\u3000\u3000\u4ed6\u81ea\u7136\u4e0d\u77e5\u9053\u5c31\u5728\u65b9\u624d\u90a3\u95f4\u4e0d\u5bb9\u53d1\u7684\u4e00\u5239\u90a3\u91cc\uff0c\u5de6\u624b\u795e\u5251\u9762\u542b\u51b7\u7b11\uff0c\u8fd0\u5251\u4e0b\u5288\u3000\uff0c\u54ea \u77e5\u8eab\u4fa7\u7a81\u7136\u98ce\u58f0\u4e00\u51db\uff0c\u4ed6\u7adf\u89c9\u51fa\u6709\u6697\u5668\u5411\u81ea\u5df1\u80c1\u4e0b\u6253\u6765\u3002 \u3000\u3000\u3000\u8fd9\u79cd\u6b66\u6797\u9ad8\u624b\u65e0\u8bba\u5728\u4efb\u4f55\u60c5\u51b5\u4e0b\uff0c\u90fd\u6709\u7740\u975e\u5e38\u7684\u8b66\u89c9\uff0c\u968f\u65f6\u63d0\u9632\u7740\u7a81\u6765\u7684\u6697\u7b97\u3000\uff0c\u5de6 \u624b\u795e\u5251\u4e01\u8863\u81ea\u7136\u4e5f\u4e0d\u4f8b\u5916\u3002 \u3000\u3000\u3000\u8fd9\u65f6\u81ea\u7136\u662f\u4f24\u654c\u5176\u6b21\uff0c\u81ea\u6551\u4e3a\u5148\uff0c\u987b\u77e5\u4ed6\u5df2\u542c\u51fa\u8fd9\u6697\u5668\u98ce\u58f0\u5f3a\u52b2\uff0c\u6765\u52bf\u7edd\u5feb\uff0c\u3000\u81ea\u5df1 \u82e5\u60f3\u5148\u5288\u4e0a\u8427\u8fdf\u4e00\u5251\uff0c\u90a3\u4e48\u81ea\u5df1\u80c1\u4e0b\u4e5f\u5f97\u52a0\u4e2a\u5927\u7a9f\u7abf\u3002 \u3000\u3000\u3000\u4ed6\u53ea\u5f97\u731b\u4e00\u5438\u6c14\uff0c\u786c\u751f\u751f\u5c06\u5251\u52bf\u64a4\u4e86\u56de\u6765\uff0c\u5927\u62e7\u8eab\uff0c\u5411\u540e\u4e00\u95ea\u2014\u2014\u53ea\u89c1\u4e00\u9053\u91d1\u3000\u5149\uff0c \u5feb\u5982\u5954\u96f7\u822c\u5411\u81ea\u5df1\u8eab\u524d\u6253\u4e86\u8fc7\u53bb\uff0c\u4ee5\u4ed6\u8fd9\u79cd\u53d1\u6697\u5668\u7684\u540d\u624b\uff0c\u53ef\u4e5f\u4e0d\u514d\u4e3a\u8fd9\u9053\u91d1\u3000\u5149\u7684\u53bb\u52bf\u4e4b \u6025\u800c\u6697\u5403\u4e00\u60ca\u3002 \u3000\u3000\u3000\u4ed6\u5927\u60ca\u4e4b\u4e0b\uff0c\u76ee\u5149\u56db\u626b\uff0c\u53ea\u89c1\u7075\u86c7\u6bdb\u81ec\u7b49\u4ff1\u90fd\u9762\u5e26\u5f02\u5bb9\uff0c\u6ee1\u697c\u7fa4\u8c6a\u66f4\u662f\u90fd\u53d1\u51fa\u3000\u60ca\u5f02 \u7684\u53eb\u58f0\u3002 \u3000\u3000\u3000\u4ed6\u518d\u4e00\u5fd6\u91cf\u8fd9\u9053\u91d1\u5149\u7684\u6765\u8def\uff0c\u663e\u7136\u662f\u6765\u81ea\u7a97\u5916\uff0c\u800c\u8fd9\u65f6\u7075\u86c7\u6bdb\u81ec\u5df2\u7136\u6781\u5feb\u5730\u4e00\u3000\u8f6c \u8eab\uff0c\u671d\u7a97\u6237\u7684\u5916\u9762\u671b\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u3000\u7a97\u5916\u6709\u98ce\u5439\u8fc7\uff0c\u4f46\u662f\u5374\u65e0\u4eba\u5f71\uff0c\u697c\u4e0b\u90a3\u6761\u8857\u4e0a\u6b64\u65f6\u4e5f\u662f\u9759\u8361\u8361\u5730\uff0c\u90a3\u4e9b\u91d1\u886b\u5927\u3000\u6c49\u4e5f \u56e0\u901a\u5bb5\u672a\u7720\uff0c\u6b64\u523b\u5df2\u8eb2\u5728\u5c4b\u6a90\u4e0b\u6253\u778c\u7761\u3002 \u3000\u3000\u3000\u6625\u65e5\u7684\u9633\u5149\u7531\u4e1c\u65b9\u5c04\u4e0b\uff0c\u7167\u5728\u8857\u5bf9\u9762\u7684\u4e00\u697c\u5b57\u4e0a\uff0c\u53ef\u662f\u5bf9\u9762\u7684\u697c\u5b57\u4e5f\u662f\u9759\u65e0\u4e00\u3000\u4eba\uff0c \u53ea\u6709\u5c4b\u6a90\u4e0a\u672a\u5e72\u7684\u6668\u9732\uff0c\u88ab\u9633\u5149\u6620\u51fa\u6676\u5149\u3002 \u3000\u3000\u3000\u8fd9\u6b66\u6797\u67ad\u96c4\u7eb5\u7136\u673a\u667a\u6df1\u6c89\uff0c\u6b64\u523b\u4e5f\u4e0d\u7981\u608d\u7136\u8272\u53d8\uff0c\u5fae\u53f1\u4e00\u58f0\uff1a\u201c\u8001\u56db\uff0c\u4f60\u51fa\u53bb\u3000\u770b \u770b\u3002\u201d \u3000\u3000\u3000\u94c1\u624b\u4ed9\u733f\u7acb\u523b\u5e94\u4e86\u4e00\u58f0\uff0c\u4e00\u8dfa\u811a\uff0c\u7a7f\u7a97\u800c\u51fa\uff0c\u7075\u86c7\u6bdb\u81ec\u5374\u4e00\u7ffb\u864e\u8eaf\uff0c\u63a0\u5230\u5bf9\u9762\u3000\u7684\u5899 \u4e0a\uff0c\u5c06\u9489\u5728\u5899\u4e0a\u7684\u90a3\u6697\u5668\u62d4\u51fa\u4e00\u770b\u2014\u2014\u5374\u8d6b\u7136\u53c8\u662f\u4e00\u67c4\u91d1\u8272\u5c0f\u5251\u3002 \u3000\u3000\u3000\u8fd9\u65f6\u7fa4\u8c6a\u53c8\u54d7\u7136\u4f4e\u547c\u51fa\u6765\uff0c\u539f\u6765\u65b9\u624d\u5927\u5bb6\u6ce8\u610f\u529b\uff0c\u90fd\u653e\u5728\u4e01\u8863\u7684\u90a3\u53e3\u5251\u4e0a\uff0c\u8c01\u3000\u4e5f\u6ca1 \u6709\u6ce8\u610f\u5230\u6697\u5668\u662f\u4ece\u54ea\u91cc\u6765\u7684\u3002 \u3000\u3000\u3000\u5c31\u8fde\u7075\u86c7\u6bdb\u81ec\uff0c\u4e5f\u89c9\u5f97\u6709\u6697\u5668\u7531\u7a97\u53e3\u8fd9\u8fb9\u5f80\u91cc\u6253\u5165\uff0c\u7b49\u5230\u4ed6\u56de\u5934\u7684\u65f6\u5019\uff0c\u7a97\u5916\u3000\u5df2\u65e0 \u4eba\u5f71\u4e86\u3002 \u3000\u3000\u3000\u7075\u86c7\u6bdb\u684c\u5c06\u8fd9\u67c4\u8fd1\u65e5\u5df1\u5728\u6c5f\u6e56\u4e2d\u9020\u51fa\u65e0\u7a77\u4e8b\u7aef\uff0c\u4e3a\u81ea\u5df1\u5e26\u6765\u83ab\u5927\u70e6\u607c\u7684\u91d1\u8272\u5c0f\u3000\u5251\u5728 \u624b\u4e2d\u7565\u4e00\u628a\u5f04\uff0c\u4e24\u9053\u957f\u7709\u7d27\u7d27\u76b1\u5230\u4e00\u5904\u3002 \u3000\u3000\u3000\u8fd9\u65f6\u5de6\u624b\u795e\u5251\u4e01\u8863\u4e5f\u5c06\u706b\u773c\u91d1\u96d5\u5148\u653e\u5728\u4e00\u8fb9\uff0c\u7eb5\u8eab\u63a0\u4e86\u8fc7\u6765\uff0c\u76ee\u5149\u4e5f\u5728\u4ed6\u624b\u4e2d\u3000\u6240\u6301 \u7684\u8fd9\u67c4\u91d1\u8272\u5c0f\u5251\u4e0a\u6253\u8f6c\uff0c\u95ee\u9053\uff1a\u201c\u53c8\u662f\u4ed6\uff1f\u201d \u3000\u3000\u3000\u6bdb\u81ec\u5fae\u5fae\u70b9\u4e86\u70b9\u5934\uff0c\u9e70\u96bc\u822c\u5730\u76ee\u5149\uff0c\u5374\u5728\u65c1\u8fd1\u7a97\u53e3\u7684\u90a3\u4e9b\u4eba\u9762\u4e0a\u4e00\u4e00\u641c\u5bfb\u7740\u2014\u3000\u2014\u9996 \u5148\uff0c\u4ed6\u770b\u5230\u516b\u9762\u73b2\u73d1\u80e1\u4e4b\u8f89\uff0c\u662f\u7ad9\u5728\u7a97\u65c1\u7684\uff0c\u6b64\u523b\u6b63\u6a2a\u7740\u8eab\u5b50\uff0c\u4e00\u4f1a\u513f\u7a97\u5916\u671b\u3000\u671b\uff0c\u4e00\u4f1a\u513f \u53c8\u8f6c\u8fc7\u5934\u6765\u671b\u7740\u4ed6\u624b\u4e0a\u7684\u91d1\u5251\u3002 \u3000\u3000\u3000\u516b\u9762\u73b2\u73d1\u80e1\u4e4b\u8f89\u8eab\u65c1\uff0c\u7ad9\u7740\u7684\u5374\u662f\u81ea\u5df1\u5973\u513f\uff0c\u6b63\u63a2\u7740\u5934\u53bb\u671b\u7a97\u5916\uff0c\u800c\u5728\u5979\u65c1\u8fb9\u3000\u7684\uff0c \u5374\u662f\u90a3\u4e2a\u6177\u6168\u591a\u91d1\u7684\u5bcc\u5bb6\u516c\u5b50\u3002 \u3000\u3000\u3000\u518d\u8fc7\u53bb\uff0c\u5c31\u4ed6\u81ea\u5df1\u5148\u524d\u6240\u7ad9\u7684\u4f4d\u7f6e\uff0c\u8fd9\u6b66\u6797\u67ad\u96c4\u5fc3\u4e2d\u4e00\u8f6c\uff0c\u5fd6\u9053\uff1a\u201c\u65b9\u624d\u8fd9\u67c4\u3000\u91d1 \u5251\uff0c\u662f\u7531\u6211\u5de6\u8fb9\u5c04\u5165\uff0c\u5982\u679c\u4e0d\u662f\u7531\u7a97\u5916\u5c04\u5165\u7684\uff0c\u5c31\u662f\u6211\u5de6\u8fb9\u7684\u8fd9\u4e9b\u4eba\u6240\u53d1\u2014\u2014\u201d\u3000\u4ed6\u76ee\u5149\u518d \u5728\u8fd9\u4e9b\u4eba\u8eab\u4e0a\u4e00\u8f6c\uff0c\u4e24\u9053\u6d53\u7709\u76b1\u5f97\u66f4\u6df1\uff0c\u7136\u540e\uff0c\u4ed6\u53c8\u63a5\u7740\u5fd6\u9053\uff1a\u201c\u80e1\u8001\u4e09\u3000\u548c\u742a\u513f\u81ea\u7136\u4e0d \u4f1a\uff0c\u552f\u4e00\uff0c\u53ef\u80fd\u5730\u5c31\u662f\u8fd9\u59d3\u7f2a\u7684\u5c0f\u5b50\uff0c\u54fc\uff01\u4ed6\u8bf4\u4ed6\u4e0d\u4f1a\u6b66\u529f\uff0c\u6211\u5374\u6709\u3000\u4e9b\u4e0d\u4fe1\uff0c\u53ef\u662f\u4e00\u4e00\u82e5 \u8bf4\u4ed6\u5c31\u662f\u91d1\u5251\u4fa0\uff0c\u4e5f\u4e0d\u53ef\u80fd\u5440\u2026\u2026\u201d \u3000\u3000\u3000\u201c\u90a3\u4e48\uff0c\u8fd9\u67c4\u91d1\u5251\u53ea\u6709\u4ece\u7a97\u5916\u5c04\u5165\u7684\u4e00\u9014\u4e86\uff0c\u4f46\u662f\uff0c\u8fd9\u4e5f\u4f3c\u4e4e\u4e0d\u5927\u53ef\u80fd\uff1f\u201d \u3000\u3000\u3000\u4ed6\u5de6\u601d\u53f3\u60f3\uff0c\u89c9\u5f97\u8fd9\u5176\u4e2d\u5927\u6709\u8e4a\u8df7\uff0c\u53ea\u89c1\u8fd9\u201c\u6b66\u6797\u9b41\u9996\u201d\u53cc\u7709\u518d\u4e00\u76b1\uff0c\u7ee7\u7eed\u8d70\u3000\u5230\u7f2a \u6587\u8eab\u540e\uff0c\u4f38\u51fa\u5de8\u638c\uff0c\u671d\u7f2a\u6587\u8eab\u4e0a\u4e00\u62cd\u3002 \u3000\u3000\u3000\u4ed6\u5b58\u5fc3\u60f3\u8bd5\u8bd5\uff0c\u624b\u5e95\u4e0b\u5df2\u7528\u51fa\u4e94\u5206\u771f\u529b\u3002\u516b\u9762\u73b2\u73d1\u80e1\u4e4b\u8f89\u6b64\u523b\u6b63\u9762\u5411\u8fd9\u8fb9\uff0c\u76ee\u3000\u5149\u52a8 \u5904\uff0c\u4e0d\u7981\u5413\u4e86\u4e00\u8df3\uff0c\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u8fd9\u662f\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u3000\u7075\u86c7\u6bdb\u81ec\u5fc3\u4e2d\u5fae\u8f6c\uff0c\u4e00\u7b11\u6536\u56de\u771f\u529b\uff0c\u624b\u638c\u8f7b\u8f7b\u62cd\u5728\u7f2a\u6587\u80a9\u4e0a\uff0c\u4e00\u9762\u5374\u5728\u6697\u5fd6\u7740\u3000\u3002 \u3000\u3000\u3000\u201c\u8fd9\u7f2a\u6587\u548c\u80e1\u4e09\u5f1f\u65e2\u662f\u7d20\u8bc6\uff0c\u60f3\u6765\u4e5f\u8bb8\u4e0d\u81f3\u4e8e\u6709\u4ec0\u4e48\u5dee\u9519\u5427\u201d\u800c\u8fd9\u65f6\u7f2a\u6587\u4e5f\u56de\u3000\u8fc7\u5934 \u6765\uff0c\u76ee\u5149\u6b63\u548c\u7075\u86c7\u6bdb\u81ec\u7684\u78b0\u5728\u4e00\u8d77\uff0c\u7075\u86c7\u6bdb\u81ec\u53cc\u7709\u5fae\u76b1\uff0c\u7b11\u95f4\u9053\uff1a\u201c\u7f2a\u6587\u8001\u5f1f\u3000\u65b9\u624d\u7ad9\u5728\u8fd9 \u91cc\uff0c\u53ef\u66fe\u89c9\u51fa\u80cc\u540e\u6709\u4ec0\u4e48\u5f71\u54cd\u5417\uff1f\u201d \u3000\u3000\u3000\u7f2a\u6587\u4e60\u60ef\u5730\u5fae\u7b11\u4e86\u4e00\u4e0b\uff0c\u6447\u4e86\u6447\u5934\uff0c\u6bdb\u6587\u742a\u5374\u62a2\u7740\u8bf4\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u771f\u662f\u7684\uff0c\u3000\u4f60\u8001 \u4eba\u5bb6\u8001\u4f1a\u95ee\u8d77\u4ed6\u6765\uff0c\u4ed6\u8fd9\u4e66\u5446\u5b50\u5440\uff01\u4eba\u5bb6\u5728\u80cc\u540e\u780d\u4ed6\u4e00\u5200\uff0c\u4ed6\u8fde\u5f71\u5b50\u90fd\u4e0d\u4f1a\u77e5\u3000\u9053\u7684\u3002\u201d \u3000\u3000\u3000\u7075\u86c7\u6bdb\u81ec\u5634\u89d2\u6cdb\u8d77\u4e00\u4e1d\u7b11\u610f\uff0c\u9887\u4e3a\u6ce8\u610f\u5730\u671d\u7f2a\u6587\u76ef\u4e86\u4e24\u773c\uff0c\u7136\u540e\uff0c\u56de\u8fc7\u5934\u53bb\uff0c\u3000\u5374\u89c1 \u8427\u8001\u96d5\u7236\u5b50\u7ad9\u5728\u4e00\u8d77\uff0c\u8f7b\u58f0\u4f4e\u8bed\u3002 \u3000\u3000\u3000\u7a81\u5730\uff0c\u7a97\u5916\u98ce\u58f0\u53c8\u4e00\u51db\uff0c\u6bdb\u81ec\u5fae\u4e00\u626d\u8170\uff0c\u811a\u8ddf\u534a\u65cb\uff0c\u56de\u5934\u671b\u5904\uff0c\u5374\u662f\u94c1\u624b\u4ed9\u733f\u3000\u63a0\u4e86 \u8fdb\u6765\uff0c\u4e00\u9762\u6447\u7740\u5934\uff0c\u4e00\u9762\u9053\uff1a\u201c\u5916\u9762\u8fde\u4e2a\u4eba\u5f71\u90fd\u6ca1\u6709\uff0c\u6211\u95ee\u4e86\u95ee\u5916\u9762\u7684\u5f1f\u5144\uff0c\u3000\u4e5f\u6ca1\u6709\u4eba\u770b \u89c1\u4ec0\u4e48\uff0c\u8fd9\u4e8b\u53ef\u6709\u70b9\u90aa\u95e8\uff0c\u96be\u9053\u90a3\u91d1\u5251\u4fa0\u4f1a\u98de\u4e0d\u6210\uff1f\u201d \u3000\u3000\u3000\u7075\u86c7\u6bdb\u81ec\u5728\u9f3b\u5b54\u4e2d\u51b7\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u770b\u8fd9\u4e24\u5e74\u6765\u4f60\u624b\u4e0b\u7684\u5f1f\u5144\u4eec\u8d8a\u6765\u8d8a\u3000\u61c8\u6020 \u4e86\uff0c\u6ca1\u6709\u4e8b\u8fd8\u597d\uff0c\u4e00\u9047\u4e0a\u4e8b\uff0c\u53ef\u5c31\u89c1\u51fa\u6211\u4eec\u5e73\u65e5\u517b\u7740\u8fd9\u73ed\u4eba\uff0c\u7adf\u7136\u5168\u662f\u5e9f\u6599\uff0c\u3000\u4e00\u70b9\u513f\u4e5f\u6392 \u4e0d\u4e0a\u7528\u573a\u3002\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u5c31\u662f\u8fd9\u91d1\u5251\u4fa0\u5012\u4e0d\u66fe\u98de\uff0c\u53ea\u662f\u90a3\u4e9b\u7ad9\u5728\u5916\u9762\u7684\u3000\u4eba\u5927\u65e0\u7528\uff0c\u6ca1\u6709\u770b \u5230\u800c\u5df2\u3002 \u3000\u3000\u3000\u94c1\u624b\u4ed9\u733f\u9762\u4e0a\u5fae\u7ea2\uff0c\u8fde\u58f0\u9053\uff1a\u201c\u5927\u54e5\u8bf4\u7684\u6781\u662f\uff0c\u8fd9\u4e9b\u4eba\u758f\u61d2\u5df2\u60ef\uff0c\u4eca\u540e\u5c0f\u5f1f\u8981\u3000\u597d\u597d \u7763\u4fc3\u4ed6\u4eec\u3002\u201d \u3000\u3000\u3000\u7075\u86c7\u6bdb\u81ec\u53c8\u5fae\u54fc\u4e86\u4e00\u4e00\u58f0\uff0c\u56de\u8eab\u7f13\u6b65\u5411\u90a3\u8427\u6c0f\u7236\u5b50\u8d70\u4e86\u8fc7\u53bb\uff0c\u5de6\u624b\u795e\u5251\u76ee\u5149\u52a8\u3000\u5904\uff0c \u4e5f\u548c\u4ed6\u5e76\u80a9\u8d70\u53bb\u3002 \u3000\u3000\u3000\u94c1\u624b\u4ed9\u733f\u89c1\u4e86\uff0c\u6697\u4e2d\u5411\u51e0\u4e2a\u4eba\u4e00\u6253\u773c\u8272\uff0c\u4e5f\u8ddf\u5728\u6bdb\u81ec\u548c\u4e01\u8863\u7684\u540e\u9762\u8d70\u53bb\uff0c\u7fa4\u8c6a\u3000\u89c1 \u4e86\uff0c\u5fc3\u4e2d\u4e0d\u7981\u53c8\u53d1\u6bdb\uff0c\u77e5\u9053\u8fd9\u4e00\u4e0b\u8427\u6c0f\u7236\u5b50\u66f4\u662f\u51f6\u591a\u5409\u5c11\u4e86\u3002 \u3000\u3000\u3000\u8427\u6c0f\u7236\u5b50\u4e00\u773c\u671b\u89c1\u8fd9\u79cd\u60c5\u5f62\uff0c\u5fc3\u91cc\u4f55\u5c1d\u4e0d\u77e5\u9053\u81ea\u5df1\u6b64\u523b\u7684\u5904\u5883\uff0c\u4f46\u4ee5\u4ed6\u4eec\u7684\u8eab\u3000\u4efd\uff0c \u6b64\u523b\u53c8\u52bf\u4e0d\u80fd\u5f53\u7740\u7fa4\u8c6a\u4e00\u6e9c\u4e86\u4e4b\u3002 \u3000\u3000\u3000\u706b\u773c\u91d1\u96d5\u54c8\u54c8\u4e00\u7b11\uff0c\u5389\u58f0\u9053\uff1a\u201c\u59d3\u6bdb\u7684\uff0c\u4f60\u8fc7\u6765\u4f5c\u4ec0\uff0c\u96be\u9053\u4f60\u8fd8\u771f\u6562\u5c06\u8001\u592b\u600e\u3000 \u7684\uff1f\u201d \u3000\u3000\u3000\u4ed6\u8fd9\u4e48\u4e00\u8bf4\uff0c\u5374\u89c1\u660e\u663e\u5730\u9732\u51fa\u4e86\u602f\u654c\u4e4b\u610f\u6765\u4e86\uff0c\u5de6\u624b\u795e\u5251\u4e01\u8863\u51b7\u7b11\u4e00\u58f0\uff0c\u505a\u7136\u3000\u9053\uff1a \u201c\u59d3\u8427\u7684\uff0c\u4f60\u7741\u5f00\u773c\u775b\u6765\u770b\u770b\u5427\uff0c\u4eca\u5929\u4f60\u96be\u9053\u8fd8\u60f3\u6d3b\u7740\u8d70\u4e0b\u697c\u53bb\uff0c\u4f60\u96be\u9053\u8fd8\u60f3\u3000\u90a3\u4e2a\u53eb\u91d1\u5251 \u4fa0\u7684\u5c0f\u5b50\u518d\u6765\u6551\u4f60\uff1f\u201d \u3000\u3000\u3000\u91d1\u9ca4\u8427\u5e73\u76ee\u6bd7\u6b32\u88c2\uff0c\u5927\u559d\u9053\uff1a\u201c\u4f60\u4eec\u7adf\u6562\u5f53\u7740\u8fd9\u4e48\u591a\u4eba\u9762\u524d\u4ee5\u4f17\u51cc\u5be1\uff0c\u6b66\u6797\u4e4b\u3000\u4e2d\uff0c \u96be\u9053\u5c31\u6ca1\u6709\u516c\u9053\u4e86\u5417\uff1f\u201d\u4ed6\u5c06\u624b\u671d\u540e\u9762\u7684\u7fa4\u8c6a\u4e71\u62f1\uff0c\u53c8\u9053\uff1a\u201c\u670b\u53cb\u4eec\uff0c\u4f60\u4eec\u53ef\u3000\u8981\u51fa\u6765\u4e3b\u6301 \u516c\u9053\uff0c\u8981\u662f\u5355\u6253\u72ec\u6597\uff0c\u6211\u8427\u5e73\u6b7b\u800c\u65e0\u6028\uff0c\u8981\u662f\u8fd9\u4e48\u7684\u8bdd\uff0c\u6211\u2026\u2026\u6211\u2026\u2026\u3000\u201d\u4ed6\u731b\u4e00\u8dfa\u811a\uff0c\u7adf \u8bf4\u4e0d\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u3000\u7075\u86c7\u6bdb\u81ec\u4ef0\u5929\u4e00\u9635\u72c2\u7b11\uff0c\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u5355\u6253\u72ec\u6597\uff0c\u6b7b\u800c\u65e0\u6028\uff0c\u597d\uff0c\u597d\u2014\u2014\u201d\u3000\u6b66\u6797 \u9b41\u9996\u7adf\u4e00\u9762\u633d\u7740\u8896\u5b50\uff0c\u4e00\u9762\u53c8\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u5c31\u8fc7\u6765\uff0c\u6211\u6bdb\u5927\u592a\u7237\u966a\u4f60\u73a9\u3000\u73a9\uff0c\u4f60\u8981\u662f \u5728\u6211\u624b\u4e0b\u8d70\u5f97\u7740\u4e94\u5341\u62db\u53bb\uff0c\u6211\u59d3\u6bdb\u7684\u5c31\u606d\u9001\u4f60\u4eec\u4e0b\u697c\uff0c\u8fd9\u8be5\u7b97\u516c\u9053\u4e86\u5427\u3000\uff01\u201d \u3000\u3000\u3000\u706b\u773c\u91d1\u96d5\u5927\u559d\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u59d3\u6bdb\u7684\uff0c\u4f60\u627e\u540e\u751f\u5c0f\u8f88\u53eb\u4ec0\u4e48\u9635\uff0c\u4f60\u82e5\u771f\u662f\u4e2a\u4eba\u7269\u3000\u7684 \u8bdd\uff0c\u4e00\u6708\u4e4b\u540e\uff0c\u4f60\u6211\u53ef\u53e6\u7ea6\u65f6\u5730\uff0c\u4e00\u51b3\u96cc\u96c4\uff0c\u6b64\u523b\u4f60\u85c9\u7740\u8be1\u8ba1\uff0c\u5c06\u6211\u7236\u5b50\u9a97\u6765\u8fd9\u3000\u91cc\uff0c\u6b64\u523b \u5374\u53c8\u4ee5\u4f17\u51cc\u5be1\uff0c\u4ee5\u5f3a\u51cc\u5f31\uff0c\u6bdb\u81ec\u5440\u6bdb\u81ec\uff0c\u4f60\u96be\u9053\u4e0d\u6015\u5929\u7406\u5faa\u73af\uff0c\u4f60\u96be\u9053\u4e0d\u3000\u6015\u906d\u62a5\u5417\uff1f\u201d \u3000\u3000\u3000\u7075\u86c7\u6bdb\u81ec\u9762\u542b\u72de\u7b11\uff0c\u68ee\u51b7\u4ed6\u8bf4\u9053\uff1a\u201c\u8427\u8001\u5934\u5b50\uff0c\u4f60\u867d\u7136\u820c\u707f\u83b2\u82b1\uff0c\u4e5f\u65e0\u6cd5\u81ea\u6c42\u3000\u751f\u8def \u4e86\uff0c\u4f60\u8981\u60f3\u5728\u6211\u6bdb\u67d0\u4eba\u9762\u524d\u8bb2\u4ec0\u4e48\u516c\u9053\uff0c\u90a3\u4e48\u6211\u544a\u8bc9\u4f60\uff0c\u6211\u6bdb\u67d0\u4eba\u5c31\u662f\u516c\u9053\u3002\u3000\u201d \u3000\u3000\u3000\u706b\u773c\u91d1\u96d5\u4e00\u54ac\u7259\uff0c\u6115\u7136\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u6211\u8001\u5934\u5b50\u82e5\u80fd\u85c9\u7740\u4e00\u6b7b\uff0c\u8ba9\u5929\u4e0b\u6b66\u6797\u670b\u3000\u53cb\u8ba4 \u6e05\u4f60\u8fd9\u4e2a\u5047\u5192\u4e3a\u5584\u7684\u6076\u5f92\u7684\u771f\u9762\u76ee\uff0c\u90a3\u4e48\u6211\u8001\u5934\u5b50\u6b7b\u590d\u4f55\u61be\u3002\u201d \u3000\u3000\u3000\u8fd9\u987b\u53d1\u5df2\u8fd1\u5168\u767d\u7684\u8001\u4eba\uff0c\u6b64\u523b\u58f0\u97f3\u60b2\u67aa\uff0c\u957f\u987b\u5fae\u98a4\uff0c\u4e00\u5206\u638c\u4e2d\u7684\u5ce8\u5d4b\u523a\uff0c\u63a5\u7740\u3000\u5389\u58f0 \u559d\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u4eec\u5c31\u7d22\u6027\u5168\u4e0a\u6765\uff0c\u8001\u592b\u4eca\u65e5\u5c31\u548c\u4f60\u4eec\u8fd9\u5e2e\u6076\u5f92\u62fc\u4e86\uff01\u201d \u3000\u3000\u3000\u5de6\u624b\u795e\u5251\u8fde\u58f0\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u6559\u8bad\u4f60\u8fd9\u79cd\u7cdf\u8001\u5934\u5b50\uff0c\u8fd8\u7528\u5f97\u7740\u522b\u4eba\u52a8\u624b\u5417\uff1f\u201d\u5065\u3000\u8155\u4e00 \u7ffb\uff0c\u5251\u5c16\u4e0a\u5f15\uff0c\u6b63\u5f85\u51fa\u624b\uff0c\u8fd9\u65f6\u8e6c\uff0c\u8e6c\uff0c\u8e6c\uff0c\u697c\u68af\u53e3\u7a81\u7136\u4e00\u9635\u66b4\u54cd\uff0c\u6025\u901f\u5730\u5954\u3000\u4e0a\u4e24\u4e2a\u4eba \u6765\uff0c\u4e00\u4ed8\u6c14\u6025\u8d25\u574f\u7684\u6837\u5b50\uff0c\u751f\u50cf\u662f\u8d76\u6765\u5954\u4e27\u4f3c\u7684\u3002 \u3000\u3000\u3000\u8fd9\u4e24\u4eba\u5168\u90fd\u7a7f\u7740\u91d1\u8272\u7684\u957f\u886b\uff0c\u4f46\u662f\u60f3\u5fc5\u56e0\u4e3a\u7ecf\u8fc7\u957f\u9014\u5954\u6ce2\uff0c\u6b64\u523b\u8fd9\u4e24\u4ef6\u91d1\u8272\u957f\u3000\u886b\u4e0a \u5df2\u88ab\u7070\u7802\u6c57\u6e0d\u6e32\u67d3\u5f97\u53d8\u4e3a\u571f\u9ec4\u8272\u4e86\uff01 \u3000\u3000\u3000\u800c\u4e14\u8fd9\u4e24\u4eba\u867d\u7136\u9762\u76ee\u82f1\u4fca\uff0c\u4f46\u9762\u4e0a\u4ea6\u662f\u98ce\u5c18\u6ee1\u9762\uff0c\u773c\u4e2d\u66f4\u662f\u9eef\u6de1\u65e0\u5149\uff0c\u50cf\u662f\u591a\u3000\u65e5\u672a \u7761\uff0c\u5fc3\u795e\u4ea4\u7601\u7684\u6837\u5b50\uff0c\u770b\u4e0a\u53bb\u4ff1\u90fd\u72fc\u72c8\u4e0d\u582a\u3002 \u3000\u3000\u3000\u8fd9\u4e24\u4eba\u4e00\u4e0a\u697c\uff0c\u76ee\u5149\u56db\u8f6c\uff0c\u4e00\u773c\u77a5\u89c1\u6bdb\u81ec\uff0c\u5fd9\u5730\u62a2\u4e0a\u51e0\u6b65\uff0c\u201c\u5657\u201d\u5730\u671d\u6bdb\u81ec\u8dea\u3000\u4e86\u4e0b \u6765\uff0c\u6bdb\u81ec\u9762\u8272\u5df2\u4e3a\u4e4b\u5927\u53d8\uff0c\u8fde\u58f0\u9053\uff1a\u201c\u4e1c\u5c71\u3001\u5141\u6cf0\uff0c\u4f60\u4eec\u5feb\u8d77\u6765\uff0c\u8fd9\u662f\u600e\u4e48\u56de\u3000\u4e8b\uff0c\u8ba1\u4e8c\u53d4 \u5462\uff1f\u5357\u677e\u5462\uff1f\u5509\u4e00\u4e00\u4f60\u4eec\u8dea\u5728\u8fd9\u91cc\u5e72\u4ec0\u4e48\uff0c\u5feb\u8d77\u6765\u8bf4\u8bdd\u5440\uff01\u201d \u3000\u3000\u3000\u8fd9\u4e00\u5411\u673a\u667a\u6df1\u6c89\u7684\u7075\u86c7\u6bdb\u81ec\uff0c\u6b64\u523b\u4e0d\u4f46\u8bed\u58f0\u60ca\u60f6\uff0c\u9762\u8272\u4e5f\u53d8\u5f97\u94c1\u9752\uff0c\u4e00\u53e0\u8fde\u58f0\u3000\u5730\u50ac \u4fc3\u7740\uff0c\u4f46\u662f\u8fd9\u4e24\u4e2a\u91d1\u886b\u5c11\u5e74\uff0c\u5374\u4e0d\u4f4f\u5730\u5598\u7740\u6c14\u3002 \u3000\u3000\u3000\u516b\u9762\u73b2\u73d1\u4e5f\u662f\u5fae\u53d8\u795e\u8272\uff0c\u8d70\u5230\u8fdc\u8fdc\u4e00\u5f20\u8fd8\u6ca1\u6709\u503e\u5012\u7684\u684c\u65c1\uff0c\u5012\u4e86\u4e24\u676f\u9152\uff0c\u9012\u5230\u3000\u8fd9\u4e24 \u4eba\u9762\u524d\u9053\uff1a\u201c\u6765\uff0c\u4f60\u4eec\u5148\u559d\u676f\u9152\uff0c\u5598\u53e3\u6c14\u3002\u201d\u53c8\u8f6c\u9996\u5411\u6bdb\u81ec\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u522b\u3000\u6025\uff0c\u8ba1\u4e8c\u54e5 \u4e0d\u4f1a\u51fa\u4ec0\u4e48\u4e8b\u7684\u3002\u201d\u5176\u5b9e\u4ed6\u53e3\u4e2d\u867d\u5982\u6b64\u8bf4\uff0c\u5fc3\u91cc\u5374\u4e5f\u6709\u4e9b\u53d1\u614c\uff0c\u4e0d\u77e5\u9053\u3000\u53c8\u51fa\u4e86\u4ec0\u4e48\u53d8\u6545\uff1f \u3000\u3000\u3000\u7f2a\u6587\u4f3c\u4e4e\u6ca1\u6709\u5174\u8da3\u518d\u770b\u8fd9\u5c40\u620f\uff0c\u957f\u957f\u6253\u4e86\u4e2a\u5475\u6b20\uff0c\u4f0f\u5728\u684c\u4e0a\u5047\u5bd0\uff0c\u6bdb\u6587\u742a\u5728\u4ed6\u3000\u65c1\u8fb9 \u8f7b\u58f0\u9053\uff1a\u201c\u4f60\u597d\u751f\u4f11\u606f\u4e00\u4f1a\uff0c\u7b49\u4e00\u4e0b\u6211\u4eec\u8981\u8d70\u7684\u65f6\u5019\uff0c\u6211\u518d\u53eb\u9192\u4f60\u3002\u201d\u7f2a\u6587\u5934\u3000\u4f0f\u5728\u684c\u5b50 \u4e0a\uff0c\u52a8\u4e5f\u4e0d\u52a8\uff0c\u4eff\u4f5b\u662f\u5df2\u7ecf\u7761\u7740\u4e86\u7684\u6837\u5b50\u3002 \u3000\u3000\u3000\u6b64\u523b\u8fd9\u4e24\u4e2a\u91d1\u886b\u5c11\u5e74\u5df2\u4ef0\u9996\u559d\u4e86\u9152\uff0c\u6b63\u5f85\u8bf4\u8bdd\uff0c\u6bdb\u684c\u5374\u5fae\u4e00\u76b1\u7709\uff0c\u671d\u4faf\u6797\u9053\uff1a\u3000\u201c\u8001 \u56db\uff0c\u4f60\u771f\u662f\u7684\uff0c\u5c06\u8fd9\u4e48\u591a\u597d\u670b\u53cb\u56f0\u5728\u8fd9\u91cc\u59d4\u5c48\u4e86\u4e00\u901a\u591c\uff0c\u73b0\u5728\u8fd8\u4e0d\u7094\u9001\u4eba\u5bb6\u53bb\u3000\u6b47\u606f\u53bb\u3002\u201d \u4e00\u9762\u53c8\u5fae\u5fae\u62f1\u624b\u9053\uff1a\u201c\u5404\u4f4d\u670b\u53cb\u8bf7\u4e86\uff0c\u4eca\u65e5\u6bdb\u81ec\u62db\u5f85\u4e0d\u5468\u4e4b\u7f6a\uff0c\u6539\u65e5\u518d\u3000\u5411\u5404\u4f4d\u8c22\u8fc7\u3002\u201d \u3000\u3000\u3000\u7fa4\u4f17\u90fd\u77e5\u9053\u8fd9\u662f\u4ed6\u5728\u4e0b\u7740\u9010\u5ba2\u4e4b\u4ee4\uff0c\u76f8\u987e\u4e4b\u4e0b\uff0c\u4e5f\u5c31\u90fd\u5411\u6bdb\u81ec\u8bf4\u7740\u5ba2\u6c14\u8bdd\uff0c\u4e00\u3000\u4e00\u4e0b \u4e86\u697c\uff0c\u8fd9\u4e9b\u4eba\u90fd\u662f\u5149\u68cd\u670b\u53cb\uff0c\u8c01\u4e5f\u4e0d\u613f\u610f\u8d9f\u8fd9\u8d9f\u6d51\u6c34\u3002 \u3000\u3000\u3000\u5de6\u624b\u795e\u5251\u6a2a\u8eab\u4e00\u62e6\uff0c\u62e6\u5728\u8427\u6c0f\u7236\u5b50\u9762\u524d\uff0c\u5e73\u5251\u5f53\u80f8\uff0c\u51b7\u7136\u8bf4\u9053\uff1a\u201c\u59d3\u8427\u7684\uff0c\u4f60\u3000\u53ef\u8fd8 \u6ca1\u6709\u5230\u8d70\u7684\u65f6\u5019\uff01\u201d \u3000\u3000\u3000\u8427\u8001\u96d5\u5389\u58f0\u7b11\u9053\uff1a\u201c\u4f60\u8981\u6211\u8d70\u6211\u8fd8\u4e0d\u5b9a\u54e9\uff0c\u6211\u8981\u542c\u542c\u4f60\u4eec\u683d\u8ddf\u5934\u7684\u4e8b\u3002\u201d\u4ed6\u5c06\u3000\u8fd9\u8bdd \u8bf4\u5f97\u7279\u522b\u54cd\uff0c\u4ee5\u671f\u7fa4\u8c6a\u90fd\u80fd\u542c\u89c1\u3002\u5176\u5b9e\u4ed6\u4e0d\u8bf4\u522b\u4eba\u5fc3\u91cc\u4e5f\u6709\u6570\uff0c\u77e5\u9053\u90a3\u4e58\u9699\u524d\u3000\u5f80\u9ad8\u3001\u6d2a\u53d6 \u5b9d\u7684\u94c1\u7b97\u5b50\u8ba1\u8c0b\uff0c\u5df2\u683d\u4e86\u5927\u5927\u7684\u8ddf\u5934\uff0c\u751a\u81f3\u6027\u547d\u90fd\u5df2\u4e0d\u4fdd\uff0c\u53ea\u662f\u5927\u5bb6\u90fd\u3000\u88c5\u7cca\u6d82\uff0c\u4e0d\u613f\u610f\u8bf4 \u51fa\u6765\u7f62\u4e86\u3002 \u3000\u3000\u3000\u8fd9\u4e24\u4e2a\u91d1\u886b\u5c11\u5e74\u5374\u6b63\u662f\u7075\u86c7\u6bdb\u81ec\u95e8\u4e0b\u5341\u5927\u5f1f\u5b50\u4e2d\u7684\u8ffd\u4e91\u4f7f\u8005\u5c09\u8fdf\u4e1c\u5c71\u3001\u795e\u5251\u4f7f\u3000\u8005\u6885 \u5141\u6cf0\uff0c\u4e5f\u6b63\u662f\u548c\u94c1\u7b97\u5b50\u8ba1\u8c0b\u540c\u53bb\u53d6\u5b9d\u4e4b\u4eba\u3002 \u3000\u3000\u3000\u662f\u4ee5\u7075\u86c7\u6bdb\u81ec\u4e00\u89c1\u8fd9\u4e24\u4eba\u72fc\u72c8\u5f52\u6765\uff0c\u5fc3\u4e2d\u81ea\u7136\u5927\u60ca\uff0c\u8fde\u706b\u773c\u91d1\u96d5\u90a3\u79cd\u8ba5\u8bbd\u7684\u8bdd\u3000\u4e5f\u987e \u4e0d\u5f97\u4e86\uff0c\u7b49\u5230\u7fa4\u8c6a\u4e00\u4e0b\u697c\uff0c\u53c8\u6025\u5207\u5730\u95ee\u9053\uff1a\u201c\u4f60\u8ba1\u4e8c\u53d4\u51fa\u4e86\u4ec0\u4e48\u4e8b\uff1f\u6211\u4ea4\u5f85\u4f60\u3000\u4eec\u7684\u4e8b\u505a\u4e86 \u6ca1\u6709\uff1f\u5feb\u8bf4\u5440\uff01\u201d \u3000\u3000\u3000\u795e\u5251\u4f7f\u8005\u559d\u4e86\u676f\u9152\uff0c\u5b9a\u4e86\u5b9a\u795e\uff0c\u624d\u7ad9\u8d77\u6765\uff0c\u6025\u6025\u7b54\u9053\uff1a\u201c\u5f1f\u5b50\u4eec\u548c\u8ba1\u4e8c\u53d4\u5230\u4e86\u3000\u6d2a\u6cfd \u6e56\u548c\u9ad8\u90ae\u6e56\u4e4b\u95f4\u7684\u6c34\u95f8\u90a3\u91cc\uff0c\u5c31\u6309\u7740\u56fe\u4e0a\u6240\u793a\u7684\u5730\u65b9\u5f00\u59cb\u5bfb\u627e\uff0c\u8fd9\u91cc\u9762\u5f53\u7136\u662f\u3000\u5c09\u8fdf\u5e08\u5144\u4e24 \u5f1f\u5144\u7684\u6c34\u6027\u6700\u9ad8\uff0c\u8ba1\u4e8c\u53d4\u5c31\u53eb\u4ed6\u4eec\u6362\u4e86\u6c34\u9760\uff0c\u4e0b\u6c34\u641c\u5bfb\u3002\u201d \u3000\u3000\u3000\u7075\u86c7\u6bdb\u81ec\u76ee\u5149\u8f6c\u5230\u53e6\u4e00\u4eba\u2014\u2014\u5c09\u8fdf\u4e1c\u5c71\u9762\u4e0a\uff0c\u8fdf\u4e1c\u5c71\u53f9\u4e86\u53e3\u6c14\uff0c\u60b2\u6ca7\u4ed6\u8bf4\u9053\uff1a\u3000\u201c\u5f1f \u5b50\u548c\u5357\u677e\u5f1f\u4e0b\u4e86\u6c34\uff0c\u679c\u7136\u770b\u5230\u5728\u65c1\u8fb9\u6e56\u5e95\u9760\u8fd1\u6e56\u5cb8\u7684\u5730\u65b9\uff0c\u6709\u56fe\u4e0a\u6240\u793a\u7684\u8bb0\u53f7\u3000\uff0c\u5f53\u7136\u9ad8\u5174 \u5f97\u5f88\uff0c\u5230\u6c34\u9762\u6362\u4e86\u53e3\u6c14\u4ee5\u540e\uff0c\u5c31\u5faa\u7740\u90a3\u8bb0\u53f7\u6240\u793a\u7684\u65b9\u5411\uff0c\u53c8\u627e\u7740\u4e00\u6761\u6c89\u3000\u8239\uff0c\u5f1f\u5b50\u4eec\u5c31\u7528\u7ef3 \u5b50\u6346\u5728\u8239\u4e0a\uff0c\u548c\u5728\u5cb8\u4e0a\u7684\u8ba1\u4e8c\u53d4\u548c\u6885\u5e08\u5f1f\u4ed6\u4eec\u4e00\u9f50\u7528\u529b\uff0c\u5c06\u90a3\u8258\u3000\u6c89\u8239\u79fb\u5f00\uff0c\u679c\u7136\u770b\u5230\u6c89\u8239 \u4e0b\u9762\u6709\u4e00\u5757\u751f\u6ee1\u4e86\u9508\u7684\u94c1\u677f\u3002\u201d \u3000\u3000\u3000\u8fd9\u65f6\u4e0d\u4f46\u7075\u86c7\u6bdb\u81ec\u5168\u795e\u51dd\u4f4f\u5728\u8fd9\u8ffd\u4e91\u4f7f\u8005\u7684\u6d3b\u4e0a\uff0c\u5176\u4f59\u7684\u4eba\uff0c\u4e5f\u90fd\u7741\u5927\u4e86\u773c\u775b\u3000\u671b\u7740 \u4ed6\uff0c\u5173\u5207\u4e4b\u5bb9\uff0c\u6ea2\u4e8e\u8a00\u8868\u3002 \u3000\u3000\u3000\u90a3\u706b\u773c\u91d1\u96d5\u5374\u4f4e\u9a82\u4e86\u4e00\u53e5\uff1a\u201c\u96be\u9053\u6e56\u4e0a\u4f0f\u6869\u7684\u72d7\u624d\u90fd\u6b7b\u5149\u4e86\u4e0d\u6210\uff1f\u201d \u3000\u3000\u3000\u5c09\u8fdf\u4e1c\u5c71\u671b\u4e86\u4ed6\u4e00\u773c\uff0c\u63a5\u7740\u8bf4\u4e0b\u53bb\u9053\uff1a\u201c\u5f1f\u5b50\u4eec\u4e00\u89c1\u94c1\u677f\uff0c\u5f53\u7136\u9ad8\u5174\u5f97\u5f88\uff0c\u4e00\u3000\u9762\u4e0a \u53bb\u6362\u6c14\uff0c\u4e00\u9762\u5c31\u5c06\u5b83\u544a\u8bc9\u4e86\u8ba1\u4e8c\u53d4\uff0c\u54ea\u60f3\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u7a81\u7136\u6709\u5f29\u7bad\u671d\u6211\u4eec\u5c04\u6765\u3000\uff0c\u5f1f\u5b50\u4eec\u5c31 \u77e5\u9053\u8eab\u5f62\u5df2\u88ab\u9ad8\u3001\u6d2a\u6c34\u5be8\u4f0f\u6869\u7684\u5f1f\u5144\u770b\u5230\u4e86\uff01\u201d \u3000\u3000\u3000\u8427\u8001\u96d5\u54fc\u4e86\u4e00\u58f0\uff0c\u5c09\u8fdf\u4e1c\u5c71\u53c8\u671b\u4e86\u4ed6\u4e00\u773c\uff0c\u51b7\u51b7\u63a5\u53e3\u9053\uff1a\u201c\u54ea\u77e5\u9ad8\u3001\u6d2a\u6c34\u5be8\u91cc\u3000\u8fd9\u6279 \u5bb6\u4f19\u5374\u65e0\u7528\u5f97\u5f88\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u5c31\u90fd\u88ab\u5236\u4f4f\u4e86\u3002\u201d\u4ed6\u773c\u89d2\u4e00\u779f\uff0c\u6c14\u5f97\u8427\u8fdf\u7684\u9762\u76ee\u3000\u53c8\u8fde\u8fde\u53d8 \u8272\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u5f1f\u5b50\u4eec\u8fd9\u624d\u53c8\u6f5c\u4e0b\u6c34\u53bb\uff0c\u79fb\u5f00\u94c1\u677f\uff0c\u94c1\u677f\u4e0b\u9762\u7684\u4e00\u4e2a\u5927\u3000\u5730\u7a96\u91cc\uff0c\u679c\u7136\u6709 \u5341\u597d\u51e0\u53e3\u7bb1\u5b50\u4e00\u4e00\u201d\u4ed6\u7565\u4e3a\u4e00\u987f\uff0c\u6bdb\u81ec\u5df2\u7740\u6025\u5730\u50ac\u4fc3\u9053\uff1a\u201c\u5feb\u8bf4\u4e0b\u3000\u53bb\uff01\u201d \u3000\u3000\u3000\u201c\u5f1f\u5b50\u4eec\u9ad8\u5174\u5f97\u4e0d\u77e5\u600e\u4e48\u597d\u4e86\uff0c\u5c31\u5c06\u8fd9\u4e9b\u7bb1\u5b50\uff0c\u90fd\u540a\u5230\u5cb8\u4e0a\uff0c\u5357\u677e\u5f1f\u5c31\u8981\u5148\u6253\u3000\u5f00\u4e00 \u53e3\u7bb1\u5b50\u770b\u770b\uff0c\u91cc\u9762\u5230\u5e95\u662f\u4ec0\u4e48\u4e1c\u897f\u3002\u201d \u3000\u3000\u3000\u5c09\u8fdf\u4e1c\u5c71\u8bf4\u5230\u8fd9\u91cc\uff0c\u7075\u86c7\u6bdb\u81ec\u5c31\u51b7\u54fc\u4e00\u58f0\uff0c\u50cf\u662f\u5bf9\u6b64\u4e3e\u6df1\u8868\u4e0d\u6ee1\u3002 \u3000\u3000\u3000\u5c09\u8fdf\u4e1c\u5c71\u5598\u4e86\u4e24\u53e3\u6c14\uff0c\u60b2\u6ca7\u4e4b\u8272\uff0c\u7a81\u53c8\u6d8c\u73b0\uff0c\u6885\u5141\u6cf0\u5c31\u63a5\u7740\u9053\uff1a\u201c\u8ba1\u4e8c\u53d4\u60f3\u4e86\u3000\u60f3\uff0c \u5c31\u7b54\u5e94\u4e86\uff0c\u8fd9\u4e9b\u7bb1\u5b50\u4e0a\u9762\u90fd\u751f\u6ee1\u4e86\u94c1\u9508\uff0c\u5c09\u8fdf\u4e8c\u54e5\u6273\u4e86\u51e0\u6273\uff0c\u624d\u628a\u7bb1\u5b50\u6273\u5f00\u3002\u3000\u54ea\u77e5\u7bb1\u5b50\u521a \u5f00\uff0c\u91cc\u9762\u7adf\u7136\u5c04\u51fa\u4e00\u84ec\u5c0f\u7bad\u6765\uff0c\u5c09\u8fdf\u4e8c\u54e5\u788e\u4e0d\u53ca\u9632\uff0c\u8eab\u4e0a\u7adf\u4e2d\u4e86\u4e03\u7bad\uff0c\u3000\u76f4\u900f\u5165\u9aa8\uff0c\u8fde\u8bdd\u90fd \u6765\u4e0d\u53ca\u8bf4\uff0c\u5c31\u2026\u2026\u5c31\u54bd\u4e86\u6c14\u4e86\u3002\u201d \u3000\u3000\u3000\u4f17\u4eba\u4e0d\u7981\u53c8\u90fd\u5012\u62bd\u4e00\u53e3\u51c9\u6c14\uff0c\u5c09\u8fdf\u4e1c\u5c71\u4fef\u9996\u4e0d\u8bed\uff0c\u6885\u5141\u6cf0\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u63a5\u7740\u8bf4\u3000\u9053\uff1a \u201c\u54ea\u77e5\u7bb1\u5b50\u4e00\u6253\u5f00\uff0c\u91cc\u9762\u88c5\u7684\u5374\u662f\u4e00\u5927\u5806\u70c2\u77f3\u5934\uff0c\u5f1f\u5b50\u4eec\u53c8\u60b2\u75db\uff0c\u53c8\u5403\u60ca\uff0c\u53c8\u3000\u6c14\u6124\u3002\u8ba1\u4e8c \u53d4\u7528\u4e24\u53e3\u5251\u5c06\u8fd9\u5341\u51e0\u53e3\u7bb1\u5b50\u90fd\u6253\u5f00\u4e86\uff0c\u6bcf\u53e3\u7bb1\u5b50\u91cc\u90fd\u88c5\u7740\u6697\u5f29\uff0c\u800c\u6bcf\u53e3\u3000\u7bb1\u5b50\u91cc\u88c5\u7684\u7adf\u7136\u90fd \u662f\u70c2\u77f3\u5934\u3002\u201d \u3000\u3000\u3000\u8bf4\u5230\u8fd9\u4e9b\uff0c\u7075\u86c7\u81ec\u7b49\u4eba\u66f4\u662f\u9762\u8272\u60e8\u53d8\uff0c\u90a3\u706b\u773c\u91d1\u96d5\u5374\u7eb5\u58f0\u72c2\u7b11\u8d77\u6765\uff0c\u4f46\u8fd9\u65f6\u5404\u3000\u4eba\u5fc3 \u4e2d\u60ca\u6050\u3001\u5931\u671b\uff0c\u7d0a\u4e71\u5982\u9ebb\uff0c\u7adf\u90fd\u6ca1\u6709\u5bf9\u8fd9\u79cd\u6076\u610f\u7684\u7b11\u58f0\u5982\u4f55\uff0c\u5374\u542c\u795e\u5251\u4f7f\u8005\u6885\u3000\u5141\u6cf0\u63a5\u9053\uff1a \u201c\u8fd9\u4e00\u6765\u5f1f\u5b50\u4eec\u4ff1\u90fd\u5927\u60ca\u5931\u8272\uff0c\u8ba1\u4e8c\u53d4\u5c06\u90a3\u4e9b\u7bb1\u5b50\u91cc\u88c5\u7740\u7684\u6d88\u606f\u5f29\u7bad\u4ed4\u3000\u7ec6\u5730\u67e5\u770b\u4e86\u4e00\u904d\uff0c \u9762\u8272\u7a81\u7136\u53d8\u5f97\u66f4\u52a0\u96be\u770b\uff0c\u8fde\u8fde\u5730\u53f9\u7740\u6c14\uff0c\u544a\u8bc9\u5f1f\u5b50\u4eec\u8bf4\uff0c\u8fd9\u4e9b\u5f29\u3000\u7bad\u5b89\u88c5\u7684\u65b9\u6cd5\uff0c\u7adf\u7136\u548c\u6570 \u5341\u5e74\u524d\u540d\u9707\u6b66\u6797\u7684\u524d\u8f88\u5f02\u4eba\u5723\u624b\u4e66\u751f\u6df3\u4e8e\u72ec\u79c0\u540c\u51fa\u4e00\u8f99\uff01\u3000\u201d \u3000\u3000\u3000\u201c\u5723\u624b\u4e66\u751f\u201d\u56db\u5b57\u4e00\u51fa\uff0c\u4f17\u4eba\u66f4\u52a0\u5927\u60ca\uff0c\u539f\u6765\u8fd9\u5723\u624b\u4e66\u751f\u6df3\u4e8e\u72ec\u79c0\u4e0d\u4f46\u6b66\u529f\u5353\u3000\u7acb\uff0c \u5c24\u7cbe\u6d88\u606f\u57cb\u4f0f\uff0c\u53ea\u662f\u6b64\u4eba\u591a\u5e74\u524d\u5df2\u5931\u8e2a\u8ff9\uff0c\u4e5f\u672a\u66fe\u542c\u8fc7\u4ed6\u4f20\u6709\u5f1f\u5b50\uff0c\u4f17\u4eba\u867d\u60ca\u3000\u7591\uff0c\u4f46\u5374\u77e5 \u9053\u94c1\u7b97\u5b50\u8ba1\u8c0b\u4e5f\u662f\u4e2a\u4e2d\u8001\u624b\uff0c\u773c\u5149\u7edd\u5bf9\u4e0d\u4f1a\u770b\u9519\u3002 \u3000\u3000\u3000\u7075\u86c7\u6bdb\u81ec\u4e00\u8dfa\u811a\uff0c\u6068\u58f0\u9053\uff1a\u201c\u8fd9\u8001\u4e0d\u6b7b\u600e\u5730\u53c8\u91cd\u73b0\u6c5f\u6e56\u4e86\uff1f\u5141\u6cf0\uff0c\u4f60\u5feb\u8bf4\u4e0b\u53bb\u3000\uff01\u201d \u3000\u3000\u3000\u4e16\u4e0a\u6709\u8bb8\u591a\u4e8b\u770b\u6765\u6beb\u65e0\u8fde\u8d2f\uff0c\u53c8\u8fd1\u4e0d\u53ef\u601d\u8bae\uff0c\u5176\u5b9e\u8fd9\u4ec5\u662f\u56e0\u4e3a\u4eba\u4eec\u7684\u611a\u6627\uff0c\u65e0\u3000\u6cd5\u77e5 \u9053\u90a3\u5176\u4e2d\u7a76\u7adf\u7684\u771f\u8c61\u800c\u5df2\u3002 \u3000\u3000\u3000\u6b64\u523b\uff0c\u8fd9\u4e9b\u4eba\u4e3a\u6b64\u4e8b\u5927\u611f\u60ca\u8be7\uff0c\u4f46\u4ed6\u4eec\u82e5\u77e5\u9053\u90a3\u5723\u624b\u4e66\u751f\u5df2\u548c\u6d77\u5929\u5b64\u71d5\u540c\u9690\u4e00\u3000\u5c9b\uff0c \u800c\u6d77\u5929\u5b64\u71d5\u53c8\u6b63\u6709\u4efd\u4e09\u624d\u5b9d\u85cf\u7684\u79d8\u56fe\uff0c\u518d\u5c06\u8fd9\u4e9b\u548c\u53e6\u5916\u7684\u4e00\u4e9b\u4e8b\u7a0d\u52a0\u8fde\u8d2f\uff0c\u90a3\u3000\u8fd9\u4e9b\u795e\u79d8\u7684 \u4e8b\u5c31\u4e0d\u518d\u795e\u79d8\u4e86\u3002 \u3000\u3000\u3000\u6885\u5141\u6cf0\u7565\u7565\u5598\u4e86\u53e3\u6c14\uff0c\u5c31\u63a5\u7740\u9053\uff1a\u201c\u8ba1\u4e8c\u53d4\u53c8\u8bf4\uff0c\u7167\u8fd9\u79cd\u60c5\u51b5\u6765\u770b\uff0c\u8fd9\u6279\u85cf\u5b9d\u3000\u4e00\u5b9a \u5df2\u88ab\u5723\u624b\u4e66\u751f\uff0c\u6216\u8005\u662f\u4ed6\u7684\u5f1f\u5b50\u6377\u8db3\u5148\u5f97\u3002\u5f1f\u5b50\u4eec\u542c\u4e86\uff0c\u53c8\u61ca\u607c\uff0c\u53c8\u6c14\u6124\uff0c\u770b\u3000\u7740\u5c09\u8fdf\u4e8c\u54e5 \u7684\u60e8\u6b7b\uff0c\u53c8\u89c9\u5f97\u96be\u53d7\u3002\u54ea\u77e5\u9053\u7978\u4e0d\u5355\u884c\uff0c\u8ba1\u4e8c\u53d4\u6b63\u5bf9\u6211\u4eec\u8bf4\u7740\u8bdd\uff0c\u5f1f\u5b50\u3000\u4eec\u7adf\u7a81\u7136\u770b\u5230\u4ed6\u8001 \u4eba\u5bb6\u8eab\u540e\u591a\u4e86\u4e00\u6761\u4eba\u5f71\uff01\u201d \u3000\u3000\u3000\u6885\u5141\u6cf0\u8138\u4e0a\u7684\u808c\u8089\u7565\u7565\u626d\u66f2\u4e86\u4e00\u4e0b\uff0c\u50cf\u662f\u6b64\u523b\u8fd8\u5728\u4e3a\u90a3\u65f6\u7684\u666f\u51b5\u800c\u60ca\u60b8\u7740\uff0c\u63a5\u3000\u7740\u53c8 \u9053\uff1a\u201c\u90a3\u65f6\u5019\u5929\u5df2\u7ecf\u9ed1\u4e86\uff0c\u6e56\u5cb8\u8fb9\u98ce\u5439\u8349\u52a8\uff0c\u90a3\u6761\u9ed1\u5f71\u50cf\u9b3c\u4f3c\u7684\uff0c\u7ad9\u5728\u8ba1\u4e8c\u53d4\u3000\u540e\u9762\uff0c\u8ba1\u4e8c \u53d4\u5374\u4ecd\u7136\u8bf4\u7740\u8bdd\uff0c\u4e00\u70b9\u513f\u4e5f\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u3000\u6bdb\u6587\u742a\u4e00\u634f\u81ea\u5df1\u7684\u638c\u5fc3\uff0c\u5df2\u7ecf\u88ab\u51b7\u5413\u6e7f\u900f\u4e86\uff0c\u5979\u5fc3\u4e2d\u52a8\u5904\uff0c\u90a3\u66fe\u7ecf\u548c\u5979\u4ea4\u624b\u7684\u3000\u9ed1\u8863 \u591c\u884c\u4eba\u7684\u5f71\u5b50\u53c8\u5728\u5979\u5fc3\u4e2d\u95ea\u8fc7\u3002 \u3000\u3000\u3000\u4f46\u90a3\u9ed1\u8863\u4eba\u662f\u5426\u5c31\u662f\u7ad9\u5728\u8ba1\u8c0b\u8eab\u540e\u7684\u9ed1\u8863\u4eba\u5462\uff1f\u8fd9\u4e2a\u6bdb\u6587\u742a\u5374\u4e5f\u4e0d\u80fd\u786e\u5b9a\uff01\u5979\u3000\u76ee\u5149 \u4e00\u8f6c\uff0c\u770b\u5230\u6bcf\u4e2a\u8138\u4e0a\u90fd\u6709\u60ca\u614c\u4e4b\u8272\uff0c\u90a3\u6885\u5141\u6cf0\u66f4\u662f\u8fde\u8fde\u4f38\u624b\u62ed\u7740\u51b7\u6c57\uff0c\u5f3a\u81ea\u6309\u3000\u637a\u7740\u8bf4\u9053\uff1a \u201c\u540e\u6765\u8ba1\u4e8c\u53d4\u53d1\u73b0\u5f1f\u5b50\u4eec\u7684\u795e\u8272\uff0c\u624d\u56de\u8fc7\u5934\u53bb\uff0c\u5f1f\u5b50\u4eec\u53ea\u662f\u89c1\u90a3\u9ed1\u8863\u4eba\u3000\u563f\u563f\u4e00\u58f0\u51b7\u7b11\uff0c\u53cc \u624b\u4e00\u626c\uff0c\u638c\u4e2d\u7adf\u53d1\u51fa\u597d\u51e0\u9053\u91d1\u5149\u6765\uff0c\u8fd9\u65f6\u6211\u548c\u5c09\u8fdf\u5927\u54e5\u6b63\u7ad9\u5728\u7bb1\u3000\u5b50\u540e\u9762\uff0c\u8fde\u5fd9\u5f80\u7bb1\u5b50\u540e\u9762 \u4e00\u4f0f\uff0c\u53ef\u662f\u8bf4\u81f3\u6b64\u5904\uff0c\u4ed6\u8fde\u58f0\u97f3\u90fd\u53d8\u5f97\u98a4\u6296\u8d77\u6765\uff0c\u51b7\u6c57\u76f4\u3000\u5192\uff0c\u4ed6\u53c8\u7528\u8896\u5b50\u64e6\u4e86\u4e24\u4e0b\uff0c\u63a5\u7740 \u5f80\u4e0b\u9762\u8bf4\u9053\uff1a\u201c\u53ef\u662f\u7b49\u5230\u5f1f\u5b50\u4eec\u7ad9\u8d77\u6765\u7684\u65f6\u5019\uff0c\u8ddf\u3000\u5f1f\u5b50\u4eec\u4e00\u9f50\u53bb\u7684\u4e94\u4e2a\u795e\u97ad\u961f\u7684\u5f1f\u5144\u4eec\u90fd \u5df2\u60e8\u53eb\u7740\uff0c\u5012\u5728\u5730\u4e0a\uff0c\u6bcf\u4e2a\u4eba\u7684\u80f8\u524d\u90fd\u63d2\u7740\u3000\u4e00\u4ef6\u91d1\u5149\u95ea\u95ea\u7684\u6697\u5668\uff0c\u8ba1\u4e8c\u53d4\u7ad9\u5728\u90a3\u91cc\uff0c\u6643\u4e86 \u4e24\u6643\uff0c\u4e5f\u5012\u5728\u5730\u4e0a\uff0c\u800c\u90a3\u4e2a\u9b3c\u9b45\u4e00\u6837\u3000\u7684\u9ed1\u8863\u4eba\uff0c\u5374\u8d70\u5f97\u4e0d\u77e5\u53bb\u5411\u4e86\u3002\u201d \u3000\u3000\u3000\u201c\u5f1f\u5b50\u548c\u8fdf\u5e08\u54e5\u58ee\u7740\u80c6\u5b50\u4e00\u770b\uff0c\u90a3\u4e9b\u795e\u97ad\u961f\u91cc\u7684\u5f1f\u5144\u80f8\u524d\uff0c\u63d2\u7740\u7684\u7adf\u7136\u90fd\u662f\u67c4\u3000\u91d1\u8272 \u7684\u5c0f\u5251\uff0c\u8ba1\u4e8c\u53d4\u80f8\u524d\u867d\u7136\u6ca1\u6709\u63d2\u7740\u5251\uff0c\u4f46\u662f\u4ed6\u8001\u4eba\u5bb6\u5934\u9876\u5374\u4e2d\u4e86\u4e00\u638c\uff0c\u8fde\u5927\u7075\u3000\u76d6\u90fd\u88ab\u6253\u5f97 \u7c89\u788e\u4e86\u3002\u201d \u3000\u3000\u3000\u201c\u5f1f\u5b50\u4eec\u518d\u4e00\u770b\u5148\u524d\u6321\u5728\u5f1f\u5b50\u4eec\u524d\u9762\u7684\u90a3\u4e24\u53e3\u7bb1\u5b50\uff0c\u7bb1\u5b50\u76d6\u53ec\u535c\u4e48\u539a\u7684\u94c1\u677f\uff0c\u3000\u7adf\u4e5f \u88ab\u6253\u5f97\u6d1e\u7a7f\uff0c\u4e0a\u9762\u7684\u90a3\u4e24\u67c4\u91d1\u5251\uff0c\u7adf\u4ece\u7bb1\u5b50\u76d6\u7684\u4e00\u9762\u7a7f\u5230\u90a3\u4e00\u9762\u53bb\u4e86\uff0c\u8fd9\u79cd\u624b\u3000\u52b2\uff0c\u5f1f\u5b50\u4eec \u522b\u8bf4\u6ca1\u6709\u770b\u5230\uff0c\u5c31\u8fde\u542c\u4e5f\u6ca1\u6709\u542c\u8fc7\uff0c\u8fd9\u9ed1\u8863\u4eba\u53cc\u624b\u7adf\u53d1\u51fa\u5341\u4ef6\u6697\u5668\uff0c\u6bcf\u3000\u4e00\u4ef6\u90fd\u6709\u7740\u5982\u6b64\u529b \u9053\uff0c\u8fd9\u2026\u2026\u8fd9\u7b80\u76f4\u2026\u2026\u8fd9\u7b80\u76f4\u6709\u4e9b\u9a87\u4eba\u4e86\uff01\u201d \u3000\u3000\u3000\u4ed6\u4e00\u53e3\u6c14\u8bf4\u5230\u8fd9\u91cc\uff0c\u7adf\u53c8\u201c\u5657\u201d\u5730\u5750\u5230\u5730\u4e0a\uff0c\u5730\u4e0a\u72fc\u85c9\u7684\u9152\u6c41\u83dc\u6c64\uff0c\u5f04\u5f97\u4ed6\u672c\u3000\u5df2\u6c61 \u79fd\u7684\u957f\u886b\u66f4\u52a0\u6dcb\u6f13\u4e0d\u582a\uff0c\u4ed6\u5374\u50cf\u662f\u4e1d\u6beb\u90fd\u6ca1\u6709\u611f\u89c9\u5230\u3002 \u7b2c\u5341\u4e00\u7ae0>> \u53e4\u9f99\u300a\u6e58\u5983\u5251\u300b \u7b2c\u5341\u4e00\u7ae0 \u3000\u3000\u3000\u8fd9\u795e\u5251\u4f7f\u8005\u6885\u5141\u6cf0\u6c14\u6025\u8d25\u574f\u5730\u5c06\u4ed6\u6240\u906d\u5230\u7684\u4e8b\u6e90\u6e90\u672c\u672c\u8bf4\u51fa\u4ee5\u540e\uff0c\u50cf\u662f\u7cbe\u795e\u518d\u3000\u4e5f\u652f \u6301\u4e0d\u4f4f\uff0c\u7adf\u762b\u8f6f\u5728\u5730\u4e0a\u3002 \u3000\u3000\u3000\u51e1\u542c\u5230\u4ed6\u8bf4\u8fd9\u4e9b\u8bdd\u7684\u4eba\uff0c\u6b64\u523b\u4e5f\u4e0d\u7981\u89c9\u5f97\u56db\u80a2\u8f6f\u8f6f\u7684\uff0c\u751f\u50cf\u662f\u4e5f\u6709\u4e9b\u652f\u6301\u4e0d\u4f4f\u3000\u7684\u6837 \u5b50\u3002 \u3000\u3000\u3000\u53ea\u6709\u7075\u86c7\u6bdb\u684c\uff0c\u9762\u76ee\u867d\u4e5f\u53d8\u8272\uff0c\u4f46\u8eab\u8eaf\u4ecd\u633a\u5f97\u7b14\u76f4\uff0c\u5ffd\u5730\u5c06\u624b\u4e0a\u7684\u91d1\u5251\u5f80\u5730\u4e0a\u3000\u4e00 \u629b\uff0c\u629b\u5728\u795e\u5251\u4f7f\u8005\u6885\u5141\u6cf0\u7684\u9762\u524d\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4f60\u4eec\u5728\u6e56\u7554\u6240\u89c1\u7684\u91d1\u5251\uff0c\u662f\u5426\u548c\u8fd9\u3000\u4e00\u6837\uff1f\u201d \u4ed6\u53cc\u76ee\u4e00\u5f20\uff0c\u77aa\u5728\u6885\u5141\u6cf0\u8138\u4e0a\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8d56\u5728\u5730\u4e0a\u5e72\u4ec0\u4e48\uff0c\u8fd8\u4e0d\u5feb\u7ed9\u6211\u3000\u7ad9\u8d77\u6765\uff0c\u54fc\uff01\u60f3 \u4e0d\u5230\u4f60\u4eec\u4e00\u9047\u7740\u4e8b\uff0c\u4e5f\u662f\u5982\u6b64\u5e9f\u6599\u3002\u201d \u3000\u3000\u3000\u6885\u5141\u6cf0\u88ab\u9a82\u5f97\u8138\u4e0a\u9752\u4e00\u9635\uff0c\u767d\u4e00\u9635\uff0c\u62fe\u8d77\u90a3\u67c4\u91d1\u5251\uff0c\u53c8\u4ece\u6000\u91cc\u638f\u51fa\u4e00\u67c4\u91d1\u5251\uff0c\u3000\u4e24\u4e0b \u5bf9\u7167\u7740\u6bd4\u4e86\u6bd4\uff0c\u5c31\u4e00\u9f50\u53cc\u624b\u4ea4\u7ed9\u6bdb\u81ec\uff0c\u4e00\u9762\u9053\uff1a\u201c\u8fd9\u67c4\u91d1\u5251\u548c\u5f1f\u5b50\u4eec\u4ece\u795e\u97ad\u961f\u3000\u5f1f\u5b50\u80f8\u524d\u62d4 \u51fa\u7684\uff0c\u5b8c\u5168\u4e00\u6837\u3002\u201d \u3000\u3000\u3000\u7075\u86c7\u6bdb\u81ec\u54fc\u4e86\u4e00\u58f0\uff0c\u5c06\u4e24\u67c4\u5251\u90fd\u63a5\u4e86\u8fc7\u6765\uff0c\u7565\u7565\u4e00\u77a5\uff0c\u4fbf\u76b1\u7709\u6c89\u601d\u8d77\u6765\uff0c\u9152\u697c\u3000\u4e0a\u987f \u65f6\u53c8\u53d8\u5f97\u6b7b\u4e00\u6837\u7684\u9759\u5bc2\uff0c\u5fae\u95fb\u5598\u6c14\u4e4b\u58f0\uff0c\u6dcb\u6dcb\u800c\u4f5c\u3002 \u3000\u3000\u3000\u6bdb\u6587\u742a\u770b\u4e86\u4f0f\u5728\u684c\u4e0a\uff0c\u4f3c\u5df2\u5165\u7761\u7684\u7f2a\u6587\u4e00\u773c\uff0c\u8f7b\u8f7b\u8e31\u5230\u5979\u7239\u7239\u7684\u8eab\u4fa7\uff0c\u4f4e\u7740\u5934\u3000\uff0c\u5728 \u4ed6\u624b\u4e0a\u7684\u90a3\u4e24\u67c4\u91d1\u5251\u4e0a\u89c2\u770b\u7740\u3002 \u3000\u3000\u3000\u516b\u9762\u73b2\u73d1\u80e1\u4e4b\u8f89\u5374\u8d70\u5230\u6885\u5141\u6cf0\u8eab\u4fa7\uff0c\u9644\u7740\u8033\uff0c\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u51fa\u4e8b\u4e4b\u540e\uff0c\u662f\u3000\u5426\u5c31 \u7acb\u5373\u56de\u6765\u4e86\uff1f\u201d \u3000\u3000\u3000\u6885\u5141\u6cf0\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5f1f\u5b50\u4eec\u5c06\u8ba1\u4e8c\u53d4\u7684\u5c38\u8eab\u62ac\u5230\u5927\u8f66\u4e0a\uff0c\u4ea4\u7ed9\u8d76\u8f66\u7684\u5f1f\u5144\u3000\uff0c\u5c31 \u8fde\u591c\u8d76\u4e86\u56de\u6765\uff0c\u4e00\u8def\u4e0a\u6362\u4e86\u4e24\u6b21\u9a6c\uff0c\u8fde\u534a\u523b\u90fd\u672a\u66fe\u803d\u8bef\u3002\u201d \u3000\u3000\u3000\u516b\u9762\u73b2\u73d1\u80e1\u4e4b\u8f89\u4e5f\u76b1\u8d77\u7709\u5934\uff0c\u6697\u5fd6\uff1a\u201c\u4ee5\u65f6\u95f4\u6765\u63e3\u6d4b\uff0c\u5141\u6cf0\u4ed6\u4eec\u8d76\u5f97\u7684\u786e\u4e5f\u53ef\u3000\u8c13\u5feb \u5230\u6781\u70b9\uff0c\u96be\u9053\u90a3\u91d1\u5251\u4fa0\u5374\u80c1\u751f\u53cc\u7fc5\uff0c\u8fd8\u80fd\u8d76\u5728\u4ed6\u4eec\u524d\u9762\uff1f\u2026\u2026\u5982\u82e5\u4e0d\u7136\uff0c\u65b9\u624d\u3000\u90a3\u67c4\u91d1\u5251\u53c8 \u662f\u8c01\u53d1\u51fa\u6765\u7684\u5462\uff1f\u4ee5\u90a3\u4eba\u53d1\u6697\u5668\u7684\u624b\u6cd5\u6765\u770b\uff0c\u529f\u529b\u4e5f\u5df2\u81fb\u7edd\u9876\uff0c\u96be\u9053\u90a3\u3000\u91d1\u5251\u4fa0\u7adf\u5206\u8eab\u6709\u672f \u5417\uff1f\u201d \u3000\u3000\u3000\u4ed6\u5fc3\u4e2d\u601d\u7591\uff0c\u7075\u86c7\u6bdb\u81ec\u6b64\u523b\u4fef\u9996\u6c89\u601d\u7740\u7684\uff0c\u5374\u4e5f\u662f\u548c\u4ed6\u540c\u4e00\u4e2a\u60f3\u6cd5\u3002 \u3000\u3000\u3000\u8fd9\u5176\u4e2d\u53ea\u6709\u706b\u773c\u91d1\u96d5\u8427\u8fdf\u5634\u89d2\u9690\u542b\u51b7\u7b11\uff0c\u4e00\u4ed8\u5e78\u707e\u4e50\u7978\u7684\u6837\u5b50\uff0c\u5176\u4f59\u7684\u4eba\u5154\u6b7b\u3000\u72d0 \u60b2\uff0c\u7269\u4f24\u5176\u7c7b\uff0c\u5fc3\u4e2d\u81ea\u7136\u96be\u514d\u60b2\u6006\u4e86\u3002 \u3000\u3000\u3000\u6ee1\u697c\u4e4b\u4e0a\uff0c\u6b64\u523b\u6ee1\u5e03\u6101\u4e91\uff0c\u6d53\u539a\u5730\u538b\u5728\u6bcf\u4e2a\u4eba\u5fc3\u4e0a\uff0c\u538b\u5f97\u4eba\u4eba\u90fd\u4eff\u4f5b\u900f\u4e0d\u51fa\u6c14\u3000\u6765\u3002 \u3000\u3000\u3000\u7a81\u5730\uff0c\u6bdb\u6587\u742a\u4e00\u58f0\u5a07\u5524\uff0c\u6253\u7834\u6c89\u5bc2\uff0c\u5979\u4f3c\u4e4e\u9887\u4e3a\u60ca\u60f6\u4ed6\u8bf4\u9053\uff1a\u201c\u7239\u7239\uff0c\u60a8\u770b\u770b\u3000\uff0c\u8fd9 \u4e24\u53e3\u5c0f\u5251\u5251\u67c4\u4e0b\u9762\uff0c\u523b\u7740\u7684\u5b57\u5e76\u4e0d\u4e00\u6837\u5462\uff1f\u201d \u3000\u3000\u3000\u7075\u86c7\u6bdb\u81ec\u624b\u5fae\u4e00\u62ac\uff0c\u76ee\u5149\u95ea\u7535\u822c\u5728\u8fd9\u4e24\u67c4\u91d1\u5251\u4e0a\u51dd\u76ee\u6ce8\u89c6\u534a\u664c\uff0c\u9762\u8272\u4e0d\u7981\u53c8\u731b\u3000\u5730\u5927 \u53d8\uff0c\u4e24\u9053\u6d53\u7709\u76b1\u5f97\u66f4\u7d27\uff0c\u800c\u4e14\u76ee\u5149\u4e4b\u4e2d\uff0c\u7adf\u7136\u9732\u51fa\u4e00\u4e1d\u60ca\u5413\u7684\u610f\u5473\u6765\uff0c\u5374\u662f\u5728\u3000\u8fd9\u6b66\u6797\u9b41\u9996 \u9762\u4e0a\u524d\u6240\u672a\u89c1\u7684\u3002 \u3000\u3000\u3000\u6b64\u523b\u548c\u4ed6\u5173\u7cfb\u8f83\u6df1\uff0c\u8eab\u4efd\u76f8\u5f53\u7684\uff0c\u5982\u5b50\u6bcd\u53cc\u98de\u3001\u516b\u9762\u73b2\u73d1\u7b49\u4eba\uff0c\u90fd\u51d1\u4e86\u4e0a\u6765\uff0c\u3000\u90fd\u4fef \u9996\u671d\u8fd9\u4e24\u67c4\u91d1\u5251\u4e0a\u51dd\u6ce8\u4e00\u4e0b\uff0c\u53ea\u89c1\u8fd9\u4e24\u67c4\u91d1\u5251\u7684\u5251\u67c4\u4e0b\uff0c\u5251\u810a\u4e0a\uff0c\u679c\u7136\u90fd\u523b\u7740\u3000\u56db\u4e2a\u975e\u7ecf\u6ce8 \u610f\uff0c\u4fbf\u96be\u53d1\u89c9\u7684\u5c0f\u5b57\uff0c\u4e00\u67c4\u4e0a\u9762\u662f\u56db\u4e2a\u5c0f\u7bc6\uff0c\u523b\u7684\u662f\uff1a\u201c\u516c\u9053\u4e4b\u5251\u3002\u201d \u3000\u3000\u3000\u800c\u53e6\u4e00\u67c4\u7684\u5251\u810a\u4e0a\uff0c\u5374\u523b\u7684\u662f\u201c\u9b4f\u7891\u201d\uff0c\u4e0a\u9762\u7adf\u8d6b\u7136\u662f\uff1a\u201c\u4ee5\u8840\u8fd8\u8840\uff01\u201d\u89e6\u76ee\u3000\u60ca\u5fc3 \u7684\u56db\u4e2a\u5b57\u3002 \u3000\u3000\u3000\u7075\u86c7\u6bdb\u81ec\u9762\u5982\u6b7b\u7070\uff0c\u642d\u7136\u5c06\u624b\u4e2d\u7684\u4e24\u67c4\u5251\uff0c\u4ea4\u7ed9\u8eab\u4fa7\u7684\u80e1\u4e4b\u8f89\uff0c\u7f13\u6b65\u8d70\u5230\u7a97\u53e3\u3000\uff0c\u4ef0 \u9996\u82cd\u7a79\uff0c\u8fd9\u6b66\u6797\u9b41\u9996\u7adf\u53c8\u843d\u5165\u6c89\u601d\u91cc\u3002 \u3000\u3000\u3000\u80e1\u4e4b\u8f89\u5c06\u8fd9\u4e24\u67c4\u91d1\u5251\u5728\u624b\u91cc\u62c8\u4e86\u62c8\uff0c\u53c8\u62ff\u5230\u773c\u524d\u770b\u4e86\u770b\uff0c\u559f\u7136\u9053\uff1a\u201c\u8fd9\u4e24\u67c4\u5251\u3000\u5236\u4f5c \u7684\u5f0f\u6837\u867d\u7136\u5b8c\u5168\u76f8\u540c\uff0c\u4f46\u91d1\u5b50\u7684\u6210\u8272\u5374\u4e0d\u4e00\u6837\uff0c\u5509\uff01\u4e8b\u60c5\u8d8a\u6765\u8d8a\u66f4\u89c9\u5947\u602a\uff0c\u771f\u3000\u4f7f\u4eba\u6709\u4e9b\u5f04 \u4e0d\u61c2\u4e86\u3002\u201d \u3000\u3000\u3000\u7ad9\u5728\u7a97\u53e3\u4ef0\u671b\u7684\u7075\u86c7\u6bdb\u81ec\uff0c\u6b64\u523b\u7a81\u5730\u4e00\u9635\u72c2\u7b11\uff0c\u7b11\u58f0\u4e2d\u6ee1\u662f\u51c4\u5389\u7684\u5473\u9053\uff0c\u7fa4\u8c6a\u3000\u6115\u7136 \u671b\u7740\u8fd9\u6b66\u6797\u9b41\u9996\u8f6c\u8fc7\u8eab\u6765\uff0c\u72c2\u7b11\u7740\u9053\uff1a\u201c\u8fd9\u4e8b\u7684\u786e\u900f\u7740\u5947\u602a\uff0c\u60f3\u4e0d\u5230\u90a3\u59d3\u4ec7\u7684\u3000\u5c0f\u5b50\uff0c\u771f\u6709 \u540e\u4eba\u6765\u66ff\u4ed6\u62a5\u4ec7\uff0c\u597d\uff0c\u597d\uff0c\u53cd\u6b63\u4e16\u4e8b\u90fd\u6709\u4e86\u5374\u7684\u65f6\u5019\uff0c\u5f3a\u5b58\u5f31\u4ea1\uff0c\u6211\u5012\u3000\u8981\u548c\u4ed6\u6597\u4e0a\u4e00 \u6597\uff01\u201d\u4ed6\u53cc\u76ee\u7535\u5f20\uff0c\u7b11\u58f0\u987f\u4f4f\uff0c\u72b7\u6840\u4e4b\u6001\uff0c\u53c8\u590d\u5927\u4f5c\uff0c\u5237\u5730\u4e00\u62c2\u8896\u5b50\u3000\uff0c\u8d70\u5230\u68af\u53e3\uff0c\u4e00\u9762\u6717 \u58f0\u9053\uff1a\u201c\u4e01\u8001\u5f1f\uff0c\u6797\u59d1\u5a18\uff0c\u80e1\u8001\u4e09\uff0c\u4f60\u4eec\u90fd\u8ddf\u7740\u6211\u8d70\u5427\uff0c\u4faf\u8001\u3000\u56db\u8fd9\u91cc\u6599\u7406\u4e00\u4e0b\uff0c\u7167\u987e\u7167\u987e \u4f60\u4f84\u5973\u513f\uff0c\u5c06\u5979\u5e73\u5e73\u5b89\u5b89\u5730\u9001\u5230\u6cb3\u5317\u53bb\u3002\u201d\u4ed6\u5728\u68af\u53e3\u4f4f\u3000\u4e86\u8db3\uff0c\u53c8\u56de\u9996\u671d\u4ed6\u7231\u5973\u8bf4\u9053\uff1a\u201c\u742a \u513f\uff0c\u4f60\u5373\u523b\u56de\u5230\u4f60\u5e08\u5085\u90a3\u91cc\u53bb\uff0c\u8def\u4e0a\u4e0d\u8981\u803d\u8bef\u4e86\u3000\u3002\u201d \u3000\u3000\u3000\u6bdb\u6587\u742a\u70b9\u4e86\u70b9\u5934\uff0c\u5374\u89c1\u5979\u7239\u7239\u5df2\u522b\u8fc7\u5934\uff0c\u5411\u6885\u5141\u6cf0\u7b49\u9053\uff1a\u201c\u5141\u6cf0\u3001\u4e1c\u5c71\uff0c\u4f60\u4e24\u3000\u4eba\u5148 \u5728\u8fd9\u91cc\u6b47\u606f\u4e00\u4e0b\uff0c\u7136\u540e\u7acb\u523b\u968f\u7740\u4f60\u4faf\u56db\u53d4\u4e00\u9f50\u5230\u676d\u5dde\uff0c\u8def\u4e0a\u968f\u65f6\u4f20\u8bed\u5404\u5730\u7684\u5f1f\u3000\u5144\uff0c\u8fd9\u4e09\u4e2a \u6708\u91cc\uff0c\u65e0\u8bba\u9047\u7740\u4ec0\u4e48\u4e8b\u90fd\u4e0d\u8981\u4f38\u624b\uff0c\u517b\u7cbe\u84c4\u9510\uff0c\u7b49\u7740\u6211\u7684\u5429\u5490\u3002\u201d \u3000\u3000\u3000\u8fd9\u6b66\u6797\u9b41\u9996\u679c\u7136\u9887\u5177\u9886\u8896\u4e4b\u624d\uff0c\u6b64\u523b\u867d\u7136\u6709\u4e9b\u5fc3\u614c\uff0c\u4f46\u4e34\u4e8b\u5374\u4ecd\u4e0d\u4e71\uff0c\u4e09\u8a00\u4e24\u3000\u8bed\uff0c \u53cd\u6d3e\u4e86\u547d\u4ee4\uff0c\u7a81\u53c8\u8de8\u524d\u4e00\u6b65\uff0c\u671d\u90a3\u8427\u6c0f\u7236\u5b50\u51b7\u7136\u9053\uff1a\u201c\u4eca\u65e5\u4e4b\u4e8b\uff0c\u6682\u4e14\u63d0\u8fc7\uff0c\u3000\u6211\u59d3\u6bdb\u7684\u4e5f \u4e0d\u518d\u4e3a\u96be\u4f60\u4eec\uff0c\u9752\u5c71\u4e0d\u6539\uff0c\u7eff\u6c34\u957f\u6d41\uff0c\u6b64\u540e\u4f60\u6211\u4e3a\u654c\u4e3a\u53cb\uff0c\u4f46\u51ed\u5c0a\u5ff5\u3002\u3000\u201d\u8bed\u58f0\u4e00\u843d\uff0c\u8fd9\u6b66 \u6797\u9b41\u9996\u6839\u672c\u4e0d\u518d\u7406\u4f1a\u8427\u8fdf\u5f20\u53e3\u8981\u8bf4\u7684\u8bdd\uff0c\u53c8\u4e00\u62c2\u8896\uff0c\u6025\u6b65\u4e0b\u697c\u800c\u3000\u53bb\u3002 \u3000\u3000\u3000\u8427\u8fdf\u6115\u4e86\u534a\u664c\uff0c\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u5374\u89c1\u5b50\u6bcd\u53cc\u98de\u51b7\u6a2a\u81ea\u5df1\u4e00\u773c\uff0c\u6b65\u4e0b\u697c\u53bb\uff0c\u53c8\u89c1\u90a3\u3000\u767e\u6b65 \u98de\u82b1\u6797\u7426\u7b5d\uff0c\u772f\u7740\u773c\u775b\uff0c\u51b7\u51b7\u4e00\u7b11\uff0c\u5fae\u626d\u7ea4\u8170\uff0c\u5374\u53c8\u8f7b\u8f7b\u56de\u987e\u4f0f\u5728\u684c\u4e0a\u7684\u7f2a\u6587\u3000\u4e00\u773c\uff0c\u4e5f\u8d70 \u4e86\u4e0b\u6765\u3002 \u3000\u3000\u3000\u8fd9\u706b\u773c\u91d1\u96d5\u6b64\u523b\u5fc3\u4e2d\u4e94\u5473\u7ffb\u6d8c\uff0c\u867d\u7136\u6c14\u6124\uff0c\u5374\u4e5f\u6709\u4e9b\u5fc3\u7070\uff0c\u81ea\u5df1\u8fd9\u4e00\u8d9f\u5bbf\u8fc1\u4e4b\u3000\u884c\uff0c \u9669\u4e9b\u5c31\u6b64\u9001\u547d\uff0c\u65b9\u624d\u773c\u524d\u5251\u5149\u7f2d\u7ed5\uff0c\u5df2\u5c06\u4e0b\u5288\u7684\u90a3\u4e00\u5239\u90a3\uff0c\u6b64\u523b\u4eff\u4f5b\u8fd8\u4f7f\u5f97\u8fd9\u3000\u5df2\u5728\u6b66\u6797\u4e2d \u7ffb\u6eda\u4e86\u6570\u5341\u5e74\u7684\u8001\u4eba\u4e3a\u4e4b\u6697\u6697\u5fc3\u60b8\uff0c\u4ed6\u4e0d\u7981\u53c8\u957f\u53f9\u4e00\u58f0\uff0c\u4e00\u6301\u957f\u987b\uff0c\u7f13\u3000\u7f13\u8d70\u4e0b\u697c\u53bb\uff0c\u6b65\u5c65 \u4e4b\u95f4\uff0c\u4f3c\u4e4e\u6709\u4e86\u9f99\u949f\u8001\u6001\u4e86\u3002 \u3000\u3000\u3000\u672c\u6765\u7fa4\u8c6a\u6d4e\u6d4e\u7684\u9152\u697c\uff0c\u970e\u65f6\u4e4b\u95f4\uff0c\u5c31\u53ea\u5269\u4e0b\u4e86\u5be5\u5be5\u6570\u4eba\u3002 \u3000\u3000\u3000\u6bdb\u6587\u742a\u811a\u5c16\u4e00\u52a8\uff0c\u5c06\u5730\u4e0a\u5df2\u7ecf\u788e\u6210\u4e24\u534a\u7684\u4e00\u4e2a\u6d77\u7897\u8e22\u5230\u697c\u68af\u53e3\uff0c\u770b\u770b\u90a3\u534a\u8fb9\u7834\u3000\u7897\uff0c \u5728\u68af\u53e3\u7565\u4e3a\u4e00\u505c\uff0c\u5374\u4ecd\u7136\u6eda\u4e0b\u697c\u53bb\uff0c\u5e26\u8d77\u201c\u54d7\u5566\u201d\u4e00\u9635\u58f0\u54cd\uff0c\u5979\u77e5\u9053\u8fd9\u53ea\u7834\u7897\u3000\u5df2\u788e\u5f97\u66f4\u5389 \u5bb3\u4e86\u3002 \u3000\u3000\u3000\u4e8e\u662f\u8fd9\u7f8e\u4e3d\u7684\u5c11\u5973\uff0c\u5e7d\u5e7d\u5730\u53f9\u4e86\u53e3\u6c14\uff0c\u62ac\u8d77\u5934\u6765\uff0c\u671d\u94c1\u624b\u4ed9\u733f\u9053\uff1a\u201c\u4faf\u56db\u53d4\uff0c\u3000\u6211\u5c31 \u4f4f\u5728\u53f3\u8fb9\u90a3\u6761\u8def\u4e0a\u7684\u5e86\u798f\u957f\u5ba2\u6808\u91cc\uff0c\u6700\u591a\u518d\u6b47\u534a\u5929\uff0c\u5c31\u8981\u8d76\u8def\u4e86\uff0c\u4f60\u8001\u4eba\u5bb6\u6709\u3000\u4e8b\u8981\u529e\uff0c\u6211 \u4e5f\u4e0d\u518d\u9ebb\u70e6\u60a8\u4e86\u3002\u201d \u3000\u3000\u3000\u94c1\u624b\u4ed9\u733f\u6b64\u523b\u4e5f\u6709\u4e9b\u5fc3\u4e71\u5982\u9ebb\uff0c\u95fb\u8a00\u70b9\u4e86\u70b9\u5934\uff0c\u3002\u8bf4\u9053\uff1a\u201c\u8def\u4e0a\u53ef\u8981\u5c0f\u5fc3\u4e9b\uff0c\u3000\u8fd8\u6709 \u4ec0\u4e48\u9700\u8981\u3002\u53ea\u7ba1\u544a\u8bc9\u6211\u597d\u4e86\u3002\u201d \u3000\u3000\u3000\u6bdb\u6587\u742a\u6447\u4e86\u6447\u5934\uff0c\u8d70\u5230\u7f2a\u6587\u8eab\u4fa7\uff0c\u8f7b\u8f7b\u5730\u62cd\u4e86\u62cd\u4ed6\u7684\u80a9\u5934\uff0c\u4fef\u4e0b\u5934\u53bb\uff0c\u5728\u4ed6\u7684\u3000\u8033\u8fb9 \u8bf4\u9053\uff1a\u201c\u6587\u54e5\uff0c\u522b\u7761\u4e86\u3002\u201d \u3000\u3000\u3000\u7f2a\u6587\u62ac\u8d77\u5934\u6765\uff0c\u8ff7\u832b\u5730\u56db\u987e\u4e00\u773c\uff0c\u4f38\u4e86\u4e2a\u5fd9\u8170\uff0c\u91cd\u91cd\u6253\u4e86\u4e2a\u5475\u6b20\uff0c\u6491\u7740\u684c\u7f18\u7f13\u3000\u7f13\u7ad9 \u4e86\u8d77\u6765\uff0c\u671d\u94c1\u624b\u4ed9\u733f\u5fae\u5fae\u4e00\u7b11\uff0c\u4e5f\u5c31\u8d70\u4e0b\u4e86\u697c\u3002 \u3000\u3000\u3000\u795e\u5251\u4f7f\u8005\u671b\u7740\u8fd9\u4e24\u4eba\u5e76\u80a9\u800c\u53bb\u7684\u80cc\u5f71\uff0c\u6697\u6697\u5550\u4e86\u4e00\u53e3\uff0c\u4f4e\u58f0\u9a82\u7740\uff1a\u201c\u4e0d\u4e2d\u7528\u7684\u3000\u4e66\u5446 \u5b50\uff01\u201d\u539f\u6765\u8fd9\u5e74\u8f7b\u4eba\u4e5f\u5bf9\u6bdb\u6587\u742a\u6709\u4e9b\u7231\u610f\uff0c\u6b64\u523b\u89c1\u4e86\u4ed6\u4e24\u4eba\u4eb2\u5bc6\u7684\u795e\u60c5\uff0c\u5992\u706b\u3000\u4e2d\u70e7\uff0c\u5fcd\u4e0d \u4f4f\u9a82\u4e86\u51fa\u6765\u3002 \u3000\u3000\u3000\u94c1\u624b\u4ed9\u733f\u53cc\u7709\u5fae\u4e00\u76b1\uff0c\u5374\u542c\u5f97\u697c\u68af\u95f4\u53c8\u4f20\u6765\u4e00\u9635\u811a\u6b65\u58f0\uff0c\u516b\u9762\u73b2\u73d1\u80e1\u4e4b\u8f89\u7adf\u53c8\u3000\u53bb\u800c \u590d\u8fd4\u3002 \u3000\u3000\u3000\u4ed6\u5306\u5fd9\u5730\u8d70\u4e86\u4e0a\u697c\uff0c\u5c06\u624b\u91cc\u62ff\u7740\u7684\u4e00\u67c4\u91d1\u5251\uff0c\u4ea4\u7ed9\u4faf\u6797\uff0c\u6c89\u58f0\u9053\u3002 \u3000\u3000\u3000\u201c\u5927\u54e5\u5429\u5490\uff0c\u53eb\u4f60\u6d3e\u51e0\u4e2a\u5f97\u529b\u7684\u5f1f\u5144\uff0c\u5230\u9547\u6c5f\u5e9c\u9644\u8fd1\u4e00\u5e26\u7684\u5927\u5c0f\u4e61\u9547\u7684\u5927\u5c0f\u91d1\u3000\u94fa\u53bb \u6253\u542c\u4e00\u4e0b\uff0c\u6700\u8fd1\u51e0\u4e2a\u6708\u6765\uff0c\u6709\u6ca1\u6709\u4eba\u53bb\u6253\u9020\u8fd9\u79cd\u9ec4\u91d1\u5c0f\u5251\u7684\uff0c\u5982\u679c\u6709\u7684\u8bdd\uff0c\u5207\u3000\u5207\u8981\u5c06\u90a3\u4eba \u7684\u5f62\u72b6\u3001\u5e74\u5c81\u67e5\u95ee\u51fa\u6765\uff0c\u8fd9\u4e8b\u5f97\u8d76\u5feb\u505a\uff0c\u5374\u4e0d\u53ef\u6cc4\u9732\u4e00\u4e1d\u98ce\u58f0\uff01\u201d \u3000\u3000\u3000\u4ed6\u4e00\u53e3\u6c14\u8bf4\u5230\u8fd9\u91cc\uff0c\u987f\u4e86\u987f\uff0c\u4f3c\u4e4e\u8003\u8651\u4e86\u534a\u664c\uff0c\u7ec8\u4e8e\u53c8\u9644\u5728\u4faf\u6797\u8033\u7554\uff0c\u6084\u8bed\u9053\u3000\uff1a \u201c\u8fd8\u6709\u4ef6\u4e8b\uff0c\u5c31\u662f\u8bf7\u56db\u5f1f\u4f60\u8d76\u7d27\u53eb\u51e0\u4e2a\u94c1\u9a91\u961f\u91cc\u7684\u5f1f\u5144\uff0c\u98de\u9a6c\u8d76\u5230\u7ca4\u4e1c\u53bb\uff0c\u67e5\u95ee\u3000\u90a3\u8fb9\u7684\u8c6a \u5bcc\u5de8\u5546\u91cc\uff0c\u6709\u6ca1\u6709\u4e00\u4e2a\u59d3\u7f2a\u7684\uff0c\u5c06\u4ed6\u7684\u8eab\u5bb6\u3001\u60c5\u51b5\uff0c\u4e5f\u8be6\u7ec6\u63a2\u67e5\u4e00\u4e0b\uff0c\u8d76\u3000\u7d27\u56de\u6765\u544a\u8bc9 \u6211\u3002\u201d \u3000\u3000\u3000\u94c1\u624b\u4ed9\u733f\u4faf\u6797\u552f\u552f\u7b54\u5e94\u7740\uff0c\u516b\u9762\u73b2\u73d1\u8fd9\u624d\u4e00\u4f38\u8170\uff0c\u8138\u4e0a\u7684\u8089\u677e\u5f1b\u4e86\u4e00\u4e0b\uff0c\u88c5\u51fa\u3000\u4e00\u4e2a \u6709\u4e9b\u50cf\u662f\u7b11\u5bb9\u7684\u5f62\u72b6\u6765\uff0c\u5fae\u559f\u8bf4\u9053\uff1a\u201c\u8fd9\u4e9b\u5929\uff0c\u6211\u9a6c\u4e0d\u505c\u8e44\uff0c\u771f\u662f\u7d2f\u5f97\u534a\u6b7b\uff0c\u3000\u56db\u5f1f\uff0c\u4f60\u5feb \u66ff\u4f60\u4e09\u54e5\u5f04\u4e9b\u9152\u83dc\u4e00\u4e00\u54c8\uff0c\u54c8\uff0c\u6700\u597d\u8fd8\u5f04\u4e2a\u599e\u513f\u6765\uff0c\u6211\u5728\u8fd9\u91cc\u8212\u670d\u4e2a\u534a\u3000\u5929\uff0c\u4eca\u5929\u665a\u4e0a\u53c8\u8fd8 \u5f97\u8d76\u5230\u676d\u5dde\u53bb\uff0c\u54c8\u2014\u2014\u4eba\u4e00\u80d6\uff0c\u5c31\u4e0d\u592a\u60f3\u52a8\u4e86\uff0c\u53ef\u662f\u4e8b\u60c5\u8d8a\u6765\u8d8a\u3000\u7cdf\uff0c\u6211\u5374\u662f\u975e\u52a8\u4e0d\u53ef\uff01\u201d \u3000\u3000\u3000\u94c1\u624b\u4ed9\u733f\u5fae\u5fae\u4e00\u7b11\uff0c\u76ee\u5149\u8f6c\u8fc7\u5904\uff0c\u592a\u9633\u5df2\u5c04\u8fdb\u7a97\u5b50\u91cc\u6765\uff0c\u539f\u6765\u6b64\u523b\u5df2\u7ecf\u8fc7\u4e86\u5348\u3000\u65f6 \u4e86\u3002 \u3000\u3000\u3000\u7f2a\u6587\u8fde\u8fde\u5730\u6253\u7740\u5475\u6b20\uff0c\u968f\u7740\u6bdb\u6587\u742a\u8d70\u4e0b\u4e86\u697c\uff0c\u521a\u5f80\u53f3\u8fb9\u4e00\u8f6c\uff0c\u773c\u89d2\u5374\u5df2\u779f\u89c1\u516b\u3000\u9762\u73b2 \u73d1\u80e1\u4e4b\u8f89\u7531\u53e6\u4e00\u65b9\u8eb2\u8eb2\u85cf\u85cf\u5730\u8d70\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u3000\u7f2a\u6587\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5374\u88c5\u505a\u6ca1\u6709\u770b\u89c1\u7684\u6837\u5b50\uff0c\u65bd\u65bd\u7136\u548c\u6bdb\u6587\u742a\u8d70\u51fa\u4e86\u8fd9\u6761\u4e24\u65c1\u4ecd\u7136\u3000\u7ad9\u7740 \u4e09\u4e94\u6210\u7fa4\u7684\u91d1\u886b\u5927\u6c49\u7684\u6a2a\u8857\u3002 \u3000\u3000\u3000\u6bdb\u6587\u742a\u4e00\u62d0\u65f6\u5b50\uff0c\u63a8\u4e86\u4ed6\u4e00\u4e0b\uff0c\u5a07\u55d4\u7740\u9053\uff1a\u201c\u4f60\u77a7\u4f60\u8fd9\u4ed8\u5fb7\u884c\uff0c\u624d\u4e00\u5929\u6ca1\u6709\u7761\u3000\u89c9\uff0c \u8d70\u8def\u5c31\u6643\u6643\u8361\u8361\u7684\u4e86\u3002\u201d \u3000\u3000\u3000\u7f2a\u6587\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u6211\u53ef\u6bd4\u4e0d\u4e0a\u4f60\uff0c\u4f60\u662f\u73a9\u5200\u821e\u5251\u7684\u5973\u82f1\u96c4\uff0c\u6211\u53ef\u4e0d\u884c\uff0c\u3000\u71ac\u4e86 \u8fd9\u4e2a\u901a\u5bb5\uff0c\u73b0\u5728\u6211\u4e24\u6761\u817f\u5c31\u50cf\u5728\u5f39\u7740\u7435\u7436\u4f3c\u7684\u3002\u201d \u3000\u3000\u3000\u6bdb\u6587\u742a\u201c\u5657\u5657\u201d\u4e00\u58f0\uff0c\u7b11\u51fa\u58f0\u6765\uff0c\u6307\u7740\u4ed6\u54af\u54af\u5730\u7b11\u7740\u9053\uff1a\u201c\u4f60\u770b\u4f60\u8fd9\u526f\u7a77\u9178\u50cf\u3000\uff0c\u51e0 \u65f6\u6211\u975e\u903c\u4f60\u7ec3\u7ec3\u6b66\u529f\u4e0d\u53ef\uff0c\u4e0d\u7136\u4f60\u8fd9\u526f\u5f31\u4e0d\u7981\u98ce\u7684\u6837\u5b50\uff0c\u8fde\u4e2a\u5927\u59d1\u5a18\u90fd\u4e0d\u5982\uff0c\u3000\u4eba\u5bb6\u4e00\u6307\u5934 \u5c31\u80fd\u628a\u4f60\u5f04\u4e2a\u5927\u8ddf\u5934\u3002\u201d \u3000\u3000\u3000\u7f2a\u6587\u8fb9\u7b11\u8fb9\u884c\uff0c\u7a81\u5730\u9a7b\u8db3\u95ee\u9053\uff1a\u201c\u522b\u7684\u6211\u90fd\u4e0d\u5947\u602a\uff0c\u6b66\u529f\u6211\u4e5f\u4e0d\u8981\u7ec3\uff0c\u53ef\u662f\u6211\u3000\u5012\u8981 \u95ee\u95ee\u4f60\uff0c\u4f60\u90a3\u67c4\u5b9d\u5251\u5230\u5e95\u6709\u4ec0\u4e48\u53e4\u602a\uff0c\u600e\u5730\uff0c\u4eba\u5bb6\u4e00\u6cbe\u7740\uff0c\u5c31\u5f97\u50cf\u7334\u5b50\u4e00\u6837\u8df3\u3000\u8d77\u6765\uff0c\u5582\uff01 \u4f60\u90a3\u4f4d\u5e08\u5085\u662f\u4e0d\u662f\u4e2a\u4f1a\u73a9\u6cd5\u672f\u7684\u5973\u9053\u58eb\u5440\uff1f\u201d \u3000\u3000\u3000\u6bdb\u6587\u742a\u7b11\u5f97\u4f3c\u4e4e\u5df2\u76f4\u4e0d\u8d77\u8170\u6765\uff0c\u4f46\u5374\u4e00\u9762\u6447\u7740\u5934\uff0c\u4e00\u9762\u9053\uff1a\u201c\u4f60\u8981\u95ee\u8fd9\u4e2a\u5440\uff0c\u3000\u6211\u53ef \u4e0d\u80fd\u544a\u8bc9\u4f60\u3002\u201d\u987f\u4e86\u987f\u53c8\u9053\uff1a\u201c\u8fd9\u53e3\u5251\u5c31\u7b97\u4e0d\u662f\u795e\u4ed9\u9020\u7684\uff0c\u53ef\u4e5f\u5dee\u4e0d\u591a\u4e86\uff0c\u6211\u3000\u5e08\u5085\u4ece\u5c0f\u5c31 \u559c\u6b22\u73a9\u5404\u5f0f\u5404\u6837\u7a00\u5947\u53e4\u602a\u7684\u4e1c\u897f\uff0c\u5979\u8001\u4eba\u5bb6\u8d39\u4e86\u8bb8\u591a\u5fc3\u529b\uff0c\u624d\u9020\u4e86\u53e3\u8fd9\u3000\u6837\u7684\u5b9d\u5251\uff0c\u5e38\u8bf4\u5c31 \u7b97\u53e4\u65f6\u7684\u6e5b\u5362\u3001\u5de8\u9619\u8fd9\u79cd\u540d\u5251\uff0c\u4e5f\u6bd4\u4e0d\u4e0a\u6211\u8fd9\u53e3\u5251\u7684\u5389\u5bb3\uff0c\u65e0\u8bba\u3000\u529f\u592b\u518d\u597d\u7684\u4eba\uff0c\u4e00\u78b0\u4e0a\u6211 \u8fd9\u53e3\u5251\u5440\uff0c\u563f\uff01\u4ed6\u4e5f\u53d7\u4e0d\u4e86\uff0c\u4f60\u770b\u6628\u5929\u665a\u4e0a\u90a3\u4e2a\u80d6\u5b50\uff0c\u4ed6\u3000\u2014\u2014\u201d\u8fd9\u7eaf\u771f\u7f8e\u4e3d\u7684\u5c11\u5973\u5471\u5471\u7b11 \u8bed\u7740\uff0c\u7a81\u7136\u770b\u5230\u7f2a\u6587\u8138\u4e0a\u4e00\u4e1d\u7b11\u5bb9\u4e5f\u6ca1\u6709\u4e86\uff0c\u7acb\u523b\u3000\u4f4f\u4e86\u53e3\uff0c\u6539\u53d8\u4e86\u4e2a\u8bdd\u9898\uff0c\u6e29\u67d4\u5730\u7b11\u9053\uff1a \u201c\u6211\u771f\u662f\uff0c\u8ddf\u4f60\u8bf4\u8fd9\u4e9b\u6709\u4ec0\u4e48\u7528\uff0c\u5582\uff01\u6211\u95ee\u3000\u4f60\uff0c\u5230\u4e86\u6cb3\u5317\uff0c\u6211\u53bb\u627e\u5e08\u5085\uff0c\u4f60\u5230\u54ea\u91cc\u53bb \u5440\uff1f\u201d \u3000\u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u3000\u7f2a\u6587\u6c89\u541f\u7740\uff0c\u5ffd\u5730\u770b\u5230\u5bf9\u9762\u8d70\u8fc7\u4e00\u4e2a\u4eba\u6765\uff0c\u8fd9\u4eba\u7a7f\u7740\u4e00\u88ad\u5df2\u7ecf\u6d17\u5f97\u53d1\u767d\u7684\u6de1\u84dd\u3000\u957f \u886b\uff0c\u957f\u8eab\u7389\u7acb\uff0c\u8863\u886b\u867d\u7136\u5bd2\u9178\uff0c\u4f46\u662f\u795e\u5149\u7115\u53d1\uff0c\u6ee1\u8138\u82f1\u5149\uff0c\u5374\u534a\u70b9\u4e5f\u6ca1\u6709\u5bd2\u9178\u7684\u3000\u6837\u5b50\u3002\u6b63 \u5982\u4e00\u7c92\u660e\u73e0\uff0c\u7eb5\u7136\u88ab\u88c5\u5728\u7834\u70c2\u7684\u5e03\u888b\u91cc\uff0c\u5374\u8d8a\u53d1\u663e\u5f97\u5149\u91c7\u593a\u76ee\u3002 \u3000\u3000\u3000\u8fd9\u4eba\u8fce\u9762\u671d\u7f2a\u6587\u884c\u6765\uff0c\u7f2a\u6587\u4e00\u62ac\u5934\uff0c\u6070\u597d\u9047\u7740\u4ed6\u7684\u773c\u795e\uff0c\u4e24\u4eba\u76ee\u5149\u76f8\u5bf9\uff0c\u8fd9\u84dd\u3000\u886b\u4eba \u7adf\u671d\u7f2a\u6587\u5fae\u5fae\u7b11\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u3000\u8fd9\u4eba\u9762\u4e0a\u7684\u8f6e\u5ed3\uff0c\u672c\u6781\u575a\u6bc5\uff0c\u4e24\u7709\u4e4b\u95f4\uff0c\u9690\u9690\u73b0\u51fa\u4e09\u9053\u6c9f\u7eb9\uff0c\u4e00\u773c\u671b\u53bb\uff0c\u672c\u89c9\u3000\u751a\u662f \u51b7\u524a\uff0c\u4f46\u7ecf\u6b64\u4e00\u7b11\uff0c\u5374\u5982\u6625\u98ce\u62c2\u9762\uff0c\u7115\u7136\u4e0d\u540c\u3002 \u3000\u3000\u3000\u4e00\u7b11\u4e4b\u95f4\uff0c\u8fd9\u84dd\u886b\u4eba\u5df2\u4ece\u7f2a\u6587\u8eab\u4fa7\u64e6\u80a9\u800c\u8fc7\uff0c\u7f2a\u6587\u5fcd\u4e0d\u4f4f\u56de\u8eab\u53bb\u770b\uff0c\u5374\u89c1\u8fd9\u84dd\u3000\u886b\u4eba \u7adf\u4e5f\u56de\u5934\u671b\u7740\u81ea\u5df1\uff0c\u4e0d\u7981\u5fc3\u4e00\u70ed\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u4f46\u5fc3\u4e2d\u5374\u4ecd\u88ab\u8fd9\u84dd\u886b\u4eba\u7684\u98ce\u4eea\u6240\u3000\u9189\uff0c\u4e45\u4e45\u4e0d \u80fd\u81ea\u5df1\u3002 \u3000\u3000\u3000\u6bdb\u6587\u742a\u5374\u5df2\u561f\u8d77\u5c0f\u5634\uff0c\u53c8\u751f\u5a07\u55d4\uff0c\u8f7b\u8f7b\u4e00\u8dfa\u811a\uff0c\u8bf4\u9053\uff1a\u201c\u4eba\u5bb6\u95ee\u4f60\u7684\u8bdd\uff0c\u4f60\u600e\u3000\u4e48\u4e0d \u7b54\u5e94\u5440\uff0c\u96be\u9053\u4f60\u804b\u4e86\u4e0d\u6210\uff1f\u201d \u3000\u3000\u3000\u7f2a\u6587\u671b\u4e86\u671b\u8fd9\u5201\u86ee\uff0c\u4f46\u5374\u771f\u60c5\u7684\u5c11\u5973\uff0c\u5fc3\u91cc\u7a81\u7136\u6cdb\u8d77\u4e86\u8bb8\u591a\u611f\u89e6\uff0c\u76ee\u5149\u4e00\u8f6c\uff0c\u3000\u5fae\u6307 \u524d\u9762\u201c\u5e86\u798f\u957f\u201d\u7684\u5e97\u62db\uff0c\u7b11\u7740\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u56de\u5230\u5ba2\u6808\u518d\u8bf4\u4e0d\u597d\u5417\uff1f\u4f60\u770b\uff0c\u8857\u4e0a\u3000\u7684\u4eba\u5df2\u5728\u671b \u7740\u6211\u4eec\u4e86\u3002\u201d \u3000\u3000\u3000\u6bdb\u6587\u742a\u773c\u6ce2\u6d3b\u8f6c\uff0c\u679c\u7136\u770b\u5230\u6709\u5341\u51e0\u9053\u76ee\u5149\uff0c\u6ce8\u5728\u81ea\u5df1\u8138\u4e0a\uff0c\u5979\u7c89\u8138\u4e0d\u7981\u53c8\u7ea2\u4e86\u3000\u8d77 \u6765\uff0c\u53e3\u4e2d\u5374\u4ecd\u7136\u4f6f\u55d4\u7740\u8bf4\u9053\uff1a\u201c\u6211\u624d\u4e0d\u6015\u522b\u4eba\u770b\u54e9\uff0c\u770b\u5c31\u770b\uff0c\u6709\u4ec0\u4e48\u5173\u7cfb\u3002\u201d\u4f46\u3000\u811a\u4e0b\u5374\u5df2 \u968f\u7740\u7f2a\u6587\uff0c\u52a0\u5feb\u5730\u671d\u90a3\u5ba2\u6808\u8d70\u53bb\u3002 \u3000\u3000\u3000\u6bdb\u6587\u742a\u6492\u5a07\u653e\u5201\uff0c\u4f6f\u55d4\u4f6f\u6012\uff0c\u60f3\u5c3d\u5343\u65b9\u767e\u8ba1\uff0c\u4ee5\u6c42\u535a\u5f97\u81ea\u5df1\u5fc3\u4e0a\u4eba\u7684\u6b22\u5fc3\uff0c\u7f2a\u3000\u6587\u5374 \u59cb\u7ec8\u662f\u5fae\u5fae\u542b\u7b11\uff0c\u7adf\u6709\u4e9b\u65e0\u52a8\u4e8e\u8877\u7684\u6837\u5b50\u3002 \u3000\u3000\u3000\u4f46\u8fd9\u5df2\u6df1\u9677\u60c5\u7f51\u7684\u6bdb\u6587\u742a\u5374\u770b\u4e0d\u51fa\u6765\uff0c\u5979\u53ea\u9053\u82b3\u5fc3\u4ece\u6b64\u6709\u5bc4\uff0c\u5343\u79cd\u67d4\u60c5\uff0c\u4e07\u7f15\u3000\u60c5 \u601d\uff0c\u90fd\u4e00\u4e1d\u4e0d\u5269\u5730\u653e\u5230\u7f2a\u6587\u8eab\u4e0a\u3002 \u3000\u3000\u3000\u82e5\u8bf4\u7f2a\u6587\u662f\u94c1\u77f3\u5fc3\u80a0\u5417\uff0c\u90a3\u5374\u4e5f\u4e0d\u89c1\u5f97\uff0c\u4ed6\u7684\u76ee\u5149\uff0c\u4ed6\u7684\u5634\u89d2\uff0c\u4e5f\u4e0d\u65f6\u4f1a\u9732\u51fa\u3000\u4e00\u4e1d \u534a\u7f15\u771f\u60c5\uff0c\u4f46\u662f\u4e0d\u77e5\u600e\u7684\uff0c\u8fd9\u5e74\u9752\u4eba\u7adf\u50cf\u5df2\u80fd\u63a7\u5236\u81ea\u5df1\u7684\u60c5\u611f\uff0c\u6bcf\u5f53\u8fd9\u79cd\u771f\u60c5\u3000\u6d41\u9732\u7684\u65f6 \u5019\uff0c\u4ed6\u7acb\u523b\u4f1a\u4ee5\u4e00\u4e2a\u83ab\u6d4b\u9ad8\u6df1\u7684\u5fae\u7b11\u6765\u906e\u76d6\u4f4f\uff0c\u7b49\u5230\u4ed6\u8fd9\u79cd\u5fae\u7b11\u6269\u6563\u7684\u65f6\u3000\u5019\uff0c\u4ed6\u8138\u4e0a\u6240\u6d41 \u9732\u7684\u771f\u60c5\u4fbf\u7acb\u523b\u6d88\u5931\u4e86\u3002 \u3000\u3000\u3000\u56de\u5230\u5ba2\u6808\uff0c\u6587\u742a\u5de6\u95ee\u53f3\u95ee\uff0c\u6765\u8ffd\u5bfb\u7f2a\u6587\u7684\u6765\u8e2a\u3001\u53bb\u5904\uff0c\u56e0\u4e3a\u4ed6\u4eec\u8ddd\u79bb\u6cb3\u5317\u8d8a\u6765\u3000\u8d8a \u8fd1\uff0c\u90a3\u5c31\u662f\u8bf4\u4ed6\u4eec\u5206\u522b\u7684\u65e5\u5b50\u5df2\u5feb\u8981\u6765\u4e86\u3002 \u3000\u3000\u3000\u6bdb\u6587\u742a\u867d\u7136\u4e0d\u820d\u5f97\uff0c\u4f46\u662f\u5979\u4e5f\u4e0d\u6562\u4e0d\u53bb\u5e08\u5085\u90a3\u91cc\uff0c\u56e0\u4e4b\u5979\u8ffd\u6839\u7a76\u5e95\uff0c\u4e3a\u7684\u4e5f\u4e0d\u3000\u8fc7\u53ea \u662f\u8981\u7f2a\u6587\u8bf4\u51fa\u51b3\u5b9a\u7b49\u5f85\u81ea\u5df1\u7684\u8bdd\u6765\u3002 \u3000\u3000\u3000\u4f46\u7f2a\u6587\u5374\u5de6\u652f\u53f3\u543e\uff0c\u53eb\u4ed6\u8bf4\u4e00\u53e5\u771f\u5fc3\u8bdd\uff0c\u4f3c\u4e4e\u6bd4\u767b\u5929\u8fd8\u96be\u3002 \u3000\u3000\u3000\u4e8e\u662f\uff0c\u6bdb\u6587\u742a\u751f\u6c14\u4e86\uff0c\u561f\u7740\u5634\u56de\u5230\u623f\u91cc\uff0c\u7f2a\u6587\u4ecd\u7136\u5e26\u7740\u5fae\u7b11\uff0c\u4e5f\u4e0d\u8feb\u8fc7\u53bb\u966a\u8bdd\u3000\uff0c\u5374 \u4e00\u4e2a\u4eba\u5728\u623f\u91cc\u8e31\u7740\u65b9\u6b65\uff0c\u53c8\u95ee\u5e97\u5bb6\u8981\u4e86\u7b14\u58a8\uff0c\u5374\u53ea\u662f\u653e\u5728\u684c\u5b50\u4e0a\u800c\u5df2\uff0c\u6839\u672c\u6ca1\u3000\u6709\u52a8\u624b\u5199\u4ec0 \u4e48\u3002 \u3000\u3000\u3000\u679c\u7136\uff0c\u8fc7\u4e86\u4e00\u4f1a\u513f\uff0c\u6bdb\u6587\u742a\u53c8\u5fcd\u4e0d\u4f4f\u8dd1\u4e86\u8fc7\u6765\uff0c\u53c8\u6e29\u67d4\u5730\u6765\u966a\u7f2a\u6587\u8bf4\u8bdd\uff0c\u7f2a\u6587\u3000\u5374\u4ecd \u7136\u5fae\u7b11\u7740\uff0c\u503e\u542c\u7740\uff0c\u65e2\u4e0d\u751f\u6c14\uff0c\u4e5f\u4e0d\u9ad8\u5174\u3002 \u3000\u3000\u3000\u5982\u6b64\u67ef\u67ef\u817e\u817e\uff0c\u8c08\u8c08\u7b11\u7b11\uff0c\u7adf\u7136\u5929\u53c8\u9ed1\u4e86\uff0c\u6bdb\u6587\u742a\u89c9\u5f97\u773c\u76ae\u6108\u6765\u6108\u91cd\uff0c\u7ec8\u4e8e\u71ac\u3000\u4e0d \u4f4f\uff0c\u4e5f\u6253\u8d77\u5475\u6b20\u3001\u4f38\u8d77\u61d2\u8170\u6765\u4e86\u3002 \u3000\u3000\u3000\u4e8e\u662f\u7f2a\u6587\u5c31\u966a\u5979\u5230\u623f\u5b50\u53bb\u7761\u3002\u4ed6\u5750\u5728\u5e8a\u8fb9\u7684\u6905\u5b50\u4e0a\uff0c\u770b\u7740\u5979\u6e10\u6e10\u7761\u7740\u4e86\uff0c\u770b\u7740\u3000\u5979\u5728 \u68a6\u91cc\u9732\u51fa\u6d77\u68e0\u4e00\u822c\u7684\u7b11\u5bb9\uff0c\u4ed6\u7684\u773c\u5149\u91cc\uff0c\u4e0d\u7981\u53c8\u6d41\u9732\u51fa\u4e00\u4e1d\u6e29\u99a8\u7684\u60c5\u610f\uff0c\u4f38\u51fa\u3000\u624b\uff0c\u60f3\u53bb\u629a \u6478\u4e00\u4e0b\u5979\u4f38\u51fa\u88ab\u5916\u7684\u7389\u81c2\u3002 \u3000\u3000\u3000\u4f46\u662f\u624b\u521a\u4f38\u51fa\uff0c\u5c31\u7acb\u523b\u7f29\u4e86\u56de\u53bb\uff0c\u4ed6\u4e5f\u968f\u4e4b\u7ad9\u8d77\u8eab\u6765\uff0c\u6084\u6084\u8d70\u56de\u81ea\u5df1\u7684\u623f\u95f4\uff0c\u3000\u53c8\u4fef \u9996\u6c89\u601d\u4e86\u534a\u664c\uff0c\u7a81\u7136\u5750\u5230\u684c\u65c1\uff0c\u63d0\u7b14\u5199\u9053\uff1a\u201c\u84dd\u886b\u4eba\uff0c\u4e09\u5341\u4f59\uff0c\u7626\u524a\u575a\u6bc5\uff0c\u7709\u3000\u5fc3\u6709\u7eb9\uff0c\u76ee \u5149\u70af\u7136\uff0c\u52a1\u987b\u7559\u610f\u6b64\u4eba\u6765\u8def\u3001\u53bb\u5411\uff0c\u4e00\u6709\u6d88\u606f\uff0c\u7acb\u5373\u901a\u77e5\u3002\u201d\u6401\u4e0b\u7b14\uff0c\u3000\u53c8\u6c89\u601d\u7247\u523b\uff0c\u7ad9\u8d77 \u6765\uff0c\u5728\u623f\u4e2d\u8f6c\u4e86\u51e0\u8f6c\uff0c\u518d\u5750\u4e0b\u6765\u63a5\u7740\u5199\u9053\uff1a\u201c\u6bdb\u67d0\u5df2\u56de\u676d\u5dde\uff0c\u7559\u3000\u610f\u5bdf\u770b\u5176\u884c\u8e2a\uff0c\u6b64\u95f4\u4faf\u6797 \u82e5\u6709\u884c\u52a8\uff0c\u4e5f\u987b\u7acb\u523b\u544a\u6211\uff0c\u5c14\u7b49\u884c\u8e2a\u52a1\u987b\u8c28\u614e\uff0c\u5207\u8bb0\uff01\u5207\u3000\u8bb0\uff01\u201d \u3000\u3000\u3000\u5199\u5b8c\u4e86\uff0c\u4ed6\u53c8\u4ece\u5934\u5230\u5c3e\u770b\u4e86\u4e00\u904d\uff0c\u5c06\u8fd9\u5f20\u5b57\u6761\u4ed4\u7ec6\u5730\u53e0\u6210\u4e00\u4e2a\u5c0f\u65b9\u5757\uff0c\u63e1\u5728\u638c\u3000\u5fc3\uff0c \u6574\u4e86\u6574\u8863\u886b\uff0c\u8d70\u51fa\u623f\u95e8\uff0c\u5230\u5ba2\u6808\u95e8\u53e3\u3002 \u3000\u3000\u3000\u4ed6\u76ee\u5149\u56db\u4e0b\u4e00\u8f6c\uff0c\u4e00\u4e2a\u4e70\u5356\u4eba\u6253\u626e\u7684\u6c49\u5b50\u4ece\u8857\u89d2\u7684\u6697\u5f71\u4e0b\u8d70\u4e86\u51fa\u6765\uff0c\u7f2a\u6587\u624b\u6307\u3000\u5fae\u5fae \u4e00\u5f39\uff0c\u638c\u5fc3\u7684\u90a3\u5757\u7eb8\u5757\uff0c\u5c31\u5de7\u5999\u5730\u843d\u5728\u90a3\u4eba\u624b\u91cc\u3002 \u3000\u3000\u3000\u90a3\u4eba\u63a5\u4e86\u7eb8\u5757\uff0c\u5c31\u82e5\u65e0\u5176\u4e8b\u5730\u8d70\u4e86\u5f00\u53bb\uff0c\u7f2a\u6587\u5374\u4ecd\u7136\u5728\u5ba2\u6808\u95e8\u53e3\u89c2\u671b\u7740\uff0c\u76ee\u5149\u3000\u8f6c\u52a8 \u5904\uff0c\u5fc3\u91cc\u4e0d\u7981\u53c8\u731b\u5730\u8df3\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u3000\u539f\u6765\u90a3\u84dd\u886b\u4eba\u4e0d\u77e5\u4f55\u65f6\u4e5f\u8d70\u4e86\u8fc7\u6765\uff0c\u53c8\u671b\u7740\u7f2a\u6587\u5fae\u5fae\u4e00\u7b11\uff0c\u7f2a\u6587\u5fc3\u91cc\u8d8a\u5947\u602a\uff0c\u3000\u773c\u91cc \u5374\u4e5f\u5c31\u8d8a\u4e0d\u6562\u518d\u671d\u8fd9\u4eba\u6253\u91cf\u3002 \u3000\u3000\u3000\u4ed6\u4f4e\u4e0b\u5934\uff0c\u8f6c\u5934\u8d70\u8fdb\u5e97\uff0c\u518d\u6084\u6084\u56de\u5934\u53bb\u671b\uff0c\u90a3\u84dd\u886b\u4eba\u5374\u5df2\u8d70\u5f97\u4e0d\u77e5\u53bb\u5411\u4e86\u3002 \u3000\u3000\u3000\u6bdb\u6587\u742a\u4e00\u7761\u4e0b\u53bb\uff0c\u5c31\u50cf\u662f\u6b7b\u4e86\u4e00\u6837\uff0c\u8fd8\u6ca1\u5230\u620c\u65f6\u5c31\u7761\u8d77\uff0c\u6b64\u523b\u5df2\u8fc7\u4e86\u5b50\u65f6\u4e86\uff0c\u3000\u5979\u4ecd \u7136\u534a\u70b9\u4e5f\u6ca1\u6709\u8981\u9192\u6765\u7684\u610f\u601d\u3002 \u3000\u3000\u3000\u4f46\u90a3\u539f\u5148\u4e00\u76f4\u6253\u5475\u6b20\uff0c\u4f38\u61d2\u8170\u7684\u7f2a\u6587\u5462\uff1f\u6b64\u523b\u5374\u4e00\u70b9\u513f\u5026\u5bb9\u4e5f\u6ca1\u6709\uff0c\u62db\u547c\u5e97\u4f19\u3000\uff0c\u9001 \u6765\u9152\u996d\uff0c\u4ed6\u4e00\u4e2a\u81ea\u659f\u81ea\u996e\uff0c\u559d\u4e86\u4e24\u676f\u70ed\u9152\uff0c\u53c8\u5403\u4e86\u4e9b\u996d\uff0c\u5c31\u659c\u501a\u5728\u5e8a\u4e0a\uff0c\u9759\u9759\u3000\u5730\u60f3\u7740\u5fc3 \u4e8b\u3002 \u3000\u3000\u3000\u4ed6\u5fc3\u91cc\u60f3\u7740\u7684\u7a76\u7adf\u662f\u4ec0\u4e48\u54e9\uff1f\u8fd9\u5f53\u7136\u6ca1\u6709\u4eba\u77e5\u9053\uff0c\u53ea\u662f\u4ed6\u7684\u9762\u5bb9\uff0c\u6b64\u523b\u5374\u662f\u5ffd\u3000\u800c\u60b2 \u6006\uff0c\u5ffd\u800c\u6124\u6012\uff0c\u6709\u65f6\u5374\u53c8\u9690\u542b\u7b11\u610f\uff0c\u751f\u50cf\u662f\u5728\u4e3a\u81ea\u5df1\u6240\u505a\u7684\u4e00\u4e9b\u4e8b\u5f97\u610f\u7740\uff0c\u4f46\u3000\u8fc7\u4e0d\u4e00\u4f1a\uff0c \u4ed6\u53c8\u4f1a\u76b1\u8d77\u7709\u6765\u3002 \u3000\u3000\u3000\u4e8c\u66f4\u6572\u8fc7\uff0c\u5e97\u4f19\u5728\u5916\u9762\u8d70\u6765\u8d70\u53bb\uff0c\u95e9\u5e97\u95e8\uff0c\u7184\u7089\u706b\uff0c\u8fc7\u4e0d\u4e00\u4f1a\uff0c\u8fd9\u4e9b\u58f0\u97f3\u5c31\u5b8c\u3000\u5168\u6ca1 \u6709\u4e86\uff0c\u53ea\u5269\u4e0b\u4e00\u53ea\u91ce\u732b\uff0c\u5728\u5c4b\u9876\u4e0a\u53eb\u7740\u6625\u3002 \u3000\u3000\u3000\u4f46\u7f2a\u6587\u5374\u4ecd\u672a\u7761\u7740\uff0c\u591c\u8d8a\u9759\uff0c\u4ed6\u7684\u601d\u6f6e\u5c31\u5954\u6d41\u5f97\u66f4\u5389\u5bb3\u3002 \u3000\u3000\u3000\u4f46\u5916\u9762\u7684\u91ce\u732b\u53eb\u5f97\u4e5f\u8d8a\u6765\u8d8a\u5389\u5bb3\uff0c\u800c\u4e14\u521a\u597d\u5728\u7f2a\u6587\u7684\u623f\u9876\u4e0a\uff0c\u7f2a\u6587\u76b1\u7740\u7709\uff0c\u4ece\u3000\u5e8a\u4e0a \u7ad9\u4e86\u8d77\u6765\uff0c\u8d70\u5230\u7a97\u53e3\uff0c\u63a8\u5f00\u7a97\u5b50\u56db\u4e0b\u4e00\u671b\uff0c\u5916\u9762\u662f\u4e00\u6761\u957f\u957f\u7684\u9662\u5b50\uff0c\u9662\u5b50\u91cc\u79cd\u3000\u7740\u4e00\u4e9b\u82b1\uff0c \u82b1\u90fd\u5f00\u4e86\uff0c\u88ab\u6708\u534e\u4e00\u6620\uff0c\u88ab\u6625\u98ce\u4e00\u5439\uff0c\u5c31\u50cf\u662f\u4e00\u4e2a\u4e2a\u7f8e\u5973\u4f3c\u7684\uff0c\u5728\u66fc\u66fc\u3000\u800c\u821e\u3002 \u3000\u3000\u3000\u9662\u5b50\u7684\u90a3\u4e00\u8fb9\u662f\u4e00\u9053\u5899\uff0c\u6b64\u523b\u8fd9\u9662\u5b50\u91cc\u9759\u6084\u6084\u7684\uff0c\u4f46\u5c4b\u9876\u4e0a\u7684\u732b\u53eb\u5f97\u592a\u70e6\u4eba\u2014\u3000\u2014\u7f2a \u6587\u76ee\u5149\u56db\u4e0b\u518d\u641c\u5bfb\u4e00\u4e0b\uff0c\u786e\u5b9a\u56db\u4e0b\u7684\u786e\u65e0\u4eba\u4e4b\u540e\uff0c\u8fd9\u201c\u5f31\u4e0d\u7981\u98ce\u7684\u4e66\u751f\u201d\uff0c\u5fae\u3000\u64a9\u957f\u886b\uff0c\u8eab \u5f62\u4e00\u52a8\uff0c\u7adf\u7136\u7a7f\u7a97\u800c\u51fa\u3002 \u3000\u3000\u3000\u4ed6\u7684\u8eab\u5f62\uff0c\u5c31\u50cf\u4e00\u7f15\u8f7b\u70df\u4f3c\u7684\uff0c\u5189\u5189\u98de\u8d77\uff0c\u5728\u7a7a\u4e2d\u8f7b\u8f7b\u4e00\u62e7\u8eab\uff0c\u4e0d\u85c9\u4efb\u4f55\u51ed\u85c9\u3000\uff0c\u5c31 \u60a0\u7136\u53d8\u4e86\u65b9\u5411\uff0c\u8f7b\u5de7\u5730\u843d\u5728\u5c4b\u9876\u4e0a\u3002 \u3000\u3000\u3000\u91ce\u732b\u7684\u4e24\u53ea\u78a7\u7eff\u7684\u773c\u775b\uff0c\u671b\u4e86\u4ed6\u4e00\u773c\uff0c\u4f3c\u4e4e\u4e5f\u88ab\u4ed6\u8fd9\u79cd\u8db3\u4ee5\u60ca\u4e16\u9a87\u4fd7\u7684\u8f7b\u8eab\u529f\u3000\u592b\u9a87 \u4f4f\u4e86\uff0c\u7adf\u8e72\u4f0f\u5728\u90a3\u91cc\uff0c\u6ca1\u6709\u8dd1\u5f00\u3002 \u3000\u3000\u3000\u7f2a\u6587\u4e0d\u5e26\u534a\u5206\u58f0\u606f\u5730\u5728\u74e6\u9762\u4e0a\u8d70\u4e86\u4e24\u6b65\uff0c\u671d\u90a3\u91ce\u732b\u4e00\u62c2\u8896\u5b50\uff0c\u90a3\u91ce\u732b\u201c\u54aa\u9e23\u201d\u3000\u4e00 \u58f0\uff0c\u7a9c\u8fc7\u5c4b\u810a\uff0c\u8fdc\u8fdc\u5730\u8df3\u8d70\u4e86\u3002 \u3000\u3000\u3000\u7f2a\u6587\u5fae\u7b11\u4e00\u4e0b\uff0c\u7a81\u5730\u5c4b\u810a\u90a3\u8fb9\uff0c\u4e5f\u4f20\u6765\u4e00\u4e1d\u8f7b\u7b11\uff0c\u4e00\u4eba\u8f7b\u58f0\u8bf4\u9053\uff1a\u201c\u5144\u53f0\u597d\u4fca\u3000\u7684\u8eab \u624b\u3002\u201d \u3000\u3000\u3000\u7f2a\u6587\u7acb\u523b\u4e3a\u4e4b\u5927\u60ca\uff0c\u5fae\u4e00\u9519\u638c\uff0c\u8eab\u8eaf\u5f80\u524d\u4e00\u63a2\uff0c\u6c89\u58f0\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u3000\u5c4b\u810a\u540e\u53c8\u4f20\u6765\u4e00\u58f0\u8f7b\u7b11\uff0c\u4e00\u4e2a\u9ed1\u8863\u4eba\u731b\u5730\u957f\u8eab\u800c\u8d77\uff0c\u53cc\u81c2\u4e00\u5f20\uff0c\u5c31\u50cf\u4e00\u679d\u7bad\u4f3c\u3000\u7684\u7a9c \u51fa\u56db\u4e08\u8fdc\u8fd1\uff0c\u518d\u4e00\u4e2a\u8d77\u843d\uff0c\u5c31\u60f3\u5f80\u9ed1\u6697\u4e2d\u9038\u53bb\uff0c\u7f2a\u6587\u884c\u85cf\u5df2\u9732\uff0c\u7109\u80af\u8ba9\u8fd9\u4e2a\u4eba\u3000\u5c31\u6b64\u4e00\u5b9a\uff0c \u5c31\u5728\u90a3\u9ed1\u8863\u4eba\u957f\u8eab\u6b32\u53bb\u7684\u65f6\u5019\uff0c\u4ed6\u4e5f\u6251\u4e86\u4e0a\u53bb\uff0c\u4f4e\u53f1\u9053\uff1a\u201c\u597d\u670b\u53cb\uff0c\u7ed9\u3000\u6211\u7559\u4e0b\u6765\u3002\u201d \u3000\u3000\u3000\u4f46\u90a3\u9ed1\u8863\u4eba\u8eab\u6cd5\u51fa\u4e4e\u5bfb\u5e38\u7684\u5feb\uff0c\u7f2a\u6587\u521a\u6251\u8fd1\u53bb\uff0c\u90a3\u4eba\u5df2\u63a0\u8d70\uff0c\u7f2a\u6587\u6beb\u4e0d\u8fdf\u7591\u5730\u3000\u4e00\u62e7 \u8eab\uff0c\u8eab\u5f62\u6beb\u672a\u4f5c\u52bf\uff0c\u4e5f\u63a0\u51fa\u56db\u4e08\u5f00\u5916\u3002 \u3000\u3000\u3000\u4e24\u4eba\u7684\u8f7b\u529f\u7adf\u7136\u597d\u5230\u6beb\u5dc5\uff0c\u51e0\u4e2a\u8d77\u843d\uff0c\u5df2\u98de\u63a0\u4e86\u5341\u4f59\u5bb6\u4eba\u7684\u5c4b\u810a\uff0c\u7f2a\u6587\u5fc3\u4e2d\u5fae\u3000\u51db\uff0c \u5fd6\u9053\uff1a\uff1a\u2018\u8fd9\u4eba\u597d\u5feb\u7684\u8eab\u624b\uff0c\u82e5\u662f\u6bdb\u81ec\u4e00\u4f19\uff0c\u5012\u7684\u786e\u53ef\u8651\uff01\u201d\u4e00\u5ff5\u81f3\u6b64\uff0c\u811a\u4e0b\u3000\u8d8a\u53d1\u52a0\u52b2\uff0c \u7acb\u5fc3\u8981\u8d76\u4e0a\u8fd9\u4eba\uff0c\u5c06\u4ed6\u5e9f\u5728\u81ea\u5df1\u624b\u4e0b\u3002\u4f46\u662f\u8fd9\u9ed1\u8863\u4eba\u8d77\u6b65\u5728\u5148\uff0c\u672c\u6765\u5df2\u3000\u9886\u5148\u4e86\u4e03\u3001\u516b\u4e08\u8ddd \u79bb\uff0c\u7f2a\u6587\u8fd9\u4e00\u52a0\u52b2\uff0c\u4e24\u4eba\u4e4b\u95f4\u7684\u8ddd\u79bb\u867d\u5df2\u6e10\u6e10\u7f29\u77ed\uff0c\u5374\u4ecd\u7136\u6709\u7740\u3000\u56db\u3001\u4e94\u4e08\u8fdc\u8fd1\u3002\u7f2a\u6587\u8fd9\u91cc \u6697\u60ca\u8fd9\u9ed1\u8863\u4eba\u8eab\u6cd5\u4e4b\u5feb\uff0c\u90a3\u9ed1\u8863\u4eba\u53c8\u4f55\u5c1d\u4e0d\u5728\u60ca\u5f02\u7f2a\u6587\u7684\u3000\u8f7b\u529f\uff0c\u8fd9\u5e74\u65b9\u5f31\u51a0\u7684\u5c11\u5e74\u7adf\u6709\u7740 \u5982\u6b64\u529f\u529b\uff0c\u82e5\u975e\u4eb2\u76ee\u6240\u89c1\uff0c\u8bf4\u51fa\u53bb\uff0c\u4efb\u662f\u8c01\u4e5f\u4e0d\u4f1a\u3000\u76f8\u4fe1\uff0c\u56e0\u4e3a\u8fd9\u7b80\u76f4\u592a\u4e0d\u53ef\u601d\u8bae\u4e86\u3002\u4e24\u4eba \u661f\u98de\u4e38\u6cfb\uff0c\u77ac\u606f\u4e4b\u95f4\uff0c\u5df2\u63a0\u51fa\u5e02\u533a\uff0c\u524d\u9762\u662f\u3000\u4e00\u7247\u7a7a\u5730\uff0c\u7a7a\u5730\u524d\u9ed1\u9edd\u9edd\u7684\u4e00\u5927\u7247\uff0c\u50cf\u662f\u4e00\u4e2a \u9887\u4e3a\u7e41\u5bc6\u7684\u6811\u6797\u5b50\u3002\u7f2a\u6587\u5fc3\u91cc\u8d8a\u53d1\u7740\u3000\u6025\uff0c\u77e5\u9053\u81ea\u5df1\u82e5\u8ba9\u8fd9\u4eba\u4e00\u94bb\u5f80\u201c\u9752\u7eb1\u5e10\u201d\uff0c\u90a3\u4e48\u81ea\u5df1 \u5c31\u518d\u4e5f\u522b\u60f3\u6293\u7740\u4ed6\u4e86\u3002\u6b64\u523b\u90ca\u3000\u91ce\u65e0\u4eba\uff0c\u7f2a\u6587\u4e5f\u5c31\u4e0d\u518d\u987e\u5fcc\uff0c\u4e00\u9762\u52a0\u6025\u98de\u63a0\uff0c\u4e00\u9762\u53f1\u9053\uff1a \u201c\u597d\u670b\u53cb\u4f55\u5fc5\u85cf\u5934\u9732\u5c3e\u7684\u3000\uff0c\u5f7c\u6b64\u90fd\u662f\u7537\u5b50\u6c49\uff0c\u6709\u4ec0\u4e48\u4e8b\u4e0d\u59a8\u5f53\u9762\u8c08\u8c08\uff0c\u670b\u53cb\u4f60\u8981\u662f\u518d\u5982 \u6b64\uff0c\u53ef\u5c31\u522b\u602a\u6211\u4e0d\u5ba2\u3000\u6c14\u4e86\u3002\u201d \u3000\u3000\u3000\u90a3\u9ed1\u8863\u4eba\u5728\u524d\u9762\u5374\u54c8\u54c8\u4e00\u7b11\uff0c\u8eab\u5f62\u4ea6\u672a\u505c\u987f\uff0c\u4e00\u9762\u5374\u4e5f\u559d\u9053\uff1a\u201c\u5144\u53f0\u4f55\u5fc5\u5ba2\u6c14\u3000\uff0c\u82e5 \u8bf4\u85cf\u5934\u9732\u5c3e\u56db\u5b57\uff0c\u6050\u6015\u8fd8\u662f\u5144\u53f0\u9ad8\u660e\u54e9\uff01\u201d \u3000\u3000\u3000\u7f2a\u6587\u5fc3\u91cc\u6697\u9a82\u4e00\u58f0\uff0c\u5c31\u8fd9\u4e24\u53e5\u8bdd\u7684\u529f\u592b\uff0c\u90a3\u9ed1\u8863\u4eba\u8ddd\u79bb\u6811\u6797\u5df2\u8d8a\u6765\u8d8a\u8fd1\uff0c\u7f2a\u6587\u3000\u4e00\u7ffb \u8155\u5b50\uff0c\u4ece\u6000\u4e2d\u638f\u51fa\u4e00\u4ef6\u6697\u5668\u6765\uff0c\u4e00\u9762\u5389\u559d\u9053\uff1a\u201c\u670b\u53cb\uff0c\u4f60\u518d\u4e0d\u7ad9\u4f4f\uff0c\u6211\u59d3\u7f2a\u7684\u3000\u5c31\u8981\u53d1\u6697\u5668 \u4e86\u3002\u201d\u6b64\u65f6\u60c5\u51b5\u867d\u7136\u5982\u6b64\uff0c\u4f46\u4ed6\u5140\u81ea\u4e0d\u80af\u6697\u7bad\u4f24\u4eba\uff0c\u5148\u5c31\u559d\u4e86\u51fa\u6765\u3002 \u3000\u3000\u3000\u54ea\u77e5\u90a3\u4eba\u53c8\u54c8\u54c8\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u597d\u6781\uff0c\u597d\u6781\uff0c\u5c0f\u5f1f\u6b63\u8981\u8ba8\u6559\u8ba8\u6559\u5144\u53f0\u7684\u9ad8\u62db\uff0c\u800c\u3000\u4e14\u5c0f \u5f1f\u65e9\u5c31\u77e5\u9053\uff0c\u5144\u53f0\u53d1\u6697\u5668\u7684\u624b\u6cd5\uff0c\u9ad8\u4eba\u4e00\u7b49\uff0c\u5144\u53f0\u5c31\u8bf7\u52a8\u624b\u5427\u3002\u201d \u3000\u3000\u3000\u7f2a\u6587\u4e00\u76b1\u7709\uff0c\u968f\u7740\u8eab\u5f62\u52a8\u5904\uff0c\u53f3\u624b\u5f80\u5916\u4e00\u626c\uff0c\u767b\u65f6\u4e00\u7f15\u5c16\u98ce\uff0c\u6025\u52b2\u5730\u671d\u90a3\u9ed1\u8863\u3000\u4eba\u80cc \u540e\u6253\u53bb\uff0c\u5355\u51ed\u8155\u529b\u5c31\u80fd\u5c06\u6697\u5668\u6253\u51fa\u56db\u4e08\u5f00\u53bb\uff0c\u8fd9\u79cd\u624b\u6cd5\u6b63\u5982\u90a3\u9ed1\u8863\u4eba\u6240\u8bf4\uff0c\u7aef\u3000\u7684\u662f\u9ad8\u4eba\u4e00 \u7b79\u3002 \u3000\u3000\u3000\u90a3\u6697\u5668\u773c\u770b\u5c31\u8981\u6253\u5728\u9ed1\u8863\u4eba\u8eab\u4e0a\uff0c\u54ea\u77e5\u8fd9\u9ed1\u8863\u4eba\u201c\u563f\u201d\u5730\u4e00\u7b11\uff0c\u542c\u98ce\u8fa8\u4f4d\uff0c\u5934\u3000\u4e5f\u4e0d \u56de\uff0c\u624b\u8155\u4e00\u53cd\uff0c\u7adf\u7136\u4e5f\u6253\u51fa\u4e86\u4e00\u4ef6\u6697\u5668\u3002 \u3000\u3000\u3000\u53ea\u542c\u201c\u545b\u5577\u201d\u4e00\u58f0\u8f7b\u54cd\uff0c\u8fd9\u4e24\u4ef6\u6697\u5668\u7adf\u7136\u5728\u7a7a\u4e2d\u4e92\u51fb\u800c\u9e23\uff0c\u968f\u5374\u4e00\u9f50\u843d\u5728\u5730\u4e0a\u3000\uff0c\u800c \u90a3\u9ed1\u8863\u4eba\u53c8\u518d\u201c\u563f\u201d\u5730\u4e00\u58f0\u8f7b\u58f0\uff0c\u987f\u4f4f\u8eab\u5f62\uff0c\u8f7b\u8fc7\u5934\u6765\uff0c\u5e26\u7740\u4e00\u8138\u8be1\u5f02\u7684\u7b11\u5bb9\u3000\uff0c\u671b\u7740\u5df2\u7136 \u5982\u98de\u63a0\u6765\u7684\u7f2a\u6587\u3002 \u7b2c\u5341\u4e8c\u7ae0>> \u53e4\u9f99\u300a\u6e58\u5983\u5251\u300b \u7b2c\u5341\u4e8c\u7ae0 \u3000\u3000\u3000\u6b64\u523b\u661f\u5149\u95ea\u70c1\uff0c\u6708\u5149\u768e\u6d01\uff0c\u98ce\u5439\u957f\u8349\uff0c\u866b\u9e23\u6742\u6811\uff0c\u6b63\u662f\u5927\u597d\u826f\u5bb5\uff0c\u661f\u6708\u4e4b\u4e0b\uff0c\u3000\u7f2a\u6587 \u95ea\u76ee\u800c\u671b\uff0c\u53ea\u89c1\u5728\u524d\u9762\u7eb5\u8dc3\u5982\u98de\u7684\u9ed1\u8863\u4eba\u7a81\u5730\u4e00\u53cd\u624b\uff0c\u6253\u51fa\u4e00\u9053\u91d1\u5149\uff0c\u7adf\u4e0d\u504f\u3000\u4e0d\u659c\u5730\u51fb\u5728 \u81ea\u5df1\u5411\u4ed6\u6253\u53bb\u7684\u6697\u5668\u4e0a\uff0c\u53ea\u542c\u201c\u545b\u5577\u201d\u4e00\u58f0\u8f7b\u54cd\uff0c\u4e24\u9053\u91d1\u5149\uff0c\u4ff1\u90fd\u843d\u5728\u3000\u5730\u4e0a\u3002 \u3000\u3000\u3000\u7f2a\u6587\u5fc3\u4e2d\u4e00\u6014\uff0c\u786c\u751f\u751f\u5c06\u81ea\u5df1\u5982\u98de\u63a0\u53bb\u7684\u8eab\u5f62\uff0c\u500f\u7136\u987f\u4f4f\uff0c\u5fc3\u5934\u6697\u9a87\u9053\uff1a\u201c\u6b64\u3000\u4eba\u5934 \u4e5f\u4e0d\u56de\uff0c\u7adf\u5c31\u5c06\u6211\u53d1\u51fa\u7684\u6697\u5668\u51fb\u843d\uff0c\u8eab\u624b\u7aef\u7684\u60ca\u4eba\uff0c\u800c\u4ed6\u53d1\u51fa\u7684\u6697\u5668\uff0c\u5c45\u7136\u4ea6\u3000\u4f5c\u91d1\u8272\uff0c\u96be \u9053\u6b64\u4eba\u771f\u7684\u662f\u4ed6\uff1f\u201d \u3000\u3000\u3000\u987b\u77e5\u4ed6\u5e74\u7eaa\u867d\u8f7b\uff0c\u5374\u662f\u4e00\u751f\u51fa\u6765\uff0c\u4fbf\u5f00\u59cb\u4e60\u6b66\uff0c\u6559\u4ed6\u6b66\u529f\u7684\u4eba\uff0c\u5374\u53c8\u90fd\u662f\u5929\u4e0b\u3000\u6b66\u6797 \u4e2d\u9876\u9876\u7edd\u9876\u7684\u9ad8\u624b\uff0c\u5e38\u4eba\u8981\u662f\u5f97\u4e00\u4e3a\u5e08\uff0c\u4fbf\u8db3\u7ec8\u8eab\u53d7\u7528\uff0c\u4ed6\u5fc3\u4e2d\u81ea\u77e5\uff0c\u82b8\u82b8\u6b66\u3000\u6797\u4e2d\uff0c\u98ce\u5c18 \u4fa0\u58eb\u867d\u591a\uff0c\u4f46\u8981\u627e\u4e00\u4e2a\u50cf\u81ea\u5df1\u8fd9\u79cd\u8eab\u624b\u7684\uff0c\u5374\u5e76\u4e0d\u591a\u3002 \u3000\u3000\u3000\u82e5\u8bba\u4ee5\u201c\u542c\u98ce\u8fa8\u4f4d\u201d\u4e4b\u6280\uff0c\u5c06\u522b\u4eba\u6697\u5668\u51fb\u843d\u7684\u529f\u592b\uff0c\u672c\u65e0\u60ca\u4eba\u4e4b\u5904\uff0c\u4f46\u7f2a\u6587\u81ea\u3000\u77e5\u81ea \u5df1\u624b\u4e2d\u53d1\u51fa\u7684\u6697\u5668\uff0c\u5176\u52b2\u9053\u548c\u53bb\u52bf\uff0c\u90fd\u7edd\u4e0d\u662f\u4e00\u822c\u6697\u5668\u540d\u624b\u6240\u80fd\u4f01\u53ca\u7684\uff0c\u800c\u6b64\u3000\u4eba\u5374\u4ece\u5bb9\u51fb \u843d\uff0c\u662f\u4ee5\u7f2a\u6587\u65b9\u81ea\u5fc3\u4e2d\u6697\u9a87\uff0c\u4e0d\u77e5\u9053\u5bbf\u8fc1\u57ce\u4e2d\uff0c\u4f55\u6765\u6b64\u6b66\u6797\u9ad8\u624b\uff1f \u3000\u3000\u3000\u62ac\u76ee\u4e00\u671b\uff0c\u53ea\u89c1\u8fd9\u6ee1\u8eab\u7384\u886b\u7684\u591c\u884c\u4eba\u6b63\u5728\u542b\u7b11\u671b\u7740\u81ea\u5df1\uff0c\u4e24\u9053\u659c\u98de\u5165\u9b13\u7684\u5251\u7709\u3000\u5f53 \u4e2d\uff0c\u6c9f\u7eb9\u5b9b\u7136\uff0c\u9762\u76ee\u4f9d\u7a00\u76f8\u8bc6\uff0c\u7adf\u662f\u81ea\u5df1\u65e5\u95f4\u6240\u9047\u7684\u90a3\u84dd\u886b\u4e66\u751f\u3002\u4ed4\u7ec6\u4e00\u770b\uff0c\u53ea\u3000\u89c1\u4ed6\u8eab\u4e0a \u7a7f\u7740\u7684\u4e5f\u4ecd\u662f\u90a3\u4e00\u88ad\u84dd\u886b\uff0c\u4e0b\u6446\u6396\u5728\u8170\u95ee\u7684\u4e1d\u7ee6\u4e0a\uff0c\u591c\u8272\u4e4b\u4e2d\uff0c\u770b\u4e0d\u751a\u6e05\u3000\uff0c\u7adf\u5c06\u84dd\u886b\u5f53\u505a \u9ed1\u8863\u3002 \u3000\u3000\u3000\u90a3\u84dd\u886b\u4e66\u751f\u51e4\u76ee\u4e4b\u4e2d\uff0c\u68f1\u68f1\u751f\u5149\uff0c\u7a81\u5411\u7f2a\u6587\u5f53\u5934\u4e00\u63d6\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6df1\u591c\u6253\u6270\u3000\uff0c\u5b9e \u662f\u65e0\u72b6\uff0c\u5510\u7a81\u4e4b\u5904\uff0c\u8fd8\u671b\u5144\u53f0\u89c1\u8c05\u3002\u201d \u3000\u3000\u3000\u7f2a\u6587\u76ee\u5149\u4e00\u8f6c\uff0c\u4ea6\u81ea\u6717\u58f0\u7b11\u9053\uff1a\u201c\u6253\u6270\u4e24\u5b57\uff0c\u5b9e\u4e0d\u6562\u5f53\uff0c\u5c0f\u53ef\u867d\u7136\u611a\u9c81\uff0c\u4f46\u4eca\u3000\u6668\u4e00 \u7779\u5144\u53f0\u4e4b\u9762\uff0c\u4fbf\u77e5\u5144\u53f0\u5fc5\u662f\u9ad8\u4eba\uff0c\u53ea\u662f\u2014\u2014\u201d\u4ed6\u8bed\u58f0\u4e00\u987f\uff0c\u5251\u7709\u5fae\u5fae\u4e00\u8f69\uff0c\u63a5\u3000\u9053\uff1a\u201c\u5144\u53f0 \u591c\u6df1\u5ba0\u53ec\uff0c\u5374\u4e0d\u77e5\u6709\u4f55\u89c1\u6559\uff1f\u201d \u3000\u3000\u3000\u90a3\u84dd\u886b\u4e66\u751f\u5fae\u5fae\u4e00\u7b11\uff0c\u6f47\u6d12\u524d\u884c\uff0c\u4e00\u9762\u9053\uff1a\u201c\u5144\u53f0\u4eba\u4e2d\u9f99\u51e4\uff0c\u5c0f\u53ef\u65e9\u5df2\u6709\u5fc3\u9ad8\u3000\u6500\uff0c \u53ea\u662f\u65e0\u7f18\u76f8\u8bc6\uff0c\u53ea\u5f97\u51fa\u6b64\u4e0b\u7b56\u4e86\u3002\u201d\u811a\u6b65\u5fae\u987f\u5904\uff0c\u7f13\u7f13\u5f2f\u4e0b\u8170\u53bb\uff0c\u4f38\u624b\u4e00\u63a2\uff0c\u3000\u7f2a\u6587\u5251\u7709\u4e00 \u76b1\uff0c\u7a81\u5730\u62a2\u51fa\u5982\u98ce\uff0c\u75be\u4f38\u53cc\u638c\uff0c\u54ea\u77e5\u90a3\u84dd\u886b\u4e66\u751f\u6717\u58f0\u5927\u7b11\u4e2d\uff0c\u8eab\u5f62\u500f\u7136\u3000\u540e\u9000\u4e09\u5c3a\uff0c\u4f38\u51fa\u624b \u638c\uff0c\u638c\u4e2d\u5df2\u591a\u4e86\u4e24\u53e3\u4e00\u5f0f\u4e00\u6837\uff0c\u91d1\u5149\u8000\u76ee\u7684\u77ed\u5251\u3002 \u3000\u3000\u3000\u7f2a\u6587\u51fa\u624b\u7565\u8fdf\uff0c\u5374\u89c1\u81ea\u5df1\u5fc3\u4e2d\u60f3\u62fe\u7684\u4e1c\u897f\uff0c\u5df2\u88ab\u5bf9\u65b9\u62fe\u4e86\u8d77\u6765\uff0c\u5fc3\u4e2d\u4e0d\u7981\u53c8\u4e00\u3000\u51db\uff1a \u201c\u6b64\u4eba\u597d\u5feb\u7684\u8eab\u624b\u2014\u2014\u201d\u62ac\u5934\u4e00\u671b\uff0c\u90a3\u84dd\u886b\u4e66\u751f\u6b63\u5728\u5c06\u638c\u4e2d\u7684\u4e24\u53e3\u91d1\u5251\uff0c\u4e0d\u4f4f\u3000\u628a\u73a9\uff0c\u4e00\u9762 \u5fae\u5fae\u7b11\u9053\uff1a\u201c\u679c\u7136\u4e00\u6a21\u4e00\u6837\u2014\u2014\u201d\u8bed\u58f0\u672a\u4e86\uff0c\u7a81\u53c8\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u4f4e\u4f4e\u3000\u5ff5\u9053\uff1a\u201c\u4ee5\u8840\u8fd8 \u8840\uff0c\u4ee5\u8840\u8fd8\u8840\u2026\u2026\u201d\u624b\u638c\u4e00\u7ffb\uff0c\u5c06\u5176\u4e2d\u4e00\u53e3\u91d1\u5251\u7528\u4e24\u6307\u634f\u7740\u5251\u5c16\uff0c\u3000\u9012\u5230\u7f2a\u6587\u9762\u524d\uff0c\u6717\u7b11 \u9053\uff1a\u201c\u8fd9\u53e3\u5251\u60f3\u5fc5\u662f\u5144\u53f0\u7684\u4e86\uff0c\u54c8\u54c8\uff0c\u82e5\u975e\u4e0a\u9762\u7684\u8fd9\u51e0\u4e2a\u5b57\uff0c\u3000\u5c0f\u53ef\u8fd8\u771f\u5206\u8fa8\u4e0d\u51fa\u54e9\uff01\u201d \u3000\u3000\u3000\u6708\u5149\u4e4b\u4e0b\uff0c\u53ea\u89c1\u7f2a\u6587\u6e05\u4fca\u7684\u9762\u5e9e\u4e0a\uff0c\u6728\u7136\u6ca1\u6709\u4efb\u4f55\u8868\u60c5\uff0c\u5446\u5446\u5730\u671b\u7740\u4ed6\u624b\u4e0a\u8fd9\u3000\u53e3\u91d1 \u5251\uff0c\u601d\u7d22\u534a\u664c\uff0c\u7a81\u5730\u4ef0\u5929\u957f\u7b11\u8d77\u6765\uff0c\u9053\uff1a\u201c\u5144\u53f0\u60f3\u5fc5\u5c31\u662f\u540d\u4f20\u6b66\u6797\u7684\u91d1\u5251\u5927\u4fa0\u3000\u4e86\uff0c\u5c0f\u53ef\u95fb \u540d\u5df2\u4e45\uff0c\u5374\u4e0d\u60f3\u4eca\u65e5\u5f97\u89c1\u2014\u2014\u201d\u7f13\u7f13\u4f38\u51fa\u624b\u638c\uff0c\u4ea6\u7528\u62c7\u3001\u98df\u4e8c\u6307\uff0c\u634f\u7740\u3000\u5251\u67c4\uff0c\u4e24\u4eba\u9762\u4e0a\u867d \u7136\u4ff1\u662f\u7b11\u5bb9\u4e0d\u7edd\uff0c\u4f46\u5fc3\u4e2d\u5374\u5404\u5404\u5b58\u4e0b\u8861\u91cf\u5bf9\u65b9\u4e4b\u5fc3\uff0c\u6b64\u523b\u7adf\u90fd\u5c06\u3000\u5168\u8eab\u771f\u6c14\uff0c\u8d2f\u8db3\u53f3\u81c2\uff0c\u805a \u5728\u8fd9\u4e24\u6839\u624b\u6307\u4e0a\u3002 \u3000\u3000\u3000\u5239\u90a3\u4e4b\u95f4\uff0c\u53ea\u89c1\u8fd9\u53e3\u957f\u672a\u8fbe\u5c3a\u7684\u91d1\u8272\u5c0f\u5251\uff0c\u968f\u7740\u4ed6\u4e24\u4eba\u7684\u56db\u6839\u624b\u6307\uff0c\u8d8a\u6765\u8d8a\u957f\u3000\uff0c\u90a3 \u84dd\u886b\u4e66\u751f\u54c8\u54c8\u4e00\u7b11\uff0c\u7f29\u56de\u624b\u53bb\uff0c\u542b\u7b11\u8bf4\u9053\uff1a\u201c\u65e0\u602a\u6c5f\u6e56\u4f20\u8a00\uff0c\u90fd\u9053\u90a3\u91d1\u5251\u4fa0\u7684\u3000\u6b66\u529f\u8d8a\u6765\u8d8a \u9ad8\uff0c\u884c\u4e8b\u4e5f\u8d8a\u6765\u8d8a\u662f\u795e\u51fa\u9b3c\u6ca1\uff0c\u539f\u6765\u5374\u662f\u51fa\u81ea\u5144\u53f0\u624b\u7b14\uff0c\u5c0f\u53ef\u867d\u7136\u65e0\u5fc3\u3000\u63a0\u7f8e\uff0c\u4f46\u4eba\u8a00\u51ff \u51ff\uff0c\u5c0f\u53ef\u5374\u4e4b\u4e0d\u606d\uff0c\u4e5f\u53ea\u6709\u751f\u53d7\u4e86\u3002\u201d \u3000\u3000\u3000\u7f2a\u6587\u76ee\u5149\u6de1\u6de1\u4e00\u7779\u90a3\u53e3\u6b64\u523b\u5df2\u53d8\u6210\u4e00\u6761\u7ec6\u68cd\u7684\u201c\u91d1\u5251\u201d\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u5c0f\u53ef\u65b9\u624d\u3000\u672c\u81ea \u5947\u602a\uff0c\u8fd9\u5c0f\u5c0f\u7684\u5bbf\u8fc1\u57ce\u91cc\uff0c\u600e\u5730\u6709\u5982\u6b64\u9ad8\u624b\uff0c\u6b64\u523b\u624d\u77e5\u9053\u662f\u91d1\u5251\u5927\u4fa0\uff0c\u60f3\u5fc5\u662f\u3000\u9601\u4e0b\u542c\u5230\u6c5f \u6e56\u9053\u4e0a\uff0c\u6709\u4e86\u81ba\u54c1\uff0c\u662f\u4ee5\u4fbf\u8d76\u6765\u67e5\u770b\u67e5\u770b\u7684\u5427\uff01\u201d \u3000\u3000\u3000\u624b\u5fae\u4e00\u626c\uff0c\u638c\u4e2d\u4e4b\u201c\u5251\u201d\uff0c\u4fbf\u5df2\u8131\u624b\u98de\u53bb\uff0c\u201c\u5657\u201d\u5730\u4e00\u58f0\uff0c\u7adf\u6df1\u6df1\u63d2\u5165\u5730\u4e0b\uff0c\u3000\u53ea\u5269 \u4e0b\u4e00\u6bb5\u7a0d\u5177\u539f\u5f62\u7684\u201c\u5251\u67c4\u201d\uff0c\u4ecd\u5728\u5730\u9762\u4e0a\u4e0d\u4f4f\u5730\u98a4\u52a8\u3002 \u3000\u3000\u3000\u90a3\u84dd\u886b\u4e66\u751f\u5fae\u77a5\u4e00\u773c\uff0c\u9762\u4e0a\u7b11\u5bb9\uff0c\u5374\u4ecd\u672a\u53d8\uff0c\u7f13\u7f13\u7b11\u9053\uff1a\u201c\u5144\u53f0\u8fd9\u5374\u9519\u4e86\uff0c\u60f3\u3000\u5144\u53f0 \u5728\u6c5f\u6e56\u9053\u4e0a\uff0c\u4ee5\u201d\u91d1\u5251\u201d\u4e4b\u540d\uff0c\u66ff\u5929\u884c\u9053\uff0c\u6240\u505a\u6240\u4e3a\uff0c\u6b63\u662f\u5c0f\u53ef\u6240\u6b32\u884c\u800c\u672a\u53ca\u3000\u884c\u8005\uff0c\u5c0f\u53ef \u6b63\u6068\u4e0d\u5f97\u5982\u540c\u5144\u53f0\u8fd9\u822c\u2018\u81ba\u54c1\u2019\uff0c\u518d\u591a\u4e0a\u51e0\u4e2a\uff0c\u4e5f\u597d\u4e3a\u82b8\u82b8\u6c5f\u6e56\u4f38\u5f20\u4e00\u3000\u4e9b\u6b63\u4e49\uff0c\u4e3a\u83bd\u83bd\u6b66 \u6797\u7559\u5f97\u4e00\u4e9b\u516c\u9053\u2014\u2014\u201d\u7f2a\u6587\u9762\u5fae\u4e00\u7ea2\uff0c\u5fc3\u4e0b\u6697\u5fd6\uff1a\u201c\u4eba\u9053\u2018\u91d1\u5251\u3000\u4fa0\u2019\u662f\u4e2a\u6177\u6168\u78ca\u843d\u7684\u6c49 \u5b50\uff0c\u4eca\u65e5\u4e00\u89c1\uff0c\u679c\u771f\u540d\u4e0b\u65e0\u865a\uff0c\u6211\u5192\u540d\u884c\u4e8b\uff0c\u53c8\u590d\u6076\u8a00\u76f8\u52a0\u3000\uff0c\u4ed6\u975e\u4f46\u4e0d\u4ee5\u4e3a\u601c\uff0c\u8fd8\u5982\u6b64\u5bf9 \u5f85\u4e8e\u6211\u2014\u2014\u201d\u4e00\u5ff5\u81f3\u6b64\uff0c\u4e0d\u7981\u5bf9\u773c\u524d\u8fd9\u84dd\u886b\u4e66\u751f\u5927\u8d77\u3000\u597d\u611f\u3002 \u3000\u3000\u3000\u987b\u77e5\u4ed6\u5e7c\u906d\u5b64\u9732\uff0c\u8eab\u5177\u6df1\u4ec7\uff0c\u800c\u4ec7\u5bb6\u53ef\u90fd\u662f\u5f53\u4eca\u6c5f\u6e56\u4e2d\u7099\u624b\u53ef\u70ed\u7684\u4eba\u7269\uff0c\u7fbd\u515a\u3000\u904d\u53ca \u5929\u4e0b\uff0c\u4ed6\u81ea\u77e5\u81ea\u5df1\u867d\u56e0\u673a\u7f18\u51d1\u5de7\uff0c\u5e38\u4eba\u68a6\u5bd0\u96be\u6c42\u4e4b\u7269\uff0c\u81ea\u5df1\u5374\u6bcf\u6bcf\u5782\u624b\u800c\u5f97\uff0c\u3000\u4f46\u81ea\u5df1\u82e5\u8981 \u62a5\u5f97\u6df1\u4ec7\uff0c\u5374\u4ecd\u975e\u6613\u4e8b\u3002 \u3000\u3000\u3000\u662f\u4ee5\u4ed6\u5e73\u65e5\u884c\u4e8b\uff0c\u614e\u91cd\u65e0\u6bd4\uff0c\u552f\u6050\u884c\u85cf\u7834\u9732\uff0c\u88ab\u522b\u4eba\u8bc6\u5f97\u771f\u8c61\uff0c\u4ed6\u867d\u662f\u6027\u60c5\u4e2d\u3000\u4eba\uff0c \u4f46\u79cd\u79cd\u539f\u56e0\uff0c\u5374\u4f7f\u5f97\u4ed6\u5bf9\u4eba\u4eec\u90fd\u6709\u4e86\u63d0\u9632\u4e4b\u5fc3\uff0c\u662f\u4ee5\u4ed6\u5148\u524d\u5bf9\u8fd9\u84dd\u886b\u4e66\u751f\u7684\u6001\u3000\u5ea6\uff0c\u4fbf\u4e5f\u56e0 \u662f\u800c\u53d1\u3002 \u3000\u3000\u3000\u90a3\u84dd\u886b\u4e66\u751f\u4e00\u53cc\u51e4\u76ee\uff0c\u59cb\u7ec8\u51dd\u6ce8\u5728\u4ed6\u9762\u4e0a\uff0c\u661f\u6708\u4ea4\u6620\u4e4b\u4e0b\uff0c\u4ed6\u9762\u4e0a\u867d\u4ecd\u4e00\u65e0\u8868\u3000\u60c5\uff0c \u4f46\u6708\u5149\u95ea\u70c1\uff0c\u5374\u663e\u89c1\u4ed6\u5fc3\u4e2d\u751a\u4e0d\u5e73\u5b9a\u3002 \u3000\u3000\u3000\u4e24\u4eba\u76ee\u5149\u76f8\u9047\uff0c\u7f2a\u6587\u5fc3\u4e2d\u6697\u53f9\u4e00\u58f0\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u5c0f\u53ef\u8eab\u4e16\u60e8\u75db\uff0c\u53c8\u591a\u96be\u8a00\u4e4b\u9690\u3000\uff0c\u5192 \u72af\u4e4b\u5904\uff0c\u5144\u53f0\u5fc5\u53ef\u89c1\u8c05\u2014\u2014\u201d\u4ed6\u5fae\u5fae\u4e00\u987f\uff0c\u53c8\u9053\uff0c\u201c\u5144\u53f0\u78ca\u843d\u7537\u5b50\uff0c\u6177\u6168\u82f1\u96c4\u3000\uff0c\u65e2\u6b32\u6298\u8282 \u4e0b\u4ea4\uff0c\u5c0f\u53ef\u6b63\u662f\u6c42\u4e4b\u4e0d\u5f97\uff0c\u65e5\u540e\u5982\u6709\u673a\u7f18\uff0c\u8fd8\u671b\u4e0d\u541d\u8d50\u6559\u3002\u201d\u8a00\u4e0b\u4e4b\u610f\u3000\uff0c\u5374\u662f\u4eca\u65e5\u5c31\u6b64\u522b \u8fc7\u4e86\u3002 \u3000\u3000\u3000\u4f46\u90a3\u84dd\u886b\u4e66\u751f\u5374\u751f\u50cf\u5168\u7136\u4e0d\u61c2\u4ed6\u8bdd\u91cc\u7684\u542b\u610f\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c0f\u53ef\u65b9\u6b63\uff0c\u590d\u3000\u59d3\u7aef \u6728\uff0c\u5374\u5230\u6b64\u523b\u8fd8\u672a\u8bf7\u6559\u9601\u4e0b\u7684\u9ad8\u59d3\u5927\u540d\u5462\uff01\u201d \u3000\u3000\u3000\u54ea\u77e5\u4ed6\u8bed\u58f0\u65b9\u843d\uff0c\u7f2a\u6587\u7adf\u7a81\u5730\u9762\u8272\u4e00\u6c89\uff0c\u8f6c\u8eab\u6b32\u53bb\uff0c\u8fd9\u84dd\u886b\u4e66\u751f\u795e\u8272\u4e5f\u4e0d\u7981\u4e3a\u3000\u4e4b\u4e00 \u53d8\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u597d\u5fc3\u7ed3\u7eb3\u4e8e\u4f60\uff0c\u4f60\u53c8\u4f55\u82e6\u505a\u51fa\u8fd9\u7b49\u9762\u76ee\u6765\uff1f\u201d\u4ed6\u81ea\u4e0d\u77e5\u8fd9\u7f2a\u6587\u3000\u8eab\u4e16\u9690\u79d8\uff0c \u6709\u4eba\u95ee\u4ed6\u59d3\u540d\uff0c\u6b63\u662f\u72af\u4e86\u4ed6\u7684\u5927\u5fcc\uff0c\u4e00\u5ff5\u81f3\u6b64\uff0c\u51b7\u54fc\u4e00\u58f0\uff0c\u8eab\u5f62\u52a8\u5904\uff0c\u3000\u7adf\u7a81\u5730\u63a0\u5230\u7f2a\u6587\u524d \u9762\uff0c\u53cc\u81c2\u5fae\u5f20\uff0c\u62e6\u4f4f\u53bb\u8def\u3002 \u3000\u3000\u3000\u7f2a\u6587\u9762\u8272\u53c8\u662f\u4e00\u6c89\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u5144\u53f0\u610f\u6b32\u4f55\u4e3a\uff1f\u201d \u3000\u3000\u3000\u8fd9\u84dd\u886b\u4e66\u751f\u7aef\u6728\u65b9\u6b63\u5251\u7709\u4e00\u8f69\uff0c\u968f\u53c8\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u5c0f\u53ef\u8bf7\u6559\u5144\u53f0\u59d3\u540d\uff0c\u5144\u3000\u53f0\u600e \u5730\u5982\u6b64\u76f8\u5f85\uff0c\u96be\u9053\u5c0f\u53ef\u5c31\u771f\u7684\u9ad8\u6500\u4e0d\u4e0a\u5417\uff1f\u201d\u867d\u7136\u4ecd\u662f\u542b\u7b11\u800c\u8a00\uff0c\u4f46\u8bed\u6c14\u4e4b\u4e2d\u3000\uff0c\u5374\u5df2\u8fdc\u975e \u65b9\u624d\u4e4b\u5ba2\u6c14\u3002 \u3000\u3000\u3000\u7f2a\u6587\u82cd\u767d\u7684\u9762\u8272\uff0c\u500f\u7136\u7531\u767d\u8f6c\u7ea2\uff0c\u53c8\u968f\u5373\u7531\u7ea2\u8f6c\u767d\uff0c\u4f3c\u4e4e\u5728\u5f3a\u5fcd\u7740\u5fc3\u4e2d\u6012\u6c14\uff0c\u3000\u6c89\u58f0 \u9053\uff1a\u201c\u5c0f\u53ef\u4e0e\u5144\u53f0\u4e00\u4e0d\u6cbe\u4eb2\uff0c\u4e8c\u4e0d\u5e26\u6545\uff0c\u4e09\u65e0\u4ec7\u6028\uff0c\u53ef\u8bf4\u662f\u5168\u65e0\u74dc\u845b\uff0c\u5144\u53f0\u5374\u3000\u6041\u5730\u76d8\u67e5\u5c0f \u53ef\u59d3\u540d\u6765\u5386\u4f5c\u4ec0\uff1f\u201d \u3000\u3000\u3000\u4ed6\u8bed\u58f0\u4e00\u987f\uff0c\u51b7\u7b11\u4e24\u58f0\u53c8\u9053\uff0c\u201c\u4f55\u51b5\u5c0f\u53ef\u7eb5\u7136\u7528\u7684\u6697\u5668\uff0c\u4ea6\u662f\u91d1\u5251\uff0c\u4f46\u5374\u4ea6\u4ece\u3000\u672a\u5192 \u8fc7\u2018\u91d1\u5251\u5927\u4fa0\u2019\u7684\u540d\u58f0\uff0c\u96be\u9053\u666e\u5929\u4e4b\u4e0b\uff0c\u5c31\u53ea\u9601\u4e0b\u4e00\u4eba\u80fd\u7528\u8fd9\u91d1\u5251\u505a\u6697\u5668\u4e0d\u6210\u3000\uff1f\u201d \u3000\u3000\u3000\u7aef\u6728\u65b9\u6b63\u6014\u4e86\u4e00\u6014\uff0c\u7acb\u5373\u8f69\u7709\u7b11\u9053\uff1a\u201c\u6781\u662f\uff0c\u6781\u662f\uff0c\u60f3\u90a3\u2018\u91d1\u5251\u2019\u4e00\u7269\uff0c\u4eba\u4eba\u3000\u7686\u53ef \u7528\u5f97\uff0c\u53c8\u5e76\u975e\u6211\u7aef\u6728\u65b9\u6b63\u4e00\u4eba\u80fd\u7528\u4e4b\u7269\uff0c\u53ea\u662f\u4e00\u201d\u4ed6\u7b11\u5bb9\u4e00\u655b\uff1a\u201c\u8fd9\u2018\u81ba\u54c1\uff0c\u3000\u4e8c\u5b57\uff0c\u5374\u662f \u51fa\u81ea\u5144\u53f0\u4e4b\u53e3\uff0c\u53c8\u4e0d\u662f\u533a\u533a\u5728\u4e0b\u8bf4\u51fa\u7684\u3002\u201d\u6b64\u756a\u7f2a\u6587\u5374\u4e0d\u7981\u4e3a\u4e4b\u4e00\u6014\uff0c\u3000\u5374\u542c\u8fd9\u91d1\u5251\u4fa0\u7aef\u6728 \u65b9\u6b63\u63a5\u53e3\u53c8\u9053\uff1a\u201c\u5144\u53f0\u82e5\u8bf4\u4e0e\u5c0f\u53ef\u4e00\u65e0\u74dc\u845b\uff0c\u6b64\u8bdd\u5c0f\u53ef\u5374\u4e5f\u4e0d\u6562\u3000\u82df\u540c\u3002\u201d \u3000\u3000\u3000\u7f2a\u6587\u76ee\u5149\u4e00\u51db\uff0c\u5389\u58f0\u9053\uff1a\u201c\u5728\u4e0b\u4e0e\u5144\u53f0\u6709\u4ec0\u722a\u845b\uff0c\u96be\u9053\u5144\u53f0\u4e5f\u662f\u4e0e\u90a3\u2014\u2014\u201d\u8bed\u3000\u72b9\u672a \u4e86\uff0c\u90a3\u7aef\u6728\u65b9\u6b63\u5374\u5df2\u63a5\u53e3\u7b11\u9053\uff1a\u201c\u5144\u53f0\u53ef\u77e5\u9053\uff0c\u88ab\u5144\u53f0\u81ea\u9ad8\u3001\u6d2a\u4e24\u6e56\u4e2d\u53d6\u53bb\u7684\u3000\u2018\u4e09\u624d\u5b9d \u85cf\u2019\uff0c\u5374\u672c\u5e94\u662f\u533a\u533a\u5728\u4e0b\u4e4b\u7269\u54e9\u3002\u201d \u3000\u3000\u3000\u6b64\u8bdd\u4e00\u51fa\uff0c\u7f2a\u6587\u4e0d\u7981\u9762\u8272\u5927\u53d8\uff0c\u500f\u7136\u5012\u9000\u4e09\u6b65\uff0c\u6233\u6307\u9053\uff1a\u201c\u9601\u4e0b\u7a76\u7adf\u662f\u8c01\uff0c\u600e\u3000\u5730\u77e5 \u9053\u90a3\u2014\u2014\u201d\u8bed\u58f0\u500f\u7136\u4e00\u987f\uff0c\u5374\u8f6c\u53e3\u9053\uff1a\u201c\u4e09\u624d\u5b9d\u85cf\u662f\u8c01\u53d6\u53bb\u7684\uff0c\u96be\u9053\u9601\u4e0b\u4eb2\u773c\u3000\u89c1\u5230\u662f\u4e3a\u5728 \u4e0b\u53d6\u53bb\u7684\u4e0d\u6210\uff1f\u201d \u3000\u3000\u3000\u54ea\u77e5\u8fd9\u7aef\u6728\u65b9\u6b63\u5374\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u6b63\u662f\uff0c\u5728\u4e0b\u6b63\u662f\u4eb2\u773c\u6240\u89c1\uff0c\u90a3\u4e09\u624d\u5b9d\u85cf\u662f\u88ab\u5144\u3000\u53f0\u53d6 \u53bb\u7684\u3002\u201d\u4f38\u624b\u4e00\u638f\uff0c\u7adf\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u5f20\u7f8a\u76ae\u8584\u7eb8\uff0c\u60f3\u662f\u56e0\u4e3a\u5e74\u4ee3\u4e45\u8fdc\uff0c\u5df2\u6cdb\u9ec4\u3000\u8272\uff0c\u7aef\u6728\u65b9 \u6b63\u53cc\u624b\u4e00\u5f20\uff0c\u5c06\u8fd9\u5f20\u7f8a\u76ae\u8584\u7eb8\uff0c\u5f20\u4e86\u5f00\u4e86\uff0c\u9001\u5230\u7f2a\u6587\u773c\u524d\uff0c\u9053\uff1a\u201c\u6b64\u662f\u3000\u4f55\u7269\uff0c\u5144\u53f0\u60f3\u5fc5\u662f \u89c1\u8fc7\u7684\u4e86\u3002\u201d \u3000\u3000\u3000\u7f2a\u6587\u76ee\u5149\u4e00\u626b\uff0c\u9762\u5bb9\u66f4\u4e3a\u4e4b\u5927\u53d8\uff0c\u6c89\u541f\u534a\u664c\uff0c\u65b9\u6b32\u7b54\u8a00\uff0c\u54ea\u77e5\u8fd9\u7aef\u6728\u65b9\u6b63\u5fae\u5fae\u3000\u4e00 \u7b11\uff0c\u5c06\u8fd9\u5f20\u7f8a\u76ae\u8584\u7eb8\uff0c\u53c8\u53e0\u4e86\u8d77\u6765\uff0c\u4e00\u9762\u9053\uff1a\u201c\u8fd9\u4efd\u2018\u4e09\u624d\u79d8\u56fe\u2019\uff0c\u5728\u4e0b\u5f97\u5230\u4e4b\u3000\u65f6\uff0c\u60f3\u5fc5 \u8fdc\u5728\u9601\u4e0b\u4e4b\u524d\uff0c\u53ea\u662f\u5c0f\u53ef\u90a3\u65f6\u4e60\u6b66\u6b63\u52e4\uff0c\u65e0\u6cd5\u5206\u5fc3\u53ca\u6b64\uff0c\u76f4\u5230\u5e74\u4f59\u4e4b\u524d\uff0c\u3000\u5c0f\u53ef\u90a3\u65f6\u6b66\u529f\u5c0f \u6709\u6240\u6210\uff0c\u4fbf\u4f9d\u56fe\u6240\u793a\uff0c\u5bfb\u5f97\u4e86\u90a3\u767e\u5341\u5e74\u6765\uff0c\u4e3a\u5929\u4e0b\u6b66\u6797\u4e2d\u4eba\u5782\u6d8e\u4e0d\u3000\u5df2\u7684\u4e09\u624d\u5b9d\u85cf\u3002\u201d \u3000\u3000\u3000\u7f2a\u6587\u4fef\u9996\u6c89\u541f\uff0c\u5583\u5583\u81ea\u8bed\uff1a\u201c\u5e74\u4f59\u4e4b\u524d\u2026\u2026\u201d\u84e6\u5730\u53cc\u76ee\u4e00\u5f20\uff0c\u95ee\u9053\uff1a\u201c\u5144\u53f0\u90a3\u3000\u65f6\u600e \u5730\u4e0d\u53d6\u53bb\u5462\uff1f\u201d \u3000\u3000\u3000\u53ea\u89c1\u7aef\u6728\u65b9\u6b63\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u53ea\u662f\u5c0f\u53ef\u90a3\u65f6\u5b64\u8eab\u800c\u5f80\uff0c\u867d\u6709\u53d6\u5b9d\u4e4b\u5fc3\uff0c\u5374\u65e0\u53d6\u5b9d\u3000\u4e4b \u529b\uff0c\u867d\u5165\u5b9d\u5c71\uff0c\u53ea\u5f97\u7a7a\u624b\u800c\u56de\uff0c\u672c\u60f3\u5c3d\u5feb\u5bfb\u627e\u51e0\u4e2a\u5e2e\u624b\uff0c\u5165\u6e56\u53d6\u5b9d\uff0c\u4f46\u5c0f\u53ef\u4e00\u751f\u3000\u72ec\u6765\u72ec \u5f80\uff0c\u8981\u5bfb\u5e2e\u624b\u8bf4\u6765\u867d\u6613\uff0c\u884c\u6765\u5374\u662f\u6781\u96be\u3002\u201d \u3000\u3000\u3000\u4ed6\u8bed\u58f0\u4e00\u987f\uff0c\u5c06\u90a3\u5f20\u7f8a\u76ae\u8584\u7eb8\uff0c\u7f13\u7f13\u6536\u56de\u6000\u91cc\uff0c\u53c8\u9053\uff1a\u201c\u800c\u4e14\u8fd9\u2018\u4e09\u624d\u5b9d\u85cf\u2019\u3000\u6df1\u5728 \u6e56\u5e95\uff0c\u53d6\u5b9d\u4e4b\u4eba\uff0c\u4e0d\u4f46\u8981\u6c34\u6027\u6781\u4f73\uff0c\u800c\u4e14\u8fd8\u8981\u751f\u6027\u4fa0\u4e49\uff0c\u53c8\u5f97\u4e0e\u90a3\u2018\u6c34\u4e0a\u8427\u95e8\u3000\uff0c\u4e2d\u4eba\u6beb\u65e0 \u5173\u8fde\uff0c\u8fd9\u4e09\u6837\u4e2d\u8981\u662f\u7f3a\u4e86\u4e00\u6837\uff0c\u4fbf\u4e07\u4e07\u4e0d\u80fd\u6c42\u4ed6\u5e2e\u52a9\u6211\u53d6\u5b9d\u3002\u201d\u7f2a\u6587\u4e0d\u3000\u7981\u6697\u4e2d\u9894\u9996\uff0c\u53ea\u89c1 \u7aef\u6728\u65b9\u6b63\u7f13\u7f13\u4f38\u51fa\u4e09\u6839\u624b\u6307\uff0c\u53c8\u9053\uff1a\u201c\u6211\u60f3\u6765\u60f3\u53bb\uff0c\u53ea\u6709\u90a3\u6614\u5e74\u3000\u540d\u626c\u5929\u4e0b\uff0c\u4eca\u65e5\u5374\u5df2\u5f52 \u9690\uff0c\u5728\u6b66\u6797\u6c34\u8def\u4e2d\u7684\u5730\u4f4d\uff0c\u4ecd\u5728\u90a3\u5929\u4e0b\u4e09\u5341\u516d\u9053\u6c34\u8def\u603b\u5de1\u9605\u3000\u4e4b\u4e0a\u7684\u4e94\u6e56\u9f99\u738b\u9f99\u5728\u7530\u4e09\u4f4d\u540e \u4eba\uff0c\u2018\u4e94\u6e56\u4e09\u9f99\u201d\u4e0d\u4f46\u6c34\u6027\u3001\u6b66\u529f\uff0c\u4ff1\u662f\u4e0a\u4e0a\u4e4b\u9009\uff0c\u3000\u800c\u4e14\u4e3a\u4eba\u4fa0\u4e49\uff0c\u4e5f\u4e0d\u4f1a\u89c1\u8d22\u8d77\u610f\uff0c\u662f \u4e09\u6761\u5149\u660e\u78ca\u843d\u7684\u6c49\u5b50\uff0c\u82e5\u80fd\u6c42\u5f97\u6b64\u4e09\u4eba\u52a9\u6211\u53d6\u3000\u5b9d\uff0c\u90a3\u662f\u518d\u597d\u4e5f\u6ca1\u6709\u4e86\u3002\u201d\u7f2a\u6587\u9762\u8272\u53c8\u662f\u4e00 \u53d8\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u53ea\u662f\u8fd9\u4e09\u4eba\u5374\u672a\u89c1\u6709\u7a7a\u3000\u5462\uff1f\u201d \u3000\u3000\u3000\u7aef\u6728\u65b9\u6b63\u8f69\u7709\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u6240\u89c1\u6781\u662f\uff0c\u60f3\u90a3\u2018\u4e94\u6e56\u4e09\u9f99\u2019\u81ea\u4ece\u4e8c\u5341\u5e74\u524d\uff0c\u3000\u2018\u4e94 \u6e56\u9f99\u738b\u2019\u7a81\u5730\u6d88\u58f0\u706d\u8ff9\u4e4b\u540e\uff0c\u4fbf\u4e5f\u76f8\u7ee7\u5f52\u9690\uff0c\u5c0f\u53ef\u4e0e\u4e4b\u53c8\u65e0\u6df1\u4ea4\uff0c\u4eba\u5bb6\u600e\u4f1a\u5192\u3000\u7136\u7b54\u5e94\uff0c\u4f46 \u6025\u75c5\u4e71\u6295\u533b\uff0c\u5c0f\u53ef\u867d\u77e5\u65e0\u751a\u539a\u671b\uff0c\u4e5f\u5f97\u53bb\u8bd5\u4e0a\u4e00\u8bd5\u3002\u201d \u3000\u3000\u3000\u7f2a\u6587\u51b7\u7b11\u4e00\u58f0\uff0c\u8d1f\u624b\u4ef0\u671b\uff0c\u53ea\u89c1\u7fa4\u661f\u6ee1\u5929\uff0c\u6708\u5706\u5982\u76d8\uff0c\u76ee\u5149\u4e00\u5782\uff0c\u5374\u89c1\u90a3\u7aef\u6728\u3000\u65b9\u6b63 \u6b63\u81ea\u4f3c\u7b11\u975e\u7b11\u5730\u671b\u7740\u81ea\u5df1\uff0c\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u5c0f\u53ef\u8d39\u4e86\u65e0\u6570\u5fc3\u529b\uff0c\u624d\u63a2\u542c\u5230\u90a3\u2018\u4e94\u3000\u6e56\u4e09\u9f99\uff0c\u5f52 \u9690\u4e4b\u540e\uff0c\u662f\u9690\u5c45\u5728\u90a3\u957f\u6c5f\u53e3\u4e2d\u7684\u5d07\u660e\u5c9b\u4e4b\u4e0a\uff0c\u4fbf\u6beb\u4e0d\u8fdf\u7591\u5730\u517c\u7a0b\u8d76\u53bb\uff0c\u3000\u54ea\u77e5\u5230\u4e86\u5d07\u660e\u5c9b\uff0c \u90a3\u2019\u4e94\u6e56\u4e09\u9f99\u2019\u5374\u90fd\u5df2\u79bb\u5c9b\u800c\u53bb\uff0c\u53ea\u5269\u4e0b\u4e24\u4e2a\u5782\u9afb\u7ae5\u5b50\uff0c\u5728\u90a3\u9f99\u3000\u6c0f\u4e09\u5144\u5f1f\u6240\u5efa\u7684\u8305\u820d\u4e2d\u5b88 \u5c4b\u3002\u201d \u3000\u3000\u3000\u201c\u5f53\u65f6\u5728\u4e0b\u5fc3\u4e2d\u5341\u5206\u5947\u602a\uff0c\u60f3\u90a3\u2018\u4e94\u6e56\u4e09\u9f99\uff0c\u4ff1\u662f\u5f52\u9690\u4e4b\u4eba\uff0c\u600e\u5730\u4f1a\u540c\u65f6\u79bb\u5c9b\u3000\u800c \u53bb\uff0c\u4fbf\u518d\u4e09\u8ffd\u95ee\u90a3\u4e24\u4e2a\u5782\u9afb\u7ae5\u5b50\uff0c\u90a3\u4e24\u4e2a\u5782\u9afb\u7ae5\u5b50\u5148\u662f\u4e0d\u8bf4\uff0c\u88ab\u6211\u95ee\u5f97\u6025\u4e86\uff0c\u624d\u3000\u9053\uff1a\u2018\u51e0 \u5929\u4e4b\u524d\uff0c\u6765\u4e86\u4f4d\u82f1\u4fca\u5c11\u5e74\uff0c\u548c\u5e08\u7236\u8c08\u4e86\u4e00\u591c\uff0c\u90a3\u4e00\u591c\u91cc\u5e08\u7236\u53c8\u54ed\u53c8\u7b11\uff0c\u6211\u3000\u4eec\u6b63\u5728\u5947\u602a\uff0c\u54ea \u77e5\u7b2c\u4e8c\u592d\u5e08\u5085\u4eec\u5c31\u90fd\u548c\u90a3\u4f4d\u5c11\u5e74\u4e00\u9f50\u8d70\u4e86\u3002\uff0c\u6211\u5c31\u95ee\uff1a\u2018\u5c0a\u5e08\u4e34\u884c\u3000\u4e4b\u65f6\uff0c\u53ef\u66fe\u7559\u4e0b\u8bdd\u6765\uff0c \u8bf4\u8981\u5230\u54ea\u91cc\u53bb\u3002\uff0c\u90a3\u4e24\u4e2a\u7ae5\u5b50\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u6211\u89c1\u4ed6\u4eec\u4eff\u4f5b\u4e0d\u3000\u613f\u8bf4\u51fa\uff0c\u4fbf\u53c8\u9053\uff1a\u2018\u6211\u548c\u5c0a\u5e08 \u662f\u6570\u5341\u5e74\u6545\u4ea4\uff0c\u6b64\u6b21\u6765\u8bbf\uff0c\u662f\u6709\u7740\u6025\u4e8b\uff0c\u4f60\u4eec\u81ea\u7ba1\u8bf4\u3000\u51fa\uff0c\u5c0a\u5e08\u5fc5\u5b9a\u4e0d\u4f1a\u89c1\u602a\u7684\u3002\u2019\u201d\uff08\u4e1c \u65b9\u5251\u6ce8\uff1a\u539f\u672c\u5982\u6b64\uff0c\u5b9e\u5728\u6539\u4e0d\u80dc\u6539\u3002\uff1a-(\uff09\u7f2a\u3000\u6587\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u9601\u4e0b\u975e\u4f46\u6b66\u529f\u60ca \u4eba\uff0c\u53e3\u624d\u4e5f\u662f\u6781\u597d\u7684\u3002\u82e5\u662f\u6362\u4e86\u522b\u4eba\uff0c\u3000\u53ea\u6015\u90a3\u4e24\u4f4d\u7ae5\u5b50\u4fbf\u518d\u4e5f\u4e0d\u4f1a\u8bf4\u51fa\u6765\u3002\u201d\u563f\u5730\u4e00\u58f0\uff0c \u76ee\u5149\u53c8\u671b\u5230\u5929\u4e0a\u3002 \u3000\u3000\u3000\u90a3\u7aef\u6728\u65b9\u6b63\u5374\u751f\u50cf\u662f\u5168\u7136\u4e0d\u61c2\u4ed6\u8bed\u4e2d\u7684\u8ba5\u5632\u4e4b\u610f\uff0c\u8fde\u58f0\u7b11\u9053\uff1a\u201c\u5c82\u6562\uff0c\u5c82\u6562\u3002\u3000\u201d \u3000\u3000\u3000\u7f2a\u6587\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u5374\u542c\u4ed6\u53c8\u81ea\u7b11\u9053\uff1a\u201c\u5f53\u4e0b\u90a3\u4e24\u4e2a\u7ae5\u5b50\u53c8\u4ed4\u7ec6\u6253\u91cf\u4e86\u6211\u4e24\u773c\u3000\uff0c\u624d \u8bf4\u9053\uff1a\u2018\u5e08\u5085\u4e34\u8d70\u7684\u90a3\u5929\uff0c\u5c06\u597d\u4e45\u90fd\u672a\u52a8\u7528\u8fc7\u7684\u6c34\u8863\u6c34\u9760\u90fd\u5e26\u4e86\u53bb\uff0c\u8bf4\u662f\u8981\u5230\u3000\u6d2a\u6cfd\u6e56\u53bb\uff0c \u5c11\u5219\u4e00\u6708\uff0c\u591a\u5219\u4e09\u6708\uff0c\u4fbf\u8981\u56de\u6765\uff0c\u5c0a\u5ba2\u662f\u6709\u8981\u7d27\u7684\u4e8b\u7b49\u4ed6\u8001\u4eba\u5bb6\uff0c\u4e0d\u59a8\u3000\u5728\u8fd9\u91cc\u4f4f\u4e0b\u6765\u3002\u3001 \u6211\u4e00\u542c\u8fd9\u8bdd\uff0c\u5413\u4e86\u4e00\u8df3\uff0c\u5fc3\u60f3\uff0c\u2019\u83ab\u975e\u4ed6\u4eec\u5df2\u88ab\u522b\u4eba\u8bf7\u53bb\u5bfb\u5b9d\u4e86\u3000\uff1f\uff0c\u53e3\u91cc\u8fde\u8bf4\uff1a\u2018\u4e0d\u5fc5 \u4e86\uff0c\u4e0d\u5fc5\u4e86\u2026\u2026\u2019\u8f6c\u8eab\u5c31\u8d70\u4e86\u51fa\u53bb\uff0c\u53ea\u542c\u90a3\u4e24\u4e2a\u7ae5\u5b50\u5728\u540e\u9762\u3000\u53eb\u9053\uff1a\u2018\u5c0a\u5ba2\u600e\u5730\u8fde\u8336\u90fd\u4e0d\u559d \u5c31\u8d70\u4e86\u3002\u2019\u6211\u5fc3\u91cc\u867d\u5f88\u559c\u6b22\u8fd9\u4e24\u4e2a\u7ae5\u5b50\u7684\u806a\u660e\u4f36\u4fd0\uff0c\u3000\u4f46\u53c8\u7740\u6025\u90a3\u2018\u4e09\u624d\u5b9d\u85cf\u2019\uff0c\u53ea\u5f97\u4e0d\u7406 \u4ed6\u4eec\u5c31\u8d70\u4e86\u3002\u201d \u3000\u3000\u3000\u7f2a\u6587\u4e24\u76ee\u4ecd\u81ea\u671b\u5728\u5929\u4e0a\uff0c\u53e3\u4e2d\u5374\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u81ea\u7136\uff0c\u60f3\u90a3\u4e24\u4e2a\u5c0f\u5b69\u5b50\u53c8\u662f\u4ec0\u3000\u4e48\u4e1c \u897f\uff0c\u600e\u914d\u548c\u2018\u91d1\u5251\u5927\u4fa0\u2019\u591a\u8bdd\u3002\u201d \u3000\u3000\u3000\u7aef\u6728\u65b9\u6b63\u8f69\u7709\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u867d\u5982\u6b64\u8bf4\uff0c\u5bf9\u5144\u53f0\u5374\u662f\u7edd\u65e0\u6076\u610f\uff0c\u5144\u53f0\u53c8\u4f55\u82e6\u5982\u6b64\u3000\u6316\u82e6 \u5e72\u6211\u3002\u201d\u7f2a\u6587\u54fc\u4e86\u4e00\u58f0\uff0c\u95ed\u53e3\u4e0d\u8a00\uff0c\u90a3\u7aef\u6728\u65b9\u6b63\u53c8\u9053\uff1a\u201c\u6211\u663c\u591c\u4e0d\u505c\u5730\u8d76\u5230\u9ad8\u3000\uff0c\u6d2a\u4e24\u6e56\u95f4 \u7684\u85cf\u5b9d\u4e4b\u5904\uff0c\u90a3\u65f6\u6b63\u662f\u4e2d\u79cb\u524d\u4e00\u65e5\uff0c\u5bb6\u5bb6\u6237\u6237\uff0c\u90fd\u5728\u5fd9\u7740\u8fc7\u8282\uff0c\u9ad8\u3001\u6d2a\u3000\u6e56\u7554\u79cb\u610f\u6b63\u6d53\uff0c\u6781 \u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u79cb\u6c34\u7cbc\u7cbc\uff0c\u4e00\u78a7\u4e07\u91cc\uff0c\u6c34\u6ce2\u6708\u8272\u4e4b\u4e2d\uff0c\u5374\u6709\u4e09\u3001\u4e94\u3000\u6761\u4eba\u5f71\uff0c\u6b63\u5728\u90a3\u8352\u65e0\u4eba\u8ff9 \u7684\u6e56\u7554\uff0c\u4e92\u76f8\u4f4e\u8bed\u3002\u201d \u3000\u3000\u3000\u7f2a\u6587\u9762\u5bb9\u9aa4\u7136\u53c8\u4e00\u53d8\uff0c\u76ee\u5149\u500f\u7136\u8f6c\u5230\u8fd9\u7aef\u6728\u65b9\u6b63\u9762\u4e0a\u3002 \u3000\u3000\u3000\u53ea\u89c1\u4ed6\u4ecd\u81ea\u4e0d\u52a8\u58f0\u8272\u5730\u9053\uff1a\u201c\u6211\u8eb2\u5728\u7ea6\u83ab\u4e03\u4e08\u5f00\u5916\u7684\u4e00\u682a\u6728\u53f6\u6b63\u6d53\uff0c\u5374\u4ecd\u672a\u843d\u3000\u5c3d\u7684 \u6811\u4e0a\uff0c\u5c4f\u4f4f\u58f0\u606f\uff0c\u51dd\u76ee\u800c\u671b\uff0c\u53ea\u89c1\u8fd9\u4e9b\u4eba\u91cc\uff0c\u6709\u4e09\u4e2a\u6ee1\u8eab\u6c34\u9760\u7684\u527d\u608d\u5927\u6c49\uff0c\u548c\u3000\u4e00\u4e2a\u6587\u8d28\u5f6c \u5f6c\u7684\u82f1\u4fca\u5c11\u5e74\uff0c\u8fd8\u6709\u4e00\u4eba\uff0c\u6211\u867d\u4e0d\u8ba4\u5f97\uff0c\u4f46\u6708\u5149\u4e4b\u4e0b\uff0c\u53ea\u89c1\u4ed6\u8eab\u624b\u77eb\u5065\u3000\uff0c\u76ee\u5149\u70af\u7136\uff0c\u663e\u89c1 \u5f97\u4e5f\u662f\u4f4d\u5185\u5bb6\u9ad8\u624b\u3002\u201d \u3000\u3000\u3000\u201c\u6211\u5fc3\u4e2d\u6697\u5fd6\uff1a\u2018\u90a3\u4e09\u4e2a\u7a7f\u7740\u6c34\u9760\u7684\u6c49\u5b50\uff0c\u60f3\u5fc5\u5c31\u662f\u90a3\u2019\u4e94\u6e56\u4e09\u9f99\u2019\u4e86\uff0c\u4f46\u90a3\u3000\u6587\u8d28 \u5f6c\u5f6c\u7684\u5c11\u5e74\u5374\u53c8\u662f\u8c01\u5462\uff1f\uff0c\u53ea\u89c1\u8fd9\u4e9b\u4eba\u5bf9\u8fd9\u5c11\u5e74\uff0c\u4eff\u4f5b\u90fd\u6781\u4e3a\u5c0a\u656c\uff0c\u6211\u5fc3\u91cc\u66f4\u3000\u5947\u602a\uff0c\u4e0d\u77e5 \u8fd9\u5c11\u5e74\u662f\u4f55\u6765\u8def\uff1f\u201d \u3000\u3000\u3000\u4ed6\u4e00\u9762\u8bf4\u7740\u8bdd\uff0c\u4e00\u9762\u5374\u542b\u7b11\u671b\u7740\u7f2a\u6587\uff0c\u7f2a\u6587\u9762\u8272\u8fde\u53d8\u6570\u6b21\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u90a3\u5c11\u5e74\u3000\u65e2\u7136 \u77e5\u9053\u85cf\u5b9d\u4e4b\u5904\uff0c\u81ea\u7136\u4e5f\u6709\u90a3\u2018\u85cf\u5b9d\u4e4b\u56fe\u201d\u60f3\u5f53\u5e74\u5c11\u6797\u6d3e\u638c\u6559\u7956\u5e08\uff0c\u8eab\u5177\u65e0\u4e0a\u964d\u3000\u9b54\u80fd\u529b\u7684\u5927 \u7a7a\u4e0a\u4eba\u672c\u5c06\u6b64\u56fe\u753b\u6210\u4e09\u4efd\uff0c\u5374\u672a\u8a00\u660e\u6b64\u5b9d\u8be5\u5f52\u54ea\u4e00\u4efd\u56fe\u7684\u5f97\u4e3b\uff0c\u60f3\u5fc5\u5f53\u3000\u7136\u662f\u5148\u5230\u5148\u5f97\uff0c\u9601 \u4e0b\u65e2\u7136\u8fdf\u5230\u4e00\u6b65\uff0c\u53c8\u6028\u5f97\u4f55\u4eba\uff1f\u800c\u90a3\u5c11\u5e74\u65e2\u5f97\u6b64\u56fe\uff0c\u5fc5\u6709\u6765\u5386\uff0c\u3000\u9601\u4e0b\u53c8\u4f55\u5eb8\u82e6\u82e6\u8ffd\u67e5 \u5462\uff1f\u201d\u7aef\u6728\u65b9\u6b63\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5144\u53f0\u6b64\u8a00\uff0c\u53ef\u4e91\u6df1\u5f97\u6211\u5fc3\uff0c\u5f53\u65f6\u3000\u5728\u4e0b\u5fc3\u4e2d\u5c31\u60f3\uff1a\u2018\u8fd9\u5c11\u5e74\u65e2 \u5f97\u6b64\u56fe\uff0c\u90a3\u4e48\u82e5\u975e\u5c11\u6797\u5f1f\u5b50\uff0c\u5c31\u5fc5\u5b9a\u662f\u6614\u5e74\u540d\u9707\u5929\u4e0b\u7684\u3000\u4e00\u4ee3\u6b66\u6797\u5947\u4eba\u2019\u6d77\u5929\u5b64\u71d5\u2019\u7684\u4f20\u4eba \u4e86\u2019\u2014\u2014\u201d\u7f2a\u6587\u5251\u7709\u4e00\u8f69\uff0c\u622a\u53e3\u9053\uff1a\u201c\u90a3\u4e48\u5144\u53f0\u3000\u5b9a\u662f\u6b66\u5f53\u4e00\u6d3e\u4e86\u3002\u201d\u5fc3\u4e0b\u5374\u604d\u7136\u800c\u609f\uff0c\u5fd6 \u9053\uff1a\u201c\u96be\u602a\u65b9\u624d\u7fa4\u96c4\u5404\u5404\u5927\u4e71\u4e4b\u9645\uff0c\u90a3\u6e05\u3000\u98ce\u5251\u6731\u767d\u7fbd\u5374\u4e0d\u52a8\u58f0\u8272\uff0c\u539f\u6765\u4ed6\u65e9\u5c31\u4ece\u8fd9\u2018\u91d1\u5251 \u4fa0\u2019\u53e3\u4e2d\uff0c\u5f97\u77e5\u6b64\u5b9d\u5df2\u88ab\u53d6\u53bb\uff0c\u662f\u3000\u4ee5\u90a3\u5c11\u6797\u58a8\u4e00\u4e0a\u4eba\u4e00\u542c\uff0c\u4fbf\u4e5f\u968f\u5373\u8d70\u4e86\u3002\u201d \u3000\u3000\u3000\u7aef\u6728\u65b9\u6b63\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5144\u53f0\u7aef\u7684\u662f\u660e\u773c\u4eba\uff0c\u5c0f\u53ef\u662f\u6b66\u5f53\u5f1f\u5b50\u3002\u201d \u3000\u3000\u3000\u7f2a\u6587\u5fc3\u4e2d\u53c8\u662f\u4e00\u52a8\uff1a\u201c\u6b66\u5f53\u6d3e\u81ea\u4ece\u90a3\u4e00\u4ee3\u5251\u8c6a\u767d\u8001\u5b97\u5e08\u53bb\u4e16\u4e4b\u540e\uff0c\u4eba\u6750\u672c\u6781\u51cb\u3000\u843d\uff0c \u636e\u6211\u6240\u77e5\uff0c\u5f53\u4eca\u6b66\u5f53\u6d3e\u7684\u7b2c\u4e00\u9ad8\u624b\uff0c\u6e05\u98ce\u5251\u6731\u81ea\u7fbd\uff0c\u6b66\u529f\u4e5f\u4e0d\u751a\u9ad8\uff0c\u600e\u5730\u8fd9\u7aef\u3000\u6728\u65b9\u6b63\u5374\u6709 \u5982\u6b64\u8eab\u624b\uff1f\u201d \u3000\u3000\u3000\u5374\u542c\u8fd9\u7aef\u6728\u65b9\u6b63\u53c8\u81ea\u7b11\u9053\uff1a\u201c\u6211\u5fc3\u4e0b\u867d\u5728\u8f6c\u7740\u5ff5\u5934\uff0c\u76ee\u5149\u5374\u77ac\u4e5f\u4e0d\u77ac\u5730\u671b\u5728\u8fd9\u3000\u4e94\u4eba \u8eab\u4e0a\uff0c\u53ea\u89c1\u4ed6\u4eec\u4f4e\u8bed\u4e86\u4e00\u9635\uff0c\u90a3\u5c11\u5e74\u7a81\u5730\u7b11\u9053\uff1a\u2018\u5982\u6b64\u5c31\u9ebb\u70e6\u9f99\u5144\u4e86\u3002\uff0c\u90a3\u4e09\u3000\u4e2a\u7a7f\u7740\u6c34\u9760 \u7684\u5927\u6c49\u9f50\u9053\uff1a\u2018\u4e0d\u6562\uff0c\u4e0d\u6562\uff0c\u5144\u53f0\u65e2\u6709\u5bb6\u7236\u4e4b\u4ee4\uff0c\u4fbf\u662f\u53eb\u6211\u5144\u5f1f\u4e09\u4eba\u8d74\u3000\u6c64\u8e48\u706b\uff0c\u6211\u5144\u5f1f\u4ea6 \u662f\u5728\u6240\u4e0d\u8f9e\u7684\u201c\u3002\u2019\u8bf4\u7740\u5c31\u4ece\u53e6\u4e00\u6c49\u5b50\u624b\u4e0a\uff0c\u63a5\u7740\u51e0\u6761\u7ef3\u5b50\uff0c\u63a5\u3000\u8fde\u7740\u8df3\u4e0b\u6c34\u53bb\uff0c\u8fd9\u4e09\u4eba\u679c \u771f\u4e0d\u6127\u662f\u2018\u4e94\u6e56\u4e4b\u9f99\uff0c\u3002\u5165\u6c34\u4e4b\u9645\uff0c\u7adf\u8fde\u6c34\u82b1\u90fd\u6ca1\u6709\u626c\u8d77\u3000\u534a\u70b9\u3002\u201d\u4ed6\u5fae\u5fae\u4e00\u987f\uff0c\u53c8\u9053\uff1a \u201c\u6211\u5fc3\u91cc\u4e00\u9762\u6697\u4f69\u8fd9\u9f99\u6c0f\u5144\u5f1f\u7684\u6c34\u6027\uff0c\u4e00\u9762\u5374\u5728\u5947\u602a\u3000\uff0c\u90a3\u2018\u4e94\u6e56\u9f99\u738b\uff0c\u9f99\u5728\u7530\u9f99\u8001\u7237\u5b50\u4e8c \u5341\u5e74\u524d\u4fbf\u5df2\u6d88\u58f0\u706d\u8ff9\uff0c\u6c5f\u6e56\u4e2d\u4ece\u672a\u6709\u4eba\u77e5\u9053\u4ed6\u3000\u8001\u4eba\u5bb6\u7684\u53bb\u5411\u7684\uff0c\u8fd9\u5c11\u5e74\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u600e\u7684\u5374 \u77e5\u9053\u4ed6\u8001\u4eba\u5bb6\u7684\u4e0b\u843d\uff0c\u800c\u4e14\u663e\u7136\u8fd8\u6301\u6709\u3000\u4ed6\u8001\u4eba\u5bb6\u7684\u624b\u4ee4\uff0c\u662f\u4ee5\u8fd9\u2019\u9f99\u6c0f\u5144\u5f1f\uff0c\u624d\u4f1a\u8ddf\u7740\u4ed6 \u524d\u6765\uff0c\u4e00\u9762\u5fc3\u4e2d\u53c8\u604d\u7136\u800c\u609f\u7684\u60f3\u3000\u96be\u9053\u90a3\u4e24\u4e2a\u7ae5\u5b50\u8bf4\u5e08\u5085\u4eec\u548c\u90a3\u5c11\u5e74\u8c08\u4e86\u4e00\u591c\uff0c\u53c8\u54ed\u53c8\u7b11\uff0c \u60f3\u5fc5\u662f\u8fd9\u5c11\u5e74\u5728\u8bf4\u9f99\u8001\u524d\u3000\u8f88\u8fd1\u5e74\u7684\u906d\u9047\u4e86\u3002\u201d \u3000\u3000\u3000\u7f2a\u6587\u51b7\u51b7\u9053\uff1a\u201c\u96be\u602a\u9601\u4e0b\u80fd\u4ee5\u996e\u8a89\u6b66\u6797\uff0c\u4eca\u65e5\u4e00\u89c1\uff0c\u679c\u7136\u806a\u660e\u7edd\u9876\uff0c\u4ec0\u4e48\u4e8b\u90fd\u3000\u9003\u4e0d \u8fc7\u9601\u4e0b\u773c\u91cc\u3002\u201d \u3000\u3000\u3000\u7aef\u6728\u65b9\u6b63\u8f69\u7709\u7b11\u9053\uff1a\u201c\u5c82\u6562\uff0c\u5c82\u6562\uff0c\u5144\u53f0\u5982\u6b64\u79f0\u8d5e\u4e8e\u6211\uff0c\u4f46\u5728\u4e0b\u90a3\u65f6\u5374\u662f\u4e00\u5934\u3000\u96fe \u6c34\uff0c\u53ea\u89c1\u8fd9\u5c11\u5e74\u548c\u90a3\u6c49\u5b50\u53cc\u624b\u63d0\u7740\u7ef3\u7d22\u7684\u4e00\u7aef\uff0c\u7acb\u5728\u6e56\u7554\uff0c\u672a\u8fc7\u7247\u523b\uff0c\u4ed6\u4eec\u53cc\u624b\u3000\u4fbf\u81ea\u7f13\u7f13 \u63d0\u8d77\uff0c\u5012\u9000\u7740\u8d70\u4e86\u5341\u6570\u6b65\u3002\u6211\u5fc3\u4e2d\u6697\u60ca\uff0c\u53ea\u6015\u4ed6\u4eec\u4f1a\u53d1\u73b0\u6211\u5b58\u8eab\u4e4b\u5904\uff0c\u54ea\u3000\u77e5\u4ed6\u4eec\u8fd8\u672a\u5230\u6811 \u4e0b\uff0c\u53cc\u624b\u53c8\u81ea\u4e00\u62ac\uff0c\u6c34\u9762\u5fae\u82b1\u5904\uff0c\u4fbf\u5192\u51fa\u56db\u53e3\u7bb1\u5b50\u6765\uff0c\u4ed6\u4eec\u8eab\u5f62\u5404\u3000\u81ea\u4e00\u4e00\u52a8\uff0c\u4fbf\u7535\u4e5f\u4f3c\u5730 \u63a0\u4e86\u8fc7\u53bb\uff0c\u5c06\u90a3\u56db\u53e3\u7bb1\u5b50\u6284\u5728\u624b\u91cc\u3002\u90a3\u65f6\u6211\u624d\u77e5\u9053\uff0c\u90a3\u6c49\u5b50\u3000\u770b\u6765\u6b66\u529f\u867d\u6781\u9ad8\uff0c\u800c\u90a3\u6587\u8d28\u5f6c \u5f6c\u7684\u5c11\u5e74\u7684\u8eab\u624b\uff0c\u7adf\u8fd8\u5728\u4ed6\u4e4b\u4e0a\u3002\u201d \u3000\u3000\u3000\u4ed6\u76ee\u5149\u53c8\u81ea\u5f80\u7f2a\u6587\u9762\u4e0a\u4e00\u626b\uff0c\u6ee1\u542b\u6df1\u610f\u5730\u5fae\u7b11\u4e00\u4e0b\uff0c\u53c8\u9053\uff1a\u201c\u8fd9\u6837\u4f55\u6d88\u7247\u523b\uff0c\u3000\u4ed6\u4eec \u5c31\u4ece\u6e56\u4e2d\u63d0\u4e0a\u5341\u6570\u53e3\u770b\u6765\u6781\u4e3a\u6c89\u91cd\u7684\u7bb1\u5b50\u6765\uff0c\u90a3\u2018\u4e94\u6e56\u4e09\u9f99\u2019\uff0c\u4fbf\u4e5f\u8dc3\u51fa\u6c34\u9762\u3000\uff0c\u4ece\u90a3\u8eab\u624b \u77eb\u5065\u7684\u6c49\u5b50\u624b\u4e2d\uff0c\u63a5\u8fc7\u4e00\u74f6\u9152\uff0c\u5404\u81ea\u559d\u4e86\u513f\u53e3\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u2018\u5e78\u4e0d\u8fb1\u547d\u3000\u3002\u2019\u90a3\u5c11\u5e74\u8fde\u8fde\u62b1 \u62f3\uff0c\u4e00\u9762\u6253\u5f00\u7bb1\u5b50\uff0c\u5fae\u5fae\u4e00\u77a5\uff0c\u6211\u867d\u8fdc\u5728\u5341\u4e08\u5f00\u5916\uff0c\u4f46\u6781\u76ee\u671b\u53bb\u3000\uff0c\u4ecd\u53ef\u9690\u7ea6\u671b\u5230\u4ed6\u9762\u4e0a\u7684 \u795e\u8272\uff0c\u867d\u7136\u6709\u4e9b\u7b11\u5bb9\uff0c\u5374\u6ca1\u6709\u4ec0\u4e48\u72c2\u559c\u4e4b\u8272\uff0c\u4e0d\u7981\u5728\u5fc3\u4e2d\u3000\u6697\u6697\u79f0\u8d5e\uff0c\u8fd9\u5c11\u5e74\u679c\u7136\u662f\u4e2a\u89d2 \u8272\u3002\u201d \u3000\u3000\u3000\u4ed6\u76ee\u5149\u53c8\u81ea\u4e00\u626b\u7f2a\u6587\uff0c\u542b\u7b11\u63a5\u53e3\u53c8\u9053\uff1a\u201c\u90a3\u5c11\u5e74\u4e00\u77a5\u4e4b\u540e\uff0c\u4fbf\u548c\u53e6\u4e00\u6c49\u5b50\u4f4e\u8bed\u3000\u4e24 \u53e5\uff0c\u6211\u867d\u7528\u5c3d\u8033\u529b\uff0c\u5374\u4e5f\u672a\u542c\u51fa\u6765\uff0c\u54ea\u77e5\u90a3\u6c49\u5b50\u7a81\u5730\u547c\u54e8\u4e00\u58f0\uff0c\u6e56\u5cb8\u56db\u4e0b\u7684\u9634\u5f71\u3000\u4e2d\uff0c\u7adf\u968f \u58f0\u8dc3\u51fa\u4e03\u3001\u516b\u6761\u9ed1\u5f71\u5927\u6c49\u6765\uff0c\u4e00\u4eba\u624b\u4e2d\u63d0\u7740\u4e00\u53e3\u9ebb\u888b\uff0c\u6211\u5fc3\u4e2d\u6697\u9053\u4e00\u58f0\uff1a\u3000\u2018\u4fa5\u5e78\u3002\uff0c\u82e5\u975e \u6211\u6781\u4e3a\u5c0f\u5fc3\uff0c\u53ea\u6015\u884c\u8ff9\u65e9\u5df2\u88ab\u4eba\u5bb6\u4f0f\u4e0b\u7684\u6697\u6869\u53d1\u73b0\u4e86\u3002\u2019 \u3000\u3000\u3000\u3000\u3000\u7f2a\u6587\u5fae\u5fae\u4e00\u7b11\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u82e5\u4ee5\u9601\u4e0b\u7684\u8eab\u624b\u800c\u8a00\uff0c\u53ea\u6015\u6bd4\u90a3\u4e9b\u6c49\u5b50\u6b66\u529f\u518d\u9ad8\u5341\u3000 \u500d\u4e4b\u4eba\uff0c\u4e5f\u96be\u4ee5\u53d1\u73b0\u9601\u4e0b\u7684\u884c\u8e2a\u54e9\u3002\u201d \u3000\u3000\u3000\u7aef\u6728\u65b9\u6b63\u4ea6\u81ea\u4e00\u7b11\uff0c\u4e24\u4eba\u76ee\u5149\u76f8\u5bf9\uff0c\u5f7c\u6b64\u4e4b\u95f4\uff0c\u7adf\u5404\u5404\u4ea4\u6362\u4e86\u4e2a\u4e92\u76f8\u4e86\u89e3\u7684\u773c\u3000\u8272\uff0c \u53ea\u662f\u7f2a\u6587\u5728\u8fd9\u79cd\u773c\u8272\u4e4b\u540e\uff0c\u5374\u6709\u4e9b\u63d0\u9632\u4e4b\u610f\uff0c\u50cf\u662f\u751f\u50cf\u8fd9\u201c\u91d1\u5251\u4fa0\u201d\u4f1a\u53d1\u73b0\u4e00\u3000\u4e9b\u81ea\u5df1\u4e0d\u613f \u88ab\u522b\u4eba\u77e5\u9053\u7684\u79d8\u5bc6\u4f3c\u7684\u3002 \u3000\u3000\u3000\u7aef\u6728\u65b9\u6b63\u542b\u7b11\u53c8\u9053\uff1a\u201c\u90a3\u4e9b\u52b2\u88c5\u9ed1\u8863\u5927\u6c49\u8dc3\u51fa\u4e4b\u540e\uff0c\u7acb\u5373\u5782\u624b\u8083\u7acb\uff0c\u90a3\u5c11\u5e74\u5fae\u3000\u4e00\u6325 \u624b\uff0c\u8fd9\u4e9b\u5927\u6c49\u5c31\u5c06\u94c1\u7bb1\u5185\u4e4b\u7269\uff0c\u5168\u90fd\u5012\u5728\u9ebb\u888b\u91cc\uff0c\u6211\u8fdc\u8fdc\u671b\u53bb\uff0c\u53ea\u89c1\u7bb1\u5185\u5149\u534e\u3000\u707f\u70c2\uff0c\u7adf\u90fd \u662f\u9ec4\u91d1\u73e0\u5b9d\u7b49\u7269\u3002\u201d \u3000\u3000\u3000\u201c\u6643\u773c\u4e4b\u95f4\uff0c\u5341\u591a\u53e3\u7bb1\u5b50\u5168\u90fd\u5012\u7a7a\uff0c\u53ea\u5269\u4e0b\u4e00\u53e3\u7bb1\u5b50\uff0c\u5374\u7531\u90a3\u8eab\u624b\u77eb\u5065\u7684\u4e2d\u5e74\u3000\u6c49\u5b50 \u6258\u5728\u624b\u91cc\uff0c\u90a3\u5c11\u5e74\u5fae\u5fae\u4e00\u7b11\uff0c\u6211\u7ea6\u83ab\u53ea\u542c\u5230\uff1a\u2018\u6881\u5144\u2026\u2026\u653e\u5728\u5c0a\u5904\u2026\u2026\u5c0f\u5f1f\u2026\u3000\u2026\u5fc5\u6765\u2026\u2026 \u5168\u4ed7\u5927\u529b\u4e86\u3002\u2019\u90a3\u4e2d\u5e74\u6c49\u5b50\u8eac\u8eab\u4e00\u793c\uff0c\u5c31\u7387\u9886\u7740\u90a3\u4e9b\u52b2\u88c5\u9ed1\u8863\u5927\u6c49\u8d70\u4e86\u3000\uff0c\u90a3\u4e9b\u5927\u6c49\u624b\u91cc\u62ff \u7740\u90a3\u4e48\u6c89\u91cd\u7684\u4e00\u888b\u4e1c\u897f\uff0c\u4f46\u6b65\u5c65\u5374\u4ecd\u6781\u4e3a\u8f7b\u677e\uff0c\u663e\u89c1\u8eab\u624b\u4ff1\u90fd\u4e0d\u3000\u5f31\u3002\u201d \u3000\u3000\u3000\u7f2a\u6587\u53cc\u7709\u4e00\u76b1\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u540e\u6765\u5462\u2026\u2026\u9601\u4e0b\u53ef\u66fe\u8ddf\u8e2a\u800c\u53bb\uff1f\u201d \u3000\u3000\u3000\u7aef\u6728\u65b9\u6b63\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u7684\u786e\u672c\u60f3\u8ddf\u8e2a\u800c\u53bb\uff0c\u4f46\u76ee\u5149\u4e00\u8f6c\uff0c\u5374\u770b\u5230\u90a3\u5c11\u5e74\u4e0d\u3000\u77e5 \u4ece\u201d\u54ea\u91cc\u53c8\u62ff\u6765\u4e2a\u5c0f\u7bb1\u5b50\uff0c\u5728\u90a3\u5341\u51e0\u7bb1\u94c1\u7bb1\u4e0a\u90fd\u88c5\u4e9b\u4e1c\u897f\uff0c\u6211\u8fdc\u770b\u4e5f\u4e0d\u751a\u6e05\uff0c\u4f46\u3000\u5374\u4e5f\u77e5\u9053 \u662f\u6d88\u606f\u5f29\u7bad\u4e00\u7c7b\u4e4b\u7269\uff0c\u53ea\u89c1\u4ed6\u53cc\u624b\u4e0d\u505c\uff0c\u7247\u523b\u4e4b\u95f4\u4fbf\u957f\u8eab\u800c\u8d77\uff0c\u4ef0\u5929\u7b11\u9053\u3000\uff1a\u2018\u4ee5\u8840\u8fd8\u8840\uff0c \u4ee5\u8840\u8fd8\u8840\uff0c\u5982\u4eca\u4f60\u4eec\u4e5f\u8be5\u5c1d\u5c1d\u90a3\u88ab\u4eba\u6697\u7b97\u7684\u6ecb\u5473\u4e86\u3002\uff0c\u8f6c\u8fc7\u5934\u53bb\uff0c\u3000\u53c8\u9053\uff1a\u2018\u53ea\u662f\u53c8\u8981\u9ebb\u70e6 \u9f99\u5144\u4e86\u3002\u2019\u201d\u4ed6\u7565\u5fae\u5598\u4e86\u53e3\u6c14\uff0c\u53c8\u9053\uff1a\u201c\u4ed6\u8fd9\u51e0\u53e5\u8bdd\u8bf4\u5f97\u58f0\u3000\u97f3\u6781\u54cd\uff0c\u662f\u4ee5\u6211\u542c\u5f97\u5341\u5206\u6e05 \u695a\uff0c\u53ea\u89c1\u90a3\u2018\u4e94\u6e56\u4e09\u9f99\u201d\u9f50\u58f0\u7b11\u9053\uff1a\u2018\u5144\u53f0\u600e\u5730\u5982\u6b64\u89c1\u3000\u5916\uff0c\u6211\u5144\u5f1f\u5982\u6709\u6548\u52b3\u4e4b\u5904\uff0c\u53ea\u7ba1\u5429 \u5490\u4fbf\u662f\u3002\uff0c\u8bf4\u7740\uff0c\u4e00\u4eba\u62ff\u8d77\u4e00\u94c1\u7bb1\uff0c\u53c8\u8dc3\u5165\u6c34\u91cc\u3000\uff0c\u90a3\u5c11\u5e74\u8d1f\u624b\u800c\u7acb\uff0c\u4ef0\u5929\u800c\u671b\uff0c\u53e3\u91cc\u5583\u5583 \u4ed6\u8bf4\u7740\u8bdd\uff0c\u53ea\u662f\u8fd9\u6b21\u4ed6\u5374\u8bf4\u5f97\u6781\u8f7b\uff0c\u6211\u4e00\u3000\u4e2a\u5b57\u4e5f\u6ca1\u6709\u542c\u5230\u3002\u201d\u7f2a\u6587\u8f7b\u8f7b\u51b7\u7b11\u4e00\u58f0\uff0c\u4fef\u8eab\u5c06 \u90a3\u67c4\u5df2\u63d2\u5165\u571f\u5185\u7684\u201c\u91d1\u5251\u201d\uff0c\u53c8\u62d4\u3000\u4e86\u51fa\u6765\uff0c\u4ecd\u662f\u7ec6\u7ec6\u4e00\u6761\uff0c\u4ed6\u65b9\u624d\u968f\u624b\u4e00\u629b\uff0c\u7adf\u5c06\u8fd9\u67c4\u7ec6 \u82e5\u7af9\u7b77\u7684\u201c\u91d1\u5251\u201d\u63b7\u5f97\u5165\u571f\u3000\u4e09\u5c3a\uff0c\u800c\u5f62\u72b6\u4ea6\u672a\u6709\u4e1d\u6beb\u6539\u53d8\uff0c\u8fd9\u79cd\u60ca\u4eba\u7684\u5185\u529b\uff0c\u7aef\u7684\u8db3\u4ee5\u60ca \u4e16\u9a87\u4fd7\u4e86\u3002\u7aef\u6728\u65b9\u6b63\u3000\u659c\u779f\u4e00\u773c\uff0c\u5140\u81ea\u63a5\u9053\uff1a\u201c\u90a3\u2018\u4e94\u6e56\u4e09\u9f99\u2019\u4e0d\u4e00\u4f1a\u53c8\u5c06\u90a3\u5341\u51e0\u53e3\u94c1\u7bb1\u90fd \u5e26\u5165\u6c34\u4e2d\uff0c\u6211\u3000\u539f\u4ee5\u4e3a\u4e8b\u60c5\u5df2\u4e86\uff0c\u54ea\u77e5\u8fd9\u5c11\u5e74\u7adf\u53c8\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u5f20\u7f8a\u76ae\u8584\u7eb8\uff0c\u6211\u4e00\u671b\u4fbf\u77e5\u5c31 \u662f\u90a3\u4efd\u3000\u2018\u85cf\u5b9d\u79d8\u56fe\u2019\uff0c\u5fc3\u91cc\u4e0d\u7981\u53c8\u5927\u4e3a\u5947\u602a\uff0c\u4e0d\u77e5\u9053\u4ed6\u5c06\u6b64\u56fe\u53d6\u51fa\u4f5c\u4ec0\uff1f\u53ea\u89c1\u4ed6\u5c06\u6b64\u56fe\u4ed4\u3000 \u7ec6\u53e0\u5728\u4e00\u5757\uff0c\u653e\u5165\u4e00\u4e2a\u91d1\u5149\u95ea\u95ea\u7684\u5c0f\u7bb1\u5b50\u91cc\uff0c\u4e00\u9762\u548c\u90a3\u2018\u4e94\u6e56\u4e09\u9f99\u2019\u8bf4\u9053\uff1a\u2018\u4f11\u770b\u3000\u8fd9\u5f20\u8584 \u7eb8\u5df2\u6210\u5e9f\u7269\uff0c\u4f46\u5374\u662f\u6839\u5927\u5927\u7684\u8089\u9aa8\u5934\uff0c\u7b49\u5230\u8fd9\u6839\u8089\u9aa8\u5934\u88ab\u4e00\u4e9b\u997f\u72d7\u53d1\u73b0\u7684\u65f6\u3000\u5019\u2014\u2014\u563f\u563f\uff0c \u90a3\u65f6\u4f60\u6211\u5374\u6709\u597d\u620f\u770b\u4e86\u3002\u2019\u201d\u7f2a\u6587\u76ee\u5149\u4e00\u51db\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u9601\u4e0b\u5012\u542c\u5f97\u3000\u6e05\u695a\u5f97\u5f88\u3002\u201d \u3000\u3000\u3000\u7aef\u6728\u65b9\u6b63\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u4e0d\u4f46\u542c\u5f97\u6e05\u695a\uff0c\u800c\u4e14\u8fd8\u770b\u5f97\u6781\u4e3a\u6e05\u695a\u54e9\u3002\u201d \u3000\u3000\u3000\u7f2a\u6587\u53cc\u76ee\u4e00\u7ffb\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4ece\u524d\u6709\u4e2a\u6781\u4e3a\u806a\u660e\u4e4b\u4eba\uff0c\u5929\u4e0b\u95ee\u4efb\u4f55\u4e8b\u90fd\u7792\u4e0d\u8fc7\u4ed6\u3000\uff0c\u4ed6 \u4e5f\u9887\u4e3a\u5f97\u610f\uff0c\u54ea\u77e5\u6211\u4f5b\u5982\u6765\u5374\u5acc\u4ed6\u542c\u5f97\u5927\u591a\uff0c\u770b\u5f97\u5927\u591a\uff0c\u53c8\u60f3\u5f97\u5927\u591a\uff0c\u5c31\u7f5a\u4ed6\u3000\u53d8\u4e3a\u4e00\u4e2a\u53c8 \u804b\u53c8\u54d1\u7684\u767d\u75f4\uff0c\u800c\u53e6\u4e00\u4eba\u5374\u8fdc\u8f83\u4ed6\u66f4\u4e3a\u806a\u660e\uff0c\u867d\u7136\u542c\u89c1\uff0c\u770b\u5230\u4e4b\u4e8b\uff0c\u4e5f\u3000\u8f83\u4ed6\u4e3a\u591a\uff0c\u5374\u4ec0\u4e48 \u90fd\u4e0d\u8bf4\u51fa\u6765\uff0c\u7ed3\u679c\u6b64\u4eba\u900d\u9065\u81ea\u5728\uff0c\u76f4\u5230\u5929\u5e74\u3002\u201d \u3000\u3000\u3000\u4ed6\u76ee\u5149\u4e00\u5782\uff0c\u76f4\u6ce8\u7740\u7aef\u6728\u65b9\u6b63\uff0c\u51b7\u51b7\u53c8\u9053\uff1a\u201c\u5144\u53f0\u53ef\u77e5\u9053\u8fd9\u6545\u4e8b\u5417\uff1f\u201d \u3000\u3000\u3000\u7aef\u6728\u65b9\u6b63\u4ef0\u5929\u7b11\u9053\uff1a\u201c\u8fd9\u6545\u4e8b\u7684\u786e\u52a8\u542c\u5f97\u5f88\uff0c\u8b6c\u5982\u8bf4\u533a\u533a\u5728\u4e0b\u5427\uff0c\u867d\u7136\u5df2\u77e5\u9053\u3000\u90a3\u5c11 \u5e74\u7ec8\u5c06\u90a3\u4efd\u2018\u85cf\u5b9d\u4e4b\u56fe\u201d\uff0c\u505a\u6210\u4e00\u4efd\u9999\u9975\uff0c\u53c8\u5c06\u8fd9\u4efd\u9999\u9975\uff0c\u653e\u5165\u4e10\u5e2e\u4e00\u4e2a\u5f1f\u5b50\u3000\u7684\u624b\u4e2d\uff0c\u5374 \u53c8\u4e0d\u77e5\u5f04\u4e86\u4e2a\u4ec0\u4e48\u624b\u6bb5\uff0c\u4f7f\u90a3\u94c1\u624b\u4ed9\u733f\u77e5\u9053\u8fd9\u4e2a\u6d88\u606f\uff0c\u5c06\u90a3\u4e10\u5e2e\u5f1f\u5b50\u6740\u3000\u4e86\uff0c\u4e00\u9762\u5374\u53c8\u6697\u5730 \u901a\u77e5\u6c34\u4e0a\u8427\u95e8\uff0c\u5d69\u5c71\u5c11\u6797\uff0c\u548c\u90a3\u2019\u7a77\u5bb6\u5e2e\u2019\u7684\u7a77\u795e\u51cc\u9f99\uff0c\u8bf4\u90a3\u2018\u3000\u85cf\u5b9d\u79d8\u56fe\u201d\u5df2\u843d\u5165\u90a3\u94c1\u624b \u4ed9\u733f\u4e4b\u624b\u2014\u2014\u201d\u4ed6\u8bed\u58f0\u4e00\u987f\uff0c\u76ee\u5149\u56db\u626b\uff0c\u53c8\u9053\uff1a\u201c\u9664\u6b64\u4e4b\u3000\u5916\uff0c\u5728\u4e0b\u8fd8\u77e5\u9053\u90a3\u5c11\u5e74\u5982\u6b64\u505a \u6cd5\uff0c\u53ea\u662f\u4e3a\u7740\u548c\u90a3\u2018\u7075\u86c7\u2019\u6bdb\u81ec\uff0c\u5177\u6709\u6df1\u4ec7\uff0c\u662f\u4ee5\u4fbf\u3000\u6311\u62e8\u5929\u4e0b\u6b66\u6797\uff0c\u5bf9\u4ed6\u7fa4\u8d77\u800c\u653b\uff0c\u60f3\u90a3 \u2018\u7075\u86c7\u2019\u6bdb\u81ec\u7fbd\u515a\u518d\u4e30\uff0c\u6b66\u529f\u518d\u9ad8\uff0c\u5374\u4e5f\u654c\u4e0d\u3000\u8fc7\u5929\u4e0b\u6b66\u6797\u7684\u529b\u91cf\u5440\uff01\u201d \u3000\u3000\u3000\u7f2a\u6587\u51b7\u201c\u54fc\u201d\u4e00\u58f0\uff0c\u5389\u58f0\u9053\uff1a\u201c\u90a3\u4e48\u9601\u4e0b\u60f3\u5fc5\u4e5f\u77e5\u9053\u90a3\u5c11\u5e74\u4fbf\u662f\u533a\u533a\u5728\u4e0b\u4e86\u3002\u3000\u201d \u3000\u3000\u3000\u7aef\u6728\u65b9\u6b63\u7b11\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d \u3000\u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u7f2a\u6587\u7a81\u5730\u5389\u53f1\u4e00\u58f0\uff0c\u8eab\u5f62\u5fae\u5c55\uff0c\u638c\u4e2d\u91d1\u5149\u4e00\u62b9\uff0c\u95ea\u7535\u822c\u5730\u6307\u5411\u7aef\u6728\u3000\u65b9\u6b63 \u524d\u80f8\uff0c\u4e00\u9762\u5389\u53f1\u9053\uff1a\u201c\u4f60\u7a76\u7adf\u662f\u8c01\uff1f\u548c\u90a3\u59d3\u6bdb\u7684\u6709\u4f55\u5173\u7cfb\uff1f\u201d \u3000\u3000\u3000\u773c\u89c1\u8fd9\u9053\u91d1\u5149\uff0c\u5df2\u582a\u582a\u88ad\u5411\u4ed6\u524d\u80f8\u7684\u201c\u4e73\u6cc9\u201d\u7a74\u4e0a\uff0c\u54ea\u77e5\u4ed6\u7adf\u7a81\u5730\u4ef0\u5929\u957f\u7b11\u8d77\u3000\u6765\u3002 \u7f2a\u6587\u4e00\u6014\uff0c\u500f\u7136\u632b\u8155\uff0c\u786c\u751f\u751f\u5c06\u638c\u4e0a\u529b\u9053\u987f\u4f4f\uff0c\u53ea\u89c1\u91d1\u82b1\u9519\u843d\uff0c\u6735\u6735\u4e0d\u79bb\u7aef\u6728\u3000\u65b9\u6b63\u7684\u8981 \u7a74\uff0c\u4f46\u5374\u6ca1\u6709\u4e00\u70b9\u771f\u7684\u6307\u5728\u4ed6\u8eab\u4e0a\uff0c\u7f2a\u6587\u5374\u53c8\u559d\u9053\uff1a\u201c\u6b64\u4e8b\u5e76\u65e0\u534a\u70b9\u53ef\u7b11\u3000\u4e4b\u5904\uff0c\u9601\u4e0b\u82e5\u662f \u518d\u7b11\u7684\u8bdd\u2014\u2014\u201d\u4ed6\u8bed\u72b9\u672a\u4e86\uff0c\u90a3\u7aef\u6728\u65b9\u6b63\u7b11\u58f0\u987f\u4f4f\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u3000\u6211\u7b11\u7684\u662f\u9601\u4e0b\u770b\u6765\u806a\u660e \u7edd\u9876\u4e86\uff0c\u4e0d\u77e5\u5374\u600e\u5730\u95ee\u51fa\u5982\u6b64\u5446\u8bdd\u6765\uff1f\u201d \u3000\u3000\u3000\u7f2a\u6587\u4e0d\u7981\u53c8\u4e3a\u4e4b\u4e00\u6014\uff0c\u5374\u542c\u4ed6\u63a5\u53e3\u9053\uff1a\u201c\u9601\u4e0b\u96be\u9053\u4e0d\u77e5\u9053\u76f4\u5230\u76ee\u524d\u4e3a\u6b62\uff0c\u90a3\u7075\u3000\u86c7\u6bdb \u81ec\u6700\u5927\u7684\u5bf9\u5934\u8fd8\u662f\u533a\u533a\u5728\u4e0b\u5417\uff1f\u9601\u4e0b\u96be\u9053\u4e0d\u77e5\u9053\u90a3\u94c1\u7b97\u5b50\u8ba1\u8c0b\u662f\u6b7b\u5728\u8c01\u7684\u624b\u4e0a\u3000\uff1f\u6211\u82e5\u548c\u90a3 \u7075\u86c7\u6bdb\u81ec\u6709\u7740\u5173\u7cfb\uff0c\u9601\u4e0b\u6b64\u523b\u8fd8\u80fd\u548c\u4ed6\u90a3\u5343\u5a07\u767e\u5a9a\u7684\u5973\u513f\u7b11\u8bed\u6e29\u67d4\u5417\uff1f\u3000\u201d\u4ed6\u8bed\u58f0\u4e00\u987f\uff0c\u53c8 \u81ea\u7eb5\u58f0\u72c2\u7b11\u8d77\u6765\u3002 \u7b2c\u5341\u4e09\u7ae0>> \u53e4\u9f99\u300a\u6e58\u5983\u5251\u300b \u7b2c\u5341\u4e09\u7ae0 \u3000\u3000\u7b11\u58f0\u672a\u7edd\uff0c\u7f2a\u6587\u4f46\u89c9\u5fc3\u4e2d\u601d\u6f6e\u7ffb\u6d8c\uff0c\u624b\u4e2d\u7684\u201c\u91d1\u5251\u201d\uff0c\u4e5f\u7f13\u7f13\u5782\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u90a3\u7aef\u6728\u65b9\u6b63\u7b11\u58f0\u53c8\u81ea\u4e00\u987f\uff0c\u76ee\u5149\u51dd\u4f4f\u7f2a\u6587\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u81ea\u4ece\u90a3\u65e5\u4e8e\u9ad8\u3001\u6d2a\u6e56\u7554\uff0c \u6697\u7779\u5144\u53f0\u8fd9\u4fca\u9762\uff0c\u4e0d\u7981\u5bf9\u5144\u53f0\u6240\u4f5c\u6240\u4e3a\uff0c\u65e2\u5947\u53c8\u4f69\uff0c\u662f\u4ee5\u8fd9\u6570\u65e5\u4ee5\u6765\uff0c\u4fbf\u65e0\u65f6\u65e0\u523b\u4e0d\u5728\u7559\u610f \u9601\u4e0b\u7684\u4e3e\u52a8\uff0c\u53ea\u89c1\u5144\u53f0\u5e74\u7eaa\u867d\u8f7b\uff0c\u884c\u4e8b\u5374\u6781\u8001\u5230\uff0c\u5c31\u8fde\u2018\u7075\u86c7\u2019\u6bdb\u81ec\u90a3\u79cd\u597d\u72e1\u4e4b\u5f92\uff0c\u90fd\u88ab\u5144 \u53f0\u7792\u5728\u9f13\u91cc\uff0c\u800c\u4e14\u5144\u53f0\u5bf9\u4ed6\u867d\u5177\u6df1\u4ec7\uff0c\u662f\u4ee5\u4fbf\u4ee5\u5176\u4eba\u4e4b\u9053\uff0c\u8fd8\u6cbb\u5176\u4eba\u4e4b\u8eab\uff0c\u6709\u4e9b\u5730\u65b9\uff0c\u56fa\u662f \u7a0d\u8fc7\u72e0\u8fa3\uff0c\u4f46\u82e5\u8bba\u5144\u53f0\u6240\u505a\u6240\u4e3a\uff0c\u5374\u65e0\u4e00\u5904\u6709\u4e8f\u5927\u8282\uff0c\u5728\u4e0b\u4e00\u751f\u867d\u5c11\u8bb8\u4eba\uff0c\u4f46\u5bf9\u5144\u53f0\uff0c\u5374\u662f \u8bda\u5fc3\u6500\u4ea4\uff0c\u5144\u53f0\u82e5\u8ba4\u5728\u4e0b\u522b\u6709\u5c45\u5fc3\uff0c\u90a3\u5374\u4ee4\u5728\u4e0b\u5931\u671b\u5f97\u5f88\u4e86\u3002\u201d \u3000\u3000\u7f2a\u6587\u62ac\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u8fd9\u7aef\u6728\u65b9\u6b63\u76ee\u5149\u68f1\u68f1\uff0c\u6b63\u6c14\u51db\u7136\uff0c\u5fc3\u4e2d\u4e0d\u7981\u5927\u751f\u611f\u6127\u4e4b\u610f\uff0c\u957f\u53f9\u4e00 \u58f0\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u7684\u786e\u5bf9\u6bdb\u81ec\u6709\u7740\u4e0d\u5171\u6234\u5929\u4e4b\u4ec7\uff0c\u7eb5\u7136\u5c06\u4e4b\u4e00\u5200\u6740\u5374\uff0c\u90fd\u4e0d\u8db3\u4ee5\u6d88\u53bb\u5fc3\u5934\u4e4b \u6068\uff0c\u662f\u4ee5\u6b63\u5982\u5144\u53f0\u6240\u8bf4\uff0c\u6709\u4e9b\u5730\u65b9\u4e0d\u514d\u7a0d\u5acc\u597d\u72e1\u72e0\u8fa3\u2014\u2014\u201d\u4ed6\u8bed\u58f0\u4e00\u987f\uff0c\u76ee\u5149\u4e2d\u6ee1\u9732\u6068\u610f\uff0c \u60e8\u75db\u7684\u5f80\u4e8b\uff0c\u53c8\u590d\u6d8c\u4e0a\u5fc3\u5934\uff0c\u6c89\u541f\u534a\u664c\uff0c\u53c8\u9053\uff1a\u201c\u4e0d\u662f\u5c0f\u53ef\u6b64\u523b\u4e0d\u80af\u5766\u8bda\u76f8\u544a\uff0c\u5374\u662f\u56e0\u7740\u6b64 \u4e8b\u56e0\u679c\u65e2\u6df1\u4e14\u5e7f\uff0c\u53c8\u6781\u590d\u6742\uff0c\u60f3\u5144\u53f0\u77e5\u6211\u8c05\u6211\uff0c\u5fc5\u4e5f\u4e0d\u4f1a\u89c1\u602a\u7684\u5427\uff1f\u201d \u3000\u3000\u7aef\u6728\u65b9\u6b63\u4e00\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u4eca\u591c\u6df1\u591c\u6253\u6270\uff0c\u5374\u662f\u4e3a\u7740\u4e00\u4e8b\u3002\u201d \u3000\u3000\u7f2a\u6587\u9053\uff1a\u201c\u4f46\u80fd\u76f8\u544a\uff0c\u65e0\u4e0d\u5c3d\u8a00\u3002\u201d \u3000\u3000\u201c\u5728\u4e0b\u6b64\u6570\u65e5\u4ee5\u6765\uff0c\u867d\u5bf9\u5144\u53f0\u5df2\u591a\u4e86\u89e3\uff0c\u4f46\u6709\u4e00\u4e8b\uff0c\u5374\u4ee4\u5728\u4e0b\u53cd\u590d\u601d\u4e4b\uff0c\u4ea6\u4e0d\u5f97\u5176 \u89e3\u3002\u201d \u3000\u3000\u4ed6\u5fae\u5fae\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u5144\u53f0\u90a3\u4efd\u2018\u85cf\u5b9d\u4e4b\u56fe\u2019\uff0c\u60f3\u5fc5\u5f97\u81ea\u90a3\u4e00\u4ee3\u5947\u4eba\u2018\u6d77\u5929\u5b64\u71d5\u2019\uff0c\u66f4 \u53c8\u4e0e\u90a3\u6c34\u4e0a\u5927\u8c6a\u2018\u4e94\u6e56\u9f99\u738b\u2019\u9f99\u8001\u524d\u8f88\u5b58\u7740\u6781\u4e0d\u5bfb\u5e38\u4e4b\u5173\u8fde\uff0c\u800c\u5144\u53f0\u5728\u90a3\u4e9b\u94c1\u7bb1\u4e4b\u4e2d\u6240\u88c5\u4e4b \u6d88\u606f\u5f29\u7bad\uff0c\u5374\u4e0e\u90a3\u6570\u5341\u5e74\u524d\u996e\u8a89\u5929\u4e0b\u7684\u2018\u5723\u624b\u4e66\u751f\u2019\u6df3\u4e8e\u72ec\u79c0\u540c\u51fa\u4e00\u8f99\uff0c\u60f3\u8fd9\u4e09\u4f4d\u8001\u524d\u8f88\u4ff1 \u5f52\u9690\u591a\u65f6\uff0c\u5374\u4e0d\u77e5\u5144\u53f0\u600e\u5730\u80fd\u5f97\u5230\u8fd9\u4e09\u4f4d\u8001\u524d\u8f88\u7684\u4f20\u6388\uff0c\u8fd9\u5012\u786e\u662f\u5f02\u6570\u4e86\u3002\u201d \u3000\u3000\u7f2a\u6587\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4e09\u4f4d\u8001\u524d\u8f88\u6b64\u523b\u5171\u9690\u4e8e\u4e00\u6d77\u5916\u5b64\u5c9b\uff0c\u5c0f\u53ef\u5e7c\u906d\u5b64\u9732\uff0c\u4fbf\u662f\u591a\u4e8f \u8fd9\u4e09\u4f4d\u6069\u5e08\u6559\u517b\u6210\u4eba\u7684\u3002\u201d \u3000\u3000\u7aef\u6728\u65b9\u6b63\u4e00\u62cd\u524d\u989d\uff0c\u7b11\u9053\uff1a\u201c\u96be\u602a\u5144\u53f0\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u8eab\u624b\u5374\u6041\u5730\u60ca\u4eba\uff0c\u5374\u539f\u6765\u662f\u51fa\u81ea\u8fd9\u4e09 \u4f4d\u524d\u8f88\u5f02\u4eba\u7684\u95e8\u4e0b\uff0c\u8fd9\u5c31\u96be\u602a\u4e86\u3002\u201d\u7f2a\u6587\u5374\u53c8\u7b11\u9053\uff1a\u201c\u5c0f\u53ef\u4ea6\u6709\u4e00\u4e8b\u60f3\u8bf7\u6559\u5144\u53f0\u3002\u201d \u3000\u3000\u7aef\u6728\u65b9\u6b63\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u4ea6\u662f\u77e5\u65e0\u4e0d\u8a00\u3002\u201d \u3000\u3000\u7f2a\u6587\u9053\uff1a\u201c\u4e0d\u77e5\u5144\u53f0\u51fa\u4e8e\u6b66\u5f53\u90a3\u4f4d\u9053\u957f\u95e8\u4e0b\uff1f\u201d \u3000\u3000\u7aef\u6728\u65b9\u6b63\u7b11\u9053\uff1a\u201c\u5c0f\u53ef\u672c\u662f\u4e00\u4e2a\u4e66\u751f\uff0c\u4e13\u597d\u6536\u96c6\u53e4\u4e66\u65e7\u518c\uff0c\u751a\u81f3\u65ad\u7b80\u6b8b\u7ae0\uff0c\u5374\u5728\u65e0\u610f\u4e4b \u4e2d\uff0c\u53d1\u73b0\u4e00\u672c\u6614\u5e74\u6b66\u5f53\u4e00\u4ee3\u5251\u8c6a\u7684\u8001\u524d\u8f88\u9057\u7559\u4e0b\u7684\u6b66\u529f\u79d8\u7626\uff0c\u90a3\u2018\u85cf\u5b9d\u4e4b\u56fe\u2019\uff0c\u4fbf\u4e5f\u662f\u9644\u4e8e \u5176\u4e0a\u3002\u201d \u3000\u3000\u7f2a\u6587\u4ea6\u5927\u7b11\u9053\uff1a\u201c\u8fd9\u5c31\u96be\u602a\u4e86\u3002\u201d \u3000\u3000\u62ac\u76ee\u4e00\u671b\uff0c\u5374\u89c1\u8fd9\u7aef\u6728\u65b9\u6b63\u76ee\u4e2d\u4ea6\u73b0\u51fa\u6c89\u601d\u4e4b\u8272\uff0c\u60f3\u662f\u4e5f\u5728\u56de\u5fc6\u4ec0\u4e48\uff0c\u6697\u9053\uff1a\u201c\u96be\u9053\u6b64 \u4eba\u4e5f\u6709\u7740\u4ec0\u4e48\u60e8\u75db\u4e4b\u5f80\u4e8b\u4e0d\u6210\uff1f\u201d \u3000\u3000\u53ea\u542c\u7aef\u6728\u65b9\u6b63\u7f13\u7f13\u53f9\u9053\uff1a\u201c\u5341\u4e03\u5e74\u524d\uff0c\u5728\u4e0b\u8fd8\u662f\u4e2a\u8d2b\u82e6\u4e66\u751f\u65f6\uff0c\u4e00\u5929\u7f13\u6b65\u9053\u4e0a\uff0c\u5374\u89c1\u5230 \u4e00\u73ed\u5f3a\u5f92\uff0c\u98de\u9a91\u5b98\u9053\uff0c\u4e00\u8a00\u4e0d\u5408\uff0c\u4fbf\u52ab\u4e86\u5c0f\u53ef\u6545\u5c45\u57ce\u5185\u2018\u632f\u6b66\u9556\u5c40\u2019\u7684\u9556\uff0c\u5374\u5c06\u94f6\u5b50\u629b\u5f97\u4e00 \u5730\uff0c\u5c0f\u53ef\u5fc3\u4e2d\u6b63\u81ea\u4e0d\u61c2\uff0c\u54ea\u77e5\u5374\u6709\u7740\u80cc\u63d2\u957f\u5251\u7684\u9053\u4eba\uff0c\u95ee\u6211\u53ef\u8981\u5b66\u6b66\uff0c\u53c8\u8981\u5c06\u6211\u6536\u5f52\u95e8\u4e0b\uff0c \u6211\u89c1\u8fd9\u9053\u4eba\u4ea6\u662f\u548c\u90a3\u73ed\u5f3a\u5f92\u4e00\u8def\uff0c\u4fbf\u65ad\u7136\u62d2\u7edd\u4e86\u3002\u201d\u4ed6\u76ee\u5149\u4e00\u62ac\uff0c\u53c8\u9053\uff1a\u201c\u540e\u6765\u6211\u77e5\u9053\u90a3\u73ed \u5f3a\u5f92\uff0c\u4fbf\u662f\u4ee5\u2018\u7075\u86c7\u2019\u6bdb\u81ec\u4e3a\u9996\uff0c\u662f\u4ee5\u827a\u6210\u4e4b\u540e\uff0c\u51e1\u662f\u4e0e\u90a3\u59d3\u6bdb\u7684\u6709\u5173\u4e4b\u9556\u5c40\u6240\u4fdd\u4e4b\u9556\uff0c\u5728 \u4e0b\u4fbf\u52a8\u624b\u52ab\u6765\u3002\u201d \u3000\u3000\u4ed6\u4ef0\u5929\u4e00\u7b11\uff1a\u201c\u8fd9\u5374\u4e5f\u662f\u4ee5\u5176\u4eba\u4e4b\u9053\u8fd8\u6cbb\u5176\u4eba\u4e4b\u8eab\u54e9\uff01\u201d \u3000\u3000\u4e24\u4eba\u76ee\u5149\u76f8\u5bf9\uff0c\u4e0d\u7981\u9f50\u5404\u629a\u638c\u800c\u7b11\uff0c\u7f2a\u6587\u5148\u524d\u5bf9\u8fd9\u201c\u91d1\u5251\u4fa0\u201d\u867d\u7136\u6df1\u5177\u731c\u5fcc\u6000\u7591\u4e4b\u5fc3\uff0c \u4f46\u6b64\u523b\u5374\u5df2\u4e3a\u4e4b\u5c3d\u6d88\uff0c\u53cd\u751f\u76f8\u60dc\u4e4b\u5ff5\u3002 \u3000\u3000\u4ed6\u81ea\u5e7c\u81f3\u957f\uff0c\u751f\u547d\u4e2d\u8fd9\u4e00\u6bb5\u6700\u6700\u6b22\u4e50\u7684\u65f6\u65e5\uff0c\u90fd\u5728\u4e00\u4e2a\u65b9\u5706\u4e0d\u8fc7\u767e\u5341\u91cc\u7684\u5b64\u5c9b\u4e0a\u6e21\u8fc7\uff0c \u76f8\u5904\u4e4b\u4eba\uff0c\u975e\u5e08\u5373\u957f\uff0c\u90a3\u4e9b\u5f52\u9690\u5728\u5b64\u5c9b\u4e0a\u7684\u6b66\u6797\u5947\u4eba\uff0c\u5bf9\u4ed6\u867d\u6781\u4eb2\u5207\u7231\u62a4\uff0c\u4f46\u7a76\u7adf\u5e74\u9f84\u60ac \u6b8a\uff0c\u4f55\u51b5\u8fd9\u4e9b\u6b66\u6797\u5947\u4eba\u4e45\u5df2\u538c\u5026\u98ce\u5c18\uff0c\u591a\u5e74\u6765\u7684\u6d77\u5916\u5b64\u5c9b\u5c81\u6708\uff0c\u66f4\u5c06\u4ed6\u4eec\u9676\u51b6\u5f97\u6781\u4e3a\u606c\u6de1\uff0c \u4ed6\u4eec\u867d\u5bf9\u7f2a\u6587\u6781\u4e3a\u7231\u62a4\uff0c\u4f46\u4e5f\u4e0d\u4f1a\u653e\u5728\u8868\u9762\u4e0a\uff0c\u662f\u4ee5\u7f2a\u6587\u6709\u751f\u4ee5\u6765\uff0c\u53ef\u8bf4\u662f\u4ece\u672a\u4eab\u53d7\u5230\u53cb\u60c5 \u7684\u6e29\u6696\uff0c\u518d\u52a0\u4ee5\u4ed6\u5fd7\u5207\u6df1\u4ec7\uff0c\u5fc3\u60c5\u4fbf\u4e5f\u672a\u514d\u5931\u4e8e\u504f\u6fc0\u3002 \u3000\u3000\u800c\u6b64\u523b\u4ed6\u4e0e\u8fd9\u7aef\u6728\u65b9\u6b63\u8a00\u7b11\u76f8\u5bf9\uff0c\u5fc3\u4e2d\u5374\u6e10\u6e10\u611f\u53d7\u5230\u201c\u53cb\u60c5\u201d\u4e24\u5b57\u4e4b\u610f\u4e49\uff0c\u8fd9\u5374\u662f\u4ed6\u6709 \u751f\u4ee5\u6765\u6240\u4ece\u672a\u611f\u53d7\u8fc7\u7684\u60c5\u611f\u3002 \u3000\u3000\u98ce\u5439\u6797\u6728\uff0c\u7c41\u7136\u4f5c\u54cd\uff0c\u4e24\u4eba\u5e76\u80a9\u800c\u884c\uff0c\u7aef\u6728\u65b9\u6b63\u7a81\u5730\u7b11\u9053\uff1a\u201c\u6b64\u523b\u4e1c\u65b9\u6e10\u767d\uff0c\u5728\u4e0b\u867d\u4ecd \u60f3\u4e0e\u5144\u53f0\u76d8\u6853\u4e9b\u65f6\uff0c\u4f46\u4ea6\u77e5\u5144\u53f0\u4e0d\u80fd\u518d\u591a\u9017\u7559\uff0c\u6765\u65e5\u65b9\u957f\uff0c\u4f60\u6211\u76f8\u89c1\u6709\u671f\uff0c\u53ea\u8981\u5144\u53f0\u4e0d\u5acc \u5f03\uff0c\u5c0f\u5f1f\u968f\u65f6\u53ef\u6765\u5bfb\u8bbf\u5144\u53f0\u7684\uff0c\u53ef\u662f\u2014\u2014\u201d\u4ed6\u5fae\u5fae\u4e00\u53f9\uff0c\u53c8\u9053\uff1a\u201c\u5144\u53f0\u65e2\u662f\u8eab\u6000\u6df1\u4ec7\uff0c\u5c31\u66f4 \u987b\u5c0f\u5fc3\u8c28\u614e\uff0c\u90a3\u2018\u7075\u86c7\u2019\u6bdb\u81ec\u9634\u6c89\u597d\u72e1\uff0c\u57ce\u5e9c\u6781\u6df1\uff0c\u6b64\u523b\u8868\u9762\u770b\u6765\uff0c\u867d\u5bf9\u5144\u53f0\u4e00\u65e0\u6000\u7591\u4e4b \u5ff5\uff0c\u4f46\u6697\u4e2d\u5374\u672a\u5fc5\u5982\u662f\uff0c\u5144\u53f0\u5929\u59ff\u82f1\u53d1\uff0c\u4fbf\u81ea\u53e4\u4ee5\u6765\uff0c\u82f1\u96c4\u4eba\u7269\uff0c\u672a\u6709\u4e0d\u591a\u60c5\u8005\uff0c\u5144\u53f0\u5bf9\u8fd9 \u2018\u60c5\uff0c\u4e4b\u4e00\u5b57\uff0c\u5c24\u5176\u8981\u770b\u5f97\u900f\u4e9b\u3002\u201d\u7f2a\u6587\u5fc3\u4e2d\u4e00\u51db\uff0c\u8bda\u58f0\u9053\uff1a\u2018\u543e\u5144\u91d1\u8a00\uff0c\u5c0f\u5f1f\u6562\u4e0d\u4ece \u547d\u3002\u201d\u5fc3\u91cc\u60f3\u8d77\u81ea\u5df1\u7684\u7239\u7239\u548c\u90a3\u77f3\u78f7\uff0c\u53c8\u5c82\u975e\u90fd\u662f\u4e3a\u4e86\u201c\u60c5\u201d\u4e4b\u4e00\u5b57\uff0c\u662f\u4ee5\u4e00\u4e2a\u5c0f\u5e74\u4ea1\u6545\uff0c \u4e00\u4e2a\u5374\u98a0\u6c9b\u7ec8\u751f\uff0c\u4e0d\u7981\u6697\u6697\u53f9\u606f\u4e00\u58f0\uff0c\u76ee\u5149\u62ac\u5904\uff0c\u53ea\u89c1\u8fd9\u7aef\u6728\u65b9\u6b63\u9762\u4e0a\u6ee1\u662f\u8bda\u631a\u4e4b\u5149\uff0c\u4f38\u624b \u7d27\u7d27\u4e00\u63e1\u81ea\u5df1\u7684\u624b\u8155\uff0c\u98d8\u7136\u800c\u53bb\u3002\u6708\u6e10\u897f\u6c89\uff0c\u661f\u5149\u5df2\u9690\uff0c\u6653\u98ce\u6b8b\u6708\uff0c\u5df2\u6709\u6599\u5ced\u4e4b\u610f\uff0c\u7ad9\u5728\u6653 \u98ce\u91cc\uff0c\u7f2a\u6587\u51fa\u795e\u5730\u671b\u7740\u4ed6\u7684\u80cc\u5f71\uff0c\u5446\u5446\u5730\u6115\u4e86\u534a\u664c\uff0c\u89c9\u5f97\u6b64\u4eba\u771f\u662f\u5982\u5929\u9645\u795e\u9f99\uff0c\u592d\u77eb\u6765\u53bb\uff0c \u60f3\u5230\u4ed6\u4e34\u53bb\u4e4b\u9645\u6240\u8bf4\u7684\u8bdd\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u66f4\u662f\u4e07\u5ff5\u4ff1\u751f\uff0c\u4e0d\u80fd\u81ea\u5df1\u3002\u4ed6\u4ef0\u89c6\u82cd\u7a79\uff0c\u9eef\u7136\u4f4e\u8bed \u9053\uff1a\u201c\u4ec7\u6055\u5440\uff0c\u4ec7\u6055\uff0c\u4f60\u540d\u867d\u53eb\u4ec7\u6055\uff0c\u7236\u4ec7\u5374\u7edd\u4e0d\u53ef\u6055\uff0c\u4f46\u662f\u4f60\u53c8\u600e\u80fd\u5fd8\u5374\u90a3\u4e00\u624b\u5c06\u4f60\u629a\u517b \u6210\u4eba\u7684\u6bcd\u4eb2\u66ff\u4f60\u53d6\u8fd9\u540d\u5b57\u7684\u7528\u5fc3\u5462\uff1f\u4f60\u82e5\u624b\u5203\u4e86\u4ec7\u4eba\uff0c\u5c82\u975e\u8981\u4f24\u4e86\u4f60\u6bcd\u4eb2\u4e4b\u5fc3\uff0c\u4f60\u82e5\u4e0d\u62a5\u6b64 \u6df1\u4ec7\uff0c\u5374\u53c8\u600e\u5bf9\u5904\u8d77\u4f60\u7239\u7239\u7684\u5728\u5929\u4e4b\u7075\uff1f\u201d \u3000\u3000\u4ed6\u6c89\u91cd\u5730\u53f9\u606f\u4e00\u58f0\uff0c\u53c8\u81ea\u9eef\u7136\u9053\uff1a\u201c\u82cd\u5929\u5440\u82cd\u5929\uff0c\u4f60\u80fd\u544a\u8bc9\u6211\uff0c\u8be5\u600e\u4e48\u529e\u5417\uff1f\u7239\u7239\u5440\u7239 \u7239\uff0c\u6211\u77e5\u9053\u4f60\u662f\u6df1\u7231\u7740\u6bcd\u4eb2\u7684\uff0c\u4f46\u6211\u4e3a\u7740\u4f60\uff0c\u5374\u53c8\u4e0d\u5f97\u4e0d\u4ee4\u6bcd\u4eb2\u4f24\u5fc3\u4ed6\u72e0\u72e0\u4e00\u8dfa\u811a\uff1a\u201c\u6211\u4e0d \u7ba1\u4f60\u8001\u4eba\u5bb6\u662f\u600e\u6837\u7684\u4e00\u4e2a\u4eba\uff0c\u4f46\u6211\u77e5\u9053\u4f60\u8001\u4eba\u5bb6\u662f\u6b63\u76f4\u7684\uff0c\u5351\u52a3\u65e0\u803b\u7684\u4e8b\uff0c\u4f60\u8001\u4eba\u5bb6\u7edd\u5bf9\u4e0d \u4f1a\u505a\uff0c\u65e0\u8bba\u662f\u8c01\u6740\u6b7b\u4e86\u4f60\u8001\u4eba\u5bb6\uff0c\u6211\u90fd\u8981\u4e3a\u4f60\u62a5\u4ec7\uff0c\u54ea\u6015\u2026\u2026\u54ea\u6015\u90a3\u4eba\u662f\u6211\u5988\u5988\u7684\u5ae1\u4eb2\u5144 \u5f1f\u3002\u201d \u3000\u3000\u6668\u5149\u5fae\u66e6\u4e2d\uff0c\u4ed6\u6025\u6b65\u8d70\u56de\u5bbf\u8fc1\u57ce\uff0c\u5fc3\u4e2d\u5df2\u4e0b\u4e86\u51b3\u5fc3\uff0c\u65e0\u8bba\u4efb\u4f55\u4e00\u4e8b\u90fd\u4e0d\u80fd\u5f71\u54cd\u4ed6\uff0c\u6539\u53d8 \u4ed6\u79bb\u5f00\u90a3\u201c\u6d77\u5929\u5b64\u5c9b\uff0c\u65f6\u6240\u7acb\u4e0b\u7684\u610f\u5ff5\uff0c\u90a3\u5c31\u662f\u590d\u4ec7\uff0c\u4e5f\u8bb8\u4ed6\u4e0d\u4f1a\u4eb2\u624b\u6740\u6b7b\u201d\u7075\u86c7\u201d\u6bdb\u81ec\uff0c \u4f46\u4ed6\u5374\u8981\u4f7f\u8fd9\u540d\u6ee1\u5929\u4e0b\u7684\u6b66\u6797\u67ad\u96c4\uff0c\u6b7b\u5728\u81ea\u5df1\u4e00\u624b\u5e03\u4e0b\u7684\u7f57\u7f51\u4e4b\u4e2d\u3002 \u3000\u3000\u4ed6\u7684\u8eab\u5f62\u662f\u65e0\u6bd4\u8f7b\u7075\u800c\u8fc5\u5feb\u7684\uff0c\u5373\u4f7f\u6b64\u523b\u5df2\u5c06\u8fd1\u65e5\u51fa\uff0c\u4f46\u5728\u8fd9\u79cd\u5fae\u660e\u7684\u6668\u5149\u4e4b\u4e2d\uff0c\u4eba\u4eec \u4ecd\u7136\u65e0\u6cd5\u8fa8\u6e05\u4ed6\u7684\u8eab\u5f62\uff0c\u7eb5\u7136\u770b\u5230\u4e86\uff0c\u4e5f\u4f1a\u7591\u60d1\u662f\u81ea\u5df1\u773c\u82b1\uff0c\u56e0\u4e3a\u5f88\u5c11\u6709\u4eba\u4f1a\u76f8\u4fe1\u4eba\u7c7b\u4f1a\u6709 \u5982\u6b64\u5feb\u7684\u8eab\u6cd5\u7684\u3002 \u3000\u3000\u4ed6\u5c3d\u4e86\u5168\u529b\uff0c\u5e0c\u5180\u81ea\u5df1\u80fd\u5728\u6bdb\u6587\u742a\u4e00\u89c9\u7761\u9192\u4e4b\u524d\u8d76\u56de\u53bb\uff0c\u65b9\u624d\u548c\u90a3\u7aef\u6728\u65b9\u6b63\u7684\u4e00\u5915\u7545 \u8c08\uff0c\u6b64\u523b\u867d\u4ecd\u5728\u4ed6\u5fc3\u4e2d\u6fc0\u8361\u4e0d\u5df2\uff0c\u56e0\u4e3a\u90a3\u9017\u8d77\u4e86\u4ed6\u5f80\u4e8b\u7684\u601d\u6f6e\uff0c\u4e5f\u9017\u8d77\u4e86\u4ed6\u5bf9\u6765\u65e5\u7684\u5fe7\u90c1\u3002 \u3000\u3000\u51cc\u6668\u7684\u7a7a\u6c14\uff0c\u50cf\u88ab\u6c34\u6d17\u8fc7\u4f3c\u7684\u6f6e\u6e7f\u800c\u6e05\u65b0\uff0c\u51cc\u6668\u7684\u57ce\u5e02\uff0c\u4ea6\u6709\u5982\u51cc\u6668\u7684\u7a7a\u6c14\uff0c\u8fd9\u662f\u6c5f\u5357 \u7684\u6625\u5929\u6240\u901a\u5e38\u6709\u7684\u597d\u5929\u6c14\u3002 \u3000\u3000\u6ed1\u8fc7\u65e0\u6570\u5c4b\u810a\uff0c\u4ed6\u56de\u5230\u5ba2\u6808\uff0c\u626b\u76ee\u56db\u671b\uff0c\u4ed6\u90a3\u95f4\u623f\u7684\u7a97\u6237\uff0c\u4ecd\u50cf\u4ed6\u63a0\u51fa\u65f6\u4e00\u6837\u5730\u655e\u5f00 \u7740\uff0c\u4e00\u5207\u90fd\u6ca1\u6709\u53d8\u52a8\uff0c\u56db\u4e0b\u662f\u9759\u5bc2\u7684\uff0c\u8c01\u4e5f\u4e0d\u80fd\u53d1\u73b0\u4ed6\u66fe\u7ecf\u79bb\u5f00\u8fc7\uff0c\u4ed6\u6ee1\u610f\u5730\u6697\u4e2d\u5fae\u7b11\u4e00 \u4e0b\u3002\u5fae\u64a9\u957f\u886b\uff0c\u907f\u514d\u7740\u8863\u8882\u53ef\u80fd\u8d77\u7684\u98ce\u58f0\uff0c\u50cf\u6e38\u9c7c\u822c\u6ed1\u8fdb\u4e86\u7a97\u6237\u3002 \u3000\u3000\u4f46\u662f\u2026\u2026 \u3000\u3000\u5f53\u4ed6\u76ee\u5149\u77a5\u4eba\u5ba4\u5185\u7684\u90a3\u4e00\u5239\u90a3\uff0c\u4ed6\u524d\u8fdb\u7684\u8eab\u8eaf\u4fbf\u6597\u7136\u505c\u987f\u4e86\u4e0b\u6765\uff0c\u53ea\u624b\u4e00\u6309\u7a97\u68ad\uff0c\u51cc\u7a7a \u4e00\u4e2a\u7ffb\u8eab\uff0c\u56e0\u4e3a\u4ed6\u76ee\u5149\u52a8\u5904\uff0c\u7adf\u53d1\u73b0\u4e00\u53cc\u7a7f\u7740\u7c89\u5e95\u5feb\u9774\u7684\u811a\uff0c\u9ad8\u9ad8\u7fd8\u8d77\u5728\u90a3\u5f20\u6728\u5e8a\u7684\u7a97\u67b6 \u4e0a\u3002 \u3000\u3000\u5e74\u4e45\u5931\u4fee\u7684\u7a97\u6839\uff0c\u5728\u4ed6\u8fd9\u5168\u8eab\u771f\u529b\u731b\u4e00\u6536\u64a4\u7684\u4e00\u6309\u4e4b\u4e0b\uff0c\u53d1\u51fa\u201c\u5431\u201d\u7684\u4e00\u54cd\u3002 \u3000\u3000\u9759\u5bc2\u7684\u623f\u95f4\u91cc\uff0c\u4e5f\u54cd\u8d77\u4e00\u9635\u8f7b\u5fae\u7684\u7b11\u58f0\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4f60\u56de\u6765\u4e86\uff1f\u201d \u3000\u3000\u7f2a\u6587\u5fc3\u5934\u84e6\u5730\u4e00\u8df3\uff0c\u500f\u7136\u98d8\u843d\u5728\u5730\u4e0a\uff0c\u53ea\u89c1\u7a97\u53e3\u4eba\u5f71\u4e00\u82b1\uff0c\u4e00\u4e2a\u61d2\u6563\u800c\u6f47\u6d12\u7684\u8eab\u5f62\uff0c\u7a81 \u5730\u81ea\u7a97\u53e3\u73b0\u51fa\uff0c\u9762\u4e0a\u4ecd\u81ea\u5e26\u7740\u6de1\u6de1\u7684\u7b11\u5bb9\uff0c\u7f13\u7f13\u53c8\u9053\uff1a\u201c\u51b3\u8fdb\u6765\u5427\uff0c\u8fd9\u91cc\u518d\u6ca1\u6709\u522b\u7684\u4eba \u4e86\u3002\u201d \u3000\u3000\u7f2a\u6587\u5df2\u7ecf\u7ef7\u7d27\u4e86\u7684\u5fc3\u5f26\uff0c\u6b64\u523b\u4e3a\u4e4b\u4e00\u677e\uff0c\u56e0\u4e3a\u8fd9\u8eab\u5f62\u5e76\u4e0d\u662f\u4ed6\u6240\u754f\u60e7\u7684\uff0c\u800c\u662f\u90a3\u5728\u676d\u5dde \u4e00\u522b\uff0c\u4fbf\u65e0\u97f3\u8baf\u7684\u77f3\u78f7\uff01 \u3000\u3000\u4e8e\u662f\u4ed6\u4ea6\u81ea\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u77f3\u5144\u600e\u5730\u6765\u4e86\uff1f\u201d\u63d0\u6c14\u7eb5\u8eab\uff0c\u8dc3\u5165\u7a97\u5185\uff0c\u56de\u8eab\u5c06\u9ad8\u9ad8\u652f\u8d77 \u7684\u7a97\u6237\u653e\u4e86\u4e0b\u6765\uff0c\u623f\u95f4\u5185\u4fbf\u9aa4\u7136\u4e00\u6697\uff0c\u90a3\u652f\u8721\u70db\u4ed6\u65b9\u624d\u63a0\u51fa\u65f6\u867d\u4ecd\u662f\u71c3\u7740\u7684\uff0c\u4f46\u6b64\u523b\u5374\u65e9\u5df2 \u71c3\u706d\u4e86\u3002\u4ed6\u4fa7\u76ee\u4e00\u987e\u77f3\u78f7\uff0c\u5fc3\u4e2d\u6697\u5fd6\uff1a\u201c\u4ed6\u6765\u65f6\u5b9a\u5fc5\u5c1a\u71c3\uff0c\u90a3\u4e48\u4e00\u5b9a\u662f\u4ed6\u5439\u7184\u7684\u4e86\uff0c\u5947\u602a\u7684 \u662f\u4ed6\u600e\u77e5\u9053\u6211\u4f4f\u4e8e\u6b64\u5904\uff0c\u6765\u6b64\u5bfb\u627e\u4e8e\u6211\uff0c\u53ef\u662f\u6709\u4f55\u7528\u610f\u5462\uff1f\u201d\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u5c0f\u5f1f\u9002\u624d\u5916\u51fa\uff0c \u4ee5\u81f3\u77f3\u5144\u6765\u6b64\u7a7a\u5019\uff0c\u5b9e\u662f\u62b1\u6b49\u5f97\u5f88\u3002\u201d\u4e3e\u624b\u4e00\u8ba9\uff0c\u81ea\u5df1\u4e5f\u5750\u5230\u6905\u4e0a\uff0c\u53ea\u542c\u90bb\u5ba4\u4e00\u65e0\u58f0\u606f\uff0c\u90a3 \u6bdb\u6587\u742a\u60f3\u5fc5\u7761\u5f97\u4ecd\u719f\u3002 \u3000\u3000\u77f3\u78f7\u542b\u7b11\u5750\u5230\u6905\u4e0a\uff0c\u9053\uff1a\u201c\u53e4\u4eba\u79c9\u70db\u591c\u6e38\uff0c\u60f3\u4e0d\u5230\u4ec7\u5144\u4ea6\u6709\u6b64\u96c5\u5174\u3002\u53ea\u53ef\u60dc\u5c0f\u5f1f\u6765\u8fdf\u4e00 \u6b65\uff0c\u672a\u80fd\u4f5c\u4ec7\u5144\u4e4b\u6e38\u4f34\u3002\u201d \u3000\u3000\u7f2a\u6587\u9762\u8272\u4e00\u53d8\uff0c\u84e6\u7136\u4ece\u6905\u4e0a\u7ad9\u4e86\u8d77\u6765\uff0c\u76ee\u5149\u76f4\u89c6\u77f3\u78f7\uff0c\u5374\u89c1\u77f3\u78f7\u76ee\u5149\u4e2d\u7199\u7199\u548c\u548c\uff0c\u534a\u70b9 \u4e5f\u6ca1\u6709\u6076\u610f\uff0c\u9042\u53c8\u957f\u53f9\u4e00\u58f0\uff0c\u5750\u56de\u6905\u4e0a\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5c0f\u5f1f\u6b63\u662f\u59d3\u4ec7\uff0c\u5c0f\u5f1f\u65e9\u5c31\u77e5\u9053\u662f\u7792\u4e0d \u8fc7\u77f3\u5144\u7684\u4e86\u3002\u201d \u3000\u3000\u77f3\u78f7\u5fae\u559f\u9053\uff1a\u201c\u5176\u5b9e\u5144\u53f0\u4e5f\u6bcb\u5eb8\u76f8\u7792\u4e8e\u6211\uff0c\u5341\u4e03\u5e74\u524d\u2026\u2026\u201d \u3000\u3000\u4ed6\u6c89\u91cd\u5730\u53f9\u606f\u4e00\u58f0\uff0c\u53c8\u9053\uff1a\u201c\u6211\u4e0e\u4ee4\u5802\u5927\u4eba\u672c\u662f\u77e5\u4ea4\uff0c\u8fd9\u5341\u4e03\u5e74\u6765\u6211\u98d8\u6cca\u6c5f\u6e56\uff0c\u4e5f\u65e0\u975e \u662f\u60f3\u77e5\u9053\u4f60\u4eec\u7684\u4e0b\u843d\uff0c\u60f3\u8981\u77e5\u9053\u4f60\u4eec\u662f\u5426\u5e73\u5b89\uff0c\u5982\u4eca\u559c\u89c1\u4f60\u5df2\u957f\u6210\uff0c\u53c8\u5982\u6b64\u82f1\u4fca\uff0c\u6211\u4e5f\u9ad8\u5174 \u5f97\u5f88\uff0c\u5509\uff01\u5341\u2014\u2014\u4e03\u5e74\u7684\u65f6\u65e5\uff0c\u5f39\u6307\u95f4\u8fc7\uff0c\u6211\u4e24\u9b13\u6e10\u6591\uff0c\u4ee4\u5802\u5927\u4eba\u60f3\u5fc5\u4e5f\u8001\u4e86\u8bb8\u591a\u5427\uff1f\u201d \u3000\u3000\u4ece\u7a97\u5e95\u95f4\u6620\u5165\u7684\u6668\u5149\uff0c\u9eef\u6de1\u5730\u6620\u5728\u8fd9\u6614\u5e74\u7684\u5e74\u8f7b\u540d\u5251\u624b\u8eab\u4e0a\u3002 \u3000\u3000\u901d\u53bb\u7684\u5e74\u534e\uff0c\u5f80\u4e8b\u7684\u8ffd\u5fc6\uff0c\u4f7f\u5f97\u4ed6\u9762\u4e0a\u60ef\u6709\u7684\u7b11\u5bb9\u4e5f\u4e3a\u4e4b\u6d88\u5931\uff0c\u7f2a\u6587\u5583\u5583\u9053\uff1a\u201c\u534e\u53d1\u5c06 \u6591\uff0c\u534e\u53d1\u5c06\u6591\u2026\u2026\u201d\u76ee\u5149\u4e00\u62ac\uff1a\u201c\u5bb6\u6bcd\u8fd9\u4e9b\u5e74\u6765\u7684\u786e\u5df2\u8001\u4e86\uff0c\u5979\u8001\u4eba\u5bb6\u7684\u5934\u53d1\u4e0d\u662f\u5c06\u6591\uff0c\u800c \u662f\u5168\u767d\u4e86\uff0c\u5509\uff01\u5fe7\u90c1\u7684\u65e5\u5b50\uff0c\u4e00\u5e74\u6bd4\u4e24\u5e74\u8fd8\u957f\uff0c\u8fd9\u662f\u5bb6\u6bcd\u5e38\u8bf4\u7684\u8bdd\uff0c\u77f3\u2026\u2026\u77f3\u53d4\u7236\uff0c\u4f60\u8bf4\u5bf9 \u5417\uff1f\u201d \u3000\u3000\u77f3\u78f7\u7f13\u7f13\u70b9\u4e86\u70b9\u5934\uff0c\u76ee\u5149\u6c89\u91cd\u5730\u7559\u6ede\u5728\u7070\u9ed1\u8272\u7684\u5730\u9762\u4e0a\uff0c\u9053\uff1a\u201c\u4f60\u8fd8\u662f\u53eb\u6211\u77f3\u5144\u7684 \u597d\u2026\u2026\u8fd9\u4e9b\u5e74\u6765\uff0c\u6211\u7684\u751f\u6d3b\u50cf\u662f\u5df2\u4e0e\u5f80\u4e8b\u8131\u4e86\u8282\uff0c\u53ea\u6709\u6b64\u523b\uff0c\u89c1\u7740\u4e86\u4f60\uff0c\u5f80\u4e8b\u867d\u7136\u4e0d\u582a\u56de \u9996\uff0c\u5374\u4e5f\u5bb9\u4e0d\u5f97\u6211\u4e0d\u53bb\u60f3\u5b83\u4e86\uff0c\u8001\u5f1f\uff0c\u4ee4\u5802\u8fd9\u4e9b\u5e74\u6765\u53ef\u8fd8\u597d\u5427\uff1f\u8fd9\u4e9b\u5e74\u6765\uff0c\u4f60\u4eec\u662f\u600e\u4e48\u751f\u6d3b \u7684\u5462\uff1f\u201d \u3000\u3000\u4ed6\u7684\u76ee\u5149\u59cb\u7ec8\u5728\u5730\u9762\u4e0a\u7559\u6ed1\u7740\uff0c\u50cf\u662f\u60f3\u4ece\u8fd9\u7070\u9ed1\u7684\u5730\u9762\u4e0a\uff0c\u641c\u7d22\u51fa\u4e00\u4e9b\u5e76\u4e0d\u7070\u9ed1\u7684\u4e1c \u897f\u3002 \u3000\u3000\u7f2a\u6587\u5782\u7740\u5934\uff0c\u6c89\u541f\u7740\uff0c\u4f46\u7ec8\u4e8e\u5c06\u4ed6\u81ea\u5df1\u6210\u957f\u7684\u5730\u65b9\u8bf4\u4e86\u51fa\u6765\uff0c\u53c8\u9053\uff1a\u201c\u5bb6\u6bcd\u5934\u53d1\u867d\u767d \u4e86\uff0c\u4f46\u8eab\u4f53\u5374\u8fd8\u5065\u6717\u5f97\u5f88\uff0c\u5979\u8001\u4eba\u5bb6\u6709\u65f6\u5019\u5ff5\u53ca\u6545\u4eba\uff0c\u4e5f\u5e38\u60f3\u56de\u6765\u770b\u770b\uff0c\u4f46\u662f\u2026\u2026\u201d \u3000\u3000\u77f3\u78f7\u53f9\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u6211\u77e5\u9053\u2026\u2026\u6211\u82e5\u662f\u5979\uff0c\u6211\u4e5f\u4e0d\u4f1a\u56de\u6765\u7684\u3002\u201d \u3000\u3000\u53c8\u9053\uff1a\u201c\u96be\u602a\u4f60\u5e74\u7eaa\u867d\u8f7b\uff0c\u6b66\u529f\u7adf\u5df2\u5982\u65af\uff0c\u539f\u6765\u4f60\u8eab\u53d7\u767e\u5341\u5e74\u6765\u6b66\u6797\u4e2d\u6700\u4eab\u76db\u540d\u7684\u51e0\u4f4d \u524d\u8f88\u5f02\u4eba\u7684\u6559\u8bf2\uff0c\u5509\uff01\u5341\u4e03\u5e74\u524d\uff0c\u90a3\u65f6\u6211\u8840\u6c14\u65b9\u521a\uff0c\u81ea\u547d\u5251\u672f\u5df1\u6709\u5c0f\u6210\uff0c\u54ea\u77e5\u5728\u4eba\u5bb6\u624b\u4e0b\uff0c \u8fde\u4e09\u62db\u90fd\u672a\u8d70\u6ee1\u3002\u201d \u3000\u3000\u4ed6\u76ee\u5149\u53c8\u4e00\u62ac\uff0c\u76f4\u6ce8\u5230\u201c\u7f2a\u6587\u201d\u9762\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u5f53\u65f6\u6211\u82e5\u77e5\u9053\u90a3\u4e24\u4f4d\u5bf9\u4f60\u6bcd\u4eb2\u672c\u662f\u4e00\u756a \u597d\u610f\uff0c\u8fd9\u6211\u518d\u4e5f\u4e0d\u4f1a\u51fa\u624b\u4e86\u3002\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u9eef\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u4ef6\u4e8b\u5bb6\u6bcd\u4e5f\u66fe\u5bf9\u5c0f\u4f84\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u77f3\u78f7\u9053\uff1a\u201c\u4f60\u6b64\u6b21\u4ee5\u2018\u7f2a\u6587\uff0c\u4e24\u5b57\u4e3a\u540d\uff0c\u53ef\u6709\u2026\u2026\u201d\u2019\u7f2a\u6587\uff0c\u63a5\u9053\uff1a\u201c\u5c0f\u4f84\u672c\u540d\u2018\u4ec7 \u6055\u2019\u3002\u8fd9\u662f\u5bb6\u6bcd\u66ff\u5c0f\u4f84\u8d77\u7684\u540d\u5b57\uff0c\u2018\u7f2a\u6587\u2019\u4e24\u5b57\uff0c\u4e0d\u8fc7\u662f\u80e1\u4e71\u7528\u7528\u800c\u5df2\u3002\u201d \u3000\u3000\u77f3\u78f7\u76ee\u5149\u4e00\u5782\uff0c\u4f4e\u8bed\u9053\uff1a\u201c\u4ec7\u6055\uff0c\u4ec7\u6055\u2026\u2026\u201d\u7a81\u5730\u6717\u58f0\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u4ee4\u5802\u4ee5\u6b64\u4e8c\u5b57\u53d6 \u540d\u7684\u9053\u7406\u5417\uff1f\u201d \u3000\u3000\u4ec7\u6055\u53cc\u76ee\u4e00\u5f20\uff0c\u76ee\u5149\u4e2d\u5149\u91c7\u53c8\u590d\u5927\u9732\uff0c\u5374\u542c\u77f3\u78f7\u63a5\u7740\u53c8\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u5e74\u8f7b\u82f1\u53d1\uff0c\u6b63\u662f \u4eba\u95f4\u7684\u7965\u9e9f\u5a01\u51e4\uff0c\u4ee5\u4f60\u7684\u667a\u6167\u6b66\u529f\uff0c\u4e0d\u96be\u5728\u4eba\u4e16\u95f4\u505a\u4e00\u756a\u60ca\u5929\u52a8\u5730\u7684\u5927\u4e8b\u6765\uff0c\u82e5\u4f60\u4ee5\u79c1\u4ec7\u4e3a \u91cd\uff0c\u90a3\u4f60\u5c31\u9519\u4e86\u3002\u201d \u3000\u3000\u4ec7\u6055\u5251\u7709\u4e00\u8f69\uff0c\u6717\u58f0\u9053\uff1a\u201c\u7236\u4ec7\u4e0d\u5171\u6234\u5929\uff0c\u4e0d\u62a5\u7109\u5f97\u4e3a\u4eba\u5b50\u3002\u201d \u3000\u3000\u77f3\u78f7\u53f9\u9053\uff1a\u201c\u4f46\u662f\u4f60\u53ef\u77e5\u9053\uff0c\u4f60\u7684\u4ec7\u5bb6\uff0c\u5374\u662f\u4ee4\u5802\u7684\u5ae1\u4eb2\u5144\u957f\uff0c\u4f60\u5982\u6b64\u505a\uff0c\u5c82\u975e\u8981\u4f24\u4e86 \u4f60\u6bcd\u4eb2\u7684\u5fc3\uff1f\u201d \u3000\u3000\u4ec7\u6055\u957f\u53f9\u4e00\u58f0\uff0c\u76ee\u5149\u53c8\u7f13\u7f13\u5782\u4e86\u4e0b\u53bb\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u77f3\u53d4\u7236\uff0c\u5bb6\u6bcd\u5e38\u8bf4\u82b8\u82b8\u5929\u4e0b\uff0c\u53ea\u6709\u4f60 \u8001\u4eba\u5bb6\u662f\u5979\u7684\u77e5\u5df2\uff0c\u6b64\u523b\u6211\u624d\u77e5\u9053\u8fd9\u8bdd\u679c\u7136\u4e0d\u9519\uff0c\u5979\u8001\u4eba\u5bb6\u59cb\u7ec8\u5c06\u5148\u7236\u7684\u4e8b\u9690\u7792\u7740\u6211\uff0c\u4e3a\u7684 \u81ea\u7136\u5c31\u662f\u4e0d\u613f\u6211\u590d\u4ec7\uff0c\u4f46\u662f\u2026\u2026\u5509\uff0c\u4efb\u4f55\u4e8b\u90fd\u7edd\u4e0d\u4f1a\u6c38\u8fdc\u88ab\u9690\u85cf\u7684\uff0c\u5148\u7236\u7684\u60e8\u6b7b\uff0c\u6211\u65e2\u7136\u77e5 \u9053\u4e86\uff0c\u53c8\u600e\u80fd\u7f6e\u4e4b\u4e0d\u7406\uff0c\u5509\uff01\u6211\u7eb5\u7136\u77e5\u9053\u8fd9\u6837\u4f1a\u4f24\u6bcd\u4eb2\u7684\u5fc3\uff0c\u4f46\u662f\u2014\u2014\u5509\uff0c\u7236\u4ec7\u5374\u662f\u975e\u62a5\u4e0d \u53ef\u7684\u3002\u201d \u3000\u3000\u77f3\u78f7\u7a81\u5730\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u597d\u4e2a\u5b5d\u5b50\uff0c\u597d\u4e2a\u5b5d\u5b50\uff01\u2026\u2026\u201d\u8bed\u58f0\u7a81\u5730\u4e00\u987f\uff0c\u957f\u8eab\u800c\u8d77\uff0c\u4e49 \u9053\uff1a\u201c\u4f60\u6bcd\u4eb2\u6000\u80ce\u5341\u6708\uff0c\u53d7\u5c3d\u56f0\u82e6\uff0c\u517b\u4f60\u80b2\u4f60\uff0c\u4f60\u5374\u4e0d\u77e5\u5b5d\u6bcd\uff0c\u53ea\u77e5\u5b5d\u7236\uff0c\u8fd8\u8c08\u4ec0\u4e48\u4e3a\u4eba\u5b50 \u4e4b\u9053\uff0c\u4f55\u51b5\u4f60\u90a3\u7236\u4eb2\u2014\u2014\u54fc\u54fc\uff01\u201d \u3000\u3000\u4ec7\u6055\u5251\u7709\u4e00\u8f69\uff0c\u6012\u9053\uff1a\u201c\u6211\u7236\u4eb2\u53c8\u600e\u7684\uff1f\u201d \u3000\u3000\u77f3\u78f7\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u90a3\u7236\u4eb2\u4e48\u2014\u2014\u54fc\u54fc\uff0c\u4e0d\u8bf4\u4e5f\u7f62\u3002\u201d \u3000\u3000\u4ed6\u4e0e\u6bdb\u51b0\uff0c\u81ea\u5e7c\u76f8\u5904\uff0c\u949f\u60c5\u6781\u6df1\uff0c\u5230\u540e\u6765\u4e00\u80a1\u76f8\u601d\uff0c\u5316\u4e3a\u6ce1\u5f71\uff0c\u5bf9\u90a3\u4ec7\u72ec\uff0c\u81ea\u7136\u96be\u514d\u5992 \u6068\uff0c\u53ea\u662f\u4ed6\u751f\u6027\u8c41\u8fbe\uff0c\u662f\u4e2a\u5149\u660e\u78ca\u843d\u7684\u6c49\u5b50\uff0c\u662f\u4ee5\u5fc3\u4e2d\u867d\u6709\u5992\u6068\uff0c\u5374\u59cb\u7ec8\u6ca1\u6709\u5c06\u4e4b\u73b0\u8bf8\u5f62 \u8272\u3002 \u3000\u3000\u76f4\u5230\u6b64\u523b\uff0c\u591a\u5e74\u7684\u79ef\u6124\uff0c\u624d\u4f7f\u4ed6\u8bf4\u51fa\u6b64\u8bdd\u6765\u3002\u4ec7\u6055\u4e00\u542c\uff0c\u81ea\u662f\u5927\u6012\uff0c\u751a\u81f3\u4ed6\u90a3\u59cb\u7ec8\u4e0d\u52a8 \u58f0\u8272\u7684\u4fca\u76ee\uff0c\u5374\u56e0\u6124\u6012\u800c\u53d8\u5f97\u8d64\u7ea2\uff0c\u731b\u5730\u4e00\u62cd\u684c\u5b50\uff0c\u6012\u89c6\u7740\u77f3\u78f7\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u6211\u7236\u4eb2\u53ef\u600e \u7684\uff1f\u4ed6\u8001\u4eba\u5bb6\u4e00\u751f\u5149\u660e\u78ca\u843d\uff0c\u5374\u4e3a\u5c0f\u4eba\u4eec\u6240\u7b97\u800c\u6b7b\uff0c\u77f3\u53d4\u7236\uff0c\u4f60\u4e0e\u5bb6\u6bcd\u867d\u662f\u77e5\u4ea4\uff0c\u6211\u4ec7\u6055\u4e5f \u56e0\u4e4b\u656c\u91cd\u4f60\u4e09\u5206\uff0c\u4f46\u4f60\u8a00\u8bed\u4e4b\u4e2d\uff0c\u82e5\u518d\u5bf9\u5148\u7236\u6709\u534a\u5206\u4e0d\u656c\uff0c\u90a3\u4e48\u2014\u2014\u54fc\u54fc\uff01\u5c31\u83ab\u602a\u6211\u59d3\u4ec7\u7684 \u4e0d\u77e5\u656c\u91cd\u5c0a\u957f\u4e86\u3002\u201d \u3000\u3000\u77f3\u78f7\u51b7\u7b11\u9053\uff1a\u201c\u597d\u6781\uff0c\u597d\u6781\uff0c\u6211\u5012\u8981\u770b\u770b\u4f60\u600e\u5730\u2014\u2014\u201d\u76ee\u5149\u4e00\u62ac\uff0c\u53ea\u89c1\u4ec7\u6055\u76ee\u5149\u4e4b\u4e2d\uff0c \u6ee1\u542b\u6028\u6bd2\u4e4b\u8272\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u7a81\u5730\u60f3\u8d77\u4ee5\u524d\u90a3\u201c\u4ec7\u5148\u751f\u201d\u7684\u4e00\u751f\u884c\u4e8b\uff0c\u4e0d\u7981\u6697\u53f9\u4e00\u58f0\uff0c\u4e2d\u6b62\u4f4f \u81ea\u5df1\u7684\u8bdd\uff0c\u6697\u5fd6\u9053\uff1a\u201c\u96be\u9053\u6b66\u6797\u4e4b\u4e2d\uff0c\u53c8\u5c06\u51fa\u73b0\u4e00\u4e2a\u884c\u4e8b\u83ab\u6d4b\u7684\u9b54\u5934\u5417\uff1f\u201d\u7f13\u7f13\u8d70\u5230\u95e8\u53e3\uff0c \u5374\u53c8\u56de\u8f6c\u8eab\u9053\uff1a\u201c\u4f60\u65e2\u5982\u6b64\uff0c\u6211\u4e5f\u4e0d\u518d\u591a\u8bf4\uff0c\u53ea\u8981\u4f60\u5fc3\u4e2d\u8fd8\u6709\u51e0\u5206\u8bb0\u5f97\u4f60\u6bcd\u4eb2\u7684\u517b\u80b2\u4e4b\u6069\u5c31 \u662f\u4e86\u3002\u201d \u3000\u3000\u4ec7\u6055\u51b7\u51b7\u9053\uff1a\u201c\u8fd9\u4e2a\u81ea\u7136\u3002\u201d \u3000\u3000\u76ee\u5149\u56db\u626b\uff0c\u77a5\u89c1\u684c\u4e0a\u653e\u7740\u7684\u8336\u676f\uff0c\u4f38\u624b\u7aef\u4e86\u8d77\u6765\uff0c\u77f3\u78f7\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u6bcb\u5eb8\u7aef\u8336\uff0c\u6211\u672c\u8981 \u8d70\u4e86\uff0c\u53ea\u662f\u6211\u5374\u8981\u544a\u8bc9\u4f60\uff0c\u4ee5\u540e\u591c\u95f4\u51fa\u53bb\uff0c\u5148\u8981\u7184\u706d\u70db\u706b\uff0c\u5173\u4e0a\u95e8\u7a97\uff0c\u82e5\u975e\u6211\u5728\u4f60\u5e8a\u4e0a\u88c5\u5f97 \u9f3b\u606f\u6c89\u6c89\uff0c\u5df2\u7136\u5165\u7761\u7684\u6837\u5b50\uff0c\u53ea\u6015\u9694\u58c1\u7684\u6bdb\u5927\u5c0f\u59d0\u65e9\u5df2\u8fdb\u6765\u67e5\u770b\u4e86\u3002\u201d \u3000\u3000\u4ec7\u6055\u5fc3\u4e2d\u6697\u9053\u4e00\u58f0\uff0c\u201c\u60ed\u6127\u3002\u201d\u53e3\u4e2d\u5374\u4ecd\u7136\u51b7\u51b7\u9053\uff1a\u201c\u6709\u52b3\u9601\u4e0b\u64cd\u5fc3\u3002\u201d \u3000\u3000\u77f3\u78f7\u53c8\u81ea\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u5e76\u65e0\u8981\u4f60\u9886\u60c5\u4e4b\u610f\uff0c\u4f60\u4e5f\u6bcb\u5eb8\u8c22\u6211\u3002\u201d \u3000\u3000\u4ec7\u6055\u7ee7\u53c8\u9053\uff1a\u201c\u9601\u4e0b\u8981\u8bf4\u7684\uff0c\u53ea\u662f\u8fd9\u51e0\u53e5\u8bdd\u5417\uff1f\u201d \u3000\u3000\u77f3\u78f7\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u8a00\u8981\u5949\u529d\u9601\u4e0b\uff0c\u9601\u4e0b\u4ee5\u540e\u8981\u9690\u85cf\u8eab\u4e16\uff0c\u8fd8\u5f97\u518d\u82b1\u4e9b\u5de5\u592b\uff0c\u5355\u8bf4\u81ea\u5df1\u662f \u767e\u7ca4\u5bcc\u5546\u4e4b\u5b50\u5374\u662f\u4e07\u4e07\u884c\u4e0d\u901a\u7684\u3002\u201d\u888d\u8896\u4e00\u62c2\uff0c\u7f13\u6b65\u8d70\u5230\u95e8\u53e3\uff0c\u54ea\u77e5\u773c\u524d\u4eba\u5f71\u4e00\u82b1\uff0c\u90a3\u4ec7\u6055 \u5df2\u51b7\u51b7\u7ad9\u5728\u95e8\u524d\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u9601\u4e0b\u8bf4\u8bdd\uff0c\u9700\u5f97\u8bf4\u5f97\u6e05\u695a\u4e9b\uff0c\u8bdd\u8bf4\u4e00\u534a\u5c31\u60f3\u8d70\u2014\u2014\u201d\u77f3\u78f7\u51b7\u7b11 \u63a5\u53e3\u9053\uff1a\u201c\u6211\u82e5\u5168\u8bf4\u51fa\u6765\uff0c\u53ea\u6015\u4f60\u8981\u611f\u8c22\u4e8e\u6211\u3002\u201d \u3000\u3000\u4ec7\u6055\u9f3b\u5b54\u91cc\u91cd\u91cd\u54fc\u4e86\u4e00\u58f0\uff0c\u77f3\u78f7\u53c8\u9053\uff1a\u2018\u9601\u4e0b\u867d\u662f\u806a\u660e\u4eba\uff0c\u522b\u4eba\u5374\u4e5f\u4e0d\u662f\u5446\u5b50\uff0c\u90a3\u7075\u86c7 \u6bdb\u81ec\u80fd\u6709\u4eca\u65e5\u4e4b\u5730\u4f4d\uff0c\u5c82\u662f\u5e78\u81f4\uff0c\u4f60\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u548c\u90a3\u201c\u516b\u9762\u73b2\u73d1\u201d\u80e1\u4e4b\u8f89\u53c8\u7d20\u4e0d\u76f8\u8bc6\uff0c\u51fa\u624b \u5c31\u662f\u6570\u5341\u4e07\u4e24\u94f6\u5b50\uff0c\u82e5\u518d\u65e0\u6b64\u7591\u5fc3\u2014\u2014\u54fc\u54fc\uff0c\u90a3\u5f53\u771f\u90fd\u662f\u5446\u5b50\u4e86\u3002\u201d\u4ec7\u6055\u5fc3\u4e2d\u4e0d\u7981\u53c8\u6697\u53eb\u4e00 \u58f0\uff1a\u201c\u60ed\u6127\u3002\u201d\u53e3\u4e2d\u5374\u51b7\u7b11\u9053\uff1a\u201c\u7591\u5fc3\u53c8\u5f53\u600e\u5730\uff1f\u201d \u3000\u3000\u77f3\u78f7\u6697\u4e2d\u4e00\u7b11\uff0c\u5fd6\u9053\uff1a\u201c\u5f53\u771f\u662f\u6709\u5176\u7236\u5fc5\u6709\u5176\u5b50\u3002\u201d\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u7591\u5fc3\u4e4b\u4e0b\uff0c\u5c31\u8981\u63a2 \u67e5\uff0c\u90a3\u2018\u94c1\u9a91\u795e\u97ad\uff0c\u9a91\u58eb\uff0c\u904d\u5e03\u5927\u6cb3\u4e24\u5cb8\uff0c\u957f\u6c5f\u5357\u5317\uff0c\u53ea\u8981\u5230\u7ca4\u4e1c\u53bb\u7a0d\u4e00\u67e5\u95ee\uff0c\u4fbf\u77e5\u9053\u4f60\u8fd9 \u5bcc\u5546\u4e4b\u5b50\u662f\u5192\u724c\u7684\u4e86\u3002\u201d\u4ec7\u6055\u5fc3\u5934\u4e00\u8df3\uff0c\u6c89\u541f\u534a\u664c\uff0c\u5374\u542c\u77f3\u78f7\u53c8\u9053\uff1a\u201c\u53ea\u662f\u90a3\u4e9b\u2018\u795e\u97ad\u9a91 \u58eb\u2019\u672a\u5230\u7ca4\u4e1c\uff0c\u5c31\u88ab\u533a\u533a\u5728\u4e0b\u5236\u6b7b\uff0c\u9601\u4e0b\u5927\u53ef\u653e\u5fc3\u4e86\u3002\u201d\u8bed\u58f0\u5fae\u987f\uff0c\u51b7\u54fc\u4e00\u58f0\uff0c\u53c8\u9053\uff1a\u201c\u6211 \u5982\u6b64\u505a\u6cd5\uff0c\u53ea\u662f\u4e3a\u4e86\u4f60\u90a3\u6bcd\u4eb2\u800c\u5df2\uff0c\u4f60\u4e5f\u6bcb\u5eb8\u611f\u6fc0\u4e8e\u6211\u2014\u2014\u54fc\u54fc\uff0c\u82e5\u662f\u4e3a\u4e86\u4f60\u90a3\u7236\u4eb2\u7684\u8bdd\uff0c \u54fc\u54fc\uff01\u6211\u4e0d\u8bf4\u4f60\u5fc3\u91cc\u4e5f\u6e05\u695a\u5f97\u5f88\u3002\u201d \u3000\u3000\u4ec7\u6055\u8f69\u7709\u6012\u9053\uff1a\u201c\u4f60\u5bf9\u6211\u65bd\u6069\u4e09\u5206\uff0c\u65e5\u540e\u6211\u5fc5\u62a5\u4f60\u4e94\u5206\uff0c\u53ea\u662f\u4f60\u8a00\u8bed\u4e4b\u4e2d\uff0c\u82e5\u518d\u5bf9\u5148\u7236 \u6709\u4e0d\u656c\u4e4b\u5904\uff0c\u90a3\u5374\u53c8\u5f53\u522b\u8bba\uff0c\u83ab\u602a\u6211\u8981\u2026\u2026\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u95e8\u5916\u7a81\u5730\u4f20\u4eba\u4e00\u9635\u94f6\u94c3\u822c\u7684\u5a07\u7b11\u58f0\uff0c\u4e00\u9762\u9053\uff1a\u201c\u4f60\u8981\u5e72\u4ec0\u4e48\u5440\uff1f\u90a3\u4e48\u4e00\u6e05 \u65e9\uff0c\u4f60\u8ddf\u8c01\u53d1\u813e\u6c14\u5440\uff1f\u201d\u4ec7\u6055\uff0c\u77f3\u78f7\u9f50\u5730\u4e00\u60ca\uff0c\u53ea\u542c\u201c\u7b03\u7b03\u201d\u4e24\u58f0\u8f7b\u5fae\u7684\u6572\u95e8\u58f0\uff0c\u90a3\u5a07\u7b11\u4e4b \u58f0\u53c8\u9053\uff1a\u201c\u6211\u53ef\u4ee5\u8fdb\u6765\u5417\uff1f\u201d\u4ec7\u6055\u811a\u6b65\u5fae\u9519\uff0c\u6e9c\u5f00\u4e94\u6b65\uff0c\u77f3\u78f7\u5374\u62a2\u6b65\u8d70\u5230\u95e8\u524d\uff0c\u62d4\u5f00\u95e8\u95e9\uff0c \u4e00\u9762\u7b11\u9053\uff1a\u201c\u662f\u6587\u742a\u59d1\u5a18\u5417\uff1f\u4f60\u5012\u8d77\u6765\u5f97\u65e9\u3002\u201d \u3000\u3000\u95e8\u5916\u53c8\u662f\u5a07\u58f0\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u65e9\u5566\u3002\u201d\u968f\u7740\u7b11\u8bed\u4e4b\u58f0\uff0c\u95ea\u5165\u4e00\u4e2a\u70af\u5a1c\u7684\u4eba\u5f71\uff0c\u77f3\u78f7\u5b9a\u775b \u4e00\u89c6\uff0c\u4e0d\u7981\u8fde\u9000\u4e09\u6b65\uff0c\u6115\u6115\u5730\u671b\u7740\u8fd9\u8eab\u6750\u70af\u5a1c\u7684\u5973\u5b50\uff0c\u4ec7\u6055\u66f4\u662f\u5927\u5947\uff1a\u201c\u5979\u600e\u5730\u4f1a\u5230\u8fd9\u91cc \u6765\uff1f\u201d \u3000\u3000\u90a3\u5973\u5b50\u5a07\u7b11\u4e0d\u7edd\uff0c\u7709\u76ee\u4e00\u779f\u77f3\u78f7\uff0c\u4fbf\u7535\u4e5f\u4f3c\u7684\u8f6c\u5230\u4ec7\u6055\u9762\u4e0a\uff0c\u7b11\u9053\uff1a\u201c\u5947\u602a\u5427\uff0c\u4f1a\u662f \u6211\uff0c\u4e0d\u662f\u4f60\u90a3\u6587\u742a\u59b9\u59b9\u3002\u201d\u8f6c\u79fb\u83b2\u6b65\uff0c\u8d70\u5230\u4ec7\u6055\u9762\u524d\uff0c\u53c8\u81ea\u7b11\u9053\uff1a\u201c\u4f60\u77a7\u4f60\uff0c\u8138\u90fd\u6c14\u767d\u4e86\uff0c \u5e72\u4ec0\u4e48\u5440\uff0c\u544a\u8bc9\u6211\uff0c\u662f\u8c01\u6b3a\u8d1f\u4e86\u4f60\uff0c\u8ba9\u5927\u59d0\u59d0\u7ed9\u4f60\u51fa\u6c14\u3002\u201d \u3000\u3000\u4ec7\u6055\u5fae\u4e00\u5b9a\u795e\uff0c\u5fc3\u4e2d\u95ea\u7535\u822c\u8f6c\u4e86\u4e24\u8f6c\uff0c\u9762\u4e0a\u4ea6\u5806\u4e0a\u7b11\u5bb9\uff0c\u8eac\u8eab\u9053\uff1a\u201c\u6211\u5f53\u662f\u8c01\uff0c\u539f\u6765\u662f \u767e\u6b65\u98de\u82b1\u6797\u4ed9\u5b50\uff0c\u6628\u65e5\u4e00\u7779\u4ed9\u59ff\uff0c\u539f\u5df2\u518d\u4e5f\u96be\u5fd8\uff0c\u54ea\u77e5\u4eca\u65e5\u4ed9\u8e2a\u8385\u81f3\uff0c\u8fd9\u771f\u53eb\u5c0f\u53ef\u559c\u51fa\u671b\u5916 \u4e86\u3002\u201d \u3000\u3000\u90a3\u5a07\u7b11\u5982\u94c3\u7684\u201c\u767e\u6b65\u98de\u82b1\u201d\u6797\u7426\u7b5d\u53c8\u662f\u201c\u54af\u54af\u201d\u4e00\u7b11\uff0c\u8f7b\u8f7b\u4f38\u51fa\u4e00\u53cc\u767d\u5982\u7389\u8471\u822c\u7684\u7389\u6307 \u6765\uff0c\u5728\u7f2a\u6587\u989d\u89d2\u4e00\u70b9\uff0c\u9053\uff1a\u201c\u6211\u8bf4\u5c0f\u5144\u5f1f\u5440\uff01\u4f60\u8fd9\u5f20\u5634\u53ef\u771f\u751c\uff0c\u751c\u5f97\u6559\u6211\u8fd9\u8001\u59d0\u59d0\u90fd\u6709\u4e9b\u53d7 \u4e0d\u4e86\u5566\u3002\u201d\u5c3e\u97f3\u62d6\u5f97\u957f\u957f\u7684\uff0c\u5c31\u50cf\u662f\u6e17\u4e86\u7cd6\u7684\u82b1\u751f\u9165\u3002 \u3000\u3000\u4ec7\u6055\u5fae\u5fae\u4e00\u7b11\uff0c\u53c8\u9053\uff1a\u201c\u4e0d\u8bc6\u6797\u4ed9\u5b50\u4e4b\u7f8e\u8005\uff0c\u662f\u4e3a\u65e0\u76ee\u4e5f\uff0c\u5c0f\u53ef\u6b64\u8a00\uff0c\u5b9e\u662f\u51fa\u81ea\u80ba\u8151\uff0c \u6797\u4ed9\u5b50\u82e5\u8bf4\u5c0f\u53ef\u4ec5\u662f\u5634\u751c\u6545\u610f\u606d\u7ef4\uff0c\u90a3\u5012\u662f\u51a4\u6789\u597d\u4eba\u4e86\u3002\u201d \u3000\u3000\u201c\u767e\u6b65\u98de\u82b1\u201d\u6797\u7426\u7b5d\u773c\u6ce2\u4e00\u8f6c\uff0c\u5a07\u7b11\u9053\uff1a\u201c\u4f60\u8001\u59d0\u59d0\u8001\u5f97\u90fd\u5feb\u6389\u4e86\u7259\u5566\uff0c\u8fd8\u8c08\u4ec0\u4e48\u7f8e\u4e0d \u7f8e\u54e9\uff0c\u4e0d\u8fc7\u2014\u2014\u201d\u4f38\u624b\u4e00\u62e2\u9b13\u53d1\uff0c\u67f3\u8170\u8f7b\u8f7b\u4e00\u6447\uff1a\u201c\u6b66\u6797\u4e2d\u4eba\u5012\u662f\u771f\u6709\u4e0d\u5c11\u4eba\u8bf4\u4f60\u8001\u59d0\u59d0\u7f8e \u7684\uff0c\u6211\u603b\u662f\u4ee5\u4e3a\u4ed6\u4eec\u778e\u606d\u7ef4\uff0c\u4eca\u5929\u4f60\u8fd9\u4e48\u4e00\u8bf4\u5440\u2014\u2014\u201d\u5979\u53c8\u8f7b\u8f7b\u4e00\u70b9\u4ec7\u6055\u989d\u89d2\uff1a\u201c\u6211\u5012\u662f\u771f \u6709\u70b9\u76f8\u4fe1\u4e86\u3002\u201d \u3000\u3000\u77f3\u78f7\u76ee\u5149\u56db\u8f6c\uff0c\u9f3b\u5b54\u4e2d\u91cd\u91cd\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u62c2\u8896\u8d70\u5230\u95e8\u53e3\uff0c\u54ea\u77e5\u8eab\u4fa7\u7a81\u5730\u9999\u98ce\u55d6\u7136\uff0c\u90a3 \u201c\u767e\u6b65\u98de\u82b1\u201d\u5df2\u4fcf\u751f\u751f\u5730\u62e6\u5728\u8eab\u524d\uff0c\u5de6\u624b\u5fae\u66f2\uff0c\u624b\u80cc\u6276\u5728\u67f3\u8170\u4e4b\u4e0a\uff0c\u53f3\u624b\u8f7b\u8f7b\u4e00\u6307\uff0c\u5a07\u58f0 \u9053\uff1a\u201c\u4f60\u54fc\u4e2a\u4ec0\u4e48\u4eba\uff0c\u662f\u4e0d\u662f\u770b\u4e0d\u60ef\u4ec0\u4e48\u4eba\u5440\uff1f\u201d\u76ee\u5149\u8d8a\u8fc7\u77f3\u78f7\uff0c\u779f\u5230\u4ec7\u6055\u8eab\u4e0a\uff0c\u53c8\u9053\uff1a \u201c\u5c0f\u5144\u5f1f\uff0c\u544a\u8bc9\u6211\uff0c\u521a\u624d\u4f60\u662f\u4e0d\u662f\u5c31\u662f\u548c\u4ed6\u751f\u6c14\u6765\u7740\u3002\uff0c\u2019\u4ec7\u6055\u5fc3\u4e2d\u4e00\u8f6c\uff0c\u7a81\u5730\u201d\u54e6\u201d\u4e86\u4e00 \u58f0\uff0c\u62a2\u6b65\u8d70\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u5c0f\u53ef\u5fd8\u4e86\u7ed9\u6797\u4ed9\u5b50\u5f15\u89c1\u4e86\uff0c\u8fd9\u4f4d\u5c31\u662f\u2014\u2014\u201d\u6797\u7426\u7b5d\u201c\u54af\u54af\u201d\u7b11 \u9053\uff1a\u201c\u4f60\u4e0d\u7528\u5f15\u89c1\uff0c\u6211\u65e9\u5c31\u77e5\u9053\u4ed6\u662f\u8c01\u4e86\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u6211\u5e38\u542c\u8bf4\u6b66\u6797\u4e2d\u6709\u4e2a\u6d41\u6d6a\u5251\u5ba2\uff0c\u662f\u6b66 \u5f53\u5f1f\u5b50\uff0c\u53eb\u77f3\u78f7\uff0c\u6574\u5929\u7684\u5728\u6c5f\u6e56\u4e2d\u4e1c\u98d8\u897f\u8361\uff0c\u4ec0\u4e48\u4e8b\u4e0d\u4e5f\u5e72\uff0c\u662f\u4e2a\u602a\u4eba\uff0c\u6211\u4e00\u542c\u5c31\u89c9\u5f97\u2018\u77f3 \u78f7\uff0c\u8fd9\u540d\u5b57\u5f88\u719f\uff0c\u5374\u59cb\u7ec8\u60f3\u4e0d\u8d77\u662f\u4ec0\u4e48\u4eba\uff0c\u4eca\u5929\u4e00\u89c1\uff0c\u6211\u624d\u77e5\u9053\u662f\u4ed6\uff0c\u591a\u5c11\u5e74\u4ee5\u524d\uff0c\u6211\u5c31\u5728 \u6bdb\u5927\u54e5\u5bb6\u91cc\u89c1\u8fc7\u4ed6\u7684\u5440\uff01\u201d\u5979\u63a9\u53e3\u4e00\u7b11\uff1a\u201c\u90a3\u65f6\u5019\u4ed6\u6574\u5929\u5730\u8ddf\u5728\u6211\u4eec\u6bdb\u5927\u59b9\u5b50\u8eab\u524d\u8eab\u540e\u4e71 \u8f6c\uff0c\u521a\u624d\u6211\u8fd8\u4ee5\u4e3a\u4f60\u4eec\u5728\u5435\u67b6\u54e9\uff0c\u539f\u6765\u4f60\u4eec\u662f\u670b\u53cb\u3002\u201d\u67f3\u8170\u4e00\u6298\uff0c\u9000\u5f00\u4e00\u6b65\uff1a\u201c\u90a3\u6211\u5c31\u4e0d\u62e6 \u60a8\u54ea\u3002\u201d \u3000\u3000\u8fd9\u201c\u767e\u6b65\u98de\u82b1\u201d\u8bf4\u8d77\u8bdd\u6765\uff0c\u5a9a\u773c\u5982\u73e0\uff0c\u4f46\u6bcf\u4e00\u53e5\u8bdd\u7684\u5c3e\u97f3\uff0c\u5374\u53c8\u62d6\u5f97\u957f\u957f\u7684\uff0c\u8fd8\u5e26\u7740\u4e00 \u4e9b\u8f7b\u5fae\u7684\u98a4\u6296\uff0c\u8ba9\u4eba\u542c\u4e86\uff0c\u5c31\u50cf\u662f\u5403\u4e86\u4e09\u65a4\u6e17\u4e86\u7cd6\u7684\u82b1\u751f\u9165\uff0c\u751c\u5f97\u90fd\u5feb\u8d77\u817b\u4e86\u3002 \u3000\u3000\u4f46\u8fd9\u4e9b\u8bdd\u542c\u5728\u4ec7\u6055\u8033\u4e2d\uff0c\u4ed6\u5fc3\u91cc\u5374\u4e0d\u7981\u4e3a\u4e4b\u7830\u5730\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u201c\u539f\u6765\u4ed6\u548c\u5988\u5988\u662f\u2026\u2026\u201d \u3000\u3000\u62ac\u773c\u4e00\u671b\u77f3\u78f7\uff0c\u53ea\u89c1\u4ed6\u4e5f\u6b63\u5728\u671b\u7740\u81ea\u5df1\uff0c\u4e24\u4eba\u76ee\u5149\u76f8\u5bf9\uff0c\u5404\u5404\u6cdb\u8d77\u4e00\u9635\u96be\u8a00\u7684\u6ecb\u5473\uff0c\u4e0d \u77e5\u662f\u6068\u3001\u662f\u6012\u3001\u6291\u6216\u662f\u5206\u4ef2\u6ee1\u542b\u6e29\u60c5\u7684\u60c5\u611f\u3002 \u3000\u3000\u53ea\u89c1\u77f3\u78f7\u53c8\u81ea\u957f\u53f9\u4e00\u58f0\uff0c\u7f13\u6b65\u8d70\u5230\u95e8\u5916\u3002\u201c\u6210\u65e5\u4e1c\u98d8\u897f\u8361\u2026\u2026\u4ec0\u4e48\u4e8b\u4e5f\u4e0d\u5e72\u2026\u2026\u8eab\u524d\u8eab \u540e\u4e71\u8f6c\u2026\u2026\u201d\u8fd9\u4e9b\u8bdd\u4e00\u53e5\u63a5\u7740\u4e00\u53e5\uff0c\u4e0d\u505c\u5730\u5728\u4ed6\u5fc3\u4e2d\u649e\u51fb\u7740\uff0c\u4ed6\u53ea\u89c9\u5fc3\u4e2d\u70ed\u8840\u6cb8\u817e\uff0c\u4e0d\u80fd\u81ea \u5df1\uff0c\u6697\u81ea\u601d\u5fd6\uff1a\u201c\u6211\u662f\u4e2a\u602a\u4eba\u5417\uff1f\u201d \u3000\u3000\u4ec7\u6055\u671b\u7740\u4ed6\u7626\u524a\u7684\u80cc\u5f71\uff0c\u6d88\u5931\u5728\u95e8\u5916\uff0c\u76ee\u5149\u8f6c\u5904\uff0c\u5374\u89c1\u90a3\u201c\u767e\u6b65\u98de\u82b1\u201d\u6797\u7426\u7b5d\u5a40\u5a1c\u5730\u8d70 \u5230\u684c\u65c1\uff0c\u4f38\u51fa\u7f57\u8896\uff0c\u8f7b\u8f7b\u5728\u6905\u5b50\u4e0a\u4e00\u62c2\uff0c\u4fa7\u8eab\u5750\u4e86\u4e0b\u53bb\uff0c\u79cb\u6ce2\u56db\u8f6c\uff0c\u5a07\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60 \u628a\u95e8\u5173\u8d77\u6765\uff0c\u5012\u676f\u8336\u7ed9\u5927\u59d0\u59d0\u559d\uff0c\u966a\u4f60\u5927\u59d0\u59d0\u804a\u804a\u5929\u3002\u201d \u3000\u3000\u4ec7\u6055\u5fc3\u4e2d\u53c8\u95ea\u7535\u822c\u8f6c\u4e86\u51e0\u8f6c\uff0c\u5634\u89d2\u4fbf\u53c8\u6cdb\u51fa\u7b11\u610f\uff0c\u968f\u624b\u5e26\u4e0a\u623f\u95e8\uff0c\u4e00\u9762\u5583\u5583\u7740\u9053\uff1a\u201c\u4e0d \u77e5\u9053\u6587\u742a\u9192\u4e86\u6ca1\u6709\uff0c\u5979\u82e5\u9192\u4e86\uff0c\u4e00\u5b9a\u4f1a\u8fc7\u6765\u7684\u3002\u201d \u3000\u3000\u4ed6\u7684\u8bdd\u50cf\u662f\u5583\u5583\u81ea\u8bed\uff0c\u81ea\u5df1\u8bf4\u7ed9\u81ea\u5df1\u542c\uff0c\u5176\u5b9e\u5374\u662f\u5bf9\u8fd9\u201c\u767e\u6b65\u98de\u82b1\u201d\u8bf4\u3002 \u3000\u3000\u6797\u7426\u7b5d\u5a07\u58f0\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u770b\u4f60\uff0c\u5634\u91cc\u4e00\u5929\u5230\u665a\u6587\u742a\u6587\u742a\u7684\uff0c\u4f60\u5c31\u77e5\u9053\u5979\u9192\u4e86\u4e00\u5b9a\u4f1a\u8fc7 \u6765\u7684\u5417\uff1f\u201d\u7389\u624b\u4e2d\u65b9\u624d\u62ff\u8d77\u7684\u7a7a\u8336\u676f\u9012\u5230\u4ec7\u6055\u624b\u4e0a\u3002 \u3000\u3000\u4ec7\u6055\u542b\u7b11\u63a5\u4e86\u8fc7\u6765\uff0c\u4e00\u9762\u9053\uff1a\u201c\u6587\u742a\u82e5\u9192\u4e86\uff0c\u60f3\u5fc5\u662f\u4e00\u5b9a\u4f1a\u8fc7\u6765\u7684\u3002\u201d \u3000\u3000\u6797\u7426\u7b5d\u79cb\u6ce2\u8361\u6f3e\uff0c\u7b11\u9053\uff1a\u201c\u60f3\u5fc5\u662f\u4e00\u5b9a\u4f1a\u8fc7\u6765\uff0c\u8fd9\u53ea\u662f\u4f60\u4e00\u4e2a\u4eba\u5728\u8fd9\u91cc\u60f3\u7f62\u4e86\uff0c\u4eba\u5bb6\u53ef \u4e0d\u8fd9\u4e48\u60f3\u3002\u201d \u3000\u3000\u4ec7\u6055\u4e00\u6115\uff0c\u9669\u4e9b\u5c06\u8336\u676f\u91cc\u7684\u8336\u90fd\u5012\u5f97\u6ee1\u6ea2\u4e86\u51fa\u6765\uff0c\u53e3\u4e2d\u5374\u7b11\u9053\uff1a\u201c\u90a3\u4e48\u6797\u4ed9\u5b50\u60a8\u53c8\u600e\u4e48 \u60f3\u5462\uff1f\u201d \u3000\u3000\u6797\u7426\u7b5d\u674f\u773c\u4e00\u779f\uff0c\u6545\u610f\u5a07\u55d4\u9053\uff1a\u201c\u4f60\u518d\u8fd9\u4e48\u6797\u4ed9\u5b50\u6797\u4ed9\u5b50\u5730\u53eb\u6211\uff0c\u6211\u4ec0\u4e48\u8bdd\u90fd\u4e0d\u544a\u8bc9\u4f60 \u4e86\uff0c\u8ba9\u4f60\u4e00\u4e2a\u4eba\u53bb\u80e1\u601d\u4e71\u60f3\u53bb\u3002\u201d \u3000\u3000\u4ec7\u6055\u7b11\u9053\uff1a\u201c\u90a3\u4e48\u6211\u53eb\u4ec0\u4e48\uff0c\u60a8\u624d\u544a\u8bc9\u6211\u4e00\u4e9b\u8bdd\u5462\uff1f\u201d \u3000\u3000\u6797\u7426\u7b5d\u79cb\u6ce2\u53c8\u662f\u4e00\u6f3e\uff0c\u6a31\u5507\u5fae\u5fae\u4e00\u62bf\uff0c\u5a07\u7b11\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u5c31\u53eb\u6211\u2026\u5927\u59d0\u59d0\uff0c\u6211 \u4e48\u2026\u2026\uff0c\u5c31\u53eb\u4f60\u5c0f\u5144\u5f1f\uff0c\u8fd9\u6709\u591a\u597d\uff0c\u663e\u5f97\u53c8\u4eb2\u8fd1\uff0c\u53c8\u987a\u53e3\uff0c\u4e0d\u6bd4\u90a3\u6797\u4ed9\u5b50\u6797\u4ed9\u5b50\u7684\u597d\u5f97\u591a \u4e48\uff1f\u201d \u3000\u3000\u4f38\u624b\u63a5\u8fc7\u4e86\u8336\uff0c\u6d45\u6d45\u555c\u4e86\u4e00\u53e3\uff0c\u6668\u5149\u4e4b\u4e2d\uff0c\u5979\u773c\u89d2\u867d\u7136\u53ef\u770b\u51fa\u4e00\u4e9b\u9c7c\u7eb9\uff0c\u4f46\u90a3\u79cd\u5a07\u597d\u7684 \u7b11\u5bb9\uff0c\u5374\u50cf\u662f\u4f7f\u5f97\u8fd9\u5df2\u534a\u8001\u7684\u5f90\u5a18\uff0c\u4e0d\u4f46\u98ce\u97f5\u72b9\u5b58\uff0c\u800c\u4e14\u5a9a\u8273\u4e4b\u6001\u4e5f\u672a\u7a0d\u51cf\u5f53\u5e74\u54e9\u3002 \u3000\u3000\u5979\u6df1\u6df1\u653e\u4e0b\u8336\u676f\uff0c\u201c\u5657\u55e4\u201d\u4e00\u7b11\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u522b\u7740\u6025\uff0c\u8ba9\u5927\u59d0\u59d0\u544a\u8bc9\u4f60\uff0c\u4f60\u6587\u742a\u59b9\u59b9\u9192 \u4e86\u4e4b\u540e\uff0c\u4e0d\u4f46\u6ca1\u6709\u8fc7\u6765\uff0c\u800c\u4e14\u65e9\u5c31\u8d70\u5f97\u4e0d\u77e5\u5230\u54ea\u91cc\u53bb\u4e86\u3002\u201d \u3000\u3000\u53c8\u8f7b\u8f7b\u6447\u4e86\u6447\u5934\uff1a\u201c\u53ef\u601c\uff0c\u53ef\u601c\uff01\u6211\u4eec\u8fd9\u4f4d\u5c0f\u5144\u5f1f\uff0c\u5374\u8fd8\u5728\u8fd9\u91cc\u82e6\u82e6\u7684\u7b49\u7740\u5979\u54e9\uff0c\u5509\u2014 \u2014\u6211\u8bf4\u6587\u742a\u59d1\u5a18\u5440\uff0c\u4f60\u8d70\u4e86\u600e\u4e48\u4e5f\u4e0d\u8bf4\u4e00\u58f0\u5440\uff1f\u201d\u5a9a\u76ee\u6d41\u6ce2\uff0c\u77ac\u4e5f\u4e0d\u77ac\u5730\u671b\u5728\u4ec7\u6055\u9762\u4e0a\u3002 \u3000\u3000\u3000\u3000\u4ec7\u6055\u5fc3\u4e2d\u5374\u4e3a\u4e4b\u4e00\u60ca\uff01 \u3000\u3000\u201c\u5979\u4f1a\u65e9\u5c31\u8d70\u4e86\uff0c\u5979\u4f1a\u4e0d\u901a\u77e5\u6211\u4e00\u58f0\u5c31\u8d70\u4e86\uff0c\u8fd9\u53c8\u662f\u4e3a\u7740\u4ec0\u4e48\u5462\uff1f\u201d\u62a2\u6b65\u8d70\u5230\u95e8\u53e3\uff0c\u60f3 \u53bb\u770b\u770b\uff0c\u4f46\u5fc3\u4e2d\u4e00\u52a8\uff0c\u53c8\u81ea\u5fd6\u9053\uff1a\u201c\u8fd9\u2018\u767e\u6b65\u98de\u82b1\u2019\u60f3\u5fc5\u4e0d\u4f1a\u9a97\u6211\u3002\u201d\u505c\u4e0b\u811a\u6b65\uff0c\u8f6c\u8eab\u8d70\u3001 \u524d\uff0c\u5fc3\u4e2d\u7591\u4e91\u5927\u8d77\uff0c\u60f3\u6765\u60f3\u53bb\uff0c\u53c8\u60f3\u4e0d\u51fa\u90a3\u6bdb\u6587\u742a\u4e3a\u4ec0\u4e48\u4f1a\u7a81\u5730\u8d70\u4e86\u3002 \u3000\u3000\u8fd9\u4e9b\u5929\u6765\uff0c\u4ed6\u786e\u4fe1\u5979\u5df2\u5760\u5165\u81ea\u5df1\u7684\u60c5\u7eb2\uff0c\u800c\u4e14\u5760\u5f97\u90a3\u4e48\u6df1\uff0c\u8fd9\u5929\u771f\u800c\u7eaf\u6d01\u7684\u5973\u5b69\u5b50\uff0c\u7ec8 \u65e5\u5fc3\u4e2d\u6240\u60f3\u7684\uff0c\u5c31\u662f\u672a\u6765\u5e78\u798f\u7684\u61a7\u61ac\uff0c\u5979\u51e0\u4e4e\u8981\u4e0d\u53bb\u89c1\u5979\u5e08\u7236\u800c\u968f\u7740\u81ea\u5df1\u3002 \u3000\u3000\u201c\u4f46\u6b64\u523b\u5979\u5374\u8d70\u4e86\u3002\u201d\u8fd9\u662f\u4e00\u4ef6\u591a\u4e48\u4ee4\u4eba\u60ca\u6115\u7684\u4e8b\uff0c\u4ec7\u6055\u5fc3\u4e2d\uff0c\u53ea\u89c9\u4eff\u4f5b\u5931\u843d\u4e86\u4ec0\u4e48\uff0c \u4e00\u65f6\u4e4b\u95f4\uff0c\u7adf\u7a7a\u865a\u5f97\u5f88\u3002 \u3000\u3000\u201c\u672a\u6709\u6240\u5f97\uff0c\u600e\u6709\u6240\u5931\uff1f\u201d\u4ed6\u6697\u95ee\u7740\u81ea\u5df1\uff1a\u201c\u96be\u9053\u6211\u66fe\u89c9\u5f97\u5230\u8fc7\u4ec0\u4e48\uff0c\u96be\u9053\u6211\u5df1\u4e3a\u6211\u6240 \u5f97\u7684\u4e1c\u897f\u800c\u611f\u5230\u53ef\u8d35\uff0c\u4e0d\u7136\u6b64\u523b\u6211\u4e3a\u4ec0\u4e48\u53c8\u4f1a\u6709\u5931\u843d\u4e86\u4ec0\u4e48\u7684\u611f\u89c9\u5462\uff1f\u800c\u4e14\u8fd9\u4efd\u611f\u89c9\u662f\u5982\u6b64 \u6d53\u539a\u3002\u201d \u3000\u3000\u4f46\u4ed6\u968f\u5373\u53c8\u4e3a\u81ea\u5df1\u8fa9\u62a4\uff1a\u201c\u6211\u8fd9\u4e0d\u8fc7\u5728\u5947\u602a\u7f62\u4e86\uff0c\u5440\u2026\u2026\u96be\u9053\u5979\u662f\u56e0\u4e3a\u77e5\u9053\u6211\u5728\u9a97\u5979\uff0c \u662f\u4ee5\u624d\u8d70\u4e86\u7684\u5417\uff1f\u96be\u9053\u5979\u5df2\u77e5\u9053\u6211\u662f\u6765\u5bfb\u4ec7\u7684\u4eba\uff1f\u96be\u9053\u6211\u4e4b\u6240\u4ee5\u5bf9\u5979\u597d\uff0c\u65e0\u975e\u662f\u4e3a\u4e86\u60f3\u9a97\u5979 \u7684\u60c5\u611f\uff0c\u6765\u4f24\u5979\u7236\u4eb2\u7684\u5fc3\uff1f\u201d \u7b2c\u5341\u56db\u7ae0>> \u53e4\u9f99\u300a\u6e58\u5983\u5251\u300b \u7b2c\u5341\u56db\u7ae0 \u3000\u3000\u8fd9\u4e9b\u95ee\u9898\uff0c\u5728\u4ec7\u6055\u5fc3\u4e2d\uff0c\u53d8\u6210\u4e86\u4e00\u4e2a\u4e2a\u96be\u4ee5\u5316\u89e3\u7684\u6b7b\u7ed3\uff0c\u4ed6\u5446\u5446\u5730\u6115\u4e86\u534a\u664c\uff0c\u5374\u4f7f\u201c\u767e \u6b65\u98de\u82b1\u201d\u53c8\u662f\u201c\u5657\u55e4\u201d\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u770b\u4f60\uff0c\u6c14\u6210\u90a3\u526f\u6837\u5b50\uff0c\u6765\u6765\uff0c\u5750\u5728\u8fd9\u91cc\uff0c\u8ba9\u5927\u59d0\u59d0\u5b89 \u6170\u5b89\u6170\u4f60\uff0c\u5979\u8d70\u4e86\u5c31\u8d70\u4e86\uff0c\u6709\u4ec0\u4e48\u5173\u7cfb\uff0c\u5929\u4e0b\u7684\u5973\u4eba\u53c8\u6ca1\u6709\u6b7b\u5149\uff01\u90a3\u6bdb\u6587\u742a\u53ea\u4e0d\u8fc7\u9ec4\u6bdb\u201d\u201d \u5934\u4e00\u4e2a\u800c\u5df2\uff0c\u6709\u4ec0\u4e48\u4e86\u4e0d\u8d77\u3002\u201d \u3000\u3000\u4ec7\u6055\u5c55\u989c\u4e00\u7b11\uff0c\u5fd6\u9053\uff1a\u201c\u60f3\u6765\u5979\u5e76\u672a\u56e0\u4e3a\u77e5\u9053\u6211\u7684\u6765\u5386\u800c\u8d70\uff0c\u5426\u5219\u8fd9\u6797\u7426\u7b5d\u53c8\u600e\u4f1a\u5bf9\u6211 \u5982\u6b64\u3002\u201d\u4e8e\u662f\u4ed6\u9762\u4e0a\u7684\u7b11\u5bb9\u5c31\u8d8a\u53d1\u5f00\u6717\u4e86\u3002 \u3000\u3000\u5ba2\u6808\u91cc\u7684\u4eba\u58f0\u6742\u4e71\u4e86\u8d77\u6765\uff0c\u5957\u7272\u53e3\u7684\u58f0\u97f3\uff0c\u8d76\u8f66\u7684\u58f0\u97f3\uff0c\u5e97\u5c0f\u4e8c\u5927\u58f0\u5730\u5406\u559d\u7740\uff1a\u201c\u8d76\u8def \u8981\u8d76\u65e9\uff0c\u8fdf\u4e86\u5c31\u70ed\u4e86\uff0c\u82e5\u8981\u5403\u65e9\u70b9\uff0c\u9a6c\u4e0a\u5c31\u9001\u5230\uff0c\u884c\u674e\u83ab\u5c11\u5e26\uff0c\u5e97\u94b1\u83ab\u5fd8\u4e86\uff0c\u5c0f\u8d39\u65e0\u6240\u8c13\uff0c \u6709\u6ca1\u6709\u90fd\u597d\u3002\u201d \u3000\u3000\u6797\u7426\u7b5d\u201c\u54af\u54af\u201d\u5730\u7b11\u7740\uff0c\u548c\u4ec7\u6055\u8bf4\u7740\u8bdd\uff0c\u4e00\u53cc\u79cb\u6ce2\uff0c\u50cf\u662f\u6625\u6c34\u822c\u8361\u6f3e\u7740\u3002 \u3000\u3000\u5979\u521d\u51fa\u6c5f\u6e56\u4e4b\u65f6\uff0c\u60c5\u7aa6\u521d\u5f00\uff0c\u90a3\u65f6\u5979\u5e08\u5144\u70b9\u82cd\u6d3e\u7684\u4e00\u4ee3\u5251\u5ba2\u795e\u5251\u624b\u8c22\u9524\u65b9\u624d\u53bb\u4e16\uff0c\u5979\u5728 \u4e00\u65e0\u7ba1\u675f\u7684\u60c5\u5f62\u4e0b\uff0c\u4fbf\u5df2\u5341\u5206\u653e\u8361\u3002 \u3000\u3000\u6b64\u540e\u7684\u4e00\u4e9b\u65f6\u65e5\u91cc\uff0c\u5979\u867d\u4e5f\u4f1a\u655b\u675f\u51e0\u5e74\uff0c\u4f46\u4e0d\u4e45\u4fbf\u53c8\u6545\u6001\u590d\u840c\uff0c\u751a\u81f3\u53d8\u672c\u52a0\u5389\u8d77\u6765\uff0c\u6b66 \u6797\u4e4b\u4e2d\u7a0d\u4e0d\u68c0\u70b9\u7684\u5e74\u9752\u8c6a\u5ba2\uff0c\u5341\u4eba\u4e4b\u4e2d\uff0c\u603b\u6709\u4e09\u4e94\u4eba\u548c\u8fd9\u4f4d\u201c\u767e\u6b65\u98de\u82b1\u201d\u6709\u8fc7\u4e00\u817f\uff0c\u6b64\u4e8b\u5df2 \u6210\u516c\u5f00\u4e4b\u79d8\u5bc6\uff0c\u4f46\u662f\u5927\u5bb6\u90fd\u6ca1\u6709\u6302\u5728\u5634\u4e0a\u800c\u5df2\u3002 \u3000\u3000\u4e16\u95f4\u65e0\u8bba\u4efb\u4f55\u4e00\u4e2a\u5973\u5b50\u2014\u2014\u5c24\u5176\u50cf\u5979\u4e4b\u7c7b\u7684\u8361\u5987\uff0c\u89c1\u4e86\u4ec7\u6055\u8fd9\u79cd\u82f1\u4fca\u5c11\u5e74\uff0c\u53ef\u8bf4\u6ca1\u6709\u4e00 \u4eba\u4f1a\u4e0d\u52a8\u5fc3\u7684\u3002 \u3000\u3000\u800c\u4ec7\u6055\u5462\uff1f\u4ed6\u53c8\u600e\u4f1a\u4e0d\u77e5\u9053\u8fd9\u6797\u7426\u7b5d\u7684\u7528\u610f\uff0c\u4ed6\u751f\u5177\u5929\u6027\uff0c\u5bf9\u8fd9\u79cd\u5973\u5b50\u672c\u6781\u4e0d\u803b\uff0c\u4f46\u5374 \u53c8\u81ea\u5df1\u544a\u8bc9\u81ea\u5df1\uff0c\u8fd9\u662f\u4e2a\u6781\u597d\u7684\u673a\u4f1a\uff0c\u56e0\u4e4b\u4ed6\u4fbf\u4e5f\u4f5c\u51fa\u4e00\u526f\u65e0\u77e5\u7684\u6837\u5b50\uff0c\u548c\u8fd9\u6797\u7426\u7b5d\u6b22\u8c08 \u7740\uff0c\u53ea\u662f\u4ed6\u5fc3\u4e2d\u5374\u65e0\u65f6\u65e0\u523b\u4e0d\u5728\u6697\u95ee\u81ea\u5df1\uff1a\u201c\u6587\u742a\u600e\u4f1a\u7a81\u5730\u8d70\u4e86\u5462\uff1f\u201d \u3000\u3000\u6625\u65e5\u65e2\u5347\uff0c\u6e10\u9ad8\uff0c\u5e97\u91cc\u7684\u5c0f\u4e8c\u8f7b\u8f7b\u6572\u4e86\u6572\u623f\u95e8\uff0c\u8f7b\u8f7b\u8d70\u4e86\u8fdb\u6765\uff0c\u8f7b\u8f7b\u653e\u4e0b\u8336\u6c34\uff0c\u53c8\u8f7b\u8f7b \u8d70\u4e86\u51fa\u53bb\uff0c\u867d\u7136\u6781\u529b\u63a7\u5236\u7740\u81ea\u5df1\uff0c\u5374\u8fd8\u4e0d\u65f6\u5077\u5077\u5411\u6797\u7426\u7b5d\u779f\u4e0a\u4e24\u773c\uff0c\u8179\u5185\u6697\u81ea\u5600\u5495\u7740\uff1a\u201c\u8fd9 \u5c0f\u5b50\u8273\u798f\u771f\u4e0d\u6d45\uff0c\u6628\u5929\u665a\u4e0a\u662f\u4e2a\u5927\u59d1\u5a18\uff0c\u4eca\u5929\u53c8\u6362\u4e86\u4e2a\u6c34\u871c\u6843\u3002\u201d\u8fc7\u4e86\u4e00\u4f1a\uff0c\u53c8\u9001\u8fdb\u4e00\u58f6\u8336 \u6765\uff0c\u4e3a\u7684\u662f\u60f3\u591a\u770b\u4e24\u773c\u3002 \u3000\u3000\u8fd9\u539f\u672c\u662f\u6625\u5929\u5440\uff01\u5728\u6625\u5929\u91cc\uff0c\u8fde\u732b\u513f\u90fd\u4f1a\u53eb\u6625\u54e9\uff01 \u3000\u3000\u7b49\u5230\u5e97\u5c0f\u4e8c\u7b2c\u4e09\u6b21\u8fdb\u6765\uff0c\u53c8\u8d70\u51fa\u53bb\u7684\u65f6\u5019\uff0c\u6797\u7426\u7b5d\u5fae\u98a6\u9edb\u7709\uff0c\u5374\u5a07\u7b11\u9053\uff1a\u201c\u6211\u7559\u5728\u8fd9 \u91cc\uff0c\u5c31\u662f\u4e3a\u4e86\u548c\u4f60\u5b89\u5b89\u9759\u9759\u5730\u8c08\u8c08\u8bdd\uff0c\u53ef\u662f\uff5e\u4e00\u4f60\u770b\uff0c\u8fd9\u91cc\u5435\u5f97\u6b7b\u4eba\uff0c\u5582\uff0c\u6211\u8bf4\u5144\u5f1f\uff0c\u4f60\u8981 \u662f\u6ca1\u6709\u4e8b\uff0c\u5c31\u966a\u4f60\u5927\u59d0\u59d0\u901b\u901b\uff0c\u7b49\u4f1a\u968f\u4fbf\u627e\u4e2a\u5730\u65b9\u559d\u4e0a\u4e24\u676f\uff0c\u7136\u540e\u2026\u2026\u201d\u5979\u54af\u54af\u4e00\u58f0\u5a07\u7b11\uff1a \u201c\u6211\u6700\u559c\u6b22\u770b\u4f60\u559d\u9152\u7684\u6837\u5b50\uff0c\u6628\u5929\u4f60\u559d\u4e86\u9152\uff0c\u8138\u7ea2\u7ea2\u7684\uff0c\u5c31\u50cf\u2026\u2026\u5c31\u50cf\u4e2a\u5927\u82f9\u679c\u4f3c\u7684\u3002\u201d \u3000\u3000\u4e8e\u662f\u4ec7\u6055\u7b97\u6e05\u4e86\u5e97\u94b1\uff0c\u548c\u6797\u7426\u7b5d\u8d70\u51fa\u623f\u95e8\uff0c\u4e00\u9762\u7b11\u9053\uff1a\u201c\u4eca\u5929\u6211\u966a\u5927\u59d0\u59d0\u75db\u75db\u5feb\u5feb\u5730\u73a9 \u4e00\u5929\uff0c\u660e\u5929\u6211\u53ef\u8981\u8d76\u5230\u6cb3\u5317\u53bb\uff0c\u6211\u7239\u7239\u6709\u4ef6\u751f\u610f\u5728\u90a3\u91cc\uff0c\u8fd8\u7b49\u7740\u6211\u53bb\u6599\u7406\u5462\u3002\u201d \u3000\u3000\u6797\u7426\u7b5d\u62bf\u5634\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4eca\u5929\u6211\u4eec\u73a9\u8fc7\u4e86\u518d\u8bf4\uff0c\u4f60\u8981\u662f\u771f\u8ba9\u5927\u59d0\u59d0\u73a9\u5f97\u75db\u5feb\uff0c\u660e\u5929\u5927\u59d0 \u59d0\u5c31\u5f53\u4f60\u7684\u4fdd\u9556\uff0c\u966a\u4f60\u5230\u6cb3\u5317\u53bb\u4e00\u8d9f\u3002\u201d\u4ec7\u6055\u4fa7\u76ee\u4e00\u671b\uff0c\u53ea\u89c1\u5979\u53cc\u988a\u7adf\u5df2\u5ae3\u7ea2\uff0c\u4e0d\u7981\u5fc3\u4e2d\u6697 \u9a82\u4e00\u58f0\uff1a\u201c\u65e0\u803b\u7684\u8361\u5987\u3002\u201d\u9762\u4e0a\u5374\u4ecd\u7136\u7b11\u5bb9\u6ee1\u9762\u4ed6\u8bf4\u9053\uff1a\u201c\u6709\u4e86\u5927\u59d0\u59d0\u4f5c\u6211\u7684\u4fdd\u9556\uff0c\u90a3\u6211\u5c31 \u653e\u5fc3\u4e86\u3002\u201d\u7a7f\u8fc7\u56de\u5eca\uff0c\u8d70\u51fa\u5e97\u95e8\uff0c\u9633\u5149\u5df2\u6652\u6ee1\u4fa7\u9762\uff0c\u7f2a\u6587\u542b\u7b11\u56de\u987e\uff0c\u5374\u89c1\u8eab\u4fa7\u7684\u6797\u7426\u7b5d\u9762\u8272 \u7adf\u7a81\u5730\u4e00\u53d8\uff0c\u6cbf\u7740\u5979\u7684\u76ee\u5149\u671b\u53bb\uff0c\u53ea\u89c1\u8857\u5fc3\u4e00\u4eba\u508d\u9a6c\u800c\u7acb\uff0c\u80cc\u810a\u633a\u5f97\u7b14\u76f4\uff0c\u76ee\u5149\u51db\u7136\u671b\u7740\u81ea \u5df1\uff0c\u5374\u662f\u90a3\u5b50\u6bcd\u53cc\u98de\u201c\u5de6\u624b\u795e\u5251\u201d\u4e01\u8863\u3002 \u3000\u3000\u6709\u98ce\u5439\u8fc7\uff0c\u5439\u5f97\u8fd9\u201c\u5de6\u624b\u795e\u5251\u201d\u7684\u8863\u8882\u4e0d\u4f4f\u98de\u626c\uff0c\u4f46\u4ed6\u7684\u8eab\u8eaf\uff0c\u5374\u751f\u50cf\u662f\u94c1\u77f3\u94f8\u6210\u7684\uff0c \u4e00\u52a8\u53c8\u4e0d\u52a8\uff0c\u9762\u76ee\u4e4b\u4e0a\uff0c\u4ea6\u662f\u6728\u7136\u6ca1\u6709\u8868\u60c5\uff0c\u53ea\u6709\u4e00\u53cc\u773c\u775b\uff0c\u70af\u70af\u53d1\u7740\u5149\u91c7\u3002 \u3000\u3000\u6797\u7426\u7b5d\u9762\u8272\u5fae\u5fae\u4e00\u53d8\uff0c\u77ac\u5373\u5a07\u7b11\u5982\u5e38\uff0c\u7f13\u6b65\u8d70\u4e86\u8fc7\u53bb\uff0c\u7b11\u9053\uff1a\u201c\u4e01\u56db\u54e5\uff0c\u4f60\u600e\u4e48\u4e5f\u6765 \u4e86\uff0c\u4f60\u4e0d\u662f\u548c\u6bdb\u5927\u54e5\u4e00\u9f50\u56de\u676d\u5dde\u53bb\u4e86\u5417\uff1f\u201d \u3000\u3000\u4e01\u8863\u51b7\u201c\u54fc\u201d\u4e00\u58f0\uff0c\u76ee\u5149\u5374\u4ecd\u7136\u505c\u7559\u5728\u4ec7\u6055\u8138\u4e0a\uff0c\u4ec7\u6055\u6697\u4e2d\u4e00\u7b11\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u4f4d\u5de6\u624b\u795e \u5251\u6562\u60c5\u662f\u5728\u5403\u918b\u3002\u201d \u3000\u3000\u5374\u542c\u4e01\u8863\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u5c31\u77e5\u9053\u4f60\u662f\u770b\u4e2d\u4e86\u8fd9\u5c0f\u5b50\uff0c\u662f\u4ee5\u624d\u4e0d\u80af\u548c\u6211\u4eec\u4e00\u9f50\u56de\u676d \u5dde\u3002\u201d\u6797\u7426\u7b5d\u9762\u8272\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u4e01\u56db\u54e5\uff0c\u4f60\u8fd9\u8bf4\u7684\u4ec0\u4e48\u8bdd\uff0c\u6211\u7231\u5230\u54ea\u91cc\u5c31\u5230\u54ea\u91cc\uff0c\u96be\u9053\u8fd8\u6709 \u8c01\u80fd\u7ba1\u5f97\u4e86\u6211\u5417\uff1f\u201d \u3000\u3000\u4e01\u8863\u76ee\u5149\u4e00\u8f6c\uff0c\u9762\u4e0a\u7adf\u5806\u51fa\u7b11\u5bb9\uff0c\u9053\uff1a\u201c\u4e03\u59b9\uff0c\u4f60\u522b\u751f\u6c14\u3002\u201d\u4ec7\u6055\u6697\u4e2d\u4e00\u7b11\uff1a\u201c\u8fd9\u4f4d\u5de6 \u624b\u795e\u5251\uff0c\u6562\u60c5\u7adf\u6709\u4e09\u5206\u754f\u60e7\u4e8e\u5979\u3002\u201d\u76ee\u5149\u4e00\u8f6c\uff0c\u53ea\u89c1\u6797\u7426\u7b5d\u4e5f\u5df2\u5a07\u7b11\u8d77\u6765\uff0c\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u6765 \u53c8\u4e3a\u7684\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e01\u8863\u6a2a\u7768\u4ec7\u6055\u4e00\u773c\uff0c\u9053\uff1a\u201c\u6bdb\u5927\u54e5\u5341\u65e5\u4e4b\u540e\uff0c\u5728\u676d\u5dde\u57ce\u6446\u4e0b\u82f1\u96c4\u76db\u5bb4\uff0c\u8fd9\u4e00\u6b21\u5c06\u5357\u4e03\u5317 \u516d\u5341\u4e09\u7701\u4e2d\u6709\u5934\u6709\u8138\u7684\u89d2\u8272\u90fd\u8bf7\u5230\u4e86\uff0c\u662f\u4ee5\u53eb\u6211\u6765\u901a\u77e5\u4f60\u4e00\u58f0\uff0c\u5927\u54e5\u4ed6\u2026\u2026\u563f\u563f\uff0c\u4ed6\u6015\u4f60\u73a9 \u5f97\u8fde\u6b63\u4e8b\u90fd\u5fd8\u4e86\u3002\u201d\u4ec7\u6055\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8fde\u5fd9\u5927\u6b65\u8d70\u4e86\u8fc7\u53bb\uff0c\u5148\u5411\u4e01\u8863\u5f53\u5934\u4e00\u63d6\uff0c\u8f6c\u8eab\u5374\u5411\u6797\u7426 \u7b5d\u7b11\u9053\uff1a\u201c\u6797\u5927\u59d0\u65e2\u7136\u6709\u7740\u6b63\u4e8b\uff0c\u90a3\u4e48\u5c0f\u5f1f\u5c31\u544a\u8f9e\u4e86\uff0c\u53cd\u6b63\u6765\u65e5\u65b9\u957f\uff0c\u65e5\u540e\u5c0f\u5f1f\u5fc5\u5b9a\u966a\u5927\u59d0 \u75db\u996e\u4e09\u65e5\u3002\u201d\u8eac\u8eab\u4e00\u63d6\uff0c\u8f6c\u5934\u800c\u53bb\uff0c\u53ea\u542c\u90a3\u201c\u767e\u6b65\u98de\u82b1\u201d\u53e3\u4e2d\u6025\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u2026\u2026\u201d\u4e0b\u6587 \u5374\u518d\u4e5f\u65e0\u6cd5\u8bf4\u4e0b\u53bb\uff0c\u53c8\u542c\u5f97\u90a3\u201c\u5b50\u6bcd\u53cc\u98de\u201d\u9053\uff1a\u201c\u5927\u54e5\u5728\u676d\u5dde\u57ce\u7b49\u6211\u4eec\uff0c\u8fd9\u4e00\u6b21\u6b66\u6797\u76db\u4f1a\uff0c \u4f60\u9519\u8fc7\u4e86\u5c82\u4e0d\u53ef\u60dc\u3002\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u65e2\u662f\u5f97\u610f\uff0c\u53c8\u662f\u597d\u7b11\uff0c\u60f3\u90a3\u6797\u7426\u7b5d\u8138\u76ae\u518d\u539a\uff0c\u4e5f\u4e0d\u4f1a\u5f53\u7740\u201c\u5de6\u624b\u795e\u5251\u201d\u62c9\u4f4f\u81ea \u5df1\uff0c\u8fd9\u4e00\u6b21\u5979\u88ab\u4e01\u8863\u7f20\u4f4f\uff0c\u5fc5\u4e5f\u65e0\u6cd5\u518d\u6765\u5bfb\u627e\u81ea\u5df1\uff0c\u4f46\u81ea\u5df1\u4ee5\u540e\u82e5\u6709\u7528\u5f97\u7740\u5979\u4e4b\u5904\uff0c\u5374\u53ef\u53bb \u627e\u5979\uff0c\u5fc3\u4e2d\u4e00\u8f6c\uff0c\u53c8\u60f3\u51fa\u4e00\u4e2a\u4e3b\u610f\uff0c\u5634\u89d2\u4e0d\u7981\u6cdb\u8d77\u4e00\u4e1d\u5fae\u7b11\u3002 \u3000\u3000\u6cbf\u7740\u8857\u6a90\u8d70\u8fc7\u4e86\u8fd9\u6761\u8857\uff0c\u56de\u76ee\u4e00\u671b\uff0c\u53ea\u89c1\u90a3\u5546\u8d3e\u6253\u626e\u7684\u6c49\u5b50\u679c\u7136\u5df1\u8ddf\u5728\u81ea\u5df1\u8eab\u540e\uff0c\u4ed6\u624b \u5fae\u4e00\u62ac\uff0c\u6253\u4e86\u4e2a\u624b\u52bf\uff0c\u90a3\u6c49\u5b50\u4fbf\u4e00\u58f0\u547c\u54e8\uff0c\u558a\u6765\u4e00\u8f86\u5927\u8f66\uff0c\u8de8\u4e0a\u8f66\u8f95\uff0c\u4ec7\u6055\u6c89\u58f0\u9053\uff1a\u201c\u9a76\u51fa \u57ce\u53bb\u3002\u201d \u3000\u3000\u90a3\u8f66\u592b\u9a6c\u97ad\u4e00\u626c\uff0c\u201c\u5427\u201d\u5730\u843d\u4e0b\uff0c\u5927\u8f66\u4fbf\u8d70\u5f97\u66f4\u5feb\uff0c\u4ec7\u6055\u56de\u9996\u9053\uff1a\u201c\u6628\u5929\u665a\u4e0a\u6211\u5429\u5490\u4f60 \u7684\u4e8b\uff0c\u4f60\u53ef\u5168\u90fd\u505a\u4e86\u5417\uff1f\u201d \u3000\u3000\u90a3\u5546\u8d3e\u6253\u626e\u7684\u6c49\u5b50\uff0c\u606d\u58f0\u9053\uff1a\u201c\u5c0f\u7684\u5df2\u53eb\u5b8b\u5c0f\u5200\u8fde\u591c\u8d76\u5230\u676d\u5dde\uff0c\u5927\u7ea6\u4e0d\u51fa\u4e09\u65e5\uff0c\u4fbf\u6709\u6bdb \u81ec\u7684\u6d88\u606f\uff0c\u201d\u4ec7\u6055\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u90a3\u6c49\u5b50\u53c8\u9053\u3002 \u3000\u3000\u201c\u90a3\u59d3\u80e1\u7684\u80d6\u5b50\u6628\u5929\u5728\u8fd9\u91cc\u6298\u817e\u4e86\u4e00\u5927\uff0c\u53c8\u5f04\u4e86\u4e24\u4e2a\u7c89\u5934\u559d\u9152\uff0c\u76f4\u5230\u665a\u4e0a\u624d\u53bb\uff0c\u6709\u4e09 \u4e2a\u201d\u94c1\u9a91\u795e\u97ad\u201d\u961f\u7684\u5bb6\u4f19\u51fa\u57ce\u5f80\u4e1c\u8d70\uff0c\u9648\u94c1\u5934\u8ddf\u4e86\u53bb\u4e00\u770b\uff0c\u8fd9\u4e09\u4e2a\u5c0f\u5b50\u4e0d\u77e5\u600e\u7684\uff0c\u5728\u57ce\u5916\u5168 \u53eb\u4eba\u7ed9\u6cbb\u6b7b\u4e86\uff0c\u8eab\u4e0a\u53ea\u6709\u4e00\u5904\u521b\u4f24\uff0c\u663e\u89c1\u90a3\u52a8\u624b\u7684\u4eba\u624b\u811a\u5e72\u51c0\u5f97\u843d\u5f97\u5f88\uff0c\u9648\u94c1\u5934\u67e5\u4e86\u4e00\u67e5\uff0c \u4e5f\u4e0d\u77e5\u9053\u662f\u8c01\uff1f\u201d \u3000\u3000\u4ec7\u6055\u53c8\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u5fc3\u91cc\u77e5\u9053\u8fd9\u5fc5\u5b9a\u5c31\u662f\u90a3\u77f3\u78f7\u5f04\u7684\u624b\u811a\u4e86\u3002 \u3000\u3000\u90a3\u6c49\u5b50\u987f\u4e86\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u80e1\u80d6\u5b50\u4e00\u8d77\u66f4\u5c31\u8d70\u4e86\uff0c\u4e5f\u662f\u56de\u676d\u5dde\uff0c\u81f3\u4e8e\u516c\u4e86\u53eb\u5c0f\u7684\u6478\u90a3\u84dd \u8863\u4eba\u7684\u6d77\u5e95\uff0c\u5c0f\u7684\u5374\u6478\u4e0d\u6e05\u695a\uff0c\u6628\u5929\u665a\u4e0a\u8ddf\u7740\u4ed6\u540e\u9762\u624d\u8d70\u4e86\u534a\u6761\u8857\uff0c\u773c\u775b\u4e00\u8f6c\uff0c\u4ed6\u5c31\u4e0d\u89c1 \u4e86\uff0c\u516c\u5b50\uff0c\u8fd9\u4eba\u53ef\u771f\u624e\u624b\u5f97\u5f88\uff0c\u6211\u725b\u4e09\u773c\u6df7\u4e86\u8fd9\u4e48\u4e45\uff0c\u8fd8\u6ca1\u89c1\u8fc7\u8fd9\u4e48\u673a\u7075\u7684\u4eba\u3002\u201d \u3000\u3000\u4ec7\u6055\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6b64\u4eba\u7684\u6d77\u5e95\u6211\u5df2\u77e5\u9053\uff0c\u4f60\u4e0d\u7528\u518d\u6253\u542c\u4e86\u3002\u201d\u76ee\u5149\u8f6c\u5904\uff0c\u53ea\u89c1\u8fd9 \u201c\u725b\u4e09\u773c\u201d\u9762\u4e0a\u6ee1\u662f\u94a6\u670d\u4e4b\u8272\uff0c\u4e0d\u7981\u4e00\u7b11\u53c8\u9053\uff1a\u201c\u6628\u591c\u548c\u6211\u5728\u4e00\u8d77\u7684\u90a3\u5973\u5b50\uff0c\u4f60\u53ef\u770b\u5230\u5979\u7684 \u53bb\u5904\uff1f\u201d \u3000\u3000\u201c\u725b\u4e09\u773c\u201d\u773c\u775b\u4e00\u5f20\uff0c\u50cf\u662f\u4e0d\u80dc\u60ca\u5f02\u4ed6\u8bf4\u9053\uff1a\u201c\u6628\u5929\u5979\u4e0d\u662f\u548c\u516c\u5b50\u4e00\u9f50\u6295\u5e97\u7684\u5417\uff0c\u5979\u4e00 \u76f4\u4e5f\u6ca1\u6709\u51fa\u6765\u8fc7\u5440\uff01\u201d \u3000\u3000\u4ec7\u6055\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u53cc\u7709\u5fae\u76b1\uff0c\u5fc3\u91cc\u66f4\u5947\u602a\uff01 \u3000\u3000\u201c\u90a3\u4e48\u5979\u53c8\u5230\u54ea\u91cc\u53bb\u4e86\u5462\uff1f\u201d \u3000\u3000\u4ed6\u4fef\u9996\u6c89\u541f\u534a\u664c\uff0c\u90a3\u201c\u725b\u4e09\u773c\u201d\u53c8\u81ea\u606d\u58f0\u9053\uff1a\u201c\u73b0\u5728\u5c0f\u7684\u8fd8\u6709\u4e94\u4e2a\u5f1f\u5144\u5728\u8fd9\u91cc\uff0c\u90fd\u6b47\u5728 \u57ce\u5916\u7684\u2018\u66fe\u6c0f\u5bb6\u8bcd\u2019\uff0c\u516c\u5b50\u82e5\u662f\u8fd8\u6709\u4ec0\u4e48\u5429\u5490\uff0c\u5c0f\u7684\u7acb\u523b\u5c31\u53bb\u901a\u77e5\u4ed6\u4eec\u3002\u201d \u3000\u3000\u4ec7\u6055\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4e9b\u65e5\u5b50\uff0c\u53ef\u8f9b\u82e6\u4f60\u4e86\u3002\u201d\u968f\u624b\u4ece\u6000\u4e2d\u638f\u51fa\u4e00\u5f20\u94f6\u7968\uff0c\u770b\u4e5f\u4e0d \u770b\uff0c\u5c31\u4ea4\u7ed9\u4e86\u4ed6\uff0c\u53c8\u9053\uff1a\u201c\u8fd9\u70b9\u94f6\u5b50\uff0c\u4f60\u5c31\u62ff\u53bb\u4e70\u9152\u559d\u5427\u3002\u201d \u3000\u3000\u90a3\u2018\u725b\u4e09\u773c\u2019\u773c\u775b\u4e00\u77aa\uff0c\u53f3\u624b\u8de8\u7740\u8f66\u8f95\uff0c\u5de6\u624b\u4e00\u62cd\u80f8\u812f\u6717\u58f0\u9053\uff1a\u201c\u516c\u5b50\uff0c\u60a8\u8fd9\u662f\u5e72\u4ec0 \u4e48\uff0c\u4e0a\u6b21\u624d\u7ed9\u4e86\u4e00\u5343\u4e24\u94f6\u5b50\uff0c\u6211\u4eec\u5144\u5f1f\u5341\u4e03\u4e2a\u600e\u4e48\u7528\u4e5f\u6ca1\u6709\u7528\u5b8c\uff0c\u8fd9\u6b21\u60a8\u600e\u4e48\u53c8\u7ed9\u4e86\u3002\u516c \u5b50\uff0c\u6211\u8ddf\u7740\u60a8\u529e\u4e8b\uff0c\u53ef\u4e0d\u662f\u4e3a\u7740\u60a8\u7684\u94f6\u5b50\uff0c\u6211\u2018\u725b\u4e09\u773c\uff0c\u867d\u7136\u4e0d\u662f\u4e2a\u4ec0\u4e48\u4e1c\u897f\uff0c\u4f46\u8fd9\u4e48\u591a\u5e74 \u6765\uff0c\u6211\u8ddf\u7740\u6881\u4e0a\u4eba\u6881\u5927\u54e5\u8d70\u5357\u95ef\u5317\uff0c\u80f3\u818a\u4e0a\u7ad9\u9e70\uff0c\u5927\u817f\u4e0a\u8dd1\u9a6c\uff0c\u4e5f\u662f\u6761\u54cd\u5f53\u5f53\u7684\u6c49\u5b50\uff0c\u516c \u5b50\uff0c\u60a8\u522b\u770b\u6881\u5927\u54e5\u53eb\u6211\u8ddf\u7740\u60a8\uff0c\u9519\u975e\u662f\u60a8\uff0c\u8981\u662f\u6362\u4e86\u4e2a\u4eba\uff0c\u6211\u2019\u725b\u4e09\u773c\u2019\u53ef\u4e5f\u6ca1\u6709\u8fd9\u4e48\u542c \u8bdd\uff0c\u6211\u6881\u5927\u54e5\u5e38\u8bf4\u5929\u4e0b\u82f1\u96c4\uff0c\u9664\u4e86\u516c\u5b50\u60a8\u4e4b\u5916\uff0c\u5c31\u518d\u6ca1\u6709\u522b\u7684\u4eba\u4e86\uff0c\u6211\u5148\u8fd8\u4e0d\u4fe1\uff0c\u53ef\u662f\u73b0\u5728 \u2014\u2014\u563f\uff0c\u6211\u53ef\u4fe1\u4e86\uff0c\u5c31\u51ed\u60a8\u8fd9\u79cd\u6c14\u6d3e\u2014\u2014\u201d\u4ec7\u6055\u5fae\u5fae\u4e00\u7b11\uff0c\u622a\u65ad\u4e86\u8fd9\u8349\u83bd\u597d\u6c49\u201c\u725b\u4e09\u773c\u201d\u7684 \u7d6e\u7d6e\u4e4b\u8a00\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u81ea\u7136\u6211\u4e5f\u6e05\u695a\uff0c\u53ea\u662f\u8fd9\u70b9\u94f6\u5b50\uff0c\u4f60\u8fd8\u662f\u62ff\u53bb\u7684\u597d\uff0c\u4f60\u867d\u4e0d\u8981\uff0c\u4f46\u4f60 \u624b\u4e0b\u7684\u5144\u5f1f\u53ef\u8981\u94f6\u5b50\u4f7f\u5440\u8c03\u7ec8\u4e8e\u5c06\u94f6\u7968\u585e\u5728\u4ed6\u624b\u91cc\u3002\u53c8\u9053\uff1a\u201c\u6211\u4e5f\u60f3\u5230\u90a3\u2018\u66fe\u6c0f\u5bb6\u8bcd\u2019\u53bb\u770b \u770b\uff0c\u987a\u4fbf\u6211\u8fd8\u8981\u627e\u4eba\u5e26\u4e2a\u4fe1\uff0c\u901a\u77e5\u4f60\u90a3\u6881\u5927\u54e5\u548c\u9f99\u6c0f\u4e09\u5144\u5f1f\u4e00\u58f0\uff0c\u53eb\u4ed6\u4eec\u5341\u5929\u4e4b\u5185\uff0c\u90fd\u8d76\u5230 \u676d\u5dde\u53bb\u3002\u201d \u3000\u3000\u90a3\u201c\u725b\u4e09\u773c\u201d\u80f8\u819b\u4e00\u633a\uff0c\u9053\uff1a\u201c\u73b0\u5728\u5df2\u51fa\u4e86\u57ce\u4e86\uff0c\u66fe\u6c0f\u5bb6\u8bcd\uff0c\u5c31\u5728\u524d\u9762\u4e0d\u8fdc\u3002\u201d\u53c8\u559d \u9053\uff0c\u201c\u5582\uff0c\u5c0f\u6bdb\u81ec\uff0c\u4f60\u628a\u97ad\u5b50\u91cd\u6253\u4e24\u4e0b\uff0c\u8ba9\u9a6c\u8dd1\u5f97\u5feb\u4e00\u70b9\u3002\u201d \u3000\u3000\u56de\u9996\u7b11\u9053\uff1a\u201c\u516c\u5b50\uff0c\u6211\u7ba1\u8fd9\u8d76\u8f66\u7684\u53eb\u5c0f\u6bdb\u81ec\uff0c\u60a8\u770b\u8fd9\u82b1\u540d\u53d6\u5f97\u53ef\u597d\uff0c\u563f\u563f\uff0c\u60a8\u77a7\u8fd9\u5c0f\u6bdb \u81ec\u97ad\u5b50\u6325\u5f97\u591a\u54cd\uff0c\u6253\u5728\u9a6c\u8eab\u4e0a\uff0c\u53ef\u4f24\u4e0d\u4e86\u9a6c\u7684\u4e00\u6839\u6bdb\uff0c\u53ea\u6015\u90a3\u5927\u6bdb\u81ec\u8fd8\u6ca1\u6709\u8fd9\u4e00\u624b\u54e9\u3002\u201d \u3000\u3000\u4ec7\u6055\u201c\u5657\u55e4\u201d\u4e00\u58f0\uff0c\u7b11\u51fa\u58f0\u6765\uff0c\u53ea\u542c\u90a3\u8d76\u8f66\u7684\u7cbe\u795e\u8d8a\u53d1\u6296\u64de\uff0c\u201c\u5427\u5427\u201d\u8fde\u58f0\uff0c\u5c06\u624b\u4e2d\u7684 \u76ae\u97ad\u6325\u5f97\u5c71\u54cd\uff0c\u8f66\u9a6c\u679c\u7136\u8d70\u5f97\u66f4\u5feb\u4e86\u3002 \u3000\u3000\u90a3\u201c\u725b\u4e09\u773c\u201d\u8de8\u5728\u8f66\u8f95\u4e0a\uff0c\u633a\u7740\u80f8\u819b\uff0c\u8fce\u7740\u6251\u9762\u800c\u6765\u7684\u6625\u98ce\uff0c\u4e5f\u50cf\u662f\u975e\u5e38\u5f97\u610f\uff0c\u6b64\u523b\u4ed6 \u8eab\u4e0a\u7a7f\u7684\u867d\u4ecd\u662f\u4e00\u8eab\u4e70\u5356\u4eba\u7684\u6253\u626e\uff0c\u4f46\u5728\u4ed6\u8eab\u4e0a\uff0c\u5374\u4ece\u5934\u5230\u811a\u518d\u4e5f\u770b\u4e0d\u51fa\u534a\u5206\u4e70\u5356\u4eba\u7684\u6837\u5b50 \u6765\u4e86\u3002 \u3000\u3000\u8d76\u8f66\u7684\u624b\u4e2d\u76ae\u97ad\u518d\u6b21\u4e00\u626c\uff0c\u53e3\u4e2d\u201c\u5f97\u513f\u201d\u5406\u559d\u4e00\u58f0\uff0c\u8f66\u9a6c\u4fbf\u500f\u7136\u505c\u4e86\u4e0b\u6765\u3002\u201c\u725b\u4e09\u773c\u201d \u5237\u5730\u8df3\u5230\u5730\u4e0a\uff0c\u6253\u5f00\u8f66\u95e8\uff0c\u4e00\u9762\u8038\u9f3b\u9053\uff1a\u201c\u597d\u9999\uff0c\u597d\u9999\u3002\u8fd9\u73ed\u5c0f\u5b50\u60f3\u5fc5\u4e0d\u77e5\u4ece\u54ea\u91cc\u53c8\u5f04\u4e86\u6761 \u91ce\u72d7\u6765\uff0c\u516c\u5b50\uff0c\u60a8\u5403\u8fc7\u72d7\u8089\u6ca1\u6709\uff1f\u559d\uff0c\u90a3\u53ef\u771f\u9999\uff0c\u4e0d\u4fe1\u60a8\u95fb\u95fb\uff0c\u6211\u90a3\u51e0\u4e2a\u5b9d\u8d1d\u5f1f\u5144\uff0c\u53c8\u5728\u90a3 \u91cc\u7096\u8d77\u72d7\u8089\u6765\u4e86\uff0c\u5c0f\u6bdb\u81ec\uff0c\u4f60\u505c\u4e86\u8f66\u4e5f\u6765\u5403\u4e24\u7897\u3002\u201d \u3000\u3000\u4ec7\u6055\u5fae\u5fae\u4e00\u7b11\uff0c\u5fc3\u4e2d\u5374\u81ea\u611f\u6168\uff1a\u201c\u5c60\u72d7\u4e4b\u8f88\uff0c\u867d\u662f\u8349\u83bd\u5c0f\u4eba\uff0c\u5374\u6bcf\u591a\u6ca1\u5962\u906e\u7684\u4e49\u6c14\u6c49 \u5b50\uff0c\u90a3\u4e9b\u9526\u8863\u7389\u98df\u7684\u670b\u53cb\uff0c\u54fc\u201d\u2014\u2014\u4e3e\u76ee\u56db\u671b\uff0c\u53ea\u89c1\u56db\u4e0b\u9752\u8471\u4e00\u7247\uff0c\u5bc2\u9759\u65e0\u4eba\uff0c\u5c31\u8fde\u5730\u4e0a\u90fd \u957f\u6ee1\u4e86\u8352\u8349\uff0c\u51e0\u682a\u6b8b\u6768\u8d25\u67f3\u4e4b\u540e\uff0c\u5899\u5b57\u9690\u73b0\uff0c\u60f3\u5fc5\u5c31\u662f\u90a3\u201c\u66fe\u6c0f\u5bb6\u8bcd\u201d\u4e86\u3002 \u3000\u3000\u6625\u65e5\u90ca\u5916\u7684\u7a7a\u6c14\u91cc\uff0c\u81ea\u7136\u662f\u65e0\u6bd4\u7684\u6e05\u65b0\uff0c\u5728\u8fd9\u6e05\u65b0\u7684\u7a7a\u6c14\u91cc\uff0c\u5374\u679c\u771f\u4f20\u6765\u4e00\u9635\u9635\u6d53\u90c1\u7684 \u9999\u6c14\uff0c\u4ec7\u6055\u5fae\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u6211\u5e38\u542c\u8bf4\u767e\u755c\u4e4b\u4e2d\uff0c\u72d7\u8089\u6700\u9999\uff0c\u662f\u4ee5\u53eb\u505a\u9999\u8089\uff0c\u4f46\u5374\u59cb\u7ec8\u672a\u66fe \u5403\u8fc7\uff0c\u4eca\u65e5\u6211\u5012\u60f3\u5c1d\u5c1d\u8fd9\u540d\u6ee1\u5929\u4e0b\u7684\u5f02\u5473\u54e9\u3002\u201d \u3000\u3000\u201c\u725b\u4e09\u773c\u201d\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4e0d\u662f\u5c0f\u7684\u80e1\u8bf4\u4e71\u9053\uff0c\u60a8\u4e00\u5403\u4e86\u4e4b\u540e\uff0c\u7ba1\u4fdd\u8fde\u9e21\u9e2d\u9c7c \u8089\u90fd\u4e0d\u8981\u5403\u4e86\u3002\u90a3\u5473\u9053\u2014\u2014\u563f\uff0c\u55b7\u55b7\uff01\u53ef\u771f\u6559\u4eba\u8fde\u8bf4\u90fd\u8bf4\u4e0d\u51fa\u6765\u3002\u201d \u3000\u3000\u8fd9\u8bcd\u5802\u7684\u571f\u5899\uff0c\u7070\u571f\u65e9\u5df2\u9893\u8d25\uff0c\u90a3\u6247\u539f\u6765\u662f\u6731\u6f06\u7684\u5927\u95e8\uff0c\u6b64\u523b\u4e5f\u56e0\u5c81\u6708\u7684\u6d88\u5931\u800c\u53d8\u6210\u571f \u9ec4\u4e4b\u8272\uff0c\u95e8\u4e0a\u7684\u94dc\u73af\uff0c\u4e5f\u9508\u5f97\u53d1\u9ed1\u4e86\u3002 \u3000\u3000\u4e00\u8d70\u5230\u95e8\u53e3\uff0c\u201c\u725b\u4e09\u773c\u201d\u5c31\u5174\u9ad8\u91c7\u70c8\u5730\u559d\u9053\uff1a\u201c\u55e8\uff01\u4f60\u4eec\u522b\u5c3d\u987e\u7740\u5403\u72d7\u8089\u5440\uff0c\u5feb\u51fa\u6765\u770b \u770b\uff0c\u770b\u662f\u8c01\u6765\u4e86\u3002\u201d \u3000\u3000\u4ec7\u6055\u4e00\u7b11\uff0c\u54ea\u77e5\u7960\u5802\u4e4b\u5185\uff0c\u5374\u4ecd\u7136\u5bc2\u65e0\u4eba\u58f0\uff0c\u201c\u725b\u4e09\u773c\u201d\u76b1\u7709\u4f4e\u58f0\u9a82\u9053\uff1a\u201c\u8fd9\u4e9b\u72d7\u5934\uff0c \u5403\u72d7\u8089\u5403\u660f\u4e86\u5440\u8c03\u4e00\u811a\u8de8\u4e86\u8fdb\u53bb\uff0c\u53ea\u89c1\u8fd9\u8bcd\u5802\u7684\u6b63\u5802\u4e0a\uff0c\u5347\u7740\u4e00\u5806\u67f4\u706b\uff0c\u706b\u4e0a\u9ad8\u9ad8\u5730\u67b6\u7740\u4e09 \u6839\u6728\u68cd\uff0c\u68cd\u4e0a\u540a\u4e0b\u4e00\u53ea\u94dc\u9505\uff0c\u9505\u91cc\u70ed\u6c14\u817e\u817e\uff0c\u6d53\u90c1\u7684\u9999\u6c14\uff0c\u4e5f\u5c31\u662f\u4ece\u9505\u91cc\u5192\u51fa\u6765\u7684\u3002\u4f46\u662f\u67f4 \u706b\u7684\u4e24\u4fa7\uff0c\u5750\u7740\u7684\u5374\u4e0d\u662f\u4ed6\u610f\u6599\u4e2d\u7684\u4eba\uff0c\u800c\u662f\u4e24\u4e2a\u5e72\u7626\u7684\u8001\u8005\uff0c\u80e1\u987b\u90fd\u5df2\u5168\u767d\uff0c\u56db\u53ea\u773c\u775b\uff0c \u77ac\u4e5f\u4e0d\u77ac\u5730\u671b\u7740\u90a3\u53ea\u716e\u7740\u72d7\u8089\u7684\u9505\u5b50\uff0c\u4e00\u4eba\u624b\u91cc\u62ff\u7740\u4e00\u4e2a\u8db3\u53ef\u88c5\u4e0b\u4e09\u65a4\u82b1\u96d5\u7684\u9152\u80e1\u82a6\uff0c\u5374\u8fde \u671b\u4e5f\u4e0d\u671b\u8fd9\u5927\u58f0\u5406\u559d\u7740\u8fdb\u6765\u7684\u201d\u725b\u4e09\u773c\u201d\u4e00\u773c\u3002 \u3000\u3000\u201c\u725b\u4e09\u773c\u201d\u4e00\u671b\u4e4b\u4e0b\uff0c\u4e0d\u7981\u6115\u5f97\u5446\u7ad9\u5728\u5730\u4e0a\uff0c\u5f20\u5f00\u6765\u7684\u5634\u5df4\uff0c\u4e5f\u5450\u5450\u4ed6\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u4ec7 \u6055\u968f\u540e\u8d70\u4e86\u8fdb\u6765\uff0c\u4ea6\u662f\u4e3a\u4e4b\u4e00\u6115\uff0c\u53ea\u89c1\u8fd9\u4e24\u4e2a\u8001\u4eba\u8eab\u4e0a\u5404\u5404\u7a7f\u7740\u4e00\u4ef6\u8934\u891b\u7684\u9053\u888d\uff0c\u867d\u7136\u6ee1\u662f \u8865\u9489\uff0c\u4f46\u5374\u6d17\u5f97\u6781\u4e3a\u5e72\u51c0\uff0c\u5168\u767d\u7684\u80e1\u987b\uff0c\u957f\u957f\u5782\u4e86\u4e0b\u6765\uff0c\u5934\u4e0a\u7684\u767d\u53d1\uff0c\u5374\u633d\u4e86\u4e2a\u9053\u9afb\uff0c\u7528\u6839 \u4e4c\u6728\u63d2\u4f4f\u3002 \u3000\u3000\u201c\u725b\u4e09\u773c\u201d\u5b9a\u4e86\u5b9a\u795e\uff0c\u624d\u5feb\u6b65\u8d70\u4e86\u8fc7\u53bb\uff0c\u5531\u4e86\u4e2a\u80a5\u8bfa\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u9053\u7237\uff0c\u53ef\u66fe\u770b\u5230\u6211\u90a3 \u4e94\u4e2a\u5f1f\u5144\u8d70\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u8fd9\u4e24\u4f4d\u88c5\u675f\u4f3c\u9053\u975e\u9053\uff0c\u4f3c\u4fd7\u975e\u4fd7\u7684\u8001\u8005\u5bf9\u671b\u4e00\u773c\uff0c\u5404\u5404\u4e00\u7b11\uff0c\u6717\u58f0\u9053\uff1a\u201c\u4f60\u7684\u5144\u5f1f\u662f\u8c01 \u8c03\u201d\u725b\u4e09\u773c\u201d\u53c8\u81ea\u4e00\u6014\uff0c\u9053\uff1a\u201c\u6211\u90a3\u4e9b\u5f1f\u5144\u2026\u2026\u55ef\uff0c\u4e00\u4e2a\u9ad8\u9ad8\u7626\u7626\u7684\uff0c\u8eab\u4e0a\u7a7f\u7740\u7684\u662f\u8d70\u65b9\u90ce \u4e2d\u7684\u6253\u626e\uff0c\u8fd8\u63d0\u7740\u4e00\u4e2a\u836f\u7bb1\u5b50\uff0c\u5e26\u7740\u4e00\u4e32\u864e\u6491\uff0c\u53e6\u4e00\u4e2a\u6ee1\u8138\u80e1\u4e8e\u3001\u7684\uff0c\u7a7f\u7684\u662f\u9ed1\u5e03\u77ed\u6253\uff0c\u53e6 \u5916\u4e00\u4e2a\u80a5\u80a5\u80d6\u80d6\u7684\uff0c\u633a\u7740\u5927\u809a\u5b50\u2026\u2026\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u8001\u8005\u4e00\u9f50\u6447\u4e86\u6447\u5934\uff0c\u5176\u4e2d\u4e00\u4e2a\u8eab\u8eaf\u8f83\u9ad8\uff0c\u5750\u5728\u5730\u4e0a\u90fd\u6bd4\u53e6\u4e00\u4e2a\u9ad8\u7740\u534a\u4e2a\u5934\u7684\u67af\u7626 \u9053\u4eba\u7f13\u7f13\u7b11\u9053\uff1a\u201c\u65bd\u4e3b\u6240\u8bf4\u7684\u4eba\uff0c\u8d2b\u9053\u4e00\u4e2a\u4e5f\u672a\u66fe\u770b\u89c1\uff01\u201d \u3000\u3000\u53e6\u4e00\u4e2a\u8001\u8005\u7b11\u9053\uff1a\u2018\u8d2b\u9053\u6e05\u6668\u5373\u6765\u6b64\u5730\uff0c\u6b64\u5730\u6839\u672c\u8fde\u534a\u6761\u4eba\u5f71\u90fd\u6ca1\u6709\uff0c\u65bd\u4e3b\u6240\u8bf4\u7684\u4eba\uff0c \u53ea\u6015\u65e9\u5df2\u8d70\u4e86\u5427\uff1f\u201d\u201c\u725b\u4e09\u773c\u201d\u4e24\u773c\u4e00\u77aa\uff0c\u7a81\u5730\u559d\u9053\uff1a\u201c\u771f\u7684\u5417\uff1f\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u8001\u4eba\u5374\u53ea\u662f\u5fae\u5fae\u4e00\u7b11\uff0c\u518d\u4e5f\u4e0d\u671b\u4ed6\u4e00\u773c\uff0c\u4e00\u4eba\u4ece\u5730\u4e0a\u53d6\u51fa\u4e00\u53cc\u957f\u8fbe\u6709\u5c3a\u7684\u7b77\u5b50\uff0c \u7f13\u7f13\u5728\u9505\u91cc\u6405\u52a8\u7740\u3002 \u3000\u3000\u90a3\u201c\u725b\u4e09\u773c\u201d\u773c\u775b\u53c8\u4e00\u77aa\uff0c\u65b9\u60f3\u518d\u5406\u559d\u4e24\u53e5\uff0c\u54ea\u77e5\u80a9\u5934\u7a81\u5730\u4e00\u7d27\uff0c\u786c\u751f\u751f\u88ab\u62d6\u5f00\u4e09\u6b65\uff0c \u56de\u5934\u4e00\u671b\uff0c\u5374\u89c1\u4ec7\u6055\u76ee\u5149\u4e4b\u4e2d\uff0c\u6000\u7591\u4e4b\u8272\uff0c\u751f\u50cf\u662f\u89c1\u7740\u4e86\u4e00\u4e9b\u4ee4\u4ed6\u6781\u4e3a\u60ca\u5f02\u7684\u4e1c\u897f\u3002 \u3000\u3000\u4ed6\u4e00\u5165\u6b64\u95f4\uff0c\u4fbf\u770b\u51fa\u8fd9\u4e24\u4e2a\u8001\u8005\u5fc5\u975e\u5e38\u4eba\uff0c\u201c\u725b\u4e09\u773c\u201d\u5728\u90a3\u91cc\u559d\u95ee\uff0c\u4ed6\u5374\u8fdc\u8fdc\u7ad9\u5728\u4e00 \u8fb9\uff0c\u51dd\u76ee\u800c\u671b\uff0c\u53ea\u89c1\u8fd9\u4e24\u4e2a\u8001\u8005\uff0c\u8863\u88f3\u867d\u8934\u697c\uff0c\u624b\u638c\u5374\u83b9\u767d\u5982\u7389\uff0c\u90a3\u8eab\u6750\u8f83\u9ad8\u7684\u4e00\u4e2a\uff0c\u624b\u4e0a \u7559\u7740\u6307\u7532\uff0c\u7adf\u957f\u8fbe\u4e24\u5bf8\uff0c\u9876\u7aef\u5fae\u5fae\u5377\u8d77\u4e00\u4e9b\uff0c\u4ed6\u5fc3\u4e2d\u4fbf\u4e0d\u7981\u4e00\u52a8\u3002 \u3000\u3000\u7b49\u5230\u53e6\u4e00\u4e2a\u8001\u8005\u53d6\u8d77\u7b77\u5b50\uff0c\u6405\u52a8\u72d7\u6c64\u4e4b\u9645\uff0c\u4ed6\u66f4\u53d1\u73b0\u4e00\u6837\u5947\u4e8b\u3002 \u3000\u3000\u539f\u6765\u8fd9\u8001\u8005\u8eab\u8eaf\u672c\u77ee\uff0c\u90a3\u6c64\u9505\u5374\u540a\u5f97\u6781\u9ad8\uff0c\u6309\u7406\u8bf4\u4ed6\u4f38\u624b\u4e4b\u5904\uff0c\u672c\u5e94\u591f\u4e0d\u7740\u90a3\u53ea\u94c1\u9505\uff0c \u4f46\u4ed6\u4f38\u624b\u4e4b\u95f4\uff0c\u5168\u8eab\u672a\u52a8\uff0c\u624b\u81c2\u5374\u50cf\u662f\u957f\u4e86\u51e0\u5bf8\uff0c\u4ec7\u6055\u5fc3\u4e2d\u66f4\u662f\u5927\u5947\uff1a\u201c\u6b64\u5730\u7109\u6709\u6b64\u5185\u5bb6\u9ad8 \u624b\uff1f\u201d \u3000\u3000\u6b64\u523b\u5df1\u5c06\u5165\u590f\uff0c\u90a3\u201c\u725b\u4e09\u773c\u201d\u7ad9\u5728\u90a3\u5806\u67f4\u706b\u4e4b\u65c1\uff0c\u53ea\u662f\u7247\u523b\uff0c\u4fbf\u5df1\u6ccc\u51fa\u6c57\u73e0\u6765\uff0c\u4f46\u8fd9\u4e24 \u4e2a\u8001\u8005\u795e\u6001\u4e4b\u95f4\uff0c\u5374\u5b89\u7965\u5df2\u6781\uff0c\u534a\u70b9\u4e5f\u6ca1\u6709\u70ed\u610f\uff0c\u8fd9\u53c8\u662f\u4e00\u4ef6\u5185\u5bb6\u9ad8\u624b\u6240\u7279\u5177\u7684\u5f02\u5e38\u4e4b\u5904\uff0c \u4ec7\u6055\u8eab\u53d7\u5f53\u4ee3\u9876\u5c16\u51e0\u4f4d\u5f02\u4eba\u7684\u8c03\u6559\uff0c\u81ea\u662f\u8bc6\u8d27\u5df2\u6781\uff0c\u4e00\u89c1\u90a3\u201c\u725b\u4e09\u773c\u201d\u53c8\u8981\u77aa\u773c\u53d1\u5a01\uff0c\u4fbf\u62a2 \u6b65\u8d70\u4e86\u8fc7\u53bb\uff0c\u5c06\u4ed6\u62c9\u4e86\u8fc7\u6765\uff0c\u90a3\u201c\u725b\u4e09\u773c\u201d\u6df7\u6df7\u9968\u9968\uff0c\u5374\u8fd8\u4e0d\u77e5\u9053\u600e\u4e48\u56de\u4e8b\u54e9\u3002 \u3000\u3000\u201c\u6ce2\u201d\u7684\u4e00\u58f0\uff0c\u706b\u5806\u4e4b\u4e2d\uff0c\u7206\u51fa\u4e00\u56e2\u706b\u82b1\uff0c\u90a3\u8001\u8005\u624b\u8155\u4e00\u7ffb\uff0c\u7b77\u5b50\u4e00\u5939\uff0c\u4fbf\u5de7\u5999\u5730\u5c06\u90a3 \u56e2\u7535\u5c04\u800c\u51fa\u7684\u706b\u82b1\u631f\u4f4f\u4e86\uff0c\u968f\u624b\u629b\u5728\u5730\u4e0a\uff0c\u53c8\u4f38\u7b77\u5165\u9505\uff0c\u6405\u52b2\u4e24\u4e0b\uff0c\u631f\u4e86\u4e00\u5757\u7ea2\u55b7\u55b7\u7684\u9999\u8089 \u51fa\u6765\uff0c\u4e00\u9762\u9053\uff1a\u2018\u8fd9\u8089\u50cf\u662f\u5df2\u7ecf\u719f\u4e86\u3002\u201d\u4e00\u9762\u653e\u4eba\u5634\u91cc\uff0c\u7ec6\u7ec6\u5480\u56bc\u8d77\u6765\u3002\u4ec7\u6055\u5fae\u5fae\u4e00\u7b11\uff0c\u5c06 \u201c\u725b\u4e09\u773c\u201d\u62d6\u5230\u4e00\u8fb9\uff0c\u81ea\u5df1\u5374\u8d70\u4e86\u8fc7\u53bb\uff0c\u8eac\u8eab\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u8001\u4e08\u8bf7\u4e86\u3002\u201d \u3000\u3000\u90a3\u4f4d\u4e2a\u8001\u8005\u9f50\u5730\u4fa7\u987e\u4e00\u773c\uff0c\u9053\uff1a\u201c\u65bd\u4e3b\u8bf7\u4e86\u3002\u201d\u76ee\u5149\u4e0a\u4e0b\u5728\u4ed6\u8eab\u4e0a\u4e00\u8f6c\uff0c\u53c8\u81ea\u7b11\u9053\uff1a \u201c\u53ef\u8981\u5c1d\u4e9b\u9999\u8089\u8c03\u4ec7\u6055\u76ee\u5149\u4e00\u8f6c\uff0c\u4e00\u64a9\u886b\u811a\uff0c\u5e2d\u5730\u5750\u4e86\u4e0b\u6765\uff0c\u7b11\u9053\uff1a\u201c\u56fa\u6240\u613f\u4e5f\uff0c\u4e0d\u6562\u8bf7 \u8033\u3002\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u8001\u8005\u9f50\u5730\u4e00\u7b11\uff0c\u4e00\u4eba\u5c06\u624b\u4e2d\u7684\u957f\u7b77\uff0c\u7f13\u7f13\u4f38\u4e86\u8fc7\u6765\uff0c\u4ec7\u6055\u968f\u624b\u63a5\u8fc7\uff0c\u7adf\u7136\u5c31\u8001\u5b9e \u4e0d\u5ba2\u6c14\u5730\u5927\u5403\u5927\u559d\u8d77\u6765\u3002 \u3000\u3000\u201c\u725b\u4e09\u773c\u201d\u773c\u775b\u77a7\u5f97\u53d1\u76f4\uff0c\u5374\u542c\u90a3\u7626\u957f\u8001\u8005\u53c8\u81ea\u7b11\u9053\uff1a\u201c\u90a3\u4f4d\u65bd\u4e3b\u53ef\u8981\u4e00\u5e76\u8fc7\u6765\uff0c\u968f\u610f \u5403\u559d\u4e9b\u3002\u201d\u76ee\u5149\u5148\u8f6c\u5411\u4ec7\u6055\uff0c\u53c8\u81ea\u51dd\u76ee\u534a\u664c\uff0c\u5fae\u559f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8d2b\u9053\u4e00\u522b\u6c5f\u5357\uff0c\u5341\u6709\u4f59\u5e74\uff0c \u60f3\u4e0d\u5230\u6c5f\u5357\u4eba\u7269\uff0c\u8d8a\u53d1\u7075\u79c0\u4e86\uff0c\u771f\u662f\u53ef\u559c\u3002\u201d \u3000\u3000\u90a3\u201c\u725b\u4e09\u773c\u201d\u5374\u5728\u65c1\u5495\u54e6\u7740\u3002 \u3000\u3000\u201c\u8fd9\u6279\u72d7\u624d\u8dd1\u5230\u54ea\u91cc\u53bb\u4e86\uff0c\u771f\u662f\u6c14\u4eba\uff01\u201d\u5927\u6b65\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u90a3\u67af\u7626\u8001\u4eba\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u65bd\u4e3b\u7684\u8fd9\u4f4d\u4f34\u5f53\uff0c\u5012\u662f\u4e2a\u70ed\u80a0\u7537\u5b50\u2014\u2014\u201d\u8bed\u58f0\u5fae\u987f\uff0c\u7a81\u5730\u957f \u53f9\u4e00\u58f0\uff1a\u201c\u53ea\u662f\u4e16\u9014\u597d\u9669\uff0c\u4eba\u5fc3\u96be\u6d4b\uff0c\u4e3a\u4eba\u4e5f\u4e0d\u8981\u592a\u8fc7\u70ed\u80a0\u4e86\uff0c\u5426\u5219\u5403\u4e8f\u7684\u5374\u662f\u81ea\u5df1\u3002\u201d\u76ee \u5149\u4e00\u5782\uff0c\u51dd\u89c6\u7740\u718a\u718a\u7089\u706b\uff0c\u7adf\u50cf\u662f\u843d\u5165\u6c89\u601d\u91cc\uff0c\u53ea\u662f\u4e0d\u77e5\u4ed6\u5728\u60f3\u7740\u4ec0\u4e48\u800c\u5df2\u3002 \u3000\u3000\u4ec7\u6055\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u6b66\u529f\u6781\u9ad8\uff0c\u6c14\u5ea6\u53c8\u9887\u4e0d\u51e1\uff0c\u5fc5\u5b9a\u662f\u5927\u6709\u6765\u5386\u4e4b\u4eba\uff0c\u4f46\u6b64 \u523b\u6df7\u8ff9\u98ce\u5c18\uff0c\u50cf\u662f\u5728\u9003\u907f\u4ec0\u4e48\uff1f\u5374\u53c8\u662f\u4e3a\u4f55\u5462\uff1f\u201d \u3000\u3000\u9505\u4e2d\u8089\u6c64\uff0c\u8d8a\u716e\u8d8a\u6cb8\uff0c\u8d8a\u6cb8\u8d8a\u9999\uff0c\u90a3\u8eab\u6750\u8f83\u9ad8\u8001\u4eba\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5f80\u4e8b\u5df2\u77e3\uff0c\u601d\u4e4b\u5f92 \u4f24\u4eba\u610f\uff0c\u4f60\u53c8\u4f55\u82e6\u5b66\u90a3\u5987\u4eba\u5973\u5b50\uff0c\u8001\u662f\u53bb\u60f3\u90a3\u4e9b\u5316\u89e3\u4e0d\u5f00\u4e4b\u4e8b\uff0c\u8fd9\u5341\u4f59\u5e74\u6765\uff0c\u4f60\u5386\u904d\u5c71\u5ddd\uff0c \u96be\u9053\u90a3\u957f\u767d\u79ef\u96ea\u3001\u9ed1\u9f99\u7384\u51b0\u3001\u585e\u5317\u9ec4\u7802\u3001\u6cb3\u897f\u79ef\u7fe0\uff0c\u8fd8\u672a\u66fe\u5c06\u4f60\u7684\u5fc3\u80f8\u9676\u51b6\u5f97\u5f00\uff0c\u6765\u3001\u6765\u3001 \u6765\uff0c\u5bf9\u9152\u5f53\u6b4c\uff0c\u4eba\u751f\u51e0\u4f55\uff0c\u4e14\u996e\u4e00\u53e3\u3002\u201d \u3000\u3000\u53e6\u4e00\u8001\u4eba\u4ea6\u81ea\u54c8\u54c8\u4e00\u7b11\uff0c\u4ee5\u7b77\u51fb\u9505\uff0c\u9ad8\u6b4c\u9053\uff1a\u201c\u5bf9\u9152\u5f53\u6b4c\uff0c\u4eba\u751f\u51e0\u4f55\uff0c\u8b6c\u5982\u671d\u9732\uff0c\u53bb\u65e5 \u82e6\u591a\uff0c\u6168\u5f53\u4ee5\u6177\uff0c\u5fe7\u601d\u96be\u5fd8\uff0c\u4f55\u4ee5\u89e3\u4f18\uff0c\u552f\u6709\u675c\u5eb7\u2026\u2026\u5509\uff0c\u4f18\u601d\u96be\u5fd8\uff0c\u552f\u6709\u675c\u5eb7\uff0c\u5374\u53c8\u600e\u80fd \u89e3\u53bb\u6211\u5fc3\u5934\u4e4b\u6068\u5462\uff1f\u201d\u968f\u624b\u4e00\u63b7\uff0c\u624b\u4e2d\u7684\u957f\u7b77\uff0c\u7535\u5c04\u800c\u51fa\uff0c\u201c\u593a\u201d\u5730\u4e00\u58f0\uff0c\u6ca1\u5165\u5899\u5185\uff0c\u6643\u773c \u4fbf\u6ca1\u4e86\u8e2a\u5f71\u3002 \u3000\u3000\u9505\u4e2d\u7684\u8089\u6c64\uff0c\u716e\u5f97\u66f4\u9999\u4e86\uff0c\u4e00\u9635\u98ce\u5439\u6765\uff0c\u5439\u5f97\u706b\u7130\u659c\u659c\u5730\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4ec7\u6055\u6697\u53f9\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u72c2\u6b4c\u5f53\u54ed\uff0c\u58ee\u58eb\u672b\u8def\uff0c\u8fd9\u4e24\u4eba\u770b\u6765\u5149\u660e\u78ca\u843d\uff0c\u5374\u4e0d\u77e5\u5fc3\u4e2d\u6709\u4ec0 \u4e48\u6068\u4e8b\u2026\u2026\u201d \u3000\u3000\u5ff5\u5934\u72b9\u672a\u8f6c\u5b8c\uff0c\u5927\u5802\u4e4b\u4e0b\uff0c\u7a81\u5730\u4f20\u6765\u4e00\u58f0\u60ca\u547c\uff0c\u90a3\u201c\u725b\u4e09\u773c\u201d\u98de\u4e5f\u4f3c\u7684\u5954\u4e86\u8fdb\u6765\uff0c\u9762\u4e0a \u4e00\u7247\u60ca\u60f6\u4e4b\u8272\uff0c\u6025\u58f0\u9053\uff1a\u201c\u516c\u5b50\uff0c\u516c\u5b50\u2026\u2026\u4f60\u53bb\u770b\u770b\uff0c\u6211\u90a3\u4e9b\u5144\u5f1f\uff0c\u5df2\u906d\u4e86\u4eba\u5bb6\u6bd2\u624b\u4e86\u3002\u201d \u3000\u3000\u4ec7\u6055\u84e6\u5730\u4e00\u60ca\uff0c\u957f\u8eab\u800c\u8d77\uff0c\u5411\u90a3\u4e24\u4e2a\u8001\u4eba\u62b1\u62f3\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u5931\u966a\u3002\u201d\u5927\u6b65\u548c\u90a3\u201c\u725b\u4e09 \u773c\u201d\u8d70\u51fa\u5385\u5916\uff0c\u53ea\u542c\u725b\u4e09\u773c\u53c8\u9053\uff1a\u201c\u516c\u5b50\uff0c\u6211\u770b\u90a3\u4e24\u4e2a\u8001\u9053\u4e0d\u662f\u597d\u4eba\uff0c\u8fd9\u4e8b\u6050\u6015\u5c31\u662f\u4ed6\u4eec\u505a \u7684\u624b\u811a\u3002\u201d\u4ec7\u6055\u8f7b\u8f7b\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u968f\u7740\u4ed6\u6cbf\u7740\u571f\u5899\u8d70\u4e86\u534a\u664c\uff0c\u53ea\u89c1\u7960\u5802\u540e\u9762\uff0c\u662f\u4e2a\u8352\u8d25\u7684 \u9662\u843d\uff0c\u6742\u8349\u4e1b\u751f\uff0c\u7816\u77f3\u6ee1\u5730\uff0c\u201c\u725b\u4e09\u773c\u201d\u4e00\u4e2a\u7bad\u6b65\u7a9c\u4e86\u8fc7\u53bb\uff0c\u6307\u7740\u4e00\u4e1b\u8352\u8349\u9053\u3002 \u3000\u3000\u201c\u516c\u5b50\uff0c\u4f60\u770b\u770b\uff0c\u4ed6\u4eec\u8fd9\u662f\u600e\u4e48\u4e86\uff1f\u201d\u53cc\u624b\u4e00\u6293\uff0c\u4ece\u8352\u8349\u4e2d\u62b1\u51fa\u4e00\u4e2a\u8eab\u7a7f\u77ed\u886b\u7684\u866c\u987b\u5927 \u6c49\u6765\u3002 \u3000\u3000\u4ec7\u6055\u5927\u6b65\u884c\u524d\uff0c\u5b9a\u773c\u800c\u671b\uff0c\u53ea\u89c1\u5927\u6c49\u5168\u8eab\u8840\u8ff9\u6dcb\u6dcb\uff0c\u8111\u888b\u4e24\u4fa7\uff0c\u7adf\u5149\u79c3\u79c3\u5730\u5c11\u4e86\u53cc\u8033\uff0c \u5168\u8eab\u50f5\u76f4\uff0c\u50cf\u662f\u5df2\u6ca1\u4e86\u6c14\u606f\u3002 \u3000\u3000\u90a3\u201c\u725b\u4e09\u773c\u201d\u53cc\u76ee\u5c3d\u8d64\uff0c\u53c8\u4ece\u56db\u4fa7\u7684\u8352\u8349\u5806\u91cc\uff0c\u62b1\u51fa\u56db\u6761\u6c49\u5b50\u6765\uff0c\u7adf\u7136\u4e00\u4e2a\u4e2a\u90fd\u662f\u5168\u8eab \u50f5\u76f4\uff0c\u8840\u8ff9\u6dcb\u6dcb\uff0c\u5c11\u4e86\u53cc\u8033\u3002 \u3000\u3000\u4ec7\u6055\u5251\u7709\u4e00\u8f69\uff0c\u4fef\u8eab\u4e00\u63a2\uff0c\u5374\u89c1\u8fd9\u4e9b\u4eba\u9f3b\u606f\u4ecd\u81ea\u672a\u65ad\uff0c\u7565\u4e00\u68c0\u89c6\uff0c\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e0d \u59a8\u4e8b\uff0c\u4ed6\u4eec\u5e76\u672a\u4e27\u547d\uff0c\u53ea\u4e0d\u8fc7\u88ab\u4e2a\u5185\u5bb6\u9ad8\u624b\u70b9\u4e2d\u7a74\u9053\u800c\u5df2\u3002\u201d\u75be\u4f38\u53cc\u638c\uff0c\u5728\u8fd9\u4e94\u6761\u5927\u6c49\u8eab \u4e0a\uff0c\u7535\u4e5f\u4f3c\u5730\u5404\u5404\u62cd\u4e86\u4e09\u638c\u3002\u8fd9\u4e9b\u6c49\u5b50\u957f\u957f\u5410\u4e86\u53e3\u6c14\uff0c\u7adf\u90fd\u5931\u58f0\u547b\u541f\u4e86\u8d77\u6765\u3002 \u3000\u3000\u201c\u725b\u4e09\u773c\u201d\u6068\u58f0\u9053\uff1a\u201c\u8fd9\u4e00\u5b9a\u53c8\u662f\u59d3\u6bdb\u7684\u624b\u5e95\u4e0b\u90a3\u73ed\u5b59\u5b50\u4eec\u4e8e\u7684\u4e8b\uff0c\u54fc\uff01\u6709\u671d\u4e00\u65e5\uff0c\u90a3 \u59d3\u6bdb\u7684\u82e5\u72af\u5728\u6211\u201d\u725b\u4e09\u773c\u201d\u624b\u91cc\uff0c\u6211\u4e0d\u5c06\u4ed6\u788e\u5c38\u4e07\u6bb5\u624d\u602a\u3002\u201d \u3000\u3000\u4ec7\u6055\u8f69\u7709\u6c89\u58f0\u9053\uff1a\u201c\u4f60\u7684\u5f1f\u5144\u4e3a\u6211\u529e\u4e8b\uff0c\u53ef\u6709\u4eba\u77e5\u9053\uff1f\u201d\u725b\u4e09\u773c\u8fde\u5fd9\u6447\u624b\u9053\uff1a\u201c\u516c\u5b50\uff0c \u6211\u2018\u725b\u4e09\u773c\u2019\u662f\u5e72\u4ec0\u4e48\u7684\uff0c\u8fd9\u79cd\u4e8b\u5c31\u662f\u6253\u6b7b\u6211\uff0c\u6211\u4e5f\u4e0d\u4f1a\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u4ec7\u6055\u5fae\u4e00\u76b1\u7709\uff0c\u6c89\u541f\u9053\uff1a\u201c\u8fd9\u5374\u602a\u4e86\u2026\u2026\u8fd9\u96be\u9053\u662f\u4ed6\u4eec\u6614\u65e5\u7684\u4ec7\u5bb6\u6240\u5e72\u7684\u4e8b\u5417\uff1f\u4f46 \u662f\u2026\u2026\u4ed6\u4eec\u7684\u4ec7\u5bb6\u53c8\u600e\u4f1a\u8fd9\u79cd\u4e0a\u4e58\u7684\u70b9\u7a74\u624b\u6cd5\u5462\uff1f\u201d \u3000\u3000\u201c\u725b\u4e09\u773c\u201d\u4ea6\u81ea\u6df1\u76b1\u7740\u6d53\u7709\uff0c\u5374\u89c1\u90a3\u4e94\u4e2a\u6c49\u5b50\u547b\u541f\u534a\u664c\uff0c\u6323\u624e\u7740\u722c\u4e86\u8d77\u6765\uff0c\u4e00\u773c\u770b\u5230 \u4ed6\uff0c\u5374\u5931\u58f0\u53eb\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u4e09\u54e5\uff0c\u4f60\u73b0\u5728\u624d\u6765\u5440\uff1f\u2026\u2026\u5509\uff0c\u6211\u4eec\u88ab\u6cbb\u5f97\u597d\u60e8\u5440\uff01\u201d \u3000\u3000\u201c\u725b\u4e09\u773c\u201d\u8dfa\u811a\u9053\uff1a\u201c\u8fd9\u5230\u5e95\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u662f\u8c01\u6cbb\u7684\u4f60\u4eec\uff0c\u5feb\u8bf4\u51fa\u6765\u5440\uff01\uff0c\u2019\u53c8\u9053\u3002\u201d \u8fd9\u4f4d\u5c31\u662f\u516c\u5b50\u7237\uff0c\u4f60\u5feb\u8bf4\u51fa\u6765\uff0c\u8ba9\u516c\u5b50\u7237\u7ed9\u4f60\u51fa\u6c14\u3002\u201d \u3000\u3000\u90a3\u4e94\u4e2a\u5927\u6c49\uff0c\u201c\u5657\u201d\u5730\u4e00\u58f0\uff0c\u9f50\u5730\u8dea\u5230\u4ec7\u6055\u9762\u524d\uff0c\u4ec7\u6055\u76ee\u5149\u4e00\u8f6c\uff0c\u548c\u58f0\u9053\uff1a\u201c\u5148\u6b47\u606f\u4e00 \u4f1a\u518d\u8bf4\u4e5f\u4e0d\u8981\u7d27\uff0c\u725b\u8001\u4e09\uff0c\u4f60\u5feb\u51fa\u53bb\u5f04\u4e9b\u91d1\u521b\u836f\u6765\u2026\u2026\u201d\u90a3\u7a7f\u7740\u4f3c\u8d70\u65b9\u90ce\u4e2d\u7684\u7626\u957f\u6c49\u5b50\u9053\uff1a \u201c\u91d1\u521b\u836f\u5c0f\u7684\u7bb1\u5b50\u91cc\u5c31\u6709\uff0c\u4e0d\u52b3\u516c\u5b50\u8d39\u5fc3\uff0c\u53ea\u662f\uff0c\u2026\u2026\u53ea\u662f\u5c0f\u7684\u4eec\u8fd9\u6b21\u4e0d\u660e\u4e0d\u767d\u5730\u88ab\u4eba\u5bb6\u524a \u53bb\u53cc\u8033\uff0c\u5374\u5b9e\u5728\u2026\u2026\u5b9e\u5728\u6c14\u4eba\u3002\u201d \u3000\u3000\u201c\u725b\u4e09\u773c\u201d\u53c8\u81ea\u8dfa\u811a\u9053\uff1a\u201c\u5149\u8bf4\u6c14\u4eba\u5e72\u4ec0\u4e48\uff1f\u662f\u8c01\u628a\u4f60\u4eec\u6574\u5f97\u8fd9\u4e48\u60e8\u7684\uff0c\u4f60\u4eec\u5012\u662f\u8bf4\u51fa \u6765\u5440\u3002\u201d \u3000\u3000\u90a3\u7626\u957f\u6c49\u5b50\u9053\uff1a\u201c\u90a3\u4eba\u662f\u8c01\uff0c\u6211\u4eec\u4e5f\u4e0d\u8ba4\u5f97\uff0c\u6628\u5929\u665a\u4e0a\uff0c\u502a\u8001\u4e03\u4e70\u4e86\u4e94\u65a4\u5364\u8089\uff0c\u53c8\u5f04\u6765 \u4e09\u65a4\u9ad8\u6881\uff0c\u6211\u4eec\u6b63\u5728\u5385\u91cc\u5403\u559d\u7740\u2026\u2026\u201d \u3000\u3000\u201c\u725b\u4e09\u773c\u201d\u63a5\u53e3\u9053\uff1a\u201c\u90a3\u4eba\u5c31\u8dd1\u6765\u628a\u4f60\u4eec\u6cbb\u5012\u4e86\u662f\u4e0d\u662f\u8c03\u90a3\u7626\u957f\u6c49\u5b50\u70b9\u4e86\u70b9\u5934\uff0c\u968f\u53c8\u6447 \u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u672c\u6765\u8fd8\u6ca1\u6709\uff0c\u540e\u6765\u2026\u2026\u540e\u6765\u502a\u8001\u4e03\u8bf4\u2026\u2026\u201d\u201c\u725b\u4e09\u773c\u201d\u5389\u58f0\u9053\uff1a\u201c\u8bf4\u4ec0 \u4e48\uff1f\u201d\u90a3\u7626\u957f\u6c49\u5b50\u773c\u89d2\u4e00\u779f\u53e6\u4e00\u67af\u7626\u6c49\u5b50\uff0c\u63a5\u9053\uff1a\u201c\u502a\u8001\u4e03\u5927\u7ea6\u662f\u559d\u4e86\u9152\uff0c\u5c31\u8bf4\uff1a\u2018\u542c\u8bf4\u6211 \u4eec\u90a3\u516c\u5b50\u5e74\u7eaa\u867d\u8f7b\uff0c\u53ef\u771f\u6709\u4e24\u624b\uff0c\u628a\u90a3\u7075\u86c7\u6bdb\u81ec\u7684\u5927\u5973\u513f\u5374\u2026\u2026\u5f04\u5230\u624b\u3002\uff0c\u6211\u5c31\u95ee\uff1a\u2018\u4f60\u600e \u4e48\u77e5\u9053\uff1f\uff0c\u502a\u8001\u4e03\u5c31\u8bf4\u2026\u2026\u5c31\u8bf4\u2026\u2026\u201d\u4ec7\u6055\u5251\u7709\u8f7b\u8f7b\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u8bf4\u4e0b\u53bb\u3002\u201d \u3000\u3000\u90a3\u7626\u957f\u6c49\u5b50\u5598\u4e86\u4e00\u53e3\u6c14\uff0c\u63a5\u9053\uff1a\u201c\u502a\u8001\u4e03\u5c31\u8bf4\u4ed6\u4eb2\u773c\u770b\u5230\u516c\u5b50\u548c\u90a3\u59d3\u6bdb\u7684\u5973\u513f\u8d70\u8fdb\u5ba2 \u6808\uff0c\u4f4f\u5728\u4e00\u95f4\u623f\u91cc\uff0c\u53c8\u8bf4\uff1a\u2018\u90a3\u59d3\u6bdb\u7684\u5e76\u4e14\u77e5\u9053\u516c\u5b50\u5e76\u4e0d\u662f\u771f\u7684\u559c\u6b22\u5979\uff0c\u800c\u662f\u6545\u610f\u2026\u2026\u2019\u4ed6 \u8bdd\u521a\u8bf4\u5230\u8fd9\u91cc\uff0c\u95e8\u5916\u7a81\u5730\u6709\u4eba\u51b7\u51b7\u5730\u4e00\u7b11\uff0c\u6211\u4eec\u5927\u5bb6\u90fd\u4f4f\u4e86\u53e3\uff0c\u4e00\u9f50\u56de\u5934\u53bb\u671b\uff0c\u53ea\u89c1\u95e8\u53e3\u7a81 \u7136\u591a\u4e86\u4e00\u4e2a\u7a7f\u7740\u767d\u888d\u5b50\u7684\u5973\u5b50\uff0c\u5934\u53d1\u957f\u957f\u7684\uff0c\u62ab\u5230\u80a9\u4e0a\uff0c\u7ad9\u5728\u54ea\u91cc\u52a8\u4e5f\u4e0d\u52a8\uff0c\u5728\u6708\u5149\u4e0b\u9762\u671b \u53bb\uff0c\u8fde\u534a\u70b9\u4eba\u5473\u90fd\u6ca1\u6709\u3002\u201d \u3000\u3000\u4ec7\u6055\u9762\u8272\u4e00\u53d8\uff0c\u53ea\u542c\u4ed6\u63a5\u7740\u53c8\u9053\uff1a\u201c\u6211\u4eec\u5927\u5bb6\u4e0d\u7981\u90fd\u5012\u62bd\u4e86\u4e00\u53e3\u51c9\u6c14\uff0c\u53ea\u89c1\u5979\u4e00\u6b65\u4e00\u6b65 \u5730\u8d70\u4e86\u8fc7\u6765\uff0c\u8d70\u5230\u8ddf\u524d\uff0c\u6211\u4eec\u624d\u770b\u51fa\u5979\u9762\u4e0a\u7adf\u662f\u4e00\u7247\u7126\u9ec4\uff0c\u53c8\u6728\u53c8\u50f5\uff0c\u4e00\u65e0\u8868\u60c5\uff0c\u54ea\u91cc\u662f\u4e2a \u6d3b\u4eba\uff0c\u7b80\u76f4\u5c31\u50cf\u4e2a\u50f5\u5c38\uff0c\u6211\u4eec\u673a\u4f36\u4f36\u6253\u4e86\u4e2a\u51b7\u6218\uff0c\u4e24\u6761\u817f\u90fd\u53d1\u8f6f\u4e86\uff0c\u8fde\u9003\u8d70\u7684\u52c7\u6c14\u90fd\u6ca1\u6709 \u4e86\u3002\u201d \u3000\u3000\u4ec7\u6055\u6697\u201c\u54fc\u201d\u4e00\u58f0\uff0c\u53ea\u89c1\u8fd9\u4e94\u4e2a\u6c49\u5b50\uff0c\u76ee\u5149\u4e4b\u4e2d\uff0c\u5404\u5404\u6ee1\u542b\u60ca\u6050\u4e4b\u8272\uff0c\u50cf\u662f\u4ecd\u5728\u88ab\u6628\u591c \u4e4b\u4e8b\u60ca\u60b8\u7740\u3002 \u3000\u3000\u90a3\u7626\u5b50\u5598\u4e86\u53e3\u6c14\uff0c\u53c8\u9053\uff1a\u201c\u5c0f\u7684\u4e00\u751f\u4e4b\u4e2d\uff0c\u4ece\u6765\u4e5f\u6ca1\u6709\u770b\u5230\u6bd4\u90a3\u4eba\u518d\u96be\u770b\u7684\u9762\u5b54\uff0c\u5f53 \u65f6\u2026\u2026\u201d\u54ea\u77e5\u4ed6\u8bdd\u72b9\u672a\u4e86\uff0c\u4ec7\u6055\u8eab\u540e\uff0c\u7a81\u53c8\u4f20\u6765\u4e00\u9635\u6c89\u91cd\u7684\u811a\u6b65\u58f0\uff0c\u4e00\u6b65\u4e00\u6b65\u5730\uff0c\u5411\u4ed6\u4eec\u8d70 \u4e86\u8fc7\u6765\uff0c\u6b64\u523b\u867d\u662f\u767d\u5929\uff0c\u4ec7\u6055\u80cc\u810a\u4e4b\u4e0a\uff0c\u4e5f\u4e0d\u7981\u6cdb\u51fa\u4e00\u9635\u5bd2\u610f\u3002 \u7b2c\u5341\u4e94\u7ae0>> \u53e4\u9f99\u300a\u6e58\u5983\u5251\u300b \u7b2c\u5341\u4e94\u7ae0 \u3000\u3000\u4ec7\u6055\u6781\u5176\u6e05\u695a\u5730\u611f\u89c9\u5230\uff0c\u8fd9\u6c89\u91cd\u7684\u811a\u6b65\u58f0\uff0c\u8ddd\u79bb\u81ea\u5df1\u5df2\u8d8a\u6765\u8d8a\u8fd1\uff0c\u4f46\u662f\u4ed6\u5374\u4ecd\u7136\u50cf\u4e00\u5ea7 \u5c71\u5ca9\u822c\u5c79\u7acb\u7740\uff0c\u8fde\u52a8\u5f39\u90fd\u6ca1\u6709\u52a8\u7985\u4e00\u4e0b\uff0c\u56e0\u4e3a\u4ed6\u786e\u5207\u5730\u77e5\u9053\uff0c\u4e00\u4e2a\u4eba\u5e94\u4ed8\u4efb\u4f55\u4e00\u79cd\u53d8\u5316\u7684\u53d1 \u751f\uff0c\u6700\u597d\u7684\u65b9\u6cd5\uff0c\u5c31\u662f\u4fdd\u6301\u9547\u9759\uff0c\u8270\u82e6\u7684\u953b\u7ec3\u4e0e\u590d\u4ec7\u7684\u610f\u5fd7\uff0c\u65e0\u6bd4\u575a\u5f3a\u7684\u590d\u4ec7\u7684\u610f\u5fd7\uff0c\u4f7f\u5f97 \u4ed6\u6bcf\u4e00\u6839\u795e\u7ecf\uff0c\u90fd\u50cf\u662f\u94a2\u94c1\u4e00\u6837\uff0c\u82e5\u6ca1\u6709\u8db3\u591f\u731b\u70c8\u7684\u6253\u51fb\uff0c\u4f11\u60f3\u4f7f\u5f97\u4ed6\u94a2\u94c1\u822c\u7684\u795e\u7ecf\u9707\u8361\u4e00 \u4e0b\u3002 \u3000\u3000\u800c\u6b64\u523b\uff0c\u8fd9\u7a81\u7136\u4f20\u6765\u7684\u811a\u6b65\u58f0\uff0c\u5bf9\u4ed6\u7684\u6253\u51fb\uff0c\u663e\u7136\u662f\u4e0d\u591f\u731b\u70c8\u7684\uff0c\u8d77\u5148\uff0c\u4ed6\u867d\u4e5f\u4f1a\u611f\u5230 \u4e00\u9635\u609a\u61d4\u7684\u5bd2\u610f\u3002 \u3000\u3000\u4f46\u662f\uff0c\u8fd9\u9635\u609a\u61d4\u7684\u5bd2\u610f\uff0c\u6781\u5feb\u5730\u4fbf\u6d88\u5931\u4e86\uff0c\u5feb\u5f97\u8fde\u4ed6\u81ea\u5df1\u90fd\u4eff\u4f5b\u6ca1\u6709\u611f\u89c9\u5230\uff0c\u5f53\u4ed6\u62ac\u8d77 \u76ee\u5149\uff0c\u770b\u5230\u7ad9\u5728\u4ed6\u5bf9\u9762\uff0c\u6b63\u5728\u4e00\u9762\u5598\u6c14\uff0c\u4e00\u9762\u8bf4\u8bdd\u7684\u67af\u7626\u6c49\u5b50\uff0c\u867d\u56e0\u8fd9\u9635\u811a\u6b65\u800c\u4e2d\u6b62\u4e86\u81ea\u5df1 \u7684\u8bdd\uff0c\u4f46\u9762\u4e0a\u5374\u5e76\u6ca1\u6709\u4ec0\u4e48\u660e\u663e\u7684\u6050\u60e7\u4e4b\u8272\uff0c\u6709\u7684\u53ea\u662f\u4e00\u4e9b\u8f7b\u5fae\u7684\u60ca\u8bb6\uff0c\u56e0\u4e4b\uff0c\u4ed6\u77e5\u9053\u81ea\u5df1 \u8eab\u540e\u884c\u6765\u7684\u8fd9\u4eba\uff0c\u5e76\u4e0d\u8db3\u4ee5\u4ee4\u81ea\u5df1\u60ca\u614c\uff0c\u56e0\u4e3a\u5047\u5982\u4e00\u4e2a\u4eba\u5e76\u6ca1\u6709\u4ee4\u4e16\u4e0a\u5176\u4ed6\u4efb\u4f55\u4e00\u4e2a\u4eba\u6050\u60e7 \u7684\u8bdd\uff0c\u90a3\u4e48\u8fd9\u4e2a\u4eba\u4e5f\u5c31\u66f4\u4e0d\u4f1a\u4ee4\u4ec7\u6055\u60ca\u614c\u4e86\u3002 \u3000\u3000\u4f55\u51b5\uff0c\u8fd9\u4e2a\u4eba\u7684\u811a\u6b65\u58f0\u662f\u90a3\u4e48\u6c89\u91cd\uff0c\u6c89\u91cd\u5f97\u5373\u4f7f\u4e00\u4e2a\u767d\u75f4\u6216\u8005\u534a\u804b\u7684\u4eba\u4e5f\u80fd\u6e05\u695a\u5730\u542c\u5f97 \u5230\uff0c\u5f53\u4eba\u4eec\u8981\u60f3\u52a0\u5bb3\u53e6\u4e00\u4e2a\u4eba\u7684\u65f6\u5019\uff0c\u4ed6\u4eec\u901a\u5e38\u662f\u4e0d\u4f1a\u53d1\u51fa\u5982\u6b64\u6c89\u91cd\u7684\u811a\u6b65\u58f0\u7684\u3002 \u3000\u3000\u56e0\u4e4b\u5f53\u811a\u6b65\u58f0\u8d8a\u6765\u8d8a\u8fd1\u7684\u65f6\u5019\uff0c\u4ed6\u53ea\u662f\u7f13\u7f13\u5730\u56de\u8fc7\u5934\u53bb\uff0c\u6295\u4ee5\u5e73\u6de1\u7684\u4e00\u77a5\uff0c\u4ed6\u751a\u81f3\u5728\u56de \u8fc7\u5934\u53bb\u4e4b\u524d\uff0c\u5df2\u80fd\u81ea\u4fe1\u5730\u731c\u900f\uff1a\u201c\u4e00\u5b9a\u662f\u65b9\u624d\u5728\u5927\u6bbf\u4e2d\u90a3\u4e24\u4e2a\u5947\u5f02\u7684\u9053\u4eba\uff0c\u6b64\u523b\u5df2\u8d70\u4e86\u51fa \u6765\u3002\u201d \u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u54ea\u77e5\u2014\u2014 \u3000\u3000\u5f53\u90a3\u67af\u7626\u7684\u6c49\u5b50\u5598\u4e86\u4e00\u53e3\u6c14\u540e\uff0c\u8bf4\uff1a\u201c\u5c0f\u7684\u4e00\u751f\u4e4b\u4e2d\uff0c\u4ece\u6765\u4e5f\u6ca1\u6709\u770b\u5230\u6bd4\u90a3\u4eba\u518d\u96be\u770b\u7684 \u9762\u5b54\uff0c\u5f53\u65f6\u2014\u2014\u201d\u5c31\u5728\u4ed6\u8bf4\u5230\u201c\u5f53\u65f6\u201d\u4e24\u5b57\u7684\u65f6\u5019\uff0c\u4ed6\u500f\u7136\u4e2d\u6b62\u4e86\u81ea\u5df1\u7684\u8bdd\uff0c\u56e0\u4e3a\u6b64\u523b\u4ed6\u773c \u4e2d\uff0c\u53c8\u51fa\u73b0\u4e86\u4e00\u4e2a\u5413\u4eba\u7684\u666f\u8c61\u3002 \u3000\u3000\u4f46\u662f\uff0c\u4ed6\u9762\u4e0a\u4e3a\u4ec0\u4e48\u6ca1\u6709\u73b0\u51fa\u50cf\u4ed6\u5fc3\u91cc\u4e00\u6837\u6050\u60e7\u7684\u9762\u5bb9\u5462\uff1f \u3000\u3000\u56e0\u4e3a\u4ed6\u867d\u7136\u770b\u5230\u4e86\u8fd9\u666f\u8c61\uff0c\u5374\u4e0d\u4f1a\u771f\u7684\u4e86\u89e3\uff0c\u8fd9\u4e00\u6765\u662f\u56e0\u4e3a\u3002 \u3000\u3000\u4ed6\u5413\u574f\u4e86\uff0c\u5413\u5f97\u4e0d\u80fd\u4e86\u89e3\uff0c\u4f46\u6700\u4e3b\u8981\u7684\u5374\u662f\uff0c\u6b64\u523b\u4ed6\u5df2\u6839\u672c\u4e0d\u77e5\u9053\u4ec0\u4e48\u201c\u6050\u60e7\u201d\uff0c\u6050\u60e7 \u662f\u5c5e\u4e8e\u795e\u5fd7\u7684\uff0c\u800c\u4ed6\u7684\u795e\u5fd7\u5374\u5b8c\u5168\u505c\u6b62\u4e86\u4f5c\u7528\uff0c\u5df2\u5b8c\u5168\u5730\u9ebb\u6728\u4e86\uff01 \u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u4e8e\u662f\u4e00\u4e00 \u3000\u3000\u8fd9\u53ef\u6068\u7684\uff0c\u8be5\u5480\u5492\u7684\u9ebb\u6728\uff1b\u4fbf\u4f7f\u5f97\u4ec7\u6055\u53c8\u4e0b\u4e86\u4e2a\u9519\u8bef\u7684\u5224\u65ad\u3002 \u3000\u3000\u4ed6\u751a\u81f3\u6ca1\u6709\u53bb\u671b\u8dea\u5728\u5730\u4e0a\u7684\u53e6\u56db\u4e2a\u4eba\uff0c\u4ee5\u53ca\u7ad9\u5728\u4ed6\u8eab\u4fa7\u7684\u201c\u725b\u4e09\u773c\u201d\u4e00\u773c\uff0c\u4e5f\u6839\u672c\u6ca1\u6709 \u6ce8\u610f\u8fd9\u4e9b\u4eba\u9762\u4e0a\u7684\u8868\u60c5\u3002 \u3000\u3000\u53ef\u662f\uff0c\u5c31\u5728\u4ed6\u65b9\u81ea\u8f6c\u8fc7\u5934\u53bb\u7684\u65f6\u5019\uff0c\u4ed6\u5fae\u5e26\u7b11\u610f\u7684\u773c\u89d2\u8f7b\u8f7b\u4e00\u77a5\u3002 \u3000\u3000\u8fd9\u4e00\u5207\u4e8b\u90fd\u662f\u5728\u6781\u77ed\u6781\u77ed\u7684\u5239\u90a3\u4e4b\u95f4\u53d1\u751f\u7684\u2014\u2014\u4ece\u90a3\u67af\u7626\u6c49\u5b50\u7684\u4e2d\u6b62\u8bf4\u8bdd\uff0c\u76f4\u5230\u4ec7\u6055\u6b64 \u523b\u7684\u56de\u8f6c\u5934\u53bb\u3002 \u3000\u3000\u725b\u4e09\u773c\u9762\u4e0a\u7684\u808c\u8089\uff0c\u662f\u5728\u6050\u60e7\u800c\u7d27\u5f20\u7684\u626d\u66f2\u7740\uff0c\u82e5\u4e0d\u662f\u56e0\u4e3a\u4ec7\u6055\u7684\u9547\u9759\uff0c\u8fd9\u6ee1\u8154\u4e49\u6c14\uff0c \u6ee1\u8179\u81ea\u50b2\u7684\u5e02\u4e95\u8c6a\u96c4\uff0c\u51c6\u4f1a\u4e0d\u987e\u4e00\u5207\u7684\u60ca\u547c\u51fa\u58f0\u6765\uff0c\u4f46\u662f\uff0c\u7b49\u5230\u4ed6\u770b\u5230\u4ec7\u6055\u8f6c\u8eab\u4e00\u77a5\u7684\u65f6 \u5019\uff0c\u4ed6\u7acb\u523b\u77e5\u9053\u8fd9\u5947\u5f02\u7684\u5c11\u5e74\u7684\u9547\u9759\uff0c\u4e5f\u662f\u6709\u7740\u9650\u5ea6\u7684\u3002 \u3000\u3000\u4ec7\u6055\u76ee\u5149\u4e00\u77a5\uff0c\u5fc3\u5934\u84e6\u5730\u4e00\u9707\uff0c\u8f6c\u8eab\u3001\u9519\u6b65\uff0c\u5530\u5730\u62e7\u8f6c\u8eab\u8eaf\uff0c\u5389\u559d\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u66ae\u6625\u7684\u9633\u5149\uff0c\u5c1a\u672a\u5b8c\u5168\u5347\u5230\u4e2d\u5929\uff0c\u4ece\u5fae\u504f\u4e1c\u5904\u659c\u659c\u5730\u7167\u4e0b\u6765\uff0c\u7167\u5728\u8fd9\u6742\u8349\u4e1b\u751f\uff0c\u7816\u77f3\u6ee1 \u5730\u7684\u8352\u91ce\u7834\u843d\u7684\u9662\u843d\u91cc\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u8352\u8d25\u9893\u5e9f\u7684\u9662\u843d\u91cc\uff0c\u4e1b\u751f\u6742\u8349\u7684\u6ce5\u5730\u4e0a\uff0c\u6b64\u523b\u6b63\u9b3c\u9b45\u822c\u5730\u7ad9\u7740\u4e00\u4e2a\u957f\u53d1\u62ab\u80a9\uff0c\u4e00 \u8eab\u957f\u888d\u7684\u5973\u5b50\uff0c\u6b64\u523b\u5979\u5df2\u505c\u4e0b\u811a\u6b65\uff0c\u4e00\u52a8\u4e5f\u4e0d\u52a8\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u6625\u9633\u6620\u7740\u5979\u7684\u957f\u53d1\uff0c\u5fae\u98ce\u5439\u7740 \u5979\u7684\u888d\u89d2\uff0c\u5979\u9634\u51c4\u51c4\u5730\u7b11\u4e86\u4e00\u4e0b\uff0c\u4f46\u7126\u9ec4\u50f5\u6728\u7684\u9762\u76ee\u4e0a\uff0c\u5374\u6ca1\u6709\u4e1d\u6beb\u7b11\u610f\uff0c\u201c\u725b\u4e09\u773c\u201d\u673a\u4f36 \u4f36\u8fde\u6253\u597d\u51e0\u4e2a\u5bd2\u5664\uff0c\u4e00\u76f4\u5230\u8bb8\u591a\u5e74\u4ee5\u540e\uff0c\u4ed6\u8fd8\u5728\u548c\u522b\u4eba\u8d4c\u5492\uff0c\u8d4c\u5492\u8bf4\u8fd9\u5973\u5b50\u662f\u521a\u4ece\u575f\u5893\u91cc\u8dd1 \u51fa\u6765\u7684\u3002 \u3000\u3000\u4ec7\u6055\u500f\u7136\u8f6c\u8eab\uff0c\u4e00\u58f0\u5389\u559d\uff0c\u5374\u6362\u5f97\u8fd9\u5973\u5b50\u7684\u4e00\u58f0\u51b7\u7b11\u3002 \u3000\u3000\u4ed6\u6697\u4e2d\u4e00\u8c03\u771f\u6c14\uff0c\u53c8\u5389\u559d\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u6b64\u6765\u4f55\u610f\uff1f\u201d \u3000\u3000\u8fd9\u957f\u53d1\u62ab\u80a9\uff0c\u5f62\u5982\u9b3c\u9b45\u7684\u767d\u888d\u5973\u5b50\uff0c\u76ee\u5149\u7d27\u7d27\u76ef\u5728\u4ec7\u6055\u8138\u4e0a\uff0c\u5c31\u50cf\u662f\u4e98\u53e4\u4ee5\u6765\u90fd\u672a\u66fe\u79fb \u52a8\u8fc7\u4e00\u4e0b\u4f3c\u7684\uff0c\u5979\u7b80\u77ed\u800c\u68ee\u51b7\u5730\u56de\u7b54\uff1a\u201c\u627e\u4f60\uff01\u201d \u3000\u3000\u201c\u627e\u6211\u8c03\u4ec7\u6055\u60ca\u5947\u5730\u91cd\u590d\u4e00\u53e5\uff0c\u4ed6\u60f3\u4e0d\u51fa\u81ea\u5df1\u51e0\u65f6\u89c1\u8fc7\u8fd9\u5973\u5b50\uff0c\u4e5f\u60f3\u4e0d\u51fa\u81ea\u5df1\u51e0\u65f6\u548c\u8fd9 \u5973\u5b50\u4ee5\u53ca\u6709\u5173\u8fd9\u5973\u5b50\u7684\u4e00\u5207\u6709\u8fc7\u5173\u8fde\uff0c\u8fd9\u79cd\u9762\u76ee\u4eba\u4eec\u53ea\u8981\u89c1\u8fc7\u4e00\u6b21\uff0c\u4fbf\u6c38\u751f\u4e5f\u4e0d\u4f1a\u5fd8\u8bb0\uff0c\u4ed6 \u786e\u4fe1\u81ea\u5df1\u7684\u8bb0\u5fc6\u8fd9\u6b21\u7edd\u4e0d\u4f1a\u6b3a\u9a97\u81ea\u5df1\uff1a\u201c\u96be\u9053\u5979\u4e5f\u662f\u90a3\u2018\u7075\u86c7\uff0c\u6bdb\u81ec\u7684\u88f3\u7fbd\uff1f\u201d\u8fd9\u5ff5\u5934\u5728\u4ed6 \u5fc3\u4e2d\u4e00\u95ea\u800c\u8fc7\uff0c\u4e8e\u662f\u4ed6\u6212\u5907\u5f97\u66f4\u4e25\u5bc6\u4e86\uff0c\u4ed6\u6c89\u58f0\u9053\uff1a\u201c\u6709\u4f55\u8d35\u5e72\uff1f\u201d \u3000\u3000\u8fd9\u767d\u888d\u5973\u5b50\u53c8\u81ea\u9634\u51c4\u51c4\u4e00\u58f0\u957f\u7b11\uff0c\u7b11\u58f0\u672a\u4f4f\uff0c\u7a81\u5730\u95ea\u7535\u822c\u65cb\u8eab\u4e00\u63a0\uff0c\u63a0\u5230\u8fd9\u7960\u5802\u6b63\u6bbf\u7684 \u540e\u9762\u95e8\u6237\u524d\uff0c\u51b7\u559d\u9053\uff1a\u201c\u51fa\u6765\uff01\uff0c\u2019\u5979\u52a8\u4f5c\u4e4b\u5feb\uff0c\u5c31\u50cf\u662f\u767d\u9a79\u8fc7\u9699\uff0c\u5f53\u4eba\u4eec\u65b9\u81ea\u60ca\u5f02\u4e8e\u5979\u8eab \u5f62\u7684\u8f6c\u52a8\u65f6\uff0c\u5979\u53c8\u5df2\u4e00\u52a8\u4e5f\u4e0d\u52a8\u5730\u7ad9\u5728\u95e8\u53e3\uff0c\u82e5\u4e0d\u662f\u4eba\u4eba\u90fd\u4eb2\u81ea\u89c1\u5230\u5979\u65b9\u81ea\u8fd9\u8fb9\u63a0\u53bb\uff0c\u5979\u5c31 \u50cf\u662f\u5df2\u5728\u90a3\u91cc\u7ad9\u4e86\u51e0\u4e2a\u65f6\u8fb0\u4f3c\u7684\u3002\u4ec7\u6055\u5251\u7709\u5fae\u76b1\uff0c\u6697\u5fd6\uff1a\u201c\u600e\u5730\u53c8\u51ed\u7a7a\u51fa\u6765\u4e2a\u5982\u6b64\u602a\u5f02\u7684\u5973 \u5b50\uff0c\u6b66\u529f\u7adf\u662f\u5982\u6b64\u4e4b\u9ad8\uff1f\u201d \u3000\u3000\u53ea\u542c\u8fd9\u5973\u5b50\u559d\u58f0\u65b9\u4f4f\uff0c\u7960\u5802\u6b63\u6bbf\u4e2d\u7a81\u5730\u4f20\u51fa\u4e00\u9635\u9635\u5927\u7b11\u4e4b\u58f0\uff0c\u90a3\u8eab\u6750\u9880\u957f\uff0c\u9762\u5bb9\u6e05\u766f\u7684 \u767d\u53d1\u9053\u4eba\uff0c\u5728\u7b11\u58f0\u4e2d\u6f2b\u6b65\u800c\u51fa\uff0c\u76ee\u5149\u95ea\u7535\u822c\u5728\u5f53\u95e8\u800c\u7acb\u7684\u957f\u53d1\u5973\u5b50\u8eab\u4e0a\u4e00\u626b\uff0c\u5374\u518d\u4e5f\u4e0d\u671b\u5979 \u4e00\u773c\uff0c\u7b14\u76f4\u5730\u8d70\u5230\u4ec7\u6055\u8eab\u524d\uff0c\u542b\u7b11\u8bf4\u9053\uff1a\u201c\u9152\u672a\u7ec8\uff0c\u7b75\u672a\u6563\uff0c\u65bd\u4e3b\u4e3a\u4f55\u5c31\u5306\u5306\u8d70\u4e86\uff0c\u4e0d\u8be5\uff0c \u4e0d\u8be5\uff0c\u5927\u662f\u4e0d\u8be5\uff0c\u4f60\u6211\u840d\u6c34\u76f8\u9022\uff0c\u9887\u89c9\u6295\u7f18\uff0c\u4e14\u968f\u8d2b\u9053\u518d\u53bb\u559d\u4e24\u53e3\u8c03\u4ed6\u653e\u58f0\u72c2\u7b11\uff0c\u6717\u58f0\u800c \u8a00\uff0c\u4e00\u628a\u62c9\u4f4f\u4ec7\u6055\u7684\u80a9\u8180\uff0c\u90a3\u8be1\u5f02\u7edd\u4f26\u7684\u767d\u888d\u957f\u53d1\u7684\u5973\u5b50\uff0c\u4ed6\u7adf\u50cf\u662f\u6839\u672c\u6ca1\u6709\u770b\u5230\u3002\u4ec7\u6055\u5fc3 \u4e2d\u4e00\u52a8\uff0c\u4ea6\u81ea\u542b\u7b11\u9053\uff1a\u201c\u9053\u957f\u5982\u6b64\u62ac\u7231\uff0c\u5c0f\u53ef\u6562\u4e0d\u4ece\u547d\u3002\u201d\u56de\u8fc7\u5934\uff0c\u5411\u90a3\u5df2\u81ea\u5413\u5f97\u9762\u65e0\u4eba\u8272 \u7684\u201c\u725b\u4e09\u773c\u201d\u9053\uff1a\u201c\u4f60\u8fd9\u4e9b\u4f34\u5f53\uff0c\u6b64\u523b\u7a74\u9053\u89e3\u5f00\uff0c\u8840\u4e5f\u6b62\u4f4f\uff0c\u4f60\u66ff\u4ed6\u4eec\u4e0a\u4e9b\u91d1\u521b\u836f\u4fbf\u53ef\u65e0 \u788d\uff0c\u6211\u4e14\u968f\u8fd9\u9053\u957f\u8fdb\u53bb\u559d\u4e24\u53e3\u3002\u201d\u76ee\u5149\u4e00\u8f6c\uff0c\u5411\u90a3\u81ea\u53d1\u9053\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u4ed6\u6b64\u523b\u7adf\u4e5f\u751f\u50cf\u662f\u4e0d \u518d\u611f\u5230\u90a3\u957f\u53d1\u5973\u5b50\u7684\u5b58\u5728\u4f3c\u7684\uff0c\u4efb\u51ed\u8fd9\u767d\u53d1\u9053\u4eba\u62c9\u7740\u81ea\u5df1\u7684\u80a9\u8180\uff0c\u5411\u6bbf\u5185\u8d70\u53bb\u3002 \u3000\u3000\u5f53\u95e8\u800c\u7acb\u7684\u957f\u53d1\u5973\u5b50\u59cb\u7ec8\u6ca1\u6709\u56de\u8fc7\u5934\u6765\uff0c\u5979\u7b14\u76f4\u5730\u7ad9\u7740\uff0c\u76f4\u5230\u4ec7\u6055\u548c\u90a3\u767d\u53d1\u9053\u4eba\u53c8\u90fd\u8d70 \u5230\u5979\u8eab\u540e\uff0c\u5979\u500f\u7136\u8f6c\u8eab\uff0c\u4ec7\u6055\u53ea\u89c9\u5fc3\u5934\u5fae\u5fae\u4e00\u9707\uff0c\u4f46\u9762\u4e0a\u5374\u4ecd\u6ee1\u5e26\u7b11\u5bb9\uff0c\u76f4\u5230\u6b64\u523b\uff0c\u4ed6\u8fd8\u4e0d \u77e5\u9053\u7a76\u7adf\u8be5\u5982\u4f55\u5e94\u4ed8\u8fd9\u602a\u5f02\u7edd\u4f26\uff0c\u6765\u5386\u4e0d\u660e\u7684\u5973\u5b50\uff0c\u800c\u4ed6\u5728\u6ca1\u6709\u51b3\u5b9a\u81ea\u5df1\u4e0b\u4e00\u4e2a\u6b65\u9aa4\u8be5\u5982\u4f55 \u505a\u7684\u65f6\u5019\uff0c\u9762\u4e0a\u6c38\u8fdc\u90fd\u5e26\u7740\u8fd9\u79cd\u98d8\u9038\u800c\u4e0d\u53ef\u6349\u6478\u7684\u7b11\u5bb9\u3002 \u3000\u3000\u767d\u53d1\u9053\u4eba\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4f4d\u5973\u65bd\u4e3b\u600e\u5730\u6321\u4f4f\u8d2b\u9053\u7684\u53bb\u8def\uff0c\u4f46\u8bf7\u501f\u8fc7\u4e00\u6b65\uff0c\u8ba9\u8d2b\u9053\u957f \u53d1\u5973\u5b50\u7684\u76ee\u5149\u5c31\u50cf\u662f\u6b63\u5728\u4ec7\u6055\u8138\u4e0a\u751f\u4e86\u6839\u4f3c\u7684\uff0c\u9664\u4e86\u4ec7\u6055\u4e4b\u5916\uff0c\u5979\u518d\u4e0d\u5411\u522b\u5904\u671b\u4e00\u773c\uff0c\u767d\u53d1 \u9053\u4eba\u7684\u8bdd\uff0c\u5979\u66f4\u662f\u7406\u4e5f\u4e0d\u7406\u3002\u201d\u6211\u4e0d\u7ba1\u4f60\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba\uff0c\u4e5f\u4e0d\u7ba1\u4f60\u8fd9\u6837\u88c5\u6a21\u4f5c\u6837\uff0c\u9b3c\u9b3c\u795f\u795f \u662f\u4e3a\u4e86\u5e72\u4ec0\u4e48\uff0c\u4f46\u662f\u2014\u2014\u201d\u5979\u751f\u51b7\u3001\u7f13\u6162\u3001\u4e00\u5b57\u4e00\u5b57\u4ed6\u8bf4\u7740\uff0c\u6bcf\u4e00\u4e2a\u5b57\u5728\u5979\u7684\u820c\u5c16\u6eda\u52a8\u4e00 \u4e0b\uff0c\u4ece\u7259\u7f1d\u4e2d\u8ff8\u51fa\uff0c\u5c31\u50cf\u662f\u51b0\u73e0\u843d\u5728\u77f3\u677f\u4e0a\u4f3c\u7684\uff0c\u51b0\u51b7\u800c\u7b80\u77ed\uff0c\u4efb\u4f55\u4eba\u90fd\u65e0\u6cd5\u4ece\u5979\u7684\u8bed\u53e5 \u4e2d\uff0c\u5bfb\u5f97\u4efb\u4f55\u4e00\u79cd\u559c\u3001\u6012\u3001\u54c0\u3001\u4e50\u7684\u60c5\u611f\u3002 \u3000\u3000\u3002\u6b64\u523b\u5979\u8bed\u58f0\u5fae\u987f\uff0c\u4f46\u7edd\u4e0d\u7ed9\u522b\u4eba\u63d2\u53e3\u7684\u673a\u4f1a\uff0c\u7acb\u523b\u63a5\u7740\u9053\uff1a\u201c\u4ee5\u540e\u4f60\u7684\u624b\u6307\u8981\u662f\u518d\u78b0 \u5230\u6bdb\u6587\u742a\u4e00\u4e0b\uff0c\u6211\u5c31\u65a9\u65ad\u4f60\u7684\u624b\u6307\uff1b\u4f60\u7684\u773c\u775b\u4e00\u8981\u662f\u518d\u671b\u6bdb\u6587\u742a\u4e00\u773c\uff0c\u6211\u5c31\u6316\u51fa\u4f60\u7684\u773c\u775b\uff0c \u800c\u4e14\u2014\u2014\u73b0\u5728\u4f60\u8981\u662f\u8fd8\u4e0d\u505c\u6b62\u4f60\u8138\u4e0a\u8fd9\u7b11\u5bb9\u7684\u6d3b\uff0c\u6211\u5c31\u4f1a\u53eb\u4f60\u6c38\u8fdc\u90fd\u7b11\u4e0d\u51fa\u6765\uff01\u201d \u3000\u3000\u5979\u51b0\u51b0\u5730\u7ed3\u675f\u4e86\u81ea\u5df1\u7684\u8bdd\uff0c\u76ee\u5149\u4ecd\u7136\u671b\u7740\u4ec7\u6055\uff0c\u671b\u7740\u4ec7\u6055\u9762\u4e0a\u7684\u7b11\u5bb9\u3002 \u3000\u3000\u4ec7\u6055\u9762\u4e0a\u7684\u7b11\u5bb9\uff0c\u679c\u7136\u6d88\u5931\u4e86\uff0c\u5979\u6ee1\u610f\u5730\u54fc\u4e86\u4e00\u58f0\uff0c\u54ea\u77e5\u5979\u201c\u54fc\u201d\u58f0\u672a\u4e86\uff0c\u4ec7\u6055\u5374\u53c8\u7eb5 \u58f0\u72c2\u7b11\u4e86\u8d77\u6765\uff0c\u4ed6\u72c2\u7b11\u7740\u9053\uff1a\u201c\u9601\u4e0b\u8bf4\u7684\u8bdd\uff0c\u5c0f\u53ef\u4e00\u53e5\u4e5f\u542c\u4e0d\u61c2\uff0c\u5982\u679c\u9601\u4e0b\u4e0d\u5acc\u9ebb\u70e6\u7684\u8bdd\uff0c \u5c31\u8bf7\u9601\u4e0b\u518d\u8bf4\u4e00\u904d\uff0c\u5c0f\u53ef\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u770b\u6bdb\u59d1\u5a18\u4e00\u773c\u2014\u2014\u201d\u4ed6\u8bdd\u58f0\u672a\u4e86\uff0c\u90a3\u767d\u53d1\u9053\u4eba\u4ea6\u81ea\u7eb5\u58f0 \u72c2\u7b11\u8d77\u6765\uff0c\u4ed6\u72c2\u7b11\u7740\u63a5\u53e3\u9053\uff1a\u201c\u8d2b\u9053\u867d\u7136\u7f6e\u8eab\u65b9\u5916\uff0c\u4f46\u8ba9\u8d2b\u9053\u89c1\u4e86\u7edd\u8272\u7f8e\u5973\u800c\u4e0d\u671b\u5979\u4e24\u773c\uff0c \u5374\u4e5f\u65e0\u6cd5\u505a\u5230\uff0c\u9664\u975e\u2014\u2014\u54c8\u54c8\uff0c\u9664\u975e\u8fd9\u5973\u5b50\u7684\u5c0a\u5bb9\u5b9e\u5728\u4e0d\u6562\u9886\u6559\u3002\u201d \u3000\u3000\u8fd9\u767d\u53d1\u9053\u4eba\u6614\u5e74\u7eb5\u6a2a\u6b66\u6797\u65f6\uff0c\u672c\u6765\u662f\u4e2a\u4e0d\u82df\u8a00\u7b11\u7684\u4eba\u7269\uff0c\u4f46\u540e\u6765\u4ed6\u6d6a\u8ff9\u5929\u4e0b\uff0c\u7eb5\u60c5\u5c71 \u6c34\uff0c\u5341\u5e74\u4ee5\u6765\uff0c\u65e9\u5df2\u5c06\u4e16\u4e0a\u7684\u4e00\u5207\u540d\u5229\u4e4b\u4e89\uff0c\u793c\u6559\u89c4\u8303\uff0c\u90fd\u629b\u5230\u4e5d\u9704\u4e91\u5916\uff0c\u5df2\u662f\u8131\u7565\u5f62\u8ff9\uff0c \u4e0d\u4fee\u8fb9\u5e45\u7684\u98ce\u5c18\u9690\u58eb\uff0c\u662f\u4ee5\u4ed6\u6b64\u523b\u65b9\u81ea\u4f1a\u8bf4\u51fa\u8fd9\u79cd\u8bdd\u6765\u3002 \u3000\u3000\u4ed6\u6b64\u523b\u5df2\u9690\u7ea6\u5730\u611f\u89c9\u5230\u8fd9\u5c11\u5e74\uff0c\u8fd9\u5973\u5b50\uff0c\u90fd\u548c\u81ea\u5df1\u6709\u7740\u4e9b\u5173\u7cfb\uff0c\u4f46\u6b64\u523b\u4ed6\u91cd\u8fd4\u6c5f\u5357\uff0c\u539f \u5df2\u5c06\u4e00\u5207\u4e8b\u90fd\u7f6e\u4e4b\u5ea6\u5916\uff0c\u662f\u4ee5\u4ed6\u4e5f\u4e0d\u6015\u4f1a\u7275\u6d89\u5230\u4efb\u4f55\u9ebb\u70e6\uff0c\u4ed6\u72c2\u7b11\u7740\u8bf4\u5b8c\u4e86\u8bdd\uff0c\u62ac\u8d77\u5934\uff0c\u53ea \u89c9\u8fd9\u957f\u53d1\u5973\u5b50\u76ee\u5149\u4e00\u95ea\uff0c\u679c\u7136\u5df2\u671b\u5230\u81ea\u5df1\u8eab\u4e0a\u3002 \u3000\u3000\u6ca1\u6709\u4e00\u53e5\u8bdd\uff0c\u751a\u81f3\u8fde\u4e00\u4e2a\u5b57\u90fd\u6ca1\u6709\uff0c\u8fd9\u957f\u53d1\u5973\u5b50\u7a81\u5730\u51b7\u7b11\u4e00\u58f0\uff0c\u7535\u4e5f\u4f3c\u5730\u4f38\u51fa\u624b\u638c\uff0c\u4ec7 \u6055\u5fc3\u4e2d\u4e00\u60ca\uff0c\u54ea\u77e5\u8fd9\u5973\u5b50\u53f3\u638c\u4e00\u4f38\uff0c\u4e00\u843d\uff0c\u201c\u556a\u201d\u5730\u4e00\u58f0\uff0c\u7adf\u5728\u81ea\u5df1\u5de6\u638c\u4e0a\u6253\u4e86\u4e00\u4e0b\uff0c\u4ec7\u6055 \u5fc3\u4e2d\u5927\u5947\uff0c\u4e0d\u77e5\u9053\u8fd9\u5973\u5b50\u600e\u5730\u7a81\u7136\u6253\u8d77\u81ea\u5df1\u6765\uff0c\u53ea\u89c1\u5979\u4e00\u53cc\u624b\u638c\uff0c\u6625\u8471\u6b32\u6298\uff0c\u83b9\u767d\u5982\u7389\uff0c\u4ed6 \u76ee\u5149\u4e00\u77ac\uff0c\u54ea\u77e5\u8fd9\u5973\u5b50\u5de6\u638c\u4e00\u53cd\uff0c\u201c\u556a\u201d\u5730\u53c8\u662f\u4e00\u58f0\uff0c\u7adf\u5728\u81ea\u5df1\u53f3\u638c\u4e0a\u53c8\u7740\u7740\u5b9e\u5b9e\u5730\u51fb\u4e86\u4e00 \u638c\u3002 \u3000\u3000\u8fd9\u4e24\u638c\u638c\u58f0\u6e05\u8106\u5df2\u6781\uff0c\u4ec7\u6055\u4e0e\u81ea\u53d1\u9053\u4eba\u4ff1\u90fd\u4e00\u6014\uff0c\u7a81\u5730\u89c9\u5f97\u4e00\u80a1\u65e0\u6cd5\u5f62\u5bb9\u7684\u8165\u81ed\u4e4b\u6c14\uff0c \u6a2a\u8eab\u800c\u6765\uff0c\u90a3\u81ea\u53d1\u9053\u4eba\u5fc3\u4e2d\u4e00\u52a8\uff0c\u53ea\u542c\u8fd9\u5973\u5b50\u201c\u54af\u201d\u5730\u51b7\u7b11\u4e00\u58f0\uff0c\u9634\u68ee\u68ee\u5730\u53c8\u81ea\u8bf4\u9053\uff1a\u201c\u8fd8 \u4e0d\u8d70\uff01\u201d \u3000\u3000\u81ea\u53d1\u9053\u4eba\u76ee\u5149\u8fde\u8f6c\u51e0\u8f6c\uff0c\u7b11\u5bb9\u5df2\u655b\uff0c\u60f3\u662f\u5728\u52aa\u529b\u601d\u7d22\u7740\u4ec0\u4e48\uff0c\u4ec7\u6055\u5fae\u5fae\u4e00\u7b11\uff0c\u6717\u58f0\u9053\uff1a \u201c\u5c0f\u53ef\u6b63\u662f\u8981\u8d70\uff0c\u53ea\u662f\u9601\u4e0b\u6321\u4f4f\u4e86\u53bb\u8def\u2014\u2014\u201d\u4ed6\u62ac\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u8fd9\u767d\u888d\u5973\u5b50\u9762\u4e0a\u4ecd\u662f\u4e00\u65e0\u8868 \u60c5\uff0c\u4f46\u76ee\u5149\u5374\u5f00\u59cb\u6d3b\u52a8\u8d77\u6765\uff0c\u4ed6\u5fc3\u4e2d\u4e00\u52a8\uff0c\u95ea\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u5979\u76ee\u5149\u4e4b\u4e2d\uff0c\u6ee1\u662f\u77db\u76fe\u75db\u82e6\u4e4b \u8272\uff0c\u8fd9\u79cd\u773c\u8272\u662f\u53ea\u6709\u4eba\u4eec\u5728\u52aa\u529b\u514b\u5236\u7740\u81ea\u5df1\u7684\u6b32\u671b\u65f6\u624d\u4f1a\u6709\u7684\uff0c\u4ed6\u4e0d\u77e5\u9053\u8fd9\u770b\u6765\u50cf\u662f\u4e00\u65e0\u60c5 \u611f\u7684\u5973\u5b50\uff0c\u600e\u4f1a\u6709\u8fd9\u79cd\u773c\u8272\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u6b63\u81ea\u731c\u7591\u4e0d\u5b9a\uff0c\u5374\u89c1\u90a3\u767d\u53d1\u9053\u4eba\u7a81\u5730\u5927\u559d\u4e00\u58f0\uff1a\u201c\u6bd2\u9f99\u638c\uff01\u201d \u3000\u3000\u767d\u888d\u5973\u5b50\u51b7\u51b7\u4e00\u7b11\uff1a\u201c\u4e0d\u9519\uff01\u201d\u53cc\u638c\u4e00\u7ffb\uff0c\u201c\u556a\u3001\u556a\u201d\u4e24\u58f0\uff0c\u53cc\u638c\u95ea\u7535\u822c\u53c8\u4e92\u51fb\u4e00\u638c\uff0c \u767d\u53d1\u9053\u4eba\u5982\u89c1\u86c7\u874e\u822c\uff0c\u7a81\u5730\u5012\u9000\u4e24\u6b65\uff0c\u4ec7\u6055\u53c8\u60ca\u53c8\u5947\uff0c\u8fd9\u767d\u53d1\u9053\u4eba\u4ecd\u62c9\u4f4f\u4ed6\u7684\u81c2\u8180\uff0c\u4ed6\u53ea\u5f97 \u968f\u7740\u5012\u9000\u4e24\u6b65\uff0c\u4e00\u9635\u98ce\u5439\u6765\uff0c\u65b9\u624d\u90a3\u8165\u81ed\u4e4b\u6c14\uff0c\u53c8\u81ea\u6251\u9f3b\u800c\u6765\uff0c\u4ed6\u53ea\u9053\u8fd9\u767d\u53d1\u9053\u4eba\u6293\u4f4f\u81ea\u5df1 \u81c2\u8180\u7684\u624b\uff0c\u6293\u5f97\u8d8a\u6765\u8d8a\u7d27\uff0c\u7a81\u5730\u624b\u638c\u4e00\u677e\uff0c\u4ec7\u6055\u773c\u524d\u4e00\u82b1\uff0c\u8fd9\u767d\u53d1\u9053\u4eba\u8eab\u5f62\u4e00\u52a8\uff0c\u53cc\u638c\u5982 \u98ce\uff0c\u5530\u5530\uff0c\u5530\u5530\uff0c\u7adf\u7a81\u5730\u5411\u8fd9\u957f\u53d1\u5973\u5b50\u653b\u51fa\u56db\u638c\u3002 \u3000\u3000\u638c\u52bf\u5982\u98ce\uff0c\u638c\u98ce\u864e\u864e\uff0c\u4ec7\u6055\u6697\u8d5e\u4e00\u58f0\uff0c\u8fd9\u81ea\u53d1\u9053\u4eba\u6b66\u529f\u679c\u7136\u4e0d\u5f31\uff0c\u5374\u89c1\u8fd9\u957f\u53d1\u5973\u5b50\u5a07\u8eaf \u7684\u6e9c\u6e9c\u4e00\u8f6c\uff0c\u8eab\u8eaf\u500f\u7136\u6ed1\u5f00\u4e94\u5c3a\uff0c\u7a81\u5730\u653e\u58f0\u547c\u9053\uff1a\u201c\u4f60\u770b\u5230\u4e86\u5417\uff1f\u8fd9\u662f\u4ed6\u903c\u6211\u52a8\u624b\u7684\uff0c\u53ef\u4e0d \u662f\u6211\u6709\u5fc3\u7834\u6212\u5440\uff01\u201d\u547c\u58f0\u867d\u5927\uff0c\u4f46\u5374\u5a07\u67d4\u6e05\u8106\uff0c\u54ea\u91cc\u8fd8\u662f\u65b9\u624d\u90a3\u79cd\u51b7\u51b0\u51b0\u7684\u58f0\u97f3\u3002 \u3000\u3000\u4ec7\u6055\u66f4\u60ca\u66f4\u5947\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u987a\u7740\u8fd9\u5973\u5b50\u7684\u76ee\u5149\u671b\u53bb\uff0c\u53ea\u89c1\u5979\u76ee\u5149\u5728\u53f3\u8fb9\u7684\u571f\u5899\u4e0a\u4e00\u8f6c\uff0c \u957f\u8896\u4e00\u62c2\uff0c\u7a81\u5730\u8f7b\u98d8\u98d8\u5411\u767d\u53d1\u9053\u4eba\u62cd\u51fa\u4e00\u638c\u3002 \u3000\u3000\u638c\u52bf\u867d\u8f7b\uff0c\u4f46\u8fd9\u767d\u53d1\u9053\u4eba\u4f3c\u662f\u5fc3\u5b58\u754f\u60e7\uff0c\u7adf\u4e0d\u6562\u786c\u63a5\u5979\u8fd9\u4e00\u638c\u3002 \u3000\u3000\u4ec7\u6055\u5fc3\u5ff5\u8fde\u8f6c\u6570\u8f6c\uff0c\u6b63\u81ea\u4e3e\u68cb\u4e0d\u5b9a\uff0c\u54ea\u77e5\u53f3\u9762\u571f\u5899\u4e0a\uff0c\u7a81\u5730\u7f13\u7f13\u5347\u8d77\u4e00\u6761\u4eba\u5f71\u6765\uff0c\u8f7b\u8f7b \u8bf4\u9053\uff1a\u201c\u5e08\u59d0\uff0c\u6211\u6ca1\u6709\u770b\u89c1\uff01\u201d \u3000\u3000\u4ec7\u6055\u4e00\u60ca\uff0c\u8f6c\u76ee\u671b\u53bb\uff0c\u8131\u53e3\u547c\u9053\uff1a\u201c\u6587\u742a\uff0c\u679c\u7136\u662f\u4f60\u5728\u8fd9\u91cc\u3002\u201d\u8bed\u58f0\u672a\u843d\uff0c\u7a81\u5730\u4e00\u80a1\u638c \u98ce\uff0c\u8fce\u9762\u62cd\u6765\uff0c\u8fd9\u638c\u98ce\u53c8\u8f7b\u53c8\u67d4\uff0c\u4f3c\u662f\u6beb\u65e0\u52b2\u9053\uff0c\u4ec7\u6055\u5168\u5fc3\u5168\u610f\u5728\u671b\u7740\u65b9\u624d\u81ea\u5899\u4e0a\u73b0\u8eab\u7684\u6bdb \u6587\u742a\uff0c\u89c1\u5230\u8fd9\u4e00\u638c\u62cd\u6765\uff0c\u4fbf\u4e5f\u968f\u610f\u62cd\u51fa\u4e00\u638c\u3002 \u3000\u3000\u773c\u770b\u4ed6\u8fd9\u4e00\u638c\u5c31\u548c\u767d\u888d\u5973\u5b50\u51fb\u6765\u7684\u4e00\u638c\u529f\u529b\u76f8\u51fb\uff0c\u767d\u53d1\u9053\u4eba\u9762\u5bb9\u9aa4\u53d8\uff0c\u5374\u5df2\u559d\u6b62\u4e0d\u53ca\uff0c \u6bdb\u6587\u742a\u7eb5\u8eab\u4e00\u8dc3\uff0c\u4ece\u5899\u4e0a\u98d8\u98d8\u843d\u4e0b\uff0c\u7a81\u53c8\u5e7d\u5e7d\u4e00\u53f9\uff0c\u8f7b\u8f7b\u9053\uff1a\u201c\u5e08\u59d0\uff0c\u6211\u6ca1\u6709\u770b\u89c1\u3002\u201d \u3000\u3000\u90a3\u767d\u888d\u5973\u5b50\u638c\u5230\u4e2d\u9014\uff0c\u773c\u770b\u5c31\u8981\u62cd\u4e0a\u4ec7\u6055\u7684\u624b\u638c\uff0c\u542c\u5230\u8fd9\u53e5\u8bdd\u7a81\u5730\u5e73\u638c\u4e00\u7f29\uff0c\u8eab\u5f62\u95ea\u7535 \u822c\u9000\u5230\u571f\u5899\u8fb9\uff0c\u72e0\u72e0\u77aa\u4e86\u6bdb\u6587\u742a\u4e00\u773c\uff0c\u5389\u58f0\u9053\uff1a\u201c\u6211\u662f\u4e3a\u4f60\u597d\uff0c\u4f60\u8fd8\u8bf4\u6ca1\u6709\u770b\u89c1\uff0c\u660e\u660e\u662f\u8001 \u9053\u58eb\u5148\u5411\u6211\u52a8\u624b\u7684\u3002\u201d \u3000\u3000\u6bdb\u6587\u742a\u773c\u5e18\u4e00\u5782\uff0c\u76ee\u5149\u671b\u5728\u5730\u4e0a\u3002 \u3000\u3000\u201c\u6211\u771f\u7684\u6ca1\u6709\u770b\u89c1\uff0c\u4f55\u51b5\u2026\u2026\u4f55\u51b5\u4ed6\u4e5f\u6ca1\u6709\u5148\u5411\u4f60\u52a8\u624b\uff01\u201d \u3000\u3000\u767d\u888d\u5973\u5b50\u72e0\u72e0\u4e00\u8dfa\u811a\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4f60\u771f\u662f\u6ca1\u51fa\u606f\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\u4eba\u5bb6\u600e\u4e48\u5bf9\u4f60\uff0c\u4f60\u8fd9\u6837\u5bf9 \u4ed6\uff1f\u6628\u5929\u665a\u4e0a\u6211\u8ddf\u4f60\u8bf4\u7684\u8bdd\uff0c\u4f60\u96be\u9053\u6ca1\u6709\u542c\u89c1\u5417\uff1f\u4f60\u8bf4\u4ed6\u4e0d\u4f1a\u6b66\u529f\uff0c\u4f60\u770b\u4ed6\u662f\u4e0d\u662f\u4e0d\u4f1a\u6b66 \u529f\uff0c\u4ed6\u5bf9\u4f60\u5230\u5e95\u5b58\u7740\u4ec0\u4e48\u574f\u5fc3\u601d\uff0c\u6211\u867d\u7136\u4e0d\u77e5\u9053\uff0c\u53ef\u662f\u2014\u2014\u53ef\u662f\u2014\u2014\u201d\u8eab\u5f62\u7a81\u5730\u4e00\u8f6c\uff0c\u95ea\u7535 \u822c\u63a0\u5230\u90a3\u5140\u81ea\u4f0f\u5728\u5730\u4e0a\uff0c\u5df1\u88ab\u5413\u5f97\u5446\u4e86\u7684\u4e94\u4e2a\u4eba\u8eab\u524d\uff0c\u76ee\u5149\u4e00\u8f6c\uff0c\u51fa\u624b\u5982\u98ce\uff0c\u5288\u9762\u6293\u4f4f\u4e00\u4e2a \u7626\u5c0f\u67af\u5e72\u7684\u6c49\u5b50\u7684\u5934\u53d1\uff0c\u4e00\u628a\u63d0\u4e86\u8d77\u6765\uff0c\u8fd9\u6c49\u5b50\u60ca\u547c\u4e00\u58f0\uff0c\u5df2\u88ab\u5979\u51cc\u7a7a\u63d0\u8d77\uff0c\u63d0\u5230\u6bdb\u6587\u742a\u8eab \u524d\uff0c\u5bd2\u58f0\u8bf4\u9053\uff1as\u201c\u4f60\u95ee\u95f4\u8fd9\u5bb6\u4f19\uff0c\u6628\u5929\u665a\u4e0a\u8bf4\u4ec0\u4e48\u8bdd\uff0c\u54fc\uff01\u6628\u5929\u665a\u4e0a\u8981\u4e0d\u662f\u4f60\u82e6\u82e6\u62c9\u7740 \u6211\uff0c\u6211\u624d\u4e0d\u7ba1\u4ec0\u4e48\u8a93\u8a00\uff0c\u65e9\u5c31\u8dd1\u5230\u4f60\u623f\u95f4\u9694\u58c1\u53bb\uff0c\u628a\u90a3\u5c0f\u5b50\u62d6\u51fa\u6765\u4e00\u5200\u5bb0\u4e86\u3002\u201d\u624b\u8155\u4e00\u53cd\uff0c \u5c06\u90a3\u67af\u7626\u6c49\u5b50\u4e22\u5728\u5730\u4e0a\uff0c\u5389\u559d\u9053\uff1a\u201c\u4f60\u8bf4\uff0c\u4f60\u8bf4\uff0c\u4f60\u6628\u5929\u665a\u4e0a\uff0c\u8bf4\u7684\u662f\u4ec0\u4e48\u8bdd\uff1f\u201d \u3000\u3000\u3002\u8fd9\u67af\u7626\u6c49\u5b50\u672c\u5df2\u5413\u5f97\u5fc3\u795e\u65e0\u4e3b\uff0c\u6b64\u523b\u88ab\u5979\u8fd9\u4e00\u62c9\uff0c\u4e00\u62d6\u3001\u4e00\u4e22\uff0c\u53ea\u89c9\u6d51\u8eab\u5b9b\u5982\u9aa8\u6298\uff0c \u7adf\u6eda\u5728\u5730\u4e0a\u6740\u732a\u822c\u53eb\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4ec7\u6055\u5446\u5446\u5730\u695e\u5728\u5f53\u5730\uff0c\u4ed6\u867d\u7136\u806a\u660e\u7edd\u9876\uff0c\u6b64\u523b\u4ea6\u4e0d\u77e5\u8be5\u5982\u4f55\u5e94\u4ed8\uff0c\u81ea\u53d1\u9053\u4eba\u76ee\u5149\u56db\u8f6c\uff0c \u89c1\u5230\u8fd9\u60c5\u666f\uff0c\u4e5f\u4e0d\u77e5\u9053\u5176\u4e2d\u7a76\u7adf\u6709\u4ec0\u4e48\u66f2\u6298\uff0c\u662f\u4ee5\u4e5f\u5446\u5446\u5730\u695e\u5728\u90a3\u91cc\uff0c\u53ea\u89c1\u6bdb\u6587\u742a\u5934\u5782\u5f97\u8d8a \u53d1\u4f4e\u4e86\uff0c\u5979\u81ea\u59cb\u81f3\u7ec8\uff0c\u6ca1\u6709\u5411\u4ec7\u6055\u671b\u4e0a\u4e00\u773c\u3002 \u3000\u3000\u201c\u5e08\u59d0\uff0c\u6211\u77e5\u9053\u4f60\u5bf9\u6211\u597d\uff0c\u6211\u4e5f\u77e5\u9053\u4ed6\u4e00\u76f4\u5728\u9a97\u6211\uff0c\u53ef\u662f\u2014\u2014\u53ef\u662f\u5e08\u59d0\u4f60\u771f\u7684\u4e0d\u80fd\u548c\u4eba \u52a8\u624b\u5440\uff0c\u82e5\u662f\u88ab\u5e08\u7236\u77e5\u9053\u4e86\u2014\u2014\u201d\u5979\u5e7d\u5e7d\u957f\u53f9\u4e00\u58f0\uff0c\u4e2d\u65ad\u4e86\u81ea\u5df1\u7684\u8bdd\uff0c\u84ec\u677e\u7684\u79c0\u53d1\u5728\u5fae\u98ce\u4e2d \u98d8\u6447\u7740\uff0c\u4e00\u5982\u571f\u5899\u8fb9\u65b0\u751f\u7684\u3001\u9752\u7eff\u7684\u3001\u5e7c\u5c0f\u7684\u6625\u8349\u3002 \u3000\u3000\u767d\u888d\u5973\u5b50\u9762\u4e0a\u4ecd\u7136\u6ca1\u6709\u8868\u60c5\uff0c\u53ef\u662f\u4ec7\u6055\u770b\u5f97\u51fa\uff0c\u5979\u53cc\u76ee\u4e2d\u4ec7\u6068\u7684\u5149\u8292\uff0c\u5df2\u5728\u6162\u6162\u5fae\u5f31\uff0c \u6b63\u5982\u5730\u4e0a\u90a3\u67af\u7626\u6c49\u5b50\u6740\u732a\u822c\u7684\u543c\u53eb\uff0c\u5df2\u9010\u6e10\u5fae\u5f31\u4e00\u6837\uff0c\u5979\u7f13\u7f13\u8f6c\u8fc7\u8eab\uff0c\u7136\u540e\u7a81\u7136\u53c8\u662f\u4e00\u4e2a\u95ea \u7535\u822c\u7684\u52a8\u4f5c\uff0c\u63a0\u5230\u90a3\u81ea\u53d1\u9053\u4eba\u8eab\u524d\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u8ba4\u51fa\u4e86\u6211\u662f\u8c01\uff01\u53ef\u662f\uff0c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u81ea\u53d1\u9053\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u4ed6\u7684\u7b11\u5bb9\u867d\u7136\u6709\u4e9b\u52c9\u5f3a\uff0c\u4f46\u90a3\u53ea\u662f\u56e0\u4e3a\u773c\u4e2d\u7684\u4e00\u4e1d\u6de1\u6de1\u7684\u5fe7\u8651\uff0c\u800c \u4e0d\u662f\u4e3a\u4e86\u6050\u60e7\u6216\u60ca\u9a87\u3002 \u3000\u3000\u201c\u5341\u5e74\u4ee5\u524d\uff0c\u8d2b\u9053\u5df2\u5fd8\u5374\u59d3\u540d\uff0c\u4e0d\u8fc7\u2014\u2014\u5973\u65bd\u4e3b\u82e5\u662f\u575a\u6301\u8981\u542c\u7684\u8bdd\uff01\u201d\u4ed6\u76ee\u5149\u9510\u5229\u5730\u626b \u4e00\u773c\uff0c\u5c24\u5176\u5728\u6bdb\u6587\u742a\u8138\u4e0a\u505c\u7559\u5f97\u66f4\u4e45\u3002 \u3000\u3000\u7136\u540e\u4ed6\u8f7b\u8f7b\u5410\u4e86\u6c14\uff0c\u4e00\uff1a\u5b57\u4e00\u5b57\u4ed6\u8bf4\u9053\uff1a\u201c\u8d2b\u9053\u5c31\u662f\u5df4\u5c71\u9053\u58eb\u67f3\u590d\u660e\uff01\u201d \u3000\u3000\u6bdb\u6587\u742a\u79c0\u53d1\u4e00\u98a4\uff0c\u98de\u5feb\u5730\u62ac\u8d77\u5934\u6765\uff0c\u4ec7\u6055\u5fc3\u5934\u4ea6\u4e3a\u4e4b\u4e00\u9707\uff0c\u7b14\u76f4\u5730\u671b\u5411\u8fd9\u767d\u53d1\u9053\u4eba\uff0c\u7136 \u540e\u8fd9\u4e24\u4eba\u76ee\u5149\u4ff1\u90fd\u4e00\u8f6c\uff0c\u76f8\u9047\uff0c\u6bdb\u6587\u742a\u79c0\u53d1\u53c8\u81ea\u4e00\u98a4\uff0c\u5782\u4e0b\u773c\u5e18\uff0c\u98de\u5feb\u5730\u5782\u4e0b\u5934\u53bb\uff0c\u4ec7\u6055\u4e0d \u77e5\u600e\u5730\uff0c\u5fc3\u4e2d\u5fcd\u4e0d\u4f4f\u8981\u6697\u53f9\u4e00\u58f0\uff0c\u5374\u542c\u201c\u5df4\u5c71\u9053\u4eba\u201d\u53c8\u9053\uff1a\u201c\u8d2b\u9053\u5982\u679c\u8001\u773c\u4e0d\u82b1\uff0c\u90a3\u4e48\u5973\u65bd \u4e3b\u60f3\u5fc5\u662f\u2018\u5c60\u9f99\u4ed9\u5b50\uff0c\u7684\u9996\u5f92\u767d\u888d\u5973\u5b50\u51b7\u7b11\u63a5\u53e3\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u5c31\u662f\u6155\u5bb9\u60dc\u751f\uff01\u201d \u3000\u3000\u67f3\u590d\u660e\u7a81\u5730\u653e\u58f0\u72c2\u7b11\u8d77\u6765\u3002 \u3000\u3000\u201c\u96be\u602a\u5973\u65bd\u4e3b\u65b9\u624d\u4e0d\u7b49\u8d2b\u9053\u51fa\u624b\u4fbf\u4e0d\u52a8\u624b\uff0c\u60f3\u5fc5\u662f\u5973\u65bd\u4e3b\u6614\u5e74\u6212\u6740\u7acb\u8a93\u5c1a\u672a\u5230\u671f\u3002\u201d\u4ed6 \u7b11\u58f0\u4e00\u987f\uff0c\u76ee\u5149\u4e00\u8f6c\uff0c\u7a81\u5730\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff1a\u201c\u4f46\u60f3\u6765\u5973\u65bd\u4e3b\u53ef\u4ee5\u518d\u5f00\u6740\u6212\u4e4b\u65e5\uff0c\u5df2\u4e0d\u8fdc \u4e86\u3002\u201d \u3000\u3000\u6155\u5bb9\u60dc\u751f\u51b7\u7b11\u9053\uff1a\u201c\u6b63\u662f\uff0c\u7b49\u5230\u90a3\u4e00\u5929\u2014\u2014\u201d\u67f3\u590d\u660e\u72c2\u7b11\uff1a\u201c\u7b49\u5230\u90a3\u4e00\u5929\uff0c\u8d2b\u9053\u5fc5\u5b9a\u4eb2 \u81f3\u5973\u65bd\u4e3b\u90a3\u91cc\u5f15\u9888\u5f85\u622e\uff0c\u5973\u65bd\u4e3b\u53ea\u7ba1\u653e\u5fc3\u597d\u4e86\u3002\u201d \u3000\u3000\u6155\u5bb9\u60dc\u751f\u53c8\u81ea\u51b7\u7b11\uff1a\u201c\u597d\u6781\u3002\u201d\u5fae\u4e00\u65cb\u8eab\uff0c\u5df2\u81ea\u63a0\u5230\u4ec7\u6055\u8eab\u524d\uff0c\u4ec7\u6055\u5fae\u7b11\uff1a\u201c\u9601\u4e0b\u8981\u8bf4 \u4ec0\u4e48\uff0c\u4e0d\u5fc5\u8bf4\u51fa\u5c0f\u53ef\u4e5f\u77e5\u9053\u4e86\uff0c\u4e0d\u8fc7\uff0c\u5c0f\u53ef\u8981\u544a\u8bc9\u9601\u4e0b\u4e00\u53e5\uff0c\u5c0f\u53ef\u4e0e\u4ee4\u5e08\u59b9\u4e4b\u95f4\u60c5\u4e8b\uff0c\u9601\u4e0b \u4e1d\u6beb\u65e0\u6743\u5e72\u6d89\u3002\u201d\u4ed6\u8bed\u58f0\u672a\u4e86\uff0c\u7a81\u5730\u65cb\u8eab\u4e00\u63a0\uff0c\u7535\u4e5f\u4f3c\u5730\u63a0\u5230\u6bdb\u6587\u742a\u8eab\u524d\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u6587 \u742a\uff0c\u4f60\u8bf4\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u67f3\u590d\u660e\u4e00\u60ca\uff0c\u76f4\u5230\u6b64\u523b\uff0c\u4ed6\u624d\u770b\u5230\u8fd9\u5c11\u5e74\u7adf\u6709\u5982\u6b64\u8eab\u624b\u3002 \u3000\u3000\u6155\u5bb9\u60dc\u751f\u4e00\u60ca\uff0c\u5979\u4e5f\u60f3\u4e0d\u5230\u8fd9\u59cb\u7ec8\u672a\u52a8\u58f0\u8272\u7684\u5c11\u5e74\uff0c\u7adf\u4f1a\u7a81\u5730\u6709\u5982\u6b64\u4e00\u7740\u3002 \u3000\u3000\u6bdb\u6587\u742a\u4e00\u60ca\uff0c\u5979\u7684\u5fc3\u5fd0\u5fd1\u4e86\uff0c\u50cf\u94c5\u4e5f\u4f3c\u5730\u76f4\u843d\u4e0b\u53bb\uff0c\u53c8\u50cf\u7fbd\u6bdb\u4f3c\u5730\u98de\u626c\u8d77\u6765\uff0c\u5979\u4e0d\u6562\u62ac \u8d77\u5934\uff0c\u4e5f\u4e0d\u77e5\u8be5\u600e\u6837\u56de\u7b54\u3002 \u3000\u3000\u4ec7\u6055\u8f7b\u53f9\u4e00\u58f0\uff1a\u201c\u6587\u742a\uff0c\u6211\u5bf9\u4f60\u600e\u6837\uff0c\u4f60\u4e5f\u8be5\u77e5\u9053\uff0c\u522b\u4eba\u7684\u95f2\u8bdd\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u542c\uff1f\u4e3a\u4ec0 \u4e48\u8981\u4fe1\uff1f\u96be\u9053\u2014\u2014\u201d\u6155\u5bb9\u60dc\u751f\u4e00\u201d\u63a0\u800c\u6765\uff0c\u8f7b\u8f7b\u63a8\u5f00\u6bdb\u6587\u742a\uff0c\u53c8\u63a0\u5230\u4ec7\u6055\u8eab\u524d\uff0c\u5979\u76ee\u5149\u95ea\u52a8 \u7740\uff0c\u50cf\u5140\u9e70\u4e00\u6837\uff1a\u201c\u4f60\u771f\u7684\u559c\u6b22\u6587\u742a\uff1f\u201d \u3000\u3000\u4ec7\u6055\u5782\u4e0b\u5934\uff0c\u4ed6\u5782\u4e0b\u5934\u53ea\u662f\u4e3a\u4e86\u4e0d\u8ba9\u81ea\u5df1\u773c\u4e2d\u7684\u795e\u8272\u7ed9\u5bf9\u65b9\u770b\u89c1\uff0c\u7136\u540e\u4ed6\u4e5f\u50cf\u662f\u8d39\u4e86\u5f88 \u5927\u529b\u6c14\u4f3c\u7684\uff0c\u5148\u5410\u4e86\u4e00\u53e3\u957f\u6c14\uff0c\u7136\u540e\u9053\uff1a\u201c\u6211\u53ef\u600e\u4f1a\u9a97\u5979\uff01\u201d \u3000\u3000\u6155\u5bb9\u60dc\u751f\u95ea\u52a8\u7740\u53cc\u76ee\uff0c\u76ee\u5149\u53c8\u81ea\u4e00\u4eae\u3002 \u3000\u3000\u201c\u597d\uff01\u201d\u5979\u8bf4\u8bdd\u7684\u8bed\u6c14\u53c8\u5f00\u59cb\u53d8\u5f97\u7b80\u77ed\u800c\u51b0\u51b7\uff1a\u201c\u6211\u628a\u5979\u5e26\u56de\u53bb\u201d\u4f60\u628a\u5979\u5e26\u56de\u53bb\uff1f\u201d\u4ec7 \u6055\u751f\u786c\u5730\u95f4\u9053\u3002 \u3000\u3000\u201c\u534a\u5e74\u4e4b\u540e\uff0c\u4f60\u518d\u6765\u627e\u5979\uff0c\u8fd9\u534a\u5e74\u2014\u2014\u54fc\uff0c\u6211\u4f1a\u77e5\u9053\u4f60\u66f4\u591a\u4e9b\u3002\u201d \u3000\u3000\u5979\u8f6c\u8eab\u62c9\u8d77\u6bdb\u6587\u742a\u7684\u624b\uff0c\u5530\u5730\uff0c\u50cf\u71d5\u5b50\u822c\u5730\u63a0\u4e0a\u571f\u5899\uff0c\u8863\u8882\u98d8\u98d8\uff0c\u8bdd\u58f0\u8885\u8885\uff0c\u5979\u548c\u6bdb\u6587 \u742a\u5df2\u4ff1\u90fd\u6d88\u5931\u5728\u571f\u5899\u5916\u9762\uff0c\u4e0a\u5899\u7684\u5c3d\u5934\u5904\uff0c\u4f3c\u4e4e\u8fd8\u7559\u7740\u6bdb\u6587\u742a\u4e00\u58f0\u8f7b\u8f7b\u7684\u53f9\u606f\u3002 \u3000\u3000\u4ec7\u6055\u4ecd\u7136\u7ad9\u5728\u5899\u4e0b\uff0c\u671b\u7740\u571f\u5899\u7684\u5c3d\u5934\uff0c\u4eff\u4f5b\u5728\u6697\u81ea\u4f4e\u8bed\uff1a\u201c\u534a\u5e74\uff1f\u5509\u2014\u2014\u534a\u5e74\u5df2\u8db3\u591f \u4e86\u3002\u201d\u4ed6\u81ea\u5632\u5730\u5fae\u7b11\u4e00\u4e0b\uff0c\u201c\u534a\u5e74\u4e4b\u540e\uff0c\u90a3\u6155\u5bb9\u60dc\u751f\u6212\u6740\u7acb\u8a93\u5927\u7ea6\u5df2\u7834\u4e86\uff0c\u662f\u4ee5\u5979\u624d\u53eb\u6211\u534a \u5e74\u4e4b\u540e\u53bb\u627e\u5979\u4eec\uff0c\u90a3\u65f6\u5979\u5c31\u4e0d\u5fc5\u50cf\u4eca\u5929\u4e00\u6837\u6709\u8fd9\u591a\u987e\u5fcc\u3002\u201d \u3000\u3000\u4ed6\u51b7\u7b11\u8d77\u6765\uff0c\u6697\u5fd6\uff1a\u201c\u53ef\u662f\uff0c\u5979\u5374\u4e0d\u77e5\u9053\uff0c\u6211\u4e5f\u4e0d\u4f1a\u6709\u4eca\u65e5\u8fd9\u4e48\u591a\u987e\u5fcc\u4e86\u3002\u201d\u4eca\u65e5\uff0c\u4ed6 \u4e0d\u6b62\u4e00\u6b21\u6709\u52a8\u624b\u7684\u51b2\u52a8\uff0c\u60f3\u5c06\u8fd9\u5e08\u59d0\u59b9\u4e24\u4eba\u4f24\u5728\u81ea\u5df1\u638c\u4e0b\uff0c\u90a3\u4e48\uff0c\u5979\u4eec\u5c31\u6c38\u8fdc\u4e0d\u4f1a\u8bf4\u51fa\u4ed6\u7684 \u79d8\u5bc6\u4e86\u3002 \u3000\u3000\u53ef\u662f\uff0c\u4ed6\u5374\u5fcd\u4f4f\u4e86\uff0c\u8fd9\u4e00\u6765\u662f\u5979\u4eec\u6240\u77e5\u9053\u7684\u79d8\u5bc6\u5e76\u4e0d\u591a\uff0c\u518d\u6765\u662f\u4ed6\u6ca1\u6709\u5341\u5206\u7684\u628a\u63e1\u80fd\u5c06 \u5979\u4eec\u51fb\u6bd9\uff0c\u8fd8\u6709\u4e00\u4e2a\u539f\u56e0\uff0c\u4ed6\u81ea\u5df1\u867d\u4e0d\u613f\u627f\u8ba4\uff0c\u4f46\u5374\u662f\u4e8b\u5b9e\uff0c\u4ed6\u5df2\u5bf9\u4ed6\u4ec7\u4eba\u7684\u5973\u513f\uff0c\u751f\u51fa\u4e00 \u4e9b\u60c5\u611f\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u5fcd\u8010\u7740\uff0c\u76f4\u5230\u6700\u540e\u6155\u5bb9\u60dc\u751f\u8bf4\u8981\u5c06\u6bdb\u6587\u742a\u5e26\u56de\u53bb\uff0c\u4ed6\u751f\u786c\u5730\u8ffd\u95ee\u4e86\u4e00\u4e00\u53e5\uff0c\u77e5\u9053 \u5979\u8981\u5c06\u6bdb\u6587\u742a\u5e26\u56de\u53bb\u7684\u5730\u65b9\u662f\u5c60\u9f99\u4ed9\u5b50\u90a3\u91cc\uff0c\u662f\u4ee5\u4ed6\u653e\u5fc3\u4e86\uff0c\u81f3\u5c11\u5728\u8fd9\u534a\u5e74\u91cc\uff0c\u6bdb\u4e08\u742a\u4e0d\u4f1a \u89c1\u7740\u5979\u7684\u7239\u7239\uff0c\u90a3\u4e48\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u4e5f\u81f3\u5c11\u5728\u8fd9\u534a\u5e74\u91cc\u4e0d\u4f1a\u53d1\u73b0\u81ea\u5df1\u662f\u4f1a\u6b66\u529f\u7684\u3002 \u3000\u3000\u4f46\u6b64\u523b\uff0c\u4ed6\u7ad9\u5728\u5899\u4e0b\uff0c\u542c\u5230\u6bdb\u6587\u742a\u90a3\u2026\xb7\u58f0\u8f7b\u8f7b\u7684\u53f9\u606f\uff0c\u4ed6\u5374\u5f00\u59cb\u6709\u4e86\u4e00\u4efd\u65e0\u6cd5\u89e3\u91ca\u7684 \u6005\u60d8\uff0c\u4ed6\u5f00\u59cb\u89c9\u5f97\u6709\u4e9b\u5bf9\u4e0d\u8d77\u5979\uff0c\u5bf9\u4e0d\u8d77\u8fd9\u7eaf\u771f\u800c\u591a\u60c5\u7684\u5c11\u5973\uff0c\u867d\u7136\uff0c\u4e3a\u4e86\u5979\u7236\u4eb2\u7684\u7f6a\u6076\uff0c \u5979\u5fc5\u987b\u4ed8\u51fa\u8bb8\u591a\u4e0d\u8be5\u4ed8\u51fa\u7684\u4ee3\u4ef7\uff0c\u4f46\u65e0\u8bba\u5982\u4f55\uff0c\u5979\u8fd9\u4efd\u60c5\u611f\u662f\u7eaf\u771f\u800c\u5723\u6d01\u7684\uff0c\u4efb\u4f55\u4eba\u73a9\u5f04\uff0c \u5192\u8bfb\u4e86\u8fd9\u79cd\u7eaf\u771f\u800c\u5723\u6d01\u7684\u60c5\u611f\uff0c\u90fd\u662f\u4e00\u79cd\u7f6a\u6076\uff0c\u4e00\u79cd\u4e0d\u53ef\u5bbd\u6055\uff0c\u5351\u9119\u7edd\u9876\u7684\u7f6a\u6076\u3002 \u3000\u3000\u4ed6\u5782\u7740\u5934\uff0c\u542c\u5230\u9662\u843d\u53c8\u5f00\u59cb\u6709\u4e86\u5404\u79cd\u58f0\u97f3\uff0c\u4e5f\u542c\u5230\u90a3\u7c97\u9c81\uff0c\u4f46\u5374\u6073\u8bda\u7684\u201c\u725b\u4e09\u773c\u201d\uff0c\u4ece \u60ca\u9a87\u4e2d\u6062\u590d\u8fc7\u6765\uff0c\u4e0d\u4f4f\u5730\u5550\u9a82\u9053\uff1a\u201c\u8fd9\u5c0f\u5a18\u513f\uff0c\u771f\u6709\u70b9\u90aa\u6c14\uff0c\u5582\uff0c\u502a\u8001\u4e03\uff0c\u4f60\u600e\u5730\u8fd9\u4e48\u8113 \u5305\uff0c\u5728\u5a18\u513f\u4eec\u9762\u524d\u7a77\u543c\u4e9b\u4ec0\u4e48\uff0c\u771f\u662f\u4e22\u516c\u5b50\u7684\u4eba\uff0c\u54fc\uff0c\u4e5f\u4e22\u4e86\u6211\u2018\u725b\u4e09\u773c\uff0c\u7684\u4eba\uff0c\u5927\u80e1\u5b50\uff0c \u5feb\u53bb\u628a\u502a\u8001\u4e03\u6276\u56de\u6765\uff01\u201d\u7136\u540e\uff0c\u4ec7\u6055\u611f\u5230\u4e00\u53ea\u6e29\u67d4\u7684\u624b\uff0c\u8f7b\u8f7b\u62cd\u4e86\u62cd\u4ed6\u7684\u80a9\u8180\uff0c\u65e0\u8a00\u5730\u62c9\u7740 \u4ed6\uff0c\u8d70\u5165\u6b63\u6bbf\uff0c\u6b63\u6bbf\u4e2d\u7684\u706b\u5149\u672a\u7184\uff0c\u8089\u9999\u4ecd\u6d53\uff0c\u718a\u718a\u7684\u706b\u5149\u8fb9\uff0c\u4ea6\u4ecd\u81ea\u5750\u7740\u90a3\u4e2a\u8eab\u6750\u7565\u77ee\uff0c \u72c2\u6b4c\u559c\u54ed\u7684\u767d\u53d1\u8001\u4eba\u3002\u4ed6\u624b\u91cc\u4e5f\u4ecd\u7136\u62ff\u7740\u90a3\u53cc\u6728\u7b77\uff0c\u5728\u7f13\u7f13\u6405\u52a8\u7740\u9505\u91cc\u7684\u8089\u6c41\uff0c\u6df1\u6c89\u7684\u76ee \u5149\uff0c\u968f\u7740\u81ea\u5df1\u7684\u7b77\u5b50\u7f13\u7f13\u6405\u52a8\uff0c\u8fd9\u8001\u4eba\u5fc3\u4e2d\u603b\u50cf\u662f\u6709\u7740\u4ec0\u4e48\u5fc3\u4e8b\uff0c\u65b9\u624d\u5916\u9762\u7684\u4e00\u5207\u53d8\u5316\uff0c\u4ed6 \u90fd\u50cf\u662f\u6ca1\u6709\u542c\u5230\u3002\u4ec7\u6055\u9ed8\u9ed8\u5730\u968f\u7740\u67f3\u590d\u660e\u5728\u706b\u65c1\u5750\u4e86\u4e0b\u6765\uff0c\u8001\u4eba\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u600e \u5730\u53bb\u4e86\u8fd9\u4e48\u4e45\uff1f\u201d \u3000\u3000\u4ec7\u6055\u832b\u7136\u4e00\u7b11\uff0c\u4ed6\u5fc3\u91cc\u5728\u6697\u4e2d\u731c\u6d4b\uff1a\u201c\u83ab\u975e\u8fd9\u8001\u4eba\u5c31\u662f\u9752\u840d\u5251\u5b8b\u4ee4\u516c\uff01\u201d\u5341\u4e03\u5e74\u524d\uff0c \u201c\u5df4\u5c71\u5251\u5ba2\u201d\u67f3\u590d\u660e\uff0c\u201c\u9752\u840d\u5251\u2019\u5b8b\u4ee4\u516c\u4e00\u9f50\u5728\u6c5f\u6e56\u4e2d\u5931\u8e2a\u7684\u4e8b\uff0c\u4ed6\u4e5f\u77e5\u9053\u7684\uff0c\u8fd9\u4e24\u4eba\u5bf9\u4ed6 \u662f\u6069\u662f\u4ec7\uff0c\u4ed6\u4e5f\u5206\u4e0d\u6e05\u695a\uff0c\u53ea\u542c\u67f3\u590d\u660e\u7b11\u9053\uff1a\u201c\u65b9\u624d\u6211\u5728\u5916\u9762\u9047\u7740\u4e00\u4eba\uff0c\u4f60\u4e14\u731c\u4e0a\u4e00\u731c\uff0c\u6b64 \u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u8fd9\u8001\u4eba\u6de1\u6de1\u4e00\u7b11\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u832b\u832b\u4f17\u751f\uff0c\u4f17\u751f\u832b\u832b\uff0c\u6211\u8ba4\u5f97\u51e0\u4eba\uff1f\u6211\u4e00\u4eba\u4e5f\u4e0d\u8ba4\u5f97\uff0c\u4f60 \u6559\u6211\u5982\u4f55\u731c\u6cd5\u3002\u201d\u631f\u8d77\u4e00\u5757\u9999\u8089\uff0c\u653e\u5230\u5634\u91cc\uff0c\u7ec6\u7ec6\u5480\u56bc\uff0c\u751f\u50cf\u662f\u65e0\u8bba\u6b64\u4eba\u662f\u8c01\uff0c\u90fd\u4e0d\u5173\u4ed6 \u4e8b\uff0c\u4ed6\u4e5f\u7edd\u4e0d\u4f1a\u653e\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u67f3\u590d\u660e\u62ff\u8d77\u706b\u8fb9\u4e00\u4e2a\u4e2d\u5dde\u7f55\u89c1\uff0c\u585e\u5916\u5374\u6781\u901a\u5e38\u7684\u7f8a\u76ae\u9152\u56ca\uff0c\u4e3e\u5230\u5934\u4e0a\uff0c\u4ed6\u4f38\u624b\u4e00\u634f\uff0c\u4e00 \u7ebf\u70c8\u9152\uff0c\u81ea\u9152\u56ca\u4e2d\u6fc0\u5c04\u800c\u51fa\uff0c\u4ed6\u62ac\u8d77\u5934\uff0c\u4e00\u6ef4\u4e0d\u6f0f\u5730\u559d\u5230\u5634\u91cc\uff0c\u54c8\u54c8\u5927\u7b11\u51e0\u7b11\uff0c\u6717\u58f0\u8bf4\u9053\uff1a \u201c\u6b64\u4eba\u4f60\u6211\u867d\u4ff1\u4e0d\u8ba4\u5f97\uff0c\u5374\u662f\u4f60\u6211\u4e00\u4e2a\u6545\u4eba\u4e4b\u5973\uff0c\u54c8\u54c8\u2014\u2014\u6b64\u4eba\u5c31\u662f\u90a3\u2018\u6bdb\u81ec\uff0c\u7684\u5973\u513f\uff0c\u5979 \u867d\u6ca1\u6709\u8bf4\u51fa\uff0c\u4f46\u6211\u5374\u5df2\u731c\u5230\uff01\u201d\u4ec7\u6055\u4e00\u6115\uff1a\u201c\u4ed6\u600e\u4e48\u731c\u5230\u7684\uff1f\u201d\u4f46\u968f\u5373\u604d\u7136\uff1a\u201c\u60f3\u5fc5\u662f\u4ed6\u65b9 \u624d\u5df2\u542c\u5230\u90a3\u6c49\u5b50\u5bf9\u6211\u8bf4\u7684\u8bdd\uff0c\u662f\u4ee5\u4e24\u4e0b\u4e00\u5408\uff0c\u4fbf\u731c\u7740\u4e86\u3002\u201d\u53ea\u89c1\u90a3\u8001\u4eba\u53cc\u76ee\u4e00\u5f20\uff0c\u76ee\u5149\u7a81\u5730 \u73b0\u51fa\u5f02\u5149\uff0c\u4f46\u77ac\u53c8\u5782\u4e0b\u773c\u5e18\u3002 \u3000\u3000\u201c\u6bdb\u81ec\u662f\u8c01\uff1f\u5509\u2014\u2014\u5f80\u4e8b\u5df2\u5931\uff0c\u6bdb\u684c\u6211\u4e5f\u4e0d\u518d\u8ba4\u5f97\u4e86\u3002\u201d\u62e8\u4e86\u62e8\u9505\u4e2d\u8089\u6c41\uff1a\u201c\u706b\u5c06\u7184\u8089 \u5c06\u51b7\uff0c\u4f60\u8fd8\u662f\u5feb\u4e9b\u5403\u7f62\u2026\u2026\u201d \u3000\u3000\u67f3\u590d\u660e\u53c8\u81ea\u54c8\u54c8\u4e00\u7b11\uff0c\u751f\u50cf\u662f\u6ca1\u6709\u542c\u5230\u4ed6\u7684\u8bdd\uff0c\u4ecd\u81ea\u63a5\u7740\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u6211\u4eec\u8fd9\u6545\u4eba\u4e4b \u5973\u5df2\u62dc\u5728\u4f55\u4eba\u95e8\u4e0b\uff1f\u201d\u4ed6\u8bed\u58f0\u4e00\u987f\uff0c\u77e5\u9053\u5fc5\u5b9a\u5f97\u4e0d\u5230\u7b54\u590d\uff0c\u662f\u4ee5\u7acb\u523b\u63a5\u9053\uff1a\u201c\u5979\u7adf\u53c8\u62dc\u5728\u90a3 \u2018\u5c60\u9f99\u4ed9\u5b50\uff0c\u7684\u95e8\u4e0b\uff0c\u4f60\u53ef\u8bb0\u5f97\u4f60\u6211\u5728\u6606\u4ed1\u5c71\u4e0b\u542c\u5230\u7684\u90a3\u6bb5\u6545\u4e8b\uff0c\u54c8\u54c8\u2014\u2014\u6211\u4eca\u65e5\u7adf\u9047\u7740\u4e86 \u90a3\u6155\u5bb9\u60dc\u751f\uff0c\u8fd8\u548c\u5979\u5bf9\u4e86\u4e24\u638c\uff0c\u5979\u679c\u7136\u4e0d\u6562\u7834\u6212\u6740\u5341\u5e74\u4e4b\u6212\uff0c\u770b\u6765\u6606\u4ed1\u4e00\u6d3e\uff0c\u8fd1\u5e74\u6765\u867d\u5df2\u65e0 \u6614\u65e5\u4e4b\u76db\uff0c\u4f46\u5374\u4ecd\u672a\u53ef\u8f7b\u89c6\u5462\uff01\u201d\u90a3\u8001\u4eba\u76ee\u5149\u53c8\u81ea\u4e00\u4eae\uff0c\u957f\u957f\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u4ec7\u6055\u5374\u5df2\u5fcd\u4e0d \u4f4f\u95ee\u9053\uff1a\u201c\u8fd9\u2018\u5c60\u9f99\u4ed9\u5b50\u2019\u7a76\u7adf\u662f\u8c01\uff1f\u9053\u957f\u5728\u6606\u4ed1\u5c71\u4e0b\u542c\u5230\u7684\u53c8\u662f\u4f55\u4e8b\uff1f\u201d \u3000\u3000\u67f3\u590d\u660e\u8f6c\u9996\u671b\u4e86\u4ed6\u4e00\u773c\uff1a\u201c\u8bf4\u8d77\u90a3\u2018\u5c60\u9f99\u4ed9\u5b50\u2019\uff0c\u5012\u7684\u786e\u662f\u4f4d\u5973\u4e2d\u5947\u4eba\uff0c\u6570\u5341\u5e74\u524d\uff0c\u5979 \u672c\u662f\u4e2a\u72ec\u884c\u5973\u76d7\uff0c\u6b66\u529f\u7edd\u9ad8\uff0c\u4f46\u5374\u55dc\u6740\uff0c\u9ed1\u767d\u4e24\u9053\uff0c\u65e0\u8bba\u662f\u8c01\uff0c\u53ea\u8981\u649e\u5728\u5979\u624b\u91cc\uff0c\u88ab\u5979\u8f7b\u8f7b \u62cd\u4e0a\u4e00\u638c\uff0c\u7acb\u65f6\u4fbf\u662f\u9aa8\u5316\u9b42\u98de\u4e4b\u7978\uff0c\u7adf\u4ece\u65e0\u4e00\u4eba\u80fd\u9003\u5f97\u6d3b\u547d\u7684\u3002\u201d \u3000\u3000\u4ec7\u6055\u5fc3\u4e2d\u4e00\u52a8\uff01 \u3000\u3000\u201c\u5979\u4eec\u65bd\u51fa\u7684\u638c\u6cd5\uff0c\u5927\u7ea6\u4fbf\u662f\u9053\u957f\u65b9\u624d\u6240\u8bf4\u7684\u2018\u6bd2\u9f99\u638c\u4e86\uff0c\u3002\u201d\u67f3\u590d\u660e\u9894\u9996\u9053\uff1a\u201c\u662f \u4e86\uff0c\u767e\u5341\u5e74\u6765\uff0c\u6b66\u6797\u4e2d\u82e5\u8bba\u638c\u6cd5\u4e4b\u5947\uff0c\u5f53\u7136\u662f\u90a3\u7eb5\u6a2a\u5929\u4e0b\u7684\u524d\u8f88\u5f02\u4eba\u2018\u6d77\u5929\u5b64\u71d5\u2019\u6240\u4f7f\u7684 \u2018\u5316\u9aa8\u795e\u62f3\u201d\u82e5\u8bba\u638c\u6cd5\u4e4b\u6bd2\uff0c\u5374\u5c31\u5f97\u6570\u8fd9\u2019\u6bd2\u9f99\u638c\u2019\u4e86\uff0c\u8fd9\u2018\u6bd2\u9f99\u638c\u2019\u4e4b\u6bd2\uff0c\u6bd2\u5728\u522b\u4eba\u770b \u6765\uff0c\u638c\u98ce\u8f6f\u5f31\uff0c\u4f3c\u662f\u6beb\u4e0d\u8d77\u773c\uff0c\u4f46\u53ea\u8981\u6cbe\u7740\u4e00\u4e9b\uff0c\u4fbf\u65e0\u836f\u53ef\u6551\u3002\u201d\u4ed6\u5fae\u7b11\u4e00\u4e0b\uff0c\u63a5\u9053\uff1a\u201c\u662f \u4ee5\u65b9\u624d\u4f60\u82e5\u786c\u63a5\u4e86\u6155\u5bb9\u60dc\u751f\u90a3\u4e00\u638c\uff0c\u90a3\u4e48\u2014\u2014\u5509\uff0c\u4f60\u6b66\u529f\u867d\u9ad8\uff0c\u4f46\u4f60\u624b\u638c\u53ea\u8981\u88ab\u5979\u7684\u624b\u638c\u4f24 \u7740\u5c11\u8bb8\uff0c\u5927\u7ea6\u4e5f\u65e0\u6cd5\u5e78\u514d\u3002\u4ec7\u6055\u5fc3\u5934\u4e00\u51db\uff0c\u5374\u542c\u4ed6\u53c8\u63a5\u9053\uff1a\u201c\u5f53\u65f6\u6b66\u6797\u4e2d\u4eba\u4f24\u5728\u5979\u8fd9\u2018\u6bd2\u9f99 \u638c\u2019\u4e0b\u7684\uff0c\u4e0d\u77e5\u51e1\u51e0\uff0c\u90a3\u65f6\u6b66\u6797\u4e2d\u4eba\u5374\u53eb\u5979\u505a\u2018\u6bd2\u9f99\u9b54\u5973\u201d\u5c06\u5979\u6068\u5165\u5207\u9aa8\uff0c\u5374\u4e5f\u65e0\u53ef\u5948\u4f55\uff0c \u76f4\u5230\u4e00\u5929\uff0c\u5979\u7a81\u5730\u626c\u8a00\u5929\u4e0b\uff0c\u6b64\u540e\u7edd\u4e0d\u518d\u7528\u2019\u6bd2\u9f99\u638c\u201d\u81ea\u6b64\u4ee5\u540e\uff0c\u5979\u4e5f\u771f\u7684\u8c28\u5b88\u8bfa\u8a00\uff0c\u4e0d\u4f46 \u4e0d\u518d\u65bd\u90a3\u2018\u6bd2\u9f99\u638c\u6cd5\u201d\u800c\u4e14\u672a\u518d\u4f24\u8fc7\u4e00\u4eba\u4e4b\u547d\uff0c\u4e8e\u662f\u6b66\u6797\u4e2d\u4e3a\u7978\u6700\u70c8\u7684\u4e00\u6761\u2019\u6bd2\u9f99\u201d\u4ece\u6b64\u9664 \u53bb\uff0c\u800c\u5979\u7684\u540d\u5b57\u4e5f\u7531\u2018\u6bd2\u9f99\u9b54\u5973\u2019\u53d8\u4e3a\u2018\u5c60\u9f99\u4ed9\u5b50\u2019\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5fae\u5fae\u4e00\u7b11\uff0c\u8bed\u6c14\u4e2d\u751a\u4e3a\u8d5e\u4f69\uff01 \u3000\u3000\u201c\u6614\u65e5\u5468\u5904\u52c7\u9664\u4e09\u5bb3\uff0c\u4f20\u4e3a\u5343\u53e4\u7f8e\u8c08\uff0c\u8fd9\u2018\u5c60\u9f99\u4ed9\u5b50\uff0c\u7684\u884c\u5f84\uff0c\u4e5f\u6b63\u548c\u4ed6\u76f8\u5dee\u65e0\u51e0\u3002\u54c8 \u54c8\u2014\u2014\u6bd2\u9f99\u81ea\u5c60\uff0c\u6bd2\u9f99\u5c60\u9f99\uff0c\u8fd9\u2019\u5c60\u9f99\u4ed9\u5b50\u2019\u7684\u540d\u5b57\uff0c\u59d4\u5b9e\u7528\u5f97\u5999\u6781\uff01\u201d \u3000\u3000\u62ac\u8d77\u5934\u6765\uff0c\u4ed6\u53c8\u5982\u957f\u9cb8\u5438\u6c34\u822c\uff0c\u559d\u4e86\u4e00\u5927\u53e3\u9152\uff0c\u8bed\u6c14\u4e4b\u4e2d\uff0c\u5bf9\u90a3\u201c\u5c60\u9f99\u4ed9\u5b50\u201d\u6570\u5341\u5e74\u524d \u7684\u82f1\u98ce\u8c6a\u4e3e\uff0c\u5140\u81ea\u503e\u670d\u65e0\u5df2\u3002 \u7b2c\u5341\u516d\u7ae0>> \u53e4\u9f99\u300a\u6e58\u5983\u5251\u300b \u7b2c\u5341\u516d\u7ae0 \u3000\u3000\u4ec7\u6055\u76ee\u5149\u4e00\u8f6c\uff0c\u7a81\u53c8\u95ee\u9053\uff1a\u201c\u95fb\u9053\u8fd9\u5c60\u9f99\u4ed9\u5b50\u4e0d\u4f46\u6b66\u529f\u6781\u7cbe\uff0c\u5c24\u5176\u73cd\u5947\u73a9\u7269\uff0c\u5bf9\u7ec3\u5251\u4e00 \u9053\uff0c\u4ea6\u591a\u5999\u8c1b\uff0c\u4e0d\u77e5\u662f\u5426\uff1f\u201d \u3000\u3000\u67f3\u590d\u660e\u9894\u9996\u4e00\u7b11\uff1a\u201c\u8fd9\u5c60\u9f99\u4ed9\u5b50\u867d\u559c\u73a9\u7269\uff0c\u5374\u4e0d\u4e27\u5fd7\uff0c\u81f3\u4e8e\u7ec3\u5251\u4e00\u9053\u4e48\u2014\u2014\u6211\u5374\u4ece\u672a\u542c \u4eba\u8bf4\u8d77\uff0c\u4f46\u4f3c\u5979\u8fd9\u822c\u5929\u7eb5\u5947\u624d\uff0c\u7ec3\u5251\u60f3\u4ea6\u5fc5\u975e\u96be\u4e8b\uff01\u201d \u3000\u3000\u4ec7\u6055\u5251\u76fe\u4e00\u6380\uff0c\u6025\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u9053\u957f\u53ef\u66fe\u77e5\u9053\u8fd9\u5c60\u9f99\u4ed9\u5b50\u6240\u5236\u7684\u4e00\u67c4\u2018\u7425\u73c0\u795e\u5251\u2019 \u4e48\uff1f\u201d \u3000\u3000\u67f3\u590d\u660e\u5fae\u4e00\u76b1\u7709\uff0c\u4fef\u9053\u6c89\u541f\uff1a\u201c\u7425\u73c0\u795e\u5251\u2026\u2026\u8fd9\u4e2a\uff0c\u6211\u4e5f\u672a\u66fe\u542c\u4eba\u8bf4\u8d77\u3002\u201d \u3000\u3000\u4ec7\u6055\u957f\u957f\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u795e\u6001\u4e4b\u95f4\uff0c\u4f3c\u662f\u9887\u4e3a\u5931\u671b\uff0c\u67f3\u590d\u660e\u76ee\u5149\u95ea\u52a8\uff0c\u4e0a\u4e0a\u4e0b\u4e0b\u5c06\u4ed6\u6253 \u91cf\u4e86\u51e0\u773c\uff0c\u7a81\u5730\u653e\u58f0\u7b11\u9053\uff1a\u201c\u8d2b\u9053\u6b64\u6b21\u91cd\u8fd4\u6c5f\u5357\uff0c\u5f97\u4ee5\u7ed3\u8bc6\u9601\u4e0b\u8fd9\u7b49\u4eba\u4e2d\u4fca\u5f66\uff0c\u5b9e\u5728\u4e00\u5927\u4e50 \u4e8b\uff0c\u9601\u4e0b\u5982\u4e0d\u5acc\u8d2b\u9053\u5192\u6627\uff0c\u4e0d\u77e5\u53ef\u5426\u5c06\u5927\u540d\u89c1\u544a\uff1f\u201d \u3000\u3000\u4ec7\u6055\u5fae\u7b11\u4e00\u4e0b\uff0c\u6bcf\u5f53\u4eba\u4eec\u95ee\u8d77\u4ed6\u540d\u5b57\u7684\u65f6\u5019\uff0c\u4ed6\u5fc3\u91cc\u5c31\u4f1a\u4e0d\u81ea\u89c9\u5730\u5f15\u8d77\u4e00\u9635\u5947\u5f02\u7684\u611f \u89c9\uff0c\u4ed6\u591a\u4e48\u60f3\u633a\u8d77\u80f8\u819b\u544a\u8bc9\u522b\u4eba\uff0c\u4ed6\u5c31\u662f\u6614\u5e74\u7eb5\u6a2a\u6b66\u6797\u7684\u201c\u4ec7\u5148\u751f\u201d\u7684\u513f\u5b50\uff0c\u4f46\u662f\uff0c\u4e3a\u4e86\u8bb8 \u591a\u7f18\u56e0\uff0c\u4ed6\u5374\u53c8\u4e0d\u80fd\u5982\u6b64\uff0c\u6b64\u523b\u4ed6\u53c8\u53ea\u5f97\u6697\u53f9\u4e00\u58f0\uff0c\u5374\u542b\u7b11\u9053\uff1a\u201c\u5c0f\u53ef\u7f2a\u6587\uff0c\u788c\u788c\u51e1\u592b\uff0c\u9053 \u957f\u7684\u8c2c\u8bb8\uff0c\u5c0f\u53ef\u5b9e\u5728\u62c5\u5f53\u4e0d\u8d77\u3002\u201d \u3000\u3000\u67f3\u590d\u660e\u5fae\u5fae\u4e00\u7b11\uff0c\u8fd8\u672a\u7b54\u8bdd\uff0c\u90a3\u59cb\u7ec8\u4e00\u65c1\u9759\u5750\u51dd\u542c\u7684\u8001\u4eba\uff0c\u7a81\u5730\u957f\u53f9\u4e00\u58f0\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a \u201c\u788c\u788c\u51e1\u592b\u2014\u2014\u5509\uff0c\u6211\u624d\u662f\u4e2a\u788c\u788c\u51e1\u592b\uff0c\u5c06\u6570\u5341\u5e74\u5927\u597d\u5c81\u6708\uff0c\u7b49\u95f2\u865a\u5ea6\uff01\u201d\u4ed6\u76ee\u5149\u7a81\u53c8\u4e00 \u4eae\uff0c\u7709\u5b57\u95f4\u610f\u5174\u98de\u626c\uff0c\u63a5\u9053\uff1a\u201c\u4f46\u8001\u592b\u81ea\u95ee\u53cc\u76ee\u4e0d\u76f2\uff0c\u6570\u5341\u5e74\u6765\uff0c\u66fe\u8bc6\u5f97\u51e0\u4e2a\u4fca\u6770\u4eba\u7269\uff0c\u9601 \u4e0b\u4f60\u4e5f\u4e0d\u5fc5\u8fc7\u8c26\uff0c\u8001\u592b\u8db3\u8ff9\u904d\u4e8e\u5929\u4e0b\uff0c\u50cf\u9601\u4e0b\u8fd9\u7b49\u4eba\u7269\uff0c\u5374\u5b9e\u5728\u672a\u66fe\u89c1\u8fc7\uff0c\u5509\u2014\u2014\u5341\u4e03\u5e74 \u524d\uff0c\u8001\u592b\u65e0\u5fc3\u94f8\u9519\uff0c\u7ec8\u751f\u8d1f\u759a\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u6211\u867d\u60f3\u5bf9\u6b64\u4e8b\u6de1\u5fd8\uff0c\u4e5f\u786e\u5b9e\u6de1\u5fd8\u8bb8\u591a\uff0c\u4f46\u4eca\u65e5\u2014 \u2014\u201d\u4ed6\u6c89\u91cd\u5730\u53f9\u606f\u4e00\u58f0\uff0c\u65b9\u81ea\u63a5\u9053\uff1a\u201c\u4eca\u65e5\u6211\u89c1\u4e86\u9601\u4e0b\uff0c\u5374\u4e0d\u77e5\u600e\u5730\uff0c\u53ea\u89c9\u5f80\u4e8b\u5982\u6f6e\u800c\u6765\uff0c \u751f\u751f\u4e0d\u5df2\uff0c\u4e0d\u53ef\u65ad\u7edd\uff0c\u5509\uff01\u4eba\u751f\u51e0\u4f55\uff0c\u8b6c\u5982\u671d\u9732\uff0c\u4f60\u6211\u840d\u6c34\u76f8\u9022\uff0c\u8001\u592b\u6bd4\u4f60\u75f4\u957f\u51e0\u5c81\uff0c\u4f46\u6709 \u4e00\u8a00\u5949\u8d60\uff0c\u5509\uff01\u5f97\u9976\u4eba\u5904\u4e14\u9976\u4eba\uff0c\u83ab\u5c06\u950b\u8292\u592a\u9732\uff0c\u83ab\u5c06\u950b\u8292\u592a\u9732\u2014\u2014\u201d\u4ed6\u91cd\u590d\u4ed6\u8bf4\u7740\uff0c\u8bed\u6c14 \u8d8a\u6765\u8d8a\u4f4e\uff0c\u4ec7\u6055\u76ee\u5149\u4f4e\u5782\uff0c\u671b\u7740\u5149\u7130\u6e10\u5f31\u7684\u706b\u5149\uff0c\u5fc3\u4e2d\u7a81\u4e5f\u5174\u8d77\u4e00\u79cd\u5982\u4e1d\u5982\u7f15\uff0c\u4e0d\u53ef\u65ad\u7edd\u7684 \u5fe7\u601d\uff0c\u4ed6\u7ec6\u7ec6\u5730\u4f53\u5473\u7740\u8fd9\u8001\u4eba\u7684\u8bdd\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u7adf\u53c8\u5446\u5446\u5730\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u53ea\u542c\u5f97\u201c\u5484\u201d\u5730\u4e00\u58f0\uff0c\u67f3\u590d\u660e\u4ee5\u7b77\u51fb\u9505\uff0c\u653e\u58f0\u6b4c\u9053\uff1a\u201c\u5c06\u8fdb\u9152\uff0c\u676f\u83ab\u505c\u2014\u2014\u53e4\u6765\u5723\u8d24\u591a \u5bc2\u5bde\uff0c\u552f\u6709\u996e\u8005\u7559\u5176\u540d\uff0c\u529d\u541b\u4e14\u996e\u4e00\u676f\u9152\uff0c\u83ab\u8bb0\u5f80\u4e8b\u6101\u4e0d\u5174\uff0c\u5373\u4eca\u4eba\u751f\u767b\u8004\u8006\uff0c\u5fe7\u4e50\u4e2d\u5206\u672a \u767e\u5e74\uff0c\u6709\u9152\u5f53\u996e\u76f4\u987b\u996e\uff0c\u4f55\u5fc5\u5bf9\u9152\u7a7a\u81ea\u601c\uff0c\u6765\u6765\u6765\u2014\u2014\u201d\u4ed6\u4e00\u624b\u4e3e\u8d77\u90a3\u6ee1\u888b\u70c8\u9152\uff0c\u9001\u5230\u4ec7\u6055 \u9762\u524d\uff0c\u653e\u58f0\u7b11\u9053\uff1a\u201c\u4e14\u996e\u4e00\u676f\u6d88\u6101\u9152\uff0c\u6211\u6765\u821e\u5251\u52a9\u541b\u5174\u3002\u201d\u4e00\u62c2\u888d\u8896\uff0c\u957f\u8eab\u800c\u8d77\uff0c\u968f\u624b\u62bd\u51fa \u4e00\u6bb5\u5c1a\u672a\u71c3\u5c3d\u7684\u67f4\u706b\uff0c\u624b\u8155\u4e00\u6296\uff0c\u706b\u661f\u6f2b\u5929\uff0c\u811a\u6b65\u7a81\u5730\u4e00\u6ed1\uff0c\u968f\u624b\u4e00\u5288\uff0c\u7adf\u7136\u4ee5\u6728\u4f5c\u5251\uff0c\u65cb \u8eab\u800c\u821e\uff0c\u4ec7\u6055\u5446\u5446\u5730\u63a5\u8fc7\u4ed6\u9012\u6765\u7684\u7f8a\u76ae\u9152\u56ca\uff0c\u53ea\u89c1\u4ed6\u888d\u8896\u98de\u62c2\uff0c\u67f4\u679d\u70b9\u70b9\uff0c\u9762\u4e0a\u5374\u5df2\u6362\u4e86\u4e00 \u8138\u8083\u7a46\u4e4b\u8272\uff0c\u8fdb\u8eab\u9000\u6b65\uff0c\u4e00\u4e1d\u4e0d\u82df\uff0c\u4e60\u6b66\u4e4b\u4eba\u5bf9\u4e8e\u7ec8\u751f\u52e4\u7ec3\u7684\u6b66\u529f\uff0c\u672c\u90fd\u6709\u4e00\u4efd\u65e0\u53ef\u6bd4\u62df\u7684 \u5d07\u656c\u3002 \u3000\u3000\u4ed6\u624b\u4e2d\u67f4\u679d\u5c06\u7184\u672a\u7184\uff0c\u6b64\u523b\u88ab\u4ed6\u65cb\u8eab\u821e\u6765\uff0c\u5239\u90a3\u4e4b\u95f4\uff0c\u4fbf\u5df2\u5316\u505a\u4e00\u56e2\u706b\u5f71\uff0c\u4ec7\u6055\u4ef0\u9996\u6ee1 \u996e\u4e00\u53e3\u5173\u5916\u70c8\u9152\uff0c\u4f46\u89c9\u5fc3\u4e2d\u5757\u5792\uff0c\u5df2\u81ea\u6d88\u53bb\u4e0d\u5c11\uff0c\u5fc3\u80f8\u4e4b\u95f4\uff0c\u70ed\u8840\u6cb8\u817e\uff0c\u5374\u89c1\u90a3\u5782\u76ee\u800c\u5750\u7684 \u8001\u4eba\uff0c\u7adf\u81ea\u6717\u7b11\u4e00\u58f0\uff0c\u957f\u8eab\u800c\u8d77\uff0c\u4ea6\u81ea\u62bd\u51fa\u4e00\u6bb5\u5c1a\u672a\u71c3\u5c3d\u7684\u677e\u679d\uff0c\u968f\u624b\u4e00\u6296\uff0c\u6f2b\u5929\u706b\u661f\u4e2d\uff0c \u53ea\u89c1\u4ed6\u7626\u524a\u7684\u8eab\u5f62\uff0c\u5b9b\u5982\u4e00\u53ea\u7070\u9e64\uff0c\u51b2\u5929\u800c\u8d77\uff0c\u659c\u659c\u63a0\u51fa\u4e24\u4e08\uff0c\u51e0\u5df2\u63a0\u81f3\u5c4b\u9876\uff0c\u7136\u540e\u8f6c\u6298\u800c \u4e0b\uff0c\u6296\u624b\u4e00\u5251\uff0c\u5411\u90a3\u56e2\u706b\u5f71\u4e2d\u523a\u53bb\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u6614\u65e5\u4e5f\u66fe\u53f1\u54a4\u6b66\u6797\u7684\u540d\u5251\u624b\uff0c\u5341\u516d\u5e74\u6765\uff0c\u843d\u62d3\u6c5f\u6e56\uff0c\u5404\u5404\u5fc3\u4e2d\uff0c\u672c\u90fd\u79ef\u90c1\u7740\u96be\u6d88 \u7684\u5757\u5792\uff0c\u5728\u90a3\u96c4\u58ee\u82cd\u51c9\u7684\u9752\u6d77\u8349\u539f\u4e2d\uff0c\u5bbd\u9614\u6f20\u51a5\u7684\u8499\u53e4\u6c99\u6f20\u91cc\uff0c\u843d\u65e5\u659c\u9633\u7684\u4e07\u91cc\u957f\u57ce\u4e0b\uff0c\u5c61 \u60ca\u80e1\u9a6c\u7684\u5cf0\u706b\u58a9\u53f0\u4e0a\u2026\u2026\u867d\u4e5f\u66fe\u4f7f\u9152\u9ad8\u6b4c\uff0c\u51fb\u7504\u4f4e\u5531\uff0c\u4f46\u5374\u4ece\u672a\u6709\u5982\u4eca\u65e5\u822c\uff0c\u7adf\u5728\u8fd9\u65b9\u5706\u4e0d \u8fc7\u6570\u4e08\u7684\u8352\u7960\u5e9f\u6bbf\u4e2d\uff0c\u4ee5\u67f4\u4f5c\u5251\uff0c\u4ee5\u5251\u76f8\u51fb\uff0c\u5bf9\u821e\u8d77\u6765\u3002 \u3000\u3000\u201c\u5df4\u5c71\u5251\u5ba2\u2019\u67f3\u590d\u660e\u53ea\u89c1\u4e00\u56e2\u7070\u5f71\uff0c\u51cc\u7a7a\u800c\u4e0b\uff0c\u4ed6\u5341\u4e03\u5e74\u6765\uff0c\u5c3d\u655b\u950b\u8292\uff0c\u4ece\u672a\u548c\u4e00\u4eba\u6709 \u8fc7\u4e00\u5251\u4e4b\u4ea4\uff0c\u6b64\u523b\u5fc3\u80f8\u95f4\u4f46\u89c9\u8c6a\u5174\u9038\u98de\uff0c\u6717\u7b11\u4e00\u58f0\uff0c\u8eab\u5f62\u659c\u8f6c\uff0c\u7a81\u5730\u6296\u624b\u4e00\u5251\uff0c\u67f4\u5316\u98de\u8679\uff0c \u5411\u90a3\u51cc\u7a7a\u800c\u4e0b\u7684\u8001\u4eba\u523a\u53bb\uff0c\u53e3\u4e2d\u4e00\u9762\u6717\u7b11\u9053\uff1a\u201c\u9752\u840d\u5251\u6728\u728a\u85cf\u73e0\uff0c\u5341\u4e03\u5e74\u4ece\u672a\u52a8\u8fc7\u5982\u6b64\u8c6a \u5174\uff0c\u5420\u5420\uff01\u4e14\u5403\u6211\u4e00\u62db\u3002\u201d \u3000\u3000\u8fd9\u8001\u4eba\u4e0d\u95ee\u53ef\u77e5\uff0c\u81ea\u7136\u5c31\u662f\u5341\u4e03\u5e74\u524d\uff0c\u542b\u6068\u9690\u53bb\u7684\u6c5f\u5357\u5927\u4fa0\u201c\u9752\u840d\u5251\u201d\u5b8b\u4ee4\u516c\uff0c\u6b64\u523b\u4ed6 \u4ea6\u81ea\u6717\u58f0\u4e00\u7b11\uff0c\u5927\u7b11\u9053\uff1a\u201c\u597d\u4e00\u62db\u2018\u6625\u98ce\u52a8\u67f3\u7b2c\u4e00\u6280\u2019\uff0c\u60f3\u4e0d\u5230\u6211\u4e0e\u4f60\u6570\u5341\u5e74\u76f8\u4ea4\uff0c\u5230\u5934\u6765 \u8fd8\u662f\u8981\u5c1d\u5c1d\u4f60\u8fd9\u2018\u4e03\u4e03\u56db\u5341\u4e5d\u5f0f\u56de\u98ce\u821e\u67f3\u5251\u3002\u2019\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u8eab\u968f\u5251\u8d70\uff0c\u67f4\u679d\u5e7b\u5f71\uff0c\u5f71\u5e7b\u5343 \u70b9\uff0c\u5530\u5730\uff0c\u4ea6\u81ea\u653b\u51fa\u4e00\u5251\u3002 \u3000\u3000\u8fd9\u957f\u624d\u76c8\u5c3a\u7684\u4e00\u6bb5\u67f4\u679d\uff0c\u6b64\u523b\u5230\u4e86\u8fd9\u201c\u9752\u840d\u5251\u201d\u5b8b\u4ee4\u516c\u624b\u4e2d\uff0c\u7adf\u50cf\u5df2\u53d8\u4f5c\u4e09\u5c3a\u9752\u950b\uff0c\u5343 \u70b9\u5251\u5149\uff0c\u4ff1\u5411\u90a3\u201c\u5df4\u5c71\u5251\u5ba2\u201d\u67f3\u590d\u660e\u6d8c\u53bb\u3002 \u3000\u3000\u67f3\u590d\u660e\u5927\u7b11\u4e00\u58f0\uff1a\u201c\u6211\u4e00\u62db\u2018\u6625\u98ce\u52a8\u67f3\u2019\uff0c\u6362\u6765\u4f60\u4e00\u62db\u2018\u6c34\u52a8\u6d6e\u840d\u2019\uff0c\u54c8\u54c8\uff0c\u5999\u6781\uff0c\u5999 \u6781\u2014\u2014\u201d\u624b\u8155\u4e00\u65cb\uff0c\u638c\u4e2d\u67f4\u679d\uff0c\u500f\u5730\u5212\u4e86\u4e2a\u534a\u5708\uff0c\u5e73\u5e73\u6325\u8d77\uff0c\u5411\u4e0a\u4e00\u683c\uff0c\u8fd9\u4e00\u683c\u521a\u4e2d\u5e26\u67d4\uff0c \u7adf\u5c06\u5b8b\u4ee4\u516c\u51fb\u6765\u7684\u5e72\u70b9\u67f4\u679d\uff0c\u5177\u90fd\u5c01\u5728\u5916\u95e8\uff0c\u6b63\u662f\u201c\u5df2\u5c71\u5251\u5ba2\u201d\u67f3\u590d\u660e\u4ed7\u4ee5\u6210\u540d\u7684\u201c\u56de\u98ce\u821e \u67f3\u201d\u5251\u4e2d\uff0c\u7d27\u63a5\u7740\u7b2c\u4e00\u62db\u653b\u52bf\u201c\u6625\u98ce\u52a8\u67f3\u201d\u7684\u7b2c\u4e8c\u62db\u5b88\u52bf\u201c\u67f3\u679d\u5f39\u98ce\u201d\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u5341\u4e03\u5e74\u6765\uff0c\u5e76\u80a9\u9080\u6e38\uff0c\u65e9\u5df2\u7ed3\u6210\u751f\u6b7b\u77e5\u5df2\uff0c\u4f46\u6570\u5341\u5e74\u6765\uff0c\u8fd9\u4e24\u4e2a\u4ff1\u662f\u4ee5\u8f7b\u7075\u5de7\u5feb \u7684\u5251\u6cd5\u6210\u4e3a\u6b66\u6797\u7684\u5251\u624b\uff0c\u5f7c\u6b64\u4e4b\u95f4\uff0c\u5374\u8c01\u4e5f\u4e0d\u77e5\u9053\u5bf9\u65b9\u6b66\u529f\u7684\u6df1\u6d45\uff0c\u6b64\u523b\u67f3\u590d\u660e\u4e00\u5251\u5f39\u6765\uff0c \u5b8b\u4ee4\u516c\u6697\u4e2d\u4e00\u53f9\uff1a\u201c\u679c\u771f\u662f\u540d\u5bb6\u8eab\u624b\uff01\u201d\u5251\u5230\u4e2d\u9014\uff0c\u624b\u8098\u4e00\u66f2\uff0c\u638c\u4e2d\u6811\u5251\uff0c\u7a81\u5730\u53d8\u4e86\u4e2a\u65b9 \u5411\uff0c\u65cb\u5251\u5411\u5de6\uff0c\u7a81\u53c8\u7531\u5de6\u81f3\u53f3\uff0c\u201c\u6c34\u5f71\u840d\u8e2a\u201d\uff0c\u4e24\u5251\u867d\u672a\u76f8\u4ea4\uff0c\u67f3\u590d\u660e\u4f46\u89c9\u81ea\u5df1\u4f7f\u51fa\u7684\u4e00 \u62db\uff0c\u5168\u65e0\u7740\u529b\u4e4b\u5904\uff0c\u800c\u5b8b\u4ee4\u516c\u4e00\u62db\u201c\u840d\u5f71\u4e07\u70b9\u201d\uff0c\u5374\u53c8\u5316\u505a\u4e00\u7247\u9eef\u7070\u5149\u5f71\uff0c\u5f53\u5934\u51fb\u6765\u3002 \u3000\u3000\u4ed6\u4e24\u4eba\u52a8\u624b\u4e4b\u521d\uff0c\u81ea\u90fd\u662f\u6e38\u620f\u6587\u7ae0\uff0c\u4f46\u6b64\u523b\u4e24\u4eba\u53cc\u5251\u4e00\u4ea4\uff0c\u540e\u8005\u7acb\u523b\u7ef5\u7ef5\u800c\u81f3\uff0c\u8c01\u4e5f\u4e0d \u80fd\u601d\u7d22\u8fdf\u7591\u534a\u5206\uff0c\u5b8b\u4ee4\u516c\u4e00\u5251\u51fb\u4e0b\uff0c\u67f3\u590d\u660e\u626c\u5251\u53cd\u524a\uff0c\u5530\u5730\u5411\u4ed6\u638c\u6307\u4e4b\u95f4\uff0c\u8981\u77e5\u9053\u6b64\u523b\u4e24\u4eba \u4ff1\u662f\u4ee5\u67f4\u4f5c\u5251\uff0c\u662f\u4ee5\u4fbf\u6ca1\u6709\u62a4\u624b\u4e4b\u7269\uff0c\u67f3\u590d\u660e\u8fd9\u4e00\u5251\u70b9\u5251\u524a\u6765\uff0c\u6b63\u81ea\u653b\u654c\u4e4b\u6240\u5fc5\u6551\uff0c\u5b8b\u4ee4\u516c \u6811\u5251\u4e00\u6325\uff0c\u8eab\u968f\u5251\u8d70\uff0c\u63d0\u5251\u4e0a\u64a9\uff0c\u67f3\u590d\u660e\u4e00\u5251\u843d\u7a7a\uff0c\u5bf9\u65b9\u5374\u5df2\u56de\u5251\u5241\u6765\uff0c\u5f53\u4e0b\u4e0d\u5f97\u4e0d\u64a4\u62db\u81ea \u6551\uff0c\u4e24\u4eba\u8fd9\u4e00\u756a\u76f8\u4e89\uff0c\u867d\u65e0\u4ec7\u6028\uff0c\u66f4\u65e0\u7f18\u7531\uff0c\u4f46\u6b64\u523b\u5404\u65bd\u7edd\u6280\uff0c\u5374\u4e5f\u6597\u5f97\u751a\u662f\u51f6\u9669\u3002 \u3000\u3000\u5385\u4e2d\u7684\u706b\u7130\uff0c\u88ab\u4ed6\u4eec\u65b9\u624d\u62bd\u53bb\u4e24\u679d\u57fa\u5c42\u7684\u67f4\u6728\uff0c\u6b64\u523b\u706b\u52bf\u5df2\u66f4\u6e10\u5fae\u5f31\uff0c\u4ed6\u4e24\u4eba\u624b\u4e2d\u7684\u67f4 \u679d\uff0c\u5374\u56e0\u4e0d\u505c\u5730\u98de\u821e\uff0c\u800c\u59cb\u7ec8\u4fdd\u6301\u7740\u70bd\u70ed\u7684\u706b\u5149\uff0c\u9752\u840d\u5251\u5b8b\u4ee4\u516c\u4f4e\u5578\u4e00\u58f0\uff0c\u7a81\u5730\u8fde\u6325\u4e09\u5251\uff0c \u67f3\u590d\u660e\u5251\u8d70\u8f7b\u7075\uff0c\u4e00\u4e00\u6d88\u53bb\uff0c\u7a81\u5730\u4e00\u5251\u56de\u65cb\uff0c\u4e24\u5251\u76f8\u4ea4\uff0c\u53ea\u542c\uff1a\u5657\u201d\u5730\u4e00\u58f0\uff0c\u5b8b\u4ee4\u516c\u638c\u4e2d\u7684 \u6811\u5251\uff0c\u7adf\u65ad\u4e86\u4e00\u8282\uff0c\u70b9\u70b9\u706b\u661f\uff0c\u6f2b\u5929\u98d8\u4e0b\uff0c\u5fc3\u4e2d\u65b9\u81ea\u4e00\uff0c\u60ca\uff0c\u5374\u89c1\u67f3\u590d\u660e\u64a4\u5251\u56de\u8eab\uff0c\u54c8\u54c8\u7b11 \u9053\uff1a\u201c\u60f3\u4e0d\u5230\uff0c\u60f3\u4e0d\u5230\uff0c\u9752\u840d\u5251\u7adf\u53d8\u505a\u706b\u840d\u5251\u4e86\u3002\u201d\u624b\u638c\u4e00\u626c\uff0c\u638c\u4e2d\u67f3\u5251\uff0c\u8131\u624b\u98de\u53bb\uff0c\u2018\u4f60 \u8fd9\u706b\u840d\u5251\u8981\u662f\u628a\u6211\u80e1\u5b50\u70e7\u6389\uff0c\u770b\u4f60\u600e\u5730\u8d54\u5f97\u8d77\uff1f\u2019\u968f\u624b\u62c2\u843d\u4e24\u70b9\u6cbe\u5728\u4ed6\u988a\u4e0b\u767d\u987b\u4e0a\u7684\u706b\u661f\uff0c \u539f\u6765\u65b9\u624d\u706b\u679d\u65ad\u843d\uff0c\u706b\u661f\u98de\u626c\uff0c\u7adf\u6709\u4e24\u70b9\u843d\u5728\u4ed6\u7684\u957f\u987b\u4e0a\u3002 \u3000\u3000\u5b8b\u4ee4\u516c\u76ee\u5149\u52a8\u5904\uff0c\u4ea6\u4e0d\u7981\u54c8\u54c8\u5927\u7b11\u8d77\u6765\uff0c\u4ea6\u81ea\u629b\u53bb\u67f4\u679d\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u6211\u8fd9\u7b49\u62fc\u6597\uff0c\u65c1\u4eba \u89c1\u4e86\uff0c\u672c\u5df2\u8981\u8bf4\u6211\u4eec\u662f\u8fd4\u8001\u8fd8\u7ae5\u4e86\uff0c\u70e7\u53bb\u4f60\u7684\u987b\u5b50\uff0c\u5c82\u975e\u66f4\u8981\u597d\u4e9b\u3002\u201d\u76ee\u5149\u4e00\u8f6c\uff1a\u201c\u4f60\u8bf4\u53ef \u662f\uff1f\u201d \u3000\u3000\u4ed6\u8fd9\u6700\u540e\u4e00\u53e5\u8bdd\uff0c\u4e43\u662f\u5bf9\u4ec7\u6055\u8bf4\u7684\uff0c\u54ea\u77e5\u4ed6\u76ee\u5149\u8f6c\u5904\uff0c\u5385\u4e2d\u5374\u5df2\u7a7a\u7a7a\uff0c\u54ea\u91cc\u8fd8\u6709\u4ec7\u6055\u7684 \u5f71\u5b50\u3002 \u3000\u3000\u5b8b\u4ee4\u516c\u4e00\u6014\uff0c\u9053\uff1a\u201c\u90a3\u5c11\u5e74\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u67f3\u590d\u660e\u76ee\u5149\u56db\u4e0b\u4e00\u626b\uff0c\u795e\u8272\u4e4b\u95f4\uff0c\u4ea6\u6014\u4e86\u4e00\u6014\uff0c\u6447\u9996\u9053\uff1a\u201c\u6211\u8fde\u4ed6\u4f55\u65f6\u8d70\u7684\u90fd\u4e0d\u77e5 \u9053\u3002\u201d \u3000\u3000\u4ed6\u4e24\u4eba\u4ff1\u662f\u5185\u5916\u517c\u4fee\u7684\u6b66\u6797\u9ad8\u624b\uff0c\u65b9\u624d\u867d\u56e0\u5f7c\u6b64\u6fc0\u6597\u4e4b\u4e2d\uff0c\u662f\u4ee5\u65e0\u6687\u65c1\u987e\uff0c\u4f46\u82e5\u80fd\u5728\u4ed6 \u4eec\u773c\u4e0b\u968f\u610f\u8d70\u52a8\uff0c\u800c\u4e0d\u88ab\u4ed6\u4eec\u89c9\u5bdf\uff0c\u8fd9\u4efd\u8eab\u624b\uff0c\u4ea6\u975e\u5e38\u4eba\u6240\u80fd\u4f01\u53ca\uff0c\u6b64\u523b\u4ed6\u4e24\u4eba\u9762\u9762\u76f8\u89d1\uff0c \u5b8b\u4ee4\u516c\u9053\uff1a\u201c\u8fd9\u5c11\u5e74\u500f\u7136\u800c\u6765\uff0c\u500f\u7136\u800c\u53bb\uff0c\u5012\u771f\u6709\u4e9b\u5947\u602a\u3002\u201d\u4ed6\u8bed\u58f0\u4e00\u987f\uff0c\u7709\u5cf0\u53c8\u81ea\u5fae\u76b1\uff0c \u63a5\u9053\uff1a\u201c\u65b9\u624d\u6211\u4e00\u89c1\u7740\u6b64\u4eba\uff0c\u4fbf\u4f3c\u4e4e\u89c9\u5f97\u5fc3\u4e2d\u4e0d\u5b9a\uff0c\u672c\u60f3\u7a0d\u5f85\u518d\u7559\u610f\u67e5\u770b\u7684\u6765\u5386\uff0c\u54ea\u77e5\u2014\u2014 \u5509\uff0c\u4ed6\u7adf\u7a81\u5730\u8d70\u4e86\u3002\u201d \u3000\u3000\u67f3\u590d\u660e\u4ea6\u5947\u9053\uff1a\u201c\u8fd9\u5c11\u5e74\u7684\u786e\u6709\u4e9b\u5947\u602a\uff0c\u65b9\u624d\u5728\u9662\u4e2d\u4ed6\u867d\u672a\u51fa\u624b\uff0c\u4f46\u8eab\u5f62\u8d70\u52a8\u95f4\uff0c\u8f7b\u5de7 \u4eff\u4f5b\u5999\u5230\u6beb\u98a0\uff0c\u7adf\u4f3c\u8fd8\u5728\u4f60\u6211\u4e4b\u4e0a\uff0c\u4ed6\u5e74\u7eaa\u770b\u6765\u6700\u591a\u5728\u5f31\u51a0\u4e4b\u95f4\uff0c\u5374\u5df2\u6709\u8fd9\u7b49\u8eab\u624b\uff0c\u800c\u4e14\u6c14 \u5ea6\u4ece\u5bb9\uff0c\u795e\u60c5\u8f69\u6602\uff0c\u4e0d\u77e5\u662f\u8c01\u5bb6\u7236\u6bcd\uff0c\u7adf\u6709\u5982\u6b64\u4f73\u5b50\u5f1f\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u5fae\u987f\uff0c\u7a81\u53c8\u653e\u58f0\u4e00\u7b11\uff1a\u201c\u6b64\u4eba\u867d\u7136\u5947\u602a\uff0c\u4f46\u5374\u4e0e\u4f60\u6211\u65e0\u5173\uff0c\u4f60\u53c8\u4f55\u82e6\u5fc3\u4e2d\u4e0d\u5b9a\uff0c \u8fd9\u4e9b\u5e74\u6765\uff0c\u4f60\u600e\u5730\u4e5f\u5e38\u5e38\u4f5c\u8d77\u675e\u4eba\u4e4b\u5fe7\u6765\uff0c\u8fd9\u624d\u53eb\u6211\u5947\u602a\u54e9\uff01\u201d \u3000\u3000\u5b8b\u4ee4\u516c\u957f\u53f9\u9053\uff1a\u201c\u5f80\u4e8b\u4f24\u4eba\uff0c\u6211\u5fc3\u4e2d\u5b9e\u5728\u8d1f\u759a\u826f\u591a\uff0c\u60f3\u90a3\u2014\u2014\u5509\uff0c\u5341\u4e03\u5e74\uff0c\u5341\u4e03\u5e74\u5c81\u6708 \u867d\u7136\u60a0\u957f\uff0c\u4f46\u5982\u4eca\u6211\u7791\u76ee\u601d\u4e4b\uff0c\u90a3\u521a\u5f3a\u6124\u6012\u7684\u9762\u5bb9\uff0c\u7adf\u4eff\u4f5b\u8fd8\u5728\u6211\u8fd8\u773c\u524d\uff0c\u4ed6\u751f\u5e73\u6076\u884c\u867d\u6709 \u4e0d\u5c11\uff0c\u4f46\u4e8e\u4eca\u6211\u4ed4\u7ec6\u60f3\u6765\uff0c\u6614\u5e74\u6b7b\u5728\u4ed6\u624b\u4e0b\u4e4b\u4eba\uff0c\u7684\u786e\u4e5f\u4e0d\u662f\u5168\u65e0\u81f4\u6b7b\u4e4b\u9053\u3002\u201d \u3000\u3000\u67f3\u590d\u660e\u7b11\u5bb9\u987f\u655b\uff0c\u5782\u9996\u4e00\u53f9\uff1a\u201c\u5f80\u4e8b\u5df2\u77e3\uff0c\u4f60\u4f55\u82e6\u8fd8\u5728\u78e8\u6298\u81ea\u5df1\uff0c\u90a3\u4e8b\u6211\u53c8\u4e0d\u662f\u672a\u66fe\u53c2 \u4e0e\uff0c\u5509\uff01\u6b64\u4eba\u5012\u7684\u786e\u662f\u4e2a\u521a\u5f3a\u7537\u5b50\uff0c\u53ea\u662f\u2014\u2014\u53ea\u662f\u6027\u60c5\u4e5f\u592b\u9f9f\u592a\u504f\u6fc0\u4e86\u4e9b\uff0c\u4ed6\u4e00\u751f\u884c\u4e8b\uff0c\u5584 \u6076\u65e0\u5e38\uff0c\u5982\u6b64\u884c\u5f84\uff0c\u4f60\u6211\u7eb5\u4e0d\u52a8\u624b\uff0c\u4e5f\u6709\u4eba\u4f1a\u4e00\u4e00\u201d\u5b8b\u4ee4\u516c\u63a5\u53e3\u53f9\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8bdd\u867d\u53ef\u5982\u6b64 \u8bb2\u6cd5\uff0c\u4f46\u6b64\u4e8b\u7ec8\u7a76\u56e0\u6211\u800c\u8d77\uff0c\u800c\u4e14\u2014\u2014\u5509\uff0c\u4ed6\u7eb5\u6709\u4e0d\u662f\u4e4b\u5904\uff0c\u4f46\u6211\u7b49\u4ee5\u90a3\u6837\u5351\u9119\u7684\u624b\u6bb5\u6765\u5bf9 \u4ed8\u4eba\u5bb6\uff0c\u53c8\u4f55\u5c1d\u662f\u4fa0\u4e49\u884c\u5f84\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u4ed6\u9762\u4e0a\u7684\u795e\u8272\uff0c\u53c8\u53d8\u5f97\u9634\u90c1\u6c89\u91cd\u8d77\u6765\uff0c\u65b9\u624d\u51fb\u5251\u9038\u98de\u7684\u8c6a\u6c14\uff0c\u6b64\u523b\u4eff\u4f5b\u4ece\u4ed6 \u4e00\u58f0\u58f0\u6c89\u91cd\u7684\u53f9\u6c14\u4e2d\uff0c\u6d88\u9038\u65e0\u5f71\u3002 \u3000\u3000\u67f3\u590d\u660e\u76ee\u5149\u95ea\u52a8\uff0c\u7a81\u53c8\u6717\u58f0\u7b11\u9053\uff1a\u201c\u4f60\u6211\u65b9\u624d\u6b63\u5728\u8bf4\u90a3\u5c11\u5e74\uff0c\u600e\u5730\u53c8\u7275\u626f\u5230\u6b64\u4e8b\u6765\uff1f\u201d \u4ed6\u8f6c\u8eab\u8d70\u5411\u540e\u9662\uff0c\u4e00\u9762\u4ecd\u81ea\u7b11\u9053\uff1a\u201c\u65b9\u624d\u90a3\u5c11\u5e74\u7684\u4f34\u5f53\uff0c\u5374\u5df2\u8eab\u53d7\u91cd\u4f24\uff0c\u6b64\u523b\u60f3\u5fc5\u8fd8\u5728\u540e\u9662 \u4e4b\u4e2d\uff0c\u4f60\u6211\u4e0d\u59a8\u53bb\u95ee\u95ee\u4ed6\u4eec\uff0c\u4e5f\u8bb8\u80fd\u63a2\u51fa\u4ed6\u7684\u6765\u5386\u4ea6\u672a\u53ef\u77e5\u3002\u201d \u3000\u3000\u201c\u9752\u840d\u5251\u201d\u5b8b\u4ee4\u516c\u795e\u8272\u9eef\u7136\uff0c\u968f\u7740\u4ed6\u8d70\u51fa\u540e\u9662\uff0c\u4f46\u8fd9\u8352\u8349\u751f\u7684\u8352\u56ed\u4e2d\uff0c\u6b64\u523b\u98ce\u5439\u8349\u52a8\uff0c \u666f\u50cf\u4f9d\uff1b\u65e5\uff0c\u53ea\u662f\u90a3\u4e9b\u5e02\u4e95\u6c49\u5b50\uff0c\u6b64\u523b\u7adf\u4e5f\u4e0d\u77e5\u8d70\u5230\u54ea\u91cc\u53bb\u4e86\uff0c\u5b8b\u4ee4\u516c\u957f\u53f9\u4e00\u58f0\uff0c\u4ef0\u9996\u671b \u5929\uff0c\u66ae\u6625\u7684\u7a79\u82cd\uff0c\u4e00\u78a7\u5982\u6d17\uff0c\u4ed6\u5fc3\u4e2d\u5374\u4f3c\u6709\u4e00\u7247\u6de1\u6de1\u7684\u9634\u7586\uff0c\u8fd9\u9634\u973e\u4ece\u4f55\u800c\u6765\uff0c\u56e0\u4f55\u800c\u751f\uff0c \u4ed6\u5374\u4e5f\u832b\u7136\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u4ec7\u6055\u5728\u201c\u5df4\u5c71\u5251\u5ba2\u201d\u67f3\u590d\u660e\u4e0e\u201c\u9752\u840d\u5251\u201d\u5b8b\u4ee4\u516c\u7684\u6fc0\u6597\u4e2d\uff0c\u773c\u770b\u5230\u90a3\u8001\u4eba\u4f7f\u51fa\u201c\u9752\u840d\u5251 \u6cd5\u201d\u4e2d\u7684\u8d77\u624b\u4e09\u62db\u201c\u6c34\u52a8\u6d6e\u840d\u201d\u3001\u201c\u6c34\u5f71\u840d\u8e2a\u201d\u3001\u201c\u840d\u5f71\u4e07\u70b9\u201d\uff0c\u65ad\u5b9a\u4e86\u8fd9\u8001\u4eba\u7684\u786e\u662f\u81ea\u5df1 \u5fc3\u4e2d\u6240\u731c\u6d4b\u7684\u201c\u9752\u840d\u5251\u201d\u5b8b\u4ee4\u516c\uff0c\u4fbf\u6084\u7136\u8d70\u4e86\u51fa\u6765\uff0c\u4e00\u9635\u98ce\u8fce\u9762\u5439\u6765\uff0c\u4ed6\u6697\u81ea\u4f4e\u8bed\uff1a\u201c\u5f97\u9976 \u4eba\u5904\u4e14\u9976\u4eba\u2014\u2014\u5509\uff0c\u5f97\u9976\u4eba\u5904\u4e14\u9976\u4eba\uff0c\u90a3\u65f6\u53c8\u6709\u8c01\u9976\u8fc7\u7239\u7239\uff1f\u201d\u4e00\u60f3\u5230\u4ed6\u7239\u7239\u7684\u7075\u9aa8\uff0c\u5982\u4eca \u8fd8\u4ecd\u7136\u6b8b\u7f3a\u4e0d\u5168\uff0c\u4ed6\u5fc3\u4e2d\u5c31\u4e0d\u7981\u6cdb\u8d77\u4e00\u9635\u7ede\u75db\uff0c\u4ec7\u6068\uff0c\u4ec7\u6068\uff0c\u4ed6\u6697\u6697\u53f9\u4e00\u58f0\uff1a\u6211\u8be5\u53eb\u505a\u4ec7\u6068 \u624d\u5bf9\uff0c\u4f46\u662f\u2014\u2014\u5509\uff0c\u4e3a\u4ec0\u4e48\u5bf9\u6709\u4e9b\u4eba\u6211\u7adf\u65e0\u6cd5\u751f\u51fa\u4ec7\u6068\u6765\uff1f\u201d \u3000\u3000\u201c\u725b\u4e09\u773c\u201d\u5927\u6b65\u8fce\u4e86\u4e0a\u6765\uff0c\u50cf\u662f\u60f3\u8bf4\u4ec0\u4e48\uff0c\u4ed6\u8f7b\u8f7b\u4e00\u6446\uff0c\u963b\u6b62\u4e86\uff0c\u4e0d\u77e5\u9053\u4e3a\u4ec0\u4e48\uff0c\u4ed6\u6b64 \u523b\u7a81\u7136\u4e0d\u613f\u610f\u518d\u89c1\u67f3\u590d\u660e\u548c\u5b8b\u4ee4\u516c\u7684\u9762\uff0c\u56e0\u4e4b\u4ed6\u4e5f\u4e0d\u613f\u4ed6\u4eec\u53d1\u73b0\u4ed6\u7684\u6084\u7136\u8d70\u53bb\u3002 \u3000\u3000\u90a3\u4e94\u4e2a\u5e02\u4e95\u8c6a\u58eb\u6b64\u523b\u90fd\u5df2\u6577\u4e0a\u4e86\u91d1\u521b\u836f\uff0c\u5446\u5446\u5730\u5750\u5728\u5730\u4e0a\uff0c\u9762\u4e0a\u4ecd\u5e26\u7740\u65b9\u624d\u7684\u60ca\u6050\uff0c\u4ed6 \u8f7b\u8f7b\u505a\u4e86\u4e2a\u624b\u5f0f\uff0c\u53eb\u4ed6\u4eec\u90fd\u4ece\u9662\u540e\u7684\u571f\u5899\u4e0a\u8dc3\u51fa\u53bb\uff0c\u7136\u540e\u4ed6\u81ea\u5df1\u4e5f\u98d8\u8eab\u800c\u51fa\uff0c\u5728\u90a3\u4e94\u4e2a\u6c49\u5b50 \u811a\u6b65\u5c1a\u672a\u7ad9\u7a33\u7684\u65f6\u5019\uff0c\u4ed6\u5df2\u63a0\u5230\u4ed6\u4eec\u9762\u524d\uff0c\u671b\u7740\u4ed6\u4eec\u9762\u4e0a\u90a3\u79cd\u60ca\u5947\u548c\u94a6\u4f69\u7684\u8868\u60c5\uff0c\u4ed6\u6de1\u6de1\u4e00 \u7b11\uff1a\u201c\u8fd9\u6b21\u8ba9\u5404\u4f4d\u53d7\u7d2f\uff0c\u6211\u5fc3\u91cc\u4e5f\u4e0d\u5b89\u5f97\u5f88\uff0c\u53ea\u662f\u4f60\u4eec\u653e\u5fc3\u597d\u4e86\uff0c\u4eca\u65e5\u4f60\u4eec\u53d7\u7684\u6c14\uff0c\u603b\u6709\u4e00 \u5929\u6211\u4f1a\u66ff\u4f60\u4eec\u51fa\u7684\u3002\u201d \u3000\u3000\u5728\u5982\u6b64\u7d0a\u4e71\u7684\u5fc3\u60c5\u4e0b\uff0c\u4ed6\u8fd8\u4f1a\u8bf4\u51fa\u8fd9\u79cd\u5b89\u6170\u522b\u4eba\u7684\u8bdd\uff0c\u4ed6\u5e74\u7eaa\u867d\u8f7b\uff0c\u5374\u597d\u50cf\u4e0a\u5929\u751f\u4ed6\u51fa \u6765\uff0c\u5c31\u662f\u4e3a\u4e86\u8ba9\u4ed6\u505a\u4e00\u4efd\u5e38\u4eba\u4e0d\u80fd\u505a\u7684\u4e8b\u4e1a\u4f3c\u7684\uff0c\u56e0\u4e4b\u5bf9\u4ed6\u4e5f\u6bd4\u5e38\u4eba\u539a\u4e9b\uff0c\u8d4b\u4e0e\u4ed6\u8bb8\u591a\u8d85\u4eba \u7684\u6761\u4ef6\u3002 \u3000\u3000\u90a3\u4e94\u4e2a\u6c49\u5b50\u5927\u4e3a\u611f\u6fc0\uff0c\u611f\u6fc0\u5f97\u5450\u5450\u4ed6\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u8fd9\u4e9b\u6027\u60c5\u7c97\u8c6a\u7684\u70ed\u80a0\u6c49\u5b50\uff0c\u867d\u7136\u4ff1\u90fd \u662f\u6027\u60c5\u7c97\u8c6a\u7684\u5e02\u4e95\u65e0\u8d56\uff0c\u4f46\u4eba\u4eec\u82e5\u662f\u5bf9\u4ed6\u597d\u4e9b\uff0c\u90a3\u4e48\u4fbf\u662f\u53eb\u4ed6\u7acb\u65f6\u4e24\u80c1\u63d2\u5200\uff0c\u4ed6\u4eec\u4e5f\u662f\u5fc3\u7518 \u60c5\u613f\u7684\u3002 \u3000\u3000\u201c\u725b\u4e09\u773c\u201d\u659c\u773c\u671b\u7740\u4ed6\u7684\u4f34\u5f53\u4eec\uff0c\u89c1\u5230\u4ed6\u4eec\u9762\u4e0a\u7684\u795e\u60c5\uff0c\u5fc3\u91cc\u4e5f\u4e0d\u7981\u6709\u7740\u4e00\u4efd\u5f97\u610f\u7684\u611f \u89c9\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u4ed6\u4eec\u6b64\u523b\u5bf9\u4ec7\u6055\u7684\u5fc3\u60c5\uff0c\u4ed6\u5df2\u5f00\u59cb\u4e3a\u81ea\u5df1\u80fd\u4e3a\uff1a\u201c\u6055\u505a\u4e9b\u4e8b\u800c\u9a84\u505a\uff0c\u8fd9\u79cd\u4eba\uff0c\u70ed \u80a0\u800c\u723d\u76f4\uff0c\u4f46\u5374\u6ca1\u6709\u505a\u9886\u8896\u7684\u624d\u534e\uff0c\u4ed6\u4eec\u4e5f\u4ece\u4e0d\u53bb\u5984\u60f3\u8fd9\u4e9b\uff0c\u53ea\u8981\u4ed6\u4eec\u77e5\u9053\u81ea\u5df1\u670d\u4ece\u7684\u5bf9\u8c61 \u662f\u503c\u5f97\u81ea\u5df1\u670d\u4ece\u7684\uff0c\u4ed6\u4eec\u5c31\u4f1a\u5f88\u9ad8\u5174\u4e86\uff0c\u725b\u4e09\u773c\u5f88\u9ad8\u5174\u800c\u611f\u6168\u4ed6\u8bf4\u9053\uff1a\u201c\u516c\u5b50\uff0c\u6211\u65e9\u5c31\u5bf9\u4ed6 \u4eec\u8bf4\u8fc7\uff0c\u516c\u5b50\u662f\u4e0d\u4f1a\u4e8f\u5f85\u522b\u4eba\u7684\uff0c\u4ed6\u4eec\u4e3a\u4e86\u516c\u5b50\u5403\u4e9b\u82e6\u7b97\u4ec0\u4e48\uff0c\u516c\u5b50\u82e5\u8fd8\u6709\u4ec0\u4e48\u5429\u5490\uff0c\u53ea\u7ba1 \u8bf4\u5c31\u662f\uff0c\u6211\u2018\u725b\u4e09\u773c\u2019\u7b2c\u4e00\u4e2a\u8d74\u706b\u2026\u2026\u54b3\uff0c\u8d74\u6c64\u8e48\u706b\uff0c\u4e5f\u6ca1\u6709\u5173\u7cfb\u3002\u201d \u3000\u3000\u4ed6\u53c8\u7b11\u4e86\uff0c\u4e3a\u4e86\u81ea\u5df1\u7ec8\u4e8e\u80fd\u8bf4\u51fa\u201c\u8d74\u6c64\u8e48\u706b\u201d\u8fd9\u79cd\u5982\u6b64\u6587\u96c5\u7684\u8bdd\u800c\u7b11\u4e86\u3002 \u3000\u3000\u4ec7\u6055\u4e5f\u7b11\u4e86\uff0c\u4ed6\u7a81\u7136\u89c9\u5f97\u8fd9\u4e9b\u4eba\u90fd\u90a3\u4e48\u53ef\u7231\uff0c\u4ed6\u7b11\u7740\u8bf4\uff1a\u201c\u4f60\u5012\u66ff\u6211\u5439\u5618\u4e86\u4e0d\u5c11\u3002\u201d\u7b11 \u5bb9\u7a81\u5730\u4e00\u655b\uff0c\u6b63\u8272\u9053\uff1a\u201c\u5927\u7ea6\u5341\u65e5\u4e4b\u540e\uff0c\u2018\u7075\u86c7\u2019\u6bdb\u81ec\u4fbf\u8981\u5728\u676d\u5dde\u57ce\u5927\u5bb4\u7fa4\u8c6a\uff0c\u4ed6\u6b64\u4e3e\u662f\u4e3a \u4e86\u8981\u5bf9\u4ed8\u8c01\uff0c\u6211\u867d\u8fd8\u4e0d\u80fd\u65ad\u5b9a\uff0c\u4f46\u5927\u7ea6\u662f\u4e3a\u4e86\u90a3\u4e9b\u2018\u94c1\u9a91\u795e\u97ad\u2019\u9a91\u58eb\u7684\u6b7b\uff0c\u548c\u5c61\u5c61\u88ab\u52a8\u7684\u9556 \u94f6\uff0c\u4ee5\u53ca\u2014\u2014\u201d\u4ed6\u8bed\u58f0\u5fae\u987f\uff1a\u201c\u603b\u4e4b\uff0c\u65e0\u8bba\u4ed6\u4e3a\u4e86\u4ec0\u4e48\uff0c\u6211\u4eec\u4e5f\u603b\u4e0d\u80fd\u8ba9\u4ed6\u5b89\u9038\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u201c\u662f\u4e48\uff1f\u201d\u4e24\u5b57\uff0c\u4ed6\u662f\u5411\u725b\u4e09\u773c\u53d1\u51fa\u7684\uff0c\u201c\u725b\u4e09\u773c\u201d\u5374\u53d7\u5ba0\u82e5\u60ca\u4e86\uff0c\u4ed6\u4e0d\u4f4f\u5730\u70b9\u7740\u5934\uff0c \u8fde\u58f0\u79f0\u662f\uff0c\u4ed6\u518d\u4e5f\u60f3\u4e0d\u5230\u201c\u516c\u5b50\u201d\u4f1a\u5fb5\u6c42\u4ed6\u7684\u610f\u89c1\u3002 \u3000\u3000\u4ec7\u6055\u53c8\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u4f60\u5c31\u8be5\u8d76\u5feb\u60f3\u529e\u6cd5\u5728\u5341\u65e5\u4e4b\u4e2d\uff0c\u628a\u4f60\u4eec\u6881\u5927\u54e5\u548c\u90a3\u4e09\u4e2a\u9f99\u5927\u7237\u90fd\u627e \u5230\u676d\u5dde\u57ce\uff0c\u5509\uff0c\u65f6\u95f4\u5b9e\u5728\u4ed3\u4fc3\u5f97\u5f88\uff0c\u4e0d\u77e5\u4f60\u80fd\u529e\u5f97\u5230\u5417\u8c03\u201d\u725b\u4e09\u773c\u201d\u7acb\u523b\u4e00\u62cd\u80f8\u819b\uff1a\u201c\u516c \u5b50\uff0c\u8fd9\u79cd\u4e8b\uff0c\u5305\u5728\u5c0f\u7684\u8eab\u4e0a\u3002\u201d \u3000\u3000\u4ed6\u8f6c\u8fc7\u5934\u53bb\uff1a\u201c\u502a\u8001\u4e03\uff0c\u5927\u80e1\u5b50\u2014\u2014\u4f60\u4eec\u633a\u5f97\u4f4f\u5417\uff1f\u633a\u5f97\u4f4f\u5c31\u8d76\u7d27\u53bb\u627e\u4eba\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u987f\u4e86\u987f\uff0c\u7136\u540e\u53cc\u7709\u4e00\u626c\uff0c\u4ece\u6000\u4e2d\u638f\u51fa\u90a3\u5f20\u4ec7\u6055\u65b9\u624d\u7ed9\u4ed6\u7684\u94f6\u7968\u6765\uff0c\u4ea4\u7ed9\u502a\u8001\u4e03\uff0c \u633a\u4e86\u633a\u80f8\u819b\uff0c\u53c8\u9053\uff1a\u201c\u8fd9\u662f\u516c\u5b50\u8d4f\u7ed9\u4f60\u4eec\u7684\uff0c\u4f60\u4eec\u4e94\u4e2a\u4eba\u62ff\u53bb\u5206\u4e86\uff0c\u505a\u8def\u8d39\uff0c\u5feb\u4e9b\u529e\u4e8b\u3002\u201d \u4ed6\u8bed\u58f0\u4e5f\u53d8\u5f97\u6d2a\u4eae\u8d77\u6765\uff0c\u5077\u5077\u671b\u4e86\u4ec7\u6055\u4e00\u773c\uff0c\u6df1\u6df1\u4e3a\u81ea\u5df1\u8fd9\u79cd\u201c\u4e00\u4e2a\u4e0d\u53d6\u201d\u7684\u5bbd\u6d2a\u5927\u5ea6\u800c\u9a84 \u505a\uff0c\u5f53\u4ed6\u89c1\u5230\u4ec7\u6055\u4e5f\u81ea\u5728\u5fae\u7b11\u7740\u770b\u4ed6\u7684\u65f6\u5019\uff0c\u4ed6\u66f4\u9ad8\u5174\u4e86\uff0c\u4e00\u6325\u624b\uff1a\u201c\u5feb\u8d70\uff01\u201d\u56de\u8fc7\u5934\u6765\uff0c \u4ed6\u70ed\u5207\u5730\u95ee\u9053\uff1a\u201c\u516c\u5b50\u8fd8\u6709\u4ec0\u4e48\u4e8b\u5429\u5490\u6211\u7684\u5417\uff1f\u201d \u3000\u3000\u4ec7\u6055\u6ee1\u610f\u5730\u770b\u7740\u90a3\u4e94\u4e2a\u6c49\u5b50\u606d\u8eab\u884c\u793c\u4e4b\u540e\uff0c\u6781\u5feb\u5730\u8d70\u4e86\uff0c\u4ed6\u6df1\u4fe1\u8fd9\u4e9b\u4eba\u529e\u8fd9\u4e9b\u4e8b\u7684\u80fd \u529b\uff0c\u7136\u540e\u4ed6\u56de\u8fc7\u5934\u5bf9\u201c\u725b\u4e09\u773c\u201d\u9053\uff1a\u201c\u4f60\u6211\u4e4b\u95f4\uff0c\u6211\u4e5f\u518d\u4e0d\u5fc5\u8bf4\u4ec0\u4e48\u5ba2\u6c14\u8bdd\u4e86\u3002\u201d\u725b\u4e09\u773c\u76ee \u5149\u95ea\u7740\u660e\u4eae\u7684\u5149\u5f69\uff0c\u4e8e\u662f\u4ec7\u6055\u53c8\u9053\uff1a\u201c\u65b9\u624d\u7960\u5802\u4e2d\u90a3\u4e24\u4e2a\u9053\u4eba\uff0c\u4f60\u5df2\u89c1\u8fc7\uff0c\u4f60\u80fd\u4e0d\u80fd\u4e0d\u8ba9\u4ed6 \u4eec\u53d1\u73b0\uff0c\u8e51\u5728\u4ed6\u4eec\u8eab\u540e\uff0c\u770b\u770b\u4ed6\u4eec\u4f55\u53bb\u4f55\u4ece\uff1f\u201d \u3000\u3000\u5f53\u7136\uff0c\u201c\u725b\u4e09\u773c\u201d\u611f\u6fc0\u5730\u7b54\u5e94\u4e86\uff0c\u56e0\u4e3a\u4ed6\u4ece\u201c\u516c\u5b50\u201d\u90d1\u91cd\u7684\u773c\u8272\u4e2d\uff0c\u770b\u51fa\u8fd9\u4ef6\u4e8b\u5e76\u975e\u8f7b \u6613\u7684\uff0c\u800c\u201c\u516c\u5b50\u201d\u7adf\u628a\u4e00\u4ef6\u7279\u522b\u91cd\u8981\u7684\u4e8b\u7559\u7ed9\u4ed6\u505a\uff0c\u4ed6\u4e0d\u4f46\u611f\u6fc0\u800c\u9a84\u505a\uff0c\u800c\u4e14\u8fd8\u5927\u6709\u4e00\u79cd\u77e5 \u5df2\u7684\u611f\u89c9\u3002 \u3000\u3000\u4ed6\u542b\u7740\u7b11\uff0c\u8bf4\uff1a\u201c\u5c0f\u7684\u7acb\u523b\u5c31\u53bb\uff01\u201d \u3000\u3000\u4ec7\u6055\u671b\u7740\u4ed6\u7684\u80cc\u5f71\uff0c\u672c\u60f3\u53eb\u4ed6\u56de\u5bb6\uff0c\u518d\u7ed9\u4ed6\u4e00\u5f20\u94f6\u7968\uff0c\u4f46\u540e\u6765\u8f6c\u5ff5\u4e00\u60f3\uff0c\u81ea\u5df1\u8fd8\u662f\u7559\u7740 \u8fd9\u5f20\u94f6\u7968\u7684\u597d\uff0c\u4e5f\u8ba9\u4ed6\u7559\u7740\u90a3\u4efd\u81ea\u5c0a\u548c\u9a84\u50b2\u3002 \u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u7136\u540e\u2014\u2014 \u3000\u3000\u56db\u4e0b\u53ea\u5269\u4e86\u4ec7\u6055\u4e00\u4eba\uff0c\u8fd9\u6b63\u662f\u4ed6\u6240\u9700\u8981\u7684\uff0c\u9759\u5bc2\uff0c\u9759\u5bc2\u7684\u7a79\u82cd\uff0c\u9759\u5bc2\u7684\u5927\u5730\u2014\u2014\u571f\u5899\u5185 \u7a81\u5730\u4f20\u51fa\u957f\u53f9\u7684\u58f0\u97f3\uff0c\u4ed6\u77e5\u9053\u8fd9\u957f\u53f9\u662f\u5b8b\u4ee4\u516c\u53d1\u51fa\u7684\uff0c\u4e5f\u77e5\u9053\u5b8b\u4ee4\u516c\u8fd9\u957f\u53f9\u662f\u4e3a\u4e86\u4ec0\u4e48\u3002\u4f46 \u662f\u4ed6\u5374\u4f46\u613f\u81ea\u5df1\u4eca\u65e5\u6ca1\u6709\u89c1\u7740\u4ed6\u4eec\u4e24\u4eba\uff0c\u4f46\u613f\u8fd9\u4e24\u4eba\u6b64\u523b\u8fd8\u6ca1\u6709\u56de\u5230\u6c5f\u5357\u6765\uff0c\u56e0\u4e3a\u5bf9\u4e8e\u8fd9\u4e24 \u4eba\uff0c\u4ed6\u4e0d\u77e5\u662f\u8be5\u62a5\u6069\uff0c\u6291\u6216\u662f\u62a5\u4ec7\uff1f \u3000\u3000\u201c\u95ee\u6211\u4f55\u5904\u6765\uff0c\u6211\u6765\u65e0\u4f55\u6709\uff1b\u5026\u4e14\u6795\u4e66\u5367\uff0c\u68a6\u4e2d\u4ecd\u89c9\u6101\u3002\u7236\u4ec7\u4ecd\u672a\u5df2\uff0c\u7236\u6069\u4e0d\u77e5\u916c\uff1b\u6069 \u4ec7\u4e24\u4e0d\u4e86\uff0c\u601d\u4e4b\u610f\u5e7d\u5e7d\u3002\u5f15\u542d\u4f38\u4e24\u7fe9\uff0c\u5927\u606f\u610f\u4e0d\u8212\uff1b\u543e\u751f\u5982\u5bc4\u8033\uff0c\u5c11\u5e74\u4f46\u8fdc\u6e38\u3002\u8fdc\u6e38\u4e0d\u77e5 \u5904\uff0c\u8361\u5fd7\u9698\u516b\u8352\uff1b\u95f4\u6211\u4eca\u671d\u53bb\uff0c\u5409\u51f6\u4e24\u4f55\u5982\uff1f\u8fd9\u662f\u5728\u4ed6\u8981\u79bb\u5f00\u4ed6\u90a3\u751f\u957f\u4e8e\u5179\u7684\u5b64\u5c9b\u7684\u665a\u4e0a\uff0c \u671b\u7740\u7a97\u5916\u5982\u94f6\u7684\u591c\u8272\uff0c\u4e2d\u5bb5\u53cd\u8986\uff0c\u968f\u610f\u4f5c\u6210\u7684\u201d\u62df\u53e4\u56db\u5531\u3002\u201d \u3000\u3000\u4ed6\u5df2\u6709\u5f88\u4e45\u6ca1\u6709\u60f3\u8d77\u8fd9\u4e9b\u8bd7\u53e5\u4e86\uff0c\u6b64\u523b\uff0c\u4ed6\u4f4e\u541f\u7740\u8fd9\u4e9b\u4f3c\u4e4e\u5df2\u5c06\u88ab\u4ed6\u9057\u5fd8\uff0c\u800c\u53c8\u7a81\u5730\u5728 \u5fc3\u80f8\u4e2d\u6d8c\u51fa\u7684\u8bd7\u53e5\uff0c\u6084\u7136\u8d70\u5230\u7960\u5802\u540e\u7684\u8352\u6797\uff0c\u5fc3\u80f8\u4e4b\u95f4\uff0c\u6b63\u662f\u201c\u5f15\u542d\u4f38\u4e24\u7fe9\uff0c\u592a\u606f\u610f\u4e0d \u8212\u201d\uff0c\u4ed6\u957f\u53f9\u4e00\u58f0\uff0c\u4e00\u9762\u6697\u81ea\u5bfb\u601d\uff1a\u201c\u592a\u6e56\u7fa4\u8c6a\uff0c\u592a\u884c\u5feb\u5200\uff0c\u4e94\u6e56\u4e09\u9f99\uff0c\u6c61\u8863\u4e10\u5e2e\uff0c\u518d\u52a0\u4e0a \u90a3\u2018\u91d1\u5251\u4fa0\uff0c\u7aef\u6728\u65b9\u6b63\uff0c\u4ee5\u53ca\u5723\u624b\u5148\u751f\u7684\u8bb0\u540d\u5f1f\u5b50\u6881\u4e0a\u4eba\u2014\u2014\u5509\uff0c\u8fd9\u4e9b\u65e5\u5b50\u6765\uff0c\u6211\u7684\u786e\u5df2\u505a \u4e86\u4e0d\u5c11\u4e8b\uff0c\u5c31\u53ea\u8fd9\u4e9b\u4eba\uff0c\u5df2\u8db3\u4ee5\u591f\u90a3\u2019\u7075\u86c7\uff0c\u5750\u7acb\u4e0d\u5b89\u7684\u4e86\uff0c\u53ef\u662f\uff0c\u6211\u8fd8\u6709\u529b\u91cf\u591a\u505a\u4e9b\uff0c\u6211 \u4e5f\u5e94\u8be5\u518d\u591a\u505a\u4e9b\u3002\u201d\u4ed6\u72ec\u81ea\u51b7\u7b11\u7740\uff0c\u6f2b\u6b65\u8d70\u5411\u8352\u6797\u6df1\u5904\uff0c\u66ae\u6625\u7684\u9633\u5149\uff0c\u4ece\u6797\u68a2\u679d\u53f6\u7684\u7a7a\u9699\u4e2d \u6f0f\u4e0b\u6765\uff0c\u7ed9\u5730\u4e0a\u94fa\u4e0b\u4e00\u7247\u7ec6\u788e\u7684\u5149\u5f69\u3002 \u3000\u3000\u4ed6\u659c\u501a\u5728\u4e00\u682a\u6811\u5e72\u4e0a\uff0c\u7791\u76ee\u6c89\u601d\uff0c\u601d\u7d22\u81ea\u5df1\u5e94\u8be5\u8fd8\u505a\u4e9b\u4ec0\u4e48\u3002 \u3000\u3000\u826f\u4e45\uff0c\u826f\u4e45\u3002 \u3000\u3000\u4ed6\u843d\u5bde\u7684\u9762\u5bb9\u4e0a\uff0c\u53c8\u6cdb\u8d77\u4e00\u4e1d\u4ed6\u60ef\u6709\u7684\u7b11\u5bb9\uff0c\u4ed6\u89c9\u5f97\u81ea\u5df1\u5df2\u638c\u63e1\u4e86\u5927\u591a\u5236\u80dc\u7684\u628a\u63e1\uff0c\u4ed6 \u4e0d\u77e5\u8fd9\u662f\u5929\u610f\uff0c\u8fd8\u662f\u81ea\u5df1\u7684\u52aa\u529b\uff0c\u4ed6\u773c\u524d\u4f3c\u5df2\u6cdb\u8d77\u90a3\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u4e00\u9762\u4f17\u53db\u4eb2\u79bb\u7684\u56fe\u753b\u3002 \u3000\u3000\u201c\u4f17\u53db\u4eb2\u79bb\uff01\u201d\u4ed6\u51b7\u7b11\u4e00\u58f0\uff0c\u633a\u76f4\u4e86\u81ea\u5df1\u7684\u8eab\u8eaf\uff1a\u201c\u6211\u8981\u8ba9\u4ed6\u6b7b\u5728\u4ed6\u81ea\u5df1\u4f17\u53db\u4eb2\u79bb\u7684\u60c5 \u666f\u4e2d\uff0c\u800c\u4e0d\u8ba9\u4ed6\u75db\u75db\u5feb\u5feb\u5730\u6b7b\u53bb\uff0c\u4f46\u662f\u2014\u2014\u5509\uff01\u8c01\u662f\u6211\u7684\u6069\u4eba\u5462\uff1f\u6211\u53c8\u8be5\u5982\u4f55\u62a5\u6069\uff1f\u201d \u3000\u3000\u76f4\u5230\u76ee\u524d\u4e3a\u6b62\uff0c\u5bf9\u4e8e\u4ec7\u4eba\uff0c\u4ed6\u5df2\u77e5\u9053\u5f97\u591f\u591a\u4e86\uff0c\u53ef\u662f\u5bf9\u4e8e\u6069\u4eba\uff0c\u4ed6\u5374\u4ec0\u4e48\u4e5f\u4e0d\u77e5\u9053\uff0c\u4ed6 \u751a\u81f3\u4e0d\u77e5\u9053\u90a3\u516b\u4e2a\u5341\u4e03\u5e74\u6765\u65f6\u65f6\u4ee4\u7075\u86c7\u6bdb\u81ec\u4e0d\u5b89\u7684\u8840\u5b57\u201c\u5341\u5e74\u4e4b\u540e\uff0c\u4ee5\u8840\u8fd8\u8840\u201d\uff0c\u7a76\u7adf\u662f\u8c01 \u5199\u7684\uff0c\u4e5f\u4e0d\u77e5\u9053\u4ed6\u7239\u7239\u6700\u540e\u7684\u6b8b\u8eaf\uff0c\u7a76\u7adf\u662f\u88ab\u8c01\u6536\u53bb\u4e86\uff1f \u3000\u3000\u6625\u98ce\u4f9d\u4f9d\uff0c\u5439\u6563\u4e86\u4ed6\u7684\u53f9\u606f\u58f0\uff0c\u4ed6\u4fca\u79c0\u7684\u8eab\u5f71\uff0c\u7f13\u7f13\u6d88\u5931\u5728\u8352\u6797\u6df1\u5904\u3002 \u7b2c\u5341\u4e03\u7ae0>> \u53e4\u9f99\u300a\u6e58\u5983\u5251\u300b \u7b2c\u5341\u4e03\u7ae0 \u3000\u3000\u5609\u5174\u3002 \u3000\u3000\u4e09\u5854\u5f2f\u7684\u666f\u8272\uff0c\u5728\u665a\u79cb\uff0c\u79cb\u98ce\u843d\u53f6\uff0c\u5915\u9633\u4e91\u70df\uff0c\u5b83\u662f\u82cd\u51c9\u800c\u7f8e\u4e3d\u7684\uff0c\u800c\u6b64\u523b\u2014\u2014\u6b64\u523b\u662f \u66ae\u6625\uff0c\u66ae\u6625\u7684\u4e09\u5854\u5f2f\uff0c\u6e05\u6c34\u6d9f\u6f2a\uff0c\u7eff\u836b\u9752\u6ce2\uff0c\u5982\u679c\u662f\u9ec4\u660f\uff0c\u659c\u9633\u5c06\u5c0f\u6cb3\u7554\u4e09\u5ea7\u5e76\u4e0d\u751a\u9ad8\u5b9d\u5854 \u7684\u5854\u5f71\uff0c\u957f\u957f\u5730\u5370\u5728\u83ba\u98de\u8349\u957f\u7684\u5927\u5730\u4e0a\uff0c\u90a3\u8272\u5f69\u7684\u7f8e\u4e3d\u8c10\u548c\uff0c\u666f\u7269\u7684\u6e05\u5e7d\u7f8e\u4e3d\uff0c\u66f4\u662f\u65e0\u4e0e\u4f26 \u6bd4\u3002 \u3000\u3000\u897f\u53bb\u4e09\u5854\u4e00\u7bad\u4e4b\u9065\uff0c\u8038\u7acb\u7740\u53c2\u5927\u7684\u4e39\u67ab\u9ec4\u67cf\uff0c\u6797\u6728\u9690\u6620\u4e2d\uff0c\u7ea2\u5899\u4e39\u69db\uff0c\u4fbf\u662f\u4eac\u628a\u5343\u79cb\u5cb3 \u7a46\u738b\u7684\u201c\u5cb3\u738b\u5e99\u201d\uff0c\u5348\u65f6\uff0c\u66ae\u6625\u7684\u9a84\u9633\uff0c\u5df2\u6709\u4e86\u51e0\u5206\u61ca\u70ed\u4e4b\u610f\uff0c\u5cb3\u738b\u5e99\u77f3\u9636\u524d\uff0c\u5374\u5bc2\u7136\u5fd9\u7acb \u7740\u4e00\u4e2a\u9526\u8863\u534e\u670d\uff0c\u98ce\u59ff\u5982\u7389\u7684\u5c11\u5e74\u3002 \u3000\u3000\u4ed6\u8d1f\u624b\u800c\u7acb\uff0c\u76ee\u5149\u5982\u526a\uff0c\u987e\u76fc\u4e4b\u95f4\uff0c\u795e\u91c7\u7167\u4eba\uff0c\u4f46\u662f\u5728\u4ed6\u90a3\u4e00\u53cc\u659c\u98de\u5165\u9b13\u7684\u5251\u7709\u4e4b\u4e2d\uff0c \u5374\u4f3c\u9690\u542b\u7740\u4e00\u79cd\u7b49\u5f85\u7684\u6c89\u90c1\u3002 \u3000\u3000\u4ed6\u5728\u7b49\u5f85\u7740\u4ec0\u4e48\uff1f \u3000\u3000\u518d\u53bb\u5cb3\u738b\u5e99\u4e00\u7bad\u4e4b\u9065\uff0c\u5728\u90a3\u6e05\u6c34\u6d41\u6ce2\u7684\u57ce\u6cb3\u4e4b\u7554\uff0c\u4e5f\u6709\u7740\u4e00\u5ea7\u5e99\u5b57\uff0c\u5e99\u5185\u8038\u7acb\u7740\u4e00\u6839\u77f3 \u574a\u5de8\u67f1\uff0c\u67f1\u4e0a\u8d6b\u7136\u6709\u8840\u75d5\u5b9b\u7136\uff0c\u6df1\u900f\u5165\u77f3\uff01\u8fd9\u2014\u2014\u4fbf\u662f\u8840\u5370\u5bfa\uff0c\u542b\u8574\u7740\u4e00\u6bb5\u58ee\u70c8\u3001\u51c4\u60e8\uff0c\u800c \u53c8\u52a8\u4eba\u7684\u6545\u4e8b\u7684\u8840\u5370\u5bfa\u3002 \u3000\u3000\u8840\u5370\u5bfa\u5916\uff0c\u58f0\u58f0\u9a6c\u5636\u3002 \u3000\u3000\u4e00\u6392\u7eff\u6811\u4e0b\uff0c\u7cfb\u7740\u4e03\u5339\u5065\u9a6c\uff0c\u9a6c\u4e0a\u978d\u8f94\u9c9c\u660e\uff0c\u663e\u89c1\u5f97\u9a6c\u4e3b\u4e0d\u662f\u9ad8\u5b98\u8d35\u7ec5\uff0c\u4fbf\u662f\u6c5f\u6e56\u5927 \u8c6a\u3002 \u3000\u3000\u8840\u5370\u5bfa\u5185\uff0c\u58f0\u58f0\u4eba\u8bed\u3002 \u3000\u3000\u6b63\u6bbf\u77f3\u9636\u524d\uff0c\u505a\u7136\u4ec1\u7acb\u7740\u4e24\u4e2a\u8eab\u8eaf\u7626\u957f\uff0c\u76ee\u5149\u5982\u9e70\u7684\u6c49\u5b50\uff0c\u5176\u4e2d\u4e00\u4eba\uff0c\u53f3\u81c2\u7a7a\u7a7a\uff0c\u4e00\u53ea \u8863\u8896\uff0c\u7f1a\u5728\u8170\u95f4\u7684\u4e1d\u7ee6\u4e0a\uff0c\u773c\u671b\u7740\u5bfa\u4e1c\u90a3\u6839\u77f3\u574a\u5de8\u67f1\uff0c\u6b63\u5728\u51dd\u795e\u503e\u542c\u7740\u8083\u7136\u7ad9\u5728\u4ed6\u4eec\u5bf9\u9762\u7684 \u4e00\u4e2a\u9762\u5982\u6ee1\u6708\u7684\u618e\u4eba\u53e3\u4e2d\u6240\u8bf4\u7684\u6545\u4e8b\u3002 \u3000\u3000\u8fd8\u6709\u4e94\u4e2a\u5e74\u8f7b\u529b\u58ee\uff0c\u795e\u8272\u6f02\u608d\u7684\u957f\u886b\u6c49\u5b50\uff0c\u5782\u624b\u606d\u7acb\u5728\u4ed6\u4eec\u8eab\u540e\uff0c\u8fd9\u4e94\u4eba\u76ee\u5149\u6d41\u8f6c\uff0c\u4e1c \u5f20\u897f\u671b\uff0c\u5fc3\u795e\u5374\u4e0d\u77e5\u5728\u60f3\u4e9b\u4ec0\u4e48\uff0c\u4f46\u8138\u4e0a\u5374\u6781\u529b\u4f5c\u51fa\u606d\u8c28\u7684\u795e\u8272\u6765\uff0c\u663e\u89c1\u5f97\u662f\u90a3\u4e24\u4e2a\u7626\u957f\u6c49 \u5b50\u7684\u5f1f\u5b50\u5bb6\u5974\u3002 \u3000\u3000\u4ed6\u4eec\u4e0d\u95ee\u53ef\u77e5\uff0c\u4fbf\u662f\u626c\u540d\u6cb3\u6714\u7684\u6b66\u6797\u5927\u8c6a\u201c\u6cb3\u6714\u53cc\u5251\u201d\u6c6a\u6c0f\u6606\u4f38\uff0c\u548c\u4ed6\u4eec\u7684\u4e94\u4e2a\u5f1f\u5b50\u3002 \u3000\u3000\u90a3\u9762\u5982\u6ee1\u6708\u7684\u50e7\u4eba\uff0c\u8eab\u7a7f\u7740\u4e00\u8eab\u6708\u767d\u50e7\u8863\uff0c\u4e0d\u4f46\u8863\u5c65\u6574\u6d01\uff0c\u800c\u4e14\u795e\u6001\u6e05\u4fca\uff0c\u5410\u5c5e\u4fca\u96c5\uff0c \u6b63\u662f\u8fd9\u79cd\u540d\u8ff9\u80dc\u5883\u4e2d\u4f4f\u9521\u50e7\u4eba\u901a\u6709\u7684\u5f62\u72b6\uff0c\u6b64\u523b\u4ed6\u4e00\u624b\u633d\u7740\u4e00\u4e32\u6a80\u6728\u4f5b\u73e0\uff0c\u4e00\u624b\u9065\u6307\u7740\u90a3\u77f3 \u574a\u5de8\u67f1\uff0c\u5a13\u5a13\u8bf4\u9053\uff1a\u201c\u6570\u5341\u5e74\u524d\uff0c\u502d\u5bc7\u81ea\u6d77\u4e0a\u6765\uff0c\u52ab\u88ad\u4e1c\u5357\u6570\u7701\uff0c\u800c\u5609\u5174\u88ab\u7978\u5c24\u70c8\uff0c\u5e38\u63a0\u8d27 \u8d22\u5987\u5973\uff0c\u8d2e\u4e8e\u655d\u5bfa\u4e4b\u4e2d\uff0c\u518d\u7387\u4f17\u5f80\u653b\u6850\u4e61\u3002\u201d \u3000\u3000\u4ed6\u5782\u76ee\u957f\u53f9\u4e00\u58f0\uff0c\u53c8\u9053\uff1a\u201c\u90a3\u65f6\u8d2b\u50e7\u867d\u8fd8\u672a\u4eba\u4e16\uff0c\u4f46\u542c\u5f97\u8bf8\u5e08\u76f8\u544a\uff0c\u6570\u767e\u5987\u5973\uff0c\u5728\u5bfa\u4e2d \u65e5\u591c\u60b2\u6ce3\uff0c\u60e8\u4e0d\u53ef\u95fb\uff0c\u6b64\u65f6\u655d\u5bfa\u65b9\u4e08\uff0c\u4e43\u5999\u8c1b\u7956\u5e08\uff0c\u5999\u8c1b\u7956\u5e08\u4e0a\u4f53\u5929\u5fc3\uff0c\u95fb\u4e4b\u4fa7\u7136\uff0c\u9042\u9189\u5b88 \u8005\uff0c\u5f00\u95e8\u653e\u4e4b\uff0c\u4ee4\u5404\u53d6\u91d1\u9003\u53bb\u3002\u201d \u3000\u3000\u201c\u5987\u5973\u4e2d\u6709\u8a00\u6050\u7d2f\u53ca\u7956\u5e08\u8005\uff0c\u7956\u5e08\u4e91\uff1a\u2018\u543e\u4ee5\u4e00\u8eab\u800c\u6551\u6570\u767e\u4eba\u4e4b\u547d\uff0c\u867d\u6b7b\u4f55\u4f24\u3002\u4e8e\u662f\u4f17 \u7686\u7f57\u62dc\uff0c\u56db\u6563\u800c\u9038\uff01\u201d\u201c\u6cb3\u6714\u53cc\u5251\u201d\u867d\u4e43\u751f\u6027\u9634\u9e37\u7684\u6b66\u6797\u67ad\u96c4\uff0c\u4f46\u6b64\u523b\u4ea6\u4e0d\u7981\u4e3a\u4e4b\u8038\u7136\u52a8 \u5bb9\u3002\u6c6a\u4e00\u9e23\u957f\u7709\u4e00\u5c55\uff1a\u201c\u8fd9\u5999\u8c1b\u7985\u5e08\uff0c\u5012\u662f\u4e2a\u78ca\u843d\u4e08\u592b\u3002\u201d \u3000\u3000\u90a3\u50e7\u4eba\u957f\u53f9\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u5f53\u65f6\u7956\u5e08\u5f1f\u5b50\u7686\u529d\u7956\u5e08\u540c\u9003\uff0c\u62cc\u5e08\u66f0\uff1a\u2018\u4e0d\u53ef\uff0c\u543e\u82e5\u4e00\u8d70\uff0c \u5219\u8ffd\u8005\u7acb\u81f3\uff01\u2019\u7adf\u72ec\u7559\u4ee5\u5f85\u4e4b\uff0c\u65e2\u800c\u5b88\u8005\u9152\u9192\uff0c\u77e5\u800c\u4e9f\u8be2\uff0c\u7956\u4fbf\u9053\uff1a\u2018\u9002\u89c1\u8fdd\u9a6e\u5c0a\u8005\u4ee5\u5b9d\u6775 \u51fb\u95e8\u5f00\uff0c\u5bfc\u4e4b\u4f7f\u53bb\uff0c\u543e\u4e0d\u6562\u963b\u4e5f\u3002\uff0c\u5509\u4e00\u4f5b\u5bb6\u867d\u6212\u5984\u8bed\uff0c\u4f46\u7956\u5e08\u5177\u5927\u6148\u60b2\u4e4b\u5fc3\uff0c\u81ea\u5f53\u522b\u8bba\uff0c \u5b88\u8005\u7d20\u754f\u9b3c\u795e\uff0c\u95fb\u8a00\u8272\u53d8\uff0c\u4e14\u6b63\u75c5\u9152\uff0c\u5f31\u4e0d\u80fd\u884c\uff0c\u7adf\u76d1\u5b88\u7956\u5e08\uff0c\u4ee5\u5f85\u5bc7\u5f52\u3002\u201d\u4ed6\u8bed\u58f0\u6e05\u6717\uff0c \u8bed\u53e5\u66f4\u5178\u96c5\u52a8\u4eba\uff0c\u5a13\u5a13\u9053\u6765\uff0c\u8fde\u90a3\u4e94\u4e2a\u5fc3\u733f\u610f\u9a6c\u7684\u5e74\u9752\u6c49\u5b50\uff0c\u95fb\u4e4b\u4e5f\u4e0d\u7981\u52a8\u5bb9\u3002\u4ed6\u957f\u53f9\u53c8 \u9053\uff1a\u201c\u672a\u51e0\u502d\u5bc7\u5f52\u6765\uff0c\u77e5\u5987\u4eba\u4e43\u7956\u5e08\u6240\u653e\uff0c\u56da\u91cd\u7b54\u5b88\u8005\uff0c\u800c\u7f1a\u7956\u5e08\u4e8e\u77f3\u67f1\uff0c\u4e1b\u77e2\u5c04\u4e4b\uff0c\u7956\u5e08 \u4e43\u897f\u5f52\uff0c\u5bc7\u590d\u5806\u85aa\u711a\u4e4b\uff0c\u5bc7\u5e73\u4e4b\u540e\uff0c\u53d7\u7956\u5e08\u5927\u6069\u8005\uff0c\u62fe\u7956\u5e08\u9aa8\u70ec\u846c\u4e8e\u5bfa\u540e\uff0c\u5509\u2014\u2014\u90a3\u77f3\u67f1\u4e4b \u4e0a\uff0c\u81ea\u6b64\u8840\u75d5\u5370\u77f3\uff0c\u81f3\u4eca\u6570\u5341\u5e74\u77e3\u3002\u201d \u3000\u3000\u201c\u6cb3\u6714\u53cc\u5251\u201d\u4e00\u9f50\u968f\u7740\u4ed6\u7684\u624b\u6307\u671b\u53bb\uff0c\u671b\u89c1\u90a3\u77f3\u67f1\u4e0a\u7684\u8840\u75d5\uff0c\u4e0d\u7981\u5404\u5404\u8272\u53d8\uff0c\u60f3\u5230\u81ea\u5df1 \u7684\u4e00\u751f\u6240\u4e3a\uff0c\u534a\u664c\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u5bfa\u50e7\u5a13\u5a13\u53d9\u8bf4\u7684\u65f6\u5019\uff0c\u5bfa\u5916\u57ce\u6cb3\u4e2d\uff0c\u7a81\u5730\u9a76\u6765\u4e00\u8258\u5feb\u8247\uff0c\u5176\u6025\u5982\u77e2\uff0c\u8239\u4e0a\u4ec1\u7acb\u7740 \u4e00\u4e2a\u957f\u8eab\u7389\u7acb\u7684\u5c11\u5e74\uff0c\u7adf\u662f\u4e00\u8eab\u91d1\u886b\uff0c\u6625\u98ce\u5439\u8d77\u4ed6\u98de\u626c\u7684\u888d\u89d2\uff0c\u671b\u6765\u76f4\u6709\u5982\u4e00\u682a\u4e34\u98ce\u4e4b\u7389 \u6811\u3002 \u3000\u3000\u8fd9\u5c0f\u821f\u7834\u6d6a\u6025\u884c\uff0c\u8fc7\u8840\u5370\u5bfa\uff0c\u53bb\u4e09\u5854\u5bfa\uff0c\u5cb3\u738b\u5e99\u524d\u7684\u534e\u670d\u5c11\u5e74\uff0c\u76ee\u5149\u654f\u9510\uff0c\u4e00\u773c\u671b\u5230\u8fd9 \u91d1\u886b\u5c11\u5e74\u6240\u4e58\u7684\u5feb\u8247\uff0c\u795e\u8272\u5fae\u5fae\u4e00\u53d8\uff0c\u888d\u8896\u5fae\u62c2\u95f4\uff0c\u8eab\u5f62\u7a81\u5730\u98d8\u98d8\u9000\u540e\u4e00\u4e08\uff0c\u5374\u89c1\u8fd9\u8258\u5feb\u8247 \u5728\u4e09\u5854\u5bfa\u524d\u7684\u6cb3\u5f2f\u4e00\u8f6c\uff0c\u53c8\u590d\u56de\u8f6c\u5934\u6765\uff0c\u5728\u5cb3\u738b\u5e99\u524d\u5fae\u5fae\u505c\u987f\uff0c\u4fbf\u53c8\u5411\u8840\u5370\u5bfa\u6025\u9a76\u800c\u53bb\u3002 \u3000\u3000\u5bfa\u50e7\u8bdd\u65b9\u8bf4\u5b8c\uff0c\u201c\u6cb3\u6714\u53cc\u5251\u201d\u6b63\u81ea\u5782\u76ee\u550f\u5618\uff0c\u5bfa\u95e8\u5916\u7a81\u5730\u5982\u98de\u95ea\u5165\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u8fd9\u4eba\u8eab\u6750\u9880\u957f\uff0c\u9762\u76ee\u82f1\u633a\uff0c\u4f46\u7709\u5b57\u4e4b\u95f4\uff0c\u5374\u5e26\u7740\u51e0\u5206\u715e\u6c14\uff0c\u53cc\u76ee\u4e4b\u4e2d\uff0c\u4e5f\u4e0d\u65f6\u95ea\u52a8\u7740\u903c \u4eba\u7684\u773c\u5149\u91c7\u3002 \u3000\u3000\uff0c\u4ed6\u7ade\u5c31\u662f\u65b9\u624d\u4ec1\u7acb\u8239\u5934\u7684\u90a3\u91d1\u886b\u5c11\u5e74\u3002 \u3000\u3000\u8fd9\u91d1\u886b\u5c11\u5e74\u4e00\u5165\u5e99\u95e8\uff0c\u76ee\u5149\u4e00\u8f6c\uff0c\u89c1\u5230\u4e86\u201c\u6cb3\u6714\u53cc\u5251\u201d\uff0c\u9762\u4e0a\u7acb\u523b\u6cdb\u51fa\u559c\u8272\uff0c\u4e09\u811a\u4e24 \u6b65\uff0c\u8dd1\u4e86\u8fc7\u53bb\uff0c\u7a81\u5730\u606d\u8eab\u4e00\u793c\uff1a\u201c\u62dc\u89c1\u4e24\u4f4d\u6c6a\u5e08\u53d4\u3002\u201d \u3000\u3000\u201c\u6cb3\u6714\u53cc\u5251\u201d\u4f3c\u4e4e\u4e3a\u8fd9\u5c11\u5e74\u7a81\u7136\u800c\u6765\u7684\u4e3e\u52a8\u6014\u4e4b\u4e00\u6014\u3002 \u3000\u3000\u4f46\u8fd9\u91d1\u886b\u5c11\u5e74\u7acb\u523b\u53c8\u9053\uff1a\u201c\u5c0f\u4f84\u593a\u547d\u4f7f\u8005\u94c1\u5e73\uff0c\u5949\u5bb6\u5e08\u4e4b\u547d\uff0c\u524d\u6765\u5bfb\u627e\u4e24\u4f4d\u6c6a\u5e08\u53d4\uff0c\u5c0f \u4f84\u4e00\u8def\u6253\u542c\uff0c\u77e5\u9053\u4e24\u4f4d\u5e08\u53d4\u5728\u5609\u5174\u6b47\u811a\uff0c\u5c0f\u4f84\u4fbf\u8d76\u5230\u5609\u5174\uff0c\u53c8\u95fb\u5f97\u4e24\u4f4d\u5e08\u53d4\u5230\u4e09\u5854\u5f2f\u6765\u8e0f \u6625\uff0c\u5c0f\u4f84\u4fbf\u8d76\u5230\u4e09\u5854\u5f2f\uff0c\u5374\u4e0d\u89c1\u4e24\u4f4d\u5e08\u53d4\u4eba\u5f71\uff0c\u540e\u6765\u89c1\u5230\u5bfa\u5916\u7684\u4e03\u5339\u5750\u9a91\uff0c\u624d\u60f3\u5230\u4e24\u4f4d\u5e08\u53d4 \u6216\u8005\u5728\u8fd9\u91cc\uff0c\u4fbf\u7acb\u523b\u8d76\u6765\u62dc\u89c1\uff01\u201d \u3000\u3000\u4ed6\u4e00\u53e3\u6c14\u8bf4\u5230\u8fd9\u91cc\uff0c\u65b9\u81ea\u5598\u4e86\u53e3\u6c14\uff0c\u8a00\u4e0b\u9887\u4e3a\u81ea\u5df1\u529e\u4e8b\u7684\u80fd\u529b\u5f97\u610f\uff0c\u5374\u4e0d\u77e5\u81ea\u5df1\u8a00\u8bed\u4e4b \u4e2d\uff0c\u5df2\u6709\u758f\u5ffd\uff0c\u72af\u4e86\u4eba\u5bb6\u5927\u5fcc\u3002 \u3000\u3000\u201c\u6cb3\u6714\u53cc\u5251\u201d\u9762\u76ee\u9634\u6c89\uff0c\u4e00\u76f4\u6728\u7136\u542c\u7740\u4ed6\u7684\u8bdd\uff0c\u6b64\u523b\u8fd9\u5144\u5f1f\u4e24\u4eba\u7adf\u5404\u5404\u53cc\u76ee\u4e00\u7ffb\uff0c\u957f\u7709 \u8f69\u7acb\uff0c\u795e\u8272\u4e4b\u95f4\uff0c\u9690\u542b\u6012\u610f\u3002 \u3000\u3000\u6c6a\u4e00\u9e23\u7adf\u51b7\u54fc\u4e00\u58f0\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u593a\u547d\u4f7f\u8005\u2014\u2014\u54fc\uff0c\u9601\u4e0b\u6b64\u6765\u5bfb\u627e\u6211\u5144\u5f1f\uff0c\u60f3\u5fc5\u662f\u90a3\u2018\u6bdb \u592a\u592a\u7237\uff0c\u8981\u9601\u4e0b\u6765\u593a\u6211\u5144\u5f1f\u4e24\u4eba\u4e4b\u547d\u7684\u4e86\u2014\u2014\u5927\u54e5\uff0c\u4f60\u8bf4\u53ef\u662f\uff1f\u201d\u8f6c\u8fc7\u5934\u53bb\uff0c\u9762\u5e26\u51b7\u7b11\uff0c\u7adf \u518d\u4e5f\u4e0d\u671b\u90a3\u593a\u547d\u4f7f\u8005\u94c1\u5e73\u4e00\u773c\u3002\u201c\u593a\u547d\u4f7f\u8005\u201d\u94c1\u5e73\u5fae\u5fae\u4e00\u6014\uff0c\u7acb\u523b\u966a\u7b11\u9053\uff1a\u201c\u4e24\u4f4d\u5e08\u53d4\u8a00\u91cd \u4e86\uff0c\u83ab\u8bf4\u5bb6\u5e08\u7edd\u4e0d\u4f1a\u6709\u6b64\u610f\uff0c\u4fbf\u662f\u5c0f\u4f84\u4e5f\u4e07\u4e07\u4e0d\u6562\u5728\u4e24\u4f4d\u5e08\u53d4\u9762\u524d\u653e\u8086\uff0c\u4e24\u4f4d\u5e08\u6c49\u5982\u6b64\u8bf4\uff0c \u5c0f\u4f84\u771f\u6068\u4e0d\u5f97\u4e00\u5934\u649e\u6b7b\u2014\u2014\u201d\u6c6a\u4e00\u9e4f\u51b7\u201c\u54fc\u201d\u4e00\u58f0\uff1a\u201c\u9601\u4e0b\u65e2\u6709\u6b64\u610f\uff0c\u5c31\u4e00\u5934\u649e\u6b7b\u597d\u4e86\uff0c\u6211 \u5144\u5f1f\u7edd\u65e0\u963b\u62e6\u4e4b\u610f\uff01\u201d \u3000\u3000\u4ed6\u53c8\u81ea\u51b7\u7b11\u4e00\u58f0\uff0c\u968f\u624b\u638f\u51fa\u4e00\u952d\u94f6\u5b50\uff0c\u4ea4\u7ed9\u5bfa\u50e7\uff0c\u4e00\u9762\u53c8\u9053\uff1a\u201c\u591a\u627f\u5927\u5e08\u8d39\u5fc3\uff0c\u533a\u533a\u4e00 \u952d\u94f6\u5b50\uff0c\u8fd8\u671b\u5927\u5e08\u66ff\u6211\u7b49\u5728\u4f5b\u524d\u8fdb\u9999\u3002\u201d\u888d\u8896\u4e00\u62c2\uff0c\u8f6c\u8eab\u5411\u5bfa\u95e8\u5916\u9762\u5927\u6b65\u8d70\u53bb\u3002\u201d \u3000\u3000\u90a3\u5bfa\u50e7\u89c1\u4e86\u4ed6\u4eec\u7684\u795e\u8272\uff0c\u5fc3\u4e2d\u672c\u5df2\u5728\u5600\u5495\uff0c\u6b64\u523b\u63a5\u4e86\u94f6\u5b50\uff0c\u8fde\u5fd9\u5408\u638c\u79f0\u8c22\uff0c\u76ee\u5149\u62ac\u5904\uff0c \u53ea\u89c1\u90a3\u91d1\u886b\u5c11\u5e74\u5446\u5446\u5730\u7ad9\u5728\u5f53\u5730\uff0c\u9762\u4e0a\u9635\u9752\u9635\u767d\uff0c\u90a3\u5bfa\u50e7\u6697\u4e2d\u4e00\u7b11\uff0c\u4e5f\u4ea6\u8f6c\u8eab\u8d70\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u201c\u7075\u86c7\u201d\u6bdb\u684c\u81ea\u5df1\u95e8\u4e0b\u7684\u5341\u5927\u5f1f\u5b50\uff0c\u4e0d\u4f46\u6b66\u529f\u9ad8\u5f3a\uff0c\u800c\u4e14\u4ff1\u662f\u80fd\u8a00\u5584\u8bed\uff0c\u98ce\u5ea6\u82f1\u633a\u7684\u82f1 \u8c6a\u5c11\u5e74\uff01 \u3000\u3000\u8fd9\u201c\u593a\u547d\u4f7f\u8005\u201d\u94c1\u5e73\uff0c\u5728\u5341\u5927\u5f1f\u5b50\u4e2d\uff0c\u53c8\u7b97\u662f\u4f7c\u4f7c\u4eba\u7269\uff0c\u5e73\u65f6\u5e38\u4ee5\u5468\u90ce\u81ea\u547d\uff0c\u81ea\u79f0\u81ea\u5df1 \u7684\u786e\u662f\u6587\u6b66\u53cc\u5168\u7684\u5c11\u5e74\u8c6a\u5ba2\u3002 \u3000\u3000\u4f46\u4ed6\u6b64\u523b\u5446\u5446\u5730\u7ad9\u5728\u5f53\u5730\uff0c\u7adf\u4e0d\u77e5\u8be5\u5982\u4f55\u662f\u597d\uff0c\u53ea\u89c1\u90a3\u201c\u6cb3\u6714\u53cc\u5251\u201d\u5728\u4ed6\u4eec\u4e94\u4e2a\u5f1f\u5b50\u62e5 \u62a4\u4e4b\u4e0b\uff0c\u5df2\u5c06\u8d70\u51fa\u5bfa\u95e8\u3002 \u3000\u3000\u4ed6\u6697\u81ea\u900f\u4e86\u53e3\u6c14\uff0c\u5927\u6b65\u8d76\u4e86\u8fc7\u53bb\uff0c\u6a2a\u8eab\u6321\u5728\u95e8\u53e3\uff0c\u6ee1\u8138\u5806\u4e0b\u7b11\u5bb9\u3002 \u3000\u3000\u54ea\u77e5\u6c6a\u4e00\u9e23\u53c8\u51b7\u54fc\u4e00\u58f0\uff1a\u201c\u9601\u4e0b\u53c8\u8981\u600e\u5730\uff1f\u96be\u9053\u90a3\u6bdb\u5927\u592a\u7237\u771f\u7684\u4e0d\u80af\u653e\u8fc7\u6211\u4eec\uff1f\u6211\u5012\u8981 \u770b\u770b\u6bdb\u5927\u5927\u7237\u9664\u4e86\u6709\u4e2a\u597d\u5973\u513f\u4e4b\u5916\uff0c\u8fd8\u6709\u591a\u5c11\u4e2a\u597d\u5f92\u5f1f\uff1f\u201d \u3000\u3000\u4ed6\u5144\u5f1f\u4e24\u4eba\u5728\u897f\u6e56\u753b\u822b\u4e4b\u4e0a\uff0c\u5403\u4e86\u6bdb\u6587\u742a\u4e00\u4e2a\u5927\u4e8f\uff0c\u4ed6\u4e24\u4eba\u751f\u5e73\u6043\u5f3a\u505a\u7269\uff0c\u54ea\u91cc\u53d7\u8fc7\u8fd9 \u79cd\u6c14\uff0c\u7adf\u8fde\u6bdb\u81ec\u90a3\u91cc\u90fd\u4e0d\u53bb\u4e86\uff0c\u51c6\u5907\u6298\u56de\u6cb3\u6714\u3002\u8def\u8fc7\u5609\u5174\uff0c\u4e3a\u5357\u6e56\u70df\u96e8\u6240\u9189\uff0c\u7adf\u5728\u90a3\u91cc\u803d\u4e86 \u6570\u6708\uff0c\u6b64\u523b\u5fc3\u4e2d\u4ecd\u7136\u803f\u803f\u4e8e\u6000\uff0c\u518d\u52a0\u4e0a\u8fd9\u82e6\u5bfb\u8bb8\u4e45\u7684\u201c\u593a\u547d\u4f7f\u8005\u201d\u627e\u5230\u4ed6\u4eec\u4e4b\u540e\uff0c\u4e00\u65f6\u5927\u610f \u758f\u5ffd\uff0c\u5fd8\u5f62\u8bf4\u51fa\u81ea\u5df1\u7684\u7ef0\u53f7\uff0c\u4ed6\u5144\u5f1f\u4e24\u4eba\u5fc3\u4e2d\u672c\u5df2\u4e0d\u5fff\uff0c\u518d\u7ecf\u5982\u6b64\u4e00\u6765\uff0c\u4fbf\u6beb\u4e0d\u5ba2\u6c14\u5730\u53d1\u4f5c \u51fa\u6765\u3002 \u3000\u3000\u8fd9\u201c\u593a\u547d\u4f7f\u8005\u201d\u94c1\u5e73\u6b64\u523b\u5fc3\u4e2d\u867d\u4ea6\u4e0d\u5fff\uff0c\u4f46\u9762\u4e0a\u5374\u4e1d\u6beb\u4e0d\u6562\u663e\u9732\u3002 \u3000\u3000\u201c\u5bb6\u5e08\u672c\u4e0d\u77e5\u9053\u90a3\u4ef6\u4e8b\uff0c\u540e\u6765\u77e5\u9053\u4e86\u5e08\u59b9\u5728\u897f\u6e56\u4e0a\u5192\u72af\u4e86\u4e24\u4f4d\uff01\u5e08\u53d4\uff0c\u5c31\u8d76\u7d27\u7279\u5730\u547d\u5f1f \u5b50\u524d\u6765\u966a\u7f6a\uff0c\u8fd8\u671b\u4e24\u4f4d\u5e08\u53d4\u5927\u4eba\u4e0d\u89c1\u5c0f\u4eba\u7f6a\uff0c\u770b\u5728\u655d\u5e08\u59b9\u5e74\u8f7b\u4e0d\u61c2\u4e8b\u7684\u4efd\u4e0a\uff0c\u9976\u5979\u8fd9\u4e00\u906d\uff0c \u8bf7\u4e24\u4f4d\u5e08\u53d4\u65e0\u8bba\u5982\u4f55\u56de\u676d\u5dde\u53bb\u4e00\u8d9f\uff0c\u4e0d\u7136\u2014\u2014\u5509\uff0c\u4e0d\u7136\u5f1f\u5b50\u771f\u7684\u786e\u65e0\u6cd5\u4ea4\u4ee3\uff0c\u5bb6\u5e08\u53ea\u6015\u53c8\u8981 \u5f53\u5f1f\u5b50\u5728\u54ea\u91cc\u5f97\u7f6a\u4e86\u4e24\u4f4d\u5e08\u53d4\u54e9\u3002\u201d \u3000\u3000\u6c6a\u6c0f\u6606\u4ef2\u5bf9\u671b\u4e00\u773c\uff0c\u90a3\u6c6a\u4e00\u9e4f\u53f3\u81c2\u88ab\u6298\u4e4b\u540e\uff0c\u6027\u60c5\u8d8a\u53d1\u504f\u6fc0\uff0c\u95fb\u8a00\u53c8\u81ea\u51b7\u7b11\u4e00\u58f0\u9053\uff1a \u201c\u5e74\u8f7b\u65e0\u77e5\uff0c\u54fc\uff01\u9976\u5979\u4e00\u906d\u2014\u2014\u54fc\uff01\u6211\u5144\u5f1f\u8fd9\u53ef\u4e0d\u6562\u5f53\uff0c\u50cf\u4ee4\u5e08\u59b9\u90a3\u6837\u7684\u5c11\u5e74\u82f1\u96c4\uff0c\u5973\u4e2d\u8c6a \u6770\uff0c\u6211\u5144\u5f1f\u53ea\u671b\u5979\u9976\u9976\u6211\u4eec\u5c31\u4e0d\u9519\u4e86\u3002\u201d \u3000\u3000\u6c6a\u4e00\u545c\u751f\u6027\u5374\u8f83\u6c89\u7a33\uff0c\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u9053\uff1a\u201c\u8fd9\u4e9b\u4e8b\u4e14\u4e0d\u53bb\u8bf4\u5b83\uff0c\u4ee4\u5e08\u8981\u6211\u5144\u5f1f\u5230\u676d\u5dde\u53bb\uff0c \u4e0d\u77e5\u662f\u4e3a\u4e86\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u4ed6\u5fc3\u5ff5\u8f6c\u5904\uff0c\u4e00\u6765\u548c\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u5230\u5e95\u76f8\u4ea4\u591a\u5e74\uff0c\u518d\u6765\u4ed6\u4e5f\u4e0d\u613f\u5f97\u7f6a\u6b64\u4eba\uff0c\u662f\u4ee5\u6b64\u523b\u8a00 \u95f4\u8bed\u6c14\uff0c\u4fbf\u548c\u7f13\u5f97\u591a\u3002 \u3000\u3000\u94c1\u5e73\u662f\u4f55\u7b49\u4eba\u7269\uff0c\u5bdf\u8a00\u89c2\u8272\uff0c\u7acb\u523b\u89c9\u5bdf\u51fa\u6765\uff0c\u559c\u9053\uff1a\u201c\u8fd9\u4e2a\u5c0f\u4f84\u4e5f\u4e0d\u77e5\u9053\uff0c\u4f46\u5bb6\u5e08\u2014 \u2014\u201d\u6c6a\u4e00\u9e4f\u51b7\u7b11\u4e00\u58f0\uff0c\u622a\u65ad\u4e86\u4ed6\u7684\u8bdd\uff1a\u201c\u4ee4\u5e08\u8fd1\u5e74\u8d35\u4eba\u591a\u5fd8\uff0c\u8fd8\u5c06\u6211\u5144\u5f1f\u8fd9\u7b49\u8001\u670b\u53cb\u653e\u5728\u5fc3 \u4e0a\u5417\uff1f\u4ed6\u65e2\u7136\u77e5\u9053\u6211\u5144\u5f1f\u5728\u5609\u5174\uff0c\u96be\u9053\u4ed6\u81ea\u5df1\u2014\u2014\u54fc\uff01\u201d \u3000\u3000\u4ed6\u51b7\u54fc\u4e00\u58f0\uff0c\u4e2d\u6b62\u4e86\u81ea\u5df1\u4e0b\u9762\u66f4\u96be\u542c\u7684\u8bdd\uff0c\u6c6a\u4e00\u9e23\u53ea\u89c1\u8fd9\u201c\u593a\u547d\u4f7f\u8005\u201d\u9762\u4e0a\u9635\u9752\u9635\u767d\uff0c \u5fc3\u5ff5\u4e00\u8f6c\uff0c\u7acb\u523b\u63a5\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u8fd8\u671b\u9601\u4e0b\u524d\u53bb\u56de\u590d\u4ee4\u5e08\uff0c\u5c31\u8bf4\u6211\u5144\u5f1f\u5373\u65e5\u5c31\u5230\u676d\u5dde\u3002\u201d \u4ed6\u5fae\u5fae\u4e00\u7b11\uff1a\u201c\u9601\u4e0b\u65c5\u5f92\u52b3\u987f\uff0c\u4e5f\u8f9b\u82e6\u4e86\u3002\u201d \u3000\u3000\u201c\u593a\u547d\u4f7f\u8005\u201d\u94c1\u5e73\u6697\u54fc\u4e00\u58f0\uff1a\u201c\u539f\u6765\u4f60\u4eec\u4e24\u4eba\u4e5f\u4e0d\u6562\u5f97\u7f6a\u5e08\u5085\uff0c\u5230\u5e95\u8fd8\u662f\u8981\u8bf4\u4e24\u53e5\u8f6f \u8bed\u3002\u201d \u3000\u3000\u4ed6\u4ea6\u751f\u6027\u504f\u7a84\u4e4b\u4eba\uff0c\u6b64\u523b\u5bf9\u8fd9\u201c\u6cb3\u6714\u53cc\u5251\u201d\u5144\u5f1f\u4e24\u4eba\uff0c\u5fc3\u4e2d\u5df2\u5927\u6709\u4e0d\u6ee1\u4e4b\u610f\uff0c\u4f46\u9762\u4e0a\u5374 \u4e1d\u6beb\u4e0d\u9732\uff0c\u4ecd\u966a\u7b11\u9053\uff1a\u201c\u5f1f\u5b50\u8f9b\u82e6\u4e9b\u7b97\u5f97\u4e86\u4ec0\u4e48\uff0c\u5e08\u53d4\u4eec\u592a\u89c1\u5916\u4e86\u3002\u201d\u4ed6\u606d\u8eab\u4e00\u793c\uff0c\u53c8\u9053\uff1a \u201c\u5e08\u53d4\u4eec\u65e2\u7136\u5c31\u8981\u5230\u676d\u5dde\u53bb\uff0c\u90a3\u5f1f\u5b50\u5c31\u884c\u5148\u5feb\u9a6c\u56de\u53bb\u7980\u544a\u5bb6\u5e08\uff0c\u8ba9\u5bb6\u5e08\u4e5f\u597d\u51c6\u5907\u63a5\u5f85\u4e24\u4f4d\u5e08 \u53d4\u7684\u5927\u9a7e\u4e8e\u676d\u5dde\u57ce\u5916\u3002\u201d \u3000\u3000\u6c6a\u4e00\u9e4f\u53c8\u81ea\u51b7\u7b11\uff1a\u201c\u90a3\u53ef\u4e0d\u6562\u5f53\uff0c\u53ea\u8981\u4ed6\u2014\u2014\u201d\u94c1\u5e73\u751f\u6015\u4ed6\u53c8\u8bf4\u51fa\u96be\u542c\u7684\u8bdd\u6765\uff0c\u8fde\u5fd9\u8eac \u8eab\u9053\uff1a\u201c\u90a3\u4e48\u5f1f\u5b50\u5c31\u5148\u544a\u8f9e\u4e86\u3002\u201d\u8f6c\u8eab\u8d70\u51fa\u95e8\u5916\uff0c\u4e24\u4e2a\u8d77\u843d\uff0c\u63a0\u5230\u5cb8\u8fb9\uff0c\u7eb5\u8eab\u8dc3\u4e0a\u8239\u5934\uff0c\u5406 \u559d\u4e00\u58f0\uff0c\u90a3\u5feb\u8247\u53c8\u590d\u7834\u6d6a\u800c\u53bb\u3002 \u3000\u3000\u6c6a\u6c0f\u6606\u4ef2\u53ea\u89c1\u8fd9\u5feb\u8247\u53bb\u8fdc\uff0c\u51b7\u51b7\u4e00\u7b11\uff0c\u6c6a\u4e00\u9e23\u7a81\u5730\u56de\u5934\u5411\u90a3\u4e94\u4e2a\u5c11\u5e74\u53f1\u9053\uff1a\u201c\u4f60\u4eec\u770b\u770b \u4eba\u5bb6\u7684\u5f92\u5f1f\uff0c\u662f\u4f55\u7b49\u7cbe\u660e\u5e72\u7ec3\uff0c\u54fc\u2014\u2014\u4f60\u4eec\u54ea\u91cc\u53ca\u5f97\u4e0a\u4eba\u5bb6\u534a\u5206\uff0c\u53ea\u4f1a\u66ff\u6211\u5728\u5916\u9762\u60f9\u4e8b\u751f \u975e\uff0c\u90a3\u65e5\u5728\u897f\u6e56\u82e5\u4e0d\u662f\u4f60\u4eec\u4e94\u4e2a\u8822\u624d\uff0c\u54fc\u2014\u2014\u201d\u4ed6\u51b7\u54fc\u4e00\u58f0\uff0c\u500f\u7136\u987f\u4f4f\uff0c\u90a3\u4e94\u4e2a\u5c11\u5e74\u4f60\u671b \u6211\uff0c\u6211\u671b\u4f60\uff0c\u8138\u4e0a\u7ea2\u5f97\u50cf\u662f\u7ea2\u5e03\u4e00\u6837\uff0c\u534a\u53e5\u8bdd\u4e5f\u8bf4\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u6c6a\u4e00\u9e23\u53cc\u76ee\u4e00\u5f20\uff0c\u5374\u53c8\u5389\u53f1\u4e00\u58f0\uff1a\u201c\u8fd8\u4e0d\u5feb\u53bb\u7275\u9a6c\uff01\u201d \u3000\u3000\u53ef\u601c\u8fd9\u4e94\u4e2a\u5c11\u5e74\uff0c\u89c1\u5230\u5e08\u7236\u5c06\u90a3\u91d1\u886b\u5c11\u5e74\u51b7\u5632\u70ed\u8bbd\u5730\u9a82\u4e86\u4e00\u987f\uff0c\u5fc3\u4e2d\u65b9\u5728\u5f97\u610f\uff0c\u5374\u4e0d\u77e5 \u5e08\u7236\u56de\u8fc7\u5934\u6765\uff0c\u53c8\u5c06\u81ea\u5df1\u75db\u9a82\u4e00\u987f\uff0c\u4e94\u4eba\u5fc3\u91cc\u867d\u7136\u6c14\u6124\uff0c\u4f46\u5374\u4ecd\u4e56\u4e56\u5730\u5c06\u9a6c\u7275\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u6c6a\u6c0f\u6606\u4ef2\u7ffb\u8eab\u4e0a\u9a6c\uff0c\u6c6a\u4e00\u9e4f\u7a81\u53c8\u51b7\u7b11\u9053\uff1a\u201c\u8001\u4e8c\uff0c\u90a3\u59d3\u6bdb\u7684\u8fd1\u6765\u786e\u662f\u8d8a\u6765\u8d8a\u72c2\u4e86\uff0c\u4f9d\u6211 \u7684\u610f\u601d\uff0c\u676d\u5dde\u57ce\u6211\u5c31\u7edd\u4e0d\u4f1a\u7b54\u5e94\u4ed6\u53bb\u7684\u3002\u201d \u3000\u3000\u6c6a\u4e00\u9e23\u5fae\u559f\u4e00\u58f0\uff1a\u201c\u5927\u54e5\uff0c\u51e1\u4e8b\u4e5f\u8be5\u60f3\u5f97\u5f00\u4e9b\uff0c\u59d3\u6bdb\u7684\u8fd1\u6765\u867d\u592a\u7316\u72c2\uff0c\u4f46\u6211\u5144\u5f1f\u53c8\u4f55\u82e6 \u5f97\u7f6a\u6b64\u4eba\u5462\u3002\u201d\u4ed6\u76ee\u5149\u4e00\u8f6c\uff0c\u53c8\u81ea\u7b11\u9053\uff1a\u201c\u6b64\u523b\u65f6\u5df2\u8fd1\u5348\uff0c\u6211\u4eec\u8fd8\u662f\u8d76\u5230\u524d\u9762\uff0c\u5f80\u90a3\u5cb3\u738b\u5e99 \u53bb\u4e00\u8f6c\uff0c\u7136\u540e\u518d\u8d76\u53bb\u4e09\u5854\u5bfa\u5403\u90a3\u6709\u540d\u7684\u7d20\u658b\u5427\uff0c\u5509\uff01\u8fd1\u5e74\u6765\u6211\u4eec\u867d\u8bf4\u6781\u5c11\u53c2\u4e0e\u6b66\u6797\u7eb7\u4e89\uff0c\u4f46 \u5374\u51e0\u65f6\u6709\u50cf\u8fd1\u6708\u6765\u8fd9\u822c\u60a0\u95f2\u81ea\u5728\u8fc7\u8c03\u4ed6\u4e00\u626c\u97ad\uff0c\u7adf\u5148\u9a70\u53bb\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u5c31\u5df2\u671b\u5230\u5cb3\u738b\u5e99\u524d\u7684 \u53c2\u5929\u53e4\u67cf\u3002\u4ec1\u7acb\u5728\u9636\u524d\u7684\u534e\u670d\u5c11\u5e74\uff0c\u76ee\u5149\u8f6c\u5904\uff0c\u89c1\u5230\u8fd9\u4e03\u4eba\u4e03\u9a6c\u9a76\u5165\u6797\u6765\uff0c\u5251\u7709\u5fae\u8f69\uff0c\u76ee\u5149 \u4e2d\u6cdb\u51fa\u559c\u8272\uff0c\u663e\u89c1\u8fd9\u201d\u6cb3\u6714\u53cc\u5251\u201d\u5c31\u662f\u4ed6\u7b49\u5f85\u7740\u7684\u4eba\uff0c\u53ea\u662f\u4ed6\u7b49\u5f85\u4ed6\u4eec\u7a76\u7adf\u662f\u4e3a\u4e86\u4ec0\u4e48\uff0c\u5374 \u53c8\u53eb\u4eba\u96be\u4ee5\u731c\u6d4b\uff01 \u3000\u3000\u6c6a\u6c0f\u6606\u4ef2\u7ffb\u8eab\u4e0b\u4e86\u9a6c\uff0c\u5c06\u9a6c\u97ad\u4ea4\u7ed9\u8eab\u540e\u7684\u5f1f\u5b50\uff0c\u7f13\u6b65\u8e31\u5411\u5cb3\u738b\u658b\u7684\u5bfa\u95e8\uff0c\u7a81\u5730\u89c1\u5230\u4e00\u4e2a \u534e\u670d\u5c11\u5e74\uff0c\u542b\u7b11\u8fce\u9762\u800c\u6765\u3002 \u3000\u3000\u6c6a\u4e00\u9e23\u76ee\u5149\u4e00\u8f6c\uff0c\u4fa7\u76ee\u9053\uff1a\u8fd9\u5c11\u5e74\u770b\u6765\u9887\u89c9\u9762\u5584\uff0c\u53c8\u4f3c\u51b2\u7740\u6211\u4eec\u800c\u6765\uff0c\u5927\u54e5\uff0c\u4f60\u53ef\u8bb0\u5f97 \u6b64\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u6c6a\u4e00\u9e4f\u5fae\u4e00\u6c89\u541f\uff1a\u201c\u6211\u4e5f\u89c9\u6b64\u4eba\u9887\u4e3a\u9762\u5584\u2014\u2014\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u5374\u89c1\u8fd9\u5c11\u5e74\u6ee1\u9762\u542b\u7b11\u884c\u6765\uff0c \u6717\u58f0\u9053\uff1a\u201c\u4e24\u4f4d\u5927\u4fa0\u78ca\u843d\u98ce\u6807\uff0c\u5982\u679c\u5c0f\u53ef\u672a\u66fe\u8bb0\u9519\u7684\u8bdd\uff0c\u4e24\u4f4d\u60f3\u5fc5\u5c31\u662f\u540d\u9707\u5929\u4e0b\uff0c\u53f1\u54a4\u6c5f\u6e56 \u7684\u2018\u6cb3\u6714\u53cc\u5251\uff0c\u6c6a\u6c0f\u6606\u4ef2\u5427\uff01\u201d\u201c\u6cb3\u6714\u53cc\u5251\u201d\u9f50\u5730\u4e00\u695e\uff1a\u201c\u8fd9\u5c11\u5e74\u600e\u5730\u8ba4\u5f97\u6211\u4eec\uff1f\u201d \u3000\u3000\u76ee\u5149\u6307\u5904\uff0c\u53ea\u89c1\u8fd9\u5c11\u5e74\u76ee\u5982\u6717\u661f\uff0c\u987e\u76fc\u751f\u59ff\uff0c\u7389\u9762\u6731\u5507\uff0c\u4fca\u7f8e\u65e0\u5339\uff0c\u8a00\u8c08\u4e3e\u6b62\uff0c\u5374\u53c8\u6587 \u8d28\u5f6c\u5f6c\uff0c\u6839\u672c\u4e0d\u4f3c\u6b66\u6797\u4e2d\u4eba\u3002 \u3000\u3000\u4ed6\u4e24\u4eba\u5fc3\u4e2d\u867d\u72d0\u7591\uff0c\u4f46\u89c1\u8fd9\u5c11\u5e74\u98ce\u59ff\u4e0d\u4fd7\uff0c\u5fc3\u4e0b\u4e5f\u6709\u4e09\u5206\u597d\u611f\u3002 \u3000\u3000\u6c6a\u4e00\u545c\u51b7\u7b11\u9053\uff1a\u201c\u655d\u5144\u5f1f\u6b63\u662f\u2018\u6cb3\u6714\u53cc\u5251\uff0c\u81f3\u4e8e\u540d\u9707\u5929\u4e0b\u2014\u2014\u54c8\u54c8\uff0c\u5374\u4e0d\u6562\u5f53\u3002\u201d\u8fd9\u5c11 \u5e74\u7684\u53cc\u7709\u4e00\u626c\uff0c\u559c\u52a8\u989c\u8272\uff0c\u62cd\u638c\u9053\uff1a\u201c\u662f\u4e86\uff0c\u679c\u7136\u662f\u2018\u6cb3\u6714\u53cc\u5251\u2019\uff0c\u5c0f\u53ef\u4eca\u65e5\u80fd\u89c1\u5230\u5f53\u4ee3\u4e24 \u5927\u5251\u5ba2\u4e4b\u9762\uff0c\u771f\u662f\u4e09\u751f\u6709\u5e78\u3002\u201d \u3000\u3000\u81ea\u53e4\u81f3\u4eca\uff0c\u4e16\u4e0a\u4ece\u65e0\u4e00\u4eba\u4e0d\u559c\u522b\u4eba\u5949\u627f\uff0c\u4ed6\u6de1\u6de1\u51e0\u53e5\u8bdd\uff0c\u8bf4\u5f97\u6c6a\u4e00\u9e4f\u4ea6\u81ea\u5c55\u989c\u4e00\u7b11\uff0c \u9053\uff1a\u201c\u591a\u627f\u5144\u53f0\u539a\u7231\uff0c\u655d\u5144\u5f1f\u5b9e\u5728\u60ed\u6127\u5f97\u5f88\uff0c\u53ea\u662f\u2014\u2014\u54c8\u54c8\uff0c\u4f11\u602a\u5728\u4e0b\u51fa\u8a00\u65e0\u72b6\uff0c\u5144\u53f0\u770b\u6765 \u867d\u7136\u6781\u4e3a\u9762\u5584\uff0c\u4f46\u6211\u5144\u5f1f\u5e74\u8001\u7cca\u6d82\u2014\u2014\u54c8\u54c8\uff0c\u5374\u5b9e\u5728\u8bb0\u4e0d\u5f97\u4f55\u5904\u66fe\u8046\u5144\u53f0\u96c5\u6559\u4e86\u3002\u201d \u3000\u3000\u8fd9\u5c11\u5e74\u542b\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u81ea\u7136\uff0c\u60f3\u4e24\u4f4d\u4e43\u5f53\u4ee3\u5927\u4fa0\uff0c\u5c0f\u53ef\u4e00\u89c1\uff0c\u81ea\u7136\u4fbf\u518d\u4e5f\u4e0d\u4f1a\u5fd8\u8bb0\uff0c\u81f3 \u4e8e\u5c0f\u53ef\u4e48\u2014\u2014\u201d\u4ed6\u5fae\u7b11\u4e00\u4e0b\uff0c\u4e00\u63d6\u5230\u5730\u3002 \u3000\u3000\u201c\u5c0f\u53ef\u7f2a\u6587\uff0c\u90a3\u65f6\u968f\u7740\u4e16\u5144\u77f3\u78f7\uff0c\u5728\u897f\u6e56\u6e38\u6625\uff0c\u5374\u4e0d\u60f3\u9047\u7740\u51e0\u4e2a\u7c97\u8c6a\u6c49\u5b50\uff0c\u4e00\u89c1\u655d\u53cb\u77f3 \u78f7\uff0c\u5c31\u5c06\u4ed6\u62c9\u5230\u90a3\u8258\u8239\u4e0a\uff0c\u540e\u6765\u2014\u2014\u201d\u6c6a\u4e00\u9e4f\u7b11\u5bb9\u4e00\u655b\u2026\u2026 \u3000\u3000\u201c\u7f2a\u6587\u5c31\u5728\u90a3\u6bdb\u5bb6\u59d1\u5a18\u7684\u8239\u4e0a\u89c1\u8fc7\u6211\u5144\u5f1f\u7684\uff1f\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u7b11\u9053\uff1a\u201c\u90a3\u59d3\u6bdb\u7684\u5973\u5b50\uff0c\u5c0f\u53ef\u4ec5\u6709\u4e00\u9762\u4e4b\u4ea4\uff0c\u5f53\u65f6\u89c1\u7740\u5979\u90a3\u7b49\u5f20\u7316\uff0c\u76ee\u65e0\u5c0a \u957f\uff0c\u82e5\u975e\u5c0f\u53ef\u624b\u65e0\u7f1a\u9e21\u4e4b\u529b\uff0c\u662f\u8981\u60e9\u6212\u4e8e\u5979\uff0c\u540e\u6765\u89c1\u5230\u4e24\u4f4d\u5927\u4fa0\u82f1\u59ff\uff0c\u6c14\u5ea6\u90a3\u822c\u6062\u5b8f\uff0c\u5c0f\u53ef \u5b9e\u5728\u5fc3\u6298\u4e0d\u5df1\u3002\u201d \u3000\u3000\u6c6a\u4e00\u9e23\u5f3a\u7b11\u9053\uff1a\u201c\u5144\u53f0\u5982\u6b64\u8bf4\u6765\uff0c\u5012\u53eb\u6211\u5144\u5f1f\u65e0\u5730\u81ea\u5bb9\u4e86\u3002\u201d \u3000\u3000\u7f2a\u6587\u9762\u8272\u4e00\u6574\uff0c\u6b63\u8272\u9053\uff1a\u201c\u5c0f\u53ef\u6240\u8bf4\uff0c\u7684\u786e\u53e5\u53e5\u90fd\u662f\u80ba\u8151\u4e4b\u8a00\uff0c\u5c0f\u53ef\u867d\u7136\u4e0d\u61c2\u6b66\u529f\uff0c\u4fbf \u4e5f\u770b\u5f97\u51fa\u90a3\u59d3\u6bdb\u7684\u5973\u5b50\u5b9e\u662f\u4ed7\u7740\u624b\u4e2d\u4e00\u67c4\u602a\u5251\uff0c\u5077\u5de7\u80dc\u5f97\u4e24\u4f4d\u5c11\u8bb8\uff0c\u82e5\u8bba\u771f\u5b9e\u529f\u529b\uff0c\u4e24\u4f4d\u5927 \u4fa0\u6570\u5341\u5e74\u4fee\u4e3a\uff0c\u90a3\u59d3\u6bdb\u5973\u5b50\u54ea\u91cc\u80fd\u53ca\u5f97\u4e0a\u4e24\u4f4d\u5927\u4fa0\u534a\u5206\uff1f\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u8bda\u6073\uff0c\u8a00\u8bed\u53c8\u6781\u5f97\u4f53\uff0c\u6b63\u8bf4\u5230\u201c\u6cb3\u6714\u53cc\u5251\u201d\u5fc3\u91cc\u3002 \u3000\u3000\u6c6a\u4e00\u9e4f\u53c8\u81ea\u5c55\u989c\u4e00\u7b11\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\uff0c\u60f3\u4e0d\u5230\uff0c\u5144\u53f0\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u6587\u91c7\u98ce\u6d41\uff0c\u5bf9\u6b66 \u529f\u4e00\u9053\uff0c\u5374\u6709\u5982\u6b64\u7cbe\u8f9f\u7684\u89c1\u89e3\uff0c\u54c8\u54c8\uff01\u4e0d\u7792\u5144\u53f0\u8bf4\uff0c\u6211\u5144\u5f1f\u90a3\u65e5\u7684\u786e\u8f93\u5f97\u4e0d\u670d\uff0c\u4f46\u770b\u5728\u5979\u5c0a \u957f\u9762\u4e0a\uff0c\u4e5f\u53ea\u5f97\u5fcd\u6c14\uff0c\u76f4\u5230\u4eca\u65e5\u89c1\u7740\u7f2a\u5144\uff0c\u542c\u5230\u7f2a\u5144\u5982\u6b64\u9ad8\u8bba\uff0c\u624d\u603b\u7b97\u7565\u8212\u5fc3\u4e2d\u95f7\u6c14\uff0c\u54c8 \u54c8\uff01\u7f2a\u5144\u5012\u771f\u662f\u6211\u5144\u5f1f\u7684\u6b66\u6797\u77e5\u5df2\u3002\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u542b\u7b11\u9053\uff1a\u201c\u5c0f\u53ef\u4e0d\u8fc7\u662f\u5c06\u773c\u4e2d\u6240\u89c1\uff0c\u7387\u76f4\u8bf4\u51fa\uff0c\u4e24\u4f4d\u5927\u4fa0\u5982\u679c\u5c06\u5c0f\u53ef\u5f15\u4e3a\u77e5 \u5df2\uff0c\u90a3\u771f\u53eb\u5c0f\u53ef\u559c\u51fa\u671b\u5916\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u5fae\u987f\uff0c\u7a81\u53c8\u6545\u610f\u957f\u53f9\u4e00\u58f0\uff1a\u201c\u4e0d\u8fc7\uff0c\u5509\uff01\u4e16\u98ce\u65e5\u4e0b\uff0c\u4eba\u5fc3\u4e0d\u53e4\uff0c\u90a3\u6bdb\u59d1\u5a18\u5c0f\u5c0f\u5e74 \u7eaa\uff0c\u975e\u4f46\u4e0d\u77e5\u656c\u91cd\u5c0a\u957f\uff0c\u800c\u4e14\u2014\u2014\u5509\uff0c\u800c\u4e14\u2014\u2014\u3002\u201d \u3000\u3000\u4ed6\u4e00\u8fde\u8bf4\u4e86\u4e24\u4e2a\u201c\u800c\u4e14\u201d\uff0c\u90a3\u6c6a\u4e00\u9e4f\u679c\u7136\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4f60\u6211\u867d\u7136\u53ea\u521d\u4ea4\uff0c\u4f46\u53ef\u8bf4\u4e00\u89c1 \u5982\u6545\uff0c\u7f2a\u5144\u6709\u4ec0\u4e48\u8bdd\uff0c\u5c3d\u7ba1\u8bf4\u51fa\u4fbf\u662f\u3002\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u6447\u5934\u53f9\u9053\uff1a\u201c\u90a3\u65e5\u4e24\u4f4d\u5927\u4fa0\u8d70\u540e\uff0c\u90a3\u6bdb\u59d1\u5a18\u82e5\u662f\u7a0d\u77e5\u4e24\u5206\u9053\u7406\uff0c\u4fbf\u8be5\u4f53\u4f1a\u5f97\u51fa \u4e24\u4f4d\u7684\u5bbd\u6000\u5927\u5ea6\uff0c\u54ea\u77e5\u4e24\u4f4d\u5927\u4fa0\u4e00\u8d70\uff0c\u5979\u4fbf\u51b7\u8a00\u70ed\u8bed\u5730\u6f2b\u9a82\u8d77\u6765\uff0c\u8fd8\u8bf4\u4ec0\u4e48\uff0c\u4eca\u65e5\u4e4b\u6b66\u6797\uff0c \u5df2\u662f\u6bdb\u5bb6\u5929\u4e0b\u2014\u2014\u201d\u6c6a\u4e00\u9e4f\u795e\u8272\u4e00\u53d8\uff0c\u6c6a\u4e00\u9e23\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u5374\u4e0d\u7981\u6697\u81ea\u601d\u5fd6\uff1a\u201c\u8fd9\u5c11\u5e74\u4e0e\u6211\u7b49 \u7d20\u4e0d\u76f8\u8bc6\uff0c\u5982\u6b64\u7ed3\u4ea4\u4e8e\u6211\uff0c\u53c8\u5982\u6b64\u66f2\u610f\u606d\u7ef4\uff0c\u96be\u9053\u662f\u6709\u7740\u4ec0\u4e48\u7528\u610f\u4e0d\u6210\uff1f\u201d \u3000\u3000\u5374\u89c1\u201c\u7f2a\u6587\u201d\u53c8\u81ea\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6b64\u4e8b\u4e0e\u5c0f\u53ef\u672c\u6765\u6beb\u65e0\u5e72\u7cfb\uff0c\u6709\u4e9b\u8bdd\u5c0f\u53ef\u4ea6\u662f\u4e0d\u8be5\u8bf4 \u7684\uff0c\u4f46\u5c0f\u53ef\u89c1\u4e86\u8fd9\u7b49\u60c5\u4e8b\uff0c\u5fc3\u91cc\u5374\u53c8\u4e0d\u7981\u4e3a\u4e24\u4f4d\u5927\u4fa0\u53eb\u5c48\u3002\u201d \u3000\u3000\u6c6a\u4e00\u9e23\u4e0d\u7981\u53c8\u5fd6\u9053\uff1a\u201c\u662f\u4e86\uff0c\u6b64\u4eba\u4e0e\u6211\u7b49\u65e0\u6beb\u5229\u5bb3\u5e72\u7cfb\uff0c\u4e0e\u90a3\u6bdb\u81ec\u4ea6\u65e0\u4ec7\u6028\uff0c\u60f3\u6765\u7684\u786e \u6ca1\u6709\u7528\u610f\u3002\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u5df2\u63a5\u53e3\u53f9\u9053\uff1a\u201c\u539f\u5148\u6211\u672c\u8fd8\u4ee5\u4e3a\u662f\u90a3\u6bdb\u59d1\u5a18\u5e74\u8f7b\u65e0\u77e5\uff0c\u54ea\u77e5\u2014\u2014\u5509\uff0c\u5979\u7239\u7239\u540e \u6765\u6765\u4e86\uff0c\u6240\u8bf4\u7684\u8bdd\uff0c\u7adf\u6bd4\u90a3\u5c0f\u5973\u5b50\u66f4\u52a0\u65e0\u793c\uff0c\u6709\u4f4d\u59d3\u80e1\u7684\u8fd8\u8bf4\u4ec0\u4e48\uff1a\u2018\u6587\u742a\u5982\u6b64\uff0c\u53ea\u6015\u6c6a\u6c0f \u6606\u4ef2\u8981\u751f\u6c14\u4e86\u3002\u2019\u54ea\u77e5\u90a3\u4f4d\u2018\u6bdb\u5927\u592a\u7237\uff0c\u7adf\u51b7\u7b11\u7740\u9053\uff1a\u2018\u751f\u6c14\u53c8\u6709\u4f55\u59a8\uff0c\u8c05\u8fd9\u4e24\u4eba\u4e5f\u4e0d\u6562\u5bf9 \u6211\u600e\u6837\u3002\u2019\u5509\uff01 \u3000\u3000\u4e0d\u662f\u5c0f\u53ef\u6545\u610f\u5728\u4e24\u4f4d\u9762\u524d\u5982\u6b64\u8bf4\u6cd5\uff0c\u5f53\u65f6\u5c0f\u53ef\u542c\u4e86\u8fd9\u7b49\u8bdd\uff0c\u5f53\u771f\u662f\u5fcd\u6c14\u4e0d\u4f4f\uff0c\u7adf\u5fcd\u4e0d\u4f4f \u51fa\u53e3\u9876\u649e\u4e86\u4e24\u53e5\uff0c\u5509\uff01\u82e5\u975e\u655d\u53cb\u77f3\u78f7\u5728\u4e2d\u95f4\u529d\u963b\uff0c\u53ea\u6015\u5c0f\u53ef\u90a3\u65e5\u4e5f\u8981\u53d7\u8fb1\u5728\u6bdb\u5bb6\u7236\u5973\u624b \u4e0b\u3002\u201d \u3000\u3000\u4ed6\u6c89\u58f0\u9053\u6765\uff0c\u53e5\u53e5\u542c\u6765\uff0c\u90fd\u4f3c\u5343\u771f\u4e07\u786e\uff0c\u6c6a\u4e00\u9e23\u60f3\u6765\u60f3\u53bb\uff0c\u53ea\u89c9\u8fd9\u5c11\u5e74\u4e07\u65e0\u7f16\u9020\u4e8b\u5b9e\u7684 \u7406\u7531\uff0c\u90a3\u6c6a\u4e00\u9e4f\u66f4\u662f\u65e9\u5df2\u76f8\u4fe1\uff0c\u6b64\u523b\u662f\u6c14\u5f97\u9762\u76ee\u53d8\u8272\uff0c\u9891\u9891\u4ee5\u62f3\u51fb\u638c\uff0c\u54ac\u7259\u5207\u9f7f\u5730\u4fa7\u987e\u6c6a\u4e00 \u9e23\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u8001\u4e8c\uff0c\u8fd9\u79cd\u4e8b\u662f\u53ef\u5fcd\u5b70\u4e0d\u53ef\u5fcd\uff0c\u54fc\uff01\u6211\u65e9\u5c31\u77e5\u9053\u90a3\u59d3\u6bdb\u7684\u4e0d\u662f\u771f\u5fc3\u6765\u5411\u6211\u7b49 \u966a\u8bdd\uff0c\u54fc\u2014\u2014\u4ed6\u53eb\u6211\u4eec\u53bb\u90a3\u676d\u5dde\u57ce\uff0c\u53ea\u6015\u4e5f\u6ca1\u6709\u4ec0\u4e48\u597d\u610f\u3002\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u76ee\u4e2d\u795e\u5149\u4e00\u95ea\uff0c\u4f46\u77ac\u5373\u655b\u53bb\uff0c\u53c8\u81ea\u53f9\u9053\uff1a\u201c\u4ed6\u679c\u7136\u53c8\u505a\u51fa\u8fd9\u7b49\u82b1\u6837\uff0c\u90a3\u65e5\u4ed6\u66fe \u9053\uff1a\u2018\u8001\u592b\u867d\u4e0d\u6015\u8fd9\u4e24\u4eba\u4f5c\u4e71\uff0c\u4f46\u4e5f\u4e0d\u5fc5\u53eb\u4ed6\u4eec\u592a\u4f24\u5fc3\uff0c\u8fc7\u4e24\u65e5\u968f\u4fbf\u53eb\u4e2a\u4eba\u627e\u4ed6\u4eec\u966a\u4e24\u53e5\u8bdd \u5c31\u662f\u4e86\u3002\u60f3\u90a3\u4e24\u4eba\u4e5f\u5c31\u2014\u2014\u201d\u6c6a\u4e00\u9e4f\u5927\u559d\u4e00\u58f0\uff1a\u201c\u8001\u4e8c\uff0c\u4f60\u770b\u600e\u5730\uff1f\u201d \u3000\u3000\u6c6a\u4e00\u545c\u76ee\u5149\u4e4b\u4e2d\uff0c\u4ea6\u4e0d\u7981\u6cdb\u51fa\u6028\u6bd2\u4e4b\u8272\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u76ee\u5149\u4e00\u8f6c\uff0c\u7a81\u5730\u6717\u58f0\u4e00\u7b11\uff1a\u201c\u8bdd\u53c8\u8bf4\u56de\u6765\u4e86\uff0c\u4e24\u4f4d\u4e5f\u4e0d\u5fc5\u548c\u90a3\u7b49\u66b4\u53d1\u6237\u822c\u7684\u72c2 \u5984\u5c0f\u4eba\u4e00\u822c\u89c1\u8bc6\uff0c\u95fb\u9053\u90a3\u4e09\u5854\u5bfa\u7684\u7d20\u658b\u6781\u597d\uff0c\u54c8\u2014\u2014\u4eca\u65e5\u5c0f\u53ef\u4f5c\u4e1c\uff0c\u8bf7\u4e24\u4f4d\u5c1d\u5c1d\u6c99\u95e8\u98ce \u5473\u3002\u201d\u6b64\u523b\u4ed6\u53c8\u4f5c\u51fa\u4e00\u526f\u606f\u4e8b\u5b81\u4eba\u7684\u6837\u5b50\u6765\u4e86\u3002 \u3000\u3000\u4e8e\u662f\u4e00\u4e00\u3002 \u3000\u3000\u90a3\u201c\u7075\u86c7\u201d\u6bdb\u684c\u7684\u4ec7\u654c\uff0c\u4fbf\u53c8\u591a\u4e86\u4e24\u4e2a\u3002 \u3000\u3000\u201c\u6cb3\u6714\u53cc\u5251\u201d\u4ee5\u53ca\u201c\u7f2a\u6587\u201d\u7545\u6e38\u8fc7\u540e\uff0c\u56de\u5230\u5609\u5174\u7684\u65f6\u5019\uff0c\u5df2\u7ecf\u662f\u9ec4\u660f\u4e86\uff0c\u8fd9\u534a\u65e5\u95f4\uff0c \u201c\u6cb3\u6714\u53cc\u5251\u201d\u5bf9\u8fd9\u8a00\u8bed\u5f97\u4f53\uff0c\u6027\u60c5\u6177\u6168\u7684\u5bcc\u5bb6\u5c11\u5e74\uff0c\u4e0d\u7981\u53c8\u589e\u4e86\u51e0\u5206\u597d\u611f\uff0c\u518d\u4e09\u7559\u4ed6\u591c\u6765\u75db \u996e\uff0c\u4f46\u662f\u4ed6\u5ba2\u6c14\u5730\u8c26\u8c22\u7740\uff0c\u5ba2\u6c14\u5730\u5a49\u62d2\u4e86\u3002 \u3000\u3000\u4ed6\u8bf4\uff1a\u201c\u5c0f\u53ef\u5728\u6b64\u95f4\u8fd8\u6709\u4e2a\u7236\u6267\u957f\u8f88\uff0c\u8981\u53bb\u62dc\u89c1\uff0c\u660e\u65e5\u5c0f\u53ef\u5b9a\u5fc5\u518d\u6765\u62dc\u8bbf\u3002\u201d\u4ed6\u8d70\u4e86\u4e4b \u540e\uff0c\u201c\u6cb3\u6714\u53cc\u5251\u2019\u7684\u5ba2\u6808\u4e2d\uff0c\u7acb\u523b\u9001\u6765\u4e00\u684c\u6781\u4e3a\u4e30\u76db\u7684\u71d5\u7fc5\u5927\u7b75\uff0c\u548c\u4e00\u575b\u7a96\u85cf\u591a\u5e74\u7684\u201d\u5973\u513f \u7ea2\u201d\u9152\uff0c\u968f\u6765\u638c\u52fa\u7684\u5927\u5e08\u5085\u8bf4\u662f\u6765\u81ea\u5609\u5174\u6700\u597d\u7684\u9152\u697c\u201c\u4e00\u5fc3\u4ead\u201d\uff0c\u662f\u4e00\u4e2a\u5e74\u8f7b\u7684\u516c\u5b50\u547d\u4ed6\u9001 \u6765\u7ed9\u6c6a\u5927\u4fa0\u7684\uff0c\u5e76\u4e14\u968f\u9644\u6709\u4e00\u5f20\u6ce5\u91d1\u5927\u7ea2\u62dc\u8d34\uff0c\u4e0a\u9762\u5ba2\u6c14\u800c\u606d\u656c\u5730\u5199\u7740\uff1a\u201c\u611a\u665a\u7f2a\u6587\u656c\u732e\u6c6a \u6c0f\u8d24\u6606\u4ef2\u3002\u201d \u3000\u3000\u201c\u6cb3\u6714\u53cc\u5251\u201d\u6ee1\u610f\u5730\u7b11\u4e86\uff0c\u6c5f\u6e56\u8c6a\u58eb\uff0c\u5c31\u559c\u6b22\u8fd9\u79cd\u8c03\u8c03\u513f\u3002 \u3000\u3000\u201c\u8c6a\u723d\u3001\u6177\u6168\u3001\u70ed\u60c5\u2014\u2014\u8fd9\u5c11\u5e74\u5012\u771f\u4e2a\u662f\u591f\u670b\u53cb\u3002\u201d \u3000\u3000\u4ec7\u6055\u867d\u7136\u6ca1\u6709\u770b\u5230\u4ed6\u4eec\u7684\u7b11\u5bb9\uff0c\u4f46\u5374\u4e5f\u60f3\u50cf\u5f97\u51fa\uff0c\u4ed6\u56de\u5230\u81ea\u5df1\u4f4f\u7684\u5e97\u623f\uff0c\u4e0d\u5230\u4e00\u523b\uff0c\u7acb \u523b\u53c8\u6709\u4e00\u6572\u95e8\u7684\u58f0\u97f3\uff0c\u8fde\u6572\u4e94\u4e0b\uff0c\u4ed6\u77e5\u9053\u53c8\u662f\u90a3\u201c\u6881\u4e0a\u4eba\u201d\u7684\u5f1f\u5144\u524d\u6765\u62a5\u544a\u4e00\u4e9b\u4e8b\u4e86\uff0c\u5bf9\u4e8e \u6881\u4e0a\u4eba\uff0c\u4ed6\u5fc3\u91cc\u7684\u786e\u6709\u7740\u4e00\u4efd\u771f\u8bda\u7684\u611f\u6fc0\uff0c\u82e5\u4e0d\u662f\u8fd9\u88ab\u6c5f\u6e56\u4eba\u79f0\u4e3a\u201c\u4e5d\u8db3\u795e\u86db\uff0c\u6881\u4e0a\u541b\u5b50\u201d \u7684\u6881\u4e0a\u4eba\u4e3a\u4ed6\u5e03\u4e0b\u4e86\u6709\u5982\u5929\u7f57\u5730\u7f51\u822c\u7684\u201c\u86db\u7f51\u201d\uff0c\u4ed6\u7eb5\u6709\u901a\u5929\u672c\u9886\uff0c\u5374\u4e5f\u4e0d\u80fd\u5c06\u4e8b\u60c5\u529e\u5f97\u5982 \u6b64\u987a\u5229\u3002 \u3000\u3000\u201c\u54c8\u54c8\uff0c\u2018\u4e5d\u8db3\u795e\u86db\u2019\uff0c\u8718\u86db\u800c\u6709\u4e5d\u8db3\uff0c\u603b\u6bd4\u4e00\u6761\u86c7\u8981\u5389\u5bb3\u5f97\u591a\u4e86\u5427\uff01\u201d\u4ed6\u9ad8\u5174\u5730\u5f00\u4e86 \u95e8\uff0c\u95e8\u5916\u7acb\u523b\u95ea\u4eba\u4e00\u4e2a\u81c3\u80bf\u7684\u80d6\u5b50\uff0c\u8fd9\u80d6\u5b50\u8eab\u6750\u81c3\u80bf\uff0c\u884c\u52a8\u5374\u6781\u8fc5\u901f\uff0c\u4e00\u95ea\u800c\u5165\u968f\u624b\u5e26\u4e0a\u623f \u95e8\uff0c\u5411\u4ec7\u6055\u8eac\u8eab\u4e00\u793c\uff0c\u4ec7\u6055\u6446\u624b\u8c26\u8c22\uff0c\u8fd9\u80d6\u5b50\u7b11\u9053\uff1a\u201c\u516c\u5b50\u771f\u6709\u4e24\u624b\uff0c\u548c\u90a3\u4e24\u4e2a\u59d3\u6c6a\u7684\u4e5f\u62c9 \u4e0a\u4ea4\u60c5\u4e86\uff0c\u6211\u5f20\u4e00\u6876\u8d70\u5357\u95ef\u5317\uff0c\u770b\u6765\u770b\u53bb\uff0c\u9664\u4e86\u6211\u4eec\u6881\u5927\u54e5\u53ef\u7b97\u662f\u5927\u82f1\u96c4\uff0c\u771f\u6709\u4e24\u4e0b\u5b50\u4e4b \u5916\uff0c\u563f\u2014\u2014\u53ef\u5c31\u5f97\u7b97\u662f\u516c\u5b50\u60a8\u4e86\u3002\u201d\u4ed6\u8a00\u8bed\u4e2d\u867d\u5c06\u4ec7\u6055\u5217\u5728\u201c\u6881\u5927\u54e5\u201d\u4e4b\u4e0b\uff0c\u4f46\u4ec7\u6055\u975e\u4f46\u4e0d \u4ee5\u4e3a\u601c\uff0c\u8fd8\u6781\u4e3a\u9ad8\u5174\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u4ed6\u77e5\u9053\u90a3\u201c\u4e5d\u8db3\u795e\u86db\uff0c\u6881\u4e0a\u541b\u5b50\u201d\u6881\u4e0a\u4eba\uff0c\u5728\u8fd9\u4e9b\u5e02\u4e95\u597d\u6c49\u5fc3\u76ee\u4e2d\u7684\u8eab\u4efd\u548c\u5730 \u4f4d\u3002 \u3000\u3000\u201c\u4e5d\u8db3\u795e\u86db\u201d\u6b66\u529f\u5e76\u4e0d\u7edd\u9ad8\uff0c\u4ed6\u751a\u81f3\u8fde\u201c\u5723\u624b\u4e66\u751f\u201d\u7684\u8bb0\u540d\u5f1f\u5b50\u90fd\u4e0d\u80fd\u7b97\uff0c\u800c\u53ea\u80fd\u7b97\u662f \u201c\u79c1\u6dd1\u5f1f\u5b50\u201d\uff0c\u56e0\u4e3a\u4ed6\u4ece\u201c\u5723\u624b\u4e66\u751f\u201d\u90a3\u91cc\u5b66\u5230\u7684\u4e1c\u897f\uff0c\u53ea\u662f\u201c\u5723\u624b\u4e66\u751f\u201d\u5728\u5f52\u9690\u4e4b\u540e\uff0c\u5076 \u6765\u4e2d\u5dde\uff0c\u5728\u4e09\u4e24\u65e5\u95f4\uff0c\u968f\u610f\u6307\u70b9\u4ed6\u7684\u51e0\u624b\u529f\u592b\u3002 \u3000\u3000\u53ea\u662f\u8fd9\u201c\u4e5d\u8db3\u795e\u86db\u201d\u5374\u662f\u4e2a\u975e\u5e38\u4e4b\u4eba\uff0c\u4ed6\u4e0d\u4f46\u5c06\u8fd9\u51e0\u624b\u529f\u592b\u90fd\u5b66\u5f97\u5b9e\u5b9e\u5728\u5728\u5730\u6beb\u65e0\u5dee \u9519\uff0c\u800c\u4e14\u8fd8\u4e3e\u4e00\u53cd\u4e09\uff0c\u53c8\u72ec\u521b\u4e86\u4e9b\u529f\u592b\u3002 \u3000\u3000\u6b64\u5916\uff0c\u8fd9\u201c\u4e5d\u8db3\u795e\u86db\u201d\u8fd8\u6709\u51e0\u70b9\u5927\u5f02\u5e38\u4eba\u4e4b\u5904\uff0c\u4ed6\u4e00\u8bfa\u5343\u91d1\uff0c\u81f3\u6b7b\u4e0d\u6094\uff0c\u800c\u4e14\u8bb0\u5fc6\u4e4b \u5f3a\uff0c\u66f4\u662f\u9a87\u4eba\u542c\u95fb\uff0c\u4efb\u4f55\u4eba\u53ea\u8981\u88ab\u4ed6\u770b\u8fc7\u4e00\u773c\u4fbf\u7ec8\u751f\u4e0d\u4f1a\u5fd8\u8bb0\u3002\u4ed6\u672c\u662f\u5de8\u5bcc\u5b50\u5f1f\uff0c\u4e00\u5e74\u4e4b \u4e2d\uff0c\u6563\u5c3d\u4e07\u8d2f\u5bb6\u8d22\uff0c\u7ed3\u4ea4\u7684\u5374\u5168\u90fd\u662f\u522b\u4eba\u4e0d\u803b\u7684\u5e02\u4e95\u5c60\u72d7\u4e4b\u8f88\uff0c\u4ed6\u4e0e\u8fd9\u4e9b\u5e02\u4e95\u597d\u6c49\u76f8\u4ea4\uff0c\u5168 \u51ed\u201c\u4e49\u201d\u6765\u670d\u4eba\uff0c\u7edd\u4e0d\u663e\u9732\u81ea\u5df1\u7684\u6b66\u529f\uff0c\u5341\u4f59\u5e74\u4e4b\u524d\uff0c\u5357\u4eac\u57ce\u4e2d\u7684\u5c60\u6237\u5e2e\u5927\u54e5\u7f57\u4e00\u5200\uff0c\u4e3a\u4e86 \u592b\u5b50\u5e99\u524d\u7684\u4e03\u5341\u4f59\u53ea\u753b\u822b\uff0c\u548c\u6881\u4e0a\u4eba\u7ed3\u4e0b\u6df1\u4ec7\uff0c\u626c\u8a00\u8981\u5c06\u6881\u4e0a\u4eba\u5927\u5378\u516b\u5757\uff0c\u7136\u540e\u518d\u5f53\u732a\u8089\u51fa \u5356\u3002 \u3000\u3000\u90a3\u65f6\u6881\u4e0a\u4eba\u6b66\u529f\u5df2\u6709\u5c0f\u6210\uff0c\u672c\u53ef\u5728\u4e3e\u624b\u4e4b\u95f4\u5c06\u90a3\u7f57\u4e00\u5200\u5236\u670d\uff0c\u4f46\u4ed6\u5374\u4e0d\u5982\u6b64\u505a\uff0c\u4ed6\u5b64\u8eab \u5230\u90a3\u7f57\u4e00\u5200\u7684\u8089\u6848\u524d\uff0c\u53eb\u8fd9\u4ee5\u4e00\u5200\u6740\u732a\u6210\u540d\u4e8e\u5e02\u4e95\u95f4\u7684\u7f57\u4e00\u5200\u780d\u4ed6\u4e00\u5200\uff0c\u7f57\u4e00\u5200\u8fd9\u4e00\u5200\u82e5\u80fd \u5c06\u4ed6\u4e5f\u50cf\u732a\u4e00\u6837\u5730\u6740\u6b7b\uff0c\u4ed6\u6beb\u65e0\u6028\u8a00\uff0c\u7f57\u4e00\u5200\u8fd9\u4e00\u5200\u82e5\u662f\u780d\u4ed6\u4e0d\u6b7b\uff0c\u90a3\u4e48\u4ed6\u5c31\u53eb\u7f57\u4e00\u5200\u4ece\u6b64 \u4e0d\u8981\u79f0\u96c4\u3002 \u3000\u3000\u8fd9\u6d88\u606f\u5f53\u65f6\u60ca\u52a8\u4e86\u5357\u4eac\u57ce\u4e2d\u6240\u6709\u7684\u5e02\u4e95\u597d\u6c49\uff0c\u6570\u767e\u4eba\u56f4\u5728\u7f57\u4e00\u5200\u7684\u5c60\u6848\u524d\uff0c\u6709\u7684\u529d\u963b\uff0c \u6709\u7684\u54c0\u6c42\uff0c\u6881\u4e0a\u4eba\u53ea\u662f\u542b\u7b11\u5fd9\u7acb\uff0c\u773c\u770b\u7740\u7f57\u4e00\u5200\u4e3e\u8d77\u5c60\u6848\u524d\u7684\u788e\u9aa8\u5927\u5200\uff0c\u4e00\u5200\u780d\u4e0b\uff0c\u4ed6\u4e0d\u907f \u4e0d\u95ea\uff0c\u505a\u7136\u4ec1\u7acb\uff0c\u56db\u4e0b\u7684\u5e02\u4e95\u597d\u6c49\u770b\u5f97\u638c\u5fc3\u6dcc\u6c57\uff0c\u53ea\u9053\u8fd9\u4e00\u5200\u780d\u4e0b\uff0c\u6881\u4e0a\u4eba\u7acb\u65f6\u4fbf\u5f97\u8eab\u9996\u5f02 \u5904\u3002 \u3000\u3000\u90a3\u201c\u7f57\u4e00\u5200\u201d\u5176\u5b9e\u4e5f\u77e5\u9053\u6881\u4e0a\u4eba\u7684\u6b66\u529f\uff0c\u751f\u6015\u81ea\u5df1\u8fd9\u4e00\u5200\u780d\u4e0b\uff0c\u780d\u4ed6\u4e0d\u7740\uff0c\u4fbf\u6545\u610f\u780d\u504f \u4e00\u70b9\uff0c\u8981\u8ba9\u4ed6\u4e00\u95ea\u4e4b\u540e\u780d\u4e2a\u6b63\u7740\uff0c\u90a3\u77e5\u4ed6\u4e0d\u907f\u4e0d\u95ea\uff0c\u8fd9\u4e00\u5200\u4fbf\u6b63\u597d\u780d\u5728\u4ed6\u5de6\u80a9\u4e4b\u4e0a\uff0c\u56db\u4e0b\u597d \u6c49\u5927\u559d\u4e00\u58f0\uff0c\u53ea\u89c1\u9c9c\u8840\u5982\u6cc9\u6d8c\u51fa\uff0c\u6881\u4e0a\u4eba\u4ecd\u633a\u80f8\u800c\u7acb\uff0c\u9762\u5e26\u7b11\u5bb9\uff0c\u7f57\u4e00\u5200\u89c1\u4e86\u4ed6\u8fd9\u79cd\u795e\u52c7\uff0c \u5f53\u4e0b\u5fc3\u865a\u624b\u8f6f\uff0c\u201c\u94db\u2019\u5730\u4e00\u58f0\uff0c\u5927\u5200\u843d\u5730\uff0c\u6251\u5730\u8dea\u5012\u5730\u4e0a\uff0c\u5927\u53eb\uff1a\u201c\u670d\u4e86\u3002\u201d\u6881\u4e0a\u4eba\u542b\u7b11\u62fe \u8d77\u90a3\u67c4\u91cd\u903e\u4e03\u65a4\u7684\u5c60\u5200\uff0c\u5530\u5730\u4e00\u638c\uff0c\u7adf\u5c06\u8fd9\u5927\u5200\u5288\u6210\u4e24\u534a\uff0c\u4e00\u534a\u4ea4\u8fd8\u7ed9\u7f57\u4e00\u5200\uff0c\u4e00\u534a\u62ff\u5728\u624b \u91cc\uff0c\u542b\u7b11\u5c06\u7f57\u4e00\u5200\u6276\u4e86\u8d77\u6765\uff0c\u5de6\u80a9\u4e0a\u7684\u9c9c\u8840\uff0c\u867d\u4ecd\u50cf\u6d41\u6cc9\u98de\u7011\u4e00\u6837\u5f80\u5916\u6d8c\uff0c\u4ed6\u5374\u8fde\u770b\u4e5f\u4e0d\u770b \u4e00\u773c\u3002 \u3000\u3000\u4ece\u6b64\u4e4b\u540e\uff0c\u6881\u4e0a\u4eba\u7684\u201c\u4e07\u513f\u201d\u4e0d\u4f46\u54cd\u5f7b\u4e5d\u57ce\uff0c\u800c\u4e14\u5929\u4e0b\u7686\u95fb\uff0c\u4ed6\u8fd9\u79cd\u82f1\u98ce\u8c6a\u4e3e\u5728\u90a3\u4e9b\u6b66 \u6797\u9ad8\u624b\u7684\u773c\u4e0b\uff0c\u867d\u7136\u4e0d\u503c\u4e00\u6652\uff0c\u4f46\u662f\u6c5f\u6e56\u4e0a\u7684\u5e02\u4e95\u597d\u6c49\uff0c\u542c\u4e86\u201c\u6881\u4e0a\u4eba\u201d\u7684\u540d\u5b57\uff0c\u5374\u518d\u4e5f\u6ca1 \u6709\u8bdd\u8bf4\u3002 \u3000\u3000\u4ec7\u6055\u79bb\u5c9b\u4e4b\u524d\uff0c\u4fbf\u4ece\u90a3\u201c\u5723\u624b\u4e66\u751f\u201d\u53e3\u4e2d\u5f97\u77e5\u6709\u7740\u5982\u6b64\u4e00\u4e2a\u4eba\u7269\uff0c\u662f\u4ee5\u4ed6\u4e00\u5230\u4e2d\u5dde\uff0c\u4fbf \u8bbe\u6cd5\u5bfb\u5f97\u6b64\u4eba\uff0c\u8fd9\u4e9b\u65e5\u5b50\u6765\uff0c\u4ed6\u5bf9\u6b64\u4eba\u7684\u4e8b\u8ff9\u77e5\u9053\u5f97\u66f4\u591a\uff0c\u867d\u7136\u89c9\u5f97\u6b64\u4eba\u884c\u4e8b\uff0c\u867d\u5927\u591a\u51fa\u4e4b \u4e8e\u597d\u52c7\u6597\u72e0\uff0c\u4e0d\u8db3\u4ee5\u4e3a\u541b\u5b50\u4e4b\u98ce\uff0c\u5374\u4ecd\u4e0d\u5931\u4e3a\u6027\u60c5\u4e2d\u4eba\uff0c\u4f55\u51b5\u6b64\u4eba\u5bf9\u4e8e\u4ec7\u6055\uff0c\u66f4\u662f\u5904\u5904\u90fd\u4ee5 \u5168\u529b\u76f8\u52a9\u3002 \u3000\u3000\u8981\u77e5\u9053\u6b66\u6797\u4e2d\u4eba\u79f0\u8fd9\u6881\u4e0a\u4eba\u4e3a\u201c\u4e5d\u8db3\u795e\u86db\u201d\uff0c\u4fbf\u662f\u4ed6\u515a\u7fbd\u904d\u5929\u4e0b\uff0c\u4ed6\u624b\u4e0b\u7684\u90a3\u4e9b\u4f34\u5f53\u82e5 \u5728\u6b66\u6797\u4e89\u96c4\uff0c\u81ea\u4e0d\u662f\u522b\u4eba\u654c\u624b\uff0c\u4f46\u7528\u6765\u505a\u6d88\u606f\u773c\u7ebf\uff0c\u5374\u518d\u597d\u4e5f\u6ca1\u6709\uff0c\u6b64\u523b\u4ec7\u6055\u542b\u7b11\u8bf4\u9053\uff1a \u201c\u6881\u5144\u4e43\u662f\u4eba\u4e2d\u4e4b\u6770\uff0c\u4e0d\u7792\u4f60\u8bf4\uff0c\u6211\u4e5f\u662f\u6781\u4e3a\u4f69\u670d\u4ed6\u7684\u3002\u201d \u3000\u3000\u5f20\u4e00\u6876\u59c6\u6307\u4e00\u6311\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u5f53\u7136\uff0c\u4f60\u4eec\u4e24\u4f4d\u90fd\u662f\u82f1\u96c4\uff0c\u82f1\u96c4\u91cd\u82f1\u96c4\uff0c\u6211\u90a3\u6881\u5927 \u54e5\u5bf9\u516c\u5b50\uff0c\u4e0d\u4f46\u4f69\u670d\uff0c\u800c\u4e14\u7b80\u76f4\u4f69\u670d\u5f97\u4e94\u4f53\u6295\u5730\u54e9\u3002\u201d \u3000\u3000\u4ed6\u7b11\u58f0\u4e00\u987f\uff0c\u7a81\u5730\u4f4e\u58f0\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4f60\u53ef\u77e5\u9053\uff0c\u2018\u7075\u86c7\u2019\u6bdb\u81ec\u624b\u4e0b\uff0c\u6709\u4e2a\u53eb\u505a\u4ec0\u4e48\u2018\u516b \u9762\u73b2\u73d1\u2019\u7684\u80e1\u80d6\u5b50\uff0c\u4e5f\u5728\u5343\u65b9\u767e\u8ba1\u5730\u627e\u6211\u4eec\u6881\u5927\u54e5\uff0c\u4e5f\u8981\u53eb\u6881\u5927\u54e5\u5e2e\u52a9\uff0c\u90a3\u80e1\u80d6\u5b50\u524d\u4e24\u5929\u4e5f \u6765\u5230\u5609\u5174\u57ce\uff0c\u627e\u4e86\u4e24\u5929\uff0c\u4e5f\u6ca1\u6709\u627e\u5230\u6881\u5927\u54e5\uff0c\u6628\u5929\u5c31\u8d70\u4e86\uff0c\u54fc\u2014\u2014\u201d\u4ed6\u51b7\u54fc\u4e00\u58f0\uff0c\u4e0d\u5c51\u4ed6 \u8bf4\uff1a\u201c\u6211\u770b\u90a3\u80d6\u5b50\u98a4\u7740\u6ee1\u8eab\u80a5\u8089\uff0c\u5230\u5904\u4e71\u8dd1\uff0c\u5fc3\u91cc\u5c31\u89c9\u5f97\u6709\u6c14\uff0c\u4ed6\u81ea\u5df1\u662f\u4e2a\u732a\u516b\u6212\uff0c\u5374\u4e5f\u4e0d \u7167\u7167\u955c\u5b50\uff0c\u8fd8\u8dd1\u5230\u5357\u6e56\u53bb\u627e\u8239\u5a18\uff0c\u786c\u8981\u4eba\u5bb6\u966a\u4ed6\u2026\u2026\u563f\u563f\uff0c\u966a\u4ed6\u5e72\u574f\u4e8b\uff0c\u4ed6\u4e5f\u4e0d\u60f3\u60f3\uff0c\u54b1\u4eec \u5609\u5174\u5357\u6e56\u5929\u4e0b\u95fb\u540d\u7684\u8239\u5a18\uff0c\u600e\u4f1a\u770b\u5f97\u4e0a\u4ed6\uff0c\u5c31\u7b97\u662f\u2014\u2014\u548c\u4ed6\u600e\u4e48\u6837\u4e86\uff0c\u4e5f\u4e0d\u8fc7\u5f53\u4ed6\u662f\u6761\u80a5\u732a \u7f62\u4e86\uff0c\u54fc\uff0c\u6211\u770b\u4ed6\u7b80\u76f4\u4ed6\u5988\u2014\u2014\u563f\u563f\uff0c\u4ed6\u7b80\u76f4\u91cc\u91cc\u5916\u5916\u90fd\u6ca1\u6709\u4e00\u6837\u4eba\u5f62\u3002\u201d \u3000\u3000\u4ec7\u6055\u770b\u7740\u4ed6\u8bf4\u8bdd\u7684\u6837\u5b50\u548c\u6ee1\u8eab\u7684\u80a5\u8089\uff0c\u518d\u542c\u5230\u4ed6\u9a82\u4eba\u7684\u8bdd\uff0c\u5fc3\u4e2d\u4e0d\u7981\u6697\u7b11\uff0c\u53ea\u89c9\u6b64\u4eba\u867d \u7136\u8a00\u8bed\u7c97\u9c81\uff0c\u8a00\u4e0d\u53ca\u4e49\uff0c\u5374\u5f53\u771f\u6709\u8da3\u5f97\u5f88\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u4e00\u53e3\u6c14\u9a82\u5b8c\u4e86\uff0c\u5598\u4e86\u4e24\u53e3\u6c14\uff0c\u53c8\u81ea\u563f\u563f\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u8ddf\u516c\u5b50\u7a77\u804a\u4e86\u8fd9\u534a\u5929\uff0c\u7adf \u5fd8\u4e86\u8ddf\u516c\u5b50\u8bf4\u6b63\u7ecf\u4e8b\u4e86\u3002\u201d\u4ed6\u53c8\u81ea\u653e\u4f4e\u58f0\u97f3\uff1a\u201c\u65b9\u624d\u5e73\u671b\u57ce\u7684\u5c0f\u94c1\u5634\u5feb\u9a6c\u8d76\u6765\uff0c\u8bf4\u662f\u770b\u5230\u90a3 \u2018\u9e33\u9e2f\u53cc\u5251\u2019\u4e5f\u5f80\u5609\u5174\u6765\uff0c\u5927\u7ea6\u4eca\u5929\u665a\u4e0a\u4e5f\u80fd\u5230\u4e86\u3002\u201d \u3000\u3000\u4ec7\u6055\u5251\u7709\u5fae\u76b1\uff0c\u4fef\u9996\u6c89\u541f\u534a\u664c\uff0c\u561f\u542c\u8fd9\u5f20\u4e00\u6876\u53c8\u9053\uff1a\u201c\u8fd8\u6709\u4ece\u592a\u884c\u5c71\u90a3\u8fb9\u8d76\u6765\u7684\uff0c\u5927\u7ea6 \u6709\u4e94\u5341\u9a91\u4eba\u9a6c\uff0c\u4eca\u5929\u5348\u95f4\uff0c\u4ece\u5609\u5174\u7ecf\u8fc7\uff0c\u76f4\u5954\u676d\u5dde\u53bb\u4e86\uff0c\u592a\u884c\u53cc\u4e49\u91d1\u6c0f\u5144\u5f1f\u5168\u5728\u8fd9\u4e9b\u4eba\u91cc \u9762\uff0c\u8ddf\u4ed6\u4eec\u4e24\u4eba\u8d70\u5728\u4e00\u5904\u7684\uff0c\u8fd8\u6709\u4e2a\u52b2\u88c5\u5c11\u5e74\uff0c\u5374\u4e0d\u77e5\u662f\u8c01\u4e86\u3002\u201d \u3000\u3000\u4ec7\u6055\u76ee\u5149\u4e00\u8f6c\uff0c\u7a81\u5730\u5c55\u989c\u4e00\u7b11\uff0c\u50cf\u662f\u53c8\u60f3\u8d77\u4e86\u4ec0\u4e48\u5999\u8ba1\u4f3c\u7684\uff1a\u201c\u8fd9\u90fd\u8f9b\u82e6\u4f60\u4e86\uff0c\u53ea\u662f\u6211 \u8fd8\u8981\u518d\u8f9b\u82e6\u4f60\u4e00\u8d9f\uff0c\u4e0d\u77e5\u9053\u5609\u5174\u57ce\u91cc\u57ce\u5916\uff0c\u4e00\u5171\u6709\u591a\u5c11\u5ba2\u6808\uff1f\u201d \u3000\u3000\u5f20\u4e00\u6876\u95ed\u8d77\u773c\u775b\uff0c\u60f3\u4e86\u4e00\u4f1a\u3002 \u3000\u3000\u201c\u4e94\u5341\u591a\u5bb6\u3002\u201d\u4ed6\u5f97\u610f\u5730\u7b11\u9053\uff0c\u201c\u6700\u5c11\u4e94\u5341\uff0c\u6700\u591a\u4e94\u5341\u4e94\uff0c\u6211\u867d\u4e5f\u4e0d\u5341\u5206\u6e05\u695a\uff0c\u4f46\u603b\u5dee \u4e0d\u591a\u4e86\u3002\u201d \u3000\u3000\u4ec7\u6055\u4e00\u7b11\uff1a\u201c\u6211\u4e14\u9ebb\u70e6\u4f60\u5c06\u8fd9\u4e94\u5341\u51e0\u5bb6\u5ba2\u6808\u6240\u6709\u7684\u5ba2\u623f\uff0c\u5168\u90fd\u5305\u4e0b\uff0c\u5c31\u7b97\u6709\u4eba\u4f4f\u7684\uff0c\u4e5f \u90fd\u9884\u5b9a\u4e0b\u6765\uff0c\u800c\u4e14\u5148\u4ed8\u5341\u5929\u623f\u94b1\uff0c\u591a\u7ed9\u5c0f\u5e10\uff0c\u8bf4\u662f\u65e0\u8bba\u4efb\u4f55\u4eba\u8981\u6765\u4f4f\u5e97\uff0c\u90fd\u4e00\u53e3\u56de\u7edd\uff0c\u4e07\u4e07 \u4e0d\u80fd\u7b54\u5e94\u3002\u201d \u3000\u3000\u5f20\u4e00\u6876\u5012\u62bd\u4e00\u53e3\u51c9\u6c14\uff0c\u4e24\u53ea\u672c\u5df1\u88ab\u6ee1\u8138\u80a5\u8089\u6324\u6210\u4e00\u7ebf\u7684\u773c\u775b\uff0c\u7a81\u5730\u7741\u5f97\u6eda\u5706\uff0c\u4f38\u51fa\u624b \u638c\uff0c\u4e00\u62cd\u524d\u989d\uff0c\u5931\u58f0\u9053\uff1a\u201c\u4e94\u5341\u591a\u5bb6\u5ba2\u6808\uff01\u5341\u5929\u623f\u94b1\u2014\u2014\u516c\u5b50\uff0c\u4f60\u8fd9\u662f\u5e72\u4ec0\u4e48\u5440\uff1f\u96be\u9053\u60a8\u6709 \u90a3\u4e48\u591a\u670b\u53cb\u5c31\u8981\u5230\u5609\u5174\u57ce\u6765\u5417\uff1f\u201d \u3000\u3000\u4ec7\u6055\u9762\u4e0a\u53c8\u81ea\u6cdb\u8d77\u90a3\u79cd\u83ab\u6d4b\u9ad8\u6df1\u7684\u7b11\u5bb9\uff0c\u4e00\u9762\u4ece\u6000\u4e2d\u638f\u51fa\u4e00\u5f20\u94f6\u7968\uff0c\u5f20\u4e00\u6876\u4e00\u773c\u626b\u5230\u94f6 \u7968\u4e0a\u7684\u6570\u5b57\uff0c\u4e0d\u7981\u53c8\u5012\u62bd\u4e00\u53e3\u51c9\u6c14\uff0c\u5374\u542c\u4ec7\u6055\u7b11\u9053\uff1a\u201c\u6211\u6b64\u4e3e\u81ea\u6709\u9053\u7406\uff0c\u4f60\u4ee5\u540e\u81ea\u7136\u4f1a\u77e5\u9053 \u7684\uff0c\u53ea\u662f\u2014\u2014\u4e0d\u77e5\u4f60\u6709\u65e0\u628a\u63e1\uff0c\u53eb\u4efb\u4f55\u5ba2\u6808\u90fd\u4e0d\u80fd\u5c06\u5ba2\u623f\u5077\u5077\u79df\u7ed9\u522b\u4eba\u3002\u201d \u3000\u3000\u5f20\u4e00\u6876\u4e00\u62cd\u80f8\u819b\uff1a\u201c\u8fd9\u4e2a\u53ea\u7ba1\u5305\u5728\u6211\u8eab\u4e0a\uff0c\u9664\u975e\u4ed6\u4eec\u4e0d\u60f3\u518d\u505a\u751f\u610f\u4e86\uff0c\u5426\u5219\u4e00\u563f\uff0c\u5c31\u7b97 \u518d\u501f\u7ed9\u4ed6\u4eec\u4e00\u4e2a\u80c6\u5b50\uff0c\u4ed6\u4eec\u53ef\u4e5f\u4e0d\u6562\u3002\u201d \u3000\u3000\u4e8e\u662f\u4ed6\u63a5\u8fc7\u94f6\u7968\uff0c\u6ee1\u6000\u60ca\u5f02\u5730\u53bb\u4e86\uff0c\u60f3\u6765\u60f3\u53bb\uff0c\u5b9e\u5728\u60f3\u4e0d\u900f\u201c\u516c\u5b50\u201d\u6b64\u4e3e\u662f\u4e3a\u4e86\u4ec0\u4e48\uff0c \u4f46\u76f4\u5230\u4ed6\u81c3\u80bf\u7684\u8eab\u5f62\u5df2\u8d70\u4e86\u8bb8\u4e45\uff0c\u4ec7\u6055\u9762\u4e0a\u5374\u4ecd\u5e26\u7740\u90a3\u79cd\u5947\u5f02\u7684\u5fae\u7b11\uff0c\u53ea\u662f\u8c01\u4e5f\u4e0d\u77e5\u9053\u4ed6\u7a76 \u7adf\u5728\u7b11\u7740\u4ec0\u4e48\u3002 \u7b2c\u5341\u516b\u7ae0>> \u53e4\u9f99\u300a\u6e58\u5983\u5251\u300b \u7b2c\u5341\u516b\u7ae0 \u3000\u3000\u591c\u6df1\uff01 \u3000\u3000\u6625\u98ce\u6251\u9762\uff0c\u7e41\u661f\u5728\u5929\uff0c\u7e41\u8363\u7684\u5609\u5174\uff0c\u591c\u5e02\u5374\u5df2\u5728\u9010\u6e10\u6d88\u6c89\u4e86\u3002 \u3000\u3000\u706f\u706b\u6e10\u5c11\u6e10\u7a00\uff0c\u884c\u4eba\u6e10\u7a00\u6e10\u65e0\uff0c\u7531\u55a7\u95f9\u800c\u6c89\u5bc2\uff0c\u7531\u6c89\u5bc2\u800c\u590d\u82cf\uff0c\u7531\u521d\u82cf\u800c\u518d\u55a7\u95f9\u2026\u2026\u8fd9 \u6b63\u662f\u5343\u53e4\u4ee5\u6765\uff0c\u4efb\u4f55\u4e00\u4e2a\u57ce\u5e02\u4e0d\u53d8\u7684\u8282\u594f\uff0c\u4e00\u8f86\u6ee1\u5806\u82b1\u7c89\u7684\u8f66\u5b50\uff0c\u88ab\u4e00\u4e2a\u6ee1\u9762\u5f97\u610f\u7684\u8d27\u90ce\uff0c \u7531\u8857\u5934\u63a8\u4e86\u8fc7\u6765\uff0c\u53c8\u6d88\u5931\u5728\u6d53\u91cd\u7684\u591c\u8272\u91cc\uff0c\u6625\u98ce\u5439\u5f97\u8f66\u4e0a\u7684\u5c0f\u94c3\uff0c\u53ee\u94db\u5fae\u9e23\uff1a\u5230\u4e86\u8fd9\u94c3\u58f0\u6447 \u66f3\u7684\u4f59\u97f3\uff0c\u8885\u8885\u6563\u5c3d\uff0c\u9759\u5bc2\u4fbf\u5b8c\u5168\u5c06\u8fd9\u6761\u9752\u77f3\u94fa\u6210\u7684\u9053\u8def\u541e\u6ca1\u2026\u2026 \u3000\u3000\u54a6\uff01\u5947\u602a\uff01 \u3000\u3000\u600e\u5730\u8fd8\u6709\u4e24\u5339\u978d\u8f94\u9c9c\u660e\u7684\u5065\u9a6c\uff0c\u505c\u7559\u5728\u8fd9\u65e0\u4eba\u7684\u8857\u7554\uff1f \u3000\u3000\u5662\uff01\u662f\u4e86\uff01 \u3000\u3000\u539f\u6765\u8fd9\u95f4\u5c0f\u5c0f\u9152\u697c\uff0c\u76f4\u5230\u6b64\u523b\u91cc\u9762\u8fd8\u6709\u5ba2\u4eba\uff01 \u3000\u3000\u95e8\u677f\u5df2\u4e0a\u8d77\u5927\u534a\uff0c\u4e00\u7ebf\u660f\u9ec4\u7684\u706f\u5149\uff0c\u81ea\u95e8\u677f\u7684\u7a7a\u9699\u4e2d\u9732\u51fa\uff0c\u65e0\u529b\u5730\u6295\u843d\u5728\u6e05\u51b7\u7684\u8857\u9053 \u4e0a\u3002 \u3000\u3000\u4ece\u8fd9\u7a7a\u9699\u4e2d\u671b\u8fdb\u53bb\uff0c\u4f60\u6070\u597d\u53ef\u4ee5\u671b\u89c1\u4e00\u4e2a\u8eab\u7a7f\u9526\u888d\uff0c\u80a9\u5bbd\u8170\u7a84\uff0c\u6c89\u539a\uff0c\u5374\u53c8\u633a\u76f4\u7684\u80cc \u5f71\u3002 \u3000\u3000\u4ed6\u7f13\u7f13\u8f6c\u56de\u5934\uff0c\u6d53\u7709\u6df1\u76b1\uff0c\u76ee\u5149\u70af\u7136\uff0c\u5229\u526a\u822c\u5411\u5916\u626b\u4e86\u4e00\u773c\u2014\u2014\u867d\u7136\u4ed6\u6b64\u523b\u5df2\u662f\u4e0d\u60d1\u4e4b \u5e74\uff0c\u4f46\u4ed6\u7684\u76ee\u5149\uff0c\u7684\u786e\u8fd8\u6709\u7740\u5229\u526a\u822c\u7684\u9510\u5229\uff0c\u4f3c\u4e4e\u8fd9\u4e00\u773c\u4fbf\u8db3\u591f\u5c06\u90a3\u539a\u91d1\u7684\u95e8\u677f\u770b\u7a7f\uff01 \u3000\u3000\u76ee\u5149\u4e00\u95ea\uff0c\u4ed6\u8f7b\u8f7b\u4e00\u58f0\u53f9\u606f\uff0c\u7136\u540e\u56de\u8eab\uff0c\u6d53\u7709\u76b1\u5f97\u66f4\u7d27\uff0c\u7f13\u5cfb\u9053\uff1a\u201c\u5929\u8272\u7adf\u8fd9\u822c\u665a \u4e86\uff01\u201d\u7a81\u5730\u91cd\u91cd\u4e00\u62cd\u684c\u9762\uff0c\u201c\u6211\u5c31\u4e0d\u4fe1\u8fd9\u504c\u5927\u7684\u5609\u5174\u57ce\uff0c\u7adf\u4f1a\u6ca1\u6709\u4e00\u5bb6\u7a7a\u7740\u7684\u5ba2\u623f\uff01\u201d \u3000\u3000\u684c\u4e0a\u96f6\u4e71\u7684\u676f\u76c6\u7897\u76cf\uff0c\u88ab\u4ed6\u8fd9\u968f\u624b\u4e00\u62cd\uff0c\u90fd\u9707\u5f97\u8df3\u4e86\u8d77\u6765\uff0c\u5750\u5728\u4ed6\u5bf9\u9762\u7684\uff0c\u662f\u4e00\u4e2a\u9752\u8863 \u7a84\u8896\uff0c\u4f46\u5374\u6ee1\u5934\u73e0\u7fe0\u7684\u4e2d\u5e74\u5987\u4eba\u2014\u2014\u8fd9\u8863\u7740\u4e0e\u5934\u9970\uff0c\u662f\u591a\u4e48\u5730\u4e0d\u76f8\u79f0\uff0c\u5c31\u6b63\u5982\u5979\u7684\u76ee\u5149\u4e0e\u8bed \u58f0\u7684\u4e0d\u79f0\u4e00\u6837\uff01 \u3000\u3000\u56e0\u4e3a\u5979\u7684\u76ee\u5149\u662f\u6e29\u67d4\u7684\uff0c\u8bed\u6c14\u5374\u4e5f\u6709\u5982\u5229\u526a\u822c\u660e\u5feb\u3002 \u3000\u3000\u5979\u76ee\u5149\u6e29\u67d4\u5730\u671b\u7740\u5bf9\u9762\u7684\u9526\u8863\u4eba\uff0c\u5507\u8fb9\u6cdb\u8d77\u4e00\u4e1d\u5fae\u7b11\uff0c\u9053\uff1a\u201c\u4e5f\u8bb8\u771f\u6709\u5927\u5e2e\u5ba2\u5546\u7ecf\u8fc7\uff0c \u4e0d\u7136\u54ea\u6709\u5f00\u5e97\u62d2\u7edd\u5ba2\u4eba\u4e0a\u95e8\u7684\u9053\u7406\uff0c\u4f60\u53c8\u4f55\u5fc5\u751f\u8fd9\u4e48\u5927\u7684\u4eba\u6c14\u8c03\u76ee\u5149\u662f\u6e29\u67d4\u7684\uff0c\u7b11\u5bb9\u4e5f\u662f\u6e29 \u67d4\u7684\uff0c\u4f46\u8fd9\u79cd\u663e\u7136\u662f\u4e45\u7ecf\u6291\u5236\u548c\u5fcd\u8010\u624d\u517b\u6210\u7684\u6e29\u67d4\uff0c\u5374\u4e1d\u6beb\u63a9\u4e0d\u4f4f\u5979\u7709\u76ee\u95f4\u7684\u521a\u5065\u6840\u50b2\u4e4b \u6c14\uff0c\u4e5f\u5c31\u6b63\u5982\u5979\u5df1\u65e5\u6e10\u4e30\u8174\u7684\u4f53\u6001\uff0c\u63a9\u4e0d\u4f4f\u5979\u8eab\u624b\u7684\u77eb\u5065\u4e00\u6837\u3002\u9526\u8863\u8000\u76ee\u7684\u4e2d\u5e74\u6c49\u5b50\u76ee\u5149\u4e00 \u843d\uff0c\u5fae\u559f\u9053\uff1a\u201c\u8bdd\u867d\u5982\u6b64\uff0c\u4f46\u8fd9\u5609\u5174\u57ce\uff0c\u4e00\u65e0\u6b66\u6797\u4eba\u5bb6\u53ef\u4f9b\u6295\u5bbf\uff0c\u96be\u9053\u771f\u6559\u6211\u4eec\u9910\u98ce\u5bbf\u9732\u4e00 \u5bb5\u4e0d\u6210\u8c03\u56db\u987e\u4e00\u773c\uff1a\u201c\u8fd9\u9152\u5e97\u7ec8\u4e0d\u662f\u957f\u7559\u4e4b\u5730\u5440\uff01\uff0c\uff0c\u8fd9\u6614\u5e74\u7eb5\u6a2a\u5929\u4e0b\uff0c\u56db\u6d77\u4e3a\u5bb6\uff0c\u4e0d\u77e5\u9910 \u98ce\u5bbf\u9732\u591a\u5c11\u6b21\u7684\u6b66\u6797\u5065\u8005\uff0c\u5df2\u56e0\u591a\u5e74\u6765\u7684\u517b\u5c0a\u5904\u4f18\uff0c\u800c\u6d88\u78e8\u53bb\u4ed6\u7684\u94a2\u7b4b\u94c1\u9aa8\uff0c\u6b64\u523b\u7adf\u4e3a\u4e86\u4e00 \u591c\u7684\u5bbf\u5904\u800c\u4e0d\u5b89\uff0c\u60f6\u6050\u8d77\u6765\uff0c\u82e5\u6362\u4e86\u4e8c\u5341\u5e74\u524d\uff0c\u4ed6\u7eb5\u7136\u5728\u9732\u5929\u4e0b\u4ec1\u7acb\u4e09\u591c\uff0c\u53ea\u6015\u4ed6\u4e5f\u4e0d\u4f1a\u76b1 \u4e00\u76b1\u7709\u5934\u3002\u4e2d\u5e74\u5987\u4eba\u8f7b\u8f7b\u4e00\u53f9\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u6211\u4eec\u8fde\u591c\u518d\u8d76\u4e00\u7ad9\uff0c\u53c8\u6709\u4f55\u59a8\u3002\u201d \u3000\u3000\u9526\u8863\u6c49\u5b50\u6d53\u7709\u4e00\u76b1\uff0c\u66b4\u58f0\u9053\uff1a\u201c\u518d\u8d76\u4e00\u7ad9\uff0c\u6211\u5012\u65e0\u59a8\uff0c\u4f60\u2026\u2026\u4f60\u2026\u2026\u201d\u8868\u60c5\u7a81\u53c8\u53d8\u5f97\u5341 \u5206\u6e29\u67d4\uff0c\u53f9\u9053\uff0c\u201c\u4f60\u96be\u9053\u5fd8\u4e86\u4f60\u5df2\u6709\u516d\u4e2a\u6708\u7684\u8eab\u2014\u2014\u201d\u4e2d\u5e74\u5987\u4eba\u79cb\u6ce2\u4e00\u8f6c\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u4f60\u8fd9 \u4eba\u771f\u662f\uff0c\u5728\u8fd9\u91cc\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u201d\u53cc\u988a\u4e4b\u4e0a\uff0c\u5c45\u7136\u9690\u73b0\u7ea2\u6655\u3002 \u3000\u3000\u9526\u8863\u6c49\u5b50\u76b1\u7709\u9053\uff1a\u201c\u6211\u53eb\u4f60\u8fd9\u6b21\u4e0d\u8981\u51fa\u6765\uff0c\u4f60\u504f\u8981\u51fa\u6765\uff0c\u8fd8\u4e00\u5b9a\u8981\u9a91\u9a6c\u2026\u5509\uff0c\u8fd9\u662f\u4f60\u7b2c \u4e00\u6b21\u2014\u2014\u201d\u8bed\u6c14\u7a81\u5730\u4e00\u8f6c\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u4e0d\u77e5\u662f\u7537\u662f\u5973\uff1f\u6b66\u6797\u4e2d\u4eba\u82e5\u662f\u77e5\u9053\u2018\u9e33\u9e2f\u53cc\u5251\uff0c\u5373\u5c06 \u6709\u540e\uff0c\u5fc5\u5b9a\u53c8\u662f\u8db3\u4ee5\u8f70\u52a8\u4e00\u65f6\u7684\u5927\u4e8b\uff01\u201d\u53cc\u7709\u5fae\u8f69\uff0c\u795e\u91c7\u98de\u626c\uff0c\u5f97\u610f\u4e4b\u60c5\uff0c\u6ea2\u4e8e\u8a00\u8868\uff0c\u90a3\u5987 \u4eba\u9762\u4e0a\u7684\u7ea2\u6655\uff0c\u5374\u66f4\u6d53\u539a\u4e86\uff0c\u6d53\u5f97\u6709\u5982\u80ed\u8102\uff01\u5979\u5782\u4e0b\u5934\uff0c\u4f4e\u8bed\uff1a\u201c\u6211\u6ca1\u6709\u4ec0\u4e48\uff0c\u8fd8\u62b5\u5f97\u4f4f\uff0c \u8fd9\u6b21\u4e8b\u5173\u7cfb\u7740\u6211\u4eec\u7684\u6b64\u540e\u534a\u751f\uff0c\u4e5f\u5173\u7cfb\u7740\u6211\u809a\u91cc\u8fd9\u5b69\u5b50\u7684\u4e00\u751f\uff0c\u6211\u600e\u80fd\u7559\u5728\u5bb6\u91cc\u4e0d\u95fb\u4e0d \u95ee\uff1f\u201d \u3000\u3000\u9526\u8863\u5927\u6c49\u53cc\u7709\u518d\u6b21\u4e00\u76b1\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4e0d\u77e5\u6c5f\u6e56\u4f20\u8a00\u53ef\u662f\u771f\u7684\uff1f\u6211\u5c31\u4e0d\u4fe1\u90a3\u59d3\u4ec7\u7684\u771f\u4e00 \u4e00\u201d\u5ffd\u5730\u4ed6\u4e0d\u4f4f\u54b3\u55fd\u3002 \u3000\u3000\u4e2d\u5e74\u5987\u4eba\u4f9d\u7136\u5782\u7740\u5934\uff0c\u8bed\u58f0\u66f4\u4f4e\uff0c\u9053\uff1a\u201c\u6709\u4ef6\u4e8b\u6211\u4e00\u76f4\u6ca1\u6709\u548c\u4f60\u8bf4\uff0c\u6015\u4f60\u5fc3\u4e71\uff01\u201d \u3000\u3000\u9526\u8863\u6c49\u5b50\u6025\u95ee\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u4e2d\u5e74\u5987\u4eba\u7f13\u7f13\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u6bdb\u5927\u54e5\u8fd9\u4e9b\u5e74\u6765\uff0c\u5e7f\u690d\u52bf\u529b\uff0c\u4e0d\u60dc\u5343\u65b9\u767e\u8ba1\uff0c\u6536\u4e70\u6b66\u6797\u4eba \u58eb\u7684\u5fc3\uff0c\u90fd\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u9526\u8863\u5927\u6c49\u76b1\u7709\u9053\uff1a\u201c\u4e0d\u77e5\u9053\uff0c\u4f60\u600e\u5730\u8fd1\u5e74\u8bf4\u8bdd\u4e5f\u53d8\u5f97\u541e\u541e\u5410\u5410\u8d77 \u6765\u3002\u201d \u3000\u3000\u4e2d\u5e74\u5987\u4eba\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5341\u4e03\u5e74\u524d\uff0c\u4e00\u4e2a\u4e0b\u96e8\u7684\u665a\u4e0a\uff0c\u4f60\u548c\u6bdb\u5927\u54e5\uff0c\u8fd8\u6709\u675c\u4ef2\u5947\u6df1\u591c \u51fa\u53bb\u641c\u5bfb\u9752\u840d\u5251\u5b8b\u4ee4\u516c\u548c\u5df4\u5c71\u5251\u5ba2\u67f3\u590d\u660e\u7684\u4e0b\u843d\u3002\u201d \u3000\u3000\u4e2d\u5e74\u9526\u8863\u5927\u6c49\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u90a3\u5929\u665a\u4e0a\u7684\u786e\u4e0b\u7740\u96e8\uff0c\u8fd8\u6709\u96f7\u7535\uff0c\u6211\u77e5\u9053\u4f60\u4e00\u5411\u6700\u6015\u96f7\u58f0\u95ea \u7535\uff0c\u5c31\u53eb\u4f60\u548c\u6bdb\u5927\u5ac2\u7761\u5728\u4e00\u8d77\u3002\u201d \u3000\u3000\u76ee\u5149\u4e00\u843d\uff0c\u601d\u6f6e\u56de\u6eaf\uff0c\u6c89\u58f0\u4f4e\u8bed\uff1a\u201c\u90a3\u5929\u665a\u4e0a\uff0c\u867d\u7136\u6ca1\u6709\u5bfb\u5f97\u5230\u5b8b\u8001\u513f\u548c\u67f3\u9053\u58eb\uff0c\u5374\u5728 \u65e0\u610f\u95f4\u62a2\u4e0b\u4e00\u6279\u7ea2\u8d27\uff0c\u8fd9\u4ef6\u4e8b\u6bdb\u8001\u5927\u548c\u793e\u4ef2\u5947\u90fd\u4e0d\u77e5\u9053\u2014\u2014\u201d\u4ed6\u76ee\u5149\u4f3c\u6709\u610f\uff0c\u4f3c\u65e0\u610f\uff0c\u671b\u4e86 \u90a3\u4e2d\u5e74\u5987\u4eba\u5934\u4e0a\u7684\u73e0\u7fe0\u4e00\u773c\uff0c\u63a5\u9053\uff1a\u201c\u540e\u6765\u6211\u4e0e\u6bdb\u8001\u5927\u3001\u675c\u4ef2\u5947\u4f1a\u9f50\uff0c\u56de\u5bb6\u7684\u65f6\u5019\uff0c\u4f60\u5374\u5df2 \u7ecf\u7761\u4e86\uff01\u201d \u3000\u3000\u4e2d\u5e74\u5987\u4eba\u53cc\u76fe\u8f7b\u98a6\uff0c\u6c89\u541f\u534a\u664c\uff0c\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u6211\u77e5\u9053\uff0c\u53ef\u662f\u8be6\u7ec6\u60c5\u5f62\uff0c\u4f60\u4e00\u76f4\u6ca1\u6709\u544a\u8bc9 \u6211\uff0c\u6211\u4e5f\u4e00\u76f4\u6ca1\u6709\u95ee\u4f60\uff0c\u56e0\u4e3a\u6bdb\u5927\u5ac2\u90a3\u5929\u665a\u4e0a\u5bf9\u6211\u8bf4\u4e86\u4e00\u4ef6\u4e8b\uff0c\u6211\u4e5f\u4e00\u76f4\u6ca1\u6709\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u8bed\u58f0\u5fae\u987f\uff0c\u534a\u664c\u9759\u5bc2\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u4e24\u4eba\u5fc3\u91cc\u4f3c\u4e4e\u90fd\u5728\u60f3\u4e9b\u4ec0\u4e48\u3002 \u3000\u3000\u7ec8\u4e8e\uff0c\u4e2d\u5e74\u5987\u4eba\u7f13\u7f13\u9053\uff1a\u201c\u90a3\u5929\u534a\u591c\u91cc\uff0c\u96f7\u58f0\u5f88\u5927\uff0c\u6211\u7ffb\u6765\u8986\u53bb\u5730\u7761\u4e0d\u7740\uff0c\u54ea\u77e5\u6bdb\u5927\u5ac2 \u4e5f\u7ffb\u6765\u8986\u53bb\u5730\u7761\u4e0d\u7740\uff0c\u6211\u5fcd\u4e0d\u4f4f\u95ee\u5979\uff1a\u2018\u4e3a\u4ec0\u4e48\uff1f\uff0c\u5979\u624d\u544a\u8bc9\u6211\uff0c\u8bf4\u6bdb\u5927\u59b9\u5b50\u51fa\u6765\u7684\u65f6\u5019\uff0c \u809a\u91cc\u5df2\u7ecf\u6709\u4e86\u8eab\u5b55\u3002\u201d\u53f9\u606f\u4e00\u58f0\uff0c\u52a0\u4e86\u53e5\uff1a\u201c\u809a\u91cc\u5df2\u7ecf\u6709\u4e86\u59d3\u4ec7\u7684\u5b69\u5b50\uff01\u201d \u3000\u3000\u9526\u8863\u6c49\u5b50\u6d53\u7709\u4e00\u626c\uff0c\u76ee\u5149\u95ea\u52a8\uff0c\u4f3c\u4e4e\u60f3\u8bf4\u4ec0\u4e48\uff0c\u5374\u59cb\u7ec8\u6ca1\u6709\u8bf4\u51fa\uff0c\u9635\u98ce\u81ea\u95e8\u9699\u4e2d\u5439\u5165\uff0c \u4ed6\u53ea\u89c9\u8eab\u4e0a\u8d77\u4e86\u4e00\u9635\u5bd2\u610f\uff01 \u3000\u3000\u4e2d\u5e74\u5987\u4eba\u9ed8\u7136\u534a\u664c\uff0c\u53c8\u9053\uff1a\u201c\u5f53\u65f6\u6211\u542c\u4e86\u5979\u7684\u8bdd\uff0c\u5fc3\u91cc\u867d\u7136\u4e5f\u5728\u5403\u60ca\uff0c\u5374\u8fd8\u662f\u5b89\u6170\u7740 \u5979\uff0c\u8bf4\uff1a\u2018\u8fd9\u5b69\u5b50\u65e2\u7136\u662f\u4f60\u59b9\u5b50\u751f\u7684\uff0c\u96be\u9053\u4f60\u59b9\u5b50\u8fd8\u4f1a\u53eb\u4ed6\u6765\u627e\u4f60\u4eec\u590d\u4ec7\u4e48\uff1f\u2019\u6bdb\u5927\u5ac2\u6ca1\u6709 \u8bf4\u8bdd\uff0c\u8fc7\u4e86\u8bb8\u4e45\uff0c\u5979\u624d\u53f9\u7740\u6c14\u9053\uff1a\u2018\u5927\u59b9\u5b50\u8981\u4e0d\u662f\u5bf9\u5979\u54e5\u54e5\u4e0d\u6ee1\uff0c\u53c8\u600e\u4e48\u4f1a\u6084\u4fcf\u5730\u6e9c\u8d70 \u5462\uff1f\u3002\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u5979\u8bed\u58f0\u4e00\u987f\uff0c\u65b9\u81ea\u63a5\u53e3\u9053\uff1a\u201c\u6240\u4ee5\u540e\u6765\u6bdb\u5927\u5ac2\u575a\u6301\u4e0d\u8ba9\u5979\u5973\u513f\u8ddf\u7740\u6bdb\u5927 \u54e5\u7ec3\u6b66\uff0c\u800c\u628a\u5979\u9001\u5230\u2018\u5c60\u9f99\u4ed9\u5b50\u2019\u90a3\u91cc\u53bb\uff0c\u4e5f\u5c31\u662f\u6015\u6bdb\u5927\u59b9\u5b50\u751f\u7684\u5b69\u5b50\u53bb\u627e\u4ed6\u4eec\u62a5\u4ec7\uff0c\u73b0\u5728 \u4e00\u5509\uff0c\u65f6\u65e5\u5306\u5306\uff0c\u90a3\u4e2a\u5b69\u5b50\u4e5f\u8be5\u957f\u5927\u4e86\u3002\u201d \u3000\u3000\u9526\u8863\u6c49\u5b50\u6d53\u7709\u76b1\u505a\u4e00\u5904\uff0c\u4fef\u9996\u6c89\u601d\u534a\u664c\uff0c\u4eff\u4f5b\u81ea\u8bed\u7740\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u8fd1\u65e5\u7684\u4e8b\uff0c\u96be\u9053 \u771f\u662f\u90a3\u59d3\u4ec7\u2014\u2014\u201d\u8bed\u58f0\u7a81\u987f\uff0c\u5927\u559d\u4e00\u58f0\uff1a\u201c\u662f\u8c01\uff1f\u201d \u3000\u3000\u53cc\u624b\u5fae\u6309\u684c\u9762\uff0c\u8eab\u5f62\u53cd\u63a0\u800c\u51fa\uff0c\u51cc\u7a7a\u4e00\u8f6c\uff0c\u843d\u5728\u95e8\u9699\u8fb9\uff0c\u4e2d\u5e74\u5987\u4eba\u4ea6\u81ea\u957f\u8eab\u800c\u8d77\uff0c\u4e8e\u662f \u5979\u51f8\u8d77\u7684\u8179\u90e8\uff0c\u4ea6\u81ea\u73b0\u51fa\u684c\u5916\u3002 \u3000\u3000\u53ea\u542c\u95e8\u5916\u4e00\u58f0\u6717\u7b11\uff0c\u4e00\u4e2a\u6e05\u6717\u7684\u8bed\u58f0\uff0c\u542b\u7b11\u7b54\u9053\uff1a\u201c\u662f\u6211\uff01\u201d \u3000\u3000\u63a5\u7740\u95e8\u677f\u53c8\u88ab\u62c9\u5f00\u4e00\u7ebf\uff0c\u9996\u5148\u8fdb\u6765\u7684\uff0c\u7adf\u662f\u8fd9\u9152\u5e97\u7684\u5e97\u4f19\u3002 \u3000\u3000\u9526\u8863\u6c49\u5b50\u51b7\u201c\u54fc\u201d\u4e00\u58f0\uff0c\u811a\u4e0b\u5fae\u9000\u534a\u6b65\uff0c\u76ee\u5149\u5374\u4ecd\u51dd\u6ce8\u95e8\u5916\u3002 \u3000\u3000\u660f\u9ec4\u7684\u706f\u5149\u4e0b\uff0c\u53ea\u89c1\u4e00\u4e2a\u9526\u8863\u534e\u670d\uff0c\u98ce\u59ff\u98d2\u723d\u7684\u82f1\u4fca\u5c11\u5e74\uff0c\u542b\u7b11\u8d70\u4e86\u8fdb\u6765\uff0c\u660e\u4eae\u7684\u76ee \u5149\uff0c\u5148\u5728\u90a3\u4e2d\u5e74\u5987\u4eba\u8eab\u4e0a\u4e00\u8f6c\uff0c\u77ac\u5373\u505c\u7559\u5728\u9526\u8863\u6c49\u5b50\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u9526\u8863\u6c49\u5b50\u76ee\u5149\u51db\u7136\uff0c\u7f13\u7ea7\u62ac\u8d77\u624b\u638c\uff0c\u63e1\u4f4f\u8170\u7554\u7684\u4e00\u67c4\u88c5\u78fa\u5f97\u6781\u4e3a\u534e\u4e3d\u7684\u957f\u5251\u5251\u67c4\uff0c\u4ed6\u7684 \u624b\u6307\u7ec6\u957f\u800c\u6709\u529b\uff0c\u6307\u7532\u66f4\u662f\u4fee\u5f97\u5149\u5149\u79c3\u79c3\uff0c\u6b66\u6797\u4e2d\u4eba\u4e00\u671b\u800c\u77e5\uff0c\u6b64\u4eba\u5b9a\u662f\u5251\u6cd5\u6781\u9ad8\u7684\u5185\u5bb6\u5251 \u624b\u3002 \u3000\u3000\u4ed6\u81ea\u4e0a\u800c\u4e0b\uff0c\u4ed4\u7ec6\u5c06\u8fd9\u534e\u670d\u5c11\u5e74\u77a7\u4e86\u4e00\u904d\uff0c\u76ee\u5149\u7f13\u7f13\u8f6c\u5411\u90a3\u5782\u624b\u7acb\u5728\u4e00\u65c1\u7684\u5e97\u4f19\uff0c\u51b7\u51b7 \u95f4\u9053\uff1a\u201c\u6b64\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u90a3\u5e97\u4f19\u89c1\u4e86\u4ed6\u7684\u76ee\u5149\uff0c\u5374\u7ed3\u7ed3\u5df4\u5df4\u4ed6\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u534e\u670d\u5c11\u5e74\u62b1\u62f3\u4e00\u63d6\uff0c\u542b\u7b11\u6717\u58f0\u8bf4\u9053\uff1a \u201c\u5728\u4e0b\u7f2a\u6587\uff0c\u4e43\u662f\u8fd9\u5bb6\u9152\u5e97\u4e1c\u4e3b\u7684\u77e5\u4ea4\u3002\u201d \u3000\u3000\u9526\u8863\u6c49\u5b50\u51b7\u54fc\u4e00\u58f0\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u96be\u9053\u4f60\u662f\u8981\u6765\u4e0b\u9010\u5ba2\u4e4b\u4ee4\u7684\u4e48\uff1f\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u62b1\u62f3\u7b11\u7b54\uff1a\u201c\u5c82\u6562\uff0c\u5c82\u6562\uff0c\u5728\u4e0b\u53ea\u662f\u542c\u5f97\u8fd9\u4f4d\u5e97\u4f19\u8bf4\u8d77\uff0c\u6709\u4e24\u4f4d\u6c14\u5ea6\u4e0d\u51e1\u7684\u5ba2 \u4eba\uff0c\u4eca\u591c\u6ca1\u6709\u5bbf\u5904\uff0c\u662f\u4ee5\u7279\u5730\u8d76\u6765\uff01\u201d \u3000\u3000\u9526\u8863\u6c49\u5b50\u9762\u5bb9\u7565\u9701\uff0c\u201c\u7f2a\u6587\u201d\u63a5\u9053\uff1a\u201c\u5c24\u5176\u662f\u5c0a\u592b\u4eba\u8eab\u4e0a\u4f3c\u4e4e\u4e0d\u4fbf\uff0c\u4e24\u4f4d\u5982\u4e0d\u5acc\u5728\u4e0b\u5192 \u6627\uff0c\u4e0d\u59a8\u5230\u5bd2\u820d\u6682\u5bbf\u4e00\u5bb5\u3002\u201d\u9526\u8863\u6c49\u5b50\u76ee\u5149\u5982\u7535\uff0c\u53c8\u81ea\u4e0a\u800c\u4e0b\u6253\u91cf\u4e86\u4ed6\u51e0\u773c\uff0c\u7a81\u5730\u51b7\u51b7\u9053\uff1a \u201c\u6211\u4e0e\u4f60\u4e00\u4e0d\u6cbe\u4eb2\uff0c\u4e8c\u4e0d\u5e26\u6545\uff0c\u4f60\u5bf9\u6211\u7684\u4e8b\u4e3a\u4f55\u5982\u6b64\u70ed\u5fc3\uff1f\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u795e\u8272\u4f3c\u4e4e\u4e00\u5446\uff0c\u5374\u542c\u4ed6\u5389\u58f0\u53c8\u9053\uff1a\u201c\u4f60\u82e5\u5bf9\u6211\u6709\u6240\u56fe\u8c0b\u4e00\u54fc\u54fc\uff0c\u90a3\u5f53\u771f\u662f\u6d3b\u5f97 \u4e0d\u8010\u70e6\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u6728\u7acb\u534a\u664c\uff0c\u7a81\u5730\u4ef0\u5929\u957f\u7b11\u8d77\u6765\uff0c\u62b1\u62f3\u7b11\u9053\uff1a\u201c\u597d\u597d\uff0c\u9601\u4e0b\u65e2\u7136\u6000\u7591\u5728\u4e0b\u522b\u6709\u7528 \u5fc3\uff0c\u90a3\u4e48\u5c31\u7b97\u5c0f\u53ef\u591a\u6b64\u4e00\u4e3e\u597d\u4e86\u3002\u201d\u888d\u8896\u4e00\u62c2\uff0c\u8f6c\u8eab\u800c\u884c\u3002 \u3000\u3000\u660f\u9ec4\u7684\u706f\u5149\uff0c\u6620\u5f97\u4ed6\u7f15\u91d1\u7684\u957f\u886b\u95ea\u95ea\u751f\u5149\uff0c\u9526\u8863\u6c49\u5b50\u7a81\u5730\u7b11\u9053\uff1a\u201c\u5144\u53f0\u6162\u8d70\u2026\u2026\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u5fae\u5fae\u4e00\u7b11\uff0c\u8f6c\u8fc7\u8eab\u6765\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u6709\u4f55\u89c1\u6559\uff0c\u96be\u9053\u8fd8\u8981\u5c06\u5728\u4e0b\u4e00\u4e00\u4e00\u201d\u9526\u8863\u6c49 \u5b50\u63a5\u53e3\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u524d\u8a00\uff0c\u4e0d\u8fc7\u804a\u4ee5\u76f8\u620f\u8033\uff0c\u4ee5\u5144\u53f0\u8fd9\u822c\u4eba\u54c1\uff0c\u5fc3\u4e2d\u600e\u5730\u4f1a\u6709\u4e0d\u7aef\u4e4b\u56fe \u8c0b\u3002\u201d\u56de\u9996\u671b\u4e86\u90a3\u4e2d\u5e74\u5987\u4eba\u4e00\u773c\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u8bf4\u662f\u4e48\uff1f\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u9762\u4e0a\u4f9d\u7136\u5fae\u5e26\u7b11\u5bb9\uff0c\u5bf9\u4ed6\u8fd9\u79cd\u524d\u5028\u540e\u606d\u7684\u6001\u5ea6\uff0c\u4e1d\u6beb\u4e0d\u4ee5\u4e3a\u610f\uff0c\u4f3c\u4e4e\u5929\u4e0b\u4efb\u4f55 \u4e8b\u7684\u53d1\u751f\uff0c\u90fd\u65e9\u5df2\u843d\u5728\u4ed6\u7684\u7b97\u4e2d\u3002 \u3000\u3000\u4ed6\u53ea\u662f\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u9601\u4e0b\u5982\u4e0b\u5acc\u5bd2\u820d\u7b80\u964b\uff0c\u4fbf\u8bf7\u59d4\u5c48\u4e00\u5bb5\uff0c\u4e5f\u597d\u8ba9\u5c0f\u53ef\u4e00\u5c3d \u5730\u4e3b\u4e4b\u8c0a\u3002\u201d \u3000\u3000\u9526\u8863\u6c49\u4e86\u8fde\u5fd9\u63a5\u53e3\u9053\uff1a\u201c\u65e2\u627f\u62ac\u7231\uff0c\u6562\u4e0d\u4ece\u547d\u3002\u201d \u3000\u3000\u8f6c\u9996\u559d\u9053\uff1a\u201c\u5e97\u5bb6\uff0c\u770b\u5e10\uff01\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u5fae\u5fae\u4e00\u7b11\uff0c\u968f\u624b\u53d6\u51fa\u4e00\u952d\u94f6\u5b50\uff0c\u629b\u5230\u684c\u4e0a\uff0c\u4e00\u9762\u7b11\u9053\uff1a\u201c\u9601\u4e0b\u8fdc\u6765\u662f\u5ba2\uff0c\u4e14\u8ba9 \u5728\u4e0b\u505a\u4e2a\u5c0f\u5c0f\u4e1c\u9053\u3002\u201d \u3000\u3000\u9526\u8863\u6c49\u4e86\u6697\u5730\u9ad8\u5174\uff0c\u5927\u7b11\u9053\uff1a\u201c\u5982\u6b64\u53ea\u5f97\u8c22\u4e86\u3002\u201d \u3000\u3000\u90a3\u4e2d\u5e74\u5987\u4eba\u4ea6\u81ea\u655b\u887d\u4e3a\u793c\u3002 \u3000\u3000\u4e09\u4eba\u3002\u9f50\u8d70\u51fa\u5e97\u5916\uff0c\u90a3\u4e24\u5339\u5065\u9a6c\uff0c\u978d\u8f94\u672a\u5378\uff0c\u4f2b\u7acb\u5728\u72b9\u5e26\u6599\u5ced\u6625\u5bd2\u7684\u665a\u98ce\u91cc\uff0c\u65e2\u4e0d\u5636 \u9e23\uff0c\u4ea6\u65e0\u8822\u52a8\uff0c\u5168\u8eab\u7eaf\u767d\uff0c\u4e00\u65e0\u6742\u8272\uff0c\u773c\u671b\u53bb\uff0c\u4f7f\u77e5\u662f\u5343\u4e2d\u9009\u4e00\u7684\u540d\u79cd\u826f\u9a6c\u3002 \u3000\u3000\u9526\u8863\u6c49\u5b50\u5927\u6b65\u800c\u524d\uff0c\u4f38\u624b\u8f7b\u629a\u9a6c\u9879\u957f\u9b03\uff0c\u542b\u7b11\u56de\u987e\u9053\uff1a\u201c\u5144\u53f0\u51fa\u8eab\u4e16\u5bb6\uff0c\u5fc5\u5b9a\u5584\u4e8e\u76f8 \u9a6c\u3002\u201d\u500f\u7136\u4f4f\u53e3\u4e0d\u8a00\uff0c\u4f46\u8a00\u4e0b\u4e4b\u610f\uff0c\u81ea\u662f\u8981\u201c\u7f2a\u6587\u201d\u5bf9\u4ed6\u8fd9\u4e24\u5339\u767d\u9a6c\u79f0\u8d5e\u4e24\u53e5\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u6de1\u7136\u4e00\u7b11\u9053\uff1a\u201c\u7684\u786e\u662f\u597d\u9a6c\u3002\u201d \u3000\u3000\u9526\u8863\u6c49\u5b50\u9762\u4e0a\u9732\u51fa\u5f97\u610f\u4e4b\u8272\uff0c\u7b11\u9053\uff1a\u201c\u4e0d\u77e5\u5144\u53f0\u53ef\u66fe\u9a76\u9a6c\u800c\u6765\uff0c\u5426\u5219\u4f60\u6211\u4fbf\u5728\u8fd9\u661f\u7a7a\u4e4b \u4e0b\u6f2b\u6b65\u800c\u5f52\uff0c\u5012\u4e5f\u53ef\u7b97\u662f\u4ef6\u96c5\u4e8b\u3002\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u542b\u7b11\u9053\uff1a\u201c\u5bd2\u820d\u79bb\u6b64\u9887\u6709\u4e00\u4e9b\u8def\u9014\uff0c\u5c0a\u592b\u4eba\u2014\u2014\u54c8\u54c8\uff0c\u4f60\u6211\u8fd8\u662f\u4e00\u9f50\u5f52\u53bb\uff0c\u5728 \u4e0b\u5f53\u547d\u4eba\u5c06\u8fd9\u4e24\u5339\u5065\u9a6c\u9001\u56de\u3002\u201d \u3000\u3000\u9526\u8863\u6c49\u5b50\u9762\u5bb9\u5fae\u5fae\u4e00\u53d8\uff0c\u6b63\u5728\u629a\u6478\u9a6c\u9879\u957f\u9b03\u7684\u624b\u638c\uff0c\u4e5f\u7a81\u5730\u505c\u987f\u4e0b\u6765\uff0c\u539f\u6765\u201c\u8fd9\u592b\u59bb\u4e24 \u4eba\u201d\u4e00\u751f\u522b\u65e0\u6240\u55dc\uff0c\u6240\u55dc\u552f\u6709\u9ec4\u767d\u4e4b\u7269\u800c\u5df2\uff0c\u8fd9\u4e24\u5339\u5065\u9a6c\u4ed6\u4e0d\u60dc\u91cd\u91d1\u6c42\u6765\uff0c\u6b64\u523b\u5fc3\u4e2d\u4e0d\u7981\u6697 \u5fd6\uff1a\u201c\u8fd9\u5c11\u5e74\u5f04\u6765\u5f04\u53bb\uff0c\u83ab\u975e\u662f\u60f3\u6765\u9a97\u6211\u8fd9\u4e24\u5339\u9a6c\u4e0d\u6210\uff1f\u201d \u3000\u3000\u5fc3\u5ff5\u65b9\u8f6c\uff0c\u53ea\u89c1\u201c\u7f2a\u6587\u201d\u5de6\u624b\u5fae\u62db\uff0c\u53e3\u4e2d\u8f7b\u8f7b\u547c\u54e8\u4e00\u58f0\uff0c\u8857\u7684\u8f6c\u89d2\u5904\uff0c\u7a81\u5730\u5954\u6765\u4e00\u8f86\u56db \u9a6c\u5927\u8f66\u3002 \u3000\u3000\u661f\u5149\u4e4b\u4e0b\uff0c\u53ea\u89c1\u8fd9\u8f86\u5927\u8f66\u7adf\u662f\u8272\u4f5c\u94f6\u767d\uff0c\u707f\u70c2\u751f\u5149\uff0c\u62c9\u8f66\u7684\u56db\u5339\u5065\u9a6c\uff0c\u4ea6\u662f\u901a\u4f53\u7eaf\u767d\uff0c \u5954\u884c\u4e4b\u52bf\u6781\u8feb\uff0c\u843d\u8e44\u4e4b\u58f0\u5374\u6781\u8f7b\uff0c\u9a6c\u5230\u8fd1\u524d\uff0c\u8d76\u8f66\u7684\u767d\u8863\u5fa1\u8005\u8f7b\u8f7b\u547c\u54e8\u4e00\u58f0\uff0c\u56db\u5339\u5065\u9a6c\uff0c\u4fbf \u4e00\u9f50\u6b62\u6b65\uff0c\u751f\u50cf\u662f\u8f7b\u529f\u5df2\u8fbe\u5999\u5883\u7684\u5185\u5bb6\u9ad8\u624b\u5728\u6025\u884c\u4e4b\u65f6\u6536\u52bf\u90a3\u4e48\u81ea\u7136\u3002 \u3000\u3000\u9526\u8863\u5927\u6c49\u3001\u4e2d\u5e74\u5987\u4eba\u5bf9\u671b\u4e00\u773c\uff0c\u54af\u7136\u82e5\u6709\u6240\u5931\uff0c\u4ed6\u7528\u91cd\u91d1\u6c42\u6765\u7684\u4e24\u5339\u540d\u9a6c\uff0c\u6b64\u523b\u4e0e\u8fd9\u56db \u5339\u767d\u9a6c\u4e00\u6bd4\uff0c\u5b9e\u662f\u5224\u5982\u9704\u58e4\u3002\u8f6c\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u8fd9\u4e24\u5339\u9a6c\u81ea\u5df1\u4f3c\u4e5f\u6709\u4e9b\u81ea\u60ed\u5f62\u79fd\uff0c\u9a6c\u5c3e\u8f7b\u9686\uff0c \u7f13\u7f13\u8d70\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u5bf9\u4ed6\u4eec\u795e\u8272\u7684\u53d8\u5316\uff0c\u4f3c\u4e4e\u6839\u672c\u672a\u66fe\u6ce8\u610f\uff0c\u4ecd\u7136\u542b\u7b11\u9053\uff1a\u201c\u4e24\u4f4d\u5148\u8bf7\u4e0a\u8f66\uff0c\u5c0a\u9a6c \u81ea\u6709\u4eba\u9001\u56de\u5bd2\u820d\u3002\u201d \u3000\u3000\u9526\u8863\u6c49\u5b50\u9021\u5de1\u9053\uff1a\u201c\u5728\u4e0b\u8fd9\u4e24\u5339\u867d\u65e0\u6cd5\u4e0e\u5144\u53f0\u4e4b\u9a6c\u76f8\u6bd4\uff0c\u4f46\u6027\u5b50\u5374\u662f\u987d\u52a3\u5f97\u5f88\uff0c\u751f\u4eba\u8fd1 \u5b83\u4e0d\u5f97\u2014\u2014\u201d\u201c\u7f2a\u6587\u201d\u63a5\u53e3\u7b11\u9053\uff1a\u201c\u5c0f\u53ef\u820d\u4e0b\u5fa1\u8005\uff0c\u6765\u81ea\u5173\u4e1c\uff0c\u4e00\u751f\u9a6f\u9a6c\uff0c\u4e14\u8ba9\u4ed6\u8bd5\u4e0a\u4e00 \u8bd5\u3002\u201d \u3000\u3000\u5fae\u4e00\u62cd\u638c\uff0c\u8fd9\u8f86\u94f6\u5149\u707f\u70c2\u7684\u9a6c\u8f66\u524d\u5ea7\u4e0a\u7684\u4e24\u4e2a\u767d\u8863\u5fa1\u8005\uff0c\u4fbf\u6709\u4e00\u4eba\u8dc3\u4e86\u4e0b\u6765\uff0c\u9526\u8863\u6c49\u5b50 \u76ee\u5149\u8f6c\u5904\uff0c\u53ea\u89c1\u6b64\u4eba\u4e00\u8eab\u94f6\u767d\u52b2\u88c5\uff0c\u677f\u808b\u866c\u9aef\uff0c\u8eab\u8eaf\u7855\u58ee\uff0c\u8eab\u624b\u5374\u6781\u5176\u77eb\u5065\uff0c\u811a\u4e0b\u73e0\u5149\u95ea\u95ea \u5730\uff0c\u7adf\u7a7f\u7740\u4e00\u53cc\u7f00\u4ee5\u660e\u73e0\u4e3a\u9762\u7684\u8584\u8863\u5feb\u9774\uff0c\u8eac\u8eab\u5411\u201c\u7f2a\u6587\u201d\u4e00\u793c\uff0c\u5927\u6b65\u8d70\u5230\u81ea\u5df1\u4e24\u5339\u9a6c\u524d\uff0c \u5ffd\u76ee\u5149\u51dd\u6ce8\uff0c\u811a\u6b65\u653e\u7f13\uff0c\u4e00\u6b65\u4e00\u6b65\u5730\u8d70\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u800c\u81ea\u5df1\u90a3\u4e24\u5339\u201c\u751f\u4eba\u96be\u8fd1\u201d\u7684\u5065\u9a6c\uff0c\u6b64\u523b\u7adf\u5982\u53d7\u9b54\u529b\uff0c\u52a8\u4e5f\u4e0d\u52a8\uff0c\u94f6\u886b\u73e0\u5c65\u7684\u5173\u4e1c\u5927\u6c49 \u8f7b\u8f7b\u6613\u6613\u5730\u4fbf\u62c9\u4e86\u5b83\u4eec\u7684\u97ad\u7d22\uff0c\u7eb5\u8eab\u8dc3\u4e86\u4e0a\u53bb\uff0c\u9526\u8863\u5927\u6c49\u9762\u988a\u4e0d\u7981\u4e3a\u4e4b\u5fae\u5fae\u4e00\u7ea2\uff0c\u4f46\u77ac\u53c8\u6717 \u58f0\u7b11\u9053\uff1a\u201c\u6614\u65e5\u5e73\u539f\u516c\u5b50\u95e8\u4e0b\u98df\u5ba2\uff0c\u7686\u8e51\u73e0\u5c65\uff0c\u5df2\u4f20\u4e3a\u5343\u53e4\u7f8e\u8c08\uff0c\u4eca\u65e5\u5144\u53f0\u95e8\u4e0b\u5fa1\u8005\uff0c\u4ea6\u8e51 \u73e0\u5c65\uff0c\u5c82\u975e\u66f4\u80dc\u5e73\u539f\u4e09\u5206\uff01\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u5fae\u7b11\u9053\uff1a\u201c\u5144\u53f0\u8fc7\u5956\u4e86\uff01\u201d\u4e3e\u624b\u63d6\u5ba2\u3002 \u3000\u3000\u9526\u8863\u6c49\u5b50\u592b\u5987\u4e8c\u4eba\u5750\u4e0a\u9a6c\u8f66\uff0c\u53ea\u89c1\u8f66\u5185\u9526\u58a9\u94f6\u6162\uff0c\u660e\u73e0\u5d4c\u58c1\uff0c\u67d4\u548c\u7684\u73e0\u5149\uff0c\u7167\u5f97\u8fd9\u8f66\u53a2 \u91cc\u66f4\u89c1\u5bcc\u4e3d\u5802\u7687\u3002 \u3000\u3000\u8f66\u53a2\u5916\u53c8\u81ea\u8f7b\u8f7b\u547c\u54e8\u4e00\u58f0\uff0c\u9a6c\u8f66\u524d\u884c\uff0c\u81ea\u9ad8\u5916\u671b\uff0c\u53ea\u89c1\u4e24\u65c1\u5e97\u5bb6\u62db\u724c\uff0c\u5982\u98de\u5411\u540e\u5012\u53bb\uff0c \u8f66\u53a2\u5185\u5374\u4ecd\u5e73\u7a33\u5df2\u6781\uff0c\u4e00\u5982\u672a\u66fe\u542f\u884c\u524d\u4e00\u6837\u3002 \u3000\u3000\u8fd9\u592b\u5987\u4e24\u4eba\u6b64\u523b\u5fc3\u4e2d\u5b9e\u662f\u60ca\u7591\u4ea4\u96c6\uff0c\u518d\u4e5f\u731c\u4e0d\u51fa\u8fd9\u964c\u751f\u5c11\u5e74\u7a76\u7adf\u662f\u4f55\u6765\u8def\uff0c\u4ed6\u65e2\u6709\u6f58\u5b89 \u4e4b\u8c8c\uff0c\u53c8\u6709\u9093\u901a\u4e4b\u5bcc\uff0c\u4f46\u884c\u6b62\u8c26\u8c26\uff0c\u8c08\u5410\u65af\u6587\u3002\u5374\u53c8\u4e0d\u5e26\u4e00\u4e1d\u9a84\u6c14\uff0c\u6b64\u523b\u4ed6\u7ed3\u4ea4\u81ea\u5df1\uff0c\u4e3a\u7684 \u662f\u4ec0\u4e48\uff1f \u3000\u3000\u8fd9\u592b\u5987\u4e24\u4eba\u4e00\u751f\u884c\u4e8b\u6c5f\u6e56\uff0c\u5374\u4ece\u672a\u89c1\u8fc7\u5982\u6b64\u5947\u4eba\uff0c\u9047\u8fc7\u5982\u6b64\u5947\u4e8b\uff0c\u53ea\u542c\u201c\u7f2a\u6587\u201d\u53c8\u81ea\u7b11 \u9053\uff1a\u2018\u9601\u4e0b\u8170\u4f69\u957f\u5251\uff0c\u6c14\u5b57\u795e\u6001\uff0c\u66f4\u662f\u8f69\u6602\u5df2\u6781\uff0c\u60f3\u5fc5\u5b9a\u662f\u6b66\u6797\u6210\u540d\u5927\u4fa0\uff0c\u4e0d\u6562\u8bf7\u6559\u4e24\u4f4d\u5927 \u540d\uff1f\u201d\u9526\u8863\u6c49\u5b50\u6d53\u7709\u5fae\u626c\uff0c\u6717\u58f0\u9053\uff1a\u201c\u5728\u4e0b\u7a0b\u67ab\uff0c\u548c\u8d31\u5185\uff0c\u627f\u6c5f\u6e56\u670b\u53cb\u62ac\u7231\uff0c\u5524\u6211\u592b\u59bb\u505a \u2018\u9e33\u9e2f\u53cc\u4fa0\u2019\uff01\u201d \u3000\u3000\u6b64\u523b\u4ed6\u5728\u8fd9\u5c11\u5e74\u9762\u524d\uff0c\u5df2\u552f\u6709\u81ea\u5df1\u7684\u59d3\u540d\u8db3\u4ee5\u81ea\u50b2\uff0c\u662f\u4ee5\u4ed6\u5c06\u2018\u9e33\u9e2f\u53cc\u4fa0\uff0c\u56db\u5b57\uff0c\u8bf4\u5f97 \u5206\u5916\u54cd\u4eae\u3002\u201c\u7f2a\u6587\u201d\u795e\u8272\u4e4b\u95f4\uff0c\u679c\u7136\u7acb\u523b\u9732\u51fa\u94a6\u4f69\u4e4b\u8272\uff0c\u542b\u7b11\u62b1\u62f3\u9053\uff1a\u201c\u5c0f\u53ef\u867d\u662f\u4e00\u4ecb\u4e66 \u751f\uff0c\u5e73\u751f\u5374\u6700\u6155\u6c5f\u6e56\u6e38\u4fa0\uff0c\u65e9\u5df2\u4e45\u95fb\u4e24\u4f4d\u5927\u540d\uff0c\u4e0d\u60f3\u4eca\u65e5\u7adf\u80fd\u5728\u65e0\u610f\u4e4b\u4e2d\u5f97\u89c1\u4fa0\u9a7e\u3002\u201d \u3000\u3000\u9526\u8863\u6c49\u5b50\u7a0b\u67ab\u54c8\u54c8\u4e00\u7b11\uff0c\u90a3\u4e00\u76f4\u655b\u8884\u7aef\u5750\uff0c\u9ed8\u9ed8\u4e0d\u8bed\u7684\u4e2d\u5e74\u5987\u4eba\u201c\u6797\u7433\u201d\u5374\u5fae\u7b11\u4e00\u58f0\uff0c \u7f13\u7f13\u9053\uff1a\u201c\u6211\u8f88\u6c5f\u6e56\u4e2d\u4eba\uff0c\u7eb5\u7136\u540d\u52a8\u56db\u6d77\uff0c\u5374\u53c8\u600e\u6bd4\u5f97\u4e0a\u516c\u5b50\u4f60\u8fd9\u822c\u5927\u5bcc\u5927\u8d35\u7684\u6c14\u8c61\u3002\u201d\u773c \u6ce2\u6a2a\u6d41\uff0c\u8198\u4e86\u4ed6\u4e08\u592b\u4e00\u773c\uff0c\u795e\u8272\u4e4b\u95f4\uff0c\u4f3c\u4e4e\u5bf9\u201c\u7f2a\u6587\u201d\u7684\u5bcc\u8d35\u6c14\u8c61\u6781\u4e3a\u7fa1\u6155\uff0c\u53ea\u5dee\u6ca1\u6709\u8bf4\u51fa \u53e3\u6765\u800c\u5df2\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u7b11\u9053\uff1a\u201c\u51e1\u4fd7\u5bcc\u8d35\uff0c\u5c0f\u53ef\u65e9\u5df2\u538c\u5026\uff0c\u54ea\u5982\u8d24\u6881\u5b5f\u6325\u97ad\u56db\u6d77\uff0c\u5feb\u610f\u6069\u4ec7\u8fd9\u822c\u900d\u9065 \u81ea\u5728\uff0c\u65e5\u524d\u5c0f\u53ef\u66fe\u6709\u5e78\u89c1\u8fc7\u676d\u5dde\u57ce\u7684\u201d\u6bdb\u5927\u8001\u7237\uff0c\u4e00\u9762\u201d\u7a0b\u67ab\u63a5\u53e3\u9053\uff1a\u201c\u539f\u6765\u5144\u53f0\u4e0e\u6211\u5927\u54e5 \u8fd8\u662f\u76f8\u8bc6\uff0c\u90a3\u4e48\u4f60\u6211\u8d8a\u53d1\u4e0d\u662f\u5916\u4eba\u4e86\u3002\u201d\u4ef0\u9996\u4e00\u9635\u5927\u7b11\uff0c\u4f46\u76ee\u5149\u5374\u7262\u7262\u5730\u76ef\u5728\u8f66\u58c1\u95f4\u7684\u660e\u73e0 \u4e0a\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u81ea\u59cb\u81f3\u7ec8\uff0c\u9762\u4e0a\u90fd\u5e26\u7740\u4ed6\u90a3\u4e00\u4efd\u60ef\u6709\u7684\u5fae\u7b11\uff0c\u800c\u6b64\u523b\u4ed6\u9762\u4e0a\u7684\u7b11\u5bb9\uff0c\u5374\u8d8a\u53d1\u5f00 \u6717\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u77e5\u9053\u81ea\u5df1\u53c8\u6293\u4f4f\u4e86\u4e00\u4e2a\u5bf9\u624b\u7684\u5f31\u70b9\uff0c\u4ed6\u76f8\u4fe1\u81ea\u5df1\u82e5\u662f\u5411\u8fd9\u4e2a\u5f31\u70b9\u8fdb\u653b\uff0c\u4e00\u5b9a\u53ef\u4ee5 \u653b\u5230\u5bf9\u65b9\u7684\u5fc3\u810f\u3002 \u3000\u3000\u7a0b\u67ab\u3001\u6797\u7433\uff0c\u5374\u65e0\u8a00\u5730\u4ea4\u6362\u4e86\u4e00\u4e2a\u773c\u8272\uff0c\u8fd9\u592b\u5987\u4e8c\u4eba\u6570\u5341\u5e74\u5bdd\u98df\u4e0e\u5171\uff0c\u81ea\u7136\u5fc3\u610f\u76f8\u901a\uff0c \u6b64\u523b\u4e0d\u7ea6\u800c\u540c\u5730\u6697\u6697\u5fd6\u9053\uff1a\u201c\u8fd9\u5c11\u5e74\u679c\u7136\u6709\u4e9b\u2018\u8840\u6c34\u2019\uff0c\u4e5f\u4e0d\u5728\u6211\u592b\u5987\u8ddf\u4ed6\u6765\u8fd9\u4e00\u8d9f\u3002\u201d \u3000\u3000\u539f\u6765\u201c\u9e33\u9e2f\u53cc\u5251\u201d\u6027\u6700\u8d2a\u8d22\uff0c\u5982\u4eca\u867d\u5df2\u5bb6\u8d22\u4e07\u8d2f\uff0c\u5374\u4ecd\u4e0d\u65f6\u51fa\u624b\u505a\u4e9b\u4e0d\u8981\u672c\u94b1\u7684\u4e70\u5356\u3002 \u3000\u3000\u8f66\u4e2d\u4e8c\u4eba\u5404\u6709\u6240\u601d\uff0c\u4f46\u9762\u4e0a\u5374\u4ff1\u6ee1\u9762\u7b11\u5bb9\uff0c\u4f3c\u4e4e\u8c08\u5f97\u5341\u5206\u878d\u6d3d\uff0c\u5927\u6709\u9877\u523b\u4fbf\u5df2\u77e5\u5df1\u6a21 \u6837\u3002 \u3000\u3000\u8c08\u7b11\u4e4b\u95f4\uff0c\u8f66\u884c\u5ffd\u987f\u4f4f\u4e86\u201d\u3002 \u3000\u3000\u7a0b\u67ab\u65b9\u5f85\u4f38\u624b\u53bb\u62c9\u8f66\u95e8\uff0c\u8f66\u95e8\u5374\u5df2\u81ea\u5f00\uff0c\u95e8\u5916\u5782\u624b\u8083\u7acb\u7740\u4e2a\u767d\u8863\u5bb6\u4e01\uff0c\u606d\u8eab\u9053\uff0c\u201c\u516c\u5b50 \u56de\u6765\u4e86\uff01\u201d \u3000\u3000\u7a0b\u67ab\u5411\u5916\u4e00\u671b\uff0c\u53ea\u89c1\u8f66\u9a6c\u7adf\u505c\u5728\u4e00\u680b\u5de8\u5b85\u9762\u524d\uff0c\u6731\u7ea2\u7684\u5927\u95e8\uff0c\u9752\u94dc\u7684\u95e8\u73af\uff0c\u6b64\u523b\u970d\u5730\u655e \u5f00\uff0c\u95e8\u5185\u5ead\u9662\u6df1\u6c89\uff0c\u4e00\u773c\u671b\u53bb\uff0c\u5f53\u771f\u662f\u5176\u6df1\u5982\u6d77\u3002 \u3000\u3000\u4e8e\u662f\u7a0b\u67ab\u3002\u6797\u7433\uff0c\u592b\u5987\u4e24\u4eba\u518d\u6b21\u5bf9\u671b\u4e00\u773c\uff0c\u4e24\u4eba\u7684\u5634\u89d2\uff0c\u4e0d\u81ea\u89c9\u5730\u90fd\u6709\u4e00\u4e1d\u5f97\u610f\u7684\u7b11\u5bb9 \u6d6e\u8d77\u3002 \u3000\u3000\u7a7f\u8fc7\u4e00\u6761\u788e\u77f3\u96e8\u9053\uff0c\u8fdb\u4e86\u5782\u82b1\u95e8\uff0c\u4e24\u8fb9\u662f\u6284\u624b\u6e38\u5eca\uff0c\u6b63\u4e2d\u662f\u7a7f\u5802\uff0c\u5802\u5916\u653e\u7740\u4e00\u9762\u7d2b\u6a80\u6728 \u67b6\u7684\u5927\u7406\u77f3\u5c4f\u98ce\uff0c\u8f6c\u8fc7\u5c4f\u98ce\uff0c\u4fbf\u662f\u4e09\u95f4\u5385\u623f\uff0c\u5385\u540e\u53c8\u662f\u6b63\u623f\u5927\u9662\uff0c\u6b63\u9762\u4e94\u95f4\u4e0a\u623f\uff0c\u7686\u662f\u96d5\u6881 \u753b\u680b\u3002 \u3000\u3000\u7a0b\u67ab\u592b\u5987\u4e0e\u201c\u7f2a\u6587\u201d\u5e76\u80a9\u8d70\u5165\u5927\u5385\uff0c\u53ea\u542c\u4e00\u9635\u5b9b\u8f6c\u96c0\u9e23\uff0c\u539f\u6765\u4e24\u8fb9\u6e38\u5eca\u5eca\u4e0a\uff0c\u7ade\u6302\u6ee1\u4e86 \u5404\u8272\u753b\u7709\u9e66\u9e49\u3002 \u3000\u3000\u7a0b\u67ab\u8fd1\u5e74\u6765\u9887\u77e5\u4eab\u53d7\uff0c\u8863\u98df\u4f4f\u884c\uff0c\u4ff1\u90fd\u9009\u7528\u7684\u7cbe\u54c1\uff0c\u4f46\u6b64\u523b\u89c1\u4e86\u8fd9\u7b49\u5ead\u9662\uff0c\u624d\u77e5\u9053\u81ea\u5df1 \u6240\u8c13\u201c\u517b\u5c0a\u5904\u4f18\u201d\u7684\u751f\u6d3b\uff0c\u548c\u4eba\u5bb6\u4e00\u6bd4\uff0c\u5b9e\u5728\u7b97\u4e0d\u5f97\u4ec0\u4e48\uff0c\u5fc3\u4e2d\u53c8\u4e0d\u7981\u4e3a\u4e4b\u60f6\u7136\u82e5\u5931\u3002 \u3000\u3000\u8fdb\u5165\u6b63\u5385\u3002\u8fce\u9762\u4fbf\u662f\u4e00\u65b9\u8d64\u91d1\u58a8\u9f99\u9752\u5730\u5927\u533e\uff0c\u533e\u4e0a\u5199\u7740\u9f99\u98de\u51e4\u821e\u7684\u56db\u4e2a\u5288\u5de2\u5927\u5b57\uff0c\u5199\u7684 \u662f\uff1a\u201c\u6ee1\u5802\u5bcc\u8d35\u3002\u201d \u3000\u3000\u7528\u610f\u867d\u4fd7\uff0c\u5b57\u8ff9\u5374\u6b8a\u4e0d\u4fd7\uff0c\u4ea6\u4e0d\u77e5\u662f\u4f55\u4eba\u624b\u7b14\u3002 \u3000\u3000\u533e\u4e0b\u4e00\u5f20\u5927\u7d2b\u6a80\u96d5\u5ae1\u6848\u4e0a\uff0c\u653e\u7740\u4e09\u5c3a\u591a\u9ad8\u7684\u4e00\u5177\u9752\u7eff\u53e4\u94dc\u9f0e\uff0c\u60ac\u7740\u5f85\u6f0f\u968b\u671d\u58a8\u9f99\u5927\u753b\uff0c \u4e00\u9762\u662f\u6574\u91d1\u5f5d\uff0c\u4e00\u9762\u5374\u662f\u4e2a\u7cbe\u81f4\u751f\u5149\uff0c\u4f3c\u662f\u6c34\u6676\uff0c\u53c8\u4f3c\u662f\u7409\u7483\u7389\u76c6\uff0c\u5730\u4e0a\u5374\u80a9\u201d\u4e24\u6392\u5341\u516d\u5f20 \u6960\u6728\u5706\u6905\u3002 \u3000\u3000\u53c8\u6709\u4e00\u4ed8\u5bf9\u8054\uff0c\u4e43\u662f\u4e4c\u6728\u8054\u724c\uff0c\u9576\u7740\u933e\u91d1\u5b57\u8ff9\uff0c\u5199\u7684\u662f\uff1a\u201c\u5ea7\u4e0a\u73e0\u7391\u662d\u65e5\u6708\uff1b\u5802\u524d\u812f\u655d \u7115\u70df\u971e\u3002\u201d \u3000\u3000\u5b57\u8ff9\u6e05\u79c0\u633a\u9038\uff0c\u4e0e\u533e\u4e0a\u7684\u90a3\u56db\u4e2a\u5288\u5de2\u5927\u5b57\uff0c\u663e\u7136\u4e0d\u662f\u4e00\u4eba\u6240\u4e66\u3002 \u3000\u3000\u4e24\u65c1\u7a97\u524d\uff0c\u5374\u6446\u7740\u4e00\u5bf9\u5bf9\u7684\u6885\u82b1\u5c0f\u51e0\uff0c\u51e0\u4e0a\u66f4\u662f\u7433\u7405\u6ee1\u76ee\uff0c\u7f8e\u4e0d\u80dc\u6536\uff0c\u6709\u53e4\u8da3\u76ce\u7136\u7684\u6587 \u738b\u9f0e\uff0c\u6ee1\u7f00\u7fe0\u7389\u73e0\u5b9d\u7684\u5319\u7b4b\u9999\u76d2\uff0c\u6709\u7a00\u4e16\u96be\u6c42\u7684\u73ca\u80e1\u7f8e\u4eba\u89ce\uff0c\u6709\u51e0\u53ef\u4e71\u771f\u7684\u7fe0\u7389\u74dc\u679c\u3002 \u3000\u3000\u4e00\u773c\u671b\u53bb\uff0c\u4f46\u89c9\u8fd9\u5927\u5385\u8fd9\u4e2d\u4ff1\u662f\u5b9d\u6c14\u73e0\u5149\uff0c\u65e0\u8bba\u4efb\u4f55\u4eba\u8d70\u4eba\u8fd9\u95f4\u5927\u5385\uff0c\u90fd\u5b9a\u7136\u4f1a\u6709\u5982\u5728 \u5c71\u836b\u9053\u4e0a\uff0c\u76ee\u4e0d\u6687\u7ed9\u7684\u611f\u89c9\u3002 \u3000\u3000\u7a0b\u67ab\u867d\u7136\u89c1\u591a\u8bc6\u5e7f\uff0c\u81f3\u6b64\u4e5f\u4e0d\u7981\u4e3a\u4e4b\u5931\u8272\u3002 \u3000\u3000\u53ea\u542c\u201c\u7f2a\u6587\u201d\u542b\u7b11\u9053\uff1a\u201c\u5609\u5174\u57ce\u5e76\u975e\u5c0f\u53ef\u4e45\u5c45\u4e4b\u5730\uff0c\u6b64\u95f4\u4e5f\u53ea\u662f\u5c0f\u53ef\u4e34\u65f6\u843d\u811a\u4e4b\u5904\uff0c\u662f \u4ee5\u7c97\u7cd9\u7b80\u964b\uff0c\u5728\u6240\u96be\u514d\uff0c\u8fd8\u8bf7\u8d24\u6881\u5b5f\u4f11\u5f97\u89c1\u7b11\u3002\u201d \u3000\u3000\u7a0b\u67ab\u76ee\u5149\u4e00\u8f6c\uff0c\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u6b64\u95f4\u82e5\u8fd8\u662f\u7c97\u7cd9\u7b80\u964b\u7684\u8bdd\uff0c\u4e16\u4e0a\u53ea\u6015\u518d\u65e0\u534e\u53a6\u4e86\u3002\u201d\u4f38 \u624b\u6307\u5411\u5802\u524d\u90a3\u65b9\u8d64\u91d1\u58a8\u9f99\u5927\u533e\uff0c\u53c8\u81ea\u7b11\u9053\uff1a\u201c\u4f9d\u5728\u4e0b\u4e4b\u6240\u89c1\uff0c\u8fd9\u5385\u5802\u4e5f\u53ea\u6709\u2018\u6ee1\u5802\u5bcc\u8d35\u2019\u56db \u5b57\uff0c\u5dee\u53ef\u5f62\u5bb9\u3002\u201d \u3000\u3000\u7acb\u523b\u4e4b\u95f4\uff0c\u53c8\u6446\u4e0a\u4e00\u684c\u9152\u83dc\uff0c\u81ea\u7136\u4ea6\u662f\u73cd\u94f8\u6ee1\u684c\uff0c\u6c34\u9646\u5e76\u5448\uff0c\u8fd9\u4e9b\u83dc\u80b4\u867d\u7136\u4e0d\u662f\u5341\u5206\u73cd \u8d35\u4e4b\u7269\uff0c\u5947\u602a\u7684\u662f\u4ed6\u600e\u80fd\u5728\u5982\u6b64\u6df1\u591c\uff0c\u9877\u523b\u7acb\u5c31\uff01 \u3000\u3000\u591c\u8272\u66f4\u6d53\uff0c\u9152\u7b75\u81ea\u7ec8\u3002 \u3000\u3000\u7a0b\u67ab\u3001\u6797\u7433\uff0c\u88ab\u5f15\u5230\u540e\u53a2\u7684\u4e09\u95f4\u8033\u623f\u3002\u4e34\u7a97\u4e00\u9762\u5927\u5e8a\uff0c\u4e0a\u94fa\u7329\u7ea2\u6bdb\u6bef\uff0c\u6b63\u9762\u6ca1\u7740\u5927\u7ea2\u91d1 \u94b1\u87d2\u5f15\u6795\uff0c\u79cb\u9999\u8272\u91d1\u94b1\u87d2\u5927\u6761\u8925\uff0c\u4e24\u8fb9\u53c8\u662f\u4e24\u5f20\u6885\u82b1\u5c0f\u51e0\uff0c\u9648\u8bbe\u4e4b\u534e\u4e3d\uff0c\u4fbf\u662f\u4ed6\u592b\u5987\u82b1\u70db\u4e4b \u591c\u7684\u6d1e\u623f\uff0c\u4ea6\u65e0\u5982\u6b64\u9c9c\u8273\u8003\u7a76\u3002 \u3000\u3000\u66f4\u6572\u56db\u9f13\u3001\u661f\u7fa4\u6e10\u7a00\u3002 \u3000\u3000\u8fd9\u534e\u4e3d\u7684\u5de8\u5b85\u4e2d\u7684\u706f\u706b\uff0c\u4ea6\u6e10\u6e10\u758f\u843d\uff0c\u7184\u706d\u3002 \u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u4f46\u662f\u2014\u2014 \u3000\u3000\u201c\u9e33\u9e2f\u53cc\u5251\u2019\u6240\u7559\u5bbf\u7684\u4e09\u95f4\u8033\u623f\u4e2d\uff0c\u5374\u7a81\u5730\u54cd\u8d77\u4e86\u8f7b\u5fae\u7684\u4eba\u8bed\u2014\u2014\u8f7b\u5fae\u5f97\u51e0\u4e4e\u6709\u5982\u868a \u545c\u3002\u53ea\u542c\u6797\u7433\u8f7b\u8f7b\u9053\uff1a\u201c\u5582\uff0c\u4f60\u8fd8\u5728\u60f3\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7a0b\u67ab\u8bed\u58f0\u66f4\u4f4e\uff0c\u9053\uff1a\u201c\u6211\u5728\u60f3\u2014\u2014\u6211\u5373\u4f7f\u505a\u4e86\uff0c\u4e5f\u6c38\u8fdc\u4e0d\u4f1a\u6709\u4eba\u731c\u5230\u4f1a\u662f\u6211\u505a\u7684\uff0c\u8fd9\u662f \u4ed6\u81ea\u5df1\u627e\u4e0a\u95e8\u6765\uff0c\u987b\u6028\u4e0d\u5f97\u6211\u3002\u201d \u3000\u3000\u6c89\u9ed8\u826f\u4e45\uff0c\u6797\u7433\u65b9\u53c8\u4f4e\u8bed\u3002 \u3000\u3000\u201c\u6211\u4ec0\u4e48\u90fd\u4e0d\u8981\uff0c\u53ea\u8981\u4e34\u7a97\u90a3\u5f20\u6885\u82b1\u51e0\u4e0a\u7684\u7fe0\u7389\u897f\u74dc\uff0c\u8fd8\u6709\u7a0b\u67ab\u8f7b\u7b11\u9053\uff1a\u201c\u8fd8\u6709\u90a3\u9762\u6c34 \u6676\u7389\u76d2\u548c\u73cd\u73e0\u9999\u76d2\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u6797\u7433\u8f7b\u8f7b\u4e00\u7b11\uff0c\u7a81\u53c8\u53f9\u9053\uff1a\u201c\u5341\u516b\u5e74\u524d\uff0c\u4f60\u53bb\u8ffd\u5b8b\u3001\u67f3\u4e24\u4eba\u7684\u90a3\u5929\u665a\u4e0a\uff0c\u5728\u676d\u5dde\u57ce\u5916\u62fe \u4e0b\u7684\u7ea2\u8d27\uff0c\u6211\u5df2\u7ecf\u4ee5\u4e3a\u662f\u7a00\u4e16\u4e4b\u5b9d\u4e86\uff0c\u4eca\u5929\u624d\u77e5\u9053\u90a3\u90fd\u7b97\u4e0d\u4e86\u4ec0\u4e48\uff01\u201d \u3000\u3000\u8bed\u8c03\u5fae\u987f\uff0c\u53c8\u9053\uff1a\u201c\u73b0\u5728\u5df2\u7ecf\u56db\u66f4\uff0c\u4f60\u8981\u53bb\u5c31\u8be5\u5feb\u53bb\uff0c\u5509\u2014\u2014\u5176\u5b9e\u4eba\u5bb6\u5982\u6b64\u6b3e\u5f85\u6211\u4eec\uff0c \u6211\u4eec\u5374\u8981\u2014\u2014\u201d\u500f\u7136\u4f4f\u53e3\uff0c\u8f7b\u8f7b\u54b3\u55fd\u3002 \u3000\u3000\u7a0b\u67ab\u5fae\u7b11\u4f4e\u9053\uff1a\u201c\u5987\u4eba\u4e4b\u89c1\uff0c\u5987\u4eba\u4e4b\u89c1\u4e00\u201d\u7a81\u7136\u4e00\u9635\u5e10\u94a9\u53ee\u94db\u8f7b\u54cd\uff0c\u7a0b\u67ab\u53c8\u9053\uff1a\u201c\u5148\u53d6 \u4eba\u5934\uff0c\u518d\u53d6\u73cd\u5b9d\uff0c\u5927\u7ea6\u4e0d\u5230\u987f\u996d\u5de5\u592b\uff0c\u6211\u5c31\u56de\u6765\uff5e\u4e00\u201d\u8bed\u58f0\u672a\u4e86\uff0c\u7a97\u4e2d\u63a8\u5f00\u4e00\u7ebf\uff0c\u7a97\u5916\u98d8\u7136 \u63a0\u51fa\u4e00\u6761\u4eba\u5f71\uff0c\u811a\u5c16\u8f7b\u70b9\uff0c\u4fbf\u5df2\u7a9c\u4e0a\u5c4b\u810a\uff0c\u8eab\u6cd5\u4e4b\u8f7b\u7075\u5de7\u5feb\uff0c\u53ef\u79f0\u4e00\u65f6\u4e4b\u9009\u3002 \u3000\u3000\u6b64\u523b\u4e07\u7c41\u4ff1\u5bc2\uff0c\u6625\u866b\u4e0d\u8bed\uff0c\u6ee1\u5929\u7e41\u661f\uff0c\u4e5f\u8fd8\u758f\u758f\u843d\u843d\u5730\u5269\u4e0b\u4e00\u534a\uff0c\u6620\u5f97\u8fdc\u8fd1\u6811\u6728\uff0c\u7ef0\u7ea6 \u5982\u4ed9\u5b50\u3002 \u3000\u3000\u7a0b\u67ab\u98d8\u8eab\u63a0\u4e0a\u5c4b\u810a\uff0c\u6e38\u76ee\u56db\u987e\uff0c\u53ea\u89c1\u5c4b\u810a\u6809\u6bd4\uff0c\u623f\u820d\u8fde\u4e91\uff0c\u90a3\u5bb6\u516c\u5b50\u201c\u7f2a\u6587\u201d\u7a76\u7adf\u4f4f\u5728 \u54ea\u91cc\uff1f \u3000\u3000\u4ed6\u4e0d\u7981\u4e3a\u4e4b\u72b9\u7591\u534a\u664c\uff0c\u6697\u4e2d\u5fd6\u9053\uff1a\u201c\u6211\u5355\u53d6\u4ed6\u73cd\u5b9d\u4e5f\u5c31\u662f\uff0c\u4f55\u5fc5\u5b9a\u53d6\u4ed6\u6027\u547d\u3002\u201d\u4e00\u5ff5\u81f3 \u6b64\uff0c\u8eab\u5f62\u63a0\u52a8\uff0c\u6709\u5982\u4e00\u7f15\u8f7b\u70df\u5411\u5927\u5385\u63a0\u53bb\uff01 \u7b2c\u5341\u4e5d\u7ae0>> \u53e4\u9f99\u300a\u6e58\u5983\u5251\u300b \u7b2c\u5341\u4e5d\u7ae0 \u3000\u3000\u5239\u90a3\u4e4b\u95f4\uff0c\u7a0b\u67ab\u8eab\u5f62\u4fbf\u5df2\u63a0\u8fc7\u51e0\u91cd\u5c4b\u9762\uff0c\u7a81\u542c\u4e00\u9635\u6717\u541f\u4e4b\u58f0\uff0c\u81ea\u53f3\u4fa7\u4f20\u6765\uff01\u4ed6\u8eab\u5f62\u7acb \u987f\uff0c\u51dd\u795e\u800c\u542c\uff0c\u53ea\u542c\u541f\u9053\uff1a\u201c\u9ec4\u6cb3\u4e4b\u6c34\u5929\u4e0a\u6765\uff0c\u7389\u697c\u6e05\u5f71\u63a5\u5929\u53f0\u3002\u4e3e\u6a3d\u8fdb\u9152\u5ba2\u8854\u676f\uff0c\u5a07\u5bb9\u6d45 \u7b11\u6bd4\u73ab\u7470\u3002\u6837\u6cfd\u4e1c\u6765\u4e03\u5341\u91cc\uff0c\u957f\u6ee1\u590d\u5811\u57cb\u4e91\u91cc\uff0c\u9ec4\u53f6\u79cb\u98ce\u4e00\u8427\u745f\uff0c\u6c49\u9675\u8d70\u9a6c\u9ec4\u5c18\u8d77\u2014\u2014\u201d\u8bd7 \u58f0\u6e05\u6717\uff0c\u58f0\u8c03\u5374\u4e0d\u751a\u9ad8\uff0c\u7a0b\u67ab\u811a\u6b65\u5fae\u5fae\u4e00\u987f\uff0c\u4fbf\u7b14\u76f4\u5411\u8bd7\u58f0\u6765\u8def\u6251\u53bb\uff0c\u53ea\u89c1\u897f\u9762\u4e09\u95f4\u53a2\u623f \u4e2d\uff0c\u8fd8\u9690\u9690\u6709\u706f\u5149\u900f\u51fa\u3002 \u3000\u3000\u4ed6\u811a\u4e0b\u8f7b\u8f7b\u4e00\u6ed1\uff0c\u6e9c\u4e0b\u5c4b\u810a\uff0c\u54ea\u77e5\u8bd7\u58f0\u7adf\u7a81\u5730\u987f\u4f4f\u3002\u4ed6\u4e0d\u7981\u4e3a\u4e4b\u4e00\u60ca\uff0c\u53ea\u542c\u90a3\u5bcc\u5bb6\u516c\u5b50 \u201c\u7f2a\u6587\u201d\u7684\u58f0\u97f3\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u9ad8\u5347\uff0c\u660e\u65e5\u6e05\u6668\uff0c\u4f60\u53bb\u9a6c\u53a9\u5c06\u4eca\u65e5\u90a3\u4e24\u4f4d\u5ba2\u4eba\u4e58\u5750\u7684\u4e24\u5339\u5065\u9a6c \u7684\u978d\u8f94\u53d6\u6765\u4e00\u4e00\u201d\u7a0b\u67ab\u53cc\u7709\u4e00\u76b1\uff0c\u51b7\u7b11\u4e00\u58f0\u3002 \u3000\u3000\u5374\u542c\u201c\u7f2a\u6587\u201d\u63a5\u9053\uff1a\u201c\u518d\u5c06\u90a3\u4e24\u4ed8\u978d\u8f94\uff0c\u914d\u5728\u5927\u767d\u548c\u4e8c\u767d\u7684\u8eab\u4e0a\u4e00\u4e00\u201d\u7a0b\u67ab\u4e3a\u4e4b\u4e00\u5446\uff0c \u5374\u542c\u4e00\u4e2a\u58f0\u97f3\u5341\u5206\u606d\u656c\u4ed6\u8bf4\u9053\uff1a\u201c\u516c\u5b50\u96be\u9053\u8981\u5c06\u2018\u5927\u767d\u2019\u3001\u2018\u4e8c\u767d\uff0c\u9001\u7ed9\u90a3\u4e24\u4f4d\u5ba2\u4eba\u4e48\uff1f\u201d \u201c\u7f2a\u6587\u201d\u9053\uff1a\u201c\u6b63\u662f\uff01\u201d \u3000\u3000\u90a3\u606d\u656c\u7684\u8bed\u58f0\u505c\u4e86\u534a\u664c\uff0c\u671f\u827e\u7740\u9053\uff1a\u201c\u53ef\u662f\u2026\u2026\u2018\u5927\u767d\u2019\u3001\u2018\u4e8c\u767d\u2019\u4e00\u53bb\uff0c\u2018\u4e09\u767d\u2019\u3001 \u2018\u56db\u767d\uff0c\u5c82\u4e0d\u662f\u8981\u592a\u5bc2\u5bde\u4e86\u4e48\uff1f\u4f55\u51b5\u2026\u2026\u8fd9\u4e24\u5339\u9a6c\u516c\u5b50\u8d39\u4e86\u8bb8\u591a\u5fc3\u8840\u624d\u5f04\u6765\uff0c\u5982\u4eca\u5374\u5982\u6b64\u8f7b \u6613\u5730\u9001\u4eba\uff0c\u5c82\u4e0d\u662f\u53c8\u592a\u53ef\u60dc\u4e86\u4e48\uff1f\u201d\u7a0b\u67ab\u60c5\u4e0d\u81ea\u7981\uff0c\u6697\u9a82\u4e86\u58f0\uff1a\u201c\u8be5\u6b7b\u7684\u5974\u624d\uff01\u201d \u3000\u3000\u5374\u542c\u201c\u7f2a\u6587\u201d\u6717\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4ec0\u4e48\uff0c\u60f3\u90a3\u4f4d\u7a0b\u5148\u751f\uff0c\u4e43\u662f\u5f53\u4eca\u7684\u5927\u82f1\u96c4\u3001\u5927\u8c6a \u6770\uff0c\u5b9d\u9a6c\u8d60\u4e88\u82f1\u96c4\uff0c\u6b63\u662f\u5929\u7ecf\u5730\u4e49\u4e4b\u4e8b\uff0c\u4f60\u96be\u9053\u4e0d\u77e5\u516c\u5b50\u6211\u5e73\u751f\u6700\u559c\u7ed3\u4ea4\u7684\uff0c\u5c31\u662f\u9876\u5929\u7acb \u5730\uff0c\u5feb\u610f\u6069\u4ec7\u7684\u82f1\u96c4\u8c6a\u5ba2\u4e48\uff1f\u201d \u3000\u3000\u5c4b\u6a90\u4e0b\u7684\u7a0b\u67ab\uff0c\u95fb\u8a00\u4e0d\u7981\u6697\u9053\u4e00\u58f0\u60ed\u6127\uff0c\u53ea\u542c\u201c\u7f2a\u6587\u201d\u53c8\u81ea\u6717\u5ff5\u8d77\u6765\uff01 \u3000\u3000\u201c\u5357\u6d66\u6709\u9c7c\u8165\u4e14\u6d8e\uff0c\u771f\u73e0\u2014\u2014\u201d\u8bd7\u58f0\u53c8\u987f\uff0c\u9053\uff1a\u201c\u9ad8\u5347\uff0c\u660e\u6668\u914d\u9a6c\u8fc7\u540e\uff0c\u518d\u81ea\u6211\u5904\u5c06\u4ec5 \u5b58\u7684\u4e00\u5347\u771f\u73e0\u5168\u90e8\u53d6\u6765\uff0c\u6084\u6084\u653e\u5230\u90a3\u4e24\u4f4d\u5ba2\u4eba\u7684\u9a6c\u978d\u91cc\uff0c\u4f11\u5f97\u8ba9\u4ed6\u4eec\u4e24\u4f4d\u77e5\u9053\uff01\u201d \u3000\u3000\u9ad8\u5347\u606d\u58f0\u5e94\u4e86\uff0c\u5450\u5450\u53c8\u9053\uff1a\u201c\u4f46\u2026\u2026\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u542b\u7b11\u63a5\u53e3\u9053\uff1a\u201c\u4f60\u662f\u5426\u5728\u5947\u602a\u6211\u4e3a\u4f55\u4e0d\u8ba9\u4ed6\u4e24\u4f4d\u77e5\u9053\uff1f\u8981\u77e5\u8fd9\u4e9b\u82f1\u96c4\u8c6a\u6770\uff0c\u884c \u4e8b\u591a\u6709\u8d85\u4e4e\u5e38\u4eba\u4e4b\u5904\uff0c\u6211\u82e5\u660e\u8d60\uff0c\u4ed6\u5b9a\u5fc5\u4e0d\u53d7\uff0c\u662f\u4ee5\u53ea\u5f97\u6697\u9001\u4e86\u3002\u201d \u3000\u3000\u7a0b\u67ab\u5446\u4e86\u4e00\u5446\uff0c\u4e0d\u7981\u53c8\u6697\u9053\u4e00\u58f0\uff1a\u201c\u60ed\u6127\uff01\u201d \u3000\u3000\u53ea\u542c\u201c\u7f2a\u6587\u201d\u63a5\u53e3\u541f\u9053\uff1a\u201c\u2026\u2026\u771f\u73e0\u53ef\u5b9d\u5f00\u5bb9\u989c\u3002\u201d \u3000\u3000\u8861\u9633\u96c1\u8fdf\u4eba\u672a\u8fd8\uff0c\u6175\u61d2\u72b9\u602f\u5c0f\u6df3\u5929\u3002 \u3000\u3000\u5fc6\u5f97\u9c9b\u4e1d\u7ec7\u8749\u7ffc\uff0c\u517d\u7089\u6c16\u6c22\u6e58\u5e18\u5782\u3002 \u3000\u3000\u7eff\u7ee3\u7b19\u56ca\u4e0d\u89c1\u4eba\uff0c\u70db\u5f71\u6447\u7a97\u591c\u6df1\u5bc2\u3002\u201d \u3000\u3000\u8bd7\u58f0\u518d\u987f\uff0c\u201c\u7f2a\u6587\u201d\u9053\uff1a\u201c\u8fd8\u6709\uff0c\u4eca\u591c\u6211\u89c1\u90a3\u4f4d\u592b\u4eba\uff0c\u76ee\u5149\u987f\u987f\u6ce8\u89c6\u7740\u90a3\u7fe0\u7389\u897f\u74dc\uff0c\u771f \u73e0\u9999\u76d2\uff0c\u4ee5\u53ca\u90a3\u6c34\u6676\u7389\u76d2\uff0c\u60f3\u5fc5\u5bf9\u8fd9\u51e0\u7965\u4e1c\u897f\uff0c\u751a\u4e3a\u559c\u7231\uff0c\u660e\u6668\u4f60\u4e5f\u5c06\u6b64\u4e09\u7269\u4e00\u5e76\u5305\u8d77\u6765\uff0c \u52a0\u4e0a\u90a3\u5177\u6587\u738b\u53e4\u9f0e\uff0c\u51d1\u6210\u56db\u6837\u793c\u7269\uff0c\u6302\u5728\u9a6c\u978d\u540e\u3002\u201d \u3000\u3000\u201c\u9ad8\u5347\u201d\u81ea\u53c8\u8bfa\u8bfa\u79f0\u662f\uff0c\u7a97\u5916\u7684\u7a0b\u67ab\u5374\u5fcd\u4e0d\u4f4f\u518d\u6b21\u6697\u9053\uff1a\u201c\u60ed\u6127\uff01\u8fd9\u5c11\u5e74\u5982\u6b64\u6177\u6168\u597d \u4e49\uff0c\u6211\u82e5\u518d\u4e0d\u5229\u4e8e\u4ed6\uff0c\u5c82\u975e\u826f\u5fc3\u6709\u6127\u3002\u201d \u3000\u3000\u5239\u90a3\u4e4b\u95f4\uff0c\u4ed6\u6025\u53c8\u601d\u53ca\u5341\u4e03\u5e74\u524d\u7684\u5f80\u4e8b\u2026\u2026 \u3000\u3000\u90a3\u662f\u4e2a\u5927\u96e8\u5927\u6ec2\u6ca6\u7684\u6df1\u591c\uff0c\u4ed6\u79bb\u5f00\u6bdb\u81ec\u548c\u675c\u4ef2\u5947\u72ec\u81ea\u641c\u5bfb\uff0c\u5927\u96e8\u4e4b\u4e2d\uff0c\u5ffd\u5730\u9a76\u6765\u4e00\u8f86\u8f66 \u9a6c\u2026\u2026 \u3000\u3000\u7a0b\u67ab\u6697\u6697\u53f9\u606f\u4e00\u58f0\uff0c\u4e2d\u65ad\u4e86\u81ea\u5df1\u7684\u601d\u6f6e\uff0c\u6697\u4e2d\u5583\u5583\u81ea\u8bed\uff1a\u201c\u8fd9\u5c11\u5e74\u6211\u5012\u8981\u597d\u751f\u4ea4\u4e0a\u4e00 \u4ea4\u3002\u201d \u3000\u3000\u8170\u8eab\u4e00\u633a\uff0c\u65e0\u6bd4\u77eb\u5065\u800c\u8f7b\u7075\u5730\u63a0\u4e0a\u5c4b\u9762\uff0c\u63a5\u8fde\u6570\u4e2a\u8d77\u843d\uff0c\u5411\u81ea\u5df1\u7559\u5bbf\u7684\u8033\u623f\u63a0\u56de\uff0c\u53ea\u542c \u201c\u7f2a\u6587\u201d\u72b9\u5728\u6717\u541f\uff1a\u201c\u5e7d\u5170\u5e26\u9732\u5e7d\u9999\u7edd\uff0c\u753b\u56fe\u6d45\u5199\u677e\u6eaa\u6c34\u3002\u695a\u5929\u6f84\u6f88\u7af9\u679d\u9ad8\uff0c\u8c31\u586b\u65b0\u8bcd\u94fa\u9526 \u7eb8\u3002\u5df4\u897f\u591c\u5e02\u7ea2\u5b88\u5bab\uff0c\u540e\u623f\u70b9\u81c2\u6591\u6591\u7ea2\uff0c\u5824\u5357\u5b64\u96c1\u81ea\u98de\u4e45\uff0c\u82a6\u82b1\u4e00\u591c\u5439\u897f\u98ce\u2026\u2026\u201d \u3000\u3000\u4ed6\u8eab\u5f62\u53bb\u5f97\u8d8a\u8fdc\uff0c\u8bd7\u58f0\u4e5f\u5c31\u9010\u6e10\u8f7b\u5fae\uff0c\u7ec8\u4e8e\u4e0d\u518d\u53ef\u95fb\uff0c\u82cd\u7a79\u4e0a\u7684\u661f\u7fa4\u66f4\u7a00\uff0c\u6599\u5ced\u7684\u591c\u98ce \u66f4\u51c9\u3002 \u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u4f46\u662f\u4e00\u4e00\u4e00 \u3000\u3000\u897f\u9762\u90a3\u4e09\u95f4\u53a2\u623f\u7684\u706f\u5149\uff0c\u5374\u7a81\u5730\u52a0\u4eae\u4e00\u4e9b\uff0c\u7d27\u95ed\u7740\u7684\u7a97\u6237\uff0c\u4e5f\u88ab\u7f13\u7f13\u63a8\u5f00\u4e00\u7ebf\u4e00\u4e00\u4e8e\u662f \u4e00\u58f0\u8f7b\u5fae\u7684\u51b7\u7b11\uff0c\u4fbf\u81ea\u8fd9\u7a97\u9699\u4e2d\u4f20\u51fa\uff0c\u968f\u98ce\u98d8\u6563\u3002 \u3000\u3000\u7a97\u5185\u4e00\u9762\u7d2b\u6a80\u6728\uff0c\u4e91\u6bcd\u9762\uff0c\u5927\u96d5\u82b1\u6848\u4fa7\uff0c\u501a\u684c\u800c\u5750\uff0c\u4e0d\u4f4f\u51b7\u7b11\u7684\uff0c\u6b63\u662f\u90a3\u201c\u6177\u6168\u201d\u7684 \u201c\u5bcc\u5bb6\u516c\u5b50\u201d\u7f2a\u6587\u3002 \u3000\u3000\u5782\u624b\u8083\u7acb\u5728\u4ed6\u8eab\u540e\u7684\u4e00\u4eba\uff0c\u8eab\u6750\u81c3\u80bf\uff0c\u9762\u76ee\u75f4\u80a5\uff0c\u5374\u6b63\u662f\u90a3\u5e02\u4e95\u597d\u6c49\u201c\u5f20\u4e00\u6876\u201d\uff0c\u6b64\u523b \u6311\u8d77\u59c6\u6307\uff0c\u8fde\u8fde\u8d5e\u9053\uff1a\u201c\u516c\u5b50\u4f60\u5f53\u771f\u6709\u4e24\u4e0b\u5b50\uff0c\u53ea\u53ef\u601c\u90a3\u59d3\u7a0b\u7684\u8fd8\u5728\u81ea\u6211\u9676\u9189\u3002\u201d \u3000\u3000\u8bed\u58f0\u5fae\u987f\uff0c\u53c8\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4f60\u5f53\u771f\u8981\u5c06\u90a3\u4e9b\u5b9d\u9a6c\u660e\u73e0\u9001\u7ed9\u4ed6\u4e48\uff1f\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u76ee\u5149\u4e4b\u4e2d\uff0c\u9690\u73b0\u6740\u673a\uff0c\u7a81\u5730\u62cd\u6848\u7b11\u9053\uff1a\u201c\u5b9d\u9a6c\u660e\u73e0\uff0c\u80fd\u503c\u51e0\u4f55\uff0c\u81ea\u7136\u662f\u771f\u7684\u8981 \u9001\u7ed9\u4ed6\u7684\u3002\u201d \u3000\u3000\u5ffd\u53c8\u8f7b\u8f7b\u4e00\u76b1\u5251\u7709\uff0c\u81ea\u8bed\u7740\u9053\uff1a\u201c\u5929\u65f6\u5df2\u5c06\u5927\u4eae\uff0c\u90a3\u4f4d\u2018\u4e03\u7a8d\uff0c\u738b\u5e73\u600e\u5730\u8fd8\u672a\u5230 \u6765\u2026\u2026\u201d\u201c\u5f20\u4e00\u6876\u201d\u4e00\u7b11\u63a5\u53e3\u9053\uff1a\u201c\u516c\u5b50\u4f46\u8bf7\u653e\u5fc3\u597d\u4e86\uff0c\u738b\u4e8c\u54e5\u505a\u4e8b\u6700\u6700\u7cbe\u7ec6\uff0c\u7edd\u4e0d\u4f1a\u51fa\u4ec0 \u4e48\u5dee\u9519\u7684\uff0c\u5927\u7ea6\u4e0d\u4e45\u4fbf\u80fd\u5230\u4e86\u3002\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u5c55\u989c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4e45\u95fb\u6881\u5927\u54e5\u624b\u4e0b\u6709\u2018\u56db\u5927\u91d1\u521a\u201d\u4ff1\u662f\u4e07\u4e2d\u9009\u4e00\u7684\u4eba\u624d\uff0c\u53ea \u53ef\u60dc\u6211\u81f3\u4eca\u53ea\u89c1\u7740\u4e86\u4f60\u548c\u201c\u51b3\u9a6c\uff0c\u7a0b\u4e03\u4e24\u4f4d\uff0c\u4f60\u7684\u529e\u4e8b\u80fd\u529b\uff0c\u81ea\u662f\u4e0d\u5fc5\u8bf4\u4e86\uff0c\u7a0b\u4e03\u9a6f\u9a6c\u7684\u529f \u592b\uff0c\u4ea6\u8db3\u4ee5\u505a\u89c6\u7fa4\u4f26\uff0c\u4e3e\u624b\u4e4b\u95f4\uff0c\u4f7f\u5f97\u90a3\u59d3\u7a0b\u7684\u81ea\u547d\u5f97\u610f\u7684\u4e24\u5339\u52a3\u9a6c\u6536\u670d\u4e86\u4e0b\u6765\uff0c\u4ee5\u6b64\u7c7b \u63a8\uff0c\u5176\u4f59\u4e24\u4f4d\u5b9a\u5fc5\u4ea6\u662f\u4e0d\u51e1\u3002\u201d\u201c\u5f20\u4e00\u6876\u201d\u7b11\u9053\uff1a\u201c\u5927\u80e1\u5b50\u8001\u7a0b\u9a6f\u9a6c\u7684\u529f\u592b\u7684\u786e\u6709\u4e24\u624b\uff0c\u65e0 \u8bba\u4ec0\u4e48\u52a3\u9a6c\uff0c\u5230\u4e86\u4ed6\u624b\u91cc\u90fd\u5f97\u670d\u670d\u8d34\u8d34\uff0c\u53ef\u662f\u6211\u4eec\u738b\u4e8c\u54e5\u5462\uff0c\u563f\u563f\uff0c\u4ed6\u5bf9\u4ed8\u4eba\u5c31\u548c\u8001\u4e03\u5bf9\u4ed8 \u9a6c\u4e00\u6837\uff0c\u65e0\u8bba\u662f\u8c01\u9047\u7740\u4ed6\uff0c\u4e09\u8a00\u4e24\u8bed\u5c31\u5f97\u670d\u670d\u8d34\u8d34\u3002\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u6697\u53f9\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u8c01\u9053\u5e02\u4e95\u4e4b\u4e2d\u6ca1\u6709\u5947\u624d\uff0c\u6709\u4e86\u8fd9\u51e0\u4eba\u4e3a\u8f85\uff0c\u65e0\u602a\u2018\u4e5d\u8db3\u795e \u86db\u2019\u6881\u4e0a\u4eba\u5f97\u4ee5\u540d\u626c\u5929\u4e0b\uff01\u201d \u3000\u3000\u76ee\u5149\u4e00\u8f6c\uff0c\u4e1c\u65b9\u5df2\u9690\u9690\u73b0\u51fa\u9c7c\u9752\u4e4b\u8272\uff0c\uff1a\u2018\u7f2a\u6587\u201d\u9762\u4e0a\u65b9\u81ea\u6cdb\u8d77\u7b11\u5bb9\uff0c\u4e0d\u7981\u53c8\u4e3a\u4e4b\u7acb \u655b\u3002\u4f46\u662f\u4e00\u4e00\u6b64\u523b\u95e8\u5916\u5374\u5df2\u54cd\u8d77\u4e86\u4e00\u9635\u5306\u5fd9\u7684\u811a\u6b65\u58f0\uff0c\u201c\u7f2a\u6587\u201d\u7cbe\u795e\u4e00\u632f\uff0c\u5f20\u4e00\u6876\u559c\u9053\uff1a \u201c\u6765\u4e86\u3002\u201d \u3000\u3000\u8f6c\u8eab\u4e00\u6b65\u63a5\u5230\u95e8\u53e3\uff0c\u4f38\u624b\u62c9\u5f00\u623f\u95e8\uff0c\u53ea\u89c1\u95e8\u5916\u4eba\u5f71\u4e00\u82b1\uff0c\u5df2\u81ea\u5927\u6b65\u95ef\u5165\u4e00\u4e2a\u8eab\u957f\u516b\u5c3a\uff0c \u80e1\u53d1\u78a7\u76ee\uff0c\u6ee1\u9762\u866c\u987b\u7684\u5f6a\u5f62\u5927\u6c49\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u6276\u6848\u800c\u8d77\uff0c\u5fc3\u4e2d\u5374\u4e0d\u7981\u5927\u5947\uff01 \u3000\u3000\u201c\u6b64\u4eba\u751f\u50cf\u5982\u6b64\u5a01\u731b\uff0c\u60f3\u5fc5\u5b9a\u662f\u2018\u56db\u5927\u91d1\u521a\uff0c\u4e2d\u7684\u53e6\u4e00\u4eba\u2019\u5927\u529b\u795e\u2019\u4e01\u9738\u4e86\u3002\u201d\u8f6c\u5ff5\u53c8 \u7126\u5207\u5730\u5fd6\u9053\uff1a\u201c\u90a3\u2018\u4e03\u7a8d\u2019\u738b\u5e73\u672a\u6765\uff0c\u6b64\u65f6\u5374\u6765\u4e86\u5927\u529b\u795e\uff0c\u5374\u53c8\u4e3a\u7684\u662f\u4ec0\u4e48\uff1f\u96be\u9053\u90a3\u738b\u5e73\u51fa \u4e86\u4ec0\u4e48\u4e8b\u6545\u4e0d\u6210\uff1f\u201d \u3000\u3000\u53ea\u89c1\u8fd9\u866c\u9aef\u5927\u6c49\u5927\u6b65\u5954\u5165\uff0c\u7adf\u5411\u4ed6\u957f\u8eab\u4e00\u793c\uff0c\u606d\u58f0\u9053\uff1a\u201c\u9601\u4e0b\u4e30\u795e\u5982\u7389\uff0c\u60f3\u5fc5\u5c31\u662f\u6211\u6881 \u5927\u54e5\u53e3\u4e2d\u7684\u4ec7\u516c\u5b50\u4e86\uff01\u201d \u3000\u3000\u8bed\u58f0\u6c89\u7f13\uff0c\u5410\u5b57\u65af\u6587\uff0c\u4e0e\u4ed6\u7684\u5916\u8868\u7adf\u662f\u5927\u4e0d\u76f8\u79f0\uff01 \u3000\u3000\u201c\u7f2a\u6587\u201d\u4e00\u9762\u542b\u7b11\u8c26\u8c22\uff0c\u4e00\u9762\u5374\u53c8\u4e0d\u7981\u4e3a\u4e4b\u5947\u602a\uff0c\u8fd9\u822c\u7c97\u9c81\u7684\u5f6a\u5f62\u5927\u6c49\uff0c\u600e\u4f1a\u8bf4\u51fa\u5982\u6b64 \u65af\u6587\u7684\u8a00\u8bed\uff1f \u3000\u3000\u53ea\u542c\u866c\u9aef\u5927\u6c49\u53c8\u9053\uff1a\u201c\u516c\u5b50\u5429\u5490\u7684\u4e8b\uff0c\u5c0f\u4eba\u5e78\u4e0d\u8fb1\u547d\uff0c\u5df2\u4ee3\u516c\u5b50\u529e\u59a5\uff0c\u53ea\u662f\u8f66\u9a6c\u803d\u8bef\uff0c \u662f\u4ee5\u6765\u665a\u4e86\u4e9b\uff0c\u8fd8\u671b\u516c\u5b50\u6055\u7f6a\u3002\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8131\u53e3\u9053\uff1a\u201c\u9601\u4e0b\u53ef\u5c31\u662f\u4eba\u79f0\u2018\u4e03\u7a8d\u2019\u7684\u738b\u5e73\uff1f\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c0f\u4eba\u6b63\u662f\u738b\u5e73\u3002\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u76ee\u5149\u4e00\u626b\uff0c\u53ea\u89c1\u6b64\u4eba\u5916\u8c8c\u867d\u7136\u7c97\u9c81\u5a01\u731b\uff0c\u4f46\u6c14\u5ea6\u5374\u6781\u4e3a\u6c89\u9759\uff0c\u8a00\u8bed\u66f4\u662f\u5341\u5206\u5f97 \u4f53\u3002 \u3000\u3000\u4ed6\u5e74\u7eaa\u867d\u8f7b\uff0c\u9605\u5386\u4ea6\u4e0d\u4e30\uff0c\u4f46\u5374\u4e0e\u751f\u4ff1\u6765\u5730\u6709\u7740\u4e00\u4efd\u80fd\u4e86\u89e3\u522b\u4eba\u7684\u80fd\u529b\uff0c\u6b64\u523b\u4ed6\u4e00\u773c\u671b \u53bb\uff0c\u4fbf\u77e5\u6b64\u4eba\u5916\u867d\u62d9\uff0c\u5185\u5b9e\u5de7\uff0c\u6b63\u662f\u51fa\u7c7b\u62d4\u9769\u7684\u7cbe\u660e\u5e72\u7ec3\u89d2\u8272\uff0c\u4e0d\u7981\u5bf9\u6b64\u4eba\u66f4\u52a0\u4e86\u51e0\u5206\u7559 \u610f\u3002 \u3000\u3000\u53ea\u542c\u8fd9\u201c\u4e03\u7a8d\u201d\u738b\u5e73\u53c8\u9053\uff1a\u201c\u5c0f\u4eba\u4e0e\u624b\u4e0b\u7684\u51e0\u4f4d\u5f1f\u5144\uff0c\u67e5\u8bbf\u591a\u65f6\uff0c\u624d\u5c06\u90a3\u4e8b\u63a2\u67e5\u786e\u5b9e\uff0c \u5341\u4e03\u5e74\u524d\uff0c\u676d\u5dde\u57ce\u5916\u6240\u4e22\u7684\u90a3\u6279\u7ea2\u8d27\uff0c\u7684\u786e\u662f\u2018\u7075\u86c7\u2019\u6bdb\u81ec\u79c1\u4e0b\u7684\u6697\u9556\u3002\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u5251\u7709\u4e00\u626c\uff0c\u76ee\u5149\u5c04\u51fa\u7cbe\u5149\uff0c\u9053\uff1a\u201c\u4f60\u4e14\u5750\u4e0b\uff0c\u5148\u559d\u53e3\u8336\uff0c\u518d\u6162\u6162\u9053\u6765\u3002\u201d \u3000\u3000\u201c\u4e03\u7a8d\u201d\u738b\u5e73\u542b\u7b11\u8c22\u8fc7\uff0c\u5374\u4ecd\u5782\u624b\u8083\u7acb\uff0c\u9053\uff1a\u201c\u5341\u4f59\u5e74\u524d\uff0c\u6c5f\u5357\u9556\u5c40\uff0c\u672c\u591a\u662f\u2018\u9752\u840d \u5251\u2019\u5b8b\u4ee4\u516c\u7684\u624b\u4e0b\uff0c\u5b8b\u4ee4\u516c\u2026\u4e00\u751f\u884c\u4e8b\uff0c\u9887\u4e3a\u5149\u660e\u78ca\u843d\u201d\u201c\u7f2a\u6587\u201d\u7a81\u5730\u51b7\u201c\u54fc\u201d\u4e00\u58f0\uff0c\u738b\u5e73 \u6115\u7136\u4f4f\u53e3\uff0c\u201c\u7f2a\u6587\u201d\u5c55\u989c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8bf4\u4e0b\u53bb\uff01\u201d \u3000\u3000\u738b\u5e73\u5e72\u54b3\u4e00\u58f0\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u662f\u4ee5\u51e1\u662f\u4e0e\u5b8b\u4ee4\u516c\u6709\u5173\u7684\u9556\u5c40\uff0c\u4e00\u5f8b\u4e0d\u5f97\u63a5\u4fdd\u2018\u6697\u9556\u2019\uff0c\u4f46 \u6709\u4e9b\u4eba\u5f97\u4e86\u4e0d\u4e49\u4e4b\u8d22\uff0c\u8b6c\u5982\u8bf4\u597d\u5546\u6240\u5f97\u7684\u66b4\u5229\uff0c\u8d2a\u5b98\u641c\u522e\u7684\u6c11\u8102\uff0c\u90fd\u52bf\u53ef\u4e0d\u80fd\u660e\u76ee\u5f20\u80c6\u5730\u8fd0 \u56de\u5bb6\u53bb\uff0c\u662f\u4ee5\u90a3\u65f6\u4fbf\u6709\u8bb8\u591a\u2018\u5730\u4e0b\u9556\u5c40\u201d\u5e94\u8fd0\u800c\u751f\u3002\u201d\u8bed\u58f0\u5fae\u987f\uff0c\u53c8\u9053\uff1a\u201c\u4f46\u8fd9\u4e9b\u2018\u5730\u4e0b\u9556 \u5c40\u2019\uff0c\u4ea6\u662f\u89c1\u4e0d\u5f97\u4eba\u7684\u52fe\u5f53\uff0c\u6b66\u6797\u4e2d\u6709\u4e9b\u2018\u4e07\u540d\uff0c\u7684\u89d2\u8272\uff0c\u591a\u4e0d\u5c51\u4e3a\uff0c\u662f\u4ee5\u4fdd\u6697\u9556\u7684\u9556\u5ba2\uff0c \u81ea\u7136\u591a\u662f\u4e9b\u4e09\u3001\u56db\u6d41\u7684\u4eba\u7269\uff0c\u4e8e\u662f\u53c8\u662f\u4e00\u6279\u7eff\u6797\u4e2d\u4eba\uff0c\u4e13\u52ab\u6697\u9556\uff0c\u4e00\u6765\u5bb9\u6613\u5f97\u624b\uff0c\u4e8c\u6765\u88ab\u52a8 \u7684\u4eba\u5927\u534a\u5fcd\u6c14\u541e\u58f0\uff0c\u4e0d\u6562\u58f0\u5f20\uff0c\u662f\u4ee5\u4e5f\u4e0d\u5bb9\u6613\u5931\u98ce\u51fa\u4e8b\uff01\u201d\u201c\u5f20\u4e00\u6876\u201d\u54c8\u54c8\u4e00\u3002\u7b11\uff0c\u63d2\u53e3 \u9053\uff1a\u201c\u8fd9\u5f53\u771f\u53ef\u4ee5\u7b97\u505a\u6807\u51c6\u7684\u9ed1\u5403\u9ed1\u4e86\u3002\u201d \u3000\u3000\u201c\u4e03\u7a8d\u201d\u738b\u5e73\u7f13\u7f13\u63a5\u53e3\u9053\uff1a\u201c\u4e0d\u9519\uff01\u8fd9\u6b63\u662f\u4ee5\u9ed1\u5403\u9ed1\uff0c\u4f46\u5982\u6b64\u4e00\u6765\uff0c\u2018\u5730\u4e0b\u9556\u5c40\uff0c\u5931\u9556 \u7684\u6b21\u6570\u4e00\u591a\uff0c\u81ea\u7136\u88ab\u6dd8\u6c70\u4e86\u8bb8\u591a\u5bb6\uff0c\u800c\u88ab\u6dd8\u6c70\u4e86\u7684\u2019\u5730\u4e0b\u9556\u5ba2\uff0c\u65e0\u6cd5\u8c0b\u751f\uff0c\u5c31\u7d22\u6027\u4e5f\u5e72\u8d77\u7eff \u6797\u751f\u6daf\u6765\uff0c\u4ed6\u4eec\u8f7b\u8f66\u719f\u8def\uff0c\u52ab\u8d77\u9556\u6765\uff0c\u66f4\u52a0\u65b9\u4fbf\uff0c\u5230\u540e\u6765\u7d22\u6027\u8fde\u2018\u5730\u4e0b\u9556\u5ba2\u2019\u4e5f\u548c\u8fd9\u4e9b\u7eff\u6797 \u52fe\u7ed3\uff0c\u4e8e\u662f\u5c31\u4e71\u4e0a\u52a0\u4e71\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u6c89\u9759\uff0c\u8bf4\u5f97\u6709\u6761\u4e0d\u7d0a\uff0c\u8981\u8a00\u4e0d\u70e6\uff0c\u201c\u7f2a\u6587\u201d\u4e0d\u7981\u6697\u8d5e\u4e00\u58f0\uff0c\u5374\u542c\u4ed6\u63a5\u7740\u53c8\u81ea\u7f13\u7f13 \u8bf4\u9053\uff1a\u201c\u8fd9\u65f6\u2018\u7075\u86c7\u2019\u6bdb\u81ec\u770b\u5230\u6709\u5229\u53ef\u56fe\uff0c\u5c45\u7136\u4e5f\u5728\u6697\u4e2d\u5e72\u8d77\u2018\u5730\u4e0b\u9556\u5c40\u2019\u7684\u4e70\u5356\uff0c\u4ee5\u4ed6\u7684 \u6b66\u529f\uff0c\u751f\u610f\u81ea\u7136\u8d8a\u505a\u8d8a\u5927\uff0c\u4e8e\u662f\u4ed6\u53c8\u6536\u4e70\u4e86\u4e00\u4e9b\u5728\u6b66\u6797\u4e2d\u65e0\u6cd5\u7acb\u8db3\u7684\u89d2\u8272\uff0c\u2018\u516b\u9762\u73b2\u73d1\u2019\u80e1 \u4e4b\u8f89\uff0c\u2018\u94c1\u624b\u4ed9\u733f\u2019\u4faf\u6797\uff0c\u2018\u94c1\u7b97\u5b50\u2019\u6c41\u8c0b\uff0c\u90fd\u662f\u5728\u90a3\u65f6\u6295\u5165\u4ed6\u7684\u95e8\u4e0b\u3002\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u51b7\u7b11\u4e00\u58f0\uff0c\u738b\u5e73\u63a5\u9053\uff1a\u201c\u53ea\u662f\u4ed6\u4e3a\u4e86\u987e\u5168\u81ea\u5df1\u7684\u58f0\u540d\uff0c\u662f\u4ee5\u4e8b\u60c5\u505a\u5f97\u6781\u4e3a\u9690 \u79d8\uff0c\u8981\u5bfb\u4ed6\u4fdd\u9556\u7684\u4eba\uff0c\u5148\u8981\u5bfb\u7740\u95e8\u8def\uff0c\u800c\u2018\u516b\u9762\u73b2\u73d1\u2019\u80e1\u4e4b\u8f89\uff0c\u90a3\u65f6\u4fbf\u8db3\u4e13\u95e8\u66ff\u4ed6\u8d1f\u8d23\u63a5\u6d3d \u751f\u610f\u7684\u5fc3\u8179\uff0c\u5230\u540e\u6765\u6bdb\u81ec\u7684\u88f3\u7fbd\u65e5\u4f17\uff0c\u4ed6\u81ea\u5df1\u4fbf\u6781\u5c11\u51fa\u624b\u3002\u201d \u3000\u3000\u201c\u5f20\u4e00\u6876\u201d\u5fcd\u4e0d\u4f4f\u53c8\u81ea\u63d2\u53e3\u9053\uff1a\u201c\u60f3\u4e0d\u5230\uff0c\u60f3\u4e0d\u5230\uff0c\u540d\u9707\u6c5f\u6e56\u7684\u2018\u4e03\u5251\u4e09\u97ad\uff0c\u91cc\uff0c\u5c45\u7136 \u8fd8\u6709\u8fd9\u79cd\u4e0b\u4e09\u8def\u7684\u89d2\u8272\uff0c\u771f\u6bd4\u6211\u2019\u5f20\u4e00\u6876\u2019\u8fd8\u4e0d\u5982\u3002\u201d \u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\xb79\u53c87\xb7 \u3000\u3000\u201c\u4e03\u7a8d\u201d\u738b\u5e73\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u7075\u86c7\u6bdb\u81ec\u867d\u7136\u5e72\u4e86\u2018\u5730\u4e0b\u9556\u5c40\uff0c\u7684\u4e70\u5356\uff0c\u4f46\u2019\u4e03\u5251\u4e09 \u97ad\uff0c\u4e2d\uff0c\u8fd8\u6709\u6bd4\u4ed6\u66f4\u52a0\u53ef\u803b\u5341\u500d\u7684\u89d2\u8272\u3002\u201d \u3000\u3000\u201c\u5f20\u4e00\u6876\u201d\u8be7\u58f0\u95ee\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d \u3000\u3000\u738b\u5e73\u7f13\u7f13\u9053\uff1a\u201c\u4e03\u5251\u4e09\u97ad\u4e2d\uff0c\u5c45\u7136\u8fd8\u6709\u4eba\u5728\u6697\u4e2d\u4e13\u52ab\u2018\u5730\u4e0b\u9556\u5c40\u2019\u7684\u7ea2\u8d27\u3002\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u5251\u7709\u5fae\u8f69\uff0c\u76ee\u4e2d\u518d\u6b21\u98d8\u8fc7\u4e00\u4e1d\u5947\u5f02\u7684\u5149\u8292\uff0c\u622a\u53e3\u9053\uff1a\u201c\u9e33\u9e2f\u53cc\u5251\uff1f\u201d \u3000\u3000\u738b\u5e73\u4f38\u624b\u4e00\u629a\u9881\u4e0b\u866c\u9aef\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5c31\u662f\u2018\u9e33\u9e2f\u53cc\u5251\uff0c\u3002\u201d\u201c\u5f20\u4e00\u6876\u201d\u60ca\u559f\u4e00\u58f0\uff0c \u201c\u4e03\u7a8d\u201d\u738b\u5e73\u63a5\u53e3\u53c8\u9053\uff1a\u201c\u5927\u7ea6\u5341\u516b\u5e74\u524d\uff0c\u2018\u7075\u86c7\u2019\u6bdb\u81ec\u7684\u2018\u5730\u4e0b\u9556\u5c40\uff0c\u63a5\u4e86\u4e00\u8d9f\u7ea2\u8d27\uff0c\u81ea \u5317\u4eac\u5230\u676d\u5dde\uff0c\u90a3\u65f6\u2019\u7075\u86c7\u2019\u6bdb\u81ec\u4e0d\u5728\u5bb6\u4e2d\uff0c\u8fd9\u8d9f\u751f\u610f\u4e43\u662f\u80e1\u4e4b\u8f89\u63a5\u7684\uff0c\u5374\u7531\u4e00\u4e2a\u53eb\u2018\u95ea\u7535\u795e \u5200\uff0c\u6731\u5b50\u660e\u7684\u2019\u5730\u4e0b\u9556\u5ba2\uff0c\u62bc\u8fd0\u3002\u201d \u3000\u3000\u4ed6\u773c\u5e18\u4e00\u5408\uff0c\u4f3c\u4e4e\u5728\u8111\u4e2d\u5c06\u8a00\u8bed\u6574\u7406\u4e86\u4e00\u4e0b\uff0c\u7136\u540e\u9053\uff1a\u201c\u8fd9\u8d9f\u9556\u62bc\u5230\u676d\u5dde\u57ce\u5916\u7684\u65f6\u5019\uff0c \u6b63\u662f\u6df1\u591c\uff0c\u800c\u4e14\u8fd8\u4e0b\u7740\u503e\u76c6\u5927\u96e8\u3002\u201d\u8bed\u58f0\u5fae\u987f\uff1a\u201c\u62bc\u8fd0\u6697\u9556\u7684\u4fdd\u4eba\uff0c\u591a\u662f\u663c\u4f0f\u591c\u51fa\u7684\u3002\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u9894\u9996\u9053\uff1a\u201c\u8bf4\u4e0b\u53bb\uff01\u201d \u3000\u3000\u738b\u5e73\u9053\uff1a\u201c\u62bc\u9556\u7684\u4eba\uff0c\u9664\u4e86\u95ea\u7535\u795e\u5200\u6731\u5b50\u660e\u5916\uff0c\u5c31\u53ea\u6709\u4e24\u4e2a\u6c5f\u6e56\u4e0b\u4e94\u95e8\u7684\u5c0f\u8d3c\uff0c\u548c\u4e00\u4e2a \u6bdb\u81ec\u7684\u5bb6\u4e01\uff0c\u56db\u4e2a\u4eba\u90fd\u88c5\u505a\u666e\u901a\u5ba2\u5546\uff0c\u4e58\u7740\u4e00\u8f86\u5927\u8f66\uff0c\u90a3\u65f6\u65b9\u5230\u676d\u5dde\u57ce\u5916\uff0c\u5c31\u9047\u4e0a\u4e86\u4e13\u52ab\u6697 \u9556\u7684\u2018\u9e33\u9e2f\u53cc\u5251\u2019\u4e2d\u7684\u7a0b\u67ab\uff0c\u7adf\u4e0b\u624b\u5c06\u8fd9\u8d9f\u6697\u9556\u52ab\u4e86\u3002\u201d \u3000\u3000\u201c\u5f20\u4e00\u6876\u201d\u5fcd\u4e0d\u4f4f\u53c8\u63d2\u53e3\u9053\uff1a\u201c\u90a3\u59d3\u7a0b\u7684\u600e\u4e48\u4f1a\u770b\u51fa\u8f66\u4e0a\u6709\u7ea2\u8d27\u5462\uff1f\u201d \u3000\u3000\u738b\u5e73\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4e8b\u7aef\u7684\u5947\u602a\u5f97\u5f88\uff0c\u82e5\u5728\u6674\u5929\uff0c\u6c5f\u6e56\u8001\u624b\u53ef\u4ece\u8f66\u8f6e\u5e26\u8d77\u7684\u5c18\u571f\uff0c \u5224\u65ad\u8f66\u4e0a\u6709\u65e0\u7ea2\u8d27\uff0c\u53ef\u662f\u90a3\u591c\u6b63\u4e0b\u7740\u5927\u96e8\uff0c\u7a0b\u67ab\u5982\u4f55\u4f1a\u77e5\u9053\u7684\uff0c\u5374\u662f\u4ef6\u7591\u6848\u3002\u636e\u6211\u731c\u6d4b\uff0c\u7a0b \u67ab\u90a3\u591c\u60f3\u5fc5\u4e5f\u662f\u5728\u641c\u5bfb\u7740\u4ec0\u4e48\uff0c\u662f\u4ee5\u89c1\u5230\u6df1\u591c\u4e2d\u8fd8\u6709\u8f66\u9a76\u6765\uff0c\u5c31\u5c06\u5b83\u62e6\u4e0b\u67e5\u770b\uff0c\u800c\u90a3\u2018\u95ea\u7535 \u795e\u5200\uff0c\u5b9a\u5fc5\u4ee5\u4e3a\u662f\u52ab\u9556\u7684\u6765\u4e86\uff0c\u662f\u4ee5\u4fbf\u5148\u51fa\u4e86\u624b\uff0c\u8fd9\u4e48\u4e00\u6765\uff0c\u9634\u9519\u9633\u5dee\uff0c\u5374\u8ba9\u7a0b\u67ab\u5728\u65e0\u610f\u4e4b \u4e2d\u5f97\u4e86\u4e00\u7b14\u5916\u5feb\u3002\u201d\u201c\u7f2a\u6587\u201d\u5fae\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u6b63\u662f\u5982\u6b64\uff01\u201d \u3000\u3000\u5fc3\u4e2d\u5374\u4e0d\u7981\u4e3a\u4e4b\u79f0\u8d5e\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u738b\u5e73\u7aef\u7684\u5fc3\u601d\u7075\u5de7\uff0c\u5206\u6790\u4e8b\u7406\uff0c\u6709\u5982\u8ddf\u89c1\uff0c\u65e0\u602a\u522b\u4eba \u79f0\u4ed6\u5fc3\u6709\u4e03\u7a8d\u3002\u201d\u539f\u6765\u65b9\u624d\u7a0b\u67ab\u3001\u6797\u7433\u7684\u591c\u534a\u79c1\u8bed\uff0c\u4ed6\u5168\u90fd\u5728\u6697\u4e2d\u542c\u5230\u4e86\u3002 \u3000\u3000\u201c\u4e03\u7a8d\uff0c\u738b\u5e73\u5446\u4e86\u4e00\u5446\uff0c\u4e0d\u77e5\u9053\u8fd9\u4f4d\u201d\u4ec7\u516c\u5b50\uff09\u600e\u4f1a\u5bf9\u81ea\u5df1\u7684\u731c\u6d4b\u5982\u6b64\u80af\u5b9a\u7684\u7b54\u590d\uff0c\u4f46 \u53e3\u4e2d\u5374\u81ea\u63a5\u9053\uff1a\u201c\u95ea\u7535\u795e\u5200\u52a8\u624b\u4e4b\u4e0b\uff0c\u600e\u4f1a\u662f\u4ee5\u5251\u672f\u540d\u626c\u6b66\u6797\u7684\u7a0b\u67ab\u654c\u624b\uff0c\u62bc\u8fd0\u8fd9\u8d9f\u6697\u9556 \u7684\uff0c\u9664\u4ed6\u4e4b\u5916\uff0c\u66f4\u65e0\u597d\u624b\uff0c\u81ea\u7136\u5168\u90fd\u88ab\u7a0b\u67ab\u4f24\u5728\u5251\u4e0b\u3002\u7a0b\u67ab\u52ab\u4e86\u8fd9\u7b14\u7ea2\u8d27\uff0c\u6ee1\u6000\u9ad8\u5174\uff0c\u4f46\u7b49 \u5230\u4ed6\u5c06\u7ea2\u8d27\u7bb1\u5b50\u6253\u5f00\u4e00\u770b\uff0c\u91cc\u9762\u7adf\u662f\u6709\u4e00\u5c01\u80e1\u4e4b\u8f89\u5199\u7ed9\u6bdb\u81ec\u7684\u79c1\u51fd\uff0c\u4ed6\u8fd9\u624d\u77e5\u9053\u539f\u6765\u8fd9\u6279\u7ea2 \u8d27\u7adf\u662f\u6bdb\u81ec\u4fdd\u7684\uff0c\u4e5f\u624d\u77e5\u9053\u201d\u7075\u86c7\uff0c\u6bdb\u81ec\u539f\u6765\u4e5f\u5728\u505a\u2018\u5730\u4e0b\u9556\u5c40\uff0c\u7684\u4e70\u5356\uff0c\u90a3\u65f6\u60f3\u5fc5\u4ed6\u4e00\u5b9a \u53c8\u60ca\u53c8\u60e7\uff0c\u751f\u6015\u6bdb\u81ec\u77e5\u9053\u4e86\u771f\u8c61\uff0c\u4f1a\u6765\u627e\u4ed6\u5bfb\u4ec7\uff0c\u662f\u4ee5\u4ed6\u4fbf\u4e00\u76f4\u4e0d\u6562\u5c06\u6b64\u4e8b\u8bf4\u51fa\u3002\u201d\u8bf4\u5230\u8fd9 \u91cc\uff0c\u4ed6\u6b47\u4e86\u53e3\u6c14\uff0c\u53c8\u9053\uff1a\u201c\u4f46\u6bdb\u81ec\u5931\u9556\u4e4b\u540e\uff0c\u5374\u4e5f\u53ea\u5f97\u54d1\u5b50\u5403\u9ec4\u8fde\uff0c\u4e0d\u6562\u5c06\u6b64\u4e8b\u8bf4\u51fa\uff0c\u4e8e\u662f \u8fd9\u4ef6\u4e8b\u4fbf\u5728\u6b66\u6797\u4e2d\u6e6e\u6ca1\u4e86\u5341\u4e03\u3001\u516b\u5e74\uff0c\u76f4\u5230\u4eca\u5929\uff0c\u624d\u7b97\u88ab\u6211\u67e5\u51fa\u3002\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\u3002\u4ed6\u6d53\u7709\u4e00\u626c\uff0c\u9762\u4e0a\u4e5f\u4e0d\u7981\u9732\u51fa\u5f97\u610f\u4e4b\u8272\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u5fae\u559f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u738b\u541b\u7aef\u7684\u662f\u975e\u5e38\u4e4b\u4eba\uff0c\u7adf\u80fd\u5c06\u8fd9\u4ef6\u6e6e\u6ca1\u591a\u5e74\u7684\u7591\u6848\u6253\u63a2\u5f97\u5982 \u6b64\u8be6\u7ec6\u3002\u534a\u6708\u4e4b\u524d\uff0c\u6211\u66fe\u5728\u65e0\u610f\u4e4b\u95f4\u542c\u5f97\u4e00\u4eba\u8bf4\u8d77\u5341\u4f59\u5e74\u524d\u7684\u6b66\u6797\u4e2d\u4e8b\uff0c\u4e5f\u8c08\u8d77\u4e86\u201d\u5730\u4e0b\u9556 \u5c40\u2019\u4e0e\u8fd9\u4ef6\u7591\u6848\uff01\u201d\u4ed6\u76ee\u5149\u4e00\u95ea\uff0c\u63a5\u53e3\u53c8\u9053\uff1a\u201c\u5bf9\u4e8e\u90a3\u65f6\u7684\u6b66\u6797\u4e2d\u4e8b\uff0c\u6211\u90fd\u6781\u4e3a\u7559\u610f\uff0c\u662f\u4ee5 \u6211\u81ea\u5df1\u5148\u4e5f\u63a2\u67e5\u4e86\u4e00\u4e0b\uff0c\u67e5\u51fa\u6b64\u4e8b\u4eff\u4f5b\u4e0e\u2018\u9e33\u9e2f\u53cc\u5251\uff0c\u4e0e\u6bdb\u81ec\u6709\u5173\uff0c\u662f\u4ee5\u6562\u8bf7\u6881\u5927\u54e5\u5c31\u4fbf\u518d \u63a2\u67e5\u4e00\u4e0b\uff0c\u4f46\u5374\u672a\u60f3\u5230\u4f60\u7adf\u80fd\u67e5\u5f97\u5982\u6b64\u8be6\u7ec6\uff01\u201d\u201c\u4e03\u7a8d\u201d\u738b\u5e73\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u516c\u5b50\u4e8b\u52a1\u70e6 \u5fd9\uff0c\u81ea\u7136\u4e0d\u4f1a\u6709\u65f6\u95f4\u53bb\u4ed4\u7ec6\u63a2\u67e5\uff0c\u4f46\u5c0f\u4eba\u5374\u7a7a\u95f2\u5f97\u5f88\u3002\u201d \u3000\u3000\u7a81\u5730\u8f6c\u8eab\u559d\u9053\uff1a\u201c\u6b64\u523b\u4f60\u53ef\u4ee5\u8fc7\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8f6c\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u95e8\u5916\u7f13\u7f13\u63a2\u8fdb\u4e00\u4e2a\u5934\u6765\uff0c\u56db\u4e0b\u5f20\u671b\u53c8\u4e24\u773c\uff0c\u624d\u754f\u7f29 \u5730\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u53ea\u89c1\u6b64\u4eba\u8eab\u8eaf\u4e5f\u9887\u4e3a\u9ad8\u5927\uff0c\u4f46\u795e\u6001\u5374\u72fc\u72c8\u4e0d\u582a\u3002\u76ee\u5149\u5982\u9f20\uff0c\u56db\u4e0b\u8f6c\u52a8\uff0c\u50cf\u662f\u5bf9\u4e16\u4e0a\u4efb\u4f55 \u4e00\u4ef6\u4e8b\u3001\u4efb\u4f55\u4e00\u4e2a\u4eba\u90fd\u6709\u754f\u60e7\u4e4b\u5fc3\uff0c\u4f46\u4e00\u773c\u671b\u89c1\u4e86\u623f\u4e2d\u73cd\u8d35\u7684\u6446\u8bbe\uff0c\u773c\u73e0\u7acb\u523b\u7741\u5f97\u6eda\u5706\uff0c\u707c \u707c\u5730\u5c04\u51fa\u8d2a\u5a6a\u7684\u5149\u8292\uff0c\u6004\u507b\u7684\u8eab\u5f62\uff0c\u4e5f\u7acb\u65f6\u50cf\u662f\u7ad9\u76f4\u4e86\u4e0d\u5c11\uff0c\u5782\u5728\u819d\u8fb9\u7684\u53cc\u624b\uff0c\u5374\u8d77\u4e86\u4e00\u9635 \u8f7b\u5fae\u7684\u98a4\u6296\u3002 \u3000\u3000\u201c\u4e03\u7a8d\u201d\u738b\u5e73\u7f13\u7f13\u9053\uff1a\u201c\u6b64\u4e8b\u7684\u524d\u9762\u4e00\u6bb5\uff0c\u6bdb\u81ec\u867d\u7136\u505a\u5f97\u9690\u79d8\uff0c\u4f46\u6c5f\u6e56\u4e2d\u6bd5\u7adf\u8fd8\u6709\u4eba\u77e5 \u9053\uff0c\u662f\u4ee5\u6211\u4e0d\u96be\u63a2\u67e5\uff0c\u4f46\u6b64\u4e8b\u7684\u540e\u534a\u6bb5\uff0c\u82e5\u975e\u6b64\u4eba\uff0c\u6211\u5374\u6c38\u8fdc\u4e5f\u63a2\u67e5\u4e0d\u51fa\uff01\u201d\u7f2a\u6587\u5251\u7709\u5fae \u76b1\uff0c\u6c89\u7709\u95ee\u9053\uff1a\u201c\u6b64\u4eba\u662f\u8c01\uff1f\u4e0e\u6b64\u4e8b\u53c8\u6709\u4f55\u5173\u7cfb\uff1f\u201d \u3000\u3000\u201c\u4e03\u7a8d\u201d\u738b\u5e73\u4e00\u7b11\u9053\uff1a\u201c\u6b64\u4eba\u65e0\u540d\u65e0\u59d3\uff0c\u5374\u6709\u4e2a\u5916\u53f7\u53eb\u505a\u2018\u4e09\u53ea\u624b\u2019\uff0c\u987e\u540d\u601d\u4e49\uff0c\u81ea\u7136 \u5e72\u7684\u662f\u6252\u7a83\u7684\u52fe\u5f53\uff0c\u5e38\u8a00\u9053\uff1a\u2018\u5077\u98ce\u4e0d\u5077\u6708\uff0c\u5077\u96e8\u4e0d\u5077\u96ea\u3002\u2019\u6b64\u4e8b\u53d1\u751f\u7684\u90a3\u5929\u665a\u4e0a\uff0c\u5927\u96e8\u6ec2 \u6ca6\uff0c\u81ea\u7136\u6b63\u662f\u6b64\u8f88\u4eba\u7269\u6d3b\u8dc3\u7684\u65f6\u5019\u3002\u51d1\u5de7\u7684\u662f\uff0c\u4ed6\u90a3\u591c\u7adf\u8d70\u4e86\u9709\u8fd0\uff0c\u5728\u9635\u4e0a\u5931\u4e86\u98ce\uff0c\u4ed6\u62fc\u547d \u9003\u51fa\u57ce\u5916\uff0c\u5c06\u540e\u9762\u8ffd\u6765\u7684\u4eba\u629b\u6389\uff0c\u5374\u6b63\u597d\u9047\u7740\u4e86\u6b64\u4e8b\u3002\u201d \u3000\u3000\u56de\u987e\u4e00\u773c\uff0c\u53f1\u9053\uff1a\u201c\u4f60\u4e14\u5c06\u5f53\u65f6\u60c5\u51b5\u8bf4\u7ed9\u8fd9\u4f4d\u516c\u5b50\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u4e09\u53ea\u624b\u201d\u8d76\u7d27\u8eac\u8eab\u5e94\u4e86\u4e00\u58f0\uff0c\u5982\u9f20\u7684\u76ee\u5149\uff0c\u95ea\u95ea\u7f29\u7f29\uff0c\u5982\u5154\u7684\u5634\u5507\uff0c\u671f\u671f\u827e\u827e\uff0c\u201c\u7f2a \u6587\u201d\u5fae\u4e00\u76b1\u7709\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4f60\u5feb\u4e9b\u8bf4\u51fa\uff0c\u5fc5\u6709\u91cd\u8d4f\u3002\u201d \u3000\u3000\u201c\u4e09\u53ea\u624b\u201d\u66f4\u5feb\u5730\u8eac\u8eab\u5e94\u4e86\u4e00\u58f0\uff0c\u53e3\u4e2d\u9053\uff1a\u201c\u5c0f\u7684\u90a3\u5929\u62fc\u547d\u8dd1\u51fa\u57ce\uff0c\u624d\u6b47\u4e86\u53e3\u6c14\uff0c\u5ffd\u7136 \u89c1\u5230\u524d\u9762\u6709\u4eba\u63d0\u7740\u67c4\u5251\uff0c\u8fd8\u6709\u4e00\u8f86\u9a6c\u8f66\uff0c\u5c0f\u7684\u5927\u9a87\u4e4b\u4e0b\uff0c\u4e5f\u987e\u4e0d\u5f97\u518d\u770b\uff0c\u5c31\u8eb2\u5728\u8def\u8fb9\u7684\u7a3b\u8349 \u91cc\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u53ea\u542c\u89c1\u5916\u9762\u6709\u4eba\u8bf4\uff1a\u2018\u7a0b\u67ab\u4f60\u600e\u5730\u4e0d\u8bb2\u60c5\u9762\uff0c\u96be\u9053\u4f60\u4e0d\u77e5\u9053\u8fd9\u8d9f\u9556\u2026\u2026\u2019\u8bdd \u672a\u8bf4\u5b8c\uff0c\u5c31\u6709\u53e6\u4e00\u4eba\u54c8\u54c8\u7b11\u9053\uff1a\u2018\u8fd9\u8d9f\u9556\u7eb5\u662f\u4f60\u2019\u95ea\u7535\u795e\u5200\u2019\u4fdd\u7684\uff0c\u4eca\u65e5\u6211\u7a0b\u67ab\u4e5f\u8981\u52a8\u4e0a\u4e00 \u52a8\u3002\uff0c\u63a5\u7740\u5c31\u662f\u4e00\u9635\u5175\u5203\u76f8\u4ea4\uff0c\u53ee\u53ee\u94db\u94db\u7684\u58f0\u97f3\u3002\u201d \u3000\u3000\u201c\u6211\u5fcd\u4e0d\u4f4f\u4f38\u51fa\u5934\u8981\u53bb\u770b\uff0c\u54ea\u77e5\u6211\u5934\u8fd8\u6ca1\u6709\u4f38\u51fa\uff0c\u5c31\u542c\u5f97\u4e00\u58f0\u60e8\u547c\uff0c\u8fd9\u58f0\u60e8\u547c\u7684\u58f0\u97f3\u8fd8 \u672a\u5b8c\uff0c\u53c8\u662f\u4e00\u58f0\u60e8\u547c\uff0c\u8fd9\u6837\u4e00\u4e00\u58f0\u63a5\u7740\u4e00\u58f0\uff0c\u4e00\u201d\u5239\u90a3\u91cc\uff0c\u7adf\u63a5\u7740\u6709\u56db\u58f0\u60e8\u547c\uff0c\u5413\u5f97\u6211\u8fde\u5fd9 \u53c8\u7f29\u8fdb\u5934\u53bb\u3002\u201d \u3000\u3000\u201c\u56db\u58f0\u60e8\u547c\u8fc7\u540e\uff0c\u5c31\u518d\u6ca1\u6709\u4e00\u70b9\u58f0\u97f3\uff0c\u53ea\u6709\u5927\u96e8\u54d7\u5566\u54d7\u5566\u5730\u4e0b\u7740\uff0c\u6dcb\u5f97\u6211\u6d51\u8eab\u53d1\u75db\uff0c\u6211 \u6084\u6084\u4e00\u6478\u5934\u989d\uff0c\u6ee1\u5934\u662f\u6c34\uff0c\u5374\u53ea\u6709\u4e00\u534a\u662f\u96e8\u6c34\uff0c\u8fd8\u6709\u4e00\u534a\u662f\u51b7\u6c57\uff0c\u4e5f\u987e\u4e0d\u5f97\u518d\u770b\u4e86\uff0c\u5c31\u4ece\u7a3b \u8349\u91cc\u722c\u5230\u53e6\u4e00\u5934\uff0c\u6084\u6084\u8dd1\u4e86\u51fa\u53bb\uff0c\u5927\u96e8\u6253\u5728\u7530\u5730\u4e0a\uff0c\u5c31\u50cf\u662f\u6709\u4eba\u5728\u91cc\u9762\u8ffd\u7740\u6211\u4f3c\u7684\u3002\u201d \u3000\u3000\u4ed6\u82e6\u82e6\u53f9\u4e86\u53e3\u6c14\uff0c\u53c8\u9053\uff1a\u201c\u8fd9\u4e00\u665a\u4e0a\u6211\u4e0d\u4f46\u6ca1\u6709\u4e00\u4e1d\u6536\u83b7\uff0c\u800c\u4e14\u8fde\u60ca\u5e26\u6015\uff0c\u518d\u52a0\u4e0a\u6dcb\u4e86 \u96e8\uff0c\u56de\u53bb\u540e\u8db3\u8db3\u75c5\u4e86\u534a\u4e2a\u591a\u6708\uff0c\u624d\u597d\u4e00\u2026\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u51b7\u53f1\u4e00\u58f0\uff1a\u201c\u591f\u4e86\uff01\u201d \u3000\u3000\u968f\u624b\u629b\u4e86\u4e00\u952d\u94f6\u5b50\uff0c\u629b\u5728\u4ed6\u9762\u524d\u7684\u5730\u4e0a\uff0c\u51b7\u51b7\u53c8\u9053\uff1a\u201c\u94f6\u5b50\u62ff\u53bb\uff0c\u5c11\u8bf4\u5e9f\u8bdd\uff0c\u82e5\u5c06\u4eca\u591c \u4e4b\u4e8b\u6cc4\u51fa\u4e00\u5b57\uff0c\u5fc5\u5b9a\u53d6\u4f60\u6027\u547d\u3002\u201d \u3000\u3000\u201c\u4e09\u53ea\u624b\u201d\u8bfa\u8bfa\u8fde\u58f0\uff0c\u773c\u775b\u5374\u77ac\u4e5f\u4e0d\u77ac\u5730\u671b\u7740\u5730\u4e0a\u7684\u94f6\u5b50\uff0c\u4e8e\u662f\u4ed6\u7684\u4e00\u53cc\u9f20\u76ee\uff0c\u53c8\u6709\u4e86 \u4e00\u4e9b\u5149\u4eae\u3002 \u3000\u3000\u201c\u4e03\u7a8d\u201d\u738b\u5e73\u51b7\u53f1\u4e00\u58f0\uff1a\u201c\u8fd8\u4e0d\u5feb\u6eda\uff0c\u8bf7\u5e26\u4f60\u8fdb\u6765\u7684\u90a3\u4f4d\u7ba1\u5bb6\u5e26\u4f60\u51fa\u53bb\uff0c\u4e0d\u5f97\u5728\u5609\u5174\u57ce \u518d\u505c\u7559\u4e00\u65f6\u534a\u523b\uff0c\u542c\u5f97\u4e86\u4e48\uff01\u201d \u3000\u3000\u201c\u4e09\u53ea\u624b\u201d\u7a81\u5730\u98de\u5feb\u5730\u4f38\u51fa\u624b\u638c\uff0c\u652b\u4e86\u5730\u4e0a\u7684\u94f6\u5b50\uff0c\u53e3\u4e2d\u8bfa\u8bfa\u8fde\u58f0\uff0c\u811a\u4e0b\u8fde\u9000\u6570\u6b65\uff0c\u5012 \u9000\u7740\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u76f4\u7b49\u4ed6\u8eab\u5f71\u6d88\u5931\uff0c\u65b9\u81ea\u53f9\u9053\uff1a\u201c\u5929\u7f51\u6062\u6062\uff0c\u758f\u800c\u4e0d\u6f0f\uff0c\u90a3\u7a0b\u67ab\u53ef\u600e\u4f1a\u6599\u5230\u6b64\u4eba \u5728\u6697\u4e2d\u2014\u2014\u201d\u7a81\u5730\u4e00\u62cd\u77f3\u6848\uff0c\u5411\u201c\u5f20\u4e00\u6876\u201d\u5927\u58f0\u9053\uff1a\u201c\u4f60\u5feb\u4e9b\u5c06\u4ed6\u8feb\u56de\uff0c\u5b89\u7f6e\u5728\u540e\u9762\u7684\u9a6c\u623f \u91cc\uff0c\u547d\u4ed6\u4e0d\u5f97\u51fa\u6765\u4e00\u6b65\u3002\u201d \u3000\u3000\u201c\u5f20\u4e00\u6876\u201d\u5446\u4e86\u4e00\u5446\uff0c\u5e94\u547d\u53bb\u4e86\u3002\u201c\u4e03\u7a8d\u201d\u738b\u5e73\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u516c\u5b50\u53ef\u662f\u8fd8\u8981\u7559\u4ed6\u65e5\u540e\u505a \u4e2a\u4eba\u8bc1\u4e48\u8c03\u201d\u7f2a\u6587\u201d\u94f6\u7259\u4e00\u7b11\uff0c\u201c\u4e03\u7a8d\u201d\u738b\u5e73\u5fcd\u4e0d\u4f4f\u53c8\u9053\uff1a\u201c\u516c\u5b50\u600e\u4f1a\u67e5\u51fa\u6b64\u4e8b\u4e0e\u6bdb\u81ec\u3001\u7a0b \u67ab\u6709\u5173\u7684\uff0c\u5c0f\u4eba\u5b9e\u5728\u731c\u4e0d\u5230\uff0c\u96be\u9053\u6b64\u4e8b\u9664\u4e86\u8fd9\u2018\u4e09\u53ea\u624b\u2019\u5916\uff0c\u8fd8\u6709\u4ec0\u4e48\u4eba\u77e5\u9053\u4e48\uff1f\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u5fae\u7b11\u4e0d\u8bed\uff0c\u7a81\u5730\u4f38\u51fa\u624b\u638c\uff0c\u8f7b\u8f7b\u62c9\u4e86\u62c9\u96d5\u87ad\u6848\u8fb9\u7684\u4e00\u6839\u4e1d\u7ee6\u3002 \u3000\u3000\u53ea\u542c\u201c\u53ee\u94db\u201d\u4e00\u9635\u94c3\u54cd\u2014\u2014\u94c3\u58f0\u672a\u7edd\uff0c\u95e8\u5916\u5df2\u8d70\u5165\u4e00\u4e2a\u9762\u5bb9\u6728\u7136\uff0c\u8eab\u5f62\u4ea6\u6728\u7136\uff0c\u4e00\u773c\u671b \u53bb\uff0c\u6709\u5982\u884c\u5c38\u8d70\u8089\u4e00\u822c\u7684\u4eba\u6765\u3002 \u3000\u3000\u6b64\u523b\u7a97\u5916\u5df2\u73b0\u66d9\u8272\uff0c\u66d9\u8272\u4e0e\u706f\u5149\u6df7\u5408\uff0c\u6620\u7740\u6b64\u4eba\u9762\u4e0a\u4e00\u9053\u957f\u8fbe\u4e94\u5bf8\u7684\u5200\u75d5\uff0c\u9690\u9690\u6cdb\u51fa\u7ea2 \u5149\u3002 \u3000\u3000\u5929\u8272\u5927\u4eae\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u5bb9\u5149\u7115\u53d1\uff0c\u7cbe\u795e\u6296\u64de\u5730\u5c06\u201c\u9e33\u9e2f\u53cc\u5251\u201d\u592b\u5987\u4e24\u4eba\u8fce\u81f3\u504f\u5385\uff0c\u8c01\u4e5f\u770b\u4e0d\u51fa\u4ed6\u6628\u591c \u7adf\u5f7b\u591c\u672a\u7720\u3002 \u3000\u3000\u5385\u5185\u53c8\u6446\u597d\u4e00\u5e2d\u7cbe\u81f4\u7684\u9152\u7b75\uff0c\u7a0b\u67ab\u7b11\u9053\uff1a\u201c\u6628\u591c\u5728\u4e0b\u5df2\u4e0d\u80dc\u9152\u529b\uff0c\u4eca\u65e5\u2014\u2014\u201d\u201c\u7f2a\u6587\u201d \u6717\u7b11\u63a5\u53e3\u9053\uff1a\u201c\u4ee5\u9152\u89e3\u9152\uff0c\u4eca\u6668\u5c0f\u53ef\u5b9a\u8981\u5949\u966a\u9601\u4e0b\u518d\u75db\u996e\u51e0\u676f\uff0c\u53ea\u53ef\u60dc\u9601\u4e0b\u6709\u8981\u4e8b\u5728\u8eab\uff0c\u4e0d \u80fd\u5728\u6b64\u591a\u76d8\u6853\u4e9b\u65e5\u5b50\uff0c\u4e0d\u7136\u5c0f\u53ef\u5b9a\u8981\u7559\u541b\u5728\u6b64\u4f5c\u5341\u65e5\u4e4b\u996e\u3002\u201d \u3000\u3000\u7a0b\u67ab\u4e00\u7b11\u5c31\u5750\uff0c\u5374\u89c1\u201c\u7f2a\u6587\u201d\u53cc\u638c\u4e00\u62cd\uff0c\u9053\uff1a\u201c\u9152\u6765\u3002\u201d \u3000\u3000\u5239\u90a3\u4e4b\u95f4\uff0c\u4fbf\u6709\u4e00\u4eba\u81ea\u8eab\u540e\u4e3a\u7a0b\u67ab\u659f\u6ee1\u4e86\u676f\u4e2d\u4e4b\u9152\u3002 \u3000\u3000\u7a0b\u67ab\u81ea\u7136\u4e0d\u4f1a\u56de\u5934\u77a7\u770b\uff0c\u53ea\u89c9\u8fd9\u53ea\u659f\u9152\u7684\u624b\u638c\uff0c\u751a\u662f\u7a33\u5b9a\uff0c\u6070\u5de7\u659f\u6ee1\u4e86\u4ed6\u7684\u9152\u676f\uff0c\u4e00\u6ef4 \u4e0d\u591a\uff0c\u4e00\u6ef4\u4e0d\u5c11\uff0c\u5fae\u5e26\u7425\u73c0\u989c\u8272\u7684\u9187\u6d53\u4f73\u917f\uff0c\u5728\u676f\u9762\u4e0a\u5fae\u5fae\u5f13\u8d77\u4e00\u4e9b\uff0c\u53ea\u8981\u518d\u591a\u4e00\u6ef4\uff0c\u4fbf\u5f97 \u6ea2\u51fa\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u542b\u7b11\u9053\uff1a\u201c\u6628\u591c\u90a3\u4ec6\u4eba\u592a\u8fc7\u591a\u5634\uff0c\u4eca\u6668\u5c0f\u53ef\u5df2\u6362\u4e86\u4e00\u4e2a\u3002\u6b64\u4eba\u795e\u5fd7\u5df2\u5168\u90fd\u9ebb \u6728\uff0c\u4fbf\u662f\u5728\u4ed6\u8eab\u4e0a\u6233\u4e0a\u4e00\u5200\uff0c\u4ed6\u4e5f\u4e0d\u4f1a\u89c9\u5f97\u75db\u7684\uff0c\u4f46\u5374\u6709\u4e00\u4e2a\u597d\u5904\uff0c\u4e3b\u4eba\u6709\u547d\uff0c\u4fbf\u662f\u5524\u4ed6\u53bb \u6b7b\uff0c\u4ed6\u4e5f\u4e0d\u4f1a\u8fdf\u7591\uff0c\u5c0f\u53ef\u6709\u4e86\u8fd9\u7b49\u4ec6\u4eba\uff0c\u5b9e\u662f\u5fc3\u6ee1\u610f\u8db3\u3002\u201d \u3000\u3000\u7a0b\u67ab\u6f2b\u4e0d\u7ecf\u5fc3\u968f\u53e3\u6577\u884d\u4e86\u4e24\u53e5\uff0c\u5fc3\u4e2d\u5374\u6709\u4e9b\u5947\u602a\uff1a\u201c\u6b64\u4eba\u81ea\u5df1\u8db3\u4ee5\u505a\u89c6\u4eba\u95f4\u7684\u540d\u5668\u3001\u73cd \u5b9d\uff0c\u4ece\u4e0d\u89c1\u4ed6\u8bf4\u51fa\u4e00\u53e5\u534a\u53e5\u81ea\u5f97\u81ea\u6ee1\u4e4b\u8a00\uff0c\u6b64\u523b\u5ffd\u5730\u4f1a\u5bf9\u4e00\u4e2a\u4ec6\u4eba\u5982\u6b64\u5938\u5956\uff1f\u201d \u3000\u3000\u62ac\u76ee\u671b\u5904\uff0c\u5ffd\u89c1\u6797\u7433\u76ee\u5149\u76f4\u5306\u5306\u5730\u671b\u7740\u81ea\u5df1\u8eab\u540e\uff0c\u751f\u50cf\u662f\u89c1\u4e86\u4ec0\u4e48\u8db3\u4ee5\u4f7f\u5979\u60ca\u8bb6\u5947\u602a\u7684 \u4e8b\u4f3c\u7684\u3002 \u3000\u3000\u7a0b\u67ab\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4e0d\u7531\u81ea\u4e3b\u5730\u8f6c\u8eab\u671b\u53bb\uff0c\u53ea\u89c1\u4e24\u9053\u5176\u5bd2\u5982\u51b0\u7684\u76ee\u5149\uff0c\u7adf\u5728\u76f4\u52fe\u52fe\u5730\u671b\u7740\u81ea \u5df1\u3002 \u3000\u3000\u8fd9\u76ee\u5149\u4f3c\u66fe\u76f8\u8bc6\uff0c\u4f46\u6b64\u4eba\u4ed6\u5374\u4ece\u672a\u89c1\u8fc7\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u4ed6\u5fc3\u4e2d\u65e2\u60ca\u53c8\u5947\uff0c\u53ea\u89c1\u6b64\u4eba\u7f13\u7f13\u8d70 \u5230\u6797\u7433\u80cc\u540e\uff0c\u7f13\u7f13\u4f38\u51fa\u638c\u4e2d\u94f6\u58f6\uff0c\u7f13\u7f13\u4e3a\u6797\u7433\u4e5f\u659f\u6ee1\u4e86\u9152\uff0c\u518d\u7f13\u7f13\u8d70\u5230\u7f2a\u6587\u8eab\u540e\u2026\u2026 \u3000\u3000\u7a0b\u67ab\u4e00\u751f\u8d70\u5357\u95ef\u5317\uff0c\u4e0d\u77e5\u89c1\u8fc7\u4e86\u591a\u5c11\u5947\u4eba\u5f02\u58eb\uff0c\u5374\u4ece\u672a\u89c1\u8fc7\u4e00\u4eba\u7684\u8eab\u5f62\u52a8\u4f5c\uff0c\u7adf\u6709\u5982\u884c \u5c38\u8d70\u8089\u4e00\u822c\uff0c\u8fdf\u7f13\u800c\u50f5\u6728\u3002 \u3000\u3000\u90a3\u8fb9\u201c\u7f2a\u6587\u201d\u5df2\u5728\u4e3e\u676f\u529d\u9152\uff0c\u4ed6\u5f3a\u7b11\u4e00\u4e0b\uff0c\u5c06\u676f\u4e2d\u4e4b\u9152\u4e00\u996e\u800c\u5c3d\uff0c\u653e\u4e0b\u9152\u676f\uff0c\u5374\u89c1\u90a3\u4e24 \u9053\u51b0\u51b7\u800c\u50f5\u6728\u7684\u76ee\u5149\uff0c\u7adf\u4ecd\u5728\u76f4\u52fe\u52fe\u5730\u671b\u7740\u81ea\u5df1\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd8\u9b42\uff0c\u8fd8\u4e0d\u5feb\u53bb\u4e3a\u5ba2\u4eba\u659f\u9152\u3002\u201d \u3000\u3000\u8fd9\u9ebb\u6728\u3001\u8fdf\u7f13\u3001\u534a\u75f4\u3001\u5947\u8be1\uff0c\u4f46\u5374\u6709\u4e00\u53cc\u51b0\u51b7\u7684\u76ee\u5149\u7684\u5947\u4ec6\uff0c\u540d\u5b57\u7adf\u7136\u53eb\u505a\u201c\u8fd8\u9b42\uff01\u201d \u7b2c\u4e8c\u5341\u7ae0>> \u53e4\u9f99\u300a\u6e58\u5983\u5251\u300b \u7b2c\u4e8c\u5341\u7ae0 \u3000\u3000\u53ea\u89c1\u201c\u8fd8\u9b42\u201d\u7f13\u7f13\u79fb\u52a8\u811a\u6b65\uff0c\u7ed5\u684c\u659f\u9152\uff0c\u4f46\u76ee\u5149\u5374\u8fde\u534a\u5206\u534a\u70b9\u4e5f\u6ca1\u6709\u79bb\u5f00\u8fc7\u7a0b\u67ab\u8eab\u4e0a\u3002 \u3000\u3000\u6b66\u6797\u4e2d\u4eba\uff0c\u9547\u9759\u529f\u592b\uff0c\u6700\u662f\u8981\u7d27\uff0c\u4f46\u6b64\u523b\u7a0b\u67ab\u5374\u4e0d\u7981\u5fc3\u5934\u7830\u7136\u8df3\u52a8\uff0c\u4ed6\u518d\u4e5f\u60f3\u4e0d\u51fa\u81ea\u5df1 \u5bf9\u8fd9\u53cc\u76ee\u5149\u4e3a\u4f55\u6709\u5982\u6b64\u719f\u6089\u7684\u611f\u89c9\u3002 \u3000\u3000\u6e05\u6668\u7684\u659c\u9633\uff0c\u6620\u5f97\u201c\u8fd8\u9b42\u201d\u9762\u4e0a\u7684\u5200\u75d5\u66f4\u7ea2\u3002 \u3000\u3000\u7a0b\u67ab\u54fc\u4e00\u58f0\uff0c\u5f3a\u7b11\u9053\uff1a\u201c\u8fd8\u9b42\uff0c\u8fd8\u2014\u2014\u54b3\u54b3\uff0c\u8fd9\u540d\u5b57\u5f53\u771f\u5947\u602a\u5f97\u5f88\u3002\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u4e00\u9762\u656c\u9152\u5e03\u83dc\uff0c\u4e00\u9762\u7b11\u9053\uff1a\u201c\u6b64\u4eba\u81ea\u8a00\u66fe\u7ecf\u6b7b\u8fc7\u4e00\u6b21\uff0c\u5374\u53c8\u8fd8\u8fc7\u9b42\u6765\uff0c\u662f\u4ee5\u5c0f \u5f1f\u4ee3\u4ed6\u53d6\u4e86\u2018\u8fd8\u9b42\u2019\u8fd9\u4e2a\u540d\u5b57\uff0c\u867d\u5acc\u4e0d\u96c5\uff0c\u5374\u4e5f\u4e0d\u4fd7\uff0c\u9601\u4e0b\u4ee5\u4e3a\u7136\u5426\uff1f\u201d \u3000\u3000\u7a0b\u67ab\u563f\u563f\u7b11\u9053\uff1a\u201c\u662f\u6781\uff0c\u5144\u6781\u2026\u2026\u201d \u3000\u3000\u4e3e\u8d77\u9152\u676f\uff0c\u4e00\u4ef0\u800c\u5c3d\uff0c\u5374\u6709\u51e0\u6ef4\u7425\u73c0\u8272\u7684\u9152\u73e0\uff0c\u81ea\u676f\u4e2d\u6e85\u51fa\uff0c\u6e85\u5728\u4ed6\u6de1\u7d20\u8272\u534e\u8d35\u7684\u8f7b\u7ef8 \u957f\u886b\u4e0a\u3002 \u3000\u3000\u4f46\u662f\u8fd9\u9152\u6e0d\u4fbf\u53d8\u6210\u7d2b\u8272\uff0c\u5c31\u5b9b\u5982\u7ecf\u4e45\u7684\u8840\u6e0d\u4e00\u6837\u3002 \u3000\u3000\u7a0b\u67ab\u773c\u6ce2\u6570\u8f6c\uff0c\u7a81\u5730\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u603b\u5acc\u6211\u5bb6\u7684\u4ec6\u4eba\u8fc7\u4e8e\u591a\u5634\uff0c\u6068\u4e0d\u5f97\u80fd\u627e\u5230\u4e00\u4f4d\u8fd9\u6837 \u7684\u7ba1\u5bb6\uff0c\u4f46\u627e\u6765\u627e\u53bb\uff0c\u603b\u662f\u627e\u4e0d\u5230\uff0c\u4e0d\u77e5\u9601\u4e0b\u5374\u662f\u5728\u54ea\u91cc\u627e\u5230\u7684\u8c03\u201d\u7f2a\u6587\u201d\u542b\u7b11\u9053\uff1a\u201c\u6b64\u4eba \u5e76\u975e\u5728\u4e0b\u5bfb\u6765\uff0c\u800c\u662f\u655d\u53cb\u5728\u5341\u516b\u5e74\u524d\u7684\u4e00\u4e2a\u5927\u96e8\u4e4b\u591c\uff0c\u81ea\u676d\u5dde\u57ce\u5916\uff0c\u6551\u56de\u6765\u7684\uff01\u201d \u3000\u3000\u4ed6\u6bcf\u8bf4\u4e00\u53e5\uff0c\u8bed\u6c14\u4fbf\u4e2d\u987f\u4e00\u4e0b\uff01 \u3000\u3000\u4ed6\u8bed\u6c14\u6bcf\u4e2d\u987f\u4e00\u4e0b\uff0c\u7a0b\u67ab\u7684\u9762\u8272\u4fbf\u968f\u4e4b\u4e00\u53d8\uff01 \u3000\u3000\u5239\u90a3\u4e4b\u95f4\uff0c\u7a0b\u67ab\u7684\u8111\u6d77\u4e4b\u4e2d\uff0c\u7a81\u5730\u5c55\u5f00\u4e00\u5e45\u56fe\u753b\uff0c\u4e00\u5e45\u8840\u6dcb\u6dcb\u7684\u56fe\u753b\u2026\u2026 \u3000\u3000\u4e24\u58f0\u60e8\u547c\u8fc7\uff0c\u4e00\u4eba\u8f6c\u8eab\u9003\u8d70\uff0c\u4e0d\u5f97\uff0c\u88ab\u6740\uff0c\u53e6\u4e00\u4eba\u633a\u80f8\u800c\u7acb\uff0c\u76ee\u5149\u51b0\u51b7\u800c\u50f5\u6728\u2026\u2026 \u3000\u3000\u5927\u96e8\u2026\u2026\u2026 \u3000\u3000\u53c8\u662f\u4e00\u58f0\u60e8\u547c\u2026\u2026 \u3000\u3000\u5288\u9762\u4e00\u5251\u2026\u2026 \u3000\u3000\u76ee\u5149\u51b0\u51b7\u800c\u50f5\u6728\u2026\u2026 \u3000\u3000\u9762\u4e0a\u5200\u75d5\u2026\u2026 \u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u7a81\u5730\u2014\u2014 \u3000\u3000\u201c\u94db\u201d\u5730\u4e00\u58f0\uff0c\u9152\u676f\u843d\u5730\uff0c\u7247\u7247\u7c89\u788e\uff01 \u3000\u3000\u201c\u7f2a\u6587\u201d\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u8fd8\u672a\u66fe\u996e\u9152\uff0c\u600e\u5730\u5df2\u5148\u9189\u4e86\uff1f\u201d \u3000\u3000\u7b11\u58f0\u4e00\u987f\uff0c\u559d\u9053\uff1a\u201c\u8fd8\u9b42\uff0c\u5feb\u5c06\u5730\u4e0a\u788e\u7247\u62fe\u8d77\uff01\u201d \u3000\u3000\u201c\u8fd8\u9b42\u201d\u7f13\u7f13\u653e\u4e0b\u94f6\u58f6\uff0c\u7f13\u7f13\u4fef\u4e0b\u8eab\u53bb\uff0c\u5730\u4e0b\u9152\u676f\u788e\u7247\uff0c\u5728\u9633\u5149\u4e2d\u95ea\u95ea\u53d1\u5149\uff0c\u4ed6\u4e00\u7247\u4e00 \u7247\u5730\u62fe\u4e86\u8d77\u6765\u76ee\u5149\u5374\u4ecd\u671b\u5728\u7a0b\u67ab\u8eab\u4e0a\u3002 \u3000\u3000\u7a0b\u67ab\u7684\u76ee\u5149\uff0c\u4e5f\u5728\u671b\u7740\u4ed6\u3002 \u3000\u3000\u4e24\u4eba\u76ee\u5149\u76f8\u5bf9\uff0c\u7a0b\u67ab\u9762\u4e0a\u6740\u673a\u7a81\u73b0\uff0c\u7f13\u7f13\u81ea\u684c\u4e0b\u4f38\u51fa\u624b\u638c\uff0c\u9a88\u6307\u5982\u5251\uff0c\u5411\u4ed6\u5927\u6a2a\u808b\u5916\u7684 \u201c\u7ae0\u95e8\u201d\u5927\u7a74\u70b9\u53bb\u3002 \u3000\u3000\u5239\u90a3\u4e4b\u95f4\uff0c\u7a0b\u67ab\u7684\u624b\u6307\uff0c\u4fbf\u5df2\u89e6\u7740\u4ed6\u8863\u886b\uff0c\u53ea\u8981\u5f80\u524d\u8f7b\u8f7b\u4e00\u70b9\uff0c\u6b64\u4eba\u7684\u6027\u547d\uff0c\u4fbf\u8981\u4e27\u5728 \u7a0b\u67ab\u7684\u6307\u4e0b\u3002 \u3000\u3000\u7f2a\u6587\u7a81\u5730\u5927\u7b11\u9053\uff1a\u201c\u996e\u9152\u6700\u5fcc\u7a7a\u8179\uff0c\u9601\u4e0b\u600e\u5730\u4e0d\u5403\u4e9b\u4e1c\u897f\uff0c\u8fd9\u5757\u9e21\u808b\u98df\u4e4b\u867d\u65e0\u5473\uff0c\u5f03\u4e4b \u5374\u53c8\u5acc\u592a\u53ef\u60dc\u5462\uff01\u201d \u3000\u3000\u7a0b\u67ab\u624b\u6307\u65b9\u81ea\u89e6\u7740\u201c\u8fd8\u9b42\u201d\u7684\u8863\u886b\uff0c\u7f2a\u6587\u7684\u4e00\u5757\u9e21\u808b\u5df2\u9001\u5230\u4ed6\u9762\u524d\uff0c\u7adf\u79bb\u4ed6\u9f3b\u7aef\u7684\u201c\u95fb \u9999\u201d\u7a74\u4e0a\uff0c\u4e0d\u5230\u4e03\u5bf8\uff01 \u3000\u3000\u4ed6\u82e5\u4e0d\u4f38\u624b\u53bb\u63a5\uff0c\u8fd9\u53cc\u7259\u7b77\u751f\u50cf\u5df2\u8981\u70b9\u5728\u4ed6\u201c\u95fb\u9999\u201d\u7a74\u4e0a\uff0c\u5176\u90e8\u4f4d\u65f6\u95f4\u62ff\u634f\u4e4b\u5999\uff0c\u7adf\u662f \u65e0\u4e0e\u4f26\u6bd4\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u53ea\u5f97\u4ece\u684c\u4e0b\u62ac\u8d77\u624b\u638c\uff0c\u7aef\u8d77\u94f6\u789f\uff0c\u63a5\u4e86\u8fc7\u6765\uff0c\u800c\u6b64\u523b\u201c\u8fd8\u9b42\u201d\u5374\u5df2\u7f13\u7f13\u957f\u8eab\u800c \u8d77\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u82e5\u65e0\u5176\u4e8b\u5730\u6536\u56de\u7259\u7b77\uff0c\u7a0b\u67ab\u5fc3\u4e2d\u5374\u53c8\u4e0d\u7981\u5927\u4e3a\u60ca\u7591\uff0c\u4e0d\u77e5\u4ed6\u65b9\u624d\u90a3\u4e00\u624b\u7a76\u7adf\u662f \u6709\u610f\uff0c\u8fd8\u662f\u65e0\u610f\u3002 \u3000\u3000\u9152\u8fc7\u4e09\u5de1\uff0c\u7a0b\u67ab\u5df2\u662f\u98df\u800c\u4e0d\u77e5\u5176\u5473\uff0c\u201c\u7f2a\u6587\u201d\u5374\u4ecd\u6ee1\u9762\u7b11\u5bb9\uff0c\u201c\u8fd8\u9b42\u201d\u7684\u76ee\u5149\u4ecd\u7136\u50f5\u6728 \u800c\u51b0\u51b7\uff01 \u3000\u3000\u6797\u7433\u4f38\u624b\u4e00\u629a\u9b13\u8fb9\u4e71\u53d1\uff0c\u9053\uff1a\u201c\u4e3b\u4eba\u6177\u6168\uff0c\u5ba2\u4eba\u5c3d\u6b22\uff0c\u6b64\u523b\u9152\u8db3\u996d\u9971\uff0c\u6211\u4eec\u4e5f\u8be5\u8d70\u4e86 \u5427\uff01\u201d \u3000\u3000\u7a0b\u67ab\u9053\uff1a\u201c\u6b63\u662f\uff0c\u6b63\u662f\uff0c\u6211\u4eec\u5df2\u60ca\u6270\u4e86\u4e00\u591c\uff0c\u8be5\u8d70\u4e86\u3002\u201d \u3000\u3000\u563f\u563f\u4e8e\u7b11\u6570\u58f0\uff0c\u4fbf\u5f85\u79bb\u684c\u800c\u8d77\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u542b\u7b11\u9053\uff1a\u201c\u600e\u5730\u5982\u6b64\u5306\u5306\u4fbf\u8981\u8d70\u4e86\uff0c\u96be\u9053\u662f\u77a7\u4e0d\u8d77\u5728\u4e0b\u4e48\uff1f\u201d \u3000\u3000\u7a0b\u67ab\u201c\u563f\u563f\u201d\u7b11\u9053\uff1a\u201c\u54ea\u91cc\u54ea\u91cc\uff0c\u5144\u53f0\u8a00\u91cd\u4e86\u3002\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u76ee\u5149\u4e00\u8f6c\uff0c\u53e3\u4e2d\u957f\u957f\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u542b\u7b11\u53c8\u9053\uff1a\u201c\u662f\u4e86\u662f\u4e86\uff0c\u4e24\u4f4d\u5b9a\u5fc5\u662f\u770b\u4e0d \u60ef\u8d31\u4ec6\u7684\u4e11\u6001\uff0c\u2018\u8fd8\u9b42\u201d\u4f60\u4e14\u9000\u53bb\uff0c\u5509\u2014\u2014\u6b64\u4eba\u5bb9\u8c8c\u867d\u51f6\u6076\u4e11\u964b\uff0c\u5176\u5b9e\u5fc3\u4e2d\u5374\u5982\u8d64\u5b50\uff0c\u4ec0\u4e48 \u4e5f\u8bb0\u4e0d\u8d77\uff0c\u4ec0\u4e48\u4e5f\u4e0d\u77e5\u9053\uff0c\u751a\u81f3\u8fde\u4e00\u53e5\u5b8c\u6574\u7684\u8bdd\u4e5f\u8bf4\u4e0d\u51fa\u6765\u3002\u201d\u7a0b\u67ab\u53cc\u7709\u4e00\u626c\uff0c\u8131\u53e3\u9053\uff1a \u201c\u771f\u7684\u4e48\uff1f\u201d\u5ffd\u5730\u4f3c\u4e4e\u63a9\u53e3\uff0c\u4e0d\u4f4f\u54b3\u55fd\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u54c8\u54c8\u4e00\u7b11\u7aef\u8d77\u9152\u676f\uff0c\u4e00\u996e\u800c\u5c3d\uff0c\u7b11\u5bb9\u7a81\u5730\u4e00\u655b\uff0c\u76ee\u5149\u7b14\u76f4\u5730\u671b\u5728\u7a0b\u67ab\u8eab\u4e0a\uff0c\u4e00 \u5b57\u4e00\u5b57\u5730\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u6b64\u4eba\u8bb0\u5fc6\u867d\u7136\u5168\u5931\uff0c\u4f46\u6709\u4e00\u4ef6\u4e8b\uff0c\u4ed6\u5374\u662f\u7262\u7262\u8bb0\u5728\u5fc3\u91cc\u7684\u3002\u201d \u3000\u3000\u7a0b\u67ab\u5fc3\u5934\u4e00\u98a4\uff0c\u5fcd\u4e0d\u4f4f\u53c8\u81ea\u8131\u53e3\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u8c03\u201d\u7f2a\u6587\u201d\u5446\u5446\u5730\u77a7\u4e86\u4ed6\u534a\u664c\uff0c\u7a81\u53c8\u5927\u7b11 \u9053\uff1a\u201c\u9601\u4e0b\u65e2\u7136\u4e5f\u5df2\u77e5\u9053\uff0c\u6211\u8fd8\u7528\u518d\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7a0b\u67ab\u9762\u5bb9\u5927\u53d8\uff0c\u53d8\u8272\u9053\uff1a\u201c\u6211\u77e5\u9053\u4ec0\u4e48\uff1f\u4ec0\u4e48\u4e8b\uff1f\u6211\u4ec0\u4e48\u90fd\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u8fd9\u540d\u6ee1\u6b66\u6797\u7684\u6c5f\u6e56\u8001\u624b\uff0c\u6b64\u523b\u8bf4\u8bdd\u7adf\u4e5f\u6709\u4e9b\u8bed\u65e0\u4f26\u6b21\u8d77\u6765\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u65e0\u8bba\u9601\u4e0b\u77e5\u4e0d\u77e5\u9053\uff0c\u6b64\u4e8b\u5c0f\u53ef\u603b\u662f\u4e0d\u4f1a\u8bf4\u7684\uff0c\u666e\u5929\u4e4b\u4e0b\uff0c\u4f46\u6709\u4f60 \u77e5\u3001\u6211\u77e5\u3001\u4ed6\u77e5\u800c\u5df2\u2014\u2014\u201d\u8bed\u58f0\u4e00\u987f\uff0c\u53cc\u7709\u7a81\u76b1\uff0c\u731b\u5730\u4e00\u62cd\u684c\u9762\uff0c\u5931\u58f0\u9053\uff1a\u201c\u54ce\u5440\uff0c\u4e0d \u597d\uff01\u201d \u3000\u3000\u7a0b\u67ab\u65b9\u81ea\u9547\u5b9a\u5fc3\u795e\uff0c\u7aef\u8d77\u9152\u676f\uff0c\u6b64\u523b\u201c\u5427\u201d\u5730\u53c8\u653e\u56de\u684c\u4e0a\uff0c\u60f6\u58f0\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u4e0d\u597d\u4e86 \u8c03\u201d\u7f2a\u6587\u201d\u53cc\u7709\u6df1\u76b1\uff0c\u957f\u53f9\u9053\uff1a\u201c\u9664\u4e86\u4f60\u3001\u6211\u3001\u4ed6\u4e4b\u5916\uff0c\u6b64\u4e8b\u8fd8\u6709\u4e00\u4eba\u77e5\u9053\u3002\u201d \u3000\u3000\u6797\u7433\u76ee\u5149\u4e00\u8f6c\uff0c\u9762\u4e0a\u6ee1\u542b\u5341\u5206\u52c9\u5f3a\u4e4b\u7b11\u5bb9\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u5440\uff1f\u201d \u3000\u3000\u4f46\u6b64\u523b\u7a0b\u67ab\u5df2\u5fcd\u4e0d\u4f4f\u8131\u53e3\u9053\uff1a\u201c\u8fd8\u6709\u4ec0\u4e48\u4eba\u77e5\u9053\uff1f\u201d \u3000\u3000\u5ffd\u53c8\u81ea\u6094\u5931\u8a00\uff0c\u77e5\u9053\u81ea\u5df1\u6b64\u8bdd\u4e00\u51fa\uff0c\u65e0\u5f02\u5df2\u627f\u8ba4\u4e86\u81ea\u5df1\u65b9\u624d\u4e00\u76f4\u4e0d\u80af\u627f\u8ba4\u7684\u4e8b\uff0c\u4f46\u8bed\u51fa \u5982\u98ce\uff0c\u5df2\u4e07\u4e07\u6536\u56de\u4e0d\u53ca\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u5fc3\u4e2d\u4e0d\u7981\u5fae\u7b11\u4e00\u4e0b\uff0c\u4f46\u9762\u4e0a\u5374\u4ecd\u6b63\u5bb9\u957f\u53f9\u9053\uff1a\u201c\u636e\u95fb\u90a3\u2018\u8fd8\u9b42\u2019\u672a\u5230\u6b64\u95f4\u4e4b \u524d\uff0c\u66fe\u5728\u2018\u5b50\u6bcd\u53cc\u98de\uff0c\u5de6\u624b\u795e\u5251\u2019\u4e01\u8863\u90a3\u91cc\u9017\u7559\u4e86\u8bb8\u4e45\uff0c\u53ea\u6015\u2014\u2014\u201d\u53c8\u662f\u4e00\u58f0\u957f\u53f9\uff0c\u500f\u7136\u4f4f \u53e3\u4e0d\u8bed\uff0c\u7a0b\u67ab\u4ea6\u5782\u9996\u9ed8\u7136\uff0c\u4f46\u4e00\u53cc\u6d53\u7709\uff0c\u5374\u5df2\u7d27\u7d27\u76b1\u5230\u4e86\u4e00\u5904\u3002 \u3000\u3000\u53ea\u542c\u201c\u7f2a\u6587\u201d\u7f13\u7f13\u53c8\u9053\uff1a\u201c\u82e5\u662f\u4e01\u8863\u4e0e\u9601\u4e0b\u4ea4\u60c5\u9887\u6df1\uff0c\u8fd8\u5012\u65e0\u59a8\uff0c\u5426\u5219\u2014\u2014\u5509\uff0c\u82e5\u662f\u88ab \u90a3\u4eba\u77e5\u9053\u4e86\uff0c\u5374\u4e0d\u662f\u73a9\u5904\u3002\u201d \u3000\u3000\u7a0b\u67ab\u6d53\u7709\u4e00\u626c\uff0c\u7a81\u5730\u4f38\u624b\u5728\u684c\u4e0a\u4e00\u51fb\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4f60\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u6211\u5b8c\u5168\u4e0d\u61c2\uff01\u201d \u3000\u3000\u76ee\u5149\u4e4b\u4e2d\uff0c\u6ee1\u73b0\u6740\u673a\uff0c\u201c\u7f2a\u6587\u201d\u54c8\u54c8\u4e00\u7b11\uff0c\u53ea\u4f5c\u672a\u89c1\uff0c\u53ea\u7ba1\u9053\uff1a\u201c\u6211\u82e5\u662f\u9601\u4e0b\uff0c\u4fbf\u8981\u2014 \u2014\u5509\uff0c\u9601\u4e0b\u65e2\u4e0d\u4fe1\u4efb\u5c0f\u53ef\uff0c\u5c0f\u53ef\u4e0d\u8bf4\u4e5f\u7f62\u3002\u201d\u7adf\u7136\u81ea\u659f\u81ea\u996e\u8d77\u6765\u3002 \u3000\u3000\u7a0b\u67ab\u7684\u624b\u6276\u684c\u6cbf\uff0c\u6115\u4e86\u534a\u664c\uff0c\u9762\u4e0a\u9635\u9752\u9635\u767d\uff0c\u60f3\u89c1\u5fc3\u4e2d\u4ea6\u662f\u8d77\u4f0f\u4e0d\u5b9a\u3002 \u3000\u3000\u826f\u4e45\u826f\u4e45\uff0c\u65b9\u81ea\u7f13\u7f13\u5410\u51fa\u4e00\u53e3\u6c14\uff0c\u76ee\u6ce8\u201c\u7f2a\u6587\u201d\u7f13\u7f13\u9053\uff1a\u201c\u5144\u53f0\u82e5\u662f\u5728\u4e0b\uff0c\u53c8\u5f53\u600e \u5730\uff1f\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u5fae\u5fae\u4e00\uff0c\u7b11\uff0c\u9053\uff1a\u201c\u5c0f\u53ef\u82e5\u662f\u9601\u4e0b\uff0c\u76ee\u524d\u5f53\u6025\u4e4b\u52a1\uff0c\u4fbf\u662f\u5148\u5c06\u77e5\u9053\u6b64\u4e8b\u7684\u4eba\u9664 \u53bb\u3002\u201d \u3000\u3000\u7a0b\u67ab\u4ef0\u5929\u72c2\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u96be\u9053\u9601\u4e0b\u4e0d\u77e5\u9053\u6b64\u4e8b\u4e48\uff1f\u8981\u77e5\u6211\u82e5\u8981\u6740\u9601\u4e0b\uff0c\u5b9e\u662f\u6613\u5982\u53cd \u638c\u3002\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u4ea6\u81ea\u4ef0\u5929\u72c2\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u4e14\u542c\u542c\uff0c\u5916\u9762\u53ef\u6709\u4ec0\u4e48\u58f0\u97f3\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u4ed6\u8bf4\u51fa\u8fd9\u53e5\u4e0e\u6b64\u523b\u8c08\u8bba\u4e4b\u4e8b\u6beb\u65e0\u5173\u7cfb\u7684\u8bdd\u6765\uff0c\u7a0b\u67ab\u4e0d\u7981\u4e3a\u4e4b\u4e00\u6115\uff0c\u4f46\u53c8\u4e0d\u5f97\u4e0d\u51dd\u795e \u542c\u53bb\u3002 \u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u53ea\u542c\u2014\u2014 \u3000\u3000\u4e00\u9635\u6025\u4fc3\u7684\u9a6c\u8e44\u58f0\uff0c\u6709\u5982\u96e8\u6253\u82ad\u8549\u4e00\u822c\uff0c\u5954\u51fa\u95e8\u5916\uff0c\u58f0\u97f3\u52a8\u8d77\u4f3c\u4e4e\u8fd8\u5728\u5927\u5385\u4e4b\u524d\uff0c\u4f46\u970e \u773c\u4e4b\u524d\uff0c\u4fbf\u5df2\u58f0\u606f\u65e0\u95fb\u3002 \u3000\u3000\u7a0b\u67ab\u6697\u4e2d\u4e00\u60ca\uff0c\u201c\u597d\u5feb\u7684\u9a6c\u529b\u3002\u201d \u3000\u3000\u53e3\u4e2d\u5374\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u9601\u4e0b\u7684\u5feb\u9a6c\uff0c\u6211\u65e9\u5df2\u89c1\u8bc6\u8fc7\u4e86\u3002\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u9a6c\u4e0a\u5750\u7684\u662f\u8c01\uff0c\u9601\u4e0b\u53ef\u77e5\u9053\u4e48\uff1f\uff0c\u201d\u7a0b\u67ab\u9762\u8272\u4e00\u53d8\uff0c\u970d\u7136\u957f\u8eab\u800c \u8d77\uff0c\u5389\u58f0\u9053\uff1a\u201c\u96be\u9053\u4fbf\u662f\u90a3\u2026\u2026\u90a3\u2018\u8fd8\u9b42\uff0c\uff1f\u201d\u201c\u7f2a\u6587\u201d\u5927\u7b11\u9053\uff1a\u201c\u4eba\u9053\u2018\u9e33\u9e2f\u53cc\u5251\uff0c\u667a\u52c7 \u53cc\u5168\uff0c\u4eca\u65e5\u4e00\u89c1\uff0c\u679c\u7136\u540d\u4e0b\u65e0\u865a\u3002\u201d\u7b11\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u9a6c\u4e0a\u4e4b\u4eba\uff0c\u6b63\u662f\u2018\u8fd8\u9b42\u2019\uff0c \u6b64\u523b\u4ed6\u53ea\u6015\u5df2\u4e0e\u6211\u90a3\u9a6c\u592b\uff0c\u9a91\u7740\u6211\u90a3\u4e24\u5339\u767d\u9a6c\uff0c\u51fa\u4e86\u5609\u5174\u57ce\u4e86\u3002\u6b64\u4eba\u867d\u7136\u4e00\u65e0\u6240\u77e5\uff0c\u4e00\u65e0\u6240 \u5fc6\uff0c\u5374\u53ea\u77e5\u5bf9\u6211\u5fe0\u5fc3\uff0c\u4e5f\u53ea\u8bb0\u5f97\u5341\u4e03\u5e74\u524d\u5927\u96e8\u4e4b\u591c\u7684\u90a3\u4e00\u4ef6\u5f80\u4e8b\u3002\u201d \u3000\u3000\u6d45\u6d45\u559d\u4e86\u53e3\u9152\uff0c\u500f\u7136\u4f4f\u53e3\u4e0d\u8bed\u3002 \u3000\u3000\u7a0b\u67ab\u5446\u5446\u5730\u6115\u4e86\u534a\u664c\uff0c\u5fc3\u4e2d\u4e0d\u7981\u6697\u53f9\u4e00\u58f0\uff0c\u53ea\u89c9\u81ea\u5df1\u7684\u4e00\u4e3e\u4e00\u52a8\uff0c\u6b65\u6b65\u4ff1\u90fd\u843d\u5165\u4e86\u8fd9\u770b \u4f3c\u4e00\u65e0\u5fc3\u673a\u7684\u201c\u5bcc\u5bb6\u516c\u5b50\u201d\u7b97\u4e2d\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u601d\u6f6e\u6570\u8f6c\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u9601\u4e0b\u5982\u6b64\u505a\u6cd5\uff0c\u7a76\u7adf\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5ffd\u5730\u4e00\u62cd\u684c\u5b50\uff0c\u5927\u559d\u9053\uff1a\u201c\u4f60\u5230\u5e95\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u542b\u7b11\u9053\uff1a\u201c\u9601\u4e0b\u4e14\u8bf7\u9547\u9759\u4e00\u4e9b\uff0c\u4f11\u5f97\u5982\u6b64\u51b2\u52a8\uff0c\u5176\u5b9e\u5c0f\u53ef\u5bf9\u9601\u4e0b\u6beb\u65e0\u6076\u610f\uff0c\u53ea \u4e0d\u8fc7\u60f3\u8981\u9601\u4e0b\u9884\u77e5\u5371\u673a\u800c\u5df2\uff0c\u9601\u4e0b\u6b64\u756a\u5230\u4e86\u676d\u5dde\u57ce\uff0c\u89c1\u4e86\u2018\u6bdb\u5927\u592a\u7237\uff0c\u2026\u2026\u201d\u7a0b\u67ab\u53d8\u8272\u9053\uff1a \u201c\u4f60\u600e\u77e5\u6211\u8981\u53bb\u676d\u5dde\uff0c\u600e\u77e5\u6211\u8981\u53bb\u89c1\u6bdb\u81ec\u8c03\u201d\u7f2a\u6587\u201d\u5403\u5403\u7b11\u9053\uff1a\u201c\u6bdb\u5927\u592a\u7237\u5341\u65e5\u4e4b\u540e\u5728\u676d\u5dde \u57ce\u4e2d\u53ec\u5f00\u7684\u82f1\u96c4\u5927\u4f1a\uff0c\u5929\u4e0b\u7686\u95fb\uff0c\u5c0f\u53ef\u81ea\u7136\u4e5f\u662f\u77e5\u9053\u7684\uff0c\u4e0d\u8fc7\u2014\u2014\u201d\u7a0b\u67ab\u8131\u53e3\u9053\uff1a\u201c\u4e0d\u8fc7\u600e \u6837\uff1f\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u8f7b\u8f7b\u4e00\u53f9\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u6211\u82e5\u6362\u4e86\u9601\u4e0b\uff0c\u8fd9\u82f1\u96c4\u5927\u4f1a\uff0c\u4e0b\u53bb\u4e5f\u7f62\u3002\u201d \u3000\u3000\u7a0b\u67ab\u6d53\u7709\u4e00\u626c\uff0c\u77ac\u53c8\u5e73\u590d\uff0c\u5782\u9996\u6c89\u541f\u534a\u664c\uff0c\u7f13\u7f13\u81ea\u8bed\u7740\uff1a\u9053\uff1a\u201c\u82e5\u662f\u4e0d\u53bb\u2026\u2026\u4e07\u4e07\u4e0d \u53ef\uff0c\u4e07\u4e07\u4e0d\u53ef\u3002\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u6b63\u8272\u9053\uff1a\u201c\u5728\u4e0b\u4e0e\u5144\u53f0\u662f\u662f\u840d\u6c34\u76f8\u4ea4\uff0c\u4f46\u5374\u5341\u5206\u613f\u610f\u7ed3\u4ea4\u5144\u53f0\u8fd9\u6837\u7684\u670b\u53cb\uff0c\u90a3 \u82f1\u96c4\u5927\u4f1a\u5144\u53f0\u82e5\u662f\u53bb\u4e86\uff0c\u4e5f\u5343\u4e07\u4e0d\u53ef\u4e3a\u6bdb\u81ec\u5c3d\u529b\u3002\u8981\u77e5\u4e16\u4e0a\u65e0\u8bba\u5982\u4f55\u9690\u79d8\u4e4b\u4e8b\uff0c\u7edd\u65e0\u53ef\u80fd\u6c38 \u4e0d\u6cc4\u6f0f\uff0c\u5144\u53f0\u82e5\u662f\u52a9\u6bdb\u81ec\u6210\u4e86\u66f4\u5927\u7684\u57fa\u4e1a\uff0c\u65e5\u540e\u6bdb\u81ec\u77e5\u9053\u4e86\u6b64\u4e8b\uff0c\u4ee5\u6b64\u4eba\u504f\u72ed\u7684\u5fc3\u80f8\uff0c\u5c82\u4f1a \u5bf9\u9601\u4e0b\u653e\u8fc7\u3002\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u8f6c\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u7a0b\u67ab\u9762\u4e0a\uff0c\u679c\u5df2\u8038\u7136\u52a8\u5bb9\uff0c\u4e0d\u7981\u6697\u4e2d\u5fae\u7b11\u4e00\u4e00\u4e0b\uff0c\u4f46\u53e3\u4e2d\u5374 \u4ecd\u6b63\u8272\u9053\uff1a\u201c\u8fd9\u5176\u4e2d\u5229\u5bb3\u5f97\u5931\uff0c\u6bcb\u5eb8\u5c0f\u53ef\u591a\u8a00\uff0c\u5144\u53f0\u81ea\u5df1\u60f3\u5fc5\u4ea6\u80fd\u6743\u8861\u5f97\u51fa\uff0c\u6bdb\u81ec\u6b64\u523b\uff0c\u5df2 \u662f\u6c5f\u6e56\u4e2d\u4f17\u77e2\u4e4b\u7684\uff0c\u56db\u9762\u5371\u673a\u91cd\u91cd\uff0c\u5144\u53f0\u4f55\u82e6\u53bb\u6dcc\u8fd9\u8d9f\u6d51\u6c34\uff0c\u4f55\u51b5\u4ed6\u82e5\u8eab\u8d25\u540d\u88c2\uff0c\u5144\u53f0\u5c82\u975e \u4fbf\u53ef\u6c38\u8fdc\u65e0\u5fe7\uff0c\u81f3\u4e8e\u90a3\u59d3\u4e01\u7684\u4e48\u2026\u2026\u201d \u3000\u3000\u53cd\u624b\u4e00\u638c\uff0c\u8f7b\u8f7b\u780d\u5728\u684c\u6cbf\u4e0a\uff0c\u5403\u5403\u7b11\u9053\uff1a\u201c\u6b64\u4eba\u6709\u52c7\u65e0\u8c0b\uff0c\u6740\u4e4b\u4e0d\u8d39\u5439\u7070\u4e4b\u529b\u8033\uff01\u201d \u3000\u3000\u7a0b\u67ab\u76ee\u5149\u51dd\u6ce8\u7740\u7a97\u5916\u4e00\u78a7\u4e07\u91cc\u7684\u7a79\u82cd\uff0c\u4f63\u53e3\u65e0\u8a00\uff0c\u4f46\u4ece\u7d27\u95ed\u7684\u7259\u5173\u548c\u7d27\u63e1\u7684\u53cc\u62f3\u4e2d\uff0c\u5374 \u53ef\u770b\u51fa\u4ed6\u6b64\u523b\u5185\u5fc3\u5b9e\u662f\u7d0a\u4e71\u5df2\u6781\uff01 \u3000\u3000\u53ea\u542c\u201c\u7f2a\u6587\u201d\u60a0\u60a0\u53c8\u9053\uff1a\u201c\u5144\u53f0\u7684\u6b66\u529f\uff0c\u667a\u6167\uff0c\u4ff1\u662f\u4eba\u4e2d\u4e4b\u9f99\uff0c\u5728\u6c5f\u6e56\u4e2d\u7684\u4eba\u7f18\uff0c\u4ea6\u8fdc \u6bd4\u6bdb\u81ec\u4e3a\u4f73\uff0c\u82e5\u518d\u52a0\u4ee5\u5144\u5f1f\u6211\u7684\u8d22\u529b\u4e00\uff5e\u54c8\u54c8\uff01\u201d \u3000\u3000\u4ed6\u4ef0\u5929\u72c2\u7b11\u6570\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u4e0e\u5176\u53d7\u4eba\u631f\u5236\uff0c\u4f55\u4e0d\u2014\u2014\u53d6\u2014\u2014\u800c\u2014\u2014\u81ea\u2014\u2014\u4ee3\uff01\u201d \u3000\u3000\u4ed6\u201c\u53d6\u800c\u81ea\u4ee3\uff0c\u2019\u56db\u5b57\uff0c\u4e00\u5b57\u4e00\u5b57\u4ed6\u8bf4\u5c06\u51fa\u6765\uff0c\u5b57\u5b57\u4ff1\u4f3c\u4e00\u67c4\u5343\u65a4\u94c1\u69cc\uff0c\u69cc\u69cc\u4ff1\u90fd\u9707\u52a8 \u4e86\u7a0b\u67ab\u7684\u5fc3\u6249\u3002\u7a0b\u67ab\u51b7\u7b11\u800c\u5750\uff0c\u76ee\u5149\u51dd\u6ce8\uff0c\u53ea\u89c1\u4ed6\u53cc\u7709\u5ffd\u800c\u8212\u5c55\uff0c\u5ffd\u800c\u6df1\u76b1\uff0c\u76ee\u4e2d\u5149\u8292\uff0c\u95ea \u70c1\u4e0d\u5b9a\u3002\u5ffd\u5730\u3002\u4ed6\u53c8\u81ea\u970d\u7136\u957f\u8eab\u800c\u8d77\uff0c\u51fb\u6848\u9053\uff1a\u201c\u4fbf\u662f\u5982\u6b64\uff01\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u5634\u89d2\uff0c\u7b11\u5bb9\u4e00\u95ea\uff0c\u53e3\u4e2d\u6c89\u58f0\u7f13\u7f13\u9053\uff1a\u201c\u5144\u53f0\u53ef\u51b3\u5b9a\u4e86\u4e48\uff1f\u201d \u3000\u3000\u7a0b\u67ab\u79bb\u5e2d\u800c\u8d77\uff0c\u5927\u6b65\u8d70\u5230\u201c\u7f2a\u6587\u201d\u8eab\u524d\uff0c\u957f\u8eab\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u82e5\u975e\u5144\u53f0\u76f8\u6559\uff0c\u5728\u4e0b\u6b64\u523b\u8fd8 \u662f\u8499\u5728\u9f13\u4e2d\uff0c\u95fb\u541b\u4e00\u8a00\uff0c\u8305\u585e\u987f\u5f00\uff0c\u5f53\u771f\u662f\u80dc\u8bfb\u5341\u5e74\u4e4b\u4e66\u3002\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u8fde\u5fd9\u907f\u5e2d\u8c26\u8c22\uff0c\u7b11\u9053\uff1a\u201c\u82e5\u975e\u8d24\u5b5f\u6881\u4eba\u4e2d\u9f99\u51e4\uff0c\u8fd9\u4e9b\u8bdd\u5c0f\u53ef\u518d\u4e5f\u4e0d\u4f1a\u8bf4\u7684\u3002\u201d \u3000\u3000\u7a0b\u67ab\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e0d\u60f3\u5728\u4e0b\u6b64\u6b21\u518d\u5230\u6c5f\u5357\uff0c\u7adf\u80fd\u4ea4\u5230\u5144\u53f0\u8fd9\u822c\u670b\u53cb\uff0c\u65e5\u540e\u5c0f\u53ef\u82e5\u6709\u7edf\u7387 \u6b66\u6797\u7684\u4e00\u65e5\uff0c\u5fc5\u5b9a\u4e0d\u4f1a\u5fd8\u4e86\u5144\u53f0\u3002\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u8fde\u5fd9\u957f\u8eab\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u5982\u6b64\u5728\u4e0b\u4fbf\u5148\u8c22\u8fc7\u3002\u201d \u3000\u3000\u5fae\u5fae\u4e00\u7b11\uff0c\u53c8\u9053\uff1a\u201c\u5728\u4e0b\u867d\u7136\u624b\u65e0\u7f1a\u9e21\u4e4b\u529b\uff0c\u5e73\u751f\u5374\u6700\u6155\u6c5f\u6e56\u6e38\u4fa0\u884c\u5f84\uff01\u201d \u3000\u3000\u7a0b\u67ab\u54c8\u54c8\u4e00\u7b11\uff0c\u5fc3\u4e2d\u5374\u6697\u5fd6\uff1a\u201c\u6b64\u4eba\u867d\u7136\u5fc3\u8ba1\u9887\u6df1\uff0c\u5bb6\u8d22\u53c8\u4e30\uff0c\u5374\u65e0\u6743\u52bf\uff0c\u53c8\u65e0\u58f0\u540d\uff0c \u662f\u4ee5\u4e0d\u60dc\u5982\u6b64\u52a9\u6211\uff0c\u4e3a\u7684\u4e5f\u4e0d\u8fc7\u662f\u2018\u540d\u2019\u4e0e\u2018\u6743\u2019\u4e24\u5b57\u800c\u5df2\u3002\u201d \u3000\u3000\u4e00\u5ff5\u81f3\u6b64\uff0c\u5bf9\u201c\u7f2a\u6587\u201d\u7684\u9632\u5907\u4e4b\u5fc3\uff0c\u4e0d\u7981\u4e3a\u4e4b\u6d88\u53bb\u4e0d\u5c11\u3002 \u3000\u3000\u4e8e\u662f\u91cd\u65b0\u6362\u8fc7\u9152\u83dc\uff0c\u5f00\u6000\u7545\u996e\uff0c\u4e14\u5df2\u65e5\u8fc7\u4e2d\u5929\uff0c\u7a0b\u67ab\u65b9\u9053\uff1a\u201c\u5927\u8ba1\u5df2\u6210\uff0c\u5c0f\u53ef\u4fbf\u544a\u8f9e \u4e86\uff0c\u5144\u53f0\u7684\u5b9d\u9a6c\u660e\u73e0\uff0c\u5c0f\u53ef\u5374\u4e4b\u4e0d\u606d\uff0c\u4e5f\u53ea\u6709\u751f\u53d7\u4e86\uff0c\u597d\u5728\u6765\u65e5\u65b9\u957f\u2014\u2014\u201d\u201c\u7f2a\u6587\u201d\u8138\u8272\u4e00 \u53d8\uff0c\u4f3c\u662f\u5341\u5206\u60ca\u8bb6\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u5c0f\u53ef\u4ee5\u767d\u9a6c\u660e\u73e0\u76f8\u8d60\uff0c\u5144\u53f0\u600e\u5730\u77e5\u9053\uff1f\u201d \u3000\u3000\u7a0b\u67ab\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5144\u53f0\u8d35\u5bb6\u516c\u5b50\uff0c\u81ea\u7136\u4e0d\u77e5\u6211\u8f88\u52fe\u5f53\uff0c\u4e0d\u7792\u5144\u53f0\u8bf4\uff0c\u6628\u591c\u5144\u53f0\u5728\u897f\u53a2\u4e66 \u623f\u4e2d\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u5728\u4e0b\u4fbf\u5728\u5144\u53f0\u7a97\u5916\uff01\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u66f4\u4f3c\u5341\u5206\u60ca\u8bb6\uff0c\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u543e\u5144\u5f53\u771f\u662f\u8eab\u6000\u7edd\u6280\uff0c\u60f3\u53e4\u4e4b\u7a7a\u7a7a\u7cbe\u7cbe\u4e00\u6d41 \u4eba\u7269\uff0c\u53ea\u6015\u4e5f\u4e0d\u8fc7\u5982\u6b64\u800c\u5df2\uff0c\u7684\u786e\u6559\u5c0f\u5f1f\u4f69\u670d\u3002\u201d \u3000\u3000\u4e8e\u662f\u7a0b\u67ab\u53c8\u662f\u4e00\u9635\u5f97\u610f\u7684\u5927\u7b11\u3002 \u3000\u3000\u5927\u7b11\u58f0\u4e2d\uff0c\u4e09\u4eba\u4e00\u9f50\u8d70\u51fa\u5385\u5916\u3002\u201c\u7f2a\u6587\u201d\u5429\u5490\u5907\u9a6c\uff0c\u7a0b\u67ab\u8dc3\u9a6c\u626c\u97ad\uff0c\u201c\u7f2a\u6587\u201d\u7acb\u5728\u9636\u524d \u7b11\u9053\uff1a\u201c\u5144\u53f0\u4e00\u8def\u4fdd\u91cd\uff0c\u5c0f\u5f1f\u5728\u6b64\u9759\u5019\u4f73\u97f3\u2026\u2026\u201d \u3000\u3000\u7a0b\u67ab\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e8b\u6210\u4e4b\u540e\uff0c\u5144\u53f0\u5f97\u52bf\uff0c\u5c0f\u5f1f\u5f97\u5229\uff0c\u81f3\u4e8e\u2018\u540d\u2019\u4e4b\u4e00\u5b57\u4e48\uff0c\u4f60\u6211\u4e24\u4eba\u66f4 \u662f\u90fd\u5c11\u4e0d\u4e86\u7684\u4e86\u3002\u201d \u3000\u3000\u97ad\u4e1d\u4e00\u626c\uff0c\u4e24\u5339\u767d\u9a6c\uff0c\u7edd\u5c18\u800c\u53bb\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u8d1f\u624b\u800c\u7acb\uff0c\u76ee\u9001\u8fd9\u6eda\u6eda\u7684\u70df\u5c18\uff0c\u9010\u6e10\u6d88\u5931\uff0c\u5634\u89d2\u4e0d\u7981\u53c8\u6cdb\u8d77\u4e00\u4e1d\u4ed6\u90a3\u60ef\u6709\u7684\u5fae \u7b11\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u51b7\u7b11\u4e00\u58f0\uff0c\u6697\u6697\u5fd6\u9053\uff1a\u201c\u6c6a\u4e00\u9e4f\u3001\u6c6a\u4e00\u9e23\u6211\u4ee5\u6c14\u76f8\u6fc0\uff0c\u5df2\u5165\u543e\u5f40\uff0c\u2018\u767e\u6b65\u98de\u82b1\uff0c \u6797\u7426\u7b5d\u6c34\u6027\u6768\u82b1\uff0c\u6211\u53ea\u8981\u7565\u65bd\u865a\u60c5\u5047\u610f\uff0c\u4ea6\u96be\u9003\u6211\u638c\u63e1\uff0c\u6b64\u523b\u2019\u9e33\u9e2f\u53cc\u5251\u2019\u592b\u5987\u4ea6\u88ab\u6211\u4ee5 \u2018\u540d\u2019\u3001\u2018\u5229\uff0c\u4e24\u5b57\u6253\u52a8\u4e00\u201d\u4ed6\u76ee\u4e2d\u95ea\u4e00\u4e1d\u5f97\u610f\u7684\u5149\u91c7\uff0c\u63a5\u7740\u5fd6\u9053\uff1a\u201c\u81f3\u4e8e\u90a3\u2018\u5b50\u6bcd\u53cc\u98de\uff0c \u5de6\u624b\u795e\u5251\u2019\u4e01\u8863\uff0c\u81ea\u6709\u2018\u9e33\u9e2f\u53cc\u5251\uff0c\u53bb\u4e3a\u6211\u5bf9\u4ed8\uff0c\u6b64\u523b\u4e0d\u8fc7\u53ea\u5269\u4e0b\u90a3\u2019\u4e03\u661f\u97ad\u2019\u675c\u4ef2\u5947\u4e00\u4eba \u800c\u5df2\uff0c\u5176\u4f59\u7684\u2018\u516b\u9762\u73b2\u73d1\u2019\u80e1\u4e4b\u8f89\u3001\u2018\u94c1\u624b\u4ed9\u733f\u2019\u4faf\u6797\uff0c\u66f4\u4f55\u5728\u6211\u4e4b\u773c\u4e0b\uff1f\u201d \u3000\u3000\u9053\u4e0a\u70df\u5c18\u5df2\u81ea\u6d88\u5931\uff0c\u4ed6\u6697\u4e2d\u5fae\u7b11\u4e00\u58f0\uff0c\u7f13\u7f13\u8f6c\u8fc7\u8eab\u53bb\uff5e\u4e00\u54ea\u77e5\u2014\u2014\u4ed6\u8eab\u5f62\u65b9\u8f6c\uff0c\u80cc\u540e\u7a81 \u6709\u4e00\u4eba\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u9601\u4e0b\u597d\u5389\u5bb3\u7684\u8fde\u73af\u5999\u8ba1\uff0c\u2018\u6cb3\u6714\u53cc\u5251\uff0c\u88ab\u4f60\u6fc0\u4e4b\u4ee5\u6c14\uff0c\u2019\u767e\u6b65\u98de\u82b1\u2019\u88ab \u4f60\u52a8\u4e4b\u4ee5\u8272\uff0c\u2018\u9e33\u9e2f\u53cc\u5251\uff0c\u88ab\u4f60\u8bf1\u4e4b\u4ee5\u5229\uff0c\u5269\u4e0b\u7684\u4e0d\u8fc7\u53ea\u5269\u4e86\u2019\u4e03\u661f\u97ad\u2019\u675c\u4ef2\u5947\u4e00\u4eba\u800c\u5df2\uff0c \u8fd9\u756a\u7075\u86c7\u6bdb\u81ec\u4f17\u53db\u4eb2\u79bb\uff0c\u5f53\u771f\u8981\u6b7b\u65e0\u5176\u6240\u4e86\u3002\u201d \u3000\u3000\u8bdd\u58f0\u6e05\u6717\uff0c\u5b57\u5b57\u60ca\u5fc3\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u5fc3\u5934\u4e00\u51db\uff0c\u5239\u90a3\u4e4b\u95f4\uff0c\u638c\u5fc3\u3001\u524d\u989d\u4fbf\u5df2\u5e03\u6ee1\u51b7\u6c57\uff0c\u95ea\u7535\u822c\u79fb\u8eab\u9519\u6b65\uff0c\u5927\u559d\u4e00 \u58f0\uff1a\u201c\u662f\u8c01\uff1f\u201d \u3000\u3000\u53ea\u542c\u8eab\u540e\u5927\u7b11\u4e0d\u7edd\uff0c\u95e8\u8fb9\u7684\u77f3\u9636\u4e0b\uff0c\u7adf\u76d8\u819d\u5750\u7740\u4e00\u4e2a\u7626\u9aa8\u9cde\u5ccb\uff0c\u9e51\u8863\u767e\u7ed3\uff0c\u76ae\u80a4\u5374\u83b9\u767d \u5982\u7389\uff0c\u76ee\u5149\u66f4\u662f\u5229\u5982\u95ea\u7535\u7684\u4e2d\u5e74\u4e10\u8005\uff0c\u6b64\u523b\u4e00\u9762\u4ef0\u5929\u5927\u7b11\uff0c\u4e00\u9762\u957f\u8eab\u800c\u8d77\uff0c\u53e3\u4e2d\u6717\u7b11\u7b54\u9053\uff1a \u201c\u5bbf\u8fc1\u6b63\u9633\u697c\u5934\uff0c\u4e0e\u516c\u5b50\u66fe\u6709\u4e00\u9762\u4e4b\u7f18\uff0c\u516c\u5b50\u53ef\u66fe\u5fd8\u8bb0\u4e86\u4e48\uff1f\u201d \u3000\u3000\u7f2a\u6587\u5fae\u4e00\u5b9a\u795e\uff0c\u76ee\u5149\u95ea\u52a8\uff0c\u7a81\u4e5f\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u5f53\u662f\u8c01\uff0c\u5374\u539f\u6765\u662f\u2018\u7a77\u795e\uff0c\u51cc\u5927\u4fa0\u3002\u201d \u4ed6\u5fc3\u4e2d\u867d\u7136\u60ca\u60e7\u4ea4\u96c6\uff0c\u4f46\u9762\u4e0a\u5374\u4e0d\u9732\u4e1d\u6beb\u795e\u8272\uff0c\u4eff\u4f5b\u5728\u90a3\u91cc\u89c1\u8fc7\u201c\u7a77\u795e\u201d\u51cc\u9f99\uff0c\u542c\u5230\u8fd9\u756a\u5b57 \u5b57\u60ca\u5fc3\u7684\u8a00\u8bed\uff0c\u672c\u662f\u5929\u7ecf\u5730\u4e49\u4e4b\u4e8b\uff0c\u4e1d\u6beb\u4e0d\u5fc5\u60ca\u5f02\u3002\u53ea\u662f\u4ed6\u90a3\u4e00\u4e00\u53cc\u7ec8\u65e5\u88ab\u7b11\u610f\u63a9\u76d6\u7684\u76ee \u5149\uff0c\u6b64\u523b\u5374\u6709\u4e00\u4e1d\u5947\u5f02\u7684\u5149\u8292\u95ea\u8fc7\uff0c\u81f3\u4e8e\u4ed6\u5fc3\u91cc\u5728\u60f3\u4ec0\u4e48\uff0c\u5bf9\u8fd9\u8bc6\u7834\u81ea\u5df1\u5999\u8ba1\u7684\u201c\u7a77\u795e\u201d\u51cc \u9f99\u5c06\u8981\u505a\u4e9b\u4ec0\u4e48\uff0c\u5374\u8c01\u4e5f\u65e0\u6cd5\u731c\u6d4b\u5f97\u5230\u3002\u800c\u90a3\u8a89\u6ee1\u5929\u4e0b\uff0c\u540d\u9707\u9ed1\u767d\u4e24\u9053\u7684\u201c\u7a77\u5bb6\u5e2e\u201d\u4e4b\u201c\u7a77 \u795e\u201d\u51cc\u9f99\u3002\u6b64\u523b\u6717\u7b11\u4e4b\u58f0\uff0c\u72b9\u672a\u7ee7\u7edd\uff0c\u4ed6\u7684\u6765\u610f\u662f\u6076\u662f\u5584\uff0c\u6559\u4eba\u65e0\u6cd5\u731c\u6d4b\u3002\u4e24\u4eba\u76f8\u5bf9\u5927\u7b11\uff0c \u7b11\u58f0\u88c2\u77f3\u7a7f\u4e91\uff0c\u771f\u6b32\u7a7f\u4e91\u800c\u4e0a\uff0c\u4e00\u53ea\u5899\u89d2\u7684\u72f8\u732b\uff0c\u88ab\u8fd9\u9707\u8033\u7684\u7b11\u58f0\u6240\u60ca\uff0c\u201c\u54aa\u545c\u201d\u201d\u58f0\uff0c\u8dd1 \u4e86\u5f00\u53bb\u3002 \u7b2c\u4e8c\u5341\u4e00\u7ae0>> \u53e4\u9f99\u300a\u6e58\u5983\u5251\u300b \u7b2c\u4e8c\u5341\u4e00\u7ae0 \u3000\u3000\u957f\u8857\u6f2b\u65e0\u4eba\u8ff9\uff0c\u6de1\u6de1\u7684\u659c\u9633\uff0c\u9759\u9759\u5730\u7167\u5728\u65e0\u4eba\u7684\u8857\u9053\u4e0a\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u7b11\u58f0\u4e0d\u7edd\uff0c\u76ee\u5149\u56db\u626b\uff0c\u4f38\u624b\u4e00\u62cd\u51cc\u9f99\u80a9\u5934\uff0c\u7b11\u9053\uff1a\u201c\u591a\u65e5\u4e0d\u89c1\uff0c\u51cc\u5927\u4fa0\u522b\u6765\u65e0 \u6059\uff1f\u201d \u3000\u3000\u7b11\u8bed\u58f0\u4e2d\uff0c\u5de6\u624b\u7a81\u5730\u51fa\u624b\u5982\u98ce\uff0c\u75be\u70b9\u51cc\u9f99\u53f3\u808b\u8110\u4e0b\u201c\u5546\u66f2\u201d\u5927\u7a74\u3002 \u3000\u3000\u201c\u7a77\u795e\u201d\u51cc\u9f99\u4ef0\u5929\u800c\u7b11\uff0c\u4eff\u4f5b\u672a\u89c1\uff0c\u201c\u7f2a\u6587\u201d\u624b\u6307\u5df2\u5c06\u89e6\u53ca\u4ed6\u7684\u8863\u886b\uff0c\u7adf\u7a81\u53c8\u786c\u751f\u751f\u987f \u5f80\uff0c\u51cc\u9f99\u7b11\u58f0\u9aa4\u987f\uff0c\u76ee\u5149\u4e00\u95ea\uff0c\u5389\u7535\u822c\u671b\u5728\u201c\u7f2a\u6587\u201d\u9762\u4e0a\uff0c\u201c\u7f2a\u6587\u201d\u624b\u638c\u4e00\u5782\uff0c\u51cc\u9f99\u6c89\u58f0 \u9053\uff1a\u201c\u516c\u5b50\u8fd9\u4e00\u6307\u539f\u8be5\u70b9\u4e0b\u53bb\u7684\uff0c\u5426\u5219\u4e8b\u5982\u6cc4\u6f0f\uff0c\u5c82\u975e\u8bef\u4e86\u516c\u5b50\u7684\u5927\u4e8b\uff1f\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u9762\u988a\u5fae\u7ea2\uff0c\u7b11\u9053\uff1a\u201c\u51cc\u5927\u4fa0\u7adf\u7136\u5bf9\u6211\u6beb\u65e0\u9632\u8303\u4e4b\u5fc3\uff0c\u663e\u89c1\u5bf9\u6211\u6beb\u65e0\u6076\u610f\uff0c\u51cc\u5927 \u4fa0\u65e2\u7136\u5bf9\u6211\u6beb\u65e0\u6076\u610f\uff0c\u6211\u53c8\u4f55\u5fc5\u4e0b\u624b\uff01\u201d \u3000\u3000\u201c\u7a77\u795e\u201d\u51cc\u9f99\u5fae\u5fae\u4e00\u6014\uff0c\u7a81\u53c8\u4ef0\u5929\u7b11\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u4f55\u5fc5\u4e0b\u624b\uff0c\u60f3\u6765\u51cc\u67d0\u82e5\u662f\u8981\u5bf9\u516c\u5b50\u4e0d \u5229\uff0c\u516c\u5b50\u662f\u5fc5\u5b9a\u8981\u4e0b\u624b\u7684\u4e86\uff1f\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u9053\uff1a\u201c\u6b63\u662f\uff01\u201d \u3000\u3000\u201c\u7a77\u795e\u201d\u51cc\u9f99\u7b11\u58f0\u5ffd\u7136\u53d8\u4e3a\u957f\u53f9\uff0c\u9053\uff1a\u201c\u51cc\u67d0\u884c\u52a8\u6c5f\u6e56\u591a\u5e74\uff0c\u516c\u5b50\u4f60\u8fd9\u822c\u4eba\u7269\uff0c\u51cc\u67d0\u5012 \u662f\u521d\u89c1\u3002\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u591a\u65e5\u672a\u89c1\u6881\u4e0a\u4eba\u6881\u5927\u54e5\uff0c\u4e0d\u77e5\u4ed6\u4fa0\u8ff9\u5728\u4f55\u5904\uff1f\u51cc\u5927\u4fa0\u4e0e\u4ed6\u65e2 \u5c5e\u77e5\u4ea4\uff0c\u60f3\u5fc5\u662f\u77e5\u9053\u7684\u4e86\uff1f\u201d \u3000\u3000\u201c\u7a77\u795e\u201d\u51cc\u9f99\u53c8\u81ea\u4e00\u6115\uff0c\u8131\u53e3\u9053\uff1a\u201c\u516c\u5b50\u600e\u4f1a\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u542b\u7b11\u622a\u53e3\u9053\uff1a\u201c\u5728\u4e0b\u505a\u4e8b\u867d\u975e\u5341\u5206\u9690\u5bc6\uff0c\u4f46\u82e5\u975e\u6881\u5927\u54e5\u66fe\u5c06\u6b64\u4e8b\u4e0e\u51cc\u5927\u4fa0\u8c08 \u53ca\uff0c\u51cc\u5927\u4fa0\u600e\u4f1a\u77e5\u9053\u5f97\u5982\u6b64\u6e05\u695a\uff0c\u4f55\u51b5\u2018\u7a77\u5bb6\u5e2e\u2019\u4e0e\u2018\u4e5d\u8db3\u795e\u86db\uff0c\u58f0\u606f\u76f8\u95fb\uff0c\u4ea6\u662f\u4eba\u4eba\u610f\u6599 \u4e2d\u4e8b\u3002\u201d\u201c\u7a77\u795e\u201d\u51cc\u9f99\u4e0a\u4e0b\u671b\u4e86\u201c\u7f2a\u6587\u201d\u51e0\u773c\uff0c\u4e0d\u7981\u53c8\u81ea\u957f\u53f9\u9053\uff1a\u201c\u884c\u4e8b\u51b3\u65ad\uff0c\u5f53\u884c\u5219\u884c\uff0c \u6599\u4e8b\u5982\u795e\uff0c\u6599\u65e0\u4e0d\u4e2d\uff0c\u65e0\u8bba\u8c01\u82e5\u7ed3\u4e0b\u4e86\u516c\u5b50\u8fd9\u6837\u7684\u4ec7\u654c\uff0c\u5b9e\u5728\u662f\u503c\u5f97\u60b2\u54c0\u7684\u4e8b\u3002\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u54c8\u54c8\u4e00\u7b11\uff0c\u7709\u5b57\u95f4\u970d\u7136\u6d8c\u8d77\u4e00\u9635\u8c6a\u6c14\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4e0d\u51fa\u5341\u65e5\uff0c\u4fbf\u662f\u90a3\u82f1\u96c4\u5927\u4f1a \u7684\u4f1a\u671f\uff0c\u5230\u90a3\u65f6\u6bdb\u81ec\u53ea\u6015\u4fbf\u8981\u5c1d\u4e00\u5c1d\u60b2\u54c0\u662f\u4f55\u6ecb\u5473\u4e86\u3002\u201d \u3000\u3000\u8bed\u58f0\u5fae\u987f\uff0c\u542b\u7b11\u53c8\u9053\uff1a\u201c\u51cc\u5927\u4fa0\u82e5\u662f\u6709\u5174\uff0c\u4f55\u59a8\u53bb\u770b\u4e00\u770b\u70ed\u95f9\uff1f\u201d \u3000\u3000\u201c\u7a77\u795e\u201d\u51cc\u9f99\u5fae\u4e00\u6c89\u541f\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u516c\u5b50\u5982\u6b64\u5e03\u7f6e\uff0c\u867d\u7136\u5341\u5206\u5468\u5bc6\uff0c\u4f46\u90a3\u2018\u6cb3\u6714\u53cc \u5251\u2019\u3001\u2018\u9e33\u9e2f\u53cc\u5251\uff0c\u4ee5\u53ca\u2019\u767e\u6b65\u98de\u82b1\uff0c\u7b49\u4eba\uff0c\u81f3\u4eca\u5c1a\u6151\u4e8e\u2018\u7075\u86c7\uff0c\u6bdb\u81ec\u4f59\u5a01\u4e4b\u4e0b\uff0c\u7eb5\u7136\u4ff1\u5df2 \u5bf9\u6bdb\u684c\u5fc3\u751f\u4e0d\u5fff\uff0c\u53ea\u6015\u4e5f\u4e0d\u6562\u5bf9\u6bdb\u81ec\u6709\u6240\u4e0d\u5229\u3002\u201d\u201c\u7f2a\u6587\u201d\u5fae\u7b11\u9053\u3002\u201c\u6211\u5df2\u5c06\u5f15\u706b\u4e4b\u7269\u5806 \u8d77\uff0c\u5230\u65f6\u53ea\u8981\u53d1\u706b\u4e00\u71c3\uff0c\u4fbf\u662f\u71ce\u539f\u4e4b\u52bf\uff0c\u82e5\u4e0d\u5c06\u6bdb\u81ec\u70e7\u6210\u7126\u5934\u70c2\u989d\uff0c\u600e\u80fd\u6cc4\u6211\u5fc3\u5934\u4e4b\u6068\u3002\u201d \u4ed6\u9762\u4e0a\u7b11\u5bb9\u6e10\u655b\uff0c\u8bf4\u5230\u540e\u6765\uff0c\u9762\u8272\u5df1\u53d8\u5f97\u6709\u5982\u7384\u51b0\u822c\u5bd2\u51b7\u3002\u201c\u7a77\u795e\u201d\u51cc\u9f99\u76ee\u5149\u95ea\u52a8\uff0c\u53cc\u7709\u7adf \u7a81\u5730\u5fae\u5fae\u4e00\u76b1\uff0c\u6697\u5fd6\u9053\uff1a\u201c\u8fd9\u5c11\u5e74\u667a\u52c7\u517c\u5907\uff0c\u6587\u6b66\u4e24\u9014\uff0c\u4ff1\u90fd\u8d85\u4eba\u4e00\u7b49\uff0c\u53ea\u53ef\u60dc\u591a\u4e86\u51e0\u5206\u50b2 \u6c14\uff0c\u5bf9\u4efb\u4f55\u4e8b\u81ea\u4fe1\u4ff1\u90fd\u592a\u6df1\u3002\u201d \u3000\u3000\u5fc3\u5ff5\u8f6c\u5904\uff0c\u53ea\u89c1\u201c\u7f2a\u6587\u201d\u7b11\u5bb9\u53c8\u73b0\uff0c\u542b\u7b11\u9053\uff1a\u201c\u51cc\u5927\u4fa0\u6b64\u756a\u5fc5\u975e\u65e0\u56e0\u800c\u6765\uff0c\u4e0d\u77e5\u6709\u4f55\u89c1 \u6559\uff1f\u201d \u3000\u3000\u4ed6\u4e00\u9762\u8bf4\u8bdd\uff0c\u4e00\u9762\u62f1\u624b\u63d6\u5ba2\u5165\u95e8\uff0c\u4f46\u201c\u7a77\u795e\u201d\u51cc\u9f99\u5374\u672a\u4e3e\u6b65\uff0c\u95ea\u7535\u822c\u7684\u76ee\u5149\u56db\u626b\u4e00\u773c\uff0c \u786e\u5b9a\u4e86\u56db\u4e0b\u4e00\u65e0\u4eba\u8ff9\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4e3a\u4e86\u516c\u5b50\u4e4b\u4e8b\uff0c\u6881\u4e0a\u4eba\u66fe\u6765\u6c42\u6211\uff0c\u8bf4\u662f\u5230\u4e86\u5fc5\u8981\u4e4b\u65f6\uff0c\u4fbf \u8981\u6211\u52a8\u5458\u7a77\u5bb6\u5e2e\u5343\u4e07\u5f1f\u5144\u4e4b\u529b\u3002\u6211\u867d\u7136\u4e45\u6155\u5f53\u5e74\u4ec7\u8001\u524d\u8f88\u7684\u82f1\u540d\uff0c\u53c8\u77e5\u9053\u516c\u5b50\u4f60\u662f\u6d77\u5916\u6765 \u5ba2\uff0c\u4f46\u6b64\u4e8b\u6bd5\u7adf\u5173\u7cfb\u592a\u5927\uff0c\u662f\u4ee5\u51cc\u67d0\u4e0d\u5f97\u4e0d\u6697\u4e2d\u8ffd\u968f\u516c\u5b50\uff0c\u770b\u4e00\u770b\u2026\u2026\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u7b11\u9053\uff1a\u201c\u770b\u4e00\u770b\u6211\u662f\u5426\u5f53\u5f97\u8d77\u5927\u4e8b\uff1f\u201d \u3000\u3000\u201c\u7a77\u795e\u201d\u51cc\u9f99\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d\u5fae\u559f\u4e00\u58f0\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u591a\u65e5\u6765\u6211\u89c1\u5230\u516c\u5b50\u679c\u7136\u662f\u4eba\u4e2d\u4e4b \u9f99\uff0c\u9e21\u4e2d\u4e4b\u9e64\uff0c\u662f\u4ee5\u6b64\u523b\u4fbf\u5192\u6627\u95ef\u6765\uff0c\u95ee\u4e00\u95f4\u516c\u5b50\u6709\u4f55\u5904\u8981\u6211\u2018\u7a77\u5bb6\u5e2e\u2019\u51fa\u529b\uff1f\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u5251\u7709\u5fae\u5254\uff0c\u5634\u89d2\u4ecd\u5e26\u7b11\u5bb9\uff0c\u9053\u3002 \u3000\u3000\u201c\u51cc\u5927\u4fa0\u7684\u597d\u610f\uff0c\u5728\u4e0b\u5fc3\u9886\uff0c\u4f46\u4e8b\u60c5\u81f3\u6b64\uff0c\u4f3c\u4e4e\u5df2\u6ca1\u6709\u4ec0\u4e48\u503c\u5f97\u51cc\u5927\u4fa0\u52b3\u52a8\u4e4b\u5904\uff0c\u4f55\u51b5 \u51cc\u5927\u4fa0\u56db\u65b9\u884c\u4fa0\uff0c\u672c\u5df2\u5206\u8eab\u4e4f\u672f\uff0c\u5728\u4e0b\u5c82\u6562\u5984\u6c42\u51cc\u5927\u4fa0\u4e3a\u8fd9\u4ef6\u79c1\u4eba\u6069\u6028\u51fa\u624b\uff1f\u201d \u3000\u3000\u4ed6\u867d\u7136\u542b\u7b11\u800c\u8a00\uff0c\u4f46\u8a00\u8bed\u4e2d\u5df2\u9690\u9690\u9732\u51fa\u950b\u8292\uff0c\u5c06\u8bdd\u4e2d\u7684\u4e00\u4e2a\u201c\u6c42\u201d\u5b57\uff0c\u58f0\u97f3\u8bf4\u5f97\u66f4\u91cd\uff0c \u53ea\u56e0\u4e3a\u65b9\u624d\u201c\u7a77\u795e\u201d\u51cc\u9f99\u8bdd\u4e2d\u7684\u201c\u6c42\u201d\u5b57\uff0c\u89e6\u52a8\u4e86\u4ed6\u7684\u5c11\u5e74\u50b2\u6c14\u3002 \u3000\u3000\u201c\u7a77\u795e\u201d\u51cc\u9f99\u76ee\u5149\u4e00\u626b\uff0c\u795e\u5149\u56db\u5c04\uff0c\u6717\u58f0\u7b11\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u5728\u4e0b\u53ea\u6709\u9759\u89c2\u516c\u5b50\u529f\u5230\u6e20 \u6210\u7684\u597d\u97f3\u4e86\uff0c\u5230\u65f6\u516c\u5b50\u5207\u83ab\u5fd8\u4e86\u8bf7\u6211\u559d\u4e00\u676f\u5e86\u529f\u4e4b\u9152\u3002\u201d \u3000\u3000\u5927\u7b11\u58f0\u4e2d\uff0c\u4ed6\u8fde\u9000\u4e09\u6b65\uff0c\u5fae\u4e00\u62ac\u624b\uff0c\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u53cc\u7709\u4e00\u626c\uff0c\u4f3c\u4e4e\u8981\u8bf4\u4ec0\u4e48\uff0c\u5374\u53c8\u500f\u7136\u4f4f\u53e3\uff0c\u53ea\u662f\u51b7\u51b7\u9053\uff1a\u201c\u51cc\u5927\u4fa0\u5306\u5306\u800c\u53bb\uff0c \u6055\u6211\u4e0d\u8fdc\u9001\u4e86\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u5374\u6652\u7136\u6697\u5fd6\u9053\uff1a\u201c\u7a77\u5bb6\u5e2e\u7eb5\u6709\u5929\u5927\u7684\u52bf\u529b\uff0c\u6211\u4ec7\u6055\u4e5f\u672a\u89c1\u8981\u6765\u6c42\u4f60\u3002\u201d\u6210\u529f\u7684\u5c11\u5e74 \u4eba\uff0c\u603b\u662f\u6709\u5e73\u4e91\u7684\u610f\u6c14\u3002 \u3000\u3000\u5915\u9633\u5c06\u843d\u3002 \u3000\u3000\u4e00\u8f86\u8272\u5982\u767d\u94f6\u7684\u56db\u9a6c\u5927\u8f66\uff0c\u9a76\u4eba\u5609\u5174\u95f9\u5e02\u4e2d\u7684\u4eba\u7fa4\u3002 \u3000\u3000\u591c\u5e02\u521d\u5347\uff0c\u5609\u5174\u57ce\u7684\u8857\u9053\uff0c\u662f\u7e41\u534e\u800c\u62e5\u6324\u7684\uff0c\u7136\u800c\u8fd9\u8f86\u94f6\u767d\u7684\u56db\u9a6c\u5927\u8f66\uff0c\u5728\u62e5\u6324\u7684\u4eba\u7fa4 \u4e2d\u5954\u884c\u7740\uff0c\u5374\u7075\u6d3b\u5f97\u6709\u5982\u4e00\u6761\u6c34\u4e2d\u7684\u9c7c\u3002 \u3000\u3000\u8f66\u53a2\u524d\u7684\u5fa1\u8005\u4e00\u8eab\u767d\u8863\uff0c\u8eab\u8eaf\u7b14\u76f4\uff0c\u624b\u4e2d\u7684\u9cb8\u9aa8\u957f\u97ad\u9ad8\u9ad8\u626c\u8d77\uff0c\u547c\u54e8\u4e00\u58f0\uff0c\u5212\u7834\u66ae\u98ce\uff0c \u5374\u4ec5\u662f\u8f7b\u8f7b\u5730\u6d8c\u5728\u9a6c\u80cc\u4e0a\u3002 \u3000\u3000\u5343\u4e2d\u9009\u4e00\u7684\u540d\u79cd\u826f\u9a6c\uff0c\u767d\u94f6\u4e3a\u9876\u7684\u534e\u4e3d\u5927\u8f66\uff0c\u7cbe\u795e\u6296\u64de\u7684\u767d\u8863\u5fa1\u8005\u2014\u2014\u8fd9\u5df2\u8db3\u591f\u4f7f\u4eba\u4eba \u4ff1\u90fd\u6295\u4ee5\u8273\u7fa1\u7684\u76ee\u5149\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u8f66\u53a2\u4e2d\u5750\u7684\u662f\u8c01\uff0c\u81ea\u7136\u5c31\u66f4\u6210\u4e86\u4eba\u4eec\u6240\u731c\u6d4b\u7684\u5bf9\u8c61\u3002 \u3000\u3000\u8f66\u9a6c\u6025\u9a70\u800c\u8fc7\uff0c\u626c\u8d77\u4e00\u80a1\u6de1\u6de1\u7684\u8f7b\u5c18\uff0c\u5374\u6ca1\u6709\u649e\u5230\u884c\u4eba\u7684\u4e00\u7247\u8863\u89d2\u3002 \u3000\u3000\u6de1\u6de1\u7684\u70df\u5c18\u4e2d\uff0c\u56db\u5339\u5065\u9a6c\uff0c\u5ffd\u7136\u9f50\u5730\u4ef0\u9996\u4e00\u9635\u957f\u5636\u3002 \u3000\u3000\u5636\u58f0\u5c16\u9510\u9ad8\u4ea2\uff0c\u767d\u9a6c\u4e00\u9f50\u4eba\u7acb\u800c\u8d77\uff0c\u524d\u8db3\u524d\u7a9c\uff0c\u540e\u8db3\u4e71\u8e48\uff0c\u8f66\u9a6c\u7adf\u5ffd\u7136\u4ff1\u90fd\u65e0\u6cd5\u518d\u5411\u524d \u79fb\u4e00\u6b65\u3002 \u3000\u3000\u767d\u8863\u5fa1\u8005\u201c\u592e\u9a6c\u201d\u7a0b\u4e03\u5927\u60ca\u4e4b\u4e0b\uff0c\u626c\u97ad\uff0c\u52d2\u7f30\u6251\u8eab\u540e\u671b\u3002 \u3000\u3000\u53ea\u542c\u8f66\u540e\u4e00\u4eba\u5389\u58f0\u53f1\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u7684\u9a6c\u8f66\u6562\u5728\u4eba\u4e1b\u4e2d\u6025\u9a70\uff0c\u4e0d\u6015\u649e\u4f24\u4e86\u4eba\u4e48\uff1f\u201d \u3000\u3000\u201c\u5feb\u9a6c\u201d\u7a0b\u4e03\u5530\u5730\u63a0\u4e0b\u8f66\u5ea7\uff0c\u56db\u4e0b\u60ca\u547c\u58f0\u4e2d\uff0c\u53ea\u89c1\u4e00\u4e2a\u9ed1\u8863\u5934\u9640\uff0c\u62ab\u80a9\u7684\u4e71\u53d1\u4e0a\uff0c\u7b8d\u7740 \u4e00\u9053\u95ea\u95ea\u751f\u5149\u7684\u94f6\u7b8d\uff0c\u5de6\u638c\u4e00\u628a\u6293\u7740\u8f66\u540e\u7684\u6a2a\u8f95\uff0c\u9ad8\u5927\u7684\u8eab\u8eaf\uff0c\u6709\u5982\u5c71\u5cb3\u822c\u9489\u7acb\u5728\u5730\u4e0a\uff0c\u8fd9 \u6025\u9a70\u800c\u884c\u7684\u56db\u9a6c\u5927\u8f66\uff0c\u7adf\u88ab\u4ed6\u4e00\u53ea\u72ec\u81c2\u633d\u4f4f\u3002 \u3000\u3000\u56db\u9a6c\u6025\u9a70\u4e4b\u52bf\uff0c\u7adf\u8fd8\u62b5\u4e0d\u4e0a\u4ed6\u72ec\u81c2\u4e4b\u529b\uff0c\u201c\u5feb\u9a6c\u201d\u7a0b\u4e03\u53ea\u89c9\u5fc3\u5934\u4e00\u9635\u9a87\u7136\uff0c\u6728\u7acb\u5f53\u5730\uff0c \u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u4e24\u65c1\u5e97\u94fa\u5c04\u51fa\u7684\u706f\u5149\u91cc\uff0c\u53ea\u89c1\u8fd9\u9ed1\u8863\u957f\u53d1\u5934\u9640\uff0c\u53f3\u81c2\u7a7a\u7a7a\uff0c\u7adf\u5df2\u65ad\u53bb\uff0c\u53ea\u5269\u4e0b\u6761\u7a7a\u8896\uff0c \u675f\u5728\u8170\u95f4\u7684\u957f\u7ee6\u4e0a\uff0c\u9762\u4e0a\u5374\u6709\u4e00\u9053\u5200\u75a4\uff0c\u81ea\u5de6\u773c\u659c\u4e0b\uff0c\u76f4\u8fbe\u53f3\u988a\u4e4b\u4e0b\uff0c\u88ab\u706f\u5149\u4e00\u6620\uff0c\u95ea\u95ea\u53d1 \u51fa\u4e11\u964b\u7684\u7ea2\u5149\uff0c\u4e0e\u4ed6\u53f3\u9762\u72ec\u76ee\u4e2d\u6709\u5982\u5229\u5251\u822c\u56db\u4e0b\u626b\u52a8\u7684\u773c\u795e\u76f8\u6620\uff0c\u66f4\u4f7f\u4ed6\u5168\u8eab\u90fd\u6563\u53d1\u7740\u4e00\u79cd \u4e0d\u53ef\u6297\u62d2\u7684\u9556\u608d\u9e37\u731b\u4e4b\u6c14\u3002 \u3000\u3000\u5982\u6b64\u4e00\u6761\u5927\u6c49\uff0c\u5982\u6b64\u60ca\u4eba\u7684\u795e\u529b\uff0c\u4e0d\u4f46\u201c\u5feb\u9a6c\u201d\u7a0b\u4e03\u89c1\u4e86\u4e3a\u4e4b\u5927\u60ca\uff0c\u76ee\u4e0b\u7684\u8def\u4eba\uff0c\u66f4\u662f \u4eba\u4eba\u9762\u5982\u571f\u8272\u3002 \u3000\u3000\u60ca\u547c\u58f0\u4e00\u505c\uff0c\u8857\u9053\u4e0a\u6240\u6709\u7684\u5e02\u58f0\u4e5f\u4ff1\u90fd\u968f\u4e4b\u5bc2\u7136\u3002 \u3000\u3000\u9ed1\u8863\u5934\u9640\u72ec\u76ee\u4e00\u626b\uff0c\u6d53\u7709\u5251\u8f69\uff0c\u5389\u559d\u53c8\u9053\uff1a\u201c\u4f60\u662f\u804b\u5b50\u8fd8\u662f\u54d1\u5427\uff0c\u83ab\u975e\u6ca1\u542c\u5230\u6d12\u5bb6\u7684\u8bdd \u4e48\uff1f\u201d \u3000\u3000\u201c\u5feb\u9a6c\u201d\u7a0b\u4e03\u5e72\u54b3\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5927\u5e08\u4f11\u2014\u2014\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u53ea\u542c\u8f66\u53a2\u4e2d\u4f20\u51fa\u4e00\u9635\u6e05\u6717\u7684\u8bed \u58f0\uff1a\u201c\u7a0b\u4e03\uff0c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u8f66\u95e8\u7f13\u7f13\u542f\u5f00\u4e00\u7ebf\uff0c\u8f66\u53a2\u4e2d\u4fe1\u6b65\u8d70\u51fa\u4e00\u4e2a\u8f7b\u888d\u7f13\u5e26\uff0c\u4e30\u795e\u5982\u7389\u7684\u5f31\u51a0\u5c11\u5e74\uff0c\u660e\u4eae\u7684\u773c\u795e \u56db\u4e0b\u4e00\u626b\uff0c\u7709\u5b57\u95f4\u4e5f\u4e0d\u7981\u6cdb\u8d77\u4e86\u4e00\u4e9b\u60ca\u8be7\u4e4b\u610f\uff0c\u4f46\u77ac\u5373\u5fae\u5fae\u4e00\u7b11\uff0c\u5fae\u4e00\u62b1\u62f3\uff0c\u6717\u58f0\u8bf4\u9053\uff1a \u201c\u5927\u5e08\u7684\u60ca\u4eba\u795e\u529b\uff0c\u53e4\u4e4b\u9738\u738b\u60f3\u6765\u4ea6\u4e0d\u8fc7\u5982\u6b64\u800c\u5df2\uff01\u201d \u3000\u3000\u4ed6\u867d\u7136\u9762\u5e26\u5fae\u7b11\uff0c\u4f46\u8a00\u8bed\u795e\u60c5\u4e4b\u4e2d\uff0c\u5374\u81ea\u6709\u4e00\u79cd\u9ad8\u8d35\u6e05\u534e\u4e4b\u6c14\uff0c\u5c31\u6b63\u5982\u6625\u65e5\u7684\u9633\u5149\uff0c\u867d \u7136\u548c\u7166\u6e29\u6696\uff0c\u5374\u4ecd\u6559\u4eba\u4e0d\u6562\u903c\u89c6\u3002 \u3000\u3000\u9ed1\u8863\u5934\u9640\u72ec\u76ee\u4e00\u5f20\uff0c\u4e0a\u4e0b\u4ed4\u7ec6\u7aef\u8be6\u4e86\u4ed6\u51e0\u773c\uff0c\u7a81\u5730\u677e\u5f00\u624b\u638c\uff0c\u5927\u6b65\u8d70\u5230\u4ed6\u9762\u524d\uff0c\u5927\u58f0\u559d \u9053\uff1a\u201c\u4f60\u5c31\u662f\u8fd9\u8f86\u9a6c\u8f66\u7684\u4e3b\u4eba\u4e48\uff1f\u201d \u3000\u3000\u8fd9\u4e00\u58f0\u5927\u559d\u5f53\u771f\u662f\u58f0\u5982\u9739\u96f3\uff0c\u56db\u4e0b\u4eba\u7fa4\uff0c\u90fd\u60c5\u4e0d\u81ea\u7981\u5730\u540e\u9000\u4e00\u6b65\uff0c\u4f46\u8fd9\u8f7b\u888d\u7f13\u5e26\u5c11\u5e74\u5374 \u4ecd\u7136\u9762\u542b\u5fae\u7b11\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u2018\u7f2a\u6587\u201d\u6b63\u662f\u8fd9\u51e0\u5339\u9a8f\u9a6c\u4e4b\u4e3b\u2014\u2014\u201d\u9ed1\u8863\u5934\u9640\u6d53\u7709\u4e00\u8f69\uff0c\u5927\u58f0 \u9053\uff1a\u201c\u7eb5\u9a6c\u95f9\u5e02\uff0c\u8086\u610f\u4f24\u4eba\uff0c\u4f60\u51ed\u7740\u4ec0\u4e48\uff0c\u7adf\u6562\u5982\u6b64\u7316\u72c2\uff1f\u201d \u3000\u3000\u9526\u8863\u5c11\u5e74\u201c\u7f2a\u6587\u201d\u5fae\u7b11\u9053\uff1a\u201c\u8086\u610f\u4f24\u4eba\uff1f\u4e0d\u6562\u8bf7\u6559\u5927\u5e08\uff0c\u5728\u4e0b\u53ef\u66fe\u4f24\u4e86\u8c01\u4e48\uff1f\u201d \u3000\u3000\u9ed1\u8863\u5934\u9640\u5fae\u5fae\u4e00\u6014\uff0c\u7a81\u5730\u4ef0\u5929\u957f\u7b11\u8d77\u6765\uff0c\u72c2\u7b11\u7740\u9053\uff1a\u201c\u7b97\u4f60\u8d70\u8fd0\uff0c\u4e0d\u4f46\u6709\u5982\u6b64\u597d\u9a6c\uff0c\u5982 \u6b64\u9a6c\u592b\uff0c\u8fd8\u6709\u4e00\u5f20\u80fd\u8a00\u5584\u8fa9\u7684\u5634\uff0c\u53ea\u602a\u6d12\u5bb6\u65b9\u624d\u4e0d\u66fe\u7b49\u4f60\u4f24\u4e86\u4eba\u540e\u518d\u6293\u4f4f\u4f60\u3002\u201d \u3000\u3000\u7a81\u5730\u4f38\u51fa\u5de8\u638c\uff0c\u5728\u201c\u7f2a\u6587\u201d\u80a9\u5934\u4e00\u62cd\uff0c\u72c2\u7b11\u53c8\u9053\uff1a\u201c\u8001\u5b9e\u544a\u8bc9\u4f60\uff0c\u6d12\u5bb6\u7231\u7684\u8fd8\u662f\u4f60\u8fd9\u4efd \u80c6\u6c14\uff0c\u5426\u5219\u6d12\u5bb6\u5e73\u767d\u8d39\u4e86\u8fd9\u8bb8\u591a\u6c14\u529b\uff0c\u5c82\u80af\u968f\u4fbf\u4fbf\u653e\u8fc7\u4f60\u3002\u201d \u3000\u3000\u72c2\u7b11\u672a\u4f4f\uff0c\u8fd9\u72ec\u81ea\u72ec\u81c2\uff0c\u5200\u75a4\u6273\u866c\uff0c\u9ed1\u8863\u957f\u53d1\u7684\u5947\u602a\u5934\u9640\uff0c\u7adf\u5df1\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u76ee\u5149\u4e00\u8f6c\uff0c\u7a81\u5730\u6717\u58f0\u9053\uff1a\u201c\u5927\u5e08\u7559\u6b65\uff01\u201d \u3000\u3000\u9ed1\u8863\u5934\u9640\u970d\u7136\u8f6c\u8fc7\u8eab\u6765\uff0c\u201c\u7f2a\u6587\u201d\u63a5\u53e3\u9053\uff1a\u201c\u65e5\u8272\u5df2\u66ae\uff0c\u6625\u5bd2\u6599\u5ced\uff0c\u5927\u5e08\u82e5\u65e0\u6025\u4e8b\uff0c\u4f55 \u4e0d\u4e0a\u697c\u540c\u996e\u4e00\u676f\uff1f\u201d\u3002 \u3000\u3000\u9ed1\u8863\u5934\u9640\u4e00\u634b\u989d\u4e0b\u6839\u6839\u89c1\u8089\u7684\u94c1\u866c\uff0c\u4ef0\u5929\u7b11\u9053\uff1a\u201c\u6709\u8da3\u6709\u8da3\uff0c\u4e8c\u5341\u5e74\u4e0d\u5230\u6c5f\u5357\uff0c\u60f3\u4e0d\u5230 \u4eca\u65e5\u7adf\u9047\u7740\u4f60\u8fd9\u822c\u6709\u8da3\u7684\u5c11\u5e74\uff0c\u6765\u6765\uff0c\u5c31\u559d\u4f60\u4e09\u676f\u3002\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u4e00\u9762\u542b\u7b11\u63d6\u5ba2\uff0c\u4e00\u9762\u5411\u201c\u5feb\u9a6c\u201d\u7a0b\u4e03\u6253\u4e86\u4e2a\u773c\u8272\uff0c\u867d\u672a\u8a00\u8bed\uff0c\u8a00\u4e0b\u4e4b\u610f\u81ea\u662f\u8981 \u7a0b\u4e03\u53bb\u6253\u63a2\u8fd9\u9ed1\u8863\u5934\u9640\u7684\u6765\u5386\u3002 \u3000\u3000\u6625\u5bd2\u867d\u4ecd\u5341\u5206\u6599\u5ced\uff0c\u4f46\u82e5\u8981\u996e\u9152\uff0c\u4f55\u60a3\u65e0\u8bcd\uff0c\u662f\u4ee5\u5047\u201c\u6321\u5bd2\u201d\u4e3a\u540d\u4e0a\u697c\u996e\u9152\u7684\uff0c\u4ecd\u5927\u6709 \u4eba\u5728\u3002 \u3000\u3000\u65e5\u8272\u672a\u66ae\uff0c\u9152\u697c\u4e0a\u5df2\u662f\u9ad8\u670b\u6ee1\u5ea7\uff0c\u201c\u7f2a\u6587\u201d\u4e0e\u9ed1\u8863\u5934\u9640\u5360\u4e86\u680f\u65c1\u4e00\u5e2d\u96c5\u5ea7\uff0c\u4e09\u676f\u8fc7\u540e\uff0c \u9ed1\u8863\u5934\u9640\u4fbf\u5df2\u7eb5\u5174\u7545\u8c08\u8d77\u6765\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u4e00\u4e2a\u7c97\u83bd\uff0c\u4e00\u4e2a\u65af\u6587\uff0c\u4e00\u4e2a\u51f6\u4e11\uff0c\u4e00\u4e2a\u6e05\u4fca\uff0c\u81ea\u7136\u5438\u5f15\u4e86\u6ee1\u697c\u9152\u5ba2\u7684\u76ee\u5149\uff0c\u4eba\u4eba \u4ff1\u5728\u6697\u4e2d\u60ca\u5f02\u3002 \u3000\u3000\u201c\u8fd9\u4e24\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u4f7f\u201c\u7f2a\u6587\u201d\u5fc3\u4e2d\u60ca\u5f02\u7684\uff0c\u5374\u662f\u8fd9\u9ed1\u8863\u5934\u9640\u4e0d\u4f46\u795e\u529b\u60ca\u4eba\uff0c\u800c\u4e14\u89c1\u95fb\u6e0a\u535a\uff0c\u5b66\u8bc6\u6781\u4e30\uff0c\u81ea \u6c5f\u5357\u81f3\u585e\u5916\uff0c\u81ea\u9ec4\u6cb3\u81f3\u5929\u5c71\uff0c\u4ed6\u4eff\u4f5b\u90fd\u66fe\u53bb\u8fc7\uff0c\u4f46\u201c\u7f2a\u6587\u201d\u5076\u4e00\u95ee\u53ca\u4ed6\u7684\u6765\u5386\uff0c\u4ed6\u7acb\u523b\u4e71\u4ee5 \u4ed6\u8bed\uff0c\u751f\u50cf\u4ed6\u8eab\u4e16\u4e4b\u4e2d\uff0c\u9690\u542b\u7740\u4ec0\u4e48\u7edd\u5927\u7684\u9690\u79d8\u3002 \u3000\u3000\u76ee\u5149\u626b\u5904\uff0c\u201c\u5feb\u9a6c\u201d\u7a0b\u4e03\u5728\u697c\u5934\u4e00\u6643\uff0c\u201c\u7f2a\u6587\u201d\u7acb\u523b\u85c9\u6545\u79bb\u5e2d\uff0c\u5306\u5306\u4e0b\u697c\uff0c\u201c\u5feb\u9a6c\u201d\u7a0b \u4e03\u7acb\u523b\u8fce\u4e86\u4e0a\u6765\uff0c\u6084\u58f0\u9053\uff1a\u201c\u5c0f\u4eba\u65b9\u624d\u95ee\u8fc7\u5609\u5174\u5730\u9762\u4e0a\u7684\u5144\u5f1f\uff0c\u77e5\u9053\u8fd9\u5934\u9640\u6628\u591c\u624d\u6765\uff0c\u4e5f\u4e0d \u6295\u5bbf\uff0c\u4e5f\u4e0d\u6293\u5355\uff0c\u5374\u996e\u9152\u996e\u4e86\u4e00\u591c\uff0c\u4e5f\u4e0d\u89c1\u9189\uff0c\u522b\u4eba\u95f4\u4ed6\u59d3\u540d\uff0c\u4ed6\u4fbf\u81ea\u79f0\u2018\u4e71\u53d1\u5934\u9640\u2019\uff0c\u6e05 \u6668\u540e\u4fbf\u53bb\u5609\u5174\u57ce\u3001\u56db\u90ca\u8f6c\u4e86\u4e00\u5708\uff0c\u4eff\u4f5b\u5728\u6253\u542c\u4ec0\u4e48\u4eba\u7684\u884c\u85cf\u4f3c\u7684\u3002\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u53cc\u7709\u5fae\u76b1\uff0c\u6c89\u541f\u9053\uff1a\u201c\u4f60\u4e45\u8d70\u6c5f\u6e56\uff0c\u53ef\u66fe\u542c\u89c1\u6b66\u6797\u4e2d\u6709\u8fd9\u6837\u4e00\u4f4d\u4eba\u7269\uff1f\u201d \u3000\u3000\u201c\u5feb\u9a6c\u201d\u7a0b\u4e03\u7acb\u523b\u6447\u5934\u9053\uff1a\u201c\u4e0d\u66fe\uff0c\u53ea\u8981\u4ed6\u5728\u6c5f\u6e56\u4e2d\u7a0d\u6709\u2018\u4e07\u513f\u2019\uff0c\u4fbf\u518d\u96be\u9003\u5f97\u8fc7\u6211\u4eec \u7684\u8033\u76ee\u3002\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u53cc\u7709\u76b1\u5f97\u66f4\u7d27\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u8fd9\u5012\u602a\u4e86\uff0c\u6b64\u4eba\u4e0d\u4f46\u4e00\u8eab\u795e\u529b\u53ef\u60ca\u4e16\u9a87\u4fd7\uff0c\u800c\u4e14\u89c1 \u95fb\u6781\u6df1\uff0c\u771f\u4f1a\u662f\u6c5f\u6e56\u4e2d\u65e0\u540d\u4e4b\u8f88\u2026\u2026\u4f46\u4ed6\u751f\u5177\u5982\u6b64\u5f02\u50cf\uff0c\u53c8\u662f\u6b8b\u5e9f\uff0c\u6240\u5230\u4e4b\u5904\uff0c\u5fc5\u5b9a\u5341\u5206\u89e6 \u76ee\uff0c\u82e5\u662f\u4ed6\u7a0d\u6709\u540d\u58f0\uff0c\u522b\u4eba\u770b\u8fc7\u4e00\u773c\u53c8\u600e\u4f1a\u5fd8\u8bdd\u58f0\u672a\u4e86\uff0c\u7a81\u89c1\u4e00\u4e2a\u7070\u888d\u8292\u978b\uff0c\u8170\u60ac\u957f\u5251\uff0c\u4e4c \u7c2a\u9ad8\u9afb\u7684\u5c11\u5e74\u9053\u4eba\uff0c\u81ea\u4ed6\u8eab\u540e\u8d70\u8fc7\uff0c\u811a\u6b65\u4e4b\u8f7b\uff0c\u6709\u5982\u98de\u82b1\u843d\u53f6\uff0c\u8d70\u8fc7\u201d\u7f2a\u6587\u201d\u8eab\u4fa7\u65f6\uff0c\u56de\u9996 \u671b\u4e86\u4ed6\u4e00\u773c\uff0c\u76ee\u5149\u4e4b\u4e2d\uff0c\u9690\u542b\u7b11\u610f\uff0c\u201c\u7f2a\u6587\u201d\u5fc3\u5934\u65b9\u81ea\u4e00\u52a8\uff0c\u8fd9\u7070\u888d\u9053\u4eba\u5374\u5df2\u98d8\u7136\u800c\u53bb\uff0c\u970e \u773c\u95f4\u4fbf\u6d88\u5931\u5728\u591c\u5e02\u91cc\u3002 \u3000\u3000\u4ed6\u884c\u8def\u770b\u4f3c\u5341\u5206\u4ece\u5bb9\uff0c\u5176\u5b9e\u5374\u6781\u4e3a\u8fc5\u5feb\uff0c\u82e5\u975e\u8f7b\u529f\u8d85\u4eba\u4e00\u7b49\uff0c\u8c01\u4e5f\u4e0d\u4f1a\u6709\u8fd9\u6837\u7684\u6b65\u5c65\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u76ee\u5149\u4e00\u626b\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u8fd9\u9053\u4eba\u4f60\u53ef\u66fe\u89c1\u8fc7\u4ed6\u4e48\uff1f\u201d \u3000\u3000\u201c\u5feb\u9a6c\u201d\u7a0b\u4e03\u76b1\u7709\u9053\uff1a\u201c\u6b66\u6797\u4e2d\u4f69\u5251\u7684\u9053\u4eba\uff0c\u9664\u4e86\u2018\u6b66\u5f53\u2019\u5f1f\u5b50\u5916\uff0c\u8fd8\u4e0d\u591a\u89c1\uff0c\u4f46\u6b66\u5f53 \u9053\u4eba\u4ff1\u662f\u84dd\u888d\uff0c\u4f3c\u8fd9\u6837\u8eab\u7a7f\u6de1\u7070\u9053\u888d\u7684\u4f69\u5251\u9053\u4eba\uff0c\u5c0f\u4eba\u4e00\u65f6\u4e5f\u60f3\u4e0d\u51fa\u4ed6\u7684\u6765\u5386\u3002\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u6f2b\u5e94\u4e00\u58f0\uff0c\u7f13\u6b65\u767b\u697c\uff0c\u5fc3\u4e2d\u5374\u5728\u4e0d\u4f4f\u6697\u5730\u5bfb\u601d\uff1a\u201c\u8fd9\u4e00\u50e7\u4e00\u9053\uff0c\u770b\u6765\u4ff1\u975e\u5e38 \u4eba\uff0c\u4f46\u5374\u53c8\u6765\u5386\u4e0d\u660e\uff0c\u600e\u5730\u4f1a\u4e00\u9f50\u5728\u8fd9\u5609\u5174\u57ce\u91cc\u73b0\u4e86\u8e2a\u8ff9\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u62ac\u52a8\uff0c\u53ea\u89c1\u90a3\u201c\u4e71\u53d1\u5934\u9640\u201d\u6b64\u523b\u6b63\u51ed\u680f\u7a97\u5916\uff0c\u76ee\u5149\u4e0d\u5f80\u5f80\u6765\u626b\u52a8\uff0c\u4f3c\u4e4e\u4e5f\u5728\u641c\u5bfb\u7740 \u4ec0\u4e48\u4eba\u4f3c\u7684\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u5e72\u54b3\u4e00\u58f0\uff0c\u9ed1\u8863\u5934\u9640\u56de\u8f6c\u8eab\u540e\uff0c\u6d53\u7709\u7adf\u4e5f\u6df1\u6df1\u76b1\u5728\u4e00\u5904\uff0c\u5fae\u4e00\u6c89\u541f\uff0c\u6c89\u58f0\u8bf4 \u9053\uff1a\u201c\u65b9\u624d\u6709\u4e2a\u8eab\u7a7f\u94f6\u7070\u8863\u886b\u7684\u9053\u4eba\uff0c\u4f60\u53ef\u770b\u89c1\u4e86\u4e48\uff1f\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u5fc3\u4e2d\u4e00\u52a8\uff0c\u9053\uff1a\u201c\u8fd9\u9053\u4eba\u83ab\u975e\u6709\u4ec0\u4e48\u5947\u5f02\u4e4b\u5904\u4e48\uff1f\u201d \u3000\u3000\u4e71\u53d1\u5934\u9640\u76b1\u7709\u9053\uff1a\u201c\u6c5f\u6e56\u4e2d\u8eab\u7a7f\u8fd9\u6837\u94f6\u7070\u8863\u886b\u7684\u4f69\u5251\u9053\u4eba\uff0c\u6614\u5e74\u4eff\u4f5b\u53ea\u6709\u2018\u534e\u5c71\u2019\u4e00 \u6d3e\uff0c\u800c\u4e14\u8fd8\u8981\u662f\u6d3e\u4e2d\u4e00\u7ea7\u5251\u624b\uff0c\u4f46\u2018\u534e\u5c71\uff0c\u5251\u6d3e\u6570\u5341\u5e74\u6765\u58f0\u52bf\u6781\u4e3a\u6d88\u6c89\uff0c\u6d12\u5bb6\u5f53\u771f\u731c\u4e0d\u900f\u8fd9 \u5609\u5174\u57ce\u4e2d\u600e\u5730\u4f1a\u7a81\u5730\u51fa\u73b0\u534e\u5c71\u4e00\u7ea7\u5251\u624b\u7684\u8e2a\u8ff9\u3002\u201d\u201c\u7f2a\u6587\u201d\u5fc3\u4e2d\u4ea6\u81ea\u5927\u4e3a\u5947\u602a\uff0c\u53ea\u89c1\u8fd9\u4e71\u53d1 \u5934\u9640\u4ef0\u9996\u53c8\u5e72\u4e86\u4e00\u676f\u70c8\u9152\uff0c\u65b9\u81ea\u63a5\u53e3\u8bf4\u9053\uff1a\u201c\u6d12\u5bb6\u4e00\u8def\u884c\u6765\uff0c\u4f3c\u8fd9\u6837\u884c\u8e2a\u4e0d\u660e\u7684\u6b66\u6797\u9ad8\u624b\uff0c \u4f3c\u4e4e\u5df2\u6709\u591a\u8d77\uff0c\u4ff1\u662f\u5385\u8272\u5306\u5306\uff0c\u5404\u6709\u5fc3\u4e8b\uff0c\u5374\u4e0d\u77e5\u8fd9\u4e9b\u4eba\u7a76\u7adf\u662f\u4f55\u6765\u5386\uff0c\u6709\u4f55\u610f\u56fe\uff1f\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u5fcd\u4e0d\u4f4f\u63a5\u53e3\u9053\uff1a\u201c\u5728\u522b\u4eba\u773c\u4e2d\u770b\u6765\uff0c\u5927\u5e08\u5c82\u975e\u4e5f\u662f\u5176\u4e2d\u4e4b\u4e00\u3002\u201d \u3000\u3000\u4e71\u53d1\u5934\u9640\u6014\u4e86\u4e00\u6014\uff0c\u4ef0\u5929\u72c2\u7b11\u9053\uff1a\u201c\u6d12\u5bb6\u53ea\u4e0d\u8fc7\u7a7a\u6709\u4e24\u8180\u6c14\u529b\uff0c\u7b97\u5f97\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4ef0\u9996\u53c8\u5e72\u4e00\u676f\uff0c\u72c2\u7b11\u4e4b\u58f0\u4e0d\u7edd\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u6d45\u6d45\u555c\u9152\uff0c\u795e\u8272\u4e0d\u53d8\uff0c\u53ea\u7b49\u4ed6\u72c2\u7b11\u58f0\u4f4f\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u8fd1\u6765\u6c5f\u5357\u4fa0\u8e2a\u9690\u73b0\uff0c\u53ea \u6015\u4e0e\u2018\u7075\u86c7\u2019\u6bdb\u81ec\u7684\u2018\u82f1\u96c4\u5927\u4f1a\u2019\u6709\u5173\uff0c\u4e0d\u77e5\u5927\u5e08\u662f\u5426\u4e5f\u4e3a\u4e86\u6b64\u4e8b\u800c\u6765\uff1f\u201d \u3000\u3000\u201c\u4e71\u53d1\u5934\u9640\u201d\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u6bdb\u81ec\u7684\u82f1\u96c4\u4f1a\u7b97\u5f97\u4e86\u4ec0\u4e48\uff01\u6d12\u5bb6\u600e\u4f1a\u2014\u2014\u201d\u8bdd\u58f0\u7a81\u987f\uff0c\u7b11 \u58f0\u4e5f\u7a81\u987f\uff0c\u9762\u4e0a\u795e\u8272\uff0c\u968f\u4e4b\u4e00\u53d8\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4f60\u65e2\u975e\u6b66\u6797\u4e2d\u4eba\uff0c\u600e\u4f1a\u5bf9\u6b66\u6797\u4e2d\u4e8b\u5982\u6b64\u6e05 \u695a\uff1f\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u6301\u676f\u542b\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u867d\u975e\u6b66\u6797\u4e2d\u4eba\uff0c\u5374\u6709\u5e78\u4e0e\u4e00\u4e9b\u6b66\u6797\u4fa0\u58eb\u4e3a\u53cb\uff0c\u5e73\u65e5\u8a00\u8c08\u6240 \u4e3a\uff0c\u6b66\u6797\u95f4\u4e8b\uff0c\u5728\u4e0b\u4e5f\u9887\u4e3a\u77e5\u9053\u4e00\u4e9b\u3002\u201d \u3000\u3000\u201c\u4e71\u53d1\u5934\u9640\u201d\u72ec\u76ee\u4e4b\u4e2d\uff0c\u5149\u8292\u95ea\u52a8\uff0c\u7a81\u5730\u6c89\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u65e2\u4e45\u5c45\u6c5f\u5357\uff0c\u53c8\u5e38\u4e0e\u6e38\u4fa0\u4e3a \u4f0d\uff0c\u53ef\u66fe\u542c\u5230\u8fc7\u6709\u4e00\u4e2a\u6765\u81ea\u585e\u5916\u7684\u72ec\u81c2\u8001\u4eba\uff0c\u8fd1\u65e5\u5728\u6c5f\u5357\u884c\u52a8\uff1f\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u76ee\u5149\u8f6c\u5904\uff0c\u53ea\u89c1\u8fd9\u201c\u4e71\u53d1\u5934\u9640\u201d\u95ee\u5230\u8fd9\u53e5\u8bdd\u65f6\uff0c\u795e\u8272\u7a81\u5730\u53d8\u5f97\u5341\u5206\u614e\u91cd\uff0c\u4e0d\u7981 \u6c89\u541f\u9053\uff1a\u201c\u5927\u5e08\u6765\u5230\u6c5f\u5357\uff0c\u53ef\u5c31\u662f\u4e3a\u4e86\u6b64\u4e8b\u4e48\uff1f\u201d \u3000\u3000\u201c\u4e71\u53d1\u5934\u9640\u201d\u76ee\u5149\u4e2d\u7a81\u9732\u51fa\u4e00\u9635\u51c4\u51c9\u60b2\u54c0\u7684\u795e\u8272\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u6d12\u5bb6\u4e0e\u6b64\u4eba\u5df2\u6709\u4e8c\u5341\u5e74\u4e0d \u89c1\uff0c\u672c\u6765\u8fd8\u4e0d\u77e5\u4ed6\u7684\u751f\u6b7b\uff0c\u8fd1\u5e74\u6765\u624d\u542c\u4e00\u4eba\u8bf4\u8d77\uff0c\u4ed6\u5df2\u5728\u585e\u5916\u6210\u5c31\u4e86\u4e00\u756a\u4e8b\u4e1a\uff0c\u4f46\u6d12\u5bb6\u8d76\u5230 \u7389\u95e8\u5173\u5916\u65f6\uff0c\u5374\u542c\u95fb\u6b64\u4eba\u5df2\u5230\u4e86\u6c5f\u5357\uff0c\u6765\u5bfb\u627e\u4e00\u4e2a\u4eba\u7684\u884c\u8e2a\u3002\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u5fcd\u4e0d\u4f4f\u8131\u53e3\u9053\uff1a\u201c\u627e\u8c01\uff1f\u2026\u201d\u4e71\u53d1\u5934\u9640\u201d\u76ee\u5149\u4e2d\u592e\u5730\u795e\u5149\u66b4\u73b0\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4e00 \u4e2a\u4ec7\u4eba\u7684\u540e\u8bf4\u5230\u8fd9\u91cc\uff0c\u4ed6\u4f3c\u4e4e\u7a81\u7136\u53d1\u73b0\u81ea\u5df1\u8bf4\u5f97\u5927\u591a\uff0c\u6d53\u7709\u4e00\u76b1\uff0c\u8bdd\u950b\u7acb\u8f6c\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4f60 \u82e5\u77e5\u9053\u8fd9\u8001\u4eba\u7684\u884c\u8ff9\uff0c\u4fbf\u5feb\u544a\u8bc9\u6211\uff0c\u4f60\u82e5\u4e0d\u77e5\uff0c\u591a\u95ee\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u5fc3\u4e2d\u6697\u7b11\uff1a\u201c\u8fd9\u5934\u9640\u7684\u66b4\u8e81\u7684\u813e\u6c14\uff0c\u6c42\u4eba\u4e4b\u65f6\uff0c\u5c1a\u4e14\u5982\u6b64\uff0c\u5982\u4e0d\u6c42\u4eba\u65f6\uff0c\u8fd8\u6709 \u8c01\u6562\u62db\u60f9\u8c03\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u53c8\u5fd6\u9053\uff1a\u201c\u4f46\u6b64\u4eba\u4e24\u81c2\u795e\u529b\uff0c\u5374\u662f\u9a87\u4eba\u542c\u95fb\uff0c\u82e5\u80fd\u5584\u52a0\u5229\u7528\u2026\u2026\u201d \u3000\u3000\u4e00\u5ff5\u5230\u6b64\uff0c\u542b\u7b11\u8bf4\u9053\uff1aI\u201c\u5728\u4e0b\u6b64\u523b\u867d\u7136\u8fd8\u4e0d\u77e5\u9053\uff0c\u4f46\u53ea\u8981\u5927\u5e08\u6240\u8bf4\u4e4b\u4eba\u786e\u5728\u6c5f\u5357\uff0c\u5728 \u4e0b\u4fbf\u6709\u628a\u63e1\u5728\u4e00\u6708\u4e4b\u5185\u5c06\u4ed6\u7684\u884c\u8ff9\u67e5\u51fa\u3002\u201d \u3000\u3000\u201c\u4e71\u53d1\u5934\u9640\u201d\u7cbe\u795e\u4e00\u632f\uff0c\u9053\uff1a\u201c\u771f\u7684\u4e48\uff1f\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u5c82\u6562\u4ee5\u865a\u8a00\u76f8\u6b3a\uff0c\u53ea\u4e0d\u77e5\u6b64\u4eba\u6709\u4f55\u7279\u5fbd\uff0c\u591a\u5927\u5e74\u7eaa\u3002\u201d \u3000\u3000\u201c\u4e71\u53d1\u5934\u9640\u201d\u76ee\u5149\u53c8\u81ea\u5782\u843d\uff0c\u6ee1\u9762\u4ff1\u90fd\u6362\u4e86\u8427\u7d22\u51c4\u51c9\u4e4b\u610f\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u6b64\u4eba\u4eca\u5e74\u5df2\u516d\u5341 \u5f00\u5916\uff0c\u8eab\u6750\u9ad8\u5927\uff0c\u58f0\u5982\u6d2a\u949f\uff0c\u4ea6\u662f\u65ad\u53bb\u4e86\u4e00\u6761\u53f3\u81c2\uff0c\u9aa4\u773c\u770b\u6765\uff0c\u6709\u51e0\u5206\u4e0e\u6d12\u5bb6\u76f8\u4f3c\u3002\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u5fc3\u4e2d\u53c8\u4e00\u52a8\uff0c\u53e3\u53e3\u542b\u7b11\u9053\uff1a\u201c\u6b64\u4eba\u82e5\u662f\u8fd9\u822c\u89e6\u76ee\uff0c\u5bfb\u8bbf\u5c31\u66f4\u975e\u96be\u4e8b\u4e86\u3002\u201d \u3000\u3000\u201c\u4e71\u53d1\u5934\u9640\u201d\u957f\u53f9\u4e00\u58f0\uff0c\u7a81\u53c8\u5927\u7b11\u9053\uff1a\u201c\u82e5\u662f\u5982\u6b64\uff0c\u6d12\u5bb6\u8fd9\u4e00\u4e2a\u6708\u91cc\u5c31\u8ddf\u5b9a\u4f60\u4e86\u3002\u201d \u7b2c\u4e8c\u5341\u4e8c\u7ae0>> \u53e4\u9f99\u300a\u6e58\u5983\u5251\u300b \u7b2c\u4e8c\u5341\u4e8c\u7ae0 \u3000\u3000\u66ae\u6625\u65f6\u8282\uff0c\u83ba\u98de\u8349\u821e\u3002 \u3000\u3000\u6f2b\u5929\u671d\u971e\u4e2d\uff0c\u767d\u9a6c\u94f6\u8f66\uff0c\u6025\u9a70\u51fa\u57ce\uff0c\u76f4\u5954\u676d\u5dde\u3002 \u3000\u3000\u4f46\u89c1\u9053\u8def\u4e24\u65c1\uff0c\u6851\u884c\u904d\u91ce\uff0c\u9e21\u72ac\u76f8\u95fb\u3002\u8fdc\u8fdc\u671b\u53bb\uff0c\u4e00\u7247\u7eff\u8272\u7684\u5929\u5730\u4e2d\uff0c\u70b9\u7f00\u7740\u4e09\u4e94\u95f4\u8305 \u820d\u4eba\u5bb6\uff0c\u5076\u800c\u6709\u4e09\u4e94\u4e2a\u660e\u7738\u7693\u9f7f\u7684\u6c5f\u5357\u5c11\u5973\uff0c\u8d64\u7740\u4e00\u53cc\u5929\u8db3\uff0c\u8e0f\u7740\u7530\u91cc\u7684\u6c34\u6ce2\uff0c\u66fc\u58f0\u4f4e\u5531\u7740 \u76f8\u601d\u7684\u60c5\u6b4c\u3002\u5374\u4e0d\u77e5\u5979\u5230\u5e95\u76f8\u601d\u7684\u5bf9\u8c61\u662f\u8c01\uff1f \u3000\u3000\u201c\u7f2a\u6587\u201d\u534a\u542f\u8f66\u7a97\uff0c\u56db\u4e0b\u773a\u671b\uff0c\u9762\u4e0a\u4e00\u7247\u5b81\u9759\u3002\u6b64\u65f6\u6b64\u523b\uff0c\u8fd9\u5c11\u5e74\u5f53\u771f\u629b\u53bb\u4e86\u5fc3\u4e2d\u7684\u4e07 \u822c\u5fc3\u4e8b\uff0c\u6765\u6b23\u8d4f\u8fd9\u6c5f\u5357\u7684\u7f8e\u666f\u3002 \u3000\u3000\u7b95\u8e1e\u5728\u4ed6\u5bf9\u9762\u7684\u201c\u4e71\u53d1\u5934\u9640\u201d\uff0c\u6000\u4e2d\u5374\u62b1\u7740\u4e00\u53ea\u6731\u7ea2\u7684\u9152\u80e1\u82a6\uff0c\u5728\u54c1\u5c1d\u7740\u6c5f\u5357\u7f8e\u9152\u3002 \u3000\u3000\u51fa\u57ce\u6e10\u8fdc\uff0c\u4eba\u8ff9\u6e10\u7a00\u3002 \u3000\u3000\u7a81\u542c\u4e00\u9635\u5947\u5f02\u800c\u6c89\u91cd\u7684\u8e44\u58f0\uff0c\u81ea\u8fdc\u800c\u8fd1\uff0c\u201c\u4e71\u53d1\u5934\u9640\u201d\u5fcd\u4e0d\u4f4f\u63a2\u9996\u7a97\u5916\uff0c\u53ea\u89c1\u8fdc\u5904\u7adf\u5954 \u6765\u4e24\u5339\u53cc\u5cf0\u9a86\u9a7c\u3002 \u3000\u3000\u9a7c\u5cf0\u4e0a\u659c\u5750\u7740\u7684\uff0c\u7adf\u662f\u4e24\u4e2a\u5bbd\u88d9\u7a84\u8896\uff0c\u7eb1\u4e2d\u63a9\u9762\u7684\u585e\u5916\u4e3d\u4eba\u3002 \u3000\u3000\u8f66\u9a6c\u4e0e\u9a86\u9a7c\u5239\u90a3\u95ee\u4fbf\u4ea4\u9519\u800c\u8fc7\uff0c\u4f46\u5c31\u5728\u8fd9\u5239\u90a3\u95f4\uff0c\u81ea\u90a3\u968f\u98ce\u98d8\u98de\u7684\u7eb1\u4e2d\u91cc\uff0c\u5df2\u53ef\u770b\u89c1\u8fd9 \u4e24\u4e2a\u5973\u5b50\u7684\u660e\u7738\u79cb\u6ce2\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u5fc3\u4e2d\u65b9\u81ea\u6697\u5947\u3002 \u3000\u3000\u8fd9\u8f6f\u98ce\u5f31\u67f3\u7684\u6c5f\u5357\u8def\u4e0a\uff0c\u600e\u4f1a\u6709\u8fd9\u53f7\u79f0\u201c\u6c99\u6f20\u4e4b\u821f\u201d\u7684\u5343\u91cc\u660e\u9a7c\u884c\u8d70\uff0c\u9a7c\u5cf0\u4e0a\u7adf\u8fd8\u5750\u7684 \u662f\u4e24\u4e2a\u4eff\u4f5b\u7edd\u7f8e\u7684\u585e\u5916\u800c\u4eba\u3002 \u3000\u3000\u4ed6\u601d\u5ff5\u65b9\u8f6c\uff0c\u201c\u4e71\u53d1\u5934\u9640\u201d\u5df2\u81ea\u6d53\u7709\u4e00\u626c\uff0c\u7830\u5730\u63a8\u5f00\u4e86\u8f66\u95e8\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u676d\u5dde\u57ce\u89c1\u3002\u201d \u3000\u3000\u8bdd\u58f0\u672a\u4e86\uff01\u5355\u638c\u659c\u7a7f\uff0c\u4fbf\u5df2\u6e38\u9c7c\u822c\u6ed1\u51fa\u8f66\u5916\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u4e0d\u53ca\u5f00\u53e3\uff0c\u53c8\u7709\u5fae\u76b1\uff0c\u53ea\u89c1\u8fd9\u201c\u4e71\u53d1\u5934\u9640\u201d\u7adf\u5df2\u5728\u767d\u663c\u4e4b\u4e2d\uff0c\u5c55\u5f00\u8f7b\u529f\u8eab\u6cd5\uff0c \u7f00\u5728\u90a3\u4e24\u5339\u660e\u9a7c\u4e4b\u540e\uff0c\u5982\u98de\u63a0\u53bb\u3002 \u3000\u3000\u8f66\u9a6c\u7a33\u5feb\uff0c\u4f46\u201c\u7f2a\u6587\u201d\u5fc3\u4e2d\uff0c\u5374\u591a\u4e86\u6ee1\u6000\u7d0a\u4e71\u7684\u5fc3\u4e8b\u3002 \u3000\u3000\u4ed6\u81ea\u5165\u6c5f\u5357\u4e4b\u540e\uff0c\u5bf9\u6bcf\u4ef6\u4e8b\u90fd\u5e03\u7f6e\u5f97\u6781\u4e3a\u5468\u5bc6\uff0c\u4e00\u5207\u4e8b\u7684\u53d1\u751f\uff0c\u90fd\u4e0d\u4f1a\u5f15\u8d77\u4ed6\u7684\u60ca\u5f02\uff0c \u56e0\u4e3a\u6bcf\u4ef6\u4e8b\u4ff1\u90fd\u5728\u4ed6\u7b97\u4e2d\u3002 \u3000\u3000\u4f46\u6b64\u523b\uff0c\u4e71\u53d1\u5934\u9640\u3001\u5c11\u5e74\u9053\u4eba\uff0c\u4ee5\u53ca\u8fd9\u660e\u9a7c\u4f73\u4e3d\u7684\u9aa4\u7136\u51fa\u73b0\uff0c\u5374\u4ff1\u90fd\u662f\u4ed6\u4e0d\u80fd\u7406\u89e3\u731c\u6d4b \u4e4b\u4e8b\u3002 \u3000\u3000\u8fd9\u4e9b\u4e8b\u770b\u6765\u867d\u7136\u4eff\u4f5b\u4e0e\u4ed6\u6beb\u65e0\u5173\u7cfb\uff0c\u4f46\u5947\u602a\u7684\u662f\uff0c\u5728\u4ed6\u5fc3\u5e95\u6df1\u5904\uff0c\u5374\u83ab\u540d\u5176\u5999\u5730\u5bf9\u8fd9\u4ef6 \u4e8b\u751f\u51fa\u4e86\u4e00\u79cd\u5947\u5999\u7684\u60ca\u60d5\u3002 \u3000\u3000\u8f66\u58f0\u8f98\u8f98\uff0c\u5bd2\u98ce\u6ee1\u7a97\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8d70\u4e86\u591a\u4e45\uff0c\u7a81\u542c\u8d76\u8f66\u7684\u201c\u5feb\u9a6c\u201d\u7a0b\u4e03\u60ca\u547c\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4f60\u770b\u8fd9\u91cc\u3002\u201d \u3000\u3000\u8f66\u9a6c\u9aa4\u7f13\uff0c\u201c\u7f2a\u6587\u201d\u4fa7\u8eab\u63a2\u51fa\u7a97\u5916\uff0c\u76ee\u5149\u8f6c\u5904\uff0c\u773c\u524d\u7adf\u662f\u4e00\u7247\u6e05\u6ce2\uff0c\u4e00\u7247\u7fe0\u7eff\u4e4b\u4e2d\uff0c\u9759 \u9759\u5730\u5d4c\u7740\u4e00\u4e2a\u5830\u6708\u5f62\u7684\u6e05\u6c60\uff0c\u5bbd\u7ea6\u56db\u4e94\u4e08\uff0c\u957f\u4e5f\u4e0d\u8fc7\u53ea\u6709\u5341\u4e94\u516d\u4e08\u800c\u5df2\uff0c\u6c34\u5374\u6d41\u5f97\u51fa\u5947\u5730 \u6162\u3002 \u3000\u3000\u56db\u4e0b\u9759\u65e0\u4eba\u58f0\uff0c\u6c60\u6c34\u7684\u5bf9\u5cf0\uff0c\u5374\u8d6b\u7136\u77d7\u7acb\u7740\u4e24\u5ea7\u5f62\u5982\u9992\u9996\u7684\u5e10\u7bf7\uff0c\u4e94\u3001\u4e03\u5339\u9a86\u9a7c\uff0c\u4e5d\u3001 \u5341\u5339\u82b1\u9a6c\uff0c\u60a0\u60a0\u5730\u5728\u6c60\u8fb9\u95f2\u8361\uff0c\u5480\u56bc\u7740\u6c60\u8fb9\u7684\u7eff\u8349\u3002\u9759\u9759\u7684\u78a7\u6ce2\uff0c\u5012\u6620\u7740\u5b83\u4eec\u7684\u8eab\u5f71\uff0c\u9aa4\u773c \u671b\u53bb\uff0c\u4e5f\u4e0d\u77e5\u6c60\u4e2d\u7684\u9a7c\u9a6c\u662f\u771f\u7684\uff0c\u6291\u6216\u662f\u5cb8\u4e0a\u7684\u9a7c\u9a6c\u662f\u771f\u7684\u3002 \u3000\u3000\u53ea\u542c\u5feb\u9a6c\u7a0b\u4e03\u60ca\u559f\u9053\uff1a\u201c\u5947\u602a\uff01\u6c5f\u5357\u5730\u9762\uff0c\u600e\u4f1a\u6709\u8fd9\u585e\u5916\u7684\u2018\u8499\u53e4\u5305\u2019\u624e\u5728\u8fd9\u91cc\uff1f\u6211\u5411 \u6765\u53ea\u95fb\u5f97\u6709\u2018\u585e\u5916\u6c5f\u5357\uff0c\u60f3\u4e0d\u5230\u4eca\u65e5\u7adf\u770b\u5230\u4e86\u6c5f\u5357\u7684\u585e\u5916\u98ce\u7269\u3002\u201d\u8a00\u8bed\u4e4b\u95f4\uff0c\u8f66\u9a6c\u5df2\u505c\u3002 \u201c\u7f2a\u6587\u201d\u4ea6\u662f\u6ee1\u5fc3\u60ca\u8be7\uff0c\u671b\u7740\u8fd9\u5947\u5f02\u7684\u666f\u8c61\uff0c\u4e0d\u89c9\u5446\u5446\u5730\u51fa\u8d77\u795e\u6765\u3002\u4e00\u53ea\u767d\u9e6d\uff0c\u76d8\u65cb\u6c60\u9762\uff0c \u98de\u5f97\u5f88\u4f4e\uff0c\u5ffd\u7136\u201c\u55e4\u201d\u5730\u4e00\u58f0\uff0c\u94bb\u5165\u4e86\u6c34\u6ce2\uff0c\u5544\u8d77\u4e00\u6761\u94f6\u9c7c\u53c8\u55d6\u5730\u98de\u4e86\u4e0a\u53bb\u3002\u6c60\u4e2d\u6d9f\u6f2a\u672a \u6563\uff0c\u5bf9\u5cb8\u5e10\u7bf7\u563b\u7b11\u7740\u8df3\u51fa\u4e00\u4e2a\u9ec4\u8863\u7ae5\u5b50\uff0c\u62cd\u638c\u9053\uff1a\u201c\u6c34\u4e0a\u4e00\u9e33\u98de\uff0c\u6c60\u5e95\u4e07\u9c7c\u60ca\u2026\u2026\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u5fc3\u5934\u4e00\u52a8\uff0c\u6697\u5fd6\u9053\uff1a\u201c\u5c0f\u5c0f\u4e00\u4e2a\u7ae5\u5b50\uff0c\u5df2\u6709\u5982\u6b64\u5410\u5c5e\uff0c\u5e10\u4e2d\u4e3b\u4eba\uff0c\u5b9a\u5fc5\u66f4\u975e\u4fd7 \u5ba2\uff0c\u5947\u602a\u7684\u662f\uff0c\u6c5f\u5357\u5730\u9762\uff0c\u600e\u5730\u5ffd\u7136\u6765\u4e86\u8fd9\u4e48\u591a\u9ad8\u4eba\uff1f\u201d \u3000\u3000\u601d\u5fd6\u4e4b\u95f4\uff0c\u5e10\u7bf7\u4e2d\u53c8\u8d70\u51fa\u4e00\u4e2a\u5bbd\u88d9\u7a84\u8896\uff0c\u7eb1\u4e2d\u8499\u9762\u7684\u5c11\u5973\uff0c\u7adf\u8fdc\u8fdc\u5411\u201c\u7f2a\u6587\u201d\u62db\u8d77\u624b \u6765\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u4e00\u6014\uff0c\u53ea\u542c\u8fd9\u8499\u88c5\u5c11\u5973\u9ad8\u547c\u9053\uff1a\u201c\u5bf9\u9762\u7684\u5ba2\u4eba\uff0c\u8bf7\u4f60\u4e0b\u8f66\u6765\u597d\u4e48\uff1f\u6211\u4eec\u7684\u4e3b\u4eba \u8bf7\u4f60\u5e10\u7bf7\u91cc\u5750\u3002\u201d \u3000\u3000\u8bed\u58f0\u4e4b\u4e2d\uff0c\u867d\u5e26\u7740\u4e00\u79cd\u5947\u5f02\u7684\u53e3\u97f3\uff0c\u4f46\u662f\u5979\u58f0\u5982\u94f6\u94c3\uff0c\u4e0d\u4f46\u63a9\u9970\u4e86\u8fd9\u5947\u5f02\u7684\u53e3\u97f3\uff0c\u8fd8\u663e \u5f97\u683c\u5916\u52a8\u542c\u3002 \u3000\u3000\u547c\u58f0\u4e4b\u4e2d\uff0c\u90a3\u9ec4\u8863\u7ae5\u5b50\u5df2\u7ed5\u7740\u6c60\u5cb8\uff0c\u5feb\u6b65\u8dd1\u4e86\u8fc7\u6765\uff0c\u201c\u7f2a\u6587\u201d\u8fd8\u5728\u60ca\u5947\u8be7\u5f02\u4e4b\u4e2d\uff0c\u8fd9\u7ae5 \u5b50\u5df1\u4e00\u628a\u7275\u4f4f\u4e86\u4ed6\u7684\u8863\u895f\uff0c\u61a8\u7b11\u9053\uff1a\u201c\u597d\u6f02\u4eae\u7684\u9a6c\uff01\u597d\u6f02\u4eae\u7684\u9a6c\u8f66\uff01\u597d\u6f02\u4eae\u7684\u4eba\uff01\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u5c55\u989c\u4e00\u7b11\uff0c\u4fef\u9996\u9053\uff1a\u201c\u5c0f\u5f1f\u5f1f\uff0c\u4f60\u4eec\u7684\u4e3b\u4eba\u662f\u8c01\uff1f\u5524\u6211\u4f5c\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9ec4\u8863\u7ae5\u5b50\u7728\u4e00\u7728\u5927\u773c\u775b\uff0c\u6447\u5934\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\uff0c\u4e5f\u8bb8\u4ed6\u8ba4\u8bc6\u4f60\u3002\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u7709\u5fc3\u5fae\u5fae\u4e00\u76b1\uff0c\u5fc3\u4e2d\u5df2\u5145\u6ee1\u597d\u5947\u4e4b\u5fc3\uff0c\u5fcd\u4e0d\u4f4f\u8d70\u4e0b\u4e86\u8f66\uff0c\u4efb\u51ed\u8fd9\u9ec4\u8863\u7ae5\u5b50\uff0c\u5c06 \u4ed6\u62c9\u5230\u5bf9\u5cb8\u3002 \u3000\u3000\u7eb1\u4e2d\u6380\u52a8\u4e2d\uff0c\u8fd9\u8499\u88c5\u5c11\u5973\u68a8\u6da1\u9690\u73b0\uff0c\u9f7f\u767d\u5982\u7389\uff0c\u5411\u201c\u7f2a\u6587\u201d\u8f7b\u8f7b\u4e00\u62db\u624b\uff0c\u8f6c\u8eab\u5954\u5165\u5e10 \u91cc\uff0c\u4e00\u9762\u5a07\u7b11\u7740\u9053\uff1a\u201c\u8001\u7237\u5b50\uff0c\u5ba2\u4eba\u8fc7\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u5e72\u54b3\u4e00\u58f0\uff0c\u53ea\u542c\u5e10\u4e2d\u4f20\u51fa\u4e00\u58f0\u82cd\u8001\u6c89\u91cd\u7684\u8bed\u58f0\uff0c\u9053\uff1a\u201c\u5916\u9762\u7684\u5ba2\u4eba\u5feb\u8bf7\u8fdb\u6765\uff0c \u6055\u8001\u592b\u884c\u8d70\u4e0d\u4fbf\uff0c\u6709\u5931\u8fdc\u8fce\u3002\u201d \u3000\u3000\u4e00\u53ea\u83b9\u767d\u5982\u7389\u7684\u7ea4\u624b\u4f38\u51fa\u5e10\u7bf7\uff0c\u5c06\u5e10\u5916\u539a\u91cd\u7684\u95e8\u5e18\u6380\u5f00\u4e00\u89d2\uff0c\u90a3\u8499\u88c5\u5c11\u5973\u53c8\u63a2\u51fa\u5934\u6765\uff0c \u5a07\u7b11\u7740\u9053\uff1a\u201c\u8001\u7237\u5b50\u8bf7\u4f60\u8fdb\u6765\u3002\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u56db\u671b\u4e00\u773c\uff0c\u53ea\u89c1\u8fd9\u4e24\u5ea7\u5e10\u7bf7\u5916\u867d\u7136\u9a7c\u9a6c\u6210\u7fa4\uff0c\u5374\u662f\u4e00\u7247\u5b81\u9759\uff0c\u53e6\u4e00\u5ea7\u8f83\u5c0f\u7684\u5e10 \u7bf7\u4e2d\uff0c\u4e0d\u65f6\u98d8\u6563\u51fa\u4e00\u9635\u751c\u7f8e\u7684\u8089\u9999\u3002 \u3000\u3000\u4e00\u773c\u770b\u53bb\uff0c\u5929\u5730\u95f4\u4eff\u4f5b\u5145\u6ee1\u4e86\u548c\u5e73\u4e0e\u6b22\u4e50\u3002 \u3000\u3000\u4ed6\u6697\u4e2d\u5b9a\u4e86\u5b9a\u795e\uff0c\u4fef\u9996\u5411\u90a3\u9ec4\u8863\u7ae5\u5b50\u5fae\u5fae\u4e00\u7b11\uff0c\u8d70\u5165\u5e10\u4e2d\uff0c\u62ac\u8d77\u5934\u6765\uff0c\u76ee\u5149\u4e00\u8f6c\uff0c\u53ea\u89c1 \u8fd9\u5916\u8868\u770b\u6765\u6781\u662f\u7b80\u964b\u7684\u5e10\u7bf7\u4e2d\uff0c\u9648\u8bbe\u5f97\u7adf\u662f\u5bcc\u4e3d\u5802\u7687\u5df2\u6781\uff0c\u56db\u9762\u77ee\u51e0\u4f4e\u51f3\u4e0a\uff0c\u90fd\u8986\u7740\u539a\u539a\u7684 \u864e\u8c79\u4e4b\u76ae\uff0c\u4e0d\u8bf4\u522b\u7684\uff0c\u5355\u51ed\u6b64\u70b9\uff0c\u6559\u4eba\u4e00\u5165\u6b64\u5e10\uff0c\u4fbf\u4e0d\u7981\u7531\u5fc3\u5e95\u5347\u51fa\u4e00\u9635\u6e29\u6696\u4e4b\u610f\u3002 \u3000\u3000\u4e00\u6761\u534e\u4e3d\u7684\u8c79\u76ae\u5782\u5e18\u540e\uff0c\u5e72\u54b3\u4e00\u58f0\uff0c\u7f13\u6b65\u8d70\u51fa\u4e00\u4e2a\u8eab\u62ab\u7d2b\u8272\u98ce\u6c05\uff0c\u8eab\u6750\u4f5d\u507b\uff0c\u6b65\u5c65\u4e5f\u6781 \u4e0d\u7075\u4fbf\u7684\u8001\u4eba\uff0c\u9762\u4e0a\u5374\u8499\u7740\u4e00\u65b9\u7d2b\u8272\u7684\u4e1d\u4e2d\uff0c\u4e1d\u4e2d\u4e0b\u767d\u987b\u8f7b\u62c2\uff0c\u5374\u65e0\u6cd5\u770b\u5f97\u5230\u4ed6\u7684\u9762\u76ee\u3002 \u3000\u3000\u4f46\u9732\u5728\u4e1d\u4e2d\u5916\u7684\u4e24\u53ea\u773c\u775b\uff0c\u5374\u6709\u5982\u660e\u661f\u822c\u5149\u4eae\uff0c\u5200\u5251\u822c\u7684\u9510\u5229\uff0c\u4e0e\u4ed6\u4f5d\u507b\u7684\u8eab\u6750\u4e0e\u8e52\u8dda \u7684\u811a\u6b65\u90fd\u5927\u4e0d\u76f8\u79f0\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u5fc3\u4e2d\u4e0d\u7981\u53c8\u4e3a\u4e4b\u6697\u6697\u79f0\u5947\uff0c\u4f46\u9762\u4e0a\u5374\u4e1d\u6beb\u4e0d\u52a8\u58f0\u8272\u3002 \u3000\u3000\u8499\u9762\u8001\u4eba\u76ee\u5149\u4e00\u626b\uff0c\u5f90\u5f90\u5728\u4e00\u5f20\u864e\u76ae\u69bb\u4e0a\u659c\u5750\u4e86\u4e0b\u6765\uff0c\u7b11\u9053\uff1a\u201c\u5144\u53f0\u53ea\u7ba1\u968f\u610f\u5750\u5427\uff0c\u8bf7 \u6055\u8001\u592b\u65e0\u793c\u3002\u201d \u3000\u3000\u8fd9\u663e\u7136\u662f\u6765\u81ea\u585e\u5916\u7684\u8001\u4eba\uff0c\u8bed\u58f0\u4e2d\u5374\u6ee1\u662f\u6cb3\u5317\u53e3\u97f3\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u5fc3\u4e2d\u601d\u6f6e\u95ea\u52a8\uff0c\u4e00\u9762\u5374\u62f1\u624b\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u7f2a\u6587\uff0c\u8499\u8001\u4e08\u5ba0\u53ec\uff0c\u4e0d\u77e5\u6709\u4f55\u89c1 \u6559\uff1f\u201d \u3000\u3000\u8499\u9762\u8001\u4eba\u8eab\u5f62\u50f5\u5367\uff0c\u53e3\u4e2d\u7b11\u9053\uff1a\u201c\u5750\uff0c\u5750\u2026\u2026\u6843\u59d1\uff0c\u8336\u6765\u3002\u201d \u3000\u3000\u90a3\u8499\u88c5\u5c11\u5973\u201c\u6843\u59d1\u201d\u626d\u52a8\u7740\u7ea4\u8170\uff0c\u8f7b\u76c8\u5730\u8f6c\u5165\u5e18\u540e\uff0c\u9ec4\u8863\u7ae5\u5b50\u5374\u4e0d\u4f4f\u7728\u7740\u5927\u773c\u775b\uff0c\u5446\u5446 \u5730\u5411\u201c\u7f2a\u6587\u201d\u51dd\u89c6\u7740\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u7528\u5c3d\u667a\u6167\uff0c\u4e5f\u731c\u4e0d\u51fa\u8fd9\u8001\u4eba\u7684\u6765\u5386\uff0c\u66f4\u4f30\u4e0d\u900f\u8fd9\u8001\u4eba\u7684\u7528\u610f\uff0c\u53ea\u597d\u9ed8\u7136\u7aef\u5750\u9759 \u5019\u522b\u4eba\u5f00\u53e3\u3002 \u3000\u3000\u7247\u523b\u95f4\u201c\u6843\u59d1\u201d\u4fbf\u5df2\u624b\u6367\u4e00\u5177\u78a7\u7389\u8336\u76cf\uff0c\u8885\u8885\u8d70\u8fd1\uff0c\u201c\u7f2a\u6587\u201d\u6b20\u8eab\u63a5\u8fc7\u3002\u76cf\u91cc\u4e5f\u662f\u5927\u6f20 \u7267\u4eba\u65e5\u5e38\u7528\u7684\u9a6c\u4e73\u8336\uff0c\u55a7\u817e\u7740\u4e00\u7247\u5947\u5f02\u7684\u9999\u6c14\u3002 \u3000\u3000\u8499\u9762\u8001\u4eba\u70af\u7136\u7684\u76ee\u5149\uff0c\u59cb\u7ec8\u672a\u66fe\u81ea\u201c\u7f2a\u6587\u201d\u8eab\u4e0a\u79fb\u5f00\uff0c\u6b64\u523b\u7a81\u5730\u6c89\u58f0\u9053\uff1a\u201c\u5144\u53f0\u4eba\u4e2d\u4e4b \u9f99\uff0c\u4e3e\u4e0a\u975e\u51e1\uff0c\u4e0d\u77e5\u662f\u54ea\u4e00\u4f4d\u8d24\u7236\u6bcd\uff0c\u65b9\u81ea\u751f\u5f97\u51fa\u5982\u6b64\u4f73\u5b50\u5f1f\uff1f\u201d \u3000\u3000\u4ed6\u6c89\u9ed8\u8bb8\u4e45\uff0c\u5ffd\u7136\u95ee\u51fa\u8fd9\u53e5\u8bdd\u6765\uff0c\u201c\u7f2a\u6587\u201d\u5fc3\u4e2d\u4e00\u6014\uff0c\u53e3\u4e2d\u5374\u542b\u7b11\u8c26\u8c22\u9053\uff1a\u201c\u5bb6\u7236\u6bcd\u4ff1 \u662f\u51e1\u4eba\uff0c\u7ecf\u5546\u7ca4\u4e1c\u3002\u770b\u8001\u4e08\u65b9\u662f\u4eba\u4e2d\u4e4b\u9f99\uff0c\u98d8\u5ffd\u6765\u53bb\uff0c\u5374\u4e0d\u77e5\u6765\u5230\u6c5f\u5357\uff0c\u6709\u4f55\u8d35\u5e72\uff1f\u201d \u3000\u3000\u8499\u9762\u8001\u4eba\u76ee\u5149\u4e00\u95ea\uff0c\u7a81\u5730\u4ef0\u5929\u957f\u7b11\u8d77\u6765\u3002 \u3000\u3000\u7b11\u58f0\u6d2a\u4eae\u9ad8\u4ea2\uff0c\u4e5f\u7edd\u4e0d\u4f3c\u5982\u6b64\u8870\u5f31\u7684\u8001\u4eba\u80fd\u591f\u53d1\u51fa\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u8f7b\u8f7b\u653e\u4e0b\u8336\u76cf\uff0c\u542b\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u867d\u7136\u2026\u2026\u201d \u3000\u3000\u8bdd\u58f0\u672a\u4e86\uff0c\u8499\u9762\u8001\u4eba\u5de6\u638c\u7a81\u5730\u4ece\u98ce\u6c05\u4e0b\u8f7b\u8f7b\u6325\u51fa\uff0c\u53ea\u542c\u4e24\u9053\u9510\u98ce\uff0c\u5954\u96f7\u822c\u5411\u201c\u7f2a\u6587\u201d\u51fb \u6765\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u5fc3\u5934\u4e00\u60ca\uff0c\u53ea\u89c1\u8fd9\u4e24\u9053\u4e4c\u5149\u6765\u52bf\u867d\u6025\uff0c\u5374\u5206\u524d\u540e\uff0c\u7adf\u662f\u7b14\u76f4\u51fb\u5411\u81ea\u5df1\u9762\u4e0a\u201c\u8fce \u9999\u201d\u5927\u7a74\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u4ed6\u5fc3\u5ff5\u95ea\u7535\u822c\u4e00\u8f6c\uff0c\u4e24\u9053\u4e4c\u5149\uff0c\u8ddd\u79bb\u4ed6\u8eab\u524d\u4e0d\u53ca\u4e00\u5c3a\uff0c\u540e\u9762\u7684\u6697\u5668\u6765 \u52bf\u7a81\u5730\u52a0\u6025\uff0c\u524d\u9762\u7684\u6697\u5668\u53bb\u52bf\u5374\u9aa4\u7136\u4e00\u7f13\uff0c\u53ea\u542c\u201c\u7830\u201d\u7136\u4e00\u58f0\uff0c\u4e24\u9762\u76f8\u51fb\uff0c\u9f50\u5730\u659c\u659c\u98de\u5f00\uff0c \u843d\u5728\uff1a\u2018\u7f2a\u6587\u201d\u4e24\u65c1\u8eab\u4fa7\u5730\u4e0a\u3002\u8fd9\u6697\u5668\u624b\u6cd5\u4e4b\u60ca\u4eba\uff0c\u5f53\u771f\u4ee4\u4eba\u9a87\u7136\uff0c\u8fd0\u529b\u4e4b\u5de7\uff0c\u624b\u6cd5\u4e4b\u5999\uff0c \u65f6\u95f4\u4e4b\u51c6\uff0c\u73af\u987e\u5f53\u4eca\u6b66\u6797\uff0c\u6240\u53ef\u6bd4\u62df\u8005\u4e0d\u8fc7\u4e09\u4e94\u4eba\u800c\u5df2\u3002\u6697\u5668\u843d\u5730\uff0c\u8499\u9762\u8001\u4eba\u957f\u7b11\u53c8\u8d77\uff0c\u4e00 \u9762\u7b11\u9053\uff1a\u201c\u597d\u6b66\u529f\u5440\u597d\u6b66\u529f\uff0c\u597d\u80c6\u6c14\u5440\u597d\u80c6\u6c14\uff0c\u8001\u592b\u53cc\u773c\u4e0d\u76f2\uff0c\u5144\u53f0\u82e5\u662f\u9ad8\u4eba\u5f1f\u5b50\uff0c\u8001\u592b\u4fbf \u4e5f\u662f\u9ad8\u4eba\u5b50\u5f1f\u4e86\u3002\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u9762\u8272\u5fae\u53d8\uff0c\u4f9d\u7136\u542b\u7b11\u9053\uff1a\u201c\u8001\u4e08\u8fc7\u5956\u4e86\uff0c\u5728\u4e0b\u6709\u4ec0\u4e48\u6b66\u529f\uff0c\u6709\u4ec0\u4e48\u80c6\u6c14\uff0c\u4e0d\u8fc7 \u6df1\u4fe1\u8001\u4e08\u4e0e\u5728\u4e0b\u65e0\u51a4\u65e0\u4ec7\uff0c\u7edd\u4e0d\u6562\u8981\u53d6\u6211\u4e4b\u6027\u547d\uff0c\u662f\u4ee5\u624d\u8fd8\u7a33\u5f97\u4f4f\uff0c\u4f55\u51b5\u4e00\u4e00\u54c8\u54c8\u201d\u4ed6\u5927\u7b11 \u4e24\u58f0\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u5728\u4e0b\u4fbf\u662f\u5fc3\u4e2d\u8981\u60f3\u95ea\u907f\uff0c\u5374\u4e5f\u4e0d\u77e5\u8be5\u5982\u4f55\u95ea\u907f\u5462\uff01\u201d \u3000\u3000\u8499\u9762\u8001\u4eba\u7b11\u58f0\u4e00\u987f\uff0c\u76ee\u5149\u5982\u5203\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4f60\u660e\u77e5\u8001\u592b\u4e0d\u4f1a\u4f24\u4f60\u6027\u547d\uff0c\u4f60\u624d\u4e0d\u907f\u4e0d\u95ea\u662f \u4e48\uff01\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u7b11\u5bb9\u4ea6\u4e0d\u7981\u4e3a\u4e4b\u5c3d\u655b\uff0c\u9762\u8272\u4e00\u6c89\uff0c\u6b63\u8272\u9053\uff1a\u201c\u5728\u4e0b\u4e0e\u8001\u4e08\u7d20\u4e0d\u76f8\u8bc6\uff0c\u8001\u4e08\u5524\u6211 \u524d\u6765\uff0c\u5982\u6b64\u620f\u5f04\uff0c\u76ee\u7684\u7a76\u7adf\u662f\u4ec0\u4e48\uff1f\u5012\u6559\u5728\u4e0b\u8d39\u89e3\uff01\u201d \u3000\u3000\u8499\u9762\u8001\u4eba\u5bb6\u201c\u563f\u563f\u201d\u4e00\u7b11\uff0c\u7a81\u53c8\u5389\u58f0\u9053\uff1a\u201c\u6843\u59d1\uff0c\u67f3\u513f\uff0c\u4f60\u4e24\u4eba\u4e00\u4eba\u53bb\u4e3a\u8001\u592b\u6316\u4e0b\u6b64\u4eba \u7684\u4e00\u53ea\u773c\u73e0\u3002\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u5251\u7709\u5fae\u8f69\uff0c\u53ea\u89c1\u6843\u59d1\u8f7b\u8f7b\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5ba2\u4eba\uff0c\u771f\u5bf9\u4e0d\u8d77\u4f60\u4e86\u3002\u201d \u3000\u3000\u5a07\u7b11\u58f0\u4e2d\uff0c\u67f3\u8170\u8f7b\u6298\uff0c\u4e00\u53ea\u83b9\u83b9\u5982\u7389\u7684\u7ea4\u7ea4\u7389\u624b\uff0c\u5df2\u5230\u4e86\u201c\u7f2a\u6587\u201d\u773c\u524d\uff0c\u4e94\u6307\u5c16\u5c16\uff0c\u5b9b \u5982\u4e94\u67c4\u9510\u5229\u7684\u77ed\u5251\u3002 \u3000\u3000\u90a3\u9ec4\u8863\u7ae5\u5b50\u201c\u67f3\u513f\u201d\u4ea6\u81ea\u563b\u563b\u4e00\u7b11\uff0c\u8fce\u9762\u4e00\u638c\uff0c\u51fb\u5411\u201c\u7f2a\u6587\u201d\u7684\u53f3\u773c\uff0c\u4e24\u4eba\u4ff1\u662f\u51fa\u624b\u5982 \u98ce\uff0c\u4e1d\u6beb\u4e0d\u7559\u60c5\u9762\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u518d\u4e5f\u60f3\u4e0d\u51fa\u8fd9\u8499\u9762\u8001\u4eba\u7a76\u7adf\u4e3a\u4e86\u4ec0\u4e48\uff0c\u7adf\u4f1a\u5982\u6b64\u5bf9\u4ed8\u81ea\u5df1\uff0c\u4f46\u6b64\u523b\u4e24\u53ea\u624b\u638c\u4ff1 \u90fd\u5df2\u5728\u81ea\u5df1\u773c\u524d\uff0c\u81ea\u5df1\u82e5\u662f\u4e0d\u907f\u4e0d\u95ea\uff0c\u4e00\u53cc\u773c\u775b\uff0c\u4fbf\u8bf4\u4e0d\u5b9a\u771f\u8981\u846c\u9001\u5728\u8fd9\u5947\u5f02\u8be1\u79d8\u7684\u5e10\u7bf7 \u91cc\u3002 \u3000\u3000\u4ed6\u672c\u5df2\u6697\u63d0\u771f\u6c14\uff0c\u6b64\u523b\u8fd0\u52b2\u4e8e\u638c\uff0c\u53ea\u8981\u624b\u638c\u4e00\u7ffb\uff0c\u4fbf\u53ef\u5c06\u8fd9\u201c\u6843\u59d1\u201d\u4e0e\u201c\u67f3\u513f\u201d\u9707\u98de\u6570 \u6b65\u3002 \u3000\u3000\u8981\u77e5\u4ed6\u81ea\u5e7c\u82e6\u7ec3\u201c\u5316\u9aa8\u795e\u62f3\u201d\uff0c\u8eab\u4f53\u5404\u90e8\uff0c\u5747\u53ef\u51fa\u4eba\u610f\u6599\u4e4b\u5916\u5730\u626d\u8f6c\uff0c\u81ea\u51fa\u5165\u610f\u6599\u4e4b\u7684 \u90e8\u4f4d\u53d1\u51fa\u62db\u5f0f\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u5982\u4f7f\u51fa\u201c\u5316\u9aa8\u795e\u62f3\u201d\uff0c\u4fbf\u65e0\u5f02\u6cc4\u9732\u4e86\u81ea\u5df1\u7684\u884c\u8e2a\u3002 \u3000\u3000\u662f\u4ee5\u4ed6\u6b64\u523b\u5b9e\u5df2\u6740\u673a\u6697\u751f\uff0c\u7acb\u5fc3\u5c06\u8fd9\u4e24\u4eba\u5168\u90fd\u6bd9\u5728\u638c\u4e0b\u3002 \u3000\u3000\u7b14\u4e0b\u5199\u6765\u867d\u6162\uff0c\u5728\u5f53\u65f6\u5374\u5feb\u5982\u7535\u5149\u77f3\u706b\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u5e10\u5916\u7a81\u5730\u66b4\u559d\u4e00\u58f0\uff1a\u201c\u4f4f\u624b\uff01\u201d \u3000\u3000\u201c\u6843\u59d1\u201d\u3001\u201c\u67f3\u513f\u201d\u62db\u5f0f\u5fae\u5fae\u4e00\u6ede\uff0c\u4e00\u9053\u94f6\u767d\u8272\u7684\u5251\u5149\uff0c\u5df2\u6709\u5982\u5339\u7ec3\u822c\u81ea\u5e10\u5916\u5212\u7a7a\u800c \u6765\u3002 \u3000\u3000\u5251\u5149\u4e00\u95ea\uff0c\u5206\u524a\u201c\u6843\u59d1\u201d\u53f3\u638c\uff0c\u201c\u67f3\u513f\u201d\u5de6\u80a9\uff0c\u4e00\u62db\u4e24\u5f0f\uff0c\u5feb\u5982\u95ea\u7535\uff0c\u53ea\u542c\u55d6\u55d6\u4e24\u58f0\uff0c \u201c\u6843\u59d1\u201d\u8863\u8896\u5df2\u88ab\u5212\u7834\u4e00\u534a\u3002 \u3000\u3000\u201c\u67f3\u513f\u201d\u5e74\u7eaa\u867d\u5c0f\uff0c\u6b66\u529f\u4e0d\u5f31\uff0c\u8eab\u5f62\u4e00\u7f29\uff0c\u7a81\u5730\u632b\u8eab\u800c\u4e0a\uff0c\u547c\u5730\u4e00\u62f3\uff0c\u76f4\u6253\u6765\u4eba\u80c1\u4e0b \u201c\u5929\u6c60\u201d\u5927\u7a74\u3002 \u3000\u3000\u201c\u6843\u59d1\u201d\u9762\u5bb9\u5931\u8272\uff0c\u76ee\u6ce8\u8863\u8896\uff0c\u5fae\u5fae\u4e00\u695e\uff0c\u67f3\u8170\u5fae\u62e7\uff0c\u4ea6\u81ea\u653b\u51fa\u4e24\u638c\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u62db\u5f0f\u914d\u5408\u5f97\u751a\u662f\u4f73\u5999\uff0c\u201c\u7f2a\u6587\u201d\u7aef\u5750\u539f\u5904\uff0c\u51dd\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u534a\u7a7a\u63a0\u5165\u5e10\u4e2d\u7684\uff0c\u7adf \u662f\u90a3\u9ad8\u51a0\u7070\u888d\u7684\u5c11\u5e74\u9053\u4eba\u3002 \u3000\u3000\u4f46\u89c1\u4ed6\u888d\u8882\u98d8\u98d8\uff0c\u957f\u8896\u62c2\u52a8\uff0c\u5239\u90a3\u95f4\u638c\u4e2d\u4e00\u67c4\u96ea\u4eae\u7684\u94f6\u5251\uff0c\u5df2\u95ea\u7535\u822c\u653b\u51fa\u4e03\u62db\uff0c\u62db\u62db\u5747 \u5206\u4e24\u5f0f\uff0c\u5251\u5251\u4e0d\u79bb\u201c\u6843\u59d1\u201d\u201c\u67f3\u513f\u201d\u7684\u8981\u5bb3\uff0c\u7adf\u4f3c\u4e0e\u8fd9\u4e24\u4eba\u6709\u7740\u4ec0\u4e48\u6df1\u4ec7\u5927\u6068\u4f3c\u7684\u3002 \u3000\u3000\u201c\u6843\u59d1\u201d\u3001\u201c\u67f3\u513f\u201d\u8eab\u5f62\u867d\u8f7b\u5de7\uff0c\u4f46\u5728\u8fd9\u5e10\u7bf7\u4e4b\u4e2d\uff0c\u88ab\u8fd9\u5339\u7ec3\u822c\u7684\u5251\u5149\u7eb5\u6a2a\u4e00\u626b\uff0c\u6b64\u523b \u5df2\u662f\u9669\u5883\u4e1b\u751f\uff0c\u773c\u770b\u4fbf\u8981\u4f24\u5728\u5251\u4e0b\u3002 \u3000\u3000\u800c\u201c\u7f2a\u6587\u201d\u5374\u662f\u76f4\u5230\u6b64\u523b\u4e3a\u6b62\uff0c\u8fd8\u4e0d\u77e5\u9053\u8fd9\u5176\u4e2d\u7a76\u7adf\u5305\u6db5\u7740\u4ec0\u4e48\u9690\u79d8\uff0c\u662f\u4ee5\u4ed6\u76f4\u5230\u6b64\u523b \u4e3a\u6b62\uff0c\u7adf\u4ecd\u7136\u7aef\u5750\u672a\u52a8\u3002 \u3000\u3000\u8499\u9762\u8001\u4eba\u68ee\u51b7\u7684\u76ee\u5149\uff0c\u4e00\u76f4\u5728\u968f\u7740\u7070\u888d\u9053\u4eba\u7684\u5251\u5c16\u79fb\u52a8\u7740\uff0c\u6b64\u523b\u7a81\u4e5f\u5389\u53f1\u4e00\u58f0\uff1a\u201c\u4f4f \u624b\uff01\u201d \u3000\u3000\u201c\u6843\u59d1\u201d\u201c\u67f3\u513f\u201d\u8eab\u5f62\u4e00\u5206\uff0c\u5404\u5404\u9000\u51fa\u6570\u5c3a\uff0c\u7d27\u8d34\u7bf7\u5e10\u3002 \u3000\u3000\u7070\u888d\u9053\u4eba\u5251\u52bf\u4e00\u6536\uff0c\u8f6c\u76ee\u671b\u4e86\u201c\u7f2a\u6587\u201d\u4e00\u773c\uff0c\u76ee\u5149\u4e2d\u53c8\u6cdb\u8d77\u4e00\u4e1d\u7b11\u610f\uff0c\u4f46\u7b49\u5230\u4ed6\u76ee\u5149\u8f6c \u5411\u90a3\u8499\u9762\u8001\u4eba\u65f6\uff0c\u4fbf\u6362\u4e86\u4e00\u79cd\u68ee\u4e25\u4e4b\u6c14\u3002 \u3000\u3000\u8499\u9762\u8001\u4eba\u4ecd\u7136\u50f5\u5367\u5728\u90a3\u4ef6\u5bbd\u5927\u7684\u98ce\u6c05\u91cc\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u9601\u4e0b\u662f\u5426\u534e\u5c71\u95e8\u4e0b\uff1f\u4e3a\u4f55\u5230\u6b64\u6492 \u91ce\uff1f\u201d \u3000\u3000\u7070\u888d\u9053\u4eba\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u542c\u95fb\u7389\u95e8\u5173\u5916\uff0c\u6709\u4e00\u4f19\u72ec\u884c\u5927\u76d7\uff0c\u6740\u4eba\u8d8a\u8d27\uff0c\u65e0\u6240\u4e0d\u4e3a\uff0c \u4e45\u8d70\u6c99\u6f20\u7684\u884c\u65c5\uff0c\u90fd\u5c06\u4e4b\u5524\u505a\u2018\u585e\u4e0a\u6e29\u67d4\u9631\u2019\u3002\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u53cc\u7709\u4e00\u76b1\uff0c\u5fd6\u9053\uff1a\u201c\u597d\u5947\u602a\u7684\u540d\u5b57\u3002\u201d \u3000\u3000\u53ea\u542c\u7070\u888d\u9053\u4eba\u63a5\u53e3\u9053\uff1a\u201c\u6c99\u6f20\u4e0a\u9965\u6e34\u7684\u65c5\u4eba\uff0c\u53ea\u8981\u9047\u4e0a\u8fd9\u2018\u585e\u4e0a\u6e29\u67d4\u9631\u2019\uff0c\u5fc5\u5b9a\u5c38\u9aa8\u65e0 \u5b58\uff0c\u60f3\u4e0d\u5230\u8fd9\u2018\u585e\u4e0a\u6e29\u67d4\u9631\u2019\uff0c\u4eca\u65e5\u5c45\u7136\u5230\u4e86\u6c5f\u5357\u2014\u2014\u54fc\u54fc\uff0c\u96be\u9053\u6c99\u6f20\u4e0a\u7684\u65c5\u4eba\uff0c\u90fd\u5df2\u88ab\u4f60 \u4eec\u5bb3\u5149\u4e86\u4e48\uff1f\u201d \u3000\u3000\u8499\u9762\u8001\u4eba\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u4ec0\u4e48\uff1f\u5f53\u771f\u53ef\u7b11\u5f97\u5f88\u3002\u201d \u3000\u3000\u7070\u888d\u9053\u4eba\u5389\u58f0\u9053\uff1a\u201c\u585e\u4e0a\u6e29\u67d4\u9631\u4ee5\u7edd\u8272\u7f8e\u5973\uff0c\u9635\u9635\u8089\u9999\uff0c\u6765\u5f15\u8bf1\u6c99\u6f20\u4e0a\u7684\u65c5\u4eba\uff0c\u8fdb\u5165\u4ed6 \u4eec\u7684\u7bf7\u5e10\uff0c\u7136\u540e\u518d\u52a0\u6b8b\u6740\uff0c\u8fd9\u884c\u5f84\u6b63\u4e0e\u4f60\u540c\u51fa\u4e00\u8f99\uff0c\u5356\u50bb\u4f5c\u4ec0\uff1f\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u604d\u7136\u5fd6\u9053\uff1a\u201c\u6e29\u67d4\u4e4b\u9631\uff0c\u539f\u6765\u5982\u6b64\uff01\u201d \u3000\u3000\u53ea\u89c1\u8499\u9762\u8001\u4eba\u76ee\u5149\u4ecd\u7136\u5bd2\u5982\u7384\u51b0\uff0c\u7070\u888d\u9053\u4eba\u957f\u5251\u4e00\u6325\uff0c\u4ef0\u5929\u7b11\u9053\uff1a\u201c\u53ea\u662f\u4f60\u8fd9\u2018\u585e\u4e0a\u6e29 \u67d4\u9631\u2019\u4eca\u65e5\u649e\u5230\u4e86\u6211\u2018\u534e\u5c71\u94f6\u9e64\uff0c\u624b\u91cc\uff0c\u53ea\u6015\u4f60\u81ea\u4eca\u800c\u540e\uff0c\u518d\u4e5f\u65e0\u6cd5\u5bb3\u4eba\u4e86\u3002\u201d\u8499\u9762\u8001\u4eba\u51b7 \u51b7\u9053\uff1a\u201c\u771f\u7684\u4e48\uff1f\u201d \u3000\u3000\u8bdd\u58f0\u65b9\u4e86\uff0c\u5e10\u5916\u7a81\u53c8\u5927\u559d\u4e00\u58f0\uff1a\u201c\u7f2a\u5144\u5f1f\u53ef\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u201c\u5636\u201d\u5730\u4e00\u54cd\uff0c\u5e10\u5e18\u4e2d\u5206\u4e3a\u4e8c\uff0c\u5e10\u5916\u5927\u6b65\u8d70\u5165\u4e00\u4e2a\u72ec\u81c2\u72ec\u76ee\u7684\u9ed1\u8863\u5934\u9640\uff0c\u72c2\u7b11\u9053\uff1a\u201c\u597d \u6781\u597d\u6781\uff0c\u679c\u7136\u5168\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u4e00\u76f4\u58f0\u52bf\u4e0d\u52a8\u7684\u8499\u9762\u8001\u4eba\uff0c\u6b64\u523b\u76ee\u5149\u7a81\u5730\u4e00\u53d8\uff0c\u90a3\u201c\u4e71\u53d1\u5934\u9640\u201d\u7684\u4e24\u9053\u773c\u795e\uff0c\u6070\u5de7\u626b \u6765\u3002 \u3000\u3000\u4e24\u4eba\u76ee\u5149\u76f8\u9047\uff0c\u201c\u4e71\u53d1\u5934\u9640\u201d\u8eab\u8eaf\u7a81\u5730\u4e00\u9707\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u2026\u2026\u53ef\u662f\u2026\u2026\u201d \u3000\u3000\u4f17\u4eba\u4ff1\u90fd\u4e00\u695e\uff0c\u8499\u9762\u8001\u4eba\u7a81\u5730\u51ed\u7a7a\u81ea\u69bb\u4e0a\u98de\u8d77\uff0c\u8eab\u8eaf\u51cc\u7a7a\u4e00\u6298\uff0c\u95ea\u7535\u822c\u63a0\u5165\u4e86\u90a3\u8c79\u76ae\u5782 \u5e18\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u5fc3\u4e2d\u4e00\u52a8\uff0c\u9053\uff1a\u201c\u6b64\u4eba\u53ef\u5c31\u662f\u5927\u5e08\u6240\u8981\u5bfb\u627e\u4e4b\u4eba\u4e48\uff1f\u201d \u3000\u3000\u7070\u888d\u9053\u4eba\u201c\u534e\u5c71\u94f6\u9e64\u201d\u76ee\u5149\u832b\u7136\u4e92\u671b\u4e00\u773c\uff0c\u201c\u4e71\u53d1\u5934\u9640\u201d\u7a81\u5730\u5927\u559d\u4e00\u58f0\uff0c\u7b14\u76f4\u62a2\u5165\u5e18 \u5185\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u3001\u201c\u534e\u5c71\u94f6\u9e64\u201d\u5bf9\u671b\u4e00\u773c\uff0c\u53cc\u53cc\u4e3e\u6b65\uff0c\u968f\u4e4b\u800c\u5165\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u8499\u9762\u8001\u4eba\u5c45\u7136\u4ee5\u80cc\u5411\u5916\uff0c\u9762\u5bf9\u7bf7\u5e10\uff0c\u8d1f\u624b\u800c\u7acb\u3002 \u3000\u3000\u201c\u4e71\u53d1\u5934\u9640\u201d\u811a\u6b65\u7a81\u987f\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u8f6c\u8fc7\u8138\u6765\uff0c\u8ba9\u6211\u770b\u4e0a\u4e00\u773c\u3002\u201d \u3000\u3000\u8fd9\u9e37\u731b\u7c97\u8c6a\u7684\u5927\u6c49\uff0c\u6b64\u523b\u4e0d\u4f46\u8bed\u58f0\u98a4\u6296\uff0c\u9762\u4e0a\u66f4\u662f\u4e00\u7247\u51c4\u51b7\u75db\u82e6\u4e4b\u8272\uff0c\u4e0e\u5148\u524d\u5927\u662f\u5224\u82e5 \u4e24\u4eba\u3002 \u3000\u3000\u8eab\u62ab\u98ce\u6c05\u7684\u8499\u9762\u8001\u4eba\uff0c\u5374\u4f9d\u7136\u9762\u58c1\u800c\u7acb\uff0c\u4e0d\u8a00\u4e0d\u52a8\uff0c\u6709\u5982\u672a\u95fb\u3002 \u3000\u3000\u201c\u534e\u5c71\u94f6\u9e64\u201d\u53cc\u7709\u4e00\u6311\uff0c\u4e00\u6b65\u62a2\u4e0a\u524d\u53bb\uff0c\u6b63\u5f85\u5c06\u8fd9\u8001\u4eba\u6273\u8f6c\u8eab\u6765\uff0c\u54ea\u77e5\u201c\u4e71\u53d1\u5934\u9640\u201d\u5374 \u7a81\u5730\u72ec\u81c2\u4e00\u4f38\uff0c\u6321\u4f4f\u4e86\u4ed6\u7684\u53bb\u8def\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4f60\u8981\u4f5c\u4ec0\uff1f\u201d \u3000\u3000\u201c\u534e\u5c71\u94f6\u9e64\u201d\u53c8\u60ca\u3001\u53c8\u5947\u3001\u53c8\u6012\uff0c\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff01\u201d \u3000\u3000\u888d\u8896\u4e00\u62c2\uff0c\u540e\u9000\u4e09\u6b65\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u5fc3\u4e2d\u4ea6\u662f\u5927\u5947\uff0c\u8fd9\u7070\u888d\u9053\u4eba\u672c\u662f\u52a9\u4ed6\uff0c\u4ed6\u5374\u5982\u6b64\u8fd8\u62a5\uff0c\u8fd9\u5176\u4e2d\u7684\u9053\u7406\uff0c\u7684\u786e\u9690 \u79d8\u5f97\u4ee4\u4eba\u96be\u6d4b\u3002 \u3000\u3000\u53ea\u542c\u8499\u9762\u8001\u4eba\u7a81\u5730\u5e72\u54b3\u4e00\u58f0\uff0c\u5636\u58f0\u9053\uff1a\u201c\u8bf7\u2026\u2026\u8ba9\u2026\u2026\u201d \u3000\u3000\u8499\u9762\u8001\u4eba\u7a81\u5730\u653e\u58f0\u72c2\u7b11\u8d77\u6765\uff0c\u72c2\u7b11\u58f0\u4e2d\uff0c\u4ed6\u970d\u7136\u98d8\u8eab\uff0c\u53cc\u81c2\u4e00\u632f\uff0c\u98ce\u6c05\u843d\u5730\uff0c\u4e3e\u624b\u4e00 \u62b9\uff0c\u626f\u4e0b\u4e1d\u4e2d\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u76ee\u5149\u8f6c\u5904\uff0c\u4e0d\u7981\u60ca\u547c\u4e00\u58f0\uff0c\u4ed6\u518d\u4e5f\u60f3\u4e0d\u5230\u6b64\u4eba\u8f6c\u8fc7\u8eab\u6765\uff0c\u8d6b\u7136\u7adf\u662f\u90a3\u2018\u516b\u9762\u73b2 \u73d1\uff0c\u80e1\u4e4b\u8f89\uff01 \u7b2c\u4e8c\u5341\u4e09\u7ae0>> \u53e4\u9f99\u300a\u6e58\u5983\u5251\u300b \u7b2c\u4e8c\u5341\u4e09\u7ae0 \u3000\u3000\u5239\u90a3\u95f4\u4f17\u4eba\u5168\u90fd\u6014\u5728\u5f53\u5730\uff0c\u53ea\u6709\u201c\u516b\u9762\u73b2\u73d1\u201d\u80e1\u4e4b\u8f89\u72c2\u7b11\u4e0d\u7edd\u3002 \u3000\u3000\u201c\u534e\u5c71\u94f6\u9e64\u201d\u9762\u8272\u6e10\u6e10\u5bd2\u51b7\uff0c\u201c\u7f2a\u6587\u201d\u76ee\u4e2d\u53c8\u81ea\u9732\u51fa\u4e86\u5947\u5f02\u7684\u5149\u8292\uff0c\u201c\u4e71\u53d1\u5934\u9640\u201d\u7a81\u5730 \u5927\u559d\u4e00\u58f0\uff0c\u95ea\u7535\u822c\u58f0\u51fa\u4e86\u94a2\u94a9\u822c\u7684\u94c1\u638c\uff0c\u652b\u4f4f\u4e86\u80e1\u4e4b\u8f89\u7684\u8863\u895f\uff0c\u80e1\u4e4b\u8f89\u7b11\u58f0\u621b\u7136\u800c\u987f\uff0c\u8eab\u8eaf \u5374\u5df2\u88ab\u201c\u4e71\u53d1\u5934\u9640\u201d\u786c\u751f\u751f\u81ea\u5730\u4e0a\u6293\u4e86\u8d77\u6765\uff0c\u5c31\u4eff\u4f5b\u662f\u5c60\u6237\u6848\u5934\u94a2\u94a7\u4e0a\u6302\u7740\u7684\u732a\u8e44\u4f3c\u7684\u3002 \u3000\u3000\u80e1\u4e4b\u8f89\u867d\u7136\u201c\u516b\u9762\u73b2\u73d1\u201d\uff0c\u4f46\u6b64\u523b\u5374\u5df2\u51c9\u614c\u8d77\u6765\uff0c\u5c24\u5176\u662f\u201c\u4e71\u53d1\u5934\u9640\u201d\u76ee\u5149\u4e2d\u7684\u90a3\u79cd\u51f6 \u731b\u9e37\u72e0\u4e4b\u6c14\uff0c\u66f4\u4f7f\u4ed6\u8fde\u6323\u624e\u90fd\u4e0d\u6562\u6323\u624e\u3002 \u3000\u3000\u201c\u4e71\u53d1\u5934\u9640\u201d\u624b\u81c2\u7b14\u76f4\uff0c\u6beb\u65e0\u534a\u70b9\u5f2f\u66f2\uff0c\u7adf\u5c06\u5982\u6b64\u81c3\u80bf\u7b28\u91cd\u7684\u4eba\u8f7b\u800c\u6613\u4e3e\u5730\u51cc\u7a7a\u626c\u8d77\u3002 \u3000\u3000\u8fd9\u79cd\u60ca\u4eba\u7684\u795e\u529b\uff0c\u4f7f\u5f97\u201c\u534e\u5c71\u94f6\u9e64\u201d\u9762\u4e0a\u4e5f\u9732\u51fa\u60ca\u5947\u6ce8\u610f\u4e4b\u8272\uff0c\u662f\u4ee5\u5927\u5bb6\u53c8\u6014\u4e86\u4e00\u6014\u4e4b \u540e\uff0c\u80e1\u4e4b\u8f89\u65b9\u81ea\u98a4\u58f0\u9053\uff1a\u201c\u5927\u5e08\u2026\u2026\u5728\u4e0b\u2026\u2026\u4ec0\u4e48\u4e8b\u5f97\u7f6a\u4e86\u4f60\uff1f\u201d \u3000\u3000\u201c\u4e71\u53d1\u5934\u9640\u201d\u76ee\u9732\u51f6\u5149\uff0c\u4e0d\u8a00\u4e0d\u52a8\uff0c\u7adf\u4f3c\u5bf9\u80e1\u4e4b\u8f89\u771f\u7684\u5341\u5206\u6000\u6068\u3002 \u3000\u3000\u80e1\u4e4b\u8f89\u5fc3\u80c6\u66f4\u5bd2\uff0c\u76ee\u5149\u4e5e\u601c\u5730\u671b\u7740\u201c\u7f2a\u6587\u201d\uff0c\u98a4\u58f0\u53c8\u9053\uff1a\u201c\u7f2a\u5144\u5f1f\u2026\u2026\u7f2a\u5144\u2026\u2026\u8bf7\u6c42\u8d35 \u53cb\u5c06\u6211\u653e\u4e0b\u6765\u2026\u2026\u5927\u5bb6\u90fd\u662f\u670b\u53cb\uff0c\u4ec0\u4e48\u4e8b\u90fd\u597d\u8bf4\u561b\u3002\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u80e1\u5144\uff0c\u4f60\u65e2\u5f00\u4e86\u522b\u4eba\u7684\u73a9\u7b11\uff0c\u522b\u4eba\u5f00\u5f00\u4f60\u73a9\u7b11\u53c8\u6709\u4f55\u59a8\u3002\u201d \u3000\u3000\u201c\u4e71\u53d1\u5934\u9640\u201d\u51b7\u54fc\u4e00\u58f0\uff0c\u4ed6\u76f4\u5230\u6b64\u523b\u65b9\u81ea\u53d1\u51fa\u58f0\u97f3\uff0c\u662f\u4ee5\u8fd9\u4e00\u58f0\u4fbf\u8d8a\u53d1\u663e\u5f97\u68ee\u51b7\u53ef\u6015\u3002 \u3000\u3000\u201c\u516b\u9762\u73b2\u73d1\u201d\u80e1\u4e4b\u8f89\u9762\u5982\u571f\u8272\uff0c\u8fd8\u8981\u52c9\u5f3a\u6324\u51fa\u4e00\u4efd\u7b11\u5bb9\uff0c\u795e\u8272\u81ea\u7136\u663e\u5f97\u66f4\u52a0\u53ef\u601c\u53ef\u7b11\uff0c \u966a\u7740\u7b11\u98a4\u58f0\u9053\uff1a\u201c\u5927\u5e08\uff0c\u5728\u4e0b\u7a76\u7adf\u662f\u4ec0\u4e48\u4e8b\u5f97\u7f6a\u4e86\u4f60\u8001\u4eba\u5bb6\uff0c\u4f60\u8001\u4eba\u5bb6\u53ea\u7ba1\u8bf4\u8bdd\u2026\u2026\u201d \u3000\u3000\u201c\u4e71\u53d1\u5934\u9640\u201d\u5389\u53f1\u4e00\u58f0\uff0c\u624b\u638c\u4e00\u63a8\uff0c\u5c06\u80e1\u4e4b\u8f89\u6454\u5230\u5730\u4e0a\uff0c\u72e0\u72e0\u671b\u4e86\u4ed6\u4e00\u773c\uff0c\u7adf\u7a81\u5730\u8f6c\u8eab \u8d70\u4e86\u51fa\u53bb\u3002\u8981\u77e5\u4ed6\u4e0e\u80e1\u4e4b\u8f89\u672c\u65e0\u4ec7\u6068\uff0c\u6709\u7684\u53ea\u662f\u7531\u5931\u671b\u5316\u6210\u7684\u6124\u6012\uff0c\u56e0\u4e3a\u4ed6\u672c\u8ba4\u5b9a\u4e86\u8fd9\u8499\u9762 \u5bb9\u4fbf\u662f\u4ed6\u60f3\u50cf\u4e2d\u7684\u4eba\u3002 \u3000\u3000\u80e1\u4e4b\u8f89\u5927\u5927\u677e\u4e86\u53e3\u6c14\uff0c\u4f46\u5374\u5f04\u5f97\u66f4\u83ab\u540d\u5176\u5999\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u53c8\u81ea\u5fae\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u5927\u5e08\u6162\u8d70\u3002\u201d \u3000\u3000\u201c\u4e71\u53d1\u5934\u9640\u201d\u8fdf\u7591\u4e00\u4e0b\uff0c\u7ec8\u4e8e\u505c\u4e0b\u811a\u6b65\uff0c\u5374\u5fcd\u4e0d\u4f4f\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u8138\u4e0a\u6362\u4e86\u4e00\u79cd\u51c4\u51c9\u5931 \u671b\u7684\u795e\u8272\uff0c\u4ef0\u5929\u53f9\u9053\uff1a\u201c\u4eba\u6d77\u832b\u832b\u2026\u2026\u4eba\u6d77\u832b\u832b\u2026\u2026\u201d \u3000\u3000\u7f2a\u6587\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u96be\u9053\u8ba4\u4e3a\u8fd9\u4f4d\u80e1\u5144\u771f\u7684\u4fbf\u662f\u65b9\u624d\u90a3\u4f4d\u8499\u9762\u4e4b\u4eba\u4e48\uff1f\u201d \u3000\u3000\u201c\u4e71\u53d1\u5934\u9640\u201d\u53cc\u76ee\u4e00\u5f20\uff0c\u970d\u7136\u8f6c\u8fc7\u8eab\u6765\uff0c\u201c\u516b\u9762\u73b2\u73d1\u201d\u80e1\u4e4b\u8f89\u5df2\u6323\u624e\u7740\u722c\u8d77\uff0c\u966a\u7b11\u8ba4 \u9053\uff1a\u201c\u6211\u5982\u6b64\u505a\u6cd5\uff0c\u4ec5\u662f\u4e3a\u4e86\u6211\u4eec\u6bdb\u5927\u54e5\u8981\u60f3\u77e5\u9053\u8fd9\u4f4d\u7f2a\u5144\u5f1f\u7684\u5e95\u7ec6\uff0c\u662f\u4ee5\u624d\u6d3e\u6211\u4e54\u88c5\u6210\u5982 \u6b64\u6a21\u6837\uff0c\u6765\u8bd5\u63a2\u4e00\u4e0b\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u5fae\u987f\uff0c\u53c8\u5411\u201c\u7f2a\u6587\u201d\u7b11\u9053\uff1a\u201c\u4f46\u6bdb\u5927\u54e5\u6b64\u4e3e\uff0c\u5bf9\u7f2a\u5144\u4e5f\u6ca1\u6709\u4e1d\u6beb\u6076\u610f\uff0c\u53ea\u4e0d\u8fc7\u662f \u4e3a\u4e86\u2026\u2026\u4e3a\u4e86\u2026\u2026\u201d\u653e\u58f0\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u4e3a\u4e86\u6bdb\u5927\u54e5\u7684\u638c\u4e0a\u660e\u73e0\u800c\u5df2\u3002\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u4ecd\u7136\u9762\u5e26\u5fae\u7b11\uff0c\u201c\u4e71\u53d1\u5934\u9640\u201d\u5374\u5728\u77ac\u4e5f\u4e0d\u77ac\u5730\u6ce8\u89c6\u7740\u80e1\u4e4b\u8f89\u7684\u773c\u775b\uff0c\u201c\u534e\u5c71\u94f6 \u9e64\u201d\u5f90\u5f90\u5c06\u638c\u4e2d\u957f\u5251\u63d2\u56de\u5251\u9798\u3002 \u3000\u3000\u4ed6\u6b64\u523b\u5fc3\u91cc\u867d\u7136\u4e5f\u6709\u4e9b\u60ca\u5f02\u548c\u5947\u602a\uff0c\u4f46\u795e\u8272\u95f4\u5374\u4ecd\u662f\u6781\u4e3a\u6f47\u6d12\u5b89\u7965\uff0c\u5f90\u6b65\u8d70\u5230\u201c\u7f2a\u6587\u201d \u8eab\u4fa7\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u6717\u58f0\u9053\uff1a\u201c\u8d2b\u9053\u4e0d\u77e5\u6b64\u4e8b\u5176\u4e2d\u8fd8\u6709\u8fd9\u4e9b\u66f2\u6298\uff0c\u539f\u6765\u5144\u53f0\u7adf\u662f\u6bdb\u65bd\u4e3b\u5fc3\u76ee\u4e2d \u7684\u4e58\u9f99\u5feb\u5a7f\uff0c\u82e5\u65e9\u77e5\u5982\u6b64\uff0c\u8d2b\u9053\u4e5f\u4e0d\u5fc5\u5306\u5306\u8d76\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u5fc3\u4e2d\u5bf9\u4ed6\u672c\u5df2\u5341\u5206\u611f\u6fc0\uff0c\u5728\u8fd9\u5239\u90a3\u95f4\uff0c\u4ed6\u7a81\u53c8\u5bf9\u8fd9\u5e74\u8f7b\u800c\u6c89\u7a33\u7684\u9053\u4eba\u751f\u51fa\u4eb2\u8fd1 \u4e4b\u5fc3\uff0c\u6df1\u6df1\u4e00\u63d6\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u5728\u4e0b\u4e0e\u9053\u957f\u840d\u6c34\u76f8\u9022\uff0c\u9053\u957f\u5374\u5bf9\u5728\u4e0b\u5982\u6b64\u5173\u5fc3\uff0c\u5728\u4e0b\u5fc3\u4e2d\u7684\u611f \u6fc0\u2026\u2026\u5509\uff01\u5b9e\u975e\u8a00\u8bed\u6240\u80fd\u5f62\u5bb9\uff0c\u53ea\u671b\u65e5\u540e\u8fd8\u6709\u673a\u4f1a\u4e0e\u9053\u957f\u91cd\u805a\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u6536\u8d77\u9762\u4e0a\u7b11\u5bb9\uff0c\u8a00\u8bed\u53c8\u8bf4\u5f97\u5341\u5206\u8bda\u6073\u3001\u6c89\u91cd\uff0c\u201c\u534e\u5c71\u94f6\u9e64\u201d\u663e\u7136\u4e5f\u751a\u611f\u52a8\uff0c\u63a5\u53e3 \u9053\uff1a\u201c\u8d2b\u9053\u81ea\u4eca\u800c\u540e\uff0c\u53ea\u6015\u8981\u5e38\u5728\u6c5f\u6e56\u95f4\u8d70\u52a8\uff0c\u82e5\u5f97\u9601\u4e0b\u8fd9\u822c\u4eba\u7269\u4e3a\u53cb\uff0c\u4e5f\u662f\u4eba\u751f\u4e00\u5927\u4e50 \u4e8b\u3002\u201d \u3000\u3000\u4ed6\u4e24\u4eba\u987f\u8d77\u60fa\u60fa\u76f8\u60dc\u4e4b\u5fc3\uff0c\u5728\u8fd9\u7247\u523b\u95f4\u4fbf\u4f3c\u5df2\u7ed3\u4e3a\u597d\u53cb\uff0c\u662f\u4ee5\u6b64\u65f6\u6b64\u523b\uff0c\u4e24\u4eba\u5c45\u7136\u8fd8\u6709 \u5fc3\u60c5\u5bd2\u55a7\u8d77\u6765\u3002 \u3000\u3000\u90a3\u8fb9\u201c\u4e71\u53d1\u5934\u9640\u201d\u76ee\u5149\u4ecd\u672a\u79fb\u52a8\uff0c\u76f4\u5c06\u80e1\u4e4b\u8f89\u770b\u5f97\u4e0d\u6562\u62ac\u8d77\u5934\u6765\u3002 \u3000\u3000\u201c\u4e71\u53d1\u5934\u9640\u201d\u76ee\u5149\u867d\u672a\u79fb\u52a8\uff0c\u4f46\u6b64\u523b\u5728\u4ed6\u8111\u6d77\u4e2d\uff0c\u6b63\u6709\u4e24\u53cc\u773c\u775b\u4e0d\u4f4f\u5728\u4ea4\u66ff\uff0c\u65cb \u8f6c\u2026\u2026\uff0c\u5176\u4e2d\u4e00\u53cc\u773c\u775b\uff0c\u5bf9\u4ed6\u662f\u90a3\u4e48\u719f\u6089\uff0c\u5374\u53c8\u9694\u5f97\u90a3\u4e48\u9065\u8fdc\uff0c\u8fd9\u53cc\u773c\u775b\u91cc\uff0c\u5305\u542b\u7740\u6148\u7965\u800c \u4eb2\u5207\u7684\u5149\u8292\uff0c\u4f46\u5ffd\u7136\u53c8\u4f1a\u53d8\u5f97\u5341\u5206\u51f6\u6076\u4e25\u5389\uff0c\u4ed6\u5f88\u5c0f\u4fbf\u671b\u7740\u8fd9\u53cc\u773c\u775b\uff0c\u4ed6\u6240\u6709\u7684\u4e00\u5207\u90fd\u8981\u51ed \u7740\u8fd9\u53cc\u773c\u775b\u7684\u53d8\u5316\u800c\u53d8\u5316\uff0c\u76f4\u5230\u6709\u4e00\u5929\u2026\u2026 \u3000\u3000\u53e6\u5916\u4e00\u53cc\uff0c\u4fbf\u662f\u65b9\u624d\u9732\u5728\u90a3\u8499\u9762\u7684\u4e1d\u4e2d\u540e\u7684\u773c\u775b\u3002 \u3000\u3000\u8fd9\u4e00\u53cc\u773c\u775b\uff0c\u770b\u6765\u662f\u90a3\u4e48\u9065\u8fdc\uff0c\u5374\u53c8\u4f3c\u90a3\u4e48\u719f\u6089\uff01 \u3000\u3000\u867d\u7136\u7ecf\u8fc7\u4e86\u8bb8\u591a\u6539\u53d8\uff0c\u4f46\u5176\u4e2d\u5374\u4f3c\u4e4e\u4ecd\u6709\u4e00\u79cd\u4ee4\u4ed6\u6151\u670d\u7684\u529b\u91cf\u5b58\u5728\u2026\u2026 \u3000\u3000\u800c\u6b64\u523b\u4ed6\u5bf9\u9762\u80fd\u591f\u671b\u89c1\u7684\u4e00\u53cc\u773c\u775b\uff0c\u5374\u662f\u6781\u72e1\u6ed1\u53c8\u61e6\u5f31\uff0c\u8fd9\u600e\u4f1a\u662f\u65b9\u624d\u9732\u5728\u4e1d\u4e2d\u5916\u7684\u773c \u775b\uff1f \u3000\u3000\u201c\u4e71\u53d1\u5934\u9640\u201d\u601d\u5ff5\u65cb\u8f6c\uff0c\u5fc3\u4e2d\u7ffb\u8d77\u4e86\u65e0\u6570\u4f24\u611f\u800c\u4e11\u6076\u7684\u5f80\u4e8b\u3002 \u3000\u3000\u201c\u516b\u9762\u73b2\u73d1\u201d\u80e1\u4e4b\u8f89\u5fcd\u4e0d\u4f4f\u5e72\u54b3\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5927\u5e08\u5982\u6b64\u795e\u529b\uff0c\u4e0d\u77e5\u9053\u662f\u2026\u2026\u201d \u3000\u3000\u53ea\u542c\u201c\u4e71\u53d1\u5934\u9640\u201d\u7a81\u5730\u5927\u559d\u4e00\u58f0\uff1a\u201c\u4e0d\u5bf9\u3002\u201d \u3000\u3000\u4e00\u4e2a\u7bad\u6b65\u7a9c\u4e86\u8fc7\u53bb\uff0c\u80e1\u4e4b\u8f89\u5927\u60ca\u4e4b\u4e0b\uff0c\u8eab\u5f62\u4e00\u95ea\uff0c\u4f46\u201c\u4e71\u53d1\u5934\u9640\u201d\u5df2\u5982\u5f71\u9644\u5f62\u5730\u6251\u4e86\u4e0a \u6765\u3002 \u3000\u3000\u80e1\u4e4b\u8f89\u867d\u7136\u6b66\u529f\u4e0d\u7b97\u5927\u5f31\uff0c\u4f46\u4ed6\u89c1\u4e86\u8fd9\u5947\u5f02\u7684\u9ed1\u8863\u5934\u9640\uff0c\u6c14\u5df2\u602f\uff0c\u80c6\u5df2\u5bd2\uff0c\u6839\u672c\u4e0d\u6562\u52a8 \u624b\uff0c\u8eab\u5f62\u518d\u6b21\u4e00\u95ea\uff0c\u5374\u53c8\u88ab\u201c\u4e71\u53d1\u5934\u9640\u201d\u5288\u624b\u4e00\u628a\uff0c\u6293\u4f4f\u4e86\u8863\u895f\uff0c\u518d\u6b21\u51cc\u7a7a\u63d0\u4e86\u8d77\u6765\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u76ee\u5149\u8f6c\u5904\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5927\u5e08\u53ef\u662f\u6b64\u523b\u4e5f\u5df2\u5206\u51fa\u8fd9\u4f4d\u80e1\u5144\u6839\u672c\u4e0d\u662f\u65b9\u624d\u7684 \u8499\u9762\u5947\u4eba\u3002\u201d \u3000\u3000\u201c\u4e71\u53d1\u5934\u9640\u201d\u987b\u53d1\u7686\u5f20\uff0c\u5341\u5206\u6124\u6012\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u679c\u7136\u6389\u4e86\u5305\u4e86\u3002\u201d \u3000\u3000\u4ed6\u6447\u52a8\u9707\u64bc\u770b\u80e1\u4e4b\u8f89\u7684\u8eab\u8eaf\uff0c\u5389\u58f0\u53c8\u9053\uff1a\u201c\u6d12\u5bb6\u95ee\u4f60\uff0c\u65b9\u624d\u90a3\u4eba\u662f\u8c01\uff1f\u6b64\u523b\u5230\u54ea\u91cc\u53bb \u4e86\uff1f\u4ed6\u4e3a\u4f55\u4e0d\u613f\u89c1\u6211\uff1f\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u4e4b\u4e2d\uff0c\u65e2\u662f\u6124\u6012\uff0c\u53c8\u662f\u60b2\u6fc0\uff0c\u4f7f\u5f97\u4ed6\u9762\u5bb9\u76ee\u5149\u770b\u6765\u66f4\u662f\u53ef\u6016\uff0c\u80e1\u4e4b\u8f89\u65e9\u5df2\u9762\u65e0\u4eba \u8272\uff0c\u5f20\u53e3\u7ed3\u820c\uff0c\u5450\u5450\u9053\uff1a\u201c\u5927\u5e08\uff0c\u4f60\u2026\u2026\u53ea\u6015\u662f\u8bef\u4f1a\u4e86\u3002\u201d \u3000\u3000\u201c\u4e71\u53d1\u5934\u9640\u201d\u5927\u559d\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8bef\u4f1a\u4ec0\u4e48\uff1f\u201d\u4f60\u518d\u4e0d\u8001\u5b9e\u8bf4\u51fa\uff0c\u6d12\u5bb6\u4e00\u624b\u5c06\u4f60\u6495\u6210\u4e24 \u534a\u3002\u201d \u3000\u3000\u4ed6\u8bed\u6c14\u4e2d\u7684\u529b\u91cf\u6559\u4eba\u542c\u4e86\u6839\u672c\u65e0\u6cd5\u4e0d\u76f8\u4fe1\u4ed6\u7684\u8bdd\uff0c\u800c\u5bf9\u4ed8\u201c\u516b\u9762\u73b2\u73d1\u201d\u80e1\u4e4b\u8f89\u8fd9\u79cd\u4eba\uff0c \u4e5f\u53ea\u6709\u8fd9\u79cd\u5f3a\u70c8\u800c\u5c16\u9510\u7684\u65b9\u6cd5\u6700\u4e3a\u6709\u6548\u3002 \u3000\u3000\u4f46\u662f\u5374\u4f3c\u53e6\u6709\u4e00\u79cd\u66f4\u5f3a\u70c8\u7684\u529b\u91cf\uff0c\u4f7f\u5f97\u201c\u516b\u9762\u73b2\u73d1\u201d\u5728\u5982\u6b64\u60ca\u5413\u4e4b\u4e0b\uff0c\u8fd8\u4e0d\u6562\u8bf4\u51fa\u4e8b\u5b9e \u4e4b\u771f\u8c61\uff0c\u53ea\u662f\u98a4\u58f0\u9053\uff1a\u201c\u5927\u5e08\u4f60\u82e5\u4e0d\u76f8\u4fe1\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u201c\u4e71\u53d1\u5934\u9640\u201d\u624b\u638c\u7a81\u5730\u4e00\u7d27\uff0c\u5c06\u80e1\u4e4b\u8f89\u80f8\u524d\u7684\u80a5\u8089\u6709\u5982\u9762\u7c89\u4f3c\u5730\u6293\u8d77\uff0c\u80e1\u4e4b\u8f89\u54ac\u4f4f\u7259 \u6839\uff0c\u4ec5\u4ec5\u8f7b\u547c\u4e00\u58f0\uff0c\u4f46\u5df2\u6d41\u4e0b\u6ee1\u5934\u5927\u6c57\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u542b\u7b11\u9053\uff1a\u201c\u5927\u5e08\u5176\u5b9e\u4e0d\u5fc5\u5982\u6b64\u8ffd\u95ee\uff0c\u90a3\u4f4d\u8499\u9762\u5947\u4eba\u6b64\u523b\u867d\u65e9\u5df2\u8d70\u4e86\uff0c\u4f46\u4ed6\u65e2\u4e0e \u2018\u7075\u86c7\uff0c\u6bdb\u81ec\u6709\u4e86\u6765\u5f80\uff0c\u8fd8\u6015\u4ed6\u4e0d\u5230\u676d\u5dde\u57ce\u53bb\u4e48\uff1f\u201d\u201c\u4e71\u53d1\u5934\u9640\u201d\u6068\u58f0\u9053\uff1a\u201c\u7eb5\u7136\u5982\u6b64\uff0c\u4eca \u65e5\u6211\u4e5f\u8981\u53eb\u6b64\u4eba\u628a\u771f\u8bdd\u5410\u9732\u51fa\u6765\uff01\u201d \u3000\u3000\u201c\u534e\u5c71\u94f6\u9e64\u201d\u5251\u7709\u5fae\u8f69\uff0c\u65b9\u5f85\u8bf4\u8bdd\uff0c\u7a81\u542c\u4e00\u9635\u9a6c\u8e44\u58f0\u8fdc\u8fdc\u5954\u6765\uff0c\u5239\u90a3\u95ee\u4fbf\u5df2\u5230\u4e86\u5e10\u7bf7\u524d \u9762\uff0c\u63a5\u7740\u4fbf\u54cd\u8d77\u4e00\u9635\u9ad8\u9ad8\u547c\u58f0\uff1a\u201c\u7f2a\u5144\uff0c\u4f60\u5728\u91cc\u9762\uff1f\u201d \u3000\u3000\u547c\u58f0\u672a\u4e86\uff0c\u5df2\u6709\u5341\u6570\u6761\u624b\u6301\u5200\u5251\u7684\u5927\u6c49\u6025\u6b65\u5954\u4eba\uff0c\u5f53\u5148\u4e00\u4eba\u77ed\u886b\u9752\u4e2d\uff0c\u811a\u7a7f\u8349\u978b\uff0c\u4e00\u773c \u671b\u53bb\u5b9b\u5982\u5e84\u7a3c\u6751\u6c49\u4f3c\u7684\uff0c\u4f46\u6ee1\u9762\u4ff1\u662f\u7cbe\u660e\u5f3a\u608d\u4e4b\u8272\uff0c\u884c\u52a8\u66f4\u662f\u51fa\u5947\u7075\u6d3b\u77eb\u5065\uff0c\u5168\u8eab\u90fd\u4f3c\u5145\u6ee1 \u4e86\u4f7f\u4e4b\u4e0d\u5c3d\uff0c\u7528\u4e4b\u4e0d\u7aed\u7684\u6d3b\u529b\u3002 \u3000\u3000\u201c\u516b\u9762\u73b2\u73d1\u201d\u80e1\u4e4b\u8f89\u76ee\u5149\u52a8\u5904\uff0c\u9762\u8272\u4e00\u5bbd\uff0c\u5927\u53eb\u9053\uff1a\u201c\u6765\u4e86\u6765\u4e86\uff0c\u6881\u5927\u54e5\u6765\u4e86\u3002\u201d \u3000\u3000\u8fd9\u77ed\u886b\u6c49\u5b50\u5374\u8fde\u671b\u4e5f\u4e0d\u671b\u4ed6\u4e00\u773c\uff0c\u6025\u6b65\u8d70\u5230\u201c\u7f2a\u6587\u201d\u8eab\u4fa7\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u7f2a\u5144\uff0c\u4f60\u6ca1\u6709\u4e8b \u4e48\uff1f\u201d \u3000\u3000\u201c\u534e\u5c71\u94f6\u9e64\u201d\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u5fc3\u4e2d\u6697\u5947\uff1a\u201c\u8fd9\u59d3\u7f2a\u7684\u5c11\u5e74\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u53c8\u4e0d\u4f3c\u6c5f\u6e56\u4e2d\u4eba\uff0c\u5374\u53c8 \u4f3c\u4e4e\u6709\u7740\u6781\u5927\u7684\u6f5c\u529b\uff0c\u53ea\u8981\u4ed6\u4e00\u9047\u770b\u56f0\u96be\uff0c\u968f\u65f6\u90e1\u4f1a\u6709\u4eba\u4e3a\u4ed6\u51fa\u624b\u3002\u201d \u3000\u3000\u7b49\u5230\u201c\u7f2a\u6587\u201d\u4e0e\u90a3\u77ed\u886b\u6c49\u5b50\u5bd2\u55a7\u4e86\u4e24\u53e5\uff0c\u4ed6\u53c8\u542c\u51fa\u8fd9\u77ed\u886b\u7684\u6751\u6c49\u5c45\u7136\u7adf\u662f\u540d\u6ee1\u5929\u4e0b\u7684 \u201c\u4e5d\u8db3\u795e\u86db\u201d\u6881\u4e0a\u4eba\uff0c\u5fc3\u5934\u4e0d\u89c9\u53c8\u662f\u4e00\u9707\u3002 \u3000\u3000\u201c\u4e71\u53d1\u5934\u9640\u201d\u4e5f\u4e0d\u7981\u8f6c\u8fc7\u5934\u53bb\uff0c\u4e0a\u4e0b\u7aef\u8be6\u4e86\u6881\u4e0a\u4eba\u51e0\u773c\uff0c\u4f46\u4ed6\u5374\u770b\u4e0d\u51fa\u5982\u6b64\u5e73\u51e1\u7684\u4e00\u4e2a \u6c49\u5b50\uff0c\u600e\u4f1a\u6709\u7edf\u7387\u6570\u5343\u4e2a\u5e02\u4e95\u82f1\u96c4\u7684\u9b54\u529b\u3002 \u3000\u3000\u53ea\u89c1\u6881\u4e0a\u4eba\u542b\u7b11\u9053\uff1a\u201c\u6211\u8def\u7ecf\u6b64\u5730\uff0c\u7a0b\u4e03\u5f1f\u6070\u5de7\u6b63\u5728\u5bfb\u4eba\u4e3a\u7f2a\u5144\u89e3\u56f4\uff0c\u6211\u4fbf\u7acb\u65f6\u8d76\u6765\uff0c \u60f3\u4e0d\u5230\u5374\u662f\u4e00\u573a\u865a\u60ca\u3002\u201d \u3000\u3000\u4ed6\u76ee\u5149\u4ec5\u4ec5\u626b\u4e86\u201c\u534e\u5c71\u94f6\u9e64\u201d\u4e00\u773c\uff0c\u4fbf\u7acb\u523b\u63a5\u9053\uff1a\u201c\u8fd9\u4f4d\u60f3\u6765\u5c31\u662f\u5f53\u4eca\u534e\u5c71\u5251\u6d3e\u4e2d\u4ec5\u6709 \u7684\u4e09\u4f4d\u2018\u94f6\u886b\u5251\u5ba2\u2019\u7684\u2018\u94f6\u9e64\u9053\u957f\u2019\u4e86\uff0c\u9053\u957f\u6025\u4eba\u4e4b\u96be\uff0c\u4e00\u5982\u81ea\u5df1\uff0c\u6881\u67d0\u597d\u751f\u4f69\u670d\uff01\u201d \u3000\u3000\u8bed\u97f3\u5fae\u987f\uff0c\u76ee\u5149\u7acb\u523b\u8f6c\u5411\u201c\u4e71\u53d1\u5934\u9640\u201d\uff0c\u63a5\u7740\u542b\u7b11\u9053\uff1a\u201c\u5927\u5e08\u795e\u529b\u60ca\u4eba\uff0c\u8c6a\u8fc8\u7edd\u4f26\uff0c\u6881 \u67d0\u66f4\u662f\u656c\u670d\uff01\u201d \u3000\u3000\u76ee\u5149\u7acb\u53c8\u8f6c\u5411\u80e1\u4e4b\u8f89\uff0c\u9053\uff1a\u201c\u80e1\u5144\u4e3a\u6bdb\u516c\u529e\u4e8b\uff0c\u53ef\u79f0\u5168\u5fc3\u5168\u529b\uff0c\u4f46\u5374\u505a\u9519\u4e86\u8bb8\u591a\uff0c\u5728\u4e0b \u5b9e\u5728\u9057\u61be\u5f97\u5f88\uff0c\u8981\u6559\u80e1\u5144\u4e3a\u6b64\u4ed8\u51fa\u4e00\u4e9b\u4ee3\u4ef7\u3002\u201d \u3000\u3000\u4ed6\u518d\u8f6c\u5411\u201c\u7f2a\u6587\u201d\uff0c\u542b\u7b11\u9053\uff1a\u201c\u676d\u5dde\u57ce\u4e2d\uff0c\u6b64\u523b\u70ed\u95f9\u5df2\u6781\uff0c\u6211\u4e8b\u5148\u4e5f\u672a\u60f3\u5230\u4f1a\u6709\u90a3\u4e48\u591a \u6b66\u6797\u82f1\u96c4\u8d76\u5230\u676d\u5dde\u57ce\u53bb\uff0c\u7f2a\u5144\u5982\u8981\u52a8\u8eab\uff0c\u6b64\u523b\u5df2\u53ef\u8d70\u4e86\u3002\u201d \u3000\u3000\u4ed6\u6ed4\u6ed4\u4e0d\u7edd\uff0c\u6839\u672c\u6ca1\u6709\u7ed9\u522b\u4eba\u8bf4\u8bdd\u7684\u65f6\u95f4\uff0c\u4f46\u662f\u4ed6\u81ea\u5df1\u4e5f\u6ca1\u6709\u8bf4\u4e00\u53e5\u5e9f\u8bdd\uff0c\u5728\u8fd9\u7247\u523b\u4e4b \u95f4\uff0c\u4ed6\u5df2\u5c06\u6bcf\u4e2a\u4eba\u7684\u8eab\u4efd\u4ff1\u90fd\u8bf4\u51fa\uff0c\u53c8\u5728\u8f7b\u63cf\u6de1\u5199\u4e4b\u95f4\uff0c\u51b3\u5b9a\u4e86\u4e00\u4e2a\u4eba\u7684\u547d\u8fd0\uff0c\u8a00\u8bed\u795e\u60c5\u4e4b \u5f97\u4f53\uff0c\u5374\u53c8\u4f7f\u522b\u4eba\u7edd\u5bf9\u4e0d\u4f1a\u4e3a\u4e86\u6ca1\u6709\u8bf4\u8bdd\u7684\u673a\u4f1a\u800c\u607c\u6012\u3002 \u3000\u3000\u201c\u534e\u5c71\u94f6\u9e64\u201d\u542b\u7b11\u8c26\u8c22\u4e24\u53e5\uff0c\u5fc3\u4e2d\u5374\u4e0d\u7981\u6697\u53f9\uff0c\u5fd6\u9053\uff1a\u201c\u4e5d\u8db3\u795e\u86db\u679c\u7136\u540d\u4e0b\u65e0\u865a\uff0c\u201d \u201c\u4e71\u53d1\u5934\u9640\u201d\u4ea6\u81ea\u83ab\u540d\u5176\u5999\u5730\u653e\u677e\u4e86\u624b\u638c\uff0c\u5c06\u80e1\u4e4b\u8f89\u6454\u5728\u5730\u4e0a\uff0c\u5411\u6881\u4e0a\u4eba\u9053\uff1a\u201c\u4f60\u53ef\u662f\u8981\u5bf9 \u4ed8\u4ed6\u4e48\uff1f\u201d \u3000\u3000\u6881\u4e0a\u4eba\u5fae\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u6b63\u8981\u8bf7\u80e1\u5144\u53bb\u597d\u597d\u4eab\u4e0a\u51e0\u5929\u6e05\u798f\uff0c\u7136\u540e\u8fd8\u6709\u501f\u91cd\u80e1\u5144\u4e4b \u5904\u3002\u201d \u3000\u3000\u4ed6\u624b\u638c\u4e00\u6325\uff0c\u7acb\u523b\u6709\u56db\u6761\u52b2\u88c5\u5927\u6c49\uff0c\u5c06\u60ca\u9b42\u672a\u5b9a\uff0c\u5168\u65e0\u6597\u5fd7\u7684\u80e1\u4e4b\u8f89\u7ed1\u4e86\u8d77\u6765\u3002 \u3000\u3000\u6881\u4e0a\u4eba\u76ee\u5149\u626b\u5904\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6b64\u95f4\u65e2\u5df2\u65e0\u4e8b\uff0c\u5728\u4e0b\u5374\u8fd8\u6709\u4e8b\u6599\u7406\uff0c\u53ea\u5f97\u5148\u8d70\u4e00\u6b65 \u4e86\u3002\u201d \u3000\u3000\u6b64\u523b\u8fd9\u5947\u5f02\u7684\u5e10\u7bf7\u5916\u867d\u7136\u4ecd\u6709\u5018\u4f6f\u7740\u7684\u9a7c\u9a6c\uff0c\u4f46\u90a3\u5947\u5f02\u7684\u8499\u9762\u8001\u4eba\uff0c\u8499\u88c5\u5c11\u5973\uff0c\u4ee5\u53ca\u90a3 \u9ec4\u8863\u7ae5\u5b50\u5374\u5df2\u4e0d\u89c1\u8e2a\u5f71\u3002 \u3000\u3000\u201c\u4e5d\u8db3\u795e\u86db\u201d\u6881\u4e0a\u4eba\u5927\u6b65\u5f53\u5148\uff0c\u7387\u9886\u7740\u8fd9\u4e00\u7fa4\u6c5f\u6e56\u597d\u6c49\uff0c\u8702\u62e5\u7740\u4e0a\u4e86\u9a6c\uff0c\u5374\u5c06\u80e1\u4e4b\u8f89\u7f1a \u5728\u9a6c\u540e\u3002 \u3000\u3000\u4e1d\u97ad\u4e00\u626c\uff0c\u5feb\u9a6c\u5954\u8d77\u3002 \u3000\u3000\u201c\u516b\u9762\u73b2\u73d1\u201d\u80e1\u4e4b\u8f89\u53cc\u81c2\u88ab\u7ed1\uff0c\u5468\u8eab\u4e0d\u80fd\u52a8\u5f39\uff0c\u4f46\u4e24\u6761\u817f\u5374\u53ef\u4ee5\u81ea\u7531\u6d3b\u52a8\uff0c\u4e8e\u662f\u4fbf\u82e6\u4e86 \u8fd9\u4e24\u6761\u817f\u4e86\u3002 \u3000\u3000\u5feb\u9a6c\u4e00\u5954\uff0c\u4e5f\u53ea\u5f97\u968f\u7740\u72c2\u5954\uff0c\u5f00\u59cb\u65f6\u4ed6\u4ed7\u7740\u4e00\u8eab\u8f7b\u529f\uff0c\u8fd8\u4e0d\u89c9\u5341\u5206\u75db\u82e6\uff0c\u53ea\u89c9\u6709\u4e9b\u7f9e\u8fb1 \u6c14\u6124\u800c\u5df2\uff0c\u4e0d\u4f4f\u5728\u9a6c\u540e\u72c2\u547c\uff01 \u3000\u3000\u201c\u6881\u5144\uff01\u2026\u2026\u6881\u5927\u54e5\u2026\u2026\u5c0f\u5f1f\u53c8\u4e0d\u66fe\u5f97\u7f6a\u4f60\uff0c\u4f60\u4f55\u82e6\u5982\u6b64\u5f85\u6211\uff1f\u201d \u3000\u3000\u4f46\u5230\u4e86\u540e\u6765\uff0c\u9a6c\u5954\u6108\u6025\uff0c\u4ed6\u5c31\u6e10\u6e10\u4e0d\u80fd\u652f\u6301\uff0c\u8bf4\u8bdd\u547c\u558a\u58f0\u4e5f\u5168\u90fd\u53d8\u6210\u4e86\u6c14\u5598\uff0c\u4e24\u6761\u817f\u867d \u7c97\uff0c\u5374\u4e5f\u652f\u6301\u4e0d\u4e86\u4ed6\u8eab\u4f53\u7684\u8d1f\u8377\u3002 \u3000\u3000\u6881\u4e0a\u4eba\u624b\u63d0\u7740\u4e1d\u97ad\uff0c\u56de\u9996\u7b11\u9053\uff1a\u201c\u80e1\u5144\u8fd1\u6765\u5fc3\u5e7f\u4f53\u80d6\uff0c\u5982\u6b64\u8fd0\u52a8\u4e00\u4e0b\uff0c\u5fc5\u5b9a\u5bf9\u8eab\u4f53\u6709\u76ca \u5f97\u5f88\u3002\u201d \u3000\u3000\u4f17\u597d\u6c49\u4e00\u9f50\u653e\u58f0\u72c2\u7b11\u8d77\u6765\uff01 \u3000\u3000\u80e1\u4e4b\u8f89\u9053\uff1a\u201c\u6881\u2026\u2026\u54b3\u54b3\u2026\u2026\u54bb\u54bb\u2026\u2026\u9976\u4e86\u6211\u5427\u2026\u2026\u201d \u3000\u3000\u4ed6\u62fc\u5c3d\u5168\u529b\uff0c\u653e\u58f0\u5636\u51fa\u6700\u540e\u56db\u5b57\uff0c\u4fbf\u6251\u5730\u5012\u5728\u5730\u4e0a\u3002 \u3000\u3000\u65b0\u5236\u7ef8\u886b\uff0c\u78e8\u7740\u5730\u4e0a\u7684\u7802\u77f3\uff0c\u78e8\u7834\u4e86\uff0c\u7802\u77f3\u5c31\u5f00\u59cb\u63a5\u89e6\u5230\u4ed6\u53d1\u4eae\u7684\u8089\uff0c\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4ed6 \u5fc3\u91cc\u7a81\u7136\u5341\u5206\u540e\u6094\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u4ed6\u82e5\u662f\u5c11\u505a\u4e9b\u5978\u72e1\u7684\u4e8b\uff0c\u591a\u7ec3\u4e9b\u6b66\u529f\uff0c\u4eca\u65e5\u53c8\u4f55\u81f3\u5982\u6b64\u3002 \u3000\u3000\u6881\u4e0a\u4eba\u56de\u9996\u4e00\u671b\uff0c\u7a81\u5730\u624b\u638c\u4e00\u626c\uff0c\u52d2\u4f4f\u9a6c\u50f5\uff0c\u7fa4\u9a6c\u4e5f\u4e00\u9f50\u505c\u4e86\u4e0b\u6765\uff0c\u6881\u4e0a\u4eba\u4e00\u8dc3\u4e0b\u9a6c\uff0c \u6276\u8d77\u4e86\u80e1\u4e4b\u8f89\uff0c\u7b11\u9053\uff1a\u201c\u80e1\u5144\u4eca\u65e5\u53ef\u662f\u8f9b\u82e6\u4e86\u3002\u201d \u3000\u3000\u80e1\u4e4b\u8f89\u6c14\u5598\u5982\u725b\uff0c\u54ea\u91cc\u8fd8\u80fd\u7b54\u8bdd\uff0c\u6881\u4e0a\u4eba\u5374\u5c06\u4ed6\u631f\u4e0a\u4e86\u9a6c\uff0c\u5e26\u5230\u676d\u5dde\u57ce\u5916\u4e00\u4e2a\u4e0d\u7b97\u5927\u5c0f \u7684\u6751\u5e84\uff0c\u4e00\u5ea7\u9887\u4e3a\u5bbd\u655e\uff0c\u4f46\u5e76\u4e0d\u534e\u4e3d\u7684\u5e84\u9662\u4e2d\uff0c\u6b64\u523b\u5929\u5df2\u53d1\u9ed1\uff0c\u5927\u5802\u4e0a\u70db\u706b\u901a\u660e\uff0c\u5df2\u6446\u597d\u4e00 \u684c\u676f\u7b77\u3002 \u3000\u3000\u6881\u4e0a\u4eba\u6276\u7740\u72b9\u5728\u6c14\u5598\u7740\u7684\u80e1\u4e4b\u8f89\u8d70\u4e0a\u5927\u5802\uff0c\u624b\u638c\u4e00\u62cd\uff0c\u56db\u4e2a\u660e\u7738\u970e\u7728\u7684\u7c89\u8863\u5973\u5b50\uff0c\u7acb\u523b \u5728\u684c\u4e0a\u6446\u8d77\u4e00\u684c\u6781\u4e3a\u4e30\u5bcc\u7684\u9152\u83dc\u3002 \u3000\u3000\u9e21\u9e2d\u9c7c\u8089\uff0c\u9999\u817e\u6ee1\u5802\uff0c\u80e1\u4e4b\u8f89\u7cbe\u795e\u7acb\u523b\u4e00\u632f\u2014\u2014\u76f4\u5230\u76ee\u524d\u4e3a\u6b62\uff0c\u4e16\u4e0a\u8fd8\u6ca1\u6709\u53d1\u73b0\u6709\u591a\u5c11 \u4e8b\u6bd4\u80d6\u5b50\u7684\u98df\u6b32\u53ef\u6015\u3002 \u3000\u3000\u6881\u4e0a\u4eba\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4e9b\u9152\u83dc\u80e1\u5144\u8fd8\u6ee1\u610f\u4e48\uff1f\u201d \u3000\u3000\u80e1\u4e4b\u8f89\u867d\u7136\u5fc3\u601d\u7075\u5de7\uff0c\u6b64\u523b\u5374\u4e5f\u4e0d\u77e5\u6881\u4e0a\u4eba\u662f\u4f55\u7528\u5fc3\u3002 \u3000\u3000\u4ed6\u5446\u5446\u5730\u6014\u4e86\u534a\u664c\uff0c\u5450\u5450\u9053\uff1a\u201c\u597d\u6781\u597d\u6781\u3002\u201d \u3000\u3000\u6881\u4e0a\u4eba\u4e00\u7b11\u53c8\u9053\uff1a\u201c\u7aef\u83dc\u7684\u8fd9\u4e9b\u5973\u5b50\uff0c\u4ff1\u662f\u626c\u5dde\u57ce\u4e2d\u6709\u540d\u7684\u7c89\u5934\uff0c\u5c0f\u5f1f\u6628\u65e5\u5df2\u770b\u8fc7\u4e86\u4ed6 \u4eec\u7684\u6b4c\u821e\uff0c\u786e\u5b9e\u4e0d\u9519\u2026\u2026\u201d \u3000\u3000\u80e1\u4e4b\u8f89\u60c5\u4e0d\u81ea\u7981\u5730\u8f6c\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u8fd9\u4e9b\u7c89\u8863\u5973\u5b50\uff0c\u50cf\u662f\u4e00\u6392\u5c4f\u98ce\u4f3c\u7684\u7ad9\u5728\u4ed6\u9762\u524d\uff0c\u516b\u9053 \u4f3c\u80fd\u52fe\u593a\u9b42\u9b44\u7684\u773c\u775b\uff0c\u4e00\u9f50\u671b\u5728\u4ed6\u8eab\u4e0a\u3002 \u3000\u3000\u5239\u90a3\u95f4\u4ed6\u8eab\u4e0a\u7684\u75b2\u52b3\u4e0e\u75db\u82e6\u4f3c\u4e4e\u5df2\u7ecf\u51cf\u8f7b\u4e86\u51e0\u5206\uff0c\u4e0d\u4f4f\u988c\u9996\u9053\uff1a\u201c\u786e\u5b9e\u4e0d\u9519\uff0c\u786e\u5b9e\u4e0d \u9519\u2026\u2026\u201d \u3000\u3000\u6881\u4e0a\u4eba\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u80e1\u5144\u5bf9\u8fd9\u56db\u4f4d\u5973\u5b50\uff0c\u4e5f\u662f\u6781\u4e3a\u6ee1\u610f\u7684\u4e86\u3002\u201d \u3000\u3000\u80e1\u4e4b\u8f89\u53c8\u81ea\u4e00\u6014\uff0c\u5450\u5450\u9053\uff1a\u201c\u6881\u5144\uff0c\u5c0f\u5f1f\u2026\u2026\u5509\uff0c\u81ea\u7136\u662f\u6781\u4e3a\u6ee1\u610f\u7684\uff0c\u6881\u5144\u5230\u5e95\u8981\u5982\u4f55 \u5bf9\u5f85\u5c0f\u5f1f\uff0c\u5c0f\u5f1f\u5b9e\u5728\u2026\u2026\u201d \u3000\u3000\u6881\u4e0a\u4eba\u542b\u7b11\u622a\u53e3\u9053\uff1a\u201c\u65b9\u624d\u5c0f\u5f1f\u5bf9\u80e1\u5144\u6781\u4e3a\u5931\u793c\uff0c\u5c0f\u5f1f\u5fc3\u91cc\u5b9e\u5728\u96be\u53d7\u5f97\u5f88\uff0c\u662f\u4ee5\u60f3\u8981\u8865 \u507f\u4e00\u4e0b\uff0c\u4e5f\u8bf7\u80e1\u5144\u4e0d\u8981\u5c06\u65b9\u624d\u7684\u4e8b\u653e\u5728\u5fc3\u4e0a\u3002\u201d \u3000\u3000\u80e1\u4e4b\u8f89\u5446\u4e86\u4e00\u5446\uff0c\u9762\u4e0a\u4e0d\u7981\u7efd\u5f00\u4e86\u4e00\u4e1d\u5f00\u5fc3\u7684\u7b11\u5bb9\uff0c\u54c8\u54c8\u9053\uff1a\u201c\u6211\u65e9\u77e5\u9053\u6881\u5144\u662f\u4e2a\u4e49\u6c14 \u670b\u53cb\uff0c\u4e0d\u4f1a\u5bf9\u5c0f\u5f1f\u600e\u6837\u7684\uff0c\u4f60\u6211\u4ff1\u662f\u81ea\u5df1\u4eba\uff0c\u6211\u600e\u4f1a\u5c06\u90a3\u4e9b\u5c0f\u4e8b\u653e\u5728\u5fc3\u4e0a\u3002\u201d \u3000\u3000\u7cb1\u4e0a\u4eba\u542b\u7b11\u9053\uff1a\u201c\u597d\u6781\u597d\u6781\uff0c\u53ea\u662f\u9152\u83dc\u7c97\u52a3\uff0c\u8bf7\u80e1\u5144\u968f\u610f\u4eab\u7528\u4e00\u4e9b\uff0c\u7136\u540e\u2026\u2026\u54c8\u54c8\u3002\u201d \u3000\u3000\u80e1\u4e4b\u8f89\u76ee\u5149\u5fcd\u4e0d\u4f4f\u53c8\u5411\u90a3\u56db\u4e2a\u5973\u5b50\u671b\u4e86\u8fc7\u53bb\uff0c\u5fcd\u4e0d\u4f4f\u4e5f\u54c8\u54c8\u5927\u7b11\u8d77\u6765\uff0c\u80f8\u819b\u4e00\u633a\uff0c\u62ff\u8d77 \u4e00\u53cc\u7259\u7b77\uff0c\u5f53\u5373\u5411\u9762\u524d\u4e00\u7897\u732a\u8e44\u6233\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u6881\u4e0a\u4eba\u7a81\u5730\u7b11\u5bb9\u4e00\u655b\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d \u3000\u3000\u80e1\u4e4b\u8f89\u624b\u8155\u4e00\u9707\uff0c\u201c\u53ee\u201d\u7684\u4e00\u58f0\uff0c\u7b77\u5b50\u5df2\u78b0\u5230\u7897\u8fb9\uff0c\u5374\u518d\u4e5f\u4e0d\u6562\u843d\u4e0b\u53bb\uff0c\u76ee\u5149\u832b\u7136\u671b\u5411 \u6881\u4e0a\u4eba\u3002 \u3000\u3000\u6881\u4e0a\u4eba\u9762\u6c89\u5982\u6c34\uff0c\u9053\uff1a\u201c\u80e1\u5144\u4e45\u8d70\u6c5f\u6e56\uff0c\u600e\u5730\u4e0d\u77e5\u9053\u5fe0\u4e49\u5802\u4e0a\uff0c\u4e3b\u4eba\u672a\u52a8\uff0c\u5ba2\u4eba\u5c82\u80fd\u5148 \u5c1d\uff01\u201d \u3000\u3000\u80e1\u4e4b\u8f89\u4e5f\u4e0d\u6562\u591a\u95f4\u8fd9\u662f\u54ea\u91cc\u7684\u89c4\u77e9\uff0c\u4f46\u5fc3\u4e2d\u603b\u7b97\u7565\u4e3a\u5b9a\u4e86\u4e00\u4e9b\uff0c\u7f29\u56de\u7b77\u5b50\uff0c\u966a\u7740\u7b11\u8138 \u9053\uff1a\u201c\u5c0f\u5f1f\u5931\u672d\uff0c\u5c0f\u5f1f\u5931\u793c\u2026\u2026\u6881\u5144\u8bf7\u3002\u201d \u3000\u3000\u6881\u4e0a\u4eba\u7b11\u5bb9\u5fae\u73b0\uff0c\u4e3e\u8d77\u7b77\u5b50\uff0c\u4f38\u51fa\u4e00\u534a\uff0c\u7a81\u53c8\u957f\u53f9\u4e00\u58f0\uff0c\u7f29\u4e86\u56de\u53bb\u3002 \u3000\u3000\u80e1\u4e4b\u8f89\u832b\u7136\u9053\uff1a\u201c\u6881\u5144\uff0c\u83dc\u5982\u51b7\u4e86\uff0c\u6709\u635f\u6ecb\u5473\u3002\u201d \u3000\u3000\u6881\u4e0a\u4eba\u6447\u5934\u53f9\u9053\u3002 \u3000\u3000\u201c\u80e1\u5144\u4f60\u6709\u6240\u4e0d\u77e5\uff0c\u5c0f\u5f1f\u5fc3\u4e2d\uff0c\u6b64\u523b\u6b63\u6709\u51e0\u4ef6\u5fc3\u4e8b\u5b9e\u5728\u4e0d\u80fd\u7b49\u7740\uff0c\u8fd8\u8bf7\u80e1\u5144\u5c11\u5019\u4e00 \u4e0b\u3002\u201d \u3000\u3000\u4ed6\u653e\u4e0b\u7af9\u7b77\uff0c\u5446\u5750\u684c\u65c1\uff0c\u4e0d\u4f4f\u957f\u5401\u77ed\u53f9\u8d77\u6765\u3002 \u3000\u3000\u4e00\u9635\u9635\u9152\u83dc\u7684\u9999\u6c14\uff0c\u51b2\u5230\u80e1\u4e4b\u8f89\u9f3b\u5b50\u91cc\uff0c\u53ea\u89c1\u4ed6\u5589\u7ed3\u4e0a\u4e0b\u79fb\u52a8\uff0c\u4e0d\u4f4f\u5728\u5077\u5077\u54bd\u7740\u53e3\u6c34\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u664c\uff0c\u7ec8\u4e8e\u518d\u4e5f\u65e0\u6cd5\u5fcd\u8010\uff0c\u8f7b\u8f7b\u9053\uff1a\u201c\u6881\u5144\u7a76\u7adf\u6709\u4ec0\u4e48\u5fc3\u4e8b\uff0c\u4e0d\u77e5\u80fd\u5426\u76f8\u544a\uff0c\u8ba9\u5c0f \u5f1f\u4e5f\u4e3a\u4f60\u5206\u4f18\u4e00\u6881\u4e0a\u4eba\u5c55\u989c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u80e1\u5144\u82e5\u80af\u7a0d\u4e3a\u5e2e\u52a9\uff0c\u5c0f\u5f1f\u7684\u5fc3\u4e8b\u4fbf\u5168\u90fd\u6ca1\u6709\u4e86\u3002\u201d \u3000\u3000\u80e1\u4e4b\u8f89\u53cc\u7709\u4e00\u76b1\uff0c\u671b\u4e86\u671b\u684c\u4e0a\u7684\u9152\u83dc\uff0c\u53c8\u671b\u4e86\u671b\u90a3\u56db\u4e2a\u5a9a\u4eba\u7684\u5973\u5b50\uff0c\u5f90\u5f90\u9053\uff1a\u201c\u5c0f\u5f1f\u867d \u4e0d\u6210\u6750\uff0c\u4f46\u6881\u5927\u54e5\u82e5\u6709\u4ec0\u4e48\u6025\u4e8b\uff0c\u5c0f\u5f1f\u81f3\u5c11\u8fd8\u53ef\u4ee5\u5728\u6bdb\u5927\u54e5\u9762\u524d\u8fdb\u8a00\u4e00\u4e8c\uff01\u2026\u2026\u201d \u3000\u3000\u6881\u4e0a\u4eba\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u80e1\u5144\u679c\u7136\u662f\u597d\u670b\u53cb\uff0c\u597d\u670b\u53cb\uff01\u2026\u2026\u201d \u3000\u3000\u6881\u4e0a\u4eba\u7b11\u58f0\u7a81\u53c8\u4e00\u987f\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u80e1\u5144\u65e2\u662f\u597d\u670b\u53cb\uff0c\u60f3\u6765\u5fc5\u5b9a\u53ef\u4ee5\u4e3a\u6211\u89e3\u9664\u75db\u82e6\uff1f\u201d \u3000\u3000\u80e1\u4e4b\u8f89\u7b11\u58f0\u4e5f\u4e0d\u7981\u968f\u4e4b\u987f\u4f4f\uff0c\u5450\u5450\u9053\uff1a\u201c\u81ea\u7136\uff01\u81ea\u7136\u2026\u2026\u4e0d\u77e5\u6881\u5144\u5230\u5e95\u6709\u4f55\u75db\u82e6\u8c03\u6881\u4e0a \u4eba\u957f\u53f9\u9053\uff1a\u201c\u4e16\u4e0a\u6700\u5927\u4e4b\u75db\u82e6\uff0c\u4fbf\u662f\u5fc3\u4e2d\u6709\u4e86\u4e00\u4e9b\u6781\u5927\u7684\u7591\u56e2\uff0c\u800c\u81ea\u5df1\u504f\u53c8\u65e0\u6cd5\u89e3\u91ca\uff0c\u4e8e\u662f \u7ec8\u65e5\u82e6\u82e6\u731c\u6d4b\uff0c\u4e8e\u662f\u7761\u4e0d\u5b89\u5bdd\uff0c\u98df\u4e0d\u77e5\u5473\u3002\u201d \u3000\u3000\u80e1\u4e4b\u8f89\u5e72\u54b3\u4e24\u58f0\uff0c\u5450\u5450\u9053\uff1a\u201c\u6b63\u662f\u6b63\u662f\uff01\u201d \u3000\u3000\u6881\u4e0a\u4eba\u5c55\u989c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u80e1\u5144\u82e5\u662f\u540c\u60c5\u5c0f\u5f1f\uff0c\u82e5\u771f\u662f\u5f1f\u4e4b\u597d\u53cb\uff0c\u90a3\u4e48\u5c0f\u5f1f\u4fbf\u662f\u8bf7\u6559\u80e1\u5144 \u4e00\u53e5\uff0c\u90a3\u5341\u4f59\u5e74\u6765\u672a\u66fe\u5165\u5173\u7684\u2018\u6e29\u67d4\u9677\u9631\u2019\u4e4b\u4e3b\uff0c\u4eba\u79f0\u2018\u4eba\u547d\u730e\u6237\uff0c\u7684\u8499\u9762\u5947\u4eba\uff0c\u7a76\u7adf\u4e3a\u4e86 \u4f55\u4e8b\u800c\u5230\u6c5f\u5357\u6765\u7684\uff1f\u6b64\u4eba\u7684\u672c\u6765\u9762\u76ee\uff0c\u7a76\u7adf\u662f\u8c01\uff1f\u201d\u80e1\u4e4b\u8f89\u9762\u8272\u7a81\u5730\u4e00\u53d8\uff0c\u653e\u4e0b\u7b77\u5b50\uff0c\u5e72\u7b11 \u9053\uff1a\u201c\u5c0f\u5f1f\u8db3\u8ff9\u672a\u51fa\u6c5f\u5357\uff0c\u90a3\u2018\u4eba\u547d\u730e\u6237\u2019\u7684\u4e8b\uff0c\u5c0f\u5f1f\u600e\u4f1a\u77e5\u9053\uff1f\u201d \u3000\u3000\u6881\u4e0a\u4eba\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u2018\u4eba\u547d\u730e\u6237\uff0c\u4e00\u81f3\u6c5f\u5357\uff0c\u4fbf\u4e0e\u2019\u7075\u86c7\u2019\u6bdb\u5927\u7237\u6709\u4e86\u8054\u7edc\uff0c\u4ed6\u82e5 \u975e\u9752\u5e74\u4fbf\u4e0e\u6bdb\u5927\u7237\u6709\u65e7\uff0c\u600e\u4f1a\u5982\u6b64\uff1f\u4ed6\u82e5\u4e0e\u6bdb\u5927\u7237\u6709\u65e7\uff0c\u80e1\u5144\u4f60\u600e\u4f1a\u4e0d\u77e5\u9053\u4ed6\u7684\u5e95\u7ec6\uff1f\u4f55\u51b5 \u80e1\u5144\u4f60\u8fd9\u4e24\u5929\u6765\uff0c\u4e00\u76f4\u4f4f\u5728\u90a3\u2018\u6e29\u67d4\u9677\u9631\uff0c\u91cc\uff0c\u4f3c\u4e4e\u4e13\u95e8\u4e3a\u4e86\u8981\u7b49\u5019\u90a3\u4f4d\u7f2a\u516c\u5b50\u8d70\u8fc7\uff0c\u4ed6\u65e2 \u975e\u6b66\u6797\u4e2d\u4eba\uff0c\u90a3\u2019\u4eba\u547d\u730e\u6237\u2019\u4e3a\u4f55\u8981\u5bf9\u4ed6\u5982\u6b64\u5173\u5fc3\uff1f\u201d \u3000\u3000\u80e1\u4e4b\u8f89\u5fc3\u5934\u4e00\u51db\uff0c\u5fd6\u9053\uff1a\u201c\u4e5d\u8db3\u795e\u86db\u679c\u7136\u5389\u5bb3\uff0c\u8fd9\u8fb9\u7684\u4e00\u4e3e\u4e00\u52a8\uff0c\u90fd\u9003\u4e0d\u51fa\u4ed6\u7684\u773c \u7ebf\u3002\u201d \u3000\u3000\u5fc3\u5ff5\u6b62\u6b64\uff0c\u53e3\u4e2d\u5374\u563f\u563f\u5f3a\u7b11\u9053\uff1a\u201c\u6bdb\u5927\u54e5\u53ea\u4e3a\u4e86\u4ed6\u7684\u5343\u91d1\u4f3c\u5bf9\u7f2a\u516c\u5b50\u6709\u60c5\uff0c\u662f\u4ee5\uff0c\u624d\u60f3 \u67e5\u67e5\u4ed6\u7684\u5e95\u7ec6\uff0c\u6b64\u4e8b\u6839\u672c\u4e0e\u2018\u4eba\u547d\u730e\u6237\u2019\u65e0\u5173\u2026\u2026\u201d \u3000\u3000\u4ed6\u76ee\u5149\u4e00\u8f6c\uff0c\u63a5\u53e3\u53c8\u9053\uff1a\u201c\u7f2a\u516c\u5b50\u65e2\u975e\u6b66\u6797\u4e2d\u4eba\uff0c\u5374\u4e0d\u77e5\u6881\u5927\u54e5\u4e3a\u4f55\u5bf9\u4ed6\u5982\u6b64\u5173\u5fc3\uff1f\u201d \u3000\u3000\u6881\u4e0a\u4eba\u6d53\u7709\u4e00\u626c\uff0c\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u653e\u4e0b\u7b77\u5b50\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u80e1\u5144\u8fd1\u6765\u52a8\u53e3\u52a8\u5f97\u5927\u591a\uff0c\u52a8\u624b \u5374\u52a8\u5f97\u592a\u5c11\uff0c\u60f3\u5fc5\u662f\u8fd8\u8981\u518d\u50cf\u65b9\u624d\u90a3\u6837\u8fd0\u52a8\u4e00\u756a\u2026\u2026\u201d \u3000\u3000\u4ed6\u53cc\u638c\u4e00\u62db\uff0c\u6c89\u58f0\u559d\u9053\uff1a\u201c\u6765\u4eba\u2026\u2026\u201d \u3000\u3000\u80e1\u4e4b\u8f89\u53d8\u8272\u9053\uff1a\u201c\u6881\u5144\u4e14\u6162\uff01\u201d \u3000\u3000\u4ed6\u4f38\u624b\u4e00\u62c9\u6881\u4e0a\u4eba\u81c2\u8180\uff0c\u9053\uff1a\u201c\u5927\u5bb6\u4ff1\u662f\u81ea\u5df1\u5f1f\u5144\uff0c\u6709\u4ec0\u4e48\u8bdd\u90fd\u597d\u5546\u91cf\u3002\u201d \u3000\u3000\u6881\u4e0a\u4eba\u624b\u8155\u4e00\u7529\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u80e1\u5144\u662f\u5426\u5df2\u60f3\u901a\u4e86\uff0c\u8fd8\u662f\u8bf4\u51fa\u6765\u7684\u597d\uff01\u201d \u3000\u3000\u80e1\u4e4b\u8f89\u957f\u53f9\u4e00\u58f0\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4e0d\u7792\u6881\u5144\u8bf4\uff0c\u8fd1\u6765\u6c5f\u6e56\u4e2d\u6240\u6709\u7684\u4e3e\u52a8\uff0c\u90fd\u662f\u4e3a\u4e86\u2026\u2026\u201d \u3000\u3000\u6881\u4e0a\u4eba\u6c89\u58f0\u9053\uff1a\u201c\u4ec0\u4e48\u4e3e\u52a8\uff0c\u8bf4\u6e05\u695a\u4e9b\u3002\u201d \u3000\u3000\u80e1\u4e4b\u8f89\u76ee\u5149\u56db\u8f6c\uff0c\u53ea\u89c1\u5385\u524d\u5df2\u6d8c\u4e0a\u5341\u6570\u6761\u52b2\u88c5\u5927\u6c49\uff0c\u4eba\u4eba\u4ff1\u662f\u5f13\u4e0a\u5f26\u3001\u5200\u51fa\u9798\uff0c\u4eba\u4eba\u7709 \u5b87\u95f4\u4ff1\u662f\u4e00\u7247\u6740\u6c14\u3002 \u3000\u3000\u4ed6\u53ea\u89c9\u5fc3\u5934\u4e00\u5bd2\uff0c\u8d76\u7d27\u63a5\u7740\u9053\uff1a\u201c\u8b6c\u5982\u6bdb\u5927\u54e5\u5728\u676d\u5dde\u57ce\u4e2d\u6240\u9080\u7684\u82f1\u96c4\u4e4b\u4f1a\uff0c\u8b6c\u5982\u6614\u5e74\u7684 \u2018\u4e03\u5251\u4e09\u97ad\u2019\u4ff1\u90fd\u517c\u7a0b\u8d76\u5230\u6c5f\u5357\uff0c\u8b6c\u5982\u90a3\u4f4d\u4ece\u672a\u51fa\u5173\u7684\u2018\u4eba\u547d\u730e\u6237\uff0c\u4e5f\u6765\u5230\u6b64\u95ee\u2026\u2026\u8fd9\u4e00\u5207 \u90fd\u662f\u4e3a\u4e86\u67e5\u660e\u4e00\u4e8b\u2026\u2026\uff0c\u2019\u4ed6\u8bed\u58f0\u7a81\u5730\u53d8\u5f97\u7f13\u6162\u800c\u6c89\u91cd\uff0c\u4e00\u5b57\u4e00\u5b57\u5730\u63a5\u53e3\u9053\uff1a\u201c\u90fd\u662f\u4e3a\u4e86\u8981 \u67e5\u660e\u6614\u5e74\u6b66\u6797\u9b54\u5934\u2018\u4ec7\u5148\u751f\uff0c\u7684\u540e\u4eba\uff0c\u662f\u5426\u5df2\u5728\u6c5f\u6e56\u4e2d\u51fa\u73b0\uff0c\u90a3\u2019\u91d1\u5251\u4fa0\uff0c\u662f\u5426\u4e0e\u2018\u4ec7\u5148 \u751f\u2019\u6709\u5173\u3002\u201d \u3000\u3000\u6881\u4e0a\u4eba\u53cc\u7709\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u8fd8\u6709\u5462\uff1f\u201d \u3000\u3000\u80e1\u4e4b\u8f89\u9053\uff1a\u201c\u8fd8\u6709\u8bb8\u591a\u4eba\u5728\u6697\u4e2d\u6000\u7591\uff0c\u90a3\u4f4d\u7f2a\u516c\u5b50\u2026\u2026\u54b3\u54b3\uff0c\u662f\u5426\u4fbf\u662f\u4ec7\u5148\u751f\u7684\u540e\u4eba\uff0c \u8fd9\u70b9\u5c0f\u5f1f\u5176\u5b9e\u4e5f\u4e0d\u76f8\u4fe1\uff0c\u4f46\u6839\u636e\u8bb8\u591a\u7ebf\u7d22\uff0c\u5374\u53c8\u4ee4\u4eba\u4e0d\u65e0\u7591\u5fc3\uff01\u5509\u2026\u2026\u5c0f\u5f1f\u5982\u6b64\u505a\u6cd5\u4e0d\u8fc7\u662f \u5949\u547d\u884c\u4e8b\u800c\u5df2\u3002\u201d \u3000\u3000\u6881\u4e0a\u4eba\u76ee\u5149\u5fae\u53d8\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4ec0\u4e48\u7ebf\u7d22\uff1f\u96be\u9053\u4f60\u4eec\u5df2\u6709\u4ec0\u4e48\u7ebf\u7d22\uff0c\u53ef\u4ee5\u8bc1\u660e\u8fd9\u6587\u8d28\u5f6c\u5f6c \u7684\u5bcc\u5bb6\u516c\u5b50\uff0c\u4fbf\u662f\u6614\u5e74\u540d\u626c\u516b\u8868\u2018\u4ec7\u5148\u751f\uff0c\u7684\u540e\u4eba\uff1f\u201d\u4ed6\u4ef0\u5929\u5927\u7b11\u51e0\u58f0\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u8fd9\u5012\u771f \u662f\u4e2a\u7b11\u8bdd\uff01\u201d \u3000\u3000\u7b11\u58f0\u662f\u9ad8\u4ea2\u800c\u54cd\u4eae\u7684\uff0c\u9707\u5f97\u684c\u4e0a\u7684\u676f\u76cf\uff0c\u8fb9\u7f18\u76f8\u51fb\uff0c\u53d1\u51fa\u4e00\u8fde\u4e32\u201c\u53ee\u94db\u201d\u8f7b\u54cd\u3002 \u3000\u3000\u4f46\u80e1\u4e4b\u8f89\u76ee\u5149\u4e00\u8f6c\uff0c\u5374\u53d1\u89c9\u4ed6\u8fd9\u54cd\u4eae\u7684\u7b11\u58f0\uff0c\u4f3c\u4e4e\u53ea\u662f\u4e3a\u4e86\u8981\u63a9\u9970\u4ed6\u9762\u4e0a\u67d0\u4e00\u4efd\u4e0d\u81ea\u7136 \u7684\u60c5\u611f\u3002 \u3000\u3000\u6881\u4e0a\u4eba\u7b11\u58f0\u65b9\u987f\uff0c\u80e1\u4e4b\u8f89\u5ffd\u7136\u957f\u53f9\u4e00\u58f0\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u90a3\u7f2a\u516c\u5b50\u82e5\u88ab\u53d1\u89c9\u771f\u7684\u662f\u2018\u4ec7\u5148 \u751f\uff0c\u7684\u540e\u4eba\uff0c\u5176\u540e\u679c\u4e5f\u5c31\u771f\u7684\u4ee4\u4eba\u4e0d\u582a\u8bbe\u60f3\uff0c\u975e\u4f46\u662f\u4ed6\uff0c\u53ea\u6015\u5c31\u8fde\u4ed6\u7684\u670b\u53cb\u548c\u7fbd\u88f3\u2026\u2026\u201d\u6881 \u4e0a\u4eba\u76ee\u5149\u4e00\u51db\uff0c\u62cd\u6848\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u4e00\u638c\u62cd\u4e0b\uff0c\u684c\u4e0a\u7684\u676f\u76cf\u66f4\u88ab\u9707\u5f97\u53ee\u94db\u4e71\u54cd\u3002 \u3000\u3000\u80e1\u4e4b\u8f89\u8eab\u8eaf\u5fae\u5fae\u4e00\u9707\uff0c\u563f\u563f\u5f3a\u7b11\u9053\uff1a\u201c\u8fd9\u53ea\u4e0d\u8fc7\u662f\u731c\u60f3\u800c\u5df2\uff0c\u563f\u563f\uff0c\u60f3\u90a3\u7f2a\u516c\u5b50\u2026\u2026\u201d \u3000\u3000\u6881\u4e0a\u4eba\u6c89\u58f0\u622a\u53e3\u9053\u3002 \u3000\u3000\u201c\u6211\u4e14\u95ee\u4f60\uff0c\u4f60\u7b49\u5230\u5e95\u600e\u4f1a\u5c06\u90a3\u7f2a\u516c\u5b50\u4e0e\u2018\u4ec7\u5148\u751f\u2019\u8bbe\u60f3\u5728\u4e00\u8d77\uff1f\u6211\u6881\u67d0\u65e2\u7136\u4e0e\u4ed6\u4e3a \u53cb\uff0c\u5374\u5bb9\u4e0d\u5f97\u4f60\u4eec\u542b\u8840\u55b7\u4eba\uff0c\u80e1\u4e71\u731c\u6d4b\u3002\u201d \u3000\u3000\u80e1\u4e4b\u8f89\u76ee\u4e2d\u5149\u8292\u95ea\u52a8\uff0c\u5ffd\u7136\u6539\u53e3\u9053\uff1a\u201c\u7ea6\u83ab\u5341\u516b\u4e5d\u5e74\u4e4b\u524d\uff0c\u90a3\u65f6\u6881\u5144\u5728\u6c5f\u6e56\u95f4\u5c1a\u672a\u5d2d\u9732 \u5934\u89d2\uff0c\u5c0f\u5f1f\u66f4\u4e0d\u77e5\u8eab\u5728\u54ea\u91cc\uff0c\u4f46\u2018\u4e03\u5251\u4e09\u97ad\uff0c\u5374\u5df2\u90fd\u58f0\u540d\u5353\u8457\uff0c\u2019\u4ec7\u5148\u751f\u2019\u66f4\u662f\u65e9\u5df2\u540d\u626c\u5929 \u4e0b\uff0c\u4e25\u7136\u5360\u4e86\u6b66\u6797\u4e2d\u7684\u7b2c\u4e00\u628a\u4ea4\u6905\u3002\u201d \u3000\u3000\u6881\u4e0a\u4eba\u51b7\u201c\u54fc\u201d\u4e00\u58f0\uff0c\u867d\u7136\u4e0d\u77e5\u9053\u4ed6\u4e3a\u4f55\u8bf4\u51fa\u8fd9\u756a\u8bdd\u6765\uff0c\u4f46\u8fd9\u756a\u8bdd\u65e2\u4e0e\u201c\u4ec7\u5148\u751f\u201d\u6709 \u5173\uff0c\u4ed6\u4e5f\u6ca1\u6709\u51fa\u53e3\u6253\u65ad\u3002 \u3000\u3000\u53ea\u542c\u80e1\u4e4b\u8f89\u63a5\u53e3\u9053\uff1a\u201c\u90a3\u65f6\u2018\u4ec7\u5148\u751f\uff0c\u7eb5\u6a2a\u6c5f\u6e56\uff0c\u6c5f\u6e56\u4e2d\u4eba\uff0c\u867d\u7136\u4eba\u4eba\u89c1\u4e86\u4ed6\u90fd\u5bb3\u6015\uff0c \u4f46\u5374\u65e0\u4e00\u4eba\u5bf9\u4ed6\u771f\u7684\u5d07\u656c\uff0c\u53ea\u56e0\u4ed6\u884c\u4e8b\u5168\u51ed\u81ea\u5df1\u7684\u597d\u6076\u559c\u6012\uff0c\u4ec0\u4e48\u5929\u7406\u4eba\u60c5\uff0c\u4ed6\u5168\u90fd\u4e0d\u7ba1\u4e0d \u987e\uff0c\u66f4\u522b\u8bf4\u4ec0\u4e48\u4e00\u4e00\u201d\u6881\u4e0a\u4eba\u5927\u559d\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4ec7\u8001\u524d\u8f88\u7684\u4e3a\u4eba\uff0c\u5c82\u662f\u4f60\u53ef\u968f\u610f\u6279\u8bc4\u7684\uff1f\u201d \u3000\u3000\u80e1\u4e4b\u8f89\u9053\uff1a\u201c\u4ec7\u5148\u751f\u7684\u4e00\u751f\u884c\u4e8b\uff0c\u662f\u975e\u529f\u8fc7\uff0c\u522b\u8bf4\u6211\u80e1\u67d0\u4eba\uff0c\u4fbf\u662f\u6b66\u6797\u5f53\u4eca\u51e0\u5927\u95e8\u6d3e\u7684 \u638c\u95e8\u4eba\uff0c\u81f3\u4eca\u4e5f\u4e0d\u6562\u5984\u4e0b\u5b9a\u8bed\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u5fae\u987f\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u4f46\u5c0f\u5f1f\u4eca\u65e5\u8bf4\u6b64\u756a\u8bdd\uff0c\u90fd\u662f\u4e3a\u4e86\u201d\u6881\u4e0a\u4eba\u819b\u76ee\u9053\uff1a\u201c\u4e3a\u4e86\u4ec0 \u4e48\uff1f\u201d \u3000\u3000\u80e1\u4e4b\u8f89\u4e5f\u4e0d\u77e5\u662f\u5426\u6545\u610f\uff0c\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u60f3\u90a3\u4ec7\u5148\u751f\u65e2\u662f\u5982\u6b64\u4e3a\u4eba\uff0c\u5728\u6c5f\u6e56\u4e2d \u600e\u4f1a\u6ca1\u6709\u4ec7\u5bb6\uff0c\u53ea\u662f\u4ec7\u5148\u751f\u6b66\u529f\u5927\u9ad8\uff0c\u6545\u4e16\u53c8\u65e9\uff0c\u8fd9\u4e9b\u4ec7\u5bb6\u5728\u2018\u4ec7\u5148\u751f\u2019\u751f\u524d\u65e0\u6cd5\u590d\u4ec7\uff0c\u6b7b \u540e\u5c31\u66f4\u8c08\u4e0d\u4e0a\u590d\u4ec7\uff0c\u4f46\u5374\u5728\u65f6\u65f6\u523b\u523b\u7559\u610f\uff0c\u4ec7\u5148\u751f\u6614\u5e74\u4ec7\u6709\u65e0\u540e\u4eba\u7559\u4e0b\u3002\u201d \u3000\u3000\u6881\u4e0a\u4eba\u53cc\u7709\u4e00\u626c\uff0c\u9053\uff1a\u201c\u8bf4\u4e0b\u53bb\uff01\u201d \u3000\u3000\u80e1\u4e4b\u8f89\u9053\uff1a\u201c\u4ec7\u5148\u751f\u7a76\u7adf\u6709\u65e0\u540e\u4eba\u7559\u4e0b\uff0c\u6c5f\u6e56\u4e2d\u4eba\u8a00\u4eba\u6b8a\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\u771f\u8c61\u3002\u53ea\u56e0\u2018\u4ec7 \u5148\u751f\u2019\u4e00\u751f\u884c\u8e2a\u98d8\u5ffd\uff0c\u5c31\u8fde\u4ed6\u662f\u5426\u7ed3\u4eb2\uff0c\u6709\u672a\u6536\u5f92\uff0c\u6b66\u6797\u4e2d\u90fd\u65e0\u4eba\u77e5\u9053\uff0c\u53ea\u9664\u4e86\u6211\u90a3\u6bdb\u5927\u54e5 \u4e00\u4eba\u4e4b\u5916\u3002\u201d\u2019\u6881\u4e0a\u4eba\u805a\u7cbe\u4f1a\u795e\uff0c\u53ea\u542c\u80e1\u4e4b\u8f89\u53c8\u9053\uff1a\u201c\u8fd9\u539f\u56e0\u4e3a\u4e86\u4ec0\u4e48\uff0c\u4eca\u65e5\u5728\u6b66\u6797\u4e2d\u5df2\u6210 \u534a\u516c\u5f00\u7684\u79d8\u5bc6\uff0c\u60f3\u6881\u5144\u81ea\u4e5f\u77e5\u9053\uff0c\u6bdb\u5927\u54e5\u5148\u672c\u4e0d\u613f\u5c06\u6b64\u4e8b\u4f20\u626c\u6c5f\u6e56\uff0c\u4f46\u540e\u6765\u60c5\u975e\u5f97\u5df2\uff0c\u53ea\u6709 \u8bf4\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u6b64\u8baf\u4e00\u4f20\uff0c\u7acb\u523b\u5728\u6c5f\u6e56\u4e2d\u4e0d\u80eb\u800c\u8d70\uff0c\u90a3\u4e9b\u2018\u4ec7\u5148\u751f\uff0c\u6614\u65e5\u7684\u4ec7\u5bb6\uff0c\u5c48\u6307\u4e00\u7b97\uff0c\u77e5\u9053\u2019 \u4ec7\u5148\u751f\u2019\u7684\u540e\u4eba\uff0c\u81f3\u4eca\u5e74\u5df2\u53ca\u51a0\uff0c\u8fd9\u4e9b\u4eba\u542b\u6068\u591a\u5e74\uff0c\u6709\u54ea\u4e00\u4e2a\u4e0d\u60f3\u6765\u5bfb\u4ec7\u62a5\u590d\uff0c\u6216\u660e\u6216\u6697\uff0c \u90fd\u5728\u8ffd\u5bfb\u90a3\u2018\u4ec7\u5148\u751f\uff0c\u540e\u4eba\u7684\u4e0b\u843d\uff1f\u201d\u6881\u4e0a\u4eba\u53cc\u7709\u5fae\u76b1\uff0c\u6697\u53f9\u5fd6\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4e0d\u4f46\u4ed6\u8981\u5bfb\u4eba \u590d\u4ec7\uff0c\u522b\u4eba\u4e5f\u8981\u5bfb\u4ed6\u590d\u4ec7\uff0c\u8fd9\u4e00\u573a\u6069\u6028\u7f20\u7ed3\uff0c\u5374\u4e0d\u77e5\u8be5\u5982\u4f55\u4e86\u65ad\uff1f\u201d \u3000\u3000\u80e1\u4e4b\u8f89\u51dd\u76ee\u671b\u4e86\u4ed6\u51e0\u773c\uff0c\u7a81\u5730\u5c55\u989c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5176\u5b9e\u8ba4\u771f\u8bf4\u6765\uff0c\u2018\u4ec7\u5148\u751f\u2019\u5982\u6709\u540e\u4eba\uff0c \u8fd9\u4f4d\u540e\u4eba\u5012\u771f\u7684\u662f\u6bdb\u5927\u54e5\u7684\u8fd1\u4eb2\uff0c\u6614\u5e74\u6bdb\u5927\u54e5\u867d\u7136\u5bf9\u4ec7\u5148\u751f\u2026\u2026\u5509\uff0c\u90a3\u5374\u4e5f\u662f\u4e0d\u5f97\u5df2\u7684\u4e8b\uff0c \u4ed6\u5fc3\u91cc\u8fd8\u662f\u65f6\u65f6\u523b\u523b\u5728\u601d\u5ff5\u7740\u4ed6\u90a3\u4f4d\u5ae1\u4eb2\u7684\u59b9\u59b9\uff0c\u4e5f\u65f6\u65f6\u523b\u523b\u5728\u601d\u5ff5\u7740\u4ed6\u59b9\u59b9\u809a\u4e2d\u7684\u5b69\u5b50\u3002 \u53ea\u8981\u8fd9\u5b69\u5b50\u4e0d\u8bb0\u524d\u4e8b\uff0c\u6bdb\u5927\u54e5\u975e\u4f46\u4e0d\u4f1a\u5bf9\u4ed6\u600e\u6837\uff0c\u8fd8\u4f1a\u5e2e\u4ed6\u6765\u5bf9\u4ed8\u8fd9\u4e00\u5e2e\u4ec7\u5bb6\uff0c\u8fd9\u90fd\u662f\u6bdb\u5927 \u54e5\u79c1\u4e0b\u544a\u8bc9\u6211\u7684\u8bdd\uff0c\u6211\u672c\u4e0d\u8be5\u8bf4\u7684\u3002\u201d \u3000\u3000\u6881\u4e0a\u4eba\u9ed8\u7136\u534a\u664c\uff0c\u76b1\u7709\u9053\uff1a\u201c\u636e\u4f60\u6240\u77e5\uff0c\u6614\u5e74\u4ec7\u5148\u751f\u7684\u4ec7\u5bb6\uff0c\u81f3\u4eca\u5230\u5e95\u8fd8\u6709\u51e0\u4eba\uff1f\u201d \u3000\u3000\u80e1\u4e4b\u8f89\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u4ec7\u5148\u751f\u6614\u5e74\u4ec7\u5bb6\u672c\u5df2\u904d\u5e03\u5929\u4e0b\uff0c\u81f3\u4eca\u8fd9\u4e9b\u4ec7\u5bb6\u53c8\u4e0d\u77e5\u591a\u4e86\u82e5\u5e72\u540e \u4eba\uff0c\u5c0f\u5f1f\u5982\u4f55\u8ba1\u7b97\u5f97\u6e05\uff0c\u8bf4\u4e0d\u5b9a\u2026\u2026\u201d \u3000\u3000\u4ed6\u76ee\u5149\u56db\u4e0b\u4e00\u626b\uff0c\u9053\uff1a\u201c\u8bf4\u4e0d\u5b9a\u6881\u5927\u54e5\u4f60\u8fd9\u4e9b\u5144\u5f1f\u4e2d\uff0c\u4e5f\u6709\u51e0\u4eba\u662f\u4ec7\u5148\u751f\u7684\u5bf9\u5934\u54e9\uff01\u201d \u3000\u3000\u6881\u4e0a\u4eba\u9762\u5bd2\u5982\u6c34\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u90a3\u2018\u4eba\u547d\u730e\u6237\u2019\uff0c\u53ea\u6015\u4e5f\u662f\u2018\u4ec7\u5148\u751f\uff0c\u6614\u65e5\u7684 \u5bf9\u5934\u4e86\uff1f\u201d\u80e1\u4e4b\u8f89\u8fde\u8fde\u9894\u9996\u9053\uff1a\u201c\u8bf4\u4e0d\u5b9a\u8bf4\u4e0d\u5b9a\u2026\u2026\u201d \u3000\u3000\u6881\u4e0a\u4eba\u5927\u559d\u4e00\u58f0\uff1a\u201c\u5230\u5e95\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u80e1\u4e4b\u8f89\u534a\u7b11\u4e0d\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u96be\u9053\u4e0e\u6881\u5927\u54e5\u4f60\u4e5f\u6709\u4ec0\u4e48\u5173\u7cfb\u4e0d\u6210\uff1f\u201d \u3000\u3000\u6881\u4e0a\u4eba\u76ee\u5149\u5982\u5203\uff0c\u4e00\u5b57\u4e00\u5b57\u5730\u7f13\u7f13\u9053\uff1a\u201c\u80e1\u5144\u4f60\u83ab\u5fd8\u4e86\uff0c\u76f4\u5230\u6b64\u523b\uff0c\u4f60\u6027\u547d\u8fd8\u5728\u5c0f\u5f1f\u7684 \u624b\u638c\u4e4b\u4e2d\uff0c\u5c0f\u5f1f\u867d\u65e0\u80fd\uff0c\u6740\u4e2a\u628a\u4eba\u5374\u4e5f\u672a\u89c1\u4f1a\u51fa\u4ec0\u4e48\u5927\u4e8b\u3002\u201d \u3000\u3000\u80e1\u4e4b\u8f89\u5fc3\u5934\u4e00\u5bd2\uff0c\u5446\u5750\u4e86\u534a\u664c\uff0c\u989d\u4e0a\u6e10\u6e10\u6ccc\u51fa\u4e86\u8c46\u5927\u7684\u6c57\u73e0\uff0c\u4ed6\u672c\u6765\u81ea\u6043\u6881\u4e0a\u4eba\u7edd\u5bf9\u4e0d \u6562\u6740\u4ed6\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u6881\u4e0a\u4eba\u5373\u4fbf\u771f\u7684\u5c06\u4ed6\u6740\u6b7b\uff0c\u53c8\u6709\u8c01\u4eba\u77e5\u9053\uff1f\u76ee\u5149\u4e00\u8f6c\uff0c\u56db\u9762\u5200\u950b\u7bad\u94fe \u5bd2\u5149\u95ea\u95ea\u3002 \u3000\u3000\u5fc3\u5ff5\u6570\u8f6c\uff0c\u80e1\u4e4b\u8f89\u7ec8\u4e8e\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u82e5\u5c06\u6b64\u4eba\u771f\u8c61\u8bf4\u51fa\uff0c\u6881\u5927\u54e5\u4f60\u2026\u2026\u201d \u3000\u3000\u6881\u4e0a\u4eba\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6881\u67d0\u4e0e\u80e1\u5144\u5e76\u65e0\u4ec7\u6068\u3002\u201d \u3000\u3000\u80e1\u4e4b\u8f89\u677e\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6881\u5144\u4f60\u53ef\u542c\u4eba\u8bf4\u8fc7\uff0c\u6570\u5341\u5e74\u524d\uff0c\u6c5f\u6e56\u4e2d\u6709\u4f4d\u6210\u540d\u7684\u8001\u6b66\u5e08\uff0c\u4ee5 \u2018\u4e09\u5341\u516d\u8def\u68a8\u82b1\u5927\u67aa\u2019\u5939\u7740\u2018\u4e03\u5341\u4e8c\u8def\u884c\u8005\u68d2\uff0c\u996e\u8a89\u6c5f\u6e56\uff0c\u540d\u5524\u2019\u795e\u67aa\u2019\u6c6a\u9c81\u5e73\u7684\uff1f\u201d \u3000\u3000\u6881\u4e0a\u4eba\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6709\u6b64\u4e00\u4eba\u3002\u201d \u3000\u3000\u80e1\u4e4b\u8f89\u9053\uff1a\u201c\u8fd9\u2018\u795e\u67aa\u2019\u6c6a\u9c81\u5e73\u884c\u4e8b\u867d\u7136\u751a\u662f\u6b63\u76f4\uff0c\u4f46\u5374\u6c14\u5982\u66b4\u706b\u3002\u5341\u5e74\u4e27\u5076\uff0c\u6709\u4e00\u4e00 \u4e2a\u513f\u5b50\uff0c\u8fd9\u513f\u5b50\u636e\u8bf4\u751a\u4e0d\u6210\u6750\uff0c\u6709\u4e00\u65e5\u89e6\u6012\u4e86\u6c6a\u8001\u82f1\u96c4\uff0c\u6c6a\u8001\u82f1\u96c4\u7adf\u8981\u5c06\u90a3\u513f\u5b50\u4e00\u5200\u6740\u6b7b\uff0c \u8fd9\u5176\u95f4\u504f\u504f\u6765\u4e86\u2018\u4ec7\u5148\u751f\uff0c\u2026\u2026\u201d\u6881\u4e0a\u4eba\u9762\u8272\u5fae\u53d8\uff0c\u7a81\u542c\u5385\u5916\u4e00\u58f0\u54c8\u54c8\u5927\u7b11\uff0c\u4e00\u4eba\u5728\u7b11\u7740 \u9053\uff1a\u201c\u597d\u6781\u597d\u6781\uff0c\u539f\u6765\u4ed6\u771f\u7684\u5c31\u662f\u6c6a\u9c81\u5e73\u3002\u201d \u3000\u3000\u7b11\u58f0\u867d\u9ad8\u4ea2\uff0c\u542c\u6765\u5374\u4e0e\u54ed\u58f0\u65e0\u5f02\uff0c\u4e5f\u4e0d\u77e5\u4ed6\u662f\u54ed\u662f\u7b11\u3002 \u3000\u3000\u4f17\u4eba\u4ff1\u90fd\u4e00\u60ca\uff0c\u53ea\u89c1\u6a90\u5934\u4eba\u5f71\u4e00\u95ea\uff0c\u72c2\u98ce\u822c\u5377\u5165\u4e00\u4e2a\u94f6\u7b8d\u4e71\u53d1\u7684\u9ed1\u8863\u5934\u9640\u6765\uff0c\u72ec\u81c2\u4e00 \u6325\uff0c\u5c06\u7acb\u5728\u5385\u524d\u7684\u5341\u6570\u6761\u5927\u6c49\uff0c\u61c2\u5f97\u4e1c\u8dcc\u897f\u5012\uff0c\u8fde\u638c\u4e2d\u7684\u5200\u7bad\u90fd\u638c\u63e1\u4e0d\u4f4f\uff0c\u54d7\u5730\u4e00\u58f0\uff0c\u6492\u5728 \u5730\u4e0a\u3002 \u3000\u3000\u60ca\u547c\u58f0\u4e2d\uff0c\u8fd9\u4e71\u53d1\u5934\u9640\u77a7\u4e5f\u4e0d\u77a7\u522b\u4eba\u4e00\u773c\uff0c\u4e00\u6b65\u8de8\u5230\u80e1\u4e4b\u8f89\u8eab\u524d\u5feb\u5982\u95ea\u7535\u5730\uff0c\u4f38\u51fa\u5de8\u7075 \u7684\u94c1\u638c\u3002 \u3000\u3000\u80e1\u4e4b\u8f89\u4e00\u89c1\u6b64\u4eba\uff0c\u65e9\u5df2\u5413\u5f97\u5446\u4e86\uff0c\u5fc3\u5934\u53d1\u98a4\uff0c\u88e4\u8863\u751f\u51b7\u3002 \u3000\u3000\u4e71\u53d1\u5934\u9640\u5939\u9888\u4e00\u628a\uff0c\u6293\u4f4f\u4e86\u4ed6\uff0c\u5389\u559d\u9053\uff1a\u201c\u4f60\u8bf4\uff0c\u4f60\u8bf4\uff0c\u90a3\u4eba\u6b64\u523b\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u8fc7\u4e86\u534a\u664c\uff0c\u72b9\u65e0\u56de\u7b54\uff0c\u53ea\u542c\u201c\u5580\u201d\u5730\u4e00\u54cd\uff0c\u80e1\u4e4b\u8f89\u7684\u5934\u9885\u7adf\u88ab\u4ed6\u8fd9\u5939\u9888\u4e00\u628a\uff0c\u751f\u751f\u634f\u65ad \u4e86\uff0c\u8fde\u60e8\u547c\u4e4b\u58f0\u90fd\u558a\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u4e71\u53d1\u5934\u9640\u76ee\u5149\u4e00\u6ede\uff0c\u9762\u4e0a\u6012\u5bb9\u6e10\u6e10\u6d88\u5931\uff0c\u624b\u638c\u4e00\u677e\uff0c\u72c2\u51ed\u80e1\u4e4b\u8f89\u7684\u5c38\u8eab\u843d\u5230\u5730\u9762\uff0c\u8f6c\u76ee \u671b\u4e86\u6881\u4e0a\u4eba\u4e00\u773c\uff0c\u5ffd\u7136\u957f\u53f9\u4e00\u58f0\uff0c\u62ff\u8d77\u684c\u4e0a\u7684\u9152\u58f6\uff0c\u4e24\u6307\u4e00\u631f\u6380\u5f00\u58f6\u76d6\uff0c\u5495\u565c\u4e00\u53e3\uff0c\u559d\u5f97\u5e72 \u5e72\u51c0\u51c0\u3002 \u3000\u3000\u5385\u524d\u5341\u6570\u6761\u5927\u6c49\uff0c\u51e0\u66fe\u89c1\u8fc7\u5982\u6b64\u60ca\u4eba\u7684\u795e\u529b\uff0c\u4ff1\u90fd\u5446\u5446\u5730\u6115\u4f4f\u4e86\u3002\u6881\u4e0a\u4eba\u9762\u8272\u5fae\u53d8\uff0c \u9053\uff1a\u201c\u5927\u5e08\u7eb5\u7136\u795e\u529b\u60ca\u4eba\uff0c\u5374\u4e5f\u4e0d\u8be5\u968f\u610f\u4f24\u4eba\u6027\u547d\uff0c\u96be\u9053\u5c06\u6881\u67d0\u89c6\u4e3a\u5e9f\u7269\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u81ea\u7136\u4e0d\u514d\u751f\u51fa\u82a5\u8482\uff0c\u8a00\u8bed\u4e2d\u4fbf\u5e26\u4e86\u950b\u9510\u3002 \u3000\u3000\u54ea\u77e5\u8fd9\u9ed1\u8863\u4e71\u53d1\u5934\u9640\u624b\u6301\u7a7a\u58f6\uff0c\u5446\u5446\u5730\u7ad9\u5728\u54ea\u91cc\uff0c\u7adf\u6839\u672c\u6ca1\u6709\u542c\u5230\u4ed6\u7684\u8bdd\uff0c\u53ea\u662f\u5728\u53e3\u4e2d \u4e0d\u4f4f\u5583\u5583\u81ea\u8bed\uff1a\u201c\u679c\u7136\u662f\u2026\u2026\u679c\u7136\u662f\u4ed6\u2026\u2026\u201d \u3000\u3000\u6881\u4e0a\u4eba\u5fc3\u4e2d\u4e00\u52a8\uff0c\u7a81\u89c1\u8fd9\u4e71\u53d1\u5934\u9640\u5927\u559d\u4e00\u58f0\uff0c\u8f6c\u8eab\u5411\u5916\u51b2\u4e86\u51fa\u53bb\uff0c\u5c06\u6ee1\u6ee1\u4e00\u684c\u9152\u83dc\uff0c\u4ff1 \u90fd\u649e\u5012\u3002 \u3000\u3000\u5385\u524d\u5341\u6570\u6761\u5927\u6c49\uff0c\u5fc3\u5934\u4e00\u60ca\uff0c\u7eb7\u7eb7\u8d70\u907f\uff0c\u8c01\u4e5f\u4e0d\u6562\u9996\u5f53\u5176\u950b\u3002 \u3000\u3000\u4e71\u53d1\u5934\u9640\u53cc\u76ee\u8d64\u7ea2\uff0c\u9762\u4e0a\u5200\u75a4\u4e5f\u9690\u9690\u6cdb\u7740\u7ea2\u5149\uff0c\u6709\u5982\u75af\u864e\u4e00\u822c\u51b2\u51fa\u5385\u5916\uff0c\u7a81\u89c1\u773c\u524d\u4eba\u5f71 \u4e00\u82b1\uff0c\u4e00\u4e2a\u7070\u886b\u4eba\u5df2\u6321\u5728\u4ed6\u8eab\u524d\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u6740\u4e86\u4eba\u5c31\u8d70\uff0c\u4e16\u95ee\u90a3\u6709\u5982\u6b64\u4fbf\u5b9c\u7684\u4e8b\u3002\u201d \u3000\u3000\u4e71\u53d1\u5934\u9640\u53cc\u76ee\u8d64\u7ea2\uff0c\u4e5f\u4e0d\u77e5\u6765\u4eba\u662f\u8c01\uff0c\u53e3\u4e2d\u5389\u559d\u4e00\u58f0\uff1a\u201c\u95ea\u5f00\uff01\u201d \u3000\u3000\u6325\u624b\u4e00\u638c\uff0c\u5411\u9762\u524d\u8fd9\u4eba\u76f4\u626b\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u795e\u529b\u60ca\u4eba\uff0c\u5df2\u662f\u4f17\u4eba\u6709\u76ee\u5171\u7779\u4e4b\u4e8b\uff0c\u8fd9\u4e00\u638c\u98ce\u58f0\u547c\u547c\uff0c\u5a01\u9053\u66f4\u662f\u60ca\u4eba\uff0c\u9762\u524d\u5373\u4f7f\u662f\u682a \u5927\u6811\uff0c\u53ea\u6015\u4e5f\u8981\u88ab\u4ed6\u9707\u5f97\u8fde\u6839\u62d4\u8d77\u3002 \u3000\u3000\u54ea\u77e5\u4ed6\u9762\u524d\u8fd9\u5165\u5374\u4ecd\u7136\u52a8\u4e5f\u4e0d\u52a8\uff0c\u53ea\u542c\u201c\u7830\u201d\u5730\u4e00\u58f0\uff0c\u8fd9\u4e00\u638c\u7adf\u7740\u7740\u5b9e\u5b9e\u51fb\u5728\u8fd9\u4eba\u8eab \u4e0a\u3002 \u3000\u3000\u4f17\u4eba\u4e00\u3002\u9f50\u60ca\u547c\uff0c\u4e71\u53d1\u5934\u9640\u4e5f\u4e0d\u7981\u5fc3\u5934\u4e00\u51db\uff0c\u53ea\u56e0\u4e3a\u4ed6\u8fd9\u4e00\u638c\u51fb\u5728\u5bf9\u65b9\u80f8\u53e3\uff0c\u731b\u89c9\u7740\u624b \u4e4b\u5904\uff0c\u7a81\u7136\u53d8\u5f97\u98d8\u98d8\u8361\u8361\uff0c\u4f46\u5374\u53c8\u4e0d\u662f\u4e00\u638c\u6253\u7a7a\uff0c\u5c31\u4eff\u4f5b\u662f\u4f38\u624b\u5165\u6cb9\uff0c\u4f3c\u7a7a\u975e\u7a7a\uff0c\u4f3c\u5b9e\u975e \u5b9e\uff0c\u53c8\u6709\u4e00\u79cd\u9ecf\u9522\u4e4b\u529b\uff0c\u5438\u5f97\u4ed6\u624b\u638c\u4e0d\u80fd\u52a8\u5f39\u3002 \u3000\u3000\u4e71\u53d1\u5934\u9640\u8fd9\u4e00\u60ca\u5f53\u771f\u975e\u540c\u5c0f\u53ef\uff0c\u62ac\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u4e00\u4e2a\u7070\u5e03\u8888\u88df\uff0c\u624b\u6301\u4f5b\u73e0\u7684\u50e7\u4eba\uff0c\u5355\u638c \u5408\u5341\uff0c\u6c14\u5b9a\u795e\u95f2\u5730\u7acb\u5728\u4ed6\u9762\u524d\uff0c\u6709\u5982\u5c71\u4f2b\u5cb3\u5cd9\u4e00\u822c\uff0c\u52a8\u4e5f\u4e0d\u52a8\u3002\u6881\u4e0a\u4eba\u89c1\u5230\u8fd9\u5916\u95e8\u521a\u731b\u4e4b\u529b \u5df2\u81fb\u6781\u5cf0\u7684\u4e71\u53d1\u5934\u9640\u4e00\u638c\u975e\u4f46\u672a\u5c06\u8fd9\u50e7\u4eba\u51fb\u5012\uff0c\u53cd\u4e3a\u5176\u6240\u5236\uff0c\u5fc3\u4e2d\u4ea6\u662f\u5927\u60ca\uff0c\u65b9\u81ea\u4e00\u6b65\u7a9c\u5230 \u5385\u524d\uff0c\u4fbf\u5df2\u6115\u4f4f\u4e86\u3002 \u3000\u3000\u53ea\u542c\u8fd9\u4e2d\u5e74\u50e7\u4eba\u6717\u541f\u4e00\u58f0\u4f5b\u53f7\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u5584\u54c9\u5584\u54c9\uff0c\u4f60\u65b9\u624d\u4f24\u4e86\u4e00\u4eba\uff0c\u96be\u9053\u8fd8\u5acc\u4e0d \u591f\uff0c\u8fd9\u4e00\u638c\u82e5\u662f\u51fb\u5728\u522b\u4eba\u8eab\u4e0a\uff0c\u5c82\u975e\u53c8\u662f\u4eba\u547d\u4e00\u6761\u3002\u201d \u3000\u3000\u8fd9\u50e7\u4eba\u867d\u7136\u8eab\u7a7f\u8888\u88df\uff0c\u624b\u6301\u4f5b\u73e0\uff0c\u4f46\u9762\u4e0a\u6d53\u7709\u5927\u773c\uff0c\u76ee\u5149\u70af\u70af\uff0c\u53e3\u4e2d\u867d\u7136\u6717\u541f\u4f5b\u53f7\uff0c\u4f46 \u5410\u5c5e\u5374\u4e0d\u4f3c\u51fa\u5bb6\u4eba\uff0c\u53ea\u662f\u7709\u5b57\u95f4\u9690\u542b\u4e00\u7247\u6b63\u6c14\uff0c\u663e\u7136\u662f\u534a\u8def\u51fa\u5bb6\u4e3a\u50e7\uff0c\u5374\u53c8\u672a\u80fd\u56db\u5927\u7686\u7a7a\u3002 \u3000\u3000\u4e71\u53d1\u5934\u9640\u4e00\u8a00\u4e0d\u53d1\uff0c\u8fd0\u52b2\u4e8e\u81c2\uff0c\u6781\u529b\u540e\u593a\uff0c\u4f46\u624b\u638c\u7adf\u79bb\u4e0d\u5f00\u8fd9\u50e7\u4eba\u7684\u80f8\u53e3\uff0c\u4ed6\u5fc3\u5934\u751f \u5bd2\uff0c\u77e5\u9053\u81ea\u5df1\u4eca\u65e5\u9047\u7740\u4e86\u7edd\u9876\u5185\u5bb6\u9ad8\u624b\uff0c\u53e3\u4e2d\u7a81\u5730\u66b4\u559d\u4e00\u58f0\uff0c\u4e0b\u9762\u4e00\u817f\uff0c\u65e0\u5f71\u65e0\u8e2a\u5730\u8e22\u5c06\u51fa \u53bb\u3002 \u3000\u3000\u5435\u535c\u5bb6\u529f\u592b\u4e2d\uff0c\u817f\u6cd5\u4e3a\u5148\uff0c\u4ed6\u8fd9\u4e00\u811a\u8e22\u51fa\uff0c\u679c\u771f\u5feb\u5982\u96f7\u9706\u95ea\u7535\u3002 \u3000\u3000\u4e2d\u5e74\u50e7\u4eba\u5fae\u4e00\u76b1\u7709\uff0c\u80f8\u819b\u4e00\u633a\uff0c\u5355\u638c\u4e0b\u5207\u4e71\u53d1\u5934\u9640\u7684\u8db3\u8e1d\u3002 \u3000\u3000\u4e71\u53d1\u5934\u9640\u4f46\u89c9\u638c\u4e0a\u4e00\u80a1\u771f\u529b\u5f39\u6765\uff0c\u8db3\u8e1d\u53c8\u5c06\u88ab\u51fb\uff0c\u5239\u90a3\u95f4\u4ed6\u9ad8\u5927\u7684\u8eab\u8eaf\u7a81\u5730\u51cc\u7a7a\u4e00\u8f6c\uff0c \u4e71\u53d1\u7eb7\u98de\uff0c\u8863\u886b\u62c2\u8361\uff0c\u4ed6\u7adf\u6709\u5982\u98ce\u8f66\u822c\u5411\u540e\u76f4\u65cb\u4e86\u51fa\u53bb\uff0c\u5355\u638c\u4e00\u642d\u5c4b\u6a90\uff0c\u5530\u5730\u5012\u7ffb\u800c\u4e0a\u3002 \u3000\u3000\u53ea\u542c\u4ed6\u5389\u58f0\u5728\u559d\u9053\uff1a\u201c\u6211\u8ba4\u5f97\u4f60\uff0c\u6211\u8ba4\u5f97\u4f60\u2026\u2026\u201d \u3000\u3000\u5389\u559d\u4e4b\u58f0\uff0c\u968f\u7740\u4e00\u8fde\u4e32\u5c4b\u74e6\u788e\u88c2\u4e4b\u58f0\uff0c\u5239\u90a3\u95f4\u4fbf\u5df2\u8fdc\u53bb\u3002 \u3000\u3000\u4e2d\u5e74\u50e7\u4eba\u5fae\u559f\u4e00\u58f0\uff0c\u6447\u5934\u9053\uff1a\u201c\u5b7d\u969c\u5b7d\u969c\u2026\u2026\u201d \u7b2c\u4e8c\u5341\u56db\u7ae0>> \u53e4\u9f99\u300a\u6e58\u5983\u5251\u300b \u7b2c\u4e8c\u5341\u56db\u7ae0 \u3000\u3000\u53ea\u89c1\u8fd9\u50e7\u4eba\u76ee\u5149\u4e00\u62ac\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u5408\u5341\u9053\uff1a\u201c\u65bd\u4e3b\u53ef\u5c31\u662f\u6881\u4e0a\u4eba\u6881\u5927\u4fa0\u4e48\uff1f\u201d \u3000\u3000\u6881\u4e0a\u4eba\u89c1\u5230\u8fd9\u50e7\u4eba\u6b66\u529f\u5982\u6b64\u9ad8\u5f3a\uff0c\u9762\u76ee\u5374\u53c8\u5982\u6b64\u751f\u758f\uff0c\u5df2\u662f\u5341\u5206\u60ca\u5f02\uff0c\u6b64\u523b\u89c1\u4ed6\u4e00\u53e3\u4fbf \u8bf4\u51fa\u4e86\u81ea\u5df1\u7684\u540d\u5b57\uff0c\u66f4\u662f\u4e00\u6014\uff0c\u8981\u77e5\u4ed6\u751f\u5177\u5f02\u7980\uff0c\u4efb\u4f55\u4eba\u53ea\u8981\u5728\u4ed6\u773c\u524d\u8d70\u8fc7\u4e00\u904d\uff0c\u4ed6\u4fbf\u518d\u4e5f \u4e0d\u4f1a\u5fd8\u8bb0\u3002 \u3000\u3000\u4e2d\u5e74\u50e7\u4eba\u5408\u5341\u5fae\u7b11\u9053\uff1a\u201c\u8d2b\u9053\u7a7a\u5e7b\uff0c\u6765\u81ea\u6606\u4ed1\uff0c\u7279\u6765\u62dc\u8bbf\u65bd\u4e3b\uff0c\u5e76\u6709\u4e00\u4e8b\u8bf7\u6559\u3002\u201d \u3000\u3000\u6881\u4e0a\u4eba\u53c8\u662f\u4e00\u60ca\uff0c\u8fd1\u5e74\u6765\u6c5f\u6e56\u4e2d\u5df2\u4e0d\u89c1\u201c\u6606\u4ed1\u201d\u95e8\u4e0b\u9ad8\u624b\u4fa0\u8e2a\uff0c\u8fd9\u50e7\u4eba\u6b66\u529f\u5982\u6b64\u60ca\u4eba\uff0c \u4fbf\u662f\u5f53\u4eca\u6606\u4ed1\u638c\u6559\uff0c\u4e5f\u4e0d\u8fc7\u5982\u6b64\u800c\u5df2\u3002 \u3000\u3000\u4ed6\u6b64\u523b\u4e0d\u8fdc\u5343\u91cc\u800c\u6765\uff0c\u7adf\u662f\u4e3a\u4e86\u8981\u627e\u81ea\u5df1\uff0c\u8fd9\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f \u3000\u3000\u4ed6\u5fc3\u4e2d\u72b9\u7591\u4e0d\u5df2\uff0c\u4f46\u53e3\u4e2d\u5374\u7acb\u523b\u62b1\u62f3\u542b\u7b11\u9053\uff1a\u201c\u5927\u5e08\u8fdc\u6765\uff0c\u5728\u4e0b\u6709\u5931\u8fdc\u8fce\uff0c\u5148\u8bf7\u5165\u5ea7\u5f85 \u8336\u3002\u201d \u3000\u3000\u5927\u5385\u4e2d\u970e\u773c\u4fbf\u6536\u62fe\u5e72\u51c0\uff0c\u201c\u516b\u9762\u73b2\u73d1\u201d\u4e00\u751f\u884c\u4e8b\u5706\u6ed1\uff0c\u66fe\u81ea\u8be9\u4e00\u751f\u672a\u7ed3\u4ec7\u5bb6\uff0c\u5374\u4e0d\u60f3\u5230 \u5934\u6765\u8fd8\u662f\u6b7b\u5728\u522b\u4eba\u624b\u91cc\u3002 \u3000\u3000\u6881\u4e0a\u4eba\u63d6\u5ba2\u4eba\u5ea7\uff0c\u4e2d\u5e74\u50e7\u4eba\u201c\u7a7a\u5e7b\u5927\u5e08\u201d\u542b\u7b11\u8bf4\u9053\uff1a\u201c\u65bd\u4e3b\u5927\u540d\uff0c\u8d2b\u50e7\u65e9\u5df2\u4e45\u4ef0\uff0c\u4f46\u82e5 \u65e0\u4e00\u4eba\u7684\u4ecb\u7ecd\uff0c\u8d2b\u50e7\u8fd8\u662f\u4e0d\u6562\u5192\u6627\u62dc\u8bbf\u3002\u201d \u3000\u3000\u6881\u4e0a\u4eba\u5fcd\u4e0d\u4f4f\u622a\u53e3\u9053\uff1a\u201c\u5927\u5e08\u5149\u4e34\u6b64\u95f4\uff0c\u5b9e\u4ee4\u5728\u4e0b\u84ec\u7b5a\u751f\u8f89\uff0c\u4f46\u4e0d\u6562\u8bf7\u6559\u5927\u5e08\u4e00\u53e5\uff0c\u4e0d \u77e5\u5927\u5e08\u8d35\u53cb\u4e4b\u4e2d\uff0c\u6709\u54ea\u4e00\u4f4d\u4e0e\u6881\u67d0\u6709\u65e7\uff1f\u201d \u3000\u3000\u7a7a\u5e7b\u5927\u5e08\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u77e5\u65bd\u4e3b\u53ef\u8fd8\u8bb0\u5f97\uff0c\u5341\u5e74\u4e4b\u524d\uff0c\u5c60\u72d7\u8f88\u4e2d\uff0c\u6709\u4e00\u4e2a\u7f57\u4e00\u5200 \u4e48\uff1f\u201d \u3000\u3000\u6881\u3002\u4e0a\u4eba\u201c\u5440\u201d\u5730\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u7f57\u4e00\u5200\uff0c\u7f57\u4e00\u5200\uff0c\u4ed6\u6b64\u523b\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u7a7a\u5e7b\u5927\u5e08\u9053\uff1a\u201c\u6b64\u4eba\u81ea\u4ece\u7ecf\u8fc7\u4e86\u65bd\u4e3b\u90a3\u6b21\u6559\u8bad\uff0c\u4ea6\u5df2\u62dc\u5728\u6211\u6606\u4ed1\u95e8\u4e0b\uff0c\u6b64\u523b\u5df2\u662f\u655d\u6559\u638c \u6559\u5e08\u5144\u7684\u4e03\u5f1f\u5b50\u3002\u201d \u3000\u3000\u7cb1\u4e0a\u4eba\u957f\u53f9\u9053\uff1a\u201c\u653e\u4e0b\u5c60\u5200\uff0c\u7acb\u5730\u6210\u4f5b\uff0c\u7f57\u4e00\u5200\u679c\u7136\u662f\u82f1\u96c4\uff0c\u5728\u4e0b\u6bd4\u8d77\u4ed6\u6765\u3002\u5f53\u771f\u60ed\u6127 \u5f97\u5f88\u3001\u60ed\u6127\u5f97\u5f88\u3002\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u5374\u5728\u6697\u60ca\u5fd6\u9053\uff1a\u201c\u6b64\u4eba\u5e74\u7eaa\u4e0d\u8fc7\u4e2d\u5e74\uff0c\u5c45\u7136\u7adf\u662f\u5f53\u4eca\u6606\u4ed1\u638c\u6559\u7684\u5e08\u5f1f\u3002\u201d \u3000\u3000\u8981\u77e5\u5f53\u4eca\u6606\u4ed1\u638c\u6559\uff0c\u5e74\u5df2\u53e4\u7a00\u5f00\u5916\uff0c\u867d\u7136\u4ece\u672a\u5728\u6c5f\u6e56\u4e2d\u8d70\u52a8\uff0c\u4f46\u884c\u8f88\u5374\u6781\u9ad8\uff0c\u53ef\u7b97\u76ee\u524d \u6b66\u6797\u4e2d\u7855\u679c\u4ec5\u5b58\u7684\u51e0\u4f4d\u9ad8\u4eba\u4e4b\u4e00\uff0c\u90a3\u5728\u6c5f\u6e56\u4e2d\u53f7\u79f0\u201c\u6606\u4ed1\u4e94\u8001\u201d\u7684\u4e94\u4f4d\u4fa0\u58eb\uff0c\u4e5f\u4e0d\u8fc7\u53ea\u662f\u4ed6 \u7684\u4fd7\u5bb6\u5f1f\u5b50\u800c\u5df2\u3002 \u3000\u3000\u7a7a\u5e7b\u5927\u5e08\u542b\u7b11\u9053\uff1a\u201c\u4f5b\u95e8\u5e7f\u5927\uff0c\u666e\u6e21\u4f17\u751f\uff0c\u60f3\u8d2b\u50e7\u5f53\u5e74\u2026\u2026\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u957f\u53f9\uff0c\u6539\u53e3\u9053\uff1a\u201c\u8d2b\u50e7\u6b64\u6b21\u8fdc\u6765\u6c5f\u5357\uff0c\u5c31\u4e3a\u4e86\u8981\u6253\u542c\u4e00\u4eba\uff0c\u6212\u6740\u5e08\u4f84\u7f57\u4e00\u5200\u591a\u6b21 \u5411\u8d2b\u618e\u8a00\u53ca\u65bd\u4e3b\u5982\u4f55\u4f7f\u4e49\u3002\u5982\u4f55\u5bbe\u670b\u904d\u6ee1\u5929\u4e0b\u2026\u2026\u201d \u3000\u3000\u4ed6\u5c55\u989c\u4e00\u7b11\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u8d2b\u50e7\u8db3\u8ff9\u4e8c\u5341\u5e74\u672a\u81f3\u6c5f\u5357\uff0c\u6b64\u6b21\u5bfb\u4eba\u8bbf\u4e8b\uff0c\u53ea\u6709\u4ef0\u4ed7\u65bd\u4e3b\u7684\u5927 \u529b\u4e86\u3002\u201d \u3000\u3000\u6881\u4e0a\u4eba\u9053\uff1a\u201c\u5927\u5e08\u5982\u6b64\u8bf4\u8bdd\uff0c\u771f\u6559\u5728\u4e0b\u6127\u715e\u3002\u6881\u67d0\u4e00\u4ecb\u7c97\u4eba\uff0c\u600e\u5f53\u5f97\u5927\u5e08\u5982\u6b64\u79f0\u8d5e\uff0c\u4e0d \u77e5\u5927\u5e08\u6240\u8981\u5bfb\u8bbf\u4e4b\u4eba\u662f\u8c01\uff0c\u5728\u4e0b\u81ea\u5f53\u5c3d\u529b\u4e3a\u5927\u5e08\u6253\u63a2\u3002\u201d \u3000\u3000\u7a7a\u5e7b\u5927\u5e08\u53c8\u81ea\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8d2b\u50e7\u6b64\u6765\uff0c\u9664\u4e86\u6212\u6740\u5e08\u4f84\u7684\u63a8\u4ecb\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u4eba\uff0c\u4ea4\u7ed9\u4e86\u8d2b \u50e7\u4e00\u4ef6\u4fe1\u7269\uff0c\u6b64\u4eba\u4e0d\u77e5\u65bd\u4e3b\u53ef\u8fd8\u8bb0\u5f97\uff1f\u201d \u3000\u3000\u4ed6\u4e00\u9762\u8bf4\u8bdd\uff0c\u4e00\u9762\u5df2\u81ea\u4ed6\u90a3\u5bbd\u5927\u7684\u888d\u8896\u4e4b\u4e2d\uff0c\u53d6\u51fa\u4e86\u4e00\u53ea\u94f6\u4e1d\u7f16\u6210\u7684\u5c0f\u5c0f\u8292\u978b\uff0c\u867d\u662f\u5177 \u4f53\u800c\u5fae\uff0c\u5236\u4f5c\u5374\u6781\u7cbe\u81f4\u3002 \u3000\u3000\u6881\u4e0a\u4eba\u7a81\u5730\u5168\u8eab\u4e00\u9707\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u4e07\u2026\u2026\u8001\u524d\u8f88\u2026\u2026\u201d \u3000\u3000\u7f13\u7f13\u4f38\u51fa\u624b\u638c\uff0c\u7f13\u7f13\u63a5\u8fc7\u4e86\u8fd9\u53ea\u8292\u978b\u3002 \u3000\u3000\u7a7a\u5e7b\u5927\u5e08\u9053\uff1a\u201c\u5982\u6b64\u770b\u6765\uff0c\u4f60\u8fd8\u8bb0\u5f97\u4ed6\u8001\u4eba\u5bb6\u4e86\u3002\u201d \u3000\u3000\u6881\u4e0a\u4eba\u6ee1\u9762\u4ff1\u662f\u6fc0\u52a8\u4e4b\u8272\uff0c\u53cc\u624b\u6367\u7740\u8292\u978b\uff0c\u606d\u606d\u656c\u656c\u5730\u8f7b\u653e\u5728\u684c\u4e0a\uff0c\u7136\u540e\u201c\u5657\u201d\u5730\u4e00 \u58f0\uff0c\u8dea\u4e86\u4e0b\u53bb\uff0c\u606d\u606d\u656c\u656c\u5730\u53e9\u4e86\u4e09\u4e2a\u5934\u3002 \u3000\u3000\u7a7a\u5e7b\u5927\u5e08\u4ea6\u81ea\u79bb\u5ea7\u800c\u8d77\uff0c\u53ea\u89c1\u6881\u4e0a\u4eba\u8dea\u5728\u5730\u4e0a\uff0c\u60b2\u58f0\u9053\uff1a\u201c\u5f1f\u5b50\u600e\u4f1a\u4e0d\u8bb0\u5f97\u4f60\u8001\u4eba\u5bb6\uff0c \u5f1f\u5b50\u867d\u611a\u6627\uff0c\u5374\u975e\u5fd8\u6069\u8d1f\u4e49\u4e4b\u8f88\uff0c\u6ca1\u6709\u4f60\u8001\u4eba\u5bb6\uff0c\u5f1f\u5b50\u65e9\u5df2\u788e\u5c38\u4e07\u6bb5\uff0c\u54ea\u91cc\u8fd8\u6709\u4eca\u65e5\u3002\u201d \u3000\u3000\u7a7a\u5e7b\u5927\u5e08\u9894\u9996\u5fd6\u9053\uff1a\u201c\u6b64\u4eba\u5012\u662f\u6761\u4e49\u70c8\u6c49\u5b50\uff0c\u4e5f\u4e0d\u5728\u6211\u6765\u6b64\u4e00\u906d\u3002\u201d \u3000\u3000\u6881\u4e0a\u4eba\u5782\u9996\u9ed8\u7136\u534a\u664c\uff0c\u65b9\u81ea\u957f\u8eab\u800c\u8d77\uff0c\u53f9\u9053\uff1a\u201c\u5927\u5e08\u6709\u6b64\u4fe1\u7269\uff0c\u600e\u4e0d\u65e9\u8bf4\uff0c\u4e07\u8001\u524d\u8f88\u4e8e \u5728\u4e0b\u6709\u5929\u9ad8\u5730\u539a\u4e4b\u6069\uff0c\u53ea\u8981\u4e07\u8001\u524d\u8f88\u7684\u7247\u8a00\u53ea\u5b57\uff0c\u4fbf\u662f\u6559\u5728\u4e0b\u8d74\u6c64\u8e48\u706b\uff0c\u4ea6\u4e0d\u6562\u8f9e\uff0c\u4f55\u51b5\u662f \u8fd9\u533a\u533a\u5c0f\u4e8b\u3002\u201d \u3000\u3000\u7a7a\u5e7b\u5927\u5e08\u9053\uff1a\u201c\u6b64\u4e8b\u8bf4\u6765\u867d\u8f7b\u6613\uff0c\u4f46\u505a\u6765\u5374\u975e\u6613\u4e8b\u2026\u2026\u201d \u3000\u3000\u6881\u4e0a\u4eba\u622a\u53e3\u9053\uff1a\u201c\u65e0\u8bba\u4e8b\u60c5\u591a\u96be\uff0c\u5728\u4e0b\u90fd\u6709\u628a\u63e1\u5c06\u4e4b\u5b8c\u6210\u3002\u53ea\u8981\u4e16\u4e0a\u771f\u6709\u90a3\u4eba\uff0c\u65e0\u8bba\u662f \u6b7b\u662f\u6d3b\uff0c\u5728\u4e0b\u90fd\u53ef\u5c06\u5176\u8e2a\u8ff9\u5bfb\u627e\u3002\u201d \u3000\u3000\u7a7a\u5e7b\u5927\u5e08\u9053\uff1a\u201c\u771f\u7684\uff1f\u201d \u3000\u3000\u6881\u4e0a\u4eba\u53f9\u9053\uff1a\u201c\u5927\u5e08\u5982\u4e0d\u4fe1\uff0c\u5728\u4e0b\u53ef\u5f53\u4e07\u8001\u524d\u8f88\u8fd9\u4ef6\u4fe1\u7269\uff0c\u53d1\u4e0b\u91cd\u8a93\uff0c\u5728\u4e0b\u82e5\u4e0d\u5c06\u6b64\u4eba \u8e2a\u8ff9\u5bfb\u51fa\uff0c\u4fbf\u662f\u2026\u2026\u201d \u3000\u3000\u7a7a\u5e7b\u5927\u5e08\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u5c06\u6b64\u4eba\u8e2a\u8ff9\u5bfb\u51fa\uff0c\u4fbf\u662f\u6b7b\u4e5f\u4e0d\u80fd\u6b7b\u7684\u3002\u201d \u3000\u3000\u6881\u4e0a\u4eba\u7acb\u523b\u63a5\u53e3\u9053\uff1a\u201c\u4fbf\u662f\u5982\u6b64\uff01\u201d \u3000\u3000\u7a7a\u5e7b\u5927\u5e08\u5c55\u989c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8d2b\u50e7\u6240\u8981\u627e\u4e4b\u4eba\uff0c\u5728\u6c5f\u6e56\u4e2d\u867d\u65e0\u540d\u6c14\uff0c\u4f46\u8bf4\u6765\u4f60\u60f3\u5fc5\u4e5f\u4f1a\u77e5 \u9053\u3002\u201d \u3000\u3000\u6881\u4e0a\u4eba\u9053\uff1a\u201c\u8c01\uff1f\u7a7a\u5e7b\u5927\u5e08\u7709\u5b57\u95ee\u7a81\u73b0\u4e00\u7247\u6028\u6bd2\u4e4b\u610f\uff0c\u76ee\u5149\u4e2d\u4e5f\u7acb\u523b\u6ee1\u542b\u6740\u673a\uff0c\u6c89\u58f0 \u9053\uff1a\u201c\u6b64\u4eba\u4fbf\u662f\u6614\u5e74\u90a3\u65e0\u6076\u4e0d\u4f5c\u7684\u9b54\u5934\u4ec7\u72ec\u4e4b\u5b50\uff0c\u8d2b\u50e7\u4e5f\u4e0d\u77e5\u4ed6\u53eb\u505a\u4ec0\u4e48\uff0c\u4f46\u7b97\u6765\u4eca\u65e5\u5df2\u6709 \u5341\u516b\u3001\u4e5d\u5c81\u4e86\u3002\u201d \u3000\u3000\u4ed6\u540e\u672a\u8bf4\u5b8c\uff0c\u6881\u4e0a\u4eba\u5df2\u662f\u5fc3\u5934\u4e00\u9707\uff0c\u8131\u53e3\u9053\uff1a\u201c\u5927\u5e08\u4e3a\u4f55\u8981\u5bfb\u6b64\u4eba\uff1f\u201d \u3000\u3000\u7a7a\u5e7b\u5927\u5e08\u4ef0\u9762\u671b\u5929\uff0c\u5207\u9f7f\u9053\uff1a\u201c\u90a3\u4ec7\u72ec\u4e0e\u6211\u4ec7\u5982\u5c71\u9ad8\uff0c\u6068\u6bd4\u6d77\u6df1\uff0c\u6211\u6068\u4e0d\u80fd\u98df\u5176\u8089\uff0c\u5bdd \u5176\u76ae\uff0c\u53ea\u53ef\u60dc\u4ed6\u4e0d\u80fd\u7b49\u6211\uff0c\u7236\u503a\u5b50\u8fd8\uff0c\u6211\u53ea\u6709\u6765\u5bfb\u4ed6\u7684\u513f\u5b50\u3002\u201d \u3000\u3000\u4ed6\u8bdd\u4e2d\u7684\u6028\u6bd2\uff0c\u4f7f\u5f97\u6881\u4e0a\u4eba\u4e0d\u7981\u81ea\u5fc3\u5e95\u5347\u51fa\u4e00\u9635\u98a4\u6296\uff0c\u5446\u5446\u5730\u6115\u4e86\u534a\u664c\uff0c\u6697\u4e2d\u81ea\u8bed\u7740 \u9053\uff1a\u201c\u4ec7\u6055\u5440\u4ec7\u6055\uff0c\u4f60\u53ea\u77e5\u5411\u4eba\u5bfb\u4ec7\uff0c\u5374\u4e0d\u77e5\u6709\u4eba\u5411\u4f60\u5bfb\u4ec7\uff0c\u4f60\u4eec\u6069\u4ec7\u7ea0\u7f20\uff0c\u5374\u53eb\u6211\u6881\u4e0a\u4eba \u5982\u4f55\u662f\u597d\u3002\u201d \u3000\u3000\u201c\u5723\u624b\u4e66\u751f\u201d\u4e0e\u4ed6\u6709\u5e08\u5f92\u4e4b\u4e49\uff0c\u201c\u5723\u624b\u4e66\u751f\u201d\u4e4b\u4ee4\uff0c\u4ed6\u81ea\u5f53\u8d74\u6c64\u8e48\u706b\uff0c\u4f46\u8fd9\u53ea\u94f6\u4e1d\u8292\u978b \u7684\u4e3b\u4eba\uff0c\u5374\u66f4\u5bf9\u4ed6\u6709\u5929\u9ad8\u5730\u539a\u4e4b\u6069\uff0c\u4ed6\u65b9\u624d\u5df2\u7acb\u4e0b\u91cd\u8a93\uff0c\u6b64\u523b\u4fbf\u6559\u8fd9\u4ee5\u4e49\u4e3a\u5148\uff0c\u4ee5\u4fe1\u4e3a\u91cd\u7684 \u6c5f\u6e56\u597d\u6c49\u5982\u4f55\u662f\u597d\uff1f \u3000\u3000\u4e00\u65f6\u95f4\u4ed6\u5df2\u89c9\u5fc3\u5934\u4e07\u5ff5\u6e43\u817e\uff0c\u65e0\u6cd5\u8a00\u8bed\u3002 \u3000\u3000\u7a7a\u5e7b\u5927\u5e08\u970d\u7136\u5782\u4e0b\u5934\u6765\uff0c\u76ee\u5149\u7b14\u76f4\u5730\u671b\u5728\u4ed6\u8138\u4e0a\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4f60\u53ef\u542c\u8fc7\u6b64\u4eba\uff1f\u4f60\u53ef\u77e5\u9053 \u6b64\u4eba\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u6881\u4e0a\u4eba\u6014\u4e86\u534a\u664c\uff0c\u9762\u4e0a\u88c2\u51fa\u4e00\u4e1d\u5e72\u7b11\uff0c\u5450\u5450\u9053\uff1a\u201c\u5927\u5e08\u8fdc\u5c45\u6606\u4ed1\uff0c\u5374\u4e0d\u77e5\u4e0e\u90a3\u4ec7\u5148\u751f\u6709 \u4f55\u4ec7\u6068\uff1f\u201d \u3000\u3000\u7a7a\u5e7b\u5927\u5e08\u6728\u7acb\u534a\u664c\uff0c\u601d\u6f6e\u4f3c\u53c8\u56de\u5230\uff1b\u65e5\u65e5\u7684\u9690\u6068\u4e2d\u3002 \u3000\u3000\u4ed6\u53e3\u4e2d\u4e0d\u4f4f\u5583\u5583\u81ea\u8bed\uff0c\u826f\u4e45\u826f\u4e45\uff0c\u65b9\u81ea\u6c89\u58f0\u9053\u3002 \u3000\u3000\u201c\u6211\u4e14\u95ee\u4f60\uff0c\u662f\u6740\u7236\u4e4b\u4ec7\u91cd\uff0c\u6291\u6216\u662f\u593a\u59bb\u4e4b\u6068\u6df1\uff1f\u201d \u3000\u3000\u6881\u4e0a\u4eba\u5450\u5450\u9053\uff1a\u201c\u4ec7\u4e0e\u6068\u4e24\u5b57\uff0c\u610f\u4e49\u672c\u5c31\u5e76\u4e0d\u5341\u5206\u76f8\u540c\uff0c\u7236\u4ec7\u4e0d\u5171\u6234\u5929\uff0c\u4f46\u593a\u59bb\u4e4b \u6068\u2026\u2026\u5509\uff0c\u786e\u4e5f\u6068\u5f97\u6781\u6df1\u3002\u201d \u3000\u3000\u7a7a\u5e7b\u5927\u5e08\u5634\u89d2\u7f13\u7f13\u5347\u8d77\u4e00\u9635\u51c4\u51c9\u800c\u6028\u6bd2\u7684\u5fae\u7b11\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u6211\u4e3a\u4f55\u51fa\u5bb6\uff1f\u4f60\u53ef \u77e5\u9053\u6211\u672a\u66fe\u51fa\u5bb6\u4e4b\u524d\u662f\u8c01\u4e48\uff1f\u201d \u3000\u3000\u6881\u4e0a\u4eba\u7a81\u5730\u5fc3\u5934\u4e00\u52a8\uff0c\u60f3\u8d77\u4e00\u4e2a\u4eba\u6765\u3002 \u3000\u3000\u8584\u66ae\u9ec4\u660f\u3002 \u3000\u3000\u897f\u5b50\u6e56\u7554\u7684\u7075\u9690\uff0c\u6b63\u5728\u7a7a\u7075\u9690\u5e7b\u4e4b\u95f4\u3002 \u3000\u3000\u4ece\u5c71\u95e8\u8fdb\u53bb\uff0c\u4e00\u9762\u9ad8\u5ca9\uff0c\u4e00\u9762\u5927\u6bbf\uff0c\u5149\u7ebf\u6c89\u6c89\uff0c\u5374\u5728\u6700\u8fdc\u7684\u665a\u7a7a\u4e2d\u6de1\u6de1\u5730\u7559\u7740\u4f59\u971e\u4e00 \u62b9\uff0c\u7ea2\u5982\u73ca\u745a\u3002 \u3000\u3000\u66ae\u4e91\u4f4e\u5782\uff0c\u6e10\u5f25\u5c71\u8c37\u3002 \u3000\u3000\u4e00\u4e2a\u5f31\u51a0\u5c11\u5e74\u3002\u51cc\u98ce\u8d1f\u624b\u4ec1\u7acb\u5728\u73ca\u745a\u822c\u7684\u4f59\u971e\u4e2d\u3002 \u3000\u3000\u4ed6\u6781\u76ee\u773a\u671b\u7740\u5929\u7554\u7684\u4f59\u971e\uff0c\u795e\u60c5\u867d\u4f3c\u6781\u4e3a\u5b89\u6837\uff0c\u4f46\u7709\u5b57\u95f4\u5374\u53c8\u9690\u542b\u7126\u6025\uff0c\u4f3c\u4e4e\u5728\u7b49\u5f85\u7740 \u4ec0\u4e48\u3002 \u3000\u3000\u5c71\u95e8\u5916\uff0c\u6563\u6f2b\u5730\u8dcc\u5750\u7740\u6570\u5341\u4e2a\u9e51\u8863\u84ec\u9762\u7684\u4e5e\u4e10\u3002\u7075\u9690\u5bfa\u4e10\uff0c\u672c\u662f\u897f\u6e56\u4e00\u666f\uff0c\u4f46\u8fd9\u4e9b\u4e5e \u4e10\uff0c\u795e\u8272\u95f4\u5374\u662f\u51fa\u5947\u5730\u5b89\u7965\uff0c\u4e00\u4e2a\u4e2a\u4f4e\u7709\u655b\u76ee\uff0c\u9ed8\u7136\u7aef\u5750\u5728\u4e00\u6392\u6392\u9ebb\u888b\u4e0a\u3002 \u3000\u3000\u826f\u4e45\uff0c\u5f31\u51a0\u5c11\u5e74\u56de\u8f6c\u5934\u6765\uff0c\u4f59\u971e\u6620\u5f97\u4ed6\u9762\u8272\u6709\u5982\u6843\u82b1\u822c\u5ae3\u7ea2\uff0c\u4ed6\u76ee\u5149\u56db\u4e0b\u4e00\u8f6c\uff0c\u7f13\u7f13\u8e31 \u51fa\u5c71\u95e8\uff0c\u8f7b\u8f7b\u95ee\u9053\uff1a\u201c\u51cc\u8001\u524d\u8f88\u771f\u7684\u8981\u6765\u4e48\uff1f\u201d \u3000\u3000\u5750\u5728\u5c71\u95e8\u5de6\u4fa7\u7684\uff0c\u662f\u4e00\u4e2a\u7626\u9aa8\u9cde\u5ccb\u7684\u5c11\u5e74\u4e10\u8005\uff0c\u4ed6\u5e74\u7eaa\u867d\u8f7b\u3002 \u3000\u3000\u4f46\u5750\u4e0b\u7684\u9ebb\u888b\u5374\u751a\u539a\uff0c\u6b64\u523b\u53cc\u76ee\u4e00\u5f20\uff0c\u795e\u5149\u9690\u73b0\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4e0d\u89c1\u5f97\u3002\u201d \u3000\u3000\u5f31\u51a0\u5c11\u5e74\u9762\u8272\u5fae\u53d8\uff0c\u9053\uff1a\u201c\u4f60\u65b9\u624d\u8bf4\u4ed6\u8981\u6765\u7684\uff1f\u201d \u3000\u3000\u5c11\u5e74\u4e10\u8005\u5782\u4e0b\u773c\u5e18\uff0c\u9053\uff1a\u201c\u53ef\u80fd\u6765\uff0c\u4e5f\u53ef\u80fd\u4e0d\u6765\uff0c\u6709\u8c01\u786e\u5b9a\u8fc7\u3002\u201d \u3000\u3000\u5f31\u51a0\u5c11\u5e74\u53cc\u7709\u4e00\u4e00\u626c\uff0c\u5927\u58f0\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u6211\u7b49\u8fd9\u4e48\u4e45\uff1f\u201d \u3000\u3000\u4ed6\u4e00\u4e00\u6025\u4e4b\u4e0b\uff0c\u58f0\u97f3\u653e\u9ad8\uff0c\u8bed\u58f0\u7a81\u5730\u53d8\u5f97\u5341\u5206\u5c16\u9510\u3002 \u3000\u3000\u5c11\u5e74\u4e10\u8005\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8c01\u6559\u4f60\u7b49\u7684\uff1f\u201d \u3000\u3000\u5f31\u51a0\u5c11\u5e74\u76ee\u5149\u4e00\u51db\uff0c\u9762\u8272\u66f4\u662f\u901a\u7ea2\uff0c\u5927\u58f0\u9053\u3002 \u3000\u3000\u201c\u597d\u4e2a\u65e0\u793c\u7684\u5974\u624d\u3002\u4fbf\u662f\u4f60\u4eec\u5e2e\u4e3b\u89c1\u4e86\u6211\uff0c\u53ea\u6015\u4e5f\u4e0d\u6562\u5982\u6b64\u3002\u201d \u3000\u3000\u5c11\u5e74\u4e10\u8005\u51b7\u201c\u54fc\u201d\u4e00\u58f0\uff0c\u4e0d\u8a00\u4e0d\u8bed\uff1a\u5f31\u51a0\u5c11\u5e74\u5927\u559d\u9053\uff1a\u201c\u770b\u4f60\u4e5f\u662f\u4e2a\u7ec3\u5bb6\u5b50\uff0c\u7ad9\u8d77\u6765\uff0c \u5c11\u7237\u6559\u8bad\u6559\u8bad\u4f60\u3002\u201d \u3000\u3000\u5c11\u5e74\u4e10\u8005\u7f13\u7f13\u5f20\u5f00\u773c\u6765\uff0c\u8f7b\u8511\u5730\u4e0a\u4e0b\u77a7\u4e86\u4ed6\u4e00\u773c\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u672c\u4eba\u4ece\u6765\u4e0d\u4e0e\u5973\u5b50\u52a8 \u624b\u3002\u201d \u3000\u3000\u5f31\u51a0\u5c11\u5e74\u4e0d\u7981\u4e00\u6014\uff0c\u9762\u4e0a\u7684\u7ea2\u971e\uff0c\u4e00\u76f4\u7ea2\u5230\u8033\u6839\uff0c\u7ad9\u5728\u5730\u4e0a\u5446\u5446\u5730\u6014\u4e86\u534a\u664c\uff0c\u72e0\u72e0\u4e00\u8dfa \u811a\uff0c\u9053\uff1a\u201c\u89c1\u7740\u4e86\u4f60\u4eec\u5e2e\u4e3b\u518d\u6765\u6559\u8bad\u4f60\u3002\u201d \u3000\u3000\u6570\u5341\u4e2a\u4e5e\u4e10\u4e00\u9f50\u8f7b\u8f7b\u4e00\u7b11\uff0c\u5f31\u51a0\u5c11\u5e74\u5df2\u5927\u6b65\u8d70\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u201c\u4ed6\u201d\u80f8\u819b\u4e0d\u4f4f\u8d77\u4f0f\uff0c\u663e\u89c1\u5f97\u80f8\u4e2d\u6ee1\u542b\u6012\u6c14\uff0c\u4f46\u5374\u53c8\u4e0d\u80fd\u4e0e\u8fd9\u4e9b\u4e5e\u4e10\u52a8\u624b\uff0c\u53ea\u56e0\u4ed6\u8fd8\u8981 \u5bfb\u627e\u90a3\u7a77\u5bb6\u5e2e\u4e3b\uff0c\u4e3a\u4ed6\u6253\u542c\u4e00\u4e2a\u4eba\u7684\u6d88\u606f\u3002 \u3000\u3000\u6f2b\u5929\u6b8b\u971e\u4e0b\uff0c\u4ed6\u811a\u6b65\u8d8a\u6765\u8d8a\u7f13\uff0c\u53e3\u4e2d\u4e5f\u4e0d\u7981\u53d1\u51fa\u4e86\u4e00\u58f0\u58f0\u8f7b\u8f7b\u7684\u53f9\u606f\uff0c\u4ed6\u5fc3\u4e2d\u6709\u8bb8\u591a \u4e8b\uff0c\u5c31\u8fde\u4ed6\u6700\u4eb2\u8fd1\u7684\u4eba\u4e5f\u65e0\u6cd5\u8bc9\u8bf4\uff0c\u662f\u4ee5\u4ed6\u53ea\u6709\u6c42\u52a9\u795e\u901a\u5e7f\u5927\u7684\u7a77\u5bb6\u5e2e\u4e3b\uff0c\u4f46\u51cc\u5e2e\u4e3b\u5374\u53c8\u5982 \u5929\u9645\u795e\u9f99\uff0c\u6ca1\u6709\u5bfb\u5904\u3002 \u3000\u3000\u4ed6\u4f38\u51fa\u83b9\u767d\u5982\u7389\u7684\u624b\u638c\uff0c\u4e0b\u610f\u8bc6\u5730\u4e00\u629a\u9b13\u811a\uff0c\u4ed6\u867d\u662f\u4e00\u8eab\u7537\u5b50\u670d\u88c5\uff0c\u4f46\u4e00\u79cd\u5973\u6027\u7684\u59a9\u5a9a \u4e4b\u6001\u5374\u5e38\u5728\u4e0d\u77e5\u4e0d\u89c9\u95f4\u6d41\u9732\u3002 \u3000\u3000\u5782\u9996\u800c\u884c\uff0c\u811a\u6b65\u7ec6\u788e\uff0c\u76ee\u5149\u62ac\u5904\uff0c\u53ea\u89c1\u4e24\u4e2a\u767d\u53d1\u8001\u4eba\uff0c\u5e76\u80a9\u9036\u8fe4\uff0c\u8fce\u9762\u800c\u6765\u3002\u8fd9\u4e24\u4eba\u8eab \u4e0a\u7a7f\u7684\u4ff1\u662f\u4e00\u8eab\u534e\u670d\uff0c\u957f\u957f\u7684\u767d\u987b\uff0c\u5728\u665a\u98ce\u4e2d\u4e0d\u4f4f\u62c2\u52a8\u7740\u3002\u4e00\u4e00\u4eba\u6781\u80d6\uff0c\u4e00\u4eba\u5374\u6781\u7626\uff0c\u4e00\u80d6 \u4e00\u7626\uff0c\u6781\u4e3a\u60ac\u6b8a\u3002 \u3000\u3000\u4ed6\u4e24\u4eba\u8d70\u5230\u8fd9\u5c11\u5e74\u8eab\u524d\u6570\u5c3a\u4e4b\u5904\uff0c\u7adf\u7a81\u5730\u4e00\u9f50\u505c\u4e0b\u4e86\u811a\u6b65\uff0c\u76ee\u5149\u6014\u6014\u5730\u671b\u5411\u8fd9\u5f31\u51a0\u5c11\u5e74 \u8eab\u4e0a\u3002 \u3000\u3000\u7136\u540e\u4e24\u4eba\u5bf9\u671b\u4e00\u773c\uff0c\u5de6\u9762\u4e00\u4eba\u8f7b\u8f7b\u9053\uff1a\u201c\u50cf\u4e48\uff1f\u201d\u8bed\u58f0\u4e4b\u4e2d\uff0c\u4f3c\u4e4e\u5e26\u7740\u4e9b\u5947\u5f02\u7684\u53e3\u97f3\u3002 \u3000\u3000\u53f3\u9762\u4e00\u4eba\u70b9\u4e86\u70b9\u5934\uff0c\u8bdd\u58f0\u66f4\u8f7b\uff0c\u9053\uff1a\u201c\u4ed6\u82e5\u662f\u5973\u5b50\u2026\u2026\u201d \u3000\u3000\u5de6\u9762\u4e00\u4eba\u622a\u53e3\u9053\uff1a\u201c\u4ed6\u672c\u5c31\u662f\u5973\u5b50\uff0c\u5509\uff01\u82e5\u6362\u5728\u4e8c\u5341\u5e74\u524d\u201d\u63d0\u5230\u4e8c\u5341\u5e74\u524d\uff0c\u4e24\u4eba\u4e00\u9f50\u4f4f \u53e3\uff0c\u76ee\u5149\u4e5f\u4e00\u9f50\u5782\u843d\u3002 \u3000\u3000\u5f31\u51a0\u5c11\u5e74\u67f3\u7709\u4e00\u626c\uff0c\u6012\u9053\uff1a\u201c\u4f60\u4eec\u5728\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u8033\u76ee\u6781\u7075\uff0c\u8fd9\u4e24\u4e2a\u8001\u4eba\u8bed\u58f0\u867d\u8f7b\uff0c\u4ed6\u5374\u5df2\u542c\u5f97\u6e05\u6e05\u695a\u695a\u3002 \u3000\u3000\u767d\u53d1\u8001\u4eba\u53c8\u81ea\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u8c01\u4e5f\u6ca1\u6709\u56de\u7b54\u4ed6\u7684\u8bdd\uff0c\u4e00\u9f50\u81ea\u4ed6\u8eab\u4fa7\u8d70\u8fc7\u3002 \u3000\u3000\u5f31\u51a0\u5c11\u5e74\u811a\u6b65\u5fae\u5fae\u4e00\u987f\uff0c\u5374\u7ec8\u4e8e\u53c8\u5fcd\u4e0b\u4e86\u8fd9\u53e3\u6c14\uff0c\u4ed6\u672c\u662f\u813e\u6c14\u6700\u8e81\u7684\u4eba\uff0c\u8fd1\u6765\u4e0d\u77e5\u4e3a\u4e86 \u4ec0\u4e48\uff0c\u7adf\u6539\u53d8\u4e86\u8bb8\u591a\u3002 \u3000\u3000\u4e00\u8f86\u9a6c\u8f66\u7b49\u5728\u8fdc\u5904\uff0c\u7b49\u5728\u8fdc\u5904\u7684\u4e00\u884c\u5782\u67f3\u4e0b\u3002\u4ed6\u7f13\u6b65\u8d70\u5411\u9a6c\u8f66\uff0c\u5782\u67f3\u540e\u4eba\u5f71\u4e00\u95ea\uff0c\u7a81\u7136 \u73b0\u4e86\u4e00\u4e2a\u957f\u8eab\u7389\u7acb\u7684\u91d1\u886b\u5c11\u5e74\uff0c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u600e\u5730\u5230\u8fd9\u91cc\u6765\u4e86\uff0c\u662f\u4e3a\u4e86\u89c2\u8d4f\u98ce\u666f\uff0c \u8fd8\u662f\u4e3a\u4e86-\u201d\u5f31\u51a0\u5c11\u5e74\u79c0\u76ee\u4e00\u5f20\uff0c\u67f3\u7709\u7acb\u76b1\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u7ba1\u4e0d\u7740\u3002\u201d \u3000\u3000\u4ed6\u7b14\u76f4\u8d70\u5411\u9a6c\u8f66\uff0c\u54ea\u77e5\u8fd9\u91d1\u886b\u5c11\u5e74\u8eab\u5f62\u4e00\u95ea\uff0c\u7adf\u6321\u5728\u4ed6\u9762\u524d\uff0c\u7b11\u9053\uff1a\u201c\u6211\u600e\u5730\u7ba1\u4e0d\u7740\uff0c \u5e08\u5085\u53eb\u6211\u2026\u2026\u201d \u3000\u3000\u5f31\u51a0\u5c11\u5e74\u559d\u9053\uff1a\u201c\u94c1\u5e73\uff0c\u4f60\u4e0d\u8981\u4ee5\u4e3a\u5728\u7239\u7239\u9762\u524d\u5f97\u5ba0\uff0c\u5c31\u5fd8\u4e86\u81ea\u5df1\u662f\u4ec0\u4e48\u8eab\u4efd\uff0c\u59d1\u5a18\u6211 \u8fd8\u662f\u7167\u6837\u6709\u529e\u6cd5\u5236\u4f60\u3002\u201d \u3000\u3000\u5979\u4e0d\u4f46\u5df2\u81ea\u79f0\u59d1\u5a18\uff0c\u8a00\u8bed\u95f4\u66f4\u6ee1\u5145\u5bcc\u5bb6\u5343\u91d1\u7684\u5a07\u55d4\u4e4b\u6c14\uff0c\u6b64\u523b\u6839\u672c\u4e0d\u7528\u591a\u8bf4\uff0c\u8c01\u90fd\u5df2\u77e5 \u9053\u201c\u4ed6\u201d\u5c31\u662f\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u7684\u72ec\u751f\u7231\u5973\u6bdb\u6587\u742a\uff0c\u4f46\u662f\u2014\u2014\u5979\u4e0d\u662f\u5df2\u56de\u5230\u5979\u5e08\u5085\u90a3\u91cc\u53bb\u4e86\u4e48\uff1f \u600e\u5730\u5374\u53c8\u56de\u5230\u6c5f\u5357\uff1f \u3000\u3000\u91d1\u886b\u5c11\u5e74\u6545\u610f\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u8981\u8fd9\u6837\u8bf4\uff0c\u6211\u5c31\u65e0\u8bdd\u53ef\u8bb2\u4e86\uff01\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u5fae\u5fae\u4e00\u987f\uff0c\u76ee\u5149\u659c\u659c\u671b\u7740\u6bdb\u6587\u742a\uff0c\u7f13\u7f13\u63a5\u53e3\u9053\uff1a\u201c\u5176\u5b9e\u6211\u4e5f\u662f\u4e3a\u4e86\u4e00\u4ef6\u6d88\u606f\uff0c\u597d \u5fc3\u597d\u610f\u5730\u6765\u544a\u8bc9\u59d1\u5a18\u7684\u3002\u201d \u3000\u3000\u8fd9\u91d1\u886b\u5c11\u5e74\uff0c\u6b63\u662f\u201c\u7075\u86c7\u201d\u95e8\u4e0b\u201c\u7389\u9aa8\u4f7f\u8005\u201d\u4e2d\u7684\u201c\u593a\u547d\u4f7f\u8005\u201d\u94c1\u5e73\uff0c\u8fd1\u65e5\u6765\u201c\u7389\u9aa8\u4f7f \u8005\u201d\u4f24\u6b8b\u9887\u91cd\uff0c\u6bdb\u81ec\u81ea\u7136\u5c31\u5bf9\u5269\u4e0b\u7684\u8fd9\u51e0\u4e2a\u5f1f\u5b50\u7279\u522b\u7231\u60dc\uff0c\u662f\u4ee5\u94c1\u5e73\u6b64\u523b\u4ecd\u65e0\u4e1d\u6beb\u754f\u60e7\u4e4b \u610f\u3002 \u3000\u3000\u6bdb\u6587\u742a\u8d70\u4e86\u4e24\u6b65\uff0c\u5fcd\u4e0d\u4f4f\u53c8\u505c\u4e86\u4e0b\u6765\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4ec0\u4e48\u6d88\u606f\uff1f\u201d \u3000\u3000\u94c1\u5e73\u55e4\u5730\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u82e5\u4e0d\u613f\u542c\uff0c\u4e5f\u5c31\u7f62\u4e86\u3002\u201d \u3000\u3000\u6bdb\u6587\u742a\u67f3\u7709\u4e00\u626c\uff0c\u7b14\u76f4\u51b2\u4e0a\u9a6c\u8f66\uff0c\u5411\u5446\u5750\u5728\u8f66\u5ea7\u524d\u7684\u8f66\u592b\u5927\u58f0\u9053\uff1a\u201c\u8d70\uff01\u201d \u3000\u3000\u8d76\u8f66\u7684\u4e1d\u97ad\u4e00\u626c\uff0c\u201c\u593a\u547d\u4f7f\u8005\u201d\u94c1\u5e73\u9762\u5e26\u5fae\u7b11\uff0c\u8d1f\u624b\u7acb\u5728\u67f3\u6811\u4e0b\uff0c\u4ed6\u9762\u4e0a\u7684\u7b11\u5bb9\u5374\u662f\u90a3 \u4e48\u5947\u5f02\u3002 \u3000\u3000\u4e1d\u97ad\u5530\u5730\u4e00\u58f0\uff0c\u5e26\u7740\u4e00\u7f15\u9510\u98ce\u843d\u4e0b\u3002 \u3000\u3000\u5065\u9a6c\u65b9\u81ea\u626c\u8e44\u3002 \u3000\u3000\u53ea\u542c\u201c\u7830\u201d\u5730\u4e00\u58f0\uff0c\u8f66\u95e8\u5927\u5f00\uff0c\u6bdb\u6587\u742a\u53c8\u81ea\u51b2\u4e86\u4e0b\u6765\uff0c\u9a6c\u8f66\u6536\u52bf\u4e0d\u4f4f\uff0c\u5374\u5df2\u51b2\u51fa\u4e09\u4e08\u3002 \u3000\u3000\u6bdb\u6587\u742a\u4e00\u6b65\u7a9c\u5230\u94c1\u5e73\u8eab\u524d\uff0c\u674f\u773c\u5706\u7741\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4ec0\u4e48\u6d88\u606f\uff0c\u5230\u5e95\u662f\u4ec0\u4e48\u6d88\u606f\uff1f\u201d \u3000\u3000\u94c1\u5e73\u4f3c\u7b11\u975e\u7b11\uff0c\u7f13\u7f13\u6478\u7740\u4ed6\u4e0b\u5df4\u4e0a\u521d\u751f\u7684\u80e1\u987b\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u8fd9\u6d88\u606f\u4e48\uff01\u54b3\u54b3\uff01\u563f \u563f\uff01\u2026\u2026\u201d \u3000\u3000\u6bdb\u6587\u742a\u5fc3\u91cc\u4e00\u80a1\u6012\u6c14\u4e0a\u51b2\uff0c\u626c\u8d77\u624b\u6765\uff0c\u201c\u5427\u201d\u5730\u5728\u94c1\u5e73\u9762\u4e0a\u62cd\u4e86\u4e00\u4e0b\u8033\u5149\uff0c\u5927\u6012\u7740\u559d \u9053\uff1a\u201c\u4f60\u5230\u5e95\u8bf4\u4e0d\u8bf4\uff1f\u201d \u3000\u3000\u94c1\u5e73\u9762\u4e0a\u4ecd\u7136\u4f3c\u7b11\u975e\u7b11\uff0c\u65b9\u624d\u90a3\u4e00\u8bb0\u8033\u5149\uff0c\u7adf\u50cf\u4f3c\u6839\u672c\u4e0d\u662f\u6253\u5728\u4ed6\u8138\u4e0a\u3002 \u3000\u3000\u4ed6\u4ecd\u7136\u7f13\u7f13\u6478\u7740\u80e1\u987b\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u8fd9\u6d88\u606f\u4e48\u2026\u2026\u662f\u548c\u59d1\u5a18\u5fc3\u91cc\u5f88\u5173\u5fc3\u7684\u4e00\u4e2a\u4eba\u6709\u5173\u7cfb \u7684\u2026\u2026\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u987f\u4f4f\u8bdd\u58f0\uff0c\u624b\u638c\u4e0a\u79fb\uff0c\u5f00\u59cb\u7f13\u7f13\u629a\u6478\u8d77\u65b9\u624d\u88ab\u6253\u8fc7\u7684\u5730\u65b9\u3002 \u3000\u3000\u6bdb\u6587\u742a\u7b49\u4e86\u534a\u664c\uff0c\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u52c9\u5f3a\u538b\u4e0b\u4e00\u9635\u6012\u6c14\uff0c\u9762\u4e0a\u6cdb\u51fa\u7740\u82b1\u822c\u7684\u5a07\u7b11\uff0c\u751c\u7b11\u7740\u67d4\u58f0 \u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u4f60\u8bf4\u5440\u3002\u201d \u3000\u3000\u94c1\u5e73\u9053\uff1a\u201c\u54ce\u54df\u2026\u2026\u54b3\u54b3\u2026\u2026\u201d \u3000\u3000\u6bdb\u6587\u742a\u751c\u7b11\u7740\u9053\uff1a\u201c\u5440\u2026\u2026\u6211\u6253\u7740\u4e86\u4f60\u4e48\uff1f\uff1f\u4f38\u624b\u5728\u4ed6\u8138\u4e0a\u8f7b\u8f7b\u6478\u4e86\u4e00\u4e0b\uff0c\u5fc3\u91cc\u7684\u6012 \u6c14\uff0c\u5374\u5df2\u5feb\u8981\u7206\u70b8\u4e86\u3002\u94c1\u5e73\u7709\u6bdb\u4e0a\u626c\uff0c\u773c\u5e18\u5374\u4e0b\u5782\uff0c\u534a\u9616\u7740\u773c\u775b\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u55ef\uff01\u73b0\u5728\u597d\u4e86 \u4e00\u4e9b\u2026\u2026\u201d \u3000\u3000\u6bdb\u6587\u742a\u67d4\u58f0\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u90a3\u6d88\u606f\uff0c\u53ef\u662f\u548c\u7f2a\u6587\u6709\u5173\u4e48\uff1f\u201d \u3000\u3000\u94c1\u5e73\u70b9\u4e86\u70b9\u5934\uff0c\u53e3\u4e2d\u5374\u9891\u9891\u9053\uff1a\u201c\u597d\u75db\u597d\u75db\uff0c\u82e5\u662f\u59d1\u5a18\u80fd\u2026\u2026\u201d \u3000\u3000\u6bdb\u6587\u742a\u8f7b\u8f7b\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u7684\u813e\u6c14\uff0c\u7edd\u4e0d\u80af\u767d\u767d\u505a\u4e00\u4ef6\u4e8b\u7684\uff0c\u5176\u5b9e\u6211\u4e5f\u4e0d\u5173\u5fc3 \u4ed6\uff0c\u53ea\u4e0d\u8fc7\u4f60\u4e0d\u8bf4\u51fa\u6765\uff0c\u6211\u5fc3\u91cc\u5b9e\u5728\u95f7\u5f97\u614c\uff01\u201d\u2026\u5979\u9762\u4e0a\u7684\u7b11\u5bb9\u53d8\u5f97\u66f4\u52a0\u751c\u7f8e\uff0c\u6084\u6084\u9053\uff1a \u201c\u4f60\u8981\u662f\u544a\u8bc9\u4e86\u6211\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u5a07\u7b11\u4e00\u58f0\uff0c\u4f4f\u53e3\u4e0d\u8bed\u3002 \u3000\u3000\u94c1\u5e73\u76ee\u5149\u4e71\u8f6c\uff0c\u53c8\u671b\u4e86\u90a3\u8fb9\u7684\u8f66\u592b\u4e00\u773c\uff0c\u7b11\u9053\uff1a\u201c\u771f\u7684\uff1f\u201d \u3000\u3000\u6bdb\u6587\u742a\u9ed8\u9ed8\u70b9\u4e86\u70b9\u5934\uff0c\u94c1\u5e73\u8f7b\u8f7b\u9053\uff1a\u201c\u90a3\u59d3\u7f2a\u7684\u2026\u2026\u6b64\u523b\u53ea\u6015\u5df2\u7ecf\u6b7b\u4e86\u3002\u201d \u7b2c\u4e8c\u5341\u4e94\u7ae0>> \u53e4\u9f99\u300a\u6e58\u5983\u5251\u300b \u7b2c\u4e8c\u5341\u4e94\u7ae0 \u3000\u3000\u6bdb\u6587\u742a\u8eab\u8eaf\u4e00\u9707\uff0c\u4f46\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u5979\u7684\u611f\u89c9\u5374\u662f\u832b\u7136\u7684\u3002 \u3000\u3000\u5979\u6ca1\u6709\u75db\u82e6\uff0c\u4e5f\u6ca1\u6709\u60ca\u9707\uff0c\u4e5f\u4e0d\u76f8\u4fe1\uff0c\u7f2a\u6587\u5df2\u662f\u6b7b\u4e86\uff0c\u5979\u5fc3\u91cc\u53ea\u662f\u832b\u832b\u7136\uff0c\u4e00\u56e2\u6df7\u4e71\u5730 \u832b\u832b\u7136\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u56e2\u6df7\u4e71\u7684\u832b\u832b\u7136\u91cc\uff0c\u94c1\u5e73\u53c8\u81ea\u4e00\u7b11\uff0c\u63a5\u53e3\u8bf4\u9053\uff1a\u201c\u5e08\u5085\u603b\u89c9\u5f97\u4ed6\u50cf\u662f\u81ea\u5df1\u4e00\u4e2a \u5f3a\u4ec7\u7684\u540e\u4eba\uff0c\u5374\u4e0d\u80fd\u51b3\u5b9a\uff0c\u53c8\u89c9\u5f97\u4ed6\u603b\u8981\u5bf9\u81ea\u5df1\u4e0d\u5229\uff0c\u4f46\u4e5f\u4e0d\u80fd\u786e\u5b9a\uff0c\u662f\u4ee5\u8fd9\u4e9b\u65e5\u5b50\uff0c\u5e08\u5085 \u5fc3\u60c5\u6781\u4e0d\u5b89\u5b81\uff0c\u5230\u540e\u6765\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u5fae\u987f\uff0c\u542b\u7b11\u63a5\u53e3\u9053\uff1a\u201c\u6709\u4e00\u5929\u5e08\u5085\u5ffd\u7136\u5bf9\u6211\u8bf4\uff1a\u2018\u5b81\u53ef\u6211\u8d1f\u5929\u4e0b\u4eba\uff0c\u6bcb\u6559\u4e00\u4eba\u8d1f \u6211\u3002\u2019 \u3000\u3000\u7b2c\u4e8c\u5929\uff0c\u5c31\u662f\u6628\u5929\uff0c\u5e08\u5085\u4fbf\u8c03\u96c6\u4e86\u5341\u6570\u4e2a\u9ad8\u624b\uff0c\u53bb\u53d6\u59d3\u7f2a\u7684\u6027\u547d\uff0c\u800c\u4e14\u8fd8\u544a\u8bc9\u4ed6\u4eec\uff0c\u4ed6 \u4eec\u53ef\u4ee5\u9009\u62e9\u4efb\u4f55\u65b9\u6cd5\uff0c\u4efb\u4f55\u624b\u6bb5\u3002\u201d \u3000\u3000\u4ed6\u4ef0\u5929\u5927\u7b11\u51e0\u58f0\uff0c\u76ee\u5149\u4e00\u671b\u5929\u8272\uff0c\u53c8\u9053\uff1a\u201c\u5230\u4e86\u6b64\u523b\u2026\u2026\u563f\u563f\uff0c\u90a3\u59d3\u7f2a\u7684\u7109\u80fd\u8fd8\u6709\u547d \u5728\uff1f\u201d \u3000\u3000\u6bdb\u6587\u742a\u6728\u7136\u7acb\u5728\u5730\u4e0a\uff0c\u6b8b\u971e\u7684\u91c7\u5149\uff0c\u6620\u7740\u5979\u82cd\u767d\u7684\u5a07\u9765\uff0c\u4f7f\u5f97\u5979\u770b\u6765\u53e6\u5177\u4e00\u79cd\u4e0d\u53ef\u6297\u62d2 \u7684\u9b45\u529b\u3002 \u3000\u3000\u94c1\u5e73\u76ee\u5149\u4e00\u8f6c\uff0c\u8f6c\u5230\u5979\u8138\u4e0a\uff0c\u4fbf\u518d\u4e5f\u79fb\u52a8\u4e0d\u5f00\u3002 \u3000\u3000\u5979\u75f4\u7b11\u7740\u9053\uff1a\u201c\u59d1\u5a18\uff01\u6211\u77e5\u9053\u7684\u5df2\u5168\u90fd\u544a\u8bc9\u4e86\u4f60\uff0c\u4f60\u2026\u2026\u201d \u3000\u3000\u6bdb\u6587\u742a\u4ecd\u7136\u5446\u5446\u5730\u6728\u7acb\u7740\uff0c\u7a81\u7136\u8f6c\u8fc7\u8eab\u6765\uff0c\u62fc\u5c3d\u5168\u529b\uff0c\u5728\u94c1\u5e73\u9762\u4e0a\u201c\u5427\u201d\u5730\u6253\u4e86\u4e00\u4e2a\u8033 \u5149\uff0c\u5530\u5730\u4e00\u63a0\u4e94\u4e08\uff0c\u63a0\u4e0a\u9a6c\u8f66\u7684\u524d\u5ea7\uff0c\u5288\u624b\u593a\u8fc7\u4e86\u8f66\u592b\u624b\u4e2d\u7684\u7f30\u7ef3\u548c\u4e1d\u97ad\uff0c\u4e1d\u97ad\u4e00\u626c\uff0c\u9a6c\u8f66 \u50cf\u662f\u4e00\u53ea\u7bad\u4f3c\u5730\u7a9c\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u638c\u76f4\u6253\u5f97\u94c1\u5e73\u51cc\u7a7a\u7ffb\u4e86\u4e00\u4e2a\u7b4b\u6597\uff0c\u201c\u5657\u201d\u5730\u5750\u5728\u5730\u4e0a\uff0c\u5de6\u988a\u706b\u8fa3\u8fa3\u5730\uff0c\u7ea2\u5f97\u5c31\u50cf\u662f \u6b64\u523b\u5929\u8fb9\u7684\u6b8b\u971e\u4e00\u6837\u3002 \u3000\u3000\u4ed6\u5446\u4e86\u534a\u664c\uff0c\u65b9\u81ea\u6068\u6068\u4e00\u54ac\u7259\uff0c\u4f46\u5de6\u8fb9\u7684\u7259\u9f7f\uff0c\u5374\u5df2\u6709\u4e24\u53ea\u8131\u843d\u4e86\u3002 \u3000\u3000\u7b49\u5230\u4ed6\u8fd9\u4e00\u9635\u6124\u6012\u7684\u9ebb\u6728\u6d88\u5931\uff0c\u62ac\u8d77\u5934\u6765\uff0c\u5fc3\u5934\u7a81\u53c8\u4e00\u9707\uff0c\u53ea\u89c1\u4e00\u80d6\u4e00\u7626\u4e24\u4e2a\u9526\u8863\u8001 \u4eba\uff0c\u5e76\u80a9\u7acb\u5728\u4ed6\u9762\u524d\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u88c5\u675f\u867d\u6781\u5e73\u51e1\uff0c\u795e\u6001\u4e5f\u5e73\u51e1\uff0c\u4f46\u9762\u5bb9\u4e0e\u76ee\u5149\u4e4b\u95f4\uff0c\u5374\u4f3c\u5e26\u7740\u4e00\u79cd\u65e0\u6cd5\u5f62\u5bb9\u7684\u5996\u5f02 \u4e4b\u6c14\uff0c\u6559\u4eba\u65e0\u8bba\u5982\u4f55\u4e5f\u4e0d\u80fd\u5c06\u4ed6\u4e24\u4eba\u5f53\u505a\u5e73\u51e1\u7684\u4eba\u3002 \u3000\u3000\u8fd9\u56db\u9053\u5996\u5f02\u7684\u76ee\u5149\uff0c\u5c31\u50cf\u662f\u6b7b\u4e86\u4f3c\u7684\uff0c\u4e00\u77ac\u4e0d\u77ac\u5730\u76ef\u5728\u94c1\u5e73\u8138\u4e0a\uff01 \u3000\u3000\u201c\u593a\u547d\u4f7f\u8005\u2019\u94c1\u5e73\u80c6\u91cf\u867d\u5927\uff0c\u4f46\u6b64\u523b\u5fc3\u5e95\u5374\u4e0d\u7531\u81ea\u4e3b\u5730\u5347\u51fa\u4e00\u9635\u5bd2\u610f\uff0c\u8fde\u9762\u4e0a\u706b\u8fa3\u8fa3\u7684 \u75bc\u75db\u548c\u5fc3\u91cc\u7684\u5c48\u8fb1\u4e0e\u6124\u6012\u90fd\u5fd8\u8bb0\u4e86\uff0c\u53cc\u624b\u6276\u5730\uff0c\u5750\u5728\u5730\u4e0a\uff0c\u4e0d\u77e5\u662f\u8be5\u7ad9\u8d77\u6765\uff0c\u6291\u6216\u662f\u4e0d\u8be5\u7ad9 \u8d77\u6765\u3002\u53ea\u542c\u5de6\u9762\u4e00\u4eba\u7f13\u7f13\u9053\uff1a\u201c\u65b9\u624d\u90a3\u5973\u5b50\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u6bcf\u4e00\u4e2a\u5b57\u90fd\u8bf4\u5f97\u5341\u5206\u6b63\u786e\uff0c\u4f46\u5374\u4ee4\u4eba\u4e0d\u80fd\u81ea\u7981\u5730\u751f\u51fa\u4e00\u79cd\u5947\u5f02\u7684\u4e0d\u8212\u670d\u4e4b\u611f\u2014\u2014 \u65e2\u751f\u786c\uff0c\u53c8\u67af\u6da9\uff0c\u4e5f\u4e0d\u77e5\u662f\u4ec0\u4e48\u5473\u9053\u3002 \u3000\u3000\u94c1\u5e73\u6014\u4e86\u534a\u664c\uff0c\u7a81\u7136\u957f\u8eab\u8dc3\u4e86\u8d77\u6765\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u8f6c\u8eab\u5c31\u8d70\u3002 \u3000\u3000\u54ea\u77e5\u4ed6\u65b9\u4e00\u4e3e\u6b65\uff0c\u90a3\u4e24\u4e2a\u9526\u8863\u8001\u4eba\u811a\u6b65\u4eff\u4f5b\u5782\u4e91\u4f3c\u7684\uff0c\u8eab\u8eaf\u867d\u672a\u52a8\uff0c\u5374\u53c8\u5df2\u5e76\u80a9\u6321\u5728\u4ed6 \u9762\u524d\u3002 \u3000\u3000\u53f3\u9762\u4e00\u4eba\u7f13\u7f13\u5f00\u53e3\uff1a\u201c\u65b9\u624d\u90a3\u5973\u5b50\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u4ecd\u7136\u540c\u6837\u7684\u4e00\u53e5\u8bdd\uff0c\u4ecd\u662f\u540c\u6837\u7684\u8bed\u58f0\uff0c\u542c\u6765\u5c31\u50cf\u662f\u4e00\u4e2a\u4eba\u8bf4\u7684\uff0c\u4e1d\u6beb\u6ca1\u6709\u534a\u70b9\u5dee\u522b\u3002 \u3000\u3000\u94c1\u5e73\u4e00\u633a\u80f8\u819b\uff0c\u6124\u6124\u6fc0\u53d1\u51fa\u4e00\u9635\u52c7\u6c14\uff0c\u5927\u559d\u9053\uff1a\u201c\u4f60\u7ba1\u4e0d\u7740\uff01\u201d \u3000\u3000\u5de6\u9762\u4e00\u4eba\u563b\u563b\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u8bf4\uff0c\u6253\u6b7b\u4f60\u3002\u201d \u3000\u3000\u8fd9\u7b11\u58f0\u7adf\u4f7f\u5f97\u94c1\u5e73\u8eab\u4e0a\u6839\u6839\u6bdb\u53d1\u4ff1\u90fd\u7ad6\u4e86\u8d77\u6765\uff0c\u6c42\u52a9\u5730\u56db\u671b\u4e00\u773c\uff0c\u56db\u4e0b\u4e00\u65e0\u4eba\u8ff9\uff0c\u6b8b\u971e \u6e10\u6ca1\uff0c\u5929\u8272\u66f4\u6697\u4e86\u3002 \u3000\u3000\u53f3\u9762\u4e00\u4eba\u4ea6\u81ea\u563b\u563b\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u544a\u8bc9\u6211\uff0c\u4f60\u6709\u597d\u5904\u3002\u201d \u3000\u3000\u94c1\u5e73\u53cc\u7709\u4e00\u626c\uff0c\u7a81\u5730\u5927\u559d\u4e00\u58f0\uff1a\u201c\u6eda\u5f00\uff01\u201d \u3000\u3000\u62fc\u5c3d\u5168\u8eab\u529f\u529b\uff0c\u4e00\u62db\u201c\u53cc\u9f99\u593a\u73e0\u201d\uff0c\u53cc\u5934\u9f50\u51fa\uff0c\u547c\u5730\u51fb\u53bb\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u65e9\u5df2\u7b97\u5b9a\u8fd9\u4e24\u62f3\u5fc5\u5b9a\u4e0d\u80fd\u5c06\u8fd9\u4e24\u4e2a\u8001\u4eba\u51fb\u5012\uff0c\u662f\u4ee5\u8fd9\u4e00\u62db\u867d\u5c3d\u5168\u529b\uff0c\u4f46\u4ecd\u7136\u7559\u6709 \u540e\u7740\uff0c\u53ea\u8981\u8fd9\u4e24\u4e2a\u8001\u4eba\u8eab\u5f62\u4e00\u95ea\uff0c\u4ed6\u4fbf\u4f1a\u7acb\u523b\u51b2\u8fc7\u53bb\uff0c\u8fdc\u8fdc\u9003\u8d70\uff0c\u56e0\u4e3a\u4ed6\u65e0\u6cd5\u5fcd\u53d7\u8fd9\u4e24\u4eba\u76ee \u5149\u4e2d\u7684\u5a07\u5f02\u4e4b\u6c14\u3002 \u3000\u3000\u54ea\u77e5\u4ed6\u53cc\u62f3\u65b9\u51fa\uff0c\u62f3\u5934\u4e0d\u77e5\u600e\u5730\uff0c\u7adf\u5df2\u5230\u4e86\u8fd9\u4e24\u4e2a\u8001\u4eba\u7684\u638c\u4e2d\uff0c\u8fd9\u4e24\u62f3\u5c31\u50cf\u662f\u4e00\u9f50\u6253\u5230 \u70c2\u6ce5\u4e0a\uff0c\u201c\u556a\u201d\u5730\u4e00\u58f0\uff0c\u52b2\u529b\u5168\u6d88\u3002 \u3000\u3000\u4ed6\u5fc3\u5934\u53c8\u4e00\u5bd2\uff0c\u518d\u6b21\u5927\u559d\u4e00\u58f0\uff0c\u8fd0\u52b2\u593a\u62f3\uff0c\u54ea\u77e5\u4ed6\u5168\u8eab\u7684\u52b2\u529b\uff0c\u7adf\u4e5f\u5ffd\u7136\u65e0\u5f71\u65e0\u8e2a\uff0c\u76ee \u5149\u62ac\u5904\uff0c\u90a3\u56db\u9053\u5996\u5f02\u7684\u76ee\u5149\uff0c\u4ecd\u7136\u6ce8\u5b9a\u7740\u4ed6\u3002 \u3000\u3000\u5de6\u9762\u8001\u4eba\u53c8\u81ea\u563b\u563b\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u6253\u4e0d\u8fc7\u6211\u7684\u3002\u201d \u3000\u3000\u53f3\u9762\u8001\u4eba\u63a5\u53e3\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u662f\u8bf4\u51fa\u6765\u5427\uff01\u201d \u3000\u3000\u4e24\u4eba\u4e00\u9f50\u7b11\u4e86\u8d77\u6765\uff0c\u94c1\u5e73\u53ea\u89c9\u81ea\u5df1\u52c7\u6c14\u5168\u6d88\uff0c\u832b\u832b\u7136\u95f4\uff0c\u5df2\u8131\u53e3\u9053\uff1a\u201c\u90a3\u662f\u2018\u7075\u86c7\u2019\u6bdb \u81ec\u7684\u7231\u5973\u3002\u201d \u3000\u3000\u4e24\u4e2a\u8001\u4eba\u5bf9\u671b\u4e00\u773c\uff0c\u76ee\u5149\u4e2d\u4f3c\u4e4e\u5728\u8bf4\uff1a\u201c\u679c\u7136\u4e0d\u9519\u3002\u201d \u3000\u3000\u5de6\u9762\u4e00\u4eba\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u5c31\u662f\u6bdb\u81ec\u7684\u5f92\u5f1f\u4e86\u3002\u201d \u3000\u3000\u94c1\u5e73\u6728\u7136\u70b9\u4e86\u70b9\u5934\uff0c\u53f3\u9762\u4e00\u4eba\u9053\uff1a\u201c\u5e26\u6211\u53bb\u89c1\u6bdb\u81ec\uff01\u201d \u3000\u3000\u4e24\u4eba\u8eab\u8eaf\u672a\u8f6c\uff0c\u4e0d\u77e5\u600e\u5730\u4e00\u6765\uff0c\u94c1\u5e73\u4fbf\u5df2\u88ab\u4ed6\u4e24\u4eba\u5939\u5728\u4e2d\u95f4\uff0c\u8fd9\u65f6\u67f3\u6811\u4e0b\u4f3c\u6709\u4eba\u5f71\u4e00 \u95ea\uff0c\u4f46\u77ac\u5373\u6ca1\u5165\u9ed1\u6697\u4e2d\u3002 \u3000\u3000\u591a\u5f69\u591a\u59ff\u7684\u676d\u5dde\u57ce\uff0c\u5728\u8fd9\u4e09\u4e94\u65e5\u91cc\uff0c\u53d8\u5f97\u66f4\u591a\u59ff\u591a\u5f69\u4e86\u3002 \u3000\u3000\u5251\u9798\u662f\u7eff\u9ca8\u9c7c\u76ae\u7684\uff0c\u5251\u7a57\u662f\u9c9c\u8840\u4e00\u822c\u7684\u7ea2\u8272\uff0c\u957f\u5251\u51fa\u9798\uff0c\u5374\u662f\u60e8\u78a7\u78a7\u7684\u9752\u5149\uff0c\u800c\u4f69\u5251\u4eba \u7684\u773c\u775b\uff0c\u5374\u662f\u72c2\u70ed\u7684\u9ed1\u8272\u3002 \u3000\u3000\u8fd9\u4e9b\uff0c\u5c31\u662f\u5ae3\u7ea2\u59f9\u7d2b\u7684\u897f\u5b50\u6e56\uff0c\u6587\u91c7\u98ce\u6d41\u7684\u676d\u5dde\u57ce\uff0c\u8fd1\u65e5\u6765\u6240\u52a0\u4e0a\u7684\u989c\u8272\uff0c\u5f53\u7136\u4e00\u4e00\u9664 \u8fd9\u4e9b\u4e4b\u5916\uff0c\u8fd8\u6709\u7425\u73c0\u8272\u7684\u7f8e\u9152\uff0c\u8c61\u7259\u8272\u7684\u80f8\u819b\uff0c\u60e8\u767d\u8272\u7684\u6307\u8282\uff0c\u60e8\u767d\u8272\u7684\u8138\uff0c\u60e8\u767d\u8272\u7684\u5973 \u4eba\u2026\u2026 \u3000\u3000\u6b66\u6797\u5251\u624b\u7684\u6307\u8282\uff0c\u4e0d\u77e5\u600e\u5730\uff0c\u901a\u5e38\u90fd\u662f\u60e8\u767d\u8272\u7684\uff0c\u5c24\u5176\u662f\u5728\u4ed6\u4eec\u63e1\u5251\u7684\u65f6\u5019\uff0c\u60e8\u767d\uff0c\u5c31 \u66f4\u60e8\u767d\u4e86\u3002 \u3000\u3000\u4e8e\u662f\u897f\u5b50\u6e56\u6d53\u6d53\u5730\u88c5\u9970\u4e86\u8d77\u6765\u2026\u2026 \u3000\u3000\u4f46\u897f\u5b50\u6e56\u4e2d\u7684\u6c34\uff0c\u5374\u4e98\u53e4\u4e5f\u4e0d\u4f1a\u53d8\u4e86\u989c\u8272\u3002 \u3000\u3000\u4e00\u5f2f\u9752\u6c34\uff0c\u4e00\u7247\u7eff\u6ce2\uff0c\u9ec4\u660f\u2026\u2026 \u3000\u3000\u7eff\u6ce2\u6e56\u6c34\u4e2d\uff0c\u753b\u822b\u5982\u7ec7\uff0c\u4f46\u753b\u822b\u4e2d\u5374\u5df2\u5c11\u4e86\u541f\u8bd7\u8054\u53e5\u7684\u6587\u4eba\u96c5\u58eb\uff0c\u53d8\u4e86\u51fb\u7504\u9ad8\u6b4c\u7684\u6b66\u6797 \u8c6a\u5ba2\u3002 \u3000\u3000\u82cf\u5824\u4e0b\u2026\u2026 \u3000\u3000\u7eff\u6ce2\u6d9f\u6f2a\uff0c\u7a81\u5730\u2026\u2026 \u3000\u3000\u4e00\u6ef4\u9c9c\u8840\uff0c\u6ef4\u5165\u7eff\u6ce2\uff0c\u4f46\u8f6c\u77ac\u95f4\u4fbf\u88ab\u5316\u5f00\uff0c\u6e56\u6c34\u4ecd\u7136\u78a7\u7eff\u3002 \u3000\u3000\u82cf\u5824\u4e0a\u2026\u2026 \u3000\u3000\u5927\u888d\u98d8\u62c2\uff0c\u8863\u8896\u51cc\u98ce\u7684\u201c\u7f2a\u6587\u201d\u76ee\u5149\u60ca\u8be7\u5730\u671b\u7740\u4ed6\u8eab\u4fa7\u7684\u4e00\u4e2a\u4e4c\u53d1\u9ad8\u7c2a\uff0c\u7070\u888d\u81ea\u889c\u7684\u9053 \u4eba\u2014\u2014\u534e\u5c71\u94f6\u9e64\u3002 \u3000\u3000\u8fd9\u534e\u5c71\u5251\u6d3e\u4e2d\u7684\u4e00\u7ea7\u5251\u624b\uff0c\u6b64\u523b\u6b63\u4ee5\u60e8\u767d\u7684\u624b\u638c\uff0c\u6a2a\u6301\u957f\u5251\uff0c\u5251\u5c16\u6a2a\u5904\uff0c\u5374\u5728\u81ea\u5df1\u81c2\u4e0a \u523a\u4e86\u4e00\u5251\u3002 \u3000\u3000\u4e00\u6ef4\u9c9c\u8840\uff0c\u6ef4\u5165\u7eff\u6ce2\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u5446\u4e86\u534a\u664c\uff0c\u5fcd\u4e0d\u4f4f\u8be7\u58f0\u9053\uff1a\u201c\u9053\u957f\uff0c\u4f60\u8fd9\u662f\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u94f6\u9e64\u9053\u4eba\u624b\u6301\u957f\u5251\uff0c\u4ef0\u76ee\u671b\u5929\uff0c\u826f\u4e45\u826f\u4e45\uff0c\u65b9\u81ea\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4ec7\u6068\uff01\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u5fae\u5fae\u76b1\u7709\uff0c\u5e94\u58f0\u9053\uff1a\u201c\u4ec7\u6068\uff1f\u2026\u2026\u201d \u3000\u3000\u94f6\u9e64\u9053\u4eba\u5782\u4e0b\u4e86\u76ee\u5149\uff0c\u4ed6\u76ee\u5149\u6b63\u548c\u6e56\u6c34\u4e00\u6837\uff0c\u6563\u53d1\u7740\u60e8\u78a7\u7684\u989c\u8272\u2014\u2014\u5c31\u50cf\u5251\u5149\u4e00\u6837\u60e8\u78a7 \u7684\u989c\u8272\u3002 \u3000\u3000\u4ed6\u671b\u4e86\u201c\u7f2a\u6587\u201d\u4e00\u773c\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4ec7\u6068\uff01\u6b63\u662f\u4e3a\u4e86\u4ec7\u6068\uff01\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u5377\u8d77\u5bbd\u5927\u7684\u8863\u8896\uff0c\u7f2a\u6587\u4e3e\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u4ed6\u4e00\u6761\u624b\u81c2\u4e4b\u4e0a\uff0c\u5251\u75d5\u6591\u6591\uff0c\u6559\u4eba\u89c1\u4e86\uff0c \u5fc3\u4e2d\u5fcd\u4e0d\u4f4f\u8981\u751f\u51fa\u4e00\u9635\u9635\u609a\u6817\u3002 \u3000\u3000\u4ed6\u6c89\u58f0\u63a5\u53e3\u9053\uff1a\u201c\u7f2a\u5144\uff0c\u4f60\u770b\uff0c\u8fd9\u4e9b\u90fd\u662f\u4ec7\u6068\uff0c\u4e8c\u5341\u5e74\u6765\uff0c\u6211\u5fc3\u4e2d\u9664\u4e86\u4ec7\u6068\u4e4b\u5916\uff0c\u51e0\u4e4e \u518d\u65e0\u4ed6\u7269\uff0c\u8fd9\u4ec7\u6068\u504f\u53c8\u65e0\u6e32\u6cc4\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u4ed6\u957f\u957f\u53f9\u606f\u4e00\u58f0\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u6211\u53ea\u6709\u4f24\u6b8b\u81ea\u5df1\u7684\u8eab\u4f53\uff0c\u8ba9\u5fc3\u91cc\u7684\u4ec7\u6068\u968f\u7740\u9c9c\u8840\u6d41\u51fa\u4e00 \u4e9b\uff0c\u5426\u5219\u2026\u2026\u5509\uff0c\u5426\u5219\u6211\u771f\u4e0d\u77e5\u9053\u5982\u4f55\u80fd\u6d3b\u5230\u4eca\u65e5\u3002\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u6233\u7136\u8bb8\u4e45\uff0c\u5583\u5583\u53cd\u590d\u81ea\u8bed\uff1a\u201c\u4ec7\u6068\u2026\u2026\u4ec7\u6068\u2026\u2026\u201d \u3000\u3000\u94f6\u9e64\u9053\u4eba\u51c4\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6740\u7236\u4ec7\u6068\uff0c\u4e0d\u5171\u6234\u5929\u7684\u4ec7\u6068\uff0c\u4ed4\u7ec6\u60f3\u6765\uff0c\u5374\u4e5f\u4e0d\u662f\u4eba\u4eba\u90fd\u80fd \u5c1d\u53d7\u7684\u4e8b\u2026\u2026\u201d \u3000\u3000\u4ed6\u76ee\u5149\u5ffd\u7136\u8f6c\u5411\u201c\u7f2a\u6587\u201d\uff0c\u9053\uff1a\u201c\u7f2a\u5144\uff0c\u4f60\u53ef\u77e5\u9053\u4ec7\u6068\u7684\u6ecb\u5473\uff1f\u5b83\u9664\u4e86\u53ef\u4ee5\u4e3a\u4eba\u5e26\u6765\u75db \u82e6\uff0c\u8fd8\u53ef\u6fc0\u52b1\u4eba\u4eec\u7684\u96c4\u5fc3\u58ee\u5fd7\u3002\u201d \u3000\u3000\u8bed\u58f0\u987f\u5904\uff0c\u5ffd\u53c8\u957f\u53f9\u4e00\u58f0\uff1a\u201c\u4f60\u81ea\u7136\u4e0d\u4f1a\u77e5\u9053\u7684\uff0c\u4e0d\u4f1a\u77e5\u9053\u7684\u2026\u2026\u6740\u7236\u4e4b\u4ec7\uff0c\u706d\u5bb6\u4e4b \u4ec7\u3002\u201d \u3000\u3000\u4ed6\u53c8\u81ea\u7f13\u7f13\u5408\u4e0a\u773c\u5e18\uff0c\u4f3c\u4e4e\u60f3\u63a9\u9970\u76ee\u4e2d\u5df2\u5c06\u6cdb\u6ee5\u7684\u6cea\u75d5\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u832b\u7136\u51dd\u6ce8\u7740\u524d\u65b9\uff0c\u5ffd\u7136\u6c89\u58f0\u9053\uff1a\u201c\u4f60\u7684\u4ec7\u4eba\u662f\u8c01\uff1f\u53ef\u4ee5\u544a\u8bc9\u6211\u4e48\uff1f\u201d \u3000\u3000\u94f6\u9e64\u9053\u4eba\u7f13\u7f13\u9053\uff1a\u201c\u4e3a\u2026\u2026\u4ec0\u2026\u2026\u4e48\uff1f\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u6c89\u58f0\u9053\uff1a\u201c\u5c0f\u5f1f\u867d\u4e0d\u624d\uff0c\u6216\u8005\u8fd8\u80fd\u52a9\u5144\u4e00\u81c2\u4e4b\u529b\uff01\u201d \u3000\u3000\u94f6\u9e64\u9053\u4eba\u4e0d\u970e\u773c\u5730\u51dd\u6ce8\u7740\u4ed6\uff0c\u4e5f\u4e0d\u77e5\u671b\u4e86\u591a\u4e45\uff0c\u65b9\u81ea\u957f\u53f9\u9053\uff1a\u201c\u6211\u90a3\u4ec7\u5bb6\uff01\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u672a\u4e86\uff0c\u7a81\u542c\u4e00\u58f0\u5927\u558a\uff1a\u201c\u5728\u8fd9\u91cc\uff01\u201d \u3000\u3000\u4e24\u4eba\u9f50\u5730\u4e00\u60ca\uff0c\u8f6c\u8eab\u671b\u53bb\uff0c\u53ea\u89c1\u957f\u5824\u5de6\u53f3\u4e24\u8fb9\uff0c\u540c\u65f6\u8d70\u8fc7\u5341\u4f59\u4e2a\u957f\u886b\u4f69\u5251\u7684\u4eba\u6765\u3002 \u3000\u3000\u8fd9\u5341\u4f59\u4eba\u6b65\u5c65\u4ff1\u90fd\u5341\u5206\u8f7b\u677e\uff0c\u4eba\u4eba\u9762\u4e0a\u4ff1\u90fd\u5e26\u7740\u7b11\u5bb9\uff0c\u5de6\u9762\u516d\u4eba\u9f50\u58f0\u7b11\u9053\uff1a\u201c\u5728\u8fd9 \u91cc\uff01\u201d \u3000\u3000\u4e00\u9f50\u8d70\u5230\u534e\u5c71\u94f6\u9e64\u8eab\u524d\uff0c\u4e3a\u9996\u4e00\u4eba\u957f\u886b\u6731\u5c65\uff0c\u795e\u91c7\u98de\u626c\uff0c\u6b63\u662f\u540d\u6ee1\u5929\u4e0b\u7684\u5251\u5ba2\u4e4b\u4e00\u2014\u2014 \u6e05\u98ce\u5251\u6731\u767d\u7fbd\u3002 \u3000\u3000\u4ed6\u76ee\u5149\u4e0a\u4e0b\u6253\u91cf\u534e\u5c71\u94f6\u9e64\u4e00\u773c\uff0c\u6717\u58f0\u7b11\u9053\uff1a\u201c\u5341\u4f59\u5e74\u6765\uff0c\u672a\u89c1\u534e\u5c71\u94f6\u886b\u5251\u5ba2\uff0c\u5374\u60f3\u4e0d\u5230 \u5728\u8fd9\u91cc\u89c1\u7740\u4e00\u4f4d\uff0c\u4e0d\u6562\u8bf7\u6559\uff0c\u9053\u5144\u51e4\u6015\u5c31\u662f\u65b9\u4e0b\u534e\u5c71\u7684\u94f6\u9e64\u9053\u957f\u5427\uff1f\u201d \u3000\u3000\u4e24\u4eba\u76ee\u5149\u4e00\u5bf9\uff0c\u5f7c\u6b64\u90fd\u5df2\u4e3a\u5bf9\u65b9\u98ce\u795e\u6240\u9189\uff0c\u5bd2\u55a7\u51e0\u53e5\uff0c\u6731\u767d\u7fbd\u5c06\u540c\u6765\u7684\u5251\u5ba2\uff0c\u4ff1\u90fd\u4e3a\u94f6 \u9e64\u9053\u4eba\u4e00\u4e00\u5f15\u89c1\uff0c\u8fd9\u4e9b\u4eba\u770b\u6765\u4ff1\u90fd\u6587\u8d28\u5f6c\u5f6c\uff0c\u4f46\u65e0\u4e00\u4e0d\u662f\u540d\u9707\u4e00\u65b9\u7684\u4fa0\u58eb\uff0c\u4e09\u8a00\u4e24\u8bed\uff0c\u4fbf\u8c08 \u5f97\u5341\u5206\u6295\u673a\u3002 \u3000\u3000\u53f3\u9762\u4e94\u4eba\uff0c\u4ea6\u81ea\u9f50\u58f0\u7b11\u9053\uff1a\u201c\u5728\u90a3\u91cc\uff01\u201d \u3000\u3000\u5374\u4e00\u9f50\u8d70\u5230\u201c\u7f2a\u6587\u201d\u8eab\u524d\uff0c\u5f53\u5934\u4e00\u4eba\uff0c\u80a9\u5bbd\u8170\u7a84\uff0c\u9526\u8863\u534e\u670d\uff0c\u5374\u662f\u90a3\u201c\u9e33\u9e2f\u53cc\u5251\u201d\u4e2d\u7684 \u7a0b\u67ab\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u5fae\u5fae\u4e00\u7b11\uff0c\u53e3\u4e2d\u9053\uff1a\u201c\u7a0b\u5144\u4e5f\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u76ee\u5149\u4e00\u626b\uff0c\u5374\u5df2\u5c06\u4ed6\u8eab\u540e\u7684\u56db\u4eba\u6253\u91cf\u4e86\u4e00\u904d\uff0c\u53ea\u89c1\u8fd9\u56db\u4eba\u4e2a\u4ff1\u662f\u4e09\u5341\u5de6\u53f3\u5e74\u7eaa\uff0c\u4ff1\u662f\u84dd \u888d\u9ed1\u5c65\uff0c\u8170\u4e2d\u6240\u60ac\uff0c\u4e5f\u4e00\u8272\u90fd\u662f\u4e4c\u9798\u957f\u5251\uff0c\u56db\u4eba\u9762\u4e0a\u4ff1\u90fd\u9762\u5e26\u7b11\u5bb9\uff0c\u4f46\u76ee\u5149\u4e2d\u5374\u65e0\u4e00\u4e1d\u4e00\u6beb \u7b11\u610f\u3002 \u3000\u3000\u7a0b\u67ab\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u7b97\u5b9a\u7f2a\u5144\u4e0d\u80af\u653e\u8fc7\u8fd9\u573a\u70ed\u95f9\uff0c\u5fc5\u5b9a\u4e5f\u8981\u5230\u676d\u5dde\u57ce\u6765\u7684\u3002\u201d \u3000\u3000\u7b11\u58f0\u4e4b\u4e2d\uff0c\u201c\u6e05\u98ce\u5251\u201d\u6731\u767d\u7fbd\u7a81\u5730\u8d70\u5230\u4ed6\u8eab\u65c1\uff0c\u6717\u58f0\u9053\uff1a\u201c\u4eca\u65e5\u5c0f\u5f1f\u4f5c\u4e1c\uff0c\u60f3\u8bf7\u8fd9\u4f4d\u94f6 \u9e64\u9053\u957f\u53bb\u75db\u996e\u4e00\u756a\u3002\u201d \u3000\u3000\u7a0b\u67ab\u832b\u7136\u4e00\u6014\uff0c\u4f46\u53e3\u4e2d\u5374\u4ea6\u7b11\u9053\uff1a\u201c\u597d\u6781\u597d\u6781\uff0c\u4e24\u4eba\u4ff1\u662f\u5f53\u4ee3\u5251\u5ba2\uff0c\u96be\u5f97\u4e00\u805a\u3002\u201d \u3000\u3000\u6731\u767d\u7fbd\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u516c\u5b50\u65e2\u4e0e\u94f6\u9e64\u9053\u957f\u540c\u5728\u4e00\u8d77\uff0c\u5c0f\u5f1f\u600e\u80fd\u4e0d\u8bf7\uff0c\u5c0f\u5f1f\u672c\u6765\u6709\u5fc3\u8ba9\u8fd9\u4f4d \u516c\u5b50\u4e0e\u5404\u4f4d\u591a\u8c08\u4e24\u53e5\uff0c\u4f46\u65e0\u5948\u9152\u763e\u53d1\u4e86\uff0c\u62b1\u6b49\u62b1\u6b49\u3002\u201d \u3000\u3000\u4ed6\u8f6c\u8eab\u5411\u201c\u7f2a\u6587\u201d\u4e00\u7b11\uff0c\u94f6\u9e64\u9053\u4eba\u5df2\u662f\u7b11\u9053\uff1a\u201c\u7f2a\u5144\uff0c\u6731\u5927\u4fa0\u5982\u6b64\u76db\u610f\uff0c\u4f55\u59a8\u540c\u53bb\u5171\u996e \u51e0\u676f\u3002\u201d \u3000\u3000\u7a0b\u67ab\u8fd8\u672a\u7b54\u8bdd\uff0c\u4ed6\u8eab\u540e\u7684\u56db\u4e2a\u84dd\u8863\u5251\u624b\u5df2\u81ea\u9762\u8272\u5fae\u53d8\uff0c\u7a0b\u67ab\u53cc\u7709\u5fae\u76b1\uff0c\u9053\uff1a\u201c\u4f46\u5c0f\u5f1f\u4e0e \u7f2a\u5144\u591a\u65e5\u672a\u89c1\uff0c\u4e5f\u60f3\u53bb\u75db\u996e\u4e00\u756a\u2026\u2026\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u5fae\u7b11\u622a\u53e3\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u6211\u4e0e\u94f6\u9e64\u9053\u957f\u53ea\u597d\u5206\u9053\u626c\u9573\u4e86\u3002\u201d \u3000\u3000\u94f6\u9e64\u9053\u4eba\u5fae\u4e00\u6c89\u541f\uff0c\u201c\u6e05\u98ce\u5251\u201d\u6731\u767d\u7fbd\u5df1\u5927\u7b11\u9053\uff1a\u201c\u597d\u597d\uff0c\u5206\u9053\u626c\u9573\u4e5f\u597d\u3002\u201d \u3000\u3000\u4e0d\u7531\u5206\u8bf4\uff0c\u62c9\u4e86\u201c\u534e\u5c71\u94f6\u9e64\u201d\u5c31\u8d70\uff0c\u8d70\u4e86\u51e0\u6b65\uff0c\u65b9\u81ea\u8f7b\u8f7b\u9053\uff1a\u201c\u6b64\u4eba\u6765\u5386\u4e0d\u660e\uff0c\u8a00\u8bed\u95ea \u70c1\uff0c\u5fc5\u6709\u4e00\u4e9b\u4e0d\u53ef\u544a\u4eba\u7684\u9690\u79c1\uff0c\u4f60\u6211\u5766\u8bda\u76f8\u4ea4\uff0c\u653e\u6000\u996e\u9152\uff0c\u5c11\u4e86\u6b64\u4eba\u4e5f\u597d\u3002\u201d \u3000\u3000\u534e\u5c71\u94f6\u9e64\u53cc\u7709\u4e00\u76b1\uff0c\u4f46\u5df2\u88ab\u8fd9\u4e00\u7fa4\u8c6a\u723d\u6d12\u8131\u7684\u5251\u5ba2\u62e5\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u4e0e\u7a0b\u67ab\u5e76\u80a9\u8d70\u4e0b\u82cf\u5824\uff0c\u7a0b\u67ab\u867d\u7136\u8c08\u7b11\u98ce\u751f\uff0c\u4f46\u5374\u59cb\u7ec8\u672a\u66fe\u5c06\u90a3\u56db\u4e2a\u84dd\u8863\u5251\u624b\u4e3a \u201c\u7f2a\u6587\u201d\u5f15\u89c1\u3002 \u3000\u3000\u8fd9\u56db\u4eba\u811a\u6b65\u8f7b\u7075\uff0c\u76ee\u4e2d\u795e\u5149\u6ee1\u8db3\uff0c\u770b\u6765\u6b66\u529f\u5b9a\u5fc5\u4e0d\u5f31\uff0c\u4f46\u4ee5\u65b9\u624d\u201c\u6e05\u98ce\u5251\u201d\u6731\u767d\u7fbd\u89c1\u5230 \u4ed6\u4eec\u65f6\u7684\u795e\u60c5\u770b\u6765\uff0c\u8fd9\u56db\u4eba\u5374\u53c8\u4e0d\u4f3c\u6b66\u6797\u4e2d\u7684\u6210\u540d\u4eba\u7269\u3002 \u3000\u3000\u6b64\u523b\u8fd9\u56db\u4eba\u4e24\u4e2a\u8d70\u5728\u201c\u7f2a\u6587\u201d\u8eab\u524d\uff0c\u53e6\u4e24\u4e2a\u5374\u8d70\u5728\u201c\u7f2a\u6587\u201d\u8eab\u540e\uff0c\u56db\u4eba\u867d\u5df2\u5206\u505a\u4e24\u5904\uff0c \u4f46\u811a\u6b65\u5374\u4ecd\u6574\u9f50\u5212\u4e00\uff0c\u4e00\u9f50\u4e3e\u6b65\uff0c\u4e00\u9f50\u843d\u6b65\uff0c\u5c31\u4eff\u4f5b\u65c1\u8fb9\u6709\u4eba\u5728\u51fb\u7740\u8282\u62cd\u4f3c\u7684\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u76ee\u5149\u8f6c\u5904\uff0c\u5fc3\u5ff5\u4ea6\u5728\u540c\u65f6\u8f6c\u52a8\uff0c\u4ed6\u5fc3\u4e2d\u867d\u5df2\u5f00\u59cb\u7591\u60d1\uff0c\u4f46\u5374\u53c8\u6781\u4e3a\u653e\u5fc3\uff0c\u56e0\u4e3a \u4ed6\u6df1\u4fe1\u8fd9\u201c\u9e33\u9e2f\u53cc\u5251\u201d\u4e2d\u7684\u7a0b\u67ab\uff0c\u65e9\u5df2\u88ab\u81ea\u5df1\u6253\u52a8\u3002 \u3000\u3000\u7a7f\u8fc7\u5782\u67f3\uff0c\u4e0b\u4e86\u82cf\u5824\uff0c\u6e56\u5149\u4e4b\u4e2d\uff0c\u4fbf\u6ee1\u662f\u5c71\u8272\u3002 \u3000\u3000\u5c06\u81f3\u5cb3\u738b\u575f\u65f6\uff0c\u201c\u7f2a\u6587\u201d\u6717\u58f0\u7b11\u9053\uff1a\u201c\u7a0b\u5144\uff0c\u4f60\u770b\u6211\u7b49\u4fe1\u6b65\u6240\u81f3\uff0c\u5c45\u7136\u8d70\u5230\u8fd9\u91cc\u6765\u4e86\uff0c \u8fd9\u91cc\u5cb3\u738b\u575f\u4e0a\uff0c\u6700\u591a\u6709\u4e9b\u796d\u9152\uff0c\u5374\u54ea\u6709\u9152\u5bb6\u53ef\u4f9b\u4f60\u6211\u4e70\u9189\uff1f\u201d \u3000\u3000\u7a0b\u67ab\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6709\u7684\u6709\u7684\u2026\u2026\u201d \u3000\u3000\u7b11\u58f0\u672a\u987f\uff0c\u56db\u4e2a\u84dd\u8863\u5251\u624b\u7a81\u7136\u4e00\u9f50\u62d4\u51fa\u5251\u6765\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u9762\u8272\u5fae\u53d8\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u7a0b\u5144\uff0c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u5fc3\u4e2d\u5374\u4e0d\u7981\u6697\u53f9\u4e00\u58f0\uff0c\u77e5\u9053\u90a3\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u7684\u786e\u662f\u4e2a\u67ad\u96c4\u4e4b\u624d\uff0c\u77ed\u77ed\u4e09\u4e24\u65e5\u529f\u592b\uff0c\u53c8\u5c06 \u7a0b\u67ab\u62c9\u4e86\u8fc7\u53bb\uff0c\u4ed6\u5374\u4e0d\u77e5\u9053\u7a0b\u67ab\u751f\u6027\u672c\u6765\u5c31\u6709\u5982\u5899\u5934\u4e4b\u8349\uff0c\u89c1\u98ce\u4fbf\u5012\u3002 \u3000\u3000\u53ea\u89c1\u7a0b\u67ab\u9762\u8272\u4e00\u6c89\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u5c31\u662f\u8fd9\u4e48\u56de\u4e8b\u3002\u201d \u3000\u3000\u624b\u638c\u4e00\u6325\uff0c\u56db\u67c4\u957f\u5251\u5ffd\u7136\u4e00\u9f50\u523a\u5411\u201c\u7f2a\u6587\u201d\u8eab\u4e0a\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u76f4\u5230\u6b64\u523b\uff0c\u867d\u4ecd\u4e0d\u80af\u5728\u4eba\u524d\u663e\u9732\u6b66\u529f\uff0c\u4f46\u8fd9\u56db\u67c4\u950b\u5229\u7684\u957f\u5251\uff0c\u5374\u4e0d\u5bb9\u4ed6\u518d\u6709\u9009 \u62e9\u3002 \u3000\u3000\u5251\u5149\u7f2d\u7ed5\u4e2d\uff0c\u201c\u7f2a\u6587\u201d\u80a9\u5934\u5fae\u8038\uff0c\u8f7b\u8f7b\u8dc3\u8d77\u3002 \u3000\u3000\u53ea\u542c\u7a0b\u67ab\u4ef0\u5929\u7b11\u9053\uff1a\u201c\u597d\u4e2a\u4e0d\u4f1a\u6b66\u529f\u7684\u6587\u5f31\u4e66\u751f\uff0c\u770b\u6765\u8fd8\u662f\u6bdb\u5927\u54e5\u80dc\u4eba\u4e00\u7b79\u3002\u201d \u3000\u3000\u72c2\u7b11\u4e4b\u58f0\uff0c\u88ab\u5636\u5636\u7684\u5251\u98ce\uff0c\u6bb5\u6bb5\u5206\u5272\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u8fd9\u56db\u4e2a\u84dd\u8863\u5251\u624b\uff0c\u5df2\u4e00\u8fde\u653b\u51fa\u56db\u4e03\u4e8c\u5341\u516b\u5251\uff0c\u4e00\u5251\u63a5\u7740\u4e00\u5251\uff0c\u4e8c\u5341\u516b \u62db\u5b9b\u5982\u540c\u65f6\u4f7f\u51fa\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u888d\u8896\u62c2\u52a8\uff0c\u8eab\u5f62\u5fae\u95ea\uff0c\u6bcf\u4e00\u5251\u90fd\u662f\u70b9\u7740\u4ed6\u8863\u670d\u524a\u4e0b\u53bb\u7684\uff0c\u4f46\u5374\u6ca1\u6709\u4e00\u5251\u6cbe\u7740\u4ed6 \u7684\u8863\u670d\u3002 \u3000\u3000\u8fd9\u56db\u4e2a\u84dd\u8863\u5251\u624b\u9762\u4e0a\u867d\u4ecd\u4e0d\u52a8\u58f0\u8272\uff0c\u4f46\u5fc3\u4e2d\u5374\u5728\u6697\u6697\u5403\u60ca\uff0c\u4ed6\u4eec\u518d\u4e5f\u60f3\u4e0d\u5230\u8fd9\u5c11\u5e74\u8eab\u5f62 \u6b65\u6cd5\uff0c\u7adf\u662f\u8fd9\u822c\u7075\u5999\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u5fc3\u4e2d\u53c8\u4f55\u5c1d\u4e0d\u5728\u6697\u6697\u5403\u60ca\uff0c\u8fd9\u56db\u4e2a\u84dd\u8863\u5251\u624b\u5251\u52bf\u4e4b\u7ef5\u5bc6\uff0c\u5251\u62db\u4e4b\u9510\u5229\uff0c\u7adf\u5df2\u51fa \u4e4e\u4ed6\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u7a0b\u67ab\u8896\u624b\u800c\u89c2\uff0c\u4e09\u62db\u4e00\u8fc7\uff0c\u4ed6\u76ee\u5149\u4fbf\u518d\u4e5f\u79bb\u4e0d\u5f00\u7f2a\u6587\u7684\u8eab\u5f62\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u65b9\u5728\u5947\u602a\uff0c\u600e\u5730\u201c\u7f2a\u6587\u201d\u76f4\u5230\u6b64\u523b\u4ecd\u672a\u8fd8\u624b\uff0c\u5fc3\u5ff5\u65b9\u8f6c\uff0c\u7a81\u542c\u201c\u53ee\u53ee\u94db\u94db\u2019\u4e00\u9635 \u58f0\u54cd\uff0c\u56db\u4e2a\u84dd\u8863\u5251\u624b\u4e2d\u7684\u957f\u5251\uff0c\u7adf\u4e00\u9f50\u5230\u4e86\u201d\u7f2a\u6587\u201d\u7684\u638c\u4e2d\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u4e00\u638c\u63e1\u7740\u56db\u67c4\u957f\u5251\u7684\u7cbe\u94a2\u5251\u5c16\u3002 \u3000\u3000\u7a0b\u67ab\u5fc3\u5934\u4e00\u9707\uff0c\u90a3\u56db\u4e2a\u84dd\u8863\u5251\u624b\u66f4\u662f\u60ca\u5f97\u76ee\u5b9a\u53e3\u5446\uff0c\u8fd9\u56db\u4eba\u4ff1\u662f\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u8fd1\u5e74\u6765\u82e6 \u5fc3\u57f9\u690d\u7684\u6b66\u529f\u597d\u624b\uff0c\u7ec8\u5e74\u4e0d\u79bb\u6bdb\u81ec\u7684\u5185\u5b85\uff0c\u867d\u7136\u5728\u6c5f\u6e56\u4e2d\u7edd\u65e0\u540d\u58f0\uff0c\u4f46\u6bdb\u81ec\u5374\u4e0d\u65f6\u8bf7\u4e00\u4e9b\u6b66 \u6797\u4e2d\u7684\u6210\u540d\u4eba\u7269\uff0c\u4e0e\u4e4b\u4ea4\u624b\uff0c\u7ed3\u679c\u8bc1\u660e\u4e86\u8fd9\u4e8c\u7fa4\u84dd\u8863\u5251\u624b\u7684\u6b66\u529f\uff0c\u6bd4\u4e4b\u6b66\u6797\u6210\u540d\u4eba\u7269\uff0c\u5e76\u65e0 \u900a\u8272\u3002 \u3000\u3000\u8981\u77e5\u6bdb\u81ec\u8fd1\u5e74\u6765\u540d\u6210\u5229\u5c31\uff0c\u5bf9\u4e8e\u81ea\u5df1\u7684\u751f\u547d\uff0c\u5f53\u771f\u770b\u5f97\u6bd4\u4ec0\u4e48\u90fd\u91cd\uff0c\u4ed6\u8111\u6d77\u4e2d\u65f6\u65f6\u523b\u523b \u4e0d\u80fd\u5fd8\u6000\u7684\u5c31\u662f\u5341\u516b\u5e74\u524d\u6df1\u5c71\u4e2d\u201c\u4ec7\u72ec\u201d\u4e34\u6b7b\u524d\u7684\u9762\u5bb9\uff0c\u4ed6\u57f9\u690d\u8fd9\u4e00\u7fa4\u8d34\u8eab\u7684\u536b\u58eb\u5251\u624b\uff0c\u4e3a \u7684\u53ea\u662f\u5728\u7d27\u6025\u5173\u5934\u4e4b\u7528\u3002 \u3000\u3000\u662f\u4ee5\u4ed6\u5c06\u8fd9\u4e00\u6279\u84dd\u8863\u5251\u624b\u770b\u5f97\u6781\u91cd\uff0c\u800c\u8fd9\u4e00\u6279\u84dd\u8863\u5251\u624b\u4e5f\u6df1\u77e5\u81ea\u8eab\u7684\u4ef7\u503c\uff0c\u54ea\u77e5\u4eca\u65e5\u600e \u4e00\u51fa\u624b\uff0c\u4fbf\u906d\u60e8\u8d25\uff01 \u3000\u3000\u201c\u7f2a\u6587\u201d\u76ee\u5149\u4e00\u626b\uff0c\u56db\u4e0b\u4f3c\u65e0\u4eba\u5f71\uff0c\u4ed6\u76ee\u5149\u4e2d\u7a81\u5730\u6cdb\u51fa\u4e00\u9635\u6740\u673a\uff0c\u624b\u8155\u4e00\u6296\uff0c\u56db\u67c4\u957f \u5251\uff0c\u4e00\u9f50\u6298\u65ad\uff0c\u638c\u4e2d\u7684\u56db\u6bb5\u5251\u5c16\uff0c\u7a81\u5730\u95ea\u7535\u822c\u98de\u51fa\uff0c\u56db\u4e2a\u84dd\u8863\u5251\u624b\u5927\u60ca\u4e4b\u4e0b\u8fd8\u672a\u5347\u8d77\u95ea\u907f\u7684 \u5ff5\u5934\uff0c\u8fd9\u56db\u6bb5\u5251\u5c16\u5df2\u81ea\u9489\u5728\u4ed6\u4eec\u7684\u80f8\u4e0a\u3002 \u3000\u3000\u56db\u58f0\u60e8\u547c\uff0c\u4e00\u9f50\u54cd\u8d77\u3002 \u3000\u3000\u7a0b\u67ab\u9762\u5bb9\u60e8\u767d\uff0c\u5450\u5450\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u2026\u2026\u201d \u3000\u3000\u4ed6\u89c1\u4e86\u201c\u7f2a\u6587\u201d\u8fd9\u79cd\u4e0d\u53ef\u6297\u62d2\u7684\u795e\u5947\u6b66\u529f\uff0c\u5fc3\u76ee\u4e2d\u5ffd\u7136\u60f3\u8d77\u4e00\u4e2a\u4eba\u7684\u5f71\u5b50\uff0c\u5341\u4f59\u5e74\u6765\uff0c \u4ed6\u4e00\u76f4\u4e0d\u613f\u60f3\u5230\u8fd9\u6761\u4eba\u5f71\uff0c\u662f\u4ee5\u8fd9\u4eba\u5f71\u5728\u4ed6\u5fc3\u4e2d\u5df2\u6e10\u6e10\u6a21\u7cca\u3002 \u3000\u3000\u800c\u6b64\u523b\u8fd9\u6a21\u7cca\u7684\u4eba\u5f71\uff0c\u7adf\u5728\u4e00\u970e\u95f4\u7a81\u5730\u53d8\u788d\u5341\u5206\u6e05\u6670\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u7f13\u7f13\u79fb\u52a8\u7740\u811a\u6b65\uff0c\u4ed6\u6bcf\u8d70\u4e00\u6b65\uff0c\u6b8b\u9633\u4fbf\u4f3c\u53c8\u9eef\u4e86\u4e00\u5206\uff0c\u8fdc\u5904\u5782\u67f3\u7684\u5f71\u5b50\uff0c\u4e5f\u6de1 \u6de1\u5730\u88ab\u878d\u5316\u5728\u591c\u8272\u4e2d\u3002 \u3000\u3000\u7a0b\u67ab\u7684\u77b3\u4ec1\u6e10\u6e10\u653e\u5927\uff0c\u4ed6\u5fc3\u4e2d\u7684\u4eba\u5f71\uff0c\u4f3c\u4e4e\u5df2\u4e0e\u4ed6\u773c\u524d\u6709\u7684\u4eba\u5f71\u5408\u4e8c\u4e3a\u4e00\u2026\uff5e\u90a3\u82f1\u4fca\u7684 \u9762\u5bb9\uff0c\u90a3\u633a\u79c0\u7684\u8eab\u8eaf\uff0c\u8fd8\u6709\u5634\u89d2\u6240\u5e26\u7740\u7684\u90a3\u4e00\u4efd\u6de1\u6de1\u7684\u8f7b\u8511\u4e0e\u5632\u5f04\uff0c\u76ee\u5149\u4e2d\u6240\u6563\u53d1\u7684\u90a3\u4e00\u79cd \u950b\u5229\u4e0e\u8427\u7d22\u2026\u2026 \u3000\u3000\u9661\u7136\u95f4\u8fd9\u540d\u626c\u5929\u4e0b\u7684\u5251\u5ba2\uff0c\u7adf\u4f3c\u5931\u53bb\u4e86\u4e89\u6218\u7684\u52c7\u6c14\uff0c\u53ea\u662f\u98a4\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u9762\u4e0a\u53c8\u6cdb\u8d77\u4e86\u90a3\u8f7b\u8511\u7684\u5fae\u7b11\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\uff01\u201d \u3000\u3000\u7a0b\u67ab\u5927\u559d\u4e00\u58f0\uff0c\u5636\u58f0\u558a\u9053\uff1a\u201c\u4ec7\u2026\u2026\u8840\u8fd8\u2026\u2026\u8840\u503a\u2026\u2026\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u51b7\u51b7\u9053\uff1a\u201c\u6b63\u662f\uff0c\u8840\u503a\u8840\u8fd8\uff01\u201d \u3000\u3000\u4ed6\u6b64\u523b\u5fc3\u4e2d\u5df2\u88ab\u4ec7\u6068\u5145\u6ee1\uff0c\u53ea\u89c9\u5fc3\u5934\u4e00\u9635\u9635\u70ed\u8840\u4e0a\u6d8c\uff0c\u6240\u6709\u7684\u8ba1\u5212\u90fd\u5728\u8fd9\u5954\u817e\u7684\u70ed\u8840\u4e2d \u6d88\u5931\uff0c\u4ed6\u6b64\u523b\u53ea\u60f3\u4ee5\u4ec7\u4eba\u7684\u9c9c\u8840\uff0c\u6765\u67d3\u7ea2\u81ea\u5df1\u7684\u53cc\u624b\u3002 \u3000\u3000\u7a0b\u67ab\u4ecd\u5728\u98a4\u6296\u7740\u2026\u2026 \u3000\u3000\u201c\u7f2a\u6587\u201d\u811a\u6b65\u66f4\u8fd1\u2026\u2026 \u3000\u3000\u7a81\u5730\uff0c\u5251\u5149\u4e00\u95ea\uff0c\u98a4\u6296\u7740\u7684\u7a0b\u67ab\uff0c\u5728\u5239\u90a3\u95f4\u62d4\u5251\u3001\u6325\u5251\uff0c\u4e00\u5251\u524a\u5411\u201c\u7f2a\u6587\u201d\u7684\u54bd\u5589\u3002\u8fd9 \u95ef\u8361\u6c5f\u6e56\u6570\u5341\u5e74\u7684\u5251\u624b\uff0c\u5728\u8fd9\u751f\u6b7b\u7684\u5173\u5934\u4e4b\u4e2d\uff0c\u518d\u4e00\u6b21\u663e\u9732\u51fa\u4ed6\u7684\u72e1\u733e\uff0c\u4ee5\u754f\u602f\u4e0e\u98a4\u6296\uff0c\u63a9 \u9970\u4e86\u4ed6\u7684\u52a8\u4f5c\uff0c\u4ed6\u8981\u5728\u5bf9\u65b9\u5168\u65e0\u9632\u8303\u4e4b\u65f6\uff0c\u624d\u80af\u62d4\u5251\u52a8\u624b\u3002 \u3000\u3000\u7ecf\u9a8c\u7684\u5806\u79ef\u4e0e\u5251\u6cd5\u7684\u8001\u7ec3\uff0c\u4f7f\u5f97\u8fd9\u4e00\u5251\u51fa\u5947\u7684\u8fc5\u5feb\u800c\u51f6\u731b\uff0c\u53ea\u89c1\u5251\u5149\u95ea\u5904\uff0c\u5251\u5c16\u5df2\u5230\u4e86 \u201c\u7f2a\u6587\u201d\u7684\u54bd\u5589\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u8eab\u8eaf\u4e00\u4ef0\uff0c\u5bbd\u5927\u7684\u888d\u8896\uff0c\u7a81\u5730\u5de8\u6d6a\u4e00\u822c\u53cd\u5377\u4e0a\u53bb\uff0c\u7a0b\u67ab\u5251\u52bf\u4e00\u8f6c\uff0c\u659c\u659c\u4e00\u5251\uff0c \u75be\u524a\u201c\u7f2a\u6587\u201d\u7684\u5916\u8098\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u90e8\u4f4d\u66f4\u662f\u5201\u94bb\uff0c\u653b\u7684\u6b63\u662f\u4eba\u7c7b\u6700\u5f31\u4e4b\u4e00\u73af\uff0c\u4e16\u4e0a\u4efb\u4f55\u4eba\u7684\u624b\u8098\uff0c\u90fd\u4e0d\u80fd\u5411\u5916\u626d \u8f6c\u3002 \u3000\u3000\u54ea\u77e5\u201c\u7f2a\u6587\u201d\u85cf\u5728\u4ed6\u5bbd\u5927\u888d\u91cc\u7684\u624b\u81c2\uff0c\u7adf\u7a81\u7834\u4e86\u8fd9\u4eba\u7c7b\u7684\u5f31\u70b9\uff0c\u7a0b\u67ab\u81ea\u89c9\uff0c\u638c\u4e2d\u4e00\u7d27\uff0c \u5251\u5c16\u5df2\u88ab\u5bf9\u65b9\u634f\u4f4f\u3002 \u3000\u3000\u201c\u53ee\u201d\u7684\u4e00\u58f0\uff0c\u957f\u5251\u53c8\u65ad\uff0c\u201c\u7f2a\u6587\u201d\u76ee\u4e2d\u795e\u5149\u66b4\u73b0\uff0c\u5c31\u5728\u7a0b\u67ab\u4e00\u60ca\u4e4b\u95f4\uff0c\u638c\u4e2d\u534a\u622a\u65ad \u5251\uff0c\u4fbf\u5df2\u9001\u5165\u4e86\u7a0b\u67ab\u7684\u80f8\u819b\u3002 \u3000\u3000\u4e00\u58f0\u60e8\u547c\uff0c\u9c9c\u8840\u98de\u6fc0\uff0c\u6709\u5982\u6cb8\u6c34\u822c\u6eda\u70ed\u7684\u9c9c\u8840\uff0c\u6cbf\u7740\u5251\u810a\uff0c\u6d41\u5230\u201c\u7f2a\u6587\u201d\u624b\u4e0a\uff0c\u4ed6\u624b\u4e0a \u7b2c\u4e00\u6b21\u67d3\u5230\u9c9c\u8840\uff0c\u4ed6\u7b2c\u4e00\u6b21\u611f\u89c9\u5230\u4ec7\u4eba\u7684\u70ed\u8840\u6d41\u5728\u81ea\u5df1\u638c\u4e0a\u7684\u6ecb\u5473\uff0c\u4ed6\u62ac\u8d77\u624b\u638c\uff0c\u9c9c\u8840\u5728\u591c \u8272\u4e2d\u5448\u73b0\u7740\u4e11\u6076\u7684\u7d2b\u8272\u5149\u8292\u3002 \u3000\u3000\u4ed6\u5408\u4e0a\u773c\u5e18\u6697\u4e2d\u4f4e\u8bed\uff1a\u201c\u7239\u7239\uff0c\u8fd9\u662f\u7b2c\u4e00\u4e2a\u2026\u2026\u201d \u3000\u3000\u5ffd\u6709\u4e00\u6ef4\u6cea\u73e0\uff0c\u6ef4\u5728\u4ed6\u6ee1\u67d3\u4ec7\u8840\u7684\u624b\u638c\u4e0a\uff0c\u539f\u6765\u590d\u4ec7\u7684\u6ecb\u5473\uff0c\u7adf\u4e5f\u662f\u5982\u6b64\u8f9b\u9178\u75db\u82e6\uff01 \u3000\u3000\u4fa0\u5ba2\u5c45\u9996\u5bb6\u63d0\u4f9b \u7b2c\u4e8c\u5341\u516d\u7ae0>> \u53e4\u9f99\u300a\u6e58\u5983\u5251\u300b \u7b2c\u4e8c\u5341\u516d\u7ae0 \u3000\u3000\u4f46\u662f\u4ec7\u4eba\u7684\u60e8\u547c\u5df2\u6e10\u6e10\u6d88\u5931\uff0c\u4ec7\u4eba\u7684\u5c38\u8eab\u4e5f\u5df2\u6e10\u6e10\u5012\u4e0b\uff0c\u4ed6\u7d27\u7ef7\u7684\u5fc3\u5f26\uff0c\u7ec8\u4e8e\u4e5f\u968f\u4e4b\u677e \u5f1b\u3002 \u3000\u3000\u201c\u53ee\u201d\u7684\u4e00\u58f0\uff0c\u5251\u5c16\u843d\u5730\uff0c\u7a81\u542c\u8eab\u540e\u8f7b\u8f7b\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4ec7\u516c\u5b50\u6740\u4e86\u4eba\uff0c\u8001\u53eb\u5316\u5e2e\u5fd9\u57cb\u57cb \u5c38\u8eab\u603b\u53ef\u4ee5\u5427\uff01\u201d \u3000\u3000\u719f\u6089\u7684\u8bed\u58f0\uff0c\u719f\u6089\u7684\u7b11\u58f0\uff0c\u4ed6\u6bcb\u5eb8\u56de\u5934\uff0c\u5df2\u77e5\u8eab\u540e\u8fd9\u4eba\u662f\u8c01\u3002 \u3000\u3000\u4ed6\u7ec8\u4e8e\u7f13\u7f13\u8f6c\u8eab\uff0c\u591c\u8272\u82cd\u832b\u4e2d\uff0c\u201c\u7a77\u795e\u201d\u51cc\u9f99\u5353\u7136\u800c\u7acb\uff0c\u624b\u4e2d\u7f13\u7f13\u64ad\u5f04\u7740\u4e00\u6761\u957f\u957f\u7684\u7ef3 \u7d22\uff0c\u4f3c\u7b11\u975e\u7b11\u5730\u671b\u7740\u4ed6\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4f60\u6b64\u756a\u6740\u4eba\uff0c\u7eb5\u7136\u65e0\u4eba\u4eb2\u773c\u76ee\u7779\uff0c\u96be\u9053\u522b\u4eba\u5c31\u731c\u4e0d\u51fa\u662f \u8c01\u4e48\uff1f\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u5fc3\u4e2d\uff0c\u6b64\u523b\u7a81\u5730\u611f\u5230\u4e00\u9635\u6df1\u6df1\u7684\u75b2\u4e4f\u2014\u2014\u4e00\u79cd\u4f3c\u4e4e\u662f\u5bf9\u4eba\u751f\u538c\u5026\u7684\u75b2\u4e4f\u3002 \u3000\u3000\u4ed6\u4f3c\u5df2\u518d\u65e0\u4f59\u529b\u6765\u601d\u8003\u8bb8\u591a\u4e8b\uff0c\u4e8e\u662f\u4ed6\u6c89\u58f0\u53f9\u9053\uff1a\u201c\u65e0\u8bba\u4ec0\u4e48\u4e8b\uff0c\u603b\u6709\u771f\u8c61\u5927\u767d\u7684\u4e00 \u5929\uff0c\u6211\u662f\u8c01\uff1f\u8c01\u662f\u6211\uff1f\u5c31\u8ba9\u522b\u4eba\u77e5\u9053\u4e86\u53c8\u6709\u4f55\u59a8\uff1f\u201d \u3000\u3000\u201c\u7a77\u795e\u201d\u51cc\u9f99\u4ef0\u5929\u7b11\u9053\uff1a\u201c\u597d\u597d\uff0c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u5f80\u65e5\u90a3\u4e00\u756a\u82e6\u5fc3\u7684\u8ba1\u5212\uff0c\u5c82\u975e\u90fd\u518d\u4e5f\u65e0 \u7528\uff0c\u4f60\u4e0d\u53ef\u60dc\uff0c\u8001\u53eb\u5316\u5374\u89c9\u5f97\u6709\u4e9b\u53ef\u60dc\u54e9\u3002\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u7f13\u7f13\u5782\u4e0b\u773c\u5e18\uff0c\u7a81\u53c8\u773c\u5e18\u4e00\u5f20\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4f60\u7a76\u7adf\u662f\u8c01\uff1f\u7a76\u7adf\u4e0e\u6211\u6709\u4f55\u5173\u7cfb\uff1f \u4e3a\u4ec0\u4e48\u603b\u662f\u8981\u6765\u7ba1\u6211\u7684\u95f2\u4e8b\uff1f\u201d \u3000\u3000\u591c\u8272\u4e2d\u53ea\u6709\u51cc\u9f99\u7684\u76ee\u5149\uff0c\u5b9b\u5982\u4e24\u7c92\u6676\u83b9\u7684\u660e\u661f\u3002 \u3000\u3000\u8fd9\u6570\u5341\u5e74\u6765\u4e00\u76f4\u6e38\u620f\u4eba\u95f4\uff0c\u7b11\u505a\u6c5f\u6e56\u7684\u7a77\u5bb6\u5e2e\u4e3b\uff0c\u9762\u8272\u7a81\u5730\u53d8\u5f97\u5341\u5206\u6c89\u8083\uff0c\u4ed6\u4e00\u8a00\u4e0d \u53d1\uff0c\u624b\u638c\u5fae\u6447\uff0c\u638c\u4e2d\u7684\u957f\u7d22\uff0c\u7a81\u5730\u6709\u5982\u5929\u8679\u822c\u6a2a\u98de\u800c\u8d77\u3002 \u3000\u3000\u4ed6\u624b\u8155\u4e00\u9707\uff0c\u5929\u8679\u822c\u7684\u957f\u7d22\u4e00\u9635\u6ce2\u52a8\uff0c\u53c8\u6709\u5982\u5929\u77eb\u53d8\u5316\u7684\u5341\u4e08\u795e\u9f99\uff0c\u7a81\u5730\u843d\u5728\u90a3\u56db\u4e2a\u84dd \u8863\u5251\u624b\u7684\u5c38\u8eab\u4e0a\u3002 \u3000\u3000\u201c\u7a77\u795e\u201d\u51cc\u9f99\u624b\u8155\u8fde\u9707\uff0c\u811a\u6b65\u79fb\u52a8\uff0c\u90a3\u957f\u7d22\u4e5f\u8ddf\u7740\u6ce2\u52a8\u626d\u8f6c\uff0c\u7a81\u5730\uff0c\u4ed6\u624b\u8155\u4e00\u7d27\uff0c\u8f6c\u8eab \u5411\u591c\u8272\u4e2d\u8d70\u53bb\uff0c\u638c\u4e2d\u7684\u957f\u7d22\u626f\u5f97\u7b14\u76f4\uff0c\u7adf\u5c06\u51e0\u5177\u5c38\u8eab\u4e00\u9f50\u5e26\u52a8\u3002 \u3000\u3000\u8fd9\u624b\u6cd5\u5f53\u771f\u662f\u795e\u4e4e\u5176\u6280\uff0c\u201c\u7f2a\u6587\u201d\u5446\u671b\u4e86\u534a\u664c\uff0c\u7b2c\u4e00\u6b21\u53d1\u89c9\u6c5f\u6e56\u4e2d\u786e\u6709\u8bb8\u591a\u6b66\u529f\u6df1\u4e0d\u53ef \u6d4b\u7684\u5f02\u4eba\uff0c\u53ea\u662f\u4ed6\u4eec\u5374\u4ece\u6765\u4e0d\u613f\u663e\u793a\u6b66\u529f\u3002 \u3000\u3000\u53ea\u89c1\u201c\u7a77\u795e\u201d\u51cc\u9f99\u62d6\u7740\u4e00\u957f\u4e32\u5c38\u8eab\uff0c\u5927\u6b65\u800c\u884c\uff0c\u4ed6\u7626\u524a\u7684\u80cc\u5f71\uff0c\u5728\u591c\u8272\u4e2d\u770b\u6765\u53ea\u89c9\u662f\u90a3 \u4e48\u719f\u6089\u800c\u4eb2\u5207\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u8f7b\u8f7b\u4e00\u63a0\uff0c\u8dc3\u5230\u4ed6\u8eab\u4fa7\uff0c\u9053\uff1a\u201c\u6211\u5bf9\u4f60\u90a3\u6837\u65e0\u7406\uff0c\u4f60\u4e3a\u4f55\u8fd8\u8981\u8fd9\u6837\u52a9\u6211\uff1f\u201d \u3000\u3000\u201c\u7a77\u795e\u201d\u51cc\u9f99\u671b\u4e5f\u4e0d\u671b\u4ed6\u4e00\u773c\uff0c\u5927\u6b65\u8d70\u5165\u4e00\u7247\u758f\u6797\u3002\u758f\u6797\u4e2d\u7adf\u6709\u4e24\u4e2a\u9e51\u8863\u4e5e\u4e10\uff0c\u5728\u6398\u7740 \u4e00\u4e2a\u571f\u5751\uff0c\u518d\u4e5f\u4e0d\u56de\u9996\u671b\u4e0a\u4e00\u773c\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u5927\u559d\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\uff0c\u6211\u6839\u672c\u4e0d\u8981\u4f60\u7684\u5e2e\u52a9\uff0c\u6211\u201d\u201c\u7a77\u795e\u201d\u51cc\u9f99\u51b7\u51b7 \u9053\uff1a\u201c\u4f60\u6b64\u523b\u5df2\u662f\u56db\u9762\u695a\u6b4c\uff0c\u53ea\u8981\u9762\u76ee\u4e00\u9732\uff0c\u5c31\u4e0d\u77e5\u6709\u591a\u5c11\u4eba\u8981\u5bfb\u4f60\u4e3a\u654c\uff0c\u6211\u4e0d\u6765\u52a9\u4f60\uff0c\u8c01 \u6765\u52a9\u4f60\uff1f\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u5446\u4e86\u4e00\u5446\uff0c\u5450\u5450\u9053\uff1a\u201c\u4f60\u4e0d\u6765\u52a9\u6211\uff0c\u8c01\u6765\u52a9\u6211\u2026\u2026\u201d \u3000\u3000\u51cc\u9f99\u51b7\u51b7\u622a\u53e3\u9053\uff1a\u201c\u5929\u6709\u4e0d\u6d4b\u98ce\u4e91\uff0c\u4eba\u6709\u65e6\u5915\u7978\u798f\uff0c\u4e00\u65e5\u4e4b\u95f4\uff0c\u53ef\u80fd\u53d1\u751f\u7684\u53d8\u5316\u4fbf\u4e0d\u77e5 \u6709\u591a\u5c11\uff0c\u4eca\u65e5\u662f\u4f60\u4e4b\u53cb\uff0c\u660e\u65e5\u4fbf\u8bf4\u4e0d\u5b9a\u5df2\u6210\u4f60\u4e4b\u654c\uff0c\u4f60\u7eb5\u6709\u7edd\u4e16\u6b66\u529f\uff0c\u7edd\u9876\u624d\u534e\uff0c\u4f46\u6c5f\u6e56\u4e2d \u4e8b\uff0c\u6ce2\u8c32\u4e91\u8be1\uff0c\u77ac\u606f\u4e07\u53d8\uff0c\u53c8\u5c82\u662f\u4f60\u80fd\u731c\u6d4b\uff1f\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u5446\u7acb\u5f53\u5730\uff0c\u4ecd\u5728\u5480\u56bc\u7740\u4ed6\u8bdd\u4e2d\u7684\u542b\u610f\uff0c\u7a81\u542c\u6797\u4e2d\u4e00\u9635\u6025\u907d\u7684\u8f66\u9a6c\u58f0\u8fdc\u8fdc\u51b2\u6765\uff0c \u621b\u7136\u800c\u987f\u3002 \u3000\u3000\u63a5\u7740\u662f\u4e00\u58f0\u5a07\u547c\uff0c\u54cd\u5f7b\u591c\u7a7a\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u5fc3\u5934\u4e00\u9707\uff0c\u8fd9\u5a07\u547c\u58f0\u7adf\u4e5f\u662f\u5982\u6b64\u719f\u6089\u3002 \u3000\u3000\u201c\u7a77\u795e\u201d\u51cc\u9f99\u9762\u8272\u5fae\u53d8\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u5feb\u8d70\u5feb\u8d70\uff0c\u8fd9\u91cc\u7684\u4e8b\u8001\u53eb\u5316\u6765\u7ba1\u3002\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u5634\u89d2\u7b11\u5bb9\u4e00\u95ea\uff0c\u627f\u7ee7\u5148\u4eba\u7684\u5014\u5f3a\u6027\u683c\uff0c\u4f7f\u5f97\u8fd9\u777f\u667a\u7684\u5c11\u5e74\uff0c\u65f6\u65f6\u523b\u523b\u90fd\u4f1a\u505a\u51fa \u51b2\u52a8\u7684\u4e8b\uff0c\u800c\u51b2\u52a8\u7684\u4e8b\uff0c\u5374\u5927\u591a\u4ff1\u662f\u611a\u7b28\u7684\u3002 \u3000\u3000\u4ed6\u4e00\u8a00\u4e0d\u53d1\uff0c\u970d\u7136\u8f6c\u8eab\uff0c\u4e00\u6b65\u63a0\u51fa\u6797\u53bb\u3002 \u3000\u3000\u201c\u7a77\u795e\u201d\u51cc\u9f99\u671b\u7740\u4ed6\u7684\u80cc\u5f71\uff0c\u9762\u4e0a\u795e\u8272\uff0c\u4e5f\u4e0d\u77e5\u662f\u559c\u662f\u6012\uff0c\u5583\u5583\u9053\uff1a\u201c\u53c8\u662f\u8fd9\u6837\u7684\u813e \u6c14\uff0c\u53c8\u662f\u8fd9\u6837\u7684\u813e\u6c14\u2026\u2026\u201d \u3000\u3000\u758f\u6797\u5916\uff0c\u4e00\u8f86\u9a6c\u8f66\uff0c\u505c\u5728\u7a0b\u67ab\u7684\u5c38\u8eab\u524d\uff0c\u4e00\u4e2a\u9526\u8863\u534e\u670d\u7684\u5c11\u5e74\uff0c\u6728\u7acb\u5728\u9a6c\u8f66\u7554\uff0c\u5782\u9996\u51dd \u6ce8\u7740\u7a0b\u67ab\u7684\u5c38\u8eab\u3002 \u3000\u3000\u201c\u4ed6\u201d\u79cb\u6ce2\u4e00\u8f6c\uff0c\u7a81\u89c9\u6709\u4e00\u53cc\u773c\u6ce2\u6b63\u5728\u51dd\u6ce8\u7740\u81ea\u5df1\uff0c\u62ac\u8d77\u5934\u6765\uff0c\u4fbf\u5df2\u548c\u201c\u7f2a\u6587\u201d\u7684\u76ee\u5149 \u76f8\u9047\u3002 \u3000\u3000\u201c\u4ed6\u201d\u5fc3\u5934\u4e00\u8df3\uff0c\u9762\u4e0a\u7acb\u523b\u7efd\u5f00\u4e00\u4e2a\u60ca\u559c\u7684\u7b11\u5bb9\uff0c\u98a4\u58f0\u9053\uff0c\u201c\u4f60\u2026\u2026\u4f60\u6ca1\u6709\u6b7b\u2026\u2026\u201d \u3000\u3000\u7ea4\u8170\u5fae\u62e7\uff0c\u4f3c\u4e4e\u8981\u6251\u5411\u201c\u7f2a\u6587\u201d\u8eab\u4e0a\uff0c\u4f46\u811a\u6b65\u65b9\u52a8\uff0c\u5374\u53c8\u500f\u7136\u6b62\u6b65\u3002\u201c\u7f2a\u6587\u201d\u6de1\u6de1\u7b11 \u9053\uff1a\u201c\u6587\u742a\uff0c\u4f60\u7626\u4e86\u3002\u201d \u3000\u3000\u8fd9\u7b11\u5bb9\u548c\u8bed\u58f0\u50cf\u6d77\u6d9b\u822c\u51b2\u51fb\u7740\u6bdb\u6587\u742a\u7684\u5fc3\u623f\uff0c\u5979\u8eab\u8eaf\u98a4\u6296\uff0c\u773c\u6ce2\u4e5f\u8361\u6f3e\u4e86\u3002 \u3000\u3000\u5979\u8f7b\u8f7b\u9053\uff1a\u201c\u4f60\u4e5f\u7626\u4e86\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u672a\u4e86\uff0c\u7a81\u7136\u540e\u9000\u4e09\u6b65\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u4f60\u7a76\u7adf\u662f\u8c01\uff1f\u4f60\u662f\u4e0d\u662f\u7239\u7239\u7684\u4ec7\u4eba\uff1f\u8fd9 \u7a0b\u67ab\u662f\u4e0d\u662f\u4f60\u6740\u6b7b\u7684\uff1f\u201d \u3000\u3000\u5c11\u5973\u7684\u5fc3\u7eea\uff0c\u7adf\u662f\u8fd9\u822c\u4ee4\u4eba\u96be\u6d4b\uff0c\u5979\u5728\u524d\u4e00\u5239\u90a3\u4e2d\u6240\u60f3\u7684\u4e8b\uff0c\u548c\u540e\u4e00\u5239\u90a3\u4e2d\u6240\u60f3\u7684\u7adf\u662f \u5982\u6b64\u4e0d\u540c\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u76ee\u4e2d\u5149\u8292\u4e00\u95ea\uff0c\u9053\uff1a\u201c\u6b64\u4eba\u2026\u2026\u201d \u3000\u3000\u54ea\u77e5\u4ed6\u8bed\u58f0\u65b9\u51fa\uff0c\u4ed6\u8eab\u524d\u3001\u8eab\u540e\uff0c\u7adf\u6709\u4e24\u4eba\u540c\u65f6\u6c89\u58f0\u9053\uff1a\u201c\u6b64\u4eba\u662f\u6211\u6740\u6b7b\u7684\uff01\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u84e6\u5730\u4e00\u60ca\uff0c\u8f6c\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u4ed6\u8eab\u540e\u7684\u758f\u6797\u4e2d\uff0c\u7f13\u6b65\u8d70\u51fa\u7684\uff0c\u6b63\u662f\u90a3\u540d\u626c\u5929\u4e0b\u7684 \u201c\u7a77\u795e\u201d\u51cc\u9f99\u3002 \u3000\u3000\u6bdb\u6587\u742a\u4ea6\u81ea\u4e00\u60ca\uff0c\u8f6c\u8eab\u671b\u53bb\uff0c\u82cd\u832b\u7684\u591c\u8272\u4e2d\uff0c\u7f13\u6b65\u884c\u6765\u7684\uff0c\u7adf\u662f\u4e00\u4e2a\u9762\u5bb9\u6728\u7136\uff0c\u8eab\u5f62\u6728 \u7136\uff0c\u76ee\u5149\u4ea6\u6728\u7136\uff0c\u671b\u6765\u6709\u5982\u884c\u5c38\u8d70\u8089\u822c\u7684\u9752\u888d\u602a\u4eba\uff0c\u4ed6\u50f5\u6728\u7684\u9762\u5bb9\u4e0a\uff0c\u90a3\u4e00\u6761\u957f\u800c\u6df1\u7684\u5200 \u75a4\uff0c\u66f4\u4f7f\u4ed6\u5e73\u6dfb\u4e86\u51e0\u5206\u602a\u5f02\u4e4b\u6c14\u3002 \u3000\u3000\u591c\u8272\u4e4b\u4e2d\uff0c\u9aa4\u7136\u89c1\u5230\u8fd9\u6837\u7684\u4eba\uff0c\u6bdb\u6587\u742a\u5fc3\u5934\u4e0d\u89c9\u53c8\u662f\u4e00\u60ca\uff0c\u4e00\u9635\u5bd2\u610f\uff0c\u500f\u7136\u6ee1\u5e03\u5168\u8eab\u3002 \u3000\u3000\u5979\u5927\u58f0\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u79cb\u6ce2\u4e00\u8f6c\uff0c\u53c8\u81ea\u559d\u9053\uff1a\u201c\u7a0b\u67ab\u5230\u5e95\u662f\u8c01\u6740\u6b7b\u7684\uff1f\u201d \u3000\u3000\u54ea\u77e5\u8fd9\u9752\u888d\u602a\u4eba\u5374\u4f3c\u6839\u672c\u6ca1\u6709\u542c\u5230\u5979\u7684\u8bf4\u8bdd\uff0c\u50f5\u6728\u5730\u79fb\u52a8\u7740\u811a\u6b65\uff0c\u50f5\u6728\u5730\u8d70\u8fc7\u5979\u8eab\u8fb9\uff0c \u4fef\u4e0b\u8eab\u53bb\uff0c\u62b1\u8d77\u4e86\u7a0b\u67ab\u7684\u5c38\u8eab\u2026\u2026 \u3000\u3000\u4ed6\u65e0\u8bba\u5728\u795e\u8272\u6216\u9762\u5bb9\u95f4\uff0c\u90fd\u6563\u53d1\u7740\u4e00\u79cd\u201c\u6b7b\u4ea1\u201d\u7684\u5996\u5f02\u9b54\u529b\uff0c\u4ed6\u4eff\u4f5b\u662f\u6765\u81ea\u5730\u72f1\u7684\u4f7f \u8005\uff0c\u4e3a\u4eba\u95f4\u5e26\u6765\u201c\u6b7b\u201d\u7684\u8baf\u606f\u3002 \u3000\u3000\u5c31\u662f\u8fd9\u79cd\u5996\u5f02\u800c\u795e\u5947\u7684\u610f\u5473\uff0c\u4f7f\u5f97\u6bdb\u6587\u742a\u773c\u7741\u7741\u5730\u671b\u7740\u4ed6\u7684\u8eab\u5f62\u79fb\u52a8\uff0c\u800c\u672a\u51fa\u58f0\u963b\u6b62\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u6a2a\u62b1\u8d77\u7a0b\u67ab\u7684\u5c38\u8eab\uff0c\u50f5\u6728\u5730\u7ad9\u4e86\u8d77\u6765\uff0c\u53c8\u5f00\u59cb\u50f5\u6728\u5730\u79fb\u52a8\u7740\u811a\u6b65\uff0c\u7136\u800c\uff0c\u5c31\u5728\u8fd9 \u4e00\u5239\u90a3\u4e4b\u95f4\u2014\u2014\u4ed6\u50f5\u6728\u7684\u76ee\u5149\uff0c\u5ffd\u7136\u53d8\u5f97\u6709\u5982\u95ea\u7535\u822c\u950b\u5229\uff0c\u4e0d\u53ef\u7f6e\u4fe1\u7684\u7075\u6d3b\uff0c\u5411\u201c\u7f2a\u6587\u201d\u6253 \u4e86\u4e2a\u773c\u8272\uff0c\u7136\u540e\u2026\u2026 \u3000\u3000\u4ed6\u53cc\u624b\u62b1\u7740\u7a0b\u67ab\u7684\u5c38\u8eab\uff0c\u50f5\u6728\u5730\u8d70\u8fc7\u51cc\u9f99\u8eab\u4fa7\uff0c\u50f5\u6728\u5730\u8d70\u5165\u9ed1\u6697\u2026\u2026 \u3000\u3000\u8fd9\u4eff\u4f5b\u6765\u81ea\u5730\u72f1\u7684\u602a\u5ba2\uff0c\u6b64\u523b\u4fbf\u4eff\u4f5b\u53c8\u8d70\u56de\u5730\u72f1\u4e2d\u53bb\u3002 \u3000\u3000\u7eb5\u7136\u662f\u201c\u7a77\u795e\u201d\u51cc\u9f99\u8fd9\u822c\u5389\u5bb3\u89d2\u8272\uff0c\u6b64\u523b\u9762\u4e0a\u4e5f\u4e0d\u7981\u9732\u51fa\u660e\u663e\u7684\u9a87\u5f02\uff0c\u4ed6\u8be2\u95ee\u5730\u5411\u201c\u7f2a \u6587\u201d\u671b\u4e86\u4e00\u773c\uff0c\u5374\u53d1\u73b0\u201c\u7f2a\u6587\u201d\u7adf\u4e5f\u4f3c\u832b\u7136\u5931\u63aa\u3002 \u3000\u3000\u6bdb\u6587\u742a\u773c\u6ce2\u56db\u8f6c\uff0c\u7a81\u7136\u9053\uff1a\u201c\u51cc\u5e2e\u4e3b\uff0c\u6211\u6b63\u8981\u627e\u4f60\u3002\u201d \u3000\u3000\u5979\u5fc3\u91cc\u89c9\u5f97\u6709\u4e9b\u832b\u7136\uff0c\u6709\u4e9b\u60ed\u6127\uff0c\u56e0\u4e3a\u5979\u7adf\u4e0d\u6562\u963b\u6b62\u90a3\u9752\u888d\u602a\u5ba2\u7684\u884c\u52a8\uff0c\u5979\u89c9\u5f97\u5b9e\u5728\u6709 \u4e9b\u4e0d\u597d\u610f\u601d\u3002 \u3000\u3000\u662f\u4ee5\u5979\u4fbf\u8131\u53e3\u8bf4\u51fa\u8fd9\u53e5\u8bdd\u6765\uff0c\u4e3a\u7684\u4e0d\u8fc7\u53ea\u662f\u6253\u5f00\u81ea\u5df1\u5fc3\u91cc\u7684\u50f5\u5c40\u3002 \u3000\u3000\u201c\u7a77\u795e\u201d\u51cc\u9f99\u5fae\u5fae\u4e00\u6115\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6bdb\u59d1\u5a18\u5bfb\u6211\u4f5c\u4ec0\uff1f\u201d \u3000\u3000\u8fd9\u98ce\u5c18\u5f02\u4eba\u53e3\u4e2d\u7684\u6717\u7b11\u4e4b\u58f0\uff0c\u5176\u5b9e\u4e5f\u662f\u5728\u63a9\u9970\u5fc3\u91cc\u7684\u4e0d\u5b89\u4e0e\u60ed\u6127\u3002 \u3000\u3000\u6bdb\u6587\u742a\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u2026\u2026\u201d \u3000\u3000\u5979\u627e\u51cc\u9f99\u4e3a\u7684\u5c31\u662f\u8981\u5bfb\u627e\u201c\u7f2a\u6587\u201d\uff0c\u4f46\u6b64\u523b\u201c\u7f2a\u6587\u201d\u5374\u5df2\u7acb\u5728\u5979\u8eab\u4fa7\uff0c\u5979\u5077\u5077\u671b\u4e86\u201c\u7f2a \u6587\u201d\u4e00\u773c\uff0c\u53e3\u4e2d\u7684\u8bdd\uff0c\u4fbf\u518d\u4e5f\u8bf4\u4e0d\u4e0b\u53bb\u3002 \u3000\u3000\u5979\u6df1\u4fe1\u201c\u7f2a\u6587\u201d\u5fc5\u5b9a\u4e0d\u662f\u81ea\u5df1\u7239\u7239\u6000\u7591\u7684\u4eba\uff0c\u662f\u4ee5\u6b64\u523b\u5fc3\u91cc\u53cd\u800c\u89c9\u5f97\u6709\u4e9b\u6b49\u610f\uff0c\u53c8\u4e0d\u7981 \u5728\u5fc3\u4e2d\u6697\u81ea\u601d\u7d22\uff0c\u4e0d\u77e5\u8be5\u7528\u4ec0\u4e48\u8a00\u8bed\u5411\u81ea\u5df1\u7684\u7239\u7239\u89e3\u8bf4\u3002 \u3000\u3000\u201c\u7a77\u795e\u201d\u51cc\u9f99\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u5e74\u8f7b\u4eba\u7684\u5fc3\u4e8b\uff0c\u5f53\u771f\u4e0d\u662f\u6211\u4eec\u8001\u5934\u5b50\u80fd\u591f\u660e\u4e86\u7684\u3002\u201d \u3000\u3000\u6bdb\u6587\u742a\u9762\u988a\u4e00\u7ea2\uff0c\u53ea\u89c1\u7f2a\u6587\u6728\u7acb\u5f53\u5730\uff0c\u5fc3\u4e2d\u4f3c\u5728\u601d\u7d22\u7740\u4ec0\u4e48\u3002 \u3000\u3000\u5979\u7f13\u7f13\u8d70\u5230\u201c\u7f2a\u6587\u201d\u8eab\u4fa7\uff0c\u8f7b\u8f7b\u9053\uff1a\u201c\u65b9\u624d\u6211\u2026\u2026\u9519\u602a\u4e86\uff0c\u4f46\u662f\uff0c\u4f60\u2026\u2026\u6700\u597d\u8fd8\u662f\u8eb2\u907f \u4e00\u4e0b\uff0c\u56e0\u4e3a\u6211\u7239\u7239\u2026\u2026\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u5fc3\u4e2d\u53ea\u5728\u601d\u7d22\u7740\u65b9\u624d\u90a3\u9752\u888d\u602a\u4eba\u201c\u8fd8\u9b42\u201d\u76ee\u5149\u4e2d\u7684\u542b\u610f\uff0c\u6839\u672c\u672a\u66fe\u542c\u5230\u5979\u7684 \u8bdd\u3002 \u3000\u3000\u5979\u8bdd\u58f0\u672a\u4e86\uff0c\u7a81\u89c1\u201c\u7f2a\u6587\u201d\u53cc\u76ee\u4e00\u5f20\uff0c\u53f3\u624b\u51fb\u989d\u9053\uff1a\u201c\u4e0d\u5bf9\uff01\u2026\u2026\u5bf9\u4e86\u2026\u2026\u201d\u4e00\u64a9\u886b \u89d2\uff0c\u8f6c\u8eab\u5954\u53bb\u3002 \u3000\u3000\u6bdb\u6587\u742a\u5fae\u5fae\u4e00\u6115\uff0c\u9053\uff1a\u201c\u5582\uff01\u4f60\u2026\u2026\u201d \u3000\u3000\u5979\u672c\u60f3\u7acb\u523b\u8ffd\u53bb\uff0c\u4f46\u62ac\u76ee\u671b\u4e86\u51cc\u9f99\u4e00\u773c\uff0c\u5374\u53c8\u4e0d\u7981\u7f9e\u6da9\u5730\u505c\u4e0b\u811a\u6b65\u3002 \u3000\u3000\u201c\u7a77\u795e\u201d\u51cc\u9f99\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u65e0\u59a8\u65e0\u59a8\uff0c\u8001\u53eb\u5316\u4ec0\u4e48\u90fd\u770b\u4e0d\u89c1\u7684\u3002\u201d \u3000\u3000\u6bdb\u6587\u742a\u9762\u988a\u53c8\u4e00\u7ea2\uff0c\u7ec8\u4e8e\u8fd8\u662f\u8dc3\u4e0a\u9a6c\u8f66\uff0c\u8ffd\u8e2a\u800c\u53bb\uff0c\u53ea\u89c1\u4e00\u80a1\u8f66\u5c18\uff0c\u77ac\u606f\u95f4\u4fbf\u6d88\u5931\u5728\u9ed1 \u6697\u91cc\u3002 \u3000\u3000\u591c\u6df1\u3002 \u3000\u3000\u6625\u591c\u4e2d\u7684\u661f\u6708\uff0c\u50cf\u662f\u65b9\u88ab\u7ec7\u5973\u7684\u7ea4\u624b\u6d17\u8fc7\uff0c\u800c\u6625\u98ce\u4fbf\u50cf\u662f\u7ec7\u5973\u7684\u773c\u6ce2\uff0c\u662f\u90a3\u4e48\u6e29\u67d4\uff0c\u5f02 \u6837\u7684\u6e29\u67d4\u3002 \u3000\u3000\u6e05\u6f88\u7684\u661f\u5149\uff0c\u6620\u7740\u6731\u7ea2\u8272\u7684\u5927\u95e8\uff0c\u6620\u7740\u95e8\u524d\u90a3\u4e00\u53cc\u77f3\u72ee\uff0c\u4f7f\u5f97\u8fd9\u4e00\u53cc\u5de8\u5927\u800c\u72f0\u72de\u7684\u77f3 \u72ee\uff0c\u770b\u6765\u4e5f\u6e29\u67d4\u4e86\u4e00\u4e9b\u3002 \u3000\u3000\u661f\u4e5f\u6e29\u67d4\uff0c\u6708\u4e5f\u6e29\u67d4\uff0c\u98ce\u66f4\u6e29\u67d4\uff0c\u6e29\u67d4\u7684\u6625\u591c\u4e2d\uff0c\u4e00\u5207\u90fd\u662f\u6e29\u67d4\u7684\u3002 \u3000\u3000\u4e8e\u662f\u6625\u591c\u4e2d\u4eba\u4eec\u7684\u5fc3\u4e5f\u6e29\u67d4\u4e86\u8d77\u6765\u3002 \u3000\u3000\u676d\u5dde\u6bdb\u5e9c\uff0c\u95e8\u5916\uff0c\u662f\u6c38\u8fdc\u4e0d\u4f1a\u5bc2\u5bde\u7684\uff0c\u4f55\u51b5\u5728\u6625\u591c\uff1f \u3000\u3000\u6b64\u523b\uff0c\u4e03\uff0c\u516b\u6761\u52b2\u88c5\u5927\u6c49\uff0c\u5f98\u5f8a\u5728\u95e8\u524d\u3002\u4ed6\u4eec\u7684\u804c\u8d23\u662f\u8fce\u5bbe\u548c\u901a\u62a5\uff0c\u5de1\u9632\u548c\u63a2\u67e5\uff0c\u4f46\u5728 \u8fd9\u6e29\u67d4\u7684\u6625\u591c\u4e2d\uff0c\u540e\u4e24\u79cd\u804c\u8d23\u663e\u7136\u5df2\u88ab\u4ed6\u4eec\u5ffd\u89c6\u4e86\uff0c\u6ca1\u6709\u4e00\u4e2a\u4eba\u7684\u773c\u5149\u4e2d\uff0c\u518d\u5e26\u6709\u8b66\u5907\u4e4b \u610f\uff01 \u3000\u3000\u4ed6\u4eec\u53ea\u662f\u61d2\u6563\u5730\u8e40\u8e31\u7740\uff0c\u6709\u7684\u751a\u81f3\u5df2\u501a\u7740\u77f3\u72ee\u5750\u4e86\u4e0b\u6765\uff0c\u5076\u800c\u6709\u4eba\u8bf4\u51fa\u4e00\u4e2a\u7c97\u4fd7\u800c\u7325\u7410 \u7684\u7b11\u8bdd\uff0c\u4fbf\u5f15\u8d77\u4e00\u9635\u54c4\u7b11\u4e00\u7b11\u8bdd\u8d8a\u7c97\u4fd7\u800c\u72e0\u7410\uff0c\u54c4\u7b11\u4e4b\u58f0\u4e5f\u5c31\u8d8a\u5927\u3002 \u3000\u3000\u7a81\u7136\uff0c\u6240\u6709\u7684\u7b11\u58f0\u4e00\u9f50\u505c\u6b62\uff0c\u6240\u6709\u61d2\u6563\u7684\u76ee\u5149\u4e00\u9f50\u51dd\u7ed3\uff0c\u7ad9\u7740\u7684\u4eba\u7ad9\u5f97\u66f4\u76f4\uff0c\u5750\u7740\u7684\u4eba \u4e5f\u7ad9\u8d77\u6765\u3002 \u3000\u3000\u9ed1\u6697\u4e2d\u4e00\u4e2a\u9752\u888d\u4eba\uff0c\u50f5\u6728\u5730\u8d70\u5165\u95e8\u524d\u7684\u706f\u7b3c\u5149\u4e0b\uff0c\u4ed6\u9762\u5bb9\u795e\u60c5\u95f4\u6240\u5e26\u7684\u90a3\u4e00\u4efd\u6b7b\u7684\u610f \u5473\uff0c\u5df2\u8db3\u4ee5\u4ee4\u4eba\u5fc3\u60ca\uff0c\u4f55\u51b5\u2026\u2026 \u3000\u3000\u4ed6\u80cc\u4e0a\u7adf\u8fd8\u8d1f\u7740\u4e00\u5177\u9c9c\u8840\u6dcb\u6f13\u7684\u6b7b\u5c38\u3002 \u3000\u3000\u4f17\u4eba\u9762\u8272\u4ff1\u90fd\u5927\u53d8\u3002\u6709\u7684\u4eba\u8fdc\u8fdc\u9000\u5230\u8def\u8fb9\uff0c\u53ea\u7b49\u4ed6\u8d70\u8fc7\u3002\u8fd9\u4e9b\u6c49\u5b50\u867d\u7136\u7c97\u9c81\u83bd\u649e\uff0c\u4f46\u6b64 \u65f6\u6b64\u523b\uff0c\u5374\u8c01\u4e5f\u4e0d\u80af\u6765\u7ba1\u95f2\u4e8b\u3002 \u3000\u3000\u53ea\u89c9\u8fd9\u9752\u8863\u4eba\u671b\u4e5f\u4e0d\u671b\u4ed6\u4eec\u4e00\u773c\uff0c\u773c\u770b\u5df2\u5c06\u8d70\u8fc7\u5927\u95e8\u7a81\u7136\u8eab\u5f62\u4e00\u8f6c\uff0c\u4e5f\u672a\u770b\u4ed6\u4e3e\u6b65\uff0c\u4fbf \u5df2\u4e0a\u4e86\u56db\u7ea7\u77f3\u9636\u3002 \u3000\u3000\u7b49\u5230\u8fd9\u516b\u6761\u5927\u6c49\u60ca\u547c\u51fa\u58f0\uff0c\u4ed6\u5df2\u7f13\u7f13\u8d70\u8fdb\u4e86\u5927\u95e8\uff0c\u8fd9\u95e8\u7981\u68ee\u4e25\u7684\u676d\u5dde\u6bdb\u5e9c\uff0c\u5728\u4ed6\u773c\u4e2d\u770b \u6765\uff0c\u7adf\u4eff\u4f5b\u662f\u4eba\u4eba\u53ef\u5165\u7684\u5e99\u5b57\u3002 \u3000\u3000\u4ed6\u4e00\u6b65\u4e00\u6b65\u5730\u7a7f\u8fc7\u5ead\u9662\uff0c\u8d70\u5411\u957f\u5eca\uff0c\u6574\u4e2a\u5b85\u9662\uff0c\u7acb\u523b\u52a8\u4e71\u4e86\u8d77\u6765\u3002 \u3000\u3000\u52a8\u4e71\u4e4b\u58f0\uff0c\u4f20\u5165\u6b63\u5385\uff0c\u6b63\u5385\u4e0a\u706f\u5149\u901a\u660e\uff0c\u201c\u7075\u86c7\u201d\u6bdb\u81ec\uff0c\u996e\u5bb4\u6b63\u6b22\uff0c\u95fb\u58f0\u4e0d\u7981\u653e\u4e0b\u676f \u76cf\uff0c\u76b1\u7709\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u4e24\u4e2a\u84dd\u8863\u5251\u624b\uff0c\u5982\u98de\u62a2\u6b65\u800c\u51fa\u3002 \u3000\u3000\u6b63\u5385\u4e0a\u7684\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u3001\u6cb3\u6714\u53cc\u5251\u3001\u5b50\u6bcd\u53cc\u98de\u3001\u767e\u6b65\u98de\u82b1\u7b49\u4eba\uff0c\u867d\u7136\u6709\u4e9b\u60ca\u8be7\uff0c\u4f46\u5374\u4e5f \u4e0d\u4ee5\u4e3a\u610f\u3002 \u3000\u3000\u5750\u5728\u4e0a\u9996\u7684\u4e00\u4eba\uff0c\u8499\u9762\u98ce\u6c05\uff0c\u8d6b\u7136\u7adf\u4f3c\u90a3\u5173\u5916\u4eba\u9b54\u201c\u4eba\u547d\u730e\u6237\u201d\uff0c\u6b64\u523b\u66f4\u662f\u52a8\u4e5f\u4e0d\u52a8\uff0c \u4ed6\u867d\u5728\u4eba\u7fa4\u4e4b\u4e2d\uff0c\u4e5f\u50cf\u662f\u53ea\u6709\u4e00\u4eba\u72ec\u5750\uff0c\u4ed6\u94a2\u94c1\u4e00\u822c\u7684\u795e\u6001\uff0c\u4f3c\u4e4e\u6c38\u8fdc\u4e0d\u4f1a\u4e3a\u4efb\u4f55\u5916\u6765\u7684\u56e0 \u7d20\u6539\u53d8\u3002 \u3000\u3000\u5ead\u56ed\u4e2d\u811a\u6b65\u7eb7\u4e71\uff0c\u4eba\u58f0\u5608\u6742\uff0c\u4e0d\u4f4f\u5389\u53f1\uff01 \u3000\u3000\u201c\u4ec0\u4e48\u4eba\uff0c\u6562\u5230\u8fd9\u91cc\u4e71\u95ef\uff1f\u201d \u3000\u3000\u4f46\u53f1\u54a4\u5c3d\u7ba1\u53f1\u54a4\uff0c\u5374\u65e0\u4e00\u4eba\u6562\u4e0a\u524d\u963b\u62e6\u3002 \u3000\u3000\u90a3\u9752\u888d\u4eba\u66f4\u662f\u671b\u4e5f\u4e0d\u671b\u8fd9\u4e9b\u4eba\u4e00\u773c\uff0c\u4e00\u6b65\u4e00\u6b65\u5730\u8d70\u4e0a\u957f\u5eca\u3002 \u3000\u3000\u4e24\u4e2a\u84dd\u8863\u5251\u624b\u5982\u98de\u800c\u6765\uff0c\u4e00\u773c\u89c1\u5230\u8fd9\u9752\u888d\u4eba\uff0c\u4e5f\u4e0d\u7981\u5012\u62bd\u4e86\u2026\u53e3\u51b7\u6c14\u3002 \u3000\u3000\u4e24\u4eba\u5bf9\u671b\u4e00\u773c\uff0c\u4e00\u9f50\u62d4\u51fa\u5251\u6765\uff0c\u5de6\u9762\u4e00\u4eba\u5389\u53f1\u9053\uff1a\u201c\u4f4f\u811a\uff01\u4f60\u82e5\u518d\u8fdb\u4e00\u6b65\u2026\u2026\u201d \u3000\u3000\u53f3\u9762\u4e00\u4eba\u5fc3\u80c6\u5df2\u5bd2\uff0c\u622a\u53e3\u9053\uff1a\u201c\u82e5\u8981\u6c42\u89c1\uff0c\u5148\u5f85\u901a\u77e5\uff0c\u676d\u5dde\u6bdb\u5e9c\uff0c\u5c82\u662f\u4f60\u4e71\u95ef\u4e4b\u5730\uff01\u201d \u3000\u3000\u9752\u888d\u4eba\u76ee\u5149\u68ee\u7136\u626b\u8fc7\u4ed6\u4eec\u9762\u4e0a\uff0c\u50f5\u6728\u7684\u811a\u6b65\uff0c\u4ecd\u7136\u4e00\u6b65\u4e00\u6b65\u5411\u524d\u79fb\u52a8\u7740\u3002 \u3000\u3000\u4e24\u4e2a\u84dd\u8863\u5251\u624b\u9f50\u5730\u5927\u559d\u4e00\u58f0\uff0c\u53cc\u5251\u4ea4\u526a\uff0c\u5530\u5530\u4e24\u5251\uff0c\u4e00\u5de6\u4e00\u53f3\uff0c\u7834\u98ce\u800c\u6765\u3002 \u3000\u3000\u53ea\u542c\u201c\u545b\u201d\u5730\u4e00\u58f0\u957f\u541f\uff0c\u53cc\u5251\u4ea4\u51fb\uff0c\u90a3\u9752\u888d\u4eba\u4e0d\u77e5\u600e\u5730\uff0c\u7adf\u5df2\u4ece\u5251\u5149\u4e2d\u7a7f\u8fc7\uff0c\u8d70\u5230\u4ed6\u4e24 \u4eba\u7684\u8eab\u540e\u3002 \u3000\u3000\u4ed6\u4e24\u4eba\u5fc3\u5934\u4e00\u5bd2\uff0c\u6014\u5728\u5730\u4e0a\uff0c\u518d\u4e5f\u4e0d\u6562\u7ffb\u8eab\u8ffd\u51fb\u3002 \u3000\u3000\u53ea\u89c1\u8fd9\u9752\u888d\u4eba\u4ecd\u7136\u5728\u7f13\u7f13\u8fc8\u7740\u811a\u6b65\uff0c\u4ed6\u80a9\u5934\u6240\u8d1f\u7684\u5c38\u8eab\uff0c\u968f\u7740\u4ed6\u7684\u811a\u6b65\uff0c\u5fae\u5fae\u6447\u6446 \u7740\u2026\u2026 \u3000\u3000\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u7ec8\u4e8e\u4e5f\u88ab\u60ca\u52a8\uff0c\u5927\u6b65\u8d70\u5230\u5385\u53e3\uff0c\u9752\u888d\u4eba\u8f6c\u8fc7\u957f\u5eca\uff0c\u8d70\u5411\u5927\u5385\uff0c\u524d\u9762\u5ffd\u6709\u4e00 \u6392\u624b\u6301\u94a2\u5200\u7684\u5927\u6c49\u6321\u4f4f\u4e86\u4ed6\u7684\u53bb\u8def\u3002 \u3000\u3000\u4e00\u6392\u94a2\u5200\uff0c\u5200\u5c16\u5411\u524d\uff0c\u88ab\u706f\u5149\u4e00\u6620\uff0c\u95ea\u95ea\u53d1\u7740\u5bd2\u5149\u3002 \u3000\u3000\u9752\u888d\u4eba\u5374\u4ecd\u7136\u89c6\u82e5\u65e0\u7779\uff0c\u7b14\u76f4\u5730\u8d70\u5411\u5200\u5149\uff0c\u8fd9\u4e00\u6392\u5200\u5c16\uff0c\u5374\u5df2\u5fae\u5fae\u8d77\u4e86\u98a4\u6296\uff0c\u53ea\u6709\u4e00\u4eba \u58ee\u80c6\u559d\u9053\uff1a\u201c\u6b62\u6b65\uff01\u2026\u2026\u6b62\u6b65\u2026\u2026\u201d \u3000\u3000\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u9762\u6c89\u5982\u6c34\uff0c\u53ea\u89c1\u8fd9\u4e00\u6392\u5927\u6c49\u5df2\u5c06\u6325\u5200\u800c\u4e0a\u3002 \u3000\u3000\u6bdb\u81ec\u7a81\u5730\u5389\u58f0\u9053\uff1a\u201c\u95ea\u5f00\uff0c\u8ba9\u4ed6\u8fc7\u6765\uff01\u201d \u3000\u3000\u9752\u888d\u4eba\u7ee7\u7eed\u7740\u811a\u6b65\uff0c\u8d70\u5411\u5927\u5385\uff0c\u9762\u4e0a\u4ecd\u7136\u6beb\u65e0\u8868\u60c5\uff0c\u8fd9\u4e00\u6392\u5927\u6c49\u95ea\u5f00\u4e0e\u5426\uff0c\u6839\u672c\u6ca1\u6709\u653e \u5728\u4ed6\u7684\u5fc3\u4e0a\u3002 \u3000\u3000\u6b63\u5385\u4e4b\u4e2d\uff0c\u9664\u4e86\u90a3\u8499\u9762\u98ce\u6c05\u7684\u201c\u4eba\u547d\u730e\u6237\u201d\u5916\uff0c\u4ff1\u5df2\u79bb\u5ea7\u800c\u8d77\u3002 \u3000\u3000\u9752\u888d\u4eba\u8d70\u4e0a\u5927\u5385\uff0c\u76ee\u5149\u6728\u7136\u671b\u5411\u6bdb\u81ec\uff0c\u7a81\u7136\u53cc\u624b\u4e00\u64a4\uff0c\u5c06\u80a9\u4e0a\u7684\u5c38\u9996\uff0c\u4ef0\u9762\u63b7\u5728\u5730\u4e0a\u3002 \u3000\u3000\u7fa4\u8c6a\u76ee\u5149\u52a8\u5904\uff0c\u8d6b\u7136\u53d1\u73b0\u8fd9\u5c38\u9996\u7adf\u662f\u7a0b\u67ab\uff0c\u4e0d\u7981\u9f50\u5730\u53d1\u51fa\u60ca\u547c\u3002 \u3000\u3000\u6bdb\u81ec\u7eb5\u7136\u9547\u9759\uff0c\u9762\u8272\u4ea6\u4e0d\u7981\u5927\u53d8\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u8d1f\u5c38\u800c\u6765\uff0c\u4e3a\u7684\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u6b64\u523b\u8fd8\u6ca1\u6709\u8fa8\u51fa\u8fd9\u9752\u888d\u4eba\u7684\u6765\u610f\uff0c\u4ee5\u4ed6\u7684\u8eab\u4efd\u3002\u81ea\u4e0d\u80fd\u968f\u4fbf\u52a8\u624b\u3002 \u3000\u3000\u53ea\u89c1\u9752\u888d\u4eba\u50f5\u6728\u7684\u9762\u5bb9\u4e0a\uff0c\u5ffd\u7136\u6cdb\u8d77\u4e00\u4e1d\u7b11\u5bb9\u2026\u2026\u7b11\u5bb9\u626d\u66f2\u4e86\u5200\u75a4\uff0c\u4f7f\u4ed6\u7684\u9762\u5bb9\u66f4\u52a0\u72f0 \u72de\u4e11\u964b\u3002 \u3000\u3000\u4ed6\u5f02\u6837\u5730\u5fae\u5fae\u4e00\u7b11\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u6211\u662f\u8c01\uff1f\u201d \u3000\u3000\u76ee\u5149\u518d\u6b21\u671b\u5411\u6bdb\u81ec\uff0c\u4e00\u5b57\u4e00\u5b57\u4ed6\u8bf4\u9053\uff1a\u201c\u96be\u9053\u4f60\u4e0d\u8ba4\u5f97\u6211\u4e86\u4e48\uff1f\u201d \u3000\u3000\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u6d53\u7709\u76b1\u5f97\u66f4\u7d27\uff0c\u76ee\u5149\u51dd\u6ce8\u5728\u8fd9\u9752\u888d\u4eba\u9762\u4e0a\uff0c\u4ed6\u867d\u7136\u641c\u904d\u8bb0\u5fc6\uff0c\u4e00\u65f6\u5374\u4e5f\u60f3 \u4e0d\u51fa\u6b64\u4eba\u662f\u8c01\uff1f \u3000\u3000\u767e\u6b65\u98de\u82b1\u6797\u7426\u7b5d\u8f7b\u8f7b\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u82e5\u662f\u6bdb\u5927\u54e5\u7684\u670b\u53cb\uff0c\u5c31\u8bf7\u4f60\u5feb\u4e9b\u8bf4\u51fa\u6765\u4e48\uff0c\u5c3d\u6253\u54d1 \u8c1c\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6b64\u65f6\u6b64\u523b\uff0c\u5979\u5c45\u7136\u8fd8\u7b11\u5f97\u51fa\u6765\uff0c\u8bed\u58f0\u5c45\u7136\u4e5f\u4ecd\u7136\u662f\u90a3\u4e48\u5a07\u7f8e\u800c\u751c\u871c\uff0c\u5b9e\u5728\u662f\u4ee4\u4eba\u60ca\u5f02\u3002 \u3000\u3000\u201c\u5de6\u624b\u795e\u5251\u201d\u4e01\u8863\u5374\u76b1\u7709\u9053\uff1a\u201c\u7a0b\u67ab\u53ef\u662f\u88ab\u4f60\u6740\u6b7b\u2026\u2026\u201d \u3000\u3000\u9752\u888d\u4eba\u51b7\u7b11\u63a5\u53e3\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u4f17\u4eba\u9f50\u5730\u4e00\u60ca\uff0c\u4e01\u8863\u8fde\u9000\u4e09\u6b65\uff0c\u201c\u545b\u5577\u201d\u4e00\u58f0\uff0c\u62d4\u51fa\u5251\u6765\u3002 \u3000\u3000\u201c\u767e\u6b65\u98de\u82b1\u201d\u6797\u7426\u7b5d\u4f3c\u7b11\u975e\u7b11\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4f60\u65e2\u7136\u6740\u4e86\u4ed6\uff0c\u53c8\u628a\u4ed6\u5c38\u8eab\u80cc\u6765\uff0c\u96be\u9053\u4f60\u662f \u60f3\u6765\u9001\u6b7b\u7684\u4e48\uff1f\u5509\u2026\u2026\u6211\u771f\u4e0d\u77e5\u9053\u4f60\u8fd9\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5979\u5c45\u7136\u8f7b\u53f9\u4e86\u4e00\u58f0\uff0c\u4f3c\u4e4e\u5bf9\u8fd9\u9752\u888d\u4eba\u751a\u4e3a\u540c\u60c5\u3002 \u3000\u3000\u9752\u888d\u4eba\u5374\u6709\u5982\u672a\u95fb\uff0c\u76ee\u6ce8\u6bdb\u81ec\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4f60\u4e0d\u8ba4\u5f97\u6211\u4e86\u4e48\uff1f\u201d \u3000\u3000\u6bdb\u81ec\u76ee\u5149\u626b\u5904\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4f60\u82e5\u662f\u6bdb\u81ec\u4e4b\u53cb\uff0c\u600e\u4f1a\u5c06\u7a0b\u67ab\u6740\u6b7b\uff1f\u2026\u201d\u5de6\u624b\u795e\u5251\u201d\u4e01\u8863 \u9053\uff1a\u201c\u6b63\u662f\u5982\u6b64\uff01\u201d \u3000\u3000\u5530\u5730\u4e00\u5251\uff0c\u659c\u659c\u524a\u5411\u9752\u888d\u4eba\u7684\u80a9\u5934\u3002 \u3000\u3000\u9752\u888d\u4eba\u8eab\u5f62\u4e00\u95ea\uff0c\u7a81\u7136\u81ea\u8896\u5e95\u5f39\u51fa\u4e00\u6307\uff0c\u5f39\u5f00\u4e86\u8fd9\u653b\u52bf\u6781\u4e3a\u51cc\u5389\u7684\u4e00\u5251\uff0c\u53e3\u4e2d\u5374\u7f13\u7f13\u8bf4 \u9053\uff1a\u201c\u5341\u516b\u5e74\u524d\uff0c\u4e00\u4e2a\u5927\u96e8\u6ec2\u6cb1\u7684\u6df1\u591c\u3002\u201d \u3000\u3000\u5de6\u624b\u795e\u5251\u4e00\u62db\u53d7\u632b\uff0c\u52c3\u7136\u5927\u6012\uff0c\u6b63\u5f85\u6325\u5251\u653b\u4e0a\uff0c\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u5374\u4e00\u76b1\u53cc\u7709\uff0c\u6447\u624b\u6c89\u58f0 \u9053\uff1a\u201c\u4e01\u5144\u6682\u4e14\u4f4f\u624b\u3002\u201d \u3000\u3000\u6b63\u5385\u4e4b\u4e0a\uff0c\u4eba\u4eba\u4ff1\u8981\u542c\u4ed6\u4e0b\u6587\uff0c\u662f\u4ee5\u53d8\u5f97\u5341\u5206\u9759\u5bc2\u3002 \u3000\u3000\u53ea\u89c1\u9752\u888d\u4eba\u4ecd\u7136\u76ee\u6ce8\u6bdb\u81ec\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u5341\u516b\u5e74\u524d\uff0c\u6211\u4e3a\u4f60\u4fdd\u90a3\u4e00\u8d9f\u7ea2\u8d27\uff0c\u534a\u9014\u906d\u52ab\uff0c\u51e0 \u4e4e\u4e22\u4e86\u6027\u547d\uff0c\u4f60\u4eca\u65e5\u5374\u4e0d\u8bb0\u5f97\u6211\u4e86\uff01\u201d \u3000\u3000\u201c\u7075\u86c7\u201d\u6bdb\u684c\u5fc3\u5934\u4e00\u9707\uff0c\u5ffd\u7136\u60f3\u8d77\u4e00\u4eba\u6765\uff0c\u53d8\u8272\u9053\uff1a\u201c\u6731\u5b50\u660e\uff0c\u4f60\u2026\u2026\u4f60\u53ef\u662f\u2018\u95ea\u7535\u795e \u5200\uff0c\u6731\u5b50\u660e\u5b50\u660e\u5144\u5f1f\u4e48\uff1f\u201d\u9752\u888d\u4eba\u6728\u7136\u9053\uff1a\u201c\u6731\u5b50\u660e\u2026\u2026\u6b63\u662f\uff0c\u6211\u5c31\u662f\u6731\u5b50\u660e\uff01\u201d \u3000\u3000\u6bdb\u81ec\u5927\u559d\u4e00\u58f0\uff0c\u4e00\u624b\u63e1\u4f4f\u4e86\u4ed6\u7684\u80a9\u5934\uff0c\u9053\uff1a\u201c\u5b50\u660e\uff0c\u4f60\u2026\u2026\u4f60\u600e\u5730\u4eca\u65e5\u624d\u6765\u89c1\u6211\u8c03\u201d\u5de6 \u624b\u795e\u5251\u201d\u9762\u8272\u94c1\u9752\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u65e0\u8bba\u6b64\u4eba\u662f\u8c01\uff1f\u4ed6\u65e2\u7136\u6740\u4e86\u7a0b\u5927\u54e5\uff0c\u5c0f\u5f1f\u4fbf\u653e\u4ed6\u4e0d\u8fc7\uff01\u201d \u3000\u3000\u2026\u6bdb\u81ec\u9762\u5bb9\u53c8\u4e00\u53d8\u3002 \u3000\u3000\u9752\u888d\u4eba\u201c\u6731\u5b50\u660e\u201d\u6728\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u96be\u9053\u6740\u4ed6\u4e0d\u5f97\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u7f13\u7f13\u62ac\u8d77\uff0c\u6307\u7740\u9762\u4e0a\u7684\u5200\u75a4\uff0c\u53c8\u9053\uff1a\u201c\u4ed6\u89c1\u5229\u5fd8\u4e49\uff0c\u523a\u4e86\u6211\u8fd9\u81f4\u547d\u7684\u4e00\u5251\u3002\u8fd9\u4e00\u5251\u867d \u672a\u80fd\u4f7f\u6211\u4e27\u751f\u3002\u5374\u4f7f\u6211\u5931\u53bb\u8bb0\u5fc6\u5341\u516b\u5e74\uff0c\u5386\u5c3d\u4e07\u65f6\u75db\u82e6\u3002\u8fd9\u2026\u201d \u3000\u3000\u4ed6\u76ee\u5149\u8f6c\u5411\u6bdb\u81ec\u3002 \u3000\u3000\u201c\u8fd9\u5c31\u662f\u4e3a\u4ec0\u4e48\u76f4\u5230\u4eca\u65e5\u6211\u624d\u6765\u89c1\u4f60\uff0c\u53ea\u56e0\u6211\u4e00\u76f4\u8bb0\u4e0d\u5f97\u5f80\u4e8b\uff0c\u751a\u81f3\u8bb0\u4e0d\u5f97\u59d3\u540d\uff0c\u5426\u5219 \u6211\u65e9\u5df2\u8981\u6765\u544a\u8bc9\u4f60\uff0c\u5341\u516b\u5e74\u524d\uff0c\u90a3\u4e00\u8d9f\u7ea2\u8d27\u3002\u201d \u3000\u3000\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u76ee\u5149\u4e00\u51db\uff0c\u9053\uff1a\u201c\u52ab\u9556\u7684\u4eba\uff0c\u83ab\u975e\u7adf\u662f\u7a0b\u67ab\uff1f\u201d \u3000\u3000\u9752\u888d\u4eba\u201c\u6731\u5b50\u660e\u201d\u9053\uff1a\u201c\u6b63\u662f\uff01\u6211\u4e22\u4e86\u4f60\u7684\u9556\uff0c\u82e5\u4e0d\u5c06\u4ed6\u6740\u6b7b\u600e\u6765\u89c1\u4f60\uff1f\u201d \u3000\u3000\u5385\u4e2d\u7684\u60c5\u7eea\uff0c\u5230\u4e86\u6b64\u523b\uff0c\u5df1\u8fbe\u9ad8\u6f6e\u3002 \u3000\u3000\u6b64\u523b\u8c01\u4e5f\u4e0d\u518d\u591a\u53e3\uff0c\u5c31\u8fde\u4e00\u62db\u53d7\u632b\uff0c\u5c3d\u6709\u4e0d\u7518\u7684\u201c\u5de6\u4e8e\u795e\u5251\u201d\u4e01\u8863\uff0c\u4e5f\u6084\u7136\u9000\u5230\u4e00\u65c1\uff0c \u63d2\u56de\u957f\u5251\u3002 \u3000\u3000\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u6014\u4e86\u534a\u664c\uff0c\u7a81\u7136\u4ef0\u5929\u72c2\u7b11\uff0c\u9053\uff1a\u201c\u597d\u6781\u597d\u6781\uff0c\u4eca\u65e5\u771f\u662f\u5927\u559c\u4e4b\u65e5\uff0c\u4e0d\u4f46\u6211 \u79ef\u90c1\u5728\u5fc3\u5934\u5341\u516b\u5e74\u4e4b\u4e45\u7684\u4e00\u4ef6\u65e0\u5934\u516c\u6848\uff0c\u4eca\u65e5\u603b\u7b97\u6709\u4e86\u4ea4\u5f85\uff0c\u6211\u5931\u6563\u4e86\u5341\u516b\u5e74\u7684\u5f1f\u5144\uff0c\u4eca\u65e5 \u4e5f\u5230\u4e86\u6211\u8eab\u8fb9\u2026\u54c8\u54c8\uff0c\u5404\u4f4d\uff0c\u8fd9\u662f\u5426\u53ef\u559c\u53ef\u8d3a\u4e4b\u4e8b\u2026\u201d \u3000\u3000\u4ed6\u53cc\u638c\u4e00\u62cd\uff0c\u9ad8\u58f0\u9053\uff1a\u201c\u6362\u4e0a\u9152\u83dc\uff0c\u4e3a\u6211\u6731\u8d24\u5f1f\u63a5\u98ce\uff01\u201d \u3000\u3000\u7b11\u58f0\u4e00\u987f\uff0c\u53c8\u9053\u3002 \u3000\u3000\u201c\u5c06\u7a0b\u5927\u4fa0\u7684\u5c38\u8eab\uff0c\u539a\u539a\u6536\u6b93\u4e86\uff0c\u6682\u83ab\u97f3\u77e5\u7a0b\u592b\u4eba\uff0c\u514d\u5f97\u5979\u60ca\u52a8\u4e86\u80ce\u6c14\u3002\u201d \u3000\u3000\u7075\u86c7\u95e8\u4e0b\uff0c\u7acb\u523b\u5f00\u59cb\u5fd9\u788c\u3002 \u3000\u3000\u201c\u767e\u6b65\u98de\u82b1\u201d\u6797\u7426\u7b5d\u5a07\u7b11\u9053\u3002 \u3000\u3000\u201c\u6bdb\u5927\u54e5\uff0c\u8fd9\u6837\u5bf9\u4f60\u4e0d\u8d77\u7684\u4eba\uff0c\u4f60\u8fd8\u5bf9\u4ed6\u8fd9\u4e48\u597d\uff0c\u5509\u2026\u2026\u6211\u6797\u7426\u7b5d\u53eb\u4f60\u4e00\u58f0\u5927\u54e5\uff0c\u603b\u7b97 \u53eb\u5f97\u4e0d\u51a4\u6789\u3002\u201d \u3000\u3000\u5979\u79cb\u6ce2\u779f\u5411\u201c\u6731\u5b50\u660e\u201d\u5a07\u7b11\u53c8\u9053\uff1a\u201c\u5582\uff0c\u6211\u8bf4\u6731\u5144\u5f1f\uff0c\u4f60\u4ec7\u4e5f\u62a5\u4e86\u3002\u6c14\u4e5f\u51fa\u4e86\uff0c\u53c8\u770b\u5230 \u4e86\u8001\u670b\u53cb\uff1a\u8fd9\u4e48\u591a\u559c\u4e8b\u90e8\u6765\u4e86\uff0c\u4f60\u603b\u8be5\u7b11\u4e00\u58f0\uff0c\u7b11\u4e86\u5427\uff0c\u8001\u5b9e\u8bf4\uff0c\u4f60\u8fd9\u6837\u7684\u795e\u6c14\uff0c\u6211\u770b\u4e86\u90fd \u8981\u5f80\u5fc3\u91cc\u6253\u54c6\u55e6\u3002\u201d \u3000\u3000\u9752\u888d\u4eba\u201c\u6731\u5b50\u660e\u201d\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u5927\u53ef\u4e0d\u5fc5\u518d\u770b\u6211\uff01\u201d \u3000\u3000\u6797\u7426\u7b5d\u6014\u4e86\u4e00\u6014\uff0c\u7ec8\u4e8e\u7b11\u4e0d\u51fa\u6765\u4e86\u3002 \u3000\u3000\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u90fd\u662f\u81ea\u5df1\u5144\u5f1f\uff0c\u4f55\u5fc5\u2026\u2026\u201d \u3000\u3000\u7b11\u58f0\u672a\u4e86\uff0c\u201c\u593a\u547d\u4f7f\u8005\u201d\u94c1\u5e73\u7a81\u5730\u5982\u98de\u5954\u4e0a\u5385\u6765\uff0c\u5598\u7740\u6c14\u9053\uff1a\u201c\u5e08\u5085\u2026\u2026\u6709\u2026\u2026\u4eba\u8981\u89c1 \u4f60\u8001\u4eba\u5bb6\u3002\u201d \u3000\u3000\u6bdb\u81ec\u7b11\u8bed\u4e00\u987f\uff0c\u53cc\u7709\u5fae\u76b1\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u4f60\u4e3a\u4f55\u5982\u6b64\u60ca\u614c\uff1f\u201d \u3000\u3000\u94c1\u5e73\u5598\u606f\u72b9\u672a\u5b9a\uff0c\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u2026\u2026\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u987f\u4f4f\u8bed\u58f0\uff0c\u76ee\u5149\u60ca\u5f02\u5730\u671b\u5411\u201c\u6731\u5b50\u660e\u201d\uff0c\u6bdb\u81ec\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u6731\u5e08\u53d4\uff01\u201d \u3000\u3000\u94c1\u5e73\u65b9\u81ea\u6447\u5934\u8bf4\u9053\uff1a\u201c\u4fbf\u662f\u8fd9\u4e24\u4eba\u7684\u6b66\u529f\u5927\u8fc7\u60ca\u4eba\uff0c\u7b80\u76f4\u4ee4\u4eba\u4e0d\u53ef\u601d\u8bae\uff0c\u800c\u4e14\u4ed6\u4e24\u4eba\u6765 \u5bfb\u5e08\u5085\u4f60\u8001\u4eba\u5bb6\u4e4b\u610f\uff0c\u4ea6\u4e0d\u77e5\u662f\u53cb\u662f\u654c\u3002\u201d \u3000\u3000\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u53cc\u7709\u5fae\u76b1\uff0c\u76ee\u5149\u4e00\u8f6c\uff0c\u7a81\u5730\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u65e0\u8bba\u4ed6\u4e24\u4eba\u6765\u610f\u5982\u4f55\uff0c\u5728\u6b64\u5730\u96be \u9053\u8fd8\u4f1a\u8ba8\u5f97\u4e86\u597d\u4e48\uff1f\u201d \u3000\u3000\u8981\u77e5\u6b64\u523b\u8fd9\u5385\u4e0a\u4e4b\u4eba\uff0c\u4ff1\u662f\u6b66\u6797\u4e2d\u7684\u4e00\u6d41\u9ad8\u624b\uff0c\u662f\u4ee5\u6bdb\u81ec\u8fd9\u756a\u8bf4\u8bdd\uff0c\u5012\u4e5f\u4e0d\u662f\u81ea\u5938\u81ea\u6ee1 \u4e4b\u8bcd\u3002\u6797\u7426\u7b5d\u79cb\u6ce2\u4e00\u8f6c\uff0c\u9762\u4e0a\u53c8\u7efd\u5f00\u5a07\u7b11\uff0c\u9053\uff1a\u201c\u6b66\u529f\u4e0d\u53ef\u601d\u8bae\u2026\u2026\u8fd9\u662f\u8c01\u5440\uff1f\u6211\u5012\u8981\u770b \u770b\uff0c\u4ed6\u4eec\u2026\u2026\u201d \u3000\u3000\u5979\u5ffd\u7136\u53d1\u89c9\u5385\u4e0a\u6240\u6709\u7684\u76ee\u5149\u90fd\u8f6c\u5411\u5385\u95e8\uff0c\u4e0d\u7981\u987f\u4f4f\u8bed\u58f0\uff0c\u8f6c\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u4e00\u80d6\u4e00\u7626\u4e24\u4e2a \u8eab\u6750\u6781\u9ad8\u7684\u9526\u8863\u8001\u4eba\uff0c\u5e76\u80a9\u7ad9\u5728\u5385\u524d\uff0c\u56db\u9053\u76ee\u5149\u4e4b\u4e2d\uff0c\u7adf\u50cf\u662f\u5e26\u7740\u4e00\u79cd\u5947\u5f02\u7684\u9b45\u529b\uff0c\u5fae\u5fae\u4e00 \u626b\uff0c\u4fbf\u5df2\u4ee4\u4eba\u5fc3\u8df3\u3002 \u3000\u3000\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u5446\u4e86\u4e00\u5446\uff0c\u65b9\u81ea\u7b11\u9053\uff1a\u201c\u4e24\u4f4d\u5bfb\u8bbf\u6bdb\u67d0\u4e0d\u77e5\u2026\u201d \u3000\u3000\u5de6\u9762\u4e00\u4eba\u9762\u5982\u6ee1\u6708\uff0c\u4e00\u62a8\u957f\u9aef\uff0c\u622a\u53e3\u9053\uff1a\u201c\u8001\u592b\u7a0b\u9a79\uff01\u201d \u3000\u3000\u53f3\u9762\u4e00\u4eba\u7626\u9aa8\u9cde\u5ccb\uff0c\u563b\u563b\u7b11\u9053\uff1a\u201c\u8001\u592b\u6f58\u4f65\uff01\u201d \u3000\u3000\u4e24\u4eba\u4e00\u9f50\u4e3e\u6b65\uff0c\u8d70\u5230\u6bdb\u81ec\u9762\u524d\uff0c\u7a0b\u9a79\u9053\uff1a\u201c\u4f60\u5c31\u662f\u6bdb\u81ec\u4e48\uff1f\u55ef\uff0c\u6709\u4e9b\u50cf\u2026\u2026\u201d \u3000\u3000\u6f58\u4f65\u9053\uff1a\u201c\u5341\u516b\u5e74\u524d\u6211\u66fe\u7ecf\u89c1\u5230\u4f60\u7684\u59b9\u5b50\u2026\u2026\u201d \u3000\u3000\u4ed6\u8f7b\u63cf\u6de1\u5199\u4ed6\u8bf4\u51fa\u8fd9\u53e5\u8bdd\u6765\uff0c\u5374\u6709\u5982\u4e00\u65b9\u5de8\u77f3\u6295\u5165\u6625\u6c34\u91cc\u3002 \u3000\u3000\u5927\u5385\u4e2d\u7fa4\u8c6a\u4eba\u4eba\u4ff1\u90fd\u4e00\u60ca\uff0c\u5c31\u8fde\u90a3\u9752\u888d\u4eba\u201c\u6731\u5b50\u660e\u201d\u6728\u7136\u76ee\u5149\u4e2d\uff0c\u90fd\u4e0d\u7981\u95ea\u8fc7\u4e00\u4e1d\u60ca\u9a87 \u7684\u795e\u8272\u3002 \u3000\u3000\u6bdb\u81ec\u5b9a\u4e86\u5b9a\u795e\uff0c\u65b9\u81ea\u8bf4\u9053\uff1a\u201c\u5bb6\u2026\u2026\u59b9\u2026\u2026\u54b3\u54b3\uff0c\u6b64\u523b\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u4ed6\u867d\u7136\u6781\u529b\u63a7\u5236\uff0c\u4f46\u8bed\u58f0\u4ecd\u4e0d\u7981\u4e3a\u4e4b\u98a4\u6296\uff0c\u662f\u4ee5\u501f\u7740\u4e24\u58f0\u5e72\u54b3\uff0c\u5c06\u4e4b\u63a9\u9970\u3002\u81ea\u7136\uff0c\u4ed6\u6240 \u60ca\u9707\u7684\u5e76\u672a\u4e3a\u4e86\u81ea\u5df1\u7684\u59b9\u59b9\uff0c\u800c\u662f\u4e3a\u4e86\u5341\u516b\u5e74\u524d\uff0c\u4ed6\u59b9\u59b9\u809a\u4e2d\u7684\u5b69\u5b50\u3002 \u3000\u3000\u8499\u9762\u98ce\u6c05\u7684\u201c\u4eba\u547d\u730e\u6237\u201d\uff0c\u4e00\u76f4\u7aef\u5750\u672a\u52a8\uff0c\u6b64\u523b\u7adf\u4e5f\u957f\u8eab\u800c\u8d77\uff0c\u76ee\u5c04\u795e\u5149\u3002 \u3000\u3000\u53ea\u542c\u7a0b\u9a79\u7f13\u7f13\u9053\uff1a\u201c\u6d77\u5929\u5b64\u5c9b\uff01\u201d \u3000\u3000\u8fd9\u56db\u5b57\u4ed6\u4e00\u5b57\u4e00\u5b57\u5730\u7f13\u7f13\u8bf4\u5c06\u51fa\u6765\uff0c\u4f17\u4eba\u53c8\u81ea\u4e00\u60ca\u3002 \u3000\u3000\u6bdb\u81ec\u6025\u6025\u95ee\u9053\uff1a\u201c\u90a3\u4e48\u2026\u2026\u5979\u6240\u4ea7\u4e0b\u7684\u5a74\u513f\u2026\u2026\u201d \u3000\u3000\u6f58\u4f65\u563b\u563b\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u81ea\u7136\u62dc\u4e86\u6d77\u5929\u5b64\u71d5\u4e3a\u5e08\uff01\u201d \u3000\u3000\u6bdb\u81ec\u5fc3\u5934\u4e00\u9707\uff0c\u8fde\u9000\u6570\u6b65\uff0c\u8dcc\u5750\u5728\u6905\u4e0a\uff0c\u201c\u4eba\u547d\u730e\u6237\u201d\u4ea6\u81ea\u5750\u5012\uff0c\u94db\u5730\u4e00\u58f0\uff0c\u5c06\u684c\u4e0a\u4e00 \u53ea\u94f6\u7b77\uff0c\u649e\u843d\u5728\u5730\u4e0a\u3002 \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u53ea\u89c1\u6bdb\u81ec\u9762\u4e0a\u9635\u9752\u9635\u767d\uff0c\u663e\u89c1\u662f\u5fc3\u4e2d\u6781\u4e3a\u60ca\u5413\u3002 \u3000\u3000\u6cb3\u6714\u53cc\u5251\u3001\u767e\u6b65\u98de\u82b1\u3001\u5de6\u624b\u795e\u5251\uff0c\u8fd9\u4e9b\u4e0e\u6614\u5e74\u4ec7\u72ec\u4e4b\u6b7b\u6709\u5173\u4e4b\u4eba\uff0c\u5fc3\u4e2d\u4ea6\u662f\u7830\u7830\u4e71\u8df3\u3002 \u4ec7\u72ec\u4e4b\u5b50\uff0c\u82e5\u662f\u201c\u6d77\u5929\u5b64\u71d5\u201d\u4e4b\u5f92\uff0c\u6b66\u529f\u90a3\u8fd8\u4e86\u5f97\uff0c\u90a3\u4e48\uff0c\u5341\u516b\u5e74\u524d\u90a3\u4e00\u6bb5\u8840\u6d77\u6df1\u4ec7\uff0c\u5c82\u975e \u771f\u7684\u8981\u4ee5\u8840\u8fd8\u507f\uff1f \u3000\u3000\u7a0b\u9a79\u76ee\u5149\u626b\u5904\uff0c\u84e6\u5730\u4e00\u6b65\u8de8\u5230\u6bdb\u81ec\u8eab\u524d\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4ec7\u72ec\u4e4b\u5b50\uff0c\u7eb5\u662f\u6d77\u5929\u5b64\u71d5\u4e4b\u5f92\uff0c \u6709\u6211\u4e24\u4eba\u5728\u6b64\uff0c\u4f60\u8fd8\u6015\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6bdb\u81ec\u970d\u7136\u7ad9\u8d77\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u3002\u6f58\u4f65\u4ea6\u81ea\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u4e24\u4eba\u6b64\u6765\uff0c\u4fbf\u662f\u4e3a\u4e86\u4fdd\u62a4\u4f60 \u7684\u3002\u201d \u3000\u3000\u6bdb\u81ec\u76ee\u5149\u95ea\u52a8\uff0c\u5fc3\u4e2d\u4f46\u613f\u76f8\u4fe1\uff0c\u53c8\u4e0d\u6562\u76f8\u4fe1\uff0c\u4ed6\u4e0d\u7981\u5728\u6697\u4e2d\u5bfb\u601d\uff0c\u8be5\u600e\u6837\u63a2\u51fa\u8fd9\u4e24\u4eba\u6765 \u610f\u7684\u771f\u5047\u4e0e\u6b66\u529f\u4e4b\u6df1\u6d45\u3002 \u3000\u3000\u8fd9\u65f6\u591c\u5df2\u5f88\u6df1\uff0c\u665a\u98ce\u9759\u9759\u5730\u5439\u5165\u5927\u5385\uff0c\u5439\u7740\u8fd9\u4e00\u7fa4\u6709\u5982\u5851\u50cf\u4e00\u822c\u7684\u4eba\u4eec\u7684\u8863\u886b\uff0c\u624d\u4f7f\u5f97 \u4ed6\u4eec\u770b\u6765\u6709\u4e86\u751f\u547d\u3002 \u3000\u3000\u65e0\u8bba\u662f\u8c01\uff0c\u6b64\u523b\u82e5\u662f\u8d70\u6765\u5411\u8fd9\u4e9b\u4eba\u770b\u4e0a\u4e00\u773c\uff0c\u90fd\u65e0\u6cd5\u76f8\u4fe1\uff0c\u8fd9\u4e9b\u4eba\u638c\u4e2d\u66fe\u7ecf\u6216\u5c06\u8981\u638c\u63e1 \u6b66\u6797\u4e2d\u7684\u4e00\u534a\u547d\u8fd0\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u9762\u4e0a\uff0c\u5e26\u7740\u7684\u7adf\u662f\u90a3\u4e48\u6d53\u91cd\u7684\u5fe7\u90c1\u3002 \u3000\u3000\u7a81\u7136\uff0c\u4e00\u9635\u72c2\u7b11\uff0c\u5c06\u6c89\u5bc2\u7684\u5fe7\u90c1\u5212\u6210\u7c89\u788e\u3002 \u3000\u3000\u8fd9\u4e00\u9635\u72c2\u7b11\u4e4b\u58f0\uff0c\u5176\u5b9e\u9065\u8fdc\u5728\u5ead\u9662\u4e4b\u5916\uff0c\u4f46\u5374\u5df2\u8db3\u591f\u4f7f\u5f97\u5385\u4e0a\u4e4b\u4eba\u8033\u9f13\u4e3a\u4e3a\u4e4b\u4e00\u9707\u3002 \u3000\u3000\u4e00\u4e2a\u84dd\u8863\u5251\u624b\uff0c\u5728\u72c2\u7b11\u58f0\u4e2d\uff0c\u6025\u6b65\u8d70\u5165\u5927\u5385\uff0c\u9053\uff1a\u201c\u5916\u9762\u53c8\u6709\u5ba2\u4eba\u2026\u2026\u201d \u3000\u3000\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u6682\u4e14\u629b\u5f00\u4e86\u5fc3\u4e2d\u7684\u601d\u8651\uff0c\u53cc\u76ee\u4e00\u5f20\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u8c01\uff1f\u5982\u6b64\u6df1\u591c\uff1f\u201d \u3000\u3000\u84dd\u8863\u5251\u624b\u5782\u9996\u9053\uff1a\u201c\u542c\u4ed6\u4eec\u81ea\u62a5\u59d3\u540d\uff0c\u5176\u4e2d\u4eff\u4f5b\u6709\u2018\u6b66\u5f53\u6d3e\u2019\u7684\u2018\u9752\u98ce\u5251\u2019\u6731\u767d\u7fbd\uff0c \u2018\u534e\u5c71\u6d3e\uff0c\u7684\u94f6\u9e64\u9053\u957f\uff0c\u8fd8\u6709\u2026\u2026\u201d\u5c31\u662f\u8fd9\u4e24\u4e2a\u4eba\u540d\uff0c\u5df2\u8db3\u591f\u4f7f\u5927\u5385\u6062\u590d\u751f\u6c14\uff0c\u800c\u518d\u5ea6\u9a9a\u52a8 \u8d77\u6765\u3002\u6bdb\u81ec\u82e6\u7b11\u4e00\u58f0\uff0c\u622a\u53e3\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4eca\u591c\u6b64\u95f4\u5012\u70ed\u95f9\u5f97\u5f88\u3002\u201d \u3000\u3000\u4ed6\u8f6c\u5411\u90a3\u84dd\u8863\u5251\u624b\u9053\uff1a\u201c\u4ed6\u4eec\u53ef\u66fe\u8bf4\u51fa\u6765\u610f\uff1f\u201d \u3000\u3000\u84dd\u8863\u5251\u624b\u55eb\u5685\u7740\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u50cf\u662f\u90fd\u5df2\u559d\u9189\u4e86\uff0c\u8bf4\u660e\u65e5\u4fbf\u662f\u2018\u897f\u6e56\u82f1\u96c4\u4e4b\u4f1a\u2019\uff0c\u4ed6\u4eec\u4eca \u591c\u8981\u6765\u770b\u770b\u82f1\u96c4\u4f1a\u7684\u4e3b\u4eba\uff0c\u8fd8\u8981\u6765\u53e8\u6270\u4e3b\u4eba\u51e0\u676f\u7f8e\u9152\u3002\u201d \u3000\u3000\u6bdb\u81ec\u53cc\u7709\u5fae\u76b1\uff0c\u6c89\u541f\u4e0d\u8bed\uff0c\u4ed6\u6b64\u523b\u56f0\u607c\u5df2\u591f\u591a\u4e86\uff0c\u5b9e\u5728\u4e0d\u613f\u518d\u60f9\u9ebb\u70e6\uff0c\u4f46\u662f\uff0c\u4ed6\u5374\u53c8\u600e \u80fd\u62d2\u7edd\u8fd9\u4e9b\u6b66\u6797\u4e2d\u7684\u9876\u5c16\u5251\u624b\u3002 \u3000\u3000\u7b2c\u4e00\u4e2a\u601d\u8651\u8fd8\u672a\u89e3\u51b3\uff0c\u4fbf\u88ab\u629b\u5f00\uff0c\u6b64\u523b\u7b2c\u4e8c\u4e2a\u601d\u8651\u5374\u5df2\u63a5\u8e7f\u800c\u6765\uff0c\u4ed6\u5f00\u59cb\u731c\u6d4b\u8fd9\u4e9b\u540d\u5251 \u624b\u7684\u6765\u610f\u3002 \u3000\u3000\u90a3\u84dd\u8863\u5251\u624b\u7acb\u5728\u4e00\u65c1\uff0c\u7b49\u4e86\u534a\u664c\uff0c\u55eb\u5685\u7740\u53c8\u81ea\u8bf4\u9053\uff1a\u201c\u662f\u8bf7\u4ed6\u4eec\u8fdb\u6765\uff0c\u8fd8\u662f\u2026\u2026\u201d \u3000\u3000\u6bdb\u81ec\u6d53\u7709\u4e00\u626c\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u8bf7\uff01\u201d \u3000\u3000\u5ead\u56ed\u4e2d\u7b11\u58f0\u672a\u4e86\uff0c\u53c8\u5df2\u4f20\u6765\u4e00\u9635\u6b4c\u58f0\uff01 \u3000\u3000\u201c\u5341\u5e74\u78e8\u5251\uff0c\u4e94\u9675\u7ed3\u5ba2\uff0c\u628a\u5e73\u751f\u6d95\u6cea\u90fd\u98d8\u5c3d\u2026\u2026\u201d \u3000\u3000\u6b4c\u58f0\u97f3\u8282\u9abc\u7136\uff0c\u8fd8\u6709\u51fb\u5251\u4e4b\u58f0\u76f8\u548c\uff0c\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u6447\u5934\u53f9\u606f\u4e00\u58f0\uff0c\u5411\u7a0b\u9a79\u3001\u6f58\u4f65\u6b49\u7136\u4e00 \u7b11\uff0c\u9053\uff1a\u201c\u5931\u966a\u3002\u201d\u5927\u6b65\u51fa\u8fce\u3002 \u3000\u3000\u65b9\u81ea\u8d70\u5230\u957f\u5eca\uff0c\u53ea\u89c1\u201c\u6e05\u98ce\u5251\u201d\u6731\u767d\u7fbd\u957f\u886b\u65e9\u5df2\u8131\u4e0b\u4e0d\u77e5\u4e22\u5230\u54ea\u91cc\uff0c\u6b64\u523b\u8eab\u4e0a\u5374\u7a7f\u7740\u4e00 \u88ad\u84d1\u8863\uff0c\u622a\u7740\u4e00\u9876\u7b20\u5e3d\uff0c\u5de6\u624b\u6276\u4f4f\u201c\u534e\u5c71\u94f6\u9e64\u201d\u7684\u80a9\u5934\uff0c\u53f3\u638c\u624b\u6301\u957f\u5251\uff0c\u9ad8\u6b4c\u72c2\u7b11\u800c\u6765\u3002 \u3000\u3000\u201c\u534e\u5c71\u94f6\u9e64\u201d\u4ea6\u662f\u84d1\u8863\u7b20\u5e3d\uff0c\u624b\u6301\u957f\u5251\uff0c\u6731\u767d\u7fbd\u6bcf\u5531\u4e00\u53e5\uff0c\u4ed6\u4e24\u4eba\u638c\u4e2d\u7684\u957f\u5251\u4fbf\u540c\u65f6\u6325 \u8d77\u4e00\u4e24\u5251\u76f8\u4ea4\uff0c\u9f99\u541f\u9707\u8033\uff0c\u5374\u538b\u4e0d\u4e0b\u4ed6\u4eec\u8eab\u540e\u4e09\u4eba\u7684\u7b11\u58f0\u3002 \u3000\u3000\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u4e0d\u7981\u53c8\u4e00\u76b1\u7709\uff0c\u5e72\u54b3\u4e00\u58f0\uff0c\u6717\u58f0\u9053\uff1a\u201c\u6bdb\u67d0\u4e0d\u77e5\u5404\u4f4d\u5927\u9a7e\u5149\u4e34\uff0c\u6709\u5931\u8fdc \u8fce\u2026\u2026\u201d \u3000\u3000\u201c\u6e05\u98ce\u5251\u201d\u6731\u767d\u7fbd\u6b4c\u58f0\u4e00\u987f\uff0c\u72c2\u7b11\u7740\u9053\uff1a\u201c\u82e5\u5f97\u7075\u86c7\u4e00\u53e5\u8bdd\uff0c\u4e0d\u8981\u8fdc\u8fce\u2026\u2026\u98ce\u6d41\u2026\u2026\u54c8 \u54c8\uff0c\u6bdb\u5927\u4fa0\uff0c\u4f60\u8fd9\u91cc\u53ef\u6709\u89e3\u6e34\u7684\u7f8e\u9152\uff1f\u201d \u3000\u3000\u201c\u534e\u5c71\u94f6\u9e64\u201d\u6717\u58f0\u5927\u7b11\uff1a\u201c\u89e3\u6e34\u7684\u7f8e\u9152\u2026\u2026\u54c8\u54c8\uff0c\u82e5\u6709\u8fd9\u79cd\u7f8e\u9152\uff0c\u6211\u4fbf\u522b\u65e0\u6240\u613f\u4e86\u3002\u201d \u3000\u3000\u201c\u6e05\u98ce\u5251\u201d\u6731\u767d\u7fbd\u4ee5\u624b\u62cd\u80a9\uff0c\u53c8\u81ea\u9ad8\u6b4c\uff1a\u201c\u4f46\u613f\u80fd\u6709\u89e3\u6e34\u4e4b\u9152\u5343\u4e07\u575b\uff0c\u996e\u5c3d\u5929\u4e0b\u9152\u5f92\u5c3d \u6b22\u989c\u2026\u2026\u201d \u3000\u3000\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u4e0d\u52a8\u58f0\u8272\uff0c\u542b\u7b11\u63d6\u5ba2\uff0c\u8fd9\u4e00\u53e5\u6b4c\u58f0\u65b9\u4e86\uff0c\u201c\u6e05\u98ce\u5251\u201d\u6731\u767d\u7fbd\u5df2\u8d70\u4e0a\u5927\u5385\uff0c \u76ee\u5149\u4e00\u626b\uff0c\u5583\u5583\u9053\uff1a\u201c\u4e00\u3001\u4e8c\u3001\u4e09\u3001\u56db\u2026\u201d \u3000\u3000\u7a81\u5730\u653e\u58f0\u7b11\u9053\uff1a\u201c\u597d\u6781\u597d\u6781\uff0c\u60f3\u4e0d\u5230\u540d\u9707\u5929\u4e0b\u7684\u2018\u4e03\u5251\u4e09\u97ad\u2019\uff0c\u4eca\u65e5\u8fd9\u91cc\u7adf\u5230\u4e86\u4e94\u4f4d\uff0c \u5728\u4e0b\u5b9e\u5728\u9ad8\u5174\u5f97\u5f88\u3002\u201d \u3000\u3000\u201c\u767e\u6b65\u98de\u82b1\u201d\u6797\u7426\u7b5d\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6731\u5927\u5251\u5ba2\uff0c\u4f60\u592a\u8c26\u4e86\uff0c\u6211\u4eec\u7b97\u5f97\u4e86\u4ec0\u4e48\uff0c\u54ea\u91cc\u6bd4 \u5f97\u4e0a\u60a8\u7684\u6b66\u5f53\u795e\u5251\uff1f\u201d \u3000\u3000\u6731\u767d\u7fbd\u53cc\u624b\u8fde\u6447\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e03\u5251\u4e09\u97ad\u9762\u524d\uff0c\u5728\u4e0b\u600e\u6562\u8c08\u5251\uff01\u201d \u3000\u3000\u7a81\u5730\u5927\u559d\u4e00\u4e00\u58f0\uff1a\u201c\u5454\uff01\u53bb\uff01\u201d \u3000\u3000\u624b\u8155\u4e00\u626c\uff0c\u638c\u4e2d\u957f\u5251\u8131\u624b\u98de\u51fa\uff0c\u593a\u7684\u4e00\u58f0\uff0c\u9489\u5728\u5927\u5385\u7684\u6b63\u6881\u4e0a\u3002 \u3000\u3000\u201c\u534e\u5c71\u94f6\u9e64\u201d\u7a81\u5730\u6545\u610f\u4e00\u6574\u9762\u8272\uff0c\u8f7b\u8f7b\u4e00\u62cd\u6731\u767d\u7fbd\u7684\u80a9\u5934\uff0c\u9053\uff1a\u201c\u6731\u5144\uff0c\u4f60\u4e0d\u53ef\u5927\u8c26\uff0c \u82e5\u8bba\u5929\u4e0b\u5251\u6cd5\uff0c\u957f\u767d\u5931\u4e4b\u504f\u6fc0\uff0c\u6606\u4ed1\u5931\u4e4b\u98de\u6d6e\uff0c\u70b9\u82cd\u7a0d\u5acc\u82b1\u5999\uff0c\u5ce8\u5d4b\u592a\u8fc7\u5fe0\u539a\uff0c\u8fd8\u662f\u6b66\u5f53\u5251 \u6cd5\uff0c\u53ef\u79f0\u64ce\u5929\u4e4b\u67f1\uff0c\u5c24\u5176\u662f\u2018\u4e5d\u4e5d\u516b\u5341\u4e00\u624b\u4e5d\u5bab\u8fde\u73af\u5251\u201d\u5251\u5251\u8fde\u73af\uff0c\u5982\u957f\u6c5f\u5927\u6cb3\u4e4b\u6c34\uff0c\u6ed4\u6ed4 \u4e0d\u7edd\uff0c\u53c8\u597d\u50cf\u2026\u2026\u201d\u4ed6\u4f3c\u4e4e\u601d\u7d22\u4e86\u4e00\u4e0b\uff0c\u65b9\u81ea\u63a5\u53e3\u7b11\u9053\uff1a\u201c\u53c8\u597d\u50cf\u674e\u767d\u4e4b\u8bd7\uff0c\u82cf\u8f7c\u4e4b\u8bcd\uff0c\u6ed4 \u6ed4\u800c\u6765\uff0c\u4e0d\u53ef\u65ad\u7edd\u2026\u2026\u54c8\u54c8\uff0c\u597d\u8bd7\u5440\u597d\u8bd7\uff0c\u597d\u5251\u5440\u597d\u5251\uff01\u201d \u3000\u3000\u201c\u6e05\u98ce\u5251\u201d\u6731\u767d\u7fbd\u5927\u7b11\u9053\uff1a\u201c\u8fc7\u5956\u8fc7\u5956\uff0c\u5982\u6b64\u8bf4\u6765\uff0c\u534e\u5c71\u5251\u6cd5\uff0c\u53c8\u5f53\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u534e\u5c71\u94f6\u9e64\u201d\u957f\u5251\u4e00\u62a1\uff0c\u5251\u98ce\u5636\u5636\uff01 \u3000\u3000\u6ee1\u5802\u70db\u706b\uff0c\u4e00\u9635\u98d8\u6447\uff0c\u201c\u534e\u5c71\u94f6\u9e64\u201d\u6447\u5934\u7b11\u9053\uff1a\u201c\u534e\u5c71\u5251\u6cd5\u4e48\u2026\u2026\u8270\u8f9b\u3001\u82e6\u6da9\u3001\u67af\u71e5\u65e0 \u5473\uff0c\u4e0d\u8fc7\u2026\u2026\u54c8\u54c8\uff0c\u4e5f\u8fd8\u4e0d\u9519\u5c31\u662f\u4e86\u3002\u201d \u3000\u3000\u4ed6\u72c2\u7b11\u58f0\u4e2d\uff0c\u957f\u5251\u53c8\u81ea\u4e00\u6325\uff0c\u53ea\u542c\u4e00\u9635\u5c16\u9510\u7684\u5251\u98ce\u81ea\u5251\u5c16\u53d1\u51fa\uff0c\u6ee1\u5385\u70db\u706b\uff0c\u7a81\u5730\u4e00\u9f50\u7184 \u706d\u3002 \u3000\u3000\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u6d53\u7709\u6df1\u76b1\uff0c\u5389\u53f1\u9053\uff1a\u201c\u638c\u706f\u6765\uff01\u201d \u7b2c\u4e8c\u5341\u4e03\u7ae0>> \u53e4\u9f99\u300a\u6e58\u5983\u5251\u300b \u7b2c\u4e8c\u5341\u4e03\u7ae0 \u3000\u3000\u9aa4\u6765\u7684\u9ed1\u6697\u4e2d\uff0c\u8fd9\u6b66\u6797\u67ad\u96c4\u65e9\u5df2\u8fd0\u6c14\u4e8e\u638c\uff0c\u6697\u6697\u6212\u5907\uff0c\u53ea\u8981\u9762\u4e0a\u7a0d\u6709\u5f02\u52a8\uff0c\u4ed6\u81ea\u4fe1\u638c\u4e0a \u7684\u771f\u529b\uff0c\u8db3\u53ef\u5e94\u4ed8\u4e00\uff1a\u5207\uff01 \u3000\u3000\u9ed1\u6697\u4e2d\u53ea\u542c\u811a\u6b65\u58f0\u5f80\u6765\u5954\u817e\uff0c\u81ea\u7136\u662f\u90a3\u4e9b\u53bb\u53d6\u706f\u706b\u7684\u7075\u86c7\u95e8\u4e0b\u3002 \u3000\u3000\u63a5\u7740\uff0c\u5341\u51e0\u6761\u58ee\u6c49\uff0c\u5404\u5404\u624b\u4e2d\u62ff\u7740\u4e0d\u540c\u7684\u706f\u706b\uff0c\u98de\u5954\u800c\u6765\u3002 \u3000\u3000\u5149\u7ebf\u9aa4\u660e\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u5149\u7ebf\u9aa4\u660e\u7684\u5239\u90a3\u95f4\uff0c\u5927\u5385\u4e2d\u5374\u53d1\u51fa\u4e00\u58f0\u60ca\u547c\uff01 \u3000\u3000\u90a3\u8499\u9762\u98ce\u6c05\u7684\u201c\u4eba\u547d\u730e\u6237\u201d\u81ea\u4ece\u201c\u6e05\u98ce\u5251\u201d\u7b49\u4eba\u4e00\u4e00\u5165\u5927\u5385\uff0c\u4fbf\u5408\u4e0a\u53cc\u76ee\uff0c\u8868\u793a\u770b\u4e0d\u60ef \u8fd9\u4e00\u7fa4\u540d\u5251\u624b\u7684\u72c2\u6001\u3002 \u3000\u3000\u706f\u706b\u4e00\u6697\uff0c\u4ed6\u66f4\u843d\u5f97\u6e05\u9759\uff0c\u54ea\u77e5\u6b64\u523b\u5149\u7ebf\u805a\u4eae\uff0c\u4ed6\u5374\u8d6b\u7136\u53d1\u73b0\u4e00\u4e2a\u8eab\u7a7f\u84d1\u8863\uff0c\u4f4e\u5e26\u7b20\u5e3d \u7684\u9ad8\u5927\u6c49\u5b50\uff0c\u6084\u7136\u7acb\u5728\u4ed6\u9762\u524d\uff0c\u4e00\u624b\u62c9\u4e86\u4ed6\u8499\u9762\u7684\u4e1d\u5dfe\uff0c\u4ed6\u5fc3\u4e2d\u5927\u6012\uff0c\u8fd9\u84d1\u8863\u6c49\u5b50\u5374\u5df2\u60ca\u547c \u51fa\u58f0\u6765\u3002 \u3000\u3000\u6240\u6709\u76ee\u5149\uff0c\u968f\u4e4b\u671b\u53bb\uff0c\u53ea\u89c1\u8fd9\u84d1\u8863\u5927\u6c49\u4e00\u58f0\u60ca\u547c\u540e\uff0c\u624b\u638c\u4e00\u62ac\uff0c\u6380\u5f00\u4e86\u7b20\u5e3d\uff0c\u626f\u843d\u4e86\u84d1 \u8863\u2026\u6ee1\u5934\u4e71\u53d1\uff0c\u4e00\u8eab\u9ed1\u8863\u2026\u2026 \u3000\u3000\u8d6b\u7136\u7adf\u662f\u90a3\u201c\u4e71\u53d1\u5934\u9640\u201d\u3002 \u3000\u3000\u4ed6\u72ec\u76ee\u4e4b\u4e2d\uff0c\u95ea\u95ea\u53d1\u5149\uff0c\u4ed6\u9762\u4e0a\u7684\u5200\u75a4\uff0c\u53d8\u4f5c\u8d64\u7ea2\uff0c\u6b63\u5982\u4ed6\u5bf9\u9762\u7684\u201c\u4eba\u547d\u730e\u6237\u201d\u9762\u4e0a\u7684 \u5200\u75a4\u4e00\u6837\uff01 \u3000\u3000\u201c\u4eba\u547d\u730e\u6237\u201d\u98a4\u6296\u7740\u957f\u8eab\u800c\u8d77\uff0c\u4ed6\u8eab\u4e0a\u7684\u98ce\u6c05\u4ea6\u81ea\u655e\u5f00\uff0c\u9732\u51fa\u4e86\u4ed6\u988f\u4e0b\u7684\u767d\u987b\uff0c\u9762\u4e0a\u7684 \u5200\u75a4\u3002\u4e5f\u9732\u51fa\u4e86\u4ed6\u67af\u7626\u7684\u8eab\u8eaf\uff0c\u7a7a\u7a7a\u7684\u53f3\u8896\u3002 \u3000\u3000\u4e24\u4eba\u5bf9\u9762\u800c\u7acb\uff0c\u4e0d\u4f46\u957f\u77ee\u4e00\u6837\uff0c\u9762\u4e0a\u7684\u5200\u75a4\u4e0e\u795e\u60c5\uff0c\u4ea6\u81ea\u5b8c\u5168\u76f8\u540c\uff0c\u53ea\u9664\u4e86\u201c\u4eba\u547d\u730e \u6237\u201d\u7684\u5200\u75a4\u6070\u5de7\u64e6\u76ee\u800c\u8fc7\uff0c\u662f\u4ee5\u4fdd\u5168\u4e86\u5de6\u76ee\u3002 \u3000\u3000\u8fd9\u666f\u8c61\u4f7f\u4eba\u4eba\u4ff1\u90fd\u4e3a\u4e4b\u4e00\u60ca\u2014\u2014\u53c8\u662f\u7247\u523b\u6c89\u5bc2\u3002 \u3000\u3000\u4e8e\u662f\u201c\u4e71\u53d1\u5934\u9640\u201d\u5f00\u59cb\u4e86\u98a4\u6296\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u2026\u2026\u201d \u3000\u3000\u5ffd\u7136\uff0c\u4ed6\u5657\u5730\u8dea\u4e86\u4e0b\u53bb\uff0c\u5927\u558a\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u613f\u89c1\u6211\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u613f\u89c1 \u6211\u2026\u2026\u201d \u3000\u3000\u8fd9\u7c97\u8c6a\u800c\u9ad8\u5927\u7684\u9ed1\u8863\u5934\u9640\uff0c\u6b64\u523b\u4ee5\u9996\u78b0\u5730\uff0c\u7adf\u653e\u58f0\u4e86\u54ed\u4e86\u8d77\u6765\uff0c\u54ed\u5f97\u5c31\u50cf\u662f\u5468\u5c81\u7684\u5a74\u513f \u4e00\u6837\u3002 \u3000\u3000\u201c\u4eba\u547d\u730e\u6237\u201d\u5446\u671b\u7740\u9762\u524d\u75db\u54ed\u7684\u4eba\uff0c\u988f\u4e0b\u7684\u767d\u987b\uff0c\u4e5f\u50cf\u662f\u79cb\u98ce\u4e2d\u7684\u67af\u53f6\u4e00\u822c\u98a4\u6296\u4e86\u8d77 \u6765\u3002 \u3000\u3000\u4ed6\u76ee\u5149\u672a\u66fe\u7247\u523b\u79fb\u52a8\uff0c\u7136\u540e\u2026\u2026 \u3000\u3000\u4ed6\u76ee\u4e2d\u7efd\u51fa\u4e86\u4e24\u6ef4\u6cea\u73e0\u3002 \u3000\u3000\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u53cc\u7709\u7d27\u76b1\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u4ed6\u6b64\u523b\u5df2\u4e86\u89e3\u4e86\u201c\u534e\u5c71\u94f6\u9e64\u201d\u65b9\u624d\u90a3\u4e00\u756a\u8a00\u8bed\uff0c\u4e0d \u8fc7\u662f\u4e3a\u4e86\u5f15\u5f00\u522b\u4eba\u7684\u6ce8\u610f\u4e4b\u529b\u3002 \u3000\u3000\u7136\u540e\u4ed6\u4e00\u5251\u706d\u53bb\u706f\u5149\uff0c\u4f7f\u5f97\u8fd9\u201c\u4e71\u53d1\u5934\u9640\u201d\u80fd\u4e58\u4e71\u95ea\u81f3\u5df2\u4e0d\u8ba4\u4ed6\u4e3a\u5b50\u7684\u7236\u4eb2\u9762\u524d\uff0c\u4e58\u4e71 \u63ed\u5f00\u4ed6\u7684\u9762\u5e55\u3002 \u3000\u3000\u4ed6\u6df1\u77e5\u8fd9\u7236\u5b50\u4e24\u4eba\u7684\u5e95\u7ec6\uff0c\u662f\u4ee5\uff0c\u6b64\u523b\u773c\u770b\u7740\u8fd9\u4e00\u5e55\u52a8\u4eba\u7684\u666f\u60c5\uff0c\u4e0d\u4f46\u6beb\u4e0d\u611f\u52a8\uff0c\u800c\u4e14 \u6709\u4e9b\u70e6\u607c\u3002 \u3000\u3000\u201c\u4eba\u547d\u730e\u6237\u201d\u9762\u4e0a\u7684\u6cea\u73e0\uff0c\u6e10\u6e10\u6d41\u5165\u4e86\u4ed6\u82cd\u767d\u7684\u80e1\u987b\u3002 \u3000\u3000\u201c\u4e71\u53d1\u5934\u9640\u201d\u54ed\u58f0\u5374\u4ecd\u672a\u5f80\uff0c\u53cd\u6765\u8986\u53bb\u4ed6\u8bf4\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u89c1\u6211\u2026\u2026\u201d \u3000\u3000\u201c\u4eba\u547d\u730e\u6237\u201d\u7a81\u5730\u5927\u559d\u4e00\u58f0\uff1a\u201c\u8c01\u662f\u4f60\u7684\u7239\u7239\uff01\u201d \u3000\u3000\u4ed6\u72e0\u72e0\u4e00\u8dfa\u811a\uff0c\u8f6c\u8eab\u800c\u884c\uff0c\u201c\u6e05\u98ce\u5251\u201d\u6731\u767d\u7fbd\u3002\u201c\u534e\u5c71\u94f6\u9e64\u201d\u9f50\u5730\u7eb5\u8eab\u4e00\u8dc3\uff0c\u6321\u4f4f\u4e86\u4ed6 \u7684\u53bb\u8def\u3002\u201c\u6e05\u98ce\u5251\u201d\u6731\u767d\u7fbd\u542b\u7b11\u9053\uff1a\u201c\u7236\u5b50\u4e4b\u60c5\uff0c\u5176\u6df1\u5982\u6d77\uff0c\u9601\u4e0b\u4f55\u5fc5\u7edd\u60c5\u592a\u751a\u8c03\u201d\u4eba\u547d\u730e \u6237\u201d\u5389\u53f1\u4e00\u58f0\uff1a\u201c\u591a\u7ba1\u95f2\u4e8b\uff01\u201d \u3000\u3000\u5355\u638c\u659c\u626c\uff0c\u5530\u5730\u4e00\u58f0\uff0c\u51fb\u5411\u6731\u767d\u7fbd\u7684\u80f8\u819b\u3002 \u3000\u3000\u6731\u767d\u7fbd\u4ecd\u7136\u9762\u542b\u5fae\u7b11\uff0c\u8eab\u8eaf\u4e00\u4fa7\uff0c\u54ea\u77e5\u201c\u4eba\u547d\u730e\u6237\u201d\u638c\u5230\u4e2d\u9014\uff0c\u7a81\u7136\u53d8\u638c\u4e3a\u6307\uff0c\u624b\u8155\u4e00 \u626d\uff0c\u75be\u70b9\u6731\u767d\u7fbd\u201c\u80a9\u4e95\u201d\u5927\u7a74\u3002 \u3000\u3000\u201c\u534e\u5c71\u94f6\u9e64\u201d\u542b\u7b11\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u4f60\u8fd9\u662f\u4f55\u82e6\uff1f\u201d \u3000\u3000\u4ed6\u505a\u51fa\u529d\u67b6\u7684\u59ff\u6001\uff0c\u4f38\u624b\u963b\u62e6\uff0c\u4f46\u624b\u638c\u6709\u610f\u65e0\u610f\u95f4\uff0c\u5374\u6293\u5411\u201c\u4eba\u547d\u730e\u6237\u201d\u65f6\u95f4\u7684\u201c\u66f2 \u6c60\u201d\u5927\u7a74\u3002 \u3000\u3000\u201c\u4eba\u547d\u730e\u6237\u201d\u76ee\u5149\u5982\u5203\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u62e7\u8eab\u9519\u6b65\uff0c\u53d8\u62db\u53d1\u62db\uff0c\u201c\u4e71\u53d1\u5934\u9640\u201d\u5374\u5df2\u98de\u8eab\u6251\u4e86 \u8fc7\u6765\uff0c\u54ed\u558a\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u8981\u6740\uff0c\u5c31\u6740\u4e86\u6211\u5427\uff01\u201d \u3000\u3000\u4e00\u628a\u62b1\u4f4f\u4e86\u4ed6\u7239\u7239\u7684\u53cc\u817f\uff0c\u518d\u4e5f\u4e0d\u80af\u653e\u5f00\u3002 \u3000\u3000\u201c\u4eba\u547d\u730e\u6237\u201d\u76ee\u5149\u4ecd\u662f\u9510\u5229\u5982\u5203\uff0c\u4f46\u8eab\u8eaf\u5374\u4e5f\u4e0d\u518d\u52a8\u5f39\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u5c31\u6740\u4e86\u4f60\u53c8\u600e \u6837\uff1f\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u4ef0\u5929\u72c2\u7b11\u8d77\u6765\uff0c\u7b11\u58f0\u4e2d\u5145\u6ee1\u60b2\u6fc0\u4e4b\u60c5\uff0c\u72c2\u7b11\u7740\u9053\uff1a\u201c\u4eca\u65e5\u7edd\u6ca1\u6709\u59d3\u4ec7\u7684\u518d\u6765\u591a\u7ba1 \u95f2\u4e8b\u4e86\u5427\uff1f\u201d \u3000\u3000\u8bdd\u58f0\u4e4b\u4e2d\uff0c\u4ed6\u7acb\u638c\u5982\u5200\uff0c\u5530\u5730\u4e00\u638c\uff0c\u5f53\u5934\u5411\u201c\u4e71\u53d1\u5934\u9640\u201d\u51fb\u4e0b\u3002 \u3000\u3000\u7fa4\u8c6a\u5fcd\u4e0d\u4f4f\u4ff1\u90fd\u53d1\u51fa\u4e00\u58f0\u60ca\u547c\uff0c\u53ea\u89c1\u4ed6\u67af\u7626\u7684\u624b\u638c\uff0c\u5df2\u89e6\u7740\u4e86\u90a3\u4e00\u5934\u4e71\u53d1\uff0c\u5374\u518d\u4e5f\u65e0\u6cd5 \u51fb\u4e0b\uff01 \u3000\u3000\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6c6a\u5144\uff01\u5f80\u4e8b\u4ff1\u5df2\u5316\u4e3a\u4e91\u70df\uff0c\u4f60\u4e0d\u5982\u5fd8\u6000\u4e86\u5427\uff01\u201d \u3000\u3000\u201c\u4eba\u547d\u730e\u6237\u201d\u72c2\u7b11\u53c8\u8d77\uff1a\u201c\u5fd8\u6000\u2026\u2026\u54c8\u54c8\u5fd8\u6000\u2026\u2026\u201d \u3000\u3000\u4ed6\u75db\u54ed\u4f3c\u7684\u72c2\u7b11\uff0c\u542c\u5f97\u4eba\u4eba\u5e95\u90fd\u4e0d\u7981\u5347\u51fa\u4e00\u9635\u5bd2\u610f\u3002 \u3000\u3000\u53ea\u542c\u4ed6\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u6211\u4e3a\u4e86\u8fd9\u4e0d\u8096\u7684\u9006\u5b50\uff0c\u65ad\u9001\u4e86\u4e00\u751f\u7684\u4e8b\u4e1a\uff0c\u65ad\u9001\u4e86\u4e00\u6761\u624b\u81c2\uff0c\u5728\u5927 \u6f20\u98ce\u96ea\u4e4b\u4e2d\uff0c\u82e6\u82e6\u594b\u6597\u4e8c\u5341\u5e74\uff0c\u5982\u4eca\u7adf\u6709\u4eba\u53eb\u6211\u5fd8\u6000\uff1f\u201d \u3000\u3000\u5239\u90a3\u4e4b\u95f4\uff0c\u4e8c\u5341\u5e74\u7684\u5f80\u4e8b\uff0c\u4f3c\u4e4e\u53c8\u81ea\u4ed6\u5fc3\u5934\u5347\u8d77\u2026\u2026 \u3000\u3000\u4ed6\u8fd8\u6e05\u695a\u5730\u8bb0\u5f97\uff0c\u90a3\u4e00\u5f20\u5fae\u5e26\u8f7b\u8511\u4e0e\u538c\u5026\u7684\u9762\u5bb9\uff0c\u90a3\u6ee1\u542b\u5bf9\u4eba\u751f\u5632\u5f04\u7684\u773c\u795e\u2026\u2026 \u3000\u3000\u8fd8\u6709\u90a3\u51b0\u51b7\u7684\u8bed\u58f0\uff1a\u201c\u4eba\u547d\u53d7\u4e4b\u4e8e\u5929\uff0c\u4f60\u7eb5\u7136\u662f\u4ed6\u7684\u7236\u4eb2\uff0c\u4e5f\u6ca1\u6709\u6743\u5229\u4f24\u6b8b\u4ed6\u7684\u6027\u547d\uff0c \u4f60\u65ad\u53bb\u4ed6\u4e00\u6761\u624b\u81c2\uff0c\u6211\u4e5f\u8981\u65ad\u53bb\u4f60\u4e00\u6761\u624b\u81c2\uff0c\u4f60\u5728\u4ed6\u9762\u4e0a\u780d\u4e86\u4e00\u5200\uff0c\u6211\u4e5f\u8981\u5728\u4f60\u9762\u4e0a\u780d\u4e0a\u4e00 \u5200\uff0c\u8fd9\u5c31\u662f\u7ed9\u4f60\u7684\u6559\u8bad\uff0c\u4e16\u4e0a\u6240\u6709\u7684\u4eba\uff0c\u7edd\u65e0\u4e00\u4eba\u80fd\u53ea\u56e0\u81ea\u5df1\u7684\u559c\u6012\uff0c\u522b\u65e0\u5176\u4ed6\u539f\u56e0\uff0c\u4fbf\u8981 \u968f\u610f\u4f24\u6b8b\u53e6\u4e00\u4eba\u7684\u8eab\u4f53\u6027\u547d\uff01\u201d \u3000\u3000\u4ed6\u53f3\u81c2\u4f3c\u4e4e\u53c8\u89c9\u5fae\u5fae\u4e00\u51c9\uff0c\u5f53\u65f6\u90a3\u4e00\u9635\u5200\u950b\u8fc7\u4f53\u7684\u611f\u89c9\u4e0e\u523a\u6fc0\uff0c\u76f4\u5230\u6d77\u67af\u77f3\u70c2\uff0c\u4ed6\u4e5f\u4e0d \u4f1a\u5fd8\u8bb0\uff01 \u3000\u3000\u4ed6\u8bb0\u5f97\u5c31\u5728\u81ea\u5df1\u75db\u82e6\u5730\u8f97\u8f6c\u547b\u541f\u5728\u5730\u4e0a\u65f6\uff0c\u4ed6\u513f\u5b50\u5374\u8ddf\u7740\u90a3\u59d3\u4ec7\u7684\u72c2\u5954\u800c\u53bb\uff0c\u4ed6\u547b\u541f\u7740 \u53d1\u4e0b\u6bd2\u8a93\uff0c\u603b\u6709\u4e00\u5929\u8981\u62a5\u590d\u4eca\u65e5\u7684\u4ec7\u6068\uff01 \u3000\u3000\u201c\u62a5\u590d\u2026\u2026\u62a5\u590d\u2026\u2026\u201d \u3000\u3000\u4ed6\u7a81\u7136\u5927\u559d\u4e00\u58f0\uff1a\u201c\u4f60\u82e5\u8981\u518d\u8ba4\u6211\u4e3a\u7236\uff0c\u9664\u975e\u4f60\u4e5f\u53bb\u5212\u5f00\u90a3\u4ec7\u72ec\u4e4b\u5b50\u7684\u9762\u76ee\uff0c\u6316\u53bb\u4ed6\u7684 \u773c\u775b\uff0c\u5272\u4e0b\u4ed6\u7684\u624b\u81c2\uff0c\u7136\u540e\u4f60\u518d\u6765\u89c1\u6211\u3002\u201d \u3000\u3000\u72ec\u81c2\u632f\u5904\uff0c\u8038\u80a9\u4e00\u8dc3\uff0c\u632f\u8d77\u90a3\u5bbd\u5927\u7684\u98ce\u6c05\uff0c\u6709\u5982\u82cd\u9e70\u822c\u63a0\u51fa\u5385\u53bb\u3002 \u3000\u3000\u201c\u4e71\u53d1\u5934\u9640\u201d\u72c2\u547c\u4e00\u58f0\uff1a\u201c\u7239\u7239\uff01\u201d \u3000\u3000\u559d\u58f0\u672a\u4e86\uff0c\u4ed6\u4fbf\u5df2\u7ffb\u8eab\u8ffd\u51fa\uff0c\u832b\u832b\u7684\u591c\u8272\uff0c\u77ac\u773c\u95f4\u4fbf\u5df1\u5c06\u4ed6\u4e24\u4eba\u7684\u8eab\u5f62\u541e\u6ca1\uff0c\u5374\u4e0d\u77e5\u9053 \u7236\u5b50\u4e24\u4eba\u95f4\u7684\u6069\u6028\u60c5\u4ec7\u5230\u4f55\u65e5\u624d\u80fd\u4e86\u7ed3\uff1f\u8be5\u5982\u4f55\u624d\u80fd\u4e86\u7ed3\u3001\u66f4\u4e0d\u77e5\u8fd9\u7236\u5b50\u4e24\u4eba\uff0c\u4e0e\u4ec7\u72ec\u7236\u5b50 \u4e24\u4eba\u4e4b\u95f4\u7684\u4ec7\u6028\uff0c\u76f4\u5230\u4f55\u65e5\u4f55\u65f6\u624d\u80fd\u4e86\u65ad\uff1f \u3000\u3000\u201c\u534e\u5c71\u94f6\u9e64\u201d\u76ee\u5149\u5782\u843d\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u59dc\u6842\u4e4b\u6027\uff0c\u8001\u800c\u5f25\u8fa3\uff0c\u60f3\u4e0d\u5230\u2018\u795e\u67aa\uff0c\u6c6a\u9c81\u5e73\u8fd9\u822c \u5e74\u7eaa\uff0c\u5374\u4ecd\u662f\u5982\u6b64\u66b4\u8e81\u7684\u813e\u6c14\uff0c\u5176\u5b9e\u2026\u2026\u5509\u2026\u2026\u201d\u4ed6\u6c89\u58f0\u4e00\u53f9\uff0c\u76ee\u5149\u56db\u626b\uff0c\u63a5\u9053\uff1a\u201c\u5728\u5ea7\u4e2d \u7684\u4eba\uff0c\u4e0e\u90a3\u4ec7\u72ec\u6709\u4ec7\u7684\uff0c\u53c8\u4f55\u6b62\u4ed6\u7236\u5b50\u4e24\u4eba\u800c\u5df2\u3002\u201d \u3000\u3000\u6bdb\u81ec\u9762\u6c89\u5982\u6c34\uff0c\u7f13\u7f13\u9894\u9996\uff0c\u7a0b\u9a79\u3001\u6f58\u4f65\u5bf9\u671b\u4e00\u773c\u3002 \u3000\u3000\u90a3\u201c\u95ea\u7535\u795e\u5200\u201d\u6731\u5b50\u660e\u7684\u9762\u4e0a\uff0c\u5374\u9732\u51fa\u4e86\u4e00\u79cd\u5947\u8be1\u7684\u51b7\u7b11\u3002 \u3000\u3000\u6bdb\u6587\u742a\u7b56\u9a6c\u72c2\u5954\uff0c\u4f46\u201c\u7f2a\u6587\u201d\u7684\u8eab\u5f62\u5374\u8d8a\u6765\u8d8a\u8fdc\uff0c\u72c2\u5954\u7684\u6012\u9a6c\uff0c\u5954\u8dd1\u7adf\u4ecd\u4e0d\u5982\u201c\u7f2a\u6587\u201d \u7684\u8eab\u5f62\u8fc5\u5feb\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u53ea\u542c\u8eab\u540e\u7684\u9a6c\u8e44\u58f0\u8d8a\u6765\u8d8a\u8fdc\uff0c\u8eab\u5f62\u4e00\u8f6c\uff0c\u7a81\u5730\u5411\u5de6\u9762\u7684\u4e00\u4e2a\u6697\u6797\u5954\u53bb\uff01\u7a7f\u8fc7\u6811 \u6797\uff0c\u4e00\u5ea7\u7cbe\u5de7\u7684\u5e84\u9662\u9759\u9759\u5730\u6d78\u6d74\u5728\u591c\u8272\u91cc\uff0c\u4ed6\u5fae\u4e00\u7eb5\u8eab\uff0c\u6025\u63a0\u5165\u5e84\uff0c\u811a\u5c16\u65b9\u81ea\u4e00\u70b9\u5730\u9762\uff0c\u4fbf \u5df2\u6c89\u58f0\u559d\u9053\uff1a\u201c\u6765\u4eba\uff01\u201d \u3000\u3000\u5ead\u9662\u5bc2\u5bc2\uff0c\u6f2b\u65e0\u56de\u5e94\uff0c\u201c\u7f2a\u6587\u201d\u8038\u8eab\u63a0\u5165\u5ead\u5802\uff0c\u53ea\u89c1\u4e00\u76cf\u6cb9\u706f\uff0c\u95ea\u52a8\u5bc2\u5bde\u7684\u706b\u5149\uff0c\u7167\u7740 \u8fd9\u5bc2\u5bde\u7684\u5385\u5802\u2014\u2014\u5385\u4e0a\u4e00\u65e0\u4eba\u8ff9\uff0c\u5374\u6709\u4e00\u5f20\u5c0f\u5c0f\u7684\u7eb8\u7b3a\uff0c\u88ab\u538b\u5728\u94dc\u706f\u4e0b\u9762\uff0c\u201c\u7f2a\u6587\u201d\u53d6\u6765\u4e00 \u770b\uff0c\u53ea\u89c1\u4e0a\u9762\u5b57\u8ff9\u5be5\u5be5\uff0c\u5199\u7684\u662f\uff1a\u201c\u516c\u5b50\uff0c\u6211\u4eec\u5949\u5927\u54e5\u4e4b\u547d\uff0c\u4e0d\u80fd\u518d\u4f8d\u5019\u516c\u5b50\u4e86\u3002\u201d \u3000\u3000\u4e0b\u9762\u7684\u5177\u540d\uff0c\u662f\u201c\u5feb\u9a6c\u201d\u7a0b\u4e03\u3001\u201c\u4e03\u7a8d\u201d\u738b\u5e73\u4e0e\u5f20\u4e00\u6876\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u53cc\u7709\u4e00\u76b1\uff0c\u84e6\u5730\uff0c\u4e00\u9635\u6c89\u91cd\u7684\u8db3\u6b65\u58f0\u7f13\u7f13\u81ea\u5185\u5802\u54cd\u8d77\uff0c\u4e00\u58f0\u63a5\u7740\u4e00\u58f0\uff0c\u7f13\u7f13\u5730 \u8d70\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u591c\u8272\u6df1\u6c89\uff0c\u8fd9\u8db3\u97f3\u542c\u6765\u4efd\u5916\u89c9\u5f97\u53ef\u6016\uff0c\u201c\u7f2a\u6587\u201d\u6c89\u58f0\u9053\uff1a\u201c\u8c01\uff1f\u201d \u3000\u3000\u95e8\u5e18\u4e00\u542f\uff0c\u4e00\u4e2a\u8eab\u5f62\u50f5\u6728\uff0c\u9762\u5e26\u5200\u75d5\u7684\u6c49\u5b50\uff0c\u624b\u91cc\u4e3e\u7740\u4e00\u6839\u60e8\u767d\u8272\u7684\u8721\u70db\uff0c\u50f5\u6728\u5730\u8d70\u4e86 \u8fdb\u6765\u2014\u2014\u4ed6\u8d6b\u7136\u7adf\u4e5f\u662f\u8fd8\u9b42\uff01 \u3000\u3000\u60e8\u767d\u8272\u7684\u70db\u706b\uff0c\u7167\u7740\u4ed6\u60e8\u767d\u8272\u7684\u9762\u76ee\uff0c\u50f5\u6728\u5730\u5411\u201c\u7f2a\u6587\u201d\u5fae\u5fae\u4e00\u7b11\uff0c\u8c01\u4e5f\u731c\u4e0d\u900f\u4ed6\u7b11\u5bb9 \u4e2d\u6709\u4ec0\u4e48\u610f\u601d\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u5fc3\u5934\u5374\u4e0d\u7981\u4e3a\u4e4b\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4f60\u56de\u6765\u4e86\uff1f\u90a3\u5177\u5c38\u8eab\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd8\u9b42\u201d\u76ee\u5149\u7a81\u5730\u53d8\u5f97\u5341\u5206\u832b\u7136\uff0c\u7f13\u7f13\u6447\u4e86\u6447\u5934\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4f60\u53ef\u662f\u4ece\u6765\u672a\u66fe\u51fa\u53bb\uff1f\u201d \u3000\u3000\u201c\u8fd8\u9b42\u201d\u7f13\u7f13\u70b9\u4e86\u70b9\u5934\uff0c\u76ee\u5149\u671b\u5411\u5385\u5916\u9ed1\u6c89\u6c89\u7684\u5929\u8272\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4ed6\u4eec\u90fd\u8d70\u4e86\uff0c\u53ea\u6709\u6211 \u5728\u8fd9\u91cc\u3002\u201d\u8bed\u58f0\u5636\u54d1\uff0c\u97f3\u8282\u50f5\u6728\uff0c\u4e0d\u5e26\u4efb\u4f55\u60c5\u611f\uff0c\u542c\u6765\u4e5f\u4eff\u4f5b\u81ea\u575f\u5893\u4e2d\u53d1\u51fa\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u53cc\u7709\u4e00\u76b1\uff0c\u540e\u9000\u4e09\u6b65\uff0c\u6c89\u91cd\u5730\u5750\u4e86\u4e0b\u6765\uff0c\u6697\u6697\u81ea\u8bed\uff1a\u201c\u4f60\u65e2\u6ca1\u6709\u51fa\u53bb\uff0c\u65b9\u624d\u90a3 \u4e00\u4eba\u53c8\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u4ed6\u62ac\u8d77\u76ee\u5149\uff0c\u4ed4\u7ec6\u7aef\u7965\u7740\u201c\u8fd8\u9b42\u201d\u7684\u9762\u5bb9\uff0c\u4efb\u4f55\u4eba\u89c1\u5230\u8fd9\u6837\u7684\u9762\u5bb9\uff0c\u90fd\u5fcd\u4e0d\u4f4f\u4f1a\u4e3a\u4e4b\u6697 \u6697\u53f9\u606f\u3002 \u3000\u3000\u90a3\u662f\u4e00\u5f20\u5b8c\u5168\u4e0d\u4f3c\u5c5e\u4e8e\u751f\u4eba\u7684\u9762\u5bb9\uff0c\u9762\u4e0a\u6240\u6709\u7684\u808c\u8089\uff0c\u90fd\u5df2\u50f5\u6728\u5f97\u4e0d\u80fd\u6709\u4efb\u4f55\u53d8\u5316\uff0c\u518d \u52a0\u4e0a\u90a3\u4e00\u9053\u4e11\u6076\u7684\u5200\u75a4\uff0c\u6728\u7136\u7684\u76ee\u5149\uff0c\u6728\u7136\u7684\u795e\u8272\uff0c\u6728\u7136\u7684\u884c\u52a8\u2026\u2026 \u3000\u3000\u201c\u7f2a\u6587\u201d\u6697\u6697\u5fd6\u9053\uff1a\u201c\u82e5\u6709\u4eba\u8981\u6613\u5bb9\u6210\u4ed6\u7684\u6a21\u6837\uff0c\u90a3\u5f53\u771f\u662f\u518d\u5bb9\u6613\u4e0d\u8fc7\uff0c\u53ea\u8981\u8eab\u6750\u4e0e\u4ed6 \u957f\u5f97\u8fd1\u4f3c\u5c31\u53ef\u4ee5\u4e86\uff0c\u800c\u4ed6\u7684\u8eab\u6750\uff0c\u5374\u53c8\u662f\u6781\u4e3a\u666e\u901a\u7684\uff0c\u53ea\u662f\u2026\u2026\u65b9\u624d\u90a3\u4e00\u4e2a\u201d\u8fd8\u9b42\u201d\uff0c\u5374\u53c8 \u662f\u8c01\u4e54\u88c5\u800c\u6210\u7684\u5462\uff1f\u201d \u3000\u3000\u4ed6\u4e0d\u65ad\u601d\u7d22\u7740\uff0c\u7a81\u542c\u5385\u5916\u4e00\u58f0\u5a07\u547c\uff1a\u201c\u4ed6\u2026\u2026\u4ed6\u4e5f\u5728\u8fd9\u91cc\uff01\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u4e00\u60ca\uff0c\u8f6c\u8eab\u671b\u53bb\uff0c\u53ea\u89c1\u6bdb\u6587\u742a\u4e91\u9b13\u5982\u96fe\uff0c\u8e0f\u7740\u660f\u9ec4\u7684\u706f\u5149\uff0c\u7f13\u7f13\u8d70\u4e86\u8fdb\u6765\uff0c\u4e00 \u53cc\u660e\u4eae\u7684\u773c\u775b\u91cc\u6ee1\u542b\u60ca\u8bb6\u7684\u795e\u8272\uff0c\u5446\u5446\u5730\u51dd\u6ce8\u7740\u201c\u8fd8\u9b42\u201d\uff0c\u7a81\u5730\u8f6c\u8fc7\u76ee\u5149\uff0c\u9762\u5411\u201c\u7f2a\u6587\u201d\u7f13 \u7f13\u9053\uff1a\u201c\u4f60\u5230\u5e95\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u96be\u9053\u4e0d\u8ba4\u5f97\u6211\u4e48\uff1f\u201d \u3000\u3000\u6bdb\u6587\u742a\u76ee\u5149\u4e0d\u77ac\uff0c\u9053\uff1a\u201c\u6211\u8ba4\u8bc6\u7684\u4f60\uff0c\u53ea\u662f\u4f2a\u88c5\u51fa\u6765\u7684\u4f60\uff0c\u6211\u2026\u2026\u6211\u2026\u2026\u201d \u3000\u3000\u5979\u51b0\u51b7\u800c\u575a\u5b9a\u7684\u773c\u6ce2\uff0c\u7a81\u7136\u8ff7\u8361\u4e86\u8d77\u6765\uff0c\u8361\u6f3e\u51fa\u4e00\u7247\u6676\u83b9\u7684\u6cea\u5149\uff0c\u5979\u8eab\u8eaf\u4e5f\u5f00\u59cb\u8f7b\u5fae\u7684 \u98a4\u6296\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u6211\u5168\u5fc3\u5168\u610f\u2026\u2026\u90fd\u2026\u2026\u90fd\u7ed9\u4e86\u4f60\uff0c\u5374\u8fde\u4f60\u7adf\u662f\u8c01\u90fd\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u773c\u5e18\u5782\u4e0b\uff0c\u6cea\u73e0\u4e5f\u8ddf\u7740\u5782\u843d\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u5fc3\u4e2d\u5341\u9635\u4fa7\u7136\uff0c\u9762\u4e0a\u5374\u4ecd\u5fae\u7b11\u9053\uff1a\u201c\u6211\u5c31\u662f\u6211\uff0c\u4f60\u672a\u514d\u60f3\u5f97\u5927\u591a\u4e86\u3002\u201d \u3000\u3000\u6bdb\u6587\u742a\u4f4e\u6ce3\u7740\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u518d\u9a97\u6211\u4e86\uff0c\u4efb\u4f55\u4eba\u90fd\u80fd\u7792\u4f4f\u81ea\u5df1\u7684\u5fc3\u4e8b\uff0c\u4f46\u4e16\u754c\u4e0a\u9664\u4e86\u6b7b\u4eba \u4e4b\u5916\uff0c\u6709\u8c01\u80fd\u5b8c\u5168\u63a7\u5236\u81ea\u5df1\u7684\u76ee\u5149\uff0c\u6709\u8c01\u80fd\u4f7f\u81ea\u5df1\u9762\u4e0a\u7684\u808c\u8089\u53d8\u6210\u548c\u6ce5\u571f\u77f3\u5934\u4f3c\u7684\uff0c\u5c06\u81ea\u5df1 \u5fc3\u91cc\u7684\u60c5\u611f\u5b8c\u5168\u9690\u85cf\uff1f\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u5fc3\u5934\u7a81\u5730\u4e00\u52a8\uff1a\u201c\u4e16\u4e0a\u9664\u4e86\u6b7b\u4eba\u4e4b\u5916\uff0c\u6709\u8c01\u80fd\u4f7f\u81ea\u5df1\u9762\u4e0a\u7684\u808c\u8089\u53d8\u5f97\u548c\u6ce5\u571f\u77f3 \u5934\u4e00\u6837\u2026\u2026\u201d \u3000\u3000\u4ed6\u7a81\u5730\u5927\u559d\u4e00\u58f0\uff0c\u957f\u8eab\u800c\u8d77\uff0c\u9053\uff1a\u201c\u6709\u7684\uff0c\u90a3\u4eba\u9762\u4e0a\u82e5\u662f\u6234\u4e86\u4eba\u76ae\u9762\u5177\uff0c\u4ed6\u9762\u4e0a\u7684\u808c\u8089 \u4fbf\u4e5f\u4e0d\u4f1a\u52a8\u4e86\uff0c\u5c31\u50cf\u662f\u6b7b\u4eba\u4e00\u6837\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u58f0\u4e2d\uff0c\u76ee\u5149\u4e00\u8f6c\uff0c\u7b14\u76f4\u5730\u671b\u5411\u201c\u8fd8\u9b42\u201d\u3002 \u3000\u3000\u6bdb\u6587\u742a\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8bed\u58f0\u672a\u4e86\uff0c\u53ea\u542c\u201c\u94db\u201d\u5730\u4e00\u58f0\uff0c\u94dc\u706f\u843d\u5730\uff0c\u706f\u5149\u9aa4\u6697\u3002 \u3000\u3000\u201c\u7f2a\u6587\u201d\u5927\u559d\u4e00\u58f0\uff1a\u201c\u4f60\u5f80\u54ea\u91cc\u53bb\uff01\u201d \u3000\u3000\u53ea\u542c\u9ed1\u6697\u4e2d\u4e00\u4eba\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u59d3\u4ec7\u7684\uff0c\u4f60\u8fd8\u662f\u4e0a\u4e86\u6211\u7684\u5f53\u4e86\uff01\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u5fc3\u5934\u4e00\u9707\uff0c\u6025\u9000\u4e09\u6b65\uff0c\u8f7b\u8f7b\u63a0\u5230\u5899\u89d2\u3002 \u3000\u3000\u6bdb\u6587\u742a\u60ca\u547c\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u771f\u7684\u662f\u4ec7\u72ec\u7684\u540e\u4eba\uff1f\u201d \u3000\u3000\u9ed1\u6697\u4e2d\u53c8\u662f\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u5c31\u662f\u4ec7\u72ec\u7684\u513f\u5b50\uff0c\u4f60\u4e0d\u8fd8\u4e0d\u6b7b\u5fc3\u4e48\uff1f\u201d \u3000\u3000\u8bed\u58f0\u5c16\u9510\u51b7\u524a\uff0c\u7adf\u4e0d\u4f3c\u7537\u5b50\u58f0\u97f3\u3002 \u3000\u3000\u6bdb\u6587\u742a\u8eab\u5b50\u4e00\u98a4\uff0c\u9053\uff1a\u201c\u5e08\u2026\u2026\u5e08\u59d0\uff0c\u662f\u4f60\u4e48\uff1f\u201d \u3000\u3000\u201c\u7f2a\u6587\u201d\u60ca\u547c\u4e00\u58f0\uff1a\u201c\u6155\u5bb9\u501f\u751f\uff01\u201d \u3000\u3000\u591c\u8272\u4fb5\u5165\u4e86\u5385\u5802\uff0c\u5927\u5385\u4e2d\u5f00\u59cb\u53ef\u4ee5\u5206\u8fa8\u5bf9\u65b9\u6726\u80e7\u6a21\u7cca\u7684\u4eba\u5f71\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u6761\u4eba\u5f71\u7b14\u76f4\u5730\u7ad9\u5728\u7a97\u524d\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u5c31\u662f\u6155\u5bb9\u60dc\u751f\uff01\u5e08\u59b9\uff0c\u5b88\u4f4f\u5385\u95e8\uff0c \u4e0d\u8981\u8ba9\u4ed6\u9003\u51fa\u53bb\uff01\u201d \u3000\u3000\u5979\u8bed\u58f0\u5fae\u987f\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u59d3\u4ec7\u7684\uff0c\u4f60\u81ea\u8ba4\u806a\u660e\uff0c\u5176\u5b9e\u5374\u662f\u4e2a\u50bb\u5b50\uff0c\u4f60\u8981\u62a5\u4ec7\uff0c\u5c31\u8be5\u7528\u5802 \u5802\u6b63\u6b63\u7684\u6cd5\u5b50\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u9a97\u6211\u7684\u5e08\u59b9\uff0c\u4e16\u4e0a\u6700\u53ef\u6068\u7684\u4eba\uff0c\u5c31\u662f\u6b3a\u9a97\u5973\u5b69\u5b50\u60c5\u611f\u7684\u4eba\uff0c\u6211\u5e08 \u59b9\u662f\u8fd9\u4e48\u7eaf\u6d01\uff0c\u4f60\u7adf\u5fcd\u5fc3\u9a97\u5979\uff01\u201d \u3000\u3000\u6bdb\u6587\u742a\u54c0\u547c\u4e00\u58f0\uff0c\u60b2\u6ce3\u9053\uff1a\u201c\u5e08\u59d0\uff0c\u5e08\u59d0\uff0c\u6211\u2026\u2026\u6211\u2026\u2026\u201d\u6ee1\u7736\u60c5\u6cea\uff0c\u7c0c\u7c0c\u6d41\u4e0b\u3002 \u3000\u3000\u6155\u5bb9\u60dc\u751f\u9053\uff1a\u201c\u4e0d\u8981\u52a8\uff0c\u7ad9\u5728\u90a3\u91cc\uff01\u201d \u3000\u3000\u5979\u63a5\u7740\u9053\uff1a\u201c\u59d3\u4ec7\u7684\uff0c\u6211\u65e9\u5c31\u770b\u51fa\u4f60\u6ca1\u6709\u5b89\u7740\u597d\u5fc3\uff0c\u53ea\u53ef\u60dc\u6ca1\u6709\u6cd5\u5b50\u63ed\u7a7f\u4f60\uff0c\u4f46\u6211\u773c\u89c1 \u5e08\u59b9\u5979\u65e5\u6e10\u6194\u60b4\uff0c\u5374\u53c8\u4e0d\u80fd\u4e0d\u7ba1\uff0c\u6211\u60f3\u6765\u60f3\u53bb\uff0c\u77e5\u9053\u4f60\u82e5\u662f\u8981\u5411\u6bdb\u5bb6\u7684\u4eba\u590d\u4ec7\uff0c\u5fc5\u5b9a\u8981\u627e\u6bdb \u5bb6\u4eba\u7684\u628a\u67c4\uff0c\u53ea\u8981\u662f\u5bf9\u2018\u4e03\u5251\u4e09\u97ad\u2019\u4e0d\u5229\u7684\u4e8b\uff0c\u4f60\u4e00\u5b9a\u90fd\u4f1a\u5343\u65b9\u767e\u8ba1\u5730\u53bb\u628a\u5b83\u641c\u5bfb\u51fa\u6765\u7684\uff0c \u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u5979\u51b7\u7b11\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u5341\u51e0\u5e74\u524d\uff0c\u6211\u8fd8\u662f\u5c0f\u5b69\u5b50\u7684\u65f6\u5019\uff0c\u665a\u4e0a\u5ffd\u7136\u6709\u4e00\u6ee1\u8eab\u9c9c\u8840\u7684\u5927 \u6c49\uff0c\u95ef\u5230\u6211\u5bb6\u91cc\uff0c\u90a3\u4eba\u5c31\u662f\u2018\u95ea\u7535\u795e\u5200\u2019\u6731\u5b50\u660e\uff0c\u4ed6\u5728\u4e34\u6b7b\u524d\uff0c\u8bf4\u51fa\u4e86\u90a3\u4ef6\u4e8b\uff0c\u6211\u548c\u5988\u5988\u628a \u4ed6\u846c\u4e86\uff0c\u540e\u6765\u88ab\u6069\u5e08\u6536\u5f52\u95e8\u4e0b\u3002\u201d \u3000\u3000\u201c\u8fd9\u5341\u51e0\u5e74\uff0c\u6211\u4e00\u76f4\u628a\u8fd9\u4ef6\u4e8b\u5fd8\u4e86\uff0c\u76f4\u5230\u89c1\u7740\u4f60\uff0c\u6211\u60f3\uff0c\u4f60\u82e5\u662f\u6bdb\u5bb6\u7684\u4ec7\u4eba\uff0c\u4e00\u5b9a\u4f1a\u4e50 \u610f\u77e5\u9053\u8fd9\u4ef6\u4e8b\uff0c\u4e8e\u662f\u6211\u5c31\u5316\u88c5\u6210\u8fd9\u4e2a\u6837\u5b50\uff0c\u6545\u610f\u8ba9\u4f60\u627e\u7740\u6211\uff0c\u4f60\u5f00\u59cb\u4e0d\u4fe1\uff0c\u4f46\u8c03\u67e5\u4e86\u4e4b\u540e\uff0c \u53d1\u73b0\u5341\u4f59\u5e74\u524d\u679c\u7136\u66fe\u7ecf\u53d1\u751f\u8fc7\u8fd9\u4ef6\u4e8b\uff0c\u4e0d\u7531\u5f97\u4f60\u4e0d\u4fe1\uff0c\u563f\u563f\uff0c\u4e8e\u662f\u4f60\u8fd9\u806a\u660e\u4eba\u5c31\u7ec8\u4e8e\u88ab\u6211\u9a97 \u4e86\u3002\u201d \u3000\u3000\u5979\u51b7\u7b11\u7740\u63a5\u53e3\u9053\uff1a\u201c\u53ef\u7b11\u4f60\u8fd8\u7ed9\u6211\u8d77\u4e86\u2018\u8fd8\u9b42\uff0c\u8fd9\u4e2a\u540d\u5b57\uff0c\u4f60\u5374\u4e0d\u60f3\u60f3\uff0c\u4e16\u4e0a\u54ea\u6709\u8fd8\u9b42 \u7684\u4eba\uff0c\u2019\u95ea\u7535\u795e\u5200\uff0c\u6b64\u523b\u8eba\u5728\u68fa\u6750\u91cc\uff0c\u53ea\u6015\u8fde\u9aa8\u5934\u90fd\u70c2\u4e86\uff0c\u4f60\u8fd8\u81ea\u9e23\u5f97\u610f\uff0c\u6211\u89c1\u4e86\u4f60\u90a3\u4ed8\u6837 \u5b50\uff0c\u51e0\u6b21\u4e09\u756a\u8981\u52a8\u624b\u6740\u4f60\uff0c\u82e5\u4e0d\u662f\u6211\u7b49\u7740\u5e08\u59b9\u5979\u6765\uff0c\u53ea\u6015\u4f60\u65e9\u5df2\u6b7b\u4e86\u51e0\u5341\u6b21\u4e86\u3002\u201d \u3000\u3000\u6bdb\u6587\u742a\u54ed\u6ce3\u4e4b\u58f0\u672a\u4f4f\uff0c\u201c\u7f2a\u6587\u201d\u2014\u2014\u4ec7\u6055\u989d\u4e0a\u4e0d\u7981\u6ccc\u51fa\u4e86\u51b7\u6c57\u3002 \u3000\u3000\u53ea\u542c\u6155\u5bb9\u60dc\u751f\u53c8\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u5e08\u59b9\u63d0\u9192\u4f60\uff0c\u4e16\u754c\u4e0a\u7edd\u4e0d\u4f1a\u6709\u8138\u4e0a\u808c\u8089\u5b8c\u5168\u6b7b\u4e86\u7684\u6d3b\u4eba\uff0c \u4f60\u8fd8\u8499\u5728\u9f13\u91cc\u3002\u544a\u8bc9\u4f60\uff0c\u806a\u660e\u4eba\uff0c\u6211\u73b0\u5728\u5bf9\u4f60\u8bf4\u51fa\u8fd9\u4e9b\u8bdd\uff0c\u5c31\u662f\u8981\u544a\u8bc9\u4f60\uff0c\u4e16\u754c\u4e0a\u7edd\u4e0d\u4f1a\u6709 \u53ef\u4ee5\u628a\u4efb\u4f55\u4eba\u90fd\u9a97\u8fc7\u7684\u806a\u660e\u4eba\uff0c\u5c31\u597d\u50cf\u4e16\u754c\u4e0a\u4e5f\u7edd\u4e0d\u4f1a\u6709\u50cf\u2018\u8fd8\u9b42\uff0c\u90a3\u6837\u7684\u2019\u6d3b\u6b7b\u4eba\u2019\u4e00 \u6837\uff0c\u6211\u8bdd\u8bf4\u5b8c\u4e86\uff0c\u4f60\u53ef\u6709\u4ec0\u4e48\u8bdd\u8bf4\uff1f\u201d \u3000\u3000\u4ec7\u6055\u9ed8\u7136\u534a\u664c\uff0c\u7a81\u5730\u4ef0\u5929\u5927\u7b11\u8d77\u6765\uff0c\u9053\uff1a\u201c\u54ea\u6709\u2018\u8fd8\u9b42\uff0c\uff1f\u54ea\u6709\u806a\u660e\u4eba\uff1f\u6211\u8d77\u5148\u53ea\u60f3 \u5230\u2019\u8fd8\u9b42\u2019\u90a3\u6837\u7684\u9762\u8c8c\uff0c\u4eba\u4eba\u4ff1\u53ef\u4e54\u88c5\uff0c\u5374\u6ca1\u6709\u60f3\u901a\u8fd9\u5176\u4e2d\u7684\u9053\u7406\u3002\u201d \u3000\u3000\u6155\u5bb9\u60dc\u751f\u51b7\u51b7\u9053\uff1a\u201c\u4e0d\u9519\uff01\u2018\u8fd8\u9b42\uff0c\u90a3\u6837\u7684\u9762\u8c8c\uff0c\u4eba\u4eba\u4ff1\u53ef\u4e54\u88c5\uff0c\u8fd9\u539f\u56e0\u662f\u56e0\u4e3a\u2019\u8fd8 \u9b42\u2019\u672c\u6765\u4e5f\u5c31\u662f\u4e54\u88c5\u51fa\u6765\u7684\uff01\u201d \u3000\u3000\u4ec7\u6055\u7b11\u58f0\u4e00\u987f\uff0c\u9053\uff1a\u201c\u6b64\u523b\u6211\u53ea\u95ee\u4f60\u4e00\u53e5\uff0c\u65b9\u624d\u5728\u90a3\u2018\u7075\u9690\u2019\u5bfa\u524d\uff0c\u4f60\u4e3a\u4f55\u8fd8\u8981\u4ee3\u6211\u53d7 \u8fc7\uff0c\u5c06\u90a3\u7a0b\u67ab\u7684\u5c38\u8eab\u62ac\u8d70\uff1f\u201d \u3000\u3000\u6155\u5bb9\u60dc\u751f\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u65b9\u624d\u8c01\u53bb\u8fc7\u2018\u7075\u9690\u5bfa\uff0c\uff1f\u201d\u4ec7\u6055\u5fc3\u4e2d\u4e0d\u7981\u53c8\u662f\u4e00\u60ca\uff0c\u5fd6\u9053\uff1a \u201c\u65e2\u4e0d\u662f\u5979\uff01\u65b9\u624d\u90a3\u2018\u8fd8\u9b42\u2019\u53c8\u662f\u8c01\u4e54\u88c5\u7684\u5462\uff1f\u201d \u3000\u3000\u53ea\u542c\u6155\u5bb9\u60dc\u751f\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u7684\u8bdd\u53ef\u8bf4\u5b8c\u4e86\u3002\u201d \u3000\u3000\u4ec7\u6055\u9ed8\u7136\u4e0d\u7b54\u3002 \u3000\u3000\u6155\u5bb9\u60dc\u751f\u9053\uff1a\u201c\u4ed6\u7684\u8bdd\u5df2\u8bf4\u5b8c\u4e86\uff0c\u5e08\u59b9\uff0c\u4f60\u600e\u5730\u8fd8\u4e0d\u52a8\u624b\uff1f\u201d \u3000\u3000\u6bdb\u6587\u742a\u5782\u9996\u4f4e\u4f4d\uff0c\u4eff\u4f5b\u6ca1\u6709\u542c\u5230\u5979\u7684\u8bdd\u3002 \u3000\u3000\u6155\u5bb9\u60dc\u751f\u5389\u58f0\u9053\uff1a\u201c\u4f60\u96be\u9053\u6ca1\u6709\u542c\u5230\u6211\u7684\u8bdd\u4e48\uff1f\u8fd9\u5c31\u662f\u9a97\u53d6\u4e86\u4f60\u7684\u5fc3\u7684\u574f\u4eba\uff01\u8fd9\u5c31\u662f\u8981 \u6740\u6b7b\u4f60\u7239\u7239\u7684\u4ec7\u4eba\uff01\u201d \u3000\u3000\u6bdb\u6587\u742a\u970d\u7136\u62ac\u8d77\u5934\u6765\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u53ef\u662f\u771f\u7684\u8981\u9a97\u6211\u4e48\uff1f\u4f60\u2026\u2026\u4f60\u5bf9\u6211\u53ef\u662f\u6ca1\u6709 \u4e00\u4e1d\u4e00\u6beb\u771f\u5fc3\u2026\u2026\u4f60\u2026\u2026\u4f60\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u62bd\u6410\uff0c\u518d\u4e5f\u8bf4\u4e0d\u4e0b\u53bb\uff01 \u3000\u3000\u8fd9\u75f4\u60c5\u7684\u5c11\u5973\uff0c\u7adf\u662f\u5982\u6b64\u75f4\u60c5\u3002 \u3000\u3000\u6155\u5bb9\u60dc\u751f\u6068\u58f0\u9053\uff1a\u201c\u5e08\u59b9\uff0c\u4f60\u600e\u4f1a\u53d8\u6210\u8fd9\u6837\uff0c\u4ed6\u4e0d\u5728\u9a97\u4f60\uff0c\u8c01\u5728\u9a97\u4f60\uff1f\u201d \u3000\u3000\u6bdb\u6587\u742a\u63a9\u9762\u4f4d\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u2026\u2026\u201d \u3000\u3000\u4ec7\u6055\u7a81\u5730\u957f\u53f9\u4e00\u58f0\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u6211\u662f\u9a97\u4f60\u7684\uff01\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u7f13\u6162\uff0c\u4e00\u5b57\u4e00\u5b57\u4ed6\u8bf4\u5c06\u51fa\u6765\uff0c\u6bcf\u4e00\u4e2a\u5b57\uff0c\u90fd\u50cf\u662f\u4e00\u67c4\u5343\u65a4\u94c1\u9524\uff0c\u51fb\u788e\u4e86\u6bdb\u6587\u742a\u7684 \u5fc3\u3002 \u3000\u3000\u5979\u54c0\u547c\u4e00\u58f0\uff0c\u4e00\u6b65\u51b2\u5230\u4ec7\u6055\u8eab\u524d\u3002 \u3000\u3000\u4ec7\u6055\u53cc\u62f3\u7d27\u63e1\uff0c\u6728\u7136\u4e0d\u52a8\uff0c\u660e\u4eae\u7684\u773c\u775b\uff0c\u5728\u9ed1\u6697\u4e2d\u95ea\u95ea\u53d1\u5149\uff0c\u5c31\u50cf\u662f\u591c\u7a7a\u4e2d\u4e00\u53cc\u4e0d\u77e5\u540d \u7684\u660e\u661f\u3002 \u3000\u3000\u6bdb\u6587\u742a\u773c\u6ce2\u4e00\u8f6c\uff0c\u63a5\u89e6\u5230\u8fd9\u53cc\u773c\u775b\uff0c\u7a81\u53c8\u54c0\u547c\u4e00\u58f0\uff0c\u63a9\u9762\u72c2\u5954\u4e86\u51fa\u53bb\uff0c\u5954\u5411\u90a3\u65e0\u8fb9\u7684\u591c \u8272\u3002 \u3000\u3000\u6155\u5bb9\u60dc\u751f\u60ca\u547c\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5e08\u59b9\uff0c\u4f60\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4f46\u6bdb\u6587\u742a\u7684\u8eab\u5f62\u5374\u5df2\u8d70\u5f97\u4e0d\u77e5\u53bb\u5411\u4e86\u3002 \u3000\u3000\u591c\u8272\u5e7d\u9eef\uff0c\u51b7\u98ce\u81ea\u5ead\u56ed\u4e2d\u76f4\u5439\u8fdb\u6765\u3002 \u3000\u3000\u6155\u5bb9\u60dc\u751f\u970d\u7136\u8f6c\u8fc7\u8eab\uff0c\u9762\u5bf9\u4ec7\u6055\uff0c\u6068\u58f0\u9053\uff1a\u201c\u4f60\u770b\u5230\u4e86\u4e48\uff01\u8fd9\u5c31\u662f\u88ab\u4f60\u9a97\u53bb\u5168\u90e8\u60c5\u611f\u7684 \u5973\u5b50\uff0c\u4f60\u90a3\u4e48\u4f24\u5bb3\u4e86\u5979\uff0c\u5979\u5374\u76f4\u5230\u6b64\u523b\u8fd8\u4e0d\u5fcd\u4f24\u5bb3\u4f60\uff01\u201d \u3000\u3000\u4ec7\u6055\u4ecd\u7136\u6728\u7acb\u4e0d\u52a8\uff0c\u4f46\u76ee\u5149\u5374\u4e0d\u7981\u9eef\u6de1\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u6155\u5bb9\u60dc\u751f\u9053\uff1a\u201c\u5979\u8fd9\u6837\u5bf9\u4f60\uff0c\u4f60\u82e5\u8fd8\u6709\u4e00\u4efd\u826f\u5fc3\uff0c\u5c31\u4e0d\u8be5\u518d\u53bb\u5bb3\u5979\uff0c\u4f60\u82e5\u8fd8\u6709\u4e00\u4efd\u826f \u5fc3\uff0c\u5c31\u4ece\u6b64\u4e0d\u8981\u518d\u89c1\u5979\uff0c\u5979\u7239\u7239\u867d\u7136\u2026\u201d \u3000\u3000\u4ec7\u6055\u7f13\u7f13\u622a\u53e3\u9053\uff1a\u201c\u7236\u4ec7\u4e0d\u5171\u6234\u5929\uff01\u201d \u3000\u3000\u8bed\u58f0\u8fdf\u7f13\u4f4e\u6c89\uff0c\u4f46\u8bed\u6c14\u5374\u662f\u65a9\u9489\u65ad\u94c1\u3002 \u3000\u3000\u6155\u5bb9\u60dc\u751f\u559d\u9053\uff1a\u201c\u4f60\u8fd8\u8981\u590d\u4ec7\uff0c\u4f60\u8fd8\u8981\u518d\u9a97\u5979\u7684\u5fc3\uff1f\u201d \u3000\u3000\u4ec7\u6055\u80f8\u819b\u4e00\u633a\uff0c\u9053\uff1a\u201c\u6b63\u662f\uff01\u201d \u3000\u3000\u8bed\u58f0\u65b9\u4e86\uff0c\u6155\u5bb9\u60dc\u751f\u8eab\u5f62\u5df1\u5c55\uff0c\u4e00\u638c\u5288\u5411\u4ed6\u80f8\u819b\uff01 \u3000\u3000\u4ec7\u6055\u5fae\u4e00\u62e7\u8170\uff0c\u6155\u5bb9\u60dc\u751f\u5de6\u638c\u5df2\u81f3\uff0c\u53f3\u638c\u659c\u659c\u5212\u4e86\u4e2a\u534a\u5708\uff0c\u4ea6\u5df2\u56de\u51fb\u8fc7\u6765\uff0c\u4e00\u51fb\u5de6\u8170\uff0c \u4e00\u51fb\u53f3\u808b\u3002 \u3000\u3000\u5979\u53cc\u638c\u5939\u51fb\uff0c\u638c\u98ce\u6fc0\u5389\uff0c\u7adf\u5c06\u4ec7\u6055\u903c\u4eba\u5899\u89d2\u3002 \u3000\u3000\u54ea\u77e5\u4ec7\u6055\u53cc\u80a9\u5fae\u8038\uff0c\u8eab\u5b50\u7a81\u7136\u6e38\u9c7c\u822c\u81ea\u5899\u4e0a\u76f4\u6ed1\u4e0a\u53bb\uff0c\u4ed6\u6b64\u523b\u53cc\u8db3\u53ea\u8981\u5fae\u5fae\u4e00\u62ac\uff0c\u4f7f\u53ef \u76f4\u8e22\u6155\u5bb9\u60dc\u751f\u7684\u9762\u76ee\uff0c\u4f46\u662f\u4ed6\u5374\u7adf\u7136\u6ca1\u6709\u4e1d\u6beb\u8fd8\u51fb\u4e4b\u610f\uff0c\u53cc\u65f6\u4e00\u70b9\u5899\u89d2\uff0c\u500f\u7136\u6a2a\u98de\u4e00\u4e08\u3002 \u3000\u3000\u6155\u5bb9\u60dc\u751f\u8f7b\u53f1\u4e00\u58f0\uff0c\u62e7\u8170\u7529\u638c\uff0c\u53cc\u638c\u76f4\u649e\u4ec7\u6055\u80cc\u810a\u3002 \u3000\u3000\u4ec7\u6055\u5934\u4e5f\u4e0d\u56de\uff0c\u8eab\u8eaf\u9661\u7136\u6a2a\u79fb\u4e09\u5c3a\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u6155\u5bb9\u60dc\u751f\uff0c\u6211\u5df2\u8ba9\u4e86\u4f60\u4e09\u62db\uff01\u201d \u3000\u3000\u6155\u5bb9\u60dc\u751f\u51b7\u7b11\u9053\uff1a\u201c\u8c01\u8981\u4f60\u8ba9\uff01\u201d \u3000\u3000\u53cc\u638c\u7ffb\u98de\uff0c\u5239\u90a3\u95f4\u8fde\u653b\u4e03\u638c\uff0c\u53ea\u542c\u638c\u98ce\u864e\u864e\uff0c\u7adf\u5c06\u4ec7\u6055\u7684\u8eab\u5f62\u7b3c\u7f69\u5728\u5979\u8fd9\u4e00\u7247\u7f24\u7eb7\u5982\u96e8 \u7684\u638c\u5f71\u4e4b\u4e0b\u3002 \u3000\u3000\u5979\u62db\u5f0f\u72e0\u8fa3\uff0c\u624b\u4e0b\u7edd\u4e0d\u5bb9\u60c5\uff0c\u638c\u638c\u4ff1\u662f\u62cd\u5411\u4ec7\u6055\u8981\u5bb3\u4e4b\u5904\uff0c\u6bcf\u4e00\u62db\u6bcf\u4e00\u638c\u4ff1\u90fd\u8db3\u4ee5\u7f6e\u4eba \u6b7b\u547d\u3002 \u3000\u3000\u4ec7\u6055\u8eab\u5f62\u672a\u8f6c\uff0c\u7adf\u4ecd\u662f\u80cc\u5bf9\u7740\u5979\u3002\u6155\u5bb9\u60dc\u751f\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u7eb5\u4e0d\u56de\u624b\uff0c\u4eca\u65e5\u6211\u4e5f\u8981\u5c06\u4f60\u6bd9 \u5728\u638c\u4e0b\uff01\u201d \u3000\u3000\u54ea\u77e5\u5979\u8bed\u58f0\u672a\u4e86\uff0c\u4ec7\u6055\u53cc\u638c\u7a81\u5730\u53cd\u5411\u76f4\u51fb\u800c\u51fa\uff0c\u6155\u5bb9\u60dc\u751f\u518d\xb7361\xb7\u4e5f\u4e0d\u4f1a\u60f3\u5230\u4ed6\u5728\u5982 \u6b64\u90e8\u4f4d\u8fd8\u80fd\u53d1\u638c\uff0c\u53ea\u89c9\u8155\u95f4\u4e00\u9ebb\uff0c\u7adf\u88ab\u4ec7\u6055\u7684\u638c\u7f18\u626b\u4e2d\uff0c\u970e\u773c\u95f4\u5979\u4e00\u53cc\u624b\u638c\uff0c\u7adf\u518d\u4e5f\u65e0\u6cd5\u62ac \u5c06\u8d77\u6765\u3002 \u3000\u3000\u8981\u77e5\u6155\u5bb9\u60dc\u751f\u6b66\u529f\u9ad8\u7edd\uff0c\u82e5\u975e\u4ec7\u6055\u5728\u6700\u6700\u4e0d\u53ef\u80fd\u53d1\u62db\u7684\u65f6\u95f4\u90e8\u4f4d\u4e2d\u51fa\u638c\uff0c\u518d\u4e5f\u65e0\u6cd5\u4e00\u638c \u4fbf\u5c06\u4e4b\u51fb\u51fa\u3002 \u3000\u3000\u8fd9\u6b63\u662f\u6b66\u7ecf\u4e2d\u51fa\u5176\u4e0d\u610f\uff0c\u653b\u5176\u4e0d\u5907\uff0c\u6700\u6700\u4e0a\u4e58\u7684\u6b66\u529f\u5fc3\u6cd5\u3002 \u3000\u3000\u6155\u5bb9\u60dc\u751f\u5fc3\u5934\u4e00\u51db\uff0c\u53ea\u542c\u4ec7\u6055\u51b7\u51b7\u9053\uff1a\u201c\u6155\u5bb9\u60dc\u751f\uff0c\u4eca\u65e5\u6211\u9976\u4f60\u4e00\u547d\uff0c\u4f60\u53ef\u8981\u8bb0\u7740 \u4e86\uff01\u201d \u3000\u3000\u8bf4\u5230\u6700\u540e\u4e00\u5b57\uff0c\u4ed6\u8eab\u5f62\u65e9\u5df2\u8fdc\u5728\u5341\u4e08\u5f00\u5916\u3002 \u3000\u3000\u6155\u5bb9\u60dc\u751f\u5446\u5446\u5730\u6115\u4e86\u534a\u664c\uff0c\u8eab\u5f62\u5fae\u5fae\u4e00\u6447\uff0c\u540e\u9000\u4e09\u6b65\uff0c\u201c\u5657\u201d\u5730\u5750\u5230\u6905\u4e0a\uff0c\u53e3\u4e2d\u5583\u5583 \u9053\uff1a\u201c\u5e08\u59b9\u2026\u2026\u5e08\u59b9\uff0c\u4f60\u7239\u7239\u6709\u4e86\u8fd9\u6837\u7684\u4ec7\u4eba\uff0c\u5509\u2026\u2026\u201d \u3000\u3000\u5979\u53ea\u89c9\u5fc3\u5934\u6c89\u91cd\uff0c\u56db\u80a2\u65e0\u529b\uff0c\u4f3c\u4e4e\u8fde\u8bdd\u90fd\u65e0\u529b\u518d\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u4ec7\u6055\u8eab\u5f62\u5982\u7535\uff0c\u63a0\u51fa\u9662\u5899\uff0c\u53ea\u542c\u8eab\u540e\u4e00\u9635\u8863\u8882\u5e26\u98ce\u4e4b\u58f0\uff0c\u968f\u4e4b\u800c\u6765\uff0c\u4ed6\u5927\u559d\u4e00\u58f0\uff0c\u5389\u58f0 \u9053\uff1a\u201c\u6155\u5bb9\u60dc\u751f\uff0c\u4f60\u8fd8\u4e0d\u8ba4\u8f93\u4e48\uff1f\u201d \u3000\u3000\u6b63\u5f85\u7ffb\u8eab\uff0c\u51cc\u7a7a\u51fb\u638c\u3002 \u3000\u3000\u54ea\u77e5\u8eab\u540e\u4e4b\u4eba\u7a81\u5730\u6c89\u58f0\u4e00\u53f9\uff0c\u9053\uff1a\u201c\u516c\u5b50\uff0c\u662f\u6211\uff01\u201d \u3000\u3000\u4ec7\u6055\u771f\u6c14\u4e00\u61c8\uff0c\u786c\u751f\u751f\u5c06\u638c\u52bf\u632b\u4f4f\uff0c\u8eab\u8eaf\u4e5f\u968f\u4e4b\u98d8\u843d\u5730\u4e0a\uff0c\u7ffb\u8eab\u671b\u53bb\uff0c\u53ea\u89c1\u81ea\u5899\u95f4\u8dc3\u843d \u7684\uff0c\u7adf\u662f\u90a3\u201c\u4e5d\u8db3\u795e\u86db\u201d\u6881\u4e0a\u4eba\u3002 \u3000\u3000\u4ed6\u6b66\u529f\u867d\u4e0d\u751a\u9ad8\uff0c\u8f7b\u529f\u5374\u5999\u7edd\u4e00\u65f6\uff0c\u6709\u5982\u843d\u53f6\u822c\u98d8\u5728\u5899\u89d2\uff0c\u4ec7\u6055\u7cbe\u795e\u4e00\u632f\uff0c\u4e00\u628a\u63e1\u4f4f\u4ed6 \u7684\u80a9\u8180\uff0c\u559c\u9053\uff1a\u201c\u6881\u5927\u54e5\uff0c\u4f60\u600e\u5730\u6765\u4e86\uff1f\u201d \u3000\u3000\u6881\u4e0a\u4eba\uff1a\u201c\u6211\u4e00\u76f4\u672a\u66fe\u79bb\u5f00\u6b64\u5730\uff0c\u7b49\u5019\u7740\u516c\u5b50\uff0c\u4e3a\u7684\u2026\u2026\u201d \u3000\u3000\u4ec7\u6055\u622a\u53e3\u9053\uff1a\u201c\u90a3\u201d\u5feb\u9a6c\u2019\u7a0b\u4e03\u7b49\u5f1f\u5144\uff0c\u600e\u5730\u4e0d\u544a\u800c\u522b\uff1f\u201d \u3000\u3000\u6881\u4e0a\u4eba\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u4e3a\u7684\u53ea\u662f\u8981\u544a\u8bc9\u516c\u5b50\uff0c\u5728\u4e0b\u4eca\u540e\u518d\u4e5f\u4e0d\u80fd\u4e3a\u516c\u5b50\u6548\u52b3\uff0c\u201d\u5feb \u9a6c\u2019\u7a0b\u4e03\u90a3\u5e2e\u5144\u5f1f\uff0c\u4e5f\u2026\u2026\u5509\uff01\u201d \u3000\u3000\u4ed6\u957f\u53f9\u4e00\u58f0\uff0c\u500f\u7136\u4f4f\u53e3\u3002 \u3000\u3000\u4ec7\u6055\u5446\u4e86\u4e00\u5446\uff0c\u653e\u5f00\u6881\u4e0a\u4eba\u7684\u80a9\u8180\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u8fd9\u2026\u2026\u8fd9\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6881\u4e0a\u4eba\u53f9\u9053\uff1a\u201c\u516c\u5b50\u6709\u4f4d\u4ec7\u5bb6\uff0c\u62ff\u4e86\u5728\u4e0b\u6614\u5e74\u6700\u5927\u6069\u4eba\u7684\u4e00\u4ef6\u4fe1\u7269\uff0c\u524d\u6765\u5bfb\u8bbf\u5728\u4e0b\uff0c\u8981 \u5728\u4e0b\u4e3a\u4ed6\u67e5\u51fa\u516c\u5b50\u7684\u884c\u8e2a\u3002\u201d \u3000\u3000\u4ec7\u6055\u5fc3\u5934\u4e00\u9707\uff0c\u8eab\u5f62\u540e\u9000\u4e00\u6b65\u3002 \u3000\u3000\u53ea\u542c\u6881\u4e0a\u4eba\u63a5\u53e3\u53f9\u9053\uff1a\u201c\u516c\u5b50\u8bf7\u653e\u5fc3\uff0c\u5728\u4e0b\u4e0e\u516c\u5b50\u591a\u65e5\u76f8\u5904\uff0c\u600e\u4f1a\u6cc4\u6f0f\u516c\u5b50\u7684\u673a\u5bc6\uff0c\u4f46 \u4e3a\u4e86\u5728\u4e0b\u6614\u5e74\u6069\u4eba\u7684\u90a3\u4ef6\u4fe1\u7269\uff0c\u5509\u2026\u2026\u2026\u4ed6\u957f\u53f9\u4e00\u58f0\uff0c\u6539\u53e3\u9053\uff1a\u201c\u5728\u4e0b\u5b9e\u5728\u5de6\u53f3\u4e3a\u96be\uff0c\u60f3\u6765 \u60f3\u53bb\uff0c\u53ea\u6709\u2026\u201d \u3000\u3000\u4ec7\u6055\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53ea\u6709\u8c01\u4e5f\u4e0d\u5e2e\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u6881\u4e0a\u4eba\u5782\u9996\u9053\uff1a\u201c\u5728\u4e0b\u5904\u5883\u4e4b\u96be\uff0c\u516c\u5b50\u4f60\u60f3\u5fc5\u4e5f\u80fd\u8c05\u89e3\u3002\u201d \u7b2c\u4e8c\u5341\u516b\u7ae0>> \u53e4\u9f99\u300a\u6e58\u5983\u5251\u300b \u7b2c\u4e8c\u5341\u516b\u7ae0 \u3000\u3000\u4ec7\u6055\u6c89\u9ed8\u534a\u664c\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u6881\u5144\u4f60\u679c\u7136\u4e0d\u6127\u662f\u4e2a\u4ec1\u4e49\u541b\u5b50\uff0c\u4e8b\u5230\u5982\u6b64\uff0c\u8fd8\u4e0d\u80af\u7792\u6211\uff0c\u6881 \u5144\uff0c\u4f60\u4eca\u65e5\u5c06\u6b64\u4e8b\u660e\u544a\u4e8e\u6211\uff0c\u6211\u5df2\u5341\u5206\u611f\u6fc0\u4e86\uff0c\u600e\u4f1a\u6709\u76f8\u602a\u4e4b\u610f\uff1f\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u8bda\u6073\uff0c\u6881\u4e0a\u4eba\u5fc3\u4e2d\u5374\u6108\u89c9\u4e0d\u5b89\u3002 \u3000\u3000\u53ea\u89c1\u4ec7\u6055\u7a81\u53c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5176\u5b9e\u81ea\u4eca\u65e5\u8d77\uff0c\u5728\u4e0b\u884c\u8e2a\uff0c\u518d\u4e5f\u6bcb\u5eb8\u7792\u4eba\u4e86\uff0c\u6881\u5144\u5bf9\u90a3\u4f4d\u670b \u53cb\uff0c\u4e5f\u4e0d\u5fc5\u518d\u4e3a\u96be\uff0c\u53ea\u7ba1\u5c06\u5728\u4e0b\u884c\u8e2a\uff0c\u544a\u8bc9\u4ed6\u597d\u4e86\u3002\u201d \u3000\u3000\u6881\u4e0a\u4eba\u795e\u8272\u4e00\u9635\u60ed\u6127\uff0c\u9ed8\u7136\u534a\u664c\uff0c\u9053\uff1a\u201c\u516c\u5b50\u90a3\u4ec7\u4eba\uff0c\u6765\u81ea\u2018\u6606\u4ed1\u2019\uff0c\u800c\u4e14\u8fd8\u662f\u5f53\u4eca\u6606 \u4ed1\u638c\u95e8\u4eba\u7684\u5e08\u5f1f\uff0c\u4e00\u8eab\u6b66\u529f\uff0c\u5df2\u53ef\u7b97\u5f97\u4e0a\u662f\u6b66\u6797\u4e2d\u9876\u5c16\u9ad8\u624b\u3002\u201d \u3000\u3000\u4ec7\u6055\u53cc\u7709\u5fae\u76b1\uff0c\u9053\uff1a\u201c\u6606\u4ed1\u95e8\u4eba\uff1f\u201d \u3000\u3000\u6881\u4e0a\u4eba\u63a5\u9053\uff1a\u201c\u6b64\u4eba\u672a\u5165\u2018\u6606\u4ed1\u2019\u4e4b\u524d\uff0c\u5df2\u662f\u6b66\u6797\u4e2d\u4e00\u6761\u597d\u624b\uff0c\u4eba\u79f0\u6ca1\u7fbd\u7bad\u2019\u8d75\u56fd\u660e\uff0c \u5341\u4f59\u5e74\u524d\uff0c\u4e0e\u4ee4\u5c0a\u2026\u2026\u201d \u3000\u3000\u4ec7\u6055\u5251\u7709\u4e00\u626c\uff0c\u9053\uff1a\u201c\u5148\u7236\u7684\u4ec7\u4eba\uff0c\u4fbf\u662f\u5728\u4e0b\u7684\u4ec7\u4eba\uff01\u201d \u3000\u3000\u6881\u4e0a\u4eba\u53c8\u81ea\u9ed8\u7136\u534a\u664c\uff0c\u5782\u9996\u9053\uff1a\u201c\u516c\u5b50\u4eca\u540e\u884c\u8e2a\u65e2\u9732\uff0c\u5fc5\u5b9a\u5f3a\u4ec7\u73af\u4f3a\uff0c\u51e1\u4e8b\u4ff1\u8981\u5c0f\u5fc3 \u4e86\uff0c\u5728\u4e0b\u2026\u2026\u5509\uff0c\u53ea\u6068\u4e0d\u80fd\u4e3a\u516c\u5b50\u6548\u529b\uff0c\u53ea\u6709\u9ed8\u7977\u516c\u5b50\u5e73\u5b89\u2026\u2026\u201d \u3000\u3000\u4ed6\u5446\u4e86\u534a\u664c\uff0c\u4f3c\u4e4e\u8fd8\u60f3\u8bf4\u4ec0\u4e48\uff0c\u4f46\u7ec8\u4e8e\u53ea\u662f\u9eef\u7136\u4e00\u63d6\uff0c\u6084\u7136\u800c\u53bb\u3002 \u3000\u3000\u4ec7\u6055\u65e0\u8a00\u5730\u9ed8\u9001\u4ed6\u7684\u8eab\u5f71\u6d88\u5931\uff0c\u5fc3\u5934\u7a81\u89c9\u4e00\u9635\u8427\u7d22\u3002 \u3000\u3000\u56db\u91ce\u7a7a\u5bc2\uff0c\u5929\u5730\u95f4\u4eff\u4f5b\u53ea\u5269\u4e0b\u4e86\u4ed6\u5b64\u8eab\u4e00\u4eba\uff0c\u56db\u4e0b\u6728\u53f6\u968f\u98ce\u6447\u66f3\uff0c\u4eff\u4f5b\u90fd\u662f\u73af\u4f3a\u7740\u4ed6\u7684 \u4ec7\u4eba\u3002 \u3000\u3000\u9ed1\u6697\u4e2d\uff0c\u4ed6\u7f13\u6162\u5730\u79fb\u52a8\u8eab\u5f62\uff0c\u811a\u6b65\u6b63\u5982\u4ed6\u5fc3\u60c5\u4e00\u822c\u6c89\u91cd\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8d70\u4e86\u591a\u8fdc\uff0c\u4ed6\u7a81\u5730\u957f\u5578\u4e00\u58f0\uff0c\u594b\u8d77\u8eab\u5f62\uff0c\u5982\u98de\u63a0\u53bb\uff0c\u5578\u58f0\u9ad8\u4ea2\uff0c\u54cd\u5f7b\u4e91\u9704\uff0c\u4e45\u4e45 \u90fd\u4e0d\u5bc2\u706d\u3002 \u3000\u3000\u6625\u9633\u53c8\u5347\u3002 \u3000\u3000\u897f\u6e56\u4e07\u9cde\u78a7\u6ce2\uff0c\u53c8\u5f00\u59cb\u8361\u6f3e\u8d77\u7729\u76ee\u7684\u6ce2\u6d6a\u3002 \u3000\u3000\u65b9\u81f3\u6e05\u6668\uff0c\u9759\u5bc2\u7684\u6e56\u9762\u4fbf\u5df2\u98de\u626c\u8d77\u6765\uff0c\u897f\u6e56\u4e2d\u6240\u6709\u7684\u753b\u822b\u6e38\u8247\uff0c\u6b64\u523b\u5374\u5df2\u805a\u96c6\u5230\u4e00\u5904\uff0c \u805a\u96c6\u5230\u6e56\u8fb9\u3002 \u3000\u3000\u8239\u8fde\u7740\u8239\uff0c\u8fde\u7ed3\u6210\u4e00\u7247\u8239\u6d77\u3002 \u3000\u3000\u6de1\u6de1\u7684\u6e56\u98ce\u4e2d\uff0c\u6563\u53d1\u7740\u9152\u9999\u4e0e\u6c61\u81ed\u3002 \u3000\u3000\u6de1\u6de1\u7684\u98ce\u58f0\u4e2d\uff0c\u98de\u626c\u8d77\u6b22\u8c08\u4e0e\u55e4\u7b11\u3002 \u3000\u3000\u4f9d\u4f9d\u7684\u6768\u67f3\u679d\u4e0b\uff0c\u5230\u5904\u90fd\u662f\u4eba\u5934\uff0c\u5230\u5904\u90fd\u6709\u957f\u5251\u2026\u2026 \u3000\u3000\u4eca\u5929\uff0c\u6b63\u662f\u676d\u5dde\u57ce\u7684\u5927\u8c6a\uff0c\u6b66\u6797\u4e2d\u7684\u5de8\u5b50\uff0c\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u67ec\u9080\u7fa4\u96c4\uff0c\u53ec\u96c6\u5230\u897f\u6e56\u7684\u82f1\u96c4 \u4e4b\u4f1a\u3002 \u3000\u3000\u753b\u822b\u5df1\u7528\u7c97\u7d22\u6216\u94c1\u94fe\u7ed3\u8fde\u4f4f\u4e86\uff0c\u767e\u6570\u6761\u753b\u822b\uff0c\u7ed3\u6210\u4e86\u4e00\u5ea7\u6e56\u4e0a\u7684\u884c\u5bab\uff0c\u8239\u5a18\u4eec\u5174\u594b\u800c\u53c8 \u60ca\u5947\uff0c\u4ee5\u8bb6\u5f02\u7684\u76ee\u5149\uff0c\u671b\u7740\u767b\u8239\u7684\u8c6a\u5ba2\u3002 \u3000\u3000\u4ed6\u4eec\u6709\u7684\u662f\u6162\u6b65\u800c\u767b\uff0c\u6709\u7684\u5374\u662f\u4e00\u8dc3\u800c\u4e0a\u3002 \u3000\u3000\u4ed6\u4eec\u9ad8\u58f0\u8c08\u7b11\uff0c\u5927\u676f\u996e\u9152\uff0c\u9152\u5230\u676f\u5e72\uff0c\u4eff\u4f5b\u559d\u7684\u4e0d\u662f\u9152\u800c\u662f\u8336\u4f3c\u7684\u3002 \u3000\u3000\u4ed6\u4eec\u867d\u7136\u4e5f\u7a7f\u7740\u534e\u4e3d\u7684\u957f\u886b\uff0c\u4f46\u5374\u4ecd\u63a9\u4e0d\u4f4f\u795e\u60c5\u95f4\u7684\u7c97\u6817\u9aa0\u608d\u4e4b\u6c14\uff0c\u95ea\u70c1\u7684\u76ee\u5149\uff0c\u5bbd\u9614 \u7684\u80f8\u819b\u2026\u2026 \u3000\u3000\u8239\u5a18\u4eec\u4e0d\u7981\u6697\u4e2d\u7fa1\u6155\u4e86\uff1a\u201c\u591a\u4e48\u96c4\u58ee\u9b41\u4f1f\u7684\u7537\u4eba\uff01\u201d \u3000\u3000\u5979\u4eec\u89c1\u60ef\u4e86\u7684\u662f\u6587\u5f31\u7684\u4e66\u751f\uff0c\u81c3\u80bf\u7684\u5546\u8d3e\uff0c\u7325\u7410\u7684\u5e2e\u95f2\uff0c\u5e73\u51e1\u7684\u6e38\u5ba2\uff0c\u6b65\u5c65\u8e52\u8dda\u7684\u8001\u5934 \u5b50\uff0c\u6276\u8001\u643a\u5e7c\u7684\u5c0f\u5987\u4eba\u2026\u2026 \u3000\u3000\u4eca\u65e5\uff0c\u5979\u4eec\u773c\u754c\u4e00\u65b0\uff0c\u5fc3\u91cc\u6697\u6697\u9ad8\u5174\uff0c\u5374\u4e0d\u77e5\u8fd9\u4e9b\u96c4\u58ee\u7684\u7537\u4eba\u4eec\uff0c\u968f\u65f6\u90fd\u4f1a\u4e3a\u5979\u4eec\u5e26\u6765 \u8165\u98ce\u8840\u96e8\uff0c\u968f\u65f6\u90fd\u4f1a\u5c06\u8fd9\u201c\u6d53\u6de1\u5986\u62b9\u603b\u76f8\u5b9c\u201d\u7684\u6e05\u6e05\u897f\u5b50\u6e56\u7684\u6e05\u6e05\u6e56\u6c34\uff0c\u67d3\u4e0a\u4e00\u7247\u7329\u7ea2\u7684\u8840 \u8272\uff01 \u3000\u3000\u7a81\u5730\uff0c\u6e56\u8fb9\u54cd\u8d77\u4e00\u9635\u53f7\u58f0\u3002 \u3000\u3000\u62c2\u52a8\u7684\u67f3\u679d\u4e0b\uff0c\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u3001\u201c\u5de6\u624b\u795e\u5251\u201d\u4e01\u8863\u3001\u201c\u767e\u6b65\u98de\u82b1\u201d\u6797\u7426\u7b5d\u3001\u201c\u6cb3\u6714\u53cc \u5251\u201d\u6c6a\u6c0f\u6606\u4ef2\u2026\u2026 \u3000\u3000\u8fd9\u4e00\u5e2e\u65e9\u5df2\u53f1\u54a4\u6c5f\u6e56\uff0c\u58f0\u540d\u663e\u8d6b\u7684\u8c6a\u5ba2\uff0c\u5927\u6b65\u767b\u4e0a\u6e56\u8239\u3002 \u3000\u3000\u4f46\u8fd9\u5176\u4e2d\u6700\u6700\u4ee4\u4eba\u89e6\u76ee\u7684\uff0c\u5374\u662f\u4e24\u4e2a\u795e\u91c7\u98de\u626c\uff0c\u8863\u886b\u534e\u4e3d\uff0c\u4f46\u9762\u76ee\u5728\u6c5f\u6e56\u95f4\u5374\u6781\u4e3a\u964c\u751f \u7684\u8001\u4eba\uff01 \u3000\u3000\u8fd8\u6709\u4e00\u4eba\uff0c\u66f4\u4ee4\u4eba\u6697\u4e2d\u79f0\u5f02\uff0c\u6b64\u4eba\u7adf\u662f\u4e2a\u770b\u6765\u6709\u5982\u50f5\u5c38\u7684\u6c49\u5b50\uff0c\u9762\u4e0a\u4e00\u6761\u5200\u75a4\uff0c\u5728\u9633\u5149 \u4e0b\u53d1\u7740\u7ea2\u5149\u3002 \u3000\u3000\u4f17\u8c6a\u4e0d\u7981\u5728\u6697\u4e2d\u7a83\u7a83\u79c1\u8bae\uff1a\u201c\u8fd9\u4e9b\u4eba\u662f\u8c01\uff1f\u4e3a\u4ec0\u4e48\u2018\u7075\u86c7\u2019\u6bdb\u81ec\u5bf9\u4ed6\u4eec\u5206\u5916\u7684\u5ba2\u6c14\uff1f\u201d \u3000\u3000\u6bdb\u81ec\u6ee1\u9762\u6625\u98ce\uff0c\u4e0d\u4f4f\u62b1\u62f3\uff0c\u4f46\u662f\u8fd9\u6625\u98ce\u5f97\u610f\u7684\u6b66\u6797\u5927\u6c49\uff0c\u76ee\u5149\u4e2d\u7adf\u4f3c\u4e5f\u6709\u7740\u4e00\u4efd\u6df1\u6df1\u7684 \u5fe7\u8651\u3002 \u3000\u3000\u4ed6\u4e34\u98ce\u5353\u7acb\u5728\u8239\u5934\uff0c\u76ee\u5149\u56db\u4e0b\u4e00\u626b\uff0c\u4f46\u95fb\u6ee1\u6e56\u7fa4\u8c6a\uff0c\u5ffd\u7136\u54cd\u8d77\u4e00\u7247\u91c7\u58f0\uff0c\u8fd8\u6709\u4eba\u5728\u8fdc \u5904\uff0c\u626c\u58f0\u95ee\u597d\u3002 \u3000\u3000\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u5fae\u5fae\u4e00\u7b11\uff0c\u76ee\u4e2d\u7684\u5fe7\u90c1\u4e0e\u9634\u973e\uff0c\u77ac\u773c\u95f4\u4fbf\u6362\u4f5c\u4e86\u5f97\u610f\u800c\u9a84\u505a\u7684\u5149\u91c7\uff0c\u62b1\u62f3 \u6717\u58f0\u9053\uff1a\u201c\u6bdb\u81ec\u4e8b\u70e6\u6687\u5c11\uff0c\u4e45\u672a\u4e0e\u4f17\u5bb6\u5144\u5f1f\u6b22\u805a\uff0c\u4eca\u65e5\u897f\u6e56\u6625\u98ce\u6768\u67f3\uff0c\u98ce\u5149\u4e0d\u6076\uff0c\u4f17\u5bb6\u5144\u5f1f \u4e14\u8bf7\u5148\u996e\u4e00\u676f\uff0c\u518d\u884c\u53d9\u8bdd\u2026\u2026\u201d \u3000\u3000\u72c2\u6d9b\u822c\u7684\u559d\u91c7\u638c\u58f0\u4e2d\uff0c\u4ed6\u7f13\u6b65\u9000\u56de\u8239\u8231\u3002 \u3000\u3000\u201c\u767e\u6b65\u98de\u82b1\u201d\u6797\u7426\u7b5d\u5a07\u7b11\u9053\uff1a\u201c\u6bdb\u5927\u54e5\uff0c\u5c31\u662f\u90a3\u4ec7\u72ec\u7684\u513f\u5b50\uff0c\u6b64\u523b\u5df2\u6765\u5230\u6c5f\u5357\uff0c\u4ed6\u82e5\u542c \u5230\u8fd9\u7247\u91c7\u58f0\uff0c\u4e5f\u8be5\u77e5\u96be\u800c\u9000\u4e86\u5427\uff01\u201d \u3000\u3000\u2018\u7075\u86c7\uff0c\u6bdb\u81ec\u6717\u58f0\u4e00\u7b11\uff0c\u7a81\u542c\u7a0b\u9a79\u51b7\u51b7\u9053\uff1a\u201c\u4ed6\u513f\u5b50\u82e5\u4e5f\u50cf\u4ed6\u7239\u7239\u90a3\u822c\u813e\u6c14\uff0c\u53ea\u6015\u518d\u54cd \u4e9b\u638c\u58f0\uff0c\u4e5f\u9a87\u4e0d\u5012\u4ed6\uff01\u201d\u6bdb\u81ec\u7b11\u5bb9\u7a81\u5730\u4e00\u655b\u3002 \u3000\u3000\u6f58\u4f65\u54af\u54af\u7b11\u9053\uff1a\u201c\u7eb5\u7136\u9a87\u4e0d\u5012\u4ed6\uff0c\u6709\u6211\u4e24\u4eba\u5728\u6b64\uff0c\u4ed6\u53c8\u5f53\u600e\u5730\uff1f\u201d \u3000\u3000\u2018\u7075\u86c7\u201d\u6bdb\u81ec\u5fc3\u4e2d\u5ffd\u5fe7\u5ffd\u559c\uff0c\u5f53\u771f\u662f\u98df\u4e0d\u77e5\u5473\uff0c\u5750\u4e0d\u5b89\u5e2d\uff0c\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\u2014\u2014\u53ea\u542c\u9762 \u6709\u4eba\u559d\u9053\uff1a\u201c\u5f1f\u5144\u4eec\u9152\u8db3\u996d\u9189\uff0c\u8bf7\u6bdb\u5927\u54e5\u51fa\u6765\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u53c8\u6709\u4eba\u626c\u58f0\u5927\u547c\u9053\uff1a\u201c\u6bdb\u5927\u54e5\u5bf9\u6211\u5144\u5f1f\u4eec\u5982\u6b64\u539a\u5f85\uff0c\u65e0\u8bba\u6bdb\u5927\u54e5\u6709\u4f55\u5429\u5490\uff0c\u6211\u5f1f\u5144\u4eec\u7eb5 \u7136\u8d74\u6c64\u8e48\u706b\uff0c\u4e5f\u7518\u613f\u4e3a\u6bdb\u5927\u54e5\u6548\u547d\uff01\u201d \u3000\u3000\u2018\u7075\u86c7\u201d\u6bdb\u684c\u7cbe\u795e\u4e00\u9707\uff0c\u632f\u8863\u800c\u8d77\uff0c\u6b65\u4e0a\u8239\u5934\uff0c\u5927\u58f0\u9053\uff1a\u201c\u591a\u5e74\u6765\u8499\u4f17\u5bb6\u5144\u5f1f\u539a\u7231\uff0c\u6bdb \u81ec\u5b9e\u662f\u611f\u6fc0\u4e0d\u5c3d\uff0c\u6bdb\u81ec\u4e00\u751f\u884c\u4e8b\uff0c\u867d\u7136\u591a\u6709\u5dee\u9519\uff0c\u4f46\u81ea\u95ee\u826f\u5fc3\uff0c\u59cb\u7ec8\u5bf9\u5f97\u4f4f\u670b\u53cb\uff0c\u5341\u4f59\u5e74 \u524d\uff0c\u6bdb\u67d0\u4e0d\u60dc\u5192\u9669\u9664\u53bb\u90a3\u9b54\u5934\u4ec7\u72ec\uff0c\u4e5f\u662f\u4e3a\u4e86\u6c5f\u6e56\u670b\u53cb\u4eec\u7684\u5b89\u5168\uff01\u201d \u3000\u3000\u7fa4\u8c6a\u5927\u58f0\u559d\u91c7\uff0c\u53ea\u56e0\u6bdb\u81ec\u9664\u53bb\u4ec7\u72ec\u4e4b\u4e8b\uff0c\u786e\u662f\u56db\u6d77\u95fb\u540d\u3002 \u3000\u3000\u6bdb\u81ec\u4e00\u7b11\u53c8\u9053\uff1a\u201c\u4f46\u4eca\u65e5\u90a3\u4ec7\u72ec\u7684\u540e\u4eba\uff0c\u4e5f\u5df2\u51fa\u9053\u6c5f\u6e56\uff0c\u6bdb\u81ec\u4e3a\u4e86\u5404\u4f4d\u9664\u53bb\u4ec7\u72ec\uff0c\u5404\u4f4d \u670b\u53cb\u4e5f\u8be5\u4e3a\u6bdb\u81ec\u9664\u53bb\u4ec7\u72ec\u4e4b\u5b50\uff01\u201d \u3000\u3000\u4f17\u7fa4\u8c6a\u54c4\u7136\u5e94\u9053\uff1a\u201c\u6b63\u8be5\u5982\u6b64\uff01\u201d \u3000\u3000\u6bdb\u81ec\u6717\u58f0\u5927\u7b11\u9053\uff1a\u201c\u670b\u53cb\u4eec\u5bf9\u6bdb\u81ec\u7684\u597d\u5904\uff0c\u6bdb\u81ec\u7edd\u5bf9\u4e0d\u4f1a\u5fd8\u8bb0\u2026\u201d \u3000\u3000\u8bed\u58f0\u672a\u4e86\uff0c\u7a81\u542c\u8fdc\u5904\u54cd\u8d77\u4e00\u4e2a\u5c16\u9510\u7684\u547c\u58f0\uff0c\u5927\u559d\u9053\uff1a\u201c\u6bdb\u81ec\u653e\u5c41\uff01\u201d \u3000\u3000\u7fa4\u8c6a\u8038\u7136\u4e00\u60ca\uff0c\u9f50\u5730\u8f6c\u76ee\u671b\u53bb\uff01 \u3000\u3000\u53ea\u89c1\u8fdc\u5904\u4e00\u8258\u624e\u5f69\u6e56\u8239\u7684\u8239\u7bf7\u4e0a\uff0c\u53c9\u624b\u7ad9\u7740\u4e00\u4e2a\u8eab\u6000\u516d\u7532\u7684\u5927\u809a\u5987\u4eba\uff0c\u621f\u6307\u6bdb\u81ec\u5927\u9a82 \u9053\uff1a\u201c\u4f60\u82e5\u5bf9\u5f97\u8d77\u670b\u53cb\uff0c\u4f60\u82e5\u4e0d\u4f1a\u5fd8\u8bb0\u670b\u53cb\u7684\u597d\u5904\uff0c\u7a0b\u67ab\u600e\u4f1a\u88ab\u4f60\u6740\u6b7b\uff1f\u201d\u8bed\u58f0\u6fc0\u6124\uff0c\u6ee1\u9762 \u4ff1\u662f\u6cea\u75d5\u3002 \u3000\u3000\u7fa4\u8c6a\u5927\u591a\u8ba4\u5f97\uff0c\u8fd9\u5987\u4eba\u4fbf\u662f\u4e03\u5251\u4e09\u97ad\u4e2d\u2018\u9e33\u9e2f\u53cc\u5251\u2019\u6797\u7433\uff0c\u542c\u5f97\u5979\u8fd9\u756a\u8bf4\u8bdd\uff0c\u90fd\u4e0d\u7981\u6697 \u4e2d\u60ca\u5947\u3002 \u3000\u3000\u2018\u7075\u86c7\u2019\u6bdb\u81ec\u9762\u8272\u5927\u53d8\uff0c\u8131\u53e3\u9053\uff1a\u201c\u7a0b\u67ab\u4e0e\u6211\u4e49\u5982\u5144\u5f1f\uff0c\u6211\u600e\u4f1a\u5c06\u4ed6\u6740\u6b7b\uff0c\u4f60\u2026\u2026\u201d \u3000\u3000\u6797\u7433\u4ef0\u5929\u60b2\u5636\u9053\uff1a\u201c\u4f60\u7adf\u7136\u8fd8\u6709\u8138\u8bf4\u4e0e\u7a0b\u67ab\u60c5\u5982\u5144\u5f1f\uff0c\u6211\u4e14\u95ee\u4f60\uff0c\u7a0b\u67ab\u82e5\u662f\u672a\u6b7b\uff0c\u4ed6\u6b64 \u523b\u5728\u54ea\u91cc\uff0c\u4f60\u8bf4\u4ed6\u6b64\u523b\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u6ee1\u6e56\u7fa4\u8c6a\uff0c\u5343\u767e\u9053\u76ee\u5149\uff0c\u4e00\u9f50\u671b\u5411\u6bdb\u81ec\u3002 \u3000\u3000\u6bdb\u684c\u7eb5\u662f\u4e00\u4ee3\u67ad\u96c4\uff0c\u4f46\u6b64\u523b\u9762\u5bf9\u7740\u5343\u767e\u9053\u8be2\u95ee\u7684\u76ee\u5149\uff0c\u4ed6\u5fc3\u795e\u4e5f\u672a\u514d\u6709\u4e9b\u60f6\u4e71\uff0c\u5450\u5450 \u9053\uff1a\u201c\u4ed6\u2026\u2026\u4ed6\u2026\u2026\u4e0d\u9519\uff0c\u7a0b\u5927\u54e5\u5df2\u4e0d\u5e78\u4ed9\u53bb\u4e86\uff01\u201d \u3000\u3000\u6797\u7433\u53cc\u62f3\u7d27\u63e1\uff0c\u6012\u559d\u9053\uff1a\u201c\u662f\u8c01\u6740\u6b7b\u4ed6\u7684\uff1f\u201d \u3000\u3000\u2018\u7075\u86c7\u2019\u6bdb\u81ec\u5446\u4e86\u4e00\u5446\uff0c\u534a\u664c\u672a\u66fe\u8bf4\u8bdd\uff0c\u6e56\u4e0a\u4fbf\u5df2\u54cd\u8d77\u4e00\u9635\u7a83\u7a83\u79c1\u8bae\u4e4b\u58f0\uff0c\u6709\u7684\u4eba\u5df2\u4e0d \u7981\u5728\u6697\u4e2d\u6447\u5934\u79c1\u8bed\uff1a\u201c\u7a0b\u67ab\u4e0e\u6bdb\u81ec\u90a3\u822c\u4ea4\u60c5\uff0c\u53ef\u8bf4\u662f\u751f\u6b7b\u4e0e\u5171\uff0c\u4ed6\u82e5\u771f\u7684\u662f\u88ab\u6bdb\u81ec\u6740\u6b7b\uff0c\u7075 \u86c7\u6bdb\u81ec\u4e5f\u672a\u514d\u592a\u72e0\u5fc3\u4e86\u4e9b\uff01\u201d \u3000\u3000\u7a81\u542c\u4e00\u58f0\u51b7\u7b11\uff0c\u6bdb\u81ec\u8eab\u540e\uff0c\u7f13\u6b65\u8d70\u51fa\u4e00\u4e2a\u5f62\u5bb9\u50f5\u6728\uff0c\u6709\u5982\u6b7b\u5c38\u4e00\u822c\u7684\u6c49\u5b50\uff0c\u5389\u58f0\u5927\u547c \u9053\uff1a\u201c\u7a0b\u67ab\u662f\u6211\u6740\u6b7b\u7684\uff01\u201d \u3000\u3000\u6797\u7433\u5207\u9f7f\u5927\u547c\u9053\uff1a\u201c\u4f60\u4e0e\u7a0b\u67ab\u65e0\u6028\u65e0\u4ec7\uff0c\u4e3a\u4f55\u8981\u5c06\u4ed6\u6740\u6b7b\uff1f\u201d \u3000\u3000\u201c\u8fd8\u9b42\u201d\u51b7\u51b7\u9053\uff1a\u201c\u4ed6\u5bf9\u4e0d\u8d77\u6211\u6bdb\u5927\u54e5\uff0c\u6211\u5c31\u5c06\u4ed6\u6740\u6b7b\u4e86\uff01\u201d \u3000\u3000\u7fa4\u8c6a\u7acb\u523b\u4e3a\u4e4b\u54d7\u7136\uff0c\u9f50\u5730\u6697\u5fd6\u9053\uff1a\u201c\u679c\u7136\u662f\u6bdb\u81ec\u4e3b\u4f7f\uff0c\u5c06\u7a0b\u67ab\u6740\u6b7b\u7684\uff01\u201d \u3000\u3000\u6ee1\u6e56\u7fa4\u8c6a\uff0c\u5341\u4e2d\u6709\u4e5d\u77e5\u9053\u7a0b\u67ab\u4e0e\u6bdb\u81ec\u7684\u4ea4\u60c5\uff0c\u6b64\u523b\u4e00\u542c\u6bdb\u81ec\u5bf9\u53cb\u5982\u6b64\uff0c\u4e00\u4e9b\u70ed\u5fc3\u7684\u670b \u53cb\uff0c\u4e5f\u4e0d\u7981\u5bd2\u4e86\u5fc3\u3002 \u3000\u3000\u201c\u8fd8\u9b42\u201d\u76ee\u5149\u56db\u4e0b\u4e00\u8f6c\uff0c\u63a5\u53e3\u53c8\u9053\uff1a\u201c\u5341\u4e03\u5e74\u524d\uff0c\u6211\u6bdb\u5927\u54e5\u5f00\u8bbe\u4e86\u4e00\u5bb6\u5730\u4e0b\u9556\u5c40\u2026\u2026\u201d \u3000\u3000\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u4e00\u542c\u8fd9\u201c\u95ea\u7535\u795e\u5200\u6731\u5b50\u660e\u201d\u7adf\u5728\u4f17\u76ee\u777d\u777d\u4e4b\u4e0b\uff0c\u63d0\u8d77\u4e86\u81ea\u5df1\u7684\u9690\u79c1\u4e4b\u4e8b\uff0c \u4e0d\u7981\u6012\u53f1\u4e00\u58f0\uff0c\u4e00\u638c\u63a8\u5728\u201c\u8fd8\u9b42\u201d\u80f8\u524d\uff0c\u559d\u9053\uff1a\u201c\u9000\u56de\u53bb\uff01\u201d \u3000\u3000\u201c\u8fd8\u9b42\u201d\u4eff\u4f5b\u811a\u6b65\u4e0d\u7a33\uff0c\u4e00\u8fde\u540e\u9000\u4e86\u51e0\u6b65\uff0c\u201c\u7830\u201d\u5730\u4e00\u58f0\uff0c\u4ef0\u5929\u8dcc\u5012\u5728\u8239\u8231\u91cc\uff0c\u53e3\u4e2d\u72b9 \u81ea\u5927\u547c\u9053\uff1a\u201c\u6bdb\u5927\u54e5\uff0c\u5c0f\u5f1f\u5168\u662f\u4e3a\u4e86\u4f60\uff0c\u4f60\u4e3a\u4f55\u5bf9\u5c0f\u5f1f\u5982\u6b64\uff1f\u201d \u3000\u3000\u672c\u5df2\u6709\u4e9b\u5bd2\u5fc3\u7684\u6b66\u6797\u7fa4\u8c6a\u542c\u5f97\u2018\u7075\u86c7\u2019\u6bdb\u81ec\u7adf\u5f00\u8bbe\u4e86\u6b66\u6797\u4e2d\u4eba\u6700\u6700\u4e0d\u803b\u7684\u5730\u4e0b\u9556\u5c40\uff0c\u53c8 \u4e00\u638c\u5c06\u4e00\u5fc3\u4e3a\u4ed6\u7684\u670b\u53cb\u6253\u5f97\u4ef0\u5929\u8dcc\u5012\uff0c\u4e0d\u7981\u66f4\u662f\u5fc3\u7070\uff0c\u6709\u7684\u4eba\u5df2\u5728\u6697\u4e2d\u51b7\u7b11\u6570\u58f0\uff0c\u6084\u7136\u800c \u9000\u3002 \u3000\u3000\u6bdb\u81ec\u773c\u89c1\u81ea\u5df1\u591a\u5e74\u6240\u5efa\u7684\u57fa\u4e1a\uff0c\u4eca\u65e5\u7adf\u5c06\u6bc1\u4e8e\u4e00\u65e6\uff0c\u5fc3\u4e0b\u66f4\u662f\u60f6\u6025\uff0c\u8fde\u8fde\u62b1\u62f3\uff0c\u8fde\u8fde\u5927 \u547c\u9053\uff1a\u201c\u4f17\u5bb6\u5144\u5f1f\uff0c\u5207\u5207\u4e0d\u8981\u542c\u4ed6\u4eec\u80e1\u8a00\u4e71\u8bed\u2026\u2026\u201d \u3000\u3000\u6797\u7433\u5df2\u8361\u7740\u4e00\u53ea\u8f7b\u821f\u8d76\u6765\uff0c\u55d6\u5730\u4e00\u58f0\uff0c\u8dc3\u4e0a\u8239\u5934\uff0c\u6bdb\u81ec\u53d8\u8272\u9053\uff1a\u201c\u4f60\u8981\u4f5c\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6797\u7433\u60b2\u5636\u9053\uff1a\u201c\u4f60\u65e2\u7136\u6740\u6b7b\u4ed6\uff0c\u7d22\u6027\u4e5f\u5c06\u6211\u4e00\u9f50\u6740\u6b7b\u7b97\u4e86\uff01\u201d \u3000\u3000\u5636\u58f0\u4e2d\u6025\u5730\u653b\u51fa\u6570\u62db\uff0c\u62db\u62db\u4ff1\u653b\u5411\u6bdb\u81ec\u81f4\u547d\u4e4b\u5904\uff01 \u3000\u3000\u5979\u62db\u5f0f\u867d\u7136\u51cc\u5389\uff0c\u4f46\u7a76\u7adf\u662f\u8eab\u6000\u516d\u7532\uff0c\u5df2\u5c06\u4e34\u76c6\uff0c\u811a\u6b65\u95f4\u5927\u662f\u4e0d\u4fbf\uff0c\u600e\u4f1a\u8fd8\u6709\u6614\u65e5\u7684\u5a01 \u98ce\uff1f \u3000\u3000\u6bdb\u81ec\u607c\u7f9e\u6210\u6012\uff0c\u6012\u559d\u9053\uff1a\u201c\u6cfc\u5987\uff0c\u4f60\u6562\u5728\u8fd9\u91cc\u6492\u5201\u4e48\uff1f\u201d \u3000\u3000\u53cd\u8155\u4e00\u638c\uff0c\u659c\u659c\u51fb\u5728\u6797\u7433\u80a9\u9aa8\u4e4b\u4e0a\u3002 \u3000\u3000\u6797\u7433\u60b2\u547c\u4e00\u58f0\uff0c\u4ef0\u5929\u8dcc\u5012\u5728\u8239\u677f\u4e0a\uff0c\u653e\u58f0\u75db\u54ed\u8d77\u6765\u3002 \u3000\u3000\u6c5f\u6e56\u8c6a\u58eb\uff0c\u672c\u5c31\u540c\u60c5\u5987\u4eba\u5f31\u8005\uff0c\u4f55\u51b5\u6797\u7433\u6b64\u523b\u6000\u6709\u8eab\u5b55\uff0c\u4f17\u4eba\u4e00\u89c1\u6bdb\u81ec\u7adf\u51fa\u624b\u6bb4\u6253\u5b55 \u5987\uff0c\u5fc3\u4e2d\u66f4\u662f\u4e0d\u5fff\uff0c\u867d\u7136\u4ecd\u5bf9\u6bdb\u81ec\u7684\u58f0\u5a01\u6709\u6240\u754f\u60e7\uff0c\u4f46\u5df2\u5fcd\u4e0d\u4f4f\u53d1\u51fa\u4e49\u6124\u4e0d\u5e73\u7684\u547c\u58f0\uff0c\u66f4\u6709 \u8bb8\u591a\u4eba\u6124\u7136\u62c2\u8896\u800c\u53bb\u3002 \u3000\u3000\u201c\u6cb3\u6714\u53cc\u5251\u201d\u6c6a\u6c0f\u6606\u4ef2\u65e0\u8a00\u5730\u5bf9\u671b\u4e00\u773c\uff0c\u4ed6\u4e24\u4eba\u89c1\u5230\u6bdb\u81ec\u8fd9\u822c\u60c5\u51b5\uff0c\u4e0d\u7981\u9f50\u5730\u60f3\u8d77\u4e86 \u201c\u7f2a\u6587\u201d\u7684\u8a00\u8bed\uff01 \u3000\u3000\u4e24\u4eba\u4e0d\u7ea6\u800c\u540c\u5730\u6697\u4e2d\u5fd6\u9053\uff1a\u201c\u6bdb\u81ec\u8fd1\u6765\u5982\u6b64\u72c2\u50b2\uff0c\u7eb5\u5bb9\u4ed6\u5973\u513f\u5bf9\u957f\u8f88\u65e0\u793c\uff0c\u4ed6\u6b64\u523b\u773c\u89c1 \u5df2\u662f\u4f17\u53db\u4eb2\u79bb\uff0c\u6211\u4e24\u4eba\u4f55\u4e0d\u4e58\u673a\u5c06\u4e4b\u9664\u53bb\uff01\u201d \u3000\u3000\u4e00\u5ff5\u81f3\u6b64\uff0c\u6c6a\u4e00\u9e23\u7a81\u5730\u632f\u81c2\u5927\u559d\u9053\uff1a\u201c\u7075\u86c7\u6bdb\u81ec\u9762\u5e26\u5fe0\u539a\uff0c\u5185\u85cf\u5978\u8bc8\uff0c\u6211\u7b49\u7eb5\u662f\u60c5\u4e49\u5144 \u5f1f\uff0c\u4e5f\u770b\u4e0d\u60ef\u4ed6\u5982\u6b64\u653e\u8086\u72c2\u884c\uff0c\u611a\u5f04\u5929\u4e0b\u6c5f\u6e56\u670b\u53cb\uff01\u201d \u3000\u3000\u6c6a\u4e00\u9e4f\u53cd\u8155\u62d4\u51fa\u957f\u5251\uff0c\u5389\u58f0\u9053\uff1a\u201c\u7a0b\u5927\u5ac2\uff0c\u770b\u6211\u5144\u5f1f\u4e3a\u4f60\u590d\u4ec7\uff01\u201d \u3000\u3000\u55d6\u5730\u4e00\u5251\uff0c\u76f4\u523a\u6bdb\u81ec\u5de6\u80c1\uff01 \u3000\u3000\u201c\u8fd8\u9b42\u201d\u7acb\u5728\u8239\u8231\u7684\u89d2\u843d\u91cc\uff0c\u76ee\u5149\u4e2d\u5df2\u9732\u51fa\u5f97\u610f\u7684\u795e\u91c7\uff0c\u7a0b\u9a79\u3001\u6f58\u4f65\u5bf9\u671b\u4e00\u773c\uff0c\u5634\u89d2\u4e5f \u5fae\u5fae\u6cdb\u51fa\u7b11\u610f\u3002 \u3000\u3000\u201c\u5de6\u624b\u795e\u5251\u201d\u4e01\u8863\u80a9\u5934\u4e00\u52a8\uff0c\u6b63\u5f85\u957f\u8eab\u800c\u8d77\uff0c\u5374\u88ab\u767e\u6b65\u98de\u82b1\u6797\u7426\u7b5d\u4e00\u628a\u62c9\u4f4f\uff0c\u9644\u5728\u4ed6\u8033 \u7554\u4f4e\u58f0\u9053\uff1a\u201c\u5750\u5c71\u89c2\u864e\u6597\uff0c\u591a\u4e48\u8212\u670d\uff0c\u901e\u52c7\u5f3a\u51fa\u5934\u5c31\u65e0\u8da3\u4e86\uff01\u201d \u3000\u3000\u4e01\u8863\u6014\u4e86\u4e00\u6014\uff0c\u624b\u6309\u5251\u67c4\uff0c\u7f13\u7f13\u5750\u4e86\u4e0b\u6765\uff01 \u3000\u3000\u53ea\u89c1\u6bdb\u81ec\u8eab\u5f62\u95ea\u52a8\uff0c\u907f\u5f00\u4e86\u6c6a\u4e00\u9e4f\u7684\u4e00\u8fde\u4e03\u5251\uff0c\u53e3\u4e2d\u5389\u559d\u9053\uff1a\u201c\u6c6a\u4e00\u9e4f\u4f60\u75af\u4e86\u4e48\uff1f\u201d \u3000\u3000\u6c6a\u4e00\u9e4f\u51b7\u54fc\u4e00\u58f0\uff0c\u5251\u52bf\u4e0d\u7edd\uff0c\u53c8\u662f\u4e00\u8fde\u4e09\u5251\u523a\u51fa\uff0c\u4ed6\u72ec\u81c2\u4f7f\u5251\uff0c\u5251\u8d70\u504f\u950b\uff0c\u7aef\u7684\u8f9b\u8fa3\u5df2 \u6781\uff01 \u3000\u3000\u6bdb\u684c\u8138\u8272\u94c1\u9752\uff0c\u96be\u770b\u4e4b\u6781\uff0c\u663e\u89c1\u4ed6\u5185\u5fc3\u4e5f\u6c14\u6781\u6012\u6781\uff0c\u4f46\u4ed6\u4f3c\u4e4e\u6709\u7740\u67d0\u79cd\u987e\u8651\uff0c\u800c\u4ecd\u4e0d\u613f \u4e0e\u6c6a\u4e00\u9e4f\u8fc7\u624b\u8fd8\u62db\uff0c\u8eab\u5f62\u95ea\u5904\uff0c\u53c8\u81ea\u5f80\u540e\u659c\u8ba9\u5f00\u53bb\uff0c\u6325\u624b\u4f4e\u559d\u4e00\u58f0\uff1a\u201c\u4eba\u6765\uff01\u201d \u3000\u3000\u6c6a\u4e00\u9e4f\u6325\u5251\u518d\u8fdb\uff0c\u7a81\u5730\u2014\u2014\u56db\u9053\u5bd2\u5149\uff0c\u631f\u5636\u5636\u9510\u5578\u4e4b\u58f0\uff0c\u4ea4\u5c3e\u75be\u5377\u8fc7\u6765\uff0c\u53ea\u542c\u201c\u94ee\u201d\u5730 \u4e00\u4e32\u7e41\u5bc6\u7684\u91d1\u94c1\u4ea4\u54cd\u4e4b\u58f0\u8fc7\u5904\uff0c\u6c6a\u4e00\u9e4f\u64a4\u5251\u66b4\u9000\u4e09\u5c3a\uff01 \u3000\u3000\u53ea\u89c1\u56db\u4e2a\u84dd\u888d\u9ed1\u5c65\uff0c\u624b\u6301\u957f\u5251\u7684\u4e2d\u5e74\u6c49\u5b50\uff0c\u4e00\u5b57\u6392\u5f00\uff0c\u6321\u5728\u4ed6\u8eab\u524d\uff0c\u56db\u67c4\u950b\u5229\u7684\u957f\u5251\uff0c \u5251\u5c16\u5916\u5410\uff0c\u5176\u52bf\u867d\u672a\u5c55\u52a8\uff0c\u4f46\u5df2\u5c06\u5bf9\u65b9\u8fdb\u9000\u90e8\u4f4d\uff0c\u5b8c\u5168\u5c01\u4f4f\u3002 \u3000\u3000\u8fd9\u56db\u4e2a\u84dd\u8863\u5251\u624b\uff0c\u4e00\u4e2a\u4e2a\u8083\u7136\u5c79\u7acb\uff0c\u76ee\u5149\u4e0d\u77ac\uff0c\u51dd\u6ce8\u5728\u6c6a\u4e00\u9e4f\u8eab\u4e0a\uff0c\u4eff\u4f5b\u6ce5\u5851\u6728\u96d5\u4e00 \u822c\u3002 \u3000\u3000\u6c6a\u4e00\u9e4f\u5fc3\u5934\u5fae\u51db\uff0c\u6697\u5fd6\u9053\uff1a\u201c\u6bdb\u81ec\u8fd9\u53ae\u679c\u7136\u9669\u6076\u6df1\u4ec7\uff0c\u7adf\u65e9\u5df2\u6697\u5730\u57cb\u4f0f\u4e86\u8fd9\u822c\u597d \u624b\u2026\u2026\u201d \u3000\u3000\u601d\u5fd6\u672a\u5df2\uff0c\u5374\u542c\u6bdb\u81ec\u6717\u58f0\u9053\uff1a\u201c\u6c6a\u5927\u5f1f\uff0c\u611a\u5144\u6709\u4f55\u5bf9\u4e0d\u8d77\u4f60\u7684\u5730\u65b9\uff0c\u5f53\u7740\u4f17\u5bb6\u5144\u5f1f\u9762 \u524d\uff0c\u4f60\u987b\u653e\u660e\u767d\u4e9b\uff01\u201d \u3000\u3000\u6c6a\u6c0f\u6606\u4ef2\u5728\u897f\u6e56\u4e0a\u53d7\u632b\u4e8e\u6bdb\u6587\u742a\u4e4b\u4e8b\uff0c\u600e\u597d\u5411\u5929\u4e0b\u7fa4\u96c4\u8bf4\u51fa\uff0c\u6c6a\u4e00\u9e4f\u76ee\u5149\u4e00\u8f6c\u5389\u58f0\u9053\uff1a \u201c\u4f60\u5be1\u5ec9\u9c9c\u803b\uff0c\u5f00\u8bbe\u5730\u4e0b\u9556\u5c40\uff0c\u80cc\u4fe1\u5fd8\u4e49\uff0c\u6697\u6740\u6211\u7a0b\u67ab\u5927\u54e5\uff0c\u6b3a\u51cc\u5b64\u5be1\uff0c\u96c6\u597d\u9669\u6bd2\u8fa3\u4e8e\u4e00 \u8eab\uff0c\u5929\u4e0b\u4e4b\u4eba\u7686\u5f97\u8bdb\u4e4b\uff0c\u6211\u5f1f\u5144\u66ff\u6b66\u6797\u9664\u5bb3\uff0c\u53c8\u4f55\u987b\u6709\u79c1\u4eba\u6069\u6028\uff01\u201d \u3000\u3000\u8fd9\u4e00\u5e2d\u8bdd\uff0c\u8bf4\u5f97\u4e49\u6b63\u8bcd\u4e25\uff0c\u7559\u7740\u672a\u8d70\u7684\u7fa4\u8c6a\uff0c\u83ab\u4e0d\u8038\u7136\u52a8\u5bb9\uff0c\u751a\u81f3\u5df1\u6709\u4eba\u6309\u5251\u800c\u8d77\u3002\u6ee1 \u6e56\u8239\u5a18\uff0c\u66f4\u65e9\u5df2\u4e71\u6210\u4e00\u5806\u3002 \u3000\u3000\u6bdb\u81ec\u6ee1\u9762\u6012\u5bb9\uff0c\u5fae\u4e00\u6325\u624b\uff0c\u51b7\u51b7\u53f1\u9053\uff1a\u201c\u6740\uff01\u201d \u3000\u3000\u53f1\u58f0\u65b9\u8d77\uff0c\u56db\u540d\u84dd\u8863\u5251\u624b\uff0c\u8eab\u5f62\u9f50\u5c55\uff0c\u56db\u67c4\u957f\u5251\uff0c\u540c\u65f6\u75be\u523a\u800c\u51fa\uff01 \u3000\u3000\u6c6a\u4e00\u9e4f\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u65e0\u77e5\u5c0f\u513f\uff0c\u4e5f\u6562\u5728\u8001\u592b\u9762\u524d\u65bd\u5251\uff01\u201d \u3000\u3000\u4eba\u968f\u58f0\u52a8\uff0c\u5251\u8d70\u8f7b\u7075\uff0c\u72ec\u624b\u632f\u5904\uff0c\u5251\u5c16\u5f39\u8d77\u56db\u6735\u5251\u82b1\uff0c\u5c06\u56db\u540d\u84dd\u8863\u5251\u624b\u7684\u957f\u5251\u4e00\u9f50\u5c01 \u4f4f\uff0c\u968f\u5373\u633d\u81c2\u4e00\u5708\uff0c\u5251\u5149\u5982\u8679\uff0c\u6025\u653b\u8fc7\u53bb\u3002 \u3000\u3000\u56db\u4e2a\u84dd\u8863\u5251\u624b\u8eab\u5f62\u5fae\u632b\uff0c\u970d\u5730\u4e00\u5206\uff0c\u907f\u653b\u8fd8\u62db\uff0c\u95ea\u7535\u822c\u8fd8\u4e86\u4e00\u5341\u4e8c\u5251\uff0c\u5251\u5251\u6307\u5411\u6c6a\u4e00\u9e4f \u5168\u8eab\u8981\u5bb3\u4e4b\u5904\u3002 \u3000\u3000\u6c6a\u4e00\u9e4f\u4e00\u58f0\u8f7b\u53f1\uff01\u632f\u81c2\u75be\u6325\uff0c\u957f\u5251\u5212\u51fa\u4e00\u5708\u5708\u5149\u5f27\uff0c\u76d8\u7a7a\u800c\u8d77\uff0c\u6709\u5982\u4e00\u5e62\u534e\u76d6\uff0c\u5c06\u8eab\u5f62 \u62a4\u4f4f\u3002 \u3000\u3000\u56db\u4e2a\u84dd\u8863\u5251\u624b\uff0c\u987f\u89c9\u624b\u4e2d\u957f\u5251\u5982\u540c\u523a\u5728\u4e00\u5835\u575a\u58c1\u4e4b\u4e0a\uff0c\u5251\u52bf\u4e3a\u4e4b\u4e00\u632b\uff01 \u3000\u3000\u6c6a\u4e00\u9e4f\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u7075\u86c7\u95e8\u4e0b\u5251\u624b\uff0c\u8fd8\u6709\u51e0\u4eba\uff1f\u201d \u3000\u3000\u7b11\u559d\u58f0\u4e2d\uff0c\u624b\u8155\u5fae\u632f\uff0c\u4e00\u8fde\u56db\u5251\uff0c\u6709\u5982\u60ca\u8292\u63a3\u7535\u822c\u51fb\u51fa\uff0c\u84dd\u8863\u5251\u624b\u9f50\u58f0\u5927\u559d\uff0c\u8eab\u5f62\u590d \u5408\uff0c\u56db\u67c4\u957f\u5251\u7ec7\u6210\u4e86\u4e00\u7247\u5149\u534e\uff01 \u3000\u3000\u77ac\u606f\u4e4b\u95f4\uff0c\u53cc\u65b9\u5df2\u4e92\u653b\u51fa\u5341\u4f59\u62db\u4e4b\u591a\uff0c\u6c6a\u4e00\u9e4f\u957f\u5251\u6325\u6d12\uff0c\u6e38\u8d70\u4e8e\u56db\u67c4\u957f\u5251\u4ea4\u7ec7\u7684\u5149\u534e \u4e2d\uff0c\u8868\u9762\u4e0a\u867d\u662f\u4ece\u5bb9\u65e0\u6bd4\uff0c\u4f46\u5fc3\u4e2d\u5374\u662f\u70e6\u8e81\u5df2\u6781\uff0c\u76ee\u4e2d\u6740\u673a\u9aa4\u76db\uff0c\u624b\u4e2d\u5251\u52bf\u7a81\u53d8\uff0c\u7531\u75be\u800c \u5f90\uff0c\u4eff\u4f5b\u5251\u8eab\u6709\u5343\u94a7\u4e4b\u91cd\uff0c\u6bcf\u4e00\u5251\u523a\u51fa\uff0c\u5176\u52bf\u867d\u7f13\uff0c\u4f46\u4ff1\u8574\u542b\u7740\u6781\u5389\u5bb3\u7684\u53d8\u5316\u4e0e\u65e0\u7a77\u6f5c\u529b\u3002 \u3000\u3000\u56db\u4e2a\u84dd\u8863\u5251\u624b\u7684\u5251\u62db\u867d\u662f\u8f9b\u8fa3\u8be1\u5f02\uff0c\u4f46\u529f\u529b\u4fee\u7ec3\u4e0a\uff0c\u54ea\u53ca\u6c6a\u4e00\u9e4f\u6df1\u539a\uff0c\u662f\u4ee5\u987f\u65f6\u4e3a\u5bf9\u65b9 \u5251\u8eab\u4e0a\u53d1\u51fa\u7684\u6f5c\u529b\u6240\u903c\uff0c\u8f9b\u8fa3\u51cc\u5389\u7684\u5251\u62db\uff0c\u518d\u4e5f\u65bd\u5c55\u4e0d\u5f00\u3002 \u3000\u3000\u6bdb\u81ec\u5728\u4e00\u65c1\u53c9\u624b\u7763\u6218\uff0c\u89c1\u72b6\uff0c\u5fc3\u4e2d\u4e0d\u7531\u5927\u4e3a\u7740\u6025\uff0c\u552f\u6050\u518d\u6253\u4e0b\u53bb\uff0c\u81ea\u5df1\u8d39\u4e86\u591a\u5e74\u5fc3\u8840\u8bad \u7ec3\u51fa\u6765\u7684\u8fd9\u56db\u540d\u5251\u624b\uff0c\u53c8\u5c06\u6bc1\u4e8e\u4e00\u65e6\uff01 \u3000\u3000\u5fc3\u5ff5\u601d\u5fd6\u95f4\uff0c\u4ed6\u4e0d\u7981\u53c8\u81ea\u60f3\u8d77\u4e86\u6628\u65e5\u968f\u7a0b\u67ab\u51fa\u52a8\u7684\u53e6\u56db\u540d\u5251\u624b\uff0c\u7adf\u76f4\u5230\u6b64\u523b\u4e3a\u6b62\uff0c\u8fd8\u4e0d \u89c1\u8e2a\u8ff9\u3002 \u3000\u3000\u4ed6\u6084\u7136\u8d70\u5230\u89d2\u843d\u91cc\u7684\u201c\u8fd8\u9b42\u201d\u8eab\u7554\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4f60\u6628\u5929\u6740\u6b7b\u7a0b\u67ab\u65f6\uff0c\u53ef\u66fe\u89c1\u5230\u8fc7\u8eab\u7a7f\u84dd \u8863\u7684\u5251\u624b\uff1f\u201d \u3000\u3000\u201c\u8fd8\u9b42\u201d\u6f20\u7136\u70b9\u4e86\u70b9\u5934\uff0c\u51b7\u51b7\u9053\uff1a\u2018\u89c1\u5230\uff01\u201d\u6bdb\u81ec\u76ee\u5149\u4e00\u5bd2\uff0c\u8ffd\u95ee\u9053\uff1a\u201c\u4ed6\u4eec\u5230\u54ea\u91cc\u53bb \u4e86\uff1f\u201d \u3000\u3000\u201c\u8fd8\u9b42\u201d\u51b7\u51b7\u9053\uff1a\u201c\u6b7b\u4e86\uff01\u201d \u3000\u3000\u6bdb\u81ec\u970d\u5730\u8de8\u524d\u4e00\u6b65\uff0c\u9762\u6c89\u5982\u6c34\uff0c\u5389\u58f0\u9053\uff1a\u201c\u600e\u6837\u6b7b\u7684\uff1f\u201d \u3000\u3000\u201c\u8fd8\u9b42\u201d\u52a8\u4e5f\u4e0d\u52a8\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u6728\u7136\u7b54\u9053\uff1a\u201c\u96be\u9053\u4ed6\u4eec\u8fd8\u4f1a\u75c5\u6b7b\u4e0d\u6210\uff1f\u201d \u3000\u3000\u6bdb\u81ec\u53cc\u62f3\u7d27\u63e1\uff0c\u4e00\u5b57\u5b57\u7f13\u7f13\u95ee\u9053\uff1a\u201c\u662f\u8c01\u52a8\u7684\u624b\uff1f\u201d \u3000\u3000\u8bdd\u58f0\u672a\u4e86\uff0c\u8239\u5934\u5df2\u54cd\u8d77\u4e24\u58f0\u91d1\u94c1\u4ea4\u545c\u7684\u5927\u9707\uff0c\u95ea\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u4e24\u67c4\u957f\u5251\u51b2\u5929\u98de\u8d77\uff0c\u5e26\u8d77 \u4e24\u9053\u5149\u5f27\uff0c\u659c\u659c\u5760\u4eba\u6e56\u4e2d\u3002\u5212\u5f00\u4e24\u9053\u78a7\u6ce2\uff01 \u3000\u3000\u4e24\u4e2a\u84dd\u8863\u5251\u624b\u75be\u9000\u800c\u51fa\uff0c\u624b\u4e0a\u7a7a\u7a7a\uff0c\u957f\u5251\u5df2\u5931\u3002 \u3000\u3000\u6c6a\u4e00\u9e4f\u5982\u5f71\u968f\u5f62\uff0c\u53e3\u4e2d\u5927\u559d\u4e00\u58f0\uff1a\u201c\u7740\uff01\u201d \u3000\u3000\u5251\u5c16\u4f38\u7f29\uff0c\u4eff\u4f3c\u6bd2\u86c7\u5410\u4fe1\uff0c\u4e00\u5206\u4e3a\u4e8c\uff0c\u95ea\u7535\u822c\u76f4\u53d6\u4e8c\u4eba\u54bd\u5589\u3002 \u3000\u3000\u4e24\u4e2a\u84dd\u8863\u5251\u624b\u7684\u8eab\u624b\u867d\u81ea\u4e0d\u5f31\uff0c\u4f46\u5bf9\u65b9\u8fd9\u4e00\u5251\uff0c\u6765\u52bf\u53c8\u51c6\u53c8\u72e0\uff0c\u5374\u4ee4\u4ed6\u4e24\u4eba\u907f\u65e0\u53ef\u907f\u3002 \u3000\u3000\u5239\u90a3\u95f4\uff0c\u53e6\u4e24\u9053\u5251\u5149\u4ece\u65c1\u8fb9\u4e00\u95ea\u800c\u81f3\uff0c\u201c\u94ee\u94ee\u201d\u4e24\u58f0\uff0c\u786c\u751f\u751f\u5c06\u6c6a\u4e00\u9e4f\u523a\u51fa\u7684\u8fd9\u4e00\u5251\u649e \u5f00\u4e86\u6570\u5bf8\u3002 \u3000\u3000\u53ea\u542c\u2018\u54e7\u54e7\uff0c\u4e24\u58f0\uff0c\u8fd9\u4e24\u4e2a\u84dd\u8863\u5251\u624b\u867d\u5e78\u514d\u5251\u6d1e\u54bd\u5589\uff0c\u4f46\u80a9\u4e0a\u4e1a\u5df2\u88ab\u6c6a\u4e00\u9e4f\u7684\u5251\u950b\u4f59 \u52bf\uff0c\u5212\u7834\u4e00\u9053\u8840\u53e3\uff01\u90a3\u51fa\u624b\u62ef\u6551\u7684\u53e6\u5916\u4e24\u4e2a\u84dd\u8863\u5251\u624b\uff0c\u4e5f\u88ab\u6c6a\u4e00\u9e4f\u957f\u5251\u53cd\u5f39\u4e4b\u529b\uff0c\u5f53\u5802\u9707\u9000 \u4e09\u6b65\u3002\u624b\u4e2d\u957f\u5251\u659c\u659c\u5782\u4e0b\uff0c\u51e0\u4e4e\u89e6\u53ca\u8231\u677f\uff0c\u663e\u89c1\u518d\u65e0\u8fd8\u624b\u4e4b\u529b\uff01\u6c6a\u4e00\u9e4f\u72ec\u529b\u6597\u8d25\u6bdb\u81ec\u56db\u4e2a\u8d34 \u8eab\u5251\u624b\uff0c\u5fc3\u4e2d\u5927\u4e3a\u5f97\u610f\uff0c\u6a2a\u5251\u4f5c\u6001\uff0c\u51dd\u89c6\u7740\u6bdb\u81ec\uff0c\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u8fd8\u6709\u4eba\u4e48\uff1f\u201d \u3000\u3000\u6bdb\u81ec\u76ee\u5149\u95ea\u7ffb\uff0c\u53d1\u73b0\u7fa4\u96c4\u5f53\u4e2d\uff0c\u7adf\u6709\u5927\u534a\u5728\u6012\u76ee\u76f8\u89c6\uff0c\u90a3\u7a0b\u9a79\u3001\u6f58\u4f65\u4e8c\u4eba\u4f9d\u7136\u5927\u9a6c\u91d1\u5200 \u5730\u5750\u5728\u5e2d\u4f4d\u4e0a\uff0c\u795e\u60c5\u51b7\u6f20\uff0c\u4f3c\u4e4e\u662f\u5bf9\u6240\u53d1\u751f\u4e4b\u4e8b\uff0c\u4e1d\u6beb\u4e0d\u611f\u5174\u8da3\u3002 \u3000\u3000\u8fd8\u6709\u90a3\u201c\u767e\u6b65\u98de\u82b1\u201d\u6797\u7426\u7b5d\u548c\u201c\u5de6\u624b\u795e\u5251\u201d\u4e01\u8863\uff0c\u4e5f\u662f\u9762\u542b\u8be1\u5f02\u83ab\u6d4b\u4e4b\u8272\uff0c\u663e\u7136\u662f\u5e78\u707e \u4e50\u7978\u7684\u6210\u4efd\u5c45\u591a\u3002 \u7b2c\u4e8c\u5341\u4e5d\u7ae0>> \u53e4\u9f99\u300a\u6e58\u5983\u5251\u300b \u7b2c\u4e8c\u5341\u4e5d\u7ae0 \u3000\u3000\u4ed6\u76ee\u5149\u95ea\u52a8\uff0c\u5fc3\u5ff5\u4ea6\u5728\u95ea\u52a8\u4e0d\u5df2\uff0c\u6c89\u541f\u534a\u664c\uff0c\u5140\u81ea\u7f13\u7f13\u9053\uff1a\u201c\u6c6a\u5927\u5f1f\uff0c\u987b\u77e5\u8fd9\u6b21\u5927\u4f1a\uff0c \u4e43\u4e3a\u4e86\u5bf9\u4ed8\u4ec7\u72ec\u7684\u540e\u4eba\u800c\u53ec\u5f00\u3002\u5f53\u5e74\u4e4b\u4e8b\uff0c\u8d24\u6606\u4ef2\u4e5f\u6709\u4e00\u4efd\uff0c\u600e\u5730\u4e3a\u4e86\u4e00\u65f6\u4e4b\u6c14\uff0c\u800c\u574f\u4e86\u5927 \u4e8b\uff1f\u201d\u6b64\u65f6\u6b64\u523b\uff0c\u9664\u4e86\u9634\u9e37\u6c89\u731b\u7684\u201c\u7075\u86c7\u201d\u53c8\u6709\u8c01\u8bf4\u5f97\u5982\u6b64\u4e0d\u5e26\u706b\u6c14\u7684\u8bdd\u6765\u3002 \u3000\u3000\u6c6a\u4e00\u9e4f\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u72c2\u505a\u8dcb\u6248\uff0c\u5904\u5fc3\u79ef\u8651\u5730\u8bdb\u9664\u5f02\u5df1\uff0c\u96be\u9053\u4e5f\u662f\u4e3a\u4e86\u5bf9\u4ed8\u4ec7\u72ec\u7684\u540e\u4eba\u4e48 \u8c03\u6bdb\u81ec\u76ee\u5149\u4e00\u8f6c\uff0c\u7adf\u7a81\u7136\u6487\u4e0b\u4e86\u6c6a\u4e00\u9e4f\uff0c\u8f6c\u8eab\u5bf9\u7fa4\u96c4\u9ad8\u58f0\u9053\uff1a\u201c\u5404\u4f4d\u53ef\u77e5\u9053\u90a3\u4ec7\u72ec\u7684\u540e\u4eba\uff0c \u4fbf\u662f\u8fd1\u65e5\u5728\u6c5f\u6e56\u4e2d\uff0c\u6380\u8d77\u65e0\u8fb9\u98ce\u6d9b\u7684\u2018\u91d1\u5251\u4fa0\u2019\uff1f\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u7fa4\u96c4\u65e0\u4e0d\u52a8\u5bb9\uff0c\u6709\u7684\u751a\u81f3\u60ca\u547c\u51fa\u58f0\u6765\u3002 \u3000\u3000\u53ea\u56e0\u90a3\u201c\u91d1\u5251\u4fa0\u201d\u51fa\u73b0\u6c5f\u6e56\u4e3a\u65f6\u867d\u77ed\u6682\uff0c\u4f46\u4e8b\u8ff9\u90fd\u5df2\u4f20\u904d\u6c5f\u6e56\uff0c\u540c\u65f6\uff0c\u6c5f\u6e56\u4e0a\u66f4\u5b58\u7740\u8bb8 \u591a\u6709\u5173\u4ed6\u795e\u79d8\u7684\u4f20\u8bf4\uff0c\u5f53\u7136\uff0c\u4e5f\u6709\u4eba\u8bf4\u4ed6\u662f\u5982\u4f55\u5982\u4f55\u5730\u4e0d\u8fd1\u4eba\u60c5\uff0c\u5fc3\u7406\u72e0\u6bd2\u3002 \u3000\u3000\u5018\u82e5\u8fd9\u79cd\u79cd\u4f20\u95fb\u90fd\u662f\u771f\u7684\uff0c\u90a3\u65e0\u5f02\u5373\u662f\u7b2c\u4e8c\u4e2a\u4ec7\u72ec\u51fa\u73b0\u6c5f\u6e56\uff0c\u201c\u4ec7\u5148\u751f\u201d\uff0c\u6614\u5e74\u7684\u4e8b\u8ff9 \u5728\u7fa4\u96c4\u4e2d\u591a\u534a\u8bb0\u5fc6\u72b9\u65b0\uff0c\u6545\u6bdb\u81ec\u4e4b\u8a00\uff0c\u600e\u6559\u4ed6\u4eec\u4e0d\u60ca\uff1f \u3000\u3000\u6bdb\u81ec\u76ee\u5149\u4f55\u7b49\u9510\u5229\uff0c\u5df2\u81ea\u5c06\u7fa4\u96c4\u795e\u6001\u5fc3\u601d\u6d1e\u5bdf\u65e0\u9057\uff0c\u4e0d\u7531\u5fc3\u5934\u6697\u559c\uff0c\u6717\u58f0\u63a5\u9053\uff1a\u201c\u4eca\u65e5 \u6bdb\u81ec\u8eab\u53d7\u8bef\u4f1a\uff0c\u6b7b\u4e0d\u8db3\u60dc\uff0c\u4f46\u6050\u4f17\u5bb6\u5144\u5f1f\u4e3a\u6b64\u800c\u5404\u81ea\u751f\u5fc3\uff0c\u81f4\u529b\u91cf\u5206\u6563\uff0c\u6388\u4eba\u4ee5\u5404\u4e2a\u51fb\u7834\u4e4b \u9699\uff0c\u54b3\u54b3\u2026\u2026\u90a3\u65f6\u2026\u2026\u201d\u4ed6\u6b64\u523b\u81ea\u77e5\u5df2\u5c06\u4f17\u53db\u4eb2\u79bb\uff0c\u662f\u4ee5\u4e00\u9762\u4ee5\u8a00\u8bed\u62d6\u5ef6\u65f6\u95f4\uff0c\u7b49\u5019\u5947\u8ff9\uff0c \u4e00\u9762\u66f4\u60f3\u4ee5\u8a00\u8bed\u8f6c\u56de\u7fa4\u8c6a\u7684\u79bb\u5fc3\u3002 \u3000\u3000\u4ed6\u8bf4\u5230\u8fd9\u91cc\uff0c\u5ffd\u7136\u957f\u53f9\u4e00\u58f0\uff0c\u4f4f\u53e3\u4e0d\u8bed\uff0c\u8fdf\u7591\u4e0d\u8bed\u3002 \u3000\u3000\u7fa4\u8c6a\u9762\u9762\u76f8\u89d1\uff0c\u6697\u5fd6\u9053\uff1a\u201c\u7075\u86c7\u6bdb\u81ec\u9886\u8896\u8349\u83bd\u82f1\u96c4\u5782\u5341\u6570\u5e74\uff0c\u6c5f\u6e56\u95f4\u603b\u7b97\u5e73\u9759\u65e0\u6ce2\uff0c\u8fd9 \u6b21\u4e00\u65e6\u5c06\u4ed6\u7684\u9886\u5bfc\u5730\u4f4d\u5e9f\u6389\uff0c\u5219\u540e\u7ee7\u4e4b\u4eba\u80fd\u5426\u6709\u6b64\u9b44\u529b\u6765\u62c5\u8d1f\u8fd9\u5343\u94a7\u91cd\u62c5\uff1f\u201d \u3000\u3000\u7fa4\u96c4\u5404\u81ea\u5fc3\u5ff5\u95ea\u52a8\uff0c\u76db\u6c14\u5df2\u6e10\u5e73\u606f\uff0c\u800c\u6bdb\u81ec\u8138\u4e0a\u7684\u60f6\u6025\u4e4b\u6001\uff0c\u4ea6\u81ea\u6d88\u5931\u4e0d\u89c1\uff0c\u7a81\u5730\u2014\u2014 \u7d20\u5973\u6797\u7433\u4e00\u60ca\u800c\u8d77\uff0c\u4e7e\u6307\u6bdb\u81ec\uff0c\u5636\u58f0\u9053\uff1a\u201c\u5f53\u5e74\u662f\u4f60\u7528\u9634\u8c0b\u6697\u7b97\u4ec7\u72ec\uff0c\u4f7f\u4ed6\u4e24\u817f\u6b8b\u5e9f\u5728\u5148\uff0c \u4f60\u624d\u662f\u771f\u6b63\u7684\u7f6a\u9b41\u7978\u9996\uff0c\u4ec7\u72ec\u7684\u513f\u5b50\u627e\u7684\u53ea\u6709\u4f60\uff01\u201d \u3000\u3000\u5979\u5927\u9a82\u6570\u53e5\u5b8c\u4e86\uff0c\u53c8\u9762\u5411\u7fa4\u96c4\uff0c\u653e\u58f0\u5927\u54ed\u9053\uff1a\u201c\u8bf8\u4f4d\u5343\u4e07\u4e0d\u8981\u542c\u4ed6\u7684\uff0c\u53ef\u601c\u7a0b\u67ab\u5f53\u5e74\u66ff \u4ed6\u5356\u547d\uff0c\u5230\u5934\u6765\u53cd\u88ab\u4ed6\u5bb3\u6b7b\u4e86\uff01\u201d\u8bdd\u58f0\u672a\u4e86\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u7a81\u5730\u4e00\u638c\u6325\u51fa\uff0c\u5f3a\u52b2\u7684\u638c\u98ce\uff0c\u4f7f\u5c06 \u5df2\u4e34\u76c6\u7684\u6797\u7433\u518d\u4e5f\u7981\u53d7\u4e0d\u4f4f\uff0c\u7adf\u547c\u4e00\u58f0\uff0c\u8dcc\u5012\u5728\u5730\uff0c\u5f53\u573a\u6655\u53a5\u3002 \u3000\u3000\u6c6a\u4e00\u9e4f\u632f\u81c2\u9ad8\u547c\u9053\uff1a\u201c\u7a0b\u5927\u54e5\u592b\u5987\u7684\u906d\u9047\uff0c\u4fbf\u662f\u54b1\u4eec\u524d\u8f66\u4e4b\u9274\uff0c\u4eca\u65e5\u4e0d\u5148\u6740\u4e86\u8fd9\u4e0d\u4ec1\u4e0d \u4e49\u7684\u6076\u8d3c\uff0c\u5c06\u6765\u54b1\u4eec\u4fbf\u6b7b\u65e0\u846c\u8eab\u4e4b\u5730\u4e86\uff01\u201d \u3000\u3000\u547c\u58f0\u4e00\u843d\uff0c\u632f\u8155\u4e00\u5251\uff0c\u5530\u5730\u76f4\u53d6\u6bdb\u81ec\u80f8\u819b\uff01 \u3000\u3000\u6c6a\u4e00\u9e23\u9002\u624d\u76ee\u7779\u6bdb\u81ec\u8eb2\u95ea\u4e43\u5144\u7684\u8eab\u6cd5\uff0c\u60c5\u77e5\u5355\u6253\u72ec\u6597\u4e43\u5144\u5b9a\u5fc5\u8ba8\u4e0d\u4e86\u597d\uff0c\u4e8e\u662f\uff0c\u4e5f\u5c06\u957f \u5251\u64a4\u51fa\uff0c\u6b3a\u8eab\u75be\u4e0a\uff0c\u6325\u5251\u5411\u6bdb\u81ec\u62e6\u8170\u626b\u53bb\uff01\u2018\u7075\u86c7\u2019\u6bdb\u81ec\u4e0e\u6c6a\u6c0f\u6606\u4ef2\u5408\u4f19\u591a\u5e74\uff0c\u6df1\u77e5\u53cc\u5251\u5408 \u58c1\u4e4b\u5a01\uff0c\u975e\u4ec5\u51ed\u8d64\u624b\u53ca\u8eab\u6cd5\u6240\u80fd\u6297\u62d2\u3002 \u3000\u3000\u53ea\u89c1\u6bdb\u81ec\u624b\u63a2\u8170\u9645\uff0c\u8eab\u5f62\u75be\u8f6c\uff0c\u2018\u547c\uff0c\u5730\u4e00\u58f0\uff0c\u9510\u98ce\u9aa4\u8d77\uff0c\u4e00\u6761\u62c7\u6307\u7c97\u7ec6\u7684\u9ed1\u5f71\uff0c\u76d8\u7a7a \u800c\u8d77\uff01\u8fd9\u4e00\u6839\u5947\u5f62\u957f\u97ad\uff0c\u53c8\u7ecf\u8fc7\u4e86\u6bdb\u81ec\u5341\u4f59\u5e74\u6765\u7684\u671d\u5915\u82e6\u7ec3\uff0c\u62db\u5f0f\u66f4\u662f\u8f9b\u8fa3\u51cc\u5389\uff0c\u8be1\u5f02\u83ab \u6d4b\u3002\u53ea\u89c1\u97ad\u68a2\u70b9\u5904\uff0c\u6c6a\u4e00\u9e4f\u7684\u957f\u5251\u7acb\u88ab\u8361\u5f00\uff0c\u8ddf\u7740\u97ad\u8eab\u4e00\u6298\uff0c\u547c\u5730\u53cd\u5411\u6c6a\u4e00\u545c\u957f\u5251\u53cd\u5377\u800c \u53bb\uff01\u6c6a\u4e00\u545c\u54ea\u6562\u8ba9\u957f\u5251\u88ab\u4ed6\u7f20\u4f4f\uff0c\u8d76\u5feb\u632b\u8155\u62bd\u5251\uff0c\u8eab\u5f62\u500f\u5730\u6a2a\u98de\u6570\u5c3a\uff0c\u5df2\u81ea\u5174\u6c6a\u4e00\u9e4f\u5e76\u80a9\u800c \u7acb\u3002\u4e24\u5144\u5f1f\u8eab\u5f62\u4e00\u5e76\uff0c\u4e0d\u5f85\u6bdb\u81ec\u7b2c\u4e8c\u6b21\u653b\u5230\uff0c\u500f\u5730\u53c8\u98de\u63a0\u4e0a\u524d\uff0c\u53cc\u5251\u5e76\u8d77\uff0c\u5b9b\u5982\u4e24\u6761\u7ecf\u5929\u957f \u8679\uff0c\u4ea4\u5c3e\u800c\u51fa\u3002\u6c6a\u4e00\u9e4f\u7684\u5251\u5149\u81ea\u5de6\u800c\u53f3\uff0c\u6c6a\u4e00\u545c\u81ea\u53f3\u800c\u5de6\uff0c\u631f\u5636\u5636\u9510\u58f0\uff0c\u76f4\u53d6\u6bdb\u684c\uff01\u53cc\u5251\u8fd9 \u4e00\u5408\u58c1\u653b\u51fa\uff0c\u5a01\u529b\u4f55\u6b62\u500d\u589e\uff0c\u987f\u89c1\u68ee\u68ee\u5251\u6c14\uff0c\u903c\u4eba\u7709\u5b57\uff0c\u89c2\u6218\u7fa4\u96c4\uff0c\u4ff1\u4e0d\u7531\u6697\u8d5e\uff1a\u201c\u597d\u5251 \u6cd5\uff01\u201d \u3000\u3000\u8239\u8231\u4e4b\u4e2d\uff0c\u8fd8\u9b42\u4ecd\u81ea\u6728\u7acb\u89d2\u843d\uff0c\u7a0b\u9a79\u3001\u6f58\u4f65\u4f9d\u65e7\u6f20\u7136\u7aef\u5750\uff0c\u4f46\u5de6\u624b\u795e\u5251\u4e01\u8863\u53ca\u767e\u6b65\u98de\u82b1 \u6797\u7426\u7b5d\u4e8c\u4eba\uff0c\u8138\u4e0a\u795e\u8272\u5df2\u81ea\u63a5\u8fde\u51e0\u53d8\uff0c\u56db\u9053\u773c\u795e\uff0c\u77ac\u4e5f\u4e0d\u77ac\u5730\u51dd\u6ce8\u5728\u6bdb\u81ec\u8eab\u4e0a\u3002 \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u89c1\u6c6a\u6c0f\u6606\u4ef2\u7adf\u5c06\u7edd\u4f20\u6b66\u6797\u591a\u5e74\u7684\u4e24\u4eea\u5251\u6cd5\u7ec3\u6210\uff0c\u5fc3\u5934\u4e0d\u7531\u4e00\u51db\uff0c\u4f46\u53e3\u4e2d\u5374\u51b7\u7b11 \u9053\uff1a\u201c\u5f88\u597d\uff0c\u6bdb\u81ec\u5012\u8981\u77a7\u77a7\u8d24\u6606\u4ef2\u8fd9\u4e24\u4eea\u5251\u6cd5\uff0c\u7ec3\u5230\u51e0\u6210\u706b\u5019\uff1f\u201d \u3000\u3000\u7b11\u8bed\u58f0\u4e2d\uff0c\u771f\u529b\u5c3d\u805a\u53f3\u81c2\uff0c\u773c\u89c2\u5251\u950b\u53ca\u8eab\u4e0d\u8db3\u4e00\u5c3a\uff0c\u970d\u5730\u4e00\u632f\u8155\uff0c\u957f\u97ad\u547c\u5730\u7ed5\u8eab\u6025\u8f6c\uff01 \u3000\u3000\u6c6a\u6c0f\u6606\u4ef2\u9aa4\u89c9\u957f\u97ad\u8f6c\u52a8\u4e4b\u65f6\uff0c\u56db\u5468\u98ce\u58f0\u90fd\u968f\u4e4b\u8d77\u4e86\u4e00\u9635\u6f29\u6da1\uff0c\u4e24\u67c4\u957f\u5251\u88ab\u6f29\u529b\u4e00\u5438\uff0c\u7adf \u4e0d\u7531\u81ea\u4e3b\u731b\u5730\u4e92\u76f8\u649e\u53bb\u3002 \u3000\u3000\u4e24\u5f1f\u5144\u5fc3\u5934\u4ff1\u4e0d\u7981\u4e3a\u4e4b\u4e00\u51db\uff0c\u5fd9\u5404\u81ea\u8fd0\u52b2\u64a4\u5251\uff0c\u624b\u8155\u75be\u7ffb\uff0c\u4e24\u5251\u5404\u81ea\u5212\u4e86\u4e2a\u534a\u5706\uff0c\u500f\u5730 \u4ece\u4e2d\u5fc3\u523a\u51fa\uff01 \u3000\u3000\u6bdb\u81ec\u4e00\u62db\u751f\u5e73\u7edd\u5b66\u201c\u9f99\u5377\u98ce\u4e91\u201d\u672a\u5c06\u5bf9\u65b9\u957f\u5251\u5438\u4f4f\uff0c\u4fbf\u77e5\u80dc\u8d1f\u5df2\u4e0d\u53ef\u6d4b\u3002\u4f46\u4ed6\u4e3a\u4eba\u8001\u8c0b \u6df1\u7b97\uff0c\u660e\u77e5\u7fa4\u96c4\u6b64\u9645\u5c1a\u81ea\u6309\u5175\u4e0d\u52a8\uff0c\u65e0\u975e\u662f\u6151\u4e8e\u4ed6\u5e73\u65e5\u4e4b\u5a01\u800c\u5df2\uff0c\u4e07\u4e00\u4ed6\u5728\u795e\u6001\u4e0a\u7a0d\u9732\u51fa\u4e00 \u4e1d\u4e0d\u5b89\u4e4b\u8272\uff0c\u5219\u540e\u679c\u5c06\u4e0d\u582a\u8bbe\u60f3\u3002 \u3000\u3000\u5f53\u4e0b\uff0c\u4ed6\u626c\u7709\u4f5c\u6001\uff0c\u655e\u58f0\u72c2\u7b11\u9053\u201c\u4e24\u4eea\u5251\u6cd5\u4e0d\u8fc7\u5982\u6b64\uff0c\u5484\uff01\u8fd8\u4e0d\u9000\uff1f\u201d \u3000\u3000\u8eab\u5f62\u75be\u8f6c\uff0c\u97ad\u5f71\u66b4\u6da8\uff0c\u77eb\u903e\u7075\u86c7\uff0c\u4e00\u5708\u63a5\u4e00\u5708\u5730\u5411\u653b\u6765\u7684\u4e24\u67c4\u957f\u5251\u5377\u53bb\u3002 \u3000\u3000\u4ed6\u97ad\u52bf\u76d8\u5c48\u4e0d\u5b9a\uff0c\u7adf\u5c06\u97ad\u98ce\u8303\u56f4\uff0c\u7f29\u5c0f\u81f3\u6570\u5c3a\u4ee5\u5185\uff0c\u4f46\u95e8\u6237\u5374\u9632\u5b88\u5f97\u4e25\u4e1d\u5bc6\u7f1d\uff0c\u53e3\u4e2d\u8fde \u8fde\u5632\u7b11\u9053\uff1a\u201c\u6bdb\u81ec\u4e14\u8ba9\u8d24\u6606\u4ef2\u5c55\u5c3d\u6240\u5b66\uff0c\u7136\u540e\u624d\u4e88\u4ee5\u8fd8\u51fb\uff0c\u597d\u6559\u4f60\u5f1f\u5144\u8f93\u5f97\u5fc3\u670d\uff01\u201d \u3000\u3000\u6c6a\u4e00\u9e23\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u60f3\u682a\u5b88\u5f85\u63f4\uff0c\u7b80\u76f4\u4f5c\u68a6\uff01\u201d \u3000\u3000\u6c6a\u4e00\u9e4f\u5927\u559d\u9053\uff1a\u201c\u653e\u773c\u6e56\u4e0a\uff0c\u8fd8\u6709\u8c01\u80af\u5e2e\u4f60\u8fd9\u597d\u6076\u4e4b\u4eba\uff0c\u4f60\u5c31\u4e56\u4e56\u8ba4\u547d\u7f62\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u53cc\u65b9\u5df2\u4e92\u62fc\u4e86\u4e09\u5341\u62db\uff0c\u53ea\u89c1\u6c6a\u6c0f\u6606\u4ef2\u5251\u52bf\u5982\u9f99\uff0c\u51b2\u523a\u640f\u51fb\uff0c\u6108\u76ca\u731b\u5389\uff0c\u90a3\u5636 \u5636\u5251\u98ce\u7834\u7a7a\u4e4b\u58f0\uff0c\u7adf\u8fdc\u8fbe\u5341\u6570\u4e08\u4e4b\u9065\u3002\u6e56\u5cb8\u8fb9\u7684\u4e1d\u67f3\u4e1d\u53f6\u88ab\u5251\u98ce\u4e00\u6fc0\uff0c\u6709\u5982\u96ea\u82b1\u822c\u98de\u821e\u8d77 \u6765\u3002 \u3000\u3000\u6b64\u65f6\uff0c\u5728\u7fa4\u96c4\u5f53\u4e2d\u98d8\u8d77\u4e86\u4e00\u9635\u7a83\u7a83\u79c1\u8bed\uff1a\u201c\u770b\u6837\u5b50\u6bdb\u5927\u54e5\u6050\u6015\u4e0d\u884c\u4e86\uff01\u201d \u3000\u3000\u201c\u60f3\u4e0d\u5230\u6cb3\u6714\u53cc\u5251\uff0c\u7adf\u8fd9\u822c\u5389\u5bb3\u2026\u2026\u201d \u3000\u3000\u201c\u54b1\u4eec\u4f55\u4e0d\u4e58\u6b64\u65f6\u673a\uff0c\u52a9\u4ed6\u5144\u5f1f\u4e00\u81c2\uff0c\u6597\u6740\u6bdb\u81ec\uff0c\u62e5\u7acb\u65b0\u76df\u4e3b\uff1f\u201d \u3000\u3000\u8fd9\u4e00\u9635\u9635\u79c1\u8bed\u4e4b\u58f0\u867d\u5fae\uff0c\u4f46\u5728\u6b64\u7fa4\u96c4\u5c4f\u606f\u89c2\u6218\u4e4b\u9645\uff0c\u7adf\u4e5f\u4f20\u51fa\u8001\u8fdc\u3002 \u3000\u3000\u79bb\u8fd9\u4e00\u6392\u753b\u822b\u5341\u6570\u4e08\u8fdc\u7684\u5de6\u65b9\uff0c\u5824\u5cb8\u4e0a\uff0c\u67f3\u836b\u63a9\u6620\u4e4b\u4e0b\uff0c\u7ef0\u7acb\u7740\u4e00\u4f4d\u5a07\u4fcf\u5973\u90ce\uff0c\u5979\u9edb\u7709 \u7d27\u8e69\uff0c\u4e24\u9053\u79cb\u6ce2\u6b63\u77ac\u4e5f\u4e0d\u77ac\u5730\u6ce8\u89c6\u7740\u4e0a\u81ec\u7684\u90a3\u8258\u5927\u8239\uff0c\u90a3\u4e00\u9635\u9635\u79c1\u8bed\u4e4b\u58f0\u8fdb\u5165\u5979\u7684\u8033\u4e2d\uff0c\u6bcf \u4e00\u4e2a\u5b57\u90fd\u4eff\u4f5b\u5229\u5251\u4e00\u822c\u523a\u7740\u5979\u7684\u5fc3\u5f26\u3002\u5f53\u7136\uff0c\u6bdb\u81ec\u7684\u906d\u9047\uff0c\u4e5f\u4e00\u693f\u4e0d\u6f0f\u5730\u70d9\u5728\u5979\u773c\u5185\uff0c\u5979\u82cd \u81ea\u7684\u6a31\u5507\u5fae\u5fae\u98a4\u6296\u7740\uff0c\u5410\u51fa\u53ea\u6709\u5979\u81ea\u5df1\u624d\u542c\u5f97\u5230\u7684\u58f0\u97f3\uff1a\u201c\u554a\uff01\u7239\u7239\uff01\u60a8\u771f\u662f\u8fd9\u6837\u7684\u4eba\u5417\uff1f \u8fd9\u4f17\u53db\u4eb2\u79bb\u7684\u82e6\u679c\uff0c\u7adf\u662f\u60a8\u81ea\u5df1\u4eb2\u624b\u79cd\u4e0b\u7684\u554a\uff01\u5929\u5440\uff01\u6559\u6211\u600e\u4e48\u529e\u5462\uff1f\u201d \u3000\u3000\u81ea\u7136\uff0c\u5979\u8fd9\u5f98\u60f6\u4e0d\u5b89\u7684\u795e\u60c5\uff0c\u662f\u518d\u4e5f\u4e0d\u4f1a\u5f15\u8d77\u7cdc\u96c6\u5824\u5cb8\u89c2\u770b\u70ed\u95f9\u4e4b\u4eba\u7684\u6ce8\u610f\uff0c\u53ea\u56e0\u5979\u65e9 \u5c06\u81ea\u5df1\u7684\u7ea4\u7ea4\u5a07\u8eaf\uff0c\u7d27\u8d34\u5728\u6811\u5e72\u540e\uff0c\u751f\u50cf\u662f\u552f\u6050\u88ab\u4eba\u53d1\u73b0\u4f3c\u7684\u3002 \u3000\u3000\u5979\u81ea\u7136\u4fbf\u662f\u5728\u8fd9\u4e00\u65e5\u95f4\u5c1d\u904d\u4eba\u751f\u82e6\u679c\u7684\u6bdb\u6587\u742a\uff0c\u5979\u5fc3\u4e2d\u6069\u6028\u53eb\u7ed3\uff0c\u7231\u6068\u96be\u5206\uff0c\u672c\u7acb\u5728\u6e56 \u5cb8\u8fb9\uff0c\u7adf\u4e0d\u77e5\u8be5\u5982\u4f55\u662f\u597d\u3002 \u3000\u3000\u5979\u81ea\u7136\u66f4\u770b\u4e0d\u5230\u5c31\u5728\u8fd9\u6392\u753b\u822b\u53f3\u65b9\u5341\u6570\u4e08\u8fdc\u5904\uff0c\u5979\u5bf9\u9762\u7684\u5824\u4e0a\uff0c\u67f3\u836b\u63a9\u6620\u4e0b\uff0c\u4e5f\u7ef0\u7acb\u7740 \u4e00\u4e2a\u4e00\u8eab\u9752\u886b\uff0c\u8c8c\u76f8\u82f1\u4fca\u7684\u5f31\u51a0\u5c11\u5e74\uff0c\u4ed6\u4e5f\u662f\u5c06\u8eab\u8eaf\u7d27\u8d34\u7740\u6811\u5e72\uff0c\u4e5f\u662f\u751f\u50cf\u88ab\u4eba\u53d1\u73b0\u4f3c\u7684\uff0c \u4f46\uff0c\u4ed6\u5411\u6bdb\u81ec\u5927\u8239\u4e0a\u7684\u4e24\u9053\u773c\u795e\uff0c\u5374\u662f\u5982\u6b64\u575a\u5b9a\uff0c\u4f3c\u4e4e\u786e\u4fe1\u6bdb\u81ec\u7684\u547d\u8fd0\u5df2\u88ab\u6ce8\u5b9a\u4e86\u3002 \u3000\u3000\u7a81\u5730\u2026\u2026 \u3000\u3000\u4eba\u7fa4\u4e4b\u4e2d\uff0c\u54cd\u8d77\u4e00\u9635\u55e1\u55e1\u4e4b\u58f0\uff0c\u8fd9\u5f31\u51a0\u5c11\u5e74\u7684\u9762\u5bb9\u4e0a\uff0c\u7acb\u523b\u968f\u4e4b\u6cdb\u8d77\u4e86\u4e00\u9635\u5174\u594b\u7684\u5149 \u5f69\uff01 \u3000\u3000\u56e0\u4e3a\uff0c\u4ed6\u770b\u5230\u4e86\u201c\u5de6\u624b\u795e\u5251\u201d\u4e01\u8863\uff0c\u5df2\u957f\u8eab\u800c\u8d77\uff0c\u633a\u5251\u52a0\u5165\u6218\u6597\uff0c\u7adf\u4e0e\u6cb3\u6714\u53cc\u5251\uff0c\u9f0e\u8db3 \u800c\u7acb\uff0c\u8054\u624b\u5939\u653b\u7075\u86c7\u6bdb\u81ec\uff01 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u6bdb\u81ec\u7acb\u89c1\u52bf\u7a77\u529b\u7ecc\uff0c\u4ed6\u5e73\u7d20\u84c4\u517b\u7684\u4e00\u73ed\u8d34\u8eab\u536b\u58eb\u4ee5\u53ca\u95e8\u4e0b\u5f1f\u5b50\uff0c\u867d\u6709\u5fc3\u60f3\u51fa\u6765 \u52a9\u4e3b\u4eba\u4e00\u81c2\u4e4b\u529b\uff0c\u4f46\u5f53\u63a5\u89e6\u5230\u767e\u6b65\u98de\u82b1\u6797\u7426\u7b5d\u90a3\u4e24\u9053\u51b7\u5cfb\u7684\u76ee\u5149\u4e0e\u76ee\u7779\u7fa4\u96c4\u8dc3\u8dc3\u6b32\u52a8\u4e4b\u52bf\uff0c \u90fd\u4e0d\u7531\u5664\u82e5\u5bd2\u8749\uff0c\u54ea\u8fd8\u6562\u54fc\u534a\u58f0\u5927\u6c14\u3002 \u3000\u3000\u4f46\u89c1\u5927\u8239\u5934\u4e0a\uff0c\u4e09\u9053\u5339\u7ec3\u5149\u8292\uff0c\u77eb\u6377\u5982\u9f99\uff0c\u73af\u7ed5\u7740\u4e00\u56e2\u97ad\u5f71\uff0c\u817e\u8e7f\u523a\u51fb\uff0c\u5251\u5251\u5feb\u903e\u95ea \u7535\uff0c\u62db\u62db\u5747\u76f4\u53d6\u6bdb\u81ec\u8981\u5bb3\uff0c\u8fd9\u6c6a\u6c0f\u6606\u4ef2\u548c\u5de6\u2018\u624b\u795e\u5251\u4e01\u8863\u2019\u4f3c\u4e4e\u5df2\u518d\u65e0\u987e\u8651\uff0c\u7adf\u653e\u624b\u56f4\u653b\uff0c \u4e00\u65e5\u524d\u8fd8\u5728\u79f0\u5144\u9053\u5f1f\u7684\u670b\u53cb\u3002 \u3000\u3000\u4f46\uff0c\u767e\u6b65\u4e4b\u866b\uff0c\u6b7b\u800c\u4e0d\u50f5\uff0c\u7075\u86c7\u6bdb\u81ec\u5728\u5f00\u59cb\u65f6\uff0c\u786e\u6709\u682a\u5b88\u5f85\u63f4\u4e4b\u610f\uff0c\u4f46\u8fd9\u4e00\u62d6\u5ef6\u4e0b\u6765\uff0c \u4e0d\u4f46\u5916\u63f4\u672a\u66fe\u89c1\u5230\uff0c\u53cd\u89e6\u53d1\u7fa4\u96c4\u4ee5\u4e3a\u76df\u4e3b\u4ea6\u4e0d\u8fc7\u5c14\u5c14\u4e4b\u5fc3\uff0c\u800c\u81f4\u5f04\u5de7\u6210\u62d9\uff0c\u5fc3\u4e2d\u6025\u6012\u4ea4\u96c6\uff0c \u4e5f\u52a8\u4e86\u62fc\u547d\u4e4b\u5fc3\u3002 \u3000\u3000\u5239\u90a3\u95f4\u4f46\u89c1\u4ed6\u94c1\u8155\u4e00\u632f\uff0c\u957f\u97ad\u66b4\u5c55\uff0c\u97ad\u98ce\u5636\u5636\u6269\u53ca\u4e00\u4e08\u5f00\u5916\u3002 \u3000\u3000\u6c6a\u6c0f\u6606\u4ef2\u53ca\u201c\u5de6\u624b\u795e\u5251\u201d\u4e01\u8863\u6ca1\u6599\u5230\u56f0\u517d\u4e4b\u6597\uff0c\u72b9\u6709\u5982\u6b64\u5a01\u529b\uff0c\u6bdb\u81ec\u7adf\u4f1a\u53cd\u5b88\u4e3a\u653b\uff0c\u4e0d \u7981\u4e3a\u4e4b\u4e00\u6014\uff0c\u4f46\u7acb\u523b\u4fbf\u660e\u767d\uff0c\u8fd8\u4e0d\u8fc7\u662f\u4ed6\u7684\u56de\u5149\u8fd4\u7167\u800c\u5df2\u3002 \u3000\u3000\u53ea\u56e0\u6bdb\u81ec\u624b\u4e2d\u957f\u97ad\u8212\u5c55\u5f00\u6765\uff0c\u653b\u52bf\u867d\u662f\u51cc\u5389\u72e0\u8fa3\uff0c\u8fe5\u5f02\u4e8e\u9632\u5b88\u4e4b\u65f6\uff0c\u4f46\u97ad\u4e0a\u7684\u5185\u529b\u6f5c \u52b2\uff0c\u5374\u5df2\u5927\u4e0d\u5982\u524d\uff0c\u201c\u6cb3\u6714\u53cc\u5251\u201d\u3001\u201c\u5de6\u624b\u795e\u5251\u201d\uff0c\u8fd9\u4e09\u4e2a\u540d\u503e\u4e00\u65f6\u7684\u5251\u624b\uff0c\u9605\u5386\u4f55\u7b49\u4e30 \u5bcc\uff0c\u5c82\u6709\u4e0d\u7acb\u5373\u9192\u609f\u4e4b\u7406\uff01 \u3000\u3000\u4ed6\u4eec\u4e92\u76f8\u8fc5\u5feb\u5730\u671b\u4e86\u4e00\u773c\uff0c\u5f7c\u6b64\u5fc3\u7167\uff0c\u9f50\u5730\u72c2\u7b11\u9053\uff1a\u201c\u6bdb\u81ec\uff01\u5144\u5f1f\u4eec\u5982\u8ba9\u4f60\u7684\u957f\u97ad\u518d\u653b \u5f97\u4e09\u62db\uff0c\u4fbf\u5c06\u4e09\u9897\u4eba\u5934\u5949\u9001\uff01\u201d \u3000\u3000\u7b11\u559d\u58f0\u4e2d\uff0c\u4e09\u67c4\u957f\u5251\u4e00\u5708\uff0c\u731b\u5730\u75be\u523a\u800c\u51fa\uff0c\u5530\u5530\u5530\u4e09\u9053\u5bd2\u5149\u95ea\u5904\uff0c\u6bdb\u81ec\u624b\u4e2d\u957f\u97ad\u5df2\u66b4\u7f29 \u56de\u53bb\u3002 \u3000\u3000\u6c6a\u6c0f\u6606\u4ef2\u53ca\u201c\u5de6\u624b\u795e\u5251\u201d\u655e\u58f0\u5927\u7b11\uff0c\u633a\u5251\u75be\u8fdb\uff01 \u3000\u3000\u6bdb\u81ec\u5389\u543c\u4e00\u58f0\uff0c\u957f\u97ad\u518d\u5ea6\u72c2\u5377\u800c\u51fa\uff0c\u4f46\u8fd9\u6b21\u7f29\u9000\u5f97\u66f4\u5feb\uff0c\u752b\u4e0e\u5bf9\u65b9\u5251\u52bf\u4e00\u63a5\uff0c\u4fbf\u5df2\u529b\u7aed \u4e0b\u5782\uff0c\u773c\u770b\u4e09\u67c4\u957f\u5251\u4e58\u52bf\u653b\u5230\uff0c\u8fd9\u4e00\u2026 \u3000\u3000\u4ee3\u67ad\u96c4\uff0c\u8d25\u4ea1\u53ea\u5728\u4fc4\u9877\u4e4b\u95f4\uff0c\u84e6\u5730\u2014\u2014\u2014\u58f0\u5a07\u53f1\uff01 \u3000\u3000\u4e00\u9053\u8000\u76ee\u7ea2\u5149\uff0c\u51cc\u7a7a\u7535\u5c04\u800c\u81f3\uff01 \u3000\u3000\u201c\u5de6\u624b\u795e\u5251\u201d\u4e01\u8863\u8eab\u968f\u5ff5\u8f6c\uff0c\u51b7\u54fc\u4e00\u58f0\uff0c\u523a\u5411\u6bdb\u81ec\u7684\u957f\u5251\u7a81\u5730\u4e00\u7ffb\uff0c\u5251\u5c16\u659c\u5411\u4e0a\u6311\uff0c\u8fce \u7740\u90a3\u9053\u7ea2\u5149\u7ede\u53bb\u3002 \u3000\u3000\u53cc\u65b9\u5251\u5149\u4e00\u63a5\uff0c\u201c\u5de6\u624b\u795e\u5251\u201d\u7acb\u89c9\u638c\u4e2d\u957f\u5251\u7a81\u7136\u9047\u7740\u4e00\u80a1\u6781\u5f3a\u7684\u5438\u529b\uff0c\u4f7f\u4ed6\u7adf\u7136\u628a\u6301\u4e0d \u4f4f\uff0c\u4e0d\u7981\u5927\u5403\u4e00\u60ca1\u53ea\u542c\u7a7a\u4e2d\u4e00\u58f0\u5a07\u53f1\u9053\uff1a\u201c\u6492\u624b\uff01\u201d \u3000\u3000\u6ee1\u5929\u5149\u5f71\u6643\u52a8\u4e2d\uff0c\u201c\u5de6\u624b\u795e\u5251\u201d\u679c\u7136\u5e94\u58f0\u6492\u624b\u4e22\u5251\uff0c\u5530\u5730\u66b4\u9000\u800c\u51fa\uff0c\u9a87\u7136\u6728\u7acb\u5f53\u5730\uff0c\u534a \u664c\u5598\u4e0d\u8fc7\u6c14\u6765\u3002 \u3000\u3000\u8239\u5934\u4e0a\u4eba\u5f71\u4e00\u6643\uff0c\u6bdb\u6587\u742a\u5df2\u624b\u6a2a\u201c\u7425\u73c0\u795e\u5251\u201d\u7ef0\u7acb\u5728\u6bdb\u81ec\u8eab\u4fa7\u3002\u9762\u4e0a\u795e\u60c5\uff0c\u4ea6\u4e0d\u77e5\u662f\u60b2 \u662f\u6012\u3002 \u3000\u3000\u53ea\u89c1\u5979\u7389\u624b\u4e00\u6325\uff0c\u90a3\u67c4\u5438\u9644\u5728\u201c\u7425\u73c0\u795e\u5251\u201d\u4e0a\u7684\u957f\u5251\uff0c\u7a81\u5730\u51b2\u5929\u98de\u8d77\uff0c\u8fdc\u8fdc\u843d\u5728\u5824\u5cb8 \u4e0a\u3002 \u3000\u3000\u8fd9\u79cd\u7f55\u89c1\u7f55\u95fb\u7684\u529f\u529b\uff0c\u987f\u4f7f\u7fa4\u96c4\u8d77\u4e86\u4e00\u9635\u9a9a\u52a8\uff0c\u201c\u6cb3\u6714\u53cc\u5251\u201d\u66f4\u662f\u8138\u8272\u5ffd\u9752\u5ffd\u81ea\uff0c\u96be\u770b \u5df2\u6781\uff0c\u53ea\u56e0\u4ed6\u4e24\u4eba\u65e9\u5df2\u8bd5\u8fc7\u8fd9\u67c4\u5251\u7684\u5a01\u5200\uff01 \u3000\u3000\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u8eab\u5f62\u65b9\u7a33\uff0c\u638c\u4e2d\u957f\u97ad\uff0c\u7a81\u5730\u53cd\u5377\u800c\u51fa\uff0c\u97ad\u68a2\u6709\u5982\u86c7\u5c3e\u4e00\u822c\u5377\u4f4f\u4e86\u6c6a\u4e00\u9e23\u7684 \u8116\u5b50\uff1a\u624b\u8155\u4e00\u9707\uff0c\u5389\u53f1\u9053\uff1a\u201c\u53bb\uff01\u201d \u3000\u3000\u6c6a\u4e00\u9e23\u534a\u58f0\u60e8\u547c\u5c1a\u672a\u51fa\u53e3\uff0c\u7acb\u65f6\u6c14\u7edd\uff0c\u4ed6\u9880\u957f\u7684\u8eab\u8eaf\uff0c\u4e5f\u968f\u7740\u7075\u86c7\u957f\u97ad\u8fd9\u4e00\u9707\u4e4b\u52bf\uff0c\u5657 \u5730\u843d\u4eba\u6e56\u4e2d\uff01 \u3000\u3000\u7fa4\u8c6a\u54c4\u7136\u5927\u54d7\uff0c\u5269\u4e0b\u7684\u51e0\u4e2a\u8239\u5a18\uff0c\u518d\u4e5f\u5446\u4e0d\u4f4f\u4e86\uff0c\u8fde\u6eda\u5e26\u722c\u5730\u9003\u4e86\u5f00\u53bb\uff0c\u8fde\u5979\u4eec\u5168\u90e8\u7684 \u8eab\u5bb6\u2014\u2014\u753b\u822b\u90fd\u987e\u4e0d\u5f97\u8981\u4e86\uff0c\u5979\u4eec\u65b9\u624d\u8fd8\u5728\u7fa1\u6155\u8fd9\u4e9b\u6c5f\u6e56\u6b66\u58eb\u7684\u8c6a\u6c14\u82f1\u98ce\uff0c\u6b64\u523b\u5374\u53d1\u8a93\u4ee5\u540e \u518d\u4e5f\u4e0d\u6562\u9886\u6559\uff01 \u3000\u3000\u6bdb\u6587\u742a\u53cc\u76ee\u5fae\u76b1\uff0c\u4e00\u58f0\u7239\u7239\u8fd8\u672a\u51fa\u53e3\uff0c\u7075\u86c7\u6bdb\u81ec\u957f\u97ad\u518d\u6b21\u5c55\u52a8\uff0c\u76f4\u53d6\u6c6a\u4e00\u9e4f\u54bd\u5589\uff01 \u3000\u3000\u6c6a\u6c0f\u6606\u4ef2\u4e00\u89c1\u201c\u7425\u73c0\u795e\u5251\u201d\uff0c\u4fbf\u5df2\u80c6\u5bd2\uff0c\u662f\u4ee5\u65b9\u624d\u6c6a\u4e00\u9e23\u624d\u4f1a\u88ab\u4e00\u51fb\u800c\u4e2d\uff0c\u6b64\u523b\u6c6a\u4e00\u9e4f \u66f4\u662f\u80c6\u6218\u5fc3\u5bd2\uff0c\u5355\u81c2\u4e00\u626c\uff0c\u957f\u5251\u8131\u624b\u98de\u51fa\uff0c\u76f4\u523a\u6bdb\u81ec\u80f8\u819b\uff0c\u4eba\u5374\u85c9\u52bf\u6251\u98de\u4e09\u5c3a\uff0c\u843d\u6c34\u800c\u9003\uff01 \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u76db\u6012\u4e4b\u4e0b\uff0c\u5df2\u4e0d\u518d\u987e\u53ca\u6b66\u6797\u7fa4\u8c6a\u5bf9\u4ed6\u7684\u770b\u6cd5\uff0c\u970d\u7136\u8f6c\u8eab\uff0c\u76f4\u89c6\u5de6\u624b\u795e\u5251\u4e01\u8863\uff01 \u3000\u3000\u4ed6\u68ee\u5bd2\u7684\u76ee\u5149\uff0c\u6709\u5982\u5229\u526a\u4e00\u822c\uff0c\u526a\u7834\u4e86\u4e01\u8863\u7684\u94c1\u80c6\u3002 \u3000\u3000\u5de6\u624b\u795e\u5251\u4e01\u8863\u7f13\u7f13\u540e\u9000\u7740\u811a\u6b65\uff0c\u7f13\u7f13\u9000\u5230\u4e86\u767e\u6b65\u98de\u82b1\u6797\u7426\u7b5d\u8eab\u540e\uff0c\u53ea\u542c\u6bdb\u81ec\u51b7\u51b7\u9053\uff1a \u201c\u4f60\u4e24\u4eba\u8fd8\u6709\u4ec0\u4e48\u8bdd\u8bf4\uff1f\u201d \u3000\u3000\u6797\u7426\u7b5d\u54ce\u54df\u4e00\u58f0\uff0c\u5f3a\u7b11\u9053\uff1a\u201c\u6bdb\u5927\u54e5\uff0c\u65b9\u624d\u53ef\u6ca1\u6709\u6211\u7684\u4e8b\uff0c\u60a8\u600e\u4e48\u628a\u5e10\u7b97\u5230\u6211\u5934\u4e0a\uff0c\u82e5 \u4e0d\u662f\u6587\u742a\u4e16\u59b9\u8d76\u6765\uff0c\u6211\u4e5f\u4f1a\u5e2e\u6bdb\u5927\u54e5\u51fa\u624b\u7684\uff01\u201d \u3000\u3000\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u51b7\u7b11\u4e00\u58f0\uff0c\u9ed8\u7136\u79fb\u52a8\u7740\u811a\u6b65\uff0c\u4e00\u6b65\u4e00\u6b65\u5730\u5411\u4ed6\u4e24\u4eba\u8d70\u4e86\u8fc7\u53bb\uff0c\u76ee\u4e2d\u6ee1\u542b\u7740 \u4e00\u7247\u6740\u673a\uff01 \u3000\u3000\u6797\u7426\u7b5d\u7389\u5bb9\u60e8\u53d8\uff0c\u7a81\u7136\u53cd\u624b\u4e00\u628a\uff0c\u6263\u4f4f\u4e86\u5de6\u624b\u795e\u5251\u4e01\u8863\u7684\u624b\u8155\uff0c\u53e3\u4e2d\u5a07\u53f1\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u6bdb\u5927\u54e5\uff0c\u6211\u5c06\u4ed6\u4ea4\u7ed9\u4f60\uff0c\u6211\u8981\u8d70\u4e86\uff01\u201d \u3000\u3000\u8bdd\u58f0\u4e2d\u5979\u7adf\u5c06\u4e01\u8863\u7b14\u76f4\u63b7\u5411\u6bdb\u81ec\uff0c\u81ea\u5df1\u7ea4\u8170\u5fae\u62e7\uff0c\u8eab\u5b50\u5012\u7a9c\u800c\u51fa\uff0c\u63a0\u4e0a\u4e86\u4e00\u682a\u67f3\u6811\u3002\u67f3 \u679d\u4e00\u5f39\uff0c\u5979\u7a88\u7a95\u7684\u8eab\u5b50\u968f\u4e4b\u98de\u8d77\uff0c\u63a5\u7740\u51e0\u4e2a\u8d77\u843d\uff0c\u9003\u5f97\u65e0\u5f71\u65e0\u8e2a\uff01 \u3000\u3000\u5de6\u624b\u795e\u5251\u4e01\u8863\u518d\u4e5f\u60f3\u4e0d\u5230\u5979\u7adf\u5982\u6b64\u72e0\u8fa3\uff0c\u624b\u8155\u88ab\u5236\uff0c\u5168\u8eab\u9178\u8f6f\uff0c\u8eab\u4e0d\u7531\u4e3b\u5730\u5411\u6bdb\u81ec\u6251\u4e86 \u8fc7\u53bb\uff01 \u3000\u3000\u6bdb\u81ec\u76ee\u4e2d\u6740\u673a\u95ea\u52a8\uff0c\u53f3\u638c\u76f4\u51fb\u800c\u51fa\uff01\u201c\u7830\u201d\u5730\u4e00\u58f0\u51fb\u5728\u4e01\u8863\u90a3\u5bbd\u9614\u7684\u80f8\u819b\u4e0a\uff0c\u4ed6\u5168\u529b\u51fb \u51fa\u4e00\u638c\uff0c\u529b\u9053\u4f55\u6b62\u5343\u94a9\uff01 \u3000\u3000\u53ea\u89c1\u4e01\u8863\u72c2\u543c\u4e00\u58f0\uff0c\u55b7\u51fa\u6ee1\u53e3\u9c9c\u8840\uff0c\u8eab\u5b50\u4ef0\u5929\u98de\u8d77\uff0c\u8dcc\u843d\u5230\u90a3\u59cb\u7ec8\u7aef\u5750\u5bfa\u52a8\u7684\u6f58\u4f65\u3001\u7a0b \u9a79\u7684\u9762\u524d\uff01 \u3000\u3000\u6f58\u4f65\u3001\u7a0b\u9a79\u795e\u8272\u4e0d\u53d8\uff0c\u6de1\u6de1\u5730\u5bf9\u671b\u4e00\u773c\uff0c\u4e24\u4eba\u5634\u5507\u5fae\u52a8\uff0c\u6beb\u65e0\u58f0\u97f3\u53d1\u51fa\uff0c\u539f\u6765\u6b63\u662f\u4ee5 \u201c\u4f20\u97f3\u5165\u5bc6\u201d\u4e4b\u529f\u5bf9\u8bdd\u3002 \u3000\u3000\u3000\u3000\u6f58\u4f65\u9053\uff0c\u201c\u4f60\u770b\u5230\u6055\u513f\u4e86\u4e48\uff1f\u201d \u3000\u3000\u7a0b\u9a79\u9053\uff1a\u201c\u65e9\u5c31\u770b\u5230\u4e86\uff0c\u4ed6\u6b63\u5728\u8eb2\u8eb2\u85cf\u85cf\u5730\u7ad9\u5728\u90a3\u8fb9\u5824\u5cb8\u4e0a\uff0c\u5374\u8eb2\u4e0d\u5f00\u6211\u7684\u76ee\u5149\uff0c\u53ea\u662f \u4ed6\u65e2\u4e0d\u613f\u73b0\u8eab\uff0c\u6211\u4eec\u4e5f\u6700\u597d\u4e0d\u8981\u591a\u4e8b\u51fa\u624b\u3002\u201d \u3000\u3000\u6f58\u4f65\u9053\uff1a\u201c\u6211\u4eec\u65e2\u7136\u770b\u5230\u4e86\u4ed6\uff0c\u8fd8\u5728\u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u8d70\u5427\uff01\u201d \u3000\u3000\u539f\u6765\u4ed6\u4e24\u4eba\u968f\u4ec7\u6055\u4e4b\u540e\u5230\u4e86\u6c5f\u5357\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u5374\u53c8\u627e\u4e0d\u7740\u4ec7\u6055\uff0c\u4e24\u4eba\u5546\u8bae\u4e4b\u4e0b\uff0c\u4fbf\u53c8\u91cd \u65bd\u6545\u8ba1\uff0c\u5148\u627e\u6bdb\u81ec\uff0c\u8fd8\u88c5\u6a21\u4f5c\u6837\uff0c\u8981\u4e3a\u6bdb\u81ec\u52a9\u62f3\uff0c\u4e3a\u7684\u53ea\u4e0d\u8fc7\u662f\u8981\u5bfb\u4ec7\u6055\u3002\u6b64\u523b\u4e24\u4eba\u53d1\u73b0\u4e86 \u4ec7\u6055\uff0c\u4fbf\u518d\u4e5f\u4e0d\u613f\u505c\u7559\uff0c\u888d\u8896\u4e00\u5c55\u9f50\u5730\u5c55\u52a8\u8eab\u5f62\uff0c\u7a7f\u7a97\u800c\u51fa\u3002 \u3000\u3000\u5824\u5cb8\u4e0a\u7684\u4ec7\u6055\u4e00\u89c1\u4ed6\u4e24\u4eba\u8fdc\u8fdc\u63a0\u6765\uff0c\u8eab\u5f62\u4e00\u95ea\uff0c\u6709\u5982\u8f7b\u70df\u822c\u6e9c\u8d70\uff0c\u7adf\u4f3c\u4e0d\u613f\u548c\u4ed6\u4e24\u4eba\u89c1 \u9762\u4e00\u6837\uff01 \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u53d8\u8272\u9053\uff1a\u201c\u4e24\u4f4d\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u4ed6\u4e00\u5fc3\u60f3\u4ef0\u4ed7\u8fd9\u4e24\u4eba\u5bf9\u4ed8\u4ec7\u72ec\u7684\u540e\u4eba\uff0c\u6b64\u523b\u89c1\u4ed6\u4e24\u4eba\u7adf\u4e0d\u544a\u800c\u522b\uff0c\u5fc3\u4e2d\u53c8\u60ca\u53c8\u6012\uff0c\u65b9\u5f85 \u8ffd\u51fa\u3002 \u3000\u3000\u6bdb\u6587\u742a\u5374\u5df2\u6a2a\u8eab\u6321\u5728\u4ed6\u9762\u524d\uff0c\u9053\uff1a\u201c\u7239\u7239\uff0c\u4e0d\u8981\u8ffd\u4e86\uff0c\u8ffd\u4e5f\u8ffd\u4e0d\u5230\u7684\uff0c\u8fd9\u8239\u4e0a\u8fd8\u6709\u4e2a\u66f4 \u53ef\u6068\u7684\u4eba\uff0c\u4f60\u8001\u4eba\u5bb6\u96be\u9053\u8fd8\u4e0d\u77e5\u9053\u4e48\uff1f\u201d \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u4f17\u53db\u4eb2\u79bb\uff0c\u5e38\u6001\u5df2\u5931\uff0c\u6012\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u6bdb\u6587\u742a\u7f13\u7f13\u8f6c\u8fc7\u76ee\u5149\uff0c\u7b14\u76f4\u5730\u671b\u5411\u201c\u8fd8\u9b42\u201d\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u8001\u4eba\u5bb6\u96be\u9053\u4ee5\u4e3a\u4ed6\u771f\u7684\u662f \u2018\u95ea\u7535\u795e\u5200\u2019\u6731\u5b50\u660e\u4e48\uff1f\u201d \u3000\u3000\u6bdb\u81ec\u5fc3\u5ff5\u52a8\u5904\uff0c\u8eab\u5b50\u4e00\u9707\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4ed6\u4e0d\u662f\u6731\u5b50\u660e\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u6bdb\u6587\u742a\u9053\uff1a\u201c\u6731\u5b50\u660e\u65e9\u5df2\u6b7b\u4e86\uff0c\u4ed6\u53ea\u4e0d\u8fc7\u662f\u501f\u7740\u6731\u5b50\u660e\u7684\u540d\u4e49\uff0c\u5047\u626e\u6210\u2018\u8fd8\u9b42\uff0c\u540e\u795e\u667a \u4e0d\u6e05\u7684\u6837\u5b50\uff0c\u6765\u9a97\u4f60\u8001\u4eba\u5bb6\u7684\uff0c\u800c\u4e14\u4ed6\u8fd8\u4e0d\u662f\u7b2c\u4e00\u4e2a\u2019\u8fd8\u9b42\u2019\uff0c\u7b2c\u4e00\u4e2a\u2018\u8fd8\u9b42\u2019\uff0c\u662f\u6211\u5e08 \u59d0\u3002\u201d \u3000\u3000\u5979\u8d8a\u8bf4\u522b\u4eba\u8d8a\u662f\u7cca\u6d82\uff0c\u4e0d\u4f46\u2018\u7075\u86c7\u2019\u6bdb\u684c\u832b\u7136\u4e0d\u89e3\uff0c\u90a3\u5047\u626e\u2018\u8fd8\u9b42\uff0c\u663e\u7136\u4ea6\u662f\u6ee1\u5934\u96fe \u6c34\uff01\u7075\u86c7\u6bdb\u81ec\u5446\u4e86\u4e00\u5446\u9053\uff1a\u201c\u4f60\u5e08\u59d0\u2026\u2026\u7b2c\u4e00\u4e2a\u2018\u8fd8\u9b42\u2019\u2026\u2026\u201d \u3000\u3000\u6bdb\u6587\u742a\u8f7b\u53f9\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u8bf4\u6765\u8bdd\u957f\uff0c\u6211\u4e00\u65f6\u4e5f\u8bf4\u4e0d\u6e05\u695a\u2026\u2026\u201d \u3000\u3000\u5979\u5806\u7136\u8f6c\u8eab\uff0c\u9762\u5411\u201c\u8fd8\u9b42\u201d\u9053\uff1a\u201c\u4f46\u4f60\u82e5\u662f\u82f1\u96c4\uff0c\u5c31\u8bf7\u5c06\u4f60\u7684\u771f\u9762\u76ee\u73b0\u51fa\u6765\u3002\u5802\u5802\u7684\u7537 \u5b50\u6c49\uff0c\u85cf\u5934\u9732\u5c3e\uff0c\u9690\u59d3\u57cb\u540d\uff0c\u83ab\u975e\u8fde\u5973\u5b50\u90fd\u4e0d\u5982\uff1f\u201d \u3000\u3000\u5979\u5fc3\u91cc\u5ffd\u7136\u60f3\u8d77\u4e86\u90a3\u4f2a\u540d\u201c\u7f2a\u6587\u201d\u7684\u4ec7\u6055\uff0c\u662f\u4ee5\u5979\u8bed\u58f0\u4e2d\u4fbf\u663e\u9732\u51fa\u77db\u76fe\u7684\u60c5\u611f\uff0c\u65e2\u662f\u5e7d \u6028\uff0c\u53c8\u662f\u6124\u6012\uff01 \u3000\u3000\u90a3\u7b2c\u4e8c\u4e2a\u5047\u5192\u7684\u201c\u8fd8\u9b42\u201d\u76ee\u5149\u4e00\u8f6c\uff0c\u7a81\u5730\u4ef0\u9762\u72c2\u7b11\u8d77\u6765\uff0c\u6bdb\u6587\u742a\u53cc\u76ee\u5fae\u76b1\uff0c\u6a2a\u5251\u5389\u58f0 \u9053\uff1a\u201c\u4f60\u7b11\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u2018\u8fd8\u9b42\u201d\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff01\u6211\u5047\u5192\u2018\u8fd8\u9b42\u2019\u662f\u4e3a\u4e86\u8981\u9a97\u4f60\u7239\u7239\uff0c\u4f46\u6211\u5374\u4e5f\u672a\u66fe\u60f3\u5230\uff0c\u90a3 \u7b2c\u4e00\u4e2a\u2018\u8fd8\u9b42\u2019\u7adf\u7136\u4e5f\u662f\u5047\u5192\u7684\uff0c\u6211\u4e00\u751f\u4e4b\u4e2d\uff0c\u4ece\u672a\u9047\u5230\u8fd9\u6837\u66f2\u6298\u79bb\u5947\uff0c\u8fd9\u6837\u4ee4\u4eba\u7cca\u6d82\u7684 \u4e8b\uff0c\u53ea\u8981\u4f60\u5c06\u6b64\u4e8b\u771f\u8c61\u8bf4\u4e86\uff0c\u6211\u4e00\u5b9a\u4e5f\u5c06\u771f\u9762\u76ee\u73b0\u51fa\uff01\u201d \u3000\u3000\u6bdb\u6587\u742a\u9053\uff1a\u201c\u4f60\u8bf4\u8bdd\u7b97\u4e0d\u7b97\u6570\uff1f\u201d \u3000\u3000\u201c\u8fd8\u9b42\u201d\u9053\uff1a\u201c\u541b\u5b50\u4e00\u8a00\uff0c\u5feb\u9a6c\u4e00\u97ad\uff01\u201d \u3000\u3000\u6bdb\u6587\u742a\u7f13\u7f13\u9053\uff1a\u201c\u6211\u5e08\u59d0\u6155\u5bb9\u60dc\u751f\uff0c\u4e3a\u4e86\u8981\u63a2\u67e5\u90a3\u4ec7\u72ec\u4e4b\u5b50\u7684\u771f\u8c61\uff0c\u662f\u4ee5\u5047\u5192\u6210\u65e9\u5df2\u6b7b \u4e86\u7684\u2018\u95ea\u7535\u795e\u5200\u2019\u6731\u5b50\u660e\uff0c\u5367\u5e95\u5728\u4ec7\u72ec\u4e4b\u5b50\u5bb6\u91cc\u3002\u201d\u8fd8\u9b42\u201d\u604d\u7136\u9053\uff1a\u201c\u5979\u4e3a\u4e86\u4e54\u88c5\u6613\u5bb9\uff0c\u6240 \u4ee5\u624d\u626e\u6210\u8fd9\u79cd\u6837\u5b50\uff0c\u800c\u6211\u5374\u4ee5\u4e3a\u4e16\u4e0a\u771f\u6709\u5176\u4eba\uff0c\u4e0d\u60f3\u5374\u4e0a\u4e86\u5979\u7684\u5f53\uff01\u201d \u3000\u3000\u6bdb\u6587\u742a\u51b7\u7b11\u9053\uff1a\u201c\u6211\u5e08\u59d0\u5929\u7eb5\u5947\u624d\uff0c\u4f60\u600e\u4e48\u6bd4\u5f97\u4e0a\u5979\uff0c\u4f60\u53ea\u60f3\u5e2e\u4ec7\u72ec\u513f\u5b50\u7684\u5fd9\uff0c\u53c8\u89c1\u5230 \u2018\u8fd8\u9b42\u2019\u7684\u5f62\u72b6\u5bb9\u6613\u4e54\u88c5\uff0c\u4fbf\u80cc\u4e86\u7a0b\u67ab\u7684\u5c38\u8eab\uff0c\u5230\u6211\u5bb6\u6765\u5367\u5e95\uff0c\u5176\u5b9e\u90a3\u7a0b\u67ab\u4e5f\u662f\u4ec7\u72ec\u4e4b\u4e86\u6740 \u6b7b\u7684\uff01\u201d \u3000\u3000\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u53d8\u8272\u9053\uff1a\u201c\u5230\u5e95\u8c01\u662f\u90a3\u4ec7\u72ec\u7684\u513f\u5b50\uff0c\u4ed6\u6b64\u523b\u5728\u54ea\u91cc\u8c03\u6bdb\u6587\u742a\u6697\u4e2d\u4f24\u5fc3\u5730\u957f \u53f9\u4e86\u4e00\u58f0\uff0c\u6545\u610f\u88c5\u4f5c\u6ca1\u6709\u2019\u542c\u5230\u5979\u7239\u7239\u95ee\u5979\u7684\u8bdd\uff0c\u9762\u5411\u2018\u8fd8\u9b42\u201d\u63a5\u53e3\u9053\uff1a\u201c\u6211\u5df2\u5c06\u6b64\u4e8b\u7684\u771f \u8c61\u8bf4\u51fa\uff0c\u4f60\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd8\u9b42\u201d\u5446\u4e86\u534a\u664c\uff0c\u7a81\u53c8\u72c2\u7b11\u9053\uff1a\u201c\u4f60\u5b9a\u8981\u77e5\u9053\u6211\u662f\u8c01\u4e48\uff1f\u201d \u3000\u3000\u6bdb\u6587\u742a\u8f7b\u8f7b\u4e00\u9707\u624b\u8155\uff0c\u638c\u4e2d\u201c\u7425\u73c0\u795e\u5251\u201d\uff0c\u4fbf\u6709\u5982\u706b\u7130\u822c\u7684\u95ea\u52a8\u8d77\u6765\uff0c\u5979\u76ee\u6ce8\u7740\u5251\u5c16\u7f13 \u7f13\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u613f\u81ea\u52a8\u8bf4\u51fa\uff0c\u53ea\u6015\u6211\u8fd9\u67c4\u5251\u4e5f\u5bb9\u4e0d\u5f97\u4f60\uff01\u201d \u3000\u3000\u201c\u8fd8\u9b42\u201d\u51b7\u7b11\u9053\uff1a\u201c\u65e0\u8bba\u6211\u662f\u5426\u81ea\u52a8\u8bf4\u51fa\uff0c\u4f60\u8fd9\u67c4\u5251\u6211\u4e5f\u8981\u9886\u6559\u9886\u6559\u7684\uff01\u201d \u3000\u3000\u6bdb\u6587\u742a\u8f7b\u53f1\u9053\uff1a\u201c\u597d\uff01\u201d \u3000\u3000\u53ea\u89c1\u4e00\u6e9c\u8d64\u7ea2\u7684\u5251\u5149\uff0c\u968f\u7740\u5979\u8f7b\u53f1\u4e4b\u58f0\u5212\u51fa\uff01 \u3000\u3000\u201c\u8fd8\u9b42\u201d\u5b58\u5fc3\u60f3\u4e00\u8bd5\u5979\u8fd9\u67c4\u201c\u7425\u73c0\u795e\u5251\u201d\u7684\u795e\u79d8\u4e4b\u5904\uff0c\u4e0d\u9000\u53cd\u8fce\uff0c\u659c\u659c\u4e00\u638c\uff0c\u62cd\u5411\u5251 \u810a\u3002 \u3000\u3000\u54ea\u77e5\u4ed6\u624b\u638c\u65b9\u89e6\u5251\u8eab\uff0c\u8eab\u5b50\u4fbf\u4e3a\u4e4b\u84e6\u5730\u4e00\u9707\uff0c\u624b\u638c\u7adf\u4f3c\u4e4e\u88ab\u8fd9\u67c4\u5251\u4e2d\u4f20\u51fa\u7684\u4e00\u80a1\u5947\u5f02\u4e4b \u529b\u5438\u4f4f\uff0c\u518d\u4e5f\u62bd\u4e0d\u51fa\u6765\uff01 \u3000\u3000\u6bdb\u6587\u742a\u8f7b\u53f1\u4e00\u58f0\uff0c\u957f\u5251\u4e58\u52bf\u9001\u51fa\uff0c\u8f7b\u53f1\u9053\uff1a\u201c\u4f60\u5230\u5e95\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u8bed\u58f0\u672a\u4e86\uff0c\u7a81\u89c1\u201c\u8fd8\u9b42\u201d\u7684\u8eab\u5b50\uff0c\u7adf\u51ed\u7a7a\u66b4\u957f\u4e86\u4e09\u5bf8\uff0c\u75be\u5730\u4e00\u811a\u8e22\u5411\u6bdb\u6587\u742a\u6301\u5251\u7684\u624b \u8155\uff01 \u3000\u3000\u6bdb\u6587\u742a\u811a\u6b65\u4e00\u9519\uff0c\u90a3\u201c\u8fd8\u9b42\u201d\u7684\u8eab\u5f62\u7adf\u5df2\u95ea\u7535\u822c\u7a7f\u7a97\u98de\u51fa\uff0c\u6bdb\u6587\u742a\u60f3\u4e0d\u5230\u6b64\u4eba\u7adf\u80fd\u5728 \u201c\u7425\u73c0\u795e\u5251\u201d\u4e0a\u8131\u8eab\uff0c\u5fc3\u5934\u4e0d\u89c9\u4e00\u51db\uff01 \u3000\u3000\u53ea\u542c\u4e00\u9635\u6e05\u6717\u7684\u7b11\u58f0\u81ea\u7a97\u5916\u4f20\u6765\uff0c\u9053\uff1a\u201c\u4f60\u8981\u77e5\u9053\u6211\u662f\u8c01\u4e48\uff1f\u770b\u770b\u8fd9\u4e2a\uff01\u201d \u3000\u3000\u968f\u7740\u8fd9\u4e00\u9635\u6e05\u6717\u7684\u7b11\u58f0\uff0c\u4e00\u9053\u91d1\u5149\uff0c\u7a7f\u7a97\u800c\u6765\uff01 \u3000\u3000\u201c\u7075\u86c7\u201d\u6bdb\u81ec\u5927\u60ca\u4e4b\u4e0b\uff0c\u8eab\u5f62\u6025\u95ea\uff01 \u3000\u3000\u6bdb\u6587\u742a\u957f\u5251\u6025\u6325\uff0c\u53ea\u542c\u53ee\u5730\u4e00\u58f0\uff0c\u90a3\u9053\u91d1\u5149\u4fbf\u88ab\u5979\u638c\u4e2d\u201c\u7425\u73c0\u795e\u5251\u201d\u5438\u4f4f\uff0c\u8d6b\u7136\u7adf\u662f\u4e00 \u67c4\u957f\u4ec5\u6570\u5bf8\u7684\u91d1\u5251\uff01 \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u9762\u8272\u5927\u53d8\uff0c\u8131\u53e3\u60ca\u547c\u9053\uff1a\u201c\u91d1\u5251\u4fa0\uff01\u201d \u3000\u3000\u4ed6\u4e00\u6b65\u63a0\u5230\u7a97\u524d\uff0c\u53ea\u89c1\u7a97\u5916\u6ee1\u5824\u67f3\u679d\uff0c\u968f\u98ce\u98d8\u821e\uff0c\u65e5\u8272\u5df2\u6e10\u897f\u6c89\uff0c\u54ea\u91cc\u8fd8\u6709\u91d1\u5251\u4fa0\u7684\u8eab \u5f71\uff01 \u3000\u3000\u4ed6\u5446\u5446\u5730\u6728\u7acb\u534a\u664c\uff0c\u8f6c\u8eab\u957f\u53f9\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u91d1\u5251\u4fa0\u8fd9\u53ae\u7adf\u5728\u6211\u7684\u8239\u4e0a\uff1f\u201d \u3000\u3000\u6bdb\u6587\u742a\u5782\u9996\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u8001\u4eba\u5bb6\u2026\u2026\u4f60\u8001\u4eba\u5bb6\u2026\u2026\u201d \u3000\u3000\u5979\u867d\u6709\u6ee1\u8154\u7684\u8bdd\u8981\u529d\u5979\u7239\u7239\uff0c\u5374\u53c8\u88ab\u6ee1\u8154\u7684\u5e7d\u6028\u4e00\u9f50\u51bb\u5f97\u6b7b\u6b7b\u7684\uff0c\u7adf\u8fde\u4e00\u53e5\u4e5f\u8bf4\u4e0d\u51fa \u6765\u3002 \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u80f8\u819b\u4e00\u633a\uff0c\u7f13\u6b65\u8d70\u5230\u8239\u5934\u3002 \u3000\u3000\u4ed6\u4f3c\u4e4e\u8fd8\u60f3\u5bf9\u6e56\u4e0a\u7fa4\u8c6a\u8bf4\u4e00\u4e9b\u8bdd\uff0c\u4f46\u8f6c\u76ee\u671b\u5904\uff0c\u6ee1\u6e56\u7684\u7fa4\u8c6a\uff0c\u867d\u8fd8\u672a\u8d70\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u4f46 \u5269\u4e0b\u7684\u4eba\u4e5f\u5df2\u5be5\u5be5\u53ef\u6570\u3002 \u3000\u3000\u5239\u90a3\u95f4\uff0c\u4ed6\u53ea\u89c9\u4e00\u9635\u5931\u8d25\u7684\u60b2\u54c0\u4e0e\u8427\u7d20\uff0c\u84e6\u5730\u6d8c\u4e0a\u4e86\u5fc3\u5934\uff0c\u5835\u585e\u5728\u5589\u95ee\uff0c\u4f7f\u5f97\u8fd9\u53f1\u54a4\u4e00 \u65f6\uff0c\u53e3\u624d\u654f\u6377\u7684\u6b66\u6797\u67ad\u96c4\uff0c\u7adf\u4e5f\u8fde\u4e00\u53e5\u8bdd\u90fd\u8bf4\u4e0d\u51fa\u6765\uff0c\u53ea\u6709\u9762\u5bf9\u7740\u90a3\u4e00\u4e9b\u5be5\u843d\u7684\u4eba\u5934\uff0c\u843d\u5bde \u7684\u76ee\u5149\uff0c\u5446\u5446\u5730\u51fa\u8d77\u795e\u6765\uff01 \u3000\u3000\u6e56\u6c34\u8361\u6f3e\uff0c\u6625\u98ce\u4f3c\u4e5f\u53d8\u6210\u4e86\u79cb\u98ce\u822c\u8427\u7d22\u3002 \u3000\u3000\u82f1\u96c4\u7684\u57fa\u4e1a\uff0c\u6210\u529f\u5f97\u5fc5\u5b9a\u5341\u5206\u8270\u82e6\u7f13\u6162\uff0c\u4f46\u5931\u8d25\u65f6\u5374\u6709\u5982\u706b\u878d\u51b0\u6d88\uff0c\u9877\u523b\u95f4\u4fbf\u5316\u4f5c\u4e86\u6d41 \u6c34\uff01 \u3000\u3000\u8fd9\u867d\u662f\u5343\u53e4\u4e0d\u53d8\u7684\u9053\u7406\uff0c\u600e\u5948\u5f53\u4e8b\u4eba\u5374\u6c38\u8fdc\u90fd\u60f3\u5b83\u4e0d\u5f00\uff0c\u6210\u529f\u540e\u518d\u5931\u8d25\u7684\u6ecb\u5473\uff0c\u6bd4\u6c38\u4e0d \u6210\u529f\u8fd8\u8981\u4ee4\u4eba\u60b2\u54c0\u3002 \u3000\u3000\u4ed6\u5353\u7acb\u8239\u5934\uff0c\u7a81\u89c9\u6ee1\u8eab\u5bd2\u610f\uff0c\u53e3\u4e2d\u5f3a\u7b11\u9053\uff1a\u201c\u6bdb\u81ec\u4e00\u751f\u95ef\u8361\u6c5f\u6e56\uff0c\u6210\u8d25\u4e14\u4e0d\u8bba\u5b83\uff0c\u7ec8\u7b97 \u80fd\u4ea4\u7740\u5404\u4f4d\u8fd9\u51e0\u4f4d\u670b\u53cb\uff0c\u6bdb\u81ec\u5df2\u662f\u5341\u5206\u201d\u8bed\u58f0\u672a\u4e86\uff0c\u7a81\u542c\u4e00\u9635\u6025\u5267\u7684\u9a6c\u8e44\u5954\u817e\u58f0\uff0c\u81ea\u8fdc\u5904\u54cd \u8d77\uff0c\u5341\u6570\u5339\u957f\u7a0b\u5065\u9a6c\uff0c\u6025\u9a70\u800c\u6765\u3002 \u3000\u3000\u5f53\u5148\u4e00\u4e00\u5339\u5065\u9a6c\uff0c\u9a6c\u978d\u4e0a\u7aef\u5750\u7740\u4e00\u4e2a\u9ec4\u9762\u5c11\u5e74\uff0c\u733f\u80cc\u83ba\u8170\uff0c\u8170\u80a2\u7b14\u633a\uff0c\u4e00\u8def\u626c\u81c2\u5927\u547c \u9053\uff1a\u201c\u82e5\u975e\u6bdb\u81ec\u4e4b\u53cb\uff0c\u5feb\u79bb\u6e56\u8239\uff0c\u4ee5\u514d\u81ea\u8bef\uff01\u201d \u3000\u3000\u547c\u58f0\u5639\u4eae\uff0c\u76f4\u4e0a\u9704\u6c49\uff01 \u3000\u3000\u4ec5\u5b58\u5728\u6e56\u4e0a\u7684\u4eba\u7269\uff0c\u4e00\u542c\u8fd9\u9635\u547c\u58f0\uff0c\u4fbf\u518d\u4e5f\u4e0d\u542c\u6bdb\u81ec\u7684\u8bf4\u8bdd\uff0c\u7eb7\u7eb7\u81ea\u8239\u5c3e\u4e0a\u5cb8\uff0c\u5404\u81ea\u6563 \u4e86\uff01 \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u53c8\u60b2\u53c8\u6012\uff0c\u76ee\u5149\u4e00\u77a5\u90a3\u9ec4\u9762\u5c11\u5e74\uff0c\u53d8\u8272\u9053\uff1a\u201c\u91d1\u8d85\u96c4\uff0c\u4f60\u4e5f\u6765\u4e86\uff01\u201d \u3000\u3000\u8fd9\u9ec4\u9762\u5c11\u5e74\u6b63\u662f\u201c\u592a\u884c\u53cc\u4e49\u4e2d\u201d\u7684\u5927\u54e5\u91d1\u8d85\u96c4\uff0c\u6b64\u523b\u4e00\u626c\u4e1d\u97ad\uff0c\u5728\u9a6c\u4e0a\u6717\u58f0\u72c2\u7b11\u7740\u9053\uff1a \u201c\u4e0d\u9519\uff0c\u6211\u6765\u4e86\uff0c\u4f60\u7684\u6b7b\u671f\u4e5f\u6765\u4e86\uff01\u201d \u3000\u3000\u4ed6\u4e1d\u97ad\u659c\u659c\u5411\u540e\u4e00\u6307\uff0c\u72c2\u7b11\u9053\uff1a\u201c\u4f60\u4e14\u770b\u770b\u90a3\u8fb9\u4f60\u7684\u8001\u5de2\u5df2\u88ab\u5c11\u7237\u6211\u653e\u706b\u70e7\u4e86\uff0c\u4f60\u65e9\u5df2 \u4f17\u53db\u4eb2\u79bb\uff0c\u6b64\u523b\u66f4\u65e0\u5bb6\u53ef\u5f52\uff0c\u6d3b\u7740\u8fd8\u6709\u4ec0\u4e48\u610f\u601d\uff1f\u4e0d\u5982\u6b7b\u4e86\u7b97\u4e86\uff01\u8bdd\u58f0\u4e2d\u4e1d\u97ad\u4e00\u843d\uff0c\u5341\u6570\u5339 \u5065\u9a6c\u8f6c\u5934\u5954\u53bb\uff0c\u626c\u8d77\u4e86\u4e00\u80a1\u6de1\u6de1\u7684\u70df\u5c18\uff0c\u77ac\u773c\u95f4\u4fbf\u88ab\u6625\u98ce\u5439\u6563\uff0c\u6b63\u6709\u5982\u6bdb\u81ec\u7684\u4e8b\u4e1a\u4e00\u822c\uff01\u7075 \u86c7\u6bdb\u81ec\u60ca\u6012\u4e4b\u4e0b\uff0c\u8f6c\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u81ea\u5df1\u5bb6\u5b85\u90a3\u8fb9\uff0c\u5df2\u6709\u4e00\u80a1\u70df\u706b\uff0c\u51b2\u9704\u800c\u8d77\uff01\u6bdb\u6587\u742a\u751f\u6015\u5979 \u7239\u7239\u6012\u6781\u751f\u53d8\uff0c\u4e00\u628a\u62c9\u4f4f\u4e86\u5979\u7239\u7239\u7684\u624b\u8155\uff0c\u5e7d\u5e7d\u957f\u53f9\u4e00\u58f0\uff0c\u5782\u9996\u8bf4\u9053\uff1a\u201c\u7239\u7239\u4f60\u8001\u4eba\u5bb6\u672c\u5df2 \u5230\u4e86\u6d17\u624b\u5f52\u9690\u7684\u65f6\u5019\uff0c\u4e58\u7740\u8fd9\u673a\u4f1a\u627e\u4e2a\u5730\u65b9\u9690\u5c45\u4f4f\u4e0b\uff0c\u8ba9\u5973\u513f\u966a\u7740\u4f60\u6de1\u6cca\u5730 \u7b2c\u4e09\u5341\u7ae0>> \u53e4\u9f99\u300a\u6e58\u5983\u5251\u300b \u7b2c\u4e09\u5341\u7ae0 \u3000\u3000\u72c2\u7b11\u58f0\u4e2d\uff0c\u53ea\u542c\u6bdb\u81ec\u7f13\u7f13\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u4e0d\u8981\u6015\uff0c\u8fd9\u4e9b\u4eba\u51fb\u4e0d\u5012\u4f60\u7239\u7239\u7684\u2026\u2026\u201d \u3000\u3000\u4ed6\u7b11\u58f0\u4e00\u987f\uff0c\u76ee\u5149\u53d8\u5f97\u66f4\u662f\u9634\u9e37\u6df1\u6c89\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u4f60\u7239\u7239\u672a\u81f3\u6b64\u95f4\u4e4b\u524d\uff0c\u65e9\u5df2\u7559\u4e0b\u4e86\u540e \u7740\uff0c\u533a\u533a\u4e00\u4e24\u4e2a\u6253\u51fb\uff0c\u5728\u4f60\u7239\u7239\u8eab\u4e0a\uff0c\u53c8\u7b97\u5f97\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6bdb\u6587\u742a\u53c8\u81ea\u4e00\u5446\uff0c\u5bf9\u5979\u7239\u7239\uff0c\u5979\u5fc3\u91cc\u4e0d\u77e5\u662f\u75db\u60dc\u6291\u6216\u662f\u94a6\u4f69\uff0c\u7ecf\u8fc7\u8fd9\u6837\u7684\u6253\u51fb\uff0c\u5979\u7239\u7239 \u72b9\u80fd\u5c79\u7acb\u4e0d\u5012\uff0c\u505a\u5973\u513f\u7684\u81ea\u4e0d\u7981\u4f1a\u751f\u51fa\u94a6\u4f69\u4e4b\u5fc3\uff0c\u4f46\u4e00\u60f3\u5230\u90a3\u4e5f\u662f\u6c38\u8fdc\u6253\u4e0d\u5012\u7684\u654c\u4eba\u4ec7\u6055\uff0c \u5979\u4e0d\u7981\u66f4\u662f\u5fc3\u788e\u3002 \u3000\u3000\u6bdb\u81ec\u76ee\u5149\u6b63\u5728\u63a2\u7d22\u7740\u5979\u7231\u5973\u7684\u5fc3\u4e8b\uff0c\u4ed6\u77e5\u9053\u4ed6\u5973\u513f\u5fc3\u91cc\u5fc5\u5b9a\u9690\u85cf\u7740\u4e00\u4e9b\u79d8\u5bc6\uff0c\u79d8\u5bc6\u5730\u7792 \u7740\u81ea\u5df1\uff01 \u3000\u3000\u5fc3\u5ff5\u6570\u8f6c\u4e4b\u95f4\uff0c\u4ed6\u7a81\u5730\u8131\u53e3\u9053\uff1a\u201c\u6211\u77e5\u9053\u4e86\uff01\u201d \u3000\u3000\u6bdb\u6587\u742a\u5fc3\u5934\u4e00\u98a4\uff0c\u9053\uff1a\u201c\u4f60\u8001\u4eba\u5bb6\u77e5\u9053\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6bdb\u81ec\u7f13\u7f13\u9053\uff1a\u201c\u7f2a\u6587\u4fbf\u662f\u4ec7\u72ec\u4e4b\u5b50\uff0c\u4ec7\u72ec\u4e4b\u5b50\u4fbf\u662f\u7f2a\u6587\uff01\u201d \u3000\u3000\u8fd9\u6b66\u6797\u67ad\u96c4\uff0c\u5fc3\u667a\u679c\u7136\u8d85\u4eba\u4e00\u7b49\uff0c\u6bdb\u6587\u742a\u4f46\u89c9\u8eab\u5b50\u4e00\u9707\uff0c\u6084\u6084\u540e\u9000\u4e86\u51e0\u6b65\uff0c\u6cea\u73e0\u5df2\u4e0d\u7981 \u6d41\u4e0b\u9762\u988a\uff01 \u3000\u3000\u4fbf\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u7a81\u542c\u4e00\u58f0\u5927\u559d\uff0c\u9053\uff1a\u201c\u6bdb\u81ec\uff0c\u4f60\u770b\u770b\u8c01\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u9a87\u7136\u671b\u53bb\uff0c\u53ea\u89c1\u897f\u6e56\u6e56\u5fc3\u4e4b\u4e2d\uff0c\u7a81\u5730\u94bb\u51fa\u4e86\u4e00\u4e2a\u767d\u53d1\u82cd\u82cd\u7684\u4eba\u5934\uff0c\u81ea\u53d1\u767d\u9aef\uff0c \u88ab\u6e56\u6c34\u6d78\u5f97\u7d27\u7d27\u8d34\u5728\u4e00\u8d77\uff0c\u4e00\u53cc\u8001\u773c\u4e4b\u4e2d\uff0c\u7cbe\u5149\u95ea\u70c1\uff0c\u8d6b\u7136\u7adf\u662f\u90a3\u79f0\u96c4\u6c34\u4e0a\u7684\u8001\u82f1\u96c4\u706b\u773c\u91d1 \u96d5\u8427\u8fdf\uff01 \u3000\u3000\u6bdb\u81ec\u5927\u559d\u4e00\u58f0\uff0c\u638c\u4e2d\u957f\u97ad\uff0c\u95ea\u7535\u822c\u7684\u5411\u8427\u8fdf\u6325\u4e86\u8fc7\u53bb\uff01 \u3000\u3000\u86c7\u97ad\u867d\u957f\uff0c\u4f46\u4ed6\u4e00\u97ad\u6325\u53bb\uff0c\u8ddd\u79bb\u90a3\u8427\u8001\u96d5\u5374\u4ecd\u6709\u4e00\u6bb5\u8ddd\u79bb\uff0c\u53ea\u4e0d\u8fc7\u7a7a\u767d\u6253\u5f97\u6e56\u6c34\u56db\u4e0b\u98de \u6fc0\u800c\u5df2\uff01 \u3000\u3000\u8427\u8001\u96d5\u72c2\u7b11\u9053\uff1a\u201c\u59d3\u6bdb\u7684\uff0c\u4f60\u795e\u6c14\u4ec0\u4e48\uff1f\u5728\u5730\u4e0a\u4f60\u7eb5\u80fd\u8000\u6b66\u626c\u5a01\u4e00\u65f6\uff0c\u4f46\u6c34\u9762\u4e0a\u7684\u5929 \u4e0b\uff0c\u5374\u662f\u8001\u592b\u7684\u3002\u201d \u3000\u3000\u4ed6\u8e0f\u6c34\u7acb\u5728\u6e56\u4e2d\uff0c\u6e56\u6c34\u4ec5\u53ca\u819d\u5934\uff0c\u6c34\u6027\u4e4b\u7cbe\u719f\uff0c\u5f53\u771f\u4e0d\u6127\u662f\u79f0\u96c4\u6c34\u9762\u6570\u5341\u5e74\u7684\u8001\u82f1\u96c4\uff01 \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u6012\u6781\u4e4b\u4e0b\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u8427\u8001\u513f\uff0c\u4f60\u6562\u4e0a\u8239\u6765\u4e48\uff1f\u8427\u8001\u96d5\u72c2\u7b11\u9053\uff1a\u201c\u6211\u4e0a\u8239\u4f5c \u4ec0\uff1f\u6b64\u523b\u6e56\u6c34\u4e0b\u5df2\u6f5c\u4f0f\u4e86\u6570\u767e\u6761\u6211\u9ad8\u90ae\u3001\u6d2a\u6cfd\u6e56\u7684\u6c34\u4e0a\u7537\u513f\uff0c\u4f60\u53ef\u8981\u4e0b\u6765\u996e\u4e9b\u6e56\u6c34\u4e48\uff1f\u201d \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u5fc3\u5934\u4e00\u9707\uff0c\u53ea\u89c1\u6c34\u82b1\u4e00\u5192\uff0c\u8427\u91d1\u9ca4\u7a81\u5730\u81ea\u6e56\u6c34\u4e0b\u94bb\u4e86\u51fa\u6765\uff0c\u8e0f\u6c34\u5927\u7b11\u9053\uff1a\u201c\u59d3 \u6bdb\u7684\uff0c\u8fd8\u8ba4\u5f97\u6211\u4e48\uff1f\u201d \u3000\u3000\u8427\u8001\u96d5\u5fae\u7b11\u9053\uff1a\u201c\u5e73\u513f\u4e0e\u8fd9\u53ae\u591a\u8bf4\u4ec0\u4e48\uff0c\u4e0b\u9762\u7684\u5f1f\u5144\u4eec\u53ef\u5df1\u51c6\u5907\u597d\u4e86\uff1f\u201d \u3000\u3000\u91d1\u9ca4\u8427\u5e73\u9053\uff1a\u201c\u968f\u65f6\u90fd\u53ef\u52a8\u624b\uff01\u201d \u3000\u3000\u8427\u8001\u96d5\u7f13\u7f13\u9053\uff1a\u201c\u52a8\u624b\uff01\u201d \u3000\u3000\u91d1\u9ca4\u8427\u5e73\u5e94\u4e86\u4e00\u58f0\uff0c\u53cc\u638c\u4e00\u5408\uff0c\u6e38\u9c7c\u822c\u6ca1\u5165\u6c34\u4e2d\uff0c\u6c34\u9762\u4ec5\u53ea\u8d77\u4e86\u4e00\u56e2\u8f7b\u8f7b\u7684\u6d9f\u6f2a\uff0c\u77ac\u5373 \u5e73\u590d\uff01 \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u53c8\u60ca\u53c8\u6012\uff0c\u5fcd\u4e0d\u4f4f\u5927\u559d\u9053\uff1a\u201c\u8427\u8001\u513f\uff0c\u4f60\u5230\u5e95\u8981\u73a9\u4ec0\u4e48\u624b\u811a\uff1f\u201d \u3000\u3000\u8427\u8fdf\u5927\u7b11\u9053\uff1a\u201c\u4f60\u591a\u95ee\u4ec0\u4e48\uff0c\u770b\u4e00\u770b\u4fbf\u53ef\u77e5\u9053\u4e86\uff01\u201d \u3000\u3000\u8bdd\u58f0\u672a\u4e86\uff0c\u53ea\u542c\u8f70\u5730\u4e00\u58f0\uff0c\u6bdb\u81ec\u90bb\u8fd1\u51e0\u6761\u8239\uff0c\u7a81\u5730\u5411\u4e0b\u6c89\u53bb\uff0c\u4ed6\u811a\u4e0b\u4ea6\u4e14\u7830\u5730\u4e00\u9707\uff0c\u8239 \u8eab\u5411\u4e0b\u76f4\u9677\uff01 \u3000\u3000\u6bdb\u6587\u742a\u5a07\u53f1\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u7239\u7239\u5feb\u9000\uff01\u201d \u3000\u3000\u7acb\u89c1\u51e0\u6761\u9ed1\u8863\u6c49\u5b50\uff0c\u6273\u4e0a\u8239\u8237\uff0c\u5979\u957f\u5251\u4e00\u6325\uff0c\u4e00\u6e9c\u706b\u5149\u95ea\u8fc7\uff0c\u90a3\u51e0\u6761\u6c49\u5b50\uff0c\u4fbf\u53c8\u6ca1\u5165\u4e86 \u6c34\u4e2d\uff01 \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u65e9\u5df2\u95ea\u52a8\u8eab\u5f62\uff0c\u63a0\u4e0a\u6e56\u5cb8\u3002 \u3000\u3000\u4ed6\u8eab\u5f62\u65b9\u8d77\uff0c\u8239\u8eab\u4fbf\u5df2\u6025\u6c89\uff0c\u660f\u8ff7\u672a\u9192\u7684\u6797\u7433\uff0c\u4fbf\u843d\u5165\u6e56\u4e2d\uff0c\u6bdb\u6587\u742a\u65e0\u6687\u4ed6\u987e\uff0c\u957f\u5251\u4e00 \u62a1\uff0c\u968f\u8eab\u6025\u8f6c\uff01 \u3000\u3000\u4f46\u89c1\u4e00\u56e2\u7ea2\u5149\uff0c\u88f9\u4f4f\u5979\u7ea4\u67d4\u7684\u8eab\u5f71\uff0c\u5530\u5730\u63a0\u4e0a\u5cb8\u8fb9\uff01 \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u4ef0\u5929\u7b11\u9053\uff1a\u201c\u8427\u8001\u513f\uff0c\u4f60\u53c8\u5c82\u80fd\u5948\u4f55\u8001\u592b\uff1f\u201d \u3000\u3000\u8427\u8001\u96d5\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8001\u592b\u5c82\u662f\u771f\u7684\u8981\u6740\u4f60\uff0c\u53ea\u4e0d\u8fc7\u662f\u60f3\u770b\u4e00\u770b\u4f60\u72fc\u72c8\u9f20\u7a9c\u800c\u9003\u7684\u60e8 \u72b6\uff0c\u4fbf\u5df2\u5fc3\u6ee1\u610f\u8db3\u4e86\uff01\u201d \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u52c3\u7136\u5927\u6012\u9053\uff1a\u201c\u8427\u8001\u513f\uff0c\u9664\u975e\u4f60\u80fd\u6c38\u8fdc\u8eb2\u5728\u6c34\u4e0b\uff0c\u5426\u5219\u53ea\u8981\u4f60\u4e00\u8e0f\u4e0a\u9646\u5730\uff0c\u8001 \u592b\u4fbf\u7acb\u65f6\u5c06\u4f60\u4e71\u5200\u5206\u5c38\u800c\u6b7b\uff01\u201d \u3000\u3000\u8427\u8001\u96d5\u563b\u563b\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u6b64\u523b\u662f\u8981\u5728\u5cb8\u8fb9\u7b49\u5019\u7740\u7684\u4e86\uff01\u201d \u3000\u3000\u6bdb\u81ec\u5927\u559d\u9053\uff1a\u201c\u6b63\u662f\uff01\u201d \u3000\u3000\u8427\u8fdf\u7b11\u9053\uff1a\u201c\u4f60\u5bb6\u91cc\u706b\u52bf\u5df2\u8d77\uff0c\u518d\u4e0d\u56de\u53bb\u770b\u770b\uff0c\u4fbf\u8981\u88ab\u70e7\u5f97\u7247\u74e6\u4e0d\u5b58\uff0c\u4f60\u82e5\u5728\u6b64\u7b49\u4faf\u8001 \u592b\uff0c\u592a\u884c\u5c71\u91d1\u5bb6\u5144\u5f1f\u4e00\u5b9a\u9ad8\u5174\u5f97\u5f88\uff01\u201d \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u53c8\u81ea\u4e00\u6123\uff0c\u53ea\u542c\u8427\u8fdf\u63a5\u53e3\u5927\u7b11\u9053\uff1a\u2018\u59d3\u6bdb\u7684\uff0c\u4f60\u5207\u5207\u8bb0\u7740\uff0c\u4ece\u4eca\u4ee5\u540e\uff0c\u5207\u83ab\u518d \u8e0f\u4e0a\u6c34\u9762\uff0c\u53ea\u8981\u4f60\u4e00\u5230\u6c34\u4e0a\uff0c\u8001\u592b\u5fc5\u5b9a\u5728\u6c34\u4e0b\u7b49\u7740\uff01\u201d\u5927\u7b11\u58f0\u4e2d\uff0c\u4ed6\u8eab\u5b50\u4e00\u6c89\uff0c\u4fbf\u5df2\u6d88\u5931\u65e0 \u5f71\uff01\u7075\u86c7\u6bdb\u81ec\u53cc\u62f3\u7d27\u63e1\uff0c\u6728\u7acb\u534a\u664c\uff0c\u76ee\u5149\u4e2d\u4e0d\u7981\u9732\u51fa\u4e9b\u9eef\u7136\u5931\u610f\u4e4b\u8272\uff0c\u957f\u53f9\u4e00\u58f0\uff0c\u542b\u6068\u81ea \u8bed\uff1a\u201c\u6bdb\u81ec\u5440\u6bdb\u81ec\uff0c\u4f60\u4e3a\u4f55\u4e0d\u7ec3\u597d\u6c34\u6027\uff0c\u81f3\u4ee4\u4eca\u65e5\u88ab\u5c0f\u4eba\u6240\u6b3a\u2026\u2026\u201d \u3000\u3000\u6bdb\u6587\u742a\u5e7d\u5e7d\u4e00\u53f9\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u7239\u7239\uff0c\u8fd8\u662f\u56de\u53bb\u770b\u770b\u7684\u597d1\u201d\u7075\u86c7\u6bdb\u81ec\u72e0\u72e0\u4e00\u8dfa\u8db3\uff0c\u9053\uff1a \u201c\u70e7\u90fd\u70e7\u4e86\uff0c\u8fd8\u770b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53e3\u4e2d\u867d\u5728\u5982\u6b64\u8bf4\u8bdd\uff0c\u4eba\u5374\u7ffb\u8eab\u63a0\u53bb\uff01 \u3000\u3000\u6b64\u523b\u65e5\u8272\u867d\u672a\u6c89\u843d\uff0c\u4f46\u5929\u7554\u5ffd\u5730\u63a9\u6765\u51e0\u7247\u4e4c\u4e91\uff0c\u4f7f\u5f97\u672c\u6781\u6674\u6717\u7684\u6c5f\u5357\u5929\u6c14\uff0c\u53d8\u5f97\u5341\u5206\u9634 \u9eef\u60e8\u6de1\uff01 \u3000\u3000\u897f\u6e56\u56db\u5468\uff0c\u65e9\u5df2\u5168\u65e0\u4eba\u8ff9\uff0c\u6bdb\u81ec\u7236\u5973\u8eab\u5f62\u98de\u63a0\uff0c\u7247\u523b\u95f4\uff0c\u4f46\u95fb\u4e00\u9635\u7126\u6728\u4e4b\u6c14\uff0c\u6251\u9f3b\u800c \u6765\u3002 \u3000\u3000\u6bdb\u81ec\u9762\u8272\u8d8a\u53d1\u9634\u6c89\uff0c\u63a5\u8fde\u51e0\u4e2a\u8d77\u843d\u540e\uff0c\u62ac\u773c\u671b\u5904\uff0c\u4f46\u89c1\u81ea\u5df1\u90a3\u96c4\u9614\u7684\u5e84\u9662\uff0c\u7adf\u5df2\u53d8\u4f5c\u4e86 \u4e00\u7247\u706b\u6d77\uff01 \u3000\u3000\u4ed6\u5e84\u9662\u5360\u5730\u867d\u5e7f\uff0c\u4f46\u56db\u5468\u5374\u65e0\u6bd7\u8fde\u7684\u4eba\u5bb6\uff0c\u6b64\u523b\u66f4\u65e0\u4e00\u4eba\u6551\u706b\uff0c\u53ea\u6709\u6570\u5341\u9a91\u9ed1\u8863\u9a91\u58eb\uff0c \u5728\u706b\u573a\u56db\u5468\u98de\u9a70\u4e0d\u5df2\u3002 \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u77e5\u9053\u5373\u4f7f\u6709\u4eba\u6551\u706b\uff0c\u4e5f\u90fd\u88ab\u8fd9\u4e9b\u9a91\u58eb\u8d76\u8dd1\uff0c\u81ea\u5df1\u7559\u5b88\u5728\u5e84\u9662\u7684\u95e8\u4e0b\uff0c\u60f3\u5fc5\u4e0d\u662f \u8dd1\u4e86\uff0c\u4fbf\u5df2\u906d\u4e86\u6bd2\u624b\u3002 \u3000\u3000\u4ed6\u6025\u6012\u4e4b\u4e0b\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u98de\u63a0\u800c\u53bb\u3002 \u3000\u3000\u54ea\u77e5\u90a3\u4e9b\u9a91\u58eb\u4f3c\u4e4e\u65e9\u5df2\u7b97\u5b9a\u4e86\u4ed6\u8981\u56de\u6765\uff0c\u4e0d\u7b49\u4ed6\u8eab\u5f62\u73b0\u51fa\uff0c\u4fbf\u5df2\u98de\u9a91\u5954\u53bb\uff0c\u9003\u5f97\u65e0\u5f71\u65e0 \u8e2a\uff01 \u3000\u3000\u53ea\u542c\u8fdc\u8fdc\u4f20\u6765\u4e00\u9635\u547c\u559d\u9053\uff1a\u201c\u59d3\u6bdb\u7684\uff0c\u662f\u6211\u91d1\u6c0f\u5144\u5f1f\u70e7\u4e86\u4f60\u7684\u5e84\u9662\uff0c\u4f60\u82e5\u4e0d\u670d\uff0c\u5c3d\u7ba1\u5230 \u592a\u884c\u5c71\u6765\u627e\u6211\u91d1\u6c0f\u5144\u5f1f\u3002\u201d \u3000\u3000\u547c\u58f0\u6e10\u6e10\u8fdc\u53bb\uff0c\u4e0e\u8e44\u58f0\u4e00\u9f50\u6d88\u901d\uff01 \u3000\u3000\u6bdb\u6587\u742a\u5c55\u52a8\u8eab\u5f62\uff0c\u5728\u706b\u5b85\u56db\u5468\u98de\u63a0\u4e86\u4e00\u5708\uff0c\u8f7b\u53f9\u9053\uff1a\u201c\u7239\u7239\uff0c\u706b\u5df2\u65e0\u6cd5\u6551\u7184\u4e86\u3002\u201d \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u9762\u6c89\u5982\u6c34\uff0c\u7a81\u5730\u9009\u4e86\u4e2a\u706b\u52bf\u8f6f\u5f31\u4e4b\u5904\uff0c\u98de\u8eab\u800c\u5165\uff0c\u6bdb\u6587\u742a\u9a87\u7136\u60ca\u547c\u4e00\u58f0\uff1a\u201c\u7239 \u7239\u2026\u2026\u201d \u3000\u3000\u5979\u968f\u4e4b\u63a0\u5165\u4e86\u706b\u5b85\uff0c\u53ea\u89c1\u706b\u52bf\u867d\u5728\u56db\u9762\u71c3\u8d77\uff0c\u4f46\u53ea\u56e0\u5e84\u9662\u592a\u5927\uff0c\u662f\u4ee5\u6b63\u4e2d\u7684\u51e0\u95f4\u5385\u623f\u5374 \u4ecd\u672a\u88ab\u70c8\u706b\u71c3\u7740\uff01 \u3000\u3000\u6bdb\u81ec\u4e00\u638c\u9707\u5f00\u4e86\u5385\u95e8\uff0c\u95ea\u8eab\u800c\u5165\u2026\u2026 \u3000\u3000\u7a81\u5730\uff0c\u56db\u9762\u70c8\u706b\u5305\u56f4\u4e2d\u7684\u5385\u5802\u91cc\uff0c\u7adf\u4f20\u51fa\u4e86\u4e00\u58f0\u51b7\u7b11\uff01 \u3000\u3000\u6bdb\u81ec\u5fc3\u5934\u4e00\u60ca\uff0c\u731b\u7136\u987f\u4f4f\u4e86\u811a\u6b65\uff01 \u3000\u3000\u53ea\u542c\u90a3\u51b7\u7b11\u4e4b\u540e\u7f13\u7f13\u9053\uff1a\u201c\u6bdb\u81ec\uff0c\u4f60\u6765\u4e86\u4e48\uff1f\u6211\u5df2\u5728\u6b64\u7b49\u4e86\u8bb8\u4e45\u4e86\uff01\u201d \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u5927\u559d\u4e00\u58f0\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u6bdb\u6587\u742a\u5251\u4e0d\u79bb\u638c\uff0c\u5df2\u968f\u4e4b\u4eba\u4e86\u5385\u5802\u3002 \u3000\u3000\u6ee1\u5385\u706b\u70df\u5f25\u6f2b\uff0c\u5385\u5802\u6df1\u5904\uff0c\u5189\u5189\u73b0\u51fa\u4e86\u4e00\u6761\u8eab\u5f71\uff0c\u98d8\u98d8\u5730\u7f13\u6b65\u8d70\u5728\u70df\u706b\u91cc\uff0c\u6709\u5982\u767d\u4e91\u96fe \u4e2d\u51fa\u73b0\u4e00\u822c\uff01 \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u4e00\u751f\u884c\u8d70\u6c5f\u6e56\uff0c\u5927\u98ce\u5927\u6d6a\u4e4b\u4e8b\uff0c\u4e0d\u77e5\u7ecf\u8fc7\u591a\u5c11\uff0c\u5200\u5934\u8214\u8840\u3001\u5251\u5e95\u60ca\u9b42\u4e4b\u4e8b\uff0c\u66f4 \u4e0d\u77e5\u5e72\u4e86\u51e0\u591a\u3002 \u3000\u3000\u4f46\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u4ed6\u5fc3\u5934\u5374\u4e0d\u7531\u81ea\u4e3b\u5730\u6cdb\u51fa\u4e00\u9635\u5bd2\u610f\uff0c\u53cc\u638c\u62a4\u80f8\uff0c\u5fae\u9000\u4e00\u6b65\uff0c\u53e3\u4e2d\u98a4\u58f0 \u9053\uff1a\u201c\u4f60\u83ab\u975e\u4fbf\u662f\u4ec7\u2026\u2026\u201d \u3000\u3000\u90a3\u4eba\u5f71\u51b7\u7b11\u4e00\u58f0\uff0c\u7a81\u7136\u4e00\u6b65\u8d70\u51fa\u4e86\u70df\u706b\uff0c\u9053\uff1a\u201c\u4f60\u770b\u770b\u6211\u662f\u8c01\uff1f\u201d \u3000\u3000\u70df\u706b\u6563\u5904\uff0c\u4f46\u89c1\u4ed6\u9526\u888d\u534e\u670d\uff0c\u6b65\u5c65\u4ece\u5bb9\uff0c\u4f46\u7709\u68a2\u773c\u89d2\uff0c\u5374\u5e26\u7740\u4e00\u79cd\u68ee\u68ee\u5bd2\u610f\uff0c\u8d6b\u7136\u6b63\u662f \u4ec7\u6055\uff01 \u3000\u3000\u6bdb\u81ec\u3001\u6bdb\u6587\u742a\u9f50\u5730\u60ca\u547c\u4e00\u58f0\uff0c\u6bdb\u6587\u742a\u5a07\u5f31\u7684\u8eab\u5b50\uff0c\u5df1\u4e0d\u7981\u6709\u5982\u98ce\u4e2d\u67f3\u679d\u822c\u5fae\u5fae\u98a4\u6296\u4e86\u8d77 \u6765\uff01 \u3000\u3000\u4ec7\u6055\u76ee\u5149\u68ee\u4e25\uff0c\u51b0\u5200\u822c\u76ef\u5728\u6bdb\u81ec\u9762\u4e0a\uff01 \u3000\u3000\u4ed6\u6545\u610f\u4e0d\u53bb\u671b\u6bdb\u6587\u742a\u4e00\u773c\uff0c\u4e00\u5b57\u5b57\u7f13\u7f13\u9053\uff1a\u201c\u6bdb\u81ec\uff0c\u4f60\u770b\u6e05\u695a\u4e86\u4e48\uff1f\u6211\u4fbf\u662f\u4ec7\u5148\u751f\u7684\u540e \u4eba\uff0c\u6765\u5411\u4f60\u8ba8\u8fd8\u5341\u516b\u5e74\u7684\u8840\u503a\uff01\u4f60\u53ef\u8981\u770b\u6e05\u6211\u7684\u771f\u9762\u76ee\uff1f\u201d \u3000\u3000\u70df\u706b\u6b32\u6563\u8fd8\u805a\uff0c\u4f9d\u7a00\u5730\u7b3c\u7f69\u7740\u4ec7\u6055\u7684\u8eab\u5f71\uff01 \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u62ac\u773c\u671b\u53bb\uff0c\u53ea\u89c9\u8fd9\u5c11\u5e74\u7684\u8eab\u5f62\u9762\u5bb9\uff0c\u6d3b\u8131\u8131\u6b63\u662f\u5341\u516b\u5e74\u524d\uff0c\u83bd\u82cd\u6df1\u5c71\u4e2d\u90a3\u9a91\u9a6c \u72ec\u884c\u7684\u4ec7\u5148\u751f\u7684\u5f71\u5b50\uff0c\u6f02\u6e3a\u5728\u4e91\u971e\u95f4\uff01 \u3000\u3000\u5239\u90a3\u4e4b\u95f4\uff0c\u7075\u86c7\u6bdb\u81ec\u4eff\u4f5b\u662f\u89c1\u7740\u4e86\u4ec7\u5148\u751f\u7684\u5e7d\u7075\u4e00\u822c\uff0c\u5fcd\u4e0d\u4f4f\u673a\u4f36\u4f36\u6253\u4e86\u4e2a\u5bd2\u5664\uff01 \u3000\u3000\u4ed6\u53ea\u89c9\u4e00\u9635\u5bd2\u610f\uff0c\u51b7\u900f\u4e86\u5168\u8eab\uff0c\u8eab\u4e0d\u7531\u4e3b\u5730\u5411\u540e\u9000\u53bb\uff0c\u5bbd\u9614\u7684\u989d\u89d2\u4e0a\uff0c\u4e5f\u5df2\u6c81\u51fa\u4e86\u5927\u7684 \u6c57\u73e0\uff01 \u3000\u3000\u4ec7\u6055\u51b7\u53f1\u4e00\u58f0\uff01 \u3000\u3000\u201c\u8840\u503a\u672a\u8fd8\uff0c\u4f60\u4fbf\u60f3\u8d70\u4e86\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u53cc\u638c\u4e0b\u5782\uff0c\u4e00\u6b65\u6b65\u5411\u6bdb\u81ec\u8d70\u4e86\u8fc7\u6765\uff0c\u6bcf\u8d70\u4e00\u6b65\uff0c\u90fd\u50cf\u662f\u4e00\u811a\u8e29\u5728\u6bdb\u81ec\u7684\u5fc3\u4e0a\uff0c\u4f7f\u5f97\u4ed6 \u5fc3\u5f26\u4e00\u9707\uff01 \u3000\u3000\u4ed6\u5e76\u975e\u80c6\u602f\u4e4b\u5f92\uff0c\u4f46\u6b64\u523b\u89c1\u4e86\u4ec7\u6055\uff0c\u4e0d\u77e5\u600e\u5730\uff0c\u7adf\u5fc3\u865a\u80c6\u6218\u8d77\u6765\uff0c\u53ea\u56e0\u5341\u516b\u5e74\u524d\u4ec7\u5148\u751f \u7684\u4f59\u5a01\u4ecd\u672a\u5728\u4ed6\u5fc3\u5934\u6d88\u6563\uff0c\u90a3\u201c\u5341\u5e74\u4e4b\u540e\uff0c\u8840\u503a\u8840\u8fd8\u201d\u516b\u4e2a\u8840\u6dcb\u6dcb\u7684\u5b57\u8ff9\uff0c\u66f4\u4e00\u76f4\u4ee4\u4ed6\u5bdd\u98df \u96be\u5b89\u3002 \u3000\u3000\u6bdb\u6587\u742a\u7d27\u54ac\u94f6\u7259\uff0c\u7a81\u5730\u5a07\u559d\u4e00\u58f0\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u5feb\u53bb\uff0c\u5f85\u6211\u6321\u4f4f\u4ed6\uff01\u201d \u3000\u3000\u8bed\u58f0\u672a\u4e86\uff0c\u4ec7\u6055\u7684\u8eab\u5b50\u5df2\u8f7b\u70df\u822c\u98de\u8d77\uff0c\u63a0\u8fc7\u4e86\u6bdb\u6587\u742a\uff0c\u659c\u659c\u4e00\u638c\uff0c\u51fb\u5411\u7075\u86c7\u6bdb\u81ec\u80f8\u819b\u4e4b \u95f4\uff01 \u3000\u3000\u4ed6\u8eab\u6cd5\u8f7b\u7075\uff0c\u62db\u5f0f\u8be1\u5f02\uff0c\u4e3e\u624b\u6295\u8db3\u95f4\u90a3\u79cd\u6f47\u6d12\u7684\u795e\u6001\uff0c\u8d6b\u7136\u7adf\u662f\u4ec7\u5148\u751f\u6614\u5e74\u7684\u6a21\u6837\uff01 \u3000\u3000\u7075\u86c7\u6bdb\u81ec\u80c6\u5bd2\u4e4b\u4e0b\uff0c\u7adf\u4e0d\u6562\u62b5\u6321\uff0c\u72c2\u543c\u4e00\u58f0\uff0c\u8f6c\u8eab\u5954\u51fa\uff01 \u3000\u3000\u4ec7\u6055\u51b7\u53f1\u9053\uff1a\u201c\u54ea\u91cc\u53bb\uff1f\u201d\u4ed6\u80a9\u5934\u5fae\u8038\uff0c\u6b63\u5f85\u7eb5\u8eab\u8ffd\u51fa\uff01\u6bdb\u6587\u742a\u5df2\u5636\u58f0\u9053\uff1a\u2018\u4ec7\u6055\u2026\u2026 \u4f60\u4e0d\u8981\u8ffd\u4e86\u2026\u2026\u201d\u8bed\u58f0\u98a4\u6296\uff0c\u6ee1\u542b\u5e7d\u6028\u60b2\u6124\uff0c\u4ec7\u6055\u5fc3\u795e\u4e00\u98a4\uff0c\u518d\u4e5f\u4e0d\u80af\u56de\u5934\uff0c\u7d27\u63e1\u53cc\u62f3\uff0c\u7d27 \u54ac\u7259\u5173\uff0c\u7b14\u76f4\u8ffd\u51fa\uff01\u6bdb\u6587\u742a\u6ee1\u9762\u6cea\u75d5\uff0c\u5530\u5730\u523a\u51fa\u4e00\u5251\uff0c\u5251\u5c16\u4e5f\u4e0d\u4f4f\u98a4\u6296\uff01\u5979\u89c1\u5230\u4ec7\u6055\u5168\u672a\u95ea \u907f\uff0c\u5fc3\u4e2d\u60b2\u54c0\u6697\u5fd6\uff1a\u201c\u6211\u82e5\u4e00\u5251\u6740\u6b7b\u4e86\u4f60\uff0c\u6211\u4e5f\u966a\u7740\u4f60\u6b7b\u2026\u2026\u201d \u3000\u3000\u5fc3\u5ff5\u4e4d\u8f6c\uff0c\u7a81\u89c1\u4ec7\u6055\u53cd\u624b\u6325\u51fa\u4e00\u638c\uff0c\u98df\u4e2d\u4e24\u6307\uff0c\u75be\u5f39\u6bdb\u6587\u742a\u5251\u5c16\uff0c\u53ea\u542c\u201c\u53ee\u201d\u7684\u4e00\u58f0\uff0c \u4ec7\u6055\u7a81\u89c9\u6307\u95f4\u4e00\u9ebb\uff0c\u52b2\u529b\u5168\u6d88\uff0c\u8eab\u5f62\u7adf\u65e0\u6cd5\u518d\u8fdb\u4e00\u6b65\uff01 \u3000\u3000\u6bdb\u6587\u742a\u98a4\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u4e3a\u4ec0\u4e48\u5b9a\u8981\u590d\u4ec7\uff1f\u201d \u3000\u3000\u4ec7\u6055\u6df1\u6df1\u5438\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u7236\u4ec7\u4e0d\u5171\u6234\u5929\uff01\u201d \u3000\u3000\u6bdb\u6587\u742a\u6d41\u6cea\u9053\uff1a\u201c\u5bf9\uff0c\u7236\u4ec7\u4e0d\u5171\u6234\u5929\uff0c\u4f46\u8981\u6740\u6211\u7239\u7239\uff0c\u6211\u53ea\u6709\u5148\u6740\u4e86\u4f60\uff01\u201d \u3000\u3000\u4ec7\u6055\u7a81\u5730\u8f6c\u53f1\u4e00\u58f0\uff0c\u8eab\u5f62\u6781\u5176\u5947\u5999\u5730\u4e00\u8f6c\uff0c\u5168\u8eab\u9aa8\u8282\uff0c\u6709\u5982\u5168\u90fd\u662f\u6d3b\u7684\u4e00\u822c\uff0c\u4e00\u638c\u62cd\u5411 \u6bdb\u6587\u742a\u9762\u95e8\uff01 \u3000\u3000\u6bdb\u6587\u742a\u53cc\u76ee\u4e00\u9616\uff0c\u5782\u4e0b\u957f\u5251\uff0c\u9053\uff1a\u201c\u4f60\u6740\u4e86\u6211\u4e5f\u597d\uff0c\u6211\u53cd\u6b63\u4e0d\u60f3\u6d3b\u4e86\uff01\u201d \u3000\u3000\u4ec7\u6055\u53ea\u89c9\u80f8\u95f4\u4e00\u80a1\u70ed\u8840\u4e0a\u6d8c\uff0c\u786c\u751f\u751f\u987f\u4f4f\u4e86\u624b\u638c\uff01 \u3000\u3000\u6bdb\u6587\u742a\u90a3\u6ee1\u9762\u51c4\u695a\u5e7d\u6028\u4e4b\u8272\uff0c\u90a3\u4e00\u8fde\u4e32\u6d41\u843d\u5728\u80f8\u524d\u6676\u83b9\u7684\u6cea\u73e0\uff0c\u4f7f\u5f97\u4ed6\u94c1\u77f3\u822c\u7684\u5fc3\u80a0\uff0c \u4e5f\u4e71\u4e86\u8d77\u6765\uff01 \u3000\u3000\u6bdb\u6587\u742a\u7d27\u95ed\u7740\u773c\u5e18\uff0c\u6d41\u6cea\u9053\uff1a\u201c\u6211\u7239\u7239\u5df2\u7ecf\u8001\u4e86\uff0c\u6b64\u523b\u53c8\u5df2\u662f\u4f17\u53db\u4eb2\u79bb\uff0c\u65e0\u5bb6\u53ef\u5f52\uff0c\u4f60 \u5df2\u5bb3\u5f97\u4ed6\u591f\u60e8\uff0c\u8fd8\u8981\u5bf9\u4ed6\u600e\u6837\uff1f\u201d \u3000\u3000\u4ec7\u6055\u7a81\u5730\u53cc\u7709\u4e00\u8f69\uff0c\u5927\u559d\u9053\uff1a\u201c\u4ed6\u5bb3\u5f97\u6211\u7239\u7239\u600e\u6837\u4e86\uff1f\u8fde\u5c38\u9aa8\u90fd\u4e0d\u80fd\u4fdd\u5168\u2026\u2026\u201d \u3000\u3000\u559d\u58f0\u4e2d\u4ed6\u8eab\u5f62\u5012\u7eb5\u800c\u51fa\uff0c\u53ea\u56e0\u90a3\u5f3a\u70c8\u7684\u4ec7\u7130\uff0c\u5df2\u71c3\u65ad\u4e86\u60c5\u4e1d\uff0c\u6bdb\u6587\u742a\u867d\u7136\u8ffd\u51fa\uff0c\u5374\u5df2\u8feb \u4e0d\u4e0a\u4e86\uff01 \u3000\u3000\u4ec7\u6055\u8eab\u5f62\u4e00\u8f6c\uff0c\u81ea\u70c8\u7130\u4e0a\u98de\u63a0\u800c\u51fa\uff0c\u811a\u5c16\u65b9\u81ea\u70b9\u5730\u2026\u2026 \u3000\u3000\u7a81\u542c\u4e00\u58f0\u5927\u7b11\u9053\uff1a\u201c\u4f60\u9003\u6765\u9003\u53bb\uff0c\u8fd8\u662f\u9003\u4e0d\u6389\u7684\uff01\u201d \u3000\u3000\u7b11\u58f0\u672a\u6b47\uff0c\u4e24\u6761\u4eba\u5f71\u5982\u98de\u9e1f\u822c\u5760\u5728\u4ed6\u9762\u524d\uff01 \u3000\u3000\u4ec7\u6055\u5fae\u5fae\u4e00\u60ca\uff0c\u8f6c\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u4e00\u80d6\u4e00\u7626\u4e24\u4e2a\u534e\u670d\u7684\u8001\u4eba\uff0c\u5e76\u80a9\u7ad9\u5728\u4ed6\u9762\u524d\uff0c\u8d6b\u7136\u7adf\u662f \u6f58\u4f65\u3001\u7a0b\u9a79\uff01 \u3000\u3000\u4ec7\u6055\u4e00\u89c1\u4ed6\u4e24\u4eba\uff0c\u4e0d\u7981\u6697\u4e2d\u53f9\u4e86\u53e3\u6c14\uff0c\u5b9a\u4e0b\u811a\u6b65\u3002 \u3000\u3000\u6bdb\u6587\u742a\u5df2\u968f\u540e\u8d76\u6765\uff0c\u89c1\u5230\u4ed6\u4e24\u4eba\uff0c\u4e5f\u4e0d\u7981\u4e3a\u4e4b\u4e00\u6014\u3002 \u3000\u3000\u7a0b\u9a79\u9065\u6307\u897f\u65b9\uff0c\u9053\uff1a\u201c\u6bdb\u59d1\u5a18\uff0c\u4f60\u7239\u7239\u4ece\u90a3\u8fb9\u8d70\u4e86\uff0c\u4f60\u5feb\u8ffd\u53bb\u5427\u3002\u201d \u3000\u3000\u6f58\u4f65\u63a5\u53e3\u9053\uff1a\u201c\u8fd9\u5c0f\u4f19\u5b50\u6709\u6211\u4e24\u4e2a\u8001\u5934\u5b50\u62e6\u4f4f\u4ed6\uff0c\u4fbf\u50cf\u662f\u5b59\u609f\uff0c\u7a7a\u5957\u4e0a\u4e86\u7d27\u7b8d\u5492\u4e00\u822c\uff0c \u518d\u4e5f\u8d70\u4e0d\u4e86\u5566\uff01\u201d \u3000\u3000\u6bdb\u6587\u742a\u8eab\u5f62\u5fae\u987f\uff0c\u6df1\u6df1\u77a7\u4e86\u4ec7\u6055\u4e00\u773c\uff0c\u9762\u4e0a\u6cea\u75d5\u672a\u5e72\uff0c\u4f3c\u4e4e\u60f3\u5bf9\u4ec7\u6055\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u53c8\u4e0d\u77e5 \u8bf4\u4e9b\u4ec0\u4e48\u624d\u597d\uff01 \u3000\u3000\u7a0b\u9a79\u7b11\u9053\uff1a\u201c\u4f60\u8981\u8bf4\u4ee5\u540e\u518d\u8bf4\u5427\uff0c\u6b64\u523b\u8fd8\u662f\u5feb\u8d70\u7684\u597d\uff01\u201d \u3000\u3000\u6bdb\u6587\u742a\u60e8\u7136\u4e00\u7b11\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u8c22\u8c22\u4e24\u4f4d\u524d\u8f88\u2026\u2026\u970d\u7136\u8f6c\u8fc7\u8eab\u5b50\uff0c\u5411\u7a0b\u9a79\u6240\u6307\u7684\u65b9\u5411\u8ffd \u53bb\uff0c\u5979\u867d\u7136\u6ca1\u6709\u518d\u56de\u5934\u770b\u4e0a\u4e00\u773c\uff0c\u4f46\u6a31\u5507\u5374\u5df2\u88ab\u5979\u6697\u4e2d\u54ac\u7834\uff01\u4ec7\u6055\u5446\u4e86\u534a\u664c\uff0c\u5931\u58f0\u957f\u53f9\u9053\uff1a \u201c\u6211\u77e5\u9053\u4f60\u4eec\u8981\u963b\u6211\u590d\u4ec7\uff0c\u662f\u4ee5\u624d\u4e00\u76f4\u8eb2\u907f\u7740\u4f60\u4eec\uff0c\u7236\u4ec7\u4e0d\u5171\u6234\u5929\uff0c\u4f60\u4eec\u53c8\u4f55\u82e6\u2026\u2026\u7a0b\u9a79\u563f \u4e86\u4e00\u58f0\uff0c\u622a\u53e3\u9053\uff1a\u201c\u4f60\u53e3\u53e3\u58f0\u58f0\u90fd\u662f\u7236\u4ec7\u4e0d\u5171\u6234\u5929\uff0c\u4f60\u96be\u9053\u5fd8\u4e86\u4f60\u7684\u6bcd\u4eb2\uff0c\u4f60\u82e5\u6740\u4e86\u6bdb\u81ec\uff0c \u4f60\u6bcd\u4eb2\u4f1a\u591a\u4e48\u4f24\u5fc3\uff1f\u201d \u3000\u3000\u6f58\u4f65\u9762\u4e0a\u5df2\u65e0\u534a\u70b9\u7b11\u5bb9\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u4f60\u6bcd\u4eb2\u518d\u4e09\u5173\u7167\u6211\u4eec\uff0c\u6211\u4e24\u4eba\u53c8\u4f55\u82e6\u5954\u6ce2\u5343 \u91cc\u5730\u8d76\u6765\uff0c\u4f60\u80fd\u5fd8\u8bb0\u5979\u7684\u8bdd\uff0c\u6211\u4eec\u5374\u5fd8\u4e0d\u4e86\u7684\uff01\u201d \u3000\u3000\u7a0b\u9a79\u9053\uff1a\u201c\u51a4\u5bb6\u5b9c\u89e3\u4e0d\u5b9c\u7ed3\uff0c\u8fd9\u53e5\u8bdd\u4f60\u6bcd\u4eb2\u5bf9\u4f60\u8bf4\u8fc7\u591a\u5c11\u6b21\uff0c\u4f60\u7236\u4eb2\u6b7b\u4e86\uff0c\u4f60\u7eb5\u7136\u6740\u4e86 \u6bdb\u81ec\uff0c\u4ed6\u4e5f\u4e0d\u80fd\u590d\u751f\uff01\u201d \u3000\u3000\u6f58\u4f65\u9053\uff1a\u201c\u4f55\u51b5\u4f60\u4e5f\u66fe\u7ecf\u8bf4\u8fc7\uff0c\u81ea\u5df1\u4e0d\u4eb2\u624b\u6740\u6b7b\u6bdb\u81ec\uff0c\u5982\u4eca\u4f60\u5df2\u6574\u5f97\u4ed6\u591f\u60e8\u4e86\uff0c\u8fd8\u8981\u5bf9 \u4ed6\u600e\u6837\uff1f\u201d \u3000\u3000\u4ed6\u4e24\u4eba\u4e00\u53e5\u63a5\u7740\u4e00\u53e5\uff0c\u6839\u672c\u4e0d\u7ed9\u4ec7\u6055\u8bf4\u8bdd\u7684\u673a\u4f1a\u3002 \u3000\u3000\u4ec7\u6055\u4f4e\u5782\u7740\u5934\uff0c\u76ee\u5149\u95ea\u52a8\u4e0d\u5b9a\uff0c\u5fc3\u4e2d\u81ea\u4e5f\u5728\u4e0d\u4f4f\u5730\u8f6c\u52a8\u7740\u5fc3\u601d\uff0c\u826f\u4e45\u826f\u4e45\uff0c\u4ed6\u65b9\u81ea\u957f\u53f9 \u4e00\u58f0\uff0c\u9053\uff1a\u201c\u65e2\u662f\u4e24\u4f4d\u53d4\u7236\u6765\u4e86\uff0c\u5c0f\u4f84\u8fd8\u6709\u4ec0\u4e48\u8bdd\u8bf4\u2026\u2026\u201d \u3000\u3000\u7a0b\u9a79\u622a\u53e3\u9053\uff1a\u201c\u6211\u4e0d\u7ba1\u4f60\u6709\u65e0\u8bdd\u8bf4\uff0c\u4e5f\u4e0d\u7ba1\u4f60\u613f\u4e0d\u613f\u610f\uff0c\u53cd\u6b63\u6211\u4e24\u4eba\u4eca\u540e\u8ddf\u5b9a\u4e86\u4f60\uff0c\u76f4 \u5230\u5c06\u4f60\u9001\u56de\u4f60\u6bcd\u4eb2\u90a3\u91cc\u4e3a\u6b62\u3002\u201d \u3000\u3000\u4ec7\u6055\u9053\uff1a\u201c\u4e00\u5207\u5168\u51ed\u53d4\u7236\u4eec\u7684\u5429\u5490\uff01\u201d \u3000\u3000\u7a0b\u9a79\u3001\u6f58\u4f65\u9f50\u5730\u5c55\u989c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u6837\u624d\u662f\u597d\u5b69\u5b50\u2026\u2026\u201d \u3000\u3000\u4ec7\u6055\u9053\uff1a\u201c\u5c0f\u4f84\u90a3\u91cc\u7f8e\u9152\u751a\u591a\uff0c\u4e14\u8bf7\u53d4\u7236\u4eec\u53bb\u5171\u996e\u4e00\u676f\uff01\u201d \u3000\u3000\u7a0b\u9a79\u5927\u7b11\u9053\uff1a\u201c\u8fd9\u6837\u5c31\u66f4\u662f\u597d\u5b69\u5b50\u4e86\uff01\u201d \u3000\u3000\u4e24\u4eba\u968f\u7740\u4ec7\u6055\uff0c\u56de\u5230\u4ed6\u90a3\u6240\u5b85\u9662\uff0c\u201c\u8fd8\u9b42\u201d\u4e00\u8d70\uff0c\u6881\u4e0a\u4eba\u95e8\u4e0b\u4e5f\u4ff1\u90fd\u6563\u53bb\uff0c\u8fd9\u5b85\u9662\u4e2d\u4fbf \u7a7a\u65e0\u4eba\u8ff9\u3002 \u3000\u3000\u3000\u3000\u4ec7\u6055\u638c\u4e0a\u4e86\u706f\u706b\uff0c\u53d6\u6765\u4e86\u7f8e\u9152\uff0c\u867d\u7136\u6709\u9152\u65e0\u80b4\uff0c\u4f46\u4e09\u4eba\u5374\u559d\u5f97\u751a\u662f\u5f00\u5fc3\uff0c\u4ec7\u6055\u6d51\u7136 \u5fd8\u53bb\u4e86\u5fc3\u4e8b\uff01 \u3000\u3000\u4e00\u575b\u9152\u4e0b\u53bb\uff0c\u4ec7\u6055\u4ecd\u7136\u9762\u8272\u4e0d\u53d8\uff0c\u7a0b\u9a79\u5374\u5df2\u9762\u7ea2\u8033\u8d64\uff0c\u6f58\u4f65\u66f4\u662f\u795e\u6001\u5927\u4e71\uff0c\u9891\u9891\u547c\u9152\uff01 \u3000\u3000\u4ec7\u6055\u7acb\u5373\u53c8\u53d6\u6765\u53e6\u4e00\u575b\u9152\uff0c\u8fd9\u4e00\u575b\u9152\u559d\u5c06\u4e0b\u53bb\uff0c\u7a0b\u9a79\u3001\u6f58\u4f65\u4fbf\u65e9\u5df2\u70c2\u9189\u5982\u6ce5\uff0c\u518d\u4e5f\u7701\u4e0d \u5f97\u4eba\u4e8b\uff01 \u3000\u3000\u4ec7\u6055\u76ee\u5149\u95ea\u52a8\uff0c\u4f4e\u547c\u9053\uff1a\u201c\u7a0b\u5927\u53d4\uff0c\u6f58\u4e8c\u53d4\u2026\u2026\u6f58\u4f65\u3001\u7a0b\u9a79\u54ea\u6709\u56de\u5e94\uff0c\u4ec7\u6055\u4f38\u51fa\u4e86\u624b \u638c\uff0c\u5728\u4ed6\u4e24\u4eba\u9762\u524d\u6447\u4e86\u51e0\u6b21\uff0c\u4ed6\u4e24\u4eba\u4ea6\u6beb\u65e0\u6240\u77e5\uff01\u4ec7\u6055\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u53d4\u7236\u4f11\u602a \u5c0f\u4f84\u65e0\u793c\uff0c\u5c0f\u4f84\u4e3a\u4e86\u8981\u62a5\u7236\u4ec7\uff0c\u8bf4\u4e0d\u5f97\u53ea\u6709\u6682\u65f6\u59d4\u5c48\u4e24\u4f4d\u53d4\u7236\u4e00\u4e0b\u4e86\u3002\u201d \u3000\u3000\u4ed6\u4e00\u624b\u4e00\u4e2a\uff0c\u5c06\u7a0b\u9a79\u3001\u6f58\u4f65\u62b1\u8fdb\u4e86\u5730\u7a96\uff0c\u5730\u7a96\u4e2d\u6ee1\u662f\u7f8e\u9152\uff0c\u4ed6\u4fbf\u5c06\u7a0b\u9a79\u3001\u6f58\u4f65\u8f7b\u653e\u5728\u9152 \u575b\u4e4b\u95f4\u3002 \u3000\u3000\u8fd9\u575b\u4e2d\u4e4b\u9152\uff0c\u4ff1\u662f\u591a\u5e74\u9648\u917f\uff0c\u4eba\u53e3\u867d\u9187\uff0c\u4f46\u9189\u540e\u5374\u4e0d\u6613\u9192\uff0c\u4ec7\u6055\u53cc\u624b\u4e00\u6307\uff0c\u5583\u5583\u9053\uff1a \u201c\u4e24\u4f4d\u53d4\u7236\u8fd9\u4e00\u9189\u81f3\u5c11\u4e09\u65e5\uff0c\u90a3\u65f6\u5c0f\u4f84\u65e9\u5df2\u53bb\u5f97\u8fdc\u4e86\uff0c\u5931\u793c\u4e4b\u5904\uff0c\u53ea\u597d\u7b49\u5c0f\u4f84\u62a5\u4e86\u7236\u4ec7\uff0c\u518d \u6765\u8bf7\u7f6a\u3002\u201d \u3000\u3000\u4ed6\u8d70\u51fa\u5730\u7a96\uff0c\u9501\u4e0a\u4e86\u95e8\uff0c\u90a3\u5730\u7a96\u4e4b\u95e8\u751a\u662f\u6c89\u539a\u3002\u7a0b\u9a79\u3001\u6f58\u4f65\u8981\u51fa\u6765\uff0c\u81f3\u5c11\u8fd8\u5f97\u82b1\u4e00\u756a\u624b \u811a\uff01 \u3000\u3000\u4e00\u987f\u9152\u559d\u4e86\u5c06\u8fd1\u4e00\u65e5\uff0c\u6b64\u523b\u53c8\u662f\u9ec4\u660f\uff01 \u3000\u3000\u897f\u5c04\u7684\u659c\u9633\u4e2d\uff0c\u4ed6\u7a81\u5730\u53d1\u73b0\u5927\u5385\u4e2d\u7adf\u591a\u4e86\u4e24\u6761\u4eba\u5f71\uff01\u6de1\u6de1\u7684\u659c\u9633\u5c06\u4ed6\u4eec\u7684\u9880\u957f\u7684\u4eba\u5f71\u7167 \u5c04\u5728\u5899\u58c1\u4e0a\u3002 \u3000\u3000\u4ec7\u6055\u5fae\u5fae\u4e00\u60ca\uff0c\u65b9\u81ea\u987f\u4f4f\u811a\u6b65\u3002 \u3000\u3000\u5927\u5385\u4e2d\u6709\u4eba\u6c89\u58f0\u9053\uff1a\u201c\u4ec7\u516c\u5b50\uff0c\u8fd8\u6709\u9152\u4e48\uff1f\u201d \u3000\u3000\u4ec7\u6055\u76ee\u5149\u4e00\u8f6c\uff0c\u6717\u58f0\u5927\u7b11\u9053\uff1a\u201c\u9152\u81ea\u7136\u6709\uff0c\u5374\u8981\u770b\u770b\u4f60\u662f\u5426\u6709\u8d44\u683c\u559d\u6211\u7684\u9152\uff1f\u201d \u3000\u3000\u4ed6\u4e00\u6b65\u8de8\u5165\u5927\u5385\uff0c\u53ea\u89c1\u4e24\u4e2a\u9752\u888d\u4eba\u5bf9\u5750\u5728\u5802\u5385\u4e2d\u7684\u684c\u5b50\u4e24\u8fb9\uff0c\u9762\u4e0a\u4e00\u7247\u6728\u7136\uff0c\u8d6b\u7136\u662f\u4e24 \u4e2a\u201d\u8fd8\u9b42\uff01 \u3000\u3000\u5de6\u9762\u4e00\u4e2a\u201c\u8fd8\u9b42\u201d\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u53ef\u6709\u8d44\u683c\u996e\u9152\uff1f\u201d \u3000\u3000\u4ec7\u6055\u9762\u5bb9\u5fae\u53d8\uff0c\u8f7b\u53f1\u9053\uff1a\u201c\u4f60\u4e24\u4eba\u8c01\u662f\u6155\u5bb9\u60dc\u751f\uff1f\u201d \u3000\u3000\u4e24\u4e2a\u201c\u8fd8\u9b42\u201d\u9f50\u58f0\u5927\u7b11\u9053\uff1a\u201c\u6211\u4e24\u4eba\u8c01\u4e5f\u4e0d\u662f\u6155\u5bb9\u60dc\u751f\uff01\u201d \u3000\u3000\u7b11\u58f0\u4e2d\u4e24\u4eba\u9f50\u5730\u624b\u638c\u4e00\u626c\uff0c\u62b9\u53bb\u4e86\u9762\u4e0a\u7684\u6613\u5bb9\u9762\u5177\u3002 \u3000\u3000\u4ec7\u6055\u8f6c\u76ee\u671b\u5904\uff0c\u53ea\u89c1\u8fd9\u4e24\u4eba\u4e00\u4e2a\u9f3b\u76f4\u53e3\u65b9\uff0c\u6ee1\u9762\u6b63\u6c14\uff0c\u7709\u95f4\u9690\u9690\u9732\u51fa\u4e00\u6761\u6c9f\u7eb9\uff0c\u6b63\u662f\u91d1 \u5251\u4fa0\u7aef\u6728\u65b9\u6b63\uff01 \u3000\u3000\u53e6\u4e00\u4eba\u5251\u7709\u661f\u76ee\uff0c\u989d\u4e0b\u5fae\u9aed\u3002\u82f1\u4fca\u7684\u9762\u5bb9\u4e0a\uff0c\u5fae\u5fae\u5e26\u7740\u4e00\u79cd\u5bf9\u4eba\u751f\u7684\u538c\u5026\u4e4b\u8272\uff0c\u5374\u662f\u4e00 \u522b\u7ecf\u5e74\u7684\u77f3\u78f7\uff01 \u3000\u3000\u8fd9\u4e24\u4eba\u7a81\u7136\u73b0\u8eab\uff0c\u7684\u786e\u4f7f\u4ec7\u6055\u51fa\u4e4e\u610f\u6599\u3002 \u3000\u3000\u4ed6\u53c8\u60ca\u53c8\u559c\uff0c\u4e00\u65f6\u95f4\uff0c\u7adf\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff01 \u3000\u3000\u91d1\u5251\u4fa0\u7aef\u6728\u65b9\u6b63\u5fae\u7b11\u9053\uff1a\u201c\u5c0f\u5f1f\u4e3a\u5144\u53f0\u5c06\u4e00\u5177\u5c38\u8eab\u4e00\u76f4\u7531\u7075\u9690\u5bfa\u80cc\u5230\u6bdb\u81ec\u5bb6\u91cc\uff0c\u4e0d\u77e5\u662f \u5426\u6709\u8d44\u683c\u559d\u4e00\u676f\u4ec7\u5144\u7684\u7f8e\u9152\uff1f\u201d \u3000\u3000\u4ec7\u6055\u66f4\u60ca\u66f4\u559c\uff0c\u8131\u53e3\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\uff01\u201d \u3000\u3000\u8fd9\u7591\u56e2\u4ed6\u4e45\u5df2\u85cf\u5728\u5fc3\u4e2d\uff0c\u76f4\u5230\u6b64\u523b\u624d\u88ab\u63ed\u7834\uff0c\u4e09\u4eba\u4e45\u522b\u91cd\u9022\uff0c\u7aef\u6728\u65b9\u6b63\u4e0d\u7981\u53c8\u81ea\u9891\u9891\u547c \u9152\u3002"}),showOnHomepage:!0}]}}]);